"use strict";(self.webpackChunkwebsite=self.webpackChunkwebsite||[]).push([[97354],{6731:(e,n,o)=>{o.r(n),o.d(n,{default:()=>h});var I=o(74848);const h=[{name:"\u7b2c\u4e00\u7ae0\u3000\u4fdd\u9556\u7684\u5c11\u5e74",content:(0,I.jsx)(I.Fragment,{children:"\u8fd9\u6761\u8def\u7b14\u76f4\u5730\u4f38\u5230\u8fd9\u91cc\u6765\uff0c\u5c31\u5f62\u6210\u4e00\u4e2a\u5f2f\u66f2\uff0c\u5f2f\u66f2\u7684\u5730\u65b9\u662f\u4e00\u7247\u957f\u5f97\u9887\u4e3a\u6d53\u5bc6\u7684\u6811\u6797 \u5b50\uff0c\u8def\u5c31\u4ece\u8fd9\u6811\u6797\u5b50\u91cc\u7a7f\u51fa\u53bb\u3002 \u3000\u3000\u867d\u7136\u5df2\u8fd1\u9ec4\u660f\uff0c\u4f46\u516d\u6708\u9a84\u9633\u7684\u4f59\u5a01\u4ecd\u5728\uff0c\u70ed\u5f97\u6559\u4eba\u96be\u8010\u3002 \u3000\u3000\u4e00\u4e1d\u98ce\u58f0\u4e5f\u6ca1\u6709\uff0c\u5f2f\u82cd\u5c31\u7f18\u662f\u4e00\u5757\u5b9d\u77f3\uff0c\u6e5b\u84dd\u4e3a\u6ca1\u6709\u4e1d\u6beb\u6742\u8272\u3002\u9633\u5149\u4ece\u897f\u8fb9\u5c04\u4e0b\u6765\uff0c\u7167 \u5728\u8def\u4e0a\uff0c\u7167\u5728\u6811\u68a2\uff0c\u5374\u7167\u4e0d\u8fdb\u6811\u6797 \u3000\u3000\u8def\u4e0a\uff0c\u672c\u6ca1\u6709\u4ec0\u4e48\u884c\u4eba\uff0c\u4f46\u6b64\u523b\u8fdc\u5904\u7a81\u5730\u5c18\u5934\u5927\u8d77\uff0c\u5954\u96f7\u4f3c\u5730\u9a70\u6765\u51e0\u5339\u5065\u9a6c\uff0c\u5230\u4e86\u8fd9\u6811 \u6797\u5b50\u524d\u9762\u4e00\u6253\u76d8\u65cb\uff0c\u7adf\u7136\u5168\u90fd\u505c\u4f4f \u3000\u3000\u4e00\u4e2a\u9a91\u7740\u6bdb\u9a74\u7684\u4e1d\u5e1b\u8d29\u5b50\u521a\u597d\u4ece\u6751\u5b50\u91cc\u51fa\u6765\uff0c\u770b\u5230\u8fd9\u51e0\u4e2a\u9a91\u58eb\uff0c\u76ee\u5149\u4e0d\u7981\u4e00\u6115\uff0c\u5728\u8fd9\u51e0 \u4e2a\u9a91\u58eb\u8eab\u4e0a\u671b\u4e86\u534a\u664c\u3002\u4f46\u81ea\u5df1\u7684\u76ee\u5149\u548c\u4eba\u5bb6\u90a3\u5229\u5203\u822c\u7684\u773c\u775b\u4e00\u89e6\uff0c\u5c31\u8d76\u7d27\u4f4e\u4e0b\u5934\uff0c\u626c\u8d77\u5c0f\u76ae \u97ad\u5728\u9a74\u5b50\u540e\u9762\u8896\u4e86\u4e00\u4e0b\uff0c\u8fd9\u6bdb\u9a74\u5c31\u653e\u5f00\u56db\u8e44\u8dd1\u4e86\u5f00\u53bb\u3002 \u3000\u3000\u539f\u6765\u8fd9\u4e94\u5339\u9a6c\u548c\u9a6c\u4e0a\u7684\u4eba\u90fd\u900f\u7740\u6709\u4e9b\u53e4\u602a\uff0c\u9a6c\u4e0a\u7684\u9a91\u58eb\uff0c\u4e00\u8272\u6de1\u9752\u7ef8\u886b\uff0c\u7ef8\u886b\u4e0a\u5374\u7f15\u7740 \u91d1\u7ebf\u3002\u8bc6\u8d27\u7684\u4eba\u4e00\u773c\u671b\u53bb\uff0c\u5c31\u77e5\u9053\u5149\u662f\u8fd9\u4e00\u88ad\u7ef8\u886b\uff0c\u4ef7\u503c\u5c31\u5728\u767e\u91d1\u4ee5\u4e0a\uff0c\u7edd\u4e0d\u662f\u666e\u901a\u4eba\u7a7f\u5f97 \u8d77\u7684\u3002 \u3000\u3000\u5c24\u5176\u602a\u7684\u662f\uff0c\u8fd9\u4e94\u5339\u9a6c\u7684\u9a6c\u978d\u4e0b\uff0c\u4e5f\u9732\u7740\u91d1\u4e1d\u7684\u6d41\u82cf\uff0c\u9633\u5149\u4e00\u95ea\uff0c\u7167\u5728\u90a3\u9a6c\u5065\u4e0a\uff0c\u9a6c\u8e6c \u7adf\u4e5f\u95ea\u7740\u91d1\u5149\u3002\u8fd9\u4e94\u4eba\u4e94\u9a6c\u7acb\u5728\u8fd9\u516d\u6708\u7684\u9633\u5149\u4e4b\u4e0b\uff0c\u53ea\u89c9\u91d1\u5149\u707f\u70c2\uff0c\u5c31\u50cf\u662f\u5e99\u91cc\u5851\u91d1\u7684\u795e\u50cf \u4f3c\u7684\u3002 \u3000\u3000\u6b64\u523b\uff0c\u8fd9\u4e9b\u9a91\u58eb\u4eec\u4e00\u52d2\u9a6c\u7586\uff0c\u9a6c\u5c31\u6162\u6162\u5730\u8fdb\u4e86\u6811\u6797\u5b50\u3002\u4e00\u4e2a\u6ee1\u9762\u4e8e\u601d\u7684\u5927\u6c49\uff0c\u5c06\u5934\u4e0a\u9576 \u7740\u4e00\u7c92\u660e\u6b8a\u7684\u6de1\u9752\u6b66\u58eb\u5dfe\u5f80\u540e\u9762\u4e00\u63a8\uff0c\u6273\u7740\u9a6c\u978d\u5b50\u56db\u4e0b\u4e00\u671b\uff0c\u4fa7\u987e\u4ed6\u7684\u540c\u4f34\u8bf4\u9053\uff1a\u201c\u8fd9\u5730\u65b9 \u53c8\u51c9\u5feb\uff0c\u53c8\u6e05\u9759\uff0c\u6211\u770b\u54b1\u4eec\u5c31\u5728\u8fd9\u91cc\u6b47\u4e00\u4e0b\u5427\uff01\u53cd\u6b63\u54b1\u4eec\u5df2\u7b97\u51c6\u90a3\u8bdd\u513f\u51c6\u5f97\u4ece\u8fd9\u6761\u9053\u4e0a\u7ecf \u8fc7\uff0c\u54b1\u4eec\u7b49\u5728\u8fd9\u91cc\uff0c\u4ee5\u9038\u5f85\u52b3\uff0c\u4e00\u4f38\u624b\u5c31\u628a\u70b9\u5b50\u7ed9\u62db\u547c\u4e0b\u6765\uff0c\u4f60\u8bf4\u8fd9\u6709\u591a\u75db\u5feb\u3002\u201d \u3000\u3000\u8fd9\u6ee1\u8138\u4e8e\u601d\u7684\u5927\u6c49\u975e\u4f46\u751f\u50cf\u5a01\u731b\uff0c\u8bf4\u8d77\u8bdd\u6765\u4e5f\u662f\u58f0\u82e5\u6d2a\u949f\uff0c\u6ee1\u53e3\u5317\u65b9\u5473\u513f\uff0c\u663e\u89c1\u662f\u6765\u81ea \u71d5\u8d75\u7684\u8c6a\u5f3a\u4e4b\u58eb\u3002\u5947\u602a\u7684\u53ea\u662f\u8fd9\u79cd\u4eba\u7269\uff0c\u600e\u4f1a\u7a7f\u7740\u8fd9\u79cd\u8863\u670d\u5462?\u4e0d\u4f46\u900f\u7740\u5947\u602a\uff0c\u7b80\u76f4\u6709\u4e9b\u900f \u7740\u7384\u5999 \u3000\u3000\u4ed6\u8bf4\u5b8c\uff0c\u4e0d\u7b49\u522b\u4eba\u7b54\u8bdd\uff0c\u5c31\u5c06\u624b\u91cc\u7684\u9a6c\u97ad\u5b50\u671d\u978d\u65c1\u4e00\u63d2\uff0c\u4e00\u7ffb\u8eab\uff0c\u8e48\u5730\u8df3\u4e0b\u4e86\u9a6c\u3002\u8eab\u624b \u7684\u77eb\u5065\uff0c\u4e5f\u8bf4\u5f97\u4e0a\u662f\u5343\u4e2d\u9009\u4e00\u7684\u597d \u3000\u3000\u53e6\u4e00\u5339\u9a6c\u4e0a\u7684\u4e00\u4e2a\u7626\u957f\u6c49\u5b50\u5728\u9f3b\u5b54\u91cc\u54fc\u4e86\u4e00\u4e0b\uff0c\u51b7\u51b7\u9053\uff1a\u4e2a\u8001\u4e8c\u8fd9\u4e00\u5e74\u6765\u628a\u6b66\u529f\u5168\u90fd\u6401 \u4e0b\u4e86\uff0c\u4f60\u4eec\u770b\u770b\uff0c\u4ed6\u521a\u8dd1\u4e86\u8fd9\u4e48\u4e00\u70b9\u513f\u8def\uff0c\u5c31\u7d2f\u5f97\u6839\u4e0d\u80fd\u627e\u5f20\u5e8a\u6765\u5f80\u4e0a\u9762\u4e00\u4f8b\u3002\u8bf4\u8d77\u8bdd\u6765\uff0c \u53c8\u751f\u50cf\u4eac\u91cc\u4e0b\u6765\u7684\u90a3\u51e0\u4e2a\u4eba\u5c31\u662f\u4ed6\u513f\u5b50\u4f3c\u7684\uff0c\u53ea\u8981\u4ed6\u4e00\u4f38\u624b\uff0c\u5c31\u4ec0\u4e48\u90fd\u6210\u4e86\u3002\u201d \u3000\u3000\u90a3\u53eb\u505a\u201c\u8001\u4e8c\u201d\u7684\u6c49\u5b50\u4f8b\u5634\u4e00\u7b11\uff0c\u4f38\u624b\u5f80\u9a6c\u80a1\u4e0a\u4e00\u62cd\uff0c\u90a3\u9a6c\u5c31\u5f97\u5f97\u5730\u8dd1\u53bb\u4e00\u8fb9\uff0c\u4e00\u9762\u4ed6 \u5374\u7b11\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4e0d\u7792\u60a8\u8bf4\uff0c\u6211\u8fd9\u8fd8\u771f\u89c9\u5f97\u6709\u70b9\u5403\u4e0d\u6d88\uff0c\u8fd9\u6b21\u8981\u4e0d\u662f\u4e3a\u4e86\u54b1\u4eec\u5403\u4e86\u4eba\u5bb6\u4e00\u5e74 \u591a\uff0c\u53c8\u8499\u4eba\u5bb6\u90a3\u79cd\u6b3e\u5f85\uff0c\u5154\u5d3d\u5b50\u624d\u4f1a\u5192\u7740\u8fd9\u4e48\u5927\u7684\u592a\u9633\u8d76\u5230\u8fd9\u91cc\u6765\u3002\u201d\u8fd9\u8eab\u957f\u4e03\u5c3a\u7684\u5f6a\u5f62\u5927 \u6c49\u53c8\u563f\u5730\u4e00\u7b11\uff0c\u9053\uff1a\u4e0d\u8fc7\u4ece\u4eac\u91cc\u4e0b\u6765\u7684\u51e0\u5757\u6599\uff0c\u8fd8\u771f\u6ca1\u5728\u6211\u4e8c\u9738\u5929\u7684\u773c\u91cc\u3002\u5c31\u7b97\u4ed6\u4eec\u80fd\u642c\u51fa \u2018\u71d5\u4eac\u9556\u5c40\u2019\u91cc\u7684\u4eba\u6765\uff0c\u53ef\u662f\u5927\u54e5\uff0c\u60a8\u60f3\u60f3\uff0c\u71d5\u4eac\u9556\u5c40\u7684\u90a3\u8001\u5934\u5b50\uff0c\u8fd8\u4f1a\u5c06\u4ec0\u4e48\u597d\u624b\u501f\u7ed9\u8fd9 \u4e9b\u9e70\u722a\u5b59\u5417?\u201d \u3000\u3000\u90a3\u4e2a\u4ed6\u53eb\u505a\u201c\u5927\u54e5\u201d\u7684\u7626\u957f\u6c49\u5b50\u53c8\u51b7\u54fc\u4e86\u4e00\u4e0b\uff0c\u76ee\u5149\u4e00\u8f6c\uff0c\u84e6\u5730\u9053\uff1a\u201c\u8001\u4e8c\uff0c\u5ff5\u77ed\uff01\u201d \u3000\u3000\u53e6\u56db\u4e2a\u7a7f\u7740\u8c6a\u534e\u3001\u8eab\u8eaf\u7cbe\u5e72\u3001\u795e\u8272\u527f\u608d\u7684\u9a91\u58eb\u4e00\u9f50\u968f\u7740\u4ed6\u7684\u76ee\u5149\u5f80\u90a3\u8fb9\u671b\u53bb\u3002\u53ea\u89c1\u4e00\u4e2a \u7a7f\u69db\u697c\u957f\u886b\u7684\u6c49\u5b50\uff0c\u624b\u91cc\u62ff\u7740\u4e00\u672c\u70c2\u4e66\uff0c\u5750\u5728\u6797\u4e2d\u9053\u65c1\u7684\u4e00\u68f5\u6811\u4e0b\uff0c\u772f\u7740\u773c\u775b\uff0c\u50cf\u662f\u5df2\u7ecf\u7761 \u7740\u4e86\uff0c\u5374\u5c06\u4e24\u53ea\u7a7f\u7740\u7834\u5e03\u978b\u7684\u811a\u4f38\u5f97\u8fdc\u8fdc\u7684\u3002 \u3000\u3000\u90a3\u6ee1\u9762\u4e8e\u601d\u7684\u5927\u6c49\u4e0d\u7981\u53c8\u54c8\u54c8\u4e00\u4e50\uff0c\u6307\u7740\u8fd9\u7a77\u6c49\u7b11\u9053\uff1a\u5927\u54e5\uff0c\u60a8\u771f\u662f\uff0c\u81ea\u4ece\u54b1\u4eec\u5144\u5f1f\u4e0a \u6b21\u683d\u4e86\u90a3\u6d53\u8ddf\u6597\u4e4b\u540e\uff0c\u60a8\u8d8a\u6765\u8d8a\u5c0f\u5fc3\u4e86\uff0c\u8fde\u8fd9\u4e48\u4e2a\u7a77\u9178\u4e5f\u542b\u7cca\u8d77\u6765\u3002\u201d \u3000\u3000\u90a3\u7626\u957f\u6c49\u5b50\u53cc\u7709\u4e00\u76b1\uff0c\u4e5f\u7ffb\u8eab\u4e0b\u4e86\u9a6c\uff0c\u8fdc\u8fdc\u8e6c\u5230\u4e00\u682a\u6811\u4e0b\uff0c\u7adf\u95ed\u76ee\u517b\u8d77\u795e\u6765\u3002\u4e5f\u6709\u98ce\u4ece \u6797\u9699\u4e2d\u5439\u4e86\u8fdb\u6765\uff0c\u90a3\u81ea\u79f0\u201c\u4e8c\u9738\u5929\u201d\u7684\u6c49\u5b50\u655e\u5f00\u8863\u895f\uff0c\u8fce\u98ce\u4e00\u5439\uff0c\u4f38\u51fa\u9752\u7b4b\u9690\u73b0\u7684\u5927\u624b\u5f80\u957f \u6ee1\u4e86\u80e1\u5b50\u7684\u5634\u8fb9\u4e00\u62b9\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u91cc\u8981\u662f\u518d\u6709\u4e00\u7897\u51b0\u9547\u6885\u6c64\uff0c\u90a3\u53ef\u5c31\u66f4\u7f8e\u4e86\u3002\u201d \u3000\u3000\u8bdd\u672b\u8bf4\u5b8c\uff0c\u773c\u775b\u7a81\u5730\u6094\u4f4f\uff0c\u539f\u6765\u90a3\u7761\u5728\u6811\u4e0b\u7684\u7a77\u9178\u8eab\u65c1\uff0c\u6b63\u653e\u7740\u4e00\u4e2a\u7ec6\u74f7\u76d6\u7897\uff0c\u7897\u76ca\u4e0a \u6c81\u7740\u6c34\u73e0\u5b50\uff0c\u91cc\u9762\u7ade\u771f\u7684\u6a61\u76db\u7740\u201c\u51b0\u9547\u6885\u6c64\u201d\u3002 \u3000\u3000\u8fd9\u5927\u6c49\u76ee\u5149\u4e00\u89e6\u7740\u8fd9\u53ea\u76d6\u7897\uff0c\u4fbf\u518d\u4e5f\u6536\u4e0d\u56de\u6765\uff0c\u4ed4\u7ec6\u53c8\u76ef\u4e86\u4e24\u773c\u3002\u8fd9\u53ea\u76d6\u7897\u6d51\u7136\u662f\u5b9d\u84dd \u8272\uff0c\u7ec6\u81f4\u5149\u6ed1\uff0c\u663e\u89c1\u662f\u540d\u7a91\u6240\u5236\u7684\u7cbe\u54c1\u3002\u53ea\u662f\u8fd9\u5927\u6c49\u4e0d\u8bc6\u8d27\uff0c\u4ed6\u770b\u7684\u53ea\u662f\u90a3\u7897\u76d6\u4e0a\u7684\u6c34\u73e0 \u5b50\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u76ee\u5149\u53c8\u56db\u4e0b\u4e00\u8f6c\uff0c\u770b\u5230\u4ed6\u7684\u5f1f\u5144\u4eec\u90fd\u5728\u671b\u7740\u4ed6\u5fae\u7b11\uff0c\u4ed6\u773a\u7740\u7259\u4e00\u6487\u5634\uff0c\u8d70\u5230\u90a3\u7a77 \u6c49\u8eab\u524d\uff0c\u671d\u90a3\u4f38\u51fa\u7684\u811a\u4e0a\u4e00\u8e22\u3002 \u3000\u3000\u90a3\u7a77\u6c49\u845b\u5730\u60ca\u9192\u4e86\uff0c\u4e00\u63a2\u5934\uff0c\u5374\u4ecd\u7136\u772f\u7740\u773c\u775b\uff0c\u4f5c\u51fa\u4e00\u526f\u83ab\u540d\u5176\u5999\u7684\u6837\u5b50\u6765\u671b\u7740\u8fd9\u8e22\u9192 \u81ea\u5df1\u7684\u4eba\u3002 \u3000\u3000\u81ea\u79f0\u201c\u4e8c\u9738\u5929\u201d\u7684\u5927\u6c49\u6b64\u523b\u4e5f\u770b\u6e05\u4e86\u8fd9\u7a77\u9178\u5e74\u7eaa\u8fd8\u8f7b\uff0c\u8138\u751f\u636e\u4e5f\u767d\u767d\u51c0\u51c0\uff0c\u6f02\u6f02\u4eae\u4eae \u7684\uff0c\u4e24\u9053\u7709\u6bdb\u53c8\u7ec6\u53c8\u957f\uff0c\u5c24\u5176\u593a\u76ee\u3002 \u3000\u3000\u4f46\u8fd9\u201c\u4e8c\u9738\u5929\u201d\u662f\u65e2\u7c97\u9c81\uff0c\u53c8\u86ee\u5e72\uff0c\u4ec0\u4e48\u4e8b\u90fd\u4e0d\u653e\u5728\u5fc3\u4e0a\uff0c\u6b64\u523b\u89c1\u8fd9\u7a77\u9178\u5c11\u5e74\u9192\u4e86\uff0c\u5c31 \u53c8\u51b2\u7740\u4ed6\u4e00\u6bd7\u7259\uff0c\u6307\u4e86\u6307\u90a3\u4e0a\u9762\u6c81\u7740\u6c34\u73e0\u5b50\u7684\u5b9d\u84dd\u76d6\u7897\uff0c\u7c97\u7740\u5589\u5499\u5927\u58f0\u95ee\u9053\uff1a\u201c\u5582\uff0c\u5c0f\u5b50\uff0c \u8fd9\u91cc\u9762\u88c5\u7684\u662f\u4ec0\u4e48?\u201d \u3000\u3000\u90a3\u7a77\u9178\u5c11\u5e74\u4eff\u4f5b\u7761\u5f97\u5f88\u719f\uff0c\u88ab\u7a81\u7136\u5f04\u9192\u6765\u4f3c\u7684\uff0c\u773c\u775b\u4ecd\u60fa\u677e\u7740\u9053\uff1a\u201c\u8fd9\u91cc\u9762\u88c5\u7684\u662f\u6885 \u6c64\uff0c\u5c0f\u751f\u7528\u51b0\u9547\u4e86\u4e00\u665a\u4e0a\uff0c\u8fd8\u820d\u4e0d\u5f97\u996e\u54e9\u3002\u201d \u3000\u3000\u8fd9\u5927\u6c49\u54c8\u54c8\u4e00\u7b11\uff0c\u5f80\u5634\u91cc\u54bd\u4e86\u53e3\u553e\u6cab\uff0c\u6307\u7740\u90a3\u76d6\u7897\u8fde\u8fde\u9053\uff1a\u201c\u597d\u6781\u4e86\uff0c\u597d\u6781\u4e86\uff0c\u5feb\u62ff\u6765 \u7ed9\u5927\u7237\u6211\u559d!\u5927\u7237\u6211\u6b63\u6e34\u5f97\u5f88\u3002\u201d \u3000\u3000\u90a3\u7a77\u9178\u5c11\u5e74\u63c9\u4e86\u63c9\u773c\u775b\uff0c\u4eff\u4f5b\u5f04\u4e0d\u61c2\u4f3c\u7684\uff0c\u7ed3\u7ed3\u5df4\u5df4\u5730\u8bf4\u9053\uff1a\u201c\u4e0d\u8fc7\u2026\u2026\u8fd9\u7897\u6885\u6c64\u5c0f\u751f \u8fd8\u8981\uff0c\u8fd8\u4e0d\u60f3\u9001\u7ed9\u9601\u4e0b\uff01\u201d \u3000\u3000\u8fd9\u4f4d\u201c\u4e8c\u9738\u5929\u201d\u4e24\u53ea\u773c\u775b\u7a81\u5730\u2014\u77aa\uff0c\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u7a77\u9178\uff0c\u6562\u60c5\u662f\u80c6\u5b50\u4e0a\u751f\u4e86\u6bdb\u4e86\uff0c\u6211\u4e8c \u9738\u5929\u4eca\u5929\u9ad8\u5174\uff0c\u624d\u5ba2\u5ba2\u6c14\u6c14\u5730\u53eb\u4f60\u628a\u6885\u6c64\u62ff\u6765\uff0c\u4e0d\u7136\u5927\u7237\u4e00\u811a\u8e22\u51fa\u4f60\u7684\u86cb\u9ec4\u5b50\uff0c\u4f60\u2014\u2014\u201d \u3000\u3000\u54ea\u77e5\u4ed6\u8bdd\u58f0\u672b\u843d\uff0c\u90a3\u9759\u7acb\u5728\u6811\u4e0b\u7684\u7626\u957f\u6c49\u5b50\u7a81\u5730\u4e00\u58f0\u559d\u53f1\u9053\uff1a\u201c\u8001\u4e8c\uff0c\u5664\u58f0\uff01\u201d\u53c8\u9053\uff1a \u201c\u8001\u4e94\uff0c\u4f60\u542c\u542c\uff0c\u662f\u4e0d\u662f\u70b9\u4e8e\u4eec\u5df2\u7ecf\u6765\u4e86?\u201d \u3000\u3000\u4e00\u4e2a\u77ed\u5c0f\u7cbe\u608d\u7684\u6c49\u5b50\u7ffb\u8eab\u8dc3\u4e86\u8d77\u6765\uff0c\u4f0f\u5411\u5730\u4e0a\uff0c\u7528\u8033\u6735\u8d34\u7740\u5730\u503e\u542c\u4e86\u534a\u664c\uff0c\u7a81\u5730\u6ee1\u8138\u559c \u8272\u5730\u8bf4\u9053\uff1a\u201c\u5927\u54e5\uff0c\u8fd8\u662f\u60a8\u8033\u6735\u7075\uff0c\u679c\u7136\u662f\u70b9\u5b50\u6765\u4e86\uff0c\u4e00\u5171\u6709\u4e09\u8f86\u8f66\uff0c\u4e5d\u5339\u9a6c\uff0c\u8ddd\u79bb\u8fd9\u91cc\u8fd8 \u6709\u4e00\u7bad\u591a\u5730\uff0c\u6700\u591a\u4e00\u76cf\u8336\u7684\u65f6\u5019\u5c31\u8fc7\u6765\u4e86\u3002\u201d \u3000\u3000\u8fd9\u65f6\u90a3\u4f4d\u81ea\u79f0\u201c\u4e8c\u9738\u5929\u201d\u7684\u5927\u6c49\u4fbf\u518d\u4e5f\u987e\u4e0d\u4e86\u559d\u6885\u6c64\uff0c\u4e00\u584c\u8eab\uff0c\u98d5\u5730\u4e00\u58f0\uff0c\u4e00\u4e2a\u7bad\u6b65\u7a9c \u5230\u53e6\u4e00\u8fb9\u7684\u6797\u53e3\uff0c\u624b\u642d\u51c9\u7bf7\uff0c\u671d\u524d\u4e00\u671b\u2014\u2014 \u3000\u3000\u524d\u9762\u679c\u7136\u6709\u4e00\u822c\u5c18\u5934\u626c\u8d77\uff0c\u4e5f\u9690\u9690\u6709\u8f66\u8f95\u9a6c\u5636\u4e4b\u58f0\u4f20\u6765\u3002\u8fd9\u6c49\u5b50\u751f\u6027\u867d\u7136\u9c81\u83bd\uff0c\u4f46\u884c\u52a8 \u5374\u77eb\u5065\u5f97\u5f88\uff0c\u4e00\u62e7\u8eab\uff0c\u53c8\u7a9c\u56de\u6811\u6797\u5b50\uff0c\u53cc\u81c2\u4e00\u5f20\uff0c\u4f4e\u4f4e\u9677\u559d\u4e00\u58f0\uff0c\u5c06\u6b63\u5728\u56db\u4e0b\u5403\u7740\u8349\u7684\u9a6c\u90fd \u8d76\u5230\u4e00\u8fb9\u53bb\uff0c\u53c8\u4ece\u81ea\u5df1\u90a3\u5339\u9a6c\u7684\u9a6c\u978d\u65c1\u62bd\u51fa\u4e00\u53e3\u6298\u94c1\u5200\u6765\uff0c\u8fce\u98ce\u4e00\u4eae\uff0e\u4e0d\u7981\u521a\u5634\u4e00\u7b11\uff0c\u6bd7\u7259 \u8bf4\u9053\uff0f\u597d\u5144\u5f1f\uff0c\u4f60\u4f11\u606f\u4e86\u8fd9\u4e48\u4e45\uff0c\u4eca\u5929\u4e5f\u8be5\u8ba9\u4f60\u53d1\u53d1\u5229\u5e02\u4e86\u3002\u201d \u3000\u3000\u8fd9\u65f6\u53e6\u56db\u4e2a\u6c49\u5b50\u4e5f\u90fd\u8dc3\u4e86\u8d77\u6765\uff0c\u51dd\u795e\u6212\u5907\u3002\u8033\u542c\u5f97\u8f66\u78f7\u9a6c\u5636\u4e4b\u58f0\u8d8a\u6765\u8d8a\u8fd1\uff0c\u4f17\u4eba\u8138\u4e0a\u7684 \u795e\u8272\uff0c\u8d8a\u53d1\u9732\u51fa\u7d27\u5f20\u7684\u6837\u5b50\u6765\u3002 \u3000\u3000\u800c\u90a3\u5bd2\u9178\u5c11\u5e74\uff0c\u66f4\u50cf\u662f\u88ab\u4ed6\u4eec\u8fd9\u79cd\u6837\u5b50\u5413\u5f97\u4e0d\u77e5\u600e\u4e48\u597d\uff0c\u62ff\u8d77\u90a3\u53ea\u5b9d\u84dd\u76d6\u7897\u6765\uff0c\u53cc\u624b\u7b4b \u9570\u5730\u53d1\u6296\uff0c\u6296\u5f97\u90a3\u53ea\u7897\u4e0d\u4f4f\u5730\u54cd\u3002 \u3000\u3000\u6ee1\u9762\u4e8e\u601d\u7684\u5927\u6c49\u4e00\u6b65\u7a9c\u8fc7\u53bb\uff0c\u638c\u4e2d\u5200\u5728\u4ed6\u9762\u95e8\u865a\u6643\u4e00\u4e0b\uff0c\u6c89\u58f0\u4f4e\u8d50\u9053\uff0f\u4f60\u5c0f\u4e8e\u8001\u8001\u5b9e\u5b9e \u8ddf\u6211\u5750\u5728\u8fd9\u91cc\uff01\u52a8\u4e00\u52a8\u5927\u7237\u5c31\u8981\u4f60\u7684\u547d\uff01\u201d\u8fd9\u5bd2\u9178\u5c11\u5e74\u6296\u5f97\u66f4\u5389\u5bb3\u4e86\uff0c\u7897\u91cc\u7684\u6885\u6c64\u6cfc\u4e86\u51fa \u6765\uff0c\u6e85\u4e86\u4e00\u8eab\uff0c \u3000\u3000\u201c\u4e8c\u9738\u5929\u201d\u60cb\u60dc\u5730\u671b\u4e86\u4e00\u773c\uff0c\u8fd9\u65f6\u90a3\u53e6\u5916\u56db\u4e2a\u6c49\u5b50\u90fd\u5df2\u95ea\u5230\u6811\u540e\uff0c\u4e00\u9762\u5411\u4ed6\u559d\u9053\uff1a\u201c\u8001 \u4e8c\uff0c\u70b9\u5b50\u6765\u4e86\u3002\u201d \u3000\u3000\u201c\u4e8c\u9738\u5929\u201d\u518d\u4e5f\u987e\u4e0d\u5f97\u6885\u6c64\u4e86\uff0c\u4e00\u62e7\u8eab\uff0c\u4e5f\u95ea\u5230\u6811\u540e\u3002\u53ea\u89c1\u6797\u5916\u5df2\u5f53\u5934\u9a70\u8fdb\u4e24\u5339\u9a6c\uff0c\u9a6c \u4e0a\u5750\u7740\u4e00\u80d6\u4e00\u7626\u4e24\u4e2a\u6c49\u5b50\u3002\u4e00\u8fdb\u6811\u6797\uff0c\u8fd9\u4e24\u4eba\u4e5f\u5598\u4e86\u4e00\u53e3\u6c14\uff0c\u65b9\u8981\u8bf4\u8bdd\uff0c\u54ea\u77e5\u5374\u542c\u5230\u66b4\u559d\u4e00 \u58f0\uff1a\u201c\u670b\u53cb\u7ad9\u7740\uff0c\u2018\u71d5\u4e91\u4e94\u9738\u5929\u2019\u5728\u6b64\u606d\u5019\u670b\u53cb\u4eec\u7684\u5927\u9a7e\u5df2\u6709\u591a\u65f6\u4e86\u3002\u201d \u3000\u3000\u201c\u71d5\u4e91\u4e94\u9738\u5929\u201d\u8fd9\u51e0\u4e2a\u5b87\u4e00\u559d\u51fa\u6765\uff0c\u90a3\u80d6\u5b50\u8138\u4e0a\u7684\u80d6\u8089\u5c31\u98a4\u6296\u4e86\u4e00\u4e0b\uff0c\u53e6\u4e00\u4eba\u9762\u4e0a\u4e5f\u662f \u5019\u7136\u8272\u53d8\uff0c\u96fe\u65f6\u95f4\uff0c\u968f\u7740\u8fd9\u559d\u58f0\uff0c\u6797\u4e2d\u5df2\u95ea\u51fa\u4e94\u4e2a\u7a7f\u7740\u7ee3\u91d1\u534e\u670d\u7684\u527f\u608d\u6c49\u4e88\u3002 \u3000\u3000\u90a3\u80d6\u5b50\u53c8\u4e00\u60ca\uff0c\u51e0\u4e4e\u4ece\u9a6c\u4e0a\u8dcc\u4e0b\u6765\uff0c\u4e24\u53ea\u5c0f\u773c\u775b\u56db\u4e0b\u4e00\u8f6c\uff0c\u5f3a\u81ea\u9547\u5b9a\u7740\uff0c\u5374\u89c1\u4e00\u4e2a\u5f6a\u5f62 \u5927\u6c49\u5df2\u7a9c\u5230\u81ea\u5df1\u9a6c\u524d\uff0c\u5389\u58f0\u559d\u6f6d\uff1a\u201c\u90d1\u80d6\u5b50\uff0c\u5feb\u628a\u4f60\u62bc\u7740\u7684\u4e1c\u897f\u6765\uff0c\u7136\u540e\u631f\u7740\u5c3e\u5df4\u5feb\u6eda\uff0c\u6211 \u5389\u6587\u8c79\u770b\u4f60\u751f\u5f97\u80a5\u5934\u5927\u8033\u7684\uff0c\u8bf4\u4e0d\u5b9a\u4f1a\u9976\u4f60\u4e00\u547d\u3002\u201d \u3000\u3000\u539f\u6765\u8fd9\u7c97\u6269\u5927\u6c49\uff0c\u6b63\u662f\u540d\u6ee1\u4e24\u6cb3\u7684\u5de8\u76d7\u201c\u71d5\u4e91\u4e94\u9738\u5929\u201d\u4e2d\u7684\u201c\u4e8c\u9738\u5929\u201d\u5389\u6587\u8c79\u3002 \u3000\u3000\u8fd9\u201c\u71d5\u4e91\u4e94\u9738\u5929\u201d\u65e2\u672b\u5b89\u5c71\uff0c\u4ea6\u672b\u7acb\u5be8\uff0c\u5374\u662f\u5927\u6cb3\u5357\u5317\u6700\u8457\u540d\u7684\u7eff\u6797\u9053\u4e4b\u4e00\uff0c\u8fd9\u540c\u65cf\u5144 \u5f1f\u4e94\u4eba\uff0c\u4ed7\u7740\u98d8\u5ffd\u7684\u884c\u8e2a\uff0c\u72e0\u8fa3\u7684\u884c\u4e8b\uff0c\u5728\u4e24\u6cb3\u4e00\u5e26\u7684\u786e\u4f5c\u8fc7\u51e0\u4ef6\u5927\u6848\uff0c\u4e5f\u535a\u5f97\u4e0d\u5c0f\u7684\u4e07 \u513f\u3002 \u3000\u3000\u8fd9\u5f53\u5934\u7684\u80d6\u5b50\u5356\u76f8\u867d\u7136\u4e0d\u4f73\uff0c\u5374\u4e5f\u662f\u4e24\u6cb3\u6b66\u6797\u4e2d\u7684\u540d\u4eba\u3001\u6714\u540d\u6355\u80d6\u7075\u5b98\u90d1\u4f2f\u8c61\u3002\u6b64\u523b\u4ed6 \u867d\u518d\u4e5f\u60f3\u4e0d\u5230\u8fd9\u201c\u71d5\u4e91\u4e94\u9738\u5929\u201d\u5728\u8fd9\u5149\u5929\u5316\u65e5\u4e4b\u6b79\uff0c\u52a8\u624b\u62db\u547c\u8fd9\u6279\u5b98\u5bb6\u8fd0\u9001\u7684\u73cd\u5b9d\uff0c\u6b64\u65f6\u4ed6 \u5fc3\u91cc\u5c3d\u7ba1\u53d1\u6bdb\uff0c\u53e3\u4e2d\u5374\u4ecd\u4e0d\u542b\u7cca\u3002\u53cc\u624b\u4e00\u62f1\uff0c\u5f3a\u7b11\u7740\u9053\uff1a\u201c\u6211\u5f53\u662f\u8c01\u5462\uff0c\u539f\u6765\u662f\u5389\u5f53\u5bb6\u7684\uff0c \u8fd9\u4e9b\u65e5\u5b50\u6765\uff0c\u5c0f\u7684\u4e5f\u4e0d\u77e5\u9053\u5389\u5f53\u5bb6\u7684\u5230\u54ea\u91cc\u53d1\u8d22\u53bb\u4e86\uff0c\u4e00\u76f4\u6ca1\u6709\u5411\u60a8\u8bf7\u5b89\uff0c\u5fc3\u91cc\u6b63\u5728\u96be\u8fc7\uff0c \u54c8\u54c8\uff0c\u60f3\u4e0d\u5230\u4eca\u5929\u5374\u8ba9\u5c0f\u7684\u5728\u8fd9\u91cc\u7ed9\u9047\u7740\u4e86\u3002\u201d \u3000\u3000\u8fd9\u4ee5\u624b\u8155\u5706\u6ed1\u4eab\u540d\u4e8e\u516d\u6247\u95e8\u91cc\u7684\u8001\u516c\u4e8b\uff0c\u6b64\u523b\u4e00\u9762\u8bf4\u7740\u8bdd\uff0c\u4e00\u9762\u4e5f\u4ece\u9a6c\u4e0a\u8dc3\u4e86\u4e0b\u6765\uff0c\u53cc \u624b\u4e00\u62f1\uff0c\u4f5c\u4e86\u4e2a\u7f57\u5708\u63d6\uff0c\u7adf\u53c8\u966a\u7740\u7b11\u9053\uff1a\u201c\u5389\u5f53\u5bb6\u7684\uff0c\u60a8\u54ea\u5927\u4eba\u4e0d\u89c1\u5c0f\u4eba\u7f6a\uff0c\u5c0f\u7684\u8fd9\u513f\u7ed9\u60a8 \u8bf7\u5b89\u4e86\u3002\u201d \u3000\u3000\u5389\u6587\u8c79\u7a81\u5730\u4ef0\u5929\u54c8\u54c8\u5927\u7b11\u8d77\u6765\uff0c\u90a3\u90d1\u4f2f\u8c61\u7684\u4e00\u5f20\u80d6\u8138\u4e0a\uff0c\u4e00\u9635\u80b2\uff0c\u4e00\u9635\u81ea\uff0c\u5fc3\u91cc\u66f4\u5728\u6253 \u7740\u9f13\uff0c\u4ed6\u6b64\u780d\u4fdd\u7684\u867d\u7136\u662f\u8d35\u91cd\u7684\u4e1c\u897f\uff0c\u4f46\u4e00\u6765\u56e0\u4e3a\u8c01\u4e5f\u60f3\u4e0d\u5230\u8fd9\u6bb5\u4ece\u6e05\u82d1\u5230\u6d4e\u5357\u5e9c\u7d20\u6765\u5e73\u9759 \u7684\u5b98\u9053\u4e0a\u4f1a\u51fa\u4e8b\uff0c\u662f\u4ee5\u62a4\u9001\u66f2\u4eba\u4e0d\u591a\uff0c\u518d\u8005\u4e5f\u662f\u56e0\u4e3a\u8fd9\u4e9b\u5e74\u6765\u516d\u6247\u95e8\u91cc\u6839\u672c\u6ca1\u6709\u80fd\u4eba\uff0c\u6240\u4ee5 \u4ed6\u6b64\u523b\u5fc3\u91cc\u6709\u6570\uff0c\u77e5\u9053\u5c31\u51ed\u81ea\u5df1\u8fbe\u9762\u7684\u51e0\u4e2a\u4eba\uff0c\u7edd\u5bf9\u4e0d\u4f1a\u662f\u8fd9\u201c\u71d5\u4e91\u4e94\u9738\u5929\u201d\u7684\u653e\u624b\u3002 \u3000\u3000\u4ed6\u5fc3\u91cc\u56bc\u5495\u7740\uff1a\u71d5\u4eac\u9556\u5c40\u7684\u90a3\u8339\u8001\u5934\u5b50\u771f\u8be5\u6b7b\uff0c\u6d3e\u4e86\u90a3\u4e48\u4e2a\u5bd2\u5bd2\u8822\u8822\u7684\u5c0f\u4f19\u5b50\u6765\u5e2e\u7740\u6211 \u4eec\u62bc\u9556\uff0c\u5496\uff0c\u8fd9\u8d9f\u53ef\u51fa\u4e8b\u4e86\uff0c\u8fd9\u5e72\u7cfb\u8c01\u6765\u62c5\u5f53?\u201d \u3000\u3000\u4ed6\u5fc3\u91cc\u6b63\u5728\u53d1\u6bdb\uff0c\u54ea\u77e5\u90a3\u5389\u6587\u8c79\u7b11\u58f0\u5019\u5730\u4e00\u4f4f\uff0c\u6bd7\u7740\u7259\u53c8\u559d\u9053\uff1a\u201c\u90d1\u80d6\u5b50\uff0c\u591a\u5e74\u4e0d\u89c1\uff0c \u4f60\u600e\u4e48\u8fd8\u662f\u8fd9\u4e00\u5957?\u8981\u662f\u4f60\u5c0f\u5b50\u60f3\u5728\u5389\u5927\u7237\u773c\u524d\u73a9\u8fd9\u4e00\u5957\uff0c\u90a3\u4f60\u53ef\u5c31\u6253\u9519\u4e86\u7b97\u76d8\u4e86\uff0c\u8bc6\u76f8 \u7684\uff0c\u4f60\u8fd8\u662f\u6492\u624b\u5feb\u6eda\u5427\uff0c\u53cd\u6b63\u8f66\u5b50\u4e0a\u90a3\u73a9\u610f\u513f\uff0c\u53c8\u4e0d\u662f\u4f60\u90d1\u80d6\u5b50\u7684\u3002\u201d \u3000\u3000\u8fd9\u80d6\u7075\u5b98\u5e73\u65e5\u89c1\u4e86\u7a7f\u5899\u6d1e\u3001\u6253\u95f7\u68cd\u7684\u6bdb\u8d3c\uff0c\u4e00\u77aa\u773c\uff0c\u4e00\u53d1\u5a01\uff0c\u5012\u5f88\u6709\u90a3\u4e48\u56de\u4e8b\u3002\u53ef\u662f\u6b64 \u523b\u89c1\u4e86\u8fd9\u6a2a\u884c\u4e00\u5e26\u7684\u5de8\u76d7\uff0c\u4ed6\u5374\u53ea\u5269\u4e0b\u966a\u7b11\u7684\u4efd\u513f\u4e86\u3002\u4ed6\u662f\u4e24\u6cb3\u7684\u8001\u516c\u4e8b\uff0c\u672c\u6765\u548c\u8fd9\u201c\u71d5\u4e91 \u4e94\u9738\u5929\u201d\u8fd8\u6709\u7740\u4e00\u661f\u534a\u70b9\u4ea4\u60c5\uff0c\u54ea\u77e5\u4eba\u5bb6\u73b0\u5728\u6839\u672c\u4e0d\u5356\u8fd9\u4e2a\u4ea4\u60c5\uff0c\u4ed6\u867d\u7136\u4ecd\u5728\u563b\u7740\u5927\u5634\u76f4 \u7b11\uff0c\u53ef\u662f\u8fd9\u7b11\u5bb9\u4e2d\u5374\u534a\u5206\u7b11\u610f\u4e5f\u6ca1\u6709\u3002\u800c\u4ed6\u8eab\u65c1\u540c\u6765\u7684\u90a3\u4e2a\u7626\u5b50\uff0c\u6bd4\u4ed6\u8fd8\u4e0d\u7ba1\u7528\uff0c\u6b64\u523b\u966a\u7b11 \u90fd\u7b11\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u5389\u6587\u8c79\u76ee\u5149\u7535\u626b\uff0c\u53c8\u6717\u58f0\u5927\u7b11\u8d77\u6765\uff0c\u56de\u9996\u671d\u90a3\u7626\u957f\u6c49\u5b50\uff0c\u4e5f\u5c31\u662f\u201c\u71d5\u4e91\u4e94\u9738\u5929\u201d\u91cc\u7684 \u201c\u5927\u9738\u5929\u201d\u5389\u6587\u864e\u4e00\u671b\uff0c\u5927\u7b11\u7740\u8bf4\u9053\uff1a\u201c\u5927\u54e5\uff0c\u5144\u5f1f\u6211\u7684\u8bdd\u53ef\u6ca1\u6709\u8bf4\u9519\u5427?\u60a8\u770b\u770b\uff0c\u8fd9\u8fd8\u4e0d \u662f\u4e00\u4f38\u624b\uff0c\u5c31\u2026\u2026\u201d \u3000\u3000\u54ea\u77e5\u4ed6\u8bdd\u5c1a\u672a\u8bf4\u5b8c\uff0c\u5728\u90d1\u80d6\u5b50\u548c\u7537\u4e00\u7626\u5b50\u7684\u4e24\u5339\u9a6c\u4e2d\u95f4\uff0c\u7a81\u5730\u591a\u4e86\u4e00\u4e2a\u957f\u8eab\u7389\u7acb\u7684\u5c11 \u5e74\uff0c\u5389\u58f0\u559d\u9053\uff1a\u54ea\u90a3\u91cc\u6765\u7684\u532a\u5f92\uff0c\u8fd9\u4e48\u5927\u7684\u80c6\u5b50\uff0c\u6562\u52a8\u71d5\u4eac\u9556\u5c40\u4fdd\u7684\u9556\uff01\u201d \u3000\u3000\u5389\u6587\u8c79\u540e\u9000\u4e00\u6b65\uff0c\u4e24\u53ea\u73af\u773c\u4e00\u8f6c\uff0c\u4e0a\u4e0a\u4e0b\u4e0b\u5c06\u8fd9\u5c11\u5e74\u6253\u91cf\u4e86\u4e00\u9635\uff0c\u4e0d\u7531\u53c8\u6717\u58f0\u5927\u7b11\u8d77 \u6765\uff0c\u7b11\u58f0\u4e2d\u6ee1\u662f\u8f7b\u8511\u7684\u610f\u6627\u3002 \u3000\u3000\u539f\u6765\u8fd9\u5c11\u5e74\u867d\u7136\u9762\u76ee\u4e5f\u9887\u4fca\u79c0\uff0c\u8eab\u4e0a\u5374\u7a7f\u7740\u4e00\u5957\u7c97\u5e03\u77ed\u886b\u88e4\uff0c\u4e00\u526f\u571f\u5934\u58eb\u8111\u7684\u6837\u5b50\uff0c\u54ea \u91cc\u50cf\u4e2a\u4fdd\u9556\u7684\u8fbe\u5bab\u3002\u201c\u4e8c\u9738\u5929\u201d\u5389\u6587\u8c79\u600e\u4f1a\u5c06\u8fd9\u4e2a\u5c11\u5e74\u653e\u5728\u670d\u91cc\uff0c\u5927\u7b11\u7740\u559d\u9053\uff1a\u201c\u602f\u5c0f\u5b50\uff0c \u4f60\u8981\u662f\u4e0d\u8981\u547d\u7684\u8bdd\uff0c\u5927\u53ef\u4ee5\u627e\u522b\u7684\u6cd5\u5b50\u53bb\u6b7b\uff0c\u4f55\u5fc5\u8981\u53eb\u4f60\u5389\u592a\u7237\u8d39\u4e8b?\u5389\u592a\u7237\u7684\u5b9d\u5200\u4e4b\u4e0b\uff0c \u8fd8\u61d2\u5f97\u6740\u4f60\u8fd9\u6837\u7684\u5c0f\u5b50\u5462!\u201d \u3000\u3000\u90a3\u80d6\u7075\u5b98\u4e00\u770b\u8fd9\u5c11\u5e74\u51fa\u6765\uff0c\u4e0d\u7981\u6697\u4e2d\u4e00\u76b1\u7709\u5934\uff0c\u5728\u809a\u91cc\u6697\u9a82\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u4e8e\u771f\u662f\u4e0d\u77e5\u5929 \u591a\u9ad8\uff0c\u5730\u591a\u539a\uff0c\u51ed\u4f60\u90a3\u70b9\u529f\u592b\u5c31\u6562\u5728\u2018\u71d5\u4e91\u4e94\u9738\u5929\u2019\u8ddf\u524d\u53eb\u9635\uff0c\u4f60\u771f\u662f\u6d3b\u5f97\u8d77\u817b\uff0c\u5509\u2014\u2014\u60f3 \u4e0d\u5230\u58f0\u540d\u8d6b\u8d6b\u7684\u71d5\u4eac\u9556\u5c40\uff0c\u7adf\u7136\u5f04\u51fa\u8fd9\u4e48\u4e00\u4e2a\u5feb\u5c0f\u4e8e\u6765\u4f5c\u9556\u5e08\uff0c\u4e0d\u7136\u968f\u4fbf\u642d\u4e0a\u4e00\u4e2a\uff0c\u4eca\u65e5\u9047 \u7740\u4e8b\uff0c\u4e5f\u53ef\u4ee5\u62b5\u6321\u4e00\u9635\u5b50\u3002\u201d \u3000\u3000\u4ed6\u5fc3\u91cc\u4e00\u9762\u8fd9\u4e48\u60f3\uff0c\u4e00\u9762\u5374\u53c8\u5728\u6253\u7740\u522b\u7684\u4e3b\u610f\uff0c\u7a81\u5730\u53c8\u4e00\u7b11\uff0c\u80c1\u7740\u80a9\u8bf4\u9053\uff1a\u201c\u5389\u5f53\u5bb6 \u7684\uff0c\u4f60\u8fd9\u53ef\u77e5\u9053\u4e86\u5427\uff0c\u8fd9\u8d9f\u8d27\u867d\u7136\u662f\u5b98\u5bb6\u7684\u4e1c\u897f\uff0c\u4f46\u53ef\u4e0d\u662f\u5c0f\u7684\u6211\u7684\u8d23\u4efb\uff0c\u800c\u662f\u71d5\u4eac\u9556\u5c40\u4fdd \u7684\u9556\u3002\u60a8\u8981\u662f\u4e0d\u4fe1\uff0c\u60a8\u53bb\u770b\u770b\uff0c\u90a3\u4e09\u8f86\u8f66\u5b50\u4e0a\u8fd8\u63d2\u7740\u2018\u94c1\u638c\u9707\u6cb3\u6714\u2019\u8339\u8001\u9556\u5934\u7684\u94c1\u638c\u9556\u65d7 \u54e9\uff01\u201d \u3000\u3000\u8fd9\u8001\u5978\u5de8\u6ed1\u7684\u8001\u516c\u4e8b\uff0c\u6b64\u523b\u4e00\u89c1\u5927\u52bf\u4e0d\u5999\uff0c\u5c31\u5148\u5c06\u8d23\u4efb\u63a8\u5230\u522b\u4eba\u5934\u4e0a\uff0c\u4e00\u9762\u6a2a\u7740\u773c\u775b\u671b \u7740\u90a3\u6d53\u7709\u5927\u773c\u5c11\u5e74\uff0c\u610f\u601d\u5c31\u662f\u8bf4\uff1a\u8fd9\u53ef\u662f\u4f60\u81ea\u5df1\u62db\u60f9\u6765\u7684\uff0c\u8be5\u600e\u4e48\u529e\u4f60\u77a7\u7740\u529e\u5427\uff01 \u3000\u3000\u8fd9\u4e9b\u4eba\u7684\u5fc3\u4e8b\u5728\u5f53\u65f6\u4ec5\u662f\u4e00\u95ea\u800c\u8fc7\uff0c\u5389\u6587\u8c79\u7b11\u9a82\u65b9\u4f4f\uff0c\u5374\u89c1\u90a3\u5c11\u5e74\u51b7\u7b11\u4e00\u58f0\uff0c\u624b\u8155\u7531\u80cc \u540e\u4e00\u6284\uff0c\u4f46\u89c9\u6f2b\u5929\u5149\u534e\u4e00\u95ea\uff0c\u8fd9\u88ab\u4eba\u77a7\u4e0d\u8d77\u7684\u7c97\u670d\u5c11\u5e74\u624b\u4e2d\u7adf\u591a\u4e86\u4e00\u67c4\u5bd2\u5149\u8000\u76ee\u3001\u5149\u534e\u6d41\u8f6c \u7684\u957f\u5251\u3002 \u3000\u3000\u8fd9\u2014\u58f0\u9f99\u541f\uff0c\u4e00\u95ea\u5149\u534e\uff0c\u4f8b\u4f7f\u672c\u6765\u7ad9\u5728\u4ed6\u8eab\u4fa7\u7684\u4e24\u5339\u9a6c\uff0c\u54a7\u54a7\u4e00\u58f0\u957f\u5636\uff0c\u4ef0\u9996\u8dd1\u4e86\u5f00 \u53bb\u3002\u5389\u6587\u8c79\u3001\u90d1\u4f2f\u8c61\u3001\u4e94\u9738\u5929\uff0c\u8138\u4e0a\u53ef\u5168\u53d8\u4e86\u989c\u8272\uff0c\u90a3\u7ad9\u5728\u6811\u4e0b\u4e00\u76f4\u4e0d\u4e3a\u4eba\u6ce8\u610f\u7684\u5bd2\u9178\u5c11 \u5e74\uff0c\u8138\u4e0a\u4e5f\u5fae\u5fae\u9732\u51fa\u8be7\u5f02\u4e4b\u8272\uff0c\u8c01\u90fd\u60f3\u4e0d\u5230\u8fd9\u58eb\u5934\u571f\u8111\u7684\u602f\u5c0f\u5b50\u624b\u91cc\uff0c\u4f1a\u6709\u8fd9\u79cd\u795e\u5175\u5229\u5668\uff0c \u56e0\u4e3a\u5404\u4eba\u90fd\u662f\u5927\u884c\u5bb6\uff0c\u5927\u5bb6\u5168\u770b\u51fa\u4e86\u8fd9\u53e3\u5251\u7684\u4e0d\u51e1\u98ce\u91c7\u3002 \u3000\u3000\u8fd9\u5c11\u5e74\u4e00\u5251\u5728\u624b\uff0c\u5168\u8eab\u4e0a\u4e0b\uff0c\u4e5f\u4eff\u4f5b\u7a81\u7136\u6362\u53d1\u4e86\u8d77\u6765\uff0c\u4e24\u53ea\u5927\u773c\u775b\u5f80\u5389\u6587\u8c79\u8eab\u4e0a\u4e00\u77aa\uff0c \u957f\u5251\u5f53\u80f8\u4e00\u62b1\uff0c\u5389\u58f0\u559d\u9053\uff1a\u201c\u4f60\u4eec\u4eca\u5929\u8c01\u8981\u662f\u60f3\u6253\u8fd9\u8f86\u9556\u8f66\u7684\u4e3b\u610f\uff0c\u5f97\u5148\u95ee\u95ee\u6211\u8fd9\u53e3\u5251\u624d \u6210\u3002\u201d \u3000\u3000\u201c\u71d5\u4e91\u4e94\u9738\u5929\u201d\u4e4b\u9996\uff0c\u90a3\u7626\u957f\u800c\u7cbe\u7ec3\u9634\u9e33\u7684\u6c49\u5b50\u2014\u2014\u5389\u6587\u864e\u53cc\u81c2\u4e00\u5206\uff0c\u8d70\u4e0a\u4e00\u6b65\uff0c\u5c06\u5389 \u6587\u8c79\u62e6\u5728\u8eab\u540e\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u6211\u4e8c\u5f1f\u62db\u5b50\u4e0d\u4eae\uff0c\u770b\u4e0d\u51fa\u670b\u53cb\u662f\u4f4d\u9ad8\u4eba\uff0c\u6211\u5389\u6587\u864e\u8fd9\u91cc\u5148\u5411\u670b\u53cb \u544a\u7f6a\u3002\u201d\u4ed6\u8bed\u58f0\u4e00\u987f\uff0c\u76ee\u5149\u5229\u526a\u4f3c\u5730\u5728\u90a3\u80d6\u7075\u5b98\u9762\u4e0a\u4e00\u77aa\uff0c\u53c8\u9053\uff1a\u201c\u53ea\u4e0d\u8fc7\u670b\u53cb\u5e74\u5c11\u82f1\u4fca\uff0c \u60f3\u5fc5\u7cfb\u51fa\u540d\u95e8\uff0c\u8fd9\u6b21\u6765\u66ff\u8fd9\u79cd\u9e70\u722a\u5b59\u5356\u547d\uff0c\u672a\u514d\u4e5f\u6709\u4e9b\u4e0d\u503c\u5427\u3002\u201d \u3000\u3000\u8fd9\u5c11\u5e74\u77aa\u7740\u4e24\u53ea\u773c\u775b\uff0c\u5634\u5df4\u62bf\u5f97\u7d27\u7d27\u7684\uff0c\u5bf9\u5389\u6587\u864e\u7684\u8bdd\u4e00\u70b9\u53cd\u5e94\u4e5f\u6ca1\u6709\uff0c\u5140\u81ea\u62b1\u5251\u5f53 \u80f8\uff0c\u542c\u4ed6\u8bf4\u5b8c\u4e86\uff0c\u624d\u6717\u58f0\u9053\uff1a\u201c\u6211\u5c55\u767d\u5e74\u8f7b\u8bc6\u6d45\uff0c\u5bf9\u8fd9\u4e00\u5957\u5168\u4e0d\u61c2\uff0c\u6211\u53ea\u77e5\u9053\u8fd9\u8d9f\u662f\u8339\u8001\u9556 \u5934\u4ea4\u7ed9\u6211\u7684\uff0c\u6211\u5c31\u8be5\u628a\u5b83\u9001\u5230\u5730\u5934\u3002\u5404\u4f4d\u670b\u53cb\u8981\u662f\u770b\u5f97\u8d77\u6211\u5c55\u767d\uff1b\u5c31\u8bf7\u8ba9\u4e2a\u9053\uff0c\u6211\u5c55\u767d\u6765\u65e5 \u5fc5\u6709\u8865\u62a5\u4e4b\u5904\uff0c\u5426\u5219\u2014\u2014\u201d \u3000\u3000\u90a3\u5389\u6587\u8c79\u5927\u559d\u4e00\u58f0\uff0c\u63a5\u53e3\u9053\uff1a\u201c\u5426\u5219\u600e\u7684?\u201d\u4ed6\u6027\u5982\u70c8\u706b\uff0c\u867d\u7136\u4e5f\u89c9\u5f97\u8fd9\u5c11\u5e74\u624b\u91cc\u62ff\u7740 \u8fd9\u79cd\u5175\u5203\uff0c\u5c31\u5fc5\u5b9a\u6709\u5176\u6765\u5934\u3002\u4f46\u8fd9\u5c11\u5e74\u8fd9\u4e48\u4e00\u6765\uff0c\u4ed6\u53ef\u5fcd\u4e0d\u4f4f\u4e86\u3002\u968f\u7740\u8fd9\u4e00\u58f0\u5389\u5428\uff0c\u4ece\u5389\u6587 \u864e\u8eab\u4fa7\u62a2\u4e0a\u4e00\u6b65\uff0c\u5200\u5149\u4e00\u95ea\uff0c\u95ea\u7535\u4f3c\u7684\u671d\u8fd9\u53eb\u201c\u5c55\u767d\u201d\u7684\u5c11\u5e74\u659c\u659c\u5288\u4e0b\uff0c\u98ce\u58f0\u52b2\u6025\uff0c\u7aef\u7684\u662f \u5200\u6c89\u529b\u731b\u3002 \u3000\u3000\u5c55\u767d\u4e00\u64a4\u6b65\uff0c\u80a9\u5934\u5fae\u584c\uff0c\u638c\u4e2d\u8fd9\u53e3\u5149\u534e\u4e71\u95ea\u7684\u5229\u5251\u4fbf\u5e26\u7740\u4e00\u69b4\u9634\u68ee\u68ee\u7684\u9752\u5149\u5411\u4e0a\u4e00\u7ffb\uff0c \u627e\u7740\u5389\u6587\u8c79\u90a3\u53e3\u6298\u94c1\u7fd8\u5c16\u5200\u5d29\u53bb\u3002 \u3000\u3000\u5389\u6587\u8c79\u8fd9\u53e3\u5200\u867d\u4e5f\u662f\u767e\u70bc\u7cbe\u94a2\u6240\u9020\uff0c\u4f46\u6b64\u523b\u53ef\u4e0d\u6562\u8ba9\u4eba\u5bb6\u7684\u5175\u5203\u5d29\u4e0a\uff0c\u4ed6\u731b\u5730\u4e00\u632b\u8155 \u5b50\uff0c\u5200\u950b\u4e00\u8f6c\uff0c\u5212\u4e86\u4e2a\u5706\u5f27\uff0c\u201c\u529b\u5288\u534e\u5c71\u201d\u7acb\u523b\u53d8\u6210\u201c\u5929\u98ce\u72c2\u98d8\u201d\uff0c\u5237\u5730\u53c8\u662f\u4e00\u5200\uff0c\u671d\u5c55\u81ea \u5241\u53bb\uff0c\u8fd9\u201c\u4e8c\u8e22\u5929\u201d\u540d\u4e0d\u865a\u4f20\uff0c\u5200\u6cd5\u7684\u786e\u7cbe\u719f\u5df2\u6781\u3002 \u3000\u3000\u54ea\u77e5\u8fd9\u5c11\u5e74\u5c55\u767d\u7684\u88c5\u675f\u867d\u7c97\u62d9\uff0c\u8eab\u624b\u5374\u7075\u6d3b\uff0c\u6839\u672c\u4e0d\u8ba9\u8fd9\u5389\u6587\u8c79\u7684\u62db\u5f0f\u4f7f\u5230\uff0c\u2014\u62e7\u8eab\uff0c \u201c\u51e4\u51f0\u5c55\u7fc5\u201d\uff0c\u53cd\u624b\u4e00\u5251\uff0c\u8fde\u6d88\u5e26\u6253\uff0c\u7adf\u4ece\u5389\u6587\u8c79\u7684\u5200\u5149\u4e4b\u4e2d\u62a2\u653b\u51fa\u53bb\u3002\u5389\u6587\u8c79\u8d76\u7d27\u4e00\u4ef0 \u8eab\uff0c\u5f80\u540e\u5012\u8e7f\uff0c\u4e60\u2018\u582a\u582a\u907f\u8fc7\u8fd9\u62db\uff0c\u4f46\u5374\u5df2\u9762\u76ee\u53d8\u8272\u4e86\u3002 \u3000\u3000\u8fd9\u4e24\u62db\u4e00\u8fc7\uff0c\u5389\u6587\u864e\u4e0d\u7981\u76b1\u4e86\u76b1\u7709\uff0c\u4ed6\u5df2\u770b\u51fa\u8fd9\u59d3\u5c55\u7684\u5c11\u5e74\u867d\u7136\u4f7f\u7684\u5251\u6cd5\u4e0d\u8fc7\u662f\u6b66\u6797\u4e60 \u89c1\u7684\u201c\u4e09\u624d\u5251\u201d\uff0c\u4f46\u8eab\u6cd5\u3001\u8def\u5b50\uff0c\u5374\u9ad8\u660e\u5f97\u5f88\uff0c\u65f6\u95f4\u3001\u90e8\u4f4d\u7684\u62ff\u634f\uff0c\u66f4\u662f\u6070\u5230\u597d\u5904\uff0c\u50cf\u662f\u8fd9 \u5c11\u5e74\u5728\u8fd9\u53e3\u5251\u4e0a\u5df2\u6709\u591a\u4e2d\u7684\u82e6\u7ec3\uff0c\u7edd\u4e0d\u662f\u81ea\u5df1\u4e8c\u5f1f\u80fd\u62b5\u654c\u5f97\u4f4f\u7684\u3002 \u3000\u3000\u4ed6\u8fd9\u91cc\u6b63\u81ea\u6697\u4e2d\u76b1\u7709\uff0c\u4f46\u5389\u6587\u8c79\u4e00\u62db\u53d7\u632b\uff0c\u6012\u706b\u66f4\u5f20\u3002\u5389\u543c\u4e00\u58f0\uff0c\u7adf\u53c8\u98de\u8eab\u6251\u4e86\u4e0a\u53bb\uff0c \u5237\u3001\u5237\u4e00\u8fde\u53c8\u662f\u4e24\u5200\u3002 \u3000\u3000\u90a3\u5c11\u4e2d\u8138\u4e0a\u7edd\u672b\u56e0\u4e00\u62db\u5360\u4e86\u4e0a\u98ce\u800c\u6709\u4e1d\u6beb\u9a84\u9ed4\u7684\u6837\u5b50\uff0c\u4e24\u53ea\u5927\u773c\u775b\uff0c\u77aa\u5728\u8fd9\u5389\u6587\u8c79\u7684\u5200 \u5c16\u4e0a\uff0c\u968f\u7740\u4ed6\u7684\u5200\u5c16\u6253\u8f6c\u3002\u5389\u6587\u8c79\u8fd9\u52bf\u5982\u75af\u864e\u7684\u4e24\u5200\u5288\u6765\uff0c\u4ed6\u8eab\u5f62\u4e00\u9519\u6b65\uff0c\u4fbf\u53c8\u8f7b\u8f7b\u6613\u6613\u5730 \u8eb2\u4e86\u5f00\u53bb\uff0c\u638c\u4e2d\u957f\u5251\u968f\u7740\u8eab\u5b50\u4e00\u5f15\uff0c\u5251\u5149\u500f\u7136\u800c\u957f\uff0c\u8eab\u968f\u5251\u8d70\uff0c\u5251\u968f\u8eab\u6e38\uff0c\u7ade\u5c06\u4e00\u8d9f\u201c\u4e09\u624d \u5251\u6cd5\u201d\u4f7f\u5f97\u65e0\u61c8\u53ef\u51fb\u3002 \u3000\u3000\u4e0d\u8fc7\u5341\u4e2a\u7167\u9762\uff0c\u8fd9\u7c97\u6269\u9a84\u6a2a\u7684\uff0c\u90d1\u4f2f\u8c61\u5728\u65c1\u8fb9\u770b\u7740\u6ee1\u5fc3\u6b22\u559c\uff0c\u54a7\u5f00\u5927\u5634\uff0c\u5fc3\u91cc\u76f4\u4e50\uff1a \u201c\u559d\uff0c\u770b\u4e0d\u51fa\u8fd9\u602f\u5c0f\u5b50\u624b\u5e95\u4e0b\u8fd8\u771f\u6709\u4e24\u4e0b\u5b50\uff0c\u6211\u8981\u80fd\u5c06\u4ed6\u62c9\u5230\u8859\u95e8\u91cc\u53bb\uff0c\u8fd8\u771f\u662f\u4e00\u628a\u597d \u624b\u3002\u201d\u4f46\u773c\u89d2\u4e00\u671b\u90a3\u5728\u65c1\u8fb9\u864e\u89c6\u65e2\u803d\u7684\u201c\u4e94\u9738\u5929\u201d\u4e2d\u53e6\u5916\u56db\u4eba\uff0c\u4ed6\u5fc3\u91cc\u7684\u9ad8\u5174\u4e0d\u7981\u5c31\u6253\u4e86\u4e2a \u6298\u6263\u3002 \u3000\u3000\u5389\u6587\u864e\u773c\u770b\u4ed6\u4e8c\u5f1f\u8d8a\u6765\u8d8a\u4e0d\u6210\uff0c\u800c\u4e14\u4ed6\u6b64\u523b\u4e5f\u770b\u51fa\u90a3\u59d3\u5c55\u7684\u5c11\u5e74\u6b66\u529f\u867d\u4e0d\u5f31\uff0c\u5251\u6cd5\u5374\u5e73 \u5e38\uff0c\u5e76\u4e0d\u662f\u4ec0\u4e48\u9ad8\u4eba\u7684\u5b50\u5f1f\uff0c\u53ea\u4e0d\u8fc7\u4ec5\u4ed7\u7740\u81ea\u5df1\u7684\u82e6\u7ec3\u624d\u5c06\u8fd9\u8d9f\u5251\u7ec3\u5f97\u5982\u6b64\u7cbe\u7eaf\u800c\u5df2\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u5fc3\u4e2d\u4fbf\u65e0\u987e\u5fcc\uff0c\u76ee\u5149\u4e00\u8f6c\uff0c\u671d\u201c\u4e94\u9738\u5929\u201d\u4e2d\u7684\u201c\u8001\u4e09\u201d\u3001\u201c\u8001\u56db\u201d\u3001\u201c\u8001\u4e94\u201d\u6253 \u4e86\u4e2a\u773c\u8272\uff0c\u53cc\u624b\u4e00\u7ffb\uff0c\u4ece\u6000\u4e2d\u62bd\u51fa\u5175\u5203\u6765\uff0c\u7ade\u662f\u4e00\u5bf9\u4e0d\u662f\u6b66\u529f\u7cbe\u7eaf\u7684\u4eba\u7edd\u4e0d\u80fd\u4f7f\u7684\u201c\u5224\u5b98\u53cc \u7b14\u201d\u3002 \u3000\u3000\u4ed6\u968f\u5373\u4e00\u957f\u8eab\uff0c\u53e3\u4e2d\u5389\u559d\u9053\uff1a\u201c\u5f1f\u5144\u4eec\uff0c\u5148\u628a\u8fd9\u5c0f\u5b50\u62fe\u6387\u4e0b\u6765\u3002\u201d \u3000\u3000\u90d1\u4f2f\u8c61\u5fc3\u91cc\u845b\u5730\u4e00\u60ca\uff0c\u59d4\u773c\u95f4\uff0c\u4f46\u89c9\u6f2b\u5929\u5bd2\u5149\u5927\u4f5c\u3002\u539f\u6765\u8fd9\u5389\u5bb6\u5144\u5f1f\u4eec\u5df2\u5168\u5c06\u5175\u5203\u62bd\u5230 \u624b\u4e0a\uff0c\u9664\u4e86\u90a3\u53e3\u6298\u94c1\u7fd8\u5c16\u5200\u548c\u8fd9\u5bf9\u5224\u5bab\u53cc\u7b14\u5916\uff0c\u8001\u4e09\u7684\u4e00\u5bf9\u6448\u94c1\u53cc\u73af\u6756\uff0c\u8001\u56db\u7684\u4e00\u6761\u94fe\u5b50 \u67aa\uff0c\u8001\u4e94\u7684\u4e00\u53e3\u4e27\u95e8\u5251\uff0c\u8fd9\u51e0\u6837\u5175\u5203\uff0c\u7ade\u6ca1\u6709\u4e00\u6837\u76f8\u540c\u7684\u3002\u4f46\u662f\u8fd9\u5389\u5bb6\u5144\u5f1f\u4eec\u8eab\u624b\u7684\u914d\u5408\uff0c \u5374\u7edd\u672b\u56e0\u5175\u5203\u7684\u5dee\u5f02\u800c\u663e\u5f97\u6563\u6f2b\u3002\u5389\u6587\u864e\u5389\u559d\u4e00\u58f0\u8fc7\u540e\uff0c\u8fd9\u5389\u53cd\u56db\u5144\u5f1f\u5404\u5404\u5c55\u52a8\u8eab\u5f62\uff0c\u5df2\u7279 \u90a3\u59d3\u5c55\u7684\u5c11\u5e74\u548c\u80d6\u7075\u5b98\u90d1\u4f2f\u8c61\u4ee5\u53ca\u7537\u4e00\u4e2a\u4eac\u57ce\u5feb\u6355\u201c\u77f3\u7334\u201d\u4faf\u816e\u5584\u56f4\u5728\u91cc\u9762\uff0c\u638c\u4e2d\u7684\u51e0\u4ef6\u5175 \u5203\uff0c\u773c\u770b\u5c31\u5168\u8981\u62db\u547c\u5230\u90a3\u59d3\u5c55\u7684\u5c11\u5e74\u8eab\u4e0a\uff0e\u2019 \u3000\u3000\u5c55\u767d\u98d5\u7136\u51e0\u5251\uff0c\u5c06\u5bf9\u624b\u903c\u5f97\u66f4\u65e0\u8fd8\u624b\u4e4b\u529b\u4e86\uff0c\u4ed6\u9762\u4e0a\u867d\u65e0\u8868\u60c5\uff0c\u5fc3\u91cc\u5374\u4e0d\u7981\u9ad8\u5174\uff0c\u81ea\u5df1 \u82e6\u7ec3\u591a\u5e74\uff0c\u867d\u7136\u6ca1\u6709\u540d\u5e08\u6307\u70b9\uff0c\u4f46\u73b0\u5728\u5374\u53ef\u4ee5\u8bd5\u51fa\u81ea\u5df1\u7684\u6b66\u529f\u5e76\u4e0d\u542b\u7cca\uff0c\u8fd9\u6a2a\u884c\u4e00\u65f6\u7684\u201c\u71d5 \u4e91\u4e94\u9738\u5929\u201d\u4e2d\u7684\u4e00\u4eba\uff0c\u773c\u770b\u5c31\u5f97\u4e27\u5728\u81ea\u5df1\u5251\u4e0b\u3002 \u3000\u3000\u4f46\u662f\u7b49\u4ed6\u770b\u5230\u5f53\u4e0b\u8fd9\u79cd\u60c5\u52bf\u65f6\uff0c\u4ed6\u5fc3\u4e2d\u4e0d\u7981\u4e00\u51db\uff0c\u56e0\u4e3a\u4ed6\u77e5\u9053\u81ea\u5df1\u5bf9\u4ed8\u201c\u4e94\u9738\u5929\u201d\u4e2d\u7684 \u4efb\u4f55\u4e00\u4eba\uff0c\uff0c\u4f46\u5047\u5982\u4eba\u5bb6\u4e94\u4e2a\u4eba\u4e00\u9f50\u4e0a\u6765\uff0c\u81ea\u5df1\u5374\u4e07\u4e07\u4e0d\u662f\u4eba\u5bb6\u7684\u5bf9\u624b\u4e86\u3002 \u3000\u3000\u90a3\u80d6\u7075\u5b98\u548c\u77f3\u7334\u4e00\u80d6\u4e00\u7626\u4e24\u4e2a\u6355\u5934\uff0c\u6b64\u523b\u66f4\u662f\u5413\u5f97\u53cc\u817f\u76f4\u6253\u54c6\u6696\u3002 \u3000\u3000\u54ea\u77e5\u5c31\u5728\u8fd9\u95f4\u4e0d\u5bb9\u53d1\u7684\u5239\u90a3\u95f4\uff0c\u7a81\u5730\u4f20\u6765\u4e00\u9635\u6e05\u6717\u7684\u7b11\u58f0\u3002 \u3000\u3000\u5389\u5bb6\u5144\u5f1f\u5fae\u5fae\u4e00\u60ca\uff0c\u5374\u89c1\u8fd9\u7b11\u58f0\u7adf\u662f\u90a3\u5bd2\u9178\u5c11\u5e74\u6240\u53d1\u51fa\u3002\u6b64\u523b\uff0c\u4ed6\u6b63\u4e00\u6447\u2014\u6643\u5730\u8d70\u5b50\u8fc7 \u6765\uff0c\u4e00\u624b\u62ff\u7740\u90a3\u53ea\u5b9d\u84dd\u76d6\u7897\uff0c\u4e00\u624b\u62ff\u7740\u90a3\u672c\u7834\u70c2\u4e0d\u582a\u7684\u4e66\uff0c\u811a\u4e0a\u7684\u978b\u5b50\u4e5f\u6ca1\u6709\u5b8c\u5168\u7a7f\u4e0a\uff0c\u62d6 \u62d6\u62c9\u62c9\u7684\uff0c\u5f62\u72b6\u7b80\u76f4\u6709\u4e9b\u7325\u7410\u3002 \u3000\u3000\u7136\u800c\u4ed6\u7684\u7b11\u58f0\uff0c\u5374\u662f\u90a3\u6837\u6e05\u6717\u3001\u9ad8\u4ea2\uff0c\u4f7f\u4eba\u7b80\u76f4\u4e0d\u60c5\u8fd9\u79cd\u4eba\u7269\u4f1a\u53d1\u51fa\u8fd9\u6837\u7684\u7b11\u58f0\u6765\u3002 \u3000\u3000\u5389\u6587\u864e\u4e45\u95ef\u6c5f\u6e56\uff0c\u6b64\u523b\u7709\u5934\u53c8\u4e00\u76b1\uff0c\u4ed8\u9053\uff1a\u201c\u5509!\u4eca\u5929\u6211\u53ef\u53c8\u770b\u8d70\u773c\u4e86\uff0c\u60f3\u4e0d\u5230\u8fd9\u7a77\u9178 \u4e5f\u662f\u4e00\u628a\u597d\u624b\uff0c\u80cc\uff0c\u600e\u5730\u7adf\u9047\u7740\u8fd9\u79cd\u96be\u7f20\u7684\u4eba\u7269\u54e9\uff01\u201d \u3000\u3000\u968f\u7740\u8fd9\u6717\u7b11\u4e4b\u58f0\uff0c\u6b63\u5728\u52a8\u7740\u624b\u7684\u4e24\u4eba\uff0c\u624b\u5e95\u4e0b\u53ef\u5168\u6162\u4e86\u4e0b\u6765\u3002\u5c55\u767d\u5fc3\u91cc\u672c\u5728\u56bc\u5495\uff0c\u6b64\u523b \u7d22\u6027\u6536\u4e86\u624b\u3002\u90a3\u5389\u6587\u8c79\u65e9\u5c31\u6ca1\u6709\u8fd8\u624b\u4e4b\u529b\u4e86\uff0c\u6b64\u523b\u5f53\u7136\u66f4\u4e0d\u4f1a\u52a8\u624b\uff0c\u7d2f\u5f97\u5728\u65c1\u547c\u547c\u5730\u5598\u7740 \u6c14\uff0c\u4e24\u53ea\u773c\u775b\uff0c\u5374\u4e5f\u4e0d\u7981\u4e3a\u8fd9\u5bd2\u9178\u5c11\u5e74\u7684\u7b11\u58f0\u800c\u5f20\u5f97\u5927\u5927\u7684\u3002 \u3000\u3000\u8fd9\u5bd2\u9178\u5c11\u5e74\u6b64\u523b\u4e00\u8f6c\u773c\u775b\uff0c\u7b11\u58f0\u987f\u4f0e\uff0c\u773c\u775b\u987f\u65f6\u53c8\u772f\u6210\u4e00\u7ebf\uff0c\u7528\u4e09\u53ea\u624b\u6307\u7aef\u7740\u7897\u5e95\uff0c\u4e24 \u53ea\u624b\u6307\u6380\u8d77\u7897\u76d6\uff0c\u5c06\u90a3\u53ea\u5b9d\u84dd\u76d6\u7897\u9001\u5230\u5634\u4e0a\uff0c\u6df1\u6df1\u7f00\u4e86\u4e00\u53e3\uff0c\u53c8\u7b11\u8d77\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u5404\u4f4d\u600e\u7684 \u4e0d\u6253\u4e86\u5440?\u5c0f\u751f\u4eca\u65e5\u6b63\u8981\u5f00\u5f00\u773c\u754c\uff0c\u770b\u770b\u4e94\u4e2a\u6253\u4e00\u4e2a\u7a76\u7adf\u662f\u600e\u4e48\u4e00\u79cd\u6253\u6cd5\uff0c\u5404\u4f4d\u4e0d\u6253\u4e86\uff0c\u5c82 \u4e0d\u53eb\u5c0f\u751f\u626b\u5174\uff01\u201d \u3000\u3000\u5389\u6587\u8c79\u521a\u5598\u8fc7\u6c14\u6765\uff0c\u6b64\u523b\u53c8\u4e00\u6bd7\u7259\uff0c\u77aa\u7740\u773c\u775b\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u7a77\u9178\uff0c\u65b9\u624d\u592a\u7237\u53eb\u4f60\u4e0d\u8981 \u52a8\uff0c\u4f60\u8dd1\u6765\u591a\u7ba1\u4ec0\u4e48\u95f2\u4e8b?\u4e0d\u6015\u592a\u7237\u628a\u4f60\u7684\u86cb\u9ec4\u5b50\u7ed9\u8e22\u51fa\u6765\uff01\u201d\u8fd9\u9c81\u83bd\u7684\u6c49\u5b50\u521a\u521a\u5403\u4e86\u5927 \u4e8f\uff0c\u6b64\u523b\u4e00\u70b9\u4e5f\u6ca1\u6709\u4e2d\u79ef\u53c8\u5f20\u7259\u821e\u722a\u8d77\u6765\u3002 \u3000\u3000\u90a3\u5bd2\u9178\u5c11\u5e74\u772f\u7740\u773c\u775b\uff0c\u201c\u563b\u201d\u5730\u4e00\u7b11\uff0c\u6307\u7740\u4ed6\u8bf4\u9053\uff1a\u201c\u54ce\u5440\uff0c\u4f60\u8fd9\u6c49\u5b50\uff0c\u751f\u5f97\u4eea\u8868\u5802 \u5802\uff0c\u600e\u7684\u8bf4\u8d77\u8bdd\u6765\u5374\u4e00\u70b9\u4e5f\u6ca1\u6709\u4eba\u6627?\u50cf\u662f\u6709\u4eba\u517b\u6ca1\u6709\u4eba\u6559\u7684\u987d\u7ae5\uff0c\u6765\uff0c\u6765\uff0c\u5feb\u7ed9\u6211\u53e9\u4e09\u4e2a \u5934\uff0c\u8ba9\u6211\u6559\u4f60\u8bfb\u4e9b\u5723\u8d24\u4e4b\u4e66\uff0c\u6559\u4f60\u4e00\u4e9b\u505a\u4eba\u7684\u9053\u7406\u3002\u201d \u3000\u3000\u8fd9\u5389\u6587\u8c79\u6c14\u5f97\u54c7\u54c7\u602a\u53eb\u4e00\u58f0\uff0c\u4e00\u584c\u8eab\uff0c\u4f38\u51fa\u84b2\u6247\u822c\u5927\u7684\u5de6\u624b\uff0c\u5c31\u8981\u53bb\u7684\u8116\u5b50\u3002\u90a3\u5bd2\u9178\u5c11 \u4e2d\u4f3c\u4e4e\u9a87\u5f97\u9762\u76ee\u53d8\u8272\uff0c\u8fde\u8fde\u5012\u9000\uff0c\u4e24\u6761\u817f\u5374\u504f\u504f\u53c8\u50cf\u4e0d\u542c\u4f7f\u5524\uff0c\u8fde\u4f38\u90fd\u4f38\u4e0d\u76f4\u4e86\u3002 \u3000\u3000\u5389\u6587\u864e\u53cc\u7709\u4e00\u76b1\uff0c\u4e00\u58f0\u5927\u559d\uff0c\u9053\uff1a\u201c\u4e8c\u5f1f\uff0c\u4f4f\u624b\uff01\u201d\u8eab\u5f62\u4e00\u52a8\uff0c\u65b9\u8981\u8d76\u4e0a\u524d\u53bb\uff0c\u54ea\u77e5\u8eab \u65c1\u5149\u534e\u4e00\u95ea\uff0c\u539f\u6765\u90a3\u59d3\u5c55\u7684\u5c11\u5e74\uff0c\u5df2\u81ea\u63a0\u4e86\u8fc7\u53bb\uff0c\u4e00\u5251\u523a\u5411\u5389\u6587\u8c79\uff0c\u4e00\u9762\u559d\u9053\uff1a\u201c\u597d\u670b\u53cb\uff0c \u4f60\u8981\u52a8\u624b\uff0c\u53ea\u7ba1\u51b2\u7740\u6211\u59d3\u5c55\u7684\u6765\uff0c\u4f55\u5fc5\u51b2\u7740\u4eba\u5bb6\u53d1\u5a01\uff01 \u3000\u3000\u90a3\u5bd2\u9178\u5c11\u5e74\u4e00\u9762\u5012\u9000\uff0c\u4e00\u9762\u5728\u5634\u91cc\u8fde\u8fde\u56b7\u7740\uff1a\u201c\u5bf9\uff0c\u5bf9\uff0c\u4f60\u8981\u53d1\u5a01\uff0c\u5c31\u627e\u4eba\u5bb6\u4f7f\u5b9d\u5251\u7684 \u53bb\uff0c\u4f55\u5fc5\u6765\u627e\u6211\uff0c\u4f60\u8981\u662f\u628a\u6211\u8fd9\u53ea\u7897\u78b0\u788e\u4e86\uff0c\u5c31\u51b2\u4f60\u8fd8\u966a\u4e0d\u8d77\u54e9\u3002\u201d\u5634\u91cc\u867d\u662f\u8fd9\u6837\u56b7\u7740\uff0c\u4f46 \u8eab\u5f62\u4e71\u52a8\u4e4b\u4e0b\uff0c\u62ff\u7897\u7684\u624b\u5374\u534a\u70b9\u4e5f\u6ca1\u6709\u54c6\u6696\u3002 \u3000\u3000\u90a3\u5389\u6587\u864e\u53cc\u7709\u53c8\u4e00\u76b1\uff0c\u559d\u9053\uff1a\u201c\u59d3\u5c55\u7684\u670b\u53cb\u4f4f\u624b\uff01\u4e8c\u5f1f\uff0c\u5feb\u4f4f\u624b\uff01\u201d\u4e00\u9762\u4e5f\u63a0\u4e0a\u524d\u53bb\uff0c \u5c06\u5389\u6587\u8c79\u6321\u5230\u8eab\u540e\uff0c\u5374\u671d\u90a3\u5bd2\u9178\u5c11\u5e74\u5f53\u5934\u4e00\u63d6\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u9601\u4e0b\u867d\u7136\u771f\u4eba\u4e0d\u9732\u50cf\uff0c\u4f46\u5389\u6587 \u864e\u4e24\u773c\u4e0d\u778e\uff0c\u5374\u770b\u5f97\u51fa\u9601\u4e0b\u662f\u9ad8\u4eba\uff0c\u6211\u71d5\u4e91\u4e94\u5144\u5f1f\u4eca\u65e5\u5f53\u7740\u9601\u4e0b\u773c\u524d\u70b9\u7ebf\u5f00\u6252\uff0c\u867d\u7136\u65e0\u72b6\uff0c \u4f46\u6211\u5144\u5f1f\u5374\u6709\u4e0d\u5f97\u5df1\u7684\u82e6\u8877\uff0c\u5e0c\u671b\u9601\u4e0b\u9ad8\u9ad8\u624b\uff0c\u8ba9\u5144\u5f1f\u4eec\u5c06\u8fd9\u4e8b\u6599\u7406\u4e86\uff0c\u65e5\u540e\u655d\u5144\u5f1f\u4e00\u5b9a\u767b \u95e8\u5230\u5e9c\u4e0a\u53bb\u5411\u9601\u4e0bD0\u5934\u3002\u201d \u3000\u3000\u8fd9\u6df7\u8ff9\u6b66\u6797\u4e8c\u5341\u591a\u5e74\u7684\u8001\u6c5f\u6e56\uff0c\u773c\u91cc\u64a4\u4e0d\u8fdb\u534a\u7c92\u6c99\u5b50\uff0c\u6b64\u523b\u7ade\u5df2\u770b\u51fa\u8fd9\u5bd2\u9178\u5c11\u5e74\u5927\u6709\u6765 \u5934\uff0c\u8fde\u8fde\u4f5c\u63d6\uff0c\u8fde\u8fde\u8d54\u8bdd\uff0c\u5e0c\u671b\u4ed6\u4e0d\u8981\u4f38\u624b\u51fa\u6765\u7ba1\u8fd9\u8d9f\u95f9\u4e8b\uff0c\u514d\u5f97\u4e00\u5757\u5230\u53e3\u7684\u8089\u53c8\u98de\u4e86\u5f00 \u53bb\u3002 \u3000\u3000\u54ea\u77e5\u90a3\u5bd2\u9178\u5c11\u5e74\u6839\u672c\u4e0d\u4e70\u8d26\uff0c\u4e00\u9762\u5f2f\u8170\u6253\u6266\uff0c\u4e00\u9762\u8fde\u8fde\u8bf4\u9053\uff1a\u201c\u597d\u6c49\uff0c\u60a8\u522b\u4f5c\u63d6\uff0c\u5c0f\u751f \u8fd9\u53ef\u62c5\u5f53\u4e0d\u8d77\uff0c\u60a8\u8981\u5230\u5bd2\u820d\u53bb\uff0c\u5c0f\u751f\u66f4\u4e0d\u6562\u5f53\uff0c\u5bd2\u820d\u5730\u65b9\u592a\u5c0f\uff0c\u8981\u662f\u597d\u6c49\u4eec\u90fd\u53bb\u7684\u8bdd\uff0c\u8fde\u7ad9 \u7684\u5730\u65b9\u90fd\u6ca1\u6709\u3002\u201d \u3000\u3000\u8fd9\u5bd2\u9178\u5c11\u5e74\u4e00\u9762\u8bf4\u7740\u8bdd\uff0c\u4e00\u9762\u90e8\u5c06\u7709\u5934\u76b1\u4e86\u8d77\u6765\uff0c\u539f\u6765\u8fd9\u65f6\u9a84\u9633\u5df2\u843d\uff0c\u5f69\u971e\u897f\u5f25\uff0c\u5df2\u8fd1 \u9ec4\u660f\uff0c\u800c\u6797\u5916\u53c8\u4f20\u6765\u4e00\u9635\u9a6c\u8e44\u4e4b\u58f0\u3002\u5389\u6587\u5f6a\u9762\u8272\u4e00\u53d8\uff0c\u963b\u7740\u90a3\u4e0d\u77e5\u5929\u591a\u9ad8\u5730\u591a\u539a\u7684\u5389\u6587\u8c79\u53d1 \u5a01\uff0c\u5374\u53c8\u5411\u8fd9\u5bd2\u9178\u5c11\u5e74\u6df1\u6df1\u4f5c\u4e0b\u63d6\u53bb\uff0c\u8bf4\u9053\uff1a\u201c\u9601\u4e0b\u65e2\u7136\u8fd9\u4e48\u8bf4\uff0c\u90a3\u5c0f\u53ef\u5c31\u5148\u5411\u9601\u4e0b\u544a\u7f6a\uff0c \u65e0\u72b6\u4e4b\u5904\uff0c\u6211\u5144\u5f1f\u4eec\u65e5\u540e\u4e00\u5b9a\u767b\u95e8\u8c22\u8fc7\u3002\u201d\u4e00\u9762\u8f6c\u8fc7\u5934\uff0c\u671d\u4ed6\u7684\u5f1f\u5144\u5428\u559d\u9053\uff1a\u201c\u5f1f\u5144\u4eec\uff0c\u5929 \u5df2\u4e0d\u65e9\uff0c\u8fd8\u4e0d\u4fa0\u628a\u70b9\u5b50\u62db\u547c\u4e0b\u6765\uff01\u201d\u638c\u4e2d\u5224\u5b98\u53cc\u7b14\u4e00\u5206\uff0c\u8eab\u8eaf\u4e00\u8f6c\uff0c\u53cc\u7b14\u62a2\u51fa\uff0c\u5c31\u8981\u5411\u90a3\u59d3 \u5c55\u7684\u5c11\u5e74\u52a8\u624b\u3002 \u3000\u3000\u54ea\u77e5\u4ed6\u53ea\u89c9\u773c\u524d\u2014\u82b1\u2606\u6321\u5728\u81ea\u5df1\u9762\u524d\u7684\uff0c\u5374\u662f\u90a3\u5bd2\u9178\u5c11\u5e74\uff0c\u800c\u6b64\u523b\u6797\u53e3\u9a6c\u8e44\u7eb7\u5bc6\uff0c\u5df2\u6709 \u4e09\u9a91\u8fde\u631f\u9a70\u8fdb\u8fd9\u6811\u6797\u91cc\u6765\u3002 \u3000\u3000\u8fd9\u4e09\u9a91\u9a6c\u4e0a\u4eba\u7684\u8eab\u5f62\uff0c\u4e00\u5165\u4f17\u4eba\u4e4b\u76ee\uff0c\u201c\u71d5\u4e91\u4e94\u9738\u5929\u201d\u3001\u80d6\u7075\u5b98\u3001\u77f3\u7334\uff0c\u60e7\u90fd\u53c8\u4e3a\u4e4b\u9762 \u8272\u5927\u53d8\uff0c\u53ea\u89c1\u80d6\u7075\u5b98\u773c\u4e2d\u6240\u95ea\u52a8\u7684\uff0c\u5374\u662f\u7b11\u8272\uff0c\u4ed6\u7ade\u5c06\u8fd9\u8fb9\u7684\u4e8b\u6401\u5728\u65c1\u8fb9\uff0c\u653e\u5f00\u4e24\u6761\u80a5\u817f\u8dd1 \u5230\u8fd9\u4e09\u4eba\u7684\u9a6c\u524d\u9762\u53bb\uff0c\u6ee1\u8138\u5806\u4e0b\u7b11\u6765\uff0c\u6df1\u6df1\u4e00\u63d6\uff0c\u5df4\u7ed3\u5730\u8bf4\u9053\uff1a\u201c\u597d\u4e45\u6ca1\u6709\u770b\u5230\u4f60\u8001\u4eba\u5bb6 \u4e86\uff0c\u4f60\u8001\u4eba\u5bb6\u53ef\u5ac1\u79cd\u7269\u4e00\u76f4\u778e\u5fd9\uff0c\u4e5f\u6ca1\u6709\u53bb\u7ed9\u4f60\u8001\u4eba\u5bb6\u8bf7\u5b89\uff01\u201d\u9a6c\u4e0a\u662f\u4e09\u4e2a\u7a7f\u7740\u9171\u7d2b\u8272\u957f\u888d \u7684\u8001\u8005\uff0c\u5e74\u7eaa\u5df2\u6709\u4e94\u65ec\u4e0a\u4e0b\u4e86\uff0c\u5750\u5728\u9a6c\u4e0a\uff0c\u5374\u4ecd\u7136\u8170\u677f\u633a\u5f97\u7b14\u76f4\uff0c\u76ee\u5149\u4e2d\u66f4\u5e26\u7740\u593a\u4eba\u7684\u795e \u3000\u3000\u91c7\u3002 \u3000\u3000\u6b64\u523b\u90a3\u5389\u6587\u864e\uff0c\u4e5f\u6487\u4e0b\u6321\u5728\u81ea\u5df1\u9762\u524d\u7684\u5bd2\u9178\u5c11\u5e74\uff0c\u548c\u90a3\u6b63\u5728\u51b2\u81ea\u5df1\u77aa\u7740\u773c\u775b\u7684\u59d3\u5c55\u7684\u58ee \u58eb\uff0c\u63a0\u5230\u8fd9\u4e09\u4e2a\u7d2b\u886b\u8001\u8005\u7684\u9a6c\u524d\uff0c\u4e5f\u81ea\u957f\u63d6\u9053\uff1a\u201c\u662f\u54ea\u9635\u98ce\u5c06\u8001\u524d\u8f88\u5439\u5230\u8fd9\u91cc\u6765\u7684?\u665a\u8f88\u5389 \u6587\u864e\uff0c\u53e9\u95ee\u8001\u524d\u8f88\u7684\u91d1\u5b89\u3002\u201d\u4e09\u9a91\u4e4b\u4e2d\uff0c\u5f53\u5934\u7684\u4e00\u4eba\u662f\u4e2a\u7626\u5c0f\u7684\u8001\u8005\uff0c\u6b64\u523b\u5374\u53ea\u5728\u9f3b\u5b54\u91cc\u5fae \u5fbd\u54fc\u4e86\u4e00\u4e0b\uff0c\u7b97\u662f\u5bf9\u8fd9\u4e24\u4e2a\u53e9\u95ee\u7684\u4eba\u7b54\u793c\u3002\u7136\u540e\u4ed6\u8eab\u5f62\u5fae\u52a8\uff0c\u4ece\u9a6c\u4e0a\u63a0\u4e86\u4e0b\u6765\uff0c\u671b\u4e5f\u4e0d\u671b\u90a3 \u6b63\u5728\u671d\u81ea\u5df1\u5f2f\u8170\u7684\u201c\u71d5\u4e91\u4e94\u9738\u5929\u201d\u548c\u80d6\u7075\u5bab\u4e00\u773c\uff0c\u5374\u5f84\u81ea\u8d70\u5230\u90a3\u5bd2\u9178\u5c11\u5e74\u9762\u524d\uff0c\u800c\u4e14\u6df1\u6df1\u8eac \u4e0b\u8170\u53bb\u3002 \u3000\u3000\u8fd9\u4e00\u6765\uff0c\u4f17\u4eba\u624d\u5927\u60ca\u5931\u8272\uff0c\u8c01\u4e5f\u60f3\u4e0d\u5230\u8fd9\u4e00\u8eab\u786c\u8f6f\u529f\u592b\u5df2\u5165\u5316\u5883\u3001\u5c0f\u5de7\u8f7b\u8eab\u4e4b\u672f\u66f4\u4f20\u6d8c \u6b66\u6797\u7684\u6c5f\u6e56\u9876\u5c16\u9ad8\u624b\u4e4b\u4e00\uff0c\u6469\u4e91\u795e\u624b\u5411\u51b2\u5929\uff0c\u7adf\u4f1a\u5411\u4e00\u4e2a\u5bd2\u9178\u5c11\u5e74\u8eac\u8eab\u884c\u793c\u3002 \u3000\u3000\u8fd9\u5bd2\u9178\u5c11\u5e74\u54c8\u54c8\u4e00\u7b11\uff0c\u8eab\u8eaf\u4e00\u76f4\uff0c\u76ee\u4e2d\u767b\u65f6\u653e\u51fa\u795e\u91c7\u6765\uff0c\u5bd2\u9178\u7684\u6837\u5b50\uff0c\u7acb\u65f6\u968f\u7740\u4ed6\u53cc\u76ee \u4e00\u5f20\u800c\u8361\u7136\u65e0\u8e2a\u3002\u69db\u697c\u7684\u8863\u886b\uff0c\u4e5f\u53d8\u5f97\u4e0d\u518d\u69db\u697c\u4e86\uff0c\u56e0\u4e3a\u8fd9\u5bd2\u9178\u5c11\u5e74\u6b64\u523b\u795e\u91c7\u4e4b\u4e2d\uff0c\u7adf\u6709\u79cd \u4ee4\u4eba\u4e0d\u53ef\u903c\u89c6\u7684\u534e\u8d35\u4e4b\u6c14\u3002 \u3000\u3000\u4ed6\u4e00\u7b11\u8fc7\u540e\uff0c\u7528\u624b\u4e2d\u7684\u4e00\u5377\u7834\u4e66\u6307\u4e86\u6307\u7ad9\u5728\u4ed6\u9762\u524d\u7684\u201c\u6469\u4e91\u795e\u624b\u201d\u5411\u51b2\u5929\uff0c\u5634\u89d2\u4ecd\u7136\u5e26 \u7740\u4e00\u4e1d\u8151\u6d12\u7684\u7b11\u610f\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u5411\u8001\u54e5\uff0c\u4f60\u8fd9\u771f\u662f\u592a\u5de7\u4e86\uff0c\u4eba\u5bb6\u2018\u71d5\u4e91\u4e94\u9738\u5929\u2019\u6b63\u8981\u52a8\u5200\u5b50 \u6536\u62fe\u6211\uff0c\u4f60\u8981\u662f\u518d\u4e0d\u6765\uff0c\u6211\u8fd9\u6761\u547d\u5c31\u5f97\u9e23\u547c\u54c0\u54c9\u4e86\u3002\u201d\u90a3\u6614\u5e74\u72ec\u63e3\u6d59\u4e1c\u4e03\u5bb6\u9556\u5c40\uff0c\u53c8\u5728\u96c1\u8361 \u5c71\u5c06\u6c5f\u5357\u5de8\u76d7\u201c\u94c1\u9a91\u91d1\u5200\u201d\u6234\u4e1c\u9a8f\u4e00\u638c\u5288\u6b7b\uff0c\u4f7f\u5f97\u6b66\u6797\u9ed1\u767d\u4e24\u9053\u83ab\u4e0d\u95fb\u540d\u80c6\u843d\u7684\u201c\u6469\u4e91\u795e \u624b\u201d\u5411\u51b2\u5929\uff0c\u95fb\u8a00\u540e\u4fbf\u8f6c\u8fc7\u8eab\u6765\uff0c\u53cc\u76ee\u7535\u5f20\uff0c\u8e6c\u5728\u90a3\u5389\u6587\u864e\u7684\u8138\u4e0a\u3002 \u7b2c\u4e8c\u7ae0\u3000\u8ffd\u98ce\u65e0\u5f71>> \u53e4\u9f99\u300a\u5251\u5ba2\u884c\u300b \u7b2c\u4e8c\u7ae0\u3000\u8ffd\u98ce\u65e0\u5f71 \u3000\u3000\u8fd9\u6469\u4e91\u795e\u624b\u5411\u51b2\u5929\u4e00\u8f6c\u8eab\uff0c\u5389\u6587\u864e\u9762\u8272\u5c31\u7acb\u523b\u4e3a\u4e4b\u82cd\u767d\u8d77\u6765\uff0c\u54ea\u77e5\u5411\u51b2\u5929\u4ec5\u4ec5\u671d\u4ed6\u77aa\u4e86 \u4e00\u773c\uff0c\u968f\u5373\u53c8\u5411\u90a3\u5bd2\u9178\u5c11\u5e74\u9053\uff1a\u201c\u8001\u673d\u4e00\u6b65\u6765\u8fdf\uff0c\u5374\u6559\u8fd9\u4e9b\u6df7\u5e10\u5192\u72af\u4e86\u516c\u5b50\uff0c\u8001\u673d\u8fd9\u5c31\u5c06\u4ed6 \u4eec\u62ff\u4e0b\uff0c\u542c\u51ed\u516c\u5b50\u53d1\u843d\u3002\u201d \u3000\u3000\u90a3\u5bd2\u9178\u5c11\u5e74\u6717\u58f0\u4e00\u7b11\uff0c\u7f13\u6b65\u8d70\u4e86\u8fc7\u6765\uff0c\u4e00\u9762\u53c8\u7b11\u9053\uff1a\u5411\u5144\uff0c\u6211\u8fd9\u53ef\u662f\u8bf4\u7740\u73a9\u7684\uff0c\u4f60\u5207\u4e0d \u53ef\u8ba4\u771f\uff01\u201d\u8bf4\u7740\uff0c\u4ed6\u521a\u597d\u8d70\u5230\u5389\u6587\u8c79\u8eab\u6d4b\uff0c\u5c31\u5c06\u624b\u4e2d\u7684\u90a3\u53ea\u76d6\u7897\u4e00\u626c\uff0c\u5e26\u7b11\u9053\uff1a\u201c\u5389\u4e8c\u4fa0\uff0c \u8fd9\u7897\u91cc\u7684\u6885\u6c64\u8fd8\u6709\u5c11\u8bb8\uff0c\u9601\u4e0b\u53ef\u8fd8\u8981\u559d\u4e9b?\u201d \u3000\u3000\u5389\u6587\u8c79\u89c1\u4e86\u8fd9\u7b49\u9635\u4ed7\uff0c\u65e9\u5df2\u5c06\u9a84\u72c2\u4e4b\u6c14\u90fd\u7f29\u56de\u809a\u91cc\uff0c\u542c\u4e86\u8fd9\u8bdd\uff0c\u4e00\u5f20\u8138\u80c0\u5f97\u8ddf\u8304\u5b50\u4f3c \u7684\uff0c\u6cb9\u6cb9\u5730\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u8fd9\u5bd2\u9178\u5c11\u4e2d\u53c8\u5fae\u5fae\u4e00\u7b11\uff0c\u7528\u624b\u4e2d\u7684\u4e66\u62cd\u4e86\u62cd\u90a3\u77aa\u7740\u773c\u53d1\u695e\u3001\u540d\u53eb\u5c55\u767d\u7684\u5c11\u5e74\u80a9\u5934\uff0c\u9053\uff1a \u201c\u5c55\u58ee\u58eb\u4f7f\u5f97\u597d\u4e00\u624b\u5251\u6cd5\uff0c\u771f\u6559\u5144\u5f1f\u7fa1\u6155\u5f97\u5f88\uff01\u5c55\u58ee\u58eb\u5982\u4e0d\u5acc\u5f03\uff0c\u4e8b\u529e\u5b8c\u540e\u52a1\u5fc5\u8bf7\u5230\u5bd2\u820d\u805a \u805a\uff0c\u5144\u5f1f\u867d\u4e0d\u624d\uff0c\u5374\u6700\u597d\u7ed3\u4ea4\u670b\u53cb\u3002\u201d \u3000\u3000\u5c55\u767d\u8138\u8272\u5fae\u5fae\u4e00\u7ea2\uff0c\u4f46\u4ecd\u7136\u633a\u7740\u8170\u677f\uff0c\u62f1\u624b\u9053\uff1a\u201c\u516c\u5b50\u592a\u5938\u5956\u4e86\uff0c\u5c55\u767d\u8499\u516c\u5b50\u89e3\u56f4\uff0c\u6b64 \u6069\u6b64\u5fb7\uff0c\u6c38\u4e0d\u6562\u5fd8\uff0c\u65e5\u540e\u4e00\u5b9a\u767b\u95e8\u8bf7\u6559\uff0c\u62dc\u8c22\u516c\u5b50\u4eca\u65e5\u7684\u5927\u6069\u3002\u201d \u3000\u3000\u5bd2\u9178\u5c11\u5e74\u8fde\u8fde\u70b9\u5934\u7b11\u9053\uff1a\u201c\u597d\uff0c\u597d\uff01\u53ea\u662f\u62dc\u8bbf\u7684\u8bdd\uff0c\u518d\u4e5f\u4e0d\u8981\u63d0\u8d77\u3002\u201d\u8bf4\u7740\u53c8\u8d70\u5230\u5389\u6587 \u864e\u8eab\u524d\uff0c\u542b\u7b11\u63a5\u9053\uff1a\u201c\u5389\u5927\u4fa0\u4eca\u65e5\u53ef\u5426\u770b\u5c0f\u5f1f\u7684\u8584\u9762\uff0c\u9ad8\u9ad8\u624b\uff0c\u653e\u4ed6\u4eec\u8fc7\u53bb?\u5389\u5927\u4fa0\u5982\u679c\u9700 \u8981\u76d8\u7f20\uff0c\u5343\u513f\u516b\u767e\u7684\uff0c\u5c31\u7531\u5c0f\u5f1f\u9001\u7ed9\u8bf8\u4f4d\u3002\u201d \u3000\u3000\u90d1\u4f2f\u8c61\u76f4\u89c9\u6251\u901a\u4e00\u58f0\uff0c\u5fc3\u91cc\u7684\u4e00\u5757\u5927\u77f3\u843d\u4e86\u5730\uff0c\u4e00\u9762\u5374\u53c8\u6697\u5730\u5bfb\u601d\uff1a\u4e00\u51fa\u53e3\u5c31\u662f\u5343\u513f\u516b \u767e\u7684\uff0c\u8fd9\u5c11\u5e74\u597d\u5927\u7684\u53e3\u6c14\uff0c\u770b\u4ed6\u8fd9\u79cd\u6c14\u6d3e\uff0c\u83ab\u975e\u4e5f\u662f\u90a3\u56db\u4e2a\u4e3b\u51e0\u7684\u5176\u4e2d\u4e4b\u4e00\u5417? \u3000\u3000\u90a3\u5389\u6587\u864e\u8fde\u5fd9\u4e00\u62f1\u624b\uff0c\u5f3a\u7b11\u9053\uff0f\u516c\u5b50\u7684\u5429\u5490\uff0c\u5c0f\u7684\u600e\u6562\u4e0d\u9075\uff0e\u516c\u5b50\u7684\u539a\u8d50\uff0c\u5c0f\u7684\u66f4\u4e0d\u6562 \u9886\u3002\u53ea\u662f\u8fd8\u8bf7\u516c\u5b50\u793a\u77e5\u5927\u540d\uff0c\u4ee5\u4fbf\u5c0f\u7684\u56de\u53bb\uff0c\u5bf9\u655d\u5bb6\u4e3b\u6709\u4e2a\u4ea4\u5f85\u3002\u201d \u3000\u3000\u6b64\u8bdd\u4e00\u51fa\uff0c\u4f17\u4eba\u53c8\u90fd\u5fae\u60ca\uff0e\u5c31\u4ee5\u5389\u5bb6\u5144\u5f1f\u7684\u8fd9\u79cd\u7a7f\u7740\u6253\u626e\uff0c\u8c01\u53c8\u60f3\u5f97\u5230\u4ed6\u4eec\u53e6\u6709\u201c\u4e3b \u4eba\u201d? \u3000\u3000\u5bd2\u9178\u5c11\u5e74\u773c\u73e0\u4e00\u8f6c\uff0c\u4ecd\u542b\u7b11\u9053\uff0c\u201c\u60f3\u4e0d\u5230\uff0c\u60f3\u4e0d\u5230\uff0c\u58f0\u540d\u8d6b\u8d6b\u7684\u71d5\u4e91\u4e94\u9738\u5929\uff0c\u4e0a\u9762\u5c45\u7136 \u8fd8\u6709\u4e3b\u4eba\u3002\u201d\u4ed6\u76ee\u5149\u7a81\u5730\u2014\u51db\uff0c\u77aa\u5728\u5389\u6587\u864e\u8eab\u4e0a\uff0c\u63a5\u7740\u53c8\u9053\uff1a\u201c\u53ea\u662f\u4e0d\u77e5\u9053\u5389\u5f53\u5bb6\u7684\u53ef\u4e0d\u53ef \u4ee5\u544a\u8bc9\u5144\u5f1f\uff0c\u8d35\u5bb6\u4e3b\u662f\u54ea\u4f4d\u9ad8\u4eba?\u96be\u9053\u5389\u5f53\u5bb6\u8fd9\u780d\u62e6\u8def\u52ab\u9556\uff0c\u4e5f\u662f\u5949\u547d\u884c\u4e8b\u5417?\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u90a3\u6469\u4e91\u795e\u624b\u5df2\u8d70\u5230\u5bd2\u9178\u5c11\u5e74\u8eab\u4fa7\uff0c\u51b7\u51b7\u8bf4\u7740\uff1a\u201c\u516c\u5b50\uff0c\u60a8\u548c\u8fd9\u4e9b\u4eba\u7f57\u55e6\u4ec0\u4e48\uff01\u5429 \u5490\u4ed6\u4eec\u4e00\u58f0\uff0c\u8ba9\u4ed6\u4eec\u628a\u9556\u8f66\u9a7e\u8d70\u4e0d\u5c31\u5f97\u4e86\uff0c\u60a8\u8981\u662f\u518d\u548c\u8fd9\u73ed\u4eba\u5ba2\u6c14\uff0c\u4ed6\u4eec\u5c31\u8d8a\u53d1\u5f97\u610f\u4e86\u3002\u201d \u3000\u3000\u5389\u6587\u864e\u5230\u5e95\u4e5f\u662f\u6b66\u6797\u4e2d\u6210\u540d\u7acb\u4e07\u7684\u4eba\u7269\uff0c\u542c\u4e86\u8fd9\u8bdd\uff0c\u8138\u4e0a\u9752\u4e00\u9635\uff0c\u81ea\u4e00\u9635\uff0c\u4f46\u5374\u4e0d\u6562\u53d1 \u4f5c\u8d77\u6765\uff0c\u53ea\u5f97\u5fcd\u7740\u6c14\u9053\uff1a\u201c\u655d\u5144\u5f1f\u867d\u7136\u662f\u6b66\u6797\u4e2d\u7684\u65e0\u540d\u5c0f\u5352\uff0c\u53ef\u662f\uff0c\u655d\u5144\u5f1f\u7684\u5c45\u505c\u4e3b\u4eba\u5374\u4e0d \u662f\u666e\u666e\u901a\u901a\u7684\u6b66\u6797\u9053\uff0c\u6c5f\u6e56\u4e2d\u4eba\u591a\u591a\u5c11\u5c11\u4e5f\u5f97\u7ed9\u4ed6\u4e09\u5206\u9762\u5b50\uff0c\u53ea\u662f\u2014\u2014\u201d \u3000\u3000\u90a3\u6469\u4e91\u795e\u624b\u4e00\u77aa\u773c\uff0c\u559d\u65ad\u4e86\u4ed6\u7684\u8bdd\uff0c\u5389\u58f0\u9053\uff1a\u201c\u4f60\u600e\u7684\u8fd9\u4e48\u591a\u5e9f\u8bdd\uff01\u90a3\u5c0f\u5b50\u7684\u540d\u5b57\uff0c\u4f60 \u7231\u8bf4\u5c31\u8bf4\uff0c\u4e0d\u8bf4\u5c31\u5feb\u6eda\uff0c\u56de\u53bb\u544a\u8bc9\u4ed6\uff0c\u8fd9\u8d9f\u4e8b\u662f\u6211\u5411\u67d0\u4eba\u7ba1\u7684\uff0c\u6709\u4ec0\u4e48\u8bdd\uff0c\u6559\u4ed6\u90fd\u51b2\u6211\u5411\u67d0 \u4eba\u6765\u8bf4\u597d\u4e86\u3002\u201d \u3000\u3000\u8fd9\u5389\u6587\u864e\u9762\u8272\u8d8a\u53d1\u53d8\u5f97\u94c1\u9752\uff0c\u4e00\u8dfa\u811a\uff0c\u56de\u8eab\u5c31\u8d70\uff0c\u4e00\u9762\u62db\u547c\u7740\u9053\uff1a\u201c\u8001\u4e8c\uff0c\u8001\u4e09\uff0c\u65e2\u7136 \u5411\u8001\u524d\u8f88\u8fd9\u4e48\u8bf4\uff0c\u6211\u4eec\u8fd8\u4e0d\u8d70\u4e8e\u4ec0\u4e48\uff01\u201d\u4e00\u6389\u5934\uff0c\u671d\u90a3\u6b64\u523b\u7ad9\u5728\u65c1\u8fb9\u5df2\u5fc3\u5b89\u7406\u5f97\u7684\u80d6\u7075\u5bab\u51b7 \u7b11\u8bf4\u9053\uff1a\u201c\u59d3\u90d1\u7684\uff0c\u4eca\u5929\u662f\u4f60\u7684\u9020\u5316\uff0c\u4e0d\u8fc7\u6211\u544a\u8bc9\u4f60\uff0c\u4f60\u8f66\u5b50\u91cc\u90a3\u53e3\u7bb1\u5b50\uff0c\u53ef\u4e0d\u662f\u6211\u5389\u5bb6\u5144 \u5f1f\u8981\u7684\uff0c\u8981\u4e1c\u897f\u7684\u4eba\u662f\u8c01\uff0c\u4f60\u5fc3\u91cc\u7422\u78e8\u7422\u78e8\uff0c\u8981\u662f\u4f60\u4ee5\u540e\u8fd8\u60f3\u5728\u6c5f\u6e56\u4e2d\u6df7\uff0c\u8d81\u65e9\u8fd8\u662f\u5c06\u4e1c\u897f \u9001\u53bb\uff0c\u4e0d\u7136\u4ee5\u540e\u6362\u4e86\u522b\u4eba\u627e\u4f60\uff0c\u53ef\u5c31\u6ca1\u6709\u6211\u59d3\u5389\u7684\u8fd9\u6837\u597d\u8bf4\u75b2\u4e45\u201d \u3000\u3000\u4ed6\u8fd9\u660e\u662f\u5411\u90d1\u4f2f\u8c61\u5406\u559d\uff0c\u5176\u5b9e\u5374\u662f\u5411\u90a3\u5411\u51b2\u5929\u793a\u610f\u3002 \u3000\u3000\u5411\u51b2\u5929\u5982\u4eca\u5df2\u903e\u77e5\u547d\uff0c\u5728\u6b66\u6797\u4e2d\u6df7\u4e86\u4e09\u5341\u5e74\uff0c\u5bf9\u8fd9\u8bdd\u54ea\u8fd8\u4f1a\u542c\u4e0d\u51fa\u6765\u7528\u610f\u4f55\u5728?\u6b64\u523b\u4ed6 \u8eab\u5f62\u4e00\u52a8\uff0c\u5feb\u5982\u95ea\u7535\u5730\u63a0\u5230\u5389\u6587\u864e\u524d\u9762\uff0c\u5389\u53f1\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u4f60\u7adf\u6562\u8bf4\u8fd9\u79cd\u72c2\u8bdd\uff0c\u4eca\u5929\u6211\u5411 \u5927\u592a\u7237\u5012\u975e\u8981\u628a\u4f60\u7559\u4e0b\u6765\u4e0d\u53ef\uff0c\u770b\u770b\u4f60\u90a3\u4e3b\u5b50\u6709\u6ca1\u6709\u4e09\u5934\u516d\u81c2\uff0c\u80fd\u628a\u6211\u5411\u67d0\u4eba\u600e\u4e48\u7740\u201d\u4e00\u4f38 \u94c1\u638c\uff0c\u671d\u5389\u6587\u864e\u5f53\u80f8\u5c31\u6293\u3002 \u3000\u3000\u5389\u6587\u864e\u4e00\u62e7\u8eab\uff0c\u65cb\u53f3\u811a\uff0c\u8eb2\u5f00\u8fd9\u62db\uff0c\u8fd8\u6ca1\u6709\u6765\u5f97\u53ca\u8bf4\u8bdd\uff0c\u90a3\u5411\u51b2\u5929\u624b\u8098\u4e00\u6c89\uff0c\u5de6\u624b\u5df2\u5019 \u7136\u671b\u4ed6\u8155\u5b50\u6293\u53bb\uff0c\u5389\u6587\u864e\u7529\u5de6\u638c\uff0c\u518d\u5f80\u540e\u9000\u3002\u54ea\u77e5\u8fd9\u6469\u4e91\u4f38\u624b\u8eab\u624b\u4e4b\u5feb\uff0c\u7684\u786e\u4e0d\u540c\u51e1\u54cd\uff0c\u6839 \u672c\u8fde\u5598\u6c14\u7684\u529f\u592b\u90fd\u4e0d\u7ed9\u4eba\u5bb6\uff0c\u819b\u76ee\u4f4e\u53f1\u4e00\u58f0\uff1a\u201c\u8eba\u4e0b\uff01\u201d\u626d\u6b65\u8fdb\u8eab\uff0c\u5de6\u624b\u539f\u5f0f\u51fb\u51fa\uff0c\u53f3\u624b\u5fae \u5fae\u4e00\u5708\uff0c\u7adf\u521a\u597d\u52fe\u4f4f\u5389\u6587\u864e\u7684\u53f3\u8155\uff0c\u5f80\u5916\u4e00\u626f\u3002 \u3000\u3000\u5389\u6587\u864e\u53ea\u89c9\u534a\u8fb9\u8eab\u5b50\u4e00\u9ebb\uff0c\u968f\u7740\u4eba\u5bb6\u8fd9\u8f7b\u8f7b\u4e00\u62c9\uff0c\u77aa\u3001\u77aa\u3001\u77aa\uff0c\u5f80\u524d\u9762\u51b2\u4e86\u597d\u51e0\u6b65\uff0c\u5230 \u5e95\u7a33\u4e0d\u4f4f\u8eab\u5f62\uff0c\u5012\u5728\u5730\u4e0a\u3002 \u3000\u3000\u8fd9\u6469\u4e91\u795e\u624b\u4e00\u4f38\u624b\uff0c\u5c31\u5c06\u540d\u5934\u9887\u54cd\u7684\u201c\u71d5\u4e91\u4e94\u9738\u5929\u201d\u4e3a\u9996\u7684\u5389\u6587\u864e\u6cbb\u5f97\u8eba\u4e0b\u6765\uff0c\u4f17\u4eba\u5fc3 \u91cc\u90fd\u4e0d\u7981\u5012\u62bd\u4e00\u53e3\u51c9\u6c14\u3002 \u3000\u3000\u90a3\u540d\u53eb\u5c55\u767d\u7684\u5c11\u5e74\uff0c\u66f4\u662f\u6697\u53eb\u60ed\u6127\uff0c\u4e00\u79cd\u5931\u671b\u7684\u611f\u89c9\uff0c\u500f\u5730\u7a81\u4e0a\u5fc3\u5934\u3002\u65b9\u624d\u4ed6\u539f\u4ee5\u4e3a\u81ea \u5df1\u7684\u8eab\u624b\u5df2\u80fd\u5728\u6b66\u6797\u4e2d\u4e89\u4e00\u5e2d\u5730\u4f4d\uff0c\u81ea\u5df2\u8eab\u4e0a\u80cc\u8d1f\u7684\u90a3\u4e00\u6bb5\u8840\u6d77\u6df1\u4ec7\uff0c\u4e5f\u6709\u4e86\u62a5\u590d\u7684\u6307\u671b\u3002 \u4f46\u6b64\u523b\u89c1\u4e86\u4eba\u5bb6\u7684\u8eab\u624b\uff0c\u624d\u77e5\u9053\u81ea\u5df2\u4ecd\u7136\u5dee\u5f97\u592a\u8fdc\uff0c\u5fc3\u91cc\u4e00\u96be\u53d7\uff0c\u957f\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u5782\u4e0b\u5934 \u53bb\uff0c\u4f46\u89c9\u773c\u4e0b\u832b\u832b\uff0c\u524d\u9014\u53c8\u590d\u6e3a\u7136\u3002 \u3000\u3000\u8fd9\u4e00\u5239\u90a3\u95f4\uff0c\u5404\u4eba\u7684\u611f\u53d7\u81ea\u7136\u90fd\u4e0d\u76f8\u540c\uff0c\u90a3\u5389\u5bb6\u56db\u5144\u5f1f\u66f4\u662f\u4e00\u4e2a\u4e2a\u9762\u5b54\u53d1\u80c0\uff0c\u7ad9\u5728\u90a3 \u91cc\uff0c\u8fdb\u53c8\u4e0d\u662f\uff0c\u9000\u53c8\u4e0d\u662f\uff0c\u4e0d\u77e5\u8be5\u600e\u4e48\u597d\u3002 \u3000\u3000\u5411\u51b2\u5929\u76ee\u5149\u56db\u8f6c\uff0c\u51db\u7136\u5728\u90a3\u5389\u6c0f\u56db\u9738\u5929\u7684\u8138\u4e0a\u6e9c\u8fc7\uff0c\u84e6\u5730\u5389\u559d\u9053\uff1a\u201c\u4f60\u4eec\u8fd8\u4e0d\u7ed9\u6211\u6eda\uff0c \u56de\u53bb\u544a\u8bc9\u4f60\u4eec\u4e3b\u5b50\uff0c\u5c31\u8bf4\u5389\u6587\u864e\u5df2\u7ecf\u7ed9\u6211\u6263\u4e0b\u4e86\uff0c\u4ed6\u6709\u4ec0\u4e48\u624b\u6bb5\uff0c\u5c3d\u7ba1\u51b2\u6211\u5411\u67d0\u4eba\u65bd\u5c55\u597d \u4e86\u3002\u201d\u90a3\u5bd2\u9178\u5c11\u5e74\u5374\u53c8\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5411\u5144\u706b\u6027\u4ecd\u7136\u4e0d\u51cf\u5f53\u5e74\uff0c\u96be\u602a\u6614\u5e74\u6b66\u6797\u5bb5\u5c0f\uff0c\u4e00\u95fb \u6469\u4e91\u795e\u624b\u4e4b\u540d\uff0c\u5c31\u60f6\u7136\u8272\u53d8\uff0c\u4f46\u662f\u2014\u2014\u5411\u5144\uff0c\u4f60\u5374\u4e5f\u72af\u4e0d\u7740\u751f\u8fd9\u4e48\u5927\u7684\u6c14\u3002\u201d \u3000\u3000\u8bf4\u7740\uff0c\u4ed6\u7adf\u4f38\u624b\u7279\u5389\u6587\u864e\u4ece\u5730\u4e0a\u6276\u8d77\u6765\uff0c\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u5389\u5f53\u5bb6\u7684\uff0c\u4f60\u8fd9\u53ef\u5c31\u4e0d\u5bf9\u4e86\uff0c\u4ee4 \u5c45\u505c\u4e3b\u4eba\u5230\u5e95\u662f\u8c01?\u4f60\u4e5f\u8be5\u8bf4\u51fa\u6765\u5440\uff0c\u96be\u9053\u5144\u5f1f\u8fd9\u4e48\u4e0d\u624d\uff0c\u8fde\u8d35\u4e3b\u4eba\u7684\u540d\u5b57\u90fd\u4e0d\u914d\u542c\u5417?\u201d \u3000\u3000\u90a3\u5389\u6587\u864e\u4e00\u8e6c\u8dcc\u5728\u5730\u4e0a\uff0c\u5c06\u8eab\u4e0a\u7684\u90a3\u4e00\u88ad\u534e\u670d\uff0c\u5f04\u5f97\u5230\u5904\u662f\u7070\uff0c\u8138\u8272\u5ffd\u9752\u5ffd\u767d\uff0c\u5fc3\u91cc\u7f9e \u6124\u5df2\u6781\uff0c\u54ac\u7740\u7259\u6c89\u541f\u4e86\u534a\u664c\uff0c\u731b\u4e00\u8dfa\u811a\uff0c\u5f88\u58f0\u9053\uff1a\u201c\u6211\u5389\u6587\u864e\u4eca\u65e5\u88ab\u8fd9\u6837\u4f5c\u8df5\uff0c\u8fd9\u53ea\u602a\u6211\u59d3 \u5389\u7684\u5b66\u827a\u4e0d\u7cbe\uff0c\u4f46\u2014\u2014\u201d \u3000\u3000\u4ed6\u8f6c\u8eab\u671d\u7740\u5411\u51b2\u5929\u4e00\u54ac\u7259\uff0c\u63a5\u7740\u53c8\u9053\uff1a\u201c\u5411\u5927\u4fa0\uff0c\u4f60\u8981\u662f\u5bf9\u6211\u6240\u8bf4\u6709\u5173\u655d\u5c45\u505c\u7684\u8bdd\u4e0d \u6ee1\uff0c\u4f55\u5fc5\u5bf9\u6211\u4eec\u8fd9\u79cd\u665a\u751f\u540e\u8f88\u52a8\u624b?\u4f60\u53ef\u4ee5\u627e\u655d\u5c45\u505c\uff0c\u6559\u8bad\u4ed6\u53bb\uff0c\u53ea\u4f2f\u2014\u2014\u4f60\u4e5f\u8ba4\u4e3a\u655d\u5c45\u505c \u592a\u4e0d\u624d\uff0c\u4e0d\u503c\u5f97\u4f60\u6559\u8bad\uff01\u201d \u3000\u3000\u5411\u51b2\u5929\u76ee\u5149\u53c8\u4e00\u51db\uff0c\u5f20\u5927\u773c\u775b\uff0c\u5428\u9053\uff1a\u201c\u59d3\u5389\u7684\uff0c\u4f60\u2014\u2014\u201d \u3000\u3000\u5374\u88ab\u90a3\u5bd2\u9178\u5c11\u5e74\u542b\u7b11\u62e6\u4f4f\uff0c\u9053\uff1a\u201c\u5411\u5144\uff0c\u522b\u53d1\u706b\uff0c\u522b\u53d1\u706b\uff01\u542c\u4ed6\u8bf4\u4e0b\u53bb\u5427\uff01\u6b64\u4eba\u5012\u5f15\u8d77 \u5c0f\u5f1f\u7684\u5174\u8da3\u6765\u4e86\uff0c\u5982\u679c\u5c0f\u5f1f\u731c\u5f97\u4e0d\u9519\u7684\u8bdd\uff0c\u90a3\u5012\u771f\u53ef\u80fd\u6709\u620f\u5531\u4e86\uff01\u201d \u3000\u3000\u5389\u6587\u864e\u53cc\u6697\u77aa\u5728\u5411\u51b2\u5929\u8eab\u4e0a\uff0c\u53f3\u624b\u4e00\u4f38\uff0c\u4f38\u51fa\u56db\u6839\u624b\u6307\u6765\uff0c\u51b7\u51b7\u63a5\u7740\u9053\uff1a\u655e\u5c45\u505c\u4e3b\u4eba\u4f4f \u5728\u5357\u4eac\uff0c\u59d3\u91d1\uff0c\u5c31\u662f\u8fd9\u4f4d\u4e3b\u513f\uff0c\u5411\u8001\u524d\u8f88\uff0c\u60f3\u5fc5\u4e5f\u77e5\u9053\u4ed6\u5427\uff01\u4e0d\u8fc7\u4ee5\u5411\u8001\u524d\u8f88\u8fd9\u79cd\u8eab\u4efd\uff0c\u81ea \u7136\u4e5f\u4e0d\u4f1a\u5c06\u4ed6\u653e\u5728\u773c\u91cc\u3002\u3002 \u3000\u3000\u53ef\u662f\u8fd9\u4e00\u5411\u72ec\u65ad\u72ec\u884c\u3001\u7d20\u6765\u5fc3\u9ad8\u6c14\u50b2\u7684\u6469\u4e91\u795e\u624b\uff0c\u5728\u770b\u4e86\u4ed6\u8fd9\u624b\u52bf\u3001\u542c\u4e86\u4ed6\u8fd9\u8bdd\u4e4b\u540e\uff0c \u867d\u7136\u6781\u529b\u63a7\u5236\u81ea\u5df1\u7684\u60c5\u7eea\uff0c\u8138\u4e0a\u7684\u795e\u8272\u5374\u4ecd\u7136\u4e0d\u7981\u53d8\u4e86\u4e00\u4e0b\u3002 \u3000\u3000\u90a3\u80d6\u7075\u5b98\u548c\u77f3\u7334\u4faf\u8146\u5584\uff0c\u8fd9\u65f6\u66f4\u662f\u9762\u5bb9\u60e8\u53d8\uff0c\u4e92\u76f8\u5bf9\u671b\u4e86\u4e00\u773c\uff1b\u90a3\u90d1\u4f2f\u8c61\u4e00\u5f20\u5634\uff0c\u50cf\u662f \u60f3\u8bf4\u8bdd\uff0c\u5374\u542c\u90a3\u5bd2\u9178\u5c11\u5e74\u4ef0\u5929\u5927\u7b11\u4e86\u8d77\u6765\uff0c\u4ed6\u5fc3\u4e2d\u4e00\u52a8\uff0c\u5c06\u5634\u8fb9\u7684\u8bdd\u53c8\u5fcd\u4f4f\u4e86\u3002 \u3000\u3000\u8fd9\u4e00\u6765\uff0c\u90a3\u5389\u6587\u864e\u53cd\u5012\u6115\u4f4f\u4e86\uff0c\u4ed6\u53ea\u671b\u81ea\u5df1\u8bf4\u51fa\u4e3b\u4eba\u7684\u540d\u5b57\u540e\uff0c\u522b\u4eba\u4e00\u5b9a\u4f1a\u5927\u60ca\u5931\u8272\uff0c \u751a\u81f3\u5c06\u81ea\u5df1\u6240\u8981\u7684\u4e1c\u897f\u53cc\u624b\u5949\u4e0a\u90fd\u672b\u53ef\u77e5\uff0c\u8fd9\u5bd2\u9178\u5c11\u5e74\u867d\u7136\u4e00\u5b9a\u4e5f\u6709\u6765\u5934\uff0c\u4f46\u6bd4\u8d77\u81ea\u5df1\u6240\u8bf4 \u51fa\u7684\u8fd9\u4eba\u6765\uff0c\u4e5f\u4e00\u5b9a\u5927\u5927\u900a\u8272\uff0c\u6469\u4e91\u795e\u624b\u6b66\u529f\u867d\u9ad8\uff0c\u5374\u4e5f\u4e07\u4e07\u60f9\u4e0d\u8d77\u8fd9\u4eba\uff0c\u662f\u4ee5\u4ed6\u795e\u8272\u4e4b \u95f4\uff0c\u624d\u4f1a\u6709\u90a3\u6837\u7684\u6001\u5ea6\uff0c\u54ea\u77e5\u8fd9\u5bd2\u9178\u5c11\u5e74\u542c\u4e86\u81ea\u5df1\u6240\u8bf4\u90a3\u8db3\u4ee5\u9707\u52a8\u6c5f\u6e56\u7684\u540d\u5b57\uff0c\u5374\u7eb5\u58f0\u5927\u7b11 \u8d77\u6765\u3002 \u3000\u3000\u8fd9\u5bd2\u9178\u5c11\u5e74\u7b11\u58f0\u672b\u4f4f\uff0c\u5374\u5c06\u624b\u4e2d\u59cb\u7ec8\u6258\u7740\u7684\u90a3\u53ea\u5b9d\u84dd\u76d6\u7897\u7684\u7897\u76d6\uff0c\u7528\u4e24\u53ea\u624b\u6307\u631f\u4e86\u8d77 \u6765\uff0c\u671d\u8fd9\u5389\u6587\u864e\u9762\u524d\u4e00\u6643\u3002 \u3000\u3000\u5389\u6587\u864e\u76ee\u5149\u52a8\u5904\uff0c\u770b\u5230\u5728\u8fd9\u7897\u76d6\u91cc\u9762\uff0c\u5374\u5199\u7740\u51e0\u4e2a\u5b87\uff0c\u4ed6\u76ee\u529b\u672c\u4f73\uff0c\u5fd9\u51dd\u775b\u4e00\u770b\uff0c\u53ea\u89c1 \u8fd9\u7897\u91cc\u9762\u7adf\u8d6b\u7136\u5199\u7740\uff1a\u201c\u5b89\u4e50\u516c\u5b50\u6700\u98ce\u6d41\u3002\u201d \u3000\u3000\u5b57\u662f\u6b8a\u7802\u8272\uff0c\u5f62\u5982\u9f99\u98de\u98ce\u821e\uff0c\u7b14\u529b\u82cd\u52b2\uff0c\u4e0b\u9762\u8fd8\u7f72\u7740\u4e0b\u6b3e\uff1a\u201c\u94ee\u5144\u6e05\u73a9\uff0c\u6a0a\u975e\u62dc\u8d60\u3002\u201d \u3000\u3000\u8fd9\u4e9b\u5b57\u8ff9\u4e00\u5165\u5389\u6587\u864e\u4e4b\u76ee\uff0c\u5389\u6587\u864e\u53ea\u89c9\u773c\u524d\u4e00\u82b1\uff0c\u9669\u4e9b\u53c8\u4e00\u8e6c\u8dcc\u5728\u5730\u4e0a\uff0c\u5fae\u5fae\u62ac\u5934\uff0c\u770b \u5230\u8fd9\u5bd2\u9178\u5c11\u5e74\u4ecd\u5728\u5e26\u7b11\u671b\u7740\u81ea\u5df1\uff0c\u5934\u4e0d\u7981\u5f80\u4e0b\u4e00\u5782\uff0c\u5374\u53c8\u770b\u5230\u5bd2\u9178\u5c11\u5e74\u90a3\u53cc\u5df2\u7ecf\u7834\u70c2\u4e0d\u582a\u7684 \u978b\u5b50\uff0c\u6b64\u523b\u5728\u4ed6\u773c\u4e2d\uff0c\u5df2\u622a\u7136\u6709\u4e86\u53e6\u4e00\u79cd\u4ef7\u503c\u4e86\uff0c\u56e0\u4e3a\u52b3\u82e6\u5929\u4e0b\uff0c\u53c8\u6709\u8c01\u6562\u8bf4\u7a7f\u5728\u201c\u5b89\u4e50\u516c \u5b50\u201d\u4e91\u94ee\u8db3\u4e0b\u7684\u978b\u5b50\u662f\u4e0d\u503c\u4e00\u6587\u7684? \u3000\u3000\u8fd9\u7d20\u6765\u9634\u9e33\u6df1\u6c89\u7684\u5389\u6587\u864e\uff0c\u6b64\u523b\u4e5f\u53d8\u5f97\u624b\u8db3\u5931\u63aa\u4e86\u8d77\u6765\uff0c\u56e0\u4e3a\u4ed6\u77e5\u9053\u81ea\u5df1\u6240\u501a\u4ed7\u7684\u4eba\uff0c \u5728\u8fd9\u4eba\u9762\u524d\uff0c\u5df2\u4e0d\u662f\u81ea\u5df2\u80fd\u591f\u501a\u4ed7\u7684\u4e86\u3002 \u3000\u3000\u90a3\u5bd2\u9178\u5c11\u5e74\u54c8\u54c8\u2606\u7b11\uff0c\u9053\uff1a\u201c\u5389\u5f53\u5bb6\u7684\uff0c\u4f60\u6b64\u523b\u8be5\u77e5\u9053\u5c0f\u5f1f\u662f\u8c01\u4e86\u5427?\u90a3\u4e48\uff0c\u5c31\u8bf7\u56de\u53bb \u4e0a\u8986\u91d1\u516c\u5b50\uff0c\u5c31\u8bf4\u4eca\u5929\u5356\u4e86\u6211\u4e91\u94ee\u4e00\u4e2a\u9762\u5b50\uff0c\u54c8\u54c8\u2026\u2026\u201d\u4ed6\u6717\u58f0\u4e00\u7b11\uff0c\u53c8\u9053\uff1a\u201c\u6211\u548c\u7965\u9e9f\u516c \u5b50\u867d\u7136\u65e0\u7f18\u89c1\u9762\uff0c\u4f46\u5374\u65e9\u5df2\u503e\u6155\u5f97\u5f88\uff0c\u8fd8\u8bf7\u5389\u5f53\u5bb6\u56de\u53bb\u4ee3\u5728\u4e0b\u5411\u91d1\u516c\u5b50\u95ee\u597d\u3002\u201d \u3000\u3000\u5389\u6587\u864e\u6b64\u523b\u518d\u4e5f\u786c\u4e0d\u8d77\u6765\u4e86\uff0c\u552f\u552f\u7b54\u5e94\u7740\u3002\u90a3\u4e91\u94ee\u53c8\u4e00\u7b11\u9053\uff1a\u201c\u5389\u5f53\u5bb6\u7684\u6b64\u523b\u4e8b\u60c5\u65e2\u5df2 \u4e86\u7ed3\uff0c\u5144\u5f1f\u4e5f\u4e0d\u4fbf\u5c48\u7559\u5927\u9a7e\uff0c\u5982\u679c\u65e5\u540e\u6709\u5174\uff0c\u9601\u4e0b\u4e0d\u59a8\u5230\u82cf\u5dde\u5bd2\u820d\u53bb\u76d8\u6853\u51e0\u5929\uff0c\u54c8\u54c8\u2026\u2026\u5389 \u5f53\u5bb6\u7684\u5c31\u8bf7\u4fbf\u5427\uff01\u201d \u3000\u3000\u8fd9\u65f6\u4e0d\u4f46\u5389\u6587\u864e\u6817\u7136\u8272\u53d8\uff0c\u5176\u4f59\u7684\u4eba\u4e5f\u4e0d\u7981\u90fd\u4ea4\u76f8\u52a8\u5bb9\uff0c\u5389\u6587\u864e\u8bfa\u8bfa\u8fde\u58f0\uff0c\u5012\u9000\u7740\u8d70\u4e86 \u4e24\u6b65\uff0c\u53c8\u6df1\u6df1\u4e00\u63d6\uff0c\u4e00\u56de\u8eab\uff0c\u5b9a\u5411\u6797\u8fb9\u3002 \u3000\u3000\u5389\u6c0f\u5144\u5f1f\u4eec\u7acb\u5373\u90fd\u8ddf\u5728\u540e\u9762\uff0c\u8fd9\u65b9\u624d\u8fd8\u4e0d\u53ef\u4e00\u4e16\u7684\u201c\u71d5\u4e91\u4e94\u9738\u5929\u201d\uff0c\u6b64\u523b\u5374\u4e00\u4e2a\u4e2a\u5782\u5934 \u4e27\u6c14\uff0c\u4e00\u8a00\u4e0d\u53d1\u5730\u8d70\u4e86\u3002 \u3000\u3000\u5c11\u5e74\u5c55\u767d\uff0c\u77aa\u7740\u5927\u773c\u775b\u7ad9\u5728\u65c1\u8fb9\uff0c\u5c06\u8fd9\u4e00\u5207\u4e8b\u90fd\u770b\u5728\u773c\u91cc\uff0c\u542c\u5728\u8033\u91cc\uff0c\u4ed6\u770b\u5230\u8fd9\u5b89\u4e50\u516c \u5b50\u4e91\u950b\u7684\u98d8\u98d8\u795e\u91c7\uff0c\u6717\u6717\u4fa0\u884c\uff0c\u81ea\u5df1\u5fc3\u80f8\u4e4b\u95f4\uff0c\u987f\u65f6\u4e5f\u89c9\u5f97\u70ed\u8840\u6cb8\u817e\uff0c\u4e0d\u80fd\u81ea\u5df2\u3002 \u3000\u3000\u90a3\u6469\u4e91\u795e\u624b\u671b\u7740\u201c\u71d5\u4e91\u4e94\u9738\u5929\u201d\u6325\u97ad\u6025\u53bb\u7684\u80cc\u5f71\uff0c\u7779\u5730\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5357\u4eac\u57ce\u91cc\u7684\u90a3 \u4e2a\u4e3b\u513f\uff0c\u6700\u8fd1\u4e5f\u8d8a\u95f9\u8d8a\u4e0d\u50cf\u8bdd\u4e86\uff0c\u4e91\u516c\u5b50\u201d \u3000\u3000\u4e91\u94ee\u5374\u6717\u58f0\u4e00\u7b11\uff0c\u622a\u4f4f\u4ed6\u7684\u8bdd\u9053\uff1a\u201c\u5411\u8001\u5e08\uff0c\u6811\u5927\u62db\u98ce\uff0c\u540d\u9ad8\u60f9\u5992\uff0c\u6211\u4f55\u5c1d\u53c8\u4e0d\u662f\u81ed\u540d \u5728\u5916?\u6c5f\u6e56\u4e2d\u7684\u95f2\u8a00\u95f2\u8bed\uff0c\u591a\u662f\u542c\u4e0d\u5f97\u7684\u3002\u201d\u4ed6\u8bed\u58f0\u5fae\u987f\uff0c\u53c8\u9053\uff0c\u201c\u65b9\u624d\u90a3\u53eb\u4ec0\u4e48\u2018\u4e94\u9738 \u5929\u2019\u7684\uff0c\u591a\u534a\u662f\u501f\u7740\u2018\u6837\u78f7\u516c\u5b50\u2019\u7684\u62db\u724c\uff0c\u5728\u5916\u60f9\u662f\u751f\u975e\uff0c\u5509\uff01\u8fd9\u79cd\u4e8b\uff0c\u6211\u4e5f\u7ecf\u5f97\u591a\u4e86\uff0c\u5411 \u8001\u5e08\uff0c\u4f60\u8fd8\u8bb0\u4e0d\u8bb0\u5f97\uff0c\u5415\u8001\u516d\u90a3\u6b21\u5728\u9547\u6c5f\u60f9\u7978\uff0c\u4e0d\u4e5f\u662f\u63a5\u7740\u6211\u7684\u62db\u724c\u5417?\u82e5\u4e0d\u662f\u6a0a\u5927\u7237\u77e5 \u9053\uff0c\u6211\u4e0d\u53c8\u60f9\u4e00\u573a\u662f\u975e?\u201d \u3000\u3000\u6469\u4e91\u795e\u624b\u542c\u4e86\uff0c\u8138\u4e0a\u867d\u4ecd\u5fae\u6709\u4e0d\u8c6b\u4e4b\u8272\uff0c\u4f46\u8fd8\u662f\u552f\u552f\u5e94\u4e86\u3002 \u3000\u3000\u5c11\u5e74\u5c55\u767d\u770b\u5728\u670d\u91cc\uff0c\u5bf9\u8fd9\u5b89\u4e50\u516c\u5b50\u8fd9\u79cd\u6062\u5b8f\u6c14\u5ea6\uff0c\u4e0d\u7981\u53c8\u6697\u6697\u4e3a\u4e4b\u5fc3\u6298\u3002 \u3000\u3000\u90a3\u4e24\u4e2a\u4eac\u57ce\u540d\u6355\uff0c\u6b64\u523b\u65e9\u5c31\u5806\u7740\u4e00\u8138\u7b11\uff0c\u9e93\u4e86\u8fc7\u6765\uff0c\u4e00\u9f50\u8eac\u8eab\u65bd\u4e0b\u793c\u53bb\uff0c\u8bda\u714c\u8bda\u6050\u5730\u8bf4 \u9053\uff1a\u201c\u5c0f\u7684\u4eec\u6709\u773c\u65e0\u73e0\uff0c\u521a\u624d\u6ca1\u6709\u8ba4\u51fa\u60a8\u8001\u4eba\u5bb6\u6765\uff0c\u4eca\u5929\u5c0f\u7684\u4eec\u627f\u4e91\u516c\u5b50\u60a8\u5fff\u660e\u4ed7\u4e49\u63f4\u624b\uff0c \u5b9e\u5728\u611f\u6fc0\u4e0d\u5c3d\uff0c\u53ea\u662f\u5c0f\u7684\u4eec\u6709\u516c\u4e8b\u5728\u8eab\uff0c\u53c8\u4e0d\u4fbf\u591a\u4f3a\u5019\u60a8\u8001\u4eba\u5bb6\uff0c\u53ea\u597d\u4ee5\u540e\u518d\u4eb2\u5230\u5e9c\u4e0a\u7ed9\u60a8 \u8001\u4eba\u5bb6\u53e9\u5934\u3002\u201d \u3000\u3000\u4e00\u9762\u53c8\u8f6c\u8fc7\u5934\uff0c\u671d\u6469\u4e91\u795e\u624b\u5411\u51b2\u5929\u8eac\u8eab\u3001\u65bd\u793c\u3001\u8d54\u8bdd\u3002 \u3000\u3000\u4e91\u94ee\u5fae\u4e00\u6325\u624b\uff0c\u542b\u7b11\u8bf4\u9053\uff1a\u201c\u4e91\u67d0\u6b64\u6b21\u9002\u9022\u5176\u4f1a\uff0c\u7406\u5e94\u66ff\u4e24\u4f4d\u6548\u52b3\uff0c\u8c08\u4e0d\u4e0a\u4ec0\u4e48\u611f \u6fc0\u3002\u201d \u3000\u3000\u8fd9\u7a7f\u7740\u4e00\u88ad\u5bd2\u886b\u7684\u6c5f\u5357\u9996\u5bcc\u7684\u516c\u5b50\u3001\u540d\u91cd\u6b66\u6797\u7684\u201c\u56db\u5927\u516c\u5b50\u201d\u4e4b\u4e00\uff0c\u6b64\u523b\u76ee\u5149\u4e00\u8f6c\uff0c\u5374 \u8f6c\u5230\u5c11\u4e2d\u5c55\u767d\u8eab\u4e0a\uff0c\u542b\u7b11\u53c8\u9053\uff1a\u201c\u8fd9\u4f4d\u5144\u53f0\u597d\u4fca\u7684\u8eab\u624b\uff0c\u5c0f\u5f1f\u65e5\u540e\u5012\u60f3\u548c\u9601\u4e0b\u591a\u4eb2\u8fd1\u4eb2\u8fd1\uff0c \u5bd2\u820d\u5c31\u5728\u82cf\u5dde\u57ce\u5916\u7684\u4e91\u68a6\u5c71\u5e84\uff0c\u5144\u53f0\u65e5\u540e\u7ecf\u8fc7\u82cf\u5dde\uff0c\u5343\u4e07\u522b\u5fd7\u4e86\u5230\u820d\u4e0b\u76d8\u6853\u51e0\u5929\u3002\u201d\u5fae\u5fae\u4e00 \u987f\uff0c\u53c8\u9053\uff1a\u201c\u8fd8\u6709\uff0c\u5144\u53f0\u56de\u5230\u9556\u5c40\u91cc\uff0c\u4e5f\u8bf7\u4ee3\u5c0f\u5f1f\u5728\u8339\u8001\u9556\u5934\u8ddf\u524d\u95ee\u597d\u3002\u201d \u3000\u3000\u5c11\u5e74\u5c55\u767d\u6307\u950b\u6cbf\u7740\u5251\u810a\u4e00\u62b9\uff0c\u7075\u5de7\u5730\u56de\u5251\u5165\u9798\uff0c\u4ed6\u4eba\u9556\u5c40\u867d\u672a\u597d\u4e45\uff0c\u4f46\u5374\u662f\u6b66\u6797\u4e16\u5bb6\u3002 \u4ed6\u4e0d\u7981\u6709\u4e9b\u60ed\u6127\uff01\u6b63\u60f3\u542f\u53e3\u8c26\u8c22\u51e0\u53e5\uff0c\u54ea\u77e5\u773c\u524d\u7a81\u7136\u4eba\u5f71\u4e00\u82b1\uff0c\u81ea\u5df1\u638c\u4e2d\u5df2\u7ecf\u56de\u9798\u4e00\u534a\u7684\u957f \u5251\uff0c\u4e0d\u77e5\u600e\u5730\uff0c\u5df2\u7ecf\u5230\u4e86\u4eba\u5bb6\u624b\u4e0a\u3002 \u3000\u3000\u8fd9\u4e00\u6765\uff0c\u4ed6\u4e0d\u7981\u4e3a\u4e4b\u5927\u5403\u4e00\u60ca\uff0c\u987b\u77e5\u4ed6\u6b66\u529f\u867d\u4e0d\u751a\u9ad8\uff0c\u4f46\u5374\u66fe\u523b\u82e6\u4e0b\u8fc7\u529f\u592b\uff0c\u773c\u529b\u3001\u624b \u52b2\u5728\u6b66\u6797\u4e2d\u5df2\u5927\u53ef\u8bf4\u5f97\u8fc7\u53bb\uff0c\u4f46\u6b64\u523b\u660e\u660e\u4ed6\u81ea\u5df2\u62ff\u5f97\u6781\u7a33\u7684\u957f\u5251\uff0c\u7adf\u4f1a\u5728\u4e00\u59bb\u773c\u95f4\u88ab\u4eba\u5bb6\u593a \u53bb\uff0c\u4ed6\u5927\u60ca\u4e4b\u4e0b\uff0c\u51dd\u6708\u4e00\u671b\uff0c\u5374\u89c1\u7ad9\u5728\u81ea\u5df1\u9762\u524d\u7684\uff0c\u7adf\u662f\u90a3\u5148\u524d\u548c\u6469\u4e91\u795e\u624b\u5411\u51b2\u5929\u4e00\u9f50\u7b56\u9a6c \u5165\u6797\u7684\u4e00\u4e2a\u8c8c\u4e0d\u60ca\u4eba\u7684\u7626\u5c0f\u8001\u8005\u3002 \u3000\u3000\u800c\u8fd9\u7626\u5c0f\u8001\u8005\uff0c\u6b64\u523b\u624b\u4e0a\u5374\u6b63\u62ff\u7740\u81ea\u5df1\u90a3\u67c4\u7231\u903e\u6027\u547d\u7684\u957f\u5251\uff0c\u4e00\u624b\u628a\u7740\u5251\u67c4\uff0c\u4e00\u624b\u5fae\u634f \u5251\u68a2\uff0c\u5728\u82e5\u65e0\u5176\u4e8b\u5730\u628a\u73a9\u7740\u3002 \u3000\u3000\u5c11\u5e74\u5c55\u767d\u4e0d\u7981\u5251\u7709\u5fae\u8f69\uff0c\u9690\u542b\u6012\u610f\uff0c\u6717\u58f0\u5389\u5428\u9053\uff1a\u201c\u670b\u53cb\u662f\u4f55\u65b9\u9ad8\u4eba?\u6b64\u4e3e\u662f\u4f55\u7528\u610f?\u201d \u3000\u3000\u90a3\u5b89\u4e50\u516c\u5b50\u9762\u4e0a\u4e5f\u5fae\u89c2\u8be7\u8272\uff0c\u8d70\u4e86\u8fc7\u6765\uff0c\u6b63\u5f85\u95ee\u8bdd\uff0c\u54ea\u77e5\u90a3\u7626\u5c0f\u8001\u8005\u624b\u6307\u8f7b\u5f39\uff0c\u8fa9\u7405\u5c06 \u957f\u5251\u5f39\u51fa\u4e00\u58f0\u9f99\u541f\uff0c\u7a81\u5730\u4e00\u6574\u9762\u8272\uff0c\u6c89\u58f0\u5411\u5c55\u81ea\u95ee\u9053\uff1a\u201c\u5c0f\u670b\u53cb\uff0c\u4f60\u8fd9\u53e3\u5251\u662f\u54ea\u91cc\u6765\u7684?\u201d \u3000\u3000\u5c11\u5e74\u5c55\u767d\u9762\u4e0a\u53d8\u5f97\u8d8a\u53d1\u96be\u770b\uff0c\u5927\u559d\u9053\uff1a\u201c\u4f60\u7ba1\u4e0d\u7740\uff01\u201d \u3000\u3000\u968f\u7740\u559d\u58f0\uff0c\u4ed6\u7adf\u5de6\u624b\u201c\u7830\u201d\u5730\u4e00\u62f3\uff0c\u5411\u90a3\u7626\u5c0f\u8001\u8005\u7684\u9762\u95e8\u6253\u53bb\uff0c\u540c\u65f6\u53f3\u624b\u75be\u4f38\uff0c\u53bb\u593a\u8fd9 \u8001\u8005\u624b\u4e2d\u7684\u5251\u3002 \u3000\u3000\u8fd9\u5c11\u5e74\u5e74\u5c11\u6c14\u76db\uff0c\u518d\u52a0\u4e0a\u81ea\u5df1\u7684\u5251\u88ab\u593a\u53bb\uff0c\u7adf\u4e0d\u7ba1\u4eba\u5bb6\u662f\u4f55\u8eab\u4efd\uff0c\u5f53\u7740\u8fd9\u4e9b\u540d\u91cd\u4e00\u65f6\u7684 \u6b66\u6797\u540d\u4eba\uff0c\u5c31\u4f38\u80f3\u818a\u52a8\u624b\u4e86\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u53cc\u624b\u65b9\u624d\u4f38\u51fa\uff0c\u773c\u524d\u5374\u53c8\u4e00\u82b1\uff0c\u5df2\u5931\u53bb\u53cc\u7626\u5c0f\u8001\u8005\u7684\u884c\u8e2a\uff0c\u5fc3\u4e2d\u6b63\u4e00\u51db\uff0c\u5de6\u62f3\u53f3 \u638c\u5df2\u88ab\u4eba\u5bb6\u8f7b\u8f7b\u6258\u51fa\uff0c\u81ea\u5df1\u6ee1\u8eab\u7684\u6c14\u52b2\uff0c\u7ade\u518d\u4e5f\u7528\u4e0d\u51fa\u4e00\u4e1d\u6765\u3002 \u3000\u3000\u53ea\u542c\u4e00\u4e2a\u6e05\u6717\u7684\u53e3\u97f3\u7b11\u9053\uff1a\u201c\u5144\u53f0\uff0c\u6709\u8bdd\u597d\u8bf4\uff0c\u5207\u5207\u4e0d\u8981\u52a8\u624b\u3002\u201d\u539f\u6765\u6258\u4f4f\u4ed6\u4e00\u4e3e\u4e00\u638c \u7684\uff0c\u5c31\u662f\u90a3\u5b89\u4e50\u516c\u5b50\u4e91\u6323\u3002 \u3000\u3000\u5c11\u5e74\u5c55\u767d\u76db\u6c14\u4e0d\u7981\u4e00\u9981\uff0c\u9893\u7136\u6536\u56de\u4e86\u624b\uff0c\u8d77\u5148\u4ed6\u5fc3\u91cc\u4ee5\u4e3a\uff0c\u8fd9\u5b89\u4e50\u516c\u5b50\u80fd\u4eab\u76db\u540d\uff0c\u4e0d\u8fc7 \u5927\u534a\u662f\u9760\u4e86\u4ed6\u624b\u4e0b\u7684\u98df\u5ba2\u90fd\u662f\u80fd\u4eba\u800c\u5df2\u3002 \u3000\u3000\u4f46\u4eba\u5bb6\u6b64\u523b\u4e00\u4f38\u624b\uff0c\u4ed6\u529e\u4e0b\u5c31\u6709\u6570\u4e86\uff0c\u77e5\u9053\u8fd9\u5b89\u4e50\u516c\u5b50\uff0c\u6b66\u529f\u7adf\u662f\u60ca\u4eba\u65e0\u6bd4\uff0c\u4f46\u662f\uff0c\u4ed6 \u867d\u660e\u77e5\u81ea\u5df1\u7684\u6b66\u529f\u6bd4\u4eba\u5bb6\u5dee\u5f97\u592a\u8fdc\uff0c\u4ecd\u5fcd\u4e0d\u4f4f\u6c14\u6124\u6124\u5730\u9053\uff1a\u201c\u4e91\u516c\u5b50\uff0c\u4f60\u8fd9\u662f\u5e72\u4ec0\u4e48?\u5047\u5982 \u516c\u5b50\u8981\u8fd9\u53e3\u5251\uff0c\u53ea\u8981\u516c\u5b50\u5f00\u53e3\uff0c\u5c0f\u5f1f\u4e00\u5b9a\u53cc\u624b\u5949\u4e0a\uff0c\u516c\u5b50\u53c8\u4f55\u5fc5\u8fd9\u4e48\u505a\u5462?\u201d \u3000\u3000\u4ed6\u8fd9\u8bdd\u5df2\u8bf4\u5f97\u5f88\u91cd\uff0c\u4f46\u662f\u5b89\u4e50\u516c\u5b50\u9762\u4e0a\u4ecd\u5fbd\u5fae\u542b\u7b11\uff0c\u4e00\u70b9\u4e5f\u4e0d\u52a8\u6c14\uff0c\u4f38\u624b\u62cd\u4e86\u62cd\u4ed6\u7684\u80a9 \u8180\uff0c\u9053\uff1a\u201c\u5144\u53f0\uff0c\u4f60\u8bef\u4f1a\u4e86\uff0c\u4f60\u8bef\u4f1a\u4e86\uff01\u201d\u4e00\u9762\u5374\u4fa7\u8fc7\u5934\uff0c\u671d\u90a3\u5df2\u8f6c\u5230\u5c55\u767d\u8eab\u540e\u7684\u7626\u5c0f\u8001\u8005 \u8e6c\uff1a\u201c\u534e\u8001\u5e08\uff0c\u4f60\u5feb\u522b\u521d\u4eba\u5bb6\u5f00\u73a9\u7b11\u4e86\uff0c\u628a\u5251\u8fd8\u7ed9\u4eba\u5bb6\u5427\uff01\u201d\u4ed6\u54c8\u54c8\u4e00\u7b11\uff0c\u6307\u7740\u8fd9\u7626\u5c0f\u8001\u8005 \u5411\u5c55\u81ea\u9053\uff1a\u201c\u5144\u53f0\uff0c\u6765\uff0c\u8ba9\u5c0f\u5f1f\u6765\u5f15\u89c1\u5f15\u89c1\uff0c\u8fd9\u4f4d\u5c31\u662f\u6c5f\u6e56\u4eba\u79f0\u2018\u8ffd\u98ce\u65e0\u5f71\u2019\u7684\u534e\u6e05\u6cc9\u8001 \u5e08\uff0c\u5144\u53f0\u653e\u5fc3\uff0c\u534e\u8001\u5e08\u7edd\u4e0d\u4f1a\u6043\u5f3a\u593a\u5251\u7684\u3002\u201d \u3000\u3000\u8fd9\u201c\u8ffd\u98ce\u65e0\u5f71\u201d\u56db\u5b57\u4e00\u51fa\uff0c\u65b9\u624d\u770b\u5230\u8fd9\u5c0f\u8001\u8005\u7684\u8eab\u624b\uff0c\u5374\u4e0d\u77e5\u9053\u4ed6\u662f\u8c01\u7684\u4eba\u90fd\u4e0d\u7981\u5927\u5403 \u4e00\u60ca\uff01\u76ee\u5149\u90fd\u8f6c\u5230\u8fd9\u8c8c\u4e0d\u60ca\u4eba\u7684\u8001\u8005\u8eab\u4e0a\uff0c\u51e0\u4e4e\u6709\u4e9b\u4e0d\u76f8\u4fe1\u6b64\u4eba\u5c31\u662f\u540d\u9707\u5929\u4e0b\u3001\u4ee5\u8f7b\u8eab\u5c0f\u5de7 \u4e4b\u672f\u9a70\u8a89\u6b66\u6797\u3001\u6c5f\u6e56\u4eba\u79f0\u201c\u7b2c\u4e00\u795e\u5077\u8ffd\u98ce\u65e0\u5f71\u201d\u7684\u534e\u6e05\u6cc9\uff0c\u4e5f\u60f3\u4e0d\u5230\u6b64\u4eba\u7adf\u4e5f\u88ab\u5b89\u4e50\u516c\u5b50\u6536 \u7f57\u4e86\u53bb\u3002 \u3000\u3000\u201c\u8ffd\u98ce\u65e0\u5f71\u201d\u534e\u6e05\u6cc9\u5374\u4ecd\u5bd2\u7740\u8138\uff0c\u7f13\u7f13\u53c8\u8d70\u5230\u5c11\u5e74\u5c55\u767d\u7684\u9762\u524d\uff0c\u6c89\u58f0\u9053\uff1a\u6211\u95ee\u4f60\uff0c\u4f60\u8fd9 \u53e3\u5251\u662f\u54ea\u91cc\u6765\u7684?\u4f60\u59d3\u4ec0\u4e48?\u53eb\u4ec0\u4e48\u540d\u5b57?\u8c01\u662f\u4f60\u7684\u6388\u4e1a\u5e08\u5085?\u201d \u3000\u3000\u4ed6\u4e00\u8fde\u58f0\u53c8\u95ee\u4e86\u8fd9\u51e0\u53e5\u8bdd\uff0c\u751f\u50cf\u662f\u6ca1\u6709\u542c\u5230\u4e91\u94ee\u7684\u8bdd\u4f3c\u7684\uff0c\u6b64\u65f6\u4e0d\u4f46\u4e91\u94ee\u9762\u4e0a\u6536\u655b\u4e86\u7b11 \u5bb9\uff0c\u6469\u4e91\u795e\u624b\u80a0\u4e0a\u4e5f\u5fae\u5fae\u53d8\u4e86\u8272\u3002 \u3000\u3000\u90a3\u4e24\u4e2a\u516d\u6247\u95e8\u91cc\u7684\u540d\u6355\uff0c\u6b64\u523b\u8001\u65e9\u7ad9\u5f97\u8fdc\u8fdc\u7684\uff0c\u4ed6\u4eec\u4e00\u542c\u201c\u8ffd\u98ce\u65e0\u5f71\u201d\u7684\u540d\u5b57\uff0c\u8111\u888b\u5c31 \u53d1\u80c0\uff0c\u518d\u4e5f\u4e0d\u6562\u8d9f\u8fdb\u8fd9\u6df1\u6c34\u91cc\u3002 \u3000\u3000\u5c11\u5e74\u5c55\u767d\u8138\u4e0a\u66f4\u53d8\u5f97\u7eb8\u4e00\u6837\u5730\u715e\u767d\uff0c\u77aa\u7740\u773c\u775b\u9053\uff1a\u534e\u8001\u524d\u8f88\uff0c\u627e\u65e9\u5c31\u542c\u9053\u4f60\u7684\u540d\u5b57\uff0c\u4e5f \u77e5\u9053\u4f60\u662f\u6b66\u6797\u91cc\u6210\u540d\u7684\u9ad8\u624b\uff0c\u53ef\u662f\u6211\u5374\u4e0d\u77e5\u9053\u4f60\u51ed\u7740\u4ec0\u4e48\uff0c\u8981\u95ee\u6211\u8fd9\u53e5\u8bdd\uff01\u201d \u3000\u3000\u8fd9\u201c\u8ffd\u98ce\u65e0\u5f71\u201d\u51b7\u51b7\u4e00\u7b11\uff0c\u7ade\u6c89\u58f0\u53c8\u9053\uff1a\u201c\u670b\u53cb\uff0c\u4eca\u5929\u4f60\u82e5\u662f\u4e0d\u597d\u597d\u628a\u6211\u95ee\u4f60\u7684\u8bdd\u8bf4\u51fa \u6765\uff0c\u6211\u534e\u67d0\u4eba\u7acb\u523b\u5c31\u53eb\u4f60\u6bd5\u547d\u6b64\u5730\uff01\u201d \u3000\u3000\u6b64\u8bdd\u4e00\u51fa\uff0c\u4f17\u4eba\u4e0d\u7981\u53c8\u4e3a\u4e4b\u5927\u5c79\u4e00\u60ca\uff0c\u90a3\u5b89\u4e50\u516c\u5b50\u5f3a\u7b11\u4e00\u4e0b\u9053\uff1a\u201c\u534e\u8001\u5e08\uff0c\u4f60\u8fd9\u662f\u5e72\u4ec0 \u4e48?\u770b\u5728\u6211\u7684\u9762\u4e0a\uff0c\u8ba9\u8fd9\u5c11\u5e74\u58ee\u58eb\u628a\u5251\u62ff\u56de\u53bb\u5427\u3002\u201d\u4ed6\u53c8\u5f3a\u7b11\u4e86\u4e00\u58f0\uff0c\u63a5\u7740\u5f80\u4e0b\u8bf4\u9053\uff1a\u201c\u4e0d \u7136\u4eba\u5bb6\u8fd8\u771f\u4ee5\u4e3a\u662f\u6211\u8981\u8fd9\u53e3\u5251\u54e9\uff01\u201d \u3000\u3000\u54ea\u77e5\u9053\u201c\u8ffd\u98ce\u65e0\u5f71\u201d\u534e\u6e05\u6cc9\u7ade\u5f80\u540e\u9000\u4e86\u4e00\u6b65\uff0c\u4ecd\u94c1\u9752\u7740\u9762\u8272\uff0c\u9053\uff1a\u201c\u4e91\u516c\u5b50\uff0c\u6211\u534e\u6e05\u6cc9 \u5728\u6b66\u6797\u4e2d\u5f97\u7f6a\u7684\u4eba\u592a\u591a\uff0c\u5f04\u5f97\u4e0d\u80fd\u7acb\u8db3\uff0c\u53bb\u6295\u5954\u60a8\uff0c\u627f\u60a8\u4e0d\u5f03\uff0c\u5f85\u6211\u5982\u4e0a\u5bbe\uff0c\u6211\u534e\u6e05\u6cc9\u611f\u6fc0 \u60a8\u4e00\u8f88\u5b50\uff0c\u53ea\u8981\u60a8\u4e91\u516c\u5b50\u4e00\u53e5\u8bdd\uff0c\u53eb\u6211\u534e\u6e05\u6cc9\u6c64\u91cc\u53bb\uff0c\u6211\u5c31\u6c64\u91cc\u53bb\uff0c\u53eb\u6211\u534e\u6e05\u6cc9\u706b\u91cc\u53bb\uff0c\u6211 \u5c31\u706b\u91cc\u53bb\uff0c\u53ef\u662f\u2014\u2014\u201d\u4ed6\u76ee\u5149\u7a81\u5730\u4e00\u51db\uff0c\u5728\u90a3\u5c11\u5e74\u5c55\u767d\u8eab\u4e0a\u4e00\u8f6c\uff0c\u6c89\u58f0\u63a5\u9053\uff1a\u201c\u53ef\u662f\u4eca\u5929\uff0c \u6211\u5374\u975e\u8981\u95ee\u6e05\u695a\u8fd9\u53e3\u5251\u7684\u6765\u5386\uff0c\u95ee\u6e05\u695a\u8fd9\u5c11\u5e74\u7684\u6765\u5386\u4e0d\u53ef\uff0c\u4ed6\u8981\u662f\u4e0d\u8bf4\u51fa\u6765\uff0c\u6211\u534e\u6e05\u6cc9\u7eb5\u7136 \u843d\u4e2a\u4ee5\u5f3a\u51cc\u5f31\u3001\u4ee5\u5927\u538b\u5c0f\u7684\u7f6a\u540d\uff0c\u4e5f\u987e\u4e0d\u5f97\u4e86\uff0c\u8981\u5c06\u4ed6\u8fd9\u6761\u547d\u6401\u5728\u8fd9\u513f\u3002\u201d \u3000\u3000\u8fd9\u4f4d\u66fe\u7ecf\u4e00\u591c\u4e4b\u95f4\uff0c\u8fde\u5077\u4eac\u57ce\u4e03\u5341\u4e09\u5bb6\u5de8\u5b85\u7684\u6c5f\u6e56\u7b2c\u4e00\u795e\u5077\uff0c\u6b64\u523b\u9762\u5bd2\u5982\u94c1\u5730\u8bf4\u5230\u8fd9 \u91cc\uff0c\u7a81\u5730\u8eab\u5f62\u4e00\u52a8\uff0c\u5b9b\u5982\u4e00\u9053\u8f7b\u70df\u822c\u5347\u8d77\uff0c\u7626\u5c0f\u7684\u8eab\u8eaf\u62d4\u5230\u4e24\u4e08\u4e94\u3001\u516d\u5904\uff0c\u53cc\u8db3\u5fae\u5fae\u4e00\u8e6c\uff0c \u7ade\u5728\u7a7a\u4e2d\u6253\u4e86\u4e2a\u76d8\u65cb\uff0c\u638c\u4e2d\u957f\u5251\u4e00\u6325\uff0c\u53ea\u89c1\u4e00\u9053\u78a7\u83b9\u83b9\u7684\u5251\u5149\uff0c\u50cf\u662f\u5728\u7a7a\u4e2d\u6253\u4e86\u4e2a\u95ea\uff0c\u201c\u683c \u64e6\u201d\u4e00\u58f0\uff0c\u7ade\u5c06\u4e00\u80a1\u7c97\u5982\u6d77\u7897\u822c\u7684\u6811\u679d\uff0c\u4e00\u5251\u65a9\u6210\u4e24\u6bb5\uff0c\u201c\u54d7\u7136\u201d\u4e00\u58f0\uff0c\u90a3\u6bb5\u6811\u679d\u5e26\u6839\u8fde\u53f6 \u7684\u843d\u4e86\u4e0b\u6765\uff0c\u8fd9\u201c\u8ffd\u98ce\u65e0\u5f71\u201d\u53c8\u5728\u7a7a\u4e2d\u8f7b\u6325\u4e00\u638c\uff0c\u5c06\u8fd9\u6811\u679d\u51fb\u5f97\u8fdc\u8fdc\u7684\uff0c\u8eab\u5f62\u624d\u98d8\u7136\u843d \u3000\u3000\u534e\u6e05\u6cc9\u9732\u4e86\u8fd9\u4e48\u4e00\u624b\u8db3\u4ee5\u60ca\u4e16\u9a87\u4fd7\u7684\u529f\u5f0f\u5934\u4e24\u811a\u4e01\u5b57\u6b65\u4e00\u7ad9\uff0c\u4ecd\u7136\u6c89\u7740\u8138\uff0c\u5389\u58f0\u9053\uff1a \u201c\u8c01\u4eca\u5929\u8981\u7ba1\u6211\u534e\u6e05\u6cc9\u7684\u95f2\u4e8b\uff0c\u4ed6\u5c31\u662f\u6211\u534e\u6e05\u6cc9\u7684\u8001\u5b50\uff0c\u6211\u4e5f\u5f97\u8ddf\u4ed6\u62fc\u4e86\u3002\u201d \u3000\u3000\u5b89\u4e50\u516c\u5b50\u7d20\u4ee5\u6c14\u5ea6\u65f7\u8fbe\u89c1\u79f0\u4e8e\u5929\u4e0b\uff0c\u6b64\u523b\u5374\u4e5f\u4e0d\u7981\u9762\u76ee\u53d8\u8272\uff0c\u6b63\u5f85\u8bf4\u8bdd\uff0c\u90a3\u201c\u6469\u4e91\u795e \u624b\u201d\u5374\u4e00\u4e2a\u7bad\u6b65\u63a0\u4e86\u8fc7\u6765\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u534e\u8001\u5e08\uff0c\u4f60\u8fd9\u662f\u5e72\u4ec0\u4e48?\u4f60\u6562\u5bf9\u516c\u5b50\u8fd9\u4e48\u65e0\u7406!\u201d \u3000\u3000\u8fd9\u201c\u8ffd\u98ce\u65e0\u5f71\u201d\u6b64\u65f6\u624b\u91cc\u6b63\u7d27\u7d27\u6293\u4f4f\u90a3\u53e3\u5bd2\u5149\u7167\u4eba\u7684\u957f\u5251\uff0c\u95fb\u8a00\u56de\u8fc7\u5934\u6765\uff0c\u51b7\u51b7\u9053\uff1a \u201c\u5411\u51b2\u5929\uff0c\u4f60\u6211\u53ef\u6709\u51e0\u5341\u5e74\u7684\u4ea4\u60c5\uff0c\u4f60\u96be\u9053\u8fd8\u4e0d\u6e05\u695a\u6211\u7684\u4e00\u5207?\u4f60\u96be\u9053\u773c\u775b\u778e\u4e86\uff0c\u770b\u4e0d\u51fa\u8fd9 \u53e3\u5251\u662f\u4ec0\u4e48\u5251?\u662f\u4ec0\u4e48\u4eba\u7684?\u201d \u3000\u3000\u4ed6\u8d8a\u8bf4\u795e\u60c5\u8d8a\u6fc0\u52a8\uff0c\u201c\u6469\u4e91\u795e\u624b\u201d\u5411\u51b2\u5929\u4e0d\u7981\u508d\u4e86\u4e00\u4e0b\uff0c\u76ee\u5149\u671d\u8fd9\u53e3\u5251\u4e0a\u7740\u5b9e\u76ef\u4e86\u51e0 \u773c\uff0c\u7a81\u5730\u50cf\u662f\u60f3\u8d77\u4e86\u4ec0\u4e48\uff0c\u9762\u8272\u7acb\u523b\u5927\u53d8\uff0c\u6cb9\u6cb9\u5730\u60f3\u5f00\u53e3\uff0c\u5374\u53c8\u5fcd\u4f4f\u4e86\uff0c\u7ade\u6a2a\u8fc7\u4e24\u6b65\uff0c\u8d70\u5230 \u4e00\u8fb9\u53bb\uff0c\u4e24\u9053\u76ee\u5149\uff0c\u5374\u4ecd\u7d27\u7d27\u77aa\u5728\u90a3\u53e3\u5251\u4e0a\u3002 \u3000\u3000\u5c11\u5e74\u5c55\u767d\u8138\u4e0a\u9752\u4e00\u9635\uff0c\u767d\u4e00\u9635\uff0c\u201c\u534e\u5927\u4fa0\uff0c\u4f60\u662f\u6b66\u6797\u4e2d\u6210\u540d\u7acb\u4e07\u7684\u4eba\u7269\uff0c\u6211\u662f\u521d\u51fa\u8305\u5e90 \u7684\u5c0f\u4f19\u5b50\uff0c\u53ef\u662f\u6211\u4eca\u5929\u5c31\u662f\u4e0d\u8bf4\uff0c\u6211\u5012\u8981\u770b\u770b\u4f60\u8fd9\u4e2a\u6210\u540d\u9732\u8138\u7684\u4eba\u7269\u80fd\u628a\u6211\u600e\u6837\uff01\u201d\u8bf4\u7740\uff0c \u4ed6\u4e00\u9762\u563f\u563f\u51b7\u7b11\uff0c\u80f8\u819b\u633a\u5f97\u66f4\u9ad8\uff0c\u4e24\u53ea\u5927\u773c\u775b\u77aa\u5f97\u6eda\u5706\uff0c\u53d1\u7740\u5149\uff0c\u4e00\u9762\u53c8\u9053\uff0f\u800c\u4e14\uff0c\u534e\u5927 \u4fa0\uff0c\u6211\u544a\u8bc9\u4f60\uff0c\u4f60\u5feb\u628a\u5251\u8fd8\u6211!\u4e0d\u7136\u53ea\u8981\u6211\u4e00\u5929\u4e0d\u6b7b\uff0c\u6211\u7eb5\u7136\u642d\u4e0a\u6027\u547d\uff0c\u4e5f\u8981\u5c06\u8fd9\u53e3\u5251\u593a\u56de \u6765\u7684\uff01\u201d \u3000\u3000\u201c\u8ffd\u98ce\u65e0\u5f71\u201d\u76ee\u5149\u66f4\u51db\u5982\u5229\u526a\uff0c\u5de6\u811a\u8fc8\u524d\u4e00\u6b65\uff0c\u5389\u58f0\u9053\uff1a\u2018\u4f60\u771f\u52a9\u4e0d\u8bf4?\u201d \u3000\u3000\u5c11\u5e74\u5c55\u767d\u4e00\u633a\u80f8\u819b\uff0c\u4e5f\u5389\u58f0\u5428\u9053\uff1a\u201c\u4e0d\u8bf4\u53c8\u600e\u5730?\u5feb\u8fd8\u5251\u6765!\u201d \u3000\u3000\u8bed\u58f0\u4e00\u4e86\uff0c\u4f17\u4eba\u4f46\u89c1\u773c\u524d\u5251\u5149\u7a81\u957f\uff0c\u90a3\u201c\u8ffd\u98ce\u65e0\u5f71\u201d\u7ade\u5927\u559d\u9053\uff1a\u201c\u90a3\u4eca\u5929\u6211\u5c31\u8981\u4f60\u7684 \u547d\uff01\u201d\u54e9\u3001\u54e9\u4e24\u5251\uff0c\u5982\u95ea\u7535\u822c\u98de\u5411\u5c55\u767d\uff0c\u8fd9\u6210\u540d\u6b66\u6797\u5df2\u8fd1\u4e09\u5341\u5e74\u7684\u4eba\u7269\uff0c\u7adf\u771f\u7684\u5411\u4e00\u4e2a\u540d\u4e0d \u89c1\u7ecf\u4f20\u7684\u5c11\u4e2d\u52a8\u8d77\u624b\u6765\u4e86\u3002 \u3000\u3000\u90a3\u5b89\u4e50\u516c\u5b50\u4e91\u94ee\u518d\u4e5f\u5fcd\u4e0d\u4f4f\uff0c\u8eab\u5f62\u58eb\u52a8\uff0c\u5df2\u6321\u5728\u5c55\u767d\u524d\u9762\uff0c\u5c06\u624b\u4e2d\u7684\u90a3\u53ea\u5b9d\u84dd\u76ca\u7897\u4e00 \u4e3e\uff0c\u7adf\u4ee5\u4e4b\u53bb\u6321\u90a3\u201c\u8ffd\u98ce\u65e0\u5f71\u201d\u7684\u5251\u5149\uff0c\u53e3\u4e2d\u4ea6\u559d\u9053\uff1a\u201c\u534e\u8001\u5e08\uff0c\u4f60\u771f\u7684\u8981\u52a8\u624b?\u201d \u3000\u3000\u534e\u6e05\u6cc9\u4e00\u62db\u4e24\u5f0f\uff0c\u5176\u5feb\u5982\u98ce\uff0c\u5df2\u53d1\u5230\u4e2d\u9014\uff0c\u4f46\u6b64\u523b\u5374\u4e5f\u4e0d\u5f97\u4e0d\u786c\u751f\u751f\u7279\u5251\u62db\u64a4\u56de\u6765\uff0c\u624b \u8155\u731b\u632b\uff0c\u90a3\u53e3\u5251\u7adf\u9aa4\u7136\u505c\u5728\u52a0\u53ea\u5b9d\u84dd\u8272\u7684\u76ca\u7897\u524d\u9762\uff0c\u53ea\u8981\u518d\u5dee\u4e86\u6beb\u5398\uff0c\u4ed6\u5c31\u5f97\u5c06\u8fd9\u53ea\u76d6\u7897\u6bc1 \u4e86\u3002 \u3000\u3000\u4ed6\u8fd9\u79cd\u624b\u52a8\u62ff\u634f\u4e4b\u5999\uff0c\u7aef\u7684\u662f\u6070\u5230\u597d\u5904\uff0c\u5b89\u4e50\u516c\u5b50\u5e73\u4f38\u638c\u5fc3\uff0c\u5374\u2014\u2014\u52a8\u4e5f\u4e0d\u52a8\u5730\u5c06\u8fd9\u53ea \u76d6\u7897\u6258\u5728\u624b\u4e0a\uff0c\u67b6\u4f4f\u90a3\u53e3\u5251\uff0c\u8bf4\u9053\uff1a\u201c\u534e\u8001\u5e08\uff0c\u4f60\u82e5\u662f\u771f\u8981\u52a8\u624b\u7684\u8bdd\uff0c\u4e5f\u5f97\u8bf4\u51fa\u4e2a\u539f\u56e0\u6765 \u5440?\u201d \u3000\u3000\u8fd9\u201c\u8ffd\u98ce\u65e0\u5f71\u201d\u63e1\u7740\u5251\u7684\u8155\u5b50\u5fae\u98a4\u4e86\u51e0\u98a4\uff0c\u663e\u89c1\u662f\u5728\u5f3a\u5fcd\u7740\u6fc0\u52a8\u7684\u60c5\u611f\uff0c\u5251\u5c16\u98a4\u52a8\u95f4\uff0c \u78b0\u5230\u90a3\u53ea\u5b9d\u84dd\u8272\u7684\u76cf\u7897\uff0c\u53d1\u51fa\u51e0\u58f0\u8f7b\u5fbd\u7684\u545b\u7405\u58f0\uff0c\u4f46\u662f\u5b89\u4e50\u516c\u5b50\u6258\u7740\u76cf\u7897\u7684\u624b\uff0c\u4ecd\u7136\u52a8\u4e5f\u4e0d \u52a8\u3002 \u3000\u3000\u4e24\u4eba\u76ee\u5149\u76f8\u63a5\uff0c\u534e\u6e05\u6cc9\u5019\u5730\u811a\u8ddf\u4e00\u65cb\uff0c\u9000\u540e\u4e00\u6b65\uff0c\u4ed6\u7ec8\u7a76\u4e0d\u6562\u5411\u8fd9\u5b89\u4e50\u516c\u5b50\u51fa\u624b\uff0c\u8f7b\u8f7b \u957f\u53f9\u4e86\u4e00\u58f0\uff0c\u6447\u83cc\u2018\u8bf4\u9053\uff1a\u201c\u4e91\u516c\u5b50\uff0c\u4f60\u53c8\u4f55\u5fc5\u63d2\u624b\u7ba1\u8fd9\u4ef6\u4e8b\u54e9\uff01\u201d \u3000\u3000\u90a3\u201c\u6469\u4e91\u795e\u624b\u201d\u5411\u51b2\u5929\uff0c\u6b64\u523b\u7ade\u4e5f\u4e00\u6b65\u63a0\u6765\uff0c\u53cc\u624b\u75be\u4f38\uff0c\u8f7b\u8f7b\u4ece\u4e91\u94ee\u624b\u91cc\u63a5\u7740\u90a3\u53ea\u76ca \u7897\uff0c\u5374\u6c89\u7740\u58f0\u97f3\u5411\u4e91\u6323\u9053\uff1a\u201c\u4e91\u516c\u5b50\uff0c\u534e\u8001\u5e08\u662f\u6709\u9053\u7406\u7684\uff0c\u516c\u5b50\u8fd8\u662f\u4e0d\u8981\u7ba1\u8fd9\u4ef6\u4e8b\u597d\u4e86\u3002\u201d \u3000\u3000\u5b89\u4e50\u516c\u5b50\u7f13\u7f13\u653e\u4e0b\u624b\u6765\uff0c\u5fc3\u4e2d\u5374\u4e0d\u7981\u7591\u4e91\u5927\u8d77\uff0c\u4ed6\u77e5\u9053\u8fd9\u201c\u6469\u4e91\u795e\u624b\u201d\u5411\u51b2\u5929\uff0c\u6df7\u8ff9\u4e8e \u6c5f\u6e56\u4e2d\u7684\u65e5\u5b50\u6781\u4e45\uff0c\u773c\u9762\u6781\u5e7f\uff0c\u662f\u4e2a\u6781\u7cbe\u660e\u5f3a\u5e72\u7684\u4eba\u7269\uff0c\u4ed6\u65e2\u7136\u5982\u6b64\u529d\u81ea\u5df1\uff0c\u90a3\u6b64\u4e8b\u5fc5\u6709\u9053 \u7406\u3002\u518d\u52a0\u4e0a\u8fd9\u201c\u8ffd\u98ce\u65e0\u5f71\u201d\u4e5f\u4e0d\u662f\u8f7b\u4e3e\u5984\u52a8\u7684\u4eba\uff0c\u5f53\u7136\u66f4\u4e0d\u4f1a\u662f\u4e3a\u4e86\u8d2a\u6c42\u8fd9\u53e3\u5b9d\u5251\uff0c\u800c\u8981\u53bb \u53d6\u8fd9\u5c11\u4e2d\u7684\u6027\u547d\u3002 \u3000\u3000\u4f46\u662f\uff0c\u8fd9\u201c\u8ffd\u98ce\u65e0\u5f71\u201d\u5728\u5916\u9762\u7684\u4ec7\u5bb6\u867d\u7136\u591a\uff0c\u53ef\u4e5f\u7edd\u5bf9\u4e0d\u4f1a\u548c\u8fd9\u521d\u51fa\u6c5f\u6e56\u3001\u4efb\u4e8b\u4e0d\u61c2\u7684 \u4e2d\u8f7b\u4eba\u7ed3\u4e0b\u6881\u5b50\u5440\u3002\u90a3\u4e48\uff0c\u4ed6\u6b64\u523b\u5982\u6b64\u903c\u7740\u8fd9\u4e2a\u5c11\u5e74\uff0c\u5374\u53c8\u662f\u4e3a\u7740\u4ec0\u4e48\u539f\u56e0\u5462? \u3000\u3000\u5b89\u4e50\u516c\u4e88\u60f3\u6765\u60f3\u53bb\uff0c\u5374\u4e5f\u60f3\u4e0d\u51fa\u8fd9\u5176\u4e2d\u7684\u9053\u7406\uff0c\u4ed6\u5e72\u54b3\u4e00\u58f0\uff0c\u9053\uff1a\u534e\u8001\u5e08\uff0c\u5047\u5982\u4f60\u771f\u7684 \u6709\u4ec0\u4e48\u91cd\u5927\u7684\u4e8b\uff0c\u90a3\u4e48\u6211\u4e5f\u4e0d\u4fbf\u7ba1\uff0c\u53ef\u662f\u2026\u2026\u201d \u3000\u3000\u4ed6\u5fae\u5fae\u987f\u4e86\u987f\uff0c\u53c8\u9053\uff1a\u201c\u4f9d\u6211\u4e4b\u610f\uff0c\u4f60\u8fd8\u662f\u5728\u8fd9\u91cc\u5f53\u7740\u5916\u9762\u7684\u670b\u53cb\uff0c\u5c06\u8fd9\u4e8b\u8bf4\u6e05\u695a\u624d \u597d\uff0c\u5426\u5219\u5916\u9762\u4f20\u4e86\u51fa\u53bb\uff0c\u4e8e\u4f60\u534e\u8001\u5e08\u7684\u6e05\u540d\u4e5f\u6709\u635f\u3001\u534e\u8001\u5e08\uff0c\u8fd9\u4e8b\u82e5\u679c\u662f\u5149\u660e\u6b63\u5927\u7684\uff0c\u90a3\u4e48 \u4f60\u5c31\u8bf4\u51fa\u6765\uff0c\u53c8\u6709\u4f55\u59a8\u5462?\u201d \u3000\u3000\u4ed6\u5634\u91cc\u8fd9\u4e48\u8bf4\uff0c\u5fc3\u91cc\u5374\u5728\u60f3\uff1a\u8fd9\u201c\u8ffd\u98ce\u65e0\u5f71\u201d\u7d27\u7d27\u903c\u7740\u8ffd\u95ee\u4e00\u4e2a\u5c11\u5e74\u6240\u6709\u7684\u5b9d\u5251\u7684\u6765 \u5386\uff0c\u53c8\u7d27\u7d27\u903c\u7740\u8feb\u95ee\u4eba\u5bb6\u7684\u59d3\u540d\u5e08\u627f\uff0c\u800c\u4ed6\u548c\u4eba\u5bb6\u5374\u975e\u4eb2\u975e\u653e\uff0c\u8fd9\u5176\u4e2d\u53c8\u4f1a\u6709\u4ec0\u4e48\u5149\u660e\u6b63\u5927 \u7684\u7406\u7531\u5462? \u3000\u3000\u90a3\u5c11\u5e74\u5c55\u767d\u6b64\u523b\u4e5f\u5927\u559d\u9053\uff1a\u201c\u5bf9\u4e86\uff0c\u534e\u5927\u4fa0\uff0c\u4f60\u5230\u5e95\u51ed\u7740\u4ec0\u4e48\u800d\u95ee\u6211\u4e0d\u613f\u56de\u7b54\u7684\u8bdd?\u8fd9 \u53e3\u5251\u662f\u5c5e\u4e8e\u6211\u7684\uff0c\u4f60\u51ed\u7740\u4ec0\u4e48\u62a2\u53bb?\u4f60\u6709\u4ec0\u4e48\u7406\u7531\uff0c\u4f60\u5c31\u8bf4\u51fa\u6765\u597d\u4e86\u3002\u201d \u3000\u3000\u8fd9\u201c\u8ffd\u98ce\u65e0\u5f71\u201d\u76ee\u5149\u4e00\u51db\uff0c\u4e00\u4e1d\u5bd2\u610f\u500f\u7136\u6cdb\u4e0a\u4ed6\u90a3\u5e72\u67af\u3001\u7626\u524a\u7684\u9762\u5b54\uff0c\u51b7\u51b7\u6ce8\u89c6\u4e86\u8fd9\u5c11 \u5e74\u534a\u664c\uff0c\u7a81\u5730\u9053\uff1a\u201c\u4f60\u96be\u9053\u771f\u4e0d\u77e5\u9053\u6211\u95ee\u4f60\u8fd9\u4e9b\u8bdd\u7684\u7528\u610f?\u4f60\u96be\u9053\u771f\u4e0d\u77e5\u9053\u8fd9\u4e3a\u7684\u662f\u4ec0\u4e48\u7406 \u7531?\u670b\u53cb\uff0c\u4f60\u8981\u662f\u5728\u6211\u59d3\u534e\u7684\u9762\u524d\u88c5\u849c\uff0c\u54e9\u563f\uff0c\u90a3\u4f60\u53ef\u8d70\u4e86\u773c\u4e86\u3002\u201d \u3000\u3000\u5c11\u5e74\u5c55\u767d\u4e00\u542c\u8fd9\u8bdd\uff0c\u5374\u695e\u4e86\u4e00\u695e\uff0c\u8fd8\u672a\u6765\u5f97\u53ca\u7b54\u8bdd\uff0c\u53ea\u89c1\u90a3\u5b89\u4e50\u5206\u5b50\u4e91\u94ee\u5411\u4ed6\u626b\u4e86\u51e0 \u773c\uff0c\u5374\u9053\uff1a\u201c\u534e\u8001\u5e08\uff0c\u8fd9\u4f4d\u5c11\u5e74\u58ee\u58eb\u867d\u7136\u548c\u6211\u4ec5\u7cfb\u4e00\u9762\u4e4b\u8bc6\uff0c\u4f46\u6211\u5374\u770b\u5f97\u51fa\u6765\u3002\u4ed6\u7edd\u4e0d\u662f\u5978 \u72e1\u865a\u4f2a\u7684\u4eba\u3002\u534e\u8001\u5e08\u6700\u597d\u8fd8\u662f\u5c06\u4e3a\u4ec0\u4e48\u8981\u95ee\u4ed6\u7684\u539f\u56e0\u8bf4\u51fa\u6765\u5427\uff01\u8fd9\u539f\u56e0\u662f\u5149\u660e\u7684\uff0c\u76f8\u4fe1\u8fd9\u4f4d \u5c11\u5e74\u58ee\u58eb\u7edd\u5bf9\u4e0d\u4f1a\u77e5\u800c\u4e0d\u8a00\u3002\u201d \u3000\u3000\u8bf4\u7740\uff0c\u4ed6\u53c8\u671b\u4e86\u8fd9\u5c11\u4e2d\u5c55\u767d\u4e00\u773c\uff0c\u53ea\u89c1\u4ed6\u9762\u4e0a\u9732\u7740\u611f\u6fc0\u77e5\u5df1\u7684\u795e\u60c5\uff0c\u6b63\u4e5f\u671b\u7740\u81ea\u5df2\uff0c\u4e24 \u53ea\u5927\u800c\u6709\u5149\u7684\u773c\u775b\uff0c\u6ee1\u662f\u6b63\u4e49\u4e4b\u6c14\uff0c\u4ed6\u786e\u4fe1\u81ea\u5df2\u7edd\u4e0d\u4f1a\u7740\u8d70\u4e86\u670d\uff0c\u9042\u4e0b\u4e86\u51b3\u5fc3\uff0c\u82e5\u662f\u201c\u8ffd\u98ce \u65e0\u5f71\u201d\u8bf4\u4e0d\u51fa\u4e00\u4e2a\u7406\u7531\u6765\uff0c\u90a3\u4e48\u81ea\u5df1\u7eb5\u7136\u62fc\u7740\u5f97\u7f6a\u4ed6\u8fd9\u4e2a\u6b66\u6797\u9ad8\u624b\uff0c\u4e5f\u5f97\u52a9\u8fd9\u5c11\u5e74\u4e00\u81c2\u4e4b \u529b\u3002 \u7b2c\u4e09\u7ae0\u3000\u4ed6\u4e3a\u4f55\u81ea\u520e>> \u53e4\u9f99\u300a\u5251\u5ba2\u884c\u300b \u7b2c\u4e09\u7ae0\u3000\u4ed6\u4e3a\u4f55\u81ea\u520e \u3000\u3000\u8fd9\u201c\u8ffd\u98ce\u65e0\u5f71\u201d\u534e\u6e05\u6cc9\u957f\u53f9\u4e00\u58f0\u9053\uff1a\u201c\u516c\u5b50\u65e2\u5982\u6b64\u8bf4\uff0c\u6b64\u4e8b\u8bf4\u51fa\u4ea6\u65e0\u59a8\uff0c\u53ea\u662f\u2014\u2014 \u5509\uff01\u201d\u4ed6\u76ee\u5149\u7adf\u8f6c\u5411\u90a3\u201c\u6469\u4e91\u795e\u624b\u201d\u5411\u51b2\u5929\uff0c\u53c8\u9053\uff1a\u201c\u5411\u5144\uff0c\u60f3\u6765\u4f60\u4e5f\u77e5\u9053\u4e86\u6211\u6b64\u4e3e\u4e4b\u6545\uff0c \u8fd8\u662f\u5411\u5144\u8bf4\u51fa\u6765\u5427\uff0c\u6545\u4eba\u867d\u5df2\u901d\uff0c\u5f80\u4e8b\u5374\u4ecd\u7136\u4ee4\u5c0f\u5f1f\u5fc3\u9178\u3002\u201d\u4ed6\u53cc\u76ee\u7a81\u5730\u4e00\u5f20\uff0c\u795e\u8272\u5df2\u53d8\u4e3a \u6fc0\u6602\uff1a\u201c\u6b64\u4e8b\u8bf4\u51fa\u540e\uff0c\u82e5\u6709\u4eba\u8fd8\u8ba4\u4e3a\u6211\u6b64\u4e3e\u4e0d\u5f53\u7684\uff0c\u6211\u534e\u6e05\u6cc9\u4fbf\u7acb\u523b\u6a2a\u5251\u81ea\u5228\uff0c\u7edd\u5bf9\u4e0d\u7528\u522b \u4eba\u52a8\u624b\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u5b8c\u8fd9\u4e9b\u8bdd\uff0c\u90a3\u5c11\u5e74\u5c55\u767d\u8138\u4e0a\u7684\u808c\u8089\u7a81\u5730\u8896\u52a8\u4e86\u4e00\u4e0b\uff0c\u50cf\u662f\u4e5f\u60f3\u8d77\u4ec0\u4e48\uff0c\u53c8\u50cf\u662f\u6709\u4ec0 \u4e48\u96be\u8a00\u7684\u9690\u8877\u4f3c\u7684\u3002 \u3000\u3000\u201c\u6469\u4e91\u795e\u624b\u201d\u5411\u51b2\u5929\u4f38\u624b\u5fbd\u629a\u989d\u4e0b\u7684\u82b1\u767d\u77ed\u987b\uff0c\u4e5f\u957f\u53f9\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u516c\u5b50\uff0c\u4f60\u53ef\u66fe\u542c\u8bf4 \u8fc7\uff0c\u4e8c\u4e09\u5341\u5e74\u524d\uff0c\u6b66\u6797\u4e2d\u66fe\u53d1\u751f\u8fc7\u4e00\u4ef6\u60ca\u5929\u52a8\u5730\u7684\u5927\u4e8b\uff0c\u8fd9\u4ef6\u4e8b\u66fe\u4ee4\u5929\u4e0b\u8c6a\u6770\u4e4b\u58eb\u4e3a\u4e4b\u627c \u8155?\u201d \u3000\u3000\u4ed6\u7565\u4e3a\u505c\u987f\u4e00\u4e0b\uff0c\u89c1\u90a3\u201c\u5b89\u4e50\u516c\u5b50\u201d\u4e91\u94ee\u9762\u4e0a\u5df2\u7115\u7136\u52a8\u5bb9\uff0c\u53c8\u5fae\u77a9\u63a5\u9053\uff1a\u201c\u8ddd\u4eca\u4e8c\u4e09\u5341 \u5e74\u524d\uff0c\u6c5f\u6e56\u4e0a\u6709\u4f4d\u60ca\u5929\u52a8\u5730\u7684\u82f1\u96c4\uff0c\u6b64\u4eba\u4e00\u751f\u884c\u4e8b\uff0c\u5149\u660e\u78ca\u843d\uff0c\u5c24\u5176\u53e4\u9053\u70ed\u80a0\uff0c\u6025\u516c\u597d\u4e49\uff0c \u6c5f\u6e56\u4e2d\u4eba\u65e0\u8bba\u54ea\u4e00\u8def\u7684\u670b\u53cb\uff0c\u6ca1\u6709\u4e0d\u66fe\u53d7\u8fc7\u6b64\u4eba\u6069\u60e0\u7684\u3002\u8fd1\u767e\u5e74\u6765\uff0c\u6b64\u4eba\u5728\u6b66\u6797\u4e2d\u5fb7\u671b\u4e4b \u9686\uff0c\u636e\u6211\u6240\u77e5\uff0c\u5b9e\u5728\u65e0\u4eba\u80fd\u8d85\u8d8a\u4ed6\u7684\u2014\u2014\u201d \u3000\u3000\u4ed6\u8bdd\u58f0\u53c8\u5fae\u987f\uff0c\u90a3\u201c\u5b89\u4e50\u516c\u5b50\u201d\u5374\u5df2\u8131\u53e3\u9053\uff1a\u201c\u5411\u8001\u5e08\uff0c\u4f60\u8bf4\u7684\u662f\u4e0d\u662f\u90a3\u4f4d\u2018\u9739\u96f7\u5251\u2019 \u5c55\u4e91\u5929\u5c55\u5927\u4fa0?\u201d \u3000\u3000\u6b64\u8bdd\u4e00\u51fa\uff0c\u90a3\u5c11\u5e74\u5c55\u767d\u9762\u8272\u5ffd\u7136\u60e8\u767d\uff0c\u7a81\u5730\u4e00\u62e7\u8eab\uff0c\u53cc\u8db3\u731b\u987f\uff0c\u5f80\u5916\u5c31\u7a9c\uff0c\u7ade\u60f3\u8d8a\u6797\u800c \u53bb \u3000\u3000\u4f46\u4ed6\u8eab\u5f62\u65b9\u52a8\uff0c\u90a3\u201c\u8ffd\u98ce\u65e0\u5f71\u201d\u5df2\u5389\u5428\u4e00\u58f0\uff0c\u66b4\u559d\u9053\uff1a\u201c\u670b\u53cb\uff0c\u4f60\u7ed9\u6211\u7559\u4e0b\u6765\uff01\u201d\u8eab\u5f62 \u6beb\u672a\u4f5c\u52bf\uff0c\u5df2\u5237\u5730\u63a0\u51fa\u4e09\u4e08\u5f00\u5916\uff0c\u5c11\u5e74\u5c55\u767d\u53ea\u89c9\u773c\u524d\u4e00\u82b1\uff0c\u8fd9\u201c\u8ffd\u98ce\u65e0\u5f71\u201d\u5df2\u62e6\u5728\u4ed6\u524d\u9762\u3002 \u3000\u3000\u4ed6\u9762\u8272\u4e00\u53d8\uff0c\u4e00\u626d\u8170\uff0c\u5f80\u4fa7\u9762\u5c31\u6251\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u5728\u8fd9\u4ee5\u8f7b\u529f\u89c1\u91cd\u6b66\u6797\u7684\u201c\u8ffd\u98ce\u65e0\u5f71\u201d\u524d\u9762\u6c57\u9b3c\u5730\u9003\u5f97\u51fa\u53bb\uff0c\u90a3\u534e\u6e05\u6cc9\u811a\u6b65\u53ea\u4e00 \u9519\uff0c\u53c8\u62e6\u5728\u4ed6\u524d\u9762\uff0c\u5de6\u624b\u75be\u51fa\uff0c\u5e76\u6307\u5982\u5251\uff0c\u98ce\u58f0\u8dd1\u7136\uff0c\u76f4\u70b9\u4ed6\u4e73\u4e0a\u4e00\u5bf8\u516d\u5207\u95f4\u7684\u201c\u81ba\u7a97 \u7a74\u201d\uff0c\u4e00\u9762\u53c8\u559d\u9053\uff1a\u597d\u7334\u513f\u5d3d\u5b50\uff0c\u4f60\u60f3\u69b4\uff0c\u4f60\u8fd9\u662f\u5728\u505a\u68a6\u3002\u201d \u3000\u3000\u5c11\u5e74\u5c55\u767d\u8eab\u5f62\u65bd\u52a8\u95f4\uff0c\u80f8\u524d\u98ce\u58f0\u5df2\u81f3\uff0c\u4ed6\u811a\u6b65\u731b\u632b\uff0c\u8f6c\u8702\u8170\uff0c\u6325\u5de6\u638c\uff0c\u6284\u7740\u8fd9\u201c\u8ffd\u98ce\u65e0 \u5f71\u201d\u7684\u624b\u8155\u4fbf\u5207\uff0c\u8eab\u624b\u4e5f\u9887\u5feb\u6377\uff0c\u8fd9\u4e00\u638c\u521a\u521a\u9010\u51fa\u53bb\uff0c\u53ea\u89c9\u8098\u95f4\u4e00\u9ebb\uff0c\u81ea\u5df1\u7684\u8eab\u8eaf\uff0c\u4fbf\u518d\u4e5f \u65e0\u6cd5\u52a8\u5f39\uff0c\u4ed6\u81ea\u77e5\u5df2\u88ab\u4eba\u5bb6\u70b9\u4e2d\u7a74\u9053\u4e86\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u5728\u5fc3\u91cc\u6697\u53f9\u4e00\u58f0\uff0c\u53c8\u6697\u6068\u4e16\u4eba\uff0c\u4e3a\u4ec0\u4e48\u5f53\u4e00\u4e2a\u4eba\u81ea\u5df1\u4e0d\u613f\u63d0\u8d77\u81ea\u5df1\u8eab\u4e16\u7684\u65f6\u5019\uff0c \u522b\u4eba\u5374\u504f\u504f\u8981\u903c\u81ea\u5df1\u8bf4\u51fa\u6765? \u3000\u3000\u8fd9\u201c\u8ffd\u98ce\u65e0\u5f71\u201d\u6307\u5c16\u5fae\u62c2\u5904\uff0c\u70b9\u4e2d\u4e86\u5c11\u5e74\u5c55\u767d\u8098\u95f4\u7684\u201c\u66f2\u6c60\u201d\u7a74\uff0c\u94c1\u8155\u4e00\u6284\uff0c\u7a7f\u5165\u4ed6\u7684 \u808b\u4e0b\uff0c\u968f\u5373\u4e00\u9707\u8155\u5b50\uff0c\u8fdc\u8fdc\u7684\u5c06\u8fd9\u5c11\u5e74\u671d\u6469\u64ce\u4e91\u795e\u624b\u201d\u5411\u51b2\u5929\u629b\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u201c\u6469\u4e91\u795e\u624b\u201d\u53cc\u638c\u5fae\u4f38\uff0c\u7ade\u50cf\u662f\u6beb\u4e0d\u8d39\u529b\u822c\uff0c\u5c31\u63a5\u4f4f\u4e86\u4ed6\u7684\u8eab\u8eaf\uff0c\u518d\u968f\u624b\u629b\u5728\u5730\u4e0a\u3002\u534e \u6e05\u6cc9\u63a0\u8fc7\u6765\uff0c\u51b7\u51b7\u671b\u4e86\u4e91\u94ee\u4e00\u773c\uff0c\u4e91\u94ee\u5251\u7709\u5fae\u76b1\uff0c\u8fd9\u4e8b\u53d1\u5c55\u81f3\u6b64\uff0c\u4ed6\u4e5f\u8d8a\u6765\u8d8a\u7cca\u6d82\u4e86\u3002 \u3000\u3000\u4ed6\u7edd\u5bf9\u60f3\u4e0d\u5230\uff0c\u8fd9\u5c11\u5e74\u5728\u4e00\u63d0\u8d77\u201c\u9739\u96f7\u5251\u201d\u4e09\u5b87\u65f6\uff0c\u4fbf\u7acb\u523b\u6e9c\u8d70\uff0c\u4ed6\u4e5f\u4ed8\u5ea6\u4e0d\u51fa\u8fd9\u5176\u4e2d \u539f\u56e0\uff0c\u4e0d\u7981\u6697\u4e2d\u601d\u7d22\u9053\uff1a\u201c\u96be\u9053\u8fd9\u5e74\u7eaa\u8f7b\u8f7b\u7684\u5c11\u5e74\uff0c\u7ade\u548c\u4e8c\u4e09\u5341\u5e74\u524d\u90a3\u2018\u9739\u96f7\u5251\u2019\u5c55\u5927\u4fa0\u4e4b \u6b7b\u6709\u7740\u4ec0\u4e48\u5173\u8fde\u4e0d\u6210?\u201d\u4e00\u5ff5\u81f3\u6b64\uff0c\u76ee\u5149\u63a0\u8fc7\u90a3\u8fd8\u5728\u201c\u8ffd\u98ce\u65e0\u5f71\u201d\u638c\u4e2d\u6301\u7740\u7684\u957f\u5251\uff0c\u4e0d\u7981\u5fc3 \u4e2d\u53c8\u662f\u4e00\u52a8\uff0c\u9a87\u7136\u53c8\u4ed8\u9053\uff1a\u201c\u8fd9\u4f4d\u7b2c\u4e00\u795e\u5077\u7d27\u7d27\u903c\u7740\u4ed6\u95ee\u7684\u539f\u56e0\uff0c\u96be\u9053\u662f\u56e0\u4e3a\u8fd9\u5c11\u5e74\u65b9\u624d\u6240 \u4f7f\u7684\u5251\uff0c\u5c31\u662f\u5f53\u4e2d\u5c55\u5927\u9655\u9707\u6444\u6c5f\u6e56\u7684\u2018\u65e0\u60c5\u78a7\u5251\u2019\u5417?\u201d \u3000\u3000\u90a3\u201c\u8ffd\u98ce\u65e0\u5f71\u201d\u9762\u5bd2\u5982\u6c34\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4e91\u516c\u5b50\uff0c\u4f60\u6b64\u523b\u5927\u7ea6\u4e5f\u77e5\u9053\u4e86\u6211\u4e3a\u4ec0\u4e48\u8981\u903c\u95ee \u4ed6\u7684\u539f\u56e0\u5427?\u6614\u5e74\u5c55\u5927\u4fa0\u7528\u8fd9\u4e24\u2018\u65e0\u60c5\u78a7\u5251\u2019\u505a\u8fc7\u4e86\u4e0d\u77e5\u591a\u5c11\u6069\u60c5\u5982\u5929\u7684\u4e8b\uff0c\u4f46\u662f\u82cd\u5929\u65e0 \u773c\uff0c\u5374\u8ba9\u5c55\u5927\u4fa0\u4e0d\u660e\u4e0d\u767d\u5730\u6b7b\u4e86\uff01\u4e91\u516c\u5b50\uff01\u201d\u4ed6\u8bdd\u58f0\u53c8\u53d8\u5f97\u6fc0\u5386\u8d77\u6765\uff0c\u9053\uff1a\u4f11\u602a\u7ade\u6597\u80c6\u8bf4\u4e00 \u53e5\uff0c\u516c\u5b50\u4f60\u5e74\u7eaa\u8fd8\u8f7b\uff0c\u4f24\u6ca1\u6709\u770b\u5230\u5c55\u5927\u4fa0\u5728\u6d1e\u5ead\u6e56\u4e0a\u6b7b\u72b6\u4e4b\u60e8\uff0c\u6211\u5374\u770b\u5230\u4e86\u3002\u6211\u534e\u6e05\u6cc9\u8eab\u53d7 \u5c55\u5927\u4fa0\u7684\u6d3b\u547d\u518d\u9020\u4e4b\u6069\uff0c\u53ef\u662f\uff0c\u5f53\u6211\u5728\u6d1e\u5ead\u6e56\u4e0a\u770b\u5230\u5c55\u5927\u4fa0\u90a3\u5177\u6b7b\u72b6\u6e17\u4e0d\u5fcd\u7779\u7684\u5c38\u8eab\u65f6\uff0c \u6211\u2026\uff0c\uff0e\xb7\u6211\u2026\u2026\u7a33\u7ade\u8fde\u51f6\u624b\u662f\u8c01\u90fd\u627e\u4e0d\u51fa\u6765\uff01\u201d \u3000\u3000\u4ed6\u60b2\u786c\u7740\u5598\u4e86\u4e00\u53e3\u6c14\uff0c\u53c8\u54bd\u4e00\u4e0b\u553e\u6cab\uff0c\u50cf\u662f\u8981\u5c06\u5df2\u5feb\u7206\u53d1\u7684\u60c5\u611f\u4e9b\uff0c\u53c8\u63a5\u7740\u9053\uff1a\u201c\u4e8c\u5341 \u5e74\u6765\uff0c\u6211\u65e0\u65f6\u65e0\u523b\u4e0d\u5728\u5bfb\u627e\u7740\u5c55\u5927\u4fa0\u7684\u4ec7\u5bb6\uff0c\u4f46\u662f\u6211\u7eb5\u7136\u7528\u5c3d\u5343\u65b9\u767e\u8ba1\uff0c\u4e5f\u63a2\u67e5\u4e0d\u51fa\u8fd9\u73ed\u8d3c \u5b50\u7a76\u7adf\u662f\u8c01\u6765\uff0c\u603b\u7b97\u5929\u53ef\u601c\u6211\uff0c\u4eca\u65e5\u8ba9\u6211\u627e\u51fa\u4e00\u4e9b\u7709\u76ee\u6765\u4e86\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u5230\u8fd9\u91cc\uff0c\u201c\u5b89\u4e50\u516c\u5b50\u201d\u5e38\u5e26\u7b11\u5bb9\u7684\u9762\u4e0a\uff0c\u4e5f\u4e0d\u7981\u4e3a\u4e4b\u9eef\u7136\u3002 \u3000\u3000\u53ea\u89c1\u8fd9\u60b2\u6006\u65e0\u6bd4\u7684\u7626\u5c0f\u8001\u4eba\uff0c\u6b64\u523b\u4e3e\u76ee\u671b\u5929\uff0c\u53c8\u9053\uff1a\u4e91\u516c\u5b50\uff0c\u4f60\u53ef\u77e5\u9053\uff0c\u5f53\u6211\u53d1\u73b0\u8fd9\u5c11 \u5e74\u624b\u4e2d\u6240\u6301\u7684\u5251\u5c31\u662f\u5f53\u5e74\u5c55\u5927\u4fa0\u7684\u6545\u7269\u65f6\uff0c\u6211\u5fc3\u91cc\u662f\u4ec0\u4e48\u6ecb\u5473?\u4e91\u516c\u5b50\uff0c\u6211\u8981\u662f\u4e0d\u5c06\u8fd9\u5c11\u5e74 \u5f97\u5230\u6b64\u5251\u7684\u6765\u5386\u95ee\u6e05\uff0c\u6211\u600e\u5bf9\u5f97\u8d77\u90a3\u5728\u4e5d\u6cc9\u4e4b\u4e0b\u7684\u6069\u4eba?\u6211\u8981\u662f\u8ba9\u5c55\u5927\u4fa0\u51a4\u6c89\u6d77\u5e95\uff0c\u6211\u8fd8\u7b97 \u662f\u4e2a\u4eba\u5417?\u201d \u3000\u3000\u201c\u5b89\u4e50\u516c\u5b50\u201d\u542c\u4e86\uff0c\u795e\u8272\u8d8a\u53d1\u7f00\u6de1\uff0c\u8bb7\u8bb7\u5730\u7adf\u518d\u8bf4\u4e0d\u51fa\u8bdd\u3002 \u3000\u3000\u201c\u8ffd\u98ce\u65e0\u5f71\u201d\u534e\u6e05\u6cc9\u53cc\u76ee\u6709\u5982\u706b\u8d64\uff0c\u7a81\u5730\u4e00\u5f2f\u8170\uff0c\u5de6\u638c\u75be\u4f38\uff0c\u5728\u8fd9\u5c11\u5e74\u7684\u80a9\u4e0a\u3001\u80c1\u4e0b\uff0c \u4e00\u62cd\u4e00\u634f\uff0c\u89e3\u5f00\u4e86\u4ed6\u7684\u7a74\u9053\uff0c\u5374\u7528\u53f3\u624b\u7684\u957f\u5251\u6307\u7740\u8fd9\u5c11\u5e74\u7684\u54bd\u5589\uff0c\u76ee\u5149\u5982\u5203\uff0c\u5389\u58f0\u9053\uff0f\u670b \u53cb\uff0c\u65b9\u624d\u7684\u8bdd\uff0c\u4f60\u603b\u8be5\u542c\u5230\u4e86\uff0c\u6211\u4e5f\u77e5\u9053\u4f60\u5e74\u7eaa\u8fd8\u8f7b\uff0c\u4e0d\u4f1a\u662f\u6740\u5bb3\u5c55\u5927\u4fa0\u7684\u51f6\u624b\uff0c\u53ef\u662f\u6211\u5374 \u5f97\u95ee\u95ee\u4f60\uff0c\u4f60\u8fd9\u53e3\u5251\u662f\u54ea\u91cc\u6765\u7684?\u4f60\u8981\u662f\u5bf9\u6211\u8001\u5934\u5b50\u9690\u85cf\u534a\u70b9\uff0c\u54fc!\u201d \u3000\u3000\u8fd9\u7626\u524a\u4e25\u5cfb\u7684\u8001\u4eba\u8bed\u58f0\u4e00\u987f\uff0c\u624b\u8155\u5fae\u6296\uff0c\u671d\u5c16\u98a4\u52a8\uff0c\u78a7\u5149\u751f\u5bd2\uff0c\u5728\u8fd9\u5c11\u5e74\u54bd\u5589\u524d\u4e09\u5206\u4e4b \u5904\u4e00\u5212\uff0c\u5389\u58f0\u63a5\u9053\uff1a\u201c\u4eca\u5929\u6211\u5c31\u800d\u8ba9\u4f60\u7684\u8840\uff0c\u7acb\u65f6\u6e85\u5728\u8fd9\u53e3\u5251\u4e0a\u3002\u201d \u3000\u3000\u5251\u5149\u5982\u78a7\uff0c\u5251\u6c14\u68ee\u5bd2\uff0c\u8fbe\u534e\u6e05\u6cc9\u67af\u7626\u7684\u624b\u638c\uff0c\u7d27\u7d27\u6293\u5728\u5251\u628a\u4e0a\uff0c\u751f\u50cf\u662f\u94a2\u94c1\u94f8\u7684\uff0c\u52a8\u4e5f \u4e0d\u52a8\uff0c\u4f7f\u5f97\u5251\u592e\u53ea\u662f\u505c\u7559\u5728\u8fd9\u5c11\u5e74\u5589\u524d\u4e09\u5206\u4e4b\u5904\u3002 \u3000\u3000\u201c\u5b89\u4e50\u516c\u5b50\u201d\u5fae\u5531\u4e00\u58f0\uff0c\u76ee\u5149\u6d41\u8f6c\uff0c\u53ea\u89c1\u8fd9\u5c11\u5e74\u5634\u89d2\u7d27\u95ed\uff0c\u53cc\u775b\u70af\u7136\uff0c\u9762\u4e0a\u7adf\u7136\u4e1d\u6beb\u6ca1 \u6709\u60ca\u60e7\u4e4b\u8272\uff0c\u4e0d\u7981\u6697\u6697\u8d5e\u53f9\uff1a\u65e0\u8bba\u5982\u4f55\uff0c\u8fd9\u5c11\u5e74\u603b\u7b97\u4e2a\u94c1\u94ee\u94ee\u7684\u6c49\u5b50\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u6b63\u81ea\u601d\u5fd6\uff0c\u5374\u89c1\u8fd9\u534e\u6e05\u6cc9\u8bed\u58f0\u4e00\u843d\uff0c\u90a3\u5c11\u5e74\u53cc\u8098\u4e00\u4f38\uff0c\u8eab\u5f62\u540e\u6ed1\uff0c\u7a81\u5730\u7ffb\u8eab\u7ad9\u4e86 \u8d77\u6765\uff0c\u534e\u6e05\u6cc9\u51b7\u559d\u4e00\u58f0\u7f6e\uff1a\u201c\u4f60\u8fd9\u662f\u627e\u6b7b!\u201d\u957f\u81c2\u4f38\u5904\uff0c\u5251\u5149\u5982\u7ec3\uff0c \u3000\u3000\u54ea\u77e5\u8fd9\u5c11\u5e74\u8eab\u8eaf\u62e7\u8f6c\uff0c\u7ade\u201c\u6251\u201d\u5730\u8dea\u4e86\u4e0b\u6765\uff0c\u606d\u606d\u656c\u656c\u5730\u5411\u534e\u6e05\u6cc9\u53e9\u4e86\u4e09\u4e2a\u5934\u3002 \u3000\u3000\u201c\u5b89\u4e50\u516c\u5b50\u201d\u89c1\u4e86\uff0c\u957f\u53f9\u4e00\u58f0\uff0c\u6697\u4e2d\u6447\u5934\uff0c\u8f6c\u8eab\u8d70\u5f00\u4e24\u6b65\u3002 \u3000\u3000\u201c\u6469\u4e91\u795e\u624b\u201d\u9762\u4e0a\u4ea6\u9732\u51fa\u4e0d\u5c51\u4e4b\u5bb9\uff0c\u8fd9\u5c11\u5e74\u82e5\u662f\u5014\u5f3a\u5230\u5e95\uff0c\u4ed6\u4eec\u6216\u8005\u4f1a\u52a9\u4ee5\u4e00\u81c2\u4e4b\u529b\uff0c \u4f46\u6b64\u523b\u89c1\u4ed6\u7ade\u505a\u51fa\u8fd9\u6837\u4e3e\u52a8\uff0c\u4e0d\u7981\u90fd\u5bf9\u6b64\u4eba\u5927\u8d77\u8f7b\u8511\u4e4b\u611f\u3002 \u3000\u3000\u201c\u8ffd\u98ce\u65e0\u5f71\u201d\u4e5f\u6697\u4e2d\u4e00\u695e\uff0c\u8155\u8098\u5fae\u632b\uff0c\u5c06\u957f\u5251\u6536\u8f6c\uff0c \u3000\u3000\u5374\u89c1\u8fd9\u5c11\u5e74\u4f38\u624b\u5165\u6000\uff0c\u638f\u51fa\u4e00\u4e2a\u7ec6\u9ebb\u7f16\u6210\u7684\u888b\u5b50\uff0c\u7f13\u7f13\u4ece\u888b\u4e2d\u53d6\u51fa\u4e00\u65b9\u4e1d\u7ef8\u2014\u2014\u60f3\u662f\u56e0 \u4e3a\u5e74\u4ee3\u4e45\u8fdc\uff0c\u8fd9\u5757\u7ef8\u7f0e\u5df2\u5931\u53bb\u65e7\u65e5\u5149\u6cfd\uff0c\u6781\u5176\u90d1\u91cd\u5730\u5c06\u5b83\u62ff\u5728\u624b\u91cc\uff0c\u6536\u56de\u9ebb\u888b\uff0c\u633a\u8170\u7ad9\u8d77\uff0c \u6025\u884c\u4e00\u6b65\uff0c\u8d70\u5230\u201c\u8ffd\u98ce\u5143\u5f71\u201d\u8eab\u524d\uff0c\u606d\u606d\u656c\u656c\u5730\u5c06\u8fd9\u65b9\u4e1d\u7ef8\u53cc\u624b\u6367\u5230\u534e\u6e05\u6cc9\u773c\u524d\uff0c\u76ee\u5149\u51dd \u6ce8\uff0c\u5374\u4ecd\u4e0d\u53d1\u4e00\u8a00\u3002 \u3000\u3000\u201c\u5b89\u4e50\u516c\u5b50\u201d\u888d\u8896\u5fae\u62c2\uff0c\u7f13\u6b65\u8d70\u5411\u6797\u5916\uff0c\u56de\u9996\u6652\u7136\u9053\uff1a\u201c\u5411\u8001\u5e08\uff0c\u6211\u4eec\u8be5\u8d70\u4e86\u2014\u2014\u201d\u8bdd \u72b9\u672a\u5b8c\uff0c\u5374\u89c1\u90a3\u201c\u8ffd\u98ce\u65e0\u5f71\u201d\u7ade\u5411\u90a3\u5c11\u5e74\u5c55\u767d\u5f53\u5934\u4e00\u63d6\uff0c\u9762\u4e0a\u795e\u8272\uff0c\u6fc0\u52a8\u96be\u5b89\uff0c\u5927\u53cd\u5e38\u6001\uff0c \u53cc\u76ee\u4e2d\u6ee1\u662f\u60ca\u8be7\u4e4b\u8272\uff0c\u7f13\u7f13\u4f38\u624b\u63a5\u8fc7\u8fd9\u65b9\u4e1d\u7ef8\uff0c\u9547\u5b9a\u7684\u624b\u638c\uff0c\u6b64\u523b\u7ade\u4ea6\u8d77\u4e86\u5fae\u5fae\u7684\u98a4\u6296\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u5c55\u767d\u6446\u4e86\u534a\u664c\uff0c\u540e\u9000\u4e00\u6b65\uff0c\u8eac\u8eab\u9053\uff1a\u201c\u8001\u524d\u8f88\u53ef\u5426\u5c06\u638c\u4e2d\u4e4b\u5251\uff0c\u8d50\u8fd8\u665a\u8f88?\u201d \u3000\u3000\u8fd9\u201c\u8ffd\u98ce\u65e0\u5f71\u201d\u65b9\u624d\u7684\u5f53\u5934\u4e00\u63d6\uff0c\u4f7f\u5f97\u4ed6\u4ea6\u662f\u60ca\u8be7\u83ab\u540d\uff0c\u76ee\u5149\u8f6c\u52a8\u5904\uff0c\u89c1\u90a3\u5b89\u4e50\u516c\u5b50\u4ea6 \u81ea\u505c\u4e0b\u811a\u6b65\uff0c\u5403\u60ca\u5730\u671b\u7740\u81ea\u5df1\uff0c\u201c\u6469\u4e91\u795e\u624b\u201d\u56de\u987e\u4e4b\u95f4\uff0c\u663e\u7136\u4ea6\u5927\u4e3a\u60ca\u6115\u3002 \u3000\u3000\u53ef\u662f\u8fd9\u4e9b\u4eba\u5fc3\u4e2d\u867d\u611f\u60ca\u8be7\uff0c\u53e3\u4e2d\u5374\u90fd\u6ca1\u6709\u95ee\u51fa\u6765\uff0c\u53ea\u89c1\u201c\u8ffd\u98ce\u65e0\u5f71\u201d\u534e\u6e05\u6cc9\u5de6\u624b\u6367\u7740\u90a3 \u65b9\u4e1d\u7ef8\uff0c\u5446\u5446\u5730\u51dd\u89c6\u4e86\u534a\u523b\uff0c\u7a81\u5730\u957f\u53f9\u4e00\u58f0\uff0c\u7535\u4e5f\u4f3c\u5730\u5012\u8f6c\u5251\u5c16\u2014\u2014 \u3000\u3000\u78a7\u5149\u4e00\u95ea\uff0c\u8840\u5149\u5d29\u73b0\uff0c\u201c\u5b89\u4e50\u516c\u5b50\u201d\u3001\u201c\u6469\u4e91\u795e\u624b\u201d\uff0c\u4e0d\u7ea6\u800c\u540c\u5730\u5927\u559d\u4e00\u58f0\uff1a\u201c\u534e\u5e08 \u5085\uff01\u201d\u7bad\u6b65\u4e00\u7a9c\u800c\u524d\uff0c\u5374\u89c1\u8fd9\u7eb5\u6a2a\u6b66\u6797\u4e00\u4e16\u7684\u201c\u8ffd\u98ce\u65e0\u5f71\u201d\u5df2\u5012\u5728\u5730\u4e0a\uff0c\u9888\u95f4\u8840\u6d41\u5982\u6ce8\uff0c\u7adf \u8fde\u540e\u8bdd\u90fd\u6ca1\u6709\u4e00\u53e5\uff0c\u5c31\u81ea\u520e\u800c\u6b7b\u3002\u4ed6\u90a3\u5e72\u67af\u7684\u624b\u638c\u91cc\uff0c\u4ecd\u7d27\u7d27\u6293\u7740\u90a3\u65b9\u4e1d\u7ef8\uff0c\u957f\u5251\u4e00\u78a7\u5982 \u6d17\uff0c\u83b9\u5982\u79cb\u6c34\uff0c\u6a2a\u7f6e\u5728\u4ed6\u80f8\u524d\uff0c\u6620\u5f97\u4ed6\u626d\u66f2\u7684\u9762\u5b54\uff0c\u770b\u8d77\u6765\u7adf\u6709\u4e00\u4efd\u72f0\u72de\u7684\u611f\u89c9\uff0c \u3000\u3000\u8fd9\u4e00\u4e2a\u7a81\u751f\u7684\u53d8\u6545\uff0c\u6709\u5982\u6674\u5929\u9739\u96f7\uff0c\u4f7f\u5f97\u6bcf\u4e2a\u4eba\u90fd\u695e\u4f4f\u4e86\u3002\u4efb\u4f55\u4eba\u8fde\u505a\u68a6\u90fd\u4e0d\u4f1a\u60f3\u5230\uff0c \u8fd9\u201c\u8ffd\u98ce\u65e0\u5f71\u201d\u7adf\u4f1a\u7a81\u5730\u6a2a\u5251\u81ea\u520e\uff0c\u4e8b\u524d\u4e0d\u4f46\u6ca1\u6709\u7559\u4e0b\u7247\u8a00\u53ea\u5b87\uff0c\u751a\u81f3\u8fde\u534a\u70b9\u8ff9\u8c61\u90fd\u6ca1\u6709\u3002 \u6469\u4e91\u795e\u624b\u201d\u867d\u662f\u6027\u60c5\u51b7\u9177\u3001\u6df1\u85cf\u4e0d\u9732\u4e4b\u4eba\uff0c\u6b64\u523b\u4ea6\u4e0d\u7981\u989c\u8272\u5927\u53d8\uff0c\u7626\u957f\u7684\u8eab\u8eaf\u4e00\u4fef\uff0c\u5c06\u8fd9\u534e \u6e05\u6cc9\u7684\u5c38\u8eab\u659c\u6284\u4e86\u8d77\u6765\u3002\u53ea\u89c1\u4ed6\u9888\u95f4\u4f24\u75d5\u751a\u6df1\uff0c\u5934\u8f6f\u8f6f\u5730\u642d\u4e86\u4e0b\u53bb\uff0c\u9762\u4e0a\u7684\u808c\u8089\uff0c\u75db\u82e6\u5730\u626d \u66f2\u7740\uff0c\u4e0d\u77e5\u662f\u56e0\u4e3a\u751f\u524d\u7684\u6fc0\u52a8\uff0c\u6291\u6216\u662f\u6b7b\u65f6\u7684\u75db\u82e6\u3002 \u3000\u3000\u66ae\u98ce\u5439\u8fc7\u6811\u6797\uff0c\u4f7f\u5f97\u4ed6\u673a\u4f36\u4f36\u5730\u6253\u4e86\u4e2a\u5bd2\u611f\uff0c\u8f6c\u76ee\u671b\u53bb\uff0c\u53ea\u89c1\u90a3\u5c11\u5e74\u5c55\u81ea\u695e\u695e\u5730\u7ad9\u5728\u65c1 \u8fb9\uff0c\u8138\u4e0a\u94c1\u9752\u4e00\u7247\uff0c\u50cf\u662f\u60ca\u5f97\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u5411\u51b2\u5929\u548c\u201c\u8ffd\u98ce\u65e0\u5f71\u201d\u76f8\u4ea4\u591a\u5e74\uff0c\u6b64\u523b\u6a2a\u6284\u7740\u8fd9\u66fe\u7ecf\u53f1\u54a4\u4e00\u65f6\u7684\u6b66\u6797\u9ad8\u624b\u7684\u5c38\u8eab\uff0c\u5fc3\u4e2d \u601d\u6f6e\u6f8e\u6e43\u3002\u4ed6\u6df1\u77e5\u534e\u6e05\u6cc9\u7684\u4e3a\u4eba\uff0c\u77e5\u9053\u4ed6\u4e5f\u6b63\u548c\u81ea\u5df1\u4e00\u6837\uff0c\u60c5\u611f\u7684\u575a\u5f3a\uff0c\u8db3\u4ee5\u7ecf\u5f97\u8d77\u4efb\u4f55\u91cd \u5927\u7684\u6253\u51fb\uff0c\u90a3\u4e48\u4ed6\u53c8\u4e3a\u4ec0\u4e48\u5728\u89c1\u5230\u90a3\u65b9\u4e1d\u7ef8\u65f6\uff0c\u5c31\u7a81\u5730\u5982\u6b64\u5462? \u3000\u3000\u4ed6\u8f7b\u8f7b\u653e\u4e0b\u8fd9\u5177\u5c38\u8eab\uff0c\u7f13\u7f13\u6252\u5f00\u90a3\u53ea\u7d27\u63e1\u7740\u7684\u624b\u62f3\uff0c\u53d6\u51fa\u90a3\u65b9\u4e1d\u7ef8\u6765\uff0c\u4e43\u89c1\u8fd9\u65b9\u7ade\u80fd\u4f7f \u5f97\u4e00\u4e2a\u6b66\u6797\u9ad8\u624b\u4e27\u5931\u6027\u547d\u7684\u4e1c\u897f\uff0c\u53ea\u662f\u4e00\u5757\u6781\u5176\u666e\u901a\u7684\u5e03\u6599\uff0c\u989c\u8272\u867d\u7136\u4e5f\u66fe\u662f\u9c9c\u8273\u7684\uff0c\u4f46\u6b64 \u523b\u5374\u5df2\u65e7\u5f97\u6cdb\u9ec4\uff0c\u800c\u4e14\u56db\u4fa7\u3002\u4e1d\u7ebf\u8131\u843d\uff0c\u6781\u4e0d\u89c4\u5219\uff0c\u50cf\u662f\u7531\u4e00\u5757\u5927\u7ef8\u5b50\u4e0a\u7528\u91cd\u624b\u6cd5\u626f\u843d\u7684\u3002 \u3000\u3000\u90a3\u4e48\uff0c\u5728\u8fd9\u4e00\u5c0f\u5757\u6781\u5176\u666e\u901a\u7684\u4e1d\u7ef8\u91cc\uff0c\u53c8\u9690\u85cf\u7740\u4e00\u4e2a\u4ec0\u4e48\u5de8\u5927\u7684\u79d8\u5bc6\u5462\uff1f \u3000\u3000\u201c\u6469\u4e91\u795e\u624b\u201d\u5fc3\u601d\u8f6c\u52a8\u95f4\uff0c\u7a81\u5730\u63a0\u8d77\u5982\u9e70\uff0c\u8eab\u5f62\u8f7b\u6298\uff0c\u75be\u4f38\u94c1\u638c\uff0c\u5237\u5730\u5411\u90a3\u5c11\u5e74\u5f53\u80f8\u51fb \u53bb\u3002 \u3000\u3000\u54ea\u77e5\u8fd9\u5c11\u5e74\u5c55\u81ea\u5374\u4ecd\u7136\u52a8\u4e5f\u4e0d\u52a8\uff0c\u76ee\u5149\u51dd\u89c6\uff0c\u597d\u50cf\u662f\u4ec0\u4e48\u4e5f\u6ca1\u770b\u5230\u3002 \u3000\u3000\u5411\u51b2\u5929\u5927\u559d\u4e00\u58f0\uff0c\u8155\u8098\u5fae\u6296\uff0c\u7a81\u5730\u53d8\u638c\u4e3a\u6293\uff0c\u4e94\u6307\u5982n\u94a9\uff0c\u5201\u4f4f\u8fd9\u5c11\u5e74\u5c55\u767d\u7684\u624b\u8155\uff0c\u5de6 \u638c\u4e00\u626c\uff0c\u5c06\u638c\u5fc3\u90a3\u65b9\u4e1d\u7ef8\u9001\u5230\u4ed6\u7684\u773c\u524d\uff0c\u5389\u58f0\u559d\u9053\uff1a\uff0f\u8fd9\u662f\u4ec0\u4e48?\u201d \u3000\u3000\u5c11\u5e74\u5c55\u767d\u7f13\u7f13\u62ac\u8d77\u773c\u775b\u6765\uff0c\u5446\u6ede\u5730\u671b\u7740\u4ed6\uff0c\u5374\u6447\u4e86\u6447\u5934\u3002 \u3000\u3000\u201c\u6469\u4e91\u795e\u624b\u201d\u52fe\u4f4f\u8fd9\u5c11\u5e74\u5c55\u81ea\u5de6\u8155\u7684\u53f3\u624b\uff0c\u7a81\u5730\u4e00\u7d27\uff0c\u4e00\u53cc\u9e70\u76ee\uff0c\u5176\u5229\u5982\u7535\uff0c\u77ac\u4e5f\u4e0d\u77ac \u5730\u671b\u5728\u8fd9\u5c11\u5e74\u9762\u4e0a\uff0c\u53c8\u5389\u58f0\u559d\u9053\uff1a\u2018\u670b\u53cb\uff0c\u4f60\u7a76\u7adf\u662f\u4ec0\u4e48\u4eba?\u8fd9\u5757\u7834\u5e03\u7a76\u7adf\u662f\u4ec0\u4e48\u4e1c\u897f?\u201d \u3000\u3000\u4e00\u79cd\u6df1\u5165\u9aa8\u9ad3\u7684\u75db\u82e6\uff0c\u4f7f\u5f97\u8fd9\u5c11\u5e74\u5c55\u767d\u7684\u4e00\u6761\u5de6\u81c2\u51e0\u4e4e\u5b8c\u5168\u5931\u53bb\u77e5\u89c9\uff0c\u4f46\u662f\u4ed6\u4ecd\u7136\u5f3a\u5fcd \u7740\uff0c\u5634\u4e2d\u7edd\u4e0d\u56e0\u4efb\u4f55\u75db\u82e6\u800c\u547b\u541f\u51fa\u6765\uff0c\u53ea\u662f\u6df1\u6df1\u5730\u53c8\u6447\u4e86\u6447\u5934\uff0c\u8fd9\u65b9\u4e1d\u7ef8\u867d\u7136\u662f\u4ed6\u81ea\u5df1\u53d6\u51fa \u7684\uff0c\u4f46\u4ed6\u548c\u522b\u4eba\u4e00\u6837\uff0c\u4e5f\u5728\u60ca\u5f02\u4e8e\u8fd9\u4ef6\u7a81\u751f\u7684\u53d8\u6545\uff0c\u60ca\u5f02\u4e8e\u8fd9\u65b9\u4e1d\u7ef8\u7684\u9b54\u529b\uff0c\u56e0\u4e3a\u4ed6\u4ea6\u662f\u4e00 \u65e0\u6240\u77e5\u7684\u3002 \u3000\u3000\u201c\u6469\u4e91\u795e\u624b\u201d\u53cc\u7709\u4e00\u8038\uff0c\u53f3\u638c\u5fae\u62e7\uff0c\u5c11\u5e74\u5c55\u767d\u7981\u4e0d\u4f4f\u8f7b\u8f7b\u4e00\u54fc\uff0c\u4ed6\u77e5\u9053\u53ea\u8981\u4eba\u5bb6\u518d\u4e00\u7528 \u529b\uff0c\u81ea\u5df2\u7684\u624b\u8155\u4fbf\u5f97\u88ab\u751f\u751f\u62e7\u65ad\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u751f\u5177\u50b2\u9aa8\uff0c\u6c42\u60c5\u4e5e\u514d\u7684\u8bdd\uff0c\u4ed6\u4e07\u4e07\u8bf4\u4e0d\u51fa\u6765\uff0c\u522b\u7684\u8bdd\uff0c\u4ed6\u5374\u56e0\u4e3a\u8fd9\u65b9\u4e1d\u7ef8\uff0c\u786e\u662f \u81ea\u5df1\u53d6\u51fa\u4ea4\u7ed9\u90a3\u201c\u8ffd\u98ce\u65e0\u5f71\u201d\u7684\uff0c\u800c\u201c\u8ffd\u98ce\u65e0\u5f71\u201d\u53c8\u786e\u662f\u4e3a\u6b64\u800c\u6a2a\u5251\u81ea\u520e\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u6697\u53f9\u4e00\u58f0\uff0c\u4ed8\u9053\uff1a\u5176\u5b9e\u6211\u53c8\u4f55\u5c1d\u77e5\u9053\u6b64\u4e8b\u7adf\u4f1a\u5982\u6b64\u53d1\u5c55?\u6211\u82e5\u77e5\u9053\u201c\u8ffd\u98ce\u65e0\u5f71\u2019 \u4f1a\u56e0\u6b64\u800c\u6b7b\uff0c\u90a3\u4e48\u6211\u4e5f\u4e07\u4e07\u4e0d\u4f1a\u53d6\u51fa\u8fd9\u65b9\u4e1d\u7ef8\u6765\u2014\u2014\u201d \u3000\u3000\u62ac\u76ee\u4e00\u671b\uff0c\u5374\u89c1\u90a3\u59cb\u7ec8\u4fef\u9996\u51dd\u601d\u7740\u7684\u201c\u5b89\u4e50\u516c\u5b50\u201d\u4e91\u94ee\u7f13\u6b65\u8d70\u4e86\u8fc7\u6765\uff0c\u5f90\u7136\u4f38\u51fa\u624b\u81c2\u642d \u5728\u5411\u51b2\u5929\u7684\u5de6\u638c\u4e0a\uff0c\u5c06\u5411\u51b2\u5929\u7684\u94c1\u638c\uff0c\u4ece\u81ea\u5df1\u7684\u8155\u95f4\u79fb\u5f00\u3002 \u3000\u3000\u5411\u51b2\u5929\u9762\u8272\u5fae\u53d8\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u516c\u5b50\uff0c\u4f60\u8fd9\u662f\u5e72\u4ec0\u4e48?\u201d \u3000\u3000\u4e91\u94ee\u5fae\u53f9\u4e00\u58f0\uff0c\u5374\u4e0d\u56de\u7b54\u4ed6\u7684\u8bdd\uff0c\u8f6c\u8fc7\u5934\u53bb\uff0c\u5411\u90a3\u5c11\u5e74\u5c55\u767d\u7f13\u7f13\u9053\uff1a\u5144\u53f0\u4ea6\u662f\u59d3\u5c55\uff0c\u4e0d \u77e5\u662f\u5426\u5c31\u662f\u90a3\u9739\u96f7\u5251\u5c55\u8001\u524d\u8f88\u7684\u540e\u4eba?\u201d \u3000\u3000\u5c55\u767d\u8eab\u8eaf\u2014\u633a\uff0c\u9053\uff1a\u201c\u5c0f\u53ef\u5eb8\u788c\u65e0\u624d\uff0c\u4e3a\u6050\u8fb1\u53ca\u5148\u4eba\uff0c\u662f\u4ee5\u4e0d\u6562\u63d0\u53ca\u3002\u6b64\u523b\u516c\u5b50\u65e2\u7136\u731c \u4e2d\uff0c\u5509!\u201d\u4ed6\u5de6\u8155\u4e4b\u95f4\uff0c\u867d\u4ecd\u75db\u5f7b\u5fc3\u813e\uff0c\u5374\u7edd\u4e0d\u7528\u53f3\u624b\u53bb\u629a\u6478\u4e00\u4e0b\u3002 \u3000\u3000\u201c\u5b89\u4e50\u516c\u5b50\u201d\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u4e86\uff0c\u5144\u53f0\u5982\u4e0d\u662f\u5c55\u5927\u4fa0\u7684\u540e\u4eba\uff0c\u65b9\u624d\u4e5f\u5c31\u7edd\u4e0d\u4f1a \u5bf9\u90a3\u2014\u2014\u534e\u5e08\u5085\u5c48\u819d\u7684\u3002\u201d \u3000\u3000\u4ed6\u8bed\u58f0\u5fae\u987f\uff0c\u5c11\u4e2d\u5c55\u767d\u7acb\u523b\u957f\u53f9\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5148\u7236\u60e8\u6b7b\u4e4b\u540e\uff0c\u5c0f\u53ef\u4e0d\u624d\uff0c\u867d\u4e0d\u80fd\u5bfb\u51fa\u5143 \u51f6\uff0c\u4f46\u4eb2\u4ec7\u5982\u5c71\uff0c\u5e76\u672a\u4e00\u65e5\u6216\u5fd8\u3002\u201d\u4ed6\u671b\u4e86\u534e\u6e05\u6cc9\u5012\u5367\u7740\u7684\u5c38\u8eab\u4e00\u773c\uff0c\u53c8\u81ea\u53f9\u9053\uff1a\u201c\u534e\u8001\u524d \u8f88\u4e49\u8584\u4e91\u5929\uff0c\u5bf9\u5148\u7236\u7684\u6069\u60c5\uff0c\u53c8\u5c82\u662f\u5c0f\u53ef\u53e9\u9996\u80fd\u62a5\u4e07\u4e00\u7684?\u5374\u53c8\u600e\u77e5\u2014\u2014\u5509\uff01\u201d \u3000\u3000\u4ed6\u957f\u53f9\u4e00\u58f0\uff0c\u7ed3\u675f\u4e86\u81ea\u5df2\u7684\u8bdd\uff0c\u80f8\u4e2d\u5374\u89c9\u5f97\u60c5\u611f\u6fc0\u52a8\u96be\u5b89\uff0c\u56e0\u4e3a\u4ed6\u611f\u5230\u81ea\u5df2\u6709\u751f\u4ee5\u6765\uff0c \u547d\u8fd0\u574e\u5777\uff0c\u5f88\u5c11\u6709\u4eba\u5bf9\u81ea\u5df2\u52a0\u4ee5\u9752\u773c\uff0c\u800c\u4eca\u8fd9\u5b89\u4e50\u516c\u5b50\u4e91\u94ee\uff0c\u4e0d\u4f46\u5bf9\u81ea\u5df1\u5c61\u5c61\u7ef4\u62a4\uff0c\u6700\u96be\u5f97 \u7684\u662f\uff0c\u81ea\u5df1\u7adf\u4ece\u8fd9\u4ec5\u89c1\u4e00\u9762\u7684\u521d\u4ea4\u8eab\u4e0a\uff0c\u83b7\u5f97\u4e00\u4efd\u4e16\u95f4\u6700\u4e3a\u96be\u6c42\u7684\u4e86\u89e3\u3002 \u3000\u3000\u201c\u6469\u4e91\u4f38\u624b\u201d\u5411\u51b2\u5929\u5de6\u638c\u4e00\u644a\uff0c\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u6b64\u7269\u53c8\u662f\u4ec0\u4e48?\u201d\u5c55\u767d\u76ee\u5149\u2014 \u5782\uff0c\u53f9\u9053\uff1a\u8fd9\u4e2a\u4e48\u2014\u2014\u5c0f\u53ef\u5374\u4e5f\u4e0d\u77e5\u9053\u56e0\u4f55\u4f1a\u4f7f\u534e\u8001\u524d\u8f88\u5982\u6b64\u2014\u2014\u201d\u4ed6\u5fc3\u4e2d\u7a81\u5730\u4e00\u52a8\uff0c\u500f\u7136 \u987f\u4f4f\u4e86\u8bdd\u3002 \u3000\u3000\u5374\u89c1\u90a3\u201c\u5b89\u4e50\u516c\u5b50\u201d\u5df2\u542b\u7b11\u9053\uff1a\u201c\u5144\u53f0\u8bda\u60c5\u541b\u5b50\uff0c\u65e2\u7136\u5982\u6b64\uff0c\u5c0f\u5f1f\u4e07\u65e0\u4fe1\u4e0d\u8fc7\u5144\u53f0\u4e4b \u7406\uff0c\u800c\u4e14\u6b64\u4e8b\u592a\u8fc7\u79bb\u5947\uff0c\u4ea6\u975e\u6211\u7b49\u80fd\u52a0\u4ee5\u5984\u6d4b\uff0c\u53ea\u662f\u2014\u2014\u201d\u4ed6\u8bed\u58f0\u4e00\u987f\uff0c\u500f\u7136\u8f6c\u8eab\uff0c\u4fef\u8eab\u6361 \u8d77\u90a3\u67c4\u78a7\u5149\u83b9\u83b9\u7684\u957f\u5251\uff0c\u7528\u5de6\u624b\u4e24\u6307\u63a5\u4efb\u5251\u5c16\uff0c\u987a\u624b\u4ea4\u4e0e\u5c55\u767d\uff0c\u53c8\u81ea\u63a5\u53e3\u8bf4\u9053\uff1a\u201c\u6b64\u5251\u795e\u5175 \u5229\u5668\uff0c\u5927\u5f02\u5e38\u5251\uff0c\u6b66\u6797\u4e2d\u4eba\u77e5\u9053\u6b64\u5251\u6765\u5386\u7684\u5fc5\u5b9a\u4e0d\u5c11\uff0c\u5144\u53f0\u631f\u5251\u800c\u884c\uff0c\u5982\u613f\u9690\u85cf\u884c\u8e2a\uff0c\u6050\u975e \u6613\u4e8b\u54e9\u3002\u201d \u3000\u3000\u6b64\u523b\u65e5\u5df2\u5c3d\u6ca1\uff0c\u665a\u98ce\u5165\u6797\uff0c\u6ebd\u6691\u5168\u6d88\u3002 \u3000\u3000\u5c55\u767d\u5fc3\u4e2d\u601d\u6f6e\u7ffb\u6d8c\uff0c\u7f13\u7f13\u4f38\u51fa\u624b\uff0c\u53bb\u63a5\u8fd9\u67c4\u78a7\u5251\uff0c\u4e00\u9762\u8bb7\u8bb7\u9053\uff1a\u5c0f\u53ef\u5b64\u96f6\u98d8\u6cca\uff0c\u4eca\u65e5\u5f97 \u8bc6\u5144\u53f0\uff0c\u590d\u8499\u5144\u53f0\u6298\u8282\u503e\u76d6\uff0c\u5509\uff01\u53ea\u662f\u5c0f\u53ef\u788c\u788c\u65e0\u624d\uff0c\u5374\u4e0d\u77e5\u600e\u6837\u62a5\u5144\u53f0\u6b64\u756a\u77e5\u5df1\u4e4b\u6069\u3002\u201d \u3000\u3000\u54ea\u77e5\u4ed6\u624b\u6307\u65b9\u81ea\u89e6\u53ca\u5251\u67c4\uff0c\u6797\u6728\u6df1\u5904\uff0c\u7a81\u5730\u4f20\u6765\u4e00\u58f0\u957f\u7b11\uff0c\u4e00\u6761\u4eba\u5f71\uff0c\u8d34\u5730\u98de\u6765\uff0c\u5176\u75be \u5982\u77e2\uff0c\u5c55\u767d\u53ea\u89c9\u8098\u95f4\u4e00\u9ebb\uff0c\u4e00\u4e2a\u6e05\u6717\u7684\u53e3\u97f3\u8bf4\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u6b64\u5251\u8fd8\u662f\u653e\u5728\u533a\u533a\u8fd9\u91cc\uff0c\u6765\u5f97\u59a5 \u5f53\u4e9b\u3002\u201d \u3000\u3000\u8bed\u58f0\u4e4b\u59cb\uff0c\u54cd\u81ea\u4ed6\u8eab\u7554\uff0c\u7136\u800c\u8bed\u58f0\u843d\u5904\uff0c\u5374\u662f\u5341\u4e08\u5f00\u5916\uff0c\u53ea\u89c1\u4e00\u6761\u8eab\u91cf\u4eff\u4f5b\u9887\u9ad8\u7684\u4eba \u5f71\uff0c\u5e26\u7740\u4e00\u6e9c\u78a7\u5149\uff0c\u7535\u4e5f\u4f3c\u5730\u63a0\u4e86\u8fc7\u53bb\uff0c\u7728\u773c\u4e4b\u95f4\uff0c\u4fbf\u81ea\u6d88\u5931\u4e8e\u6797\u6728\u63a9\u6620\u4e4b\u4e2d\u3002 \u3000\u3000\u8fd9\u6761\u4eba\u5f71\u6765\u5982\u8fc5\u96f7\uff0c\u53bb\u5982\u95ea\u7535\uff0c\u8f7b\u529f\u4e4b\u5999\uff0c\u53ef\u8bf4\u60ca\u4e16\u9a87\u4fd7\uff0c\u4e0d\u56e0\u5c55\u767d\u6295\u6709\u770b\u6e05\u4ed6\u7684\u6765 \u52bf\uff0c\u5c31\u8fde\u201c\u6469\u4e91\u795e\u624b\u201d\u53ca\u201c\u5b89\u4e50\u516c\u5b50\u201d\u90fd\u50cf\u662f\u5927\u51fa\u610f\u5916\uff0c\u4e0d\u7981\u4e3a\u4e4b\u4e00\u60ca\u3001\u4e00\u695e\uff0c\u539f\u5148\u631f\u5728 \u201c\u5b89\u4e50\u516c\u5b50\u201d\u4e91\u94ee\u624b\u4e0a\u7684\u5251\uff0c\u6b64\u523b\u7ade\u65e0\u5f71\u65e0\u8e2a\u3002 \u3000\u3000\u4e91\u94ee\u5927\u559d\u4e00\u58f0\uff0c\u8eab\u5f62\u66b4\u957f\uff0c\u98d5\u7136\u51e0\u4e2a\u8d77\u843d\uff0c\u5411\u90a3\u4eba\u5f71\u53bb\u5411\u63a0\u53bb\uff0c\u201c\u6469\u4e91\u795e\u624b\u201d\u5411\u51b2\u5929\u76ee \u5149\u4e00\u8f6c\uff0c\u51b7\u7b11\u4e00\u58f0\uff0c\u53cc\u81c2\u5fbd\u632f\uff0c\u4ea6\u81ea\u5982\u98de\u63a0\u53bb\u3002 \u3000\u3000\u5c55\u767d\u5fae\u5fae\u508d\u4e86\u695e\uff0c\u773c\u89c1\u90a3\u5411\u51b2\u5929\u7684\u80cc\u5f71\u4ea6\u8c89\u6d88\u5931\uff0c\u518d\u4e0d\u8fdf\u7591\uff0c\u731b\u4e00\u5f13\u8eab\uff0c\u811a\u4e0b\u52a0\u52b2\uff0c\u4fbf \u4e5f\u8ffd\u53bb\u3002 \u3000\u3000\u8033\u7554\u53ea\u542c\u8eab\u540e\u53d1\u51fa\u7126\u6025\u7684\u547c\u559d\u58f0\uff0c\u60f3\u5fc5\u662f\u90a3\u4e9b\u59cb\u7ec8\u8fdc\u8fdc\u7ad9\u5728\u4e00\u8fb9\u7684\u9556\u5ba2\u6355\u5934\u53d1\u51fa\u7684\uff0c\u4ed6 \u4e5f\u6ca1\u6709\u9a7b\u8db3\u800c\u542c\u3002 \u3000\u3000\u4ed6\u867d\u7136\u65bd\u51fa\u5168\u529b\uff0c\u5728\u8fd9\u5df2\u7ecf\u5b8c\u5168\u780c\u9ed1\u7684\u6797\u6728\u4e2d\u72c2\u5954\uff0c\u4f46\u662f\u7247\u523b\u4e4b\u95f4\uff0c\u4ed6\u5374\u8fde\u90a3\u201c\u6469\u4e91\u795e \u624b\u201d\u5411\u51b2\u5929\u7684\u8eab\u5f71\u4e5f\u770b\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u8fd9\u7247\u6797\u6728\u867d\u7136\u5360\u5730\u9887\u5e7f\uff0c\u4f46\u662f\u4ed6\u5168\u529b\u800c\u5954\uff0c\u4f55\u6d88\u7247\u523b\uff0c\u4ea6\u81ea\u63a0\u51fa\u6797\u5916\uff0c\u4e3e\u76ee\u56db\u671b\uff0c\u53ea\u89c1 \u5f2f\u82cd\u4f3c\u78a7\uff0c\u6708\u534e\u5982\u6d17\uff0c\u6708\u5149\u6620\u5c04\u4e4b\u4e0b\uff0c\u56db\u91ce\u4e00\u7247\u6c89\u5bc2\uff0c\u5374\u8fde\u534a\u6761\u4eba\u5f71\u4e5f\u770b\u4e0d\u5230\uff0c \u3000\u3000\u4ed6\u5fae\u5fae\u5598\u4e86\u53e3\u6c14\uff0c\u89e3\u5f00\u524d\u895f\u7684\u4e00\u7c92\u94ae\u5b50\uff0c\u8ba9\u6e05\u51c9\u591c\u98ce\u5f53\u80f8\u5439\u6765\uff0c\u4f46\u5fc3\u4e2d\u5374\u4ecd\u662f\u70ed\u8840\u5982 \u6cb8\uff0c\u7d0a\u4e71\u96be\u5b89\uff0c\u8fd9\u4e24\u4e2a\u65f6\u8fb0\u4e2d\u6240\u53d1\u751f\u7684\u4e8b\uff0c\u4ef6\u4ef6\u90fd\u5728\u5fc3\u4e2d\uff0c\u7136\u800c\u5374\u4ef6\u4ef6\u4f7f\u4ed6\u601d\u7591\u4e0d\u89e3\uff0c \u3000\u3000\u4ee4\u4ed6\u6700\u611f\u5947\u602a\u7684\u662f\uff0c\u90a3\u201c\u8ffd\u98ce\u65e0\u5f71\u201d\u534e\u6e05\u6cc9\uff0c\u65e2\u662f\u4ed6\u6545\u53bb\u7236\u4eb2\u7684\u77e5\u4ea4\uff0c\u90a3\u4e48\u5374\u53c8\u4e3a\u7740\u4ec0 \u4e48\u4e00\u89c1\u90a3\u65b9\u65e7\u4e86\u7684\u4e1d\u7ef8\uff0c\u5c31\u7a81\u5730\u81ea\u520e?\u800c\u81ea\u520e\u4e4b\u524d\uff0c\u5fc3\u60c5\u53c8\u663e\u5f97\u6fc0\u52a8\u4e0d\u5df2? \u3000\u3000\u4ed6\u957f\u53f9\u4e00\u58f0\uff0c\u6697\u95ee\u81ea\u5df2\uff1a\u201c\u8fd9\u65b9\u7ef8\u5e03\u4e2d\uff0c\u53c8\u9690\u85cf\u7740\u4ec0\u4e48\u79d8\u5bc6\u5462?\u201d \u3000\u3000\u4ed6\u81ea\u7136\u65e0\u6cd5\u89e3\u7b54\uff0c\u800c\u53e6\u4e00\u4ef6\u96be\u89e3\u4e4b\u4e8b\uff0c\u5374\u53c8\u8ddf\u8e2a\u800c\u81f3\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u4e0d\u4f46\u90a3\u201c\u6469\u4e91\u795e\u624b\u201d\u5411\u51b2\u5929\u5df2\u4eab\u8a89\u6b66\u6797\uff0c\u90a3\u201c\u5b89\u4e50\u516c\u5b50\u201d\u4e91\u94ee\uff0c\u66f4\u662f\u5728\u6c5f\u6e56\u4e0a\u6781 \u6709\u58f0\u540d\u5730\u4f4d\u7684\u4eba\u7269\uff0c\u662f\u4ee5\u4ed6\u4e07\u4e07\u60f3\u4e0d\u5230\uff0c\u4f1a\u6709\u4eba\u7ade\u6562\u5f53\u8fd9\u4e24\u4eba\u4e4b\u9762\uff0c\u62a2\u53bb\u81ea\u5df2\u7684\u78a7\u5251\u3002 \u3000\u3000\u4ed6\u53c8\u626f\u843d\u4e00\u7c92\u94ae\u6263\uff0c\u80f8\u524d\u7684\u8863\u895f\u4fbf\u655e\u5f97\u66f4\u5f00\u4e86\u4e9b\uff0c\u81ea\u5df2\u88f8\u9732\u7684\u80f8\u819b\uff0c\u53ef\u4ee5\u66f4\u6df1\u6c89\u5730\u9886\u53d7 \u5230\u665a\u98ce\u7684\u51c9\u610f\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u5fc3\u80f8\u4e4b\u4e2d\uff0c\u5374\u4ecd\u50cf\u662f\u5835\u585e\u7740\u4e00\u5757\u5343\u94a9\u5de8\u77f3\uff0c\u591a\u5e74\u6765\u7684\u6c89\u90c1\uff0c\u6b64\u523b\u50cf\u5df2\u79ef\u5728\u4e00\u5904\uff0c \u4e8e\u662f\u4ed6\u7684\u601d\u6f6e\uff0c\u4f7f\u4e0d\u80fd\u81ea\u7981\u5730\u56de\u60f3\u5230\u8fc7\u53bb\u3002\u20266 \u3000\u3000\u90a3\u662f\u5f88\u4e45\u5f88\u4e45\u4ee5\u524d\u4e86\uff0c\u4ed6\u8fd8\u662f\u4e2a\u65b9\u61c2\u4e8b\u7684\u5b69\u5b50\uff0c\u5728\u4e00\u4e2a\u5176\u51c9\u5982\u6c34\u3001\u661f\u7a00\u6708\u660e\u7684\u4ef2\u79cb\u4e4b \u591c\uff0c\u4ed6\u548c\u4ed6\u6bcd\u4eb2\uff0c\u6b63\u81ea\u5fc6\u5ff5\u7740\u79bb\u5bb6\u5df2\u4e45\u7236\u4eb2\u7684\u65f6\u5019\uff0c\u4ed6\u7684\u7236\u4eb2\u679c\u7136\u50cf\u5f80\u5e74\u4e00\u6837\uff0c\u5728\u4e2d\u79cb\u4e4b \u524d\uff0c\u8d76\u56de\u5bb6\u6765\u4e86\u3002\u53ea\u662f\uff0c\u548c\u5f80\u5e74\u4e0d\u4e00\u6837\uff0c\u4ed6\u7239\u7239\u6b64\u6b21\u5e26\u56de\u6765\u7684\u5e76\u4e0d\u662f\u6b22\u4e50\u7684\u7b11\u5bb9\uff0c\u800c\u662f\u6ee1\u8eab \u7684\u4f24\u75d5\u548c\u4e0d\u4f4f\u7684\u547b\u541f\uff01 \u3000\u3000\u53bb\u65e5\u867d\u5df2\u4e45\uff0c\u8bb0\u5fc6\u5374\u72b9\u65b0\u3002\u6b64\u523b\u4ed6\u4ecd\u6e05\u695a\u5730\u8bb0\u5f97\u90a3\u5929\u665a\u4e0a\u7684\u4e00\u5207\uff0c\u4ed6\u7239\u7239\u90a3\u6ee1\u8eab\u7684\u8840 \u8ff9\uff0c\u6b64\u523b\u4e5f\u4eff\u4f5b\u53c8\u5728\u4ed6\u9762\u524d\u8df3\u52a8\u7740\uff0c\u51dd\u7ed3\u6210\u4e00\u7247\u9c9c\u7ea2\u7684\u8840\u8272\u3002\u800c\u90a3\u7edf\u7edf\u98ce\u58f0\uff0c\u5374\u6709\u5982\u90a3\u58f0\u58f0 \u7684\u547b\u541f\u3002 \u3000\u3000\u4ed6\u6c89\u91cd\u5730\u53f9\u606f\u4e00\u58f0\uff0c\u4ece\u6000\u4e2d\u53d6\u51fa\u90a3\u53ea\u7ec6\u9ebb\u7f16\u6210\u7684\u888b\u5b50\uff0c\u4e0d\u7528\u6253\u5f00\uff0c\u4ed6\u5c31\u77e5\u9053\u8fd9\u888b\u5b50\u91cc\u88c5 \u7684\u662f\u4ec0\u4e48\uff0c\u56e0\u4e3a\u8fd9\u66fe\u662f\u4ed6\u7ec8\u65e5\u628a\u73a9\u51dd\u6ce8\u7684\u2014\u2014\u4e00\u56e2\u5e72\u53d1\uff0c\u4e00\u6bb5\u4e1d\u6761\uff0c\u4e00\u7c92\u94a2\u73e0\uff0c\u4e00\u7c92\u9752\u94dc\u94ae \u6263\uff0c\u4e00\u679a\u9752\u94dc\u5236\u94b1\u548c\u90a3\u65b9\u663e\u7136\u662f\u81ea\u8863\u895f\u626f\u843d\u7684\u4e1d\u7ef8\u3002 \u3000\u3000\u8fd9\u4e9b\u90fd\u662f\u4ed6\u7239\u7239\u5782\u6b7b\u4e4b\u9645\u4ea4\u7ed9\u4ed6\u7684\uff0c\u8fd8\u6323\u624e\u7740\u544a\u8bc9\u4ed6\u516d\u4e2a\u4eba\u7684\u540d\u5b57\uff0c\u8981\u4ed6\u4ee5\u540e\u89c1\u7740\u4ed6\u4eec \u65f6\uff0c\u5c06\u8fd9\u4e9b\u4e1c\u897f\u5206\u522b\u4ea4\u7ed9\u4ed6\u4eec\u3002\u6700\u540e\uff0c\u4ed6\u8bb0\u5f97\u7236\u4eb2\u98a4\u6296\u5730\u6307\u7740\u90a3\u67c4\u5251\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8981\u597d\u597d \u7684\u2026\u2026\u201d \u3000\u3000\u53ef\u662f\u4ed6\u8fd9\u53e5\u8bdd\u8fd8\u6ca1\u6709\u8bf4\u5b8c\u7684\u65f6\u5019\uff0c\u4ed6\u7239\u7239\u5c31\u6b7b\u4e86\uff0c\u4ed6\u90a3\u65f6\u5e74\u7eaa\u867d\u5c0f\uff0c\u5374\u4e5f\u77e5\u9053\u4ed6\u7684\u7239\u7239 \u4e0d\u662f\u5e38\u4eba\u3002\u4e8e\u662f\uff0c\u4ed6\u60b2\u75db\u4ed6\u4e3a\u4ec0\u4e48\u8981\u50cf\u5e38\u4eba\u4e00\u6837\u5730\u6b7b\u53bb\uff0c\u6b7b\u7684\u65f6\u5019\uff0c\u9762\u4e0a\u751a\u81f3\u5e26\u7740\u75db\u82e6\u7684\u626d \u66f2\u3002 \u3000\u3000\u201c\u4f60\u8981\u597d\u597d\u7684\u7528\u8fd9\u67c4\u5251\u4e3a\u6211\u590d\u4ec7\u3002\u201d \u3000\u3000\u4ed6\u75db\u82e6\u5730\u4f4e\u8bed\u7740\uff0c\u5c06\u4ed6\u7239\u7239\u6ca1\u6709\u8bf4\u5b8c\u7684\u8bdd\uff0c\u63a5\u4e86\u4e0b\u53bb\uff0c\u591a\u5e74\u4ee5\u6765\uff0c\u4ed6\u65e0\u65f6\u65e0\u523b\u4e0d\u5728\u60f3\u7740 \u8fd9\u53e5\u8bdd\uff0c\u4e5f\u65e0\u65f6\u65e0\u523b\u4e0d\u4e3a\u8fd9\u53e5\u8bdd\u800c\u75db\u827a\u2019\u7740\uff0c\u56e0\u4e3a\u8fd9\u4e48\u591a\u5e74\u6765\uff0c\u4ed6\u59cb\u7ec8\u65e0\u6cd5\u77e5\u9053\u6740\u6b7b\u4ed6\u7236\u4eb2 \u7684\u4ec7\u4eba\u7a76\u7adf\u662f\u8c01\u3002 \u3000\u3000\u90a3\u662f\u4e00\u6bb5\u5145\u6ee1\u4e86\u75db\u82e6\uff0c\u75db\u82e6\u5f97\u51e0\u4e4e\u7edd\u671b\u7684\u65e5\u5b50\u3002\u4ed6\u548c\u4ed6\u6bcd\u4eb2\uff0c\u4ece\u672a\u6d89\u8db3\u8fc7\u6b66\u6797\uff0c\u6839\u672c\u4e0d \u8ba4\u5f97\u4efb\u4f55\u4e00\u4e2a\u6b66\u6797\u4e2d\u4eba\uff0c\u6b66\u6797\u4e2d\u4e5f\u4ece\u6765\u6ca1\u6709\u4e00\u4e2a\u4eba\u77e5\u9053\u201c\u9739\u96f7\u5251\u201d\u5c55\u4e91\u5929\u8fd8\u6709\u59bb\u5b50\uff0c\u4ed6\u4eec\u867d \u7136\u56e0\u6b64\u800c\u8eb2\u8fc7\u4e86\u4ec7\u5bb6\u7684\u8ffd\u6355\uff0c\u5374\u4e5f\u56e0\u6b64\u5f97\u4e0d\u5230\u4efb\u4f55\u63f4\u52a9\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u4eec\u8f97\u8f6c\u6d41\u6d6a\u7740\uff0c\u671f\u5180\u80fd\u5b66\u5f97\u4e00\u4efd\u60ca\u4eba\u7684\u7edd\u827a\uff0c\u4f46\u662f\u4ed6\u4eec\u5931\u671b\u4e86\uff0c\u76f4\u5230\u4ed6\u7684\u6bcd\u4eb2\u4e5f \u56fa\u75db\u82e6\u548c\u6298\u78e8\u800c\u6b7b\u53bb\uff0c\u5c55\u767d\u5b66\u5f97\u7684\uff0c\u4ecd\u7136\u662f\u6b66\u6797\u4e2d\u5e38\u89c1\u7684\u529f\u592b\u3002\u4ed6\u867d\u7136\u6709\u8fc7\u4eba\u7684\u5929\u8d44\u548c\u8fc7\u4eba \u7684\u523b\u82e6\uff0c\u4f46\u90a3\u4e5f\u53ea\u662f\u4f7f\u4ed6\u7684\u6b66\u529f\u7565\u6bd4\u5e38\u4eba\u597d\u4e9b\uff0c\u8ddd\u79bb\u6b66\u6797\u9ad8\u624b\u7684\u529f\u592b\uff0c\u5374\u4ecd\u7136\u662f\u65e0\u6cd5\u4f01\u53ca\u7684 \u9065\u8fdc\u3002 \u3000\u3000\u4e8e\u662f\uff0c\u6b64\u523b\u4ed6\u4f4d\u7acb\u4ea4\u590f\u591c\u7684\u51c9\u98ce\u91cc\uff0c\u60ed\u5080\u3001\u81ea\u8d23\u3001\u75db\u82e6\u5730\u6298\u78e8\u7740\u81ea\u5df1\u3002 \u3000\u3000\u201c\u5373\u4f7f\u6211\u77e5\u9053\u4e86\u7239\u7239\u7684\u4ec7\u4eba\uff0c\u53c8\u80fd\u600e\u6837\u5462?\u6211\u751a\u81f3\u8fde\u4ed6\u9057\u7559\u7ed9\u6211\u7684\u5251\u90fd\u4fdd\u5b58\u4e0d\u4e86\uff0c\u6211\u53c8 \u6709\u4ec0\u4e48\u529b\u91cf\u4e3a\u4ed6\u590d\u4ec7\u3002\u201d \u3000\u3000\u4e3e\u76ee\u56db\u671b\uff0c\u8ddf\u524d\u4ecd\u7136\u770b\u4e0d\u5230\u674e\u6761\u4eba\u5f71\uff0c\u552f\u6709\u7785\u7785\u866b\u9e23\u548c\u98d8\u7ef8\u98ce\u54cd\uff0c\u5728\u4ed6\u8033\u7554\u6df7\u5408\u6210\u4e00\u79cd \u54c0\u4f24\u51c4\u60cb\u7684\u97f3\u4e50\u3002 \u3000\u3000\u4ed6\u957f\u53f9\u4e00\u58f0\uff0c\u4e3e\u6b65\u5411\u524d\u8d70\u53bb\uff0c\u53ea\u89c9\u81ea\u5df1\u524d\u9014\uff0c\u4ea6\u6709\u5982\u773c\u524d\u7684\u90ca\u91ce\u822c\u9ed1\u6697\uff0c\u6b64\u523b\u4ed6\u51e0\u4e4e\u5df2 \u6d51\u5fd8\u4e00\u5207\uff0c\u5fc3\u4e2d\u6df7\u6df7\u5a49\u6c60\u7684\uff0c\u4f46\u89c9\u4e07\u5ff5\u4ff1\u7070\uff0c\u4ec0\u4e48\u4e8b\u90fd\u4e0d\u653e\u5728\u5fc3\u4e0a\u4e86\u3002 \u3000\u3000\u4ed6\u57cb\u846c\u4e86\u81ea\u5df1\u7684\u6bcd\u4eb2\u4e4b\u76fe\uff0c\u5c31\u5b64\u8eab\u51fa\u6765\u95ef\u8361\uff0c\u4f46\u662f\u8fd9\u5bf9\u6c5f\u6e56\u4e00\u65e0\u6240\u77e5\u7684\u5c11\u5e74\uff0c\u80fd\u591f\u751f\u5b58 \u4e0b\u53bb\uff0c\u5df2\u6781\u4e0d\u6613\uff0c\u522b\u7684\u4e8b\uff0c\u4ed6\u53c8\u6709\u4ec0\u4e48\u80fd\u529b\u5b8c\u6210\u5462?\u4ed6\u51ed\u7740\u4e2a\u4eba\u7684\u52c7\u6c14\uff0c\u6323\u624e\u7740\uff0c\u7ec8\u4e8e\u8ba9\u4ed6 \u5728\u90a3\u9a70\u8a89\u6b66\u6797\u7684\u9556\u5c40\u91cc\u627e\u5230\u4e00\u4e2a\u804c\u52a1\uff0c\u867d\u662f\u5de7\u5408\uff0c\u5374\u4e5f\u662f\u56f0\u96be\u7684!\u800c\u6b64\u523b\u4ed6\u5374\u8fde\u8fd9\u4e9b\u4e5f\u5168\u90fd \u5fd8\u4e86\uff0c\u4ed6\u5fd8\u4e86\u81ea\u5df2\u80a9\u4e0a\u4ecd\u7136\u62c5\u8d1f\u7740\u62bc\u9556\u7684\u8d23\u4efb\uff0c\u53ea\u662f\u832b\u65e0\u76ee\u7684\u5730\u524d\u884c\u7740\uff0c\u4f3c\u4e4e\u5728\u5bfb\u627e\u4e00\u4e9b\u4ed6 \u5931\u843d\u4e86\u7684\u4e1c\u897f\u3002 \u3000\u3000\u6797\u6728\u4f9d\u7136\uff0c\u661f\u5149\u4ea6\u4f9d\u7136\uff0c\u6c89\u5bc2\u7684\u590f\u591c\u91cc\uff0c\u5927\u5730\u4f3c\u4e4e\u6ca1\u6709\u4e00\u4e1d\u53d8\u5316\uff0c\u7136\u800c\u751f\u5b58\u5728\u5927\u5730\u7684\u4eba \u4eec\u7684\u53d8\u5316\uff0c\u5374\u53c8\u6709\u591a\u4e48\u5927\u5462? \u3000\u3000\u5c55\u767d\u884c\u884c\u6b62\u6b62\uff0c\u5fc3\u4e2d\u6697\u6697\u5e0c\u671b\u90a3\u201c\u5b89\u4e50\u516c\u5b50\u201d\u80fd\u4e3a\u81ea\u5df1\u593a\u56de\u5251\u6765\uff0c\u4f46\u4ed6\u82e5\u662f\u771f\u7684\u593a\u56de\u5251 \u6765\uff0c\u90a3\u5bf9\u5c55\u767d\u6765\u8bf4\uff0c\u53c8\u8be5\u662f\u4e00\u79cd\u591a\u5927\u7684\u60b2\u54c0\u5440\uff01\u81ea\u5c0a\u7684\u4eba\uff0c\u6709\u8c01\u613f\u610f\u4ece\u522b\u4eba\u624b\u4e0a\u5f97\u56de\u81ea\u5df1\u4e0d \u80fd\u4fdd\u7559\u7684\u4e1c\u897f\u5462? \u3000\u3000\u201c\u77e5\u4e86\u201d\u4e00\u58f0\uff0c\u4e00\u53ea\u91d1\u8749\u4ece\u4ed6\u8eab\u4fa7\u98de\u8fc7\uff0c\u6ca1\u5165\u4ed6\u811a\u4e0b\u7684\u8352\u8349\u91cc\u3002\u50a8\u832b\u7136\u56db\u987e\u4e00\u773c\uff0c\u76ee\u5149 \u8f6c\u52a8\u5904\uff0c\u5fc3\u5934\u4e0d\u7981\u62a8\u5730\u4e00\u8df3\uff0c\u4e00\u9635\u96be\u8a00\u7684\u5bd2\u610f\uff0c\u4ece\u811a\u5e95\u76f4\u900f\u800c\u4e0a\u3002 \u3000\u3000\u7fa4\u661f\u6ee1\u5929\uff0c\u6708\u5149\u5c06\u4ed6\u7684\u8eab\u5f71\u957f\u957f\u5730\u6620\u5728\u957f\u6ee1\u4e86\u8352\u8349\u7684\u6ce5\u5730\u4e0a\uff0c\u4f46\u4f7f\u4ed6\u60ca\u60b8\u7684\u5374\u662f\uff0c\u6b64\u671d \u5728\u4ed6\u7684\u5f71\u5b50\u540e\u9762\uff0c\u7adf\u6620\u7740\u53e6\u5916\u4e00\u4e2a\u5f71\u5b50\u2014\u2014\u4e00\u4e2a\u4eba\u7684\u5f71\u5b50\u3002 \u3000\u3000\u4ed6\u5927\u60ca\u4e4b\u4e0b\uff0c\u8fd8\u672a\u6765\u5f97\u53ca\u5176\u8eab\uff0c\u5374\u542c\u8eab\u540e\u5df2\u4f20\u6765\u4e00\u58f0\u5389\u53f1\uff0c\u9053\uff1a\u201c\u4f60\u6cc4\u6f0f\u8001\u592b\u7684\u79d8\u5bc6\uff0c \u8001\u592b\u6253\u6b7b\u4f60!\u201d \u3000\u3000\u4ed6\u53c8\u662f\u4e00\u9a87\uff01\u5fc3\u4e2d\u7535\u4e5f\u4f3c\u5730\u95ea\u8fc7\u4e00\u4e2a\u5ff5\u5934\uff1a\u201c\u6211\u4f55\u66fe\u6cc4\u6f0f\u8fc7\u4ec0\u4e48\u4eba\u7684\u79d8\u5bc6\uff0c\u4ed6\u4e0d\u8981\u662f\u8ba4 \u9519\u5165\u4e86\u3002\u201d\u8eab\u968f\u5ff5\u52a8\uff0c\u500f\u7136\u8f6c\u4e86\u8fc7\u53bb\uff0c\u5374\u89c1\u81ea\u5df1\u8eab\u540e\uff0c\u4e0d\u77e5\u4f55\u65f6\uff0c\u7ade\u7ad9\u7740\u4e00\u4e2a\u77ee\u80d6\u7684\u8001\u4eba\u3001 \u3000\u3000\u6708\u5149\u4e4b\u4e0b\uff0c\u53ea\u89c1\u8fd9\u8001\u8005\u6ee1\u9762\u6012\u5bb9\uff0c\u773c\u775b\u6076\u72e0\u72e0\u5730\u77a7\u7740\u5730\u4e0a\u7684\u5f71\u5b50\uff0c\u7adf\u53c8\u5389\u58f0\u9053\uff0f\u4f60\u6cc4\u6f0f \u4e86\u8001\u592b\u7684\u79d8\u5bc6\uff0c\u8001\u592b\u6253\u6b7b\u4f60\u3002\u201d\u626c\u624b\u4e00\u638c\uff0c\u671d\u5730\u4e0a\u6620\u7740\u7684\u5f71\u5b50\u6253\u53bb\uff0c\u53ea\u542c\u201c\u547c\u201d\u4e00\u58f0\uff0c\u5730\u4e0a \u8352\u8349\u4e71\u98de\uff0c\u6ce5\u6c99\u6e85\u8d77\uff0c\u7ade\u88ab\u8fd9\u8001\u8005\u51cc\u5389\u7684\u638c\u98ce\u626b\u4e86\u4e2a\u571f\u5751\uff0c\u8fd9\u8001\u8005\u610f\u72b9\u672a\u5c3d\uff0c\u8eab\u5f62\u672b\u52a8\uff0c\u626c \u624b\u53c8\u662f\u6570\u638c\uff0c\u638c\u98ce\u864e\u864e\uff0c\u7adf\u662f\u4ed6\u524d\u6240\u672b\u89c1 \u3000\u3000\u4ed6\u60ca\u9a87\u4e4b\u4e0b\uff0c\u4e0d\u7981\u4e3a\u4e4b\u5446\u5446\u6115\u4f4f\u4e86\uff0c\u98de\u626c\u8d77\u7684\u65ad\u8349\u6ce5\u6c99\uff0c\u6cbe\u4e86\u4ed6\u4e00\u8eab\uff0c\u4ed6\u5374\u6325\u5982\u672b\u89c9\uff0c \u7247\u523b\u4e4b\u95f4\uff0c\u53ea\u89c1\u90a3\u7247\u672c\u6765\u6620\u7740\u8fd9\u8001\u8005\u4eba\u5f71\u7684\u8352\u8349\u5730\u4e0a\uff0c\u6ce5\u6c99\u9677\u843d\uff0c\u90a3\u6761\u5f71\u5b50\u679c\u771f\u4e0d\u6210\u4eba\u5f62 \u4e86\u3002 \u3000\u3000\u5c55\u767d\u5fc3\u4e2d\u4e00\u5bd2\uff0c\u8f6c\u76ee\u671b\u53bb\uff0c\u5374\u89c1\u8fd9\u8001\u8005\u76ee\u5149\u4ea6\u6b63\u8f6c\u5411\u81ea\u5df2\uff0c\u624b\u6307\u7740\u5730\u4e0a\u7684\u571f\u5751\uff0c\u7ade\u7a81\u5730 \u54c8\u54c8\u4e00\u7b11\u9053\uff1a\u201c\u8fd9\u79cd\u574f\u4e1c\u897f\uff0c\u975e\u6253\u6b7b\u4ed6\u4e0d\u53ef\uff0c\u59d3\u5c55\u7684\u5a03\u5a03\uff0c\u4f60\u8bf4\u5bf9\u4e0d\u5bf9?\u201d \u3000\u3000\u5c55\u767d\u5fc3\u4e2d\u53c8\u662f\u62a8\u5730\u4e00\u8df3\u3002 \u3000\u3000\u201c\u4ed6\u600e\u5730\u77e5\u9053\u6211\u59d3\u4ec0\u4e48?\u201d\u76ee\u5149\u8f6c\u5904\uff0c\u7a81\u5730\u60f3\u8d77\u773c\u524d\u8001\u8005\uff0c\u7adf\u662f\u65b9\u624d\u548c\u90a3\u201c\u8ffd\u98ce\u65e0\u5f71\u201d \u534e\u6e05\u6cc9\u3001\u201c\u6469\u4e91\u795e\u624b\u201d\u5411\u51b2\u5929\u540c\u65f6\u7b56\u9a6c\u5165\u6797\u7684\uff0c\u53ea\u662f\u81ea\u5df1\u65b9\u624d\u6ca1\u6709\u6ce8\u610f\u6b64\u4eba\u7684\u884c\u52a8\uff0c\u6b64\u4eba\u4e5f \u4ece\u672a\u6709\u6240\u884c\u52a8\uff0c\u5374\u60f3\u4e0d\u5230\u4ed6\u6b64\u523b\u7adf\u4f1a\u7a81\u7136\u5728\u81ea\u5df2\u9762\u524d\u51fa\u73b0\u3002 \u7b2c\u56db\u7ae0\u3000\u795e\u79d8\u7f8e\u5987>> \u53e4\u9f99\u300a\u5251\u5ba2\u884c\u300b \u7b2c\u56db\u7ae0\u3000\u795e\u79d8\u7f8e\u5987 \u3000\u3000\u5c11\u5e74\u5c55\u767d\u5fc3\u601d\u8f6c\u5904\uff0c\u5374\u89c1\u8fd9\u8001\u8005\u4f38\u51fa\u4e00\u53ea\u80a5\u80d6\u800c\u77ed\u5c0f\u7684\u624b\u638c\uff0c\u9053\uff1a\u201c\u5c55\u5a03\u5a03\uff0c\u4f60\u628a\u624b\u4e0a \u7684\u4e1c\u897f\u4ea4\u7ed9\u8001\u592b\u770b\u3002\u201d \u3000\u3000\u8bf4\u7740\u53c8\u54c8\u54c8\u4e00\u7b11\uff1a\u201c\u8001\u592b\u8981\u770b\u770b\u8fd9\u91cc\u9762\u88c5\u7684\u7a76\u7adf\u662f\u4e9b\u4ec0\u4e48\u4e1c\u897f\uff0c\u600e\u7684\u62ff\u51fa\u4e00\u6837\uff0c\u5c31\u9001\u4e86 \u534e\u8001\u7334\u513f\u7684\u7ec8?\u8981\u662f\u8001\u592b\u4e5f\u6709\u4e2a\u8fd9\u6837\u7684\u888b\u5b50\u591a\u597d!\u201d \u3000\u3000\u5c55\u767d\u4e0d\u7981\u540e\u9000\u4e00\u6b65\uff0c\u8eac\u8eab\u9053\uff1a\u201c\u6b64\u4e43\u5148\u7236\u9057\u7269\uff0c\u8001\u524d\u8f88\u8bf7\u6055\u665a\u8f88\u4e0d\u80fd\u2014\u2014\u201d \u3000\u3000\u8bdd\u72b9\u672a\u4e86\uff0c\u90a3\u8001\u8005\u7a81\u5730\u51b7\u54fc\u4e00\u58f0\uff0c\u9762\u4e0a\u7b11\u5bb9\u5c3d\u655b\uff0c\u5389\u53f1\u9053\u201c\u4f60\u662f\u7ed9\u8fd8\u662f\u4e0d\u7ed9?\u201d \u3000\u3000\u76ee\u5149\u4e2d\u6076\u6bd2\u4e4b\u610f\u7adf\u53c8\u5927\u73b0\uff0c\u5c31\u751f\u50cf\u662f\u65b9\u624d\u77aa\u7740\u90a3\u6761\u5f71\u5b50\u65f6\u7684\u795e\u6001\u4e00\u822c\u3002 \u3000\u3000\u5c55\u767d\u5fc3\u4e2d\u4e00\u5bd2\uff0c\u60f3\u8d77\u4ed6\u65b9\u624d\u7684\u638c\u98ce\uff0c\u4e0d\u7981\u957f\u53f9\u4e00\u58f0\uff0c\u5fc3\u4e2d\u6697\u9a82\uff1a\u201c\u600e\u7684\u6211\u4eca\u65e5\u9047\u7740\u7684\u5c3d \u662f\u8fd9\u4e9b\u4e0d\u53ef\u7406\u89e3\u4e4b\u4e8b\uff0c\u4e0d\u53ef\u7406\u55bb\u4e4b\u4eba?\u201d\u5fc3\u91cc\u4e00\u53d1\u95f7\uff0c\u8d8a\u53d1\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u5374\u89c1\u8fd9\u8001\u8005\u9762\u4e0a\u795e\u8272\u66f4\u52a0\u4e0d\u8010\uff0c\u7f13\u7f13\u5730\u79fb\u52a8\u811a\u6b65\uff0c\u5411\u4ed6\u8d70\u6765\u3002\u5c55\u767d\u4ece\u672a\u9003\u907f\u8fc7\u4efb\u4f55\u4e8b\uff0c \u4f46\u6b64\u523b\u4ed4\u7ec6\u4e00\u60f3\uff0c\u81ea\u5df1\u4f55\u5fc5\u548c\u8fd9\u79cd\u4e0d\u53ef\u7406\u55bb\u4e4b\u4eba\u5939\u7f20?\u811a\u6b65\u5fae\u9519\uff0c\u53e3\u4e2d\u559d\u9053\uff1a\u201c\u665a\u8f88\u6709\u4e8b\uff0c \u6055\u4e0d\u5949\u966a\u4e86\uff01\u201d\u5237\u5730\u5411\u6797\u4e2d\u63a0\u53bb\u3002 \u3000\u3000\u54ea\u77e5\u8033\u7554\u95fb\u51b7\u51b7\u4e00\u54fc\uff0c\u773c\u524d\u4e00\u82b1\uff0c\u90a3\u8001\u8005\u7adf\u53c8\u6321\u5728\u81ea\u5df1\u9762\u524d\uff0c\u5389\u58f0\u559d\u9053\uff1a\u201c\u5a03\u5a03\uff0c\u4f60\u60f3 \u8dd1?\u4f60\u4e0d\u95ee\u95ee\uff0c\u6709\u8c01\u9003\u5f97\u8fc7\u6211\u8d39\u4e00\u7ae5\u7684\uff01\u201d \u3000\u3000\u5c55\u767d\u867d\u7136\u521d\u4eba\u6c5f\u6e56\uff0c\u4f46\u201c\u8d39\u4e00\u7ae5\u201d\u4e09\u5b57\u4e00\u5165\u4ed6\u8033\uff0c\u5374\u4e0d\u7981\u8fde\u901a\u6253\u4e86\u51e0\u4e2a\u5bd2\u6218\uff0c\u6697\u53f9\u81ea\u5df1 \u5012\u9709\uff0c\u4eca\u65e5\u5c45\u7136\u9047\u7740\u6b64\u4eba\u3002 \u3000\u3000\u539f\u6765\u8fd9\u8d39\u4e00\u7ae5\u6b66\u529f\u7edd\u9ad8\uff0c\u884c\u4e8b\u53c8\u6781\u96be\u6d4b\uff0c\u7eb5\u7136\u662f\u6b66\u6797\u4e2d\u4e00\u6d41\u9ad8\u624b\uff0c\u4e5f\u6ca1\u6709\u4e0d\u722c\u9047\u7740\u201c\u8fa3 \u624b\u7ae5\u5fc3\u201d\u8d39\u4e00\u7ae5\u7684\u3002 \u3000\u3000\u5c55\u767d\u6b64\u523b\u76ee\u5149\u4e00\u8f6c\uff0c\u770b\u5230\u8352\u8349\u5730\u4e0a\uff0c\u53c8\u6620\u51fa\u4e86\u8fd9\u8d39\u4e00\u7ae5\u7684\u5f71\u5b50\uff0c\u5fc3\u5ff5\u7a81\u5730\u4e00\u52a8\uff0c\u6307\u7740\u5730 \u4e0a\u968f\u5f71\u5b50\u9053\uff1a\u201c\u8d39\u8001\u524d\u8f88\uff0c\u4f60\u770b\u8fd9\u8be5\u6b7b\u7684\u5bb6\u4f19\u53c8\u6765\u4e86\u3002\u201d\u8d39\u4e00\u7ae5\u76ee\u5149\u4e00\u51db\uff0c\u671b\u7740\u5730\u4e0a\u7684\u5f71 \u5b50\uff0c\u7f13\u7f13\u626c\u8d77\u624b\u638c\u6765\uff0c\u5c55\u767d\u5fc3\u4e2d\u81ea\u6697\u559c\uff0c\u54ea\u77e5\u8fd9\u201c\u8fa3\u624b\u7ae5\u5fc3\u201d\u7a81\u5730\u6536\u56de\u624b\u638c\uff0c\u54b1\u54c8\u7b11\u9053\uff1a \u201c\u6765\u4e86\u5c31\u6765\u4e86\uff0c\u8001\u592b\u624d\u4e0d\u4e0a\u50cf\u8fd9\u4e2a\u5f53\uff1b\u5feb\u628a\u624b\u4e0a\u7684\u4e1c\u897f\u62ff\u6765\uff01\u201d\u8bed\u58f0\u65b9\u843d\uff0c\u7a81\u5730\u51fa\u624b\uff0c\u7535\u4e5f \u4f3c\u5730\u5f80\u5c55\u767d\u624b\u4e0a\u7684\u9ebb\u888b\u5b50\u652b\u53bb\u3002 \u3000\u3000\u5c55\u767d\u5927\u559d\u4e00\u58f0\uff0c\u8eab\u5f62\u5fae\u957f\uff0c\u5411\u540e\u5012\u7a9c\u3002 \u3000\u3000\u8d39\u4e00\u7ae5\u54c8\u54c8\u4e00\u7b11\uff0c\u624b\u8155\u5fbd\u6296\uff0c\u4f38\u51fa\u5c0f\u6307\uff0c\u659c\u659c\u4e00\u5212\uff0c\u5c55\u767d\u53ea\u89c9\u5de6\u8155\u4e00\u9ebb\uff0c\u53f3\u624b\u7684\u9ebb\u888b\u4fbf \u88ab\u4eba\u5bb6\u652b\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u4ed6\u5fae\u5fae\u5b9a\u795e\uff0c\u5374\u89c1\u90a3\u201c\u8fa3\u624b\u7ae5\u5fc3\u201d\u8eab\u5f62\u5df2\u5728\u4e24\u4e08\u5f00\u5916\uff0c\u6b63\u6447\u6447\u6643\u6643\u5730\u8d70\u5165\u6811\u6797\uff1b\u5fc3\u4e2d\u7f9e \u607c\u4ea4\u96c6\uff0c\u518d\u4e5f\u987e\u4e0d\u5f97\u522b\u7684\uff0c\u5019\u7136\u4e24\u4e2a\u8d77\u843d\uff0c\u4fbf\u5df2\u8ffd\u4eba\u6797\u4e2d\uff0c\u53ea\u89c1\u90a3\u8d39\u4e00\u7ae5\u7684\u8eab\u5f62\uff0c\u6b63\u5728\u6811\u5e72 \u4e4b\u95f4\u7f13\u7f13\u800c\u884c\uff0c\u4e00\u624b\u62ff\u7740\u53ea\u7ec6\u9ebb\u7f16\u6210\u7684\u888b\u5b50\uff0c\u53e6\u4e00\u53ea\u5374\u5728\u638f\u90a3\u888b\u5b50\u91cc\u88c5\u7740\u7684\u4e1c\u897f\u3002 \u3000\u3000\u5c55\u767d\u534a\u65e5\u4e4b\u95f4\uff0c\u8fde\u906d\u6253\u51fb\uff0c\u7406\u667a\u51e0\u4e4e\u5b8c\u5168\u62bf\u6ca1\uff0c\u7acb\u5373\u50cf\u53ea\u75af\u4e86\u7684\u731b\u864e\u822c\u671d\u90a3\u4eff\u4f5b\u5728\u6797\u4e2d \u65bd\u7136\u8e31\u6b65\u7684\u201c\u8fa3\u624b\u7ae5\u5fc3\u201d\u6251\u4e86\u8fc7 \u3000\u3000\u4f46\u8fd9\u6811\u6797\u679d\u5e72\u9887\u5bc6\uff0c\u90a3\u201c\u8fa3\u624b\u7ae5\u5fc3\u201d\u8d39\u4e00\u7ae5\u770b\u6765\u4f3c\u5728\u8e31\u6b65\uff0c\u5176\u5b9e\u8eab\u6cd5\u5374\u8fc5\u5feb\u65e0\u6bd4\uff0c\u7b49\u5230 \u5c55\u767d\u7ed5\u8fc7\u5341\u6570\u680b\u6811\u5e72\uff0c\u53d1\u72c2\u4f3c\u5730\u6251\u8fd1\u65f6\uff0c\u8fd9\u8d39\u4e00\u76f4\u5374\u53c8\u65e9\u5df2\u8d70\u5f97\u8fdc\u8fdc\u7684\u4e86\uff0c\u2014\u624b\u4ece\u5e03\u888b\u91cc\u6293 \u51fa\u4e00\u56e2\u4e71\u53d1\uff0c\u5f80\u5730\u4e0a\u72e0\u72e0\u4e22\u53bb\uff0c\u4e00\u9762\u53e3\u4e2d\u8fde\u8fde\u9a82\u9053\uff0f\u539f\u6765\u8fd9\u5c0f\u4e8e\u662f\u4e2a\u5446\u5b50\uff0c\u539f\u6765\u8fd9\u4e2a\u5c0f\u5b50\u662f \u4e2a\u5446\u5b50!\u6211\u5f53\u4ed6\u8fd9\u888b\u5b50\u91cc\u653e\u7740\u4ec0\u4e48\u597d\u4e1c\u897f\uff0c\u54ea\u77e5\u5374\u662f\u4e9b\u81ed\u5783\u573e\u3002\u201d\u624b\u81c2\u8fde\u6325\uff0c\u5c06\u888b\u5b50\u91cc\u7684\u5236 \u94b1\u3001\u94a2\u73e0\u3001\u94dc\u6263\u3001\u4e1d\u6761\uff0c\u7eb7\u7eb7\u4e22\u5230\u5730\u4e0a\uff0c\u7a81\u53c8\u7eb5\u8eab\u8dc3\u8d77\uff0c\u5de6\u624b\u6293\u4f4f\u4e00\u6839\u67d4\u5f31\u7684\u679d\u4e2b\uff0c\u53f3\u624b\u5c06 \u888b\u5b50\u6302\u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u5c55\u767d\u62ac\u5934\u671b\u53bb\uff0c\u53ea\u89c1\u8fd9\u679d\u4e2b\u79bb\u5730\u7ade\u6709\u4e09\u4e08\uff0c\u4f46\u8d39\u4e00\u7ae5\u8eab\u8eaf\u540a\u5728\u4e0a\u9762\uff0c\u5374\u50cf\u662f\u56db\u4e24\u68c9\u82b1\u4f3c \u7684\uff0c\u968f\u7740\u8fd9\u67d4\u5f31\u7684\u679d\u4e2b\u4e0a\u4e0b\u5f39\u52a8\u3002 \u3000\u3000\u4ed6\u5927\u559d\u4e00\u58f0\uff0c\u4ea6\u81ea\u7eb5\u8eab\u6251\u4e86\u4e0a\u53bb\uff0c\u54ea\u77e5\u8eab\u5f62\u63a0\u8d77\u4e0d\u53ca\u4e24\u4e08\uff0c\u5c31\u53c8\u201c\u6251\u201d\u5730\u843d\u4e86\u4e0b\u6765\uff0c\u8d39 \u4e00\u7ae5\u54c8\u54c8\u5927\u7b11\uff0c\u4e00\u7ffb\u8eab\uff0c\u6a2a\u8de8\u5230\u679d\u4e2b\u4e4b\u4e0a\uff0c\u671b\u7740\u5730\u4e0a\u7684\u5c55\u767d\uff0c\u7b11\u58f0\u5f97\u610f\u5df2\u6781\u3001 \u3000\u3000\u5c55\u81ea\u5fc3\u80f8\u4e4b\u4e2d\uff0c\u6012\u706b\u5927\u5f20\uff0c\u867d\u7136\u660e\u77e5\u8fd9\u602a\u4eba\u6b66\u529f\u8fdc\u5728\u81ea\u5df2\u4e4b\u4e0a\uff0c\u4f46\u5374\u65e9\u5c06\u751f\u6b7b\u7f6e\u4e4b\u5ea6 \u5916\uff0c\u7ee7\u7eed\u4f7f\u8db3\u5168\u529b\u731b\u6251\u4e0a\u53bb\u3002 \u3000\u3000\u8fd9\u6b21\u4ed6\u7adf\u8dc3\u81f3\u4e24\u4e08\u5f00\u5916\uff0c\u8ddf\u89c1\u90a3\u679d\u4e2b\u5df2\u79bb\u5934\u9876\u4e0d\u8fdc\uff0c\u4f38\u624b\u4e00\u6284\uff0c\u54ea\u77e5\u62c7\u6307\u65b9\u89e6\u7740\u679d\u5e72\uff0c \u5c31\u518d\u4e5f\u65e0\u6cd5\u5411\u4e0a\u8dc3\u9ad8\u4e00\u5bf8\uff0c\u53ea\u5f97\u53c8\u843d\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u8fd9\u201c\u8fa3\u624b\u7ae5\u5fc3\u201d\u8d39\u4e00\u7ae5\u62cd\u638c\u5927\u7b11\uff0c\u7a81\u5730\u50cf\u662f\u5f97\u610f\u8fc7\u5ea6\uff0c\u8eab\u5b50\u4e00\u6b6a\uff0c\u8dcc\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u5c55\u767d\u6697\u54fc\u4e00\u58f0\u2606\u51c6\u5907\u53ea\u8981\u4ed6\u8eab\u5f62\u4e00\u843d\u5730\uff0c\u4fbf\u72e0\u72e0\u7ed9\u4ed6\u4e00\u638c\u3002 \u3000\u3000\u54ea\u77e5\u8d39\u4e00\u7ae5\u8dc3\u4e0a\u4e00\u534a\uff0c\u51cc\u7a7a\u4e00\u4e2a\u201c\u6b7b\u4eba\u63d0\u201d\uff0c\u8eab\u8eaf\u7adf\u53c8\u7b14\u76f4\u5730\u7ffb\u4e86\u4e0a\u53bb\uff0c\u56db\u5e73\u516b\u7a33\u5730\u5750 \u5230\u6811\u679d\u4e0a\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5c0f\u4f19\u5b50\uff0c\u4f60\u8981\u662f\u80fd\u4e0a\u5f97\u4e86\u8fd9\u91cc\uff0c\u6211\u5c31\u628a\u8fd9\u7834\u888b\u5b50\u8fd8\u4f60\u3002\u201d \u3000\u3000\u5c55\u767d\u89c1\u4ed6\u51cc\u7a7a\u540a\u7740\u7684\u4e24\u53ea\u811a\uff0c\u4e0d\u4f4f\u5730\u6765\u56de\u6643\u52a8\uff0c\u800c\u90a3\u6839\u67d4\u5f31\u7684\u679d\u4e2b\uff0c\u4ecd\u53ea\u88ab\u538b\u4e0b\u4e00\u70b9\uff0c \u5fc3\u77e5\u8fd9\u602a\u4eba\u5458\u4f3c\u75af\u8e66\uff0c\u6b66\u529f\u5374\u9ad8\u4e0d\u53ef\u6d4b\u3002\u957f\u53f9\u4e00\u58f0\uff0c\u65b9\u5f85\u56de\u8eab\u8d70\u51fa\uff0c\u4f46\u8f6c\u5ff5\u4e00\u60f3\uff0c\u6697\u9a82\u81ea \u5df1\uff1a\u201c\u5c55\u767d\u5440\u5c55\u767d\uff0c\u4f60\u8fd9\u8fd8\u7b97\u5f97\u4ec0\u4e48\u7537\u5b50\u6c49\uff0c\u9047\u7740\u4e00\u70b9\u56f0\u96be\u4fbf\u754f\u9996\u754f\u5c3e\u8d77\u6765\uff0c\u5c06\u6765\u8fd8\u80fd\u6210\u4ec0 \u4e48\u5927\u4e8b?\u4e0d\u5982\u6b7b\u4e86\u7b97\u4e86\uff01\u201d \u3000\u3000\u4e00\u5ff5\u81f3\u6b64\uff0c\u4ed6\u4f46\u89c9\u5fc3\u4e2d\u70ed\u8840\u5954\u6cb8\u4e0d\u5df1\uff0c\u7a81\u5730\u4e00\u4e2a\u7bad\u6b65\u63a0\u5230\u6811\u4e0b\uff0c\u624b\u8db3\u5e76\u7528\u5730\u671d\u6811\u5e72\u722c\u4e86 \u4e0a\u53bb\uff0c\u8033\u4e2d\u542c\u5230\u90a3\u602a\u4eba\u7684\u7b11\u58f0\u867d\u4ecd\u672b\u7edd\uff0c\u4f46\u5374\u4f3c\u4e4e\u5df2\u6e10\u6e10\u8fdc\u53bb\uff0c\u62ac\u5934\u4e00\u671b\uff0c\u679d\u4e2b\u4e0a\u679c\u7136\u5df2\u7a7a \u7a7a\u7684\u518d\u65e0\u4eba\u5f71\uff0c\u90a3\u602a\u4eba\u5df2\u4e0d\u77e5\u54ea\u91cc\u53bb\u4e86\u3002 \u3000\u3000\u8f6c\u773c\u56db\u987e\uff0c\u98ce\u5439\u6797\u6728\uff0c\u72e1\u53f6\u7b5b\u52a8\uff0c\u90a3\u79cd\u6df7\u5408\u7740\u8ba5\u5632\u548c\u5f97\u610f\u7684\u7b11\u58f0\uff0c\u4e5f\u5df2\u6d88\u5931\u5728\u7edf\u7edf\u98ce\u58f0 \u91cc\u3002\u5c55\u81ea\u5f81\u4e86\u4e00\u6014\uff0c\u89c1\u90a3\u53ea\u888b\u5b50\u4ecd\u5728\u6811\u68a2\u968f\u98ce\u98d8\u52a8\uff0c\u4fbf\u518d\u722c\u4e0a\u51e0\u5c3a\uff0c\u4f38\u51fa\u53f3\u624b\u53bb\u6293\u90a3\u53ea\u888b \u5b50\uff0c\u4f46\u6821\u957f\u4e94\u5c3a\uff0c\u624b\u957f\u5374\u4e0d\u53ca\u4e09\u5c3a\uff0c\u4ed6\u7a7a\u81ea\u7740\u6025\uff0c\u65e0\u8bba\u5982\u4f55\u4e5f\u65e0\u6cd5\u5c06\u888b\u5b50\u6478\u5728\u624b\u91cc\uff0c \u3000\u3000\u888b\u5b50\u4ecd\u5728\u6447\u52a8\u7740\uff0c\u4eff\u4f5b\u90a3\u602a\u4eba\u7684\u58f0\u97f3\uff0c\u8ba5\u5632\u800c\u53c8\u5f97\u610f\u3002\u5c55\u767d\u6697\u4e2d\u4e00\u54ac\u7259\uff0c\u62e7\u8eab\u4e00\u6251\uff0c\u5c06 \u5b83\u6293\u5728\u624b\u4e2d\uff0c\u4f46\u8eab\u8eaf\u5df2\u65e0\u7740\u529b\u4e4b\u5904\uff0c\u201c\u5657\u201d\u5730\u6389\u5230\u5730\u4e0a\uff0c\u8e6c\u3001\u8e6c\u3001\u8e6c\u51b2\u51fa\u6570\u6b65\uff0c\u65b9\u81ea\u7ad9\u7a33\u3002 \u3000\u3000\u4e00\u65f6\u4e4b\u95f4\uff0c\u4ed6\u5fc3\u4e2d\u7f9e\u3001\u6012\u3001\u6127\u3001\u607c\uff0c\u4ea4\u76f8\u7eb7\u81f3\uff0c\u4e5f\u4e0d\u77e5\u7a76\u7adf\u662f\u4ec0\u4e48\u6ecb\u5473\uff0c\u4f38\u624b\u4e00\u63a2\uff0c\u888b \u4e2d\u65e9\u5df3\u7a7a\u7a7a\uff0c\u53ea\u5269\u4e0b\u90a3\u65b9\u892a\u8272\u7684\u4e1d\u7ef8\u3002\u4f46\u4ed6\u8111\u5b50\u91cc\u5374\u5835\u585e\u7740\u592a\u591a\u7684\u4e8b\uff0c\u591a\u5f97\u4ed6\u81ea\u5df1\u4e5f\u6574\u7406\u4e0d \u51fa\u4e00\u4e2a\u5934\u7eea\u805a\u3002 \u3000\u3000\u6811\u6797\u4e4b\u4e2d\uff0c\u867d\u6709\u6708\u5149\u6f0f\u5165\uff0c\u4f46\u7a76\u7adf\u662f\u9ed1\u6697\u7684\uff0c\u4ed6\u832b\u7136\u4e3e\u6b65\u800c\u884c\uff0c\u65e2\u5fd8\u4e86\u81ea\u5df1\u4ece\u4f55\u800c\u6765\u4e5f \u4e0d\u77e5\u8def\u8981\u5411\u4f55\u800c\u53bb\uff0c\u4e0d\u7531\u6697\u4e2d\u8c34\u8d23\u81ea\u5df2\uff1a\u7236\u4eb2\u7684\u9057\u547d\uff0c\u670b\u53cb\u7684\u91cd\u6258\uff0c\u81ea\u5df1\u7ade\u6ca1\u6709\u4e00\u6837\u80fd\u59a5\u5584 \u5730\u5b8c\u6210\uff0c\u5c31\u662f\u7236\u4eb2\u4e34\u7ec8\u4e4b\u9645\u90a3\u4e48\u614e\u91cd\u5730\u4ea4\u7ed9\u81ea\u5df1\u7684\u4e1c\u897f\uff0c\u6b64\u523b\u4e5f\u5168\u90fd\u4ece\u81ea\u5df1\u624b\u4e2d\u5931\u53bb\u4e86\uff0c\u4ed6 \u7eb5\u7136\u6709\u5fc3\u4e00\u6b7b\u8c22\u7f6a\uff0c\u5374\u53c8\u6709\u4f55\u989c\u9762\u89c1\u7236\u4eb2\u4e8e\u4e5d\u6cc9\u4e4b\u4e0b\u5462? \u3000\u3000\u4e8e\u662f\u4ed6\u5f00\u59cb\u5728\u5730\u4e0a\u641c\u7d22\uff0c\u5e0c\u5180\u80fd\u627e\u56de\u88ab\u90a3\u5982\u75af\u5b50\u822c\u7684\u602a\u4eba\u6240\u629b\u53bb\u7684\u4e1c\u897f\uff0c\u4f46\u5728\u8fd9\u8fde\u5bf9\u9762 \u90fd\u770b\u4e0d\u6e05\u7684\u6811\u6797\u91cc\uff0c\u53c8\u600e\u80fd\u627e\u5230\u8fd9\u4e9b\u7ec6\u5c0f\u7684\u4e1c\u897f? \u3000\u3000\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u4ed6\u505c\u4e0b\u811a\u6b65\uff0c\u6781\u529b\u5c06\u5fc3\u4e2d\u6742\u4e71\u7684\u601d\u6f6e\u538b\u4e86\u4e0b\u53bb\uff0c\u76ee\u5149\u56db\u626b\uff0c\u89c1\u81ea\u5df1\u7acb \u8eab\u4e4b\u5904\uff0c\u7adf\u8fd8\u662f\u65b9\u624d\u9047\u7740\u201c\u71d5\u4e91\u4e94\u9738\u5929\u201d\u4ee5\u53ca\u201c\u5b89\u4e50\u516c\u5b50\u201d\u7b49\u4eba\u7684\u90a3\u5757\u6797\u95f4\u7a7a\u5730\uff0c\u4f46\u6b64\u523b\u5df2 \u4eba\u8ff9\u5168\u6e3a\uff0c\u5c31\u8fde\u90a3\u201c\u8ffd\u98ce\u65e0\u5f71\u201d\u534e\u6e05\u6cc9\u7684\u5c38\u8eab\uff0c\u90fd\u4e0d\u77e5\u88ab\u8c01\u642c\u53bb\u4e86\u3002 \u3000\u3000\u62ac\u76ee\u4e00\u671b\uff0c\u6811\u68a2\u661f\u6708\u4ecd\u660e\uff0c\u4ed6\u6697\u4ed8\u9053\uff1a\u201c\u6b64\u523b\u4e5f\u4e0d\u77e5\u662f\u4ec0\u4e48\u65f6\u5019\u4e86\uff0c\u6211\u4e14\u5728\u8fd9\u91cc\u6b47\u606f\u4e00 \u4e0b\uff0c\u7b49\u5929\u5149\u5927\u4eae\uff0c\u518d\u4eba\u6797\u53bb\u627e\u627e\u90a3\u4e9b\u7239\u7239\u7684\u9057\u7269\uff0c\u5509\uff01\u53cd\u6b63\u6211\u73b0\u4e0b\u5df2\u662f\u65e0\u5904\u9e25\u51fa\u8e1e\u5728\u8fd9\u91cc\u4e00 \u523b\uff0c\u5c11\u7559\u5728\u8fd9\u91cc\u4e00\u523b\uff0c\u53c8\u6709\u4ec0\u4e48\u4e24\u6837?\u201d \u3000\u3000\u4ed6\u5fc3\u80f8\u4e4b\u4e2d\uff0c\u832b\u7136\u5df2\u6781\uff0c\u968f\u610f\u5bfb\u4e86\u4e00\u5757\u77f3\u5757\uff0c\u501a\u7740\u6811\u4e8e\u5750\u4e86\u4e0b\u53bb\uff0c\u53ea\u89c9\u601d\u6f6e\u8d8a\u6765\u8d8a\u662f\u6df7 \u5a49\uff0c\u4e0d\u77e5\u4e0d\u89c9\u5730\u5c31\u7761\u7740\u4e86\uff0c\u7adf\u4e0d\u77e5\u4e1c\u65b9\u4e4b\u65e2\u767d\u3002 \u3000\u3000\u7761\u68a6\u4e4b\u4e2d\uff0c\u4ed6\u4eff\u4f5b\u53c8\u56de\u5230\u90a3\u6709\u5982\u9ec4\u91d1\u822c\u7684\u7ae5\u5e74\uff0c\u6148\u7965\u7684\u6bcd\u4eb2\uff0c\u6b63\u6e29\u67d4\u5730\u62cd\u7740\u4ed6\u7684\u8eab\u5b50\uff0c \u5634\u91cc\u54fc\u7740\u4e00\u9996\u4e0d\u77e5\u540d\u7684\u513f\u6b4c\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u7b11\u4e86\uff0c\u521d\u5347\u7684\u9633\u5149\uff0c\u6b63\u50cf\u6148\u6bcd\u7684\u624b\uff0c\u6e29\u67d4\u5730\u62c2\u5728\u4ed6\u8eab\u4e0a\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u4ed6\u4e0d\u77e5\u6b64\u523b \u662f\u771f\u662f\u68a6\u3002\u53ea\u89c9\u5f97\u90a3\u62cd\u5728\u81ea\u5df1\u8eab\u4e0a\u7684\u624b\uff0c\u7adf\u8d8a\u62cd\u8d8a\u91cd\uff0c\u7ec8\u4e8e\u4e00\u63c9\u773c\u775b\uff0c\u9192\u4e86\u8fc7\u6765\uff0c\u8033\u7554\u5374\u6709 \u4e00\u4e2a\u6e29\u67d4\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u671d\u9732\u6668\u98ce\uff0c\u5982\u6b64\u4e4b\u91cd\uff0c\u4f60\u7761\u5728\u8fd9\u91cc\uff0c\u4e5f\u4e0d\u6015\u7740\u4e86\u51c9\u5417?\u201d \u3000\u3000\u8fd9\u58f0\u97f3\u8d8a\u53d1\u771f\u5207\uff0c\u771f\u5207\u5f97\u4f7f\u4ed6\u4e5f\u77e5\u9053\u5e76\u975e\u6765\u81ea\u68a6\u4e2d\u3002\u4ed6\u52aa\u529b\u6e05\u9192\u4e00\u4e0b\u81ea\u5df1\u7684\u5934\u8111\uff0c\u5f20\u76ee \u4e00\u671b\uff0c\u53ea\u89c1\u4e00\u4e2a\u6ee1\u8eab\u534e\u670d\u7684\u4e2d\u5e74\u7f8e\u5987\uff0c\u6b63\u7ad9\u5728\u81ea\u5df1\u8eab\u524d\uff0c\u7528\u4e00\u79cd\u65e0\u6bd4\u6148\u7965\u7684\u76ee\u5149\u671b\u7740\u81ea\u53ec\uff0c \u800c\u8fd9\u79cd\u76ee\u5149\uff0c\u4ed6\u5df2\u4e45\u4e45\u6ca1\u6709\u4eab\u53d7\u5230\u4e86\u3002 \u3000\u3000\u8fd9\u4e2d\u5e74\u7f8e\u5987\u89c1\u4ed6\u5f20\u5f00\u773c\u6765\uff0c\u6148\u7965\u7684\u8138\u4e0a\u5fae\u5fae\u4e00\u7b11\uff0c\u53c8\u9053\uff1a\u201c\u5c11\u5e74\u4eba\u4e0d\u77e5\u73cd\u501f\u81ea\u5df1\u7684\u751f \u547d\uff0c\u5230\u5e74\u7eaa\u5927\u4e86\u4ee5\u540e\uff0c\u800d\u540e\u6094\u4e5f\u6765\u4e0d\u53ca\u4e86\u201d \u3000\u3000\u8bed\u58f0\u867d\u4eb2\u5207\uff0c\u5176\u4e2d\u5374\u4f3c\u6709\u79cd\u96be\u4ee5\u63cf\u8ff0\u7684\u5fe7\u90c1\u5473\u9053\u3002 \u3000\u3000\u5c55\u81ea\u6014\u4e86\u4e00\u5f81\uff0c\u7ffb\u8eab\u722c\u4e86\u8d77\u6765\uff0c\u4ed6\u672c\u662f\u81f3\u60c5\u81f3\u6027\u4e4b\u4eba\uff0c\u6b64\u523b\u89c1\u8fd9\u4e2d\u5e74\u7f8e\u5987\u4e0e\u81ea\u5df1\u7d20\u4e0d\u76f8 \u8bc6\uff0c\u5374\u5982\u6b64\u6e29\u67d4\u6148\u7965\u5730\u5bf9\u5f85\u81ea\u5df1\uff0c\u5fc3\u4e2d\u4e0d\u7981\u5927\u4e3a\u611f\u52a8\uff0c\u60f3\u8bf4\u51e0\u53e5\u611f\u6fc0\u7684\u8bdd\uff0c\u5374\u53c8\u6cb9\u7eb3\u5730\u4e0d\u77e5 \u8be5\u8bf4\u4ec0\u4e48\u597d\u3002 \u3000\u3000\u90a3\u4e2d\u5e74\u7f8e\u5987\u89c1\u5230\u4ed6\u8fd9\u526f\u6837\u5b50\uff0c\u76ee\u4e2d\u7684\u795e\u8272\u66f4\u4e3a\u6148\u7965\u4e86\uff0c\u8f7b\u8f7b\u957f\u53f9\u4e00\u58f0\uff0c\u53c8\u9053\uff1a\u201c\u7537\u5b50\u6c49 \u5fd7\u5728\u56db\u65b9\uff0c\u672c\u5e94\u51fa\u6765\u95ef\u8361\u7684\u597d\uff0c\u4f46\u662f\uff0c\u5509\uff0c\u4e16\u4e0a\u53c8\u6709\u4ec0\u4e48\u5730\u65b9\u80fd\u6709\u5bb6\u90a3\u4e48\u6e29\u6696\u5462?\u6211\u770b\u4f60\u9762 \u76ee\u6194\u60b4\uff0c\u663e\u89c1\u5f97\u5728\u5916\u9762\u5df2\u7ecf\u6d41\u6d6a\u5f88\u4e45\u4e86\uff0c\u4f60\u8981\u662f\u4e0d\u602a\u6211\u591a\u5634\uff0c\u4f60\u2026\u2026\u4f60\u8fd8\u662f\u5feb\u70b9\u56de\u5bb6\u7684 \u597d\u3002\u201d \u3000\u3000\u8bf4\u5baa\u8f7b\u8f7b\u4e00\u62cd\u4ed6\u7684\u80a9\u8180\uff0c\u8f6c\u8eab\u8d70\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u5c55\u767d\u671b\u7740\u5979\u7684\u80cc\u5f71\uff0c\u5fc3\u80f8\u4e4b\u95f4\u4f46\u89c9\u70ed\u8840\u5954\u817e\uff0c\u4e0d\u80fd\u81ea\u5df2\uff0c\u7a81\u5730\u54c0\u58f0\u53f9\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u6ca1 \u6709\u5bb6\uff01\u201d\u4e24\u6ef4\u6676\u83b9\u7684\u6cea\u73e0\uff0c\u5728\u773c\u7736\u4e2d\u8f6c\u4e86\u4e24\u8f6c\uff0c\u7ec8\u4e8e\u5fcd\u4e0d\u4f4f\u6d41\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u90a3\u4e2d\u5e74\u7f8e\u5987\u8d70\u4e86\u4e24\u6b65\uff0c\u542c\u5230\u8fd9\u53e5\u8bdd\uff0c\u811a\u6b65\u4e00\u989d\uff0c\u53c8\u8f6c\u8eab\u56de\u6765\uff0c\u5c55\u767d\u4f38\u624b\u4e00\u62b9\u9762\u988a\u4e0a\u7684\u6cea \u73e0\uff0c\u957f\u58f0\u56db\u9053\uff1a\u201c\u6211\u4e00\u751f\u4e4b\u4e2d\uff0c\u4ece\u6ca1\u6709\u89c1\u8fc7\u50cf\u592b\u4eba\u8fd9\u6837\u7684\u597d\u4eba\uff0c\u6240\u4ee5\u5fcd\u4e0d\u4fca\u2014\u2014\u201d \u3000\u3000\u4ed6\u8bed\u58f0\u4e00\u987f\uff0c\u626b\u76ee\u671b\u5904\uff0c\u5374\u89c1\u6811\u6797\u5c3d\u5934\uff0c\u505c\u7740\u4e00\u8f86\u6781\u4e3a\u534e\u4e3d\u7684\u9a6c\u8f66\uff0c\u8f66\u8f95\u4e24\u4fa7\uff0c\u7adf\u6709\u56db \u4e2a\u52b2\u88c5\u4f69\u5251\u5927\u6c49\u7aef\u5750\u9a6c\u4e0a\uff0c\u4e0d\u4f4f\u5730\u56de\u5934\u671b\u6765\uff0c\u4e00\u4e2a\u4e2a\u6d53\u7709\u6df1\u76b1\uff0c\u4f3c\u662f\u4e0d\u9ad8\u5174\u3002 \u3000\u3000\u4ed6\u5fc3\u5ff5\u4e00\u52a8\uff0c\u4fbf\u53c8\u63a5\u9053\uff1a\u592b\u4eba\u6709\u4e8b\uff0c\u8fd8\u662f\u8d70\u5427\uff0c\u6211\xb7\uff0e\uff0e\u2026\u6211\u4ee5\u540e\u4e00\u5b9a\u73cd\u501f\u81ea\u5df2\u7684\u751f \u547d\u3002\u201d \u3000\u3000\u4ed6\u5634\u91cc\u5982\u6b64\u8bf4\uff0c\u5fc3\u4e2d\u5374\u5728\u6697\u4ed8\uff1a\u201c\u5176\u5b9e\u751f\u547d\u6709\u4ec0\u4e48\u503c\u5f97\u73cd\u60dc\u7684\uff0c\u9b4f\u82e5\u4e0d\u662f\u8fd8\u6709\u7236\u4ec7\u5b8b \u62a5\uff0c\u5c31\u7b97\u7acb\u523b\u6b7b\u4e86\u4e5f\u4e0d\u53ef\u60dc\uff0c\u53ea\u662f\u6211\u8fde\u6740\u7236\u4ec7\u4eba\u662f\u8c01\u90fd\u4e0d\u77e5\u9053\uff0c\u7236\u4eb2\u7684\u9057\u7269\u4e5f\u88ab\u6211\u5f04\u6389 \u4e86\uff01\u201d\u4e0d\u7981\u53c8\u4e3a\u4e4b\u60b2\u6006\u4e0d\u5df2\u3002 \u3000\u3000\u90a3\u4e2d\u5e74\u7f8e\u5987\u67f3\u7709\u5fae\u76b1\uff0c\u67d4\u58f0\u95ee\u9053\uff1a\u201c\u4f60\u5e74\u7eaa\u8fd8\u8f7b\uff0c\u4f46\u8a00\u8bcd\u4e4b\u4e2d\uff0c\u90fd\u600e\u7684\u50cf\u662f\u6709\u7740\u8bb8\u591a\u60b2 \u607c\u96be\u89e3\u4e4b\u4e8b?\u5509\uff01\u4f60\u4eec\u5c11\u5e74\u4eba\u603b\u662f\u8fd9\u6837\uff0c\u8fd8\u672a\u8bc6\u5f97\u6101\u6ecb\u6627\uff0c\u5c31\u5df2\u5982\u6b64\u5fe7\u90c1\u4e86\uff0c\u7b49\u5230\u4f60\u50cf\u9b4f\u8fd9 \u6837\u7684\u5e74\u7eaa\uff0c\u5fc3\u91cc\u5c31\u662f\u6709\u5fe7\u6101\u70e6\u95f7\u4e4b\u4e8b\uff0c\u4e5f\u4e0d\u4f1a\u8bf4\u51fa\u6765\u4e86\uff0c\u5509!\u6b32\u8bf4\u8fd8\u4f11\uff0c\u5374\u9053\u5929\u51c9\u597d\u4e2a\u79cb\uff0c \u5509\uff0c\u5c11\u5e74\u4eba\uff0c\u8fd8\u4e0d\u7b11\u4e00\u7b11?\u5927\u597d\u751f\u547d\uff0c\u9edb\u7eff\u5e74\u534e\uff0c\u90fd\u5728\u7b49\u7740\u4f60\u53bb\u597d\u597d\u4e8b\u53d7\u54e9!\u201d \u3000\u3000\u8fd9\u4e2d\u5e74\u7f8e\u5987\u6e29\u67d4\u5730\u8bf4\u7740\uff0c\u5c55\u767d\u53ea\u6df7\u4e0d\u5f97\u5979\u6c38\u8fdc\u8bf4\u4e0b\u53bb\uff0c\u62ac\u5934\u4e00\u671b\uff0c\u5374\u89c1\u5979\u773c\u4e2d\u7684\u5fe7\u90c1\u4e4b \u8272\uff0c\u4f3c\u4e4e\u751a\u4e8e\u81ea\u5df1\uff0c\u4e0d\u7981\u6697\u5fd6\uff1a\u201c\u8fd9\u4f4d\u5987\u4eba\u8863\u886b\u9c9c\u4e3d\uff0c\u98ce\u59ff\u534e\u8d35\uff0c\u663e\u89c1\u4e0d\u662f\u8fbe\u5b98\u8d35\u4eba\u5bb6\u7737\uff0c \u4fbf\u662f\u5de8\u5546\u5bcc\u8d3e\u59bb\u5ba4\uff0c\u6b63\u662f\u6781\u6709\u798f\u6c14\u4e4b\u4eba\uff0c\u600e\u5730\u5374\u6709\u7740\u5982\u8bb8\u70e6\u607c?\u201d \u3000\u3000\u53c8\u5fd6\u9053\uff1a\u201c\u5979\u548c\u6211\u7d22\u6627\u751f\u5e73\uff0c\u5c31\u5df2\u5982\u6b64\u5bf9\u6211\uff0c\u60f3\u89c1\u5979\u5e73\u65e5\u5fc5\u662f\u6781\u4e3a\u6148\u7965\u7684\u597d\u4eba\uff0c\u5979\u82e5\u771f \u662f\u70e6\u607c\uff0c\u6211\u5c82\u80fd\u4e0d\u4e3a\u5979\u89e3\u51b3?\u201d \u3000\u3000\u4ed6\u53ea\u77e5\u4eba\u5bb6\u5982\u6b64\u5bf9\u5f85\u81ea\u5df1\uff0c\u81ea\u5df1\u4f7f\u5e94\u52a0\u4e0a\u5341\u500d\u53bb\u62a5\u7b54\u4eba\u5bb6\uff0c\u5374\u5c06\u81ea\u5df1\u7684\u70e6\u607c\u629b\u5728\u4e00\u8fb9\uff0c \u81f3\u4e8e\u4eba\u5bb6\u7684\u70e6\u607c\uff0c\u662f\u5426\u4ed6\u6240\u80fd\u89e3\u51b3\uff0c\u4ed6\u4e5f\u4e0d\u7ba1\uff0c\u4e00\u633a\u80f8\u819b\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u770b\u592b\u4eba\u4e5f\u50cf\u6709\u7740 \u4ec0\u4e48\u70e6\u607c\u4e4b\u4e8b\uff0c\u4e0d\u59a8\u544a\u8bc9\u5728\u4e0b\uff0c\u6211\u867d\u7136\u65e0\u7528\uff0c\u5374\u8fd8\u6709\u4e9b\u7b28\u529b\u6c14\u3002\u53ea\u8981\u6211\u80fd\u529e\u5230\u7684\u4e8b\uff0c\u4e00\u5b9a\u5168 \u529b\u4e3a\u592b\u4eba\u53bb\u505a\u3002\u201d \u3000\u3000\u90a3\u4e2d\u5e74\u7f8e\u5987\u5c55\u989c\u4e00\u7b11\u9053\uff1a\u201c\u6211\u4e0e\u4f60\u7d20\u4e0d\u76f8\u8bc6\uff0c\u4f60\u4e3a\u4ec0\u4e48\u8981\u5e2e\u6211\u7684\u5fd9\u5462?\u201d \u3000\u3000\u5c55\u767d\u4e0d\u7981\u6014\u4e86\u2014\u6014\uff0c\u8bb7\u5450\u5730\u8bf4\u9053\uff1a\u201c\u592b\u4eba\u5982\u6b64\u95ee\u6211\u7b54\u4e0d\u51fa\uff0c\u4f46\u6211\u6d41\u6d6a\u4ee5\u6765\uff0c\u5c31\u7b97\u8eba\u5728\u5927 \u96e8\u4e4b\u4e0b\uff0c\u4e5f\u4ece\u672a\u6709\u4eba\u7ba1\u6211\uff0c\u800c\u6b64\u523b\u592b\u4eba\u5374\u5982\u6b64\u7167\u987e\u6211\uff0c\u6211\u82e5\u80fd\u4e3a\u592b\u4eba\u6548\u52b3\uff0c\u4fbf\u662f\u6700\u4e3a\u9ad8\u5174\u4e4b \u4e8b\u4e86\u3002\u201d \u3000\u3000\u8bf4\u5230\u540e\u6765\uff0c\u4ed6\u53ea\u89c9\u81ea\u5df1\u6240\u8bf4\u4e4b\u8bdd\uff0c\u6b63\u662f\u5929\u5730\u95f4\u552f\u4e00\u7684\u9053\u7406\uff0c\u662f\u4ee5\u58f0\u8c03\u4fbf\u8d8a\u8bf4\u8d9f\u54cd\uff0c\u4ecd\u81ea \u60fa\u677e\u7740\u7684\u7761\u773c\uff0c\u4e5f\u9732\u51fa\u795e\u91c7\u6765\u4e86\u3002 \u3000\u3000\u90a3\u4e2d\u5e74\u7f8e\u5987\u76ee\u5149\u8f6c\u4e86\u4e24\u7279\uff0c\u4f3c\u4e4e\u5fc3\u4e2d\u4e5f\u5927\u4e3a\u611f\u52a8\uff0c\u8f7b\u8f7b\u53f9\u9053\uff1a\u5509\uff0c\u50bb\u5b69\u5b50\uff0c\u6211\u53ea\u662f\u4e58\u8f66 \u7ecf\u8fc7\u8fd9\u91cc\uff0c\u770b\u5230\u4f60\u7761\u5728\u671d\u9732\u4e4b\u4e0b\uff0c\u6015\u4f60\u8425\u4e86\u51c9\uff0c\u662f\u4ee5\u4fbf\u4e0b\u8f66\u62db\u547c\u4f60\u4e00\u58f0\uff0c\u8fd9\u53c8\u6709\u4ec0\u4e48\u4e86\u4e0d\u8d77? \u6211\u82e5\u771f\u6709\u4ec0\u4e48\u56f0\u96be\u4e4b\u4e8b\uff0c\u8981\u4f60\u53bb\u505a\uff0c\u90a3\u4f60\u5c82\u4e0d\u662f\u592a\u5446\u4e86\u4e9b\u5417?\u201d \u3000\u3000\u5c55\u767d\u957f\u53f9\u4e00\u58f0\u9053\uff1a\u201c\u6211\u4e0d\u4f1a\u8bf4\u8bdd\uff0c\u5fc3\u91cc\u60f3\u7740\u7684\u4e8b\uff0c\u5e38\u5e38\u5145\u6cd5\u8bf4\u51fa\u6765\uff01\u201d \u3000\u3000\u90a3\u4e2d\u5e74\u7f8e\u5987\u7a81\u5730\u8f7b\u8f7b\u6447\u4e86\u6447\u624b\uff0c\u9053\uff1a\u201c\u4e0d\u8bf4\u4e5f\u597d\uff0c\u53cd\u6b63\u6211\u5df2\u77e5\u9053\u4f60\u662f\u4e2a\u5f88\u597d\u7684\u5b69\u5b50\uff0c\u4f60 \u7684\u597d\u610f\uff0c\u6211\u4f1a\u5e38\u5e38\u8bb0\u5728\u5fc3\u91cc\u7684\uff0c\u5509\u2014\u2014\u9752\u513f\u7684\u5fc3\uff0c\u8981\u662f\u6709\u4f60\u4e00\u534a\u5584\u826f\u5c31\u597d\u4e86\uff0c\u8001\u5929\u4e3a\u4ec0\u4e48\u603b \u662f\u8ba9\u5584\u826f\u7684\u4eba\u53d7\u82e6\u5462?\u201d \u3000\u3000\u4f38\u624b\u4e00\u629a\u4e24\u988a\uff0c\u76ee\u5149\u6e29\u67d4\u5730\u5728\u5c55\u81ea\u8eab\u4e0a\u51dd\u89c6\u534a\u664c\uff0c\u53c8\u9053\uff1a\u201c\u4e0d\u8981\u5fd8\u8bb0\u6211\u7684\u8bdd\uff0c\u628a\u5fc3\u91cc\u70e6 \u607c\u7684\u4e8b\u629b\u5f00\uff0c\u4e16\u4e0a\u6ca1\u6709\u5bb6\u7684\u4eba\u591a\u5f97\u5f88\uff0c\u5e74\u8f7b\u4eba\u6700\u8981\u4e0d\u5f97\u7684\u5c31\u662f\u81ea\u6028\uff0c\u4f60\u77e5\u4e0d\u77e5\u9053\uff0c\u751f\u547d\u4e2d\u4e00 \u4e9b\u7f8e\u597d\u7684\u4e8b\u60c5\uff0c\u662f\u8981\u81ea\u5df2\u53bb\u521b\u9020\u7684\uff0c\u82e5\u662f\u610f\u5fd7\u6d88\u6c89\uff0c\u4e0d\u53bb\u594b\u6597\uff0c\u8fd9\u79cd\u4eba\u5c31\u53ea\u914d\u53d7\u82e6\u4e00\u8f88 \u5b50\u3002\u201d\u5979\u53c8\u5fae\u5fbd\u4e00\u7b11\uff0c\u8f6c\u8eab\u8d70\u53bb\u3002 \u3000\u3000\u4ed6\u7ad9\u5728\u6811\u4e0b\uff0c\u5446\u5446\u5730\u695e\u4e86\u534a\u664c\uff0c\u90a3\u4e2d\u5e74\u7f8e\u5987\u6240\u8bf4\u7684\u8bdd\uff0c\u6b64\u523b\u4ecd\u7136\u5728\u4ed6\u8033\u7a77\u7edf\u7edf\u7740\uff1a \u201c\u2026\u2026\u5927\u597d\u751f\u547d\uff0c\u7559\u7eff\u5e74\u534e\uff0c\u90fd\u5728\u7b49\u7740\u4f60\u53bb\u4eab\u53d7\u2026\u2026\u751f\u547d\u4e2d\u4e00\u4e9b\u7f8e\u597d\u4e4b\u4e8b\uff0c\u662f\u8981\u81ea\u5df1\u53bb\u521b\u9020 \u7684\u2026\u2026\u201d\u4ed6\u7ec6\u7ec6\u4f53\u4f1a\u7740\u8fd9\u4e9b\u8bdd\u91cc\u7684\u542b\u610f\uff0c\u4e0d\u89c9\u60f3\u5f97\u75f4\u4e86\u3002 \u3000\u3000\u54ea\u77e5\u6797\u5916\u9a6c\u8e44\u4e4b\u58f0\uff0c\u53c8\u590d\u5927\u4f5c\uff0c\u4ed6\u62ac\u76ee\u671b\u53bb\uff0c\u53ea\u89c1\u4e09\u5339\u5065\u9a6c\uff0c\u7b4b\u4e5f\u4f3c\u5730\u51b2\u8fdb\u6811\u6797\u6765\uff0c\u582a \u582a\u9a70\u5230\u4ed6\u9762\u524d\uff0c\u9a6c\u4e0a\u7684\u4eba\u5404\u81ea\u4e00\u52d2\u7586\u7ef3\uff0c\u90a3\u4e09\u5339\u9a6c\u6602\u9996\u957f\u5636\u4e00\u58f0\uff0c\u4eba\u7acb\u800c\u8d77\uff0c\u9a6c\u4e0a\u9664\u9a91\u58eb\u5df2 \u63a0\u4e0b\u9a6c\u6765\uff0c\u5374\u6b63\u662f\u65b9\u624d\u62a4\u5728\u90a3\u4e2d\u5e74\u7f8e\u5987\u674e\u65c1\u7684\u52b2\u88c5\u6c49\u5b50\u3002 \u3000\u3000\u5c55\u767d\u5fae\u5fae\u4e00\u60ca\uff0c\u53c8\u5927\u4e3a\u5947\u602a\uff0c\u4e0d\u77e5\u9053\u8fd9\u4e09\u4e2a\u5927\u6c49\u7a81\u5730\u6298\u4e86\u56de\u6765\uff0c\u662f\u4f55\u7528\u610f\u3002 \u3000\u3000\u90a3\u4e09\u4e2a\u52b2\u88c5\u5927\u6c49\uff0c\u811a\u6b65\u6c89\u5b9e\uff0c\u8eab\u8eaf\u9157\u58ee\uff0c\u4e24\u8fb9\u7684\u592a\u9633\u7a74\u9f13\u8d77\u5982\u4e18\uff0c\u4e00\u773c\u671b\u53bb\uff0c\u4fbf\u80fd\u770b\u51fa \u60e7\u662f\u6b66\u529f\u4e0d\u5f31\u7684\u7ec3\u5bb6\u5b50\u3002\u4ed6\u4eec\u6a2a\u626b\u5c55\u767d\u4e00\u773c\uff0c\u4e00\u8a00\u4e0d\u53d1\uff0c\u4f7f\u5e76\u80a9\u5411\u4ed6\u8d70\u4e86\u8fc7\u6765\uff0c\u9762\u4e0a\u5bd2\u610f\u68ee \u68ee\uff0c\u773c\u4e2d\u66f4\u662f\u6740\u6c14\u817e\u817e\u3002 \u3000\u3000\u5c55\u767d\u5927\u4e3a\u8be7\u5f02\uff1a\u201c\u8fd9\u4e9b\u4eba\u770b\u6765\u4f3c\u8981\u52a0\u5bb3\u4e8e\u6211\uff0c\u4f46\u6211\u5374\u4e00\u4e2a\u4e5f\u4e0d\u8ba4\u5f97\uff0c\u5929\u4e0b\u4e8b\u600e\u7684\u5982\u662f\u5947 \u602a\uff0c\u603b\u662f\u8981\u8ba9\u6211\u9047\u7740\u4e9b\u65e0\u8c13\u7684\u70e6\u607c\uff01\u201d \u3000\u3000\u5ff5\u5934\u5c1a\u672a\u8f6c\u5b8c\uff0c\u8fd9\u4e09\u4e2a\u52b2\u88c5\u5927\u6c49\u5df2\u5404\u81ea\u66b4\u5676\u4e00\u58f0\uff0c\u5206\u505a\u4e09\u4e2a\u65b9\u5411\u6251\u4e86\u4e0a\u6765\uff0c\u5c55\u767d\u5927\u60ca\u4e4b \u4e0b\uff0c\u8eab\u5f62\u5fae\u584c\uff0c\u540e\u9000\u4e24\u6b65\uff0c\u80cc\u810a\u7d27\u7d27\u9760\u5728\u6811\u6797\u4e0a\uff0c\u9738\u738b\u5378\u7532\u201d\u3001\u201c\u5982\u5c01\u4f3c\u95ed\u201d\u4e00\u8fde\u6321\u4e86\u4e09 \u62db\u3002 \u3000\u3000\u90a3\u4e09\u6761\u5927\u6c49\u51b7\u7b11\u4e00\u58f0\uff0c\u53f1\u9053\uff1a\u201c\u5c0f\u4f19\u5b50\u5feb\u4e9b\u7eb3\u547d\u6765\u5427\uff01\u5c31\u51ed\u8fd9\u4e24\u4e0b\u5b50\u60f3\u5728\u592a\u7237\u4eec\u9762\u524d\u62fc \u547d\uff0c\u90a3\u4f60\u662f\u5728\u505a\u68a6\u3002\u201d\u4e09\u4eba\u8054\u624b\uff0c\u5237\u3001\u5237\u3001\u5237\uff0c\u53c8\u662f\u4e09\u638c\u3002 \u3000\u3000\u5c55\u767d\u6b66\u529f\u672c\u5c31\u4e0d\u9ad8\uff0c\u624b\u4e2d\u65e0\u5251\uff0c\u66f4\u8981\u518d\u6253\u4e09\u5206\u6298\u6263\uff0c\u52a0\u4e0a\u4ed6\u75b2\u52b3\u672a\u590d\uff0c\u5fc3\u795e\u4ea4\u7601\uff0c\u6b64\u523b \u54ea\u91cc\u662f\u8fd9\u4e09\u6761\u5982\u9f99\u4f3c\u864e\u7684\u5927\u6c49\u654c\u624b\uff0c\u52c9\u5f3a\u53c8\u62c6\u4e86\u6570\u62db\uff0c\u5fc3\u91cc\u5fcd\u4e0d\u4f4f\u60f3\u95ee\uff1a\u201c\u6211\u548c\u4f60\u4eec\u53c8\u6709\u4f55 \u51a4\u4f55\u4ec7?\u4f60\u4eec\u600e\u7684\u4ec0\u4e48\u8bdd\u4e0d\u8bf4\uff0c\u5c31\u8981\u6211\u7eb3\u547d?\u201d\u4f46\u4ed6\u4e43\u5341\u5206\u5014\u5f3a\u4e4b\u4eba\uff0c\u53e3\u4e2d\u5374\u7edd\u5bf9\u4e0d\u4f55\u8c22\u6765\uff0c \u56e0\u4e3a\u53ea\u8981\u4e00\u95ee\uff0c\u4fbf\u663e\u5f97\u81ea\u5df1\u793a\u5f31\u4e8e\u4eba\uff0c\u90a3\u662f\u4ed6\u5b81\u53ef\u6b7b\u53bb\u4e5f\u4e0d\u80af\u4e8e\u7684\u3002 \u3000\u3000\u8fd9\u4e09\u6761\u5927\u6c49\u51b7\u7b11\u8fde\u8fde\uff0c\u624b\u5e95\u4e0b\u8d8a\u6765\u8d8a\u8fa3\uff0c\u7ade\u90fd\u662f\u6b66\u6797\u4e2d\u53eb\u5f97\u51fa\u5b57\u53f7\u6765\u7684\u9ad8\u624b\uff0c\u5c55\u767d\u4e00\u4e2a \u758f\u795e\uff0c\u524d\u80f8\u4f7f\u201c\u7830\u201d\u5730\u7740\u4e86\u4e00\u638c\uff0c\u51e0\u4e4e\u5c06\u4ed6\u808b\u9aa8\u90fd\u5c3d\u6570\u6253\u6298\uff0c\u4f46\u4ed6\u5374\u8fde\u54fc\u4e5f\u672a\u54fc\u4e00\u58f0\uff0c\u201c\u529b \u5288\u534e\u5c71\u201d\u3001\u201c\u9ec4\u864e\u638f\u5fc3\u201d\uff0c\u5019\u7136\u653b\u51fa\u4e00\u62f3\uff0c\u540c\u65f6\u201c\u8fdb\u6b65\u64a9\u9634\u201d\uff0c\u4e00\u811a\u8e22\u5411\u53f3\u8fb9\u90a3\u5927\u6c49\u7684\u4e0b \u8179\u3002 \u3000\u3000\u8fd9\u4e00\u62f3\u3001\u4e00\u817f\uff0c\u6b63\u662f\u4ed6\u5168\u8eab\u529f\u529b\u6240\u805a\uff0c\u90a3\u4e09\u6761\u5927\u6c49\u7ade\u90fd\u88ab\u4ed6\u903c\u9000\u4e00\u6b65\uff0c\u5c24\u5176\u53f3\u8fb9\u90a3\u5927\u6c49 \u4e45\u5c45\u6c5f\u5357\uff0c\u201c\u5357\u62f3\u5317\u817f\u201d\uff0c\u5357\u4eba\u672c\u4e0d\u5584\u4f7f\u817f\u6cd5\uff0c\u6b64\u523b\u7ade\u9669\u4e9b\u88ab\u5c55\u767d\u4e00\u817f\u8e22\u4e2d\u3002 \u3000\u3000\u4ed6\u8fde\u9000\u4e8c\u6b65\uff0c\u65b9\u81ea\u62ff\u6869\u7ad9\u7a33\uff0c\u5927\u6012\u4e4b\u4e0b\uff0c\u7a81\u5730\u53cd\u8eab\u4e00\u62bd\uff0c\u4ece\u8eab\u540e\u62bd\u51fa\u4e00\u6a71\u7cbe\u5149\u96ea\u4eae\u5e7c\u9b3c \u5171\u5200\u6765\uff0c\u8fce\u98ce\u5341\u8b6c\uff0c\u559d\u9053\uff0c\u70b9\u5b50\u4e0d\u8f6f\uff0c\u5e76\u80a9\u5b50\u64a4\u9752\u5b50\u62db\u547c\u4ed6\u3002\u201d \u3000\u3000\u4e00\u6e9c\u9752\u5149\uff0c\u5f53\u5934\u5411\u5c55\u767d\u780d\u4e86\u4e0b\u53bb\uff0c\u53e6\u4e24\u4eba\u4e5f\u5404\u81ea\u62bd\u51fa\u5175\u5203\u6765\uff0c\u6076\u72e0\u72e0\u5730\u6251\u5411\u5c55\u767d\uff0c\u4e00\u9762 \u7eb5\u58f0\u7b11\u9053\uff1a\u201c\u5582\uff0c\u4f60\u8fd9\u5c0f\u5b50\u53ef\u77e5\u9053\u592a\u7237\u4eec\u4e3a\u4ec0\u4e48\u8981\u5bb0\u4f60?\u563f\u563f\uff0c\u60f3\u662f\u4f60\u8fd9\u5c0f\u4e8e\u524d\u751f\u7f3a\u4e86\u5fb7\uff0c \u4eca\u751f\u53eb\u4f60\u6b7b\u4e86\u4e5f\u662f\u4e2a\u7cca\u6d82\u9b3c\u3002\u201d \u3000\u3000\u5c55\u767d\u65e2\u60ca\u81f3\u6012\uff0c\u8eab\u5f71\u5de6\u907f\u53f3\u95ea\uff0c\u52c9\u5f3a\u8eb2\u4e86\u4e09\u6570\u62db\uff0c\u773c\u524d\u5200\u5149\u4e00\u6643\uff0c\u5df2\u5230\u5f53\u5934\uff0c\u4ed6\u5168\u529b\u62e7 \u8eab\u95ea\u907f\uff0c\u54ea\u77e5\u817f\u4e0a\u4e00\u5bd2\uff0c\u5374\u5df3\u4e2d\u4e86\u4e00\u5200\u3002\u4ed6\u6697\u53f9\u4e00\u58f0\uff0c\u77e5\u9053\u4eca\u65e5\u5df2\u662f\u51f6\u591a\u5409\u5c11\uff0c\u4ed6\u867d\u672a\u5c06\u751f \u6b7b\u4e4b\u4e8b\u6545\u5728\u5fc3\u4e0a\uff0c\u4f46\u60f3\u5230\u7236\u4ec7\u672a\u62a5\uff0c\u5c31\u6b64\u6b7b\u53bb\uff0c\u771f\u5c48\u6b7b\u4e0d\u7791\u76ee\u3002\u4e00\u5ff5\u81f3\u6b64\uff0c\u52c7\u6c14\u5927\u589e\uff0c\u594b\u8d77 \u7cbe\u795e\uff0c\u53c8\u62c6\u4e86\u6570\u62db\uff0c\u817f\u4e0a\u7684\u75bc\u75db\u4e5f\u4e0d\u89c9\u5f97\u4e86\uff0c\u54ea\u77e5\u5de6\u81c2\u53c8\u662f\u4e00\u5bd2\uff0c\u88ab\u5200\u950b\u5212\u4e86\u4e00\u9053\u957f\u8fbe\u4e00\u5c3a \u7684\u53e3\u5b50\u3002 \u3000\u3000\u8fd9\u65f6\u4ed6\u7eb5\u7136\u6709\u7740\u65e0\u6bd4\u7684\u52c7\u6c14\uff0c\u4e3a\u751f\u547d\u800c\u640f\u6597\uff0c\u4f46\u8eab\u4e0a\u7684\u5200\u4f24\u75bc\u75db\uff0c\u5374\u4f7f\u4ed6\u518d\u4e5f\u65e0\u6cd5\u652f \u6301\uff0c\u6697\u53f9\u4e00\u53e3\u6c14\uff0c\u65b9\u5f85\u98de\u8eab\u6251\u4e0a\uff0c\u5c06\u53f3\u4fa7\u90a3\u5927\u6c49\u7d27\u7d27\u62b1\u4efb\uff0c\u8ba9\u4ed6\u9677\u81ea\u5df1\u4e00\u9f50\u6b7b\u53bb\u3002 \u3000\u3000\u54ea\u77e5\u6797\u5916\u7a81\u53c8\u9a70\u6b62\u54d1\u5065\u9a6c\uff0c\u5c1a\u672a\u5230\u8fbe\uff0c\u9a6c\u4e0a\u5df2\u81ea\u559d\u9053\uff1a\u201c\u9648\u6e05\uff0c\u9648\u5e73\uff0c\u4f60\u4eec\u8fd8\u4e0d\u7ed9\u6211\u4f4f \u624b!\u201d\u8bed\u58f0\u6e05\u8106\uff0c\u7adf\u662f\u90a3\u4e2d\u5e74\u7f8e\u5987\u7684\u53e3\u97f3\u3002 \u3000\u3000\u90a3\u4e09\u6761\u5927\u6c49\u5bf9\u671b\u4e00\u773c\uff0c\u4e00\u3002\u9f50\u9000\u4e86\u5f00\u53bb\uff0c\u53f3\u8fb9\u90a3\u4e2a\uff0c\u53e3\u4e2d\u5374\u5411\u5c55\u767d\u4f4e\u58f0\u9a82\u9053\uff1a\u201c\u5c0f\u4f19\u5b50 \u4f60\u518d\u6562\u5bf9\u6211\u4eec\u592b\u4eba\u2026\u2026\u201d \u3000\u3000\u8a00\u72b9\u672a\u4e86\uff0c\u53ea\u542c\u201c\u556a\u201d\u7684\u4e00\u58f0\uff0c\u4ed6\u8138\u4e0a\u5df2\u7740\u4e86\u4e00\u638c\uff0c\u9762\u5bb9\u9aa4\u53d8\uff0c\u4e00\u773c\u671b\u53bb\uff0c\u5374\u89c1\u7ad9\u5728\u4ed6 \u9762\u524d\u7684\uff0c\u6b63\u662f\u90a3\u4e2d\u5e74\u7f8e\u5987\uff0c\u5df2\u4e0d\u77e5\u4f55\u65f6\u63a0\u4e0b\u9a6c\u6765\uff0c\u4ee5\u53ca\u7528\u4ec0\u4e48\u8eab\u6cd5\u63b4\u4e86\u4ed6\u4e00\u638c\uff0c\u540c\u65f6\u8fd8\u5728\u6012 \u53f1\u9053\uff1a\u4f60\u8bf4\u6211\u4ec0\u4e48?\u201d \u3000\u3000\u90a3\u52b2\u88c5\u5927\u6c49\u7a7a\u81ea\u6c14\u5f97\u9762\u76ee\u53d8\u8272\uff0c\u53e3\u4e2d\u5374\u4e0d\u6562\u542d\u534a\u53e5\u3002 \u3000\u3000\u90a3\u4e2d\u5e74\u7f8e\u5987\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u8fd1\u6765\u4e5f\u8d8a\u6765\u8d8a\u4e0d\u50cf\u8bdd\u4e86\uff0c\u52a8\u4e0d\u52a8\u5c31\u8981\u6740\u4eba\uff0c\u8fd9\u5c11\u5e74\u624d \u548c\u9752\u5c11\u7237\u4e00\u6837\u5927\uff0c\u5c31\u7b97\u8001\u7237\u5b50\u4eb2\u773c\u770b\u89c1\u6211\u548c\u4ed6\u8bf4\u8bdd\uff0c\u4e5f\u4e0d\u4f1a\u600e\u7684\uff0c\u4f60\u4eec\u8fd9\u4e9b\u72d7\u4ed7\u4eba\u52bf\u7684\u5974 \u624d\uff0c\u5374\u6765\u591a\u4ec0\u4e48\u4e8b?\u82e5\u4e0d\u662f\u6211\u4e00\u53d1\u73b0\u4f60\u4eec\u4e0d\u5728\u5c31\u8d76\u4e86\u6765\uff0c\u4eba\u5bb6\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u5c82\u975e\u8981\u88ab\u4f60\u4eec\u4f24\u4e86 \u6027\u547d?\u201d \u3000\u3000\u5979\u9a82\u4e00\u53e5\uff0c\u90a3\u4e09\u6761\u5927\u6c49\u9762\u4e0a\u5162\u53d8\u8272\u4e00\u4e0b\uff0c\u5374\u6ca1\u6709\u4e00\u4e2a\u4eba\u6562\u62ac\u8d77\u5934\u6765\u3002\u53ea\u542c\u5979\u54fc\u4e86\u4e00\u58f0\uff0c \u53c8\u5428\u9053\uff1a\u201c\u8fd8\u4e0d\u8fdc\u8fdc\u5730\u6eda\u5f00\u53bb\uff01\u201d \u3000\u3000\u8fd9\u4e09\u6761\u5927\u6c49\u4fef\u8eab\u5782\u624b\uff0c\u4e00\u8fde\u9000\u4e86\u4e94\u6b65\uff0c\u624d\u4e00\u9f50\u62e7\u8f6c\u8eab\uff0c\u5934\u4e5f\u4e0d\u56de\u5730\u5f80\u6797\u5916\u5954\u53bb\uff0c\u8fde\u9a6c\u90fd \u5fd8\u8bb0\u7275\u5b9a\u4e86\u3002 \u3000\u3000\u5c55\u767d\u8eab\u4e0a\u7684\u5200\u4f24\uff0c\u867d\u7136\u75db\u5f7b\u5fc3\u813e\uff0c\u4f46\u77e5\u89c9\u4ecd\u672a\u5931\u53bb\uff0c\u773c\u770b\u8fd9\u7f8e\u5987\u7eb5\u90a3\u5927\u6c49\u4e00\u638c\uff0c\u5fc3\u4e2d\uff0c \u4e0d\u7981\u6697\u53eb\u201c\u60ed\u6127\u201d\uff0c\u4ed6\u672c\u4ee5\u4e3a\u8fd9\u5987\u4eba\u662f\u4e2a\u5f31\u4e0d\u7981\u98ce\u7684\u5bcc\u5ba4\u8d35\u5987\uff0c\u518d\u4e5f\u60f3\u4e0d\u5230\u4eba\u5bb6\u7684\u8eab\u624b\uff0c\u7adf \u8fdc\u8fdc\u9ad8\u51fa\u81ea\u5df1\u4e4b\u4e0a\uff0c\u800c\u81ea\u5df1\u5148\u524d\u5374\u8fd8\u8bf4\u8981\u51ed\u7740\u4e00\u4e9b\u529b\u6c14\uff0c\u6765\u5e2e\u4eba\u5bb6\u89e3\u51b3\u70e6\u607c\u3002 \u3000\u3000\u540e\u6765\u4ed6\u89c1\u5230\u8fd9\u5987\u4eba\u9762\u5e26\u79cb\u971c\uff0c\u4e00\u626b\u5148\u524d\u7684\u6e29\u67d4\u4e4b\u6001\uff0c\u5c06\u90a3\u4e09\u4e2a\u6b66\u529f\u751a\u9ad8\u7684\u52b2\u88c5\u5927\u6c49\uff0c\u9a82 \u5f97\u72d7\u8840\u7433\u5934\uff0c\u800c\u8fd9\u4e09\u4eba\u975e\u4f46\u4e0d\u6562\u8fd8\u53e3\uff0c\u5e76\u4e14\u754f\u60e7\u4e4b\u8272\uff0c\u8868\u9732\u65e0\u9057\uff0c\u5fc3\u91cc\u4e0d\u7981\u66f4\u611f\u5947\u602a\uff0c\u4e0d\u77e5 \u9053\u8fd9\u5987\u4eba\u7a76\u7adf\u662f\u4f55\u8bb8\u4eba\u7269\u3002 \u3000\u3000\u90a3\u4e2d\u5e74\u7f8e\u5987\u76ee\u9001\u90a3\u4e09\u6761\u5927\u6c49\u5982\u98de\u5954\u51fa\u6797\u5916\uff0c\u65b9\u59cb\u8f6c\u8fc7\u5934\u6765\uff0c\u8d70\u5230\u5c55\u767d\u8eab\u524d\u3002 \u3000\u3000\u5c55\u767d\u5f3a\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u591a\u8c22\u592b\u4eba\u642d\u6551\uff0c\u4e0d\u7136\u2026\u2026\u201d \u3000\u3000\u54ea\u77e5\u8bdd\u672b\u8bf4\u5b8c\uff0c\u8fd9\u4e2d\u5e74\u7f8e\u5987\u7a81\u5730\u6307\u7740\u4ed6\u53eb\u51fa\u4e00\u58f0\u201c\u54ce\u54df\u201d\u3002 \u3000\u3000\u5c55\u767d\u4e0d\u7981\u4e3a\u4e4b\u4e00\u695e\uff0c\u62ac\u773c\u671b\u53bb\uff0c\u53ea\u89c1\u8fd9\u4e2d\u5e74\u7f8e\u5987\u76ee\u5149\u4e4b\u4e2d\uff0c\u6ee1\u662f\u5173\u6000\u4e4b\u60c5\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a \u4f60\u4eec\u5e74\u8f7b\u4eba\u771f\u662f\u2026\u2026\u4f60\u77e5\u4e0d\u77e5\u9053\u4f60\u81ea\u5df1\u5df2\u6709\u75c5\u4e86?\u201d \u3000\u3000\u5c55\u767d\u53c8\u5f3a\u7b11\u4e00\u4e0b\uff0c\u5374\u89c1\u5979\u63a5\u7740\u53c8\u9053\uff1a\u65b9\u624d\u65e0\u8fd8\u6ca1\u6709\u770b\u51fa\u6765\uff0c\u4f46\u89c9\u5c31\u7b97\u4f60\u8eab\u4e8e\u662f\u597d\u597d\u7684\uff0c \u5728\u8fd9\u51cc\u6668\u9732\u91cd\u7684\u65f6\u5019\u7761\u5728\u8fd9\u91cc\uff0c\u5df2\u662f\u6781\u4e3a\u4e0d\u59a5\uff0c\u73b0\u5728\u2026\u2026\u5509\uff01\u8981\u662f\u98ce\u5bd2\u5165\u9aa8\uff0c\u5185\u5916\u4ea4\u4fb5\uff0c \u90a3\u2026\u2026\u201d \u3000\u3000\u5979\u8f7b\u8f7b\u53f9\u606f\u4e00\u58f0\uff0c\u4e2d\u6b62\u4e86\u81ea\u5df1\u7684\u8bdd\u3002 \u3000\u3000\u5c55\u767d\u53ea\u89c9\u5979\u8a00\u8bcd\u4e4b\u4e2d\uff0c\u6240\u542b\u7684\u6e29\u7559\u6148\u7965\uff0c\u7adf\u662f\u81ea\u5df1\u4e00\u751f\u4ece\u672a\u9886\u53d7\u8fc7\u7684\u3002\u4e00\u65f6\u4e4b\u95f4\uff0c\u5fc3\u4e2d \u6ee1\u542b\u611f\u64a4\u4e4b\u60c5\uff0c\u5446\u5446\u5730\u671b\u7740\u8fd9\u4e2d\u5e74\u7f8e\u5987\uff0c\u597d\u4e45\u597d\u4e45\u90fd\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u4ed6\u695e\u4e86\u534a\u664c\uff0c\u8f6c\u76ee\u671b\u53bb\uff0c\u53ea\u89c1\u9053\u4e0a\u5df2\u6709\u884c\u4eba\uff0c\u800c\u4e14\u50cf\u662f\u9a6c\u4e0a\u5c31\u8981\u8d70\u8fdb\u6811\u6797\u4e86\uff0c\u5fc3\u4e2d\u957f\u53f9 \u4e00\u58f0\uff0c\u5411\u90a3\u4e2d\u5e74\u7f8e\u5987\u957f\u63d6\u53ca\u5730\uff0c\u9053\uff1a\u201c\u5c0f\u53ef\u5b64\u96f6\u98d8\u6cca\uff0c\u592b\u4eba\u7adf\u5982\u6b64\u76f8\u5f85\uff0c\u5c0f\u53ef\u4e0d\u6562\u8a00\u62a5\uff0c\u53ea \u6709\u6df1\u94ed\u4e8e\u5fc3\uff0c\u7ec8\u751f\u4e0d\u5fd8\u3002\u201d \u3000\u3000\u4ed6\u8bed\u58f0\u5fae\u5fae\u4e00\u987f\uff0c\u53c8\u9053\uff1a\u201c\u53ea\u662f\u5c0f\u53ef\u8eab\u5b50\u5012\u8fd8\u7c97\u58ee\uff0c\u5c31\u7b97\u6709\u4e9b\u5fae\u4f24\u75db\uff0c\u4e5f\u8fd8\u652f\u6301\u5f97\u4f4f\uff0c \u592b\u4eba\u4e5f\u4e0d\u5fc5\u4ee5\u6b64\u4e3a\u5ff5\u3002\u201d\u90a3\u4e2d\u5e74\u7f8e\u5987\u8f7b\u8f7b\u6447\u4e86\u6447\u5934\uff0c\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\uff0c\u4f60\u5916\u8868\u770b\u6765\u867d\u7136\u8fd8 \u5201\uff1b\u600e\u6837\uff0c\u4f46\u76ee\u4e2d\u795e\u5149\u5df2\u6563\uff0c\u4f9d\u6211\u89c2\u5bdf\uff0c\u4f60\u4e0d\u4f46\u53d7\u4e86\u4f24\uff0c\u800c\u4e14\u4f24\u8fd8\u4e0d\u8f7b\uff0c\u4e60\u6b66\u4e4b\u4eba\uff0c\u4e0d\u75c5\u5219 \u5df2\uff0c\u4e00\u75c5\u4e0b\u6765\uff0c\u4fbf\u662f\u4e0d\u53ef\u6536\u62fe\uff01\u5509\uff0c\u4f60\u5e74\u7eaa\u8fd8\u8f7b\uff0c\u6709\u8bb8\u591a\u4e8b\u4f60\u8fd8\u4e0d\u77e5\u9053\uff0c\u6211\u7684\u8bdd\u4f60\u8be5\u542c\u542c\uff0c \u6211\u76f8\u4fe1\u6211\u7edd\u4e0d\u4f1a\u770b\u9519\u7684\u3002\u201d \u3000\u3000\u5c55\u767d\u5fc3\u4e2d\u4e00\u52a8\uff1a\u96be\u9053\u6211\u771f\u7684\u4f24\u5f97\u4e0d\u8f7b\u2026\u2026\u201d\u6697\u4e2d\u8bd5\u4e00\u8c03\u606f\uff0c\u679c\u7136\u53d1\u73b0\u80f8\u81c6\u6781\u4e0d\u8212\u7545\uff0c\u987b \u77e5\u4ed6\u5fc3\u4e2d\u79ef\u90c1\u672c\u6df1\uff0c\u867d\u4ed7\u7740\u5148\u5929\u4f53\u8d28\u6781\u4f73\uff0c\u5c1a\u672a\u75c5\u5012\uff0c\u4f46\u6628\u591c\u4ed6\u8fde\u906d\u5404\u79cd\u53d8\u6545\uff0c\u5fc3\u60c5\u5927\u5927\u6fc0 \u52a8\uff0c\u65b9\u624d\u53c8\u548c\u90a3\u4e09\u6761\u5927\u6c49\u4e00\u756a\u6fc0\u6597\uff0c\u53d7\u4e86\u5916\u4f24\uff0c\u6b63\u662f\u5185\u5916\u4ea4\u4fb5\uff0c\u773c\u770b\u5c31\u8981\u5012\u4e0b\u53bb\u4e86\uff0c\u53ea\u662f\u4e00 \u65f6\u4e4b\u95f4\uff0c\u4ed6\u81ea\u5df1\u8fd8\u672a\u89c9\u5bdf\u800c\u5df2\u3002\u90a3\u4e2d\u5e74\u7f8e\u5987\u8f7b\u53f9\u4e00\u58f0\uff0c\u53c8\u9053\uff1a\u4f60\u542c\u6211\u7684\u8bdd\uff0c\u83e0\u5feb\u56de\u5bb6\u2026\u2026\u6216 \u662f\u627e\u4e2a\u7ec6\u5fc3\u670b\u53cb\u4e4b\u5904\uff0c\u597d\u751f\u6b47\u606f\u4e9b\u65f6\u65e5\u3002\u201d \u3000\u3000\u5979\u8bf4\u7740\u4f38\u624b\u5165\u6000\uff0c\u53d6\u51fa\u4e00\u4e2a\u4e0a\u9762\u6ee1\u9576\u73e0\u5b9d\uff0c\u5236\u9020\u5f97\u6781\u4e3a\u7cbe\u5de7\u7684\u5c0f\u76d2\u5b50\uff0c\u7f13\u7f13\u6253\u5f00\uff0c\u975e\u5e38 \u614e\u91cd\u5730\u4ece\u91cc\u9762\u62ff\u51fa\u4e00\u4e2a\u8f6f\u7f0e\u5305\u7740\u7684\u5c0f\u5305\uff0c\u5c0f\u5fc3\u5730\u5c55\u4e86\u5f00\u6765\uff0c\u91cc\u9762\u7adf\u662f\u4e00\u7c92\u50cf\u662f\u7409\u7483\u822c\u7684\u8d64\u7ea2 \u4e39\u4e38\u3002\u5979\u7529\u62c7\u3001\u98df\u4e8c\u6307\uff0c\u5939\u8d77\u8fd9\u7c92\u4e39\u4e38\uff0c\u9001\u5230\u5c55\u767d\u9762\u524d\uff0c\u53c8\u9053\uff1a\u201c\u6211\u4e00\u65f6\u5927\u610f\uff0c\u4e0d\u77e5\u9053\u90a3\u4e9b \u8822\u6c49\u7adf\u662f\u5982\u6b64\u65e0\u804a\uff0c\u5bb3\u5f97\u4f60\u53d7\u4e86\u4f24\uff0c\u5509\u2026\u2026\u6211\u867d\u7136\u77e5\u9053\u4f60\u4e0d\u4f1a\u602a\u6211\uff0c\u4f46\u6211\u5fc3\u91cc\u8fd8\u662f\u96be\u53d7\u5f97 \u5f88\uff0c\u8fd9\u7c92\u836f\u4e38\u6211\u4fdd\u5b58\u4e86\u8bb8\u591a\u5e74\uff0c\u5bf9\u4f60\u4e5f\u8bb8\u6709\u4e9b\u7528\uff0c\u4f60\u62ff\u53bb\u5403\u4e86\u5427\uff01\u201d \u3000\u3000\u5c55\u767d\u7f13\u7f13\u4f38\u51fa\u624b\u638c\uff0c\u63a5\u4e86\u8fc7\u6765\uff0c\u53ea\u89c1\u8fd9\u7c92\u8d64\u7ea2\u7684\u4e39\u4e38\uff0c\u5728\u81ea\u5df2\u638c\u5fc3\u4e0d\u4f4f\u5730\u6eda\u52a8\u7740\uff0c\u5fc3\u4e2d \u60f3\u5230\u81ea\u5df2\u7684\u4e00\u751f\u906d\u9047\uff0c\u4e0d\u89c9\u60b2\u4ece\u4e2d\u6765\uff0c\u8bb7\u8bb7\u8bf4\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u6ca1\u6709\u5bb6\uff0c\u4e5f\u2026\u2026\u4e5f\u6ca1\u6709\u670b\u53cb\uff0c \u6211\u6ca1\u6709\u5bb6\u2026\u2026\u4e5f\u6ca1\u6709\u670b\u53cb\u3002\u201d\u5fc3\u80f8\u4e4b\u4e2d\uff0c\u60b2\u62a2\u4e0d\u5df2\uff0c\u70ed\u8840\u7ffb\u6d8c\uff0c\u4f46\u89c9\u8ddf\u524d\u8fd9\u7c92\u8d64\u7ea2\u4e39\u4e38\uff0c\u8d8a \u6eda\u8d8a\u5feb\uff0c\u7ade\u53d8\u5f97\u4e00\u7247\u8d64\u7ea2\uff0c\u50cf\u662f\u6709\u4e00\u56e2\u706b\uff0c\u5728\u81ea\u5df2\u56db\u5468\u71c3\u70e7\u7740\uff0c\u201c\u54c7\u201d\u5730\u4e00\u58f0\uff0c\u5f20\u53e3\u5410\u51fa\u4e00 \u53e3\u9c9c\u8840\u6765\uff0c\u95ed\u76ee\u6643\u4e86\u4e24\u6643\uff0c\u7ec8\u4e8e\u5012\u4e86\u4e0b\u53bb\u3002\u8033\u77ac\u4f46\u542c\u8925\u90a3\u4e2d\u5e74\u7f8e\u5987\u60ca\u547c\u4e86\u4e00\u58f0\uff0c\u4fbf\u4ec0\u4e48\u90fd\u4e0d \u77e5\u9053\u4e86\u3002 \u7b2c\u4e94\u7ae0\u3000\u201c\u51cc\u98ce\u516c\u5b50\u201d\u65e0\u60c5\u5ba2>> \u53e4\u9f99\u300a\u5251\u5ba2\u884c\u300b \u7b2c\u4e94\u7ae0\u3000\u201c\u51cc\u98ce\u516c\u5b50\u201d\u65e0\u60c5\u5ba2 \u3000\u3000\u5c55\u767d\u660f\u8ff7\u4e4b\u4e2d\uff0c\u53ea\u89c9\u8f66\u58f0\u78f7\u78f7\uff0c\u98a0\u7c38\u4e0d\u5df2\uff0c\u53c8\u4f3c\u95fb\u6c34\u58f0\u6dd9\u6dd9\uff0c\u4eff\u4f5b\u5728\u6c34\u4e0a\uff0c\u4f46\u8111\u4e2d\u5374\u59cb \u7ec8\u662f\u4e00\u7247\u6df7\u5a49\u3002\u6709\u65f6\u89c9\u5f97\u81ea\u5df1\u53c8\u56de\u5230\u8bb8\u4e45\u8bb8\u4e45\u4ee5\u524d\uff0c\u8fd8\u8eba\u5728\u5988\u5988\u90a3\u6e29\u6696\u7684\u6000\u62b1\u91cc\uff1b\u6709\u65f6\u53c8\u89c9 \u5f97\u81ea\u5df2\u85cf\u624b\u7a7a\u62f3\uff0c\u6b63\u5728\u548c\u65e0\u6570\u4e2a\u624b\u6301\u5229\u5251\u7684\u6076\u9b54\u62fc\u547d\u6fc0\u6597\u3002\u81ea\u5df2\u4e00\u4f1a\u89c1\u5c06\u8fd9\u4e9b\u6076\u9b54\u5168\u90fd\u6253 \u8dd1\uff0c\u4f46\u4e00\u4f1a\u513f\u53c8\u88ab\u8fd9\u4e9b\u6076\u9b54\u6253\u5012\u5730\u4e0a\uff0c\u90a3\u65e0\u6570\u989d\u5229\u5251\u5c31\u5728\u81ea\u5df1\u8eab\u4e0a\u4e00\u5206\u4e00\u5bf8\u5730\u5207\u5272\u8d77\u6765\u3002 \u3000\u3000\u7ec8\u4e8e\uff0c\u4e00\u5207\u58f0\u97f3\u5f52\u4e8e\u9759\u5bc2\uff0c\u4e00\u5207\u5e7b\u8c61\u4e5f\u5168\u90fd\u6d88\u5931\u3002 \u3000\u3000\u4ed6\u832b\u7136\u7741\u5f00\u773c\u6765\uff0c\u80f8\u4e2d\u7a7a\u7a7a\u6d1e\u6d1e\u7684\uff0c\u773c\u524d\u4e5f\u8fd8\u662f\u4e00\u7247\u7a7a\u767d\uff0c\u8fd9\u4e9b\u5929\u6765\uff0c\u4ed6\u4e00\u76f4\u5728\u6d51\u745f\u4e2d \u5ea6\u8fc7\uff0c\u6b64\u523b\u81ea\u7136\u96be\u514d\u6709\u8fd9\u79cd\u73b0\u8c61\u3002\u76f4\u5230\u65f6\u95f4\u53c8\u8fc7\u53bb\u8bb8\u4e45\uff0c\u4ed6\u5446\u6ede\u7684\u76ee\u5149\uff0c\u624d\u7565\u4e3a\u8f6c\u52a8\u4e00\u4e0b\uff0c \u8fd9\u65f6\u5019\u4e00\u5207\u4ed6\u89c6\u89c9\u6240\u89c1\u4e4b\u7269\uff0c\u624d\u80fd\u6e05\u695a\u5730\u6620\u5165\u8111\u4e2d\u3002 \u3000\u3000\u4ed6\u8d6b\u7136\u53d1\u73b0\u81ea\u5df2\u7adf\u662f\u5904\u8eab\u5728\u4e00\u95f4\u7cbe\u81f4\u534e\u8d35\u65e0\u6bd4\u7684\u623f\u95f4\u91cc\uff0c\u6df1\u7684\u65c1\u8fb9\uff0c\u653e\u7740\u4e00\u4e2a\u8336\u51e0\uff0c\u901a \u4f53\u662f\u78a7\u7389\u6240\u5236\uff0c\u8336\u51e0\u4e0a\u4e00\u53ea\u91d1\u7897\uff0c\u4e00\u7f15\u6de1\u70df\u6613\u88ad\u5347\u8d77\uff0c\u4ecd\u5728\u4e0d\u65ad\u5730\u53d1\u7740\u5e7d\u9999\u3002 \u3000\u3000\u4e8e\u662f\u5343\u767e\u79cd\u7d0a\u4e71\u7684\u601d\u6f6e\uff0c\u8fd9\u4e00\u5239\u90a3\u95f4\uff0c\u4fbf\u5728\u4ed6\u7a7a\u865a\u7684\u8111\u6d77\u4e2d\u7ffb\u6d8c\u8d77\u6765\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\u5730\u65b9? \u6211\u7a76\u7adf\u600e\u7684\u4e86?\u8fd9\u662f\u600e\u4e48\u56de\u4e8b?\u6211\u4e0d\u662f\u968f\u7740\u9556\u8f66\u2026\u2026\u54e6\uff0c\u4e0d\u5bf9\uff0c\u6211\u65e9\u5df3\u79bb\u5f00\u4ed6\u4eec\u3002\u201d \u3000\u3000\u56e0\u4e4b\u90a3\u5929\u665a\u4e0a\u6240\u53d1\u751f\u7684\u6bcf\u4e00\u4ef6\u4e8b\uff0c\u4f7f\u4e00\u5e55\u4e00\u5e55\u5730\u5728\u4ed6\u8111\u4e2d\u6620\u73b0\u4e86\u51fa\u6765\u3002 \u3000\u3000\u4ed6\u8bb0\u8d77\u4e86\u201c\u71d5\u4e91\u4e94\u9738\u5929\u201d\u52ab\u9556\uff0c\u8bb0\u8d77\u4e86\u5b89\u4e50\u516c\u5b50\u4ed7\u4e49\u51fa\u624b\uff0c\u4e5f\u8bb0\u8d77\u4e86\u90a3\u53ea\u91cc\u9762\u653e\u7740\u6885\u6c64 \u7684\u7ec6\u74f7\u76d6\u7897\uff0c\u8bb0\u8d77\u4e86\u90a3\u201c\u8ffd\u98ce\u65e0\u5f71\u201d\u534e\u6e05\u6cc9\u795e\u79d8\u7684\u6b7b\u3002\u8bb0\u8d77\u4e86\u81ea\u5df1\u624b\u4e2d\u4e4b\u5251\u7adf\u88ab\u90a3\u795e\u79d8\u7684\u4eba \u5f71\u593a\u53bb\uff0c\u53c8\u8bb0\u8d77\u4e86\u90a3\u8be1\u5f02\u7684\u5947\u4eba\uff0c\u795e\u79d8\u7684\u4e2d\u4e2d\u7f8e\u5987\u548c\u5979\u6148\u7965\u7684\u7b11\u5bb9\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u4e5f\u8bb0\u8d77\u660f\u8ff7\u524d\u7684\u90a3\u4e00\u5239\u90a3\uff0c\u4ed6\u77e5\u9053\u5f53\u81ea\u5df2\u660f\u8ff7\u4e4b\u540e\uff0c\u4e00\u5b9a \u3000\u3000\u201c\u4f46\u662f\uff0c\u5979\u7a76\u7adf\u53c8\u662f\u4ec0\u4e48\u4eba\u7269\u5462?\u201d\u4e00\u773c\u671b\u53bb\uff0c\u4efb\u4f55\u4eba\u90fd\u4f1a\u5c06\u5979\u770b\u6210\u4e00\u4f4d\u9ad8\u5bab\u7684\u547d\u5987\uff0c \u6216\u8005\u662f\u5de8\u5bcc\u7684\u592b\u4eba\uff0c\u4f46\u662f\u5f53\u4ed6\u60f3\u8d77\u90a3\u5b88\u62a4\u5728\u8f66\u65c1\u7684\u4e09\u4e2a\u5927\u6c49\uff0c\u60f3\u8d77\u5979\u548c\u8fd9\u4e09\u6761\u5927\u6c49\u6240\u8bf4\u7684 \u8bdd\uff0c\u60f3\u8d77\u5f53\u5979\u5c06\u81ea\u5df1\u4ece\u8fd9\u4e09\u6761\u5927\u6c49\u624b\u4e2d\u6551\u51fa\u65f6\u6240\u65bd\u5c55\u7684\u90a3\u79cd\u60ca\u4eba\u7684\u8eab\u6cd5\uff0c\u4e0d\u7981\u53c8\u4e3a\u4e4b\u832b\u7136\u3002 \u3000\u3000\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u4ed6\u53ea\u89c9\u81ea\u5df2\u7684\u601d\u6f6e\u8d8a\u6765\u8d8a\u4e71\uff0c\u8bd5\u4e00\u6323\u624e\u5750\u8d77\uff0c\u5168\u8eab\u7adf\u662f\u8f6f\u8f6f\u7684\u6ca1\u6709\u4e00 \u4e1d\u529b\u9053\uff0c\u957f\u53f9\u4e00\u58f0\uff0c\u4fa7\u76ee\u671b\u53bb\uff0c\u53ea\u89c9\u7a97\u5916\u6708\u8272\u751a\u660e\uff0c\u9ad8\u9ad8\u5730\u6302\u5728\u67f3\u6811\u68a2\u5934\uff0c\u6708\u5149\u6d12\u6ee1\u7a97\u7eb8\uff0c \u6620\u5165\u623f\u4e2d\uff0c\u7167\u5f97\u5e8a\u524d\u5730\u4e0a\uff0c\u5448\u73b0\u51fa\u4e00\u7247\u94f6\u8272\u5149\u534e\u3002 \u3000\u3000\u201c\u5047\u82e5\u7239\u7239\u4e0d\u6b7b\uff0c\u90a3\u4e48\u751f\u6d3b\u662f\u591a\u4e48\u7684\u7f8e\u5440\uff01\u6b64\u523b\u6211\u4e5f\u8bb8\u8fd8\u548c\u65e7\u65f6\u4e00\u6837\uff0c\u548c\u90a3\u53ea\u82b1\u732b\u4e00\u9f50 \u8eba\u5728\u5c4b\u89d2\u7684\u659c\u9633\u4e4b\u4e0b\uff0c\u5509\u2026\u2026\u7239\u7239\uff0c\u4f60\u4e34\u6b7b\u7684\u65f6\u5019\uff0c\u5212\u5207\u8fd8\u5c06\u5bb3\u6b7b\u4f60\u7684\u4eba\u5230\u5e95\u662f\u8c01\u544a\u8bc9\u6211\u5440? \u5509\u2026\u2026\u7eb5\u7136\u6211\u77e5\u9053\u4e86\u53c8\u600e\u6837\uff01\u6211\u2026\u2026\u6211\u53ea\u662f\u4e00\u4e2a\u65e0\u7528\u7684\u4eba\uff0c\u6211\u8fde\u7239\u7239\u7684\u9057\u7269\u90fd\u4e0d\u80fd\u4fdd\u7559\uff0c\u53c8 \u600e\u80fd\u4e3a\u4ed6\u8001\u4eba\u5bb6\u590d\u4ec7\u3002\u201d \u3000\u3000\u4e00\u65f6\u4e4b\u95f4\uff0c\u4ed6\u5fc3\u80f8\u4e2d\u53c8\u88ab\u60b2\u62a2\u5835\u585e\uff0c\u7981\u4e0d\u4f4f\u518d\u6b21\u957f\u53f9\u4e00\u58f0\uff0c\u5f20\u5f00\u773c\u6765\u3002\u54ea\u77e5\u76ee\u5149\u52a8\u5904\uff0c \u5374\u89c1\u5230\u4e00\u53cc\u51b0\u51b7\u7684\u76ee\u5149\uff0c\u6b63\u77ac\u4e5f\u4e0d\u77ac\u5730\u671b\u5728\u81ea\u5df1\u8eab\u4e0a\u3002 \u3000\u3000\u5c4b\u91cc\u6ca1\u6709\u706f\u5149\uff0c\u4f46\u7a97\u5916\u6708\u660e\u5982\u6d17\uff0c\u6708\u5149\u4e4b\u4e0b\uff0c\u53ea\u89c1\u8fd9\u4eba\u7a7f\u7740\u4e00\u88ad\u6de1\u84dd\u7684\u4e1d\u888d\uff0c\u957f\u8eab\u7389 \u7acb\uff0c\u795e\u60c5\u6f47\u6d12\u5df2\u6781\uff0c\u9762\u76ee\u6781\u4e3a\u82f1\u4fca\uff0c\u53ea\u662f\u5634\u89d2\u4e0b\u6487\uff0c\u5728\u6708\u5149\u4e4b\u4e2d\uff0c\u4e5f\u51b7\u68ee\u68ee\u5730\u5e26\u7740\u4e00\u4efd\u5bd2 \u610f\u3002 \u3000\u3000\u5c55\u767d\u5fc3\u5934\u4e00\u8df3\uff0c\u4ed6\u867d\u5728\u75c5\u4e2d\uff0c\u81ea\u60c5\u8033\u76ee\u8fd8\u662f\u6781\u4e3a\u7075\u654f\uff0c\u751a\u81f3\u7a97\u5916\u79cb\u866b\u7684\u4f4e\u9e23\uff0c\u4ed6\u90fd\u80fd\u6781 \u4e3a\u6e05\u695a\u5730\u542c\u51fa\uff0c\u4f46\u8fd9\u4eba\u4ece\u4f55\u800c\u6765\uff0c\u4f55\u65f6\u800c\u6765\uff0c\u4ed6\u5374\u4e00\u70b9\u4e5f\u4e0d\u77e5\u9053\uff0c\u8fd9\u82f1\u4fca\u3001\u6f47\u6d12\uff0c\u5374\u53c8\u68ee \u51b7\u3001\u5014\u50b2\u7684\u5c11\u5e74\uff0c\u5c31\u50cf\u5e7d\u7075\u4f3c\u7684\uff0c\u7a81\u7136\u51fa\u73b0\u5728\u4ed6\u773c\u524d\u3002 \u3000\u3000\u8fd9\u5c11\u4e2d\u76ee\u5149\u51db\u51db\u4e00\u626b\uff0c\u7f13\u6b65\u8d70\u5230\u5e8a\u524d\uff0c\u68ee\u51b7\u5730\u8f7b\u5428\u4e00\u58f0\uff0f\u4f60\u662f\u8c01?\u201d \u3000\u3000\u5c55\u767d\u4e00\u508d\uff0c\u968f\u5373\u9053\uff1a\u201c\u5c0f\u53ef\u2026\u2026\u201d \u3000\u3000\u54ea\u77e5\u8fd9\u5c11\u5e74\u53cc\u76ee\u4e00\u7ffb\uff0c\u6839\u672c\u4e0d\u7406\u776c\u4ed6\u7684\u7b54\u8bdd\uff0c\u53c8\u51b7\u53f1\u9053\uff1a\u201c\u4e0d\u7ba1\u4f60\u662f\u8c01\uff0c\u5feb\u7ed9\u6211\u6eda\u51fa \u53bb\uff01\u201d \u3000\u3000\u5c55\u767d\u4e0d\u7531\u5fc3\u4e2d\u5927\u6012\uff0c\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u9601\u4e0b\u53c8\u662f\u4f55\u4eba?\u5c0f\u53ef\u4e0e\u9601\u4e0b\u7d20\u4e0d\u76f8\u8bc6\uff0c\u8bf7\u9601\u4e0b\u8bf4 \u8bdd\uff0c\u8fd8\u662f\u653e\u5c0a\u91cd\u4e9b\uff01\u201d \u3000\u3000\u90a3\u5c11\u5e74\u76ee\u5149\u5982\u5229\u5251\u822c\u51dd\u6ce8\u5728\u4ed6\u7684\u8138\u4e0a\uff0c\u9762\u4e0a\u6728\u7136\u6ca1\u6709\u4efb\u4f55\u8868\u60c5\uff0c\u6709\u5982\u6ce5\u5851\u4e00\u822c\uff0c\u53e3\u4e2d\u5374 \u51b7\u7b11\u4e00\u58f0\uff0c\u4e00\u5b57\u4e00\u5b57\u5730\u7f13\u7f13\u8bf4\u9053\uff0c\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u6211\u662f\u8c01?\u4f60\u77e5\u4e0d\u77e5\u9053\u8fd9\u662f\u4ec0\u4e48\u5730\u65b9?\u201d \u3000\u3000\u5c55\u767d\u4e0d\u7981\u53c8\u662f\u4e00\u695e\uff0c\u6697\u95ee\u81ea\u5df1\uff1a\u6b64\u4eba\u662f\u8c01?\u8fd9\u662f\u4ec0\u4e48\u5730\u65b9\uff0c\u96be\u9053\u4ed6\u5c31\u662f\u8fd9\u91cc\u7684\u4e3b\u4eba?\u90a3\u4e48 \u90a3\u9ad8\u8d35\u7684\u5987\u4eba\uff0c\u600e\u4f1a\u5c06\u6211\u5e26\u5230\u8fd9\u91cc\u6765\u800c\u4ed6\u5374\u4e0d\u77e5\u9053?\u201d \u3000\u3000\u5fc3\u5ff5\u6570\u8f6c\uff0c\u6012\u6c14\u6e10\u6d88\uff0c\u7591\u4e91\u5374\u53c8\u5927\u8d77\uff0c\u6323\u624e\u7740\u60f3\u652f\u6491\u5750\u8d77\uff0c\u4f46\u529b\u4e0d\u4ece\u5fc3\uff0c\u53c8\u6251\u5730\u5012\u5728\u5e8a \u4e0a\u3002 \u3000\u3000\u90a3\u5c11\u4e2d\u76ee\u5149\uff0c\u4f3c\u4e4e\u4e5f\u5927\u4e3a\u60ca\u5f02\uff0c\u51b7\u54fc\u4e00\u58f0\u9053\uff1a\u201c\u539f\u6765\u4f60\u53d7\u4e86\u4f24\uff0c\u90a3\u4e48\uff0c\u53c8\u662f\u8c01\u5c06\u4f60\u5e26\u6765 \u6b64\u5730\u7684?\u201d \u3000\u3000\u888d\u8896\u4e00\u62c2\uff0c\u8d70\u5230\u90a3\u78a7\u7389\u5c0f\u51e0\u4e4b\u524d\uff0c\u5c06\u51e0\u4e0a\u7684\u91d1\u8272\u9999\u7089\u79fb\u52a8\u4e00\u2606\u4e0b\uff0c\u653e\u5f97\u6b63\u4e86\u4e9b\uff0c\u53c8\u51b7\u54fc \u4e00\u58f0\uff0c\u4f4e\u8bed\u9053\uff1a\u201c\u7adf\u5c06\u6211\u7684\u9f99\u6d8e\u9999\u90fd\u70b9\u4e86\u8d77\u6765\u3002\u201d \u3000\u3000\u5c55\u767d\u5fc3\u4e2d\u4e00\u52a8\uff0c\u8131\u66f0\u9053\uff1a\u201c\u9601\u4e0b\u662f\u5426\u6b64\u5730\u7684\u4e3b\u4eba?\u201d \u3000\u3000\u90a3\u5c11\u5e74\u51b7\u7b11\u4e00\u58f0\uff0c\u63a5\u53e3\u9053\uff1a\u201c\u6211\u4e0d\u662f\u6b64\u5730\u7684\u4e3b\u4eba\uff0c\u54fc\u54fc\uff0c\u96be\u9053\u4f60\u662f\u6b64\u5730\u7684\u4e3b\u4eba\u4e0d\u6210!\u201d \u3000\u3000\u5c55\u81ea\u5fc3\u4e2d\u6697\u53eb\u4e00\u58f0\uff1a\u201c\u60ed\u6127!\u201d \u3000\u3000\u975e\u4f46\u518d\u65e0\u6012\u706b\uff0c\u53cd\u89c9\u6b49\u7136\uff0c\u6cb9\u6cb9\u8bf4\u9053\uff1a\u201c\u5c0f\u53ef\u5b9e\u5728\u4e0d\u77e5\u6b64\u5904\u662f\u4f55\u5730\uff0c\u4e5f\u4e0d\u77e5\u662f\u600e\u4e48\u6765 \u7684\uff0c\u9601\u4e0b\u82e5\u662f\u6b64\u5730\u7684\u4e3b\u4eba\uff0c\u53ea\u77a5\u5c06\u5c0f\u53ef\u62ac\u51fa\u53bb\u4fbf\u662f\uff0c\u5509!\u5c0f\u53ef\u2026\u2026\u201d \u3000\u3000\u90a3\u5c11\u5e74\u53cc\u76ee\u2014\u2014\u5f20\uff0c\u51b7\u665a\u9053\uff1a\u201c\u4f60\u4e0d\u77e5\u9053\u8fd9\u662f\u4ec0\u4e48\u5730\u65b9?\u4e5f\u4e0d\u77e5\u9053\u662f\u600e\u4e48\u6765\u7684?\u54fc\u54fc!\u201d \u3000\u3000\u7a81\u7136\u56de\u8fc7\u8eab\u6765\uff0c\u5389\u53f1\u4e00\u58f0\uff1a\u201c\u4e0d\u7ba1\u4f60\u6709\u4f24\u65e0\u4f24\uff0c\u6709\u75c5\u65e0\u75c5\uff0c\u5feb\u4e9b\u7ed9\u6211\u6eda\u51fa\u53bb!\u82e5\u662f\u7b49\u5230 \u6211\u4eb2\u81ea\u51fa\u624b\uff0c\u54fc\u7741\uff0c\u90a3\u4f60\u5c31\u60e8\u4e86!\u201d \u3000\u3000\u5c55\u767d\u6697\u53f9\u4e00\u58f0\uff0c\u4ed6\u6b64\u523b\u5fc3\u4e2d\u867d\u53c8\u6012\u706b\u5927\u4f5c\uff0c\u4f46\u8f6c\u5ff5\u4e00\u60f3\uff0c\u8fd9\u91cc\u82e5\u662f\u522b\u4eba\u7684\u5c45\u5904\uff0c\u800c\u81ea\u5df1 \u5374\u7cca\u91cc\u7cca\u6d82\u5730\u7761\u5728\u4eba\u5bb6\u5e8a\u4e0a\uff0c\u81ea\u7136\u96be\u602a\u4eba\u5bb6\u4e0d\u6ee1\uff0c\u4fbf\u53c8\u5c06\u5fc3\u4e2d\u6012\u706b\u64a9\u4e0b\u53bb\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u9601\u4e0b \u82e5\u662f\u6b64\u5730\u7684\u4e3b\u4eba\uff0c\u5c0f\u53ef\u81ea\u5e94\u79bb\u53bb\uff0c\u53ea\u662f\u5c0f\u53ef\u6b64\u6765\uff0c\u5b9e\u975e\u51fa\u4e8e\u672c\u610f\uff0c\u9601\u4e0b\u53c8\u4f55\u82e6\u5982\u6b64\u5484\u5484\u903c \u4eba\uff01\u201d \u3000\u3000\u90a3\u5c11\u5e74\u5251\u7709\u4e00\u8f69\uff0c\u5389\u53f1\u9053\uff1a\u201c\u4e00\u76cf\u8336\u4e4b\u5185\uff0c\u4f60\u82e5\u4e0d\u5feb\u4e9b\u6eda\u51fa\u53bb\uff0c\u672c\u516c\u5b50\u7acb\u65f6\u4fbf\u8ba9 \u4f60\u2026\u2026\u201d \u3000\u3000\u5c55\u767d\u7eb5\u662f\u6781\u529b\u5fcd\u8010\uff0c\u6b64\u523b\u4ea6\u4e0d\u89c9\u6c14\u5f80\u4e0a\u649e\uff0c\u63a5\u53e3\u9053\uff1a\u201c\u9601\u4e0b\u7eb5\u7136\u80fd\u7279\u4e00\u4e2a\u624b\u4e0d\u80fd\u52a8\u3001\u8eab \u4e0d\u80fd\u79fb\u7684\u75c5\u4eba\u4f24\u5728\u638c\u4e0b\uff0c\u4e5f\u7b97\u4e0d\u5f97\u4ec0\u4e48\u82f1\u96c4\u3002\u201d \u3000\u3000\u90a3\u5c11\u5e74\u76ee\u5149\u4e00\u51db\uff0c\u7a81\u5730\u8fde\u58f0\u51b7\u7b11\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u4f60\u82e5\u672a\u75c5\uff0c\u6211\u5c31\u65e0\u6cd5\u4f24\u4f60\u4e86?\u201d \u3000\u3000\u5c55\u767d\u4e5f\u51b7\u7b11\u9053\uff1a\u201c\u8fd9\u4e2a\u4ea6\u672a\u53ef\u77e5\u3002\u201d \u3000\u3000\u4ed6\u672c\u975e\u8a00\u8bed\u5c16\u523b\u4e4b\u4eba\uff0c\u4f46\u6b64\u523b\u5374\u88ab\u8fd9\u5c11\u5e74\u6fc0\u5f97\u53e3\u9f7f\u950b\u5229\u8d77\u6765\uff0c\u5fc3\u4e2d\u672c\u60f3\u8bf4\u51fa\u81ea\u5df1\u6765\u5230\u6b64 \u5730\uff0c\u5927\u7ea6\u662f\u88ab\u90a3\u4e2d\u5e74\u8d35\u5987\u5e26\u6765\uff0c\u4f46\u81ea\u5df2\u5374\u8fde\u4eba\u5bb6\u7684\u59d3\u540d\u6765\u5386\u90fd\u4e0d\u77e5\u9053\uff0c\u60f3\u8d77\u90a3\u4e09\u6761\u5927\u6c49\u548c\u5979 \u7684\u5bf9\u8bdd\uff0c\u66f4\u6015\u66ff\u90a3\u4e2d\u4e2d\u8d35\u5987\u5e26\u6765\u9ebb\u70e6\u3002 \u3000\u3000\u6697\u9053\u4e00\u58f0\uff1a\u201c\u5c55\u767d\u5440\u5c55\u767d\uff0c\u4f60\u5b81\u53ef\u88ab\u8fd9\u5c11\u5e74\u6454\u51fa\u623f\u53bb\uff0c\u4e5f\u4e07\u4e07\u4e0d\u53ef\u8fde\u7d2f\u4eba\u5bb6!\u201d \u3000\u3000\u53ea\u662f\u4ed6\u5374\u672a\u60f3\u5230\uff0c\u4ed6\u82d7\u771f\u7684\u662f\u88ab\u90a3\u4e2d\u5e74\u8d35\u5987\u5e26\u6765\u6b64\u95f4\uff0c\u90a3\u4e48\u90a3\u4e2d\u5e74\u8d35\u5987\u5fc5\u5b9a\u6709\u7740\u539f\u56e0\uff0c \u5979\u548c\u8fd9\u5c11\u5e74\u4e5f\u5fc5\u5173\u7cfb\u5f02\u5e38\u5bc6\u5207\uff0c\u5426\u5219\u600e\u4f1a\u5982\u6b64? \u3000\u3000\u90a3\u5c11\u5e74\u76ee\u5149\u8f6c\u4e86\u51e0\u8f6c\uff0c\u7a81\u5730\u8d70\u5230\u5c55\u81ea\u8eab\u524d\u5750\u4e86\u4e0b\u6765\uff0c\u4f38\u624b\u628a\u4f4f\u5c55\u767d\u7684\u8109\u95e8\uff0c\u5c55\u767d\u5fc3\u4e2d\u65e2 \u60ca\u4e14\u5947\uff0c\u4f46\u5468\u8eab\u65e0\u529b\uff0c\u6839\u672c\u65e0\u6cd5\u6297\u62d2\uff0c\u53ea\u5f97\u7531\u4ed6\u6349\u4f4f\u624b\u8155\uff0c\u62ac\u76ee\u671b\u6765\uff0c\u5374\u89c1\u8fd9\u5c11\u5e74\u7709\u5fc3\u6df1 \u76b1\uff0c\u53f3\u624b\u4e00\u52a8\uff0c\u53c8\u5c06\u81ea\u5df1\u7684\u53e6\u4e00\u53ea\u624b\u8155\u6293\u4f50\uff0c\u6c89\u541f\u534a\u664c\uff0c\u76ee\u4e2d\u7ade\u73b0\u51fa\u60ca\u5f02\u4e4b\u8272\uff0c\u8d77\u8eab\u5728\u5c4b\u5185 \u8f6c\u4e86\u4e24\u8f6c\uff0c\u888d\u8896\u4e00\u62c2\uff0c\u4e00\u8a00\u4e0d\u53d1\u5730\u8f6c\u8eab\u8d70\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u5c55\u767d\u76ee\u9001\u4ed6\u8eab\u5f71\u6d88\u5931\uff0c\u5fc3\u4e2d\u4e0d\u7981\u5927\u5947\uff0c\u6697\u6697\u5fd6\u9053:\u8fd9\u5c11\u5e74\u672c \u6765\u53eb\u6211\u7acb\u5373\u79bb\u5f00\u8fd9\u91cc\uff0c\u600e\u5730 \u5fae\u5fae\u628a\u4e86\u628a\u6211\u7684\u8109\uff0c\u5c31\u4e00\u8a00\u4e0d\u53d1\u5730\u8f6c\u8eab\u8d70\u4e86?\u201d \u3000\u3000\u53c8\u5fd6\u9053\uff1a\u201c\u6211\u6b64\u523b\u5468\u8eab\u5e76\u4e0d\u75db\u82e6\uff0c\u5374\u53c8\u6ca1\u6709\u4e00\u4e1d\u529b\u6c14\uff0c\u8fd9\u4e9b\u5929\u6765\uff0c\u6211\u5931\u53bb\u77e5\u89c9\uff0c\u7406\u5e94\u75c5 \u5f97\u4e0d\u8f7b\uff0c\u4f46\u6b64\u523b\u6211\u600e\u7684\u8fde\u4e00\u70b9\u75c5\u540e\u90a3\u79cd\u96be\u53d7\u7684\u611f\u89c9\u90fd\u6ca1\u6709?\u201d \u3000\u3000\u60f3\u6765\u60f3\u53bb\uff0c\u53ea\u89c9\u81ea\u5df2\u8fd9\u4e9b\u65e5\u5b50\u6240\u9047\u4e4b\u4e8b\uff0c\u7adf\u7136\u5168\u90fd\u5927\u8d85\u5e38\u60c5\u4e4b\u5916\uff0c\u65e0\u4e00\u80fd\u4ee5\u5e38\u7406\u63e3\u6d4b\uff0c \u4fbf\u7d22\u6027\u5c06\u8fd9\u4e9b\u4e8b\u629b\u5728\u4e00\u8fb9\uff0c\u518d\u4e5f\u4e0d\u51fb\u60f3\u5b83\u3002\u6d41\u76ee\u56db\u987e\uff0c\u53ea\u89c1\u7a97\u5916\u5ead\u9662\u6df1\u6c89\uff0c\u67f3\u4e1d\u968f\u98ce\u98d8\u821e\uff0c \u5c4b\u5185\u9999\u6c14\u9635\u9635\uff0c\u9648\u8bbe\u9ad8\u96c5\u3002\u4ed6\u8eab\u4e16\u5b64\u82e6\uff0c\u51e0\u66fe\u5230\u8fc7\u8fd9\u79cd\u5730\u65b9\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u66f4\u89c9\u90a3\u4e2d\u5e74\u7f8e\u5987\u548c \u8fd9\u5014\u50b2\u5c11\u5e74\u7684\u6765\u5386\u4e0d\u53ef\u601d\u8bae\uff0c\u5fc3\u91cc\u867d\u544a\u8bc9\u81ea\u5df1\u4e0d\u8981\u53bb\u60f3\u8fd9\u4e9b\u4e0e\u81ea\u5df1\u672c\u65e0\u5e72\u7cfb\u4e4b\u4e8b\uff0c\u4f46\u601d\u7eea\u7d22 \u4e71\uff0c\u5374\u53c8\u65e0\u6cd5\u4e0d\u51fb\u60f3\u5b83\u3002 \u3000\u3000\u4ed6\u5fc3\u5ff5\u529b\u81ea\u8f6c\u4e86\u6570\u8f6c\uff0c\u54ea\u77e5\u95e8\u5916\u7a81\u53c8\u4f20\u4eba\u90a3\u5014\u50b2\u5c11\u5e74\u51b0\u51b7\u7684\u58f0\u97f3\uff1a\u6700\u8fd1\u5929\u6c14\u592a\u70ed\uff0c\u4f60\u4eec \u60f3\u5fc5\u61d2\u5f97\u505a\u4e8b\uff0c\u6211\u770b\uff0c\u4f60\u4eec\u771f\u8be5\u6b47\u6b47\u590f\u4e86\uff0e\u201d \u3000\u3000\u8bed\u58f0\u843d\u5904\uff0c\u95e8\u53e3\u4eba\u5f71\u5fae\u52a8\uff0c\u90a3\u5014\u50b2\u5c11\u5e74\uff0c\u4f7f\u53c8\u8d1f\u624b\u8d70\u4e86\u8fdb\u6765\uff0c\u53cc\u773c\u5fae\u5fae\u4e0a\u7ffb\uff0c\u9762\u4e0a\u867d\u662f \u6728\u65e0\u8868\u60c5\uff0c\u4f46\u4ee4\u4eba\u770b\u6765\uff0c\u5374\u4e0d\u7531\u81ea\u4e3b\u5730\u4f1a\u4ece\u5fc3\u5e95\u6cdb\u8d77\u4e00\u9635\u9635\u6062\u6817\u7684\u5bd2\u610f\u3002 \u3000\u3000\u5c55\u81ea\u5fae\u4e00\u504f\u9996\uff0c\u76ee\u5149\u52a8\u5904\uff0c\u53ea\u89c1\u56db\u4e2a\u9ed1\u8863\u52b2\u88c5\u7684\u5f6a\u5f62\u5927\u6c49\uff0c\u5782\u7740\u53cc\u624b\uff0c\u8fdc\u8fdc\u8ddf\u5728\u4ed6\u8eab\u540e \u8d70\u4e86\u8fdb\u6765\uff0c\u884c\u52a8\u4e4b\u95f4\uff0c\u867d\u7136\u90fd\u6781\u4e3a\u8198\u608d\u77eb\u5065\uff0c\u4f46\u9762\u76ee\u5374\u6709\u5982\u6b7b\u7070\uff0c\u60ca\u60b8\u6050\u60e7\u4e4b\u60c5\uff0c\u6ea2\u4e8e\u989c \u8868\uff0c\u751f\u50cf\u90a3\u5014\u50b2\u5c11\u5e74\u65b9\u624d\u6240\u8bf4\u90a3\u51e0\u65ec\u8f7b\u63cf\u6de1\u5199\u3001\u4f3c\u4e4e\u6ca1\u6709\u534a\u70b9\u8d23\u5907\u610f\u6627\u7684\u8bdd\uff0c\u5df2\u4f7f\u5f97\u8fd9\u51e0\u4e2a \u527d\u608d\u3001\u77eb\u5065\u7684\u5f6a\u5f62\u5927\u6c49\uff0c\u4e3a\u4e4b\u60ca\u9a87\u5230\u8fd9\u79cd\u5730\u6b65\u3002 \u3000\u3000\u90a3\u5014\u50b2\u5c11\u5e74\u9f3b\u5b54\u91cc\u51b7\u54fc\u4e00\u58f0\uff0c\u5c16\u957f\u7684\u624b\u6307\uff0c\u4ece\u8896\u4e2d\u4f38\u51fa\uff0c\u5f80\u8eba\u5728\u5e8a\u4e0a\u7684\u5c55\u81ea\u8eab\u4e0a\u8f7b\u8f7b\u4e00 \u6307\uff0c\u7528\u4ed6\u60ef\u6709\u7684\u51b0\u51b7\u8bed\u8c03\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u8fd9\u4eba\u662f\u8c01?\u5c45\u7136\u5728\u6211\u5e8a\u4e0a\u9ad8\u5367\u8d77\u6765\uff0c\u4f60\u4eec\u867d\u7136\u90fd\u517b\u5c0a \u5904\u4f18\u60ef\u4e86\uff0c\u7b49\u95f2\u4e0d\u4f1a\u8f7b\u6613\u52a8\u5f39\u4e00\u4e0b\uff0c\u4f46\u5374\u4e0d\u81f4\u4e00\u4e2a\u4e2a\u8fde\u773c\u775b\u90fd\u778e\u4e86\u5427?\u201d \u3000\u3000\u8fd9\u5014\u50b2\u5c11\u5e74\u8bf4\u8d77\u8bdd\u6765\uff0c\u58f0\u97f3\u51b7\u6de1\u5e73\u9759\u5df2\u6781\uff0c\u65e2\u4e0d\u5927\u58f0\u559d\u53f1\uff0c\u4ea6\u4e0d\u9ad8\u58f0\u8c29\u9a82\uff0c\u4f46\u8fd9\u56db\u6761\u5f6a \u5f62\u5927\u6c49\u542c\u4e86\uff0c\u9762\u4e0a\u7684\u60ca\u60b8\u6050\u60e7\u4e4b\u8272\uff0c\u5374\u66f4\u91cd\u51e0\u5206\u3002 \u3000\u3000\u5c55\u767d\u4e0d\u5b89\u5730\u5728\u5e8a\u4e0a\u8f6c\u4fa7\u4e00\u4e0b\uff0c\u89c1\u5230\u8fd9\u56db\u6761\u5f6a\u5f62\u5927\u6c49\u90a3\u79cd\u9762\u5982\u6b7b\u7070\u3001\u9aa4\u82e5\u5bd2\u8749\u7684\u6837\u5b50\uff0c\u4e0d \u7981\u5927\u751f\u540c\u60c5\u4e4b\u5fc3\uff1a\u201c\u4e3a\u4ec0\u4e48\u540c\u6837\u662f\u4eba\uff0c\u6709\u4e9b\u4eba\u5374\u5982\u6b64\u53ef\u601c?\u201d \u3000\u3000\u89c1\u5230\u8fd9\u5c11\u5e74\u7684\u72c2\u50b2\u4e4b\u6001\uff0c\u5fc3\u4e2d\u53c8\u4e0d\u7981\u9887\u4e3a\u6c14\u6025\u2026\u2026 \u3000\u3000\u201c\u8fd9\u5c11\u5e74\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u600e\u5730\u5c31\u5982\u6b64\u76ee\u4e2d\u65e0\u4eba\uff0c\u505a\u51fa\u4e00\u526f\u76db\u6c14\u51cc\u4eba\u7684\u6837\u5b50\u6765?\u201d \u3000\u3000\u8f6c\u5ff5\u53c8\u4e0d\u7981\u5fd6\u9053\uff1a\u201c\u8fd9\u600e\u602a\u5f97\u4eba\u5bb6?\u82e5\u662f\u6709\u4e2a\u4e0d\u76f8\u8bc6\u7684\u4eba\u9ad8\u5367\u5728\u6211\u7684\u5e8a\u4e0a\uff0c\u6211\u53c8\u5f53\u5982\u4f55 \u5462?\u201d \u3000\u3000\u5fc3\u4e2d\u6697\u53f9\u4e00\u58f0\uff0c\u6068\u4e0d\u5f97\u9a6c\u4e0a\u7ad9\u8d77\u8eab\u6765\uff0c\u8dd1\u51fa\u8fd9\u623f\u95f4\uff0c\u53c8\u6068\u4e0d\u5f97\u80fd\u8bf4\u51e0\u53e5\u8bdd\uff0c\u4e3a\u81ea\u5df1\u89e3\u91ca \u4e00\u4e0b\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u5fc3\u6709\u4f59\u800c\u529b\u4e0d\u8db3\uff0c\u8fd9\u4e24\u6837\u4e8b\u4ed6\u90fd\u65e0\u6cd5\u505a\u5230\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u4ed6\u5fc3\u4e2d\u7f9e\u60ed\u3001\u607c\u6012\u3001\u4e0d\u5b89 \u5404\u79cd\u60c5\u611f\uff0c\u7eb7\u81f3\u7763\u6765\uff0c\u53c8\u5446\u5446\u5730\u695e\u4f4f \u3000\u3000\u5374\u89c1\u90a3\u5014\u50b2\u5c11\u5e74\u76ee\u5149\u7a81\u5730\u4e00\u5782\uff0c\u5728\u90a3\u56db\u6761\u52b2\u88c5\u5927\u6c49\u7684\u9762\u4e0a\uff0c\u50cf\u5389\u7535\u822c\u4e00\u626b\u800c\u8fc7\uff0c\u51b7\u51b7\u53c8 \u9053\uff1a\u5982\u679c\u4f60\u4eec\u5df2\u7ecf\u4f11\u517b\u591f\u4e86\u7684\u8bdd\uff0c\u6b64\u523b\u5c31\u8bf7\u52a8\u52a8\u624b\uff0c\u7279\u6b64\u4eba\u642c\u51fa\u53bb\u5427\u3002\u201d \u3000\u3000\u8a00\u8bcd\u66f4\u4e3a\u5ba2\u6c14\uff0c\u8bed\u6c14\u5374\u66f4\u52a0\u51b0\u51b7\uff0c\u53cc\u76ee\u53c8\u662f\u4e00\u7ffb\uff0c\u671b\u5728\u5c4b\u9876\u4e4b\u4e0a\uff0c\u518d\u4e5f\u4e0d\u77a7\u522b\u4eba\u4e00\u773c\u3002 \u3000\u3000\u90a3\u56db\u6761\u52b2\u88c5\u5927\u6c49\uff0c\u9f50\u58f0\u7b54\u5e94\u4e00\u58f0\uff0c\u8f6c\u8eab\u8d70\u5411\u5c55\u767d\u7684\u5e8a\u524d\u3002 \u3000\u3000\u5c55\u767d\u773c\u671b\u7740\u4ed6\u4eec\u4e00\u6b65\u6b65\u8d70\u8fd1\u81ea\u5df1\uff0c\u77e5\u9053\u4e0d\u51fa\u7247\u523b\uff0c\u81ea\u5df1\u4fbf\u8981\u88ab\u8fd9\u56db\u6761\u5927\u6c49\u62ac\u51fa\u623f\u5916\uff0c\u5fc3 \u4e2d\u968f\u7136\u4e00\u9635\u70ed\u8840\u4e0a\u6d8c\uff0c\u62fc\u5c3d\u5168\u529b\uff0c\u592a\u559d\u9053\uff1a\u201c\u6218\u4f4f\uff01\u201d \u3000\u3000\u56db\u6761\u5927\u6c49\u7684\u811a\u6b65\u5fae\u5fae\u4e00\u987f\uff0c\u8d70\u5728\u6700\u540e\u7684\u6c49\u5b50\uff0c\u5feb\u754f\u5730\u5f80\u540e\u770b\u4e86\u4e00\u773c\uff0c\u90a3\u5014\u50b2\u5c11\u5e74\u7684\u4e00\u53cc \u773c\u775b\uff0c\u5374\u4ecd\u77ac\u4e5f\u4e0d\u89e3\u671b\u5728\u5c4b\u9876\u4e0a\uff0c\u5c55\u767d\u65b9\u624d\u62fc\u5c3d\u5168\u529b\u7684\u4e00\u58f0\u5927\u559d\uff0c\u4ed6\u7ade\u50cf\u6839\u672c\u6ca1\u6709\u542c\u5230\u3002 \u3000\u3000\u5728\u8fd9\u4e00\u5239\u90a3\u91cc\uff0c\u4e00\u9635\u9635\u7684\u7f9e\u60cb\u3001\u60b2\u6124\u3001\u96be\u582a\uff0c\u4f7f\u5f97\u8fd9\u5fc3\u6027\u5014\u5f3a\u7684\u5c11\u5e74\u5c55\u767d\uff0c\u5b81\u613f\u7acb\u65f6\u8840 \u6e85\u5f53\u5730\uff0c\u4e5f\u4e0d\u613f\u88ab\u8fd9\u56db\u6761\u5927\u6c49\u62fe\u51fa\u5c4b\u53bb\uff0c\u56e0\u4e3a\uff0c\u8fd9\u5bf9\u4e00\u4e2a\u5014\u5f3a\u7684\u70ed\u8840\u5c11\u5e74\u6765\u8bf4\uff0c\u8be5\u662f\u4e00\u79cd\u591a \u4e48\u5927\u7684\u5c48\u8fb1\u5440\uff01 \u3000\u3000\u4f46\u8fd9\u56db\u6761\u6c49\u5b50\uff0c\u811a\u6b65\u7a0d\u5fae\u4e00\u989d\u4e4b\u540e\uff0c\u53c8\u7b14\u76f4\u5730\u5411\u5c55\u767d\u8d70\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u5c55\u767d\u518d\u6b21\u60b2\u6124\u5730\u5927\u559d\u4e00\u58f0\uff0c\u53cc\u8098\u4e00\u6491\u5e8a\u9762\uff0c\u60f3\u594b\u529b\u6323\u8d77\uff0c\u5f53\u4e8b\u5b9e\u6b8b\u9177\u5730\u544a\u8bc9\u4ed6\uff0c\u65e0\u8bba\u5728 \u60c5\u5728\u7406\uff0c\u4ed6\u90fd\u65e0\u6cd5\u5728\u8fd9\u95f4\u623f\u5b50\u91cc\u9017\u7559\u7684\u65f6\u5019\uff0c\u4ed6\u5b81\u53ef\u81ea\u5df2\u722c\u51fa\u53bb\uff0c\u4e5f\u4e0d\u613f\u88ab\u4eba\u62ac\u8d70\u3002 \u3000\u3000\u4f46\u662f\uff0c\u4ed6\u90a3\u4e00\u53cc\u5e73\u65e5\u575a\u5f3a\u800c\u6709\u529b\u7684\u81c2\u8180\uff0c\u6b64\u523b\u5374\u6709\u5982\u5a74\u513f\u822c\u7684\u67d4\u8f6f\u800c\u8106\u5f31\u3002 \u3000\u3000\u4e8e\u662f\uff0c\u4ed6\u90a3\u5df2\u88ab\u591a\u65e5\u6765\u7684\u4f24\u75bc\u75c5\u82e6\u6298\u78e8\u5f97\u5931\u53bb\u539f\u6709\u7cbe\u529b\u7684\u865a\u5f31\u8eab\u8eaf\uff0c\u65b9\u4e00\u6323\u8d77\uff0c\u4fbf\u53c8\u843d \u5728\u5e8a\u4e0a\u67d4\u8f6f\u800c\u534e\u4e3d\u7684\u88ab\u8925\u4e0a\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u6b64\u523b\u4e00\u5207\u7684\u6323\u624e\u4e0e\u53cd\u6297\uff0c\u90fd\u662f\u591a\u4f59\u800c\u65e0\u7528\u7684\u4e86\u3002 \u3000\u3000\u4ed6\u53ea\u5f97\u7edd\u671b\u5730\u95ed\u4e0a\u773c\u775b\uff0c\u63a5\u53d7\u8fd9\u65e0\u6cd5\u907f\u514d\u7684\u5c48\u8fb1\uff0c\u7eb5\u7136\u4ed6\u7684\u5fc3\u5df2\u88ab\u592a\u591a\u7684\u60b2\u6124\u523a\u5f97\u4eff\u4f5b \u8981\u6ef4\u51fa\u8840\u6765\u3002 \u3000\u3000\u54ea\u77e5\u5c31\u5728\u90a3\u56db\u6761\u527f\u608d\u7684\u52b2\u88c5\u5927\u6c49\u6c89\u91cd\u7684\u811a\u6b65\u5c06\u8981\u8d70\u5230\u5e8a\u524d\uff0c\u5c55\u767d\u7edd\u671b\u7684\u773c\u5e18\u5c06\u5408\u672a\u5408\u7684 \u65f6\u4e00\u58f0\u8f7b\u8106\u7684\u5a07\u53f1\uff1a \u3000\u3000\u201c\u4f4f\u624b\uff01\u2019 \u3000\u3000\u5c55\u767d\u5fc3\u4e2d\u62a8\u7136\u4e00\u8df3\uff0c\u731b\u5730\u5f20\u5f00\u773c\u6765\uff0c\u53ea\u89c1\u6708\u5149\u6811\u836b\u531d\u5730\uff0c\u679d\u5185\u7c0c\u7136\u7684\u7a97\u68c2\u4e4b\u5916\uff0c\u6709\u5982\u60ca \u9e3f\u63a0\u4eba\u4e00\u6761\u9ed1\u8272\u4eba\u5f71\u6765\u3002 \u3000\u3000\u4ed6\u76ee\u5149\u867d\u5feb\uff0c\u4f3c\u4e4e\u8fd8\u8ddf\u4e0d\u4e0a\u8fd9\u4eba\u5f71\u7684\u90a3\u79cd\u4e0d\u53ef\u4f01\u53ca\u7684\u901f\u5ea6\uff0c\u4ed6\u53ea\u89c9\u81ea\u5df1\u7684\u76ee\u5149\u65b9\u81ea\u4e00 \u77ac\uff0c\u8fd9\u6761\u4eba\u5f71\u5df2\u7ad9\u5728\u4ed6\u7684\u5e8a\u524d\u3002 \u3000\u3000\u90a3\u56db\u6761\u52b2\u88c5\u5927\u6c49\u53e3\u4e2d\u77ed\u4f4e\u60ca\u547c\u4e00\u58f0\uff0c\u52b3\u9f50\u987f\u4f4f\u811a\u6b65\uff0c\u5f2f\u4e0b\u8170\u53bb\uff0c\u5341\u5206\u606d\u656c\u5730\u884c\u4e86\u4e00\u672d\uff0c \u5f2f\u4e0b\u53bb\u7684\u8eab\u5f62\uff0c\u4e45\u4e45\u90fd\u672a\u7ad9\u76f4\u3002 \u3000\u3000\u90a3\u5014\u50b2\u5c11\u5e74\u7684\u76ee\u5149\uff0c\u6b64\u65f6\u7531\u5c4b\u9876\u79fb\u4e0b\uff0c\u5fbd\u4e00\u76b1\u7709\uff0c\u524d\u884c\u4e24\u6b65\uff0c\u5bf9\u90a3\u6765\u4eba\u9053\uff1a\u201c\u4f60\u6765\u5e72\u4ec0 \u4e48?\u201d \u3000\u3000\u8bed\u58f0\u867d\u4e0d\u548c\u60a6\uff0c\u5374\u4e5f\u4e0d\u662f\u65b9\u624d\u90a3\u79cd\u51b0\u51b7\u7684\u6837\u5b50\u3002 \u3000\u3000\u5c55\u81ea\u5fc3\u4e2d\u4e0d\u7981\u5927\u5947\uff1a\u201c\u8fd9\u4eba\u662f\u8c01?\u600e\u5730\u8fd9\u56db\u6761\u5f6a\u5f62\u5065\u58ee\u7684\u6c49\u5b50\uff0c\u7adf\u4f1a\u5bf9\u5979\u5982\u6b64\u606d\u8c28?\u201d \u3000\u3000\u8fd9\u9ed1\u8863\u4eba\u5f71\u80cc\u5e8a\u800c\u7acb\uff0c\u5c55\u767d\u867d\u7136\u65e0\u6cd5\u770b\u6e05\u5979\u7684\u9762\u5bb9\uff0c\u4f46\u4ece\u5979\u90a3\u88ab\u4e00\u88ad\u67d4\u8f6f\u7684\u9ed1\u4e1d\u8863\u88f3\u7d27 \u7d27\u88f9\u4f50\u7684\u7eb2\u5a1c\u80cc\u5f71\u4e0a\uff0c\u5374\u5df2\u77e5\u9053\u8fd9\u53e6\u5f62\u5982\u7535\u7684\u4eba\u5f71\uff0c\u7adf\u7136\u662f\u4e2a\u5973\u5b50\u3002 \u3000\u3000\u201c\u96be\u9053\u5979\u5c31\u662f\u90a3\u795e\u79d8\u800c\u9ad8\u8d35\u7684\u4e2d\u5e74\u7f8e\u5987?\u201d \u3000\u3000\u5c55\u767d\u76ee\u5149\u8f6c\u5904\uff0c\u5374\u89c1\u8fd9\u5973\u5b50\u7ea4\u8170\u4ec5\u5bb9\u4e00\u63e1\uff0c\u4f53\u6001\u5982\u67f3\uff0c\u5343\u7f15\u9752\u4e1d\uff0c\u633d\u7740\u4e00\u4e2a\u62d8\u8c28\u7684\u53d1 \u7559\uff0c\u659c\u659c\u5782\u4e0b\u7684\u53cc\u624b\uff0c\u66f4\u662f\u5176\u767d\u5982\u7389\uff0c\u65e0\u8bba\u4ece\u4f55\u5904\u53bb\u770b\uff0c\u90fd\u548c\u90a3\u4e2d\u5e74\u7f8e\u5987\u4e0d\u5c3d\u76f8\u540c\uff0c\u2018 \u3000\u3000\u4e8e\u662f\u4ed6\u5fc3\u4e2d\u66f4\u52a0\u7591\u60d1\uff0c\u53ea\u89c9\u4e0d\u4f46\u90a3\u4e2d\u5e74\u7f8e\u5987\u3001\u8fd9\u5014\u50b2\u5c11\u5e74\u3001\u4ee5\u53ca\u50cf\u60ca\u9e3f\u822c\u7a81\u5730\u63a0\u6765\u7684\u9ed1 \u8863\u5973\u5b50\u7684\u6765\u5386\u4e0d\u53ef\u601d\u8bae\uff0c\u5373\u8fde\u8fd9\u90c1\u90c1\u82cd\u82cd\u3001\u6df1\u6c89\u5bbd\u9614\u7684\u5ead\u9662\u91cc\uff0c\u4f3c\u4e4e\u4e5f\u5305\u542b\u7740\u4e00\u4e9b\u79d8\u5bc6\u3002 \u3000\u3000\u201c\u8fd9\u4e9b\u4eba\u5230\u5e95\u662f\u4ec0\u4e48\u8eab\u4efd?\u8fd9\u5730\u65b9\u7a76\u7adf\u662f\u4ec0\u4e48\u5730\u65b9?\u201d \u3000\u3000\u4ed6\u5c06\u8fd9\u95ee\u9898\u5728\u81ea\u5df2\u7684\u8111\u6d77\u4e4b\u4e2d\uff0c\u518d\u6b21\u95ea\u7535\u822c\u5bfb\u601d\u4e00\u904d\uff0e \u3000\u3000\u201c\u662f\u6b66\u6797\u4e16\u5bb6?\u6291\u6216\u662f\u8c6a\u5bcc\u5de8\u5b85?\u751a\u6216\u662f\u516c\u4faf\u5e9c\u90b8\u5462?\u201d \u3000\u3000\u5374\u89c1\u8fd9\u9ed1\u886b\u5973\u5b50\uff0c\u9664\u4e86\u90a3\u4e00\u53cc\u659c\u5782\u4e0b\u7684\u7389\u624b\uff0c\u4fdd\u6301\u7740\u4e00\u4e2a\u7f8e\u5999\u7684\u5f27\u5ea6\u4e4b\u5916\uff0c\u5168\u8eab\u7b14\u76f4\u5730 \u7ad9\u5728\u5e8a\u524d\uff0c\u8fde\u4e00\u4e1d\u52a8\u5f39\u90fd\u6ca1\u6709\uff0c\u5c55\u767d\u867d\u7136\u770b\u4e0d\u5230\u5979\u7684\u9762\u5bb9\uff0c\u5374\u4e0d\u7981\u5728\u5fc3\u4e2d\u52fe\u63cf\u51fa\u4e00\u4e2a\u51b7\u9759\u3001 \u5014\u50b2\u800c\u9ad8\u8d35\u7f8e\u8273\u5982\u82b1\u7684\u8f6e\u5ed3\u6765\u3002\u2606\u5979\u751a\u81f3\u8fde\u8bdd\u90fd\u6ca1\u6709\u8bf4\u4e00\u53e5\uff0c\u53ea\u662f\u9759\u9759\u5730\u4e0e\u90a3\u5014\u50b2\u5c11\u5e74\u9762\u5bf9 \u800c\u7acb\u3002 \u3000\u3000\u5239\u90a3\u4e4b\u95f4\uff0c\u6625\u65e5\u6e29\u6696\u800c\u98de\u626c\u7684\u7a7a\u6c14\uff0c\u4fbf\u751f\u50cf\u662f\u4f7f\u7136\u4e3a\u4e4b\u51bb\u51dd\u4f4f\u4e86\u4e00\u822c\uff0c\u90a3\u56db\u6761\u52b2\u88c5\u5f6a\u5f62 \u58ee\u6c49\u7f13\u7f13\u6307\u8d77\u5934\u6765\uff0c\u5404\u81ea\u5bf9\u671b\u4e00\u773c\uff0c\u6697\u4e2d\u79fb\u52a8\u7740\u811a\u6b65\uff0c\u4f3c\u60f3\u5012\u9000\u7740\u8d70\u51fa\u8fd9\u95f4\u623f\u5b50\u3002 \u3000\u3000\u54ea\u77e5\u4ed6\u4eec\u7684\u811a\u6b65\u65b9\u81ea\u79fb\u52a8\u4e86\u4e09\u4e24\u6b65\uff0c\u90a3\u9ed1\u8863\u5973\u5b50\u5374\u53c8\u5a07\u53f1\u9053\uff1a\u201c\u7ad9\u4f4f\uff01\u201d \u3000\u3000\u5428\u58f0\u65b9\u843d\uff0c\u8fd9\u56db\u6761\u5927\u6c49\u7684\u8eab\u5f62\uff0c\u4fbf\u5982\u7ef4\u65af\u5e94\u5730\u4e3a\u4e4b\u505c\u987f\u3002 \u3000\u3000\u53ea\u542c\u8fd9\u9ed1\u8863\u5973\u5b50\u53c8\u9053\uff1a\u201c\u4f60\u4eec\u65b9\u624d\u5728\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u58f0\u97f3\u867d\u7136\u5a07\u67d4\uff0c\u7adf\u7136\u4ea6\u662f\u68ee\u51b7\u800c\u4e25\u8083\u7684\uff0c\u4e0e\u5979\u90a3\u963f\u5a1c\u800c\u66fc\u5999\u7684\u8eab\u8eaf\u5927\u4e0d\u76f8\u79f0\u3002 \u3000\u3000\u5c55\u767d\u6697\u4e2d\u4e00\u53f9\uff0c\u5fd6\u9053\uff1a\u201c\u600e\u5730\u53c8\u662f\u8fd9\u79cd\u8154\u8c03!\u201d \u3000\u3000\u4f46\u662f\u4ed6\u7684\u76ee\u5149\uff0c\u5374\u4e0d\u505c\u5730\u5728\u8fd9\u9ed1\u8863\u5973\u5b50\u3001\u5014\u50b2\u5c11\u5e74\uff0c\u4ee5\u53ca\u90a3\u56db\u6761\u52b2\u888b\u5f6a\u5f62\u5927\u6c49\u7684\u8eab\u4e0a\u63a0 \u8fc7\uff0c\u53ea\u89c1\u8fd9\u56db\u6761\u6c49\u4e8e\u754f\u602f\u5730\u62ac\u8d77\u5934\u6765\uff0c\u671b\u4e86\u9ed1\u8863\u5973\u5b50\u4e00\u773c\uff0c\u4fbf\u53c8\u6781\u5feb\u5730\u5782\u4e0b\u5934\u53bb\uff0c\u7b54\u9053\uff1a \u201c\u521a\u624d\u516c\u5b50\u7237\u5429\u5490\u5c0f\u7684\u4eec\u5c06\u8fd9\u4f4d\u76f8\u516c\u62ac\u51fa\u53bb\uff0c\u662f\u4ee5\u2014\u2014\u201d \u3000\u3000\u9ed1\u8863\u5973\u5b50\u51b7\u54fc\u4e00\u58f0\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u4f60\u4eec\u5012\u542c\u8bdd\u5f97\u5f88\u3002\u201d \u3000\u3000\u5c55\u767d\u76ee\u5149\u56de\u5230\u5979\u7684\u80cc\u5f71\u4e0a\uff0c\u53ea\u89c1\u5979\u8776\u9996\u5fae\u5fae\u8f6c\u52a8\u4e00\u4e0b\uff0c\u76ee\u53c8\u51dd\u6ce8\u5230\u90a3\u5014\u50b2\u5c11\u5e74\u9762\u76ee\u4e4b \u4e0a\uff0c\u51b7\u51b7\u95ee\u9053\uff1a\u201c\u662f\u4f60\u53eb\u4ed6\u4eec\u628a\u4eba\u62ac\u51fa\u53bb\u7684\u5417?\u201d \u3000\u3000\u90a3\u5014\u50b2\u5c11\u5e74\u8f7b\u8f7b\u4e00\u76b1\u7709\u5934\uff0c\u9053\uff1a\u201c\u8981\u4f60\u6765\u7ba1\u4ec0\u4e48\u95f2\u4e8b\uff0c\u96be\u9053\u53eb\u4eba\u5c06\u4e00\u4e2a\u4e0d\u76f8\u8bc6\u7684\u4eba\u4ece\u6211 \u5e8a\u4e0a\u62ac\u8d70\uff0c\u4e0e\u4f60\u53c8\u6709\u4ec0\u4e48\u5e72\u7cfb\u4e0d\u6210?\u201d \u3000\u3000\u8bf4\u7740\u8f6c\u8eab\u4f4e\u54fc\u4e00\u58f0\uff0c\u5411\u90a3\u56db\u6761\u5927\u6c49\u5fae\u4e00\u77aa\u76ee\uff0c\u8fd9\u56db\u6761\u5927\u6c49\u53ea\u6ee1\u542b\u60ca\u6050\u3001\u754f\u60e7\u4e4b\u8272\u7684\u773c \u775b\uff0c\u4e00\u4f1a\u513f\u671b\u636e\u50b2\u5c11\u5e74\uff0c\u4e00\u4f1a\u513f\u53c8\u671b\u5411\u8fd9\u9ed1\u8863\u5973\u5b50\uff0c\u5f20\u53e3\u6b32\u8a00\uff0c\u4e3e\u6b65\u53c8\u6b62\uff0c\u4e0d\u77e5\u600e\u6837\u624d\u597d\uff0e \u3000\u3000\u5374\u542c\u8fd9\u9ed1\u8863\u5973\u5b50\u53c8\u51b7\u7136\u8bf4\u9053\uff1a\u201c\u4e8f\u4f60\u8fd8\u7b97\u662f\u6b66\u6797\u4e2d\u4e45\u4ee5\u806a\u660e\u667a\u8ba1\u8457\u540d\u7684\u4eba\u7269\uff01\u54fc\uff0c\u6211\u770b \u4f60\u7684\u8111\u7b4b\uff0c\u5012\u4e5f\u6709\u9650\u5f97\u5f88\uff0c\u4f60\u96be\u9053\u4e0d\u4f1a\u60f3\u4e00\u60f3\uff0c\u8fd9\u5c11\u5e74\u82e5\u662f\u6ca1\u6709\u6765\u5386\uff0c\u53c8\u600e\u4f1a\u8dd1\u5230\u8fd9\u91cc\u6765\u517b \u4f24?\u96be\u9053\u5bb6\u91cc\u7684\u4eba\u90fd\u6b7b\u4e86\u4e0d\u6210?\u201d \u3000\u3000\u90a3\u636e\u50b2\u5c11\u5e74\u51b7\u5cfb\u7684\u76ee\u5149\uff0c\u4ecd\u7136\u505c\u7559\u5728\u90a3\u56db\u6761\u5f6a\u58ee\u5927\u6c49\u7684\u8eab\u4e0a\uff0c\u5934\u4e5f\u4e0d\u56de\u5730\u8bf4\u9053\uff1a\u201c\u6211\u770b \u4f60\u4eec\u8fd8\u662f\u6b7b\u4e86\u597d\u4e86\uff0c\u50cf\u4f60\u4eec\u8fd9\u6837\u534a\u6b7b\u4e0d\u6d3b\u7684\u6837\u5b50\uff0c\u54fc\u54fc\uff0c\u771f\u662f\u2014\u2014\u201d \u3000\u3000\u90a3\u9ed1\u8863\u5973\u5b50\u7ea4\u8170\u7a81\u5730\u4e00\u6643\uff0c\u811a\u6b65\u672a\u89c1\u79fb\u52a8\uff0c\u963f\u5a1c\u7684\u8eab\u8eaf\u5374\u5df2\u903c\u5230\u5028\u50b2\u5c11\u5e74\u9762\u524d\uff0c\u51b7Bh \u9053\uff0f\u4f60\u5728\u8bf4\u8c01?\u53ef\u8981\u8bf4\u6e05\u695a\u4e9b\uff01\u201d \u3000\u3000\u8eab\u5f62\u867d\u5df2\u79fb\u52a8\uff0c\u5374\u4ecd\u7136\u662f\u80cc\u5bf9\u5c55\u767d\u3002 \u3000\u3000\u90a3\u5014\u50b2\u5c11\u5e74\u773c\u89d2\u4e00\u626c\uff0c\u63a5\u53e3\u9053\uff1a\u201c\u4f60\u5982\u6b64\u7d27\u5f20\u4f5c\u751a?\u96be\u9053\u6211\u8bf4\u7684\u662f\u4f60?\u201d \u3000\u3000\u9ed1\u8863\u5c11\u5973\u51b7\u54fc\u4e00\u58f0\uff0c\u9053\uff0c\u201c\u6211\u77e5\u9053\u4f60\u73b0\u5728\u662f\u6b66\u6797\u4e2d\u6210\u540d\u9732\u8138\u7684\u5927\u82f1\u96c4\u3001\u5927\u8c6a\u6001\u4e86\uff0c\u600e\u4f1a \u628a\u6211\u8fd9\u4e2a\u59d0\u59d0\u6b32\u5728\u773c\u91cc?\u53ef\u662f-\u2014\u54fc\uff0c\u96be\u9053\u8fde\u5988\u5988\u4e5f\u90fd\u4e0d\u5728\u4f60\u773c\u4e0b\u4e86\u5417?\u201d \u3000\u3000\u5014\u50b2\u5c11\u5e74\u795e\u8272\u4e00\u52a8\uff0c\u7a81\u5730\u56de\u8fc7\u5934\u6765\uff0c\u9053\uff1a\u201c\u554a\uff01\u8fd9\u964c\u751f\u5c11\u5e74\uff0c\u96be\u9053\u662f\u5979\u8001\u4eba\u5bb6\u9001\u5230\u6211\u8fd9 \u91cc\u6765\u517b\u4f24\u7684?\u2019 \u3000\u3000\u76ee\u5149\u4e00\u8f6c\uff0c\u8d8a\u8fc7\u9ed1\u8863\u5c11\u5973\u7684\u80a9\u5934\uff0c\u51dd\u6ce8\u5230\u5c55\u767d\u7684\u8eab\u4e0a\uff0c \u3000\u3000\u5c55\u767d\u6b64\u523b\u5fc3\u4e2d\u624d\u544a\u604d\u7136\u3002\u201c\u539f\u6765\u8fd9\u5014\u50b2\u5c11\u4e2d\u7adf\u662f\u90a3\u4e2d\u5e74\u7f8e\u5987\u7684\u513f\u5b50\u3002\u201d \u3000\u3000\u60f3\u5230\u5979\u5728\u5bf9\u81ea\u5df2\u8bf4\u8bdd\u4e4b\u65f6\u7684\u5fe7\u90c1\u795e\u60c5\uff0c\u53c8\u81ea\u4ed8\u9053\uff1a\u201c\u5979\u4e3a\u4ec0\u4e48\u4f1a\u9732\u51fa\u90a3\u79cd\u5fe7\u90c1\u7684\u795e\u6001\u5462? \u6309\u7406\u8bf4\uff0c\u5979\u4e0d\u8be5\u5982\u6b64\u5fe7\u90c1\u7684\u5440\uff01\u5979\u8a00\u8bed\u4e4b\u4e2d\uff0c\u50cf\u662f\u5bf9\u81ea\u5df1\u7684\u513f\u5b50\u5931\u671b\u5f97\u5f88\uff0c\u5374\u53c8\u662f\u4e3a\u7740\u4ec0\u4e48 \u5462?\u5982\u4eca\u5979\u7684\u513f\u5b50\u4e0d\u4ec5\u5e74\u8f7b\u82f1\u4fca\uff0c\u5e76\u4e14\u53c8\u5728\u6b66\u6797\u4e2d\u4eab\u6709\u76db\u540d\uff0c\u800c\u6211\u5462?\u2026\u2026\u201d \u3000\u3000\u60f3\u5230\u81ea\u5df1\uff0c\u4ed6\u4e0d\u7981\u6697\u4e2d\u957f\u53f9\u4e00\u58f0\uff0c\u4ec0\u4e48\u4e8b\u4e5f\u4e0d\u6562\u518d\u60f3\u4e0b\u53bb\u3002\u67d4\u8f6f\u534e\u4e3d\u7684\u88ab\u8925\uff0c\u4f7f\u5f97\u4ed6\u6709 \u5982\u7761\u5728\u4e91\u5806\u4e2d\u4e00\u822c\u8212\u9002\uff0c\u4f46\u8fd9\u5014\u50b2\u5c11\u5e74\u76ee\u5149\u4e2d\u7684\u8f7b\u8511\u4e0e\u68ee\u51b7\uff0c\u5374\u53c8\u4f7f\u4ed6\u6709\u5982\u7f6e\u8eab\u5bd2\u51b0\u3002 \u3000\u3000\u4ed6\u4e0d\u77e5\u81ea\u5df1\u8be5\u4e0d\u8be5\u5012\u8f6c\u5934\u907f\u5f00\u8fd9\u5c11\u5e74\u7684\u76ee\u5149\uff0c\u5374\u542c\u90a3\u9ed1\u8863\u5c11\u5973\u53c8\u9053\uff1a\u201c\u82e5\u4e0d\u662f\u5979\u8001\u4eba \u5bb6\uff0c\u8fd8\u6709\u8c01\u6562\u628a\u4eba\u5e26\u5165\u4f60\u8fd9\u623f\u2026\u2026\u201d\u8bed\u58f0\u7a81\u5730\u4e00\u987f\u3002\u5c55\u767d\u53ea\u89c9\u5f97\u773c\u524d\u4eba\u5f71\u4e00\u82b1\uff0c\u63a5\u7740\u4fbf\u542c\u5230 \u4e00\u9635\u6e05\u8106\u7684\u638c\u58f0\uff0c\u5fc3\u4e2d\u4e0d\u7981\u5927\u5947\uff0c\u5b9a\u775b\u671b\u53bb\uff0c\u90a3\u56db\u6761\u52b2\u88c5\u5927\u6c49\uff0c\u6b64\u65f6\u6b63\u5e76\u6392\u7ad9\u5728\u95e8\u53e3\uff0c\u540c\u7528 \u53cc\u624b\u6367\u7740\u9762\u988a\uff0c\u8138\u4e0a\u4ff1\u662f\u4e00\u7247\u832b\u7136\u4e2d\u60ca\u60e7\u7684\u795e\u8272\uff1b\u90a3\u5014\u50b2\u5c11\u5e74\uff0c\u76ee\u5149\u4e4b\u4e2d\u6ee1\u542b\u6012\u610f\uff0c\u5374\u671b\u5728 \u90a3\u53c8\u590d\u80cc\u5e8a\u800c\u7acb\u7684\u9ed1\u8863\u5c11\u5973\u8eab\u4e0a\u3002 \u3000\u3000\u5c55\u767d\u5fc3\u4e2d\u4e0d\u7981\u53c8\u4e3a\u4e4b\u4e00\u60ca\uff1a\u201c\u65b9\u624d\u90a3\u5239\u90a3\u4e4b\u95f4\uff0c\u96be\u9053\u5979\u5df2\u5728\u8fd9\u56db\u6761\u5927\u6c49\u7684\u9762\u988a\u4e4b\u4e0a\uff0c\u5404 \u5404\u51fb\u4e86\u4e24\u638c?\u201d\u987b\u77e5\u4ed6\u81ea\u5df2\u4ea6\u662f\u6709\u6b66\u529f\u4e4b\u4eba\uff0c\u5bf9\u6b66\u529f\u4e00\u9014\uff0c\u4ea6\u9887\u4e0b\u8fc7\u82e6\u529f\uff0c\u6b64\u523b\u89c1\u4e86\u8fd9\u9ed1\u8863 \u5973\u5b50\u7684\u6b66\u529f\uff0c\u5fc3\u4e2d\u4e0d\u7981\u5927\u611f\u60ca\u9a87\uff0c\u77e5\u9053\u82e5\u62ff\u81ea\u5df1\u82e6\u7ec3\u5341\u6570\u5bd2\u6691\u7684\u529f\u592b\u6765\u548c\u4eba\u5bb6\u4e00\u6bd4\uff0c\u771f\u6709\u5982 \u7693\u6708\u4e0b\u7684\u4e00\u70b9\u8424\u5149\u800c\u5df2\u3002 \u3000\u3000\u53ea\u89c1\u90a3\u5014\u50b2\u5c11\u5e74\u7684\u76ee\u5149\uff0c\u77ac\u4e5f\u4e0d\u77ac\u5730\u671b\u7740\u90a3\u5c11\u5973\u8eab\u4e0a\uff0c\u826f\u4e45\u826f\u4e45\uff0c\u65b9\u4e00\u5b57\u4e00\u5b57\u5730\u7f13\u7f13\u8bf4 \u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\u4ed6\u4eec\u662f\u8c01\u7684\u624b\u4e0b?\u201d\u9ed1\u8863\u5973\u5b50\u51b7\u51b7\u9053\uff1a\u9664\u4e86\u540d\u6ee1\u5929\u4e0b\u7684\u2018\u51cc\u98ce\u516c\u4e8e\u2019\u6155\u5bb9\u627f \u4e1a\u4e4b\u5916\uff0c\u8fd8\u6709\u8c01\u914d\u5f53\u5f97\u8d77\u4ed6\u4eec\u7684\u4e3b\u4eba?\u201d\u59cb\u7ec8\u5728\u7559\u610f\u7740\u4ed6\u4eec\u8c08\u8bdd\u7684\u5c55\u767d\uff0c\u6b64\u65f6\u5fc3\u4e2d\u9a87\u7136\u4e00 \u9707\uff1a\u201c\u539f\u6765\u8fd9\u5c11\u5e74\u7adf\u662f\u6b66\u6797\u56db\u516c\u5b50\u4e2d\u6700\u65e0\u60c5\u7684\u2018\u51cc\u98ce\u516c\u5b50\u2019\u3002\u201d\u4ed6\u867d\u662f\u521d\u5165\u6c5f\u6e56\uff0c\u4f46\u201c\u6b66\u6797 \u56db\u516c\u5b50\u201d\u540d\u4f20\u5929\u4e0b\uff0c\u4e43\u662f\u5f53\u4eca\u6c5f\u6e56\u4e2d\u98ce\u5934\u6700\u52b2\u7684\u4eba\u7269\uff0c\u4f60\u82e5\u5bf9\u4e2a\u7a0d\u7a0d\u6d89\u8db3\u6b66\u6797\u7684\u6c49\u5b50\u5ff5\u4e00 \u53e5\uff1a\u201c\u5b89\u4e50\u98ce\u6d41\u3002\u201d\u4ed6\u4fbf\u7acb\u523b\u53ef\u4ee5\u63a5\u7740\u5ff5\u9053\uff1a\u201c\u98d8\u96f6\u7aef\u65b9\uff0c\u51cc\u98ce\u65e0\u60c5\uff0c\u6837\u9e9f\u70ed\u80a0\u3002\u201d\u56e0\u4e3a\u8fd9 \u56db\u53e5\u6d41\u4f20\u6c5f\u6e56\u7684\u53e3\u8bed\uff0c\u6b63\u662f\u63cf\u8ff0\u8fd9\u201c\u6b66\u6797\u56db\u516c\u5b50\u201d\u4e3a\u4eba\u7684\u7279\u8272\u7684\u3002 \u7b2c\u516d\u7ae0\u3000\u8bf7\u8bf4\u4f60\u7684\u8eab\u4e16>> \u53e4\u9f99\u300a\u5251\u5ba2\u884c\u300b \u7b2c\u516d\u7ae0\u3000\u8bf7\u8bf4\u4f60\u7684\u8eab\u4e16 \u3000\u3000\u5c55\u767d\u5fc3\u5ff5\u8f6c\u5904\uff0c\u76ee\u5149\u51dd\u6ce8\u5728\u8fd9\u201c\u51cc\u98ce\u516c\u5b50\u201d\u7684\u8eab\u4e0a\uff0c\u89c1\u4ed6\u867d\u662f\u6012\u6781\uff0c\u4f46\u795e\u8272\u5374\u4ecd\u7136\u6728\u65e0 \u8868\u60c5\uff0c\u4e0d\u7981\u6697\u81ea\u611f\u53f9\u4e00\u58f0\uff0c\u5fd6\u9053\uff1a\u51cc\u98ce\u516c\u5b50\u65e0\u60c5\u5ba2\uff0c\u65e0\u60c5\u6700\u662f\u51cc\u98ce\u4eba\uff0c\u4eba\u9053\u6c5f\u6e56\u4f20\u8a00\u96be\u4ee5\u542c \u4fe1\uff0c\u4f46\u6b64\u523b\u770b\u6765\uff0c\u867d\u4e0d\u80fd\u5c3d\u60c5\uff0c\u5374\u4e5f\u5e76\u975e\u5168\u4e0d\u53ef\u4fe1\u7684\u5462\u3002\u201d \u3000\u3000\u5374\u89c1\u8fd9\u201c\u51cc\u98ce\u516c\u5b50\u201d\u8584\u524a\u7684\u5634\u5507\uff0c\u8f7b\u8f7b\u4e00\u6487\uff0c\u76ee\u5149\u77ac\u4e5f\u4e0d\u77ac\u5730\u5728\u90a3\u9ed1\u8863\u5973\u5b50\u9762\u4e0a\u51dd\u6ce8\u534a \u664c\uff0c\u7a81\u5730\u51b7\u51b7\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u597d\u6781\uff0c\u597d\u6781\uff0c\u60f3\u4e0d\u5230\u975e\u4f46\u6211\u7684\u623f\u95f4\uff0c\u6211\u81ea\u5df1\u4e0d\u80fd\u5b89\u6392\uff0c\u7ade\u8fde\u6211\u7684 \u624b\u4e0b\uff0c\u90fd\u8981\u52b3\u52a8\u4f60\u6765\u66ff\u6211\u6559\u8bad\u4e86\uff0c\u597d\uff0c\u597d\u2014\u2014\u201d\u51b7\u7b11\u8fde\u8fde\uff0c\u8863\u8896\u4e00\u62c2\uff0c\u7ade\u81ea\u8f6c\u8eab\u5411\u95e8\u5916\u8d70 \u53bb\uff0c\u90a3\u56db\u6761\u5927\u6c49\u695e\u4e86\u4e00\u695e\uff0c\u5404\u81ea\u8e0c\u8e87\u5730\u671b\u4e86\u90a3\u9ed1\u886b\u5973\u5b50\u4e00\u773c\uff0c\u9762\u76ee\u4e4b\u4e0a\uff0c\u6ee1\u662f\u8fdb\u9000\u7ef4\u8c37\u7684\u5c34 \u5c2c\u4e4b\u6001\u3002 \u3000\u3000\u5c55\u767d\u6df1\u6df1\u4e3a\u8fd9\u56db\u6761\u770b\u6765\u52c7\u6562\u527d\u608d\uff0c\u5176\u5b9e\u5374\u53c8\u5982\u6b64\u602f\u61e6\u7684\u6c49\u5b50\u60b2\u54c0\uff0c\u4ed6\u65e0\u6cd5\u4e86\u89e3\u4e16\u4e0a\u751f\u5177 \u5974\u624d\u4e4b\u6027\u7684\u4eba\uff0c\u600e\u4f1a\u5982\u6b64\u4e4b\u591a\u3002 \u3000\u3000\u4ed6\u76ee\u5149\u53c8\u7f13\u7f13\u8f6c\u5230\u90a3\u9ed1\u8863\u5973\u5b50\u7684\u80cc\u5f71\u4e0a\uff0c\u53ea\u89c1\u5979\u963f\u5a1c\u591a\u59ff\u7684\u8eab\u8eaf\u3002\u6b64\u523b\u8d77\u4e86\u2014\u9635\u5fae\u5fae\u7684 \u98a4\u6296\uff0c\u4eff\u4f5b\u5fae\u98ce\u4e2d\u7684\u67f3\u4e1d\u4e00\u6837\uff0c\u6123\u5728\u90a3\u91cc\uff0c\u826f\u4e45\u826f\u4e45\uff0c\u7a81\u5730\u5e7d\u5e7d\u957f\u53f9\u4e00\u58f0\uff0c\u6625\u8471\u822c\u7684\u624b\u638c\u8f7b \u8f7b\u5411\u90a3\u56db\u6761\u6ee1\u9762\u6050\u614c\u7684\u5927\u6c49\u4e00\u6325\uff0c\u5bbd\u5927\u7684\u8863\u8896\uff0c\u98d8\u98d8\u843d\u4e86\u4e0b\u6765\uff0c\u4e00\u9762\u7f13\u7f13\u8bf4\u9053\uff1a\u516c\u5b50\u8d70\u4e86\uff0c \u4f60\u4eec\u8fd8\u7ad9\u5728\u8fd9\u91cc\u5e72\u4ec0\u4e48?\u201d\u56db\u6761\u5927\u6c49\u5982\u83b7\u5927\u8d66\uff0c\u9f50\u9f50\u606d\u8eab\u7b54\u5e94\u4e00\u58f0\uff0c\u7f13\u7f13\u9000\u51fa\u95e8\u5916\uff0c\u8f6c\u8eab\u5306 \u5fd9\u5730\u8d70\u4e86\u3002 \u3000\u3000\u8fd9\u95f4\u5e7d\u9759\u6e05\u96c5\u7684\u6a59\u5ba4\uff0c\u4fbf\u53c8\u6062\u590d\u539f\u6765\u7684\u6e05\u9759\uff0c\u7761\u5728\u5e8a\u4e0a\u7684\u5c55\u767d\uff0c\u6697\u4e2d\u957f\u957f\u677e\u4e86\u4e00\u53e3\u6c14\uff0c \u4f46\u5fc3\u4e2d\u4e0d\u5b89\u4e4b\u610f\uff0c\u5374\u4ecd\u4e0d\u80fd\u56e0\u4e4b\u5c3d\u6d88\u3002\u56e0\u4e3a\u4ed6\u6b64\u523b\u4f24\u75c5\u65b9\u611f\u7a0d\u6108\uff0c\u4f46\u4f53\u529b\u672a\u590d\uff0c\u4ecd\u662f\u865a\u5f31\u65e0 \u6bd4\uff0c\u5bf9\u4efb\u4f55\u4e8b\u7684\u53d1\u751f\uff0c\u4ed6\u90fd\u6ca1\u6709\u5e94\u53d8\u4e4b\u529b\uff0c\u800c\u4ed6\u6b64\u523b\u7684\u5b58\u8eab\u4e4b\u5730\uff0c\u5374\u53c8\u662f\u5982\u6b64\u7684\u4e0d\u5b89\u5b9a\uff0c\u4ed6 \u81ea\u77e5\u968f\u65f6\u90fd\u6709\u906d\u53d7\u522b\u4eba\u7f9e\u8fb1\u7684\u5371\u9669\uff0c\u8fd9\u662f\u4e00\u4e2a\u751f\u6027\u5014\u5f3a\u9ad8\u50b2\u4e4b\u4eba\u6240\u6700\u96be\u4ee5\u5fcd\u53d7\u7684\u4e8b\u3002 \u3000\u3000\u4f46\u65e0\u8bba\u5982\u4f55\uff0c\u4ed6\u5bf9\u8fd9\u9ed1\u8863\u5973\u5b50\uff0c\u5374\u662f\u65e0\u6bd4\u611f\u6fc0\u7684\uff0c\u4ed6\u55eb\u5685\u7740\uff0c\u4e0d\u77e5\u9053\u8be5\u8bf4\u4ec0\u4e48\u8bdd\uff0c\u624d\u80fd \u591f\u5c06\u81ea\u5df1\u5fc3\u4e2d\u7684\u611f\u6fc0\u4e4b\u60c5\u8868\u8fbe\u51fa\u6765\u3002 \u3000\u3000\u54ea\u77e5\u8fd9\u9ed1\u8863\u5973\u5b50\u7a81\u53c8\u957f\u53f9\u4e00\u58f0\uff0c\u4f3c\u4e4e\u9887\u4e3a\u5fe7\u90c1\u5730\u8bf4\u9053\uff1a\u201c\u820d\u5f1f\u65e0\u77e5\uff0c\u4e0d\u77e5\u505a\u4eba\u4e4b\u9053\uff0c\u8fd8 \u8bf7\u76f8\u516c\u539f\u8c05\u4ed6\u7684\u72c2\u5984\u624d\u597d\u3002\u201d \u3000\u3000\u8bed\u58f0\u662f\u90a3\u4e48\u5fe7\u90c1\uff0c\u4f7f\u5f97\u5c55\u767d\u4e0d\u7981\u4e3a\u4e4b\u60f3\u8d77\u90a3\u4e2d\u5e74\u7f8e\u5987\uff0c\u56e0\u4e3a\u5979\u4eec\u8bf4\u8bdd\u7684\u58f0\u97f3\uff0c\u7adf\u662f\u5982\u6b64 \u76f8\u4f3c\uff0c\u800c\u5979\u5fe7\u90c1\u7684\u8bed\u58f0\u4e4b\u4e2d\uff0c\u5374\u53c8\u542b\u8574\u7740\u90a3\u4e48\u591a\u7684\u6e29\u67d4\uff0c\u5c31\u50cf\u662f\u5b9c\u4eba\u7684\u6625\u98ce\u4e00\u822c\uff0c\u4f7f\u5f97\u5c55\u767d \u5fc3\u4e2d\u56e0\u65b9\u624d\u7684\u5c48\u8fb1\u800c\u53d7\u5230\u7684\u521b\u4f24\uff0c\u90fd\u4e3a\u4e4b\u5e73\u590d\u8d77\u6765\u3002 \u3000\u3000\u4ed6\u8bb7\u8bb7\u5730\u5e76\u6ca1\u6709\u7acb\u523b\u56de\u7b54\uff0c\u56e0\u4e3a\u4ed6\u77e5\u9053\u81ea\u5df2\u6b64\u523b\u7684\u5904\u5883\uff0c\u90a3\u201c\u51cc\u98ce\u516c\u4e8e\u201d\u867d\u7136\u72c2\u5984\uff0c\u4f46 \u81ea\u5df2\u65e0\u8bba\u5982\u4f55\u603b\u662f\u7761\u5728\u4eba\u5bb6\u7684\u5e8a\u4e0a\uff0c\u5e94\u8be5\u8bf7\u6c42\u539f\u8c05\u7684\uff0c\u4e5f\u8be5\u662f\u81ea\u5df1\u800c\u4e0d\u662f\u4ed6\u5440\uff01 \u3000\u3000\u4e8e\u662f\uff0c\u4ed6\u53c8\u6697\u4e2d\u957f\u53f9\u4e00\u58f0\uff0c\u5446\u5446\u5730\u671b\u7740\u8fd9\u9ed1\u8863\u5973\u5b50\u7684\u80cc\u5f71\uff0c\u9053\uff1a\u201c\u5c0f\u53ef\u98d8\u6cca\u5b64\u96f6\uff0c\u4e00\u65e0 \u6240\u6210\u2026\u2026\u5509\uff0c\u59d1\u5a18\u5982\u6b64\u5bf9\u5f85\u4e8e\u6211\uff0c\u5df2\u4f7f\u5c0f\u53ef\u611f\u6fc0\u4e0d\u5c3d\uff0c\u82e5\u518d\u8bf4\u8fd9\u6837\u7684\u8bdd\uff0c\u90a3\u5c0f\u53ef\u771f\u662f\u65e0\u5730\u81ea \u5bb9\u4e86\u3002\u201d \u3000\u3000\u4ed6\u524d\u9762\u6240\u8bf4\u7684\u4e24\u53e5\u8bdd\uff0c\u672c\u662f\u5fc3\u4e2d\u81ea\u6028\u81ea\u827e\u3001\u81ea\u8d23\u81ea\u60ed\u7684\u611f\u89c9\uff0c\u8bf4\u4e86\u4e24\u53e5\uff0c\u5ffd\u7136\u89c9\u5f97\u81ea\u5df1 \u5728\u4e00\u4e2a\u7d20\u4e0d\u76f8\u8bc6\u7684\u5973\u5b50\u9762\u524d\uff0c\u8bf4\u51fa\u8fd9\u79cd\u8bdd\u6765\u751a\u662f\u4e0d\u59a5\uff0c\u4fbf\u6539\u53d8\u4e86\u8bed\u6c14\uff0c\u4f46\u5fc3\u4e2d\u5374\u4ecd\u4e0d\u7981\u6697\u6697 \u8c34\u8d23\u7740\u81ea\u5df1\uff1a\u201c\u600e\u5730\u6211\u8fde\u8bdd\u90fd\u4e0d\u4f1a\u8bf4\u4e86\uff01\u201d \u3000\u3000\u54ea\u77e5\u8fd9\u9ed1\u8863\u5973\u5b50\u542c\u4e86\u4ed6\u7684\u8bdd\uff0c\u5374\u53c8\u5e7d\u5e7d\u957f\u53f9\u4e00\u58f0\uff0c\u5583\u5583\u4f4e\u8bed\u7740\u9053\uff1a\u201c\u5b64\u96f6\u98d8\u6cca\u2026\u2026\u5b64\u96f6 \u98d8\u6cca\u53c8\u6709\u4ec0\u4e48\u4e0d\u597d?\u81ea\u7531\u81ea\u5728\u7684\uff0c\u603b\u6bd4\u56f0\u4e8e\u6a0a\u7b3c\u4e4b\u4e2d\u8981\u597d\u5f97\u591a\u4e86\u5427\u3002\u201d\u8bed\u6c14\u4e2d\u7684\u81ea\u6028\u81ea\u827e\u4e4b \u610f\uff0c\u7adf\u4f3c\u6bd4\u5c55\u767d\u8fd8\u8981\u6d53\u539a\u5341\u500d\u3002 \u3000\u3000\u5c55\u767d\u4e0d\u7981\u4e00\u695e\uff0c\u6697\u81ea\u65f6\u9053\uff0c\u201c\u5979\u751f\u4e8e\u5982\u6b64\u8c6a\u5bcc\u4e4b\u5bb6\uff0c\u5e73\u65e5\u517b\u5c0a\u5904\u4f18\uff0c\u53ea\u8981\u5979\u8bf4\u4e00\u53e5\u8bdd\uff0c \u4fbf\u4e0d\u77e5\u9053\u4f1a\u6709\u591a\u5c11\u4eba\u8981\u4e89\u7740\u53bb\u505a\uff0c\u600e\u5730\u8a00\u8bcd\u4e4b\u4e2d\u5374\u53c8\u5982\u6b64\u54c0\u4f24\u5e7d\u6028?\u201d \u3000\u3000\u4ed6\u53c8\u60f3\u8d77\u90a3\u4e2d\u5e74\u7f8e\u5987\u7684\u6028\u827e\u4e4b\u8272\uff0c\u4f3c\u4e4e\u5728\u8fd9\u6837\u534e\u4e3d\u6df1\u6c89\u7684\u5ead\u9662\u4e2d\uff0c\u6bcf\u4e2a\u4eba\u5fc3\u91cc\u90fd\u6709\u7740\u5fc3 \u4e8b\uff0c\u800c\u6bcf\u4e2a\u4eba\u7684\u5fc3\u4e8b\u90fd\u662f\u6781\u4e0d\u5feb\u6d3b\u7684\uff0c\u53ea\u662f\u5979\u4eec\u7684\u5fc3\u4e8b\u7a76\u7adf\u662f\u4ec0\u4e48\uff0c\u4ed6\u5374\u6781\u96be\u731c\u6d4b\u51fa\u6765\u800c \u5df2\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u6b63\u5728\u611f\u6168\u4e1b\u751f\uff0c\u5374\u89c1\u8fd9\u9ed1\u8863\u5c11\u5973\u67f3\u8170\u8f7b\u8f7b\u4e00\u6446\uff0c\u7ade\u81ea\u7f13\u7f13\u8f6c\u8fc7\u8eab\u6765\uff0c\u5c55\u767d\u5fc3\u5934\u4e00 \u8df3\uff0c\u4e0d\u80fd\u81ea\u7981\u5730\u5c06\u76ee\u5149\u671b\u5411\u5979\u9762\u76ee\u4e4b \u3000\u3000\u4ed6\u7684\u76ee\u5149\u7acb\u523b\u51dd\u7ed3\u5728\u5979\u7684\u9762\u4e0a\u4e86\uff0c\u51e0\u4e4e\u518d\u4e5f\u65e0\u6cd5\u79fb\u52a8\u4e00\u4e0b\u3002 \u3000\u3000\u4ed6\u867d\u7136\u62d9\u4e8e\u8a00\u8bcd\uff0c\u5374\u662f\u6781\u4e3a\u806a\u6167\u4e4b\u4eba\uff0c\u4f46\u662f\u4ed6\u6b64\u523b\u7eb5\u7136\u7528\u5c3d\u81ea\u5df2\u7684\u667a\u529b\u601d\u7d22\uff0c\u5374\u4e5f\u65e0\u6cd5 \u60f3\u51fa\u4efb\u4f55\u8bcd\u6c47\u6765\u5f62\u5bb9\u81ea\u5df2\u773c\u4e2d\u6240\u89c1\u5230\u7684\u9762\u5bb9\u3002 \u3000\u3000\u4f7f\u4ed6\u65e0\u6cd5\u4e86\u89e3\u7684\uff0c\u5374\u662f\u8fd9\u5168\u8eab\u9ed1\u8863\u7684\u5973\u5b50\uff0c\u9762\u4e0a\u7ade\u4ea6\u8499\u4e86\u4e00\u65b9\u9ed1\u7eb1\uff0c\u5c06\u5979\u7684\u6a31\u5507\u548c\u9f3b\u7aef \u4e00\u9f50\u63a9\u4f4f\uff0c\u4f46\u662f\u9ed1\u7eb1\u4e0a\u9762\u6240\u9732\u51fa\u7684\u6625\u5c71\u9edb\u7709\uff0c\u5982\u6c34\u79cb\u6ce2\uff0c\u5374\u662f\u5c55\u767d\u5e73\u751f\u4ece\u672a\u7779\u89c1\u7684\u7f8e\u4e3d\uff0c\u7f8e \u4e3d\u5f97\u5c06\u8fd9\u65b9\u5e73\u51e1\u7684\u9ed1\u7eb1\uff0c\u90fd\u6620\u6210\u4e00\u7247\u7729\u76ee\u800c\u795e\u79d8\u7684\u5149\u91c7\u3002 \u3000\u3000\u5979\u79cb\u6ce2\u6de1\u6de1\u5411\u5c55\u767d\u7684\u8eab\u4e0a\u4e00\u626b\uff0c\u773c\u6ce2\u4e2d\u90a3\u4eae\uff0c\u50cf\u662f\u6b8b\u6625\u4e2d\u7684\u9633\u5149\uff0c\u4f7f\u5f97\u5c55\u767d\u5fc3\u4e2d\u4e00\u70eb\uff0c \u7a81\u7136\u89c9\u5f97\u5929\u5730\u95f4\u90fd\u53d8\u5f97\u6e29\u6696\u8d77\u6765\u3002 \u3000\u3000\u8fd9\u79cd\u611f\u89c9\u662f\u5c55\u767d\u5e73\u751f\u672a\u6709\u8fc7\u7684\uff0c\u4ed6\u867d\u7136\u6697\u81ea\u9547\u6444\u7740\u5fc3\u795e\uff0c\u60f3\u5c06\u81ea\u5df1\u76ee\u5149\u6536\u8f6c\uff0c\u4f46\u662f\u4ed6\u7684 \u76ee\u5149\u5374\u50cf\u662f\u5bc2\u5bde\u7684\u6e38\u5b50\u7a81\u7136\u5bfb\u5f97\u4e00\u4e2a\u6e29\u6696\u7684\u5bb6\u5ba4\uff0c\u7559\u604b\u5730\u505c\u7559\u5728\u5979\u9762\u4e0a\uff0c\u65e0\u6cd5\u79fb\u52a8\u3002 \u3000\u3000\u4e24\u4eba\u76ee\u5149\u76f8\u5bf9\uff0c\u90a3\u9ed1\u8863\u5973\u5b50\u7a81\u5730\u5782\u4e0b\u5934\u51fb\uff0c\u826f\u4e45\u65b9\u59cb\u62ac\u5934\uff0c\u76ee\u5149\u90fd\u53c8\u548c\u5c55\u767d\u7684\u9047\u5728\u4e00 \u5904\u3002 \u3000\u3000\u53c8\u662f\u4e00\u9635\u65e0\u80b2\u7684\u6c89\u9ed8\u3002\u5c55\u767d\u7684\u76ee\u5149\u6e10\u6e10\u660e\u4eae\u8d77\u6765\uff0c\u5374\u662f\u8fd9\u9ed1\u8863\u5c11\u5973\u7684\u76ee\u5149\u6e10\u6e10\u9eef\u6de1\uff0c\u76ee \u5149\u4e2d\u7684\u5fe7\u90c1\u4e4b\u8272\uff0c\u4e5f\u8d8a\u53d1\u91cd\u4e86\uff0c\u5979\u7a81\u53c8\u67f3\u8170\u4e00\u52a8\uff0c\u8f6c\u8fc7\u8eab\u53bb\uff0c\u5934\u4e5f\u4e0d\u56de\u5730\u8d70\u5411\u95e8\u8fb9\u3002 \u3000\u3000\u5c55\u767d\u5fc3\u4e2d\u4e00\u51db\u5239\u90a3\u4e4b\u95f4\uff0c\u81ea\u8d23\u81ea\u60ed\u4e4b\u5ff5\u53c8\u590d\u5927\u4f5c\uff0c\u6697\u6068\u81ea\u5df2\u600e\u5730\u5982\u6b64\u5b5f\u6d6a\uff0c\u53c8\u6697\u6068\u81ea\u5df2 \u65b9\u624d\u600e\u4f1a\u751f\u51fa\u90a3\u79cd\u5947\u5f02\u7684\u611f\u89c9\u3002 \u3000\u3000\u54ea\u77e5\u8fd9\u5c11\u5973\u8d70\u5230\u95e8\u8fb9\uff0c\u811a\u6b65\u7a81\u5730\u4e00\u987f\uff0c\u8f7b\u8f7b\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff0c\u201c\u4f60\u6655\u8fc7\u53bb\u4e86\u597d\u591a\u5929\uff0c\u6b64\u523b \u8eab\u5b50\u4e00\u5b9a\u865a\u5f31\u5f97\u5f88\uff0c\u7b49\u4e00\u4f1a\u6211\u53eb\u4eba\u9001\u4e9b\u4e1c\u897f\u6765\u2014\u2014\u201d\u8bed\u58f0\u5fae\u987f\uff0c\u53c8\u9053\uff1a\u201c\u4f46\u662f\u4f60\u5374\u7528\u4e0d\u7740\u8c22 \u6211\uff0c\u8fd9\u4e00\u5207\u4e8b\u90fd\u662f\u6709\u4eba\u6258\u6211\u505a\u7684\uff0c\u6211\u4e0d\u8fc7\u662f\u770b\u4ed6\u7684\u9762\u5b50\u800c\u5df2\u3002\u201d\u8bed\u58f0\u672a\u843d\uff0c\u7f57\u8896\u5fae\u62c2\uff0c\u60ca\u9e3f \u822c\u63a0\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u5979\u524d\u9762\u51e0\u53e5\u8bdd\u8bf4\u5f97\u672c\u6765\u6e29\u67d4\u65e0\u6bd4\uff0c\u4f46\u8bed\u58f0\u4e00\u987f\u4e4b\u540e\uff0c\u5374\u7acb\u523b\u53d8\u6210\u51b7\u51b0\u51b0\u7684\u8bed\u6c14\uff0c\u8fd9\u524d\u540e \u51e0\u53e5\u8bdd\u8ba9\u4eba\u542c\u6765\uff0c\u7ade\u50cf\u4e0d\u662f\u4e00\u4e2a\u4eba\u8bf4\u7684\u3002 \u3000\u3000\u5c55\u767d\u76ee\u9001\u5979\u80cc\u5f71\u6d88\u5931\uff0c\u5374\u53ea\u89c9\u5ba4\u4e2d\u4eff\u4f5b\u98d8\u6563\u7740\u5979\u8eab\u4e0a\u7684\u6de1\u6de1\u5e7d\u9999\uff0c\u773c\u524d\u8fd8\u6d6e\u7740\u5979\u963f\u5a1c\u7684 \u8eab\u5f71\uff0c\u800c\u6700\u540e\u7684\u51e0\u53e5\u8bdd\uff0c\u4e5f\u4ecd\u7136\u5728\u8033\u8fb9\u8361\u6f3e\u7740\uff0c\u5c31\u53c8\u751f\u50cf\u662f\u4e00\u679d\u51b0\u51b7\u7684\u7bad\uff0c\u7531\u4ed6\u7684\u8033\u4e2d\u523a\u4eba \u5fc3\u91cc\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u82e6\u607c\u5730\u62fe\u8d77\u624b\u6765\uff0c\u626f\u52a8\u7740\u81ea\u5df2\u5934\u4e0a\u7684\u4e71\u53d1\uff0c\u624b\u81c2\u867d\u4ecd\u75db\u82e6\uff0c\u5374\u62b5\u4e0d\u4e0a\u4ed6\u5fc3\u4e2d\u7684\u75db \u82e6\uff0c\u201c\u8fd9\u5973\u5b50\u867d\u7136\u6709\u6069\u4e8e\u6211\uff0c\u5374\u4e0e\u6211\u6beb\u65e0\u74dc\u845b\uff0c\u5979\u662f\u4ec0\u4e48\u8eab\u4efd\uff0c\u6211\u53c8\u662f\u4ec0\u4e48\u4eba\uff0c\u5979\u5982\u6b64\u5bf9 \u6211\uff0c\u5df2\u662f\u6781\u7559\u60c5\u9762\u7684\u4e86\uff0c\u622e\u53c8\u4f55\u5fc5\u4e3a\u8fd9\u79cd\u4e8b\u82e6\u607c?\u201d \u3000\u3000\u4ed6\u867d\u7136\u6697\u4e2d\u5982\u6b64\u601d\u8ba8\uff0c\u4f46\u4e0d\u77e5\u600e\u5730\uff0c\u5fc3\u91cc\u5374\u4ecd\u7136\u653e\u4e0d\u4e0b\u6b64\u4e8b\u3002 \u3000\u3000\u4ed6\u4f3c\u4e4e\u89c9\u5f97\u4e16\u4e0a\u6240\u6709\u4eba\u5bf9\u4ed6\u7684\u8f7b\u8511\uff0c\u90fd\u6bd4\u4e0d\u4e0a\u8fd9\u9ed1\u8863\u5973\u5b50\u5bf9\u4ed6\u7684\u51b7\u6de1\u66f4\u4ee4\u4ed6\u96be\u53d7\uff0c\u4e00\u9762 \u53c8\u4e0d\u7981\u6697\u6697\u5bfb\u601d\uff1a\u201c\u5979\u8bf4\uff1a\u770b\u4ed6\u7684\u9762\u5b50\uff0c\u2018\u4ed6\u2019\u53c8\u662f\u8c01\u5462?\u600e\u5730\u4f1a\u5c06\u8fd9\u79cd\u4e8b\u6258\u5979\u505a\uff0c\u800c\u5979\u4e5f \u7b54\u5e94\u4e86?\u90a3\u4e48\uff0c\u4ed6\u4eec\u4e4b\u95f4\u2026\u2026\u201d\u4ed6\u75db\u82e6\u5730\u626f\u52a8\u81ea\u5df2\u7684\u5934\u53d1\u2026\u3002\xb7 \u3000\u3000\u95e8\u5916\u5ffd\u5730\u8f7b\u54b3\u4e00\u58f0\uff0c\u6084\u7136\u8d70\u5165\u4e00\u4e2a\u9752\u519c\u5c0f\u5a62\uff0c\u624b\u91cc\u6367\u7740\u4e00\u53ea\u78a7\u7389\u8336\u76d8\uff0c\u76d8\u4e0a\u653e\u7740\u4e00\u53ea\u78a7 \u7389\u76d6\u7897\uff0c\u8885\u8885\u5a77\u5a77\u5730\u8d70\u5230\u5c55\u767d\u8eab\u524d\uff0c\u83b2\u8db3\u8f7b\u9519\uff0c\u5fae\u4e00\u88e3\u887d\uff0c\u8f7b\u8f7b\u9053\uff1a\u201c\u8bf7\u516c\u5b50\u7528\u6c64\uff01\u201d\u8bf4 \u7740\uff0c\u7ea4\u624b\u52a8\u5904\uff0c\u5df2\u5c06\u76d6\u7897\u6380\u5f00\u3002 \u3000\u3000\u5c55\u767d\u53ea\u89c9\u6ee1\u5ba4\u6e05\u9999\u6251\u9762\u800c\u6765\uff0c\u5fc3\u4e2d\u8fd8\u672a\u53ca\u591a\u4f5c\u601d\u7d22\uff0c\u8fd9\u9752\u8863\u5c0f\u5a62\u4fbf\u53c8\u5c06\u76d6\u7897\u6367\u5230\u4ed6\u9762 \u524d\uff0c\u4e00\u9762\u53c8\u4ece\u76d8\u4e2d\u53d6\u4e86\u4e2a\u78a7\u7389\u6c64\u5319\uff0c\u4e00\u5319\u5319\u5730\u5c06\u7897\u4e2d\u53c2\u6eba\uff0c\u5582\u5165\u5c55\u767d\u5634\u91cc\u3002 \u3000\u3000\u5c55\u767d\u832b\u7136\u5403\u5b8c\u4e86\u5b83\uff0c\u795e\u6c14\u84e6\u89c9\u4e00\u65fa\uff0c\u4f46\u5fc3\u91cc\u5374\u66f4\u611f\u96be\u53d7\uff0c\u81ea\u5df2\u6b64\u523b\u771f\u6709\u5982\u5728\u63a5\u53d7\u7740\u522b\u4eba \u7684\u65bd\u820d\u4e00\u6837\uff0c\u800c\u65bd\u820d\u81ea\u5df1\u7684\u5bf9\u8c61\uff0c\u5374\u5b8c\u5168\u662f\u4e3a\u7740\u53e6\u4e00\u4e2a\u4eba\u7684\u9762\u5b50\uff0c\u800c\u81ea\u5df1\u7ade\u8fde\u6b64\u4eba\u662f\u8c01\u90fd\u4e0d \u77e5\u9053\u3002 \u3000\u3000\u4e00\u60f3\u5230\u8fd9\u91cc\uff0c\u4ed6\u4fbf\u6068\u4e0d\u5f97\u5c06\u65b9\u624d\u5403\u4e0b\u53bb\u7684\u4e1c\u897f\u5168\u90e8\u5410\u51fa\u6765\u3002\u76ee\u5149\u8f6c\u5904\uff0c\u5374\u89c1\u95e8\u53e3\u53c8\u6709\u4eba \u5f71\u5fae\u5fae\u4e00\u95ea\uff0c\u63a5\u7740\u4fbf\u6709\u4e00\u58f0\u8f7b\u8106\u7684\u5a07\u7b11\u4ece\u95e8\u5916\u4f20\u6765\uff0c\u56db\u5468\u7684\u9759\u5bc2\uff0c\u4f3c\u4e4e\u5168\u90fd\u88ab\u5b83\u5212\u5f00\u3002 \u3000\u3000\u4f46\u5c55\u767d\u6b64\u523b\u7684\u5fc3\u60c5\uff0c\u5374\u662f\u6781\u4e0d\u9002\u5b9c\u627f\u53d7\u8fd9\u79cd\u7b11\u58f0\u7684\u3002 \u3000\u3000\u4ed6\u538c\u6076\u5730\u76b1\u4e86\u76b1\u7709\u5934\uff0c\u53ea\u89c1\u95e8\u5916\u53c8\u5df2\u6084\u7136\u8d70\u8fdb\u4e00\u4e2a\u963f\u5a1c\u7684\u8eab\u5f71\uff0c\u624b\u91cc\u7adf\u53c8\u662f\u7aef\u7740\u4e00\u4e2a\u9752 \u7389\u8336\u76d8\uff0c\u76d8\u4e0a\u53c8\u662f\u4e00\u53ea\u9752\u7389\u76d6\u7897\u3002\u8fd9\u8eab\u6750\u963f\u5a1c\u7684\u5999\u9f84\u5c11\u5973\uff0c\u4e00\u624b\u7aef\u7740\u8336\u76d8\uff0c\u4e00\u624b\u6276\u7740\u7ea4\u8170\uff0c \u83b2\u6b65\u4f9d\u4f9d\uff0c\u4f53\u6001\u5a5a\u5a76\uff0c\u50cf\u662f\u67f3\u4e1d\u4f3c\u7684\uff0c\u88ab\u5fae\u7b11\u5439\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u5c55\u767d\u6b64\u523b\u8f6c\u8fc7\u5934\u53bb\uff0c\u8fd9\u5c11\u5973\u8f7b\u8f7b\u4e00\u7b11\uff0c\u67d4\u58f0\u963f\u9053\uff1a\u201c\u516c\u5b50\uff0c\u4f60\u53ef\u8981\u5403\u4e9b\u4e1c\u897f?\u5440\u2014\u2014\u4f60 \u5df2\u6709\u597d\u591a\u5929\u6ca1\u6709\u5403\u4e1c\u897f\u4e86\u54e9\u3002\u201d \u3000\u3000\u5979\u8bf4\u8bdd\u7684\u58f0\u97f3\u8fd9\u4e48\u5a07\u67d4\uff0c\u6bcf\u53e5\u8bdd\u7684\u5c3e\u97f3\u90fd\u62d6\u5f97\u957f\u957f\u7684\uff0c\u5c31\u50cf\u662f\u6708\u591c\u4e4b\u4e0b\u8fdc\u65b9\u98d8\u6765\u7684\u9752\u7389 \u8427\u58f0\uff0c\u7bab\u58f0\u867d\u6b62\uff0c\u4f59\u97f3\u5374\u4e45\u4e45\u4e0d\u6b47\u3002 \u3000\u3000\u4f46\u662f\u8fd9\u5a07\u67d4\u7684\u8bed\u58f0\u542c\u8fdb\u5c55\u767d\u7684\u8033\u91cc\uff0c\u4ed6\u7d27\u76b1\u7740\u7684\u53cc\u7709\uff0c\u5374\u76b1\u5f97\u66f4\u6df1\u4e86\uff0c\u4ed6\u751a\u81f3\u89c9\u5f97\u8fd9\u5a07 \u67d4\u7684\u8bed\u58f0\u53ea\u4e0d\u8fc7\u662f\u7528\u6765\u63f6\u63c4\u8ba5\u7b11\u81ea\u5df1\u2014\u2014\u201c\u516c\u5b50\u2026\u2026\u597d\u591a\u5929\u6ca1\u6709\u5403\u4e1c\u897f\u4e86\u3002\u201d\u4ed6\u4e0d\u7531\u6697 \u201c\u54fc\u201d\u4e00\u58f0\uff0c\u5fd6\u9053\uff1a\u201c\u65bd\u820d\uff0c\u53c8\u662f\u65bd\u820d\uff01\u201d\u4e8e\u662f\u4ed6\u5927\u58f0\u53eb\u4e86\u8d77\u6765\uff1a\u7aef\u51fa\u53bb\uff0c\u7aef\u51fa\u53bb\uff0e\u201d \u3000\u3000\u8fd9\u5999\u9f84\u5c11\u5973\u811a\u6b65\u5df3\u505c\u5728\u4ed6\u7684\u5e8a\u524d\uff0c\u6b64\u523b\u4e0d\u7981\u4e3a\u4e4b\u4e00\u5f81\uff0c\u9053\uff1a\u2018\u4f60\u8fd9\u662f\u5e72\u4ec0\u4e48?\u201d\u8bed\u58f0\u7adf \u4ecd\u7136\u662f\u5a07\u67d4\u7684\uff0e \u3000\u3000\u5c55\u767d\u6697\u53f9\u4e00\u58f0\uff0c\u5fc3\u4e2d\u7a81\u53c8\u89c9\u5f97\u6709\u4e9b\u6b49\u6127\uff0c\u65e0\u8bba\u5982\u4f55\uff0c\u4eba\u5bb6\u5bf9\u81ea\u5df2\u603b\u662f\u4e00\u756a\u597d\u610f\uff0c\u81ea\u5df2\u5982 \u6b64\u76f8\u5f85\uff0c\u5c82\u975e\u592a\u8fc7\u65e0\u793c\u3002\u4e0d\u7981\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u59d1\u5a18\u7684\u597d\u610f\uff0c\u4e0d\u8fc7\u2014\u2014\u4f60\u8fd8\u662f\u7aef\u51fa\u53bb\u597d\u4e86\u3002\u201d\u4ed6 \u8bed\u6c14\u867d\u5df3\u548c\u7f13\u5f97\u591a\uff0c\u4f46\u5934\u5374\u4ecd\u672a\u8f6c\u56de\uff0c\u53ea\u5e0c\u671b\u81ea\u5df2\u56de\u8fc7\u5934\u6765\u7684\u65f6\u5019\uff0c\u623f\u4e2d\u53c8\u53ea\u6709\u81ea\u5df1\u4e00\u4e2a \u4eba\uff0c\u90a3\u4e48\uff0c\u4ed6\u4fbf\u80fd\u9759\u9759\u5730\u601d\u7d22\u4e00\u4e0b\u3002\u54ea\u77e5\u9053\u5c11\u5973\u5374\u53c8\u5a07\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u4e0d\u60f3\u5403\u4e1c\u897f\u5c31\u7b97 \u4e86\uff0c\u5e72\u5417\u8fd9\u4e48\u51f6\u5440\uff01\u4eba\u5bb6\u8d39\u4e86\u597d\u591a\u5fc3\u601d\uff0c\u5168\u5fc3\u5168\u610f\u5730\u5e2e\u4e86\u4f60\u8fd9\u4e00\u6b21\u5fd9\uff0c\u4f60\u2026\u2026\u4f60\u73b0\u5728\u5374\u8981\u53eb \u4eba\u5bb6\u51fa\u53bb\u3002\u201d \u3000\u3000\u8fd9\u51e0\u53e5\u8bdd\u8bf4\u5f97\u5c55\u767d\u4e3a\u4e4b\u4e00\u6014\uff0c\u56de\u8fc7\u5934\u6765\uff0c\u53ea\u89c1\u7ad9\u5728\u81ea\u5df1\u5e8a\u524d\u7684\u5c11\u5973\uff0c\u4e00\u8eab\u9526\u8863\uff0c\u4e91\u9b13\u9ad8 \u633d\uff0c\u795e\u6001\u867d\u7136\u5a07\u4fcf\u4e4b\u4e2d\uff0c\u5374\u53c8\u6d41\u9732\u51fa\u4e00\u79cd\u6e05\u96c5\u9ad8\u8d35\u4e4b\u6c14\u3002 \u3000\u3000\u8fd9\u5c11\u5973\u79cb\u6ce2\u4e00\u8f6c\uff0c\u77ac\u4e5f\u4e0d\u77ac\u5730\u51dd\u6ce8\u5728\u4ed6\u8138\u4e0a\uff0c\u7a81\u53c8\u5a07\u7b11\u9053\uff1a\u201c\u8bf4\u771f\u7684\uff0c\u4f60\u5bf9\u6211\u8fd9\u4e48\u51f6\uff0c \u771f\u662f\u4e0d\u5e94\u8be5\u4e86\uff0c\u4f60\u77e5\u4e0d\u77e5\u9053\uff0c\u6211\u4e3a\u4e86\u5e2e\u4f60\u7684\u5fd9\uff0c\u60f9\u4e86\u591a\u5c11\u9ebb\u70e6?\u4f60\u5440\u2026\u2026\u4f60\u771f\u662f\u4e0d\u77e5\u597d \u6b79\u3002\u201d \u3000\u3000\u7ea4\u8170\u4e00\u626d\uff0c\u5c06\u624b\u4e2d\u7684\u7389\u76d8\uff0c\u653e\u5230\u5c55\u767d\u5e8a\u5934\u7684\u5c0f\u51e0\u4e4b\u4e0a\uff0c\u81ea\u5df1\u7684\u8eab\u8eaf\uff0c\u5374\u8f7b\u8f7b\u5750\u5230\u5c55\u767d\u5e8a \u4fa7\uff0c\u63a5\u7740\u9053\uff1a\u201c\u6765\uff0c\u6211\u5582\u4f60\u5403\u4e1c\u897f\uff0c\u4f60\u8981\u662f\u751f\u4e86\u6c14\uff0c\u5c3d\u7ba1\u6c14\uff0c\u53ef\u522b\u628a\u81ea\u5df1\u6c14\u574f\u4e86\uff0c\u997f\u574f\u4e86\u809a \u5b50\uff0c\u90a3\u6211\u53ef\u4e0d\u7b54\u5e94\uff01\u201d\u5c55\u767d\u5446\u5446\u5730\u671b\u7740\u8fd9\u5c11\u5973\uff0c\u5fc3\u91cc\u66f4\u52a0\u8ff7\u60d1\uff0c\u4ed6\u4e0d\u7528\u8d39\u5fc3\u601d\u7d22\uff0c\u4fbf\u77e5\u9053\u81ea \u5df1\u548c\u8fd9\u5c11\u5973\u6839\u672c\u8fde\u9762\u90fd\u672a\u89c1\u8fc7\uff0c\u4f46\u8fd9\u5c11\u5973\u6b64\u523b\u5bf9\u81ea\u5df1\u8bf4\u8d77\u8bdd\u6765\uff0c\u5374\u50cf\u662f\u591a\u5e74\u77e5\u4ea4\u4f3c\u7684\uff0c\u65e2\u5173 \u6000\u53c8\u4eb2\u70ed\uff0c\u201c\u5979\u8fd8\u5e2e\u8fc7\u6211\u7684\u5fd9?\u201d\u4f46\u5e2e\u7684\u662f\u4ec0\u4e48\u5fd9\uff0c\u5c55\u767d\u5374\u5b8c\u5168\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u4e00\u9635\u9635\u6de1\u8c08\u7684\u5e7d\u9999\uff0c\u968f\u7740\u7a97\u5916\u5439\u5165\u7684\u5fae\u98ce\uff0c\u5439\u8fdb\u4ed6\u7684\u9f3b\u7aef\uff0c\u4ed6\u53ea\u89c9\u8fd9\u5c11\u5973\u5750\u5f97\u8d8a\u6765\u8d8a \u8fd1\uff0c\u4e00\u5f20\u5a07\u751c\u51d1\u5230\u81ea\u5df1\u773c\u524d\uff0c\u4ed6\u5bf9\u8fd9\u5c11\u5973\u867d\u65e0\u6076\u611f\uff0c\u4f46\u5979\u8fd9\u79cd\u8086\u5143\u5fcc\u60ee\u7684\u5927\u80c6\u4f5c\u98ce\uff0c\u5374\u53c8\u4f7f \u4ed6\u5fc3\u5c48\u6cdb\u8d77\u4e00\u79cd\u538c\u6076\u7684\u611f\u89c9\u3002 \u3000\u3000\u4ed6\u4e00\u8138\u6b63\u8272\uff0c\u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u5728\u4e0b\u4e0e\u59d1\u5a18\u7d20\u6627\u5e73\u751f\uff0c\u59cb\u5a18\u5982\u679c\u771f\u7684\u6709\u6069\u4e8e\u5728\u4e0b\uff0c\u5728\u4e0b\u65e5\u540e \u5fc5\u6709\u4ee5\u62a5\u7b54\u59d1\u5a18\uff0c\u4f46\u5728\u4e0b\u6b64\u523b\u5e76\u4e0d\u60f3\u5403\u4e1c\u897f\uff1b\u518d\u8005\u7537\u5973\u72ec\u5904\u4e00\u5ba4\uff0c\u4e5f\u8be5\u7a0d\u907f\u74dc\u7530\u674e\u4e0b\u4e4b\u5acc\uff0c \u8bf7\u59d1\u5a18\u8fd8\u662f\u7559\u610f\u4e9b\u7684\u597d\u3002\u201d \u3000\u3000\u54ea\u77e5\u8fd9\u5c11\u5973\u5750\u5728\u5e8a\u4fa7\uff0c\u4e00\u624b\u652f\u7740\u5e8a\u6cbf\uff0c\u4e00\u624b\u652f\u7740\u4e0b\u989d\uff0c\u4e00\u53cc\u660e\u76ee\uff0c\u5374\u671b\u5728\u5c4b\u9876\u4e0a\uff0c\u751f\u50cf \u6839\u672c\u6ca1\u6709\u542c\u5230\u4ed6\u7684\u8bdd\u4f3c\u7684\u3002 \u3000\u3000\u7b49\u5230\u5c55\u767d\u7684\u8bdd\u8bf4\u5b8c\uff0c\u5979\u65b9\u81ea\u7f13\u7f13\u5782\u4e0b\u5934\u6765\uff0c\u773c\u89d2\u659c\u659c\u4e00\u779f\uff0c\u5374\u53c8\u7acb\u523b\u6536\u56de\u76ee\u5149\uff0c\u671b\u5728\u81ea \u5df1\u7684\u4e00\u53cc\u7ea4\u7ea4\u83b2\u8db3\u4e0a\uff0c\u4f4e\u8bed\u9053\uff1a\u201c\u771f\u7684\u6709\u6069\u4e8e\u5728\u4e0b\uff0c\u771f\u7684\uff0c\u5728\u4e0b\u2026\u2026\u201d\u63a9\u53e3\u5657\u55e4\u4e00\u7b11\uff0c\u773c\u6ce2 \u6d41\u8f6c\uff0c\u779f\u4e86\u5c55\u767d\u4e00\u773c\uff1a\u201c\u96be\u9053\u4f60\u8ba4\u4e3a\u662f\u5047\u7684\u5417?\u201d\u7389\u624b\u8f7b\u62ac\uff0c\u4e00\u53ea\u6625\u8471\u822c\u7684\u624b\u6307\uff0c\u7b14\u76f4\u5730\u6307 \u5230\u5c55\u767d\u9762\u524d\uff1a\u201c\u544a\u8bc9\u4f60\uff0c\u8981\u4e0d\u662f\u6211\uff0c\u4f60\u5440\u2026\u2026\u4f60\u5c3d\u5c31\u88ab\u4eba\u62ac\u51fa\u53bb\u4e86\u3002\u201d\u8bed\u58f0\u8f7b\u67d4\u5a07\u8106\uff0c\u914d\u5408 \u7740\u5979\u7684\u773c\u6ce2\u548c\u52a8\u4f5c\uff0c\u4ee4\u4eba\u770b\u6765\uff0c\u53ea\u89c9\u5979\u4e3e\u624b\u62ac\u76ee\u4e4b\u95f4\uff0c\u90fd\u542b\u8574\u7740\u4e07\u5343\u79cd\u98ce\u60c5\u4eea\u6001\uff0c\u751f\u50cf\u662f\u5979 \u867d\u7136\u5728\u9a82\u4eba\uff0c\u53ef\u662f\u88ab\u9a82\u7684\u4eba\u5374\u4ecd\u7136\u6709\u798f\u4e86\u3002 \u3000\u3000\u5c55\u767d\u5446\u5446\u5730\u671b\u7740\u5979\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u4e5f\u4e0d\u77e5\u81ea\u5df2\u5fc3\u91cc\u662f\u4ec0\u4e48\u6ecb\u5473\uff0c\u4e00\u9762\u6697\u4e2d\u601d\u5fd6\uff1a\u201c\u5982\u6b64\u8bf4 \u6765\uff0c\u521a\u624d\u90a3\u9ed1\u8863\u5c11\u5973\u4e4b\u6765\uff0c\u4fbf\u662f\u53d7\u5979\u6240\u6258\u4e86\u2026\u2026\u201d\u5fc3\u5ff5\u4e00\u8f6c\uff1a\u201c\u90a3\u4e48\u5979\u662f\u8c01\u5462?\u96be\u9053\u5979\u4e5f\u662f \u90a3\u51cc\u98ce\u516c\u5b50\u7684\u59d0\u59b9\u4e0d\u6210?\u201d\u4ed4\u7ec6\u4e00\u770b\uff0c\u8fd9\uff0c\u8131\u7565\u5f62\u8ff9\uff0c\u867d\u548c\u90a3\u9ed1\u8863\u5c11\u5973\u7684\u8273\u5982\u6843\u674e\uff0c\u51b7\u82e5\u51b0 \u971c\uff0c\u4ee5\u53ca\u90a3\u201c\u51cc\u98ce\u516c\u5b50\u201d\u7684\u72c2\u5984\u9ad8\u50b2\uff0c\u51b7\u9177\u65e0\u60c5\uff0c\u5927\u4e0d\u76f8\u540c\uff0c\u4f46\u7709\u76ee\u4e4b\u95f4\uff0c\u5374\u548c\u4ed6\u4eec\u6709\u51e0\u5206 \u76f8\u4f3c\u4e4b\u5904\u3002\u4ed6\u65e0\u6cd5\u4e86\u89e3\u8fd9\u5144\u59b9\u4e09\u4eba\u7684\u751f\u6027\u600e\u4f1a\u6709\u5982\u6b64\u7684\u5dee\u5f02\uff0c\u4e00\u9762\u90e8\u53c8\u4e0d\u7981\u5927\u4e3a\u540c\u60c5\u90a3\u4e2d\u5e74 \u7f8e\u5987\uff0c\u8bd5\u60f3\u6709\u7740\u8fd9\u6837\u4e09\u4e2a\u513f\u5973\u7684\u6bcd\u4eb2\uff0c\u5bf9\u5176\u8eab\u5fc3\u7684\u8d1f\u62c5\uff0c\u53c8\u8be5\u662f\u591a\u4e48\u6c89\u91cd\u54e9\uff01 \u3000\u3000\u4ed6\u867d\u7136\u66fe\u7ecf\u542c\u8fc7\u201c\u6b66\u6797\u56db\u516c\u5b50\u201d\u7684\u58f0\u540d\uff0c\u4f46\u5bf9\u6c5f\u6e56\u4e2d\u8fd9\u58f0\u540d\u6781\u54cd\u7684\u56db\u4f4d\u201c\u516c\u5b50\u201d\u7684\u5bb6 \u4e16\uff0c\u5374\u53ea\u6709\u4e00\u4e2a\u6781\u4e3a\u6a21\u7cca\u7684\u5370\u8c61\u9762\u5df2\uff0c\u4ec5\u77e5\u9053\u8fd9\u56db\u4eba\u5bb6\u4e16\u4ff1\u90fd\u663e\u8d6b\u65e0\u6bd4\uff0c\u6b66\u529f\u7684\u5e08\u627f\uff0c\u66f4\u662f \u6765\u5386\uff0c\u4e0d\u51e1\uff0c\u662f\u4ee5\u751a\u81f3\u5728\u4e00\u773c\u77a5\u89c1\u201c\u5b89\u4e50\u516c\u5b50\u201d\u65f6\uff0c\u90fd\u4e0d\u80fd\u5f88\u5feb\u5730\u60f3\u51fa\u6b64\u4eba\u7a76\u7adf\u662f\u4ec0\u4e48\u8eab\u4efd \u7684\u3002 \u3000\u3000\u4ed6\u6c89\u601d\u534a\u664c\uff0c\u601d\u8def\u8d8a\u6765\u8d8a\u8fdc\uff0c\u76f4\u5230\u8fd9\u5c11\u5973\u53c8\u81ea\u4e00\u7b11\uff0c\u95ee\u9053\uff1a\u201c\u6211\u8bf4\u7684\u8bdd\u4f60\u542c\u5230\u6ca1\u6709?\u201d \u4ed6\u624d\u60f3\u8d77\u81ea\u5df1\u8fd8\u6ca1\u6709\u56de\u7b54\u4eba\u5bb6\u7684\u8bdd\u3002 \u3000\u3000\u201c\u4f46\u662f\uff0c\u6211\u8be5\u5982\u4f55\u6765\u56de\u7b54\u5979\u7684\u8bdd\u5462?\u201d\u4ed6\u4e0d\u7981\u53c8\u5728\u8e0c\u8e87\uff1a\u201c\u611f\u6fc0?\u201d\u8fd9\u5728\u4e00\u4e2a\u5014\u5f3a\u7684\u4eba\u6765 \u8bf4\uff0c\u90a3\u662f\u4e00\u79cd\u591a\u4e48\u96be\u4ee5\u8868\u8fbe\u7684\u60c5\u611f\u554a1\u4ed6\u4e00\u9762\u5bfb\u627e\u7740\u81ea\u5df1\u7684\u7b54\u8bdd\uff0c\u4e00\u9762\u5374\u53c8\u6697\u6697\u5fd6\u9053\uff1a\u201c\u5979 \u5988\u5988\u6551\u4e86\u6211\uff0c\u5979\u54e5\u54e5\u8981\u8d76\u51fa\u53bb\uff0c\u5979\u59d0\u59d0\u66ff\u6211\u89e3\u4e86\u56f4\uff0c\u5374\u662f\u53d7\u5979\u6240\u6258\uff0c\u4f46\u6211\u53c8\u6839\u672c\u4e0d\u8ba4\u5f97\u5979\u3002 \u5509\u2014\u2014\u8fd9\u5176\u4e2d\u7a76\u7adf\u662f\u600e\u4e48\u56de\u4e8b?\u4ed6\u4eec\u672c\u662f\u4e00\u5bb6\u4eba\uff0c\u4f46\u5f7c\u6b64\u7684\u5173\u7cfb\uff0c\u4e3a\u4ec0\u4e48\u5982\u6b64\u590d\u6742\u5462?\u201d \u3000\u3000\u4ed6\u672c\u5c31\u5f02\u5e38\u7d0a\u4e71\u7684\u601d\u6f6e\uff0c\u6b64\u523b\u66f4\u662f\u7d0a\u4e71\u4e0d\u582a\uff0c\u7ade\u8fde\u4e00\u53e5\u8be5\u8bf4\u7684\u8bdd\u90fd\u8bf4\u4e0d\u51fa\u6765\uff0c\u65b9\u81ea\u5b9a\u4e86 \u5b9a\u795e\uff0c\u54ea\u77e5\u8eab\u4fa7\u7a81\u5730\u54cd\u8d77\u4e00\u4e2a\u5176\u51b7\u5f7b\u9aa8\u7684\u58f0\u97f3\uff0c\u4e00\u5b87\u4e00\u5b87\u5730\u8bf4\u9053\uff1a\u5979\u8bf4\u7684\u8bdd\u4f60\u542c\u5230\u6ca1\u6709?\u201d \u3000\u3000\u5c55\u767d\u5fc3\u5934\u4e00\u51db\uff0c\u8f6c\u76ee\u671b\u53bb\uff0c\u5374\u89c1\u5e8a\u6d4b\u4e0d\u77e5\u4f55\u65f6\u591a\u4e86\u4e00\u4e2a\u8eab\u6750\u9880\u957f\u7684\u4eba\u5f71\uff0c\u4e00\u8eab\u69db\u697c\u7684\u8863 \u886b\uff0c\u4e00\u5934\u84ec\u677e\u7684\u4e71\u53d1\uff0c\u989d\u4e0b\u7684\u80e1\u987b\uff0c\u66f4\u662f\u4e71\u5f97\u60ca\u4eba\uff0c\u4e0e\u8fd9\u5ead\u9662\u4e2d\u7684\u4e00\u5207\u90fd\u4e0d\u5927\u76f8\u79f0\uff0c\u53ea\u6709\u90a3 \u4e00\u53cc\u5229\u5982\u95ea\u7535\u7684\u773c\u775b\uff0c\u6b63\u5728\u77ac\u4e5f\u4e0d\u77ac\u5730\u671b\u7740\u81ea\u5df1\uff0c\u76ee\u4e2d\u7684\u5bd2\u610f\uff0c\u6bd4\u8bed\u6c14\u8fd8\u91cd\u4e09\u5206\u3002 \u3000\u3000\u8fd9\u7a81\u6765\u7684\u602a\u4eba\uff0c\u8fd9\u7a81\u6765\u7684\u95ee\u8bdd\uff0c\u4f7f\u5f97\u5c55\u767d\u66f4\u52a0\u6014\u4f4f\u4e86\u3002 \u3000\u3000\u90a3\u5c11\u5973\u9762\u4e0a\u4ecd\u7136\u5e26\u7740\u6625\u82b1\u822c\u7684\u7b11\u5bb9\uff0c\u4e5f\u6ca1\u6709\u53bb\u671b\u8fd9\u602a\u4eba\uff0c\u8ddf\uff0c\u4eff\u4f5b\u8fd9\u602a\u4eba\u7684\u51fa\u73b0\uff0c\u6839\u672c \u5c31\u5728\u5979\u7684\u610f\u6599\u4e4b\u4e2d\u4f3c\u7684\u3002 \u3000\u3000\u4e71\u53d1\u602a\u4eba\u7709\u5cf0\u5fae\u76b1\uff0c\u51b7\u51b7\u53c8\u9053\uff1a\u201c\u4f60\u542c\u5230\u6211\u8bf4\u7684\u8bdd\u6ca1\u6709?\u201d \u3000\u3000\u5c55\u767d\u5931\u795e\u5730\u671b\u7740\u4ed6\uff0c\u4ecd\u672a\u7b54\u8bdd\u3002\u4e71\u53d1\u602a\u4eba\u51b7\u51b7\u4e00\u7b11\uff0c\u970d\u7136\u4f38\u51fa\u624b\u6765\uff0c\u6b8b\u7834\u7684\u8863\u8896\u4e5f\u968f\u4e4b \u626c\u8d77\uff0c\u5e26\u8d77\u4e00\u9635\u9635\u5f3a\u52b2\u7684\u98ce\u58f0\u3002 \u3000\u3000\u90a3\u5c11\u5973\u9762\u4e0a\u7b11\u5bb9\u672a\u655b\uff0c\u7a81\u5730\u4e00\u56de\u8eab\uff0c\u62b1\u4f4f\u8fd9\u4e71\u53d1\u602a\u4eba\u7684\u624b\u81c2\uff0c\u5728\u4ed6\u8033\u8fb9\u4f4e\u4f4e\u8bf4\u4e86\u4e24\u53e5 \u8bdd\uff0c\u602a\u4eba\u76ee\u5149\u4e2d\u7684\u5a01\u5149\uff0c\u7acb\u523b\u5c3d\u655b\uff0c\u6e29\u67d4\u5730\u671b\u4e86\u5c11\u5973\u51e0\u773c\uff0c\u624b\u81c2\u4e00\u4f38\u4e00\u7f29\uff0c\u8eab\u5f62\u7a81\u5730\u7535\u95ea\u800c \u9000\uff0c\u5934\u4e5f\u672b\u56de\uff0c\u4fbf\u4ece\u5f00\u542f\u7684\u7a97\u4e2d\u63a0\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u7a97\u6237\u867d\u4e0d\u5c0f\uff0c\u4f46\u53ea\u67b6\u5f00\u4e00\u534a\uff0c\u8fd9\u602a\u4eba\u8eab\u5f62\u987e\u957f\uff0c\u4e0d\u77e5\u600e\u5730\uff0c\u7ade\u8fde\u671b\u90fd\u672a\u671b\u4e00\u773c\uff0c\u4fbf\u4ece\u90a3 \u8fdc\u6bd4\u4ed6\u8eab\u5f62\u72ed\u5c0f\u7684\u7a97\u4e2d\u63a0\u51fa\uff0c\u5c31\u50cf\u4ed6\u80cc\u540e\u957f\u4e86\u773c\u775b\uff0c\u53c8\u50cf\u4ed6\u8eab\u8eaf\u53ef\u4ee5\u968f\u610f\u4f38\u7f29\u4f3c\u7684\u3002 \u3000\u3000\u4ed6\u6765\u5f97\u7a81\u7136\uff0c\u53bb\u5f97\u66f4\u662f\u7a81\u7136\uff0c\u5c55\u767d\u671b\u7740\u4ed6\u7684\u5019\u5ffd\u6765\u53bb\uff0c\u5fc3\u91cc\u66f4\u662f\u60ca\u7591\uff0c\u53ea\u89c9\u81ea\u5df1\u6240\u7ecf\u6240 \u904f\uff0c\u90fd\u6709\u5982\u68a6\u5883\u4e00\u822c\u3002 \u3000\u3000\u90a3\u5c11\u5973\u7f13\u7f13\u56de\u8fc7\u5934\u6765\uff0c\u671b\u7740\u5c55\u81ea\u683c\u683c\u4e00\u7b11\u9053\uff1a\u201c\u4f60\u6015\u4e0d\u6015\u4ed6?\u201d \u3000\u3000\u5c55\u767d\u832b\u7136\u6447\u4e86\u6447\u5934\uff0c\u9053\uff1a\u4ed6\u662f\u8c01?\u6211\u4e3a\u4ec0\u4e48\u8981\u6015\u4ed6?\u201d \u3000\u3000\u8fd9\u5c11\u5973\u4f38\u624b\u4e00\u62e2\u9b03\u811a\uff0c\u53c8\u5728\u5c55\u767d\u7684\u5e8a\u4fa7\u5750\u4e86\u4e0b\u6765\uff0c\u4e00\u9762\u4ecd\u81ea\u5a07\u7b11\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u6015\u4ed6? \u4ed6\u7684\u6b66\u529f\u53ef\u771f\u5389\u5bb3\u5440\uff0c\u8fde\u5927\u54e5\u548c\u7239\u7239\u90fd\u8bf4\u4ed6\u6b66\u529f\u6df1\u4e0d\u53ef\u6d4b\u2018\u53ea\u662f\u4ed6\u4ece\u6765\u4e0d\u548c\u4eba\u52a8\u624b\uff0c\u662f\u4ee5\u4ed6 \u7684\u6b66\u529f\u5230\u5e95\u6709\u591a\u9ad8\uff0c\u8c01\u4e5f\u4e0d\u77e5\u9053\uff0c\u53ef\u662f\u2026\u2026\u563f\u563f\uff0c\u8981\u662f\u6709\u8c01\u6b3a\u8d1f\u4e86\u6211\u5440\uff0c\u4ed6\u8001\u4eba\u5bb6\u5c31\u4e0d\u7b54\u5e94 \u4e86\uff0c\u975e\u5c06\u90a3\u4eba\u6253\u4e2a\u534a\u6b7b\u4e0d\u53ef\u3002\u201d\u5979\u8bed\u58f0\u5fae\u987f\uff0c\u53c8\u9053\uff1a\u201c\u4e0a\u6b21\u4e00\u4e2a\u4ece\u9c81\u5317\u6765\u7684\uff0c\u53eb\u4ec0\u4e48\u2018\u4e09\u7fc5 \u7c89\u8776\u2019\u7684\u5bb6\u4f19\u62dc\u89c1\u7239\u7239\uff0c\u5728\u82b1\u56ed\u91cc\u78b0\u89c1\u4e86\u6211\uff0c\u4ee5\u4e3a\u6211\u597d\u6b3a\u8d1f\uff0c\u5c31\u5bf9\u6211\u8bf4\u4e86\u4e24\u53e5\u96be\u542c\u7684\u8bdd\uff0c\u6211 \u5fc3\u91cc\u53c8\u7f9e\u53c8\u6c14\uff0c\u6b63\u60f3\u52a8\u624b\u6559\u8bad\u4ed6\uff0c\u4f46\u662f\u8fd8\u7b49\u4e0d\u5230\u6211\u52a8\u624b\uff0c\u96f7\u5927\u53d4\u4ed6\u8001\u4eba\u5bb6\u597d\u50cf\u8ddf\u5728\u6211\u8eab\u540e\u4f3c \u7684\uff0c\u90a3\u5c0f\u5b50\u770b\u89c1\u4ed6\u8001\u4eba\u5bb6\u6765\u5230\uff0c\u8fd8\u8981\u901e\u5a01\u98ce\uff0c\u4ed6\u8001\u4eba\u5bb6\u8fde\u8bdd\u90fd\u6ca1\u6709\u8bf4\uff0c\u8f7b\u8f7b\u4e00\u62cd\u624b\uff0c\u5c31\u5c06\u90a3 \u5c0f\u5b50\u6d3b\u6d3b\u5730\u5288\u6b7b\u5728\u4e00\u4e1b\u73ab\u7470\u82b1\u4e0b\u4e86\uff0c\u8ba9\u4ed6\u2026\u2026\u6b7b\u4e86\u8fd8\u505a\u4e2a\u98ce\u6d41\u9b3c\u3002\u201d \u3000\u3000\u5979\u54ad\u54ad\u72d0\u72d0\u8bf4\u4e86\u4e00\u5927\u5957\uff0c\u8bf4\u5230\u540e\u6765\uff0c\u53c8\u5657\u55e4\u7b11\u51fa\u58f0\u6765\uff0c\u8fd9\u5c11\u5973\u65e2\u50cf\u662f\u8f7b\u6311\uff0c\u53c8\u50cf\u662f\u5929 \u771f\uff0c\u4ec0\u4e48\u8bdd\u90fd\u6562\u8bf4\u3002\u5c55\u767d\u4e00\u9762\u542c\u7740\u5979\u7684\u8bdd\uff0c\u5fc3\u4e2d\u4e00\u9762\u4e0d\u505c\u5730\u601d\u5fd6\uff1a\u201c\u8fd9\u4e71\u53d1\u602a\u4eba\u662f\u8c01?\u600e\u5730 \u80fd\u5728\u8fd9\u6df1\u6c89\u4f3c\u6d77\u3001\u6709\u5982\u4faf\u95e8\u822c\u7684\u5bb6\u5ead\u4e2d\u6765\u53bb\u81ea\u5982?\u201d \u3000\u3000\u53c8\u5fd6\u9053\uff0c\u201c\u5979\u4e3a\u7239\u7239\u5230\u5e95\u662f\u4ec0\u4e48\u8eab\u4efd?\u600e\u5730\u8fde\u91c7\u82b1\u5927\u76d7\u90fd\u4f1a\u6765\u62dc\u89c1\u4ed6?\u201d \u3000\u3000\u542c\u5230\u540e\u6765\uff0c\u8fd9\u5c11\u5973\u8bf4\u201c\u4e09\u7fc5\u7c89\u8776\u201d\u6b7b\u5728\u82b1\u4e0b\uff0c\u8fd8\u66ff\u4ed6\u4e0b\u4e86\u4e2a\u201c\u98ce\u6d41\u9b3c\u201d\u7684\u6ce8\u811a\uff0c\u53c8\u4e0d\u7981 \u5728\u5fc3\u4e2d\u6697\u7b11\uff1a\u201c\u5979\u600e\u5730\u8fde\u8fd9\u8bdd\u90fd\u8bf4\u5f97\u51fa\u53e3\u3002\u2019 \u3000\u3000\u4ed6\u5374\u4e0d\u77e5\u9053\u8fd9\u5c11\u5973\u81ea\u5e7c\u5a07\u7eb5\u6210\u6027\uff0c\u4ece\u6765\u4e0d\u77e5\u9053\u4ec0\u4e48\u662f\u5bb3\u7f9e\uff0c\u66f4\u4e0d\u77e5\u9053\u4ec0\u4e48\u662f\u754f\u60e7\uff0c\u6b64\u523b \u201c\u5657\u54e7\u201d\u4e00\u7b11\uff0c\u53c8\u81ea\u8bf4\u9053\uff1a\u201c\u65b9\u624d\u96f7\u5927\u53d4\u4f38\u51fa\u624b\u6765\uff0c\u82e5\u4e0d\u662f\u6211\u7ad9\u5728\u65c1\u8fb9\uff0c\u4f60\u8fd9\u6761\u5c0f\u547d\u4e5f\u7b97\u5b8c \u4e86\u3002\u201d\u5979\u63a9\u53e3\u4e00\u7b11\uff0c\u5ffd\u53c8\u5e7d\u5e7d\u957f\u53f9\u4e86\u4e00\u58f0\uff0c\u53cc\u76ee\u671b\u7740\u7a97\u5916\u3002 \u3000\u3000\u5c55\u767d\u89c1\u5979\u5ffd\u800c\u5a07\u7b11\uff0c\u5ffd\u800c\u957f\u53f9\uff0c\u5fc3\u4e2d\u6b63\u81ea\u8be7\u5f02\uff0c\u5374\u542c\u5979\u63a5\u7740\u9053\uff1a\u201c\u771f\u5947\u602a\uff0c\u81ea\u4ece\u5988\u5988\u628a \u4f60\u5e26\u56de\u6765\u90a3\u5929\uff0c\u6211\u7b2c\u4e00\u773c\u770b\u89c1\u4f60\uff0c\u5c31\u559c\u6b22\u2026\u2026\u201d \u3000\u3000\u5979\u867d\u662f\u5929\u771f\u672a\u62bf\uff0c\u5a07\u7eb5\u6210\u6027\uff0c\u4f46\u4e0b\u9762\u7684\u8bdd\uff0c\u4ecd\u662f\u8bf4\u4e0d\u4e0b\u53bb\uff0c\u4e24\u989d\u5fae\u5fbd\u4e00\u7ea2\uff0c\u4f38\u624b\u4e00\u62e2\u9b03 \u53d1\uff0c\u65b9\u81ea\u63a5\u7740\u9053\uff1a\u201c\u6240\u4ee5\u540e\u6765\u5988\u5988\u4e0d\u80fd\u6765\u770b\u4f60\u7684\u65f6\u5019\uff0c\u6211\u5c31\u5929\u5929\u6765\u770b\u4f60\uff0c\u4eca\u5929\u5927\u54e5\u4ece\u592a\u6e56\u56de \u6765\uff0c\u6211\u5c31\u77e5\u9053\u8981\u7cdf\uff0c\u4ee5\u5927\u54e5\u7684\u813e\u6c14\uff0c\u4e00\u5b9a\u4f1a\u628a\u4f60\u4ece\u4ed6\u623f\u91cc\u6454\u51fa\u6765\uff0c\u5988\u5988\u4e0d\u5728\uff0c\u6211\u53c8\u6015\u5927\u54e5\uff0c \u60f3\u6765\u60f3\u53bb\uff0c\u53ea\u6709\u642c\u51fa\u5927\u59d0\u6765\u5f53\u6551\u5175\uff0c\u4f60\u4e0d\u77e5\u9053\uff0c\u5927\u59d0\u7684\u813e\u6c14\u53ef\u8ddf\u6211\u4e0d\u4e00\u6837\uff0c\u4e00\u5e74\u4e4b\u95f4\uff0c\u4e5f\u96be \u542c\u5230\u5979\u8bf4\u4e0a\u53e5\u8bdd\uff0c\u6211\u8bf4\u597d\u8bf4\u6b79\uff0c\u592e\u6c42\u4e86\u534a\u5929\uff0c\u624d\u7b97\u628a\u5979\u8bf7\u6765\uff0c\u4f60\u5440\u2026\u2026\u4f60\u5374\u4e0d\u627f\u60c5\u3002\u201d \u3000\u3000\u5c55\u767d\u867d\u672c\u5bf9\u5979\u7684\u653e\u7eb5\u4e4b\u6001\uff0c\u6781\u4e3a\u4e0d\u559c\uff0c\u4f46\u6b64\u523b\u89c1\u5979\u5982\u6b64\u5bf9\u5f85\u81ea\u5df2\uff0c\u5fc3\u4e2d\u4ea6\u4e0d\u7981\u5927\u751f\u611f\u6fc0 \u4e4b\u60c5\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u59d1\u5a18\u5982\u6b64\u5bf9\u5f85\u4e8e\u6211\uff0c\u5728\u4e0b\u5b9e\u662f\u611f\u6fc0\u4e0d\u5c3d\uff0c\u54ea\u6709\u4e0d\u627f\u60c5\u7684\u9053\u7406\uff01\u201d \u3000\u3000\u8fd9\u5c11\u5973\u9762\u5b54\u4e00\u677f\uff0c\u6545\u4f5c\u55d4\u607c\u4e4b\u6001\uff0c\u9053\uff1a\u201c\u8c01\u8981\u4f60\u611f\u6fc0\u6211?\u8c01\u8981\u4f60\u627f\u60c5\uff01\u201d \u3000\u3000\u5c55\u767d\u4e00\u695e\uff0c\u5374\u89c1\u5979\u53c8\u5657\u54e7\u7b11\u51fa\u58f0\u6765\uff0c\u7ea4\u624b\u6387\u8d77\u8863\u89d2\uff0c\u7f13\u7f13\u5f04\u7740\uff0c\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u53ea\u8981\u4f60\u77e5 \u9053\u6211\u5bf9\u4f60\u597d\uff0c\u4e0d\u8981\u518d\u51f6\u72e0\u72e0\u5730\u5bf9\u6211\uff0c\u6211\u5c31\u9ad8\u5174\u4e86\u3002\u201d \u3000\u3000\u5c55\u767d\u867d\u7136\u6781\u4e3a\u62d8\u8c28\uff0c\u6b64\u523b\u5fc3\u4e2d\u4ea6\u4e0d\u7531\u5fae\u5fae\u4e00\u8361\uff0c\u53ea\u89c9\u8fd9\u5c11\u5973\u5bf9\u81ea\u5df2\u7684\u60c5\u611f\u7adf\u662f\u5982\u6b64\u76f4 \u7387\uff0c\u4e0d\u52a0\u534a\u4e1d\u63a9\u9970\uff0c\u4ed6\u81ea\u5e7c\u5b64\u96f6\uff0c\u957f\u6210\u540e\u523b\u82e6\u4e60\u6b66\uff0c\u4e00\u751f\u4e4b\u4e2d\u51e0\u66fe\u4eab\u53d7\u8fc7\u8fd9\u79cd\u6e29\u6696\u7684\u60c5\u610f\uff0c \u4e00\u65f6\u4e4b\u95f4\uff0c\u4e0d\u89c9\u5446\u5446\u5730\u695e\u4f4f\u4e86\uff0c\u671b\u7740\u8fd9\u5c11\u5973\uff0c\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u8fd9\u5c11\u5973\u5f04\u7740\u8863\u89d2\uff0c\u4e00\u9762\u53c8\u9053\uff1a\u201c\u4f60\u59d3\u4ec0\u4e48?\u53eb\u4ec0\u4e48?\u6211\u95ee\u5988\u5988\uff0c\u5988\u5988\u4e5f\u8bf4\u4e0d\u77e5\u9053\uff0c\u771f\u5947 \u602a\uff0c\u5988\u5988\u4e5f\u662f\u8ddf\u5927\u59d0\u4e00\u6837\uff0c\u5e73\u5e38\u603b\u662f\u4e00\u526f\u51b7\u82e5\u51b0\u7bb1\u7684\u6837\u5b50\uff0c\u96be\u5f97\u770b\u5230\u5979\u8001\u4eba\u5bb6\u7b11\u4e00\u7b11\uff0c\u4f46\u5bf9 \u4f60\u5374\u4e5f\u50cf\u662f\u5f88\u5173\u5fc3\u7684\u6837\u5b50\uff0c\u6211\u672c\u6765\u4ee5\u4e3a\u4f60\u8ddf\u5979\u8001\u4eba\u5bb6\u4e00\u5b9a\u5f88\u719f\uff0c\u54ea\u77e5\u5979\u8001\u4eba\u5bb6\u8fde\u4f60\u7684\u540d\u5b57\u90fd \u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u5c55\u767d\u5fae\u53f9\u4e00\u58f0\uff0c\u524d\u5c18\u5f80\u4e8b\uff0c\u53c8\u590d\u6d8c\u4e0a\u5fc3\u5934\uff0c\u5fc3\u60f3\uff1a\u82e5\u4e0d\u662f\u90a3\u4f4d\u4e2d\u5e74\u7f8e\u5987\u4ed7\u4e49\u63f4\u624b\uff0c\u81ea\u5df1 \u53ea\u6015\u6b64\u523b\u5df2\u66b4\u5c38\u8352\u91ce\u4e86\u3002\u4e0d\u7981\u6697\u56db\u5fd6\u9053\uff1a\u201c\u4eba\u5bb6\u5bf9\u6211\u6709\u5982\u6b64\u5927\u6069\uff0c\u6211\u5374\u8fde\u4eba\u5bb6\u7684\u540d\u5b57\u90fd\u4e0d\u77e5 \u9053\uff01\u201d \u3000\u3000\u76ee\u5149\u8f6c\u52a8\uff0c\u6e05\u4e86\u6e05\u5589\u5499\uff0c\u9053\uff1a\u201c\u4ee4\u5802\u5927\u4eba\uff0c\u9ad8\u8d35\u6148\u7965\uff0c\u6709\u5979\u6148\u822a\u666e\u6e21\uff0c\u5979\u8001\u4eba\u5bb6\u5bf9\u6211\u7684 \u6069\u60c5\uff0c\u5b9e\u5728\u4f7f\u6211\u94ed\u611f\uff0c\u59d1\u5a18\u5982\u4e0d\u89c1\u602a\u7684\u8bdd\uff0c\u4e0d\u77e5\u53ef\u5426\u5c06\u5979\u8001\u4eba\u5bb6\u7684\u540d\u8bb3\u544a\u8bc9\u6211\uff0c\u4e5f\u8ba9\u6211\u2026\u3002 oD \u3000\u3000\u8fd9\u5c11\u5973\u683c\u683c\u4e00\u7b11\uff0c\u622a\u65ad\u4e86\u4ed6\u7684\u8bdd\uff0c\u9053\uff1a\u201c\u770b\u4e0d\u51fa\u4f60\u8bf4\u8bdd\u9178\u6e9c\u6e9c\u7684\uff0c\u5012\u50cf\u4e2a\u7a77\u79c0\u624d\u3002\u201d \u3000\u3000\u5c55\u767d\u9762\u988a\u4e00\u7ea2\uff0c\u5374\u89c1\u5979\u53c8\u9053\uff1a\u201c\u6211\u7239\u7239\u59d3\u6155\u5bb9\uff0c\u6211\u5927\u54e5\u3001\u5927\u59d0\u4e5f\u59d3\u6155\u5bb9\uff0c\u4f60\u731c\u6211\u59d3\u4ec0 \u4e48?\u201d \u3000\u3000\u5c55\u767d\u4e00\u5446\uff0c\u5fc3\u60f3\u8fd9\u5c11\u5973\u771f\u662f\u61a8\u5f97\u53ef\u4ee5\uff0c\u600e\u5730\u5411\u6211\u95ee\u8fd9\u79cd\u8bdd\uff0c\u96be\u9053\u6211\u662f\u5446\u5b50\u4e2d\u6210?\u53e3\u4e2d\u5374 \u9053\uff1a\u201c\u59d1\u5a18\u60f3\u5fc5\u4e5f\u662f\u59d3\u6155\u5bb9\u4e86\u3002\u201d \u3000\u3000\u54ea\u77e5\u8fd9\u5c11\u5973\u5374\u6447\u4e86\u6447\u5934\uff0c\u62cd\u624b\u7b11\u9053\uff1a\u4f60\u731c\u9519\u4e86\uff0c\u6211\u4e0d\u59d3\u6155\u5bb9\uff0c\u6211\u59d3\u5c55\uff0c\u8ddf\u6211\u5988\u5988\u7684 \u59d3\u3002\u201d\u795e\u8272\u4e4b\u95f4\uff0c\u6781\u4e3a\u9ad8\u5174\u5f97\u610f\u3002 \u3000\u3000\u5c55\u767d\u5fc3\u4e2d\u6697\u7b11\uff0c\u7b54\u9053\uff1a\u201c\u5982\u6b64\u6211\u5f53\u7136\u731c\u4e0d\u51fa\u4e86\u3002\u201d \u3000\u3000\u4e00\u9762\u53c8\u4e0d\u6148\u6697\u4e2d\u601d\u5fd6\uff1a\u201c\u539f\u6765\u90a3\u4f4d\u592b\u4eba\u4e0e\u6211\u7adf\u662f\u540c\u59d3\u3002\u201d \u3000\u3000\u5374\u89c1\u90a3\u5c11\u5973\u4e00\u7b11\u53c8\u9053\uff1a\u201c\u770b\u4f60\u7684\u6837\u5b50\uff0c\u4e5f\u50cf\u662f\u6b66\u6797\u4e2d\u4eba\uff0c\u600e\u5730\u8fde\u6211\u4eec\u5bb6\u7684\u540d\u8bb3\u90fd\u6ca1\u6709\u542c \u8fc7?\u201d\u8a00\u4e0b\u4e4b\u610f\uff0c\u5927\u6709\u51e1\u662f\u6b66\u6797\u4e2d\u4eba\u90fd\u8be5\u77e5\u9053\u5979\u5bb6\u7684\u6837\u5b50\u3002 \u3000\u3000\u5c55\u767d\u51dd\u6ce8\u7740\u5979\uff0c\u53ea\u89c9\u8fd9\u5c11\u5973\u5a07\u61a8\u4e4b\u6001\uff0c\u73b0\u4e8e\u8f9e\u8272\uff0c\u5fc3\u4e2d\u539f\u672c\u4ee5\u4e3a\u5979\u751a\u662f\u8f7b\u6311\u7684\u611f\u89c9\uff0c\u6b64 \u523b\u5df2\u8361\u7136\u65e0\u5b58\u3002 \u3000\u3000\u90a3\u5c11\u5973\u79cb\u6ce2\u4e00\u8f6c\uff0c\u9047\u5230\u4ed6\u76ee\u5149\uff0c\u4e0d\u89c9\u8f7b\u8f7b\u4e00\u7b11\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u544a\u8bc9\u4f60\uff0c\u6211\u53eb\u5c55\u5a49\u513f\uff0c\u4f60\u53eb \u4ec0\u4e48\u540d\u5b57\uff0c\u600e\u4e48\u4e0d\u544a\u8bc9\u6211?\u4f60\u7684\u7239\u7239\u5988\u5988\u8fd8\u5728\u5417?\u5728\u54ea\u91cc?\u4f60\u6709\u6ca1\u6709\u2026\u2026\u201d \u3000\u3000\u5979\u5fae\u54ac\u4e0b\u5507\uff0c\u8f7b\u8f7b\u4e00\u7b11\uff0c\u5782\u4e0b\u5934\u53bb\uff0c\u63a5\u9053\uff1a\u201c\u592a\u592a\u3002\u201d \u3000\u3000\u5979\u4e00\u8fde\u95ee\u4e86\u4e94\u53e5\uff0c\u53e5\u53e5\u90fd\u95ee\u7740\u5c55\u767d\u5fc3\u4e2d\u7684\u521b\u75db\u4e4b\u5904\uff0c\u4ed6\u695e\u4e86\u534a\u664c\uff0c\u957f\u53f9\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a \u201c\u5728\u4e0b\u4e5f\u59d3\u5c55\uff0c\u53eb\u5c55\u767d\uff0c\u5bb6\u7236\u5bb6\u6bcd\u90fd\u2026\u2026\u90fd\u5df2\u6545\u53bb\u4e86\uff0c\u6211\u5b64\u8eab\u98d8\u6cca\uff0c\u4e00\u65e0\u6240\u6210\uff0c\u8fde\u5bb6\u7236\u7684\u6df1 \u4ec7\uff0c\u90fd\u672a\u5f97\u62a5\u3002\u201d \u3000\u3000\u4ed6\u5fc3\u4e2d\u79ef\u90c1\u591a\u5e74\uff0c\u59cb\u7ec8\u6ca1\u6709\u4e00\u4e2a\u503e\u8bc9\u7684\u5bf9\u8c61\uff0c\u6b64\u523b\u89c1\u8fd9\u5c11\u5973\u5bf9\u81ea\u5df2\u6709\u5982\u6b64\u76f4\u7387\u7684\u60c5\u611f\uff0c \u4e0d\u89c9\u7279\u5fc3\u4e2d\u7684\u79ef\u90c1\uff0c\u90fd\u8bf4\u4e86\u51fa\u6765\u3002 \u3000\u3000\u53ea\u89c1\u5f97\u5c55\u5a49\u513f\u773c\u5708\u8d8a\u6765\u8d8a\u7ea2\uff0c\u7ec8\u4e8e\u5fcd\u4e0d\u4f4f\uff0c\u4e24\u6ef4\u6676\u83b9\u7684\u6cea\u73e0\uff0c\u593a\u7736\u800c\u51fa\uff0c\u6cbf\u7740\u5979\u4fcf\u7f8e\u7684 \u9762\u988a\u7f13\u7f13\u6d41\u4e0b\u3002\u4eba\u7c7b\u7684\u60c5\u611f\uff0c\u539f\u672c\u5c31\u662f\u90a3\u4e48\u5947\u5999\uff0c\u6709\u7684\u4eba\u4f60\u4e0e\u4ed6\u76f8\u4ea4\u4e00\u751f\uff0c\u4e5f\u4e0d\u4f1a\u542c\u5230\u4ed6\u8bf4 \u51fa\u4e00\u53e5\u771f\u5fc3\u7684\u8bdd\uff0c\u53e6\u5916\u4e00\u4e9b\u4eba\u4f60\u4e0e\u4ed6\u5306\u5306\u4e00\u9762\uff0c\u5374\u4f1a\u5c3d\u503e\u5fc3\u4e8b\u3002\u5c55\u767d\u8d8a\u8bf4\u8d8a\u89c9\u60b2\u4ece\u4e2d\u6765\uff0c\u96be \u4ee5\u6291\u5236\uff0c\u7adf\u5fd8\u4e86\u81ea\u5df1\u503e\u8bc9\u7684\u5bf9\u8c61\uff0c\u4e0d\u8fc7\u662f\u4e00\u4e2a\u65b9\u624d\u76f8\u8bc6\u7684\u5a07\u61a8\u5c11\u5973\u3002 \u3000\u3000\u4ed6\u7684\u8bed\u58f0\u662f\u4f4e\u6c89\u7684\uff0c\u8fd9\u95f4\u7cbe\u96c5\u7684\u623f\u95f4\uff0c\u4e5f\u4eff\u4f5b\u88ab\u60b2\u54c0\u7684\u6c14\u6c1b\u5145\u6ee1\u3002 \u3000\u3000\u54ea\u77e5\u4ed6\u8bdd\u672a\u8bf4\u5b8c\uff0c\u7a97\u5916\u7a81\u53c8\u95ea\u7535\u6bbf\u63a0\u5165\u4e00\u6761\u4eba\u5f71\uff0c\u6251\u5230\u5c55\u767d\u7684\u5e8a\u524d\uff0c\u4e00\u628a\u6293\u4f4f\u4ed6\u7684\u624b \u81c2\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u4f60\u662f\u8c01?\u5c55\u4e91\u5929\u662f\u4f60\u4ec0\u4e48\u4eba?\u201d \u3000\u3000\u5c55\u767d\u4e00\u60ca\u4e4b\u4e0b\uff0c\u53ea\u89c9\u81ea\u5df1\u7684\u624b\u8155\uff0c\u5176\u75db\u6b32\u6298\uff0c\u4e0d\u77e5\u4e0d\u89c9\u7684\u624b\u638c\u4e00\u677e\uff0c\u638c\u4e2d\u7ade\u843d\u4e0b\u4e00\u56e2\u4e71 \u53d1\u6765\u3002 \u3000\u3000\u539f\u6765\u4ed6\u65b9\u624d\u5fc3\u60c5\u79ef\u90c1\u96be\u6d88\uff0c\u60b2\u6124\u586b\u5e99\uff0c\u7ade\u5c06\u81ea\u5df2\u7684\u5934\u53d1\u626f\u4e0b\u4e00\u7efa\uff0c\u6b64\u523b\u843d\u5728\u6de1\u9752\u8272\u7684\u9526 \u88f3\u4e0a\uff0c\u4fbf\u5206\u5916\u523a\u76ee\u3002 \u3000\u3000\u5239\u90a3\u4e4b\u95f4\uff0c\u4ed6\u5fc3\u4e2d\u65e2\u60ca\u53c8\u5947\uff0c\u4e0d\u77e5\u9053\u8fd9\u4eba\u600e\u4f1a\u77e5\u9053\u4ed6\u7239\u7239\u7684\u540d\u5b57\uff0c\u66f4\u4e0d\u77e5\u9053\u8fd9\u4eba\u4e3a\u4ec0\u4e48 \u8981\u5982\u6b64\u5bf9\u5f85\u81ea\u5df2\uff0c\u62ac\u76ee\u671b\u53bb\uff0c\u53ea\u89c1\u7ad9\u5728\u5e8a\u524d\u6293\u7740\u81ea\u5df2\u624b\u81c2\u7684\u4eba\uff0c\u7adf\u7136\u5c31\u662f\u65b9\u624d\u90a3\u8eab\u8eaf\u987e\u957f\u3001 \u6f66\u5012\u8934\u891b\u7684\u602a\u4eba\u201c\u96f7\u5927\u53d4\u201d\u3002 \u7b2c\u4e03\u7ae0\u3000\u60c5\u5230\u6d53\u5904>> \u53e4\u9f99\u300a\u5251\u5ba2\u884c\u300b \u7b2c\u4e03\u7ae0\u3000\u60c5\u5230\u6d53\u5904 \u3000\u3000\u4f46\u5c55\u767d\u751f\u5c31\u50b2\u9aa8\uff0c\u522b\u4eba\u5bf9\u4ed6\u8d8a\u662f\u86ee\u6a2a\u65e0\u7406\uff0c\u8d8a\u662f\u80fd\u6fc0\u8d77\u4ed6\u7684\u50b2\u6027\u3002 \u3000\u3000\u5982\u679c\u6709\u4eba\u7528\u66b4\u529b\u5f3a\u8feb\u4ed6\uff0c\u5c31\u7b97\u5200\u65a7\u67b6\u5728\u9888\u4e0a\uff0c\u4ed6\u8fde\u773c\u7709\u90fd\u4e0d\u4f1a\u76b1\u4e00\u76b1\u3002 \u3000\u3000\u56e0\u6b64\uff0c\u8fd9\u7a81\u7136\u95f4\u53bb\u800c\u590d\u8fd4\u7684\u4e71\u53d1\u602a\u4eba\u2014\u2014\u96f7\u5927\u53d4\uff0c\u867d\u7136\u624b\u5982\u94a2\u7b8d\uff0c\u7d27\u63e1\u4f4f\u4ed6\u7684\u624b\u8155\uff0c\u4f7f \u4ed6\u7684\u624b\u81c2\u5267\u75db\u5982\u6298\uff0c\u4ed6\u4ecd\u7136\u662f\u4e0d\u7406\u776c\u3002 \u3000\u3000\u201c\u8bf4\uff01\u4f60\u662f\u8c01\uff1f\u201d\u96f7\u5927\u53d4\u602a\u76ee\u5706\u7741\uff0c\u5389\u5149\u5982\u7535\uff0c\u7d27\u76ef\u7740\u5c55\u767d\uff0c\u5389\u58f0\u53f1\u9053\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u5c55 \u4e91\u5929\u7684\u540e\u4eba\uff1f\u201d \u3000\u3000\u96f7\u5927\u53d4\u663e\u7136\u795e\u60c5\u751a\u4e3a\u6fc0\u52a8\uff0c\u95ee\u5c55\u767d\u8fd9\u8bdd\u65f6\uff0c\u53cc\u624b\u7ade\u5fae\u5fae\u53d1\u6296\uff0c\u4f46\u63e1\u4f4f\u5c55\u767d\u7684\u624b\uff0c\u53ef\u5c31\u65e0 \u5f62\u4e2d\u53c8\u52a0\u91cd\u4e86\u51e0\u5206\u529b\u9053\uff01 \u3000\u3000\u5c55\u767d\u611f\u5230\u88ab\u4e71\u53d1\u602a\u4eba\u7d27\u63e1\u4e4b\u5904\uff0c\u5947\u75db\u5165\u9aa8\uff0c\u53c8\u52a0\u4e0a\u4ed6\u8eab\u6709\u91cd\u75c5\uff0c\u517c\u8d1f\u5200\u4f24\uff0c\u65e0\u6cd5\u8fd0\u529f\u548c \u4e71\u53d1\u602a\u4eba\u7684\u624b\u52b2\u76f8\u62b5\uff0c\u53ea\u75db\u5f97\u4ed6\u9762\u767d\u6c14\u4fc3\uff0c\u51e0\u4e4e\u660f\u6b7b\u8fc7\u53bb\u3002 \u3000\u3000\u4f46\u5c31\u5728\u8fd9\u79cd\u96be\u8a00\u7684\u5de8\u75db\u4e4b\u4e0b\uff0c\u5c55\u767d\u4f9d\u65e7\u54ac\u7259\u82e6\u6491\u7740\uff0c\u4e0d\u7ba1\u90a3\u4e71\u53d1\u602a\u4eba\uff0c\u662f\u5982\u4f55\u5730\u7a77\u51f6\u6076 \u6781\uff0c\u4ecd\u7136\u662f\u95ed\u7d27\u5634\u5507\uff0c\u7ed8\u4ed6\u6765\u4e86\u4e2a\u76f8\u5e94\u4e0d\u7406\u3002 \u3000\u3000\u5728\u5c55\u81ea\u8eab\u65c1\u5750\u7740\u7684\u5982\u82b1\u5c11\u5973\uff0c\u89c1\u4ed6\u75db\u5f97\u8138\u8272\u60e8\u767d\uff0c\u989d\u4e0a\u8c46\u5927\u7684\u6c57\u6b8a\u6eda\u6eda\u800c\u4e0b\uff0c\u82b3\u5fc3\u4e2d\u8001 \u5927\u4e0d\u5fcd\u3002\u53c8\u89c1\u5c55\u767d\u867d\u5728\u5267\u75db\u4e4b\u4e0b\uff0c\u4ecd\u7136\u6beb\u65e0\u4e5e\u9976\u6c42\u6055\u7684\u795e\u60c5\uff0c\u66f4\u4e3a\u4ed6\u7684\u786c\u9aa8\u6c14\u800c\u6697\u6697\u5fc3\u6298\u3002 \u76f8\u53cd\u7684\uff0c\u5979\u5bf9\u96f7\u5927\u53d9\u8fd9\u79cd\u7c97\u66b4\u4e3e\u52a8\uff0c\u5374\u6709\u4e86\u8001\u5927\u7684\u4e0d\u9ad8\u5174\uff0c\u53ea\u89c1\u5979\u5c0f\u5634\u4e00\u561f\u8bf4\u9053\uff1a\u201c\u5927\u53d4\uff01 \u4f60\u653e\u624b\u5440\uff01\u770b\uff0c\u5feb\u628a\u4eba\u5bb6\u7684\u624b\u90fd\u8981\u6298\u65ad\u4e86\uff0c\u53eb\u4eba\u5bb6\u600e\u4e48\u56de\u7b54\u4f60\u7684\u8bdd\uff1f\u2026\u2026\u201d \u3000\u3000\u8fd9\u96f7\u5927\u53d4\u672c\u6765\u6700\u75db\u5a49\u513f\uff0c\u7d20\u5e38\u5bf9\u5a49\u513f\u7684\u8981\u6c42\uff0c\u767e\u4f9d\u767e\u987a\uff0c\u65e0\u6240\u4e0d\u4ece\u3002\u4f46\u5728\u76ee\u524d\uff0c\u8fd9\u96f7\u5927 \u53d4\u5374\u4f3c\u5931\u53bb\u4e86\u5f80\u65e5\u7684\u9547\u9759\u3002 \u3000\u3000\u5c55\u5a49\u513f\u4f7f\u55d4\u64a4\u5a07\uff0c\u53eb\u4ed6\u653e\u5f00\u63e1\u4f4f\u5c55\u767d\u7684\u624b\uff0c\u4ed6\u7adf\u604d\u5982\u672a\u95fb\uff0c\u4ecd\u7136\u53cc\u624b\u7d27\u63e1\u7740\u5c55\u767d\u7684\u8155\u90e8 \u5173\u8282\uff0c\u4e71\u53d1\u84ec\u4e71\u7684\u8111\u4e0a\u95ea\u8fc7\u65e0\u9650\u7684\u60b2\u6124\u6005\u60af\u4e4b\u60c5\uff0c\u53cc\u773c\u6b7b\u6b7b\u5730\u76ef\u5728\u5c55\u767d\u7684\u8138\u4e0a\u2026\u2026 \u3000\u3000\u201c\u4e91\u5929\u5440\uff01\u4e91\u5929\uff01\u83ab\u975e\u771f\u662f\u82cd\u5929\u6709\u773c\uff0c\u7ed9\u4f60\u7559\u4e0b\u4e86\u540e\u4ee3\u5417\uff1f\u2026\u2026\u554a\uff01\u8fd9\u4e00\u5b9a\u662f\u4e86\u2026\u2026\u4e00 \u5b9a\u662f\u4e86\uff01\u4e00\u5b9a\u662f\u4e86\uff01\u6211\u96f7\u2026\u2026\u201d \u3000\u3000\u96f7\u5927\u53d4\u72e0\u72e0\u5730\u671b\u4e86\u5c55\u767d\u4e00\u4f1a\u513f\uff0c\u5ffd\u7136\u4ef0\u8d77\u8138\u6765\uff0c\u4e00\u8138\u7684\u8083\u7a46\u4e4b\u60c5\uff0c\u53e3\u4e2d\u4eff\u4f5b\u7948\u7977\u822c\u5730\u5583 \u5583\u81ea\u8bed\u3002 \u3000\u3000\u4f46\u4ed6\u521a\u521a\u8bf4\u5230\u6b64\u5904\uff0c\u5ffd\u542c\u5a49\u513f\u4e00\u58f0\u60ca\u53eb\uff1a\u201c\u54ce\u54df\uff01\u4ed6\u6b7b\u4e86\u3002\u96f7\u5927\u53d4\uff01\u96f7\u5927\u53d4\uff01\u4ed6\u6b7b\u4e86 \u5440\u2026\u2026\u201d \u3000\u3000\u96f7\u5927\u53d4\u5982\u5927\u68a6\u521d\u89c9\uff0c\u731b\u7136\u4f4e\u4e0b\u5934\u6765\uff0c\u53ea\u89c1\u5c55\u767d\u9762\u767d\u5982\u7eb8\uff0c\u53cc\u76ee\u7d27\u95ed\uff0c\u53e3\u9f3b\u4e4b\u95f4\u4f3c\u662f\u5df2\u6ca1 \u6709\u4e86\u6c14\u606f\u3002 \u3000\u3000\u96f7\u5927\u53d4\u2014\u2014\u8fd9\u6b66\u6797\u5947\u4eba\uff0c\u60f3\u5f53\u5e74\u4e0e\u201c\u9739\u96f7\u5251\u201d\u5c55\u4e91\u5929\uff0c\u4e49\u7ed3\u91d1\u5170\uff0c\u60c5\u540c\u751f\u6b7b\uff0c\u4e8c\u4eba\u5e76\u9053 \u6c5f\u6e56\uff0c\u4e0d\u77e5\u505a\u4e86\u591a\u5c11\u60ca\u5929\u52a8\u5730\u3001\u8f70\u8f70\u70c8\u70c8\u7684\u4ed7\u4e49\u4fa0\u884c\u3002 \u3000\u3000\u4f46\u5728\u4e8c\u4eba\u4e00\u6b21\u5c0f\u522b\u671f\u95f4\uff0c\u5ffd\u7136\u5669\u8017\u4f20\u6765\uff0c\u6b66\u529f\u4fa0\u884c\u9707\u60ca\u5929\u4e0b\u7684\u201c\u9739\u96f3\u5251\u201d\u5c55\u4e91\u5929\uff0c\u7adf\u7136 \u88ab\u6697\u7b97\u60e8\u6b7b\uff01 \u3000\u3000\u5f53\u65f6\u7684\u96f7\u5927\u53d4\uff0c\u542c\u5230\u8fd9\u4e2a\u5669\u8017\uff0c\u51e0\u4e4e\u75db\u4e0d\u6b32\u751f\u2606\u7acb\u5373\u8d76\u5230\u51fa\u4e8b\u5730\u70b9\uff0c\u6d1e\u5ead\u541b\u5c71\u7edd\u9876\u3002 \u4f46\uff0c\u4ed6\u4e0d\u4ec5\u672a\u80fd\u67e5\u5230\u6740\u6b7b\u5c55\u4e91\u5929\u7684\u51f6\u624b\uff0c\u751a\u81f3\u8fde\u5c55\u4e91\u5929\u7684\u5c38\u9996\u90fd\u672a\u627e\u5230\uff01 \u3000\u3000\u53ef\u662f\uff0c\u541b\u5c71\u7edd\u9876\u7684\u73b0\u573a\uff0c\u5374\u662f\u4e00\u7247\u51cc\u4e71\uff0c\u6811\u6298\u8349\u98de\uff0c\u65ad\u5251\u6b8b\u6208\uff0c\u5230\u5904\u7686\u662f\u6563\u843d\u7684\u6697\u5668\uff0c \u5c24\u5176\u9057\u7559\u5728\u5730\u4e0a\u4e00\u6ee9\u6ee9\u6bb7\u7ea2\u7684\u8840\u8ff9\uff0c\u4e1c\u4e00\u7247\u897f\u4e00\u7247\uff0c\u67d3\u7ea2\u4e86\u9ec4\u6c99\u67af\u8349\u3002\u4e00\u5207\u666f\u51b5\uff0c\u5747\u663e\u793a\u51fa \u662f\u7ecf\u8fc7\u4e00\u573a\u60e8\u70c8\u7684\u51f6\u6740\uff0c\u6240\u7559\u4e0b\u7684\u75d5\u8ff9\uff01 \u3000\u3000\u96f7\u5927\u53d4\u89c1\u6b64\u5149\u666f\uff0c\u77e5\u9053\u6c5f\u6e56\u4e0a\u4f20\u8a00\u4e0d\u5047\u3002\u5f53\u65f6\uff0c\u4ed6\u66fe\u60b2\u6124\u5f97\u51e0\u4e4e\u53d1\u75af\uff0c\u4e5f\u66fe\u60f3\u5230\u6a2a\u5251\u81ea \u5228\u5728\u541b\u5c71\u7edd\u9876\uff0c\u4ee5\u916c\u62a5\u77e5\u4ea4\u597d\u53cb\u3002 \u3000\u3000\u4f46\u662f\uff0c\u4e00\u4e2a\u6bd4\u6b7b\u4ea1\u66f4\u5927\u7684\u6b32\u671b\uff0c\u4f7f\u4ed6\u6d3b\u4e86\u4e0b\u6765\uff0c\u90a3\u5c31\u662f\u590d\u4ec7\uff01 \u3000\u3000\u4ed6\u8981\u5bfb\u8bbf\u5230\u6697\u7b97\u6740\u6b7b\u5c55\u4e91\u5929\u7684\u4ec7\u4eba\uff0c\u4e3a\u4ed6\u7ed3\u4e49\u76df\u5144\u590d\u4ec7\u3002 \u3000\u3000\u53ef\u662f\uff0c\u4ed6\u8d70\u904d\u5929\u6daf\uff0c\u8e0f\u7834\u94c1\u978b\uff0c\u8fde\u6740\u6b7b\u5c55\u4e91\u5929\u7684\u4ec7\u4eba\u662f\u8c01\uff0c\u4ed6\u90fd\u672a\u5bfb\u8bbf\u51fa\u6765\uff0c\u590d\u4ec7\u5c31\u66f4 \u65e0\u671b\u4e86\u3002 \u3000\u3000\u4e8b\u9694\u4e09\u5341\u4f59\u5e74\uff0c\u4ed6\u5df2\u7ecf\u5bf9\u4e07\u4e8b\u90fd\u611f\u5230\u5fc3\u7070\u610f\u51b7\uff0c\u6b64\u65f6\u5374\u6709\u51cc\u98ce\u516c\u5b50\u7684\u7236\u4eb2\uff0c\u6155\u5bb9\u5e84\u4e3b\uff0c \u4ef0\u6155\u4ed6\u7684\u4fa0\u540d\uff0c\u91cd\u91d1\u793c\u8058\uff0c\u8bf7\u4ed6\u5230\u5e84\u4e0a\u5145\u5f53\u4e00\u4f4d\u95e8\u5ba2\u3002 \u3000\u3000\u96f7\u5927\u53d4\u672c\u65e0\u610f\u5bc4\u4eba\u7bf1\u4e0b\uff0c\u4f46\u4ed6\u53c8\u60f3\u5230\u4e45\u8bbf\u6740\u6b7b\u4e49\u5144\u7684\u4ec7\u4eba\uff0c\u6773\u65e0\u7aef\u7eea\uff0c\u81ea\u5df1\u4e07\u5ff5\u60e7\u7070\uff0c \u843d\u62d3\u6c5f\u6e56\uff0c\u4e5f\u4e0d\u662f\u4e2a\u529e\u6cd5\uff0c\u6b66\u6797\u56db\u516c\u5b50\uff0c\u65b0\u8fd1\u5d1b\u8d77\u6c5f\u6e56\uff0c\u5404\u81ea\u6536\u7f57\u62c9\u62e2\u6b66\u6797\u9ad8\u624b\uff0c\u5f52\u5176\u95e8 \u4e0b\uff0c\u51e0\u5e74\u7684\u65f6\u95f4\uff0c\u6b66\u6797\u56db\u516c\u5b50\u7684\u95e8\u4e0b\uff0c\u6b66\u6797\u9ad8\u624b\u5df2\u7ecf\u662f\u6210\u5343\u8bba\u767e\uff0c\u58f0\u52bf\u4e4b\u9686\uff0c\u76f4\u53ef\u5ab2\u7f8e\u6625\u79cb \u6218\u56fd\u65f6\u4ee3\u7684\u56db\u5927\u516c\u5b50\u4e86\u3002 \u3000\u3000\u81ea\u5df1\u6682\u65f6\u5728\u6155\u5e84\u4e3b\u7684\u5e84\u4e0a\u6b47\u9a6c\uff0c\u6155\u5bb9\u5e84\u4e0a\u9c7c\u9f99\u6df7\u6742\uff0c\u8bf4\u4e0d\u5b9a\u4e5f\u8bb8\u4f1a\u628a\u6740\u6b7b\u4e49\u5144\u7684\u4ec7\u4eba\uff0c \u67e5\u51fa\u4e2a\u7aef\u502a\u6765\uff01 \u3000\u3000\u56e0\u4e4b\uff0c\u96f7\u5927\u53d4\u843d\u8db3\u5728\u6155\u5bb9\u5e84\u4e0a\u3002 \u3000\u3000\u6155\u5bb9\u5e84\u4e3b\uff0c\u5bcc\u53ef\u654c\u56fd\uff0c\u6700\u8bb2\u7a76\u6392\u573a\uff0c\u4e0d\u4ec5\u672c\u5bb6\u4eba\u8c6a\u534e\u65e0\u6bd4\uff0c\u5c31\u662f\u5bf9\u95e8\u4e0b\u98df\u5ba2\uff0c\u4e5f\u5747\u662f\u793c \u9047\u6709\u52a0\uff0c\u4e00\u4e2a\u4e2a\u8863\u9526\u534e\u88ad\u3002 \u3000\u3000\u552f\u72ec\u96f7\u5927\u53d4\uff0c\u7b28\u8def\u69db\u697c\uff0c\u4e0d\u4fee\u8fb9\u5e45\u3002 \u3000\u3000\u4f46\u6155\u5bb9\u5e84\u4e3b\u6df1\u77e5\u96f7\u5927\u4e49\u6c14\u5e72\u4e91\uff0c\u6240\u4ee5\u5bf9\u96f7\u5927\u53d4\u7684\u884c\u6b62\uff0c\u4e1d\u6beb\u4e0d\u52a0\u4e8e\u6d89\uff0c\u5e76\u59d4\u4ee5\u4fdd\u62a4\u5185\u5b85 \u7684\u91cd\u4efb\u3002 \u3000\u3000\u6155\u5bb9\u5e84\u4e3b\u7684\u5185\u5b85\uff0c\u95e8\u7981\u68ee\u4e25\uff0c\u5373\u4e09\u5c3a\u5b69\u7ae5\uff0c\u65e0\u4eba\u547c\u5524\u4e5f\u4e0d\u5f97\u5165\u5185\u3002 \u3000\u3000\u8fd9\u96f7\u5927\u53d4\u4e00\u4e2a\u8349\u83bd\u8c6a\u5ba2\uff0c\u80fd\u591f\u767b\u5802\u5165\u5ba4\uff0c\u4e14\u5c45\u4f4f\u4e8e\u5185\u5b85\u4e4b\u4e2d\uff0c\u53ef\u4ee5\u8bf4\u662f\u6df1\u8499\u6155\u5bb9\u5e84\u4e3b\u53e6 \u8ddf\u76f8\u770b\u4e86\u3002 \u3000\u3000\u81f3\u4e8e\u96f7\u5927\u53d4\u80fd\u5728\u6155\u5bb9\u5e84\u4e3b\u7684\u95e8\u4e0b\uff0c\u5b89\u5fc3\u4f4f\u4e0b\u6765\uff0c\u8fd8\u4e0d\u4ec5\u662f\u4e3a\u4e86\u916c\u7b54\u6155\u5bb9\u5e84\u4e3b\u7684\u8d4f\u8bc6\uff0c\u800c \u662f\u96f7\u5927\u53d4\u7279\u522b\u559c\u6b22\u5a49\u513f\uff0c\u771f\u6bd4\u5a49\u513f\u7684\u4eb2\u751f\u7236\u6bcd\u2014\u2014\u6155\u5bb9\u5e84\u4e3b\u592b\u59bb\uff0c\u8fd8\u8981\u6df1\u4e00\u5c42\u3002\u56e0\u6b64\uff0c\u96f7\u5927 \u53d4\u7adf\u5728\u6155\u5bb9\u5e84\u4e0a\u4e45\u4e45\u4f4f\u4e0b\u6765\u3002 \u3000\u3000\u53ef\u662f\uff0c\u96f7\u5927\u53d4\u5bf9\u67e5\u8bbf\u6740\u6b7b\u4e49\u5144\u7684\u4ec7\u4eba\uff0c\u5374\u59cb\u7ec8\u6ca1\u653e\u677e\u8fc7\u3002 \u3000\u3000\u6570\u5341\u5e74\u5982\u4e00\u65e5\uff0c\u96f7\u5927\u53d4\u65f6\u65f6\u8bb0\u5ff5\u7740\uff0c\u8981\u4e3a\u76df\u5144\u590d\u4ec7\u3002 \u3000\u3000\u5982\u4eca\uff0c\u7adf\u5927\u51fa\u610f\u5916\u5730\uff0c\u53eb\u4ed6\u89c1\u5230\u4e86\u4f3c\u4e4e\u662f\u76df\u5144\u5c55\u4e91\u5929\u7684\u540e\u4eba\uff01\u53c8\u53eb\u4ed6\u5982\u4f55\u4e0d\u5fc3\u60c5\u6fc0\u52a8\uff1f \u5982\u4f55\u4e0d\u5931\u5e38\u5462\uff1f \u3000\u3000\u56e0\u4e3a\u4ed6\u4ece\u672a\u542c\u76df\u5144\u8bf4\u9053\u6709\u59bb\u5ba4\u513f\u5973\u3002 \u3000\u3000\u4f46\uff0c\u4ed6\u4eca\u5929\u89c1\u5230\u5c55\u767d\uff0c\u8fd9\u5c11\u5e74\u4eba\u7709\u68a2\u670d\u89d2\u95f4\u7684\u82f1\u4fca\u6c14\u6982\uff0c\u6781\u50cf\u76df\u5144\u5f53\u5e74\u7684\u6837\u5b50\u3002 \u3000\u3000\u4ed6\u53c8\u5728\u7a97\u5916\uff0c\u542c\u5c11\u4e2d\u5bf9\u5a49\u513f\u8bf4\uff1a\u4ed6\u4e5f\u59d3\u5c55\uff0c\u7236\u4eb2\u63ba\u6b7b\uff0c\u81f3\u4eca\u8fde\u6740\u7236\u4ec7\u4eba\u90fd\u4e0d\u77e5\u662f\u8c01\uff1f \u3000\u3000\u56e0\u6b64\uff0c\u4ed6\u4ef0\u9996\u5411\u5929\uff0c\u5583\u5583\u81ea\u8bed\uff0c\u5bf9\u5c55\u767d\u5fcd\u75db\u4e0d\u4f4f\u3001\u660f\u6b7b\u8fc7\u53bb\u7684\u60c5\u5f62\uff0c\u7adf\u6beb\u65e0\u6240\u77e5\u3002 \u3000\u3000\u7ed9\u5c55\u5a49\u513f\u60ca\u58f0\u4e00\u547c\uff0c\u96f7\u5927\u613f\u624d\u5982\u68a6\u521d\u9192\uff0c\u4f4e\u5934\u4e00\u770b\u5c55\u767d\u75db\u6b7b\u8fc7\u53bb\uff0c\u5413\u5f97\u5fd9\u628a\u624b\u677e\u5f00\uff0c\u7d27 \u8ddf\u7740\u4f38\u51fa\u53cc\u624b\u4e3a\u5c55\u767d\u63a8\u5bab\u6d3b\u7a74\uff01 \u3000\u3000\u770b\u5230\u5c55\u767d\u660f\u6b7b\u7684\u60c5\u72b6\uff0c\u5c55\u5a49\u513f\u7adf\u8be5\u7136\u6b32\u6ce3\uff01 \u3000\u3000\u8fd9\u8c8c\u6bd4\u5929\u4ed9\u3001\u81ea\u5e7c\u5a07\u7eb5\u6210\u6027\u7684\u59d1\u5a18\uff0c\u5305\u56f4\u8ffd\u6c42\u5979\u7684\u6b66\u6797\u5b50\u5f1f\u6210\u5343\u903e\u767e\uff0c\u5bcc\u82e5\u738b\u4faf\u7684\uff0c\u6b66 \u529f\u9ad8\u5f3a\u7684\uff0c\u8c8c\u6bd4\u6f58\u5b899b\u2026\u3002\uff0c\xb7\u5404\u5f0f\u5404\u6837\u7684\u4eba\u7269\uff0c\u4e0d\u8ba1\u5176\u6570\uff0c\u4f46\u5979\u4ece\u672a\u628a\u4e00\u4e2a\u653e\u8fdb\u773c\u5185\u3002 \u3000\u3000\u5982\u4eca\uff0c\u5374\u8877\u5fc3\u7231\u4e0a\u8fd9\u7a77\u6101\u6f66\u5012\u3001\u53c8\u6709\u4f24\u75c5\u5728\u8eab\u7684\u843d\u65bd\u5c11\u5e74\uff01 \u3000\u3000\u60c5\u8fd9\u4e00\u4e2a\u5b87\uff0c\u771f\u662f\u4ee4\u4eba\u4e0d\u53ef\u7406\u89e3\u7684\u4e86\u3002 \u3000\u3000\u201c\u59d1\u5a18\uff01\u201d\u96f7\u5927\u53d9\u89c1\u5a49\u513f\u5b9a\u4f24\u7684\u795e\u60c5\uff0c\u6df1\u6094\u81ea\u5df2\u7684\u5b5f\u6d6a\uff0c\u4e0d\u8be5\u51fa\u624b\u592a\u91cd\u4f24\u4e86\u8fd9\u5c11\u5e74\uff0c\u5fc3 \u4e2d\u8001\u5927\u4e0d\u5fcd\u3002\u4e8e\u662f\u6e29\u548c\u5730\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u7528\u62c5\u5fc3\uff0c\u4ed6\u4e0d\u4f1a\u6b7b\u7684\uff01\u201d \u3000\u3000\u201c\u6211\uff0c\u6211\u6068\u6b7b\u4f60\u4e86\uff01\u2026\u2026\u201d\u5a49\u513f\u542c\u96f7\u5927\u53d4\u4e00\u5b89\u6170\u5979\uff0c\u53cd\u800c\u5fcd\u4e0d\u4f4f\u5b58\u4e8e\u773c\u7736\u5185\u7684\u6cea\u6c34\uff0c\u50cf \u65ad\u7ebf\u73cd\u73e0\u822c\uff0c\u6ef4\u843d\u5728\u5979\u9526\u7ee3\u7684\u8863\u895f\u4e0a\u3002 \u3000\u3000\u5979\u5fc3\u75db\u5c55\u81ea\u88ab\u96f7\u5927\u53d4\u6293\u5f97\u75db\u660f\u8fc7\u53bb\uff0c\u7adf\u53e3\u4e0d\u62e9\u8a00\u5730\u8bf4\u51fa\u4e86\u8fd9\u6837\u7684\u8bdd\u6765\u3002 \u3000\u3000\u53ef\u662f\uff0c\u5979\u8bdd\u4e00\u51fa\u53e3\uff0c\u53c8\u89c9\u5f97\u5bf9\u4e00\u4e2a\u975e\u5e38\u75bc\u7231\u81ea\u5df2\u7684\u957f\u8f88\uff0c\u7ade\u8bf4\u51fa\u8fd9\u6837\u7684\u8bdd\uff0c\u6709\u70b9\u4e0d\u59a5\u3002 \u505c\u987f\u4e86\u4e00\u4e0b\uff0c\u7acb\u5373\u6539\u53d8\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u4ed6\uff0c\u4ed6\u82e5\u662f\u6b7b\u4e86\u2026\u2026\u6211\u6c38\u8fdc\u4e0d\u4f1a\u539f\u8c05\u2026\u2026\u4f60\u2026\u2026\u201d \u3000\u3000\u867d\u7136\u5979\u6781\u529b\u60f3\u6539\u53d8\u53e3\u6c14\uff0c\u4e0d\u613f\u8bf4\u51fa\u5bf9\u4e0d\u8d77\u96f7\u5927\u53d4\u7684\u8bdd\u6765\uff0c\u4f46\u56e0\u4e3a\u5979\u592a\u5173\u5fc3\u5c55\u767d\u7684\u5b89\u5371\uff0c \u6240\u4ee5\uff0c\u8bf4\u51fa\u53e3\u6765\u7684\u8bdd\uff0c\u4f9d\u7136\u663e\u5f97\u4e0d\u592a\u5ba2\u6c14\u3002 \u3000\u3000\u96f7\u5927\u53d4\u542c\u4e86\u5fae\u5fae\u4e00\u695e\uff0c\u4ed6\u81ea\u4ece\u5230\u6155\u5bb9\u5e84\u4e0a\u4ee5\u6765\uff0c\u7231\u62a4\u5a49\u513f\uff0c\u751a\u4e8e\u7231\u62a4\u81ea\u5df1\u7684\u4eb2\u751f\u5973\u513f\u3002 \u867d\u7136\uff0c\u4ed6\u8fde\u5a5a\u90fd\u6ca1\u6709\u7ed3\u8fc7\uff0c\u66f4\u4e0d\u4f1a\u6709\u4eb2\u751f\u5973\u513f\uff0c\u4f46\u4ed6\u76f8\u4fe1\uff0c\u5c31\u7b97\u81ea\u5df1\u6709\u4e86\u4eb2\u751f\u5973\u513f\uff0c\u4f46\u7231\u5973 \u513f\u7684\u5fc3\u4e5f\u4e0d\u4f1a\u8d85\u8fc7\u7231\u5a49\u513f\u7684\u5fc3\u3002\u60f3\u4e0d\u5230\u5a49\u513f\u7ade\u5bf9\u81ea\u5df2\u8bf4\u51fa\u8fd9\u6837\u7684\u8bdd\u6765\uff0c\u96f7\u5927\u53d4\u4e0d\u7981\u5fae\u5fae\u4e00\u695e \u2606\u2026\uff0c\xb7\u4e0d\u8fc7\uff0c\u8fd9\u4e5f\u662f\u4e00\u77ac\u95f4\u7684\u4e8b\uff0c\u96f7\u5927\u53d4\u4ec5\u5fae\u5fae\u4e00\u695e\uff0c\u4e00\u8fb9\u7528\u53cc\u624b\u4e3a\u5c55\u767d\u63a8\u5bab\u6d3b\u7a74\uff0c\u4e00\u8fb9 \u8f6c\u5934\u671b\u4e86\u5a49\u513f\u4e00\u773c\u3002 \u3000\u3000\u89c1\u5a49\u513f\u75f4\u671b\u7740\u5c55\u767d\uff0c\u6ee1\u8138\u5173\u6000\u4e4b\u60c5\uff0c\u773c\u6cea\u7edf\u7edf\u5730\u843d\u4e0b\uff0c\u5fc3\u4e2d\u7acb\u523b\u660e\u767d\u4e86\u4e00\u5927\u534a\u3002\u5fc3\u4e2d\u5fd6 \u9053\uff1a\u201c\u770b\u6765\u6211\u8fd9\u5201\u94bb\u7684\u5973\u5a03\u513f\uff0c\u516b\u6210\u5df2\u7ecf\u7231\u4e0a\u8fd9\u5c11\u5e74\u3002\u554a\uff01\u2026\u2026\u6211\u624d\u662f\u8001\u7cca\u6d82\uff0c\u5bf9\u4e00\u4e2a\u8c46\u85d0 \u5e74\u534e\u7684\u9752\u6625\u5c11\u5973\u6765\u8bf4\uff0c\u8fd8\u6709\u4ec0\u4e48\u6bd4\u5979\u7684\u610f\u4e2d\u4eba\uff0c\u66f4\u80fd\u4f7f\u5979\u5f00\u5fc3\u7684\u5462\uff1f\u2026\u2026\u201d \u3000\u3000\u96f7\u5927\u53d4\u60f3\u5230\u8fd9\u91cc\uff0c\u5bf9\u5a49\u513f\u65e0\u793c\u7684\u8bdd\uff0c\u4e0d\u4f46\u4e0d\u751f\u6c14\uff0c\u53cd\u800c\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u5a49\u513f\uff0c\u4f60\u4e0d \u7528\u5fc3\u6025\uff1a\u5927\u53d4\u8d1f\u8d23\u8fd8\u7ed9\u4f60\u4e00\u4e2a\u6d3b\u2026\u2026\u201d \u3000\u3000\u96f7\u5927\u53d4\u8bf4\u81f3\u6b64\u5904\uff0c\u5374\u518d\u4e5f\u8bf4\u4e0d\u4e0b\u53bb\u4e86\u3002\u6d3b\u4ec0\u4e48\u5462\uff1f\u6d3b\u60c5\u90ce\uff0c\u6d3b\u672a\u5a5a\u592b\uff0c\u8fd8\u662f\u6d3b\u7231\u4eba\u2026\u2026 \u603b\u89c9\u5f97\u600e\u4e48\u8bf4\u4e5f\u662f\u4e0d\u59a5\uff0c\u4e0d\u7531\u5c34\u5c2c\u5730\u76f4\u7528\u624b\u6293\u80e1\u5b50\uff0c\u5e72\u77aa\u773c\u2026\u2026\uff3c\u504f\u504f\u5c55\u5a49\u513f\uff0c\u53c8\u662f\u4eca\u5929\u771f \u672a\u51ff\u3001\u5a07\u61a8\u65e0\u6bd4\u7684\u5c11\u5973\u3002\u5979\u89c1\u96f7\u5927\u53d4\u7684\u602a\u6837\u5b50\uff0c\u4e0d\u7531\u7834\u6d95\u4e3a\u7b11\uff0c\u5982\u96e8\u540e\u6625\u82b1\u822c\u3002\u8bf4\u9053\uff1a\u201c\u6d3b \u4ec0\u4e48\u5440\uff1f\u5927\u53d4\uff0c\u4f60\u600e\u4e48\u4e0d\u8bf4\u4e86\uff1f\u201d \u3000\u3000\u201c\u6d3b\u2026\u2026\u6d3b\u4eba\uff01\u201d\u96f7\u5927\u53d4\u55eb\u5685\u4e86\u534a\u5929\uff0c\u7a81\u7136\u7528\u624b\u4e00\u62cd\u81ea\u5df1\u7684\u8111\u888b\uff0c\u5230\u5e95\u8ba9\u4ed6\u60f3\u51fa\u6765\u4e86\uff0c \u8fd9\u4e48\u4e00\u53e5\u6070\u5f53\u7684\u8bdd\uff0c\u8131\u53e3\u8bf4\u51fa\uff0c\u663e\u5f97\u5f88\u9ad8\u5174\u7684\u6837\u5b50\u3002 \u3000\u3000\u201c\u5657\u54e7\uff01\u201d\u5a49\u513f\u518d\u4e5f\u5fcd\u4e0d\u4f4f\uff0c\u4e0d\u7531\u7b11\u51fa\u58f0\u6765\u3002\u5a07\u7b11\u60c5\u4e2d\u5730\u8bf4\u9053\uff1a\u201c\u5f53\u7136\u662f\u6d3b\u4eba\u4e86\uff0c\u96be\u9053 \u6211\u8fd8\u8981\u4e2a\u2018\u6b7b\u4eba\u2019\u4e0d\u6210\u5417\uff1f\u2026\u2026\u201d \u3000\u3000\u5a49\u513f\u7b11\u7740\u8bf4\u81f3\u6b64\u5904\uff0c\u7a81\u7136\u8138\u5b54\u4e00\u7ea2\uff0c\u8109\u8109\u5730\u4f4e\u4e0b\u5934\u53bb\uff0c\u7528\u624b\u62c9\u626f\u4e86\u4e00\u4e0b\u81ea\u5df2\u7684\u8863\u895f\u3002 \u3000\u3000\u7136\u540e\u53c8\u779f\u4e86\u96f7\u5927\u53d4\u4e00\u773c\uff0c\u89c1\u96f7\u5927\u53d4\u6b63\u7528\u4e00\u79cd\u4f3c\u4e4e\u542b\u6709\u6df1\u610f\u7684\u773c\u5149\u671b\u7740\u5979\uff0c\u7acb\u523b\u53c8\u5f88\u5feb\u5730 \u6536\u56de\u76ee\u5149\uff0c\u5979\uff0c\u5934\u5782\u5f97\u66f4\u4f4e\u4e86\uff0c\u8138\u5b54\u6da8\u5f97\u66f4\u7ea2\u4e86\u3002 \u3000\u3000\u6709\u4eba\u8bf4\uff1a\u201c\u5c11\u5973\u5bb3\u7f9e\u7684\u795e\u60c5\u6700\u7f8e\uff01\u201d\u6b64\u8bdd\u4e00\u70b9\u513f\u4e5f\u4e0d\u9519\u3002\u53ea\u89c1\u5c55\u5a49\u513f\u8d5b\u96ea\u6b3a\u971c\u7684\u7c89\u767d\u5c0f \u8138\u4e0a\uff0c\u70d8\u67d3\u4e0a\u4e00\u5c42\u671d\u971e\u822c\u9c9c\u8273\u7684\u7ea2\u6655\uff0c\u660e\u5982\u6625\u6c34\u4f3c\u7684\u773c\u6ce2\uff0c\u653e\u51fa\u4e00\u79cd\u707f\u70c2\u7684\u5149\u5f69\uff0c\u4e3d\uff0c\u7eaf\u7f8e \u65e0\u6bd4\uff0c\u4e0d\u4e9a\u4e8e\u4e00\u6735\u7ea2\u7761\u83b2\uff0c\u5728\u6668\u9732\u4e2d\u8fce\u7740\u671d\u9633\u76db\u5f00\uff0c\u771f\u662f\u7f8e\u4e3d\u6781\u4e86\uff01 \u3000\u3000\u5176\u5b9e\uff0c\u96f7\u5927\u53d4\u5e76\u4e0d\u77e5\u9053\uff0c\u5c55\u5a49\u513f\u662f\u4e3a\u4e86\u4ec0\u4e48\uff0c\u7ade\u65e0\u7aef\u4e0d\u80dc\u5a07\u7f9e\uff1f \u3000\u3000\u4f46\uff0c\u4e16\u4e0a\u53c8\u6709\u51e0\u4eba\u80fd\u591f\u731c\u6d4b\u51fa\uff0c\u9752\u6625\u5c11\u5973\u7684\u5fc3\u5462\uff1f \u3000\u3000\u539f\u6765\uff0c\u5c55\u5a49\u513f\u5728\u80cc\u5730\u91cc\u5e38\u542c\u5230\u6bcd\u4eb2\u7ba1\u7236\u4eb2\u53eb\u201c\u6b7b\u4eba\u201d\uff0c\u5979\u5929\u771f\u5c11\u5973\u7684\u5fc3\u4e0a\uff0c\u4fbf\u4ee5\u4e3a\u201c\u6b7b \u4eba\u201d\u662f\u201c\u4e08\u592b\u201d\u7684\u4ee3\u540d\u8bcd\u3002 \u3000\u3000\u5982\u4eca\uff0c\u5979\u65e0\u610f\u4e2d\u628a\u5c55\u767d\u6bd4\u505a\u4e86\u201c\u6b7b\u4eba\u201d\uff0c\u96be\u602a\u5979\u8981\u8138\u7ea2\u4e86\u2026\u2026 \u3000\u3000\u5c31\u5728\u6b64\u65f6\uff0c\u5c55\u767d\u5728\u96f7\u5927\u53d4\u4e00\u9635\u63a8\u62ff\u4e4b\u540e\uff0c\u5df2\u7136\u60a0\u60a0\u9192\u8f6c\uff0c\u4ed6\u7f13\u7f13\u5730\u7741\u5f00\u773c\u6765\uff0c\u9996\u5148\u6620\u5165 \u4ed6\u773c\u5e18\u7684\uff0c\u662f\u5a49\u513f\u8c8c\u7f8e\u5982\u82b1\u7684\u5a07\u989c\uff0c\u4f46\u5374\u6726\u80e7\u4e0d\u6e05\uff0c\u6709\u5982\u96fe\u91cc\u770b\u82b1\u2026\u2026 \u3000\u3000\u201c\u6c34\xb7\u2026\u2026\u201d \u3000\u3000\u5a49\u513f\u89c1\u4ed6\u82cf\u9192\u8fc7\u6765\uff0c\u795e\u6001\u9ad8\u5174\u5df2\u6781\u3002\u542c\u5230\u4ed6\u8bf4\u8981\u6c34\uff0c\u7acb\u523b\u62ff\u8d77\u8336\u51e0\u4e0a\u7684\u78a7\u7389\u76d6\u7897\uff0c\u5148\u5728 \u6e29\u6c34\u91cc\u6d17\u8fc7\uff0c\u7136\u540e\u5012\u4e86\u4e00\u676f\u5f00\u6c34\u6765\uff0c\u5c31\u5728\u5e8a\u4e0a\u8f7b\u8f7b\u6276\u8d77\u5c55\u81ea\u7684\u5934\u6765\uff0c\u628a\u76d6\u7897\u91cc\u7684\u5f00\u6c34\u4e00\u53e3\u4e00 \u53e3\u5730\u5582\u7ed9\u5c55\u767d\u5403\u3002 \u3000\u3000\u201c\u5509\uff01\u201d\u96f7\u5927\u53d4\u8f7b\u80c3\u4e86\u4e00\u58f0\uff0c\u89c1\u8fd9\u5a07\u8d35\u65e0\u6bd4\u7684\u6155\u5bb9\u5e9c\u4e2d\u4e8c\u5343\u91d1\uff0c\u5bf9\u4e00\u4e2a\u843d\u9b44\u9752\u5e74\uff0c\u7adf\u662f \u5982\u6b64\u7684\u6e29\u67d4\u4f53\u8d34\u3002 \u3000\u3000\u4e0d\u7531\u6697\u53f9\u201c\u60c5\u201d\u5b57\u529b\u91cf\u4e4b\u4f1f\u5927\uff0c\u771f\u662f\u4e0d\u53ef\u601d\u8bae\u2026\u2026 \u3000\u3000\u201c\u8c22\u2026\u2026\u8c22\u2026\u2026\u201d\u5c55\u767d\u5c31\u7740\u5a49\u513f\u7684\u7d20\u624b\u4e2d\uff0c\u5438\u4e86\u51e0\u53e3\u6c34\u3002\u4eba\u5728\u795e\u667a\u5df2\u89c1\u6e05\u9192\u65f6\uff0c\u7b2c\u4e00\u4e2a \u662f\u55c5\u89c9\uff0c\u4ed6\u9f3b\u5b54\u4e2d\u55c5\u5230\u4e00\u822c\u5982\u5170\u4f3c\u9e9d\u7684\u5c11\u5973\u8eab\u4e0a\u7279\u6709\u7684\u5e7d\u9999\u3002 \u3000\u3000\u7b2c\u4e8c\u4e2a\u662f\u89c6\u89c9\uff0c\u4ed6\u770b\u5230\u4e00\u5f20\u7edd\u8272\u5c11\u5973\u7684\u5982\u82b1\u5a07\u56ed\uff0c\u7d27\u7d27\u8d34\u5728\u81ea\u5df1\u8138\u65c1\u3002 \u3000\u3000\u7b2c\u4e09\u4e2a\u662f\u89e6\u89c9\uff0c\u4ed6\u53ea\u89c9\u8f6f\u7389\u6e29\u9999\uff0c\u81ea\u5df1\u6b63\u5012\u5728\u4e00\u4e2a\u7eaf\u7f8e\u7684\u5c11\u5973\u6000\u4e2d\uff0c\u4e0d\u7531\u8138\u5b54\u53d1\u70e7\uff0c\u4e00 \u822c\u8bf4\u4e0d\u51fa\u7684\u7f20\u7ef5\u6ecb\u5473\uff0c\u7adf\u4f7f\u4ed6\u5fc3\u4e2d\u4e00\u8361\u2026\u2026 \u3000\u3000\u8fd9\u79cd\u6e29\u67d4\u6ecb\u5473\uff0c\u8fd9\u79cd\u65d6\u65ce\u98ce\u5149\uff0c\u662f\u4ed6\u4e00\u751f\u4e2d\u4ece\u672a\u9886\u7565\u8fc7\u7684\uff1b\u4ed6\u53c8\u89c1\u8fd9\u5982\u82b1\u5c11\u5973\uff0c\u6e29\u67d4\u5730 \u62e5\u62b1\u7740\u81ea\u5df1\uff0c\u767d\u7389\u4f3c\u7684\u7d20\u624b\uff0c\u7aef\u7740\u4e00\u676f\u6c34\uff0c\u4e00\u53e3\u4e00\u53e3\u5730\u5582\u81ea\u5df1\uff1b\u800c\u4e14\uff0c\u90a3\u5c11\u5973\u6bd4\u6625\u6c34\u66f4\u52a0\u660e \u5a9a\u7684\u53cc\u775b\uff0c\u542b\u7740\u65e0\u8fb9\u7684\u7231\uff0c\u4e07\u7f15\u67d4\u60c5\u5730\u671b\u7740\u81ea\u5df1\u3002 \u3000\u3000\u554a\uff01\u8fd9\u4e00\u5207\u7684\u4e00\u5207\uff0c\u4f3c\u68a6\u4f3c\u771f\uff0c\u7adf\u4f7f\u4ed6\u611f\u52a8\u5f97\u4e0d\u5f97\u4e86\u3002 \u3000\u3000\u53e3\u4e2d\u5583\u5583\u5730\u8bf4\u4e86\u201c\u8c22\u8c22\uff01\u201d\u4e24\u4e2a\u5b57\uff0c\u7a81\u7136\u53c8\u8f6c\u5934\u671b\u89c1\uff0c\u7acb\u4e8e\u5e8a\u524d\u7684\u4e71\u53d1\u602a\u4eba\uff0c\u4e24\u53ea\u6bd4\u7535 \u95ea\u8fd8\u660e\u4eae\u7684\u4e00\u53cc\u602a\u76ee\uff0c\u6b63\u5728\u7d27\u7d27\u5730\u76ef\u7740\u81ea\u5df2\u3002\u4ed6\u53c8\u611f\u5230\u8fd9\u6837\u4eb2\u5bc6\u5730\u504e\u5728\u4e00\u4e2a\u964c\u751f\u5c11\u5973\u7684\u6000 \u4e2d\uff0c\u5b9e\u5728\u96be\u4e3a\u60c5\uff0c\u4fbf\u6323\u624e\u7740\u60f3\u5750\u8d77\u6765\uff01 \u3000\u3000\u8c01\u77e5\u4ed6\u4e0d\u6323\u624e\u8fd8\u597d\uff0c\u8fd9\u731b\u529b\u4e00\u6323\uff0c\u53ea\u89c9\u5de6\u81c2\u5904\u7684\u5200\u4f24\uff0c\u4e00\u9635\u566c\u5fc3\u7684\u5de8\u75db\uff0c\u4e0d\u7531\u4f7f\u4ed6\u54ac\u7259 \u76b1\u7709\uff0c\u53c8\u9893\u7136\u5012\u5728\u5c11\u5973\u7684\u6000\u4e2d\u3002 \u3000\u3000\u201c\u54ce\u5440\uff01\u201d\u5c55\u767d\u5929\u751f\u50b2\u9aa8\uff0c\u867d\u7136\u6025\u75db\u94c1\u94c1\u8155\u670d\u7259\u76b1\u7709\uff0c\u6ca1\u6709\u53d1\u51fa\u58f0\u6765\u3002\u4f46\u4ed6\u8fd9\u7b2c\u4e8c\u6b21\u53c8 \u5012\u5728\u5a49\u513f\u7684\u6000\u4e2d\uff0c\u5a49\u513f\u7684\u624b\uff0c\u6b63\u89e6\u5230\u4ed6\u7684\u80a9\u811a\u4e4b\u5904\uff0c\u5a49\u513f\u53ea\u89c9\u89e6\u624b\u6e7f\u6f09\u6f09\u7684\u4e00\u7247\uff0c\u5979\u8fd8\u4ee5\u4e3a \u662f\u81ea\u5df2\u4e0d\u614e\uff0c\u6cfc\u6e85\u51fa\u6765\u7684\u6c34\uff0c\u8c01\u77e5\u5f85\u5979\u64d2\u624b\u770b\u6e05\u7adf\u662f\u9c9c\u7ea2\u7684\u8840\uff0c\u4e0d\u7531\u60ca\u5413\u5f97\u5c16\u53eb\u8d77\u6765\u3002 \u3000\u3000\u201c\u600e\u4e48\uff01\u201d\u96f7\u5927\u53d4\u4e0d\u77e5\u5a49\u513f\u4e3a\u4f55\u5982\u6b64\u60ca\u60f6\uff0c\u6025\u4e0a\u524d\u6765\u63a2\u89c6\u2026\u2026 \u3000\u3000\u201c\u5a49\u513f\u201d\u63a5\u7740\u95e8\u5916\u4e5f\u4f20\u6765\u4e00\u58f0\u60ca\u547c\uff0c\u53ea\u89c1\u4e00\u4e2a\u4e2d\u5e74\u8d35\u5987\uff0c\u73af\u4f69\u53ee\u94db\uff0c\u5feb\u6b65\u8d70\u8fdb\u5c4b\u4e2d\uff0c\u60ca \u95ee\u9053\uff1a\u201c\u600e\u4e48\u4e86\u2026\u2026\u4ed6\uff01\u2026\u2026\u201d \u3000\u3000\u8fd9\u65f6\u96f7\u5927\u53d4\u4e5f\u770b\u6e05\u695a\uff0c\u539f\u6765\u5c55\u767d\u594b\u529b\u4e00\u6323\uff0c\u7adf\u628a\u5de6\u81c2\u4e0a\u7684\u521b\u53e3\uff0c\u91cd\u884c\u9707\u88c2\uff0c\u9c9c\u8840\u900f\u8863\uff0c \u6d41\u4e86\u4e00\u5e8a\u3002 \u3000\u3000\u4e2d\u5e74\u8d35\u5987\u6ee1\u8138\u60ca\u714c\u5173\u5207\u4e4b\u5bb9\uff0c\u4e00\u8fb9\u4f38\u51fa\u7d20\u624b\u8fde\u70b9\u5c55\u81ea\u201c\u81c2\u5112\u201d\u201c\u5fc3\u4fde\u201d\u7a74\uff0c\u4e3a\u5c55\u767d\u6b62\u4f4f \u6d41\u8840\uff0c\u4e00\u8fb9\u56de\u5934\u5bf9\u9526\u8863\u5c11\u5973\u8bf4\u9053\uff1a\u201c\u5a49\u513f\uff0c\u4f60\u53bb\u53d6\u4e00\u676f\u4eba\u53c2\u71d5\u7a9d\u7fb9\u6765\uff0c\u9700\u8981\u6d53\u4e00\u70b9\uff01\u201d \u3000\u3000\u9526\u8863\u5c11\u5973\u5fd9\u4e0d\u9009\u5730\u5e94\u4e86\u4e00\u58f0\uff0c\u98de\u5feb\u5411\u95e8\u5916\u8dd1\u53bb\u2026\u2026 \u3000\u3000\u201c\u5a49\u513f\uff01\u201d\u7ad9\u5728\u4e00\u65c1\u7684\u96f7\u5927\u53d4\uff0c\u5ffd\u7136\u53eb\u4f4f\u5a49\u513f\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u7528\u53bb\u4e86\u3002\u6211\u8fd9\u91cc\u6709\u4e00\u9897\u4e38 \u836f\uff0c\u4eba\u53c2\u71d5\u7a9d\u867d\u80fd\u63d0\u795e\u8865\u8840\uff0c\u4f46\u6211\u8fd9\u9897\u836f\u4e38\uff0c\u5374\u6bd4\u4eba\u53c2\u71d5\u7a9d\u8981\u5f3a\u591a\u4e86\uff01\u201d \u3000\u3000\u96f7\u5927\u53d4\u8bf4\u7740\uff0c\u4ece\u6000\u91cc\u53d6\u51fa\u4e00\u4e2a\u7f8a\u8102\u5c0f\u74f6\u6765\u3002\u8fd9\u7f8a\u8102\u5c0f\u74f6\u53ea\u6709\u9f3b\u70df\u58f6\u5927\u5c0f\uff0c\u5916\u8fb9\u5305\u4e86\u51e0\u5c42 \u7ef8\u5e03\uff1b\u96f7\u5927\u53d4\u90d1\u91cd\u5730\u6253\u5f00\uff0c\u770b\u6837\u5b50\u6781\u4e3a\u73cd\u8d35\u2026\u2026 \u3000\u3000\u201c\u5927\u53d4\u7684\u836f\u4e5f\u7ed9\u4ed6\u5403\uff0c\u95e8\u5916\u4f20\u6765\u5a49\u513f\u7684\u58f0\u97f3\uff1a\u201c\u4eba\u53c2\u71d5\u7a9d\u6c64\u4f84\u5973\u4e5f\u53bb\u53d6\u2026\u2026\u201d\u8bf4\u7740\u5df2\u53bb \u8fdc\u4e86\u2026\u2026 \u3000\u3000\u201c\u8fd9\u5b69\u5b50\u5411\u6765\u662f\u6781\u70ed\u5fc3\u7684\u2026\u2026\u201d\u4e2d\u5e74\u8d35\u5987\u7b11\u5bf9\u96f7\u5927\u53d4\u8bf4\u3002 \u3000\u3000\u5c55\u767d\u8fd9\u6b21\u521b\u53e3\u8ff8\u88c2\uff0c\u867d\u7136\u75db\u6781\uff0c\u5374\u672a\u5931\u53bb\u77e5\u89c9\u3002\u4ed6\u54ac\u7259\u82e6\u5fcd\u7740\u8680\u5fc3\u523a\u9aa8\u7684\u5de8\u75db\uff0c\u7741\u773c\u671b \u7740\u4e2d\u5e74\u8d35\u5987\u5982\u6148\u6bcd\u4e00\u6837\u6148\u7231\u5730\u5173\u5fc3\u7740\u81ea\u5df1\uff0c\u6697\u60f3\u5a49\u513f\u4eb2\u501a\u6c64\u836f\uff0c\u6781\u70ed\u5fc3\u5730\u4e3a\u81ea\u5df1\u5954\u8dd1\uff0c\u4eba\u5bb6 \u5c0a\u8d35\u7684\u8eab\u4efd\u5730\u4f4d\uff0c\u53ef\u4ee5\u8bf4\u662f\u5974\u4ec6\u5982\u4e91\uff0c\u4e00\u547c\u767e\u8bfa\uff0c\u5982\u4eca\u4e3a\u81ea\u5df2\u4e00\u4e2a\u7a77\u82e6\u6c83\u5012\u7684\u4eba\uff0c\u7ade\u80af\u964d\u5c0a \u7ea4\u8d35\uff0c\u5c3d\u5fc3\u670d\u4f8d\u81ea\u5df1\uff0c\u4e0d\u7531\u4e00\u4e1d\u6e29\u6696\u76f4\u88ad\u5fc3\u5934\uff0c\u53ef\u53c8\u5939\u6742\u7740\u65e0\u9650\u611f\u6fc0\u4e4b\u4e00\u7c92\u6765\u5386\u4e0d\u660e\u7684\u836f \u4e38\uff0c\u8981\u631f\u81ea\u5df1\u2026\u2026 \u3000\u3000\u5c55\u767d\u672c\u662f\u751f\u5177\u50b2\u9aa8\u4e4b\u4eba\uff0c\u53c8\u53d7\u5c3d\u4e86\u4eba\u4e16\u95f4\u7684\u51b7\u843d\uff0c\u4ece\u4e0d\u613f\u5411\u4eba\u4e5e\u601c\uff0c\u66f4\u4e0d\u613f\u63a5\u53d7\u522b\u4eba\u7684 \u8981\u631f\u3002 \u3000\u3000\u56e0\u6b64\uff0c\u5728\u96f7\u5927\u53d4\u8981\u4ed6\u8bf4\u5b9e\u8bdd\uff0c\u624d\u80af\u7ed9\u4ed6\u5403\u836f\u65f6\uff0c\u7ade\u8f6c\u5934\u9762\u5411\u5899\u58c1\uff0c\u7ed9\u96f7\u5927\u53d4\u6765\u4e86\u4e2a\u4e0d\u7406 \u4e0d\u776c\u3002 \u3000\u3000\u8fd9\u4e00\u6765\uff0c\u628a\u4e00\u4e2a\u6027\u60c5\u66b4\u8e81\u7684\u96f7\u5927\u53d4\uff0c\u6c14\u4e86\u4e2a\u987b\u7709\u7686\u70b8\u3002 \u3000\u3000\u5c31\u8fde\u4e2d\u5e74\u8d35\u5987\uff0c\u89c1\u5c55\u767d\u5bf9\u96f7\u5927\u53d4\u7684\u5584\u610f\uff0c\u7adf\u505a\u51fa\u65e0\u793c\u7684\u6837\u5b50\uff0c\u4e5f\u4e0d\u7531\u6df1\u611f\u610f\u5916\uff0c\u67d4\u58f0\u8bf4 \u9053\uff1a\u201c\u5b69\u5b50\uff01\u8fd9\u9f99\u864e\u7eed\u547d\u4e39\uff0c\u529f\u53ef\u8d77\u6b7b\u56de\u751f\uff0c\u7ec3\u6b66\u7684\u4eba\u5403\u4e86\uff0c\u66f4\u53ef\u589e\u957f\u529f\u529b\uff0c\u4e00\u822c\u6b66\u6797\u4e4b \u4eba\uff0c\u8fde\u68a6\u60f3\u90fd\u60f3\u4e0d\u5230\u7684\uff01\u96f7\u5927\u53d4\u95ee\u4f60\u4ec0\u4e48\u8bdd\uff0c\u5feb\u56de\u7b54\u5927\u53d4\uff01\u4f60\u5403\u4e0b\u8fd9\u7c92\u4e39\u836f\uff0c\u8eab\u4e0a\u7684\u4f24\u75c5\uff0c \u90fd\u53ef\u4ee5\u597d\u4e86\uff01\u800c\u4e14\uff0c\u5bf9\u4f60\u597d\u5904\u65e0\u7a77\u54e9\u2026\u2026\u201d \u3000\u3000\u4e2d\u5e74\u8d35\u5987\u8bed\u8c03\u6148\u7965\uff0c\u6001\u5ea6\u548c\u853c\u53ef\u4eb2\uff0c\u5bf9\u5c55\u767d\u72b9\u5982\u6148\u6bcd\u3002 \u3000\u3000\u53ef\u662f\uff0c\u5c55\u767d\u4ecd\u6ca1\u6709\u56de\u8fc7\u8138\u6765\uff0c\u9762\u5411\u5899\u58c1\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u5e0c\u7f55\uff01\u201d \u3000\u3000\u201c\u6c14\u6b7b\u6211\u4e5f\uff01\u201d\u96f7\u5927\u53d4\u6012\u53eb\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u96be\u9053\u6211\u771f\u662f\u778e\u4e86\u773c\uff01\u6211\u2026\u2026\u201d \u3000\u3000\u96f7\u5927\u53d4\u795e\u60c5\u6fc0\u8d8a\uff0c\u8bf4\u81f3\u6b64\u5904\uff0c\u7adf\u8bed\u4e0d\u6210\u58f0\u3002\u624b\u62ff\u9f99\u864e\u7eed\u547d\u4e39\uff0c\u5fc3\u4e2d\u6697\u60f3\uff1a\u6b66\u6797\u4e2d\u4eba\u68a6\u5bdd \u6c42\u4e4b\u800c\u4e0d\u53ef\u5f97\uff0c\u81ea\u5df1\u4e0d\u987e\u751f\u547d\u4e3a\u5c11\u6797\u5bfa\u5c3d\u4e86\u4e00\u6b21\u5927\u529b\uff0c\u5c11\u6797\u638c\u95e8\u65b9\u6587\u4e3a\u62a5\u7b54\u81ea\u5df1\u6069\u60e0\uff0c\u624d\u8d60 \u4e86\u8fd9\u4e48\u4e00\u7c92\uff0c\u81ea\u5df1\u73cd\u85cf\u5728\u8eab\u4e0a\u5341\u4e94\u5e74\u4e4b\u4e45\uff0c\u820d\u4e0d\u5f97\u670d\u7528\uff0c\u5982\u4eca\uff0c\u81ea\u5df2\u597d\u5fc3\u597d\u610f\u62ff\u51fa\u6765\u7ed9\u4ed6 \u9662\uff0c\u4eba\u5bb6\u4e0d\u5c51\u4e00\u987e\xb7\u3002\u2606\u2026 \u3000\u3000\u96f7\u5927\u53d4\u8d8a\u60f3\u8d8a\u96be\u8fc7\uff0c\u624b\u6267\u90a3\u7c92\u73cd\u836f\u5de6\u53f3\u4e3a\u96be\u3002 \u3000\u3000\u9001\u51fa\u53c8\u4e0d\u662f\u6536\u56de\u4e5f\u4e0d\u662f\u3002\u5982\u679c\u6b64\u65f6\u81ea\u5df2\u518d\u6536\u56de\u6000\u91cc\uff0c\u522b\u4eba\u53ef\u80fd\u8fd8\u4f1a\u8bf4\u81ea\u5df1\u662f\u820d\u4e0d\u5f97 \u54e9\u2026\u2026 \u3000\u3000\u201c\u53ed\u201d\u7684\u4e00\u58f0\u8106\u54cd\uff0c\u4efb\u8c01\u4e5f\u60f3\u4e0d\u5230\uff0c\u96f7\u5927\u53d4\u7ade\u628a\u4e00\u7c92\u73cd\u8d35\u65e0\u6bd4\u7684\u7075\u836f\uff0c\u4e00\u6296\u624b\u6454\u5728\u5730 \u4e0a\uff01 \u3000\u3000\u5728\u4e2d\u5e74\u8d35\u5987\u60ca\u8bb6\u3001\u83ab\u660e\u6240\u4ee5\u7684\u65f6\u5019\u3002\u96f7\u5927\u53d4\u5df2\u7ecf\u50cf\u7535\u5149\u77f3\u706b\u4f3c\u7684\uff0c\u7eb5\u51fa\u5ba4\u5916\u3002 \u3000\u3000\u7a81\u5982\u5176\u6765\u7684\u4e00\u58f0\u8106\u54cd\uff0c\u5c55\u767d\u60c5\u4e0d\u7531\u5df1\u7684\u8f6c\u56de\u5934\u6765\uff0c\u53ea\u89c1\u4e71\u53d1\u602a\u4eba\u5df2\u4e0d\u5728\u623f\u4e2d\uff0c\u4e2d\u5e74\u8d35\u5987 \u4e00\u8138\u7684\u60ca\u5f02\u4e4b\u5bb9\u3002 \u3000\u3000\u201c\u600e\u4e48\u56de\u4e8b\uff1f\u201d\u5c55\u767d\u4e0d\u77e5\u4f55\u6545\uff0c\u8131\u53e3\u95ee\u51fa\u3002 \u3000\u3000\u201c\u5509\uff01\u201d\u4e2d\u5e74\u8d35\u5987\u8f7b\u5631\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4f60\u4f24\u4e86\u5927\u53d4\u7684\u5fc3\u4e86\u201d\u201c\u4f24\u8c01\u7684\u5fc3\uff1f\u201d\u5fae \u98ce\u8fc7\u5904\uff0c\u5c55\u5a49\u51e0\u5a07\u8273\u5982\u82b1\uff0c\u7ea4\u624b\u6258\u7740\u4e00\u53ea\u7389\u76d8\uff0c\u7389\u76d8\u4e0a\u653e\u7740\u4e00\u4e2a\u78a7\u7389\u76d6\u7897\uff0c\u6613\u5a1c\u5f97\u5982\u98ce\u56de\u6768 \u67f3\uff0c\u4fa0\u6b65\u8d70\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u4e0d\u7b49\u4e2d\u5e74\u8d35\u5987\u7b54\u8a00\uff0c\u5a49\u513f\u5373\u628a\u7389\u76d8\u653e\u5728\u8336\u51e0\u4e0a\uff0c\u7528\u624b\u7aef\u8d77\u76d6\u7897\uff0c\u6380\u4e86\u76d6\uff0c\u5148\u5438\u8d77\u5c0f\u5634\u5439 \u4e86\u5439\u51c9\uff0c\u7136\u540e\u62ff\u4e86\u4e00\u4e2a\u53f0\u7389\u7fb9\u5319\uff0c\u8f7b\u8f7b\u5728\u7897\u5185\u6405\u4e86\u4e00\u6405\uff0c\u7acb\u523b\u7aef\u81f3\u5c55\u767d\u9762\u524d\uff0c\u5a07\u7b11\u8bf4\u9053\uff1a \u201c\u6765\uff01\u5403\u5427\uff0c\u6211\u5582\u4f60\uff01\u201d \u3000\u3000\u5c55\u767d\u5148\u4e0d\u5403\u4eba\u53c2\u71d5\u7a9d\u6c64\uff0c\u542b\u7740\u7591\u95ee\u7684\u773c\u5149\uff0c\u95ee\u90a3\u4e2d\u5e74\u8d35\u5987\u9053\uff1a\u201c\u592b\u4eba\uff0c\u5c0f\u53ef\u4e0d\u613f\u5403\u4ed6\u7684 \u836f\uff0c\u600e\u4e48\u7b97\u662f\u4f24\u4e86\u4ed6\u7684\u5fc3\u5462\uff1f\u2018\u2019\u4e2d\u5e74\u8d35\u5987\u6ca1\u6709\u56de\u7b54\u5c55\u767d\u7684\u95ee\u8bdd\uff0c\u4ef0\u8138\u82e5\u6709\u6240\u601d\uff0c\u505c\u4e86\u4e00 \u4f1a\uff0c\u5ffd\u7136\u4f4e\u4e0b\u5934\u6765\u201d\u96f7\u5927\u53d4\u95ee\u4f60\u4ec0\u4e48\u6765\u7740\uff1f\u201c\u201d\u4ed6\u95ee\u6211\u2026\u2026\u662f\u8c01\uff1f\u201c\u5c55\u767d\u89c1\u4e2d\u5e74\u8d35\u5987\u6148\u853c\u5982 \u6148\u6bcd\uff0c\u4e0d\u5fcd\u62d2\u7edd\u56de\u7b54\u3002\u201d\u53c8\u95ee\u6211\u2026\u2026\u201c\u201d\u53c8\u95ee\u4ec0\u4e48\u5462\uff1f\u201c\u4e2d\u5e74\u8d35\u5987\u663e\u5f97\u5f88\u5173\u5fc3\uff0c\u89c1\u5c55\u767d\u541e\u541e \u5410\u5410\uff0c\u4fbf\u6025\u5207\u5730\u56de\u95ee\u4e86\u4e00\u65ec\u3002\u201d\u5a18\uff01\u201c\u8fd9\u65f6\uff0c\u5c55\u5a49\u513f\u5374\u5728\u4e00\u8fb9\u63d2\u5634\u9053\uff1a\u201c\u4e0d\u8981\u95ee\u90a3\u4e48\u591a\u561b\uff01 \u5148\u8ba9\u4ed6\u5403\uff0c\u597d\u4e0d\u597d\uff01\u82e5\u4e0d\uff0c\u4ed6\u4f1a\u2026\u2026\u201d \u3000\u3000\u201c\u522b\u6253\u5c94\uff01\u201d\u4e2d\u5e74\u8d35\u5987\u795e\u8272\u5f88\u5e84\u91cd\uff0c\u963b\u6b62\u5a49\u513f\u63d2\u5634\uff0c\u4e00\u53cc\u7f8e\u5999\u7684\u98ce\u76ee\uff0c\u201c\u53c8\u95ee\u5c55\u2026\u2026\u201d \u5c55\u767d\u53ea\u6709\u636e\u5b9e\u56de\u7b54\u3002\u4f46\u8bf4\u5230\u7236\u4eb2\u7684\u540d\u5b57\u65f6\uff0c\u4e0d\u7981\u6fc0\u52a8\u5f97\u5634\u5507\u53d1\u79d1\uff0c\u8bf4\u9053\uff1a\u201c\u2026\u2026\u4e91\u5929\uff0c\u662f\u6211 \u7684\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u542c\u5230\u5c55\u4e91\u5929\u7684\u540d\u5b57\uff0c\u4e2d\u5e74\u8d35\u5987\u7684\u795e\u60c5\uff0c\u4f3c\u4e4e\u4e00\u9707\uff0c\u66f4\u52a0\u7d27\u5730\u95ee\u5c55\u767d\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u56de\u7b54 \u96f7\u5927\u53d4\uff1f\u5c55\u2026\u2026\u4e91\u5929\uff0c\u7a76\u7adf\u662f\u4f60\u7684\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u201c\u5c55\u2026\u2026\u4e91\u5929\u662f\u5148\u7236\u2026\u2026\u201d\u5c55\u767d\u611f\u6fc0\u4e2d\u5e74\u8d35\u5987\u6551\u547d\u4e4b\u6069\uff0c\u53c8\u52a0\u4e0a\u4e2d\u5e74\u8d35\u5987\u5f85\u4ed6\u5982\u6148\u6bcd\uff0c \u53ea\u6709\u636e\u5b9e\u4ee5\u7b54\u3002 \u3000\u3000\u4e2d\u5e74\u8d35\u5987\u542c\u5c55\u767d\u8bf4\u51fa\uff0c\u5c55\u4e91\u5929\u662f\u4ed6\u7236\u4eb2\uff0c\u8138\u4e0a\u987f\u73b0\u51fa\u4e00\u79cd\u65e0\u6bd4\u60ca\u5947\u4e4b\u5bb9\uff0c\u51e4\u76ee\u4e2d\u73b0\u51fa\u4e00 \u79cd\u65e0\u6bd4\u6b23\u559c\u7684\u5149\u5f69\uff0c\u5f20\u53e3\u6b32\u8a00\uff0c\u4f46\u5fc3\u60c5\u6fc0\u52a8\u65e0\u6bd4\uff0c\u7ade\u4e00\u65f6\u987f\u4efb\uff0c\u4e0d\u77e5\u8bf4\u4ec0\u4e48\u624d\u597d\u2026\u2026 \u3000\u3000\u90a3\u7edd\u8272\u9526\u8863\u7f8e\u5973\u2014\u2014\u5c55\u5a49\u513f\uff0c\u5374\u4e0d\u77e5\u5c55\u4e91\u5929\u662f\u4f55\u4eba\uff0c\u4e5f\u672a\u7559\u610f\u4e2d\u5e74\u8d35\u5987\u795e\u8272\u907d\u53d8\uff0c\u53ea\u7aef \u7740\u4eba\u53c2\u71d5\u7a9d\u626c\uff0c\u4e00\u53ea\u7ea4\u624b\u62ff\u7740\u767d\u7389\u5319\uff0c\u8981\u5582\u5c55\u767d\u5403\uff1b\u5ffd\u89c1\u5c55\u767d\u6389\u5934\u843d\u6cea\uff0c\u5fd9\u628a\u767d\u7389\u5319\u653e\u8fdb\u7897 \u5185\uff0c\u5728\u8863\u895f\u5185\u638f\u51fa\u4e00\u65b9\u7ee2\u5e15\uff0c\u4e00\u8fb9\u4e3a\u5c55\u767d\u62ed\u6cea\uff0c\u4e00\u8fb9\u4ee5\u4e07\u822c\u6e29\u67d4\u7684\u58f0\u8c03\u8bf4\u9053\uff1a\u201c\u4e0d\u8981\u54ed\u561b\uff01 \u6765\uff0c\u64e6\u5e72\u773c\u6cea\uff0c\u5403\u4e0b\u8fd9\u7897\u4eba\u53c2\u71d5\u7a9d\u6c64\uff0c\u4f60\u7684\u75db\u5c31\u4f1a\u597d\u5566\uff01\u4e56\uff01\u542c\u8bdd\uff0c\u554a\uff01H\u2026\u201d \u3000\u3000\u8fd9\u4e8c\u516b\u5e74\u534e\u7684\u5c11\u5973\uff0c\u54ea\u91cc\u662f\u50cf\u8ddf\u4e00\u4e2a\u5c1a\u6bd4\u5979\u5927\u4e00\u4e24\u5c81\u7684\u5c11\u5e74\u8bf4\u8bdd\uff0c\u5012\u50cf\u662f\u5728\u54c4\u5b69\u5b50\u3002 \u3000\u3000\u5c55\u767d\u5fc3\u5934\u611f\u5230\u4e00\u9635\u65e0\u6bd4\u7684\u5f02\u6837nnoo\u6b64\u65f6\u2014\u2014\u4e00 \u3000\u3000\u5ffd\u7136\u4ece\u95e8\u5916\u614c\u614c\u5f20\u5f20\u8dd1\u8fdb\u6742\u4e00\u4e2a\u9752\u8863\u5c0f\u5a62\uff0c\u8fdb\u95e8\u6765\u5f20\u671b\u5230\u4e2d\u5e74\u8d35\u5987\uff0c\u5fd9\u4e0a\u524d\u65bd\u793c\u8bf4\u9053\uff0c \u201c\u592b\u4eba\uff01\u2026\u2026\u60a8\u5728\u8fd9\u91cc\u5440\uff01\u53eb\u5c0f\u5a62\u597d\u627e\u2026\u3002\xb7\u8001\u3002\u2018\u2026\xb7\u7237\u5b50\u6b63\u6025\u7740\u2026\u2026\u627e\u592b\u4eba\u2026\u2026\u201d\u9752\u8863 \u5c0f\u5a62\u597d\u4f3c\u7d27\u5f20\u8fc7\u5ea6\uff0c\u8138\u5b54\u6da8\u5f97\u901a\u7ea2\uff0c\u4e0a\u6c14\u4e0d\u63a5\u4e0b\u6c14\uff0c\u7ed3\u7ed3\u5df4\u5df4\u5730\u8bf4\u3002\u4e2d\u5e74\u8d35\u5987\u76b1\u4e86\u76b1\u7709\uff0c\u8138 \u4e0a\u95ea\u8fc7\u4e00\u4e1d\u4e0d\u5927\u6109\u5feb\u7684\u795e\u8272\uff0c\u5e73\u9759\u5730\u95ee\u9053\uff1a\u201c\u8001\u7237\u5b50\u627e\u6211\u6709\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u201c\u5c0f\u5a62\u4e0d\u2026\u2026\u4e0d\u77e5\u9053\u3002\u201d\u9752\u8863\u5c0f\u5a62\u7ed3\u5df4\u5730\u8bf4\uff1a\u201c\u8001\u7237\u5b50\u6b63\u5728\u53d1\u2026\u2026\u53d1\u813e\u6c14\uff0c\u8bf4\u53eb\u592b \u4eba\u2026\u2026\u5feb\u53bb\uff01\u201d \u3000\u3000\u4e2d\u5e74\u8d35\u5987\u4f3c\u662f\u65e0\u53ef\u5948\u4f55\u5730\u7acb\u8d77\u8eab\u6765\uff0c\u53c8\u671b\u4e86\u5012\u5728\u5e8a\u4e0a\u7684\u5c55\u767d\u4e00\u773c\uff0c\u5411\u5a49\u513f\u8bf4\u9053\uff1a\u5a49\u513f\u3002 \u4f60\u8981\u597d\u597d\u7167\u987e\u4ed6\uff0c\u5a18\u53bb\u53bb\u5c31\u6765\uff01\u201c\u5a49\u513f\u55ef\u4e86\u4e00\u58f0\uff0c\u4e2d\u5e74\u8d35\u5987\u5373\u968f\u7740\u9752\u8863\u5c0f\u5a62\u800c\u53bb\u3002\u8fd9\u65f6\uff0c\u504c \u5927\u4e00\u95f4\u534e\u4e3d\u7684\u5367\u5ba4\u4e4b\u4e2d\uff0c\u53ea\u5269\u4e0b\u5a49\u513f\u4e0e\u5c55\u767d\u4e8c\u4eba\u3002\u5c55\u5a49\u513f\u64a4\u5a07\u4f7f\u8d56\uff0c\u534a\u54c4\u5e26\u529d\uff0c\u4e00\u53e3\u4e00\u53e3\u5730 \u5582\u7740\u5c55\u767d\u5403\u4e86\u90a3\u7897\u4eba\u53c2\u71d5\u7a9d\u6c64\u3002\u5c55\u81ea\u4ece\u6bcd\u4eb2\u6b7b\u540e\uff0c\u5929\u6daf\u98d8\u96f6\uff0c\u5386\u5c3d\u4e16\u6001\u708e\u51c9\uff0c\u4ece\u6765\u6ca1\u6709\u5c1d\u53d7 \u8fc7\u8fd9\u822c\u6e29\u60c5\u3002\u53ea\u89c9\u82b3\u9999\u6251\u9f3b\u751c\u7f8e\u5982\u871c\u7684\u4eba\u53c2\u71d5\u7a9d\u6c64\uff0c\u4ece\u5c11\u5973\u767d\u7389\u822c\u7684\u7ea4\u624b\u4e2d\uff0c\u4e00\u53e3\u4e00\u53e3\u5730\u5582 \u8fdb\u81ea\u5df2\u5634\u91cc\uff0c\u8fd9\u4e00\u751c\u76f4\u751c\u5230\u5fc3\u574e\u91cc\uff0c\u6696\u6696\u7684\u70ed\u6c14\uff0c\u4e5f\u968f\u7740\u4eba\u53c2\u71d5\u7a9d\u6c64\uff0c\u2014\u76f4\u6e29\u6696\u5230\u5fc3\u7a9d\uff01\u5c55 \u767d\u4e00\u8fb9\u5f20\u5634\u5403\u7740\uff0c\u4e00\u8fb9\u4e0d\u4f4f\u6253\u91cf\u8fd9\u4f4d\u5bf9\u5f85\u81ea\u5df2\u6709\u7740\u65e0\u6bd4\u6df1\u60c5\u7684\u7edd\u7f8e\u5c11\u5973\u3002\u89c1\u5979\u8eab\u7a7f\u4e00\u88ad\u526a\u88c1 \u5408\u4f53\u7684\u6d45\u84dd\u8272\u9526\u8863\uff0c\u90a3\u9526\u8863\u7684\u8d28\u6599\u975e\u4e1d\u975e\u7ef8\uff0c\u5374\u67d4\u98d8\u5149\u4eae\u65e0\u6bd4\uff0c\u4f7f\u5979\u66f2\u7ebf\u73b2\u6591\u7684\u5a07\u4f53\uff0c\u5999\u97f5 \u5929\u6210\uff0c\u66f4\u89c1\u4f18\u7f8e\uff01\u6d45\u84dd\u95ea\u4eae\u7684\u9526\u8863\u9886\u66f0\uff0c\u7ee3\u7740\u4e00\u5708\u767d\u8272\u7684\u5c0f\u82b1\uff0c\u4eff\u4f5b\u5927\u6d77\u91cc\u6db5\u8d77\u7684\u767d\u8272\u6d6a \u82b1\uff0c\u6e05\u65b0\u7eaf\u7f8e\u3002\u5c11\u5973\u5468\u8eab\u7684\u808c\u80a4\uff0c\u767d\u5982\u51dd\u8102\uff0c\u767d\u7389\u8239\u7684\u7c89\u9888\uff0c\u4e4c\u9ed1\u7684\u79c0\u53d1\uff0c\u88ad\u76d6\u7740\u4e00\u6735\u671d\u971e \u91cc\u76db\u5f00\u7684\u81ea\u83b2\u822c\u7684\u692d\u5706\u5c0f\u8138\uff0c\u7ec6\u957f\u7684\u7709\u513f\uff0c\u5982\u8774\u8776\u7fc5\u8180\u4e00\u6837\u5de6\u53f3\u5f00\u5c55\u7740\uff0c\u7476\u9f3b\u6a31\u53e3\uff0c\u4e00\u53cc\u9ed1 \u767d\u5206\u660e\u7684\u660e\u7741\uff0c\u987e\u76fc\u751f\u5a46\u3002\u7b11\u65f6\u9732\u51fa\u7f16\u8d1d\u4f3c\u7684\u7693\u9f7f\uff0c\u5634\u4e24\u8fb9\u6709\u4e24\u4e2a\u6df1\u6df1\u7684\u68a8\u7a9d\uff0c\u53eb\u4eba\u770b\u4e86\u610f \u4e71\u60c5\u8ff7\u3002\u2026\u2018\u4f46\u6700\u4f7f\u4eba\u52a8\u5fc3\u7684\uff0c\u8fd8\u4e0d\u5728\u5979\u8fd9\u8131\u5c18\u51fa\u4fd7\u3001\u7f8e\u903e\u5929\u4ed9\u7684\u5bb9\u8c8c\uff0c\u800c\u662f\u5979\u90a3\u4e00\u79cd\u5185\u5728 \u7684\u6c14\u8d28\uff0c\u5a07\u61a8\u5929\u771f\uff0c\u6beb\u65e0\u4e00\u70b9\u673a\u5fc3\uff0c\u7eaf\u6d01\u5584\u826f\u5f97\u72b9\u5982\u5929\u4f7f\u3002\u73b0\u5728\u5979\u5a07\u8eaf\u4f9d\u504e\u5728\u5e8a\u524d\uff0c\u51e0\u4e0e\u5c55 \u767d\u808c\u80a4\u76f8\u63a5\uff0c\u5c55\u767d\u4e00\u8fb9\u5f20\u5634\u63a5\u5403\u7740\u5c11\u5973\u4e00\u5319\u4e00\u5319\u9001\u6765\u7684\u53c2\u6c64\uff0c\u4e00\u8fb9\u9f3b\u5b54\u4e2d\u55c5\u5230\u4ece\u5979\u8eab\u4e0a\u6563\u53d1 \u51fa\u6765\u7684\u5904\u5973\u4e4b\u5e7d\u9999\uff0c\u51e0\u7591\u8eab\u5728\u68a6\u4e2d\uff01\u4ed6\u5de6\u601d\u53f3\u60f3\uff0c\u4e5f\u60f3\u4e0d\u51fa\u8fd9\u840d\u6c34\u76f8\u9022\u7684\u7edd\u7f8e\u5c11\u5973\uff0c\u4e3a\u4ec0\u4e48 \u4f1a\u5bf9\u4ed6\u8fd9\u6837\u597d\uff1f\u2026\u2026\u201d\u5728\u4e0b\u2026\u2026\u60f3\u95ee\u59d1\u5a18\u4e00\u53e5\u8bdd\u3002\u201c\u5728\u90a3\u9752\u8863\u5c0f\u5a62\u53eb\u8d70\u4e2d\u5e74\u8d35\u5987\u65f6\uff0c\u4f7f\u5c55\u767d \u60f3\u8d77\u4e2d\u5e74\u8d35\u5987\u5728\u9014\u4e2d\u6551\u81ea\u5df1\u65f6\u7684\u5fe7\u90c1\u795e\u60c5\uff0c\u4e0d\u77e5\u5982\u6b64\u9ad8\u8d35\u6148\u7965\u7684\u8d35\u5987\u4eba\uff0c\u8fd8\u4f1a\u6709\u4ec0\u4e48\u5fc3\u4e8b\uff1f \u53c8\u60f3\u8d77\u8fd9\u5a49\u513f\u5982\u6b64\u7eaf\u771f\u5584\u826f\uff0c\u7ade\u8ddf\u521a\u624d\u90a3\u5014\u50b2\u5c11\u5e74\uff0c\u4e0e\u90a3\u51b7\u82e5\u51b0\u971c\u3001\u795e\u79d8\u65e0\u6bd4\u7684\u9ed1\u8863\u8499\u9762\u5973 \u90ce\uff0c\u50cf\u662f\u5144\u5f1f\u59ca\u59b9\u4f3c\u7684\uff0c\u8981\u662f\u540c\u80de\u5144\u5f1f\u59ca\u59b9\uff0c\u6027\u683c\u600e\u4f1a\u5982\u6b64\u4e0d\u540c\uff0c\u800c\u90a3\u9752\u8863\u5c0f\u5a62\u53e3\u4e2d\u7684\u8001\u7237 \u5b50\u53c8\u662f\u8c01\uff1f\u5c55\u767d\u5fc3\u4e2d\u5145\u6ee1\u4e86\u7591\u95ee\uff0c\u7981\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u4e0d\u77e5\u59d1\u5a18\u2026\u2026\u80af\u5f00\u8bda\u76f8\u544a\u5426\uff1f\u201d \u3000\u3000\u4f46\uff0c\u5c55\u767d\u95ee\u51fa\u53e3\u6765\uff0c\u624d\u89c9\u5f97\u63a2\u8be2\u4eba\u5bb6\u7684\u9690\u79c1\uff0c\u5b9e\u6709\u5192\u6627\u4e4b\u5acc\uff0c\u4e0d\u7531\u5f97\u541e\u541e\u5410\u5410\u3002 \u3000\u3000\u201c\u5728\u4e0b\u2026\u2026\u59d1\u5a18\u2026\u2026\u59d1\u5a18\u2026\u2026\u5728\u4e0b\u2026\u2026\u201d\u5a49\u513f\u6a21\u4eff\u7740\u5c55\u767d\u7684\u53e3\u543b\uff0c\u5b8b\u8bf4\u5b8c\u5148\u81ea\u82b1\u6821\u4e71\u98a4 \u5730\u7b11\u8d77\u6765\u3002 \u3000\u3000\u53c8\u8bf4\uff1a\u201c\u54ce\u5440\uff0c\u9178\u6b7b\u4e86\uff01\u201d \u3000\u3000\u5c55\u767d\u8138\u5b54\u4e00\u7ea2\u2026\u2026 \u3000\u3000\u201c\u767d\u54e5\uff0c\u6709\u4ec0\u4e48\u8bdd\u5c3d\u7ba1\u95ee\u597d\u5566\uff01\u201d\u5a49\u513f\u4e00\u7247\u5929\u771f\uff0c\u4e0a\u8fb9\u7684\u8bdd\u53ea\u662f\u89c9\u5f97\u597d\u73a9\uff0c\u4e1d\u6beb\u6ca1\u6709\u8ba5 \u7b11\u5c55\u767d\u7684\u6210\u4efd\u3002 \u3000\u3000\u4e00\u89c1\u5c55\u767d\u8138\u7ea2\uff0c\u7acb\u523b\u6b62\u4f4f\u4e86\u563b\u7b11\uff0c\u8bda\u6073\u5730\u8bf4\u9053\uff1a\u201c\u5982\u5c0f\u59b9\u77e5\u9053\u7684\u4e00\u5b9a\u544a\u8bc9\u4f60\u3002\u4e0d\u8981\u52c9 \u5a18\u3001\u5728\u4e0b\u7684\uff0c\u542c\u7740\u591a\u89c1\u5916\uff01\u4ee5\u540e\u5c31\u53eb\u6211\u59b9\u59b9\u597d\u5566\uff01\u201d \u3000\u3000\u201c\u54ea\u80fd\u2026\u2026\u6211\u5b9e\u4e0d\u6562\u5f53\u2026\u2026\u201d\u5c55\u767d\u8fd8\u60f3\u63a8\u6258\uff0c\u8c01\u77e5\u5a49\u513f\u63a5\u5634\u9053\uff1a\u201c\u6211\u4eec\u90fd\u59d3\u5c55\uff0c\u6ca1\u6709\u4ec0 \u4e48\u6562\u5f53\u4e0d\u6562\u5f53\uff01\u767d\u54e5\uff0c\u4f60\u6709\u4ec0\u4e48\u8bdd\u5c31\u5feb\u8bf4\u5427\uff01\u201d \u3000\u3000\u5c55\u767d\u89c1\u5a49\u513f\u8654\u8bda\uff0c\u81ea\u5df1\u4e0d\u597d\u610f\u601d\u518d\u63a8\u6258\uff0c\u4fbf\u8bf4\u9053\uff1a\u201c\u5a49\u59b9\u2014\u2014\u201d\u8fd9\u4e00\u58f0\u5a49\u59b9\uff0c\u5a49\u513f\u542c\u4e86 \u751a\u662f\u5f00\u5fc3\uff0c\u7b11\u5bb9\u5982\u82b1\u3002 \u3000\u3000\u201c\u90a3\u9752\u8863\u5c0f\u5a62\u53e3\u4e2d\u8bf4\u7684\u8001\u7237\u4e88\uff0c\u662f\u4e0d\u662f\u4ee4\u5c0a\u2026\u2026\u201d \u3000\u3000\u5a49\u513f\u62a2\u7740\u70b9\u4e86\u70b9\u5934\uff0c\u5c55\u767d\u7ee7\u7eed\u95ee\u9053\uff1a\u201c\u4ee4\u5802\u597d\u50cf\u662f\u4e0d\u751a\u5feb\u4e50\uff0c\u96be\u9053\u4ee4\u5c0a\u4e0e\u4ee4\u5802\u2026\u2026\u201d \u3000\u3000\u5a49\u513f\u7b11\u5bb9\u7acb\u655b\uff0c\u9891\u76b1\u86fe\u7709\uff0c\u65e0\u9650\u59d4\u5a49\u5730\u8bf4\u9053\uff1a\u201c\u767d\u54e5\uff0c\u8bf7\u4f60\u4e0d\u8981\u95ee\u6211\u8fd9\u4e9b\u597d\u4e0d\u597d\uff0c\u5c0f\u59b9 \u4e0d\u613f\u8c08\u8bba\u4e0a\u4e00\u8f88\u4eba\u7684\u4e8b\u2026\u2026\u201d \u3000\u3000\u5a49\u513f\u8bf4\u5230\u6700\u540e\u8bed\u58f0\u6e10\u4f4e\uff0c\u5934\u4e5f\u8ddf\u7740\u4f4e\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u5c55\u81ea\u89c1\u5a49\u513f\u5e7d\u6028\u4e4b\u60c5\uff0c\u77e5\u9053\u4eba\u5bb6\u6709\u96be\u8a00\u4e4b\u9690\uff0c\u4fbf\u6539\u53e3\u95ee\u9053\uff1a\u201c\u65e2\u662f\u5a49\u59b9\u4e0d\u613f\u8bf4\uff0c\u611a\u5144\u4e0d \u4fbf\u518d\u95ee\u3002\u4f46\u662f\uff0c\u611a\u5144\u8fd8\u6709\u4e00\u4e8b\uff0c\u6df1\u611f\u4e0d\u89e3\uff0c\u4e3a\u4ec0\u4e48\u5a49\u59b9\u8fd9\u6837\u597d\uff0c\u4ee4\u5144\u5374\u90a3\u4e48\u5484\u5484\u903c\u4eba\uff1f\u4ee4\u59d0 \u53c8\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u8981\u8c08\u4ed6\u4eec\u5566\uff01\u201d\u5a49\u513f\u53c8\u62ac\u8d77\u5934\u6765\uff0c\u542b\u7740\u65e0\u9650\u6df1\u60c5\u51dd\u671b\u7740\u5c55\u767d\uff0c\u8bf4\u9053\uff1a\u201c\u4e5f\u8ba9\u5c0f\u59b9\u8bf7 \u95ee\u51e0\u4e2a\u95ee\u9898\uff0c\u767d\u54e5\uff0c\u4f60\u7684\u75c5\u597d\u4e86\u4ee5\u540e\uff0c\u51c6\u5907\u4f5c\u4f55\u6253\u7b97\uff1f\u201d \u3000\u3000\u5c55\u767d\u84e6\u5730\u542c\u5230\u5a49\u513f\u5982\u6b64\u4e00\u95ee\uff0c\u5343\u7aef\u4e07\u7eea\uff0c\u7acb\u523b\u538b\u4e0a\u5fc3\u5934\uff0c\u4e0d\u7531\u4f7f\u4ed6\u5446\u4f4f\u4e86\u2026\u2026 \u3000\u3000\u201c\u7236\u4ec7\u4e0d\u5171\u6234\u5929\uff01\u201d\u5f53\u7136\u81ea\u5df1\u75c5\u6108\u4e4b\u540e\uff0c\u662f\u8981\u53bb\u4e3a\u7236\u4eb2\u62a5\u4ec7\u3002 \u3000\u3000\u56e0\u81ea\u5df1\u8fde\u7236\u4eb2\u7684\u4ec7\u4eba\u662f\u8c01\uff0c\u90fd\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u800c\u4e14\uff0c\u53c8\u628a\u7236\u4eb2\u4e34\u6b7b\u65f6\uff0c\u9057\u7559\u4e0b\u7684\u5b9d\u5251\u53ca\u9057\u7269\uff0c\u4e5f\u7ed9\u5f04\u4e22\u4e86\u3002 \u3000\u3000\u81ea\u5df1\u6b66\u529f\u672b\u6210\uff0c\u4e3e\u76ee\u65e0\u63f4\uff0c\u6b64\u540e\u8fde\u4e2a\u5b58\u8eab\u4e4b\u5904\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u534a\u9014\u5f03\u804c\uff0c\u71d5\u4eac\u9556\u5c40\u662f\u65e0\u8138\u518d\u56de\u53bb\uff0c\u81f3\u4e8e\u73b0\u5728\u81ea\u5df1\u5b58\u8eab\u4e4b\u5904\u2014\u2014\u8fd9\u795e\u79d8\u4e0d\u53ef\u6d4b\u7684\u5730\u65b9\uff0c \u867d\u7136\u4e2d\u5e74\u8d35\u5987\u53ca\u5a49\u513f\uff0c\u5bf9\u5f85\u81ea\u5df1\u751a\u597d\uff0c\u4f46\u8bf4\u4e0d\u5b9a\u4eba\u5bb6\u662f\u89c1\u81ea\u5df1\u4f24\u75c5\uff0c\u624d\u4ea7\u751f\u4e86\u540c\u60c5\uff0c\u7b49\u5230\u81ea \u5df1\u4f24\u597d\u75c5\u6108\uff0c\u4e07\u65e0\u6551\u4f4f\u4e4b\u7406\u3002\u4f55\u51b5\uff0c\u8fd8\u6709\u90a3\u5014\u50b2\u5c11\u5e74\uff0c\u53ca\u90a3\u75af\u766b\u7684\u4e71\u53d1\u602a\u4eba\uff0c\u81ea\u5df1\u60f3\u8d77\u6765\u5c31 \u5bd2\u5fc3\uff0c\u5c31\u7b97\u8ba9\u81ea\u5df1\u4efb\uff0c\u81ea\u5df1\u4e5f\u4f4f\u4e0d\u4e0b\u53bb\u2026\u2026 \u3000\u3000\u5c55\u767d\u601d\u53ca\u6b64\u5904\uff0c\u987f\u611f\u524d\u9014\u832b\u832b\uff0c\u5145\u6ee1\u4e86\u60b2\u89c2\u4e0e\u65e0\u671b\uff0c\u771f\u662f\u5230\u4e86\u8d70\u6295\u65e0\u8def\u7684\u7edd\u5883\u4e86\uff01 \u3000\u3000\u56fa\u7136\uff0c\u5c55\u767d\u5904\u6b64\u60b2\u89c2\u7edd\u671b\u4e4b\u5883\uff0c\u5bf9\u4eba\u4e16\u4e00\u65e0\u7559\u604b\uff0c\u4e00\u6b7b\u6beb\u4e0d\u8db3\u60dc\u3002\u4f46\u60f3\u5230\u7236\u4ec7\u672a\u62a5\uff0c\u81ea \u5df1\u53c8\u4e0d\u80fd\u6b7b\u3002\u771f\u5e94\u4e86\u90a3\u53e5\u5fcf\u8bed\uff1a\u201c\u6c42\u751f\u65e0\u8def\uff0c\u6b32\u6b7b\u65e0\u95e8\uff01\u201d \u3000\u3000\u601d\u53ca\u6b64\u5904\uff0c\u5c55\u81ea\u771f\u6709\u82f1\u96c4\u672b\u8def\u4e4b\u611f\uff0c\u4e0d\u81ea\u89c9\u5730\u6ef4\u4e0b\u51e0\u6ef4\u82f1\u7ef4\u6cea\u6765\u2026\u2026 \u3000\u3000\u201c\u767d\u54e5\uff01\u201d\u8c01\u77e5\u5a49\u513f\u89c1\u613f\u767d\u5e10\u671b\u5c4b\u9876\uff0c\u9ed8\u9ed8\u65e0\u8a00\uff0c\u72ec\u81ea\u843d\u6cea\uff0c\u7ade\u4e00\u63a2\u5a07\u8eaf\uff0c\u4f0f\u5728\u5c55\u767d\u8eab \u4e0a\uff0c\u53cc\u624b\u62b1\u4f4f\u5c55\u767d\uff0c\u7528\u4e00\u79cd\u94c1\u77f3\u4e4b\u4eba\u542c\u4e86\u4e5f\u4f1a\u5fc3\u8f6f\u7684\u6e29\u67d4\u58f0\u8c03\u8bf4\u9053\uff1a\u5929\u6daf\u6d77\u89d2\uff0c\u4e0d\u7ba1\u4f60\u8d70\u5230 \u54ea\u91cc\uff0c\u5c55\u5a49\u59b9\u4e5f\u4e0d\u8ddf\u4f60\u5206\u5f00\uff01\u201c\u8fd9\u7eaf\u6d01\u5c11\u5973\u7684\u771f\u60c5\u6d41\u9732\uff0c\u4f7f\u5c55\u767d\u5fc3\u4e2d\u5927\u4e3a\u611f\u52a8\uff0c\u72b9\u5982\u5728\u708e\u51c9 \u7684\u4eba\u4e16\u4e4b\u4e2d\uff0c\u989d\u9022\u77e5\u5df1\u4e00\u822c\u3002\u5bd2\u51ac\u91cc\u53c8\u51fa\u73b0\u4e86\u6625\u5929\uff0c\u7edd\u671b\u4e2d\u53c8\u751f\u51fa\u4e86\u5e0c\u671b\uff0c\u9ed1\u6697\u91cc\u6709\u4e86\u5149 \u660e\uff0c\u6c99\u6f20\u4e2d\u5f00\u904d\u4e86\u82b1\u6735\uff01\u8fd9\u867d\u662f\u865a\u5e7b\u7684\u4e0d\u53ef\u6349\u6478\uff0c\u4f46\uff0c\u53c8\u663e\u5f97\u591a\u4e48\u5145\u5b9e\u5462\uff1f\u5c55\u767d\u60c5\u4e0d\u7531\u5df2 \u5730\uff0c\u4e5f\u4ece\u88ab\u4e2d\u63a2\u51fa\u53cc\u624b\uff0c\u7d27\u7d27\u62e5\u62b1\u7740\u5a49\u513f\uff0c\u5634\u4e2d\u5583\u5583\u4f4e\u8bed\uff1a\u201c\u662f\u7684\uff0c\u6211\u4eec\u6c38\u8fdc\u4e0d\u5206\u5f00\uff01\u6c38\u8fdc \u4e0d\u5206\u5f00\uff0c\u6c38\u8fdc\u4e0d\u5206\u5f00\u2026\u2026\u201d \u3000\u3000\u201c\u54fc\uff1a\u201c\u7a81\u7136\u7a97\u5916\u4f20\u6765\u4e00\u58f0\u6781\u51b7\u7684\u51b7\u54fc\uff0c\u6709\u5982\u4e00\u9635\u51db\u51bd\u7684\u5bd2\u98ce\uff0c\u5239\u65f6\uff0c\u628a\u904d\u5730\u7684\u82b1\u6735\u5439 \u843d\u5f97\u65e0\u5f71\u65e0\u8e2a\u3002\u201d\u65e0\u803b\u7684\u4e2b\u5934\uff0c\u80c6\u6562\u8d25\u574f\u95e8\u98ce\uff1a\u201c\u51b7\u54fc\u8fc7\u540e\uff0c\u8ddf\u7740\u4f20\u6765\u4e00\u58f0\u5bd2\u51b7\u72b9\u5982\u51b0\u7a9f\u96ea \u7a96\u7684\u8bed\u58f0\u8d23\u9a82\u3002\u201d\u5927\u54e5\uff01\u201c\u5a49\u513f\u5a07\u559d\u4e00\u58f0\uff1a\u201c\u4f60\u6562\u6b3a\u4fae\u6211\uff01\u201d\u559d\u7f62\uff0c\u5a49\u513f\u4ece\u5c55\u767d\u6000\u4e2d\u6323\u8d77\uff0c \u98de\u63a0\u81f3\u7a97\u5916\u3002 \u3000\u3000\u4e00\u9635\u4e89\u5435\u58f0\u4f20\u6765\uff0c\u6108\u8d70\u6108\u8fdc\uff0c\u7ec8\u4e8e\u542c\u4e0d\u5230\u4e86\u2026\u3002\u2018\u5239\u90a3\u4e4b\u95f4\uff0c\u5c55\u81ea\u4eff\u4f5b\u89c9\u5f97\u65b9\u624d\u9038\u7136\u7684 \u623f\u95f4\uff0c\u4e8e\u4eca\u53c8\u53d8\u5f97\u5bc2\u5bde\u51b7\u6e05\u8d77\u6765\uff0c\u8fd9\u76db\u590f\u7684\u516d\u6708\u4e4b\u591c\uff0c\u600e\u7684\u6709\u5982\u6b64\u5bc2\u5bde\u51b7\u6e05\u7684\u611f\u89c9\uff0c\u4ed6\u81ea\u5df1 \u4e5f\u5f04\u4e0d\u6e05\u695a\uff0c\u800c\u8111\u6d77\u4e4b\u4e2d\uff0c\u90fd\u504f\u504f\u53c8\u6df7\u4e71\u5f97\u5f88\u3002\u81ea\u4ed6\u5728\u90a3\u5c0f\u6797\u4e2d\u9047\u7740\u5b89\u4e50\u516c\u5b50\u4e4b\u540e\uff0c\u4e00\u5207\u4e16 \u4e8b\u5c31\u4eff\u4f5b\u53d8\u5f97\u6df7\u4e71\u4e0d\u582a\uff0c\u4ed6\u867d\u60f3\u9759\u4e0b\u601d\u6f6e\u6765\u4ed4\u7ec6\u601d\u91cf\u2014\u904d\uff0c\u7ade\u4e0d\u80fd\u591f\u3002 \u7b2c\u516b\u7ae0\u3000\u77f3\u7a9f\u4e4b\u79d8>> \u53e4\u9f99\u300a\u5251\u5ba2\u884c\u300b \u7b2c\u516b\u7ae0\u3000\u77f3\u7a9f\u4e4b\u79d8 \u3000\u3000\u4eba\u7c7b\u7684\u60c5\u7eea\uff0c\u7684\u786e\u5947\u602a\u5f97\u4ee4\u4eba\u96be\u4ee5\u89e3\u91ca\u3002\u6709\u65f6\uff0c\u4f60\u5728\u4e00\u4e2a\u70ed\u95f9\u65e0\u6bd4\u7684\u573a\u5408\u91cc\uff0c\u5f80\u5f80\u4f1a\u6709 \u7740\u975e\u5e38\u51b7\u9759\u800c\u6e05\u6670\u7684\u5934\u8111\uff1b\u4f46\u662f\uff0c\u5f53\u4e00\u5207\u4e8b\u90fd\u9759\u4e0b\u6765\u7684\u65f6\u5019\uff0c\u4f60\u7684\u601d\u7eea\u5374\u5f80\u5f80\u4f1a\u6df7\u4e71\u8d77\u6765\u3002 \u3000\u3000\u4ed6\u6697\u81ea\u82e6\u53f9\u4e00\u58f0\uff0c\u65b9\u81ea\u5408\u4e0a\u773c\u5e18\uff0c\u60f3\u5b89\u9759\u5730\u6b47\u606f\u4e00\u9635\u3002 \u3000\u3000\u54ea\u77e5\u2014\u2014 \u3000\u3000\u5c31\u5728\u8fd9\u4e00\u5239\u90a3\u91cc\uff0c\u7a97\u53e3\u53c8\u6f2b\u65e0\u58f0\u606f\u5730\u63a0\u5165\u4e00\u6761\u4eba\u5f71\uff0c\u8fd9\u4eba\u5f71\u8eab\u52bf\u4e4b\u5feb\uff0c\u6709\u5982\u95ea\u7535\uff0c\u8eab\u5f62 \u843d\u4e0b\uff0c\u811a\u5c16\u5728\u5730\u9762\u4e0a\u53ea\u8f7b\u8f7b\u4e00\u70b9\uff0c\u4fbf\u5df2\u843d\u5230\u5e8a\u524d\uff0c\u53cc\u624b\u7a81\u5730\u4f38\u51fa\uff0c\u5f80\u5c55\u767d\u7684\u8eab\u4e0a\u62cd\u53bb\u3002 \u3000\u3000\u5c55\u767d\u773c\u5e18\u5408\u7740\uff0c\u6839\u672c\u4e0d\u77e5\u9053\u6709\u4eba\u63a0\u5165\u5c4b\u6765\uff0c\u6b64\u523b\u53ea\u542c\u5f97\u5e8a\u524d\u6709\u4e9b\u5fae\u5f02\u58f0\u54cd\u52a8\uff0c\u4ed6\u4e0b\u610f\u8bc6 \u5730\u5f20\u5f00\u773c\u6765\uff0c\u76ee\u5149\u52a8\u5904\uff0c\u4e0d\u7981\u8131\u53e3\u9053\uff1a\u201c\u96f7\u5927\u53d4\uff01\u4f60\u2014\u2014\u201d\u7a81\u5730\u77a5\u89c1\u201c\u96f7\u5927\u53d4\u201d\u9762\u4e0a\u4e00\u7247\u72de \u6076\u4e4b\u6001\uff0c\u53cc\u624b\u524d\u4f38\uff0c\u4f3c\u4e4e\u8981\u62e9\u4eba\u800c\u566c\uff0c\u4ed6\u5fc3\u4e2d\u4e0d\u9ece\u4e3a\u4e4b\u4e00\u5bd2\uff0c\u4e0b\u9762\u7684\u8bdd\uff0c\u4fbf\u518d\u4e5f\u8bf4\u4e0d\u51fa\u6765 \u4e86\u3002 \u3000\u3000\u539f\u6765\u8fd9\u6761\u63a0\u7a97\u800c\u4eba\u7684\u4eba\u5f71\uff0c\u6b63\u662f\u65b9\u624d\u7a81\u7136\u79bb\u53bb\u7684\u201c\u96f7\u5927\u53d4\u201d\u3002 \u3000\u3000\u4ed6\u65b9\u81ea\u4f38\u51fa\u53cc\u624b\uff0c\u5f80\u5e8a\u4e0a\u7684\u5c55\u767d\u62cd\u53bb\uff0c\u542c\u89c1\u5c55\u81ea\u7684\u8fd9\u4e00\u58f0\u547c\u58f0\uff0c\u4f3c\u4e4e\u5446\u4e86\u4e00\u5446\uff0c\u624b\u6500\u64e6 \u7136\u505c\u4f4f\uff0c\u4e24\u4eba\u76ee\u5149\u76f8\u9047\uff0c\u201c\u96f7\u5927\u53d4\u201d\u9762\u4e0a\u7684\u72de\u6076\u4e4b\u6001\u7a81\u7136\u6d88\u53bb\uff0c\u4e00\u4e1d\u7b11\u5bb9\uff0c\u7f13\u7f13\u81ea\u773c\u89d2\u6cdb \u8d77\u3002 \u3000\u3000\u4ed6\u5446\u5446\u5730\u671b\u4e86\u5c55\u767d\u4e24\u773c\uff0c\u7a81\u5730\u4e00\u628a\u62ac\u8d77\u5c55\u767d\uff0c\u8eab\u5f62\u731b\u5730\u4e00\u65cb\uff0c\u811a\u5c11\u5fae\u70b9\uff0c\u4fbf\u53c8\u95ea\u7535\u822c\u81ea \u7a97\u4e2d\u63a0\u4e86\u51fa\u53bb\u3002 \u3000\u3000\uff0c\u5c55\u767d\u5927\u60ca\u4e4b\u4e0b\uff0c\u8131\u53e3\u60ca\u547c\u4e00\u58f0\uff0c\u547c\u58f0\u672a\u6b47\uff0c\u4ed6\u5df2\u88ab\u8fd9\u4f3c\u75af\u975e\u75af\u3001\u884c\u4e8b\u5374\u4ef6\u4ef6\u8d85\u4e8e\u5e38\u60c5 \u5e38\u7406\u4e4b\u5916\u7684\u602a\u4eba\u201c\u96f7\u5927\u53d4\u201d\u631f\u5230\u56ed\u4e2d\uff0c\u4ed6\u5fc3\u60f3\u6323\u624e\uff0c\u4f46\u5468\u8eab\u65e0\u529b\uff0c\u53c8\u60f3\u95ee\u95ee\u8fd9\u201c\u96f7\u5927\u53d4\u201d\u5982 \u6b64\u5bf9\u5f85\u81ea\u5df1\uff0c\u7a76\u7ade\u662f\u4e3a\u4ec0\u4e48\uff0c\u4f46\u8f6c\u5ff5\u4e00\u60f3\uff0c\u6b64\u4eba\u884c\u4e8b\u65e2\u662f\u4ef6\u4ef6\u4e0d\u8fd1\u60c5\u7406\uff0c\u5c31\u7b97\u95ee\u4ed6\uff0c\u53ea\u6015\u4e5f \u662f\u65e0\u7528\u3002 \u3000\u3000\u201c\u96f7\u5927\u53d4\u201d\u8eab\u5f62\u4e00\u843d\u7a97\u5916\uff0c\u5fae\u4e00\u70b9\u8db3\uff0c\u4fbf\u659c\u659c\u5f80\u53f3\u8dc3\u53bb\uff0c\u5c31\u5728\u4ed6\u8fd9\u5fae\u4e00\u70b9\u8db3\u95f4\uff0c\u5c55\u767d\u52c9 \u529b\u4f38\u8d77\u5934\u3002 \u3000\u3000\u76ee\u5149\u671b\u4e0b\u6253\u91cf\u4e00\u4e0b\uff0c\u53ea\u89c1\u8fd9\u5ead\u56ed\u4e4b\u4e2d\uff0c\u6797\u6728\u8471\u90c1\uff0c\u5982\u82b1\u5982\u9526\uff0c\u867d\u7136\u5904\u5904\u5747\u6709\u4ead\u53f0\u697c\u9601\uff0c \u4f46\u5374\u88ab\u56db\u4e0b\u7684\u5047\u5c71\u6e56\u77f3\u65bd\u53bb\u5927\u534a\uff0c\u4e5f\u5c31\u770b\u4e0d\u5341\u5206\u6e05\u695a\uff0c\u4e00\u773c\u671b\u53bb\uff0c\u4f46\u89c9\u8fd9\u5ead\u56ed\u4e4b\u6df1\u6c89\u5e7f\u9614\uff0c \u7adf\u662f\u81ea\u5df1\u751f\u5e73\u672b\u89c1\u3002 \u3000\u3000\u4ed6\u4e0d\u7981\u4e3a\u4e4b\u6697\u4e2d\u60ca\u8d5e\uff0c\u65b9\u60f3\u518d\u4ed4\u7ec6\u770b\u4e0a\u4e00\u773c\uff0c\u4f46\u201c\u96f7\u5927\u53d4\u201d\u8eab\u5f62\u53c8\u8d77\uff0c\u4f7c\u7136\u51e0\u4e2a\u8d77\u843d\uff0c \u5c55\u767d\u53ea\u89c9\u56db\u4e0b\u7684\u6811\u6728\u4ead\u53f0\u5c71\u77f3\uff0c\u50cf\u98ce\u4e00\u6837\u5730\u5012\u9000\u56de\u53bb\uff0c\u670d\u4e2d\u53ea\u80fd\u89c1\u5230\u8fd9\u4e9b\u6797\u6728\u4ead\u53f0\u5c71\u77f3\u7684\u4e00 \u70b9\u5f71\u5b50\uff0c\u8fd9\u201c\u96f7\u5927\u53d4\u201d\u8eab\u5f62\u4e4b\u5feb\uff0c\u7684\u786e\u662f\u60ca\u4eba\u65e0\u6bd4\u3002 \u3000\u3000\u77ac\u606f\u4e4b\u95f4\u4eff\u4f5b\u63a0\u81f3\u4e00\u9053\u957f\u5eca\uff0c\u201c\u96f7\u5927\u53d4\u201d\u8eab\u5f62\u4fbf\u4ece\u8fd9\u957f\u5eca\u4e0b\u7a7f\u8fc7\uff0c\u957f\u5eca\u5c3d\u5934\uff0c\u7adf\u662f\u4e00\u5ea7 \u5c0f\u5c71\uff0c\u8fd9\u5c0f\u5c71\u4f3c\u771f\u4f3c\u5047\uff0c\u867d\u7136\u50cf\u5047\u5c71\uff0c\u4f46\u5047\u5c71\u5374\u53c8\u4e0d\u4f1a\u5982\u6b64\u9ad8\u5dcd\uff0c\u82e5\u901a\u5b83\u662f\u771f\u5c71\uff0c\u4f46\u771f\u5c71\u5374 \u53c8\u4e0d\u4f1a\u5982\u6b64\u73b2\u6591\uff0c\u4e00\u6761\u4e0a\u5c71\u7684\u5761\u9053\uff0c\u4f9d\u5c71\u66f2\u6298\uff0c\u5c71\u4e0a\u6797\u6728\u68ee\u68ee\uff0c\u82cd\u82cd\u90c1\u90c1\uff0c\u66f4\u662f\u65b9\u624d\u5ead\u56ed\u4e2d \u6240\u89c1\u4e4b\u4e0a\u3002 \u3000\u3000\u4f46\u201c\u96f7\u5927\u53d4\u201d\u5374\u4e0d\u7531\u8fd9\u6761\u5c71\u9053\u64a9\u4e0a\uff0c\u8eab\u5f62\u4e00\u8f6c\uff0c\u7ade\u6251\u5411\u8fd9\u8471\u97f6\u7684\u5c71\u6797\u4e4b\u4e2d\uff0c\u8fd9\u4e00\u6765\u5c55\u767d \u5fc3\u4e2d\u66f4\u662f\u60ca\u60b8\u96be\u5b9a\uff0c\u56db\u4e0b\u7684\u6797\u6728\u6811\u5e72\uff0c\u90fd\u4f3c\u8981\u5411\u4ed6\u8eab\u4e0a\u8fce\u9762\u98de\u6765\uff0c\u4ed6\u53ea\u597d\u95ed\u4e0a\u773c\u775b\u3002 \u3000\u3000\u5fc3\u60f3\u65e0\u8bba\u8fd9\u201c\u96f7\u5927\u53d4\u201d\u8981\u5c06\u81ea\u5df1\u5e26\u5f80\u6cb3\u6b7b\uff0c\u81ea\u5df1\u90fd\u65e0\u529b\u53cd\u6297\uff0c\u53ea\u5f97\u542c\u5929\u7531\u547d\u4e86\u3002\u2014 \u2014\u2026\u2026 \u3000\u3000\u4ed6\u867d\u7136\u95ed\u4e0a\u773c\u775b\uff0c\u5374\u65e0\u6cd5\u95ed\u4e0a\u8033\u6735\uff0c\u53ea\u89c9\u5f97\u6ee1\u8033\u98ce\u58f0\u5982\u6f6e\u6c34\u62cd\u5cb8\uff0c\u547c\u547c\u4e0d\u7edd\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u65b9\u81ea\u8f6c\u5ff5\u4e4b\u95f4\uff0c\u8fd9\u6ee1\u8033\u7684\u98ce\u58f0\u53c8\u4e00\u9f50\u505c\u4f4f\uff0c\u5374\u542c\u5f97\u201c\u96f7\u5927\u53d4\u201d\u9053\uff1a\u201c\u5230\u4e86\u3002\u201d \u3000\u3000\u5c55\u767d\u7741\u5f00\u773c\u6765\uff0c\u53d1\u89c9\u81ea\u5df1\u6b64\u523b\u7adf\u662f\u7f6e\u8eab\u4e8e\u4e00\u95f4\u6d1e\u7a9f\u4e4b\u4e2d\uff0c\u661f\u5149\u4ece\u6d1e\u5916\u6620\u5165\uff0c\u53ea\u89c1\u8fd9\u6d1e\u4e2d \u867d\u7136\u5341\u5206\u5e7d\u9ed8\uff0c\u4f46\u77f3\u5e8a\u77f3\u51e0\uff0c\u5e03\u7f6e\u5f97\u5374\u6781\u4e3a\u4e95\u7136\u6709\u5e8f\uff0c\u800c\u4e14\u5341\u5206\u6d01\u51c0\u3002\u8fd9\u4e0d\u4f46\u4e0e\u201c\u96f7\u5927\u53d4\u201d \u7684\u5916\u8868\u4e0d\u76f8\u79f0\uff0c\u800c\u201c\u96f7\u5927\u53d4\u201d\u7ade\u4f1a\u5c06\u5c55\u81ea\u5e26\u5230\u8fd9\u79cd\u5730\u65b9\u6765\uff0c\u66f4\u5927\u5927\u51fa\u4e4e\u5c55\u767d\u7684\u610f\u6599\u4e4b\u5916\uff0c\u4ed6 \u4e0d\u7981\u6697\u4e2d\u601d\u4ed8\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\u5730\u65b9\uff1f\u4ed6\u5c06\u6211\u5e26\u5230\u8fd9\u91cc\u6765\uff0c\u5230\u5e95\u662f\u4e3a\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4f46\u201c\u96f7\u5927\u53d4\u201d\u8bf4\u4e86\u90a3\u53e5\u201c\u5230\u4e86\u201d\u4e4b\u540e\uff0c\u4fbf\u518d\u4e5f\u4e0d\u53d1\u4e00\u8a00\uff0c\u5c55\u767d\u5fc3\u91cc\u60f3\u95ee\uff0c\u4f46\u7ade\u8fd8\u662f\u6ca1\u6709 \u95ee\u51fa\u3002 \u3000\u3000\u53ea\u5f97\u4efb\u7531\u8fd9\u8be1\u5f02\u795e\u79d8\u7684\u602a\u4eba\u5c06\u4ed6\u653e\u5230\u90a3\u5f20\u77f3\u5e8a\u4e4b\u4e0a\uff0c\u65e0\u53ef\u5948\u4f55\u5730\u6697\u53f9\u4e00\u58f0\uff0c\u518d\u6b21\u9602\u4e0a\u773c \u5e18\u3002\u4ed6\u60f3\uff1a\u65e0\u8bba\u4ec0\u4e48\u4e8b\uff0c\u8c1c\u5e95\u5374\u603b\u6709\u63ed\u89e3\u7684\u65f6\u5019\u3002 \u3000\u3000\u201c\u96f7\u5927\u53d4\u201d\u7acb\u5728\u5e8a\u524d\uff0c\u50cf\u662f\u53c8\u7279\u5c55\u767d\u4ed4\u7ec6\u5730\u770b\u4e86\u4e24\u773c\uff0c\u7a81\u53c8\u75be\u4f38\u53cc\u624b\uff0c\u5f80\u5c55\u767d\u8eab\u4e0a\u62cd\u4e0b \u2014\u2014\u5c55\u767d\u8fd9\u6b21\u5374\u6ca1\u6709\u5f20\u5f00\u773c\u6765\uff0c\u4ed6\u53ea\u89c9\u201c\u7830\u201d\u7136\u4e24\u638c\uff0c\u62cd\u5728\u81ea\u5df1\u80f8\u524d\u3001\u8170\u7554\uff0c\u4f3c\u662f\u75db\u6781\uff0c\u53c8 \u4f3c\u662f\u9178\u6781\u3002 \u3000\u3000\u4ed6\u5927\u53eb\u4e00\u58f0\uff0c\u5f20\u5f00\u773c\u6765\uff0c\u6a21\u7cca\u4e2d\u53ea\u89c1\u5230\u201c\u96f7\u5927\u53d4\u201d\u4e11\u602a\u7684\u9762\u5bb9\uff0c\u548c\u6d1e\u5916\u7684\u4e00\u7ebf\u5929\u5149\u3002 \u3000\u3000\u63a5\u7740\uff0c\u4ed6\u4fbf\u832b\u7136\u5931\u53bb\u77e5\u89c9\uff0c\u4e16\u95f4\u7eb5\u6709\u5343\u4e07\u4ef6\u4e8b\u53d1\u751f\uff0c\u4ed6\u90fd\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u8fd9\u5176\u95f4\uff0c\u4e16\u4e0a\u662f\u5426\u6709\u4e8b\u53d1\u751f\u5462\uff1f \u3000\u3000\u201c\u5b89\u4e50\u516c\u5b50\u201d\u4e91\u94ee\uff0c\u4ee5\u53ca\u201c\u9b54\u4e91\u795e\u624b\u201d\u5411\u51b2\u5929\uff0c\u662f\u5426\u8ffd\u5f97\u4e0a\u90a3\u7a81\u7136\u73b0\u8eab\u3001\u81ea\u4e91\u94ee\u624b\u4e0a\u593a \u53bb\u7c4d\u5251\u7684\u795e\u79d8\u4eba\u5f71\u5462\uff1f \u3000\u3000\u8fd9\u795e\u79d8\u4eba\u5f71\u662f\u8c01\uff1f\u4e3a\u4ec0\u4e48\u7518\u5192\u5927\u9669\uff0c\u81ea\u6b66\u6797\u4e2d\u8d6b\u64bc\u6709\u540d\u3001\u5a01\u9547\u4e00\u65b9\u7684\u201c\u5b89\u4e50\u516c\u5b50\u201d\u624b \u4e2d\uff0c\u593a\u53bb\u8fd9\u67c4\u201c\u65e0\u60c5\u78a7\u5251\u201d\u5462\uff1f \u3000\u3000\u8fd8\u6709\uff0c\u8fd9\u795e\u79d8\u6df1\u6c89\u7684\u5ead\u56ed\u4e2d\u7684\u5144\u5f1f\u59d0\u59b9\uff0c\u662f\u5426\u4f1a\u56e0\u4ed6\u5931\u8e2a\u800c\u53c8\u751f\u51fa\u8bb8\u591a\u4e8b\u7aef\uff1f \u3000\u3000\u8fd9\u4e00\u5207\uff0c\u5c55\u767d\u90fd\u65e0\u6cd5\u77e5\u9053\u3002 \u3000\u3000\u4ed6\u9192\u8f6c\u6765\u7684\u65f6\u5019\uff0c\u6d1e\u7a9f\u4e2d\u4ecd\u7136\u662f\u4e00\u7247\u6f06\u9ed1\uff0c\u751a\u81f3\u6bd4\u4ed6\u6765\u65f6\u66f4\u9edd\u9ed1\u4e86\u3002 \u3000\u3000\u4ed6\u7f13\u7f13\u7741\u5f00\u773c\u775b\uff0c\u4f46\u5374\u50cf\u662f\u6ca1\u6709\u7741\u5f00\u65f6\u4e00\u6837\uff0c\u56e0\u4e3a\u4ed6\u867d\u7136\u7741\u5f00\u773c\u6765\uff0c\u5374\u4ecd\u7136\u662f\u4ec0\u4e48\u4e5f\u770b \u4e0d\u89c1\uff0c\u4e3a\u4ec0\u4e48\uff1f\u96be\u9053\u6b64\u523b\u4ecd\u7136\u662f\u6df1\u591c\uff1f \u3000\u3000\u4f46\u6df1\u591c\u4e4b\u4e2d\uff0c\u4e5f\u8be5\u6709\u4e00\u4e9b\u9eef\u6de1\u7684\u5149\u7ebf\u5440\uff01 \u3000\u3000\u4e8e\u662f\u4ed6\u4fbf\u60f3\u6323\u624e\u7740\u5750\u8d77\u6765\uff0c\u54ea\u77e5\u4ed6\u8eab\u8eaf\u4e00\u52a8\uff0c\u4fbf\u5df2\u8f7b\u7075\u800c\u4e0d\u8d39\u4e8b\u5730\u5750\u4e86\u8d77\u6765\uff0c\u4ee5\u524d\u7684\u75c5 \u75db\u4e0e\u75b2\u60eb\u65e0\u529b\uff0c\u6b64\u523b\u7adf\u4f1a\u6d88\u5931\u65e0\u5f71\u3002 \u3000\u3000\u4ed6\u60ca\u547c\uff0c\u51e0\u4e4e\u4e0d\u76f8\u4fe1\u8fd9\u662f\u4e8b\u5b9e\u3002\u81ea\u5e7c\u4ee5\u957f\uff0c\u4ed6\u4e5f\u66fe\u53d7\u8fc7\u4e0d\u5c11\u6b21\u75c5\u9b54\u548c\u6298\u78e8\uff0c\u4f46\u5374\u4ece\u672a\u6709 \u4e00\u6b21\uff0c\u75c5\u75db\u7684\u6d88\u5931\uff0c\u7adf\u6709\u5982\u6b64\u4e4b\u5feb\u7684\u3002 \u3000\u3000\u4ed6\u65cb\u8eab\u4e0b\u4e86\u5e8a\uff0c\u56db\u4e0b\u4ecd\u662f\u6697\u4e0d\u89c1\u7269\uff0c\u4ed6\u8fdf\u7591\u7740\uff0c\u558a\u4e86\u4e00\u58f0\uff1a\u201c\u96f7\u5927\u53d4\uff01\u201d \u3000\u3000\u56db\u4e0b\u5bc2\u65e0\u5e94\u58f0\uff0c\u8fd9\u8be1\u5f02\u795e\u79d8\u7684\u201c\u96f7\u5927\u53d4\uff3c\u6b64\u523b\u4e5f\u4e0d\u77e5\u9053\u8d70\u5230\u54ea\u91cc\u53bb\u4e86\u3002\u5982\u6b64\u9ed1\u6697\u4e2d\uff0c\u4ed6 \u867d\u7136\u7ad9\u4e86\u8d77\u6765\uff0c\u5374\u4e0d\u6562\u968f\u610f\u79fb\u52a8\u811a\u6b65\uff0c\u7565\u4e00\u5c55\u52a8\u624b\u811a\uff0c\u5404\u5904\u5374\u8f7b\u7075\u5982\u524d\uff0c\u751a\u81f3\u6bd4\u5f80\u6614\u66f4\u8f7b\u7075 \u4e86\u4e9b\u3002\u4ed6\u5446\u5446\u5730\u7ad9\u5728\u5e8a\u524d\uff0c\u4f46\u7ad9\u4e86\u8bb8\u4e45\uff0c\u7a81\u5730\u611f\u89c9\u5230\u6709\u4e9b\u5fae\u98ce\uff0c\u5439\u5230\u4ed6\u8eab\u4e0a\u3002\u4ed6\u5947\u602a\uff0c\u5728\u8fd9 \u6697\u65e0\u5929\u5149\u7684\u5730\u65b9\uff0c\u600e\u4f1a\u6709\u5fae\u98ce\u5439\u8fdb\u6765\u5462\uff1f\u4e8e\u662f\u4ed6\u6478\u7d22\u7740\uff0c\u5411\u5fae\u98ce\u5439\u6765\u7684\u65b9\u5411\uff0c\u7f13\u7f13\u6c60\u8d70\u4e86\u8fc7 \u53bb\uff0c\u4ed6\u53d1\u89c9\u81ea\u5df2\u5b9a\u5230\u4e00\u7247\u5c71\u77f3\u524d\uff0c\u800c\u5fae\u98ce\uff0c\u7ade\u5c31\u662f\u4ece\u8fd9\u5c71\u77f3\u4e0a\u6620\u4eba\u7684\u3002\u4ed6\u66f4\u5927\u60d1\u4e0d\u89e3\u4e86\uff1a \u201c\u5c71\u77f3\u4e4b\u4e0a\uff0c\u600e\u4f1a\u6709\u98ce\u5439\u8fdb\u6765\u5462\uff1f\u201d\u4ed6\u4f38\u51fa\u624b\u638c\uff0c\u7f13\u7f13\u6478\u7d22\u7740\uff0c\u4ed6\u53d1\u89c9\u8fd9\u7247\u5c0f\u77f3\u56db\u5468\u4e0a\u6709\u5341 \u6570\u4e2a\u9f99\u773c\u5927\u5c0f\u7684\u6d1e\uff0c\u5fae\u98ce\uff0c\u4fbf\u662f\u4ece\u8fd9\u5c0f\u6d1e\u4e2d\u5439\u5165\u7684\u3002 \u3000\u3000\u2018\u671f\u6709\u98ce\u6b27\u8fdb\u6765\u4e86\uff0c\u4e3a\u4ec0\u4e48\u5374\u6ca1\u6709\u5149\u7ebf\u4e00\u9f50\u900f\u5165\u5462\uff1f\u201c\u4ed6\u6697\u95ee\u7740\u81ea\u5df1\uff0c\u4e00\u9762\u5374\u4e5f\u4e3a\u81ea\u5df1 \u5bfb\u5f97\u4e86\u7b54\u6848\uff01\u201d\u60f3\u5fc5\u662f\u8fd9\u4e9b\u5c0f\u6d1e\u4e5f\u662f\u901a\u5411\u4e00\u4e2a\u9ed1\u6697\u7684\u5730\u65b9\uff0c\u4f46\u8fd9\u5730\u65b9\uff0c\u5374\u662f\u53ef\u4ee5\u900f\u4eba\u5929\u98ce \u7684\u3002\u201c\u4e8e\u662f\uff0c\u4ed6\u5bf9\u81ea\u5df1\u7f6e\u8eab\u4e4b\u5730\uff0c\u4fbf\u6709\u4e86\u4e9b\u77aa\u89e3\uff0c\u4f46\u9664\u6b64\u4e4b\u5916\uff0c\u4ed6\u8fd8\u662f\u4ec0\u4e48\u4e5f\u4e0d\u77e5\u9053\u3002\u4ed6\u95ed \u4e0a\u773c\u775b\uff0c\u826f\u4e45\uff0c\u518d\u5f20\u5f00\u6765\uff0c\u5e0c\u5180\u80fd\u770b\u5230\u4e00\u4e9b\u4e1c\u897f\uff0c\u4f46\u4f38\u624b\u5904\uff0c\u5374\u4ecd\u7136\u662f\u9ed1\u6697\u4e0d\u89c1\u4e94\u6307\u3002\u8fd9\u6d53 \u91cd\u7684\u9ed1\u6697\u4f7f\u5f97\u8fd9\u5730\u65b9\u867d\u6709\u5929\u98ce\uff0c\u7a7a\u6c14\u5374\u4ecd\u65e7\u4f7f\u5f97\u4ed6\u900f\u4e0d\u8fc7\u6c14\u6765\u3002\u4ed6\u4ec0\u4e48\u4e5f\u4e0d\u80fd\u505a\uff0c\u53ea\u6709\u5750\u4e0b \u6765\u601d\u7d22\uff0c\u4f46\u6b64\u65f6\u6b64\u5730\uff0c\u4ed6\u53c8\u600e\u80fd\u4e13\u5fc3\u601d\u7d22\u5462\uff1f\u77ed\u6682\u7684\u9ed1\u6697\u5df2\u80fd\u4f7f\u4eba\u53d1\u72c2\uff0c\u4f55\u51b5\u5982\u6b64\u6f2b\u957f\u7684\u9ed1 \u6697\u3002\u518d\u7ad9\u8d77\u6765\uff0c\u4ed6\u6697\u4e2d\u5206\u8fa8\u7740\u65b9\u624d\u81ea\u5df2\u5367\u5012\u65f6\uff0c\u6240\u89c1\u7684\u8fd9\u5ea7\u6d1e\u53e3\uff0c\u6478\u7d22\u7740\u8d70\u5230\u90a3\u91cc\uff0c\u4f38\u624b\u4e00 \u6478\u2014\u2014\u5440\uff01\u8fd9\u539f\u5148\u7684\u6d1e\u53e3\uff0c\u6b64\u523b\u7ade\u53d8\u6210\u4e86\u4e00\u7247\u77f3\u58c1\uff0c\u4ed6\u53d1\u72c2\u4e86\u4f3c\u7684\u5728\u8fd9\u7247\u5c71\u77f3\u4e0a\u4e0b\u5de6\u53f3\u90fd\u4ed4 \u7ec6\u6478\u4e86\u4e00\u904d\uff0c\u8fd9\u7247\u5c71\u77f3\uff0c\u5b8c\u6574\u5f97\u7adf\u6ca1\u6709\u88c2\u9699\u3002\u90a3\u4e48\uff0c\u65b9\u624d\u7684\u6d1e\u53e3\u5230\u54ea\u91cc\u53bb\u4e86\u5462\uff1f\u8fd9\u5c71\u7a9f\u82e5\u662f \u6ca1\u6709\u51fa\u53e3\uff0c\u90a3\u4e48\uff0c\u81ea\u5df1\u65b9\u624d\u53c8\u662f\u600e\u4e48\u8fdb\u6765\u7684\u5462\uff1f\u4ed6\u771f\u7684\u5b8c\u5168\u56f0\u60d1\u4e86\uff0c\u6cbf\u7740\u8fd9\u7247\u77f3\u58c1\uff0c\u4ed6\u5411\u53f3 \u8d70\u53bb\uff0c\u8f6c\u4e86\u4e2a\u62d0\u89d2\uff0c\u4f38\u624b\u5904\uff0c\u7a81\u5730\u89e6\u5230\u4e00\u5305\u9ebb\u888b\uff0c\u9ebb\u888b\u4e2d\u4e00\u7c7b\u7684\u98df\u7269\uff0c\u9ebb\u888b\u65c1\u4f3c\u4e4e\u8fd8\u54a8\u2014\u7f38 \u6e05\u6c34\uff0c\u4ed6\u4fef\u4e0b\u5934\uff0c\u95fb\u4e86\u95fb\uff0c\u8fd9\u7f38\u6e05\u6c34\u4f3c\u4e4e\u8fd8\u6563\u53d1\u7740\u4e00\u79cd\u9999\u6c14\uff0c\u4f3c\u662f\u9152\u9999\uff0c\u53c8\u4f3c\u662f\u83b1\u9999\u3002\u4ed6\u5fcd \u4e0d\u4f4f\u559d\u4e86\u4e00\u65e5\uff0c\u6c34\u7684\u6ecb\u6627\uff0c\u4e5f\u4f3c\u4e4e\u662f\u4e0d\u53ef\u5f62\u5bb9\u7684\u9999\u751c\uff0c\u9999\u751c\u4e2d\u53c8\u5e26\u5165\u4e9b\u82e6\u6da9\uff0c\u4e00\u751f\u4e4b\u4e2d\uff0c\u4ed6 \u7ade\u4ece\u672a\u559d\u8fc7\u7c7b\u4f3c\u8fd9\u6837\u7684\u201d\u6c34\u201c\uff0c\u4ed6\u53c8\u559d\u4e86\u4e00\u53e3\uff0c\u6e05\u51c9\u7684\u201d\u6c34\u201c\uff0c\u4f7f\u5f97\u4ed6\u7cbe\u795e\u9547\u5b9a\u4e0d\u5c11\u3002\u4e8e\u662f \u4ed6\u518d\u6478\u7d22\u7740\u8d70\u8fc7\u53bb\uff0c\u4e00\u5f20\u77f3\u51e0\uff0c\u4e24\u5f20\u77f3\u6905\uff0c\u77f3\u51e0\u4e0a\u7a7a\u65e0\u4e00\u7269\uff0c\u7a81\u7136\u6478\u5230\u8584\u8584\u7684\u4e00\u518c\u4e66\u7c4d\uff0c\u4ed6 \u5fcd\u4e0d\u4f4f\u5c06\u4e4b\u62ff\u5230\u624b\u4e0a\u3002\u4f46\u8f6c\u5ff5\u4e00\u60f3\uff0c\u8fd9\u79cd\u9ed1\u6697\u7684\u5730\u65b9\uff0c\u7eb5\u6709\u4e66\u7c4d\uff0c\u5374\u53c8\u6709\u4ec0\u4e48\u7528\u5462\uff1f\u518d\u8d70\u8fc7 \u53bb\uff0c\u53c8\u662f\u4e00\u4e2a\u8f6c\u89d2\uff0c\u8fc7\u53bb\u4fbf\u662f\u90a3\u7247\u5fae\u98ce\u6b27\u5165\u7684\u5c71\u58c1\uff0c\u7136\u540e\uff0c\u4ed6\u53c8\u56de\u5230\u77f3\u5e8a\u8fb9\uff0c\u4f3c\u662f\u4ed6\u5931\u671b \u4e86\uff0c\u4e5f\u8ff7\u60d1\u4e86\uff0c\u8fd9\u4e2a\u6d1e\u7a9f\u4e4b\u4e2d\uff0c\u7ade\u4f3c\u771f\u7684\u6ca1\u6709\u4e00\u4e2a\u50cf\u662f\u51fa\u53e3\u7684\u5730\u65b9\u3002\u5728\u5e8a\u4e0a\u4ed6\u4e0d\u77e5\u5750\u4e86\u591a \u4e45\uff0c\u53c8\u4e0d\u77e5\u7761\u4e86\u591a\u4e45\uff0c\u7ad9\u8d77\u6765\uff0c\u8d70\u5230\u6c34\u7f38\u8fb9\uff0c\u559d\u4e24\u53e3\u6c34\uff0c\u4ece\u9ebb\u888b\u53d6\u4e86\u4e00\u5757\u4e1c\u897f\u51fa\u6765\uff0c\u54ac\u4e86\u4e00 \u53e3\uff0c\u53c8\u662f\u5947\u602a\u7684\u6ecb\u6627\uff0c\u4ed6\u957f\u53f9\u9891\u9891\uff0c\u600e\u5730\u81ea\u5df1\u4e00\u751f\u4e2d\uff0c\u4f1a\u6709\u5982\u6b64\u5947\u5f02\u7684\u906d\u9047\u3002\u601d\u6f6e\u7d0a\u4e71\uff0c\u767e \u822c\u65e0\u804a\u3002\u4ed6\u6478\u7d22\u7740\u62ff\u8d77\u90a3\u672c\u4e66\uff0c\u8d70\u56de\u5e8a\u4fa7\uff0c\u4ed6\u591a\u4e48\u5e0c\u671b\u81ea\u5df1\u80fd\u5728\u8fd9\u65e0\u804a\u7684\u65f6\u5149\u4e2d\u6709\u6d88\u9063\u7684\u4e1c \u897f\uff0c\u53ef\u662f\u6ca1\u6709\u5149\u7ebf\uff0c\u53c8\u600e\u80fd\u770b\u4e66\u5462\uff1f\u4ed6\u65e0\u53ef\u5948\u4f55\u5730\u5c06\u4e66\u9875\u7ffb\u52a8\u7740\uff0c\u7a81\u5730\u53d1\u73b0\uff0c\u4e66\u4e0a\u7684\u5b57\u8ff9\uff0c \u7ade\u50cf\u5fae\u6709\u4e9b\u51f8\u51fa\u7684\u6837\u5b50\uff0c\u90a3\u60f3\u5fc5\u662f\u4e3a\u77f3\u523b\u65f6\u805a\u58a8\u8fc7\u591a\uff0c\u6216\u8005\u662f\u6284\u5199\u65f6\u805a\u58a8\u592a\u6d53\uff0c\u65e0\u8bba\u5982\u4f55\uff0c \u4ed6\u7684\u5fc3\uff0c\u5374\u72c2\u559c\u5730\u8df3\u52a8\u4e86\u4e00\u4e0b\uff0c\u56e0\u4e3a\uff0c\u5728\u8fd9\u65e0\u804a\u65f6\u5019\u91cc\uff0c\u4ed6\u603b\u7b97\u6709\u4e86\u53ef\u4ee5\u6d88\u9063\u7684\u4e1c\u897f\u3002\u4ece\u7b2c \u4e00\u4e2a\u5b87\u6478\u8d77\uff0c\u5440\uff0c\u4e0d\u80fd\u9605\u8bfb\uff0c\u800c\u53ea\u80fd\u5982\u6b64\u6478\u7d22\uff0c\u53ef\u7684\u786e\u662f\u4ef6\u82e6\u4e8b\uff0c\u4ed6\u5ffd\u7136\u6709\u4e86\u76f2\u4eba\u7684\u75db\u82e6\uff0c \u4e5f\u5f00\u59cb\u4f53\u4f1a\u5230\u76f2\u4eba\u7684\u75db\u82e6\u3002\u4e00\u7b14\u4e00\u5212\uff0c\u4e00\u6487\u4e00\u6a2a\uff0c\u4ed6\u53f9\u7740\u6c14\uff0c\u6478\u7d22\u7740\uff0c\u7ec8\u4e8e\uff0c\u4ed6\u8131\u53e3\u547c\u9053\uff1a \u201c\u6c14\uff01I\u201d\u7b2c\u4e00\u4e2a\u5b57\uff0c\u662f\u201c\u6c14\u201d\u5b57\uff0c\u90a3\u4e48\u7b2c\u4e8c\u4e2a\u5b57\u5462\uff1f\u7ec8\u4e8e\uff0c\u4ed6\u4e5f\u6478\u4e86\u51fa\u6765\uff0c\u90a3\u662f\u4e2a \u201c\u6df7\u201d\u3002 \u3000\u3000\u6478\u51fa\u4e86\u4e24\u4e2a\u5b57\uff0c\u4ed6\u4fe1\u5fc3\u5927\u589e\uff0c\u4e0b\u9762\u7684\u5b87\uff0c\u4ed6\u4fbf\u66f4\u4ed4\u7ec6\u800c\u8010\u5fc3\u5730\u6478\u7740\uff0c\u4e8e\u662f\uff0c\u4ed6\u53c8\u6478\u51fa \u4e86\u3002 \u3000\u3000\u201c\u63a9\uff0c\u6e05\uff0c\u6d4a\u3002\u201d\u4e09\u4e2a\u5b57\u3002 \u3000\u3000\u7b2c\u516d\u4e2a\u5b57\u4ed6\u6478\u5f97\u6781\u5feb\uff0c\u56e0\u4e3a\u90a3\u53c8\u662f\u4e2a\u201c\u6e05\u201d\u5b87\uff0c\u7b2c\u4e03\u4e2a\u5b57\uff0c\u201c\u5347\u201d\uff0c\u7b2c\u516b\u4e2a\u5b57\uff0c\u53c8\u662f \u201c\u6df7\u201d\uff0c\u7b2c\u4e5d\u4e2a\u5b57\uff0c\u201c\u964d\u201d\uff0c\u7b2c\u5341\u4e2a\u5b87\uff0c\u201c\u9053\u201d\uff0c\u7b2c\u5341\u4e00\u4e2a\u4e2d\uff0c\u4ed6\u6478\u5f97\u6781\u5feb\uff0c\u56e0\u4e3a\u90a3\u662f\u4e2a \u201c\u4e00\u201d\u5b57\uff0c\u7b2c\u5341\u4e8c\u4e2a\u5b57\uff0c\u201c\u6cd5\u201d\uff0c\u7b2c\u5341\u4e09\u4e2a\u5b57\uff0c\u4ed6\u6478\u4e86\u66f4\u4e45\uff0c\u624d\u6478\u51fa\u662f\u4e2a\u201c\u4f17\u201d\u5b57\u3002 \u3000\u3000\u9605\u8bfb\u5341\u4e09\u4e2a\u5b57\uff0c\u90a3\u51e0\u4e4e\u5728\u671b\u773c\u4e4b\u95f4\u4fbf\u53ef\u5b8c\u6210\uff0c\u53ef\u662f\u8981\u6478\u51fa\u5341\u4e09\u4e2a\u5b57\uff0c\u5374\u7684\u786e\u662f\u4ef6\u56f0\u96be\u7684 \u4e8b\u3002\u4ed6\u6b47\u4e86\u53e3\u6c14\uff0c\u4f38\u4e86\u4f38\u624b\uff0c\u624b\u6307\u5374\u50cf\u662f\u6709\u4e9b\u9ebb\u6728\u4e86\uff0c\u65f6\u95f4\u66f4\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u4ed6\u5c06\u8fd9\u5341\u4e09\u4e2a \u5b57\u4f4e\u5ff5\u4e00\u904d\u3002 \u3000\u3000\u201c\u6c14\u6df7\u63a9\u6e05\u6d4a\u6e05\u5347\u6df7\u964d\u9053\u4e00\u6cd5\u4f17\u2026\u3002\uff0f\u4e8e\u662f\uff0c\u4ed6\u832b\u7136\u4e86\uff0c\u8fd9\u5341\u4e09\u4e2a\u5b57\u662f\u4ec0\u4e48\u610f\u601d\uff0c\u4ed6\u65e0 \u6cd5\u4e86\u89e3\uff0c\u53ea\u5f97\u96c6\u4e2d\u601d\u7d22\uff0c\u53c8\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u4ed6\u6697\u4e2d\u601d\u7d22\u7740\u9053\uff1a\u201c\u6c14\uff0c\u5927\u6982\u662f\u8bf4\u771f\u6c14\u4e00\u7c7b\u7684\u6c14 \u7684\uff0c\u662f\u6df7\u63a9\u7684\uff0c\u6e05\u6d4a\u4e0d\u95ee\uff0c\u8981\u60f3\u6e05\u6c14\u5347\uff0c\u6d4a\u6c14\u964d\uff0c\u9053\u7406\u53ea\u6709\u4e00\u4e2a\uff0c\u4f46\u662f\u65b9\u6cd5\u5374\u6709\u8bb8\u591a\u2014\u2014\u201d \u201c\u5440\uff01\u8fd9\u5341\u4e09\u4e2a\u5b57\uff0c\u662f\u4e0d\u662f\u8fd9\u6837\u7684\u610f\u601d\uff1f\u201d \u3000\u3000\u4ed6\u53ea\u80fd\u731c\u6d4b\uff0c\u5374\u4e0d\u77e5\u9053\u81ea\u5df1\u7684\u731c\u6d4b\u662f\u5426\u6b63\u786e\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u8d77\u6765\u559d\u4e86\u4e24\u53e3\u6c34\uff0c\u53c8\u5403\u4e86\u4e9b\u4e1c\u897f\uff0c\u4fbf\u518d\u6478\u4e0b\u53bb\uff0c\u53ea\u89c9\u4e0b\u9762\u7684\u53e5\u5b50\uff0c\u8d8a\u6765\u8d8a\u7e41\u590d\u6df1 \u5965\uff0c\u4ed6\u6bcf\u6478\u4e00\u4e2a\u5b57\uff0c\u4fbf\u8981\u505c\u4e0b\u6765\u601d\u7d22\u8bb8\u4e45\uff0c\u5728\u6478\u4e0b\u4e00\u4e2a\u5b57\u7684\u65f6\u5019\uff0c\u4ed6\u5fc3\u91cc\u8fd8\u5728\u4e0d\u65ad\u5730\u601d\u7d22\u7740 \u4e0a\u4e00\u4e2a\u5b57\u7684\u610f\u4e49\uff0c\u8fd9\u6837\uff0c\u4ed6\u6478\u5f97\u4fbf\u66f4\u52a0\u6162\u4e86\u3002 \u3000\u3000\u65f6\u95f4\uff0c\u4fbf\u5728\u8fd9\u6478\u7d22\u7684\u82e6\u601d\u4e4b\u4e0b\u8fc7\u53bb\uff0c\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u5c11\u65f6\u5019\uff0c\u4ed6\u53ea\u77e5\u9053\u90a3\u4e00\u7f38\u9ad8\u8fbe\u4e94\u5c3a\u3001 \u7c97\u4e0d\u80fd\u5408\u62b1\u7684\u6c34\u7f38\u4e2d\u7684\u201c\u6c34\u201d\u5df2\u51e0\u8fd1\u559d\u5b8c\uff0c\u800c\u90a3\u9ebb\u888b\u4e2d\u7684\u201c\u98df\u7269\u201d\u4e5f\u4f3c\u5269\u65e0\u51e0\u4e86\u3002 \u3000\u3000\u4f46\u89c1\u4ed6\u6b64\u523b\u5374\u5e76\u6ca1\u6709\u6765\u4e3a\u8fd9\u4e9b\u751f\u6d3b\u4e0a\u5fc5\u9700\u7684\u4e1c\u897f\u53d1\u6101\uff0c\u56e0\u4e3a\u8fd9\u672c\u8584\u8584\u7684\u4e66\u518c\u4e0a\u7684\u5b87\u8ff9\uff0c \u5df2\u5438\u5f15\u4e86\u4ed6\u5927\u90e8\u5206\u5fc3\u795e\u3002 \u3000\u3000\u4ed6\u518d\u4e5f\u60f3\u4e0d\u5230\u8fd9\u8584\u8584\u7684\u4e00\u672c\u4e66\u518c\u4e0a\uff0c\u6240\u8bb0\u8f7d\u7684\u4e1c\u897f\uff0c\u7adf\u662f\u6df1\u6e0a\u5982\u6ca7\u6d77\uff0c\u8fd9\u5176\u4e2d\u6bcf\u5b57\u90fd\u50cf \u662f\u6709\u7740\u4e00\u4e2a\u7279\u522b\u7684\u610f\u4e49\uff0c\u800c\u6bcf\u4e00\u4e2a\u610f\u4e49\u5374\u53c8\u90fd\u662f\u6b66\u5b66\u4e2d\u6781\u6df1\u5965\u7684\u7cbe\u5999\u4e4b\u5904\u3002 \u3000\u3000\u5c55\u767d\u5929\u6027\u672c\u6781\u597d\u6b66\uff0c\u53ea\u82e6\u4e8e\u672a\u9047\u660e\u5e08\uff0c\u6b64\u523b\u4ed6\u53d1\u89c9\u4e86\u8fd9\u79cd\u6b66\u5b66\u79d8\u7c07\uff0c\u600e\u4f1a\u4e0d\u6b22\u559c\u5982\u72c2\uff0c \u522b\u7684\u4e8b\uff0c\u4ed6\u4f7f\u4e00\u6982\u4e0d\u653e\u5728\u5fc3\u4e0a\u4e86\u3002 \u3000\u3000\u4ed6\u5bf9\u5b57\u8ff9\u7684\u6478\u7d22\uff0c\u867d\u7136\u8d8a\u6765\u8d8a\u89c9\u5bb9\u6613\uff0c\u4f46\u662f\u4e66\u4e2d\u7684\u5b57\u53e5\uff0c\u5374\u8d8a\u6765\u8d8a\u96be\u4ee5\u660e\u4e86\uff0c\u5f80\u5f80\u4e00\u4e2a \u5b57\u4ed6\u8981\u8be6\u601d\u8bb8\u4e45\uff0c\u800c\u4e14\u8981\u627f\u4e0a\u987e\u4e0b\uff0c\u518d\u5206\u8fa8\u54ea\u4e2a\u5b57\u76f8\u8fde\u662f\u4e00\u53e5\u8bdd\uff0c\u5230\u54ea\u91cc\u624d\u80fd\u6210\u4e00\u6bb5\u843d\uff0c\u56e0 \u4e4b\uff0c\u4ed6\u7684\u8fdb\u5c55\u53cd\u800c\u8d8a\u6765\u8d8a\u6162\u3002 \u3000\u3000\u4f46\u662f\u4efb\u4f55\u4e8b\u53ea\u8981\u6709\u4e86\u5f00\u59cb\uff0c\u4fbf\u4f1a\u6709\u7ed3\u675f\u7684\u4e00\u5929\uff0c\u4f55\u51b5\u4ed6\u662f\u5982\u6b64\u6709\u6052\u3002 \u3000\u3000\u7ec8\u4e8e\uff0c\u4e00\u5929\uff0c\u5f53\u4ed6\u5c06\u6700\u540e\u4e00\u4e2a\u5b57\u90fd\u8fa8\u6e05\u7684\u65f6\u5019\uff0c\u4ed6\u7684\u5fc3\uff0c\u4e0d\u7981\u4e3a\u4e4b\u72c2\u7a96\u5730\u8df3\u52a8\u8d77\u6765\uff0c\u4ed6 \u5367\u5728\u5e8a\u4e0a\uff0c\u4ed4\u7ec6\u5730\u518d\u5c06\u8fd9\u518c\u4e66\u4e0a\u7684\u6bcf\u4e00\u5b87\uff0c\u6bcf\u4e00\u53e5\u90fd\u4ed4\u7ec6\u5730\u601d\u7d22\u4e00\u904d\uff0c\u6b64\u523b\u4ed6\u5df2\u80fd\u9a86\u8fd9\u518c\u4e66 \u4e0a\u7684\u6bcf\u4e00\u4e2a\u5b87\u90fd\u6beb\u4e0d\u56f0\u96be\u5730\u80cc\u8bf5\u51fa\u6765\u3002 \u3000\u3000\u4ed6\u601d\u7d22\u5f97\u8d8a\u6df1\uff0c\u72c2\u559c\u7684\u5fc3\uff0c\u4fbf\u4e5f\u8df3\u52a8\u5f97\u66f4\u5389\u5bb3\uff0c\u56e0\u4e3a\u4ed6\u6bcf\u601d\u7d22\u4e00\u904d\uff0c\u4fbf\u53d1\u89c9\u8fd9\u5176\u4e2d\u7684\u6240 \u542b\u7684\u6b66\u5b66\u7cbe\u5999\uff0c\u7adf\u662f\u4ed6\u8fde\u505a\u68a6\u4e5f\u6ca1\u6709\u60f3\u5230\u8fc7\u7684\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u5f00\u59cb\u4f9d\u7740\u8fd9\u79d8\u63a5\u4e0a\u6240\u8bb0\u8f7d\u7684\u65b9\u6cd5\uff0c\u7ec3\u4e60\u8d77\u6765\u3002 \u3000\u3000\u6478\u7d22\u7740\u7684\u65e5\u5b50\u867d\u7136\u662f\u56f0\u82e6\u7684\uff0c\u4f46\u4e00\u5207\u56f0\u82e6\uff0c\u4ed6\u6b64\u523b\u90fd\u5df2\u5f97\u5230\u4e86\u62a5\u507f\uff0c\u56e0\u4e3a\u4ed6\u53d1\u89c9\u4f9d\u7740\u8fd9 \u672c\u4e66\u4e0a\u7684\u65b9\u6cd5\u6765\u4fee\u4e60\u5185\u529f\uff0c\u8fdb\u5883\u7adf\u662f\u65e0\u6bd4\u7684\u8fc5\u901f\uff0c\u8fd9\u548c\u4ee5\u524d\u4ed6\u82e6\u7ec3\u6b66\u529f\u7684\u65f6\u5019\uff0c\u5176\u96be\u6613\u4e4b \u522b\uff0c\u771f\u662f\u5224\u82e5\u9704\u58e4\u3002 \u3000\u3000\u4ed6\u4f11\u606f\u7684\u65f6\u5019\u8d8a\u6765\u8d8a\u5c11\uff0c\u56e0\u4e3a\u53c8\u53d1\u89c9\u81ea\u5df2\u7684\u7cbe\u795e\u6b64\u523b\u7adf\u662f\u5982\u6b64\u5145\u6c9b\uff0c\u4ed6\u518d\u4e5f\u4e0d\u51fb\u60f3\u522b\u7684 \u4e1c\u897f\uff0c\u56e0\u4e3a\u8fd9\u4e9b\u6b66\u5b66\u7684\u7cbe\u5999\uff0c\u5df2\u4f7f\u4ed6\u65e0\u6687\u65c1\u9a9b\u3002 \u3000\u3000\u54ea\u77e5\u2014\u2014 \u3000\u3000\u8fc7\u4e86\u4e0d\u77e5\u591a\u5c11\u9ed1\u6697\u7684\u65e5\u5b50\u4e4b\u540e\uff0c\u6709\u4e00\u5929\u4ed6\u76d8\u8198\u5750\u5728\u5e8a\u3002\u4ea1\uff0c\u7ee7\u7eed\u7740\u4ed6\u5185\u529f\u7684\u4fee\u4e60\u3002 \u3000\u3000\u5f53\u4ed6\u610f\u4e0e\u795e\u5408\u3001\u5fc3\u65e0\u6742\u5ff5\u7684\u65f6\u5019\uff0c\u4ed6\u53d1\u89c9\u4ed6\u8eab\u4e0b\u7684\u77f3\u5e8a\uff0c\u7adf\u7a81\u5730\u7f13\u7f13\u79fb\u52a8\u4e86\u8d77\u6765\u3002 \u3000\u3000\u4ed6\u5927\u60ca\u4e4b\u4e0b\uff0c\u731b\u63d0\u4e00\u53e3\u771f\u6c14\uff0c\u8eab\u8eaf\u4fbf\u53c8\u8f7b\u7075\u800c\u66fc\u5999\u5730\u8dc3\u5230\u5730\u4e0a\uff0c\u51dd\u795e\u6212\u5907\uff0c\u4ed6\u4e0d\u77e5\u9053\u5728 \u81ea\u5df2\u4e00\u751f\u4e4b\u4e2d\uff0c\u73b0\u5728\u53c8\u5c06\u901a\u7740\u4ec0\u4e48\u5947\u602a\u7684\u53d8\u5316\u3002 \u3000\u3000\u77f3\u5e8a\u4ecd\u5728\u7f13\u7f13\u79fb\u52a8\u7740\uff0c\u5c71\u58c1\u5916\u7a81\u5730\u4f20\u6765\u4e00\u9635\u6e05\u6717\u7684\u7b11\u58f0\uff0c\u8fd9\u7b11\u58f0\u7adf\u51b2\u8fc7\u5c71\u58c1\uff0c\u4f20\u4eba\u4ed6\u7684 \u8033\u91cc\uff0c\u4ed6\u7d27\u5f20\u5730\u671f\u5f85\u7740\uff0c\u8eab\u4e0a\u7684\u6bcf\u4e00\u6839\u795e\u7ecf\uff0c\u4f3c\u4e4e\u90fd\u5df2\u56fa\u7740\u8fd9\u7d27\u5f20\u7684\u671f\u5f85\u800c\u7ef7\u8d77\u5982\u5f13\u5f26\u4e86\u3002 \u3000\u3000\u201c\u8f70\u9686\uff01\u201d\u4e00\u58f0\u5de8\u54cd\uff0c\u4e00\u7ebf\u5929\u5149\u7834\u58c1\u800c\u5165\uff0c\u5728\u77f3\u5e8a\u540e\u8fb9\u7684\u77f3\u58c1\u4e0a\uff0c\u7ade\u73b0\u51fa\u4e00\u4e2a\u6570\u5c3a\u5927\u7684 \u6d1e\u6765\u3002 \u3000\u3000\u5c55\u767d\u5927\u5403\u4e00\u60ca\uff0c\u5fc3\u60f3\uff1a\u201c\u4ec0\u4e48\u529b\u91cf\u53ef\u4ee5\u628a\u8fd9\u6574\u5ea7\u77f3\u58c1\u9707\u5f00\uff1f\u2026\u2026\u201d \u3000\u3000\u4f46\uff0c\u5c55\u767d\u60ca\u8be7\u672a\u5b9a\uff0c\u7b11\u58f0\u9707\u8033\uff0c\u7834\u66f0\u4e4b\u5904\uff0c\u9661\u7136\u6d8c\u73b0\u4e00\u4e2a\u9880\u957f\u7684\u4eba\u5f71\uff01 \u3000\u3000\u9880\u957f\u4eba\u5f71\uff0c\u80cc\u5149\u800c\u7acb\uff0c\u5c55\u81ea\u89c6\u7ebf\u7a81\u7136\u7531\u6697\u5230\u660e\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u770b\u4e0d\u6e05\u6765\u4eba\u7684\u9762\u8c8c\uff0c\u53ea\u80fd\u770b \u5230\u90a3\u9880\u957f\u4eba\u5f71\uff0c\u6ee1\u5934\u4e71\u53d1\u98de\u84ec\uff0c\u957f\u8863\u5728\u5fae\u98ce\u4e2d\u98d8\u626c\uff0c\u4ed6\u5f53\u6d1e\u53e3\u800c\u7acb\uff01 \u3000\u3000\u9880\u957f\u4eba\u5f71\uff0c\u54c8\u54c8\u5927\u7b11\uff0c\u77f3\u58c1\u56de\u97f3\uff0c\u7b11\u58f0\u9707\u8033\uff0c\u55e1\u55e1\u4e0d\u7edd\uff01 \u3000\u3000\u9880\u957f\u4eba\u5f71\u8eab\u5f62\u4e00\u6643\uff0c\u8c08\u7136\u64a4\u8eab\u7ad9\u5728\u77f3\u5e8a\u4e0a\u3002 \u3000\u3000\u5c55\u767d\u518d\u51dd\u795e\u4e00\u770b\uff0c\u539f\u6765\u7adf\u662f\u90a3\u4e71\u53d1\u602a\u4eba\uff0c\u96f7\u5927\u53d4\uff01 \u7b2c\u4e5d\u7ae0\u3000\u6deb\u4e66\u5947\u529f>> \u53e4\u9f99\u300a\u5251\u5ba2\u884c\u300b \u7b2c\u4e5d\u7ae0\u3000\u6deb\u4e66\u5947\u529f \u3000\u3000\u5c55\u767d\u76ee\u5149\u60ca\u5947\u800c\u9519\u6115\u5730\u671b\u5728\u8fd9\u602a\u5f02\u8001\u4eba\u96f7\u5927\u53d4\u7684\u8eab\u4e0a\uff0c\u53ea\u89c1\u8fd9\u96f7\u5927\u53d4\u7b11\u58f0\u6e10\u655b\uff0c\u7f13\u7f13\u8de8 \u4e0b\u77f3\u5e8a\uff0c\u542b\u7b11\u9053\uff1a\u201c\u4f60\u5fc3\u91cc\u5728\u5947\u602a\uff0c\u6211\u600e\u5730\u4f1a\u5c06\u4f60\u5e26\u5230\u8fd9\u91cc\u6765\uff0c\u53c8\u4e3a\u4f55\u7a81\u7136\u8d70\u4e86\uff0c\u662f\u4e0d \u662f\uff1f\u201d \u3000\u3000\u5c55\u767d\u4e0d\u7981\u4e00\u695e\uff0c\u53ea\u5f97\u8f7b\u8f7b\u70b9\u4e86\u70b9\u5934\uff0c\u5374\u542c\u96f7\u5927\u53d4\u8bf4\u9053\uff1a\u201c\u4f60\u5fc3\u91cc\u8fd8\u5728\u5947\u602a\uff0c\u8fd9\u77f3\u6d1e\u83ab\u975e \u6709\u4ec0\u4e48\u53e4\u602a\uff0c\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u5c55\u767d\u53c8\u81ea\u4e00\u695e\uff0c\u6697\u9053\uff1a\u201c\u4ed6\u600e\u5730\u5b8c\u5168\u77e5\u9053\u6211\u7684\u5fc3\u4e8b\uff1f\u201d\u4ed6\u5374\u4e0d\u77e5\u9053\u6b64\u60c5\u6b64\u666f\uff0c\u65e0\u8bba\u662f\u4efb \u4f55\u4eba\u8bbe\u8eab\u5904\u5883\uff0c\u90fd\u4f1a\u6709\u8fd9\u6837\u7684\u731c\u7591\uff1b\u8fd9\u96f7\u5927\u53d4\u5c06\u5fc3\u6bd4\u5fc3\uff0c\u81ea\u7136\u4e00\u731c\u4fbf\u4e2d\u3002\u5c55\u81ea\u4e00\u695e\u4e4b\u540e\uff0c\u53ea \u5f97\u53c8\u70b9\u4e86\u70b9\u5934\uff0c\u5374\u89c1\u96f7\u5927\u53d4\u54c8\u54c8\u4e00\u7b11\uff0c\u597d\u6574\u4ee5\u6687\u5730\u5728\u5e8a\u8fb9\u5750\u4e86\u4e0b\u6765\uff0c\u9053\uff1a\u201c\u90a3\u7b2c\u4e00\u4ef6\u4e8b\u4f60\u81ea \u7136\u4e0d\u4f1a\u731c\u5230\uff0c\u81f3\u4e8e\u90a3\u7b2c\u4e8c\u4ef6\u4e8b\u561b\u2014\u2014\u201d\u4ed6\u8bed\u58f0\u5fae\u987f\uff0c\u7f13\u7f13\u4f38\u51fa\u624b\u638c\uff0c\u56db\u4e0b\u4e00\u6307\uff0c\u63a5\u7740\u53c8\u9053\uff1a \u201c\u4f60\u4e14\u770b\u770b\uff0c\u8fd9\u5c71\u6d1e\u539f\u672c\u4e00\u65e0\u5de7\u5999\uff0c\u53ea\u4e0d\u8fc7\u6211\u5728\u4f60\u719f\u7761\u4e4b\u9645\uff0c\u5c06\u77f3\u5e8a\u77f3\u684c\u7684\u4f4d\u7f6e\u79fb\u52a8\u4e86\u4e2a\u65b9 \u5411\uff0c\u7136\u540e\u518d\u7528\u5757\u5de8\u77f3\u5835\u4f4f\u6d1e\u53e3\uff0c\u4f60\u5728\u9ed1\u6697\u4e4b\u4e2d\uff0c\u53ea\u5f53\u662f\u6d1e\u53e3\u8fd8\u5728\u77f3\u5e8a\u524d\u9762\uff0c\u5374\u4e0d\u77e5\u2014\u2014\u54c8 \u54c8\u3002\u201d\u4ed6\u4f38\u624b\u4e00\u6307\u77f3\u5e8a\u8fb9\u4ed6\u65b9\u624d\u7a81\u7136\u73b0\u8eab\u7684\u6d1e\u53e3\uff0c\u5927\u7b11\u4e24\u58f0\uff0c\u6781\u4e3a\u5f97\u610f\u5730\u63a5\u9053\uff1a\u7559\u5b9a\u6d1e\u53e3\u53ea \u662f\u5728\u8fd9\u77f3\u5e8a\u65c1\u8fb9\u800c\u5df2\u3002\u201c\u5c55\u81ea\u76ee\u5149\u52a8\u5904\uff0c\u53ea\u89c1\u4ed6\u65b9\u624d\u73b0\u8eab\u4e4b\u5904\uff0c\u5929\u5149\u76f4\u5c04\u800c\u4eba\uff0c\u4e00\u5757\u5de8\u77f3\uff0c \u5df2\u88ab\u79fb\u5230\u4e00\u8fb9\uff0c\u5fc3\u4e2d\u4e0d\u7981\u604d\u7136\uff0c\u6697\u53f9\u4e00\u58f0\uff1a\u201c\u6211\u600e\u5730\u7ade\u8fde\u8fd9\u9053\u7406\u90fd\u60f3\u4e0d\u51fa\u6765\uff1f\u201d\u5fc3\u601d\u2014\u8f6c\uff0c \u53c8\u8ba8\u9053\uff1a\u201c\u8fd9\u602a\u8001\u4eba\u6b64\u523b\u8bf4\u8d77\u8bdd\u6765\uff0c\u4e0d\u4f46\u8bed\u58f0\u6e05\u6670\uff0c\u800c\u4e14\u6709\u6761\u6709\u7406\uff0c\u54ea\u91cc\u8fd8\u6709\u534a\u5206\u4ed6\u5148\u524d\u90a3 \u766b\u72c2\u602a\u5f02\u4e4b\u6001\uff0c\u83ab\u975e\u4ed6\u4ee5\u524d\u53ea\u662f\u6545\u4f5c\u59ff\u6001\u800c\u5df2\uff0c\u53ea\u662f\u2014\u2014\u4ed6\u8fd9\u5374\u53c8\u662f\u4e3a\u7740\u4ec0\u4e48\u5462\uff1f\u201d\u4ed6\u5fc3\u4e2d \u4ecd\u7136\u5927\u611f\u4e0d\u89e3\uff0c\u4f46\u4e00\u65f6\u4e4b\u95f4\uff0c\u5374\u53c8\u4e0d\u77e5\u8be5\u5982\u4f55\u95ee\u51fa\u53e3\u6765\u3002 \u3000\u3000\u53ea\u89c1\u96f7\u5927\u53d4\u76ee\u5149\u4e00\u8f6c\uff0c\u7a81\u5730\u770b\u5230\u5c55\u81ea\u65f6\u523b\u6478\u7d22\u3001\u56e0\u800c\u4e00\u76f4\u653e\u5728\u684c\u8fb9\u7684\u90a3\u672c\u5185\u8f7d\u6b66\u529f\u5965\u5999 \u7684\u4e66\u518c\uff0c\u65b9\u81ea\u655b\u53bb\u5fae\u7b11\u7684\u9762\u5bb9\uff0c\u53c8\u81ea\u6cdb\u8d77\u4e00\u4e1d\u7b11\u610f\uff0c\u7f13\u7f13\u4f38\u624b\u62ff\u4e86\u8d77\u6765\u3002\u5c55\u767d\u76f4\u5230\u6b64\u523b\uff0c\u65b9 \u7b2c\u4e00\u773c\u89c1\u5230\u8fd9\u672c\u4ed6\u4e0d\u77e5\u6478\u7d22\u82e5\u5e72\u904d\u7684\u6b66\u529f\u79d8\u63a5\u7684\u6837\u5b50\uff0c\u53ea\u89c1\u8fd9\u518c\u8584\u8584\u7684\u79d8\u7b3c\uff0c\u5c01\u9762\u7adf\u7136\u5f69\u8272 \u6591\u70c2\uff0c\u4e00\u773c\u671b\u53bb\uff0c\u53ea\u89c9\u8272\u5f69\u593a\u76ee\u5df2\u6781\u3002 \u3000\u3000\u4ed6\u5148\u524d\u53ea\u5f53\u8fd9\u672c\u79d8\u7b56\uff0c\u5fc5\u5b9a\u662f\u6d45\u706b\u6de1\u9ec4\u4e00\u7c7b\u989c\u8272\uff0c\u6b64\u523b\u89c9\u5927\u51fa\u610f\u5916\uff0c\u4e0d\u7981\u4e3a\u4e4b\u4e00\u6115\uff0c\u7a81 \u5730\u60f3\u8d77\u4ed6\u5e7c\u65f6\u542c\u5230\u7684\u4e00\u4e2a\u201c\u778e\u5b50\u6478\u8c61\u201d\u7684\u6545\u4e8b\uff0c\u90a3\u662f\u5728\u4e00\u4e2a\u591c\u51c9\u5982\u6c34\u7684\u665a\u4e0a\uff0c\u4ed6\u90a3\u5df2\u56e0\u957f\u4e45 \u7684\u75db\u82e6\u6298\u78e8\u800c\u6b7b\u53bb\u7684\u6148\u6bcd\uff0c\u5728\u4e00\u76cf\u5b64\u706f\u8fb9\u5bf9\u4ed6\u8bf4\u7684\u3002 \u3000\u3000\u9ec4\u660f\u7684\u706f\u5149\uff0c\u6148\u6bcd\u7684\u9762\u5bb9\uff0c\u6b64\u523b\u4f3c\u4e4e\u53c8\u6cdb\u8d77\u5728\u4ed6\u773c\u524d\uff0c\u67d4\u548c\u7684\u8bed\u58f0\uff0c\u8c06\u8c06\u7684\u6559\u8bf2\uff0c\u6b64\u523b \u4e5f\u4f3c\u4e4e\u54cd\u8d77\u5728\u4ed6\u8033\u7554\uff0f\u4f60\u82e5\u6ca1\u6709\u4eb2\u773c\u89c1\u5230\uff0c\u5373\u4f7f\u90a3\u4e1c\u897f\u662f\u4f60\u4eb2\u624b\u6478\u89e6\u5230\u7684\uff0c\u4f60\u4e5f\u4e0d\u80fd\u66ff\u5b83\u5984 \u4e0b\u65ad\u8bed\uff0c\u4e0d\u7136\uff0c\u4f60\u4e5f\u5c31\u4f1a\u53d8\u6210\u548c\u90a3\u4e9b\u6478\u8c61\u7684\u778e\u5b50\u4e00\u6837\u611a\u7b28\u3002\u201c\u4ed6\u5df2\u6df1\u6df1\u5730\u4f53\u4f1a\u5230\u8fd9\u51e0\u53e5\u8bdd\u91cc \u6240\u5305\u542b\u7684\u6df1\u523b\u6559\u8bad\u3002\u4ed6\u4e5f\u5df2\u6df1\u6df1\u5730\u4e86\u89e3\u5230\u8fd9\u6559\u8bad\u4e2d\u6240\u5305\u542b\u7684\u7231\u5fc3\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u4ed6\u4e0d\u7981\u53c8\u56de\u5230 \u9065\u8fdc\u7684\u5f80\u4e8b\u4e2d\u53bb\uff0c\u7adf\u5fd8\u8bb0\u4e86\u4ed6\u6b64\u523b\u8eab\u5728\u4f55\u5904\uff01\u96f7\u5927\u53d4\u4e00\u9762\u7f13\u7f13\u7ffb\u52a8\u7740\u624b\u4e2d\u7684\u79d8\u63a5\uff0c\u4e00\u9762\u53c8\u7f13 \u7f13\u9053\uff1a\u201c\u8001\u592b\u5e26\u4f60\u5230\u8fd9\u91cc\u6765\uff0c\u5c31\u662f\u4e3a\u4e86\u8981\u4f60\u80fd\u8c08\u5230\u8fd9\u672c\u79d8\u7b56\uff0c\u8fd9\u4e9b\u65e5\u5b50\u6765\uff0c\u60f3\u5fc5\u4f60\u5df2\u8bfb\u8fc7 \u4e86\uff0c\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u65b9\u4ece\u5f80\u4e8b\u7684\u68a6\u4e2d\u9192\u6765\u7684\u5c55\u81ea\uff0c\u773c\u7736\u4e2d\u4f3c\u5df2\u6709\u6d45\u6d45\u7684\u6cea\u75d5\u3002 \u3000\u3000\u4ed6\u832b\u7136\u70b9\u4e86\u70b9\u5934\uff0c\u5374\u542c\u96f7\u5927\u53d4\u53c8\u9053\uff1a\u201c\u8001\u592b\u5c06\u4f60\u72ec\u81ea\u5173\u95ed\u5728\u8fd9\u6d1e\u7a9f\u4e4b\u4e2d\uff0c\u4e5f\u662f\u4e3a\u4e86\u8981\u4f60 \u80fd\u5728\u9ed1\u6697\u4e0e\u5b64\u72ec\u4e4b\u4e2d\uff0c\u4ed4\u7ec6\u7814\u8bfb\u8fd9\u4e66\u4e2d\u7684\u7cbe\u5999\uff0c\u4e0d\u77e5\u9053\u2026\u3002\u201d \u3000\u3000\u4ed6\u8bed\u58f0\u8d8a\u6765\u8d8a\u89c1\u90d1\u91cd\uff0c\u5c55\u767d\u542c\u4e86\u5fc3\u91cc\u5374\u4e0d\u7981\u6709\u6c14\uff0c\u6697\u5fd6\uff1a\u201c\u4f60\u8981\u6211\u4ed4\u7ec6\u7814\u8c08\u8fd9\u4e66\u4e2d\u7684\u7cbe \u5999\uff0c\u5374\u53c8\u7279\u6211\u5173\u5728\u4f38\u624b\u4e0d\u89c1\u6307\u7684\u9ed1\u6697\u4e4b\u4e2d\uff0c\u54fc\u2014\u2014\u8fd9\u662f\u4ec0\u4e48\u8bdd\uff01\u201d\u5fcd\u4e0d\u4f4f\u62a2\u53e3\u9053\uff1a\u201c\u8001\u524d\u8f88 \u5bf9\u665a\u8f88\u7684\u76db\u60c5\uff0c\u665a\u8f88\u5b9e\u5728\u662f\u611f\u6fc0\u5f97\u5f88\uff0c\u53ea\u4e0d\u8fc7\u665a\u8f88\u7684\u773c\u775b\u5e76\u6ca1\u6709\u4ec0\u4e48\u6bdb\u75c5\uff0c\u5728\u6709\u5149\u7684\u5730\u65b9\u4e00 \u6837\u4e5f\u80fd\u770b\u5f97\u89c1\u5b57\u8ff9\uff0c\u800c\u4e14\u770b\u5f97\u5341\u5206\u6e05\u695a\uff0c\u8001\u524d\u8f88\u52b3\u4ee5\u4e3a\u665a\u8f88\u53ea\u6709\u5728\u9ed1\u6697\u4e2d\u624d\u80fd\u89c1\u7269\uff0c\u90a3\u4e48\u2014 \u2014\u54fc\u54fc\u2014\u2014\u201d\u4ed6\u751f\u5177\u76f4\u80a0\uff0c\u6b64\u523b\u5fc3\u4e2d\u6709\u6c14\uff0c\u4fbf\u4e0d\u7ba1\u5bf9\u65b9\u662f\u8c01\uff0c\u4e5f\u8981\u75db\u75db\u5feb\u5feb\u5730\u8bf4\u51fa\u6765\uff0c\u81f3\u4e8e \u8bf4\u51fa\u6765\u7684\u540e\u679c\u5982\u4f55\uff0c\u4ed6\u5374\u6839\u672c\u672a\u66fe\u8003\u8651\uff0c\u8fd9\u5374\u4e5f\u6b63\u662f\u5c11\u5e74\u7537\u513f\u7684\u672c\u8272\u3002 \u3000\u3000\u54ea\u77e5\u8fd9\u96f7\u5927\u53d9\u9ed8\u9ed8\u5730\u542c\u7740\u4ed6\u7684\u8bdd\uff0c\u975e\u4f46\u4e1d\u6beb\u4e0d\u4ee5\u4e3a\u5fe4\uff0c\u9762\u4e0a\u53cd\u800c\u6cdb\u8d77\u4e00\u79cd\u6de1\u6de1\u7684\u7b11\u5bb9\uff0c \u76f4\u5230\u5c55\u767d\u8bdd\u8bf4\u5b8c\u4e86\uff0c\u4ed6\u9762\u4e0a\u7a81\u53c8\u63a0\u8d77\u4e00\u9635\u5947\u5f02\u7684\u8868\u60c5\uff0c\u8c61\u662f\u7a81\u7136\u60f3\u8d77\u6765\u4ec0\u4e48\uff0c\u7ade\u81ea\u957f\u53f9\u4e00 \u58f0\uff0c\u9053\uff0c\u201c\u5f53\u771f\u662f\u4e00\u6a21\u4e00\u6837\u7684\u813e\u6c14\uff0c\u5509\u2014\u2014\u201d\u957f\u53f9\u4e00\u58f0\uff0c\u8bed\u58f0\u7a81\u987f\uff0c\u5c55\u767d\u542c\u4e86\u4ed6\u8fd9\u6ca1\u5934\u6ca1\u8111 \u7684\u4e00\u53e5\u8bdd\uff0c\u5fc3\u4e2d\u65b9\u81ea\u4e00\u52a8\uff0c\u5374\u89c1\u4ed6\u7a81\u5730\u624b\u8155\u4e00\u626c\uff0c\u5c06\u624b\u4e2d\u90a3\u672c\u8272\u5f69\u6591\u70c2\u7684\u4e66\u518c\uff0c\u7b14\u76f4\u5730\u629b\u4e86 \u8fc7\u6765\uff0c\u53e3\u4e2d\u9053\uff1a\u201c\u5c11\u5e74\u4eba\u5fc3\u76f4\u53e3\u5feb\uff0c\u539f\u672c\u662f\u597d\u4e8b\uff0c\u4f46\u5bf9\u4eba\u5bf9\u4e8b\u5374\u4e0d\u53ef\u8f7b\u52a0\u5224\u65ad\uff0c\u77e5\u9053\u5417\uff1f\u201d \u3000\u3000\u5c55\u767d\u53c8\u4e3a\u4e4b\u4e00\u6115\uff0c\u5168\u7136\u4e0d\u77e5\u9053\u4ed6\u8bdd\u4e2d\u7684\u7528\u610f\uff0c\u76f4\u5230\u90a3\u672c\u5f69\u8272\u6591\u70c2\u7684\u4e66\u518c\u5df2\u5728\u6d1e\u5916\u5c04\u5165\u7684 \u5929\u5149\u7684\u6620\u662d\u4e4b\u4e0b\u5c55\u73b0\u5728\u4ed6\u773c\u524d\uff0c\u4ed6\u65b9\u81ea\u75be\u5fd9\u4f38\u51fa\u624b\u6765\uff0c\u63a5\u7740\u4e86\u5b83\uff0c\u53ea\u542c\u96f7\u5927\u53d4\u51b7\u51b7\u9053\uff1a\u201c\u6253 \u5f00\u770b\u770b\uff01\u201d \u3000\u3000\u5c55\u767d\u5fc3\u4e2d\u5927\u5947\u3002 \u3000\u3000\u201c\u96be\u9053\u8fd9\u672c\u4e66\u5728\u6709\u5149\u7684\u5730\u65b9\u5c31\u770b\u4e0d\u89c1\u4e86\u5417\uff1f\u201d\u4f46\u662f\u4ed6\u5374\u6e05\u695a\u5730\u8bb0\u5f97\uff0c\u4e66\u4e2d\u7684\u5b57\u8ff9\u662f\u6574\u9f50 \u5730\u6392\u5217\u7740\u7684\uff0c\u4e8e\u662f\u4ed6\u6697\u4e2d\u66ff\u81ea\u5df1\u65b9\u624d\u7684\u731c\u6d4b\u4e0b\u4e86\u4e2a\u575a\u51b3\u7684\u5426\u5b9a\uff0c\u4f38\u624b\u7ffb\u5f00\u8fd9\u672c\u4e66\u518c\u5b9a\u775b\u4e00\u770b \u2014\u2014\u4ed6\u5374\u53c8\u4e0d\u7981\u5446\u679c\u5730\u695e\u4f4f\u4e86\u3002 \u3000\u3000\u4ed6\u7684\u5fc3\uff0c\u4e5f\u4e3a\u4e4b\u6025\u901f\u5730\u8df3\u52a8\u4e86\u8d77\u6765\uff0c\u4ed6\u51e0\u4e4e\u60f3\u7acb\u523b\u5c06\u8fd9\u672c\u4e0a\u9762\u6ee1\u8f7d\u6b66\u529f\u5965\u79d8\u7684\u79d8\u7b3c\u6495 \u6bc1\u3002 \u3000\u3000\u4f46\u662f\u53e6\u4e00\u79cd\u6df7\u5408\u7740\u5f3a\u70c8\u7684\u597d\u5947\u4e0e\u539f\u59cb\u7684\u6b32\u671b\u7684\u51b2\u52a8\uff0c\u5374\u53c8\u4f7f\u5f97\u4ed6\u7684\u773c\u775b\u518d\u4e0d\u80fd\u79fb\u52a8\u4e00 \u4e0b\uff0c\u5239\u90a3\u4e4b\u95f4\uff0c\u4ed6\u53ea\u89c9\u76ee\u7729\u795e\u8ff7\uff0c\u5fc3\u8361\u610f\u6447\uff0c\u8eab\u5f62\u51e0\u4e4e\u7ad9\u7acb\u4e0d\u7a33\uff0c\u98a4\u6296\u7740\u9669\u51fa\u624b\u638c\uff0c\u518d\u53bb\u7ffb \u52a8\u7b2c\u4e09\u9875\u3002 \u3000\u3000\u54ea\u77e5\u2014\u2014 \u3000\u3000\u201c\u5427\u201d\u5730\u4e00\u58f0\uff0c\u4ed6\u9762\u988a\u4e4b\u4e0a\u7ade\u88ab\u96f7\u5927\u53d4\u91cd\u91cd\u62cd\u4e86\u4e00\u638c\uff0c\u624b\u8155\u5fae\u5c55\uff0c\u624b\u4e2d\u7684\u4e66\u518c\u4e5f\u88ab\u96f7\u5927 \u53d4\u5288\u624b\u593a\u4e86\u8fc7\u53bb\uff0c\u4ed6\u5fc3\u5934\u4e00\u9707\uff0c\u5fc3\u667a\u4e00\u6e05\uff0c\u60f3\u5230\u81ea\u5df2\u65b9\u624d\u7684\u6837\u5b50\uff0c\u4e0d\u7981\u4e3a\u4e4b\u7ea2\u751f\u53cc\u988a\u3002 \u3000\u3000\u539f\u6765\u4ed6\u4f38\u624b\u7ffb\u5f00\u90a3\u4e00\u9875\u5f69\u8272\u6591\u70c2\u7684\u5c01\u9762\uff0c\u76ee\u5149\u8f6c\u5904\uff0c\u5374\u89c1\u7b2c\u4e09\u9875\u4e2d\uff0c\u867d\u6709\u4e00\u884c\u884c\u6de1\u6de1\u7684 \u5b57\u8ff9\uff0c\u4f46\u6574\u9875\u4e4b\u4e0a\uff0c\u5374\u753b\u6ee1\u4e86\u8eab\u65e0\u5bf8\u7f15\u7684\u7edd\u8272\u7f8e\u5973\uff0c\u800c\u4e14\u4ea6\u662f\u4ee5\u6781\u4e3a\u9c9c\u8273\u7684\u8272\u5f69\u7ed8\u5c31\u3002 \u3000\u3000\u8fd9\u4e9b\u7f8e\u5973\u6216\u5750\u6216\u5367\uff0c\u7c89\u81c2\u96ea\u80a1\uff0c\u83b9\u83b9\u751f\u5149\uff0c\u4e0d\u4f46\u4f53\u6001\u59ff\u52bf\uff0c\u5404\u5c3d\u5176\u5999\uff0c\u753b\u5f97\u751f\u52a8\u65e0\u6bd4\uff0c \u800c\u4e14\u7709\u68a2\u773c\u89d2\uff0c\u9690\u542b\u6625\u610f\uff0c\u9762\u76ee\u4e4b\u95f4\uff0c\u66f4\u6ee1\u542b\u8361\u610f\uff0c\u6709\u7684\u662f\u4e4c\u53d1\u4e71\u6d12\uff0c\u80f8\u96ea\u6a2a\u8212\uff0c\u6709\u7684\u662f\u91d1 \u9488\u8f7b\u62c8\uff0c\u7ee3\u6a71\u659c\u5367\uff0c\u4fbf\u662f\u94c1\u77f3\u50bb\u5b50\u89c1\u4e86\uff0c\u4e5f\u65e0\u6cd5\u4e0d\u4e3a\u4e4b\u5fc3\u52a8\u3002\u90a3\u5c55\u767d\u867d\u7136\u5766\u8361\u6b63\u76f4\uff0c\u4f46\u7a76\u7adf \u662f\u8840\u8089\u4e4b\u8eaf\uff0c\u800c\u4e14\u8840\u6c14\u65b9\u521a\uff0c\u4e00\u751f\u4e4b\u4e2d\uff0c\u51e0\u66fe\u89c1\u8fc7\u8fd9\u79cd\u56fe\u4e66\uff0c\u66f4\u4f55\u51b5\u8fd9\u4e9b\u56fe\u4e66\u4e4b\u4e2d\uff0c\u8fd8\u4f3c\u9690 \u542b\u7740\u4e00\u79cd\u5947\u8be1\u7684\u9b45\u529b\u3002 \u3000\u3000\u6b64\u523b\u4ed6\u5b9a\u4e86\u5b9a\u795e\uff0c\u53ea\u89c9\u5f97\u5fc3\u5934\u4f3c\u4e4e\u8fd8\u5728\u7830\u7830\u8df3\u52a8\uff0c\u5374\u542c\u96f7\u5927\u53d4\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u9ed1\u6697\u4e4b \u4e2d\uff0c\u867d\u7136\u770b\u4e0d\u89c1\uff0c\u4f46\u5374\u6bd4\u770b\u5f97\u89c1\u8fd8\u8981\u597d\u4e9b\u5427\uff01\u201d \u3000\u3000\u5c55\u767d\u76ee\u5149\u4e00\u671b\uff0c\u5fc3\u4e2d\u5927\u611f\u60ed\u6127\uff0c\u54ea\u77e5\u96f7\u5927\u53d4\u5374\u53c8\u5fae\u5fae\u4e00\u7b11\uff0c\u4f38\u624b\u4e00\u62cd\u4ed6\u80a9\u5934\uff0c\u548c\u58f0\u9053\uff1a \u201c\u4e0d\u8fc7\u4f60\u5fc3\u91cc\u4e5f\u4e0d\u8981\u96be\u53d7\uff0c\u8fd9\u672c\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\uff0c\u81ea\u53e4\u81f3\u4eca\uff0c\u4e0d\u77e5\u846c\u9001\u4e86\u591a\u5c11\u82f1\u96c4\u8c6a\u6770 \u7684\u96c4\u5fc3\u58ee\u5fd7\uff0c\u4f60\u5e74\u7eaa\u8fd8\u8f7b\uff0c\u8fd9\u53c8\u7b97\u5f97\u4e86\u4ec0\u4e48\u3002\u201d\u8a00\u8bed\u4e4b\u4e2d\uff0c\u7adf\u6ee1\u542b\u5b89\u6170\u9f13\u52b1\u4e4b\u610f\u3002 \u3000\u3000\u5c55\u81ea\u5fc3\u4e2d\u4e0d\u7981\u5927\u4e3a\u611f\u6fc0\uff0c\u76ee\u5149\u4e00\u62ac\uff0c\u8bb7\u8bb7\u5730\u9053\uff1a\u201c\u8001\u524d\u8f88\u2026\u3002\uff0c\u665a\u8f88\u2026\u2026\u5e74\u8f7b\u8bc6\u6d45\uff0c\u8fd8 \u671b\u8001\u524d\u8f88\u4e0d\u8981\u602a\u7f6a\u3002\u201d \u3000\u3000\u8981\u77e5\u9053\u4ed6\u751f\u6027\u521a\u76f4\uff0c\u522b\u4eba\u82e5\u662f\u5bf9\u4ed6\u8f7b\u89c6\u6b3a\u51cc\uff0c\u8981\u4ed6\u4f4e\u5934\u8ba4\u7f6a\uff0c\u90a3\u662f\u4e07\u4e07\u4e0d\u80fd\uff0c\u4f46\u82e5\u662f\u522b \u4eba\u5bf9\u4ed6\u597d\u4e9b\uff0c\u4ed6\u5fc3\u4e2d\u6709\u6127\uff0c\u4fbf\u53c8\u5fcd\u4e0d\u4f4f\u4e0d\u8bf4\u51fa\u6765\u3002 \u3000\u3000\u96f7\u5927\u53d4\u5fae\u5fae\u4e00\u7b11\uff0c\u53c8\u9053\uff1a\u201c\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\uff0c\u5929\u4e0b\u7b2c\u4e00\u5947\u4e66\uff0c\u4f60\u5e74\u7eaa\u5c1a\u8f7b\uff0c\u5927\u7ea6\u4e0d \u66fe\u542c\u8fc7\u8fd9\u53e5\u8bdd\uff0c\u4f46\u82e5\u662f\u2014\u2014\u5509\uff0c\u82e5\u662f\u5e74\u7eaa\u548c\u6211\u76f8\u82e5\u4e4b\u4e0b\uff0c\u5374\u6781\u5c11\u6709\u4eba\u672a\u66fe\u542c\u8fc7\u8fd9\u5929\u4e0b\u7b2c\u4e00\u5947 \u4e66\u7684\u6545\u4e8b\u3002\u6211\u8d39\u5c3d\u5343\u65b9\u767e\u8ba1\uff0c\u5bfb\u5f97\u6b64\u4e66\uff0c\u5374\u4e5f\u9669\u4e9b\u56e0\u5b83\u8d70\u706b\u4eba\u9b54\u3002\u201d\u4ed6\u8bed\u58f0\u4e00\u987f\uff0c\u7a81\u53c8\u5c06\u8fd9 \u672c\u602a\u7edd\u5929\u4e0b\u3001\u4e5f\u5999\u7edd\u5929\u4e0b\u7684\u5947\u4e66\uff0c\u9001\u5230\u5c55\u767d\u9762\u524d\uff0c\u53c8\u9053\uff1a\u201c\u4f60\u518d\u770b\u770b\uff0c\u8fd9\u4e66\u4e2d\u7684\u7384\u79d8\u4e4b\u5904\uff0c \u8fd8\u4e0d\u6b62\u6b64\u54e9\u3002\u201d \u3000\u3000\u5c55\u767d\u5782\u4e0b\u5934\u53bb\uff0c\u773c\u89c2\u9f3b\uff0c\u9f3b\u89c2\u5fc3\uff0c\u53ea\u662f\u518d\u4e5f\u4e0d\u6562\u671b\u5b83\u4e00\u773c\u3002 \u3000\u3000\u96f7\u6728\u53d4\u5fbd\u5fae\u4e00\u7b11\uff0c\u4f38\u624b\u63a9\u4f4f\u4e86\u6b64\u4e66\u7684\u5927\u534a\uff0c\u53c8\u9053\uff1a\u201c\u4f60\u4e14\u770b\u7740\u8fd9\u4e66\u4e0a\u7684\u5b57\u8ff9\u3002\u201d \u3000\u3000\u5c55\u767d\u5fc3\u6709\u4f59\u60b8\uff0c\u4f46\u77e5\u9053\u8fd9\u602a\u5f02\u8001\u4eba\u6b64\u4e3e\u5b9a\u6709\u6df1\u610f\uff0c\u8f7b\u8f7b\u4e00\u62cd\u773c\u5e18\uff0c\u53ea\u89c1\u8fd9\u4e0a\u9762\u7684\u6781\u6de1\u5b57 \u8ff9\uff0c\u5f00\u5934\u51e0\u5b57\uff0c\u7adf\u662f\u5199\u7740\uff1a\u201c\u7f8e\u4eba\u6709\u6001\u6709\u60c5\u6709\u8da3\u6709\u795e\uff0c\u5507\u6a80\u62c2\u65e5\uff0c\u5a9a\u4f53\u8fce\u98ce\u2026\u2026\u201d\u4ed6\u5fc3\u5934\u4e00 \u632f\uff0c\u62ac\u8d77\u76ee\u5149\uff0c\u518d\u4e5f\u4e0d\u6562\u671b\u4e0b\u770b\u53bb\uff0c\u5fc3\u4e2d\u5374\u4e0d\u7981\u5927\u5947\uff0c\u671f\u7b11\u9053\uff1a\u201c\u665a\u8f88\u5728\u6697\u4e2d\u6478\u7d22\uff0c\u8fd9\u5f00\u5934 \u51e0\u5b87\uff0c\u4f3c\u4e4e\u6839\u672c\u4e0e\u6b64\u5927\u4e0d\u76f8\u540c\uff01\u600e\u5730\u2014\u2014\u201d\u96f7\u5927\u53d4\u53cc\u7709\u4e00\u5c55\uff0c\u559c\u52a8\u989c\u8272\uff0c\u9053\uff1a\u201c\u4f60\u518d\u95ed\u8d77\u773c \u775b\u6478\u6478\u770b\u3002\u2018\u5c55\u767d\u5fc3\u4e2d\u4e00\u52a8\uff0c\u7acb\u523b\u95ed\u8d77\u773c\u5e18\uff0c\u4f38\u624b\u6478\u53bb\uff01\u4e0a\u9762\u5b57\u8ff9\u5fae\u51f8\uff0c\u4ed6\u4eba\u624b\u4fbf\u77e5\uff0c\u4ecd\u7136 \u662f\u90a3\u4e9b\u5185\u542b\u6b66\u529f\u5965\u79d8\u7684\u5b57\u8ff9\uff0c\u4e0d\u7981\u5f20\u5f00\u773c\u775b\uff0c\u5947\u9053\uff1a\u201c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d \u3000\u3000\u96f7\u5927\u53d4\u5634\u89d2\u542b\u7b11\uff0c\u50cf\u662f\u6781\u4e3a\u9ad8\u5174\uff0c\u9053\uff1a\u201c\u5148\u524d\u6211\u751f\u6015\u4f60\u7eb5\u7136\u5728\u9ed1\u6697\u4e2d\u5bfb\u5f97\u6b64\u4e66\uff0c\u5374\u4e5f\u4e0d \u77e5\u5176\u4e2d\u5965\u5999\uff0c\u54ea\u77e5\u7adf\u771f\u7684\u6478\u51fa\u4e86\u4e0a\u9762\u7684\u5b57\u8ff9\u3002\u201d \u3000\u3000\u5c55\u767d\u63a5\u53e3\u9053\uff1a\u201c\u665a\u8f88\u8fd9\u4e9b\u65e5\u5b50\u4ee5\u6765\uff0c\u65e5\u65e5\u90fd\u5728\u6478\u7d22\uff0c\u5df2\u5c06\u6b64\u4e66\u4e0a\u7684\u5b57\u8ff9\u5b8c\u5168\u9ed8\u6d8c\u51fa\u6765\u2014 \u2014\u201d\u96f7\u5927\u53d4\u53cc\u7709\u4e00\u8f69\uff0c\u6025\u6025\u95ee\u9053\uff1a\u201c\u4e66\u4e2d\u542b\u610f\uff0c\u4f60\u53ef\u66fe\u660e\u4e86\uff1f\u201d \u3000\u3000\u5c55\u767d\u53f9\u9053\uff1a\u201c\u665a\u8f88\u8d44\u8d28\u611a\u949d\uff0c\u4e66\u4e2d\u5b57\u8ff9\u5982\u6b64\u8270\u6da9\u751f\u5965\uff0c\u665a\u8f88\u82e6\u82e6\u7422\u78e8\u591a\u65e5\uff0c\u624d\u5c06\u6b64\u4e2d\u542b \u610f\uff0c\u7565\u5fae\u4e86\u89e3\u5c11\u8bb8\uff0c\u8fd8\u671b\u8001\u524d\u8f88\u518d\u52a0\u6307\u70b9\u201d\u54ea\u77e5\u96f7\u5927\u53d4\u7a81\u5730\u773c\u5e18\u4e00\u5782\uff0c\u6d69\u53f9\u4e00\u58f0\uff0c\u7f13\u7f13\u8bf4 \u9053\uff1a\u201c\u770b\u6765\u5929\u7f18\u5076\u5408\uff0c\u4e00\u4e1d\u4e5f\u5f3a\u6c42\u4e0d\u5f97\uff0c\u5509\u2014\u2014\u6211\u8fd9\u756a\u82e6\u5fc3\uff0c\u603b\u7b97\u4e5f\u6ca1\u6709\u767d\u8d39\u3002\u201d\u4ed6\u7f13\u7f13\u5f20 \u5f00\u773c\u775b\uff0c\u9000\u56de\u77f3\u5e8a\u5750\u4e0b\uff0c\u53c8\u9053\uff1a\u4f60\u82e5\u771f\u80fd\u5c06\u6b64\u4e66\u4e2d\u5965\u5999\u4e86\u7136\uff0c\u53ea\u8981\u518d\u52a0\u7814\u4e60\uff0c\u53ea\u6015\u6bcb\u5eb8\u591a \u8bf4\uff0c\u5c31\u8fde\u8001\u592b\u4e5f\u4e0d\u518d\u662f\u4f60\u654c\u624b\u3002\u201c\u5c55\u767d\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u6b64\u4e66\u660e\u660e\u662f\u672c\u6b63\u6b63\u5f53\u5f53\u7684\u5185\u529f\u79d8\u7b08\uff0c \u600e\u5730\u5374\u6709\u4e2a\u5982\u6b64\u4e0d\u6b63\u7684\u540d\u5b57\uff0c\u8457\u4e66\u4e4b\u4eba\u660e\u660e\u60f3\u5c06\u81ea\u5df1\u7684\u4e00\u8eab\u6b66\u529f\u4f20\u4e4b\u540e\u4e16\uff0c\u5374\u53c8\u600e\u5730\u5728\u4e66\u4e0a \u753b\u4e0a\u8fd9\u4e9b\u2014\u2014\u5509\uff0c\u8fd9\u5c82\u975e\u6545\u610f\u8981\u9677\u4eba\u5165\u7f6a\u3002\u201d\u4ed6\u8bed\u58f0\u6e10\u6e10\u5730\u53d8\u5f97\u9ad8\u6602\u8d77\u6765\uff1a\u201c\u50cf\u8fd9\u79cd\u4eba\u5199\u4e0b \u7684\u5185\u529f\u79d8\u7b08\uff0c\u53ea\u6015\u4e5f\u4e0d\u662f\u4ec0\u4e48\u6b63\u9053\u529f\u592b\uff0c\u665a\u8f88\u4e0d\u5b66\u4e5f\u7f62\u3002\u201d \u3000\u3000\u8981\u77e5\u9053\u4ed6\u672c\u5177\u521a\u5f3a\u6b63\u76f4\u7684\u81f3\u60c5\u81f3\u6027\uff0c\u5e7c\u4ece\u7236\u6bcd\u4e4b\u8bad\uff0c\u66f4\u4f7f\u4ed6\u6210\u4e3a\u4e00\u4e2a\u4e00\u4e1d\u4e0d\u82df\u7684\u6b63\u4eba\u541b \u5b50\uff0c\u6b64\u523b\u4f46\u89c9\u5fc3\u6709\u6240\u611f\uff0c\u4fbf\u53c8\u76f4\u7387\u5730\u8bf4\u4e86\u51fa\u6765\u3002\u90a3\u96f7\u5927\u53d4\u5fae\u5fae\u4e00\u7b11\uff0c\u610f\u793a\u8d5e\u8bb8\uff0c\u9053\uff1a\u201c\u6b64\u4e66 \u867d\u6709\u8bb8\u591a\u90aa\u5f02\u4e4b\u5904\uff0c\u4f46\u4e66\u4e2d\u6240\u8f7d\u6b66\u5b66\u5965\u79d8\uff0c\u5374\u90ce\u662f\u6b66\u6797\u6b63\u5b97\u7684\u4e0d\u4f20\u4e4b\u79d8\uff0c\u800c\u4e14\u8457\u4e66\u4e4b\u4eba\u5982\u6b64 \u4f5c\u6cd5\uff0c\u4e5f\u5e76\u975e\u6ca1\u6709\u6df1\u610f\u3002\u201d \u3000\u3000\u5c55\u767d\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u65b9\u5f85\u8fa9\u9a73\uff0c\u5374\u542c\u96f7\u5927\u53d4\u53c8\u5df2\u63a5\u9053\uff1a\u201c\u6b64\u4e66\u7684\u6765\u5386\uff0c\u6b66\u6797\u4e2d\u4eba\u8a00\u4eba \u6b8a\uff0c\u83ab\u8877\u4e00\u662f\u3002\u4f46\u5f52\u7eb3\u8d77\u6765\uff0c\u6b64\u4e66\u5927\u7ea6\u662f\u4e24\u767e\u4e03\u5341\u5e74\u524d\uff0c\u4e00\u4f4d\u53eb\u505a\u2018\u53ea\u773c\u90ce\u541b\u2019\u7684\u6b66\u6797\u5947\u4eba \u6240\u8457\u3002\u201d \u3000\u3000\u5c55\u767d\u5fcd\u4e0d\u4f4f\u53c8\u81ea\u95ee\u9053\uff1a\u201c\u8fd9\u2018\u53ea\u773c\u90ce\u541b\u2019\u53c8\u662f\u4ec0\u4e48\u4eba\uff0c\u96be\u9053\u4ed6\u53ea\u6709\u4e00\u53ea\u773c\u775b\u5417\uff1f\u201d\u4ed6\u7ec8 \u7a76\u662f\u5c11\u4e2d\u5fc3\u6027\uff0c\u5fc3\u91cc\u89c9\u5f97\u5947\u602a\uff0c\u4fbf\u53c8\u95ee\u4e86\u51fa\u6765\u3002 \u3000\u3000\u96f7\u5927\u53d4\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u4e2a\u53ea\u773c\u90ce\u541b\u540d\u867d\u2018\u53ea\u773c\u2019\uff0c\u5374\u975e\u2018\u53ea\u773c\u2019\uff0c\u4ed6\u53d6\u6b64\u540d\u5927\u7ea6\u662f \u53d6\u7684\u2018\u72ec\u5177\u53ea\u773c\u2019\u4e4b\u610f\u3002\u543e\u751f\u4e5f\u665a\uff0c\u867d\u7136\u4e0d\u80fd\u773c\u89c1\u8fd9\u4f4d\u524d\u8f88\u5947\u4eba\u7684\u98ce\u91c7\uff0c\u4f46\u95fb\u5f97\u6c5f\u6e56\u6545\u8001\u4f20 \u8a00\uff0c\u8fd9\u2018\u53ea\u773c\u90ce\u541b\u2019\u4e0d\u4f46\u6b66\u529f\u5947\u9ad8\uff0c\u800c\u4e14\u51e1\u4e8b\u90fd\u6709\u72ec\u7279\u7684\u89c1\u5730\uff0c\u66f4\u80fd\u8bc6\u4eba\uff0c\u6c5f\u6e56\u4e2d\u4eba\u7684\u597d\u6b79 \u5584\u6076\uff0c\u53ea\u8981\u88ab\u4ed6\u89c1\u4e86\u4e00\u773c\uff0c\u4fbf\u7acb\u523b\u53ef\u4ee5\u5206\u8fa8\uff0c\u518d\u4e5f\u65e0\u6240\u9041\u5f62\uff0c\u662f\u4ee5\u6709\u8bb8\u591a\u5047\u5192\u4f2a\u5584\u7684\u6b66\u6797\u4e2d \u4eba\uff0c\u90fd\u88ab\u4ed6\u63ed\u7a7f\u9690\u79c1\u3002\u201d \u3000\u3000\u5c55\u767d\u5251\u7709\u4e00\u626c\uff0c\u53c8\u95ee\u9053\uff1a\u201c\u6b64\u4eba\u65e2\u662f\u5982\u6b64\u4eba\u7269\uff0c\u600e\u5730\u5374\u53c8\u5f04\u51fa\u8fd9\u79cd\u5bb3\u4eba\u4e0d\u6d45\u7684\u4e1c\u897f\u6765\uff0c \u4f9d\u665a\u8f88\u770b\u6765\uff0c\u6b64\u4eba\u53ea\u6015\u4e5f\u662f\u4e2a\u5047\u5192\u4f2a\u5584\u7684\u4f2a\u541b\u5b50\u54e9\uff01\u201d \u3000\u3000\u96f7\u5927\u53d4\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u4eba\u662f\u2018\u76d6\u68fa\u4fbf\u53ef\u8bba\u5b9a\u2019\uff0c\u4f46\u8fd9\u4f4d\u6b66\u6797\u524d\u8f88\u7684\u4e00\u751f\u884c\u4e8b\uff0c\u6b64\u523b\u4ed6\u4e0d\u4f46 \u2018\u76d6\u68fa\u2019\u5df2\u4e45\uff0c\u800c\u4e14\u53ea\u6015\u65e9\u5df2\u9aa8\u5316\u98de\u706b\uff0c\u5374\u4ecd\u65e0\u6cd5\u2018\u8bba\u5b9a\u2019\uff0c\u8fd9\u81ea\u7136\u4fbf\u662f\u56e0\u4e3a\u4ed6\u5728\u6b66\u6797\u4e2d\u60f9 \u4e0b\u65e0\u7a77\u98ce\u6ce2\uff0c\u4e0d\u8fc7\u2014\u2014\u4ed6\u4e00\u751f\u884c\u4e8b\u662f\u5584\u662f\u6076\uff0c\u867d\u7136\u5404\u4eba\u89c2\u70b9\u4e0d\u540c\uff0c\u770b\u6cd5\u5404\u5f02\uff0c\u4f46\u662f\u4ed6\u7559\u4e0b\u7684 \u8fd9\u672c\u6b66\u529f\u79d8\u63a5\uff0c\u5374\u4e07\u4e07\u4e0d\u80fd\u7b97\u505a\u2018\u5bb3\u4eba\u7684\u4e1c\u897f\u2019\u3002\u201d \u3000\u3000\u5c55\u767d\u5251\u7709\u53c8\u89c1\u4e00\u626c\uff0c\u5fc3\u4e2d\u5927\u611f\u4e0d\u670d\uff0c\u5fcd\u4e0d\u4f4f\u6297\u58f0\u8bf4\u9053\uff1a\u201c\u8001\u524d\u8f88\u65b9\u624d\u8fd8\u8bf4\u8fd9\u672c\u79d8\u7b08\u4e0d\u77e5 \u846c\u9001\u4e86\u591a\u5c11\u6b66\u6797\u8c6a\u6770\u7684\u96c4\u5fc3\u58ee\u5fd7\uff0c\u6b64\u523b\u600e\u53c8\u8bf4\u5b83\u4e0d\u662f\u5bb3\u4eba\u7684\u4e1c\u897f\uff1f\u201d \u3000\u3000\u96f7\u5927\u53d4\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u4f60\u5e74\u7eaa\u5982\u6b64\uff0c\u5374\u4e5f\u56fa\u6267\u5982\u6b64\uff0c\u4f46\u56fa\u6267\u5b9a\u987b\u62e9\u5584\uff0c\u2018\u62e9\u5584 \u4e24\u56fa\u6267\u4e4b\u2019\u65b9\u662f\u541b\u5b50\u3002\u201d\u4ed6\u5fae\u7b11\u7a0d\u6b47\uff0c\u53c8\u9053\uff1a\u201c\u95fb\u9053\u90a3\u2018\u53ea\u773c\u6717\u541b\u2019\u975e\u4f46\u4e0d\u662f\u2018\u53ea\u773c\u2019\uff0c\u800c \u4e14\u5929\u751f\u4fca\u79c0\uff0c\u8c8c\u5982\u5b50\u90fd\uff0c\u5728\u5f53\u65f6\u6c5f\u6e56\u4e2d\uff0c\u4eab\u6709\u7b2c\u4e00\u7f8e\u7537\u4e4b\u8a89\uff0c\u662f\u4ee5\u4ed6\u4e00\u751f\u4e4b\u4e2d\uff0c\u4e0d\u77e5\u7ecf\u8fc7\u4e86 \u591a\u5c11\u60c5\u5b7d\u7ea0\u7f20\uff0c\u53ea\u662f\u4ed6\u5fc3\u5982\u94c1\u77f3\uff0c\u4e1d\u6beb\u65e0\u52a8\u4e8e\u8877\u3002\u201d \u3000\u3000\u5c55\u767d\u8e0f\u201c\u54fc\u201d\u4e00\u58f0\uff0c\u5fd6\u9053\uff1a\u201c\u5fc3\u5982\u94c1\u77f3\uff0c\u4fbf\u662f\u65e0\u60c5\u4e4b\u4eba\uff0c\u4eba\u65e2\u65e0\u60c5\uff0c\u5fc5\u5b9a\u4e0d\u4f1a\u662f\u4ec0\u4e48\u597d \u4eba\u3002\u201d\u4ed6\u6b64\u523b\u5fc3\u4e2d\u5bf9\u8fd9\u201c\u53ea\u773c\u90ce\u541b\u201d\u5df2\u6709\u6210\u89c1\uff0c\u662f\u4ee5\u65e0\u8bba\u96f7\u5927\u53d4\u5982\u4f55\u8bf4\u6cd5\uff0c\u4ed6\u5fc3\u4e2d\u90fd\u4e0d\u670d\uff0c \u53ea\u662f\u4ed6\u89c1\u96f7\u5927\u53d4\u5bf9\u6b64\u4eba\u50cf\u662f\u5341\u5206\u63a8\u795f\uff0c\u662f\u4ee5\u53e3\u4e2d\u4e5f\u5c31\u6ca1\u6709\u8bf4\u51fa\u3002 \u3000\u3000\u53ea\u542c\u96f7\u5927\u53d4\u53c8\u9053\uff1a\u201c\u8fd9\u4f4d\u524d\u8f88\u8d77\u521d\u5728\u6c5f\u6e56\u4e2d\u6210\u540d\u7acb\u4e1a\u4e4b\u9645\uff0c\u6b66\u529f\u867d\u9ad8\uff0c\u5374\u672a\u81fb\u7edd\u9876\uff0c\u88ab \u4ed6\u63ed\u53d1\u4e86\u9690\u79c1\u4e4b\u4eba\uff0c\u81ea\u7136\u6068\u4ed6\u5165\u9aa8\uff0c\u53ea\u662f\u4ed6\u4ea4\u6e38\u5e7f\u9614\uff0c\u5f53\u65f6\u6b66\u6797\u4e2d\u6709\u6570\u7684\u51e0\u4f4d\u5947\u4eba\uff0c\u5bf9\u4ed6\u90fd \u7279\u522b\u9752\u7750\uff0c\u662f\u4ee5\u90a3\u4e9b\u4eba\u5fc3\u4e2d\u867d\u7136\u79ef\u6068\uff0c\u5374\u4e5f\u65e0\u53ef\u5948\u4f55\u3002\u201d \u3000\u3000\u201c\u4e8e\u662f\u8fd9\u4e9b\u4eba\u82e6\u5fc3\u79ef\u8651\u4e4b\u4e0b\uff0c\u5c31\u60f3\u5c3d\u5343\u65b9\u767e\u8ba1\u6765\u5f15\u8bf1\u4e8e\u4ed6\uff0c\u53ea\u8981\u4ed6\u505a\u51fa\u4e00\u4ef6\u90aa\u884c\uff0c\u90a3\u4e9b \u4eba\u5c31\u53ef\u501f\u53e3\u5c06\u4e4b\u9664\u53bb\uff0c\u54ea\u77e5\u2014\u2014\u54c8\u54c8\u3002\u201d \u3000\u3000\u4ed6\u5f97\u610f\u5730\u5927\u7b11\u4e24\u58f0\uff0c\u53c8\u9053\uff1a\u201c\u54ea\u77e5\u4ed6\u5fc3\u80a0\u5f53\u771f\u662f\u575a\u5982\u91d1\u77f3\uff0c\u65e0\u8bba\u4f60\u5229\u8bf1\u6216\u662f\u8272\u8bf1\uff0c\u4ed6\u90fd \u65e0\u52a8\u4e8e\u8877\uff0c\u6240\u4ee5\u4ed6\u59cb\u7ec8\u6ca1\u6709\u843d\u5165\u9677\u9631\u3002\u201d \u3000\u3000\u5c55\u767d\u5fc3\u4e2d\u867d\u7136\u4e0d\u670d\uff0c\u4f46\u6b64\u523b\u5374\u4e5f\u4e0d\u7981\u5bf9\u6b64\u4eba\u7684\u884c\u5f84\uff0c\u6697\u4e2d\u8d77\u4e86\u4e9b\u8d5e\u4f69\u4e4b\u5fc3\uff0c\u4ed8\u9053\uff1a\u201c\u6b64 \u4eba\u7740\u771f\u7684\u5982\u6b64\uff0c\u5012\u4e5f\u662f\u4e2a\u9876\u5929\u7acb\u5730\u7684\u5927\u4e08\u592b\u3002\u201d \u3000\u3000\u5374\u542c\u96f7\u5927\u53d4\u53c8\u9053\uff1a\u201c\u540e\u6765\u4ed6\u5ffd\u7136\u53c2\u900f\u5185\u5bb6\u5999\u8c1b\uff0c\u4fbf\u5bfb\u4e86\u4e2a\u9690\u50fb\u4e4b\u5730\uff0c\u9759\u7814\u6b66\u529f\u4e0a\u4e58\u5965 \u5999\uff0c\u4ed6\u867d\u7136\u5904\u5904\u8bbe\u9632\uff0c\u54ea\u77e5\u88ab\u4ed6\u4e00\u4e2a\u6700\u4eb2\u8fd1\u7684\u670b\u53cb\uff0c\u56e0\u5992\u751f\u6068\uff0c\u5c06\u4ed6\u9759\u4fee\u4e4b\u5730\uff0c\u8bf4\u51fa\u4e86\u53bb\uff0c \u4e8e\u662f\u6b64\u8baf\u4e00\u4f20\uff0c\u7fa4\u9b54\u5927\u52a8\uff0c\u7adf\u7b49\u4ed6\u9759\u4fee\u4e4b\u9645\uff0c\u524d\u53bb\u9a9a\u6270\uff0c\u8fd9\u5176\u4e2d\u6700\u6700\u5389\u5bb3\u7684\uff0c\u636e\u8bf4\u662f\u4e00\u4e2a\u7f8e \u7edd\u5929\u4ed9\u7684\u9b54\u5973\uff0c\u7adf\u65bd\u5c55\u2018\u59f9\u5973\u8ff7\u9b42\u5927\u6cd5\u2019\uff0c\u5728\u4ed6\u90a3\u7edd\u9876\u5185\u529f\u5c06\u6210\u672a\u6210\u4e4b\u9645\uff0c\u4f7f\u4ed6\u5fc3\u52a8\u3002\u201d \u3000\u3000\u4ed6\u8bed\u58f0\u4e00\u987f\uff0c\u82e6\u53f9\u4e00\u58f0\uff0c\u5c55\u767d\u4ea6\u4e0d\u7981\u4e3a\u4e4b\u5fc3\u52a8\u795e\u9a70\uff0c\u53f9\u53e3\u6c14\u9053\uff1a\u53ef\u60dc\u3002\u201c\u96f7\u5927\u53d4\u53c8\u9053\uff1a \u201c\u5185\u529f\u7ec3\u4e0d\u6210\uff0c\u53ef\u60dc\u8fd8\u5728\u5176\u6b21\uff0c\u5509\u2014\u2014\u8981\u77e5\u9053\u2018\u9053\u9ad8\u4e00\u5c3a\uff0c\u9b54\u9ad8\u4e00\u6587\u2019\uff0c\u5185\u529f\u4fee\u4e60\u5f97\u6108\u52a0\u4e0a \u4e58\uff0c\u5fc3\u9b54\u4e5f\u5c31\u6108\u52a0\u96be\u9632\uff0c\u5c24\u5176\u5728\u4ed6\u8fd9\u79cd\u5c06\u5fc3\u5999\u8c1b\uff0c\u6027\u547d\u4ea4\u4fee\uff0c\u751f\u6b7b\u7384\u5173\u5c06\u901a\u672a\u901a\u4e4b\u9645\uff0c\u4e00\u4e2a \u4e0d\u597d\uff0c\u975e\u4f46\u7acb\u65f6\u8981\u8d70\u706b\u5165\u9b54\uff0c\u800c\u4e14\u6027\u547d\u4e5f\u5371\u5982\u60ac\u5375\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e00\u4ee3\u6b66\u6797\u5947\u4eba\u4fbf\u5728\u8fd9\u6027\u547d\u6062\u5173\u4e4b\u9645\uff0c\u5fae\u751f\u7ee9\u5ff5\uff0c\u8d70\u706b\u5165\u9b54\uff0c\u82e5\u975e\u5f53\u65f6\u6b66\u5f53\u7384\u95e8\u7684\u97e6 \u6559\u771f\u4eba\u2018\u94c1\u5fc3\u9053\u957f\u2019\uff0c\u4e0e\u5c11\u6797\u4f5b\u95e8\u7684\u2018\u97e6\u6559\u7956\u5e08\u2019\u3001\u2018\u82e6\u6c34\u4e0a\u4eba\u2019\u95fb\u5f97\u8baf\u606f\uff0c\u4e00\u6012\u8fde\u6289\u4e0b \u5c71\uff0c\u4ee5\u4f5b\u9053\u4e24\u95e8\u7684\u65e0\u4e0a\u5927\u6cd5\uff0c\u5c06\u4ed6\u6551\u8f6c\uff0c\u90a3\u4e48\u4ed6\u7eb5\u4e0d\u7acb\u523b\u9b42\u5f52\u79bb\u6068\uff0c\u81f3\u5c11\u4e5f\u5f97\u8d70\u706b\u5165\u9b54\uff0c\u4ece \u6b64\u4e0d\u80fd\u52a8\u5f39\u4e86\u3002\u201d \u3000\u3000\u4ed6\u5c06\u8fd9\u6614\u5e74\u8f70\u52a8\u5929\u4e0b\u7684\u6b66\u6797\u638c\u6545\u5f80\u4e8b\uff0c\u8bf4\u5230\u8fd9\u91cc\uff0c\u5c55\u767d\u624d\u4e0d\u7981\u900f\u4e86\u53e3\u957f\u6c14\uff0c\u4f38\u624b\u4e00\u62b9\u989d \u4e0a\u6c57\u73e0\uff0c\u6447\u9996\u53f9\u9053\uff1a\u201c\u597d\u9669\uff01\u201d \u3000\u3000\u96f7\u5927\u53d4\u5374\u53c8\u9053\uff1a\u5509\uff01\u7eb5\u7136\u5982\u6b64\uff0c\u4f46\u8fd9\u4f4d\u6b66\u6797\u5947\u4eba\uff0c\u867d\u7136\u65e9\u5df3\u53c2\u900f\u5185\u5bb6\u7edd\u9876\u5965\u5999\uff0c\u4f46\u5374\u56e0 \u4e3a\u8eab\u4f53\u53d7\u635f\uff0c\u4ece\u6b64\u4e0d\u80fd\u52d8\u7834\u5185\u529f\u6700\u540e\u4e00\u5173\uff0c\u4ee5\u81f4\u62b1\u6068\u7ec8\u751f\uff0c\u4ed6\u867d\u7136\u4e0d\u613f\u5c06\u81ea\u5df1\u82e6\u5fc3\u7814\u900f\u7684\u6b66 \u529f\u7edd\u9876\u5965\u79d8\uff0c\u56e0\u6b64\u57cb\u6ca1\uff0c\u5374\u4e5f\u4e0d\u7518\u540e\u5b66\u6beb\u65e0\u56f0\u96be\u5730\u5f97\u5230\u8fbe\u79cd\u7edd\u9876\u5999\u8bc0\u3002\u201c\u201d\u56e0\u4e4b\u4ed6\u624d\u8d39\u5c3d\u5fc3 \u529b\uff0c\u5236\u4e86\u5982\u6b64\u4e00\u672c\u5947\u4e66\uff0c\u85cf\u5728\u7f57\u6d6e\u7edd\u9876\u7684\u4e00\u4e2a\u9690\u79d8\u6240\u5728\uff0c\u800c\u4e14\u626c\u8a00\u5929\u4e0b\uff0c\u6709\u5982\u6b64\u4e00\u90e8\u5947\u4e66\uff0c \u53ea\u662f\u76f4\u8a00\u5b9a\u529b\u4e0d\u575a\u7684\uff0c\u5207\u5207\u4e0d\u53ef\u5c1d\u8bd5\u2014\u2014\u201c\u4ed6\u76ee\u5149\u4e00\u8f6c\uff0c\u671b\u5411\u5c55\u767d\uff0c\u9053\uff1a\u201c\u8fd9\u53c8\u600e\u80fd\u8bf4\u4ed6\u4e0d \u5bf9\u3002\u201d \u3000\u3000\u5c55\u767d\u695e\u4e86\u4e00\u695e\uff0c\u5782\u9996\u65e0\u8a00\uff0c\u5374\u542c\u96f7\u5927\u53d4\u63a5\u7740\u53c8\u9053\uff1a\u201c\u8fd9\u4f4d\u524d\u8f88\u5f02\u4eba\uff0c\u540e\u6765\u81ea\u77e5\u6b66\u529f\u65e0\u6cd5 \u518d\u8fdb\u4e00\u5c42\uff0c\u4fbf\u57cb\u9996\u4e8e\u8bd7\u8bcd\u4e66\u753b\u4e4b\u4e2d\uff0c\u4ed6\u5929\u8d44\u7edd\u9876\uff0c\u5f53\u771f\u662f\u2018\u4e00\u901a\u767e\u901a\u2019\uff0c\u540e\u6765\u7adf\u6210\u4e86\u5929\u4e0b\u95fb \u540d\u7684\u4e39\u9752\u5999\u624b\u3002\u636e\u8bf4\u8fd9\u672c\u5947\u4e66\u4e0a\u7684\u56fe\u753b\uff0c\u4e0d\u4f46\u5168\u662f\u4ed6\u4eb2\u624b\u6240\u7ed8\uff0c\u800c\u6240\u7ed8\u7684\u4eba\uff0c\u4fbf\u662f\u90a3\u66fe\u6bc1\u4ed6 \u5927\u9053\u7684\u9b54\u5973\u3002\u201d \u3000\u3000\u4ed6\u5c06\u624b\u4e2d\u4e66\u518c\u4e00\u626c\uff0c\u63a5\u9053\uff1a\u201c\u4f60\u65b9\u624d\u89c1\u8fd9\u4e66\u4e2d\u4e4b\u4eba\uff0c\u662f\u5426\u795e\u6001\u5404\u5f02\uff0c\u4f46\u9762\u76ee\u5374\u5b8c\u5168\u4e00 \u6837\uff0c\u5509\u2014\u2014\u8fd9\u9b54\u5973\u5f53\u771f\u662f\u5929\u751f\u5c24\u7269\uff0c\u4fbf\u53ea\u8fd9\u753b\u91cc\u4f20\u771f\uff0c\u5df2\u80fd\u4f7f\u4eba\u610f\u9a6c\u5fc3\u733f\uff0c\u4e5f\u96be\u602a\u90a3\u2018\u53ea\u773c \u90ce\u541b\u2019\u2014\u2014\u201d\u4ed6\u957f\u53f9\u4e00\u58f0\uff0c\u7115\u7136\u4e2d\u6b62\u4e86\u81ea\u5df1\u7684\u8bdd\uff0c\u8a00\u4e0b\u4e4b\u610f\uff0c\u4e0d\u8a00\u53ef\u55bb\u3002 \u3000\u3000\u8fd9\u6bb5\u79bb\u5947\u8be1\u5f02\u3001\u66f2\u6298\u8c6a\u5feb\u7684\u6b66\u6797\u5f80\u4e8b\uff0c\u53ea\u542c\u5f97\u5c55\u767d\u76ee\u5b9a\u53e3\u5446\uff0c\u610f\u9189\u795e\u8ff7\uff0c\u773c\u524d\u4f3c\u4e4e\u6d3b\u8131 \u8131\u5730\u73b0\u51fa\u90a3\u201c\u53ea\u773c\u90ce\u541b\u201d\u7684\u5f71\u5b50\u3002 \u3000\u3000\u4ed6\u4e0d\u7981\u4e3a\u4e4b\u9ed8\u7136\u5782\u4e0b\u5934\u53bb\uff0c\u5fc3\u4e2d\u53cd\u590d\u4ed8\u9053\uff1a\u201c\u4fbf\u53ea\u8fd9\u753b\u91cc\u4f20\u771f\uff0c\u5df2\u80fd\u4f7f\u4eba\u610f\u9a6c\u5fc3\u733f\u2026\u2026 \u5509\uff01\u770b\u6765\u4e0d\u4f46\u8fd9\u2018\u53ea\u773c\u90ce\u541b\u2019\u662f\u4f4d\u5947\u4eba\uff0c\u5c31\u662f\u8fd9\u9b54\u5973\u4e5f\u662f\u5947\u4eba\u3002\u201d \u3000\u3000\u4e24\u4eba\u9ed8\u7136\u826f\u4e45\uff0c\u5404\u5404\u4f3c\u4e4e\u90fd\u5728\u8ffd\u5fc6\u6b66\u6797\u524d\u8f88\u7684\u82f1\u52c7\u5f80\u4e8b\uff0c\u5c55\u767d\u5fc3\u4e2d\u66f4\u591a\u4e86\u51e0\u5206\u8b66\u60d5\u3002\u4e00 \u9635\u98ce\u7531\u6d1e\u4e0a\u5439\u6765\uff0c\u6251\u9762\u5439\u5411\u5c55\u767d\uff0c\u4ed6\u59cb\u8d77\u5934\u6765\uff0c\u5b9a\u4e86\u5b9a\u795e\uff0c\u5fae\u5531\u4e00\u58f0\uff0c\u65b9\u81ea\u95ee\u9053\uff1a\u201c\u8fd9\u672c\u5947 \u4e66\u540e\u6765\u7684\u5386\u53f2\u5982\u4f55\uff1f\u53c8\u600e\u5730\u4f1a\u5230\u4e86\u8001\u524d\u8f88\u4f60\u7684\u624b\u4e0a\uff1f\u201d \u3000\u3000\u96f7\u5927\u53d4\u76ee\u5149\u4e00\u62ac\uff0c\u50cf\u662f\u65b9\u81ea\u4ece\u56de\u5fc6\u4e2d\u9192\u6765\u3002\u5b9a\u4e86\u5b9a\u795e\uff0c\u9053\uff1a\u201c\u90a3\u2018\u53ea\u773c\u90ce\u541b\u2019\u8bdd\u867d\u90a3\u822c \u8bf4\u6cd5\uff0c\u4f46\u6b66\u6797\u4e2d\u4eba\u542c\u5f97\u6709\u8fd9\u79cd\u5185\u5bb6\u79d8\u7b08\uff0c\u8c01\u80fd\u4e0d\u7830\u7136\u5fc3\u52a8\uff0c\u4e0d\u5230\u534a\u5e74\uff0c\u7f57\u6d6e\u5c71\u7fa4\u96c4\u6bd5\u96c6\uff0c\u90fd \u662f\u4e00\u5fc3\u60f3\u8981\u5bfb\u5f97\u8fd9\u6b66\u6797\u79d8\u7b08\u7684\u4eba\u3002\u4f46\u8f6c\u773c\u4e00\u5e74\u8fc7\u53bb\uff0c\u5728\u7f57\u6d6e\u5c71\u98a0\u7684\u5927\u5c0f\u6d1e\u51e0\u4e4e\u88ab\u8fd9\u4e9b\u4eba\u641c\u5bfb \u4e00\u904d\u4e4b\u540e\uff0c\u8fd9\u672c\u6b66\u6797\u79d8\u7b08\u4e5f\u7ec8\u4e8e\u88ab\u2018\u6cd5\u534e\u5357\u5bb6\u2019\u95e8\u4e0b\u7684\u4e24\u4e2a\u5f1f\u5b50\u5bfb\u5230\u3002\u201d \u3000\u3000\u5c55\u767d\u53cc\u7709\u5fae\u76b1\uff0c\u63a5\u53e3\u9053\uff1a\u201c\u90a3\u4e9b\u4e00\u5fc3\u5bfb\u5b9d\uff0c\u4f46\u5374\u5931\u671b\u4e86\u7684\u4eba\uff0c\u53ea\u6015\u4e0d\u4f1a\u8ba9\u4ed6\u4eec\u90a3\u4e48\u5b89\u7a33 \u5730\u5f97\u5230\u6b64\u4e66\uff01\u8fd8\u6709\u2014\u2014\u4ed6\u4eec\u89c1\u5230\u8fd9\u4e2a\u2014\u2014\u90a3\u672c\u770b\u6765\u5f69\u8272\u6591\u70c2\uff0c\u4eff\u4f5b\u662f\u4e00\u672c\u8273\u8bcd\u6deb\u4e66\u7684\u5947\u4e66\u7684 \u65f6\u5019\uff0c\u53c8\u600e\u5730\u77e5\u9053\u8fd9\u4fbf\u662f\u2018\u53ea\u773c\u90ce\u541b\u2019\u6240\u7559\u7684\u5185\u5bb6\u79d8\u7b08\u5462\uff1f\u201d \u3000\u3000\u96f7\u5927\u53d4\u5fae\u5fbd\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u4e9b\u4e8b\u6211\u4e5f\u662f\u542c\u6545\u8001\u76f8\u4f20\uff0c\u771f\u5b9e\u8be6\u7ec6\u7684\u60c5\u51b5\uff0c\u6211\u4e5f\u77e5\u9053\u5f97\u5e76\u4e0d \u6e05\u695a\uff0c\u53ea\u77e5\u8fd9\u2018\u6cd5\u534e\u5357\u5b97\u2019\u7684\u4e24\u4e2a\u5f1f\u5b50\uff0c\u5728\u6b66\u6797\u4e2d\u672c\u662f\u6709\u540d\u7684\u786c\u624b\u2014\u2014\u201d\u4ed6\u8bed\u58f0\u505c\u987f\uff0c\u7a81\u5730 \u957f\u53f9\u4e00\u58f0\u9053\uff1a\u201c\u8981\u77e5\u9053\u8fd9\u4e9b\u6b66\u6797\u9ad8\u624b\u805a\u5230\u7f57\u6d6e\u5c71\u4e4b\u540e\uff0c\u672c\u5df2\u7ecf\u8fc7\u4e00\u624b\u7684\u660e\u4e89\u6697\u6597\uff0c\u846c\u8eab\u4e8e\u6b64 \u4e8b\u4e2d\u7684\u4eba\uff0c\u4e0d\u77e5\u5df2\u6709\u4e86\u591a\u5c11\uff0c\u8fd9\u2018\u6cd5\u534e\u5357\u5b97\u2019\u7684\u5f1f\u5b50\u4e24\u4eba\uff0c\u7ecf\u8fc7\u4e00\u9635\u5f31\u8089\u5f3a\u98df\u7684\u638f\u6c70\u7ade\u4e89\u4e4b \u540e\uff0c\u8fd8\u80fd\u5c79\u7acb\u4e0d\u5012\uff0c\u60f3\u5fc5\u4e0d\u4f46\u6b66\u529f\u6781\u786c\uff0c\u4fbf\u662f\u5fc3\u8ba1\u4e5f\u5b9a\u6709\u8fc7\u4eba\u4e4b\u5904\u3002\u201d \u3000\u3000\u5c55\u81ea\u8fde\u8fde\u989d\u9996\uff0c\u9053\uff1a\u201c\u662f\u6781\uff01\u201d\u5fc3\u4e2d\u4e00\u9762\u5374\u5bf9\u8fd9\u96f7\u5927\u53d4\u5206\u6790\u4e8b\u60c5\u7684\u51b7\u9759\u6e05\u695a\uff0c\u9887\u4e3a\u656c \u4f69\uff0c\u5ff5\u5934\u8f6c\u5904\uff0c\u5fc3\u4e2d\u4e0d\u7981\u53c8\u4e3a\u4e4b\u4e00\u52a8\uff1a\u201c\u4ed6\u672c\u662f\u6781\u7aef\u806a\u660e\u7684\u4eba\uff0c\u4ee5\u524d\u5374\u4e3a\u4ec0\u4e48\u8981\u88c5\u6210\u90a3\u526f\u6837 \u5b50\uff1f\u5509\uff01\u60f3\u89c1\u4ed6\u81ea\u8eab\u4e5f\u5b9a\u7136\u6709\u7740\u4e00\u6bb5\u4e0d\u5e73\u51e1\u7684\u5f80\u4e8b\uff0c\u4ee5\u540e\u6211\u5012\u8981\u95ee\u95ee\u4ed6\uff01\u201d \u3000\u3000\u5374\u89c1\u96f7\u5927\u53d9\u4e00\u626c\u624b\u4e2d\u90a3\u672c\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\uff0c\u63a5\u7740\u53c8\u9053\uff0c\u201c\u6b64\u4e66\u88ab\u90a3\u4e24\u4eba\u53d1\u73b0\u4e4b\u9645\uff0c \u636e\u8bf4\u662f\u88ab\u88c5\u5728\u4e00\u4e2a\u5236\u4f5c\u5f97\u6781\u5176\u7cbe\u5999\u7684\u6a80\u9999\u5323\u4e2d\uff0c\u5323\u9762\u4e4b\u4e0a\u4fbf\u5199\u7684\u662f\u2018\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u79d8\u5f55\u2019\u516b \u5b57\uff0c\u8fd9\u4e5f\u5c31\u4fbf\u662f\u6b64\u4e66\u6709\u6b64\u540d\u79f0\u7684\u7531\u6765\u3002\u90a3\u4e24\u4eba\u53d1\u73b0\u6b64\u672c\u5947\u4e66\u4e4b\u540e\uff0c\u7ade\u5168\u7136\u4e0d\u52a8\u58f0\u8272\uff0c\u53ea\u5c06\u6a80 \u6728\u5323\u5b50\u6253\u5f00\uff0c\u53d6\u51fa\u8fd9\u672c\u79d8\u7b08\uff0c\u6362\u4e0a\u4e00\u672c\u2018\u592a\u6781\u62f3\u6cd5\u8bc0\u8981\u2019\u653e\u5728\u5323\u91cc\uff0c\u795e\u4e0d\u77e5\u9b3c\u4e0d\u89c9\u5730\u518d\u5c06\u8fd9 \u5323\u5b50\u653e\u56de\u539f\u5904\uff0c\u7136\u540e\u4ed6\u4e24\u4eba\u7ade\u518d\u8ddf\u7740\u522b\u4eba\u4e00\u9f50\u5bfb\u627e\uff0c\u53ea\u5f53\u6839\u672c\u6ca1\u6709\u53d1\u751f\u8fd9\u56de\u4e8b\u4e00\u6837\uff0c\u522b\u4eba\u81ea \u4e5f\u5168\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u5c55\u767d\u6697\u53f9\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8fd9\u4e24\u4eba\u5f53\u771f\u662f\u5de5\u4e8e\u5fc3\u8ba1\uff0c\u96be\u9053\u4ed6\u4eec\u795e\u8272\u4e4b\u95f4\uff0c\u4e00\u4e1d\u4e5f\u6ca1\u6709\u9732\u51fa \u5417\uff1f\u201d \u3000\u3000\u96f7\u5927\u53d4\u9894\u9996\u53f9\u9053\uff0c\u201c\u60f3\u90a3\u822c\u6b66\u6797\u8c6a\u58eb\uff0c\u90fd\u662f\u4f55\u7b49\u4eba\u7269\uff0c\u7aef\u7684\u662f\u773c\u4e2d\u4e0d\u7559\u534a\u7c92\u6c99\u5b50\uff0c\u53ea\u8981 \u4ed6\u4e24\u4eba\u7a0d\u73b0\u8f9e\u8272\uff0c\u522b\u4eba\u7109\u6709\u770b\u4e0d\u51fa\u6765\u7684\u9053\u7406\uff01\u201d \u3000\u3000\u5c55\u767d\u53f9\u9053\uff1a\u201c\u6211\u53ea\u9053\u2018\u6cd5\u534e\u5357\u5b97\u2019\u662f\u6b66\u6797\u6b63\u5b97\uff0c\u5374\u60f3\u4e0d\u5230\u4e5f\u6709\u8fd9\u7b49\u5f1f\u5b50\u3002\u201d \u3000\u3000\u96f7\u5927\u53d4\u5b89\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u83ab\u8bf4\u2018\u6cd5\u534e\u5357\u5b97\u2019\uff0c\u4fbf\u662f\u2018\u6b66\u5f53\u2019\uff0c\u2018\u5c11\u6797\u2019\uff0c\u53c8\u4f55\u5c1d\u6ca1\u6709\u8d25 \u7c7b\u3002\u201d\u5c55\u767d\u9894\u9996\u4e00\u53f9\uff0c\u5374\u542c\u96f7\u5927\u53d4\u63a5\u9053\uff1a\u201c\u4e0a\u5c71\u5bfb\u5b9d\u4e4b\u4eba\uff0c\u6709\u7684\u88ab\u60e8\u6740\u800c\u6b7b\uff0c\u6709\u7684\u5931\u671b\u5f52 \u53bb\uff0c\u6700\u540e\u53ea\u4e0d\u8fc7\u5269\u4e0b\u5341\u6570\u4eba\u800c\u5df2\uff01\u90a3\u2018\u6cd5\u534e\u5357\u5b97\u2019\u7684\u4e24\u4e2a\u5f1f\u5b50\uff0c\u4fbf\u4e0d\u52a8\u58f0\u8272\u5730\u6df7\u5728\u5176\u95f4\uff01\u8fc7 \u4e86\u6570\u65e5\uff0c\u4e00\u4e2a\u6df1\u591c\u4e4b\u4e2d\uff0c\u90a3\u65f6\u6b63\u503c\u521d\u51ac\uff0c\u7f57\u6d6e\u5c71\u98a0\uff0c\u5bd2\u610f\u5df2\u91cd\uff0c\u5927\u5bb6\u6b63\u5728\u56f4\u706b\u53d6\u6696\uff0c\u5ffd\u5730\u542c \u5f97\u4e00\u9635\u9635\u72c2\u7b11\uff0c\u8fdc\u8fdc\u4f20\u6765\uff0c\u5927\u5bb6\u5fc3\u4e2d\u4e00\u60ca\uff01\u8dd1\u53bb\u4e00\u770b\u2014\u2014\u5509\uff0c\u591c\u8272\u4e4b\u4e2d\uff0c\u53ea\u89c1\u90a3\u2018\u6cd5\u534e\u5357 \u5b97\u2019\u7684\u4e24\u4e2a\u5f1f\u5b50\u5176\u4e2d\u4e00\u4eba\uff0c\u7ade\u8131\u5f97\u6d51\u8eab\u8d64\u88f8\uff0c\u5728\u6714\u98ce\u4e2d\u6ee1\u5730\u6253\u6eda\uff0c\u800c\u4ed6\u624b\u91cc\uff0c\u4fbf\u62ff\u7740\u8fd9\u672c\u5947 \u4e66\u3002\u201d \u3000\u3000\u5c55\u767d\u53ea\u542c\u5f97\u5fc3\u5934\u4e00\u98a4\uff0c\u5fcd\u4e0d\u4f4f\u8131\u53e3\u60ca\u547c\u4e00\u58f0\uff0c\u96f7\u5927\u53d4\u957f\u53f9\u63a5\u9053\uff1a\u201c\u539f\u6765\u90a3\u4eba\u8eab\u6000\u5947\u4e66\uff0c \u5fcd\u4e86\u6570\u65e5\uff0c\u7ec8\u7a76\u5fcd\u4e0d\u4f4f\uff0c\u5fc3\u60f3\uff1a\u2018\u6df1\u591c\u4e4b\u4e2d\uff0c\u6211\u5077\u5077\u770b\u51e0\u773c\u53c8\u6709\u4f55\u59a8\u3002\u2019\u4fbf\u4e58\u7740\u5927\u5bb6\u672a\u66fe\u6ce8 \u610f\u4e4b\u9645\uff0c\u8dd1\u5230\u4e00\u4e2a\u5c71\u7a9f\u4e2d\uff0c\u501f\u7740\u5fae\u5f31\u7684\u706b\u5149\u5077\u770b\uff0c\u5509\u2014\u2014\u4ed6\u4e0d\u770b\u4fbf\u597d\uff0c\u8fd9\u4e00\u770b\u4e4b\u4e0b\uff0c\u53ea\u770b\u5f97 \u4ed6\u9762\u8d64\u5fc3\u8df3\uff0c\u795e\u9500\u9b42\u8361\uff0c\u5fc3\u4e2d\u65e0\u4e3b\u6b64\u4eba\u4e2d\u7eaa\u5c1a\u8f7b\uff0c\u672c\u662f\u4e2a\u72ec\u884c\u5de8\u76d7\uff0c\u540e\u6765\u624d\u6295\u5165\u2018\u6cd5\u534e\u5357 \u5b97\u2019\uff0c\u662f\u4ee5\u5185\u529f\u4fee\u4e3a\u4ea6\u4e0d\u751a\u7eaf\uff0c\u518d\u52a0\u4e0a\u4ed6\u65e9\u5e74\u653e\u8361\u6c5f\u6e56\uff0c\u96be\u514d\u58f0\u8272\u72ac\u9a6c\uff0c\u5728\u7f57\u6d6e\u5c71\u98a0\u82e6\u4e86\u4e00 \u5e74\uff0c\u5fcd\u4e86\u4e00\u5e74\uff0c\u5fc3\u4e2d\u672c\u81ea\u6709\u4e9b\u53d1\u614c\uff0c\u54ea\u7981\u5f97\u5982\u6b64\u523a\u6fc0\uff0c\u770b\u4e86\u8bb8\u4e45\uff0c\u7ade\u770b\u5f97\u53d1\u72c2\u4e86\u3002\u201c\u5c55\u767d\u5fc3 \u5934\u4e0d\u7981\u53c8\u4e3a\u4e4b\u4e00\u9707\uff0c\u8131\u53e3\u9053\uff1a\u201c\u8fd9\u672c\u4e66\u4e0a\u7684\u51e0\u9875\u56fe\u753b\uff0c\u5f53\u771f\u6709\u8fd9\u822c\u9b54\u529b\uff1f\u201d \u3000\u3000\u96f7\u5927\u53d4\u53f9\u9053\uff1a\u201c\u4f60\u672a\u7aa5\u5168\u8c8c\uff0c\u81ea\u7136\u4e0d\u77e5\u9053\u5176\u4e2d\u5965\u5999\uff0c\u636e\u8bf4\u6b64\u4e66\u4e2d\u6240\u8bf4\u7684\u56fe\u753b\uff0c\u90fd\u662f\u4f9d\u7167 \u90a3\u9b54\u5973\u7684\u2018\u59f9\u5973\u8ff7\u9b42\u5927\u6cd5\u2019\u6240\u7ed8\uff0c\u4e66\u4e2d\u8bcd\u53e5\uff0c\u66f4\u662f\u2014\u2014\u5509\uff01\u4f60\u4f46\u60f3\u6b64\u4e66\u65e2\u6709\u2018\u9501\u9aa8\u9500\u9b42\u2019\u4e4b \u53e5\uff0c\u4fbf\u81ea\u6709\u2018\u9501\u9aa8\u9500\u9b42\u2019\u4e4b\u529b\uff0c\u7531\u6b64\u4fbf\u53ef\u89c1\u4e00\u822c\u4e86\u3002\u201d \u3000\u3000\u4ed6\u5fae\u987f\u53c8\u9053\uff1a\u201c\u53e6\u4e00\u4e2a\u2018\u6cd5\u534e\u5357\u5b97\u2019\u5f1f\u5b50\uff0c\u89c1\u4e86\u8fd9\u60c5\u51b5\uff0c\u5927\u5403\u4e00\u60ca\uff0c\u614c\u4e71\u4e4b\u4e0b\uff0c\u8dd1\u4e86\u8fc7 \u53bb\uff0c\u5148\u4e0d\u7ba1\u4ed6\u540c\u95e8\u5144\u5f1f\u7684\u751f\u6b7b\uff0c\u4f38\u624b\u5c31\u5c06\u6b64\u4e66\u62a2\u4e86\u8fc7\u53bb\uff0c\u4ed6\u5982\u6b64\u4e00\u6765\u7528\u90a3\u4e9b\u6b66\u6797\u7fa4\u8c6a\u4fbf\u52a8\u4e86 \u7591\u5ff5\uff0c\u5927\u5bb6\u7adf\u4e00\u9f50\u52a8\u624b\uff0c\u5c06\u8fd9\u5e08\u5144\u5f1f\u4e24\u4eba\u5236\u4f4f\uff0c\u800c\u4e14\u5927\u5bb6\u7ea6\u5b9a\uff0c\u8c01\u4e5f\u4e0d\u5f97\u7ffb\u9605\u6b64\u4e66\uff0c\u4e00\u9762\u7279 \u6b64\u4e66\u538b\u5728\u4e00\u5757\u5927\u77f3\u4e4b\u4e0b\uff0c\u4e00\u9762\u60f3\u51fa\u5404\u79cd\u9177\u5211\uff0c\u6765\u62f7\u95ee\u8fd9\u5e08\u5144\u5f1f\u4e24\u4eba\uff0c\u8fd9\u4e24\u4eba\u4e00\u4e2a\u72c2\u4e86\uff0c\u4e00\u4e2a \u53d7\u5211\u4e0d\u4f4f\u4fbf\u8bf4\u4e86\u51fa\u6765\uff01\u201d \u3000\u3000\u5c55\u767d\u53c8\u81ea\u63a5\u4e0a\u53f9\u9053\uff1a\u201c\u6b64\u79cd\u60c5\u51b5\uff0c\u8fd9\u4e24\u4eba\u60f3\u5fc5\u90fd\u96be\u9003\u6bd2\u624b\u4e86\u5427\uff01\u201d \u3000\u3000\u96f7\u5927\u53d4\u53f9\u9053\uff1a\u201c\u4e0d\u4f46\u8fd9\u4e24\u4eba\u8eab\u906d\u6bd2\u624b\uff0c\u800c\u4e14\u6b7b\u5f97\u6781\u60e8\uff0c\u522b\u7684\u4eba\u4e00\u542c\u4e4b\u4e0b\uff0c\u4fbf\u4e5f\u7acb\u523b\u4e3a\u4e4b \u5927\u4e71\uff01\u636e\u95fb\u90a3\u6cd5\u534e\u95e8\u4eba\u8bdd\u65b9\u51fa\u53e3\uff0c\u7ad9\u5728\u6700\u524d\u7684\u4e94\u4eba\uff0c\u4fbf\u88ab\u4ed6\u4eec\u8eab\u540e\u7684\u4eba\u4e0b\u4e86\u6bd2\u624b\uff0c\u5176\u4f59\u7684\u4eba \u4e0d\u5206\u4eb2\u758f\uff0c\u4e0d\u5206\u654c\u53cb\uff0c\u4e00\u9635\u4e71\u780d\u4e71\u6740\uff0c\u5176\u4e2d\u53ea\u6709\u4e00\u4eba\u53eb\u505a\u2018\u4e94\u722a\u7075\u72d0\u2019\u7684\uff0c\u5fc3\u667a\u7a0d\u6e05\uff0c\u5fd6\u91cf \u81ea\u5df2\u6b66\u529f\u8f83\u5dee\uff0c\u662f\u4ee5\u5148\u5c31\u6e9c\u4e86\uff0c\u4f46\u5374\u4e5f\u672a\u66fe\u8d70\u8fdc\uff0c\u8eb2\u5728\u6697\u4e2d\u5077\u770b\uff0c\u5230\u540e\u6765\u4ed6\u773c\u89c1\u90a3\u4e9b\u6b66\u6797\u9ad8 \u624b\uff0c\u4e92\u76f8\u6b8b\u6740\u6b86\u5c3d\uff0c\u53ea\u5269\u4e0b\u4e00\u4e2a\u5d06\u5cd2\u5f1f\u5b50\uff0c\u8eab\u624b\u8f83\u9ad8\uff0c\u72c2\u7b11\u7740\u642c\u5f00\u90a3\u5757\u5de8\u77f3\uff0c\u53d6\u51fa\u8fd9\u672c\u5947 \u4e66\uff0c\u54ea\u77e5\u8fd9\u4eba\u7b11\u58f0\u5b8b\u7edd\uff0c\u8eab\u540e\u7a81\u5730\u4e2d\u4e86\u4e00\u5200\uff0c\u7acb\u523b\u6c14\u7edd\uff0c\u539f\u6765\u90a3\u2018\u4e94\u722a\u7075\u72d0\u2019\u77e5\u9053\u4ed6\u6b64\u523b\u5df2 \u662f\u5f3a\u5f29\u4e4b\u672b\uff0c\u4fbf\u5077\u5077\u63a2\u5230\u4ed6\u8eab\u540e\uff0c\u4e00\u5200\u7279\u4ed6\u780d\u6b7b\u4e86\uff0c\u7a7a\u5c71\u4e4b\u4e2d\uff0c\u72c2\u7b11\u4e4b\u58f0\u53c8\u8d77\uff0c\u5374\u5df2\u662f\u90a3 \u2018\u4e94\u722a\u7075\u72d0\u2019\u53d1\u51fa\u7684\u4e86\u3002\u201d \u3000\u3000\u96f7\u5927\u53d4\u4e00\u53e3\u6c14\u8bf4\u5230\u8fd9\u91cc\uff0c\u8bed\u58f0\u65b9\u81ea\u4e00\u987f\u3002\u53ea\u542c\u5f97\u5c55\u767d\u98a4\u6296\uff0c\u624b\u8db3\u51b0\u51c9\uff0c\u4ed6\u521d\u6d89\u6c5f\u6e56\uff0c\u751f \u6027\u5fe0\u539a\uff0c\u51e0\u65f6\u60f3\u5230\u8fc7\u6c5f\u6e56\u4e2d\u7ade\u6709\u5982\u6b64\u51c4\u60e8\u6b8b\u9177\u4e4b\u4e8b\uff0c\u6b66\u6797\u4e2d\u7ade\u6709\u5982\u6b64\u5978\u72e1\u51f6\u6b8b\u4e4b\u4eba\uff01\u4e00\u65f6\u4e4b \u95f4\uff0c\u53ea\u89c9\u6012\u6c14\u586b\u80f8\uff0c\u518d\u4e5f\u5fcd\u8010\u4e0d\u4f4f\uff0c\u7a81\u5730\u5288\u624b\u593a\u8fc7\u8fd9\u672c\u5947\u4e66\uff0c\u53cc\u624b\u4e00\u5206\uff0c\u7adf\u8981\u5c06\u8fd9\u672c\u5929\u4e0b\u7b2c \u4e00\u5947\u4e66\u6495\u6bc1\u3002 \u7b2c\u5341\u7ae0\u3000\u9ed1\u8863\u5c11\u5973>> \u53e4\u9f99\u300a\u5251\u5ba2\u884c\u300b \u7b2c\u5341\u7ae0\u3000\u9ed1\u8863\u5c11\u5973 \u3000\u3000\u201c\u4e14\u6162\uff01\u201d\u96f7\u5927\u53d4\u6025\u58f0\u559d\u6b62\u3002 \u3000\u3000\u4eba\u5f71\u4e00\u6643\uff0c\u6d1e\u53e3\u4e4b\u5904\u9b3c\u9b41\u4f3c\u7684\u51fa\u73b0\u4e86\u4e00\u6761\u4eba\u5f71\u3002 \u3000\u3000\u5c55\u767d\u76ee\u5149\u89e6\u53ca\u90a3\u6761\u9b3c\u9b45\u4f3c\u7684\u4eba\u5f71\uff0c\u5fc3\u4e2d\u4e0d\u7531\u4e00\u695e\uff01 \u3000\u3000\u539f\u6765\u8fd9\u9b3c\u9b45\u4f3c\u7684\u4eba\u5f71\uff0c\u7ea4\u8170\u4e00\u675f\uff0c\u4f53\u6001\u5982\u67f3\uff0c\u5934\u4e0a\u53d1\u9afb\u9ad8\u633d\uff0c\u4e00\u88ad\u67d4\u8f6f\u7684\u9ed1\u7ef8\u8863\u886b\u968f\u98ce \u5fae\u626c\uff0c\u8138\u4e0a\u8499\u7740\u4e00\u65b9\u9ed1\u7eb1\uff0c\u53cc\u624b\u80a4\u767d\u5982\u7389\uff0c\u6b63\u662f\u51cc\u98ce\u516c\u5b50\u623f\u4e2d\u51fa\u73b0\u8fc7\u7684\u795e\u79d8\u9ed1\u8863\u5973\u90ce\uff01 \u3000\u3000\u96f7\u5927\u53d4\u89c1\u8fd9\u795e\u79d8\u7684\u9ed1\u8863\u5973\u90ce\uff0c\u5728\u6b64\u51fa\u73b0\uff0c\u8138\u4e0a\u73b0\u51fa\u4e00\u4e1d\u4e0d\u60a6\u4e4b\u8272\uff0c\u76b1\u4e86\u76b1\u7709\uff0c\u95ee\u9053\uff1a \u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u8986\u9762\u9ed1\u7eb1\u4e4b\u4e2d\uff0c\u9ed1\u8863\u5c11\u5973\u5982\u6c34\u79cb\u6ce2\uff0c\u5411\u5c55\u767d\u98de\u5feb\u5730\u4e00\u77a5\uff0c\u5634\u4e2d\u5374\u51b0\u51b7\u5730\u8bf4\u9053\uff1a\u201c\u5f1f\u5f1f\u548c \u59b9\u59b9\u6253\u8d77\u6765\u4e86\u3002\u201d \u3000\u3000\u201c\u4f60\u600e\u4e48\u4e0d\u7ba1\uff1f\u201d\u96f7\u5927\u53d4\u4f3c\u4e4e\u9887\u4e3a\u5173\u5fc3\u3002 \u3000\u3000\u201c\u6211\u7ba1\u4e0d\u4e86\uff01\u201d\u9ed1\u8863\u5c11\u5973\u4ecd\u7136\u662f\u51b0\u51b7\u7684\u8bed\u8c03\u3002 \u3000\u3000\u201c\u54fc\uff01\u201d\u96f7\u5927\u53d4\u50cf\u662f\u4e0d\u4fe1\uff0c\u6c89\u54fc\u4e86\u4e00\u58f0\uff0c\u53c8\u95ee\u9053\uff1a\u201c\u4f60\u6bcd\u4eb2\u5462\uff1f\u201d \u3000\u3000\u201c\u4ed6\u66f4\u4e0d\u542c\u6bcd\u4eb2\u7684\u8bdd\uff01\u201d \u3000\u3000\u201c\u4f60\u7236\u4eb2\uff0c\u8fd8\u6709\u522b\u7684\u4eba\uff01\u201d\u9ed1\u8863\u5c11\u5973\u4e00\u8d2f\u51b0\u51b7\u8bed\u8c03\uff0c\u4f3c\u4e4e\u5df2\u60f9\u8d77\u96f7\u5927\u53d4\u7684\u4e0d\u5feb\uff0c\u8bed\u6c14\u4e2d \u6709\u70b9\u4e0d\u8010\u70e6\u5730\u8bf4\uff1a\u201c\u96be\u9053\u4f60\u5bb6\u7684\u4e8b\uff0c\u975e\u8981\u627e\u6211\u4e0d\u884c\u5417\uff1f\u201d \u3000\u3000\u201c\u522b\u4eba\u7ba1\u4e0d\u4e86\uff01\u201d \u3000\u3000\u5c55\u767d\u5728\u4e00\u8fb9\u4e5f\u611f\u5230\u5947\u602a\uff0c\u770b\u96f7\u5927\u53d4\u7684\u60c5\u5f62\uff0c\u5206\u660e\u53c8\u60ca\u53c8\u6025\uff0c\u4f46\u9ed1\u8863\u5c11\u5973\u8bed\u8c03\u5374\u59cb\u7ec8\u662f\u51b0 \u51b7\u51b7\u7684\uff0c\u597d\u4f3c\u6f20\u4e0d\u76f8\u5173\u7684\u795e\u6001\uff0c\u4f55\u51b5\u5979\u8bf4\u7684\u662f\u5979\u81ea\u5df1\u7684\u540c\u80de\u5144\u59b9\u4e4b\u95f4\u7684\u4e8b\u5462\uff01 \u3000\u3000\u5c55\u767d\u5173\u5207\u7684\u53ea\u662f\u5a49\u513f\uff0c\u90a3\u5929\u771f\u672a\u51ff\u7684\u5c11\u5973\uff0c\u662f\u4e0d\u662f\u4e3a\u81ea\u5df1\u8ddf\u5979\u5014\u50b2\u65e0\u60c5\u7684\u54e5\u54e5\u6253\u8d77\u6765 \u4e86\uff1f \u3000\u3000\u201c\u6211\u53bb\u770b\u770b\u3002\u201d\u96f7\u5927\u53d4\u695e\u4e86\u4e00\u4e0b\uff0c\u663e\u7136\u662f\u5173\u5fc3\u5a49\u513f\u3002\u53c8\u8f6c\u5934\u5bf9\u5c55\u767d\u8c03\u5934\u201c\u4f60\u5728\u6b64\u5730\u7b49 \u6211\uff01\u201d \u3000\u3000\u8bf4\u7f62\u53c8\u793a\u610f\u5c55\u767d\uff0c\u628a\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\u6536\u8d77\u6765\u3002\u8eab\u5f62\u4e00\u6643\uff0c\u987e\u957f\u7684\u8eab\u5f62\uff0c\u5c31\u5728\u9ed1\u8863\u5c11 \u5973\u7ad9\u5728\u6d1e\u53e3\u7a7a\u9699\u4e4b\u5904\uff0c\u5982\u4e00\u7f15\u8f7b\u70df\u822c\u5730\u98de\u4e86\u51fa\u53bbI\u5728\u96f7\u5927\u53d4\u9a70\u53bb\u4e4b\u540e\uff0c\u9ed1\u8863\u5c11\u5973\u5e76\u6ca1\u6709\u968f\u7740 \u8d70\u5f00\uff0c\u5374\u4e00\u504f\u8eab\u5411\u6d1e\u5185\u8de8\u4e86\u4e00\u6b65\uff0c\u659c\u8eab\u501a\u5728\u77f3\u58c1\u4e0a\uff0c\u4e00\u53cc\u5982\u6c34\u7684\u7f8e\u76ee\u7d27\u7d27\u5730\u76ef\u4f4f\u5c55\u767d\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u6d1e\u5916\u660e\u4eae\u7684\u5149\u7ebf\uff0c\u659c\u5c04\u5728\u9ed1\u8863\u5c11\u5973\u7684\u8138\u4e0a\u3002\u867d\u7136\u5979\u7684\u6a31\u53e3\u4e0e\u7476\u9f3b\u88ab\u9ed1\u7eb1\u63a9\u4f4f\u4e86\uff0c \u4f46\u9ed1\u7eb1\u4e0a\u9762\u6240\u9732\u51fa\u7684\u6625\u5c71\u9edb\u7709\uff0c\u5982\u6c34\u79cb\u6ce2\uff0c\u88ab\u659c\u5c04\u7684\u5149\u7ebf\u4e00\u7167\uff0c\u8d8a\u53d1\u663e\u5f97\u7709\u76ee\u5982\u753b\uff0c\u7f8e\u4e3d\u5f97 \u4ee4\u4eba\u7729\u76ee\u3001\u795e\u593a\uff01 \u3000\u3000\u201c\u59d1\u5a18\uff0c\u8bf7\u91cc\u8fb9\u5750\u2026\u2026\u201d\u5c55\u767d\u88ab\u9ed1\u8863\u5c11\u5973\u7f8e\u76ee\u60c5\u5206\u5730\u76ef\u7740\u4e00\u77a7\uff0c\u76ee\u7729\u795e\u6447\uff0c\u60f3\u8bf4\u65ec\u5ba2\u6c14 \u8bdd\u4ee5\u63a9\u7a98\u6001\u3002\u8c01\u77e5\u8bdd\u4e00\u51fa\u53e3\uff0c\u624d\u60f3\u5230\u5728\u8fd9\u8352\u5c71\u91ce\u6d1e\u91cc\uff0c\u81ea\u5df2\u662f\u4e00\u4e2a\u5b64\u7537\uff0c\u600e\u597d\u8bf7\u4eba\u5bb6\u4e00\u4e2a\u9ec4 \u82b1\u5c11\u5973\u5230\u6d1e\u91cc\u8fb9\u6765\u5750\uff1f\u60f3\u5230\u8fd9\u91cc\uff0c\u795e\u60c5\u66f4\u663e\u5f97\u5c34\u5c2c\u4e86\uff0c\u4e0d\u7531\u5f97\u7528\u624b\u6293\u6293\u9f3b\u5b50\uff0c\u53c8\u6478\u6478\u8033\u6735\uff0c \u504f\u504f\u4ed6\u624b\u4e2d\u53c8\u62ff\u7740\u90a3\u672c\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u7737\u300b\uff0c\u6ca1\u4e2a\u653e\u5904\u3002 \u3000\u3000\u201c\u4f60\u624b\u4e2d\u62ff\u7684\u662f\u4ec0\u4e48\uff1f\u53ef\u4e0d\u53ef\u4ee5\u7ed9\u6211\u770b\u770b\uff1f\u201d \u3000\u3000\u9ed1\u8863\u5c11\u5973\u76ee\u5149\u6295\u5728\u8272\u5f69\u7f24\u7eb7\u7684\u5377\u4e0a\u3002\u5c55\u767d\u624d\u731b\u7136\u60ca\u609f\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u7737\u300b\u4e2d\uff0c\u6625\u8272\u65e0\u8fb9 \u7684\u753b\u9762\uff0c\u662f\u4e07\u4e07\u4e0d\u80fd\u7ed9\u4e00\u4e2a\u5c11\u5973\u770b\u7684\uff0c\u4e0d\u7531\u6025\u5f97\u5fd9\u5411\u6000\u91cc\u63e3\uff0c\u4e00\u8fb9\u6025\u5f97\u7ed3\u7ed3\u5df4\u5df4\u5730\u8bf4\uff1a\u6ca1 \u6709\xb7\u2026\u3002\u6ca1\u6709\u4ec0\u4e48\uff01\u201c\u201d\u85cf\u4ec0\u4e48\uff1f\u201c\u601d\u8863\u5c11\u5973\u9ed1\u6f06\u6f06\u7684\u77b36\u4ec1\u4e00\u629b\uff0c\u7ed9\u4e86\u5c55\u767d\u4e00\u4e2a\u767d\u773c\uff0c\u5e26 \u7740\u5351\u89c6\u7684\u53e3\u543b\u8bf4\u9053\uff1a\u201c\u6211\u53ea\u662f\u770b\u770b\uff0c\u53c8\u4e0d\u8981\u4f60\u7684\uff0c\u5c0f\u6c14\u9b3c\uff01\u201d \u3000\u3000\u201c\u8fd9\u2026\u2026\u8fd9\u2026\u2026\u59d1\u5a18\u4e0d\u80fd\u770b\u2026\u2026\u201d \u3000\u3000\u5c55\u767d\u751f\u5177\u50b2\u9aa8\uff0c\u5341\u6570\u5e74\u7684\u98d8\u96f6\u8eab\u4e16\uff0c\u53d7\u5c3d\u4e86\u51b7\u843d\u4e0e\u81ea\u773c\uff0c\u6700\u6015\u53d7\u4eba\u5351\u89c6\uff0c\u800c\u9ed1\u8863\u5c11\u5973\u8fd9 \u51e0\u53e5\u8bdd\uff0c\u5374\u6b63\u523a\u4f24\u4e86\u4ed6\u7684\u75db\u5904\uff0c\u5982\u82e5\u662f\u522b\u4e8b\uff0c\u4ed6\u62fc\u547d\u4e5f\u4e0d\u60dc\u7684\u3002\u53ef\u662f\uff0c\u8fd9\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b \u5377\u300b\uff0c\u4ed6\u662f\u8bf4\u4ec0\u4e48\u4e5f\u6ca1\u6709\u80c6\u91cf\u62ff\u7ed9\u6444\u8863\u5c11\u5973\u770b\u7684\u3002 \u3000\u3000\u201c\u54fc\uff01\u201d\u9ed1\u8863\u5c11\u5973\u76b1\u8d77\u7476\u9f3b\u54fc\u4e86\u4e00\u58f0\uff0c\u8bed\u6c14\u66f4\u662f\u51b0\u51b7\uff1a\u201c\u6211\u4ece\u6765\u6ca1\u6709\u6c42\u8fc7\u4eba\uff0c\u6c49\u60f3\u5230\u7b2c \u4e00\u6b21\u6c42\u4eba\u5c31\u78b0\u4e86\u9489\u5b50\u3002\u6211\u6551\u8fc7\u4f60\u4e00\u547d\uff0c\u51ed\u8fd9\u4e00\u70b9\uff0c\u4f60\u4e5f\u975e\u8981\u7ed9\u6211\u770b\u770b\u4e0d\u53ef\uff01\u201d \u3000\u3000\u9ed1\u8863\u5c11\u5973\u8bf4\u81f3\u6b64\u5904\uff0c\u8471\u6b65\u8f7b\u79fb\uff0c\u8885\u8885\u5a1c\u5a1c\u5730\u5411\u5c55\u767d\u8d70\u6765\uff0c\u800c\u4e14\u767d\u5982\u51dd\u8102\u7684\u7389\u624b\u4e00\u4f38\uff0c\u51b7 \u51b7\u5730\u8bf4\u9053\uff1a\u201c\u62ff\u6765\uff01\u201d \u3000\u3000\u5c55\u767d\u55c5\u5230\u4e00\u822c\u4f3c\u5170\u4f3c\u9e9d\u7684\u9e9d\u9999\uff0c\u968f\u7740\u9ed1\u8863\u5c11\u5973\u4e00\u62ac\u624b\u4e4b\u95f4\uff0c\u51b2\u8fdb\u9f3b\u7aef\uff0c\u53c8\u89c1\u5979\u5982\u6c34\u7684\u79cb \u6ce2\u7d27\u7d27\u76ef\u7740\u81ea\u5df1\uff0c\u4e00\u8fb9\u610f\u4e71\u60c5\u8ff7\uff0c\u4e00\u8fb9\u6b62\u4e0d\u4f4f\u8fde\u8fde\u540e\u9000\uff0c\u5634\u91cc\u8bf4\u9053\uff1a\u59d1\u5a18\u2026\u2026\u5b9e\u5728\u4e0d\u80fd \u770b\u2026\u2026\u201c\u9ed1\u8863\u5c11\u5973\u89c1\u5c55\u767d\u771f\u4e0d\u7ed9\u5979\u9762\u5b50\uff0c\u811a\u5c16\u4e00\u70b9\u52b2\uff0c\u8eab\u5f62\u6bd4\u7535\u8fd8\u75be\uff0c\u6b3a\u8fd1\u5c55\u767d\uff0c\u540c\u65f6\uff0c\u5de6 \u624b\u5e76\u4e8c\u6307\uff0c\u76f4\u70b9\u5c55\u767d\u53cc\u76ee\u5de6\u624b\u5374\u7528\u201d\u53f6\u5e95\u5077\u6843\u201c\u62db\u5f0f\uff0c\u76f4\u62a2\u5c55\u767d\u624b\u4e2d\u62ff\u7684\u300a\u5929\u4f5b\u5377\u300b\u3002\u9ed1\u8863 \u5c11\u5973\u7a81\u7136\u51fa\u62db\uff0c\u8eab\u6cd5\u4e0e\u62db\u5f0f\uff0c\u90fd\u662f\u5947\u5feb\u65e0\u6bd4\uff0c\u5c55\u767d\u65e0\u5907\uff0c\u84e6\u611f\u773c\u524d\u4e00\u82b1\uff0c\u52b2\u98ce\u88ad\u4f53\uff0c\u9ed1\u8863\u5c11 \u5973\u5df2\u653b\u81f3\u773c\u524d\u3002\u5c55\u767d\u65e0\u6687\u601d\u7d22\uff0c\u5b8c\u5168\u662f\u672c\u80fd\u7684\u53cd\u5e94\uff0c\u53f3\u624b\u4ee5\u300a\u5929\u4f5b\u5377\u300b\u731b\u6572\u9ed1\u8863\u5c11\u5973\u8155\u90e8\u201d \u5173\u5143\u201c\uff0c\u5de6\u638c\u4e0b\u5288\uff0c\u5de7\u7834\u201d\u53f6\u5e95\u5077\u6843\u201c\u5999\u62db\u3002\u9ed1\u8863\u5c11\u5973\u5bb6\u5b66\u6e0a\u6e90\uff0c\u5373\u5f53\u4eca\u6b66\u6797\u4e00\u6d41\u9ad8\u624b\uff0c\u4ea6 \u5c11\u6709\u654c\u624b\uff0c\u82e5\u662f\u5c55\u767d\u672b\u8fdb\u6d1e\u5185\u4e4b\u524d\uff0c\u5c31\u8fd9\u4e00\u62db\uff0c\u5c55\u767d\u4e5f\u4e07\u96be\u8eb2\u95ea\u3002\u4f46\u5c55\u767d\u4e60\u5f97\u300a\u9501\u9aa8\u9500\u9b42\u5929 \u4f5b\u5377\u300b\u4e0a\u6240\u8f7d\uff0c\u65e0\u4e0a\u7684\u4f5b\u5bb6\u5185\u529f\u5410\u7eb3\u5bfc\u5f15\u4e4b\u6cd5\uff0c\u867d\u662f\u5728\u6697\u4e2d\u6478\u7d22\uff0c\u4e4f\u4eba\u6307\u5bfc\uff0c\u800c\u4e14\u65f6\u626b\u751a \u77ed\uff0c\u4f46\u4e5f\u4eca\u975e\u6614\u6bd4\uff0c\u5c31\u5728\u6beb\u65e0\u62db\u5f0f\u7ae0\u6cd5\u53ef\u8a00\u4e4b\u4e0b\uff0c\u51fa\u624b\u4e5f\u5999\u5230\u6beb\u989d\uff0c\u653b\u654c\u4e4b\u5fc5\u6551\uff0c\u903c\u5f97\u9ed1\u8863 \u5c11\u5973\u975e\u8981\u64a4\u62db\u81ea\u4fdd\u4e0d\u53ef\uff01\u4e0d\u8fc7\uff0c\u9ed1\u8863\u5c11\u5973\u6b66\u529f\u6bd4\u5c55\u767d\u5b9e\u5728\u9ad8\u51fa\u751a\u591a\uff0c\u800c\u4e14\u5c55\u767d\u5185\u529f\u867d\u5df2\u98de\u5feb \u5730\u589e\u52a0\uff0c\u5374\u4e0d\u81ea\u5982\uff0c\u5e76\u4e0d\u80fd\u7075\u6d3b\u8fd0\u7528\uff0c\u4e5f\u5c31\u4e0d\u80fd\u53d1\u6325\u5a01\u529b\u3002\u5728\u5c11\u5973\u7684\u53cc\u624b\u4e4b\u540e\uff0c\u5fae\u4e00\u6014\u795e\uff0c \u9ed1\u8863\u5c11\u5973\u5de6\u624b\u4e00\u7ffb\uff0c\u201d\u5237\u201c\u7684\u4e00\u58f0\uff0c\u5df2\u628a\u5c55\u767d\u62ff\u5728\u53f3\u624b\u4e4b\u4e2d\u7684\u300a\u5929\u4f5b\u5377\u300b\uff0c\u7ed9\u593a\u4e86\u8fc7\u53bb\u3002\u5c55 \u767d\u624b\u5fc3\u4e00\u6ed1\uff0c\u300a\u5929\u4f5b\u5377\u300b\u5df2\u51fa\u624b\uff0c\u9ed1\u8863\u5c11\u5973\u5374\u5df2\u7eb5\u8eab\u63a0\u81f3\u6d1e\u53e3\u4e4b\u5904\u3002\u201d\u6211\u770b\u5230\u5e95\u662f\u4ec0\u4e48\u4e66\uff1f \u8fd9\u6837\u5b9d\u8d35\uff01\u770b\u90fd\u4e0d\u820d\u5f97\u7ed9\u4eba\u770b\u2026\u2026\u2018\u9ed1\u8863\u5c11\u5973\u624b\u62ff\u300a\u5929\u4f5b\u5377\u300b\uff0c\u4e00\u8fb9\u8bf4\uff0c\u4e00\u8fb9\u8f7b\u79fb\u83b2\u6b65\u5411\u6d1e \u5916\u8d70\u53bb\uff0c\u540c\u65f6\u4ee5\u767d\u7389\u822c\u7ea4\u7ea4\u624b\u6307\u5f00\u59cb\u7ffb\u9605\u2014\u3002\u2019 \u3000\u3000\u201c\u59d1\u5a18\uff01\u770b\u4e0d\u5f97\uff01\u201d\u5c55\u767d\u5fc3\u4e2d\u5927\u6025\uff0c\u4e00\u8fb9\u53eb\uff0c\u4e00\u8fb9\u7531\u6d1e\u5185\u8ffd\u4e86\u51fa\u6765\uff01 \u3000\u3000\u201c\u5550\uff01\u201d\u9ed1\u8863\u5c11\u5973\u5df2\u628a\u300a\u5929\u4f5b\u5377\u300b\u7ffb\u5f00\uff0c\u53ea\u7740\u4e86\u4e00\u773c\uff0c\u4fbf\u4e0d\u7531\u7c89\u9762\u901a\u7ea2\uff0c\u4f4e\u5550\u4e86\u4e00\u53e3\u3002 \u8bf4\u9053\uff1a\u201c\u8fd9\u6837\u7684\u574f\u4e66\uff01\u8fd8\u7ed9\u4f60\uff01\u201d \u3000\u3000\u201c\u54d7\uff01\u201d\u9ed1\u8863\u5c11\u5973\u4e00\u8fd4\u8eab\uff0c\u628a\u300a\u5929\u4f5b\u5377\u300b\u5411\u6d1e\u5185\u4e22\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u201c\u5657\uff01\u201d\u9ed1\u8863\u5c11\u5973\u8fd4\u8eab\u5feb\uff0c\u5c55\u767d\u51b2\u51fa\u6765\u4e5f\u5feb\uff0c\u4e8c\u4eba\u649e\u4e86\u4e2a\u6ee1\u6000\uff0c\u4e0d\u7531\u540c\u65f6\u60ca\u547c\u51fa\u58f0\uff1a \u201c\u54ce\u5440\uff01\u201d \u3000\u3000\u9ed1\u8863\u5c11\u5973\u88ab\u5c55\u767d\u649e\u8fdb\u6000\u5185\uff0c\u80f8\u524d\u4e00\u9ebb\uff0c\u5468\u8eab\u9165\u4e86\u534a\u8fb9\uff0c\u8fd9\u662f\u5979\u4e00\u751f\u4e2d\u4ece\u672a\u7ecf\u8fc7\u7684\u4e8b\u3002\u4ee5 \u4e00\u4e2a\u9ec4\u82b1\u5904\u5973\uff0c\u88ab\u4e00\u4e2a\u7537\u5b50\u649e\u5728\u6000\u5185\uff0c\u867d\u7136\u4e0d\u592a\u75db\uff0c\u5374\u662f\u53c8\u60ca\u53c8\u7f9e\uff0c\u4e0d\u7981\u4f7f\u5979\u5fc3\u5934\u5c0f\u9e7f\u822c\u7a81 \u7a81\u4e71\u8df3\uff0c\u7acb\u65f6\u4e4b\u95f4\uff0c\u6ee1\u8138\u7ea2\u971e\uff0c\u5446\u5446\u5730\u4e00\u8a00\u4e0d\u53d1\u2026\u2026 \u3000\u3000\u5c55\u767d\u69fd\u7740\u5934\u649e\u5728\u9ed1\u8863\u5c11\u5973\u6000\u5185\uff0c\u53ea\u89c9\u7740\u6696\u7389\u6e29\u9999\u649e\u4e86\u6ee1\u6000\uff0c\u4e00\u822c\u4ece\u672a\u611f\u53d7\u8fc7\u7684\u6ecb\u5473\uff0c\u4f7f \u5f97\u4ed6\u5fc3\u8361\u795e\u6447\uff0c\u6025\u9000\u4e09\u6b65\uff0c\u62ac\u5934\u4e00\u89c1\u9ed1\u8863\u5c11\u5973\u6ee1\u8138\u7ea2\u971e\uff0c\u79cb\u6c34\u822c\u7684\u53cc\u76ee\u660e\u5a9a\u6b32\u6d41\uff0c\u4f3c\u55d4\u4f3c\u6012 \u5730\u671b\u7740\u81ea\u201c\u554a\uff01\u5bf9\u4e0d\u8d77\uff01\u201d\u5c55\u767d\u5230\u5e95\u662f\u4e2a\u5fc3\u65e0\u90aa\u5ff5\u7684\u5927\u5b69\u5b50\uff0c\u4e00\u89c1\u649e\u4e86\u4eba\u5bb6\uff0c\u8d76\u5feb\u8d54\u793c\uff0c\u5411 \u9ed1\u8863\u5c11\u5973\u6df1\u65bd\u4e86\u4e00\u63d6\u3002\u540c\u65f6\uff0c\u53c8\u5f2f\u8170\u628a\u6389\u5728\u5730\u4e0a\u7684\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\u62fe\u4e86\u8d77\u6765\u3002\u5ffd\u542c\u4e00\u58f0 \u9634\u68ee\u7684\u51b7\u7b11\u8d77\u81ea\u8eab\u4fa7\u3002 \u3000\u3000\u8fd9\u58f0\u51b7\u7b11\u6781\u51b7\uff0c\u604d\u5982\u4eca\u4eba\u6709\u5bd2\u7a96\u7684\u611f\u89c9\uff0c\u542c\u5f97\u5c55\u767d\u5fc3\u4e2d\u4e00\u60ca\uff0c\u5f53\u5373\u8f6c\u8138\u671b\u53bb\u3002 \u3000\u3000\u5f85\u5c55\u767d\u4e00\u770b\u6e05\u773c\u524d\u7684\u6001\u52bf\uff0c\u5fc3\u4e2d\u66f4\u52a0\u5403\u60ca\uff0c\u539f\u6765\u4e0d\u77e5\u4f55\u65f6\uff0c\u7ade\u5728\u6d1e\u5916\u4e0d\u8fdc\u7684\u8349\u5730\u4e0a\uff0c\u7ad9 \u4e86\u5341\u6570\u4eba\u4e4b\u591a\uff0c\u4ed6\u7ade\u4e0d\u77e5\u8fd9\u4e9b\u4eba\u662f\u4f55\u65f6\u6765\u7684\u3002 \u3000\u3000\u4e3a\u9996\u4e00\u4eba\uff0c\u7a7f\u4e00\u88ad\u6de1\u84dd\u8272\u4e1d\u888d\uff0c\u957f\u8eab\u7389\u7acb\uff0c\u795e\u60c5\u6f47\u6d12\u5df2\u6781\uff0c\u9762\u76ee\u4e5f\u6781\u4e3a\u82f1\u4fca\uff0c\u53ea\u662f\u5634\u89d2 \u4e0b\u6487\uff0c\u6ee1\u8138\u5bd2\u9716\uff0c\u867d\u5728\u5a07\u9633\u7167\u8000\u4e4b\u4e0b\uff0c\u4ecd\u4f7f\u4eba\u6709\u51b7\u68ee\u68ee\u7684\u611f\u89c9\u3002 \u3000\u3000\u5c55\u767d\u4e00\u770b\u6765\u4eba\uff0c\u6b63\u662f\u5728\u75c5\u4e2d\u8981\u628a\u81ea\u5df1\u4e22\u51fa\u5ba4\u5916\u3001\u5014\u50b2\u65e0\u60c5\u7684\u51cc\u98ce\u516c\u5b50\u2014\u2014\u6155\u5bb9\u627f\u4e1a\uff01 \u3000\u3000\u51cc\u98ce\u516c\u5b50\u8eab\u540e\uff0c\u6709\u516b\u540d\u52b2\u88c5\u4f69\u5200\u5927\u6c49\uff0c\u4e00\u4e2a\u4e2a\u53cc\u773c\u7cbe\u5149\u66b4\u5c04\uff0c\u72e0\u72e0\u5730\u77aa\u4f4f\u5c55\u767d\u3002 \u3000\u3000\u5c55\u767d\u89c1\u8fd9\u516b\u540d\u52b2\u88c5\u4f69\u5200\u5927\u6c49\uff0c\u5176\u4e2d\u5012\u6709\u4e09\u56db\u540d\u773c\u719f\u3002\u8ddf\u968f\u4e2d\u5e74\u8d35\u5987\uff0c\u66fe\u5728\u6797\u4e2d\u4f24\u4e86\u81ea\u5df1 \u4e24\u5200\u7684\u9648\u6e05\u3001\u9648\u5e73\u4e5f\u5728\u5176\u5185\uff0c\u5176\u4f59\u89c9\u5f97\u773c\u719f\u7684\uff0c\u53ef\u80fd\u662f\u5728\u51cc\u98ce\u516c\u5b50\u623f\u4e2d\u89c1\u5230\u8fc7\uff0c\u4f46\u60f3\u4e0d\u51fa\u4ed6 \u4eec\u7684\u540d\u5b57\u3002 \u3000\u3000\u5728\u51cc\u98ce\u516c\u5b50\u53f3\u9996\uff0c\u7ad9\u5b9a\u4e00\u4e2a\u7707\u76ee\u9053\u4eba\uff0c\u7070\u5e03\u9053\u888d\uff0c\u524a\u816e\u5c16\uff0c\u9762\u76ee\u5982\u9e1f\uff0c\u4e24\u53ea\u76f2\u773c\u4e71\u7ffb\uff0c \u53ea\u6709\u773c\u767d\u6ca1\u6709\u77b3\u4ec1\uff0c\u604d\u5982\u4e24\u679a\u767d\u679c\uff0c\u770b\u6765\u9634\u68ee\u53ef\u6016\u3002\u6328\u7740\u7707\u76ee\u9053\u4eba\u7ad9\u5728\u4e00\u4e2a\u4e2d\u5e74\u5112\u751f\uff0c\u5dfe\u5e26 \u98d8\u626c\uff0c\u4e00\u8138\u72c2\u50b2\u4e4b\u6001\u3002 \u3000\u3000\u51cc\u98ce\u516c\u5b50\u5de6\u9996\uff0c\u4e5f\u7ad9\u5b9a\u4e8c\u4eba\uff0c\u4e00\u4e2a\u8138\u8272\u9752\u767d\uff0c\u9876\u4e0a\u65e0\u6bdb\u7684\u65ad\u81c2\u8001\u8005\u3002\u4e00\u4e2a\u4e00\u8eab\u534e\u670d\uff0c\u4f0a \u5982\u8c6a\u8d35\u7684\u5bcc\u7ec5\u3002 \u3000\u3000\u8fd9\u56db\u4e2a\u4eba\u7ad9\u5728\u4e00\u8d77\uff0c\u867d\u7136\u663e\u5f97\u4e0d\u4f26\u4e0d\u7c7b\uff0c\u4f46\u4e00\u4e2a\u4e2a\u592a\u9633\u53cc\u7a74\u9ad8\u9ad8\u9f13\u8d85\uff0c\u9664\u4e86\u90a3\u7707\u76ee\u9053\u4eba \u4e4b\u5916\uff0c\u6bcf\u4e2a\u4eba\u53cc\u76ee\u5f00\u9602\u4e4b\u95f4\uff0c\u4ff1\u90fd\u662f\u7cbe\u5149\u56db\u5c04\uff0c\u5982\u5229\u5203\u822c\u5149\u707c\u523a\u4eba\uff0c\u53ef\u60f3\u800c\u77e5\u90fd\u662f\u6b66\u529f\u9ad8\u5f3a \u4e4b\u5947\u4eba\u5f02\u58eb\u3002 \u3000\u3000\u5c55\u767d\u4e00\u89c1\u51cc\u98ce\u516c\u5b50\u7387\u9886\u591a\u4eba\u524d\u6765\uff0c\u4e00\u65f6\u4e0d\u77e5\u5176\u610f\uff0c\u53ea\u77a7\u77a7\u8fd9\u4e00\u4e2a\uff0c\u53c8\u671b\u671b\u90a3\u4e00\u4e2a\uff0c\u53e3\u4e2d \u5374\u672b\u53d1\u4e00\u8a00\u3002 \u3000\u3000\u201c\u54fc\uff01\u201d\u9ed1\u8863\u5c11\u5973\u4f4e\u54fc\u4e86\u4e00\u58f0\uff0c\u51b7\u7136\u8bf4\u9053\uff1a\u201c\u6b3a\u4fae\u4e86\u59b9\u59b9\uff0c\u53c8\u627e\u59d0\u59d0\u6765\u5566\uff01\u201d \u3000\u3000\u51cc\u98ce\u516c\u5b50\u8f7b\u8f7b\u4e00\u76b1\u7709\uff0c\u4e0d\u7406\u4f1a\u9ed1\u8863\u5c11\u5973\uff0c\u5374\u4ee5\u5014\u50b2\u65e0\u6bd4\u7684\u51b0\u51b7\u58f0\u8c03\uff0c\u5bf9\u5c55\u767d\u8bf4\u9053\uff1a\u201c\u60f3 \u662f\u4f60\u7684\u75c5\u597d\u4e86\u5427\uff1f\u201d \u3000\u3000\u201c\u6258\u798f\uff0c\u5728\u4e0b\u7684\u75c5\u75ca\u6108\u4e86\u3002\u201d\u5c55\u767d\u4e0d\u77e5\u51cc\u98ce\u516c\u5b50\u4e3a\u4f55\u5173\u5fc3\u8d77\u4ed6\u7684\u75c5\u6765\u4e86\uff0c\u53ea\u6709\u636e\u5b9e\u4ee5 \u7b54\u3002 \u3000\u3000\u201c\u4f60\u8fd8\u6709\u4ec0\u4e48\u540e\u4e8b\uff0c\u9700\u8981\u4ea4\u4ee3\u5417\uff1f\u201d\u51cc\u98ce\u516c\u4e88\u8bf4\u6b64\u8bdd\u65f6\uff0c\u5634\u89d2\u7ade\u6d6e\u8d77\u4e00\u4e1d\u7b11\u5bb9\u3002\u4e0d\u8fc7\uff0c \u8fd9\u7b11\u5bb9\u5374\u51b7\u5f97\u4f7f\u4eba\u5fc3\u5934\u53d1\u98a4\u3002 \u3000\u3000\u201c\u2026\u201d\u5c55\u767d\u4e0d\u77e5\u6240\u4e91\uff0c\u4e00\u65f6\u672a\u7b54\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u201c\u4f60\u662f\u88c5\u50bb\uff0c\u8fd8\u662f\u5bb3\u6015\uff1f\u201d\u51cc\u98ce\u516c\u5b50\u5634\u89d2\u4e0b\u6487\uff0c\u7d27\u76ef\u7740\u5c55\u767d\u95ee\u9053\uff1a\u4f60\u4e0d\u8bb0\u5f97\u5728\u6211\u623f\u4e2d\u8bf4 \u7684\u8bdd\u4e86\u5417\uff1f\u201c\u201d\u5927\u4e08\u592b\u751f\u800c\u4f55\u6b22\uff0c\u6b7b\u800c\u4f55\u60e7\uff1f\u201c\u51cc\u98ce\u516c\u4e8e\u7840\u7840\u903c\u4eba\uff0c\u6fc0\u8d77\u4e86\u5c55\u767d\u7684\u6012\u706b\uff0c\u4e0d \u7531\u50b2\u7136\u7b54\u9053\uff1a\u201c\u5728\u4e0b\u4ece\u6765\u6ca1\u6709\u6015\u8fc7\u4ec0\u4e48\uff0c\u66f4\u4e0d\u77e5\u516c\u4e8e\u662f\u6307\u4f55\u800c\u8bf4\uff1f\u201d \u3000\u3000\u201c\u54c8\u54c8\u54c8\uff01\u2026\u2026\u201d\u5c55\u767d\u6b64\u8bdd\u4e00\u51fa\u53e3\uff0c\u51cc\u98ce\u516c\u4e8e\u5c1a\u672a\u7b54\u8a00\uff0c\u7ad9\u5728\u4e00\u65c1\u7684\u72c2\u50b2\u4e66\u751f\uff0c\u7a81\u7136\u53d1 \u51fa\u4e00\u58f0\u9ad8\u4ea2\u5165\u4e91\u7684\u72c2\u7b11\uff0c\u7b11\u58f0\u55e1\u7136\u9707\u8033\uff0c\u663e\u7136\u8fd9\u4e2d\u5e74\u72c2\u751f\u5185\u529f\u9ad8\u6df1\u96be\u6d4b\u3002 \u3000\u3000\u201c\u80ce\u6bdb\u672a\u5e72\u7684\u5c0f\u4e8e\uff0c\u7adf\u6562\u5bf9\u516c\u5b50\u5982\u6b64\u8bf4\u8bdd\uff1f\u60f3\u662f\u6d3b\u5f97\u4e0d\u8010\u70e6\u4e86\uff01\u201d \u3000\u3000\u4e2d\u5e74\u72c2\u751f\u8bf4\u7f62\uff0c\u5389\u76ee\u4e00\u7741\uff0c\u5947\u5149\u5982\u7535\uff0c\u888d\u8896\u4e00\u7529\uff0c\u5f80\u524d\u8d70\u4e86\u4e24\u6b65\uff0c\u770b\u6837\u5b50\u5c31\u8981\u52a8\u624b\u3002 \u3000\u3000\u5c55\u767d\u88ab\u4e2d\u4e2d\u72c2\u751f\u5982\u5229\u5203\u7684\u773c\u5149\u4e00\u77aa\uff0c\u5fc3\u91cc\u4e0d\u7981\u4e00\u5bd2\u3002\u4f46\u4ed6\u662f\u4e2a\u5b81\u6298\u4e0d\u5c48\u7684\u4eba\uff0c\u867d\u660e\u77e5\u4e0d \u654c\uff0c\u4f9d\u7136\u6beb\u4e0d\u754f\u7f29\u5730\u7ad9\u5728\u90a3\u91cc\uff0c\u5e76\u6697\u4e2d\u8fd0\u529f\u6212\u5907\uff0c\u7334\u5907\u968f\u65f6\u4e00\u62fc\u3002 \u3000\u3000\u8c01\u77e5\u4e2d\u5e74\u72c2\u751f\u5c1a\u672a\u51fa\u624b\uff0c\u51cc\u98ce\u516c\u5b50\u8eab\u540e\u7684\u516b\u540d\u52b2\u88c5\u4f69\u5200\u5927\u6c49\u4e4b\u4e2d\uff0c\u5374\u8e7f\u51fa\u4e8c\u4eba\uff0c\u5411\u7740\u51cc \u98ce\u516c\u5b50\u4e00\u62f1\u624b\uff0c\u8eac\u8eab\u8bf4\u9053\uff1a\u201c\u516c\u5b50\u7237\uff0c\u5c0f\u7684\u53bb\u628a\u6b64\u4eba\u64d2\u6765\uff01\u201d \u3000\u3000\u5c55\u767d\u4e00\u770b\uff0c\u8e7f\u51fa\u7684\u4e24\u540d\u52b2\u88c5\u5927\u6c49\uff0c\u6b63\u662f\u5728\u6811\u6797\u4e2d\u4f24\u4e86\u81ea\u5df2\u4e24\u5200\u7684\u9648\u6e05\u3001\u9648\u5e73\uff0c\u4e0d\u7531\u5fc3\u4e2d \u66f4\u6c14\uff0c\u6697\u9053\uff1a\u201c\u771f\u662f\u65f6\u8870\u88ab\u72d7\u6b3a\u4e86\uff0c\u8fde\u4e24\u4e2a\u5974\u624d\u90fd\u8fd9\u822c\u77a7\u4e0d\u8d77\u81ea\u5df1\u2026\u2026\u201d \u3000\u3000\u51cc\u98ce\u516c\u5b50\u4f4e\u5934\u77a7\u4e86\u4e8c\u4eba\u4e00\u773c\uff0c\u51b7\u50b2\u5730\u8bf4\u9053\uff1a\u201c\u8981\u6d3b\u7684\u4e0d\u8981\u6b7b\u7684\uff01\u201d \u3000\u3000\u5c31\u8fd9\u4e00\u53e5\u8bdd\uff0c\u66f4\u628a\u5c55\u81ea\u6c14\u5f97\u70ed\u8840\u6cb8\u817e\u2026\u2026 \u3000\u3000\u201c\u78b4\uff01\u201d\u9648\u6e05\u3001\u9648\u4e4e\u53cc\u53cc\u5e94\u4e86\u4e00\u58f0\uff0c\u53c8\u5bf9\u4e2d\u5e74\u72c2\u751f\u4e00\u62b1\u62f3\uff0c\u8bf4\u9053\uff1a\u201c\u60f3\u8fd9\u4e48\u4e00\u4e2a\u65e0\u540d\u5c0f \u5352\uff0c\u4f55\u9700\u4e8c\u7237\u51fa\u624b\uff0c\u770b\u5c0f\u7684\u53bb\u628a\u4ed6\u64d2\u6765\uff01\u201d \u3000\u3000\u201c\u54c8\u54c8\u54c8\uff01\u201d\u4e2d\u5e74\u72c2\u751f\u53c8\u662f\u4e00\u9635\u72c2\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u53bb\u4e00\u4e2a\u5c31\u591f\u4e86\uff0c\u7528\u4e0d\u7740\u4e24\u4e2a\u9f50\u4e0a\uff01\u201d \u3000\u3000\u5c55\u767d\u4e00\u542c\uff0c\u8fd9\u4e2a\u6c14\u5c31\u66f4\u5927\u4e86\uff0c\u6697\u60f3\uff1a\u201c\u773c\u524d\u4e4b\u4eba\uff0c\u4e00\u4e2a\u4e2a\u90fd\u662f\u8fd9\u822c\u5c0f\u77a7\u81ea\u5df1\uff0c\u81ea\u5df2\u7eb5\u7136 \u4e0d\u654c\uff0c\u4e5f\u8981\u62fc\u6389\u4ed6\u4e00\u4e2a\u4e24\u4e2a\u2026\u2026\u201d \u3000\u3000\u9648\u6e05\u3001\u9648\u5e73\u542c\u4e2d\u5e74\u72c2\u751f\u8fd9\u4e00\u8bf4\uff0c\u4f8b\u771f\u4e0d\u597d\u610f\u601d\u4e8c\u4eba\u540c\u65f6\u51fa\u624b\u4e86\u3002\u9648\u6e05\u201c\u545b\u54ea\u201d\u4e00\u58f0\uff0c\u62bd \u51fa\u808b\u4e0b\u4f69\u5200\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u7531\u6211\u6765\uff01\u201d \u3000\u3000\u9648\u6e05\u9b3c\u5934\u5200\u51fa\u9798\uff0c\u4e00\u4e2a\u864e\u6b65\u8dc3\u81f3\u5c55\u767d\u9762\u524d\uff0c\u7528\u5200\u5931\u4e00\u6307\u5c55\u767d\u9f3b\u6881\uff0c\u559d\u9053\uff1a\u201c\u4eae\u5175\u5668 \u5427\uff01\u201d \u3000\u3000\u5c55\u767d\u89c1\u9648\u6eb6\u5bf9\u51cc\u98ce\u516c\u5b50\u4e0e\u4e2d\u5e74\u72c2\u751f\u90a3\u4efd\u5974\u624d\u50cf\uff0c\u5bf9\u7740\u81ea\u5df1\u5374\u5982\u6b64\u8000\u6b66\u626c\u5a01\uff01\u53c8\u60f3\u5230\u81ea\u5df1 \u5728\u75c5\u4e2d\u88ab\u4ed6\u4eec\u4e09\u4eba\u56f4\u653b\uff0c\u8fde\u780d\u4e86\u81ea\u5df1\u4e24\u5200\u7684\u4ec7\u6068\uff0c\u4e0d\u7531\u6012\u706b\u9ad8\u70e7\uff0c\u563f\u563f\u8bf4\u9053\uff1a\u201c\u8ddf\u4f60\u8fd9\u5974\u624d \u52a8\u624b\uff0c\u7528\u4e0d\u7740\u62ff\u5175\u5668\uff0c\u5c0f\u7237\u7a7a\u624b\u5949\u966a\u597d\u4e86\uff01\u201d\u5176\u5b9e\uff0c\u5c55\u767d\u7684\u5bb6\u4f20\u81f3\u5b9d\u201c\u65e0\u60c5\u78a7\u5251\u201d\uff0c\u5df2\u5728\u5b89 \u4e50\u516c\u5b50\u624b\u4e2d\u9057\u5931\uff0c\u6b64\u65f6\u60f3\u7528\u5175\u5668\u4e5f\u6ca1\u6709\u3002\u4e0d\u8fc7\uff0c\u4ed6\u89c1\u9648\u6e05\u7684\u52b2\u72c2\uff0c\u5b9e\u5728\u5fcd\u4e0d\u4e0b\u8fd9\u53e3\u6c14\uff0c\u7ade\u7528 \u4e86\u4e2a\u201c\u4ee5\u5b50\u4e4b\u77db\uff0c\u653b\u5b50\u4e4b\u76fe\u201d\u7684\u529e\u6cd5\uff0c\u7ed9\u4ed6\u6765\u4e86\u4e2a\u66f4\u72c2\uff0c\u66f4\u9a84\u50b2\uff01 \u3000\u3000\u5c55\u767d\u6b64\u8bdd\u4e00\u51fa\u53e3\uff0c\u51cc\u98ce\u516c\u5b50\u3001\u4e2d\u5e74\u72c2\u751f\u4e0d\u5fc5\u8bf4\uff0c\u51e1\u662f\u968f\u7740\u51cc\u98ce\u516c\u5b50\u4e00\u8d77\u6765\u7684\u4eba\uff0c\u83ab\u4e0d\u9aa4 \u7136\u8272\u53d8\uff1b\u9648\u6e05\u8138\u4e0a\u66f4\u662f\u6302\u4e0d\u4f4f\uff0c\u5927\u543c\u4e00\u58f0\u53eb\u9053\uff1a\u201c\u597d\u72c2\u7684\u5c0f\u4e8e\uff01\u770b\u5200\uff01\u201d \u3000\u3000\u9648\u6e05\u90a3\u5929\u5728\u677e\u6797\u5185\uff0c\u66fe\u9886\u6559\u8fc7\u5c55\u767d\u62f3\u638c\u4e0a\u7684\u529f\u592b\uff0c\u90a3\u5929\u662f\u4e09\u6253\u4e00\uff0c\u8fd8\u6ca1\u8ba8\u4e86\u597d\u53bb\uff0c\u5982\u4eca \u4e00\u5bf9\u4e00\uff0c\u5728\u62f3\u638c\u4e0a\u81ea\u5df1\u51b3\u4e0d\u662f\u773c\u524d\u5c11\u5e74\u7684\u5bf9\u624b\u3002\u56e0\u6b64\uff0c\u5c55\u767d\u867d\u7528\u8bdd\u6316\u82eb\u4ed6\uff0c\u4ed6\u4ecd\u4e0d\u6562\u5f92\u624b\u4e0e \u5c55\u767d\u76f8\u640f\u3002\u4e00\u58f0\u66b4\u559d\u4e4b\u540e\uff0c\u624b\u4e2d\u9b3c\u5934\u5200\u4e00\u7d27\uff0c\u5c31\u8981\u4e0a\u524d\u52a8\u624b\uff0c\u5fc3\u60f3\uff1a\u597d\u6b79\u6eaf\u4ed6\u4e00\u5200\u4e24\u5200\u7684\uff0c \u4e5f\u5f97\u5728\u4eba\u524d\u51fa\u51fa\u8fd9\u53e3\u7a9d\u56ca\u6c14\u2026\u2026 \u3000\u3000\u201c\u4f4f\u624b\uff01\u201d \u3000\u3000\u8c01\u77e5\u9648\u6e05\u9b3c\u5934\u5200\u5c1a\u672a\u4eae\u62db\uff0c\u9ed1\u8863\u5c11\u5973\u5374\u7a81\u7136\u5a07\u5428\u9053\uff1a\u201c\u9648\u6e05\uff0c\u4f60\u8981\u4e0d\u8981\u8138\uff1f\u62ff\u5175\u5668\u548c\u4eba \u5bb6\u5f92\u624b\u6253\uff01\u201d \u3000\u3000\u9648\u6e05\u95fb\u58f0\uff0c\u8138\u4e0a\u4e00\u9635\u767d\u4e00\u9635\u7ea2\uff0c\u624b\u62ff\u9b3c\u5934\u5200\uff0c\u4e0a\u4e5f\u4e0d\u662f\uff0c\u9000\u4e5f\u4e0d\u662f\uff0c\u4e00\u65f6\u4e4b\u95f4\u5446\u4e86\u2026\u2026 \u3000\u3000\u201c\u8fd9\u91cc\u4e8b\u4e0d\u8981\u4f60\u7ba1\uff01\u201d\u51cc\u98ce\u516c\u5b50\u76b1\u7709\u8bf4\u9053\uff1a\u201c\u4f60\u4e0e\u964c\u751f\u7537\u5b50\u5355\u72ec\u76f8\u5904\uff0c\u6211\u4e0d\u8bf4\u4f60\uff0c\u4e5f\u5c31 \u591f\u4e86\uff0c\u8fd8\u5728\u4e00\u8fb9\u591a\u5634\uff0c\u96be\u9053\u4e0d\u77e5\u7f9e\u5417\uff1f\u201d \u3000\u3000\u9ed1\u8863\u5c11\u5973\u6c14\u5f97\u5468\u8eab\u53d1\u6296\uff0c\u90a3\u4e48\u51b7\u50b2\u9547\u9759\u7684\u4eba\uff0c\u7adf\u6c14\u5f97\u8bed\u4e0d\u6210\u58f0\uff0c\u4ee5\u767d\u7389\u822c\u7684\u7ea4\u6307\uff0c\u6307\u7740 \u51cc\u98ce\u516c\u4e8e\uff0c\u201c\u4f60\u2026\u2026\u4f60\u2026\u2026\u201d\u4e86\u534a\u5929\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u201c\u9648\u6e05\uff01\u52a8\u624b\uff01\u201d\u51cc\u98ce\u516c\u5b50\u5374\u4e0d\u7ba1\u8fd9\u4e9b\uff0c\u51b7\u51b7\u559d\u4ee4\u9648\u6e05\u52a8\u624b\u3002 \u3000\u3000\u9648\u6e05\u6b63\u5728\u8fdb\u9000\u7ef4\u8c37\u7684\u5f53\u513f\uff0c\u51cc\u98ce\u516c\u5b50\u4e00\u58f0\u4ee4\u4e0b\uff0c\u5982\u5949\u7eb6\u97f3\uff0c\u63d0\u8d77\u4eae\u5149\u5149\u7684\u9b3c\u5934\u5200\uff0c\u6402\u5934 \u76d6\u9876\u5411\u7740\u5c55\u767d\u780d\u4e0b\uff01 \u3000\u3000\u5c55\u767d\u65e9\u5df2\u84c4\u6c14\u5f85\u654c\uff0c\u89c1\u9648\u6e05\u4e00\u5200\u780d\u6765\uff0c\u5fae\u4e00\u504f\u5934\uff0c\u8ba9\u8fc7\u5200\u950b\uff0c\u201c\u529b\u5288\u534e\u5c71\u201d\uff0c\u5f53\u80f8\u4e00\u638c \u5411\u9648\u6e05\u6253\u53bb\uff01 \u3000\u3000\u201c\u562d\uff01\u201d\u4e00\u58f0\u5927\u9707\uff0c\u5c55\u81ea\u8fd9\u5168\u529b\u5288\u51fa\u7684\u4e00\u638c\uff0c\u7ed3\u7ed3\u5b9e\u5b9e\u5730\u6253\u5728\u9648\u6e05\u524d\u80f8\u4e4b\u4e0a\uff01 \u3000\u3000\u9648\u6e05\u201c\u542d\u201d\u7684\u4e00\u58f0\uff0c\u524d\u80f8\u5982\u53d7\u4e07\u65a4\u91cd\u9524\uff0c\u4e00\u526f\u9ad8\u5927\u8eab\u8eaf\uff0c\u76f4\u88ab\u5c55\u767d\u4e00\u638c\u9707\u98de\u4e09\u4e08\u5f00\u5916\uff01 \u3000\u3000\u4f17\u4eba\u84e6\u7136\u60ca\u987e\uff0c\u9648\u6e05\u5df2\u53e3\u55b7\u9c9c\u8840\uff0c\u6454\u5728\u5730\u4e0a\u6b7b\u53bb\uff0c\u201c\u5f53\u5577\u201d\u8fde\u54cd\uff0c\u4e00\u67c4\u660e\u6643\u6643\u7684\u9b3c\u5934\u5200 \u4e5f\u629b\u51fa\u8001\u8fdc\u2026\u2026 \u3000\u3000\u4e5f\u5c31\u662f\u5728\u4f17\u4eba\u5fae\u4e00\u60ca\u6115\u4e4b\u95f4\uff0c\u8033\u8fb9\u53c8\u4f20\u6765\u201c\u7830\uff01\u8a07\uff01\u201d\u4e00\u9635\u8fde\u54cd\uff0c\u539f\u6765\u662f\u9648\u5e73\u89c1\u4e43\u5144\u60e8 \u6b7b\u5728\u5c55\u767d\u638c\u4e0b\uff0c\u60b2\u6124\u586b\u8150\uff0c\u731b\u7136\u5411\u5c55\u767d\u540e\u5fc3\u6eaf\u4e86\u4e00\u5200\uff1b\u5c55\u767d\u542c\u5230\u8eab\u540e\u91d1\u5203\u5288\u98ce\uff0c\u77e5\u9053\u6709\u4eba\u6697 \u88ad\uff0c\u4ed6\u81ea\u77e5\u5728\u8fd9\u6837\u591a\u6b66\u6797\u9ad8\u624b\u73af\u4f3a\u4e4b\u4e0b\uff0c\u4e07\u96be\u8ba8\u4e86\u597d\u5904\uff0c\u65e9\u7acb\u4e0b\u62fc\u547d\u4e4b\u5fc3\uff0c\u56e0\u4e3a\u4ed6\u6ee1\u8179\u60b2 \u6124\uff0c\u518d\u4e5f\u53d7\u4e0d\u4f4f\u522b\u4eba\u7684\u4fae\u8fb1\u4e86\uff0c\u5c24\u5176\u662f\u5f53\u7740\u9ed1\u8863\u5c11\u5973\u7684\u9762\u524d\uff0c\u4ed6\u4e5f\u4e0d\u77e5\u9053\u662f\u4e00\u79cd\u4ec0\u4e48\u5fc3\u7406\uff0c \u53ea\u77e5\u9053\u201c\u5a01\u6b66\u4e0d\u5c48\u201d\uff0c\u8c01\u6765\u8ddf\u8c01\u62fc\u3002\u56e0\u6b64\uff0c\u5728\u542c\u5230\u8111\u540e\u91d1\u5203\u5288\u98ce\u4e4b\u58f0\uff0c\u4ed6\u4e0d\u95ea\u4e0d\u8eb2\uff0c\u76f4\u5f85\u9648 \u5e73\u624b\u4e2d\u9b3c\u5934\u5200\u7684\u53ca\u8def\u773c\u770b\u8981\u523a\u8fdb\u4ed6\u7684\u540e\u5fc3\u4e4b\u9645\uff0c\u4ed6\u624d\u731b\u7136\u4e00\u65cf\u8eab\uff0c\u5e76\u501f\u65cb\u8eab\u4e4b\u52bf\uff0c\u53f3\u638c\u4e00\u5f0f \u201c\u5012\u6253\u91d1\u949f\u201d\uff0c\u6b63\u597d\u6253\u5728\u9648\u4e4e\u8033\u95e8\u4e0a\uff0c\u9648\u5e73\u8fde\u542d\u58f0\u90fd\u672a\u542d\u58f0\uff0c\u7ffb\u8eab\u88c1\u5012\uff0c\u7acb\u5373\u4e03\u7a8d\u6d41\u8840\u5730\u6b7b \u53bb\u3002 \u3000\u3000\u518d\u8bf4\u9648\u6e05\u3001\u9648\u5e73\u867d\u7136\u4ec5\u662f\u6155\u5bb9\u5e9c\u4e0a\u7684\u4e24\u540d\u536b\u58eb\uff0c\u4f46\u6b66\u529f\u5374\u90fd\u4e0d\u5f31\uff0c\u5c31\u7b97\u6b66\u6797\u4e2d\u7b2c\u4e00\u6d41\u9ad8 \u624b\uff0c\u8981\u60f3\u4e09\u62db\u4e24\u5f0f\u628a\u4ed6\u4eec\u5144\u5f1f\u6253\u8d25\uff0c\u4e5f\u4e0d\u662f\u6613\u4e8b\u3002\u5982\u4eca\uff0c\u5c55\u767d\u53ea\u4e00\u51fa\u624b\uff0c\u4fbf\u628a\u4e8c\u4eba\u6253\u6b7b\uff0c\u773c \u524d\u4e4b\u4eba\u7eb5\u7136\u4e2a\u4e2a\u6b66\u529f\u9ad8\u5f3a\uff0c\u773c\u9ad8\u4e8e\u9876\uff0c\u89c1\u4e86\u8fd9\u5b8c\u5168\u51fa\u4e4e\u610f\u5916\u7684\u7ed3\u5c40\uff0c\u4e5f\u83ab\u4e0d\u8138\u73b0\u60ca\u5bb9\u3002 \u3000\u3000\u4e00\u4ee4\u4eca\u671d\u770b\u53cc\u773c\u77aa\u7740\u5c55\u767d\uff0c\u5fc3\uff0c\u6237\u6697\u5fd6\uff1a\u201c\u60f3\u4e0d\u5230\u8fd9\u4e0d\u8d77\u773c\u7684\u5c11\u5e74\uff0c\u5012\u771f\u6709\u70b9\u95e8\u9053\uff01\u201d \u3000\u3000\u4e0d\u8981\u8bf4\u4f17\u4eba\u5947\u602a\uff0c\u5c31\u8fde\u5c55\u81ea\u81ea\u5df2\u5fc3\u4e2d\uff0c\u4e5f\u591c\u60ca\u5f02\u4e0d\u6b62\u3002\u6697\u60f3\uff1a\u201c\u524d\u4e9b\u5929\u5728\u677e\u6797\u4e2d\u4ed6\u4e8c\u4eba \u622a\u6740\u81ea\u5df2\u65f6\uff0c\u81ea\u5df1\u8fd8\u4e0d\u662f\u4ed6\u4e8c\u4eba\u7684\u5bf9\u624b\uff0c\u5982\u4eca\uff0c\u600e\u4e48\u4e8c\u4eba\u5982\u6b64\u4e0d\u6d4e\u4e8b\uff0c\u53ea\u4e00\u638c\u4fbf\u8981\u4e86\u4ed6\u4eec\u7684 \u547d\u5462\uff1f\u201d \u3000\u3000\u5c82\u4e0d\u77e5\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\uff0c\u4e43\u5929\u4e0b\u7b2c\u4e00\u5947\u4e66\uff0c\u5185\u8f7d\u6b66\u5b66\u5747\u662f\u4e0d\u4f20\u4e4b\u79d8\uff0c\u5c55\u767d\u867d\u4ec5\u4e60\u6570 \u5341\u5929\uff0c\u5185\u529b\u5df2\u7ecf\u5927\u589e\u4f55\u51b5\u4ed6\u8fd8\u8fd0\u8db3\u4e86\u5168\u529b\uff0c\u9648\u6e05\u3001\u9648\u5e73\u7109\u80fd\u627f\u53d7\u5f97\u4f4f\u3002 \u3000\u3000\u5728\u5c55\u767d\u6697\u6697\u81ea\u5fd6\u7684\u5f53\u513f\uff0c\u51cc\u98ce\u516c\u5b50\u89c1\u5c55\u767d\u8fde\u6bd9\u81ea\u5df1\u4e24\u540d\u624b\u4e0b\uff0c\u9762\u4e0a\u52c3\u7136\u8272\u53d8\u2026\u3002\xb7\u201c\u54c8 \u54c8\u54c8\u3002\u2026\u201d\u4f46\u51cc\u98ce\u516c\u5b50\u5c1a\u672a\u51fa\u9762\uff0c\u90a3\u4e2d\u5e74\u72c2\u751f\u5374\u4ef0\u5929\u53d1\u51fa\u4e00\u58f0\u72c2\u7b11\uff0c\u7b11\u58f0\u9ad8\u4ea2\u4eba\u4e91\uff0c\u55e1\u7136\u9707 \u8033\uff0c\u76f4\u5230\u4ed6\u7b11\u7f62\u6536\u58f0\uff0c\u4f59\u97f3\u4ecd\u4e45\u4e45\u4e0d\u7edd\uff0c\u53ef\u89c1\u4e2d\u5e74\u72c2\u751f\u5185\u529f\u706b\u5019\uff0c\u5df2\u8fbe\u7089\u706b\u7eaf\u9752\u4e4b\u5883\u3002 \u3000\u3000\u201c\u5c11\u5b50\uff01\u201d\u4e2d\u5e74\u72c2\u751f\u7b11\u7f62\u4e00\u77aa\u773c\uff0c\u5411\u5c55\u767d\u559d\u9053\uff1a\u201c\u4f60\u5728\u516c\u5b50\u9762\u524d\uff0c\u5efa\u6bd9\u6155\u5bb9\u5e9c\u4e0a\u4e24\u540d\u5bb6 \u4e86\uff0c\u4f60\u5c0f\u4e8e\u591f\u72c2\uff01\u6709\u80c6\u91cf\uff01\u201d \u3000\u3000\u201c\u4e0d\u7ba1\u662f\u8c01\uff01\u201d\u5c55\u767d\u4e0d\u77e5\u81ea\u5df2\u51fa\u624b\u8f7b\u91cd\uff0c\u63a5\u8fde\u8fde\u5288\u4e8c\u4eba\uff0c\u5fc3\u4e2d\u4e0d\u65e0\u6127\u759a\u4e4b\u5ff5\u3002\u4f46\u4ed6\u53c8\u53d7 \u4e0d\u4f4f\u8fd9\u4e9b\u4eba\u7684\u5f53\u4f17\u4fae\u8fb1\uff0c\u66f4\u77e5\u4ec7\u6068\u5df2\u7ed3\u6210\uff0c\u5316\u89e3\u65e0\u671b\u3002\u5de6\u53f3\u524d\u540e\uff0c\u53cd\u6b63\u662f\u4e2a\u4e0d\u89e3\u4e4b\u5c40\uff0c\u4e0d\u7531 \u8c41\u51fa\u53bb\u4e86\uff0c\u662f\u4ee5\u4e2d\u5e74\u72c2\u751f\u4e00\u559d\u95ee\uff0c\u7acb\u523b\u6602\u7136\u7b54\u9053\uff1a\u201c\u627e\u5230\u5728\u4e0b\u5934\u4e0a\uff0c\u5728\u4e0b\u4e5f\u53f0\u547d\u76f8\u966a\uff01\u201d \u3000\u3000\u201c\u72c2\u5984\u5c0f\u5b50\uff01\u201d\u4e2d\u4e2d\u72c2\u751f\u53cc\u76ee\u795e\u5149\u66b4\u5c04\uff0c\u6c89\u559d\u9053\uff1a\u201c\u4f60\u8ba4\u8bc6\u6211\u662f\u8c01\u5417\uff1f\u201d \u3000\u3000\u201c\u6055\u5728\u4e0b\u773c\u62d9\uff0c\u201d\u5c55\u81ea\u7b54\u9053\uff1a\u201c\u4e0d\u77e5\u9601\u4e0b\u662f\u8c01\uff01\u201d \u3000\u3000\u201c\u5929\u6daf\u72c2\u751f\uff01\u201d\u4e2d\u4e2d\u72c2\u751f\u66b4\u543c\u9053\uff1a\u201c\u77e5\u9053\u5417\uff1f\u5929\u6daf\u72c2\u751f\u8d75\u4e5d\u5dde\u5c31\u662f\u6211\uff0c\u6211\u5c31\u662f\u5929\u6daf\u72c2\u751f \u8d75\u4e5d\u5dde\uff01\u4e09\u62db\uff01\u53ea\u8981\u4e09\u62db\u4fbf\u5236\u4f60\u6b7b\u547d\uff01\u201d \u3000\u3000\u201c\u5982\u679c\u4e09\u62db\u4e4b\u540e\uff0c\u5728\u4e0b\u4e0d\u6b7b\u5462\uff1f\u201d\u5c55\u767d\u53cd\u6b63\u8c41\u51fa\u53bb\u4e86\uff0c\u660e\u77e5\u4e0d\u654c\uff0c\u5634\u8fb9\u4e5f\u4e0d\u80af\u793a\u5f31\u3002\u56e0 \u4e3a\u6bd4\u4ed6\u7684\u6b66\u529f\u9ad8\u51fa\u592a\u591a\uff0c\u5929\u6daf\u72c2\u751f\u4e4b\u540d\uff0c\u4ed6\u662f\u542c\u5f97\u592a\u591a\u4e86\uff0c\u90a3\u5dee\u4e0d\u591a\u662f\u4e2a\u5947\u6027\u7684\u4eba\u7269\uff0c\u6b66\u6797 \u4e2d\u5987\u5b7a\u7686\u77e5\uff0c\u800c\u4e14\u90fd\u6d25\u5f8b\u4e50\u9053\uff0c\u5929\u6daf\u72c2\u751f\u51fa\u8eab\u5728\u957f\u767d\u95e8\u4e0b\uff0c\u4e09\u5e74\u827a\u6210\uff0c\u4fbf\u6218\u8d25\u4e86\u957f\u767d\u95e8\u6240\u6709 \u9ad8\u624b\uff0c\u8fde\u4ed6\u63a5\u4e1a\u7684\u6069\u5e08\u4e5f\u8d25\u5728\u4ed6\u7684\u624b\u4e0b\u3002\u4ed6\u89c9\u5f97\u957f\u767d\u95e8\u4e2d\u518d\u6ca1\u6709\u4eba\u53ef\u4ee5\u6559\u4ed6\uff0c\u4e5f\u6ca1\u6709\u4ec0\u4e48\u53ef \u5b66\u7684\u4e86\uff0c\u4fbf\u72ec\u81ea\u4e0b\u5c71\uff0c\u5728\u6c5f\u6e56\u4e0a\u626c\u8a00\uff0c\u8c01\u80fd\u80dc\u4ed6\uff0c\u4ed6\u4fbf\u62dc\u8c01\u4e3a\u5e08\u3002\u56e0\u4ed6\u5929\u8d44\u5947\u9ad8\uff0c\u65e0\u8bba\u4efb\u4f55 \u95e8\u6d3e\u6b66\u529f\uff0c\u53ea\u8981\u4ed6\u770b\u5230\uff0c\u4ed6\u4fbf\u80fd\u8fc7\u76ee\u4e0d\u5fd8\uff0c\u800c\u4e14\u80fd\u4e3e\u4e00\u53cd\u4e09\uff0c\u7acb\u523b\u609f\u51fa\u5236\u80dc\u8be5\u95e8\u6b66\u529f\u4e4b\u9053 \u6765\u3002\u56e0\u6b64\uff0c\u4e09\u4e94\u5e74\u4e0b\u6765\uff0c\u627e\u4ed6\u8f83\u91cf\u7684\u6b66\u6797\u9ad8\u624b\uff0c\u56fa\u7136\u4e0d\u5728\u5c11\u6570\uff0c\u4f46\u6ca1\u6709\u4e00\u4e2a\u4eba\u80fd\u662f\u4ed6\u7684\u5bf9 \u624b\uff0c\u540e\u6765\u4ed6\u5355\u4eba\u72ec\u9a91\u4e0a\u5d69\u5c71\u5c11\u5ba4\u8702\uff0c\u72ec\u95ef\u5c11\u6797\u5bfa\u5929\u4e0b\u95fb\u540d\u7684\u201c\u7f57\u6c49\u9635\u201d\uff0c\u53c8\u72ec\u81ea\u4e00\u4eba\u8dd1\u4e0a\u6b66 \u5f53\u5c71\uff0c\u5355\u4eba\u529b\u6218\u6b66\u5f53\u6d3e\u6b66\u529f\u6700\u9ad8\u7684\u201c\u6b66\u5f53\u4e09\u5b50\u201d\uff0c\u4ecd\u80fd\u5168\u8eab\u9762\u9000\uff0c\u201c\u5929\u6daf\u72c2\u751f\u201d\u4e4b\u540d\uff0c\u5df2\u8f70 \u52a8\u4e86\u6574\u4e2a\u6b66\u6797\u3002 \u3000\u3000\u540e\u6765\u4e0d\u77e5\u4e3a\u4ec0\u4e48\uff0c\u6c5f\u6e56\u4e0a\u4e0d\u89c1\u4e86\u5929\u6daf\u72c2\u751f\u7684\u8e2a\u8ff9\u3002\u6ca1\u60f3\u5230\u4e8b\u8fc7\u5341\u6570\u5e74\uff0c\u5982\u4eca\u5929\u6daf\u72c2\u751f\u53c8 \u5728\u6b64\u5730\u51fa\u73b0\uff0c\u800c\u4e14\uff0c\u770b\u6837\u5b50\u4ed6\u4e5f\u88ab\u6155\u5bb9\u5e9c\u201c\u8c79\u7a81\u5c71\u5e84\u201d\u6536\u7f57\u4e86\u6765\uff0c\u4ee5\u5929\u6daf\u72c2\u751f\u4e4b\u72c2\uff0c\u4ece\u672a\u670d \u8fc7\u4eba\uff0c\u5bf9\u51cc\u98ce\u516c\u5b50\u5374\u4f3c\u6267\u793c\u751a\u606d\uff0c\u8fd9\u66f4\u662f\u4e00\u4e2a\u4f7f\u4eba\u60f3\u4e0d\u900f\u7684\u8c1c\uff01\u2026\u2026 \u3000\u3000\u201c\u4f60\u80fd\u4e09\u62db\u4e0d\u6b7b\uff0c\u201d\u5929\u6daf\u72c2\u751f\u8d75\u4e5d\u5dde\u888d\u8896\u4e00\u62c2\uff0c\u8bf4\u9053\uff1a\u201c\u5929\u6daf\u72c2\u751f\u7684\u540d\u53f7\u9001\u7ed9\u4f60\u4e86\uff0c\u5c0f \u5b50\uff0c\u4f60\u5c31\u51c6\u5907\u53d7\u6b7b\u5427\uff01\u201d \u3000\u3000\u201c\u65e9\u5c31\u6dee\u5907\u597d\u4e86\uff01\u201d\u5c55\u81ea\u88ab\u5929\u6daf\u72c2\u751f\u7684\u72c2\u50b2\uff0c\u4e5f\u628a\u4ed6\u6f5c\u5b58\u5728\u7684\u72c2\u52b2\u6fc0\u8d77\u6765\u4e86\uff0c\u6a2a\u7709\u51dd\u6c14 \u5730\u7b54\u9053\u3002 \u3000\u3000\u201c\u63a5\u7740\uff0c\u7b2c\u4e00\u62db\uff01\u201d \u3000\u3000\u6700\u540e\u7684\u201c\u62db\u201d\u5b57\u5c1a\u672a\u843d\u5730\uff0c\u5929\u6daf\u72c2\u751f\u8eab\u5f62\u7535\u5c04\u800c\u8d77\uff0c\u534a\u7a7a\u4e2d\u5de6\u81c2\u6a2a\u5c48\uff0c\u4ee5\u201c\u8155\u201d\u3001 \u201c\u8098\u201d\u649e\u70b9\u5c55\u767d\u524d\u80f8\u201c\u4e09\u9633\u201d\u3001\u201c\u5206\u6c34\u201d\u91cd\u7a74\uff1b\u53f3\u638c\u5374\u5728\u534a\u7a7a\u4e2d\u5212\u4e86\u4e00\u4e2a\u5706\u5f27\uff0c\u6405\u8d77\u4e00\u822c\u6e38 \u6da1\u5f62\u7684\u52b2\u98ce\uff0c\u76f4\u5411\u5c55\u767d\u9762\u95e8\u7f69\u6765\uff01 \u3000\u3000\u5c55\u767d\u5927\u5403\u4e00\u63a0\uff0c\u8fd9\u602a\u5f02\u8be1\u8c32\u7684\u62db\u5f0f\uff0c\u771f\u662f\u95fb\u6240\u672a\u95fb\uff0c\u89c1\u6240\u672a\u89c1\u3002 \u3000\u3000\u56e0\u4e3a\u5c55\u81ea\u4e0d\u8ba4\u5f97\u5bf9\u65b9\u62db\u5f0f\u95e8\u9053\uff0c\u4e0d\u6562\u8d38\u7136\u51fa\u624b\u63a5\u62db\uff0c\u53ea\u6709\u7528\u201c\u8de8\u9a6c\u91d1\u5c71\u201d\u6b65\u6cd5\uff0c\u5411\u4e00\u65c1 \u95ea\u53bb\uff01 \u3000\u3000\u8bf4\u4e5f\u771f\u5de7\uff0c\u5c55\u767d\u5411\u4fa7\u65b9\u56de\u8eab\u8de8\u6b65\uff0c\u4e00\u811a\u8e29\u5728\u4e00\u98a0\u6eda\u5706\u7684\u5375\u77f3\u4e0a\uff0c\u811a\u4e0b\u4e00\u6ed1\uff0c\u8eab\u4f53\u5931\u53bb\u5e73 \u8861\uff0c\u4e0d\u7531\u201c\u54ce\u201d\u7684\u4e00\u58f0\uff0c\u5f80\u659c\u523a\u91cc\u683d\u5012\u4e0b\u53bb\uff01\u2026\u2026 \u3000\u3000\u201c\u7b2c\u4e8c\u62db\uff01\u201d \u3000\u3000\u5c31\u5728\u5c55\u767d\u811a\u4e0b\u8e29\u6ed1\uff0c\u8eab\u5f62\u5411\u4e0b\u683d\u5012\u7684\u5239\u90a3\uff0c\u5929\u6daf\u72c2\u751f\u5df2\u62db\u968f\u58f0\u51fa\uff0c\u65bd\u51fa\u7b2c\u4e8c\u62db\u3002 \u3000\u3000\u5929\u6daf\u72c2\u751f\u7684\u7b2c\u4e8c\u62db\uff0c\u65bd\u51fa\u7684\u66f4\u89c1\u5947\u8be1\uff0c\u4ed6\u8eab\u5f62\u5e73\u98de\u75be\u5c04\u4e4b\u4e2d\uff0c\u5c55\u81c2\u62d7\u817f\uff0c\u98d8\u9038\u7684\u8eab\u6750\uff0c \u5b9b\u5982\u4e00\u53ea\u70b9\u6c34\u7684\u8d4c\u8712\uff0c\u8db3\u5c16\u5fae\u4e00\u70b9\u5730\uff0c\u8fde\u770b\u4e5f\u6ca1\u6709\u770b\uff0c\u501f\u8eab\u5f62\u7535\u65cb\u4e4b\u52bf\uff0c\u53cd\u81c2\u5288\u51fa\u4e00\u638c\uff01 \u3000\u3000\u638c\u5203\u5288\u98ce\uff0c\u6302\u8d77\u4e00\u822c\u7834\u7a7a\u7684\u9510\u5578\uff0c\u201c\u64e6\u201d\u7684\u4e00\u58f0\uff0c\u4e94\u5c3a\u5f00\u5916\u4e00\u68f5\u7897\u53e3\u7c97\u7684\u5c0f\u677e\u6811\uff0c\u638c\u7f18 \u8fc7\u5904\uff0c\u7ade\u5982\u5200\u65a9\u4e00\u822c\u6574\u9f50\uff0c\u88ab\u5929\u6daf\u72c2\u751f\u4e00\u638c\u780d\u6298\u3002 \u3000\u3000\u201c\u5657\uff01\u201d\u88ab\u5929\u6daf\u72c2\u751f\u62e6\u8170\u65a9\u65ad\u7684\u5c0f\u677e\uff0c\u4e0a\u534a\u622a\u6811\u8eab\u98de\u843d\u5728\u6570\u5c3a\u5f00\u5916\uff0c\u4e0d\u6b6a\u4e0d\u659c\uff0c\u76f4\u76f4\u5730 \u63d2\u8fdb\u571f\u4e2d\uff0c\u5e76\u672a\u503e\u5012\uff0c\u5b9b\u7136\u683d\u5728\u5730\u4e0b\u4e00\u822c\u3002 \u3000\u3000\u5929\u6daf\u72c2\u751f\u7684\u638c\u529b\uff0c\u5b9e\u5728\u60ca\u4eba\u3002 \u3000\u3000\u4f46\u672a\u4f24\u5230\u5c55\u767d\uff0c\u56e0\u4e3a\u5c55\u767d\u6b64\u65f6\u5df2\u683d\u4f8b\u5730\u4e0a\uff0c\u6070\u597d\u8eb2\u8fc7\u5929\u6daf\u72c2\u751f\u8fd9\u6bd4\u5200\u5251\u8fd8\u5389\u5bb3\u7684\u4e00\u638c\uff01 \u3000\u3000\u8bf4\u8d77\u6765\u662f\u5e78\u8fd0\uff0c\u5c55\u81ea\u5982\u679c\u4e0d\u662f\u6ed1\u8dcc\uff0c\u52bf\u96be\u8eb2\u8fc7\u5929\u6daf\u72c2\u751f\u8fd9\u52bf\u6bd4\u98d8\u98ce\u8fd8\u75be\u7684\u4e00\u638c\u3002 \u3000\u3000\u5929\u6daf\u72c2\u751f\u5e76\u4e0d\u662f\u5927\u8a00\u6b3a\u4eba\uff0c\u8fd9\u8be1\u5947\u5feb\u6377\u65e0\u6bd4\u7684\u4e24\u62db\uff0c\u4e0d\u8981\u8bf4\u5c55\u767d\uff0c\u5c31\u662f\u5f53\u4eca\u6b66\u6797\u7b2c\u4e00\u6d41 \u7684\u9876\u5c16\u9ad8\u624b\uff0c\u4e5f\u4e07\u96be\u8eb2\u8fc7\u3002 \u3000\u3000\u4e94\u5c3a\u5916\u7684\u4e00\u68f5\u5c0f\u677e\uff0c\u5c1a\u88ab\u780d\u65ad\uff0c\u5982\u679c\u662f\u8840\u8089\u4e4b\u8eaf\uff0c\u6328\u4e0a\u8fd9\u5229\u903e\u950b\u5203\u7684\u4e00\u638c\uff0c\u90a3\u8fd8\u4e0d\u50cf\u5c0f \u677e\u4e00\u6837\uff0c\u4e0d\u88ab\u62e6\u8170\u65a9\u4e3a\u4e24\u622a\u624d\u602a\u5462\u3002 \u3000\u3000\u53ef\u4ee5\u8bf4\u5c55\u767d\u5b8c\u5168\u662f\u4fa5\u5e78\uff0c\u6070\u5de7\u811a\u4e0b\u8e29\u6ed1\uff0c\u8eab\u5f62\u8e87\u8dcc\u5728\u5730\uff0c\u624d\u8eb2\u8fc7\u4e86\u8fd9\u4e00\u638c\u4e4b\u5384\u3002\u56e0\u4e3a\u5929 \u6daf\u72c2\u751f\u51fa\u62db\u62ff\u634f\u5de7\u5999\uff0c\u5728\u51fa\u7b2c\u4e00\u62db\u65f6\uff0c\u5df2\u7b97\u51c6\u4e86\u5c55\u767d\u8981\u5f80\u4f55\u5904\u8eb2\u907f\uff0c\u662f\u4ee5\u7b2c\u4e8c\u62db\u8fde\u770b\u90fd\u672b\u770b \u4fbf\u65bd\u5c55\u51fa\u6765\u4e86\uff0c\u5982\u679c\u4e0d\u662f\u5c55\u767d\u6ed1\u8dcc\uff0c\u65e0\u8bba\u5982\u4f55\u662f\u8eb2\u4e0d\u8fc7\u8fd9\u4e00\u638c\u7684\u3002 \u3000\u3000\u5929\u6daf\u72c2\u751f\u7b2c\u4e8c\u62db\u6ca1\u6709\u6253\u5230\u5c55\u767d\uff0c\u4f3c\u4e4e\u5927\u51fa\u610f\u5916\uff0c\u5fae\u5fae\u4e00\u6094\u4e4b\u960e\uff0c\u89c1\u5c55\u767d\u81ea\u5df1\u8d54\u8dcc\u5728\u5730\uff0c \u4ed6\u5e76\u4e0d\u77e5\u9053\u5c55\u767d\u662f\u65e0\u5fc3\u4e2d\u6ed1\u8dcc\u7684\uff0c\u8fd8\u4ee5\u4e3a\u5c55\u767d\u662f\u7528\u4ec0\u4e48\u7279\u6b8a\u8eab\u6cd5\uff0c\u8eb2\u8fc7\u4ed6\u7684\u4e24\u5927\u6740\u62db\u3002\u4f46\u518d \u4e00\u770b\u5c55\u767d\u8dcc\u5012\u7684\u60c5\u5f62\uff0c\u5e76\u4e0d\u662f\u8eab\u6cd5\u62db\u5f0f\u4e2d\u7684\u4ee5\u201c\u819d\u201d\u3001\u201c\u8098\u201d\u89e6\u5730\uff0c\u800c\u662f\u5e73\u5e73\u5b9e\u5b9e\u7684\u8dcc\u5012\uff0c \u4e0d\u7531\u829c\u5c14\u4e00\u7b11\uff0c\u559d\u9053\uff1a\u201c\u5c0f\u5b50\uff01\u722c\u8d77\u6765\u5427\uff01\u201d \u3000\u3000\u8bf4\u7f62\u968f\u624b\u4e00\u638c\uff0c\u5411\u8dcc\u5728\u5730\u4e0a\u7684\u5c55\u767d\u6325\u53bb\u3002 \u3000\u3000\u5c55\u767d\u5931\u8db3\u6454\u5012\u6b63\u5f85\u722c\u8d77\uff0c\u5ffd\u89c9\u52b2\u98ce\u6251\u9762\uff0c\u4ee5\u4e3a\u5929\u6daf\u72c2\u751f\u7684\u7b2c\u4e09\u62db\u53c8\u6765\u4e86\uff0c\u614c\u5fd9\u4e2d\u51fa\u4e00\u5f0f \u201c\u61d2\u9a74\u6253\u6eda\u201d\uff0c\u76f4\u6eda\u51fa\u516b\u5c3a\u4ee5\u5916\uff0c\u624d\u817e\u8eab\u800c\u8d77\u3002 \u3000\u3000\u201c\u54c8\u54c8\u54c8\u2026\u3002\u201d\u5929\u6daf\u72c2\u751f\u4e00\u9635\u72c2\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u5b50\uff01\u4e0d\u7528\u7a77\u7d27\u5f20\uff0c\u6211\u7b2c\u4e09\u62db\u8fd8\u6ca1\u6709\u51fa\u624b \u5462\uff01\u201d \u3000\u3000\u5929\u6daf\u72c2\u751f\u8bf4\u7f62\uff0c\u4e24\u773c\u76f4\u89c6\u7740\u5c55\u767d\uff0c\u7f13\u6b65\u8d70\u8fd1\u2026\u2026 \u3000\u3000\u8fd9\u6837\u8c08\u7b11\u4ece\u5bb9\u4e4b\u95f4\u53bb\u6740\u4eba\uff0c\u6bd4\u72c2\u5578\u6012\u543c\u3001\u6325\u5200\u821e\u6756\u7684\u60c5\u52bf\uff0c\u66f4\u52a0\u4f7f\u4eba\u9634\u5bd2\u6050\u6016\u3002 \u3000\u3000\u201c\u54fc\uff01\u8d75\u53d4\u53d4\uff01\u201d\u9ed1\u8863\u5c11\u5973\u5374\u5ffd\u5728\u4e00\u8fb9\u53eb\u9053\uff1a\u201c\u4f60\u8fd8\u662f\u6b66\u6797\u4e2d\u6210\u540d\u7684\u4eba\u7269\uff0c\u8bf4\u51fa\u53e3\u6765\u7684 \u8bdd\uff0c\u7b97\u4e0d\u7b97\u6570\uff1f\u201d \u3000\u3000\u201c\u8d75\u53d4\u53d4\u5411\u6765\u4e00\u8a00\u4e5d\u9f0e\uff0c\u4e00\u8bfa\u5343\u91d1\uff0c\u6ca1\u6709\u8bf4\u8fc7\u7684\u8bdd\u4e0d\u7b97\u6570\u7684\uff01\u82e5\u4e0d\u7136\u6211\u4e5f\u4e0d\u4f1a\u5728\u4f60\u4eec\u6155 \u5bb9\u5e9c\u4e2d\uff0c\u4e00\u4f4f\u5c31\u5341\u5e74\u3002\u7ea2\u59d1\u5a18\uff0c\u4f60\u8bf4\u5bf9\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u5929\u6daf\u72c2\u751f\u4e00\u8fb9\u5bf9\u9ed1\u8863\u5c11\u5973\u8bb2\u8bdd\uff0c\u4e00\u8fb9\u4ecd\u7136\u6b65\u6b65\u903c\u8fd1\u5c55\u767d\u3002 \u3000\u3000\u201c\u90a3\u4e48\uff0c\u201d\u9ed1\u8863\u5c11\u5973\u8bf4\u9053\uff1a\u8d75\u53d4\u53d4\u8bf4\u8fc7\u5728\u4e09\u62db\u4e4b\u5185\uff0c\u81f4\u4eba\u4e8e\u4e00\u62db\uff0c\u90a3\u968f\u624b\u4e00\u6325\u4e5f\u771f\u4f3c\u90a3 \u62db\u5f0f\u7684\u6837\u5f0f\uff0c\u4e0d\u8fc7\u5374\u6ca1\u6709\u7528\u5fc3\u65bd\u5c55\u548c\u90a3\u79cd\u5965\u5999\u53d8\u5316\u53ca\u5de8\u5927\u5a01\u529b\u7f62\u4e86\u3002\u5929\u6daf\u72c2\u751f\u65e0\u5fc3\u51fa\u62db\uff0c\u5982 \u679c\u662f\u6709\u5fc3\uff0c\u5012\u5728\u5730\u4e0b\u7684\u5c55\u767d\u4e5f\u4e07\u96be\u8eb2\u8fc7\u3002\u6b64\u65f6\uff0c\u5929\u6daf\u72c2\u751f\u7ecf\u9ed1\u8863\u5c11\u5973\u70b9\u7834\uff0c\u672c\u53ef\u4e0d\u8ba4\u8d26\uff0c\u7ee7 \u7eed\u5bf9\u5c55\u767d\u65bd\u51fa\u7b2c\u4e09\u62db\uff0c\u4ee5\u81f4\u5c55\u767d\u6b7b\u547d\uff0c\u53ef\u662f\u5929\u6daf\u72c2\u751f\u4e43\u662f\u6210\u540d\u591a\u5e74\u7684\u6b66\u6797\u9ad8\u624b\uff0c\u53c8\u52a0\u4e0a\u4ed6\u72c2 \u50b2\u7684\u6027\u683c\uff0c\u5c82\u80af\u90a3\u4e48\u505a\uff01\u56e0\u6b64\uff0c\u5929\u6daf\u72c2\u751f\u6014\u4e86\u4e00\u6014\uff0c\u6447\u4e86\u6447\u5934\uff0c\u9eef\u7136\u8bf4\u9053\uff1a\u201c\u8d75\u53d4\u53d4\u4eca\u5929\u7b97 \u683d\u4e86\uff0c\u4e0d\u8fc7\uff0c\u8bdd\u5f97\u8bf4\u5230\u5934\u91cc\u3002\u7ea2\u59d1\u5a18\uff0c\u8d75\u53d4\u53d4\u4eca\u5929\u662f\u683d\u5728\u4f60\u7684\u5634\u91cc\uff0c\u4e0d\u662f\u683d\u5728\u4ed6\u7684\u624b\u4e2d\uff01\u201d \u3000\u3000\u8bf4\u7740\uff0c\u56de\u5934\u5411\u51cc\u98ce\u516c\u5b50\u4e00\u62b1\u62f3\uff0c\u8bf4\u9053\uff1a\u201c\u8d75\u67d0\u5728\u516c\u5b50\u5e9c\u4e0a\uff0c\u4e00\u4f4f\u5341\u5e74\uff0c\u6beb\u65e0\u8d21\u732e\uff0c\u73b0\u5728 \u5c31\u6b64\u544a\u522b\uff0c\u9752\u5c71\u4e0d\u6539\uff0c\u540e\u4f1a\u6709\u671f\uff01\u201d \u3000\u3000\u201c\u540e\u4f1a\u6709\u671f\u201d\u56db\u5b57\u672b\u843d\uff0c\u5929\u6daf\u72c2\u751f\u895f\u8896\u98d8\u626c\uff0c\u4eba\u5df2\u5982\u4e00\u53ea\u95f2\u4e91\u91ce\u9e64\u822c\u5730\u98d8\u8fd9\u662f\u4efb\u4f55\u4eba\u4e5f \u6ca1\u6709\u60f3\u5230\u7684\u4e8b\uff0c\u5929\u6daf\u72c2\u751f\u7ade\u9009\u7136\u8d70\u4e86\uff01\u800c\u4e14\uff0c\u4ed6\u7684\u8eab\u6cd5\u592a\u5feb\uff0c\u51cc\u98ce\u516c\u5b50\u7b49\u4eba\u8fde\u60f3\u51fa\u53e3\u633d\u7559\u90fd \u6765\u4e0d\u53ca\uff0c\u5a07\u9633\u7eff\u91ce\u4e4b\u4e2d\uff0c\u5df2\u7ecf\u6d88\u5931\u4e86\u5929\u6daf\u72c2\u751f\u7684\u8e2a\u5f71\uff01 \u3000\u3000\u201c\u54fc\uff01\u201d\u51cc\u98ce\u516c\u5b50\u7adf\u628a\u4e00\u8154\u6028\u6c14\uff0c\u53d1\u5728\u59d0\u59d0\u8eab\u4e0a\u3002\u53ea\u89c1\u4ed6\u51b7\u54fc\u4e00\u58f0\uff0c\u5bf9\u9ed1\u8863\u5c11\u5973\u8bf4\u9053\uff1a \u201c\u4f60\u628a\u8d75\u4e8c\u53d4\u6c14\u8d70\uff0c\u770b\u56de\u53bb\u4f60\u600e\u4e48\u8ddf\u7236\u4eb2\u4ea4\u4ee3\uff1f\u201d \u3000\u3000\u9ed1\u8863\u5c11\u5973\u4e00\u8038\u7476\u9f3b\uff0c\u4e5f\u51b7\u54fc\u4e86\u4e00\u58f0\u3002\u8bf4\u9053\uff1a\u201c\u4ed6\u81ea\u5df1\u8981\u8d70\uff0c\u6211\u6709\u4ec0\u4e48\u529e\u6cd5\uff01\u201d \u3000\u3000\u201c\u4f60\u662f\u767d\u7528\u5fc3\u673a\uff01\u201d\u51cc\u98ce\u516c\u5b50\u5634\u89d2\u4e0b\u6487\uff0c\u8bed\u6c14\u66f4\u52a0\u9634\u51b7\uff0c\u8bf4\u9053\uff1a\u201c\u4ef7\u5c31\u662f\u628a\u8d75\u4e8c\u53d4\u6c14 \u8d70\uff0c\u6211\u4e5f\u4e0d\u80fd\u9976\u8fc7\u4ed6\uff01\u201d \u3000\u3000\u8bf4\u7f62\uff0c\u6c14\u52bf\u864e\u864e\u5730\u5411\u5c55\u767d\u6b3a\u8fd1\u3002 \u3000\u3000\u201c\u516c\u5b50\u7559\u6b65\uff01\u201d\u5ffd\u89c1\u90a3\u9526\u8863\u534e\u670d\u3001\u72b6\u5982\u8d35\u7ec5\u7684\u58ee\u5e74\u5927\u6c49\uff0c\u5411\u524d\u8de8\u51fa\u4e00\u6b65\uff0c\u5524\u4f4f\u51cc\u98ce\u516c \u5b50\uff0c\u7136\u540e\u8f6c\u5411\u5c55\u767d\u8bf4\u9053\uff1a\u201c\u8001\u592b\u60f3\u5439\u4e00\u9996\u5c0f\u66f2\uff0c\u8bf7\u5c0f\u54e5\u513f\u6b23\u8d4f\u6b23\u8d4f\uff0c\u4e0d\u77e5\u5c0f\u54e5\u513f\u53ef\u6709\u6b64\u96c5\u5174 \u5426\uff1f\u201d \u3000\u3000\u5c55\u767d\u89c1\u8fd9\u9526\u8863\u58ee\u6c49\uff0c\u4e00\u8eab\u5bcc\u8d35\u6c14\uff0c\u9762\u767d\u65e0\u987b\uff0c\u770b\u6837\u5b50\u6700\u591a\u4e5f\u4e0d\u4f1a\u8d85\u8fc7\u56db\u5341\u5c81\uff0c\u53e3\u4e2d\u81ea\u79f0 \u8001\u592b\uff0c\u53c8\u89c1\u4ed6\u8c08\u5410\u5112\u96c5\uff0c\u8ddf\u8fd9\u4e00\u8eab\u534e\u4e3d\u670d\u9970\u663e\u5f97\u5341\u5206\u4e0d\u8c03\u548c\u3002\u4e0d\u8fc7\uff0c\u5c55\u767d\u5fc3\u91cc\u660e\u767d\uff0c\u6c5f\u6e56\u4e0a \u53cc\u65b9\u5bf9\u654c\uff0c\u6108\u662f\u8868\u9762\u5ba2\u6c14\u7684\u4eba\u6108\u4e0d\u597d\u60f9\u3002\u4ed6\u867d\u4e0d\u77e5\u9053\u9526\u8863\u58ee\u6c49\u59d3\u751a\u540d\u8c01\uff0c\u4f46\u770b\u90a3\u6c14\u6d3e\uff0c\u5c24\u5176 \u662f\u53cc\u76ee\u5f00\u9602\u4e4b\u95f4\uff0c\u76ee\u5149\u7280\u5229\uff0c\u51e0\u5982\u4e00\u67c4\u950b\u5203\u76f4\u523a\u4eba\u5185\u5fc3\uff0c\u77e5\u5176\u4ea6\u5fc5\u4e3a\u4e00\u6b66\u6797\u9876\u5c16\u9ad8\u624b\u3002 \u3000\u3000\u53ef\u662f\uff0c\u5c55\u767d\u6b64\u65f6\u5df2\u4e0b\u5b9a\u51b3\u5fc3\uff0c\u4e0d\u5411\u4efb\u4f55\u4eba\u4f4e\u5934\u3002\u56e0\u4e3a\u4ed6\u5fc3\u91cc\u660e\u767d\uff0c\u5728\u8fd9\u4e9b\u6740\u4eba\u4e0d\u7728\u773c\u7684 \u7eff\u6797\u8c6a\u5ba2\u9762\u524d\uff0c\u54c0\u6073\u6c42\u601c\uff0c\u4ed6\u4eec\u5fc3\u5982\u94c1\u77f3\uff0c\u4e0d\u4f46\u4e0d\u4f1a\u540c\u60c5\u4f60\uff0c\u8bf4\u4e0d\u5b9a\u53cd\u800c\u62db\u81f4\u6bd4\u6b7b\u66f4\u4ee4\u4eba\u96be \u4ee5\u5fcd\u53d7\u7684\u5c48\u8fb1\u3002\u56e0\u6b64\uff0c\u4ed6\u6beb\u4e0d\u8003\u8651\u5730\u6602\u7136\u7b54\u9053\uff1a\u201c\u4e0d\u8bba\u8bf8\u4f4d\u5212\u4e0b\u4ec0\u4e48\u9053\u513f\uff0c\u5728\u4e0b\u4e00\u5f8b\u63a5\u4f4f\u5c31 \u662f\u4e86\uff01\u201d \u3000\u3000\u5c55\u81ea\u6b64\u8bdd\u4e00\u51fa\u53e3\uff0c\u9ed1\u8863\u5c11\u5973\u5728\u4e00\u8fb9\u76f4\u76b1\u7709\uff0c\u5fc3\u8bf4\uff1a\u201c\u50bb\u5c0f\u5b50\uff01\u4f60\u8fd9\u4e0d\u662f\u81ea\u5df1\u627e\u9ebb\u70e6\u5417\uff1f \u8fd9\u94f6\u8427\u593a\u9b42\u7ae0\u58eb\u670b\uff0c\u6bd4\u5929\u6daf\u72c2\u751f\u66f4\u96be\u60f9\uff0c\u4f60\u600e\u4e48\u4e5f\u6562\u5411\u4ed6\u6311\u6218\uff1f\u201d \u3000\u3000\u201c\u58ee\u54c9\uff01\u201d\u9526\u8863\u58ee\u6c49\u2014\u2014\u4e5f\u5c31\u662f\u94f6\u8427\u593a\u9b42\u7ae0\u58eb\u670b\u6587\u7ec9\u7ec9\u5730\u559d\u4e00\u58f0\u91c7\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u54e5\u513f\u8c6a \u6c14\u5e72\u4e91\uff0c\u6211\u5439\u7bab\u5ba2\u7b97\u662f\u9047\u5230\u77e5\u97f3\u4e86\uff01\u201d \u3000\u3000\u94f6\u8427\u593a\u9b42\u7ae0\u58eb\u670b\u8bf4\u7740\uff0c\u4ece\u8896\u7ba1\u5185\u53d6\u51fa\u4e00\u53ea\u94f6\u5149\u707f\u70c2\u7684\u6d1e\u7bab\u6765\uff0c\u5148\u5fae\u5fae\u4e00\u7b11\uff0c\u7136\u540e\u628a\u8427\u53e3 \u5bf9\u6b63\u5634\u5507\uff0c\u201c\u545c\u2014\u2014\u5f8b\uff01\u545c\u2014\u2014\u5f8b\uff01\u201d \u3000\u3000\u5439\u51fa\u4e86\u4e24\u4e2a\u97f3\u7b26\uff0c\u5176\u58f0\u6e05\u8d8a\uff0c\u5176\u97f3\u9ad8\u6602\uff01 \u3000\u3000\u5c55\u767d\u5c1a\u4e0d\u77e5\u5c31\u91cc\u2026\u2026 \u3000\u3000\u56db\u5468\u4e4b\u4eba\uff0c\u5df2\u7f13\u7f13\u5411\u540e\u9000\u53bb\uff0c\u5e76\u7eb7\u7eb7\u53d6\u4e1d\u5dfe\u7eb8\u5377\u5835\u585e\u8033\u5b54\u3002\u9ed1\u8863\u5c11\u5973\u5374\u6025\u5f97\u76f4\u8dfa\u811a\uff0c\u5fc3 \u4e2d\u6697\u6697\u53eb\u7cdf\u2026\u2026 \u7b2c\u5341\u4e00\u7ae0\u3000\u94f6\u7bab\u7d22\u9b42>> \u53e4\u9f99\u300a\u5251\u5ba2\u884c\u300b \u7b2c\u5341\u4e00\u7ae0\u3000\u94f6\u7bab\u7d22\u9b42 \u3000\u3000\u9ed1\u8863\u5c11\u5973\u6b32\u62e6\u4e0d\u53ca\uff0c\u7ae0\u58eb\u670b\u5df2\u628a\u94f6\u7bab\u51d1\u5728\u5634\u8fb9\uff0c\u201c\u545c\u5f8b\uff01\u545c\u5f8b\uff01\u201d\u5730\u5439\u594f\u8d77\u6765\u3002 \u3000\u3000\u8427\u58f0\u867d\u7136\u4e0d\u5927\uff0c\u4f46\u6e05\u8d8a\u5f02\u5e38\uff0c\u4e00\u4e2a\u97f3\u7b26\u4e00\u4e2a\u97f3\u7b26\uff0c\u90fd\u6253\u8fdb\u4eba\u5fc3\u6df1\u5904\u3002\u4f4e\u6c89\u7684\u5730\u65b9\u5982\u9ece\u5987 \u591c\u6ce3\uff0c\u545c\u54bd\u51c4\u8ff7\uff0c\u4f7f\u4eba\u542c\u4e86\u6709\u9b42\u9500\u80a0\u65ad\u4e4b\u611f\uff0c\u5fcd\u4e0d\u4f4f\u8981\u5760\u4e0b\u6cea\u6765\u3002\u9ad8\u4ea2\u5904\u9510\u97f3\u6276\u6447\u76f4\u4e0a\uff0c\u51e0 \u53ef\u7a7f\u82cd\u7a9c\u800c\u7834\u5c42\u4e91\uff0c\u53c8\u5982\u58ee\u58eb\u60b2\u5578\uff0c\u98ce\u4e91\u5931\u8272\uff0c\u5927\u6709\u201c\u98ce\u8427\u8427\u4e2d\u6613\u6c34\u5bd2\uff0c\u58ee\u58eb\u4e00\u53bb\u516e\u4e0d\u590d \u8fd8\u201d\u7684\u60b2\u58ee\u60c5\u6000\uff0c\u66f4\u662f\u4f7f\u4eba\u60b2\u6101\u6fc0\u8361\uff0c\u4e0d\u80fd\u81ea\u5df2\u3002 \u3000\u3000\u60b2\u4f24\u51c4\u51c9\u7684\u7bab\u58f0\uff0c\u6b63\u5408\u4e86\u5c55\u767d\u7684\u5fc3\u5883\uff0c\u4ed6\u60f3\u8d77\u88ab\u60e8\u6740\u800c\u6b7b\u7684\u7236\u4eb2\uff0c\u8339\u82e6\u542b\u60b2\u800c\u6b7b\u7684\u6bcd \u4eb2\uff0c\u4ee5\u53ca\u81ea\u5df1\u60b2\u51c9\u51c4\u82e6\u53d7\u5c3d\u6298\u78e8\u5c48\u8fb1\u7684\u5f80\u4e8b\uff0c\u4e0d\u7981\u60b2\u6101\u5fe7\u4f24\u5f97\u51fa\u4e86\u795e\uff0c\u7adf\u5fd8\u4e86\u5927\u654c\u5f53\u524d\uff0c\u81ea \u5df2\u5904\u8eab\u4f55\u5730\u3002 \u3000\u3000\u201c\u545c\u5f8b\uff01\u545c\u5f8b\uff01\u201d\u7bab\u58f0\u6108\u6765\u6108\u51c4\u51c9\uff0c\u5c55\u767d\u9762\u5bb9\u60b2\u82e6\uff0c\u53cc\u773c\u5446\u5446\u5730\u51dd\u89c6\u7740\u8fdc\u65b9\uff0c\u8c01\u4e5f\u4e0d\u77e5 \u4ed6\u628a\u81ea\u5df1\u7684\u5fc3\u795e\u5f15\u5230\u4e86\u4f55\u5904\uff0c\u4e0d\u8fc7\uff0c\u4e24\u884c\u6e05\u6cea\u5df2\u6cbf\u7740\u4ed6\u7684\u9762\u988a\u6d41\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u9ed1\u8863\u5c11\u5973\u56e0\u77e5\u9053\u94f6\u7bab\u593a\u9b42\u7684\u7bab\u58f0\u5389\u5bb3\uff0c\u4e8b\u5148\u65e9\u5df2\u51dd\u795e\u6212\u5907\uff0c\u5185\u5fc3\u62b1\u5143\u5b88\u4e00\uff0c\u672a\u88ab\u7bab\u58f0\u611f \u67d3\u3002\u4f46\u5979\u770b\u5230\u5c55\u767d\u5fe7\u4f24\u51c4\u82e6\u6cea\u6d41\u6ee1\u9762\u7684\u60c5\u5f62\uff0c\u77e5\u9053\u5c55\u81ea\u5df2\u7ecf\u5728\u65e0\u5907\u4e2d\u5760\u4eba\u672f\u4e2d\uff0c\u4e0d\u7531\u82b3\u5fc3\u5927 \u6025\uff0c\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u7ae0\u53d4\u53d4\uff01\u8fd9\u6837\u4e0d\u516c\u5e73\uff01\u201d \u3000\u3000\u539f\u6765\u540d\u91cd\u6b66\u6797\u7684\u8c79\u7a81\u5c71\u5e84\u5e84\u4e3b\u3001\u201c\u6458\u661f\u624b\u201d\u6155\u5bb9\u6db5\uff0c\u5bf9\u91cd\u91d1\u672d\u8058\u7f51\u7f57\u5728\u95e8\u4e0b\u7684\u6b66\u6797\u9ad8 \u624b\uff0c\u793c\u9047\u65e2\u9686\uff0c\u5f85\u4e4b\u4e5f\u751a\u606d\uff0c\u4e0d\u4f46\u201c\u6458\u661f\u624b\u201d\u672c\u4eba\u5747\u4e0e\u4e4b\u79f0\u5144\u9053\u5f1f\uff0c\u5c31\u662f\u4ed6\u4eb2\u751f\u513f\u5973\uff0c\u4e5f\u5747 \u4ee5\u53d4\u4f2f\u547c\u4e4b\uff0c\u6240\u4ee5\u201c\u9ed1\u8863\u5c11\u5973\u201d\u6155\u5bb9\u7ea2\u5bf9\u8fd9\u4e9b\u6b66\u6797\u8c6a\u5ba2\uff0c\u5747\u4ee5\u53d4\u53d4\u547c\u4e4b\uff0c\u5f53\u7136\uff0c\u8fd9\u662f\u201c\u6458\u661f \u624b\u201d\u7b3c\u7edc\u4eba\u5fc3\u7684\u53e6\u4e00\u624b\u6cd5\u3002 \u3000\u3000\u94f6\u7bab\u593a\u9b42\u7ae0\u58eb\u670b\u5fae\u5fae\u4e00\u7b11\uff0c\u505c\u4f4f\u7b56\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u7ea2\u59d1\u5a18\uff0c\u53c8\u6709\u4ec0\u4e48\u9ad8\u89c1\uff1f\u201d \u3000\u3000\u9ed1\u8863\u5c11\u5973\u7c89\u8138\u4e00\u7ea2\uff0c\u5979\u8138\u4e0a\u867d\u6709\u4e00\u65b9\u9ed1\u7eb1\uff0c\u9f50\u9f3b\u63a9\u4f4f\u8138\u5b54\u4e00\u534a\uff0c\u4f46\u4ecd\u80fd\u770b\u51fa\u5979\u7709\u76ee\u95f4\u5a07 \u7f9e\u65e0\u9650\u3002\u5979\u5904\u5904\u5e87\u62a4\u5c55\u767d\u6765\u4e0e\u81ea\u5df1\u4eba\u4f5c\u5bf9\uff0c\u88ab\u94f6\u7bab\u593a\u9b42\u7ae0\u58eb\u670b\u6697\u4e2d\u4e00\u70b9\uff0c\u5c24\u5176\u7ae0\u58eb\u670b\u542b\u6709\u6df1 \u610f\u7684\u7b11\u5bb9\uff0c\u66f4\u4f7f\u5979\u89c9\u5f97\u88ab\u4eba\u8bc6\u7834\u5fc3\u4e8b\uff0c\u611f\u5230\u4e00\u9635\u96be\u4e3a\u60c5\u3002\u9ed1\u8863\u5c11\u5973\u7d20\u6027\u51b7\u6f20\uff0c\u8f7b\u6613\u4e0d\u52a8\u60c5 \u611f\uff0c\u5982\u4eca\uff0c\u4e0d\u77e5\u600e\u4e48\u7adf\u5bf9\u5b64\u82e6\u843d\u62d6\u7684\u5c55\u767d\uff0c\u52a8\u8d77\u60c5\u611f\u6765\u3002\u6240\u4ee5\uff0c\u6001\u5ea6\u4e0a\u4e5f\u5c31\u6709\u4e86\u5f88\u5927\u7684\u8f6c \u53d8\uff0c\u628a\u90a3\u79cd\u51b7\u6f20\u3001\u887f\u6301\u3001\u9ad8\u50b2\u7684\u5fc3\u6027\uff0c\u4e00\u4e0b\u5b50\u8f6c\u53d8\u6210\u50cf\u4e00\u822c\u9752\u6625\u5c11\u5973\u4e00\u6837\u7684\u5584\u7f9e\u5584\u611f\u4e86\uff01 \u3000\u3000\u201c\u7ae0\u53d4\u53d4\u540d\u91cd\u6b66\u6797\uff0c\u201d\u9ed1\u8863\u5c11\u5973\u867d\u7136\u542b\u7f9e\uff0c\u4f46\u77e5\u5c55\u767d\u5df2\u5371\u5728\u65e6\u5915\uff0c\u4fbf\u4e0d\u987e\u4e00\u5207\u5730\u8bf4\u9053\uff1a \u201c\u600e\u80fd\u5bf9\u4e00\u4e2a\u665a\u751f\u540e\u8f88\uff0c\u9aa4\u65bd\u6697\u7b97\uff01\u201d \u3000\u3000\u94f6\u7bab\u593a\u9b42\u4e00\u695e\uff0c\u80d6\u56e2\u56e2\u7684\u8138\u4e0a\u663e\u51fa\u4e00\u4e1d\u4e0d\u60a6\u4e4b\u8272\uff0c\u95ee\u9053\uff1a\u201c\u7ea2\u59d1\u5a18\uff01\u6b64\u8bdd\u600e\u8bb2\uff1f\u201d \u3000\u3000\u201c\u7ae0\u53d4\u53d4\u2018\u97f3\u9b54\u593a\u9b42\u7bab\u6cd5\u2019\uff0c\u4e3a\u5929\u4e0b\u9a70\u540d\u4e4b\u7edd\u6280\uff0c\u4e8b\u5148\u4e0d\u5bf9\u4eba\u5bb6\u8bf4\u660e\u767d\uff0c\u4fbf\u907d\u7136\u65bd\u5c55\uff0c \u653b\u4eba\u65e0\u5907\uff0c\u8fd9\u4e0d\u662f\u7b49\u4e8e\u6697\u7b97\u5417\uff1f\u201d \u3000\u3000\u201c\u8c01\u8bf4\u6ca1\u6709\u8bf4\u660e\u767d\uff1f\u201d\u94f6\u7bab\u593a\u9b42\u7ae0\u58eb\u670b\u88ab\u9ed1\u8863\u5c11\u5973\u4e00\u53e3\u4e00\u4e2a\u201c\u6697\u7b97\u201d\uff0c\u8bf4\u5f97\u52a8\u4e86\u771f\u6012\uff0c \u9762\u8272\u4e00\u6c89\uff0c\u8bf4\u9053\uff1a\u201c\u8001\u592b\u8bf7\u4ed6\u54c1\u7bab\uff0c\u4ed6\u4eb2\u53e3\u7b54\u5e94\uff0c\u5341\u76ee\u6240\u89c6\uff0c\u5341\u8033\u6240\u542c\uff0c\u600e\u4e48\u8bf4\u6ca1\u6709\u8bf4\u660e \u767d\uff1f\u54fc\uff01\u771f\u771f\u5c82\u6709\u6b64\u7406\uff01\u201d \u3000\u3000\u201c\u53ef\u662f\uff0c\u201d\u9ed1\u8863\u5c11\u5973\u806a\u6167\u903e\u4eba\uff0c\u867d\u7136\u94f6\u7bab\u593a\u9b42\u7ae0\u58eb\u670b\u53e5\u53e5\u5b9e\u8a00\uff0c\u4f46\u5979\u4e3a\u4e86\u6551\u52a9\u5c55\u767d\uff0c\u773c \u73e0\u4e00\u8f6c\uff0c\u65e9\u53c8\u8ba1\u4e0a\u5fc3\u7c73\uff0c\u95fb\u8a00\u5fd9\u9053\uff1a\u201c\u7ae0\u53d4\u53d4\u5e76\u6ca1\u6709\u8bf4\u660e\u767d\uff0c\u662f\u4ee5\u7bab\u58f0\u4e0e\u4eba\u5bb6\u6bd4\u6b66\uff0c\u5f53\u7136\u4eba \u5bb6\u4e0d\u4f1a\u6709\u5907\uff01\u201d \u3000\u3000\u9ed1\u8863\u5c11\u5973\u8bf4\u81f3\u6b64\u5904\uff0c\u53c8\u8f6c\u8138\u5bf9\u5c55\u767d\u8bf4\u9053\uff1a\u201c\u4f60\u8bf4\u5bf9\u4e0d\u5bf9\uff1f\u4f60\u77e5\u9053\u7ae0\u8001\u524d\u8f88\u7684\u2018\u97f3\u9b54\u593a\u9b42 \u7bab\u6cd5\u2019\uff0c\u662f\u4e00\u95e8\u66f4\u5389\u5bb3\u7684\u6b66\u529f\u5417\uff1f\u201d \u3000\u3000\u9ed1\u8863\u5c11\u5973\u539f\u662f\u60f3\u70b9\u9192\u5c55\u767d\uff0c\u53eb\u4ed6\u63d0\u9ad8\u8b66\u89c9\uff0c\u4e0d\u8981\u8ff7\u8ff7\u7cca\u7cca\u5730\u4fbf\u5984\u9001\u4e86\u6027\u547d\u3002 \u3000\u3000\u8c01\u77e5\u5c55\u767d\u8ff7\u79bb\u6005\u60d8\uff0c\u5446\u5446\u5730\u51dd\u671b\u7740\u8fdc\u65b9\uff0c\u7ade\u5982\u50bb\u4e86\u4e00\u822c\uff0c\u5bf9\u9ed1\u8863\u5c11\u5973\u7684\u95ee\u8bdd\uff0c\u604d\u5982\u672a \u95fb\uff0c\u53ea\u4efb\u7740\u4e24\u884c\u70ed\u6cea\u6d94\u6d94\u5730\u6eda\u843d\uff0c\u628a\u80f8\u524d\u9752\u886b\u90fd\u6e7f\u4e86\u4e00\u5927\u7247\u3002 \u3000\u3000\u9ed1\u8863\u5c11\u5973\u5fc3\u4e2d\u4e00\u60ca\uff0c\u77e5\u9053\u5c55\u767d\u4e3a\u7bab\u58f0\u6240\u8ff7\uff0c\u4f46\u4e0d\u77e5\u4ed6\u5185\u8151\u771f\u5143\u53d7\u4e86\u4f24\u6ca1\u6709\uff1f\u5f53\u5373\u63a8\u4e86\u75f4 \u5446\u7684\u5c55\u767d\u4e00\u628a\uff0c\u9ad8\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u8bf4\u7684\u8bdd\uff0c\u4f60\u542c\u5230\u4e86\u6ca1\u6709\uff1f\u201d \u3000\u3000\u5c55\u767d\u673a\u4f36\u4f36\u6253\u4e86\u4e00\u4e2a\u51b7\u6218\uff0c\u845b\u7136\u6e05\u9192\u8fc7\u6765\uff0c\u6536\u56de\u773c\u5149\uff0c\u4f46\u4ecd\u7136\u5f81\u671b\u7740\u9ed1\u8863\u5c11\u5973\uff0c\u7ade\u4e0d\u77e5 \u65b9\u624d\u51fa\u4e86\u4f55\u4e8b\u3002 \u3000\u3000\u539f\u6765\uff0c\u9ed1\u8863\u5c11\u5973\u63a8\u4e86\u5c55\u767d\u4e00\u628a\uff0c\u5e76\u5728\u6697\u4e2d\u75b2\u70b9\u4e86\u5c55\u767d\u80f8\u524d\u201c\u91c7\u53f0\u201d\u3001\u201c\u6c14\u6237\u201d\u4e24\u5927\u91cd\u7a74 \u4e00\u6307\uff0c\u662f\u4ee5\u5c55\u767d\u7acb\u65f6\u6e05\u9192\u8fc7\u6765\u3002 \u3000\u3000\u201c\u94f6\u7bab\u593a\u9b42\u7ae0\u8001\u524d\u8f88\uff0c\u201d\u9ed1\u8863\u5c11\u5973\u89c1\u5c55\u767d\u6e05\u9192\u8fc7\u6765\uff0c\u53c8\u6697\u4e2d\u63d0\u9192\u5c55\u767d\uff1a\u201c\u8981\u4ee5\u538b\u5012\u6b66\u6797 \u7684\u2018\u97f3\u9b54\u593a\u9b42\u7bab\u6cd5\u2019\u548c\u4f60\u6bd4\u6b66\uff0c\u4f60\u4f30\u91cf\u7740\u80fd\u63a5\u5f97\u4e0b\u6765\u5417\uff1f\u5982\u679c\u81ea\u8ba4\u4e0d\u884c\uff0c\u6700\u597d\u8fd8\u662f\u522b\u81ea\u627e\u82e6 \u5403\uff01\u201d \u3000\u3000\u9ed1\u8863\u5c11\u5973\u4e00\u5fc3\u7ef4\u62a4\u5c55\u767d\uff0c\u8bdd\u4e2d\u7279\u522b\u63d0\u51fa\u94f6\u7bab\u593a\u9b42\u7ae0\u58eb\u670b\u201c\u593a\u9b42\u7bab\u6cd5\u201d\u7684\u5389\u5bb3\uff0c\u662f\u6697\u793a\u5c55 \u767d\u4e0d\u8981\u901e\u5f3a\uff0c\u5982\u679c\u4e0d\u63a5\u53d7\u94f6\u7bab\u593a\u9b42\u7684\u6311\u6218\uff0c\u4ee5\u7ae0\u58eb\u670b\u5728\u6b66\u6797\u4e2d\u7684\u5730\u4f4d\u6765\u8bb2\uff0c\u65e0\u8bba\u5982\u4f55\u4e5f\u4e0d\u597d \u610f\u601d\u5bf9\u4e00\u4e2a\u665a\u751f\u540e\u8f88\uff0c\u9aa4\u65bd\u6740\u624b\u3002 \u3000\u3000\u53ef\u662f\uff0c\u5c55\u767d\u5374\u5b8c\u5168\u9519\u4f1a\u4e86\u9ed1\u8863\u5c11\u5973\u7684\u597d\u610f\u3002 \u3000\u3000\u4ed6\u88ab\u7bab\u58f0\u6240\u60d1\uff0c\u6c89\u6eba\u5728\u60b2\u75db\u54c0\u4f24\u7684\u5f80\u4e8b\u4e4b\u4e2d\uff0c\u8fc7\u5ea6\u7684\u54c0\u4f24\uff0c\u4f7f\u4ed6\u7070\u5fc3\u7edd\u671b\uff0c\u51e0\u4e4e\u5185\u8151\u771f \u5143\u6e83\u6563\uff0c\u5982\u679c\u4e0d\u662f\u9ed1\u8863\u5c11\u5973\u89c1\u673a\u5f97\u5feb\uff0c\u53ca\u65f6\u963b\u6b62\u94f6\u7bab\u593a\u9b42\u7ae0\u58eb\u670b\u7ee7\u7eed\u5439\u594f\uff0c\u53c8\u5728\u6697\u4e2d\u70b9\u5f00\u5c55 \u767d\u7a74\u9053\uff0c\u4f7f\u4ed6\u5185\u8151\u771f\u5143\u4e0d\u81f4\u6e83\u6563\u3002\u518d\u8fdf\u4e00\u523b\uff0c\u5c55\u81ea\u6050\u6015\u5df2\u7ecf\u4f24\u5728\u94f6\u8fa9\u593a\u9b42\u7684\u201c\u97f3\u9b54\u7bab\u6cd5\u201d\u4e4b \u4e0b\uff0c\u8fd9\u5728\u8868\u9762\u4e0a\u867d\u7136\u770b\u4e0d\u51fa\u4ec0\u4e48\u51f6\u9669\uff0c\u4e8b\u5b9e\u4e0a\u5c55\u767d\u5df2\u5728\u751f\u6b7b\u8fb9\u7f18\u4e0a\u515c\u4e00\u8f6c\u56de\u6765\u4e86\u3002 \u3000\u3000\u4f46\u5c55\u767d\u6e05\u9192\u4e4b\u540e\uff0c\u542c\u4e86\u9ed1\u8863\u5c11\u5973\u7684\u8bdd\uff0c\u8bef\u4ee5\u4e3a\u9ed1\u8863\u5c11\u5973\u4e5f\u5728\u77a7\u4e0d\u8d77\u4ed6\uff0c\u5251\u7709\u4e00\u6311\uff0c\u6602\u7136 \u8bf4\u9053\uff1a\u201c\u7ae0\u524d\u8f88\u2018\u593a\u9b42\u7bab\u6cd5\u2019\u5a01\u9707\u6b66\u6797\uff0c\u5c55\u767d\u4e43\u4e00\u540e\u751f\u665a\u5b66\uff0c\u5f97\u8046\u7ae0\u524d\u8f88\u7bab\u6cd5\uff0c\u4f55\u5e78\u5982\u4e4b\uff01 \u5c31\u8bf7\u8001\u524d\u8f88\u8d50\u6559\u5427\uff0c\u5728\u4e0b\u7eb5\u7136\u4e0d\u654c\uff0c\u5c31\u662f\u6b7b\u4e86\u4e5f\u4f1a\u6df1\u89c9\u8363\u5e78\uff01\u201d \u3000\u3000\u539f\u6765\u5c55\u767d\u8bef\u4f1a\u4e86\u9ed1\u8863\u5c11\u5973\u4e4b\u610f\uff0c\u4ed6\u4ece\u54c0\u4f24\u6c89\u75db\u4e4b\u4e2d\uff0c\u6062\u590d\u4e86\u7406\u667a\uff0c\u5fc3\u4e2d\u6697\u6697\u8d23\u5907\u81ea\u5df1\uff1a \u201c\u5c55\u767d\u5440\u5c55\u767d\uff01\u4f60\u7eb5\u7136\u53d7\u5c3d\u75db\u82e6\uff0c\u53ef\u4e5f\u4e0d\u80fd\u6bc1\u4e86\u95e8\u98ce\uff0c\u60f3\u5f53\u5e74\u7236\u4eb2\u638c\u4e2d\u4e00\u67c4\u2018\u65e0\u60c5\u78a7\u5251\u2019\uff0c \u8d70\u5251\u5357\u5317\uff0c\u89c1\u4e49\u52c7\u4e3a\uff0c\u4f55\u7b49\u6c14\u6982\uff1f\u81ea\u5df1\u7eb5\u4e0d\u80fd\u5b66\u5f97\u7236\u4eb2\u5f53\u5e74\u7684\u82f1\u52c7\uff0c\u4e5f\u4e0d\u80fd\u754f\u7f29\u82df\u6d3b\uff0c\u88ab\u522b \u4eba\u8fd9\u822c\u77a7\u4e0d\u8d77\uff01\u201d \u3000\u3000\u94f6\u7bab\u593a\u9b42\u7ae0\u58eb\u670b\uff0c\u7518\u5e74\u524d\u51ed\u4e00\u652f\u94f6\u7bab\uff0c\u5728\u534e\u5c71\u7edd\u9876\u4e0a\uff0c\u201c\u4e00\u7bab\u4f1a\u4e09\u8001\u201d\uff0c\u72ec\u81ea\u4e00\u4eba\uff0c\u529b \u654c\u7518\u5e74\u524d\u9886\u8896\u6b66\u6797\u7684\u201c\u4e2d\u539f\u4e09\u8001\u201d\uff0c\u58f0\u540d\u4e4b\u76db\uff0c\u53ef\u4ee5\u8bf4\u662f\u538b\u5012\u5929\u4e0b\u6b66\u6797\u3002\u8fd9\u4e9b\u6b66\u6797\u9057\u4e8b\uff0c\u7a0d \u4e3a\u6d89\u8db3\u6b66\u6797\u7684\u4eba\uff0c\u6ca1\u6709\u4e00\u4e2a\u4eba\u4e0d\u77e5\u9053\u3002\u5c55\u767d\u4e5f\u4e0d\u662f\u4e0d\u77e5\u9053\u94f6\u7bab\u593a\u9b42\u7ae0\u58eb\u670b\u7684\u5389\u5bb3\uff0c\u4f46\u4ed6\u5df2\u62b1 \u5b9a\u5fc5\u6b7b\u51b3\u5fc3\uff0c\u89c9\u5f97\u6108\u662f\u6b7b\u5728\u540d\u6c14\u5927\u7684\u6b66\u6797\u9ad8\u624b\u624b\u5185\uff0c\u6108\u89c9\u503c\u5f97\uff0c\u56e0\u6b64\uff0c\u6beb\u4e0d\u8003\u8651\u5730\u63a5\u53d7\u4e86\u94f6 \u7bab\u593a\u9b42\u7684\u6311\u6218\u3002 \u3000\u3000\u201c\u58ee\u54c9\uff01\u58ee\u54c9\uff01\u201d\u94f6\u8427\u593a\u9b42\u7ae0\u58eb\u670b\u89c1\u9ed1\u8863\u5c11\u5973\u9053\u51fa\u4e86\u81ea\u5df1\u7684\u540d\u53f7\uff0c\u6b63\u4ee5\u4e3a\u9762\u524d\u5c11\u5e74\uff0c\u7edd \u4e0d\u6562\u63a5\u53d7\u81ea\u5df1\u7684\u6311\u6218\uff0c\u4f46\u51fa\u4e4e\u610f\u5916\uff0c\u5c55\u767d\u7ade\u8c6a\u6c14\u5e72\u4e91\u5730\u63a5\u53d7\u4e86\u3002\u7ae0\u58eb\u670b\u8fde\u547c\u4e24\u58f0\u58ee\u54c9\uff0c\u6ee1\u9762 \u6b23\u7136\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u5c31\u8bf7\u5c0f\u54e5\u513f\u8046\u542c\u8001\u592b\u4e00\u66f2\uff01\u201d \u3000\u3000\u8bf4\u7f62\uff0c\u7adf\u81ea\u8e31\u5230\u4e00\u5757\u597d\u6574\u4ee5\u6687\u5730\u5750\u4e86\u4e0b\u6765\uff0c\u5148\u671b\u4e86\u671b\u5c55\u767d\uff0c\u62bf\u4e86\u62bf\u5634\u5507\uff0c\u839e\u5c14\u4e00\u7b11\uff0c\u7136 \u540e\u5c06\u90a3\u652f\u8d56\u4ee5\u6210\u540d\u7684\u70c2\u94f6\u7bab\uff0c\u51d1\u5728\u5507\u8fb9\u2026\u2026 \u3000\u3000\u9ed1\u8863\u5c11\u5973\u72e0\u72e0\u77aa\u4e86\u4ed6\u4e00\u773c\uff0c\u5fc3\u91cc\u4e8e\u7740\u6025\uff0c\u89c1\u4e8b\u5df2\u81f3\u6b64\uff0c\u5374\u65e0\u6cd5\u518d\u52a0\u963b\u62e6\u3002 \u3000\u3000\u5176\u4f59\u7684\u4eba\uff0c\u65e9\u5df2\u9000\u51fa\u8001\u8fdc\uff0c\u4e00\u9f50\u5f20\u5927\u773c\u775b\uff0c\u671b\u7740\u8fd9\u96be\u5f97\u4e00\u89c1\u7684\u7edd\u4e16\u6b66\u5b66\u2014\u2014\u201c\u97f3\u9b54\u8427 \u6cd5\u201d\u7684\u8868\u6f14\u3002 \u3000\u3000\u201c\u545c\u5f8b\uff01\u545c\u5f8b\uff01\u2026\u2026\u201d \u3000\u3000\u6e05\u8d8a\u60a0\u626c\u7684\u7b56\u58f0\uff0c\u5df2\u4ece\u94f6\u7bab\u593a\u9b42\u7ae0\u58eb\u670b\u7684\u5507\u8fb9\u54cd\u8d77\u3002 \u3000\u3000\u8fd9\u4e00\u6b21\u7684\u7bab\u58f0\uff0c\u5374\u4e0d\u4f3c\u521a\u624d\u5439\u594f\u7684\u90a3\u822c\u60b2\u4f24\u51c4\u51c9\u3002 \u3000\u3000\u8fd9\u662f\u4e00\u79cd\u6b22\u6109\u65e0\u6bd4\u7684\u60c5\u6000\uff0c\u597d\u50cf\u6625\u6696\u82b1\u5f00\uff0c\u767e\u9e1f\u55a7\u9e23\uff0c\u5927\u5730\u5145\u6ee1\u4e86\u751f\u547d\u7684\u6b22\u6b23\uff0c\u4f7f\u4eba\u542c \u4e86\uff0c\u53ea\u611f\u5230\u6ee1\u773c\u6625\u5149\uff0c\u5185\u5fc3\u8bf4\u4e0d\u51fa\u7684\u5fc3\u65f7\u795e\u60c5\u3002 \u3000\u3000\u53c8\u597d\u50cf\u4e00\u4e2a\u5e74\u8f7b\u7684\u60c5\u4eba\uff0c\u77e5\u9053\u90a3\u591a\u60c5\u7684\u7231\u4fa3\uff0c\u6b63\u5728\u767e\u82b1\u76db\u5f00\u7684\u56ed\u5730\u7b49\u7740\u4ed6\uff0c\u4ed6\u8f7d\u6b4c\u8f7d \u821e\uff0c\u4e00\u8def\u6b4c\u5531\u6b22\u8dc3\u5730\u8dd1\u7740\uff0c\u53bb\u5bfb\u627e\u4ed6\u7684\u7231\u4fa3\uff0c\u6295\u5728\u4ed6\u7231\u4fa3\u7684\u6000\u62b1\uff0c\u53ea\u6709\u5e78\u798f\uff0c\u53ea\u6709\u7231\uff0c\u53ea\u6709 \u5feb\u4e50\uff0c\u6ca1\u6709\u4e00\u4e1d\u513f\u60b2\u4f24\u4e0e\u82e6\u607c\u2026\u2026 \u3000\u3000\u5c55\u767d\u8fd9\u6b21\u6709\u4e86\u51c6\u5907\uff0c\u4ed6\u6f84\u6e05\u5fc3\u5fd7\uff0c\u62b1\u5143\u5b88\u4e00\uff0c\u5e76\u4e14\u4e5f\u5728\u8349\u5730\u4e0a\u76d8\u819d\u5750\u4e86\u4e0b\u6765\uff0c\u4e94\u5fc3\u671d \u5929\uff0c\u9ed8\u6d8c\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\u7ec3\u6c14\u79d8\u8bc0\uff1a\u201c\u6c14\u6709\u6e05\u6d4a\uff0c\u6e05\u5347\u6d4a\u964d\uff0c\u9053\u4e00\u6cd5\u4f17\u2026\u2026\u201d\u4ed6\u7adf\u7ec3\u8d77 \u6b63\u5b97\u5fc3\u6cd5\u4e2d\u7684\u5410\u7eb3\u529f\u592b\u6765\u4e86\u3002 \u3000\u3000\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\u4e0d\u6127\u4e3a\u5929\u4e0b\u7b2c\u4e00\u5947\u4e66\uff0c\u5c55\u767d\u6309\u7167\u5176\u4e2d\u79d8\u8bc0\u7ec3\u6c14\uff0c\u547c\u5438\u4e4b\u95f4\uff0c\u5df2\u8fbe\u7075 \u53f0\u6e05\u660e\u3001\u6d51\u7136\u5fd8\u6211\u4e4b\u5883\uff0c\u5bf9\u90a3\u611f\u91d1\u5316\u77f3\u7684\u7bab\u58f0\uff0c\u5145\u8033\u672a\u95fb\uff01 \u3000\u3000\u9000\u51fa\u8001\u8fdc\u7684\u4f17\u4eba\uff0c\u5c1a\u4e14\u585e\u4f4f\u8033\u6735\uff0c\u5728\u5982\u5929\u7c41\u4f3c\u7684\u7bab\u58f0\u611f\u67d3\u4e4b\u4e0b\uff0c\u51e0\u4e2a\u529f\u529b\u8f83\u6d45\u7684\u52b2\u88c5\u4f69 \u5200\u58ee\u6c49\uff0c\u5df2\u6709\u70b9\u62b5\u53d7\u4e0d\u4f4f\uff0c\u6ee1\u8138\u5411\u5f80\u6b23\u70ed\u4e4b\u60c5\uff0c\u8eab\u4e0d\u7531\u5df1\u5730\u624b\u821e\u8db3\u8e48\u8d77\u6765\u2026\u2026 \u3000\u3000\u5c31\u8fde\u5b9a\u529b\u6781\u5f3a\u3001\u4e14\u4e8b\u524d\u6709\u5907\u7684\u9ed1\u8863\u5c11\u5973\uff0c\u7adf\u4e5f\u7709\u98de\u8272\u821e\uff0c\u8dc3\u8dc3\u6b32\u52a8\uff0c\u6709\u70b9\u628a\u63e1\u4e0d\u4f4f\u7684\u6837 \u5b50\u2026\u2026\u2026 \u3000\u3000\u6b22\u6109\u7684\u7bab\u58f0\u7ee7\u7eed\u4e0b\u53bb\uff0c\u5c55\u767d\u4f9d\u7136\u65e0\u52a8\u4e8e\u8877\u2026\u2026 \u3000\u3000\u94f6\u7bab\u593a\u9b42\u7ae0\u58eb\u670b\u4e00\u8fb9\u5439\u7bab\uff0c\u4e00\u8fb9\u53cc\u76ee\u6ce8\u5b9a\u5c55\u767d\uff0c\u89c1\u5c55\u81ea\u95ed\u76ee\u5782\u7709\uff0c\u8dcc\u5750\u5728\u8349\u5730\u4e0a\uff0c\u4e1d\u6beb \u4e0d\u4e3a\u7bab\u58f0\u6240\u52a8\uff0c\u4e0d\u7531\u5fc3\u4e2d\u5927\u611f\u8be7\u5f02\uff0c\u6697\u9053\uff1a\u201c\u6b64\u4e8e\u6839\u9aa8\u867d\u4f73\uff0c\u770b\u6837\u5b50\u6b66\u529f\u5e76\u65e0\u591a\u5927\u6839\u5e95\uff0c\u600e \u80fd\u5177\u6709\u5982\u6b64\u9ad8\u5f3a\u7684\u5b9a\u529b\uff1f\u7ade\u4e0d\u4e3a\u6211\u7684\u7bab\u58f0\u6240\u52a8\u2026\u3002\u201d \u3000\u3000\u94f6\u8427\u593a\u9b42\u5fc3\u4e2d\u8fd9\u6837\u60f3\u7740\uff0c\u5439\u594f\u7684\u7bab\u58f0\u4e00\u53d8\uff1a\u7531\u6b22\u6109\u4e4b\u60c5\uff0c\u4e00\u53d8\u4e3a\u60b2\u4f24\u6101\u82e6\u4e4b\u97f3\u3002 \u3000\u3000\u6b22\u6109\u7684\u7bab\u58f0\uff0c\u4e00\u53d8\u4e3a\u60b2\u6101\u51c4\u82e6\u4e4b\u97f3\uff0c\u5c31\u597d\u50cf\u7531\u6625\u6696\u82b1\u5f00\u4e4b\u5883\uff0c\u4e00\u4e0b\u4e8e\u6389\u8fdb\u8427\u6740\u7684\u5bd2\u51ac\uff1a \u51b0\u96ea\u5c01\u51bb\u4e86\u5927\u5730\u7684\u751f\u673a\uff0c\u6ee1\u773c\u7e41\u82b1\u88ab\u72c2\u66b4\u7684\u5bd2\u98ce\u5439\u6b8b\uff0c\u6b22\u4e50\u5df2\u6210\u8fc7\u53bb\uff0c\u5e78\u798f\u53d8\u4e3a\u60b2\u4f24\uff0c\u90a3\u540c \u5fc3\u8fde\u7406\u7684\u7231\u4fa3\u7115\u7136\u4e27\u5931\u4e86\uff0c\u5e0c\u671b\u6ca1\u6709\u4e86\uff0c\u5145\u585e\u5728\u76ee\u524d\u7684\u4e00\u5207\uff0c\u5747\u662f\u4ee4\u4eba\u7070\u5fc3\u7684\u3001\u7edd\u671b\u7684\uff0c\u597d \u50cf\u53ea\u662f\u4e00\u7247\u6e3a\u832b\u65e0\u5e95\u7684\u6df1\u8c37\uff0c\u53ea\u6709\u9ed1\u6697\u4e0e\u7a7a\u5c55\uff0c\u51cc\u98ce\u516c\u5b50\u3001\u7707\u76ee\u9053\u4eba\u3001\u79c3\u9876\u8001\u8005\u7b49\u4eba\uff0c\u56e0\u529f \u529b\u6df1\u539a\uff0c\u8ddd\u79bb\u8f83\u8fdc\uff0c\u5c1a\u672a\u4e3a\u7bab\u58f0\u6240\u5de6\u53f3\u3002\u4f46\u90a3\u8ddf\u5728\u51cc\u98ce\u516c\u5b50\u8eab\u540e\u7684\u516d\u540d\u52b2\u88c5\u4f69\u5200\u5927\u6c49\uff0c\u5374\u968f \u7740\u7bab\u58f0\u7684\u97f3\u5f8b\u800c\u53d8\u5e7b\uff0c\u7bab\u58f0\u559c\uff0c\u4ed6\u4eec\u4e5f\u8ddf\u7740\u6b22\u6b23\u9f13\u821e\uff1b\u7bab\u58f0\u8f6c\u60b2\uff0c\u4ed6\u4eec\u4e5f\u968f\u7740\u60b2\u4f24\u5e0c\u5618\u8d77 \u6765\u3002 \u3000\u3000\u5c31\u662f\u5b9a\u529b\u6781\u9ad8\u7684\u9ed1\u8863\u5c11\u5973\uff0c\u56e0\u4e3a\u5c31\u7ad9\u5728\u5c55\u767d\u4e00\u4fa7\uff0c\u8ddd\u79bb\u8f83\u8fd1\uff0c\u7ade\u4e5f\u88ab\u7bab\u58f0\u611f\u67d3\u3002\u5148\u524d\u7bab \u58f0\u6b22\u6109\uff0c\u5979\u6ee1\u8138\u6b23\u559c\u4e4b\u5bb9\uff0c\u7f8e\u5f97\u51fa\u5947\u7684\u7c89\u8138\u4e0a\uff0c\u867d\u7136\u88ab\u9ed1\u7eb1\u906e\u4f4f\u4e00\u534a\uff0c\u4ecd\u80fd\u89c1\u5230\u5979\u201c\u7709\u5982\u6625 \u5c71\u6a2a\uff0c\u773c\u5982\u79cb\u6c34\u805a\u201d\uff0c\u7709\u773c\u76c8\u76c8\uff0c\u7b11\u5bb9\u5982\u82b1\uff0c\u6709\u63a9\u4e0d\u4f4f\u7684\u5185\u5fc3\u6b23\u60a6\u4e4b\u60c5\uff1b\u5982\u4eca\uff0c\u7bab\u58f0\u8f6c\u60b2\uff0c \u5979\u7709\u773c\u95f4\u7684\u7b11\u5bb9\u6e10\u6e10\u6d88\u5931\u4e86\uff0c\u4ee3\u4e4b\u800c\u8d77\u7684\u9edb\u7709\u6df1\u9501\uff0c\u661f\u76ee\u542b\u60b2\uff0c\u6ee1\u8138\u7684\u5e7d\u4f24\u54c0\u6028\uff0c\u5f85\u7bab\u58f0\u54bd \u9e23\u51c4\u5207\u8fbe\u5230\u9ad8\u6f6e\u4e4b\u9645\uff0c\u7adf\u5c14\u6389\u4e0b\u6cea\u6765\u3002 \u3000\u3000\u5c55\u767d\u4f9d\u7136\u4e0d\u4e3a\u6240\u52a8\uff0c\u8fd9\u5c31\u4f7f\u94f6\u7bab\u593a\u9b42\u7ae0\u58eb\u670b\u66f4\u611f\u60ca\u5947\u4e86\u3002 \u3000\u3000\u201c\u9633\u6625\u8638\u9732\u201d\u3001\u201c\u5bd2\u51ac\u745e\u96ea\u201d\uff0c\u8fde\u6f14\u4e24\u5927\u4e50\u7ae0\uff0c\u7adf\u7136\u6ca1\u6709\u611f\u52a8\u4e00\u4e2a\u5c11\u5e74\uff01I\u201c\u545c\u5f8b\uff01\u545c \u6d25\uff01\u201d \u3000\u3000\u7bab\u58f0\u53c8\u4e00\u53d8\uff0c\u7531\u201c\u5bd2\u51ac\u745e\u96ea\u201d\u8f6c\u53d8\u4e3a\u201c\u79cb\u98ce\u971c\u5200\u201d\u3002 \u3000\u3000\u54bd\u545c\u5e7d\u4f24\u7684\u7bab\u58f0\uff0c\u987f\u65f6\u53d8\u4e3a\u6fc0\u6602\u8427\u6740\u4e4b\u97f3\u3002 \u3000\u3000\u6e05\u8d8a\u6fc0\u6602\u7684\u7bab\u58f0\uff0c\u6108\u6765\u6108\u9ad8\u4ea2\uff0c\u6108\u6765\u6108\u6fc0\u8d8a\uff0c\u5230\u540e\u6765\u7ade\u5982\u6218\u9a6c\u60b2\u9e23\uff0c\u53f7\u89d2\u6025\u6b27\uff0c\u597d\u50cf\u5343 \u519b\u4e07\u9a6c\u9707\u5929\u52a8\u5730\u800c\u6765\u4e00\u822c\uff01 \u3000\u3000\u6740\u4f10\u7684\u91d1\u97f3\uff0c\u9707\u8033\u60ca\u5fc3\uff0c\u4f7f\u4eba\u72b9\u5982\u7f6e\u8eab\u4e8e\u60e8\u70c8\u7684\u6218\u573a\uff01 \u3000\u3000\u201c\u545b\uff01\u545b\uff01\u2026\u2026\u201d\u4e00\u7247\u91d1\u94c1\u4ea4\u9e23\u4e4b\u58f0\uff0c\u8ddf\u968f\u51cc\u98ce\u516c\u5b50\u7684\u516d\u540d\u52b2\u88c5\u58ee\u6c49\uff0c\u7adf\u7136\u628a\u6301\u4e0d\u4f4f\uff0c \u7eb7\u7eb7\u62bd\u51fa\u8170\u95f4\u7684\u4f69\u5200\uff0c\u4e92\u76f8\u780d\u6740\u8d77\u6765\u3002 \u3000\u3000\u7bab\u9f13\u58f0\u4e2d\uff0c\u5200\u5d29\u8840\u73b0\uff0c\u65ad\u80a2\u4e0e\u6b8b\u5203\u9f50\u98de\uff0c\u516d\u540d\u52b2\u88c5\u5927\u6c49\uff0c\u72b6\u5982\u75af\u72b6\uff0c\u4e92\u76f8\u780d\u6740\u4e4b\u95f4\uff0c\u5df2 \u6709\u4e09\u56db\u4eba\u8d1f\u4f24\u6302\u5f69\uff01 \u3000\u3000\u51cc\u98ce\u516c\u5b50\u6025\u58f0\u559d\u6b62\uff0c\u7ade\u4e0d\u80fd\u963b\u4f4f\u516d\u540d\u624b\u4e0b\u4e4b\u75af\u72c2\u780d\u6740\uff0c\u79c3\u9876\u8001\u8005\u53cc\u7709\u4e00\u76b1\uff0c\u51fa\u624b\u5982\u98ce\uff0c \u7acb\u523b\u70b9\u4e86\u516d\u540d\u7261\u6c49\u7684\u7a74\u9053\uff0c\u516d\u540d\u58ee\u6c49\u4fbf\u50cf\u6728\u96d5\u6ce5\u5851\u5730\u5446\u4f4f\uff0c\u4f46\u4e00\u4e2a\u4e2a\u4ecd\u7136\u6012\u76ee\u91d1\u94a2\u822c\u7684\uff0c\u4e3e \u5200\u6b32\u6251\u3002 \u3000\u3000\u800c\u4e14\u4e00\u4e2a\u4e2a\u8eab\u4e0a\u8fd8\u6d41\u7740\u8840\uff0c\u72b6\u6781\u53ef\u6016\u2026\u2026 \u3000\u3000\u9ed1\u8863\u5c11\u5973\u7eaf\u7f8e\u7684\u7709\u76ee\u4e4b\u95f4\uff0c\u9690\u7136\u73b0\u51fa\u4e00\u822c\u6d53\u91cd\u6740\u795d\uff0c\u4f46\u5979\u4ecd\u7136\u54ac\u7259\u5f3a\u5fcd\u7740\uff0c\u7709\u5fc3\u5fae\u73b0\u6c57 \u8ff9\uff0c\u53ef\u89c1\u5176\u5df2\u7ecf\u5f88\u5403\u529b\u4e86\u2026\u3002\u2018\u201c\u7ae0\u5144\uff01\u201d\u7707\u76ee\u9053\u4eba\u867d\u7136\u53cc\u76ee\u5df2\u76f2\uff0c\u4f46\u542c\u89c9\u7279\u522b\u654f\u9510\uff0c\u542c\u97f3 \u8fa8\u4f4d\uff0c\u5bf9\u9644\u8fd1\u72b6\u51b5\u4e86\u5982\u6307\u638c\uff0c\u77e5\u9053\u94f6\u7bab\u593a\u9b42\u7ae0\u58eb\u670b\u7684\u593a\u9b42\u7bab\u201c\uff0c\u5e76\u672a\u5236\u4f4f\u773c\u524d\u5c11\u5e74\uff0c\u81ea\u5df1\u4eba \u53cd\u800c\u95f9\u4e86\u4e2a\u72fc\u72c8\u4e0d\u582a\uff0c\u5f53\u5373\u4ee5\u201d\u4f20\u97f3\u5165\u5bc6\u201c\u7edd\u6280\uff0c\u5bf9\u7ae0\u58eb\u670b\u8bf4\u9053\uff1a\u201c\u6b64\u5c11\u5e74\u6709\u70b9\u602a\u9053\uff0c\u6700\u597d \u8fd8\u662f\u6362\u4e2a\u65b9\u6cd5\u5236\u4f4f\u4ed6\uff0c\u4ee5\u514d\u60ca\u52a8\u8001\u7237\u5b50\uff01\u201d \u3000\u3000\u7707\u76ee\u9053\u4eba\u662f\u7528\u201c\u4f20\u97f3\u5165\u5bc6\u201d\u7edd\u6280\uff0c\u4e0e\u94f6\u7bab\u593a\u9b42\u8bb2\u8bdd\u3002\u522b\u4eba\u53ea\u80fd\u770b\u5230\u4ed6\u5634\u5507\u7fd5\u52a8\uff0c\u5374\u4e0d\u80fd \u542c\u5230\u4ed6\u8bf4\u4e9b\u4ec0\u4e48\u3002\u552f\u6709\u94f6\u7bab\u593a\u9b42\u542c\u5f97\u6e05\u695a\uff0c\u4f46\u7707\u76ee\u9053\u4eba\u7684\u8bdd\u53cd\u800c\u6fc0\u8d77\u4e86\u94f6\u7bab\u593a\u9b42\u7684\u6012\u706b\uff0c\u4ed6 \u60f3\u5230\u81ea\u5df2\u4ed7\u4ee5\u6210\u540d\u7684\u201c\u97f3\u9b54\u593a\u9b42\u7bab\u6cd5\u201d\uff0c\u7adf\u5236\u4e0d\u4f4f\u4e00\u4e2a\u540d\u4e0d\u89c1\u7ecf\u4f20\u7684\u5c11\u5e74\uff0c\u8fd9\u82e5\u4f20\u51fa\u6c5f\u6e56\u8fd8 \u50cf\u4ec0\u4e48\u8bdd\uff01\u56e0\u6b64\uff0c\u4ed6\u4e0d\u4f46\u6ca1\u6709\u505c\u6b62\uff0c\u53cd\u800c\u628a\u7bab\u58f0\u66f4\u7d27\u51d1\u5730\u5439\u594f\u8d77\u6765\u3002 \u3000\u3000\u4ed6\u7bab\u58f0\u53c8\u4e3a\u4e4b\u4e00\u53d8\uff0c\u4f46\u4e0d\u8bba\u4ed6\u7684\u7b56\u58f0\u600e\u6837\u53d8\u5316\uff0c\u751a\u81f3\u628a\u5f15\u4eba\u9009\u5931\u672c\u6027\u7684\u559c\uff0c\u6012\uff0c\u54c0\uff0c \u60e7\uff0c\u50e7\u2026\u2026\u4e03\u60c5\u516d\u6b32\uff0c\u90fd\u4e00\u4e00\u6f14\u904d\u4e86\uff0c\u4ecd\u7136\u4e0d\u80fd\u64bc\u52a8\u5c55\u767d\u3002 \u3000\u3000\u6b64\u65f6\uff0c\u5c55\u767d\u8dc3\u5750\u5728\u8349\u5730\u4e0a\uff0c\u4e94\u5fc3\u671d\u5929\uff0c\u95ed\u76ee\u5782\u7709\uff0c\u7eaf\u7f8e\u4e4b\u4e2d\u5e26\u7740\u51e0\u5206\u7ae5\u7a1a\u7684\u8138\u4e0a\uff0c\u9690\u7136 \u6cdb\u8d77\u4e00\u5c42\u5b9d\u5149\uff0c\u9752\u5c71\u7eff\u91ce\uff0c\u8273\u9633\u7167\u8000\u4e4b\u4e0b\uff0c\u7ade\u6709\u5b9d\u76f8\u5e84\u4e25\u3001\u4e00\u6d3e\u51db\u7136\u4e0d\u53ef\u4fb5\u72af\u7684\u795e\u5723\u6c14\u6c1b\u3002 \u3000\u3000\u94f6\u7bab\u593a\u9b42\u4e00\u652f\u94f6\u7bab\uff0c\u7eb5\u6a2a\u6c5f\u6e56\u6570\u5341\u5e74\uff0c\u6240\u5411\u65e0\u654c\uff0c\u4e00\u751f\u4e4b\u4e2d\uff0c\u6700\u5f97\u610f\u7684\u58ee\u4e3e\u662f\u534e\u5c71\u7edd\u9876 \u201c\u4e00\u7bab\u4f1a\u4e09\u8001\u201d\uff0c\u51ed\u7740\u201c\u97f3\u9b54\u7bab\u6cd5\u201d\u4e4b\u5965\u5999\u96be\u6d4b\uff0c\u7adf\u4f7f\u5f53\u5e74\u9886\u8896\u6b66\u6797\u7684\u201c\u4e2d\u539f\u4e09\u8001\u201d\uff0c\u77e5\u96be \u800c\u9000\u3002\u6b64\u540e\uff0c\u4ed6\u529f\u529b\u66f4\u662f\u5927\u8fdb\uff0c\u4e0d\u8fc7\u5e74\u4e8b\u6e10\u9ad8\uff0c\u5df2\u7565\u77e5\u9690\u6666\uff0c\u4e0d\u590d\u5982\u58ee\u5e74\u65f6\u4e4b\u950b\u8292\u6bd5\u9732\u7f62 \u4e86\u3002\u4eca\u65e5\uff0c\u4ed6\u89c1\u5c55\u767d\u5c0f\u5c0f\u5e74\u7eaa\uff0c\u7ade\u80fd\u4e00\u638c\u51fb\u6bd9\u9648\u6e05\u3001\u9648\u5e73\uff0c\u8f7b\u6613\u8eb2\u8fc7\u5929\u6daf\u72c2\u751f\u4e09\u5927\u6740\u62db\uff0c\u53c8 \u628a\u5929\u6daf\u72c2\u751f\u6c14\u8d70\uff0c\u800c\u5f15\u8d77\u4e86\u4ed6\u7684\u8c6a\u5174\uff0c\u60f3\u4ee5\u201c\u97f3\u9b54\u7bab\u6cd5\u201d\u8bd5\u8bd5\u5c55\u767d\u5230\u5e95\u6709\u591a\u5927\u9053\u884c\uff1f \u3000\u3000\u4e0d\u8fc7\uff0c\u8d77\u521d\u4ed6\u5e76\u672b\u5168\u529b\u65bd\u4e3a\uff0c\u53ea\u60f3\u4ee5\u201c\u6625\u9633\u51ac\u96ea\u201d\u7b49\u66f2\u8c31\u3002\u4fbf\u81ea\u8ba4\u4e3a\u53ef\u5236\u670d\u5c55\u767d\u3002 \u3000\u3000\u6ca1\u60f3\u5230\uff0c\u4ed6\u8fde\u6f14\u591a\u6625\u3001\u590f\u3001\u79cb\u3001\u51ac\u201c\u56db\u7ae0\uff0c\u53c8\u628a\u80fd\u8ff7\u5931\u4eba\u7c7b\u672c\u6027\u7684\u201d\u4e03\u60c5\u516d\u6b32\u201c\u7ae0\u8282\uff0c \u4e00\u4e00\u6f14\u594f\uff0c\u5c55\u767d\u7ade\u4e1d\u6beb\u672b\u4e3a\u6240\u52a8\uff0c\u94f6\u7bab\u593a\u9b42\u53c8\u6025\u53c8\u6012\uff0c\u7acb\u523b\u628a\u201d\u97f3\u9b54\u7bab\u6cd5\u201c\u4e2d\u6700\u5389\u5bb3\u7684\u201d\u593a \u9b42\u7bab\u6cd5\u201c\u65bd\u5c55\u51fa\u6765\uff0c\u7adf\u5168\u529b\u65bd\u4e3a\u8d77\u6765\u3002\u4f46\uff0c\u5c55\u767d\u4f9d\u7136\u672a\u4e3a\u6240\u52a8\uff0c\u94f6\u7bab\u593a\u9b42\u4e0d\u9ece\u6697\u6697\u7eb3\u7f55\u3002\u5fc3 \u4e2d\u4e0d\u4fe1\u6697\u9053\uff1a\u201c\u8fd9\u5c11\u5e74\u660e\u5fc3\u89c1\u6027\uff0c\u5b9a\u529b\u4e4b\u5f3a\u4f1a\u8d85\u8fc7\u4e2d\u539f\u4e09\u8001\uff1f\u201d \u3000\u3000\u94f6\u7bab\u593a\u9b42\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\uff0c\u5c55\u767d\u8fd0\u529f\u7528\u4ee5\u5bf9\u6297\u201c\u97f3\u9b54\u7bab\u6cd5\u201d\u7684\uff0c\u7adf\u662f\u5929\u4e0b\u7b2c\u4e00\u5947\u4e66\u300a\u9501\u9aa8 \u9500\u9b42\u5929\u4f5b\u5377\u300b\u4e2d\u6240\u8f7d\uff0c\u6b66\u6797\u7edd\u54cd\u7684\u4f5b\u95e8\u6b63\u5b97\u5fc3\u6cd5\uff01 \u3000\u3000\u60f3\u5f53\u5e74\u201c\u53ea\u773c\u90ce\u541b\u201d\uff0c\u53d7\u7edd\u4ee3\u5c24\u7269\u201c\u5929\u4ed9\u9b54\u5973\u201d\u7684\u201c\u59e5\u5973\u8ff7\u9b42\u5927\u6cd5\u201d\u6240\u82e6\uff0c\u5b9a\u706b\u4eba\u9b54\uff0c \u4ee5\u201c\u53ea\u773c\u90ce\u541b\u201d\u4e4b\u7edd\u4e16\u5947\u624d\uff0c\u5c1a\u4e14\u51e0\u4e4e\u6ca6\u4e8e\u4e07\u52ab\u4e0d\u590d\u4e4b\u5730\u3002\u591a\u4e8f\u6b66\u5f53\u638c\u95e8\u201c\u94c1\u5fc3\u5927\u5e08\u201d\u4e0e\u5c11 \u6797\u638c\u6559\u201c\u82e6\u6c34\u4e0a\u4eba\u201d\uff0c\u4ee5\u91ca\u9053\u4e24\u95e8\u65e0\u4e0a\u5927\u5fb7\uff0c\u5c06\u4ed6\u6551\u8f6c\uff0c\u4ed6\u624d\u4ee5\u5e73\u751f\u82e6\u5b66\u6240\u5f97\uff0c\u7ed8\u5c31\u4e86\u8fd9\u672c \u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\uff0c\u5176\u4e2d\u70bc\u6c14\u5fa1\u9b54\u7684\u6cd5\u672f\uff0c\u53ef\u4ee5\u8bf4\u662f\u8d85\u7edd\u4eca\u53e4\uff0c\u80fd\u591f\u52d8\u7834\u201c\u5929\u4ed9\u9b54\u5973\u201d\u90a3 \u7edd\u4ee3\u5c24\u7269\u7684\u8ff7\u60d1\uff0c\u4e16\u4e0a\u8fd8\u6709\u4ec0\u4e48\u4e1c\u897f\u53ef\u4ee5\u8ff7\u60d1\u4e86\u7684\uff1f\u56e0\u6b64\uff0c\u94f6\u7bab\u593a\u9b42\u7684\u201c\u97f3\u9b54\u7bab\u6cd5\u201d\u518d\u5389 \u5bb3\uff0c\u4e5f\u65e0\u6cd5\u52a8\u5f97\u5c55\u767d\u5206\u6beb\u3002 \u3000\u3000\u53ef\u662f\uff0c\u4ee5\u201c\u5929\u4e0b\u65e0\u654c\u201d\u81ea\u8be9\u7684\u94f6\u7bab\u593a\u9b42\uff0c\u600e\u80af\u5c31\u6b64\u7518\u5fc3\uff1f\u4ed6\u56e2\u56e2\u7684\u5706\u8138\u4e0a\u6da8\u5f97\u901a\u7ea2\uff0c\u53cc \u76ee\u795e\u5149\u66b4\u5c04\uff0c\u9f13\u5507\u4f5c\u6c14\uff0c\u7adf\u628a\u6700\u5389\u5bb3\u7684\u201c\u97f3\u9b54\u5e7b\u5883\u201d\u6f14\u594f\u51fa\u6765\uff01 \u3000\u3000\u6e05\u8d8a\u9707\u8033\u7684\u7b56\u58f0\uff0c\u9ad8\u4ea2\u5165\u4e91\uff0c\u76f4\u53ef\u7a7f\u91d1\u7834\u77f3\uff0c\u201c\u545c\u5f8b\uff0c\u545c\u5f8b\uff01\u201d \u3000\u3000\u7684\u9510\u97f3\u6276\u6447\u76f4\u4e0a\uff0c\u8885\u8885\u7136\u5347\u9ad8\uff0c\u5347\u9ad8\uff0c\u518d\u5347\u9ad8\uff01\u51e0\u4e4e\u5347\u9ad8\u81f3\u4e5d\u9704\u94f6\u6c49\uff0c\u500f\u7136\u53c8\u56db\u6563\u7740\u4ece \u9ad8\u7a7a\u6447\u843d\u4e0b\u6765\uff0c\u4e00\u4e2a\u97f3\u5f8b\uff0c\u5206\u6563\u6210\u5343\u6210\u4e07\u4e2a\u7f24\u7eb7\u7684\u97f3\u7b26\uff0c\u72b9\u5982\u5929\u5973\u6563\u82b1\u822c\u6d12\u843d\u4e0b\u6765\uff0c\u201c\u7f24\u7eb7 \u82b1\u96e8\u201d\u3001\u201c\u745e\u96ea\u6f2b\u7a7a\u98de\u821e\u201d\u2026\u3002\u751a\u81f3\u201c\u5929\u9f99\u8131\u7532\u201d\u3001\u201c\u6413\u788e\u7684\u4e03\u5f69\u8679\u9713\u201d\u6f2b\u7a7a\u6563\u843d\u4e0b\u6765\uff0c\u4e5f \u6ca1\u6709\u8fd9\u822c\u591a\u5f69\u591a\u59ff\u2026\u2026 \u3000\u3000\u51cc\u98ce\u516c\u5b50\u907d\u7136\u8272\u53d8\uff0c\u79c3\u9876\u8001\u8005\u9ad8\u53eb\u4e86\u4e00\u58f0\u201c\u901f\u9000\uff01\u201d\u62b1\u4f4f\u51cc\u98ce\u516c\u5b50\u7684\u624b\u8155\uff0c\u98d8\u7136\u540e\u8dc3\u5341 \u6570\u4e08\u5916\u3002 \u3000\u3000\u7707\u76ee\u9053\u4eba\u77e5\u9053\u94f6\u7bab\u593a\u9b42\u5df2\u7ecf\u50f5\u4e0a\u4e86\uff0c\u6697\u81ea\u6447\u5934\u592a\u606f\uff0c\u4f46\u4e8b\u5df2\u81f3\u6b64\uff0c\u65e0\u6cd5\u62e6\u963b\uff0c\u53ea\u597d\u968f\u7740 \u79c3\u9876\u8001\u8005\u9000\u540e\u5341\u6570\u6587\u5916\uff0c\u4ee5\u7ef4\u62a4\u51cc\u98ce\u516c\u5b50\u2026\u2026 \u3000\u3000\u4e03\u5f69\u7f24\u7eb7\u7684\u7bab\u58f0\u4e2d\uff0c\u5b9b\u7136\u6709\u65e0\u6570\u7f8e\u5999\u7684\u5c11\u5973\uff0c\u88f8\u80a2\u9732\u4f53\uff0c\u7fe9\u7fe9\u8d77\u821e\uff0c\u6625\u8272\u65e0\u8fb9\u2026\u2026\u8fd9\u662f \u5728\u5c11\u7537\u5fc3\u4e2d\u7684\u5e7b\u8c61\u3002\u4f46\u5728\u5c11\u5973\u5fc3\u4e2d\uff0c\u5374\u53c8\u4e0d\u540c\uff0c\u604d\u60da\u4e2d\u4e00\u4e2a\u6bd4\u7406\u60f3\u66f4\u5b8c\u7f8e\u7684\u9752\u5e74\u7537\u5b50\uff0c\u5728\u5979 \u8eab\u8fb9\u8f6f\u8bed\u6e29\u5b58\uff0c\u7ec6\u8bc9\u8877\u60c5\u3002\u4ed6\u7684\u70ed\u60c5\uff0c\u4ed6\u7684\u81f3\u9ad8\u65e0\u4e0a\u7684\u7231\uff0c\u4ed6\u7684\u7eaf\u7f8e\uff0c\u53ef\u4ee5\u6eb6\u5316\u4e16\u4e0a\u4efb\u4f55\u5c11 \u5973\u7684\u5fc3\u2026\u2026\u5982\u679c\u662f\u5728\u4e00\u4e2a\u8d2a\u8d22\u5974\u7684\u9762\u524d\uff0c\u53c8\u4f1a\u73b0\u51fa\u5927\u628a\u7684\u91d1\u949e\uff0c\u6210\u5806\u7684\u9ec4\u91d1\uff0c\u8000\u773c\u751f\u8f89\u7684\u73e0 \u5b9d\u2026\u2026\u82e5\u662f\u4e00\u4e2a\u7231\u6155\u76db\u540d\u7684\u4eba\uff0c\u5728\u4ed6\u773c\u524d\u53c8\u4f1a\u5e7b\u5316\u51fa\u7687\u51a0\uff0c\u5e1d\u5195\uff0c\u6570\u4e0d\u6e05\u7684\u5802\u800c\u7687\u4e4b\u7684\u5927\u5e3d \u5b50\u2026\u2026\u603b\u4e4b\u201c\u5e7b\u7531\u5fc3\u751f\uff0c\u666f\u7531\u5fc3\u9020\u201d\uff0c\u90a3\u5965\u5999\u7684\u7bab\u58f0\uff0c\u80fd\u968f\u5404\u4eba\u5fc3\u5934\u6240\u6b32\uff0c\u5e7b\u5316\u51fa\u4f60\u7231\u6155\u3001 \u5411\u5f80\u3001\u7ec8\u751f\u8ffd\u6c42\u5f97\u4e0d\u5230\u7684\u4e1c\u897f\uff0c\u663e\u73b0\u5728\u4f60\u7684\u9762\u524d\uff0c\u51e0\u4e4e\u4f38\u624b\u53ef\u5f97\u3002 \u3000\u3000\u8bd5\u60f3\uff0c\u8fd9\u6837\u9488\u5bf9\u5404\u4eba\u5fc3\u5934\u7684\u68a6\u60f3\u3001\u6b32\u671b\uff0c\u6709\u8c01\u80fd\u591f\u6297\u62d2\uff1f\u6709\u8c01\u80fd\u591f\u4e0d\u53d7\u5f15\u8bf1\uff1f\u800c\u4e0d\u5815\u5165 \u4ed6\u7684\u672f\u4e2d\uff1f \u3000\u3000\u4f46\uff0c\u5c55\u767d\u4f9d\u7136\u4e0d\u4e3a\u6240\u52a8\u3002 \u3000\u3000\u8fd9\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\u7684\u4f5b\u95e8\u6b63\u5b97\u5fc3\u6cd5\uff0c\u5b9a\u529b\u4e4b\u5f3a\uff0c\u7684\u786e\u4e0d\u540c\u51e1\u54cd\uff01 \u3000\u3000\u53ef\u662f\uff0c\u7ad9\u5728\u4e00\u8fb9\u7684\u9ed1\u8863\u5c11\u5973\u6155\u5bb9\u7ea2\u3001\u540d\u91cd\u6b66\u6797\u201c\u56db\u5927\u516c\u5b50\u201d\u4e4b\u4e2d\u201c\u65e0\u60c5\u516c\u5b50\u201d\u7684\u80de\u59d0\u3001 \u9886\u8896\u5317\u4e94\u7701\u5f53\u4eca\u6b66\u6797\u7684\u8c79\u7a81\u5c71\u5e84\u5e84\u4e3b\u3001\u201c\u6458\u661f\u624b\u201d\u6155\u5bb9\u6db5\u7684\u5973\u516c\u5b50\uff0c\u7adf\u7136\u5931\u53bb\u4e86\u5e38\u6001\u3002 \u3000\u3000\u53ea\u89c1\u5979\u82d7\u6761\u5982\u67f3\u7684\u5a07\u8eaf\uff0c\u4e00\u9635\u98a4\u6296\uff0c\u5982\u7389\u6d01\u767d\u7684\u808c\u80a4\u9690\u6cdb\u51fa\u7ea2\u6655\u7684\u8272\u5f69\uff0c\u5982\u8fdc\u5c71\u542b\u70df\u7684 \u9edb\u7709\u5fae\u8e59\u7740\uff0c\u4f3c\u662f\u71ac\u53d7\u7740\u5fc3\u4e2d\u7684\u75db\u82e6\uff0c\u5a9a\u5982\u6625\u6c34\u51dd\u805a\u7684\u773c\u6ce2\uff0c\u76c8\u76c8\u6b32\u6d41\uff0c\u53c8\u4f3c\u6709\u7740\u8bf4\u4e0d\u51fa\u7684 \u6b32\u671b\uff0c\u8138\u6cdb\u6843\u82b1\uff0c\u5a07\u7f9e\u4e4b\u4e2d\uff0c\u53c8\u89c2\u51fa\u63a9\u9970\u4e0d\u4f4f\u7684\u5a9a\u6001\u2026\u2026 \u3000\u3000\u201c\u5403\uff01\u201d\u6155\u5bb9\u7ea2\u6b32\u706b\u70eb\u8eab\uff0c\u4e00\u62ac\u7d20\u624b\uff0c\u628a\u8986\u9762\u9ed1\u7eb1\u626f\u843d\uff0c\u987a\u624b\u4e22\u5728\u4e00\u8fb9\uff01 \u3000\u3000\u554a\uff01\u6a31\u5507\u7d20\u53e3\uff0c\u5a07\u8273\u6b32\u6ef4\uff0c\u5982\u7389\u67f1\u822c\u7684\u7476\u9f3b\uff0c\u66f4\u662f\u7eaf\u7f8e\u73b2\u73d1\uff0c\u51dd\u8102\u4f3c\u7684\u6d51\u5706\u4e0b\u5df4\uff0c\u5c31\u662f \u6700\u9ad8\u660e\u7684\u96d5\u523b\u5bb6\u4e5f\u65e0\u6cd5\u5851\u51fa\u5982\u6b64\u5b8c\u7f8e\u7684\u5178\u578b\uff01\u201c\u9ed1\u8863\u5c11\u5973\u201d\u6155\u5bb9\u7ea2\uff0c\u73b0\u9732\u4e86\u771f\u9762\u76ee\uff0c\u7f8e\u903e\u5929 \u4ed9\uff0c\u5c18\u4e16\u65e0\u89c5\u5904\u3002 \u3000\u3000\u6155\u5bb9\u7ea2\u6495\u843d\u8986\u9762\u9ed1\u7eb1\uff0c\u7f8e\u8273\u7167\u4eba\uff0c\u5979\u5b8c\u5168\u5931\u53bb\u4e86\u5f80\u65e5\u7684\u51b7\u6f20\u4e0e\u9a84\u887f\uff0c\u5a9a\u89c6\u70df\u884c\uff0c\u98ce\u60c5\u4e07 \u79cd\uff0c\u542b\u7740\u70ed\u60c5\u5982\u706b\u7684\u6b32\u671b\uff0c\u8885\u8885\u5a77\u5a77\u5730\u8d70\u8fd1\u5c55\u767d\u3002 \u3000\u3000\u201c\u54e5\u2026\u2026\u6211\u7b49\u4e86\u4f60\u597d\u4e45\u2026\u2026\u5631\uff0c\u6211\u60f3\u4f60\u2026\u2026\u60f3\u5f97\u597d\u82e6\u2026\u2026\u5509\uff0c\u54e5\u2026\u2026\u6211\u7231\u4f60\u2026\u2026\u201d \u3000\u3000\u5979\u70ed\u60c5\u5982\u706b\uff0c\u6a80\u53e3\u5fae\u5f20\uff0c\u661f\u76ee\u5fae\u95ed\uff0c\u5634\u4e2d\u68a6\u5453\u822c\u5583\u5583\u4f4e\u8bed\uff0c\u4e00\u526f\u5a07\u8eaf\u5982\u626d\u80a1\u7cd6\u4f3c\u7684\u6251\u8fdb \u5c55\u767d\u6000\u4e2d\uff0c\u4e00\u624b\u5708\u4f4f\u5c55\u767d\u7684\u8116\u5b50\uff0c\u53e6\u4e00\u624b\u8f7b\u8f7b\u629a\u6478\u7740\u5c55\u767d\u7684\u8138\u2026\u2026 \u3000\u3000\u5c55\u81ea\u673a\u4f36\u4f36\u6253\u4e86\u4e00\u4e2a\u5bd2\u98a4\uff0c\u611f\u5230\u8138\u4e0a\u5fae\u75d2\uff0c\u7741\u773c\u4e00\u770b\uff0c\u4e00\u4e2a\u7f8e\u8273\u65e0\u6bd4\u7684\u7edd\u8272\u5c11\u5973\uff0c\u6b63\u6251 \u5728\u81ea\u5df1\u6000\u5185\uff0c\u6696\u7389\u6e29\u9999\u3002\u201c\u549a\uff01\u549a\uff01\u549a\uff01\u2026\u2026\u201d\u4ed6\u5fc3\u4e2d\u7acb\u5982\u4e07\u9a6c\u5954\u817e\u4e00\u822c\uff0c\u4e00\u80a1\u70ed\u6d41\u4ece\u4e39\u7530 \u76f4\u51b2\u6ce5\u4e38\uff0c\u5fc3\u65cc\u65d7\u64ad\uff0c\u4e94\u5185\u5982\u711a\u2026\u2026 \u3000\u3000\u8fd9\u4e00\u4e0b\u5b50\u51b2\u7834\u4e86\u5c55\u767d\u201c\u5fc3\u4e4b\u9632\u7ebf\u201d\uff0c\u201c\u97f3\u9b54\u7bab\u58f0\u201d\u7acb\u523b\u4e58\u865a\u800c\u5165\uff0c\u5c55\u767d\u518d\u4e5f\u628a\u6301\u4e0d\u4f4f\uff0c \u5f20\u5f00\u53cc\u81c2\uff0c\u7d27\u7d27\u62e5\u62b1\u4f4f\u9ed1\u8863\u5c11\u5973\u2026\u2026 \u3000\u3000\u8fd9\u70ed\u60c5\u5982\u706b\u7684\u604b\u7231\u753b\u9762\uff0c\u843d\u5728\u72c2\u50b2\u65e0\u60c5\u7684\u51cc\u98ce\u516c\u5b50\u773c\u5185\uff0c\u4f7f\u4ed6\u53c8\u7f9e\u53c8\u6012\uff0c\u66b4\u559d\u4e00\u58f0\uff1a \u201c\u591f\u4e86\uff01\u201d \u3000\u3000\u8fd9\u4e00\u58f0\u66b4\u543c\uff0c\u4e43\u51cc\u98ce\u516c\u5b50\u529f\u529b\u5386\u805a\uff0c\u604d\u5982\u5e73\u5730\u54cd\u8d77\u4e00\u58f0\u66b4\u96f7\uff0c\u94f6\u7bab\u593a\u9b42\u84e6\u7136\u4e00\u508d\uff0c\u7adf\u505c \u6b62\u4e86\u4ed6\u90a3\u611f\u91d1\u5316\u77f3\u7684\u7bab\u58f0\u3002 \u3000\u3000\u66b4\u559d\u58f0\u4e2d\uff0c\u51cc\u98ce\u516c\u5b50\u604d\u5982\u98d8\u98ce\u5c04\u7535\uff0c\u7eb5\u8eab\u8dc3\u81f3\u5c55\u767d\u4e0e\u6155\u5bb9\u7ea2\u8fd1\u524d\uff0c\u4e00\u624b\u62c9\u5f00\u504e\u5728\u5c55\u767d\u6000 \u4e2d\u7684\u59d0\u59d0\uff0c\u4e00\u638c\u5288\u51fa\uff0c\u5f53\u80f8\u5411\u5c55\u767d\u6253\u53bb\u3002 \u3000\u3000\u201c\u562d\u201d\u7684\u4e00\u58f0\uff0c\u5c55\u767d\u4e0d\u77e5\u8eb2\u95ea\uff0c\u88ab\u51cc\u98ce\u516c\u5b50\u4e00\u638c\u6253\u5728\u524d\u80f8\u4e4b\u5c55\u767d\u5750\u5728\u5730\u4e0a\u7684\u8eab\u5f62\u6643\u4e86\u51e0 \u6643\uff0c\u7ade\u672a\u8dcc\u5012\u3002 \u3000\u3000\u4f46\u5c55\u767d\u5fc3\u80f8\u4e4b\u95f4\uff0c\u88ab\u51cc\u98ce\u516c\u5b50\u5343\u94a9\u638c\u529b\u51fb\u4e2d\uff0c\u72b9\u5982\u53d7\u4e86\u5343\u65a4\u94c1\u9524\u4e00\u822c\uff0c\u5185\u8151\u771f\u6c14\u6d41\u7a9c\uff0c \u4e94\u810f\u7ffb\u6eda\uff0c\u5de8\u75db\u6512\u5fc3\uff0c\u5fcd\u4e0d\u4f4f\u4e00\u5f20\u5634\uff0c\u201c\u54c7\uff01\u201d\u55b7\u51fa\u4e00\u53e3\u9c9c\u8840\u3002 \u3000\u3000\u201c\u556a\uff01\u201d\u4e00\u58f0\u8106\u54cd\uff0c\u9ed1\u8863\u5c11\u5973\u63b4\u5728\u51cc\u98ce\u516c\u5b50\u9762\u988a\u4e4b\u4e0a\uff0c\u6253\u5f97\u51cc\u98ce\u516c\u5b50\u4e00\u8def\u8e09\u8dc4\uff0c\u76f4\u659c\u9000 \u51fa\u4e94\u516d\u6b65\u624d\u52c9\u5f3a\u7ad9\u4f4f\uff0c\u9669\u4e9b\u683d\u5012\u3002 \u3000\u3000\u51cc\u98ce\u516c\u5b50\u4fca\u7f8e\u65e0\u6bd4\u7684\u8138\u4e0a\uff0c\u7acb\u523b\u73b0\u51fa\u4e94\u4e2a\u7ea2\u7ea2\u7684\u6307\u5370\uff0c\u80bf\u8d77\u8001\u9ad8\uff0c\u9c9c\u8840\u4e5f\u987a\u7740\u5634\u89d2\u6d41\u4e0b \u6765\u3002 \u3000\u3000\u663e\u7136\u9ed1\u8863\u5973\u6155\u5bb9\u7ea2\u8fd9\u4e00\u638c\u6253\u5f97\u4e0d\u8f7b\uff0c\u51cc\u98ce\u516c\u5b50\u5bcc\u8d35\u4e16\u5bb6\uff0c\u9a84\u751f\u60ef\u517b\uff0c\u4e0d\u8981\u8bf4\u6328\u6253\uff0c\u957f\u8fd9 \u4e48\u5927\uff0c\u8fde\u6076\u58f0\u90fd\u672a\u542c\u8fc7\uff0c\u8fd9\u7a81\u5730\u4e09\u8bb0\u91cd\u51fb\uff0c\u5927\u51fa\u610f\u5916\uff0c\u7ade\u628a\u4ed6\u6253\u695e\u4e86\u2026\u2026 \u3000\u3000\u9ed1\u8863\u5c11\u5973\u6155\u5bb9\u7ea2\uff0c\u4e00\u638c\u6253\u9000\u51cc\u98ce\u516c\u5b50\uff0c\u5b8c\u5168\u4e0d\u5f53\u4e00\u56de\u4e8b\uff0c\u53ea\u53cc\u773c\u671b\u4f4f\u5c55\u767d\uff0c\u6ee1\u8138\u5173\u5207\u4e4b \u60c5\uff0c\u5a07\u58f0\u8bf4\u9053\uff1a\u201c\u54e5\u2026\u2026\u5662\u2026\u2026\u4f60\u88ab\u6253\u75db\u4e86\uff1f\u554a\uff01\u53ef\u601c\u7684\u54e5\u2026\u2026\u8ba9\u59b9\u770b\u770b\u4f24\u5f97\u91cd\u4e0d \u91cd\uff1f\u2026\u2026\u201d \u3000\u3000\u8bf4\u7740\uff0c\u8d70\u4e0a\u524d\u53bb\uff0c\u63d0\u8d77\u8863\u895f\u4e0b\u6446\uff0c\u6765\u4e3a\u5c55\u767d\u64e6\u62ed\u5634\u89d2\u7684\u8840\u8ff9\u3002 \u3000\u3000\u201c\u4f60\u8d70\u5f00\u5427\uff01\u59d1\u5a18\uff0c\u5728\u4e0b\u81ea\u5e7c\u5403\u82e6\u60ef\u4e86\uff0c\u8fd9\u70b9\u4f24\u8fd8\u4e0d\u7b97\u4ec0\u4e48\uff01\u201d \u3000\u3000\u8c01\u77e5\u5c55\u767d\u5bf9\u6155\u5bb9\u7ea2\u7684\u67d4\u60c5\u871c\u610f\uff0c\u7adf\u89c6\u82e5\u65e0\u7779\uff0c\u6446\u624b\u62e8\u5f00\u9ed1\u8863\u5c11\u5973\uff0c\u4ece\u5730\u4e0a\u7ad9\u8d77\u8eab\u6765\uff0c\u6447 \u6447\u6643\u6643\u5730\u8f6c\u8eab\u4fbf\u8d70\u2026\u3002\xb7\u2026\u54e5\uff01\u7b49\u6211\uff0c\u59b9\u8ddf\u4f60\u4e00\u5757\u8d70\uff01\u201c\u9ed1\u8863\u5c11\u5973\u4ece\u540e\u8fb9\u7d27\u7d27\u8ffd\u4e0a\uff0c\u89c1\u5c55\u767d \u53ea\u662f\u6447\u6447\u6643\u6643\u5730\u5f80\u524d\u8d70\uff0c\u5bf9\u5979\u7406\u4e5f\u4e0d\u7406\uff0c\u4e0d\u7531\u6025\u5f97\u6cea\u73e0\u591c\u773c\u7736\u91cc\u76f4\u6253\u8f6c\uff0c\u4e00\u8fb9\u7d27\u8ffd\uff0c\u4e00\u8fb9\u6073 \u6c42\u3002\u51cc\u98ce\u516c\u5b50\u8138\u4e0a\u867d\u7136\u6328\u4e86\u59d0\u59d0\u4e00\u638c\uff0c\u8fd9\u662f\u4ed6\u957f\u8fd9\u4e48\u5927\u4ece\u672a\u5c1d\u53d7\u8fc7\u7684\u6ecb\u5473\uff0c\u4f46\u8fd9\u8fd8\u4e0d\u7b97\u4f7f\u4ed6 \u60ca\u5947\u7684\uff0c\u6700\u4f7f\u4ed6\u5403\u60ca\u7684\u662f\uff0c\u4ed6\u90a3\u7ec8\u5e74\u96be\u5f97\u8bf4\u4e0a\u4e00\u53e5\u8bdd\u3001\u51e1\u4eba\u4e0d\u7406\u7684\u59d0\u59d0\uff0c\u5982\u4eca\uff0c\u7adf\u5927\u53cd\u5e38 \u6001\uff0c\u5bf9\u4e00\u4e2a\u964c\u751f\u5c11\u5e74\u6b7b\u8ffd\u4e0d\u820d\u3002\u662f\u4ee5\u51cc\u98ce\u516c\u5b50\u77aa\u5927\u4e86\u773c\u775b\uff0c\u5446\u5446\u5730\u77aa\u89c6\u7740\u8fd9\u5e55\uff0c\u7adf\u5fd8\u8bb0\u4e86\u8138 \u4e0a\u7684\u75bc\u75db\u2026\u3002\xb7\u201d\u7ea2\u59d1\u5a18\uff01\u201c\u201d\u6155\u5bb9\u5c0f\u59d0\uff01\u201c\u7707\u76ee\u9053\u4eba\u3001\u79c3\u9876\u8001\u8005\uff0c\u4e00\u9f50\u6a2a\u8eab\u5411\u524d\uff0c\u963b\u4f4f\u9ed1 \u8863\u5c11\u5973\u7684\u53bb\u8def\uff0c\u610f\u601d\u662f\u63d0\u9192\u5979\u7684\u6ce8\u610f\uff0c\u4e0d\u8981\u5fd8\u8bb0\u4e86\u8eab\u4efd\u2026\u2026\u8c01\u77e5\u9ed1\u8863\u5c11\u5973\u6155\u5bb9\u7ea2\uff0c\u773c\u775b\u4e00 \u77aa\uff0c\u8d50\u9053\uff1a\u201c\u4f60\u4eec\u5e72\u4ec0\u4e48\uff1f\u8eb2\u5f00\uff01\u201d \u3000\u3000\u7707\u76ee\u9053\u4eba\u3001\u79c3\u9876\u8001\u8005\u5747\u662f\u6b66\u6797\u524d\u8f88\u5f02\u4eba\uff0c\u5728\u6c5f\u6e56\u4e0a\u8f88\u4efd\u6781\u5c0a\uff0c\u867d\u5728\u8c79\u7a81\u5c71\u5e84\u6155\u5bb9\u5e9c\u4e0a\u4e3a \u95e8\u5ba2\uff0c\u4f46\u5c31\u662f\u5a01\u540d\u663e\u8d6b\u7684\u6155\u5bb9\u5e84\u4e3b\uff0c\u5bf9\u4ed6\u4e8c\u4eba\u4e5f\u4e0d\u6562\u7a0d\u5b58\u4e0d\u656c\u3002\u5982\u4eca\uff0c\u9ed1\u8863\u5c11\u5973\u6155\u5bb9\u7ea2\u7ade\u547c \u559d\u4e0b\u4eba\u4e00\u822c\uff0c\u5bf9\u4e8c\u4eba\u547c\u559d\u8d77\u6765\uff0c\u4e8c\u4eba\u5747\u4e0d\u514d\u4e00\u6014\u3002 \u3000\u3000\u201c\u7ea2\u59d1\u5a18\uff01\u201d\u79c3\u9876\u8001\u8005\u53cc\u7709\u4e00\u76b1\u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u5c31\u662f\u4f60\u81ea\u5df1\u4e0d\u987e\u8eab\u4efd\uff0c\u6211\u8fd9\u505a\u4f2f\u4f2f\u7684\uff0c\u4e5f \u4e0d\u80fd\u8ba9\u4f60\u80e1\u6765\uff01\u201d \u3000\u3000\u7707\u76ee\u9053\u4eba\u5728\u4e00\u65c1\u2018\u51fa\u63a5\u53e3\u9053\uff1a\u59d1\u5a18\uff01\u8981\u8003\u8651\u4ee4\u5c0a\u5728\u6b66\u6797\u4e2d\u7684\u5730\u4f4d\uff0c\u4e0d\u53ef\u4efb\u6027\u800c\u4e3a\u2026\u2026 \u201c\u201d\u522b\u7f57\u55e6\uff01\u201c\u9ed1\u8863\u5c11\u5973\u5c31\u662f\u4e0d\u542c\u8fd9\u4e00\u5957\uff0c\u89c1\u7707\u76ee\u9053\u4eba\u4e0e\u79c3\u9876\u8001\u8005\u963b\u5728\u81ea\u5df1\u9762\u524d\uff0c\u800c\u5c55\u767d\u5df2 \u6447\u6447\u6643\u6643\u5730\u8d70\u51fa\u597d\u8fdc\uff0c\u5ffd\u201d\u91ce\u9a6c\u5206\u9b03\u201c\uff0c\u53cc\u624b\u5de6\u53f3\u4e00\u5206\uff0c\u903c\u9000\u7707\u76ee\u9053\u4eba\u4e0e\u79c3\u9876\u8001\u8005\uff0c\u98de\u8eab\u5411 \u5c55\u767d\u8ffd\u53bb\u3002\u4e00\u8fb9\u53e3\u4e2d\u53eb\u9053\uff1a\u54e5\u2026\u2026\u7b49\u6211\u2026\u2026\u201d\u7707\u76ee\u9053\u4eba\u3001\u79c3\u9876\u8001\u8005\u4e07\u4e5f\u60f3\u4e0d\u5230\u9ed1\u8863\u5c11\u5973\u3001\u6155 \u5bb9\u5e84\u7684\u5927\u5c0f\u59d0\uff0c\u7adf\u4f1a\u5411\u4e8c\u4eba\u51fa\u624b\u653b\u51fb\uff0c\u800c\u4e14\u6155\u5bb9\u7ea2\u8fd9\u4e00\u62db\u201c\u91ce\u9a6c\u5206\u9b03\u201d\uff0c\u53c8\u65bd\u7528\u5f97\u51cc\u5389\u65e0 \u6bd4\uff0c\u5728\u4e8e\u65e0\u5907\u4e4b\u4e0b\uff0c\u8fd9\u6b66\u6797\u58f0\u671b\u6781\u9ad8\u7684\u4e24\u5927\u9ad8\u624b\uff0c\u7ade\u88ab\u903c\u9000\u4e86\u4e24\u6b65\uff0c\u4e8c\u4eba\u4e0d\u7531\u8001\u8138\u4e00\u7ea2\uff0c\u4e0d \u7ea6\u800c\u540c\u5730\uff0c\u53cc\u53cc\u817e\u8eab\uff0c\u53c8\u963b\u5728\u9ed1\u8863\u5c11\u5973\u9762\u524d\u3002\u9ed1\u8863\u5c11\u5973\u89c1\u4e8c\u4eba\u53c8\u6321\u5728\u81ea\u5df1\u9762\u524d\uff0c\u7ade\u5982\u5c0f\u5b69\u6492 \u5a07\u822c\u5730\u54ed\u53eb\u9053\uff1a\u201c\u8eb2\u5f00\uff01\u8eb2\u5f00\uff01\u6211\u4e0d\u8981\u4f60\u4eec\u7ba1\u2026\u2026\u201d \u3000\u3000\u4e00\u8fb9\u54ed\u53eb\uff0c\u4e00\u8fb9\u7adf\u7528\u624b\u628a\u8eab\u4e0a\u7684\u8863\u670d\uff0c\u4e00\u6761\u6761\u5730\u6495\u626f\u4e0b\u6765\u3002 \u3000\u3000\u9ed1\u8863\u5c11\u5973\u624b\u6cd5\u6781\u5feb\uff0c\u4e09\u628a\u4e94\u628a\uff0c\u5df2\u628a\u8eab\u4e0a\u4e00\u88ad\u9ed1\u7ebf\u7ef8\u8863\u626f\u788e\uff0c\u968f\u7740\u5979\u7ea4\u624b\u626c\u5904\uff0c\u6761\u6761\u788e \u7ef8\u5982\u4e00\u7fa4\u9ed1\u8272\u8774\u8776\u968f\u98ce\u98de\u6563\uff0c\u628a\u5979\u7eaf\u7f8e\u65e0\u6bd4\u767d\u7389\u7f8a\u8102\u822c\u7684\u80f4\u4f53\uff0c\u7acb\u523b\u5448\u73b0\u4e86\u51fa\u6765\u3002 \u3000\u3000\u79c3\u9876\u8001\u8005\u819b\u76ee\u5931\u8272\uff0c\u614c\u5fd9\u540e\u9000\u3002\u4ed6\u5e74\u903e\u77e5\u547d\uff0c\u6b66\u6797\u76d6\u4e16\uff0c\u5927\u6c5f\u5927\u6d6a\u7684\u573a\u9762\u89c1\u591a\u4e86\uff0c\u53ef \u662f\uff0c\u4ece\u6ca1\u6709\u4e00\u6b21\u4e8b\u4ef6\u7684\u6f14\u53d8\u4f1a\u50cf\u8fd9\u4e00\u6b21\u7a81\u7136\uff0c\u81f4\u4f7f\u4ed6\u65e0\u6cd5\u5e94\u4ed8\uff0c\u4e0d\u6562\u518d\u4e0a\u524d\u963b\u62e6\uff0c\u8fde\u5fd9\u540e\u9000 \u4e0d\u8fed\uff0c\u5c34\u5c2c\u4e07\u5206\u2026\u2026 \u7b2c\u5341\u4e8c\u7ae0\u3000\u77f3\u6d1e\u5f00\u7384\u5173>> \u53e4\u9f99\u300a\u5251\u5ba2\u884c\u300b \u7b2c\u5341\u4e8c\u7ae0\u3000\u77f3\u6d1e\u5f00\u7384\u5173 \u3000\u3000\u7707\u76ee\u9053\u4eba\u867d\u7136\u53cc\u76ee\u5168\u76f2\uff0c\u4f46\u542c\u89c9\u4e4b\u7075\u654f\uff0c\u4e0d\u4e9a\u4e8e\u6709\u53cc\u773c\u7684\u4eba\uff0c\u542c\u97f3\u8fa8\u4f4d\uff0c\u5df2\u77e5\u773c\u524d\u60c5 \u72b6\uff0c\u7eb5\u7136\u4e0d\u80fd\u770b\u89c1\uff0c\u4e5f\u5413\u5f97\u4ed6\u8fde\u9000\u6570\u6b65\uff0c\u767d\u679c\u773c\u8fde\u7ffb\uff0c\u5f20\u53e3\u7ed3\u820c\uff0c\u534a\u5929\u505a\u58f0\u4e0d\u5f97\u3002 \u3000\u3000\u201c\u4ec0\u4e48\u8bdd\uff1f\u7b80\u76f4\u2026\u2026\u7b80\u76f4\u2026\u2026\u201d \u3000\u3000\u51cc\u98ce\u516c\u5b50\u6c14\u5f97\u4fca\u9762\u6cdb\u767d\uff0c\u6413\u624b\u987f\u8db3\uff0c\u5583\u5583\u81ea\u8bed\uff0c\u534a\u5929\u4e0d\u77e5\u5982\u4f55\u624d\u597d\u3002\u53ef\u662f\uff0c\u9ed1\u8863\u5c11\u5973\u867d \u662f\u4ed6\u7684\u80de\u59d0\uff0c\u5982\u4eca\u8eab\u4e0a\u4e00\u4e1d\u4e0d\u6302\uff0c\u4ed6\u4e5f\u4e0d\u6562\u4e0a\u524d\u62e6\u963b\uff0c\u7a7a\u5728\u4e00\u8fb9\u6413\u624b\u987f\u8db3\uff0c\u5e72\u7740\u6025\u2026\u2026\u5c55\u767d \u4e5f\u95fb\u58f0\u56de\u8fc7\u5934\u6765\uff0c\u4e4d\u89c1\u9ed1\u8863\u5c11\u5973\u7eaf\u7f8e\u65e0\u6bd4\u3001\u767d\u7389\u51dd\u8102\u822c\u7684\u88f8\u4f53\u5448\u73b0\u5728\u773c\u524d\uff0c\u4e0d\u7981\u4e3a\u4e4b\u4e00\u5446\u3002 \u3000\u3000\u201c\u8c01\u4e5f\u4e0d\u80fd\u7ba1\u6211\uff01\u6211\u7231\u4ed6\uff01\u8c01\u4e5f\u4e0d\u80fd\u7ba1\u6211\uff01\u6211\u7231\u4ed6\uff01\u2026\u2026\u201d \u3000\u3000\u9ed1\u8863\u5c11\u5973\u4e00\u8fb9\u75af\u4e86\u822c\u7684\u6495\u788e\u81ea\u5df1\u5468\u8eab\u7684\u8863\u670d\uff0c\u76f4\u81f3\u5bf8\u7f15\u4e0d\u5269\uff0c\u610f\u72b9\u672a\u5c3d\uff0c\u53c8\u5f00\u59cb\u53cc\u624b\u62d4 \u4e0b\u5934\u4e0a\u7684\u94ae\u73af\uff0c\u9996\u9970\uff0c\u8033\u5760\uff0c\u9879\u7ec3\uff0c\u767d\u7389\u624b\u94a5\u2026\u2026\u4e00\u6837\u4e00\u6837\u5730\u968f\u53d6\u968f\u4e22\uff0c\u5019\u65f6\u95f4\u5979\u8eab\u4e0a\u4e00\u4ef6 \u4eba\u4e3a\u7684\u88c5\u9970\u7269\u4e5f\u4e0d\u5269\u4e86\uff0c\u6062\u590d\u4e86\u5979\u539f\u59cb\u7684\u7eaf\u771f\uff0c\u5979\u5934\u4e0a\u4e4c\u4e91\u4f3c\u7684\u9752\u4e1d\uff0c\u4e5f\u62ab\u6563\u5f00\u6765\uff0c\u79c0\u53d1\u5982 \u6ce2\u6d6a\u822c\u5730\u968f\u98ce\u98d8\u626c\uff0c\u5979\u5f20\u5f00\u53cc\u81c2\uff0c\u5982\u4e00\u53ea\u5c0f\u9e1f\u5f52\u5de2\u822c\u5411\u5c55\u767d\u98de\u6765\u3002 \u3000\u3000\u5c55\u767d\u5fc3\u4e4b\u5723\u6bbf\uff0c\u5947\u5f02\u5730\u5c55\u5f00\u4e86\u3002\u4ed6\u773c\u775b\u770b\u5230\u4e00\u5e55\u524d\u6240\u672a\u89c1\u7684\u666f\u50cf\uff1a\u9752\u5c71\u7eff\u91ce\uff0c\u767d\u4e91\u98d8\u6d6e \u5728\u5947\u5cf0\u7684\u5c71\u8170\uff0c\u7eff\u6c34\u7ed5\u8fc7\u7fe0\u7eff\u7684\u4e1b\u6797\uff0c\u4e00\u7247\u5f00\u9614\u7684\u8349\u539f\u4e0a\u6709\u65e0\u6570\u7684\u7e41\u82b1\u5728\u76db\u5f00\uff0c\u8fdc\u671b\u72b9\u5982\u4e00 \u5f20\u9526\u7ee3\u7684\u7ed2\u6be1\u5e73\u94fa\u5728\u5927\u5730\u3002\u5c31\u5728\u90a3\u98ce\u666f\u5982\u753b\u7684\u9526\u7ee3\u7ed2\u6be1\u4e0a\uff0c\u6b22\u8dc3\u5954\u8dd1\u6765\u4e86\u4e00\u4e2a\u7eaf\u771f\u81ea\u7136\u3001\u8d64 \u88f8\u7684\u5c11\u5973\uff0c\u5979\u662f\u5468\u8eab\u4e0d\u5e26\u4e00\u4e1d\u7f6a\u6076\uff0c\u7f8e\u4e3d\u65e0\u6bd4\uff0c\u5723\u6d01\u65e0\u6bd4\uff0c\u4e5f\u5584\u826f\u65e0\u6bd4\u7684\u4e00\u4f4d\u5929\u4f7f\uff01 \u3000\u3000\u5bf9\u7740\u6155\u5bb9\u7ea2\u7f8e\u8273\u65e0\u6bd4\u7684\u88f8\u4f53\uff0c\u5c55\u81ea\u5fc3\u4e2d\u6beb\u65e0\u4e00\u4e1d\u90aa\u5ff5\uff0c\u65e2\u65e0\u6b32\u671b\uff0c\u4e5f\u6ca1\u6709\u4e00\u70b9\u4e00\u6ef4\u7f9e\u803b \u4e4b\u611f\u3002\u53cd\u800c\u89c9\u5f97\u81ea\u7136\u3001\u5e94\u8be5\uff0c\u597d\u50cf\u89c9\u5f97\u8bb8\u4e45\u8bb8\u4e45\u4ee5\u524d\uff0c\u5c31\u5e94\u8be5\u662f\u8fd9\u4e2a\u6837\u5b50\uff0c\u5728\u7eaf\u6d01\u7f8e\u4e3d\u7684\u5927 \u81ea\u7136\u4e4b\u4e2d\uff0c\u5929\u751f\u7f8e\u4e3d\u7684\u8089\u4f53\uff0c\u5584\u826f\u65e0\u6bd4\u7684\u5fc3\u7075\uff0c\u5e94\u8be5\u662f\u8d64\u88f8\u7684\uff0c\u8bda\u5b9e\u7684\uff0c\u4e0d\u6742\u4e00\u4e1d\u90aa\u5ff5\u7684\uff0c \u4e0d\u52a0\u4e00\u70b9\u63a9\u9970\u7684\u3002 \u3000\u3000\u4e00\u79cd\u5947\u5f02\u7684\u51b2\u52a8\uff0c\u4ed6\u4e5f\u5f00\u59cb\u6495\u788e\u81ea\u5df1\u8eab\u4e0a\u7684\u8863\u670d\uff0c\u7247\u7247\u77ed\u5e03\u968f\u624b\u800c\u98de\uff0c\u53ef\u662f\u7b49\u4e0d\u53ca\u4ed6\u6495 \u6389\u6700\u540e\u7684\u5185\u88e4\uff0c\u6155\u5bb9\u7ea2\u5df2\u98de\u8dd1\u81f3\u4ed6\u7684\u8eab\u524d\uff0c\u8fb9\u8dd1\u8fb9\u671b\u7740\u4ed6\u6495\u788e\u5468\u8eab\u8863\u670d\uff0c\u4ed6\u5065\u58ee\u7684\u53e4\u94dc\u8272\u7684 \u808c\u80a4\u9732\u51fa\u6765\u4e86\uff0c\u5728\u9633\u5149\u4e0b\u95ea\u95ea\u53d1\u5149\uff0c\u6155\u5bb9\u7ea2\u773c\u4e2d\u4e5f\u6d41\u9732\u51fa\u5f02\u6837\u7684\u5149\u8f89\uff0c\u5634\u4e2d\u53d1\u7740\u9635\u9635\u7684\u6b22 \u5578\uff1a\u201c\u554a\uff01\u5537\uff01\u8377\uff01\u2026\u2026\u201d \u3000\u3000\u8fd9\u65f6\u5c55\u767d\u5468\u8eab\u8d64\u88f8\uff0c\u53ea\u5269\u4e0b\u4e00\u6761\u77ed\u77ed\u7684\u5185\u88e4\u3002\u6155\u5bb9\u7ea2\u5df2\u8dd1\u8fd1\u4ed6\u8eab\u524d\uff0c\u7ade\u5408\u8eab\u6251\u8fd1\u4ed6\u7684\u6000 \u5185\uff0c\u543b\u7740\u4ed6\u5bbd\u9614\u7684\u80f8\u819b\uff0c\u6293\u7d27\u4ed6\u7ed3\u5b9e\u7684\u81c2\u8180\uff0c\u62e5\u62b1\u4e00\u9635\uff0c\u4eb2\u543b\u4e00\u9635\uff0c\u53c8\u56f4\u7740\u4ed6\u6b22\u5578\u8df3\u8dc3\u4e00 \u9635\u2026\u2026\u5979\u662f\u75af\u72c2\u4e86\uff0c\u4e3a\u6b22\u4e50\u75af\u72c2\u4e86\uff0c\u4e3a\u89e3\u653e\u81ea\u5df1\u75af\u72c2\u4e86\uff0c\u5979\u4ece\u672a\u611f\u53d7\u8fc7\u8fd9\u6837\u7684\u5feb\u4e50\uff0c\u8fd9\u6837\u7684 \u81ea\u7531\uff1b\u8fd9\u6837\u7684\u65e0\u62d8\u65e0\u67ec\uff0c\u8fd9\u6837\u5e78\u798f\u3002 \u3000\u3000\u4f46\uff0c\u8fd9\u573a\u9762\u5374\u628a\u51cc\u98ce\u516c\u5b50\u3001\u94f6\u7bab\u593a\u9b42\u7ae0\u58eb\u670b\u3001\u7707\u76ee\u9053\u4eba\u3001\u79c3\u9876\u8001\u8005\u4f17\u4eba\u7ed9\u5413\u5446\u4e86\u3002\u773c\u524d \u7684\u60c5\u666f\uff0c\u7b80\u76f4\u4f7f\u4eba\u4e0d\u80fd\u60f3\u50cf\uff0c\u4e5f\u4e0d\u6562\u76f8\u4fe1\u3002 \u3000\u3000\u90a3\u516d\u540d\u52b2\u88c5\u58ee\u6c49\uff0c\u867d\u7136\u88ab\u5236\u4f4f\u7a74\u9053\uff0c\u624b\u811a\u4e0d\u80fd\u79fb\u52a8\uff0c\u4f46\u773c\u775b\u4e0d\u778e\uff0c\u89c1\u5230\u8fd9\u756a\u60c5\u666f\uff0c\u4e5f\u83ab \u4e0d\u5728\u5fc3\u4e2d\u6697\u6697\u79f0\u5947\u3002\u2026\u2026\u8fd9\u4e9b\u4eba\u4e4b\u4e2d\uff0c\u53ea\u6709\u7ae0\u58eb\u670b\u6700\u96be\u8fc7\u3002\u4ed6\u4e00\u4e07\u4e2a\u4e5f\u60f3\u4e0d\u5230\uff0c\u81ea\u5df1\u538b\u5012\u6b66 \u6797\u7684\u7edd\u6280\uff0c\u201c\u97f3\u9b54\u593a\u9b42\u7bab\u6cd5\u201d\uff0c\u7ade\u9020\u6210\u4e86\u8fd9\u6837\u7684\u540e\u679c\u3002 \u3000\u3000\u5c55\u767d\u5fc3\u4e2d\u4e5f\u6709\u7c7b\u4f3c\u6155\u5bb9\u7ea2\u7684\u6b22\u4e50\u3001\u81ea\u7531\u2026\u2026\u7b49\u7b49\u6b22\u4e50\u7684\u611f\u53d7\u3002\u4f46\u4ed6\u6495\u788e\u81ea\u5df1\u8eab\u4e0a\u8863\u670d\uff0c \u7247\u7247\u788e\u5e03\u968f\u624b\u4e22\u5f03\uff0c\u5230\u4e86\u6700\u540e\uff0c\u6709\u4e24\u6837\u4e1c\u897f\uff0c\u4ed6\u4e0d\u4ec5\u6ca1\u6709\u4e22\u5f03\uff0c\u53cd\u800c\u4ece\u5e7b\u5f71\u7684\u6b22\u4e50\u4e2d\uff0c\u628a\u4ed6 \u62c9\u56de\u5230\u73b0\u5b9e\u4e2d\u6765\u3002 \u3000\u3000\u4e00\u4ef6\u662f\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\uff0c\u90a3\u662f\u4ed6\u4e00\u5207\u5e0c\u671b\u6240\u5bc4\uff0c\u96f7\u5927\u53d4\u8a00\u72b9\u5728\u8033\uff1a\u4fee\u7ec3\u81f3\u9ad8\u65e0\u4e0a\u7684 \u6b66\u529f\uff0c\u5f00\u521b\u81ea\u5df1\u5149\u660e\u7684\u524d\u9014\uff0c\u4e3a\u62a5\u7236\u4eb2\u8840\u6d77\u6df1\u4ec7\u2014\u2014\u4e00\u5207\u7684\u4e00\u5207\u90fd\u5bc4\u6258\u5728\u8fd9\u672c\u4e66\u4e0a\uff0c\u4ed6\u4e0d\u80fd \u4e22\uff1b\u53e6\u4e00\u4ef6\u662f\uff0c\u4ed6\u7236\u4eb2\u4e34\u6b7b\u4e4b\u524d\uff0c\u4ea4\u7ed9\u4ed6\u7684\u9ebb\u5e03\u5c0f\u888b\u5b50\uff0c\u4ed6\u8981\u51ed\u7740\u8fd9\u552f\u4e00\u7684\u7ebf\u7d22\uff0c\u53bb\u5bfb\u8bbf\u6740 \u7236\u7684\u4ec7\u4eba\uff0c\u4ed6\u4e0d\u80af\u4e22\u3002 \u3000\u3000\u56e0\u6b64\uff0c\u4ed6\u4ece\u5e7b\u60f3\u4e4b\u5e78\u798f\u7684\u5929\u56fd\u4e2d\uff0c\u4e00\u4e0b\u5b50\u53c8\u8dc3\u8fdb\u51b7\u9177\u7684\u73b0\u5b9e\u91cc\u6765\u3002\u7eb5\u7136\u5468\u8eab\u8d64\u88f8\u3001\u5947\u7f8e \u65e0\u6bd4\u7684\u6155\u5bb9\u7ea2\uff0c\u4ee5\u4e07\u5343\u60c5\u4e1d\u6765\u7f20\u7ed5\u4ed6\uff0c\u4ee5\u7231\u604b\u5347\u9ad8\u8fd1\u4f3c\u5d07\u62dc\u7684\u75f4\u5973\u5fc3\u6765\u62e5\u543b\u4ed6\uff0c\u4ed6\u5df2\u5168\u4e0d\u653e \u5728\u5fc3\u4e0a\uff0c\u5fc3\u91cc\u53ea\u76d8\u7b97\u7740\uff0c\u600e\u6837\u6446\u8131\u773c\u524d\u8fd9\u4e9b\u4eba\u7684\u7ea0\u7f20\uff0c\u6309\u7167\u300a\u5929\u4f5b\u5377\u300b\u53bb\u4fee\u70bc\u6b66\u529f\uff1f\u600e\u6837\u67e5 \u8bbf\u6740\u7236\u4ec7\uff1f\u2026\u2026\u5c55\u767d\u624b\u6267\u4e8c\u7269\uff0c\u9677\u4e8e\u82e6\u601d\u4e4b\u4e2d\uff0c\u5bf9\u4e8e\u62e5\u543b\u81ea\u5df1\u3001\u5728\u8eab\u8fb9\u6b22\u5578\u8df3\u8dc3\u7684\u6155\u5bb9\u7ea2\uff0c \u5b8c\u5168\u5fd8\u8bb0\u4e86\u2026\u2026 \u3000\u3000\u201c\u54fc\uff01\u201d\u7a81\u7136\u4f20\u6765\u4e00\u58f0\u51b7\u54fc\uff0c\u7ee7\u4e4b\u662f\u51b0\u51b7\u51b7\u7684\u58f0\u8c03\uff1a\u201c\u6ca1\u6709\u7528\u7684\u4e1c\u897f\uff01\u8fd8\u4e0d\u628a\u5979\u5f04\u56de \u53bb\uff1f\u5728\u8fd9\u91cc\u7ed9\u6211\u4e22\u8138\uff01\u201d \u3000\u3000\u201c\u7ea2\u513f\uff01\u7ea2\u513f\uff01\u201d\u540c\u65f6\u4e00\u4e2a\u5973\u5b50\u58f0\u97f3\uff0c\u60ca\u53eb\u9053\uff1a\u201c\u4f60\u600e\u4e48\u5566\uff1f\u600e\u4e48\u5566\uff1f\u201d \u3000\u3000\u8ddf\u7740\u4e00\u9635\u811a\u6b65\u96f6\u4e71\u4e4b\u58f0\uff0c\u5c55\u767d\u60ca\u9192\u8fc7\u6765\uff0c\u62ac\u5934\u4e00\u770b\uff0c\u9762\u524d\u5df2\u591a\u4e86\u6570\u5341\u4eba\u4e4b\u591a\u3002 \u3000\u3000\u6570\u5341\u4eba\u62e5\u62a4\u7740\u4e00\u4e2a\u5e74\u7ea6\u4e94\u65ec\u5f00\u5916\u3001\u8eab\u9ad8\u4f53\u5065\u3001\u6ee1\u9762\u7ea2\u5149\u7684\u8d35\u4eba\u3002\u53ea\u89c1\u4ed6\u4e00\u8eab\u95ea\u95ea\u53d1\u5149\u7684 \u534e\u670d\uff0c\u8d64\u7ea2\u8138\uff0c\u864e\u76ee\u8695\u7709\uff0c\u65b9\u989d\u9614\u53e3\uff0c\u9ed1\u6f06\u6f06\u7684\u5934\u53d1\uff0c\u68b3\u5f97\u51c0\u5149\uff0c\u4e24\u7ffc\u5939\u6742\u7740\u6570\u6897\u5982\u971c\u7684\u94f6 \u53d1\uff0c\u53cc\u76ee\u5f00\u9614\u4e4b\u95f4\uff0c\u7cbe\u5149\u903c\u4eba\uff0c\u96cd\u5bb9\u534e\u8d35\uff0c\u4e0d\u6012\u800c\u5a01\uff0c\u51e0\u81f3\u4f7f\u4eba\u4e0d\u6562\u4ef0\u89c6\u3002 \u3000\u3000\u5728\u6797\u4e2d\u6551\u81ea\u5df1\uff0c\u53c8\u5728\u51cc\u98ce\u516c\u5b50\u623f\u4e2d\u767e\u822c\u7ef4\u62a4\u81ea\u5df1\u7684\u4e2d\u5e74\u8d35\u5987\uff0c\u4e0e\u90a3\u8d35\u4eba\u5e76\u80a9\u800c\u7acb\u3002 \u3000\u3000\u5176\u4f59\u4e09\u4e94\u5341\u4eba\uff0c\u80a5\u80d6\u9ad8\u77ee\u3001\u4e11\u964b\u4fca\u5f66\u4e0d\u7b49\uff0c\u4f46\u4e00\u4e2a\u4e2a\u795e\u6c14\u5145\u8db3\uff0c\u8eab\u8eaf\u527f\u608d\uff0c\u4fbf\u77e5\u5c3d\u662f\u4e9b \u4e09\u5c71\u4e94\u5cb3\uff0c\u5947\u4eba\u5f02\u58eb\u3002\u8fd9\u4e9b\u4eba\u4fbf\u50cf\u4f17\u660c\u62f1\u6708\u4e00\u822c\uff0c\u62e5\u7c07\u7740\u96cd\u5bb9\u534e\u8d35\u7684\u4e2d\u5e74\u592b\u5987\u3002 \u3000\u3000\u5c55\u767d\u4e00\u770b\u8fd9\u6c14\u6d3e\uff0c\u4fbf\u731c\u5230\u8fd9\u534e\u670d\u8d35\u4eba\u5fc5\u662f\u8fd9\u4e2d\u5e74\u8d35\u5987\u7684\u4e08\u592b\uff0c\u4e00\u5b9a\u4e5f\u5c31\u662f\u4f17\u4eba\u53e3\u4e2d\u7684 \u201c\u8001\u7237\u5b50\u201d\u4e86\u3002\u534e\u670d\u8d35\u4eba\u4e00\u8138\u6012\u5bb9\uff0c\u4e2d\u5e74\u8d35\u5987\u6ee1\u8138\u7591\u8272\u3002 \u3000\u3000\u96cd\u5bb9\u534e\u8d35\u7684\u8d35\u4eba\uff0c\u672c\u5c31\u6c14\u52bf\u6151\u4eba\uff0c\u4e0d\u6012\u800c\u5a01\uff0c\u5982\u4eca\uff0c\u8138\u4e0a\u5e26\u7740\u6012\u5bb9\uff0c\u66f4\u4f7f\u4eba\u6709\u63e3\u63e3\u4e0d \u5b89\u3001\u547c\u5438\u7a92\u606f\u4e4b\u611f\uff01 \u3000\u3000\u5c31\u5728\u5c55\u767d\u60ca\u89c6\u4e4b\u4e2d\uff0c\u65e9\u6709\u56db\u540d\u9752\u8863\u5a62\u5973\uff0c\u5404\u81ea\u8131\u4e0b\u4e00\u4ef6\u5916\u8863\uff0c\u7528\u53cc\u624b\u4e3e\u7740\uff0c\u6765\u906e\u6321\u6155\u5bb9 \u7ea2\u7684\u88f8\u4f53\uff0c\u5e76\u62e5\u62a4\u7740\u6155\u5bb9\u7ea2\u8d70\u56de\u53bb\u3002 \u3000\u3000\u201c\u6211\u4e0d\u8981\uff01\u6211\u4e0d\u8981\uff01\u2026\u2026\u201d\u6155\u5bb9\u7ea2\u4e00\u8fb9\u5a07\u557c\u5636\u5524\u7740\uff0c\u4e00\u8fb9\u88ab\u56db\u540d\u5a62\u5973\u62e5\u7c07\u7740\u8d70\u4e86\u56de\u53bb\u3002 \u663e\u7136\uff0c\u5979\u4e0d\u6562\u5fe4\u9006\u7236\u4eb2\u7684\u610f\u601d\uff0c\u867d\u7136\u5979\u795e\u667a\u5c1a\u672a\u5b8c\u5168\u6e05\u9192\uff0c\u5fc3\u4e2d\u662f\u6781\u5ea6\u4e0d\u613f\u3002 \u3000\u3000\u5c55\u767d\u731b\u7136\u7075\u673a\u4e00\u52a8\uff0c\u5c31\u5728\u4f17\u5973\u5a62\u4e3e\u8863\u906e\u4f4f\u6155\u5bb9\u7ea2\u4e4b\u9645\uff0c\u628a\u624b\u4e2d\u7684\u300a\u5929\u4f5b\u5377\u300b\u4e0e\u9ebb\u5e03\u5c0f\u888b \u5b50\uff0c\u4e00\u9f50\u585e\u8fdb\u88e4\u4e2d\u3002\u597d\u5728\u4ed6\u7684\u77ed\u88e4\u88e4\u53e3\u5f88\u7d27\uff0c\u4e0d\u6015\u6389\u51fa\u6765\u3002 \u3000\u3000\u201c\u4e22\u7ed9\u4ed6\u4e00\u4ef6\u8863\u670d\uff0c\u53eb\u4ed6\u8ddf\u6211\u6765\uff01\u201d \u3000\u3000\u534e\u670d\u8d35\u4eba\u8bf4\u8bdd\u4f3c\u6709\u83ab\u5927\u6743\u5a01\uff0c\u5b8c\u5168\u662f\u547d\u4ee4\u5f0f\uff0c\u8bed\u6c14\u4e4b\u4e2d\uff0c\u597d\u50cf\u4ed6\u7684\u8bdd\u4e00\u4e1d\u4e0d\u5bb9\u53cd\u6297\u3002 \u3000\u3000\u5f53\u5373\u6709\u4e00\u540d\u52b2\u88c5\u4f69\u5200\u58ee\u6c49\uff0c\u8131\u4e0b\u8eab\u4e0a\u9ed1\u7f0e\u62ab\u98ce\uff0c\u6296\u624b\u4e22\u5411\u5c55\u767d\u3002 \u3000\u3000\u4e5f\u8bb8\u8fd9\u52b2\u88c5\u58ee\u6c49\uff0c\u6210\u5fc3\u663e\u9732\u4e00\u624b\uff0c\u6216\u662f\u60f3\u628a\u5c55\u767d\u5236\u4e8e\u6b7b\u5730\uff0c\u867d\u662f\u4e00\u4ef6\u67d4\u8f6f\u7684\u8863\u670d\u7ade\u8d2f\u8db3 \u4e86\u5185\u5bb6\u771f\u529b\uff0c\u628a\u4e00\u4ef6\u9ed1\u7f0e\u88ab\u98ce\u6296\u5f97\u7b14\u76f4\uff0c\u72b9\u5982\u4e00\u6839\u94c1\u68d2\u4e00\u822c\uff0c\u5288\u7a7a\u7834\u98ce\uff0c\u76f4\u5411\u5c55\u767d\u80f8\u524d\u63bc \u6765\uff01 \u3000\u3000\u5c55\u767d\u5fae\u5fae\u4e00\u7b11\uff0c\u4f38\u51fa\u4e24\u6307\u4e00\u634f\u4e00\u6296\uff0c\u4e0d\u4f46\u7834\u4e86\u6697\u84c4\u5185\u529b\uff0c\u5e76\u7acb\u523b\u628a\u9ed1\u7f0e\u62ab\u98ce\u88ab\u5728\u8eab\u4e0a\u3002 \u3000\u3000\u201c\u70b9\u529b\u7834\u5343\u94a7\u201d\uff0c\u6309\u8bf4\u5c55\u767d\u4ee5\u4e24\u6307\u63a5\u8fd9\u52b2\u88c5\u5927\u6c49\u7684\u5168\u529b\u4e00\u51fb\uff0c\u9732\u7684\u8fd9\u4e00\u624b\uff0c\u5e76\u4e0d\u7b80\u5355\uff0c \u4f46\u534e\u670d\u8d35\u4eba\u8fde\u770b\u4e5f\u4e0d\u770b\uff0c\u7adf\u8f6c\u8eab\u5411\u56de\u8d70 \u3000\u3000\u597d\u50cf\u77e5\u9053\u5c55\u767d\u4e0d\u6562\u53cd\u6297\uff0c\u5fc5\u4f1a \u3000\u3000\u5c55\u767d\u5fc3\u4e2d\u8d77\u4e86\u4e00\u9635\u5f3a\u70c8\u7684\u53cd\u5e94\u3002\u4f46\u4e00\u770b\u62e5\u7c07\u5728\u534e\u670d\u8d35\u4eba\u56db\u5468\u7684\u6b66\u6797\u8c6a\u5ba2\uff0c\u4e00\u4e2a\u4e2a\u5bf9\u4ed6\u6012 \u76ee\u800c\u89c6\uff0c\u5c55\u767d\u81ea\u77e5\u4e0d\u662f\u8fd9\u4e9b\u4eba\u7684\u5bf9\u624b\uff0c\u53c8\u89c1\u4e2d\u5e74\u8d35\u5987\uff0c\u4ee5\u65e0\u6bd4\u6148\u7231\u7684\u773c\u5149\u671b\u4e86\u81ea\u5df1\u4e00\u773c\uff0c\u7ade \u628a\u51b2\u5230\u5634\u8fb9\u7684\u5014\u5f3a\u8a00\u8bed\uff0c\u786c\u7ed9\u54bd\u4e86\u56de\u53bb\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u6697\u60f3\uff1a\u201c\u770b\u4f60\u4eec\u8981\u628a\u6211\u600e\u6837\uff1f\u4e14\u8ddf\u4f60\u4eec\u53bb\u4e86\u518d\u8bf4\uff01I\u201d \u3000\u3000\u4e0d\u8fc7\uff0c\u5728\u4ed6\u4e3e\u6b65\u8ddf\u968f\u4eba\u5bb6\u53bb\u65f6\uff0c\u5185\u5fc3\u7684\u81ea\u5c0a\uff0c\u597d\u50cf\u53d7\u4e86\u65e0\u9650\u5c48\u8fb1\uff0c\u6697\u81ea\u8d23\u9053\uff1a\u201c\u5c55\u767d\uff0c \u5c55\u767d\uff01\u96be\u9053\u4f60\u5c31\u662f\u8fd9\u822c\u602f\u61e6\u5417\uff1f\u5982\u4e00\u4e2a\u7f94\u7f8a\u4e00\u6837\uff0c\u4f0f\u7740\u4efb\u4eba\u5bb0\u5272\uff01\u201d \u3000\u3000\u53ef\u662f\uff0c\u654c\u52bf\u592a\u5f3a\u4e86\uff0c\u4ed6\u81ea\u77e5\u6ca1\u6709\u53cd\u6297\u7684\u529b\u91cf\u3002\u4ed6\u5185\u5fc3\u6027\u683c\u867d\u7136\u5014\u5f3a\uff0c\u4f46\u4ed6\u4e5f\u660e\u767d\uff0c\u6b64\u65f6 \u4e0d\u80fd\u66b4\u864e\u51af\u6cb3\uff0c\u76f2\u76ee\u727a\u7272\uff0c\u4ed6\u8fd8\u8981\u7559\u7740\u6709\u7528\u4e4b\u8eaf\uff0c\u5b8c\u6210\u81ea\u5df1\u80a9\u8d1f\u7684\u91cd\u4efb\u3002 \u3000\u3000\u56e0\u6b64\uff0c\u4ed6\u5f3a\u5fcd\u4e0b\u4e86\u8fd9\u4e00\u53e3\u6c14\uff0c\u6253\u7b97\u5148\u8d70\u4e00\u6b65\u7b97\u4e00\u6b65\uff0c\u4e0d\u5230\u4e07\u4e0d\u5f97\u5df2\u65f6\uff0c\u51b3\u4e0d\u8f7b\u8a00\u727a\u7272\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u8ddf\u7740\u8fd9\u4e9b\u4eba\u8d70\u53bb\u3002\u4f46\u5fc3\u4e2d\u6127\u759a\u81ea\u8d23\uff0c\u4e0d\u7531\u5f97\u4ed6\u4f4e\u4e0b\u5934\u53bb\u3002\u8c01\u77e5\u4ed6\u4e00\u4f4e\u5934\uff0c\u9ed1\u7f0e\u62ab \u98ce\u7684\u5de6\u524d\u895f\u4e0a\u523a\u7ee3\u7740\u4e00\u53ea\u91d1\u94b1\u8c79\uff0c\u6829\u6829\u5982\u751f\uff0c\u5206\u5916\u523a\u773c\u3002 \u3000\u3000\u4ed6\u5bf9\u4e2d\u5e74\u8d35\u5987\uff0c\u9ed1\u8863\u5c11\u5973\uff0c\u51cc\u98ce\u516c\u5b50\u2026\u2026\u4ee5\u53ca\u773c\u524d\u7b49\u4eba\uff0c\u6beb\u4e0d\u77e5\u5176\u5e95\u8574\uff0c\u5fc3\u4e2d\u4e00\u76f4\u5145\u6ee1 \u4e86\u597d\u5947\uff0c\u4f46\u4e8b\u5b9e\u7684\u6025\u907d\u53d8\u5316\uff0c\u7ade\u4f7f\u4ed6\u65e0\u6687\u987e\u53ca\u3002\u5982\u4eca\uff0c\u770b\u5230\u8fd9\u9ed1\u7f0e\u62ab\u98ce\u4e0a\u523a\u7ee3\u5f97\u975e\u5e38\u7cbe\u81f4\u7684 \u91d1\u94b1\u8c79\uff0c\u5f88\u5feb\u5730\u4f7f\u4ed6\u60f3\u5230\uff1a\u6c5f\u6e56\u4e0a\u540d\u6ee1\u5929\u4e0b\u7684\u4e00\u5927\u8c6a\u95e8\u3002 \u3000\u3000\u201c\u8c79\u7a81\u5c71\u5e84\uff01\u201d\u5c55\u767d\u5bfb\u601d\u9053\uff1a\u5982\u6211\u731c\u6d4b\u4e0d\u9519\u7684\u8bdd\uff0c\u8fd9\u534e\u670d\u8d35\u4eba\uff0c\u5b9a\u662f\u8c79\u7a81\u5c71\u5e84\u5e84\u4e3b\uff0c\u6458 \u661f\u624b\u6155\u5bb9\u6db5\u65e0\u7591\u4e86\uff01\u201d \u3000\u3000\u5c55\u767d\u60f3\u81f3\u6b64\u5904\uff0c\u4e3e\u76ee\u5411\u524d\u671b\u53bb\uff0c\u4ed6\u60f3\u4ed4\u7ec6\u6253\u91cf\u4e00\u4e0b\uff0c\u8fd9\u540d\u6ee1\u5929\u4e0b\u3001\u88ab\u6b66\u6797\u9ed1\u767d\u4e24\u9053\u6f14\u67d3 \u5f97\u795e\u4e4e\u5176\u795e\u7684\u201c\u6458\u661f\u624b\u201d\u6155\u5bb9\u6db5\uff0c\u5230\u5e95\u6709\u4f55\u51fa\u5947\u8fc7\u4eba\u4e4b\u5904\u3002 \u3000\u3000\u534e\u670d\u8d35\u4eba\u5728\u524d\u8d70\u7740\uff0c\u4ed6\u8eab\u540e\u6742\u67e5\u5730\u8ddf\u968f\u7740\u4e09\u4e94\u5341\u540d\u6b66\u6797\u9ad8\u624b\uff0c\u51cc\u98ce\u516c\u5b50\u3001\u7707\u76ee\u9053\u4eba\u3001\u79c3 \u9876\u8001\u8005\u3001\u94f6\u7bab\u593a\u9b42\uff0c\u4ee5\u53ca\u81ea\u76f8\u6b8b\u6740\u7684\u516d\u540d\u52b2\u88c5\u5927\u6c49\uff0c\u6b64\u65f6\u4ea6\u4e3a\u4eba\u4ee3\u89e3\u7a74\u9053\uff0c\u5305\u624e\u4e86\u4f24\u53e3\uff0c\u90fd \u8ddf\u5728\u534e\u670d\u8d35\u4eba\u7684\u8eab\u540e\u3002 \u3000\u3000\u81f3\u4e8e\u88ab\u5c55\u767d\u6253\u6b7b\u7684\u9648\u6e05\u3001\u9648\u5e73\uff0c\u4e5f\u6709\u4eba\u53bb\u4e3a\u5176\u63a9\u57cb\u5c38\u9996\uff0c\u534e\u670d\u8d35\u4eba\u8fde\u8fc7\u95ee\u90fd\u672a\u8fc7\u95ee\uff0c\u597d \u50cf\u8fd9\u6b7b\u4f24\u4eba\u547d\uff0c\u5728\u4ed6\u4eec\u773c\u4e2d\u770b\u6765\uff0c\u7adf\u5982\u5bb6\u5e38\u4f7f\u996d\u4e00\u822c\u5bfb\u5e38\u3002 \u3000\u3000\u5c55\u767d\u5728\u4eba\u5f71\u4ea4\u9519\u4e4b\u4e2d\uff0c\u53ea\u80fd\u63a9\u63a9\u6620\u6620\u770b\u5230\u534e\u670d\u8d35\u4eba\u7684\u80cc\u5f71\uff0c\u4ed6\u90a3\u4e00\u8eab\u975e\u4e1d\u975e\u7ef8\u7684\u534e\u670d\uff0c \u5728\u9633\u5149\u4e0b\u95ea\u95ea\u53d1\u5149\u3002\u5c55\u767d\u731b\u7136\u5fc3\u4e2d\u4e00\u52a8\uff0c\u89c9\u5f97\u534e\u670d\u8d35\u4eba\u6240\u7a7f\u7684\u8863\u670d\u8d28\u6599\uff0c\u7ec6\u770b\u6709\u70b9\u7279\u522b\uff0c\u8fd0 \u8db3\u76ee\u529b\u671b\u53bb\uff0c\u8fa8\u522b\u8d35\u4eba\u534e\u670d\u65e2\u4e0d\u662f\u7eeb\u7f57\uff0c\u53c8\u4e0d\u662f\u7ef8\u7f0e\uff0c\u4f46\u95ea\u95ea\u53d1\u5149\uff0c\u67d4\u97e7\u534e\u8d35\u65e0\u6bd4\u3002\u8ddf\u5728\u8eab \u540e\u7684\u4e09\u4e94\u5341\u4eba\u4e4b\u4e2d\uff0c\u4e5f\u6709\u4e0d\u5c11\u8eab\u5fa1\u534e\u670d\u7684\uff0c\u5373\u8fde\u5a62\u5973\u5bb6\u4e01\u88c5\u675f\u7684\u4f63\u4eba\uff0c\u8eab\u4e0a\u5386\u7a7f\u7684\u4e5f\u5747\u662f\u8d21 \u9752\u5339\u7f0e\uff0c\u8fd9\u79cd\u8863\u6599\u5728\u5e02\u9762\u4e0a\u4e5f\u7b97\u662f\u8d35\u91cd\u8863\u6599\u4e86\uff0c\u975e\u5bcc\u8d35\u4e4b\u5bb6\u662f\u7a7f\u4e0d\u8d77\u7684\u3002\u4f46\u4e0e\u90a3\u534e\u670d\u8d35\u4eba\u8eab \u4eba\u7a7f\u7684\u534e\u670d\u4e00\u6bd4\uff0c\u90fd\u4e0d\u514d\u9eef\u7136\u65e0\u5149\u3002 \u3000\u3000\u5c55\u767d\u731b\u7136\u60f3\u8d77\uff0c\u8fd9\u534e\u670d\u8d35\u4eba\u8eab\u4e0a\u8863\u670d\u7684\u8d28\u6599\uff0c\u4e0d\u6b63\u662f\u8ddf\u81ea\u5df1\u9ebb\u5e03\u5c0f\u888b\u5b50\u91cc\u88c5\u7684\u90a3\u65b9\u975e\u4e1d \u975e\u7ef8\u7684\u5e03\u6599\uff0c\u5b8c\u5168\u76f8\u540c\u5417\uff1f \u3000\u3000\u8fd9\u4e00\u610f\u5916\u53d1\u73b0\uff0c\u4f7f\u5c55\u767d\u5fc3\u795e\u60ca\u7136\u4e00\u9707\uff0c\u5982\u88ab\u96f7\u51fb\uff0c\u51e0\u4e4e\u4f7f\u4ed6\u628a\u6301\u4e0d\u4f4f\uff0c\u70ed\u8840\u4e0a\u51b2\uff0c\u5f88\u60f3 \u731b\u7136\u6251\u4e0a\u524d\u53bb\u4ee5\u6b7b\u76f8\u62fc\u3002 \u3000\u3000\u4f46\u4ed6\u6781\u529b\u5236\u6b62\u7740\u81ea\u5df1\uff0c\u6697\u4e2d\u54ac\u7259\u5207\u9f7f\uff1a\u2018\u83ab\u975e\u6b64\u8001\u8d3c\uff0c\u5c31\u662f\u6211\u7684\u6740\u7236\u4ec7\u4eba\uff1f\u201d \u3000\u3000\u5c55\u767d\u5fc3\u4e2d\u601d\u6f6e\u5982\u6d8c\uff0c\u6697\u6697\u5bfb\u601d\uff1a\u201c\u7236\u4eb2\u4e34\u6b7b\u524d\u4eb2\u624b\u4ea4\u7ed9\u6211\u8fd9\u5c0f\u888b\u5b50\uff0c\u888b\u5185\u88c5\u7684\u4e00\u56e2\u4e71 \u53d1\uff0c\u4e00\u6bb5\u4e1d\u6761\uff0c\u4e00\u7c92\u94a2\u73e0\uff0c\u4e00\u7c92\u9752\u94dc\u94ae\u6263\uff0c\u4e00\u679a\u9752\u94dc\u5236\u94b1\uff0c\u548c\u90a3\u65b9\u663e\u7136\u662f\u81ea\u8863\u895f\u626f\u843d\u7684\u4e1d \u7ef8\uff0c\u81ea\u5df1\u59cb\u7ec8\u4e0d\u77e5\u4f55\u610f\uff0c\u5982\u4eca\uff0c\u8fd9\u65b9\u4e1d\u7ef8\u4e0e\u8fd9\u534e\u670d\u8d35\u4eba\u6240\u7a7f\u8863\u670d\u8d28\u6599\u76f8\u540c\uff0c\u5c31\u7b97\u8fd9\u534e\u670d\u8d35\u4eba \u4e0d\u662f\u6740\u6b7b\u81ea\u5df1\u7236\u4eb2\u7684\u4ec7\u4eba\uff0c\u6700\u4f4e\u9650\u5ea6\u8fd9\u4e5f\u662f\u4e00\u4e2a\u6709\u529b\u7684\u7ebf\u7d22\u2026\u2026\u201d \u3000\u3000\u5c55\u767d\u5c31\u8fd9\u6837\u795e\u601d\u6f6e\u6d8c\uff0c\u8ddf\u7740\u6155\u5bb9\u5e84\u4e3b\u4e00\u884c\u4eba\uff0c\u4e5f\u4e0d\u77e5\u8d70\u4e86\u591a\u8fdc\uff0c\u7ecf\u8fc7\u591a\u5c11\u91cd\u95e8\u91cd\u6237\uff0c\u6700 \u540e\u201c\u6458\u661f\u624b\u201d\u4e5f\u672b\u5ba1\u95ee\u4ed6\uff0c\u8ff7\u8ff7\u7cca\u7cca\u7684\u628a\u5c55\u767d\u5173\u8fdb\u4e00\u5ea7\u77f3\u7262\u4e4b\u5185\u3002 \u3000\u3000\u5c55\u767d\u4e0d\u77e5\u201c\u6458\u661f\u624b\u201d\u7a76\u7adf\u600e\u6837\u5bf9\u4ed8\u81ea\u5df1\uff0c\u521d\u88ab\u5173\u8fdb\u77f3\u7262\u5185\uff0c\u5fc3\u9887\u4e0d\u5b89\uff0c\u4f46\u540e\u6765\u60f3\u901a\u4e86\uff0c \u4e00\u5207\u62b1\u5b9a\u9006\u6765\u987a\u53d7\u5fc3\u7406\uff0c\u51b3\u5b9a\u6682\u65f6\u5fcd\u8010\uff0c\u5e76\u501f\u7740\u4e0e\u5916\u754c\u9694\u7edd\u7684\u673a\u4f1a\uff0c\u5728\u77f3\u7262\u5185\u82e6\u7ec3\u300a\u9501\u9aa8\u9500 \u9b42\u5929\u4f5b\u5377\u300b\u6240\u8f7d\u7684\u9ad8\u6df1\u6b66\u529f\u3002 \u3000\u3000\u521d\u65f6\uff0c\u5c55\u767d\u5fc3\u6025\u7ec3\u4e60\u6b66\u529f\uff0c\u5728\u65e0\u4eba\u76d1\u89c6\u65f6\uff0c\u767d\u5929\u4e5f\u6253\u5f00\u300a\u5929\u4f5b\u5377\u300b\u7814\u4e60\uff0c\u4f46\u4e66\u9875\u4e0a\u7684\u9b54 \u5973\u753b\u50cf\uff0c\u7c89\u817f\u9165\u80f8\uff0c\u7ade\u4f7f\u4ed6\u4e0d\u80fd\u9759\u4e0b\u5fc3\u6765\u7814\u4e60\u5176\u4e2d\u9ad8\u5965\u6b66\u529f\uff0c\u53cd\u800c\u65f6\u65f6\u60f3\u5165\u975e\u975e\uff0c\u5c24\u5176\u4ed6\u9886 \u7565\u4e86\u6155\u5bb9\u7ea2\u7eaf\u7f8e\u65e0\u6bd4\u7684\u8d64\u88f8\u8111\u4f53\uff0c\u753b\u4e2d\u4f20\u771f\uff0c\u4e00\u76f4\u5f15\u5bfc\u4ed6\u8054\u60f3\u5230\u6155\u5bb9\u7ea2\uff0c\u808c\u80a4\u82b3\u6cfd\uff0c\u66f4\u4f7f\u4ed6 \u5fc3\u70e6\u610f\u4e71\uff0c\u610f\u9a6c\u5fc3\u733f\uff01\u2026\u2026 \u3000\u3000\u540e\u6765\uff0c\u4ed6\u731b\u7136\u60ca\u609f\uff0c\u89c9\u5f97\u8fd9\u6837\u7ee7\u7eed\u4e0b\u53bb\uff0c\u81ea\u5df1\u975e\u8d70\u706b\u5165\u9b54\u4e0d\u53ef\u3002\u4e8e\u662f\uff0c\u4ed6\u53c8\u6309\u7167\u5728\u9ed1\u6d1e \u4e2d\u7684\u65b9\u6cd5\uff0c\u95ed\u76ee\u6478\u7d22\u8d77\u6765\uff0c\u597d\u5728\u4ed6\u5df2\u7ecf\u6478\u7d22\u8fc7\u5f88\u4e45\uff0c\u4ee5\u6307\u4ee3\u76ee\uff0c\u5e76\u4e0d\u592a\u56f0\u96be\uff0c\u4e00\u8fb9\u6478\u7d22\uff0c\u4e00 \u8fb9\u7814\u4e60\uff0c\u5168\u4f53\u5fc3\u795e\uff0c\u5b8c\u5168\u6c89\u6d78\u5728\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\u5185\u4e2d\u7684\u6b66\u529f\u79d8\u8bc0\u4e0a\uff0c\u7adf\u5fd8\u8bb0\u4e86\u65e5\u591c\u6668 \u660f\uff0c\u4ea6\u5fd8\u8bb0\u4e86\u77f3\u7262\u4e2d\u7684\u5b64\u82e6\u5bc2\u5bde\u2026\u2026 \u3000\u3000\u4e0d\u8c08\u5c55\u767d\u5728\u77f3\u7262\u4e2d\u82e6\u7ec3\u300a\u5929\u4f5b\u5377\u300b\u79d8\u6280\u5947\u529f\uff0c\u518d\u8bf4\u9886\u8896\u5317\u4e94\u7701\u6b66\u6797\u7684\u201c\u6458\u661f\u624b\u201d\u6155\u5bb9 \u6db5\uff0c\u628a\u5c55\u767d\u5173\u8fdb\u77f3\u7262\u5185\uff0c\u5bf9\u8fd9\u540d\u4e0d\u89c1\u7ecf\u4f20\u7684\u5c11\u5e74\uff0c\u5e76\u6ca1\u6709\u6302\u5728\u5fc3\u4e0a\u3002\u4ed6\u53ea\u662f\u6c14\u607c\u4ee5\u81ea\u5df1\u7ba1\u6559 \u4e4b\u4e25\uff0c\u95e8\u98ce\u9632\u8303\u4e4b\u8c28\uff0c\u7d20\u4ee5\u201c\u6e05\u767d\u201d\u4e8c\u5b87\uff0c\u5938\u8000\u6b66\u6797\uff0c\u4ee5\u6807\u699c\u4e0d\u540c\u4e8e\u4e00\u822c\u7537\u5973\u6df7\u6742\uff0c\u79fd\u95fb\u56db \u64ad\u4e4b\u6c5f\u6e56\u9053\u3002\u5982\u4eca\uff0c\u81ea\u5df1\u4eb2\u751f\u7684\u5973\u513f\uff0c\u7ade\u5728\u4f17\u76ee\u777d\u777d\u4e4b\u4e0b\u8131\u4e86\u4e2a\u7cbe\u5149\uff0c\u5f53\u4f17\u8868\u6f14\u4e00\u4e1d\u4e0d\u6302\uff0c \u8fd9\u8981\u4f20\u51fa\u6b66\u6797\uff0c\u81ea\u5df1\u7684\u989c\u9762\u5c82\u4e0d\u662f\u88ab\u626b\u5c3d\u4e86\u5417\uff1f \u3000\u3000\u201c\u6458\u661f\u624b\u201d\u2014\u2014\u5317\u4e94\u7701\u6b66\u6797\u9886\u8896\uff0c\u7d22\u4ee5\u51b7\u9759\u3001\u6c89\u7740\u3001\u559c\u6076\u4e0d\u5f62\u4e8e\u8272\uff0c\u89c1\u79f0\u4e8e\u6b66\u6797\u3002\u5927\u6709 \u201c\u6cf0\u5c71\u5d29\u4e8e\u524d\u800c\u8272\u4e0d\u53d8\u201d\u4e4b\u6168\u3002\u8fd9\u4e00\u6b21\u4ed6\u5374\u52a8\u4e86\u771f\u6012\uff0c\u4ed6\u4e0e\u592b\u4eba\u5728\u540e\u5b85\u79d8\u5ba4\u4e4b\u4e2d\uff0c\u4e25\u5389\u5730\u8ffd \u95ee\u6155\u5bb9\u7ea2\uff0c\u5230\u5e95\u4e3a\u4e86\u4ec0\u4e48\uff0c\u7ade\u4e0d\u987e\u7f9e\u803b\u5230\u4e86\u5982\u6b64\u5730\u6b65\uff01 \u3000\u3000\u4f46\u6155\u5bb9\u7ea2\u4e00\u5473\u5730\u555c\u6ce3\uff0c\u5bf9\u7236\u4eb2\u7684\u95ee\u8bdd\uff0c\u4e00\u8a00\u4e0d\u7b54\u3002 \u3000\u3000\u8fd9\u66f4\u52fe\u8d77\u4e86\u6155\u5bb9\u6db5\u7684\u6012\u706b\uff0c\u201c\u556a\uff01\u201d\u4e00\u58f0\u8106\u54cd\uff0c\u4ed6\u4e00\u638c\u628a\u4e00\u4e2a\u540d\u5e08\u96d5\u523b\u7684\u6960\u6728\u8336\u51e0\uff0c\u51fb \u4e86\u4e2a\u7c89\u788e\u3002\u864e\u5730\u7acb\u8d77\u8eab\u6765\uff0c\u4ece\u58c1\u4e0a\u6458\u4e0b\u4e00\u67c4\u5207\u91d1\u65ad\u7389\u7684\u77ed\u5251\uff0c\u201c\u5f53\u5577\u201d\u4e00\u58f0\u63bc\u5728\u6155\u5bb9\u7ea2\u811a\u524d \u5730\u4e0a\uff0c\u5389\u58f0\u53f1\u9053\uff1a\u201c\u4f60\u5c31\u7ed9\u6211\u6b7b\u4e86\u5427\uff01\u514d\u5f97\u7ed9\u6211\u4e22\u8138\uff01\u201d \u3000\u3000\u8bf4\u7f62\uff0c\u6c14\u51b2\u51b2\u5730\u6454\u95e8\u51fa\u53bb\u3002 \u3000\u3000\u6155\u5bb9\u7ea2\u6beb\u4e0d\u72b9\u8c6b\uff0c\u62ac\u8d77\u6cea\u773c\uff0c\u89c1\u7236\u4eb2\u5df2\u7ecf\u51fa\u53bb\uff0c\u7acb\u523b\u62fe\u8d77\u5730\u4e0a\u7684\u77ed\u5251\uff0c\u731b\u5411\u81ea\u5df2\u9888\u95f4\u520e \u53bb\u3002 \u3000\u3000\u4e2d\u5e74\u8d35\u5987\u2014\u2014\u4e5f\u5c31\u662f\u5c55\u6c0f\u592b\u4eba\uff0c\u4f38\u624b\u593a\u4e0b\u5973\u513f\u624b\u4e2d\u77ed\u5251\uff0c\u4e00\u628a\u6402\u4f4f\u6155\u5bb9\u7ea2\u6d41\u6cea\u9053\uff1a\u201c\u7ea2 \u513f\uff0c\u5343\u4e07\u4e0d\u8981\u5bfb\u6b7b\uff01\u4f60\u7236\u4eb2\u53ea\u662f\u4e00\u65f6\u6c14\u6124\uff0c\u8fc7\u4e9b\u65f6\u4ed6\u5c31\u4f1a\u597d\u7684\uff01\u201d \u3000\u3000\u201c\u5a18\uff01\u201d\u6155\u5bb9\u7ea2\u4e0d\u7531\u54ed\u5012\u5728\u6bcd\u4eb2\u6000\u91cc\u2026\u2026 \u3000\u3000\u201c\u6458\u661f\u624b\u201d\u6155\u5bb9\u5e84\u4e3b\u6c14\u864e\u864e\u5730\u51fa\u4e86\u5185\u5b85\uff0c\u8d70\u81f3\u4e1c\u8de8\u9053\u53bb\u627e\u513f\u5b50\u6155\u5bb9\u627f\u4e1a\uff0c\u5b88\u95e8\u7684\u5c0f\u5e62\u5f00 \u95e8\u6162\u4e86\u4e00\u70b9\uff0c\u6155\u5bb9\u6db5\u4e00\u811a\u8e22\u98de\u4e86\u95e8\u6249\uff0c\u5b88\u95e8\u5c0f\u50ee\u542c\u5230\u4e00\u58f0\u5de8\u54cd\uff0c\u95e8\u6249\u5df2\u788e\uff0c\u4e0d\u77e5\u51fa\u4e86\u4f55\u4e8b\uff0c \u4ece\u95e8\u623f\u4e2d\u63a2\u5934\u5916\u671b\uff0c\u201c\u562d\u201d\u7684\u4e00\u58f0\uff0c\u5c0f\u50ee\u7684\u5934\u9885\u88ab\u5e84\u4e3b\u5288\u4e86\u4e2a\u7c89\u788e\uff0c\u7acb\u65f6\u5012\u5730\u6b7b\u53bb\uff0c\u5176\u4f59\u7537 \u5973\u4f63\u4eba\u89c1\u5230\u5982\u6b64\u60c5\u5f62\uff0c\u4e00\u4e2a\u4e2a\u5413\u5f97\u4f53\u4f3c\u7b5b\u7ce0\uff0c\u754f\u7f29\u5728\u5899\u89d2\u95e8\u8fb9\uff0c\u5927\u6c14\u90fd\u4e0d\u6562\u51fa\u4e00\u58f0\uff01 \u3000\u3000\u6070\u5de7\u51cc\u98ce\u516c\u5b50\u53c8\u672a\u5728\u623f\u5185\uff0c\u8fd9\u5c31\u66f4\u6fc0\u8d77\u4e86\u201c\u6458\u661f\u624b\u201d\u7684\u6012\u706b\uff0c\u4ed6\u627e\u4e0d\u5230\u51fa\u6c14\u7684\u5bf9\u8c61\uff0c\u4e00 \u6307\u624b\u53c8\u628a\u8fce\u95e8\u6761\u6848\u4e0a\u7684\u53e4\u73a9\u7389\u5668\uff0c\u6253\u788e\u4e86\u4e00\u5927\u5806\u3002 \u3000\u3000\u201c\u7a00\u54e9\u54d7\u5566\uff01\u201d \u3000\u3000\u4e00\u9635\u66b4\u664c\uff0c\u4f20\u51fa\u5ba4\u5916\uff0c\u628a\u754f\u7f29\u5728\u95e8\u5916\u7684\u7537\u5973\u4f63\u4eba\uff0c\u5413\u5f97\u4e2a\u4e2a\u9b42\u98de\u9b44\u6563\u3002\u8fd9\u6050\u60e7\u662f\u53cc\u91cd \u7684\uff1a\u4e0d\u4f46\u89e6\u5728\u5e84\u4e3b\u706b\u5934\u4e0a\uff0c\u5c0f\u547d\u5c31\u8981\u73a9\u5b8c\uff1b\u516c\u5b50\u623f\u5185\u6446\u8bbe\u6253\u7891\uff0c\u8fd9\u4e9b\u90fd\u662f\u516c\u5b50\u5fc3\u7231\u4e4b\u7269\uff0c\u4ed6 \u4eec\u90fd\u662f\u516c\u5b50\u9662\u4e2d\u7684\u4f63\u4eba\uff0c\u51cc\u98ce\u516c\u5b50\u6027\u60c5\u66b4\u8e81\uff0c\u51b7\u9177\u65e0\u60c5\uff0c\u66f4\u8d85\u8fc7\u5176\u7236\uff0c\u7b49\u5230\u8001\u7237\u5b50\u53d1\u5b8c\u813e\u6c14 \u8d70\u4e86\uff0c\u516c\u5b50\u56de\u6765\uff0c\u4ed6(\u5979)\u4eec\u53c8\u662f\u6709\u5634\u8bf4\u4e0d\u6e05\uff0c\u751a\u81f3\u8fde\u539f\u56e0\u90fd\u4e0d\u6562\u8bf4\u51fa\uff0c\u4fbf\u8981\u53d7\u5230\u90a3\u65e0\u60c5\u516c\u5b50 \u7684\u60e8\u9177\u8d23\u7f5a\u3002 \u3000\u3000\u56e0\u6b64\uff0c\u51cc\u98ce\u516c\u5b50\u9662\u4e2d\u7684\u7537\u5973\u4f63\u4eba\uff0c\u4eba\u4eba\u81ea\u5371\uff0c\u4e00\u4e2a\u4e2a\u5413\u5f97\u9762\u9762\u76f8\u89d1\uff0c\u505a\u58f0\u4e0d\u5f97\u2014\u2014 \u3000\u3000\u201c\u516c\u5b50\u4f55\u5904\u53bb\u4e86\uff1f\u201d \u3000\u3000\u6155\u5bb9\u5e84\u4e3b\u65cb\u98ce\u822c\u5730\u56de\u5230\u95e8\u5916\uff0c\u5389\u58f0\u547c\u53f1\u3002\u5413\u5f97\u4e09\u9b42\u5c11\u4e86\u4e8c\u9b42\u7684\u7537\u5973\u4f63\u4eba\uff0c\u6ca1\u6709\u4e00\u4e2a\u6562\u4e0a \u524d\u56de\u7b54\u3002 \u3000\u3000\u201c\u4f60\u4eec\u90fd\u54d1\u4e86\u5417\uff1f\u4e3a\u4ec0\u4e48\u4e0d\u56de\u8bdd\uff01\u201d \u3000\u3000\u6155\u5bb9\u5e84\u4e3b\u4e0d\u6012\u800c\u5a01\uff0c\u7d20\u5e38\u4e0d\u8a00\u4e0d\u7b11\uff0c\u95e8\u4e0b\u4eba\u7b49\u754f\u60e7\u4ed6\u50cf\u754f\u60e7\u5929\u7136\u795e\u4e00\u6837\uff0c\u8fde\u6b63\u773c\u76f8\u89c6\u90fd \u4e0d\u6562\uff0c\u5bf9\u4e00\u5207\u5429\u5490\uff0c\u53ea\u6709\u4f4e\u5934\u552f\u552f\u7684\u4efd\u513f\u3002\u5982\u4eca\uff0c\u5e84\u4e3b\u5927\u53d1\u96f7\u9732\uff0c\u8fd9\u8fd8\u662f\u4e0b\u4eba\u4eec\u4ece\u672a\u89c1\u8fc7\u7684 \u4e8b\u3002\u4e00\u4e2a\u4e2a\u5413\u5f97\u7259\u9f7f\u6253\u6218\uff0c\u817f\u809a\u5b50\u76f4\u8f6c\u7b4b\uff0c\u6ca1\u6709\u4e00\u4e2a\u6562\u4e0a\u524d\u6765\u3002\u201c\u516c\u5b50\u7237\u5728\u524d\u5385\u2026\u2026\u201d \u3000\u3000\u6700\u540e\u8fd8\u662f\u4e00\u4e2a\u5341\u56db\u4e94\u5c81\u7684\u9752\u8863\u5c0f\u5a62\uff0c\u5927\u7740\u80c6\u5b50\u8bf4\u3002 \u3000\u3000\u201c\u54fc\uff01\u201d\u6155\u5bb9\u5e86\u4e3b\u51b7\u54fc\u4e86\u4e00\u58f0\uff0c\u7adf\u6ca1\u6709\u518d\u53d1\u6012\uff0c\u62c2\u8896\u51fa\u4e86\u516c\u5b50\u9662\u95e8\u3002 \u3000\u3000\u7537\u5973\u4f63\u4eba\u4eec\uff0c\u4e0d\u7981\u957f\u957f\u51fa\u4e86\u4e00\u53e3\u6c14\uff0c\u90fd\u89c9\u5f97\u6709\u70b9\u50cf\u4ece\u9b3c\u95e8\u5173\u4e0a\u8f6c\u4e86\u4e00\u5708\u56de\u6765\u7684\u611f\u89c9\uff0c\u8fd9 \u6761\u547d\u4e0d\u557b\u662f\u68c0\u56de\u6765\u7684\u4e00\u822c\u4e86\u2026\u201d \u3000\u3000\u6155\u5bb9\u5e84\u4e3b\u201c\u6458\u661f\u624b\u201d\u6012\u706b\u711a\u5fc3\uff0c\u4ece\u513f\u5b50\u9662\u5185\u6c14\u552c\u552c\u5730\u51fa\u6765\uff0c\u5411\u524d\u9662\u5927\u5385\u5185\u8d70\u53bb\u65f6\uff0c\u5929\u8272 \u5df2\u5168\u9ed1\u4e86\u4e0b\u6765\u3002\u8bfa\u5927\u4e00\u5ea7\u8c79\u7a81\u5c71\u5e84\u5185\uff0c\u6bcf\u95f4\u623f\u5b50\u95e8\u7a97\u4e4b\u95f4\u900f\u51fa\u7684\u706f\u5149\uff0c\u5176\u6570\u76ee\u4e4b\u591a\uff0c\u76f4\u53ef\u548c \u6ee1\u5929\u7e41\u661f\u4e92\u76f8\u8f89\u6620\u3002 \u3000\u3000\u6bcf\u9053\u9662\u95e8\uff0c\u623f\u5c4b\u8f6c\u89d2\uff0c\u901a\u8def\u53e3\u4e0a\uff0c\u5904\u5904\u6709\u660e\u6869\u6697\u5361\uff0c\u89c1\u4e86\u5e84\u4e3b\uff0c\u4e00\u4e2a\u4e2a\u606d\u8eab\u884c\u672d\u3002\u201c\u6458 \u661f\u624b\u201d\u7406\u4e5f\u4e0d\u7406\uff0c\u53ea\u5927\u8e0f\u6b65\u5411\u524d\u8d70\u7740\u2026\u2026\u7a81\u7136\u5728\u4e00\u5904\u82b1\u6728\u6276\u758f\u4e4b\u5904\uff0c\u5ffd\u6709\u4e09\u6761\u9ed1\u5f71\uff0c\u6bd4\u7535\u8fd8 \u75be\uff0c\u4e00\u6643\u8ddf\u9690\u6ca1\u4e8e\u82b1\u4e1b\u4e4b\u4e2d\u3002 \u3000\u3000\u201c\u8c01\uff1f\u201d \u3000\u3000\u201c\u6458\u661f\u624b\u201d\u76ee\u5149\u4f55\u7b49\u7280\u5229\uff0c\u867d\u5728\u9ed1\u591c\u4e4b\u4e2d\uff0c\u90a3\u4e09\u6761\u9ed1\u5f71\u53c8\u5947\u5feb\u65e0\u6bd4\uff0c\u4f46\u4e5f\u96be\u9003\u8fc7\u201c\u6458\u661f \u624b\u201d\u7684\u773c\u775b\uff0c\u56e0\u6b64\uff0c\u66b4\u7136\u559d\u95ee\u4e86\u4e00\u58f0\u3002 \u3000\u3000\u98ce\u58f0\u7c0c\u7136\uff0c\u4e09\u4e2a\u9ed1\u5dfe\u8499\u9762\u7684\u591c\u884c\u4eba\uff0c\u624b\u4e2d\u60e7\u5404\u6267\u5bd2\u5149\u95ea\u95ea\u7684\u957f\u5251\uff0c\u6a2a\u5251\u5f53\u80f8\uff0c\u98de\u8eab\u63a0\u5230 \u6458\u661f\u624b\u9762\u524d\u3002 \u3000\u3000\u8fd9\u5012\u5927\u51fa\u6155\u5bb9\u6db5\u610f\u5916\uff0c\u4ed6\u4e07\u4e5f\u60f3\u4e0d\u5230\u81ea\u5df1\u5367\u864e\u85cf\u9f99\u7684\u201c\u8c79\u7a81\u5c71\u5e84\u201d\uff0c\u52a0\u4e0a\u81ea\u5df1\u6b66\u6797\u4e2d\u7684 \u58f0\u540d\u548c\u5730\u4f4d\uff0c\u591c\u4e4b\u95f4\uff0c\u7adf\u4f1a\u6709\u591c\u884c\u4eba\u51fa\u73b0\uff01\u90a3\u4e48\uff0c\u8fd9\u591c\u884c\u4eba\u7684\u80c6\u5b50\u53ef\u7b97\u5f97\u662f\u80c6\u5927\u5305\u5929\u4e86\u3002 \u3000\u3000\u6155\u5bb9\u5e84\u4e3b\u4e4d\u89c1\u4e09\u6761\u9ed1\u5f71\uff0c\u9690\u6ca1\u4e8e\u82b1\u4e1b\u4e4b\u4e2d\uff0c\u8fd8\u4ee5\u4e3a\u662f\u81ea\u5df1\u5e84\u4e0a\u7684\u95e8\u5ba2\uff0c\u968f\u4e4b\u4e00\u58f0\u559d\u95ee\uff0c \u89c1\u4e09\u4e2a\u591c\u884c\u4eba\u4ee5\u9ed1\u5dfe\u8499\u9762\uff0c\u624b\u6267\u957f\u5251\uff0c\u73b0\u8eab\u5728\u81ea\u5df2\u9762\u524d\uff0c\u5fc3\u4e2d\u8be7\u5f02\u5927\u4e8e\u60ca\u5947\uff0c\u4ed6\u65e2\u4e0d\u76f8\u4fe1\u6c5f \u6e56\u9053\u4e0a\u7684\u4eba\u7269\uff0c\u6709\u80c6\u91cf\u6562\u6765\u591c\u72af\u8c79\u7a81\u5c71\u5e84\uff0c\u53c8\u5947\u602a\u81ea\u5df1\u5e84\u4e0a\u7684\u660e\u6869\u6697\u5361\uff0c\u90fd\u662f\u5e72\u4ec0\u4e48\u5403\u7684 \u4e86\uff0c\u6709\u591c\u884c\u4eba\u95ef\u5165\u5e84\u5185\u8179\u5730\uff0c\u7ade\u65e0\u4e00\u4eba\u53d1\u89c9\uff01 \u3000\u3000\u201c\u4f55\u65b9\u670b\u53cb\uff1f\u5924\u591c\u6765\u5230\u8c79\u7a81\u5c71\u5e84\uff0c\u6709\u4f55\u89c1\u6559\uff1f\u201d \u3000\u3000\u6155\u5bb9\u6db5\u867d\u5728\u6c14\u5934\u4e0a\uff0c\u4f46\u89c1\u5230\u6c5f\u6e56\u9053\uff0c\u4ecd\u7136\u80fd\u591f\u9547\u5b9a\u4e0b\u6765\uff0c\u4e0d\u5931\u4e00\u6d3e\u5b97\u4e3b\u8eab\u4efd\uff0c\u8bf4\u7684\u8bdd\u4e0d \u75be\u4e0d\u5f90\uff0c\u4ece\u5bb9\u4e0d\u8feb\u3002 \u3000\u3000\u201c\u65e2\u6765\u5b9d\u5e84\u4e0a\u5f53\u7136\u662f\u597d\u670b\u53cb\u4e86\uff01\u201d \u3000\u3000\u5f53\u4e2d\u7ad9\u7acb\u7684\u8499\u9762\u591c\u884c\u4eba\uff0c\u4e00\u8a00\u752b\u7f62\uff0c\u4e24\u65c1\u7ad9\u7acb\u7684\u9ed1\u8863\u8499\u9762\u4eba\uff0c\u5374\u4ee5\u8fc5\u96f7\u4e0d\u53ca\u63a9\u8033\u7684\u624b \u6bb5\uff0c\u4e24\u652f\u957f\u5251\uff0c\u5982\u6025\u98ce\u66b4\u96e8\u4e00\u822c\uff0c\u731b\u5411\u201c\u6458\u661f\u624b\u201d\u5de6\u53f3\u8981\u5bb3\u523a\u6765\u3002 \u3000\u3000\u8fd9\u4e24\u4e2a\u8499\u9762\u4eba\uff0c\u6b66\u529f\u4e0d\u5f31\uff0c\u4e24\u652f\u957f\u5251\uff0c\u62db\u5f0f\u7cbe\u5947\uff0c\u4f0a\u7136\u6b66\u5f53\u8def\u6570\uff0c\u663e\u89c1\u662f\u5251\u672f\u540d\u5bb6\uff0c\u800c \u4e14\u4e8c\u4eba\u5206\u523a\u5408\u51fb\uff0c\u914d\u5408\u5f97\u5929\u8863\u65e0\u7f1d\u3002 \u3000\u3000\u4e24\u67c4\u957f\u5251\uff0c\u70b9\u523a\u4e4b\u95f4\uff0c\u7eab\u6210\u4e94\u4e03\u6735\u91d1\u82b1\uff0c\u7535\u95ea\u661f\u98de\u822c\u653b\u5411\u6155\u5bb9\u5e84\u4e3b\u3002 \u3000\u3000\u8fd9\u5c31\u770b\u51fa\u6155\u5bb9\u6db5\u827a\u4e1a\u4e0d\u6bd4\u5bfb\u5e38\u6765\u4e86\uff0c\u5728\u4e24\u5927\u5251\u672f\u9ad8\u624b\u9aa4\u8d77\u53d1\u96be\u3001\u770b\u6765\u4e07\u96be\u62db\u67b6\u7684\u51cc\u5389\u653b \u52bf\u4e4b\u4e0b\uff0c\u4ed6\u6c89\u54fc\u4e00\u58f0\uff0c\u62a1\u8d77\u5de6\u624b\u4e8c\u6307\uff0c\u8f7b\u6572\u5de6\u53f3\u4e8c\u5251\uff0c\u201c\u53ee\uff01\u53ee\uff01\u201d\u4e24\u58f0\u5fae\u54cd\uff0c\u4e24\u4e2a\u8499\u9762\u4eba \u7684\u957f\u5251\uff0c\u7acb\u88ab\u9707\u5f00\u6570\u5bf8\u3002 \u3000\u3000\u4e24\u8499\u9762\u4eba\u987f\u89c9\u63e1\u5251\u53f3\u624b\u638c\u5fc3\u4e00\u70ed\uff0c\u957f\u5251\u51e0\u4e4e\u628a\u63e1\u4e0d\u4f4f\uff0c\u4fbf\u77e5\u4e0d\u597d\uff0c\u624d\u5f85\u62bd\u5251\u6362\u5f0f\uff0c\u4f46 \u201c\u6458\u661f\u624b\u201d\u5de6\u624b\u4e8c\u6307\u70b9\u5f00\u957f\u5251\uff0c\u53f3\u638c\u5e73\u80f8\u534a\u5706\u5212\u51fa\uff0c\u867d\u89c1\u4ed6\u638c\u52bf\u4e0d\u75be\uff0c\u4f46\u9510\u5578\u7684\u638c\u52b2\uff0c\u5df2\u968f \u7740\u4e00\u6325\u4e4b\u52bf\u53d1\u51fa\u3002\u4e24\u8499\u9762\u4eba\u53ea\u611f\u5230\u4e00\u80a1\u5de8\u5927\u65e0\u6bd4\u7684\u529b\u9053\u8fce\u80f8\u538b\u6765\uff0c\u518d\u60f3\u9003\u907f\uff0c\u7109\u5f97\u80fd\u591f\uff01\u53ea \u542c\u201c\u7830\uff01\u5676\uff01\u201d\u4e24\u58f0\u5de8\u9707\uff0c\u4e8c\u4eba\u80f8\u524d\u5982\u53d7\u4e07\u65a4\u94a2\u9524\u731b\u62f3\u4e00\u822c\uff0c\u7acb\u88ab\u9707\u98de\u6587\u4e4b\u5f00\u5916\uff0c\u53e3\u55b7\u9c9c\u8840 \u800c\u4ea1\u3002 \u3000\u3000\u53e6\u4e00\u540d\u8499\u9762\u4eba\u4e00\u89c1\u5927\u60ca\uff0c\u987e\u4e0d\u5f97\u770b\u89c6\u540c\u4f34\u6b7b\u6d3b\uff0c\u98de\u8eab\u60f3\u9003\uff0c\u4f46\u4ed6\u8eab\u5f62\u5c1a\u672a\u7eb5\u8d77\uff0c\u7a81\u611f\u624b \u8155\u4e00\u7d27\uff0c\u5982\u88ab\u94a2\u7b8d\u4f4f\u4e00\u822c\uff0c\u5de6\u624b\u5df2\u88ab\u201c\u6458\u661f\u624b\u201d\u6293\u4f4f\u3002 \u3000\u3000\u6458\u661f\u624b\u51b7\u54fc\u4e00\u58f0\uff0c\u6c89\u58f0\u559d\u9053\uff1a\u201c\u4f60\u7ed9\u6211\u8eba\u4e0b\uff01\u201d \u3000\u3000\u8499\u9762\u4eba\u7acb\u611f\u201c\u80be\u4fde\u7a74\u201d\u4e00\u9ebb\uff0c\u6492\u624b\u4e22\u5251\uff0c\u7ffb\u8eab\u683d\u4f8b\u3002 \u3000\u3000\u6458\u661f\u624b\u4e0d\u6127\u4e3a\u5317\u4e94\u7701\u6b66\u6797\u76df\u4e3b\uff0c\u7565\u4e00\u4f38\u624b\uff0c\u4e09\u4e2a\u591c\u884c\u4eba\uff0c\u4f46\u90fd\u79f0\u5f97\u8d77\u662f\u6b66\u6797\u4e00\u6d41\u9ad8\u624b\uff0c \u7acb\u5373\u4e8c\u6b7b\u4e00\u4f24\uff01\u4e09\u4eba\u5012\u5730\uff0c\u7830\uff0c\u562d\uff01\u5de8\u54cd\uff0c\u4e09\u5251\u8131\u624b\uff0c\u5f53\u5577\uff01\u9707\u8033\uff0c\u500f\u65f6\u95f4\u60ca\u52a8\u4e86\u9644\u8fd1\u7684\u660e \u6869\u6697\u5361\uff0c\u516d\u4e03\u540d\u52b2\u88c5\u5927\u6c49\u5df2\u98de\u8e7f\u81f3\u8fd1\u524d\uff0c\u4e00\u89c1\u662f\u5e84\u4e3b\u4eb2\u81ea\u6349\u5230\u523a\u5ba2\uff0c\u4e00\u4e2a\u4e2a\u5413\u5f97\u9762\u5982\u571f\u8272\u3002 \u3000\u3000\u201c\u5636\uff01\u201d\u6458\u661f\u624b\u4e00\u628a\u5c06\u591c\u884c\u4eba\u8499\u9762\u9ed1\u5dfe\u653e\u4e0b\uff0c\u770b\u6765\u4eba\u7adf\u662f\u4e00\u4e2a\u5eff\u4f59\u5c81\u9752\u5e74\uff0c\u5e76\u4e0d\u8ba4\u8bc6\uff1f \u51b7\u54fc\u4e86\u4e00\u58f0\uff0c\u624d\u5f85\u8981\u95ee\uff0c\u773c\u5149\u7a81\u7136\u770b\u5230\u9752\u5e74\u7d27\u8eab\u591c\u884c\u8863\u7684\u5de6\u524d\u895f\u4e0a\uff0c\u7ee3\u7740\u4e00\u53ea\u9e92\u9e9f\uff0c\u7acb\u65f6\u8138 \u8272\u4e00\u6c89\uff0c\u559d\u9053\uff1a\u201c\u6a0a\u975e\uff01\u4f60\u662f\u6a0a\u975e\u624b\u4e0b\uff1f\u201d \u3000\u3000\u5c1a\u672a\u7b49\u88ab\u64d2\u9752\u5e74\u7b54\u8bdd\uff0c\u6155\u5bb9\u5e84\u4e3b\u7a81\u7136\u4e00\u6296\u624b\uff0c\u628a\u88ab\u64d2\u9752\u5e74\u6454\u7ed9\u2014\u8fb9\u5413\u5446\u4e86\u7684\u58ee\u6c49\uff0c\u5428 \u9053\uff1a\u201c\u7ed9\u6211\u7ed1\u4e86\uff01\u201d \u3000\u3000\u559d\u58f0\u752b\u843d\uff0c\u6155\u5bb9\u5e86\u4e3b\u53cc\u8db3\u4e00\u989d\uff0c\u8eab\u5f62\u5e73\u5c04\u800c\u8d77\uff0c\u75be\u5982\u9e70\u96bc\uff0c\u5212\u7a7a\u800c\u53bb\u3002 \u3000\u3000\u539f\u6765\u524d\u9662\u5df2\u9690\u9690\u4f20\u6765\u6740\u4f10\u4e4b\u58f0\uff0c\u663e\u7136\u6709\u4e0d\u5c11\u5916\u654c\u4fb5\u5165\u5e84\u5185\uff0c\u662f\u4ee5\u6155\u5bb9\u5e84\u4e3b\u6025\u9a70\u800c\u53bb\u2026\u2026 \u3000\u3000\u51e0\u4e2a\u52b2\u88c5\u5927\u6c49\uff0c\u60ca\u9b42\u752b\u5b9a\uff0c\u7acb\u523b\u628a\u88ab\u64d2\u9752\u5e74\uff0c\u4e94\u82b1\u5927\u7ed1\u6346\u4e86\u4e2a\u7ed3\u5b9e\uff0c\u625b\u625b\u62ac\u62ac\u628a\u90a3\u5c11\u5e74 \u5173\u8fdb\u77f3\u7262\u4e4b\u5185\u3002 \u3000\u3000\u5c55\u767d\u6b63\u5728\u77f3\u7262\u5185\u547c\u5438\u5410\u7eb3\uff0c\u7ec3\u4e60\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\uff0c\u5df2\u8fbe\u6d51\u7136\u5fd8\u6211\u5efa\u5883\uff0c\u53ea\u611f\u7075\u53f0\u6e05 \u660e\uff0c\u5468\u8eab\u8212\u7545\u65e0\u6bd4\uff0c\u4e00\u9635\u771f\u6c14\u5728\u4f53\u5185\u7545\u901a\u6d41\u8f6c\u2026\u2026\u5ffd\u542c\u77f3\u95e8\u94c1\u9501\u94ff\u9535\u4e00\u54cd\uff0c\u201c\u5580\uff01\u201d\u7684\u4e00 \u58f0\uff0c\u53c8\u4ece\u7262\u5916\u63bc\u8fdb\u4e00\u4e2a\u4eba\u6765\uff0c\u5728\u6697\u4e2d\u5c55\u767d\u5f20\u76ee\u4e00\u671b\uff0c\u7ade\u770b\u5f97\u5206\u5916\u6e05\u695a\u3002 \u3000\u3000\u201c\u591c\u80fd\u89c6\u7269\u4e86\uff01\u201d\u5c55\u767d\u5fc3\u5934\u4e00\u9635\u72e1\u559c\u2026\u2026 \u3000\u3000\u539f\u6765\u5c55\u767d\u8fd0\u529f\u62b5\u6297\u94f6\u7bab\u7ae0\u58eb\u670b\u7684\u201c\u97f3\u9b54\u7bab\u6cd5\u201d\u65f6\uff0c\u6b63\u5728\u7d27\u8981\u5173\u5934\uff0c\u7a81\u88ab\u6155\u5bb9\u7ea2\u4e00\u6270\uff0c\u5dee \u4e00\u70b9\u8d70\u706b\u5165\u9b54\u3002\u5e78\u4e8f\u6155\u5bb9\u7ea2\u4e0e\u4ed6\u7684\u70ed\u7231\u955c\u5934\uff0c\u6fc0\u6012\u4e86\u51cc\u98ce\u516c\u5b50\uff0c\u5f53\u80f8\u6253\u4e86\u4ed6\u4e00\u638c\uff0c\u4e0d\u4f46\u672a\u4f7f \u4ed6\u53d7\u4f24\uff0c\u53cd\u800c\u628a\u4ed6\u4f53\u5185\u7684\u6b7b\u8109\u9707\u5f00\u4e86\uff0c\u4f7f\u4ed6\u5f20\u53e3\u5410\u51fa\u8151\u5185\u7684\u6de4\u8840\uff0c\u5185\u8151\u201c\u4efb\u7763\u201d\u4e8c\u8109\u6253\u901a\uff0c \u8fd9\u201c\u4efb\u7763\u201d\u4e8c\u8109\uff0c\u4e43\u662f\u7ec3\u6b66\u4e4b\u4eba\u6700\u96be\u653b\u7834\u7684\u4e00\u5173\u3002\u9634\u5dee\u9633\u9519\uff0c\u5c55\u81ea\u56e0\u7978\u5f97\u798f\uff0c\u7adf\u5728\u4e00\u8fde\u4e32\u7684 \u6298\u78e8\u6253\u51fb\u4e4b\u4e0b\uff0c\u628a\u201c\u4efb\u7763\u201d\u4e8c\u8109\u6253\u901a\uff0c\u771f\u4e43\u5929\u5047\u5947\u7f18\u3002 \u3000\u3000\u56fa\u7136\uff0c\u5c55\u767d\u5728\u65e0\u77e5\u4e4b\u4e2d\uff0c\u6253\u901a\u4e86\u7ec3\u6b66\u4e4b\u4eba\u8270\u96be\u751a\u4e8e\u4e00\u6b7b\u7684\u201c\u4efb\u7763\u201d\u4e8c\u8109\uff0c\u4f46\u4ed6\u6240\u7ecf\u7684\u5374 \u4e0d\u8db3\u6b63\u9014\uff0c\u5982\u679c\u4ed6\u88ab\u51cc\u98ce\u516c\u5b50\u4e00\u638c\u9707\u5f00\u6b7b\u8109\uff0c\u5982\u4e0d\u5373\u65f6\u6d3b\u52a8\u4e00\u4e0b\u5468\u8eab\u7b4b\u9aa8\uff0c\u5fc5\u81f4\u762b\u75ea\uff0c\u90a3\u4ed6 \u540e\u534a\u751f\u7684\u547d\u8fd0\u4e5f\u5c31\u5b8c\u4e86\u3002\u504f\u504f\u6155\u5bb9\u5e84\u4e3b\u9002\u65f6\u8d76\u81f3\uff0c\u628a\u4ed6\u5e26\u56de\u5e84\u5185\uff0c\u6709\u4e86\u6b65\u884c\u8fd9\u4e00\u6bb5\u8def\u7684\u8ddd \u79bb\uff0c\u4f7f\u4ed6\u540c\u8eab\u7b4b\u9aa8\u5df2\u6d3b\u52a8\u5f00\u4e86\uff0c\u4e0d\u81f4\u843d\u6210\u534a\u8eab\u4e0d\u9042\u7684\u6b8b\u5e9f\u4eba\u3002\u4f46\u4ec5\u662f\u6d3b\u52a8\u5f00\u7b4b\u9aa8\uff0c\u5982\u4e0d\u80fd\u63a5 \u7740\u83b7\u5f97\u4f11\u517b\u8c03\u606f\uff0c\u8fd8\u662f\u4f1a\u843d\u4e2a\u6c14\u8840\u865a\u5f31\u7684\u6b7b\u6545\u3002\u5de7\u4e0d\u5de7\uff0c\u6155\u5bb9\u5e84\u4e3b\u628a\u4ed6\u631f\u56de\u5e84\u5185\uff0c\u5e76\u672a\u7f57\u55e6 \u4ed6\uff0c\u53ea\u628a\u4ed6\u5173\u8fdb\u4e86\u77f3\u4e2d\uff0c\u6b63\u597d\u8ba9\u4ed6\u901a\u65f6\u5f97\u5230\u4e86\u8c03\u606f\u7684\u673a\u4f1a\u3002 \u3000\u3000\u53ef\u8bf4\u662f\u673a\u7f18\u51d1\u5de7\u5230\u4e86\u6781\u70b9\uff0c\u597d\u50cf\u5929\u610f\u5e94\u8be5\u4ea7\u751f\u5c55\u767d\u8fd9\u4e48\u4e00\u4f4d\u7eaf\u6b63\u5584\u826f\u7684\u6b66\u6797\u5947\u8469\u3002\u4e00\u5207 \u52a0\u5bb3\u4e8e\u4ed6\u7684\u529b\u91cf\uff0c\u53cd\u800c\u6210\u4e86\u52a9\u957f\u4ed6\u7684\u529b\u91cf\u3002 \u3000\u3000\u5c55\u767d\u8fd9\u65f6\u8c03\u606f\u8fc7\u6765\uff0c\u5c82\u4ec5\u201c\u9ed1\u591c\u80fd\u89c6\u7269\u201d\u7684\u6b66\u529f\u5185\u529b\uff0c\u4ea6\u4e0d\u6b62\u589e\u957f\u4e86\u6570\u500d\u3002 \u3000\u3000\u4e0d\u8fc7\u4ed6\u76ee\u524d\u8fd8\u4e0d\u81ea\u77e5\u7f62\u4e86\uff0c\u7b49\u5230\u4ed6\u81ea\u5df1\u77e5\u9053\u7684\u65f6\u5019\uff0c\u6050\u6015\u4ed6\u5df2\u7ecf\u662f\u5929\u4e0b\u65e0\u654c\u4e86\u3002 \u3000\u3000\u8fd9\u662f\u4ee5\u540e\u7684\u8bdd\u4e86\uff0c\u6682\u4e14\u4e0d\u8868\u3002\u4e14\u8bf4\uff0c\u5c55\u767d\u5728\u9ed1\u6697\u7684\u77f3\u7262\u4e2d\uff0c\u53c8\u662f\u9ed1\u591c\uff0c\u7741\u773c\u80fd\u591f\u89c6\u7269\uff0c \u5fc3\u5934\u4e00\u9635\u72c2\u559c\u3002\u4ed6\u8fd8\u4e0d\u6562\u76f8\u4fe1\u81ea\u5df1\uff0c\u5148\u62ac\u5934\u671b\u77f3\u4e2d\u94c1\u7a97\u5916\uff0c\u5929\u7a7a\u7684\u661f\u6597\uff0c\u77e5\u9053\u4e0d\u662f\u5047\uff0c\u53c8\u7528 \u7259\u9f7f\u54ac\u4e86\u81ea\u5df1\u7684\u624b\u6307\u5934\uff0c\u4e5f\u8bc1\u5b9e\u4e86\u4e0d\u662f\u68a6\uff0c\u671b\u89c1\u88ab\u5173\u8fdb\u7262\u6765\u7684\u8fd9\u4f4d\u96be\u53cb\u3002 \u3000\u3000\u53ea\u89c1\u4ed6\u7a7f\u4e86\u4e00\u8eab\u5bc6\u6263\u7d27\u8eab\u591c\u884c\u8863\uff0c\u8eab\u4e0a\u50cf\u626d\u80a1\u7cd6\u4f3c\u7684\u4e94\u82b1\u5927\u7ed1\u6346\u4e86\u4e2a\u7ed3\u5b9e\uff0c\u4e00\u5f20\u6e29\u7389\u4f3c \u7684\u4fca\u7f8e\u8138\u5e9e\uff0c\u751f\u5f97\u7709\u6e05\u76ee\u79c0\uff0c\u5507\u7ea2\u9f7f\u767d\uff0c\u597d\u4e00\u8868\u4fca\u54c1\u4eba\u7269\u3002 \u3000\u3000\u5c55\u767d\u4e0d\u77e5\u4e3a\u4ec0\u4e48\uff0c\u4e00\u89c1\u4ed6\u5c31\u597d\u50cf\u6709\u7f18\u4f3c\u7684\uff0c\u4e00\u79cd\u8bf4\u4e0d\u51fa\u7684\u4eb2\u5207\u4e4b\u611f\uff0c\u6cb9\u7136\u800c\u751f\uff0c\u4e0d\u7981\u51b2 \u7740\u4ed6\u6bd7\u7259\u4e00\u7b11\u3002\u8c01\u77e5\u90a3\u4eba\u4ef0\u9762\u5411\u5929\uff0c\u76ee\u5149\u671b\u7740\u623f\u9876\uff0c\u5bf9\u5c55\u767d\u7406\u4e5f\u4e0d\u7406\u3002\u5c55\u767d\u987f\u7136\u9192\u609f\uff0c\u65f6\u503c \u9ed1\u591c\uff0c\u7262\u623f\u5185\u53c8\u6ca1\u6709\u4eae\u706f\uff0c\u81ea\u5df1\u80fd\u770b\u5230\u4eba\u5bb6\uff0c\u6050\u6015\u4eba\u5bb6\u4e0d\u80fd\u770b\u5230\u81ea\u5df1\u5462\uff01\u4e8e\u662f\uff0c\u53d1\u8a00\u9053\uff1a \u201c\u8fd9\u4f4d\u5144\u53f0\uff0c\u56e0\u4f55\u4e5f\u88ab\u5173\u8fdb\u7262\u5185\uff1f\u201d \u3000\u3000\u201c\u8c01\uff1f\u4f60\u662f\u8c01\uff1f\u201d\u90a3\u4eba\u4ef0\u9996\u4fa7\u671b\uff0c\u76ee\u5149\u832b\u7136\uff0c\u663e\u89c1\u4ed6\u5728\u9ed1\u6697\u4e2d\u770b\u4e0d\u6e05\u7262\u5185\u60c5\u5f62\u3002 \u3000\u3000\u201c\u6211\u53eb\u5c55\u767d\uff0c\u670b\u53cb\u7684\u9ad8\u59d3\u5927\u540d\u80fd\u5426\u89c1\u544a\uff1f\u201d \u3000\u3000\u90a3\u4eba\u542c\u5230\u5c55\u767d\u81ea\u62a5\u59d3\u540d\uff0c\u60f3\u4e86\u4e00\u60f3\uff0c\u89c9\u5f97\u6c5f\u6e56\u4e0a\u6ca1\u6709\u542c\u8bf4\u8fc7\u8fd9\u4e48\u4e00\u53f7\u4eba\u7269\uff0c\u4e0d\u7531\u6212\u5fc3\u653e \u5bbd\u4e0d\u5c11\uff0c\u957f\u53f9\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u5df2\u7ecf\u505a\u4e86\u9636\u4e0b\u56da\uff0c\u8fd8\u63d0\u540d\u9053\u59d3\u505a\u751a\uff01\u670b\u53cb\uff0c\u8001\u5b9e\u5bf9\u4f60\u8bf4\uff0c\u6211\u548c \u4f60\u5747\u5df2\u8eab\u9677\u9f99\u6f6d\u864e\u7a74\uff0c\u53ea\u6709\u8ba4\u547d\u7b49\u6b7b\u7684\u4efd\u513f\uff0c\u8fd8\u6709\u4ec0\u4e48\u53ef\u8bf4\u7684\uff1f\u5509\uff01\u201d\u8bf4\u7f62\u53c8\u957f\u53f9\u4e86\u4e00\u58f0\u3002 \u3000\u3000\u5c55\u767d\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u751f\u6b7b\u6709\u547d\uff0c\u5927\u4e08\u592b\u751f\u800c\u4f55\u6b22\uff0c\u6b7b\u800c\u4f55\u60e7\uff1f\u53ea\u8981\u6211\u4eec\u6240\u4f5c\u6240\u4e3a\uff0c \u65e0\u6127\u4e8e\u81ea\u5df1\u7684\u826f\u5fc3\uff0c\u5200\u5251\u5728\u524d\uff0c\u53c8\u6709\u4ec0\u4e48\u53ef\u6015\u7684\uff1f\u800c\u4e14\uff0c\u6211\u4eec\u8fd8\u53ef\u4ee5\u51ed\u7740\u81ea\u5df1\u806a\u660e\u624d\u667a\uff0c\u594b \u6597\u6c42\u751f\uff01\u5c31\u7b97\u96be\u514d\u4e00\u6b7b\uff0c\u6211\u4eec\u80fd\u591f\u5728\u4e34\u6b7b\u4e4b\u524d\uff0c\u591a\u4ea4\u4e00\u4e2a\u670b\u53cb\uff0c\u4e0d\u662f\u4e5f\u53ef\u4ee5\u591a\u5f97\u4e00\u70b9\u5b89\u6170 \u5417\uff1f\u4f55\u51b5\u751f\u6b7b\u4e4b\u4ea4\uff0c\u60a3\u96be\u4e4b\u53cb\uff0c\u66f4\u662f\u6bd5\u751f\u96be\u9047\u5462\uff01\u201d \u3000\u3000\u90a3\u4eba\u542c\u4e86\u5c55\u767d\u8fd9\u4e00\u5927\u7bc7\u9053\u7406\uff0c\u89c9\u5f97\u5c55\u767d\u89c1\u89e3\u4e0d\u51e1\uff0c\u5fc3\u5934\u90c1\u7ed3\u6292\u6563\u4e0d\u5c11\uff0c\u968f\u8bf4\u9053\uff1a\u201c\u591a\u627f \u6307\u6559\u3002\u5728\u4e0b\u6a0a\u7d20\u2026\u2026\u201d\u672c\u6765\u4ed6\u540d\u5b57\u201c\u7d20\u201d\u5b57\u4e0b\u8fb9\u8fd8\u6709\u4e00\u4e2a\u5b57\uff0c\u4f46\u8bf4\u4e86\u4e00\u534a\uff0c\u60f3\u8d77\u9690\u60c5\uff0c\u7acb\u523b \u4f4f\u53e3\u672a\u8bf4\u3002\u5c55\u767d\u5374\u4ee5\u4e3a\u4ed6\u5c31\u662f\u201c\u59d3\u6a0a\u540d\u7d20\u201d\uff0c\u4e0d\u52a0\u601d\u7d22\u5730\u63a5\u53e3\u9053\uff1a\u201c\u539f\u6765\u662f\u6a0a\u7d20\u5144\uff01\u5f85\u5728\u4e0b \u4e3a\u6a0a\u5144\u677e\u7ed1\uff01\u201d \u3000\u3000\u8bf4\u7740\uff0c\u8d70\u6765\u4e3a\u6a0a\u7d20\u89e3\u4e0b\u8eab\u4e0a\u7684\u4e94\u82b1\u5927\u7ed1\uff0c\u90a3\u4eba\u4f30\u4e0d\u5230\u5c55\u767d\u624b\u811a\u672a\u7f1a\uff0c\u7b49\u5230\u5c55\u767d\u624b\u6307\u89e6\u5230 \u4ed6\u8eab\u4e0a\uff0c\u4ed6\u60f3\u8eb2\u95ea\u5df2\u662f\u529b\u4e0d\u4ece\u5fc3\u3002\u5ffd\u7136\u8138\u5b54\u4e00\u7ea2\uff0c\u60f3\u8d77\u81ea\u5df2\u7d20\u5e38\u4f55\u7b49\u5fc3\u9ad8\u6c14\u50b2\uff1f\u5982\u4eca\uff0c\u7adf\u53ea \u6709\u7741\u7740\u773c\u4efb\u4eba\u6446\u5e03\u7684\u4efd\u513f\u3002\u5fc3\u5982\u5200\u6512\uff0c\u4e0d\u7531\u843d\u4e0b\u4e24\u6ef4\u6cea\u6765\u3002 \u3000\u3000\u5c55\u767d\u4e0d\u77e5\u4ed6\u7684\u5fc3\u60c5\uff0c\u4e3a\u4ed6\u89e3\u5f00\u7ed1\u7f1a\uff0c\u53cd\u89c1\u4ed6\u843d\u6cea\uff0c\u968f\u5b89\u6170\u9053\uff1a\u201c\u6a0a\u5144\uff0c\u4e9b\u5fae\u632b\u6298\uff0c\u4f55\u987b \u70e6\u607c\uff01\u60f3\u5b54\u5723\u4eba\u90fd\u6709\u8fc7\u9648\u8521\u4e4b\u5371\u3002\u201d\u8bf4\u7740\uff0c\u53c8\u628a\u81ea\u5df2\u4e00\u751f\u4e2d\u6240\u53d7\u7684\u5c48\u8fb1\u6298\u78e8\u7565\u8bb2\u4e86\u4e00\u4e9b\u3002\u6a0a \u7d20\u542c\u5c55\u767d\u8a00\u8bed\u8bda\u6073\uff0c\u8c26\u865a\u70ed\u60c5\uff0c\u5185\u5fc3\u5bf9\u5c55\u767d\u66f4\u589e\u4e86\u51e0\u5206\u597d\u611f\u3002 \u3000\u3000\u4f46\u7b49\u5230\u5c55\u767d\u89e3\u5f00\u7ef3\u7d22\uff0c\u89c1\u4ed6\u4ecd\u4e0d\u80fd\u8f6c\u52a8\uff0c\u53d1\u73b0\u4ed6\u5c1a\u88ab\u70b9\u4e86\u7a74\u9053\uff0c\u8981\u60f3\u4e3a\u4ed6\u63a8\u5bab\u6d3b\u7a74\u65f6\uff0c \u6a0a\u7d20\u5374\u65e0\u8bba\u5982\u4f55\u4e0d\u80af\u8ba9\u5c55\u767d\u52a8\u624b\u3002 \u3000\u3000\u5c55\u767d\u89c1\u4ed6\u4e89\u5f97\u6ee1\u8138\u901a\u7ea2\uff0c\u5fc3\u4e2d\u867d\u611f\u5947\u602a\uff0c\u4f46\u5c55\u767d\u662f\u4e2a\u5fc3\u5730\u7eaf\u6d01\u7684\u9752\u5e74\uff0c\u5e76\u672a\u60f3\u5230\u5176\u4ed6\u3002 \u89c1\u4ed6\u81f3\u6b7b\u4e0d\u4ece\uff0c\u4fbf\u7528\u201c\u51cc\u7a7a\u62c2\u7a74\u201d\u624b\u6cd5\uff0c\u4e3a\u6a0a\u7d20\u89e3\u5f00\u4e86\u7a74\u9053\u3002 \u3000\u3000\u8fd9\u201c\u51cc\u7a7a\u62c2\u7a74\u201d\u624b\u6cd5\uff0c\u4e5f\u662f\u5c55\u767d\u4ece\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\u4e2d\uff0c\u6478\u7d22\u7ec3\u4e60\u5f97\u6765\u7684\u3002\u5c55\u767d\u53ea\u662f \u6309\u7167\u4e66\u4e2d\u6240\u5199\u65bd\u5c55\uff0c\u5e76\u4e0d\u77e5\u8fd9\u624b\u6cd5\uff0c\u65bd\u5c55\u51fa\u6765\u591a\u4e48\u60ca\u4e16\u9a87\u4fd7\uff01\u6a0a\u7d20\u5374\u6697\u6697\u5fc3\u60ca\uff0c\u60f3\u4e0d\u5230\u88ab\u6458 \u661f\u624b\u5173\u5728\u7262\u5185\uff0c\u8fd8\u6709\u8fd9\u7b49\u8eab\u8d1f\u7edd\u4e16\u6b66\u529f\u7684\u6b66\u6797\u9ad8\u624b\u3002 \u3000\u3000\u5c31\u5728\u6b64\u65f6\uff0c\u7a81\u89c1\u7a97\u5916\u706b\u5149\u4e00\u4eae\uff0c\u63a5\u7740\u4eba\u58f0\u9f0e\u6cb8\uff0c\u6740\u58f0\u9707\u8033\u2026\u2026 \u7b2c\u5341\u4e09\u7ae0\u3000\u5c71\u5e84\u591c\u6210\u6025>> \u53e4\u9f99\u300a\u5251\u5ba2\u884c\u300b \u7b2c\u5341\u4e09\u7ae0\u3000\u5c71\u5e84\u591c\u6210\u6025 \u3000\u3000\u4e8c\u4eba\u65b9\u4e00\u9519\u6115\uff0c\u7a81\u542c\u77f3\u7262\u95e8\u5916\u201c\u5443\uff01\u5443\uff01\u201d\u4e24\u58f0\u5589\u9e23\uff0c\u4f3c\u6709\u4eba\u88ab\u70b9\u4e2d\u7a74\u9053\uff0c\u63a5\u7740\u94c1\u95e8\u54d7 \u5577\u4e00\u58f0\u88ab\u6253\u5f00\uff0c\u4e00\u6761\u9880\u957f\u4eba\u5f71\u5728\u95e8\u524d\u4e00\u6643\uff0c\u6c89\u58f0\u559d\u9053\uff1a\u201c\u5feb\u8d70\uff01\u201d \u3000\u3000\u5c55\u767d\u773c\u5149\u4f55\u7b49\u7280\u5229\uff0c\u7acb\u523b\u770b\u51fa\u6765\u4eba\u662f\u4e71\u53d1\u98de\u84ec\u7684\u96f7\u5927\u53d4\uff0c\u4f46\u96f7\u5927\u53d4\u5728\u7262\u95e8\u53e3\u4e00\u95ea\u5373\u901d\uff0c \u5c55\u767d\u65e0\u6687\u7ec6\u60f3\uff0c\u4e00\u62c9\u6a0a\u7d20\uff0c\u559d\u9053\uff1a\u201c\u8d70\uff01\u201d \u3000\u3000\u201c\u8d70\u201d\u5b57\u51fa\u53e3\uff0c\u4eba\u5df2\u98de\u8eab\u63a0\u51fa\u4e2d\u95e8\uff0c\u6a0a\u7d20\u968f\u540e\u7d27\u8ddf\u51fa\u6765\uff0c\u4e8c\u4eba\u51fa\u5f97\u7262\u95e8\uff0c\u53ea\u89c1\u9760\u5de6\u4e00\u5e26 \u53a2\u623f\uff0c\u706b\u5149\u51b2\u5929\uff0c\u706b\u5149\u7167\u6620\u4e0b\uff0c\u5200\u5149\u5251\u5f71\uff0c\u4eba\u5f71\u7eb7\u7136\u4ea4\u9519\uff0c\u5df2\u6709\u6570\u5341\u4eba\u6349\u5bf9\u513f\u53ae\u6740\u5728\u4e00\u8d77\uff01 \u3000\u3000\u201c\u7830\u7830\uff01\u562d\u562d\uff01\u201d\u91d1\u94c1\u4ea4\u9e23\u53ca\u638c\u62f3\u76f8\u4ea4\u4e4b\u58f0\uff0c\u60ca\u5929\u52a8\u5730\uff0c\u6218\u51b5\u751a\u662f\u6fc0\u70c8\uff0c\u5c1a\u4e0d\u65f6\u4f20\u51fa\u8d1f \u4f24\u5012\u5730\u4e4b\u4eba\u7684\u95f7\u54fc\u60e8\u964d\uff0c\u773c\u770b\u7740\uff0c\u65ad\u80a2\u98de\u5c04\uff0c\u5200\u5d29\u8840\u73b0\uff0c\u4ee4\u4eba\u89e6\u76ee\u5fc3\u60ca\uff0c \u3000\u3000\u5c55\u767d\u4e1c\u5bdf\u897f\u770b\uff0c\u5374\u770b\u4e0d\u5230\u96f7\u5927\u53d4\u5728\u4f55\u5904\uff1f\u6a0a\u7d20\u4e00\u773c\u770b\u5230\u81ea\u5df1\u65b9\u9762\u7684\u4eba\uff0c\u6b7b\u4f24\u60e8\u91cd\uff0c\u6e10\u5448 \u4e0d\u652f\u73b0\u8c61\uff1b\u8c79\u7a81\u5c71\u5e84\u65b9\u9762\u7684\u4eba\uff0c\u5374\u6108\u6218\u6108\u52c7\uff0c\u5176\u4e2d\u4e00\u4e2a\u7707\u76ee\u9053\u4eba\u66f4\u662f\u52c7\u4e0d\u53ef\u5f53\uff0c\u8eab\u5f62\u7535\u65cb\u661f \u98de\uff0c\u4e00\u53cc\u94c1\u638c\u6325\u821e\u5f97\u98ce\u96e8\u4e0d\u900f\uff0c\u638c\u98ce\u70c8\u70c8\uff0c\u75be\u5578\u751f\u98d8\uff0c\u53ef\u8bf4\u662f\u5f53\u8005\u6218\u9761\u3002 \u3000\u3000\u6a0a\u7d20\u4e0d\u77e5\u90a3\u7707\u76ee\u9053\u4eba\u662f\u4f55\u8bb8\u4eba\u7269\uff0c\u53ea\u89c1\u81ea\u5df1\u7684\u4e8c\u54e5\uff0c\u8ffd\u98ce\u5251\u201d\u6a0a\u6770\uff0c\u53ca\u5954\u725b\u5c71\u4e8c\u4e49\u91d1\u6c0f \u5f1f\u5144\uff0c\u4e09\u4eba\u4e09\u652f\u957f\u5251\uff0c\u4f1a\u6218\u7707\u76ee\u9053\u4eba\uff0c\u7ade\u88ab\u7707\u76ee\u9053\u4eba\u4e00\u53cc\u94c1\u638c\u903c\u5f97\u6ef4\u6e9c\u6e9c\u4e71\u8f6c\uff0c\u5982\u8d70\u9a6c\u706f\u76f8 \u4eff\uff0c\u4e0d\u4f46\u8fd1\u8eab\u4e0d\u5f97\uff0c\u800c\u4e14\u9669\u72b6\u767e\u51fa\u3002 \u3000\u3000\u6b63\u5de7\u8ffd\u98ce\u5251\u6a0a\u6770\u5251\u6f14\u201c\u8ffd\u98ce\u5251\u201d\u7edd\u62db\u201c\u75be\u98ce\u65a9\u52b2\u8349\u201d\uff0c\u8eab\u8eaf\u5e73\u5c04\u800c\u51fa\uff0c\u624b\u4e2d\u5251\u5316\u4e00\u9053\u94f6 \u8292\uff0c\u76f4\u5411\u7802\u76ee\u9053\u4eba\u540e\u5fc3\u201c\u4e09\u7126\u201d\u3001\u201c\u51e4\u773c\u201d\u3001\u201c\u795e\u5802\u201d\u4e09\u5927\u91cd\u7a74\u7f69\u53bb\u3002 \u3000\u3000\u7707\u76ee\u9053\u4eba\u5de6\u624b\u7b95\u5f20\uff0c\u6307\u5c16\u5fae\u52fe\uff0c\u731b\u6293\u91d1\u6c0f\u8001\u5927\u9762\u95e8\uff0c\u53f3\u638c\u6a2a\u7acb\u5982\u5200\uff0c\u659c\u780d\u91d1\u6c0f\u8001\u4e8c\u201c\u80a9 \u5e76\u201d\u4e00\u62db\u4e8c\u5f0f\uff0c\u540c\u65f6\u653b\u5411\u4e8c\u4eba\uff0c\u8eab\u624b\u4e0d\u51e1\u91d1\u6c0f\u5f1f\u5144\u7acb\u611f\u4e00\u822c\u5de8\u5927\u538b\u529b\uff0c\u8fce\u9762\u649e\u6765\uff0c\u80f8\u95f4\u6c14\u7ffb \u8840\u6d8c\uff0c\u6b32\u60f3\u62db\u67b6\uff0c\u800c\u4e3e\u624b\u4e4f\u529b\uff0c\u582a\u582a\u5c31\u8981\u4f24\u5728\u7707\u76ee\u9053\u4eba\u638c\u6307\u4e4b\u4e0b\u3002 \u3000\u3000\u8ffd\u98ce\u5251\u201c\u75be\u98ce\u65a9\u52b2\u8349\u201d\u62db\u5f0f\u9002\u65f6\u800c\u81f3\uff0c\u5bd2\u68ee\u68ee\u7684\u5251\u5c16\uff0c\u773c\u770b\u8ddd\u7707\u76ee\u9053\u4eba\u540e\u5fc3\u4e0d\u53ca\u4e09\u5bf8\uff1b \u90a3\u7707\u76ee\u9053\u4eba\u867d\u7136\u53cc\u76ee\u5df2\u76f2\uff0c\u80cc\u540e\u5374\u50cf\u957f\u4e86\u773c\u775b\u4e00\u822c\uff0c\u8eab\u5f62\u500f\u7136\u4e00\u8f6c\uff0c\u8ffd\u98ce\u5251\u957f\u5251\u843d\u7a7a\uff0c\u64e6\u8863 \u800c\u8fc7\uff0c\u7707\u76ee\u9053\u4eba\u767d\u679c\u773c\u4e00\u7ffb\uff0c\u66b4\u559d\u4e00\u58f0\uff1a\u201c\u7740\uff01\u201d\u7535\u5954\u4f3c\u5730\u4e00\u638c\u5411\u8ffd\u98ce\u5251\u51cc\u7a7a\u5e73\u5c04\u7684\u8eab\u5f62\u731b \u780d\u800c\u4e0b\u3002 \u3000\u3000\u8ffd\u98ce\u5251\u7528\u52b2\u592a\u731b\uff0c\u672c\u60f3\u4e00\u62db\u7ed3\u675f\u7707\u76ee\u9053\u4eba\uff0c\u6ca1\u60f3\u5230\u7707\u76ee\u9053\u4eba\u201c\u542c\u98ce\u8fa8\u4f4d\u201d\uff0c\u8033\u6735\u6bd4\u773c\u775b \u7075\u6d3b\uff0c\u4e0d\u4f46\u8eb2\u8fc7\u4ed6\u201c\u8ffd\u98ce\u5251\u201d\u6740\u62db\uff0c\u800c\u4e14\u4e00\u638c\u780d\u6765\uff0c\u75be\u5578\u7834\u98ce\uff0c\u8ffd\u98ce\u5251\u8eab\u5f62\u51cc\u7a7a\uff0c\u65e0\u6cd5\u6362 \u5f0f\uff0c\u6697\u53eb\u4e00\u58f0\uff1a\u201c\u543e\u547d\u4f11\u77e3\uff01\u201d \u3000\u3000\u7a81\u89c1\u4e00\u6761\u9ed1\u5f71\u7535\u6cfb\u800c\u81f3\uff0c\u534a\u7a7a\u4e2d\u786c\u63a5\u4e86\u7707\u76ee\u9053\u4eba\u4e00\u638c\uff0c\u201c\u81a8\u201d\u7684\u4e00\u58f0\u5927\u9707\uff0c\u6765\u4eba\u7ade\u4ece\u7707 \u76ee\u9053\u4eba\u638c\u4e0b\uff0c\u6551\u4e86\u8ffd\u98ce\u5251\u4e00\u547d\u3002 \u3000\u3000\u4f46\u6765\u4eba\u5374\u88ab\u7707\u76ee\u9053\u4eba\u5a01\u5927\u65e0\u5339\u7684\u638c\u98ce\uff0c\u51ed\u7a7a\u9707\u98de\u4e24\u4e08\uff0c\u90a3\u4eba\u8eab\u5728\u534a\u7a7a\u8fde\u7ffb\u4e09\u4e2a\u7b4b\u6597\uff0c\u624d \u843d\u8db3\u5730\u4e0a\uff0c\u4f46\u5df2\u8e09\u8dc4\u6570\u6b65\uff0c\u6447\u6447\u6b32\u5012\u3002 \u3000\u3000\u5c55\u767d\u770b\u5f97\u6e05\u695a\uff0c\u90a3\u6551\u4e86\u8ffd\u98ce\u5251\u4e00\u547d\u7684\uff0c\u6b63\u662f\u8ddf\u81ea\u5df1\u7ad9\u5728\u4e00\u8d77\u7684\u6a0a\u7d20\u3002 \u3000\u3000\u8ffd\u98ce\u5251\u6b7b\u91cc\u9003\u751f\uff0c\u8eab\u5f62\u76f4\u98d8\u51fa\u4e00\u6587\u5f00\u5916\uff0c\u624d\u53cc\u811a\u843d\u5730\uff0c\u60ca\u9b42\u752b\u5b9a\uff0c\u6a2a\u5251\u5f53\u80f8\uff0c\u77a0\u76ee\u56db \u987e\uff1b\u6a0a\u7d20\u786c\u6321\u4e86\u7707\u76ee\u9053\u4eba\u4e00\u638c\uff0c\u6574\u6761\u624b\u81c2\u5374\u9ebb\u4e86\uff0c\u80f8\u5185\u6c14\u7ffb\u8840\u6d8c\uff0c\u53cc\u811a\u8e0f\u5730\uff0c\u52c9\u5f3a\u672a\u5012\uff0c\u6b63 \u5728\u62bf\u7d27\u5634\u5507\uff0c\u5f3a\u538b\u4f4f\u51b2\u5230\u5589\u5934\u7684\u4e00\u53e3\u9c9c\u8840\uff0c\u661f\u76ee\u6da3\u6563\uff0c\u4fca\u7f8e\u7684\u8138\u4e0a\u4e00\u7247\u60e8\u66f0\uff0c \u3000\u3000\u201c\u597d\u5c0f\u5b50\uff01\u201d\u5999\u76ee\u9053\u4eba\u4e00\u58f0\u72c2\u7b11\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u8fd8\u6709\u591a\u5c11\u4eba\uff1f\u5c31\u4e00\u9f50\u4e0a\u5427\uff01\u8ba9\u9053\u7237\u4e00\u9053 \u9001\u4f60\u4eec\u4e0a\u897f\u5929\uff01\u201d \u3000\u3000\u7707\u76ee\u9053\u4eba\u5634\u4e2d\u8bf4\u7740\uff0c\u5de6\u624b\u5e73\u80f8\u6325\u51fa\u4e00\u638c\uff0c\u7834\u7a7a\u72c2\u98d8\uff0c\u5377\u5411\u5954\u725b\u5c71\u4e8c\u4e49\u53ca\u8ffd\u98ce\u5251\uff0c\u53f3\u624b\u9ad8 \u4e3e\u8fc7\u9876\uff0c\u6447\u4e86\u4e24\u6447\uff0c\u81c2\u4e0a\u9aa8\u8282\u201c\u54af\uff01\u54af\uff01\u201d\u7206\u54cd\uff0c\u638c\u5fc3\u7acb\u65f6\u53d8\u6210\u975b\u9752\uff0c\u6cf0\u5c71\u538b\u9876\u4e00\u822c\uff0c\u52b2\u5578 \u7834\u7a7a\u5411\u6a0a\u7d20\u5f53\u5934\u62cd\u4e0b\u3002 \u3000\u3000\u663e\u7136\u7707\u76ee\u9053\u4eba\u6210\u5fc3\u8981\u628a\u6a0a\u7d20\u4e00\u638c\u51fb\u6bd9\uff0c\u8fd9\u4e00\u638c\u7adf\u662f\u5168\u8eab\u529f\u529b\u6240\u805a\u3002 \u3000\u3000\u6a0a\u7d20\u5927\u5403\u4e00\u60ca\uff0c\u89c1\u7707\u76ee\u9053\u4eba\u638c\u98ce\u5982\u6392\u7a7a\u5de8\u6d6a\uff0c\u8fce\u5934\u538b\u4e0b\uff0c\u5a01\u529b\u7adf\u5e7f\u7f69\u4e8c\u4e08\u4ee5\u5185\uff0c\u8eb2\u4e5f\u65e0 \u5904\u53ef\u8eb2\uff0c\u65e0\u5948\u54ac\u7d27\u7259\u5173\uff0c\u201c\u9738\u738b\u4e3e\u9f0e\u201d\uff0c\u53cc\u638c\u4ee5\u5468\u8eab\u6240\u6709\u7684\u529b\u91cf\u5411\u4e0a\u8fce\u53bb\u3002 \u3000\u3000\u5954\u725b\u5c71\u4e8c\u4e49\u53ca\u8ffd\u98ce\u5251\uff0c\u88ab\u7707\u76ee\u9053\u4eba\u5de6\u638c\u6325\u51fa\u7231\u98ce\u52b2\u6d41\uff0c\u903c\u5f97\u60ca\u547c\u6025\u9000\uff1b\u6a0a\u7d20\u786c\u63a5\u7707\u76ee\u9053 \u4eba\u4e00\u638c\uff0c\u5185\u8151\u5df2\u7ecf\u53d7\u4f24\u4e0d\u8f7b\uff0c\u600e\u518d\u5f53\u5f97\u8d77\u7707\u76ee\u9053\u4eba\u5168\u8eab\u529f\u529b\u6240\u805a\u7684\u4e00\u51fb\uff1f\u53cc\u638c\u4ee5\u5468\u8eab\u771f\u529b\u5411 \u4e0a\u8fce\u53bb\uff0c\u7acb\u611f\u7707\u76ee\u9053\u4eba\u638c\u52bf\u5982\u5343\u65a4\u5de8\u95f8\uff0c\u5f53\u5934\u538b\u4e0b\uff0c\u773c\u524d\u4e00\u9ed1\uff0c\u518d\u4e5f\u5fcd\u4e0d\u4f4f\u51b2\u5230\u53e3\u8fb9\u7684\u9c9c \u8840\uff0c\u4e00\u5f20\u5634\u201c\u54c7\uff01\u201d\u7684\u4e00\u58f0\uff0c\u6ee1\u5634\u9c9c\u8840\u72c2\u55b7\uff0c\u4eba\u4e5f\u5f80\u524d\u683d\u5012\u3002 \u3000\u3000\u201c\u4f4f\u624b\uff01\u6253\uff01\u201d\u4e00\u58f0\u66b4\u559d\uff0c\u5728\u8fd9\u5343\u94a9\u4e00\u53d1\u7684\u7d27\u8981\u5173\u5934\uff0c\u5c55\u767d\u75be\u8eab\u6251\u81f3\uff0c\u201c\u53cc\u649e\u638c\u201d\u731b\u5411 \u7707\u76ee\u9053\u4eba\u524d\u80f8\u63a8\u53bb\u3002 \u3000\u3000\u7707\u76ee\u9053\u4eba\u76ee\u4e0d\u80fd\u89c6\uff0c\u4f46\u51ed\u542c\u89c9\uff0c\u4ea6\u80dc\u8fc7\u6709\u773c\u4e4b\u4eba\u3002\u5c55\u767d\u58f0\u51fa\u62db\u81f3\uff0c\u7707\u76ee\u9053\u4eba\u767d\u679c\u773c\u2014 \u7ffb\uff0c\u8138\u4e0a\u62b9\u8fc7\u72e0\u6bd2\u6740\u673a\uff0c\u663e\u7136\u4ed6\u88ab\u591a\u4eba\u56f4\u653b\uff0c\u5df2\u6fc0\u8d77\u4e86\u6ee1\u8154\u6012\u706b\uff0c\u4ed6\u6068\u4e4b\u5165\u9aa8\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u7684\u53cc\u773c\u4fbf\u662f\u5728\u591a\u4eba\u201c\u56f4\u653b\u201d\u53ca\u201c\u6697\u7b97\u201d\u4e4b\u4e0b\u778e\u6389\u7684\u3002\u4f46\u4ed6\u51ed\u7740\u81ea\u5df2\u575a\u5f3a\u7684\u6bc5\u529b\uff0c \u53cc\u773c\u5168\u778e\u4e4b\u540e\uff0c\u4e0d\u4f46\u672b\u628a\u5168\u8eab\u9ad8\u5f3a\u7684\u6b66\u529f\u4e22\u4e0b\uff0c\u53cd\u800c\u66f4\u52a0\u523b\u82e6\u4fee\u7ec3\uff0c\u4e09\u5341\u4f59\u4e2d\u8352\u5c71\u91ce\u6d1e\u9690\u59d3 \u57cb\u540d\u7684\u82e6\u4fee\uff0c\u7adf\u4f7f\u4ed6\u8fbe\u5230\u201c\u4ee5\u8033\u4ee3\u76ee\u201d\uff0c\u53cd\u6bd4\u6709\u773c\u775b\u4e4b\u4eba\uff0c\u66f4\u52a0\u7075\u654f\u7684\u5730\u6b65\uff0c\u540c\u65f6\uff0c\u4e09\u5341\u4f59 \u4e2d\u7684\u82e6\u7ec3\uff0c\u4ed6\u7684\u6b66\u529f\u53c8\u4e0d\u77e5\u589e\u5f3a\u4e86\u591a\u5c11\u3002\u4ed6\u4e8c\u6b21\u51fa\u5c71\uff0c\u6c5f\u6e56\u4e0a\u82cd\u6d77\u6851\u7530\uff0c\u5df2\u7ecf\u4eba\u4e8b\u5168\u975e\u6545\uff0c \u4e00\u4e8c\u6b8b\u4f59\uff0c\u4e5f\u88ab\u4ed6\u627e\u5230\u6740\u6b7b\uff0c\u540e\u6765\u4ed6\u88ab\u201c\u6458\u661f\u624b\u201d\u7f51\u7f57\u5728\u5e84\u4e0a\uff0c\u6210\u4e3a\u8c79\u7a81\u5c71\u5e84\u5341\u5927\u9ad8\u624b\u4e4b \u4e00\u3002 \u3000\u3000\u4eca\u665a\uff0c\u4ec7\u4eba\u5927\u4e3e\u6765\u72af\uff0c\u7707\u76ee\u9053\u4eba\u4e3a\u62a5\u7b54\u6155\u5bb9\u5e84\u4e3b\u77e5\u9047\u4e4b\u6069\uff0c\u62fc\u547d\u529b\u6218\uff0c\u52a0\u4e4b\u4ed6\u6b66\u529f\u53c8 \u9ad8\uff0c\u4e09\u4e94\u7167\u9762\uff0c\u5373\u6709\u6570\u4eba\u4f24\u5728\u7707\u76ee\u9053\u4eba\u638c\u4e0b\uff0c\u6a0a\u95e8\u4e09\u6770\u7684\u8001\u4e8c\u8ffd\u98ce\u5251\uff0c\u5728\u72af\u5e84\u4e4b\u4eba\u4e2d\u7b97\u662f\u6709 \u6570\u7684\u9ad8\u624b\u4e4b\u4e00\uff0c\u4e00\u89c1\u7707\u76ee\u9053\u5165\u795e\u6001\u5a01\u731b\uff0c\u65e0\u4eba\u80fd\u6321\uff0c\u7acb\u5373\u4e00\u633a\u957f\u5251\uff0c\u201c\u5b64\u98ce\u51fa\u5de2\u201d\uff0c\u653b\u6218\u7707 \u76ee\u9053\u4eba\u3002 \u3000\u3000\u4f46\u51ed\u4ee5\u4e09\u5341\u516d\u8def\u201c\u8ffd\u98ce\u5251\u201d\u95fb\u540d\u6b66\u6797\u7684\u8ffd\u98ce\u5251\uff0c\u4ecd\u4e0d\u662f\u7707\u76ee\u9053\u4eba\u7684\u654c\u624b\uff0c\u5341\u6570\u62db\u4e0b\u6765\uff0c \u5df2\u662f\u9669\u72b6\u767e\u51fa\uff0c\u957f\u5251\u62db\u5f0f\u6bcf\u6bcf\u9010\u51fa\uff0c\u5373\u88ab\u7707\u76ee\u9053\u4eba\u5f3a\u52b2\u638c\u98ce\u903c\u51fa\u5708\u5916\u3002\u5954\u725b\u5c71\u4e8c\u4e49\u6050\u6015\u5c11\u5e84 \u4e3b\u6709\u5931\uff0c\u4e00\u6446\u624b\u4e2d\u957f\u5251\uff0c\u4e0e\u8ffd\u98ce\u5251\u653b\u6218\u7707\u76ee\u9053\u4eba\u3002 \u3000\u3000\u7707\u76ee\u9053\u4eba\u767d\u679c\u773c\u4e71\u7ffb\uff0c\u5389\u5578\u58f0\u4e2d\uff0c\u4e00\u53cc\u94c1\u638c\u8fd0\u8f6c\u5982\u98de\uff0c\u6307\u70b9\u638c\u5288\uff0c\u5f3a\u5927\u7684\u638c\u98ce\u52b2\u6d41\uff0c\u6392 \u7a7a\u9a6d\u4e91\uff0c\u6fc0\u8361\u800c\u51fa\uff0c\u4e00\u53cc\u8d64\u638c\uff0c\u529b\u6218\u4e09\u5927\u5251\u672f\u540d\u5bb6\uff0c\u4ecd\u7136\u7a33\u5360\u4e0a\u98ce\u3001 \u3000\u3000\u8ffd\u98ce\u5251\u6c42\u80dc\u5fc3\u5207\uff0c\u5728\u6e10\u5904\u4e0b\u98ce\u4e4b\u9645\uff0c\u5192\u9669\u65bd\u51fa\u201c\u8ffd\u98ce\u5251\u201d\u6740\u62db\u201c\u75be\u98ce\u65a9\u52b2\u8349\u201d\uff0c\u4ee5\u56fe\u529b \u633d\u52a3\u52bf\uff0c\u5f53\u7707\u76ee\u9053\u4eba\u638c\u6307\u653b\u5411\u91d1\u6c0f\u4e8c\u4e49\uff0c\u540e\u5fc3\u95e8\u6237\u6d1e\u5f00\u65f6\uff0c\u8ffd\u98ce\u5251\u5bb9\u8eab\u5251\u5408\u4e00\uff0c\u731b\u88ad\u7707\u76ee\u9053 \u4eba\u540e\u5fc3\u4e09\u5927\u91cd\u7a74\uff1b\u6ca1\u60f3\u5230\u7707\u76ee\u9053\u4eba\u6b66\u529f\u767b\u5cf0\u9020\u6781\uff0c\u542c\u98ce\u8fa8\u4f4d\uff0c\u8eab\u5f62\u7535\u65cb\uff0c\u4e0d\u4f46\u8eb2\u8fc7\u8ffd\u98ce\u5251\u51cc \u5389\u6740\u62db\uff0c\u4e14\u65cb\u98ce\u822c\u5288\u51fa\u4e00\u638c\uff0c\u8ffd\u98ce\u5251\u7acb\u9677\u5384\u8fd0\uff0c\u5982\u679c\u4e0d\u662f\u6a0a\u7d20\u9002\u65f6\u8d76\u81f3\uff0c\u63a5\u4e0b\u7707\u76ee\u9053\u4eba\u4e00 \u638c\uff0c\u8ffd\u98ce\u5251\u65e9\u5df3\u6a2a\u5c38\u5728\u7707\u76ee\u9053\u4eba\u94c1\u638c\u4e4b\u4e0b\u3002 \u3000\u3000\u53ef\u662f\uff0c\u6a0a\u7d20\u786c\u63a5\u7707\u76ee\u9001\u4eba\u4e00\u638c\uff0c\u5185\u8151\u5df2\u7ecf\u53d7\u4f24\u3002\u7707\u76ee\u9053\u4eba\u6700\u6068\u654c\u4eba\u4ee5\u591a\u4e3a\u80dc\uff0c\u89c1\u654c\u624b\u8d8a \u6253\u8d8a\u591a\uff0c\u987f\u8d77\u6740\u673a\uff0c\u66b4\u6012\u4e4b\u4e0b\uff0c\u5df2\u628a\u4ed6\u9707\u60ca\u6c5f\u6e56\u7684\u201c\u7f61\u7707\u9ed1\u715e\u638c\u201d\u65bd\u51fa\u3002 \u3000\u3000\u7707\u76ee\u9053\u4eba\u5de6\u638c\u5e73\u80f8\u6325\u51fa\uff0c\u5148\u903c\u903c\u8ffd\u98ce\u5251\u4e0e\u5954\u725b\u5c71\u4e8c\u4e49\uff0c\u53f3\u638c\u8fd0\u8db3\u4e86\u201c\u7f61\u7707\u9ed1\u715e\u638c\u201d\uff0c\u731b \u5411\u6a0a\u7d20\u5f53\u5934\u62cd\u4e0b\uff01\u6a0a\u7d20\u5728\u6551\u52a9\u8ffd\u98ce\u5251\uff0c\u65f6\uff0c\u786c\u63a5\u4e86\u7707\u76ee\u9053\u4eba\u4e00\u638c\uff0c\u5185\u8151\u5df2\u53d7\u4f24\u4e0d\u8f7b\uff0c\u600e\u80fd\u518d \u5f53\u5f97\u8d77\u7707\u76ee\uff0c\u9053\u4eba\u201c\u7f61\u7707\u9ed1\u715e\u638c\u201d\u7684\u5168\u529b\u4e00\u51fb\uff1f\u6a0a\u7d22\u52c9\u5f3a\u4e3e\u8d77\u53cc\u638c\u5411\u4e0a\u8fce\u53bb\uff0c\u7acb\u611f\u7707\u76ee\u9053\u4eba \u638c\u529b\u5982\u6cf0\u5c71\u538b\u9876\u76f4\u538b\u4e0b\u6765\uff0c\u773c\u524d\u4e00\u9ed1\uff0c\u80f8\u51856i\u7ffb\u8840\u6d8c\uff0c\u518d\u4e5f\u652f\u6301\u4e0d\u4f4f\uff0c\u5f20\u53e3\u55b7\u51fa\u4e00\u53e3\u9c9c \u8840\uff0c\u4eba\u4e5f\u5f80\u524d\u88c1\u5012\u3002 \u3000\u3000\u7707\u76ee\u9053\u4eba\u638c\u52bf\u6392\u7a7a\u6fc0\u8361\uff0c\u731b\u51fb\u800c\u4e0b\uff0c\u8fd9\u4e00\u638c\u4e0b\u53bb\uff0c\u6a0a\u7d20\u4e0d\u88ab\u51fb\u6210\u4e00\u6ee9\u8089\u9171\u624d\u602a\u5462\u2026\u3002\xb7 \u3000\u3000\u5c55\u767d\u4e0d\u77e5\u5389\u5bb3\uff0c\u731b\u559d\u4e00\u58f0\uff0c\u53cc\u4e2d\u820d\u8eab\u6251\u81f3\uff0c\u5458\u6ca1\u6709\u4ec0\u4e48\u62db\u5f0f\u53ef\u8a00\uff0c\u300a\u5929\u4f5b\u5377\u300b\u7684\u4f5b\u95e8\u771f \u529b\uff0c\u5374\u5df2\u8fd0\u81f3\u5341\u6210\uff01 \u3000\u3000\u201c\u8f70\u9686\uff01\u201d\u4e00\u58f0\u5de8\u96f7\u822c\u66b4\u54cd\uff0c\u5c55\u767d\u53cc\u638c\u4e0e\u7707\u76ee\u9053\u4eba\u7684\u638c\u529b\u51fb\u5b9e\uff0c\u52b2\u6d41\u6fc0\u5c04\uff0c\u56de\u65cb\u751f\u98ce\uff0c \u7802\u77f3\u56db\u98de\uff0c\u5a01\u52bf\u60ca\u4eba\uff01 \u3000\u3000\u201c\u8e6c\u8e6c\u8e6c\u2026\u2026\u201d\u5c55\u767d\u88ab\u7707\u76ee\u8fdb\u4eba\u201c\u9ed1\u715e\u638c\u201d\u529b\uff0c\u9707\u5f97\u731b\u9000\u4e94\u516d\u5927\u6b65\uff0c\u53cc\u773c\u53d1\u9ed1\uff0c\u8033\u5185\u96f7 \u9e23\uff0c\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u597d\u5927\u7684\u638c\u529b\uff01\u201d \u3000\u3000\u5c82\u4e0d\u77e5\uff0c\u8fd9\u662f\u4ed6\u53d7\u52a9\u4e8e\u300a\u5929\u4f5b\u5377\u300b\u7684\u529f\u6548\u82e5\u662f\u5728\u4ed6\u672a\u4e60\u300a\u5929\u4f5b\u5377\u300b\u5185\u529f\u4e4b\u524d\uff0c\u7707\u76ee\u9053\u4eba \u8fd9\u4e00\u638c\uff0c\u7109\u6709\u4ed6\u7684\u547d\u5728\uff01 \u3000\u3000\u7707\u76ee\u9053\u4eba\u8eab\u5f62\u4e71\u6643\uff0c\u80a5\u5927\u9053\u62b1\u6f2b\u7a7a\u98de\u626c\uff0c\u5fc3\u4e2d\u4e5f\u6697\u6697\u5403\u60ca\uff1a\u201c\u8fd9\u4eba\u638c\u529b\u6c89\u539a\uff0c\u9690\u542b\u4f5b\u95e8 \u964d\u9b54\u638c\u529b\uff0c\u52bf\u5a01\u529b\u731b\uff0c\u751f\u5e73\u4ec5\u89c1\uff01\u201d \u3000\u3000\u66b4\u559d\u58f0\u4e2d\uff0c\u8feb\u98ce\u5251\u3001\u5954\u4e2d\u5c71\u4e8c\u4e49\uff0c\u4e09\u652f\u957f\u5251\u5982\u94f6\u8679\u7ecf\u5929\uff0c\u53c8\u9f50\u5411\u7802\u76ee\u9053\u4eba\u653b\u53bb\uff01 \u3000\u3000\u5c55\u767d\u88ab\u7707\u76ee\u9053\u4eba\u638c\u529b\u9707\u5f97\u6709\u70b9\u5934\u660f\u8111\u80c0\uff0c\u7acb\u5730\u7247\u523b\uff0c\u624d\u6062\u590d\u795e\u667a\uff0c\u4e00\u773c\u770b\u5230\u59d4\u987f\u5728\u5730\u7684 \u6a0a\u7d20\uff0c\u9762\u767d\u5982\u7eb8\uff0c\u53e3\u89d2\u6cdb\u8840\uff0c\u53c8\u89c1\u8ffd\u98ce\u5251\u3001\u5954\u725b\u5c71\u4e8c\u4e49\uff0c\u4e09\u652f\u957f\u5251\u5df2\u91cd\u65b0\u56f4\u7740\u7707\u76ee\u9053\u4eba\u6253\u4e86 \u8d77\u6765\uff0c\u5c55\u767d\u65e0\u5fc3\u604b\u6218\uff0c\u5148\u6551\u4eba\u8981\u7d27\uff0c\u4ece\u5730\u4e0a\u62b1\u8d77\u6a0a\u7d20\uff0c\u7eb5\u8d77\u8eab\u5f62\u5411\u9ed1\u6697\u4e4b\u5904\u9a70\u53bb\uff01 \u3000\u3000\u201c\u5c0f\u8f88\uff01\u4f60\u7ed9\u6211\u7559\u4e0b\uff01\u201d \u3000\u3000\u66b4\u559d\u58f0\u4e2d\uff0c\u4e00\u6761\u4eba\u5f71\u6fc0\u5c04\u800c\u81f3\uff0c\u534a\u7a7a\u4e2d\u5288\u51fa\u4e00\u638c\uff0c\u7834\u7a7a\u9510\u5578\uff0c\u58f0\u52bf\u60ca\u4eba\u3002 \u3000\u3000\u5c55\u767d\u624b\u4e2d\u62b1\u7740\u4e00\u4e2a\u4eba\uff0c\u89c1\u6765\u52bf\u51f6\u731b\uff0c\u4e0d\u6562\u786c\u63a5\uff0c\u6025\u6253\u201c\u5343\u65a4\u5760\u201d\u8eab\u5f62\u75be\u6cfb\u843d\u5730\uff0c\u4f46\u6765\u4eba \u8eab\u624b\u77eb\u6377\uff0c\u534a\u7a7a\u4e2d\u4e00\u638c\u5288\u7a7a\uff0c\u8eab\u5f62\u5982\u98d8\u98ce\u75be\u96e8\uff0c\u500f\u5730\u2014\u51fb\uff0c\u5df2\u4e34\u5c55\u767d\u5934\u9876\u4e0a\u7a7a\uff0c\u63a2\u51fa\u7684\u624b\u8155 \u4e00\u6c89\uff0c\u201c\u4e91\u9f99\u73b0\u722a\u201d\uff0c\u53d8\u638c\u4e3a\u6293\uff0c\u4e94\u6307\u5636\u5636\u52b2\u5578\uff0c\u731b\u5411\u5c55\u767d\u5934\u9876\u6293\u4e0b\u3002 \u3000\u3000\u8fd9\u8eab\u6cd5\u62db\u5f0f\uff0c\u5feb\u903e\u7535\u95ea\uff0c\u800c\u4e14\u6765\u4eba\u8fd9\u4e34\u7a7a\u4e00\u6293\uff0c\u5a01\u529b\u5e7f\u7f69\u4e8c\u4e08\u65b9\u5706\u4e3a\u9762\uff0c\u5c55\u6b32\u8eb2\u4e0d\u80fd\uff0c \u6b32\u67b6\u65e0\u529b\uff0c\u4f55\u51b5\u4ed6\u8fd8\u4e0d\u613f\u4f7f\u53cc\u624b\u62b1\u4f4f\u7684\u6a0a\u7d20\u53d7\u5230\u4f24\u5bb3\u3002\u7a0d\u4e00\u8fdf\u7591\uff0c\u7acb\u611f\u5934\u9876\u4e94\u80a1\u75be\u98ce\uff0c\u523a\u80a4 \u751f\u75db\uff0c\u8fce\u5934\u538b\u4e0b\u3002 \u3000\u3000\u5c55\u767d\u5fc3\u4e2d\u4e00\u60ca\uff0c\u6697\u9053\uff1a\u201c\u4e0d\u597d\u2026\u2026\u201d \u3000\u3000\u5ffd\u89c1\u53c8\u4e00\u6761\u4eba\u5f71\uff0c\u6765\u52bf\u66f4\u75be\uff0c\u4ece\u659c\u523a\u91cc\u5c04\u6765\uff0c\u4e34\u7a7a\u5212\u4e86\u4e00\u4e2a\u5706\u5f27\uff0c\u534a\u7a7a\u4e2d\u4e0e\u6251\u5411\u5c55\u767d\u7684 \u4eba\u5f71\u76f8\u4ea4\uff0c\u201c\u556a\u201d\u7684\u4e00\u58f0\u8f7b\u54cd\uff0c\u4e24\u6761\u4eba\u5f71\u8fc5\u5982\u9668\u661f\u75be\u843d\uff0c\u5206\u5411\u4e24\u65c1\u4e8c\u4e08\u5f00\u5916\u843d\u4e0b\u5730\u3002 \u3000\u3000\u201c\u96f7\u9707\u8fdc\uff01\u201d\u843d\u4e0b\u5730\u6765\u7684\u4e00\u6761\u4eba\u5f71\uff0c\u7adf\u662f\u9762\u8272\u9752\u767d\u72ec\u81c2\u79c3\u9876\u8001\u8005\uff0c\u53e6\u5916\u4e00\u6761\u4eba\u5f71\uff0c\u6b63\u662f \u4e71\u53d1\u2014\u84ec\u7684\u96f7\u5927\u53d4\u3002\u53ea\u89c1\u79c3\u9876\u8001\u8005\uff0c\u9762\u8272\u6c14\u5f97\u60e8\u767d\uff0c\u6012\u76ee\u5706\u7741\uff0c\u77aa\u89c6\u7740\u96f7\u5927\u53d4\u8bf4\u9053\uff1a\u201c\u96be\u9053 \u4f60\u5403\u91cc\u6252\u5916\u4e0d\u6210\uff1f\u201d \u3000\u3000\u201c\u53f8\u9a6c\u656c\uff01\u201d\u96f7\u5927\u53d4\u4e71\u53d1\u88ab\u591c\u98ce\u5439\u5f97\u7eb7\u98de\u5e45\u626c\uff0c\u6c89\u58f0\u5428\u9053\uff1a\u201c\u4f11\u788d\u80e1\u8a00\uff01\u60f3\u6211\u96f7\u67d0\u662f\u4f55 \u7b49\u6837\u4eba\uff1f\u201d \u3000\u3000\u5c55\u767d\u4e00\u542c\uff0c\u8fd9\u72ec\u81c2\u79c3\u9876\u8001\u8005\uff0c\u7adf\u662f\u4e8c\u5341\u4e2d\u524d\u6b66\u6797\u4e2d\u95fb\u540d\u4e27\u80c6\u7684\u201c\u8ffd\u9b42\u94c3\u201d\u53f8\u9a6c\u656c\uff0c\u5fc3\u4e2d \u4e0d\u7531\u2014\u60ca\u3002 \u3000\u3000\u539f\u6765\u201c\u8ffd\u9b42\u94c3\u201d\u53f8\u9a6c\u656c\uff0c\u4e43\u662f\u897f\u5317\u9053\u4e0a\u6709\u540d\u7684\u9b54\u5934\uff0c\u4e0e\u201c\u72ec\u811a\u98de\u9b54\u674e\u4e3e\u201d\u53f7\u79f0\u201c\u585e\u5916\u53cc \u6b8b\u201d\uff0c\u6b66\u529f\u9ad8\u4e0d\u53ef\u6d4b\uff0c\u884c\u4e8b\u4e56\u50fb\uff0c\u624b\u6bb5\u6b8b\u9177\uff0c\u5982\u88ab\u7f20\u4e0a\u8a93\u6b7b\u4e0d\u4f11\uff0c\u4e0d\u628a\u5bf9\u65b9\u6740\u622e\u6b86\u5c3d\uff0c\u6c38\u4e0d \u7b97\u5b8c\u3002\u6c5f\u6e56\u4e0a\u4e0d\u8bba\u9ed1\u767d\u4e24\u9053\uff0c\u5bf9\u8fd9\u4e24\u4e2a\u9b54\u5934\uff0c\u83ab\u4e0d\u907f\u82e5\u86c7\u874e\uff0c\u65e0\u4eba\u6562\u60f9\u3002\u6ca1\u60f3\u5230\u4e5f\u88ab\u6155\u5bb9\u5e84 \u4e3b\u6536\u7f57\u5728\u5e84\u4e0a\u3002 \u3000\u3000\u201c\u90a3\u4e48\u201d\u8feb\u9b42\u94c3\u53f8\u9a6c\u656c\u5bf9\u96f7\u5927\u53d4\u51b7\u7136\u8bf4\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u963b\u6321\u8001\u592b\u64d2\u62ff\u5e84\u4e0a\u9003\u72af\uff1f\u201d \u3000\u3000\u201c\u53f8\u9a6c\u5144\u53ef\u80fd\u62ff\u9519\u4e86\u3002\u201d\u96f7\u5927\u53d9\u8bf4\uff1a\u201c\u8fd9\u5c11\u5e74\u4e43\u662f\u96f7\u67d0\u4e00\u4e2a\u665a\u8f88\uff0c\u54ea\u662f\u4ec0\u4e48\u9003\u72af\uff1f\u201d \u3000\u3000\u201c\u563f\u563f\u563f\u2026\u2026\u201d\u8ffd\u9b42\u94c3\u53f8\u9a6c\u656c\u4e00\u58f0\u51b7\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8001\u592b\u4e0e\u5e84\u4e3b\u4eb2\u81ea\u628a\u4ed6\u64d2\u6765\uff0c\u5173\u5728\u4e2d \u5185\uff0c\u8fd8\u4f1a\u6709\u5047\u5417\uff1f\u96f7\u9707\u8fdc\u4f60\u660e\u662f\u5403\u91cc\u6252\u5916\u2026\u2026\u201d \u3000\u3000\u201c\u4f4f\u53e3\uff01\u201d\u96f7\u5927\u53d4\u4e71\u53d1\u98de\u626c\uff0c\u53cc\u76ee\u5982\u7535\uff0c\u5389\u58f0\u5428\u9053\uff1a\u201c\u5c31\u7b97\u6211\u96f7\u9707\u8fdc\u5403\u91cc\u6252\u5916\uff0c\u4e5f\u8f6e\u4e0d \u5230\u4f60\u8fd9\u79c3\u9b3c\u6765\u8bf4\u5634\uff01\u201d \u3000\u3000\u201c\u96f7\u75af\u5b50\uff01\u201d\u8ffd\u9b42\u94c3\u53f8\u9a6c\u656c\u6700\u5fcc\u8bb3\u8bf4\u5230\u4ed6\u7684\u79c3\u9876\uff0c\u89c1\u96f7\u5927\u53d4\u9a82\u4ed6\u79c3\u9b3c\uff0c\u4e0d\u7531\u76ee\u95ea\u51f6\u5149\uff0c \u9634\u72e0\u8bf4\u9053\uff1a\u201c\u522b\u4eba\u6015\u4f60\uff0c\u6211\u53f8\u9a6c\u656c\u53ef\u4e0d\u6015\u4f60\uff01\u522b\u4ee5\u4e3a\u4f60\u7684\u2018\u4e03\u5341\u4e8c\u8def\u5929\u4f5b\u638c\u2019\u5c31\u5929\u4e0b\u65e0\u654c\uff01 \u53eb\u8001\u592b\u770b\u6765\u4e0d\u503c\u4e00\u987e\uff01\u201d \u3000\u3000\u201c\u4e0d\u4fe1\u4f60\u53ef\u4ee5\u4e0a\u6765\u8bd5\u8bd5\uff01\u201d\u96f7\u5927\u53d4\u53cc\u638c\u4e00\u7acb\uff0c\u50b2\u7136\u8bf4\u9053\u3002 \u3000\u3000\u201c\u597d\uff01\u8001\u592b\u5c31\u9886\u6559\u9886\u6559\u4f60\u96f7\u75af\u5b50\u7684\u5929\u4f5b\u7edd\u5b66\uff01\u201d \u3000\u3000\u8feb\u9b42\u94c3\u559d\u7f62\uff0c\u72ec\u81c2\u4e00\u63a2\uff0c\u8eab\u5f62\u7535\u5c04\u800c\u8d77\uff0c\u5411\u96f7\u5927\u53d4\u8fce\u80f8\u5370\u6765\u4e00\u638c\uff01 \u3000\u3000\u638c\u98ce\u7834\u7a7a\u9510\u5578\uff0c\u52b2\u6d41\u5c71\u6d8c\uff0c\u8ffd\u9b42\u94c3\u638c\u529b\u60ca\u4eba\u3002 \u3000\u3000\u96f7\u5927\u53d4\u5634\u89d2\u542b\u7b11\uff0c\u7a33\u7acb\u5982\u5c71\uff0c\u5bf9\u7740\u8ffd\u9b42\u94c3\u5a01\u52bf\u5f3a\u5927\u7684\u4e00\u638c\u89c6\u52b3\u65e0\u7779\u3002 \u3000\u3000\u8ffd\u9b42\u94c3\u5f3a\u5927\u638c\u98ce\uff0c\u604d\u5982\u98de\u661f\u9668\u77f3\uff0c\u773c\u770b\u5df2\u51fb\u5728\u96f7\u5927\u53d4\u524d\u80f8\u4e4b\u4e0a\uff0c\u53c8\u7a81\u7136\u53d8\u638c\u4e3a\u6293\uff0c\u4e94\u6307 \u7b95\u5f20\uff0c\u7eab\u8d77\u6f2b\u5929\u722a\u5f71\uff0c\u7b3c\u7f69\u96f7\u5927\u53d4\u80f8\u8179\u4e4b\u95f4\u81f4\u547d\u8981\u5bb3\uff0c\u731b\u6293\u800c\u4e0b\u3002 \u3000\u3000\u5c55\u767d\u5728\u4e00\u8fb9\u770b\u5f97\u5fc3\u5934\u72c2\u9707\uff0c\u50cf\u79c3\u9876\u8001\u8005\u4e00\u53ea\u72ec\u81c2\uff0c\u80fd\u65bd\u51fa\u8fd9\u6837\u5a01\u3001\u731b\u3001\u8fc5\u3001\u6377\u3001\u53d8\u5316\u83ab \u6d4b\u7684\u795e\u5947\u62db\u5f0f\uff0c\u5b9e\u5728\u4ee4\u4eba\u89e6\u76ee\u60ca\u5fc3\u3002 \u3000\u3000\u96f7\u5927\u53d4\u5bf9\u8feb\u9b42\u94c3\u7684\u5947\u5965\u62db\u5f0f\uff0c\u5374\u4ece\u5bb9\u4e0d\u8feb\uff0c\u4f9d\u7136\u7a33\u7acb\u5f53\u573a\uff0c\u76f4\u5230\u6f2b\u5929\u722a\u5f71\u5df2\u4e34\u8fd1\u80f8\u524d\uff0c \u96f7\u5927\u53d4\u624d\u8eab\u5f62\u7535\u65cb\u800c\u51fa\uff0c\u5c55\u767d\u51e0\u4e4e\u6ca1\u770b\u6e05\u695a\u96f7\u5927\u53d4\u662f\u7528\u4ec0\u4e48\u8eab\u6cd5\uff0c\u53ea\u89c9\u773c\u524d\u4e00\u82b1\uff0c\u96f7\u5927\u53d4\u5df2 \u8131\u51fa\u722a\u5f71\u4e4b\u5916\uff0c\u540c\u65f6\uff0c\u89c1\u96f7\u5927\u53d4\u53f3\u638c\u5982\u5200\uff0c\u659c\u780d\u79c3\u9876\u8001\u8005\u540e\u8111\u201c\u98ce\u5e9c\u201d\u91cd\u7a74\uff0c\u5de6\u817f\u731b\u8e22\u79c3\u9876 \u8001\u8005\u201c\u5c3e\u690e\u201d\u2019\u4e00\u62db\u4e8c\u5f0f\uff0c\u5947\u5965\u7edd\u4f26\u3002 \u3000\u3000\u96f7\u5927\u53d4\u89c1\u62db\u6253\u62db\uff0c\u638c\u817f\u9f50\u65bd\uff0c\u5feb\u5982\u7535\u5149\u77f3\u706b\uff0c\u653b\u654c\u4e4b\u5fc5\u6551\uff0c\u5c55\u767d\u521d\u7779\u9ad8\u624b\u8fc7\u62db\uff0c\u5fc3\u75d2\u96be \u71ac\uff0c\u8111\u9645\u4e2d\u7075\u5149\u95ea\u70c1\u2026\u2026 \u3000\u3000\u79c3\u9876\u8001\u8005\u8eab\u5f62\u4e00\u7ffb\u4e00\u5f13\uff0c\u659c\u5c04\u51fa\u4e8c\u6587\u5f00\u5916\uff0c\u624d\u8eb2\u8fc7\u4e86\u96f7\u5927\u53d4\u7684\u4e00\u638c\u4e00\u817f\uff0c\u4f46\u5df2\u6c14\u5f97\u53cc\u76ee \u5982\u6b32\u55b7\u706b\u3002 \u3000\u3000\u79c3\u9876\u8001\u8005\uff0c\u66f4\u4e0d\u7b54\u8bdd\uff0c\u4e8c\u6b21\u731b\u6251\u800c\u4e0a\u3002 \u3000\u3000\u4e8c\u4eba\u5019\u5206\u53c8\u5408\uff0c\u8eab\u6cd5\u5747\u662f\u5feb\u5f97\u51fa\u5947\uff0c\u62db\u5f0f\u66f4\u662f\u7cbe\u5965\u4e07\u5206\u3002\u53ea\u770b\u5f97\u5c55\u767d\u773c\u82b1\u64a9\u4e71\uff0c\u5fc3\u5934\u72c2 \u559c\uff0c\u628a\u6551\u4eba\u9003\u8dd1\u7684\u4e8b\u90fd\u5fd7\u4e8e\u4e5d\u9704\u4e91\u5916\u53bb\u4e86\uff0c\u773c\u775b\u7741\u5f97\u6eda\u5706\uff0c\u7d27\u7d27\u6ce8\u89c6\u7740\u4e8c\u4eba\u7684\u4e00\u62db\u4e00\u5f0f\u2026\u2026 \u3000\u3000\u6709\u9053\u662f\uff1a\u201c\u80fd\u89c1\u9ad8\u624b\u8fc7\u62db\uff0c\u80dc\u8fc7\u4ece\u5e08\u4e09\u5e74\u3002\u201d\u5c55\u767d\u5fc3\u6000\u7236\u4ec7\uff0c\u9189\u5fc3\u4e60\u6b66\uff0c\u5374\u4ece\u672a\u9047\u5230\u660e \u5e08\uff0c\u4f46\u6240\u5b66\u7684\u4e0d\u8fc7\u662f\u4e00\uff0c\u4e3a\u4e86\u5f3a\u58ee\u7b4b\u9aa8\u5012\u53ef\u4ee5\uff0c\u8981\u60f3\u4e0e\u6b66\u6797\u9ad8\u624b\u4e00\u4e89\u957f\u77ed\uff0c\u5b9e\u5728\u5dee\u5f97\u592a\u8fdc\u3002 \u3000\u3000\u662f\u96f7\u5927\u53d4\u82e6\u5fc3\u5b64\u8be3\uff0c\u628a\u4ed6\u5173\u95ed\u591c\u77f3\u6d1e\uff0c\u6388\u4ee5\u5929\u4e0b\u7b2c\u4e00\u5947\u4e66\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\uff0c\u4f7f\u5176\u5728 \u6697\u4e2d\u6478\u7d22\uff0c\u5f97\u7aa5\u6b63\u5b97\u5fc3\u6cd5\u95e8\u5f84\uff0c\u7ec3\u4f1a\u4e86\u5410\u7eb3\u7ec3\u6c14\u4e4b\u672f\u3002\u53c8\u7ecf\u8fc7\u201c\u94f6\u7bab\u593a\u9b42\u201d\u97f3\u9b54\u7bab\u6cd5\u7684\u8bd5 \u70bc\uff0c\u53ca\u51cc\u98ce\u516c\u5b50\u7684\u65e0\u60c5\u6253\u51fb\uff0c\u9707\u5f00\u4e86\u5468\u8eab\u5947\u7ecf\u516b\u8109\uff0c\u4f46\u5bf9\u4e8e\u771f\u6b63\u9ad8\u624b\u8fc7\u62db\u7684\u5947\u5965\u53d8\u5316\uff0c\u968f\u673a \u5e94\u53d8\uff0c\u4ecd\u7136\u662f\u4e00\u7a8d\u4e0d\u901a\u3002 \u3000\u3000\u5c31\u662f\u96f7\u5927\u53d4\u4e0e\u79c3\u9876\u72ec\u81c2\u8001\u8005\u201c\u8ffd\u9b42\u94c3\u201d\u53f8\u9a6c\u656c\uff0c\u5747\u662f\u5f53\u4eca\u6b66\u6797\u4e00\u7b49\u4e00\u7684\u9ad8\u624b\uff0c\u540d\u5217\u8c79\u7a81 \u5c71\u5e84\u5341\u4e2a\u5927\u9ad8\u624b\u4e4b\u5217\u3002\u4e8c\u4eba\u5185\u529f\u4fee\u4e3a\u5747\u81f3\u7089\u706b\u7eaf\u9752\u4e4b\u5883\uff0c\u8eab\u8d1f\u7edd\u4e16\u6b66\u5b66\u9ad8\u4e0d\u53ef\u6d4b\uff0c\u7d20\u5e38\u867d\u88ab \u6155\u5bb9\u5e84\u4e3b\u4e00\u6837\u7684\u91cd\u89c6\uff0c\u4e00\u6837\u7684\u6069\u793c\u6709\u52a0\uff0c\u4f46\u9aa8\u5b50\u91cc\u8c01\u4e5f\u4e0d\u670d\u8c01\u3002\u4e0d\u8fc7\u5747\u662f\u6b66\u6797\u4e2d\u8eab\u4efd\u5730\u4f4d\u6781 \u5c0a\uff0c\u65e0\u7f18\u65e0\u7531\u4e0d\u4f1a\u5f53\u9762\u8f83\u91cf\u7f62\u4e86\u3002 \u3000\u3000\u56e0\u6b64\uff0c\u8fd9\u4e8c\u4eba\u4e00\u52a8\u4e0a\u624b\uff0c\u867d\u7136\u540c\u662f\u8c79\u7a81\u5c71\u5e84\u65b9\u9762\u7684\u4eba\uff0c\u5374\u6bd4\u4e0e\u654c\u4eba\u52a8\u624b\u8fc7\u62db\uff0c\u66f4\u52a0\u72e0 \u6bd2\uff0c\u66f4\u52a0\u731b\u70c8\uff01 \u3000\u3000\u597d\u5728\uff0c\u591c\u72af\u6155\u5bb9\u5e84\u7684\u4ec7\u5bb6\uff0c\u4eba\u6570\u4e0d\u5c11\uff0c\u6155\u5bb9\u5e84\u4e3b\u7684\u6b66\u6797\u9ad8\u624b\u53ca\u5e84\u4e3b\u672c\u4eba\uff0c\u5747\u5728\u8fce\u6218\u6765\u72af \u4e4b\u654c\uff0c\u53c8\u52a0\u4e0a\u6765\u654c\u7eb5\u706b\u4fa5\u5e84\uff0c\u6574\u4e2a\u8c79\u7a81\u5c71\u5e84\uff0c\u6740\u58f0\u9707\u8033\uff0c\u706b\u5149\u70db\u5929\uff0c\u6709\u767e\u6570\u5341\u4eba\u6349\u5bf9\u513f\u80a0 \u6740\uff0c\u96f7\u5927\u53d4\u4e0e\u8ffd\u9b42\u94c3\u53f8\u9a6c\u656c\uff0c\u81ea\u5df1\u4eba\u4e0e\u81ea\u5df1\u4eba\u640f\u6597\u76f8\u6253\uff0c\u7adf\u672a\u88ab\u4eba\u53d1\u73b0\u3002 \u3000\u3000\u96f7\u5927\u53d4\u4e0e\u8feb\u9b42\u94c3\u53f8\u9a6c\u656c\uff0c\u5feb\u653b\u5feb\u6253\uff0c\u604d\u7136\u95f4\u6253\u4e86\u4e8c\u5341\u4f59\u4e2a\u7167\u9762\u3002 \u3000\u3000\u4e09\u4eba\u8d8a\u6253\u8d8a\u5feb\uff0c\u8d8a\u6253\u8d8a\u72e0\uff0c\u62db\u5f0f\u66f4\u662f\u4e91\u8be1\u6ce2\u8c32\uff0c\u5947\u5965\u767e\u51fa\uff0c\u8eab\u6cd5\u8fc5\u903e\u98d8\u98ce\u95ea\u7535\uff0c\u7ffb\u7ffb\u6eda \u6eda\uff0c\u638c\u98ce\u52b2\u6d41\u6fc0\u8361\u56db\u5c04\uff0c\u6ce2\u53ca\u4e24\u6587\u65b9\u5706\u8303\u56f4\uff0c\u76f4\u6253\u5f97\u7802\u77f3\u6a2a\u98de\uff0c\u5ead\u4e2d\u82b1\u6728\u679d\u53f6\u56db\u6e85\uff0c\u58f0\u52bf\u597d \u4e0d\u60ca\u4eba\u3002 \u3000\u3000\u5c55\u81ea\u770b\u5f97\u7740\u4e86\u8ff7\uff0c\u53cc\u624b\u62b1\u7740\u8d1f\u4f24\u660f\u8ff7\u7684\u6a0a\u7d20\uff0c\u7ad9\u5728\u4e00\u65c1\uff0c\u53cc\u773c\u4e00\u77ac\u4e0d\u77ac\u5730\u671b\u4f4f\u4e8c\u4eba\u52a8\u624b \u7684\u4e00\u62db\u4e00\u5f0f\uff0c\u5bf9\u773c\u524d\u706b\u5149\u70db\u5929\u3001\u6740\u58f0\u76c8\u8033\u7684\u6df7\u6218\u573a\u9762\uff0c\u7ade\u604d\u5982\u672a\u89c1\uff0c\u66f4\u5fd8\u4e86\u81ea\u5df1\u8eab\u5904\u4f55\u5730\u3002 \u3000\u3000\u201c\u4f60\u597d\u5927\u80c6\uff01\u8fd8\u4e0d\u5feb\u8d70\uff0c\u7b49\u4ea4\u8fd9\u91cc\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e00\u58f0\u4f4e\u53f1\uff0c\u54cd\u5728\u5c55\u767d\u8033\u4fa7\uff0c\u53c8\u89c9\u5f97\u8863\u8896\u88ab\u4eba\u4e00\u62c9\uff0c\u5c55\u81ea\u84e6\u7136\u60ca\u89c9\uff0c\u56de\u5934\u89c1\u4e00\u6761\u5a07\u5c0f\u4eba \u5f71\uff0c\u5df2\u98de\u8eab\u63a0\u5411\u6697\u5904\u3002 \u3000\u3000\u5c55\u767d\u8fd9\u624d\u60f3\u8d77\u81ea\u5df1\u8eab\u5904\u9f99\u6f6d\u864e\u7a74\uff0c\u800c\u4e14\u6000\u4e2d\u8fd8\u62b1\u4f4f\u4e00\u4e2a\u8d1f\u4f24\u660f\u8ff7\u7684\u65b0\u4ea4\uff0c\u89c9\u5f97\u8b66\u544a\u81ea\u5df1 \u7684\u4eba\u4f3c\u53cb\u975e\u654c\uff0c\u5fc3\u60f3\u5148\u79bb\u5f00\u9669\u5730\uff0c\u4e3a\u6a0a\u7d20\u533b\u597d\u4f24\u52bf\u8981\u7d27\uff0c\u6709\u4ec0\u4e48\u4e8b\u4ee5\u540e\u518d\u8bf4\uff0c\u5f53\u5373\u7eb5\u8d77\u8eab \u5f62\uff0c\u968f\u7740\u90a3\u5a07\u5c0f\u9ed1\u5f71\u8dd1\u53bb\u3002 \u3000\u3000\u5728\u524d\u5954\u8dd1\u9ed1\u5f71\uff0c\u5bf9\u5e84\u4e0a\u8def\u5f84\u4f3c\u4e4e\u751a\u4e3a\u719f\u6089\uff0c\u7a7f\u5c4b\u8fc7\u5eca\uff0c\u817e\u623f\u8d8a\u810a\uff0c\u4e13\u627e\u9ed1\u6697\u65e0\u5165\u4e4b\u5904\u8dd1 \u53bb\u3002\u800c\u4e14\u5176\u8eab\u6cd5\u4e5f\u751a\u5feb\uff0c\u4e0d\u65f6\u4f4f\u4e0b\u8eab\u5f62\u7b49\u5019\u5c55\u767d\uff0c\u5230\u8f6c\u5f2f\u62d0\u89d2\u4e4b\u5904\uff0c\u66f4\u662f\u6697\u6253\u62db\u547c\uff0c\u4e00\u76f4\u5f15 \u5c55\u767d\u5230\u5e84\u5916\uff0c\u8dd1\u4e0a\u4e00\u4e2a\u5bc6\u6797\u4e1b\u751f\u7684\u5c0f\u5c71\u4e0a\uff0c\u624d\u505c\u4e0b\u811a\u6765\u3002 \u3000\u3000\u5c55\u767d\u76ee\u529b\u5df2\u5230\u591c\u80fd\u89c6\u7269\u5730\u6b65\uff0c\u4e00\u8def\u8dd1\u6765\uff0c\u65e9\u5df2\u770b\u51fa\u5728\u524d\u5f15\u8def\u7684\u9ed1\u5f71\uff0c\u4f3c\u662f\u5929\u771f\u672a\u51ff\u7684\u5a49 \u513f\uff0c\u5982\u4eca\uff0c\u90a3\u9ed1\u5f71\u8dd1\u5230\u5c0f\u5c71\u9876\u4e0a\uff0c\u7acb\u5b9a\u811a\u6b65\uff0c\u5c55\u767d\u5b9a\u775b\u770b\u53bb\uff0c\u591c\u98ce\u4e60\u4e60\u6b27\u5f97\u5979\u8863\u88ab\u98d8\u4e3e\uff0c\u79c0 \u53d1\u5fbd\u626c\uff0c\u6ee1\u5929\u7e41\u661f\u4e4b\u4e0b\uff0c\u6084\u7136\u7acb\u5728\u5c0f\u5c71\u5761\u4e0a\u7684\u4e0d\u662f\u5a49\u513f\u8fd8\u4f1a\u662f\u8c01\u5462\uff01 \u3000\u3000\u201c\u4f60\u6000\u62b1\u7684\u662f\u8c01\uff1f\u201d\u5a49\u513f\u7ad9\u5b9a\u8eab\u5f62\uff0c\u7b49\u5c55\u767d\u8dd1\u8fd1\uff0c\u7528\u624b\u63a0\u4e86\u4e00\u4e0b\u88ab\u591c\u98ce\u5439\u4e71\u4e86\u7684\u9b13\u53d1\uff0c \u5411\u5c55\u767d\u95ee\u9053\u3002 \u3000\u3000\u201c\u6211\u65b0\u4ea4\u7684\u4e00\u4f4d\u670b\u53cb\uff0c\u540d\u53eb\u6a0a\u7d20\u3002\u201d\u5c55\u767d\u6beb\u4e0d\u9690\u7792\u5730\u8bf4\uff1a\u201c\u59d1\u5a18\u2026\u2026\u201d \u3000\u3000\u201c\u59d3\u6a0a\u7684\uff1f\u201d\u5a49\u513f\u6253\u65ad\u5c55\u767d\u7684\u8bdd\u3002\u201c\u90a3\u4e00\u5b9a\u662f\u6211\u5bb6\u7684\u5927\u4ec7\u4eba\u4e86\uff0c\u8ba9\u6211\u770b\u662f\u6dee\uff1f\u201d \u3000\u3000\u5fae\u98ce\u6251\u9762\uff0c\u5a49\u513f\u5634\u91cc\u8bf4\u7740\u8bdd\uff0c\u4eba\u5df2\u68c9\u7d6e\u968f\u98ce\u822c\u5730\u98d8\u5230\u5c55\u767d\u9762\u524d\u3002\u540c\u65f6\uff0c\u4f38\u624b\u5411\u6a0a\u7d20\u9762\u95e8 \u6293\u53bb\u3002 \u3000\u3000\u5c55\u767d\u4f30\u4e0d\u5230\u5a49\u513f\u8eab\u5b50\u7ade\u4e5f\u5982\u6b64\u5feb\u6377\uff0c\u6beb\u4e0d\u4e9a\u4e8e\u4e43\u59ca\u3002 \u3000\u3000\u5982\u4eca\uff0c\u8fd9\u59b9\u59b9\u51fa\u4e4e\u53c8\u662f\u8fd9\u4e48\u5feb\uff0c\u800c\u4e14\u542c\u5979\u8bf4\u6a0a\u7d20\u662f\u5979\u5bb6\u5927\u4ec7\u4eba\uff0c\u5c55\u767d\u8bef\u4ee5\u4e3a\u5a49\u513f\u60f3\u65bd\u6740 \u624b\uff0c\u6765\u4f24\u5bb3\u660f\u8ff7\u4e0d\u9192\u7684\u6a0a\u7d20\u3002\u5c55\u767d\u4e0e\u6a0a\u7d22\u867d\u662f\u65b0\u53cb\uff0c\u5374\u4e00\u89c1\u6388\u7f18\uff0c\u5728\u4e2d\u4e2d\u8c08\u5f97\u751a\u662f\u6295\u673a\uff0c\u4ed6 \u6015\u5a49\u513f\u4f24\u4e86\u6a0a\u7d20\uff0c\u540c\u65f6\uff0c\u4e5f\u662f\u4e0b\u610f\u8bc6\u7684\u53cd\u5e94\uff0c\u89c1\u5a49\u513f\u63a2\u624b\u6293\u6765\uff0c\u8eab\u5f62\u4e00\u6d4b\uff0c\u5411\u4e00\u65c1\u95ea\u53bb\u3002 \u3000\u3000\u201c\u5636\uff01\u201d\u5c55\u767d\u95ea\u5f97\u5feb\uff0c\u5a49\u513f\u7684\u624b\u6765\u5f97\u4e5f\u5feb\uff0c\u5c55\u767d\u4e00\u95ea\uff0c\u5a49\u513f\u4e00\u6293\uff0c\u6b63\u597d\u628a\u6a0a\u7d20\u5934\u4e0a\u9752\u5dfe \u62b9\u989d\u7684\u6b66\u58eb\u5dfe\u6293\u843d\u4e0b\u6765\u3002 \u3000\u3000\u201c\u54a6\uff01\u201d \u3000\u3000\u201c\u5440\uff01\u201d \u3000\u3000\u5a49\u513f\u3001\u5c55\u767d\u540c\u58f0\u60ca\u547c\uff0c\u539f\u6765\u6a0a\u7d20\u5934\u5dfe\u8131\u843d\uff0c\u9732\u51fa\u6ee1\u5934\u9752\u4e1d\uff0c\u7ade\u662f\u4e00\u7edd\u8272\u5c11\u5973\uff0c\u5206\u660e\u662f\u5973 \u626e\u7537\u88c5\u3002 \u3000\u3000\u201c\u54fc\uff01\u201d\u5a49\u513f\u4e00\u6115\uff0c\u77ac\u5373\u5c0f\u5634\u4e00\u6487\uff0c\u7c89\u8138\u73b0\u51fa\u5992\u610f\uff0c\u51b7\u54fc\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u539f\u6765\u662f\u4e2a\u5973 \u7684\uff01\u6211\u8bf4\u4f60\u4e3a\u4ec0\u4e48\u820d\u6b7b\u5fd8\u751f\uff0c\u4e5f\u8981\u6551\u5979\u54e9\uff01\u201d \u3000\u3000\u201c\u6211\u2026\u2026\u6211\u4e0d\u77e5\u9053\uff01\u201d\u5c55\u767d\u9519\u6115\u5730\u8bf4\uff0c\u201c\u771f\u2026\u2026\u4e0d\u77e5\u4ed6\u662f\u5973\u7684\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u7528\u88c5\u50bb\u4e86\uff01\u201d\u5992\u5ac9\uff0c\u5f62\u5973\u4eba\u5929\u6027\uff0c\u7231\u4e4b\u6108\u6df1\uff0c\u5992\u4e4b\u6108\u751a\u3002\u5a49\u513f\u867d\u662f\u7eaf\u6d01\u5929\u771f\uff0c\u5fc3\u65e0 \u70b9\u5c18\uff0c\u4f46\u5979\u65e2\u4e3a\u5973\u4eba\uff0c\u4e0e\u5973\u4eba\u751f\u4e4b\u4ff1\u6765\u7684\u5992\u5ac9\u5fc3\u8fd8\u662f\u6709\u7684\u3002\u75c5\u69bb\u4e0a\u4e0e\u5c55\u767d\u4e00\u5915\u76f8\u5bf9\uff0c\u5979\u5fc3\u4e4b \u6df1\u5904\u5df2\u6697\u6697\u7231\u4e0a\u5c55\u767d\uff0c\u5982\u4eca\u5c55\u767d\u820d\u6b7b\u5fd7\u751f\u6240\u6551\u63f4\u7684\u7adf\u662f\u4e00\u7edd\u8272\u5c11\u5973\uff0c\u800c\u4e14\u62b1\u5728\u6000\u5185\u7231\u62a4\u5907 \u81f3\uff0c\u5a49\u513f\u5fc3\u91cc\u7acb\u523b\u8d77\u4e86\u9178\u6e9c\u6e9c\u7684\u611f\u89c9\uff0c\u4e0d\u9ad8\u5174\u5730\u8bf4\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u73b0\u5728\u4f60\u8be5\u77e5\u9053\u4e86\u5427\uff01\u201d \u3000\u3000\u8a00\u5916\u4e4b\u610f\uff0c\u662f\u8981\u5c55\u767d\u628a\u6a0a\u7d20\u653e\u4e0b\u3002 \u3000\u3000\u504f\u504f\u5c55\u767d\u662f\u4e2a\u4e0d\u61c2\u5973\u5b69\u513f\u5bb6\u5fc3\u4e8b\u7684\u61a8\u76f4\u9752\u5e74\uff0c\u4f4e\u5934\u671b\u4e86\u671b\u660f\u8ff7\u5728\u6000\u4e2d\u7684\u6a0a\u7d20\uff0c\u53c8\u62ac\u5934\u4e00 \u773c\u671b\u89c1\uff0c\u6b64\u5c71\u7adf\u662f\u96f7\u5927\u53d4\u7b2c\u4e00\u6b21\u5e26\u4ed6\u6765\u5230\u7684\u5730\u65b9\uff0c\u81ea\u5df1\u66fe\u4f4f\u8fc7\u7684\u77f3\u6d1e\u5c31\u5728\u524d\u9762\uff0c\u5634\u4e2d\u968f\u5373\u5583 \u5583\u8bf4\u9053\uff1a\u201c\u5979\u8d1f\u4f24\u5f88\u91cd\uff0c\u6211\u8981\u7ed9\u5979\u7597\u4f24\u3002\u201d \u3000\u3000\u8bf4\u7f62\uff0c\u4e5f\u4e0d\u7ba1\u5a49\u513f\u7ad9\u5728\u4e00\u8fb9\u6485\u7740\u5634\u4e0d\u9ad8\u5174\uff0c\u7ade\u62b1\u7740\u6a0a\u7d20\u5927\u8e0f\u6b65\u5730\u5411\u77f3\u6d1e\u4e2d\u8d70\u53bb\u3002 \u3000\u3000\u201c\u4f60\u2026\u2026\u201d\u5c55\u767d\u6b64\u4e3e\u5927\u51fa\u5a49\u513f\u610f\u5916\u3002\u89c1\u5c55\u767d\u62b1\u7740\u6000\u4e2d\u5c11\u5973\u76f4\u5411\u77f3\u6d1e\u4e2d\u8d70\u53bb\uff0c\u8138\u8272\u5927\u53d8\uff0c \u62ac\u8d77\u624b\u6765\u60f3\u963b\u62e6\uff0c\u4e0d\u77e5\u53c8\u60f3\u8d77\u4ec0\u4e48\uff0c\u53e3\u4e2d\u53ea\u8bf4\u4e86\u4e00\u4e2a\u201c\u4f60\u201d\u5b87\uff0c\u7acb\u523b\u564e\u4f4f\uff0c\u6014\u4e86\u4e00\u6014\uff0c\u54ac\u4e86 \u54ac\u4e0b\u5634\u5507\uff0c\u7adf\u4e00\u8dfa\u811a\u6068\u6068\u5730\u79bb\u53bb\u3002 \u3000\u3000\u5a49\u513f\u8eab\u5f62\u5982\u4e00\u9635\u8f7b\u70df\uff0c\u6d88\u5931\u5728\u5c71\u8170\u5bc6\u6797\u91cc\uff1b\u5c55\u767d\u4e00\u5fc3\u5173\u6ce8\u6a0a\u7d20\u4f24\u52bf\uff0c\u5bf9\u5a49\u513f\u7684\u542b\u6012\u79bb\u53bb \u5e76\u672a\u7559\u610f\u3002 \u3000\u3000\u5c55\u767d\u628a\u6a0a\u7d20\u62b1\u8fdb\u6d1e\u5185\uff0c\u5c06\u5979\u653e\u5728\u81ea\u5df2\u66fe\u7761\u8fc7\u7684\u5e8a\u4e0a\uff0c\u4f38\u624b\u4e00\u63a2\u5979\u7684\u9f3b\u606f\uff0c\u5df2\u662f\u5fbd\u5f31\u5f97\u53ef \u601c\uff0c\u79bb\u6b7b\u4e0d\u8fdc\u4e86\u3002 \u3000\u3000\u5c55\u767d\u987e\u4e0d\u5f97\u7537\u5973\u4e4b\u5acc\uff0c\u5148\u6551\u4eba\u8981\u7d27\u3002\u4e8b\u5b9e\u4e0a\uff0c\u6b64\u9645\u4ed6\u4e5f\u6ca1\u60f3\u5230\u7537\u5973\u4e4b\u5206\u3002\u6a0a\u7d20\u5df2\u88ab\u79c3\u9876 \u8001\u8005\u638c\u529b\u9707\u5f97\u4e94\u810f\u79bb\u4f4d\uff0c\u5c55\u767d\u5148\u4e3a\u5176\u4e94\u810f\u5f52\u8fd8\u672c\u6765\u4f4d\u7f6e\uff0c\u7136\u540e\u53cc\u638c\u6309\u5728\u6a0a\u7d20\u547d\u95e8\u7a74\u4e0a\uff0c\u4e3a\u5176 \u63a8\u5bab\u6d3b\u7a74\u3002 \u3000\u3000\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\u679c\u7136\u65f7\u4e16\u7edd\u5b66\uff0c\u5c55\u767d\u77ed\u77ed\u65f6\u95f4\u7684\u63e3\u6469\uff0c\u6309\u7167\u4e66\u58eb\u79d8\u8bc0\u65bd\u4e3a\uff0c\u4e0d\u6d88\u7247 \u523b\u529f\u592b\uff0c\u6a0a\u7d20\u7adf\u547b\u541f\u51fa\u58f0\uff0c\u82cf\u9192\u8fc7\u7c73\u3002 \u3000\u3000\u4f46\u5c55\u767d\u521d\u6b21\u4e3a\u4eba\u8fd0\u529f\u7597\u4f24\uff0c\u635f\u8017\u771f\u529b\u751a\u5de8\uff0c\u4e3a\u6a0a\u7d20\u7597\u597d\u4f24\u52bf\u4e4b\u540e\uff0c\u5df2\u662f\u75b2\u4e4f\u52b3\u7d2f\u4e0d\u582a\u3002 \u3000\u3000\u6a0a\u7d20\u957f\u5401\u4e00\u58f0\uff0c\u7741\u773c\u4e00\u7247\u660f\u9ed1\uff0c\u4efb\u4ec0\u4e48\u4e5f\u770b\u4e0d\u5230\uff0c\u7ade\u4e0d\u77e5\u81ea\u5df1\u8eab\u5904\u4f55\u5730\u3002 \u3000\u3000\u604d\u60da\u4e2d\uff0c\u5979\u8bb0\u5f97\u81ea\u5df1\u88ab\u7707\u76ee\u9053\u4eba\u638c\u529b\u9707\u4f24\uff0c\u706b\u5149\u4e2d\u4e0e\u81ea\u5df1\u540c\u56f0\u7262\u4e2d\u7684\u5c11\u5e74\u6551\u4e86\u5979\uff0c\u5f80\u540e \u4fbf\u660f\u8ff7\u4e0d\u9192\u4e86\u2026\u2026 \u3000\u3000\u90a3\u4e48\uff0c\u81ea\u5df1\u73b0\u5728\u662f\u7f6e\u8eab\u4f55\u5730\uff1f\u5979\u8f7b\u52a8\u4e86\u4e00\u4e0b\u8eab\u4f53\uff0c\u53c8\u53d1\u89c9\u80f8\u8179\u95f4\u75bc\u75db\u5df2\u5931\uff0c\u4f3c\u4e4e\u4f24\u52bf\u4e5f \u597d\u4e86\uff0c\u53c8\u662f\u8c01\u4e3a\u81ea\u5df1\u7597\u597d\u4f24\u52bf\u5462\uff1f\u2026\u2026\u8bb8\u591a\u7591\u95ee\uff0c\u5728\u4f38\u624b\u4e0d\u89c1\u4e94\u6307\u7684\u9ed1\u6d1e\u4e2d\uff0c\u4f7f\u6a0a\u7d20\u60f3\u4e0d \u901a\uff0c\u51e0\u7591\u8eab\u5728\u68a6\u4e2d\u3002 \u3000\u3000\u7b49\u5230\u5979\u795e\u667a\u5b8c\u5168\u6e05\u9192\uff0c\u5df2\u8bc1\u660e\u81ea\u5df1\u4e0d\u662f\u5728\u68a6\u4e2d\uff0c\u5979\u53c8\u4e0b\u610f\u8bc6\u5730\u629a\u4e86\u4e00\u4e0b\u81ea\u5df1\u989d\u524d\u7684\u6563 \u53d1\uff0c\u624d\u731b\u7136\u5fc6\u8d77\uff0c\u81ea\u5df1\u6613\u9497\u800c\u5f03\uff0c\u7792\u7740\u7236\u4eb2\u8ddf\u7740\u4e09\u4f4d\u54e5\u54e5\uff0c\u7387\u9886\u5e84\u4e0a\u95e8\u5ba2\uff0c\u6765\u5230\u201c\u8c79\u7a81\u5c71 \u5e84\u201d\uff0c\u62a5\u590d\u6740\u6bcd\u4e4b\u4ec7\u3002\u81ea\u5df1\u4f24\u52bf\u5df2\u597d\uff0c\u8fd8\u4e0d\u8d76\u901f\u79bb\u5f00\uff0c\u7b49\u5230\u5929\u8272\u5927\u4eae\uff0c\u53ef\u5c31\u4e0d\u597d\u529e\u4e86\u3002 \u3000\u3000\u601d\u53ca\u6b64\u5904\uff0c\u6025\u5f52\u4e4b\u5fc3\u6cb9\u7136\u800c\u751f\uff0c\u8f6c\u4fa7\u56db\u987e\uff0c\u867d\u6ca1\u6709\u8ba9\u5979\u53d1\u73b0\u8eab\u65c1\u7684\u5c55\u767d\uff0c\u5374\u8ba9\u5979\u770b\u5230\u6d1e \u53e3\u4e9b\u5fae\u5149\u4eae\u3002 \u3000\u3000\u539f\u6765\u5c55\u767d\u6b63\u5728\u6309\u7167\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\u6240\u8f7d\uff0c\u65e0\u4e0a\u6b63\u5b97\u5fc3\u6cd5\uff0c\u201c\u8fd4\u672c\u5f52\u5143\u201d\u3002\u800c\u8fd9\u201c\u8fd4 \u672c\u5f52\u5143\u201d\u5927\u6cd5\uff0c\u53c8\u662f\u8e69\u7740\u4e00\u53e3\u771f\u6c14\uff0c\u5728\u5185\u8179\u6d88\u8f6c\u201c\u7d2b\u5bab\u201d\u800c\u201c\u4e39\u7530\u201d\uff0c\u76f4\u8fbe\u5341\u4e09\u91cd\u697c\uff0c\u8fde\u547c \u5438\u4e4b\u58f0\u7686\u65e0\uff0c\u662f\u4ee5\u8eab\u5728\u54ab\u5c3a\uff0c\u53ea\u56e0\u6d1e\u4e2d\u9ed1\u6697\uff0c\u6a0a\u7d20\u5e76\u672a\u53d1\u89c9\u3002 \u3000\u3000\u7b49\u5230\u6a0a\u7d20\u53d1\u73b0\u6d1e\u53e3\u5fae\u5149\uff0c\u722c\u8d77\u8eab\u6765\uff0c\u6478\u7d22\u7740\u5411\u6d1e\u53e3\u8d70\u53bb\u65f6\uff0c\u5c55\u81ea\u5df2\u8c03\u606f\u590d\u5143\u3002\u8bf4\u9053\uff1a \u201c\u6a0a\u5144\u2026\u2026\u4e0d\uff01\u6a0a\u59d1\u5a18\uff0c\u4f60\u8981\u5230\u54ea\u91cc\u53bb\uff1f\u201d \u3000\u3000\u660f\u9ed1\u4e4b\u4e2d\uff0c\u6a0a\u7d20\u7a81\u95fb\u4eba\u8a00\uff0c\u5413\u4e86\u4e00\u8df3\u3002\u4f46\u5fae\u5fae\u4e00\u6014\u4e4b\u540e\uff0c\u53c8\u731b\u7136\u671d\u6d1e\u5916\u8e7f\u53bb\u3002 \u3000\u3000\u4e00\u4e2a\u95fa\u9601\u5c11\u5973\uff0c\u7eb5\u7136\u8eab\u8d1f\u7edd\u4e16\u6b66\u529f\uff0c\u660f\u9ed1\u6697\u5ba4\u4e4b\u4e2d\uff0c\u7a81\u95fb\u4eba\u8a00\uff0c\u4e5f\u751f\u51fa\u754f\u60e7\u9003\u907f\u4e4b\u5fc3\u3002 \u3000\u3000\u5c55\u767d\u4e0d\u77e5\u5c31\u91cc\uff0c\u803d\u5fc3\u6a0a\u7d20\u4f24\u52bf\u521d\u6108\uff0c\u4e0d\u80fd\u5984\u52a8\u771f\u6c14\uff0c\u53c8\u6015\u5979\u8dd1\u81f3\u6d1e\u5916\uff0c\u9047\u5230\u654c\u624b\u65e0\u6cd5\u5e94 \u6218\uff0c\u5f53\u5373\u968f\u7740\u6a0a\u7d20\u8eab\u5f71\uff0c\u8ffd\u51fa\u6d1e\u5916\u3002 \u3000\u3000\u6708\u6b8b\u661f\u7a00\uff0c\u5c71\u98ce\u4e60\u4e60\uff0c\u4e1c\u65b9\u5929\u9645\u5df2\u73b0\u51fa\u9c7c\u809a\u767d\uff0c\u5929\u8272\u63a5\u8fd1\u9ece\u660e\u4e86\u3002 \u3000\u3000\u5c55\u767d\u8e7f\u51fa\u6d1e\u5916\uff0c\u89c1\u6a0a\u7d20\u5e76\u672a\u8fdc\u8d70\uff0c\u7ad9\u5728\u5c71\u5761\u4e0a\uff0c\u79c0\u53d1\u968f\u98ce\u5fae\u626c\uff0c\u7384\u8272\u52b2\u88c5\u8863\u89d2\u88ab\u98ce\u6b27\u5f97 \u6298\u6298\u4f5c\u54cd\uff0c\u5973\u8eab\u800c\u7537\u88c5\uff0c\u66f4\u663e\u51fa\u4e00\u79cd\u629a\u5a9a\u82f1\u4fca\u4e4b\u6001\u3002 \u3000\u3000\u201c\u54e6\uff01\u201d\u6a0a\u7d20\u8f7b\u5531\u4e00\u58f0\uff0c\u8138\u5bd2\u4f3c\u6c34\uff0c\u7edd\u65e0\u534a\u70b9\u5973\u4eba\u5ff8\u59ae\u4e4b\u6001\uff0c\u51b7\u7136\u8bf4\u9053\uff1a\u201c\u539f\u6765\u662f\u5c55\u5144 \u76f8\u6551\uff01\u6211\u8fd9\u91cc\u8c22\u8fc7\u4e86\u3002\u201d\u8bf4\u7f62\u62b1\u62f3\u4e00\u63d6\u3002 \u3000\u3000\u5c55\u767d\u60f3\u4e0d\u5230\u5979\u4f1a\u7a81\u7136\u53d8\u5f97\u5982\u6b64\u51b7\u6f20\uff0c\u89c1\u5730\u9053\u8c22\u5fd9\u4e5f\u8fd8\u4e86\u4e00\u672d\uff0c\u8bf4\u9053\uff1a\u201c\u6a0a\u2026\u2026\u6a0a\u59d1\u5a18\u4f24 \u52bf\u521d\u6108\uff0c\u4e0d\u5b9c\u8d70\u52a8\u2026\u2026\u201d \u3000\u3000\u6a0a\u7d20\u79c0\u8138\u4e00\u626c\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4e2a\u4e0d\u52b3\u6302\u6000\u3002\u201d\u8bf4\u7f62\u8f6c\u8eab\u5c31\u8d70\u3002 \u3000\u3000\u201c\u59d1\u5a18\u6162\u8d70\uff01\u201d\u5c55\u767d\u8d85\u524d\u4e24\u6b65\u5524\u9053\u3002 \u3000\u3000\u6a0a\u7d20\u731b\u7136\u56de\u8eab\uff0c\u76ee\u84c4\u6012\u5149\uff0c\u8bf4\u9053\uff1a\u201c\u662f\u5426\u5c55\u5144\u56e0\u4e3a\u6709\u6069\u4e8e\u6211\uff0c\u800c\u6709\u4ed6\u6c42\uff1f\u201d \u3000\u3000\u8fd9\u4e24\u53e5\u8bdd\u8bf4\u5f97\u51b7\u5cfb\u5df2\u6781\u3002\u5c55\u767d\u95fb\u8a00\u4e00\u6115\uff01\u5fc3\u4e2d\u6697\u60f3\uff1a\u201c\u6211\u53c8\u6ca1\u5f97\u7f6a\u4f60\uff0c\u600e\u4e48\u4e00\u65f6\u4e4b\u95f4\u6001 \u5ea6\u53d8\u5f97\u5982\u6b64\u51b7\u6f20\uff1f\u2026\u2026\u201d \u3000\u3000\u5c31\u5728\u5c55\u767d\u5fae\u7136\u4e00\u6115\u4e4b\u9645\uff0c\u6797\u836b\u6df1\u5904\u55d6\u7136\u8dc3\u51fa\u4e09\u6761\u4eba\u5f71\uff0e \u3000\u3000\u4e09\u6761\u4eba\u5f71\u8eab\u6cd5\u7279\u5feb\uff0c\u624d\u4e00\u6643\u8eab\u5df2\u8dc3\u81f3\u5c55\u767d\u9762\u524d\uff0c\u6e05\u4e00\u8272\u7384\u8272\u52b2\u88c5\uff0c\u4e09\u67c4\u6676\u83b9\u8000\u76ee\u957f\u5251\uff0c \u5251\u5c16\u659c\u6307\u5c55\u767d\u80f8\u8179\u8981\u7a74\uff0c\u6765\u52bf\u7535\u75be\uff0c\u4f7f\u5c55\u767d\u65e0\u6687\u7ec6\u60f3\uff0c\u5df2\u9677\u5728\u4e09\u4eba\u5251\u5f0f\u5305\u56f4\u4e4b\u4e0b\u3002 \u3000\u3000\u5c55\u767d\u4e00\u6253\u91cf\u4e09\u4eba\uff0c\u5e74\u9f84\u5747\u5728\u4e8c\u5341\u5c81\u81f3\u4e09\u5341\u5c81\u4e4b\u95f4\uff0c\u4e00\u4e2a\u4e2a\u82f1\u633a\u4fca\u62d4\uff0c\u4e09\u53cc\u661f\u76ee\u5982\u516d\u9897\u660e \u4eae\u7684\u6668\u661f\uff0c\u7cbe\u8292\u5982\u7535\u76ef\u89c6\u7740\u81ea\u5df1\uff0c\u6bcf\u4e2a\u4eba\u7709\u76ee\u4e4b\u95f4\u90fd\u6cdb\u8d77\u6d53\u91cd\u6740\u673a\u3002 \u3000\u3000\u5c55\u767d\u770b\u8fd9\u4e09\u4eba\u751a\u662f\u773c\u751f\uff0c\u53ea\u6709\u7ad9\u5728\u5f53\u4e2d\u7684\u9752\u5e74\u6b66\u58eb\uff0c\u50cf\u662f\u6628\u591c\u5728\u706b\u5149\u4e2d\u529b\u6218\u7707\u76ee\u9053\u4eba\u4e4b \u4eba\u3002\u7531\u6b64\u5c55\u767d\u6599\u5230\u8fd9\u4e09\u4eba\u51b3\u4e0d\u662f\u201c\u8c79\u7a81\u5c71\u5e84\u201d\u4e0a\u7684\u4eba\u3002 \u3000\u3000\u201c\u5927\u54e5\uff01\u201d\u6a0a\u7d20\u5374\u5728\u4e00\u65c1\u53eb\u9053\uff1a\u201c\u4ed6\u4e0d\u662f\u8c79\u7a81\u5c71\u5e84\u4e0a\u7684\u72d7\u817f \u3000\u3000\u4e09\u4eba\u4e4b\u4e2d\u8f83\u5e74\u957f\u7684\u4e00\u4e2a\uff0c\u76b1\u4e86\u76b1\u7709\uff0c\u5411\u5c55\u767d\u4f4e\u53f1\u9053\uff1a\u201c\u5c0f\u5b50\uff01\u5e08\u627f\u4f55\u4eba\uff1f\u4e3a\u4f55\u52ab\u63a0\u6211\u7684 \u59b9\u59b9\uff1f\u8d81\u65e9\u5b9e\u8bdd\u5b9e\u8bf4\uff0c\u514d\u4f5c\u51a4\u6b7b\u4e4b\u9b3c\uff01\u201d \u3000\u3000\u5c55\u767d\u4e00\u542c\uff0c\u6c14\u4e0d\u6253\u4e00\u5904\u6765\uff0c\u81ea\u5df1\u62fc\u7740\u635f\u8017\u771f\u5143\uff0c\u6551\u4eba\u4e00\u547d\uff0c\u53cd\u53d7\u5230\u5982\u6b64\u6050\u5413\u3002\u60f3\u4ed6\u672c\u662f \u6027\u683c\u9ad8\u50b2\u4e4b\u4eba\uff0c\u600e\u4f1a\u5728\u5a01\u903c\u4e0b\u4f4e\u5934\uff01\u4f55\u51b5\uff0c\u5728\u8fd9\u79cd\u60c5\u51b5\u4e4b\u4e0b\uff0c\u66f4\u6fc0\u8d77\u4e86\u4ed6\u7684\u50b2\u6027\uff0c\u5bf9\u903c\u8fd1\u80f8 \u524d\u7684\u4e09\u67c4\u5229\u5251\uff0c\u89c6\u82e5\u65e0\u7779\uff0c\u53ea\u5411\u4e09\u4e2a\u7384\u88c5\u4fa0\u58eb\u629b\u4e86\u4e00\u4e2a\u5351\u89c6\u7684\u773c\u8272\uff0c\u6487\u4e86\u6487\u5634\u3001\u51b7\u54fc\u4e86\u4e00 \u58f0\u3001\u4e00\u8a00\u4e0d\u53d1\u3002 \u3000\u3000\u4e09\u4eba\u4e4b\u4e2d\u6700\u5e74\u5e7c\u7684\u4e00\u4e2a\uff0c\u6027\u5b50\u4e5f\u6700\u6025\uff0c\u89c1\u5230\u5c55\u767d\u7684\u50b2\u7136\u4e4b\u8272\uff0c\u65e9\u5df2\u4e0d\u8010\uff0c\u7acb\u65f6\u53eb\u9053\uff1a \u201c\u5927\u54e5\uff0c\u770b\u8fd9\u5c0f\u5b50\u6240\u7a7f\u8863\u670d\uff0c\u5206\u660e\u4ec7\u4eba\u722a\u7259\u3002\u4f55\u5fc5\u8ddf\u4ed6\u7f57\u55e6\uff01\u65e9\u89e3\u51b3\u4ed6\u65e9\u8d76\u8def\uff01\u201d \u3000\u3000\u5634\u4e2d\u8bf4\u7740\uff0c\u624b\u8155\u5411\u524d\u4e00\u9012\uff0c\u5251\u5c16\u98a4\u5904\uff0c\u731b\u523a\u5c55\u767d\u524d\u80f8\u3002 \u3000\u3000\u539f\u6765\u5c55\u767d\u8eab\u4e0a\u7a7f\u7684\uff0c\u8fd8\u662f\u7ee3\u6709\u201c\u8c79\u7a81\u5c71\u5e84\u201d\u6807\u8bc6\u7684\u9ed1\u7f0e\u62ab\u98ce\u3002 \u3000\u3000\u5251\u5c16\u950b\u5229\u65e0\u6bd4\uff0c\u8ddd\u79bb\u53c8\u8fd1\uff0c\u5c11\u5e74\u4fa0\u58eb\u624b\u8155\u5fae\u4e00\u5410\u52b2\uff0c\u5df2\u523a\u7834\u5c55\u767d\u524d\u895f\u3002 \u3000\u3000\u5c55\u767d\u524d\u80f8\u5fae\u611f\u4e00\u75db\uff0c\u4e5f\u662f\u4e2a\u731b\u52b2\uff0c\u968f\u624b\u6325\u51fa\u4e00\u638c\uff0c\u5411\u5251\u8eab\u538b\u4e0b\u3002 \u3000\u3000\u201c\u5636\uff01\u201d\u4e00\u58f0\u5fae\u54cd\uff0c\u5c55\u767d\u6240\u7a7f\u4e00\u4ef6\u7ee3\u8c79\u62ab\u98ce\uff0c\u524d\u895f\u88ab\u5251\u5c16\u5212\u7834\u4e00\u9053\u5c3a\u8bb8\u957f\u7684\u53e3\u5b50\uff0c\u80a4\u7834 \u8840\u6d41\uff0c\u6240\u5e78\u672a\u4f24\u5230\u808b\u9aa8\uff0c\u90a3\u51e0\u4e4e\u8d2f\u80f8\u800c\u5165\u7684\u957f\u5251\uff0c\u5374\u5df2\u88ab\u5c55\u767d\u638c\u529b\u9707\u5f00\u6a2a\u79fb\u4e09\u5c3a\u3002 \u3000\u3000\u5c11\u4e2d\u4fa0\u58eb\u4f30\u4e0d\u5230\u5e74\u7eaa\u8f7b\u8f7b\u7684\u5c55\u767d\uff0c\u7ade\u6709\u5982\u8bb8\u6df1\u539a\u638c\u529b\uff0c\u800c\u4e14\u786c\u6562\u4ee5\u7a7a\u624b\u6321\u5251\uff0c\u4f7f\u4ed6\u864e\u53e3 \u4e00\u70ed\uff0c\u957f\u5251\u51e0\u4e4e\u8131\u624b\u3002 \u3000\u3000\u201c\u5c0f\u5b50\uff01\u6709\u70b9\u95e8\u9053\uff0c\u518d\u63a5\u8fd9\u4e2a\uff01\u201d \u3000\u3000\u5c11\u5e74\u4fa0\u58eb\u66b4\u559d\u58f0\u4e2d\uff0c\u957f\u5251\u53cd\u81c2\u6a2a\u524a\uff0c\u4e00\u5f0f\u201c\u62e8\u8349\u5bfb\u86c7\u201d\uff0c\u5251\u5c16\u541e\u5410\u4e00\u7247\u91d1\u8292\uff0c\u5411\u5c55\u81ea\u62e6 \u8170\u65a9\u81f3\u3002 \u3000\u3000\u5c55\u767d\u4e00\u638c\u9707\u5f00\u524d\u80f8\u4e4b\u5228\uff0c\u53cc\u624b\u4e00\u5206\u201c\u62e8\u4e91\u73b0\u65e5\u201d\uff0c\u731b\u5288\u5de6\u53f3\u5251\u624b\u524d\u80f8\u8981\u5bb3\u3002 \u3000\u3000\u4ee5\u957f\u5251\u62b5\u4f4f\u5c55\u767d\u5de6\u53f3\u4e24\u808b\u7684\u4e24\u5404\u631f\u58eb\uff0c\u4e0d\u9632\u5c55\u767d\u8d64\u638c\u7a7a\u62f3\uff0c\u5728\u4e09\u67c4\u5229\u5251\u62b5\u5236\u4e4b\u4e0b\u5c1a\u6562\u53cd \u6297\uff0c\u5c55\u767d\u84e6\u7136\u51fa\u624b\uff0c\u4e8c\u4eba\u7acb\u611f\u524d\u80f8\u5982\u53d7\u91cd\u538b\uff0c\u8eab\u4e0d\u7531\u5df1\u5438\u80f8\u540e\u9000\u5c06\u53ca\u4e00\u6587\u3002 \u3000\u3000\u8fd9\u65f6\u5019\u5c11\u5e74\u4fa0\u58eb\u7b2c\u4e8c\u6b21\u8fd0\u5251\u5411\u5c55\u81ea\u62e6\u8170\u65a9\u6765\uff0c\u5c55\u767d\u9aa4\u7136\u53d1\u96be\u903c\u9000\u4e09\u5251\u624b\uff0c\u89c1\u5c11\u5e74\u4fa0\u58eb\u51b7 \u68ee\u68ee\u7684\u5251\u950b\u7b2c\u4e8c\u6b21\u903c\u80f8\u800c\u81f3\uff0c\u5c55\u767d\u53cc\u624b\u5de6\u63a8\u53f3\u6321\uff0c\u4ee5\u5f3a\u52b2\u638c\u98ce\u903c\u5f00\u5c11\u5e74\u4fa0\u58eb\u957f\u5251\uff0c\u201c\u8fdb\u6b65\u64a9 \u9634\u201d\uff0c\u98de\u8d77\u4e00\u817f\uff0c\u731b\u8e22\u5c11\u5e74\u4fa0\u58eb\u4e0b\u8179\u201c\u4e39\u7530\u201d\u3002 \u3000\u3000\u5c11\u5e74\u4fa0\u58eb\u5fae\u4e00\u4ef0\u8eab\uff0c\u8eb2\u8fc7\u5c55\u767d\u4e00\u817f\uff1b\u5c55\u767d\u5374\u662f\u4ee5\u8fdb\u4e3a\u9000\uff0c\u4e00\u817f\u903c\u9000\u5c11\u5e74\u4fa0\u58eb\uff0c\u98d8\u7136\u75be\u9000 \u4e00\u4e08\u5f00\u5916\uff0c\u540c\u65f6\u5634\u91cc\u559d\u9053\uff1a\u201c\u4f60\u7b49\u4e0d\u77e5\u597d\u6b79\uff0c\u5984\u60f3\u4ee5\u591a\u4e3a\u80dc\uff0c\u5728\u4e0b\u5931\u966a\u4e86\uff01\u201d\u9707\u8eab\u6b32\u8d70\u2026\u2026 \u3000\u3000\u4e09\u540d\u7384\u88c5\u4fa0\u58eb\uff0c\u4e43\u9547\u6c5f\u4e00\u5e26\u6709\u540d\u5251\u672f\u540d\u5bb6\uff0c\u6c5f\u6e56\u9053\u4e0a\u63d0\u8d77\u201c\u9547\u6c5f\u6a0a\u6c0f\u4e09\u6770\u201d\uff0c\u5927\u6c5f\u4e24\u5cb8 \u65e0\u4eba\u4e0d\u77e5\uff0c\u65e0\u4eba\u4e0d\u6653\uff0c\u4eca\u591c\u7387\u9886\u5e84\u4e0a\u95e8\u5ba2\u524d\u5bfb\u4ec7\uff0c\u867d\u7136\u653e\u706b\u70e7\u5e84\uff0c\u628a\u6b66\u6797\u4e2d\u89c6\u4e3a\u7981\u5730\u7684\u201c\u8c79 \u7a81\u5c71\u5e84\u201d\u95f9\u4e86\u4e2a\u5730\u8986\u5929\u7ffb\uff0c\u4f46\u5e76\u6ca1\u6709\u8ba8\u4e86\u597d\u5904\uff0c\u5f1f\u5144\u4e09\u4eba\u7387\u987f\u4e8c\u5341\u4e09\u540d\u4e00\u6d41\u9ad8\u624b\uff0c\u88ab\u8c79\u7a81\u5c71 \u5e84\u65b9\u9762\u7684\u4eba\u56f4\u653b\u622a\u6740\uff0c\u6b7b\u4f24\u60e8\u91cd\u3002\u773c\u770b\u5929\u5c06\u7834\u6653\uff0c\u6a0a\u6c0f\u4e09\u6770\u660e\u77e5\u604b\u6218\u65e0\u76ca\uff0c\u8fd9\u624d\u547c\u54e8\u9000\u53bb\u3002 \u3000\u3000\u4f46\u6a0a\u6c0f\u4e09\u6770\u7684\u8001\u4e8c\u201c\u8ffd\u98ce\u5251\u201d\u6a0a\u6770\uff0c\u82e6\u6218\u7707\u76ee\u9053\u4eba\u65f6\uff0c\u89c1\u59b9\u59b9\u88ab\u4e00\u5c11\u5e74\u62d0\u8d70\uff0c\u4fbf\u544a\u8bf7\u5927 \u54e5\u201c\u6233\u60c5\u5251\u201d\u6a0a\u4fca\uff0c\u53ca\u4e09\u5f1f\u201c\u6469\u4e91\u5251\u201d\u6a0a\u82f1\uff0c\u6765\u5230\u5e84\u540e\u5c0f\u5c71\u4e0a\u5bfb\u627e\uff0e \u3000\u3000\u6070\u5de7\u9047\u5230\u4ed6\u59b9\u59b9\u4e0e\u5c55\u767d\u5728\u4e00\u8d77\uff0c\u5f1f\u5144\u4e09\u4eba\u5747\u4ee5\u8eab\u6cd5\u5feb\u901f\u3001\u5251\u672f\u9ad8\uff0c\u4e00\u9732\u9762\u4e09\u5251\u4ea4\u8f89\u628a\u5c55 \u767d\u5236\u4e8e\u5251\u4e0b\uff0c\u4f46\u4e07\u4e5f\u60f3\u4e0d\u5230\u5c55\u767d\u80fd\u4ece\u4e09\u4eba\u5251\u4e0b\u8131\u8eab\u800c\u51fa\u3002 \u3000\u3000\u6a0a\u6c0f\u4e00\u65cf\u4e0e\u6155\u5bb9\u4e00\u59d3\uff0c\u539f\u4e3a\u901a\u5bb6\u4e4b\u597d\u3002\u53ea\u56e0\u4e00\u6b21\u6843\u8272\u7ea0\u7eb7\uff0c\u53cd\u76ee\u5931\u548c\u3002\u53c8\u56e0\u4e3a\u4eba\u8a00\u53ef \u754f\uff0c\u6a0a\u6c0f\u592b\u4eba\u7ade\u542b\u7f9e\u81ea\u5c3d\uff0c\u81f4\u4f7f\u4e24\u5bb6\u7ed3\u4e0b\u5927\u4ec7\uff0c\u867d\u7136\u6a0a\u5927\u7237\u6709\u5fc3\u5316\u89e3\uff0c\u60f3\u538b\u4e0b\u8fd9\u4ef6\u4e8b\uff0c\u4e0d\u518d \u63d0\u8d77\u4e3a\u6b7b\u53bb\u7684\u7231\u59bb\u590d\u4ec7\uff0c\u4f46\u4ed6\u7684\u513f\u5973\u5374\u6c38\u4e0d\u5fd8\u6000\u8fd9\u8fb1\u6bcd\u6740\u6bcd\u4e4b\u6068\uff0c\u65f6\u601d\u62a5\u590d\u3002 \u3000\u3000\u6700\u8fd1\u6a0a\u5927\u7237\u56e0\u4e8b\u79bb\u5bb6\u5357\u4e0b\uff0c\u6a0a\u6c0f\u4e09\u6770\u5144\u5f1f\u59ca\u59b9\u56db\u4eba\u8d81\u7236\u4eb2\u4e0d\u5728\u5bb6\uff0c\u7387\u9886\u95e8\u4e0b\u9ad8\u624b\u524d\u6765\u5bfb \u4ec7\uff0c\u6ca1\u60f3\u5230\u5be1\u4e0d\u654c\u4f17\uff0c\u4ec7\u672a\u62a5\u6210\uff0c\u53cd\u800c\u95f9\u4e86\u4e2a\u94e9\u7fbd\u800c\u5f52\u3002 \u3000\u3000\u5982\u4eca\uff0c\u5c55\u767d\u8eab\u7a7f\u201c\u8c79\u7a81\u5c71\u5ea7\u201d\u6807\u8bc6\u5916\u8863\uff0c\u5f1f\u5144\u4e09\u4eba\u6740\u673a\u9661\u8d77\uff0c\u66f4\u6ca1\u60f3\u5230\u4e00\u4e2a\u540d\u4e0d\u89c1\u7ecf\u4f20 \u7684\u5c11\u5e74\uff0c\u7ade\u80fd\u4ece\u4ed6\u5f1f\u5144\u4e09\u4eba\u5251\u4e0b\u9003\u8131\u3002 \u3000\u3000\u5c55\u767d\u98de\u8eab\u6b32\u8d70\u4e4b\u9645\uff0c\u5f1f\u5144\u4e09\u4eba\u7eb7\u7eb7\u66b4\u559d\uff0c\u4e00\u9f50\u817e\u8d77\u8eab\u5f62\uff0c\u534a\u7a7a\u51fa\u62db\uff0c\u4e09\u652f\u957f\u5251\u5982\u4e09\u6761\u8d2f \u65e5\u767d\u8679\uff0c\u4ece\u4e09\u4e2a\u4e0d\u540c\u7684\u65b9\u5411\u671d\u7740\u5c55\u767d\u8fce\u5934\u7f69\u4e0b\u3002 \u3000\u3000\u5c55\u767d\u521d\u7aa5\u201c\u5929\u4f5b\u7edd\u5b66\u201d\u95e8\u5f84\uff0c\u867d\u7136\u5468\u8eab\u5947\u7ecf\u516b\u8109\u5df2\u88ab\u6253\u901a\uff0c\u7a0d\u5047\u65f6\u65e5\uff0c\u6216\u9047\u9ad8\u624b\u6307\u70b9\uff0c \u4e0d\u96be\u8fbe\u5230\u8d85\u51e1\u4eba\u5723\u4e4b\u5883\u3002\u4f46\u7a76\u7adf\u662f\u6b20\u7f3a\u7ecf\u9a8c\uff0c\u7eb5\u6709\u4e00\u8eab\u9ad8\u5f3a\u5185\u529f\uff0c\u7ade\u4e0d\u77e5\u7075\u6d3b\u8fd0\u7528\u3002\u8eab\u5f62\u521a \u521a\u817e\u8d77\uff0c\u84e6\u8d1d\u4e09\u6761\u7cbe\u8292\u8000\u76ee\u7684\u5251\u5f71\uff0c\u5929\u5a07\u5982\u9f99\uff0c\u5e7b\u6210\u6f2b\u5929\u5251\u5e55\uff0c\u8fce\u5934\u7f69\u4e0b\uff0c\u7acb\u611f\u51b7\u68ee\u68ee\u7684\u5251 \u6c14\uff0c\u8fce\u9762\u538b\u6765\u3002 \u3000\u3000\u8033\u9645\u7a81\u542c\u4e00\u58f0\u5a07\u547c\uff1a\u201c\u4e09\u4f4d\u5144\u957f\uff0c\u4e14\u6162\uff01\u2026\u201d \u7b2c\u5341\u56db\u7ae0\u3000\u6a0a\u6c0f\u4e09\u5251>> \u53e4\u9f99\u300a\u5251\u5ba2\u884c\u300b \u7b2c\u5341\u56db\u7ae0\u3000\u6a0a\u6c0f\u4e09\u5251 \u3000\u3000\u6a0a\u6c0f\u4e09\u6770\u542c\u5230\u59b9\u59b9\u6025\u547c\uff0c\u624b\u4e2d\u5251\u4ff1\u81ea\u987f\u4e86\u4e00\u987f\u3002 \u3000\u3000\u5c55\u767d\u5728\u4e09\u5251\u5939\u653b\u5371\u6025\u4e4b\u4e0b\uff0c\u5c06\u5fc3\u4e00\u6a2a\uff0c\u201c\u98ce\u6253\u67af\u8377\u201d\u3001\u201c\u8c79\u9690\u5357\u5c71\u201d\uff0c\u4e00\u8fde\u594b\u529b\u8fde\u51fb\u4e09 \u638c\u3002 \u3000\u3000\u6b64\u65f6\uff0c\u5c55\u767d\u529f\u529b\u9ad8\u51fa\u4ee5\u524d\u751a\u591a\uff0c\u5728\u60c5\u6025\u62fc\u547d\u7684\u60c5\u51b5\u4e0b\uff0c\u594b\u529b\u5288\u51fa\u4e09\u638c\uff0c\u7adf\u7136\u662f\u5a01\u4e0d\u53ef \u6321\u3002 \u3000\u3000\u53ea\u89c1\u5982\u6392\u5c71\u5012\u6d77\u822c\u7684\u638c\u98ce\u52b2\u6d41\uff0c\u7834\u7a7a\u800c\u81f3\uff0c\u6a0a\u95e8\u4e09\u6770\u4e00\u4e07\u4e2a\u4e5f\u60f3\u4e0d\u51fa\uff0c\u773c\u524d\u6beb\u4e0d\u8d77\u773c\u7684 \u6728\u8bb7\u5c11\u4e2d\uff0c\u7adf\u6709\u5982\u6b64\u9ad8\u5f3a\u7684\u5185\u5bb6\u638c\u529b\uff0c\u5f53\u5373\u5404\u81ea\u6536\u52bf\u9000\u8eab\uff0c\u6325\u5251\u81ea\u4fdd\u3002 \u3000\u3000\u6a0a\u95e8\u4e09\u6770\u5404\u81ea\u98d8\u8eab\u843d\u4e8e\u4e94\u5c3a\u4e4b\u5916\uff0c\u4f46\u4e09\u67c4\u957f\u5251\u4ecd\u81ea\u4e25\u5b88\u95e8\u6237\uff0c\u5bf9\u5c55\u767d\u9690\u53d6\u5305\u56f4\u4e4b\u52bf\u3002 \u3000\u3000\u201c\u5c0f\u5b50\uff01\u679c\u7136\u6709\u70b9\u95e8\u9053\uff0c\u201d\u8001\u5927\u201c\u6233\u60c5\u5251\u201d\u6a0a\u4fca\u5251\u7709\u4e00\u8f69\uff0c\u5411\u5c55\u767d\u6c89\u58f0\u53f1\u9053\uff1a\u201c\u6211\u8bf4\u4f60 \u5c0f\u5b50\u600e\u4e48\u6562\u8fd9\u6837\u72c2\u5462\uff1f\u201d \u3000\u3000\u6a0a\u6c0f\u4e09\u5251\u7684\u8001\u4e09\u201c\u6469\u4e91\u5251\u201d\u6a0a\u82f1\u563f\u563f\u4e00\u9635\u51b7\u7b11\uff0c\u5251\u5c16\u4e00\u6307\u5c55\u767d\u7709\u5fc3\uff0c\u8bf4\u9053\uff1a\u201c\u4f46\u4f60\u5c0f\u5b50 \u8981\u60f3\u5728\u6a0a\u5bb6\u4e09\u5251\u9003\u51fa\u6d3b\u547d\uff0c\u90a3\u4f60\u5c0f\u5b50\u7b97\u662f\u68a6\u60f3\uff01\u201d \u3000\u3000\u5c55\u767d\u5c1a\u672a\u7b54\u8a00\uff0c\u6a0a\u7d20\u9e3e(\u6a0a\u7d20\u9e3e\u6613\u9497\u800c\u5f03\uff0c\u5728\u77f3\u7262\u4e2d\u8ddf\u5c55\u767d\u4e92\u901a\u59d3\u540d\u65f6\uff0c\u8131\u53e3\u8bf4\u51fa\u201c\u6a0a \u7d20\u201d\uff0c\u5c1a\u6709\u4e00\u4e2a\u201c\u9e3e\u201d\u5b57\uff0c\u53ca\u65f6\u5fcd\u4f4f\u53e3\u8fb9\u672b\u8bf4\u51fa\u6765\u3002)\u5df2\u8d76\u81f3\u8fd1\u524d\uff0c\u7528\u624b\u63a0\u4e86\u4e00\u4e0b\u88ab\u98ce\u5439\u4e71 \u7684\u9b03\u53d1\uff0c\u5a07\u58f0\u8bf4\u9053\uff1a\u201c\u4e09\u4f4d\u5144\u5f1f\uff0c\u4ed6\u2026\u2026\u5c55\u5c0f\u4fa0\u5728\u77f3\u7262\u4e2d\u52a9\u6211\u8131\u9669\uff0c\u5e76\u4e14\uff0c\u4ed6\u2026\uff0e\uff0c\uff0e\u521a\u624d \u66fe\u4e3a\u6211\u7597\u4f24\u2026\u2026\u201d \u3000\u3000\u6a0a\u7d20\u9e3e\u867d\u662f\u5973\u513f\u8eab\uff0c\u4f46\u7d20\u6709\u7537\u5b50\u6c14\u6982\uff0c\u4e0d\u77e5\u4e3a\u4f55\u5ffd\u7136\u5728\u8a00\u8bcd\u4e4b\u95f4\uff0c\u7ade\u6709\u70b9\u541e\u5410\u5ff8\u6029\u4e4b \u6001\u3002 \u3000\u3000\u6a0a\u6c0f\u4e09\u5251\u6643\u76ee\u770b\u4e86\u59b9\u59b9\u4e00\u773c\uff0c\u8138\u4e0a\u6d41\u9732\u4e00\u822c\u4f3c\u4e4e\u4e0d\u4fe1\u3001\u5374\u53c8\u4e0d\u5f97\u4e0d\u4fe1\u7684\u795e\u60c5\u3002\u8001\u5927\u201c\u6233 \u60c5\u5251\u201d\u6a0a\u4fca\u95ee\u9053\uff1a\u201c\u6b64\u8bdd\u662f\u771f\uff1f\u201d \u3000\u3000\u201c\u6233\u60c5\u5251\u201d\u51fa\u4e86\u540d\u7684\u51b7\u9177\u5be1\u60c5\uff0c\u81ea\uff0c\u6b64\u8bdd\u4e0d\u77e5\u662f\u5411\u5c55\u767d\u53d1\u95ee\uff0c\u8fd8\u662f\u5411\u4ed6\u7684\u80de\u59b9\u53d1\u95ee\u3002 \u3000\u3000\u5c55\u767d\u70ed\u5fc3\u6551\u4eba\uff0c\u6ca1\u60f3\u5230\u7adf\u60f9\u6765\u4e86\u4e00\u987f\u95f2\u6c14\u3002\u4e0d\u8fc7\uff0c\u4ed6\u8fd1\u6765\u9047\u5230\u4e0d\u8fd1\u60c5\u7406\u7684\u4e8b\u592a\u591a\u4e86\u3002\u6240 \u4ee5\u4ecd\u81ea\u5f3a\u884c\u5fcd\u9690\u7740\uff0c\u7ad9\u7acb\u5f53\u573a\uff0c\u4e00\u8a00\u4e0d\u53d1\u3002 \u3000\u3000\u201c\u5927\u54e5\uff01\u201d\u6a0a\u7d20\u9e3e\u5374\u5e7d\u5e7d\u5730\u8bf4\u9053\uff1a\u201c\u4f60\u4ee5\u4e3a\u59b9\u59b9\u4f1a\u8bf4\u8c0e\u5417\uff1f\u201d \u3000\u3000\u6a0a\u6c0f\u4e09\u5251\u867d\u672a\u56de\u5934\uff0c\u4ecd\u7136\u72e0\u72e0\u5730\u6ce8\u89c6\u5c55\u767d\uff0c\u4f46\u8033\u4e2d\u542c\u5230\u59b9\u59b9\u8fd9\u5145\u6ee1\u4e86\u5e7d\u6028\u7684\u58f0\u8c03\uff0c\u4e5f\u77e5 \u9053\u4e8b\u60c5\u4e0d\u4f1a\u6709\u5047\uff0c\u800c\u4e14\uff0c\u8001\u5927\u201c\u6233\u60f0\u5251\u201d\uff0c\u4e5f\u4f53\u4f1a\u5230\u521a\u624d\u81ea\u5df1\u7684\u591a\u7591\uff0c\u53ef\u80fd\u4e25\u91cd\u5730\u4f24\u5bb3\u4e86\u59b9 \u59b9\u7684\u81ea\u5c0a\u5fc3\u3002 \u3000\u3000\u201c\u6233\u60c5\u5251\u201d\u6a0a\u4fca\u867d\u7136\u4e2a\u6027\u5b64\u50fb\uff0c\u51b7\u50b2\u5be1\u6e05\uff0c\u4f46\u5bf9\u4ed6\u8fd9\u552f\u4e00\u7684\u80de\u59b9\uff0c\u5374\u662f\u7231\u62a4\u5907\u81f3\u3002\u4e00\u542c \u5230\u59b9\u59b9\u5e7d\u6028\u7684\u58f0\u97f3\uff0c\u7acb\u523b\u5c06\u624b\u4e2d\u5251\u4e00\u6a2a\uff0c\u6536\u52bf\u56de\u8eab\uff0c\u8bf4\u9053\uff1a\u201c\u65e2\u7136\u662f\u771f\uff0c\u90a3\u4e48\uff0c\u5c31\u653e\u8fc7\u4ed6 \u4e86\u201d \u3000\u3000\u6a0a\u6c0f\u4e8c\u5251\uff0c\u542c\u5230\u5927\u54e5\u8fd9\u6837\u4e00\u8bf4\uff0c\u4e5f\u4ff1\u81ea\u6536\u5251\u64a4\u62db\u3002\u201c\u6233\u60c5\u5251\u201d\u8ddf\u7740\u8f7b\u559d\u4e86\u4e00\u58f0\uff1a \u201c\u8d70\uff01\u201d \u3000\u3000\u4e09\u5251\u4e00\u9e3e\uff0c\u968f\u5373\u817e\u8eab\u5411\u5c0f\u5c71\u4e0b\u7eb5\u592b\u2026\u2026 \u3000\u3000\u5c55\u767d\u770b\u5230\u6b64\u79cd\u60c5\u5f62\uff0c\u5fc3\u4e2d\u611f\u6982\u4e07\u5343\u3002\u6697\u60f3\uff1a\u81ea\u5df2\u771f\u662f\u65f6\u4e56\u547d\u8e47\uff0c\u6240\u9047\u5230\u7684\u5c3d\u662f\u4e9b\u4e0d\u53ef\u7406 \u55bb\u4e4b\u4eba\uff0c\u597d\u5fc3\u597d\u610f\u6551\u52a9\u4eba\u5bb6\uff0c\u53cd\u5012\u60f9\u6765\u4e00\u809a\u5b50\u95f2\u6c14\u3002 \u3000\u3000\u4f46\u770b\u5230\u6a0a\u7d20\u9e3e\u5a07\u5a1c\u8eab\u5f71\uff0c\u79c0\u53d1\u968f\u98ce\u98d8\u626c\uff0c\u8ddf\u7740\u5979\u4e09\u4f4d\u5144\u957f\u98d8\u7136\u79bb\u53bb\u4e4b\u9645\uff0c\u5fc3\u4e2d\u4e0d\u514d\u53c8\u751f \u8d77\u4e00\u822c\u6005\u60d8\u96be\u820d\u4e4b\u60c5\u3002\u4ed6\u4e5f\u8bf4\u4e0d\u51fa\u4e2a\u6240\u4ee5\u7136\u6765\uff0c\u53ea\u89c9\u5f97\u5728\u77f3\u7262\u4e2d\u4e0e\u6a0a\u7d20\u9e3e\u76f8\u8c08\u751a\u662f\u6295\u673a\uff0c\u867d \u7136\u90a3\u65f6\u4e0d\u77e5\u5979\u662f\u6613\u9497\u800c\u5e76\uff0c\u4f46\u4f9d\u604b\u5730\u7adf\u4e0d\u613f\u4e0e\u5979\u5206\u79bb\u3002\u56e0\u6b64\uff0c\u89c1\u5979\u8d70\u4e86\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u5fc3\u795e\u8fdc \u884c\uff0c\u6014\u5728\u90a3\u513f\u2026\u2026 \u3000\u3000\u751a\u81f3\u4ed6\u8fde\u6a0a\u6c0f\u4e09\u5251\u7684\u65e0\u7406\u4e4b\u8a00\uff0c\u4e5f\u5fd8\u4e86\u7f6e\u8fa9\uff1b\u53ef\u662f\u5c31\u5728\u4ed6\u4e00\u6014\u4e4b\u95f4\uff0c\u4e09\u5251\u4e00\u9e3e\u8eab\u5f62\u9a70\u51fa \u672a\u8fdc\uff0c\u5ffd\u542c\u6570\u4e08\u4e4b\u5916\u5bd2\u6797\u4e2d\u4e00\u58f0\u51b7\u54fc\uff0c\u4e00\u4e2a\u9634\u6c89\u7684\u8bed\u58f0\u4f20\u6765\u3002 \u3000\u3000\u201c\u8d70\uff1f\u6ca1\u6709\u90a3\u4e48\u5bb9\u6613\uff0c\u2018\u8c79\u7a81\u5c71\u5e84\u2019\u5c82\u662f\u5c14\u7b49\u8981\u6765\u4fbf\u6765\u3001\u8981\u8d70\u4fbf\u8d70\u7684\u5730\u65b9\uff1f\u201d \u3000\u3000\u968f\u7740\u9634\u6c89\u8bed\u58f0\uff0c\u98ce\u58f0\u8fa3\u7136\uff0c\u5728\u56db\u5468\u5bc6\u6797\u4e4b\u4e2d\uff0c\u4eba\u5f71\u6643\u52a8\uff0c\u4e00\u8fde\u95ef\u51fa\u6570\u5341\u4eba\u4e4b\u591a\uff01 \u3000\u3000\u8fd9\u6570\u5341\u6b66\u6797\u4eba\u7269\uff0c\u63d0\u5200\u631a\u6756\uff0c\u8702\u62e5\u800c\u81f3\uff0c\u6b63\u597d\u963b\u6ce8\u4e09\u5251\u4e00\u9e3e\u7684\u53bb\u8def\u3002 \u3000\u3000\u4e09\u5251\u4e00\u5f2f\uff0c\u6536\u6b65\u505c\u8eab\uff0c\u6a2a\u5251\u5f53\u80f8\uff0c\u4e3e\u76ee\u56db\u987e\uff0c\u8138\u4e0a\u4ff1\u81ea\u95ea\u9053\u4e00\u4e1d\u60ca\u60f6\u795e\u8272\uff1b\u4f46\u8fc5\u5373\u4e09\u5251 \u659c\u4e3e\uff0c\u6446\u5f00\u95e8\u6237\uff0c\u628a\u5f92\u624b\u7684\u6a0a\u7d20\u9e3e\u7ef4\u62a4\u5728\u4e2d\u5fc3\uff0c\u770b\u6837\u5b50\u662f\u60c5\u6025\u4e0d\u60dc\u4ee5\u6b7b\u529b\u6218\uff01 \u3000\u3000\u5c55\u767d\u4e3e\u76ee\u4e00\u770b\uff0c\u4ece\u5bd2\u6797\u4e2d\u73b0\u8eab\u51fa\u6765\u7684\u4e00\u7fa4\u4eba\uff0c\u4e0d\u7531\u70ed\u8840\u6cb8\u817e\uff0c\u53cc\u773c\u51e0\u4e4e\u5192\u51fa\u706b\u6765\u2026\u2026 \u3000\u3000\u539f\u6765\uff0c\u8fd9\u7fa4\u6b66\u6797\u4eba\u7269\u4e4b\u4e2d\uff0c\u5f53\u5148\u4e00\u4eba\uff0c\u8eab\u9ad8\u4f53\u5065\uff0c\u4e00\u88ad\u9ad8\u8d35\u65e0\u6bd4\u7684\u7ef8\u888d\uff0c\u7ea2\u6da6\u7684\u989c\u9762\uff0c \u6f06\u9ed1\u7684\u5934\u53d1\u5982\u9ed1\u7f0e\u822c\u5149\u4eae\uff0c\u4e24\u9b03\u5374\u6709\u6570\u6839\u661f\u661f\u767d\u53d1\uff0c\u864e\u76ee\u8695\u7709\uff0c\u72ee\u9f3b\u9614\u53e3\uff0c\u53cc\u773c\u795e\u5149\u903c\u4eba\uff0c \u771f\u662f\u4eea\u6001\u68ee\u4e25\uff0c\u4ee4\u4eba\u671b\u4e4b\u751f\u5a01\u3002\u6765\u4eba\u975e\u4ed6\uff0c\u6b63\u662f\u90a3\u201c\u8c79\u7a81\u5c71\u5e84\u201d\u5e84\u4e3b\u3001\u4e2d\u539f\u6b66\u6797\u65e0\u4eba\u6562\u8f7b\u652b \u5176\u950b\u7684\u201c\u6458\u661f\u624b\u201d\u6155\u5bb9\u6db5\u3002 \u3000\u3000\u5176\u5b9e\u5c55\u767d\u5e76\u4e0d\u8ba4\u8bc6\u8fd9\u52bf\u53ef\u76d6\u5929\u7684\u201c\u6458\u661f\u624b\u201d\u53ea\u662f\u201c\u6458\u661f\u624b\u201d\u8eab\u5fa1\u7684\u90a3\u4ef6\u534e\u8d35\u7684\u7ef8\u888d\uff0c\u5176 \u8d28\u6599\u7adf\u8ddf\u5c55\u767d\u7684\u7236\u4eb2\u4e34\u6b7b\u4e4b\u524d\u4ea4\u7ed9\u5c55\u767d\u9057\u7269\u4e4b\u4e2d\u7684\u90a3\u65b9\u892a\u4e86\u8272\u7684\u4e1d\u7ef8\u4e00\u822c\u65e0\u4e8c\uff0c\u8fd9\u662f\u4f7f\u4ed6\u5fc3 \u60c5\u6fc0\u52a8\u7684\u6700\u5927\u539f\u56e0\u3002 \u3000\u3000\u5c55\u81ea\u5e76\u4e0d\u786e\u77e5\u8fd9\u201c\u6458\u661f\u624b\u201d\u6155\u5bb9\u6db5\uff0c\u4fbf\u662f\u4ed6\u7684\u6740\u7236\u4ec7\u4eba\u3002\u4f46\uff0c\u8fd9\u88ad\u4e16\u4e0a\u5f88\u5c11\u89c1\u7684\u9ad8\u8d35\u7ef8 \u62b1\uff0c\u5374\u662f\u4ed6\u8ffd\u67e5\u6740\u7236\u4ec7\u4eba\u59d3\u540d\u7684\u552f\u4e00\u7ebf\u7d22\u3002 \u3000\u3000\u5c55\u767d\u89c1\u5230\u201c\u6458\u661f\u624b\u201d\u6240\u7a7f\u7684\u90a3\u88ad\u7ef8\u888d\uff0c\u5728\u521d\u5347\u671d\u9633\u7684\u6620\u7167\u4e0b\u95ea\u95ea\u53d1\u5149\u3002\u9661\u7136\u5fc6\u8d77\u7236\u4eb2\u4e34 \u6b7b\u4e4b\u524d\u7684\u60e8\u72b6\uff1f\u53c8\u60f3\u8d77\u81ea\u5df2\u4fdd\u9556\u51fa\u6765\uff0c\u9047\u5230\u201c\u71d5\u4e91\u4e94\u9738\u5929\u201d\u52ab\u9556\uff0c\u5b89\u4e50\u516c\u5b50\u4ed7\u4e49\u76f8\u52a9\uff0c\u201c\u7b2c \u4e00\u795e\u5077\u8ffd\u98ce\u65e0\u5f71\u201d\u534e\u6e05\u6cc9\u62a2\u5251\uff0c\u903c\u95ee\u81ea\u5df1\u201c\u65e0\u60c5\u78a7\u5251\u201d\u7684\u6765\u6e90\uff0c\u81ea\u5df1\u628a\u7236\u4eb2\u9057\u7269\u663e\u51fa\u6765\uff0c \u201c\u795e\u5077\u65e0\u5f71\u201d\u7a81\u7136\u6a2a\u5251\u81ea\u520e\u2026\u2026\u8fd9\u4e00\u5207\u7684\u4e00\u5207\uff0c\u5ffd\u7136\u4e4b\u95f4\uff0c\u90fd\u6d8c\u73b0\u5728\u5c55\u767d\u7684\u773c\u5e18\u2026\u2026 \u3000\u3000\u4f46\uff0c\u5c31\u5728\u5c55\u81ea\u77a0\u7136\u53d1\u6014\u3001\u8111\u6d77\u4e2d\u601d\u7eea\u7535\u8f6c\u661f\u98de\u4e4b\u9645\uff0c\u90a3\u8fb9\u5a01\u9707\u6b66\u6797\u7684\u201c\u6458\u661f\u624b\u201d\u6155\u5bb9 \u6db5\uff0c\u5df2\u7ecf\u5411\u4e09\u5251\u4e00\u9e3e\u559d\u95ee\u9053\uff1a\u201c\u5927\u80c6\u5c0f\u8f88\uff01\u7adf\u6562\u591c\u72af\u2018\u8c79\u7a81\u5c71\u5e84\u2019\uff0c\u8fd8\u4e0d\u5f03\u5251\u53d7\u7f1a\uff1f\u96be\u9053\u8fd8 \u8981\u7b49\u672c\u5e84\u4e3b\u4e0b\u4ee4\u52a8\u624b\u5417\uff1f\u201d \u3000\u3000\u201c\u8001\u8d3c\uff01\u201d\u6233\u60c5\u5251\u6a0a\u4fca\u6012\u53f1\u9053\uff1a\u201c\u7528\u4e0d\u7740\u8000\u6b66\u626c\u5a01\uff01\u4e0d\u8fc7\u662f\u501a\u4ed7\u5c14\u7b49\u4eba\u591a\uff0c\u6a0a\u6c0f\u4e09\u6770\u65e2 \u6562\u6765\u5c31\u4e0d\u6015\uff0c\u6709\u4ec0\u4e48\u672c\u9886\u4f7f\u51fa\u6765\u597d\u4e86\uff01\u201d \u3000\u3000\u201c\u6233\u60c5\u5251\u201d\u8fd9\u51e0\u53e5\u8bdd\u5206\u660e\u662f\u8272\u5389\u5185\u832c\u3002\u201c\u6458\u661f\u624b\u201d\u6155\u5bb9\u6db5\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u8f88\uff01\u6b7b \u5728\u773c\u524d\uff0c\u5c1a\u6562\u5927\u8a00\u4e0d\u60ed\uff0c\u5047\u5982\u5c14\u7b49\u5f03\u5251\u5c31\u7f1a\uff0c\u90a3\u4e48\u672c\u5e84\u4e3b\u770b\u5728\u4ee5\u5f80\u548c\u4f60\u7236\u4eb2\u7684\u4ea4\u60c5\u4e0a\u51fa\u8fd8\u53ef \u653e\u7ed9\u5c14\u7b49\u2014\u6761\u751f\u8def\uff0c\u5982\u82e5\u4e0d\u7136\uff0c\u54fc\uff01\u201d \u3000\u3000\u201c\u6458\u661f\u624b\u201d\u6155\u5bb9\u6db5\u8bf4\u81f3\u6b64\u5904\uff0c\u51b7\u54fc\u4e86\u4e00\u58f0\uff0c\u4e8c\u76ee\u795e\u5149\u66b4\u5c04\uff0c\u8fd9\u5e55\u5bb9\u5e84\u4e3b\u672c\u5c31\u50cf\u8c8c\u5a01\u4e25\uff0c \u4e0d\u5ffd\u800c\u5a01\uff0c\u5982\u4eca\u4e00\u53d1\u6012\uff0c\u66f4\u4f7f\u4eba\u4e0d\u5bd2\u800c\u6817\u3002 \u3000\u3000\u201c\u8fd9\u5c0f\u5b64\u5c71\u5c31\u662f\u5c14\u7b49\u846c\u8eab\u4e4b\u5730\uff01\u201d \u3000\u3000\u201c\u6458\u661f\u624b\u201d\u6b64\u8bdd\u51fa\u53e3\uff0c\u6740\u6c14\u903c\u4eba\u3002 \u3000\u3000\u6a0a\u6c0f\u4e09\u5251\u2014\u86ee\u7d20\u77e5\u201c\u6458\u661f\u624b\u201d\u8a00\u51fa\u5fc5\u884c\uff0c\u4ee4\u51fa\u5982\u5c71\u3002\u5982\u4eca\uff0c\u770b\u5230\u201c\u6458\u661f\u624b\u201d\u864e\u76ee\u653e\u5149\uff0c \u8138\u4e0a\u5e03\u6ee1\u715e\u6c14\uff0c\u53c8\u89c1\u7ad9\u5728\u201c\u6458\u661f\u624b\u201d\u8eab\u540e\u7684\u201c\u8c79\u7a81\u5c71\u5e84\u201d\u5341\u5927\u9ad8\u624b\uff0c\u4ee5\u53ca\u6570\u5341\u4e2a\u95e8\u4e0b\u98df\u5ba2\uff0c \u4e2a\u4e2a\u6740\u6c14\u817e\u817e\uff0c\u72de\u7709\u7acb\u76ee\u72e0\u77aa\u7740\u4ed6\u4eec\u56db\u4eba\uff0c\u4fbf\u77e5\u7edd\u96be\u8ba8\u4e0d\u4e86\u597d\u53bb\uff0c\u8bf4\u4e0d\u5b9a\u4fbf\u8981\u6a2a\u5c38\u5f53\u573a\uff0c\u5fc3 \u4e2d\u4e0d\u514d\u63a0\u8fc7\u4e00\u4e1d\u5bd2\u610f\u3002 \u3000\u3000\u4f46\uff0c\u60f3\u5230\u6a0a\u5bb6\u5728\u6b66\u6797\u4e2d\u7684\u95e8\u9600\u5730\u4f4d\uff0c\u6a0a\u5bb6\u7684\u4e3b\u6bcd\u2014\u2014\u4e5f\u5c31\u662f\u81ea\u5df1\u7684\u6bcd\u4eb2\uff0c\u53d7\u5230\u6155\u5bb9\u5e84\u7684 \u4fae\u8511\u800c\u542b\u8fb1\u81ea\u6740\u7684\u803b\u8fb1\uff0c\u4e0d\u7981\u70ed\u8840\u4e0a\u51b2\uff0c\u65e9\u628a\u751f\u6b7b\u7f6e\u4e4b\u5ea6\u5916\uff0c\u6012\u58f0\u545b\u9053\uff1a\u201c\u5e9f\u8bdd\u5c11\u8bf4\uff01\u6709\u672c \u9886\u628a\u4f60\u5bb6\u5c0f\u592a\u7237\u62ff\u4e0b\u5c31\u662f\u4e86\uff01\u201d \u3000\u3000\u201c\u6458\u661f\u624b\u201d\u9762\u5b54\u4e00\u6c89\uff0c\u518d\u4e0d\u53d1\u8a00\uff0c\u624b\u52bf\u5411\u524d\u4e00\u6446\uff0c\u98ce\u58f0\u98d2\u7136\uff0c\u7ad9\u5728\u201c\u6458\u661f\u624b\u201d\u8eab\u540e\u7684\u5341 \u5927\u9ad8\u624b\uff0c\u7adf\u6709\u4e94\u516d\u4eba\u4e00\u9f50\u6643\u8eab\u8d8a\u4f17\u800c\u51fa\uff01 \u3000\u3000\u201c\u8c79\u7a81\u5c71\u5e84\u201d\u5341\u5927\u9ad8\u624b\uff0c\u5728\u6b66\u6797\u4e2d\u60e7\u90fd\u662f\u72ec\u9738\u4e00\u65b9\u7684\u9876\u5c16\u9ad8\u624b\uff0c\u968f\u4fbf\u4e3e\u51fa\u4efb\u4f55\u4e00\u4eba\uff0c\u90fd \u662f\u5728\u6c5f\u6e56\u4e0a\u53eb\u5f97\u54cd\u7684\u4eba\u7269\uff0c\u8ba4\u4e3a\u6709\u81ea\u5df2\u5728\u5e84\u4e0a\uff0c\u65e0\u4eba\u518d\u6562\u4fb5\u72af\u201c\u8c79\u7a81\u5c71\u5e84\u201d\u3002\u5982\u4eca\uff0c\u4e00\u591c\u4e4b \u95f4\uff0c\u88ab\u4eba\u5927\u4e3e\u6d78\u72af\uff0c\u800c\u4e14\u7eb5\u706b\u711a\u5e84\uff0c\u4ed6\u4eec\u5341\u4eba\u5747\u8ba4\u4e3a\u8fd9\u662f\u4e2a\u4eba\u7684\u5947\u803b\u5927\u8fb1\uff0c\u56e0\u6b64\uff0c\u4e00\u89c1\u5e84\u4e3b \u793a\u610f\uff0c\u7acb\u523b\u62a2\u5148\u800c\u51fa\u3002\u4f46\uff0c\u51ed\u5404\u4eba\u5728\u6b66\u6797\u4e2d\u7684\u8eab\u4efd\u5730\u4f4d\uff0c\u5374\u4e0d\u597d\u610f\u601d\u5408\u624b\u56f4\u653b\uff0c\u9996\u5148\u201c\u94f6\u7bab \u593a\u9b42\u201d\u7ae0\u58eb\u9e4f\u4e00\u62b1\u62f3\uff0c\u5411\u5176\u4ed6\u6570\u4eba\u8a00\u9053\uff1a\u201c\u8bf8\u4f4d\u8d24\u5f1f\uff01\u8001\u54e5\u54e5\u5356\u4e2a\u8001\uff0c\u8fd9\u7b2c\u4e00\u4ed7\u5c31\u8ba9\u7ed9\u8001\u54e5 \u54e5\u5427\uff01\u201d \u3000\u3000\u8bf4\u7f62\uff0c\u4ece\u8896\u53e3\u5185\u8896\u51fa\u94f6\u5149\u707f\u70c2\u7684\u70c2\u94f6\u7bab\uff0c\u5c31\u60f3\u4e0a\u524d\u52a8\u624b\u3002 \u3000\u3000\u770b\u8fd9\u4e00\u8eab\u534e\u670d\uff0c\u9762\u767d\u65e0\u987b\uff0c\u4f0a\u5982\u4e2d\u5e74\u8d35\u7ec5\u7684\u201c\u94f6\u7bab\u593a\u9b42\u201d\uff0c\u5bf9\u7740\u4e94\u516d\u4e2a\u79c3\u53d1\u9e64\u989c\u5e74\u81f3\u7a92 \u6beb\u7684\u6b66\u6797\u9ad8\u624b\uff0c\u81ea\u79f0\u8001\u54e5\u54e5\uff0c\u53ef\u77e5\u4ed6\u4e00\u5b9a\u8981\u6bd4\u90a3\u4e94\u516d\u4e2a\u8001\u4eba\u4e2d\u7eaa\u66f4\u5927\uff0c\u4f46\u5916\u8868\u4e0a\u5374\u65e0\u8bba\u5982\u4f55 \u4e5f\u770b\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u7a81\u7136\uff0c\u4e00\u4e2a\u7707\u76ee\u9053\u4eba\uff0c\u7ffb\u7740\u4e00\u53cc\u767d\u679c\u773c\uff0c\u8c6a\u7b11\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u7ae0\u5144\u7684\u2018\u593a\u9b42\u7bab\u6cd5\u2019\u5929\u4e0b \u9a70\u540d\uff0c\u5bf9\u4ed8\u51e0\u4e2a\u9f20\u8f88\uff0c\u54ea\u8fd8\u503c\u5f97\u7ae0\u5144\u51fa\u624b\uff0c\u6211\u770b\u8fd9\u7b2c\u4e00\u4ed7\uff0c\u8fd8\u662f\u8ba9\u7ed9\u6211\u8fd9\u778e\u8001\u9053\u5427\uff01\u201d \u3000\u3000\u53ef\u662f\uff0c\u8fd8\u672a\u7b49\u7707\u76ee\u9053\u4eba\u51fa\u624b\uff0c\u53e6\u4e00\u72ec\u81c2\u79c3\u9876\u8001\u8005\u53eb\u9053\uff1a\u201c\u9053\u5144\u4e5f\u8bf7\u613f\u4e00\u4f1a\uff0c\u7531\u8001\u592b\u5148\u4e0a \u7b2c\u4e00\u9635\uff01\u201d \u3000\u3000\u72ec\u81c2\u79c3\u9876\u8001\u8005\uff0c\u6b63\u662f\u201c\u8ffd\u9b42\u94c3\u201d\u53f8\u9a6c\u656c\uff0c\u53ea\u89c1\u4ed6\u58f0\u51fa\u62db\u81f3\uff0c\u72ec\u81c2\u4e00\u62a2\u201c\u6a2a\u626b\u5343\u519b\u201d\uff0c\u638c \u7f18\u63a5\u8d77\u4e00\u822c\u5c16\u5578\u52b2\u98ce\uff0c\u731b\u5411\u4e09\u5251\u4e00\u9e3e\u505c\u8eab\u4e4b\u5904\u5288\u53bb\u3002 \u3000\u3000\u6a0a\u7d20\u9e3e\u6e38\u8eab\u56de\u638c\uff0c\u4ece\u4e8c\u54e5\u201c\u8ffd\u98ce\u5251\u201d\u808b\u4e0b\uff0c\u9065\u653b\u4e24\u638c\uff1a\u201c\u8ffd\u98ce\u5251\u201d\u957f\u5251\u821e\u8d77\u4e00\u9762\u5251\u5899\uff0c \u6765\u6321\u201c\u8feb\u98ce\u5251\u9b42\u94c3\u201d\u5f3a\u5927\u638c\u98ce\uff0c\u201c\u6233\u60c5\u5251\u201d\u4e0e\u201c\u6469\u4e91\u5251\u201d\uff0c\u5374\u8eab\u5f62\u6e38\u8d70\uff0c\u4e24\u67c4\u7cbe\u8292\u957f\u5251\uff0c\u72b9 \u5982\u7075\u86c7\u51fa\u6d1e\uff0c\u4e00\u5de6\u4e00\u53f3\uff0c\u5206\u5411\u201c\u8ffd\u9b42\u94c3\u201d\u4e24\u4fa7\u653b\u81f3\uff01 \u3000\u3000\u4e09\u5251\u4e00\u5f2f\u540c\u65f6\u51fa\u62db\u5e94\u654c\uff0c\u914d\u5408\u5f97\u7adf\u662f\u5929\u8863\u65e0\u7f1d\uff0c\u6070\u5230\u597d\u5904\u3002\u539f\u6765\u8fd9\u4e09\u5251\u53cc\u638c\uff0c\u6b63\u662f\u6a0a\u5bb6 \u5251\u62f3\u9635\u5f0f\u4e2d\u95fb\u540d\u7684\u201c\u6df7\u5143\u4e09\u624d\u9635\u201d\uff0c\u4e09\u5251\u4e00\u9e3e\u5e73\u65e5\u6f14\u7ec3\u6709\u7d20\uff0c\u5982\u4eca\u88ab\u903c\u65bd\u5c55\uff0c\u5a01\u529b\u7ade\u81ea\u4e0d \u5c0f\u3002 \u3000\u3000\u201c\u8ffd\u9b42\u94c3\u201d\u53f8\u9a6c\u656c\uff0c\u4e3a\u897f\u5317\u9053\u4e0a\u6709\u540d\u7684\u9ad8\u624b\uff0c\u6b66\u529f\u9ad8\u4e0d\u53ef\u6d4b\uff0c\u6027\u60c5\u53c8\u6b8b\u66b4\u65e0\u6bd4\uff0c\u4e0e\u201c\u72ec \u811a\u98de\u9b54\u201d\u674e\u4e3e\uff0c\u5408\u79f0\u4e3a\u201c\u585e\u5916\u53cc\u6b8b\u201d\u3002\u5982\u4eca\uff0c\u5f53\u7740\u5e84\u4e3b\u4e0e\u5341\u5927\u9ad8\u624b\u4e4b\u9762\uff0c\u62a2\u5148\u51fa\u624b\uff0c\u539f\u60f3\u5728 \u4eba\u524d\u9732\u8138\uff0c\u6ca1\u6444\u5230\u5927\u610f\u8f7b\u654c\uff0c\u773c\u524d\u56db\u4e2a\u540e\u751f\u665a\u8f88\uff0c\u5408\u8d77\u624b\u6765\u7ade\u6709\u8fd9\u7b49\u5965\u5999\u62db\u5f0f\u3002 \u3000\u3000\u201c\u8ffd\u9b42\u94c3\u201d\u53f8\u9a6c\u656c\uff0c\u4e00\u638c\u5288\u51fa\uff0c\u7acb\u611f\u81ea\u5df2\u638c\u529b\u5982\u77f3\u6c89\u5927\u6d77\u3002\u540c\u65f6\uff0c\u4e24\u9053\u51b7\u68ee\u5251\u8292\uff0c\u5df2\u631f \u7740\u52b2\u6c14\u5411\u81ea\u5df1\u5de6\u53f3\u4e24\u65b9\u523a\u6765\uff0c\u4fbf\u77e5\u4e0d\u597d\u3002\u597d\u5728\u4ed6\u4e34\u6218\u7ecf\u9a8c\u4e30\u5bcc\uff0c\u89c1\u52bf\u4e0d\u5999\uff0c\u5f53\u5373\u632b\u8eab\u8e6c\u817f\uff0c \u8eab\u5f62\u5012\u8dcc\u4e00\u4e08\uff0c\u65b9\u624d\u8eb2\u8fc7\u4e86\u5de6\u53f3\u53cc\u65b9\u4e8c\u5251\u7684\u6740\u62db\uff01 \u3000\u3000\u201c\u8ffd\u9b42\u94c3\u201d\u53f8\u9a6c\u656c\u6c14\u5f97\u6b8b\u7709\u5012\u7acb\uff0c\u602a\u76ee\u5706\u7741\uff0c\u201c\u53ee\u6c21\u201d\u4e00\u58f0\uff0c\u4ece\u6000\u4e2d\u638f\u51fa\u4e00\u4e2a\u9ec4\u6f84\u6f84\u7684 \u94dc\u94c3\u3002 \u3000\u3000\u8fd9\u94dc\u94c3\u5e76\u6ca1\u6709\u4ec0\u4e48\u5947\u7279\u4e4b\u5904\uff0c\u5706\u53e3\u6728\u67c4\uff0c\u5c31\u5982\u8d70\u6c5f\u6e56\u90ce\u4e2d\u5356\u836f\u6240\u7528\u7684\u94dc\u94c3\u5dee\u4e0d\u591a\uff0c\u4e0d\u8fc7 \u7a0d\u4e3a\u5927\u4e00\u53f7\u800c\u5df2\u3002\u4f46\u53f8\u9a6c\u656c\u628a\u8fd9\u94dc\u94c3\u64ce\u5728\u624b\u4e2d\uff0c\u4e3e\u94c3\u8fc7\u80a9\uff0c\u201c\u53ee\u94c3\uff01\u53ee\u94c3\uff01\u201d\u7528\u624b\u4e00\u64ad\uff0c\u58f0 \u58f0\u8106\u54cd\uff0c\u7adf\u662f\u9e23\u91d1\u51fb\u7389\uff0c\u9707\u8033\u6b32\u88c2\uff0c\u95fb\u4e4b\u4ee4\u4eba\u5fc3\u98a4\u795e\u6447\uff01 \u3000\u3000\u5e76\u89c1\u8bb8\u591a\u6b66\u6797\u9ad8\u624b\uff0c\u7eb7\u7eb7\u540e\u9000\uff0c\u663e\u89c1\u8fd9\u5c0f\u5c0f\u94dc\u94c3\u5fc5\u6709\u60ca\u4eba\u4e4b\u5904\uff0c\u518d\u770b\u4ed6\u5916\u53f7\u53eb\u201c\u8ffd\u9b42 \u94c3\u201d\uff0c\u4fbf\u53ef\u77e5\u8fd9\u5c0f\u5c0f\u94dc\u94c3\uff0c\u51b3\u4e0d\u7b80\u5355\u3002 \u3000\u3000\u6a0a\u6c0f\u4e09\u5251\u4e00\u89c1\u53f8\u9a6c\u656c\u53d6\u51fa\u94dc\u94c3\uff0c\u5934\u8111\u4e2d\u6781\u5feb\u7684\u60f3\u8d77\u4e00\u4e2a\u4eba\u6765\uff0c\u4e0d\u7531\u9661\u7136\u8272\u53d8\uff01 \u3000\u3000\u4f46\u53f8\u9a6c\u656c\u4e00\u9707\u624b\u4e2d\u201c\u8ffd\u9b42\u94c3\u201d\uff0c\u201c\u53ee\u94c3\u94c3\uff01\u201d\u4e00\u4e32\u5faa\u9b42\u593a\u9b44\u7684\u9510\u97f3\uff0c\u968f\u7740\u7535\u5c04\u4f3c\u7684\u8eab \u5f71\uff0c\u5df2\u6251\u5411\u4e09\u5251\u4e00\u9e3e\uff01 \u3000\u3000\u6a0a\u6c0f\u4e09\u5251\u60ca\u60e7\u4e4b\u4e2d\uff0c\u5251\u6f14\u201c\u4e09\u661f\u5728\u6237\u201d\uff0c\u4e09\u67c4\u957f\u5251\uff0c\u5e76\u6392\u4e00\u7ebf\uff0c\u5251\u5c16\u6296\u98a4\u4e4b\u5904\uff0c\u5e7b\u6210\u4e09 \u6735\u94f6\u661f\uff0c\u9f50\u5411\u201c\u8ffd\u9b42\u94c3\u201d\u8fce\u6765\u3002 \u3000\u3000\u6a0a\u7d20\u9e3e\u4e0d\u77e5\u201c\u8ffd\u9b42\u94c3\u201d\u7684\u5389\u5bb3\uff0c\u5728\u4e09\u4f4d\u5144\u957f\u4e09\u5251\u9f50\u51fa\u4e4b\u9645\uff0c\u4e3a\u4e86\u914d\u5408\u201c\u4e09\u624d\u5251\u9635\u201d\u4e4b\u53d8 \u5316\uff0c\u5e73\u80f8\u63a8\u51fa\u4e24\u638c\uff0c\u540d\u4e3a\u201c\u5251\u6797\u7389\u8776\u201d\uff0c\u7c89\u767d\u638c\u5fc3\uff0c\u771f\u5982\u4e24\u53ea\u7389\u8776\u5728\u4e09\u5251\u4ea4\u8f89\u4e2d\u62cd\u51fa\uff0c\u76f4\u53d6 \u53f8\u9a6c\u656c\u5fc3\u8179\u8981\u5bb3\u3002 \u3000\u3000\u53f8\u9a6c\u656c\u534a\u7a7a\u4e2d\u4e00\u58f0\u957f\u5578\uff0c\u8eab\u5f62\u7535\u5c04\uff1b\u624b\u4e2d\u201c\u8ffd\u9b42\u94c3\u201d\u5316\u4e3a\u4e00\u73af\u91d1\u8292\uff0c\u201c\u53ee\uff01\u53ee\uff01\u53ee\uff01\u201d \u4e09\u58f0\u8106\u54cd\uff0c\u706b\u82b1\u56db\u5c04\uff0c\u7ade\u628a\u6a0a\u6c0f\u4e09\u5251\u7684\u4e09\u67c4\u957f\u5251\u540c\u65f6\u8361\u5f00\uff0c\u6a0a\u6c0f\u4e09\u5251\u4e00\u8def\u8ddf\u8dea\uff0c\u4ece\u659c\u523a\u91cc\u51b2 \u51fa\u4e94\u516d\u6b65\u53bb\uff0c\u624d\u62ff\u6869\u7ad9\u7a33\uff0c\u4f46\u4e09\u4e2a\u4eba\u5df2\u662f\u4fca\u9762\u6cdb\u767d\uff0c\u53cc\u773c\u795e\u5149\u6da3\u6563\u3002 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\u3002\u4e09\u5251\u88ab\u53f8\u9a6c\u656c\u201c\u8ffd\u9b42\u94c3\u201d\u8361\u5f00\uff0c\u53f8\u9a6c\u656c\u8eab\u5f62\u5e76\u672a\u843d\u5730\uff0c\u54c8\u54c8\u54c8\u2026\u2026\u201d \u624b\u8155\u9707\u5904\uff0c\u94c3\u9524\u5728\u94c3\u7897\u5185\u4e00\u9635\u6405\u52a8\uff0c\u5c16\u97f3\u76c8\u8033\uff0c\u731b\u5411\u6a0a\u7d20\u9e3e\u9762\u95e8\u538b\u4e0b\u3002 \u3000\u3000\u6a0a\u7d20\u9e3e\u5fc3\u795e\u88ab\u201c\u8ffd\u9b42\u201d\u94c3\u97f3\u9707\u6563\uff0c\u4e00\u53cc\u7389\u638c\u7684\u529b\u9053\u5df2\u4e0d\u77e5\u6295\u5411\u4f55\u65b9\uff0c\u53ea\u89c9\u5fc3\u60b8\u795e\u6447\uff0c\u4e00 \u5708\u4e00\u5708\u7684\u9ec4\u8272\u5149\u5f71\uff0c\u72b9\u5982\u9633\u5149\u65e5\u8f6e\uff0c\u8fce\u9762\u538b\u6765\uff1b\u4e00\u88ab\u4e00\u6ce2\u7684\u9510\u97f3\uff0c\u6405\u4e71\u795e\u667a\uff0c\u5145\u76c8\u6ee1\u8033\uff0c\u53ea \u611f\u5230\u5468\u8eab\u53d1\u8f6f\uff0c\u5934\u660f\u76ee\u7729\uff0c\u5fc3\u77e5\u4e0d\u597d\uff0c\u201c\u54ce\u5440I\u201d\u4e00\u58f0\u5a07\u547c\uff0c\u4ef0\u9762\u5411\u540e\u5012\u53bb\u3002 \u3000\u3000\u53f8\u9a6c\u656c\u7684\u201c\u8ffd\u9b42\u94c3\u201d\uff0c\u679c\u7136\u5389\u5bb3\uff0c\u53ea\u4e00\u62db\u201c\u6398\u9b42\u593a\u9b44\u201d\u4fbf\u7834\u4e86\u4e09\u5251\u4e00\u5f2f\u7684\u201c\u6df7\u5143\u4e09\u624d \u5251\u201d\u9635\uff0c\u800c\u4e14\uff0c\u6a0a\u7d20\u9e3e\u773c\u770b\u4fbf\u5c06\u4f24\u5728\u201c\u8ffd\u9b42\u94c3\u201d\u4e0b\u3002 \u3000\u3000\u9661\u542c\u4e00\u58f0\u66b4\u559d\uff1a\u201c\u4f4f\u624b\uff01\u201d\u638c\u98ce\u5c71\u6d8c\uff0c\u94c3\u97f3\u201c\u55e1\u201d\u7136\u72c2\u9e23\uff0e \u3000\u3000\u53f8\u9a6c\u656c\u5012\u98de\u4e24\u4e08\u5f00\u5916\uff0c\u9762\u524d\u4eba\u5f71\u4e00\u6643\uff0c\u5f53\u573a\u5df2\u591a\u4e86\u4e00\u4e2a\u7eaf\u6734\u6728\u8bb7\u7684\u5c11\u5e74\u3002 \u3000\u3000\u8fd9\u7eaf\u6734\u5c11\u5e74\u4e00\u73b0\u8eab\uff0c\u4e0d\u5c11\u4eba\u60ca\u547c\u51fa\u58f0\uff1a\u201c\u54a6\uff01\u662f\u4ed6\uff01\u201d \u3000\u3000\u539f\u6765\u8fd9\u7eaf\u6734\u5c11\u5e74\uff0c\u6b63\u662f\u5c55\u767d\u3002 \u3000\u3000\u5c55\u81ea\u81ea\u4ece\u53d1\u73b0\u6155\u5bb9\u5e86\u4e3b\u201c\u6458\u661f\u624b\u201d\uff0c\u6240\u5fa1\u534e\u8d35\u7ef8\u888d\u8d28\u6599\uff0c\u7ade\u4e0e\u4ed6\u7236\u4eb2\u9057\u7269\u4e4b\u4e2d\u90a3\u65b9\u4e1d\u7ef8 \u76f8\u540c\uff0c\u5468\u8eab\u70ed\u8840\u6cb8\u817e\uff0c\u4e00\u65f6\u4e4b\u95f4\u7adf\u4f7f\u4ed6\u6014\u5728\u90a3\u91cc\uff0c\u5bf9\u773c\u524d\u7684\u6fc0\u70c8\u6253\u6597\u90fd\u7f6e\u82e5\u7f54\u95fb\u3002 \u3000\u3000\u5ffd\u7136\u201c\u8ffd\u9b42\u94c3\u201d\u7684\u94c3\u58f0\uff0c\u5c06\u4ed6\u4ece\u6c89\u601d\u8ff7\u606b\u4e2d\u60ca\u9192\uff0c\u62ac\u5934\u4e00\u770b\uff0c\u6b63\u597d\u770b\u5230\u53f8\u9a6c\u656c\u8361\u5f00\u4e09\u67c4 \u957f\u5251\uff0c\u6a0a\u7d20\u9e3e\u5371\u5728\u9877\u523b\u4e4b\u95f4\u3002 \u3000\u3000\u5c55\u767d\u751f\u5c31\u4fa0\u9aa8\u4e49\u80a0\uff0c\u89c1\u6b7b\u5c82\u6709\u4e0d\u6551\u4e4b\u7406\u3002\u4f55\u51b5\uff0c\u6a0a\u7d20\u9e3e\u8fd8\u662f\u4ed6\u5fc3\u76ee\u4e2d\u7684\u77e5\u5df1\u5462\u3002 \u3000\u3000\u6b64\u65f6\uff0c\u5782\u5371\uff0c\u65e9\u628a\u53d7\u4e86\u5979\u4e09\u4f4d\u5144\u957f\u4e00\u809a\u5b50\u95f2\u6c14\u7684\u4e8b\u7ed9\u5fd8\u4e86\uff0c\u53ea\u89c9\u5f97\u6551\u4eba\u8981\u7d27\u3002\u800c\u4e14\u4ed6\u4e5f \u6ca1\u6709\u8003\u8651\u81ea\u5df1\u80fd\u5426\u80dc\u8fc7\u201c\u8ffd\u9b42\u94c3\u201d\uff0c\u53ef\u8bf4\u662f\u201c\u53ea\u89c1\u4e00\u4e49\uff0c\u4e0d\u89c1\u751f\u6b7b\u201d\uff0c\u4f55\u51b5\u662f\u6551\u81ea\u5df1\u7684\u77e5\u5df1 \u670b\u53cb\uff0c\u90a3\u7b80\u76f4\u662f\u4e49\u4e0d\u5bb9\u8f9e\u3002 \u3000\u3000\u5c55\u767d\u4e5f\u662f\u6025\u52b2\uff0c\u5927\u559d\u4e00\u58f0\u201c\u4f4f\u624b\uff01\u201d\u4eba\u5df2\u817e\u7a7a\u800c\u8d77\uff0c\u534a\u7a7a\u4e2d\u5c06\u771f\u529b\u8fd0\u81f3\u5341\u6210\uff0c\u53cc\u638c\u51ed\u7a7a \u63a8\u51fa\u3002 \u3000\u3000\u53f8\u9a6c\u656c\u628a\u6210\u540d\u7684\u5175\u5668\u201c\u8ffd\u9b42\u94c3\u201d\u65bd\u51fa\uff0c\u773c\u770b\u4e00\u62db\u5f97\u624b\uff0c\u7a81\u611f\u52b2\u98ce\u5c71\u6d8c\uff0c\u6251\u9762\u800c\u81f3\uff0c\u53f8\u9a6c \u656c\u6697\u5403\u4e00\u60ca\uff0c\u4e0d\u613f\u4f24\u654c\uff0c\u6643\u8eab\u8dc3\u51fa\u4e8c\u4e08\u5f00\u5916\u3002 \u3000\u3000\u53f8\u9a6c\u656c\u6643\u8eab\u8dc3\u51fa\u5708\u5916\uff0c\u539f\u4ee5\u4e3a\u662f\u53c8\u6765\u4e86\u4ec0\u4e48\u6b66\u6797\u9ad8\u624b\u3002\u5f85\u5b9a\u775b\u4e00\u770b\uff0c\u7adf\u662f\u90a3\u4f7f\u81ea\u5df2\u4e0e\u96f7 \u75af\u5b50\u5f15\u8d77\u8bef\u4f1a\u7684\u5c11\u5e74\uff0c\u4e0d\u7531\u5f97\u53c8\u60ca\u53c8\u6012\u3002 \u3000\u3000\u6012\u7684\u662f\u81ea\u5df1\u4e0e\u4eba\u640f\u6597\uff0c\u7ade\u6709\u4eba\u6562\u534a\u8def\u63d2\u624b\uff1b\u60ca\u7684\u662f\uff0c\u8fd9\u770b\u4e0d\u8d77\u773c\u7684\u5c11\u5e74\uff0c\u7adf\u6709\u8fd9\u822c\u96c4\u539a \u7684\u638c\u529b\uff0c\u771f\u662f\u4ee4\u4eba\u4e0d\u53ef\u601d\u8bae\u3002 \u3000\u3000\u201c\u5c0f\u5b50\uff01\u201d\u53f8\u9a6c\u656c\u6012\u53f1\u4e00\u58f0\uff0c\u51b7\u68ee\u8bf4\u9053\uff1a\u201c\u542c\u8bf4\u4f60\u662f\u96f7\u9707\u8fdc\u7684\u665a\u8f88\uff0c\u4e3a\u4ec0\u4e48\u4e09\u756a\u4e94\u6b21\u8ddf \u672c\u5e84\u4f5c\u5bf9\uff1f\u201d \u3000\u3000\u5c55\u767d\u5728\u4eba\u7fa4\u4e2d\u770b\u4e86\u770b\uff0c\u5e76\u4e0d\u89c1\u96f7\u5927\u53d4\u540c\u6765\u3002\u4ed6\u867d\u7136\u6728\u8bb7\u8bda\u5b9e\uff0c\u4f46\u4e5f\u542c\u51fa\u6765\u53f8\u9a6c\u656c\u8bdd\u4e2d\u4e4b \u610f\uff0c\u662f\u60f3\u5ac1\u7978\u6c5f\u4e1c\u3002\u5c55\u767d\u4e0d\u5584\u8bf3\u8bed\uff0c\u8981\u60f3\u8bf4\u4e0d\u8ba4\u8bc6\u96f7\u5927\u53d4\uff0c\u96f7\u5927\u53d4\u5374\u5bf9\u4ed6\u6069\u91cd\u5982\u5c71\uff0c\u66fe\u4e24\u6b21 \u6551\u4ed6\u8131\u9669\uff0c\u53c8\u5c06\u300a\u5929\u4f5b\u5377\u300b\u4f20\u6388\u4e8e\u4ed6\uff1b\u8981\u60f3\u627f\u8ba4\u96f7\u5927\u53d4\u7684\u540e\u8f88\uff0c\u90a3\u96f7\u5927\u53d4\u5b58\u8eab\u201c\u8c79\u7a81\u5c71 \u5e84\u201d\uff0c\u800c\u4e14\u4e0e\u5e84\u4e0a\u5c61\u5c61\u4f5c\u5bf9\uff0c\u4f1a\u6709\u4e0d\u6e05\u7684\u9ebb\u70e6\uff0c\u56e0\u6b64\uff0c\u4ed6\u7741\u7740\u4e24\u4e2a\u660e\u4eae\u7684\u5927\u773c\u775b\uff0c\u671b\u671b\u8fd9\u4e00 \u4e2a\u53c8\u671b\u671b\u90a3\u4e00\u4e2a\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u7ade\u7b54\u4e0d\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u6a0a\u6c0f\u4e09\u5251\u8e09\u8dc4\u7ad9\u5b9a\uff0c\u6a2a\u5251\u5f53\u80f8\uff0c\u60ca\u714c\u56de\u987e\uff0c\u89c1\u53c8\u662f\u90a3\u5c11\u5e74\u6551\u4e86\u81ea\u5df1\u59b9\u59b9\uff0c\u8138\u4e0a\u95ea\u8fc7\u4e00\u4e1d \u60ed\u6127\u795e\u8272\u3002 \u3000\u3000\u6a0a\u7d20\u9e3e\u60ca\u9b42\u752b\u5b9a\uff0c\u7528\u4e00\u53cc\u660e\u5982\u79cb\u6c34\u7684\u79c0\u76ee\uff0c\u6ce8\u5b9a\u5c55\u767d\uff0c\u7c89\u8138\u6cdb\u767d\uff0c\u4e0d\u77e5\u5fc3\u4e2d\u662f\u4f55\u6ecb\u5473 \u2014\u2026 \u3000\u3000\u201c\u6458\u661f\u624b\u201d\u76b1\u4e86\u76b1\u7709\u2026\u2026 \u3000\u3000\u201c\u5c0f\u53cb\u827a\u4e1a\u4e0d\u51e1\uff01\u201d\u201c\u94f6\u7bab\u593a\u9b42\u201d\u7ae0\u58eb\u9e4f\u8d8a\u4f17\u800c\u51fa\uff0c\u5411\u5c55\u767d\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u77e5\u662f\u4f55\u4eba \u95e8\u4e0b\uff1f\u59d3\u751a\u540d\u8c01\uff0c\u53ef\u5426\u544a\u77e5\u8001\u592b\uff1f\u201d \u3000\u3000\u5c55\u767d\u5c1a\u672a\u7b54\u8a00\uff0c\u7a81\u95fb\u4e00\u58f0\u5389\u543c\uff1a\u201c\u7ae0\u5144\uff0c\u4f55\u5fc5\u591a\u8d39\u5507\u820c\uff01\u5148\u628a\u5c0f\u8f88\u62ff\u4e0b\uff0c\u8fd8\u6015\u4ed6\u6709\u4ec0\u4e48 \u5b9e\u8bdd\u4e0d\u80af\u8bf4\u5417\uff1f\u201d \u3000\u3000\u53d1\u8bdd\u4e4b\u4eba\uff0c\u8eab\u6cd5\u597d\u5feb\uff0c\u53ea\u89c1\u604d\u5982\u4e00\u7f15\u9ed1\u70df\u8eab\u5728\u534a\u7a7a\uff0c\u5c48\u6307\u5982\u94a9\uff0c\u731b\u5411\u5c55\u767d\u53f3\u81c2\u201c\u7ecf\u6e20\u201d \u91cd\u7a74\u6293\u6765\u3002 \u3000\u3000\u5c55\u767d\u7a81\u611f\u773c\u524d\u4e00\u9ed1\uff0c\u6765\u4eba\u6307\u672b\u5230\uff0c\u53f3\u81c2\u808c\u80a4\u5148\u611f\u4e00\u9635\u5bd2\u98ce\u523a\u9aa8\uff0c\u77e5\u9053\u6765\u4eba\u6b66\u529f\u9ad8\u5f3a\uff0c\u4e0d \u6562\u786c\u63a5\uff0c\u7529\u81c2\u584c\u80a9\uff0c\u5c31\u52bf\u5de6\u624b\u5288\u51fa\u4e00\u638c\u3002 \u3000\u3000\u6765\u4eba\u54c8\u54c8\u4e00\u7b11\uff0c\u559d\u58f0\uff1a\u201c\u4f60\u7ed9\u6211\u8eba\u4e0b\uff01\u201d\u8eab\u5f62\u4e34\u7a7a\u7535\u8f6c\uff0c\u500f\u7136\u53c8\u6b3a\u8fd1\u5c55\u767d\u5de6\u4fa7\uff0c\u5de6\u624b\u4f38 \u51fa\uff0c\u75be\u6263\u5c55\u767d\u6253\u51fa\u7684\u5de6\u638c\u624b\u8155\u3002 \u3000\u3000\u6765\u4eba\u62db\u5f0f\u5947\u8be1\u795e\u901f\uff0c\u53d8\u5316\u83ab\u6d4b\uff0c\u5c55\u767d\u8d76\u7d27\u6536\u638c\u9000\u8eab\uff0c\u811a\u4e0b\u4e00\u9519\uff0c\u6a2a\u8de8\u4e94\u5c3a\uff0c\u51e0\u4e4e\u5de6\u8155\u53f3 \u81c2\u88ab\u6765\u4eba\u6293\u4f4f\u3002\u5c31\u8fd9\u6837\uff0c\u5c55\u81ea\u5de6\u8155\u6162\u4e86\u4e00\u70b9\uff0c\u4ecd\u88ab\u6765\u4eba\u6307\u98ce\u626b\u4e2d\uff0c\u53ea\u89c9\u5de6\u8155\u5947\u5bd2\uff0c\u9aa8\u75db\u5982 \u6298\uff0c\u8fde\u534a\u8fb9\u8eab\u5b50\u90fd\u9ebb\u4e86\u3002\u5c55\u767d\u5927\u5403\u4e00\u60ca\uff0c\u4e3e\u76ee\u4e00\u770b\uff0c\u6765\u4eba\u7adf\u662f\u4e00\u4e2a\u7eb5\u9762\u94dc\u987b\u3001\u65e0\u6bd4\u72de\u6076\u7684\u72ec \u817f\u8001\u5934\u3002 \u3000\u3000\u8fd9\u9762\u8c8c\u72de\u6076\u7684\u72ec\u817f\u8001\u9b54\uff0c\u6b63\u662f\u4e0e\u201c\u8ffd\u9b42\u94c3\u201d\u53f8\u9a6c\u656c\u9f50\u540d\uff0c\u5728\u897f\u5317\u9053\u4e0a\uff0c\u4eba\u79f0\u4e3a\u201c\u585e\u5916\u53cc \u6b8b\u201d\u7684\u201c\u72ec\u811a\u98de\u9b54\u201d\u674e\u4e3e\u3002\u6b64\u8001\u6027\u683c\u6b8b\u66b4\uff0c\u9b3c\u8bc8\u767e\u51fa\u3002\u5728\u5e84\u4e3b\u8eab\u540e\u770b\u5230\u5c55\u767d\u7adf\u80fd\u628a\u81ea\u5df1\u7684\u8001 \u53cb\u201c\u8ffd\u9b42\u94c3\u201d\u4e00\u638c\u9707\u51fa\u5708\u5916\uff0c\u5f53\u65f6\u4e5f\u662f\u4e00\u6014\u3002 \u3000\u3000\u4f46\u4ed6\u53ef\u4e0d\u50cf\u522b\u4eba\uff0c\u770b\u5230\u51fa\u4e4e\u610f\u6599\u7684\u4e8b\uff0c\u4fbf\u60ca\u60ee\u7eb3\u7f55\uff0c\u7eb7\u7eb7\u731c\u6d4b\u8fd9\u4e0d\u8d77\u773c\u5c11\u5e74\u7684\u6765\u5386\u51fa \u8eab\uff1b\u4ed6\u5374\u4e00\u773c\u4fbf\u770b\u51fa\u95e8\u9053\uff0c\u773c\u524d\u5c11\u5e74\u53ea\u662f\u5185\u529b\u96c4\u539a\uff0c\u5e76\u6ca1\u6709\u4ec0\u4e48\u51fa\u5947\u7684\u624b\u6cd5\u3002\u56e0\u6b64\uff0c\u4ed6\u773c\u73e0 \u4e00\u8f6c\uff0c\u8ba1\u4e0a\u5fc3\u6765\uff0c\u60f3\u4ee5\u81ea\u5df1\u6210\u540d\u6c5f\u6e56\u7684\u72ec\u95e8\u624b\u6cd5\u201c\u95ea\u7535\u8ffd\u98ce\u64d2\u62ff\u624b\u201d\uff0c\u53bb\u5236\u670d\u5c11\u5e74\u3002 \u3000\u3000\u8001\u602a\u7269\u60f3\u5230\u5c31\u505a\uff0c\u5728\u201c\u94f6\u7bab\u593a\u9b42\u201d\u7ae0\u58eb\u9e4f\u51fa\u9762\u4e0e\u5c55\u767d\u7b54\u8bdd\u7684\u5f53\u513f\uff0c\u66b4\u559d\u4e00\u58f0\uff0c\u8eab\u5f62\u7535\u5c04 \u800c\u8d77\uff0c\u534a\u7a7a\u4e2d\u4e00\u5f0f\u201c\u91d1\u8c79\u9732\u722a\u201d\uff0c\u731b\u5411\u5c55\u81ea\u201c\u7ecf\u6e20\u201d\u91cd\u7a74\u6293\u53bb\u3002 \u3000\u3000\u5c55\u767d\u7529\u817f\u51fa\u638c\uff0c\u201c\u72ec\u811a\u98de\u9b54\u201d\uff0c\u5355\u817f\u4e00\u70b9\u5730\u9762\uff0c\u5de6\u624b\u4fa5\u4f38\uff0c\u95ea\u7535\u822c\u53c8\u65bd\u51fa\u4e00\u62db\u201c\u5206\u733f\u88c2 \u864e\u201d\u3002 \u3000\u3000\u201c\u72ec\u811a\u98de\u9b54\u201d\u4e24\u62db\u8fde\u73af\u800c\u51fa\uff0c\u5341\u62ff\u4e5d\u7a33\uff0c\u8ba4\u4e3a\u773c\u524d\u5c11\u5e74\u518d\u4e5f\u65e0\u6cd5\u8131\u9003\uff0c\u56e0\u6b64\uff0c\u53e3\u4e2d\u5e76\u559d \u4e86\u58f0\uff1a\u201c\u4f60\u7ed9\u6211\u8eba\u4e0b\uff01\u201d \u3000\u3000\u4e07\u4e5f\u60f3\u4e0d\u5230\u773c\u524d\u5c11\u5e74\u770b\u4f3c\u8fdf\u949d\uff0c\u52a8\u4f5c\u5374\u7075\u5feb\u65e0\u6bd4\uff0c\u4ecd\u80fd\u8f7b\u6613\u95ea\u8fc7\u3002\u201c\u72ec\u811a\u98de\u9b54\u201d\u8c6a\u6c14\u5927 \u53d1\uff0c\u201c\u5378\u5173\u70b9\u5143\u201d\u3001\u201c\u65ad\u7b4b\u622a\u8109\u201d\u3001\u201c\u997f\u9b3c\u652b\u9b42\u201d\uff0c\u6307\u638c\u517c\u65bd\uff0c\u4e00\u8fde\u653b\u51fa\u4e09\u5927\u6740\u62db\u3002 \u3000\u3000\u522b\u770b\u8001\u602a\u7269\u53ea\u6709\u4e00\u6761\u817f\uff0c\u8eab\u6cd5\u771f\u662f\u5947\u5feb\u65e0\u6bd4\uff0c\u8981\u4e0d\u4ed6\u600e\u4e48\u53eb\u201c\u72ec\u811a\u98de\u9b54\u201d\u5462\uff01\u5c24\u5176\u4ed6\u8fd9 \u64d2\u62ff\u624b\u6cd5\uff0c\u540d\u4e3a\u201c\u95ea\u7535\u8feb\u98ce\u201d\uff0c\u65bd\u5c55\u51fa\u6765\u771f\u5982\u817f\u98ce\u95ea\u7535\u4e00\u822c\uff0c\u5feb\u901f\u7edd\u4f26\uff0c\u800c\u4e14\u51fa\u624b\u5947\u5965\u65e0 \u6bd4\u3002 \u3000\u3000\u201c\u72ec\u811a\u586b\u9b54\u201d\u8fd9\u79cd\u5947\u5965\u64d2\u62ff\u624b\u6cd5\uff0c\u5c31\u662f\u4e00\u822c\u6b66\u6797\u9ad8\u624b\u770b\u6765\uff0c\u90fd\u89c9\u773c\u82b1\u7f2d\u4e71\uff0c\u53ef\u662f\uff0c\u8fd9\u770b \u4e0d\u8d77\u773c\u7684\u7eaf\u6734\u5c11\u4e2d\uff0c\u5374\u80fd\u4e0d\u614c\u4e0d\u5fd9\uff0c\u4e00\u4e00\u8eb2\u8fc7\u3002 \u3000\u3000\u4f17\u4eba\u65e0\u4e0d\u6697\u6697\u79f0\u5947\uff0c\u56e0\u4e3a\u5728\u201c\u72ec\u811a\u98de\u9b54\u201d\u5947\u5965\u7684\u64d2\u62ff\u624b\u95ea\u7535\u822c\u8fde\u73af\u653b\u51fb\u4e4b\u4e0b\uff0c\u90a3\u7eaf\u6734\u5c11 \u5e74\u5728\u6307\u98ce\u638c\u5f71\u4e4b\u4e0b\uff0c\u53ea\u662f\u5fae\u4e00\u95ea\u8eab\uff0c\u7565\u4e00\u632b\u6b65\uff0c\u4fbf\u80fd\u628a\u770b\u6765\u9669\u6781\u7684\u6740\u62db\u8eb2\u8fc7\uff0c\u4f46\u770b\u4ed6\u90a3\u4efd\u695e \u695e\u6014\u6014\u7684\u6837\u5b50\uff0c\u5206\u660e\u4e0d\u662f\u7528\u4ec0\u4e48\u5947\u5965\u8eab\u6cd5\u8eb2\u8fc7\u7684\uff0c\u800c\u53ea\u662f\u4e34\u673a\u5e94\u53d8\u4e4b\u62db\u5f0f\u53ca\u8eab\u6cd5\uff0c\u624d\u8eb2\u5f00\u4ed6 \u7684\u4e00\u51fb\u3002 \u3000\u3000\u5c24\u5176\u90a3\u5c11\u5e74\u77aa\u7740\u4e00\u53cc\u660e\u4eae\u7684\u5927\u773c\u775b\uff0c\u5168\u795e\u6ce8\u89c6\u7740\u201c\u72ec\u811a\u98de\u9b54\u201d\u51fa\u62db\u53d8\u62db\u7684\u624b\u6cd5\uff0c\u5bf9\u4e8e\u8eb2 \u62db\u8fd8\u624b\u7684\u4e8b\u53cd\u597d\u50cf\u4e0d\u5927\u7559\u610f\uff0c\u770b\u6837\u5b50\u5c31\u5982\u540c\u5e08\u7236\u7ed9\u5f92\u5f1f\u5582\u62db\u7684\u60c5\u5f62\u5dee\u4e0d\u591a\uff0c\u5f92\u5f1f\u7528\u7684\u62db\u5f0f\u90fd \u662f\u5e08\u7236\u6559\u7684\uff0c\u518d\u5feb\u4e5f\u6253\u4e0d\u5230\u5e08\u7236\u8eab\u4e0a\uff0c\u800c\u5e08\u7236\u7528\u4e0d\u7740\u5b58\u5fc3\u8eb2\u95ea\uff0c\u4fbf\u53ef\u8eb2\u5f00\u5f92\u5f1f\u7684\u62db\u5f0f\uff0c\u6240\u4ee5 \u7528\u4e0d\u7740\u9632\u5907\u5f92\u5f1f\u600e\u4e48\u6253\uff0c\u53ea\u662f\u6ce8\u610f\u5f92\u5f1f\u51fa\u62db\u53d8\u62db\u7684\u624b\u6cd5\u6b65\u4f4d\u5bf9\u4e0d\u5bf9\u5c31\u662f\u4e86 \u3000\u3000\u8fd9\u6837\u770b\u6765\uff0c\u773c\u524d\u8fd9\u4e0d\u8d77\u773c\u7684\u5c11\u5e74\uff0c\u5c82\u4e0d\u662f\u6bd4\u4eab\u540d\u6b66\u6797\u6570\u5341\u5e74\u7684\u201c\u72ec\u811a\u98de\u9b54\u201d\u674e\u4e3e\u7684\u6b66 \u529f\uff0c\u8fd8\u8981\u9ad8\u5417\uff1f\u8fd9\u771f\u662f\u4ee4\u4eba\u4e0d\u53ef\u601d\u8bae\uff01\u56e0\u6b64\uff0c\u5f53\u573a\u90a3\u4e48\u591a\u6b66\u6797\u9876\u5c16\u9ad8\u624b\uff0c\u7adf\u90fd\u53d1\u8d77\u5446\u6765\uff0c\u5168 \u795e\u8d2f\u6ce8\u5730\u671b\u7740\u573a\u4e2d\u4e8c\u4eba\u52a8\u624b\u2026\u2026\u201c\u72ec\u811a\u98de\u9b54\u201d\u674e\u4e3e\uff0c\u6bd4\u522b\u4eba\u66f4\u52a0\u5403\u60ca\uff0c\u4e00\u8fb9\u52a8\u7740\u624b\uff0c\u4e00\u8fb9\u5fc3 \u4e2d\u7535\u8f6c\uff0c\u6697\u60f3\uff1a\u201c\u771f\u662f\u78b0\u5230\u9b3c\u4e86\uff01\u51ed\u8001\u592b\u72ec\u9738\u6b66\u6797\u7684\u64d2\u62ff\u624b\u6cd5\uff0c\u7adf\u4e0d\u80fd\u5236\u4f7f\u4e00\u4e2a\u540d\u4e0d\u89c1\u7ecf\u4f20 \u7684\u5c11\u5e74\uff1f\u8fd9\u4e2a\u9762\u5b50\u53ef\u7b97\u4e22\u5927\u4e86\uff01\u201d \u3000\u3000\u201c\u72ec\u811a\u98de\u9b54\u201d\u8fd9\u6837\u60f3\u7740\uff0c\u624b\u5e95\u4e0b\u53ef\u5c31\u66f4\u52a0\u72e0\u6bd2\uff0c\u65bd\u51fa\u624b\u6cd5\uff0c\u62db\u62db\u5747\u53ef\u81f4\u547d\u3002\u4e8b\u5b9e\u4e0a\uff0c\u5c55 \u767d\u80fd\u591f\u53ca\u65f6\u8eb2\u8fc7\u201c\u72ec\u811a\u98de\u9b54\u201d\u51cc\u5389\u6740\u624b\uff0c\u5b8c\u5168\u662f\u4e0b\u610f\u8bc6\u7684\u53cd\u5e94\uff0c\u5fc3\u4e2d\u4e00\u70b9\u4e5f\u4e0d\u77e5\u5176\u4e2d\u5965\u5999\u3002 \u3000\u3000\u5f53\u7136\uff0c\u8fd9\u8981\u5f52\u529f\u4e8e\u5c55\u767d\u5f53\u521d\u5728\u57fa\u672c\u529f\u592b\u4e0a\u4e0b\u8fc7\u82e6\u529f\uff0c\u540c\u65f6\uff0c\u5c55\u767d\u9ed8\u4e60\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b \u5377\u300b\u4e0a\u6b63\u5b97\u5fc3\u6cd5\uff0c\u5185\u529f\u5927\u589e\uff0c\u8033\u806a\u76ee\u654f\u8d85\u4e8e\u5e38\u4eba\uff0c\u53c8\u52a0\u4e0a\u4ed6\u65e0\u5fc3\u4e2d\u88ab\u6253\u901a\u5468\u8eab\u5947\u7ecf\u516b\u8109\uff0c\u53cd \u5e94\u7279\u5feb\uff0c\u5fc3\u968f\u610f\u52a8\uff0c\u610f\u5728\u795e\u5148\uff0c\u6545\u80fd\u5728\u201c\u72ec\u811a\u98de\u9b54\u201d\u95ea\u7535\u51cc\u5389\u653b\u52bf\u4e4b\u4e0b\uff0c\u4ecd\u80fd\u89c1\u62db\u8eb2\u62db\uff0c\u89c1 \u5f0f\u8eb2\u5f0f\uff0c\u4e00\u4e00\u8eb2\u5f00\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u5c55\u767d\u5374\u5fd8\u8bb0\u653b\u51fb\u4e86\u3002\u4ed6\u4e00\u8fb9\u6643\u80a9\u9519\u8eab\uff0c\u968f\u7740\u201c\u72ec\u811a\u98de\u9b54\u201d\u7684\u51cc\u5389\u62db\u5f0f\uff0c\u95ea\uff0c\u5c55\uff0c \u817e\uff0c\u5a1c\uff1b\u4e00\u8fb9\u77aa\u5927\u4e86\u773c\u775b\u6ce8\u610f\u201c\u72ec\u811a\u98de\u9b54\u201d\u7684\u5947\u5999\u6740\u624b\uff0c\u8c01\u8bf4\u4ed6\u50bb\uff1f\u4ed6\u7ade\u5728\u52a8\u624b\u76f8\u640f\u4e4b\u95f4\uff0c \u8ddf\u654c\u4eba\u5b66\u4e60\u8d77\u5de7\u5999\u62db\u5f0f\u6765\u4e86\u3002\u5c31\u8fd9\u6837\uff0c\u201c\u72ec\u811a\u98de\u9b54\u201d\u6beb\u4e0d\u77e5\u60c5\u4e2d\u8ba9\u5c55\u767d\u5077\u5b66\u4e86\u62db\u5f0f\uff0c\u8001\u602a\u7269 \u8fd8\u4e0d\u81ea\u77e5\uff0c\u5feb\u653b\u5feb\u6253\uff0c\u6643\u773c\u4e4b\u95f4\uff0c\u653b\u51fa\u4e86\u4e8c\u4e09\u5341\u62db\u3002 \u7b2c\u5341\u4e94\u7ae0\u3000\u201c\u795e\u9a74\u94c1\u80c6\u201d>> \u53e4\u9f99\u300a\u5251\u5ba2\u884c\u300b \u7b2c\u5341\u4e94\u7ae0\u3000\u201c\u795e\u9a74\u94c1\u80c6\u201d \u3000\u3000\u8ddf\u524d\u90a3\u4e48\u591a\u6b66\u6797\u9ad8\u624b\uff0c\u867d\u7136\u90fd\u662f\u8d70\u5357\u95ef\u5317\uff0c\u7ecf\u591a\u8bc6\u5e7f\uff0c\u4f46\u4e5f\u6ca1\u6709\u95f4\u8fc7\u8fd9\u6837\u7684\u6253\u6cd5\uff0c\u56e0 \u6b64\u2026\u3002 \u3000\u3000\u4e00\u4e2a\u4e2a\u76ee\u4e0d\u8f6c\u775b\u671b\u7740\u4e8c\u4eba\u52a8\u624b\u76f8\u640f\uff0c\u597d\u50cf\u770b\u70ed\u95f9\u4e00\u822c\uff0c\u7adf\u5fd8\u4e86\u8fd9\u662f\u4e00\u573a\u4ee5\u6027\u547d\u4e3a\u8d4c\u7684\u751f \u6b7b\u640f\u6597\u3002 \u3000\u3000\u4f46\u662f\u201c\u8ffd\u9b42\u94c3\u201d\u53f8\u9a6c\u656c\u7684\u786e\u662f\u79f0\u5f97\u4e0a\u8001\u597d\u5de8\u6ed1\uff0c\u4ed6\u5728\u4f17\u591a\u9ad8\u624b\u4e4b\u4e2d\u62a2\u5148\u51fa\u624b\uff0c\u773c\u770b\u4e00\u62db \u201c\u6398\u9b42\u593a\u9b44\u201d\uff0c\u5373\u5c06\u6151\u670d\u4e09\u5251\u4e00\u9e3e\uff0c\u7a81\u7136\u534a\u8def\u4e00\u4e2a\u50bb\u5c0f\u5b50\uff0c\u4e00\u638c\u7adf\u5c06\u4ed6\u903c\u9000\uff0c\u8fd9\u65e0\u5f02\u5728\u4eba\u524d \u4f7f\u4ed6\u683d\u4e86\u4e00\u4e2a\u5927\u7b4b\u6597\uff0c\u8001\u602a\u7269\u5728\u897f\u5317\u9053\u4e0a\u6210\u540d\u591a\u5e74\uff0c\u4ece\u6765\u65e0\u4eba\u6562\u60f9\uff0c\u54ea\u91cc\u5fcd\u5f97\u4e0b\u8fd9\u4e2a\u6298\u8fb1\uff1f \u3000\u3000\u56e0\u6b64\uff0c\u5728\u522b\u4eba\u51fa\u795e\u5730\u671b\u7740\u201c\u72ec\u811a\u7535\u9b54\u201d\u4e0e\u5c55\u767d\u820d\u547d\u76f8\u6251\u4e4b\u9645\uff0c\u4ed6\u5374\u5728\u5fc3\u4e2d\u6697\u6253\u633d\u56de\u989c\u9762 \u7684\u4e3b\u610f\u3002 \u3000\u3000\u4ee5\u201c\u8ffd\u9b42\u94c3\u201d\u5728\u6c5f\u6e56\u4e0a\u7684\u540d\u671b\u5730\u4f4d\uff0c\u5f53\u7136\u4e0d\u597d\u610f\u601d\u4e0e\u201c\u72ec\u811a\u98de\u9b54\u201d\u5408\u6218\u5c55\u767d\uff0c\u53c8\u770b\u5230\u5c55 \u767d\u4e0e\u201c\u72ec\u811a\u98de\u9b54\u201d\u7adf\u7f20\u6597\u4e86\u4e8c\u4e09\u5341\u62db\uff0c\u4ecd\u7136\u4e0d\u5206\u80dc\u8d1f\u63d2\u4e0d\u8fdb\u624b\u53bb\uff0c\u4fbf\u62a2\u76ee\u56db\u987e\uff0c\u770b\u5230\u201c\u4e09\u5251 \u4e00\u9e3e\u201d\u5446\u7ad9\u5728\u4e00\u8fb9\uff0c\u56db\u53cc\u773c\u775b\u77aa\u4e86\u4e2a\u6eda\u5706\uff0c\u9762\u9732\u60ca\u8be7\u4e4b\u8272\uff0c\u4e00\u9f50\u6ce8\u89c6\u7740\u5c55\u767d\u529b\u6218\u201c\u72ec\u811a\u98de \u9b54\u201d\uff0c\u4f3c\u4e4e\u662f\u8fde\u7f6e\u8eab\u4f55\u5730\u90fd\u5fd8\u4e86\u3002 \u3000\u3000\u201c\u8ffd\u9b42\u94c3\u201d\u5fc3\u60f3\uff1a\u201c\u4f55\u4e0d\u8d81\u6b64\u673a\u4f1a\uff0c\u5148\u628a\u56db\u4e2a\u5c0f\u8f88\u6536\u62fe\u4e86\uff0c\u56de\u5934\u518d\u8bbe\u6cd5\u5904\u7406\u90a3\u50bb\u5c0f \u5b50\uff01\u201d \u3000\u3000\u201c\u8ffd\u9b42\u94c3\u201d\u4e3b\u610f\u5df2\u5b9a\uff0c\u7f13\u6b65\u6b3a\u8fd1\u4e09\u5251\u4e00\u9e3e\u8eab\u524d\uff0c\u563f\u563f\u2014\u2014\u9635\u51b7\u7b11\uff0c\u6c89\u8d50\u9053\uff1a\u201c\u4f60\u4eec\u56db\u4e2a \u9f20\u8f88\uff0c\u8fd8\u8981\u7b49\u6211\u8001\u4eba\u5bb6\u8d39\u4e8b\u5417\uff1f\u201d \u3000\u3000\u8bf4\u7740\u201c\u53ee\u76ef\u53ee\uff01\u201d\u4e00\u9707\u624b\u4e2d\u201c\u8ffd\u9b42\u94c3\u201d\uff0c\u8106\u97f3\u9707\u8033\u3002 \u3000\u3000\u4e09\u5251\u9661\u7136\u4e00\u60ca\uff0c\u7acb\u523b\u8f6c\u8fc7\u8138\u6765\uff0c\u4e00\u770b\u662f\u201c\u8ffd\u9b42\u94c3\u201d\uff0c\u4e0d\u81ea\u7981\u5730\u5404\u81ea\u63d0\u8d77\u957f\u5251\uff0c\u53c8\u89c1\u53f8\u9a6c \u656c\u4e24\u622a\u65ad\u7709\u8038\u7acb\uff0c\u4e00\u53cc\u602a\u76ee\u5706\u7ffb\u3002 \u3000\u3000\u72ec\u81c2\u9ad8\u4e3e\u201c\u8ffd\u9b42\u94c3\u201d\uff0c\u6837\u5b50\u597d\u50cf\u51f6\u795e\u6076\u9b3c\u4e00\u822c\uff0c\u4e0d\u7531\u5404\u81ea\u5fc3\u4e2d\u6253\u4e86\u4e2a\u51b7\u98a4\uff0c\u4ff1\u5404\u540e\u9000\u4e86 \u4e00\u6b65\u3002 \u3000\u3000\u4f46\u6a0a\u7d20\u9e3e\u4e00\u53cc\u660e\u7738\u4ecd\u7136\u76ef\u7740\u5c55\u767d\uff0c\u5bf9\u53f8\u9a6c\u656c\u6b65\u5230\u8eab\u8fb9\u604d\u5982\u672a\u89c1\u3002 \u3000\u3000\u53f8\u9a6c\u656c\u5374\u4e0d\u7ba1\u8fd9\u4e9b\uff0c\u9661\u7136\u4e00\u9707\u624b\u4e2d\u201c\u8ffd\u9b42\u94c3\u201d\uff0c\u53e3\u4e2d\u201c\u54c7\uff01\u201d\u7684\u4e00\u58f0\u95f7\u543c\uff0c\u4f5c\u52bf\u6b32\u6251\u3002 \u3000\u3000\u201c\u6a0a\u6c0f\u4e09\u5251\u201d\u9762\u4e0a\u4e00\u60ca\uff0c\u88ab\u5413\u5f97\u53c8\u5404\u81ea\u540e\u9000\u4e00\u6b65\u3002 \u3000\u3000\u4f46\u53f8\u9a6c\u656c\u5374\u5e76\u672a\u51fa\u624b\uff0c\u53ea\u662f\u865a\u58f0\u6050\u5413\uff0c\u89c1\u201c\u6a0a\u6c0f\u4e09\u5251\u201d\u88ab\u5413\u5f97\u90a3\u4e2a\u6837\u5b50\uff0c\u4e0d\u7531\u4ef0\u8138\u54c8\u54c8 \u4e00\u9635\u72e1\u7b11\uff0c\u795e\u6001\u5f97\u610f\u5df2\u6781\u3002 \u3000\u3000\u201c\u6a0a\u6c0f\u4e09\u5251\u201d\u89c1\u81ea\u5df1\u88ab\u8001\u602a\u7269\u5982\u6b64\u620f\u5f04\uff0c\u4e0d\u7531\u53c8\u7f9e\u53c8\u6012\uff0c\u60f3\u8d77\u81ea\u5df1\u5f1f\u5144\u4e09\u4eba\u4ee5\u53ca\u7236\u4eb2\u5728 \u6b66\u6797\u4e2d\u7684\u540d\u671b\u5730\u4f4d\uff0c\u5982\u4eca\u7adf\u88ab\u4eba\u5982\u6b64\u620f\u5f04\u3002 \u3000\u3000\u60e7\u5404\u6124\u6012\u586b\u81ba\uff0c\u5f1f\u5144\u4e09\u4eba\u4e00\u4f7f\u773c\u8272\uff0c\u8d81\u7740\u201c\u8ffd\u9b42\u94c3\u201d\u4ef0\u5929\u53d1\u7b11\u7684\u5f53\u513f\u3002 \u3000\u3000\u4e09\u5251\u9f50\u51fa\uff0c\u731b\u88ad\u201c\u8ffd\u9b42\u94c3\u201d\u5589\u4e0b\u201c\u7487\u7391\u201d\u3001\u80f8\u524d\u201c\u4e09\u9633\u201d\u3001\u4e0b\u8179\u201c\u6c14\u6d77\u201d\u4e09\u5927\u8981\u7a74\uff01 \u3000\u3000\u9547\u6c5f\u201c\u6a0a\u6c0f\u4e09\u5251\u201d\uff0c\u4ee5\u5bb6\u4f20\u201c\u8feb\u98ce\u5251\u6cd5\u201d\u79f0\u96c4\u6b66\u6797\uff0c\u5251\u62db\u795e\u5947\u5feb\u901f\u662f\u5176\u7279\u957f\uff0c\u5c24\u5176\u4ed6\u5f1f \u5144\u4e09\u4eba\u5408\u8d77\u624b\u6765\u201c\u4e09\u5251\u4ea4\u8f89\u201d\u3002 \u3000\u3000\u5f53\u4eca\u6b66\u6797\u5f88\u5c11\u6709\u4eba\u80fd\u591f\u62b5\u6321\u4ed6\u5f1f\u5144\u4e09\u4eba\u5fc3\u4e0e\u795e\u4f1a\uff0c\u52a8\u4f5c\u9ed8\u5951\uff0c\u4e09\u652f\u51b7\u68ee\u957f\u5251\uff0c\u5206\u8fdb\u5408 \u51fb\uff0c\u540c\u65f6\u653b\u5411\u53f8\u9a6c\u656c\u4e0a\u4e2d\u4e0b\u4e09\u76d8\u8981\u5bb3\u3002 \u3000\u3000\u201c\u5c0f\u8f88\uff01\u5c14\u662f\u627e\u6b7b\uff01\u201d \u3000\u3000\u53f8\u9a6c\u656c\u66b4\u8d50\u4e00\u58f0\uff0c\u6643\u80a9\u62fe\u817f\uff0c\u8eb2\u8fc7\u4e0a\u3001\u4e0b\u4e24\u5251\uff0c\u72ec\u81c2\u4e00\u62a1\u624b\u4e2d\u201c\u8ffd\u9b42\u94c3\u201d\uff0c\u201c\u53ee\uff01\u201d\u4e00 \u58f0\u91d1\u94c1\u4ea4\u9e23\u2026\u2026 \u3000\u3000\u8001\u4e8c\u201c\u8ffd\u98ce\u5251\u201d\u6a0a\u6770\u88ab\u9707\u5f97\u4e00\u8def\u8ddf\u8def\uff0c\u76f4\u51b2\u51fa\u4e94\u516d\u6b65\u53bb\uff0c\u8eab\u5f62\u5c1a\u672a\u7ad9\u7a33\uff0c\u5e76\u89c9\u5f97\u864e\u53e3\u5982 \u88ab\u706b\u70e7\uff0c\u957f\u5251\u51e0\u4e4e\u8131\u624b\u3002 \u3000\u3000\u53f8\u9a6c\u656c\u54c8\u54c8\u72c2\u7b11\u58f0\u4e2d\u3002 \u3000\u3000\u201c\u8ffd\u9b42\u94c3\u201d\u6447\u8d77\u4e00\u4e32\u9707\u6151\u5fc3\u9b42\u7684\u9510\u755c\uff0c\u8ddf\u8e2a\u800c\u81f3\uff0c\u731b\u7838\u8e09\u8dc4\u6b32\u5012\u7684\u201c\u8ffd\u98ce\u5251\u201d\u540e\u8111\u3002 \u3000\u3000\u773c\u89c1\u201c\u8ffd\u98ce\u5251\u201d\u5c31\u8981\u6b7b\u5728\u201c\u8ffd\u9b42\u94c3\u201d\u4e0b\u3002 \u3000\u3000\u6a0a\u7d20\u9e3e\u6479\u7136\u56de\u9996\uff0c\u770b\u5230\u4e8c\u54e5\u5371\u5728\u65e6\u5915\uff0c\u4e00\u58f0\u60ca\u547c\uff0c\u594b\u4e0d\u987e\u8eab\uff0c\u626c\u8d77\u4e00\u53cc\u7389\u638c\uff0c\u731b\u5411\u201c\u8ffd \u9b42\u94c3\u201d\u6251\u6765\u3002 \u3000\u3000\u540c\u65f6\u201c\u6233\u60c5\u5251\u201d\u6a0a\u4fca\uff0c\u4e0e\u201c\u6469\u4e91\u5251\u201d\u6a0a\u82f1\uff0c\u770b\u5230\u8001\u4e8c\u5931\u62db\u9047\u9669\uff0c\u5927\u559d\u4e00\u58f0\uff0c\u4e24\u652f\u957f\u5251\uff0c \u4e00\u6307\u53f8\u9a6c\u656c\u5de6\u808b\uff0c\u4e00\u6307\u53f8\u9a6c\u656c\u540e\u5fc3\uff0c\u540c\u65f6\u653b\u5230\u3002 \u3000\u3000\u53f8\u9a6c\u656c\u201c\u8ffd\u9b42\u94c3\u201d\u5411\u4e0b\u4e00\u6309\uff0c\u201c\u8ffd\u98ce\u5251\u201d\u95f7\u54fc\u4e00\u58f0\uff0c\u591a\u4e8f\u4ed6\u767e\u5fd9\u4e2d\u4f4e\u5934\uff0c\u8eb2\u8fc7\u4e86\u8981\u5bb3\uff0c \u88ab\u53f8\u9a6c\u656c\u201c\u8ffd\u9b42\u94c3\u201d\u6309\u5728\u53f3\u80a9\u4e4b\u4e0a\uff0c\u6a0a\u6770\u53ea\u89c9\u53f3\u80a9\u5982\u53d7\u5343\u65a4\u91cd\u9524\uff0c\u75db\u5f7b\u5fc3\u8151\uff0c\u4e00\u5934\u683d\u5012\u5728 \u5730\uff0c\u76f4\u6eda\u51fa\u8001\u8fd8\u2026\u2026 \u3000\u3000\u201c\u53ee\uff01\u53ee\uff01\u201d\u4e24\u58f0\u8106\u54cd\uff0c\u53f8\u9a6c\u656c\u94c3\u4f24\u201c\u8ffd\u98ce\u5251\u201d\u540e\u53ca\u65f6\u56de\u624b\uff0c\u8361\u5f00\u4e86\u8eab\u540e\u88ad\u6765\u7684\u4e24\u67c4\u957f \u5251\u3002 \u3000\u3000\u201c\u6233\u60c5\u5251\u201d\u4e0e\u201c\u6469\u4e91\u5251\u201d\uff0c\u5144\u5f1f\u4e8c\u4eba\u88ab\u201c\u8ffd\u9b42\u94c3\u201d\u9707\u5f97\u8eab\u5f62\u4e71\u6643\u2026\u2026 \u3000\u3000\u53f8\u9a6c\u656c\u8eab\u5f62\u5982\u670b\u98ce\u95ea\u7535\uff0c\u94c3\u4f24\u6a0a\u6c0f\u4e09\u5251\u4e4b\u8001\u4e8c\uff0c\u53cd\u624b\u6447\u94c3\uff0c\u9707\u5f00\u4e09\u5251\u4e2d\u8001\u5927\uff0c\u8001\u4e09\u8eab\u540e \u88ad\u6765\u7684\u4e24\u652f\u957f\u5251\uff0c\u8eab\u5f62\u6beb\u4e0d\u6ede\u7559\uff0c\u5c31\u5730\u4e00\u65cb\uff0c\u907f\u5f00\u6a21\u7d20\u86ee\u7684\u53cc\u638c\uff0c\u201c\u8ffd\u9b42\u94c3\u201d\u62a1\u8d77\u4e00\u73af\u91d1 \u8292\u3002 \u3000\u3000\u201c\u53ee\u53ee\u53ee\u2026\u2026\u201d\u9510\u97f3\u76c8\u8033\uff0c\u731b\u5411\u6a0a\u7d20\u9e3e\u9165\u80f8\u4e0a\u7838\u6765\u3002 \u3000\u3000\u53f8\u9a6c\u656c\u4e0d\u6127\u662f\u897f\u5317\u9053\u4e0a\u4e00\u5927\u9ad8\u624b\uff0c\u529b\u6218\u56db\u4eba\uff0c\u62db\u5f0f\u8fde\u73af\u800c\u51fa\uff0c\u6df1\u5982\u4e00\u6c14\u5475\u6210\u3002 \u3000\u3000\u6a0a\u7d20\u9e3e\u6b66\u529f\u4e0d\u53ca\u4e09\u5144\uff0c\u5979\u4e09\u4e2a\u54e5\u54e5\u5408\u8d77\u624b\u6765\uff0c\u8fd8\u6321\u4e0d\u4f4f\u201c\u8ffd\u9b42\u94c3\u201d\u7684\u5168\u529b\u4e00\u51fb\uff0c\u5982\u4eca\u5979 \u4e24\u4e2a\u54e5\u54e5\u88ab\u9707\u9000\uff0c\u4e00\u4e2a\u54e5\u54e5\u88ab\u6253\u4f24\uff0c\u5bf9\u53f8\u9a6c\u656c\u5a01\u731b\u7edd\u4f26\u3001\u8fc5\u903e\u9634\u98ce\u95ea\u7535\u7684\u62db\u5f0f\u66f4\u52a0\u65e0\u6cd5\u62db \u67b6\u3002 \u3000\u3000\u5979\u53cc\u638c\u843d\u7a7a\uff0c\u7acb\u611f\u9762\u524d\u4e00\u82b1\uff0c\u52b2\u98ce\u538b\u4f53\uff0c\u201c\u8ffd\u9b42\u94c3\u201d\u631f\u7740\u9707\u6444\u4eba\u5fc3\u7684\u9510\u97f3\uff0c\u5982\u6cf0\u5c71\u538b\u9876 \u822c\u5411\u7740\u81ea\u5df1\u80f8\u524d\u7f69\u6765\uff0c\u4e0d\u7531\u7c89\u8138\u60e8\u767d\uff0c\u5a07\u547c\u51fa\u58f0\u2026\u3002\xb7 \u3000\u3000\u7a81\u7136\uff0c\u8fdc\u5904\u4f20\u6765\u4e00\u58f0\u6c89\u559d\uff0c\u58f0\u97f3\u867d\u7136\u4e0d\u5927\uff0c\u5374\u5982\u6c34\u94f6\u6cfb\u5730\uff0c\u4e00\u4e2a\u5b57\u4e00\u4e2a\u5b57\u5f88\u6e05\u6670\u5730\u704c\u8fdb \u53f8\u9a6c\u656c\u8033\u4e2d\uff1a\u201c\u53f8\u9a6c\u5c0f\u513f\uff01\u80c6\u6562\u901e\u51f6\u6740\u4eba\uff1f\u8fd8\u4e0d\u7ed9\u6211\u4f4f\u624b\uff01\u201d \u3000\u3000\u53f8\u9a6c\u656c\u95fb\u58f0\u5fc3\u60ca\uff0c\u7115\u7136\u6536\u624b\u505c\u8eab\uff0c\u626d\u5934\u4e00\u770b\u2026\u2026 \u3000\u3000\u201c\u6258\uff01\u6258\uff01\u6258\uff01\u2026\u2026\u201d \u3000\u3000\u4e00\u4e2a\u5e9e\u5927\u8eab\u5f71\u8dcc\u8dcc\u649e\u649e\u51b2\u8fd1\u53f8\u9a6c\u656c\u8eab\u524d\uff0c\u53f8\u9a6c\u656c\u6025\u7528\u201c\u8ffd\u9b42\u94c3\u201d\u4e00\u6321\uff0c\u628a\u90a3\u4eba\u6321\u4f4f\uff0c\u5b9a \u775b\u4e00\u770b\uff0c\u539f\u6765\u662f\u4ed6\u81ea\u5df1\u7684\u8001\u642d\u6321\u201c\u72ec\u811a\u98de\u9b54\u201d\u674e\u4e3e\u3002 \u3000\u3000\u8fd9\u4e00\u6765\u53ef\u628a\u201c\u8ffd\u9b42\u94c3\u201d\u53f8\u9a6c\u656c\u7ed9\u5f04\u50bb\u4e86\uff0c\u518d\u4e00\u770b\u201c\u72ec\u811a\u98de\u9b54\u201d\u8138\u8272\u60e8\u767d\uff0c\u7259\u5173\u7d27\u54ac\uff0c\u770b \u6837\u5b50\u7adf\u4f3c\u53d7\u4e86\u6781\u91cd\u7684\u5185\u4f24\u3002 \u3000\u3000\u201c\u83ab\u975e\u6211\u8fd9\u4f19\u4f34\uff0c\u7adf\u88ab\u90a3\u4e0d\u8131\u4e73\u81ed\u7684\u5c11\u5e74\u6253\u8d25\u4e86\uff1f\u201d \u3000\u3000\u53f8\u9a6c\u656c\u5fc3\u4e2d\u5403\u60ca\uff0c\u62ac\u5934\u4e00\u770b\uff0c\u5c55\u767d\u6b63\u7ad9\u7acb\u5728\u90a3\u91cc\uff0c\u4e00\u53cc\u660e\u6f88\u7684\u5927\u773c\u775b\u671b\u7740\u4ed6\u70af\u70af\u653e\u5149\uff0c \u8fd9\u773c\u5149\u4f7f\u53f8\u9a6c\u656c\u5403\u4e86\u4e00\u60ca\uff0c\u6b64\u65f6\u4ed6\u624d\u7b97\u770b\u51fa\u6765\uff0c\u773c\u524d\u8fd9\u4e2a\u4e0d\u8d77\u773c\u7684\u5c11\u5e74\uff0c\u5185\u529f\u7adf\u662f\u7cbe\u6e5b\u65e0 \u6bd4\uff0c\u82e5\u4e0d\u7136\u4e0d\u4f1a\u6709\u8fd9\u79cd\u5145\u8db3\u7684\u773c\u795e\u3002\u4e0d\u8fc7\uff0c\u8981\u8bf4\u51ed\u4ed6\u8fd9\u70b9\u5e74\u7eaa\uff0c\u80fd\u628a\u6210\u540d\u591a\u5e74\u7684\u201c\u72ec\u811a\u98de \u9b54\u201d\u6253\u8d25\uff0c\u8fd9\u662f\u53f8\u9a6c\u656c\u65e0\u8bba\u5982\u4f55\u4e0d\u4f1a\u76f8\u4fe1\u7684\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u8fd9\u6a2a\u884c\u897f\u5317\u9053\u4e0a\u65e0\u4eba\u6562\u60f9\u7684\u6b66\u6797\u9876\u5c16\u4eba\u7269\u201c\u72ec\u811a\u98de\u9b54\u201d\u674e\u4e3e\uff0c\u8fd8\u771f\u662f\u88ab\u5c55\u767d\u4e00 \u638c\u9707\u4f24\u7684\u3002 \u3000\u3000\u539f\u6765\u5c55\u767d\u9189\u5fc3\u201c\u72ec\u811a\u98de\u9b54\u201d\u7684\u62db\u5f0f\u5947\u5965\uff0c\u89e6\u53d1\u4e86\u89c1\u4e60\u6b66\u529f\u7684\u6d53\u539a\u5174\u8da3\uff0c\u52a8\u624b\u4e2d\u95f4\uff0c\u53ea\u987e \u8d2a\u7740\u201c\u72ec\u811a\u98de\u9b54\u201d\u7684\u4e00\u62db\u4e00\u5f0f\uff0c\u65bd\u6cd5\u6b65\u4f4d\uff0c\u53cd\u800c\u628a\u4e0e\u654c\u4eba\u640f\u6597\u3001\u51fa\u62db\u4f24\u654c\u7684\u4e8b\u7ed9\u5fd8\u4e86\u3002 \u3000\u3000\u4e00\u5473\u968f\u7740\u201c\u72ec\u811a\u98de\u9b54\u201d\u8f6c\uff0c\u77aa\u5927\u773c\u775b\u770b\u7740\u201c\u72ec\u811a\u98de\u9b54\u201d\u53d1\u62db\u53d8\u62db\uff0c\u201c\u72ec\u811a\u98de\u9b54\u201d\u600e\u4f1a\u77e5 \u9053\u5c55\u767d\u662f\u5728\u5411\u4ed6\u5077\u5b66\u6b66\u529f\uff1f\u4f46\u5c55\u767d\u8ddf\u4ed6\u8fd9\u79cd\u6253\u6cd5\uff0c\u5374\u662f\u4ed6\u95ef\u8361\u6c5f\u6e56\u56db\u4e94\u5341\u5e74\u6765\u4ece\u672a\u9047\u5230\u7684\u602a \u4e8b\u3002 \u3000\u3000\u8001\u602a\u7269\u89c1\u5c55\u767d\u53ea\u662f\u4e0d\u8fd8\u624b\uff0c\u800c\u81ea\u5df2\u8fde\u65bd\u6740\u62db\uff0c\u7ade\u88ab\u8fd9\u5c11\u5e74\u50bb\u50bb\u5475\u5475\u5730\u8eb2\u8fc7\u4e86\uff0c\u4e0d\u7531\u8d8a\u6253 \u5fc3\u4e2d\u8d8a\u611f\u5947\u602a\u3002\u8001\u602a\u7269\u6c5f\u6e56\u7ecf\u9a8c\u56fa\u7136\u8001\u5230\uff0c\u4f46\u4e00\u65f6\u4e5f\u4e0d\u660e\u767d\u5c55\u767d\u7528\u610f\u3002\u540e\u6765\u52a8\u624b\u7684\u65f6\u95f4\u4e00 \u957f\uff0c\u53ef\u5c31\u770b\u51fa\u6765\uff0c\u9762\u524d\u5c11\u5e74\u53ea\u6ce8\u5b9a\u4ed6\u51fa\u624b\u53d1\u62db\uff0c\u5206\u660e\u662f\u5077\u4e60\u4ed6\u7684\u624b\u6cd5\u62db\u5f0f\u3002 \u3000\u3000\u201c\u72ec\u811a\u98de\u9b54\u201d\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u5c0f\u5b50\uff01\u5012\u8dd1\u5230\u6211\u8001\u4eba\u5bb6\u9762\u524d\u6765\u6361\u4fbf\u5b9c\u4e86\uff01\u54fc\uff01\u54fc\uff01\u6211\u8981\u4e0d\u7ed9 \u4f60\u5c0f\u5b50\u4e00\u4e2a\u5389\u5bb3\uff0c\u8fd8\u8ba9\u4f60\u5c0f\u4e8e\u628a\u6211\u8001\u4eba\u5bb6\u5f53\u51a4\u5927\u5934\u5462\uff01\u201d \u3000\u3000\u201c\u72ec\u811a\u98de\u9b54\u201d\u601d\u81f3\u6b64\u5904\uff0c\u201c\u5f00\u95e8\u89c1\u5c71\u201d\uff0c\u53cc\u638c\u5411\u5c55\u767d\u9762\u95e8\u6309\u6765\u3002 \u3000\u3000\u56e0\u662f\u8fd1\u8eab\u640f\u6597\uff0c\u201c\u72ec\u811a\u98de\u9b54\u201d\u53cc\u638c\u5947\u5feb\u65e0\u6bd4\uff0c\u5c55\u767d\u6643\u80a9\u6025\u8eb2\uff0c\u9669\u4e9b\u88ab\u8001\u9b54\u53cc\u638c\u6309\u5728\u9762\u95e8 \u4e4b\u4e0a\u3002 \u3000\u3000\u638c\u7f18\u52b2\u98ce\uff0c\u628a\u5c55\u767d\u5de6\u988a\u626b\u5f97\u751f\u75db\uff0c\u5c55\u767d\u5fae\u7136\u4e00\u695e\uff0c\u5c82\u4e0d\u77e5\u8fd9\u4e00\u62db\u8fd8\u662f\u8001\u9b54\u7684\u865a\u62db\uff0c\u5c31\u5728 \u5c55\u767d\u6643\u80a9\u5411\u5de6\uff0c\u8001\u9b54\u8eab\u5f62\u4e00\u65cb\uff0c\u771f\u6bd4\u95ea\u7535\u8fd8\u75f0\uff0c\u5355\u638c\u63a5\u98ce\uff0c\u731b\u6263\u5c55\u767d\u5de6\u8033\u6839\u201c\u85cf\u8840\u201d\u91cd\u7a74\u3002 \u3000\u3000\u8fd9\u4e00\u62db\u53d8\u5316\u5f97\u7a81\u7136\u800c\u5feb\u901f\uff0c\u5c55\u767d\u51e0\u4e4e\u65e0\u6cd5\u8eb2\u8fc7\uff0c\u767e\u5fd9\u4e2d\u7f29\u9888\u85cf\u5934\u2026\u2026 \u3000\u3000\u201c\u54c8\u54c8\u54c8\u2026\u2026\u201d\u72ec\u811a\u98de\u9b54\u521a\u5634\u4e00\u7b11\uff0c\u559d\u9053\uff1a\u201c\u5c0f\u5b50\uff0c\u8eba\u2026\u2026\u201d \u3000\u3000\u201c\u4e0b\u201d\u5b87\u5c1a\u672a\u51fa\u53e3\uff0c\u72ec\u811a\u98de\u9b54\u53f3\u638c\u638c\u7acb\u5982\u5200\uff0c\u731b\u5411\u5c55\u767d\u524d\u80f8\u6309 \u3000\u3000\u4e0d\u4f46\u662f\u201c\u72ec\u811a\u98de\u9b54\u201d\u672c\u4eba\uff0c\u5c31\u8fde\u90a3\u4e48\u591a\u7684\u6b66\u6797\u9ad8\u624b\uff0c\u4e5f\u770b\u5f97\u5f88\u6e05\u695a\uff0c\u773c\u524d\u5c11\u5e74\uff0c\u5b9a\u7136\u65e0 \u6cd5\u518d\u8eb2\u8fc7\u8fd9\u4e00\u638c\u3002 \u3000\u3000\u5c55\u767d\u5fc3\u5934\u4e00\u6817\uff0c\u624d\u4e00\u4f4e\u5934\uff0c\u201c\u547c\u201d\u7684\u4e00\u58f0\uff0c\u4e0a\u76d8\u4e00\u638c\u64e6\u9876\u800c\u8fc7\uff0c\u5f53\u80f8\u4e00\u638c\uff0c\u7d27\u8ddf\u7740\u72c2\u5578 \u800c\u81f3\u3002 \u3000\u3000\u5c55\u767d\u4e5f\u662f\u6025\u52b2\uff0c\u767e\u5fd9\u4e2d\u53cc\u638c\u4e00\u5c01\uff0c\u300a\u5929\u4f5b\u5377\u300b\u7684\u7edd\u5b66\u65e0\u5fc3\u4e4b\u4e2d\u8ba9\u4ed6\u7528\u5bf9\u4e86\uff0c\u6b63\u662f\u4e00\u62db \u201c\u4f5b\u7956\u53c2\u63b8\u201d\u3002\u201c\u562d\u201d\u7684\u4e00\u58f0\u5927\u9707\uff0c\u5c55\u767d\u8eab\u5f62\u6643\u4e86\u4e24\u6643\uff0c\u7adf\u628a\u6a2a\u884c\u897f\u5317\u7684\u201c\u72ec\u811a\u98de\u9b54\u201d\uff0c\u9707 \u9000\u4e86\u516d\u3001\u4e03\u5927\u6b65\u5f00\u5916\u3002 \u3000\u3000\u56e0\u201c\u72ec\u811a\u98de\u9b54\u201d\u53ea\u6709\u4e00\u6761\u72ec\u817f\uff0c\u8001\u9b54\u751f\u6027\u602a\u7663\uff0c\u867d\u662f\u72ec\u817f\uff0c\u65e2\u4e0d\u7528\u62d0\u4e5f\u4e0d\u7528\u6756\uff0c\u884c\u8d70\u662f \u4ee5\u72ec\u817f\u70b9\u5730\u5411\u524d\u8df3\u8dc3\u3002 \u3000\u3000\u56e0\u6b64\uff0c\u88ab\u5c55\u767d\u53cc\u638c\u4e00\u5c01\u9707\u9000\u51fa\u53bb\uff0c\u72ec\u817f\u7ade\u6536\u811a\u4e0d\u4f4f\uff0c\u4e00\u76f4\u9000\u81f3\u53f8\u9a6c\u656c\u8eab\u8fb9\uff0c\u624d\u88ab\u53f8\u9a6c\u656c \u4e00\u638c\u8ffd\u9b3c\u94c3\u6321\u4f4f\u3002 \u3000\u3000\u5f53\u7740\u8fd9\u4e48\u591a\u6b66\u6797\u9ad8\u624b\uff0c\u5c24\u5176\u662f\u5728\u5e84\u4e3b\u9762\u524d\uff0c\u201c\u72ec\u817f\u98de\u9b54\u201d\u8138\u4e0a\u5982\u4f55\u80fd\u6302\u5f97\u4f4f\uff0c\u66b4\u543c\u4e00 \u58f0\uff0c\u60c5\u6025\u60f3\u8ddf\u5c55\u767d\u62fc\u547d\u2026\u2026 \u3000\u3000\u201c\u8ffd\u9b3c\u94c3\u201d\u5374\u6a2a\u81c2\u628a\u4ed6\u62e6\u4f4f\uff0c\u773c\u5411\u5341\u4e08\u4f59\u5916\u5bc6\u6797\u4e4b\u5904\u671b\u53bb\uff0c\u8138\u8272\u60e8\u53d8\uff0c\u6ee1\u662f\u60ca\u6050\u4e4b\u8272\u3002 \u3000\u3000\u201c\u72ec\u811a\u98de\u9b54\u201d\u4e0d\u7531\u81ea\u4e3b\u5730\u4e5f\u968f\u7740\u53f8\u9a6c\u656c\u7684\u76ee\u5149\u671b\u53bb\uff0c\u53ea\u89c1\u5728\u5bc6\u6797\u4e2d\u8d70\u51fa\u4e00\u4e2a\u9a91\u7740\u6bdb\u9a74\u7684 \u4e1d\u5e1b\u8d29\u5b50\u3002 \u3000\u3000\u8fd9\u4e1d\u5e1b\u8d29\u5b50\u5e74\u7eaa\u5f88\u8001\u4e86\u3002\u767d\u53d1\u767d\u7709\uff0c\u988a\u4e0b\u7559\u7740\u4e00\u64ae\u96ea\u767d\u7684\u5c71\u7f8a\u80e1\u5b50\uff0c\u770b\u6837\u5b50\u8db3\u6709\u516b\u4e5d\u5341 \u5c81\uff0c\u7626\u5c0f\u67af\u5e72\uff0c\u8138\u4e0a\u76b1\u7eb9\u5f88\u6df1\uff0c\u4f46\u53cc\u76ee\u4f38\u5149\u5145\u8db3\uff0c\u5f00\u9614\u4e4b\u95f4\u7cbe\u8292\u6151\u4eba\u3002 \u3000\u3000\u4ed6\u8eab\u7a7f\u767d\u7eba\u7ef8\u88e4\u8902\uff0c\u7f0e\u978d\u767d\u889c\uff0c\u88e4\u817f\u624e\u7740\u85d5\u8377\u8272\u4e1d\u5e26\uff0c\u82cd\u82cd\u767d\u53d1\u5728\u8111\u540e\u7528\u7ea2\u7ef3\u624e\u4e86\u4e00\u4e2a \u5c0f\u8fab\u5b50\u3002 \u3000\u3000\u7a33\u5750\u5728\u5c0f\u6bdb\u9a74\u4e0a\uff0c\u6bdb\u9a74\u80cc\u4e0a\u9a6e\u7740\u5341\u6570\u5339\u7ef8\u7f0e\uff0c\u4ed6\u624b\u6325\u5c0f\u76ae\u97ad\uff0c\u5634\u91cc\u201c\u5f97\u513f\uff01\u5f97 \u513f\uff01\u2026\u2026\u201d\u50ac\u9a91\u5feb\u8d70\u3002 \u3000\u3000\u53ef\u662f\u90a3\u5c0f\u6bdb\u9a74\u5c31\u662f\u4e0d\u80af\u5411\u524d\u8d70\uff0c\u800c\u4e14\u56db\u8e44\u62a2\u5730\u5411\u540e\u5012\u6323\u7740\uff0c\u4e5f\u8bb8\u662f\u5b83\u89c1\u5230\u5c71\u5761\u4e0a\u4eba\u591a\u964c \u751f\uff0c\u8001\u5934\u50ac\u5f97\u6025\u4e86\uff0c\u7ade\u201c\u545c\u2026\u2026\u54c7\uff01\u545c\u2026\u2026\u54c7\uff01\u201d\u5636\u558a\u8d77\u6765\u3002 \u3000\u3000\u522b\u770b\u8fd9\u5c0f\u6bdb\u9a74\u8eab\u5f62\u5947\u5c0f\uff0c\u6bd4\u5927\u4e00\u70b9\u7684\u72d7\u5927\u4e0d\u4e86\u8bb8\u591a\uff0c\u4f46\u53eb\u8d77\u6765\u55d3\u95e8\u8fd8\u771f\u5927\uff0c\u53ea\u9707\u5f97\u56db\u91ce \u8f70\u9e23\u3002 \u3000\u3000\u201c\u755c\u7272\uff01\u4f60\u89c1\u4e86\u4eba\u591a\u5c31\u5bb3\u6015\u662f\u4e0d\u662f\uff1f\u201d\u4e1d\u5e1b\u8d29\u5b50\u5728\u9a74\u4e0a\u559d\u9053\uff1a\u201c\u4f46\u6709\u6025\u4e8b\uff0c\u4e0d\u5feb\u8d70\u53ef\u5c31 \u8d76\u4e0d\u4e0a\u4e86\u3002\u201d \u3000\u3000\u8bf4\u7740\uff0c\u626c\u8d77\u5c0f\u76ae\u97ad\u5728\u5c0f\u6bdb\u9a74\u7684\u540e\u817f\u4e0a\uff0c\u201c\u5288\u5288\uff01\u62cd\u62cd\uff01\u201d\u4e00\u9635\u4e71\u62bd\u2026\u2026 \u3000\u3000\u8fd9\u5e74\u8001\u7684\u4e1d\u5e1b\u8d29\u5b50\u4e00\u9732\u9762\uff0c\u201c\u585e\u5916\u53cc\u6b8b\u201d\uff0c\u90a3\u897f\u5317\u9053\u4e0a\u4e24\u5927\u9876\u5c16\u9ad8\u624b\uff0c\u7adf\u662f\u989c\u8272\u60e8\u53d8\uff0c \u8138\u4e0a\u6d41\u9732\u51fa\u60ca\u6050\u5df2\u6781\u7684\u795e\u6001\u2026\u2026 \u3000\u3000\u201c\u8ffd\u9b42\u94c3\u201d\u5413\u5f97\u989d\u4e0a\u6e17\u51fa\u51b7\u6c57\uff0c\u5fc3\u4e2d\u6697\u6177\uff1a\u201c\u6211\u8bf4\u90a3\u8bf4\u8bdd\u7684\u58f0\u97f3\u600e\u4e48\u5f88\u719f\uff0c\u679c\u7136\u662f\u8fd9\u4f4d \u4e3b\u513f\uff01\u5509\uff01\u4eca\u5929\u6211\u53f8\u9a6c\u656c\u53ef\u771f\u662f\u5012\u4e86\u5927\u9709\uff0c\u600e\u4e48\u4f1a\u78b0\u4e0a\u4ed6\u2026\u2026\u201d \u3000\u3000\u201c\u72ec\u811a\u98de\u9b54\u201d\u5185\u5fc3\u7684\u60ca\u9a87\uff0c\u6bd4\u201c\u8ffd\u9b42\u94c3\u201d\u66f4\u751a\uff0c\u5fc3\u795e\u7686\u98a4\uff0c\u6697\u60f3\uff1a\u201c\u5b8c\u4e86\uff01\u4eca\u5929\u771f\u4e22\u5927 \u4eba\u73b0\u5927\u773c\u2026\u2026\u201d \u3000\u3000\u4e0d\u4f46\u201c\u585e\u5916\u53cc\u6b8b\u201d\u5fc3\u60ca\u80c6\u6015\uff0c\u8c79\u7a81\u5c71\u5e84\u201d\u5341\u5927\u9ad8\u624b\uff0c\u751a\u81f3\u8fde\u5e84\u4e3b\u672c\u4eba\u2014\u2014\u201c\u6458\u661f\u624b\u201d\u6155 \u5bb9\u6db5\uff0c\u770b\u5230\u8fd9\u8001\u5e74\u4e1d\u5e1b\u8d29\u5b50\u7a81\u7136\u51fa\u73b0\uff0c\u4e5f\u4e0d\u7531\u8138\u4e0a\u5fae\u5fae\u53d8\u8272\uff0c\u5c24\u5176\u201c\u6458\u661f\u624b\u201d\u4e43\u4e2d\u539f\u6b66\u6797\u4e00 \u5927\u8c6a\u95e8\uff0c\u95e8\u4e0b\u9ad8\u624b\u4e0a\u5343\u8bba\u767e\uff0c\u81ea\u5df1\u672c\u8eab\u6b66\u529f\u4e5f\u9ad8\u81f3\u7edd\u9876\uff0c\u9ad8\u8d35\u7684\u5730\u4f4d\uff0c\u5a01\u4e25\u7684\u4eea\u8868\uff0c\u4f0a\u7136\u4e00 \u4ee3\u5b97\u4e3b\u7684\u8eab\u4efd\uff0c\u5982\u4eca\u89c1\u4e86\u8fd9\u7626\u5c0f\u67af\u5e72\u7684\u8001\u5e74\u4e1d\u5e1b\u8d29\u5b50\uff0c\u795e\u60c5\u4e4b\u95f4\u7ade\u6709\u4e86\u754f\u60e7\u4e4b\u8272\uff0c\u8fd9\u771f\u662f\u4ee4 \u4eba\u767e\u601d\u4e0d\u5f97\u5176\u89e3\u7684\u602a\u4e8b\u3002 \u3000\u3000\u5c55\u767d\u5fc3\u4e2d\u7eb3\u7f55\uff0c\u6697\u60f3\uff1a\u201c\u8fd9\u8d29\u5356\u7ef8\u7f0e\u7684\u8001\u5934\uff0c\u7531\u81ea\u5df1\u5728\u9556\u5c40\u62bc\u9556\u4e0a\u8def\u5f00\u59cb\uff0c\u51e0\u6b21\u548c\u4ed6\u78b0 \u9762\uff0c\u4ed6\u8d29\u5356\u7ef8\u7f0e\u5e38\u8d70\u5230\u6b66\u6797\u4eba\u7269\u51fa\u6ca1\u805a\u4f1a\u7684\u573a\u6240\uff0c\u597d\u50cf\u4e0d\u662f\u5355\u7eaf\u4e3a\u4e86\u505a\u751f\u610f\u5427\uff1f\u2026\u2026\u201d \u3000\u3000\u5c31\u5728\u5c55\u767d\u5fae\u4e00\u5fd6\u5ea6\u4e4b\u95f4\uff0c\u90a3\u4e1d\u5e1b\u8d29\u5b50\u7684\u5c0f\u6bdb\u9a74\u5ffd\u7136\u56db\u8e44\u817e\u5f00\uff0c\u6cfc\u98ce\u4f3c\u5730\u5411\u7740\u201c\u8c79\u7a81\u5c71 \u5e84\u201d\u4f17\u6b66\u6797\u9ad8\u624b\u505c\u8eab\u4e4b\u5904\u5954\u6765\u3002 \u3000\u3000\u201c\u5537\uff01\u5537\uff01\u201d \u3000\u3000\u5356\u7ef8\u7f0e\u7684\u8001\u4eba\u4e00\u8138\u60ca\u714c\u4e4b\u5bb9\uff0c\u7fd8\u7740\u5c71\u7f8a\u767d\u80e1\uff0c\u4e00\u8fb9\u624b\u5fd9\u811a\u4e71\u5730\u52d2\u7f30\u7ef3\uff0c\u4e00\u8fb9\u5634\u4e2d\u6025\u58f0\u559d \u6b62\u3002\u5e76\u53eb\u9053\uff1a\u8bf8\u4f4d\u8001\u4e61\u4eb2\uff01\u5e2e\u5e2e\u5fd9\uff01\u54ce\u54df\uff01\u4e0d\u597d\uff01\u6211\u7684\u5c0f\u9a74\u5b50\u53d7\u60ca\u5566\uff01\u5feb\u5e2e\u5fd9\u62e6\u4e00\u62e6\uff01\u54ce\uff01 \u54ce\uff01\u4e0d\u597d\u4e0d\u597d\uff01\u6211\u8001\u4eba\u5bb6\u8981\u5b8c\u86cb\u2026\u2026\u201d \u3000\u3000\u4ed6\u5c31\u8fd9\u6837\u4e00\u8def\u4e0a\u80e1\u56b7\u4e71\u53eb\uff0c\u5f20\u624b\u821e\u811a\uff0c\u8fd8\u771f\u662f\u50cf\u8981\u4ece\u60ca\u5954\u7684\u6bdb\u9a74\u80cc\u4e0a\u6454\u4e0b\u6765\u3002 \u3000\u3000\u201c\u547c\u62c9\u62c9\uff01\u201d\u8fd9\u5c0f\u6bdb\u9a74\u4e0d\u8dd1\u662f\u4e0d\u8dd1\uff0c\u8dd1\u8d77\u6765\u8fd8\u662f\u771f\u5feb\uff0c\u56db\u8e44\u7ffb\u98de\uff0c\u8e22\u6c99\u626c\u5c18\uff0c\u5341\u6570\u4e08\u7684 \u8ddd\u79bb\uff0c\u98d8\u98ce\u95ea\u7535\u822c\u5730\u6643\u773c\u51b2\u81f3\u4f17\u4eba\u9762 \u3000\u3000\u524d\u3002 \u3000\u3000\u90a3\u4e48\u591a\u6b66\u6797\u9876\u5c16\u9ad8\u624b\uff0c\u7adf\u65e0\u4eba\u6562\u51fa\u624b\u62e6\u963b\uff0c\u5e76\u7eb7\u7eb7\u5411\u56db\u8fb9\u95ea\u5f00\u2026\u2026 \u3000\u3000\u201c\u54ce\u54df\uff01\u201d\u8001\u4eba\u60ca\u53eb\u9053\uff1a\u4f60\u4eec\u600e\u4e48\u4e0d\u5e2e\u5fd9\u5440\uff1f\u96be\u9053\u89c1\u6b7b\u4e0d\u6551\u5417\uff1f\u54ce\uff01\u54ce\uff01\u8fd9\u5e74\u5934\u4eba\u5fc3\u5927 \u574f\uff01\u4eba\u5fc3\u5927\u574f\u2026\u2026\u201d \u3000\u3000\u8bf4\u7740\uff0c\u53c8\u662f\u201c\u54ce\u201d\u7684\u4e00\u58f0\u60ca\u547c\uff0c\u5728\u98de\u5954\u7684\u6bdb\u9a74\u80cc\u4e0a\uff0c\u8eab\u5f62\u4e00\u6e9c\u6b6a\u659c\uff0c\u770b\u60c5\u5f62\u70ad\u70ad\u53ef\u5371\uff0c \u771f\u8981\u6454\u4e0b\u6765\u7684\u6837\u5b50\u3002 \u3000\u3000\u201c\u6458\u661f\u624b\u201d\u6155\u5bb9\u5e84\u4e3b\uff0c\u5ffd\u7136\u4e0a\u524d\u4e24\u6b65\u8eac\u8eab\u62b1\u62f3\uff0c\u6ee1\u8138\u8c26\u606d\u4e4b\u8272\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8001\u4eba\u5bb6\u4fbf\u662f \u2018\u795e\u9a74\u94c1\u80c6\u2019\u8463\u8001\u524d\u8f88\u5427\uff1f\u665a\u8f88\u6155\u5bb9\u6db5\u8fd9\u53a2\u6709\u793c\u5566\uff01\u201d \u3000\u3000\u201c\u6458\u661f\u624b\u201d\u8fd9\u4e00\u8bf4\uff0c\u8001\u4eba\u5ffd\u7136\u563b\u563b\u4e00\u7b11\uff0c\u8bf4\u4e5f\u5947\u602a\uff0c\u90a3\u60ca\u5954\u7684\u5c0f\u6bdb\u9a74\u7acb\u523b\u6536\u52bf\u7ad9\u4f4f\uff0c\u4e0d \u8fdc\u4e0d\u8fd1\uff0c\u6070\u6070\u505c\u5728\u201c\u6458\u661f\u624b\u201d\u9762\u524d\u4e94\u5c3a\u4e4b\u5904\uff0c\u8001\u4eba\u7a33\u5750\u9a74\u80cc\uff0c\u6839\u672c\u50cf\u6ca1\u6709\u521a\u624d\u90a3\u4e48\u56de\u4e8b\u4e00 \u6837\u3002 \u3000\u3000\u53ef\u662f\u201c\u6458\u661f\u624b\u201d\u6b64\u8a00\u4e00\u51fa\uff0c\u5728\u573a\u4f17\u4eba\u65e0\u4e0d\u6177\u7136\u53d8\u8272\u3002 \u3000\u3000\u5148\u524d\u8fd9\u770b\u4f3c\u4e1d\u5e1b\u8d29\u5b50\u7684\u8001\u4eba\u4e00\u9732\u9762\uff0c\u8fd8\u53ea\u6709\u8001\u4e00\u8f88\u7684\u6b66\u6797\u9ad8\u624b\u84e6\u7136\u5fc3\u60ca\u3002\u4f46\u5e74\u7eaa\u6bd4\u8f83\u8f7b \u4e00\u70b9\u7684\uff0c\u8fd8\u90fd\u61f5\u7136\u4e0d\u8bc6\u3002 \u3000\u3000\u5982\u4eca\u542c\u6155\u5bb9\u5e84\u4e3b\u8fd9\u4e00\u79f0\u547c\uff0c\u6765\u7684\u8fd9\u8001\u4eba\u7adf\u662f\u56db\u4e94\u5341\u5e74\u4ee5\u524d\u540d\u9707\u5929\u4e0b\u7684\u201c\u795e\u9a74\u94c1\u80c6\u201d\u8463\u5343 \u91cc\uff0c\u4e0d\u7531\u4e2a\u4e2a\u609a\u7136\u8272\u53d8\u3002 \u3000\u3000\u60f3\u90a3\u201c\u795e\u9a74\u94c1\u80c6\u201d\u8463\u5343\u91cc\uff0c\u4e43\u662f\u56db\u4e94\u5341\u5e74\u4ee5\u524d\u6b66\u6797\u4e2d\u95fb\u540d\u4e27\u80c6\u7684\u4eba\u7269\u3002 \u3000\u3000\u4ed6\u7684\u4e8b\u8ff9\u5145\u6ee1\u4e86\u4f20\u5947\u8272\u5f69\uff0c\u6b66\u529f\u4e4b\u9ad8\u66f4\u662f\u795e\u9b3c\u96be\u6d4b\u3002\u5173\u4e8e\u4ed6\u7684\u5947\u4e8b\u94c1\u95fb\uff0c\u6b66\u6797\u4e2d\u53ea\u5f53\u795e \u8bdd\u6765\u4f20\u8bf4\uff0c\u56e0\u4e3a\u56db\u4e94\u5341\u5e74\u4ee5\u524d\uff0c\u6c5f\u6e56\u4e0a\u4fbf\u5931\u53bb\u4e86\u4ed6\u7684\u8e2a\u8ff9\uff0c\u6b66\u6797\u4e2d\u5f88\u5c11\u4eba\u89c1\u8fc7\u3002 \u3000\u3000\u201c\u585e\u5916\u53cc\u6b8b\u201d\u53f8\u9a6c\u656c\u7684\u5de6\u81c2\uff0c\u674e\u4e3e\u7684\u53f3\u817f\uff0c\u542c\u8bf4\u4fbf\u662f\u88ab\u201c\u795e\u9a74\u94c1\u80c6\u201d\u5e9f\u4e86\u7684\u3002 \u3000\u3000\u5f53\u65f6\u201c\u8ffd\u9b42\u94c3\u201d\u53f8\u9a6c\u656c\u4e0e\u201c\u72ec\u811a\u98de\u9b54\u201d\u674e\u4e3e\uff0c\u4e8c\u4eba\u5404\u81ea\u8fd4\u56de\u5e08\u95e8\u5411\u638c\u95e8\u5e08\u7236\u54ed\u8bc9\u88ab\u201c\u795e \u9a74\u94c1\u80c6\u201d\u4f24\u6b8b\u80a2\u4f53\u7684\u7ecf\u8fc7\u3002 \u3000\u3000\u5f53\u7136\uff0c\u4ed6\u4eec\u7edd\u4e0d\u8bf4\u51fa\u81ea\u5df2\u5728\u897f\u5317\u9053\u4e0a\u6043\u5f3a\u51cc\u5f31\u7684\u6b8b\u66b4\u3002\u53ea\u662f\u8bf4\u5982\u4f55\u88ab\u201c\u795e\u9a74\u94c1\u80c6\u201d\u6740 \u4f24\uff0c\u201c\u795e\u9a74\u94c1\u80c6\u201d\u53c8\u600e\u6837\u6c61\u8fb1\u5e08\u7236\uff0c\u4e8c\u4eba\u52a0\u6cb9\u52a0\u918b\u4e00\u6e32\u67d3\uff0c\u7acb\u628a\u4e8c\u4eba\u7684\u5e08\u7236\u6fc0\u6012\uff0c\u5f53\u65f6\u8054\u6289 \u8fdb\u5173\u627e\u201c\u795e\u9a74\u94c1\u80c6\u201d\u4e3a\u5f92\u5f1f\u62a5\u4ec7\u3002 \u3000\u3000\u56fa\u7136\uff0c\u90a3\u65f6\u4ed6\u4e8c\u4eba\u7684\u5e08\u7236\uff0c\u5df2\u7ecf\u662f\u5f53\u65f6\u6b66\u6797\u4e2d\u5c48\u6307\u53ef\u6570\u7684\u5934\u53f7\u4eba\u7269\u3002\u4f46\u4e5f\u77e5\u9053\u5355\u51ed\u4e8c\u4eba \u8981\u60f3\u80dc\u8fc7\u201c\u795e\u9a74\u94c1\u80c6\u201d\uff0c\u90a3\u662f\u6beb\u65e0\u628a\u63e1\u3002\u4e8e\u662f\u53c8\u7ea6\u96c6\u4e86\u5f53\u65f6\u51e0\u4e2a\u9ed1\u9053\u4e0a\u7684\u9876\u5c16\u9ad8\u624b\uff0c\u4e00\u5171\u662f \u5341\u6570\u4e2a\u6b66\u6797\u5de8\u624b\uff0c\u4e5d\u4e5d\u91cd\u9633\uff0c\u5728\u592a\u884c\u5c71\u5409\u9ad8\u5cf0\u4e0a\uff0c\u7ea6\u4f1a\u201c\u795e\u9a74\u94c1\u80c6\u201d\u6bd4\u6b66\u3002 \u3000\u3000\u8fd9\u5409\u9ad8\u5cf0\u4e0a\u7684\u6bd4\u6b66\u5927\u4f1a\uff0c\u662f\u5f53\u5e74\u6b66\u6797\u4e00\u5927\u80dc\u4e3e\uff0c\u5dee\u4e0d\u591a\u4e2d\u539f\u6b66\u6797\u9053\u4e0a\u7684\u9ad8\u624b\uff0c\u4ee5\u53ca\u56db\u6d77 \u516b\u8352\u7684\u5947\u4eba\u5f02\u58eb\u5168\u5230\u4e86\u3002 \u3000\u3000\u4f46\u662f\uff0c\u201c\u8ffd\u9b42\u94c3\u201d\u4e0e\u201c\u72ec\u811a\u98de\u9b54\u201d\u7684\u6069\u5e08\uff0c\u4e0e\u5341\u6570\u4f4d\u5f53\u65f6\u9ed1\u9053\u4e0a\u7684\u9876\u5c16\u9ad8\u624b\uff0c\u4e00\u4e2a\u4e2a\u90fd \u8d25\u5728\u201c\u795e\u9a74\u94c1\u80c6\u201d\u7684\u4e09\u679a\u94c1\u80c6\u3001\u516b\u516b\u516d\u5341\u56db\u5f0f\u201c\u5947\u5f62\u638c\u201d\u4e0b\uff0c\u975e\u6b7b\u5373\u4f24\uff0c\u800c\u4e14\u7ade\u6ca1\u6709\u4e00\u4eba\u80fd \u8d70\u51fa\u5341\u62db\uff01 \u3000\u3000\u8fd9\u4e00\u6765\u201c\u795e\u9a74\u94c1\u80c6\u201d\u5a01\u540d\u5927\u9707\uff0c\u53ef\u662f\uff0c\u4ece\u6b64\u6c5f\u6e56\u4e0a\u4e5f\u5c31\u5931\u53bb\u4e86\u201c\u795e\u9a74\u94c1\u80c6\u201d\u7684\u8e2a\u8ff9\u3002 \u3000\u3000\u5982\u4eca\uff0c\u8fd9\u795e\u8bdd\u822c\u7684\u4eba\u7269\uff0c\u201c\u795e\u9a74\u94c1\u80c6\u201d\u8463\u5343\u91cc\uff0c\u53c8\u5728\u6b64\u5904\u73b0\u8eab\uff0c\u600e\u4e0d\u4f7f\u4f17\u4eba\u5403\u60ca\uff1f \u3000\u3000\u201c\u54c8\u54c8\u54c8\uff01\u2026\u3002\u3002\u201d \u3000\u3000\u5356\u7ef8\u7f0e\u7684\u8001\u4eba\u4e00\u58f0\u957f\u7b11\uff0c\u4e24\u53ea\u7ec6\u76ee\u4e00\u7741\uff0c\u5947\u5149\u56db\u5c04\uff0c\u5411\u201c\u6458\u661f\u624b\u201d\u8bf4\u9053\uff1a\u201c\u4f60\u8fd9\u53ef\u662f\u8ba4 \u9519\u4eba\u4e86\uff01\u4e0d\u8981\u770b\u5230\u8001\u673d\u9a91\u9a74\uff0c\u5c31\u628a\u8001\u673d\u5f53\u505a\u2018\u795e\u9a74\u2019\uff01\u8001\u673d\u66f4\u4e0d\u662f\u4ec0\u4e48\u2018\u94c1\u80c6\u2019\uff01\u54c8\u54c8\uff01\u8001 \u673d\u662f\u2018\u8c46\u8150\u80c6\u2019\uff0c\u6700\u6015\u770b\u5230\u6253\u67b6\u6597\u6bb4\u2026\u2026\u201d \u3000\u3000\u8001\u4eba\u8bf4\u7740\uff0c\u4e00\u5708\u6bdb\u9a74\uff0c\u53c8\u8d70\u81f3\u201c\u6a0a\u6c0f\u4e09\u5251\u4e00\u9e3e\u201d\u8eab\u65c1\uff0c\u8fd9\u65f6\u201c\u6a0a\u6c0f\u4e09\u5251\u201d\u4e2d\u7684\u8001\u5927\u3001\u8001 \u4e09\uff0c\u4ee5\u53ca\u6a0a\u7d20\u9e3e\u5144\u59b9\u4e09\u4eba\uff0c\u6b63\u5728\u6551\u6cbb\u88ab\u53f8\u9a6c\u656c\u201c\u8ffd\u9b42\u94c3\u201d\u6253\u4f24\u7684\u8001\u4e8c\uff0c\u201c\u8ffd\u98ce\u5251\u5ba2\u201d\u6a0a\u6770\uff01 \u3000\u3000\u8001\u4e09\u201c\u6469\u4e91\u5251\u201d\u6a0a\u835a\u6276\u4f4f\u6a0a\u6770\uff0c\u8001\u5927\u201c\u6233\u60c5\u5251\u201d\u6b63\u4e3a\u6a0a\u6770\u63a8\u5bab\u6d3b\u7a74\uff0c\u6a0a\u7d20\u5f2f\u62ff\u51fa\u6a0a\u5bb6\u79d8 \u4f20\u7684\u8dcc\u6253\u5723\u836f\u6b63\u5582\u6a0a\u6770\u541e\u670d\u3002 \u3000\u3000\u201c\u4f60\u4eec\u770b\u53ef\u6015\u4e0d\u53ef\u6015\uff1f\u8fd9\u4e0d\u662f\u6253\u67b6\u53c8\u6253\u4f24\u4eba\u4e86\uff01\u201d\u8001\u4eba\u5728\u9a74\u80cc\u4e0a\u770b\u4e86\u770b\u6a0a\u6770\uff0c\u7136\u540e\u62a2\u76ee \u56db\u987e\uff0c\u626b\u89c6\u4e86\u53f8\u9a6c\u656c\u3001\u674e\u4e3e\u4e8c\u4eba\u4e00\u773c\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u8c01\u52a8\u624b\u6253\u7684\uff1f\u201d \u3000\u3000\u72c2\u50b2\u7684\u201c\u585e\u5916\u53cc\u6b8b\u201d\uff0c\u8138\u8272\u5413\u5f97\u53d8\u6210\u6b7b\u7070\u4e00\u822c\uff0c\u754f\u60e7\u5730\u671b\u7740\u8001\u4eba\uff0c\u65e2\u4e0d\u6562\u627f\u8ba4\uff0c\u53c8\u4e0d\u6562 \u5426\u8ba4\uff0c\u53d8\u5f97\u5c31\u4f3c\u804b\u54d1\u4e00\u822c\u2026\u3002\xb7 \u3000\u3000\u8fd9\u65f6\uff0c\u8001\u4eba\u7684\u773c\u5149\u671b\u89c1\u5c55\u767d\uff0c\u5411\u5c55\u767d\u6bd7\u7259\u7b11\u4e86\u4e00\u7b11\u3002 \u3000\u3000\u5c55\u767d\u4e0d\u77e5\u9053\u4e70\u7ef8\u7f0e\u7684\u8001\u4eba\uff0c\u4e3a\u4ec0\u4e48\u8001\u662f\u5411\u81ea\u5df1\u53d1\u7b11\uff1f\u5728\u62bc\u9556\u7684\u8def\u4e0a\uff0c\u6bcf\u9047\u5230\u8001\u4eba\uff0c\u8001\u4eba \u603b\u8981\u5411\u81ea\u5df2\u6bd7\u7259\u7b11\u7b11\uff0c\u5c55\u767d\u4e5f\u832b\u7136\u5730\u8ddf\u4ed6\u7b11\u4e86\u7b11\u3002 \u3000\u3000\u201c\u5c0f\u54e5\uff01\u201d\u8001\u4eba\u7ade\u5bf9\u5c55\u767d\u5f00\u53e3\u8bf4\u8bdd\u4e86\uff1a\u201c\u54b1\u4eec\u8001\u5c0f\u4e8c\u4eba\u5012\u662f\u5f88\u6709\u7f18\uff0c\u53c8\u78b0\u4e0a\u4e86\uff01\u201d \u3000\u3000\u201c\u771f\u662f\u5de7\u9047\uff01\u201d\u5c55\u767d\u542b\u7740\u6df1\u610f\u5730\u7b54\u9053\uff1a\u201c\u5c0f\u53ef\u8d70\u5230\u54ea\u91cc\uff0c\u8001\u5148\u751f\u4e5f\u8d70\u5230\u54ea\u91cc\uff01\u201d \u3000\u3000\u201c\u5403\u2026\u2026\u201d\u8001\u4eba\u7b11\u4e86\u3002\u8ddf\u7740\u4e00\u62ac\u817f\u4ece\u5c0f\u6bdb\u9a74\u4e0a\u4e0b\u6765\uff0c\u8d70\u81f3\u201c\u6a0a\u6c0f\u4e09\u5251\u4e00\u9e3e\u201d\u8fd1\u524d\uff0c\u7528\u624b \u4e00\u6307\u6a0a\u6770\u7684\u53f3\u80a9\uff0c\u8bf4\u9053\uff1a\u201c\u53f3\u80a9\u79c9\u98ce\u7a74\u632b\u4f24\uff0c\u5982\u4e0d\u5feb\u6cbb\uff0c\u4fbf\u8981\u843d\u4e2a\u534a\u8eab\u4e0d\u9042\uff01\u201d \u3000\u3000\u201c\u6233\u60c5\u5251\u201d\u7d2f\u5f97\u6ee1\u5934\u5927\u6c57\uff0c\u7528\u63a8\u5bab\u6d3b\u7a74\u624b\u6cd5\uff0c\u5c31\u662f\u89e3\u4e0d\u5f00\u4e8c\u5f1f\u53d7\u4f24\u7684\u7a74\u9053\u3002\u6b63\u5728\u5fc3\u6025\uff0c \u7a81\u7136\u8001\u4eba\u7528\u624b\u4e00\u6307\uff0c\u201c\u6233\u60c5\u5251\u201d\u79bb\u5f97\u6700\u8fd1\uff0c\u5fae\u611f\u8001\u4eba\u6307\u5904\u4e00\u4e1d\u5fae\u98ce\u5439\u8fc7\uff0c\u6a0a\u4fca\u84e6\u7136\u60ca\u609f\uff1a\u8fd9 \u5206\u660e\u662f\u6c5f\u6e56\u4e0a\u53ea\u95fb\u4f20\u8bf4\uff0c\u672a\u66fe\u89c1\u8fc7\u7684\u201c\u51cc\u7a7a\u62c2\u7a74\u201d\u624b\u6cd5\u3002 \u3000\u3000\u201c\u6233\u60c5\u5251\u201d\u4e0d\u660e\u9a91\u9a74\u8001\u4eba\u7684\u7528\u610f\uff0c\u6050\u6015\u8001\u4e8c\u8d1f\u4f24\u540e\u518d\u88ab\u6697\u7b97\uff0c\u6115\u7136\u4e00\u60ca\u2026\u2026 \u3000\u3000\u201c\u8ffd\u98ce\u5251\u201d\u5374\u6c40\u4e86\u4e00\u5bf8\u51b7\u98a4\uff0c\u4eba\u5df2\u82cf\u9192\u8fc7\u6765\uff0c\u7f13\u7f13\u7741\u5f00\u773c\u775b\u2026\u2026 \u3000\u3000\u201c\u6233\u60c5\u5251\u201d\u8fd9\u624d\u77e5\u9053\u8001\u4eba\u662f\u5e2e\u5fd9\u81ea\u5df2\uff0c\u4e3a\u4e8c\u5f1f\u89e3\u5f00\u4e86\u7a74\u9053\u3002\u800c\u81ea\u5df1\u5374\u8d39\u4e86\u534a\u5929\u52b2\uff0c\u624b\u63c9 \u638c\u63a8\uff0c\u7528\u5c3d\u4e86\u5468\u8eab\u771f\u529b\u4ecd\u7136\u89e3\u4e0d\u5f00\uff0c\u800c\u4eba\u5bb6\u53ea\u90a3\u4e48\u865a\u7a7a\u7528\u624b\u4e00\u6307\uff0c\u4fbf\u89e3\u5f00\u4e86\uff0c\u81ea\u5df1\u8fd8\u5dee\u4e00\u70b9 \u8981\u51fa\u624b\u963b\u62e6\uff0c\u4e0d\u7531\u6697\u9053\u4e86\u4e00\u58f0\uff1a\u60ed\u6127\uff01\u201d \u3000\u3000\u4f46\uff0c\u8001\u4eba\u4e0b\u5f97\u9a74\u6765\uff0c\u5c55\u81ea\u4e00\u773c\u770b\u51fa\u5c0f\u6bdb\u9a74\u7684\u978d\u7559\u94dc\u73af\u4e0a\uff0c\u659c\u6302\u7740\u4e00\u53e3\u5b9d\u5251\uff0c\u53ea\u56e0\u4e3a\u8001\u4eba \u521a\u624d\u9a91\u5728\u9a74\u80cc\u4e0a\uff0c\u6b63\u597d\u7528\u817f\u628a\u90a3\u5b9d\u5251\u6321\u4f4f\u4e86\u3002\u8001\u4eba\u8fd9\u4e00\u4e0b\u9a74\uff0c\u90a3\u5b9d\u5251\u4fbf\u970d\u7136\u5165\u76ee\u3002 \u3000\u3000\u5b9d\u5251\u7684\u5f62\u8c61\u4e00\u89e6\u53ca\u5c55\u767d\u7684\u773c\u5e18\uff0c\u5c55\u767d\u4e0d\u7531\u5fc3\u5934\u72c2\u9707\u2026\u2026 \u3000\u3000\u539f\u6765\u90a3\u67c4\u5b9d\u5251\uff0c\u7eff\u9c7c\u76ae\u9798\uff0c\u9ec4\u91d1\u541e\u53e3\uff0c\u5251\u67c4\u4e0a\u5d4c\u9576\u7740\u4e00\u5757\u6676\u83b9\u900f\u660e\u7684\u78a7\u7389\uff0c\u674f\u9ec4\u4e1d\u7a57\u968f \u98ce\u5fae\u62c2\u3002\u90a3\u4e0d\u6b63\u662f\u5728\u201c\u5b89\u4e50\u516c\u5b50\u201d\u624b\u4e0a\u9057\u5931\uff0c\u7236\u4eb2\u5728\u4e34\u6b7b\u4e4b\u524d\u4ea4\u7ed9\u81ea\u5df1\uff0c\u5e76\u9057\u547d\u81ea\u5df1\u8981\u4ee5\u6b64 \u5251\u4e3a\u7236\u62a5\u4ec7\u7684\u201c\u65e0\u60c5\u78a7\u5251\u201d\u5417\uff1f \u3000\u3000\u5c55\u767d\u4e4d\u7779\u5931\u800c\u590d\u5f97\u7684\u6545\u7269\uff0c\u5fc3\u60c5\u5927\u4e3a\u6fc0\u52a8\uff0c\u8eab\u5f62\u731b\u8e7f\u9762\u524d\uff0c\u4ef2\u624b\u53bb\u6293\u9a74\u80cc\u4e0a\u7684\u201c\u65e0\u60c5\u78a7 \u5251\u201d\u3002 \u3000\u3000\u540c\u65f6\u5634\u4e2d\u5927\u58f0\u559d\u9053\uff1a\u201c\u8fd9\u4e0d\u662f\u6211\u7684\u5b9d\u5251\u5417\uff1f\u8001\u5148\u751f\u2026\u2026\u201d \u3000\u3000\u8981\u8bf4\u5c55\u767d\u5728\u5fc3\u60c5\u6fc0\u52a8\u4e4b\u4e0b\uff0c\u8eab\u5f62\u4e0d\u80fd\u8bf4\u4e0d\u591f\u5feb\uff1b\u4f46\u4ed6\u5feb\uff0c\u8001\u4eba\u6bd4\u4ed6\u66f4\u5feb\uff0c\u5c55\u767d\u8eab\u5f62\u5c1a\u672a \u6251\u81f3\u9a74\u524d\uff0c\u8001\u4eba\u540e\u811a\u5374\u5148\u81f3\uff0c\u4e00\u6643\u8eab\u8dc3\u4e0a\u9a74\u80cc\uff0c\u5634\u4e2d\u8fde\u5fd9\u53eb\u9053\uff1a\u201c\u5443\uff01\u8fd9\u4f4d\u5c0f\u54e5\uff0c\u4f60\u662f\u600e\u4e48 \u5566\uff1f\u8981\u62a2\u6211\u8001\u4eba\u5bb6\u7684\u5b9d\u5251\u5417\uff1f\u201d \u3000\u3000\u201c\u54fc\uff01\u201d\u5c55\u767d\u6012\u6781\uff0c\u51b7\u54fc\u4e00\u58f0\u559d\u9053\uff1a\u201c\u4e0d\u77e5\u662f\u8c01\u62a2\u4e86\u8c01\u7684\u5b9d\u5251\uff1f\u54b1\u4eec\u5149\u68cd\u773c\u91cc\u4e0d\u63c9\u6c99 \u5b50\uff01\u4f60\u4e00\u8def\u8ddf\u7740\u6211\uff0c\u62a2\u4e86\u6211\u7684\u5b9d\u5251\uff0c\u8fd8\u5230\u6211\u8ddf\u524d\u6765\u663e\u5149\u5417\uff1f\u2026\u2026\u201d \u3000\u3000\u5c55\u767d\u4e00\u8fb9\u6012\u559d\u4e00\u8fb9\u7d27\u8d76\uff0c\u6b64\u65f6\u8001\u4eba\u5df2\u9a91\u9a74\u8dd1\u51fa\u4e24\u4e08\u5f00\u5916\u3002 \u3000\u3000\u5c55\u767d\u6015\u88ab\u4ed6\u518d\u8dd1\u4e86\uff0c\u201c\u516b\u6b65\u8d76\u8749\u201d\u8eab\u5f62\u6025\u8dc3\uff0c\u968f\u540e\u8ffd\u53bb\uff0c\u540c\u65f6\uff0c\u8eab\u5f62\u8dc3\u8d77\u534a\u7a7a\uff0c\u731b\u7136\u5411 \u8001\u4eba\u540e\u5fc3\u5288\u51fa\u4e00\u638c\u3002 \u3000\u3000\u201c\u54ce\u54df\uff01\u201d\u8001\u4eba\u5c16\u58f0\u6025\u547c\uff0c\u540c\u65f6\u9a91\u5728\u9a74\u80cc\u4e0a\u8eab\u5f62\u4e71\u6643\u3002 \u3000\u3000\u5c55\u767d\u5288\u51fa\u7684\u4e00\u638c\u843d\u4e86\u7a7a\uff0c\u8001\u4eba\u6b7b\u81ea\u53eb\u9053\uff1a\u201c\u5c0f\u54e5\u89c1\u8d22\u8d77\u610f\uff0c\u8981\u60f3\u62e6\u8def\u62a2\u52ab\uff01\u4f60\u4eec\u90a3\u4e48\u591a \u4eba\uff0c\u8c01\u6765\u5e2e\u5fd9\u62e6\u4f4f\u4ed6\u5440\uff01\u201d \u3000\u3000\u201c\u8001\u513f\uff01\u7528\u4e0d\u7740\u88c5\u75af\u5356\u50bb\uff01\u201d\u5c55\u767d\u5728\u540e\u8fb9\u6c14\u5f97\u9a82\u8d77\u6765\uff0c\u4e00\u8fb9\u6025\u8d76\uff0c\u4e00\u8fb9\u53c8\u5288\u51fa\u4e24\u638c\uff0c\u540c \u65f6\u5634\u91cc\u8bf4\u9053\uff1a\u201c\u4f60\u8981\u4e0d\u628a\u5c0f\u7237\u7684\u5b9d\u5251\u7559\u4e0b\uff0c\u4f60\u5c31\u662f\u9003\u5230\u5929\u8fb9\uff0c\u5c0f\u7237\u4e5f\u8ffd\u4e0a\u4f60\u628a\u5b9d\u5251\u8981\u56de \u6765\uff01\u201d \u3000\u3000\u201c\u54ce\u5440\uff01\u2026\u2026\u54ce\u54df\uff01\u2026\u2026\u201d\u8001\u4eba\u9a91\u5728\u9a74\u4e0a\u5934\u4e5f\u4e0d\u56de\uff0c\u4e00\u8fb9\u5982\u98ce\u9a70\u7535\u63a3\u5411\u524d\u8dd1\u53bb\uff0c\u4e00\u8fb9\u5634 \u4e2d\u201c\u54ce\u5440\uff01\u54ce\u54df\uff01\u201d\u5730\u4e71\u53eb\u3002 \u3000\u3000\u4f46\u5c55\u767d\u63a5\u8fde\u671d\u8001\u4eba\u540e\u5fc3\u8981\u5bb3\u5288\u51fa\u7684\u6570\u638c\uff0c\u5747\u5728\u8001\u4eba\u8eab\u5f62\u4e71\u6643\u4e71\u52a8\u4e4b\u4e0b\u843d\u4e86\u7a7a\u3002\u5c31\u662f\u5c55\u767d \u5411\u5c0f\u6bdb\u9a74\u8179\u80cc\u4e0a\u5288\u4e86\u4e00\u638c\u4e24\u62f3\uff0c\u4e5f\u88ab\u5c0f\u6bdb\u9a74\u4e71\u8e66\u4e71\u8df3\u4e4b\u95f4\u8eb2\u8fc7\uff01 \u3000\u3000\u4e00\u4eba\u4e00\u9a91\uff0c\u90fd\u662f\u8dd1\u5f97\u98de\u5feb\uff0c\u6643\u773c\u4e4b\u95f4\uff0c\u5df2\u8dd1\u51fa\u5341\u6570\u4e08\u4e4b\u5916\uff0c\u773c\u770b\u5c06\u8981\u9690\u6ca1\u4e8e\u5bc6\u6797\u4e4b\u4e2d\u3002 \u3000\u3000\u201c\u8c79\u7a81\u5c71\u5e84\u201d\u4e0a\u7684\u9ad8\u624b\uff0c\u89c1\u5c55\u767d\u8ffd\u8e2a\u8001\u4eba\u8dd1\u4e86\uff0c\u6709\u6570\u4eba\u8dc3\u8dc3\u6b32\u8ffd\uff0c\u5374\u88ab\u5e84\u4e3b\u201c\u6458\u661f\u624b\u201d \u963b\u6b62\u4f4f\u2014\u2014 \u3000\u3000\u773c\u770b\u7740\u4e00\u4eba\u4e00\u9a91\uff0c\u6108\u8dd1\u6108\u8fdc\uff0c\u8eab\u5f71\u6e10\u6b21\u9690\u6ca1\u4e8e\u5bc6\u6797\u6811\u4e1b\u4e4b\u4e2d\u3002\u53f1\u54a4\u58f0\u3001\u8e44\u58f0\uff0c\u4e5f\u6e10\u6e10\u4e0d \u95fb\u2026\u2026 \u7b2c\u5341\u516d\u7ae0\u3000\u9a91\u9a74\u602a\u8001\u4eba>> \u53e4\u9f99\u300a\u5251\u5ba2\u884c\u300b \u7b2c\u5341\u516d\u7ae0\u3000\u9a91\u9a74\u602a\u8001\u4eba \u3000\u3000\u539f\u6765\u5c55\u767d\u5728\u201c\u8c79\u7a81\u5c71\u5e84\u201d\u5e84\u540e\u5c0f\u5b64\u5c71\u4e0a\uff0c\u8ffd\u90a3\u9a91\u9a74\u7684\u4e1d\u5e1b\u8d29\u5b50\uff0c\u8db3\u8db3\u8ffd\u51fa\u6709\u56db\u4e94\u5341\u91cc\u4e4b \u9065\uff0c\u5728\u4e00\u5bc6\u5bc6\u677e\u6797\u4e4b\u524d\uff0c\u5ffd\u7136\u5931\u53bb\u4e86\u9a91\u9a74\u8001\u4eba\u7684\u8e2a\u8ff9\u3002 \u3000\u3000\u5947\u4e8b\u53d1\u751f\u4e86\uff0c\u90a3\u9a91\u9a74\u8001\u4eba\u867d\u7136\u8e2a\u5f71\u4e0d\u89c1\uff0c\u4ed6\u90a3\u67c4\u201c\u65e0\u60c5\u78a7\u5251\u201d\u5374\u63a5\u5728\u4e00\u68f5\u5927\u677e\u4e0a\u3002 \u3000\u3000\u5c55\u767d\u51e0\u4e4e\u4e0d\u6562\u76f8\u4fe1\u81ea\u5df1\u7684\u773c\u775b\uff0c\u5929\u4e0b\u54ea\u91cc\u6709\u8fd9\u6837\u5947\u4e8b\uff0c\u5931\u53bb\u7684\u5b9d\u5251\uff0c\u7adf\u4f1a\u4e4e\u81ea\u65e0\u6545\u5730\u6302 \u5728\u6811\u4e0a\uff0c\u7b49\u7740\u81ea\u5df1\u6765\u53d6\uff1f \u3000\u3000\u4f46\u4e8b\u5b9e\u6446\u5728\u773c\u524d\uff0c\u9ec4\u91d1\u541e\u53e3\uff0c\u7eff\u9c7c\u76ae\u9798\uff0c\u674f\u9ec4\u5251\u7a57\u968f\u98ce\u6487\u626c\uff0c\u201c\u65e0\u60c5\u78a7\u5251\u201d\u660e\u660e\u6302\u5728\u90a3 \u91cc\u3002 \u3000\u3000\u5c55\u767d\u4ee5\u4e3a\u81ea\u5df1\u773c\u82b1\u4e86\uff0c\u6574\u5929\u5ff5\u7740\u90a3\u67c4\u5931\u5251\uff0c\u773c\u524d\u624d\u4f1a\u51fa\u73b0\u8fd9\u79cd\u5e7b\u50cf\u3002\u5f53\u5373\u63c9\u4e86\u63c9\u773c\u775b\uff0c \u518d\u62ac\u5934\u671b\u53bb\uff0c\u201c\u65e0\u60c5\u78a7\u5251\u201d\u8fd8\u4e0d\u662f\u597d\u597d\u6302\u5728\u6811\u68a2\u3002 \u3000\u3000\u6302\u5251\u7684\u6811\u68a2\uff0c\u8ddd\u5730\u8db3\u6709\u56db\u4e08\u4f59\u9ad8\uff0c\u4e00\u6708\u4f59\u524d\uff0c\u201c\u8fa3\u624b\u7ae5\u5fc3\u201d\u8d39\u4e00\u7ae5\u62a2\u53bb\u5c55\u767d\u7684\u5c0f\u888b\u5b50\uff0c \u628a\u91cc\u8fb9\u7684\u4e1c\u897f\u4e00\u4e00\u4e22\u6389\uff0c\u7136\u540e\u628a\u888b\u5b50\u6302\u5728\u8ddd\u5730\u4e09\u4e08\u9ad8\u7684\u88ab\u5934\uff0c\u5c55\u767d\u5373\u65e0\u6cd5\u53d6\u4e0b\u3002\u8fd9\u780d\u6302\u5251\u6280 \u5934\uff0c\u8ddd\u5730\u56db\u4e08\u4f59\u9ad8\uff0c\u6309\u7406\u5c55\u767d\u51b3\u65e0\u6cd5\u8dc3\u4e0a\uff1b\u53ef\u662f\uff0c\u5c55\u767d\u5fc3\u6025\u53d6\u5251\uff0c\u5e76\u6ca1\u6709\u8003\u8651\u8fd9\u4e9b\uff0c\u5f53\u4ed6\u8bc1 \u660e\u201c\u65e0\u60c5\u78a7\u5251\u201d\u786e\u5b9e\u6302\u5728\u90a3\u91cc\uff0c\u7acb\u5373\u62d4\u8d77\u8eab\u5f62\uff0c\u55d6\u201d\u7684\u4e00\u58f0\uff0c\u4e00\u4e0b\u5b50\u5ba1\u8d77\u8db3\u6709\u56db\u4e08\u4f59\u9ad8\uff0c\u534a \u7a7a\u4e2d\u8eab\u5f62\u4e00\u6298\uff0c\u201c\u9756\u8712\u62b5\u67f1\u201d\uff0c\u4f38\u624b\u6284\u4f4f\u5251\u67c4\uff0c\u4eba\u4e5f\u98d8\u8eab\u800c\u4e0b\u3002\u201c\u597d\u8eab\u6cd5\uff01\u201d\u5c55\u767d\u5fc3\u6025\u53d6 \u5251\uff0c\u5bf9\u81ea\u5df1\u7684\u8f7b\u8eab\u63d0\u7eb5\u672f\uff0c\u5ffd\u7136\u589e\u9ad8\u4e86\u8bb8\u591a\uff0c\u5e76\u672a\u7559\u610f\u3002\u4f46\u8eab\u540e\u4f20\u6765\u4e00\u58f0\u559d\u679c\uff0c\u5374\u628a\u5c55\u767d\u5413 \u4e86\u4e00\u8df3\u3002 \u3000\u3000\u5c55\u767d\u624b\u4e2d\u4e4b\u7269\uff0c\u6709\u4e24\u6b21\u88ab\u62a2\u7684\u7ecf\u9a8c\uff0c\u90a3\u771f\u662f\u4f7f\u4eba\u75db\u4e0d\u6b32\u751f\u3002\u8fd9\u6b21\u5931\u5251\u521a\u4e00\u5230\u624b\uff0c\u7a81\u7136\u8eab \u540e\u53c8\u73b0\u654c\u8e2a\uff0c\u5c55\u767d\u51e0\u6210\u60ca\u5f13\u4e4b\u9e1f\uff0c\u811a\u843d\u5730\u9762\u4e4b\u540e\uff0c\u624b\u63e1\u5251\u67c4\uff0c\u6307\u6309\u5251\u67c4\u5361\u7c27\uff0c\u201c\u545b\u7405\uff01\u201d\u4e00 \u58f0\u9f99\u541f\uff0c\u201c\u65e0\u60c5\u78a7\u5251\u201d\u65ad\u9798\uff0c\u95ea\u8d77\u4e00\u6e9c\u78a7\u5149\uff0c\u5c55\u767d\u5c31\u64a4\u5251\u51fa\u9798\u4e4b\u52bf\uff0c\u53cd\u81c2\u540e\u62a1\uff0c\u4e00\u5f0f\u201c\u591c\u6218 \u516b\u65b9\u201d\uff0c\u65e0\u60c5\u78a7\u5251\u5728\u8eab\u540e\u634c\u4e86\u4e00\u9053\u5149\u5f27\uff0c\u7136\u540e\u8f6c\u8eab\u5c55\u773c\u56db\u987e\u3002 \u3000\u3000\u5c55\u767d\u4e0d\u7531\u5012\u62bd\u4e86\u4e00\u53e3\u51b7\u6c14\u3002 \u3000\u3000\u539f\u6765\u7ad9\u5728\u5c55\u767d\u8eab\u540e\u7684\uff0c\u7adf\u662f\u4e24\u4e2a\u8eab\u7a7f\u767d\u8272\u9ebb\u8863\uff0c\u8170\u7cfb\u8349\u7ef3\uff0c\u957f\u53d1\u62ab\u80a9\uff0c\u9762\u76ee\u5446\u677f\u5f97\u6beb\u65e0 \u4e00\u70b9\u8868\u60c5\uff0c\u800c\u4e14\u8138\u8272\u60e8\u767d\u5f97\u65e0\u70b9\u6ef4\u8840\u8272\u7684\u4e24\u4e2a\u602a\u4eba\u3002 \u3000\u3000\u8fd9\u4e24\u4e2a\u602a\u4eba\uff0c\u65e0\u58f0\u65e0\u606f\u5730\u51fa\u73b0\u5728\u5c55\u767d\u8eab\u540e\uff0c\u604d\u5982\u4e24\u5177\u5e7d\u7075\u3002\u800c\u4e14\uff0c\u8fd9\u4e24\u4e2a\u602a\u4eba\u5468\u8eab\u5e26\u7740 \u4e00\u79cd\u9b3c\u6c14\uff0c\u4ece\u8fd9\u4e24\u4e2a\u5e7d\u7075\u822c\u7684\u602a\u4eba\u51fa\u73b0\u4e4b\u540e\u987f\u4f7f\u8fd9\u9602\u65e0\u4eba\u8ff9\u7684\u5bc6\u677e\u6797\uff0c\u4e5f\u7b3c\u7f69\u4e0a\u4e00\u5c42\u9634\u68ee\u68ee \u7684\u611f\u89c9\u3002 \u3000\u3000\u867d\u7136\u662f\u4e3d\u65e5\u4e2d\u5929\uff0c\u5c55\u767d\u5374\u6709\u6050\u6016\u9634\u68ee\u4e4b\u611f\uff0c\u604d\u5982\u7f6e\u8eab\u5730\u72f1\uff0c\u5468\u8eab\u6c57\u6bdb\u6839\u6839\u53d1\u70b8\u3002 \u3000\u3000\u5c24\u5176\u5947\u602a\u7684\uff0c\u8fd9\u4e24\u4e2a\u602a\u4eba\uff0c\u65e0\u8bba\u8863\u7740\u6253\u626e\uff0c\u9762\u8c8c\u5f62\u72b6\uff0c\u65e0\u4e00\u4e0d\u540c\uff0c\u51e0\u4e4e\u5982\u4e00\u4eba\u5206\u8eab\u4e3a\u4e8c \u4eba\u4e00\u822c\u3002 \u3000\u3000\u5c31\u5728\u5c55\u767d\u60ca\u6016\u5931\u795e\u4e4b\u4e2d\uff0c\u5176\u4e2d\u4e00\u4e2a\u602a\u4eba\u6bd7\u7259\u4e00\u7b11\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u4ed6\u8fd9\u7b11\u5bb9\u6bd4\u4e0d\u7b11\u66f4\u5413\u4eba\uff0c\u9762\u4e0a\u808c\u8089\u52a8\u90fd\u4e0d\u52a8\uff0c\u53ea\u9732\u51fa\u4e24\u6392\u81ea\u68ee\u68ee\u7684\u7259\u9f7f\u3002 \u3000\u3000\u53e6\u4e00\u4e2a\u602a\u4eba\uff0c\u5374\u5411\u5c55\u767d\u4e00\u4f38\u624b\uff0c\u9634\u68ee\u68ee\u5730\u559d\u9053\uff1a\u201c\u62ff\u6765\uff01\u201d \u3000\u3000\u5c55\u767d\u9000\u540e\u4e00\u6b65\uff0c\u6a2a\u5251\u5f53\u80f8\uff0c\u5fc3\u4e2d\u6697\u4e0b\u51b3\u5fc3\uff1a\u8fd9\u4e00\u6b21\u65e0\u8bba\u5982\u4f55\u4e0d\u80fd\u518d\u628a\u7236\u4eb2\u7684\u9057\u5251\u5931\u843d\uff0c \u7eb5\u7136\u4e00\u6b7b\uff0c\u4e5f\u5728\u6240\u4e0d\u60dc\uff01\u60f3\u7f62\u8bf4\u9053\uff1a\u201c\u5728\u4e0b\u4e0e\u4e8c\u4f4d\u7d20\u4e0d\u76f8\u8bc6\uff0c\u4e0d\u77e5\u4e8c\u4f4d\u53eb\u5728\u4e0b\u62ff\u4ec0\u4e48\u6765\uff1f\u201d \u3000\u3000\u201c\u54af\uff01\u54af\uff01\u54af\uff01\u2026\u2026\u201d \u3000\u3000\u4e24\u602a\u4eba\u9f50\u58f0\u602a\u7b11\uff0c\u58f0\u5982\u9e21\u557c\u3002\u7b11\u5f97\u5c55\u767d\u5468\u8eab\u76f4\u8d77\u9e21\u76ae\u7599\u7629\u3002 \u3000\u3000\u201c\u7b2c\u4e00\u8981\u4f60\u5148\u62ff\u5251\u6765\uff01\u201d\u4e24\u4e2a\u602a\u4eba\u7b11\u7f62\uff0c\u4ecd\u7531\u5176\u4e2d\u4e4b\u4e00\u5148\u53d1\u95fb\u3002 \u3000\u3000\u9a7e\u9732\u4e8c\u8981\u4f60\u518d\u628a\u547d\u62ff\u6765I\u4e8c\u4e8b\u4e3a\u4e00\uff0c\u6211\u770b\u4f60\u8fd8\u662f\u5148\u628a\u5251\u62ff\u6765\u6bd4\u8f83\u65b9\u4fbf\uff0c\u7701\u5f97\u4f60\u6b7b\u540e\uff0c\u6211 \u8001\u4eba\u5bb6\u8fd8\u5f97\u5f2f\u8170\u62fe\u5251\uff01\u201d \u3000\u3000\u8fd9\u8bdd\u72c2\u50b2\u5df2\u6781\uff0c\u76f4\u628a\u5c55\u767d\u89c6\u5982\u65e0\u7269\u3002\u5c55\u81ea\u542c\u7f62\uff0c\u5251\u7709\u4e00\u8f69\uff0c\u6fc0\u8d77\u6ee1\u8154\u6012\u706b\uff0c\u65e9\u628a\u751f\u6b7b\u4e4b \u4e8b\u7f6e\u4e4b\u5ea6\u5916\uff0c\u51b7\u7b11\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u4e8c\u4f4d\u5927\u8a00\u4e0d\u60ed\uff0c\u8bf7\u62a5\u4e0a\u4e2a\u540d\u513f\u6765\uff01\u6211\u5c55\u767d\u5251\u4e0b\u4e5f\u4e0d\u6b7b\u65e0\u540d \u4e4b\u9b3c\uff01\u201d \u3000\u3000\u4e8c\u602a\u4eba\u542c\u5c55\u767d\u81ea\u62a5\u59d3\u540d\uff0c\u4e92\u76f8\u5bf9\u671b\u4e86\u4e00\u773c\uff0c\u6beb\u65e0\u8868\u60c5\u7684\u8138\u4e0a\uff0c\u7adf\u4e5f\u8038\u52a8\u5b50\u2014\u4e0b\uff0c\u9f50\u58f0\u95ee \u9053\uff1a\u201c\u600e\u4e48\uff01\u4f60\u4e5f\u59d3\u5c55\uff01\u4e0d\u4f1a\u662f\u5047\u7684\u5427\uff1f\u201d \u3000\u3000\u201c\u5c82\u6709\u6b64\u7406\uff01\u201d \u3000\u3000\u5c55\u81ea\u5fc3\u8bf4\uff1a\u201c\u59d3\u8fd8\u6709\u5047\u7684\uff1f\u201d \u3000\u3000\u60f3\u7f62\u50b2\u7136\u8bf4\u9053\uff1a\u201c\u662f\u4e0d\u662f\u4e8c\u4f4d\u7528\u7684\u5047\u59d3\u5047\u540d\uff0c\u624d\u4e0d\u6562\u8bf4\u51fa\u6765\uff1f\u201d \u3000\u3000\u8c01\u77e5\u8fd9\u8bdd\uff0c\u6b63\u89e6\u52a8\u4e86\u4e8c\u602a\u4eba\u7684\u9690\u75db\u3002\u53ea\u89c1\u5176\u4e2d\u4e4b\u4e00\u53f1\u9053\uff1a\u201c\u6211\u53eb\u6d3b\u6b7b\u4eba\uff01\u201d \u3000\u3000\u53e6\u4e00\u4e2a\u4e5f\u5389\u58f0\u53f1\u9053\uff1a\u201c\u6211\u53eb\u6b7b\u6d3b\u4eba\uff01\u201d \u3000\u3000\u4e8c\u4eba\u53c8\u540c\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u4fe9\u8fd8\u771f\u662f\u65e0\u540d\u65e0\u59d3\uff0c\u4f46\u8bf4\u51fa\u540d\u53f7\u4e4b\u9645\uff0c\u4e5f\u5c31\u662f\u4f60\u6b7b\u4ea1\u964d\u4e34\u4e4b\u65f6\uff01 \u3000\u3000\u4e24\u602a\u4eba\u8bf4\u7f62\uff0c\u540c\u65f6\u7eb5\u8d77\u8eab\u6765\uff0c\u638c\u3001\u722a\u517c\u65bd\uff0c\u5411\u5c55\u767d\u731b\u6251\u9762\u53bb\u3002 \u3000\u3000\u5c55\u767d\u624b\u4e2d\u5251\u4e00\u7d27\uff0c\u5de6\u5c01\u53f3\u6321\uff0c\u63a5\u8fde\u65bd\u51fa\u4e94\u516d\u62db\uff0c\u624d\u628a\u4e24\u4e2a\u602a\u4eba\u903c\u9000\u3002 \u3000\u3000\u8fd1\u65e5\u6765\uff0c\u5c55\u767d\u63a5\u8fde\u4f1a\u8fc7\u4e0d\u5c11\u6b66\u6797\u9876\u5c16\u9ad8\u624b\uff0c\u4f46\u4ece\u672a\u89c1\u8fc7\u5982\u6b64\u602a\u5f02\u7684\u62db\u5f0f\u3002 \u3000\u3000\u4e24\u4e2a\u602a\u4eba\u62db\u5f0f\u51fa\u624b\uff0c\u4f3c\u6162\u6e10\u5feb\uff0c\u770b\u4ed6\u638c\u6307\u7f13\u7f13\u800c\u51fa\uff0c\u7a81\u5730\u72c2\u96e8\u66b4\u98ce\u800c\u81f3\uff1b\u5ffd\u7136\u53c8\u5feb\u800c \u6f2b\uff0c\u89c1\u4ed6\u95ea\u7535\u8ffd\u98ce\u800c\u81f3\uff0c\u7a81\u5730\u53c8\u51dd\u6b62\u5728\u8ddf\u524d\uff0c\u638c\u6307\u7f13\u7f13\u5212\u51fa\u3002\u602a\u4eba\u6bcf\u51fa\u4e00\u62db\uff0c\u5c55\u767d\u5fc5\u987b\u8fde\u6362 \u4e09\u4e94\u62db\uff0c\u624d\u80fd\u6321\u4f4f\uff0c\u56e0\u6b64\uff0c\u4e24\u4eba\u4e92\u653b\u51fa\u4e24\u4e09\u62db\uff0c\u5df2\u628a\u5c55\u81ea\u95f9\u4e86\u4e2a\u624b\u5fd9\u811a\u4e71\u3002 \u3000\u3000\u6b64\u65f6\uff0c\u5c55\u767d\u5b8c\u5168\u5931\u5374\u4e86\u4e0e\u201c\u72ec\u811a\u98de\u9b54\u201d\u52a8\u624b\u65f6\u7684\u4ece\u5bb9\u9547\u5b9a\u3002\u56e0\u4e3a\u201c\u72ec\u811a\u98de\u9b54\u201d\u62db\u5f0f\u518d \u5feb\uff0c\u603b\u6709\u8109\u7edc\u53ef\u5faa\uff0c\u5982\u4eca\uff0c\u8fd9\u4e24\u602a\u4eba\u65bd\u51fa\u7684\u624b\u6cd5\uff0c\u5374\u662f\u6beb\u65e0\u8ff9\u8c61\u53ef\u9274\u3002 \u3000\u3000\u5c55\u767d\u7684\u6b66\u529f\uff0c\u539f\u5c31\u5f88\u6742\uff0c\u5f53\u521d\u4ed6\u867d\u5728\u6b66\u5b66\u4e00\u9053\u4e0a\uff0c\u523b\u4e0b\u5341\u6570\u5e74\u7684\u82e6\u529f\uff0c\u4f46\u7f3a\u4e4f\u660e\u5e08\u6307 \u70b9\uff0c\u6240\u5b66\u7684\u4e0d\u8fc7\u662f\u6781\u666e\u901a\u7684\u6b66\u529f\u62db\u5f0f\u3002\u540e\u6765\u4ed6\u82e6\u4e60\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\u4f5b\u95e8\u6b63\u5b97\u5fc3\u6cd5\uff0c\u5185\u529f \u5927\u589e\uff0c\u8033\u76ee\u9510\u654f\uff0c\u5927\u903e\u5bfb\u5e38\uff0c\u624d\u8fbe\u5230\u4e86\u4fee\u4e60\u4e0a\u4e58\u6b66\u529f\u7684\u95e8\u5f84\u3002\u53ef\u662f\uff0c\u5bf9\u5947\u5965\u7684\u5251\u638c\u62db\u5f0f\uff0c\u4ed6 \u4ecd\u662f\u4e00\u7a8d\u4e0d\u901a\u3002\u7136\u540e\uff0c\u4ed6\u63a5\u8fde\u4e0e\u9ad8\u624b\u8fc7\u62db\uff0c\u90fd\u662f\u4ece\u522b\u4eba\u7684\u5947\u5965\u62db\u5f0f\u4e2d\uff0c\u89e6\u52a8\u4e86\u4ed6\u7684\u7075\u601d\uff0c\u624d \u5b66\u4f1a\u4e86\u4e09\u62db\u4e94\u5f0f\uff0c\u5176\u4e2d\u4e0d\u8fde\u63a5\u4e4b\u5904\uff0c\u8fd8\u5f97\u7531\u81ea\u5df1\u4e34\u673a\u5e94\u53d8\uff0c\u8bbe\u6cd5\u5f25\u8865\u3002 \u3000\u3000\u4ed6\u4e0e\u201c\u4e09\u5bf8\u4e01\u201d\u52a8\u624b\u65f6\uff0c\u4ed6\u7684\u5185\u529f\u6f5c\u529b\uff0c\u6bd4\u201c\u4e09\u5bf8\u4e01\u201d\u9ad8\u5f97\u591a\uff0c\u4f46\u4ec5\u80fd\u4e0e\u201c\u4e09\u5bf8\u4e01\u201d\u6253 \u6210\u5e73\u624b\uff0c\u5c31\u662f\u56e0\u4e3a\u62db\u5f0f\u4e0d\u7eaf\u719f\u7684\u539f\u56e0\u3002 \u3000\u3000\u5982\u4eca\uff0c\u5728\u8fd9\u4e24\u602a\u4eba\u7684\u602a\u5f02\u624b\u6cd5\u5939\u653b\u4e4b\u4e0b\uff0c\u7acb\u523b\u4f7f\u5c55\u81ea\u6349\u895f\u89c1\u8098\uff0c\u5de6\u53f3\u652f\u62d9\u3002 \u3000\u3000\u5341\u6570\u62db\u5df2\u8fc7\uff0c\u5c55\u767d\u5df2\u662f\u5b88\u591a\u653b\u5c11\uff0c\u957f\u5251\u6bcf\u6bcf\u9012\u4e0d\u5230\u90e8\u4f4d\uff0c\u5373\u88ab\u4e24\u4e2a\u602a\u4eba\u5f3a\u52b2\u638c\u98ce\u903c\u56de\u3002 \u3000\u3000\u5c55\u767d\u6108\u6765\u6108\u611f\u5fc3\u60ca\uff0c\u8f6c\u773c\u56db\u9762\u516b\u65b9\uff0c\u5747\u662f\u9762\u76ee\u9634\u68ee\u7684\u767d\u8272\u4eba\u5f71\uff1b\u6709\u7684\u5feb\u5982\u98d8\u98ce\uff0c\u95ea\u773c\u6251 \u81f3\u773c\u524d\uff1b\u6709\u7684\u51dd\u7acb\u9762\u524d\uff0c\u6d51\u5982\u4e0d\u52a8\uff0c\u5c48\u6307\u4f38\u638c\u6293\u5411\u81ea\u5df1\u3002\u4f46\u4e0d\u7559\u662f\u5feb\u8fd8\u662f\u6162\uff0c\u4e1d\u4e1d\u52b2\u98ce\uff0c\u900f \u9aa8\u5947\u5bd2\uff0c\u62f3\u62f3\u6307\u5f71\uff0c\u89e6\u80a4\u751f\u75db\uff0c\u800c\u4e14\u56f4\u5728\u81ea\u5df2\u56db\u5468\u7684\u767d\u8272\u602a\u4eba\uff0c\u6108\u6765\u6108\u591a\uff0c\u4e24\u4e2a\u53d8\u6210\u56db\u4e2a\uff0c \u56db\u4e2a\u53d8\u6210\u516b\u4e2a\uff0c\u516b\u4e2a\u53d8\u6210\u5341\u516d\u4e2a\uff0c\u5341\u516d\u4e2a\u53d8\u6210\u66f4\u591a\u3002 \u3000\u3000\u5c55\u81ea\u867d\u7136\u5fc3\u4e2d\u660e\u767d\uff0c\u773c\u524d\u5e7b\u50cf\u662f\u7531\u4e8e\u4e24\u4e2a\u602a\u4eba\u8eab\u6cd5\u53d8\u5e7b\u6240\u81f4\uff0c\u4f46\u5df2\u4e0d\u77e5\u54ea\u4e2a\u662f\u5b9e\uff0c\u54ea\u4e2a \u662f\u865a\uff1f\u53ea\u6709\u628a\u201c\u65e0\u60c5\u78a7\u5251\u201d\u821e\u4e86\u4e2a\u98ce\u96e8\u4e0d\u900f\uff0c\u4f9b\u6c42\u81ea\u4fdd\u3002 \u3000\u3000\u5c55\u767d\u7684\u201c\u4e09\u624d\u5251\u6cd5\u201d\u662f\u6781\u4e3a\u666e\u901a\u7684\u4e00\u5957\u5251\u6cd5\uff0c\u4f46\u7531\u5c55\u767d\u624b\u4e2d\u65bd\u5c55\u51fa\u6765\uff0c\u5374\u53c8\u4e0d\u540c\u51e1\u54cd\u3002 \u4e00\u662f\u201c\u65e0\u60c5\u78a7\u5251\u201d\u4e3a\u4e00\u6b66\u6797\u81f3\u5b9d\uff0c\u4e8c\u662f\u5c55\u767d\u5185\u529f\u771f\u529b\u6fc0\u589e\u3002\u4e00\u5957\u6b66\u6797\u4e60\u89c1\u7684\u201c\u4e09\u624d\u5251\u6cd5\u201d\uff0c \u7531\u5c55\u767d\u65bd\u5c55\u51fa\u6765\uff0c\u5c45\u7136\u5bd2\u5149\u6eda\u6eda\uff0c\u72b9\u5982\u6012\u9f99\u960e\u6d77\uff0c\u51b7\u68ee\u68ee\u7684\u5251\u6c14\uff0c\u6251\u9762\u751f\u5bd2\uff0c\u821e\u5230\u5feb\u65f6\uff0c\u5ffd \u5ffd\u9686\u9686\uff0c\u7adf\u7136\u9690\u631f\u98ce\u96f7\u4e4b\u58f0\u3002 \u3000\u3000\u4e24\u4e2a\u602a\u4eba\u7684\u5403\u60ca\uff0c\u4e0d\u4e0b\u4e8e\u5c55\u767d\uff0c\u56e0\u4e3a\u5f1f\u5144\u4e24\u4e2a\u8054\u624b\u7684\u201c\u592a\u6781\u4e24\u4eea\u79bb\u9b42\u638c\u201d\uff0c\u5f88\u5c11\u4eba\u80fd\u591f \u5728\u638c\u4e0b\u8d70\u51fa\u5341\u62db\u53bb\uff0c\u800c\u9762\u524d\u8fd9\u6beb\u4e0d\u8d77\u773c\u7684\u5c11\u5e74\uff0c\u7ade\u80fd\u529b\u6218\u5341\u6570\u62db\u800c\u4e0d\u8d25\u3002 \u3000\u3000\u6643\u773c\u53c8\u662f\u4e94\u516d\u62db\u8fc7\u53bb\u4e86\u3002 \u3000\u3000\u201c\u6d3b\u6b7b\u4eba\u201d\u8fd8\u80fd\u6c89\u5f97\u4f4f\u6c14\uff0c\u4e0d\u7d27\u4e0d\u6162\u3001\u4e00\u62db\u4e00\u5f0f\u5730\u5f80\u4e0b\u6253\uff1a\u201c\u6b7b\u6d3b\u4eba\u201d\u6027\u5b50\u6025\u800c\u70c8\uff0c\u89c1 \u4e45\u6218\u5c55\u767d\u4e0d\u4e0b\uff0c\u5389\u5578\u4e00\u58f0\uff0c\u5de6\u638c\u6a2a\u524a\uff0c\u53f3\u638c\u7ad6\u780d\uff0c\u4e00\u5f0f\u201c\u9634\u9633\u5f02\u8def\u201d\uff0c\u731b\u7f69\u5c55\u767d\u4e24\u8def\u4e94\u5904\u8981 \u7a74\u3002 \u3000\u3000\u8fd9\u4e00\u62db\u51cc\u5389\u65e0\u6bd4\uff0c\u5c55\u81ea\u88ab\u4e24\u4e2a\u602a\u4eba\u56f4\u56f0\u5f97\u5934\u660f\u773c\u82b1\uff0c\u65e9\u5df2\u4e0d\u77e5\u654c\u4eba\u62db\u5f0f\u4ece\u4f55\u800c\u81f3\uff0c\u53ea\u987e \u594b\u529b\u628a\u65e0\u60c5\u78a7\u5251\u821e\u4e86\u4e2a\u98ce\u96e8\u4e0d\u900f\uff0c\u4e0d\u6c42\u4f24\u654c\uff0c\u53ea\u6c42\u81ea\u4fdd\uff0c\u5bf9\u654c\u4eba\u51cc\u5389\u6740\u62db\u6d51\u7136\u4e0d\u89c9\u3002 \u3000\u3000\u53ef\u4ee5\u8bf4\u7b49\u4e8e\u76f2\u4eba\u9a91\u778e\u9a6c\uff0c\u8d70\u5230\u5371\u9669\u8fb9\u7f18\u800c\u4e0d\u81ea\u77e5\u2026\uff0e\xb7 \u3000\u3000\u4f46\u201c\u6d3b\u6b7b\u4eba\u201d\u7a81\u7136\u770b\u89c1\u5c55\u81ea\u5251\u7a57\u4e0a\uff0c\u60ac\u5760\u4e00\u7269\uff0c\u5fc3\u4e2d\u731b\u7136\u4e00\u9707\uff0c\u6709\u610f\u65e0\u610f\u4e4b\u95f4\uff0c\u51fa\u624b\u767d \u7684\u6740\u62db\uff0c\u963b\u4e86\u4e00\u963b\uff1b\u6b63\u5f53\u201c\u6b7b\u6d3b\u4eba\u201d\u8981\u8df3\u811a\u53d1\u813e\u6c14\u65f6\uff0c\u201c\u6d3b\u6b7b\u4eba\u201d\u51fa\u624b\u5982\u98ce\uff0c\u5df2\u628a\u5c55\u767d\u5251\u7a57 \u4e0a\u98d8\u5760\u4e4b\u7269\u6293\u5230\u624b\u4e2d\uff0c\u8ddf\u7740\u98d8\u8eab\u540e\u9000\u3002 \u3000\u3000\u201c\u6b7b\u6d3b\u4eba\u201d\u867d\u7136\u4e0d\u77e5\u201c\u6d3b\u6b7b\u4eba\u201d\u6b64\u4e3e\u7684\u7528\u610f\uff0c\u4f46\u4e8c\u4eba\u5411\u6765\u540c\u8fdb\u540c\u9000\uff0c\u89c1\u201c\u6d3b\u6b7b\u4eba\u201d\u7a9c\u51fa \u5916\u5708\uff0c\u9cd6\u7740\u4e00\u809a\u5b50\u7684\u4e0d\u9ad8\u5174\uff0c\u4e5f\u968f\u540e\u5012\u8dc3\u800c\u51fa\u3002 \u3000\u3000\u5c55\u767d\u987f\u89c9\u538b\u529b\u4e00\u51cf\uff0c\u56db\u5468\u767d\u8272\u4eba\u5f71\u5019\u7136\u800c\u6536\uff0c\u5fd9\u4e5f\u6536\u52bf\u505c\u8eab\uff0c\u6a2a\u5251\u800c\u7acb\u3002 \u3000\u3000\u4e24\u4e2a\u602a\u4eba\u5df2\u7ecf\u6253\u5f00\u4e00\u4e2a\u7ef8\u5e03\u5c0f\u5305\uff0c\u968f\u624b\u6293\u51fa\u4e00\u56e2\u4e71\u53d1\uff0c\u4e24\u4e2a\u602a\u4eba\u5148\u81ea\u5bf9\u671b\u4e86\u4e00\u773c\uff0c\u7136\u540e \u5411\u5c55\u767d\u9762\u524d\u4e00\u9012\uff0c\u5428\u95ee\u9053\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5c55\u767d\u4e00\u773c\u770b\u89c1\u4e24\u602a\u4eba\u624b\u4e2d\u62ff\u7684\u90a3\u56e2\u4e71\u53d1\uff0c\u4e0d\u7981\u70ed\u8840\u4e0a\u51b2\uff0c\u53cc\u76ee\u5c3d\u8d64\u3002 \u3000\u3000\u90a3\u4e0d\u6b63\u662f\u201c\u8fa3\u624b\u7ae5\u5fc3\u201d\u8d39\u4e00\u7ae5\u7ed9\u81ea\u5df1\u4e22\u6389\uff0c\u81ea\u5df1\u82e6\u5bfb\u4e0d\u83b7\u7684\u7236\u4eb2\u9057\u7269\u5417\uff1f \u3000\u3000\u201c\u8fd8\u7ed9\u6211\uff01\u201d \u3000\u3000\u5c55\u767d\u5389\u58f0\u5636\u543c\u3002 \u3000\u3000\u201c\u4f60\u4eec\u662f\u4ece\u54ea\u91cc\u6361\u6765\u7684\uff1f\u201d \u3000\u3000\u4e24\u4e2a\u602a\u4eba\u4f30\u4e0d\u5230\u5c55\u81ea\u5ffd\u7136\u53d8\u5f97\u8fd9\u4e48\u51f6\uff0c\u6487\u4e86\u6487\u5634\uff0c\u628a\u90a3\u56e2\u4e71\u53d1\uff0c\u5411\u5c55\u767d\u9762\u524d\u4e00\u4e22\u3002\u540c\u65f6 \u51b7\u51b7\u5730\u8bf4\u9053\uff1a\u201c\u8fd8\u4f60\u5c31\u8fd8\u4f60\uff0c\u4ec0\u4e48\u597d\u4e1c\u897f\uff1f\u201d \u3000\u3000\u8bf4\u7740\u53c8\u4ece\u7ef8\u5e03\u5305\u91cc\u7ffb\u51fa\u4e00\u6bb5\u4e1d\u6761\uff0c\u4e24\u602a\u4eba\u53c8\u5bf9\u671b\u4e86\u4e00\u773c\uff0c\u65e0\u8868\u60c5\u7684\u8138\u4e0a\uff0c\u4e5f\u62b9\u8fc7\u4e00\u4e1d\u7591 \u60d1\u4e4b\u8272\uff0c\u8f6c\u5411\u5c55\u767d\u95ee\u9053\uff1a\u201c\u8fd9\u53c8\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u5feb\u8fd8\u6211\uff01\u201d \u3000\u3000\u5c55\u767d\u672a\u7559\u5fc3\u201c\u6d3b\u6b7b\u4eba\u201d\u662f\u5728\u81ea\u5df1\u5251\u7a57\u4e0a\u6293\u53bb\u7684\u90a3\u4e2a\u7ef8\u5e03\u5305\uff0c\u53ea\u5947\u602a\u4e24\u4e2a\u602a\u4eba\u4ece\u4f55\u5f97\u6765\u7236 \u4eb2\u7684\u9057\u7269\uff1f\u540c\u65f6\uff0c\u5185\u5fc3\u53c8\u6fc0\u52a8\u4e07\u5206\uff0c\u8fde\u58f0\u53eb\u9053\uff1a\u201c\u90a3\u5c0f\u5305\u7684\u4e1c\u897f\u90fd\u662f\u6211\u7684\uff01\u201d \u3000\u3000\u4e24\u4e2a\u4eba\u4e0d\u7406\u5c55\u767d\u53eb\u95f9\uff0c\u628a\u90a3\u6bb5\u4e1d\u6761\u4e22\u7ed9\u5c55\u767d\uff0c\u53c8\u4ece\u7ef8\u5e03\u5305\u5185\uff0c\u63a5\u8fde\u7ffb\u51fa\u4e00\u7c92\u94a2\u73e0\uff0c\u4e00\u4e2a \u9752\u94dc\u94ae\u6263\uff0c\u4e00\u4e00\u4e22\u8fd8\u5c55\u767d\u3002 \u3000\u3000\u6700\u540e\uff0c\u90a3\u4e24\u4e2a\u602a\u4eba\u4ece\u7ef8\u5e03\u5305\u5185\u7ffb\u51fa\u4e00\u679a\u9752\u94dc\u5236\u94b1\uff0c\u7acb\u523b\u5982\u89e6\u86c7\u874e\uff0c\u731b\u7136\u8df3\u4e86\u8d77\u6765\uff0c\u72c2\u5578 \u5389\u543c\uff0c\u53cc\u624b\u628a\u81ea\u5df1\u5934\u4e0a\u7684\u62ab\u80a9\u957f\u53d1\uff0c\u7f15\u7f15\u6293\u843d\uff0c\u6f2b\u7a7a\u98d8\u626c\u3002 \u3000\u3000\u8fd9\u4e00\u56de\u8be5\u8f6e\u5230\u5c55\u767d\u5403\u60ca\u4e86\uff0c\u4ed6\u4e0d\u77e5\u8fd9\u4e24\u4e2a\u602a\u4eba\u4e3a\u4ec0\u4e48\u5ffd\u7136\u53d1\u8d77\u75af\u6765\uff1f \u3000\u3000\u4e24\u4e2a\u602a\u4eba\u8dcc\u811a\u6376\u80f8\uff0c\u6572\u81ea\u5df2\u7684\u8111\u888b\uff0c\u62d4\u626f\u81ea\u5df1\u7684\u5934\u53d1\uff0c\u60b2\u5636\u60e8\u547c\u5982\u9b3c\u54ed\u72fc\u568e\uff0c\u5404\u81ea\u53d1\u4e86 \u534a\u5929\u75af\uff1b\u53c8\u4e92\u76f8\u62b1\u5728\u4e00\u8d77\uff0c\u4e24\u5934\u4e92\u649e\uff0c\u201c\u562d\uff01\u562d\uff01\u201d\u53d1\u51fa\u5de8\u664c\uff0c\u6837\u5b50\u7adf\u50cf\u662f\u75db\u4e0d\u6b32\u751f\u2026\u2026 \u3000\u3000\u5c55\u767d\u5982\u5760\u4e94\u91cc\u96fe\u4e2d\uff0c\u6014\u6014\u5730\u671b\u5b9a\u4e24\u4e2a\u5ffd\u7136\u53d1\u75af\u7684\u602a\u4eba\uff0c\u83ab\u77e5\u6240\u4ee5\u2026\u2026 \u3000\u3000\u5ffd\u7684\uff0c\u90a3\u4e24\u4e2a\u602a\u4eba\u51fa\u624b\u5982\u98ce\uff0c\u4e00\u8fb9\u4e00\u4e2a\uff0c\u4e00\u4e2a\u6349\u4f4f\u5c55\u767d\u7684\u5de6\u81c2\uff0c\u4e00\u4e2a\u6349\u4f4f\u5c55\u767d\u7684\u53f3\u81c2\u3002 \u3000\u3000\u4e00\u662f\u5c55\u767d\u4e0d\u9632\uff0c\u4e8c\u662f\u4e24\u4e2a\u602a\u4eba\u51fa\u624b\u5b9e\u5728\u592a\u5feb\u4e86\u3002 \u3000\u3000\u5c55\u767d\u731b\u5403\u4e00\u60ca\uff0c\u53cc\u81c2\u88ab\u6293\u4e4b\u5904\uff0c\u75db\u5982\u9aa8\u6298\uff0c\u4f46\u4ecd\u7136\u54ac\u7259\u786c\u633a\u4f50\uff0c\u6ca1\u6709\u53d1\u51fa\u58f0\u6765\u3002 \u3000\u3000\u201c\u8fd9\u4fbf\u662f\u2018\u65e0\u60c5\u78a7\u5251\u2019\uff1f\u201d \u3000\u3000\u6293\u4f4f\u5c55\u81ea\u53f3\u81c2\u7684\u201c\u6d3b\u6b7b\u4eba\u201d\u60b2\u58f0\u95ee\u9053\u3002 \u3000\u3000\u5c55\u767d\u6297\u58f0\u558a\u9053\uff1a\u201c\u653e\u5f00\u6211\uff01\u201d \u3000\u3000\u201c\u4f60\u662f\u5c55\u4e91\u5929\u5c55\u5927\u4fa0\u7684\u540e\u4eba\uff1f\u201d \u3000\u3000\u6293\u4f4f\u5c55\u767d\u5de6\u81c2\u7684\u201c\u6b7b\u6d3b\u4eba\u201d\u51c4\u60e8\u95ee\u9053\u3002 \u3000\u3000\u5c55\u767d\u4e00\u9635\u5fc3\u60b2\uff0c\u51c4\u7136\u4e0d\u7b54\u3002 \u3000\u3000\u4e24\u4e2a\u602a\u4eba\u5ffd\u7136\u53c8\u653e\u5f00\u5c55\u767d\uff0c\u4e00\u9f50\u8eac\u8eab\u5411\u5c55\u767d\u65bd\u4e86\u4e00\u793c\uff0c\u7136\u540e\u201c\u6d3b\u6b7b\u4eba\u201d\u60b2\u58f0\u547c\u9053\uff1a\u201c\u82cd \u5929\u5b58\u773c\uff0c\u6069\u4eba\u6709\u540e\uff01\u201d \u3000\u3000\u201c\u6b7b\u6d3b\u4eba\u201d\u4e5f\u60b2\u58f0\u547c\u9053\uff1a\u201c\u82cd\u5929\u65e0\u773c\uff0c\u6069\u4eba\u51a4\u6c89\u6d77\u5e95\uff01\u201d \u3000\u3000\u201c\u4e0d\u7136\uff01\u201d\u201c\u6d3b\u6b7b\u4eba\u201d\u62c9\u4f4f\u201c\u6b7b\u6d3b\u4eba\u201d\uff0c\u628a\u624b\u4e2d\u90a3\u679a\u9752\u94dc\u5236\u94b1\u644a\u5728\u638c\u5fc3\u3002 \u3000\u3000\u51c4\u60e8\u8bf4\u9053\uff1a\u201c\u5144\u5f1f\uff0c\u4f60\u770b\u8fd9\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u545c\u2014\u2014\u554a\uff01\u201c\u6b7b\u6d3b\u4eba\u201d\u4ef0\u5929\u957f\u58f0\u60b2\u66dd\uff0c\u55e5\u58f0\u60b2\u58ee\u60e8\u70c8\uff0c\u51e0\u53ef\u7a7f\u77f3\u7834\u4e91\u3002 \u3000\u3000\u201c\u4f60\u6211\u5f1f\u5144\uff0c\u4e3a\u4e86\u6069\u4eba\u6b7b\u5f97\u4e0d\u660e\u4e0d\u81ea\uff0c\u4e00\u65f6\u53c8\u67e5\u8bbf\u4e0d\u51fa\u4ec7\u5bb6\u3002\u201d\u201c\u6b7b\u6d3b\u4eba\u201d\u60b2\u6666\u8fc7\u540e\uff0c \u6c89\u75db\u8bf4\u9053\uff1a\u201c\u539a\u989c\u6d3b\u5728\u4e16\u4e0a\uff0c\u6240\u8c13\u2018\u6709\u6069\u4e0d\u62a5\uff0c\u751f\u4e0d\u5982\u6b7b\u2019\u3002\u624d\u9690\u59d3\u57cb\u540d\uff0c\u4ee5\u2018\u6d3b\u6b7b\u4eba\u2019 \u2018\u6b7b\u6d3b\u4eba\u2019\u81ea\u79f0\uff0c\u5982\u4eca\u89c1\u4e86\u6b64\u7269\u2026\u2026\u201d \u3000\u3000\u201c\u6b7b\u6d3b\u4eba\u201d\u8bf4\u7740\u4e00\u6307\u201c\u6d3b\u6b7b\u4eba\u201d\u624b\u4e2d\u62ff\u7684\u9752\u94dc\u5236\u94b1\uff0c\u5fc3\u60c5\u66f4\u89c1\u60b2\u75db\uff0c\u6ee1\u9762\u6cea\u75d5\uff0c\u7ee7\u7eed\u8bf4 \u9053\uff1a\u201c\u5df2\u77e5\u4ec7\u4eba\u662f\u8c01\uff0c\u4f46\u5374\u4e0d\u80fd\u4e3a\u6069\u4eba\u590d\u4ec7\uff0c\u4f60\u636e\u5f1f\u5144\u8fd8\u6709\u4f55\u989c\u9762\uff0c\u5728\u4e16\u4e0a\u5077\u751f\uff1f\u201d \u3000\u3000\u201c\u662f\u5440\uff01\u201d\u201c\u6d3b\u6b7b\u4eba\u201d\u4e5f\u60b2\u54ed\u8d77\u6765\uff0c\u8ddf\u7740\u53cd\u95ee\u9053\uff1a\u201c\u5144\u5f1f\uff01\u6211\u4eec\u8fd8\u6709\u4ec0\u4e48\u8138\u9762\u6d3b\u5728\u4e16 \u4e0a\uff1f\u201d \u3000\u3000\u4e24\u602a\u4eba\u8bf4\u7f62\uff0c\u53c8\u62b1\u5934\u9e23\u9e23\u75db\u54ed\u8d77\u6765\u2026\u3002\xb7 \u3000\u3000\u5c55\u767d\u60f3\u4e0d\u5230\u4e24\u4e2a\u5e7d\u7075\u4f3c\u7684\u602a\u4eba\uff0c\u770b\u4f3c\u9634\u68ee\u51b7\u9177\uff0c\u5374\u5177\u6709\u5982\u6b64\u70ed\u70c8\u7684\u60c5\u611f\uff01\u800c\u4e14\uff0c\u542c\u4e8c\u4eba \u8bdd\u91cc\u7684\u542b\u610f\uff0c\u5206\u660e\u4e5f\u662f\u7236\u4eb2\u7684\u6545\u4ea4\u3002\u6b64\u65f6\u5c55\u767d\u5df2\u628a\u521d\u89c1\u4e8c\u4eba\u65f6\u7684\u53cd\u611f\u548c\u538c\u6076\u5316\u4e3a\u4e4c\u6709\uff0c\u53cd\u800c \u89c9\u5f97\u8ddf\u4e8c\u4eba\u5341\u5206\u4eb2\u5207\u8d77\u6765\uff0c\u5c31\u5982\u89c1\u4e86\u7236\u8f88\u7684\u4eb2\u53cb\u4e00\u822c\u3002\u53c8\u89c1\u4e8c\u4eba\u54ed\u5f97\u60b2\u5207\uff0c\u5fcd\u4e0d\u4f4f\u5728\u4e00\u65c1\u529d \u9053\uff1a\u201c\u4e8c\u4f4d\u4e14\u4e0d\u5fc5\u4f24\u5fc3\uff0c\u6709\u9053\u662f\u2018\u541b\u5b50\u62a5\u4ec7\uff0c\u5341\u5e74\u4e0d\u665a\u3002\u2019\u4e8c\u4f4d\u53ea\u8981\u6709\u8fd9\u4e00\u7247\u5fc3\uff0c\u4e0d\u8981\u8bf4\u662f \u5c55\u767d\uff0c\u5c31\u662f\u6211\u90a3\u8fc7\u4e16\u7684\u5148\u7236\uff0c\u5728\u4e5d\u6cc9\u4e4b\u4e0b\uff0c\u4e5f\u611f\u6fc0\u4e0d\u5c3d\u4e86\uff01\u201d \u3000\u3000\u8c01\u77e5\u5c55\u767d\u4e0d\u5b89\u6170\u4e8c\u4eba\u8fd8\u597d\uff0c\u5c55\u767d\u8fd9\u4e00\u5b89\u6170\u4e8c\u4eba\uff0c\u8bdd\u521a\u8bf4\u5b8c\uff0c\u4e8c\u4eba\u5ffd\u7136\u653e\u624b\u5206\u5f00\uff0c\u6cea\u5982\u6cc9 \u6d8c\u5730\u53eb\u9053\uff1a\u201c\u6127\u89c1\u6545\u4eba\uff01\u6127\u89c1\u6545\u4eba\uff01\u2026\u2026\u201d \u3000\u3000\u53eb\u7740\u53eb\u7740\uff0c\u201c\u6d3b\u6b7b\u4eba\u201d\u731b\u7136\u57cb\u5934\u5411\u4e00\u68f5\u6709\u4e24\u4eba\u5408\u62b1\u7684\u5de8\u677e\u6811\u5e72\u4e0a\u649e\u53bb\u3002 \u3000\u3000\u663e\u7136\u4ed6\u662f\u60b2\u4f24\u8fc7\u5206\uff0c\u60f3\u649e\u6811\u81ea\u6740\u3002 \u3000\u3000\u5c55\u767d\u63aa\u624b\u4e0d\u53ca\uff0c\u60f3\u62e6\u6ca1\u62e6\u4f4f\uff1b\u800c\u8fd9\u8fb9\u201c\u6b7b\u6d3b\u4eba\u201d\u4e5f\u540c\u6837\u57cb\u5934\u5411\u53e6\u4e00\u5de8\u5927\u677e\u5e72\u4e0a\u649e\u53bb\uff01 \u3000\u3000\u201c\u6628\u55b3\uff01\u201d \u3000\u3000\u201c\u6627\u55b3\uff01\u201d \u3000\u3000\u5148\u540e\u63a5\u8fde\u4e24\u58f0\u5de8\u54cd\uff0c\u8ddf\u7740\u201c\u8f70\u901a\uff01\u201d\u201c\u8f70\u901a\uff01\u201d\u4e24\u58f0\u5927\u9707\uff0c\u679d\u6e85\u53f6\u98de\uff0c\u5c18\u6d8c\u6c99\u626c\uff01 \u3000\u3000\u4e24\u602a\u4eba\u649e\u6811\u81ea\u6740\uff0c\u4e0d\u4f46\u81ea\u6740\u672a\u6210\uff0c\u53cd\u628a\u4e24\u68f5\u53cc\u4eba\u5408\u56f4\u4e0d\u62e2\u6765\u7684\u53c2\u5929\u5de8\u677e\u7ed9\u4e00\u5934\u649e\u5012\u3002 \u3000\u3000\u5c55\u767d\u89c1\u72b6\u6697\u6697\u6628\u820c\u4e0d\u5df2\uff0c\u770b\u8fd9\u4e24\u4e2a\u602a\u4eba\u8fd9\u57cb\u5934\u4e00\u649e\uff0c\u6015\u4e0d\u6709\u5343\u94a9\u529b\u9053\uff1f \u3000\u3000\u4f46\u4e24\u4e2a\u602a\u4eba\u4e00\u5934\u672b\u628a\u81ea\u5df1\u649e\u6b7b\uff0c\u5fc3\u72b9\u672b\u7518\uff0c\u63a5\u8fde\u53c8\u57cb\u5934\u649e\u53bb\uff01\u201c\u5494\u55b3\uff01\u8f70\u901a\uff01\u201d \u3000\u3000\u201c\u5494\u55b3\uff01\u8f70\u901a\uff01\u5494\u55b3\u2014\u2014\u8f70\u901a\uff01\u2026\uff0e\uff0c\uff0e\u201d \u3000\u3000\u58f0\u58f0\u66b4\u54cd\uff0c\u63a5\u8fde\u4f20\u6765\uff0c\u4e24\u602a\u4eba\u4e00\u5934\u4e00\u5934\u5730\u649e\u53bb\uff0c\u4e00\u68f5\u4e00\u68f5\u7684\u5de8\u677e\u5e94\u5934\u800c\u5012\uff0c\u628a\u6574\u5ea7\u5bc6\u677e \u6797\uff0c\u960e\u5f97\u5730\u8986\u5929\u7ffb\uff0c\u5c18\u57c3\u853d\u7a7a\u3002\u5982\u679c\u662f\u5728\u8fdc\u5904\u7684\u4eba\uff0c\u770b\u5230\u8fd9\u6837\u7684\u58f0\u52bf\uff0c\u8fd8\u4ee5\u4e3a\u8fd9\u677e\u6797\u91cc\u5c71\u5d29 \u5730\u9677\u4e86\u54e9\uff01\u5927\u6982\u4e24\u4e2a\u602a\u4eba\u81ea\u5df1\u4e5f\u660e\u767d\u6b32\u6d78\u649e\u6811\u81ea\u6740\u4e0d\u4e86\uff0c\u7ade\u820d\u4e0b\u5c55\u767d\u4e0d\u987e\uff0c\u60b2\u5929\u62a2\u5730\u7684\u75db\u54ed \u54c0\u53f7\u7740\u8dd1\u53bb\u3002 \u3000\u3000\u53ea\u89c1\u4e24\u4e2a\u602a\u4eba\u8eab\u5f62\u5982\u98de\uff0c\u957f\u53d1\u5e45\u626c\uff0c\u58f0\u58f0\u88c2\u5e1b\u4f3c\u5730\u60e8\u53f7\u60b2\u5578\uff0c\u6643\u773c\u8dd1\u5f97\u5931\u53bb\u8e2a\u8ff9\u3002 \u3000\u3000\u800c\u58f0\u58f0\u60b2\u5578\u8fd8\u6447\u66f3\u5728\u5929\u8fb9\uff0c\u54cd\u904d\u4e86\u6574\u4e2a\u8352\u91ce\u2026\u2026 \u3000\u3000\u5c55\u81ea\u60df\u7136\u671b\u7740\u4e24\u4e2a\u602a\u4eba\u53bb\u8fdc\uff0c\u6014\u949f\u826f\u4e45\uff0c\u624d\u6536\u56de\u5fc3\u795e\uff0c\u6697\u9053\uff1a\u201c\u770b\u4e0d\u51fa\u8fd9\u4e24\u4e2a\u5e7d\u7075\u4f3c\u7684 \u602a\u4eba\uff0c\u5012\u662f\u81f3\u60c5\u81f3\u6027\u4e4b\uff011\u201d \u3000\u3000\u201c\u5509\uff01\u201d\u5c55\u767d\u53c8\u53f9\u606f\u4e00\u58f0\uff0c\u6697\u81ea\u5fd6\u9053\uff1a\u201c\u542c\u4ed6\u4e8c\u4eba\u4e4b\u8a00\uff0c\u4f3c\u5df2\u77e5\u9053\u6740\u6b7b\u81ea\u5df2\u7236\u4eb2\u7684\u4ec7\u4eba \u662f\u8c01\uff0c\u4f46\u600e\u4e48\u53c8\u8bf4\u662f\u4e0d\u80fd\u4e3a\u7236\u4eb2\u62a5\u4ec7\u2026\u2026\u201d \u3000\u3000\u5c55\u767d\u60f3\u5230\u8fd9\u91cc\u731b\u7136\u50cf\u609f\uff0c\u7a81\u4e00\u8e81\u811a\uff0c\u7adf\u81ea\u53eb\u51fa\u58f0\u6765\uff1a\u201c\u54ce\u5440\uff01\u6211\u600e\u4e48\u5fd8\u4e86\u95ee\u95ee \u4ed6\u2026\u3002\u3002\u201d \u3000\u3000\u201c\u5a03\u5a03\uff01\u4f60\u5fd8\u4e86\u95ee\u8c01\uff1f\u201d\u7a81\u7136\u8eab\u540e\u6709\u4eba\u7b54\u4e86\u8154\u3002\u201c\u7adf\u81ea\u5df1\u8ddf\u81ea\u5df2\u8bf4\u8d77\u8bdd\u6765\uff01\u201d \u3000\u3000\u5c55\u767d\u56de\u5934\u4e00\u770b\uff0c\u5728\u8eab\u540e\u7ad9\u5b9a\u7684\u7ade\u662f\u90a3\u201c\u8fa3\u624b\u7ae5\u5fc3\u201d\u8d39\u4e00\u7ae5\u3002 \u3000\u3000\u5c55\u767d\u5fc3\u4e2d\u4e00\u5bd2\uff0c\u77e5\u9053\u6b64\u8001\u6700\u662f\u4e0d\u53ef\u7406\u55bb\uff0c\u4e0d\u7531\u5f97\u76b1\u4e86\u76b1\u7709\u2026\u2026 \u3000\u3000\u8d39\u4e00\u7ae5\u54c8\u54c8\u4e00\u7b11\uff0c\u6837\u5b50\u662f\u5f00\u5fc3\u5df2\u6781\uff0c\u772f\u7740\u4e24\u53ea\u8ddf\u775b\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u4f19\u5b50\uff0c\u4f60\u4e0d\u613f\u9047\u5230\u6211\u8001 \u4eba\u5bb6\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u5c55\u767d\u6ca1\u6709\u7406\u4ed6\u3002 \u3000\u3000\u201c\u53ef\u662f\uff0c\u504f\u504f\u53c8\u53eb\u6211\u4eec\u9047\u4e0a\u4e86\uff01\u800c\u4e14\uff0c\u8fd8\u662f\u8001\u5730\u65b9\uff0c\u5c0f\u4f19\u5b50\uff0c\u4f60\u80fd\u8bf4\u8fd9\u4e0d\u662f\u54b1\u7237\u4fe9\u6709\u7f18 \u5417\uff1f\u201d \u3000\u3000\u5c55\u767d\u8fd9\u624d\u731b\u7136\u8bb0\u8d77\uff0c\u8fd9\u5ea7\u677e\u6797\u539f\u662f\u81ea\u5df1\u9047\u5230\u201c\u71d5\u4e91\u4e94\u9738\u5929\u201d\u52ab\u9556\u4e4b\u5904\u3002\u65e7\u5730\u91cd\u6e38\uff0c\u60f3\u8d77 \u81ea\u5df2\u8fd9\u4e00\u4e2a\u591a\u6708\u6765\u7684\u5947\u5f02\u7ecf\u5386\uff0c\u771f\u662f\u5982\u6709\u9694\u4e16\u4e4b\u611f\uff01 \u3000\u3000\u201c\u65e2\u662f\u54b1\u7237\u4fe9\u6709\u7f18\uff0c\u201d\u8d39\u4e00\u7ae5\u4e0d\u7ba1\u5c55\u767d\u5fc3\u4e2d\u611f\u6168\u4e07\u7aef\uff0c\u5140\u81ea\u563b\u778e\u7b11\u9053\uff1a\u201c\u90a3\u5c31\u62ff\u6765 \u5427\uff01\u201d \u3000\u3000\u5c55\u767d\u4e00\u542c\uff0c\u5fc3\u8bf4\uff1a\u201c\u53c8\u6765\u4e86\uff01\u201d\u5fd9\u81ea\u9000\u540e\u4e00\u6b65\uff0c\u82e6\u7b11\u4e00\u58f0\u3002\u8bf4\u9053\uff1a\u201c\u8001\u524d\u8f88\uff01\u4f60\u628a\u665a\u8f88 \u7684\u4e1c\u897f\u4e22\u6389\uff0c\u5bb3\u5f97\u665a\u8f88\u8fd8\u4e0d\u591f\u82e6\u5417\uff1f\u73b0\u5728\u53c8\u6765\u8981\uff01\u4f60\u8981\u4ec0\u4e48\uff1f\u665a\u8f88\u8eab\u65e0\u957f\u7269\u2026\u2026\u201d \u3000\u3000\u201c\u53cd\u6b63\u8fd9\u4e00\u6b21\uff0c\u8001\u592b\u4e0d\u8981\u4f60\u90a3\u81ed\u5783\u573e\u5c31\u662f\u4e86\uff01\u201d\u8d39\u4e00\u7ae5\u4ecd\u7136\u563b\u7b11\u7740\uff0c\u5374\u7528\u624b\u4e00\u6307\u5c55\u767d\u624b \u4e2d\u7684\u201c\u65e0\u60c5\u78a7\u5251\u3002\u201d\uff0c\u8bf4\u9053\uff1a\u201c\u628a\u4f60\u90a3\u67c4\u5251\uff0c\u62ff\u6765\u7ed9\u8001\u592b\u770b\u770b\uff01\u201d \u3000\u3000\u5c55\u767d\u4e00\u542c\u5927\u6012\uff0c\u5fc3\u60f3\uff1a\u201c\u96be\u9053\u6211\u5c55\u767d\u5c31\u662f\u8fd9\u4e48\u4efb\u4eba\u6570\u53d6\u6b32\u593a\u5417\uff1f\u201d\u60f3\u7f62\u53cc\u76ee\u4e00\u77aa\uff0c\u51db\u7136 \u8bf4\u9053\uff1a\u201c\u6b66\u6797\u4e2d\u4eba\u4eba\u7686\u77e5\uff0c\u2018\u5175\u5668\uff0c\u4e43\u4e60\u6b66\u4e4b\u4eba\u7b2c\u4e8c\u751f\u547d\uff01\u2019\u8001\u524d\u8f88\u8bf4\u51fa\u6b64\u8a00\uff0c\u4e0d\u89c9\u5f97\u6709\u70b9 \u8fc7\u5206\u5417\uff1f\u201d \u3000\u3000\u8d39\u4e00\u7ae5\u5019\u7136\u8138\u8272\u4e00\u5bd2\uff0c\u7b11\u5bb9\u5c3d\u655b\uff0c\u6c89\u58f0\u559d\u9053\uff1a\u201c\u8001\u592b\u53ea\u95ee\u4f60\uff0c\u662f\u7ed9\uff1f\u8fd8\u662f\u4e0d\u7ed9\uff1f\u201d \u3000\u3000\u5c55\u767d\u563f\u7136\u4e00\u58f0\u51b7\u7b11\uff0c\u50b2\u7136\u8bf4\u9053\uff1a\u201c\u90a3\u8981\u95ee\u95ee\u8fd9\u67c4\u5251\uff0c\u770b\u5b83\u81ea\u5df1\u613f\u4e0d\u613f\u610f\uff01\u201d \u3000\u3000\u8d39\u4e00\u7ae5\u76ee\u5149\u4e2d\u6740\u673a\u987f\u73b0\uff0c\u5c55\u81ea\u4ee5\u4e3a\u4ed6\u8981\u51fa\u624b\u62a2\u593a\uff0c\u7acb\u5373\u6697\u4e2d\u8fd0\u529f\u6212\u5907\uff1b\u5ffd\u7136\u90a3\u8d39\u4e00\u7ae5\u76ee \u5149\u4e00\u8f6c\uff0c\u53c8\u770b\u5411\u56db\u5468\u90a3\u4e9b\u6a2a\u4e03\u7ad6\u516b\u3001\u6298\u65ad\u5012\u5730\u7684\u5de8\u677e\uff0c\u5ffd\u5730\u8138\u8272\u53c8\u8d8b\u7f13\u548c\uff0c\u7528\u624b\u56db\u4e0b\u4e00\u6307\uff0c \u95ee\u5c55\u767d\uff1a\u201c\u8fd9\u662f\u600e\u4e48\u641e\u7684\uff1f\u8fd9\u4e9b\u5927\u6811\u600e\u4f1a\u9f50\u8170\u6298\u65ad\uff1f\u201d \u3000\u3000\u5c55\u767d\u771f\u6709\u557c\u7b11\u7686\u975e\u4e4b\u611f\uff0c\u81ea\u5df1\u767d\u7d27\u5f20\u4e86\u534a\u5929\uff0c\u4ed6\u5374\u8f6c\u53d8\u4e86\u5174\u8da3\uff0c\u5ffd\u7136\u53c8\u53bb\u95ee\u90a3\u4e9b\u65ad\u6811 \u4e86\u3002\u8f6c\u53c8\u4e00\u60f3\uff0c\u6b64\u8001\u4e0d\u53ef\u7406\u55bb\uff0c\u4f55\u5fc5\u8ddf\u4ed6\u591a\u803d\u8bef\u65f6\u95f4\u3002 \u3000\u3000\u201c\u90a3\u2014\u2014\u201d\u5c55\u767d\u8bf4\uff1a\u201c\u8fd8\u662f\u8bf7\u8001\u524d\u8f88\u81ea\u5df2\u53bb\u7814\u7a76\u7814\u7a76\u5427\uff01\u665a\u8f88\u8fd8\u6709\u6025\u4e8b\u5f85\u529e\uff0c\u5c31\u6b64\u544a \u8f9e\uff01\u201d\u5c55\u767d\u8bf4\u7f62\uff0c\u56de\u5934\u5c31\u8d70\u3002n\u201d \u3000\u3000\u54ea\u77e5\u8033\u7554\u4e00\u58f0\u51b7\u54fc\uff0c\u9762\u524d\u4eba\u5f71\u4e00\u6643\uff0c\u201c\u8fa3\u624b\u7ae5\u5fc3\u201d\u53c8\u6a2a\u963b\u5728\u5c55\u767d\u9762\u524d\uff0c\u5389\u58f0\u53f1\u9053\uff1a\u201c\u5a03 \u5a03\uff01\u4f60\u53c8\u60f3\u8dd1\u662f\u4e0d\u662f\uff1f\u5728\u6211\u8001\u4eba\u5bb6\u9762\u524d\u73a9\u8fd9\u4e00\u5957\uff0c\u90a3\u4f60\u53ef\u662f\u81ea\u8ba8\u82e6\u5403\uff01\u201d \u3000\u3000\u5c55\u767d\u4e00\u9707\u624b\u4e2d\u201c\u65e0\u60c5\u78a7\u5251\u201d\uff0c\u51b7\u7136\u8bf4\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u5c31\u8bf7\u8001\u524d\u8f88\u5212\u4e0b\u9053\u513f\u6765\u5427\uff01\u5728\u4e0b\u63a5\u7740 \u5c31\u662f\u4e86\uff01\u201d \u3000\u3000\u201c\u54fc\uff01\u201d\u8d39\u4e00\u7ae5\u4ece\u9f3b\u5b54\u91cc\u54fc\u51fa\u4e00\u822c\u51b7\u6c14\uff0c\u6ee1\u8138\u4e0d\u5c51\u4e4b\u8272\uff0c\u8bf4\u9053\uff0c\u201c\u5c0f\u4f19\u5b50\uff01\u4f60\u8fd8\u6562\u8ddf\u6211 \u8001\u4eba\u5bb6\u52a8\u624b\u5417\uff1f\u201d \u3000\u3000\u5c55\u767d\u80f8\u812f\u4e00\u633a\uff0c\u6beb\u4e0d\u754f\u60e7\u5730\u8bf4\u9053\uff1a\u201c\u8bf4\u4e0d\u5f97\u8981\u9886\u6559\u8001\u524d\u8f88\u51e0\u624b\u9ad8\u62db\u4e86\uff01\u201d \u3000\u3000\u8d39\u4e00\u7ae5\u8138\u4e0a\u9634\u6674\u4e0d\u5b9a\uff0c\u76ee\u5149\u5de6\u53f3\u6d41\u52a8\u2026\u2026 \u3000\u3000\u5c55\u767d\u77e5\u9053\u8fd9\u201c\u8fa3\u624b\u7ae5\u5fc3\u201d\uff0c\u867d\u7136\u8868\u9762\u4e0a\u75af\u75af\u766b\u766b\uff0c\u5176\u5185\u5fc3\u5374\u6700\u662f\u9634\u9669\u8be1\u8bc8\u4e0d\u8fc7\uff0c\u6015\u4ed6\u7a81 \u65bd\u4ec0\u4e48\u6697\u7b97\uff0c\u7acb\u5224\u5168\u795e\u6212\u5907\uff0c\u529f\u8fd0\u5168\u8eab\uff0c\u63a8\u5907\u968f\u65f6\u63a5\u53d7\u8d38\u7136\u4e00\u51fb\u3002 \u3000\u3000\u201c\u8fa3\u624b\u7ae5\u5fc3\u201d\u8d39\u4e00\u7ae5\uff0c\u53ef\u8bf4\u662f\u602a\u8bde\u5230\u4e86\u6781\u5904\uff0c\u773c\u770b\u53cc\u65b9\u640f\u6597\u4e00\u89e6\u5373\u53d1\u4e4b\u9645\uff0c\u5ffd\u7136\u4ed6\u773c\u5149 \u53c8\u505c\u5728\u6570\u4e08\u4e4b\u5916\u7684\u5730\u9762\u4e0a\uff0c\u597d\u50cf\u53c8\u628a\u8981\u8ddf\u5c55\u767d\u52a8\u624b\u7684\u4e8b\u5fd8\u4e86\uff0c\u8138\u4e0a\u6d41\u9732\u51fa\u597d\u5947\u4e4b\u8272\uff0c\u4e00\u8fb9\u8fc8 \u6b65\u5411\u90a3\u773c\u671b\u4e4b\u5904\u8d70\u53bb\uff0c\u4e00\u8fb9\u5634\u91cc\u81ea\u8a00\u81ea\u8bed\uff1a\u201c\u54a6\uff01\u8fd9\u5730\u4e0b\u662f\u4ec0\u4e48\u4eba\u5199\u7684\u5b57\uff1f\u201d \u3000\u3000\u8d39\u4e00\u7ae5\u8fd9\u6837\u4e00\u9635\u7d27\u4e00\u9635\u677e\uff0c\u4f7f\u5c55\u767d\u7684\u5fc3\u60c5\u4e5f\u8ddf\u7740\u7d27\u4e00\u9635\u6781\u4e00\u9635\uff0c\u5c55\u767d\u771f\u611f\u5230\u5fae\u7b11\u4e0d\u80fd\uff0c \u6b32\u54ed\u65e0\u6cea\u3002\u5bf9\u8d39\u4e00\u7ae5\u5954\u5411\u5145\u6ee1\u4e86\u597d\u5947\u7684\u5730\u65b9\uff0c\u8fde\u770b\u4e5f\u4e0d\u770b\uff0c\u6602\u9996\u63d0\u5251\uff0c\u5927\u8e0f\u6b65\u5730\u5411\u6797\u5916\u8d70 \u53bb\u2026\u2026 \u3000\u3000\u540c\u65f6\uff0c\u5c55\u767d\u6697\u4e0b\u72e0\u5fc3\uff0c\u53ea\u8981\u8d39\u4e00\u7ae5\u518d\u4e00\u62e6\u963b\u81ea\u5df1\uff0c\u4e00\u5b9a\u8fd0\u8d77\u5468\u8eab\u529f\u529b\uff0c\u5f53\u80f8\u5c31\u7ed9\u4ed6\u4e00 \u5251\uff0c\u54ea\u6015\u4e00\u5251\u628a\u8fd9\u8005\u602a\u7269\u523a\u4e00\u4e2a\u900f\u5fc3\u7a9f\u7abf\uff0c\u81ea\u5df1\u51b3\u4e0d\u76b1\u4e00\u76b1\u7709\u2026\u2026 \u3000\u3000\u54ea\u77e5\uff0c\u5c55\u767d\u7684\u72e0\u5fc3\u53c8\u767d\u4e0b\u4e86\uff0c\u4efb\u7740\u4ed6\u5927\u8e0f\u6b65\u5730\u79bb\u53bb\uff0c\u8d39\u4e00\u7ae5\u8fd9\u6b21\u5374\u6ca1\u6709\u518d\u6765\u62e6\u963b\u4ed6\u3002\u4e0d \u8fc7\uff0c\u8d39\u4e00\u7ae5\u5728\u5c55\u767d\u8eab\u540e\uff0c\u65ad\u65ad\u7eed\u7eed\u5730\u8bfb\u51fa\u5730\u4e0b\u7684\u5b57\u8ff9\uff0c\u542c\u5230\u5c55\u767d\u8033\u4e2d\uff0c\u5c55\u767d\u53ef\u4e0d\u7531\u81ea\u4e3b\u5730\u8f6c \u4e86\u56de\u53bb\uff01 \u3000\u3000\u53ea\u542c\u8d39\u4e00\u76f4\u5728\u90a3\u91cc\u65ec\u4e0d\u6210\u6587\u7684\u5ff5\u9053\uff1a\u201c\u6740\u7236\u4ec7\u2026\u2026\u76d8\u2026\u2026\u91d1\u9675\u2026\u2026\u52bf\u53ef\u2026\u2026\u5929\u2026\u2026 \u4e0d\u2026\u2026\u5984\u52a8\u2026\u2026\u9a74\u2026\u2026\u80c6\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u901a\uff01\u4e0d\u901a\uff01\u201d\u53ea\u542c\u8d39\u4e00\u7ae5\u5728\u90a3\u91cc\u8df3\u811a\u9a82\u9053\uff1a\u201c\u7b80\u76f4\u72d7\u5c41\u4e0d\u901a\uff01\u201d \u3000\u3000\u5c55\u81ea\u5440\u5165\u8033\u4e2d\uff0c\u5fc3\u5934\u731b\u9707\uff01\u201c\u54ce\u5440\uff01\u8fd9\u5c82\u4e0d\u662f\u9a91\u9a74\u8001\u4eba\uff0c\u4e66\u544a\u81ea\u5df2\u6740\u7236\u4ec7\u4eba\u7684\u7ebf\u7d22 \u5417\uff1f\u201d \u3000\u3000\u5c55\u767d\u731b\u7136\u56de\u8eab\uff0c\u6025\u5411\u8d39\u4e00\u7ae5\u7ad9\u5904\u5954\u53bb\u2014\u2014\u4f46\u662f\uff0c\u665a\u4e86\uff01\u8d39\u4e00\u7ae5\u5df2\u7ecf\u5728\u90a3\u91cc\u9a82\u9a7e\u56db\u5219\uff0c\u53c8 \u8e66\u53c8\u8df3\uff0c\u98de\u8d77\u4e00\u53ea\u811a\u6765\uff0c\u7528\u978b\u5e95\u628a\u90a3\u4e9b\u5b57\u8ff9\u6d82\u53bb\uff01 \u3000\u3000\u201c\u8001\u524d\u8f88\uff01\u4e0d\u8981\u628a\u90a3\u4e9b\u5b57\u8ff9\u64e6\u6389\uff01\u201d \u3000\u3000\u5c55\u767d\u6025\u58f0\u547c\u6b62\uff0c\u4eba\u98d8\u98ce\u95ea\u7535\u4e5f\u4f3c\u5730\u8d76\u4e86\u8fc7\u53bb\uff1b\u4f46\u662f\uff0c\u8d39\u4e00\u7ae5\u5df2\u628a\u5730\u4e0b\u6240\u6709\u5b57\u8ff9\uff0c\u64e6\u4e86\u4e2a \u5e72\u5e72\u51c0\u51c0\uff0c\u53cd\u800c\u77aa\u8d77\u4e00\u53cc\u5c0f\u5706\u773c\uff0c\u671b\u5b9a\u5c55\u767d\u95ee\u9053\uff1a\u201c\u600e\u4e48\uff1f\u5c0f\u4f19\u5b50\uff01\u8fd9\u5730\u4e0b\u7684\u5b57\u662f\u4f60\u5199\u7684 \u5417\uff1f\u771f\u662f\u4e0d\u901a\u5df2\u6781\uff01\u96be\u9053\u4f60\u5c0f\u4e8e\u957f\u8fd9\u4e48\u5927\u8fde\u4e66\u90fd\u6ca1\u6709\u8bfb\u8fc7\u5417\uff1f\u201d \u3000\u3000\u5c55\u767d\u54ea\u6709\u5fc3\u8ddf\u4ed6\u80e1\u626f\uff0c\u9540\u4f4e\u5934\u671b\u53bb\uff0c\u89c1\u5730\u4e0b\u5df2\u662f\u6c99\u571f\u4e00\u7247\uff0c\u6beb\u65e0\u5b57\u8ff9\u53ef\u5bfb\u4e0d\u7531\u8dcc\u811a\u6025 \u9053\uff1a\u201c\u8001\u524d\u8f88\uff01\u4f60\u8fd9\u662f\u4f55\u82e6\u5462\uff1f\u5904\u5904\u548c\u5728\u4e0b\u4f5c\u5bf9\uff01\u771f\u662f\uff01\u771f\u662f\u2026\u2026\u201d \u3000\u3000\u5c55\u767d\u201c\u771f\u662f\u201d\u4e86\u534a\u5929\uff0c\u771f\u662f\u4e0d\u77e5\u8be5\u600e\u4e48\u9a82\u4ed6\u624d\u597d\u3002 \u3000\u3000\u90a3\u201c\u8fa3\u624b\u7ae5\u5fc3\u201d\u8d39\u4e00\u7ae5\uff0c\u89c1\u5c55\u767d\u6025\u6210\u8fd9\u4e2a\u6837\u5b50\uff0c\u5012\u9887\u89c9\u597d\u73a9\uff0c\u7adf\u7136\u62cd\u624b\u6253\u638c\u5730\u7b11\u4e86\u8d77 \u6765\u3002 \u3000\u3000\u201c\u5509\uff01\u201d\u5c55\u767d\u4ef0\u5929\u957f\u53f9\u4e00\u58f0\uff0c\u5fc3\u8bf4\uff1a\u201c\u4f55\u5fc5\u8ddf\u4ed6\u4e00\u822c\u89c1\u8bc6\uff0c\u8fd8\u662f\u65e9\u65e9\u79bb\u5f00\u4ed6\u4e3a\u5999\uff01\u90a3\u9a91 \u9a74\u8001\u4eba\u65e2\u662f\u6302\u5251\u7559\u5b57\uff0c\u53ef\u80fd\u662f\u6697\u4e2d\u5e2e\u52a9\u81ea\u5df1\uff0c\u90a3\u7559\u5b57\u4e2d\u65e2\u6709\u2018\u6740\u7236\u4ec7\uff0c\u76d8\u91d1\u9675\u2019\u5b87\u6837\uff0c\u867d\u7136 \u5b57\u4e49\u4e0d\u5168\uff0c\u4f46\u6740\u7236\u7684\u4ec7\u4eba\uff0c\u53ef\u80fd\u662f\u5728\u5357\u4eac\uff0c\u81ea\u5df1\u4e0d\u5982\u5c31\u7ecf\u5357\u4eac\u8d70\u4e00\u8d9f\uff0c\u8bf4\u4e0d\u5b9a\u53ef\u4ee5\u63a2\u542c\u51fa\u4ec7 \u4eba\u7684\u8e2a\u8ff9\u6765\u2026\u2026\u201d \u3000\u3000\u5c55\u767d\u60f3\u7f62\uff0c\u5f53\u5373\u8f6c\u8eab\u51c6\u5907\u79bb\u53bb\u2026\u2026 \u3000\u3000\u53ef\u662f\uff0c\u90a3\u201c\u8fa3\u624b\u7ae5\u5fc3\u201d\u8d39\u4e00\u7ae5\u7a81\u5730\u53c8\u6643\u8eab\u6321\u4f4f\u5c55\u767d\u53bb\u8def\uff0c\u559d\u9053\uff1a\u201c\u5c0f\u5b50\uff01\u4f60\u5251\u8fd8\u672a\u7559 \u4e0b\uff0c\u5c31\u60f3\u8d70\uff01\u4f60\u60f3\u4f60\u8d70\u5f97\u4e86\u5417\uff1f\u201d \u3000\u3000\u5c55\u767d\u771f\u88ab\u4ed6\u9017\u7684\u53d1\u8d77\u706b\u6765\uff0c\u518d\u4e0d\u53d1\u8a00\uff0c\u201c\u5251\u6307\u5929\u5357\u201d\uff0c\u6296\u624b\u4e00\u5251\uff0c\u76f4\u5411\u8d39\u4e00\u7ae5\u201c\u7709\u5fc3\u201d \u91cd\u7a74\u523a\u53bb\u3002 \u3000\u3000\u201c\u6765\u5f97\u597d\uff01\u201d \u3000\u3000\u8d39\u4e00\u76f4\u9661\u559d\u2014\u58f0\uff0c\u4e0a\u8eab\u4e00\u4fa7\uff0c\u8eb2\u8fc7\u9508\u767d\u5251\u5c16\uff0c\u6643\u80a9\u8de8\u6b65\uff0c\u5de6\u624b\u5019\u4f38\uff0c\u731b\u6263\u5c55\u767d\u53f3\u624b\u8109 \u95e8\uff0c\u7adf\u662f\u201c\u7a7a\u624b\u5165\u767d\u5203\u201d\u624b\u6cd5\uff0c\u540c\u65f6\uff0c\u53f3\u638c\u95ea\u7535\u822c\u4ece\u8098\u8428\u5bb9\u51fa\uff0c\u638c\u7f18\u6302\u98ce\uff0c\u731b\u6309\u5c55\u767d\u5de6\u80f8\u8981 \u5bb3\u3002 \u3000\u3000\u201c\u8fa3\u624b\u7ae5\u5fc3\u201d\u8d39\u4e00\u7ae5\uff0c\u679c\u7136\u4e0d\u662f\u6d6a\u5f97\u865a\u540d\u4e4b\u8f88\uff0c\u51fa\u4e4e\u62db\u5f0f\u8be1\u5947\u7edd\u4f26\uff0c\u4e0d\u540c\u51e1\u54cd\u3002 \u3000\u3000\u5c55\u767d\u731b\u5403\u4e00\u60ca\uff0c\u4f30\u4e0d\u5230\u8d39\u4e00\u7ae5\u8d64\u624b\u7a7a\u62f3\uff0c\u5bf9\u7740\u81ea\u5df2\u51cc\u5389\u5251\u62db\uff0c\u4e0d\u4f46\u4e0d\u9000\uff0c\u53cd\u800c\u6b3a\u8eab\u76f4 \u8fdb\u3002\u5c55\u767d\u6c14\u6781\u529b\u731b\uff0c\u957f\u5251\u51fa\u62db\u7528\u8001\uff0c\u5f85\u8d39\u4e00\u7ae5\u903c\u8fd1\u81ea\u5df1\u8eab\u524d\uff0c\u518d\u60f3\u64a4\u5251\u6362\u62db\uff0c\u5df2\u7ecf\u6765\u4e0d\u53ca \u4e86\uff0c\u8d76\u7d27\u6c89\u53f3\u8155\uff0c\u53f3\u8155\u5df2\u88ab\u8d39\u4e00\u7ae5\u6307\u98ce\u626b\u4e2d\uff0c\u5c55\u81ea\u53ea\u89c9\u9aa8\u75db\u5982\u6298\uff0c\u534a\u8fb9\u8eab\u5b50\u53d1\u9ebb\uff0c\u201c\u65e0\u60c5\u78a7 \u5251\u201d\u51e0\u4e4e\u8131\u624b\u800c\u51fa\u2026\u2026 \u3000\u3000\u597d\u5728\u53f3\u8155\u672b\u88ab\u8d39\u4e00\u7ae5\u6293\u4f50\uff0c\u4f46\u8d39\u4e00\u7ae5\u6309\u5411\u81ea\u5df2\u80f8\u524d\u7684\u4e00\u638c\uff0c\u5374\u518d\u4e5f\u8eb2\u907f\u4e0d\u5f00\u3002 \u3000\u3000\u5c55\u767d\u4e5f\u662f\u4e2a\u6025\u52b2\uff0c\u63d0\u8d77\u5de6\u638c\u5411\u5916\u4e00\u5c01\uff0c\u201c\u562d\u201d\u7684\u4e00\u58f0\u5927\u9707\uff0c\u4e8c\u4eba\u540c\u65f6\u8e6c\u8e6c\u9000\u540e\u4e09\u6b65\u3002 \u3000\u3000\u56e0\u662f\u8fd8\u8eab\u535a\u6597\uff0c\u4e8c\u4eba\u5747\u672b\u8fd0\u4e0a\u5168\u529b\uff0c\u4f46\u8c01\u4e5f\u6ca1\u6709\u5360\u5230\u8c01\u7684\u4fbf\u5b9c\u3002 \u3000\u3000\u5c55\u767d\u5306\u5fd9\u4e2d\uff0c\u8fd0\u638c\u4e00\u5c01\uff0c\u5316\u9669\u4e3a\u5937\uff0c\u5fc3\u91cc\u8fd8\u4e0d\u600e\u4e48\u6837\uff0c\u4f46\u201c\u8fa3\u624b\u7ae5\u5fc3\u201d\u8d39\u4e00\u7ae5\u5fc3\u6709\u4e0d \u7518\uff0c\u4ee5\u4ed6\u7684\u6b66\u529f\u4fee\u4e3a\uff0c\u548c\u7d20\u5e38\u9887\u4e3a\u81ea\u8d1f\u7684\u5fc3\u60c5\u6765\u8bf4\uff0c\u5c31\u51ed\u5c55\u767d\u2014\u2014\u4e00\u4e2a\u4e73\u81ed\u672a\u5e72\u7684\u5c0f\u4f19\u5b50\uff0c \u6562\u8ddf\u81ea\u5df1\u786c\u78b0\u786c\u7684\u5bf9\u4e00\u638c\uff0c\u800c\u4e14\u7adf\u80fd\u8ddf\u81ea\u5df1\u6253\u4e2a\u5e73\u624b\uff0c\u8fd9\u7b80\u76f4\u662f\u4e0d\u53ef\u80fd\u7684\u4e8b\u3002 \u3000\u3000\u201c\u597d\u5c0f\u5b50\uff01\u771f\u6709\u4f60\u7684\uff01\u201d \u3000\u3000\u8d39\u4e00\u7ae5\u53cc\u7709\u8f69\u52a8\uff0c\u4e24\u773c\u5706\u964d\uff0c\u5389\u58f0\u53eb\u9053\uff1a\u201c\u518d\u63a5\u8001\u592b\u4e00\u638c\u8bd5\u8bd5\uff01\u201d \u3000\u3000\u8bf4\u7740\uff0c\u5708\u81c2\u7acb\u638c\uff0c\u8fdc\u4e86\u516b\u6210\u529f\u52b2\uff0c\u731b\u7136\u5e73\u80f8\u63a8\u51fa\u3002 \u3000\u3000\u638c\u98ce\u5c71\u6d8c\uff0c\u547c\u5578\u800c\u81f3\uff0c\u8fa3\u624b\u7ae5\u5fc3\u201d\u638c\u529b\u60ca\u4eba\u3002 \u3000\u3000\u5c55\u767d\u65e0\u5fc3\u4e2d\u4e0e\u8d39\u4e00\u7ae5\u786c\u5bf9\u4e86\u4e00\u638c\uff0c\u7adf\u7136\u6253\u4e86\u4e2a\u5e73\u624b\uff0c\u4fe1\u5fc3\u5927\u589e\uff0c\u89c1\u8d39\u4e00\u7ae5\u53c8\u5288\u51fa\u4e00\u4e3e\uff0c \u5f53\u5373\u5251\u4ea4\u5de6\u624b\uff0c\u8fce\u7740\u8d39\u4e00\u7ae5\u5f3a\u52b2\u638c\u98ce\uff0c\u4ee5\u53f3\u638c\u5168\u529b\u8fce\u53bb\u3002 \u3000\u3000\u201c\u8f70\u201d\u7136\u4e00\u58f0\u5de8\u9707\uff0c\u4e8c\u4eba\u638c\u529b\u51fb\u5b9e\uff0c\u4f59\u529b\u56db\u6fc0\uff0c\u98de\u6c99\u626c\u5c18\u3002 \u3000\u3000\u5c55\u767d\u8eab\u5f62\u6643\u4e86\u4e24\u6643\uff0c\u4f9d\u7136\u7a33\u7ad9\u539f\u5730\u4e0d\u52a8\uff1b\u90a3\u201c\u8fa3\u624b\u7ae5\u5fc3\u201d\u8d39\u4e00\u76f4\u5374\u8e6c\u8e6c\u8e6c\u2026\u2026\u4e00\u8fde\u540e\u9000 \u4e94\u516d\u6b65\u624d\u62ff\u88ab\u7ad9\u7a33\u3002 \u3000\u3000\u8fd9\u4e00\u56de\u8be5\u8f6e\u5230\u8d39\u4e00\u7ae5\u5403\u60ca\u4e86\uff0c\u6697\u60f3\uff1a\u201c\u81ea\u5df1\u6210\u540d\u6c5f\u6e56\u6570\u4e2d\u5e74\uff0c\u5f88\u5c11\u9047\u5230\u654c\u624b\uff0c\u600e\u4e48\u773c\u524d \u8fd9\u4e0d\u8d77\u773c\u7684\u5c11\u5e74\uff0c\u7ade\u80fd\u4e00\u638c\u628a\u81ea\u5df1\u9707\u9000\uff1f\u201d \u3000\u3000\u5c24\u5176\u662f\u4e00\u6708\u4e4b\u524d\uff0c\u5728\u8fd9\u91cc\u66fe\u548c\u8fd9\u5c11\u5e74\u9047\u5230\u8fc7\uff0c\u5206\u660e\u4ed6\u6b66\u529f\u5e73\u5e73\uff0c\u600e\u80fd\u5728\u8fd9\u4e00\u6708\u4e4b\u95f4\uff0c\u4ed6 \u7684\u529f\u529b\u5ffd\u7136\u589e\u9ad8\u8bb8\u591a\uff1f\u2026\u2026 \u3000\u3000\u8d39\u4e00\u76f4\u6781\u4e3a\u81ea\u8d1f\uff0c\u7d20\u5e38\u773c\u9ad8\u4e8e\u9876\uff0c\u5982\u4eca\uff0c\u88ab\u8fd9\u540d\u4e0d\u89c1\u7ecf\u4f20\u7684\u5c11\u5e74\u4e00\u638c\u9707\u9000\uff0c\u5982\u82e5\u4f20\u51fa \u53bb\uff0c\u90a3\u4ee5\u540e\u81ea\u5df1\u4e0d\u8981\u5728\u6c5f\u6e56\u4e0a\u53eb\u5b57\u53f7 \u3000\u3000\u8d39\u4e00\u7ae5\u60f3\u5230\u8fd9\u91cc\uff0c\u65e2\u60ca\u4e14\u6012\uff0c\u4e24\u53ea\u602a\u773c\u5706\u7741\uff0c\u5934\u4e0a\u767d\u53d1\u6839\u6839\u76f4\u7acb\uff0c\u770b\u8fd9\u8001\u513f\u80fd\u591f\u201c\u529b\u8d2f \u53d1\u68a2\u201d\uff0c\u8db3\u89c1\u5176\u5185\u529f\u4fee\u4e3a\u5df2\u81f3\u7089\u706b\u7eaf\u9752\u4e4b\u5883\u3002 \u3000\u3000\u201c\u5c0f\u5b50\uff01\u6709\u70b9\u95e8\u9053\u3002\u201d \u3000\u3000\u201c\u8fa3\u624b\u7ae5\u5fc3\u201d\u8d39\u4e00\u7ae5\u66b4\u543c\u9053\uff1a\u201c\u8fd8\u8981\u63a5\u8001\u592b\u4e00\u638c\uff01\u201d \u3000\u3000\u8d39\u4e00\u7ae5\u8bf4\u7f62\uff0c\u8fd9\u6b21\u7adf\u672a\u731b\u7136\u51fa\u624b\u800c\u662f\u8de8\u524d\u6570\u6b65\uff0c\u9a91\u9a6c\u8e72\u6321\u4e00\u7ad9\uff0c\u9f13\u8179\u7eb3\u6c14\uff0c\u95ed\u76ee\u5782\u7709\uff0c \u5148\u628a\u4e24\u81c2\u5e73\u76f4\u4f38\u51fa\uff0c\u7136\u540e\u7f13\u7f13\u6536\u56de\u53cc\u638c\uff0c\u638c\u7acb\u5982\u5200\uff0c\u7f6e\u4e8e\u80f8\u524d\u3002\u4ed6\u5728\u505a\u8fd9\u4e9b\u52a8\u4f5c\u65f6\uff0c\u5185\u8179\u771f \u6c14\u6d41\u8f6c\uff0c\u76ae\u4e0b\u808c\u8089\u9f13\u8d77\u5982\u9f20\uff0c\u968f\u7740\u6c14\u6d41\u6765\u56de\u4e71\u7a9c\uff0c\u800c\u4e14\u5468\u8eab\u9aa8\u8282\u54af\u54af\u4f5c\u54cd\uff0c\u6837\u5b50\u51f6\u6076\u5df2\u6781\u3002 \u3000\u3000\u5c55\u767d\u770b\u4ed6\u8fd9\u4efd\u795e\u60c5\uff0c\u72b9\u5982\u4e00\u53ea\u6fc0\u6012\u7684\u516c\u9e21\uff0c\u84c4\u6ee1\u5168\u8eab\u529b\u91cf\uff0c\u5f85\u673a\u6251\u654c\uff0c\u77e5\u9053\u6b64\u8001\u513f\u5fc5\u6b32 \u548c\u81ea\u5df1\u5168\u529b\u4e00\u62fc\u3002\u5f53\u5373\u60f3\u9053\uff1a\u83ab\u82e5\u8ddf\u8001\u513f\u5168\u529b\u4e00\u62fc\uff0c\u4e5f\u597d\u8bd5\u4e00\u8bd5\u81ea\u5df1\u5185\u529b\u4fee\u4e3a\u7a76\u7adf\u8fdb\u5883\u5230\u4ec0 \u4e48\u5730\u6b65\uff1f\u60f3\u5230\u8fd9\u91cc\uff0c\u7acb\u5373\u5c06\u5251\u8fd8\u9798\u659c\u63d2\u4e8e\u80cc\u4e0a\uff0c\u7136\u540e\u4e5f\u62ff\u6869\u7ad9\u7a33\uff0c\u6f84\u5fc3\u51c0\u8651\uff0c\u62b1\u5143\u5b88\u4e00\uff0c\u628a \u201c\u5929\u4f5b\u7edd\u5b66\u201d\u8fd0\u81f3\u5341\u6210\u3002 \u3000\u3000\u5728\u5c55\u767d\u8fd0\u529f\u6212\u5907\u65f6\uff0c\u8d39\u4e00\u7ae5\u5df2\u7ecf\u529f\u805a\u53cc\u624b\uff0c\u7f13\u7f13\u7741\u5f00\u773c\u775b\uff0c\u53cc\u773c\u5185\u7adf\u662f\u7cbe\u5149\u5982\u7535\uff0c\u6740\u6c14 \u903c\u4eba\u3002 \u3000\u3000\u4ed6\u89c1\u5c55\u767d\u4e5f\u5728\u51dd\u805a\u529f\u529b\uff0c\u54a7\u5634\u7b11\u9053\uff1a\u201c\u5a03\u5a03\uff01\u4f60\u51c6\u5907\u597d\u4e86\u5417\uff1f\u201d \u3000\u3000\u201c\u8bf7\u8001\u524d\u8f88\u8d50\u62db\u5427\uff01\u201c \u3000\u3000\u5c55\u767d\u4e0d\u77e5\u8d39\u4e00\u7ae5\u6709\u8bc8\uff0c\u5f53\u5373\u8bf4\u9053\uff1a\u201c\u665a\u8f88\u51c6\u2026\u2026\u201d \u3000\u3000\u8c01\u77e5\u8d81\u7740\u5c55\u767d\u5f00\u53e3\u8bf4\u8bdd\uff0c\u771f\u6c14\u4e00\u6cfb\u4e4b\u9645\uff0c\u8d39\u4e00\u7ae5\u5410\u6c14\u5f00\u58f0\uff1a\u201c\u563f\uff01\u201d \u3000\u3000\u53cc\u638c\u5982\u6392\u5c71\u4f8b\u6d77\u4e00\u822c\uff0c\u5411\u5c55\u81ea\u731b\u63a8\u800c\u81f3\u3002 \u3000\u3000\u5c55\u767d\u5927\u5403\u4e00\u60ca\uff0c\u8d76\u7d27\u95ed\u4f0e\u4e00\u53e3\u771f\u6c14\uff0c\u8fd0\u8d77\u5468\u8eab\u529f\u529b\uff0c\u4ee5\u53cc\u638c\u5411\u88ad\u6765\u7684\u5f3a\u52b2\u624b\u98ce\u8fce\u53bb\u3002 \u3000\u3000\u201c\u7830\uff01\u201d \u3000\u3000\u4e00\u58f0\u77ed\u800c\u8106\u7684\u66b4\u54cd\uff0c\u5c55\u81ea\u53ea\u89c9\u53cc\u8033\u96f7\u9e23\uff0c\u773c\u524d\u91d1\u661f\u4e71\u7a9c\uff0c\u72b9\u5982\u72c2\u98ce\u6012\u6d6a\u7684\u5de8\u5927\u51b2\u529b\uff0c\u51e0 \u4e4e\u4f7f\u81ea\u5df1\u7acb\u8db3\u4e0d\u7a33\u3002 \u3000\u3000\u800c\u4e14\uff0c\u90a3\u5de8\u5927\u65e0\u5f62\u7684\u538b\u529b\uff0c\u4ecd\u7136\u5982\u957f\u6c5f\u5927\u6cb3\u4e00\u822c\uff0c\u5411\u81ea\u5df2\u6c79\u6d8c\u800c\u81f3\uff1b\u5c55\u767d\u77e5\u9053\u81ea\u5df1\u5371\u673a \u4e00\u53d1\uff0c\u5982\u4e0d\u80fd\u594b\u529b\u62b5\u4f4f\uff0c\u90a3\u4e48\uff0c\u81ea\u5df1\u8fd9\u6761\u547d\u4e5f\u5c31\u7b97\u5b8c\u4e86\u3002 \u3000\u3000\u5c55\u767d\u5fc3\u601d\u7535\u8f6c\uff0c\u7acb\u5373\u6c14\u6c89\u4e39\u7530\uff0c\u529b\u6253\u201c\u5343\u65a4\u5760\u201d\uff0c\u5f3a\u628a\u8eab\u5f62\u7a33\u4f4f\uff0c\u8d76\u7d27\u628a\u5468\u8eab\u529f\u529b\u8fd0\u96c6 \u5728\u53cc\u81c2\u4e0a\uff0c\u529b\u900f\u638c\u5fc3\uff0c\u54ac\u7259\u82e6\u6491\uff0c\u7ade\u628a\u8fd9\u5343\u94a9\u4e00\u53d1\u7684\u5371\u9669\u573a\u9762\uff0c\u786c\u7ed9\u4ed6\u633a\u4f4f\u3002 \u3000\u3000\u201c\u8fa3\u624b\u7ae5\u5fc3\u201d\u8d39\u4e00\u7ae5\uff0c\u867d\u7136\u672a\u628a\u5c55\u767d\u653e\u5728\u773c\u5185\uff0c\u4f46\u662f\u8fde\u7740\u786c\u5bf9\u4e86\u4e24\u638c\uff0c\u5df2\u77e5\u9053\u773c\u524d\u5c11\u5e74 \u4e0d\u53ef\u8f7b\u89c6\u3002\u5728\u7b2c\u4e09\u6b21\u5bf9\u638c\uff0c\u8fd0\u529f\u96c6\u6c14\u65f6\uff0c\u4f7f\u8be1\u5f04\u8bc8\uff0c\u5f15\u8bf1\u5c55\u767d\u8bf4\u8bdd\uff0c\u8d81\u7740\u5c55\u767d\u5f00\u53e3\u8bf4\u8bdd\u771f\u6c14 \u4e00\u6cc4\u4e4b\u9645\uff0c\u731b\u7136\u53cc\u638c\u5168\u529b\u63a8\u51fa\uff0c\u60f3\u628a\u5c55\u767d\u7acb\u6bd9\u638c\u4e0b\uff0c\u4ee5\u4fdd\u5168\u81ea\u5df2\u7684\u989c\u9762\u3002 \u3000\u3000\u4ed6\u8fd0\u96c6\u4e86\u4fee\u7ec3\u56db\u4e94\u5341\u5e74\u7684\u529f\u529b\uff0c\u53cc\u638c\u5168\u529b\u63a8\u51fa\uff0c\u5fc3\u4e2d\u4e00\u9635\u5f97\u610f\uff0c\u6697\u9053\uff1a\u201c\u5c0f\u5b50\uff0c\u8fd9\u4e00\u4e0b \u5b50\u4f60\u5c0f\u5b50\u7b97\u73a9\u5b8c\u2026\u2026\u201d \u3000\u3000\u8c01\u77e5\uff0c\u5c55\u767d\u638c\u529b\u4e00\u6cfb\uff0c\u53c8\u731b\u7136\u633a\u4f4f\uff1b\u4e00\u822c\u5a01\u731b\u7edd\u4f26\u7684\u529b\u9053\uff0c\u53cd\u800c\u538b\u8feb\u56de\u6765\u3002 \u3000\u3000\u8d39\u4e00\u7ae5\u5927\u5403\u4e00\u60ca\uff0c\u8d76\u7d27\u6536\u6444\u5fc3\u795e\uff0c\u628a\u4f59\u4e0b\u7684\u529b\u9053\uff0c\u53c8\u52a0\u6ce8\u5230\u53cc\u638c\u4e4b\u4e0a\u2026\u3002\uff0e \u3000\u3000\u5c55\u767d\u4e5f\u81ea\u8fd0\u96c6\u5468\u8eab\u529f\u529b\u76f8\u6297\u2026\u3002\uff0e \u3000\u3000\u8fd9\u4e00\u6765\uff0c\u4e8c\u4eba\u4e92\u76f8\u8f83\u91cf\u4e0a\u4e86\u771f\u529b\u3002\u53ea\u89c1\u4e8c\u4eba\u5747\u662f\u9a91\u9a6c\u624e\u6869\u7ad9\u7a33\uff0c\u76f8\u8ddd\u7ea6\u6709\u4e94\u5c3a\uff0c\u53cc\u81c2\u4e4e \u76f4\u524d\u4f38\uff0c\u56db\u638c\u9065\u9065\u76f8\u62b5\u3002\u4e0d\u77e5\u9053\u5185\u60c5\u7684\u4eba\uff0c\u8fdc\u8fdc\u671b\u53bb\uff0c\u8fd8\u4ee5\u4e3a\u4e00\u8001\u4e00\u5c11\uff0c\u4e24\u4e2a\u4eba\u5728\u6811\u6797\u91cc\u6446 \u4ec0\u4e48\u67b6\u5f0f\u54e9\uff01 \u3000\u3000\u79bb\u8fd1\u4e86\u4e00\u770b\uff0c\u624d\u80fd\u770b\u51fa\u4e8c\u4eba\u795e\u60c5\u7d27\u5f20\u65e0\u6bd4\uff1a\u8001\u4eba\u5934\u4e0a\u767d\u53d1\u72e0\u6839\u76f4\u7acb\uff0c\u602a\u76ee\u5706\u7741\uff0c\u5468\u8eab\u808c \u8089\u7d22\u7d22\u76f4\u6296\u3002 \u3000\u3000\u53ef\u662f\uff0c\u5c55\u767d\u4e60\u5f97\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\u4e0a\u7684\u6b63\u5b97\u5fc3\u6cd5\uff0c\u90a3\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\u662f\u524d\u8f88\u5f02\u4eba \u201c\u53ea\u773c\u90ce\u541b\u201d\u6240\u9057\uff0c\u4e3a\u5929\u4e0b\u7b2c\u4e00\u5947\u4e66\uff0c\u4e66\u4e0a\u6240\u5f55\uff0c\u5747\u662f\u5c18\u4e16\u96be\u89c1\u7684\u4e0a\u4e58\u6b66\u529f\uff0c\u5177\u6709\u4f5b\u95e8\u964d\u9b54 \u7684\u65e0\u6bd4\u5a01\u529b\u3002\u53c8\u52a0\u4e0a\u5c55\u767d\u8fde\u756a\u5947\u9047\uff0c\u5148\u53d7\u201c\u94f6\u7bab\u593a\u9b42\u201d\u7ae0\u58eb\u9e4f\u201c\u97f3\u9b54\u5927\u6cd5\u201d\u7684\u8bd5\u70bc\uff0c\u4f7f\u4ed6 \u201c\u5f52\u771f\u8fd4\u749e\u201d\uff0c\u53c8\u88ab\u201c\u51cc\u98ce\u516c\u5b50\u201d\u65e0\u610f\u4e2d\u4e00\u638c\u628a\u4ed6\u5468\u8eab\u5947\u7ecf\u516b\u8109\u9707\u5f00\u3002\u53ef\u4ee5\u8bf4\uff0c\u5c55\u767d\u5728\u77ed\u77ed \u4e00\u6708\u4e4b\u95f4\uff0c\u6b66\u529f\u5185\u529b\u5927\u589e\uff0c\u4e0d\u4e9a\u4e8e\u5e73\u5e38\u4eba\u6309\u6b63\u89c4\u4fee\u7ec3\u4e94\u516d\u5341\u5e74\u7684\u529f\u529b\u3002\u56e0\u6b64\uff0c\u4e8c\u4eba\u5b9e\u529b\u76f8 \u5f53\uff0c\u7adf\u95f9\u4e86\u4e2a\u68cb\u9022\u5bf9\u624b\u3002 \u3000\u3000\u4e8c\u4eba\u638c\u529b\u752b\u63a5\u65f6\uff0c\u5c55\u767d\u5403\u4e86\u8d39\u4e00\u7ae5\u4f7f\u8be1\u5f04\u8bc8\u7684\u4e8f\uff0c\u51e0\u4e4e\u88ab\u8d39\u4e00\u7ae5\u5185\u529b\u9707\u4f24\uff0c\u5f85\u4ed6\u52c9\u529b\u6491 \u4f4f\uff0c\u6c14\u7eb3\u4e39\u7530\uff0c\u4e00\u65e5\u771f\u6c14\u5728\u5185\u8179\u6d41\u8f6c\uff0c\u7acb\u89c9\u8d39\u4e00\u7ae5\u53cc\u638c\u7684\u538b\u529b\u5927\u51cf\uff0c\u81ea\u5df1\u5185\u529b\u53cd\u9762\u6e90\u6e90\u800c \u751f\uff0c\u5fc3\u5185\u5927\u5b9a\uff0c\u5373\u523b\u53c8\u52a0\u4e0a\u4e24\u6210\u529b\u9053\uff0c\u5411\u8d39\u4e00\u7ae5\u53cd\u51fb\u56de\u53bb\u3002 \u3000\u3000\u8d39\u4e00\u7ae5\u53ea\u6709\u5728\u53cc\u638c\u521d\u6539\u65f6\uff0c\u611f\u5230\u5c55\u767d\u5185\u529b\u4e00\u6cfb\uff0c\u6ca1\u60f3\u5230\u8fc5\u5373\u88ab\u5c55\u767d\u633a\u4f4f\uff0c\u7ee7\u800c\u611f\u5230\u5c55\u767d \u5185\u529b\u5982\u957f\u6c5f\u5927\u6cb3\u4e00\u822c\uff0c\u4ece\u53cc\u638c\u4e4b\u4e0a\uff0c\u6eda\u6eda\u538b\u6765\uff0c\u5fc3\u4e2d\u53c8\u60ca\u53c8\u6012\uff0c\u53ea\u6709\u54ac\u7259\u82e6\u6491\u3002 \u3000\u3000\u53c8\u662f\u4e00\u76cf\u70ed\u8336\u7684\u65f6\u95f4\u8fc7\u53bb\u4e86\uff0c\u8352\u91ce\u5bc6\u6797\u4e2d\u7adf\u662f\u51fa\u5947\u7684\u9759\uff0c\u7ea2\u65e5\u5df2\u504f\u897f\uff0c\u5fae\u98ce\u62c2\u8fc7\u6811\u68a2\uff0c \u53ea\u6709\u6797\u836b\u5904\u4e0d\u65f6\u4f20\u6765\u4e00\u4e24\u58f0\u6591\u9e20\u7684\u557c\u58f0\uff0c\u6b64\u5916\uff0c\u56db\u91ce\u662f\u9759\u6084\u6084\u7684\u6ca1\u6709\u4e00\u70b9\u58f0\u606f\u3002\u53c8\u8c01\u77e5\u5728\u8fd9 \u5b81\u9759\u7684\u6811\u6797\u91cc\uff0c\u6b63\u6709\u4e00\u573a\u751f\u6b7b\u7684\u640f\u6597\u3002\u5c55\u767d\u5728\u7a33\u4f4f\u5c40\u52bf\u4e4b\u540e\uff0c\u5fc3\u6709\u4f59\u88d5\uff0c\u8111\u4e2d\u7075\u5149\u8000\u95ea\uff0c\u7a81 \u7136\u60f3\u5230\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u7737\u300b\u4e2d\uff0c\u6709\u4e24\u53e5\u79d8\u8bc0\uff1a\u201c\u865a\u800c\u4e0d\u865a\uff0c\u5f31\u800c\u4e0d\u5f31\u3002\u201d\u90a3\u89e3\u8bf4\u6b63\u662f\u4e00\u4e2a \u201c\u5438\u201d\u5b57\u8bc0\u3002 \u3000\u3000\u5c55\u767d\u6025\u6b32\u8d76\u5f80\u5357\u4eac\uff0c\u5b9e\u4e0d\u613f\u4e0e\u8d39\u4e00\u7ae5\u5728\u6b64\u82e6\u8017\uff0c\u5fc3\u60f3\uff1a\u201c\u50cf\u8fd9\u6837\u8ddf\u4ed6\u82e6\u6491\u4e0b\u53bb\uff0c\u4e0d\u77e5\u4f55 \u65f6\u7b97\u5b8c\uff1f\u4f55\u4e0d\u7528\u2018\u5438\u2019\u5b57\u8bd5\u4e00\u8bd5\uff0c\u5982\u80fd\u8131\u79bb\u4e00\u70b9\u79bb\u5f00\u6b64\u5730\uff01\u201d \u3000\u3000\u5c55\u767d\u5c11\u5e74\u5fc3\u6027\uff0c\u5e76\u6ca1\u8003\u8651\u5230\u6b64\u4e3e\u5173\u7cfb\u751f\u6b7b\uff0c\u5374\u662f\u60f3\u5230\u5c31\u505a\u3002\u5f53\u5373\u8c03\u8282\u5185\u8151\u771f\u6c14\uff0c\u638c\u5fc3\u5411 \u5185\u4e00\u5438\uff0c\u7a81\u611f\u8d39\u4e00\u7ae5\u7684\u638c\u529b\uff0c\u5982\u4e07\u6cb3\u51b3\u5824\u4e00\u822c\uff0c\u5411\u4ed6\u6c79\u6d8c\u538b\u6765\uff01\u5c55\u767d\u5927\u5403\u4e00\u60ca\u2026\u2026 \u7b2c\u5341\u4e03\u7ae0\u3000\u5c0f\u9547\u9669\u9047>> \u53e4\u9f99\u300a\u5251\u5ba2\u884c\u300b \u7b2c\u5341\u4e03\u7ae0\u3000\u5c0f\u9547\u9669\u9047 \u3000\u3000\u8d39\u4e00\u7ae5\u5374\u662f\u5fc3\u5934\u72c2\u559c\uff0c\u5ffd\u611f\u5c55\u767d\u638c\u529b\u4e00\u6cfb\uff0c\u4ee5\u4e3a\u5c55\u767d\u5185\u529b\u8017\u5c3d\u4e0d\u652f\uff0c\u5f53\u5373\u5927\u543c\u4e00\u58f0\u3002 \u3000\u3000\u201c\u8eba\u2026\u2026\u201d \u3000\u3000\u4f46\u662f\uff0c\u4ed6\u201c\u4e0b\u201d\u5b57\u8fd8\u672a\u51fa\u53e3\uff0c\u7a81\u89c9\u5c55\u767d\u529b\u9053\u53c8\u731b\u7136\u5f39\u9707\u800c\u56de\uff0c\u8d39\u4e00\u7ae5\u7acb\u611f\u6709\u5982\u5343\u94a9\u5de8\u95f8 \u538b\u4e0a\u8eab\u6765\uff0c\u773c\u4e00\u9ed1\uff0c\u8033\u5185\u96f7\u9e23\uff0c\u518d\u4e5f\u652f\u6491\u4e0d\u4f4f\u3002 \u3000\u3000\u201c\u55f7\uff01\u201d\u4e00\u58f0\u60b2\u60e8\u957f\u53f7\uff0c\u4eba\u4e5f\u5f80\u540e\u5012\u649e\u51fa\u53bb\uff0c\u53c8\u662f\u201c\u562d\u201d\u7684\u4e00\u58f0\u5927\u9707\uff1b\u8d39\u4e00\u7ae5\u76f4\u8dcc\u51fa\u4e00 \u4e08\u5f00\u5916\uff0c\u56db\u4ef0\u516b\u53c9\u5730\u4e4e\u6454\u5728\u5730\u4e0a\u3002 \u3000\u3000\u539f\u6765\u5c55\u767d\u201c\u5438\u201d\u5b57\u8bc0\u521a\u4e00\u65bd\u51fa\uff0c\u7acb\u611f\u4e0d\u5999\uff0c\u968f\u5373\u53c8\u628a\u201c\u5b9e\u800c\u53c8\u5b9e\uff0c\u5f3a\u800c\u53c8\u5f3a\u201d\uff0c\u300a\u5929\u4f5b \u5377\u300b\u4e2d\u7684\u201c\u5f39\u201d\u5b57\u8bc0\u65bd\u51fa\uff0c\u7acb\u628a\u201c\u8fa3\u624b\u7ae5\u5fc3\u201d\u8d39\u4e00\u7ae5\u9707\u5012\u3002 \u3000\u3000\u8fd9\u4f8b\u662f\u5c55\u767d\u6ca1\u60f3\u5230\u7684\uff0c\u4ed6\u770b\u8d39\u4e00\u7ae5\u60e8\u964d\u4e00\u58f0\uff0c\u5012\u8dcc\u4e4b\u540e\u7ad9\u76f4\u8eab\u5f62\uff0c\u5148\u81ea\u8c03\u606f\u4e00\u756a\uff0c\u624d\u8d70 \u8fc7\u53bb\uff0c\u4e00\u770b\u90a3\u6840\u50b2\u602a\u8bde\u7684\u8d39\u4e00\u7ae5\uff0c\u76f4\u633a\u633a\u5730\u5012\u5728\u5730\u4e0a\uff0c\u8ddf\u3001\u9f3b\u3001\u53e3\u3001\u8033\u5185\u90fd\u6e17\u51fa\u9c9c\u8840\uff0c\u4eba\u5df2 \u7ecf\u662f\u6b7b \u3000\u3000\u5c55\u767d\u867d\u4e5f\u5728\u6c5f\u6e56\u4e0a\u8d70\u52a8\u8fc7\uff0c\u4f46\u8fd9\u8fd8\u662f\u7b2c\u4e00\u6b21\u6740\u4eba\uff0c\u4ed6\u770b\u5230\u8d39\u4e00\u7ae5\u4e03\u7a8d\u6d41\u8840\uff0c\u53cc\u773c\u4e0a\u7ffb\uff0c \u8138\u4e0a\u808c\u8089\u626d\u66f2\uff0c\u6b7b\u72b6\u4e4b\u6e17\uff0c\u89e6\u76ee\u60ca\u5fc3\u3002 \u3000\u3000\u5bf9\u8fd9\u4e09\u756a\u4e24\u6b21\u6b3a\u4fae\u4ed6\u7684\u8001\u8005\uff0c\u7ade\u800c\u5fc3\u751f\u6b49\u610f\uff0c\u6697\u6697\u9ed8\u7977\u9053\uff1a\u201c\u8001\u524d\u8f88\uff0c\u8fd9\u662f\u4f55\u82e6\u6765\uff1f\u4f60 \u4e09\u756a\u4e24\u6b21\u627e\u6211\u9ebb\u70e6\uff0c\u54ce\uff01\u60f3\u4e0d\u5230\u6211\u7adf\u5931\u624b\u628a\u4f60\u6253\u6b7b\u2026\u2026\u201d \u3000\u3000\u5c55\u767d\u9ed8\u7977\u7f62\uff0c\u5fc3\u8bf4\uff1a\u201c\u8fd8\u662f\u628a\u4ed6\u63a9\u57cb\u4e86\uff0c\u514d\u5f97\u4f7f\u4ed6\u66dd\u5c38\u8352\u90ca\uff0c\u88ab\u91ce\u72fc\u82cd\u9e70\u5403\u6389\u2026\u2026\u201d \u3000\u3000\u4e8e\u662f\uff0c\u4ed6\u62d4\u51fa\u201c\u65e0\u60c5\u78a7\u5251\u201d\uff0c\u5c31\u5728\u6811\u6797\u91cc\u6316\u4e86\u4e2a\u5751\u3002\u53ef\u662f\uff0c\u5f53\u4ed6\u62d6\u7740\u8d39\u4e00\u7ae5\u7684\u5c38\u8eab\uff0c\u521a \u8981\u4e22\u4e0b\u65f6\uff0c\u7a81\u7136\u4ece\u6797\u5916\u201c\u55d6\uff01\u55d6\uff01\u55d6\uff01\u201d\u63a5\u8fde\u7a9c\u8fdb\u4e09\u4e2a\u52b2\u88c5\u5927\u6c49\u6765\u3002 \u3000\u3000\u201c\u597d\u54c7\uff01\u201d \u3000\u3000\u4e00\u4e2a\u8c79\u5934\u73af\u773c\u7684\u5927\u6c49\u8bf4\u9053\uff1a\u201c\u9752\u5929\u767d\u65e5\uff0c\u6740\u4eba\u8fd8\u60f3\u706d\u8ff9\uff0c\u4f60\u5c0f\u5b50\u5c31\u522b\u60f3\u8131\u4e86\u8fd9\u4e2a\u5e72 \u7cfb\uff01\u201d \u3000\u3000\u5c55\u767d\u731b\u7136\u4e00\u695e\uff0c\u53c8\u4e00\u4e2a\u6ee1\u8138\u7cbe\u608d\u4e4b\u8272\u7684\u6c49\u5b50\u563f\u563f\u7b11\u9053\uff1a\u201c\u670b\u53cb\u662f\u54ea\u6761\u7ebf\u4e0a\u7684\uff1f\u6cbe\u4e86\u6cb9\u6c34 \u522b\u60f3\u72ec\u541e\uff01\u201d \u3000\u3000\u7b2c\u4e09\u4e2a\u52b2\u88c5\u5927\u6c49\uff0c\u8138\u8272\u9752\u767d\uff0c\u4e00\u8138\u7684\u6666\u6c14\uff0c\u4e5f\u5728\u4e00\u8fb9\u51b7\u51b7\u5730\u8bf4\u9053\uff1a\u201c\u89c1\u8005\u6709\u4efd\uff0c\u5927\u79e4 \u91d1\uff0c\u5c0f\u79e4\u94f6\uff0c\u6446\u51fa\u6765\u8fc7\u8fc7\u5206\u91cf\u5427\uff01\u201d \u3000\u3000\u5c55\u767d\u867d\u5728\u6c5f\u6e56\u9053\u4e0a\u6df7\u4e86\u6ca1\u6709\u51e0\u5929\uff0c\u4f46\u5bf9\u8fd9\u4e9b\u773c\u9762\u7684\u9ed1\u8bdd\u8fd8\u662f\u61c2\u5f97\u7684\u3002\u4e00\u542c\u4e09\u4e2a\u52b2\u88c5\u5927\u6c49 \u7ade\u628a\u4ed6\u5f53\u505a\u62e6\u8def\u52ab\u8d22\u7684\u5f3a\u76d7\uff0c\u800c\u60f3\u627e\u4ed6\u6765\u5206\u6cb9\u6c34\u7684\uff0c\u5fc3\u4e2d\u9887\u4e0d\u662f\u6ecb\u5473\uff0c\u4ed6\u4e5f\u6253\u7740\u534a\u751f\u4e0d\u719f\u7684 \u6c5f\u6e56\u9ed1\u8bdd\u7b54\u9053\uff1a\u201c\u539f\u6765\u4e09\u4f4d\u662f\u5408\u5b57\uff0c\u53ef\u60dc\u62db\u5b50\u4e0d\u4eae\uff0c\u8fd9\u91cc\u5e76\u6ca1\u6709\u6cb9\u6c34\u53ef\u6cbe\uff0c\u53ea\u662f\u5728\u4e0b\u4e00\u4e2a\u540c \u4f34\uff0c\u75c5\u6b7b\u9014\u4e2d\uff0c\u5728\u4e0b\u4e3a\u4ed6\u6536\u5c38\uff01\u201d \u3000\u3000\u542c\u4e86\u5c55\u767d\u7684\u8bdd\uff0c\u4e09\u4e2a\u52b2\u88c5\u5927\u6c49\u4f3c\u662f\u4e0d\u4fe1\uff0c\u4e00\u9f50\u8d70\u62e2\u6765\u770b\u3002\u4e00\u89c1\u8d39\u4e00\u7ae5\u90a3\u4efd\u6b7b\u50cf\uff0c\u6ee1\u8138\u7cbe \u608d\u7684\u6c49\u5b50\u7acb\u523b\u770b\u51fa\u8e4a\u8df7\uff0c\u563f\u563f\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u660e\u4eba\u773c\u524d\u4e0d\u8bf4\u5047\u8bdd\uff0c\u670b\u53cb\uff0c\u4f60\u8fd9\u4f4d\u540c\u4f34\u4e0d\u662f\u75c5 \u6b7b\u7684\u5427\uff1f\u201d \u3000\u3000\u201c\u54ce\u5440\uff01\u201d \u3000\u3000\u5c55\u767d\u5c1a\u672a\u7b54\u8a00\uff0c\u8138\u8272\u9752\u767d\u7684\u6c49\u5b50\u4e00\u58f0\u60ca\u547c\uff1a\u201c\u6b7b\u7684\u8fd9\u4e0d\u662f\u2018\u8fa3\u624b\u7ae5\u5fc3\u2019\u8d39\u8001\u524d\u8f88\u5417\uff01\u201d \u3000\u3000\u5176\u4ed6\u4e24\u4e2a\u58ee\u6c49\u95fb\u8a00\u2019\uff0c\u4e5f\u7741\u5927\u4e86\u773c\u775b\uff0c\u91cd\u65b0\u6253\u91cf\u4e86\u8d39\u4e00\u7ae5\u4e24\u773c\uff0c\u5f85\u53d1\u73b0\u6b7b\u8005\u679c\u7136\u662f\u201c\u8fa3 \u624b\u7ae5\u5fc3\u201d\u8d39\u4e00\u7ae5\uff0c\u7acb\u523b\u817e\u8eab\u540e\u9000\u4e24\u6b65\uff0c\u5404\u81ea\u63a2\u624b\u80cc\u540e\uff0c\u201c\u545b\u54ea\u201d\u4e00\u58f0\uff0c\u62bd\u51fa\u5200\u6765\u3002 \u3000\u3000\u4e09\u4e2a\u52b2\u88c5\u5927\u6c49\u4e09\u67c4\u9b3c\u5934\u94a2\u5200\uff0c\u7acb\u5411\u5c55\u767d\u91c7\u53d6\u5305\u56f4\u7684\u6001\u52bf\u3002 \u3000\u3000\u201c\u4f60\u5c0f\u5b50\u600e\u4e48\u628a\u8d39\u8001\u524d\u8f88\u5bb3\u6b7b\uff1f\u201d \u3000\u3000\u8c79\u5934\u73af\u773c\u7684\u5927\u6c49\u671d\u6307\u5c55\u767d\uff0c\u5428\u9053\uff1a\u201c\u8fd8\u4e0d\u7167\u5b9e\u8bf4\u6765\uff01\u201d \u3000\u3000\u201c\u8001\u5927\uff01\u201d \u3000\u3000\u53e6\u4e24\u4e2a\u58ee\u6c49\u4e00\u9f50\u53eb\u9053\uff1a\u201c\u8fd8\u6709\u4ec0\u4e48\u53ef\u95ee\u7684\uff01\u8981\u4ed6\u5c0f\u5b50\u7ed9\u8d39\u8001\u524d\u8f88\u507f\u547d\u5c31\u662f\u4e86\uff01\u201d \u3000\u3000\u8bf4\u7f62\uff0c\u624b\u6267\u9b3c\u5934\u5200\uff0c\u6076\u72e0\u72e0\u5730\u9f50\u5411\u5c55\u767d\u56f4\u4e86\u4e0a\u6765\u3002 \u3000\u3000\u201c\u4e09\u4f4d\u4e14\u6162\uff01\u201d\u5c55\u767d\u559d\u9053\uff1a\u201c\u5728\u4e0b\u4e0e\u8d39\u8001\u524d\u8f88\u6bd4\u6b66\uff0c\u4e00\u65f6\u5931\u624b\u8bef\u4f24\u2026\u2026\u201d \u3000\u3000\u201c\u5c0f\u5b50\uff01\u8bf4\u5927\u8bdd\u4e0d\u6015\u6298\u4e86\u820c\u5934\uff01\u201d\u8138\u8272\u9752\u767d\u7684\u6c49\u5b50\uff0c\u51b7\u68ee\u68ee\u5730\u8bf4\u9053\uff1a\u51ed\u4f60\u660e\u6253\u660e\u6597\uff0c\u4f1a \u662f\u8d39\u8001\u524d\u8f88\u7684\u5bf9\u624b\uff1f\u4e00\u5b9a\u4f60\u662f\u7528\u4ec0\u4e48\u6697\u7b97\uff0c\u5bb3\u6b7b\u8d39\u8001\u524d\u8f88\uff01\u201d \u3000\u3000\u201c\u5c0f\u5b50\uff01\u4f60\u5c31\u507f\u547d\u6765\u5427\uff01\u201d\u6ee1\u8138\u7cbe\u608d\u7684\u6c49\u5b50\u8dc3\u8d77\u8eab\u5f62\uff0c\u6402\u5934\u76d6\u9876\u5411\u5c55\u767d\u4e00\u5200\u780d\u4e0b\u3002 \u3000\u3000\u5c55\u767d\u6643\u8eab\u8eb2\u8fc7\uff0c\u53e6\u4e24\u4e2a\u6c49\u5b50\uff0c\u4e00\u5de6\u4e00\u53f3\uff0c\u4e24\u67c4\u9b3c\u5934\u5200\uff0c\u4e00\u523a\u5c55\u767d\u5de6\u808b\uff0c\u4e00\u523a\u5c55\u767d\u540e\u5fc3\u4e00 \u9f50\u5411\u5c55\u767d\u653b\u6765\u3002 \u3000\u3000\u5c55\u767d\u8f6c\u8eab\u51fa\u638c\uff0c\u8eb2\u8fc7\u8eab\u540e\u9b3c\u5934\u5200\uff0c\u987a\u638c\u4e00\u63a8\uff0c\u53c8\u628a\u523a\u5411\u5de6\u808b\u7684\u9b3c\u5934\u5200\u63a8\u5f00\u3002 \u3000\u3000\u4e09\u4e2a\u58ee\u6c49\u8eab\u5f71\u4ea4\u9519\uff0c\u53c8\u4e00\u9f50\u4e3e\u5200\u653b\u4e0a\u3002 \u3000\u3000\u5c55\u767d\u770b\u4e09\u4e2a\u58ee\u6c49\u5200\u6cd5\u51cc\u5389\uff0c\u800c\u4e14\u6709\u7406\u8bf4\u4e0d\u6e05\uff0c\u5728\u8eab\u5f62\u7535\u65cb\u661f\u98de\u4e4b\u9645\uff0c\u53cd\u624b\u62bd\u51fa\u80cc\u4e0a\u7684 \u201c\u65e0\u60c5\u78a7\u5251\u201d\u3002 \u3000\u3000\u201c\u545b\u5577\u201d\u9f99\u541f\u58f0\u4e2d\uff0c\u4e00\u6e9c\u78a7\u8272\u5149\u534e\uff0c\u201c\u65e0\u60c5\u78a7\u5251\u201d\u51fa\u9798\uff0c\u5c55\u767d\u5c31\u64a4\u5251\u51fa\u9798\u4e4b\u52bf\uff0c\u65bd\u51fa\u4e00 \u62db\u201c\u6865\u6c5f\u65ad\u6d41\u201d\u3002 \u3000\u3000\u201c\u545b\uff01\u201d\u201c\u545b\uff01\u201d\u63a5\u8fde\u4e24\u54cd\uff0c\u6709\u4e24\u67c4\u9b3c\u5934\u5200\u4e3a\u5c55\u767d\u201c\u65e0\u60c5\u78a7\u5251\u201d\u524a\u65ad\u3002 \u3000\u3000\u4e09\u4e2a\u7261\u6c49\u60ca\u547c\u6025\u9000\uff0c\u5404\u81ea\u8dc3\u8eab\u7eb5\u51fa\u4e00\u4e08\u5f00\u5916\uff0c\u4e00\u9f50\u60ca\u89c6\u7740\u5c55\u767d\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u4ed6\u4eec\u53ef\u4e0d\u6562\u5c0f\u770b\u5c55\u767d\u4e86\uff0c\u89c1\u5c55\u767d\u4e00\u5251\u4fbf\u524a\u65ad\u4e86\u4e24\u4eba\u7684\u5175\u5668\uff0c\u5dcd\u5cd9\u7ad9\u5728\u5f53\u573a\uff0c\u7a33\u7136 \u6709\u5927\u5c06\u519b\u516b\u9762\u5a01\u98ce\u4e4b\u6982\u3002 \u3000\u3000\u4e09\u4f1e\u58ee\u6c49\u65e2\u60ca\u4e14\u6012\uff0c\u4e24\u4e2a\u88ab\u524a\u65ad\u5175\u5668\u7684\u58ee\u6c49\uff0c\u4e00\u54ac\u7259\u628a\u624b\u4e2d\u7684\u65ad\u5200\uff0c\u731b\u5411\u5c55\u767d\u63b7\u6765\u3002 \u3000\u3000\u201c\u55e1\uff01\u201d\u201c\u55e1\uff01\u201d\u4e24\u67c4\u65ad\u5200\uff0c\u63a5\u7740\u98ce\u58f0\uff0c\u4e00\u88ad\u5c55\u767d\u9762\u95e8\uff0c\u4e00\u88ad\u5c55\u767d\u524d\u80f8\u3002 \u3000\u3000\u770b\u6765\u52b2\u529b\u9887\u731b\uff0c\u5c55\u767d\u4e0d\u6562\u7528\u624b\u53bb\u63a5\u90a3\u65ad\u5200\uff0c\u8eab\u5f62\u4e00\u77ee\uff0c\u7528\u201c\u767d\u9e6d\u5367\u6ce2\u201d\u8eab\u6cd5\uff0c\u8eb2\u8fc7\u63b7\u6765 \u4e24\u67c4\u65ad\u5200\u3002 \u3000\u3000\u53ef\u662f\uff0c\u4e00\u77ee\u4e4b\u9645\uff0c\u53e6\u4e00\u58ee\u6c49\u8d81\u7740\u5c55\u767d\u8eb2\u95ea\u4e24\u67c4\u65ad\u5200\u4e4b\u9645\uff0c\u4e3e\u8d77\u9b3c\u5934\u5200\uff0c\u201c\u7384\u9e1f\u5212\u6c99\u201d\uff0c \u731b\u5730\u5411\u5c55\u767d\u780d\u81f3\u3002 \u3000\u3000\u5c55\u767d\u4f30\u4e0d\u5230\u4e09\u4e2a\u58ee\u6c49\uff0c\u5982\u6b64\u527d\u608d\uff0c\u8d25\u800c\u72b9\u6597\uff0c\u624d\u4f0f\u8eab\u8eb2\u8fc7\u4e24\u67c4\u65ad\u5200\uff0c\u89c1\u53e6\u4e00\u58ee\u6c49\u5200\u53c8\u653b \u5230\uff0c\u7acb\u7528\u5251\u5c16\u4e00\u70b9\u5730\u9762\uff0c\u8eab\u5f62\u5e73\u5c04\u800c\u8d77\uff0c\u534a\u7a7a\u4e2d\u98de\u51fa\u4e00\u817f\uff0c\u5de6\u811a\u5c16\u4e00\u70b9\u58ee\u6c49\u7684\u8170\u773c\uff0c\u58ee\u6c49\u7acb \u811a\u4e0d\u4f4f\uff0c\u76f4\u5728\u5730\u4e0a\u6eda\u51fa\u8001\u8fdc\uff0c\u65b9\u624d\u633a\u8eab\u7ad9\u8d77\u3002\u8fd9\u65f6\uff0c\u4e09\u4e2a\u58ee\u6c49\u7070\u5934\u571f\u8138\u6597\u5fd7\u5168\u5931\uff0c\u6ee1\u8138\u60ca\u6050 \u4e4b\u8272\u671b\u5b9a\u5c55\u767d\uff0c\u610f\u601d\u662f\u6050\u6015\u5c55\u767d\u8ffd\u6740\u4ed6\u4eec\uff0c\u811a\u6b65\u907f\u8d85\u540e\u9000\uff0c\u53ef\u53c8\u4e0d\u6562\u6389\u5934\u540e\u8dd1\uff0c\u540e\u6765\u89c1\u5c55\u767d \u5e76\u6ca1\u6709\u8ffd\u6740\u4ed6\u4eec\u7684\u610f\u601d\uff0c\u80c6\u6c14\u7a0d\u4e3a\u58ee\u4e86\u4e00\u70b9\uff0c\u8138\u8272\u9752\u767d\u7684\u58ee\u6c49\uff0c\u8272\u5389\u5185\u832c\u5730\u8bf4\u9053\uff1a\u201c\u76f8\u597d \u7684\uff01\u6709\u79cd\u7559\u4e0b\u4e2a\u4e07\u513f\u6765\uff01\u201d \u3000\u3000\u201c\u5728\u4e0b\u5c55\u767d\uff01\u201d\u5c55\u767d\u4f53\u4f1a\u5230\u81ea\u5df1\u6b66\u529f\u8fdb\u5883\u751a\u901f\uff0c\u8eab\u6cd5\u62db\u5f0f\u5f97\u5fc3\u5e94\u624b\uff0c\u5185\u5fc3\u95ea\u8fc7\u4e00\u4e1d\u5f97 \u610f\uff0c\u95fb\u8a00\u7b54\u9053\uff1a\u201c\u4e09\u4f4d\u8fd8\u6709\u4f55\u89c1\u6559\uff1f\u201d \u3000\u3000\u201c\u9601\u4e0b\u4e0d\u8981\u795e\u6c14\uff01\u201d\u6ee1\u201c\u6211\u4eec\u54e5\u513f\u4e09\u4e2a\u8ba4\u683d\u5566\uff01\u54b1\u4eec\u8d70\u7740\u77a7\uff01\u201d \u3000\u3000\u8bf4\u7f62\uff0c\u4e09\u4e2a\u58ee\u6c49\u60bb\u60bb\u79bb\u53bb\u3002\u89c1\u4e09\u4e2a\u58ee\u6c49\u8d70\u4e86\uff0c\u5c55\u767d\u6697\u81ea\u4e00\u7b11\uff0c\u5fc3\u8bf4\uff1a\u201c\u6211\u5c55\u767d\u4e5f\u8be5\u662f\u626c \u7709\u5410\u6c14\u7684\u65f6\u5019\u4e86\u2026\u2026\u201d \u3000\u3000\u4ed6\u628a\u8d39\u4e00\u7ae5\u5c38\u4f53\u57cb\u846c\u4e86\uff0c\u7acb\u5373\u8fd4\u8eab\u4e0a\u8def\uff0c\u8ba4\u5b9a\u65b9\u5411\uff0c\u76f4\u671d\u5357\u4eac\u8d76\u53bb\u3002 \u3000\u3000\u5929\u9ed1\u65f6\uff0c\u8d70\u8fd1\u4e00\u4e2a\u5927\u9547\uff0c\u5c55\u767d\u4e5f\u4e0d\u77e5\u9053\u8fd9\u5e02\u9547\u662f\u4ec0\u4e48\u540d\u5b57\uff0c\u53ea\u89c1\u8857\u8862\u4e0a\u706f\u706b\u8f89\u714c\uff0c\u4eba\u6765 \u7199\u5f80\uff0c\u5546\u8086\u6797\u7acb\uff0c\u5e02\u9762\u7ade\u662f\u975e\u5e38\u70ed\u95f9\u3002 \u3000\u3000\u5c55\u767d\u5306\u5306\u884c\u6765\uff0c\u8179\u4e2d\u65e9\u611f\u9965\u997f\uff0c\u4e00\u8fb9\u8d70\u4e00\u8fb9\u5411\u56db\u5904\u6253\u91cf\uff0c\u60f3\u627e\u4e00\u5bb6\u9152\u697c\u7528\u996d\u4efb\u5bbf\uff0c\u5929\u660e \u518d\u8d70\u3002 \u3000\u3000\u4e00\u8def\u4e0a\u5c55\u767d\u4e1c\u5f20\u897f\u671b\uff0c\u53ea\u6ce8\u610f\u9152\u697c\u7684\u62db\u724c\uff0c\u53ef\u5c31\u6ca1\u6ce8\u610f\u5230\u4e0d\u5c11\u7684\u77ed\u88c5\u6c49\u5b50\uff0c\u4e5f\u5728\u4e0d\u4f4f\u5730 \u6253\u91cf\u4ed6\u3002 \u3000\u3000\u5c55\u767d\u8d70\u7740\u8d70\u7740\uff0c\u8fdc\u8fdc\u770b\u5230\u4e00\u5bb6\u9152\u697c\uff0c\u91d1\u5b57\u5927\u613f\uff0c\u4e0a\u4e66\u201c\u7fa4\u82f1\u9152\u697c\u201d\uff0c\u697c\u4e0a\u697c\u4e0b\u706f\u706b\u7167\u8000 \u5982\u540c\u767d\u663c\uff0c\u9152\u5ba2\u8fdb\u51fa\u5ddd\u6d41\u4e0d\u606f\uff0c\u5212\u62f3\u95f9\u9152\u4e4b\u58f0\u55a7\u8fbe\u6237\u5916\uff0c\u800c\u4e14\u4e00\u9635\u9635\u9152\u770b\u9999\u5473\uff0c\u88ad\u8fdb\u9f3b\u7aef\uff0c \u5c55\u767d\u5f53\u5373\u5927\u6b65\u5411\u7fa4\u82f1\u9152\u697c\u8d70\u53bb\u3002 \u3000\u3000\u5c55\u767d\u4e00\u8fdb\u95e8\uff0c\u5373\u770b\u5230\u4e00\u4e2a\u77ed\u8863\u52b2\u88c5\u6c49\u5b50\u8fce\u4e0a\u524d\u6765\uff0c\u5728\u5c55\u767d\u9762\u524d\u4e00\u7ad9\uff1a\u95ee\u9053\uff1a\u201c\u4f60\u662f\u60f3\u5403 \u9152\uff0c\u8fd8\u662f\u60f3\u4f4f\u5bbf\uff1f\u201d \u3000\u3000\u5c55\u767d\u770b\u4ed6\u4e0d\u50cf\u9152\u4fdd\u6a21\u6837\uff0c\u4f46\u5634\u91cc\u8fd8\u662f\u7167\u5b9e\u7b54\u9053\uff1a\u201c\u65e2\u8981\u5403\u9152\uff0c\u4e5f\u8981\u4f4f\u5bbf\u3002\u201d \u3000\u3000\u77ed\u8863\u52b2\u88c5\u5927\u6c49\uff0c\u4e0a\u4e0b\u53c8\u6253\u91cf\u5c55\u767d\u51e0\u773c\uff0c\u51b7\u51b7\u5730\u8bf4\u9053\uff1a\u201c\u5bf9\u4e0d\u8d77\uff01\u672c\u5e97\u9152\u5ea7\u5ba2\u6ee1\uff0c\u623f\u95f4\u4e5f \u5747\u88ab\u5ba2\u4eba\u5305\u4e0b\uff0c\u8bf7\u4f60\u5230\u522b\u5bb6\u53bb\u5427\uff01\u201d \u3000\u3000\u5c55\u767d\u770b\u8fd9\u5927\u6c49\uff0c\u4e0d\u50cf\u5f00\u9152\u5e97\u4e4b\u4eba\uff0c\u53c8\u89c1\u5e10\u684c\u65c1\u51e0\u4e2a\u9152\u4fdd\u9762\u73b0\u60ca\u714c\u4e4b\u8272\uff0c\u53ea\u8fdc\u8fdc\u5730\u7ad9\u7740\u5e76 \u4e0d\u6562\u8d70\u8fd1\u6765\uff0c\u5fc3\u4e2d\u5145\u6ee1\u4e86\u7591\u95ee\uff0c\u4f46\u662f\uff0c\u4eba\u5bb6\u8bf4\u5ba2\u6ee1\u4e86\uff0c\u5f53\u7136\u4e5f\u4e0d\u597d\u610f\u601d\u786c\u5f80\u91cc\u95ef\uff0c\u53ea\u6709\u8f6c\u5934 \u51fa\u6765\u3002 \u3000\u3000\u8c01\u77e5\u5c55\u767d\u8fde\u8d70\u4e86\u4e94\u516d\u5bb6\u9152\u697c\u996d\u9986\uff0c\u5747\u6709\u4eba\u5728\u95e8\u53e3\u6321\u9a7e\uff0c\u5168\u8bf4\u5ba2\u6ee1\uff0c\u8bf7\u4ed6\u5230\u522b\u5bb6\u53bb\u3002\u4e00\u4e00 \u76f4\u8d70\u5230\u6700\u540e\u4e00\u5bb6\u996d\u5e97\uff0c\u8fbe\u5df2\u662f\u5230\u4e86\u5e02\u9547\u7684\u8fb9\u4e0a\u4e86\u3002\u518d\u5f80\u524d\u770b\u4e00\u7247\u660f\u9ed1\uff0c\u4e0d\u8981\u8bf4\u662f\u4eba\u5bb6\uff0c\u8fde\u706f \u5149\u90fd\u6ca1\u6709\u4e86\u3002\u5c55\u767d\u4e0d\u4ec5\u5fc3\u4e2d\u8d77\u7591\uff0c\u4e5f\u6e10\u6e10\u751f\u8d77\u6c14\u6765\uff0c\u6697\u9053\uff1a\u201c\u54ea\u6709\u8fd9\u6837\u5de7\u7684\u4e8b\uff1f\u4e00\u5bb6\u5ba2\u6ee1\uff0c \u5bb6\u5bb6\u5ba2\u6ee1\uff01\u773c\u770b\u518d\u5411\u524d\u5c31\u8981\u8d70\u51fa\u9547\u53bb\u4e86\uff0c\u96be\u9053\u8fd9\u4e48\u5927\u7684\u9547\u57ce\uff0c\u7adf\u8fde\u5403\u996d\u4f4f\u5bbf\u7684\u5730\u65b9\u90fd\u627e\u4e0d\u5230 \u5417\uff1f\u201d \u3000\u3000\u8fd9\u6837\u60f3\u7740\uff0c\u4ed6\u53ef\u5c31\u53c8\u8fc8\u6b65\u8d70\u8fdb\u4e00\u5bb6\u9152\u5e97\u7684\u5927\u95e8\uff0c\u8fd9\u6b21\u4ed6\u5b66\u4e56\u4e86\uff0c\u5b50\u91cc\u5411\u91cc\u8fb9\u770b\u4e86\u770b\uff0c\u89c1 \u98df\u5ba2\u4e09\u4e09\u4e94\u4e94\uff0c\u5012\u6709\u5927\u534a\u5ea7\u4f4d\u7a7a\u7740\uff0c\u8fd9\u624d\u5927\u8e0f\u6b65\u5730\u5411\u91cc\u8fb9\u8d70\u53bb\u3002 \u3000\u3000\u679c\u7136\u4ece\u91cc\u53c8\u8d70\u51fa\u4e00\u4e2a\u52b2\u88c5\u5927\u6c49\u6765\uff0c\u5361\u8170\u5728\u5c55\u767d\u9762\u524d\u4e00\u7ad9\uff0c\u559d\u9053\uff1a\u201c\u670b\u53cb\uff01\u4f60\u614c\u614c\u5f20\u5f20\u5f80 \u91cc\u95ef\uff0c\u8981\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5c55\u767d\u7075\u673a\u4e00\u52a8\uff0c\u8bf4\u9053\uff1a\u201c\u627e\u4eba\uff01\u201d\u8fd9\u6b21\u4ed6\u4e0d\u8bf4\u5403\u9152\u4f4f\u5bbf\u4e86\u3002\u56e0\u4e3a\u4ed6\u5df2\u770b\u51fa\u6bcf\u9047\u5230\u62e6\u8def\u7684 \u58ee\u6c49\uff0c\u5747\u4e0d\u50cf\u5f00\u5e97\u7684\u4eba\uff0c\u4ed6\u60f3\u5148\u8d70\u8fdb\u91cc\u8fb9\u53bb\uff0c\u8981\u5403\u4ec0\u4e48\uff0c\u627e\u5230\u9152\u4fdd\u4ee5\u540e\u518d\u8bf4\u3002 \u3000\u3000\u8c01\u77e5\u90a3\u52b2\u88c5\u5927\u6c49\uff0c\u5e76\u4e0d\u653e\u8fc7\u4ed6\uff0c\u4ecd\u7136\u6321\u5728\u4ed6\u8eab\u524d\uff0c\u51b7\u51b7\u5730\u95ee\u9053\uff1a\u201c\u4f60\u627e\u8c01\uff1f\u201d \u3000\u3000\u5c55\u767d\u4e00\u695e\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u627e\u8c01\u8fd8\u8981\u544a\u8bc9\u4f60\u5417\uff1f\u201d \u3000\u3000\u90a3\u58ee\u6c49\u563f\u7136\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u627e\u4eba\u8981\u8bf4\u51fa\u59d3\u540d\u6765\uff0c\u7531\u6211\u6d3e\u4eba\u53bb\u53eb\uff0c\u81ea\u5df1\u4e0d\u80fd\u968f\u4fbf\u5f80\u91cc\u8fb9\u4e71 \u8dd1\uff01\u201d \u3000\u3000\u5c55\u767d\u5fc3\u8bf4\uff1a\u201c\u54ea\u91cc\u6765\u7684\u8fd9\u4e48\u591a\u89c4\u77e9\u3002\u201d\u4f46\u4ed6\u5df2\u770b\u51fa\u6765\uff0c\u8fd9\u4e9b\u4eba\u662f\u4e13\u95e8\u627e\u9ebb\u70e6\u6765\u7684\uff0c\u4e8e\u662f \u4e5f\u88c5\u50bb\u5145\u6123\u5730\u8bf4\uff1a\u201c\u6211\u8981\u627e\u9152\u4fdd\uff01\u201d \u3000\u3000\u90a3\u4eba\u4e00\u6123\uff0c\u4f3c\u662f\u4f30\u4e0d\u5230\u5c55\u767d\u6709\u8fd9\u4e00\u62db\uff0c\u5373\u65cb\u5373\u4f1a\u8fc7\u610f\u6765\uff0c\u77e5\u662f\u88ab\u5c55\u767d\u800d\u4e86\uff0c\u5f53\u5373\u4e00\u77aa \u773c\uff0c\u53f1h\u9053\uff1a\u201c\u4f60\u627e\u9152\u4fdd\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5c55\u767d\u6b64\u65f6\u5012\u6c89\u4f4f\u6c14\u4e86\uff0c\u6162\u6761\u65af\u7406\u5730\u8bf4\uff1a\u201c\u6211\u627e\u9152\u4fdd\u5f53\u7136\u662f\u8981\u5403\u996d\u4f4f\u5e97\u4e86\u3002\u201d \u3000\u3000\u201c\u670b\u53cb\uff01\u8001\u5b9e\u5bf9\u4f60\u8bf4\u7f62\uff01\u201d\u52b2\u88c5\u5927\u6c49\u4e00\u9635\u51b7\u7b11\uff0c\u8bf4\u9053\uff1a\u6b64\u5730\u6ca1\u6709\u996d\u7ed9\u4f60\u5403\uff0c\u4e5f\u6ca1\u6709\u623f\u5b50 \u7ed9\u4f60\u4f4f\uff0c\u6211\u770b\u4f60\u8fd8\u662f\u5230\u522b\u5904\u53bb\u5427\uff01\u201d \u3000\u3000\u5c55\u767d\u9965\u80a0\u8f97\u8f98\uff0c\u8fde\u756a\u53d7\u963b\uff0c\u4e00\u628a\u6012\u706b\uff0c\u65e9\u5df2\u6309\u538b\u4e0d\u4f4f\uff0c\u95fb\u8a00\u4e5f\u51b7\u7b11\u9053\uff1a\u201c\u6211\u5403\u996d\u7ed9\u996d \u94b1\uff0c\u4f4f\u623f\u7ed9\u623f\u94b1\uff0c\u4f55\u5fc5\u8981\u9601\u4e0b\u591a\u7ba1\uff1f\u201d \u3000\u3000\u5c55\u767d\u8bf4\u7f62\uff0c\u7ed5\u8fc7\u5927\u6c49\u76f4\u5411\u5e97\u5185\u8d70\u53bb\u3002 \u3000\u3000\u201c\u8bf4\u4e0d\u51c6\u4f60\u4f4f\uff0c\u4f60\u5c31\u4f4f\u4e0d\u4e86\uff01\u201d\u52b2\u88c5\u5927\u6c49\u8bf4\u7f62\uff0c\u4f38\u624b\u5c31\u5411\u5c55\u767d\u6293\u6765\u3002 \u3000\u3000\u5c55\u767d\u5c82\u80fd\u8ba9\u4ed6\u6293\u4f4f\uff1f\u8eab\u5f62\u4e00\u6643\uff0c\u5df2\u8eb2\u9053\u90a3\u4e00\u6293\u3002\u4f46\u5927\u6c49\u5de6\u624b\u4e00\u6293\u843d\u7a7a\uff0c\u53f3\u624b\u515c\u80f8\u5411\u5c55\u81ea \u53c8\u6253\u6765\u4e00\u62f3\u3002 \u3000\u3000\u52b2\u88c5\u5927\u6c49\u8fd9\u4e00\u62f3\uff0c\u52b2\u9053\u8fd8\u4e0d\u5c0f\uff0c\u864e\u864e\u5e26\u98ce\uff0c\u800c\u4e14\u53c8\u75be\u53c8\u5feb\u3002 \u3000\u3000\u5c55\u767d\u76f4\u7b49\u5927\u6c49\u62f3\u5df2\u8fd1\u80f8\uff0c\u6062\u7136\u75be\u4f38\u5de6\u624b\uff0c\u53cd\u638c\u5201\u4f4f\u5927\u6c49\u7684\u8155\u5b50\uff0c\u8f7b\u8f7b\u5f80\u524d\u4e00\u5e26\uff0c\u53e3\u4e2d\u8bf4 \u4e86\u58f0\uff1a\u201c\u6eda\u51fa\u53bb\uff01\u201d \u3000\u3000\u90a3\u52b2\u88c5\u5927\u6c49\u8fd8\u662f\u771f\u542c\u8bdd\uff0c\u77fe\u54e9\u5495\u5531\u4e00\u76f4\u6eda\u51fa\u9152\u5e97\u95e8\u5916\u3002 \u3000\u3000\u52b2\u88c5\u5927\u6c49\u4ece\u5730\u4e0a\u722c\u8d77\u6765\uff0c\u5411\u5c55\u767d\u88c1\u6307\u9a82\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff01\u6709\u79cd\u4f60\u522b\u8dd1\uff01\u201d\u9a82\u5b8c\u4e4b\u540e\uff0c\u5306\u5306 \u79bb\u53bb\u3002 \u3000\u3000\u5c55\u767d\u6de1\u7136\u4e00\u7b11\uff0c\u5927\u6447\u5927\u6446\u8d70\u5230\u4e00\u4e2a\u5ea7\u4f4d\u4e0a\u5750\u4e86\u4e0b\u6765\uff0c\u539f\u5728\u9152\u5e97\u5403\u9152\u7528\u996d\u7684\u5ba2\u4eba\uff0c\u90fd\u4e00\u9f50 \u7741\u5927\u773c\u775b\u671b\u5b9a\u5c55\u767d\uff0c\u5e97\u5c0f\u4e8c\u754f\u7f29\u5730\u7ad9\u5728\u4e00\u8fb9\uff0c\u89c1\u5c55\u767d\u5750\u4e0b\u7ade\u4e0d\u6562\u8fc7\u6765\u6253\u62db\u547c\u3002 \u3000\u3000\u201c\u5582\uff01\u201d\u5c55\u767d\u5750\u4e86\u4e00\u4f1a\u513f\uff0c\u89c1\u4ecd\u65e0\u4eba\u8d70\u8fc7\u6765\uff0c\u4fbf\u53eb\u9053\uff1a\u201c\u62ff\u9152\u62ff\u996d\u6765\u7528\uff01\u201d \u3000\u3000\u5e97\u5c0f\u4e8c\u3001\u5e10\u623f\u5148\u751f\uff0c\u4e92\u76f8\u671b\u4e86\u671b\uff0c\u505c\u4e86\u4e00\u4e0b\uff0c\u624d\u6709\u4e00\u4e2a\u5927\u7740\u80c6\u5b50\u8d70\u8fd1\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d \u7237\uff01\u8fd8\u662f\u8bf7\u5230\u522b\u5904\u53bb\u7528\u9152\u7528\u996d\u5427\uff01\u5c0f\u5e97\u5b9e\u5728\u4e0d\u6562\u63a5\u5f85\uff01\u201d \u3000\u3000\u201c\u4f60\u653e\u5fc3\u597d\u4e86\uff01\u201d\u5c55\u767d\u9053\uff1a\u201c\u5c3d\u7ba1\u628a\u9152\u996d\u62ff\u6765\uff0c\u6211\u60f9\u4e86\u4ec0\u4e48\u7978\u7531\u6211\u4e00\u4e2a\u4eba\u62c5\u5f53\uff0c\u51b3\u4e0d\u4f1a \u8fde\u7d2f\u4f60\u4eec\u3002\u201d \u3000\u3000\u5e97\u5c0f\u4e8c\u82e6\u7b11\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u5ba2\u7237\uff01\u60a8\u8bf4\u7684\u662f\u4e0d\u9519\uff0c\u53ef\u662f\uff0c\u6211\u4eec\u8981\u662f\u7559\u60a8\u7528\u9152\u7528\u996d\uff0c\u6211\u4eec \u8fd9\u5c0f\u5e97\u4e5f\u5c31\u522b\u60f3\u5f00\u4e86\u3002\u201d \u3000\u3000\u201c\u521a\u624d\u90a3\u5c0f\u5b50\u662f\u5e72\u4ec0\u4e48\u7684\uff0c\u4f60\u4eec\u8fd9\u6837\u6015\u4ed6\uff1f\u201d\u5c55\u767d\u95ee\u9053\uff1a\u201c\u96be\u9053\u8fd9\u91cc\u5c31\u6ca1\u6709\u738b\u6cd5\u5417\uff1f\u201d \u3000\u3000\u201c\u738b\u6cd5\u5012\u6709\uff01\u201d\u5e97\u5c0f\u4e8c\u8bf4\uff1a\u201c\u5ba2\u7237\uff0c\u60a8\u53ef\u542c\u8bf4\u8fc7\u2018\u5b89\u4e50\u98ce\u6d41\u2019\uff1f\u201d \u3000\u3000\u5c55\u767d\u5fc3\u5934\u4e00\u9707\uff0c\u609f\u7136\u800c\u609f\uff0c\u5fc3\u8bf4\uff1a\u201c\u5662\uff01\u662f\u4e86\uff01\u6211\u8bf4\u8fd9\u5e97\u5bb6\u4e3a\u4f55\u8fd9\u6837\u60e7\u6015\uff0c\u539f\u6765\u90a3\u5c0f\u5b50 \u7adf\u662f\u5b89\u4e50\u516c\u5b50\u95e8\u4e0b\uff01\u201d \u3000\u3000\u65cb\u5373\u5c55\u767d\u53c8\u60f3\u9053\uff1a\u201c\u81ea\u5df2\u6708\u4f59\u4e4b\u524d\uff0c\u66fe\u4f1a\u8fc7\u5b89\u4e50\u516c\u5b50\uff0c\u770b\u4ed6\u6717\u6717\u4fa0\u884c\uff0c\u4e14\u8863\u8868\u4e0d\u4fd7\uff0c\u96be \u9053\u4ed6\u7684\u624b\u4e0b\uff0c\u5982\u6b64\u80e1\u4f5c\u975e\u4e3a\uff0c\u90a3\u5b89\u4e50\u516c\u5b50\u7adf\u4e00\u70b9\u4e5f\u4e0d\u77e5\u9053\u5417\uff1f\u2026\u2026\u201d \u3000\u3000\u201c\u4f60\u8bf4\u7684\u53ef\u662f\u5b89\u4e50\u516c\u5b50\uff1f\u201d\u5c55\u81ea\u60f3\u7f62\u95ee\u9053\uff1a\u201c\u5b89\u4e50\u516c\u5b50\u662f\u4f4f\u5728\u8fd9\u9547\u4e0a\u5417\uff1f\u201d \u3000\u3000\u5e97\u5c0f\u4e8c\u89c1\u5c55\u767d\u80fd\u76f4\u547c\u5b89\u4e50\u516c\u5b50\u7684\u5b57\uff0c\u7acb\u65f6\u8138\u4e0a\u5806\u4e0b\u7b11\u6765\uff0c\u5411\u5c55\u767d\u54c8\u8170\u8bf4\u9053\uff1a\u5ba2\u7237\uff0c\u60a8\u77e5 \u9053\u5c31\u597d\u4e86\uff0c\u5b89\u4e50\u516c\u5b50\u867d\u4e0d\u4f4f\u5728\u6b64\u5730\uff0c\u4f46\u8fd9\u9547\u4e0a\u5927\u534a\u662f\u516c\u5b50\u7684\u4ea7\u4e1a\uff0c\u4e43\u662f\u6211\u4eec\u8fd9\u4e00\u65b9\u5c0f\u6c11\u7684\u8863 \u98df\u7236\u6bcd\uff0c\u60a8\u60f3\u8c01\u6562\u4e0d\u5c0a\u5462\uff1f\u2026\u2026\u201d \u3000\u3000\u5c55\u767d\u4e00\u8fb9\u542c\u5e97\u5c0f\u4e8c\u8bf4\u8bdd\uff0c\u4e00\u8fb9\u5fc3\u601d\u7535\u8f6c\uff0c\u6697\u60f3\u9053\uff1a\u201c\u8fd9\u4e9b\u52b2\u88c5\u5927\u6c49\uff0c\u53ef\u80fd\u662f\u501f\u5b89\u4e50\u516c\u5b50 \u7684\u540d\u671b\uff0c\u5728\u8fd9\u91cc\u4f5c\u5a01\u4f5c\u798f\uff0c\u542c\u9152\u4fdd\u8bf4\u5b89\u4e50\u516c\u5b50\u5e76\u4e0d\u4f4f\u5728\u6b64\u5730\uff0c\u6709\u7406\u65e0\u5904\u8bf4\uff0c\u81ea\u5df1\u7eb5\u7136\u8bf4\u51fa\u8ba4 \u8bc6\u5b89\u4e50\u516c\u5b50\uff0c\u4ed6\u4eec\u4e5f\u4e0d\u4f1a\u76f8\u4fe1\uff0c\u770b\u6765\u4eca\u5929\u771f\u8981\u6328\u997f\u4e86\uff01\u2026\u2026\u201d \u3000\u3000\u5c55\u767d\u773c\u5149\u4e00\u8f6c\uff0c\u5ffd\u7136\u770b\u5230\u53a8\u5185\u6709\u73b0\u6210\u7684\u9e21\u3001\u9e2d\u3001\u719f\u8089\uff0c\u7075\u673a\u4e00\u52a8\uff0c\u8bf4\u9053\uff1a\u201c\u5e97\u5bb6\u65e2\u7136\u8fd9 \u6837\u8bf4\uff0c\u6211\u4e5f\u4e0d\u53eb\u4f60\u4eec\u4e3a\u96be\uff0c\u4f60\u5c31\u628a\u73b0\u6210\u7684\u719f\u8089\u7ed9\u6211\u5207\u4e24\u65a4\uff0c\u518d\u62ff\u70b9\u9992\u5934\u82b1\u5377\uff0c\u6211\u5e26\u5728\u8def\u4e0a\u53bb \u5403\u7f62\uff01\u201d \u3000\u3000\u5e97\u5c0f\u4e8c\u9762\u6709\u96be\u8272\uff0c\u671b\u7740\u5c55\u767d\u70b9\u5934\u54c8\u8170\u8fde\u8fde\u82e6\u7b11\u2026\u2026 \u3000\u3000\u201c\u4e0d\u8981\u903c\u4eba\u592a\u751a\uff01\u201d\u5c55\u767d\u53cc\u76ee\u4e00\u77aa\uff0c\u795e\u5149\u56db\u5c04\uff0c\u559d\u9053\uff1a\u201c\u5feb\u53bb\u628a\u98df\u7269\u62ff\u6765\uff01\u5982\u82e5\u4e0d\u7136\uff0c \u53ef\u522b\u8bf4\u6211\u8981\u4e0d\u5ba2\u6c14\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u5ba2\u6c14\uff0c\u4f60\u53c8\u6562\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u5c55\u767d\u5bf9\u5e97\u5c0f\u4e8c\u7684\u8bdd\u8fd8\u672a\u8bf4\u5b8c\uff0c\u7a81\u7136\u5ba4\u5185\u70db\u5149\u4e00\u6697\uff0c\u5fae\u98ce\u8fa3\u7136\uff0c\u7b49\u5230\u70db\u706b\u590d\u660e\u65f6\uff0c\u5c45\u4e2d\u5df2 \u591a\u4e86\u4e24\u4e2a\u52b2\u88c5\u6c49\u5b50\u3002 \u3000\u3000\u6765\u4eba\u4e00\u8001\u4e00\u5c11\uff0c\u8001\u8005\u5e74\u7ea6\u516d\u65ec\uff0c\u6ee1\u5934\u77ed\u77ed\u7684\u767d\u53d1\u5982\u7316\uff0c\u7ea2\u5149\u6ee1\u9762\uff0c\u6d53\u7709\u73af\u76ee\uff0c\u7cbe\u5149\u5982 \u70ac\uff0c\u751f\u50cf\u5f02\u5e38\u5a01\u731b\uff0c\u8eab\u7a7f\u96c5\u9752\u7eba\u7ef8\u88e4\u8902\uff0c\u8170\u624e\u624b\u638c\u5bbd\u4e1d\u633a\u5e26\uff0c\u80f8\u7cfb\u5341\u5b57\u7eca\uff0c\u80a9\u4e0a\u659c\u63d2\u4e00\u67c4\u624b \u638c\u6837\u7684\u5947\u5f62\u5175\u5668\uff0c\u7ef8\u5e26\u94a2\u73af\uff0c\u95ea\u95ea\u53d1\u5149\uff0c\u66f4\u589e\u52a0\u4e86\u8001\u8005\u7684\u51e0\u5206\u6740\u6c14\u3002 \u3000\u3000\u5e74\u8f7b\u8005\u5e74\u7ea6\u4e8c\u5341\u51fa\u5934\uff0c\u957f\u8eab\u7389\u7acb\uff0c\u5251\u7709\u661f\u76ee\uff0c\u4e4d\u770b\u4e0d\u5931\u4e3a\u4fca\u54c1\u4eba\u7269\uff0c\u4f46\u7389\u9762\u5e26\u715e\uff0c\u800c\u4e14 \u5634\u89d2\u4e0b\u6487\u8fd8\u4f46\u9634\u72e0\uff0c\u770b\u6837\u5b50\u4e5f\u591f\u72c2\u50b2\uff0c\u4e5f\u662f\u77ed\u88c5\u52b2\u670d\uff0c\u80a9\u4e0b\u5251\u7a57\u98d8\u626c\uff0c\u53cc\u773c\u6ce8\u5b9a\u5c55\u81ea\uff0c\u6ee1\u8138 \u4e0d\u5c51\u4e4b\u8272\uff0c \u3000\u3000\u8fd9\u4e8c\u4eba\u4e00\u73b0\u8eab\uff0c\u5e97\u5c0f\u4e8c\u5413\u5f97\u9762\u65e0\u4eba\u8272\uff0c\u4f17\u9152\u5ba2\u7eb7\u7eb7\u7ad9\u8d77\uff0c\u79bb\u5ea7\u540e\u9000\u2026\u2026 \u3000\u3000\u5c55\u767d\u5f80\u8d77\u4e00\u7ad9\uff0c\u8fd8\u672a\u5f00\u53e3\uff0c\u7ea2\u5149\u6ee1\u9762\u7684\u8001\u8005\u6c89\u58f0\u559d\u9053\uff1a\u201c\u5c14\u5c31\u662f\u9aa4\u65bd\u6697\u7b97\uff0c\u5bb3\u6b7b\u2018\u8fa3\u624b \u7ae5\u5fc3\u2019\u8d39\u4e00\u7ae5\u7684\u4eba\u5417\uff1f\u201d \u3000\u3000\u8fd9\u7ea2\u9762\u8001\u8005\uff0c\u8bf4\u8bdd\u4e2d\u6c14\u5145\u8db3\uff0c\u74ee\u58f0\u74ee\u6c14\uff0c\u9707\u4eba\u53cc\u8033\uff0c\u55e1\u55e1\u8f70\u9e23\u3002 \u3000\u3000\u5c55\u767d\u4e00\u542c\uff0c\u8fd9\u4e9b\u4eba\u786c\u5f80\u81ea\u5df1\u5934\u4e0a\u6263\u9ed1\u5e3d\u5b50\uff0c\u660e\u660e\u662f\u640f\u6597\u800c\u6b7b\uff0c\u800c\u5f3a\u8bf4\u81ea\u5df1\u662f\u6697\u7b97\u5bb3\u6b7b\u8d39 \u4e00\u7ae5\uff0c\u5f53\u5373\u82e6\u7b11\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4e8b\u6050\u6015\u6709\u70b9\u8bef\u4f1a\uff0c\u5728\u4e0b\u4e0e\u5b89\u4e50\u516c\u5b50\u6709\u4e00\u9762\u4e4b\u8bc6\uff0c\u5982\u82e5\u89c1\u5230 \u516c\u5b50\u201d \u3000\u3000\u5728\u4e00\u65c1\u7ad9\u7acb\u7684\u72c2\u50b2\u9752\u5e74\uff0c\u51b7\u54fc\u4e00\u58f0\u8bf4\u9053\uff1a\u201c\u5b89\u4e50\u516c\u5b50\u5c82\u4f1a\u8ba4\u8bc6\u4f60\u8fd9\u65e0\u540d\u4e4b\u8f88\uff0c\u5e9f\u8bdd\u5c11 \u8bf4\uff0c\u4f60\u5c0f\u5b50\u5c31\u7ed9\u8d39\u8001\u524d\u8f88\u507f\u547d\u6765\u5427\uff01\u201d \u3000\u3000\u5634\u4e2d\u8bf4\u7740\uff0c\u4e94\u6307\u5982\u94a9\uff0c\u731b\u5411\u5c55\u767d\u53f3\u8155\u8109\u95e8\u6263\u6765\u3002 \u3000\u3000\u5c55\u767d\u770b\u4ed6\u51fa\u624b\u7684\u624b\u6cd5\u4e0d\u5f31\uff0c\u9519\u6b65\u56de\u8eab\uff0c\u53f3\u8155\u4e00\u6c89\uff0c\u5df2\u8131\u51fa\u72c2\u50b2\u9752\u5e74\u7684\u4e94\u6307\u4e4b\u5916\u3002 \u3000\u3000\u72c2\u50b2\u9752\u5e74\uff0c\u53d8\u6293\u4e3a\u638c\uff0c\u968f\u7740\u5c55\u81ea\u64a4\u8eab\u4e4b\u52bf\uff0c\u731b\u5411\u5c55\u767d\u8f6f\u808b\u63d2\u4e0b\uff0c\u7adf\u662f\u201c\u91d1\u63d2\u624b\u201d\u624b\u6cd5\u3002 \u540c\u65f6\uff0c\u8de8\u524d\u4e00\u6b65\uff0c\u53f3\u638c\u5982\u5200\uff0c\u731b\u5288\u5c55\u767d\u201c\u80a9\u7a74\u201d\u91cd\u7a74\u3002 \u3000\u3000\u8fd9\u4e00\u62db\u4e24\u5f0f\uff0c\u4e0d\u4f46\u53d8\u5e7b\u5feb\u5982\u7535\u5149\u77f3\u706b\uff0c\u800c\u4e14\u638c\u7f18\u7834\u98ce\u9510\u5578\uff0c\u663e\u89c1\u72c2\u50b2\u9752\u5e74\u5185\u529f\u52b2\u529b\u4e5f\u4e0d \u5f31\u3002 \u3000\u3000\u5982\u679c\u662f\u4e00\u6708\u4e4b\u524d\uff0c\u5c31\u8fd9\u4e24\u62db\uff0c\u5c55\u767d\u4fbf\u8981\u5f53\u573a\u843d\u8d25\uff0c\u53ef\u662f\u4eca\u65e5\u7684\u5c55\u767d\uff0c\u5df2\u975e\u6614\u65e5\u5434\u4e0b\u963f \u8499\uff0c\u89c1\u72c2\u50b2\u9752\u5e74\u7a81\u65bd\u6740\u624b\uff0c\u7acb\u523b\u53cc\u624b\u9f50\u51fa\uff0c\u201c\u562d\uff01\u562d\uff01\u201d\u4e24\u58f0\uff0c\u72c2\u50b2\u9752\u5e74\u4e00\u53cc\u624b\u8155\uff0c\u968f\u7740\u88ab \u5c55\u767d\u53cc\u624b\u6293\u4f4f\u3002 \u3000\u3000\u5c55\u53f0\u53cc\u624b\u5fae\u4e00\u7528\u529b\uff0c\u72c2\u58a9\u9752\u5e74\u7acb\u65f6\u75db\u5f97\u989c\u8272\u60e8\u53d8\uff0c\u989d\u4e0a\u7684\u8c46\u5927\u6c57\u73e0\u7acb\u523b\u6eda\u4e0b\u6765\uff0c\u6240\u5dee\u672a \u51fa\u58f0\u60e8\u547c\u800c\u5df2\u3002 \u3000\u3000\u8fd9\u4e00\u62db\u540d\u4e3a\u201c\u5de7\u5957\u53cc\u9501\u201d\uff0c\u6b63\u662f\u5c55\u767d\u8ddf\u201c\u72ec\u811a\u98de\u9b54\u201d\u5077\u5b66\u6765\u7684\u7edd\u62db\uff0c\u8bbe\u60f3\u5230\u4eca\u591c\u6d3e\u4e0a\u4e86 \u7528\u573a\uff0c\u53ea\u4e00\u51fa\u624b\u4fbf\u628a\u72c2\u50b2\u4e0d\u53ef\u4e00\u4e16\u7684\u9752\u5e74\u5236\u4f4f\u3002 \u3000\u3000\u56e0\u4e3a\u8fd9\u4e00\u62db\u5c55\u767d\u53cc\u624b\u6263\u4f4f\u72c2\u50b2\u9752\u5e74\u7684\u53cc\u8155\u5173\u8282\uff0c\u72c2\u50b2\u9752\u5e74\u53cc\u8155\u75db\u5982\u4e0d\u80fd\u52a8\u4e00\u4e0b\uff0c\u7a7a\u6709\u4e00 \u8eab\u9ad8\u5f3a\u6b66\u529f\uff0c\u4e5f\u81ea\u65e0\u6cd5\u65bd\u5c55\u3002 \u3000\u3000\u5c55\u767d\u4e0d\u4e3a\u5df1\u751a\uff0c\u521a\u60f3\u4ea4\u4ee3\u4e24\u53e5\u573a\u9762\u8bdd\uff0c\u5c31\u628a\u72c2\u50b2\u9752\u5e74\u653e\u5f00\uff0c\u7a81\u7136\uff0c\u4e00\u822c\u52b2\u98ce\uff0c\u76f4\u5411\u4ed6\u8eab \u540e\u649e\u6765\uff0c \u3000\u3000\u540c\u65f6\uff0c\u542c\u90a3\u4f4d\u7ea2\u9762\u8001\u8005\u559d\u9053\uff1a\u201c\u653e\u624b\uff01\u201d \u3000\u3000\u4e0d\u7528\u7ea2\u9762\u8001\u8005\u547c\u559d\uff0c\u5c55\u767d\u4e5f\u77e5\u9053\u662f\u90a3\u8001\u8005\u5411\u81ea\u5df1\u51fa\u624b\uff0c\u5f53\u5373\u653e\u5f00\u6293\u4f4f\u72c2\u50b2\u9752\u5e74\u7684\u53cc\u624b\uff0c \u98d8\u8eab\u6a2a\u8dc3\u4e94\u5c3a\u5f00\u5916\u3002 \u3000\u3000\u201c\u7830\uff01\u201d\u4e00\u58f0\u5de8\u54cd\uff0c\u7ea2\u9762\u8001\u8005\u7528\u529b\u8fc7\u731b\uff0c\u6536\u624b\u4e0d\u53ca\uff0c\u5c55\u767d\u53ca\u65f6\u8eb2\u5f00\uff0c\u90a3\u5f3a\u52b2\u7684\u4e00\u638c\uff0c\u6b63 \u597d\u6253\u5728\u72c2\u50b2\u9752\u5e74\u7684\u80f8\u4e0a\u3002 \u3000\u3000\u8fd9\u4e00\u638c\u628a\u90a3\u72c2\u50b2\u9752\u5e74\u51ed\u7a7a\u9707\u98de\uff0c\u76f4\u98de\u4e00\u4e08\u5f00\u5916\uff0c\u624d\u201c\u562d\u201d\u7684\u4e00\u58f0\uff0c\u649e\u5728\u5899\u58c1\u4e0a\uff0c\u72c2\u50b2\u9752 \u5e74\u7acb\u65f6\u59d4\u987f\u5012\u5730\uff0c\u8fde\u542d\u58f0\u90fd\u672a\u542d\u4e00\u58f0\uff0c\u770b\u6765\u5df2\u662f\u6b7b\u591a\u6d3b\u5c11\u2026\u2026 \u3000\u3000\u5e97\u5bb6\u3001\u98df\u5ba2\u4e00\u9635\u5927\u4e71\uff0c\u9ad8\u58f0\u547c\u558a\uff1a\u201c\u6253\u6b7b\u4eba\u4e86\uff01\u2026\u2026\u201d \u3000\u3000\u7ea2\u9762\u8001\u8005\u89c1\u81ea\u5df1\u4e00\u638c\u672a\u4f24\u5c55\u767d\uff0c\u53cd\u800c\u628a\u81ea\u5df1\u7231\u5f92\u6253\u4f24\uff0c\u6c14\u5f97\u9762\u5982\u55b7\u8840\uff0c\u53d1\u7709\u7686\u70b8\uff0c\u53cc\u638c \u4e00\u62a2\uff0c\u5411\u5c55\u767d\u731b\u6251\u800c\u81f3\u3002 \u3000\u3000\u5c55\u767d\u4e5f\u6ca1\u60f3\u5230\u7ea2\u9762\u8001\u8005\u89c1\u81ea\u5df1\u95ea\u5f00\uff0c\u4ecd\u4e0d\u6536\u638c\uff0c\u4ee5\u81f4\u628a\u90a3\u72c2\u50b2\u9752\u5e74\u6253\u6b7b\uff0c\u53c8\u89c1\u7ea2\u9762\u8001\u8005 \u66b4\u6012\u5982\u96f7\uff0c\u638c\u52bf\u5982\u7ffb\u6c5f\u5012\u6d77\u800c\u81f3\uff0c\u5f53\u5373\u6643\u8eab\u8eb2\u5f00\u3002 \u3000\u3000\u53ef\u662f\uff0c\u7ea2\u9762\u8001\u8005\u72b6\u5982\u75af\u864e\uff0c\u5c55\u767d\u624d\u4e00\u8eb2\u5f00\uff0c\u7ea2\u9762\u8001\u8005\u66b4\u543c\u4e00\u58f0\uff1a\u201c\u54ea\u91cc\u8d70\uff01\u63a5\u62db\uff01\u201d\u53cc \u638c\u6a2a\u626b\uff0c\u53c8\u731b\u5411\u5c55\u767d\u62e6\u8170\u6253\u6765\u3002 \u3000\u3000\u623f\u4e2d\u72ed\u7a84\uff0c\u53c8\u6709\u684c\u6905\u5c4f\u98ce\u7b49\u9648\uff0c\u7ea2\u9762\u8001\u8005\u53cc\u638c\u6765\u52bf\uff0c\u53c8\u731b\u53c8\u5feb\uff0c\u800c\u4e14\u5a01\u529b\u5e7f\u7f69\u4e00\u4e08\u65b9 \u5706\uff0c\u5c55\u767d\u65e0\u6cd5\u8eb2\u95ea\uff0c\u53ea\u6709\u594b\u529b\u786c\u63a5\u4e86\u4e24\u638c\u3002 \u3000\u3000\u201c\u7830\u2014\u2014\u562d\uff01\u201d\u201c\u54d7\u5566\uff01\u201d\u4e00\u9635\u66b4\u54cd\uff0c\u4e24\u4eba\u56db\u638c\u6253\u5728\u4e00\u8d77\uff0c\u4f59\u529b\u56db\u6fc0\uff0c\u684c\u4e0a\u7684\u76d8\u3001\u7897\u3001 \u789f\u3001\u76cf\uff0c\u6a2a\u98de\u56db\u6e85\u3002 \u3000\u3000\u201c\u547c\u5566\uff01\u201d\u201c\u54ce\u54df\uff01\u201d\u5e97\u4e2d\u7684\u98df\u5ba2\u4ee5\u53ca\u5e97\u5c0f\u4e8c\uff0c\u6025\u5411\u95e8\u5916\u8702\u62e5\u9003\u7a9c\uff0c\u6709\u4e0d\u5c11\u4eba\u88ab\u638c\u529b\u4f59 \u52b2\u548c\u6a2a\u98de\u7684\u76d8\u7897\u51fb\u4f24\uff0c\u53d1\u51fa\u60ca\u547c\u60e8\u53eb\uff0c\u4e71\u6210\u4e00\u7247\u2026\u2026 \u3000\u3000\u5c55\u767d\u53ea\u89c9\u7ea2\u9762\u8001\u8005\u638c\u529b\u6df1\u539a\uff0c\u53cc\u638c\u4e00\u63a5\uff0c\u638c\u5fc3\u706b\u70ed\uff0c\u53cc\u773c\u4e00\u9ed1\uff0c\u6697\u60ca\u7ea2\u9762\u8001\u8005\u597d\u5927\u7684\u638c \u52b2\u3002 \u3000\u3000\u7a81\u53c8\u89c1\u7ea2\u9762\u8001\u8005\uff0c\u5934\u4e0a\u77ed\u77ed\u767d\u53d1\u6839\u6839\u76f4\u7acb\uff0c\u53cc\u76ee\u6012\u7741\uff0c\u51e0\u4e4e\u51f8\u51fa\u7736\u5916\uff0c\u53cc\u638c\u638c\u5fc3\u5982\u6d82\u6731 \u67d3\u8840\uff0c\u5411\u4ed6\u4f5c\u52bf\u6251\u6765\uff0c\u72b6\u6781\u53ef\u6016\u2026\u2026 \u3000\u3000\u5c55\u767d\u731b\u7136\u8bb0\u8d77\uff0c\u6b66\u6797\u4f20\u8bf4\u6709\u4e00\u79cd\u7edd\u6bd2\u638c\u529f\uff0c\u540d\u53eb\u201c\u7ea2\u7802\u8840\u5f62\u638c\u201d\u4e2d\u4eba\u5982\u88ab\u706b\u4fa5\uff0c\u4e94\u810f\u5185 \u8151\u711a\u70e7\u67af\u7126\u800c\u6b7b\uff0c\u6b79\u6bd2\u65e0\u6bd4\uff0c\u4e0d\u8fc7\u53ea\u95fb\u4f20\u8a00\uff0c\u4ece\u672a\u89c1\u8fc7\uff0c\u5982\u4eca\u9661\u89c1\u7ea2\u9762\u8001\u8005\u53cc\u638c\u638c\u5fc3\u706b\u8d64\uff0c \u800c\u4e14\uff0c\u521a\u624d\u786c\u63a5\u4e24\u638c\uff0c\u638c\u5fc3\u706b\u70ed\uff0c\u4e5f\u662f\u4ee5\u524d\u4ece\u672a\u7ecf\u8fc7\uff0c\u60f3\u5230\u8fd9\u91cc\uff0c\u5c55\u767d\u4e0d\u7531\u5fc3\u5185\u4e00\u5bd2\u2026\u2026 \u3000\u3000\u65cb\u53c8\u60f3\u5230\uff0c\u5371\u6025\u65f6\u5b89\u4e50\u516c\u5b50\u4ed7\u4e49\u63f4\u624b\uff0c\u5bf9\u81ea\u5df1\u603b\u7b97\u6709\u6069\uff0c\u5982\u4eca\u8ddf\u4ed6\u7684\u624b\u4e0b\u4eba\u53d1\u751f\u8bef\u4f1a\uff0c \u800c\u4e14\u8bef\u4f1a\u6108\u7ed3\u6108\u6df1\uff0c\u4ee5\u540e\u96be\u518d\u89c1\u9762\uff0c\u52a0\u4e4b\u76ee\u524d\u60c5\u52bf\u4e5f\u65e0\u6cd5\u89e3\u91ca\uff0c\u4e0d\u5982\u5148\u8131\u79bb\u6b64\u5730\uff0c\u4ee5\u540e\u6709\u4ec0 \u4e48\u4e8b\u518d\u8bf4\u2026\u2026 \u3000\u3000\u5c55\u767d\u601d\u7d22\u8fd9\u4e9b\u65f6\uff0c\u5fc3\u601d\u7535\u8f6c\uff0c\u53ea\u662f\u5239\u90a3\u95f4\u7684\u4e8b\uff0c\u4f46\u7ea2\u9762\u8001\u8005\u201c\u7ea2\u7802\u8840\u5f62\u638c\u201d\uff0c\u529f\u529b\u5df2\u8fd0 \u81f3\u671f\u5cf0\uff0c\u5927\u543c\u4e00\u58f0\uff1a\u201c\u563f\uff01\u201d\u53cc\u624b\u5982\u72c2\u98ce\u5de8\u5f88\uff0c\u731b\u5411\u5c55\u767d\u63a8\u51fa\u3002 \u3000\u3000\u5c55\u767d\u5355\u638c\u4f3c\u5c01\u4f3c\u95ed\uff0c\u53ea\u8f7b\u8f7b\u5411\u6765\u52bf\u4e00\u63a5\uff0c\u501f\u529b\u817e\u8eab\uff0c\u53e3\u4e2d\u559d\u9053\uff1a\u201c\u5931\u966a\u4e86\uff01\u201d\u76f4\u5411\u7a97\u5916 \u9038\u53bb\u3001 \u3000\u3000\u201c\u54ea\u91cc\u8d70\uff1f\u201d\u8eab\u540e\u4f20\u6765\u7ea2\u9762\u8001\u8005\u7684\u66b4\u6012\u547c\u5428\u2026\u2026 \u3000\u3000\u201c\u6253\uff01\u201d\u5c55\u767d\u7a9c\u51fa\u7a97\u5916\uff0c\u53ea\u89c1\u4e09\u70b9\u5bd2\u8292\u8fce\u9762\u98de\u6765\u3002 \u5c55\u767d\u51ed\u7a7a\u2014\u4e2a\u201c\u4e91\u91cc\u7ffb\u201d\uff0c\u8eab\u5f62\u53c8\u63d0\u9ad8\u4e09\u5c3a\uff0c\u201c\u593a\uff01\u593a\uff01\u593a\uff01\u201d\u4e09\u679a\u900f\u9aa8\u9489\u843d\u7a7a\u9489\u5728\u7a97\u68c2 \u4e0a\uff0c\u5c55\u767d\u633a\u8eab\u843d\u5730\uff0c\u62ac\u5934\u4e00\u770b\uff0c\u5927\u5403\u4e00\u60ca\u2026\u2026\u201d \u7b2c\u5341\u516b\u7ae0\u3000\u7ea2\u7802\u8840\u5f62\u638c>> \u53e4\u9f99\u300a\u5251\u5ba2\u884c\u300b \u7b2c\u5341\u516b\u7ae0\u3000\u7ea2\u7802\u8840\u5f62\u638c \u3000\u3000\u5c55\u767d\u8dc3\u51fa\u7a97\u5916\uff0c\u8fde\u8eb2\u8fc7\u8fce\u9762\u800c\u6765\u7684\u4e09\u822c\u6697\u5668\uff0c\u4e00\u63a5\u5934\u731b\u89c1\u9ed1\u547c\u547c\u4e00\u7247\uff0c\u5e26\u7740\u52b2\u98ce\uff0c\u731b\u5982 \u6cf0\u5c71\u538b\u9876\u4e00\u822c\u5411\u4ed6\u5934\u4e0a\u538b\u4e0b\u3002 \u3000\u3000\u5c55\u767d\u5927\u5403\u4e00\u60ca\uff0c\u731c\u4e0d\u900f\u8fd9\u8fce\u9762\u538b\u6765\u7684\u662f\u4ec0\u4e48\u7269\u4ef6\u3002\u5306\u5fd9\u4e2d\uff0c\u4e00\u633a\u78a7\u5251\uff0c\u201c\u56db\u4e24\u62e8\u5343 \u65a4\u201d\uff0c\u7528\u5251\u5c16\u4e00\u9876\uff0c\u201c\u547c\u201d\u7684\u4e00\u58f0\uff0c\u8d8a\u9876\u800c\u8fc7\uff0c\u56de\u5934\u4e00\u770b\uff0c\u539f\u6765\u662f\u4e00\u4e2a\u80d6\u5927\u548c\u5c1a\uff0c\u7eb5\u7b4b\u76d8\u7ed3 \u7684\u7c97\u81c2\u4e0a\uff0c\u633d\u4e86\u4e00\u4e2a\u95e8\u6247\u5927\u5c0f\u7684\u95e8\u724c\uff0c\u7eb5\u843d\u4ed6\u7684\u8eab\u540e\u3002 \u3000\u3000\u8fd9\u548c\u5c1a\u9ad8\u5927\u7c97\u58ee\uff0c\u795e\u6001\u5a01\u731b\uff0c\u751f\u5c31\u7684\u8c79\u5934\u73af\u670d\u7eb5\u987b\u7ed5\u989d\uff0c\u5149\u5149\u7684\u9876\u95e8\u4e0a\uff0c\u70eb\u7740\u516b\u9897\u8c46\u5927 \u7684\u6212\u75a4\uff0c\u6b63\u8e6c\u7740\u4e00\u53cc\u73af\u76ee\u671b\u7740\u4ed6\uff0c\u6ee1\u817f\u60ca\u8be7\u4e4b\u8272\u3002 \u3000\u3000\u5c55\u767d\u731b\u7136\u8bb0\u8d77\u6c5f\u6e56\u4e0a\u201c\u94c1\u724c\u548c\u5c1a\u201d\u4e4b\u540d\uff0c\u624b\u62ff\u5343\u65a4\u4e4b\u91cd\u7684\u94c1\u813e\uff0c\u4e00\u6251\u4e00\u538b\u4e4b\u52bf\uff0c\u529b\u903e\u4e07 \u65a4\uff0c\u81ea\u5df1\u5728\u69fd\u7136\u65e0\u77e5\u4e4b\u4e2d\uff0c\u4ee5\u5251\u5c16\u6321\u8fc7\u5982\u6b64\u91cd\u5927\u7684\u4e00\u538b\uff0c\u83ab\u8bf4\u201c\u94c1\u724c\u548c\u5c1a\u201d\u5403\u60ca\uff0c\u5c31\u8fde\u81ea\u5df1 \u4e5f\u4e0d\u6562\u76f8\u4fe1\u3002 \u3000\u3000\u53ef\u662f\uff0c\u7a97\u5916\u8857\u9053\u4e0a\uff0c\u5df2\u6709\u6570\u5341\u4eba\u628a\u9152\u5e97\u56e2\u56e2\u56f4\u4f4f\uff0c\u5c31\u5728\u5c55\u767d\u5fae\u4e00\u9519\u6115\u4e4b\u9645\uff0c\u4e24\u9053\u5149\u5f71\uff0c \u604d\u5982\u6d41\u661f\uff0c\u5728\u534a\u7a7a\u4e2d\u5212\u4e86\u4e24\u9053\u5149\u5f27\uff0c\u731b\u5411\u5c55\u767d\u5934\u4e0a\u5de6\u53f3\u201c\u592a\u9633\u201d\u53cc\u7a74\u5c04\u6765\u3002 \u3000\u3000\u5c55\u767d\u8eab\u5f62\u4e00\u77ee\uff0c\u624b\u4e2d\u78a7\u5251\u201c\u4e3e\u706b\u70e7\u5929\u201d\u5f80\u4e0a\u8fce\u53bb\u3002 \u3000\u3000\u201c\u9f50\uff01\u545b\uff01\u201d \u3000\u3000\u4e24\u58f0\u5fae\u9e23\uff0c\u6765\u4eba\u4e00\u5bf9\u7ec3\u5b50\u6d41\u661f\u9524\u7684\u9524\u5934\uff0c\u88ab\u5c55\u767d\u4e00\u5251\u524a\u843d\uff0c\u201c\u53ee\u5f53\uff01\u5495\u9c81\uff01\u201d\u4e24\u4e2a\u9524\u5934 \u5728\u9752\u77f3\u677f\u7684\u8857\u9053\u4e0a\u6eda\u51fa\u8001\u8fdc\u3002 \u3000\u3000\u6570\u58f0\u6012\u53f1\uff0c\u4e09\u9053\u5bd2\u5149\u70df\u95ea\uff0c\u4e24\u652f\u5251\uff0c\u4e00\u67c4\u5200\uff0c\u9f50\u5411\u5c55\u767d\u780d\u523a\u800c\u6765\u3002 \u3000\u3000\u5c55\u767d\u8eab\u968f\u5251\u8d70\uff0c\u65e0\u60c5\u78a7\u5251\u201d\u5728\u8eab\u524d\u8361\u8d77\u4e00\u7f15\u78a7\u5149\uff0c\u731b\u5411\u4e09\u822c\u5175\u5668\u524a\u53bb\u3002 \u3000\u3000\u6765\u4eba\u4f3c\u5df2\u77e5\u9053\u5c55\u767d\u201c\u65e0\u60c5\u78a7\u5251\u201d\u5389\u5bb3\uff0c\u62db\u81f3\u534a\u9014\uff0c\u6025\u5fd9\u6536\u62db\uff0c\u817e\u8eab\u540e\u8dc3\u2026\u2026 \u3000\u3000\u201c\u547c\uff01\u201d\u4e00\u80a1\u52b2\u98ce\uff0c\u731b\u7136\u53c8\u5411\u5c55\u767d\u88ad\u5230\u3002 \u3000\u3000\u5c55\u767d\u5251\u62db\u51fa\u624b\uff0c\u6765\u4e0d\u53ca\u64a4\u5251\uff0c\u5de6\u638c\u5f53\u80f8\u6325\u51fa\uff0c\u5ba3\u5411\u88ad\u6765\u52b2\u98ce\u8fce\u53bb\u3001 \u3000\u3000\u201c\u562d\uff01\u201d\u4e00\u58f0\u5927\u9707\uff0c\u5c55\u767d\u4e0a\u8eab\u5fae\u6643\uff0c\u90a3\u53cc\u638c\u731b\u88ad\u5c55\u767d\u7684\u534a\u767e\u8001\u8005\uff0c\u8e6c\uff01\u8e6c\uff01\u8e6c\uff01\u540e\u9000\u4e09 \u6b65\uff0c\u5bf9\u5c55\u767d\u6115\u7136\u800c\u89c6\u3002 \u3000\u3000\u8fd9\u534a\u767e\u8001\u8005\uff0c\u6b63\u662f\u4ee5\u4e00\u53cc\u94c1\u638c\u95fb\u540d\u9c81\u5357\u7684\u201c\u94c1\u638c\u201d\u5415\u516d\u987a\uff0c\u82e6\u7ec3\u201c\u94c1\u7802\u638c\u201d\uff0c\u8db3\u4e0b\u4e86\u4e8c \u4e09\u5341\u5e74\u7684\u529f\u592b\uff0c\u7d20\u5e38\u4ee5\u201c\u94c1\u638c\u201d\u81ea\u8d1f\u3002\u4ed6\u89c1\u5c55\u767d\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u8fde\u6218\u4e94\u4eba\uff0c\u4e0d\u8fc7\u662f\u4ed7\u7740\u5b9d\u5251\u950b \u5229\uff0c\u5728\u638c\u529f\u5185\u529b\u4e0a\u51b3\u4e0d\u4f1a\u6709\u592a\u6df1\u7684\u706b\u5019\uff0c\u60f3\u5728\u4eba\u524d\u9732\u8138\uff0c\u731b\u7136\u5411\u5c55\u767d\u5288\u51fa\u4e00\u638c\u3002\u4ed6\u62ff\u634f\u5f97\u6070 \u5230\u597d\u5904\uff0c\u89c6\u5b9a\u5c55\u767d\u5251\u62db\u9012\u51fa\uff0c\u65e0\u6cd5\u62bd\u5251\u8fd8\u62db\u4e4b\u9645\uff0c\u4e00\u638c\u63a8\u51fa\uff0c\u7528\u4e86\u516b\u6210\u529f\u529b\u3002 \u3000\u3000\u6ca1\u60f3\u5230\u5c55\u767d\u786c\u63a5\u4e86\u4ed6\u4e00\u638c\uff0c\u800c\u4e14\u7528\u7684\u662f\u5de6\u624b\uff0c\u8f7b\u8f7b\u677e\u677e\u5730\u628a\u4ed6\u9707\u9000\u4e86\u4e09\u6b65\u3002\u770b\u5c55\u767d\u90a3\u628a \u5b50\u5e74\u7eaa\uff0c\u5c31\u7b97\u6253\u51fa\u5a18\u80ce\u7ec3\u8d77\uff0c\u529f\u529b\u4e5f\u4e0d\u4f1a\u6df1\u539a\u8fc7\u81ea\u5df1\uff0c\u8fd9\u600e\u4e0d\u4f7f\u72c2\u50b2\u81ea\u8d1f\u3001\u6700\u7231\u51fa\u98ce\u5934\u7684 \u201c\u94c1\u638c\u201d\u5415\u8001\u516d\u5403\u60ca\u5462\uff01 \u3000\u3000\u5c55\u767d\u809a\u5b50\u5df2\u997f\u5f97\u5495\u5495\u53eb\uff0c\u996d\u6ca1\u6709\u5403\u6210\uff0c\u5374\u88ab\u4eba\u8fde\u756a\u88ad\u51fb\uff0c\u4e0d\u7ed9\u4ed6\u4e00\u70b9\u5598\u606f\u7684\u673a\u4f1a\uff0c\u8fde\u997f \u5e26\u6c14\uff0c\u4f8b\u662f\u771f\u6709\u70b9\u706b\u4e86\uff0c\u53cc\u76ee\u4e00\u77aa\uff0c\u795e\u5149\u56db\u5c04\uff0c\u5bf9\u7740\u56f4\u5728\u4ed6\u56db\u5468\u7684\u6570\u5341\u4f4d\u6b66\u6797\u4eba\u7269\uff0c\u201c\u65e0\u60c5 \u78a7\u5251\u201d\u4e00\u9707\u2026\u2026 \u3000\u3000\u5176\u5b9e\u5c55\u767d\u5c1a\u672a\u51fa\u624b\uff0c\u56f4\u5728\u4ed6\u56db\u5468\u7684\u6b66\u6797\u4eba\u7269\uff0c\u8bef\u4ee5\u4e3a\u5c55\u767d\u8981\u51fa\u624b\u653b\u51fb\uff0c\u7ade\u5404\u81ea\u9000\u4e86\u4e00 \u6b65\uff0c\u9762\u73b0\u60e7\u8272\u2026\u2026 \u3000\u3000\u5c55\u767d\u4e0d\u7531\u54c8\u54c8\u4e00\u7b11\uff0c\u5148\u770b\u5230\u8fd9\u4e9b\u4eba\u6765\u52bf\u6c79\u6c79\uff0c\u60f3\u4e0d\u5230\u81ea\u5df2\u7a0d\u4e00\u4f5c\u52bf\uff0c\u5c1a\u672a\u51fa\u62db\uff0c\u7adf\u5982\u6b64 \u80c6\u602f\u3002 \u3000\u3000\u88ab\u5c55\u767d\u4e00\u7b11\uff0c\u4f17\u4eba\u66ae\u7136\u8b66\u609f\uff0c\u60f3\u8fd9\u4e9b\u6b66\u6797\u4eba\u7269\u4e5f\u5747\u662f\u5728\u5200\u5c16\u4e0a\u6253\u8f6c\u7684\u786c\u6c49\uff0c\u5bf9\u654c\u4e4b\u95f4\uff0c \u54ea\u80fd\u5411\u654c\u624b\u793a\u5f31\uff1f\u4e0d\u7531\u4e2a\u4e2a\u8138\u4e0a\u53d1\u70e7\u3002\u53ef\u4e5f\u5c31\u6fc0\u6012\u4e86\u6570\u4eba\uff0c\u66b4\u8d50\u58f0\u4e2d\uff0c\u4eba\u5f71\u7eb7\u6251\uff0c\u5200\u3001\u5251\u3001 \u9524\u3001\u6293\uff0c\u6570\u822c\u5175\u5668\uff0c\u5982\u72c2\u98ce\u9aa4\u96e8\uff0c\u9f50\u5411\u5c55\u767d\u653b\u5230\u3001 \u3000\u3000\u6025\u5207\u95f4\uff0c\u5c55\u767d\u7528\u51fa\u4e00\u62db\u201c\u75be\u98ce\u65a9\u52b2\u8349\u201d\u5251\u62db\uff0c\u201c\u65e0\u60c5\u78a7\u5251\u201d\u95ea\u8d77\u4e00\u7247\u78a7\u8272\u5149\u5899\uff0c\u201c\u6652\uff01 \u545b\uff01\u201d\u8fde\u54cd\uff0c\u4f8b\u6709\u4e8c\u4e09\u4eba\u6536\u62db\u4e0d\u53ca\uff0c\u624b\u4e2d\u5175\u5668\u88ab\u5c55\u767d\u78a7\u5251\u524a\u65ad\uff0c\u4f17\u4eba\u4e00\u9635\u60ca\u547c\uff0c\u4e00\u9f50\u817e\u8eab\u540e \u9000\u2026\u2026\u5c55\u767d\u8fd9\u62db\u201c\u75be\u98ce\u65a9\u52b2\u8349\u201d\uff0c\u4e43\u662f\u5728\u201c\u8c79\u7a81\u5c71\u5e84\u201d\u770b\u5230\u201c\u8ffd\u98ce\u5251\u201d\u6a0a\u6770\u4e24\u6b21\u65bd\u5c55\uff0c\u800c\u5077 \u5b66\u4f1a\u7684\u3002\u867d\u7136\u5c1a\u4e0d\u80fd\u5b8c\u5168\u628a\u63e1\u4f4f\u5176\u4e2d\u5965\u5999\uff0c\u4f46\u5927\u81f4\u624b\u6cd5\u5df1\u4e0d\u5dee\u4e86\uff0c\u60f3\u4e0d\u5230\u65bd\u5c55\u51fa\u6765\uff0c\u7adf\u6709\u8fd9 \u5927\u5a01\u529b\u3002 \u3000\u3000\u5c55\u767d\u4e00\u62db\u5f97\u624b\uff0c\u6b63\u60f3\u4e58\u52bf\u51b2\u6740\uff0c\u7a81\u542c\u5927\u559d\u4e00\u58f0\uff1a\u201c\u4f4f\u624b\uff01\u201d \u3000\u3000\u58f0\u5982\u6d2a\u949f\uff0c\u9707\u8033\u8f70\u9e23\uff0c\u5c55\u81ea\u56de\u5934\u4e00\u770b\uff0c\u4ece\u5e97\u95e8\u9ad8\u53f0\u9636\u4e0a\u8fc8\u6b65\u8d70\u6765\uff0c\u6b63\u662f\u90a3\u7ea2\u9762\u8001\u8005\uff0c\u8eab \u540e\u8ddf\u5b9a\u90a3\u4fca\u79c0\u5c11\u5e74\u3002\u201c\u4f60\u662f\u4f55\u4eba\u95e8\u4e0b\uff1f\u201d\u7ea2\u9762\u8001\u8005\u5b9a\u8fd1\u5c55\u767d\u52b2\u6570\u5927\u9a6c\u91d1\u5200\u7684\u4e00\u7ad9\uff0c\u7528\u624b\u6307\u5b9a \u5c55\u767d\u95ee\u9053\uff1a\u201c\u4e0e\u9547\u6c5f\u6a0a\u5927\u7237\u6709\u4ec0\u4e48\u6e0a\u6e90\uff1f\u8bf4\u8bf4\u660e\u767d\uff0c\u514d\u5f97\u5f15\u8d77\u8bef\u4f1a\uff01\u201d \u3000\u3000\u201c\u5728\u4e0b\u4e0e\u4ec0\u4e48\u9547\u6c5f\u6a0a\u5927\u7237\u7d20\u4e0d\u76f8\u8bc6\uff01\u201d\u5c55\u767d\u7b54\u9053\uff1a\u201c\u81f3\u4e8e\u5e08\u95e8\uff0c\u6055\u96be\u5949\u544a\u3002\u201d\u5c55\u767d\u662f\u4e2a \u8bda\u5b9e\u9752\u5e74\uff0c\u4e0d\u8bc6\u6c5f\u6e56\u4e0a\u7684\u9634\u8c0b\u9669\u8bc8\uff0c\u809a\u5b50\u91cc\u6709\u4ec0\u4e48\u5634\u4e2d\u4fbf\u8bf4\u4ec0\u4e48\u3002\u672c\u6765\u4ed6\u9189\u5fc3\u4e60\u6b66\uff0c\u5374\u59cb\u7ec8 \u6ca1\u6709\u62dc\u8fc7\u5e08\uff0c\u8ddf\u8fbe\u4e2a\u8ba8\u6559\u4e24\u624b\uff0c\u8ddf\u90a3\u4e2a\u5077\u5b66\u4e24\u62db\uff0c\u5f53\u7136\u4fbf\u8bf4\u4e0d\u4e0a\u5e08\u627f\u4f55\u4eba\u3002 \u3000\u3000\u7ea2\u9762\u8001\u8005\u54c8\u54c8\u5927\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u5b50\u591f\u72c2\uff01\u4f60\u53ef\u77e5\u8001\u592b\u662f\u4f55\u4eba\uff1f\u201d \u3000\u3000\u201c\u6055\u5728\u4e0b\u773c\u62d9\uff0c\u5e76\u4e0d\u8bc6\u8001\u2026\u2026\u5c0a\u9a7e\u4f55\u4eba\uff01\u201d\u5c55\u767d\u672c\u60f3\u79f0\u547c\u4ed6\u4e00\u58f0\u8001\u524d\u8f88\uff0c\u4f46\u770b\u5230\u4ed6\u8f7b\u89c6 \u81ea\u5df2\u7684\u795e\u8272\uff0c\u4e34\u65f6\u6539\u53e3\uff0c\u8bed\u6c14\u4e5f\u5f88\u4e0d\u5ba2\u6c14\u3002 \u3000\u3000\u201c\u521d\u51fa\u8305\u5e90\u7684\u6bdb\u5934\u5c0f\u4f19\u5b50\uff01\u201d\u7ea2\u9762\u8001\u8005\u53c8\u662f\u54c8\u54c8\u4e00\u7b11\u3002\u8bf4\u9053\uff1a\u201c\u8001\u592b\u771f\u4e0d\u76f8\u4fe1\u2018\u8fa3\u624b\u7ae5 \u5fc3\u2019\u4f1a\u683d\u5728\u4f60\u7684\u624b\u4e2d\u3002\u6ca1\u6709\u522b\u7684\uff0c\u8001\u592b\u8981\u8003\u91cf\u4f60\u4e09\u638c\uff0c\u5982\u4f60\u80fd\u63a5\u4f4f\u8001\u592b\u4e09\u638c\uff0c\u82cf\uff0c\u9c81\u5883\u5185\u4efb \u7531\u4f60\u8d70\uff0c\u51b3\u4e0d\u4f1a\u6709\u4eba\u62e6\u963b\u4f60\uff0c\u5c0f\u4f19\u5b50\uff0c\u4f60\u770b\u8fd9\u529e\u6cd5\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u5c55\u767d\u4e0d\u8ba4\u8bc6\u8fd9\u7ea2\u9762\u8001\u8005\uff0c\u4e8b\u5b9e\u4e0a\u8fd9\u7ea2\u9762\u8001\u8005\u4e43\u82cf\u3001\u9c81\u4e00\u5e26\u6c5f\u6e56\u9053\u4e0a\u95fb\u540d\u4e27\u80c6\u7684\u4eba\u7269\u3002\u59d3 \u59da\u540d\u70b3\u6606\uff0c\u7ef0\u53f7\u4eba\u79f0\u201c\u8840\u638c\u706b\u9f99\u201d\uff0c\u4e0d\u4f46\u201c\u7ea2\u7802\u8840\u5f62\u638c\u201d\u7ec3\u6709\u5341\u6210\u706b\u5019\uff0c\u800c\u4e14\u4e00\u8eab\u706b\u836f\u6697 \u5668\uff0c\u66f4\u662f\u72ec\u6b65\u6c5f\u6e56\uff0c\u5c24\u5176\u4ed6\u80a9\u4e0a\u659c\u63d2\u7684\u90a3\u67c4\u5916\u95e8\u5175\u5668\uff0c\u5f62\u4f3c\u4eba\u638c\uff0c\u5374\u6bd4\u4eba\u638c\u7565\u5927\uff0c\u4e43\u767e\u201c\u8ffd \u98ce\u516b\u6253\u201d\u64c5\u957f\u70b9\u7a74\uff0c\u65e2\u53ef\u5f53\u70b9\u7a74\u4f7f\u7528\uff0c\u53c8\u53ef\u5f53\u4e07\u5b57\u6885\u82b1\u593a\u7528\uff0c\u9664\u4e86\u70b9\u7a74\u3001\u9501\u593a\u654c\u624b\u5175\u5668\u4e4b \u5916\uff0c\u4f38\u76f4\u4e2d\u6307\u4e4b\u4e2d\uff0c\u5c1a\u85cf\u6709\u6781\u5389\u5bb3\u7684\u706b\u836f\u6697\u5668\uff0c\u4e0e\u654c\u4eba\u8fc7\u62db\u4e4b\u9645\uff0c\u62db\u51fa\u4e4b\u540e\uff0c\u4e00\u6309\u628a\u67c4\u5f39 \u7c27\uff0c\u6697\u5668\u5373\u968f\u6307\u5c16\u53d1\u51fa\uff0c\u4f7f\u4eba\u9632\u4e0d\u80dc\u9632\uff0c\u8eb2\u4e0d\u80dc\u8eb2\uff0c\u53ef\u8bf4\u662f\u5389\u5bb3\u9738\u9053\u5df2\u6781\u3002\u4ed6\u628a\u8fd9\u72ec\u7279\u7684\u5916 \u7528\u5175\u5668\uff0c\u53eb\u505a\u201c\u4ed9\u4eba\u638c\u201d\uff0c\u6709\u4e0d\u5c11\u6c5f\u6e56\u597d\u624b\uff0c\u683d\u5728\u4ed6\u8fd9\u67c4\u5916\u95e8\u5175\u5668\u4e4b\u4e0b\u3002\u4ed6\u7eb5\u6a2a\u82cf\u9c81\u4e24\u7701\uff0c \u9c9c\u9022\u654c\u624b\uff0c\u56e0\u6b64\u517b\u6210\u4ed6\u8ddf\u9ad8\u4e8e\u9876\u7684\u50b2\u6027\u3001 \u3000\u3000\u8fd9\u201c\u8840\u638c\u706b\u9f99\u201c\u59da\u70b3\u6606\uff0c\u5728\u82cf\u9c81\u4e00\u5e26\u4fe8\u7136\u4e00\u65b9\u9738\u4e3b\uff0c\u4e0d\u77e5\u600e\u4e48\u4e5f\u88ab\u201c\u5b89\u4e50\u516c\u5b50\u201d\u6536\u7f57\u4e86 \u53bb\uff0c\u5728\u8fd9\u5174\u9686\u9547\u4e0a\u5750\u9547\uff0c\u4e3a\u82cf\u5dde\u4e91\u68a6\u5c71\u5e84\u5916\u56f4\uff0c\u72ec\u6321\u4e00\u9762\u3002\u4eca\u5929\u4ed6\u542c\u5230\u5c5e\u4e0b\u5bdf\u62a5\uff0c\u8bf4\u6709\u4e00\u4e2a \u5e26\u5251\u5c11\u5e74\uff0c\u5728\u9547\u5317\u5bc6\u6811\u6797\u5185\u628a\u201c\u8fa3\u624b\u7ae5\u5fc3\u201d\u8d39\u4e00\u7ae5\u6253\u6b7b\uff0c\u4ed6\u7b80\u76f4\u4e0d\u6562\u76f8\u4fe1\u81ea\u5df1\u7684\u8033\u6735\uff0c\u60f3\u90a3 \u201c\u8fa3\u624b\u7ae5\u5fc3\u201d\u8d39\u4e00\u7ae5\uff0c\u4e43\u5b89\u4e50\u516c\u5b50\u95e8\u4e0b\u98df\u5ba2\u4e2d\u7684\u4e00\u6d41\u9ad8\u624b\uff0c\u5728\u6c5f\u6e56\u4e0a\u6b66\u529f\u3001\u540d\u671b\u90fd\u5f88\u9ad8\uff0c\u5c31 \u662f\u81ea\u5df2\u4e5f\u4e0d\u6562\u8bf4\u51c6\u6709\u628a\u63e1\u80fd\u80dc\u8fc7\u8d39\u4e00\u7ae5\u3002\u56e0\u6b64\uff0c\u4ed6\u534a\u4fe1\u534a\u7591\uff0c\u4e00\u65b9\u9762\u901a\u77e5\u5c5e\u4e0b\u6ce8\u610f\u4fa6\u5bdf\u5e26\u5251 \u5c11\u5e74\u7684\u884c\u8e2a\uff0c\u4e00\u65b9\u9762\u6d3e\u4e13\u4eba\u5411\u82cf\u5dde\u5b89\u4e50\u516c\u5b50\u62a5\u4fe1\u53bb\u4e86\u3002 \u3000\u3000\u540e\u6765\u4ed6\u542c\u5c5e\u4e0b\u62a5\u544a\uff0c\u5e26\u5251\u5c11\u5e74\u5f84\u884c\u6765\u5230\u5174\u9686\u9547\u4e0a\uff0c\u4ed6\u8fd9\u624d\u7387\u9886\u81ea\u5df1\u7684\u5f92\u5f1f\u201c\u7389\u9762\u54ea\u5412\u201d \u5b8b\u5c0f\u98de\u53ca\u5c5e\u4e0b\u9ad8\u624b\uff0c\u8d76\u5230\u9152\u5e97\u6765\u4f1a\u5c55\u767d\u3002 \u3000\u3000\u5982\u4eca\u89c1\u5c55\u767d\u65bd\u51fa\u9547\u6c5f\u6a0a\u6c0f\u95e8\u4e2d\u7684\u201c\u8ffd\u98ce\u5251\u6cd5\u201d\uff0c\u56e0\u4e3a\u5b89\u4e50\u516c\u5b50\u4e0e\u9547\u6c5f\u6a0a\u5bb6\u6709\u6781\u6df1\u7684\u4ea4 \u60c5\uff0c\u4ed6\u4ee5\u4e3a\u5c55\u767d\u662f\u9547\u6c5f\u6a0a\u5bb6\u65b9\u9762\u7684\u4eba\uff0c\u6015\u5f15\u8d77\u4e24\u5bb6\u7684\u8bef\u4f1a\uff0c\u56e0\u6b64\u624d\u51fa\u9762\u559d\u95ee\u3002 \u3000\u3000\u8c01\u77e5\u5c55\u767d\u65ad\u7136\u5426\u8ba4\uff0c\u8bed\u6c14\u751a\u72c2\uff0c\u8fd9\u624d\u6fc0\u8d77\u4e86\u201c\u8840\u638c\u706b\u9f99\u201d\u7684\u6012\u706b\uff0c\u58f0\u8a00\u8981\u4e0e\u5c55\u767d\u4e09\u638c\u8d4c \u8f93\u8d62\u3002 \u3000\u3000\u5c55\u767d\u751f\u5177\u50b2\u9aa8\uff0c\u4e5f\u662f\u4e0d\u77e5\u5929\u9ad8\u5730\u539a\uff0c\u89c1\u7ea2\u9762\u8001\u8005\u77a7\u4e0d\u8d77\u81ea\u5df1\u7684\u795e\u8272\uff0c\u5f53\u5373\u50b2\u7136\u5e94\u9053\uff1a \u201c\u4e0d\u7ba1\u4f60\u5212\u4e0b\u4ec0\u4e48\u9053\u6765\uff0c\u5728\u4e0b\u63a5\u4f4f\u4f60\u7684\u5c31\u662f\u4e86\uff01\u201d\u8bf4\u7740\u5c06\u5251\u8fd8\u9798\uff0c\u84c4\u52bf\u5f85\u654c\u3002 \u3000\u3000\u201c\u597d\u5c0f\u5b50\uff0c\u7b97\u4f60\u6709\u52c7\u6c14\uff01\u201d\u8840\u638c\u706b\u9f99\u66b4\u543c\u58f0\u4e2d\uff0c\u53cc\u80a9\u5411\u4e0a\u4e00\u8038\uff0c\u8eab\u5f62\u524d\u5f2f\u5982\u5f13\uff0c\u5934\u4e0a\u77ed \u5982\u523a\u732c\u7684\u767d\u53d1\u6839\u6839\u76f4\u7acb\uff0c\u539f\u5c31\u7ea2\u6da6\u6da6\u7684\u4e00\u5f20\u8138\u9762\u66f4\u52a0\u901a\u7ea2\u8d77\u6765\uff0c\u66f2\u81c2\u7acb\u8155\uff0c\u53cc\u638c\u7ad6\u8d77\u5982\u5200\uff0c \u638c\u5fc3\u66f4\u662f\u8d64\u7ea2\u5982\u706b\uff0c\u95f7\u58f0\u543c\u9053\uff1a\u201c\u5c0f\u5fc3\u4e86\uff01\u8fd9\u662f\u8001\u592b\u7684\u7b2c\u4e00\u638c\uff01\u201d \u3000\u3000\u66b4\u543c\u58f0\u4e2d\uff0c\u201c\u8840\u638c\u706b\u9f99\u201d\u53cc\u638c\u4e00\u632b\uff0c\u628a\u4ed6\u9707\u60ca\u6c5f\u6e56\u7684\u201c\u7ea2\u7802\u8840\u5f62\u638c\u201d\u529f\uff0c\u8fd0\u81f3\u4e94\u6210\u529f \u529b\uff0c\u547c\u7684\u4e00\u58f0\uff0c\u53f3\u638c\u63a8\u51fa\u4e00\u822c\u52b2\u98ce\uff0c\u731b\u5411\u5c55\u767d\u80f8\u524d\u649e\u81f3\u3002 \u3000\u3000\u201c\u7830\u201d\u7136\u4e00\u58f0\u66b4\u54cd\uff0c\u53cc\u638c\u51fb\u5b9e\uff0c\u52b2\u98ce\u6fc0\u8361\uff0c\u98de\u6c99\u626c\u77f3\uff0c\u98de\u5c18\u5f71\u4e2d\uff0c\u5c55\u767d\u4e0a\u8eab\u6643\u4e86\u4e24\u6643\uff0c \u4ecd\u7136\u7ad9\u4f0e\uff0c\u4f46\u4e00\u822c\u70ed\u6d41\u901a\u8fc7\u638c\u5fc3\uff0c\u53ea\u611f\u5230\u540c\u8eab\u5982\u88ab\u706b\u7099\uff0c\u5947\u70ed\u96be\u6328\uff0c\u53e3\u5e72\u820c\u71e5\uff0c\u5934\u6655\u6b32\u5012\u3002 \u3000\u3000\u90a3\u201c\u8840\u638c\u706b\u9f99\u201d\u5374\u88ab\u5f53\u5730\u9707\u9000\u4e24\u6b65\uff0c\u8fd9\u5f53\u7136\u662f\u4ed6\u672a\u7528\u51fa\u5168\u529b\uff0c\u4ec5\u4ee5\u4e94\u6210\u529f\u529b\u5e94\u654c\uff0c\u800c\u5c55 \u81ea\u5374\u5df2\u628a\u638c\u529b\u8fd0\u81f3\u5341\u6210\u3002\u4f46\u8fd9\u5c31\u66f4\u6fc0\u8d77\u4e86\u201c\u8840\u638c\u706b\u9f99\u201d\u7684\u6012\u706b\uff0c\u53ea\u89c1\u4ed6\u53cc\u76ee\u6012\u5f20\uff0c\u5927\u543c\u4e00 \u58f0\uff1a\u201c\u597d\u5c0f\u5b50\uff01\u518d\u63a5\u8001\u592b\u4e00\u638c\u8bd5\u8bd5\uff01\u201d \u3000\u3000\u201c\u8840\u638c\u706b\u9f99\u201d\u66b4\u543c\u58f0\u4e2d\uff0c\u53f3\u638c\u731b\u6536\uff0c\u5708\u7acb\u80f8\u524d\u7684\u5de6\u638c\u987a\u52bf\u63a8\u51fa\uff0c\u5df2\u8fd0\u81f3\u516b\u6210\uff0c\u63a8\u51fa\u7684\u638c \u98ce\u6bd4\u7b2c\u4e00\u6b21\u66f4\u89c1\u5f3a\u70c8\u3002 \u3000\u3000\u7ecf\u9a8c\u4e30\u5bcc\u7684\u8001\u6c5f\u6e56\uff0c\u4e0e\u4e0d\u660e\u5b9e\u529b\u7684\u8d66\u624b\u8fc7\u62db\uff0c\u521d\u4ea4\u624b\u65f6\uff0c\u591a\u534e\u4e0d\u613f\u7528\u51fa\u5168\u529b\uff0c\u800c\u662f\u5148\u4ee5 \u56db\u4e94\u6210\u529f\u529b\u8bd5\u8bd5\u654c\u624b\u7684\u5f3a\u5f31\uff0c\u7136\u540e\u518d\u9010\u6b21\u52a0\u5f3a\u52b2\u9053\u3002\u6240\u4ee5\u201c\u8840\u638c\u706b\u9f99\u201d\u7b2c\u4e8c\u638c\u5c31\u6bd4\u7b2c\u4e00\u638c\u7684 \u5a01\u529b\u5927\u591a\u4e86\uff01\u56e0\u4e3a\u8fd9\u6837\u624d\u80fd\u6108\u6218\u6108\u52c7\uff0c\u4e0d\u81f4\u6709\u201c\u4e00\u9f13\u80dc\uff0c\u4e8c\u9f13\u8870\uff0c\u4e09\u9f13\u7aed\u201d\u540e\u529b\u4e0d\u7ee7\u7684\u73b0 \u8c61\u3002 \u3000\u3000\u4f46\u5c55\u767d\u4e0d\u660e\u6b64\u7406\uff0c\u7b2c\u4e00\u638c\u4fbf\u65bd\u51fa\u4e86\u5168\u529b\uff0c\u7b49\u5230\u201c\u8840\u638c\u706b\u9f99\u201d\u7b2c\u4e8c\u638c\u4e0b\u6765\uff0c\u638c\u98ce\u6f6e\u6d8c\uff0c\u5a01 \u529b\u5927\u589e\uff0c\u4ed6\u53cd\u800c\u89c9\u5f97\u65e0\u529b\u53ef\u4f7f\u4e86\u3002\u4f46\u4ed6\u5b81\u6298\u4e0d\u5c48\u7684\u4e2a\u6027\uff0c\u4ecd\u7136\u4e0d\u613f\u8eb2\u907f\uff0c\u540c\u6837\u7528\u5de6\u638c\u5411\u6765\u52bf \u8fce\u53bb\u3002 \u3000\u3000\u201c\u7830\uff01\u201d\u53c8\u662f\u4e00\u58f0\u5927\u9707\uff0c\u4e24\u80a1\u5f3a\u52b2\u638c\u98ce\u649e\u5728\u4e00\u8d77\uff0c\u4f59\u529b\u56db\u5c04\uff0c\u56de\u65cb\u751f\u98ce\uff0c\u7adf\u901a\u5f97\u56f4\u7ad9\u5f97 \u8fd1\u7684\u5341\u6570\u9ad8\u624b\uff0c\u7ad9\u4e0d\u4f4f\u811a\uff0c\u7eb7\u7eb7\u540e\u9000\u2026 \u3000\u3000\u201c\u8840\u638c\u706b\u9f99\u201d\u9ad8\u5927\u8eab\u5f62\uff0c\u7eb9\u4e1d\u672b\u52a8\uff0c\u8fd9\u780d\u5c55\u767d\u5374\u88ab\u9707\u5f97\u540e\u9000\u4e86\u4e24\u6b65\uff0c\u800c\u4e14\u638c\u98ce\u70ed\u6d41\uff0c\u4f7f \u5c55\u767d\u5de6\u638c\u638c\u5fc3\u5982\u88ab\u706b\u70e7\uff0c\u5468\u8eab\u6c57\u6d41\u5982\u6d46\uff0c\u5934\u8111\u4e00\u6655\uff0c\u51e0\u4e4e\u7ffb\u8eab\u88c1\u5012\u2026\u2026 \u3000\u3000\u5c55\u767d\u786c\u63a5\u201c\u8840\u638c\u706b\u9f99\u201d\u4e24\u638c\uff0c\u867d\u7136\u5185\u8151\u5df2\u88ab\u201c\u8840\u638c\u706b\u9f99\u201d\u7eaf\u9633\u638c\u529f\u707c\u4f24\uff0c\u4f46\u4f9d\u7136\u5c79\u7acb\u5982 \u5c71\uff0c\u8fd9\u53ef\u628a\u56f4\u7ad9\u5728\u56db\u5468\u7684\u6570\u5341\u4e2a\u6b66\u6797\u9ad8\u624b\u5413\u5446\u4e86\uff0c\u7eb7\u7eb7\u548b\u820c\uff0c\u6697\u60f3\uff1a\u201c\u8fd9\u5c0f\u5b50\u771f\u4e0d\u7b80\u5355\uff0c\u7ade \u80fd\u786c\u63a5\u5a01\u9707\u82cf\u9c81\u7684\u8840\u638c\u706b\u9f99\u7684\u4e24\u638c\u2026\u2026\u201d \u3000\u3000\u4f46\u201c\u8840\u638c\u706b\u9f99\u201d\u672c\u4eba\u5374\u5df2\u4f53\u4f1a\u51fa\uff0c\u5c55\u767d\u540e\u529b\u4e0d\u7ee7\uff0c\u81ea\u5df1\u5927\u8bdd\u8bf4\u5230\u5934\u91cc\uff0c\u5982\u679c\u4e09\u638c\u4e0d\u80fd\u628a \u773c\u524d\u5c11\u5e74\u6253\u5012\uff0c\u90a3\u4e48\uff0c\u81ea\u5df1\u4fbf\u7b97\u683d\u4e86\u4e00\u4e2a\u8ddf\u5934\u3002\u73b0\u5728\u89c1\u5c55\u767d\u638c\u529b\u5df2\u7aed\uff0c\u66f4\u4e0d\u613f\u4f7f\u5c55\u767d\u6709\u8c03\u606f \u590d\u539f\u7684\u673a\u4f1a\uff0c\u7b2c\u4e09\u62ff\u63a8\u51fa\u4e4b\u540e\uff0c\u7d27\u63a5\u7740\u53c8\u662f\u4e00\u58f0\u5927\u559d\uff1a\u201c\u7b2c\u4e09\u638c\uff01\u5c0f\u5b50\uff01\u4f60\u7ed9\u6211\u5012\u4e0b\u5427\u2014\u4f60 \u2014\u2014\u201d \u3000\u3000\u201c\u8840\u638c\u706b\u9f99\u201d\u559d\u58f0\u4e2d\uff0c\u53cc\u638c\u4e00\u4ea4\uff0c\u8fd0\u96c6\u4e86\u5468\u8eab\u529f\u529b\uff0c\u2019\u4e24\u638c\u9f50\u51fa\uff0c\u731b\u5411\u5c55\u767d\u80f8\u524d\u649e\u53bb\u3002 \u3000\u3000\u8fd9\u624d\u770b\u51fa\u201c\u8840\u638c\u706b\u9f99\u201d\u638c\u529b\u4e4b\u5f3a\u6765\uff0c\u53ea\u89c1\u638c\u98ce\u5c71\u6d8c\uff0c\u9510\u5578\u7834\u7a7a\uff0c\u72b9\u5982\u6392\u5c71\u5012\u6d77\u822c\u5411\u5c55\u81ea \u80f8\u524d\u538b\u6765\u3002 \u3000\u3000\u638c\u98ce\u672a\u81f3\uff0c\u5c55\u81ea\u5df2\u80fd\u611f\u5230\u70ed\u98ce\u6251\u9762\uff0c\u8fde\u547c\u5438\u90fd\u611f\u56f0\u96be\uff0c\u5c55\u767d\u81ea\u77e5\u8fd9\u7b2c\u4e09\u638c\u518d\u4e5f\u4e0d\u80fd\u63a5 \u4f4f\uff0c\u53ef\u662f\u4ed6\u5929\u751f\u50b2\u9aa8\uff0c\u660e\u77e5\u4e0d\u654c\uff0c\u4ecd\u4e0d\u613f\u5728\u4eba\u524d\u793a\u5f31\uff0c\u7adf\u7136\u529b\u8d2f\u53cc\u638c\uff0c\u540c\u6837\u7684\u53cc\u638c\u5e73\u80f8\u63a8 \u51fa\u2026\u2026 \u3000\u3000\u201c\u8f70\uff01\u201d\u4e00\u58f0\u5de8\u54cd\uff0c\u201c\u6bd5\u535c\uff01\u6bd5\u535c\xb7\uff0e\u2026\u201d\u9547\u8ddd\u79bb\u4e94\u5c3a\u4ee5\u5916\u7684\u7a97\u7eb8\u90fd\u88ab\u638c\u98ce\u4f59\u529b\u9707\u788e\uff0c \u90a3\u9152\u5e97\u7a97\u4e0a\u7cca\u7684\u662f\u9ad8\u7b49\u7ef5\u7eb8\uff0c\u5e76\u6cab\u4ee5\u6850\u6cb9\uff0c\u5c31\u662f\u72c2\u98ce\u66b4\u96e8\u90fd\u4e0d\u80fd\u628a\u8fd9\u7a97\u7eb8\u6253\u574f\uff0c\u800c\u7adf\u88ab\u4e24\u4eba \u5bf9\u5b9e\u7684\u638c\u98ce\u9707\u788e\uff0c\u7531\u6b64\u53ef\u89c1\u4e8c\u4eba\u638c\u98ce\u4e4b\u5f3a\uff0c \u3000\u3000\u8def\u4e0a\u7684\u5c18\u571f\uff0c\u88ab\u638c\u98ce\u6fc0\u8d77\u4e00\u56e2\u98de\u5c18\uff0c\u5347\u7a7a\u8db3\u6709\u4e24\u4e09\u4e08\u9ad8\uff0c\u4f7f\u4f17\u4eba\u89c6\u7ebf\u4e00\u65f6\u770b\u4e0d\u6e05\u573a\u4e2d\u4e8c \u4eba\u7684\u771f\u5b9e\u60c5\u51b5\u3002 \u3000\u3000\u5239\u65f6\uff0c\u98ce\u4f4f\u5c18\u6536\uff0c\u4f17\u4eba\u624d\u770b\u6e05\u695a\uff0c\u201c\u8840\u638c\u706b\u9f99\u201d\u4e0e\u5c11\u5e74\u5c55\u767d\uff0c\u4e8c\u4eba\u4ecd\u7136\u9762\u5bf9\u9762\u7684\u7ad9\u7acb\uff0c \u8c01\u4e5f\u6ca1\u6709\u5012\u4e0b\u3002 \u3000\u3000\u4f17\u4eba\u4e0d\u7531\u7eb7\u7136\u60ca\u60ee\u52a8\u5bb9\uff0c\u7adf\u7981\u4e0d\u4f4f\u4ea4\u76f8\u8c08\u8bba\uff1a\u201c\u8fd9\u5c11\u5e74\u7684\u6b66\u529f\u662f\u600e\u4e48\u7ec3\u7684\uff1f\u2026\u2026\u201d \u3000\u3000\u201c\u7adf\u80fd\u786c\u63a5\u4f4f\u59da\u8001\u82f1\u96c4\u7684\u4e09\u638c\uff01\u2026\u2026\u201d \u3000\u3000\u4f17\u4eba\u5403\u60ca\uff0c\u662f\u5728\u6c5f\u6e56\u9053\u4e0a\u4ece\u672a\u89c1\u8fc7\u5982\u6b64\u5f3a\u731b\u638c\u98ce\u3002\u800c\u4e14\u201c\u8840\u638c\u706b\u9f99\u201d\u662f\u6210\u540d\u5341\u4f59\u5e74\u7684\u6b66 \u6797\u9ad8\u624b\uff0c\u5bf9\u65b9\u5374\u4ec5\u662f\u4e00\u4e2a\u540d\u4e0d\u89c1\u7ecf\u4f20\u7684\u5c11\u5e74\u2026\u2026 \u3000\u3000\u4f46\u4ed6\u518d\u7559\u795e\u4e00\u7ec6\u770b\uff0c\u624d\u770b\u51fa\u5c11\u5e74\u5c55\u81ea\u795e\u60c5\u6709\u5f02\u3002\u53ea\u89c1\u5c55\u767d\u9762\u7ea2\u5982\u706b\uff0c\u53cc\u76ee\u795e\u6b64\u6da3\u6563\uff0c\u867d \u7136\u4ecd\u5728\u53cc\u773c\u819b\u89c6\u7740\u81ea\u5df1\uff0c\u53ef\u662f\uff0c\u773c\u795e\u4e2d\u5374\u4f3c\u5df2\u5931\u53bb\u4e86\u77e5\u89c9\u3002\u201c\u8840\u638c\u706b\u9f99\u201d\u731b\u7136\u9192\u609f\uff0c\u8fd9\u5c11\u5e74 \u4eba\u5df2\u88ab\u81ea\u5df1\u638c\u529b\u9707\u4f24\u5185\u8155\u4e0a\u4e14\u5df2\u5931\u53bb\u77e5\u89c9\uff0c\u4ed6\u8eab\u5f62\u4e0d\u5012\u7684\u539f\u56e0\uff0c\u53ef\u80fd\u662f\u5c11\u5e74\u4eba\u8eab\u540e\u4e08\u4f59\u5904\u6709 \u4e00\u9053\u5bbd\u539a\u7684\u5f71\u58c1\u6f14\uff0c\u81ea\u5df1\u6253\u51fa\u638c\u98ce\u751a\u731b\uff0c\u98ce\u529b\u53ca\u5899\u53cd\u5f39\u800c\u56de\uff0c\u5f97\u5230\u4e00\u4e2a\u5de7\u5999\u7684\u5e73\u8861\uff0c\u628a\u5c11\u5e74 \u7684\u8eab\u5f62\u7a33\u4f4f\u4e86\uff0c\u6240\u4ee5\u624d\u4e0d\u5012\u2026\u2026 \u3000\u3000\u201c\u5582\uff01\u5c0f\u4f19\u5b50\uff01\u8001\u592b\u8fd9\u4e09\u638c\u7684\u6ecb\u5473\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u5c55\u767d\u832b\u7acb\u5982\u6545\uff0c\u4e0d\u8a00\u4e0d\u52a8\u3002 \u3000\u3000\u201c\u54c8\u54c8\u54c8\uff01\u201d\u8840\u638c\u706b\u9f99\u4ef0\u5929\u4e00\u9635\u5927\u7b11\uff0c\u795e\u60c5\u5f97\u610f\u5df2\u6781\u3002\u8bf4\u9053\uff1a\u201c\u60f3\u5fc5\u4f60\u5c0f\u5b50\u4e5f\u56de\u7b54\u4e0d\u51fa \u8001\u592b\u7684\u95ee\u8bdd\u6765\u4e86\uff0c\u5947\u602a\u7684\u662f\uff0c\u4f60\u5c0f\u5b50\u53c8\u4e0d\u662f\u4ec0\u4e48\u5fe0\u81e3\u4e49\u58eb\uff0c\u4e3a\u4ec0\u4e48\u6b7b\u5c38\u4e0d\u5012\uff1f\u201d \u3000\u3000\u201c\u8840\u638c\u706b\u9f99\u201d\u5634\u91cc\u8bf4\u7740\u8bdd\uff0c\u8eab\u5f62\u5411\u524d\u75be\u5c04\uff0c\u500f\u4f38\u4e00\u6307\uff0c\u731b\u5411\u5c55\u767d\u201c\u7709\u5fc3\u201d\u91cd\u7a74\u6233\u53bb\uff01 \u3000\u3000\u4ed6\u6b64\u4e3e\u6709\u4e24\u4e2a\u7528\u610f\uff1a\u5982\u679c\u5c55\u767d\u5df2\u6b7b\uff0c\u8fd9\u6b7b\u5c38\u4e0d\u5012\u603b\u4e0d\u50cf\u8bdd\uff0c\u4ed6\u60f3\u4e00\u6307\u628a\u5c55\u767d\u6233\u5012\uff0c\u4e5f\u597d \u53eb\u624b\u4e0b\u4e3a\u5c55\u767d\u6536\u5c38\uff1b\u5982\u679c\u5c55\u767d\u672b\u6b7b\uff0c\u53ea\u662f\u5185\u8151\u53d7\u4f24\uff0c\u90a3\u4e48\uff0c\u4ed6\u8fd9\u4e00\u6307\uff0c\u4e5f\u53ef\u4ee5\u8981\u4e86\u5c55\u767d\u7684 \u547d\u3002 \u3000\u3000\u8981\u77e5\u201c\u8840\u638c\u706b\u9f99\u201d\u59da\u70b3\u6606\uff0c\u5fc3\u72e0\u624b\u8fa3\u662f\u6c5f\u6e56\u9053\u4e0a\u51fa\u4e86\u540d\u7684\u8fa3\uff0c\u4e0e\u654c\u4eba\u8fc7\u62db\uff0c\u4ece\u4e0d\u7559\u6d3b \u53e3\uff0c\u4e00\u5b9a\u628a\u654c\u4eba\u5236\u4e8e\u6b7b\u5730\u800c\u540e\u5df2\u3002\u5bf9\u6b64\u4ed6\u81ea\u5df2\u4e5f\u6709\u4e2a\u8bf4\u6cd5\uff0c\u6240\u8c13\u201c\u5bf9\u654c\u4eba\u4ec1\u6148\uff0c\u5c31\u662f\u5bf9\u81ea\u5df1 \u6b8b\u5fcd\u201d\u3002\u53c8\u9053\u662f\u201c\u65a9\u8349\u4e0d\u9664\u6839\uff0c\u6625\u98ce\u5439\u53c8\u751f\u201d\u3002 \u3000\u3000\u4e0d\u4e0e\u4eba\u4e3a\u654c\u4fbf\u7f62\uff0c\u5982\u4e0e\u4eba\u4e3a\u654c\u52a8\u624b\uff0c\u4e0d\u628a\u654c\u4eba\u6740\u6b7b\u51b3\u4e0d\u7f62\u4f11\uff0c\u4e0d\u80fd\u4e3a\u4e00\u65f6\u4ec1\u6148\uff0c\u7ed9\u81ea\u5df2 \u7559\u4e0b\u540e\u60a3\u3002 \u3000\u3000\u53ef\u662f\uff0c\u5f53\u4ed6\u7eb5\u8d77\u8eab\u5f62\uff0c\u4f38\u51fa\u624b\u6307\u5c1a\u672a\u89e6\u53ca\u5c55\u81ea\u7709\u5fc3\u4e4b\u9645\uff0c\u7a81\u542c\u4e00\u58f0\u5a07\u53f1\uff1a\u201c\u8001\u9b3c\uff01\u5c14 \u6562\uff1f\u201d \u3000\u3000\u5a07\u53f1\u58f0\u4e2d\uff0c\u4e00\u7f15\u75be\u98ce\u7834\u7a7a\uff0c\u4e00\u6761\u9ed1\u5f71\u731b\u5411\u201c\u8840\u638c\u706b\u9f99\u201d\u4f38\u51fa\u53f3\u81c2\u7684\u201c\u9634\u90fd\u201d\u91cd\u7a74\u6253\u5230\uff0c \u3000\u3000\u201c\u8840\u638c\u706b\u9f99\u201d\u827a\u4e1a\u679c\u7136\u4e0d\u51e1\uff0c\u53d8\u751f\u8098\u814b\uff0c\u7adf\u80fd\u4e34\u5371\u4e0d\u4e71\uff0c\u524d\u6251\u8eab\u5f62\uff0c\u5019\u7136\u51cc\u7a7a\u4e00\u6298\uff0c\u7528 \u4e86\u4e00\u4e2a\u201c\u4e91\u91cc\u7ffb\u201d\u7684\u8eab\u6cd5\uff0c\u786c\u628a\u524d\u6251\u7684\u8eab\u5f62\u7ed9\u5012\u63d0\u4e86\u56de\u8def\u534a\u7a7a\u4e2d\u7ffb\u4e86\u4e00\u4e2a\u7b4b\u6597\uff0c\u53cc\u811a\u53c8\u843d\u56de \u539f\u6765\u7ad9\u7acb\u4e4b\u5904 \u3000\u3000\u201c\u5403\u2014\u2014\u54ce\u54df\u4e00\u4e00\u5657\u201d\u4e00\u8fde\u4e32\u7684\u54cd\u58f0\u8fc7\u540e\uff0c\u5fae\u98ce\u60ca\u7136\uff0c\u4f17\u4eba\u773c\u524d\u4e00\u82b1\uff0c\u5728\u201c\u8840\u6500\u706b\u9f99\u201d \u4e0e\u5c11\u5e74\u5c55\u767d\u5f53\u4e2d\uff0c\u5df2\u7ad9\u5b9a\u4e00\u4e2a\u5a07\u7f8e\u65e0\u6bd4\u7684\u9526\u8863\u5c11\u5973\u3002 \u3000\u3000\u521a\u624d\u90a3\u4e00\u8fde\u4e32\u7684\u54cd\u58f0\uff0c\u5374\u662f\u4e00\u6761\u4e09\u5c3a\u957f\u7684\u6b67\u76ae\u9a6c\u97ad\uff0c\u90a3\u9a6c\u97ad\u7531\u5c11\u5973\u624b\u4e2d\u6296\u624b\u63b7\u51fa\uff0c\u539f\u662f \u731b\u88ad\u201c\u8840\u638c\u706b\u9f99\u201d\u7684\u53f3\u8155\uff0c\u4ee5\u6551\u63f4\u5c11\u5e74\u5c55\u767d\u7684\u3002\u88ab\u201c\u8840\u638c\u706b\u9f99\u201d\u534a\u7a7a\u6298\u8f6c\u7684\u5de7\u5999\u8eab\u6cd5\u8eb2\u8fc7\uff0c \u201c\u55e4\u201d\u7684\u4e00\u58f0\uff0c\u9a6c\u97ad\u7a7f\u8fc7\u201c\u94c1\u724c\u548c\u5c1a\u201d\u7684\u8033\u6735\uff0c\u201c\u94c1\u724c\u548c\u5c1a\u201d\u86ee\u529b\u4e0d\u5c0f\uff0c\u5374\u662f\u4e2a\u7c97\u4eba\uff0c\u6b63\u5728 \u76f4\u7709\u77aa\u773c\u5730\u770b\u201c\u8840\u638c\u706b\u9f99\u201d\u4e0e\u5c11\u5e74\u5c55\u767d\u4e09\u5bf9\u638c\uff0c\u5ffd\u89c9\u8033\u6735\u4e00\u75db\uff0c\u5fd9\u7528\u624b\u53bb\u6478\uff0c\u8033\u6735\u5df2\u5c11\u4e86\u534a \u4e2a\uff0c\u5374\u6478\u4e86\u4e00\u624b\u8840\uff0c\u4e0d\u7531\u201c\u54ce\u54df\u201d\u60ca\u53eb\u8d77\u6765\u3002 \u3000\u3000\u201c\u5657\uff01\u201d\u9a6c\u97ad\u53c8\u8d2f\u8fdb\u5899\u58c1\u5185\uff0c\u8db3\u6709\u4e09\u3001\u56db\u5bf8\u6df1\uff0c\u4e8c\u5c3a\u4f59\u957f\u7684\u97ad\u634e\u8fd9\u624d\u52bf\u5c3d\u5782\u843d\u4e0b\u6765\uff0c\u7ade \u6302\u5728\u5899\u4e0a\u7a7a\u81ea\u6765\u56de\u6446\u52a8\u3002\u8fd9\u7a81\u7136\u73b0\u8eab\u7684\u9526\u8863\u5c11\u5973\uff0c\u8155\u52b2\u4e4b\u5927\u4ee4\u4eba\u6628\u820c\uff0c\u60f3\u90a3\u86df\u76ae\u9a6c\u97ad\u4e43\u662f\u67d4 \u8f6f\u4e4b\u7269\uff0c\u7531\u5c11\u5973\u624b\u4e2d\u6296\u624b\u63b7\u51fa\uff0c\u5374\u80fd\u6296\u5f97\u7b14\u76f4\uff0c\u72b9\u5982\u5229\u954a\u5c11\u822c\uff0c\u6253\u4f24\u4e86\u4e00\u4eba\uff0c\u4ecd\u80fd\u5c04\u8fdb\u575a\u786c \u7684\u5899\u58c1\u5185\uff0c\u53ef\u89c1\u8fd9\u9526\u8863\u5c11\u5973\u8155\u52b2\u4e4b\u5927\uff0c\u6b66\u529f\u4e4b\u9ad8\u3002 \u3000\u3000\u4f17\u4eba\u4e0d\u7531\u9707\u60ca\u5f97\u77aa\u5927\u4e86\u773c\u775b\uff0c\u4e00\u9f50\u8f6c\u5934\u6253\u91cf\u8fd9\u9526\u8863\u5c11\u5973\u3002 \u3000\u3000\u53ea\u89c1\u5979\u660e\u7741\u7693\u9f7f\uff0c\u80a4\u767d\u4f3c\u96ea\uff0c\u4f53\u6001\u5a07\u5c0f\u73b2\u73d1\uff0c\u7a7f\u4e00\u88ad\u4e91\u9526\u7ef8\u8863\uff0c\u5934\u4e0a\u4e91\u9b03\u9ad8\u633d\uff0c\u5e74\u7eaa\u4e5f \u5c31\u662f\u5341\u516d\u3001\u4e03\u5c81\uff0c\u795e\u60c5\u5a07\u7f8e\u4fcf\u4e3d\u5df2\u6781\uff0c\u4f46\u5a07\u4fcf\u4e4b\u4e2d\uff0c\u5374\u53c8\u6d41\u9732\u51fa\u4e00\u79cd\u6e05\u96c5\u9ad8\u8d35\u4e4b\u6c14\uff0c\u771f\u53ef\u8bf4 \u662f\u201c\u6bd4\u82b1\u82b1\u89e3\u8bed\uff0c\u6bd4\u7389\u7389\u751f\u9999\u201d\u3002\u5c24\u5176\u5979\u8138\u4e0a\u5c1a\u6709\u4e09\u5206\u7a1a\u6c14\uff0c\u770b\u6765\u662f\u7eaf\u6d01\u5929\u771f\uff0c\u61a8\u6001\u53ef\u638f\u3002 \u5c31\u662f\u751f\u6c14\uff0c\u5634\u89d2\u4ecd\u4f3c\u63a5\u7740\u7b11\u610f\uff0c\u6837\u5b50\u662f\u53ef\u7231\u5df2\u6781\uff0c \u3000\u3000\u201c\u4f60\u662f\u8c01\u5bb6\u7684\u91ce\u4e2b\u5934\uff1f\u201d\u8840\u638c\u706b\u9f99\u770b\u6e05\u6765\u4eba\u4ec5\u662f\u4e00\u4e2a\u5a07\u7f8e\u5c11\u5973\uff0c\u7adf\u628a\u81ea\u5df1\u95f9\u4e86\u4e2a\u624b\u5fd9\u811a \u4e71\uff0c\u800c\u4e14\u8fd8\u4f24\u4e86\u81ea\u5df1\u4e00\u4e2a\u5c5e\u4e0b\u9ad8\u624b\uff0c\u4e0d\u7531\u8138\u5b54\u4e00\u6c89\uff0c\u6012\u9053\uff1a\u201c\u7adf\u6562\u63d2\u624b\u8ddf\u6211\u67b6\u8fd9\u4e2a\u6881\u5b50\uff1f\u201d \u3000\u3000\u201c\u7ea2\u8138\u8001\u5934\uff01\u4f60\u5148\u522b\u795e\u6c14\uff01\u201d\u9526\u8863\u5c11\u5973\u4e00\u624b\u63d2\u8170\uff0c\u4e00\u624b\u6307\u7740\u201c\u8840\u638c\u706b\u9f99\u201d\u7684\u9f3b\u5b50\uff0c\u8bf4 \u9053\uff1a\u201c\u6211\u5148\u95ee\u4f60\uff0c\u4f60\u8fd9\u4e48\u5927\u5e74\u7eaa\uff0c\u8bf4\u8fc7\u7684\u8bdd\u7b97\u4e0d\u7b97\u6570\uff1f\u201d \u3000\u3000\u201c\u54fc\uff01\u201d\u8840\u638c\u706b\u9f99\u51b7\u54fc\u4e00\u58f0\u8bf4\u9053\uff1a\u201c\u65e0\u4eba\u6559\u517b\u7684\u91ce\u4e2b\u5934\uff01\u4f60\u6562\u5728\u8001\u592b\u9762\u524d\u80e1\u8a00\u4e71\u8bed\uff0c\u4e0d \u77e5\u5c0a\u656c\u957f\u4e0a\uff0c\u522b\u602a\u6211\u4e00\u638c\u5288\u4e86\u4f60\uff01\u201d \u3000\u3000\u201c\u54fc\uff01\u8bf4\u8bdd\u4e0d\u7b97\u6570\u7684\u7cdf\u8001\u5934\uff0c\u201d\u9526\u8863\u5c11\u5973\u7adf\u6a21\u4eff\u201c\u8840\u638c\u706b\u9f99\u201d\u7684\u53e3\u543b\u8bf4\u9053\uff1a\u201c\u522b\u8bf4\u4f60\u4e00 \u638c\u5288\u4e0d\u4e86\u6211\uff0c\u751a\u81f3\u4f60\u8fde\u6211\u7684\u4e00\u638c\u90fd\u63a5\u4e0d\u4e0b\uff0c\u4f60\u5982\u679c\u80fd\u63a5\u4f4f\u59d1\u5a18\u7684\u4e00\u638c\uff0c\u5357\u4e03\u5317\u516d\u5341\u4e09\u7701\uff0c\u4efb \u4f60\u8d70\u4e86\uff0c\u51b3\u4e0d\u4f1a\u6709\u4eba\u62e6\u963b\u4f60\u2026\u2026\u201d \u3000\u3000\u201c\u4f4f\u53e3\uff01\u2026\u2026\u201d\u8840\u638c\u706b\u9f99\u4e00\u542c\uff0c\u9526\u8863\u5c11\u5973\u5b8c\u5168\u662f\u6a21\u4eff\u81ea\u5df1\u8bf4\u8bdd\uff0c\u4e0d\u7531\u6012\u6c14\u4e0a\u51b2\uff0c\u5927\u559d\u4e00 \u58f0\uff0c\u5c31\u8981\u4e0a\u524d\u51fa\u624b\u2026\u2026 \u3000\u3000\u201c\u5927\u80c6\u5c0f\u5a62\uff01\u4f60\u628a\u4f5b\u7237\u7684\u8033\u6735\u6253\u7834\u4e86\uff0c\u4f5b\u7237\u8981\u4f60\u62ff\u547d\u8d54\u507f\uff01\u201d \u3000\u3000\u201c\u94c1\u724c\u548c\u5c1a\u201d\u66b4\u543c\u4e00\u58f0\uff0c\u5355\u81c2\u62a1\u8d77\u5343\u65a4\u91cd\u7684\u94c1\u724c\uff0c\u8fce\u5934\u5411\u9526\u8863\u5c11\u5973\u5934\u9876\u538b\u4e0b\u3002 \u3000\u3000\u9526\u8863\u5c11\u5973\u7ad9\u5728\u90a3\u91cc\uff0c\u5634\u89d2\u542b\u7b11\uff0c\u5bf9\u201c\u94c1\u724c\u548c\u5c1a\u201d\u91cd\u903e\u5343\u65a4\u7684\u94c1\u724c\uff0c\u6cf0\u5c71\u538b\u9876\u822c\u8fce\u5934\u538b \u4e0b\uff0c\u72b9\u5982\u672a\u89c1\uff0c\u76f4\u5230\u94c1\u724c\u79bb\u5979\u5934\u9876\u4e0d\u53ca\u4e8c\u5bf8\uff0c\u5979\u5ffd\u7136\u8eab\u5f62\u4e00\u77ee\uff0c\u7adf\u4e0d\u77e5\u7528\u4e86\u4e2a\u4ec0\u4e48\u8eab\u6cd5\uff0c\u4ece \u94c1\u724c\u4e4b\u4e0b\u94bb\u51fa\uff0c\u51cc\u7a7a\u4e00\u7ffb\uff0c\u4e00\u4e2a\u201c\u9e5e\u5b50\u7ffb\u8eab\u201d\uff0c\u53cc\u811a\u7ade\u7ad9\u5728\u94c1\u724c\u4e4b\u4e0a\uff0c\u8eab\u5f62\u8f7b\u7075\u8885\u5a1c\u5df2\u6781\uff0c \u5e76\u4e14\u53e3\u4e2d\u8bf4\u9053\uff1a\u201c\u4f60\u8fd9\u7b28\u548c\u5c1a\uff01\u6ca1\u6709\u5175\u5668\u6253\u4ed7\uff0c\u5c31\u628a\u5e99\u91cc\u7684\u95e8\u677f\u642c\u51fa\u6765\u4e86\uff01\u201d \u3000\u3000\u201c\u94c1\u724c\u548c\u5c1a\u201d\u6c14\u5f97\u54c7\u54c7\u602a\u53eb\uff0c\u4e00\u8fb9\u628a\u4e00\u5757\u94c1\u724c\u821e\u5f97\u98ce\u8f66\u4f3c\u7684\u4e71\u8f6c\uff0c\u60f3\u628a\u9526\u8863\u5c11\u5973\u629b\u4e0b\u5730 \u6765\u3002 \u3000\u3000\u90a3\u9526\u8863\u5c11\u5973\u98de\u3001\u817e\u3001\u8df3\u3001\u8dc3\uff0c\u7adf\u5728\u94c1\u724c\u4e0a\u8df3\u8d77\u821e\u6765\u4e86\uff0c\u4e00\u8fb9\u5634\u91cc\u563b\u563b\u54c8\u54c8\u7b11\u9053\uff1a\u597d\u73a9\uff01 \u771f\u597d\u73a9\uff01\u2026\u2026\u201d \u3000\u3000\u8fd9\u5149\u666f\u5012\u771f\u597d\u770b\uff0c\u8857\u4e0a\u706f\u706b\u901a\u660e\uff0c\u4e00\u4e2a\u7c97\u5927\u548c\u5c1a\u624b\u821e\u95e8\u677f\u4f3c\u7684\u94c1\u724c\uff0c\u94c1\u724c\u4e0a\u4e00\u4e2a\u9526\u8863\u5a07 \u7f8e\u5c11\u5973\uff0c\u5728\u98ce\u8f66\u4f3c\u7684\u94c1\u724c\u4e0a\u98de\u817e\u8df3\u8dc3\uff0c\u4e00\u8fb9\u8fd8\u563b\u563b\u54c8\u54c8\u7684\u8bf4\u7b11\uff0c\u4e0d\u77e5\u9053\u7684\u4eba\u8fd8\u4ee5\u4e3a\u8fd9\u662f\u8dd1\u6c5f \u6e56\u7684\uff0c\u8868\u6f14\u4ec0\u4e48\u7279\u6280\u8282\u76ee\u5462\uff01 \u3000\u3000\u8fd9\u65f6\uff0c\u4e0d\u4f46\u201c\u8840\u638c\u706b\u9f99\u201d\u4e32\u9886\u6765\u7684\u6570\u5341\u540d\u52b2\u88c5\u5927\u6c49\uff0c\u5728\u56db\u5468\u56f4\u770b\uff0c\u5f88\u591a\u5c45\u6c11\u3001\u884c\u4eba\u539f\u5148 \u770b\u5230\u6c5f\u6e56\u4e0a\u6b66\u6797\u4eba\u7269\u4ec7\u6740\uff0c\u90fd\u8eb2\u5f97\u8fdc\u8fdc\u7684\uff0c\u73b0\u5728\u770b\u5230\u8fd9\u79cd\u60c5\u5f62\uff0c\u5012\u4e5f\u90fd\u8dd1\u4e86\u6765\uff0c\u8fdc\u8fdc\u7ad9\u7740\u770b \u70ed\u95f9\u2026\u2026 \u3000\u3000\u201c\u94c1\u724c\u548c\u5c1a\u201d\u867d\u7136\u662f\u4e2a\u6d51\u4eba\uff0c\u4f46\u6253\u4e86\u534a\u5929\u4ecd\u4e0d\u80fd\u628a\u9526\u8863\u5c11\u5973\u6253\u4e0b\u94c1\u724c\uff0c\u5fc3\u91cc\u4e5f\u8f6c\u8fc7\u5f2f\u6765 \u4e86\uff0c\u77e5\u9053\u8fd9\u6837\u4e0d\u80fd\u628a\u5c11\u5973\u7529\u8131\uff0c\u7adf\u7136\u4e00\u624b\u633d\u5b9a\u94c1\u724c\uff0c\u7a7a\u51fa\u4e00\u53ea\u624b\u6765\uff0c\u628a\u6597\u5927\u7684\u4e00\u4e2a\u62f3\u5934\uff0c\u5411 \u9526\u8863\u5c11\u5973\u5c0f\u8179\u4e0a\u6363\u53bb\uff0c\u5634\u4e2d\u5e76\u53eb\u9a82\u9053\uff1a\u201c\u4f60\u5976\u5976\u4e2a\u718a\uff01\u4f60\u5012\u62ff\u7740\u4f5b\u7237\u597d\u800d\u5b50\uff0c\u4e0b\u53bb\uff01\u201d \u3000\u3000\u5bf9\u4e00\u4e2a\u9752\u6625\u5c11\u5973\u6765\u8bf4\uff0c\u201c\u94c1\u724c\u548c\u5c1a\u201d\u8fd9\u4e00\u62db\u4f7f\u5f97\u6709\u70b9\u4e0b\u6d41\u3002\u9526\u8863\u5c11\u5973\u7c89\u8138\u2014\u7ea2\uff0c\u4e5f\u4e0d\u7b11 \u4e86\u3002\u201c\u5f20\u98de\u9a97\u9a6c\u201d\uff0c\u4e00\u62ac\u817f\u8eb2\u8fc7\u548c\u5c1a\u88ad\u5411\u5c0f\u8179\u7684\u4e00\u62f3\uff0c\u8ddf\u7740\u7528\u4e86\u4e2a\u201c\u5343\u65a4\u5760\u201d\uff0c\u5c0f\u811a\u5c16\u7528\u529b \u4e00\u70b9\u94c1\u724c\uff0c\u4eba\u5374\u817e\u7a7a\u7ffb\u843d\u4e8c\u4e08\u5f00\u5916\u3002 \u3000\u3000\u8fd9\u4e00\u4e0b\u201c\u94c1\u724c\u548c\u5c1a\u201d\u597d\u770b\u4e86\uff0c\u5355\u624b\u633d\u5b9a\u94c1\u724c\uff0c\u88ab\u9526\u8863\u5c11\u5973\u7528\u529b\u4e00\u8e29\uff0c\u628a\u63e1\u4e0d\u7262\uff0c\u94c1\u724c \u201c\u545b\u54e8\u201d\u6389\u843d\u5730\u4e0a\uff0c\u6b63\u597d\u7838\u5728\u81ea\u5df1\u811a\u4e0a\uff0c\u94c1\u724c\u672c\u5c31\u91cd\uff0c\u53c8\u88ab\u9526\u8863\u5c11\u5973\u811a\u6cd5\u91cd\u529b\u4e00\u8e29\uff0c\u867d\u7136\u9694 \u7740\u591a\u8033\u50e7\u978b\uff0c\u4e5f\u628a\u201c\u94c1\u724c\u548c\u5c1a\u201d\u53cc\u811a\u5341\u8dbe\u7838\u788e\uff0c\u75db\u5f97\u548c\u5c1a\u5f2f\u4e0b\u8170\u53bb\uff0c\u55f7\u55f7\u602a\u5578\u2026\u2026 \u3000\u3000\u9526\u8863\u5c11\u5973\u5374\u5df2\u4ece\u5bb9\u843d\u5728\u201c\u8840\u638c\u706b\u9f99\u201d\u59da\u70b3\u6606\u9762\u524d\uff0c\u7528\u624b\u4e00\u63a0\u6563\u4e71\u7684\u9b13\u53d1\uff0c\u8bf4\u9053\uff1a\u7ea2\u8138\u8001 \u5934\uff0c\u770b\u6837\u5b50\u4f60\u5728\u6c5f\u6e56\u4e0a\u4e5f\u662f\u6709\u540d\u6709\u59d3\u7684\u4eba\u7269\uff0c\u521a\u624d\u8ddf\u4eba\u5bb6\u5c55\u5c0f\u4fa0\u8bf4\u7684\u8bdd\uff0c\u8fd8\u7b97\u4e0d\u7b97\u6570\uff1f\u201d \u3000\u3000\u201c\u8840\u638c\u706b\u9f99\u201d\u770b\u5230\u9526\u8863\u5c11\u5973\u620f\u5f04\u201c\u94c1\u724c\u548c\u5c1a\u201d\u7684\u8eab\u6cd5\u6b65\u6cd5\uff0c\u7adf\u4f3c\u201c\u8e22\u7a7a\u5e7b\u5f71\u201d\uff0c\u7a81\u7136\u60f3 \u8d77\u6b66\u4f53\u4e2d\u4e00\u5927\u8c6a\u95e8\u6765\uff0c\u77e5\u9053\u8fd9\u79cd\u201c\u8e22\u7a7a\u5e7b\u5f71\u201d\u8eab\u6cd5\uff0c\u662f\u90a3\u8c6a\u95e8\u4e2d\u4e0d\u4f20\u4e4b\u79d8\uff0c\u8fd9\u9526\u8863\u5c11\u5973\u65e2\u7136 \u4f1a\u8fd9\u79cd\u8eab\u6cd5\uff0c\u5fc5\u4e0e\u90a3\u4e00\u5927\u8c6a\u95e8\u6709\u7740\u5bc6\u5207\u5173\u7cfb\uff0c\u4e0d\u8981\u8bf4\u81ea\u5df1\u201c\u8840\u638c\u706b\u9f99\u201d\uff0c\u5c31\u662f\u81ea\u5df1\u7684\u5c45\u505c\u4e3b \u4eba\u201c\u5b89\u4e50\u516c\u5b50\u201d\u4e5f\u60f9\u4e0d\u8d77\u4eba\u5bb6\uff0c\u4e8e\u662f\u628a\u4e00\u4efd\u72c2\u50b2\u3001\u6ee1\u8154\u6012\u706b\uff0c\u7edf\u7edf\u6536\u8d77\uff0c\u53e6\u6362\u4e86\u4e00\u526f\u8138\uff0c\u548c \u989c\u60a6\u8272\u5730\u7b11\u9053\uff1a\u201c\u59d1\u5a18\uff01\u53ea\u8981\u4f60\u77e5\u9053\u8001\u592b\u5728\u6c5f\u6e56\u4e0a\u662f\u6709\u5730\u4f4d\u7684\u4eba\u5c31\u884c\u4e86\u3002\u4f60\u4e14\u8bf4\u8bf4\u770b\uff0c\u6211\u8bf4 \u9053\u7684\u4ec0\u4e48\u8bdd\u8bf4\u4e86\u4e0d\u7b97\u6570\uff1f\u201d \u3000\u3000\u201c\u8840\u638c\u706b\u9f99\u201d\u8001\u597d\u5de8\u6ed1\uff0c\u867d\u7136\u5fc3\u4e2d\u5df2\u51c6\u5907\u4e70\u8fd9\u9526\u8863\u5c11\u5973\u7684\u5e10\uff0c\u4f46\u5634\u91cc\u8fd8\u5728\u81ea\u62ac\u8eab\u4ef7\u3002 \u3000\u3000\u201c\u54fc\uff01\u201d\u9526\u8863\u5c11\u5973\u7476\u9f3b\u4e00\u8038\uff0c\u8bf4\u9053\uff1a\u201c\u7cdf\u8001\u5934\uff0c\u4f60\u4e0d\u7528\u5f80\u81ea\u5df1\u8138\u4e0a\u8d34\u91d1\uff01\u4e5f\u7528\u4e0d\u7740\u88c5 \u50bb\uff01\u65b9\u624d\u4f60\u8ddf\u5c55\u5c0f\u4fa0\u6bd4\u6b66\u8d4c\u7ea6\uff0c\u8bf4\u5c55\u5c0f\u4fa0\u63a5\u4f4f\u4f60\u4e09\u638c\uff0c\u82cf\u3001\u9c81\u5883\u5185\u4efb\u4eba\u5bb6\u8d70\uff0c\u51b3\u4e0d\u518d\u6709\u4eba\u62e6 \u963b\uff0c\u5982\u4eca\uff0c\u4eba\u5bb6\u5c55\u5c0f\u4fa0\u8fde\u63a5\u4e86\u4f60\u4e09\u638c\uff0c\u73b0\u5728\u8be5\u600e\u4e48\u8bf4\uff1f\u201d \u3000\u3000\u201c\u8840\u638c\u706b\u9f99\u201d\u54c8\u54c8\u4e00\u7b11\u8bf4\u9053\uff1a\u201c\u5408\u7740\u6211\u4e0e\u8fd9\u4f4d\u5c0f\u54e5\u7684\u8bdd\uff0c\u59d1\u5a18\u5168\u542c\u5230\u4e86\u3002\u90a3\u4e48\uff0c\u5c31\u542c\u59d1 \u5a18\u4e00\u53e5\u8bdd\uff0c\u653e\u8fd9\u4f4d\u5c0f\u54e5\u8d70\u8def\u5c31\u662f\uff01\u201d \u3000\u3000\u201c\u8fd9\u624d\u50cf\u8bdd\uff01\u201d\u9526\u8863\u5c11\u5973\u8bf4\uff1a\u201c\u90a3\u5c31\u8bf7\u4f60\u7684\u4eba\u8ba9\u8def\u5427\uff01\u201d\u94f6\u8863\u5c11\u5973\u8bf4\u8457\u64ae\u5507\u5439\u4e86\u4e00\u58f0\u53e3 \u54e8\uff0c\u8e44\u58f0\u5f97\u5f97\uff0c\u4ece\u5708\u5916\u8dd1\u8fdb\u4e00\u5339\u67a3\u7ea2\u8272\u5927\u9a6c\u6765\uff0c\u8fd9\u67a3\u7ea2\u8272\u5927\u9a6c\u795e\u9a8f\u975e\u5e38\uff0c\u5230\u4e86\u9526\u8863\u5c11\u5973\u8eab \u8fb9\uff0c\u7528\u90a3\u957f\u5634\uff0c\u5728\u5c11\u5973\u8eab\u4e0a\u4e00\u9635\u63c9\u64e6\uff0c\u72b6\u6781\u4eb2\u70ed\u3002 \u3000\u3000\u6570\u5341\u52b2\u88c5\u5927\u6c49\u4e4b\u4e2d\uff0c\u6709\u4e0d\u5c11\u597d\u8272\u4e4b\u5f92\uff0c\u89c1\u9526\u8863\u5c11\u5973\u7f8e\u903e\u5929\u4eba\uff0c\u5a07\u61a8\u5929\u771f\uff0c\u5374\u53c8\u51f6\u6a2a\u9738\u9053 \u65e0\u6bd4\uff0c\u8fde\u4ed6\u4eec\u7684\u5934\u513f\u201c\u8840\u638c\u706b\u9f99\u201d\u90fd\u4e0d\u5f97\u4e0d\u4e70\u4eba\u5bb6\u7684\u8d26\uff0c\u5fc3\u4e2d\u867d\u6709\u975e\u5206\u4e4b\u60f3\uff0c\u4f46\u4e5f\u4e0d\u6562\u4e0a\u524d \u4e00\u4eb2\u52b3\u6cfd\uff0c\u81ea\u8ba8\u82e6\u5403\u3002\u5982\u4eca\uff0c\u89c1\u8fd9\u5339\u67a3\u7ea2\u8272\u9a8f\u9a6c\uff0c\u4eb2\u70ed\uff0c\u5927\u6709\u201c\u4eba\u4e0d\u5982\u9a6c\u201d\u4e4b\u53f9\u2026\u2026 \u3000\u3000\u9526\u8863\u5c11\u5973\u7528\u767d\u7389\u4f3c\u7684\u7d20\u624b\uff0c\u62cd\u4e86\u62cd\u9a6c\u7684\u8116\u5b50\uff0c\u7136\u540e\u98de\u8eab\u63a0\u81f3\u5899\u8fb9\uff0c\u628a\u63d2\u8fdb\u5899\u5185\u7684\u9a6c\u97ad\u53d6 \u4e0b\uff0c\u53c8\u53cd\u8eab\u8dc3\u56de\u9a6c\u8fb9\uff0c\u8fd9\u4e00\u5f80\u4e00\u8fd4\uff0c\u6765\u53bb\u5982\u7535\uff0c\u5374\u672a\u89c1\u5979\u811a\u8e29\u7740\u5730\uff0c\u4e5f\u672b\u89c1\u5979\u624b\u6307\u89e6\u5899\uff0c\u5149 \u8fd9\u4efd\u8f7b\u529f\u5c31\u770b\u5f97\u4f17\u591a\u6b66\u6797\u8c6a\u5ba2\uff0c\u81ea\u53f9\u4e0d\u5982\u3002\u9526\u8863\u5c11\u5973\u5728\u6570\u5341\u9053\u773c\u5149\u6ce8\u89c6\u4e4b\u4e0b\uff0c\u4ece\u4ece\u5bb9\u5bb9\uff0c\u6beb \u65e0\u5c40\u4fc3\u4e0d\u5b89\u4e4b\u6001\uff0c\u53d6\u56de\u9a6c\u97ad\uff0c\u62c9\u9a6c\u8d70\u5230\u5c55\u767d\u8eab\u65c1\uff0c\u89c1\u5c55\u767d\u4ecd\u662f\u832b\u7136\u7ad9\u5728\u90a3\u91cc\uff0c\u8138\u7ea2\u4f3c\u706b\uff0c\u53cc \u76ee\u75f4\u5446\uff0c\u4e0d\u7531\u773c\u4e2d\u6d41\u9732\u51fa\u4e00\u79cd\u75db\u60dc\u795e\u60c5\uff0c\u67d4\u58f0\u8bf4\u9053\uff1a\u5c55\u54e5\u54e5\uff01\u4f60\u8d1f\u4f24\u4e86\uff1f\u201d\u5c55\u767d\u4e0d\u8a00\u4e0d\u52a8\u3002 \u3000\u3000\u201c\u5c55\u54e5\u54e5\uff0c\u4f60\u4f24\u5f97\u5f88\u91cd\u5417\uff1f\u4e3a\u4ec0\u4e48\u4e0d\u8bf4\u8bdd\uff1f\u201d\u5c55\u767d\u4f9d\u7136\u4e0d\u8a00\u4e2d\u52a8\uff0c\u800c\u4e14\u8fde\u773c\u73e0\u4e5f\u6ca1\u6709\u8f6c \u52a8\u4e00\u4e0b\u3002 \u3000\u3000\u9526\u8863\u5c11\u5973\u89c1\u6b64\u60c5\u5f62\uff0c\u773c\u5708\u4e00\u7ea2\uff0c\u7ade\u6389\u4e0b\u51e0\u6ef4\u73cd\u73e0\u822c\u7684\u6cea\u73e0\u6765\u3002\u53c8\u6068\u58f0\u8bf4\u9053\uff1a\u201c\u54fc\uff01\u4e00\u5b9a \u662f\u8fd9\u7cdf\u8001\u5934\u628a\u4f60\u6253\u4f24\u4e86\uff01\u7b49\u59b9\u59b9\u628a\u4f60\u9001\u56de\u5bb6\u53bb\u517b\u4f24\uff0c\u56de\u6765\u59b9\u59b9\u627e\u8fd9\u7cdf\u8001\u5934\u6765\u7ed9\u54e5\u54e5\u62a5\u4ec7\uff01\u201d \u3000\u3000\u8bf4\u7740\u8fd8\u56de\u5934\u72e0\u72e0\u77aa\u4e86\u201c\u8840\u638c\u706b\u9f99\u201d\u4e00\u773c\u3002\u7136\u540e\u98de\u8eab\u4e0a\u9a6c\uff0c\u5355\u624b\u4e00\u63d0\uff0c\u8f7b\u8f7b\u5730\u4fbf\u628a\u5c55\u767d\u63d0 \u5230\u9a6c\u978d\u4e4b\u4e0a\u3002\u9526\u8863\u5c11\u5973\u4e00\u624b\u62b1\u4f4f\u5c55\u767d\uff0c\u4e00\u624b\u626c\u97ad\u5fae\u8d70\u2026\u2026\u201c\u59d1\u5a18\uff0c\u6162\u8d70\uff01\u201d\u8840\u638c\u706b\u9f99\u8de8\u524d\u4e00 \u6b65\uff0c\u5634\u4e2d\u53eb\u9053\u3002 \u3000\u3000\u201c\u600e\u4e48\uff1f\u201d\u9526\u8863\u5c11\u5973\u6ee1\u8138\u4e0d\u9ad8\u5174\uff0c\u7ecd\u76b1\u86fe\u7709\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u7cdf\u8001\u5934\u5b50\uff01\u4f60\u53c8\u53cd\u6094\u4e86\u201d \u3000\u3000\u201c\u8840\u638c\u706b\u9f99\u201d\u82e6\u7b11\u4e00\u58f0\u8bf4\u9053\uff1a\u201c\u59cb\u5a18\uff0c\u4f60\u8fd9\u7cdf\u8001\u5934\u7cdf\u8001\u5934\u7684\u4e71\u53eb\uff0c\u662f\u4e0d\u662f\u5bf9\u957f\u8005\u4e0d \u656c\uff1f\u2026\u2026\u201d \u3000\u3000\u201c\u8fd8\u6709\u4ec0\u4e48\u8bf4\u7684\u6ca1\u6709\uff1f\u201d\u9526\u8863\u5c11\u5973\u5728\u9a6c\u4e0a\u6253\u65ad\u8840\u638c\u706b\u9f99\u7684\u8bdd\uff0c\u72b6\u9887\u4e0d\u8010\u3002 \u3000\u3000\u201c\u5927\u80c6\u5c0f\u5a62\uff01\u4f60\u4e0d\u8981\u5f97\u5bf8\u8fdb\u5c3a\uff01\u201d\u7389\u9762\u54ea\u5412\u5b8b\u5c0f\u98de\u89c1\u5e08\u7236\u5927\u53cd\u5e38\u6001\uff0c\u4ed6\u65e9\u5df2\u770b\u4e0d\u60ef\uff01\u53ea \u662f\u788d\u7740\u5e08\u7236\u7684\u989c\u9762\uff0c\u6ca1\u6709\u51fa\u9762\u63d2\u5634\uff0c\u5982\u4eca\uff0c\u89c1\u9526\u8863\u5c11\u5973\u8981\u8d70\u4e86\uff0c\u5bf9\u81ea\u5df1\u5e08\u7236\u66f4\u662f\u4e0d\u656c\uff0c\u4e0d\u7531 \u6012\u6c14\u4e0a\u51b2\uff0c\u8de8\u524d\u4e00\u6b65\uff0c\u53eb\u9a82\u9053\uff1a\u201c\u5c0f\u7237\u4eca\u5929\u8981\u2026\u2026\u201d \u3000\u3000\u201c\u98de\u513f\uff0c\u4f60\u4e0d\u8981\u63d2\u5634\uff01\u201d\u201c\u8840\u638c\u706b\u9f99\u201d\u5236\u6b62\u66b4\u8df3\u5982\u96f7\u7684\u5b8b\u5c0f\u98de\uff0c\u53c8\u5bf9\u9526\u8863\u5c11\u5973\u8bf4\u9053\uff1a\u59d1 \u5a18\uff0c\u6211\u662f\u770b\u5728\u4f60\u5bb6\u957f\u7684\u4efd\u4e0a\uff0c\u4f60\u65e2\u7136\u4e0d\u613f\u542c\uff0c\u6211\u4e5f\u4e0d\u591a\u8bf4\uff0c\u8fd9\u5c55\u59d3\u5c11\u5e74\uff0c\u4e2d\u4e86\u8001\u592b\u7684\u2018\u7ea2\u7802 \u8840\u5f62\u638c\u2019\uff0c\u5982\u65e0\u89e3\u836f\uff0c\u4e0d\u51fa\u4e09\u5929\uff0c\u5fc5\u4e94\u5185\u67af\u7126\u9762\u6b7b\uff0c\u73b0\u4e0b\u8001\u592b\u597d\u4eba\u505a\u5230\u5e95\uff0c\u5c31\u9001\u7ed9\u59d1\u5a18\u4e00\u7c92 \u89e3\u836f\uff0c\u56de\u53bb\u7ed9\u4ed6\u670d\u4e0b\uff0c\u9759\u517b\u6570\u65e5\u5373\u80fd\u75ca\u6108\uff01\u201d\u8bf4\u7f62\uff0c\u4ece\u6000\u4e2d\u638f\u51fa\u4e00\u4e2a\u7f8a\u8102\u5c0f\u74f6\uff0c\u53d6\u51fa\u4e00\u7c92\u6d45 \u7eff\u8272\u4e38\u836f\uff0c\u5c48\u6307\u4e00\u5f39\uff0c\u76f4\u5411\u9526\u8863\u5c11\u5973\u5c04\u53bb\uff01\u9526\u8863\u5c11\u5973\u5fae\u5fae\u4e00\u7b11\uff0c\u4f38\u51fa\u4e24\u53ea\u6625\u8471\u822c\u7684\u4e94\u6307\uff0c\u8f7b \u5de7\u5730\u628a\u98de\u5c04\u800c\u81f3\u7684\u4e00\u7c92\u836f\u4e38\u634f\u4f4f\u3002 \u3000\u3000\u522b\u770b\u8fd9\u662f\u4e00\u7c92\u836f\u4e38\uff0c\u5728\u201c\u8840\u638c\u706b\u9f99\u201d\u624b\u4e2d\u5f39\u51fa\uff0c\u52b2\u9053\u4e5f\u4e0d\u5c0f\u3002\u5c24\u5176\u836f\u4e38\u90a3\u4e48\u5c0f\uff0c\u98de\u5c04\u53c8 \u5feb\uff0c\u9526\u8863\u5c11\u5973\u80fd\u7528\u4e24\u6307\u628a\u98de\u5c04\u800c\u81f3\u7684\u836f\u4e38\u634f\u4f4f\uff0c\u90a3\u773c\u795e\u4e4b\u4f73\uff0c\u5185\u529f\u4e4b\u7eaf\uff0c\u624b\u6cd5\u4e4b\u5de7\uff0c\u62ff\u634f\u4e4b \u51c6\uff0c\u201c\u8840\u638c\u706b\u9f99\u201d\u8fd9\u624d\u7b97\u771f\u6253\u5fc3\u5e95\u4f69\u670d\u4e86\u4eba\u5bb6\uff0c\u6697\u6697\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u56de\u5934\u671b\u4e86\u671b\u81ea\u5df1\u7684\u5f92\u513f \u201c\u7389\u9762\u54ea\u5412\u201d\uff0c\u5b8b\u5c0f\u98de\u770b\u5230\u9526\u8863\u5c11\u5973\u8868\u73b0\u7684\u8fd9\u4e00\u624b\uff0c\u4e5f\u4e0d\u7981\u60ed\u6127\u5730\u4f4e\u4e0b\u5934\u53bb\u2026\u2026\u8c01\u77e5\u9526\u8863\u5c11 \u5973\u5728\u9a6c\u4e0a\u63a5\u4f4f\u836f\u4e38\uff0c\u653e\u5728\u638c\u5fc3\u770b\u4e86\u770b\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u600e\u4e48\u77e5\u9053\u4f60\u8fd9\u836f\u4e38\u662f\u771f\u662f\u5047\u5462\uff1f\u5047\u82e5\u4f60\u7ed9 \u6211\u7684\u662f\u4e00\u7c92\u6bd2\u836f\u5462\uff1f\u2026\u2026\u201d \u3000\u3000\u201c\u8840\u638c\u706b\u9f99\u201d\u5e76\u4e0d\u662f\u5ffd\u53d1\u6148\u5fc3\uff0c\u60f3\u6551\u56de\u5c55\u767d\u4e00\u547d\uff0c\u800c\u662f\u77e5\u9053\u9526\u8863\u5c11\u5973\u7684\u5bb6\u95e8\u52bf\u529b\u6d01\u5927\uff0c \u89c1\u8fd9\u9526\u8863\u5c11\u5973\u5bf9\u5c55\u767d\u90a3\u6837\u4eb2\u70ed\uff0c\u731c\u60f3\u5173\u7cfb\u5fc5\u4e0d\u5bfb\u5e38\uff0c\u5982\u82e5\u5c55\u767d\u4e0d\u6551\uff0c\u90a3\u9526\u8863\u5c11\u5973\u5fc5\u5b9a\u8981\u627e\u81ea \u5df1\u62a5\u4ec7\uff0c\u56e0\u6b64\u624d\u987a\u6c34\u63a8\u821f\uff0c\u505a\u4e2a\u4eba\u60c5\uff0c\u6545\u793a\u6069\u60e0\uff0c\u65e5\u540e\u9526\u8863\u5c11\u5973\u5c31\u4e0d\u4f1a\u627e\u81ea\u5df1\u5bfb\u4ec7\u4e86\u3002\u8c01\u77e5 \u4ed6\u5982\u6b64\u59d4\u66f2\u6c42\u5168\uff0c\u9526\u8863\u5c11\u5973\u7ade\u8bf4\u51fa\u8fd9\u79cd\u6c14\u4eba\u7684\u8bdd\u6765\uff0c\u4e0d\u7531\u6b8b\u7709\u4f8b\u7ad6\uff0c\u51b7\u7b11\u4e00\u58f0\u8bf4\u9053\uff1a\u201c\u8001\u592b \u82e5\u4e0d\u662f\u8bda\u5fc3\u76f8\u6551\uff0c\u5c31\u662f\u4e0d\u7ed9\u4ed6\u6bd2\u836f\uff0c\u4ed6\u4e5f\u6d3b\u4e0d\u4e86\u2026\u2026\u201d \u201c\u8fd9\u6837\u8bf4\u6765\uff0c\u5012\u8981\u8c22\u8c22\u4f60\u4e86\uff0c\u5bf9\u4e0d\u5bf9\uff1f\u7cdf\u8001\u5934\uff01\u201d\u9526\u8863\u5c11\u5973\u5df2\u7ecf\u4fe1\u5f97\u8fc7\u201c\u8840\u638c\u706b\u9f99\u201d\u4ea4 \u51fa\u7684\u4e00\u5b9a\u662f\u89e3\u836f\uff0c\u4e0d\u7b49\u201c\u8840\u638c\u706b\u9f99\u201d\u628a\u8bdd\u8bf4\u5b8c\uff0c\u5df2\u626c\u97ad\u50ac\u9a6c\u8d70\u4e86\uff0c\u9a6c\u884c\u5982\u98ce\uff0c\u8f6c\u773c\u6d88\u5931\u5728\u8857 \u53e3\u9ed1\u6697\u4e4b\u4e2d\uff0c\u76f4\u628a\u201c\u8840\u638c\u706b\u9f99\u201d\u6c14\u5f97\u5439\u80e1\u77aa\u773c\uff0c\u5c24\u5176\u540e\u8fb9\u90a3\u4e00\u53e5\u201c\u7cdf\u8001\u5934\u201d\uff01 \u7b2c\u5341\u4e5d\u7ae0\u3000\u201c\u6b7b\u4eba\u5c45\u201d>> \u53e4\u9f99\u300a\u5251\u5ba2\u884c\u300b \u7b2c\u5341\u4e5d\u7ae0\u3000\u201c\u6b7b\u4eba\u5c45\u201d \u3000\u3000\u84dd\u5929\u767d\u4e91\uff0c\u7ea2\u65e5\u5f53\u5934\u3002 \u3000\u3000\u5b98\u9053\u4e0a\u6cfc\u524c\u524c\u8dd1\u6765\u4e00\u9a91\u67a3\u7ea2\u8272\u9a8f\u9a6c\u3002 \u3000\u3000\u9a6c\u4e0a\u9a91\u5750\u7740\u4e00\u4e2a\u5a07\u7f8e\u7684\u9526\u8863\u5c11\u5973\uff0c\u5c11\u5973\u6000\u4e2d\u62b1\u7740\u4e00\u4e2a\u660f\u8ff7\u4e0d\u9192\u7684\u5c11\u5e74\u3002\u8fd9\u5a07\u7f8e\u7684\u9526\u8863\u5c11 \u5973\uff0c\u5927\u767d\u5929\u5728\u9a6c\u4e0a\u62b1\u7740\u4e00\u4e2a\u5c11\u5e74\u7537\u5b50\uff0c\u6beb\u4e0d\u907f\u5acc\uff0c\u5438\u5f15\u5f97\u884c\u4eba\u4fa7\u76ee\u3002 \u3000\u3000\u4f46\uff0c\u8fd9\u5339\u9a8f\u9a6c\u8dd1\u5f97\u592a\u5feb\u4e86\uff0c\u8def\u4e0a\u884c\u4eba\u521a\u4e00\u6ce8\u76ee\uff0c\u67a3\u7ea2\u8272\u7684\u5927\u9a6c\u5374\u5982\u4e00\u9635\u98ce\u4f3c\u5730\u51b2\u8fc7\u53bb \u4e86\uff0c\u56db\u8e44\u6380\u52a8\uff0c\u53ea\u80fd\u770b\u5230\u9a6c\u540e\u626c\u8d77\u7684\u4e00\u7247\u98de\u5c18\u3002 \u3000\u3000\u9526\u8863\u5c11\u5973\u4e00\u8fb9\u6325\u97ad\u50ac\u9a6c\u98de\u9a70\uff0c\u4e00\u8fb9\u4e0d\u4f73\u4f4e\u5934\u770b\u987e\u6000\u4e2d\u5c11\u5e74\uff0c\u8138\u4e0a\u5145\u6ee1\u4e86\u5173\u5207\u601c\u7231\u4e4b\u60c5\uff0c \u5982\u679c\u8fd9\u5c11\u4e2d\u591c\u6e05\u9192\u65f6\uff0c\u80fd\u770b\u5230\u8fd9\u5a07\u7f8e\u7684\u5c11\u5973\uff0c\u5bf9\u4ed6\u5982\u6b64\u5173\u6000\u7231\u62a4\uff0c\u4ed6\u4e5f\u8bb8\u4f1a\u611f\u5230\u81ea\u5df2\u662f\u5929\u5e95 \u4e0b\u6700\u5e78\u798f\u7684\u4eba\u4e86\u3002 \u3000\u3000\u53ef\u662f\uff0c\u8fd9\u5c11\u5e74\u662f\u5728\u6655\u8ff7\u4e4b\u4e2d\uff0c\u9526\u8863\u5c11\u5973\u7684\u4e07\u7f15\u67d4\u60c5\uff0c\u4ed6\u61f5\u7136\u4e0d\u89c9\u3002\u800c\u4e14\uff0c\u770b\u4ed6\u8138\u7ea2\u4f3c \u706b\uff0c\u9f3b\u606f\u7c97\u91cd\uff0c\u80f8\u524d\u6025\u901f\u5730\u8d77\u4f0f\uff0c\u4fbf\u77e5\u4ed6\u751f\u547d\u5df2\u5230\u4e86\u5782\u6b7b\u8fb9\u7f18\u3002 \u3000\u3000\u9526\u8863\u5c11\u5973\u50ac\u9a6c\u6025\u9a70\uff0c\u770b\u5230\u6000\u4e2d\u5c11\u5e74\u6108\u6765\u6108\u4e0d\u884c\u4e86\uff0c\u8eab\u4e0a\u7684\u70ed\u5ea6\u9010\u6e10\u589e\u9ad8\uff0c\u6000\u4e2d\u5982\u62b1\u7740\u4e00 \u56e2\u70c8\u706b\u4e00\u822c\uff0c\u4e0d\u7531\u5fc3\u4e2d\u7126\u6025\u3002\u6108\u8d70\u6108\u4e0d\u653e\u5fc3\uff0c\u6700\u540e\u5979\u7ec8\u4e8e\u52d2\u9a6c\u505c\u4e86\u4e0b\u6765\uff0c\u5fc3\u4e2d\u5fd6\u9053\uff1a\u770b\u6837\u5b50 \u662f\u8d76\u4e0d\u56de\u5bb6\u4e2d\u4e86\uff0c\u4f46\u5728\u8fd9\u8352\u91ce\u4e4b\u4e2d\uff0c\u53c8\u5411\u54ea\u91cc\u53bb\u627e\u533b\u751f\u7ed9\u5c55\u54e5\u54e5\u770b\u75c5\u5462\uff01\u2026\u2026\u201d \u3000\u3000\u201c\u8be5\u6b7b\uff01\u201d\u9526\u8863\u5c11\u5973\u7ee7\u7eed\u5728\u5fc3\u4e2d\u6697\u60f3\u9053\uff1a\u201c\u5982\u679c\u628a\u5bb6\u4e2d\u7956\u4f20\u7684\u7597\u4f24\u5723\u836f\u2018\u5927\u8fd8\u4e39\u2019\u5e26\u5728 \u8eab\u4e0a\u51e0\u7c92\uff0c\u4e5f\u4e0d\u4f1a\u8fd9\u6837\u5954\u547d\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u54ce\u5440\uff01\u201d\u9526\u8863\u5c11\u5973\u60f3\u5230\u7956\u4f20\u5723\u836f\uff0c\u7a81\u7136\u60f3\u8d77\uff1a\u201c\u90a3\u7ea2\u8138\u7cdf\u8001\u5934\uff0c\u4e34\u884c\u65f6\u7ed9\u4e86\u81ea\u5df1\u4e00\u7c92 \u89e3\u836f\uff0c\u56e0\u81ea\u5df1\u592a\u76f8\u4fe1\u5bb6\u4e2d\u7956\u4f20\u7597\u4f24\u5723\u836f\u7684\u529f\u6548\uff0c\u53cd\u800c\u672a\u628a\u90a3\u7c92\u89e3\u836f\u7ed9\u5c55\u54e5\u54e5\u5403\u4e0b\u89c4\u5728\u4f55\u4e0d\u5148 \u628a\u90a3\u7c92\u89e3\u836f\u7ed9\u5c55\u54e5\u54e5\u670d\u4e0b\uff0c\u5148\u6551\u4f4f\u5c55\u54e5\u54e5\u7684\u6027\u547d\uff01\u2026\u2026\u201d\u5979\u60f3\u5230\u8fd9\u91cc\u4e0d\u7531\u60ca\u547c\u51fa\u58f0\uff0c\u6697\u9a82\u81ea \u5df2\u7cca\u6d82\uff0c\u4e00\u8fb9\u53c8\u95ea\u8d77\u4e24\u53ea\u526a\u6c34\u53cc\u77b3\uff0c\u56db\u5916\u6253\u91cf\uff0c\u5979\u60f3\u770b\u770b\u9644\u8fd1\u6709\u6ca1\u6709\u4eba\u5bb6\uff0c\u53bb\u8ba8\u4e00\u676f\u5f00\u6c34\uff0c \u7ed9\u5c55\u54e5\u54e5\u670d\u89e3\u836f\u3002 \u3000\u3000\u4f46\u56db\u91ce\u832b\u832b\uff0c\u9664\u4e86\u8fde\u5929\u52b3\u8349\u53ca\u519c\u5bb6\u8015\u8018\u7684\u7530\u5784\u4e4b\u5916\uff0c\u56db\u5916\u4e0d\u89c1\u4e00\u4e2a\u4eba\u5bb6\u3002 \u3000\u3000\u8f6c\u773c\u770b\u5230\u5de6\u4fa7\u7ea6\u4e94\u516d\u91cc\u5916\u6709\u2014\u2014\u5ea7\u9ad8\u5c97\uff0c\u9526\u8863\u5c11\u5973\u867d\u7136\u5f88\u5c11\u51fa\u8fdc\u95e8\uff0c\u5728\u5bb6\u4e2d\u53c8\u662f\u5a07\u751f\u60ef \u517b\u7684\u5343\u91d1\u5c0f\u59d0\uff0c\u4f46\u5fc3\u773c\u5374\u73b2\u6591\u5254\u900f\uff0c\u806a\u6167\u65e0\u6bd4\uff0c\u60f3\u5230\u90a3\u9ad8\u5c97\u4e0a\u53bb\uff0c\u770b\u5230\u4f55\u5904\u6709\u4eba\u5bb6\u518d\u9a91\u9a6c\u8d76 \u53bb\uff0c\u603b\u6bd4\u8fd9\u6837\u76f2\u76ee\u524d\u884c\u597d\u5f97\u591a\u3002\u4e8e\u662f\uff0c\u7acb\u523b\u62c9\u8f6c\u9a6c\u5934\u8d8a\u91ce\u671d\u90a3\u9ad8\u5c97\u4e0a\u8dd1\u53bb\u3002 \u3000\u3000\u9526\u8863\u5c11\u5973\u9a91\u7684\u8fd9\u5339\u67a3\u7ea2\u8272\u5927\u9a6c\uff0c\u4e43\u662f\u5343\u91cc\u540d\u9a79\uff0c\u79f0\u4e3a\u201c\u8d64\u54d7\u9a9d\u201d\u811a\u7a0b\u98de\u5feb\uff0c\u867d\u662f\u8d8a\u91ce\u800c \u8d70\uff0c\u4e94\u516d\u91cc\u7684\u8def\u7a0b\u6643\u773c\u5373\u5230\u3002\u5c11\u5973\u7eb5\u9a6c\u9ad8\u5c97\uff0c\u8f6e\u76ee\u56db\u671b\uff0c\u89c1\u9ad8\u5c97\u80cc\u540e\u9752\u5c71\u7fe0\u8c37\uff0c\u534a\u5c71\u8170\u91cc\u7eff \u836b\u63a9\u6620\uff0c\u9732\u51fa\u4e00\u89d2\u7ea2\u697c\uff0c\u867d\u7136\u90a3\u4e00\u89d2\u7ea2\u697c\u8ddd\u79bb\u9ad8\u5c97\u5c1a\u6709\u5341\u6570\u91cc\u4e4b\u9065\uff0c\u4f46\u9664\u6b64\u4e4b\u5916\uff0c\u832b\u832b\u56db \u91ce\uff0c\u518d\u770b\u4e0d\u5230\u4e00\u4e2a\u4eba\u5bb6\u4e86\uff0c\u53ea\u597d\u50ac\u9a6c\u4e0a\u5c71\uff0c\u76f4\u671d\u90a3\u4e00\u89d2\u7ea2\u697c\u8dd1\u53bb\u3002 \u3000\u3000\u201c\u8d64\u9a85\u9a9d\u201d\u771f\u662f\u4e00\u5339\u5b9d\u9a6c\uff0c\u4e0d\u4f46\u5728\u5e73\u5730\u8dd1\u8d77\u6765\u98de\u5feb\uff0c\u5c31\u662f\u5728\u5d0e\u5c96\u5c71\u5f84\u4e0a\u4e5f\u5954\u9a70\u5982\u98de\uff0c\u7a9c \u5c71\u8d8a\u810a\uff0c\u8f6c\u773c\u8dd1\u5230\u90a3\u4e00\u89d2\u7ea2\u697c\u4e4b\u5904\u3002 \u3000\u3000\u9526\u8863\u5c11\u5973\u4ece\u9a6c\u4e0a\u628a\u660f\u8ff7\u4e2d\u7684\u5c11\u5e74\u62b1\u4e86\u4e0b\u6765\uff0c\u8d70\u8fd1\u90a3\u5ea7\u5efa\u7b51\u95e8\u524d\u4e00\u770b\uff0c\u4e0d\u7531\u695e\u4e86\u3002 \u3000\u3000\u8fd9\u5ea7\u5efa\u7b51\u5947\u7279\u4e4b\u6781\uff0c\u7ea2\u8272\u5706\u9876\uff0c\u81ea\u77f3\u56f4\u5899\uff0c\u8fde\u6728\u95e8\u4e5f\u662f\u767d\u7684\uff0c\u5728\u767d\u6728\u6a2a\u6881\u4e0a\u7528\u58a8\u7b14\u5199\u4e86 \u4e09\u4e2a\u5927\u5b87\uff1a\u201c\u6b7b\u4eba\u5c45\u3002\u201d \u3000\u3000\u9526\u8863\u5c11\u5973\u6b66\u529f\u751a\u9ad8\uff0c\u5374\u5f88\u5c11\u5728\u6c5f\u6e56\u4e0a\u8d70\u52a8\uff0c\u54ea\u91cc\u89c1\u8fc7\u50cf\u8fd9\u7b49\u5947\u7279\u7684\u5730\u65b9\uff01\u770b\u8fd9\u5ea7\u5efa\u7b51\u80cc \u5c71\u800c\u5efa\uff0c\u5e99\u5b87\u4e0d\u50cf\u5e99\u5b87\uff0c\u575f\u5893\u4e0d\u50cf\u575f\u5893\uff0c\u5374\u5728\u95e8\u6881\u4e0a\u9898\u7740\u201c\u6b7b\u4eba\u5c45\u201d\u4e09\u4e2a\u5927\u5b57\uff0c\u4f7f\u8fd9\u5c11\u5973\u4e00 \u65f6\u4e4b\u95f4\u6014\u5728\u95e8\u524d\uff0c\u771f\u662f\u8fdb\u4e5f\u4e0d\u662f\uff0c\u9000\u4e5f\u4e0d\u662f\uff0c\u53ea\u7741\u5927\u4e86\u4e00\u53cc\u7f8e\u4e3d\u7684\u5927\u773c\u775b\u3002\u5446\u5446\u53d1\u6014\u3002 \u3000\u3000\u5c31\u7b97\u5979\u662f\u51b0\u96ea\u806a\u660e\uff01\u8fd9\u773c\u524d\u602a\u5f02\u7684\u5730\u65b9\uff0c\u4e5f\u628a\u5979\u806a\u660e\u7684\u5c0f\u5934\u8111\u5f04\u7cca\u6d82\u4e86\u3002 \u3000\u3000\u60f3\u4e16\u754c\u4e0a\u54ea\u6709\u8fd9\u6837\u602a\u7684\u5730\u65b9\uff1f\u8c01\u4f1a\u5728\u81ea\u5df1\u5bb6\u95e8\u9898\u4e0a\u201c\u6b7b\u4eba\u5c45\u201d\u7684\u540d\u5802\u8fd9\u6837\u5199\u5427\uff1f \u3000\u3000\u53ef\u662f\uff0c\u4e8b\u5b9e\u6446\u5728\u773c\u524d\uff0c\u4e16\u4e0a\u5c31\u6709\u8fd9\u79cd\u602a\u4e8b\u60c5\u3002\u53ef\u8bf4\u662f\u201c\u4e16\u754c\u4e4b\u5927\uff0c\u65e0\u5947\u4e0d\u6709\u201d\u4e86\u3002 \u3000\u3000\u5979\u6b63\u5728\u8e2f\u8e85\u4e0d\u524d\u7684\u5f53\u513f\uff0c\u5ffd\u7136\u6000\u4e2d\u7684\u5c11\u5e74\u5468\u8eab\u6296\u4e86\u4e00\u4e0b\uff0c\u660f\u8ff7\u4e2d\u4e24\u9053\u957f\u7709\u76b1\u8d77\uff0c\u72b6\u751a\u75db \u82e6\u2026\u2026 \u3000\u3000\u8fd9\u4e00\u6765\uff0c\u628a\u5c11\u5973\u4ece\u8ff7\u60af\u4e2d\u60ca\u9192\u8fc7\u6765\uff0c\u4ed4\u7ec6\u4e00\u60f3\uff0c\u6551\u4eba\u8981\u7d27\uff0c\u7ba1\u4f60\u662f\u6d3b\u4eba\u5c45\u6b7b\u4eba\u5c45\uff0c\u5148\u8fdb \u53bb\u770b\u770b\u518d\u8bf4\u3002 \u3000\u3000\u7231\u7684\u529b\u91cf\uff0c\u771f\u662f\u4f1f\u5927\u3002\u9526\u8863\u5c11\u5973\u5343\u91d1\u4e4b\u8eaf\uff0c\u5374\u4e3a\u4e86\u5fc3\u7231\u7684\u5c55\u59d3\u5c11\u5e74\uff0c\u7ade\u628a\u5bb3\u6015\u548c\u5371\u9669\u90fd \u7f6e\u4e8e\u4e5d\u9704\u4e91\u5916\uff0c\u5979\u7adf\u6562\u5b64\u8eab\u72ec\u95ef\u201c\u6b7b\u4eba\u5c45\u201d\u3002 \u3000\u3000\u53ea\u89c1\u5979\u62bf\u7d27\u5634\u5507\uff0c\u2014\u8138\u575a\u6bc5\u4e4b\u8272\uff0c\u8fde\u9a6c\u4e5f\u6ca1\u6709\u62f4\uff0c\u53ea\u4efb\u9a6c\u513f\u5728\u5c71\u8fb9\u5403\u8349\uff0c\u5979\u62b1\u8d77\u660f\u8ff7\u4e2d \u7684\u5c55\u59d3\u5c11\u5e74\uff0c\u8d70\u5230\u524d\u9762\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u6709\u4eba\u5417\uff1f\u201d \u3000\u3000\u5979\u8fde\u53eb\u4e86\u6570\u58f0\uff0c\u7a7a\u5c71\u56de\u97f3\uff0c\u54ea\u91cc\u6709\u4eba\u7b54\u5e94\uff01\u5979\u9f13\u8d77\u52c7\u6c14\uff0c\u7528\u811a\u5c16\u4e00\u70b9\uff0c\u4e24\u6247\u767d\u6728\u95e8\u5440\u7136 \u800c\u5f00\u3002\uff0c \u3000\u3000\u5f00\u95e8\u5904\u7adf\u662f\u4e00\u4e2a\u5c0f\u5c0f\u7684\u5ead\u9662\uff0c\u9662\u4e2d\u82b1\u6728\u6276\u758f\uff0c\u7adf\u7136\u5e7d\u96c5\u975e\u5e38\uff0c\u53ea\u662f\u5bc2\u9759\u65e0\u58f0\uff0c\u9690\u7136\u6709\u4e00 \u79cd\u9634\u68ee\u6050\u6016\u4e4b\u611f\u3002\u2018 \u3000\u3000\u9662\u843d\u5f53\u4e2d\uff0c\u4e00\u6761\u767d\u77f3\u96e8\u9053\uff0c\u7b14\u76f4\u5730\u76f4\u8fbe\u90a3\u5ea7\u7ea2\u9876\u5c0f\u697c\u2019\u5c31\u628a\u5b83\u53eb\u505a\u697c\u5427)\uff0c\u5c0f\u697c\u95e8\u7a97\u7d27 \u95ed\uff0c\u91cc\u8fb9\u4f3c\u4e4e\u5173\u95ed\u7740\u65e0\u6570\u7684\u795e\u79d8\u3002 \u3000\u3000\u53ef\u662f\uff0c\u5c0f\u59d1\u5a18\u80c6\u6bd4\u5929\u5927\uff0c\u7adf\u7136\u6000\u62b1\u660f\u8ff7\u5c11\u5e74\uff0c\u4e00\u6b65\u4e00\u6b65\u5730\u8d70\u5411\u795e\u79d8\u6050\u6016\u7684\u5c0f\u697c\u3002 \u3000\u3000\u8d70\u5230\u697c\u95e8\u524d\uff0c\u697c\u95e8\u4e5f\u662f\u767d\u6728\u505a\u6210\uff0c\u672a\u52a0\u6cb9\u6f06\uff0c\u767d\u6728\u95e8\u4e0a\u8d6b\u7136\u53c8\u7528\u58a8\u7b14\u5199\u7740\u201c\u6b7b\u4eba\u4e4b\u5c45\uff0c \u6d3b\u4eba\u514d\u8fdb\u201d\u516b\u4e2a\u5927\u5b57\u3002 \u3000\u3000\u5c0f\u59d1\u5a18\u4e00\u54ac\u7259\uff0c\u626c\u58f0\u53eb\u9053\uff1a\u201c\u6b7b\u4eba\uff01\u6709\u6d3b\u4eba\u6765\u8bbf\u3002\u201d \u3000\u3000\u201c\u560e\u4e00\u560e\u4e00\u560e\u201d\uff0c\u4e00\u53ea\u602a\u9e1f\uff0c\u201c\u5495\uff01\u5495\uff01\u201d\u53eb\u4e86\u4e24\u58f0\uff0c\u4ece\u697c\u9876\u98de\u8d77\uff0c\u5c55\u7fc5\u6251\u5411\u5c71\u540e\u6d53\u836b \u53bb\u4e86\u3002 \u3000\u3000\u5c0f\u59d1\u5a18\u4e00\u9897\u82b3\u5fc3\uff0c\u88ab\u5413\u5f97\u51e0\u4e4e\u4ece\u5589\u5499\u91cc\u8dc3\u51fa\u8154\u5916\u6765\uff1b\u4f46\u8fc7\u4e86\u4e00\u4f1a\u513f\uff0c\u89c1\u4ecd\u7136\u6773\u65e0\u4eba\u58f0\uff0c \u5c0f\u59d1\u5a18\u53c8\u58ee\u4e86\u58ee\u80c6\u5b50\uff0c\u7528\u811a\u5c16\u70b9\u5f00\u697c\u95e8\u3002 \u3000\u3000\u697c\u95e8\u4e5f\u662f\u672a\u6813\u672a\u9501\uff0c\u5c0f\u59d1\u5a18\u811a\u5c16\u70b9\u5904\uff0c\u5440\u7136\u800c\u5f00\uff0c\u8fce\u9762\u7adf\u662f\u4e00\u4e2a\u5bbd\u655e\u5c0f\u5385\u3002 \u3000\u3000\u5c0f\u5385\u5185\u9648\u8bbe\u7b80\u5355\uff0c\u4f46\u5374\u6253\u626b\u5f97\u7ea4\u5c18\u4e0d\u67d3\uff0c\u4e0d\u7528\u95ee\uff0c\u8fd9\u201c\u6b7b\u4eba\u201d\u5fc5\u4e5f\u662f\u624b\u811a\u591f\u52e4\u7684\u4e86\u3002 \u3000\u3000\u9526\u8863\u5c11\u5973\u7684\u5fc3\u63d0\u5230\u55d3\u5b50\u773c\u91cc\uff0c\u4e00\u8fb9\u5168\u795e\u6212\u5907\uff0c\u4e00\u8fb9\u4e24\u53ea\u5927\u773c\u775b\u7741\u5f97\u633a\u5927\uff0c\u6ef4\u6e9c\u6e9c\u56db\u5904\u6253 \u91cf\u3002 \u3000\u3000\u53ea\u89c1\u8fce\u95e8\u4e00\u957f\u767d\u6728\u6761\u6848\uff0c\u4e00\u5f20\u81ea\u6728\u65b9\u684c\uff0c\u767d\u6728\u65b9\u684c\u4e24\u8fb9\u5404\u653e\u7740\u4e00\u5f20\u6905\u5b50\uff0c\u4e5f\u662f\u767d\u6728\u505a \u6210\u3002\u597d\u50cf\u8fd9\u4e9b\u95e8\u3001\u7a97\u3001\u684c\u3001\u6905\u90fd\u662f\u7528\u5c71\u4e0a\u6811\u6728\u505a\u7684\uff0c\u4e0d\u52a0\u6cb9\u6f06\uff0c\u4ee5\u4fdd\u6301\u6728\u6750\u7684\u539f\u72b6\uff0c\u4f7f\u623f\u4e2d \u7684\u7a7a\u6c14\u90fd\u5145\u6ea2\u7740\u6811\u8102\u7684\u9999\u5473\u3002\u4f46\u8fd9\u5c31\u66f4\u5bb9\u6613\u4f7f\u4eba\u8054\u60f3\u5230\u68fa\u6750\uff0c\u56e0\u4e3a\u68fa\u6750\u5e97\u91cc\u5c31\u662f\u5145\u6ea2\u7740\u8fd9\u79cd \u5473\u9053\u3002 \u3000\u3000\u9526\u8863\u5c11\u5973\u56db\u5904\u5bdf\u770b\u4e86\u534a\u5929\uff0c\u89c1\u6ca1\u6709\u4ec0\u4e48\u5f02\u72b6\uff0c\u8fd9\u624d\u628a\u660f\u8ff7\u4e2d\u7684\u5c11\u5e74\u653e\u5728\u767d\u6728\u6905\u4e0a\uff0c\u56e0\u4e3a \u5979\u62b1\u4e86\u534a\u5929\uff0c\u867d\u7136\u5979\u6b66\u529f\u751a\u9ad8\uff0c\u4e5f\u611f\u5230\u4e24\u81c2\u6709\u70b9\u9178\u9ebb\u4e86\u3002 \u3000\u3000\u5979\u653e\u4e0b\u660f\u8ff7\u4e2d\u7684\u5c11\u5e74\uff0c\u8ba9\u5c11\u5e74\u59d4\u5750\u5728\u6905\u5b50\u4e0a\uff0c\u7136\u540e\u957f\u5401\u4e86\u4e00\u53e3\u6c14\uff0c\u5fc3\u8bf4\uff1a\u201c\u8fd9\u9b3c\u5730\u65b9\uff01 \u540d\u53eb\u2018\u6b7b\u4eba\u5c45\u2019\uff0c\u8fde\u6b7b\u4eba\u4e5f\u6ca1\u6709\u770b\u5230\u4e00\u4e2a\u5462\uff01\u2026\u2026\u201d \u3000\u3000\u5979\u4e0d\u7531\u53c8\u56db\u5904\u6253\u91cf\uff0c\u89c1\u6b63\u4e2d\u5899\u58c1\u4e0a\u63a5\u7740\u5e03\u59d0\uff0c\u4ece\u53e3\u5916\u5439\u8fdb\u5fae\u98ce\uff0c\u5e03\u9992\u865a\u865a\u6643\u6643\uff0c\u4e0d\u77e5\u5e03 \u5e4c\u540e\u8fb9\u9690\u85cf\u7740\u4ec0\u4e48\u4e1c\u897f\uff1f\u5979\u597d\u5947\u5fc3\u5927\u53d1\uff0c\u9f13\u7740\u52c7\u6c14\uff0c\u4f38\u624b\u62c9\u5f00\u5e03\u5cfb\uff0c\u5e03\u6696\u540e\u8fb9\u5374\u7c7b\u4f3c\u4e00\u4e2a\u4f5b \u9f9b\uff0c\u4f5b\u9f9b\u5185\u4f9b\u7740\u4e00\u4e2a\u724c\u4f4d\uff0c\u4e5f\u662f\u7528\u767d\u6728\u523b\u6210\uff0c\u724c\u4f4d\u56db\u8fb9\u4e0a\u96d5\u4e91\u523b\u9f99\uff0c\u505a\u5f97\u975e\u5e38\u7cbe\u81f4\uff0c\u5f53\u4e2d\u5199 \u7740\u201c\u6069\u516c\u9739\u96f7\u5251\u5c55\u5927\u4fa0\u4e91\u5929\u4e4b\u795e\u4f4d\u201d\u3002 \u3000\u3000\u201c\u5c55\u4e91\u5929\uff01\u201d \u3000\u3000\u9526\u8863\u5c11\u5973\u51e0\u4e4e\u60ca\u547c\u51fa\u58f0\uff0c\u5979\u6e05\u695a\u8bb0\u5f97\u5728\u81ea\u5df2\u5bb6\u4e2d\uff0c\u6551\u4e86\u5c55\u54e5\u54e5\uff0c\u5979\u4e0e\u5c55\u54e5\u54e5\u4e92\u8bc9\u8eab\u4e16 \u65f6\uff0c\u5c55\u54e5\u54e5\u521a\u8bf4\u5230\u4ed6\u53eb\u5c55\u767d\uff0c\u7236\u6bcd\u53cc \u3000\u3000\u96f7\u5927\u53d4\u4fbf\u4ece\u7a97\u5916\u7a9c\u4e86\u8fdb\u6765\uff0c\u62c9\u7740\u5c55\u54e5\u54e5\u7684\u624b\uff0c\u5f88\u7740\u6025\u5730\u95ee\u5c55\u54e5\u54e5\uff1a\u201c\u5c55\u4e91\u5929\u662f\u4f60\u4ec0\u4e48 \u4eba\uff1f\u2026\u2026\u201d \u3000\u3000\u8fd9\u201c\u6b7b\u4eba\u5c45\u201d\u53c8\u6709\u5c55\u4e91\u5929\u7684\u795e\u4f4d\uff0c\u83ab\u975e\u5c55\u54e5\u54e5\u4e0e\u8fd9\u5c55\u4e91\u5929\u6709\u4ec0\u4e48\u5f88\u5bc6\u5207\u7684\u5173\u7cfb\u5417\uff1f \u3000\u3000\u539f\u6765\u8fd9\u9526\u8863\u5c11\u5973\u5c31\u662f\u5c55\u5a49\u513f\uff0c\u5979\u79c1\u604b\u5c55\u767d\uff0c\u5374\u53c8\u6c14\u5c55\u767d\u548c\u6a0a\u7d20\u9e3e\u4eb2\u8fd1\uff0c\u5728\u5c0f\u5b64\u5c71\u4e0a\u4e00\u6c14 \u56de\u5230\u95fa\u623f\uff0c\u540e\u6765\u542c\u8bf4\u5c55\u767d\u8ffd\u8d76\u201c\u795e\u9a74\u94c1\u80c6\u201d\u8463\u5343\u91cc\u8d70\u4e86\uff0c\u5979\u5374\u653e\u4e0d\u4e0b\u5fc3\u6765\uff0c\u7adf\u80cc\u7740\u7236\u6bcd\uff0c\u5077 \u5077\u5730\u9a91\u4e86\u7236\u4eb2\u7684\u5343\u91cc\u540d\u9a79\u201c\u8d64\u9a85\u9a9d\u201d\uff0c\u79bb\u5bb6\u6765\u627e\u5bfb\u5c55\u767d\u3002 \u3000\u3000\u5979\u867d\u7136\u5bb6\u5b66\u6e0a\u6e90\uff0c\u53c8\u4ece\u5c0f\u5f97\u5230\u7236\u6bcd\u6eba\u7231\uff0c\u5b66\u4e86\u4e00\u8eab\u9ad8\u5f3a\u6b66\u529f\uff0c\u4f46\u5374\u653e\u672a\u5728\u6c5f\u6e56\u9053\u4e0a\u8d70 \u8fc7\u3002\u5979\u97e7\u79bb\u5f00\u5bb6\uff0c\u4e2d\u4e0d\u77e5\u5c55\u767d\u8d70\u5411\u54ea\u91cc\uff0c\u4f46\u5374\u8bef\u6253\u8bef\u649e\uff0c\u8ba9\u5979\u5728\u5174\u9686\u9547\u4e0a\u9047\u5230\u5c55\u767d\u3002\u90a3\u65f6\uff0c \u5979\u4e5f\u5728\u90a3\u5bb6\u9152\u697c\u5403\u996d\uff0c\u4e0d\u8fc7\u4e00\u4e2a\u5728\u697c\u4e0a\u4e00\u4e2a\u5728\u697c\u4e0b\uff0c\u4e8c\u4eba\u672a\u78b0\u9762\u7f62\u4e86\u3002\u540e\u6765\uff0c\u5979\u542c\u5230\u697c\u4e0b\u4eba \u58f0\u9f0e\u6cb8\uff0c\u5175\u5668\u4ea4\u9e23\uff0c\u6709\u5165\u6253\u67b6\uff0c\u5979\u624d\u8dd1\u51fa\u6765\u770b\uff0c\u4e00\u89c1\u6b63\u662f\u5c55\u767d\u4e00\u652f\u78a7\u5251\uff0c\u72ec\u6218\u5341\u6570\u9ad8\u624b\u800c\u7a33 \u5360\u4e0a\u98ce\uff0c\u5979\u6682\u65f6\u672a\u9732\u9762\uff0c\u7b49\u5230\u5c55\u767d\u4e0e\u201c\u8840\u638c\u706b\u9f99\u201d\u786c\u62fc\u4e86\u4e09\u638c\u53d7\u4f24\uff0c\u800c\u201c\u8840\u638c\u706b\u9f99\u201d\u4ecd\u60f3\u4f24 \u5bb3\u5c55\u767d\u65f6\uff0c\u5979\u624d\u628a\u624b\u4e2d\u7684\u9a6c\u97ad\u5f53\u201c\u7529\u624b\u7bad\u201d\u4f7f\u7528\uff0c\u6551\u4e86\u5c55\u767d\u3002 \u3000\u3000\u5982\u4eca\uff0c\u5979\u53c8\u8bef\u6253\u8bef\u649e\uff0c\u8dd1\u5230\u8fd9\u201c\u6b7b\u4eba\u5c45\u201d\uff0c\u5374\u770b\u5230\u201c\u5c55\u4e91\u5929\u201d\u7684\u795e\u4f4d\uff0c\u77e5\u9053\u5979\u5fc3\u76ee\u4e2d\u7684 \u5c55\u54e5\u54e5\u4e0e\u8fd9\u5979\u600e\u80fd\u4e0d\u5173\u5fc3\u5462\uff1f \u3000\u3000\u5979\u56db\u5904\u5bdf\u770b\uff0c\u8fde\u627e\u6c34\u4e3a\u5c55\u54e5\u54e5\u670d\u836f\u7684\u4e8b\u4e5f\u5fd8\u4e86\u3002\u89c1\u90a3\u795e\u4f4d\u4e4b\u524d\uff0c\u4f9b\u7740\u6570\u789f\u9c9c\u82b1\u9999\u679c\uff0c\u94dc \u9f0e\u9999\u7089\uff0c\u9c9c\u82b1\u5c1a\u6cbe\u7740\u9732\u6c34\uff0c\u7089\u9999\u5c1a\u6709\u4f59\u70ec\uff0c\u53ef\u89c1\u6709\u4eba\u6309\u65f6\u4f9b\u62dc\u4e86\u3002 \u3000\u3000\u5728\u90a3\u795e\u9f9b\u4e4b\u524d\u5374\u662f\u4e00\u6761\u5bbd\u7ea6\u4e94\u5c3a\u7684\u6a2a\u884c\u8d70\u9053\uff0c\u5de6\u53f3\u5404\u6709\u5185\u5ba4\uff0c\u5185\u5ba4\u7684\u95e8\u4e5f\u662f\u767d\u6728\u505a\u6210\u3002 \u5de6\u9996\u95e8\u4e0a\u8d34\u7740\u4e00\u5f20\u767d\u7eb8\uff0c\u58a8\u7b14\u4e66\u201c\u6d3b\u6b7b\u4eba\u201d\uff1b\u53f3\u9996\u5185\u5ba4\u95e8\u4e0a\u5219\u5927\u4e66\u7740\u201c\u6b7b\u6d3b\u4eba\u201d\u3002\u4e24\u8fb9\u95e8\u4e0a \u5404\u6709\u4e00\u526f\u5bf9\u8054\uff0c\u4e5f\u662f\u7528\u767d\u7eb8\u58a8\u7b14\u5199\u7684\uff0c\u4e0a\u8054\u662f\u201c\u6709\u6069\u4e0d\u62a5\u751f\u4e0d\u5982\u6b7b\u201d\uff0c\u4e0b\u8054\u662f\u201c\u6709\u4ec7\u672a\u96ea\u5fcd \u6b7b\u5077\u751f\u201d\uff0c\u6a2a\u6279\u5219\u5199\u7740\u201c\u96ea\u4ec7\u62a5\u6069\u201d\u3002 \u3000\u3000\u770b\u81f3\u6b64\u5904\uff0c\u5a49\u513f\u5fc3\u4e2d\u5df2\u660e\u767d\u4e86\u4e94\u6210\uff0c\u60f3\u8fd9\u201c\u6b7b\u4eba\u5c45\u201d\u4f4f\u7684\u5e76\u4e0d\u662f\u4ec0\u4e48\u6b7b\u4eba\uff0c\u800c\u662f\u6d3b\u4eba\u6b20 \u4e86\u5c55\u5927\u4fa0\u7684\u6069\uff0c\u5c55\u5927\u4fa0\u51a4\u6b7b\uff0c\u672a\u80fd\u4e3a\u5c55\u5927\u4fa0\u96ea\u4ec7\u4ee5\u635f\u5927\u6069\uff0c\u624d\u81ea\u79f0\u6b7b\u4eba\u2026\u2026 \u3000\u3000\u5a49\u513f\u60f3\u5230\u8fd9\u91cc\uff0c\u5947\u602a\u4e3a\u4ec0\u4e48\u6ca1\u89c1\u5230\u8fd9\u201c\u6d3b\u6b7b\u4eba\u201d\uff0c\u8fd8\u662f\u201c\u6b7b\u6d3b\u4eba\u201d\u7684\u9762\u3002\u5f53\u5373\u8d70\u5230\u5de6\u9996 \u5185\u5ba4\uff0c\u7528\u624b\u4e00\u63a8\uff0c\u5ba4\u95e8\u5e94\u624b\u800c\u5f00\u3002\u53d1\u73b0\u5ba4\u5185\u4e00\u684c\u4e00\u6905\uff0c\u9648\u8bbe\u7b80\u5355\u4e4b\u6781\uff0c\u65e0\u5e8a\u65e0\u5e10\uff0c\u5374\u5728\u5ba4\u5185 \u4e00\u89d2\u505c\u653e\u7740\u4e00\u53e3\u767d\u6728\u68fa\u6750\uff01 \u3000\u3000\u767d\u6728\u68fa\u6750\u76d6\u5f97\u5f88\u5bc6\uff0c\u5c55\u5a49\u513f\u770b\u4e86\u534a\u5929\uff0c\u89c1\u5ba4\u5185\u65e0\u5176\u4ed6\u4e8b\u7269\uff0c\u53c8\u8f6c\u8eab\u51fa\u6765\uff0c\u8fdb\u5230\u53f3\u9996\u5ba4\u5185 \u53bb\u770b\u3002 \u3000\u3000\u8c01\u77e5\u53f3\u9996\u5ba4\u5185\u4e0e\u5de6\u9996\u5ba4\u5185\uff0c\u5b8c\u5168\u4e00\u6837\uff0c\u4e5f\u662f\u4e00\u684c\u4e00\u6905\uff0c\u9760\u5899\u505c\u7740\u4e00\u53e3\u767d\u672c\u68fa\u6750\u3002 \u3000\u3000\u8bfa\u5927\u4e00\u5ea7\u5ead\u9662\uff0c\u9602\u65e0\u4e00\u4eba\uff0c\u5374\u5728\u623f\u5185\u505c\u4e86\u4e24\u5177\u767d\u8272\u68fa\u6728\uff0c\u800c\u4e14\u9898\u4e86\u201c\u6b7b\u4eba\u5c45\u201d\u90a3\u4e48\u4e2a\u602a \u540d\u5b57\uff0c\u5e72\u79cd\u96be\u8a00\u7684\u9634\u68ee\u6050\u6016\u4e4b\u611f\uff0c\u53ea\u628a\u4e00\u4e2a\u6ca1\u6709\u51fa\u8fc7\u8fdc\u95e8\u7684\u5a49\u513f\u59d1\u5a18\uff0c\u5413\u5f97\u516d\u795e\u65e0\u4e3b\uff0c\u82b3\u5fc3 \u5fd0\u5fd1\u4e0d\u5b89 \u3000\u3000\u5a49\u513f\u6b63\u5728\u53f3\u9996\u5ba4\u5185\u5fd0\u5fd1\u53d1\u6014\u7684\u5f53\u513f\uff0c\u7a81\u542c\u623f\u5916\u201c\u549a\u201d\u7684\u4e00\u58f0\u5de8\u54cd\uff0c\u628a\u5c55\u5a49\u513f\u5413\u5f97\u4e00\u8df3\uff0c \u5f53\u5373\u5de6\u638c\u62a4\u80f8\uff0c\u53f3\u638c\u5f85\u654c\uff0c\u98de\u8eab\u63a0\u51fa\u5ba4\u5916\u3002 \u3000\u3000\u53ea\u89c1\u5979\u7684\u5c55\u54e5\u54e5\u4ece\u6905\u4e0a\u683d\u5012\u5730\u4e0b\uff0c\u76f4\u633a\u633a\u5730\u8eba\u5728\u90a3\u91cc\uff0c\u5a49\u513f\u4e0d\u987e\u5343\u5207\u98de\u8eab\u63a0\u5230\u5c55\u767d\u8eab \u524d\uff0c\u4fef\u4e0b\u8eab\u4e00\u770b\uff0c\u5c55\u767d\u9762\u7ea2\u5982\u706b\uff0c\u7c97\u91cd\u7684\u9f3b\u606f\u5df2\u5f31\u4e0d\u53ef\u95fb\uff0c\u7709\u950b\u7d27\u76b1\uff0c\u5634\u89d2\u6d41\u51fa\u4e00\u4e1d\u9c9c \u8840\u2026\u2026 \u3000\u3000\u5a49\u513f\u5927\u5403\u4e00\u60ca\uff0c\u7ec6\u770b\u5c55\u767d\u8eab\u4e0a\u5e76\u6ca1\u6709\u65b0\u7684\u4f24\u75d5\uff0c\u77e5\u662f\u81ea\u5df1\u8fdb\u5185\u5ba4\u641c\u5bfb\u65f6\u65e0\u4eba\u7167\u987e\uff0c\u5c55\u54e5 \u54e5\u81ea\u884c\u683d\u5012\u7684\uff0c\u4e0d\u7531\u6697\u9a82\u81ea\u5df1\u8be5\u6b7b\uff0c\u653e\u7740\u6b63\u4e8b\u4e0d\u5e72\uff0c\u5374\u53bb\u4e1c\u5bfb\u897f\u770b\uff0c\u4e8e\u662f\u8d76\u7d27\u628a\u5c55\u54e5\u54e5\u53c8\u62b1 \u5750\u5e84\u6905\u4e0a\uff0c\u53d6\u51fa\u7ea2\u8138\u8001\u5934\u9001\u7684\u90a3\u7c92\u7eff\u8272\u836f\u4e38\uff0c\u53ef\u662f\u6025\u5207\u95f4\u53c8\u627e\u4e0d\u5230\u5f00\u6c34\uff0c\u5a49\u513f\u79c0\u7709\u4e00\u76b1\uff0c\u7ade \u5f20\u5f00\u6a31\u53e3\uff0c\u4ee5\u53e3\u6db2\u6d25\u6c34\uff0c\u5634\u5bf9\u5634\u4e3a\u5c55\u767d\u628a\u836f\u4e38\u9001\u4e0b\u3002 \u3000\u3000\u5343\u662f\u5a49\u513f\u5fc3\u5730\u7eaf\u6d01\uff0c\u5e76\u6ca1\u6709\u60f3\u5230\u7537\u5973\u4e4b\u95f4\u3002\u9664\u4e86\u592b\u59bb\u662f\u4e0d\u80fd\u505a\u51fa\u8fd9\u79cd\u4eb2\u5462\u7684\u52a8\u4f5c\uff1b\u518d\u8005 \u4e5f\u662f\u5a49\u513f\u5fc3\u4e2d\u592a\u7231\u5979\u5c55\u54e5\u54e5\uff0c\u4e8b\u51fa\u7d27\u6025\uff0c\u624d\u6beb\u4e0d\u907f\u8bb3\u5730\u4ee5\u53e3\u6c34\u9001\u836f\u4e3a\u5c55\u767d\u670d\u4e0b\u3002\u5a49\u513f\u4ecd\u6015\u5c55 \u54e5\u54e5\u5931\u53bb\u77e5\u89c9\uff0c\u4e0d\u80fd\u987a\u5229\u628a\u836f\u4e38\u54bd\u4e0b\uff0c\u5f53\u5373\u529f\u8fd0\u638c\u5fc3\uff0c\u4e3a\u5c55\u767d\u5728\u80f8\u524d\u6309\u6469\u3002 \u3000\u3000\u5a49\u513f\u7389\u638c\u8fc7\u5904\uff0c\u53ea\u89c9\u5c55\u767d\u6000\u5185\u9f13\u7ef7\u7ef7\u7684\u4e00\u7269\u683c\u624b\uff0c\u5a49\u513f\u4e0d\u77e5\u5c55\u767d\u6000\u4e2d\u6240\u85cf\u4f55\u7269\uff0c\u5f53\u5373\u63a2 \u624b\u53d6\u51fa\uff0c\u539f\u6765\u662f\u4e00\u672c\u5f69\u8272\u5c01\u9762\u7684\u4e66\u3002 \u3000\u3000\u5a49\u513f\u5029\u7136\u4e00\u7b11\uff0c\u5fc3\u8bf4\uff1a\u6211\u8fd9\u5c55\u54e5\u54e5\u4e5f\u50cf\u4e2a\u5c0f\u5b69\u4f3c\u7684\uff0c\u8fd9\u4e48\u5927\u4e86\u8fd8\u770b\u82b1\u4e66\uff01\u2026\u2026\u201d \u3000\u3000\u5a49\u513f\u60f3\u7740\uff0c\u968f\u624b\u628a\u4e66\u7ffb\u5f00\uff0c\u6ca1\u60f3\u5230\u4e66\u5185\u753b\u7684\u5168\u662f\u8d64\u88f8\u7684\u5973\u5b50\uff0c\u59ff\u6001\u5996\u8273\uff0c\u51b6\u8361\u8ff7\u4eba\u2026\u2026 \u3000\u3000\u201c\u597d\u574f\uff01\u4f60\u2014\u2014\u201d\u5a49\u513f\u4fca\u8138\u4e00\u7ea2\uff0c\u4f4e\u9a82\u4e86\u4e00\u58f0\uff0c\u7adf\u5fd8\u8bb0\u5c55\u767d\u662f\u5728\u660f\u8ff7\u4e4b\u4e2d\uff0c\u628a\u4e00\u518c\u4e66 \u753b\uff0c\u76f4\u5411\u5c55\u767d\u6000\u4e2d\u6454\u53bb\u3002 \u3000\u3000\u201c\u556a\u2014\u2014\u54d2\uff01\u201d\u753b\u4e66\u4ece\u5c55\u767d\u6000\u4e2d\uff0c\u6389\u5728\u5730\u4e0b\u2026\u2026 \u3000\u3000\u5ffd\u542c\u95e8\u5916\u4e00\u58f0\u66b4\u559d\uff1a\u201c\u4f55\u4eba\u5927\u80c6\uff1f\u7adf\u6562\u95ef\u8fdb\u6b7b\u4eba\u5c45\uff01\u201d \u3000\u3000\u58f0\u843d\uff0c\u6765\u4eba\u5df2\u6251\u8fdb\u623f\u4e2d\uff0c\u5a49\u513f\u5c1a\u6765\u4e0d\u53ca\u56de\u8eab\uff0c\u4e00\u80a1\u52b2\u98ce\u5df2\u5411\u8eab\u540e\u649e\u6765\u3002 \u3000\u3000\u5a49\u513f\u6050\u88ab\u6765\u4eba\u4f24\u4e86\u5979\u7684\u5c55\u54e5\u54e5\uff0c\u4e0d\u8eb2\u4e0d\u95ee\uff0c\u4e00\u5f0f\u201c\u5012\u8f6c\u9634\u9633\u201d\u501f\u8eab\u5f62\u56de\u65cb\u4e4b\u52bf\uff0c\u53cc\u638c\u9f50 \u51fa\uff0c\u731b\u5411\u8eab\u540e\u52b2\u98ce\u8fce\u53bb\u3002 \u3000\u3000\u201c\u54a6\uff01\u662f\u5c0f\u6069\u516c\u2026\u2026\u201d \u3000\u3000\u5a49\u513f\u4e00\u8f6c\u8eab\uff0c\u6765\u4eba\u5df2\u4ece\u5a49\u513f\u8eab\u540e\uff0c\u770b\u6e05\u5c55\u767d\u7684\u9762\u76ee\uff0c\u7acb\u5373\u60ca\u547c\u4e00\u58f0\uff0c\u8d76\u7d27\u632b\u638c\u6536\u52bf\uff0c\u786c \u628a\u51fb\u51fa\u7684\u638c\u529b\u6536\u4e86\u56de\u53bb\u3002 \u3000\u3000\u201c\u7830\uff01\u562d\uff01\u201d\u8fd8\u662f\u6162\u4e86\uff0c\u63a5\u8fde\u4e24\u58f0\u8106\u54cd\uff0c\u5a49\u513f\u53cc\u638c\u88ab\u6765\u4eba\u638c\u529b\u4f59\u52b2\u9707\u5f97\u5a07\u8eaf\u8fde\u6643\uff0c\u4e24\u81c2 \u53d1\u9ebb\u3002 \u3000\u3000\u201c\u597d\u5927\u7684\u638c\u529b\uff01\u201d\u5a49\u513f\u5fc3\u4e2d\u5403\u60ca\uff0c\u62ac\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u6765\u7684\u7adf\u662f\u4e24\u4e2a\u602a\u4eba\uff0c\u4e00\u6837\u7684\u8eab\u7a7f\u767d\u5e03 \u845b\u8863\uff0c\u8170\u7cfb\u4e00\u6839\u9ebb\u7ef3\uff0c\u4e00\u6837\u7684\u82cd\u767d\u9634\u68ee\uff0c\u6beb\u65e0\u8868\u60c5\u7684\u6b7b\u4eba\u8138\uff0c\u4e00\u5de6\u4e00\u53f3\u7ad9\u7acb\u5a49\u513f\u8eab\u524d\uff0c\u6d3b\u4f3c \u4e24\u5177\u50f5\u5c38\uff0c\u5a49\u513f\u4e0d\u7531\u5012\u6cb9\u4e00\u53e3\u51b7\u6c14\u3002 \u3000\u3000\u4e24\u4e2a\u602a\u4eba\uff0c\u56db\u53ea\u51b7\u7535\u4f3c\u7684\u76ee\u5149\uff0c\u4ece\u5a49\u513f\u8138\u4e0a\u770b\u5230\u5c55\u767d\u8138\u4e0a\uff0c\u53c8\u4ece\u5c55\u767d\u8138\u4e0a\u770b\u5230\u5a49\u513f\u8138 \u4e0a\u3002 \u3000\u3000\u5a49\u513f\u89c1\u4e24\u4e2a\u602a\u4eba\u9634\u68ee\u6050\u6016\uff0c\u8138\u4e0a\u9634\u6674\u4e0d\u5b9a\uff0c\u76ee\u5149\u95ea\u70c1\uff0c\u6050\u6015\u6709\u4e0d\u5229\u5c55\u54e5\u54e5\u7684\u4f01\u56fe\uff0c\u867d\u7136 \u660e\u77e5\u81ea\u5df1\u4e0d\u662f\u4e24\u4e2a\u602a\u4eba\u7684\u5bf9\u624b\uff0c\u4f46\u4e3a\u4e86\u7ef4\u62a4\u5c55\u54e5\u54e5\uff0c\u5979\u7ade\u6697\u4e2d\u8fd0\u529f\u6212\u5907\uff0c\u4e0d\u4f46\u6ca1\u6709\u4e00\u4e1d\u754f\u60e7 \u9000\u7f29\u4e4b\u610f\uff0c\u53cd\u800c\u6697\u4e0b\u51b3\u5fc3\uff0c\u5047\u5982\u4e24\u4e2a\u602a\u4eba\u5bf9\u5979\u5c55\u54e5\u54e5\u6709\u4ec0\u4e48\u4e0d\u5229\u7684\u884c\u52a8\uff0c\u5979\u7acb\u523b\u5168\u529b\u53cd \u51fb\u2026\u2026 \u3000\u3000\u201c\u5c0f\u6069\u516c\u8d1f\u4f24\u4e0d\u8f7b\uff01\u201d\u5de6\u9996\u602a\u4eba\u8fd9\u53e5\u8bdd\u4e0d\u77e5\u662f\u5bf9\u4f55\u4eba\u6240\u8bf4\u3002 \u3000\u3000\u201c\u6240\u4ee5\u6211\u8bf4\u6211\u4eec\u4e0d\u80fd\u6b7b\uff0c\u7559\u4e0b\u6b8b\u547d\uff0c\u8fd8\u6709\u5f88\u591a\u7528\u5904\uff01\u201d\u53f3\u9996\u602a\u4eba\u8bf4\u3002 \u3000\u3000\u5a49\u513f\u770b\u4ed6\u4eec\u4e24\u4eba\u4f3c\u5728\u4e92\u76f8\u8c08\u8bdd\uff0c\u51b7\u7535\u4f3c\u7684\u76ee\u5149\uff0c\u5374\u671b\u7740\u81ea\u5df2\u548c\u5c55\u54e5\u54e5\uff0c\u6697\u4e2d\u8be7\u5f02\u4e24\u602a\u4eba \u4e4b\u602a\u2026\u2026 \u3000\u3000\u201c\u5c0f\u5a18\u4eec\uff01\u53ef\u662f\u4f60\u628a\u5c0f\u6069\u516c\u6253\u4f24\uff1f\u201d\u5de6\u9996\u602a\u4eba\u5ffd\u7136\u5411\u5a49\u513f\u5389\u58f0\u95ee\u9053\u3002 \u3000\u3000\u201c\u4f60\u4eec\u4e24\u4e2a\u662f\u4ec0\u4e48\u4eba\uff1f\u201d\u5a49\u513f\u53cd\u95ee\u9053\uff0c\u7f13\u4e86\u4e00\u7f13\uff0c\u53c8\u8bf4\uff1a\u201c\u6211\u4e3a\u4ec0\u4e48\u8981\u6253\u4f24\u5c55\u54e5 \u54e5\uff1f\u2026\u2026\u201d \u3000\u3000\u201c\u776b\uff01\u201d\u53f3\u9996\u602a\u4eba\u7a81\u7136\u4e00\u58f0\u66b4\u559d\uff0c\u8eab\u5f62\u5e73\u5c04\u800c\u8d77\uff0c\u5de6\u624b\u4e00\u6325\uff0c\u62e8\u5f00\u5a49\u513f\uff0c\u76f4\u5411\u660f\u8ff7\u4e2d\u7684 \u5c55\u767d\u6251\u53bb\u3002 \u3000\u3000\u201c\u4f60\u8981\u5e72\u4ec0\u4e48\uff1f\u201d\u5a49\u513f\u6050\u6015\u602a\u4eba\u4f24\u5bb3\u5979\u7684\u54e5\u54e5\uff0c\u5927\u53eb\u4e00\u58f0\uff0c\u201c\u529b\u6311\u5343\u94a7\u201d\uff0c\u53f3\u624b\u731b\u5411\u6251 \u6765\u602a\u4eba\u7684\u81c2\u4e0a\u683c\u53bb\u3002 \u3000\u3000\u5a49\u513f\u60c5\u6025\u51fa\u624b\uff0c\u8fd9\u4e00\u638c\u7528\u51fa\u4e86\u5168\u529b\uff0c\u4e0d\u8981\u662f\u8bf4\u4eba\u81c2\uff0c\u5c31\u662f\u94c1\u6761\u4e5f\u53ef\u4ee5\u6253\u5f2f\u3002 \u3000\u3000\u4f46\u602a\u4eba\u5bf9\u7740\u5a49\u513f\u5168\u529b\u7684\u4e00\u638c\uff0c\u7ade\u5982\u672a\u89c1\uff0c\u4e0d\u8eb2\u4e0d\u95ea\uff0c\u4ecd\u7136\u5e73\u5c04\u8eab\u5f62\uff0c\u5411\u5c55\u767d\u6251\u53bb\u3002 \u3000\u3000\u201c\u562d\uff01\u201d\u5a49\u513f\u4e00\u638c\u6b63\u6253\u5728\u602a\u4eba\u7684\u5de6\u81c2\u4e0a\uff0c\u5982\u540c\u51fb\u4e2d\u575a\u94a2\uff0c\u534a\u8fb9\u8eab\u5b50\u90fd\u9ebb\u4e86\uff0c\u638c\u7f18\u66f4\u662f\u4e00 \u9635\u5de8\u75db\uff0c\u88ab\u9707\u5f97\u7ad9\u811a\u4e0d\u4f4f\uff0c\u4e00\u76f4\u9000\u51fa\u4e94\u516d\u6b65\uff0c\u624d\u62ff\u6869\u7ad9\u7a33\u3002 \u3000\u3000\u90a3\u602a\u4eba\u5374\u5df2\u6251\u8fd1\u5c55\u767d\u8eab\u524d\uff0c\u4f38\u51fa\u4e24\u53ea\u6bdb\u8338\u8338\u7684\u5927\u624b\uff0c\u76f4\u5411\u5c55\u767d\u524d\u5fc3\u8981\u5bb3\u6309\u4e0b\u3002 \u3000\u3000\u5a49\u513f\u53c8\u60ca\u53c8\u6025\uff0c\u5927\u53eb\u4e00\u58f0\uff1a\u8001\u602a\u7269\uff01\u4f60\u6562\u78b0\u4e00\u78b0\u5c55\u54e5\u54e5\uff0c\u59d1\u5a18\u4fbf\u8ddf\u4f60\u62fc\u4e86\uff01\u201d \u3000\u3000\u559d\u53eb\u58f0\u4e2d\uff0c\u201c\u9ec4\u83ba\u51fa\u8c37\u201d\u53cc\u638c\u9f50\u51fa\uff0c\u5168\u8eab\u731b\u5411\u6251\u8fd1\u5c55\u767d\u7684\u602a\u4eba\u6253\u53bb\u3002 \u3000\u3000\u201c\u5c0f\u5a18\u4eec\uff01\u8001\u5b9e\u4e00\u4f1a\u5427\uff01\u4f60\u2014\u2014\u201d\u53e6\u4e00\u602a\u4eba\u5355\u638c\u4e00\u6325\uff0c\u5a49\u513f\u524d\u6251\u7684\u8eab\u5f62\uff0c\u5982\u53d7\u65e0\u5f62\u7684\u4e00 \u9053\u6c14\u5899\u4e00\u963b\uff0c\u4e00\u526f\u5a07\u8eaf\u53cd\u88ab\u53cd\u5f39\u56de\u6765\uff0c\u201c\u7830\u201d\u7684\u4e00\u58f0\uff0c\u649e\u5728\u5899\u58c1\u4e0a\u3002 \u3000\u3000\u53ea\u649e\u5f97\u5a49\u513f\u53cc\u8ddf\u6ca1\u9ed1\uff0c\u51e0\u4e4e\u660f\u4e86\u8fc7\u53bb\u3002\u5979\u8d76\u5fd9\u80cc\u9760\u5899\u58c1\u8fd0\u6c14\u8c03\u606f\uff0c\u518d\u7741\u773c\u4e00\u770b\uff0c\u53ea\u89c1\u5148 \u524d\u602a\u4eba\u53cc\u638c\u5df2\u6309\u5728\u5c55\u54e5\u201c\u547d\u95e8\u201d\u8981\u7a74 \u3000\u3000\u201c\u547d\u95e8\u201d\u4e43\u4eba\u8eab\u6b7b\u7a74\uff0c\u53ea\u8981\u602a\u4eba\u638c\u4e0b\u5fae\u4e00\u5410\u52b2\uff0c\u5c55\u54e5\u54e5\u4fbf\u53ef\u4e27\u547d\u3002\u5a49\u513f\u6025\u5f97\u773c\u6cea\u90fd\u6db2\u4e0b \u6765\u4e86\u2026\u2026 \u3000\u3000\u4f46\u5979\u518d\u4e00\u7ec6\u770b\uff0c\u624d\u77e5\u9053\u602a\u4eba\u5e76\u4e0d\u662f\u8981\u52a0\u5bb3\u5c55\u54e5\u54e5\uff0c\u800c\u662f\u4ee5\u672c\u8eab\u771f\u529b\u4e3a\u5c55\u54e5\u54e5\u63a8\u5bab\u6cbb\u7a74\uff0c \u56e0\u4e3a\u5979\u770b\u5230\u5c55\u767d\u8138\u4e0a\u7ea2\u6f6e\u5df2\u9000\uff0c\u800c\u4e14\u7f13\u7f13\u7741\u5f00\u4e86\u773c\u775b\u3002\u5a49\u513f\u4e0d\u7531\u6697\u53eb\u4e00\u58f0\uff1a\u60ed\u6127\uff01\u201d\u4fbf\u5c31\u52bf \u501a\u5728\u5899\u4e0a\uff0c\u501f\u673a\u8c03\u606f\u8d77\u6765\u2026\u2026 \u3000\u3000\u53e6\u5916\u4e00\u602a\u4eba\u4e5f\u8d70\u8fd1\u5c55\u767d\uff0c\u5374\u4e00\u773c\u770b\u5230\u5c55\u767d\u811a\u4e0b\u90a3\u672c\u5f69\u8272\u5c01\u9762\u7684\u4e66\u3002 \u3000\u3000\u201c\u54ce\u5440\uff01\u201d\u90a3\u602a\u4eba\u62ac\u8d77\u90a3\u672c\u4e66\uff0c\u53ea\u770b\u4e86\u4e00\u773c\uff0c\u4fbf\u60ca\u53eb\u8d77\u6765\uff1a\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\u5144\u957f\uff01 \u5144\u957f\uff01\u4f60\u770b\u2026\u2026\u201d \u3000\u3000\u4ed6\u60ca\u53eb\u7740\uff0c\u62ac\u5934\u2014\u770b\u4ed6\u7684\u5144\u957f\u5934\u4e0a\u5192\u51fa\u84b8\u84b8\u70ed\u6c14\uff0c\u663e\u7136\u662f\u4e3a\u5c55\u767d\u63a8\u5bab\u6d3b\u8840\uff0c\u6b63\u5230\u4e86\u7d27\u8981 \u5173\u5934\uff0c\u4ed6\u7acb\u523b\u628a\u540e\u8fb9\u7684\u8bdd\u54bd\u4e86\u56de\u53bb\u3002 \u3000\u3000\u4f46\u4ed6\u51b7\u68ee\u65e0\u8868\u60c5\u7684\u8138\u4e0a\uff0c\u4e5f\u900f\u51fa\u4e86\u65e0\u6bd4\u7684\u6fc0\u52a8\uff0c\u53ea\u89c1\u4ed6\u53cc\u773c\u653e\u5149\uff0c\u8fde\u6367\u7740\u90a3\u672c\u4e66\u7684\u53cc\u624b \u4e5f\u5fbd\u5fae\u98a4\u6296\u8d77\u6765\u3002 \u3000\u3000\u9760\u5728\u5899\u58c1\u4e0a\u8fd0\u6c14\u8c03\u606f\u7684\u5a49\u513f\uff0c\u542c\u5230\u90a3\u602a\u4eba\u53eb\u51fa\u300a\u9501\u9aa8\u9500\u9b42\u5929\u62c2\u5377\u300b\u7684\u540d\u5b57\uff0c\u5fc3\u5934\u4e5f\u731b\u7136 \u4e00\u9707\uff01\u5979\u6709\u4e00\u6b21\u65e0\u610f\u4e2d\uff0c\u66fe\u542c\u5230\u7239\u7239\u8ddf\u95e8\u4e0b\u98df\u5ba2\u95f2\u8c08\u6b66\u6797\u638c\u6545\uff0c\u8bf4\u5230\u4e8c\u767e\u4f59\u5e74\u4ee5\u524d\uff0c\u6709\u4e00\u4f4d \u6b66\u6797\u5947\u4eba\u201c\u53ea\u773c\u90ce\u541b\u201d\uff0c\u7559\u4e0b\u4e00\u90e8\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\uff0c\u88ab\u6b66\u6797\u79f0\u4e3a\u5929\u4e0b\u7b2c\u4e00\u5947\u4e66\uff0c\u8c01\u8981\u80fd \u5f97\u5230\u8fd9\u672c\u5947\u4e66\uff0c\u6309\u7167\u4e66\u4e0a\u8981\u8bc0\u4fee\u70bc\uff0c\u4fbf\u53ef\u6210\u4e3a\u6b66\u529f\u5929\u4e0b\u7b2c\u4e00\u2026\u2026 \u3000\u3000\u60f3\u5230\u8fd9\u91cc\uff0c\u5a49\u513f\u6709\u70b9\u8ff7\u60d1\u4e86\u3002\u56e0\u4e3a\u5979\u65b9\u624d\u770b\u8fc7\uff0c\u4e66\u4e2d\u5c3d\u662f\u4e9b\u8d64\u88f8\u88f8\u7684\u5973\u5b50\u753b\u50cf\uff0c\u5e76\u6ca1\u6709 \u6b66\u529f\u8bc0\u7a8d\uff0c\u83ab\u975e\u90a3\u4e9b\u8d64\u88f8\u5973\u5b50\u7684\u5404\u79cd\u5996\u6cbb\u5f62\u72b6\uff0c\u5c31\u662f\u6b66\u529f\u7684\u62db\u5f0f\u5417\uff1f\u2026\u2026 \u3000\u3000\u5a49\u51e0\u4e00\u8fb9\u5fc3\u4e2d\u80e1\u4e71\u731c\u60f3\uff0c\u4e00\u8fb9\u770b\u90a3\u6367\u7740\u4e66\u7684\u602a\u4eba\u3002\u53ea\u89c1\u4ed6\u53cc\u624b\u98a4\u6296\u7740\u628a\u4e66\u672c\u6253\u5f00\uff0c\u4e00\u9875 \u4e00\u9875\u5730\u7ffb\u9605\u7740\u2026\u2026 \u3000\u3000\u6108\u5411\u4e0b\u770b\uff0c\u602a\u4eba\u8138\u4e0a\u7684\u8868\u60c5\u6108\u662f\u7279\u522b\u3002\u4e00\u4f1a\u513f\u773c\u7709\u8038\u52a8\u4e24\u4e0b\uff0c\u4e00\u4f1a\u5634\u89d2\u6485\u52a8\u4e24\u4e0b\uff0c\u53cc\u773c \u653e\u51fa\u5947\u5f02\u7684\u5149\u8292\uff0c\u90a3\u9634\u68ee\u60e8\u767d\u6beb\u65e0\u8840\u8272\u7684\u8138\u4e0a\u7ade\u4e5f\u6cdb\u8d77\u4e86\u7ea2\u6f6e\u2026\u2026 \u3000\u3000\u6e10\u6e10\u5730\u602a\u4eba\u6d51\u8eab\u53d1\u6296\uff0c\u4f3c\u662f\u5df2\u628a\u63e1\u4e0d\u4f4f\u5185\u5fc3\u6fc0\u60c5\u7684\u6cdb\u6ee5\uff0c\u6700\u540e\uff0c\u7ec8\u4e8e\u95ed\u4e0a\u4e86\u773c\u775b\u2026\u2026 \u3000\u3000\u90a3\u4e3a\u5c55\u767d\u7597\u4f24\u7684\u602a\u4eba\uff0c\u5934\u4e0a\u84b8\u84b8\u7684\u70ed\u6c14\u6108\u6765\u6108\u6d53\uff0c\u6700\u540e\u7ade\u5728\u5934\u9876\u51dd\u805a\u4e86\u4e09\u6735\u767d\u4e91\uff0c\u8fdc\u8fdc \u671b\u53bb\u72b9\u5982\u5728\u5934\u4e0a\u5f00\u4e86\u4e09\u6735\u767d\u83b2\u82b1\u4e00\u822c\u3002 \u3000\u3000\u5a49\u513f\u9760\u5728\u5899\u58c1\u4e0a\u77aa\u5927\u4e86\u773c\u775b\uff0c\u770b\u7740\u4e24\u4e2a\u602a\u4eba\uff1b\u90a3\u7ffb\u9605\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\u7684\u602a\u4eba\u4e4b\u602a\u6a21 \u6837\uff0c\u5df2\u7ecf\u591f\u5438\u5f15\u4eba\u7684\u4e86\u3002\u53c8\u89c1\u4e3a\u5c55\u54e5\u54e5\u7528\u5185\u529f\u7597\u4f24\u7684\u4eba\uff0c\u5934\u4e0a\u7684\u767d\u6c14\u7ade\u5f62\u6210\u4e86\u4e09\u6735\u767d\u4e91\u72b6\u7684 \u82b1\u6735\uff0c\u5a49\u51e0\u66f4\u662f\u5403\u60ca\uff0c\u5979\u77e5\u9053\u8fd9\u662f\u201c\u4e09\u82b1\u805a\u9876\u201d\uff0c\u4e3a\u5185\u5bb6\u529f\u592b\u7ec3\u5230\u767b\u5cf0\u9020\u6781\u7684\u73b0\u8c61\uff0c\u5979\u60f3\u4e0d \u5230\u8fd9\u50cf\u9b3c\u9b45\u4f3c\u7684\u602a\u4eba\uff0c\u7adf\u6709\u8fd9\u7b49\u9ad8\u6df1\u7684\u529f\u529b\u3002\u56e0\u6b64\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u5979\u770b\u5f97\u51fa\u4e86\u795e\u2026\u2026 \u3000\u3000\u7a81\u7136\uff0c\u5979\u89c9\u5f97\u6709\u4e24\u9053\u5229\u526a\u4f3c\u7684\u5149\u8292\uff0c\u9489\u5728\u81ea\u5df1\u8eab\u4e0a\uff0c\u8f6c\u5934\u4e00\u770b\uff0c\u539f\u6765\u662f\u7ffb\u9605\u300a\u9501\u9aa8\u9500\u9b42 \u5929\u4f5b\u5377\u300b\u7684\u602a\u4eba\uff0c\u6fc0\u60c5\u96be\u6301\u95ed\u4e0a\u773c\u775b\uff0c\u73b0\u5728\u53c8\u628a\u773c\u775b\u6253\u5f00\u3002 \u3000\u3000\u53ea\u89c1\u4ed6\u9762\u6cdb\u7ea2\u6f6e\uff0c\u6df1\u8eab\u98a4\u6296\uff0c\u53cc\u773c\u6b7b\u76ef\u7740\u81ea\u5df2\u80f8\u524d\uff0c\u5c0f\u8179\uff0c\u7adf\u7acb\u8d77\u8eab\u5f62\u5411\u81ea\u5df2\u8d70\u6765\u2026\u2026 \u5a49\u513f\u53ea\u662f\u4e00\u4e2a\u5929\u771f\u672b\u51ff\u7684\u5c11\u5973\uff0c\u54ea\u91cc\u77e5\u9053\u7537\u4eba\u5bf9\u5f02\u6027\u6e34\u6c42\u7684\u60c5\u7130\u6b32\u706b\uff0c\u662f\u5982\u6b64\u7684\u75af\u72c2\u7c97\u91ce\u3002 \u3000\u3000\u8fd9\u602a\u4eba\u672c\u4e5f\u662f\u4e00\u4f4d\u6b66\u6797\u602a\u6770\uff0c\u800c\u4e14\u4e5f\u6709\u7740\u6570\u5341\u5e74\u7684\u82e6\u4fee\uff0c\u6b66\u529f\u4e4b\u9ad8\uff0c\u5b9a\u529b\u4e4b\u5f3a\uff0c\u5728\u5f53\u4eca \u6b66\u6797\u5df2\u662f\u5c48\u6307\u53ef\u6570\u3002\u8bbe\u60f3\u5230\u4e00\u518c\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\uff0c\u7ade\u80fd\u4f7f\u4ed6\u5931\u53bb\u5e38\u6001\uff01\u6b64\u65f6\uff0c\u4ed6\u60c5\u7130\u9ad8 \u6da8\uff0c\u82e6\u4fee\u6570\u5341\u5e74\u7684\u60c5\u5173\u5df2\u88ab\uff0c\u518d\u4e5f\u628a\u6301\u4e0d\u4f4f\u5982\u706b\u71ce\u539f\u7684\u6ee1\u8154\u6b32\u706b\uff0c\u9762\u5bf9\u7740\u5a07\u6ef4\u6ef4\u7684\u5999\u9f84\u5c11\u5973 \u5a49\u513f\u59d1\u5a18\uff0c\u4ed6\u5fd8\u8bb0\u81ea\u5df1\u7684\u8eab\u5206\uff0c\u4e5f\u5fd8\u4e86\u81ea\u5df1\u4e00\u751f\u7684\u6e05\u8a89\uff0c\u66f4\u5fd8\u4e86\u8eab\u65c1\u6709\u81ea\u5df1\u540c\u80de\u7684\u5144\u957f\uff0c\u6069 \u516c\u7684\u513f\u5b50\u2026\u2026\u6b64\u65f6\uff0c\u4ed6\u9ad8\u6da8\u7684\u6b32\u706b\u5df2\u4f7f\u4ed6\u5931\u53bb\u4e86\u7406\u667a\uff0c\u7ade\u50cf\u997f\u864e\u6251\u7f8a\u4f3c\u6251\u6765\u3002 \u3000\u3000\u5a49\u513f\u60ca\u53eb\u2014\u58f0\u201c\u95ed\u95e8\u8c22\u5ba2\u201d\uff0c\u53cc\u638c\u4ee5\u5468\u8eab\u7684\u529b\u6c14\uff0c\u731b\u5411\u75af\u72c2\u6251\u81f3\u7684\u602a\u4eba\u524d\u80f8\u51fb\u53bb\u3002 \u3000\u3000\u201c\u562d\uff01\u201d\u4e00\u58f0\u5927\u9707\uff0c\u5bf9\u5a49\u513f\u6253\u6765\u7684\u53cc\u638c\uff0c\u90a3\u602a\u4eba\u7ade\u4e0d\u95ee\u4e0d\u8eb2\uff0c\u7ed3\u7ed3\u5b9e\u5b9e\u5730\u6328\u4e86\u5a49\u513f\u4e24 \u638c\u3002 \u3000\u3000\u5a49\u513f\u5168\u529b\u63a8\u51fa\u7684\u8fd9\u4e24\u638c\uff0c\u5c11\u8bf4\u4e5f\u6709\u4e94\u516d\u767e\u65a4\u7684\u529b\u91cf\uff0c\u8c01\u77e5\u6253\u5728\u602a\u4eba\u80f8\u524d\uff0c\u602a\u4eba\u8eab\u5f62\u53ea\u6643 \u4e86\u4e24\u6643\uff0c\u524d\u6251\u7684\u8eab\u5f62\u5e76\u672b\u505c\u6b62\uff0c\u53cd\u5e94\u66f4\u52a0\u5feb\u901f\uff0c\u53cc\u81c2\u4e00\u5f20\uff0c\u5df2\u5c06\u5a49\u513f\u7684\u5a07\u8eaf\uff0c\u62b1\u4e86\u4e2a\u6ee1\u6000\u3002 \u3000\u3000\u5a49\u513f\u611f\u5230\u5468\u8eab\u5982\u88ab\u94a2\u7b8d\uff0c\u538b\u5728\u80f8\u524d\u7684\u91cd\u91cf\u51e0\u4e4e\u4f7f\u81ea\u5df1\u7a92\u606f\uff0c\u53c8\u60ca\u53c8\u6025\uff0c\u4f46\u82e6\u4e8e\u624b\u811a\u4e0d\u80fd \u5c55\u52a8\uff0c\u7ec8\u4e8e\u660f\u4e86\u8fc7\u53bb\u2026\u2026 \u3000\u3000\u88ab\u6b32\u706b\u714e\u71ac\u7684\u602a\u4eba\uff0c\u5982\u540c\u75af\u72c2\uff0c\u5f00\u59cb\u52a8\u624b\u6495\u7834\u5a49\u513f\u8eab\u4e0a\u7684\u7ec7\u9526\u7ef8\u8863\uff0c\u4e00\u8fb9\u5634\u91cc\u547c\u547c\u7c97\u91cd \u7684\u5598\u606f\u2026\u2026 \u3000\u3000\u201c\u5636\uff01\u201d\u4ef7\u503c\u5343\u91d1\u7684\u9526\u8863\uff0c\u88ab\u602a\u4eba\u575a\u903e\u7cbe\u94a2\u7684\u5229\u722a\u4e00\u4e0b\u6495\u7834\u4e00\u6761\u5c3a\u4f59\u957f\u7684\u88ab\u53e3\uff0c\u5a49\u513f\u8d5b \u96ea\u6b3a\u971c\u7684\u80f4\u4f53\u5df2\u73b0\u9732\u51fa\u6765\u2026\u2026 \u3000\u3000\u5a49\u513f\u5a07\u5ae9\u5982\u82b1\u857e\uff0c\u5728\u660f\u8ff7\u4e2d\u773c\u770b\u8981\u906d\u5230\u72c2\u98ce\u66b4\u96e8\u7684\u6467\u6b8b\uff0c\u82b1\u843d\u6c34\u6d41\u7ea2\uff0c\u60e8\u4e0d\u5fcd\u7779\u2026\u2026 \u3000\u3000\u7a81\u542c\u4e00\u58f0\u51b7\u54fc\uff0c\u90a3\u4e3a\u5c55\u767d\u7597\u4f24\u7684\u602a\u4eba\uff0c\u98de\u8eab\u63a0\u81f3\uff0c\u5355\u6307\u75be\u4f38\uff0c\u731b\u7136\u70b9\u4e2d\u62b1\u7d27\u5a49\u513f\u7684\u602a\u4eba \u80cc\u540e\u201c\u7cbe\u4fc3\u7a74\u201d\u3002 \u3000\u3000\u201c\u627c\uff01\u201d\u4e00\u58f0\u5589\u9e23\uff0c\u62b1\u7d27\u5a49\u513f\u7684\u602a\u4eba\u5e94\u6307\u800c\u5012\u3002\u4e3a\u5c55\u767d\u7597\u4f24\u7684\u602a\u4eba\uff0c\u7d27\u8ddf\u7740\u53c8\u8fde\u62cd\u5012\u5730 \u602a\u4eba\u7684\u201c\u957f\u5f3a\u201d\u3001\u201c\u7075\u53f0\u201d\u3001\u201c\u80be\u95e8\u201d\u4e09\u5927\u8981\u7a74\uff0c\u7136\u540e\u63d0\u8d77\u5012\u5730\u602a\u4eba\uff0c\u98de\u8eab\u63a0\u5165\u5de6\u9996\u5185\u5ba4\uff0c \u6380\u5f00\u68fa\u76d6\uff0c\u628a\u4ed6\u4e22\u8fdb\u68fa\u5185\u3002 \u3000\u3000\u8fd9\u602a\u4eba\u8eab\u5feb\u5982\u98ce\uff0c\u6643\u773c\u53c8\u628a\u5a49\u513f\u63d0\u8d77\uff0c\u5173\u8fdb\u53f3\u9996\u5185\u5ba4\u7684\u68fa\u6750\u91cc\u8fb9\uff0c\u628a\u6389\u5728\u5730\u4e0b\u7684\u300a\u9501\u9aa8 \u9500\u9b44\u5929\u4f5b\u5377\u300b\u62fe\u8d77\u6765\u88c5\u8fdb\u6000\u5185\uff0c\u4f7f\u5385\u5802\u91cc\u597d\u50cf\u6ca1\u6709\u53d1\u751f\u8fc7\u4efb\u4f55\u4e8b\u4e00\u6837\uff0c\u7136\u540e\u53c8\u731b\u62cd\u5c55\u767d\u5468\u8eab \u5341\u4e8c\u5904\u7a74\u9053\uff0c\u3002 \u3000\u3000\u5c55\u767d\u6e05\u9192\u8fc7\u6765\uff0c\u5f20\u773c\u770b\u5230\u8fd9\u9b3c\u9b45\u4f3c\u7684\u602a\u4eba\uff0c\u4e00\u65f6\u4e4b\u95f4\u695e\u4e86\u3002 \u3000\u3000\u201c\u5c55\u5c0f\u4fa0\uff0c\u8fd8\u8ba4\u8bc6\u8001\u592b\u5427\uff1f\u201d \u3000\u3000\u5c55\u767d\u731b\u7136\u8bb0\u8d77\u5728\u5bc6\u677e\u6797\u5148\u593a\u81ea\u5df1\u7684\u5251\uff0c\u540e\u6765\u53c8\u649e\u6811\u81ea\u6740\u4e0d\u6b7b\u7684\u4e24\u4e2a\u602a\u4eba\u6765\u3002\u6014\u6014\u5730\u95ee \u9053\uff1a\u201c\u6211\u600e\u4e48\u4f1a\u5230\u4e86\u8fd9\u91cc\u6765\uff1f\u4f60\u90a3\u4f4d\u540c\u4f34\u5462\uff1f\u201d \u3000\u3000\u602a\u4eba\u6447\u4e86\u6447\u5934\u3002\u8bf4\u9053\uff1a\u201c\u6211\u90a3\u4f4d\u540c\u4f34\u5916\u51fa\u672a\u5f52\uff0c\u81f3\u4e8e\u8bf4\u4f60\u600e\u4e48\u5230\u4e86\u8fd9\u91cc\u6765\uff0c\u90a3\u8fd8\u5f97\u8981\u95ee \u4f60\u81ea\u5df1\uff01\u201d \u3000\u3000\u201c\u95ee\u6211\u81ea\u5df1\uff1f\u201d\u5c55\u767d\u5982\u5760\u96fe\u4e2d\uff0c\u6697\u60f3\u5728\u5c0f\u9547\u4e0a\u4e0e\u7ea2\u8138\u8001\u8005\u5bf9\u4e86\u4e09\u638c\uff0c\u53ea\u89c9\u5947\u70ed\u96be\u6328\u4fbf\u660f \u4e86\u8fc7\u53bb\uff0c\u600e\u4f1a\u5230\u4e86\u8fd9\u602a\u4eba\u5c45\u4e2d\uff1f\u4e8e\u662f\u7075\u673a\u4e00\u52a8\uff0c\u95ee\u9053\uff1a\u8001\u524d\u8f88\uff0c\u662f\u60a8\u6551\u4e86\u6211\uff1f\u201d \u3000\u3000\u201c\u8001\u592b\u4e5f\u4e0d\u77e5\u662f\u8c01\u6551\u4e86\u4f60\uff0c\u602a\u4eba\u8bf4\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u5728\u8fd9\u623f\u4e2d\u8001\u592b\u5012\u662f\u52a9\u4e86\u4f60\u4e00\u81c2\u4e4b\u529b\u3002\u5c55 \u5c0f\u4fa0\u4f60\u5148\u8bf4\u8bf4\u770b\uff0c\u6211\u4eec\u5206\u624b\u540e\uff0c\u4f60\u53c8\u5230\u8fc7\u4e9b\u4ec0\u4e48\u5730\u65b9\uff1f\u201d \u3000\u3000\u4e8e\u662f\uff0c\u5c55\u767d\u628a\u6240\u7ecf\u8fc7\u7684\u4e8b\u5168\u8bf4\u4e86\u3002\u602a\u4eba\u957f\u53f9\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8fd9\u771f\u662f\u5929\u5047\u5176\u7f18\uff0c\u8001\u592b\u51e0\u6b21\u81ea \u6740\u672a\u6b7b\uff0c\u7559\u4e0b\u6b8b\u547d\uff0c\u53cd\u800c\u4e3a\u6069\u4eba\u4e4b\u540e\uff0c\u7565\u5c3d\u7ef5\u8584\uff01\u201d\u8bf4\u7740\u7ad9\u8d77\u8eab\u6765\uff0c\u5f15\u5c55\u767d\u5230\u5c55\u4e91\u5929\u795e\u4f4d\u4e4b \u524d\u8bf4\u9053\uff1a\u8001\u592b\u8fd9\u4f4d\u6069\u516c\uff0c\u5c31\u662f\u4f60\u7684\u7236\u4eb2\u4e86\uff01\u201d \u3000\u3000\u5c55\u767d\u770b\u5230\u7236\u4eb2\u7684\u795e\u4f4d\uff0c\u4e0d\u7531\u6d41\u4e0b\u6cea\u6765\uff0c\u53cc\u819d\u8dea\u5012\uff0c\u8fde\u78d5\u4e86\u4e09\u4e2a\u5934\u3002\u60f3\u8d77\u81ea\u5df1\u51e0\u5e74\u6765\u5386\u53d7 \u7684\u6298\u78e8\u5c48\u8fb1\uff0c\u4e0d\u7531\u4f0f\u5730\u653e\u58f0\u75db\u54ed\u8d77\u6765\u3002 \u3000\u3000\u90a3\u602a\u4eba\u770b\u5c55\u767d\u54ed\u5f97\u60b2\u5207\uff0c\u60f3\u8d77\u81ea\u5df2\u9690\u59d3\u57cb\u540d\uff0c\u4e0d\u89c1\u5929\u65e5\uff0c\u4f4f\u6b7b\u4eba\u4e4b\u5c45\uff0c\u7761\u68fa\u6750\u4e4b\u5e8a\uff0c\u4e5f \u672a\u80fd\u4e3a\u6069\u516c\u62a5\u4ec7\uff0c\u75db\u5fc3\u75be\u9996\uff0c\u7adf\u4e5f\u966a\u7740\u5c55\u767d\u987f\u8db3\u9524\u80f8\uff0c\u568e\u9676\u5927\u54ed\u8d77\u6765\u3002 \u7b2c\u4e8c\u5341\u7ae0\u3000\u8c01\u662f\u4ec7\u4eba>> \u53e4\u9f99\u300a\u5251\u5ba2\u884c\u300b \u7b2c\u4e8c\u5341\u7ae0\u3000\u8c01\u662f\u4ec7\u4eba \u3000\u3000\u4e8c\u4eba\u54ed\u4e86\u534a\u5929\uff0c\u602a\u4eba\u7a81\u7136\u4ef0\u8138\u4e00\u58f0\u957f\u5578\uff0c\u4f3c\u662f\u5410\u51fa\u4e86\u6ee1\u8154\u60b2\u6124\uff0c\u7528\u624b\u4e00\u62b9\u8138\u4e0a\u6cea\u75d5\uff0c\u6602 \u58f0\u8bf4\u9053\uff1a\u201c\u82f1\u96c4\u6709\u6cea\u4e0d\u8f7b\u5f39\uff01\u5c0f\u6069\u516c\uff0c\u4e0d\u8981\u54ed\u4e86\uff01\u6211\u6d3b\u6b7b\u4eba\u8fd8\u6709\u51e0\u53e5\u8981\u7d27\u7684\u8bdd\u544a\u8bc9\u4f60\uff01\u201d \u3000\u3000\u5c55\u767d\u4e00\u9635\u5927\u54ed\uff0c\u5fc3\u5934\u79ef\u90c1\u5df2\u503e\u5410\u4e0d\u5c11\uff0c\u95fb\u8a00\u6b62\u4f4f\u60b2\u58f0\uff0c\u7ad9\u8d77\u8eab\u8bf4\u9053\uff1a\u8001\u524d\u8f88\u4e0d\u5fc5\u5ba2\u6c14\uff0c \u6709\u4ec0\u4e48\u8bdd\u5c3d\u7ba1\u5429\u5490\u5c31\u662f\uff01\u201d \u3000\u3000\u201c\u60ed\u6127\uff01\u201d\u6d3b\u6b7b\u4eba\u4ef0\u5929\u4e00\u53f9\u8bf4\u9053\uff1a\u6211\u5f1f\u5144\u8eab\u53d7\u6069\u516c\u5927\u6069\uff0c\u7ec8\u8eab\u96be\u62a5\uff0c\u6ca1\u60f3\u5230\u6069\u516c\u60e8\u6b7b\uff0c \u6211\u5f1f\u5144\u8fde\u6740\u5bb3\u6069\u516c\u7684\u4ec7\u4eba\u662f\u8c01\u90fd\u4e0d\u77e5\u9053\uff0c\u6211\u5f1f\u5144\u8fd8\u6709\u4ec0\u4e48\u989c\u9762\u6d3b\u5728\u4e16\u4e0a\uff1f\u672c\u60f3\u81ea\u788e\u5929\u7075\u8ffd\u968f \u6069\u516c\u4e8e\u4e5d\u6cc9\uff0c\u4f46\u53c8\u60f3\u67e5\u8bbf\u6740\u5bb3\u6069\u516c\u7684\u4ec7\u4eba\u4e3a\u6069\u516c\u62a5\u4ec7\uff0c\u624d\u5fcd\u8fb1\u5077\u751f\u6d3b\u4e86\u4e0b\u6765\uff0c\u6211\u4e8c\u4eba\u5e9f\u53bb\u540d \u53f7\uff0c\u4ee5\u2018\u6d3b\u6b7b\u4eba\u2019\u4e0e\u2018\u6b7b\u6d3b\u4eba\u2019\u81ea\u79f0\uff0c\u4e00\u65e5\u4e0d\u80fd\u4e3a\u6069\u516c\u62a5\u4ec7\uff0c\u4fbf\u4e00\u65e5\u4e0d\u79f0\u540d\u9053\u59d3\u3002\u53ef\u662f\u6740\u5bb3 \u6069\u516c\u7684\u4ec7\u4eba\u624b\u6bb5\u65e2\u72e0\u6bd2\uff0c\u884c\u4e8b\u53c8\u6781\u7aef\u9690\u5bc6\uff0c\u7ecf\u8fc7\u6211\u5f1f\u5144\u5341\u5e74\u6765\u7684\u660e\u5bdf\u6697\u8bbf\uff0c\u624d\u7ea6\u7565\u77e5\u9053\u6740\u5bb3 \u6069\u516c\u7684\u7adf\u662f\u6c5f\u6e56\u4e0a\u516d\u4e2a\u58f0\u540d\u663e\u8d6b\u7684\u6b66\u6797\u9ad8\u624b\u6240\u4e3a\uff01\u201d \u3000\u3000\u6d3b\u6b7b\u4eba\u8bf4\u5230\u8fd9\u91cc\u987f\u4e86\u4e00\u987f\uff0c\u5c55\u767d\u542c\u5230\u5c06\u8981\u8bf4\u51fa\u6740\u5bb3\u7236\u4eb2\u7684\u4ec7\u4eba\u7684\u59d3\u540d\u65f6\uff0c\u7adf\u6fc0\u52a8\u5f97\u6d51\u8eab \u53d1\u6296\uff0c\u4e00\u8fb9\u5636\u54d1\u5730\u53eb\u9053\uff1a\u8bf4\u4e0b\u53bb\uff01\u8001\u524d\u8f88\uff0c\u8bf4\u4e0b\u53bb\u2026\u2026\u201d \u3000\u3000\u201c\u5509\uff01\u201d\u6d3b\u6b7b\u4eba\u6447\u5934\u957f\u53f9\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u516d\u4e2a\u4eba\u539f\u4e0e\u4f60\u7236\u4eb2\u4e49\u7ed3\u91d1\u5170\uff0c\u5728\u6c5f\u6e56\u9053\u4e0a\u5408 \u79f0\u4e2d\u539f\u4e03\u4fa0\uff01\u60f3\u4e0d\u5230\u4e3a\u4e86\u6d1e\u5ead\u6e56\u7554\u4e00\u5b97\u4ef7\u503c\u8fde\u57ce\u7684\u5b9d\u85cf\uff0c\u7adf\u9634\u8c0b\u9677\u5bb3\uff0c\u628a\u4f60\u7236\u4eb2\u6697\u7b97\u6740 \u6b7b\uff01\u2026\u2026\u201d \u3000\u3000\u201c\u8001\u524d\u8f88\uff01\u4f60\u8bf4\u5440\uff01\u4ed6\u4eec\u662f\u8c01\uff1f\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u201d\u5c55\u767d\u89c1\u6d3b\u6b7b\u4eba\u8bf4\u5230\u8fd9\u91cc\uff0c\u7a00\u5618\u60b2\u53f9\uff0c\u4e0d \u518d\u8bf4\u4e0b\u53bb\uff0c\u4e0d\u7531\u5f97\u7740\u6025\u5730\u53eb\u9053\u3002 \u3000\u3000\u201c\u4ed6\u4eec\u516d\u4eba\u4e4b\u4e2d\uff0c\u9664\u4e86\u4e00\u4eba\u8fdc\u9041\u6d77\u5916\uff0c\u4e0d\u77e5\u6240\u7ec8\uff0c\u5176\u4f59\u4e94\u4eba\u90fd\u6210\u4e86\u5f53\u4eca\u6b66\u6797\u6700\u5927\u7684\u8c6a\u95e8 \u4e86\u3002\u82cd\u5929\u5440\uff01\u4e3a\u4ec0\u4e48\u597d\u4eba\u4e0d\u5f97\u597d\u62a5\uff0c\u574f\u4eba\u53cd\u800c\u98de\u9ec4\u817e\u8fbe\u5462\uff1f\u2026\u2026\u201d \u3000\u3000\u201c\u8001\u524d\u8f88\uff01\u4f60\u5feb\u8bf4\u51fa\u4ed6\u4eec\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u201d\u5c55\u767d\u89c1\u6d3b\u6b7b\u4eba\u4e00\u5473\u5730\u60b2\u53f9\u611f\u6982\uff0c\u8bf4\u4e86\u534a\u5929\u8fd8\u6ca1 \u6709\u8bf4\u51fa\u6740\u6b7b\u7236\u4eb2\u7684\u4ec7\u4eba\u662f\u8c01\uff0c\u4e0d\u7531\u50ac\u4fc3\u4ed6\u5feb\u8bf4\u3002 \u3000\u3000\u201c\u4e00\u4e2a\u662f\u9547\u6c5f\u7684\u9738\u738b\u97ad\u6a0a\u975e\uff01\u201d\u6d3b\u6b7b\u4eba\u53cc\u773c\u4e00\u77aa\uff0c\u65e0\u9650\u60b2\u6124\u5730\u8bf4\u9053\uff1a\u201c\u56db\u4e2a\u662f\u5f53\u4eca\u540d\u91cd \u6b66\u6797\u7684\u6b66\u6797\u56db\u516c\u5b50\u2026\u2026\u201d \u3000\u3000\u201c\u6b66\u6797\u56db\u516c\u5b50\uff1f\u201d\u5c55\u767d\u5934\u8111\u8f70\u5730\u4e00\u9707\uff0c\u63a2\u624b\u6293\u4f4f\u6d3b\u6b7b\u4eba\u7684\u81c2\u8180\uff0c\u53cc\u76ee\u51e0\u6b32\u6d41\u8840\uff0c\u77aa\u89c6\u7740 \u6d3b\u6b7b\u4eba\u98a4\u58f0\u95ee\u9053\uff1a\u201c\u7adf\u662f\u6b66\u6797\u56db\u516c\u5b50\uff1f\u201d \u3000\u3000\u6d3b\u6b7b\u4eba\u6c89\u91cd\u5730\u70b9\u4e86\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u6b66\u6797\u56db\u516c\u5b50\u7684\u7236\u4eb2\u2026\u2026\uff01\u201d \u3000\u3000\u201c\u51cc\u98ce\u516c\u5b50\u4e4b\u7236\u3001\u6458\u661f\u624b\u6155\u5bb9\u6db5\uff1f\u201d\u5c55\u767d\u4e00\u5b57\u4e00\u987f\u5730\u543c\u58f0\u95ee\u9053\u3002 \u3000\u3000\u6d3b\u6b7b\u4eba\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u201c\u5b89\u4e50\u516c\u5b50\u4e4b\u7236\u3001\u4e7e\u5764\u638c\u4e91\u5b97\u9f99\uff1f\u201d\u5c55\u767d\u4ecd\u662f\u4e00\u5b57\u4e00\u987f\u5730\u95ee\u9053\u3002 \u3000\u3000\u6d3b\u6b7b\u4eba\u70b9\u4e86\u70b9\u5934\uff0c\u4ecd\u7136\u662f\u4e00\u8bed\u4e0d\u53d1\u3002 \u3000\u3000\u201c\u7aef\u65b9\u516c\u5b50\u4e4b\u7236\u3001\u6df7\u5143\u6307\u53f8\u7a7a\u664b\uff1f\u201d\u5c55\u767d\u7d27\u63a5\u7740\u95ee\u9053\uff1a\u201c\u7965\u9e9f\u516c\u5b50\u4e4b\u7236\u3001\u9752\u86a8\u795e\u91d1 \u4e5d\uff1f\u201d \u3000\u3000\u6d3b\u6b7b\u4eba\u53ea\u662f\u9762\u8272\u51dd\u91cd\u5730\u70b9\u5934\uff0c\u7b49\u5230\u5c55\u767d\u95ee\u5b8c\uff0c\u4ed6\u53c8\u52a0\u4e0a\u4e00\u53e5\uff1a\u201c\u8fd8\u6709\u4e00\u4e2a\uff0c\u5c31\u662f\u90a3\u8fdc\u8d70 \u6d77\u5916\u3001\u4e0b\u843d\u4e0d\u660e\u7684\u94f6\u6247\u5b50\u67f3\u795f\u539a\u2026\u2026\u201d \u3000\u3000\u201c\u54ce\u5440\uff01\u201d\u6d3b\u6b7b\u4eba\u58f0\u672a\u843d\u5730\uff0c\u5c55\u767d\u5df2\u5927\u53eb\u4e00\u58f0\uff0c\u4ef0\u9762\u8dcc\u5012\uff0c\u4e00\u65f6\u6c14\u660f\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u6d3b\u6b7b\u4eba\u4e00\u624b\u53c8\u628a\u5c55\u767d\u63d0\u4e86\u8d77\u6765\uff0c\u5355\u638c\u8d2f\u6ce8\u771f\u529b\uff0c\u5728\u5c55\u767d\u540e\u5fc3\u201c\u547d\u95e8\u201d\u7a74\u4e0a\u4e00\u9635\u6309\u6469\uff0c\u5c55 \u767d\u53c8\u60a0\u60a0\u9192\u8f6c\u8fc7\u6765\uff0c\u4e0d\u7531\u661f\u76ee\u6d41\u6cea\uff0c\u9893\u7136\u8bf4\u9053\uff1a\u201c\u8001\u524d\u8f88\uff0c\u770b\u6765\u665a\u8f88\u8fd9\u6740\u7236\u4e4b\u4ec7\uff0c\u662f\u62a5\u4e0d\u6210 \u4e86\uff1f\u201d \u3000\u3000\u201c\u5509\uff01\u201d\u6d3b\u6b7b\u4eba\u957f\u53f9\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u6069\u516c\uff01\u542c\u5230\u8fd9\u4e9b\u4eba\u7684\u540d\u5b57\uff0c\u4e0d\u8981\u8bf4\u5c0f\u6069\u516c\u611f\u5230\u6c14 \u9981\uff0c\u5c31\u8fde\u6211\u5144\u5f1f\u4e8c\u4eba\u77e5\u9053\u4e4b\u540e\uff0c\u4e5f\u89c9\u5f97\u4e3a\u6069\u516c\u590d\u4ec7\u65e0\u671b\uff0c\u8981\u4e0d\u7136\u5728\u5bc6\u677e\u6797\u5185\uff0c\u6211\u5f1f\u5144\u4e3a\u4ec0\u4e48 \u8981\u649e\u6811\u81ea\u6740\u5462\uff01\u201d \u3000\u3000\u6d3b\u6b7b\u4eba\u8fd9\u51e0\u53e5\u8bdd\uff0c\u8fd8\u771f\u662f\u53c8\u9f13\u4e86\u5c55\u767d\u7684\u51e0\u5206\u52c7\u6c14\uff0c\u4ed6\u5fc3\u4e2d\u6697\u60f3\uff1a\u81ea\u5df1\u600e\u80fd\u8fd9\u4e48\u6ca1\u9aa8\u6c14\uff1f \u9047\u5230\u56f0\u96be\u4fbf\u754f\u7f29\u8d77\u6765\uff01\u7559\u4e0b\u6709\u7528\u4e4b\u8eab\uff0c\u53ea\u8981\u81ea\u5df2\u523b\u82e6\u7ec3\u529f\uff0c\u5b66\u6b66\u7565\u6709\u6240\u6210\uff0c\u5c31\u662f\u4e0d\u80fd\u628a\u6740\u7236 \u4ec7\u4eba\u4e00\u4e00\u65a9\u5c3d\u6740\u7edd\uff0c\u6ce1\u8981\u62fc\u7740\u6027\u547d\u53bb\u6740\u4e00\u4e2a\u7b97\u4e00\u4e2a\uff0c\u8ba9\u5929\u4e0b\u6b66\u6797\u9053\u4e5f\u660e\u767d\u7236\u4eb2\u8fd8\u6709\u8fd9\u4e48\u4e00\u4e2a \u540e\u4ee3\u2026\u2026\u201d \u3000\u3000\u5c55\u767d\u60f3\u5230\u8fd9\u91cc\uff0c\u89e6\u52a8\u7075\u673a\uff0c\u6251\u8eab\u671d\u6d3b\u6b7b\u4eba\u8dea\u5012\uff0c\u4e07\u5206\u8bda\u6073\u5730\u8bf4\u9053\uff1a\u201c\u591a\u8499\u524d\u8f88\u6559\u8bf2\uff0c\u4f7f \u665a\u8f88\u987f\u5f00\u8305\u585e\uff0c\u8001\u524d\u8f88\u65e2\u662f\u4e0e\u5148\u7236\u6709\u4ea4\u60c5\uff0c\u5c31\u8bf7\u6536\u665a\u8f88\u505a\u4e2a\u5f1f\u5b50\u5427\uff01\u665a\u8f88\u8ddf\u524d\u8f88\u5b66\u597d\u6b66\u529f\uff0c \u4e5f\u597d\u53bb\u4e3a\u7236\u62a5\u4ec7\uff01\u2026\u2026\u201d \u3000\u3000\u6d3b\u6b7b\u4eba\u89c1\u5c55\u81ea\u5411\u4ed6\u8dea\u4e0b\uff0c\u614c\u4e86\u624b\u811a\uff0c\u62c9\u5c55\u767d\u4e0d\u53ca\uff0c\u81ea\u5df1\u4e5f\u5411\u5c55\u81ea\u8dea\u5012\uff0c\u8fde\u5fd9\u8bf4\u9053\uff1a\u201c\u5c0f \u6069\u516c\u5feb\u5feb\u8bf7\u8d77\uff0c\u4f60\u8fd9\u6837\u4e00\u6765\uff0c\u5c82\u4e0d\u662f\u6298\u6740\u8001\u673d\u4e86\uff01\u201d \u3000\u3000\u5c55\u767d\u4ee5\u4e3a\u6d3b\u6b7b\u4eba\u4e0d\u80af\u6536\u7eb3\u81ea\u5df2\uff0c\u8d8a\u4e0d\u80af\u8d77\u6765\uff0c\u6700\u540e\u6d3b\u6b7b\u4eba\u5f3a\u628a\u5c55\u767d\u62b1\u8d77\u6765\uff0c\u6309\u5c55\u767d\u5728\u6905 \u4e0a\u5750\u4e0b\uff0c\u624d\u6b63\u5bb9\u8bf4\u9053\uff1a\u975e\u662f\u8001\u673d\u63a8\u8f9e\uff0c\u4e0d\u80af\u6559\u4f60\uff0c\u8fd9\u91cc\u8fb9\u5b9e\u6709\u91cd\u5927\u539f\u56e0\u3002\u4ee5\u8001\u673d\u6b66\u529f\u6765\u8bf4\uff0c \u5bf9\u4ed8\u4eba\u5bb6\u4e8c\u4e09\u6d41\u7684\u811a\u8272\uff0c\u8fd8\u6709\u7528\u5904\uff0c\u5374\u51b3\u4e0d\u662f\u4eba\u5bb6\u4e00\u6d41\u9ad8\u624b\u7684\u5bf9\u624b\uff0c\u5e38\u8a00\u9053\u2018\u53d6\u6cd5\u4e4e\u4e0a\u6d41\u4e8e \u4e2d\u2019\uff0c\u5c31\u662f\u8001\u673d\u628a\u538b\u5e95\u7684\u529f\u592b\u90fd\u638f\u51fa\u6765\uff0c\u628a\u4f60\u6559\u6210\u4e86\u8fd8\u662f\u65e0\u7528\uff0c\u5c24\u5176\u662f\u6b66\u6797\u4e2d\u4e00\u65e6\u62dc\u5e08\uff0c\u4fbf \u4e0d\u80fd\u89c1\u5f02\u601d\u8fc1\uff0c\u518d\u53bb\u6539\u6295\u522b\u7684\u5e08\u7236\uff0c\u8fd9\u5c82\u4e0d\u662f\u8bef\u4e86\u5c0f\u6069\u516c\u7684\u524d\u7a0b\uff1f\u6b64\u5176\u4e00\u3002\u518d\u8005\uff0c\u8001\u673d\u5144\u5f1f \u4e8c\u4eba\u4e0e\u6069\u516c\u5c55\u5927\u4fa0\u4e3b\u4ec6\u7684\u540d\u4efd\uff0c\u4e25\u683c\u8bf4\u6765\uff0c\u5c0f\u6069\u516c\u8fd8\u662f\u8001\u5974\u7684\u5c0f\u4e3b\u4eba\uff0c\u5974\u4ec6\u600e\u80fd\u505a\u4e3b\u4eba\u7684\u5e08 \u7236\uff1f\u201d \u3000\u3000\u5c55\u767d\u4e00\u542c\u6d3b\u6b7b\u4eba\u6240\u8a00\u751a\u662f\u6709\u7406\uff0c\u77e5\u4e0d\u80fd\u5f3a\u6c42\uff0c\u968f\u5373\u9ed8\u7136\u4e0d\u8bed\uff0c\u8138\u4e0a\u6d41\u9732\u51fa\u5931\u671b\u7684\u795e\u8272\u3002 \u3000\u3000\u201c\u5176\u5b9e\uff0c\u5c0f\u6069\u516c\u4e5f\u7528\u4e0d\u7740\u5931\u671b\uff0c\u4f60\u8eab\u4e0a\u4e0d\u662f\u5e26\u7740\u6bd4\u540d\u5e08\u8fd8\u8981\u9ad8\u660e\u7684\u6b66\u529f\u79d8\u5f55\u5417\uff1f\u201d \u3000\u3000\u6d3b\u6b7b\u4eba\u8fd9\u4e00\u8bf4\uff0c\u5c55\u767d\u845b\u7136\u9192\u609f\uff0c\u60f3\u8d77\u6000\u4e2d\u7684\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\uff0c\u7acb\u523b\u4f38\u624b\u53bb\u6478\uff0c\u8c01\u77e5 \u4e00\u6478\u6478\u4e86\u4e00\u4e2a\u7a7a\uff0c\u53ea\u5413\u5f97\u5fc3\u4e2d\u4e00\u51c9\u2026\u2026 \u3000\u3000\u6d3b\u6b7b\u4eba\u5374\u4ece\u6000\u4e2d\u628a\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\u638f\u51fa\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u5929\u4e0b\u7b2c\u4e00\u5947\u4e66\uff0c\u5c0f\u6069\u516c\u4ece\u4f55 \u5904\u5f97\u6765\uff1f\u201d\u201c\u662f\u4e00\u4e2a\u53eb\u96f7\u5927\u53d4\u7684\u4eba\u9001\u7ed9\u6211\u7684\u3002\u201d\u5c55\u767d\u89c1\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\u672a\u4e22\uff0c\u8fd9\u624d\u653e\u5fc3 \u4e0b\u6765\u3002 \u3000\u3000\u5728\u4e8c\u4eba\u8bf4\u8bdd\u7684\u5f53\u513f\uff0c\u6d3b\u6b7b\u4eba\u628a\u4e66\u9875\u7ffb\u5f00\uff0c\u624d\u770b\u4e86\u4e24\u773c\uff0c\u8d76\u5feb\u53c8\u628a\u4e66\u4e2d\u5408\u4e0a\uff0c\u95ed\u76ee\u8c03\u606f\u4e86 \u4e00\u4f1a\uff0c\u624d\u7741\u5f00\u773c\u775b\u8bf4\u9053\uff1a\u201c\u597d\u5389\u5bb3\uff01\u8fd9\u4e66\u53ef\u662f\u6700\u6613\u5f15\u4eba\u5b9a\u706b\u5165\u9b54\uff0c\u5c0f\u6069\u516c\u4e2d\u7eaa\u8f7b\u8f7b\uff0c\u4e0d\u77e5\u600e \u4e48\u770b\u7684\uff1f\u201d \u3000\u3000\u201c\u665a\u8f88\u5728\u9ed1\u6697\u4e2d\u7528\u624b\u6478\u7684\u3002\u201d\u5c55\u767d\u6beb\u65e0\u5fc3\u673a\uff0c\u5bf9\u4efb\u4f55\u4e8b\uff0c\u90fd\u662f\u6709\u4ec0\u4e48\u8bf4\u4ec0\u4e48\u3002 \u3000\u3000\u6d3b\u6b7b\u4eba\u542c\u5c55\u767d\u8bf4\u201c\u7528\u624b\u6478\u4e66\u201d\uff0c\u4f3c\u662f\u4e0d\u4fe1\uff0c\u4f46\u5f53\u4ed6\u4f38\u6307\u4e00\u6478\uff0c\u7acb\u523b\u604d\u7136\u5927\u609f\uff0c\u4e0d\u7531\u8138\u4e0a \u95ea\u8fc7\u4e00\u4e1d\u8d2a\u5a6a\u7684\u795e\u8272\u3002\u5c55\u767d\u4e5f\u770b\u51fa\u6d3b\u6b7b\u4eba\u8d2a\u5a6a\u4e4b\u8272\u3002 \u3000\u3000\u6d3b\u6b7b\u4eba\u628a\u4e66\u4ea4\u8fd8\u5c55\u767d\uff0c\u4ef0\u5934\u601d\u7d22\u4e86\u4e00\u4f1a\uff0c\u53c8\u95ee\u9053\uff1a\u201c\u96f7\u5927\u53d4\u662f\u4f55\u4eba\uff1f\u7ade\u5982\u6b64\u6177\u6168\uff01\u201d \u3000\u3000\u5c55\u767d\u628a\u96f7\u5927\u53d4\u7684\u5f62\u72b6\u63cf\u8ff0\u4e86\u4e00\u756a\u3002\u201c\u5662\uff01\u201d\u6d3b\u6b7b\u4eba\u604d\u7136\u8bf4\u9053\uff1a\u201c\u96f7\u9707\u8fdc\uff01\u201d \u3000\u3000\u201c\u8001\u524d\u8f88\u8ba4\u8bc6\uff1f\u201d\u5c55\u767d\u53cd\u95ee\u9053\u3002 \u3000\u3000\u201c\u600e\u4e48\u4e0d\u8ba4\u8bc6\uff01\u201d\u6d3b\u6b7b\u5927\u7709\u98de\u8272\u821e\u5730\u8bf4\u9053\uff1a\u4ed6\u548c\u4f60\u53c8\u662f\u6700\u597d\u7684\u670b\u53cb\uff0c\u60f3\u5f53\u5e74\u6211\u5144\u5f1f\u8ffd\u968f \u6069\u516c\u3001\u884c\u9053\u6c5f\u6e56\u65f6\uff0c\u4ed6\u548c\u6211\u4eec\u5e38\u5728\u4e00\u5757\uff01\u2026\u2026\u201d \u3000\u3000\u201c\u8001\u524d\u8f88\u662f\u4f55\u4eba\uff1f\u201d\u5c55\u767d\u63d2\u53e3\u95ee\u9053\uff1a\u201c\u80fd\u5426\u5c06\u5927\u540d\u544a\u77e5\u665a\u8f88\uff0c\u4e5f\u4e0d\u6789\u5728\u6b64\u76f8\u9047\u4e00\u573a\uff01\u201d \u3000\u3000\u6d3b\u6b7b\u4eba\u8138\u8272\u53c8\u9eef\u6de1\u4e0b\u6765\uff0c\u957f\u53f9\u8bf4\u9053\uff1a\u201c\u8fd9\u4e00\u70b9\u8981\u8bf7\u5c0f\u6069\u516c\u539f\u8c05\uff0c\u56e0\u4e3a\u6211\u5f1f\u5144\u53d1\u4e0b\u91cd\u8a93\uff0c \u5728\u672a\u80fd\u7ed9\u6069\u516c\u62a5\u4ec7\u4e4b\u524d\uff0c\u6c38\u4e0d\u63d0\u540d\u9053\u59d3\u3002\u4ee5\u540e\u4f60\u53ea\u53eb\u6211\u5f1f\u5144\u2018\u6d3b\u6b7b\u4eba\u2019\u3001\u2018\u6b7b\u6d3b\u4eba\u2019\u597d \u4e86\uff1f\u201d \u3000\u3000\u5c55\u767d\u89c1\u4ed6\u4e0d\u80af\u8bf4\u51fa\u59d3\u540d\uff0c\u4e5f\u4e0d\u597d\u52c9\u5f3a\uff0c\u987f\u4e86\u4e00\u4e0b\uff0c\u53c8\u95ee\u9053\uff1a\u201c\u8001\u524d\u8f88\uff0c\u600e\u4e48\u77e5\u9053\u665a\u8f88\u6740 \u7236\u7684\u4ec7\u4eba\u2026\u2026\u201d\u6d3b\u6b7b\u4eba\u4e0d\u7b49\u5c55\u767d\u95ee\u5b8c\uff0c\u4fbf\u63a5\u53e3\u8bf4\u9053\uff1a\u8fd9\u8981\u95ee\u2018\u795e\u9a74\u94c1\u80c6\u2019\u8463\u8001\u524d\u8f88\uff0c\u6211\u5f1f\u5144 \u4e8c\u4eba\u5148\u524d\u542c\u5230\u8fd9\u6d88\u606f\uff0c\u8fd8\u4e0d\u6562\u76f8\u4fe1\uff0c\u540e\u6765\u8463\u8001\u524d\u8f88\u524d\u6765\u8bc1\u5b9e\uff0c\u524d\u4e24\u5929\u6211\u5144\u5f1f\u4e8c\u4eba\u53c8\u9047\u5230\u5c0f\u6069 \u516c\uff0c\u4ece\u5c0f\u6069\u516c\u5251\u7a57\u4e0a\u770b\u5230\u4e86\u90a3\u679a\u2018\u9752\u86a8\u9556\u2019\u2014\u2014\u5c31\u662f\u90a3\u679a\u9752\u94dc\u5236\u94b1\u3002\u90a3\u662f\u2018\u9752\u86a8\u795e\u2019\u91d1\u4e5d\u7684 \u72ec\u95e8\u6697\u5668\uff0c\u8fd9\u6837\u6211\u5f1f\u5144\u624d\u4e0d\u5f97\u4e0d\u76f8\u60c5\uff0c\u90a3\u4f20\u8a00\u7adf\u662f\u4e8b\u5b9e\uff01\u201d \u3000\u3000\u201c\u795e\u9a74\u94c1\u80c6\uff01\u201d\u5c55\u767d\u5bfb\u601d\u9053\uff1a\u201c\u8463\u8001\u524d\u8f88\u662f\u4e0d\u662f\u4e00\u4e2a\u4e1d\u7ef5\u8d29\u5b50\u6a21\u6837\u7684\u9a91\u9a74\u8001\u4eba\uff1f\u201d \u3000\u3000\u6d3b\u6b7b\u4eba\u9053\uff1a\u6b63\u662f\u4ed6\u8001\u4eba\u5bb6\u4e86\uff0c\u201d \u3000\u3000\u6d3b\u6b7b\u4eba\u8bf4\u5230\u8fd9\u91cc\uff0c\u7a81\u7136\u60f3\u8d77\u4ec0\u4e48\u91cd\u5927\u7684\u4e8b\u60c5\u4e00\u822c\uff0c\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u795e\u9a74\u94c1\u80c6\u8463\u8001\u524d\u8f88\uff0c\u4e3a \u524d\u8f88\u5f02\u4eba\u7855\u679c\u4ec5\u5b58\u7684\u4e00\u4f4d\u4e86\uff0c\u5e74\u7eaa\u6050\u6015\u6709\u4e00\u767e\u591a\u5c81\u4ee5\u4e0a\u5427\uff0c\u4e09\u7c92\u94c1\u80c6\uff0c\u516b\u516b\u516d\u5341\u56db\u624b\u2018\u5947\u5f62 \u8ffd\u638c\u2019\uff0c\u6b66\u529f\u4e4b\u9ad8\uff0c\u5f53\u4eca\u6b66\u6797\u6050\u6015\u5df2\u65e0\u51fa\u5176\u53f3\u8005\uff0c\u521a\u624d\u5c0f\u6069\u516c\u8bf4\u8981\u62dc\u5e08\uff0c\u4f55\u4e0d\u5c31\u53bb\u6c42\u4ed6\u8001\u4eba \u5bb6\u6536\u5f55\uff1f\u201d \u3000\u3000\u5c55\u767d\u4e00\u542c\u6709\u8fd9\u6761\u660e\u8def\uff0c\u5373\u523b\u5174\u594b\u5730\u95ee\u9053\uff1a\u4ed6\u8001\u4eba\u5bb6\u4f4f\u5728\u4ec0\u4e48\u5730\u65b9\uff1f\u201d \u3000\u3000\u201c\u4ed6\u8001\u4eba\u5bb6\u867d\u7136\u6e38\u8e2a\u65e0\u5b9a\uff0c\u201d\u6d3b\u6b7b\u4eba\u8bf4\uff1a\u201c\u4f46\u7ecf\u5e38\u5728\u5357\u4eac\u71d5\u5b50\u77fe\u6c5f\u8fb9\uff0c\u5ca9\u5c71\u5341\u4e8c\u6d1e\u5b58 \u8eab\uff0c\u5c0f\u6069\u516c\u5230\u90a3\u91cc\u53bb\u6216\u80fd\u627e\u5230\u4ed6\u8001\u4eba\u5bb6\u2026\u2026\u201d \u3000\u3000\u5c55\u767d\u4e0d\u7b49\u6d3b\u6b7b\u4eba\u8bf4\u5b8c\uff0c\u8df3\u8d77\u8eab\u6765\uff0c\u5411\u6d3b\u6b7b\u4eba\u8eac\u8eab\u2014\u672d\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u665a\u8f88\u5c31\u6b64\u544a\u8f9e\uff01 \u5371\u96be\u4e4b\u95f4\u591a\u8499\u8001\u524d\u8f88\u76f8\u6551\uff0c\u53c8\u8499\u6307\u793a\u660e\u8def\uff0c\u4e00\u5207\u6069\u5178\uff0c\u5c55\u767d\u7262\u8bb0\u5fc3\u5e95\u4e86\u2026\u2026\u201d \u3000\u3000\u5c55\u767d\u4e00\u8fb9\u8bf4\uff0c\u4e00\u8fb9\u817e\u8eab\u5411\u95e8\u5916\u8dd1\u53bb\uff0c\u8bdd\u5b8b\u8bf4\u5b8c\uff0c\u4eba\u5df2\u8dc3\u51fa\u201c\u6b7b\u4eba\u5c45\u201d\u95e8\u5916\u4e86\u2026\u2026 \u3000\u3000\u201c\u5c0f\u6069\u516c\uff01\u2026\u2026\u201d\u6d3b\u6b7b\u4eba\u5728\u8eab\u540e\u6025\u53eb\uff0c\u60f3\u544a\u8bc9\u5c55\u767d\u8fd8\u6709\u540c\u6765\u7684\u5c11\u5973\uff0c\u4f46\u5ffd\u7136\u60f3\u5230\u4e00\u4e9b\u4e0d \u4fbf\u7684\u5730\u65b9\uff0c\u5f20\u5634\u6b32\u8a00\u53c8\u6b62\u2026\u2026\u5c31\u5728\u8fd9\u7565\u2014\u72b9\u8c6b\u4e4b\u95f4\uff0c\u5c55\u767d\u5df2\u5954\u4e0b\u5c71\u53bb\u4e86\u3002 \u3000\u3000\u5c55\u767d\u5fc3\u6025\u4f3c\u7bad\uff0c\u5954\u51fa\u201c\u6b7b\u4eba\u5c45\u201d\u5927\u95e8\uff0c\u8fde\u56de\u5934\u770b\u90fd\u672a\u56de\u5934\u770b\uff0c\u5728\u5c71\u5761\u4e0a\u4ed6\u4e5f\u770b\u5230\u90a3\u5339\u67a3 \u7ea2\u8272\u5927\u9a6c\u5728\u5403\u8349\uff0c\u5c55\u767d\u8fd8\u4ee5\u4e3a\u6d3b\u6b7b\u4eba\u7684\u9a6c\uff0c\u4ed6\u4e5f\u6ca1\u6709\u4ed4\u7ec6\u60f3\u60f3\u201c\u6d3b\u6b7b\u4eba\u201d\u90a3\u602a\u50cf\uff0c\u600e\u4e48\u4f1a\u6709 \u8fd9\u822c\u978d\u8f94\u9c9c\u660e\u7684\u795e\u9a8f\u5750\u9a91\u2026\u2026 \u7b2c\u4e8c\u5341\u4e00\u7ae0\u3000\u77ee\u4eba\u5947\u4eba\u201c\u4e09\u5bf8\u4e01\u201d>> \u53e4\u9f99\u300a\u5251\u5ba2\u884c\u300b \u7b2c\u4e8c\u5341\u4e00\u7ae0\u3000\u77ee\u4eba\u5947\u4eba\u201c\u4e09\u5bf8\u4e01\u201d \u3000\u3000\u5357\u4eac\uff0c\u53e4\u540d\u91d1\u9675\uff0c\u4e43\u516d\u671d\u53e4\u90fd\uff0c\u5c71\u5ddd\u5f62\u80a1\uff0c\u7269\u534e\u6c47\u96c6\uff0c\u4e3a\u6218\u56fd\u7b2c\u4e00\u5927\u57ce\u3002 \u3000\u3000\u5357\u4eac\u57ce\u7684\u53e4\u8ff9\u540d\u80dc\u65e0\u6570\uff0c\u6700\u8457\u540d\u7684\u6709\uff1a\u6c34\u897f\u95e8\u5916\u7684\u83ab\u6101\u6e56\uff0c\u57ce\u5317\u6c5f\u8fb9\u7684\u71d5\u5b50\u77fe\uff0c\u57ce\u4e1c\u949f \u5c71\u5357\u9e93\u7684\u660e\u5b5d\u9675\uff0c\u6ce2\u5149\u660e\u5a9a\u7684\u7384\u6b66\u6e56\uff0c\u4ee5\u53ca\u57ce\u5185\u7684\u5317\u6781\u9601\u3001\u6e05\u51c9\u5c71\u7b49\u5904\uff0c\u6709\u7684\u5e84\u4e25\u4f1f\u5927\uff0c\u6709 \u7684\u5e7d\u7f8e\u58ee\u4e3d\uff0c\u4efb\u4f55\u4e00\u5904\u5747\u53ef\u4f7f\u4eba\u6d41\u8fde\u5fd8\u8fd4\uff0c \u3000\u3000\u867d\u7136\u65f6\u5c4a\u4ef2\u79cb\uff0c\u4f46\u5929\u7a7a\u9a84\u9633\u5982\u706b\uff0c\u771f\u53ef\u70c1\u77f3\u6d41\u91d1\u3002\u5357\u4eac\u590f\u5929\u4e4b\u70ed\u662f\u5168\u56fd\u6709\u540d\u7684\uff0c\u8fd9\u201c\u79cb \u8001\u864e\u201d\u4e00\u53d1\u5a01\uff0c\u771f\u6bd4\u76db\u6691\u8fd8\u70ed\uff0c\u56e0\u6b64\u57ce\u5185\u4e00\u822c\u4ed5\u5973\uff0c\u591a\u4e09\u4e94\u6210\u7fa4\u5230\u57ce\u5317\u6c5f\u8fb9\u71d5\u5b50\u77fe\u6765\u7eb3\u51c9\u3002 \u3000\u3000\u71d5\u5b50\u77fe\u76f4\u7acb\u6c5f\u8fb9\uff0c\u72b6\u5982\u98de\u71d5\uff0c\u975e\u5e38\u58ee\u4e3d\u3002\u9644\u8fd1\u5e76\u6709\u5ca9\u5c71\u5341\u4e8c\u6d1e\u4e4b\u80dc\uff0c\u4e3a\u590f\u65e5\u907f\u6691\u80dc\u5730\u3002 \u3000\u3000\u6c5f\u98ce\u4e60\u4e60\uff0c\u67f3\u836b\u5904\u5904\u3002\u71d5\u5b50\u808c\u65c1\u5ca9\u5c71\u5341\u4e8c\u6d1e\u4e00\u5e26\uff0c\u6709\u4e0d\u5c11\u8336\u8086\u9152\u644a\uff0c\u4f9d\u6c5f\u800c\u8bbe\uff0c\u5750\u6ee1\u4e86 \u907f\u6691\u4e58\u51c9\u7684\u7ea2\u7537\u7eff\u5973\uff0c\u4e00\u4e2a\u4e2a\u8863\u5fa1\u8f7b\u7f57\uff0c\u624b\u62ff\u7ee2\u6247\uff0c\u6307\u70b9\u5c71\u6c34\u4e4b\u95f4\uff0c\u7b11\u8bed\u968f\u98ce\u64ad\u9001\uff0c\u4f7f\u4eba\u610f \u4f1a\u5230\u6c5f\u5357\u5bcc\u5eb6\u4e4b\u4e61\uff0c\u516d\u671d\u91d1\u7c89\u4e4b\u80dc\uff0c\u679c\u7136\u4e0d\u6bd4\u5bfb\u5e38\u3002 \u3000\u3000\u6b64\u65f6\u5e84\u6c5f\u8fb9\u8e2f\u8e85\u6765\u4e86\u4e00\u4e2a\u843d\u9b44\u5c11\u5e74\u3002\u53ea\u89c1\u4ed6\u8eab\u7a7f\u4e00\u4ef6\u9ed1\u7f0e\u62ab\u98ce\uff0c\u8d28\u6599\u867d\u7136\u4e0d\u574f\uff0c\u4f46\u8eab\u4e0a \u63a7\u7834\u4e86\u6570\u9053\u88c2\u53e3\uff0c\u7f00\u4e0b\u5e03\u6761\u4e5f\u672a\u7f1d\u8865\uff0c\u968f\u98ce\u98d8\u626c\uff0c\u800c\u4e14\u697c\u4e0a\u6cbe\u6ee1\u4e86\u5c18\u571f\uff0c\u8eab\u4e0a\u6e0d\u6ee1\u4e86\u6c57\u8ff9\uff0c \u53eb\u4eba\u4e00\u770b\u4fbf\u77e5\u4ed6\u2014\u5b9a\u8dcb\u6d89\u957f\u9014\uff0c\u8d70\u4e86\u4e0d\u5c11\u8def\u4e86\u3002 \u3000\u3000\u8fd9\u843d\u9b44\u5c11\u5e74\uff0c\u8138\u4e0a\u6c57\u6c34\u51b2\u6d41\u800c\u4e0b\uff0c\u9057\u7559\u4e0b\u4e00\u6761\u6761\u7684\u6c57\u6e0d\uff0c\u770b\u6837\u5b50\u662f\u597d\u591a\u5929\u6ca1\u6709\u6d17\u8138\u4e86\uff0c \u4f46\u4ecd\u63a9\u4e0d\u4f73\u4ed6\u7709\u68a2\u773c\u89d2\u4e4b\u95f4\u7684\u4fca\u633a\u82f1\u79c0\u4e4b\u6c14\uff01 \u3000\u3000\u800c\u4e14\uff0c\u843d\u9b44\u5c11\u5e74\u867d\u7136\u8863\u853d\u5f62\u75b2\uff0c\u80cc\u4e0a\u5374\u80cc\u4e86\u4e00\u67c4\u53e4\u8272\u6591\u70c2\u7684\u957f\u5251\u3002\u800c\u4e14\u770b\u90a3\u957f\u5251\u7eff\u9c7c\u76ae \u9798\u9ec4\u91d1\u541e\u624b\uff0c\u674f\u9ec4\u4e1d\u7a57\uff0c\u663e\u7136\u662f\u4e00\u67c4\u4e0a\u597d\u5b9d\u5251\u3002\u4ed6\u4f4e\u5934\u832b\u7136\u8d70\u7740\uff0c\u5fae\u8e59\u7709\u5934\uff0c\u4f3c\u6709\u65e0\u9650\u5fc3 \u4e8b\uff0c\u5bf9\u4e8e\u773c\u524d\u5c71\u5ddd\u666f\u7269\uff0c\u4ee5\u53ca\u7f18\u836b\u4e0b\u4e58\u51c9\u8c08\u7b11\u7684\u7ea2\u7537\u7eff\u5973\u604d\u5982\u672a\u89c1\u3002 \u3000\u3000\u504f\u504f\u6709\u4eba\u4e13\u627e\u5012\u9709\u7684\u6666\u6c14\u3002\u843d\u9b44\u5c11\u5e74\u5140\u81ea\u4f4e\u5934\u8d70\u7740\uff0c\u5ffd\u7136\u98de\u6765\u4e00\u5757\u62c7\u6307\u5927\u7684\u5375\u77f3\u201c\u53ed\u201d \u7684\u4e00\u58f0\uff0c\u6b63\u6253\u5728\u5c11\u5e74\u7684\u540e\u8111\u58f3\u4e0a\u3002 \u3000\u3000\u88ab\u6253\u7684\u5c11\u5e74\u4e00\u8df3\u597d\u9ad8\uff0c\u731b\u7136\u56de\u5934\u56db\u987e\uff0c\u56db\u5468\u4e58\u51c9\u7684\u7ea2\u7537\u7eff\u5973\u54d7\u7136\u9f50\u7b11\uff0c\u7adf\u5224\u65ad\u4e0d\u51fa\u662f\u8c01 \u6253\u7684\u3002 \u3000\u3000\u8fd9\u4e00\u679a\u77f3\u5b50\uff0c\u6253\u6765\u5f97\u602a\uff0c\u867d\u672a\u4f7f\u5c11\u5e74\u53d7\u4f24\uff0c\u4f46\u5374\u5f88\u75db\u3002 \u3000\u3000\u843d\u9b44\u5c11\u5e74\u56db\u987e\uff0c\u53ea\u89c1\u7eff\u836b\u8336\u5ea7\u4e0a\u7684\u6e38\u5ba2\uff0c\u90fd\u9762\u9732\u63f6\u63c4\u7684\u7b11\u5bb9\u671b\u7740\u81ea\u5df1\uff0c\u7528\u624b\u4e00\u6478\uff0c\u8111\u540e \u7adf\u88ab\u6253\u8d77\u4e00\u4e2a\u5305\u6765\uff0c\u4f46\u5728\u7fa4\u4f17\u4e4b\u4e2d\uff0c\u5c31\u662f\u770b\u4e0d\u51fa\u662f\u4f55\u4eba\u6253\u7684\uff0c\u5f53\u7136\u4e5f\u5c31\u65e0\u6cd5\u53d1\u4f5c\u3002 \u3000\u3000\u53ef\u662f\uff0c\u5c11\u5e74\u521a\u4e00\u56de\u5934\u201c\u53ed\u201d\u7684\u4e00\u58f0\uff0c\u53c8\u662f\u4e00\u679a\u77f3\u5b50\u6253\u5728\u5934 \u3000\u3000\u8fd9\u4e00\u4e0b\u6253\u5f97\u6bd4\u521a\u624d\u66f4\u91cd\uff0c\u88ab\u6253\u7684\u5c11\u5e74\u8df3\u8d77\u6709\u4e09\u5c3a\u9ad8\uff0c\u731b\u7136\u56de\u5934\uff0c\u53cc\u76ee\u5706\u7741\uff0c\u6ee1\u9762\u6012\u5bb9\u3002 \u3000\u3000\u6e38\u5ba2\u54c4\u5802\u5927\u7b11\u2026\u2026 \u3000\u3000\u4f46\u8fd9\u4e00\u6b21\uff0c\u5c11\u4e2d\u5374\u770b\u51fa\u4e86\u95e8\u9053\u3002\u539f\u6765\u6709\u4e24\u4e2a\u5c0f\u5b69\uff0c\u4e00\u7537\u4e00\u5973\uff0c\u7537\u5b69\u7ea6\u4e03\u516b\u5c81\uff0c\u5973\u5b69\u4e5f\u5c31 \u662f\u516d\u4e03\u5c81\uff0c\u7537\u5b69\u7a7f\u4e00\u8eab\u6d45\u7ef8\u88e4\u8902\uff0c\u5973\u5b69\u7a7f\u4e00\u8eab\u8c08\u7c89\u8863\u88d9\uff0c\u4e00\u6837\u957f\u5f97\u7c89\u88c5\u7389\u7422\uff0c\u4fca\u7f8e\u975e\u51e1\u3002 \u3000\u3000\u4e24\u4e2a\u5c0f\u5b69\u80cc\u7740\u4e00\u4e2a\u767e\u5b50\u77f3\u69b4\u82b1\u76c6\u800c\u7acb\uff0c\u90fd\u80cc\u7740\u624b\uff0c\u82b1\u76c6\u91cc\u5806\u7684\u6b63\u662f\u6253\u5728\u5c11\u5e74\u5934\u4e0a\u7684\u5c0f\u5757 \u5375\u77f3\u3002\u4e24\u4e2a\u5c0f\u5b69\u671b\u7740\u5c11\u5e74\u5c34\u5c2c\u7684\u6837\u4e8e\uff0c\u5c0f\u773c\u9f13\u5f97\u6eda\u5706\uff0c\u62bf\u7d27\u5634\u5507\uff0c\u770b\u6837\u5b50\u662f\u5f3a\u884c\u5fcd\u4f4f\uff0c\u4f7f\u81ea \u5df1\u4e0d\u53d1\u51fa\u7b11\u58f0\u6765\u3002 \u3000\u3000\u5728\u4e24\u4e2a\u5c0f\u5b69\u7ad9\u7684\u9644\u8fd1\uff0c\u6709\u4e00\u526f\u9ad8\u96c5\u8336\u5ea7\uff0c\u5927\u5706\u684c\u9762\uff0c\u767d\u8272\u53f0\u5dfe\uff0c\u684c\u4e0a\u6446\u7740\u4e00\u74f6\u9c9c\u82b1\uff0c\u6570 \u6837\u65b0\u9c9c\u6c34\u679c\uff0c\u51e0\u676f\u51b7\u996e\uff0c\u56db\u5468\u6570\u5f20\u9ad8\u80cc\u85e4\u6905\uff0c\u6905\u4e0a\u95f2\u6563\u5750\u7740\u4e94\u516d\u4e2a\u8863\u886b\u9c9c\u660e\u7684\u7537\u5973\uff0c\u8868\u9762\u4e0a \u770b\u50cf\u662f\u5bcc\u8d35\u4eba\u5bb6\u7684\u516c\u5b50\u5c0f\u59d0\u4e4b\u6d41\uff0c\u4f46\u7ec6\u770b\u4e00\u4e2a\u4e2a\u7cbe\u795e\u5145\u8db3\uff0c\u592a\u9633\u7a74\u9ad8\u9ad8\u9f13\u8d77\uff0c\u53cc\u76ee\u70af\u5220\u8df3\uff0c \u5206\u660e\u662f\u4e9b\u8eab\u8d1f\u9ad8\u5f3a\u6b66\u529f\u7684\u6b66\u6797\u4eba\u7269\u3002 \u3000\u3000\u5176\u4e2d\u6700\u624e\u773c\u7684\uff0c\u662f\u4e00\u4e2a\u5eff\u5c81\u5de6\u53f3\u7684\u5c11\u4e2d\uff0c\u957f\u5f97\u80a4\u767d\u5982\u7389\uff0c\u8c8c\u6bd4\u6f58\u5b89\uff0c\u8eab\u7a7f\u4e00\u88ad\u767d\u7eba\u7ef8\u957f \u886b\uff0c\u7a33\u5750\u5728\u4e2d\u4e0a\u5ea7\uff0c\u6f47\u6d12\u4e2d\u5e26\u7740\u9ad8\u8d35\uff0c\u9ad8\u8d35\u4e2d\u53c8\u663e\u5f97\u82f1\u6c14\u52c3\u52c3\u3002\u6709\u5982\u5f53\u5e74\u201c\u5c0f\u4e54\u521d\u5ac1\uff0c\u82f1\u59ff \u52c3\u53d1\u201d\uff0c\u8c08\u7b11\u95f4\u4f7f\u66f9\u64cd\u516b\u5341\u4e09\u4e07\u5927\u519b\u70df\u6d88\u7070\u6563\u7684\u5468\u516c\u8c28\u90a3\u6837\u82f1\u4fca\u7684\u6c14\u6982\uff01 \u3000\u3000\u4e0e\u8fd9\u9ad8\u8d35\u4fca\u7f8e\u5c11\u5e74\u5e76\u80a9\u5750\u7740\u7684\uff0c\u662f\u4e00\u4e2a\u5bb9\u5149\u7167\u4eba\u7684\u5c11\u5973\uff0c\u5e74\u4ec5\u53ca\u7b79\uff0c\u6e05\u65b0\u7edd\u4fd7\uff0c\u72b9\u5982\u59d1 \u5c04\u4ed9\u5b50\u3002\u8749\u7ffc\u822c\u7684\u4e91\u7f57\u7fbd\u8863\uff0c\u5a07\u8273\u5982\u82b1\u7684\u9762\u5e9e\u4e0a\uff0c\u6d6e\u7740\u5fae\u7b11\uff0c\u98de\u77a5\u4e86\u817f\u5979\u7684\u843d\u9b44\u5c11\u5e74\u4e00\u773c\uff0c \u7136\u540e\u53c8\u4ee5\u4f3c\u6012\u542b\u55a7\u7684\u773c\u5149\uff0c\u77aa\u7740\u4e24\u4e2a\u5c0f\u5b69\uff0c\u90a3\u773c\u5149\u7684\u795e\u60c5\u662f\u8d23\u5907\u4e24\u4e2a\u5c0f\u5b69\u4e0d\u8be5\u987d\u76ae\u6dd8\u6c14\u3002 \u3000\u3000\u843d\u9b44\u5c11\u5e74\u8fde\u7740\u88ab\u77f3\u5b50\u6253\u4e2d\u4e24\u4e0b\uff0c\u53c8\u88ab\u4f17\u6e38\u5ba2\u6c55\u7b11\uff0c\u5df2\u6fc0\u8d77\u4e86\u6ee1\u8154\u6012\u706b\u3002\u4f46\u662f\uff0c\u5f53\u4ed6\u770b\u51fa \u662f\u4e24\u4e2a\u5b69\u5b50\u6076\u4f5c\u5267\u65f6\uff0c\u5fc3\u4e2d\u6697\u60f3\u53c8\u4f55\u5fc5\u8ddf\u4e24\u4e2a\u5c0f\u5b69\u5b50\u4e00\u822c\u89c1\u8bc6\uff0c\u56e0\u6b64\u6012\u6c14\u6d88\u4e86\u4e00\u534a\u3002\u4f46\u5634\u4e2d \u4ecd\u9053\uff1a\u201c\u5c0f\u670b\u53cb\uff01\u4e0d\u5e94\u8be5\u65e0\u6545\u6253\u4eba\uff0c\u6253\u5230\u6211\u6ca1\u6709\u4ec0\u4e48\u5173\u7cfb\uff0c\u82e5\u662f\u813e\u6c14\u574f\u7684\u4eba\uff0c\u4e00\u5b9a\u4e0d\u4f1a\u9976\u8fc7 \u4f60\u4eec\u2026\u2026\u201d \u3000\u3000\u90a3\u5c0f\u7537\u5b69\u773c\u73e0\u4e00\u8f6c\uff0c\u5e26\u7740\u987d\u76ae\u7684\u7b11\u5bb9\uff0c\u4ef0\u8d77\u5c0f\u8138\u53cd\u95ee\u9053\uff1a\u8fd9\u6837\u8bf4\uff0c\u4f60\u4e0d\u574f\u561b\uff1f\u201d \u3000\u3000\u201c\u5657\u55e4\uff01\u201d\u5c0f\u5973\u5b69\u5fcd\u4e0d\u4f4f\u7b11\u51fa\u58f0\u6765\uff0c\u4f46\u4e00\u7b11\u51fa\u6765\u53c8\u611f\u89c9\u4e0d\u597d\u610f\u601d\uff0c\u5fd9\u8f6c\u8eab\u9762\u5411\u6c5f\u6c34\u3002 \u3000\u3000\u5c0f\u5973\u5b69\u8f6c\u8fc7\u5934\u53bb\uff0c\u4e00\u773c\u770b\u89c1\u6c5f\u8fb9\u5ca9\u77f3\u4e0a\uff0c\u722c\u7740\u4e00\u53ea\u6597\u7b95\u5927\u7684\u4e4c\u9f9f\uff0c\u6b63\u62f1\u7740\u76d6\u5b50\u6652\u592a\u9633\u3002 \u5c0f\u5973\u5b69\u7ae5\u5fc3\u5927\u53d1\uff0c\u5c0f\u624b\u6307\u4e00\u5c48\u4e00\u5f39\uff0c\u628a\u85cf\u5728\u638c\u5fc3\u91cc\u7684\u53e6\u4e00\u679a\u77f3\u5b50\u968f\u6307\u5f39\u51fa\uff0c\u201c\u53ed\u201d\u7684\u4e00\u58f0\uff0c \u4e0d\u504f\u4e0d\u501a\uff0c\u6b63\u6253\u5728\u4e4c\u9f9f\u5934\u4e0a\uff0c\u628a\u90a3\u4e4c\u9f9f\u6253\u4e86\u4e00\u4e2a\u7ffb\u8eab\uff0c\u771f\u6b63\u662f\u201c\u738b\u516b\u7ffb\u8eab\u5fd9\u4e86\u722a\u201d\uff0c\u90a3\u4e4c\u9f9f \u4ef0\u9762\u5411\u5929\uff0c\u56db\u811a\u4e00\u9635\u4e71\u6293\uff0c\u5374\u65e0\u4e2a\u7740\u529b\u5904\uff0c\u518d\u4e5f\u722c\u4e0d\u8d77\u6765\u2026\u2026 \u3000\u3000\u201c\u563b\uff01\u201d\u5c0f\u5973\u5b69\u62cd\u624b\u6b22\u547c\uff1a\u201c\u54e5\u54e5\uff01\u6211\u6253\u4e2d\u4e4c\u9f9f\u7684\u5934\u4e86\uff01\u201d \u3000\u3000\u5750\u5728\u8336\u5ea7\u4e0a\u7684\u9ad8\u8d35\u5c11\u5e74\uff0c\u4e0e\u4fca\u7f8e\u5c11\u5973\u540c\u58f0\u559d\u6b62\uff1a\u201c\u5170\u5170\uff0c\u4e0d\u8bb8\u6dd8\u6c14\u2606\u2026\uff0e\u201d \u3000\u3000\u201c\u55d6\u2014\u2014\u53ed\uff01\u201d \u3000\u3000\u4f46\u662f\u9ad8\u8d35\u5c11\u5e74\u4e0e\u4fca\u7f8e\u5c11\u5973\u559d\u58f0\u672a\u4f4f\uff0c\u5c0f\u7537\u5b69\u4ee5\u76f8\u540c\u7684\u624b\u6cd5\uff0c\u5c0f\u624b\u6307\u4e00\u5c48\u4e00\u5f39\uff0c\u628a\u63e1\u5728\u638c \u5fc3\u7684\u4e00\u679a\u77f3\u5b50\uff0c\u540c\u6837\u6253\u5728\u4e4c\u9f9f\u7684\u5934 \u3000\u3000\u5c0f\u7537\u5b69\u8fd9\u2014\u4e0b\u6bd4\u5c0f\u5973\u5b69\u624b\u6cd5\u91cd\uff0c\u56db\u811a\u671d\u5929\u7684\u4e4c\u9f9f\u88ab\u6253\u5f97\u56db\u811a\u7ffb\u98de\uff0c\u4e00\u8def\u6eda\u5411\u6c5f\u6c34\u4e2d\uff0c \u201c\u5657\u901a\u201d\u4e00\u58f0\uff0c\u6c34\u82b1\u56db\u6e85\uff0c\u4e4c\u9f9f\u8d81\u52bf\u6f5c\u5165\u6c34\u4e2d\u4e0d\u89c1\u3002 \u3000\u3000\u6c5f\u8fb9\u8336\u5ba2\uff0c\u8db3\u6709\u6570\u767e\u4e4b\u4f17\uff0c\u89c1\u72b6\u54d7\u7136\u5927\u7b11\u3002 \u3000\u3000\u201c\u6709\u4ec0\u4e48\u65b0\u5947\uff1f\u201d\u5c0f\u7537\u5b69\u5bf9\u5c0f\u5973\u5b69\u8bf4\uff1a\u6211\u8fd8\u4e0d\u662f\u4e00\u6837\u6253\u4e2d\u4e4c\u9f9f\u7684\u5934\uff01\u201d \u3000\u3000\u6570\u767e\u8336\u5ba2\u66f4\u662f\u54c4\u5802\u5927\u7b11\u2026\u2026 \u3000\u3000\u4e24\u5c0f\u5b69\u867d\u662f\u7ae5\u8a00\u65e0\u5fcc\uff0c\u4f46\u4e00\u8bed\u53cc\u5173\uff0c\u53c8\u52a0\u4e0a\u5468\u906d\u8336\u5ba2\u4e00\u9635\u5927\u7b11\uff0c\u53ea\u628a\u843d\u9b44\u5c11\u5e74\u6c14\u5f97\u8138\u4e0a \u4e00\u9635\u7ea2\u4e00\u9635\u767d\uff0c\u4e24\u53ea\u773c\u775b\u77aa\u5f97\u6eda\u5706\uff0c\u8981\u60f3\u53d1\u4f5c\uff0c\u65e0\u5948\u5bf9\u65b9\u4ec5\u662f\u4e24\u4e2a\u4e0d\u61c2\u4e8b\u7684\u5b69\u5b50\uff0c\u53c8\u89c9\u5f97\u4e0d \u4fbf\u53d1\u4f5c\u3002\u4e8e\u662f\uff0c\u4ed6\u53f9\u4e86\u4e00\u53e3\u6c14\u3002\u5fc3\u8bf4\uff1a\u201c\u547d\u4e56\u8fd0\u53c8\u8e47\uff0c\u65f6\u88ab\u9b3c\u63f6\u63c4\uff01\u81ea\u5df1\u4ec0\u4e48\u6837\u7684\u6c14\u90fd\u53d7\u8fc7 \u4e86\uff0c\u53c8\u4f55\u5fc5\u8ddf\u4e24\u4e2a\u5b69\u5b50\u4e00\u822c\u89c1\u8bc6\u2026\u2026\u201d \u3000\u3000\u843d\u9b44\u5c11\u5e74\u60f3\u5230\u8fd9\u91cc\uff0c\u5934\u4e00\u4f4e\uff0c\u52a0\u7d27\u811a\u6b65\uff0c\u60f3\u8d76\u5feb\u79bb\u5f00\u8fd9\u5c34\u5c2c\u4e4b\u5730\u2026\u2026 \u3000\u3000\u8c01\u77e5\u504f\u504f\u6709\u4eba\u627e\u4ed6\u7684\u9ebb\u70e6\uff0c\u5c31\u5728\u843d\u9b44\u5c11\u5e74\u5feb\u6b65\u79bb\u53bb\u4e4b\u9645\uff0c\u7a81\u542c\u4e00\u4e2a\u516c\u7f8a\u55d3\u95e8\u53eb\u9053\uff1a \u201c\u55e8\uff01\u8001\u4e8c\u5440\uff01\u4f60\u65b9\u624d\u8fd8\u8bf4\u4ec0\u4e48\u2018\u5927\u4e08\u592b\u5934\u53ef\u65ad\uff0c\u8840\u53ef\u6d41\uff0c\u51b3\u4e0d\u53ef\u5fcd\u8fb1\u5077\u751f\uff0c\u606c\u989c\u6d3b\u5728\u4e16 \u4e0a\u3002\u2019\u5982\u4eca\u53eb\u6211\u8001\u4eba\u5bb6\u770b\u6765\uff0c\u4e16\u4e0a\u591a\u7684\u662f\u7f29\u5934\u4e4c\u9f9f\uff0c\u5c11\u5e74\u65e0\u5fd7\u4e4b\u4eba\uff0c\u53d7\u4e86\u4eba\u5bb6\u4fae\u8fb1\uff0c\u8fd8\u4e0d\u662f \u5c41\u90fd\u4e0d\u6562\u653e\u4e00\u4e2a\uff01\u201d \u3000\u3000\u8fd9\u516c\u7f8a\u55d3\u95e8\u53c8\u9ad8\u53c8\u5c16\uff0c\u5206\u5916\u523a\u8033\uff0c\u800c\u4e14\u58f0\u97f3\u975e\u5e38\u4e4b\u5927\uff0c\u53eb\u5f97\u4eba\u4eba\u90fd\u542c\u5230\u4e86\u843d\u9b44\u5c11\u5e74\u79bb\u5f97 \u5f88\u8fd1\uff0c\u95fb\u8a00\u8eab\u4e0d\u7531\u5df1\u5730\u626d\u5934\u671b\u53bb\u3002 \u3000\u3000\u6c5f\u8fb9\u6811\u836b\u4e0b\u6709\u4e00\u4e2a\u8336\u5ea7\uff0c\u5750\u7740\u4e8c\u8001\u4e00\u5c11\uff0c\u6b63\u5728\u4ee5\u6781\u4e3a\u5351\u89c6\u7684\u773c\u5149\uff0c\u671b\u7740\u843d\u9b44\u5c11\u5e74\u3002 \u3000\u3000\u4e24\u4e2a\u8001\u4eba\u5e74\u7eaa\u90fd\u5f88\u5927\u4e86\uff0c\u4f46\u50cf\u8c8c\u751f\u5f97\u751a\u4e3a\u5947\u7279\u3002\u4e00\u4e2a\u957f\u7740\u6ee1\u5934\u7ea2\u53d1\uff0c\u5468\u8eab\u76ae\u80a4\u7c97\u7cd9\u780c \u9ed1\uff0c\u552f\u6709\u773c\u9f3b\u7d27\u957f\u5f97\u9c9c\u767d\u67d4\u5ae9\uff0c\u4e00\u53cc\u7cbe\u5149\u56db\u5c04\u7684\u5c0f\u5706\u773c\uff0c\u8eab\u7a7f\u4e00\u4ef6\u767e\u8865\u94a0\u8863\uff0c\u62f1\u80cc\u7f29\u80a9\uff0c\u4e4d \u770b\u771f\u50cf\u4e00\u4e2a\u9a6c\u620f\u73ed\u7684\u5927\u9a6c\u7334\u4e00\u822c\u3002 \u3000\u3000\u5750\u5728\u8c8c\u4f3c\u9a6c\u7334\u7684\u8001\u4eba\u5bf9\u9762\u7684\u90a3\u4e2a\u8001\u4eba\uff0c\u867d\u7136\u4e0d\u90a3\u4e48\u4e0d\u582a\u5165\u76ee\uff0c\u4f46\u7626\u5c0f\u67af\u5e72\uff0c\u5934\u6234\u5927\u6be1 \u5e3d\uff0c\u8eab\u7a7f\u539a\u957f\u888d\uff0c\u5728\u8925\u6691\u5929\u6c14\uff0c\u5149\u662f\u8fd9\u8eab\u7a7f\u7740\uff0c\u5c31\u591f\u4f7f\u4eba\u89c9\u5f97\u602a\u7684\u4e86\u3002 \u3000\u3000\u590f\u7a7f\u51ac\u8863\u7684\u7626\u5c0f\u8001\u5934\uff0c\u53cc\u624b\u62e2\u8896\uff0c\u76b1\u7709\u6324\u773c\u671b\u5b9a\u843d\u9b44\u5c11\u5e74\u3002\u8fd9\u7626\u5c0f\u8001\u5934\u4e0a\u5507\u84c4\u7740\u4e24\u6487\u5c0f \u80e1\u5b50\uff0c\u4e00\u4e2a\u5927\u7ea2\u9152\u7cdf\u9f3b\u5b50\uff0c\u6447\u5934\u6643\u8111\uff0c\u4e00\u8fb9\u5634\u4e2d\u8fd8\u6587\u7ec9\u7ec9\u541f\u9053\uff1a\u201c\u543e\u5144\u8a00\u4e4b\u4e0d\u8c2c\u4e5f\uff01\u201d\u751f\u5c31 \u4e00\u526f\u4e09\u5bb6\u6751\u51ac\u70d8\u5148\u751f\u7684\u6a21\u6837\u3002 \u3000\u3000\u843d\u9b44\u5c11\u5e74\u4e00\u53e3\u6012\u6c14\uff0c\u4ece\u809a\u5b50\u91cc\u76f4\u51b2\u8111\u95e8\uff0c\u4f46\u4ed6\u5c1a\u672a\u53d1\u4f5c\uff0c\u90a3\u51ac\u70d8\u5148\u751f\u6478\u6837\u7684\u8001\u4eba\u62db\u624b\u53eb \u9053\uff1a\u8fc7\u6765\uff01\u201d \u3000\u3000\u843d\u9b44\u5c11\u5e74\u5f3a\u538b\u4f4f\u6ee1\u8154\u6012\u706b\uff0c\u5047\u88c5\u4e0d\u61c2\u5730\u95ee\u9053\uff1a\u201c\u8001\u5148\u751f\u662f\u53eb\u6211\u5417\uff1f\u201d \u3000\u3000\u201c\u5509\uff01\u771f\u4e43\u987d\u51a5\u4e0d\u7075\uff01\u201d\u51ac\u70d8\u5148\u751f\u8138\u5b54\u4e00\u6c89\uff0c\u53f1\u9053\uff1a\u8001\u592b\u4e0d\u662f\u53eb\u6c5d\uff0c\u96be\u9053\u662f\u53eb\u72ac\u5417\uff1f\u201d \u3000\u3000\u51ac\u70d8\u5148\u751f\u628a\u201c\u4f60\u201d\u53eb\u6210\u201c\u6c5d\u201d\uff0c\u628a\u201c\u72d7\u201d\u79f0\u505a\u201c\u72ac\u201d\uff0c\u60f9\u5f97\u5468\u906d\u8336\u5ba2\uff0c\u53c8\u662f\u4e00\u9635\u54c4\u7b11\uff01 \u3000\u3000\u8fd9\u4e00\u4e0b\u4e8e\uff0c\u843d\u9b44\u5c11\u4e2d\u518d\u4e5f\u5fcd\u4e0d\u4f4f\u4e86\uff0c\u4e0d\u7531\u6012\u9053\uff1a\u201c\u8001\u5148\u751f\u6ee1\u5634\u65af\u6587\uff0c\u5374\u51fa\u8a00\u4e0d\u900a\uff0c\u60f3\u5fc5 \u4e5f\u4e0d\u662f\u4ec0\u4e48\u6b63\u7ecf\u8bfb\u4e66\u4eba\uff0c\u5c0f\u53ef\u82e5\u4e0d\u770b\u4f60\u90a3\u4e48\u5927\u5e74\u7eaa\uff0c\u54fc\uff01\u201d \u3000\u3000\u843d\u9b44\u5c11\u5e74\u8bdd\u4e2d\u4e4b\u610f\u867d\u672a\u660e\u8bf4\u51fa\u6765\uff0c\u4f46\u4e5f\u53ef\u4ece\u90a3\u4e00\u58f0\u51b7\u54fc\u4e2d\u542c\u51fa\u6765\u4e86\u3002 \u3000\u3000\u8c01\u77e5\u843d\u9b44\u5c11\u5e74\u6b64\u8a00\u4e00\u51fa\uff0c\u5374\u628a\u90a3\u4e00\u65c1\u7684\u8d64\u53d1\u8001\u4eba\u4e50\u574f\u4e86\u3002\u53ea\u89c1\u4ed6\u7b11\u5f97\u524d\u4ef0\u540e\u5408\uff0c\u62cd\u624b\u8dfa \u811a\uff0c\u54c8\u54c8\u5927\u7b11\u58f0\u4e2d\uff0c\u4ee5\u4ed6\u7279\u6709\u7684\u516c\u7f8a\u55d3\u95e8\u8bf4\u9053\uff1a\u201c\u54c8\u54c8\u54c8\u2026\u2026\u6587\u8001\u4e8c\uff01\u54c8\u54c8\u2026\u2026\u4f60\u4e00\u5929\u5230\u665a \u611f\u53f9\u65af\u6587\u626b\u5730\u2026\u2026\u54c8\u54c8\u54c8\u2026\u2026\u73b0\u5728\u53ef\u771f\u662f\u65af\u6587\u626b\u5730\u4e86\uff0c\u54c8\u54c8\u2026\u2026\u8fd9\u5c0f\u5b50\u8bf4\u4f60\u4e0d\u662f\u6b63\u7ecf\u8bfb\u4e66 \u4eba\uff0c\u54c8\u54c8\u2026\u2026\u201d \u3000\u3000\u51ac\u7115\u5148\u751f\u88ab\u8d64\u53d1\u8001\u4eba\u7b11\u5f97\u5439\u80e1\u5b50\u77aa\u670d\uff0c\u9f13\u7740\u816e\u5e2e\uff0c\u6012\u5411\u843d\u9b44\u5c11\u5e74\u53f1\u9053\uff1a\u7caa\u571f\u4e4b\u5899\uff01\u7caa\u571f \u4e4b\u5899\uff01\u5b7a\u5b50\u771f\u4e0d\u53ef\u6559\u4e5f\uff01\u8001\u592b\u53eb\u6c5d\uff0c\u6c5d\u4e0d\u8fc7\u6765\uff0c\u8fd8\u80c6\u6562\u8fb1\u9a82\u8001\u592b\uff01\u54fc\uff01\u201d \u3000\u3000\u8bf4\u7740\u51b7\u54fc\u4e00\u58f0\uff0c\u53cc\u624b\u4e00\u6309\u684c\u9762\uff0c\u4f5c\u52bf\u6b32\u8d77\u2026\u2026 \u3000\u3000\u201c\u8001\u5e08\uff0c\u4e14\u4f4f\uff01\u201d\u5750\u5728\u4e8c\u8001\u5bf9\u9762\u7684\u5c0f\u50ee\uff0c\u7a81\u7136\u5f80\u8d77\u4e00\u957f\u8eab\uff0c\u5411\u51ac\u70d8\u5148\u751f\u8bf4\u9053\uff1a\u201c\u6709\u4e8b\u5f1f \u5b50\u670d\u5176\u52b3\uff0c\u6740\u9e21\u7109\u7528\u5bb0\u4e2d\u5200\uff0c\u6536\u62fe\u8fd9\u5c0f\u5b50\uff0c\u54ea\u91cc\u8fd8\u8981\u60a8\u8001\u4eba\u5bb6\u4eb2\u81ea\u52a8\u624b\uff0c\u8ba9\u5f1f\u5b50\u6765\u6559\u8bad\u6559\u8bad \u4ed6\u3002\u201d \u3000\u3000\u51ac\u70d8\u5148\u751f\u70b9\u4e86\u70b9\u5934\uff0c\u53c8\u5750\u4e86\u4e0b\u53bb\u3002\u5c0f\u50ee\u4ece\u7af9\u6905\u4e0a\u6ed1\u4e86\u4e0b\u6765\uff0c\u54c8\u5427\u7740\u4e24\u6761\u7f57\u5708\u817f\u5411\u843d\u9b44\u5c11 \u5e74\u8d70\u8fd1\u2026\u2026 \u3000\u3000\u600e\u4e48\u8bf4\u5c0f\u50ee\u662f\u4ece\u7af9\u6905\u4e0a\u201c\u6ed1\u201d\u4e86\u4e0b\u6765\u5462\uff1f\u539f\u6765\u5c0f\u50ee\u8eab\u5f62\u5947\u77ee\uff0c\u5750\u5728\u7af9\u6905\u4e0a\u4e24\u811a\u4e0d\u7740\u5730\uff0c \u8336\u5ea7\u7684\u7af9\u6905\u53c8\u79bb\uff0c\u56e0\u6b64\u8fd9\u8eab\u5f62\u5947\u77ee\u7684\u5c0f\u50ee\uff0c\u79bb\u5ea7\u65f6\u662f\u8eab\u5f62\u4e00\u633a\uff0c\u5c41\u80a1\u987a\u7740\u6905\u9762\u5411\u4e0b\u4e00\u6ed1\uff0c\u624d \u53cc\u811a\u843d\u5730\u3002 \u3000\u3000\u843d\u9b44\u5c11\u5e74\u4e00\u770b\u8fd9\u5c0f\u50ee\uff0c\u8eab\u9ad8\u4e0d\u6ee1\u4e09\u5c3a\uff0c\u5374\u957f\u4e86\u4e00\u4e2a\u8d85\u4e4e\u5e38\u4eba\u7684\u5927\u8111\u888b\uff0c\u5927\u5934\u5927\u8138\u4e0a\uff0c\u5c0f \u9f3b\u5b50\u5c0f\u773c\u90fd\u6324\u5728\u4e00\u5757\u513f\uff0c\u5c24\u5176\u5c0f\u50ee\u751f\u7740\u4e24\u6761\u7f57\u5708\u817f\uff0c\u4e0a\u5507\u63a5\u7740\u4e24\u6761\u9f3b\u6d95\uff0c\u591a\u908b\u9062\uff0c\u800c\u4e14\u5947\u4e11 \u65e0\u6bd4\uff01 \u3000\u3000\u56db\u5468\u8336\u5ba2\u4e00\u770b\u5c0f\u649e\u8fd9\u4efd\u957f\u50cf\uff0c\u5fcd\u4e0d\u4f4f\u201c\u566c\u55e1\u201d\u53d1\u7b11\uff1b\u90a3\u5c0f\u50ee\u5374\u5927\u6a21\u5927\u6837\uff0c\u54c8\u5427\u7740\u4e24\u6761\u7f57 \u5708\u817f\u8d70\u8fd1\u843d\u9b44\u5c11\u5e74\u9762\u524d\uff0c\u5927\u9a6c\u91d1\u5200\u5730\u4e00\u7ad9\uff0c\u7528\u624b\u4e00\u6307\u843d\u9b44\u5c11\u5e74\u7684\u9f3b\u5b50\uff0c\u53eb\u9053\uff1a\u201c\u5454\uff01\u4f60\u5c0f\u5b50 \u5f97\u7f6a\u4e86\u6211\u5e08\u7236\u8001\u592a\u7237\uff01\u53ea\u8981\u7ed9\u6211\u8fd9\u5c0f\u592a\u7237\u78d5\u4e2a\u54cd\u5934\uff0c\u90a3\u4e48\uff0c\u6211\u5c0f\u592a\u7237\u4fbf\u4ee3\u4f60\u5c0f\u5b50\u5411\u5e08\u7236\u8001\u592a \u7237\u6c42\u6c42\u60c5\uff0c\u5e08\u7236\u8001\u592a\u7237\u4e5f\u8bb8\u4f1a\u9976\u4e86\u4f60\uff01\u5982\u82e5\u4e0d\u7136\uff0c\u54fc\uff01\u522b\u8bf4\u5e08\u7236\u8001\u592a\u7237\u4f1a\u53d1\u813e\u6c14\uff0c\u5c31\u662f\u5c0f\u592a \u7237\u4e5f\u4e0d\u9976\u4f60\uff01\u201d \u3000\u3000\u8fd9\u201c\u4e09\u5bf8\u4e01\u201d\u4f3c\u7684\u5c0f\u50ee\uff0c\u5927\u6a21\u5927\u6837\u5730\u5411\u843d\u9b44\u5c11\u5e74\u4e00\u53eb\u9635\uff0c\u800c\u4e14\u6ee1\u5634\u7684\u8001\u592a\u7237\u3001\u5c0f\u592a\u7237\uff0c \u8fd8\u4e0d\u4f4f\u7684\u7528\u8896\u53e3\u62b9\u9f3b\u6d95\uff0c\u8fd9\u4e00\u6765\u628a\u56db\u5468\u8336\u5bb9\u66f4\u662f\u9017\u5f97\u54c4\u5802\u5927\u7b11\u8d77\u6765\u3002 \u3000\u3000\u843d\u9b44\u5c11\u5e74\u8fd9\u4e2a\u6c14\u53ef\u5c31\u5927\u4e86\uff0c\u770b\u5230\u8fd9\u4e09\u5206\u4e0d\u50cf\u4eba\u7684\u682a\u5112\uff0c\u4e5f\u5411\u81ea\u5df1\u559d\u4e94\u9a82\u516d\u3001\u8000\u6b66\u626c\u5a01\u8d77 \u6765\uff0c\u76f4\u6c14\u5f97\u534a\u5929\u8bf4\u4e0d\u4e0a\u8bdd\u6765\u3002 \u3000\u3000\u201c\u5c0f\u5b50\u4e3a\u4ec0\u4e48\u4e0d\u8bf4\u8bdd\uff1f\u201d\u5c0f\u50ee\u4e24\u53ea\u7eff\u8c46\u773c\u4e00\u77aa\uff0c\u5428\u9053\uff1a\u201c\u83ab\u975e\u662f\u5f53\u771f\u8ba8\u6253\uff01\u201d \u3000\u3000\u843d\u9b44\u5c11\u5e74\u53ea\u662f\u563f\u563f\u51b7\u7b11\uff0c\u65e2\u672a\u8bf4\u8bdd\u4e5f\u672a\u51fa\u624b\u3002\u4e8b\u5b9e\u4e0a\uff0c\u4ed6\u5fc3\u4e2d\u662f\u60f3\u8ddf\u8fd9\u6837\u4e00\u4e2a\u4e09\u5206\u4e0d\u50cf \u4eba\u7684\u8bdb\u5112\u6253\u8d77\u6765\uff0c\u80dc\u4e4b\u4e0d\u6b66\uff0c\u800c\u4e14\u88ab\u8336\u5ba2\u4eec\u50cf\u800d\u72d7\u718a\u4f3c\u5730\u770b\u70ed\u95f9\uff0c\u90a3\u624d\u771f\u53eb\u5212\u4e0d\u6765\u3002 \u3000\u3000\u5c0f\u50ee\u5374\u4e0d\u7ba1\u843d\u9b44\u5c11\u5e74\u5fc3\u4e2d\u600e\u6837\u60f3\uff0c\u89c1\u4ed6\u4e0d\u7b54\u8bdd\uff0c\u4ee5\u4e3a\u843d\u9b44\u5c11\u5e74\u77a7\u4e0d\u8d77\u4ed6\u3002\u7a81\u7136\u5de6\u624b\u4e00\u9886 \u843d\u9b44\u5c11\u5e74\u773c\u795e\uff0c\u53f3\u624b\u51fa\u624b\u5982\u98ce\uff0c\u8eab\u5f62\u968f\u7740\u51fa\u624b\u4e4b\u52bf\uff0c\u7535\u5c04\u4f3c\u5730\u903c\u8fd1\u843d\u9b44\u5c11\u5e74\u80f8\u524d\uff0c\u4e94\u6307\u5982 \u94a9\uff0c\u5411\u7740\u843d\u9b44\u5c11\u5e74\u8155\u90e8\u5173\u8282\u6263\u6765\uff0c\u6240\u7528\u624b\u6cd5\uff0c\u7adf\u662f\u6b66\u6797\u7f55\u89c1\u7684\u201c\u5927\u64d2\u62ff\u201d\u624b\u6cd5\u4e4b\u4e2d\u7684\u4e00\u5f0f \u201c\u5378\u5173\u70b9\u5143\u201d\uff0c\u4e0d\u4ec5\u51fa\u62db\u5feb\uff0c\u800c\u4e14\u62db\u5f0f\u5947\uff0c\u4e0d\u4e9a\u4e8e\u5f53\u4eca\u6b66\u6797\u4e00\u6d41\u9ad8\u624b\u3002 \u3000\u3000\u843d\u9b44\u5c11\u5e74\u9661\u7136\u4e00\u60ca\uff0c\u4f30\u4e0d\u5230\u8fd9\u8c8c\u4e0d\u60ca\u4eba\u7684\u201c\u4e09\u5bf8\u4e01\u201d\u7ade\u6709\u8fd9\u6837\u7684\u9ad8\u5f3a\u6b66\u529f\u3002\u89c1\u5c0f\u50ee\u62db \u5230\uff0c\u4e0d\u6562\u6020\u6162\uff0c\u659c\u8eab\u632b\u6b65\uff0c\u7529\u81c2\u66f2\u8098\uff0c\u7528\u51fa\u4e00\u62db\u201c\u65ad\u7b4b\u622a\u8109\u201d\uff0c\u4e5f\u662f\u201c\u5927\u64d2\u62ff\u624b\u201d\u4e2d\u7684\u7edd \u62db\uff0c\u4e94\u6307\u7b95\u5f20\uff0c\u7531\u4e0b\u5411\u4e0a\uff0c\u53cd\u6263\u5c0f\u50ee\u53f3\u624b\u8109\u95e8\u3002 \u3000\u3000\u201c\u6765\u5f97\u597d\uff01\u201d\u5c0f\u50ee\u5c16\u53eb\u4e00\u58f0\uff0c\u8eab\u5f62\u5982\u65cb\u98ce\u822c\u6ef4\u6e9c\u6e9c\u5730\u4e00\u8f6c\uff0c\u53f3\u624b\u5411\u4fa7\u4e00\u6ed1\uff0c\u8eb2\u8fc7\u843d\u9b44\u5c11 \u5e74\u4e94\u6307\uff0c\u731b\u6293\u843d\u9b44\u5c11\u5e74\u80f8\u8179\u8981\u5bb3\uff1b\u540c\u65f6\uff0c\u5de6\u624b\u5982\u53c9\u6293\u5411\u843d\u9b44\u5c11\u5e74\u54bd\u5589\u91cd\u7a74\uff0c\u7528\u7684\u662f\u201c\u5927\u64d2\u62ff \u624b\u201d\u4e2d\u201c\u6293\u888d\u64c5\u5e26\u201d\u7edd\u62db\u3002 \u3000\u3000\u843d\u9b44\u5c11\u5e74\u89c1\u8fd9\u4e0d\u8d77\u773c\u7684\u5c0f\u649e\uff0c\u51fa\u624b\u62db\u5f0f\u4e0d\u540c\u51e1\u54cd\uff0c\u7acb\u523b\u6536\u8d77\u4e86\u8f7b\u654c\u50b2\u6162\u4e4b\u5fc3\uff0c\u53f3\u624b\u4e94\u6307 \u5e76\u62e2\uff0c\u201c\u91d1\u4e1d\u7f20\u8155\u201d\uff0c\u53cd\u62ff\u5c0f\u5e62\u53c9\u5411\u54bd\u5589\u7684\u5de6\u8155\u5173\u8282\uff0c\u5de6\u624b\u6a2a\u524a\u5c0f\u50ee\u53f3\u81c2\u201c\u7ecf\u6e20\u201d\u91cd\u7a74\u3002 \u3000\u3000\u5c0f\u50ee\u5c16\u5578\u95ea\u8fc7\uff0c\u4e8c\u4eba\u5feb\u653b\u5feb\u6253\uff0c\u6240\u7528\u624b\u6cd5\u5747\u662f\u6b66\u6797\u7f55\u89c1\u7684\u201c\u5927\u64d2\u62ff\u201d\u624b\u6cd5\uff0c\u5947\u8be1\u7edd\u4f26\uff0c \u6643\u773c\u4e4b\u95f4\uff0c\u4e92\u62c6\u4e86\u4e94\u4e03\u62db\u3002 \u3000\u3000\u71d5\u5b50\u77fe\u6c5f\u8fb9\u8336\u5ea7\u4e4b\u4e2d\uff0c\u5367\u864e\u85cf\u9f99\uff0c\u6709\u4e0d\u5c11\u6b66\u6797\u540d\u5bb6\u6742\u8eab\u5176\u4e2d\uff0c\u5148\u524d\u89c1\u843d\u9b44\u5c11\u5e74\u4e0e\u6beb\u4e0d\u8d77 \u773c\u7684\u5c0f\u50ee\u8d77\u4e86\u51b2\u7a81\uff0c\u4ee5\u4e3a\u4e0d\u8fc7\u662f\u201c\u72d7\u6253\u67b6\u201d\u7684\u628a\u620f\uff0c\u5747\u672a\u52a0\u4ee5\u91cd\u89c6\uff1b\u4f46\u7b49\u5230\u4e8c\u4eba\u4e00\u4ea4\u4e0a\u624b\uff0c \u90fd\u4e0d\u514d\u7741\u5927\u4e86\u773c\u775b\uff0c\u6697\u6697\u4e3a\u4e8c\u4eba\u7684\u7cbe\u5965\u624b\u6cd5\uff0c\u611f\u5230\u60ca\u5f02\u8d77\u6765\u3002 \u3000\u3000\u5176\u4e2d\u6700\u7559\u5fc3\u4e8c\u4eba\u52a8\u624b\u7684\uff0c\u662f\u90a3\u4e24\u4e2a\u5947\u7279\u7684\u8001\u5934\uff0c\u4ee5\u53ca\u90a3\u9ad8\u8d35\u7684\u5c11\u5e74\u4e0e\u4fca\u7f8e\u5c11\u5973\u3002 \u3000\u3000\u6562\u60c5\u8fd9\u4e24\u4e2a\u5947\u602a\u7684\u8001\u5934\u5b50\uff0c\u7adf\u662f\u5927\u6c5f\u5357\u5cb8\u9ed1\u767d\u4e24\u9053\u95fb\u540d\u4e27\u80c6\u7684\u201c\u6c5f\u5357\u4e8c\u5947\u201d\u3002 \u3000\u3000\u90a3\u957f\u7740\u6ee1\u5934\u7ea2\u53d1\uff0c\u5468\u8eab\u808c\u80a4\u6f06\u9ed1\uff0c\u9762\u767d\u5982\u7334\u4e3a\u516c\u7f8a\u55d3\u95e8\u8001\u5934\uff0c\u4e43\u662f\u201c\u6c5f\u5357\u4e8c\u5947\u201d\u7684\u8001 \u5927\uff0c\u6c5f\u6e56\u4eba\u79f0\u201c\u8d64\u53d1\u8001\u4eba\u201d\u5e38\u53bb\u6076\u3002\u90a3\u590f\u7a7f\u51ac\u8863\u3001\u9152\u7cdf\u9f3b\u5b50\u3001\u6ee1\u5634\u4e4b\u4e4e\u8005\u4e5f\u3001\u72b9\u5982\u4e09\u5bb6\u6751\u51ac \u70d8\u5148\u751f\u7684\u5e72\u67af\u7626\u5c0f\u8001\u5934\uff0c\u662f\u201c\u6c5f\u5357\u4e8c\u5947\u201d\u7684\u8001\u4e8c\uff0c\u59d3\u6587\u540d\u6b63\u5947\uff0c\u6c5f\u6e56\u4eba\u79f0\u201c\u9b3c\u8c37\u9690\u53df\u201d\u3002 \u3000\u3000\u8fd9\u4e8c\u4eba\u4eab\u8a89\u6b66\u6797\u6570\u5341\u5e74\uff0c\u6b66\u529f\u81ea\u6210\u4e00\u683c\uff0c\u5185\u3001\u5916\u3001\u8f7b\u4e09\u529f\u5747\u81f3\u767b\u5cf0\u9020\u6781\u5730\u6b65\uff0c\u751f\u6027\u602a \u5f02\uff0c\u4e0d\u559c\u548c\u4eba\u6253\u4ea4\u9053\uff0c\u5e38\u5e74\u9690\u8eab\u201c\u9b3c\u8c37\u201d\uff0c\u4f46\u65e0\u4eba\u77e5\u9053\u201c\u9b3c\u8c37\u201d\u786e\u5b9e\u7684\u5730\u70b9\uff0c\u4e5f\u5f88\u5c11\u4eba\u53bb \u8fc7\uff0c\u53ea\u542c\u4f20\u8a00\u5728\u96c1\u8361\u5c71\u4e2d\u3002\u8fd9\u4e8c\u4eba\u8f7b\u6613\u4e0d\u51fa\u8c37\uff0c\u53ef\u662f\u4ed6\u4e8c\u4eba\u8981\u8d70\u51fa\u8c37\u6765\uff0c\u5728\u6c5f\u6e56\u4e0a\u4e00\u9732\u9762\uff0c \u5fc5\u5b9a\u9614\u51fa\u51e0\u4ef6\u8f70\u52a8\u6b66\u6797\u7684\u5927\u4e8b\u6765\u3002 \u3000\u3000\u90a3\u4e0e\u843d\u9b44\u5c11\u4e2d\u52a8\u624b\u7684\u5947\u77ee\u5c0f\u50ee\uff0c\u662f\u4e09\u4eba\u552f\u4e00\u65e0\u4e8c\u72ec\u4f20\u5f1f\u5b50\uff0c\u4e43\u662f\u4e8c\u4eba\u5728\u5c71\u8def\u4e0a\u68c0\u5230\u7684\u4e00 \u4e2a\u5f03\u5a74\uff0c\u201c\u6c5f\u5357\u4e8c\u5947\u201d\u672c\u6765\u4e0d\u559c\u964c\u751f\u4eba\uff0c\u4e0d\u77e5\u600e\u4e48\u4e00\u6765\uff0c\u7ade\u5927\u53d1\u5584\u5fc3\uff0c\u628a\u8fd9\u62fe\u6765\u7684\u5f03\u5a74\u6276\u517b \u957f\u5927\uff0c\u4e14\u62db\u4ee5\u6b66\u827a\u3002\u56e0\u4ed6\u751f\u5f97\u5947\u77ee\uff0c\u53c8\u65e0\u540d\u5c11\u59d3\uff0c\u56e0\u6b64\u53eb\u505a\u201c\u4e09\u5bf8\u4e01\u201d\uff0c\u53c8\u6709\u4e2a\u5916\u53f7\uff0c\u540d\u53eb \u201c\u5c0f\u4e27\u95e8\u201d\u3002 \u3000\u3000\u522b\u770b\u201c\u5c0f\u4e27\u95e8\u4e09\u5bf8\u4e01\u201d\u957f\u5f97\u4eba\u4e0d\u50cf\u4eba\uff0c\u9b3c\u4e0d\u50cf\u9b3c\uff0c\u5374\u5f97\u5230\u201c\u6c5f\u5357\u4e8c\u5947\u201d\u7684\u4eb2\u4f20\uff0c\u5177\u6709\u4e86 \u4e8c\u5947\u56db\u4e94\u6210\u7684\u529f\u529b\uff0c\u867d\u4e0d\u6562\u8bf4\u5929\u4e0b\u65e0\u654c\uff0c\u4f46\u4e5f\u53ef\u6324\u8eab\u6b66\u6797\u4e00\u6d41\u9ad8\u624b\u4e4b\u5217\u3002 \u3000\u3000\u5982\u4eca\u8fd9\u843d\u9b44\u5c11\u5e74\uff0c\u7ade\u4e0e\u201c\u4e09\u5bf8\u4e01\u201d\u6253\u4e86\u4e2a\u68cb\u9022\u5bf9\u624b\uff0c\u8fd9\u8fd8\u4e0d\u4f7f\u201c\u6c5f\u5357\u4e8c\u5947\u201d\u5927\u611f\u5947\u602a \u5417\uff1f \u3000\u3000\u201c\u6c5f\u5357\u4e8c\u5947\u201d\u56db\u53ea\u773c\u775b\u77aa\u5f97\u6eda\u5706\uff0c\u6ce8\u89c6\u7740\u843d\u9b44\u5c11\u5e74\u7684\u4e00\u62db\u4e00\u5f0f\uff0c\u89c1\u5176\u8fd0\u7528\u7684\u201c\u5927\u64d2\u62ff\u201d \u624b\u6cd5\uff0c\u7adf\u4e0e\u4e8c\u54e5\u201c\u9b3c\u8c37\u9690\u91cc\u6587\u6b63\u5947\u201d\u6240\u4f20\u6388\u201c\u4e09\u5bf8\u4e01\u201d\u7684\u201c\u5c60\u9f99\u5341\u516b\u624b\u201d\u6709\u4e9b\u7c7b\u4f3c\uff0c\u4e8c\u4eba\u5fc3 \u4e2d\u4e0d\u4ec5\u5947\u602a\uff0c\u800c\u4e14\u611f\u5230\u8bf4\u4e0d\u51fa\u7684\u8be7\u5f02\u3002 \u3000\u3000\u81f3\u4e8e\u53e6\u4e00\u8336\u5ea7\u4e0a\u90a3\u9ad8\u8d35\u5c11\u5e74\uff0c\u66f4\u662f\u5f53\u4eca\u6b66\u6797\u54cd\u5f53\u5f53\u7684\u4eba\u7269\uff0c\u53eb\u8d77\u540d\u53f7\u6765\uff0c\u53ef\u8bf4\u662f\u5929\u4e0b\u6b66 \u6797\u65e0\u4eba\u4e0d\u77e5\u3002\u5f53\u4eca\u6b66\u6797\u5a01\u540d\u6700\u663e\u8d6b\u7684\uff0c\u4fbf\u662f\u201c\u6b66\u6797\u56db\u516c\u5b50\u201d\u3002\u6240\u8c13\u201c\u5b89\u4e50\u98ce\u6d41\uff0c\u98d8\u96f6\u7aef\u65b9\uff0c \u51cc\u98ce\u65e0\u60c5\uff0c\u7965\u9e9f\u70ed\u80a0\u201d\u3002 \u3000\u3000\u201c\u6b66\u6797\u56db\u516c\u5b50\u201d\u540d\u91cd\u6b66\u6797\uff0c\u4e3a\u5f53\u4eca\u6b66\u6797\u52bf\u529b\u6700\u6d69\u5927\u7684\u56db\u5927\u8c6a\u95e8\uff0c\u4ff1\u5404\u7f51\u7f57\u5929\u4e0b\u6b66\u6797\u9ad8 \u624b\uff0c\u95e8\u4e0b\u98df\u5ba2\uff0c\u5947\u4eba\u5f02\u58eb\uff0c\u4e0a\u767e\u8bba\u5343\u3002\u524d\u9762\u63d0\u5230\u7684\uff0c\u5df2\u6709\u201c\u5b89\u4e50\u516c\u5b50\u201d\u4e91\u94ee\uff0c\u201c\u51cc\u98ce\u516c\u5b50\u201d \u6155\u5bb9\u9752\uff0c\u800c\u5f53\u524d\u8336\u5ea7\u4e0a\u5750\u7740\u7684\u8fd9\u9ad8\u8d35\u65e0\u6bd4\u7684\u5c11\u5e74\uff0c\u6b63\u662f\u88ab\u6c5f\u6e56\u4e0a\u606d\u79f0\u4e3a\u53e4\u9053\u70ed\u80a0\u7684\u201c\u7965\u78f7\u516c \u5b50\u201d\u91d1\u5f69\u7115\u3002 \u3000\u3000\u201c\u7965\u9e9f\u516c\u5b50\u201d\u91d1\u5f69\u7115\uff0c\u4e16\u5c45\u5357\u4eac\uff0c\u5bb6\u8d44\u5de8\u4e07\uff0c\u53c8\u52a0\u4e0a\u4ed6\u5e7c\u5f97\u5f02\u4eba\u4f20\u6388\uff0c\u6b66\u529f\u9ad8\u5f3a\uff0c\u95e8\u4e0b \u98df\u5ba2\u4e0a\u5343\uff0c\u4e0d\u4e4f\u6b66\u6797\u4e2d\u7b2c\u4e00\u6d41\u9ad8\u624b\uff0c\u4e0e\u53e6\u5916\u4e09\u516c\u5b50\uff0c\u5728\u6b66\u6797\u9f50\u540d\uff0c\u53ef\u4ee5\u8bf4\u662f\u7ad9\u5728\u5357\u4eac\u57ce\u2014\u8dfa \u811a\uff0c\u6574\u4e2a\u4e2d\u539f\u6b66\u6797\u90fd\u4f1a\u4e71\u98a4\u7684\u4eba\u7269\u3002 \u3000\u3000\u4e0e\u201c\u7965\u9e9f\u516c\u5b50\u201d\u5750\u5728\u4e00\u8d77\u7684\u7eaf\u7f8e\u5c11\u5973\uff0c\u4e43\u662f\u201c\u7965\u9e9f\u516c\u5b50\u201d\u7684\u5ae1\u4eb2\u80de\u59b9\uff0c\u95fa\u540d\u91d1\u5f69\u51e4\uff0c\u56e0 \u559c\u5728\u9b13\u65c1\u659c\u66ff\u4e00\u679d\u6885\u82b1\uff0c\u4eba\u53c8\u751f\u5f97\u6e05\u65b0\u8131\u4fd7\uff0c\u4fca\u7f8e\u65e0\u4f26\uff0c\u6545\u6b64\u6709\u4e2a\u5916\u53f7\u53eb\u201c\u4e00\u679d\u6885\u201d\u3002 \u3000\u3000\u8fd9\u4e00\u5929\uff0c\u5929\u6c14\u5b9e\u5728\u61ca\u70ed\u96be\u6328\uff0c\u201c\u7965\u9e9f\u516c\u5b50\u201d\u5144\u59b9\u5e26\u7740\u51e0\u4e2a\u95e8\u5ba2\uff0c\u53ca\u5df2\u51fa\u5ac1\u59d0\u59d0\u7684\u4e00\u5bf9\u513f \u5973\uff0c\u660e\u660e\u548c\u5170\u5170\uff0c\u5230\u71d5\u5b50\u77fe\u8336\u5ea7\u6765\u5403\u8336\u4e58\u51c9\uff0c\u6ca1\u60f3\u5230\u5374\u9047\u5230\u90a3\u6781\u4e3a\u624e\u773c\u7684\u201c\u6c5f\u5357\u4e8c\u5947\u201d\u53ca \u201c\u4e09\u5bf8\u4e01\u201d\u5c0f\u4e27\u95e8\u3002 \u3000\u3000\u201c\u6c5f\u5357\u4e09\u5947\u201d\u8fd9\u4e00\u6b21\u5e26\u7740\u72ec\u2014\u7684\u5f1f\u5b50\u79bb\u8c37\u4e0b\u5c71\uff0c\u8fd8\u771f\u662f\u6765\u627e\u201c\u7965\u9e9f\u516c\u5b50\u201d\u7684\u78b4\u513f\u7684\u3002 \u3000\u3000\u770b\u5230\u201c\u7965\u9e9f\u516c\u5b50\u201d\u4e00\u9732\u9762\uff0c\u4e24\u4e2a\u8001\u602a\u7269\u4fbf\u51b7\u8a00\u51b7\u8bed\u5730\u5728\u4e00\u8fb9\u6572\u5c71\u95e8\uff0c\u504f\u504f\u201c\u7965\u9e9f\u516c\u5b50\u201d \u52bf\u53ef\u76d6\u5929\uff0c\u6db5\u517b\u529f\u592b\u5374\u6781\u597d\uff0c\u5728\u672a\u6478\u6e05\u201c\u6c5f\u5357\u4e8c\u5947\u201d\u95e8\u8def\u4e4b\u524d\uff0c\u5c3d\u7ba1\u201c\u6c5f\u5357\u4e8c\u5947\u201d\u51b7\u8a00\u51b7 \u8bed\uff0c\u5c31\u662f\u4e0d\u63a5\u78b4\u3002\u751a\u81f3\u4ed6\u7684\u95e8\u4e0b\u5fcd\u4e0d\u4f4f\u6c14\uff0c\u51e0\u6b21\u60f3\u8d77\u8eab\u5e94\u5bf9\uff0c\u4e5f\u88ab\u201c\u7965\u9e9f\u516c\u5b50\u201d\u6697\u4e2d\u5236\u6b62 \u4f4f\u3002 \u3000\u3000\u201c\u6c5f\u5357\u4e8c\u5947\u201d\u96be\u5f97\u5230\u8c37\u5916\u4e00\u6b21\uff0c\u5e76\u4e0d\u8be6\u7ec6\u4e86\u89e3\u201c\u7965\u9e9f\u516c\u5b50\u201d\u5728\u4e2d\u539f\u6b66\u6797\u7684\u52bf\u529b\uff0c\u8fd9\u6b21\u51fa \u8c37\u6765\u627e\u201c\u7965\u9e9f\u516c\u5b50\u201d\u4e00\u8f83\u77ed\u957f\uff0c\u4e5f\u662f\u53d7\u4e86\u5c0f\u4eba\u7684\u6311\u62e8\u79bb\u95f4\uff0c\u89c1\u51b7\u5632\u70ed\u8bbd\u201c\u7965\u9e9f\u516c\u5b50\u201d\u5747\u4e0d\u4e3a \u6240\u52a8\uff0c\u4e00\u65f6\u4e4b\u95f4\u5012\u4e0d\u597d\u610f\u601d\u65e0\u7aef\u5bfb\u4ec7\uff0c\u56e0\u6b64\uff0c\u53cc\u65b9\u7684\u4eba\uff0c\u4e00\u65f6\u4e4b\u95f4\u50f5\u6301\u5728\u90a3\u91cc\u3002 \u3000\u3000\u6070\u5de7\u6b64\u65f6\u843d\u9b44\u5c11\u5e74\u4f4e\u5934\u884c\u6765\uff0c\u4ed6\u6ee1\u8179\u5fc3\u601d\uff0c\u832b\u7136\u8d70\u7740\uff0c\u65e0\u5fc3\u4e2d\u628a\u5170\u5170\u624b\u4e2d\u7275\u7740\u73a9\u7684\u4e00\u53ea \u86b1\u8722\u8e0f\u6b7b\u4e86\u3002 \u3000\u3000\u201c\u5582\uff01\u201d\u5170\u5170\u5927\u58f0\u6025\u547c\uff0c\u5e76\u62c9\u8d77\u6346\u86b1\u8722\u7684\u7ec6\u7ebf\uff0c\u4e00\u770b\u86b1\u8722\u5df2\u88ab\u843d\u9b44\u5c11\u5e74\u8e0f\u6b7b\uff0c\u9ad8\u53eb\u9053\uff1a \u201c\u770b\u4f60\u628a\u6211\u7684\u86b1\u8722\u8e0f\u6b7b\u4e86\uff0c\u8981\u4f60\u8d54\uff01\u201d \u3000\u3000\u8c01\u77e5\u843d\u9b44\u5c11\u5e74\u6b63\u5728\u60f3\u5fc3\u4e8b\uff0c\u6839\u672c\u672a\u542c\u5230\u5170\u5170\u547c\u53eb\uff0c\u4f9d\u7136\u4f4e\u5934\u5411\u524d\u8d70\u7740\u2026\u3002 \u3000\u3000\u5170\u5170\u867d\u662f\u4e00\u4e2a\u516d\u4e03\u5c81\u7684\u5c0f\u5973\u5b69\uff0c\u4f46\u751f\u4e8e\u6b66\u5b66\u4e16\u5bb6\uff0c\u6b66\u529f\u5df2\u6709\u76f8\u5f53\u6839\u5e95\uff0c\u89c1\u843d\u9b44\u5c11\u5e74\u8e0f\u6b7b \u5979\u7684\u86b1\u8722\uff0c\u4efb\u51ed\u5979\u53eb\u8fde\u5934\u4e5f\u4e0d\u56de\uff0c\u7ade\u6c14\u8d77\u6765\uff0c\u56de\u624b\u4ece\u82b1\u76c6\u5185\u6293\u8d77\u4e00\u679a\u5375\u77f3\uff0c\u5c0f\u624b\u6307\u4e00\u5c48\u4e00 \u5f39\uff0c\u7ade\u4ee5\u201c\u5f39\u6307\u94f6\u4e38\u201d\u6253\u6697\u5668\u7684\u624b\u6cd5\uff0c\u5411\u7740\u843d\u9b44\u5c11\u5e74\u540e\u8111\u6253\u53bb\u3002 \u3000\u3000\u4e00\u662f\u843d\u9b44\u5c11\u5e74\u6ee1\u6000\u5fc3\u4e8b\uff0c\u5728\u4eba\u70df\u7a20\u5bc6\u4e4b\u5904\uff0c\u672a\u9632\u6709\u4eba\u6697\u7b97\uff0c\u4e8c\u662f\u5170\u5170\u201c\u5f39\u6307\u94f6\u4e38\u201d\u867d\u7136 \u624b\u6cd5\u5947\u51c6\uff0c\u4f46\u529b\u9053\u7a76\u7adf\u662f\u5dee\uff0c\u51fa\u624b\u540e\u4e0d\u5e26\u4e1d\u6beb\u7834\u7a7a\u4e4b\u58f0\u3002 \u3000\u3000\u56e0\u6b64\uff0c\u7ade\u6253\u4e86\u4e2a\u6b63\u7740\u3002\u5c0f\u5b69\u8138\u8584\uff0c\u5f85\u843d\u9b44\u5c11\u5e74\u88ab\u6253\u56de\u5934\uff0c\u5979\u5374\u95f7\u7740\u6c14\u4e0d\u6562\u5f00\u8154\u4e86\u3002 \u3000\u3000\u843d\u9b44\u5c11\u5e74\u56de\u5934\u770b\u4e86\u534a\u5929\uff0c\u770b\u4e0d\u51fa\u662f\u8c01\u6253\u6765\u7684\uff0c\u518d\u8f6c\u5934\u8d70\u53bb\u65f6\uff0c\u5170\u5170\u5411\u7740\u660e\u660e\u5410\u820c\u4e00\u7b11\u3002 \u660e\u660e\u8bef\u4ee5\u4e3a\u5170\u5170\u662f\u5411\u4ed6\u6311\u6218\u6bd4\u8d5b\uff0c\u6545\u5373\u4e5f\u5728\u82b1\u76c6\u4e2d\u53d6\u4e86\u4e00\u679a\u5375\u77f3\uff0c\u7b2c\u4e8c\u6b21\u6253\u4e2d\u843d\u9b44\u5c11\u5e74\u7684 \u5934\u3002 \u3000\u3000\u6ca1\u60f3\u5230\u5c31\u56e0\u4e3a\u8fd9\u4e00\u95f9\uff0c\u53c8\u7ed9\u201c\u6c5f\u5357\u4e8c\u5947\u201d\u6293\u4f4f\u5f31\u70b9\uff0c\u201c\u9b3c\u8c37\u9690\u53df\u201d\u6587\u6b63\u5947\u9996\u5148\u62db\u547c\u843d\u9b44 \u5c11\u5e74\uff0c\u501f\u673a\u717d\u52a8\uff0c\u8981\u843d\u9b44\u5c11\u5e74\u627e\u201c\u6837\u9cde\u516c\u5b50\u201d\u7b97\u5e10\u90a3\u4e48\uff0c\u4ed6\u201c\u6c5f\u5357\u4e8c\u5947\u201d\u5e08\u5f92\uff0c\u4e5f\u53ef\u501f\u673a\u548c \u201c\u7965\u9e9f\u516c\u5b50\u201d\u52a8 \u3000\u3000\u8c01\u77e5\u8bdd\u4e0d\u6295\u673a\uff0c\u201c\u6c5f\u5357\u4e8c\u5947\u201d\u81ea\u5df1\u7adf\u8ddf\u843d\u9b44\u5c11\u5e74\u8d77\u4e86\u51b2\u7a81\u3002\u7b49\u5230\u201c\u5c0f\u4e27\u95e8\u4e09\u5bf8\u4e01\u201d\u4e0e\u843d \u9b44\u5c11\u5e74\u6253\u8d77\u6765\uff0c\u6c5f\u5357\u4e8c\u5947\u201d\u624d\u77e5\u9053\u770b\u8d70\u4e86\u773c\uff0c\u8fd9\u843d\u9b44\u5c11\u5e74\u7adf\u8eab\u8d1f\u7edd\u4e16\u6b66\u529f\u3002 \u3000\u3000\u201c\u7965\u9e9f\u516c\u5b50\u201d\u5144\u59b9\u6bd4\u6c5f\u5357\u4e8c\u5947\u66f4\u611f\u60ca\u5947\u3002\u6697\u60f3\uff1a\u5728\u81ea\u5df1\u52bf\u529b\u8303\u56f4\u4ee5\u5185\uff0c\u5c45\u7136\u6709\u4eba\u6562\u6765\u5bfb \u8845\uff1f\u5df2\u662f\u5929\u5927\u7684\u51fa\u4eba\u610f\u5916\u5982\u4eca\u53c8\u89c1\u4e00\u4e2a\u843d\u9b44\u5c11\u5e74\uff0c\u7ade\u6709\u5982\u8bb8\u9ad8\u5f3a\u6b66\u529f\uff0c\u800c\u4e14\u5728\u81ea\u5df1\u5c45\u5730\u5185\u51fa \u73b0\uff0c\u95e8\u4e0b\u4eba\u7ade\u6beb\u65e0\u6240\u6089\uff0c\u66f4\u662f\u4ee4\u4eba\u532a\u5937\u6240\u601d\u3002 \u3000\u3000\u56e0\u6b64\uff0c\u7965\u9e9f\u516c\u5b50\u201d\u5144\u59b9\uff0c\u4ee5\u53ca\u95e8\u4e0b\u98df\u5ba2\u4e2d\u7684\u6b66\u6797\u9ad8\u624b\uff0c\u4e5f\u4ff1\u90fd\u7741\u5927\u4e86\u773c\u775b\uff0c\u671b\u5b9a\u573a\u4e2d\u4e8c \u4eba\u76f8\u640f\u3002 \u3000\u3000\u843d\u9b44\u5c11\u5e74\u4e0e\u2018\u2018\u4e09\u5bf8\u4e01\u5c0f\u4e27\u95e8\u201d\uff0c\u5feb\u653b\u5feb\u6253\uff0c\u6643\u773c\u53c8\u662f\u5341\u4f59\u62db\u8fc7\u53bb\u4e86\uff0c \u3000\u3000\u2018\u4e09\u5bf8\u4e01\u201d\u522b\u770b\u4eba\u77ee\uff0c\u8eab\u6cd5\u7684\u786e\u7075\u6d3b\u65e0\u6bd4\uff0c\u7ffb\u3001\u817e\u3001\u7eb5\u3001\u8dc3\u3001\u5947\u5feb\u5982\u7535\uff0c\u800c\u4e14\uff0c\u201c\u5c60\u9f99 \u5341\u516b\u624b\u201d\u51fa\u62db\u4e4b\u51c6\uff0c\u62ff\u634f\u4e4b\u5999\uff0c\u5904\u5904\u653b\u654c\u4e4b\u5fc5\u6551\uff0c\u89c1\u62db\u6253\u62db\uff0c\u89c1\u5f0f\u6253\u5f0f\uff0c\u6ce2\u8be1\u4e91\u8c32\uff0c\u5947\u5965\u7edd \u4f26\u3002 \u3000\u3000\u843d\u9b44\u5c11\u5e74\u51fa\u624b\u62db\u5f0f\uff0c\u4ea6\u662f\u7cbe\u5947\u795e\u5999\uff0c\u4e14\u638c\u51fa\u970d\u7136\u751f\u98ce\uff0c\u663e\u89c1\u5185\u529f\u8981\u6bd4\u201c\u4e09\u5bf8\u4e01\u5c0f\u4e27\u95e8\u201d \u6df1\u539a\uff0c\u53ea\u662f\u8eab\u6cd5\u4e0d\u5982\u201c\u4e09\u5bf8\u4e01\u201d\u7075\u6d3b\uff0c\u51fa\u624b\u62db\u5f0f\u4e5f\u663e\u5f97\u751f\u758f\u8fdf\u6ede\uff0c\u56e0\u6b64\uff0c\u4e8c\u4eba\u7adf\u6253\u4e86\u4e2a\u65d7\u9f13 \u76f8\u5f53\uff0c\u96be\u5206\u8f69\u8f7e\u3002 \u3000\u3000\u65f6\u95f4\u4e00\u4e45\uff0c\u843d\u9b44\u5c11\u5e74\u5403\u4e86\u62db\u5f0f\u751f\u758f\u7684\u4e8f\u3002 \u3000\u3000\u5728\u201c\u4e09\u5bf8\u4e01\u201d\u8dc3\u8d77\u8eab\u5f62\uff0c\u65bd\u51fa\u4e00\u62db\u201c\u4e91\u9f99\u4e09\u73b0\u201d\uff0c\u5de6\u624b\u51cc\u7a7a\u4e24\u6293\uff0c\u865a\u6309\u843d\u9b44\u5c11\u5e74\u9762\u95e8\uff0c \u53f3\u638c\u62a1\u4e86\u4e2a\u534a\u5706\uff0c\u731b\u62cd\u843d\u9b44\u5c11\u5e74\u524d\u80f8\u201c\u4e09\u9633\u201d\u8981\u5bb3\u65f6\uff0c\u843d\u9b44\u5c11\u5e74\u5e94\u8be5\u662f\u7528\u201c\u6a2a\u4e91\u65ad\u5cf0\u201d\u62db \u5f0f\uff0c\u5d29\u817f\u6a2a\u8eab\uff0c\u907f\u62db\u8fdb\u62db\u3002 \u3000\u3000\u53ef\u662f\uff0c\u843d\u9b44\u5c11\u5e74\u62db\u5f0f\u4e0d\u7eaf\uff0c\u6a2a\u8eab\u5374\u672a\u5d29\u817f\uff0c\u6293\u5411\u9762\u95e8\u7684\u4e24\u638c\u662f\u8eb2\u8fc7\u4e86\uff0c\u62cd\u5411\u524d\u80f8\u7684\u4e00 \u638c\uff0c\u5374\u518d\u4e5f\u5316\u89e3\u4e0d\u5f00\u3002 \u3000\u3000\u201c\u4e09\u5bf8\u4e01\u201d\u5916\u53f7\u4eba\u79f0\u201c\u5c0f\u4e27\u95e8\u201d\uff0c\u81ea\u662f\u5fc3\u72e0\u624b\u8fa3\uff0c\u6253\u4e86\u534a\u5929\u672a\u5f97\u624b\uff0c\u53c8\u662f\u5f53\u7740\u4e8c\u4f4d\u6069\u5e08 \u7684\u9762\uff0c\u8138\u4e0a\u65e9\u89c9\u6302\u4e0d\u4f4f\u4e86\uff0c\u5982\u4eca\u89c1\u4e00\u638c\u5f97\u624b\uff0c\u7acb\u523b\u53c8\u52a0\u4e86\u4e8c\u6210\u529b\u9053\uff0c\u8d2f\u6ce8\u5728\u53f3\u638c\u4e4b\u4e0a\uff0c\u5410\u6c14 \u5f00\u58f0\uff0c\u201c\u7740\uff01\u201d\u66b4\u559d\u58f0\u4e2d\uff0c\u6500\u631f\u52b2\u98ce\uff0c\u731b\u5411\u843d\u9b44\u5c11\u5e74\u524d\u80f8\u8981\u5bb3\u62cd\u81f3\u3002 \u3000\u3000\u8fd9\u4e00\u4e8b\u8981\u88ab\u6253\u5b9e\uff0c\u843d\u9b44\u5c11\u5e74\u4e0d\u6b7b\uff0c\u4e5f\u5f97\u88ab\u6253\u6210\u91cd\u4f24\u3002 \u3000\u3000\u201c\u5440\uff01\u201d\u4e0d\u5c11\u4eba\u60ca\u547c\u51fa\u58f0\uff0c\u5c24\u5176\u90a3\u201c\u4e00\u679d\u6885\u201d\u91d1\u5f69\u98ce\uff0c\u7c89\u8138\u4e0a\u7ade\u6d41\u8fc7\u4e00\u4e1d\u60cb\u60dc\u7684\u795e\u8272\uff0c \u4f3c\u662f\u4e0d\u613f\u89c1\u843d\u9b44\u5c11\u5e74\u6a2a\u5c38\u5f53\u573a\uff0c\u4f46\u53c8\u4e0d\u597d\u610f\u601d\u8d38\u7136\u51fa\u624b\u63f4\u6551\u2026\u2026 \u3000\u3000\u5c31\u5728\u8fd9\u5343\u94a9\u4e00\u53d1\u4e4b\u9645\uff0c\u7a81\u542c\u843d\u9b44\u5c11\u5e74\u5927\u559d\u4e00\u58f0\uff1a\u201c\u563f\uff01\u201d\u2026\u2026 \u3000\u3000\u201c\u6f8e\u201d\u7684\u4e00\u58f0\u5927\u9707\uff0c\u5728\u522b\u4eba\u770b\u6765\u4e07\u96be\u8eb2\u95ea\u7684\u4e00\u62db\uff0c\u843d\u9b44\u5c11\u5e74\u7adf\u80fd\u5438\u80f8\u8fd0\u638c\u548c\u201c\u4e09\u5bf8\u4e01\u201d \u786c\u5bf9\u4e00\u638c\u3002 \u3000\u3000\u201c\u4e09\u5bf8\u4e01\u201d\u4eba\u5c0f\u4f53\u8f7b\uff0c\u88ab\u843d\u9b44\u5c11\u5e74\u638c\u529b\u9707\u5f97\u5982\u65ad\u7ebf\u7eb8\u9e22\u822c\u5e73\u76f4\u98de\u4e86\u51fa\u53bb\uff0c\u98de\u53bb\u7684\u65b9\u5411\u65e0 \u5de7\u4e0d\u5de7\u7684\uff0c\u6b63\u662f\u201c\u6c5f\u5357\u4e8c\u5947\u201d\u5750\u7684\u90a3\u8336\u5ea7\u4e0a\u3002 \u3000\u3000\u201c\u6c5f\u5357\u4e8c\u5947\u201d\u8138\u8272\u7acb\u53d8\uff0c\u5927\u5947\u201c\u7ea2\u53d1\u8001\u4eba\u201d\u5e38\u53bb\u6076\u4e00\u4e3e\u53cc\u624b\uff0c\u63a5\u4f4f\u4e86\u5e73\u98de\u800c\u81f3\u7684\u201c\u4e09\u5bf8 \u4e01\u201d\uff0c\u5f80\u7af9\u6905\u4e0a\u4e00\u653e\uff0c\u201c\u4e09\u5bf8\u4e01\u201d\u5374\u4e00\u633a\u8eab\u53c8\u4ece\u7af9\u6905\u4e0a\u7ad9\u8d77\u6765\uff0c\u7528\u624b\u6478\u4e86\u6478\u8111\u888b\uff0c\u5c0f\u773c\u4e00 \u7ffb\uff0c\u5c16\u58f0\u5c16\u6c14\u5730\u8bf4\u9053\uff1a\u201c\u5e08\u7236\uff0c\u4f60\u653e\u5fc3\uff01\u6328\u8fd9\u4e48\u4e00\u4e0b\u4e24\u4e0b\u7684\uff0c\u5f92\u513f\u8fd8\u4e0d\u5728\u4e4e\uff01\u201d \u3000\u3000\u8bf4\u7740\u53c8\u60f3\u7eb5\u8eab\u5411\u524d\u518d\u6218\u3002 \u3000\u3000\u4f17\u4eba\u90fd\u60f3\u4e0d\u900f\u201c\u4e09\u5bf8\u4e01\u201d\u88ab\u843d\u9b44\u5c11\u4e2d\u4e00\u638c\u9707\u98de\u53ca\u4e08\uff0c\u7adf\u4e1d\u6beb\u672a\u8d1f\u4f24\uff0c\u4e0d\u77e5\u662f\u4f55\u9053\u7406\uff1f \u3000\u3000\u53ef\u662f\uff0c\u4e8c\u5947\u201c\u9b3c\u8c37\u9690\u91cc\u201d\u6587\u6b63\u5947\u5374\u201c\u547c\u201d\u7684\u4e00\u58f0\uff0c\u4ece\u5ea7\u4f4d\u4e0a\u7ad9\u8d77\u6765\uff0c\u6325\u821e\u7740\u5927\u8896\uff0c\u6447\u6447 \u6446\u63f4\u5730\u8d70\u8fd1\u843d\u9b44\u5c11\u5e74\u9762\u524d\uff0c\u53cc\u773c\u4e00\u77aa\uff0c\u5411\u843d\u9b44\u5c11\u5e74\u5389\u58f0\u53f1\u9053\uff1a\u5c14\u59d3\u751a\u540d\u8c01\uff1f\u4f55\u4eba\u95e8\u4e0b\uff1f\u201d \u3000\u3000\u843d\u9b44\u5c11\u5e74\u6602\u7136\u7b54\u9053\uff1a\u201c\u591c\u4e0b\u5c55\u767d\uff0c\u51fa\u8eab\u5e08\u95e8\uff0c\u5374\u65e0\u53ef\u5949\u544a\uff01\u201d \u3000\u3000\u201c\u9b3c\u8c37\u9690\u91cc\u201d\u626c\u8138\u60f3\u4e86\u4e00\u4f1a\uff0c\u597d\u50cf\u6ca1\u542c\u8fc7\u201c\u5c55\u767d\u201d\u8fd9\u4e48\u4e00\u53f7\u4eba\u7269\u3002\u77ac\u5373\u6447\u5934\u6643\u8111\u5730\u8bf4\uff1a \u201c\u8fd9\u5c31\u5947\u4e86\uff01\u5c14\u65e2\u662f\u8bf4\u4e0d\u51fa\u5e08\u95e8\u6765\u5386\uff0c\u6240\u7528\u64d2\u62ff\u624b\u6cd5\uff0c\u7adf\u548c\u8001\u592b\u6240\u521b\u624b\u6cd5\u76f8\u540c\uff1f\u201d \u3000\u3000\u5c55\u767d\u548c\u201c\u4e09\u5bf8\u4e01\u201d\u52a8\u624b\u65f6\uff0c\u5fc3\u91cc\u4e00\u76f4\u611f\u5230\u5947\u602a\uff0c\u89c1\u90a3\u4f8f\u5112\u4f3c\u7684\u5c0f\u4eba\uff0c\u65bd\u5c55\u624b\u6cd5\uff0c\u7adf\u662f\u719f \u6089\u5f97\u5f88\u3002\u5982\u4eca\u53c8\u7ecf\u8fd9\u51ac\u70d8\u5148\u751f\u822c\u7684\u8001\u4eba\u4e00\u95ee\uff0c\u731b\u7136\u8bb0\u8d77\u201c\u4e09\u5bf8\u4e01\u201d\u7684\u64d2\u62ff\u62db\u5f0f\uff0c\u8ddf\u201c\u72ec\u811a\u98de \u9b54\u201d\u674e\u4e3e\u7684\u64d2\u62ff\u624b\u6cd5\u5982\u51fa\u4e00\u8f99\u3002\u4ee5\u4e3a\u773c\u524d\u8fd9\u4f8f\u5112\u51ac\u70d8\uff0c\u8ddf\u201c\u72ec\u811a\u5b83\u9b54\u201d\u5e08\u51fa\u4e00\u95e8\uff0c\u4e8e\u662f\u51b7\u7b11 \u9053\uff1a\u201c\u7f3a\u817f\u5c11\u811a\u4e4b\u4eba\uff0c\u8001\u5148\u751f\u53ef\u8ba4\u8bc6\uff1f\u201d \u3000\u3000\u201c\u9b3c\u8c37\u9690\u5965\u201d\u95fb\u8a00\u8138\u8272\u60e8\u53d8\uff0c\u76db\u6c14\u51cc\u4eba\u7684\u6001\u5ea6\uff0c\u4e00\u53d8\u800c\u4e3a\u754f\u7f29\u96be\u5b89\u7684\u6837\u5b50\uff0c\u5636\u58f0\u8bf4\u9053\uff1a \u201c\u600e\u4e48\uff1f\u4f60\u662f\u4ed6\u2014\u2014\u7684\u5f1f\u5b50\uff1f\u201d \u3000\u3000\u201c\u8d64\u53d1\u8001\u4eba\u201d\u4e5f\u6643\u8eab\u9a70\u8fd1\uff0c\u6115\u7136\u52a8\u5bb9\uff0c\u6328\u7740\u201c\u9b3c\u8c37\u9690\u53df\u201d\u7684\u80a9\u8180\u95ee\u9053\uff1a\u201c\u8fd9\u5c11\u5e74\u662f\u4ed6\u7684 \u4f20\u4eba\uff1f\u4ed6\u2014\u2014\u8fd8\u6d3b\u7740\uff1f\u201d \u3000\u3000\u5c55\u767d\u770b\u8fd9\u4e24\u4e2a\u5947\u7279\u7684\u8001\u4eba\u5982\u6b64\u7d27\u5f20\uff0c\u83ab\u660e\u6240\u4ee5\u3002\u4f46\u81ea\u5df2\u8ddf\u201c\u72ec\u811a\u98de\u9b54\u201d\u52a8\u624b\u65f6\u5077\u5b66\u4e86\u4e24 \u62db\uff0c\u65e0\u8bba\u5982\u4f55\u4e5f\u4e0d\u80fd\u627f\u8ba4\u662f\u90a3\u8001\u602a\u7269\u7684\u5f1f\u5b50\uff0c\u4e8e\u662f\u6297\u58f0\u8bf4\u9053\uff1a\u2018\u4e8c\u4f4d\u522b\u7d27\u5f20\uff01\u5728\u4e0b\u53ef\u6ca1\u6709\u90a3 \u6837\u7684\u5e08\u7236\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u8001\u4eba\u5bb6\u4e5f\u6ca1\u6709\u90a3\u6837\u7684\u5f1f\u5b50\uff01\u201d\u7a81\u7136\u8fdc\u5904\u6709\u4eba\u63a5\u4e86\u8154\u3002 \u3000\u3000\u201c\u6211\u8001\u4eba\u5bb6\u4e00\u751f\u4e4b\u4e2d\uff0c\u53ea\u6536\u4e86\u4e24\u4e2a\u5f92\u5f1f\uff0c\u5c31\u6389\u4e86\u4e24\u6761\u817f\uff0c\u5982\u679c\u518d\u6536\u5f92\u5f1f\uff0c\u5c82\u4e0d\u662f\u9879\u4e0a\u8fd9 \u9897\u4eba\u5934\u4e5f\u4fdd\u4e0d\u4f4f\u4e86\u5417\uff01\u201d \u3000\u3000\u5c55\u767d\u95fb\u8a00\u8f6c\u5934\u4e00\u770b\uff0c\u6cbf\u7740\u6c5f\u8fb9\u8d70\u6765\u4e00\u4e2a\u8001\u4eba\uff0c\u90a3\u8001\u4eba\u9f50\u5927\u817f\u4ee5\u4e0b\uff0c\u53cc\u811a\u5168\u65e0\uff0c\u5374\u63a5\u88c5\u4e86 \u4e0a\u7c97\u4e0b\u7ec6\u7684\u6728\u68d2\uff0c\u627f\u63a5\u4f4f\u80a2\u4f53\uff0c\u67b6\u7740\u53cc\u62d0\u60a0\u60a0\u8361\u8361\u5730\u98d8\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u5c55\u767d\u518d\u56de\u5934\u4e00\u770b\uff0c\u201c\u6c5f\u5357\u4e8c\u5947\u201d\u65e9\u5df2\u4e0d\u89c1\u3002\u539f\u6765\u8fd9\u65ad\u53bb\u53cc\u817f\u7684\u8001\u4eba\u4e00\u51fa\u73b0\uff0c\u201c\u6c5f\u5357\u4e8c \u5947\u201d\u4fbf\u5413\u8dd1\u4e86\u3002 \u3000\u3000\u8336\u5ea7\u4e0a\u4e5f\u5931\u4e86\u201c\u4e09\u5bf8\u4e01\u201d\u7684\u5f71\u5b50\u3002 \u3000\u3000\u201c\u5b7d\u5f92\uff01\u201d\u65ad\u53bb\u53cc\u817f\u7684\u8001\u4eba\uff0c\u53e3\u4e2d\u559d\u9a82\uff1a\u201c\u6211\u8001\u4eba\u5bb6\u627e\u4e86\u4e09\u56db\u5341\u5e74\uff0c\u4eca\u5929\u8ba9\u6211\u627e\u5230\uff0c\u8fd8 \u60f3\u8dd1\u5417\uff1f\u201d \u3000\u3000\u8bf4\u7740\uff0c\u53cc\u62d0\u4e00\u6491\u5730\u9762\uff0c\u8eab\u5f62\u867d\u7136\u5e73\u5c04\u800c\u8d77\uff0c\u522b\u770b\u8fd9\u8001\u4eba\u65ad\u53bb\u53cc\u817f\uff0c\u501f\u7740\u53cc\u62d0\u7684\u652f\u6491\uff0c\u8dd1 \u8d77\u6765\u75be\u4fa0\u5982\u98de\uff0c\u53ea\u89c1\u4ed6\u4e00\u8dc3\u5c31\u4e8c\u4e09\u5341\u6587\u8fdc\uff0c\u53cc\u62d0\u8fde\u70b9\uff0c\u4eba\u5df2\u75be\u5982\u98de\u9e1f\u822c\u5411\u524d\u8ffd\u53bb\u3002 \u3000\u3000\u5c55\u767d\u987a\u7740\u8001\u4eba\u8ffd\u53bb\u7684\u65b9\u5411\u770b\u53bb\uff0c\u5728\u90a3\u5ca9\u5c71\u5341\u4e8c\u6d1e\u7684\u5d0e\u5c96\u5c71\u8def\u4e0a\uff0c\u6709\u4e09\u4e2a\u9ed1\u70b9\uff0c\u6b63\u5982\u75be\u77e2 \u822c\u5411\u6df1\u5c71\u5bc6\u6797\u4e2d\u9038\u53bb\u3002\u4e0d\u7528\u95ee\uff0c\u90a3\u6b63\u662f\u201c\u6c5f\u5357\u4e8c\u5947\u201d\u4e0e\u201c\u4e09\u5bf8\u4e01\u201d\u4e86\u3002 \u3000\u3000\u5c55\u767d\u83ab\u540d\u5176\u5999\uff0c\u4e00\u65f6\u6014\u5728\u90a3\u513f\u2026\u2026 \u3000\u3000\u7a81\u7136\u9762\u524d\u4e00\u82b1\uff0c\u73b0\u51fa\u4e00\u6761\u4eba\u5f71\uff0c\u6a2a\u963b\u5728\u5c55\u767d\u9762\u524d\uff0c\u5c55\u767d\u62ac\u5934\u4e00\u770b\uff0c\u89c1\u662f\u4e00\u4e2a\u5345\u4f59\u5c81\u7684\u58ee \u6c49\uff0c\u81ea\u5df1\u5e76\u4e0d\u8ba4\u8bc6\uff0c\u5fae\u5fae\u4e00\u6014\uff0c\u90a3\u58ee\u6c49\u5374\u53d1\u8bdd\u4e86\uff1a\u201c\u670b\u53cb\u6162\u8d70\uff01\u6211\u5bb6\u516c\u5b50\u7237\u627e\u4f60\u6709\u8bdd\u8bf4\uff01\u201d \u3000\u3000\u201c\u6055\u5728\u4e0b\u6ca1\u6709\u65f6\u95f4\uff01\u201d\u5c55\u767d\u81ea\u4ece\u5f97\u77e5\u201c\u6b66\u6797\u56db\u516c\u5b50\u201d\u662f\u6740\u7236\u7684\u4ec7\u4eba\uff0c\u5bf9\u516c\u5b50\u201d\u4e00\u8bcd\uff0c\u6781 \u7aef\u538c\u6076\uff0c\u5251\u7709\u4e00\u76b1\u8bf4\u9053\uff1a\u201c\u800c\u4e14\u5728\u4e0b\u4e5f\u4e0d\u8ba4\u8bc6\u4f60\u4eec\u4ec0\u4e48\u516c\u5b50\u7237\uff01\u201d\u8bf4\u7f62\uff0c\u95ea\u8fc7\u90a3\u58ee\u6c49\uff0c\u4ecd\u5411 \u524d\u8d70\u53bb\u3002 \u3000\u3000\u90a3\u58ee\u6c49\u51b7\u7b11\u4e00\u58f0\uff0c\u53c8\u6643\u8eab\u6321\u5728\u5c55\u767d\u9762\u524d\uff0c\u53cc\u773c\u4e00\u77aa\uff0c\u5411\u5c55\u767d\u559d\u9053\uff1a\u201c\u670b\u53cb\uff01\u522b\u4e0d\u8bc6\u62ac \u4e3e\uff0c\u516c\u5b50\u7237\u60f3\u89c1\u4f60\u662f\u77a7\u5f97\u8d77\u4f60\uff0c\u5982\u679c\u4e0d\u662f\u516c\u5b50\u7237\u6307\u660e\u4f1a\u4f60\uff0c\u4f60\u60f3\u89c1\u516c\u5b50\u7237\u8fd8\u89c1\u4e0d\u5230\u2026\u2026\u201d \u3000\u3000\u5c55\u767d\u89c1\u4ed6\u6321\u5728\u9762\u524d\u7f57\u55e6\u6ca1\u5b8c\uff0c\u5fc3\u4e2d\u65e9\u5df2\u4e0d\u8010\u70e6\uff0c\u4e0d\u7b49\u4ed6\u8bf4\u5b8c\uff0c\u5373\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u7b11\u8bdd\uff01\u7eb5 \u7136\u4f60\u4eec\u516c\u5b50\u7237\u662f\u4ec0\u4e48\u571f\u7687\u5e1d\u3001\u5730\u5934\u86c7\uff0c\u5728\u4e0b\u8bf4\u4e0d\u613f\u89c1\uff0c\u4fbf\u4e0d\u613f\u89c1\uff01\u2026\u2026\u201d \u3000\u3000\u58ee\u6c49\u89c1\u5c55\u767d\u51fa\u8a00\u4e0d\u900a\uff0c\u9a82\u5230\u4ed6\u4eec\u516c\u5b50\u5934\u4e0a\uff0c\u5927\u559d\u4e00\u58f0\uff1a\u201c\u4e0d\u613f\u89c1\uff0c\u4f60\u4e5f\u5f97\u89c1\uff01\u201d\u559d\u58f0\u4e2d \u5355\u81c2\u4e00\u6643\uff0c\u201c\u63a2\u9886\u53d6\u73e0\u201d\u76f4\u5411\u5c55\u767d\u80f8\u524d\u6293\u6765\u3002 \u3000\u3000\u5c55\u767d\u89c1\u4ed6\u51fa\u62db\u8fc5\u901f\uff0c\u52b2\u98ce\u7834\u7a7a\uff0c\u77e5\u9053\u8fd9\u58ee\u6c49\u6b66\u529f\u4e0d\u5f31\uff0c\u4f46\u4ed6\u6b63\u5728\u6c14\u5934\u4e0a\uff0c\u89c1\u58ee\u6c49\u62db\u5230\uff0c \u4e0d\u613f\u8eb2\u95ea\uff0c\u7528\u4e00\u62db\u201c\u7f1a\u864e\u64d2\u9f99\u201d\uff0c\u5de6\u638c\u5411\u58ee\u6c49\u6253\u6765\u7684\u53f3\u81c2\u642d\u53bb\uff0c\u53f3\u638c\u5e73\u80f8\u63a8\u51fa\uff0c\u731b\u5288\u58ee\u6c49\u524d \u80f8\u8981\u5bb3\u3002 \u3000\u3000\u50cf\u8fd9\u79cd\u201c\u4ee5\u653b\u8fd8\u653b\u201d\u7684\u6253\u6cd5\uff0c\u6b66\u6797\u5c11\u89c1\uff0c\u4e0d\u4f46\u5927\u51fa\u58ee\u6c49\u7684\u610f\u5916\uff0c\u5c31\u8fde\u5750\u5728\u8336\u5ea7\u4e0a\u7684\u201c\u7965 \u9e9f\u516c\u5b50\u201d\u5144\u59b9\u53ca\u5c5e\u4e0b\u9ad8\u624b\uff0c\u4e5f\u83ab\u4e0d\u8038\u7136\u52a8\u5bb9\u2026\u2026 \u3000\u3000\u53ef\u662f\uff0c\u4e8c\u4eba\u90fd\u662f\u653b\u52bf\uff0c\u51fa\u624b\u5b9e\u5728\u592a\u5feb\uff0c\u201c\u7965\u9e9f\u516c\u5b50\u201d\u4e00\u5e72\u4eba\u6765\u4e0d\u53ca\u51fa\u58f0\u963b\u6b62\uff0c\u201c\u562d\u201d\u7684 \u4e00\u58f0\uff0c\u90a3\u58ee\u6c49\u5df2\u88ab\u5c55\u767d\u4e00\u638c\u9707\u98de\u4e24\u4e08\u5f00\u201d\u5916\uff0c\u534a\u7a7a\u4e2d\u55b7\u51fa\u4e00\u53e3\u9c9c\u8840\uff0c\u9880\u957f\u8eab\u5f62\u201c\u53ed\u54d2\u201d\u4e00\u58f0 \u6454\u843d\u5730\u4e0a\uff0c\u52a8\u4e5f\u6ca1\u52a8\uff0c\u663e\u7136\u662f\u6bd9\u547d\u4e86\u3002\u56db\u5468\u8336\u5ba2\u4e00\u770b\u6253\u6b7b\u4eba\u4e86\uff0c\u7eb7\u7eb7\u79bb\u5ea7\u800c\u8d77\uff0c\u5239\u65f6\u4e00\u9635\u5927 \u4e71\u2026\uff0e\uff0e \u3000\u3000\u4e00\u58f0\u5389\u5578\uff0c\u522b\u7a7a\u800c\u8d77\uff0c\u4e00\u6761\u8eab\u5f71\u5e73\u5c04\u800c\u81f3\uff0c\u8eab\u5f62\u672a\u843d\u5730\uff0c\u62db\u5df2\u5148\u51fa\uff0c\u72b9\u5982\u4e00\u5934\u5927\u9e70\u4e00 \u822c\uff0c\u5341\u6307\u7b95\u5f20\uff0c\u51cc\u7a7a\u5411\u5c55\u767d\u5934\u9876\u51fb\u4e0b\u3002 \u3000\u3000\u8fd9\u4e00\u62db\u5a01\u52bf\u5947\u731b\uff0c\u8fc5\u5982\u661f\u706b\uff0c\u5c55\u81ea\u60f3\u8eb2\u4e5f\u6ca1\u6cd5\u8eb2\uff0c\u9738\u738b\u4e3e\u9f0e\u201d\uff0c\u53cc\u638c\u8fc7\u9876\u731b\u5411\u6765\u52bf\u8fce \u53bb\u3002 \u3000\u3000\u201c\u562d\uff01\u201d\u53c8\u662f\u786c\u6253\u786c\uff0c\u5c55\u767d\u81ea\u89c9\u5982\u4e07\u65a4\u94c1\u9524\u7838\u5728\u53cc\u81c2\u4e0a\uff0c\u4e00\u9635\u6c14\u7ffb\u8840\u6d8c\uff0c\u53cc\u773c\u91d1\u661f\u4e71 \u8fdb\uff0c\u8e09\u8dc4\u4e94\u3001\u516d\u6b65\uff0c\u4ecd\u7136\u7ad9\u4e0d\u7a33\uff0c\u4e00\u5c41\u80a1\u5750\u5728\u5730\u4e0a\u3002\u6765\u4eba\u51cc\u7a7a\u4e00\u51fb\uff0c\u628a\u5c55\u767d\u6253\u5750\u843d\u5730\uff0c\u8eab\u5f62 \u6cfb\u5730\uff0c\u4e0d\u7b49\u5c55\u767d\u7ffb\u8eab\u5750\u8d77\uff0c\u53cc\u638c\u4e00\u632b\uff0c\u53c8\u5411\u5c55\u81ea\u80f8\u524d\u63a8\u51fa\u4e00\u638c\u3002 \u3000\u3000\u6765\u4eba\u4e0b\u4e86\u72e0\u5fc3\uff0c\u60f3\u4e00\u638c\u628a\u5c55\u767d\u51fb\u6bd9\uff0c\u4ee5\u4ee3\u6b7b\u53bb\u7684\u5c5e\u4e0b\u62a5\u4ec7\uff0c\u8fd9\u4e00\u638c\u7528\u51fa\u4e86\u5168\u529b\uff0c\u53ea\u89c1\u638c \u98ce\u5982\u98d8\u5377\u81f3\uff0c\u5c55\u767d\u8fd9\u4e00\u6b21\u60f3\u8eb2\u4e5f\u6765\u4e0d\u53ca\u4e86\u3002\u773c\u770b\u8981\u4f24\u591c\u6765\u4eba\u638c\u52bf\u4e4b\u4e0b\uff0c\u7a81\u542c\u4e00\u58f0\u559d\u5428\uff1a\u201c\u5df4 \u5144\uff01\u4e14\u6162\u2014\u2014\u201d \u3000\u3000\u542c\u5230\u8fd9\u559d\u58f0\uff0c\u6765\u4eba\u638c\u52bf\u4e00\u7f13\uff0c\u5c55\u767d\u5374\u5df2\u5355\u8db3\u4e00\u70b9\u5730\u9762\uff0c\u7ffb\u8eab\u800c\u8d77\uff0c\u6a2a\u8eab\u98d8\u8dc3\u516b\u5c3a\u3002 \u3000\u3000\u4e03\u6253\u91cf\u6765\u4eba\uff0c\u5374\u662f\u4e00\u4e2a\u9762\u76ee\u9ece\u9ed1\u7684\u7626\u5c0f\u8001\u8005\uff0c\u4e00\u8eab\u95ea\u95ea\u53d1\u5149\u7684\u9ed1\u8863\uff0c\u53cc\u638c\u4e4e\u4f38\u7740\u53c9\u5728\u80f8 \u524d\uff0c\u4e24\u638c\u7684\u5c0f\u6307\u4e0a\u5404\u5957\u7740\u4e00\u4e2a\u94a2\u73af\uff0c\u94a2\u73af\u7684\u53e6\u4e00\u7aef\u7cfb\u7740\u4e0b\u895f\u4e24\u6446\u8863\u89d2\uff0c\u6b63\u77aa\u7740\u4e00\u53ea\u68f1\u8292\u523a\u4eba \u7684\u4e09\u89d2\u773c\uff0c\u671b\u5b9a\u5c55\u767d\u3002 \u3000\u3000\u5c55\u767d\u6b7b\u91cc\u9003\u751f\uff0c\u60ca\u9b42\u752b\u5b9a\uff0c\u671b\u7740\u8fd9\u9ed1\u7626\u8001\u5934\u7684\u602a\u5f02\u88c5\u675f\uff0c\u5374\u662f\u9762\u751f\u5f97\u7d27\uff0c\u4ed6\u8fd9\u6307\u5957\u94a2\u73af \u8fde\u7cfb\u8863\u895f\u7684\u5f62\u88c5\uff0c\u4e5f\u4ece\u672a\u542c\u4eba\u63d0\u8d77 \u3000\u3000\u8fc7\u3002 \u3000\u3000\u518d\u4e00\u770b\u559d\u6b62\u9ed1\u7626\u8001\u4eba\u4e0b\u6bd2\u624b\u7684\uff0c\u6b63\u662f\u8336\u5ea7\u4e0a\u90a3\u6c14\u5ea6\u9ad8\u8d35\u7684\u201c\u7965\u9e9f\u516c\u5b50\u201d\u3002 \u3000\u3000\u201c\u5929\u8d6b\u5144\uff01\u201d\u7965\u9e9f\u516c\u5b50\u201d\u98d8\u9038\u8d70\u6765\uff0c\u795e\u60c5\u6e85\u6d12\u5df2\u6781\uff0c\u5148\u5411\u9ed1\u7626\u8001\u5934\u8bf4\u9053\uff1a\u5c0f\u5f1f\u662f\u60f3\u548c\u8fd9 \u4f4d\u5144\u53f0\u4ea4\u4e2a\u670b\u53cb\uff0c\u5e76\u4e0d\u662f\u8981\u6253\u67b6\u3002\u201d \u3000\u3000\u8bf4\u7740\u53c8\u8f6c\u5411\u5c55\u767d\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u5144\u53f0\u597d\u4fca\u7684\u529f\u592b\uff0c\u5728\u4e0b\u91d1\u7115\u5f69\uff0c\u4e16\u5c45\u5357\u4eac\uff0c\u60f3\u8bf7\u5144\u53f0\u5230\u5bd2 \u820d\u76d8\u6853\u51e0\u5929\uff0c\u4e0d\u77e5\u5144\u53f0\u80af\u8d4f\u5149\u5426\uff1f\u201d \u3000\u3000\u672a\u7b49\u5c55\u767d\u7b54\u8bdd\uff0c\u90a3\u9ed1\u7626\u8001\u5934\u90e8\u9762\u5e26\u4e0d\u6124\uff0c\u62a2\u5148\u8bf4\u9053\uff1a\u201c\u96be\u9053\u516c\u5b50\u4e00\u540d\u5c5e\u4e0b\u5c31\u767d\u767d\u6b7b \u4e86\uff01\u2026\u2026\u201d \u3000\u3000\u8bf4\u7740\u8bdd\u53cc\u81c2\u5728\u80f8\u524d\u4e00\u5f20\u4e00\u76d2\uff0c\u4e24\u53ea\u4e09\u89d2\u5f62\u7684\u5389\u76ee\u66f4\u662f\u51cc\u832b\u56db\u5c04\uff0c\u770b\u90a3\u6837\u5b50\u4ecd\u60f3\u5411\u5c55\u767d\u51fa \u624b\u3002 \u3000\u3000\u201c\u6253\u65e0\u597d\u624b\uff0c\u9a82\u65e0\u597d\u53e3\u3002\u201d\u201c\u7965\u9e9f\u516c\u5b50\u201d\u62e6\u4f4f\u9ed1\u7626\u8001\u8005\uff0c\u8bf4\u9053\uff1a\u201c\u60f3\u6211\u6b66\u6797\u4e2d\u4eba\uff0c\u53cc\u65b9 \u4e00\u8a00\u4e0d\u5408\uff0c\u4e92\u76f8\u4ea4\u624b\uff0c\u4f24\u6b8b\u6b7b\u4ea1\u662f\u514d\u4e0d\u4e86\u7684\uff0c\u8fd9\u53ea\u80fd\u6028\u81ea\u5df1\u5b66\u827a\u4e0d\u7cbe\uff0c\u4e0d\u80fd\u6028\u4eba\u5bb6\uff0c\u2018\u6df7\u6c5f \u9f99\u2019\u6881\u670b\u6b7b\u4e86\uff0c\u6211\u7ed9\u4ed6\u4e70\u4e00\u526f\u6700\u597d\u68fa\u6728\uff0c\u91cd\u6b96\u539a\u846c\uff0c\u518d\u539a\u6064\u9057\u65cf\u5c31\u662f\u4e86\u2019\u3002\u201d \u3000\u3000\u201c\u7965\u9e9f\u516c\u5b50\u201d\u8bf4\u5230\u8fd9\u91cc\uff0c\u56de\u5934\u5524\u9053\uff1a\u6881\u73cf\uff01\u4f60\u8fc7\u6765\uff01\u201d \u3000\u3000\u4e00\u540d\u5e74\u8f7b\u58ee\u6c49\uff0c\u5e94\u58f0\u800c\u81f3\uff0c\u6ee1\u8154\u60b2\u6124\u4e4b\u8272\uff0c\u72e0\u72e0\u76ef\u4e86\u5c55\u767d\u2014\u2014\u773c\uff0c\u7136\u540e\u5411\u201c\u7965\u9e9f\u516c\u5b50\u201d \u4e00\u8eac\u8eab\uff0c\u8bf4\u9053\uff1a\u201c\u516c\u5b50\u7237\u6709\u4ec0\u4e48\u5429\u5490\uff1f\u201d \u3000\u3000\u5c11\u652f\u9886\u4e09\u5343\u4e24\u94f6\u5b50\uff0c\u539a\u846c\u4f60\u7684\u5144\u957f\uff01\u201d \u3000\u3000\u201c\u8c22\u8c22\u516c\u5b50\u7237\uff01\u201d \u3000\u3000\u5e74\u8f7b\u58ee\u6c49\u53c8\u662f\u8eac\u8eab\u4e00\u793c\uff0c\u8f6c\u8eab\u81ea\u53bb\u6536\u7877\u4ed6\u54e5\u54e5\u7684\u5c38\u8eab\u53bb\u4e86\uff0c\u4f46\u5728\u4e34\u8d70\u4e4b\u524d\uff0c\u6068\u6bd2\u5730\u671b\u4e86 \u5c55\u767d\u4e00\u773c\u3002 \u3000\u3000\u5c55\u767d\u4e5f\u6ca1\u60f3\u5230\u4e00\u638c\u7ade\u628a\u201c\u6df7\u6c5f\u9f99\u201d\u6881\u670b\u6253\u6b7b\uff0c\u770b\u5230\u6881\u670b\u5f1f\u5f1f\u7684\u60b2\u6124\u773c\u8272\uff0c\u81ea\u5df1\u5fc3\u91cc\u4e5f\u5728 \u6697\u6697\u540e\u6094\uff0c\u4e0d\u8be5\u51fa\u624b\u592a\u91cd\uff0c\u4f46\u53c8\u770b\u5230\u8fd9\u88ab\u79f0\u505a\u201c\u516c\u5b50\u7237\u201d\u7684\u534e\u8d35\u5c11\u5e74\u9890\u6307\u6c14\u4f7f\u7684\u6c14\u6982\u3002 \u3000\u3000\u5f00\u53e3\u5c31\u662f\u4e09\u5343\u94f6\u5b50\uff0c\u53c8\u542c\u4ed6\u81ea\u79f0\u59d3\u91d1\uff0c\u4f7f\u5df2\u731c\u6eaf\u5230\u5fc5\u662f\u201c\u7965\u9e9f\u516c\u5b50\u201d\u4e86\u3002\u5c55\u767d\u6697\u5728\u5fc3\u4e2d \u4ed8\u9053\uff1a\u201c\u54fc\uff01\u8fd8\u4e0d\u662f\u4ed7\u7740\u51e0\u4e2a\u81ed\u94b1\uff0c\u6765\u6536\u4e70\u4eba\u5fc3\uff0c\u66ff\u4ed6\u5356\u547d\uff01\u2026\u2026\u201d \u3000\u3000\u90a3\u9ed1\u5ea7\u8001\u8005\u89c1\u201c\u7965\u9e9f\u516c\u5b50\u201d\u5f53\u4f17\u8d4f\u4e86\u4ed6\u4e00\u4e2a\u9762\u5b50\uff0c\u6012\u6c14\u7565\u5e73\uff0c\u64a4\u53bb\u4e86\u53cc\u638c\u96c6\u8fd0\u7684\u529f\u529b\uff0c \u4ea4\u53c9\u5728\u80f8\u524d\u7684\u4e24\u81c2\u4e5f\u7f13\u7f13\u5782\u4e86\u4e0b\u6765\uff0c\u4f46\u4ecd\u5411\u5c55\u767d\u72e0\u72e0\u8bf4\u9053\uff1a\u201c\u770b\u5728\u516c\u5b50\u7237\u7684\u9762\u4e0a\uff0c\u6682\u65f6\u9976\u4f60 \u4e0d\u6b7b\uff01\u201d \u3000\u3000\u5c55\u767d\u770b\u9ed1\u7626\u8001\u8005\u5351\u5937\u4ed6\u7684\u795e\u8272\uff0c\u4e0d\u7531\u51b7\u54fc\u4e00\u58f0\uff0c\u50b2\u9053\uff1a\u672a\u5fc5\uff01\u2026\u2026\u201d\u9ed1\u7626\u8001\u8005\u521a\u653e\u677e\u7684 \u795e\u8272\uff0c\u7acb\u65f6\u53c8\u7d27\u5f20\u8d77\u6765\u3002\u2026\u2026 \u3000\u3000\u4f46\u201c\u7965\u9e9f\u516c\u5b50\u201d\u4e0d\u7b49\u5c55\u767d\u518d\u8bf4\u4e0b\u53bb\u3002 \u3000\u3000\u54c8\u54c8\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u5929\u5927\u7684\u4e8b\u60c5\uff0c\u4e00\u8fc7\u53bb\u5c31\u7b97\u4e86\u5566\uff01\u8fd9\u4f4d\u5144\u53f0\uff0c\u6211\u8fd8\u6ca1\u8bf7\u6559\u4f60\u8d35\u59d3\u5927\u540d \u5450\u3002\u201d \u3000\u3000\u5c55\u81ea\u7a81\u7136\u8d77\u4e86\u4e00\u4e2a\u5ff5\u5934\uff0c\u6697\u60f3\uff0c\u201c\u8fd9\u2018\u7965\u9e9f\u516c\u5b50\u2019\u4e4b\u7236\uff0c\u4e43\u662f\u6211\u6740\u7236\u7684\u5927\u4ec7\u4eba\uff0c\u81ea\u5df1\u65e9 \u665a\u8981\u627e\u5230\u4ed6\u7236\u5b50\u6e05\u7b97\u8fd9\u7b14\u5e10\uff0c\u5982\u4eca\u5f53\u7740\u8fd9\u4e48\u591a\u4eba\uff0c\u4f55\u4e0d\u663e\u9732\u81ea\u5df1\u7684\u59d3\u540d\uff0c\u4ee5\u540e\u4e5f\u8ba9\u6c5f\u6e56\u9053\u4e0a \u77e5\u9053\uff0c\u5c55\u5bb6\u8fd8\u6709\u8fd9\u4e48\u4e00\u6761\u4e0d\u754f\u5f3a\u6743\u3001\u4e3a\u7236\u62a5\u4ec7\u7684\u540e\u4ee3\u6839\u82d7\u2026\u2026\u201d \u3000\u3000\u5c55\u767d\u60f3\u81f3\u6b64\u5904\uff0c\u7acb\u523b\u8bf4\u9053\uff1a\u201c\u5728\u4e0b\u5c55\u767d\uff0c\u9601\u4e0b\u60f3\u5fc5\u5c31\u662f\u95fb\u540d\u5929\u4e0b\u7684\u2018\u7965\u9e9f\u516c\u5b50\u2019\u4e86\uff01\u201d \u3000\u3000\u201c\u627f\u8499\u8c2c\u5956\uff01\u201d \u3000\u3000\u201c\u7965\u9e9f\u516c\u5b50\u201d\u4fca\u7f8e\u7684\u9762\u5bb9\u4e0a\uff0c\u5e26\u7740\u9ad8\u8d35\u65e0\u6bd4\u7684\u7b11\u5bb9\u8bf4\u9053\uff1a\u201c\u7965\u9e9f\u4e00\u751f\u597d\u5ba2\uff0c\u5982\u8499\u5c55\u5c0f\u4fa0 \u4e0d\u5f03\uff0c\u8bf7\u5c55\u5c0f\u631f\u81f3\u5bd2\u820d\uff0c\u5bb9\u6837\u9cde\u4e00\u5c3d\u5730\u4e3b\u4e4b\u8c0a\uff01\u201d \u3000\u3000\u6b64\u65f6\u91d1\u4e4c\u897f\u5760\uff0c\u5915\u9633\u971e\u5f71\u6295\u5c04\u5728\u6c5f\u9762\u4e0a\uff0c\u95ea\u70c1\u4e00\u7247\u91d1\u9cde\u3002 \u3000\u3000\u5c55\u767d\u6b63\u5f85\u63a8\u8f9e\u4e0d\u53bb\uff0c\u5ffd\u89c9\u773c\u524d\u4e00\u4eae\uff0c\u539f\u6765\u662f\u201c\u7965\u9e9f\u516c\u5b50\u201d\u7684\u80de\u59b9\u91d1\u5f69\u98ce\u5728\u51e0\u4eba\u8bf4\u8bdd\u65f6\uff0c \u4e5f\u8d70\u4e86\u8fc7\u6765\uff0c\u5979\u5c31\u7ad9\u5728\u5979\u80de\u5144\u7684\u8eab\u540e\uff0c\u53ea\u4ee5\u4e00\u53cc\u660e\u5982\u79cb\u6c34\u7684\u773c\u775b\u671b\u5b9a\u5c55\u767d\uff0c\u5e76\u672a\u53d1\u8a00\u3002 \u3000\u3000\u91d1\u91ca\u51e4\u6709\u6c5f\u5357\u7b2c\u4e00\u7f8e\u4eba\u4e4b\u79f0\uff0c\u5176\u7f8e\u8273\u5982\u6625\u534e\u79cb\u5b9e\uff0c\u660e\u4e3d\u5982\u660e\u6708\u79cb\u6c34\uff0c\u7b80\u76f4\u4e0d\u662f\u4efb\u4f55\u7b14\u58a8 \u53ef\u4ee5\u5f62\u5bb9\u7684\uff0c\u5c24\u5176\u5979\u7684\u6027\u683c\uff0c\u8131\u7565\u5f62\u9ab8\uff0c\u65e2\u4e0d\u817c\u8146\u89c1\u4eba\uff0c\u53c8\u4e0d\u5fc3\u9ad8\u6c14\u50b2\uff0c\u5728\u4efb\u4f55\u573a\u5408\u90fd\u662f\u5927 \u5927\u65b9\u65b9\u5730\u9732\u9762\uff0c\u4efb\u4e07\u4eba\u6b23\u8d4f\uff0c\u4efb\u4e07\u4eba\u8d5e\u53f9\uff0c\u51b3\u4e0d\u85cf\u85cf\u8eb2\u8eb2\uff0c\u81ea\u81ea\u7136\u7136\uff0c\u6ca1\u6709\u4e00\u4e1d\u77eb\u63c9\u9020\u4f5c\uff0c \u771f\u597d\u50cf\u78a7\u7a7a\u8721\u6708\uff0c\u88ab\u5934\u540d\u82b1\u4e00\u822c\u3002 \u3000\u3000\u5c55\u767d\u867d\u7136\u7eaf\u6734\u5982\u7389\uff0c\u80f8\u4e0d\u70b9\u5c18\uff0c\u5c24\u5176\u6700\u8fd1\u51e0\u5929\u7edd\u8272\u7f8e\u5973\u89c1\u8fc7\u597d\u51e0\u4e2a\uff0c\u50cf\u6155\u5bb9\u7ea2\u3001\u5c55\u5a49 \u513f\u3001\u6a0a\u7d20\u9e3e\u2026\u2026\u4f46\u65e0\u6cd5\u548c\u773c\u524d\u7684\u91d1\u5f69\u51e4\u76f8\u6bd4\u62df\uff0c\u53ea\u89c9\u5979\u660e\u4e3d\u7167\u4eba\uff0c\u5c24\u5176\u5979\u5634\u89d2\u4e0a\u90a3\u2014\u2014\u5bf9\u6d45 \u6d45\u7684\u68a8\u6da1\uff0c\u6ca1\u6709\u7b11\u4e5f\u4f3c\u4e4e\u5e26\u7740\u751c\u871c\u7684\u7b11\u5bb9\uff0c\u5c55\u767d\u4e0e\u5979\u76ee\u5149\u76f8\u89e6\uff0c\u4e0d\u7531\u5fae\u5fae\u4e00\u679c\uff0c\u5728\u5fc3\u4e2d\u6697\u6697 \u559d\u5f69\uff1a\u201c\u597d\u7f8e\uff01\u2026\u2026\u201d \u3000\u3000\u89c1\u5c55\u767d\u672a\u518d\u63a8\u8f9e\uff0c\u201c\u7965\u9e9f\u516c\u5b50\u201d\u4ee5\u4e3a\u5c55\u767d\u7b54\u5e94\u4e86\uff0c\u7acb\u5373\u5429\u5490\u624b\u4e0b\u5e26\u8f66\u3001\u5907\u9a6c\u3001\u62ac\u8f7f\uff0c\u5019 \u65f6\u8f66\u3001\u9a6c\u3001\u8f7f\u90fd\u5230\uff0c\u201c\u7965\u9e9f\u516c\u5b50\u201d\u5411\u5c55\u767d\u4e00\u62b1\u62f3\uff0c\u8bf4\u9053\uff1a\u201c\u8bf7\u5427\uff01\u201d \u3000\u3000\u5c55\u767d\u6b64\u65f6\u518d\u60f3\u63a8\u8f9e\u4e5f\u8bf4\u4e0d\u51fa\u53e3\u6765\u4e86\uff0c\u53ea\u6709\u8d70\u5230\u54ea\u91cc\u7b97\u54ea\u91cc\uff0c\u8de8\u4e0a\u4e00\u5339\u9a6c\u53bb\uff0c\u4f17\u4eba\u9a91\u9a6c\u7684 \u9a91\u9a6c\uff0c\u4e0a\u8f7f\u7684\u4e0a\u8f7f\u3002 \u3000\u3000\u201c\u7965\u9e9f\u516c\u5b50\u201d\u4e0e\u5c55\u767d\u5e76\u7559\u800c\u884c\uff0c\u4e00\u8def\u4e0a\u9ad8\u8c08\u9614\u8bba\uff0c\u663e\u5f97\u65e2\u70ed\u60c5\uff0c\u53c8\u8bda\u6073\uff0c\u5c55\u767d\u5fc3\u5b58\u4ec7 \u89c6\uff0c\u4f46\u4e5f\u4e0d\u5f97\u4e0d\u6697\u6697\u4f69\u670d\uff0c\u201c\u7965\u9e9f\u70ed\u80a0\u201d\uff0c\u6c5f\u6e56\u4f20\u8a00\u4e0d\u865a\u3002 \u3000\u3000\u5c24\u5176\u90a3\u91d1\u5f69\u51e4\uff0c\u4e5f\u5f03\u8f7f\u4e0d\u5750\uff0c\u9a91\u4e86\u4e00\u5339\u96ea\u767d\u7684\u9a8f\u9a6c\uff0c\u968f\u5728\u80de\u5144\u8eab\u4fa7\u3002\uff0c\u9694\u7740\u201c\u7965\u9e9f\u516c \u5b50\u201d\uff0c\u4e0d\u65f6\u4ee5\u5979\u90a3\u7f8e\u5f97\u4e0d\u80fd\u518d\u7f8e\u7684\u4fcf\u76ee\uff0c\u77a5\u671b\u5c55\u767d\u4e24\u773c\u5979\u81ea\u59cb\u81f3\u7ec8\u4e00\u53e5\u8bdd\u4e5f\u6ca1\u6709\u8bf4\uff0c\u4f46\u5979\u7684 \u5a07\u7f8e\u53cc\u773c\uff0c\u4f3c\u662f\u4f1a\u8bf4\u8bdd\u4e00\u822c\uff0c\u5c55\u767d\u53ea\u89c9\u6709\u65e0\u9650\u60c5\u610f\uff0c\u4ece\u90a3\u4e24\u53ea\u7f8e\u5f97\u4e0d\u80fd\u518d\u7f8e\u7684\u5927\u773c\u775b\u91cc\u4f20\u4e86 \u8fc7\u6765\uff0c\u4f7f\u5f97\u4ed6\u5fc3\u4e2d\u4e00\u9635\u8ff7\u60d8\uff0c\u53c8\u662f\u4e00\u9635\u8ff7\u60d8\u2026\uff0e\uff0e\uff0e \u3000\u3000\u201c\u770b\u4ed6\u5144\u59b9\u8fd9\u822c\u795e\u4fca\u6e05\u79c0\u7684\u4eba\u54c1\uff0c\u5047\u5982\u6709\u4e00\u5929\u81ea\u5df1\u6b63\u5f0f\u5411\u4ed6\u5144\u59b9\u7684\u7236\u4eb2\u5bfb\u4ec7\u65f6\uff0c\u4e0d\u77e5\u80fd \u4e0d\u80fd\u4e0b\u72e0\u5fc3\u8ddf\u4ed6\u5144\u59b9\u7ffb\u8138\u52a8\u624b\uff1f \u3000\u3000\u5c4a\u767d\u5fc3\u4e2d\u611f\u6982\u4e07\u5343\uff0c\u6cbf\u9014\u6240\u7ecf\u6beb\u672a\u7559\u610f\uff0c\u8f6c\u773c\u95f4\u6765\u5230\u4e00\u5ea7\u9ad8\u5927\u5e9c\u7b2c\u4e4b\u524d\u3002 \u3000\u3000\u53ea\u89c1\u9ad8\u95e8\u5e9c\u7b2c\uff0c\u95e8\u524d\u4e0a\u9a6c\u77f3\u4e0b\u9a6c\u77f3\u5206\u5217\u5de6\u53f3\uff0c\u6c49\u767d\u7389\u7684\u9ad8\u77f3\u53f0\u9636\uff0c\u9ad8\u8fbe\u6570\u5341\u7ea7\uff0c\u5e7f\u6709\u5341 \u6570\u6587\uff0c\u6c14\u6d3e\u4e4b\u5927\u4ece\u672a\u89c1\u8fc7\u3002 \u3000\u3000\u6bcf\u9694\u4e94\u5c42\u53f0\u9636\uff0c\u5de6\u53f3\u5373\u7ad9\u5b9a\u4e24\u4e2a\u91d1\u76d4\u91d1\u7532\u3001\u4f69\u5251\u6267\u77db\u7684\u536b\u58eb\uff0c\u7531\u4f4e\u81f3\u9ad8\uff0c\u9009\u9898\u4e24\u5217\uff0c\u8db3 \u6709\u4e94\u3001\u516d\u5341\u540d\u4e4b\u591a\uff0c\u5c55\u81ea\u770b\u5f97\u6697\u6697\u548b\u820c\uff0c\u5fc3\u8bf4\uff1a\u201c\u60f3\u4e0d\u5230\u6b66\u6797\u8c6a\u95e8\uff0c\u7ade\u6709\u8fd9\u7b49\u6c14\u6d3e\uff0c\u5373\u662f\u738b \u4faf\u5e9c\u7b2c\u4e5f\u4e0d\u8fc7\u5982\u6b64\u4e86\u2026\u2026\u201d \u3000\u3000\u4f17\u4eba\u5230\u4e86\u5e9c\u524d\uff0c\u4e0b\u8f66\u4e0b\u9a6c\u3002 \u3000\u3000\u201c\u7965\u9e9f\u516c\u5b50\u201d\u6ee1\u8138\u6625\u98ce\uff0c\u4e1d\u6beb\u4e0d\u5e26\u9a84\u77dc\u4e4b\u8272\uff0c\u5f88\u70ed\u8bda\u5730\u63e1\u7740\u5c55\u767d\u7684\u624b\uff0c\u5f53\u5148\u8fc8\u6b65\u767b\u9636\u3002 \u3000\u3000\u91d1\u7532\u536b\u58eb\u9010\u4e2a\u5730\u6536\u77db\u81f4\u656c\uff0c\u5c55\u767d\u4e0d\u7531\u5fc3\u4e2d\u6697\u53f9\uff0c\u770b\u201c\u7965\u9e9f\u516c\u5b50\u201d\u5bcc\u7532\u738b\u4faf\uff0c\u52bf\u53ef\u76d6\u5929\uff0c \u5bf9\u81ea\u5df1\u4e00\u4e2a\u843d\u9b44\u5c11\u5e74\uff0c\u7ade\u5982\u6b64\u70ed\u60c5\uff0c\u771f\u4e0d\u6127\u201c\u7965\u9e9f\u70ed\u80a0\u201d\u4e4b\u540d\uff01\u2026\u2026 \u3000\u3000\u95e8\u697c\u6a90\u7259\u8038\u4e91\uff0c\u5eca\u6a90\u4e0b\u63a5\u6ee1\u4e86\u91d1\u5b57\u5927\u533e\uff0c\u201c\u72b6\u5143\u53ca\u7b2c\u201d\u3001\u201c\u8fdb\u58eb\u201d\u3001\u201c\u699c\u773c\u201d\u3001\u201c\u5fa1\u524d \u4e00\u54c1\u201d\u7b49\u7b49\u2026\u2026\u4e0d\u80dc\u679a\u4e3e\uff0c\u5f53\u4e2d\u4e00\u5757\u533e\uff0c\u6597\u5927\u7684\u56db\u4e2a\u91d1\u5b57\u201c\u5efa\u4e1a\u91d1\u5e9c\u201d\uff0c\u770b\u6837\u5b50\u8fd9\u201c\u7965\u9e9f\u516c \u5b50\u201d\u4e0d\u4f46\u662f\u6b66\u6797\u8c6a\u95e8\uff0c\u800c\u4e14\u662f\u5b98\u5ba6\u4e16\u5bb6\u4e86\u3002 \u3000\u3000\u9ed1\u6f06\u5927\u95e8\u7684\u53f3\u4fa7\uff0c\u63a5\u7740\u201c\u5343\u9877\u813e\u201d\u3001\u201c\u4e07\u9877\u724c\u201d\u7684\u724c\u5b50\uff0c\u53e6\u6709\u4e00\u5757\u91d1\u5e95\u7ea2\u5b57\u7684\u724c\u5b50\uff0c\u4e0a \u4e66\u201c\u6c5f\u5357\u7b2c\u4e00\u5bb6\u201d\uff0c\u5206\u5916\u523a\u773c\u3002 \u3000\u3000\u201c\u54fc\uff01\u201d\u5c55\u767d\u5fc3\u8bf4\uff1a\u201c\u597d\u5927\u7684\u53e3\u6c14\uff01\u201d \u3000\u3000\u5c55\u767d\u4e00\u8def\u89c2\u671b\u3001\u601d\u7d22\u7740\uff0c\u88ab\u201c\u7965\u9e9f\u516c\u5b50\u201d\u8ba9\u8fdb\u4e00\u5ea7\u5927\u5385\uff0c\u6b64\u65f6\u5929\u5df2\u660f\u9ed1\uff0c\u5927\u5385\u5185\u71c3\u8d77\u4e86 \u4e0a\u767e\u76cf\u7684\u706f\u70db\uff0c\u628a\u4e00\u5ea7\u5bbd\u655e\u8c6a\u534e\u7684\u5927\u5385\uff0c\u7167\u8000\u5f97\u91d1\u78a7\u8f89\u714c\u3002 \u3000\u3000\u201c\u5c55\u5144\u60f3\u5fc5\u5c1a\u672a\u7528\u9910\u3002\u201d\u201c\u7965\u9e9f\u516c\u5b50\u201d\u542b\u7b11\u8bf4\u9053\uff1a\u201c\u5c0f\u5f1f\u804a\u5c3d\u5730\u4e3b\u4e4b\u8c0a\uff0c\u8bf7\u5c55\u5144\u4fbf\u996d\uff0c \u5343\u4e07\u522b\u5ba2\u6c14\uff01\u201d \u3000\u3000\u201c\u7965\u9e9f\u516c\u5b50\u201d\u8bf4\u7f62\uff0c\u4e5f\u4e0d\u7b49\u5c55\u767d\u7b54\u5e94\u836f\u5373\u56de\u5934\u5429\u5490\u5c5e\u4e0b\u6446\u996d\u3002 \u3000\u3000\u201c\u54e5\u54e5\uff01\u4f60\u771f\u662f\u2014\u2014\u201d\u59cb\u7ec8\u672a\u5f00\u53e3\u8bf4\u8bdd\u7684\u91d1\u5f69\u98ce\uff0c\u5ffd\u7136\u7f8e\u76ee\u6d41\u8f6c\uff0c\u773c\u5149\u5f80\u201c\u7965\u9e9f\u516c\u5b50\u201d \u8f6c\u5230\u5c55\u767d\u8eab\u4e0a\uff0c\u5410\u58f0\u5982\u5456\u5456\u83ba\u9e23\uff0c\u8bf4\u9053\uff1a\u201c\u770b\u5c55\u5c0f\u4fa0\u2014\u2014\u201d \u3000\u3000\u8fd9\u6709\u7740\u201c\u6c5f\u5357\u7b2c\u4e00\u7f8e\u4eba\u201d\u4e4b\u79f0\u7684\u91d1\u5f69\u51e4\uff0c\u5f53\u771f\u662f\u773c\u775b\u4f1a\u8bf4\u8bdd\uff0c\u5c31\u5979\u8fd9\u4e48\u4e24\u53e5\u4e0d\u5b8c\u6574\u8bdd\u4e00 \u8bf4\uff0c\u7965\u9e9f\u516c\u5b50\u201d\u5df2\u7ecf\u660e\u767d\uff0c\u54c8\u54c8\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u662f\u59b9\u59b9\u63d0\u8d77\u6211\u5012\u5fd8\u4e86\u3002\u201d \u3000\u3000\u8bf4\u7740\u63d0\u9ad8\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u6765\u4eba\u5450\uff01\u201d \u3000\u3000\u5e94\u58f0\u4ece\u4e00\u5ea7\u7fe0\u7389\u5c4f\u98ce\u540e\u9762\uff0c\u8f6c\u51fa\u56db\u4e2a\u9752\u8863\u5c0f\u5a62\uff0c\u8885\u8885\u8d70\u81f3\u201c\u7965\u9e9f\u516c\u5b50\u201d\u9762\u524d\uff0c\u9f50\u8eab\u655b\u887d \u884c\u793c\uff0c\u9f50\u53eb\u4e86\u4e00\u58f0\uff1a\u201c\u516c\u5b50\u7237\u2014\u2014\u201d \u3000\u3000\u201c\u4f3a\u5019\u8d35\u5bbe\u6c90\u6d74\u66f4\u8863\uff01\u201d\u2018\u7965\u9e9f\u516c\u5b50\u201d\u4e00\u6325\u624b\uff0c\u56db\u4e2a\u9752\u8863\u5c0f\u5a62\u4e00\u9f50\u6765\u5230\u5c55\u767d\u9762\u524d\uff0c\u655b\u887d \u4e3a\u793c\u8bf4\u9053\uff1a\u201c\u8d35\u5bbe\uff0c\u8bf7\u6765\u5427\uff01\u201d \u3000\u3000\u8bf4\u7f62\u5f53\u5148\u8d70\u53bb\uff0c\u5c55\u767d\u7565\u4e00\u72b9\u8c6b\uff0c\u770b\u5230\u81ea\u5df2\u4e00\u8eab\u6c57\u57a2\uff0c\u4e5f\u771f\u8be5\u68b3\u6d17\u4e00\u756a\u4e86\uff0c\u4e5f\u4e0d\u5ba2\u6c14\uff0c\u5373 \u968f\u7740\u90a3\u56db\u4e2a\u9752\u8863\u5c0f\u5a62\u884c\u53bb\u3002 \u3000\u3000\u8d70\u8fc7\u51e0\u9053\u94fa\u7740\u534e\u8d35\u5730\u6be1\u7684\u752d\u9053\uff0c\u9752\u8863\u5c0f\u5a62\u63a8\u5f00\u4e00\u6247\u9ad8\u5927\u7409\u7483\u95e8\uff0c\u5df2\u8fdb\u5165\u4e00\u95f4\u8c6a\u534e\u5bbd\u655e\u7684 \u6d74\u5ba4\u3002 \u3000\u3000\u53ea\u89c1\u6d74\u5ba4\u4e2d\u592e\u4e00\u4e2a\u4e24\u6587\u89c1\u65b9\u7684\u6d74\u6c60\uff0c\u6c60\u4e2d\u6c34\u60c5\u89c1\u5e95\uff0c\u6c60\u4e2d\u592e\u4e00\u5ea7\u767d\u7389\u96d5\u5851\u7684\u534a\u88f8\u5973\u50cf\uff0c \u5973\u50cf\u80a9\u4e0a\u8d1f\u7740\u4e00\u4e2a\u82b1\u74f6\u4f3c\u7684\u6c34\u9890\uff0c\u4e00\u7f15\u6e05\u6cc9\u7531\u74f6\u4e2d\u503e\u5165\u6c60\u5185\uff0c\u6c34\u58f0\u6e85\u6b8a\u6cfc\u7389\uff0c\u4ee4\u4eba\u4eba\u6d74\u4e4b \u524d\uff0c\u5148\u6709\u4e00\u79cd\u6e05\u65b0\u6d01\u51c0\u3001\u5c18\u4fd7\u7686\u6d88\u4e4b\u611f\u3002 \u3000\u3000\u56db\u4e2a\u9752\u8863\u5c0f\u5a62\u59c6\u4e00\u8fdb\u6d74\u5ba4\uff0c\u5373\u81ea\u884c\u5bbd\u8863\u89e3\u5e26\uff0c\u628a\u5c55\u767d\u5413\u4e86\u4e00\u8df3\uff0c\u6025\u95ee\u9053\uff1a\u201c\u600e\u4e48\uff01\u4f60\u4eec \u4e5f\u8981\u6d17\u6fa1\u5440\uff1f\u201d \u7b2c\u4e8c\u5341\u4e8c\u7ae0\u3000\u6bd2\u9152>> \u53e4\u9f99\u300a\u5251\u5ba2\u884c\u300b \u7b2c\u4e8c\u5341\u4e8c\u7ae0\u3000\u6bd2\u9152 \u3000\u3000\u5728\u5c55\u767d\u5927\u611f\u610f\u5916\u3001\u60ca\u8be7\u83ab\u540d\u4e4b\u9645\uff0c\u90a3\u56db\u540d\u9752\u8863\u5999\u9f84\u5c0f\u5a62\uff0c\u4e8e\u8c6a\u534e\u6d74\u5ba4\u4e2d\uff0c\u5bf9\u7740\u2014\u4e2a\u964c\u751f \u7537\u5b50\uff0c\u53cc\u773c\u8e6c\u89c6\u4e4b\u4e0b\uff0c\u7ade\u5927\u5927\u65b9\u65b9\u628a\u8eab\u4e0a\u7684\u8863\u670d\u8131\u4e86\u4e2a\u51c0\u5149\u3002 \u3000\u3000\u53ea\u89c1\u56db\u4e2a\u9752\u8863\u5a62\u5973\uff0c\u7c89\u81c2\u738b\u817f\uff0c\u9165\u80f8\u4e30\u81c0\uff0c\u9664\u4e86\u2014\u62b9\u6781\u77ed\u7684\u4e1d\u8d28\u77ed\u88e4\u3001\u4e00\u62b9\u6781\u7a84\u7684\u7c89\u7ea2 \u80f8\u5dfe\u4e4b\u5916\uff0c\u626e\u767d\u80f4\u4f53\u4e0a\u5df2\u662f\u4e00\u4e1d\u4e0d\u6302\u3002 \u3000\u3000\u5c55\u767d\u773c\u775b\u7741\u4e86\u597d\u5927\uff0c\u77a0\u76ee\u7ed3\u820c\u5730\u671b\u7740\u8fd9\u5927\u5927\u51fa\u4e4e\u610f\u5916\u7684\u6ee1\u773c\u6625\u5149\uff0c\u7b80\u76f4\u5df2\u662f\u8bdd\u90fd\u8bf4\u4e0d\u51fa \u6765\u4e86\u3002 \u3000\u3000\u90a3\u56db\u4e2a\u5999\u9f84\u5a62\u5973\uff0c\u90fd\u597d\u50cf\u6ca1\u6709\u90a3\u4e48\u56de\u4e8b\u4e00\u822c\uff0c\u6781\u81ea\u7136\u5730\u628a\u9999\u5dfe\u3001\u6d74\u5177\u2026\u2026\u4e00\u6837\u4e00\u6837\u5730\u51c6 \u5907\u8212\u9f50\uff0c\u597d\u50cf\u8fd9\u4e9b\u4e8b\u505a\u5f97\u975e\u5e38\u719f\u6089\uff0c\u4f46\u8f6c\u8138\u89c1\u5c55\u767d\u8fd8\u4e0d\u8131\u8863\u670d\uff0c\u6b7b\u81ea\u7ad9\u5728\u90a3\u91cc\u77aa\u7740\u773c\u775b\u53d1 \u6014\u3002 \u3000\u3000\u4e0d\u7531\u63a9\u5634\u4e00\u7b11\uff0c\u90a3\u516b\u53ea\u4fcf\u76ee\uff0c\u66fc\u6ce2\u6d41\u5149\uff0c\u610f\u601d\u5df2\u660e\u663e\u5730\u8bf4\u51fa\u6765\uff1a\u201c\u8981\u6d17\u6fa1\uff0c\u600e\u4e48\u4e0d\u8131\u8863 \u670d\uff1f\u201d \u3000\u3000\u7f9e\u803b\u4e4b\u5fc3\uff0c\u4eba\u7686\u6709\u4e4b\uff0c\u4e00\u4e2a\u5927\u7537\u4eba\uff0c\u7741\u5927\u767d\u773c\u5f53\u7740\u964c\u751f\u5973\u5b50\u9762\u524d\u8131\u8863\u670d\uff0c\u9664\u975e\u795e\u7ecf\u75c5\uff0c \u4efb\u4f55\u4eba\u4e5f\u6ca1\u6709\u8fd9\u4efd\u80c6\u91cf\u3002 \u3000\u3000\u4efb\u4f60\u662f\u5929\u4e0d\u6015\u5730\u4e0d\u6015\u7684\u6c5f\u6e56\u8c6a\u5ba2\uff0c\u9047\u5230\u8fd9\u79cd\u573a\u9762\uff0c\u4e5f\u4f1a\u611f\u5230\u5c34\u5c2c\u4e07\u5206\u3002 \u3000\u3000\u5c55\u767d\u867d\u6709\u4e00\u6b21\u5927\u767d\u5929\u5f53\u7740\u5973\u6027\u6495\u7834\u5468\u8eab\u8863\u670d\u3002 \u3000\u3000\u4f46\u90a3\u662f\u88ab\u201c\u94f6\u7bab\u593a\u9b42\u201d\u7ae0\u58eb\u670b\u7684\u201c\u97f3\u9b54\u593a\u9b42\u201d\u6240\u4f24\uff0c\u4e00\u65f6\u5931\u53bb\u7406\u6027\uff0c\u5728\u5e7b\u89c9\u4e2d\u505a\u51fa\u7684\u4e0b \u610f\u8bc6\u884c\u52a8\u3002 \u3000\u3000\u4f46\u6b64\u65f6\u4ed6\u5374\u662f\u5934\u8111\u6e05\u9192\uff0c\u867d\u7136\u4f1a\u610f\u56db\u4e2a\u5999\u9f84\u5973\u5a62\u773c\u5149\u4e2d\u6240\u9732\u7684\u610f\u601d\uff0c\u4f46\u4e00\u65f6\u4e4b\u95f4\uff0c\u4ecd\u662f \u6ca1\u6709\u52c7\u6c14\u8131\u4e0b\u81ea\u5df1\u8eab\u4e0a\u7684\u8863\u670d\u3002 \u3000\u3000\u6b64\u65f6\uff0c\u7a81\u7136\u5ba4\u5916\u4e00\u4e2a\u8f7b\u76c8\u7684\u811a\u6b65\u58f0\u8d70\u6765\uff0c\u5ffd\u4ece\u95e8\u5916\u4f20\u8fdb\u83ba\u58f0\u71d5\u8bed\u8bf4\u9053\uff1a\u201c\u524d\u5385\u9152\u5e2d\u5df2\u7ecf \u6446\u4e0a\u4e86\uff0c\u8d35\u5ba2\u51b0\u6d74\u5df2\u6bd5\uff0c\u5c31\u8bf7\u5165\u5e2d\uff01\u201d \u3000\u3000\u56db\u4e2a\u8d64\u88f8\u5c0f\u5a62\uff0c\u4e00\u9f50\u54c8\u54c8\u7b11\u9053\uff1a\u4ed6\u2026\u2026\u8fd8\u6ca1\u6709\u5f00\u59cb\u6d17\u54e9\uff01\u201d\u8bf4\u5b8c\u53c8\u662f\u54c8\u54c8\u2026\u2026\u7b11\u4e86\u4e00 \u9635\u3002 \u3000\u3000\u201c\u600e\u4e48\uff1f\u8fd9\u4e48\u4e45\u4e86\uff0c\u4f60\u4eec\u56db\u4e2a\u8fd8\u4e0d\u670d\u4f8d\u2026\u2026\u201d\u95e8\u5916\u4e4b\u4eba\u8bf4\u81f3\u6b64\u5904\uff0c\u5ffd\u63a8\u95e8\u800c\u5165\u3002 \u3000\u3000\u89c1\u5c55\u767d\u8863\u670d\u7a7f\u5f97\u6574\u6574\u9f50\u9f50\uff0c\u7ad9\u5728\u6d74\u5ba4\u4e2d\u592e\u53d1\u6014\uff0c\u5979\u9a6c\u4e0a\u7b11\u8bed\u76c8\u76c8\u5730\u8bf4\u9053\uff1a\u201c\u770b\u4f60\u4eec\u56db \u4e2a\uff0c\u6ca1\u5148\u7ed9\u5ba2\u4eba\u89e3\u8863\u670d\uff0c\u5012\u5148\u628a\u81ea\u5df1\u5265\u5f97\u5149\u5149\u7684\uff0c\u771f\u662f\uff01\u4f60\u4eec\u8d8a\u6765\u8d8a\u4e0d\u4f1a\u505a\u4e8b\u4e86\uff01\u201d \u3000\u3000\u5c55\u767d\u89c1\u8fdb\u6765\u7684\u4e5f\u662f\u4e00\u4e2a\u5a62\u5973\u6a21\u6837\u7684\u5c11\u5973\uff0c\u53cc\u5341\u5e74\u534e\uff0c\u98ce\u97f5\u5a1f\u7136\uff0c\u5e74\u7eaa\u6bd4\u56db\u4e2a\u5c0f\u5a62\u7565\u5927\uff0c \u4e00\u8eab\u6d45\u7c89\u8863\u88d9\uff0c\u670d\u9970\u4e5f\u6bd4\u8f83\u8003\u7a76\uff0c\u60f3\u5fc5\u5979\u662f\u4e00\u4e2a\u5730\u4f4d\u8f83\u9ad8\u7684\u5a62\u5973\u3002 \u3000\u3000\u4f46\uff0c\u5979\u4e00\u8fdb\u95e8\u4fbf\u6765\u89e3\u5c55\u767d\u7684\u8863\u94ae\uff0c\u5c55\u767d\u5fc3\u91cc\u4e00\u6014\uff0c\u5c34\u5c2c\u4e07\u5206\u5730\u5de6\u53f3\u4e3a\u96be\u3002 \u3000\u3000\u5c55\u767d\u4e00\u6014\u4e4b\u4e0b\uff0c\u7c89\u8863\u5a62\u5973\u7d20\u624b\u5df2\u5230\u4ed6\u80f8\u524d\uff0c\u5c55\u767d\u4e00\u60ca\uff0c\u4e0d\u7531\u5f97\u95ea\u8eab\u4e00\u8eb2\u3002 \u3000\u3000\u4f46\u90a3\u7c89\u8863\u5a62\u5973\u8eab\u624b\u7adf\u4f3c\u4e0d\u5f31\uff0c\u5fc3\u601d\u66f4\u662f\u7075\u5de7\uff0c\u5728\u4f38\u51fa\u67d4\u8351\u65f6\uff0c\u597d\u50cf\u7b97\u5b9a\u5c55\u767d\u5fc5\u4f1a\u5411\u4e00\u4fa7 \u8eb2\u95ea\uff0c\u987a\u624b\u5411\u4fa7\u4e00\u62e8\uff0c\u5c16\u957f\u7684\u4e24\u6307\u8f7b\u8f7b\u4e00\u626f\uff0c\u7ade\u628a\u5c55\u767d\u808b\u4e0b\u7684\u8863\u94ae\u89e3\u5f00\u4e86\u3002 \u3000\u3000\u7c89\u8863\u5a62\u5973\u54af\u54af\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8d35\u5ba2\u53ef\u80fd\u662f\u7b2c\u4e00\u6b21\u6765\u91d1\u5e9c\uff0c\u4e0d\u60ef\u6211\u4eec\u670d\u4f8d\uff0c\u8bf7\u62c5\u4ee3\u4e86\uff01\u201d \u3000\u3000\u53e3\u4e2d\u8bf4\u7740\uff0c\u5c16\u957f\u4e24\u6307\u626f\u7740\u5c55\u767d\u895f\u524d\u8863\u7eca\uff0c\u5e76\u672a\u653e\u5f00\uff0c\u8eab\u5f62\u5de7\u5999\u5730\u4e00\u8f6c\uff0c\u5df2\u628a\u5c55\u767d\u7684\u8863\u895f \u89e3\u5f00\uff0e \u3000\u3000\u5c55\u767d\u7a7f\u7684\u5916\u8863\uff0c\u4ecd\u662f\u7ee3\u6709\u201c\u8c79\u7a81\u5c71\u5e84\u201d\u6807\u5fd7\u7684\u90a3\u4ef6\u9ed1\u7ef8\u98ce\u9cd6\uff0c\u655e\u9886\u535a\u5e26\uff0c\u53ea\u6709\u808b\u4e0b\u4e00\u4e2a \u6263\u7eca\uff0c\u88ab\u7c89\u8863\u5a62\u5973\u4e00\u626f\u626f\u5f00\uff0c\u5df2\u7136\u662f\u8131\u4e0b\u4e00\u534a\u3002 \u3000\u3000\u5c55\u767d\u5fc3\u5934\u5fae\u60ca\uff0c\u4f30\u4e0d\u5230\u91d1\u5e9c\u4e00\u4e2a\u5a62\u5973\uff0c\u7ade\u5177\u6709\u8fd9\u7b49\u8eab\u624b\u3002\u6b64\u65f6\uff0c\u7c89\u8863\u5a62\u5973\u62c9\u4f4f\u5c55\u767d\u8863 \u895f\uff0c\u8f6c\u8eab\u8ba9\u7ed9\u5c55\u767d\u4e86\u4e00\u4e2a\u4fa7\u80cc\uff0c\u5c55\u767d\u6b64\u65f6\u8981\u60f3\u51fa\u624b\u4f24\u5979\uff0c\u53ef\u8bf4\u662f\u6613\u5982\u53cd\u638c\u3002 \u3000\u3000\u4f46\u5c55\u767d\u662f\u6765\u91d1\u5e9c\u4f5c\u5bb9\uff0c\u867d\u7136\u5df2\u77e5\u9053\u8fd9\u91d1\u5e9c\u7684\u8001\u4eba\u201c\u9752\u86a8\u795e\u201d\u91d1\u4e5d\uff0c\u662f\u81ea\u5df1\u7684\u6740\u7236\u4ec7\u4eba\uff0c \u4f46\u672b\u5230\u7ffb\u8138\u52a8\u624b\u4ee5\u524d\uff0c\u600e\u6837\u4e5f\u4e0d\u80fd\u5411\u4e00\u4e2a\u5a62\u5973\u5148\u4e0b\u6bd2\u624b\u3002\u56e0\u6b64\uff0c\u4ed6\u53ea\u662f\u7a98\u5f97\u8138\u5b54\u6392\u7ea2\uff0c\u5e76\u6ca1 \u6709\u51fa\u624b\u63a8\u5f00\u7c89\u8863\u5a62\u5973\uff0c\u5634\u4e2d\u5374\u5403\u5403\u5730\u8bf4\u9053\uff1a\u201c\u59d1\u5a18\uff01\u4f60\u4eec\u51fa\u53bb\uff0c\u6211\u81ea\u5df1\u6765\u2026\u2026\u201d \u3000\u3000\u53ef\u662f\uff0c\u4ed6\u7684\u8bdd\u5c1a\u672a\u8bf4\u5b8c\uff0c\u201c\u53ed\u201d\u7684\u4e00\u58f0\uff0c\u4e00\u672c\u5f69\u8272\u5c01\u9762\u7684\u4e66\uff0c\u4ece\u5c55\u767d\u6000\u4e2d\u6389\u4e0b\u5730\u6765\u2026\u2026 \u3000\u3000\u201c\u54df\uff0c\u8fd9\u662f\u4ec0\u4e48\u82b1\u4e66\uff1f\u8fd9\u6837\u597d\u770b\uff01\u201d\u7c89\u8863\u5a62\u5973\u8f6c\u8ddf\u77a5\u89c1\uff0c\u5634\u4e2d\u4e00\u8fb9\u8bf4\uff0c\u4e00\u8fb9\u8eab\u5f62\u5fbd\u5f2f\uff0c \u4f38\u624b\u4fbf\u8981\u62fe\u53d6\u3002 \u3000\u3000\u5c55\u767d\u5927\u60ca\uff0c\u77e5\u9053\u81ea\u5df1\u53ea\u8eab\u6df1\u5165\u4ec7\u4eba\u5bb6\u4e2d\uff0c\u8fd9\u662f\u85cf\u9f99\u5367\u864e\u3001\u5947\u4eba\u9ad8\u624b\u5982\u4e91\u4e4b\u5730\uff0c\u800c\u300a\u9501\u9aa8 \u9500\u9b42\u5929\u4f5b\u5377\u300b\u53c8\u662f\u4e00\u672c\u6b66\u6797\u4eba\u7269\u4eba\u4eba\u90fd\u6b32\u4e89\u593a\u7684\u201c\u5929\u4e0b\u7b2c\u4e00\u5947\u4e66\u201d\uff0c\u5c82\u80fd\u8ba9\u522b\u4eba\u8fc7\u76ee\uff1f\u5927\u60ca \u4e4b\u4e0b\uff0c\u5355\u638c\u7528\u529b\u4e00\u63a8\uff0c\u7c89\u8863\u5a62\u5973\u4e0d\u9632\uff0c\u88ab\u5c55\u767d\u4e00\u638c\u63a8\u5728\u5de6\u80a9\u4e4b\u4e0a\uff0c\u201c\u54ce\u2026\u2026\u54df\u201d\u534a\u58f0\uff0c\u98de\u51fa \u4e94\u6b65\uff0c\u5657\u901a\u201d\u4e00\u58f0\uff0c\u5934\u4e0b\u811a\u4e0a\uff0c\u4e00\u5934\u683d\u8fdb\u6ee1\u6c60\u6e05\u6c34\u7684\u6d74\u6c60\u4e4b\u4e2d\u3002 \u3000\u3000\u6c34\u82b1\u56db\u6e85\uff0c\u7c89\u8863\u5a62\u5973\u5728\u6d74\u6c60\u4e2d\u7ffb\u4e86\u4e00\u4e2a\u8eab\u53c8\u722c\u4e86\u4e0a\u6765\uff0c\u5f20\u5634\u5410\u51fa\u4e00\u53e3\u6e05\u6c34\uff0c\u5468\u8eab\u6c34\u6dcb\u7433 \u7684\uff0c\u5df2\u6210\u4e86\u540d\u7b26\u5176\u5b9e\u7684\u201c\u843d\u6c64\u9e21\u201d\uff0c\u5934\u4e0a\u7684\u9752\u4e1d\uff0c\u8eab\u4e0a\u7684\u7ef8\u8863\u90fd\u88ab\u6c34\u6e7f\u900f\u4e86\u3002 \u3000\u3000\u6b64\u65f6\u5c55\u767d\u5df2\u62fe\u8d77\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\uff0c\u85cf\u5728\u6000\u5185\uff0c\u89c1\u7c89\u8863\u5a62\u5973\u843d\u8fdb\u6d74\u6c60\uff0c\u72fc\u72c8\u4e0d\u582a\uff0c\u5fc3 \u751f\u6b49\u610f\u2026\u2026 \u3000\u3000\u4f46\u90a3\u56db\u4e2a\u8d64\u88f8\u88f8\u7684\u5c0f\u5a62\u53ef\u7b11\u5f2f\u4e86\u8170\uff0c\u62cd\u624b\u6253\u638c\uff0c\u7b11\u5f97\u7389\u4f53\u4e71\u98a4\uff01 \u3000\u3000\u201c\u56db\u4e2a\u9b3c\u5934\uff01\u4f60\u4eec\u7b11\u4ec0\u4e48\uff1f\u201d\u7c89\u8863\u5a62\u5973\u72e0\u72e0\u767d\u4e86\u5c55\u767d\u4e00\u773c\uff0c\u4e00\u809a\u5b50\u6c14\u6ca1\u5730\u51fa\uff0c\u7adf\u5411\u56db\u4e2a \u5c0f\u5a62\u9a82\u9053\uff1a\u201c\u8fd8\u4e0d\u5feb\u628a\u6211\u6276\u4e0a\u6765\uff0c\u91cd\u65b0\u653e\u6c34\uff0c\u670d\u4f8d\u5ba2\u4eba\u6d17\u6fa1\uff01\u201d \u3000\u3000\u56db\u4e2a\u8d64\u88f8\u5c0f\u5a62\uff0c\u5bf9\u7c89\u8863\u5a62\u5973\u597d\u50cf\u5341\u5206\u754f\u60e7\uff0c\u88ab\u7c89\u8863\u5a62\u5973\u4e00\u9a82\uff0c\u679c\u7136\u9f50\u6536\u4e86\u7b11\u58f0\uff0c\u4ece\u6d74\u6c60 \u4e2d\u628a\u7c89\u8863\u5a62\u5973\u62c9\u4e86\u4e0a\u6765\uff0c\u53c8\u628a\u6c60\u4e2d\u7684\u6c34\u7edf\u7edf\u653e\u51fa\uff0c\u5f85\u8981\u91cd\u65b0\u6b32\u6c34\u65f6\uff0c\u90a3\u7c89\u8863\u5a62\u5973\u7a81\u7136\u8bf4\u9053\uff1a \u201c\u8fd9\u5ba2\u4eba\u662f\u516c\u5b50\u7237\u7684\u8d35\u5bbe\uff0c\u2018\u5170\u7389\u6c64\u2019\u4e0d\u591f\u5c0a\u656c\uff0c\u653e\u90a3\u8fb9\u7684\u2018\u6e29\u4e61\u6c34\u2019\u5427\uff01\u201d \u3000\u3000\u542c\u5230\u7c89\u8863\u5a62\u5973\u5982\u6b64\u8bf4\uff0c\u56db\u4e2a\u8d64\u88f8\u5c0f\u5a62\uff0c\u5fae\u6115\u4e86\u4e00\u4e0b\uff0c\u4f46\u770b\u5230\u7c89\u8863\u5a62\u5973\u7f8e\u76ee\u751f\u5a01\uff0c\u5fd9\u53c8\u4f4e \u4e0b\u5934\u53bb\uff0c\u679c\u7136\uff0c\u5230\u53e6\u4e00\u4e2a\u6c34\u95e8\u6309\u94ae\uff0c\u7acb\u5373\u6253\u5f00\u5f00\u5173\uff0c\u4e00\u6db5\u6e05\u6cc9\uff0c\u4ece\u534a\u88f8\u77f3\u96d5\u5973\u90ce\u80a9\u4e0a\u6c34\u7511\u4e2d \u6cfb\u4e86\u51fa\u6765\u3002 \u3000\u3000\u5c55\u767d\u7edd\u672a\u7559\u610f\u7c89\u8863\u5a62\u5973\uff0c\u6307\u793a\u56db\u4e2a\u8d64\u88f8\u5c0f\u5a62\u653e\u6c34\u65f6\u7684\u8138\u8272\uff0c\u53ea\u770b\u5230\u5979\u4e00\u8eab\u6e7f\u8863\u7d27\u8d34\u5728\u8eab \u4e0a\uff0c\u886c\u51fa\u5979\u4e30\u6ee1\u670d\u4f53\u4e0a\u7684\u73b2\u73d1\u66f2\u7ebf\uff0c\u800c\u4e14\u7c89\u8863\u767d\u8089\u9690\u9690\u73b0\u73b0\uff0c\u7f8e\u662f\u591f\u7f8e\uff0c\u4f46\u72fc\u72c8\u4e5f\u591f\u72fc\u72c8 \u4e86\uff0c\u5fc3\u4e2d\u8001\u5927\u4e0d\u5fcd\uff0c\u62b1\u6b49\u5730\u8bf4\u9053\uff1a\u201c\u90a3\u672c\u4e66\uff0c\u5b9e\u5728\u4e0d\u80fd\u7edc\u4f60\u770b\uff0c\u5728\u4e0b\u4e00\u65f6\u9c81\u83bd\uff0c\u5510\u7a81\u5904\uff0c\u59d1 \u5a18\u591a\u591a\u539f\u8c05\uff01\u201d \u3000\u3000\u201c\u6211\u4eec\u672c\u662f\u670d\u4f8d\u7237\u4eec\u7684\uff0c\u670d\u4f8d\u4e0d\u597d\uff0c\u8981\u6253\u8981\u9a82\uff0c\u4efb\u51ed\u7237\u4eec\u9ad8\u5174\uff0c\u8c01\u53eb\u6211\u4eec\u7239\u5988\u4e0d\u957f\u773c\uff0c \u751f\u4e0b\u6211\u4eec\u6765\u5c31\u662f\u5f53\u5a62\u5973\u7684\u547d\u5462\uff01\u201d \u3000\u3000\u7c89\u8863\u5a62\u5973\u8bf4\u6b64\u8bdd\u65f6\uff0c\u8bed\u6c14\u51b0\u51b7\u5df2\u6781\u3002\u4f46\u5c55\u767d\u81ea\u77e5\u7406\u5c48\uff0c\u4e5f\u4e0d\u5728\u610f\uff0c\u4ecd\u7136\u8bda\u6073\u5730\u8bf4\u9053\uff1a \u201c\u5728\u4e0b\u81ea\u5e7c\u8d2b\u82e6\uff0c\u672a\u53d7\u4eba\u670d\u4f8d\u60ef\uff0c\u8fd8\u662f\u8bf7\u8bf8\u4f4d\u59d1\u5a18\u9000\u51fa\uff0c\u7531\u5728\u4e0b\u81ea\u884c\u6c90\u6d74\u597d\u4e86\uff01\u201d \u3000\u3000\u7c89\u8863\u5a62\u5973\u7565\u4e00\u8fdf\u7591\uff0c\u5f53\u5373\u8bf4\u9053\uff1a\u606d\u656c\u548c\u5982\u4ece\u547d\uff0c\u5ba2\u4eba\u65e2\u6015\u6211\u4eec\u670d\u4f8d\u4e0d\u5468\uff0c\u547d\u6211\u4eec\u51fa\u53bb\uff0c \u6211\u4eec\u53ea\u597d\u51fa\u53bb\u4e86\u3002\u201d \u3000\u3000\u8bf4\u7f62\uff0c\u5bf9\u6014\u5728\u4e00\u8fb9\u56db\u4e2a\u8d64\u88f8\u5c0f\u5a62\u8bf4\u9053\uff1a\u7a7f\u4e0a\u8863\u670d\uff0c\u6211\u4eec\u8d70\uff01\u201d \u3000\u3000\u56db\u4e2a\u5c0f\u5a62\u5bf9\u7c89\u8863\u5a62\u5973\u50cf\u662f\u4e0d\u6562\u8fdd\u6297\uff0c\u5f53\u5373\u628a\u8863\u670d\u7a7f\u597d\uff0c\u5404\u81ea\u9000\u4e86\u51fa\u53bb\u3002\u7c89\u8863\u5a62\u5973\u4e34\u8d70 \u65f6\uff0c\u53c8\u8bf4\u9053\uff1a\u5ba2\u4eba\u8bf7\u5feb\u4e00\u70b9\u6d17\uff0c\u522b\u53eb\u6211\u5bb6\u516c\u5b50\u7237\u5728\u9152\u5e2d\u5bb4\u524d\u7d27\u7b49\uff01\u201d \u3000\u3000\u8bf4\u7f62\uff0c\u4e5f\u4e0d\u7b49\u5c55\u767d\u56de\u7b54\uff0c\u8eab\u5f62\u4e00\u95ea\uff0c\u8d70\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u5c55\u767d\u5306\u5306\u6d17\u597d\uff0c\u91d1\u5e9c\u4e3a\u4ed6\u51c6\u5907\u7684\u65b0\u8863\u4ed6\u4e5f\u4e0d\u7a7f\uff0c\u4ecd\u7136\u7a7f\u4e0a\u81ea\u5df2\u90a3\u5957\u7834\u8863\uff0c\u51fa\u4e86\u6d74\u5ba4\uff0c\u5374 \u89c1\u56db\u4e2a\u9752\u8863\u5c0f\u5a62\uff0c\u4ecd\u7136\u5728\u95e8\u524d\u7b49\u7740\uff0c\u5f53\u5373\u968f\u7740\u56db\u4e2a\u9752\u8863\u5c0f\u5a62\uff0c\u6765\u5230\u4e86\u5927\u5385\u3002 \u3000\u3000\u5c1a\u672a\u8d70\u8fdb\u5927\u5385\u8ddf\u524d\uff0c\u8001\u8fdc\u4fbf\u542c\u5230\u90a3\u4e9b\u6c5f\u6e56\u8c6a\u5ba2\u9ad8\u58f0\u8c08\u7b11\uff0c\u5c55\u767d\u6ce8\u610f\u4e00\u542c\uff0c\u5374\u6b63\u5728\u8c08\u8bba\u81ea \u5df1\u3002\u53ea\u542c\u4e00\u4eba\u8bf4\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\uff0c\u770b\u4e0d\u51fa\u4ec0\u4e48\u8def\u6570\uff0c\u4f46\u624b\u5e95\u4e0b\u8fd8\u771f\u6709\u4e24\u4e0b\u5b50\uff0c\u2018\u6df7\u6c5f\u9f99\u2019\u6881 \u670b\uff0c\u7ade\u5403\u4e0d\u4f4f\u4ed6\u4e00\u638c\uff01\u201d \u3000\u3000\u53e6\u4e00\u4eba\u63a5\u53e3\u9053\uff1a\u201c\u624b\u6cd5\u6742\u5f97\u5f88\uff0c\u4e0d\u77e5\u4ed6\u5c0f\u5c0f\u5e74\u7eaa\u662f\u600e\u6837\u7ec3\u7684\uff1f\u201d \u3000\u3000\u63a5\u7740\uff0c\u4e00\u7247\u5567\u5567\u79f0\u8d5e\u4e4b\u58f0\u3002 \u3000\u3000\u5c55\u767d\u542c\u5230\u4eba\u5728\u80cc\u5730\u91cc\u6697\u8d5e\u81ea\u5df1\uff0c\u7981\u4e0d\u4f4f\u5fc3\u91cc\u7565\u611f\u6b23\u6170\u3002\u4f46\u5ffd\u542c\u4e00\u4e2a\u4eba\u9ad8\u58f0\u56b7\u9053\uff1a\u201c\u4f60\u4eec \u522b\u66ff\u4ed6\u5439\u4e86\uff01\u4ed6\u8fd8\u4e0d\u662f\u63a5\u4e0d\u4f4f\u2018\u94c1\u7ffc\u98de\u9e4f\u2019\u5df4\u4e8c\u7237\u7684\u4e00\u51fb\uff01\u2026\u2026\u201d \u3000\u3000\u5c55\u767d\u6b64\u65f6\u5df2\u8fc8\u8fdb\u5927\u5385\uff0c\u6570\u5341\u9053\u773c\u5149\u4e00\u9f50\u6295\u4e86\u8fc7\u6765\uff0c\u4f17\u4eba\u53ea\u89c9\u773c\u524d\u4e00\u4eae\uff0c\u773c\u89c1\u65b9\u624d\u8fdb\u6765 \u65f6\uff0c\u6ee1\u5934\u4e71\u53d1\uff0c\u4e00\u8138\u6c57\u6e0d\u7684\u843d\u9b44\u5c11\u4e2d\uff0c\u7ade\u4e00\u53d8\u4e3a\u4e30\u795e\u4fca\u6717\u3001\u7389\u9762\u6731\u5507\u3001\u7709\u68a2\u773c\u89d2\u82f1\u6c14\u52c3\u52c3\u7684 \u7f8e\u5c11\u5e74\u3002 \u3000\u3000\u867d\u7136\u4ed6\u8eab\u4e0a\u8fd8\u662f\u90a3\u4ef6\u7834\u65e7\u7684\u9ed1\u7ef8\u957f\u886b\uff0c\u4f46\u5df2\u63a9\u9970\u4e0d\u4f4f\u4ed6\u90a3\u4fca\u7f8e\u7684\u4eea\u8868\u3002 \u3000\u3000\u4f17\u4eba\u7684\u8bae\u8bba\uff0c\u7acb\u523b\u88ab\u5c55\u767d\u4e0d\u51e1\u7684\u98ce\u59ff\u9547\u4f4f\u4e86\uff0c\u4e00\u9f50\u54d1\u53e3\u65e0\u80b2\uff0c\u77aa\u5927\u53cc\u773c\u671b\u7740\u4ed6\u3002\u90a3\u6709\u7740 \u201c\u6c5f\u5357\u7b2c\u4e00\u7f8e\u4eba\u201d\u4e4b\u79f0\u7684\u91d1\u5e9c\u5343\u91d1\u91d1\u5f69\u51e4\uff0c\u4e00\u53cc\u5982\u6c34\u7684\u7f8e\u76ee\uff0c\u66f4\u662f\u4e00\u77ac\u4e0d\u77ac\u671b\u7740\u4ed6\uff0c\u95ea\u8fc7\u4e86 \u4e00\u9053\u5947\u5f02\u7684\u5149\u8f89\u2026\u2026 \u3000\u3000\u201c\u7965\u9e9f\u516c\u5b50\u201d\u65e9\u5df2\u7ad9\u8d77\uff0c\u62b1\u62f3\u8083\u5bb9\uff0c\u8bf7\u5c55\u767d\u5165\u5e2d\uff0c\u5e76\u4e3a\u5c55\u767d\u4e00\u4e00\u4ecb\u7ecd\u5728\u5ea7\u7684\u4f17\u4eba\u3002 \u3000\u3000\u5c55\u767d\u89c1\u5927\u5385\u4e2d\u957f\u6761\u684c\u6446\u6210\u4e00\u4e2a\u9a6c\u8e44\u5f62\uff0c\u5728\u5ea7\u7684\u6c5f\u6e56\u8c6a\u5ba2\uff0c\u8db3\u6709\u6570\u5341\u4eba\u4e4b\u591a\uff0c\u4e00\u4e2a\u4e2a\u7cbe\u534e \u5185\u8574\uff0c\u53cc\u76ee\u795e\u5149\u6151\u4eba\uff0c\u77e5\u9053\u5747\u662f\u6b66\u6797\u9ad8\u624b\u3002\u4ed6\u4e00\u8fb9\u62b1\u62f3\u5411\u4f17\u4eba\u89c1\u672d\uff0c\u4e00\u8fb9\u542c\u201c\u7965\u9e9f\u516c\u5b50\u201d\u5ff5 \u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u2018\u94c1\u80cc\u9a7c\u9f99\u2019\u516c\u5b59\u695a\u524d\u8f88\uff01\u201d\u5c55\u767d\u89c1\u662f\u4e00\u4e2a\u9a7c\u80cc\u8001\u8005\uff0c\u795e\u6001\u5a01\u731b\uff0c\u53cc\u76ee\u795e\u5149\u5982 \u7535\uff0c\u77e5\u662f\u4e00\u5927\u9ad8\u624b\uff0c\u4e00\u62b1\u62f3\u9053\uff1a\u201c\u516c\u5b59\u524d\u8f88\uff0c\u4e45\u4ef0\uff0c\u4e45\u4ef0\uff01\u201d \u3000\u3000\u201c\u94c1\u80cc\u9a7c\u9f99\u201d\u54c8\u54c8\u4e00\u9635\u5927\u7b11\uff0c\u58f0\u9707\u5c4b\u74e6\uff0c\u9053\uff1a\u201c\u5c0f\u54e5\u513f\uff0c\u4e0d\u5fc5\u5ba2\u6c14\uff01\u201d \u3000\u3000\u201c\u8fd9\u4f4d\u662f\u2018\u94c1\u7ffc\u98de\u9e4f\u2019\u5df4\u5929\u8d6b\u524d\u8f88\uff01\u201c\u7965\u9e9f\u516c\u5b50\u201d\u5728\u8bf4\u5230\u201c\u94c1\u7ffc\u98de\u9e4f\u201d\u5df4\u5929\u8d6b\u65f6\uff0c\u7279\u522b \u52a0\u91cd\u4e86\u8bed\u6c14\uff0c\u201c\u521a\u624d\u5c55\u5144\u5df2\u7ecf\u4f1a\u8fc7\u4e86\uff0c\u6240\u8c13\u2018\u4e0d\u6253\u4e0d\u6210\u76f8\u8bc6\u2019\uff0c\u4eca\u540e\u8fd8\u8bf7\u4e8c\u4f4d\u591a\u4eb2\u8fd1\u4eb2 \u8fd1\u3002\u201d\u8bf4\u5b8c\u4e5f\u662f\u54c8\u54c8\u4e00\u9635\u5927\u7b11\u3002 \u3000\u3000\u5c55\u767d\u53ea\u89c9\u8138\u4e0a\u53d1\u70e7\uff0c\u4e00\u80a1\u88ab\u7f9e\u8fb1\u540e\u7684\u6124\u6012\u4e4b\u611f\uff0c\u76f4\u51b2\u8111\u95e8\uff0c\u4f46\u662f\u4ed6\u5374\u5f3a\u5fcd\u7740\u6ca1\u6709\u53d1\u4f5c\u51fa \u6765\uff0c\u5fc3\u91cc\u5374\u81ea\u8d23\u9053\uff1a\u201c\u5c55\u767d\u5440\uff01\u5c55\u767d\uff01\u4f60\u8fde\u4eba\u5bb6\u95e8\u4e0b\u4e00\u4e2a\u98df\u5ba2\u90fd\u6253\u4e0d\u8fc7\uff0c\u8fd8\u5411\u4eba\u5bb6\u4e3b\u513f\u62a5\u96ea \u6df1\u4ec7\uff1f\u2026\u2026\u201d \u3000\u3000\u5c55\u767d\u5fc3\u60c5\u6fc0\u52a8\uff0c\u201c\u7965\u9e9f\u516c\u5b50\u201d\u9010\u6b21\u4ecb\u7ecd\uff0c\u8bf8\u6b66\u6797\u9ad8\u624b\u4e4b\u4e2d\u968f\u4fbf\u63d0\u51fa\u4efb\u4f55\u4e00\u4eba\uff0c\u90fd\u53ef\u9707\u52a8 \u6b66\u6797\uff0c\u4f46\u4ed6\u4e00\u4e2a\u4e5f\u6ca1\u6709\u542c\u8fdb\u8033\u4e2d\uff0c\u53ea\u662f\u6014\u6014\u5730\u7ad9\u5728\u90a3\u91cc\uff0c\u5185\u5fc3\u4e2d\u5374\u662f\u70ed\u8840\u6cb8\u817e\uff0c\u56e0\u6b64\uff0c\u7ade\u8fde \u773c\u524d\u7684\u89c1\u9762\u793c\u90fd\u5fd8\u4e86\u3002 \u3000\u3000\u7a81\u542c\u4e00\u58f0\u51b7\u54fc\uff0c\u8d77\u81ea\u5ea7\u4fa7\uff0c\u58f0\u97f3\u867d\u4e0d\u5927\uff0c\u5374\u5f7b\u9aa8\u51b0\u51b7\uff0c\u51b7\u54fc\u8fc7\u540e\uff0c\u4e00\u4eba\u6602\u7136\u8bf4\u9053\uff1a\u201c\u65e2 \u6ca1\u6709\u771f\u624d\u5b9e\u5b66\uff0c\u53c8\u6beb\u4e0d\u61c2\u6c5f\u6e56\u793c\u6570\uff0c\u7ade\u606c\u7136\u6562\u5750\u9ad8\u4f4d\uff01\u201d \u3000\u3000\u7b49\u5230\u8fd9\u4e00\u58f0\u51b7\u54fc\uff0c\u53ca\u8fd9\u8ba5\u523a\u5982\u5229\u7bad\u7684\u8bed\u6c14\u4f20\u6765\uff0c\u628a\u5c55\u767d\u4ece\u7f9e\u6124\u4e2d\u60ca\u9192\u8fc7\u6765\uff0c\u8f6c\u5934\u4e00\u770b\uff0c \u7adf\u662f\u4e00\u4e2a\u9ed1\u8863\u4fca\u7f8e\u5c11\u5e74\u6240\u53d1\u3002 \u3000\u3000\u8fd9\u4fca\u7f8e\u5c11\u5e74\u7edd\u4e0d\u5230\u4e8c\u5341\u5c81\uff0c\u751f\u5f97\u9762\u5982\u5085\u7c89\uff0c\u5507\u82e5\u6d82\u4e39\uff0c\u957f\u7709\u5165\u9b03\uff0c\u76ee\u82e5\u6717\u661f\u3002\u4e0d\u4f46\u4eba\u957f \u5f97\u5982\u6f58\u5b89\u3001\u5b8b\u7389\uff0c\u5c31\u770b\u4ed6\u5c0f\u5c0f\u5e74\u7eaa\u80fd\u6742\u5750\u5728\u4f17\u591a\u4e00\u6d41\u9ad8\u624b\u4e4b\u5217\uff0c\u6b66\u529f\u5fc5\u4e5f\u4e0d\u5f31\u3002 \u3000\u3000\u539f\u6765\u8fd9\u9ed1\u8863\u7f8e\u5c11\u5e74\uff0c\u4e43\u662f\u201c\u9752\u86a8\u795e\u201d\u91d1\u4e5d\u7684\u7231\u5f92\uff0c\u540d\u5524\u5b5f\u5982\u840d\uff0c\u4ece\u5c0f\u5728\u91d1\u5e9c\u957f\u5927\uff0c\u91d1\u4e5d \u7231\u62a4\u4ed6\u4e0d\u4e9a\u4e8e\u7231\u81ea\u5df1\u7684\u72ec\u5b50\u91d1\u7965\u9e9f\uff0c\u56e0\u6b64\u628a\u81ea\u5df1\u4e00\u8eab\u9ad8\u5f3a\u7684\u6b66\u529f\uff0c\u503e\u56ca\u76f8\u6388\u3002\u867d\u7136\u8fd9\u5b5f\u5982 \u840d\uff0c\u5e74\u7eaa\u4e0d\u5927\uff0c\u4f46\u5df2\u6709\u4e86\u5f88\u9ad8\u7684\u6b66\u529f\uff0c\u5c24\u5176\u5bf9\u201c\u9752\u86a8\u795e\u201d\u7684\u7edd\u95e8\u6697\u5668\u201c\u9752\u86a8\u91d1\u94b1\u9556\u201d\uff0c\u53ef\u4ee5 \u8bf4\u5b8c\u5168\u627f\u88ad\u4e86\u4e0b\u6765\uff0c\u53ea\u662f\u5185\u529f\u771f\u529b\u8fd8\u7a0d\u6b20\u706b\u5019\u800c\u5df2\uff0c\u5728\u6c5f\u6e56\u5e74\u8f7b\u4e00\u8f88\u7684\u6b66\u6797\u9ad8\u624b\u4e4b\u4e2d\uff0c\u582a\u79f0 \u4e3a\u4f7c\u997a\uff0c\u5df2\u7ecf\u53eb\u54cd\u4e86\u4e00\u4e2a\u540d\u53f7\uff0c\u4eba\u79f0\u201c\u7389\u9762\u5c0f\u9752\u86a8\u201d\uff01 \u3000\u3000\u201c\u7389\u9762\u5c0f\u9752\u86a8\u201d\u4e0e\u91d1\u6c0f\u5144\u59b9\u5e74\u9f84\u76f8\u82e5\uff0c\u6bd4\u201c\u7965\u9e9f\u516c\u5b50\u201d\u5c0f\u4e24\u5c81\uff0c\u6bd4\u91d1\u5f69\u98ce\u5927\u4e00\u5c81\uff0c\u4e0e \u201c\u7965\u9e9f\u516c\u5b50\u201d\u5144\u59b9\u4ece\u5c0f\u4e00\u5757\u957f\u5927\uff0c\u60c5\u903e\u540c\u80de\u3002\u5e74\u7eaa\u7a0d\u957f\uff0c\u6e10\u89e3\u4eba\u4e8b\uff0c\u91d1\u5f69\u51e4\u53c8\u662f\u7f8e\u903e\u5929\u4eba\u7684 \u7f8e\u4e3d\uff0c\u800c\u4ed6\u81ea\u5df1\u81ea\u89c6\u751a\u9ad8\uff0c\u867d\u662f\u5bc4\u517b\u5728\u91d1\u5e9c\u7684\u4e00\u4e2a\u5b64\u513f\uff0c\u5185\u5fc3\u91cc\u5374\u628a\u8fd9\u6709\u7740\u201c\u6c5f\u5357\u7b2c\u4e00\u7f8e \u4eba\u201d\u4e4b\u79f0\u7684\u91d1\u5f69\u51e4\uff0c\u89c6\u4e3a\u81ea\u5df1\u7684\u7231\u4fa3\u3002 \u3000\u3000\u91d1\u5f69\u51e4\u5bf9\u4ed6\u4e5f\u5f88\u597d\uff0c\u5e73\u5e38\u201c\u840d\u54e5\u957f\uff0c\u840d\u54e5\u77ed\u201d\u5730\u4e71\u53eb\uff0c\u4f46\u201c\u7389\u9762\u5c0f\u9752\u86a8\u201d\u5e76\u4e0d\u80fd\u611f\u5230\u6ee1 \u8db3\uff0c\u56e0\u4e3a\u4ed6\u770b\u5f97\u51fa\uff0c\u91d1\u5f69\u51e4\u5bf9\u4ed6\u53ea\u662f\u5144\u59b9\u822c\u7684\u611f\u60c5\uff0c\u5374\u7f3a\u5c11\u5e74\u8f7b\u604b\u4eba\u7684\u70ed\u7231\u3002\u5c24\u5176\u91d1\u5f69\u51e4\u4e3a \u201c\u9752\u86a8\u795e\u201d\u91d1\u4e5d\u6700\u5e7c\u7684\u7231\u5973\uff0c\u4ece\u5c0f\u5a07\u751f\u60ef\u517b\uff0c\u5fc3\u91cc\u8981\u600e\u6837\u4fbf\u600e\u6837\uff0c\u5e38\u559c\u6b22\u4e1c\u8dd1\u897f\u8dd1\uff0c\u5c24\u5176\u559c \u6b22\u4e0e\u6c5f\u6e56\u4e0a\u4eba\u7269\u63a5\u8fd1\uff0c\u6beb\u4e0d\u907f\u8bb3\uff0c\u8131\u7565\u5f62\u8ff9\uff0c\u4e0e\u4efb\u4f55\u65b0\u5014\u8d77\u7684\u5e74\u8f7b\u597d\u624b\u90fd\u613f\u610f\u62c9\u62e2\uff0c\u4f46\uff0c\u8fd9 \u4e9b\u843d\u5728\u201c\u7389\u9762\u5c0f\u9752\u86a8\u201d\u7684\u773c\u4e2d\uff0c\u5374\u6700\u4f7f\u4ed6\u5185\u5fc3\u5992\u5fcc\u96be\u5b89\u3002 \u3000\u3000\u4eca\u5929\u56de\u7a0b\u4e0a\uff0c\u5e76\u7559\u9a91\u9a6c\uff0c\u5230\u4e86\u5bb6\u4e2d\uff0c\u663e\u51fa\u4e0d\u5bfb\u5e38\u7684\u5173\u6ce8\uff0c\u201c\u7389\u9762\u5c0f\u9752\u86a8\u201d\u5df2\u7ecf\u6ee1\u8138\u5992\u706b \u70e7\u5230\u8138\u95e8\u4e0a\uff0c\u522b\u770b\u4ee5\u524d\u90a3\u4e48\u591a\u6b66\u6797\u6770\u51fa\u7684\u540e\u8d77\u4e4b\u79c0\u6ca1\u6709\u4ece\u4ed6\u624b\u4e2d\uff0c\u628a\u8fd9\u201c\u6c5f\u5357\u7b2c\u4e00\u7f8e\u4eba\u201d\u62a2 \u8d70\uff0c\u53ef\u662f\uff0c\u8fd9\u843d\u9b44\u5c11\u5e74\u7684\u51fa\u73b0\uff0c\u5374\u4f7f\u4ed6\u6709\u4e86\u4e0d\u7965\u7684\u9884\u611f\u3002 \u3000\u3000\u53c8\u89c1\u4f17\u4eba\u90fd\u5728\u80cc\u5730\u6697\u8d5e\u5c55\u767d\uff0c\u4ed6\u624d\u5927\u58f0\u53eb\u51fa\u90a3\u4e00\u53e5\uff1a\u4ed6\u8fd8\u4e0d\u662f\u63a5\u4e0d\u4f4f\u2018\u94c1\u7ffc\u98de\u9e4f\u2019\u5df4\u4e8c \u7237\u7684\u4e00\u51fb\uff01\u201d \u3000\u3000\u5982\u4eca\u5c55\u767d\u88ab\u201c\u7965\u9e9f\u516c\u5b50\u201d\u62c9\u5230\u9996\u5ea7\uff0c\u4e0d\u6309\u4f8b\u89c4\u9000\u4e0b\uff0c\u6240\u4ee5\u4ed6\u6293\u4f4f\u8fd9\u4e2a\u673a\u4f1a\uff0c\u7ed9\u5c55\u767d\u4e0b\u4e0d \u4e86\u53f0\uff0c\u518d\u8005\u4e5f\u662f\u501f\u673a\u4f1a\u5411\u5c55\u767d\u6311\u8845\uff0c\u60f3\u4ee5\u81ea\u5df1\u624b\u4e2d\u4e00\u67c4\u5251\uff0c\u808b\u4e0b\u4e00\u56ca\u201c\u9752\u86a8\u91d1\u94b1\u9556\u201d\uff0c\u628a\u5c55 \u767d\u8d76\u8d70\u6216\u9664\u53bb\uff0c\u4ee5\u62d4\u6389\u8fd9\u4e2a\u201c\u8089\u4e2d\u523a\uff0c\u773c\u4e2d\u9489\u201d\u3002 \u3000\u3000\u5c55\u767d\u65e9\u5c31\u5fc3\u91cc\u4e0d\u81ea\u5728\uff0c\u5f3a\u81ea\u6309\u538b\u7740\u672b\u53d1\u4f5c\uff0c\u5982\u4eca\u542c\u201c\u7389\u9762\u5c0f\u9752\u86a8\u201d\u8fd9\u4e00\u8ba5\u8bbd\uff0c\u518d\u4e5f\u6309\u538b \u4e0d\u4f4f\uff0c\u7acb\u523b\u4e00\u62b1\u62f3\u8bf4\u9053\uff1a\u201c\u5230\u8d35\u5e9c\u6765\uff0c\u5e76\u975e\u51fa\u81ea\u5728\u4e0b\u60c5\u613f\uff0c\u65e2\u7136\u4e0d\u53d7\u6b22\u8fce\uff0c\u5728\u4e0b\u5c31\u6b64\u544a \u8f9e\uff01\u201d \u3000\u3000\u8bf4\u7f62\u62c2\u8896\u800c\u8d85\uff0c\u4fbf\u6b32\u8d77\u8eab\u79bb\u53bb\u2026\u2026 \u3000\u3000\u201c\u7965\u9e9f\u516c\u5b50\u201d\u8d76\u7d27\u4e0a\u524d\u62e6\u4f4f\uff0c\u542b\u7b11\u8bf4\u9053\uff1a\u201c\u5c55\u5144\uff0c\u96be\u9053\u4ee5\u4e3a\u610f\u4e0d\u81f3\u8bda\u5417\uff1f\u9152\u5bb4\u5df2\u7ecf\u6446 \u597d\uff0c\u65e0\u8bba\u5982\u4f55\u4e5f\u5f97\u8bf7\u5c55\u5144\u7565\u8fdb\u51e0\u676f\u6c34\u9152\uff0c\u4e5f\u597d\u8ba9\u7965\u9e9f\u4e00\u5c3d\u5730\u4e3b\u4e4b\u8c0a\uff01\u201d\u5c55\u767d\u89c1\u7965\u9e9f\u516c\u5b50\u201d\u8bed \u610f\u8bda\u6073\uff0c\u5fc3\u4e2d\u6697\u6b49\uff1a\u201c\u7965\u9e9f\u70ed\u80a0\uff0c\u8a00\u4e0b\u4e0d\u865a\uff0c\u770b\u4ed6\u793c\u8d24\u4e0b\u58eb\uff0c\u8c26\u865a\u8bda\u6073\uff0c\u5b8c\u5168\u53d1\u81ea\u5185\u5fc3\uff0c\u51b3 \u4e0d\u662f\u673a\u8bc8\u6743\u8c0b\u4e4b\u58eb\u505a\u4f5c\u5f97\u51fa\u6765\u7684\uff01\u201d \u3000\u3000\u4f46\u5634\u4e2d\u4ecd\u8bf4\u9053\uff1a\u91d1\u5144\u76db\u60c5\uff0c\u5728\u4e0b\u5fc3\u9886\u4e86\uff01\u5b9e\u662f\u5728\u4e0b\u771f\u6709\u6025\u4e8b\uff0c\u6539\u65e5\u518d\u8ba8\u6270\u5427\uff01\u201d \u3000\u3000\u8bf4\u7740\u4ecd\u5411\u5916\u8d70\u53bb\u3002 \u3000\u3000\u91d1\u5e9c\u65e2\u6562\u6842\u201c\u6c5f\u5357\u7b2c\u4e00\u5bb6\u201d\u7684\u724c\u5b50\uff0c\u5f85\u5ba2\u7684\u5e2d\u9762\u771f\u662f\u591f\u8003\u7a76\u4e86\uff0c\u73cd\u80b4\u7f8e\u5473\u5df2\u4e0d\u8db3\u55bb\u5176 \u73cd\uff0c\u9f99\u809d\u51e4\u9ad3\u201d\u4ea6\u4e0d\u8db3\u55bb\u5176\u8d35\uff0c\u83dc\u624d\u4e0a\u4e86\u51e0\u9053\uff0c\u9152\u4ec5\u6253\u5f00\u4e00\u575b\uff0c\u5df2\u662f\u6ee1\u5ba4\u6e05\u9999\uff0c\u6709\u4e2a\u8001\u8b66\uff0c \u5df2\u662f\u553e\u6d8e\u6b32\u6ef4\uff0c\u4f46\u5c55\u767d\u8179\u4e2d\u7eb5\u662f\u9965\u80a0\u8f98\u8f98\uff0c\u5bf9\u90a3\u7f8e\u9152\u4f73\u770b\u5df2\u662f\u4e1d\u6beb\u4e0d\u611f\u5174\u8da3\u201c\u541b\u5b50\u4e0d\u5403\u78cb\u6765 \u98df\u201d\uff0c\u826f\u6709\u4ee5\u4e5f\u3002 \u3000\u3000\u5c1a\u672a\u7b49\u201c\u7965\u9e9f\u516c\u5b50\u201d\u518d\u53d1\u8a00\uff1a\u7389\u9762\u5c0f\u9752\u86a8\u201d\u5df2\u7136\u547c\u7684\u4e00\u58f0\uff0c\u4ece\u5750\u4f4d\u4e0a\u7ad9\u4e86\u8d77\u6765\uff0c\u51b7\u51b7\u5730 \u8bf4\u9053\uff1a\u201c\u8981\u8d70\u5c31\u8d70\uff01\u4f55\u5fc5\u88c5\u8154\u4f5c\u52bf\uff0c\u96be\u9053\u6211\u4eec\u5efa\u4e1a\u91d1\u5e9c\uff0c\u8fd8\u7f3a\u4e86\u4f60\u8fd9\u4f4d\u9ad8\u5ba2\u5417\uff01\u201d \u3000\u3000\u201c\u5e08\u5144\uff01\u201d\u91d1\u5f69\u51e4\u5fcd\u4e0d\u4f4f\u5728\u4e00\u8fb9\u9053\uff1a\u4f60\u8fd9\u7b97\u4ec0\u4e48\uff1f\u54e5\u54e5\u7559\u5ba2\uff0c\u4f60\u9010\u5ba2\uff01\u201d \u3000\u3000\u201c\u7965\u9e9f\u516c\u5b50\u201d\u4e5f\u5411\u5b5f\u5982\u840d\u77aa\u4e86\u4e00\u773c\uff0c\u4ecd\u7136\u4e07\u5206\u8bda\u631a\u5730\u62c9\u4f4f\u5c55\u767d\u9053\uff1a\u201c\u5728\u4e0b\u8fd9\u4f4d\u5c0f\u5e08\u5f1f\u706b \u7206\u6027\u5b50\uff0c\u9c81\u83bd\u4e4b\u5904\u8bf7\u8c05\uff01\u5c55\u5144\uff0c\u6709\u518d\u8981\u7d27\u7684\u6025\u4e8b\uff0c\u4e5f\u559d\u4e24\u676f\u6c34\u9152\u518d\u8d70\uff0c\u82e5\u98de\u7136\uff0c\u5c55\u5144\u4fbf\u662f\u77a7 \u4e0d\u8d77\u7965\u9e9f\u4e86\uff01\u201d \u3000\u3000\u5c55\u767d\u54ea\u91cc\u80af\u518d\u5c31\u5ea7\uff0c\u5fc3\u91cc\u6267\u610f\u8981\u8d70\uff0c\u8fde\u51e0\u4f4d\u524d\u8f88\u9ad8\u624b\uff0c\u4e5f\u90fd\u51fa\u8a00\u7559\u4ed6\uff0c\u4ed6\u4e00\u6982\u4e0d\u5e94\u3002 \u3000\u3000\u201c\u5e74\u8f7b\u4eba\u522b\u62d6\u62d6\u62c9\u62c9\uff01\u4e00\u70b9\u4e0d\u723d\u5feb\uff01\u201d\u201c\u94c1\u80cc\u9a7c\u9f99\u201d\u662f\u51fa\u540d\u7684\u9152\u9b3c\uff0c\u89c1\u9152\u6446\u4e86\u534a\u5929\uff0c\u4e0d \u80fd\u5230\u53e3\uff0c\u65e9\u5df3\u4e0d\u8010\u9053\uff1a\u201c\u96be\u9053\u8fd9\u91d1\u5e9c\u662f\u9e3f\u95e8\u5bb4\uff0c\u9152\u4e2d\u653e\u6bd2\u836f\uff0c\u4f60\u8fd9\u5c0f\u5a03\u513f\u624d\u4e0d\u6562\u559d\uff01\u201d \u3000\u3000\u8fd9\u4e00\u53e5\u8bdd\u6fc0\u6012\u4e86\u5c55\u767d\uff0c\u9053\uff1a\u516c\u5b59\u524d\u8f88\u8fd9\u4e00\u8bf4\uff0c\u5c55\u767d\u5012\u975e\u8981\u559d\u4e09\u676f\u4e0d\u53ef\u4e86\uff0c\u4f46\u8bdd\u8bf4\u5728\u5934 \u91cc\uff0c\u5728\u4e0b\u4e09\u676f\u9152\u5e72\uff0c\u7acb\u523b\u5c31\u8d70\uff0c\u4e5f\u514d\u5f97\u8bf8\u4f4d\u8ba5\u7b11\u5c55\u767d\u662f\u6015\u6b7b\u8d2a\u751f\u4e4b\u8f88\uff01\u201d \u3000\u3000\u8bf4\u7f62\uff0c\u7aef\u8d77\u684c\u4e0a\u9152\u676f\uff0c\u5411\u56db\u5468\u56f4\u4e00\u4e3e\u9053\uff1a\u201c\u6765\uff01\u5c55\u767d\u540e\u751f\u665a\u8f88\uff0c\u656c\u5404\u4f4d\u524d\u8f88\u4e00\u676f\uff01\u201d\u4ef0 \u8116\u5b50\u4e00\u996e\u800c\u5c3d\u3002 \u3000\u3000\u201c\u6211\u4e5f\u7b97\u8001\u524d\u8f88\u5417\uff1f\u201d\u91d1\u5f69\u51e4\u8bf4\u7740\uff0c\u54af\u54af\u4e00\u7b11\uff0c\u4e5f\u968f\u7740\u559d\u4e86\u4e00\u676f\u3002 \u3000\u3000\u5c55\u767d\u8138\u4e00\u7ea2\uff0c\u8fd9\u4e00\u676f\u9152\u4e0b\u809a\uff0c\u5c31\u89c9\u5f97\u50cf\u4e00\u80a1\u70d9\u7ea2\u4e86\u7684\u94c1\uff0c\u5012\u8fdb\u8179\u4e2d\u4e00\u6837\uff0c\u53ea\u89c9\u5947\u70ed\u5982 \u711a\uff0c\u4e0d\u7531\u5fc3\u4e2d\u4e00\u64e6\uff0c\u6697\u9053\uff1a\u201c\u83ab\u975e\u9152\u4e2d\u771f\u6709\u70c8\u6027\u6bd2\u836f\uff1f\u201d \u3000\u3000\u4f46\u8f6c\u53c8\u6697\u60f3\uff0c\u8fd9\u7edd\u4e0d\u53ef\u80fd\uff0c\u201c\u7965\u9e9f\u516c\u5b50\u201d\u5c1a\u4e0d\u77e5\u81ea\u5df2\u7684\u8eab\u4efd\uff0c\u4ed6\u6ca1\u6709\u5bb3\u6b7b\u81ea\u5df2\u7684\u7406\u7531\uff0c \u800c\u4e14\u4ed6\u540d\u5217\u201c\u6b66\u6797\u56db\u516c\u5b50\u201d\u4e4b\u4e00\uff0c\u5c82\u80af\u5f53\u7740\u8fd9\u591a\u6b66\u6797\u9876\u5c16\u9ad8\u624b\u9762\u524d\uff0c\u65bd\u7528\u4e0b\u6d41\u6697\u7b97\u624b\u6bb5\uff1f \u3000\u3000\u8fd9\u6837\u4e00\u60f3\uff0c\u5728\u4f17\u4eba\u7eb7\u7eb7\u5e72\u676f\u559d\u5f69\u58f0\u4e2d\uff0c\u4ed6\u53c8\u7aef\u8d77\u4e86\u7b2c\u4e8c\u676f\u9152\u9053\uff1a\u201c\u5c55\u767d\u7ecf\u5c11\u8bc6\u8584\uff0c\u521a\u624d \u5931\u8a00\uff0c\u7b2c\u4e00\u676f\u656c\u524d\u8f88\uff0c\u8fd9\u7b2c\u4e8c\u676f\u656c\u8bf8\u4f4d\u540c\u8f88\u5148\u8fdb\uff01\u201d \u3000\u3000\u201c\u8fd9\u624d\u50cf\u8bdd\uff01\u201d\u91d1\u5f69\u51e4\u5a07\u7b11\u60c5\u516e\uff0c\u8f7b\u8bed\u8f7b\u76c8\uff0c\u8fd9\u201c\u6c5f\u5357\u7b2c\u4e00\u7f8e\u4eba\u201d\u7f8e\u76ee\u6d41\u6ce2\u671b\u7740\u5c55\u767d\uff0c \u53ef\u8bf4\u662f\u98ce\u60c5\u4e07\u79cd\u3002 \u3000\u3000\u5c55\u767d\u5374\u72b9\u5982\u672a\u89c1\uff0c\u4e00\u4ef0\u8116\u5b50\u53c8\u559d\u4e0b\u7b2c\u4e8c\u676f\u9152\u3002 \u3000\u3000\u201c\u7389\u9762\u5c0f\u9752\u86a8\u201d\u770b\u5728\u773c\u91cc\uff0c\u6068\u5f97\u5fc3\u5934\u75d2\u75d2\u7684\uff0c\u771f\u60f3\u63a2\u624b\u9556\u56ca\uff0c\u4ee5\u201c\u5012\u6d12\u91d1\u94b1\u201d\u624b\u6cd5\uff0c\u628a \u5c55\u767d\u6253\u6210\u4e00\u4e2a\u7b5b\u5b50\u5e95\u3002 \u3000\u3000\u5c55\u767d\u600e\u77e5\u9053\u201c\u6c5f\u5357\u7b2c\u4e00\u7f8e\u4eba\u201d\u5a07\u58f0\u7b11\u8bed\u4e4b\u4e2d\uff0c\u5df2\u7ed9\u4ed6\u70b9\u71c3\u4e86\u4e00\u76c6\u918b\u706b\uff1f\u53ef\u662f\uff0c\u8fd9\u7b2c\u4e8c\u676f \u4e0b\u809a\uff0c\u76f4\u89c9\u5f97\u4e94\u5185\u5982\u711a\uff0c\u4e00\u822c\u70ed\u6d41\u4ece\u4e39\u7530\u76f4\u6d8c\u6ce5\u4e38\uff0c\u8bf4\u4e0d\u51fa\u7684\u4e00\u79cd\u51b2\u52a8\u548c\u6b32\u5ff5\uff0c\u6124\u7136\u5174 \u8d77\u2026\u2026 \u3000\u3000\u5c55\u767d\u60f3\u5b9a\u4e86\u201c\u7965\u9e9f\u516c\u5b50\u201d\u4e0d\u4f1a\u5f53\u4f17\u6697\u7b97\u4e8e\u4ed6\uff0c\u662f\u4ee5\u4ecd\u4e0d\u5728\u610f\uff0c\u8fd8\u4ee5\u4e3a\u81ea\u5df2\u662f\u7a7a\u8179\u5403\u9152\uff0c \u6240\u4ee5\u624d\u6709\u8fd9\u6837\u5f3a\u70c8\u7684\u53cd\u5e94\u2026\u2026 \u3000\u3000\u4f46\uff0c\u91d1\u5f69\u51e4\u5df2\u770b\u51fa\u4e86\u4e0d\u5bf9\uff0c\u5979\u4e00\u4e2a\u5973\u5b69\u5b50\u5403\u4e86\u4e24\u676f\u9152\uff0c\u8fd8\u6beb\u4e0d\u5728\u4e4e\uff0c\u56e0\u4e3a\u5979\u77e5\u9053\u8fd9\u9152\u662f \u5bb6\u4e2d\u7a96\u85cf\u7684\u4e0a\u597d\u7f8e\u9152\u201c\u5973\u513f\u7ea2\u201d\uff0c\u9152\u6027\u9187\u800c\u4e0d\u70c8\uff0c\u5c55\u767d\u4e00\u4e2a\u5927\u7537\u4eba\u5403\u4e2a\u5341\u676f\u516b\u676f\u7684\u4e5f\u4e0d\u59a8 \u4e8b\uff0c\u600e\u4e48\u4e24\u676f\u9152\u65b9\u4e0b\u809a\uff0c\u8138\u4e0a\u4fbf\u4f3c\u7ea2\u5e03\u4e00\u6837\uff0c\u800c\u4e14\u53cc\u773c\u5c04\u51fa\u5947\u5f02\u7684\u5149\u8f89\uff0c\u8eab\u5f62\u7ade\u6447\u6447\u6b32\u5012\uff0c \u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f \u3000\u3000\u201c\u54a6\u2014\u2014\u201d \u3000\u3000\u5979\u521a\u60ca\u566b\u534a\u58f0\uff0c\u8fd8\u4e0d\u53ca\u8be2\u95ee\u4f55\u56e0\uff0c\u5c55\u767d\u5df2\u7aef\u8d77\u7b2c\u4e09\u676f\u9152\uff0c\u4ef0\u8d77\u8116\u5b50\u53c8\u559d\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u201c\u597d\u9152\uff01\u201d\u5c55\u767d\u5468\u8eab\u50cf\u706b\u70ad\u4e00\u6837\uff0c\u9ad8\u70e7\u5df2\u5230\u534a\u660f\u8ff7\u72b6\u6001\uff0c\u5fc3\u4e2d\u4f3c\u5df2\u89c9\u5f97\u4e0d\u5bf9\uff0c\u9065\u7136\u4e00\u9635 \u5267\u75db\uff0c\u7acb\u5373\u77e5\u662f\u53d7\u4e86\u6697\u7b97\uff0c\u60f3\u5230\u4ee5\u201c\u7965\u9e9f\u516c\u5b50\u201d\u5728\u6b66\u6797\u4e2d\u7684\u8eab\u4efd\u5730\u4f4d\uff0c\u7adf\u7136\u5bf9\u81ea\u5df1\u65bd\u51fa\u4e86\u8fd9 \u79cd\u5351\u9119\u4e0b\u6d41\u7684\u624b\u6bb5\uff0c\u53c8\u60f3\u5230\u81ea\u5df1\u5b64\u8eab\u843d\u5728\u4ec7\u4eba\u624b\u4e2d\uff0c\u540e\u679c\u5b9e\u4e0d\u582a\u8bbe\u60f3\uff0c\u81ea\u5df1\u4e00\u6b7b\u4e00\u5207\u7b97\u5b8c\uff0c \u5c55\u6c0f\u95e8\u4e2d\u7edd\u4e86\u540e\uff0c\u6740\u7236\u4e4b\u4ec7\u518d\u4e5f\u62a5\u4e0d\u6210\u4e86\uff0c\u4e0d\u7531\u60b2\u6124\u5df2\u6781\u5730\u9a82\u9053\uff1a\u201c\u91d1\u6c0f\u95e8\u4e2d\u7684\u597d\u9152\uff01\u4e09\u676f \u4fbf\u53ef\u65ad\u80a0\uff01\u53ea\u53ef\u6068\u2026\u2026\u201d \u3000\u3000\u8bf4\u5230\u8fd9\u91cc\uff0c\u7ffb\u8eab\u683d\u5012\uff0c\u5df2\u7ecf\u662f\u4eba\u4e8b\u4e0d\u77e5\u4e86\u2026\u2026\u3002 \u7b2c\u4e8c\u5341\u4e09\u7ae0\u3000\u51e4\u6c42\u51f0>> \u53e4\u9f99\u300a\u5251\u5ba2\u884c\u300b \u7b2c\u4e8c\u5341\u4e09\u7ae0\u3000\u51e4\u6c42\u51f0 \u3000\u3000\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u5c55\u767d\u53c8\u82cf\u9192\u8fc7\u6765\uff0c\u7741\u773c\u4e00\u770b\uff0c\u81ea\u5df1\u8eba\u5728\u4e00\u4e2a\u8003\u7a76\u7684\u5e8a\u4e0a\u3002 \u3000\u3000\u53ea\u89c1\u7ee3\u88ab\u9526\u8925\uff0c\u7f57\u5e10\u91d1\u94a9\uff0c\u6e05\u5e7d\u867d\u4e0d\u53ca\u6155\u5bb9\u5e9c\u4e2d\u201c\u51cc\u98ce\u516c\u5b50\u201d\u7684\u5bdd\u5ba4\uff0c\u4f46\u8c6a\u534e\u53c8\u6709\u8fc7 \u4e4b\u3002 \u3000\u3000\u5c55\u767d\u82cf\u9192\u540e\uff0c\u53ea\u89c9\u5589\u5e72\u5982\u88c2\uff0c\u8179\u5185\u4ecd\u4f3c\u6709\u4f59\u70ec\u5728\u71c3\u70e7\uff0c\u832b\u7136\u53eb\u4e86\u4e00\u58f0\uff1a\u201c\u6c34\u2026\u2026\u201d \u3000\u3000\u4e00\u4e2a\u6e05\u79c0\u8131\u4fd7\u7684\u5c0f\u7537\u5b69\uff0c\u548c\u4e00\u4e2a\u4fca\u7f8e\u65e0\u6bd4\u7684\u5c0f\u5973\u5b69\uff0c\u6b63\u662f\u660e\u660e\u548c\u5170\u5170\uff0c\u89c1\u5c55\u767d\u9192\u6765\uff0c\u9762 \u73b0\u559c\u8272\uff0c\u8f6c\u5934\u4fbf\u5411\u5ba4\u5916\u8dd1\u53bb\uff0c\u4e00\u8fb9\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u963f\u59e8\uff01\u963f\u59e8\uff01\u4ed6\u9192\u8fc7\u6765\u4e86\uff01\u201d \u3000\u3000\u5e94\u58f0\u8d70\u8fdb\u4e00\u4e2a\u6de1\u88c5\u4e3d\u4eba\u6765\uff0c\u6b63\u662f\u201c\u6c5f\u5357\u7b2c\u4e00\u7f8e\u4eba\u201d\u91d1\u5f69\u51e4\u3002 \u3000\u3000\u4eca\u5929\uff0c\u5979\u53ea\u7a7f\u4e86\u4e00\u4ef6\u6de1\u6de1\u5982\u4e91\u7684\u7f57\u8863\uff0c\u6ee1\u5934\u7684\u9752\u4e1d\u53ea\u968f\u4fbf\u5728\u9876\u4e0a\u633d\u4e86\u4e00\u4e2a\u9b03\u513f\uff0c\u4f59\u4e0b\u7684 \u957f\u53d1\u98d8\u5760\u8eab\u540e\uff0c\u53d1\u4e0a\u6ca1\u6709\u4e00\u4ef6\u9996\u9970\uff0c\u80ce\u4e0a\u4e0d\u65bd\u4e00\u70b9\u8102\u7c89\uff0c\u4f46\u66f4\u663e\u51fa\u4e86\u5979\u5929\u9999\u56fd\u8272\u3001\u4e3e\u4e16\u65e0\u53cc \u7684\u4e3d\u8d28\uff0c\u4e0d\u6127\u8a89\u4e3a\u201c\u6c5f\u5357\u7b2c\u4e00\u7f8e\u4eba\u201d\uff0c\u66f4\u5e94\u4e86\u4e00\u53e5\u4fd7\u8bdd\uff0c\u201c\u771f\u6b63\u7684\u7f8e\u4eba\u662f\u65e0\u987b\u5316\u88c5\u7684\u201d\uff0c\u4e00 \u70b9\u4e0d\u5047\u3002 \u3000\u3000\u5979\u8d70\u8fdb\u5c4b\u6765\uff0c\u89c1\u5c55\u767d\u7741\u5f00\u773c\u775b\uff0c\u79cb\u6c34\u5982\u795e\u7684\u53cc\u773c\u4e00\u4eae\uff0c\u53cc\u988a\u68a8\u6da1\u9690\u73b0\uff0c\u6a31\u5507\u5982\u82b1\u6735\u822c\u7efd \u5f00\uff0c\u8d1d\u9f7f\u707f\u7136\uff0c\u5979\u7b11\u4e86 \u3000\u3000\u4f46\u73b0\u5728\u4ed6\u662f\u72ec\u5367\u9759\u5ba4\uff0c\u5fc3\u667a\u4e4d\u9192\uff0c\u4e00\u773c\u770b\u5230\u5982\u6b64\u7f8e\u4e3d\u7684\u91d1\u5f69\u51e4\uff0c\u4e07\u7531\u5fc3\u5e95\u6697\u8d5e\u4e86\u4e00\u58f0\uff1a \u201c\u597d\u7f8e\uff01\u2026\uff0c\uff0e\u201d \u3000\u3000\u201c\u5c55\u5c0f\u4fa0\uff01\u201d\u91d1\u5f69\u98ce\u4ece\u767e\u82b1\u76db\u5f00\u4f3c\u7684\u7b11\u5bb9\u4e2d\uff0c\u5410\u51fa\u83ba\u58f0\u6ca5\u6ca5\uff0c\u8bf4\u9053\uff1a\u4f60\u9192\u8fc7\u6765\u5566\uff01\u5c0f \u7ea2\uff0c\u5feb\u5012\u8336\u6765\uff01\u201d \u3000\u3000\u5c55\u767d\u5e72\u6e34\u96be\u5fcd\uff0c\u4f46\u672a\u7b49\u4ed6\u8bf4\u51fa\uff0c\u91d1\u5f69\u51e4\u4f3c\u5df2\u77e5\u9053\u4ed6\u9700\u8981\u7684\u662f\u4ec0\u4e48\uff0c\u7acb\u523b\u56de\u5934\u53eb\u4eba\u5012\u8336\u3002 \u7ee3\u5e18\u542f\u5904\uff0c\u4e00\u4e2a\u7c89\u8272\u8863\u88d9\u7684\u5a62\u5973\uff0c\u624b\u6258\u4e00\u676f\u9999\u8317\u8d70\u4e86\u8fdb\u6765\u3002\u5c55\u767d\u2014\u770b\uff0c\u6b63\u662f\u5728\u6d74\u5ba4\u4e2d\u62a2\u81ea\u5df1 \u7684\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\uff0c\u88ab\u81ea\u5df1\u4e00\u638c\u6253\u843d\u6d74\u6c60\u91cc\u7684\u5a62\u5973\u3002 \u3000\u3000\u4f46\u6b64\u65f6\uff0c\u4ed6\u4e0d\u987e\u5176\u4ed6\uff0c\u6293\u8fc7\u8336\u676f\u6765\uff0c\u9cb8\u996e\u800c\u5c3d\uff0c\u559d\u5b8c\u4ee5\u540e\uff0c\u5c24\u611f\u8fd8\u6e34\uff0c\u7528\u820c\u5934\u8214\u7740\u5634 \u5507\u3002\u2026\u2026 \u3000\u3000\u201c\u60f3\u4f60\u662f\u6e34\u574f\u4e86\uff01\u201d\u91d1\u5f69\u51e4\u7b11\u9053\uff1a\u201c\u5c0f\u7ea2\uff0c\u518d\u7aef\u4e00\u676f\u6765\uff01\u201d \u3000\u3000\u5979\u7f8e\u662f\u5230\u4e86\u6781\u70b9\uff0c\u806a\u660e\u4e5f\u662f\u5230\u4e86\u6781\u70b9\uff0c\u522b\u4eba\u5fc3\u4e2d\u6240\u60f3\uff0c\u4f3c\u4e4e\u65e0\u987b\u8bf4\u51fa\u6765 \u3000\u3000\u4f46\u90a3\u53eb\u201c\u5c0f\u7ea2\u201d\u7684\u7c89\u8863\u5a62\u5973\u5c1a\u672a\u56de\u8eab\uff0c\u660e\u660e\u548c\u5170\u5170\u5df2\u4ece\u623f\u5916\u63d0\u4e86\u2014\u53ea\u5b9c\u5174\u5bab\u7a91\u7ec6\u74f7\u6761\u58f6 \u8d70\u4e86\u8fdb\u6765\uff0c\u4e00\u8fb9\u5634\u4e2d\u8fd8\u53eb\u9053\uff1a\u6765\u5566\uff01\u8336\u6765\u5566\uff01\u201d \u3000\u3000\u6025\u5f97\u5c0f\u7ea2\u5fd9\u4e0a\u524d\u63a5\u4f4f\uff0c\u5e76\u57cb\u6028\u9053\uff1a\u201c\u54ce\u5440\uff01\u6211\u7684\u5c0f\u7956\u5b97\uff01\u628a\u58f6\u6253\u574f\u4e86\u8fd8\u6ca1\u6709\u5173\u7cfb\uff0c\u8981\u70eb \u4e86\u4f60\u7684\u4e24\u4e2a\u4eba\u7684\u811a\uff0c\u53ef\u4e0d\u662f\u73a9\u7684\uff01\u201d \u3000\u3000\u201c\u7ea2\u59d0\u59d0\uff0c\u4e0d\u8981\u770b\u4e0d\u8d77\u4eba\uff01\u201d\u660e\u660e\u5201\u94bb\u6c60\u9053\uff1a\u201c\u6211\u5c31\u662f\u628a\u58f6\u4e22\u51fa\uff0c\u6c34\u4e5f\u4e0d\u4f1a\u6e85\u51fa\u6765\uff01\u201d \u3000\u3000\u8bf4\u7740\u624b\u8155\u5411\u5916\u4e00\u6296\uff0c\u628a\u4e00\u53ea\u770b\u6765\u4ef7\u503c\u9887\u6602\u7684\u5b9c\u5174\u5bab\u7a91\u7ec6\u74f7\u8336\u58f6\u7adf\u51cc\u7a7a\u5411\u5c0f\u7ea2\u63b7\u6765\u3002 \u3000\u3000\u201c\u54ce\u5440\uff01\u201d\u5c0f\u7ea2\u7c89\u8138\u53d8\u8272\uff0c\u8981\u662f\u6697\u5668\u6253\u6765\uff0c\u5979\u53ef\u4ee5\u4e00\u638c\u5288\u843d\u6216\u7eb5\u8eab\u8eb2\u95ea\uff0c\u4f46\u8fd9\u662f\u2014\u53ea\u540d \u8d35\u7684\u8336\u58f6\uff0c\u4e43\u662f\u5927\u5185\u4e4b\u7269\uff0c\u5f53\u4eca\u5723\u4e0a\u6240\u8d50\uff0c\u5c24\u5176\u660e\u660e\u628a\u8336\u58f6\u51fa\u624b\uff0c\u7d2b\u91d1\u63d0\u624b\u5df2\u7136\u5012\u4e0b\uff0c\u58f6\u4e2d \u53c8\u6ee1\u6ce8\u6eda\u6c34\uff0c\u4e00\u4e2a\u63a5\u4e0d\u597d\uff0c\u4e0d\u4f46\u8981\u628a\u67d4\u5ae9\u7684\u5341\u6307\u70eb\u4f24\uff0c\u8bf4\u4e0d\u5b9a\u8fd8\u4f1a\u628a\u8336\u58f6\u6253\u7834\uff0c\u662f\u4ee5\u5979\u867d\u6709 \u4e0d\u9519\u7684\u6b66\u529f\uff0c\u4e5f\u5413\u5f97\u60ca\u53eb\u51fa\u58f0\u3002 \u3000\u3000\u6b63\u5728\u5c0f\u7ea2\u4e3e\u7740\u53cc\u624b\u3001\u4e0d\u77e5\u5982\u4f55\u662f\u597d\u4e4b\u9645\uff0c\u91d1\u5f69\u51e4\u5374\u5728\u4e00\u8fb9\u7b11\u9a7e\u9053\uff1a\u201c\u660e\u660e\uff01\u4f60\u771f\u6dd8 \u6c14\uff01\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u7ea4\u624b\u4e00\u626c\uff0c\u5e73\u98de\u6025\u5c04\u800c\u81f3\u7684\u8336\u58f6\uff0c\u88ab\u638c\u98ce\u4e00\u963b\uff0c\u7adf\u5411\u9ad8\u5347\u4e09\u5c3a\uff0c\u52bf\u5c3d\u4e0b\u843d\u4e4b \u9645\uff0c\u7d2b\u91d1\u63d0\u624b\u5411\u4e0a\u4e00\u7acb\uff0c\u5c0f\u7ea2\u8d81\u52bf\u4f38\u624b\u63d0\u4f4f\u58f6\u6881\uff0c\u6ee1\u58f6\u6eda\u6c34\u679c\u7136\u70b9\u6ef4\u672a\u6e85\u51fa\u6765\uff0c\u4f46\u5c0f\u7ea2\u5df2\u7136 \u5413\u51fa\u4e86\u4e00\u8eab\u51b7\u6c57\u3002 \u3000\u3000\u5c55\u767d\u4fa7\u5367\u5728\u5e8a\u4e0a\uff0c\u628a\u8fd9\u4e9b\u770b\u5728\u773c\u91cc\uff0c\u5fc3\u4e2d\u65e0\u9650\u611f\u6168\u3002\u770b\u8fd9\u5efa\u4e1a\u91d1\u5e9c\uff0c\u5987\u4eba\u5b7a\u5b50\u90fd\u5177\u6709\u8fd9 \u7b49\u597d\u624b\uff0c\u7684\u786e\u662f\u4e0d\u53ef\u8f7b\u89c6\u3002 \u3000\u3000\u5c55\u767d\u559d\u4e86\u5c0f\u7ea2\u5012\u7684\u8336\uff0c\u8179\u5185\u7684\u7126\u8e81\u597d\u4e86\u4e00\u4e9b\uff0c\u4f46\u56db\u80a2\u53d1\u8f6f\uff0c\u624b\u811a\u4ecd\u4e0d\u80fd\u4e3e\u52a8\u3002 \u3000\u3000\u6b63\u5728\u6b64\u65f6\uff0c\u7ee3\u5e18\u542f\u5904\uff0c\u4e00\u8fde\u4e32\u53c8\u8d70\u8fdb\u56db\u4e2a\u9752\u8863\u5c0f\u5a62\u3002 \u3000\u3000\u5176\u4e2d\u4e00\u4e2a\u9752\u8863\u5c0f\u5a62\uff0c\u5fae\u5411\u91d1\u5f69\u51e4\u4e00\u5c48\u819d\u7980\u9053\uff1a\u201c\u542f\u7980\u5c0f\u59d0\uff0c\u516c\u5b50\u9a7e\u5230\uff01\u201d \u3000\u3000\u201c\u4ed6\u7684\u6d88\u606f\uff0c\u53ef\u5012\u7075\u901a\uff01\u201d \u3000\u3000\u91d1\u5f69\u51e4\u4e00\u8bed\u672a\u6bd5\uff0c\u7965\u9e9f\u516c\u5b50\u5dfe\u5e26\u98d8\u626c\uff0c\u6b65\u5c65\u917f\u9152\uff0c\u5df2\u7136\u8e31\u4e86\u8fdb\u6765\uff0c\u8eab\u540e\u8ddf\u7740\u201c\u94c1\u80cc\u9a7c \u9f99\u201d\u4e0e\u201c\u94c1\u7ffc\u98de\u9e4f\u201d\u3002 \u3000\u3000\u201c\u5c55\u5144\uff0c\u5df2\u6e05\u9192\u4e86\uff01\u201d \u3000\u3000\u7965\u9e9f\u516c\u5b50\u4e00\u8fdb\u5c4b\u6765\uff0c\u4fbf\u8d8b\u81f3\u5e8a\u524d\uff0c\u6001\u5ea6\u5f88\u662f\u5173\u5207\u5730\u95ee\u5019\u5c55\u767d\uff0c\u4f46\u5c55\u767d\u53cc\u773c\u6545\u610f\u671b\u5411\u5e10\u9876 \u4e0d\u7406\u4e0d\u776c\u3002 \u3000\u3000\u5bf9\u5c55\u767d\u7684\u51b7\u50b2\u795e\u60c5\uff0c\u7965\u9e9f\u516c\u5b50\u6beb\u4e0d\u5728\u610f\uff0c\u4ecd\u7136\u70ed\u5ff1\u5730\u8bf4\u9053\uff1a\u201c\u5c55\u5144\u8bef\u6d74\u2018\u5bd2\u6cc9\u6c34\u2019\uff0c\u5bd2 \u95ed\u767e\u7a8d\uff0c\u53c8\u88ab\u4e09\u676f\u70ed\u9152\u4e00\u903c\uff0c\u662f\u4ee5\u660f\u7edd\u8fc7\u53bb\uff0c\u4f46\u4e0d\u8981\u7d27\uff0c\u867d\u7136\u5468\u8eab\u7b4b\u7edc\u5c3d\u6563\uff0c\u6682\u65f6\u4e0d\u80fd\u884c \u52a8\uff0c\u597d\u5728\u6211\u5bb6\u8fd8\u6709\u89e3\u5bd2\u9a71\u6bd2\u4e4b\u836f\uff0c\u4e0d\u51fa\u4e09\u65e5\uff0c\u5c55\u5144\u4fbf\u53ef\u597d\u4e86\uff0c\u8fd9\u2026\u2026\u201d \u3000\u3000\u7965\u9e9f\u516c\u5b50\u8fd8\u5f85\u8bf4\u4e0b\u53bb\uff0c\u5c55\u767d\u5374\u51b7\u51b7\u5730\u63d2\u8a00\u9053\uff1a\u201c\u5012\u662f\u5de7\u5f97\u5f88\u554a\uff01\u201d \u3000\u3000\u4ed6\u8fd9\u53e5\u8bdd\u51b0\u51b7\u8bf4\u51fa\uff0c\u523a\u4eba\u5df2\u6781\uff0c\u8fde\u7965\u9e9f\u516c\u5b50\u7d20\u4ee5\u6c89\u7a33\u89c1\u79f0\u7684\u4eba\uff0c\u4e5f\u4e0d\u7531\u4e00\u695e\u3002\u4f46\u8f6c\u53c8\u7b11 \u9053\uff1a\u201c\u4e5f\u96be\u602a\u5c55\u5144\u8d77\u7591\uff0c\u8d76\u5de7\u2018\u94c1\u80cc\u9a7c\u9f99\u2019\u516c\u5b59\u524d\u8f88\u8bf4\u4e86\u90a3\u53e5\u73a9\u7b11\u8bdd\uff0c\u5c55\u5144\u8bef\u8ba4\u4e3a\u9152\u4e2d\u6709 \u6bd2\uff0c\u7ade\u8c6a\u6c14\u5e72\u4e91\uff0c\u4ee5\u8eab\u76f8\u8bd5\uff0c\u5f53\u573a\u5012\u4e0b\uff0c\u4e0d\u8981\u8bf4\u662f\u5c55\u5144\uff0c\u5f53\u65f6\u8fde\u7965\u9e9f\u4e5f\u88ab\u8499\u4f4f\u4e86\uff0c\u540e\u6765\u542c\u5c0f \u7ea2\u8bf4\u624d\u77e5\u9053\u5c55\u5144\u4e0d\u60ef\u4e0b\u4eba\u670d\u4f8d\uff0c\u81ea\u884c\u6d74\u5ba4\u653e\u6c34\uff0c\u60f3\u662f\u628a\u6c34\u95e8\u653e\u9519\u4e86\uff0c\u8bef\u4ee5\u5bd2\u6cc9\u5f53\u6e29\u6c64\uff0c\u624d\u6709 \u8fd9\u756a\u5dee\u9519\uff01\u201d \u3000\u3000\u7965\u9e9f\u516c\u5b50\u8bf4\u5b8c\uff0c\u53c8\u54c8\u54c8\u4e00\u7b11\uff0c\u8fde\u8fde\u62b1\u6b49\u4e0d\u6b62\u3002 \u3000\u3000\u201c\u54c8\u54c8\u54c8\uff01\u201d\u94c1\u80cc\u9a7c\u9f99\u4e5f\u5728\u4e00\u8fb9\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u4e0d\u8fc7\uff0c\u8fd9\u4e00\u6765\u5012\u663e\u51fa\u5c0f\u54e5\u513f\u7684\u80c6\u91cf\u6765\u4e86\uff01 \u5f53\u771f\u662f\u89c6\u6b7b\u5982\u5f52\uff0c\u4f69\u670d\uff01\u4f69\u670d\uff01\u201d \u3000\u3000\u8fd9\u9a7c\u80cc\u8001\u4eba\uff0c\u9707\u60ca\u6b66\u6797\u7684\u6c5f\u6e56\u9ad8\u624b\uff0c\u5589\u97f3\u6d2a\u4eae\uff0c\u7b11\u58f0\u9707\u74e6\uff0c\u7fd8\u8d77\u5927\u62c7\u6307\u6765\u8d5e\u8a89\u5c55\u767d\u3002 \u3000\u3000\u201c\u94c1\u7ffc\u98de\u9e4f\u201d\u5374\u5728\u4e00\u8fb9\u51b7\u51b7\u5730\u8bf4\u9053\uff1a\u4f60\u7528\u4e0d\u7740\u5fc3\u6000\u4e0d\u8c6b\uff0c\u7965\u9e9f\u516c\u5b50\u4fa0\u9aa8\u67d4\u80a0\uff0c\u793c\u8d24\u4e0b \u58eb\uff0c\u5929\u4e0b\u5723\u540d\uff0c\u771f\u8981\u548c\u4f60\u8fc7\u4e0d\u53bb\uff0c\u4e5f\u7528\u4e0d\u5230\u5728\u9152\u91cc\u505a\u624b\u811a\uff0c\u8fd9\u5b8c\u5168\u662f\u4e2a\u5de7\u5408\uff0c\u7531\u4e0d\u5f97\u4f60\u4e0d \u4fe1\uff01\u201d \u3000\u3000\u8fd9\u201c\u94c1\u80cc\u9a7c\u9f99\u201d\u4e0e\u201c\u94c1\u7ffc\u98de\u9e4f\u201d\u5408\u79f0\u4e3a\u201c\u91d1\u5e9c\u53cc\u94c1\u536b\u201d\uff0c\u5728\u91d1\u5e9c\u5730\u4f4d\u4e4b\u9ad8\uff0c\u5728\u6c5f\u6e56\u4e0a\u58f0 \u8a89\u4e4b\u9686\uff0c\u53ef\u8bf4\u662f\u65e0\u4e0e\u4f26\u6bd4\uff0c\u4e5f\u90fd\u8fd9\u6837\u8bf4\uff0c\u60f3\u5fc5\u4e0d\u4f1a\u6709\u5047\u3002 \u3000\u3000\u4f46\u5c55\u767d\u8fd8\u662f\u4e0d\u4fe1\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u5c55\u767d\u4e0d\u60ef\u522b\u4eba\u670d\u4f8d\u662f\u5b9e\uff0c\u4f46\u6d74\u6c60\u7684\u6c34\u4e0d\u662f\u5c55\u767d\u81ea\u5df2\u653e\u5374 \u662f\u771f\u3002\u4e24\u4f4d\u8001\u524d\u8f88\u8bf4\u8bdd\u4e00\u8a00\u4e5d\u9f0e\uff0c\u66f4\u4e0d\u4f1a\u5047\uff0c\u91d1\u516c\u5b50\u53c8\u662f\u6b63\u4eba\u541b\u4e8e\uff0c\u975e\u662f\u6697\u7b97\u5bb3\u4eba\u3002\u90a3\u4e48\uff0c \u6211\u5c55\u767d\u4e00\u5b9a\u662f\u81ea\u5df1\u4e2d\u98ce\u3001\u4e2d\u6691\uff0c\u7a81\u7136\u660f\u53a5\uff1f\u8fd8\u662f\u4e0d\u80dc\u9152\u91cf\uff0c\u4e09\u676f\u9189\u5012\u4e86\uff1f\u201d \u3000\u3000\u5c55\u767d\u867d\u6ca1\u6307\u8d23\u7740\u8c01\uff0c\u4f46\u4efb\u4f55\u4eba\u4e5f\u542c\u5f97\u51fa\u4ed6\u8bdd\u4e2d\u7684\u542b\u610f\uff0c\u60f3\u7965\u9e9f\u516c\u5b50\u5728\u6b66\u6797\u4e2d\u5730\u4f4d\u4f55\u7b49\u795f \u9ad8\uff0c\u5c82\u53ef\u53d7\u5c55\u767d\u5982\u6b64\u8f7b\u8511\uff1f\u8fde\u91d1\u5f69\u51e4\u90fd\u8038\u7136\u52a8\u5bb9\uff0c\u201c\u91d1\u5e9c\u53cc\u94c1\u536b\u201d\u66f4\u662f\u6012\u5bb9\u6ee1\u9762\uff0c\u53cc\u53cc\u4fbf\u8981 \u53d1\u4f5c\u2026\u2026 \u3000\u3000\u7965\u9e9f\u516c\u5b50\u5374\u610f\u5916\u5730\u5f88\u51b7\u9759\uff0c\u5bf9\u5c55\u767d\u7684\u8fb1\u9a82\u8ba5\u8bbd\u6beb\u4e0d\u4e3a\u610f\uff0c\u53cd\u5012\u8f6c\u5934\u5411\u90a3\u53eb\u201c\u5c0f\u7ea2\u201d\u7684\u7c89 \u8863\u5a62\u5973\u8bf4\u9053\uff1a\u201c\u8c0e\u8a00\u6b3a\u4e3b\uff0c\u9a84\u50b2\u6162\u5ba2\uff0c\u4f60\u5df2\u72af\u4e86\u4e0d\u53ef\u539f\u8c05\u7684\u9519\u8bef\uff0c\u96be\u9053\u8fd8\u8981\u7b49\u6211\u8bf4\u8bdd\u5417\uff1f\u201d \u3000\u3000\u5c0f\u7ea2\u95fb\u8a00\uff0c\u9762\u8272\u5927\u53d8\uff0c\u53ea\u6014\u4e86\u4e00\u6014\uff0c\u4fbf\u65e0\u8a00\u5730\u8f6c\u8eab\u9000\u81f3\u5ba4\u5916\uff0c\u53ea\u542c\u201c\u562d\u201d\u7684\u4e00\u58f0\u5927\u54cd\uff0c \u63a5\u7740\u4f20\u6765\u91cd\u7269\u5760\u5730\u7684\u58f0\u97f3\u3002 \u3000\u3000\u4ece\u8fd9\u58f0\u97f3\u91cc\uff0c\u5c55\u767d\u5df2\u5224\u660e\u4e86\u662f\u600e\u4e48\u56de\u4e8b\uff0c\u4e0d\u7531\u5fc3\u5934\u5927\u53d7\u9707\u52a8\uff0c\u6697\u60ca\uff1a\u201c\u96be\u9053\u7965\u9e9f\u516c\u5b50\u8f7b \u8f7b\u677e\u677e\u51e0\u53e5\u8bdd\uff0c\u90a3\u7c89\u8863\u5a62\u5973\u7ade\u81ea\u7edd\u4e86\u5417\uff1f\u8fd9\u91d1\u5e9c\u7684\u5bb6\u6cd5\u4e5f\u771f\u591f\u4e25\u82db\uff01\u2026\u2026\u201d \u3000\u3000\u5728\u5c55\u767d\u770b\u6765\u89e6\u76ee\u60ca\u5fc3\uff0c\u4f46\u5ba4\u5185\u91d1\u5e9c\u4e0a\u4e0a\u4e0b\u4e0b\u8239\u4eba\u7403\u597d\u50cf\u6295\u6709\u90a3\u4e48\u56de\u4e8b\u4e00\u822c\uff0c\u795e\u8272\u81ea\u82e5\u3002 \u3000\u3000\u7965\u9e9f\u516c\u5b50\u5374\u7ad9\u8d77\u8eab\u6765\u8bf4\u9053\uff1a\u201c\u5c55\u5144\u597d\u597d\u517b\u606f\u5427\uff0c\u4e09\u65e5\u4fdd\u8bc1\u4f60\u75ca\u6108\uff01\u201d \u3000\u3000\u8bf4\u7f62\uff0c\u5934\u4e5f\u4e0d\u56de\u7684\u5e26\u7740\u201c\u91d1\u5e9c\u53cc\u94c1\u536b\u201d\u53ca\u56db\u540d\u9752\u8863\u5a62\u5973\u8d70\u51fa\u5c45\u53bb\u3002 \u3000\u3000\u660e\u660e\u548c\u5170\u5170\u5df2\u4e0d\u77e5\u4f55\u65f6\uff0c\u65e9\u8dd1\u5230\u5916\u8fb9\u73a9\u53bb\u4e86\u3002 \u3000\u3000\u5ba4\u5185\u4ec5\u7559\u4e0b\u91d1\u5f69\u51e4\u4e0e\u5c55\u767d\u4e8c\u4eba\u3002 \u3000\u3000\u91d1\u5f69\u51e4\u671b\u7740\u5c55\u767d\uff0c\u5e7d\u5e7d\u5730\u8bf4\u9053\uff1a\u201c\u6211\u54e5\u54e5\u8fd9\u6837\u505a\uff0c\u5bf9\u522b\u4eba\u800c\u8a00\uff0c\u53ef\u8bf4\u662f\u83ab\u5927\u7684\u8d4f\u8138\u3002\u4f46 \u5bf9\u4f60\u6765\u8bf4\uff0c\u5374\u662f\u9002\u5f97\u5176\u53cd\uff0c\u66f4\u5f15\u8d77\u4e86\u4f60\u7684\u53cd\u611f\uff01\u201d \u3000\u3000\u201c\u8bf7\u9053\u5176\u8be6\uff01\u201d\u5c55\u767d\u539f\u4ec7\u6068\u91d1\u5e9c\u7684\u4eba\uff0c\u5982\u4eca\u89c1\u5230\u8fd9\u6b8b\u9177\u4e3a\u4e00\u5e55\uff0c\u4ec7\u6068\u91cc\u66f4\u52a0\u4e0a\u538c\u6076\uff1b\u7eb5 \u7136\u5bf9\u7740\u6c5f\u5357\u7edd\u8272\u7f8e\u4eba\uff0c\u5df2\u5f15\u4e0d\u8d77\u4ed6\u4e00\u4e1d\u7eee\u5ff5\uff0c\u56e0\u6b64\u795e\u60c5\u4e0e\u8bed\u8c03\u4e00\u6837\u7684\u51b0\u51b7\u3002 \u3000\u3000\u201c\u5c0f\u7ea2\u5728\u6211\u5bb6\u4e2d\u867d\u662f\u4e00\u4e2a\u5a62\u5973\uff0c\u4f46\u5730\u4f4d\u5e76\u4e0d\u4f4e\uff0c\u53ea\u56e0\u4e3a\u5979\u7b80\u6162\u5ba2\u4eba\uff0c\u6211\u54e5\u54e5\u4fbf\u8d50\u5979\u4e00 \u6b7b\uff0c\u8fd9\u8981\u5728\u4e00\u822c\u6c5f\u6e56\u8c6a\u5ba2\u6765\u8bf4\uff0c\u8fd8\u4e0d\u662f\u6781\u7aef\u7684\u8d4f\u8138\u5417\uff1f\u4e00\u5b9a\u8981\u60f6\u6050\u4e07\u5206\uff0c\u611f\u6fc0\u83ab\u540d\uff0c\u8ba4\u6211\u54e5 \u54e5\u4e3a\u793c\u8d24\u4e0b\u58eb\u7684\u77e5\u5df1\uff0c\u6b7b\u5fc3\u584c\u5730\u5730\u62a5\u6548\u6211\u5bb6\u4e86\u3002\u53ef\u662f\uff0c\u4f60\u4e0d\u540c\u2026\u2026\u201d \u3000\u3000\u91d1\u5f69\u51e4\u8fd8\u5f85\u8bf4\u4e0b\u53bb\uff0c\u5c55\u767d\u51b7\u7b11\u4e00\u58f0\u9053\uff1a\u201c\u6211\u4e0d\u662f\u6beb\u65e0\u4eba\u6027\uff0c\u4ee5\u4ed6\u4eba\u7684\u751f\u547d\uff0c\u5411\u6c5f\u6e56\u4eba\u7269 \u4e70\u597d\u3002\u4f60\u54e5\u54e5\u4ee5\u522b\u4eba\u7684\u751f\u547d\uff0c\u6765\u5411\u6211\u4e70\u597d\uff0c\u5e76\u5e0c\u671b\u6211\u611f\u6069\u56fe\u62a5\uff0c\u90a3\u66f4\u662f\u5984\u60f3\uff0c\u53cd\u9002\u8db3\u5f15\u8d77\u6211 \u7684\u53cd\u611f\u3002\u201d\u91d1\u5f69\u51e4\u9053\uff1a\u201c\u53ef\u662f\uff0c\u6211\u54e5\u54e5\u5904\u4e8b\u516c\u6b63\uff0c\u5b8c\u5168\u5c31\u4e8b\u8bba\u4e8b\uff0c\u5e76\u6ca1\u6709\u6210\u5fc3\u5411\u4f60\u4e70\u597d\u3002\u5c0f \u7ea2\u8c0e\u8a00\u6b3a\u4e3b\uff0c\u7f6a\u4e0d\u5bb9\u8d66\u3002\u5979\u7b80\u6162\u5ba2\u4eba\uff0c\u6697\u7b97\u5ba2\u4eba\uff0c\u662f\u4e3a\u4e0d\u656c\u3002\u4e0d\u5fe0\u4e0d\u656c\u4e4b\u4e8b\u88ab\u63ed\u9732\uff0c\u8138\u4e0a\u6302 \u4e0d\u4f4f\uff0c\u81ea\u6740\u8eab\u6b7b\uff0c\u7b97\u662f\u4e00I\u8d4e\u524d\u884d\u3002\u800c\u4e14\u5979\u81ea\u5df1\u4e00\u6b7b\uff0c\u4e5f\u8868\u73b0\u4e86\u5979\u613f\u610f\u4ee5\u6b7b\u6d17\u5237\u5979\u81ea\u8eab\u7684 \u6c61\uff0c\u70b9\uff0c\u4e8e\u5404\u65b9\u9762\u6765\u8bb2\uff0c\u5e76\u6ca1\u6709\u4ec0\u4e48\u4e0d\u5bf9\u3002\u4f55\u51b5\uff0c\u4e5f\u4e0d\u662f\u6211\u54e5\u54e5\u7684\u9519\uff0c[\u4f60\u4e3a\u4ec0\u4e48\u8fd9\u822c\u4ec7\u89c6 \u6211\u54e5\u54e5\u5462\uff1f\u9664\u975e\u53e6\u6709\u539f\u56e0\uff0c\u4e0d\u7136\u90a3\u5c31\u5f88\u8d39\u4eba\u731c\u89e3\u4e86\uff01\u201d \u3000\u3000\u91d1\u5f69\u98ce\u5206\u6790\u4e8b\u7406\uff0c\u89c2\u5bdf\u5165\u5fae\uff0c\u5c55\u767d\u4e0d\u7531\u5fc3\u6298\uff0c\u4e0d\u613f\u518d\u8ddf\u5979\u591a\u6de1\uff0c\u6df1\u6015\u518d\u8c08\u4e0b\u53bb\uff0c\u5bf9\u65b9\u7684 \u5185\u60c5\u6ca1\u6709\u6478\u6e05\uff0c\u81ea\u5df1\u53cd\u800c\u5148\u6cc4\u4e86\u5e95\uff0c\u5bf9I\u81ea\u5df1\u4ee5\u540e\u62a5\u4ec7\u7684\u4e8b\u4e0d\u5927\u65b9\u4fbf\uff0c\u4f46\u5ffd\u7136\u8111\u5b50\u4e00\u52a8\uff0c\u5fcd \u4e0d\u4f4f\u53c8\u95ee\u9053\uff1a1\u201c\u90a3\u2018\u5730\u5fc3\u5bd2\u6cc9\u6c34\u2019\u65e2\u7136\u5177\u6709\u5bd2\u6bd2\uff0c\u4e3a\u4ec0\u4e48\u8981\u5f15\u5230\u6d74\u5ba4\u53bb\u5462\uff1f\u83ab\u4e0d\uff0c \u662f\xb7\uff0e\uff0e\uff0e\uff0e\uff0e\uff1a\u2019 \u3000\u3000\u91d1\u5f69\u51e4\u5a49\u7136\u4e00\u7b11\u9053\uff1a\u201c\u5efa\u4e1a\u91d1\u5e9c\u8981\u60f3\u8bbe\u673a\u5173\u5bb3\u4eba\uff0c\u4e5f\u7528\u4e0d\u5230\u653e\u5728\u5bb6\u4e2d\u6d74\u5ba4\u91cc\uff0c\u90a3\u662f\u6211\u7236 \u4eb2\u5f15\u6765\u505a\u4e3a\u7ec3\u529f\u7528\u7684\uff01\u201d \u3000\u3000\u201c\u4f60\u7236\u4eb2\uff01\u201d\u5c55\u767d\u7741\u5927\u4e86\u773c\u775b\u95ee\u9053\uff1a\u600e\u4e48\u4e00\u76f4\u6ca1\u6709\u89c1\u5230\u4f60\u7236\u4eb2\uff0c\u96be\u9053\u4f60\u7236\u4eb2\u4e0d\u4f4f\u5728\u5bb6\u4e2d \u5417\uff1f\u201d \u3000\u3000\u201c\u6211\u7236\u4eb2\u5c31\u4f4f\u5728\u5bb6\u4e2d\uff01\u201d\u91d1\u5f69\u51e4\u5fae\u73b0\u8be7\u5f02\u5730\u8bf4\u9053\uff1a\u4e0d\u8fc7\uff0c\u4ed6\u8001\u4eba\u5bb6\u4e0d\u5229\u4e8e\u884c\uff0c\u4e0d\u80fd\u51fa\u6765 \u89c1\u5ba2\u7f62\u4e86\uff01\u201d \u3000\u3000\u201c\u4ed6\u4f4f\u5728\u4ec0\u4e48\u5730\u65b9\uff1f\u201d\u5c55\u767d\u8fd9\u8bdd\u95ee\u5f97\u8fc7\u6025\u4e86\u4e9b\u3002 \u3000\u3000\u201c\u600e\u4e48\uff1f\u201d\u91d1\u5f69\u51e4\u66f4\u89c1\u60ca\u5947\u9053\uff1a\u201c\u5c55\u5c0f\u4fa0\u8ba4\u8bc6\u6211\u7236\u4eb2\uff1f\u201d \u3000\u3000\u5c55\u767d\u4e00\u58f0\u60e8\u7b11\u9053\uff1a\u201c\u9752\u86a8\u795e\u91d1\u4e5d\uff0c\u6c5f\u6e56\u4e0a\u6709\u51e0\u4eba\u4e0d\u77e5\u4ee4\u5c0a\u7684\u5927\u540d\uff01\u201d \u3000\u3000\u91d1\u5f69\u51e4\u4e5f\u7b11\u4e86\u9053\uff1a\u201c\u53ea\u95fb\u540d\u672a\u89c1\u9762\uff1f\u201d \u3000\u3000\u5c55\u767d\u70b9\u4e86\u70b9\u5934\uff0c\u7b97\u662f\u9ed8\u8ba4\u3002 \u3000\u3000\u91d1\u5f69\u51e4\u9053\uff1a\u201c\u90a3\u8fd8\u5dee\u4e0d\u591a\uff0c\u6211\u7236\u4eb2\u5df2\u6709\u51e0\u5341\u5e74\u6ca1\u6709\u51fa\u5916\u8d70\u52a8\u8fc7\u4e86\uff0c\u4f9d\u4f60\u7684\u5e74\u7eaa\uff0c\u4e0d\u53ef\u80fd \u89c1\u8fc7\u6211\u7236\u4eb2\uff01\u201d \u3000\u3000\u5c55\u767d\u8131\u53e3\u95ee\u9053\uff1a\u201c\u90a3\u662f\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u91d1\u5f69\u51e4\u773c\u4e2d\u5c04\u51fa\u7591\u95ee\u7684\u5149\u8292\u8be7\u9053\uff1a\u201c\u5c55\u5c0f\u4fa0\uff0c\u4f60\u597d\u50cf\u5bf9\u6211\u7684\u7236\u4eb2\u5f88\u5173\u5fc3\uff1f\u201d \u3000\u3000\u5c55\u767d\u8138\u4e00\u7ea2\uff0c\u77e5\u9053\u81ea\u5df1\u95ee\u5f97\u592a\u9732\u9aa8\uff0c\u5df2\u5f15\u8d77\u91d1\u5f69\u98ce\u7684\u7591\u5fc3\uff0c\u968f\u6447\u4e86\u6447\u5934\u9053\uff1a\u201c\u6211\u4e0d\u8fc7\u53ea \u662f\u597d\u5947\uff0c\u4ee5\u4ee4\u5c0a\u5728\u6c5f\u6e56\u4e0a\u7684\u5a01\u671b\uff0c\u4e0d\u77e5\u4e3a\u4ec0\u4e48\u7adf\u5446\u5728\u5bb6\u4e2d\u5341\u51e0\u5e74\u4e0d\u5c65\u6c5f\u6e56\uff1f\u201d \u3000\u3000\u91d1\u5f69\u51e4\u679c\u7136\u4e0d\u518d\u8d77\u7591\uff0c\u7f8e\u4e3d\u7684\u8138\u4e0a\u6362\u4e86\u4e00\u79cd\u9eef\u6de1\u7684\u795e\u60c5\u8bf4\u9053\uff1a\u201c\u6211\u7236\u4eb2\u5341\u51e0\u5e74\u524d\u5f97\u4e86\u4e00 \u573a\u602a\u75c5\uff0c\u53cc\u817f\u4e0d\u80fd\u884c\u8d70\uff0c\u6545\u6b64\u624d\u6ca1\u6709\u51fa\u8fc7\u95e8\uff0c\u53ea\u5728\u540e\u9662\u2018\u6021\u60c5\u9662\u2019\u517b\u606f\u3002\u201d \u3000\u3000\u5c55\u767d\u604d\u7136\u800c\u609f\uff0c\u5fc3\u4e2d\u5df2\u6697\u6697\u6709\u4e86\u51b3\u5b9a\uff0c\u968f\u5373\u4e0d\u518d\u591a\u95ee\u3002 \u3000\u3000\u4ece\u6b64\uff0c\u5c55\u767d\u5728\u91d1\u5e9c\u517b\u4f24\uff0c\u91d1\u5f69\u51e4\u5e38\u5e38\u5230\u623f\u4e2d\u770b\u987e\u4ed6\uff0c\u201c\u7965\u9e9f\u516c\u5b50\u201d\u56e0\u4e3a\u65f6\u6709\u6c5f\u6e56\u8c6a\u5ba2\u9020 \u8bbf\uff0c\u5012\u5f88\u5c11\u6765\u3002\u8f6c\u773c\u4e09\u5929\u8fc7\u53bb\uff0c\u8fd9\u662f\u7b2c\u4e09\u5929\u7684\u508d\u665a\u4e86\uff0c\u5c55\u767d\u4f24\u52bf\u5df2\u5b8c\u5168\u590d\u5eb7\uff0c\u5373\u65f6\u5c31\u544a\u8f9e\u6b32 \u8d70\uff0c\u6070\u5de7\u201c\u7965\u9e9f\u516c\u5b50\u201d\u672a\u5728\u5bb6\u4e2d\uff0c\u91d1\u5f69\u51e4\u767e\u822c\u633d\u7559\u4ed6\uff0c\u4f46\u5c55\u767d\u51b3\u5b9a\u544a\u8f9e\u3002 \u3000\u3000\u4ece\u91d1\u5f69\u51e4\u90a3\u604b\u604b\u4e0d\u820d\u7684\u795e\u60c5\u4e0a\u770b\u6765\uff0c\u65e0\u7591\u77ed\u77ed\u4e24\u65e5\u76f8\u805a\uff0c\u8fd9\u6709\u7740\u201c\u6c5f\u5357\u7b2c\u4e00\u7f8e\u4eba\u201d\u4e4b\u79f0 \u7684\u7edd\u8272\u4f73\u4e3d\u91d1\u5f69\u51e4\uff0c\u82b3\u5fc3\u5df2\u6697\u6697\u7231\u4e0a\u5c55\u767d\u4e86\u3002 \u3000\u3000\u4f46\u5c55\u767d\u5bf9\u8fd9\u4eba\u4eba\u7fa1\u6155\u5411\u5f80\u7684\u7edd\u8272\u4f73\u4eba\uff0c\u5374\u4e00\u70b9\u4e5f\u6ca1\u6709\u653e\u5728\u5fc3\u4e0a\uff0c\u91d1\u5f69\u51e4\u7684\u8f6f\u8bed\u6e29\u5b58\uff0c\u4ee5 \u53ca\u5bf9\u4ed6\u7684\u4e07\u7f15\u67d4\u60c5\uff0c\u4ed6\u8fde\u770b\u90fd\u61d2\u5f97\u770b\u3002 \u3000\u3000\u53ef\u662f\u5c55\u767d\u6108\u662f\u8fd9\u6837\u6f20\u7136\u65e0\u52a8\u4e8e\u8877\uff0c\u91d1\u5f69\u51e4\u6108\u89c9\u5f97\u4ed6\u4e0e\u4f17\u4e0d\u540c\uff0c\u7231\u4ed6\u7684\u5fc3\u66f4\u5207\u4e86\u3002 \u3000\u3000\u201c\u597d\uff0c\u5c31\u6025\u7740\u8981\u8d70\u5462\uff1f\u201d\u91d1\u5f69\u51e4\u53cc\u773c\u8ff7\u60d1\u5730\u671b\u7740\u5c55\u767d\u9053\uff1a\u96be\u9053\u4e0d\u80fd\u5728\u6211\u5bb6\u591a\u4f4f\u51e0\u5929 \u5417\uff1f\u201d \u3000\u3000\u201c\u4e0d\u884c\uff01\u201d\u5c55\u767d\u65a9\u91d1\u65ad\u94c1\u5730\u8bf4\uff1a\u201c\u56e0\u4e3a\u6211\u6709\u6025\u4e8b\uff01\u201d \u3000\u3000\u201c\u60f3\u662f\u6211\u5bb6\u4e0d\u597d\u6216\u2026\u2026\u201d\u91d1\u5f69\u51e4\u6ceb\u7136\u6b32\u6ce3\u3002 \u3000\u3000\u201c\u6211\u5012\u6ca1\u6709\u90a3\u4e48\u8bf4\u3002\u201d\u5c55\u767d\u9053\uff1a\u201c\u4e0d\u8fc7\u6211\u4e0d\u80fd\u591a\u5728\u8d35\u5e9c\u803d\u6401\u7f62\u4e86\u3002\u201d\u8bed\u8c03\u4ecd\u662f\u51b7\u51b0\u51b0 \u7684\u3002 \u3000\u3000\u201c\u96be\u9053\u591a\u4f4f\u4e00\u591c\u4e5f\u4e0d\u884c\u5417\uff1f\u2026\u2026\u201d\u91d1\u5f69\u51e4\u8bf4\u81f3\u6b64\u5904\uff0c\u4e00\u53cc\u660e\u5a9a\u7684\u5927\u773c\u775b\u91cc\uff0c\u5df2\u7ecf\u6d8c\u51fa\u4e86 \u660e\u4eae\u7684\u6cea\u73e0\u3002 \u3000\u3000\u5c55\u767d\u89c1\u5979\u82b1\u5bb9\u9eef\u7136\uff0c\u660e\u7738\u542b\u6cea\u201d\u663e\u5f97\u5206\u5916\u660e\u5a9a\u52a8\u4eba\uff0c\u4e0d\u7531\u5fc3\u5185\u4e00\u8361\uff0c\u53f9\u4e86\u4e00\u58f0\u9053\uff1a \u201c\u5509\uff01\u8fd9\u2026\u2026\u8fd9\u662f\u4e0d\u53ef\u80fd\u7684\uff01 \u3000\u3000\u8fd9\u5239\u90a3\u4e4b\u95f4\uff0c\u4ed6\u660e\u767d\u4e86\uff0c\u4ed6\u5e76\u4e0d\u662f\u4e0d\u7231\u8fd9\u201c\u6c5f\u5357\u7b2c\u4e00\u7f8e\u4eba\u201d\uff0c\u7f8e\u8272\u4eba\u4eba\u7231\uff0c\u4f55\u51b5\uff0c\u8fd9\u7f8e \u4eba\u5bf9\u4ed6\u8fd8\u662f\u60c5\u6709\u72ec\u949f\u5462\uff01\u4f46\u662f\u57cb\u5728\u5fc3\u91cc\u7684\u4ec7\u6068\uff0c\u4f7f\u4ed6\u4e0d\u80fd\u7231\u5979\u3002 \u3000\u3000\u56e0\u6b64\uff0c\u5728\u4e07\u611f\u4ea4\u96c6\u4e4b\u4e2d\uff0c\u8ff8\u51fa\u4e86\u8fd9\u4e48\u4e00\u53e5\u65e0\u5934\u65e0\u5c3e\u7684\u8bdd\u3002 \u3000\u3000\u8bf4\u5b8c\u4e4b\u540e\u4ed6\u518d\u4e5f\u4e0d\u56de\u5934\uff0c\u80cc\u4e0a\u201c\u65e0\u60c5\u78a7\u5251\u201d\uff0c\u5927\u6b65\u5411\u95e8\u5916\u8d70\u53bb\u3002 \u3000\u3000\u56e0\u4e3a\uff0c\u4ed6\u5fc3\u91cc\u660e\u767d\uff0c\u5982\u679c\u518d\u7a0d\u4e00\u5ef6\u7559\uff0c\u6050\u6015\u771f\u72e0\u4e0d\u4e0b\u5fc3\u80a0\u8d70\u51fa\u91d1\u5e9c\u3002\u4e07\u4e00\u81ea\u5df1\u63a7\u5236\u4e0d\u4f4f \u611f\u60c5\uff0c\u7231\u4e0a\u4e86\u6740\u7236\u4ec7\u4eba\u7684\u5973\u513f\uff0c\u90a3\u81ea\u5df2\u5c06\u4f55\u4ee5\u81ea\u5904\uff1f\u62a5\u4ec7\u65e2\u4e0d\u80fd\uff0c\u4e0d\u62a5\u4ec7\u826f\u5fc3\u96be\u5b89\u2026\u2026\u90a3\u7ed3 \u679c\u662f\u4e0d\u582a\u8bbe\u60f3\u7684\u3002 \u3000\u3000\u4f46\u5c55\u767d\u65b9\u5b9a\u4e86\u4e24\u6b65\uff0c\u91d1\u5f69\u51e4\u7a81\u7136\u4e00\u628a\u62c9\u4f4f\u4ed6\u7684\u624b\u81c2\uff0c\u60b2\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u518d\u7a0d\u7b49\u4e00\u4f1a\uff0c\u542c\u6211 \u8bf4\u4e24\u53e5\u6210\u4e0d\u6210\uff1f\u2026\u2026\u201d \u3000\u3000\u5c1a\u672a\u7b49\u5c55\u767d\u7b54\u8a00\uff0c\u7a81\u7136\u8863\u6289\u98d8\u98ce\uff0c\u4ece\u7a97\u5916\u5c04\u8fdb\u4e00\u6761\u4eba\u5f71\uff01 \u3000\u3000\u4eba\u5f71\u843d\u5730\uff0c\u5c55\u767d\u4e0e\u91d1\u5f69\u98ce\u540c\u65f6\u770b\u6e05\uff0c\u6765\u4eba\u6b63\u662f\u201c\u9752\u86a8\u795e\u201d\u7684\u7231\u5f92\u3001\u4e0e\u91d1\u6c0f\u5144\u59b9\u4e00\u8d77\u957f\u5927 \u7684\u201c\u7389\u9762\u5c0f\u9752\u86a8\u201d\u5b5f\u5982\u840d\u3002 \u3000\u3000\u201c\u7389\u9762\u5c0f\u9752\u86a8\u201d\u4e00\u8eab\u9ed1\u8272\u52b2\u88c5\uff0c\u4fca\u8138\u6cdb\u81ea\uff0c\u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u5e08\u59b9\uff0c\u653e\u4ed6\u8d70\uff01\u201d \u3000\u3000\u201c\u8c01\u8981\u4f60\u6765\u591a\u7ba1\u95f2\u4e8b\uff01\u201d\u91d1\u5f69\u51e4\u6ee1\u8138\u4e0d\u9ad8\u5174\u5730\u9053\uff1a\u201c\u5feb\u7ed9\u6211\u6eda\u5f00\uff01\u201d \u3000\u3000\u201c\u7389\u9762\u5c0f\u9752\u86a8\u201d\u4e00\u695e\uff0c\u4ed6\u60f3\u4e0d\u5230\u4ece\u5c0f\u4e0e\u81ea\u5df1\u4e00\u5757\u957f\u5927\u7684\u5c0f\u5e08\u59b9\uff0c\u5bf9\u81ea\u5df2\u8bf4\u8bdd\u7ade\u5982\u6b64\u4e0d\u5ba2 \u6c14\u3002\u4f46\u4e5f\u66f4\u6fc0\u8d77\u4e86\u4ed6\u7684\u6012\u706b\uff0c\u6076\u72e0\u72e0\u5730\u8bf4\u9053\uff1a\u201c\u5e08\u54e5\u4e0d\u5728\u5bb6\uff0c\u6211\u4e0d\u7ba1\u8c01\u6765\u7ba1\u4f60\uff0c\u96be\u9053\u8ba9\u4f60\u628a \u91d1\u5bb6\u7684\u8138\u90fd\u7ed9\u4e22\u5149\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u556a\uff01\u201d\u4e00\u58f0\u8106\u54cd\uff0c\u91d1\u5f69\u51e4\u6068\u6068\u5730\u6253\u4e86\u201c\u7389\u9762\u5c0f\u9752\u86a8\u201d\u4e00\u8bb0\u8033\u5149\uff0c\u7389\u5bb9\u6c14\u5f97\u715e\u767d\uff0c\u9053\uff1a \u201c\u6211\u6709\u4ec0\u4e48\u4e22\u8138\uff1f\u54e5\u54e5\u4e5f\u4e0d\u6562\u8fd9\u6837\u9a82\u6211\uff01\u2026\u2026\u201d \u3000\u3000\u201c\u7389\u9762\u5c0f\u9752\u86a8\u201d\u4e0d\u9632\u91d1\u5f69\u51e4\u6562\u6253\u4ed6\uff0c\u5f53\u65f6\u88ab\u6253\u5f97\u4e00\u695e\uff0c\u5f85\u4ed6\u56de\u5473\u8fc7\u6765\uff0c\u715e\u767d\u7684\u7389\u9762\u4e0a\u5df2 \u7ea2\u80bf\u4e86\u4e94\u4e2a\u624b\u6307\u5370\uff0c\u4e0d\u7531\u8138\u8272\u6c14\u5f97\u94c1\u9752\uff0c\u72e0\u72e0\u5730\u77aa\u4e86\u91d1\u5f69\u51e4\u51e0\u773c\uff0c\u7136\u540e\u5411\u5c55\u767d\u8bf4\u9053\uff1a\u201c\u81ed\u5c0f \u5b50\uff01\u4eca\u591c\u4f60\u8981\u4e0d\u79bb\u5f00\u91d1\u5e9c\uff0c\u5c0f\u592a\u7237\u53eb\u4f60\u6b7b\u65e0\u846c\u8eab\u4e4b\u5730\uff01\u201d \u3000\u3000\u8bf4\u7f62\uff0c\u4e00\u8dfa\u811a\u55d6\u7684\u4e00\u58f0\uff0c\u7a9c\u51fa\u4e86\u95e8\u3002 \u3000\u3000\u201c\u8fd9\u6837\u8bf4\u6765\uff0c\u201d\u5c55\u767d\u51b7\u7b11\u4e00\u58f0\u8bf4\u9053\uff1a\u5c55\u767d\u5012\u975e\u8981\u591a\u5728\u8d35\u5e9c\u4f4f\u4e00\u591c\u4e0d\u53ef\u4e86\uff01\u201d \u3000\u3000\u4f46\u201c\u7389\u9762\u5c0f\u9752\u86a8\u201d\u5df2\u8d70\u5f97\u6ca1\u4e86\u8e2a\u5f71\u3002 \u3000\u3000\u91d1\u5f69\u51e4\u5374\u5728\u4e00\u8fb9\u9053\uff1a\u5c55\u5c0f\u4fa0\uff0c\u4f60\u653e\u5fc3\u597d\u4e86\uff01\u6709\u6211\u5728\u6b64\uff0c\u4ed6\u4e0d\u6562\u52a8\u4f60\u4e00\u6839\u6c57\u6bdb\uff01\u201d \u3000\u3000\u5c55\u81ea\u563f\u563f\u51b7\u7b11\u9053\uff1a\u201c\u5728\u4e0b\u8fd8\u4e0d\u613f\u6cbe\u5c0f\u59d0\u7684\u5149\uff0c\u81ea\u4fe1\u8fd8\u53ef\u4ee5\u5e94\u4ed8\u5f97\u4e86I\u201d \u3000\u3000\u91d1\u5f69\u51e4\u4e00\u53cc\u51fa\u5947\u7f8e\u4e3d\u7684\u5927\u773c\u775b\uff0c\u597d\u597d\u5730\u671b\u4e86\u5c55\u767d\u4e00\u4f1a\uff0c\u624d\u6447\u5934\u53f9\u9053\uff1a\u201c\u6211\u91d1\u5f69\u51e4\u4e0d\u662f\u81ea \u5938\uff0c\u89c1\u5230\u6211\u7684\u4eba\uff0c\u6ca1\u6709\u4e00\u4e2a\u4e0d\u8bf4\u6211\u751f\u5f97\u7f8e\u7684\u3002\u5411\u6211\u732e\u6bb7\u52e4\u3001\u60f3\u63a5\u8fd1\u6211\u7684\u9752\u5e74\u7537\u5b50\uff0c\u66f4\u4e0d\u77e5\u6709 \u591a\u5c11\u3002\u4f46\u6211\u4ece\u6ca1\u6709\u7231\u8fc7\u4efb\u4f55\u4eba\uff0c\u81ea\u4ece\u89c1\u4e86\u5c55\u5c0f\u4fa0\uff0c\u4e0d\u77e5\u600e\u4e48\uff0c\u6211\u2026\u2026\u201d \u3000\u3000\u91d1\u5f69\u51e4\u7eb5\u7136\u662f\u6b66\u6797\u4e16\u5bb6\uff0c\u6c5f\u6e56\u513f\u5973\uff0c\u8c6a\u723d\u5927\u65b9\uff0c\u4e0d\u540c\u4e8e\u4e00\u822c\u95fa\u9601\u5343\u91d1\uff0c\u4f46\u8bf4\u81f3\u6b64\u5904\u4e5f\u4e0d \u7981\u7c89\u9762\u901a\u7ea2\uff0c\u4f4f\u53e3\u8bf4\u4e0d\u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u5c55\u767d\u89c1\u5979\u4e0d\u80dc\u5a07\u7f9e\uff0c\u82b1\u5bb9\u5982\u7389\uff0c\u7684\u786e\u662f\u81ea\u5df2\u751f\u5e73\u6240\u89c1\u7684\u7f8e\u5973\u4e4b\u4e2d\u6700\u7f8e\u7684\u7f8e\u4eba\uff0c\u5c24\u5176\u96be\u5f97 \u7684\u662f\uff0c\u5979\u5bf9\u81ea\u5df1\u7ade\u60c5\u6709\u72ec\u949f\uff0c\u771f\u53ef\u8bf4\u662f\u4e07\u91d1\u51c6\u4e70\u3001\u53ef\u9047\u800c\u4e0d\u53ef\u6c42\u7684\u4f73\u8fd0\uff01\u4ed6\u53c8\u8bb0\u8d77\u5728\u9556\u5c40\u505a \u4e8b\u65f6\uff0c\u4e00\u4e2a\u5e74\u8001\u9556\u5e08\u8bf4\u8fc7\u7684\u4e00\u756a\u8bdd\u201c\u4eba\u751f\u5728\u4e16\uff0c\u4ea4\u901a\u5929\u4e0b\u53cb\uff0c\u96be\u5f97\u4e00\u4e2a\u77e5\u5df2\u3002\u77e5\u5df1\u96be\u6c42\uff0c\u4f46 \u7ea2\u7c89\u77e5\u5df1\u66f4\u96be\u6c42\uff0c\u5047\u5982\u6709\u4e00\u4e2a\u7edd\u8272\u7f8e\u4eba\uff0c\u8bb8\u4e3a\u77e5\u5df1\uff0c\u90a3\u4e0d\u77e5\u8981\u51e0\u4e16\u6e05\u4fee\uff0c\u624d\u80fd\u5f97\u6765\uff01\u201d \u3000\u3000\u53ef\u662f\uff0c\u5982\u4eca\u5c55\u767d\u9047\u5230\u4e86\u7ea2\u7c89\u77e5\u5df2\uff0c\u4f46\u5979\u5374\u662f\u81ea\u5df1\u6740\u7236\u4ec7\u4eba\u7684\u5973\u513f\uff0c\u4f7f\u81ea\u5df1\u4e0d\u80fd\u63a5\u53d7\u5979\u7684 \u7231\u60c5\uff0c\u771f\u53ef\u8bf4\u662f\u9020\u5316\u5f04\u4eba\u4e86\u3002 \u3000\u3000\u5c55\u767d\u5fc3\u5ff5\u7535\u8f6c\uff0c\u601d\u524d\u60f3\u540e\uff0c\u4e00\u65f6\u695e\u4e86\uff0c\u7adf\u4e0d\u77e5\u8bf4\u4e9b\u4ec0\u4e48\u624d\u597d\uff1f\u2026\u2026 \u3000\u3000\u6b63\u5728\u4e24\u4eba\u4e00\u4e2a\u542b\u7f9e\u4e0d\u8bed\uff0c\u4e00\u4e2a\u611f\u6168\u65e0\u8a00\uff0c\u9ed8\u9ed8\u76f8\u5bf9\u7684\u5f53\u513f\uff0c\u7a81\u542c\u95e8\u5916\u54c8\u54c8\u4e00\u9635\u5927\u7b11\uff0c\u58f0 \u9707\u7a97\u68c2\u3002 \u3000\u3000\u53ea\u542c\u4e00\u4e2a\u8f70\u96f7\u4f3c\u7684\u55d3\u95e8\u8bf4\u9053\uff1a\u201c\u8d24\u4f84\u5973\uff0c\u542c\u8bf4\u4f60\u7ed9\u54b1\u4eec\u91d1\u5e9c\u4e22\u4eba\u4e86\uff01\u90a3\u5c0f\u5b50\u2026\u2026\u201d \u3000\u3000\u91d1\u5f69\u51e4\u3001\u5c55\u767d\u95fb\u8a00\uff0c\u5dee\u4e0d\u591a\u662f\u540c\u65f6\u8138\u4e0a\u53d8\u8272\uff0c\u817e\u8eab\u7a9c\u51fa\u95e8\u5916\u3002 \u3000\u3000\u201c\u5b5f\u5982\u840d\uff01\u201d\u91d1\u5f69\u51e4\u6c14\u5f97\u82b1\u5bb9\u60e8\u767d\uff0c\u5c16\u58f0\u9a82\u9053\uff1a\u201c\u4f60\u6562\u8840\u53e3\u55b7\u4eba\uff0c\u4ece\u6b64\uff0c\u6211\u91d1\u5f69\u51e4\u4e0d\u8ba4 \u4f60\u8fd9\u5e08\u5144\uff01\u201d \u3000\u3000\u5c55\u767d\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u6211\u4ee5\u4e3a\u4f60\u5c0f\u5b50\u6709\u4ec0\u4e48\u9ad8\u62db\uff1f\u6562\u5927\u8a00\u4e0d\u60ed\uff01\u539f\u6765\u662f\u642c\u5e2e\u624b\u6765\u4e86\uff0c\u4ed7\u7740 \u4eba\u591a\u6491\u8170\uff01\u2026\u2026\u201d \u3000\u3000\u201c\u7389\u9762\u5c0f\u9752\u86a8\u201d\u542c\u5230\u91d1\u5f69\u51e4\u51b3\u7edd\u7684\u4e00\u9a82\uff0c\u53c8\u88ab\u5c55\u767d\u62ff\u8bdd\u4e00\u6fc0\uff0c\u6c14\u5f97\u7736\u773a\u7686\u88c2\uff0c\u5389\u58f0\u53eb \u9053\uff1a\u201c\u6211\u8bf7\u4e24\u4f4d\u524d\u8f88\u6765\uff0c\u662f\u4f5c\u4e2a\u89c1\u8bc1\uff0c\u59d3\u5c55\u7684\u81ed\u5c0f\u5b50\uff01\u4f60\u4ee5\u4e3a\u91d1\u5e9c\u662f\u4f60\u6492\u91ce\u7684\u5730\u65b9\u5417\uff1f\u5c31\u5b5f \u5c0f\u7237\u4e00\u4eba\u4e5f\u53ef\u4ee5\u8981\u4f60\u7684\u72d7\u547d\uff01\u201d \u3000\u3000\u73b0\u5728\uff0c\u5c55\u767d\u5632\u7b11\u4ed6\uff0c\u5df2\u4f7f\u4ed6\u6c14\u6124\u586b\u81ba\uff0c\u91d1\u5f69\u51e4\u60c5\u7edd\u4e49\u88c2\u7684\u4e00\u9a82\uff0c\u66f4\u4f7f\u4ed6\u775a\u7726\u7686\u88c2\uff0c\u4e0d\u7531 \u5389\u5578\u4e00\u58f0\uff0c\u545b\u5577\uff01\u957f\u5251\u51fa\u9798\uff0c\u4e00\u5f0f\u201c\u795e\u9f99\u51fa\u6c34\u201d\uff0c\u8fde\u8eab\u5408\u5251\uff0c\u731b\u5411\u5c55\u767d\u523a\u53bb\uff1a \u3000\u3000\u5c55\u767d\u8eab\u5f62\u5fae\u632b\uff0c\u95ea\u5f00\u8fce\u9762\u4e00\u5251\uff0c\u63a2\u624b\u80cc\u540e\u6293\u4f4f\u201c\u65e0\u60c5\u78a7\u5251\u201d\u5251\u67c4\u2026\u2026 \u3000\u3000\u4f46\u201c\u7389\u9762\u5c0f\u9752\u86a8\u201d\u4e0d\u7b49\u5c55\u767d\u628a\u5251\u8896\u51fa\u9798\u6765\uff0c\u201c\u4e4c\u4e91\u853d\u6708\u201d\uff0c\u2019\u7b11\u6307\u5357\u5929\u201d\uff0c\u201c\u9a7c\u9e1f\u5212 \u6c99\u201d\uff0c\u5237\uff01\u5237\uff01\u5237\uff01\u95ea\u7535\u4f3c\u5730\u8fde\u653b\u4e09\u62db\uff0c\u62db\u62db\u6307\u5411\u5c55\u767d\u8981\u5bb3\u3002 \u3000\u3000\u5c55\u767d\u4e00\u65f6\u5931\u53bb\u5148\u673a\uff0c\u95ea\u3001\u5c55\u3001\u817e\u3001\u632a\uff0c\u4e00\u8def\u75be\u95ea\uff0c\u6700\u540e\u4e00\u62db\u817e\u8eab\u7eb5\u51fa\u4e08\u5916\uff0c\u4f46\u4ecd\u662f\u6162\u4e86 \u2014\u2014\u6162\uff0c\u4e00\u88ad\u957f\u886b\u7684\u540e\u63f4\uff0c\u88ab\u5251\u524a\u843d\u4e00\u7247\uff0c\u4e00\u6761\u53f3\u817f\u9669\u4e9b\u88ab\u5b5f\u5982\u840d\u65a9\u65ad\u3002 \u3000\u3000\u5c55\u767d\u60ca\u51fa\u4e00\u8eab\u51b7\u6c57\u6765\uff0c\u4f46\u201c\u65e0\u60c5\u78a7\u5251\u201d\u5df2\u8d81\u52bf\u64ce\u5728\u624b\u4e2d\u3002 \u3000\u3000\u5c55\u767d\u9677\u8eab\u5b5f\u5982\u840d\u4e09\u62db\u6025\u653b\u7684\u5251\u5e55\u4e4b\u4e2d\uff0c\u9669\u72b6\u767e\u51fa\uff0c\u5c24\u5176\u6700\u540e\u4e00\u5251\uff0c\u5dee\u4e9b\u628a\u5c55\u767d\u4e00\u6761\u53f3\u817f \u780d\u4e0b\u6765\uff0c\u91d1\u5f69\u51e4\u5413\u5f97\u82b1\u5bb9\u5931\u8272\uff0c\u6700\u540e\u89c1\u5c55\u767d\u5b89\u7136\u8131\u51fa\u5708\u5916\uff0c\u4e0d\u7531\u5c16\u58f0\u5411\u5b5f\u5982\u840d\u53eb\u9053\uff1a\u201c\u4f60\u8fd9 \u6837\u6253\u6cd5\uff0c\u662f\u4e0d\u662f\u6210\u5fc3\u7ed9\u91d1\u5bb6\u7684\u4eba\u4e22\u8138\uff1f\u201d \u3000\u3000\u201c\u6211\u6709\u4ec0\u4e48\u4e22\u8138\uff1f\u201d\u76c2\u5982\u840d\u4e0d\u7518\u793a\u5f31\u5730\u53cd\u5634\u5632\u9053\uff1a\u201c\u603b\u4e0d\u50cf\u4f60\uff0c\u624d\u662f\u7ed9\u91d1\u5bb6\u4e22\u4eba\u5462\uff01\u201d \u3000\u3000\u91d1\u5f69\u98ce\u6c14\u5f97\u5468\u8eab\u4e71\u98a4\uff0c\u7389\u9762\u901a\u7ea2\uff0c\u6068\u58f0\u9053\uff1a\u201c\u6211\u7684\u4e8b\u4f60\u7ba1\u4e0d\u7740\uff01\u4f46\u4f60\u4e0e\u4eba\u5bb6\u52a8\u624b\uff0c\u4e0d\u7b49 \u4eba\u5bb6\u4eae\u51fa\u5175\u5668\u6765\uff0c\u5c31\u4ed7\u5251\u731b\u653b\uff0c\u8fde\u7239\u7684\u4eba\u90fd\u8ba9\u4f60\u4e22\u5149\u5566\uff01\u201d \u3000\u3000\u201c\u4f4f\u53e3\uff01\u201d\u5b5f\u5982\u840d\u5389\u53f1\u9053\uff1a\u201c\u90a3\u6028\u4ed6\u81ea\u5df1\u5b66\u827a\u4e0d\u7cbe\uff0c\u8ddf\u4eba\u5bb6\u52a8\u624b\uff0c\u62d4\u4e0d\u51fa\u5251\u6765\uff0c\u96be\u9053\u8fd8 \u8981\u522b\u4eba\u628a\u5251\u4ea4\u5728\u4ed6\u624b\u4e2d\u4e0d\u6210\uff1f\u201d \u3000\u3000\u5e08\u5144\u59b9\u4e8c\u4eba\u820c\u5251\u5507\u67aa\uff0c\u8bf4\u8bdd\u8c01\u4e5f\u4e0d\u8ba9\u8c01\uff0c\u5c55\u767d\u5374\u8d81\u673a\u201c\u545b\u201d\u7136\u4e00\u58f0\u9f99\u541f\uff0c\u628a\u201c\u65e0\u60c5\u78a7 \u5251\u201d\u64a4\u51fa\u9798\u6765\u3002 \u3000\u3000\u6b64\u65f6\u665a\u971e\u5df2\u6536\uff0c\u9ed1\u591c\u964d\u4e34\uff0c\u91d1\u5e9c\u5ead\u9662\u91cc\u672c\u6765\u5230\u5904\u6302\u6709\u706f\u70db\uff0c\u53c8\u52a0\u4e0a\u4e0d\u5c11\u7537\u5973\u4f63\u4eba\u9ad8\u4e3e\u706f \u7b3c\uff0c\u56f4\u5728\u56db\u5468\u770b\u70ed\u95f9\uff0c\u7167\u5f97\u6574\u4e2a\u9662\u843d\u4e2d\u5982\u540c\u767d\u663c\uff0c\u770b\u90a3\u4e9b\u4f63\u4eba\u4e2a\u4e2a\u8138\u4e0a\u53cd\u6709\u5174\u594b\u4e4b\u8272\uff0c\u6ca1\u6709 \u4e00\u4e2a\u8138\u9732\u60ca\u5bb9\uff0c\u4fbf\u77e5\u8fd9\u91d1\u5e9c\u4e2d\u6c5f\u6e56\u8c6a\u5ba2\u53ae\u6740\uff0c\u5df2\u662f\u53f8\u7a7a\u89c1\u60ef\uff0c\u4e00\u89c1\u6709\u4eba\u52a8\u624b\u8fc7\u62db\uff0c\u4f7f\u9f50\u96c6\u5728 \u9662\u4e2d\u770b\u70ed\u95f9\u6765\u4e86\u3002 \u3000\u3000\u5c55\u767d\u201c\u65e0\u60c5\u78a7\u5251\u201d\u64ce\u5728\u624b\u4e2d\u4e00\u9707\uff0c\u5bd2\u8292\u5728\u706f\u5149\u4e2d\u6253\u95ea\uff0c\u53eb\u9053\uff0c\u201c\u91d1\u5c0f\u59d0\uff0c\u8bf7\u4f60\u95ea\u5f00\uff01\u5c55 \u767d\u4eca\u591c\u8981\u4f1a\u4e00\u4f1a\u9ad8\u4eba\uff01\u201d \u3000\u3000\u201c\u597d\u54c7\uff01\u201d\u91d1\u5e9c\u53cc\u94c1\u536b\u4e0d\u4f46\u4e0d\u5e72\u6d89\uff0c\u201c\u94c1\u80cc\u9a7c\u9f99\u201d\u53cd\u5728\u4e00\u8fb9\u9ad8\u58f0\u56b7\u9053\uff1a\u201c\u5c0f\u4f19\u5b50\uff01\u8fd9\u624d \u591f\u52b2\uff0c\u4e0a\u554a\uff01\u201d \u3000\u3000\u4e0d\u7b49\u91d1\u5f69\u51e4\u518d\u53d1\u8a00\uff0c\u201c\u7389\u9762\u5c0f\u9752\u86a8\u201d\u65e9\u5df3\u5389\u53f1\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u8fd9\u56de\u4f60\u5251\u5df2\u51fa\u624b\uff0c\u8be5\u518d\u6ca1\u6709 \u8bdd\u8bf4\u4e86\uff1f\u81ed\u5c0f\u5b50\uff01\u4f60\u5c31\u7eb3\u547d\u6765\u5427\uff01\u201d \u3000\u3000\u62db\u968f\u58f0\u51fa\uff0c\u7389\u9762\u5c0f\u9752\u86a8\u201d\u53cc\u811a\u4e00\u8e6c\uff0c\u8eab\u5f62\u817e\u7a7a\u800c\u8d77\uff0c\u534a\u7a7a\u4e2d\u957f\u5251\u95ea\u8d77\u4e00\u7247\u9752\u8292\uff0c\u731b\u5411\u5c55 \u767d\u5934\u4e0a\u7f69\u4e0b\u3002 \u3000\u3000\u5c55\u767d\u89c1\u4ed6\u8eab\u6cd5\u8f7b\u7075\uff0c\u5251\u62db\u6bd2\u8fa3\uff0c\u62db\u5f0f\u672a\u5230\uff0c\u5df2\u611f\u5230\u51b7\u68ee\u68ee\u7684\u5251\u6c14\u76f4\u903c\u9762\u95e8\uff0c\u5f53\u4e0b\u62b1\u5143\u5b88 \u4e00\uff0c\u6f84\u5fc3\u9759\u8651\uff0c\u201c\u65e0\u60c5\u78a7\u5251\u201d\u7528\u51fa\u4e00\u62db\u201c\u5377\u5730\u7ffb\u5929\u201d\uff0c\u731b\u5411\u6765\u52bf\u64a9\u53bb\u3002 \u3000\u3000\u201c\u65e0\u60c5\u78a7\u5251\u201d\u679c\u7136\u795e\u5175\u5229\u5668\uff0c\u78a7\u6f84\u6f84\u8000\u773c\u731c\u8292\u5927\u76db\uff0c\u7acb\u628a\u201c\u7389\u9762\u5c0f\u9752\u86a8\u201d\u5251\u5149\u63a9\u76d6\u4f4f\uff0e \u7b2c\u4e8c\u5341\u56db\u7ae0\u3000\u529b\u6218\u201c\u7389\u9762\u5c0f\u9752\u86a8\u201d>> \u53e4\u9f99\u300a\u5251\u5ba2\u884c\u300b \u7b2c\u4e8c\u5341\u56db\u7ae0\u3000\u529b\u6218\u201c\u7389\u9762\u5c0f\u9752\u86a8\u201d \u3000\u3000\u201c\u7389\u9762\u5c0f\u9752\u86a8\u2019\u2019\u4f3c\u662f\u770b\u5230\u5c55\u767d\u201c\u65e0\u60c5\u78a7\u5251\u201d\u975e\u662f\u51e1\u54c1\uff0c\u4f46\u4ecd\u5b58\u5fc3\u501f\u51cc\u7a7a\u4e0b\u538b\u4e4b\u52bf\uff0c\u8155 \u4e0a\u8fd0\u8db3\u4e86\u52b2\u529b\uff0c\u731b\u5411\u4e0b\u524a\u53bb\u3002 \u3000\u3000\u201c\u545b\u5577\uff01\u201d\u4e00\u58f0\u9f99\u541f\u864e\u5578\uff0c\u53cc\u5251\u731b\u51fb\u5728\u4e00\u8d77\uff0c\u5728\u591c\u7a7a\u706f\u5149\u4e4b\u4e0b\uff0c\u91d1\u661f\u56db\u5c04\uff0c\u8f89\u70c2\u8000\u76ee\u5982 \u706b\u6811\u94f6\u82b1\uff0c\u851a\u6210\u4e00\u7247\u5947\u666f\u3002 \u3000\u3000\u4e8c\u4eba\u53cc\u81c2\u5747\u611f\u4e00\u9635\u53d1\u9ebb\uff0c\u81c2\u529b\u7adf\u662f\u4e0d\u76f8\u4e0a\u4e0b\uff0c\u5f53\u7136\u201c\u7389\u9762\u5c0f\u9752\u86a8\u201d\u662f\u5360\u4e86\u5c45\u9ad8\u4e34\u4e0b\u7684 \u5149\uff0c\u201c\u7389\u9762\u5c0f\u9752\u86a8\u201d\u98d8\u843d\u516b\u5c3a\u5f00\u5916\uff0c\u5c55\u767d\u5219\u7a33\u7ad9\u5f53\u573a\uff0c\u4e8c\u4eba\u4e0d\u7ea6\u800c\u540c\u4e00\u9f50\u68c0\u89c6\u624b\u4e2d\u5b9d\u5251\uff0c\u770b \u6709\u65e0\u4f24\u635f\u3002 \u3000\u3000\u201c\u65e0\u60c5\u78a7\u5251\u201d\u4e00\u6f84\u5982\u6c34\uff0c\u4e1d\u6beb\u672b\u89c1\u635f\u4f24\uff0c\u7389\u9762\u5c0f\u9752\u86a8\u201d\u9ed1\u94c1\u957f\u5251\uff0c\u6697\u9752\u5982\u58a8\uff0c\u4ea6\u662f\u672a\u635f \u5206\u6beb\u3002 \u3000\u3000\u6b64\u65f6\uff0c\u4e8c\u4eba\u5fc3\u4e2d\u5747\u5df2\u6709\u6570\uff0c\u77e5\u9053\u5bf9\u65b9\u4ff1\u662f\u5b9d\u5251\uff0c\u4e0d\u518d\u5b58\u524a\u6bc1\u5bf9\u65b9\u5175\u5668\u4e4b\u5fc3\uff0c\u53cc\u65b9\u4e8c\u6b21\u5f80 \u4e0a\u4e00\u51d1\uff0c\u5404\u5c55\u7edd\u5b66\uff0c\u6253\u5728\u4e00\u8d77\u3002 \u3000\u3000\u53ea\u89c1\u5c55\u767d\u201c\u65e0\u60c5\u78a7\u5251\u201d\u5982\u60ca\u8679\u7ed5\u7a7a\uff0c\u201c\u7389\u9762\u5c0f\u9752\u86a8\u201d\u9ed1\u94c1\u957f\u5251\u4f3c\u4e4c\u9f99\u95f9\u6d77\uff0c\u4e00\u78a7\u4e00\u9752\uff0c \u4e24\u80a1\u5251\u6c14\uff0c\u7ffb\u817e\u7f2d\u7ed5\uff0c\u4e8c\u4eba\u6253\u5f97\u5feb\u65f6\uff0c\u53ea\u89c1\u68ee\u68ee\u5251\u6c14\u6beb\u5149\uff0c\u5374\u4e0d\u89c1\u4e8c\u4eba\u8eab\u5f71\u3002 \u3000\u3000\u4e8c\u4eba\u90fd\u662f\u5feb\u653b\u5feb\u6253\uff0c\u6643\u773c\u5df2\u6253\u4e86\u56db\u5341\u4f59\u62db\u3002 \u3000\u3000\u201c\u94c1\u80cc\u9a7c\u9f99\u201d\u624b\u637b\u7eb5\u987b\uff0c\u4e00\u53cc\u73af\u76ee\u77aa\u5f97\u6eda\u5706\uff0c\u671b\u5b9a\u4e09\u4eba\u9f99\u817e\u864e\u8dc3\u7684\u640f\u6597\uff0c\u9ad8\u58f0\u558a\u597d\uff0c\u5927 \u53eb\u5927\u56b7\u5730\u6279\u8bc4\u4e8c\u4eba\u5251\u62db\uff1a\u563f\uff01\u597d\u5c0f\u5b50\uff01\u8fd9\u4e00\u62db\u2018\u91d1\u9488\u5b9a\u6d77\u2019\u65bd\u5f97\u4e0d\u9519\uff0c\u591f\u5473\u9053\u3002\u201d \u3000\u3000\u201c\u55e8\uff01\u53ef\u60dc\uff01\u2018\u5c0f\u9752\u86a8\u2019\uff01\u8fd9\u62db\u2018\u6d6a\u91cc\u65a9\u86df\u2019\uff0c\u53ea\u5dee\u4e24\u5bf8\uff0c\u4f24\u4e0d\u5230\u5bf9\u65b9\u2026\u2026\u201d \u3000\u3000\u201c\u94c1\u7ffc\u98de\u9e4f\u201d\u9762\u8272\u6df1\u6c89\uff0c\u867d\u7136\u4e00\u53cc\u7cbe\u5149\u66b4\u5c04\u7684\u5c0f\u5706\u773c\u7d27\u76ef\u573a\u5185\u52a8\u624b\u7684\u4e8c\u4eba\uff0c\u4f46\u5374\u662f\u7d27\u95ed \u5634\u5507\uff0c\u4e00\u8a00\u4e0d\u53d1\u3002 \u3000\u3000\u201c\u6c5f\u5357\u7b2c\u4e00\u7f8e\u4eba\u201d\u91d1\u5f69\u51e4\uff0c\u7d20\u77e5\u5e08\u5144\u6b66\u529f\u5df2\u5f97\u7236\u4eb2\u771f\u4f20\uff0c\u6697\u6697\u4e3a\u5fc3\u4e0a\u4eba\u62c5\u5fe7\uff0c\u4e00\u53cc\u5a9a\u5982 \u6625\u6c34\u7684\u660e\u7741\uff0c\u77ac\u4e5f\u4e0d\u77ac\u5730\u671b\u5b9a\u4e8c\u4eba\uff0c\u5982\u82b1\u7684\u7c89\u9762\u4e0a\u662f\u65f6\u60ca\u65f6\u559c\uff0c\u7d27\u5f20\u5730\u63e1\u7d27\u53cc\u62f3\uff0c\u638c\u5fc3\u91cc\u5df2 \u6e17\u51fa\u9999\u6c57\u6765\u3002 \u3000\u3000\u56f4\u5728\u56db\u5468\u770b\u70ed\u95f9\u7684\u7537\u5973\u4f63\u4eba\uff0c\u5374\u4e00\u4e2a\u4e2a\u7741\u5927\u4e86\u773c\u775b\u770b\u50bb\u4e86\uff0c\u867d\u7136\u4ed6(\u5979)\u4eec\u66fe\u89c1\u8fc7\u4e0d\u5c11\u6b21 \u6fc0\u6597\uff0c\u4f46\u4ece\u6ca1\u6709\u8fd9\u4e00\u6b21\u7d27\u5f20\u6fc0\u70c8\uff0c\u51b7\u68ee\u68ee\u7684\u5251\u950b\uff0c\u56de\u65cb\u751f\u98d9\uff0c\u5bd2\u5149\u903c\u4eba\uff0c\u6709\u51e0\u4e2a\u80c6\u5c0f\u7684\uff0c\u7ad9 \u4e0d\u4f4f\u811a\uff0c\u5df2\u8eab\u4e0d\u7531\u5df1\u5730\u7f13\u7f13\u540e\u9000\u3002 \u3000\u3000\u5c55\u767d\u4e0e\u201c\u7389\u9762\u5c0f\u9752\u86a8\u201d\u5374\u5df2\u6253\u5230\u4e86\u751f\u6b7b\u4ea4\u5173\u7684\u7d27\u5f20\u9636\u6bb5\u3002 \u3000\u3000\u201c\u7389\u9762\u5c0f\u9752\u86a8\u201d\u5251\u62db\u9ad8\u8d85\uff0c\u8eab\u6cd5\u8f7b\u7075\uff0c\u98de\u3001\u817e\u3001\u5954\u3001\u7a9c\uff0c\u72b9\u5982\u7075\u733f\u8df3\u6da7\uff0c\u51fa\u624b\u66f4\u662f\u72e0 \u8fa3\uff0c\u62db\u62db\u4e0d\u79bb\u5c55\u767d\u8981\u5bb3\uff0c\u6068\u4e0d\u5f97\u4e00\u5251\u628a\u5c55\u767d\u523a\u4e2a\u5bf9\u7a7f\uff0c\u9664\u53bb\u8fd9\u4e00\u5927\u60c5\u654c\u3002 \u3000\u3000\u5c55\u767d\u5185\u529b\u96c4\u6d51\uff0c\u6c89\u7740\u7a33\u5b9a\uff0c\u6240\u65bd\u5251\u62db\u5b8c\u5168\u662f\u5927\u5f00\u5927\u5408\uff0c\u624b\u3001\u773c\u3001\u8eab\u3001\u6cd5\u3001\u6b65\uff0c\u5904\u5904\u663e\u793a \u51fa\u624e\u6709\u5f88\u6df1\u6839\u5e95\uff0c\u5251\u62db\u5149\u660e\u6b63\u5927\uff0c\u5c24\u5176\u662f\u6c14\u5ea6\u96cd\u5bb9\uff0c\u9690\u7136\u6709\u4e00\u6d3e\u5927\u5bb6\u98ce\u8303\u3002 \u3000\u3000\u201c\u7389\u9762\u5c0f\u9752\u86a8\u201d\u4e00\u8fb9\u52a8\u624b\uff0c\u4e00\u8fb9\u5fc3\u4e2d\u751f\u602a\uff0c\u770b\u5c55\u767d\u6240\u7528\u5251\u6cd5\uff0c\u4e0d\u8fc7\u662f\u6b66\u6797\u5e38\u89c1\u7684\u6781\u666e\u901a \u7684\u201c\u4e09\u624d\u5251\u6cd5\u201d\uff0c\u5076\u5c14\u4ea4\u6742\u4e0a\u51e0\u62db\u602a\u62db\uff0c\u56e0\u4e5f\u4e0d\u89c1\u5f97\u9ad8\u660e\u5230\u54ea\u91cc\u53bb\u3002\u81ea\u5df1\u7d20\u4ee5\u5251\u6cd5\u89c1\u957f\uff0c\u7adf \u7136\u4e00\u65f6\u4e4b\u95f4\u5360\u4e0d\u4e86\u4e0a\u98ce\uff0c\u660e\u660e\u81ea\u5df1\u65bd\u51fa\u7edd\u5927\u6740\u62db\uff0c\u5c55\u767d\u5374\u4e0d\u614c\u4e0d\u5fd9\uff0c\u53ea\u7528\u4e00\u62db\u6781\u5e73\u5e38\u7684\u5251 \u6cd5\uff0c\u4fbf\u628a\u81ea\u5df1\u7684\u7edd\u5927\u6740\u62db\u5316\u89e3\u6389\u4e86\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u4e8c\u4eba\u5df2\u6218\u4e86\u5c06\u8fd1\u767e\u62db\uff0c\u4ecd\u7136\u4e0d\u5206\u80dc\u8d1f\uff0c\u201c\u7389\u9762\u5c0f\u9752\u86a8\u201d\u5fc3\u4e2d\u4e0d\u8010\u70e6\uff0c\u6b63\u5de7\u5c55\u767d\u4f7f \u4e86\u4e00\u62db\u201c\u7acb\u626b\u5b87\u5b99\u201d\uff0c\u78a7\u5251\u5411\u201c\u7389\u9762\u5c0f\u9752\u86a8\u201d\u9876\u4e0a\u626b\u6765\u3002 \u3000\u3000\u201c\u7389\u9762\u5c0f\u9752\u86a8\u201d\u8eab\u5f62\u66b4\u7f29\u534a\u5c3a\uff0c\u8eb2\u8fc7\u9876\u4e0a\u4e00\u5251\uff0c\u9ed1\u94c1\u5251\u201c\u6c34\u4e2d\u635e\u6708\u201d\uff0c\u731b\u65a9\u5c55\u767d\u4e0b\u76d8\u3002 \u3000\u3000\u5c55\u81ea\u53cc\u8db3\u4e00\u987f\uff0c\u79bb\u5730\u4e09\u5c3a\uff0c\u624b\u4e2d\u5251\u6f14\u201c\u5bd2\u661f\u5954\u6708\u201d\uff0c\u731b\u70b9\u201c\u7389\u9762\u5c0f\u9752\u86a8\u201d\u9876\u95e8\u201c\u534e\u76d6\u201d \u91cd\u7a74\u3002 \u3000\u3000\u6309\u5e38\u89c4\u201c\u7389\u9762\u5c0f\u9752\u86a8\u201d\u5e94\u8be5\u4f7f\u201c\u56de\u98ce\u62c2\u67f3\u201d\uff0c\u6216\u8005\u201c\u6e38\u8702\u620f\u854a\u201d\uff0c\u8f6c\u8eab\u8eb2\u5f00\u5c55\u767d\u90a3\u62db \u201c\u5bd2\u5c4b\u5954\u6708\u201d\uff0c\u624d\u80fd \u3000\u3000\u53ef\u662f\uff0c\u201c\u7389\u9762\u5c0f\u9752\u86a8\u201d\u6c42\u80dc\u5fc3\u5207\uff0c\u5f03\u6b63\u89c4\u6218\u6cd5\u60f3\u51fa\u9669\u62db\u6c42\u80dc\uff0c\u4e0d\u8eb2\u4e0d\u95ea\uff0c\u6b3a\u8eab\u6a2a\u5251\uff0c\u7528 \u4e86\u4e00\u62db\u201c\u4e07\u82b1\u732e\u4f5b\u201d\uff0c\u5251\u8eab\u6a2a\u7740\u4e00\u6321\u5c55\u767d\u5251\u52bf\uff0c\u987a\u52bf\u6a2a\u5411\u524d\u63a8\uff0c\u731b\u780d\u5c55\u767d\u524d\u80f8\u3002 \u3000\u3000\u8fd9\u4e00\u62db\uff0c\u771f\u662f\u9669\u6781\uff0c\u5982\u679c\u5c55\u767d\u8f7b\u529f\u8f83\u9ad8\uff0c\u80fd\u591f\u51cc\u7a7a\u6362\u6b65\uff0c\u8eab\u5f62\u518d\u4e0a\u63e1\u4e09\u5c3a\uff0c\u539f\u52bf\u4e0d\u53d8\uff0c \u5251\u5c16\u4e0b\u843d\uff0c\u5fc5\u53ef\u628a\u201c\u7389\u9762\u5c0f\u9752\u86a8\u201d\u5934\u9876\u201c\u534e\u76d6\u7a74\u201d\u523a\u4e00\u4e2a\u8840\u7a9f\u7abf\u3002 \u3000\u3000\u4f46\u4e8c\u4eba\u6253\u4e86\u4e00\u767e\u4f59\u62db\uff0c\u201c\u7389\u9762\u5c0f\u9752\u86a8\u201d\u89c1\u5c55\u767d\u8eab\u6cd5\u8fdf\u8def\uff0c\u7b97\u5b9a\u5c55\u767d\u4e0d\u80fd\u51cc\u7a7a\u518d\u884c\u4e0a\u7a9c\uff0c \u6545\u800c\u7528\u4e86\u8fd9\u4e48\u4e00\u62db\u9669\u62db\u3002 \u3000\u3000\u5c55\u767d\u6709\u6ca1\u6709\u51cc\u7a7a\u6362\u6b65\u7684\u529f\u529b\uff1f\u6709\uff01\u4f46\u4ed6\u6b66\u529f\u5185\u529b\u8fdb\u6b65\u592a\u5feb\uff0c\u8fde\u4ed6\u81ea\u5df1\u4e5f\u4e0d\u77e5\u9053\u81ea\u5df1\u76ee\u4e0b \u8eab\u5177\u5185\u529f\u6f5c\u529b\u7a76\u7adf\u6709\u591a\u5927\u3002\u53c8\u52a0\u4e0a\u7f3a\u5c11\u5e94\u654c\u7ecf\u9a8c\uff0c\u4ed6\u90a3\u4e00\u5251\u76f4\u523a\u4e0b\u53bb\uff0c\u7b97\u5b9a\u201c\u7389\u9762\u5c0f\u9752\u86a8\u201d \u8981\u5411\u4e00\u65c1\u8eb2\u95ea\u3002 \u3000\u3000\u8bbe\u60f3\u5230\u201c\u7389\u9762\u5c0f\u9752\u86a8\u201d\u4e0d\u9000\u53cd\u8fdb\uff0c\u6a2a\u5251\u5411\u4ed6\u80f8\u524d\u5207\u6765\uff0c\u81ea\u5df1\u5374\u65e0\u6cd5\u518d\u884c\u8eb2\u95ea\uff0c\u773c\u770b\u201c\u7389 \u9762\u5c0f\u9752\u86a8\u201d\u9ed1\u94c1\u957f\u5251\uff0c\u8d34\u7740\u81ea\u5df1\u78a7\u5251\u9551\u5203\u5411\u81ea\u5df1\u80f8\u524d\u5207\u5230\u2026\u2026 \u3000\u3000\u4e00\u822c\u6b66\u529f\u8f83\u4f4e\u7684\u4eba\uff0c\u8fd8\u6ca1\u6709\u770b\u51fa\u8fd9\u4e00\u62db\u7684\u51f6\u9669\uff0c\u4f46\u201c\u91d1\u5e9c\u53cc\u94c1\u536b\u201d\u53ca\u91d1\u5f69\u51e4\u7b49\u4eba\uff0c\u5374\u5df2 \u770b\u51fa\u8fd9\u4e00\u62db\uff0c\u771f\u662f\u51f6\u9669\u65e0\u6bd4\uff0c\u4e0d\u7531\u4e00\u9f50\u60ca\u53eb\u51fa\u58f0\u3002 \u3000\u3000\u201c\u5c0f\u86a8\u5b50\uff01\u201d\u201c\u94c1\u80cc\u9a7c\u9f99\u201d\u9ad8\u58f0\u5927\u53f1\uff1a\u4f60\u8fd9\u662f\u4ec0\u4e48\u6253\u6cd5\uff01\u201d\u8bf4\u7740\u817e\u8eab\u6251\u81f3\u2026\u2026 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u4fa0\uff0c\u201c\u94c1\u80cc\u9a7c\u9f99\u201d\u5c1a\u672a\u6251\u5230\uff0c\u767e\u5fd9\u4e2d\uff0c\u5c55\u767d\u8fd0\u529b\u2014\u3002\u9707\u624b\u8155\uff0c\u628a\u5468\u8eab\u771f\u529b \u8d2f\u6ce8\u5251\u8eab\uff0c\u731b\u7136\u5411\u4e0b\u4e00\u9707\uff0c\u201c\u5f53\u5f53\u201d\u4e00\u58f0\uff0c\u201c\u7389\u9762\u5c0f\u9752\u86a8\u201d\u864e\u53e3\u4e00\u9707\uff0c\u957f\u94c1\u5251\u8131\u624b\uff0c\u6389\u843d\u5730 \u4e0a\u3002 \u3000\u3000\u5c55\u767d\u5c31\u52bf\u4e00\u7ffb\u624b\u8155\uff0c\u201c\u5929\u60c5\u78a7\u5251\u201d\u51b7\u68ee\u68ee\u7684\u5251\u5c16\u5df2\u903c\u5728\u201c\u7389\u9762\u5c0f\u9752\u86a8\u201d\u54bd\u5589\u4e4b\u4e0a\u3002 \u3000\u3000\u201c\u7389\u9762\u5c0f\u9752\u86a8\u201d\u884c\u9669\u843d\u8d25\uff0c\u5fc3\u4e2d\u72b9\u5982\u4e07\u7bad\u9f50\u6512\uff0c\u96be\u8fc7\u4e07\u5206\uff0c\u8fd9\u662f\u4ed6\u751f\u5e73\u7b2c\u4e00\u6b21\u60e8\u8d25\uff0c\u4fca \u7f8e\u7684\u8138\u4e0a\u53d8\u6210\u7070\u8272\u3002 \u3000\u3000\u5c55\u767d\u4e5f\u4f30\u4e0d\u5230\u81ea\u5df1\u5185\u529b\uff0c\u5230\u4e86\u6536\u53d1\u7531\u5fc3\u7684\u7a0b\u5ea6\uff0c\u4e00\u5251\u628a\u201c\u7389\u9762\u5c0f\u9752\u86a8\u201d\u94c1\u5251\u9707\u51fa\u624b\u53bb\uff0c \u201c\u65e0\u60c5\u78a7\u5251\u201d\u70b9\u5728\u201c\u7389\u9762\u5c0f\u9752\u86a8\u201d\u54bd\u5589\u4e4b\u4e0a\uff0c\u5fae\u5fbd\u4e00\u6014\uff0c\u5e76\u672a\u7acb\u5373\u65bd\u51fa\u6740\u624b\u3002 \u3000\u3000\u201c\u54c8\u54c8\u54c8\uff01\u2026\u2026\u201d\u201c\u94c1\u80cc\u9a7c\u9f99\u201d\u8eab\u5982\u98d8\u98ce\uff0c\u5df2\u7a9c\u81f3\u4e8c\u4eba\u9762\u524d\uff0c\u5148\u54c8\u54c8\u4e00\u7b11\uff0c\u624d\u8bf4\u9053\uff1a\u5c0f \u54e5\u513f\uff0c\u771f\u6709\u4f60\u7684\uff01\u4f60\u8d62\u4e86\uff0c\u8d62\u5f97\u5149\u5f69\uff0c\u53ef\u662f\uff0c\u6211\u4eec\u8fd9\u53ea\u662f\u6bd4\u6b66\u6027\u8d28\uff0c\u53cc\u65b9\u53c8\u6ca1\u6709\u6df1\u4ec7\u5927\u6068\uff0c \u70b9\u5230\u5c31\u591f\u4e86\uff0c\u8bf7\u628a\u4f60\u7684\u5251\u6536\u56de\u53bb\u5427\uff01\u201d \u3000\u3000\u201c\u94c1\u80cc\u9a7c\u9f99\u201d\u7ea2\u9762\u7eb5\u987b\uff0c\u82cd\u5934\u9a7c\u80cc\uff0c\u795e\u6001\u5f02\u5e38\u5a01\u731b\uff0c\u8bf4\u8bdd\u5410\u6c14\u5982\u96f7\uff0c\u9690\u7136\u6709\u4e00\u526f\u9707\u670d\u7fa4 \u4f26\u7684\u5a01\u52bf\u3002 \u3000\u3000\u5c55\u767d\u662f\u4e0d\u613f\u6740\u5931\u53bb\u62b5\u6297\u529b\u4e4b\u4eba\uff0c\u518d\u8005\u81ea\u5df1\u7236\u4ec7\u771f\u76f8\u672a\u63ed\u4e4b\u524d\uff0c\u81ea\u5df1\u5728\u91d1\u5e9c\u4ecd\u7b97\u662f\u4f5c\u5ba2\uff0c \u4e0d\u613f\u843d\u4e2a\u65e0\u6545\u4f24\u4eba\u7684\u7f6a\u540d\u3002\u5f53\u7136\uff0c\u201c\u94c1\u80cc\u9a7c\u9f99\u201d\u8fd9\u51e0\u53e5\u8bdd\u7684\u529b\u91cf\u4e5f\u4e0d\u5c0f\uff0c\u95fb\u8a00\u7adf\u6536\u5251\u56de\u9798\uff0c \u5012\u7eb5\u51fa\u516b\u5c3a\u5f00\u5916\u8bf4\u9053\uff1a\u201c\u8c28\u9075\u8001\u524d\u8f88\u5429\u5490\u3002\u201d\u7ee7\u800c\u53c8\u5bf9\u201c\u7389\u9762\u5c0f\u9752\u86a8\u201d\u51b7\u51b7\u5730\u8bf4\u9053\uff1a\u201c\u53ea\u8981 \u8ba9\u4f60\u61c2\u5f97\uff0c\u4ee5\u540e\u5c11\u518d\u76ee\u4e2d\u65e0\u4eba\u2026\u2026\u201d \u3000\u3000\u201c\u81ed\u5c0f\u5b50\uff01\u4f11\u72c2\uff01\u201d\u5b5f\u5982\u840d\u7a81\u7136\u4e00\u58f0\u5389\u53f1\u3002 \u3000\u3000\u201c\u63a5\u4f4f\u5c0f\u592a\u7237\u8fd9\u4e2a\uff01\u201d \u3000\u3000\u5728\u201c\u7389\u9762\u5c0f\u9752\u8dcc\u201d\u5b5f\u5982\u840d\u66b4\u559d\u58f0\u4e2d\uff0c\u53ea\u89c1\u4ed6\u5de6\u624b\u4e00\u626c\uff0c\u4e00\u84ec\u9752\u8272\u5149\u5f71\uff0c\u731b\u5411\u5c55\u767d\u5468\u8eab\u6253 \u6765\u3002 \u3000\u3000\u539f\u6765\u201c\u7389\u9762\u5c0f\u9752\u86a8\u201d\u94c1\u5251\u88ab\u5c55\u767d\u9707\u98de\uff0c\u607c\u7f9e\u6210\u6012\uff0c\u628a\u201c\u9752\u86a8\u795e\u201d\u9707\u60ca\u6c5f\u6e56\u7684\u72ec\u95e8\u6697\u5668 \u201c\u9752\u8dcc\u91d1\u94b1\u9556\u201d\u4ee5\u201c\u6ee1\u5929\u82b1\u96e8\u201d\u624b\u6cd5\uff0c\u5411\u5c55\u767d\u6253\u51fa\u3002 \u3000\u3000\u201c\u5e08\u5144\uff01\u4f60\u6562\uff01H\u2026\u3002\u201d\u91d1\u5f69\u98ce\u5c16\u58f0\u60ca\u547c\u2026\u2026 \u3000\u3000\u201c\u5982\u840d\uff01\u201d\u201c\u94c1\u80cc\u9a7c\u9f99\u201d\u4ea6\u611f\u5927\u51fa\u610f\u5916\uff0c\u5c55\u767d\u7b97\u662f\u91d1\u5e9c\u7684\u5ba2\u4eba\uff0c\u90fd\u542c\u4ed6\u7684\u8bdd\u628a\u5251\u6536\u56de\uff0c \u6ca1\u60f3\u5230\u81ea\u5df1\u4eba\u5012\u4e0d\u7ed9\u4ed6\u7559\u9762\u5b50\uff0c\u8d81\u4eba\u4e0d\u5907\u65f6\u731b\u4e0b\u8fa3\u624b\uff0c\u4e0d\u7531\u66b4\u6012\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u7b97\u4ec0\u4e48\u2026\u2026 \u3000\u3000\u66b4\u559d\u58f0\u4e2d\uff0c\u731b\u7136\u6325\u51fa\u4e00\u638c\uff0c\u4e00\u80a1\u72c2\u98ce\u52b2\u6d41\uff0c\u5377\u5730\u800c\u8d77\uff0c\u76f4\u5411\u6f2b\u7a7a\u9752\u8272\u5149\u5f71\u626b\u53bb\u3002 \u3000\u3000\u4f46\u201c\u9752\u86a8\u795e\u201d\u7684\u201c\u9752\u86a8\u9556\u201d\uff0c\u7ecf\u8fc7\u7279\u522b\u70bc\u5236\uff0c\u53c8\u4ee5\u7279\u6b8a\u624b\u6cd5\u6253\u51fa\uff0c\u8fde\u201c\u94c1\u80cc\u9a7c\u9f99\u201d\u90a3\u4e48 \u521a\u70c8\u5a01\u731b\u7684\u638c\u98ce\uff0c\u90fd\u4e0d\u80fd\u5b8c\u5168\u6321\u4f4f\uff0c\u53ea\u542c\u51e0\u58f0\u5c16\u9510\u523a\u8033\u7684\u91d1\u5203\u5578\u98ce\u4e4b\u58f0\uff0c\u5df2\u6709\u6570\u679a\u201c\u9752\u86a8 \u9556\u201d\u7a7f\u8fc7\u201c\u94c1\u80cc\u9a7c\u9f99\u201d\u638c\u98ce\uff0c\u901f\u5ea6\u53cd\u800c\u66f4\u52a0\u8fc5\u75be\uff0c\u5982\u6d41\u661f\u987b\u77f3\u4e00\u822c\uff0c\u731b\u5411\u5c55\u767d\u5c04\u81f3\u3002 \u3000\u3000\u201c\u6323\uff01\u6323\uff01\u6323\uff01\u201d \u3000\u3000\u4e09\u58f0\u91d1\u9e23\uff0c\u4e09\u6735\u91d1\u661f\u706b\u82b1\uff0c\u5728\u591c\u7a7a\u91cc\u95ea\u8fc7\uff0c\u539f\u6765\u91d1\u5f69\u51e4\u65e9\u5728\u624b\u4e2d\u6263\u4e86\u4e09\u679a\u201c\u91d1\u86a8\u9556\u201d\u51c6 \u5907\u5e94\u6025\uff0c\u5979\u89c1\u201c\u94c1\u80cc\u9a7c\u9f99\u201d\u638c\u98ce\u7f62\u6c14\uff0c\u4ecd\u4e0d\u80fd\u5b8c\u5168\u963b\u4f4f\u5b5f\u5982\u840d\u6253\u51fa\u7684\u201c\u9752\u86a8\u9556\u201d\uff0c\u624d\u6296\u624b\u6253 \u51fa\u81ea\u5df1\u6240\u6263\u7684\u91d1\u9556\uff0c\u9707\u843d\u5c04\u5411\u5c55\u767d\u9762\u95e8\u3001\u5fc3\u3001\u8179\u8981\u5bb3\u7684\u4e09\u679a\u201c\u9752\u86a8\u9556\u201d\u3002 \u3000\u3000\u53ef\u662f\uff0c\u4ecd\u6709\u56db\u679a\uff01\u9752\u86a8\u9556\u201d\u75be\u98de\u731b\u5c04\uff0c\u4e00\u5de6\u4e00\u53f3\u88ad\u5411\u5c55\u767d\u53cc\u80a9\uff0c\u4e24\u679a\u8d34\u5730\u98de\u5954\u5c55\u767d\u53cc \u817f\uff01 \u3000\u3000\u91d1\u5f69\u51e4\u6b64\u65f6\u518d\u60f3\u63a2\u624b\u53d6\u9556\u5df2\u6765\u4e0d\u53ca\uff0c\u53ea\u6025\u5f97\u98ce\u76ee\u5706\u7741\uff0c\u7c89\u8138\u5931\u8272\u2026\u2026 \u3000\u3000\u5c55\u767d\u5374\u6643\u80a9\u817e\u8eab\uff0c\u8fde\u8eb2\u8fc7\u4e09\u679a\uff0c\u88ad\u5411\u5de6\u80a9\u7684\u4e00\u679a\u518d\u4e5f\u65e0\u6cd5\u8eb2\u8fc7\uff0c\u201c\u5657\u201d\u7684\u4e00\u58f0\uff0c\u6b63\u6253\u5728 \u5de6\u80a9\u5934\u4e0a\uff0c\u6df1\u6ca1\u5165\u9aa8\uff0c\u9c9c\u8840\u7acb\u523b\u987a\u7740\u624b\u81c2\u6dcc\u4e0b\u6765\uff01 \u3000\u3000\u5c55\u767d\u53ea\u89c9\u4f24\u5904\u4e00\u80a1\u5bd2\u6c14\uff0c\u76f4\u4fb5\u80ba\u8151\uff0c\u77e5\u7cfb\u6697\u5668\u6709\u6bd2\uff0c\u4e0d\u7531\u5fc3\u4e2d\u4e00\u60ca\uff0c\u4f46\u4ecd\u54ac\u7259\u5fcd\u75db\uff0c\u95ed \u4efb\u5de6\u80a9\u5904\u7a74\u9053\uff0c\u4ee5\u53f3\u624b\u53cc\u6307\uff0c\u6697\u8fd0\u201c\u91d1\u94a2\u201d\u624b\u6cd5\uff0c\u786c\u4ece\u8089\u5185\u628a\u90a3\u679a\u201c\u9752\u86a8\u91d1\u94b1\u9556\u201d\u94b3\u4e86\u51fa \u6765\u3002 \u3000\u3000\u201c\u5c55\u5c0f\u4fa0\uff01\u201d\u91d1\u5f69\u51e4\u638f\u51fa\u4e00\u9897\u836f\u4e38\uff0c\u8d85\u524d\u51e0\u6b65\uff0c\u5e7d\u5e7d\u5730\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u89e3\u836f\uff0c\u4f60\u6577\u4e0a\u5427\uff01 \u82e5\u4e0d\u7136\u2026\u2026\u201d \u3000\u3000\u5c55\u767d\u624b\u4e2d\u63e1\u7740\u90a3\u679a\u5e26\u8840\u7684\u201c\u9752\u86a8\u9556\u201d\uff0c\u8138\u8272\u72f0\u72de\u5f97\u53ef\u6015\uff0c\u53cc\u773c\u5ffd\u7741\uff0c\u773c\u7736\u90fd\u6d41\u51fa\u8840\u6765 \u4e86\uff0c\u6cbf\u7740\u53cc\u989d\u7f13\u7f13\u6d41\u4e0b\uff0c\u5bf9\u91d1\u5f69\u51e4\u7684\u8f6f\u8bed\u6e29\u5b58\uff0c\u72b9\u5982\u672a\u89c1\u2026\u2026 \u3000\u3000\u91d1\u5f69\u51e4\u5927\u5403\u4e00\u60ca\uff0c\u89c1\u5c55\u767d\u7684\u8138\u8272\u90a3\u4e48\u53ef\u6015\uff0c\u82b3\u5fc3\u4e0d\u7531\u4e00\u5bd2\uff0c\u98a4\u58f0\u9053\uff1a\u201c\u5c55\u5c0f\u4fa0\uff0c\u8bf7\u4e0d\u8981 \u8fd9\u6837\uff01\u6211\u5e08\u5144\u5bf9\u4e0d\u8d77\u4f60\uff0c\u7b49\u6211\u54e5\u54e5\u56de\u6765\uff0c\u6211\u4e00\u5b9a\u544a\u8bc9\u54e5\u54e5\uff0c\u8bf7\u54e5\u54e5\u7ed9\u4f60\u4e3b\u6301\u516c\u9053\uff01\u201d \u3000\u3000\u91d1\u5f69\u51e4\u8bf4\u7740\uff0c\u8d85\u524d\u63e1\u4f4f\u5c55\u767d\u5de6\u81c2\uff0c\u628a\u4f24\u5904\u8863\u886b\u6495\u5f00\uff0c\u4ee5\u89e3\u836f\u6309\u8fdb\u4f24\u53e3\u4e4b\u5185\uff0c\u7528\u4e00\u53ea\u7389 \u638c\uff0c\u7f13\u7f13\u5730\u63c9\u6309\u2026\u2026 \u3000\u3000\u5c55\u767d\u604d\u5982\u672a\u89c9\uff0c\u53ea\u60b2\u6124\u83ab\u540d\u5730\u77a0\u89c6\u7740\u8fdc\u65b9\uff0c\u4f3c\u662f\u60f3\u8d77\u5f88\u5927\u7684\u4f24\u5fc3\u4e8b\uff0c\u4f46\u4f17\u4eba\u5374\u4e0d\u77e5\u4ed6\u60f3 \u4e9b\u4ec0\u4e48\u2026\u2026 \u3000\u3000\u201c\u54fc\uff01\u201d\u201c\u7389\u9762\u5c0f\u9752\u86a8\u201d\u89c1\u91d1\u5f69\u51e4\u5bf9\u5c55\u767d\u5173\u5207\u903e\u6052\uff0c\u67d4\u60c5\u4f3c\u6c34\uff0c\u5992\u706b\u4e2d\u70e7\uff0c\u51b7\u54fc\u4e00\u58f0\uff0c \u8138\u8272\u72de\u6076\u66f4\u751a\u4e8e\u5c55\u767d\uff0c\u7f13\u7f13\u53c8\u63a2\u624b\u9556\u56ca\u3002 \u3000\u3000\u201c\u5982\u840d\uff01\u201d\u201c\u94c1\u80cc\u9a7c\u9f99\u201d\u5389\u53f1\u9053\uff1a\u201c\u4f60\u8981\u5e72\u4ec0\u4e48\uff1f\u96be\u9053\u4e00\u70b9\u8138\u9762\u90fd\u4e0d\u987e\uff0c\u771f\u8981\u7b49\u6211\u9a7c\u5b50 \u51fa\u624b\u5417\uff1f\u201d \u3000\u3000\u8fd9\u4e9b\u4eba\u8bf4\u8bdd\u3001\u884c\u52a8\uff0c\u4ee5\u53ca\u91d1\u5f69\u51e4\u4e3a\u4ed6\u6577\u836f\uff0c\u5c55\u767d\u6d51\u7136\u672a\u89c9\uff0c\u539f\u6765\u4ed6\u662f\u60f3\u5230\u4e86\u7236\u4eb2\u7684\u60e8 \u6b7b\uff0c\u8fd9\u679a\u5e26\u8840\u7684\u201c\u9752\u86a8\u9556\u201d\u4e0d\u6b63\u548c\u7236\u4eb2\u4ea4\u7ed9\u4ed6\u516d\u4ef6\u9057\u7269\u4e2d\u4e4b\u4e00\u201c\u9752\u94dc\u5236\u94b1\u201d\u4e00\u6837\u5417\uff01\u8fd9\u4f7f\u4ed6 \u5e7b\u60f3\u5230\u7236\u4eb2\u88ab\u5f53\u4e16\u516d\u5927\u9ad8\u624b\u7684\u56f4\u653b\uff0c\u6d74\u8840\u82e6\u6218\u7684\u60c5\u5f62\uff1a\u7236\u4eb2\u2014\u2014\u201c\u9739\u96f3\u5251\u201d\u5c55\u4e91\u5929\uff0c\u624b\u6267 \u201c\u65e0\u60c5\u78a7\u5251\u201d\uff0c\u6602\u7acb\u4e8e\u91cd\u56f4\u4e4b\u4e2d\uff0c\u5f53\u4e16\u516d\u5927\u6b66\u6797\u9ad8\u624b\uff0c\u805a\u4f17\u7fa4\u6740\uff0c\u8fd8\u662f\u8f66\u8f6e\u6218\u6cd5\uff1f\u4ed6\u731c\u6d4b\u4e0d \u5230\uff0c\u4f46\u7236\u4eb2\u4e00\u5b9a\u662f\u6218\u5f97\u771f\u529b\u6d88\u8017\u6b86\u5c3d\uff0c\u7136\u540e\u7531\u201c\u9752\u86a8\u795e\u201d\u91d1\u4e5d\uff0c\u4ee5\u6697\u5668\u5077\u88ad\u751a\u6216\u516d\u4eba\u4e00\u9f50\u65bd \u7528\u6697\u5668\uff0c\u4ea6\u672a\u53ef\u77e5\uff1f\u4f7f\u7236\u4eb2\u5468\u8eab\u8d1f\u4f24\uff0c\u7136\u540e\u624d\u4ee5\u4e71\u5200\u4e71\u5251\u628a\u7236\u4eb2\u6740\u6b7b\u2026\u2026\u4f46\u4e0d\u77e5\u4ed6\u4eec\u201c\u6c5f\u5357 \u4e03\u4fa0\u201d\u4e49\u7ed3\u91d1\u5170\uff0c\u8a93\u540c\u751f\u6b7b\uff0c\u4e3a\u4ec0\u4e48\u516d\u4e2a\u4eba\u5408\u8d77\u6765\u5bb3\u6b7b\u7236\u4eb2\uff1f\u8fd9\u59cb\u7ec8\u662f\u4f7f\u4eba\u60f3\u4e0d\u900f\u7684\u4e00\u4e2a \u8c1c\uff01\u2026\u2026 \u3000\u3000\u5c55\u767d\u53c8\u7ee7\u7eed\u60f3\u9053\uff1a\u201c\u5047\u5982\u7236\u4eb2\u4e0d\u6b7b\uff0c\u6c5f\u5357\u4e03\u4fa0\u6c5f\u6e56\u9f50\u540d\uff0c\u81ea\u5df1\u957f\u5927\u7eb5\u7136\u4e2d\u4e0e\u5f53\u4eca\u2018\u6b66\u6797 \u56db\u516c\u5b50\u2019\u5206\u5ead\u6297\u793c\uff0c\u6700\u4f4e\u9650\u5ea6\u4e0e\u7236\u6bcd\u900d\u9065\u5c71\u6797\u4e4b\u4e50\u4e0d\u95ee\u6c5f\u6e56\u662f\u975e\uff0c\u4e5f\u4e0d\u81f3\u4e8e\u50cf\u73b0\u5728\u7684\u843d\u9b44\u6c5f \u6e56\u3001\u51e0\u65e0\u7acb\u8eab\u4e4b\u5730\u7684\u63ba\u51b5\uff0c\u4e0d\u53ef\u540c\u65e5\u800c\u8bed\u4e86\uff01\u2026\u2026\u201d \u3000\u3000\u201c\u53ef\u662f\u201d\u5c55\u767d\u731b\u7136\u8b66\u609f\uff1a\u201c\u9752\u94dc\u5236\u94b1\uff0c\u5df2\u6653\u5f97\u5c31\u662f\u2018\u9752\u86a8\u795e\u2019\u91d1\u4e5d\u7684\u2018\u9752\u86a8\u9556\u2019\u65e0\u7591\uff0c \u8fd9\u5df2\u8bc1\u660e\u91d1\u4e5d\u4fbf\u662f\u6740\u6b7b\u7236\u4eb2\u7684\u4e3b\u51f6\uff0c\u81ea\u5df1\u5192\u9669\u8fdb\u5165\u91d1\u5e9c\uff0c\u867d\u7136\u672a\u80fd\u89c1\u5230\u91d1\u4e5d\uff0c\u4f46\u773c\u524d\u5c11\u4e2d\u5373 \u662f\u91d1\u4e5d\u4e4b\u5f92\uff0c\u6740\u4e86\u4ed6\u96be\u9053\u8fd8\u6015\u91d1\u4e5d\u4e0d\u9732\u9762\uff1f\u826f\u673a\u5c31\u5728\u773c\u524d\uff0c\u6b64\u65f6\u4e0d\u62a5\u6740\u7236\u4e4b\u4ec7\uff0c\u5c1a\u5f85\u4f55 \u65f6\uff1f\u201d\u5c55\u767d\u601d\u81f3\u6b64\u5904\u9661\u7136\u5927\u559d\u4e00\u58f0\uff1a\u201c\u7ad9\u4f4f\uff01\u201d \u3000\u3000\u8fd9\u4e00\u58f0\u66b4\u559d\uff0c\u4e43\u662f\u5c55\u767d\u4ec7\u6068\u4e2d\u6124\u7136\u53d1\u51fa\uff0c\u771f\u529b\u8d2f\u6ce8\uff0c\u58f0\u5982\u7126\u96f7\u3002\u9707\u5f97\u56db\u5468\u4e4b\u4eba\u53cc\u8033\u55e1\u55e1 \u76f4\u54cd\uff0c\u8033\u9f13\u66f4\u662f\u523a\u75db\u6b32\u88c2\u3002 \u3000\u3000\u201c\u7389\u9762\u5c0f\u9752\u86a8\u201d\u88ab\u201c\u94c1\u80cc\u9a7c\u9f99\u201d\u559d\u9000\uff0c\u624d\u62fe\u8d77\u5730\u4e0a\u94c1\u5251\u8f6c\u8eab\u8d70\u51fa\u4e24\u6b65\uff0c\u7a81\u542c\u5c55\u767d\u5927\u8d50\u4e4b \u58f0\uff0c\u53c8\u505c\u6b65\u8f6c\u8eab\uff0c\u6b7b\u76ef\u7740\u5c55\u767d\u6076\u72e0\u72e0\u5730\u8bf4\u9053\uff1a\u7ad9\u4f4f\u5c31\u7ad9\u4f4f\uff0c\u563f\u563f\uff01\u4f60\u4ee5\u4e3a\u90a3\u4e00\u5957\u70c2\u5251\u6cd5\u5c31\u80fd \u80dc\u8fc7\u5c0f\u7237\u5417\uff1f\u90a3\u662f\u5c0f\u7237\u4e00\u65f6\u5931\u624b\uff0c\u624d\u4f7f\u4f60\u6361\u4e86\u4e2a\u4fbf\u5b9c\u3002\u82e5\u4e0d\u662f\u770b\u5728\u516c\u5b59\u524d\u8f88\u9762\u4e0a\uff0c\u4f60\u5c0f\u5b50\u65e9 \u5df2\u6b7b\u5728\u5c0f\u7237\u7684\u2018\u9752\u86a8\u9556\u2019\u4e4b\u4e0b\uff01\u201d \u3000\u3000\u5c55\u767d\u201c\u545b\u201d\u7684\u4e00\u58f0\uff0c\u53c8\u628a\u201c\u65e0\u60c5\u78a7\u5251\u201d\u62bd\u51fa\u9798\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u670d\u6c14\uff0c\u54b1\u4eec\u5c31\u91cd\u65b0\u518d\u6765\uff0c \u5206\u51fa\u4e00\u4e2a\u5f3a\u5f31\u5b58\u4ea1\u6765\uff01\u201d \u3000\u3000\u76c2\u5982\u840d\u4e5f\u628a\u9ed1\u94c1\u5251\u64a4\u81f3\u638c\u4e2d\u9053\uff1a\u201c\u8fd8\u6015\u4e86\u4f60\u4e0d\u6210\uff01\u201d \u3000\u3000\u201c\u5c55\u5c0f\u4fa0\uff01\u201d\u91d1\u5f69\u51e4\u5ffd\u62c9\u5c55\u767d\u5de6\u81c2\uff0c\u6025\u53eb\u76f4\uff1a\u201c\u4f60\u5df2\u8d1f\u4e86\u4f24\uff0c\u4e0d\u8981\u518d\u8ddf\u4ed6\u4e00\u822c\u89c1\u8bc6 \u4e86\u2026\u2026\u201d \u3000\u3000\u8fde\u201c\u94c1\u80cc\u9a7c\u9f99\u201d\u4e5f\u9053\uff1a\u201c\u7b97\u4e86\u7f62\uff0c\u5df2\u7ecf\u89c1\u8bc6\u8fc7\u4e86\uff0c\u4f55\u5fc5\u518d\u62fc\uff1f\u8fd9\u4e0d\u662f\u4ec7\u6740\u2026\u2026\u201d \u3000\u3000\u5c55\u767d\u7529\u81c2\u9707\u5f00\u91d1\u5f69\u98ce\uff0c\u4e00\u9707\u624b\u4e2d\u78a7\u5251\uff0c\u8bf4\u9053\uff1a\u201c\u4eca\u5929\u8c01\u4e5f\u963b\u4e0d\u4e86\uff0c\u4e0d\u662f\u4ed6\u6b7b\uff0c\u5c31\u662f\u6211 \u4ea1\uff01\u201d \u3000\u3000\u4f17\u4eba\u9f50\u60ca\uff0c\u4ee5\u4e3a\u4e24\u4e2a\u9752\u5e74\u4eba\u771f\u662f\u6740\u4e0a\u706b\u6765\u4e86\u3002\u4f46\u5c1a\u672a\u60f3\u5230\u5c55\u767d\u5fc3\u4e2d\u7684\u4ec7\u6068\uff0c\u5b5f\u5982\u840d\u8179\u5185 \u7684\u5992\u706b\uff0c\u65e9\u5df2\u9ad8\u70e7\u4e09\u5343\u4e08\u3002 \u3000\u3000\u201c\u597d\uff01\u201d\u201c\u7389\u9762\u5c0f\u9752\u86a8\u201d\u66b4\u559d\u4e00\u58f0\uff1a\u201c\u5c0f\u7237\u4eca\u5929\u4e5f\u4e0e\u4f60\u62fc\u4e86\uff01\u201d \u3000\u3000\u66b4\u559d\u58f0\u4e2d\uff0c\u817e\u8eab\u800c\u8d77\uff0c\u9ed1\u94c1\u5251\u63a5\u8d77\u4e00\u6e9c\u4e4c\u5149\uff0c\u201c\u8d64\u8679\u8d2f\u65e5\u201d\u731b\u523a\u5c55\u767d\u9762\u95e8\u3002 \u3000\u3000\u5c55\u767d\u5df2\u77e5\u201c\u7389\u9762\u5c0f\u9752\u86a8\u201d\u5185\u529b\u4e0d\u53ca\u81ea\u5df1\u6df1\u539a\uff0c\u5b8c\u5168\u4ed7\u7740\u5251\u62db\u5965\u5999\uff0c\u624d\u548c\u81ea\u5df1\u6253\u4e86\u4e2a\u5e73 \u624b\uff0c\u6050\u6015\u518d\u5931\u53bb\u5148\u673a\uff0c\u6216\u4e45\u6218\u4e0d\u4e0b\uff0c\u89c1\u5b5f\u5982\u840d\u817e\u8eab\u6251\u6765\uff0c\u4e5f\u81ea\u817e\u8eab\u800c\u8d77\uff0c\u5411\u6765\u52bf\u8fce\u53bb\uff0c\u534a\u7a7a \u4e2d\u201c\u6cdb\u6f6e\u5357\u6d77\u201d\uff0c\u201c\u65e0\u60c5\u78a7\u5251\u201d\u821e\u8d77\u4e00\u9762\u5149\u5899\uff0c\u731b\u5411\u5b5f\u5982\u840d\u5251\u4e0a\u5c01\u53bb\u3002 \u3000\u3000\u8fd9\u662f\u6b66\u6797\u7f55\u89c1\u7684\u6253\u6cd5\uff0c\u56db\u5468\u56f4\u89c2\u4e4b\u4eba\u3001\u201c\u94c1\u80cc\u9a7c\u9f99\u201d\u3001\u91d1\u5f69\u51e4\uff0c\u751a\u81f3\u559c\u6012\u4e0d\u5f62\u4e8e\u8272\u6df1\u6c89 \u65e0\u6bd4\u7684\u201c\u94c1\u7ffc\u98de\u9e4f\u201d\u90fd\u4e00\u9f50\u60ca\u547c\u51fa\u58f0\u3002 \u3000\u3000\u4f46\u4e24\u4e2a\u4eba\u76f8\u5bf9\u731b\u6251\uff0c\u53bb\u52bf\u7535\u75be\uff0c\u7eea\u4eba\u60ca\u547c\u672a\u7ade\uff0c\u201c\u545b\u54ea\u201d\u4e00\u58f0\u91d1\u94c1\u4ea4\u9e23\uff0c\u53cc\u5251\u5df2\u731b\u51fb\u5728 \u4e00\u8d77\uff0c\u5251\u5149\u706b\u82b1\u56db\u5c04\u4e4b\u4e0b\uff0c\u4e8c\u4eba\u8eab\u5f62\u4e4d\u5408\u5373\u5206\uff0c\u98d8\u843d\u5730\u4e0a\u3002 \u3000\u3000\u201c\u7389\u9762\u5c0f\u9752\u86a8\u201d\u53ea\u89c9\u534a\u8fb9\u8eab\u5b50\u53d1\u9ebb\uff0c\u864e\u53e3\u75bc\u75db\u5982\u88c2\uff0c\u9ed1\u94c1\u5251\u51e0\u4e4e\u51fa\u624b\uff0c\u843d\u4e0b\u5730\u6765\uff0c\u8ddf\u968f \u6570\u6b65\uff0c\u65b9\u624d\u62ff\u6869\u7ad9\u7a33\u3002 \u3000\u3000\u4f46\u5c55\u767d\u5374\u5982\u65e0\u4e8b\u4e00\u822c\uff0c\u811a\u5c16\u4e00\u70b9\u5730\u9762\uff0c\u5237\uff01\u5237\uff01\u5237\uff01\u2026\u2026\u201c\u65e0\u60c5\u78a7\u5251\u201d\u5982\u72c2\u98ce\u5de8\u6d6a\uff0c\u4e00 \u8fde\u653b\u51fa\u5341\u6570\u5251\u4e4b\u591a\uff0c\u628a\u4e00\u4e2a\u72c2\u50b2\u4e0d\u53ef\u4e00\u4e16\u7684\u201c\u7389\u9762\u5c0f\u9752\u86a8\u201d\u6740\u4e86\u4e2a\u624b\u5fd9\u811a\u4e71\uff0c\u53ea\u6709\u62db\u67b6\u4e4b \u529f\uff0c\u5e76\u65e0\u8fd8\u624b\u4e4b\u529b\u3002 \u3000\u3000\u53ef\u662f\uff0c\u201c\u7389\u9762\u5c0f\u9752\u86a8\u201d\u4ed7\u7740\u8eab\u6cd5\u7075\u6d3b\uff0c\u5251\u62db\u7cbe\u5947\uff0c\u5c55\u767d\u4e00\u62a1\u6025\u653b\uff0c\u867d\u4f7f\u4ed6\u624b\u5fd9\u811a\u4e71\uff0c\u4e00 \u76f4\u9000\u540e\u6570\u6587\uff0c\u4f46\u5374\u6ca1\u6709\u4f24\u5230\u4ed6\u3002 \u3000\u3000\u4e8c\u4eba\u6fc0\u6597\uff0c\u201c\u7389\u9762\u5c0f\u9752\u86a8\u201d\u5df2\u9000\u81f3\u82b1\u5703\u680f\u5e72\u9644\u8fd1\uff0c\u56db\u5468\u56f4\u89c2\u4e4b\u4eba\uff0c\u968f\u7740\u4e8c\u4eba\u6240\u81f3\u4e4b\u5904\uff0c \u56db\u6563\u8eb2\u5f00\u3002 \u3000\u3000\u6b63\u597d\u5c55\u767d\u4e00\u5f0f\u201c\u6a2a\u626b\u4e94\u5cb3\u201d\uff0c\u731b\u5411\u201c\u7389\u9762\u5c0f\u9752\u86d9\u201d\u62e6\u8170\u65a9\u53bb\uff0c\u201c\u7389\u9762\u5c0f\u9752\u86a8\u201d\u8eab\u6cd5\u7075 \u6d3b\uff0c\u4e00\u95ea\u95ea\u81f3\u680f\u4e8e\u540e\u9762\uff0c\u5c55\u767d\u7528\u529b\u8fc7\u731b\u6536\u62db\u4e0d\u4f4f\uff0c\u5251\u8292\u8fc7\u5904\uff0c\u628a\u6446\u5728\u66f2\u680f\u4e0a\u4e00\u6e9c\u5341\u6570\u53ea\u9752\u7389 \u82b1\u76c6\uff0c\u6089\u6570\u65a9\u788e\uff0c\u788e\u679d\u6b8b\u53f6\u4e0e\u74e6\u7247\u5c18\u571f\u9f50\u98de\uff0c\u5a01\u52bf\u597d\u4e0d\u60ca\u4eba\u3002 \u3000\u3000\u8d1f\u8d23\u62a4\u82b1\u9664\u8349\u7684\u56ed\u4e01\u89c1\u72b6\uff0c\u9891\u9891\u987f\u811a\u60ca\u547c\u4e0d\u5df2\uff0c\u6bc1\u574f\u4e86\u8fd9\u5341\u6570\u76c6\u540d\u82b1\uff0c\u4ed6\u4f2f\u4e3b\u4eba\u602a\u7f6a\u4e0b \u6765\u5c31\u5403\u4e0d\u6d88\u4e86\u2026\u2026 \u3000\u3000\u53ef\u662f\uff0c\u7531\u4e8e\u8fd9\u4e00\u6765\uff0c\u201c\u7389\u9762\u5c0f\u9752\u86a8\u201d\u5df2\u7f13\u8fc7\u6c14\u6765\uff0c\u4ece\u680f\u6746\u540e\u7eb5\u8dc3\u800c\u524d\uff0c\u7acb\u523b\u8fd8\u4ee5\u989c\u8272\uff0c \u5237\uff01\u5237\uff01\u5237\uff01\u2026\u2026\u4e00\u8fde\u6025\u653b\u4e86\u5341\u6570\u62db\uff0c\u56e0\u4e3a\u4ed6\u5251\u62db\u7cbe\u5947\uff0c\u62db\u62db\u6307\u5411\u8981\u5bb3\uff0c\u4e5f\u628a\u5c55\u767d\u653b\u4e86\u4e2a\u624b \u5fd9\u811a\u4e71\uff0c\u8282\u8282\u540e\u9000\u3002 \u3000\u3000\u7b49\u5230\u5c55\u767d\u7f13\u8fc7\u624b\u6765\uff0c\u53c8\u662f\u4e00\u756a\u6025\u653b\uff0c\u4e8c\u4eba\u8fd9\u79cd\u6253\u6cd5\uff0c\u53ef\u8bf4\u662f\u6b66\u6797\u4e2d\u4ece\u672a\u89c1\u8fc7\u7684\u6253\u6cd5\u201c\u91d1 \u53a2\u53cc\u94c1\u536b\u201d\u4ee5\u53ca\u91d1\u5e9c\u5176\u4ed6\u6210\u540d\u6c5f\u6e56\u591a\u5e74\u7684\u9ad8\u624b\uff0c\u5927\u98ce\u5927\u6d6a\u7684\u6218\u9635\u4e0d\u77e5\u7ecf\u8fc7\u591a\u5c11\uff0c\u53ef\u4e5f\u6ca1\u89c1\u8fc7 \u8fd9\u7b49\u75af\u72c2\u7684\u6253\u6cd5\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u90fd\u770b\u5f97\u5446\u4e86\uff0c\u8fde\u51fa\u9762\u963b\u6b62\u90fd\u5fd8\u8bb0\u4e86\u2026\u2026 \u3000\u3000\u91d1\u5f69\u51e4\u5728\u4e00\u8fb9\u5374\u6025\u5f97\u516d\u795e\u65e0\u4e3b\uff0c\u81f3\u6b64\uff0c\u5979\u624d\u660e\u767d\uff0c\u5979\u5fc3\u4e2d\u65e2\u4e0d\u613f\u5b5f\u5982\u840d\u4f24\u53ca\u5c55\u767d\uff0c\u53c8\u6015 \u5c55\u767d\u4f24\u53ca\u5b5f\u5982\u840d\uff0c\u4e0d\u7ba1\u600e\u4e48\u8bf4\uff0c\u6240\u6709\u4f17\u4eba\u4e4b\u4e2d\uff0c\u5979\u662f\u6700\u7126\u6025\u7684\u4e00\u4e2a\u3002 \u3000\u3000\u53ef\u662f\uff0c\u4e8c\u4eba\u72b6\u5982\u75af\u864e\uff0c\u4efb\u5979\u53eb\u54d1\u4e86\u5589\u5499\uff0c\u53ea\u662f\u4e0d\u7406\u4e0d\u776c\uff0c\u4e00\u5473\u5730\u72e0\u6597\u3002 \u3000\u3000\u4e8c\u4eba\u6253\u6cd5\u5947\u7279\uff0c\u4e00\u4e2a\u4ed7\u7740\u8eab\u6cd5\u7075\u6d3b\uff0c\u5251\u62db\u7cbe\u5947\uff1b\u4e00\u4e2a\u4ed7\u7740\u5185\u529b\u96c4\u539a\uff0c\u5251\u6cd5\u535a\u5927\uff0c\u7ffb\u7ffb\u6eda \u6eda\uff0c\u628a\u6574\u4e2a\u5ead\u9662\u6253\u5f97\u4e71\u4e03\u516b\u7cdf\uff0c\u8eab\u5230\u5904\uff0c\u6309\u5012\u5899\u9893\uff1b\u5251\u8fc7\u5904\uff0c\u65f6\u6e85\u82b1\u98de\uff0c\u4e00\u6240\u7e41\u82b1\u9c9c\u8349\u3001\u66f2 \u680f\u6731\u6237\u7684\u5e7d\u96c5\u5ead\u9662i\u8f6c\u8ddf\u95f4\u6253\u5f97\u4e00\u7247\u96f6\u4e71\uff0c\u9762\u76ee\u5168\u975e\u3002 \u3000\u3000\u6643\u773c\u95f4\uff0c\u4e8c\u4eba\u5df2\u4e92\u76f8\u653b\u51fa\u4e86\u4e00\u767e\u4f59\u62db\uff0c\u5c31\u662f\u4e8c\u767e\u4f59\u5408\u4e86\uff0c\u4ecd\u7136\u662f\u80dc\u8d1f\u96be\u5206\uff0c\u4e0d\u5c11\u4eba\u5728\u70b9 \u5934\u8d5e\u53f9\uff0c\u8ba4\u4e3a\u662f\u751f\u5e73\u4ec5\u89c1\u4e4b\u6076\u6218\u3002 \u3000\u3000\u4e0d\u5c11\u4eba\u5728\u6697\u6697\u803d\u5fc3\uff0c\u4e0d\u77e5\u4e8c\u4eba\u6253\u5230\u6700\u540e\uff0c\u5982\u4f55\u6536\u573a\uff1f \u3000\u3000\u201c\u91d1\u5e9c\u53cc\u94c1\u536b\u201d\u5df2\u770b\u5165\u795e\uff0c\u5fd8\u8bb0\u4e8c\u4eba\u6240\u8d1f\u7684\u8d23\u4efb\u4e3a\u4f55\uff0c\u201c\u94c1\u80cc\u9a7c\u9f99\u201d\u624b\u637b\u7eb5\u987b\uff0c\u8fde\u8fde\u9053 \u597d\uff0c\u201c\u94c1\u7ffc\u98de\u9e4f\u201d\u9634\u6c89\u7684\u8138\u4e0a\uff0c\u4e5f\u9732\u51fa\u4e86\u65e0\u6bd4\u6fc0\u594b\u4e4b\u8272\uff0c\u4e00\u53cc\u7cbe\u8292\u5c0f\u773c\u77aa\u5f97\u5706\u6eda\uff0c\u671b\u7740\u573a\u4e2d \u4e8c\u4eba\u75af\u72c2\u53ae\u6740\u3002 \u3000\u3000\u7684\u95f4\u4e00\u4e45\u3002\u201c\u7389\u9762\u5c0f\u9752\u86a8\u201d\u5185\u529b\u4e0d\u53ca\u5c55\u767d\u6df1\u539a\uff0c\u989d\u4e0a\u5df2\u6d41\u4e0b\u6c57\u73e0\u6765\u3002 \u3000\u3000\u5c55\u767d\u5374\u662f\u6108\u6218\u6108\u52c7\uff0c\u867d\u7136\u5de6\u80a9\u4f24\u53e3\u9c9c\u8840\u957f\u6d41\uff0c\u4ed6\u4e5f\u53ea\u4e00\u5473\u731b\u653b\uff0c\u597d\u50cf\u4ed6\u7684\u5185\u529b\u6108\u6253\u6108\u589e \u52a0\uff0c\u4e00\u67c4\u201c\u65e0\u60c5\u78a7\u5251\u201d\u6325\u821e\u5f97\u5982\u72c2\u98ce\u9aa1\u96e8\u4e00\u822c\uff0c\u78a7\u6f84\u6f84\u7684\u5251\u5149\u6bd2\u8292\uff0c\u5728\u706f\u5149\u7167\u8000\u4e4b\u4e0b\uff0c\u604d\u5982 \u4e00\u7247\u5251\u5c71\uff0c\u631f\u7740\u864e\u864e\u98ce\u58f0\uff0c\u731b\u5411\u201c\u7389\u9762\u5c0f\u9752\u86a8\u201d\u5b5f\u5982\u840d\u6d12\u843d\u3002 \u3000\u3000\u5c55\u81ea\u7684\u5251\u5149\u5df2\u628a\u5b5f\u5982\u840d\u7684\u8eab\u5f71\u5305\u56f4\u8d77\u6765\uff0c\u5b5f\u5982\u840d\u53ea\u89c1\u56db\u5468\u90fd\u662f\u68ee\u68ee\u5251\u5149\uff0c\u538b\u529b\u6108\u6765\u6108 \u5927\uff0c\u4e0d\u7531\u5fc3\u5185\u7126\u6025\uff0c\u77e5\u9053\u8fd9\u6837\u6253\u4e0b\u53bb\uff0c\u81ea\u5df1\u975e\u843d\u8d25\u4e0d\u53ef\uff0c\u8fd9\u5df2\u4e0d\u662f\u4e89\u4e00\u53e3\u6c14\u7684\u6bd4\u6b66\u8f83\u91cf\uff0c\u800c \u662f\u4e00\u573a\u751f\u6b7b\u5b58\u4ea1\u7684\u640f\u6597\uff0c\u7740\u5e01\u628a\u59d3\u5c55\u7684\u5c0f\u5b50\u6253\u5012\uff0c\u5931\u53bb\u5fc3\u4e0a\u4eba\u3001\u4e22\u8138\u7684\u4e8b\u8fd8\u5c0f\uff0c\u8fde\u6027\u547d\u90fd\u8981 \u4e0d\u6df1\u3002 \u3000\u3000\u4e0d\u7531\u4e00\u54ac\u7259\uff0c\u6697\u4e0b\u6740\u5fc3\uff0c\u5728\u52a8\u624b\u4e4b\u95f4\uff0c\u63a2\u624b\u9556\u56ca\uff0c\u53d6\u4e86\u4e00\u628a\u201c\u9752\u86a8\u9556\u201d\u5728\u624b\u4e2d\u3002 \u3000\u3000\u91d1\u5f69\u51e4\u5230\u6b64\u65f6\u53eb\u4e0d\u51fa\u58f0\u97f3\u6765\u4e86\uff0c\u53ef\u80fd\u56e0\u5c55\u767d\u5df2\u5360\u4e0a\u98ce\uff0c\u5979\u4ece\u5c55\u767d\u795e\u60c5\u4e0a\u770b\u5f97\u51fa\uff0c\u5c55\u767d\u5982 \u83b7\u80dc\uff0c\u662f\u4e0d\u4f1a\u518d\u5251\u4e0b\u7559\u60c5\u7684\uff0c\u800c\u975e\u628a\u5b5f\u5982\u840d\u6740\u6b7b\u4e0d\u53ef\u3002 \u3000\u3000\u800c\u5b5f\u5982\u840d\u7684\u795e\u8272\u4e00\u6837\u6076\u6bd2\uff0c\u53c8\u628a\u7236\u4eb2\u4f20\u4ed6\u7684\u8ffd\u547d\u6697\u5668\u201c\u9752\u86a8\u9556\u201d\u63e1\u5728\u638c\u4e2d\uff0c\u53ea\u8981\u201c\u9752\u86a8 \u9556\u201d\u4e00\u51fa\u624b\uff0c\u5c55\u767d\u4ea6\u662f\u975e\u6b7b\u5fc5\u4f24\u3002 \u3000\u3000\u53ef\u662f\uff0c\u8fd9\u4e8c\u4eba\u7684\u6b7b\u4f24\u90fd\u975e\u5979\u6240\u613f\uff0c\u53ea\u6025\u5f97\u82b3\u5fc3\u65e0\u4e3b\uff0c\u82b1\u5bb9\u60e8\u53d8\uff0c\u5979\u7d20\u5e38\u4ee5\u4ece\u5bb9\u9547\u5b9a\u800c\u8457 \u540d\uff0c\u6b64\u65f6\u5374\u60ca\u714c\u5931\u63aa\u4e86\uff0c\u53ea\u6025\u5f97\u56e2\u56e2\u4e71\u8f6c\u3002\u4f46\u65f6\u95f4\u4e00\u4e45\uff0c\u7ec8\u4e8e\u5979\u7684\u5934\u8111\u8fd8\u662f\u806a\u660e\u5730\u60f3\u5230\u4e86 \u201c\u91d1\u5e9c\u53cc\u94c1\u536b\u201d\uff0c\u54e5\u54e5\u4e0d\u5728\uff0c\u5e9c\u4e0a\u7684\u4eba\u53ea\u6709\u4ed6\u4e8c\u4eba\u6709\u80fd\u529b\u6392\u89e3\u8fd9\u573a\u7ea0\u7eb7\uff0c\u4e8e\u662f\uff0c\u5979\u671b\u7740\u573a\u4e2d \u9ed8\u9ed8\u51fa\u795e\u7684\u201c\u94c1\u7ffc\u98de\u9e4f\u201d\u53eb\u9053\uff1a\u201c\u5df4\u4e8c\u53d4\uff01\u8bf7\u4f60\u5236\u6b62\u4ed6\u4eec\u5427\uff01\u4e0d\u7136\u2026\u2026\u201d \u3000\u3000\u5979\u770b\u5230\u201c\u94c1\u7ffc\u98de\u9e4f\u201d\u9634\u6c89\u7740\u8138\u8272\uff0c\u4e24\u8ddf\u671b\u5b9a\u573a\u4e2d\uff0c\u5bf9\u5979\u7684\u8bdd\u604d\u5982\u672a\u95fb\uff0c\u5979\u624d\u60f3\u8d77\u8fd9\u5df4\u4e8c \u53d4\u751f\u6027\u602a\u6d8e\uff0c\u5e38\u5e38\u505a\u4e9b\u4f7f\u4eba\u4e0d\u53ef\u7406\u89e3\u7684\u602a\u4e8b\u6765\uff0c\u627e\u4ed6\u6392\u89e3\u4e0d\u6210\uff0c\u518d\u751f\u5176\u4ed6\u6821\u8282\uff0c\u90a3\u66f4\u53eb\u4eba\u5934 \u75db\uff0c\u4e8e\u662f\uff0c\u53ea\u8bf4\u4e86\u4e00\u534a\uff0c\u53c8\u8f6c\u5934\u5411\u201c\u94c1\u80cc\u9a7c\u9f99\u201d\u8bf4\u9053\uff1a\u201c\u516c\u5b59\u5927\u53d4\uff0c\u8bf7\u60a8\u51fa\u9762\uff0c\u4e0d\u8981\u518d\u8ba9\u4ed6 \u4e8c\u4eba\u6253\u4e86\uff01\u201d \u3000\u3000\u201c\u54c8\u54c8\uff01\u201d\u201c\u94c1\u80cc\u9a7c\u9f99\u201d\u516c\u5b59\u695a\u7709\u98de\u8272\u821e\uff0c\u8c6a\u5174\u6e4d\u98de\u5730\u8bf4\u9053\uff1a\u201c\u59d1\u5a18\u4f60\u653e\u5fc3\uff0c\u4ed6\u4e8c\u4eba\u867d \u6253\u5f97\u6fc0\u70c8\uff0c\u4e00\u65f6\u4e4b\u95f4\u8fd8\u5206\u4e0d\u51fa\u2026\u2026\u54ce\u5440\uff01\u201d \u3000\u3000\u201c\u94c1\u80cc\u9a7c\u9f99\u201d\u4e0e\u91d1\u5f69\u98ce\u8bdd\u8fd8\u672a\u8bf4\u5b8c\uff0c\u53ea\u542c\u4e00\u58f0\u60e8\u66dd\uff0c\u5251\u5149\u8fc7\u5904\uff0c\u6e85\u8d77\u4e00\u84ec\u8840\u96e8\uff0c\u201c\u7389\u9762 \u5c0f\u9752\u86a8\u201d\u8138\u8272\u60e8\u767d\uff0c\u4e00\u6761\u5de6\u81c2\u5df2\u9f50\u80a9\u800c\u65ad\uff0c\u53f3\u624b\u6267\u5251\uff0c\u6447\u6447\u6b32\u5012\uff01 \u3000\u3000\u4e00\u89c1\u4e3b\u4eba\u7684\u7231\u5f92\u53d7\u4f24\uff0c\u91d1\u5e9c\u4e0a\u4e0b\u4eba\u7b49\u9f50\u58f0\u60ca\u547c\u2026\u2026 \u3000\u3000\u539f\u6765\u91d1\u5f69\u51e4\u592e\u8bf7\u201c\u94c1\u80cc\u9a7c\u9f99\u201d\u51fa\u9762\u5236\u6b62\u4e8c\u4eba\u6076\u6597\u4e4b\u9645\uff0c\u5c55\u767d\u89c1\u201c\u7389\u9762\u5c0f\u9752\u86a8\u201d\u63a2\u624b\u53d6 \u9556\uff0c\u63a5\u8fde\u51e0\u5251\u731b\u653b\uff0c\u7528\u51fa\u4e00\u62db\u201c\u5f69\u7ebf\u659c\u629b\u201d\uff0c\u8fd9\u4e00\u62db\u4e43\u662f\u201c\u8ffd\u98ce\u5251\u201d\u6cd5\u4e2d\u7684\u7edd\u62db\uff0c\u865a\u91cc\u6709 \u5b9e\uff0c\u5b9e\u91cc\u6709\u865a\uff0c\u770b\u662f\u65a9\u5b5f\u5982\u840d\u53f3\u81c2\uff0c\u7b49\u5230\u5b5f\u5982\u840d\u4e3e\u5251\u4e00\u5c01\uff0c\u8eab\u5f62\u5de6\u8f6c\uff0c\u6b63\u6b32\u501f\u673a\u4f1a\u628a\u63e1\u5728\u5de6 \u624b\u7684\u201c\u9752\u86a8\u9556\u201d\u65bd\u51fa\u4e4b\u9645\uff01 \u3000\u3000\u5c55\u767d\u78a7\u5251\u534a\u7a7a\uff0c\u659c\u5288\u731b\u5411\u5b5f\u5982\u840d\u5de6\u80a9\u780d\u843d\u3002 \u3000\u3000\u5b5f\u5982\u840d\u8eb2\u907f\u4e0d\u53ca\uff0c\u4e00\u6761\u5de6\u81c2\u5df2\u88ab\u5c55\u767d\u4e00\u5251\u9f50\u80a9\u524a\u65ad\u3002 \u3000\u3000\u90a3\u63e1\u5728\u624b\u4e2d\u7684\u201c\u9752\u86a8\u9556\u201d\u4e5f\u6d12\u4e86\u6ee1\u5730\u3002 \u3000\u3000\u53ef\u8bf4\u4e8b\u6709\u51d1\u5de7\uff0c\u5982\u4e0d\u662f\u91d1\u5f69\u51e4\u627e\u201c\u94c1\u80cc\u9a7c\u9f99\u201d\u8bf4\u60c5\uff0c\u201c\u94c1\u80cc\u9a7c\u9f99\u201d\u4e0d\u81f4\u5206\u795e\u4ed6\u987e\uff0c\u4e5f\u4e0d \u4f1a\u8ba9\u5c55\u767d\u4f24\u5230\u5b5f\u5982\u840d\u3002 \u3000\u3000\u201c\u94c1\u7ffc\u98de\u9e4f\u201d\u867d\u770b\u5230\u4e86\uff0c\u4ed6\u5374\u4e0d\u4f38\u624b\u62e6\u963b\uff0c\u56e0\u4e3a\u4ed6\u8ba4\u4e3a\u53cc\u65b9\u6253\u6597\uff0c\u4e00\u65b9\u843d\u8d25\u88ab\u6740\uff0c\u90a3\u662f \u6d3b\u8be5\uff0c\u53ea\u80fd\u6028\u81ea\u5df1\u5b66\u827a\u4e0d\u7cbe\uff0c\u6028\u4e0d\u5f97\u65c1\u4eba\uff0c\u800c\u4e14\uff0c\u4e0d\u89c1\u80dc\u8d25\u4f24\u4ea1\uff0c\u5c31\u662f\u4ed6\u4eb2\u513f\u5b50\uff0c\u4ed6\u4e5f\u4e0d \u7ba1\uff0c\u56e0\u4e3a\u4ed6\u8ba4\u4e3a\u53ae\u6740\u4e0d\u89c1\u8840\u4fbf\u4e0d\u8fc7\u763e\u3002 \u3000\u3000\u201c\u94c1\u80cc\u9a7c\u9f99\u201d\u89c1\u5230\u7978\u95ef\u5927\u4e86\uff0c\u66b4\u559d\u4e00\u58f0\uff0c\u817e\u8eab\u800c\u8d77\uff0c\u6251\u5411\u573a\u4e2d\uff0c\u534a\u7a7a\u6325\u51fa\u4e00\u638c\uff0c\u76f4\u6253\u5c55 \u767d\uff0c\u8eab\u5f62\u5374\u5411\u5b5f\u5982\u840d\u7a77\u53bb\u3002\u4ed6\u662f\u6015\u5c55\u767d\u8d81\u5b5f\u5982\u840d\u8d1f\u4f24\u4e4b\u4e0b\uff0c\u518d\u65bd\u8fa3\u624b\uff0e \u3000\u3000\u4f46\u201c\u94c1\u7ffc\u98de\u9e4f\u201d\u6bd4\u201c\u94c1\u80cc\u9a7c\u9f99\u201d\u66f4\u5feb\uff0c\u94c1\u7ffc\u5c55\u5904\uff0c\u75be\u5982\u98de\u9e1f\uff0c\u4e2d\u7a7a\u4e2d\u94c1\u7ffc\u731b\u6325\uff0c\u5982\u6cf0\u5c71 \u538b\u9876\u4e00\u822c\uff0c\u731b\u5411\u5c55\u767d\u5934\u4e0a\u63a0\u4e0b\u3002 \u3000\u3000\u4e24\u80a1\u5e9e\u5927\u81f3\u6781\u7684\u52b2\u98ce\u72c2\u98d9\uff0c\u6b63\u597d\u4e00\u5de6\u4e00\u53f3\uff0c\u9f50\u5411\u5c55\u767d\u5377\u81f3\u3002 \u3000\u3000\u5c55\u767d\u89c1\u6765\u52bf\u60ca\u4eba\uff0c\u4e0d\u6562\u786c\u63a5\uff0c\u98de\u8eab\u8dc3\u51fa\u4e00\u4e08\u5f00\u5916\u3002 \u3000\u3000\u201c\u562d\u201d\u4e00\u58f0\u5de8\u54cd\uff0c\u201c\u53cc\u94c1\u536b\u201d\u4e00\u7ffc\u4e00\u638c\uff0c\u4e24\u80a1\u529b\u9053\u534a\u7a7a\u76f8\u649e\uff0c\u4f59\u529b\u56db\u6fc0\uff0c\u98de\u7070\u626c\u5c18\u3002 \u3000\u3000\u8fd9\u8fd8\u662f\u4e8c\u4eba\u53d1\u89c9\u5f97\u65e9\uff0c\u5378\u53bb\u5927\u534a\u529b\u9053\uff0c\u4f46\u4ecd\u6709\u5982\u6b64\u5a01\u52bf\uff0c\u4e8c\u4eba\u529f\u529b\u6df1\u539a\uff0c\u7684\u786e\u60ca\u4eba\u3002 \u3000\u3000\u5c55\u767d\u5374\u6602\u7136\u4e0d\u60e7\uff0c\u6a2a\u5251\u8bf4\u9053\uff1a\u201c\u662f\u4e0d\u662f\u4e8c\u4f4d\u524d\u8f88\uff0c\u4e5f\u60f3\u8d50\u6559\uff1f\u201d \u3000\u3000\u201c\u94c1\u80cc\u9a7c\u9f99\u201d\u5148\u70b9\u5b5f\u5982\u840d\u80a9\u4e0a\u7a74\u9053\uff0c\u4e3a\u4ed6\u6b62\u8840\uff0c\u518d\u53eb\u4e86\u4e24\u540d\u4e0b\u4eba\uff0c\u6276\u6301\u4e0b\u53bb\u88f9\u4f24\u4e0a \u836f\u2026\u2026 \u3000\u3000\u201c\u94c1\u7ffc\u98de\u9e4f\u201d\u5374\u51b7\u51b7\u5730\u8bf4\u9053\uff1a\u201c\u5c0f\u5b50\uff01\u4f60\u81ea\u5df1\u4e5f\u65ad\u53bb\u4e00\u81c2\uff0c\u7701\u5f97\u8001\u592b\u52a8\u624b\uff01\u201d\u5c55\u767d\u5374\u5251 \u7709\u4e00\u8038\uff0c\u4f46\u5c1a\u672a\u7b49\u5c55\u767d\u53d1\u8a00\uff0c\u201c\u94c1\u80cc\u9a7c\u9f99\u201d\u5374\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5df4\u8001\u4e8c\uff01\u4ed6\u4eec\u665a\u4e00\u8f88\u7684\u4e8b\uff0c\u8ba9\u4ed6 \u4eec\u81ea\u884c\u5904\u7406\u53bb\uff0c\u4f55\u5fc5\u6211\u4eec\u591a\u4e8b\uff0c\u88ab\u6c5f\u6e56\u4e0a\u4f20\u51fa\u53bb\uff0c\u8fd8\u8bf4\u6211\u4eec\u4ee5\u5927\u6b3a\u5c0f\u5462\uff01\u201d \u3000\u3000\u201c\u7389\u9762\u5c0f\u9752\u86a8\u201d\u5728\u4e24\u540d\u4e0b\u4eba\u6276\u6301\u4e4b\u4e0b\uff0c\u4e34\u8d70\u65f6\u5bf9\u5c55\u767d\u6076\u72e0\u72e0\u5730\u8bf4\u9053\uff1a\u201c\u8fd9\u65ad\u81c2\u4e4b\u4ec7\uff0c\u5b5f \u5982\u840d\u7ec8\u8eab\u4e0d\u5fd8\uff01\u201d \u3000\u3000\u5c55\u767d\u9053\uff1a\u201c\u5728\u4e0b\u968f\u65f6\u5019\u6559\uff01\u201d\u201c\u94c1\u7ffc\u98de\u9e4f\u201d\u4f3c\u662f\u6781\u542c\u201c\u94c1\u80cc\u9a7c\u9f99\u201d\u7684\u8bdd\uff0c\u95fb\u8a00\u4e0d\u518d\u51fa \u58f0\u3002 \u3000\u3000\u91d1\u5f69\u51e4\u5374\u82b1\u5bb9\u60e8\u6de1\uff0c\u4e0d\u77e5\u5982\u4f55\u662f\u597d\u2026\u2026 \u3000\u3000\u5c55\u767d\u5374\u53c8\u5411\u201c\u94c1\u80cc\u9a7c\u9f99\u201d\u4e00\u62b1\u62f3\uff0c\u8bf4\u9053\uff1a\u201c\u8001\u524d\u8f88\uff0c\u5982\u679c\u6ca1\u6709\u4ec0\u4e48\u5429\u5490\uff0c\u5728\u4e0b\u544a\u8f9e\u4e86\uff01 \u3000\u3000\u201c\u94c1\u80cc\u9a7c\u9f99\u201d\u5fae\u4e00\u6014\u795e\u9053\uff1a\u201c\u5c0f\u54e5\u513f\uff0c\u8bf7\u7b49\u5230\u660e\u5929\uff0c\u516c\u5b50\u56de\u6765\u518d\u8d70\u4e0d\u597d\u5417\uff1f\u201d \u3000\u3000\u201c\u5728\u4e0b\u5c1a\u6709\u6025\u4e8b\uff0c\u4e0d\u80fd\u518d\u7b49\uff0c\u201d\u5c55\u767d\u8bf4\u9053\uff1a\u201c\u4e00\u5207\u5305\u6db5\uff0c\u8c22\u8c22\u8001\u524d\u8f88\uff0c\u5728\u4e0b\u544a\u8f9e\u4e86\uff01\u201d \u3000\u3000\u8bf4\u7f62\uff0c\u201c\u65e0\u60c5\u78a7\u5251\u201d\u5165\u9798\uff0c\u62b1\u62f3\u4e00\u793c\uff0c\u56de\u8eab\u4fbf\u8d70\u2026\u2026 \u3000\u3000\u201c\u5c55\u5c0f\u4fa0\u2026\u2026\u201d\u91d1\u5f69\u51e4\u6025\u53eb\u4e09\u58f0\uff0c\u6b32\u8a00\u53c8\u6b62\u3002\u5c55\u767d\u5374\u5934\u4e5f\u4e0d\u56de\u5730\u5411\u95e8\u5916\u8d70\u53bb\u3002 \u3000\u3000\u201c\u94c1\u80cc\u9a7c\u9f99\u201d\u73af\u76ee\u53cc\u7741\uff0c\u77aa\u7740\u5c55\u767d\u7684\u80cc\u5f71\u6108\u8d70\u6108\u8fdc\uff0c\u7ec8\u4e8e\u6d88\u5931\u5728\u95e8\u5916\uff0c\u4ed6\u5374\u6ca1\u6709\u53d1\u58f0\u963b \u6b62\u2026\u2026 \u3000\u3000\u5c55\u767d\u51fa\u4e86\u91d1\u5e9c\uff0c\u8fde\u5e97\u4e5f\u4e0d\u4f4f\uff0c\u8e0f\u7740\u591c\u8272\u76f4\u5954\u5ca9\u5c71\u5341\u4e8c\u6d1e\u800c\u53bb\u3002 \u3000\u3000\u6c5f\u8fb9\u4e00\u94a9\u65b0\u6708\uff0c\u6c5f\u6c34\u5954\u6d41\uff0c\u6708\u5149\u4e2d\u71d5\u5b50\u77fe\u77d7\u7acb\u5728\u6c5f\u8fb9\uff0c\u771f\u5982\u4e00\u53ea\u5de8\u5927\u65e0\u670b\u7684\u98de\u9e1f\uff0c\u51e0\u6b32 \u51b2\u5929\u800c\u53bb\u3002 \u3000\u3000\u6c5f\u98ce\u4e60\u4e60\uff0c\u8925\u6691\u5168\u6d88\uff0c\u6c5f\u9762\u4e0a\u4e24\u4e09\u6e14\u706b\uff0c\u591c\u8272\u5b9c\u4eba\uff0c\u5c55\u767d\u4e0d\u7981\u957f\u957f\u5730\u5401\u4e86\u4e00\u53e3\u6c14\u3002 \u3000\u3000\u6b64\u65f6\uff0c\u4ed6\u516c\u91cc\u6709\u6ee1\u610f\u4e5f\u6709\u5931\u610f\uff0c\u6709\u6b22\u559c\u4e5f\u6709\u60c6\u6005\uff0c\u6cbf\u6c5f\u8d70\u6765\uff0c\u7b80\u76f4\u4e0d\u77e5\u5fc3\u4e2d\u662f\u4f55\u6ecb\u5473\u3002 \u3000\u3000\u6ee1\u610f\u7684\u662f\u81ea\u5df1\u6b66\u529f\u5927\u6709\u8fdb\u5883\uff0c\u7adf\u80fd\u6218\u8d25\u4e86\u201c\u9752\u86a8\u795e\u201d\u91d1\u4e5d\u7684\u4eb2\u4f20\u5f1f\u5b50\u3002 \u3000\u3000\u5931\u610f\u7684\u662f\u770b\u5230\u201c\u91d1\u5e9c\u53cc\u94c1\u536b\u201d\u7684\u6b66\u529f\uff0c\u5b9e\u5728\u592a\u9ad8\uff0c\u51ed\u81ea\u5df1\u4e07\u4e0d\u662f\u4eba\u5bb6\u7684\u5bf9\u624b\uff0c\u4f55\u51b5\u91d1\u5e9c \u9ad8\u624b\u5982\u4e91\uff0c\u4ee5\u81ea\u5df1\u4e00\u5df1\u4e4b\u529b\uff0c\u62a5\u7236\u4ec7\u53ef\u8bf4\u662f\u7edd\u65e0\u5e0c\u671b\u3002\u81f3\uff0c\u4ed6\u5374\u4e0d\u80fd\u786e\u5207\u5730\u8bf4\u51fa\u6765\uff0c\u53ea\u662f\u8111 \u6d77\u4e2d\u5e38\u5e38\u6d6e\u73b0\u51fa\u91d1\u5f69\u51e4\u7f8e\u903e\u5929\u4eba\u7684\u5f71\u5b50\uff0c\u4ee5\u53ca\u5367\u75c5\u65f6\u91d1\u5f69\u51e4\u5bf9\u4ed6\u6b3e\u6b3e\u7684\u6df1\u60c5\uff0c\u4f7f\u4ed6\u516c\u91cc\u6709\u751c \u4e5f\u6709\u82e6\uff0c\u6709\u559c\u4e5f\u6709\u5fe7\u2026\u2026 \u3000\u3000\u5c55\u767d\u6ee1\u8179\u5fc3\u4e8b\uff0c\u6f2b\u6b65\u8d70\u4e0a\u5ca9\u5c71\u5341\u4e8c\u6d1e\u7684\u5d0e\u5c96\u5c71\u8def\uff0c\u4ed6\u6240\u4ee5\u4e0d\u6025\u7740\u8d70\uff0c\u662f\u56e0\u4e3a\u4e0d\u77e5\u9053\u201c\u795e \u9a74\u94c1\u80c6\u201d\u786e\u5207\u7684\u843d\u811a\u4e4b\u5904\uff0c\u624d\u6162\u6162\u5730\u5bfb\u4e0a\u5c71\u6765\u3002 \u3000\u3000\u4ed6\u5df2\u7ecf\u8f6c\u8fc7\u4e86\u4e09\u4e2a\u77f3\u6d1e\uff0c\u9664\u4e86\u5728\u4e00\u4e2a\u6d1e\u91cc\u60ca\u8d77\u4e00\u53ea\u8759\u8760\u4ee5\u5916\uff0c\u5176\u4f59\u4e00\u65e0\u6240\u89c1\u3002 \u3000\u3000\u4ed6\u770b\u8fd9\u4e9b\u77f3\u6d1e\uff0c\u867d\u662f\u8352\u6d1e\uff0c\u4f46\u6bcf\u6d1e\u5747\u6709\u6e38\u4eba\u7559\u4e0b\u7684\u75d5\u8ff9\uff0c\u6709\u7684\u77f3\u58c1\u4e0a\u9898\u7740\u201c\u67d0\u4e2d\u67d0\u6708\u67d0 \u4eba\u5230\u6b64\u4e00\u6e38\u201d\uff0c\u4ee5\u53ca\u6a2a\u4e03\u7ad6\u516b\u5728\u58c1\u4e0a\u9898\u7684\u6b6a\u8bd7\u4e4b\u5916\uff0c\u5730\u4e0b\u8fd8\u6709\u679c\u76ae\u3001\u7eb8\u5c51\u7b49\u7269\u3002\u201c\u795e\u9a74\u94c1 \u80c6\u201d\u65e2\u662f\u98ce\u5c18\u4fa0\u9690\u4e00\u6d41\u7684\u4eba\u7269\uff0c\u7edd\u4e0d\u4f1a\u4f4f\u5728\u6e38\u4eba\u70e6\u56a3\u4e4b\u5730\uff0c\u4e0d\u7981\u5fc3\u4e2d\u6697\u6697\u5931\u671b\uff0c\u6697\u60f3\u201c\u6d3b\u6b7b \u4eba\u201d\u53ef\u80fd\u6d88\u606f\u4e0d\u786e\uff0c\u8ba9\u81ea\u5df1\u767d\u8dd1\u8fd9\u4e00\u8d9f\u2026\u2026 \u3000\u3000\u4f46\u4ed6\u53c8\u4e0d\u80fd\u7070\u5fc3\uff0c\u660e\u77e5\u65e0\u671b\uff0c\u4ecd\u5728\u5c71\u8def\u4e0a\u6328\u6d1e\u641c\u53bb\uff0c\u53c8\u6251\u7a7a\u4e86\u4e09\u4e2a\u77f3\u6d1e\uff0c\u5df2\u6df1\u5165\u5c71\u533a \u4e86\u3002 \u3000\u3000\u8f6c\u8fc7\u4e00\u5ea7\u5cf0\u5934\uff0c\u7a81\u7136\u542c\u5230\u4e00\u4e2a\u6781\u4e3a\u6e05\u8106\u7684\u5c11\u5973\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u8fd9\u56de\u4e0d\u7b97\uff0c\u91cd\u6765\uff01\u201d \u3000\u3000\u63a5\u7740\u4e00\u4e2a\u82cd\u8001\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u4f60\u8fd9\u5973\u5a03\u513f\uff0c\u82b1\u6837\u592a\u591a\uff0c\u62ff\u7740\u6211\u8001\u4eba\u5bb6\u5f53\u7334\u5b50\u800d\u4e86\uff0c\u4e0d \u5e72\uff01\u4e0d\u5e72\uff01\u201d \u3000\u3000\u53c8\u4e00\u4e2a\u82cd\u8001\u7684\u58f0\u97f3\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u4e0d\u8981\u60f3\u8d56\uff01\u4e0d\u7167\u6837\u6765\u4e00\u904d\uff0c\u5c31\u5f97\u8ba4\u8f93\uff01\u201d \u3000\u3000\u539f\u5148\u82cd\u8001\u7684\u58f0\u97f3\u9053\uff1a\u201c\u6ca1\u6709\u90a3\u4e48\u4fbf\u5b9c\uff01\u522b\u770b\u6211\u8001\u4eba\u5bb6\u7f3a\u811a\u5c11\u817f\uff0c\u8fd9\u4e00\u5957\u8fd8\u96be\u4e0d\u5012\u6211\uff01\u201d \u3000\u3000\u8bf4\u5230\u8fd9\u91cc\uff0c\u9690\u9690\u4f20\u6765\u8863\u631f\u98d8\u98ce\u4e4b\u58f0\uff01 \u3000\u3000\u5c55\u767d\u542c\u5230\u8fd9\u4e00\u5c11\u4e8c\u8001\u8bf4\u8bdd\u7684\u58f0\u97f3\uff0c\u5f88\u662f\u719f\u6089\uff0c\u4f46\u4e00\u65f6\u53c8\u60f3\u4e0d\u8d77\u662f\u54ea\u4e9b\u4eba\u6765\uff0c\u53c8\u5947\u602a\u4e09\u4eba \u534a\u591c\u91cc\u5728\u6df1\u5c71\u8d4c\u4ec0\u4e48\u3002\u60f3\u5230\u8fd9\u91cc\uff0c\u597d\u5947\u6c0f\u7684\u5207\u75ea\u5411\u53d1\u8bdd\u4e4b\u5904\u8d76\u53bb\uff0c\u8d70\u5230\u5c31\u8fd1\uff0c\u624d\u6f5c\u8db3\u9690\u5f62\uff0c \u501f\u7740\u5c71\u4e0a\u6811\u6728\u7684\u63a9\u62a4\uff0c\u7f13\u7f13\u63a5\u8fd1\u3002\u524d\u8fb9\u662f\u4e00\u4e2a\u5c71\u515c\uff0c\u5c71\u515c\u91cc\u6709\u4e00\u5757\u5e73\u6574\u7684\u5de8\u77f3\uff0c\u9ad8\u7ea6\u4e09\u6587\uff0c \u65b9\u5706\u5c06\u8fd1\u5341\u4e08\uff0c\u56db\u8fb9\u77ee\u6811\u5947\u82b1\uff0c\u4eff\u4f5b\u662f\u624e\u7684\u5929\u7136\u82b1\u8fb9\uff0c\u800c\u8fd9\u5757\u5de8\u77f3\uff0c\u6070\u4f3c\u4eba\u5de5\u642d\u5efa\u7684\u4e00\u5ea7\u770b \u53f0\u4e00\u6837\u3002 \u3000\u3000\u5de8\u77f3\u4e00\u6d4b\u6709\u6570\u682a\u5927\u677e\uff0c\u53c8\u6070\u4f3c\u7fe0\u7eff\u5c4f\u98ce\uff0c\u5c31\u5728\u6570\u682a\u5de8\u677e\u4e4b\u524d\u7ad9\u7740\u4e00\u4e2a\u767d\u8863\u5c11\u5973\uff0c\u548c\u4e00\u4e2a \u767d\u987b\u7684\u7626\u8001\u5934\uff0c\u9f50\u5927\u817f\u68cd\u4ee5\u4e0b\u53cc\u817f\u5168\u65e0\uff0c\u5374\u5728\u90a3\u91cc\u5934\u4e0b\u817f\u4e0a\uff0c\u4ee5\u53cc\u624b\u4ee3\u817f\uff0c\u5728\u5730\u4e0b\u7eb5\u8dc3\u98de \u65cb\u3002 \u3000\u3000\u8fd9\u602a\u8001\u5934\u5f88\u5947\u7279\uff0c\u53cc\u817f\u7686\u65e0\uff0c\u90a6\u5728\u4e0b\u80a2\u88c5\u4e86\u4e00\u4e2a\u4e0a\u7c97\u4e0b\u7ec6\u7684\u6728\u6869\uff0c\u627f\u63a5\u4f4f\u80a2\u4f53\uff0c\u5982\u4eca\u5934 \u4e0b\u811a\u4e0a\u5012\u7acb\u5728\u5730\u4e0a\uff0c\u4ee5\u624b\u4ee3\u811a\uff0c\u7eb5\u8dc3\u8df3\u65cb\uff0c\u5c31\u597d\u50cf\u4e00\u4e2a\u5c16\u5934\u9b3c\u5728\u6708\u5149\u4e0b\u8df3\u201c\u9b54\u9b3c\u821e\u201d\u4e00\u822c\u3002 \u3000\u3000\u6708\u5149\u660e\u4eae\uff0c\u5c55\u767d\u76ee\u529b\u53c8\u4f73\uff0c\u867d\u7136\u8ddd\u6709\u6570\u4e08\u4e4b\u9065\uff0c\u5c55\u767d\u4e5f\u770b\u5f97\u6e05\u695a\uff0c\u90a3\u5927\u8df3\u201c\u9b54\u9b3c\u821e\u201d\u7684 \u602a\u8001\u5934\uff0c\u6b63\u662f\u4e09\u65e5\u524d\u5728\u6c5f\u8fb9\u8ffd\u8d76\u201c\u6c5f\u5357\u4e8c\u5947\u201d\u7684\u624b\u67b6\u53cc\u62d0\u3001\u53cc\u817f\u7686\u65e0\u3001\u5374\u5176\u884c\u5982\u98de\u7684\u8001\u8005\u3002 \u3000\u3000\u90a3\u767d\u53d1\u6e05\u7626\u8001\u8005\uff0c\u56e0\u8ddd\u79bb\u592a\u8fdc\uff0c\u5c55\u767d\u8fd0\u8db3\u76ee\u529b\u4e5f\u8fa8\u8ba4\u4e0d\u51fa\u5728\u54ea\u91cc\u89c1\u8fc7\u3002\u90a3\u767d\u8863\u5c11\u5973\uff0c\u79c0 \u53d1\u5fae\u626c\uff0c\u8863\u6289\u98d8\u4e3e\uff0c\u5374\u6b63\u662f\u5a07\u61a8\u5929\u771f\u7684\u5c55\u5a49\u513f\u3002 \u3000\u3000\u201c\u5979\u6708\u591c\u8352\u5c71\uff0c\u4e0e\u4e24\u4e2a\u602a\u8001\u5934\uff0c\u53c8\u662f\u73a9\u7684\u4ec0\u4e48\u628a\u620f\u5462\uff1f\u2026\u2026\u201d \u3000\u3000\u5c55\u767d\u6b63\u5728\u5fc3\u611f\u5947\u602a\uff0c\u90a3\u602a\u8001\u5934\u5934\u4e0b\u811a\u4e0a(\u4e8b\u5b9e\u4e0a\u4ed6\u5df2\u6ca1\u6709\u811a\uff0c\u53ea\u662f\u7ad6\u7740\u4e00\u6839\u6728\u6869\u3002)\u4ee5\u624b \u62c4\u5730\uff0c\u524d\u540e\u5de6\u53f3\u5730\u8df3\u8dc3\u4e00\u904d\uff0c\u7136\u540e\u7ffb\u8eab\u800c\u8d77\uff0c\u540c\u65f6\u987a\u624b\u5728\u5730\u4e0a\u6284\u8d77\u62d0\u6756\u6765\uff0c\u5df2\u9000\u7ad9\u5728\u4e00\u8fb9\uff0c \u975e\u5e38\u5f97\u610f\u5730\u8bf4\u9053\uff1a\u201c\u600e\u4e48\u6837\uff1f\u522b\u770b\u6211\u8001\u4eba\u5bb6\u6ca1\u6709\u817f\uff0c\u4e0d\u662f\u7167\u6837\u505a\u5230\u4e86\uff01\u201d \u3000\u3000\u201c\u5509\uff01\u201d\u5c55\u5a49\u513f\u53f9\u606f\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u770b\u4e24\u4f4d\u8001\u4eba\u5bb6\uff0c\u529f\u529b\u90fd\u5dee\u4e0d\u591a\uff0c\u96be\u5206\u4e0a\u4e0b\uff0c\u8fd8\u662f \u4e0d\u8981\u6bd4\u4e86\uff01\u201d \u3000\u3000\u201c\u4ec0\u4e48\u5dee\u4e0d\u591a\uff1f\u201d\u767d\u53d1\u6e05\u7626\u8001\u8005\u53eb\u9053\uff1a\u201c\u5973\u5a03\u513f\uff01\u4f60\u5e72\u8106\u5c31\u8bf4\u6211\u4e8c\u4eba\u6b66\u529f\u9ad8\u5f3a\u624d\u5bf9\uff0c\u4f46 \u4e0d\u7ba1\u600e\u4e48\u8bf4\uff0c\u8001\u673d\u4e00\u5b9a\u8981\u8ddf\u4ed6\u6bd4\u51fa\u4e2a\u9ad8\u4e0b\u6765\uff01\u201d \u3000\u3000\u201c\u5bf9\uff01\u201d\u5931\u53bb\u53cc\u817f\u7684\u602a\u8001\u5934\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u4e24\u4e2a\u5df2\u7ecf\u6bd4\u4e86\u4e09\u5929\u4e09\u591c\uff0c\u6050\u6015\u628a\u4f60\u8001\u5bb6\u4f19\u538b\u7bb1 \u5e95\u7684\u529f\u592b\u90fd\u6296\u5f04\u51fa\u6765\u4e86\uff0c\u96be\u9053\u8fd8\u6709\u4ec0\u4e48\u9ad8\u62db\u4e0d\u6210\uff1f\u975e\u8981\u8ddf\u4f60\u8fd9\u8001\u5bb6\u4f19\u4e00\u8f83\u957f\u77ed\u4e0d\u53ef\uff01\u201d \u3000\u3000\u201c\u8fd8\u6709\u4ec0\u4e48\u53ef\u6bd4\u5462\uff1f\u201d\u5c55\u5a49\u513f\u9053\uff1a\u201c\u62f3\uff0c\u638c\uff0c\u5175\u5668\uff0c\u6697\u5668\uff0c\u5185\u529f\uff0c\u771f\u529b\uff0c\u8eab\u6cd5\u548c\u6b65\u6cd5\u90fd \u6bd4\u8fc7\u4e86\uff0c\u518d\u6ca1\u6709\u53ef\u6bd4\u7684\u5566\uff0c\u6211\u770b\u4e8c\u4f4d\u8001\u4eba\u5bb6\u5c31\u7b97\u5e73\u5c40\u5566\uff01\u201d \u3000\u3000\u201c\u4e0d\u6210\uff01\u4e0d\u6210\uff01\u201d\u767d\u53d1\u6e05\u7626\u8001\u8005\u5934\u6447\u5f97\u50cf\u62e8\u6d6a\u9f13\u4e00\u822c\uff0c\u8bf4\u4e86\u4e24\u4e2a\u201c\u4e0d\u6210\u201d\u3002 \u3000\u3000\u53c8\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u73b0\u5728\u9898\u76ee\u53c8\u6765\u4e86\uff0c\u521a\u624d\u6765\u4e86\u4e00\u4e2a\u4eba\uff0c\u85cf\u5728\u9644\u8fd1\u5077\u770b\u6211\u4eec\u2026\u2026\u201d \u3000\u3000\u4e0d\u7b49\u767d\u53d1\u6e05\u7626\u8001\u8005\u8bf4\u5b8c\uff0c\u67b6\u53cc\u62d0\u7684\u8001\u5934\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u6211\u8001\u4eba\u5bb6\u65e9\u5c31\u77e5\u9053\u5566\uff01\u558f\uff0c\u5c31\u5728 \u90a3\u5c71\u5761\u4e0a\u5927\u6811\u540e\u8fb9\uff01\u201d\u8bf4\u7740\u7528\u53f3\u624b\u62d0\u6756\u5411\u5c55\u767d\u85cf\u8eab\u4e4b\u5904\u4e00\u6307\u3002 \u3000\u3000\u5c55\u767d\u5413\u4e86\u4e00\u8df3\uff0c\u81ea\u4ee5\u4e3a\u591f\u9690\u79d8\u7684\u4e86\uff0c\u8c01\u77e5\u7ade\u7792\u4e0d\u8fc7\u4e24\u4e2a\u8001\u4eba\uff0c\u6b63\u60f3\u73b0\u8eab\u51fa\u6765\u2606\u2026\u201c\u6162\uff0c \u6162\u6765\uff01\u201d\u767d\u53d1\u6e05\u7626\u8001\u8005\u5fd9\u53eb\u9053\uff1a\u4f60\u5148\u4e0d\u8981\u51fa\u6765\uff01\u2026\u2026\u201d \u3000\u3000\u5c55\u767d\u53c8\u5413\u4e86\u4e00\u8df3\uff0c\u5fc3\u8bf4\uff1a\u201c\u6211\u8fd8\u6ca1\u52a8\uff0c\u4ed6\u5c31\u731c\u4e2d\u6211\u5fc3\u4e2d\u6240\u60f3\uff0c\u83ab\u975e\u8fd9\u8001\u4eba\u6709\u2018\u5929\u89c6\u5730 \u542c\u2019\u4e4b\u529f\uff0c\u80fd\u591f\u6d4b\u77e5\u5bf9\u65b9\u5fc3\u610f\uff01\u2026\u2026\u201d \u7b2c\u4e8c\u5341\u4e94\u7ae0\u3000\u201c\u795e\u9a74\u201d\u6597\u201c\u795e\u7334\u201d>> \u53e4\u9f99\u300a\u5251\u5ba2\u884c\u300b \u7b2c\u4e8c\u5341\u4e94\u7ae0\u3000\u201c\u795e\u9a74\u201d\u6597\u201c\u795e\u7334\u201d \u3000\u3000\u201c\u6211\u4eec\u5c31\u731c\u8fd9\u6765\u4eba\u7684\u8eab\u5206\u3002\u201d\u6e05\u7626\u8001\u8005\u8bf4\u9053\uff1a\u201c\u6765\u4eba\u7684\u5e74\u5c81\u6709\u591a\u5927\uff1f\u662f\u7537\u662f\u5973\uff1f\u731c\u5f97\u5bf9 \u7684\u4e3a\u8d62\uff0c\u731c\u4e0d\u5bf9\u7684\u7b97\u8f93\uff01\u8001\u602a\u7269\uff0c\u8fd9\u529e\u6cd5\u4f60\u770b\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u5931\u53bb\u53cc\u817f\u7684\u8001\u5934\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u9a91\u9a74\u7684\u8001\u9b3c\uff0c\u51ed\u4f60\u806a\u660e\u518d\u591a\uff0c\u4e5f\u9a97\u4e0d\u4e86\u6211\u8001\u4eba\u5bb6\uff01\u4f60\u6765 \u4e86\u719f\u4eba\uff0c\u96be\u9053\u6211\u8001\u4eba\u5bb6\u8fd8\u4e0d\u77e5\u9053\u5417\uff1f\u201d \u3000\u3000\u5c55\u767d\u4e00\u542c\u201c\u9a91\u9a74\u201d\u4e8c\u5b87\uff0c\u604d\u7136\u5927\u609f\uff0c\u8fd9\u773c\u524d\u7684\u6e05\u7626\u8001\u8005\uff0c\u4e0d\u6b63\u662f\u81ea\u5df1\u8981\u627e\u7684\u98ce\u5c18\u5947\u4eba \u201c\u795e\u9a74\u94c1\u80c6\u201d\u8463\u5343\u91cc\u5417\uff01 \u3000\u3000\u4e8e\u662f\uff0c\u4ed6\u4e5f\u4e0d\u7b49\u4e24\u4e2a\u8001\u4eba\u7a76\u7adf\u8981\u62ff\u4ed6\u6253\u4ec0\u4e48\u8d4c\uff0c\u7ade\u81ea\u98d8\u8eab\u63a0\u4e0a\u77f3\u53f0\uff0c\u8001\u8fdc\u4fbf\u53eb\u9053\uff1a\u8463\u8001 \u524d\u8f88\uff0c\u665a\u8f88\u5bfb\u5f97\u4f60\u597d\u82e6\u54c7\uff01\u201d \u3000\u3000\u8463\u5343\u91cc\u4e00\u695e\uff0c\u4ed6\u529f\u53c2\u9020\u5316\uff0c\u8033\u806a\u76ee\u654f\uff0c\u539f\u662f\u542c\u5230\u6f5c\u5f62\u9690\u8e2a\u7684\u6765\u4eba\uff0c\u811a\u6b65\u6c89\u7740\u7a33\u5b9a\uff0c\u5fc5\u662f \u4e00\u5e74\u8f7b\u4eba\uff0c\u800c\u4e14\u5fc5\u662f\u7537\u5b50\uff0c\u672c\u60f3\u51ed\u6b64\u7cbe\u5bc6\u7684\u5224\u65ad\uff0c\u6765\u80dc\u8fc7\u5f53\u524d\u7684\u602a\u8001\u4eba\uff0c\u4f46\u4e5f\u60f3\u4e0d\u5230\u6765\u4eba\u771f \u8ba4\u8bc6\u4ed6\uff0c\u56e0\u4e3a\u4ed6\u9690\u5bc6\u884c\u8e2a\u5df2\u5341\u6570\u5e74\uff0c\u6c5f\u6e56\u4e0a\u5f88\u5c11\u4eba\u77e5\u9053\u4ed6\u7684\u771f\u5b9e\u59d3\u540d\u4e86\u3002 \u3000\u3000\u5982\u4eca\u5c55\u767d\u4e00\u547c\u53eb\u4ed6\u7684\u59d3\u6c0f\uff0c\u65e0\u5f62\u4e2d\u88ab\u602a\u8001\u5934\u8a00\u4e2d\u4e86\uff0c\u4ed6\u4e00\u53cc\u7cbe\u5149\u5982\u70ac\u7684\u773c\u775b\uff0c\u4e0d\u7531\u5f97\u671b \u5b9a\u5c55\u81ea\uff0c\u6014\u4e86\u4e00\u4e0b\u9053\uff1a\u4f60\u5c0f\u5b50\uff0c\u600e\u4e48\u77e5\u9053\u8001\u592b\u59d3\u8463\uff1f\u201d \u3000\u3000\u672a\u7b49\u5c55\u767d\u7b54\u8a00\uff0c\u90a3\u602a\u8001\u5934\u5374\u5475\u5475\u7b11\u9053\uff1a\u201c\u7528\u4e0d\u7740\u5531\u620f\u4e86\uff01\u6211\u770b\u4f60\u9a91\u9a74\u8001\u9b3c\u662f\u9ed4\u9a74\u6280\u7a77 \u4e86\uff0c\u7adf\u53eb\u4e00\u4e2a\u540e\u751f\u85cf\u5728\u4e00\u8fb9\uff0c\u6765\u9a97\u6211\u8001\u4eba\u5bb6\uff0c\u8fd9\u8fde\u4e09\u5c81\u5b69\u7ae5\u4e5f\u9a97\u2026\u2026\u201d \u3000\u3000\u201c\u795e\u9a74\u94c1\u80c6\u201d\u8463\u5343\u91cc\u5927\u6012\uff0c\u5c48\u8098\u56f0\u638c\uff0c\u8f7b\u98d8\u98d8\u5730\u6325\u51fa\uff0c\u540c\u65f6\u6012\u9053\uff1a\u8001\u602a\u7269\uff01\u4f11\u8981\u9976\u820c\uff0c \u4f60\u518d\u63a5\u8001\u592b\u51e0\u62db\u8bd5\u8bd5\uff01\u201d \u3000\u3000\u522b\u770b\u638c\u52bf\u6325\u51fa\uff0c\u4e1d\u6beb\u4e0d\u5e26\u7834\u7a7a\u4e4b\u58f0\uff0c\u4f46\u90a3\u4e00\u80a1\u9634\u67d4\u4e4b\u529b\u5374\u662f\u5927\u5f88\u60ca\u4eba\u3002 \u3000\u3000\u201c\u51e0\u767e\u62db\u4e5f\u4e0d\u5728\u4e4e\uff01\u201d \u3000\u3000\u602a\u8001\u5934\u5634\u91cc\u8bf4\u7740\uff0c\u5355\u62d0\u63a5\u5728\u81c2\u5f2f\u4e0a\uff0c\u4e94\u6307\u4e00\u65cb\uff0c\u4e5f\u662f\u4e00\u80a1\u67d4\u52b2\uff0c\u968f\u6307\u800c\u51fa\u3002 \u3000\u3000\u4e24\u80a1\u67d4\u52b2\u4e00\u649e\uff0c\u4e09\u4eba\u8eab\u5f62\u540c\u65f6\u4e00\u632b\uff0c\u4fca\u53c8\u7535\u5c04\u800c\u8d77\uff0c\u7830\uff01\u7830\uff01 \u3000\u3000\u7830\uff01\u5feb\u5982\u7535\u5149\u77f3\u706b\uff0c\u4e00\u9635\u6c14\u7206\u4e4b\u58f0\u4f20\u6765\uff0c\u4e8c\u4eba\u8eab\u5f62\u5f80\u8d77\u4e00\u63a5\uff0c\u77ac\u95f4\u786c\u5bf9\u4e86\u4e09\u638c\u3002 \u3000\u3000\u90a3\u201c\u7830\u7830\u201d\u4e4b\u58f0\uff0c\u54cd\u5728\u8eab\u8fb9\u5e76\u4e0d\u5927\uff0c\u4f46\u6fc0\u8361\u800c\u51fa\uff0c\u7531\u8fdc\u5c71\u7fa4\u5cf0\u649e\u56de\u6765\u7684\u56de\u97f3\uff0c\u5374\u9686\u9669\u9707 \u8033\u3002 \u3000\u3000\u5c55\u767d\u6697\u6697\u5fc3\u60ca\uff0c\u4e8c\u4eba\u8eab\u6cd5\u62db\u5f0f\u5feb\u5f97\u51fa\u5947\u8fd8\u4e0d\u8bf4\uff0c\u8fd9\u9634\u67d4\u638c\u52b2\uff0c\u5982\u6b64\u60ca\u4eba\uff0c\u53ef\u77e5\u4e8c\u4eba\u7684\u6b66 \u529f\u5b9e\u975e\u5c0f\u53ef\u3002 \u3000\u3000\u4e24\u4e2a\u8001\u4eba\u604d\u773c\u95f4\uff0c\u8eab\u5f62\u98d8\u5ffd\uff0c\u638c\u98ce\u547c\u547c\uff0c\u6253\u505a\u4e00\u56e2\u3002 \u3000\u3000\u5a49\u513f\u4e4d\u89c1\u5c55\u767d\u51fa\u73b0\uff0c\u60ca\u559c\u83ab\u540d\uff0c\u4e00\u65f6\u5446\u4f4f\u4e86\uff0c\u89c1\u4e24\u4e2a\u8001\u4eba\u53c8\u6253\u8d77\u6765\u4e86\uff0c\u5c55\u767d\u53c8\u770b\u5f97\u51fa \u795e\uff0c\u5bf9\u5979\u8fde\u770b\u4e00\u773c\u90fd\u4e0d\u770b\uff0c\u4e0d\u7531\u5e7d\u5e7d\u8bf4\u9053\uff1a\u201c\u5509\uff01\u4ed6\u4eec\u53c8\u6253\u8d77\u6765\u4e86\uff0c\u53ef\u80fd\u53c8\u662f\u6ca1\u5b8c\uff0c\u6ca1\u60f3\u5230 \u4ed6\u4eec\u90fd\u90a3\u4e48\u5927\u5e74\u7eaa\u4e86\uff0c\u706b\u6c14\u8fd8\u662f\u8fd9\u6837\u5927\uff01\u201d \u3000\u3000\u5c55\u81ea\u770b\u90a3\u602a\u8001\u4eba\u867d\u7136\u53cc\u817f\u7686\u65e0\uff0c\u4e0b\u80a2\u53ea\u662f\u4e00\u6839\u6728\u6869\uff0c\u4f46\u53cc\u62d0\u63a5\u5728\u5de6\u53f3\u80a9\u4e0a\uff0c\u524d\u70b9\u540e\u89e6\uff0c \u53cc\u638c\u66f4\u662f\u8fde\u8f6c\u5982\u98de\uff0c\u6251\u9ad8\u7eb5\u77ee\uff0c\u5de6\u8e66\u53f3\u7a9c\uff0c\u8eab\u6cd5\u7075\u6d3b\uff0c\u4e1d\u6beb\u4e0d\u4e0b\u4e8e\u201c\u795e\u9a74\u94c1\u80c6\uff1f\uff0c\u6b8b\u5e9f\u4eba\u80fd \u6709\u8fd9\u79cd\u6210\u5c31\uff0c\u771f\u53ef\u4f7f\u4eba\u53f9\u4e3a\u89c2\u6b62\u4e86\u3002 \u3000\u3000\u5c55\u767d\u8d8a\u770b\u8d8a\u5947\uff0c\u4e0d\u7531\u95ee\u9053\uff1a\u201c\u4ed6\u4eec\u4e3a\u4ec0\u4e48\u8981\u6253\u5462\uff1f\u201d \u3000\u3000\u5a49\u513f\u8bf4\uff1a\u201c\u6211\u4e5f\u4e0d\u77e5\u9053\u4ed6\u4eec\u4e3a\u4ec0\u4e48\u6253\uff0c\u6211\u6765\u5230\u8fd9\u91cc\u627e\u4f60\u6ca1\u627e\u5251\uff0c\u90fd\u78b0\u5230\u4ed6\u4eec\uff0c\u8bf4\u662f\u5df2\u7ecf \u6253\u4e86\u4e09\u5929\u4e09\u591c\u4e86\uff0c\u638c\u62f3\u5175\u5668\uff0c\u6b66\u529f\u5185\u529b\uff0c\u4ec0\u4e48\u90fd\u6bd4\u8fc7\u4e86\uff0c\u8fd8\u662f\u5206\u4e0d\u51fa\u9ad8\u4e0b\uff0c\u624d\u8bf7\u6211\u5f53\u88c1\u5224\uff0c \u53eb\u6211\u51fa\u4e3b\u610f\u4f7f\u4ed6\u4eec\u5206\u51fa\u80dc\u8d25\u6765\uff0c\u53ef\u662f\u6211\u60f3\u5c3d\u65b9\u6cd5\uff0c\u4ed6\u4eec\u4ecd\u662f\u4e0d\u8f93\u4e0d\u8d62\uff0c\u4f60\u6765\u7684\u65f6\u5019\uff0c\u6211\u6b63\u8981 \u4ed6\u4eec\u6bd4\u8d5b\u2018\u954a\u7a7a\u5e7b\u5f71\u2019\u7684\u6b65\u6cd5\uff0c\u90a3\u602a\u8001\u4eba\u6ca1\u6709\u817f\uff0c\u7ed3\u679c\u96be\u4e0d\u5012\u4ed6\uff0c\u4ed6\u7528\u53cc\u624b\u4ee3\u811a\uff0c\u7167\u6837\u529e\u5f97 \u5230\uff01\u201d \u3000\u3000\u5a49\u513f\u8fd9\u4e48\u4e00\u8bf4\uff0c\u5c55\u767d\u65b9\u7b97\u660e\u767d\u4e86\u4e2a\u5927\u6982\uff0c\u4f46\u8fd8\u662f\u4e0d\u77e5\u9053\u4e24\u4e2a\u8001\u4eba\u7a76\u7adf\u4e3a\u4ec0\u4e48\u6253\u4e86\u8d77\u6765\uff0c \u4f46\u8f6c\u800c\u5fc3\u4e2d\u4e00\u52a8\uff0c\u56de\u5934\u95ee\u9053\uff1a\u5a49\u513f\uff0c\u4f60\u8bf4\u627e\u6211\uff0c\u627e\u6211\u6709\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u5a49\u513f\u5927\u773c\u775b\u4e00\u8f6c\uff0c\u5fc3\u91cc\u4e00\u9178\uff0c\u5dee\u70b9\u6ca1\u843d\u4e0b\u6cea\u6765\u3002\u5fc3\u8bf4\uff1a\u201c\u6211\u4e3a\u4e86\u6551\u4f60\uff0c\u5dee\u70b9\u6ca1\u628a\u547d\u4e22 \u4e86\uff0c\u96be\u9053\u4f60\u4e00\u70b9\u4e5f\u4e0d\u77e5\u9053\u5417\uff1f\u201d \u3000\u3000\u4f46\u5634\u4e2d\u5374\u6ca1\u6709\u8fd9\u4e48\u8bf4\uff0c\u53ea\u5e7d\u5e7d\u5730\u9053\uff1a\u201c\u6211\u5012\u95ee\u4f60\uff0c\u4f60\u5728\u5174\u9686\u9152\u5e97\uff0c\u88ab\u2018\u8840\u638c\u706b\u9f99\u2019\u7ea2\u7802 \u6bd2\u638c\u6253\u4f24\uff0c\u662f\u8c01\u6551\u4e86\u4f60\uff1f\u201d \u3000\u3000\u201c\u5662\uff01\u201d\u5c55\u767d\u604d\u7136\u5927\u609f\uff1a\u201c\u539f\u6765\u662f\u5a49\u513f\u59d1\u5a18\u6551\u4e86\u6211\uff01\u8fd9\u6837\u8bf4\u6765\uff0c\u4f60\u4e5f\u89c1\u8fc7\u2018\u6d3b\u6b7b\u4eba\u2019 \u4e86\uff01\u6211\u9192\u6765\u600e\u4e48\u6ca1\u6709\u770b\u5230\u4f60\uff1f\u201d \u3000\u3000\u5a49\u513f\u8138\u4e00\u7ea2\uff0c\u60f3\u5230\u5728\u201c\u6b7b\u4eba\u5c45\u201d\u6240\u53d7\u7684\u59d4\u66f2\uff0c\u5dee\u70b9\u54ed\u51fa\u6765\u2026\u2026 \u3000\u3000\u201c\u4f60\u4eec\u4e24\u4e2a\u5a03\u513f\uff0c\u5c3d\u7ba1\u5520\u53e8\u6ca1\u5b8c\uff01\u201d\u4e8c\u4eba\u6fc0\u6597\u4e8e\u4e00\u56e2\u638c\u98ce\u4eba\u5f71\u4e4b\u4e2d\uff0c\u4f20\u51fa\u201c\u795e\u9a74\u94c1\u80c6\u201d \u7684\u58f0\u97f3\u9053\uff1a\u201c\u6562\u4fa0\u8eb2\u8fdc\u4e00\u70b9\uff0c\u6211\u8001\u4eba\u5bb6\u8981\u65bd\u6740\u624b\u4e86\uff01\u201d \u3000\u3000\u201c\u55ec\u2026\u2026\u201d\u53ea\u542c\u602a\u8001\u5934\u5475\u5475\u7b11\u9053\uff1a\u201c\u9a91\u9a74\u8001\u513f\uff01\u5c11\u5728\u8fd9\u513f\u865a\u5f20\u58f0\u52bf\uff0c\u6709\u4ec0\u4e48\u7259\u9ec4\u72d7\u5b9d\uff0c \u5c3d\u91cf\u65bd\u5c55\u5c31\u662f\u4e86\uff01\u88c1\u8001\u4eba\u5bb6\u90fd\u63a5\u7740\u4f60\u7684\uff01\u201d \u3000\u3000\u201c\u55b3\uff01\u201d \u3000\u3000\u53ea\u542c\u201c\u795e\u9a74\u94c1\u80c6\u201d\u7684\u6012\u53f1\u4e4b\u58f0\uff0c\u8ddf\u7740\u52b2\u6d41\u6fc0\u8361\uff0c\u9690\u9690\u6709\u98ce\u96f7\u4e4b\u58f0\uff0c\u679c\u7136\u638c\u98ce\u5a01\u529b\u5927\u589e\uff0c \u6570\u5341\u4e08\u65b9\u5706\u7684\u77f3\u576a\u4e0a\uff0c\u52b2\u98ce\u6fc0\u8361\uff0c\u5c55\u767d\u4e0e\u5a49\u513f\u4e8c\u4eba\u5df2\u611f\u5230\u52bf\u96be\u7acb\u8db3\uff0c\u4e0d\u7531\u53cc\u53cc\u98d8\u8eab\u8df3\u4e0b\u53f0 \u6765\uff0c\u53c8\u8e7f\u4e0a\u77f3\u576a\u524d\u6570\u4e08\u4e4b\u5916\u4e00\u63a2\u866c\u677e\u4e0a\u53bb\u3002 \u3000\u3000\u4e8c\u4eba\u5750\u5728\u7c97\u5927\u677e\u6746\u4e0a\uff0c\u4e00\u8fb9\u8c08\u8bdd\uff0c\u4e00\u8fb9\u671b\u7740\u53f0\u4e0a\u4e8c\u4eba\u6fc0\u70c8\u640f\u6597\u3002 \u3000\u3000\u4e24\u4e2a\u8001\u4eba\u8eab\u6cd5\u62db\u5f0f\u90fd\u592a\u5feb\uff0c\u52a0\u4e0a\u638c\u98ce\u5f3a\u70c8\uff0c\u867d\u7136\u540c\u662f\u8d70\u7684\u9634\u67d4\u6697\u52b2\u7684\u8def\u5b50\uff0c\u4e0d\u4f3c\u9633\u521a\u638c \u529b\u90a3\u822c\u60ca\u5929\u52a8\u5730\uff0c\u4f46\u5728\u6708\u591c\u770b\u6765\uff0c\u5df2\u96be\u5206\u6e05\u4eba\u5f71\u3002 \u3000\u3000\u6708\u5149\u4e0b\uff0c\u5bbd\u5e7f\u77f3\u53f0\u4e0a\uff0c\u4eff\u4f5b\u9686\u8d77\u4e86\u2014\u4e2a\u767d\u7070\u8272\u7684\u5927\u5706\u7403\uff0c\u53c8\u50cf\u5728\u90a3\u513f\u6709\u4e00\u80a1\u5947\u5f62\u7684\u9f99\u65cb \u98ce\uff0c\u7ffb\u6eda\u84b8\u817e\uff0c\u6839\u672c\u5c31\u770b\u4e0d\u6e05\u662f\u4e24\u4e2a\u4eba\uff0c\u5728\u90a3\u91cc\u6bd4\u6b66\u640f\u6597\u3002 \u3000\u3000\u5c55\u767d\u4fee\u4e60\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\u6b63\u5b97\u5fc3\u6cd5\uff0c\u53c8\u52a0\u4e0a\u5947\u7ecf\u516b\u8109\u5df2\u901a\uff0c\u8033\u806a\u76ee\u654f\uff0c\u5df2\u80fd\u9ed1\u591c\u89c6 \u7269\uff0c\u4f46\u4ecd\u4e0d\u80fd\u5b8c\u5168\u770b\u6e05\u4e8c\u4eba\u51fa\u62db\u6362\u5f0f\u7684\u5de7\u5999\u8eab\u6cd5\uff0c\u81f3\u4e8e\u5a49\u513f\u5c31\u66f4\u770b\u4e0d\u6e05\u4e86\u3002 \u3000\u3000\u5ffd\u542c\u201c\u7830\uff01\u7830\uff01\u2026\u2026\u201d\u51e0\u58f0\u7206\u54cd\u4f20\u6765\uff0c\u58f0\u9707\u591c\u7a7a\uff0c\u4e8c\u4eba\u5feb\u5982\u98d8\u98ce\u7684\u8eab\u6cd5\uff0c\u500f\u7136\u5de6\u53f3\u5206 \u5f00\u3002 \u3000\u3000\u602a\u8001\u4eba\u6840\u6840\u602a\u7b11\u9053\uff1a\u201c\u9a91\u9a74\u8001\u9b3c\uff0c\u2018\u5947\u5f62\u8ffd\u98ce\u638c\u2019\uff0c\u4e5f\u4e0d\u8fc7\u5982\u6b64\uff0c\u8fd8\u6709\u4ec0\u4e48\u65b0\u9c9c\u7684\u73a9 \u610f\uff0c\u638f\u51fa\u6765\u7ed9\u6211\u8001\u4eba\u5bb6\u6b23\u8d4f\u6b23\u8d4f\uff1f\u201d \u3000\u3000\u8fd9\u602a\u8001\u4eba\u8bed\u610f\u8bd9\u8c10\uff0c\u6781\u5c3d\u6316\u82e6\u8bbd\u523a\u4e4b\u80fd\u4e8b\uff0c\u6bd4\u6597\u4e86\u4e09\u5929\u4e09\u591c\uff0c\u201c\u795e\u9a74\u94c1\u80c6\u201d\u5df2\u6fc0\u52a8\u4e86\u771f \u706b\uff0c\u95fb\u8a00\u5389\u53f1\u9053\uff1a\u201c\u8001\u602a\u7269\uff0c\u4f60\u5c11\u5356\u72c2I\u518d\u63a5\u8001\u592b\u4e24\u679a\u94c1\u80c6\u8bd5\u8bd5\uff01\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u4e2d\u4e00\u6296\u624b\uff0c\u4e00\u9053\u5bd2\u8292\uff0c\u5e26\u7740\u201c\u55e1\u55e1\u201d\u91d1\u97f3\uff0c\u7535\u5c04\u8001\u4eba\u9762\u95e8\u3002 \u3000\u3000\u602a\u8001\u4eba\u4ef0\u5929\u5927\u7b11\u9053\uff1a\u201c\u96d5\u866b\u5c0f\u9f13\uff0c\u4e5f\u6562\u5728\u6211\u8001\u4eba\u5bb6\u9762\u524d\u732e\u4e11\uff01\u201d\u5634\u4e2d\u8bf4\u7740\u8bdd\uff0c\u53f3\u624b\u5355\u62d0 \u6f2b\u4e0d\u7ecf\u5fc3\u5730\u5411\u4e0a\u4e00\u64a9\uff0c\u201c\u6323\u201d\u7684\u4e00\u58f0\u8106\u9e23\uff0c\u628a\u6253\u5411\u9762\u95e8\u7684\u94c1\u80c6\u9707\u98de\uff0c\u76f4\u5c04\u534a\u5929\u4e4b\u5916\u3002 \u3000\u3000\u201c\u795e\u9a74\u94c1\u80c6\u201d\u5927\u559d\u4e00\u58f0\uff0c\u53c8\u4e00\u679a\u94c1\u80c6\uff0c\u6296\u624b\u63b7\u51fa\uff0c\u5374\u4e0d\u662f\u6253\u5411\u602a\u8001\u5934\uff0c\u800c\u662f\u76f4\u5411\u88ab\u602a\u8001 \u5934\u5355\u62d0\u9707\u98de\u534a\u7a7a\u7684\u90a3\u679a\u94c1\u80c6\u5c04\u53bb\u3002 \u3000\u3000\u201c\u53ee\uff01\u201d\u4e24\u679a\u94c1\u80c6\u534a\u7a7a\u76f8\u649e\uff0c\u6fc0\u8d77\u4e00\u6e9c\u706b\u82b1\uff0c\u63a5\u7740\u5411\u4e0b\u75be\u6cfb\uff0c\u604d\u5982\u4e24\u9897\u6d41\u661f\u4e00\u822c\uff0c\u5212\u8d77 \u4e24\u9053\u94f6\u8292\uff0c\u631f\u7740\u201c\u55e1\uff01\u55e1\uff01\u201d\u6151\u4eba\u5fc3\u9b44\u7684\u9510\u97f3\uff0c\u4e00\u5de6\u4e00\u53f3\uff0c\u76f4\u5411\u602a\u8001\u4eba\u4e24\u808b\u6253\u5230\u3002 \u3000\u3000\u602a\u8001\u4eba\u4e5f\u88ab\u8fd9\u5947\u7279\u7684\u6697\u5668\u624b\u6cd5\uff0c\u60ca\u5f97\u5446\u4e86\u4e00\u5446\uff0c\u4f46\u77ac\u5373\u6062\u590d\u4e86\u9547\u5b9a\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u8dd1\u9a6c\u89e3 \u5c0f\u59d1\u5a18\u90fd\u4f1a\u7684\u624b\u6cd5\uff0c\u8fd8\u96be\u4e0d\u5012\u6211\u8001\u4eba\u5bb6\uff01\u201d \u3000\u3000\u8bf4\u7f62\uff0c\u53cc\u62d0\u4e00\u62a1\uff0c\u201c\u53ee\u53ee\u201d\u4e24\u58f0\uff0c\u628a\u4e24\u679a\u94c1\u80c6\u53c8\u9707\u98de\u53ca\u4e08\u3002 \u3000\u3000\u8bf4\u4e5f\u5947\u602a\uff0c\u90a3\u4e24\u679a\u94c1\u80c6\u7ade\u50cf\u6709\u7075\u6027\u4e00\u822c\uff0c\u88ab\u602a\u8001\u5934\u53cc\u62d0\u9707\u98de\uff0c\u534a\u7a7a\u4e2d\u4e92\u76f8\u7ed5\u4e86\u4e00\u4e2a\u5708 \u5b50\uff0c\u53c8\u5728\u534a\u7a7a\u4e2d\u76f8\u649e\uff0c\u201c\u53ee\u201d\u7684\u4e00\u58f0\uff0c\u91cd\u65b0\u5411\u602a\u8001\u4eba\u524d\u80f8\u88ad\u6765\u3002 \u3000\u3000\u201c\u54c8\u54c8\uff01\u201d\u602a\u8001\u4eba\u7b11\u9053\uff1a\u201c\u6709\u70b9\u610f\u601d\uff0c\u9a91\u9a74\u8001\u9b3c\uff0c\u8fd9\u6bd4\u8dd1\u9a6c\u89e3\u7684\u5c0f\u59d1\u5a18\u9ad8\u660e\u591a\u4e86\uff01\u201d \u3000\u3000\u8bf4\u8bdd\u58f0\u4e2d\uff0c\u53cc\u62d0\u4e00\u78b0\uff0c\u518d\u628a\u4e24\u679a\u94c1\u80c6\u9707\u98de\uff0c\u4f46\u90a3\u4e24\u679a\u94c1\u80c6\u5374\u50cf\u957f\u4e86\u7fc5\u8180\u7684\u98de\u9e1f\uff0c\u500f\u98de\u5373 \u56de\uff0c\u201c\u53ee\u53ee\u201d\u4e4b\u58f0\u4e0d\u7edd\u4e8e\u8033\uff0c\u800c\u4e14\u5747\u662f\u6307\u5411\u602a\u8001\u4eba\u7684\u5468\u8eab\u91cd\u7a74\u3002 \u3000\u3000\u8fd9\u5947\u5f02\u7684\u6697\u5668\u624b\u6cd5\uff0c\u53ef\u4ee5\u8bf4\u591f\u662f\u6177\u4e16\u9a87\u4fd7\u7684\u4e86\uff0c\u5c55\u767d\u4e0e\u5a49\u513f\u5750\u5728\u677e\u6811\u5e72\u4e0a\uff0c\u671b\u7740\u8fd9\u60ca\u5fc3 \u52a8\u9b44\u7684\u4e00\u5e55\uff0c\u53cc\u773c\u77aa\u5f97\u5927\u5927\u7684\uff0c\u8fde\u8bdd\u4e5f\u5fd8\u4e86\u8bf4\u5566\uff01 \u3000\u3000\u53ef\u662f\u602a\u8001\u4eba\u4f9d\u7136\u4e1d\u6beb\u4e0d\u5728\u610f\uff0c\u4e00\u8fb9\u54bf\u54bf\u5440\u5440\uff0c\u8bf4\u7740\u98ce\u51c9\u8bdd\uff0c\u4e00\u8fb9\u4ece\u5bb9\u6325\u52a8\u53cc\u62d0\uff0c\u94c1\u80c6\u8fd1 \u8eab\u5373\u88ab\u78d5\u98de\u3002 \u3000\u3000\u201c\u795e\u9a74\u94c1\u80c6\u201d\u89c1\u81ea\u5df2\u4e24\u679a\u6210\u540d\u94c1\u80c6\uff0c\u4ecd\u4f24\u4e0d\u4e86\u4ed6\uff0c\u8bf4\u9053\uff1a\u201c\u8001\u602a\u7269\uff0c\u73a9\u5f97\u4e0d\u5c3d\u5174\uff0c\u518d\u7ed9 \u4f60\u52a0\u4e0a\u4e00\u679a\u5982\u4f55\uff1f\u201d \u3000\u3000\u201c\u5982\u4f55\u201d\u4e24\u5b57\u672b\u843d\u5730\uff0c\u53e6\u4e00\u679a\u94c1\u80c6\u5df2\u968f\u624b\u6296\u51fa\u3002 \u3000\u3000\u8fd9\u4e00\u679a\u94c1\u80c6\uff0c\u6bd4\u524d\u4e24\u679a\u7565\u5c0f\uff0c\u6253\u51fa\u4e4b\u540e\uff0c\u4e0d\u662f\u201c\u55e1\u55e1\u201d\u91d1\u97f3\uff0c\u800c\u662f\u9510\u5578\u7834\u7a7a\uff0c\u4eff\u4f5b\u5c16\u957f \u7684\u54e8\u97f3\u4e00\u822c\uff0c\u75be\u5982\u95ea\u7535\uff0c\u53bb\u52bf\u4e5f\u6bd4\u90a3\u4e24\u653e\u5feb\u591a\u4e86\uff0c\u53ea\u89c1\u5982\u4e00\u7ebf\u767d\u5f71\uff0c\u4ee5\u89c6\u89c9\u96be\u89c1\u7684\u901f\u5ea6\uff0c\u76f4 \u5c04\u602a\u8001\u4eba\u9762\u95e8\u3002 \u3000\u3000\u602a\u8001\u4eba\u5927\u53eb\uff1a\u201c\u4e0d\u80fd\u518d\u52a0\u591a\u4e86\uff01\u201d \u3000\u3000\u4f46\u4e0d\u7b49\u4ed6\u98ce\u51c9\u8bdd\u51fa\u53e3\uff0c\u94c1\u80c6\u5df2\u8fd1\u9762\u95e8\uff0c\u602a\u8001\u4eba\u75be\u5fd9\u4e3e\u62d0\u4e00\u5c01\uff0c\u5374\u610f\u5916\u5730\u5c01\u4e86\u4e2a\u7a7a\u3002\u539f\u6765 \u8fd9\u6700\u540e\u4e00\u679a\u94c1\u80c6\uff0c\u4e0d\u987b\u78b0\u5230\u5b9e\u7269\uff0c\u9047\u529b\u4e00\u963b\u5373\u81ea\u884c\u8f6c\u5f2f\uff0c\u602a\u8001\u4eba\u4e3e\u62d0\u4e00\u5c01\uff0c\u52b2\u529b\u6307\u5904\uff0c\u90a3\u679a \u94c1\u80c6\u5df2\u7ed5\u4e86\u4e00\u4e2a\u5c0f\u5708\uff0c\u4fa7\u51fb\u602a\u8001\u4eba\u5de6\u8033\u85cf\u8840\u3002 \u3000\u3000\u602a\u8001\u4eba\u4e0d\u5907\uff0c\u5dee\u70b9\u88ab\u8fd9\u540e\u6765\u4e00\u80c6\u51fb\u4e2d\uff0c\u5e78\u597d\u4ed6\u6b66\u529f\u5df2\u5230\u767b\u5cf0\u9020\u6781\u5730\u6b65\uff0c\u80fd\u591f\u5fc3\u968f\u610f\u52a8\uff0c \u52a8\u5728\u610f\u5148\uff0c\u52b2\u98ce\u8d2f\u8033\uff0c\u81ea\u52a8\u4e00\u7f29\u5934\uff0c\u94c1\u80c6\u64e6\u9876\u800c\u8fc7\u3002 \u3000\u3000\u800c\u4e14\uff0c\u5c1a\u6709\u5148\u524d\u4e24\u679a\u94e3\u80c6\uff0c\u500f\u5ffd\u53c8\u5230\uff0c\u5fd9\u6325\u62d0\u9707\u5f00\uff0c\u540e\u4e00\u679a\u94c1\u80c6\u843d\u7a7a\uff0c\u53c8\u81ea\u884c\u7ed5\u4e86\u56de \u6765\uff0c\u76f4\u5954\u5c0f\u8179\u6c14\u6d77\u6253\u5230\u3002 \u3000\u3000\u602a\u8001\u4eba\u6b66\u529f\u518d\u9ad8\uff0c\u81f3\u6b64\uff0c\u4e5f\u95f9\u4e86\u4e2a\u624b\u5fd9\u811a\u4e71\uff0c\u5634\u4e2d\u5df2\u4e0d\u662f\u8f7b\u677e\u7684\u8c08\u7b11\uff0c\u800c\u662f\u54c7\u54c7\u602a\u53eb \u4e86\u3002 \u3000\u3000\u5c55\u767d\u4e0e\u5a49\u513f\u5df2\u770b\u5f97\u8ddf\u82b1\u7f2d\u4e71\uff0c\u53ea\u89c1\u4e09\u9053\u94f6\u8292\uff0c\u72b9\u5982\u4e09\u6761\u7075\u86c7\uff0c\u56f4\u7740\u602a\u8001\u4eba\u5468\u8eab\u76d8\u7ed5\uff0c\u53ee \u53ee\uff01\u201d\u91d1\u97f3\uff0c\u201c\u5578\u5578\u201d\u5c16\u54e8\uff0c\u4ea4\u7ec7\u6210\u4e00\u7247\u9707\u6151\u5fc3\u9b44\u7684\u58f0\u6d6a\uff0c\u52a0\u4e0a\u201c\u53ee\u53ee\uff01\u201d\u4ea4\u9e23\uff0c\u706b\u661f\u94f6\u82b1 \u8000\u773c\uff0c\u5012\u5f62\u6210\u4e00\u96f7\u5947\u5f02\u7684\u666f\u8c61\u3002 \u3000\u3000\u201c\u795e\u9a74\u94c1\u80c6\u201d\u89c1\u4e09\u679a\u94c1\u80c6\uff0c\u5df2\u5c06\u602a\u8001\u5934\u95f9\u4e2a\u624b\u5fd9\u811a\u4e71\uff0c\u4e0d\u7531\u8d1f\u624b\u800c\u7acb\uff0c\u795e\u60c5\u6cf0\u7136\u5730\u7b11 \u9053\uff1a\u201c\u600e\u4e48\u8001\u602a\u7269\uff01\u4e09\u4e38\u9f50\u98e8\uff0c\u5473\u9053\u4e0d\u9519\u5427\uff1f\u201d \u3000\u3000\u602a\u8001\u5934\u5927\u543c\u4e00\u58f0\uff0c\u53cc\u62d0\u731b\u6325\uff0c\u91d1\u94c1\u4ea4\u9e23\uff0c\u95ea\u95ea\u94f6\u661f\u4ea4\u76f8\u8ff8\u5c04\uff0c\u628a\u4e09\u679a\u94c1\u80c6\u9707\u98de\u8eab\u5916\u6570 \u4e08\uff0c\u500f\u5730\u8eab\u5f62\u5c31\u5730\u4e00\u4ec6\u3002 \u3000\u3000\u7b49\u5230\u4e09\u679a\u94c1\u80c6\u5728\u534a\u7a7a\u7ed5\u4e86\u4e00\u4e2a\u5927\u5708\u5b50\uff0c\u7ed5\u8f6c\u800c\u56de\u65f6\uff0c\u539f\u5730\u5df2\u4e0d\u89c1\u4e86\u602a\u8001\u5934\u8e2a\u5f71\uff0c\u53ea\u6709\u4e09 \u9053\u94f6\u8292\uff0c\u7a7a\u81ea\u534a\u7a7a\u7edf\u7edf\u3002 \u3000\u3000\u201c\u795e\u9a74\u8dcc\u80c6\u201d\u5927\u611f\u610f\u5916\uff0c\u695e\u4e86\u4e00\u4f1a\uff0c\u624d\u62db\u624b\u6536\u56de\u94c1\u80c6\uff0c\u602a\u8001\u5934\u7a81\u5728\u4ed6\u8eab\u540e\u51b7\u51b7\u8bf4\u9053\uff1a \u201c\u4e09\u4e38\u4ea4\u98de\uff0c\u4e5f\u6321\u4e0d\u4f4f\u6211\u8001\u4eba\u5bb6\u2018\u95ea\u5f71\u65e0\u5f71\u2019\u8eab\u6cd5\uff0c\u6211\u8001\u4eba\u5bb6\u82e5\u4e0d\u662f\u81ea\u987e\u8eab\u4efd\uff0c\u6b64\u65f6\u51fa\u624b\uff0c \u4f60\u9a91\u9a74\u8001\u9b3c\u5df2\u65e9\u8d1f\u4f24\u591a\u65f6\u4e86\uff01\u201d \u3000\u3000\u201c\u795e\u9a74\u94c1\u80c6\u201d\u8138\u8272\u4e00\u6c89\uff0c\u845b\u5730\u56de\u8eab\uff0c\u53cd\u81c2\u7a7f\u638c\uff0c\u7f13\u7f13\u5411\u540e\u64a9\u53bb\u3002 \u3000\u3000\u4e00\u80a1\u65e0\u5f62\u7684\u67d4\u52b2\u6fc0\u8361\u800c\u51fa\uff0c\u5982\u6012\u6d77\u72c2\u6d9b\u4e00\u822c\uff0c\u5411\u8eab\u540e\u5377\u53bb\u3002 \u3000\u3000\u602a\u8001\u5934\u60ca\u53eb\u9053\uff1a\u201c\u96f7\u97f3\u4f5b\u638c\uff01\u201d \u3000\u3000\u60ca\u53eb\u58f0\u4e2d\uff0c\u8eab\u5f62\u5c31\u5730\u4e00\u4ec6\uff0c\u539f\u5730\u5df2\u7136\u5931\u53bb\u602a\u8001\u5934\u7684\u8e2a\u8ff9\u3002 \u3000\u3000\u5f3a\u52b2\u638c\u98ce\uff0c\u5374\u5377\u5411\u77f3\u53f0\u4e00\u4fa7\u9ad8\u53ef\u5165\u4e91\u7684\u6570\u682a\u53c2\u5929\u5927\u677e\u4e0a\u53bb\uff0c\u53ea\u542c\u60ca\u5929\u9707\u5730\u7684\u4e00\u58f0\u54cd\uff0c\u6321 \u7740\u638c\u98ce\u7684\u4e00\u68f5\u5927\u677e\uff0c\u5df2\u9f50\u8170\u6298\u65ad\uff0c\u8f70\u8f70\u5730\u5012\u4e86\u4e0b\u53bb\uff0c\u5de8\u5927\u6811\u8eab\u7838\u5728\u5730\u4e0a\uff0c\u679d\u6e85\u53f6\u98de\uff0c\u5c18\u98de\u7070 \u626c\uff0c\u9686\u9686\u5de8\u54cd\u4e4b\u58f0\uff0c\u4e07\u5c71\u56de\u5e94\uff0c\u5386\u4e45\u4e0d\u7edd\u3002 \u3000\u3000\u5c55\u767d\u548b\u820c\u9053\uff1a\u201c\u597d\u5927\u7684\u638c\u529b\uff01\u4e00\u4e2a\u4eba\u80fd\u4fee\u70bc\u5230\u8fd9\u79cd\u7a0b\u5ea6\uff0c\u7684\u786e\u4f7f\u4eba\u4e0d\u53ef\u601d\u8bae\u2026\u2026\u201d \u3000\u3000\u5a49\u513f\u4e5f\u70b9\u5934\u9053\uff1a\u201c\u6211\u7239\u95e8\u4e0b\u98df\u5ba2\uff0c\u4e0d\u5c11\u662f\u6b66\u6797\u4e2d\u9876\u5c16\u9ad8\u624b\uff0c\u7d20\u5e38\u89c1\u4ed6\u4eec\u52a8\u6b66\u8fc7\u62db\uff0c\u6bd4\u8bd5 \u638c\u529b\uff0c\u4e5f\u4ece\u6ca1\u6709\u89c1\u8fc7\u8fd9\u4e48\u5927\u7684\u529b\u9053\uff01\u201d \u3000\u3000\u201c\u4f60\u7236\u4eb2\uff01\u201d\u5c55\u767d\u7a81\u7136\u60f3\u5230\u201c\u6458\u661f\u624b\u201d\u6155\u5bb9\u6db5\uff0c\u4e5f\u662f\u81ea\u5df1\u6740\u7236\u4ec7\u4eba\u4e4b\u4e00\uff0c\u4e0d\u7531\u95ee\u9053\uff1a \u201c\u53ef\u662f\u2018\u6458\u661f\u624b\u2019\u6155\u5bb9\u6db5\uff1f\u201d \u3000\u3000\u5a49\u513f\u767d\u4e86\u4ed6\u4e00\u773c\uff0c\u5e7d\u5e7d\u8bf4\u9053\uff1a\u201c\u4f60\u660e\u77e5\u9053\uff0c\u8fd8\u95ee\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u90a3\u4e48\uff0c\u4f60\u4e3a\u4ed8\u4e48\u4e0d\u968f\u7236\u59d3\u6155\u5bb9\uff0c\u201d\u5c55\u767d\u95ee\u9053\uff1a\u201c\u800c\u8981\u59d3\u5c55\u5462\uff1f\u201d \u3000\u3000\u201c\u4f60\u662f\u5f53\u771f\u5584\u5fd8\uff0c\u8fd8\u662f\u6545\u610f\u88c5\u7cca\u6d82\uff1f\u201d\u5a49\u513f\u4e0d\u9ad8\u5174\u5730\u8bf4\uff1a\u201c\u6211\u4e0d\u662f\u65e9\u5c31\u544a\u8bc9\u8fc7\u4f60\uff0c\u6211\u662f \u968f\u6bcd\u59d3\u5417\uff1f\u201d \u3000\u3000\u201c\u4e16\u4e0a\u7684\u4eba\uff0c\u901a\u5e38\u90fd\u662f\u968f\u7236\u59d3\uff0c\u5f88\u5c11\u8ddf\u6bcd\u59d3\u3002\u201d\u5c55\u767d\u4ecd\u4e0d\u80fd\u91ca\u7136\u4e8e\u6000\u9053\uff1a\u201c\u5a49\u513f\u59d1\u5a18\uff0c \u4f60\u4e5f\u8bb8\u4e0d\u662f\u2018\u6458\u661f\u624b\u2019\u7684\u4eb2\u751f\u5973\u513f\uff01\u201d \u3000\u3000\u5a49\u513f\u8138\u8272\u5927\u53d8\uff0c\u6012\u9053\uff1a\u201c\u4f60\u4e0d\u76f8\u4fe1\u6211\uff01\u4ee5\u4e3a\u6211\u4f1a\u8bf4\u8c0e\u5417\uff1f\u201d \u3000\u3000\u5c55\u767d\u5fc3\u4e2d\u4e00\u9635\u96be\u8fc7\uff0c\u6697\u60f3\uff1a\u201c\u5a49\u513f\u662f\u8fd9\u4e48\u4e00\u4e2a\u7eaf\u771f\u5584\u826f\u7684\u5c11\u5973\uff0c\u4e14\u4e24\u6b21\u6570\u4e86\u81ea\u5df1\u6027\u547d\uff0c \u5047\u5982\u81ea\u5df2\u4e3a\u62a5\u7236\u4ec7\uff0c\u8981\u53bb\u6740\u6b7b\u5979\u7684\u7236\u4eb2\u7684\u65f6\uff0c\u5979\u4e0d\u77e5\u5bf9\u81ea\u5df1\u8be5\u600e\u6837\u7684\u75db\u6068\uff1f\u2026\u2026\u201d \u3000\u3000\u5a49\u513f\u5fc3\u4e2d\u672c\u6765\u6781\u7231\u5c55\u767d\uff0c\u82e5\u4e0d\u7136\u5979\u4e0d\u4f1a\u5077\u5077\u79bb\u5bb6\uff0c\u5403\u82e6\u5192\u9669\u6765\u627e\u4ed6\u4e86\u3002 \u3000\u3000\u53ea\u662f\u5c55\u767d\u521a\u624d\u95ee\u7684\u8bdd\uff0c\u4f7f\u5979\u8fc7\u5206\u96be\u582a\uff0c\u624d\u4e0d\u5ba2\u6c14\u5730\u9876\u649e\u4e86\u5c55\u767d\u4e24\u53e5\uff0c\u5982\u4eca\uff0c\u89c1\u5c55\u81ea\u7709\u5934 \u7d27\u76b1\uff0c\u6c89\u541f\u4e0d\u8bed\uff0c\u4ee5\u4e3a\u5c55\u767d\u751f\u5979\u7684\u6c14\u4e86\uff0c\u53c8\u8001\u5927\u4e0d\u5fcd\uff0c\u5fd9\u9053\uff1a\u5c55\u54e5\u54e5\uff0c\u4f60\u751f\u6211\u7684\u6c14\u4e86\u5417\uff1f\u201d \u3000\u3000\u5c55\u767d\u6447\u4e86\u6447\u5934\uff0c\u957f\u5401\u4e86\u4e00\u53e3\u6c14\u9053\uff1a\u201c\u6211\u5e76\u6ca1\u6709\u751f\u4f60\u7684\u6c14\uff0c\u53ea\u662f\u2026\u2026\u54ce\u5440\uff01\u201d \u3000\u3000\u5c55\u767d\u8bf4\u5230\u8fd9\u91cc\uff0c\u731b\u62ac\u5934\u89c1\u77f3\u53f0\u4e0a\u4e24\u4e2a\u8001\u4eba\u62fc\u6597\u5df2\u5230\u4e86\u751f\u6b7b\u4e00\u53d1\u7684\u5371\u9669\u5173\u5934\uff0c\u4e0d\u7531\u60ca\u547c\u51fa \u58f0\u3002 \u3000\u3000\u5a49\u513f\u4e5f\u88ab\u4ed6\u8c05\u53eb\u4e4b\u58f0\u8b66\u89c9\uff0c\u5fd9\u8f6c\u5934\u5411\u77f3\u53f0\u4e0a\u770b\u53bb\uff0c\u53ea\u89c1\u4e24\u4e2a\u8001\u4eba\uff0c\u5728\u53f0\u4e0a\u72b9\u5982\u6fc0\u6012\u7684\u4e24 \u53ea\u96c4\u9e21\uff0c\u4e92\u76f8\u77aa\u7740\u7ed5\u5708\u5b50\u3002 \u3000\u3000\u4e24\u4e2a\u8001\u4eba\u5df2\u4e0d\u518d\u662f\u98d8\u98ce\u95ea\u7535\u8fc5\u75be\u731b\u6251\uff0c\u800c\u662f\u5c48\u8eab\u584c\u6b65\uff0c\u7ed5\u573a\u7f13\u7f13\u8d70\uff0c\u4f46\u5149\u8292\u5982\u7535\u7684\u53cc \u773c\uff0c\u4e00\u77ac\u4e0d\u77ac\u5730\u5bf9\u671b\u7740\uff0c\u7ed5\u534a\u5929\u624d\u4e92\u76f8\u731b\u7136\u6253\u51fa\u4e00\u638c\uff0c\u52b2\u5578\u7834\u7a7a\uff0c\u58f0\u82e5\u96f7\u9e23\u3002 \u3000\u3000\u522b\u770b\u4e24\u4e2a\u6253\u5f97\u6162\u4e86\uff0c\u8868\u9762\u4e0a\u6ca1\u6709\u521a\u624d\u731b\u6251\u72e0\u640f\u6765\u5f97\u7d27\u5f20\u70ed\u70c8\u3002 \u3000\u3000\u4f46\u5c55\u767d\u4e0e\u5a49\u513f\u5374\u662f\u8bc6\u8d27\u8005\uff0c\u77e5\u9053\u4e24\u4e2a\u8001\u4eba\u8fd9\u79cd\u6253\u6cd5\uff0c\u662f\u4e92\u76f8\u4ee5\u4e2d\u8eab\u771f\u529b\u786c\u62fc\uff0c\u4e00\u70b9\u53d6\u5de7 \u4f59\u5730\u90fd\u6ca1\u6709\uff0c\u800c\u4e14\uff0c\u6bcf\u4e00\u62db\u90fd\u53ef\u5f00\u7891\u88c2\u77f3\uff0c\u7a0d\u4e00\u4e0d\u614e\uff0c\u4e07\u65e0\u751f\u7406\u3002 \u3000\u3000\u5c55\u767d\u5fc3\u5207\u7236\u4ec7\uff0c\u5173\u5fc3\u201c\u795e\u9a74\u94c1\u80c6\u201d\u7684\u6210\u8d25\uff0c\u7236\u4eb2\u60e8\u6b7b\u7684\u771f\u8c61\uff0c\u53ea\u6709\u4ed6\u624d\u77e5\u9053\u5f97\u6e05\u695a\uff0c\u800c \u4e14\u81ea\u5df1\u6b66\u529f\u592a\u5dee\uff0c\u8fd8\u8981\u6073\u6c42\u4ed6\u8001\u4eba\u5bb6\u6536\u5f55\uff0c\u53ef\u4ee5\u8bf4\u81ea\u5df1\u80fd\u5426\u62a5\u5f97\u7236\u4ec7\uff0c\u5168\u5728\u6b64\u8001\u8eab\u4e0a\u3002 \u3000\u3000\u5047\u5982\u8fd9\u201c\u795e\u9a74\u94c1\u80c6\u201d\u8d25\u5728\u602a\u8001\u5934\u624b\u4e2d\uff0c\u81ea\u5df1\u4e00\u5207\u7684\u5e0c\u671b\u90fd\u5c06\u6210\u4e3a\u6ce1\u5f71\uff0c\u56e0\u6b64\uff0c\u4ed6\u7d27\u5f20\u5f97 \u638c\u5fc3\u90fd\u6e17\u51fa\u51b7\u6c57\u6765\u3002 \u3000\u3000\u56fa\u7136\u8fd9\u5931\u53bb\u53cc\u817f\u7684\u602a\u8001\u5934\uff0c\u6b66\u529f\u4e4b\u9ad8\uff0c\u4e5f\u662f\u5c18\u4e16\u7f55\u89c1\uff0c\u4f46\u5c55\u767d\u5374\u4ece\u672a\u60f3\u5230\u6073\u6c42\u505a\u4ed6\u7684\u5f1f \u5b50\uff0c\u4e00\u662f\u770b\u602a\u8001\u5934\u5e26\u7740\u4e00\u8eab\u90aa\u6c14\uff0c\u8a00\u884c\u4e3e\u6b62\u3001\u6b66\u529f\u8def\u6570\u5747\u4e0d\u50cf\u6b63\u6d3e\u4eba\u58eb\uff0c\u518d\u8005\uff0c\u602a\u8001\u5934\u4e5f\u4e0d \u4f1a\u77e5\u9053\u4ed6\u7236\u4eb2\u60e8\u6b7b\u7684\u771f\u50cf\u3002 \u3000\u3000\u6240\u4ee5\uff0c\u4e24\u4e2a\u8001\u4eba\u867d\u7136\u4e0e\u5c55\u767d\u90fd\u6ca1\u6709\u4ec0\u4e48\u6e0a\u6e90\uff0c\u4f46\u5c55\u767d\u5374\u70ed\u5207\u5e0c\u671b\u7740\u201c\u795e\u9a74\u94c1\u80c6\u201d\u5f97\u80dc\u3002 \u3000\u3000\u5a49\u513f\u5374\u5bf9\u4e24\u4e2a\u8001\u4eba\u7684\u80dc\u8d25\u6f20\u4e0d\u5173\u5fc3\uff0c\u5979\u6574\u4e2a\u5fc3\u7075\u5df2\u5b8c\u5168\u653e\u5728\u5c55\u767d\u8eab\u4e0a\uff0c\u89c1\u5c55\u767d\u7d27\u5f20\u5f97\u6d51 \u8eab\u76f4\u6296\uff0c\u989d\u4e0a\u9690\u9690\u89c1\u6c57\uff0c\u4e0d\u7531\u67d4\u60c5\u4e07\u79cd\u5730\u8bf4\u9053\uff1a\u201c\u5c55\u54e5\u54e5\uff0c\u4f60\u4f55\u5fc5\u8fd9\u4e48\u7d27\u5f20\uff1f\u4ed6\u4eec\u4e24\u4e2a\u4eba\u90fd \u662f\u8001\u602a\u7269\uff0c\u8c01\u80dc\u8c01\u8d25\uff0c\u8ddf\u6211\u4eec\u53c8\u6709\u4ec0\u4e48\u5173\u7cfb\u5462\uff1f\u201d \u3000\u3000\u5c55\u767d\u5374\u53cc\u670d\u6ce8\u5b9a\u53f0\u4e0a\uff0c\u5bf9\u5a49\u513f\u67d4\u60c5\u871c\u610f\u7684\u8bdd\uff0c\u72b9\u5982\u672b\u95fb\u3002\u5ffd\u7136\u53c8\u628a\u5a49\u513f\u501a\u5230\u4ed6\u8eab\u4e0a\u7684\u5a07 \u8eaf\u63a8\u5f00\uff0c\u7eb5\u8eab\u8dc3\u4e0b\u5730\u6765\uff0c\u76f4\u5411\u77f3\u53f0\u4e0a\u7eb5\u53bb\u3002 \u3000\u3000\u201c\u5c55\u54e5\u54e5\uff01\u53bb\u4e0d\u5f97\uff01\u201d \u3000\u3000\u5a49\u513f\u60ca\u547c\u51fa\u58f0\uff0c\u5979\u53eb\u5c55\u767d\u4e0d\u80fd\u53bb\uff0c\u81ea\u5df1\u5374\u5fcd\u4e0d\u4f4f\u4e5f\u7eb5\u4e0b\u5730\u6765\uff0c\u98de\u8eab\u63a0\u81f3\u53f0\u4e0a\u3002 \u3000\u3000\u6b64\u65f6\uff0c\u4e24\u4e2a\u8001\u4eba\u5df2\u4e0d\u518d\u56f4\u573a\u7ed5\u8d70\uff0c\u800c\u662f\u76f8\u5bf9\u800c\u7acb\uff0c\u53cc\u638c\u9065\u9065\u76f8\u632b\uff0c\u5185\u529f\u771f\u529b\u4e0d\u65ad\u4ece\u4e8c\u8001 \u638c\u5fc3\u6e90\u6e90\u6d8c\u51fa\uff0c\u4e8c\u8001\u8863\u98d8\u53d1\u626c\uff0c\u5934\u4e0a\u5192\u7740\u84b8\u84b8\u70ed\u6c14\uff0c\u811a\u4e0b\u90e8\u5411\u9752\u77f3\u5730\u9762\u6df1\u9677\u4e0b\u53bb\u3002 \u3000\u3000\u663e\u89c1\u5f97\u4e8c\u8001\u5df2\u4e92\u76f8\u8f83\u4e0a\u4e86\u771f\u529b\uff0c\u4e14\u5230\u4e86\u5371\u673a\u4e00\u53d1\uff0c\u7acb\u89c1\u751f\u6b7b\u7684\u5173\u5934\u3002 \u3000\u3000\u201c\u795e\u9a74\u94c1\u80c6\u201d\u9762\u8272\u51dd\u91cd\uff0c\u9a91\u9a6c\u8e72\u6321\u7ad9\u597d\uff0c\u987b\u53d1\u7686\u7acb\uff0c\u53cc\u76ee\u6012\u7741\uff0c\u8db3\u4e0b\u4e00\u53cc\u9752\u7f0e\u56e2\u82b1\u53cc\u6881 \u798f\u5c65\uff0c\u5df2\u7136\u5b8c\u5168\u7efd\u5f00\uff0c\u53cc\u811a\u5df2\u9677\u8fdb\u575a\u786c\u65e0\u6bd4\u7684\u9752\u77f3\u5730\u9762\u4e09\u5bf8\u6df1\uff0c\u663e\u89c1\u5403\u529b\u975e\u5e38\u3002 \u3000\u3000\u90a3\u602a\u8001\u5934\u7684\u59ff\u5f0f\u5374\u66f4\u602a\uff0c\u72ec\u6728\u6869\u5165\u5730\u5df2\u6709\u534a\u622a\uff0c\u53cc\u62d0\u5957\u5728\u53cc\u80a9\u4e0a\uff0c\u62d0\u5c16\u6491\u5728\u8eab\u540e\uff0c\u4e5f\u9677 \u8fdb\u5730\u9762\u5f88\u6df1\u4e86\uff0c\u4f8b\u50cf\u4e2a\u4e09\u89d2\u67b6\u4e00\u822c\uff0c\u652f\u6301\u4e86\u4ed6\u7684\u534a\u85cf\u8eab\u8eaf\uff0c\u65e0\u7591\u5730\u8fd9\u7b49\u4e8e\u6709\u4e09\u6761\u817f\uff0c\u5728\u7ad9\u59ff \u4e0a\u591a\u4e86\u4e00\u6761\u817f\u7684\u4fbf\u5b9c\uff0c\u800c\u4e14\u4e0d\u865e\u5026\u4e4f\u3002 \u3000\u3000\u4f46\u4ed6\u53cc\u638c\u5e73\u7f6e\u80f8\u524d\uff0c\u53cc\u81c2\u5fae\u5fae\u53d1\u6296\uff0c\u5934\u4e0a\u767d\u6c14\u84b8\u817e\uff0c\u8981\u6bd4\u201c\u795e\u9a74\u94c1\u80c6\u201d\u6d53\u539a\u5f97\u591a\uff0c\u53ef\u89c1 \u4e5f\u6ca1\u6709\u5360\u5230\u4e0a\u98ce\u3002 \u3000\u3000\u5c55\u767d\u77e5\u9053\u8fd9\u79cd\u4e92\u62fc\u5185\u529b\u7684\u6253\u6cd5\uff0c\u6700\u4e3a\u5371\u9669\u4e0d\u8fc7\uff0c\u53cc\u65b9\u4e4b\u4e2d\uff0c\u4efb\u4f55\u4e00\u65b9\u529f\u529b\u8f6c\u5f31\uff0c\u7565\u89c1\u4e0d \u6d4e\uff0c\u7acb\u88ab\u5bf9\u65b9\u771f\u529b\u9707\u88c2\u5185\u8151\u4e94\u810f\u800c\u6b7b\uff0c\u5c31\u662f\u53cc\u65b9\u529f\u529b\u76f8\u7b49\uff0c\u4e5f\u5f97\u843d\u4e2a\u4e24\u8d25\u60e7\u4f24\uff0c\u540c\u5f52\u4e8e\u5c3d\uff0c \u4e0d\u7531\u6025\u9053\uff1a\u201c\u4e8c\u4f4d\u8001\u524d\u8f88\uff01\u6709\u4ec0\u4e48\u8bdd\u4e0d\u597d\u5546\u91cf\uff0c\u4f55\u5fc5\u5b9a\u8981\u843d\u4e2a\u540c\u5f52\u4e8e\u5c3d\uff1f\u201d \u3000\u3000\u4f46\u4e24\u4e2a\u8001\u4eba\u5df2\u7ecf\u62fc\u4e0a\u4e86\uff0c\u6b63\u5728\u5371\u673a\u4e00\u53d1\u4e4b\u9645\uff0c\u54ea\u6709\u5fc3\u60c5\u542c\u5c55\u767d\u4e4b\u8a00\uff1f\u5c31\u662f\u542c\u5230\u4e86\uff0c\u4e5f\u65e0 \u6687\u56de\u7b54\u3002\u56e0\u6b64\uff0c\u4ecd\u7136\u5728\u90a3\u91cc\u54ac\u7259\u786c\u62fc\u3002 \u3000\u3000\u5c55\u767d\u5fc3\u6025\u8d77\u6765\uff0c\u6b3a\u8eab\u524d\u8fdb\uff0c\u7528\u610f\u662f\u60f3\u628a\u4e24\u4e2a\u8001\u4eba\u5206\u5f00\u2026\u2026 \u3000\u3000\u5c55\u767d\u521a\u5f80\u524d\u8d70\u4e86\u4e24\u6b65\uff0c\u5a49\u513f\u6025\u628a\u4ed6\u62c9\u4f4f\u9053\uff1a\u201c\u5c55\u54e5\u54e5\uff0c\u8fc7\u53bb\u4e0d\u5f97\uff01\u6b64\u65f6\uff0c\u4ed6\u4e8c\u4eba\u529f\u529b\u8fd0 \u81f3\u9876\u5cf0\uff0c\u7f61\u529b\u56db\u5e03\uff0c\u4e0d\u7b49\u4f60\u8d70\u5230\u4ed6\u4eec\u8eab\u8fb9\uff0c\u4fbf\u8981\u88ab\u9707\u4f24\u7684\uff01\u2026\u2026\u201d \u3000\u3000\u5c55\u767d\u6323\u8131\u5a49\u51e0\u7684\u624b\uff0c\u8bf4\u9053\uff1a\u201c\u603b\u4e0d\u80fd\u773c\u770b\u7740\u4e24\u4f4d\u8001\u4eba\u5bb6\u5c31\u843d\u4e2a\u4e24\u8d25\u4ff1\u4f24\u5440\uff01\u201d\u8bf4\u7740\u76f4\u5411 \u4e8c\u8001\u8eab\u524d\u95ef\u53bb\u3002 \u3000\u3000\u53ef\u662f\u5c1a\u9694\u7740\u6709\u4e24\u4e08\u8fdc\uff0c\u5c55\u767d\u4fbf\u89c9\u5f97\u8eab\u524d\u6709\u4e00\u822c\u65e0\u5f62\u5927\u529b\uff0c\u628a\u81ea\u5df1\u7684\u8eab\u5f62\u963b\u4f4f\u3002 \u3000\u3000\u5c55\u767d\u518d\u8fc8\u6b65\u524d\u95ef\uff0c\u5df2\u662f\u4e0d\u80fd\u4e3e\u6b65\uff0c\u5fc3\u5934\u5fae\u60ca\uff0c\u4f46\u4ecd\u4e0d\u6b7b\u5fc3\uff0c\u731b\u529b\u5411\u524d\u4e00\u51b2\uff0c\u53ea\u542c\u201c\u562d\u201d \u7684\u4e00\u58f0\uff0c\u4e0d\u4f46\u672a\u80fd\u524d\u8fdb\uff0c\u53cd\u800c\u628a\u5c55\u767d\u5012\u9707\u51fa\u6570\u6b65\uff0c\u6c14\u7ffb\u8840\u6d8c\uff0c\u53cc\u8033\u96f7\u9e23\uff0c\u4e0d\u7531\u6697\u6697\u548b\u820c\u9053\uff1a \u201c\u597d\u5389\u5bb3\uff01\u201d \u3000\u3000\u5a49\u513f\u8d85\u5fd9\u4e0a\u524d\u6276\u4f4f\uff0c\u8bf4\u9053\uff1a\u201c\u5c55\u54e5\u54e5\uff0c\u4f60\u4f24\u7740\u4e86\u6ca1\u6709\uff1f\u201d \u3000\u3000\u5c55\u767d\u6447\u4e86\u6447\u5934\u9053\uff1a\u201c\u4e0d\u59a8\u4e8b\u2026\u2026\u201d \u3000\u3000\u8c01\u77e5\u5c55\u767d\u8bdd\u672a\u8bf4\u5b8c\uff0c\u5ffd\u542c\u4e24\u4e2a\u8001\u4eba\u5404\u81ea\u5927\u559d\u4e00\u58f0\uff0c\u60ca\u5929\u52a8\u5730\u7684\u4e00\u58f0\u5927\u9707\uff0c\u4eff\u4f5b\u4e00\u4e2a\u5f3a\u5927 \u7684\u6c14\u7206\uff0c\u7a81\u7136\u70b8\u5f00\uff0c\u52b2\u6d41\u6fc0\u8361\u56db\u8c7a\uff0c\u5f62\u6210\u65e0\u6570\u80a1\u5c0f\u578b\u9f99\u65cb\u98ce\uff0c\u56db\u65cb\u6563\u5f00\u3002 \u3000\u3000\u5c55\u767d\u4e0e\u5a49\u513f\u7ad9\u5728\u4e09\u6587\u5f00\u5916\uff0c\u4ecd\u88ab\u90a3\u4e00\u5de8\u5927\u7684\u529b\u91cf\uff0c\u903c\u9000\u6570\u6b65\uff0c\u4e00\u4e2a\u7acb\u8db3\u4e0d\u7a33\uff0c\u53cc\u53cc\u4ece\u4e8c \u4e08\u4f59\u9ad8\u7684\u77f3\u53f0\u4e0a\u8dcc\u4e86\u4e0b\u6765\u3002\u597d\u5728\u4e8c\u4eba\u7ad9\u5f97\u8fdc\uff0c\u4e8c\u8001\u638c\u529b\u53c8\u4e0d\u662f\u5411\u4ed6\u4e8c\u4eba\u53d1\u51fa\uff0c\u867d\u7136\u88ab\u638c\u98ce\u4f59 \u529b\u903c\u4e0b\u53f0\u6765\uff0c\u5e76\u672a\u8d1f\u4f24\uff0c\u534a\u7a7a\u4e2d\u4e8c\u4eba\u7565\u4e00\u63d0\u52b2\uff0c\u8db3\u5c16\u4e00\u70b9\u5730\u9762\uff0c\u53c8\u53cc\u53cc\u7a9c\u4e0a\u53f0\u6765\u3002 \u3000\u3000\u4e8c\u4eba\u4e0d\u7ea6\u800c\u540c\u7a9c\u4e0a\u77f3\u53f0\uff0c\u518d\u4e00\u770b\u77f3\u53f0\u4e0a\u7684\u666f\u8c61\uff0c\u628a\u4e8c\u4eba\u540c\u65f6\u5413\u5f97\u4e00\u6014\u3002 \u3000\u3000\u53ea\u89c1\u201c\u795e\u9a74\u94c1\u80c6\u201d\u8463\u5343\u91cc\u9762\u8272\u60e8\u767d\uff0c\u5634\u89d2\u3001\u767d\u53d1\u90fd\u6cbe\u6ee1\u4e86\u8840\u8ff9\uff0c\u76d8\u819d\u5750\u5728\u5730\u4e0a\uff0c\u53cc\u773c\u7d27 \u95ed\uff0c\u4f3c\u662f\u5df2\u53d7\u4e86\u91cd\u4f24\u3002 \u3000\u3000\u518d\u770b\u90a3\u602a\u8001\u5934\uff0c\u4e0b\u8eab\u6728\u6869\u53ca\u6491\u5728\u8eab\u540e\u7684\u53cc\u7f00\uff0c\u4e00\u9f50\u6298\u65ad\uff0c\u534a\u85cf\u8eab\u8eaf\u5750\u5728\u5730\u4e0a\uff0c\u4e5f\u662f\u53cc\u76ee \u7d27\u95ed\uff0c\u9762\u8272\u59dc\u9ec4\uff0c\u5634\u89d2\u8863\u895f\u67d3\u6ee1\u4e86\u9c9c\u76bf\u3002 \u3000\u3000\u663e\u89c1\u4e24\u4e2a\u8001\u4eba\u5df2\u540c\u65f6\u8d1f\u4f24\u3002 \u3000\u3000\u5c55\u767d\u56fe\u524d\u4e5d\u6b65\uff0c\u8d70\u5230\u201c\u795e\u9a74\u94c1\u80c6\u201d\u9762\u524d\uff0c\u6025\u9053\uff1a\u201c\u8463\u8001\u524d\u8f88\uff0c\u60a8\u53d7\u4e86\u4f24\uff1f\u201d \u3000\u3000\u201c\u795e\u9a74\u94c1\u80c6\u201d\u95ed\u76ee\u4e0d\u8bed\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u624d\u4f38\u624b\u4ece\u6000\u53e3\uff1a\u638f\u51fa\u51e0\u7c92\u4e38\u836f\uff0c\u653e\u8fdb\u53e3\u4e2d\uff0c\u683c\u5d29\uff01 \u683c\u5d29\uff01\u4e00\u9635\u5480\u56bc\uff0c\u54bd\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u8fd9\u624d\u5f20\u773c\uff0c\u60e8\u7b11\u9053\uff1a\u8001\u602a\u7269\uff0c\u4f60\u8fd8\u6d3b\u7740\u5417\uff1f\u201d \u3000\u3000\u201c\u653e\u5fc3\uff01\u201d\u602a\u8001\u5934\u4e5f\u5f20\u5f00\u773c\u775b\uff0c\u60e8\u7b11\u9053\uff1a\u4f60\u9a91\u9a74\u8001\u513f\u4e0d\u6b7b\uff0c\u6211\u8001\u4eba\u5bb6\u4e5f\u51b3\u6b7b\u4e0d\u4e86\uff01\u201d\u8bf4 \u7740\u4e5f\u4ece\u6000\u4e2d\u638f\u51fa\u4e00\u5305\u836f\u7c89\u541e\u4e0b\u53bb\u3002\u201c\u5509\uff01\u201d \u3000\u3000\u8463\u5343\u91cc\u957f\u53f9\u4e00\u58f0\u9053\uff1a\u8001\u602a\u7269\uff0c\u4f60\u662f\u6211\u751f\u5e73\u4ec5\u89c1\u7684\u5f3a\u654c\uff01\u201d \u3000\u3000\u201c\u5f7c\u6b64\uff01\u5f7c\u6b64\uff01\u201d\u602a\u8001\u5934\u4e5f\u9053\uff1a\u201c\u6211\u8001\u4eba\u5bb6\u5e73\u751f\u4ee5\u6253\u901a\u5929\u4e0b\u65e0\u654c\u624b\u81ea\u8be9\uff0c\u6ca1\u60f3\u5230\u4e34\u6b7b\u4e4b \u524d\u78b0\u5230\u4f60\uff0c\u867d\u7136\u6211\u4eec\u90fd\u6d3b\u4e0d\u4e45\u5566\uff0c\u53ef\u662f\u6253\u5f97\u4e5f\u8fc7\u763e\uff0c\u7ec3\u6b66\u7684\u4eba\u6b7b\u5728\u6b66\u529f\u4e0a\uff0c\u8fd9\u624d\u53eb\u6b7b\u5f97\u5176 \u6240\uff01\u201d \u3000\u3000\u201c\u53ef\u662f\uff0c\u201d\u8463\u5343\u91cc\u9053\uff1a\u201c\u6211\u600e\u4e48\u5bf9\u4f60\u8fd9\u8001\u602a\u7269\u9762\u751f\u5f97\u7d27\uff0c\u597d\u50cf\u6c5f\u6e56\u9053\u4e0a\u4ece\u6ca1\u6709\u89c1\u8fc7\uff1f\u4f60 \u5230\u5e95\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u80fd\u5426\u89c1\u544a\uff0c\u4e5f\u4e0d\u6789\u6211\u4eec\u4e92\u76f8\u62fc\u6b7b\u4e00\u573a\u2026\u2026\u201d \u3000\u3000\u602a\u8001\u5934\u54c8\u54c8\u5927\u7b11\uff0c\u4f46\u4e2d\u6c14\u5df2\u6ca1\u6709\u53d7\u4f24\u4e4b\u524d\u90a3\u4e48\u5145\u8db3\uff0c\u7b11\u5b8c\u4e4b\u540e\u9053\uff1a\u201c\u4e8f\u4f60\u8fd8\u53eb\u2018\u795e \u9a74\u2019\uff0c\u96be\u9053\u4f60\u5c31\u6ca1\u542c\u8bf4\u8fc7\u2018\u795e\u7334\u2019\u4e4b\u540d\u5417\uff1f\u201d \u3000\u3000\u8463\u5343\u91cc\u201c\u54e6\u201d\u4e86\u4e00\u58f0\uff0c\u4f3c\u662f\u604d\u7136\u5927\u609f\u9053\uff1a\u201c\u4f60\u5c31\u662f\u5341\u6570\u5e74\u524d\u5357\u8352\u9ece\u8d21\u5c71\u2018\u795e\u7334\u2019\u94c1\u51cc \u5417\uff1f\u201d \u3000\u3000\u201c\u6b63\u662f\u8001\u592b\uff01\u201d\u602a\u8001\u5934\u9053\uff1a\u201c\u5357\u7334\u5317\u9a74\uff0c\u867d\u7136\u6211\u4eec\u4ece\u672a\u8c0b\u9762\uff0c\u5341\u6570\u5e74\u524d\u6c5f\u6e56\u9053\u4e0a\u7684\u670b \u53cb\uff0c\u65e9\u628a\u6211\u4eec\u8001\u54e5\u4fe9\u5e76\u5217\u4e86\uff01\u201d \u3000\u3000\u5c55\u767d\u4e0e\u5a49\u513f\u542c\u8fd9\u602a\u8001\u5934\u5c31\u662f\u5341\u6570\u5e74\u524d\u540d\u6ee1\u5929\u4e0b\u7684\u201c\u795e\u7334\u201d\u94c1\u51cc\uff0c\u4e0d\u7531\u540c\u65f6\u9732\u51fa\u60ca\u8be7\u7684\u795e \u8272\uff0c\u56e0\u4e3a\u201c\u795e\u7334\u201d\u867d\u7136\u5341\u6570\u5e74\u672a\u73b0\u6c5f\u6e56\uff0c\u4f46\u4ed6\u5f53\u5e74\u90a3\u4e9b\u51fa\u795e\u5165\u5316\u7684\u8f76\u4e8b\uff0c\u5374\u662f\u6b66\u6797\u4e2d\u8001\u4e00\u8f88 \u7684\u4eba\u7269\u6d25\u6d25\u4e50\u9053\u7684\u3002 \u3000\u3000\u6ca1\u60f3\u5230\u8fd9\u53ea\u95fb\u5176\u540d\u3001\u672b\u89c1\u5176\u9762\u7684\u4f20\u5947\u4eba\u7269\uff0c\u7ade\u5728\u6b64\u65f6\u6b64\u5730\u76f8\u9047\u3002\u518d\u4e00\u770b\u602a\u8005\u5934\u957f\u50cf\uff0c\u5c16 \u5634\u7334\u816e\uff0c\u53cc\u773c\u706b\u7ea2\uff0c\u8fd8\u771f\u50cf\u4e2a\u7334\u5b50\u4e00\u6837\uff0c\u867d\u611f\u6ed1\u7a3d\uff0c\u5374\u7b11\u4e0d\u51fa\u58f0\u6765\u2026\u2026 \u3000\u3000\u8463\u5343\u91cc\u5ffd\u7136\u4ef0\u5929\u5927\u7b11\uff0c\u76f8\u540c\u7684\u662f\u7b11\u58f0\u5636\u54d1\uff0c\u4e5f\u5931\u53bb\u4e86\u53d7\u4f24\u4e4b\u524d\u7684\u6d2a\u4eae\uff0c\u7b11\u7f62\u8bf4\u9053\uff1a\u201c\u5341 \u6570\u5e74\u65e9\u601d\u4e00\u4f1a\uff0c\u5374\u56e0\u7410\u52a1\u7f20\u8eab\uff0c\u59cb\u7ec8\u672a\u80fd\u5982\u613f\uff0c\u6ca1\u60f3\u5230\u5341\u6570\u5e74\u4e4b\u540e\uff0c\u8fd8\u662f\u78b0\u9762\uff0c\u73b0\u5728\u6211\u4eec\u8001 \u54e5\u4fe9\uff0c\u53ef\u8bf4\u662f\u6b7b\u800c\u65e0\u61be\u4e86\uff01\u201d \u3000\u3000\u201c\u795e\u7334\u201d\u5374\u53cc\u773c\u4e00\u77aa\uff0c\u9762\u8272\u51c4\u7136\uff0c\u8bf4\u9053\uff1a\u201c\u4e34\u6b7b\u4e4b\u524d\uff0c\u80fd\u4e0e\u4f60\u2018\u795e\u9a74\u2019\u4e00\u4f1a\uff0c\u7684\u786e\u662f\u6170 \u5c3d\u5e73\u751f\u4e4b\u613f\uff0c\u4f46\u4e0d\u80fd\u8bf4\u6beb\u65e0\u9057\u61be\uff01\u201d \u3000\u3000\u201c\u795e\u9a74\u201d\u8463\u5343\u91cc\u4e00\u609f\u9053\uff1a\u201c\u8001\u592b\u5e74\u5df2\u8fd1\u767e\uff0c\u76f8\u4fe1\u4f60\u7684\u5c81\u6570\u4e5f\u4e0d\u6bd4\u6211\u5c0f\uff0c\u50cf\u6211\u4eec\u884c\u9053\u6c5f \u6e56\uff0c\u5728\u5200\u5c16\u4e0a\u6253\u6eda\uff0c\u80fd\u6d3b\u5230\u8fd9\u4e2a\u5c81\u6570\u5df2\u662f\u4e0d\u6613\uff0c\u800c\u4e14\u4e34\u6b7b\u8fd8\u6b7b\u5728\u4e92\u76f8\u6155\u540d\u7684\u8001\u670b\u53cb\u624b\u4e2d\uff0c\u8fd8 \u6709\u4ec0\u4e48\u9057\u61be\u4e4b\u4e8b\uff1f\u201d \u3000\u3000\u201c\u795e\u7334\u201d\u94c1\u51cc\u9eef\u7136\u8bf4\u9053\uff1a\u201c\u6211\u6ca1\u6709\u4f60\u90a3\u4e48\u60f3\u5f97\u5f00\uff0c\u60f3\u60f3\u770b\uff0c\u6211\u4eec\u6a2a\u5c38\u8352\u90ca\uff0c\u8fd9\u4e24\u628a\u9aa8 \u5934\uff0c\u8fde\u4e2a\u6536\u5c38\u7684\u4eba\u4e5f\u6ca1\u6709\uff0c\u6b7b\u4e86\u6b7b\u4e86\uff0c\u8fd8\u8981\u53d7\u5140\u9e70\u91ce\u517d\u51cc\u8fb1\uff0c\u7684\u786e\u6b7b\u800c\u4e0d\u5b89\uff01\u201d \u3000\u3000\u201c\u795e\u9a74\u201d\u8463\u5343\u91cc\u4e5f\u9eef\u7136\u9053\uff1a\u201c\u50cf\u6211\u751f\u524d\u53ea\u56fe\u6e05\u95f2\uff0c\u65e0\u513f\u65e0\u5973\uff0c\u4e5f\u6ca1\u6709\u6536\u5f92\u513f\uff0c\u8fd9\u4e24\u6839\u8001 \u9aa8\u5934\uff0c\u65e9\u5c31\u6253\u5b9a\u4e3b\u610f\u5582\u72d7\u4e86\uff0c\u53ef\u662f\uff0c\u542c\u8bf4\u4f60\u8001\u7334\u5b50\u65e9\u5e74\u5c31\u6536\u4e0b\u4e24\u4e2a\u5f1f\u5b50\uff0c\u96be\u9053\u4f60\u7684\u4e24\u4e2a\u5f1f\u5b50 \u8fd8\u4e0d\u4f1a\u4e3a\u4f60\u6536\u5c38\u5417\uff1f\u201d\u542c\u5230\u63d0\u8d77\u4ed6\u7684\u5f1f\u5b50\uff0c\u201c\u795e\u7334\u201d\u8138\u4e0a\u53d8\u4e3a\u6068\u6bd2\uff0c\u54ac\u7259\u8bf4\u9053\uff1a\u4e0d\u63d0\u4e24\u4e2a\u5b7d \u5f92\u8fd8\u7f62\u4e86\uff0c\u63d0\u8d77\u4e24\u4e2a\u5b7d\u5f92\uff0c\u8001\u592b\u6068\u4e0d\u5f97\u751f\u98df\u5176\u8089\uff01\u558f\uff01\u4f60\u770b\uff01\u201d\u8bf4\u7740\u4e00\u6307\u4ed6\u65ad\u53bb\u7684\u53cc\u817f\u9053\uff1a \u201c\u6b8b\u53bb\u53cc\u817f\uff0c\u5bb3\u5f97\u6211\u5341\u6570\u5e74\u4e0d\u80fd\u91cd\u5c65\u6c5f\u518c\uff0c\u5c31\u662f\u6211\u7684\u4e24\u4e2a\u5b7d\u5f92\u6240\u8d50\uff01\u201d\u8fd9\u756a\u8bdd\u542c\u5f97\u5c55\u767d\u548c\u5a49 \u513f\u4e5f\u4e49\u6124\u586b\u81ba\uff0c\u5e38\u8a00\u9053\uff1a\u6069\u5e08\u5982\u6069\u7236\u3002\u201d\u4e16\u4e0a\u7ade\u8fd8\u6709\u5982\u6b64\u72fc\u5fc3\u72d7\u80ba\u4e4b\u4eba\uff0c\u4e0b\u6bd2\u5fc3\u6b8b\u53bb\u5e08\u7236\u53cc \u8db3\uff1f \u3000\u3000\u8463\u5343\u91cc\u66f4\u662f\u6012\u6c14\u51b2\u51b2\u5927\u53eb\u9053\uff1a\u201c\u96be\u9053\u8001\u7334\u513f\u5c31\u628a\u4e24\u4e2a\u5b7d\u5f92\u8f7b\u8f7b\u653e\u8fc7\u4e0d\u6210\uff1f\u201d \u3000\u3000\u201c\u795e\u7334\u201d\u94c1\u51cc\u72e0\u77aa\u4e86\u8463\u5343\u91cc\u4e00\u773c\u9053\uff1a\u8fd8\u8bf4\u5450\uff01\u8981\u4e0d\u662f\u4f60\u8fd9\u8001\u4e0d\u6b7b\u6a2a\u52a0\u963b\u62e6\uff0c\u4e24\u4e2a\u5b7d\u5f92\uff0c \u65e9\u5df2\u6b7b\u5728\u6211\u8fd9\u53cc\u62d0\u4e4b\u4e0b\uff0c\u5c82\u80fd\u8ba9\u4ed6\u4eec\u5154\u8131\u9003\u8d70\uff01\u201d \u3000\u3000\u8463\u5343\u91cc\u8bb6\u7136\u60ca\u547c\uff1a\u201c\u4e09\u5929\u524d\u88ab\u4f60\u8ffd\u5f97\u8d70\u5934\u65e0\u8def\u4e4b\u4eba\uff0c\u5c31\u662f\u5bb3\u4f60\u7684\u4e24\u4e2a\u5b7d\u5f92\u5440\uff01\u8fd8\u6709\u4e00\u4e2a \u5c0f\u4f8f\u5112\uff0c\u90a3\u53c8\u662f\u8c01\uff1f\u201d\u201c\u6b63\u662f\u4e24\u4e2a\u5b7d\u5f92\u3002\u201d\u201c\u795e\u7334\u201d\u55d2\u7136\u82e5\u5931\u8bf4\u9053\uff1a\u201c\u90a3\u5c0f\u77ee\u5b50\u662f\u4e8c\u4eba\u6536\u7684 \u4f20\u4eba\uff0c\u6240\u4ee5\uff0c\u90a3\u5929\u4f60\u963b\u62e6\u6211\u4e0d\u8ba9\u6211\u8ffd\u6740\u4e09\u4eba\uff0c\u7acb\u523b\u6fc0\u8d77\u6211\u7684\u6012\u706b\uff0c\u8ddf\u4f60\u6253\u8d77\u6765\uff0c\u5f53\u65f6\uff0c\u4f60\u4e5f \u8bb8\u8ba4\u4e3a\u6211\u4e0d\u53ef\u7406\u55bb\u2026\u2026\u5509\uff01\u201d \u3000\u3000\u201c\u795e\u7334\u201d\u8bf4\u5230\u8fd9\u91cc\u53f9\u606f\u4e00\u58f0\uff0c\u65e0\u9650\u60cb\u60dc\u5730\u8bf4\u9053\uff1a\u201c\u5f53\u65f6\uff0c\u6211\u4e5f\u662f\u592a\u6027\u6025\uff0c\u8bdd\u672a\u8bf4\u6e05\u695a\uff0c \u4fbf\u8ddf\u4f60\u6253\u4e86\u8d77\u6765\uff0c\u540e\u6765\u53c8\u8ba4\u51fa\u4f60\u662f\u2018\u795e\u9a74\u2019\uff0c\u65e9\u601d\u4e00\u4f1a\u4e4b\u4eba\uff0c\u66f4\u52a0\u4e0d\u613f\u9519\u8fc7\uff0c\u53cd\u5012\u8ba9\u4e24\u4e2a\u5b7d \u5f92\u4ece\u5bb9\u9003\u6389\u4e86\uff0c\u8fd9\u4e00\u6765\u5012\u597d\uff0c\u8fd9\u6e05\u7406\u95e8\u6237\u662f\u505a\u4e0d\u5230\u4e86\uff0c\u7b49\u6211\u8fd9\u4e00\u6b7b\uff0c\u4e24\u4e2a\u5b7d\u5f92\u66f4\u65e0\u5fcc\u60ee\uff0c\u4e0d \u77e5\u5728\u6c5f\u6e56\u4e0a\u8981\u95ef\u51fa\u4ec0\u4e48\u6837\u7684\u7978\u4e8b\u6765\uff1f\u201d\u8bf4\u7f62\u5140\u81ea\u53f9\u606f\u4e0d\u5df2\u3002 \u3000\u3000\u201c\u795e\u9a74\u94c1\u80c6\u201d\u8463\u5343\u91cc\u4e5f\u662f\u6376\u811a\u987f\u8db3\uff0c\u607c\u6094\u4e0d\u5df2\uff0c\u9053\uff1a\u201c\u5509\uff01\u6211\u53ea\u8bf4\u6551\u4eba\u4e00\u547d\uff0c\u6ca1\u60f3\u5230\u6551 \u4e86\u4e24\u4e2a\u574f\u86cb\uff0c\u53ef\u89c1\u884c\u4fa0\u4fa0\u4e49\u4e5f\u83bd\u649e\u4e0d\u5f97\u2026\u2026\u201d \u7b2c\u4e8c\u5341\u516d\u7ae0\u3000\u5782\u6b7b\u4f20\u7edd\u827a>> \u53e4\u9f99\u300a\u5251\u5ba2\u884c\u300b \u7b2c\u4e8c\u5341\u516d\u7ae0\u3000\u5782\u6b7b\u4f20\u7edd\u827a \u3000\u3000\u5c55\u767d\u4e0a\u524d\u4e00\u63d6\u9053\uff1a\u201c\u4e8c\u4f4d\u8001\u4eba\u5bb6\u53d7\u4f24\u6781\u91cd\uff0c\u8bef\u4f1a\u65e2\u5df2\u89e3\u91ca\u5f00\uff0c\u8fd8\u662f\u5c11\u8bf4\u8bdd\u591a\u517b\u795e\uff0c\u5f85\u4f24 \u52bf\u590d\u539f\uff0c\u6709\u4ec0\u4e48\u6d3b\u518d\u8bf4\u4e0d\u8fdf\u2026\u2026\u201d \u3000\u3000\u795e\u9a74\u8463\u5343\u91cc\u53cc\u773c\u4e00\u77aa\u9053\uff1a\u4f60\u4ee5\u4e3a\u6211\u4eec\u4e24\u4e2a\u8001\u4e0d\u6b7b\u8fd8\u4f1a\u6d3b\u7740\u5417\uff1f\u201d \u3000\u3000\u5c55\u767d\u95fb\u8a00\u4e00\u695e\u3002 \u3000\u3000\u795e\u733f\u94c1\u51cc\u5374\u60e8\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u5a03\u513f\u5fc3\u610f\u4e0d\u574f\uff0c\u53ef\u662f\uff0c\u6211\u4fe9\u771f\u529b\u4f7f\u7528\u8fc7\u5de8\u3001\u9707\u4f24\u5185\u8151\uff0c\u5df2 \u7ecf\u6d3b\u4e0d\u4e86\u591a\u4e45\u5566\uff0c\u4e0d\u8d81\u6b64\u591a\u8c08\u8c08\uff0c\u7b49\u5230\u4e00\u4f38\u817f\u2014\u77aa\u773c\uff0c\u60f3\u8c08\u4e5f\u8c08\u4e0d\u6210\u5566\uff01\u201d \u3000\u3000\u5c55\u767d\u53c8\u662f\u4e00\u6014\uff0c\u5fe7\u4f24\u4e4b\u60c5\uff0c\u6ea2\u4e8e\u8a00\u8868\u3002\u9053\uff1a\u96be\u9053\u4e8c\u4f4d\u8001\u4eba\u5bb6\u5c31\u65e0\u836f\u53ef\u6551\u4e86\u5417\uff1f\u6211\u5c55\u767d\u5e74 \u8f7b\u8bc6\u6d45\uff0c\u4e0d\u77e5\u9053\u54ea\u91cc\u6709\u795e\u533b\u5723\u836f\uff0c\u5982\u679c\u4e8c\u4f4d\u8001\u524d\u8f88\u77e5\u9053\uff0c\u544a\u8bc9\u665a\u8f88\u4e00\u58f0\uff0c\u665a\u8f88\u4e00\u5b9a\u4e3a\u4e8c\u4f4d\u8001 \u524d\u8f88\u5954\u8dd1\u4e00\u8d9f\uff0c\u65e0\u8bba\u5982\u4f55\u4e5f\u8981\u6551\u2026\u2026\u201d \u3000\u3000\u5a49\u513f\u4e5f\u62a2\u7740\u8bf4\u9053\uff1a\u5bf9\u5440\uff01\u6211\u7239\u5b58\u4e86\u5f88\u591a\u7075\u836f\uff0c\u4ec0\u4e4c\u5566\uff0c\u6210\u5f62\u53c2\u738b\u5566\uff0c\u7075\u829d\u4ed9\u6db2\u5566\u2026\u2026\u597d \u591a\u597d\u591a\uff0c\u53ea\u8981\u6211\u56de\u5bb6\u53bb\u5411\u6211\u7239\u8981\uff0c\u4fbf\u53ef\u4ee5\u62ff\u6765\u6551\u6d3b\u4e24\u4f4d\u8001\u4eba\u5bb6\uff0c\u800c\u4e14\uff0c\u8fd8\u6709\u2018\u8bf8\u7a3d\u795e\u533b\u2019\u6881 \u8001\u4f2f\u4e5f\u4f4f\u5728\u6211\u5bb6\u4e2d\u2026\u2026\u201d \u3000\u3000\u795e\u7334\u94c1\u51cc\u63d2\u8a00\u9053\uff1a\u5973\u5a03\u5a03\uff0c\u4f60\u7236\u4eb2\u662f\u8c01\uff1f\u201d \u3000\u3000\u672a\u7b49\u5a49\u513f\u8bf4\u51fa\uff0c\u8463\u5343\u91cc\u5374\u4ee3\u5979\u7b54\u9053\uff1a\u201c\u6458\u661f\u624b\u2019\u6155\u5bb9\u6db5\uff01\u201d \u3000\u3000\u795e\u7334\u94c1\u51cc\u5947\u9053\uff1a\u201c\u53ef\u662f\u5f53\u5e74\u2018\u6c5f\u5357\u4e03\u4fa0\u2019\u7684\u8001\u4e09\uff1f\u201d \u3000\u3000\u8463\u5343\u91cc\u9053\uff1a\u201c\u4e0d\u662f\u4ed6\u8fd8\u6709\u8c01\uff1f\u201d \u3000\u3000\u5a49\u513f\u7741\u5927\u4e86\u773c\u775b\u8bf4\u9053\uff1a\u201c\u4f60\u8ba4\u8bc6\u6211\u7239\uff1f\u201d \u3000\u3000\u8463\u5343\u91cc\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u5f53\u4eca\u6b66\u6797\u56db\u5927\u8c6a\u95e8\uff0c\u4f60\u7236\u4eb2\u5df2\u662f\u6570\u4e00\u6570\u4e8c\u7684\u4eba\u7269\uff0c\u4e2d\u539f\u6b66\u6797\u53c8\u6709 \u54ea\u4e00\u4e2a\u4e0d\u77e5\u9053\u4f60\u7236\u7684\u5927\u540d\uff01\u201d \u3000\u3000\u795e\u7334\u94c1\u51cc\u5374\u611f\u6168\u7cfb\u4e4b\u9053\uff1a\u201c\u8001\u673d\u5341\u6570\u4e2d\u4e0d\u5c65\u6c5f\u6e56\uff0c\u60f3\u4e0d\u5230\u5f53\u5e74\u7684\u540e\u8d77\u4e4b\u79c0\uff0c\u4eca\u65e5\u5df2\u4fe8\u7136 \u79f0\u8c6a\u79f0\u9738\u4e86\uff0c\u4f46\u4e0d\u77e5\u53e6\u5916\u4e09\u5927\u8c6a\u95e8\u662f\u8c01\uff1f\u201d \u3000\u3000\u795e\u9a74\u8463\u5343\u91cc\u9053\uff1a\u201c\u4f60\u8001\u7334\u513f\u53ef\u771f\u662f\u5b64\u964b\u5be1\u95fb\uff0c\u5982\u4eca\u4e0d\u4f46\u662f\u5f53\u5e74\u7684\u540e\u8d77\u4e4b\u79c0\u4ff1\u5df2\u6210\u4e86\u6c14 \u5019\uff0c\u5c31\u662f\u540e\u8d77\u4e4b\u79c0\u7684\u513f\u5b50\uff0c\u4e5f\u90fd\u6210\u4e86\u4e86\u4e0d\u8d77\u7684\u4eba\u7269\uff0c\u76ee\u524d\u6c5f\u6e56\u4e0a\u6d41\u7740\u56db\u53e5\u6b4c\u8bcd\uff1a\u2018\u5b89\u4e50\u98ce \u6d41\uff0c\u51cc\u98ce\u65e0\u60c5\uff0c\u98d8\u96f6\u7aef\u65b9\uff0c\u7965\u9e9f\u70ed\u80a0\u3002\u2019\u8fd9\u56db\u53e5\u6b4c\u8bcd\u4ee3\u8868\u7740\u6b66\u6797\u56db\u5927\u516c\u5b50\uff0c\u8fd9\u56db\u5927\u516c\u5b50\u5374\u662f \u5f53\u5e74\u90a3\u4e9b\u540e\u8d77\u4e4b\u79c0\u7684\u7b2c\u4e8c\u4ee3\u4e86\uff0c\u53ef\u60dc\u4f60\u8001\u7334\u513f\u5c31\u8981\u9a7e\u8fd4\u897f\u5929\uff0c\u65e0\u7f18\u89c1\u4f1a\u9ad8\u4eba\u4e86\uff01\u2026\u2026\u201d \u3000\u3000\u795e\u7334\u94c1\u51cc\u77aa\u773c\u6025\u9053\uff1a\u201c\u8001\u53eb\u9a74\uff01\u4f60\u5148\u4e0d\u8981\u8ffd\u4eca\u629a\u6614\uff0c\u56db\u5927\u8c6a\u95e8\u8fd8\u672a\u4ea4\u4ee3\u6e05\u695a\uff0c\u600e\u4e48\u53c8\u51fa \u6765\u56db\u5927\u516c\u5b50\u4e86\uff0c\u4f60\u8bf4\u8bf4\u660e\u767d\u884c\u4e0d\u884c\uff1f\u522b\u8ba9\u6211\u8001\u4eba\u5bb6\u6b7b\u4e86\u4e5f\u843d\u4e2a\u7cca\u6d82\u9b3c2\u201d\u201c\u56db\u5927\u8c6a\u95e8\u56db\u5927\u516c \u5b50\u662f\u4e00\u6863\u5b50\u4e8b\u3002\u201d\u8463\u5343\u91cc\u7b11\u9053\uff1a\u201c\u51cc\u98ce\u516c\u5b50\u5c31\u662f\u773c\u524d\u8fd9\u4f4d\u59d1\u5a18\u7684\u54e5\u54e5\uff0c\u4e5f\u5c31\u662f\u6458\u661f\u624b\u6155\u5bb9\u6db5 \u7684\u513f\u5b50\u3002\u5b89\u4e50\u516c\u5b50\u662f6\u4e7e\u5764\u638c\u2019\u4e91\u5b97\u9f99\u7684\u513f\u5b50\u3002\u7aef\u65b9\u516c\u5b50\u662f\u2018\u6df7\u5143\u6307\u2019\u53f8\u7a7a\u664b\u7684\u513f\u5b50\u3002\u7965\u9e9f \u516c\u5b50\u662f\u2018\u9752\u86a8\u795e\u2019\u91d1\u4e5d\u7684\u513f\u5b50\u3002\u8fd9\u56db\u5927\u516c\u5b50\u7684\u7236\u4eb2\uff0c\u4fbf\u662f\u56db\u5927\u8c6a\u95e8\uff0c\u6b64\u5916\uff0c\u5c1a\u6709\u5b9a\u5c45\u5728\u9547\u6c5f \u7684\u2018\u9738\u738b\u97ad\u2019\u6a0a\u975e\uff0c\u867d\u7136\u4e0d\u53ca\u56db\u5927\u8c6a\u95e8\u52bf\u5927\uff0c\u4f46\u4e5f\u662f\u4e00\u65b9\u7684\u9738\u4e3b\u4e86\uff0c\u8fd9\u4e00\u8bf4\uff0c\u4f60\u8001\u7334\u513f\u8be5\u77e5 \u9053\u5f53\u4eca\u6b66\u6797\u662f\u8c01\u5bb6\u5929\u4e0b\u4e86\u5427\uff1f\u201d \u3000\u3000\u795e\u7334\u94c1\u51cc\u70b9\u5934\u9053\uff1a\u201c\u4f60\u63d0\u8d77\u7684\u8fd9\u4e94\u4eba\uff0c\u90fd\u662f\u5f53\u5e74\u201c\u6c5f\u5357\u4e03\u4fa0\u2019\u4e4b\u4e2d\u7684\u4eba\u7269\uff0c\u4f46\u662f\u90a3\u6c5f\u5357 \u4e03\u4fa0\u4e2d\u7684\u9996\u9886\uff0c\u2018\u9739\u96f7\u7fc5\u2019\u5c55\u4e91\u5929\uff0c\u5374\u6ca1\u89c1\u4f60\u63d0\u8d77\u5462\uff1f\u83ab\u975e\u8bf4\u5c55\u4e91\u5929\u7684\u526f\u624b\u90fd\u6210\u4e86\u6c14\u5019\uff0c\u4ed6 \u672c\u4eba\u53cd\u800c\u9041\u4e16\u6f5c\u9690\u4e86\u4e0d\u6210\uff1f\u201d \u3000\u3000\u5c55\u767d\u542c\u4e8c\u4f4d\u8001\u4eba\u8c08\u8d77\u7236\u4eb2\u6614\u5e74\u65e7\u4e8b\uff0c\u53cc\u76ee\u5706\u7741\uff0c\u7736\u6bd7\u6b32\u88c2\uff0c\u8138\u4e0a\u7684\u6fc0\u52a8\u4e4b\u60c5\u5df2\u6d41\u9732\u65e0 \u9057\u3002\u8463\u5343\u91cc\u5374\u542b\u6709\u6df1\u610f\u5730\u671b\u4e86\u4ed6\u4e00\u773c\u9053\uff1a\u201c\u5c55\u4e91\u5929\u5df2\u6b7b\u5728\u6570\u5341\u4e2a\u6b66\u6797\u9ad8\u624b\u56f4\u653b\u4e4b\u4e0b\uff01\u2026\u2026\u201d \u3000\u3000\u5c55\u767d\u542c\u81f3\u6b64\u5904\uff0c\u60b2\u6124\u83ab\u540d\uff0c\u53cc\u773c\u6cea\u843d\u5982\u96e8\uff0c\u6251\u5012\u5728\u795e\u9a74\u94c1\u80c6\u8463\u5343\u91cc\u9762\u524d\uff0c\u54ed\u9053\uff1a\u201c\u5c1a\u8bf7 \u8001\u524d\u8f88\u660e\u793a\u665a\u8f88\uff0c\u6740\u6b7b\u540d\uff0c\u4e5f\u597d\u8ba9\u665a\u8f88\u4e3a\u5148\u7236\u62a5\u4ec7\uff01\u2026\u2026\u201d \u3000\u3000\u795e\u7334\u94c1\u51cc\u4e5f\u81ea\u706b\u773c\u5706\u7741\uff0c\u6012\u53eb\u9053\uff1a\u201c\u5c55\u4e91\u5929\u5f53\u771f\u6b7b\u4e86\u5417\uff1f\u201d \u3000\u3000\u795e\u9a74\u94c1\u80c6\u8463\u5343\u91cc\u9053\uff1a\u6211\u8fd8\u9a97\u4f60\u4e0d\u6210\uff0c\u773c\u524d\u5c31\u6709\u5c55\u4e91\u5929\u7684\u540e\u4eba\u4e3a\u8bc1\uff01\u201d \u3000\u3000\u795e\u7334\u94c1\u51cc\u6012\u89c6\u7740\u5c55\u767d\uff0c\u54ac\u7259\u5207\u9f7f\u5730\u628a\u53f3\u638c\u4e3e\u8d77\uff0c\u4f5c\u52bf\u6b32\u51fb\uff0c\u4f46\u7b49\u4ed6\u8fd0\u96c6\u529f\u529b\u65f6\uff0c\u611f\u5230\u5185 \u8151\u771f\u6c14\u6563\u800c\u4e0d\u805a\uff0c\u624d\u60f3\u8d77\u81ea\u5df1\u8eab\u8d1f\u91cd\u4f24\u7684\u4e8b\u6765\uff0c\u4e0d\u7531\u9893\u7136\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u53c8\u628a\u4e3e\u8d77\u7684\u53f3\u638c\u65e0\u529b \u5730\u5782\u4e0b\uff0c\u9053\uff1a\u201c\u5509\uff01\u60f3\u4e0d\u5230\u8001\u592b\u8ddf\u59d3\u5c55\u7684\u4e00\u6bb5\u65e7\u8d26\uff0c\u4e5f\u65e0\u529b\u6e05\u7b97\u4e86\u2026\u2026\u201d \u3000\u3000\u8bf4\u7f62\u5140\u81ea\u60b2\u53f9\uff0c\u6068\u6068\u4e0d\u5df2\u3002 \u3000\u3000\u5c55\u767d\u5e76\u6ca1\u6709\u770b\u5230\u795e\u7334\u94c1\u51cc\u7684\u795e\u60c5\u53d8\u5316\uff0c\u53ea\u4e00\u5473\u5730\u8dea\u62dc\u5728\u5730\uff0c\u592e\u6c42\u795e\u9a74\u8463\u5343\u91cc\u544a\u8bc9\u4ed6\u6740\u7236 \u4ec7\u4eba\u662f\u8c01\u2026\u2026 \u3000\u3000\u5a49\u513f\u8fd9\u624d\u77e5\u9053\u5c55\u767d\u771f\u662f\u5f53\u5e74\u201c\u9739\u96f7\u5251\u201d\u5c55\u4e91\u5929\u5c55\u5927\u4fa0\u7684\u540e\u4eba\uff0c\u5fc3\u4e2d\u8bf4\u4e0d\u51fa\u662f\u60ca\u662f\u559c\uff0c\u60ca \u7684\u662f\u5c55\u54e5\u54e5\u843d\u9b44\u7a77\u9014\uff0c\u770b\u4e0d\u51fa\u4ed6\u662f\u540d\u95e8\u4e4b\u540e\uff0c\u559c\u7684\u662f\u81ea\u5df1\u7684\u7236\u4eb2\u4e0e\u5c55\u54e5\u54e5\u7684\u7236\u4eb2\u540c\u79f0\u201c\u6c5f\u5357 \u4e03\u4fa0\u201d\uff0c\u5fc5\u4e5f\u662f\u901a\u5bb6\u4e4b\u597d\uff0c\u65e0\u5f62\u4e2d\u4e0e\u5c55\u54e5\u54e5\u7684\u5173\u7cfb\u8fd1\u4e86\u4e00\u5c42\uff0c\u4f46\u5979\u53c8\u89c1\u5c55\u767d\u60b2\u54ed\u96f6\u6d95\uff0c\u5374\u6b62 \u4e0d\u4f4f\u614c\u4e86\u624b\u811a\uff0c\u4e00\u8fb9\u53bb\u6400\u6276\u5c55\u767d\uff0c\u4e00\u8fb9\u4e0d\u4f4f\u5730\u5b89\u6170\u5979\u7684\u5c55\u54e5\u54e5\u2026\u2026 \u3000\u3000\u795e\u9a74\u94c1\u80c6\u770b\u5230\u795e\u7334\u94c1\u51cc\u6028\u6bd2\u7684\u795e\u8272\uff0c\u4e0d\u7531\u4e00\u6014\uff0c\u5148\u53eb\u5c55\u767d\u8d77\u6765\u7ad9\u5728\u4e00\u8fb9\uff0c\u53c8\u5bf9\u795e\u7334\u8bf4 \u9053\uff1a\u83ab\u975e\u4f60\u8001\u7334\u513f\u4e0e\u5c55\u4e91\u5929\uff0c\u8fd8\u6709\u4ec0\u4e48\u8fc7\u8282\uff1f\u201d \u3000\u3000\u795e\u7334\u94c1\u51cc\u957f\u53f9\u4e00\u58f0\uff0c\u6b32\u8a00\u53c8\u6b62\uff0c\u7ade\u8bb7\u8bb7\u5730\u8bf4\u4e0d\u51fa\u53e3\u6765\u3002 \u3000\u3000\u8463\u5343\u91cc\u53c8\u95ee\u9053\uff1a\u5c55\u4e91\u5929\u5df2\u6b7b\uff0c\u4f60\u6211\u4e5f\u6d3b\u4e0d\u4e86\u591a\u4e45\u5566\uff0c\u8fd8\u6709\u4ec0\u4e48\u8bdd\u4e0d\u597d\u8bf4\u7684\uff1f\u201d \u3000\u3000\u795e\u7334\u53c8\u4e00\u58f0\u957f\u53f9\u9053\uff1a\u8bf4\u8d77\u6765\u8001\u54e5\u54e5\u4f60\u4e5f\u8bb8\u4e0d\u4f1a\u76f8\u4fe1\uff0c\u6211\u5e73\u751f\u81ea\u8ba4\u4e3a\u5929\u4e0b\u65e0\u6545\uff0c\u5374\u8f93\u7ed9\u5c55 \u4e91\u5929\u65e0\u60c5\u78a7\u5251\u4e4b\u4e0b\u4e00\u62db\uff01\u201d \u3000\u3000\u795e\u9a74\u8463\u5343\u91cc\u9053\uff1a\u201c\u8fd9\u8bdd\u6211\u5012\u76f8\u4fe1\uff0c\u6211\u4e5f\u4e0d\u4e00\u5b9a\u6253\u5f97\u8fc7\u4ed6\u2026\u2026\u201d \u3000\u3000\u8fd9\u4e00\u53e5\u8bdd\u53c8\u6fc0\u6012\u4e86\u795e\u7334\u94c1\u51cc\uff0c\u53ea\u89c1\u4ed6\u6012\u545b\u9053\uff1a\u201c\u9a91\u9a74\u8001\u9b3c\uff01\u4f60\u7684\u610f\u601d\u8bf4\u4f60\u6b66\u529f\u6bd4\u6211\u8001\u4eba \u5bb6\u9ad8\u4e86\uff1f\u201d \u3000\u3000\u8463\u5343\u91cc\u60f3\u4e0d\u5230\u4ed6\u5982\u6b64\u597d\u80dc\uff0c\u82e6\u7b11\u4e00\u58f0\u9053\uff1a\u201c\u6211\u8981\u6bd4\u4f60\u9ad8\uff0c\u4e5f\u4e0d\u4f1a\u8ddf\u4f60\u843d\u4e2a\u4e24\u8d25\u4ff1\u4f24\u4e86\uff01 \u90fd\u662f\u5feb\u6b7b\u7684\u4eba\u4e86\uff0c\u8fd8\u90a3\u4e48\u5927\u7684\u706b\u6c14\uff0c\u5feb\u63a5\u7740\u8bf4\u4e0b\u53bb\u5427\uff01\u201d \u3000\u3000\u795e\u7334\u706b\u6c14\u7565\u5e73\uff0c\u63a5\u4e0b\u53bb\u8bf4\u9053\uff1a\u201c\u5341\u6570\u5e74\u524d\uff0c\u5c55\u4e91\u5929\u627e\u5230\u9ece\u8d21\u5c71\u8001\u592b\u6f5c\u4fee\u4e4b\u6240\uff0c\u58f0\u8a00\u8981\u501f \u8001\u592b\u9547\u5c71\u4e4b\u5b9d\u2018\u907f\u6c34\u795e\u73e0\u2019\u4e00\u7528\uff0c\u867d\u7136\u4ed6\u8a00\u8f9e\u5ba2\u6c14\uff0c\u4f46\u2018\u907f\u6c34\u795e\u73e0\u2019\u4e43\u6211\u9ece\u8d21\u5c71\u9547\u5c71\u4e4b\u5b9d\uff0c \u600e\u80af\u8f7b\u6613\u501f\u7ed9\u5916\u4eba\uff1f\u800c\u4e14\uff0c\u4ed6\u53c8\u4e0d\u8bf4\u660e\u7406\u7531\uff0c\u53ea\u8bf4\u7528\u540e\u4e00\u5b9a\u5f52\u8fd8\uff0c\u5e76\u63d0\u51fa\u2018\u6c5f\u5357\u4e03\u4fa0\u2019\u7684\u540d \u8a89\u4fdd\u8bc1\u3002\u201d \u3000\u3000\u201c\u5f53\u65f6\uff0c\u8001\u592b\u542c\u4ed6\u81ea\u62a5\u5b57\u53f7\uff0c\u624d\u77e5\u4ed6\u4fbf\u662f\u6b66\u6797\u4e2d\u65b0\u5d1b\u8d77\u7684\u540e\u8d77\u4e4b\u79c0\uff0c\u5fc3\u4e2d\u4fbf\u66f4\u4e0d\u80af\u501f\u7ed9 \u4ed6\u4e86\uff0c\u56e0\u4e3a\u6211\u82e5\u501f\u7ed9\u4ed6\u907f\u6c34\u795e\u73e0\uff0c\u6c5f\u6e56\u4e0a\u4f20\u8a00\u51fa\u53bb\uff0c\u4e00\u5b9a\u4ee5\u4e3a\u8001\u592b\u88ab\u4ed6\u2018\u6c5f\u5357\u4e03\u4fa0\u2019\u7684\u540d\u5934 \u9707\u5413\u4f4f\u4e86\uff0c\u4e8e\u662f\u63d0\u51fa\u6bd4\u6b66\u8f83\u91cf\u7684\u6761\u4ef6\uff0c\u4ed6\u5982\u80fd\u80dc\u8fc7\u8001\u592b\uff0c\u907f\u6c34\u795e\u73e0\u4fbf\u7531\u4ed6\u62ff\u53bb\uff0c\u8fd8\u4e0d\u8fd8\u90fd\u6089 \u542c\u5c0a\u4fbf\u3002\u4ed6\u5982\u8d25\u5728\u8001\u592b\u624b\u4e0b\uff0c\u90a3\u4ed6\u4e5f\u5c31\u4e0d\u7528\u60f3\u6d3b\u7740\u79bb\u5f00\u9ece\u8d21\u5c71\u4e86\uff01\u201d \u3000\u3000\u795e\u9a74\u94c1\u80c6\u8463\u5343\u91cc\u63d2\u5634\u9053\uff1a\u201c\u7ed3\u679c\u4f60\u8001\u7334\u513f\u8d25\u7ed9\u5c55\u4e91\u5929\u4e86\uff01\u201d \u3000\u3000\u201c\u4f60\u8001\u9b3c\u542c\u6211\u8bf4\u5427\uff01\u201d\u795e\u7334\u7a0d\u5e26\u5ffd\u610f\u5730\u9053\uff1a\u201c\u5f53\u6211\u4fe9\u5728\u9ece\u8d21\u5c71\u4e5d\u66f2\u6d1e\u524d\uff0c\u82e6\u6218\u4e09\u5929\u4e09 \u591c\uff0c\u4e5f\u5c31\u50cf\u73b0\u5728\uff0c\u6211\u548c\u4f60\u4e00\u6837\uff0c\u4e0d\u8fc7\u4ed6\u5374\u6ca1\u8d1f\u4f24\uff0c\u6fc0\u6218\u81f3\u6700\u540e\uff0c\u4ed6\u7528\u65e0\u60c5\u78a7\u5251\u5728\u6211\u524d\u80f8\u5212\u4e86 \u4e00\u9053\u53e3\u5b50\uff0c\u5374\u53ea\u5212\u7834\u5916\u8863\u800c\u672a\u4f24\u5230\u808c\u8089\uff0c\u6211\u77e5\u9053\u662f\u4ed6\u624b\u4e0b\u7559\u60c5\uff0c\u4f46\u5f53\u65f6\u8fd9\u5374\u6bd4\u6740\u4e86\u6211\u8fd8\u96be \u53d7\uff0c\u5f53\u65f6\u6211\u66b4\u53eb\u9053\uff1a\u2018\u5c55\u4e91\u5929\uff01\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u6740\u6211\uff1f\u4f60\u6740\u4e86\u6211\u5427\uff01 \u3000\u3000\u8463\u5343\u91cc\u53c8\u63d2\u5634\u9053\uff1a\u201c\u7ed3\u679c\uff0c\u5c55\u4e91\u5929\u6ca1\u6740\u4f60\uff01\u201d \u3000\u3000\u201c\u5e9f\u8bdd\uff01\u201d\u2018\u795e\u7334\u201d\u94c1\u51cc\u5ffd\u9053\uff1a\u201c\u5f53\u65f6\u4ed6\u8981\u6740\u4e86\u6211\uff0c\u4eca\u5929\u4e5f\u4e0d\u4f1a\u5728\u6b64\u8ddf\u4f60\u8001\u4e0d\u6b7b\u843d\u4e2a\u4e24 \u8d25\u4ff1\u4f24\u4e86\uff01\u6b63\u56e0\u4e3a\u4ed6\u6ca1\u6740\u6211\uff0c\u624d\u4f7f\u6211\u53d7\u4e86\u66f4\u5927\u7684\u75db\u82e6\uff0c\u5f53\u65f6\u6211\u4f9d\u7ea6\u5c06\u907f\u6c34\u795e\u73e0\u4ea4\u7ed9\u4ed6\uff0c\u5e76\u7ea6 \u4ed6\u4e09\u5e74\u540e\u539f\u5730\u518d\u884c\u6bd4\u6b66\u51b3\u6597\uff0c\u4ed6\u8d70\u540e\u6211\u5373\u95ed\u5173\u4fee\u70bc\u51e0\u95e8\u7edd\u4e16\u795e\u529f\uff0c\u6ca1\u60f3\u5230\u6b63\u5728\u6211\u70bc\u529f\u7d27\u8981\u5173 \u5934\uff0c\u6211\u7684\u4e24\u4e2a\u5b7d\u5f92\u89ca\u89ce\u6211\u7684\u2018\u795e\u529f\u79d8\u7b08\u2019\uff0c\u6270\u5f97\u6211\u8d70\u706b\u5165\u9b54\uff0c\u81ea\u65ad\u53cc\u817f\uff0c\u6211\u7684\u4e24\u4e2a\u5b7d\u5f92\u5374\u5e26 \u7740\u795e\u529f\u79d8\u6388\u9003\u51fa\u9ece\u8d21\u5c71\uff0c\u4e34\u8d70\u4e4b\u524d\uff0c\u4e95\u4ee5\u5de8\u77f3\u5c01\u585e\u6d1e\u53e3\uff0c\u4e0b\u6bd2\u624b\u60f3\u628a\u6211\u8fd9\u6388\u4e1a\u6069\u5e08\u6d3b\u846c\u5728\u8352 \u6d1e\u4e4b\u4e2d\uff01\u201d \u3000\u3000\u8463\u5343\u91cc\u542c\u795e\u7334\u94c1\u51cc\u8bf4\u5b8c\u7ecf\u8fc7\uff0c\u4e0d\u7981\u65e0\u9650\u611f\u6168\u9053\uff1a\u201c\u4e4b\u540e\u9739\u96f3\u5251\u5c55\u4e91\u5929\u4e5f\u6ca1\u6709\u518d\u53bb\u8fd8\u4f60\u7684 \u907f\u6c34\u795e\u73e0\uff0c\u5bf9\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u795e\u7334\u94c1\u51cc\u6620\u7259\u5207\u9f7f\u9053\uff1a\u201c\u5f53\u7136\u5c55\u4e91\u5929\u6ca1\u518d\u53bb\uff0c\u8001\u592b\u795e\u529f\u529f\u8d25\u5782\u6210\uff0c\u867d\u7136\u81ea\u65ad\u53cc\u817f\uff0c\u5168\u8eab \u6b66\u529f\u5e76\u672b\u5931\u53bb\uff0c\u6211\u88ab\u57cb\u5728\u8352\u6d1e\u4e2d\uff0c\u8fd0\u529f\u7597\u4f24\uff0c\u4f24\u6108\u540e\u7834\u6d1e\u800c\u51fa\uff0c\u91cd\u4e0b\u6c5f\u6e56\uff0c\u7b2c\u4e00\u5c31\u662f\u8981\u627e\u4e24 \u4e2a\u5b7d\u5f92\uff0c\u4ee5\u6e05\u7406\u95e8\u6237\uff0c\u7b2c\u4e09\u4fbf\u662f\u8981\u627e\u5c55\u4e91\u5929\uff0c\u4ee5\u62a5\u5f53\u4e2d\u4e00\u5251\u4e4b\u8fb1\u53ca\u593a\u5b9d\u4e4b\u6068\uff01\u6ca1\u60f3\u5230\u5374\u78b0\u5230 \u4f60\u8fd9\u8001\u4e0d\u6b7b\uff0c\u4f7f\u8001\u592b\u4e24\u6837\u5fc3\u613f\u90fd\u6210\u4e86\u6ce1\u5f71\uff01\u53ef\u89c1\u2026\u2026\u201d \u3000\u3000\u8463\u5343\u91cc\u622a\u4f4f\u4ed6\u4e0d\u8ba9\u4ed6\u518d\u8bf4\u4e0b\u53bb\uff0c\u9053\uff1a\u8001\u673d\u62e6\u4f4f\u4f60\uff0c\u6ca1\u6709\u8ba9\u4f60\u8001\u7334\u513f\u6740\u6b7b\u4e24\u4e2a\u5b7d\u5f92\uff0c\u7684\u786e \u62b1\u6b49\uff0c\u4f46\u4e8b\u5df2\u81f3\u6b64\uff0c\u62b1\u6b49\u4e5f\u65e0\u6d4e\u4e8e\u4e8b\u3002 \u3000\u3000\u5012\u662f\u5c55\u4e91\u5929\u672a\u80fd\u4f9d\u7ea6\u5f52\u8fd8\u4f60\u7684\u907f\u6c34\u795e\u73e0\uff0c\u5b9e\u5728\u4e0d\u662f\u4ed6\u5931\u4fe1\uff0c\u56e0\u4e3a\u5c55\u4e91\u5929\u5411\u4f60\u501f\u907f\u6c34\u795e \u73e0\uff0c\u662f\u51c6\u5907\u5728\u6d1e\u5ead\u6e56\u5e95\u635e\u53d6\u4e00\u7b14\u6c89\u5b9d\uff0c\u6c89\u5b9d\u51fa\u6c34\uff0c\u5c55\u4e91\u5929\u5374\u88ab\u6b66\u6797\u5341\u6570\u9ad8\u624b\u56e0\u6740\u800c\u6b7b\uff0c\u5f53\u7136 \u4ed6\u4e5f\u6ca1\u6709\u529e\u6cd5\u53bb\u8fd8\u4f60\u7684\u795e\u6b8a\u4e86\u3002\u5e38\u8a00\u9053\uff1a\u2019\u2018\u6b7b\u4e86\uff0c\u6b7b\u4e86\uff01\u2019\u4eba\u6b7b\u4e0d\u8bb0\u4ec7\uff0c\u6069\u6028\u2018\u7b14\u6d88\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u6211\u4eec\u4e24\u4e2a\u4eba\u7684\u4e8b\uff0c\u4f60\u8001\u7334\u513f\u82e5\u662f\u4e0d\u670d\u6c14\u7684\u8bdd\uff0c\u6211\u4eec\u8fd8\u53ef\u4ee5\u7ee7\u7eed\u6bd4\u6597\u4e0b\u53bb\uff01\u201d \u3000\u3000\u795e\u733f\u94c1\u51cc\u4e00\u695e\u9053\uff1a\u201c\u4f60\u6211\u5185\u529f\u771f\u529b\u5df2\u6e83\uff0c\u5982\u4eca\u5df2\u4e0e\u5e38\u4eba\u65e0\u5f02\uff0c\u518d\u8005\uff0c\u5185\u8151\u53d7\u521b\u4e5f\u6d3b\u4e0d\u4e86 \u51e0\u5929\u5566\uff0c\u8fd8\u6709\u4ec0\u4e48\u597d\u6bd4\uff1f\u201d \u3000\u3000\u5a49\u513f\u5374\u5728\u4e00\u8fb9\u63d2\u5634\u9053\uff1a\u201c\u53ea\u8981\u6211\u56de\u5bb6\u6551\u6d3b\u4e8c\u4f4d\u8001\u4eba\u5bb6\uff01\u201d \u3000\u3000\u5a49\u513f\u4e00\u65e0\u5fc3\u673a\uff0c\u867d\u7136\u4e8c\u8001\u8bf4\u4e86\u534a\u5929\u771f\u8c61\uff0c\u4e0d\u89c1\u5f97\u5bf9\u5979\u6216\u5bf9\u5c55\u767d\u6709\u5229\uff0c\u4f46\u5979\u53ea\u89c9\u5f97\u89c1\u6b7b\u4e0d \u80fd\u4e0d\u6551\uff0c\u5b8c\u5168\u6ca1\u6709\u60f3\u5230\u5176\u4ed6\u3002\u8bf4\u5b8c\u4e4b\u540e\u8fd8\u771f\u8981\u6280\u7740\u5c55\u767d\u8ddf\u5979\u56de\u5bb6\u627e\u7236\u4eb2\u53bb\u62ff\u836f\u2026\u2026 \u3000\u3000\u795e\u7334\u94c1\u51cc\u5374\u662f\u4e2a\u6709\u6069\u5fc5\u62a5\u3001\u6709\u4ec7\u5fc5\u6d88\u7684\u6c5f\u6e56\u602a\u6770\uff0c\u89c1\u5a49\u513f\u5bb9\u989c\u4fcf\u4e3d\uff0c\u5fc3\u6027\u5584\u826f\uff0c\u4e00\u5473\u5730 \u60f3\u6551\u4ed6\uff0c\u4e0d\u7531\u6fc0\u52a8\u5730\u8bf4\u9053\uff1a\u201c\u4f60\u8fd9\u5973\u5a03\u513f\uff0c\u8fd9\u4efd\u5fc3\u60c5\u5b9e\u5728\u53ef\u611f\uff01\u770b\u6765\u6211\u8001\u4eba\u5bb6\u4ee5\u4e3a\u4e16\u4e0a\u65e0\u597d \u4eba\u7684\u89c2\u5ff5\uff0c\u53c8\u8981\u6539\u53d8\u4e00\u4e0b\u4e86\u3002\u2026\u2026\u201d \u3000\u3000\u8463\u5343\u91cc\u5374\u5411\u5a49\u513f\u7b11\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u4e0d\u7528\u8d39\u5fc3\u4e86\uff01\u4f60\u7239\u867d\u6709\u73cd\u8d35\u7075\u836f\uff0c\u4f46\u4e5f\u6bd4\u4e0d\u4e0a\u795e\u733f\u513f \u521a\u624d\u670d\u4e0b\u7684\u2018\u7eed\u547d\u6563\u2019\u548c\u8001\u673d\u773c\u4e0b\u7684\u2018\u5927\u8fd8\u4e39\u2019\u66f4\u5f3a\uff0c\u60f3\u5f53\u5e74\u6c5f\u6e56\u9053\u4e0a\u6709\u2018\u5357\u7334\u5317\u9a74\u2019\u4e4b \u79f0\uff0c\u4e5f\u6709\u2018\u5357\u6563\u5317\u4e39\u2019\u4e4b\u8a89\uff0c\u2018\u5357\u6563\u5317\u4e39\u2019\u90fd\u4e0d\u80fd\u4fdd\u4f4f\u6211\u4e8c\u4eba\u7684\u6027\u547d\uff0c\u90a3\u5c31\u5927\u7f57\u795e\u4ed9\u4e5f\u6ca1\u6709 \u529e\u6cd5\u4e86\uff01\u201d \u3000\u3000\u8463\u5343\u91cc\u8bf4\u5b8c\uff0c\u795e\u7334\u9eef\u7136\u70b9\u5934\uff0c\u5a49\u513f\u89c1\u4e09\u4eba\u4e0d\u4f3c\u54c4\u5979\uff0c\u6ee1\u8138\u51c4\u60f6\u9053\uff1a\u201c\u8fd9\u6837\u8bf4\u6765\uff0c\u4e8c\u4f4d\u8001 \u4eba\u5bb6\u6ca1\u6709\u6551\u4e86\uff1f\u2026\u2026\u201d \u3000\u3000\u201c\u59d1\u5a18\uff0c\u4e0d\u7528\u60b2\u4f24\uff01\u201d\u8463\u5343\u91cc\u9053\uff1a\u201c\u751f\u6b7b\u6709\u547d\uff0c\u5bcc\u8d35\u5728\u5929\uff0c\u6211\u4e8c\u4eba\u6d3b\u4e86\u5c06\u8fd1\u767e\u5c81\u4e86\uff0c\u6b7b \u800c\u4e0d\u7b97\u5929\uff0c\u8fde\u6211\u4eec\u81ea\u5df1\u90fd\u4e0d\u4f24\u5fc3\uff0c\u4f60\u66ff\u6211\u4eec\u4f24\u4ec0\u4e48\u5fc3\uff1f\u201d \u3000\u3000\u795e\u7334\u5f20\u5634\u6b32\u8a00\uff0c\u8463\u5343\u91cc\u5374\u63a5\u7740\u8bf4\u4e0b\u53bb\u9053\uff1a\u201c\u6211\u60f3\u5230\u4e86\u4e00\u4e2a\u597d\u4e3b\u610f\uff0c\u6211\u4e24\u4e2a\u8001\u4e0d\u6b7b\u7684\u6b7b\u540e \u65e2\u6709\u4eba\u6536\u62fe\u6211\u4fe9\u8fd9\u51e0\u6839\u8001\u9aa8\u5934\uff0c\u53c8\u53ef\u7ee7\u7eed\u6bd4\u8f83\u6211\u4fe9\u672a\u5206\u51fa\u7684\u80dc\u8d1f\u6765\uff01\u201d \u3000\u3000\u795e\u7334\u773c\u73e0\u4e00\u8f6c\u9053\uff1a\u201c\u8001\u9b3c\u4f60\u4e0d\u7528\u8bf4\uff0c\u6211\u4e5f\u77e5\u9053\u4e86\uff0c\u4f60\u60f3\u51ed\u6211\u4e24\u4e2a\u8001\u4e0d\u6b7b\u4f20\u7ed9\u4e24\u4e2a\u5a03\u513f\u51e0 \u62db\u6b66\u529f\uff0c\u53eb\u4e24\u4e2a\u5a03\u513f\u4e3a\u6211\u4fe9\u6536\u5c38\u4e0d\u96be\uff0c\u4f46\u8981\u60f3\u53eb\u4ed6\u4eec\u4e8c\u4eba\u6bd4\u6b66\u51b3\u6597\uff0c\u90a3\u5374\u662f\u65e0\u6cd5\u529e\u5230\u7684\u4e00\u4ef6 \u4e8b\u3002\u201d \u3000\u3000\u201c\u8001\u7334\u5b50\uff0c\u6709\u4f60\u7684\uff01\u201d\u8463\u5343\u91cc\u5174\u9ad8\u5f69\u70c8\u5730\u53eb\u9053\uff1a\u201c\u4eba\u4eba\u90fd\u8bf4\u7334\u806a\u660e\u7334\u806a\u660e\uff0c\u4eca\u65e5\u2014\u89c1\u679c \u7136\u4e0d\u5047\uff0c\u4f46\u4f60\u8001\u7334\u5b50\u806a\u660e\u53cd\u88ab\u806a\u660e\u8bef\uff0c\u53ea\u60f3\u5230\u524d\u4e00\u534a\u6ca1\u60f3\u5230\u540e\u4e00\u534a\uff01\u201d \u3000\u3000\u795e\u7334\u94c1\u51cc\u51b7\u7136\u9053\uff1a\u201c\u613f\u95fb\u5176\u8be6\uff01\u201d \u3000\u3000\u795e\u9a74\u8463\u5343\u91cc\u9053\uff1a\u201c\u770b\u4e24\u4e2a\u5a03\u513f\u7684\u4eb2\u70ed\u52b2\uff0c\u60f3\u53eb\u4ed6\u4fe9\u62fc\u4e2a\u4f60\u6b7b\u6211\u6d3b\uff0c\u5f53\u7136\u662f\u529e\u4e0d\u5230\uff0c\u4f46\u8981 \u6211\u4eec\u5206\u522b\u4f20\u7ed9\u4ed6\u4eec\u6b66\u529f\uff0c\u53eb\u4ed6\u4e24\u4e2a\u6bd4\u4e00\u6bd4\u9ad8\u4e0b\uff0c\u5374\u662f\u505a\u5f97\u5230\u7684\uff0c\u8c01\u7684\u4f20\u4eba\u9ad8\u5f3a\uff0c\u90a3\u5c31\u662f\u8c01\u8d62 \u4e86\u3002\u8001\u7334\u4e8e\uff0c\u4f60\u770b\u8fd9\u529e\u6cd5\u600e\u4e48\u6837\u4e2d\u795e\u7334\u94c1\u51cc\u773c\u653e\u5947\u5149\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u4f60\u662f\u6311\u7537\u7684\u4e86\uff1f\u201d \u3000\u3000\u8463\u5343\u91cc\u9053\uff1a\u201c\u90a3\u662f\u5f53\u7136\uff01\u5c55\u4e91\u5929\u4e0e\u4f60\u6709\u4ec7\uff0c\u5f53\u7136\u4f60\u4e0d\u4f1a\u518d\u4f20\u7ed9\u5c55\u4e91\u5929\u4e4b\u5b50\u6b66\u529f\uff01\u201d \u3000\u3000\u795e\u7334\u94c1\u51cc\u9f13\u8d77\u4e24\u53ea\u7334\u773c\uff0c\u72e0\u72e0\u6ce8\u89c6\u4e86\u5c55\u767d\u4e0e\u5a49\u513f\u4e00\u4f1a\u9053\uff1a\u201c\u90a3\u4f60\u9a91\u9a74\u8001\u513f\u53c8\u5360\u4fbf\u5b9c\u4e86\uff0c \u663e\u7136\u7537\u7684\u8981\u6bd4\u5973\u7684\u5185\u529f\u6df1\u539a\uff01\u201d \u3000\u3000\u8463\u5343\u91cc\u9053\uff1a\u201c\u6bd4\u7537\u7684\u8981\u597d\uff0c\u5404\u6709\u957f\u77ed\uff0c\u8c01\u4e5f\u4e0d\u5360\u8c01\u7684\u4fbf\u5b9c\uff01\u201d \u3000\u3000\u795e\u7334\u60f3\u4e86\u4e00\u4e0b\u9053\uff1a\u201c\u597d\uff01\u5c31\u8fd9\u6837\u51b3\u5b9a\u4e86\u3002\u4f46\u4f60\u9a91\u9a74\u8001\u9b3c\u5c1a\u80fd\u6d3b\u591a\u4e45\uff1f\u6211\u4eec\u4e5f\u5f97\u5b9a\u4e2a\u671f\u9650 \u5440\uff01\u201d \u3000\u3000\u8463\u5343\u91cc\u9053\uff1a\u201c\u5927\u7ea6\u4e0d\u4f1a\u8d85\u8fc7\u767e\u5929\uff01\u201d \u3000\u3000\u795e\u7334\u94c1\u51cc\u9ed8\u7b97\u4e86\u4e00\u4f1a\uff0c\u8c6a\u6c14\u4e8e\u4e91\u5730\u9053\uff1a\u201c\u6211\u4e5f\u5dee\u4e0d\u591a\uff0c\u5c31\u8fd9\u6837\u5427\uff0c\u6211\u4eec\u4e09\u4e2a\u6708\u4e3a\u671f\uff0c\u4e5f \u597d\u7559\u4e0b\u4e00\u70b9\u5bff\u547d\uff0c\u4eb2\u773c\u770b\u770b\u6211\u4eec\u4e24\u4e2a\u8001\u4e0d\u6b7b\u5230\u5e95\u662f\u8c01\u884c\u8c01\u4e0d\u884c\uff1f\u201d \u3000\u3000\u8463\u5343\u91cc\u9053\uff1a\u201c\u4e00\u8a00\u4e3a\u5b9a\uff0c\u6765\uff01\u6211\u4eec\u4e09\u51fb\u638c\u5427\uff01\u201d \u3000\u3000\u8bf4\u7740\uff0c\u6323\u624e\u7740\u7ad9\u8d77\u6765\uff0c\u6447\u6447\u6446\u6446\u5730\u8d70\u8fd1\u795e\u7334\u94c1\u51cc\u9762\u524d\uff0c\u4e24\u4e2a\u8001\u4eba\u201c\u62cd\uff01\u62cd\uff01\u62cd\uff01\u201d\u8fde\u7740 \u4e92\u51fb\u4e09\u638c\uff0c\u679c\u7136\u638c\u98ce\u5df2\u4e45\uff0c\u4e8c\u4eba\u53cc\u624b\u62cd\u5728\u4e00\u8d77\u4e0e\u5e38\u4eba\u65e0\u5f02\uff01 \u3000\u3000\u5c55\u767d\u4e0e\u5a49\u513f\u5148\u542c\u5230\u795e\u9a74\u94c1\u80c6\u8bf4\u4ed6\u4eec\u4e24\u4e2a\u4eba\u4eb2\u70ed\uff0c\u53d6\u53cc\u9762\u5b54\u4e00\u7ea2\uff1b\u53c8\u89c1\u4e24\u4e2a\u8001\u4eba\u5982\u6b64\u597d \u5f3a\uff0c\u4e34\u6b7b\u4e4b\u524d\uff0c\u4ecd\u4e0d\u653e\u8fc7\u6bd4\u6597\u7684\u673a\u4f1a\uff0c\u4e00\u65f6\u77a7\u5f97\u6123\u4e86\u2026\u2026 \u3000\u3000\u5f85\u4e24\u4e2a\u8001\u4eba\u4e09\u51fb\u638c\u5df2\u6bd5\uff0c\u56de\u5934\u53eb\u4ed6\u4eec\u4e24\u4e2a\u4eba\u65f6\uff0c\u4e8c\u4eba\u624d\u4e92\u76f8\u5bf9\u671b\u4e86\u4e00\u773c\uff0c\u7ade\u8eab\u4e0d\u7531\u5df1\u5730 \u5404\u81ea\u8d70\u5411\u4e00\u4e2a\u8001\u4eba\u8eab\u8fb9\u3002 \u3000\u3000\u8463\u5343\u91cc\u5bf9\u5c55\u767d\u9053\uff1a\u201c\u5c0f\u4f19\u5b50\uff0c\u968f\u6211\u6765\u5427\uff01\u201d \u3000\u3000\u8bf4\u7f62\u6447\u6447\u6446\u6446\u5411\u524d\u8d70\u53bb\uff0c\u8d70\u5230\u77f3\u53f0\u8fb9\u4e0a\uff0c\u7531\u4e8e\u4ed6\u5185\u8151\u53d7\u4f24\uff0c\u771f\u6c14\u6e83\u6563\uff0c\u7adf\u65e0\u6cd5\u7eb5\u4e0b\u4e24\u4e08 \u4f59\u9ad8\u7684\u77f3\u53f0\uff0c\u56f4\u7740\u77f3\u53f0\u8f6c\u4e86\u534a\u5929\uff0c\u770b\u5b9e\u5728\u4e0d\u4e0b\u53bb\uff0c\u7adf\u53f9\u4e86\u4e00\u53e3\u6c14\u9053\uff1a\u201c\u5c0f\u4f19\u5b50\uff0c\u8fd8\u662f\u7531\u4f60\u628a \u6211\u80cc\u4e0b\u53bb\u5427\uff01\u201d \u3000\u3000\u5c55\u767d\u6b64\u65f6\u624d\u77e5\u795e\u9a74\u94c1\u80c6\uff0c\u8fd9\u4e00\u4ee3\u5947\u4fa0\uff0c\u679c\u771f\u662f\u6b66\u529f\u5df2\u5931\uff0c\u4e0d\u77e5\u4ed6\u8fd8\u600e\u6837\u4f20\u7ed9\u81ea\u5df1\u6b66\u529f\uff1f \u7531\u4e8e\u81ea\u5df1\u8fd8\u8981\u5411\u4ed6\u63a2\u8be2\u6740\u7236\u4ec7\u4eba\u7684\u5e95\u8574\uff0c\u968f\u6beb\u4e0d\u8fdf\u7591\uff0c\u5f53\u5373\u80cc\u8d77\u8463\u5343\u91cc\u98d8\u8eab\u8dc3\u4e0b\u77f3\u53f0\u3002\u8463\u5343 \u91cc\u5374\u4e00\u76f4\u9886\u4ed6\u8f6c\u8fc7\u4e00\u4e2a\u5c71\u515c\uff0c\u8fdb\u5165\u4e00\u5ea7\u9690\u79d8\u77f3\u6d1e\u4e4b\u4e2d\u2026\u2026 \u3000\u3000\u5a49\u513f\u6014\u6014\u5730\u671b\u7740\u4e8c\u4eba\u80cc\u5f71\u6d88\u5931\uff0c\u4ecd\u5728\u53d1\u5446\u2026\u2026\u4f38\u7334\u94c1\u51cc\u5374\u5750\u5728\u5730\u4e0b\u8bf4\u9053\uff1a\u5973\u5a03\u513f\uff0c\u6211\u4eec \u600e\u4e48\u8d70\uff1f\u201d \u3000\u3000\u539f\u6765\u4ed6\u4e0b\u80f6\u6728\u6869\u53ca\u53cc\u584c\u5747\u5df2\u6298\u65ad\uff0c\u53c8\u4e0d\u597d\u610f\u601d\u51fa\u53e3\u53eb\u5a49\u513f\u80cc\u4ed6\uff0c\u6240\u4ee5\u53cd\u95ee\u5a49\u513f\u5e94\u5982\u6cb3\u8d70 \u6cd5\uff0c\u8c01\u77e5\u5a49\u513f\u4ec5\u9762\u5b54\u5fae\u7ea2\u4e00\u4e0b\uff0c\u7ade\u5927\u5927\u65b9\u65b9\u5730\u9053\uff1a\u201c\u770b\u6837\u5b50\u4f60\u4e5f\u65e0\u6cd5\u8d70\u8def\u4e86\uff0c\u7531\u6211\u80cc\u4f60\u5427\uff01 \u53ef\u662f\uff0c\u6211\u4eec\u4e0a\u54ea\u513f\u53bb\u5462\uff1f\u201d \u3000\u3000\u5a49\u513f\u8bf4\u8bdd\u6ca1\u6709\u5c55\u767d\u5ba2\u6c14\uff0c\u4f46\u5374\u6b63\u5bf9\u4e86\u4f38\u7334\u94c1\u51cc\u7684\u813e\u80c3\uff0c\u95fb\u8a00\u7b11\u9053\uff1a\u6b64\u5730\u5c71\u6d1e\u591a\u7684\u662f\uff0c\u968f \u4fbf\u627e\u4e00\u4e2a\u5c71\u6d1e\u5c31\u662f\u4e86\uff0c\u4f46\u4e0d\u8981\u79bb\u5f00\u592a\u8fdc\uff0c\u4e09\u6708\u4e4b\u540e\uff0c\u6211\u8001\u4eba\u5bb6\u8fd8\u8981\u4eb2\u81ea\u770b\u4f60\u628a\u8001\u9a74\u513f\u7684\u4f20\u4eba \u6253\u8d25\uff01\u2026\u2026\u201d \u3000\u3000\u5a49\u513f\u76b1\u7709\u9053\uff1a\u201c\u6211\u4e0d\u8981\uff01\u5728\u53c8\u9ed1\u53c8\u810f\u7684\u5c71\u6d1e\u91cc\u4f4f\u4e09\u4e2a\u6708\uff0c\u6211\u53ef\u4e0d\u5e72\uff01\u201d \u3000\u3000\u795e\u7334\u94c1\u51cc\u4e00\u6014\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u4f60\u4e0d\u8981\u5b66\u7edd\u6cc4\u6b66\u529f\u4e86\uff1f\u201d \u3000\u3000\u5a49\u513f\u9053\uff1a\u201c\u5c31\u5728\u8fd9\u77f3\u53f0\u4e0a\u4e0d\u4e5f\u662f\u5f88\u597d\u5417\uff1f\u201d \u3000\u3000\u795e\u7334\u94c1\u51cc\u9053\uff1a\u7ec3\u6b66\u529f\u4e00\u5b9a\u8981\u9690\u79d8\uff0c\u4e00\u662f\u6015\u5916\u4eba\u5077\u7aa5\uff0c\u518d\u8005\u4e5f\u6015\u5916\u7269\u5e72\u6270\uff0c\u6211\u8001\u4eba\u5bb6\u4f20\u4f60 \u51e0\u95e8\u8fde\u6211\u81ea\u5df1\u4e5f\u6ca1\u6709\u7ec3\u4f1a\u7684\u6b66\u529f\uff0c\u5305\u4f60\u80fd\u80dc\u8fc7\u90a3\u8001\u9a74\u513f\u7684\u4f20\u4eba\uff01\u201d \u3000\u3000\u8bf4\u7f62\u8fd8\u8be1\u79d8\u4e00\u7b11\u3002\u5a49\u513f\u4e0d\u4fe1\u9053\uff1a\u201c\u8fde\u4f60\u81ea\u5df1\u90fd\u4e0d\u4f1a\uff0c\u8fd8\u600e\u80fd\u4f20\u7ed8\u6211\uff1f\u4f55\u51b5\u4f60\u7684\u6b66\u529f\u5df2 \u5931\uff0c\u53c8\u8d1f\u4f24\u2026\u2026\u201d \u3000\u3000\u795e\u7334\u94c1\u51cc\u773c\u4e00\u77aa\uff0c\u5ffd\u9053\uff1a\u201c\u4f60\u613f\u4e0d\u613f\u5b66\uff1f\u2026\u2026\u201d \u3000\u3000\u5a49\u513f\u9053\uff1a\u201c\u6211\u4e0d\u5b66\u4e86\uff01\u201d \u3000\u3000\u8bf4\u7f62\u56de\u8eab\u5c31\u8d70\u2014\u201c\u5443\uff01\u5443\uff01\u201d\u795e\u7334\u6025\u53eb\u4f4f\u5a49\u513f\uff0c\u7ade\u6ee1\u8138\u54c0\u6c42\u4e4b\u8272\u903c\uff1a\u201c\u6211\u5df2\u7ecf\u8ddf\u8001\u9a74\u513f \u4e09\u51fb\u638c\u8fc7\u4e86\uff0c\u600e\u80fd\u8bf4\u4e86\u4e0d\u7b97\uff1f\u65e0\u8bba\u5982\u4f55\u6211\u8981\u628a\u8eab\u8d1f\u7edd\u4e16\u6b66\u529f\u4f20\u7ed9\u4f60\u2026\u2026\u201d \u3000\u3000\u5a49\u513f\u89c1\u4ed6\u8bf4\u5f97\u6073\u5207\uff0c\u53c8\u8f6c\u56de\u8eab\u6765\u9053\uff1a\u201c\u90a3\u4e48\u5c31\u8981\u4f9d\u6211\uff0c\u5c31\u5728\u8fd9\u77f3\u53f0\u4e0a\u2026\u2026\u201d \u3000\u3000\u795e\u7334\u94c1\u51cc\u9762\u6709\u96be\u8272\uff0c\u601d\u7d22\u4e86\u4e00\u4f1a\u9053\uff1a\u201c\u7edd\u4e16\u6b66\u529f\u7684\u79d8\u8bc0\uff0c\u4e0d\u5165\u4e24\u8033\uff0c\u800c\u4e14\u4e5f\u6700\u6015\u5916\u9b54\u4fb5 \u6270\uff0c\u6211\u8001\u4eba\u5bb6\u65ad\u53bb\u4e24\u817f\u5c31\u662f\u4e00\u4e2a\u4f8b\u5b50\u6211\u65e2\u8981\u4f20\u4f60\u6b66\u529f\uff0c\u80fd\u80dc\u8fc7\u8001\u9a74\u513f\u7684\u4f20\u4eba\uff0c\u5374\u51b3\u4e0d\u80fd\u5bb3 \u4f60\u3002\u73b0\u5728\u4f60\u80cc\u6211\u5230\u4e00\u4e2a\u9690\u79d8\u6d1e\u5e9c\uff0c\u6211\u4f20\u4f60\u6b66\u529f\u79d8\u8bc0\uff0c\u9664\u4e86\u4f60\u7ec3\u529f\u65f6\u5728\u6d1e\u5e9c\u4e4b\u4e2d\uff0c\u4ee5\u5916\u7684\u65f6 \u95f4\uff0c\u968f\u4f60\u81ea\u7531\u884c\u52a8\uff0c\u4f60\u770b\u8fd9\u53ef\u597d\u4e86\u5427\uff1f\u201d \u3000\u3000\u5a49\u513f\u4e00\u53f9\u9053\uff1a\u201c\u6211\u771f\u6ca1\u6709\u4ec0\u4e48\u5fc3\u601d\u5b66\u4e60\u6b66\u529f\uff0c\u4f46\u53c8\u4e0d\u5fcd\u62d2\u7edd\u4f60\uff0c\u770b\u6765\u4e5f\u53ea\u597d\u5982\u6b64\u4e86\uff01\u201d \u3000\u3000\u8bf4\u7f62\u80cc\u8f6c\u8138\u6765\u8e72\u4e0b\u8eab\u53bb\uff0c\u795e\u7334\u94c1\u51cc\u5927\u7a96\u8fc7\u671b\uff0c\u5fd9\u4f38\u624b\u56f4\u4f4f\u5a49\u513f\u8116\u9888\uff0c\u4f0f\u5728\u5a49\u513f\u80cc\u4e0a\uff0c\u7531 \u5a49\u513f\u80cc\u7740\u4ed6\u7a9c\u4e0b\u77f3\u53f0\uff0c\u6cbf\u7740\u5c71\u5d16\u627e\u53bb\uff0c\u8c01\u77e5\u8fde\u627e\u4e86\u6570\u4e2a\u77f3\u6d1e\uff0c\u5747\u4e0d\u5408\u795e\u7334\u94c1\u51cc\u7406\u60f3\uff0c\u6700\u540e\u53c8 \u5728\u4e00\u5757\u5de8\u77f3\u65c1\u627e\u5230\u4e00\u4e2a\u6d1e\u53e3\uff0c\u5a49\u513f\u6c14\u8d77\u6765\u9053\uff1a\u201c\u4e0d\u7ba1\u884c\u4e0d\u884c\uff0c\u54b1\u4eec\u5c31\u662f\u8fd9\u4e2a\u6d1e\u4e86\uff0c\u6211\u51b3\u4e0d\u518d \u80cc\u7740\u4f60\u4e1c\u8dd1\u897f\u8dd1\uff0c\u597d\u50cf\u627e\u4e0d\u5230\u7a9d\u7684\u5154\u5b50\u4e00\u6837\uff01\u2026\u2026\u201d \u3000\u3000\u8bf4\u5230\u6700\u540e\uff0c\u5a49\u513f\u81ea\u5df1\u4e5f\u5fcd\u4fca\u4e0d\u4f4f\uff0c\u5657\u54e7\u7b11\u51fa\u58f0\u6765\uff0c\u5e76\u5c31\u52bf\u4e00\u8e72\u8eab\uff0c\u628a\u795e\u7334\u94c1\u51cc\u653e\u5728\u6d1e\u5185 \u5730\u4e0b\u3002 \u3000\u3000\u795e\u7334\u94c1\u51cc\u7741\u7740\u4e00\u5bf9\u7334\u773c\uff0c\u56db\u4e0b\u625b\u91cf\u4e00\u756a\u76b1\u7709\u9053\uff1a\u201c\u8fd9\u4e2a\u6d1e\u8fd8\u6bd4\u4e0d\u4e0a\u5148\u524d\u627e\u5230\u7684\u90a3\u4e24\u4e2a\uff0c \u800c\u4e14\uff0c\u6d1e\u53e3\u98ce\u8fd9\u6837\u5927\uff0c\u8bf4\u4e0d\u5b9a\u662f\u4e2a\u6f0f\u5e95\u6d1e\uff0c\u6211\u770b\u2026\u2026\u201d \u3000\u3000\u5a49\u513f\u4e0d\u8010\u70e6\u5730\u9053\uff1a\u201c\u4e0d\u8981\u4f60\u770b\u4e86\uff01\u8bf4\u4e0d\u627e\u5c31\u4e0d\u627e\u4e86\uff0c\u65e2\u6015\u6d1e\u53e3\u98ce\u5927\uff0c\u6211\u5c31\u80cc\u4f60\uff0c\u518d\u5230\u91cc \u8fb9\u627e\u80cc\u98ce\u7684\u5730\u65b9\u4e5f\u5c31\u662f\u4e86\uff01\u201d \u3000\u3000\u8bf4\u7f62\u80cc\u8138\u8e72\u4e0b\u8eab\u6765\u3002 \u3000\u3000\u795e\u7334\u94c1\u51cc\u957f\u53f9\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u6211\u795e\u7334\u94c1\u51cc\uff0c\u7eb5\u6a2a\u4e00\u4e16\uff0c\u5929\u4e0d\u6015\uff0c\u5730\u4e0d\u6015\uff0c\u4e34\u6b7b\u4e4b\u524d\uff0c\u5374\u8981 \u53d7\u4f60\u8fd9\u5973\u5a03\u513f\u7684\u95f2\u6c14\u2026\u2026\u201d \u3000\u3000\u5a49\u513f\u53c8\u7ad9\u8d77\u6765\uff0c\u67f3\u7709\u4e00\u6311\uff0c\u6012\u9053\uff1a\u201c\u6211\u8fd8\u4e0d\u613f\u610f\u5462\uff01\u65e2\u662f\u5982\u6b64\u8bf4\uff0c\u54b1\u4eec\u7b97\u4e86\uff01\u6211\u4e5f\u4e0d\u60f3 \u5b66\u4f60\u4ec0\u4e48\u7edd\u4e16\u6b66\u529f\uff0c\u4f60\u4e5f\u7528\u4e0d\u5230\u53d7\u6211\u7684\u95f2\u6c14\uff0c\u54b1\u4eec\u662f\u4e24\u884c\u5176\u4fbf\uff01\u201d \u3000\u3000\u8bf4\u7f62\u56de\u8eab\u5c31\u8d70\uff0c\u795e\u7334\u94c1\u51cc\u6025\u9053\uff1a\u201c\u5443\uff01\u5443\uff01\u5973\u5a03\u513f\uff01\u4f60\u4e0d\u8981\u90a3\u4e48\u6027\u6025\u597d\u4e0d\u597d\uff1f\u6211\u8001\u4eba\u5bb6 \u90fd\u4f9d\u4f60\u7684\u5c31\u662f\u4e86\uff01\u201d \u3000\u3000\u5a49\u513f\u53c8\u505c\u4e0b\u8eab\u6765\u9053\uff1a\u90a3\u4ee5\u540e\u4e5f\u522b\u8001\u662f\u5973\u5a03\u513f\u5973\u5a03\u513f\u7684\u4e71\u53eb\uff0c\u6211\u53eb\u5c55\u5a49\u513f\uff0c\u6709\u4ec0\u4e48\u8bdd\uff0c\u4f60 \u5c31\u53eb\u6211\u7684\u540d\u5b57\u597d\u4e86\uff01\u201d \u3000\u3000\u201c\u4e00\u5207\u9075\u547d\uff01\u201d\u795e\u7334\u8fde\u5fd9\u70b9\u5934\u9053\uff1a\u201c\u6211\u4eca\u751f\u7b97\u662f\u7b2c\u4e00\u6b21\u6015\u4e86\u4e00\u4e2a\u4eba\uff01\u2026\u2026\u201d \u3000\u3000\u5a49\u513f\u63a5\u53e3\u9053\uff1a\u201c\u4e0d\u5bf9\uff01\u4f60\u6700\u5c11\u6015\u4e86\u4e24\u4e2a\u4eba\uff0c\u9664\u4e86\u6211\u4e4b\u5916\u8fd8\u6709\u80dc\u4e86\u4f60\u4e00\u5251\u7684\u2026\u2026\u201d \u3000\u3000\u795e\u7334\u4e00\u77aa\u773c\u9053\uff1a\u201c\u6211\u51b3\u6ca1\u6709\u6015\u8fc7\u7b2c\u4e8c\u4eba\uff0c\u6211\u73b0\u5728\u7684\u529f\u529b\uff0c\u5c31\u80fd\u80dc\u8fc7\u5c55\u4e91\u5929\u8bb8\u591a\uff0c\u5982\u679c\u5047 \u4ee5\u65f6\u65e5\uff0c\u6211\u53e6\u5916\u51e0\u624b\u7edd\u4e16\u795e\u529f\u7ec3\u597d\uff0c\u5c55\u4e91\u5929\u6050\u6015\u8fde\u6211\u7684\u4e00\u62db\u4e5f\u63a5\u4e0d\u4e0b\u2026\u2026\u201d \u3000\u3000\u201c\u5f97\uff01\u5f97\uff01\u201d\u5a49\u513f\u9053\uff1a\u201c\u4e0d\u7528\u5439\u4e86\uff01\u73b0\u5728\u4f60\u6050\u6015\u8fde\u4e00\u4e2a\u4e0d\u4f1a\u6b66\u529f\u7684\u58ee\u6c49\u90fd\u6253\u4e0d\u8fc7\uff01\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u8bf4\u5f97\u795e\u7334\u94c1\u51cc\u54d1\u53e3\u65e0\u8a00\uff0c\u4f46\u8138\u4e0a\u5374\u95ea\u8fc7\u4e00\u62b9\u72e0\u6bd2\u6740\u673a\uff0c\u5fc3\u4e2d\u5bf9\u795e\u9a74\u7684\u4ec7\u6068\u66f4\u52a0\u6df1 \u4e86\u51e0\u5206\u2026\u2026 \u3000\u3000\u5a49\u513f\u4e5f\u89c9\u5f97\u8fd9\u53e5\u8bdd\u8bf4\u5f97\u8fc7\u5206\u4e86\u4e00\u70b9\uff0c\u770b\u4ed6\u75db\u82e6\u7684\u795e\u60c5\uff0c\u5fc3\u4e2d\u4e0d\u5fcd\uff0c\u53c8\u8e72\u4e0b\u8eab\u5f62\u9053\uff1a\u201c\u597d \u4e86\uff0c\u8fc7\u53bb\u7684\u4e0d\u8981\u8bf4\u4e86\uff0c\u8fd8\u662f\u7531\u6211\u80cc\u4f60\u5230\u4e00\u4e2a\u80cc\u98ce\u7684\u5730\u70b9\u5427\uff01\u201d \u3000\u3000\u795e\u7334\u94c1\u51cc\u4e0d\u518d\u53d1\u8a00\uff0c\u4f0f\u5728\u5a49\u513f\u80cc\u4e0a\uff0c\u4efb\u7531\u5a49\u513f\u80cc\u7740\u76f4\u5411\u77f3\u6d1e\u6df1\u5904\u8d70\u53bb\u3002 \u3000\u3000\u5947\u602a\u7684\u662f\u8fd9\u6d1e\u53e3\u8fdb\u53e3\u5904\u4e0d\u5927\uff0c\u5374\u76f8\u5f53\u6df1\u8fdc\uff0c\u5a49\u513f\u80cc\u7740\u795e\u7334\u94c1\u51cc\u4e00\u76f4\u6df1\u5165\u6570\u5341\u4e08\uff0c\u4e0d\u4f46\u8fde \u4e2a\u7565\u53ef\u80cc\u98ce\u7684\u5730\u65b9\u4e5f\u6ca1\u627e\u5230\uff0c\u800c\u4e14\u6d1e\u5185\u6b67\u8def\u9519\u7efc\u590d\u6742\uff0c\u6bcf\u4e00\u4e2a\u9ed1\u5e7d\u5e7d\u7684\u6d1e\u53e3\u4e4b\u4e2d\uff0c\u90fd\u6709\u547c\u547c \u52b2\u98ce\u5439\u51fa\uff0c\u98ce\u52bf\u6c47\u5408\uff0c\u54e8\u54e8\u4f5c\u54cd\uff0c\u7adf\u6bd4\u6d1e\u53e3\u5904\u98ce\u52bf\u66f4\u589e\u5927\u4e86\u6570\u500d\u3002 \u3000\u3000\u8fd9\u60c5\u5f62\u4e8c\u4eba\u5fc3\u4e2d\u90fd\u611f\u5230\u4e0d\u5999\uff0c\u4f46\u662f\u8c01\u4e5f\u4e0d\u8bf4\u51fa\u53e3\u6765\uff0c\u795e\u7334\u94c1\u51cc\u4ece\u77ed\u77ed\u76f8\u805a\u4e4b\u4e2d\uff0c\u5df2\u7ea6\u7565 \u77e5\u9053\u80cc\u7740\u81ea\u5df1\u7684\u7f8e\u4e3d\u5c11\u5973\uff0c\u5fc3\u5730\u867d\u7136\u6781\u4e3a\u7eaf\u6d01\u5584\u826f\uff0c\u4f46\u6027\u60c5\u5374\u662f\u76f8\u5f53\u7684\u4efb\u6027\uff0c\u5047\u5982\u6b64\u65f6\u5f00\u53e3 \u8981\u5979\u56de\u8f6c\u53bb\uff0c\u6050\u6015\u5979\u4e0d\u4f46\u4e0d\u542c\uff0c\u53cd\u800c\u53c8\u8981\u4e22\u4e0b\u81ea\u5df1\u8dd1\u6389\u3002 \u3000\u3000\u81ea\u5df1\u5bf8\u6b65\u4e0d\u80fd\u79fb\u52a8\uff0c\u90a3\u53ef\u5c31\u60e8\u4e86\u3002\u6240\u4ee5\u867d\u770b\u51fa\u60c5\u5f62\u4e0d\u5bf9\uff0c\u4ecd\u5f3a\u5fcd\u4f4f\u4e0d\u8bf4\uff0c\u4efb\u7531\u5c55\u5a49\u513f\u80cc \u7740\u5411\u524d\u6df1\u4e00\u811a\u6d45\u4e00\u811a\u5730\u8d70\u53bb\u3002 \u3000\u3000\u5a49\u513f\u56e0\u4e3a\u8bdd\u5df2\u8bf4\u6ee1\uff0c\u4e00\u5b9a\u8981\u5728\u8fd9\u6d1e\u4e2d\u7ec3\u6b66\uff0c\u73b0\u5728\u867d\u770b\u51fa\u6b64\u6d1e\u5b9e\u4e0d\u7406\u60f3\uff0c\u4f46\u4e00\u65f6\u6539\u4e0d\u8fc7\u5634 \u6765\uff0c\u6240\u4ee5\u4ecd\u7136\u5411\u77f3\u6d1e\u6df1\u5904\u8d70\u53bb\uff0c\u800c\u4e14\u811a\u4e0b\u53c8\u52a0\u4e86\u51e0\u6210\u529f\u529b\u3002 \u3000\u3000\u5a49\u513f\u8f7b\u8eab\u529f\u529b\u672c\u5c31\u597d\uff0c\u518d\u52a0\u4e0a\u5973\u5b69\u5b50\u5bb6\u5148\u5929\u7684\u8eab\u4f53\u5a07\u5c0f\u73b2\u6591\uff0c\u53c8\u6709\u5979\u6155\u5bb9\u6db5\u5bb6\u4f20\u7684\u7edd\u4e16 \u8f7b\u529f\u8eab\u6cd5\u201c\u954a\u7a7a\u5e7b\u5f71\u201d\uff0c\u7eb5\u5728\u5149\u7ebf\u4e0d\u592a\u660e\u4eae\u3001\u77f3\u58c1\u51f9\u51f8\u3001\u5730\u4e0b\u5d0e\u5c96\u4e0d\u5e73\u7684\u5c71\u6d1e\u4e4b\u4e2d\uff0c\u4ecd\u80fd\u7eb5 \u8dc3\u5982\u98de\uff0c\u52a0\u901f\u524d\u8fdb\u3002 \u3000\u3000\u8db3\u8db3\u5954\u9a70\u4e86\u4e00\u987f\u996d\u7684\u65f6\u95f4\uff0c\u4ee5\u5a49\u513f\u7684\u811a\u7a0b\u8ba1\u7b97\uff0c\u867d\u7136\u6d1e\u4e2d\u9ed1\u6697\u5d0e\u9a71\u8def\u4e0d\u597d\u8d70\uff0c\u5979\u672a\u80fd\u5168 \u529b\u65bd\u4e3a\uff0c\u4f46\u6700\u5c11\u4f30\u8ba1\u6df1\u5165\u5c71\u6d1e\u4e5f\u6709\u6570\u91cc\u4e4b\u9065\u4e86\u3002\u4e09\u4eba\u5fc3\u4e2d\u90fd\u5df2\u6709\u4e86\u9000\u610f\uff0c\u4e0d\u8fc7\u8fd8\u6ca1\u6709\u8bf4\u51fa\u53e3 \u6765\uff0c\u5a49\u513f\u7684\u811a\u6b65\u4e5f\u6e10\u6e10\u6162\u4e0b\u6765\u3002 \u3000\u3000\u5ffd\u7136\uff0c\u5a49\u513f\u60ca\u547c\u4e86\u4e00\u58f0\uff1a\u201c\u54ce\u5440\uff01\u201d \u3000\u3000\u795e\u7334\u94c1\u51cc\u56e0\u529f\u529b\u5df2\u5931\uff0c\u76ee\u529b\u5927\u4e0d\u5982\u524d\uff0c\u542c\u5230\u5a49\u513f\u60ca\u547c\uff0c\u5fd9\u95ee\u9053\uff1a\u5a49\u513f\uff0c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u5a49\u513f\u9053\uff1a\u8fd9\u91cc\u600e\u4e48\u6709\u6b7b\u4eba\uff1f\u201d \u3000\u3000\u8bf4\u7740\u8d70\u8fd1\u4e00\u5177\u6b7b\u5c38\u8ddf\u524d\uff0c\u795e\u7334\u94c1\u51cc\u8fd9\u624d\u770b\u5230\u501a\u58c1\u7acb\u7740\u4e00\u5177\u8840\u6dcb\u6dcb\u7684\u6b7b\u5c38\uff01 \u3000\u3000\u4e8c\u4eba\u4ee5\u4e3a\u90a3\u6b7b\u5c38\u662f\u501a\u5899\u800c\u7acb\uff0c\u6240\u4ee5\u672a\u4f8b\uff0c\u8c01\u77e5\u4ed4\u7ec6\u4e00\u770b\uff0c\u5927\u8c2c\u4e0d\u7136\uff0c\u539f\u6765\u90a3\u6b7b\u5c38\u7684\u5fc3\u7a9d \u4e0a\u63d2\u7740\u4e00\u67c4\u4f3c\u9556\u975e\u9556\u3001\u4f3c\u68f1\u975e\u68f1\u7684\u68f1\u5f62\u6697\u5668\uff0c\u51c0\u84dd\u74e6\u4eae\uff0c\u4e00\u770b\u800c\u77e5\u8574\u6709\u5947\u6bd2\uff0c\u8db3\u6709\u4e00\u5c3a\u4f59 \u957f\uff0c\u7531\u524d\u80f8\u8d2f\u5165\u540e\u5fc3\uff0c\u9489\u5165\u575a\u786c\u7684\u77f3\u58c1\u6570\u5bf8\u4e4b\u6df1\uff0c\u662f\u4ee5\u8fd9\u5177\u6b7b\u5c38\u624d\u80fd\u4e0d\u5012\u3002 \u3000\u3000\u5a49\u513f\u6115\u7136\u9053\uff1a\u770b\u8fd9\u6837\u5b50\uff0c\u6b64\u4eba\u662f\u751f\u524d\u88ab\u4eba\u7528\u6697\u5668\u6253\u6b7b\uff0c\u4e00\u76f4\u7ad9\u5728\u8fd9\u91cc\uff0c\u8eab\u4e0a\u7684\u8840\u8ff9\u672a \u5e72\uff0c\u6b7b\u4e86\u6050\u6015\u6ca1\u6709\u591a\u4e45\uff0c\u8fd9\u79cd\u6697\u5668\uff0c\u5374\u4ece\u672a\u89c1\u8fc7\uff0c\u4e0d\u77e5\u662f\u4ec0\u4e48\u6697\u5668\uff1f\u201d \u3000\u3000\u795e\u7334\u770b\u4e86\u4e00\u4f1a\uff0c\u6447\u5934\u9053\uff1a\u201c\u8fde\u6211\u8001\u4eba\u5bb6\u4e5f\u6ca1\u6709\u89c1\u8fc7\uff0c\u4e0d\u77e5\u662f\u4ec0\u4e48\u6697\u5668\uff0c\u4e0d\u8fc7\uff0c\u8fd9\u4f7f\u7528\u6697 \u5668\u7684\u4eba\u624b\u52b2\u4e0d\u5c0f\uff0c\u800c\u4e14\u8fd9\u6697\u5668\u4e0a\u8fd8\u8574\u6709\u5947\u6bd2\uff0c\u5374\u662f\u4e0d\u4f1a\u9519\u7684\uff01\u201d \u3000\u3000\u5a49\u513f\u53c8\u9053\uff1a\u201c\u8fd9\u88ab\u6697\u5668\u6253\u6b7b\u7684\u4eba\uff0c\u8001\u524d\u8f88\u8ba4\u8bc6\u5417\uff1f\u201d \u3000\u3000\u795e\u7334\u6253\u91cf\u4e86\u4e00\u4e0b\uff0c\u89c1\u90a3\u4eba\u8eab\u7a7f\u6de1\u9752\u7f15\u91d1\u7ef8\u886b\uff0c\u5934\u62b9\u9576\u7740\u4e00\u7c92\u660e\u73e0\u7684\u6de1\u6b66\u58eb\u5dfe\uff0c\u8863\u7740\u5f02\u5e38 \u534e\u7f8e\uff0c\u4f46\u8138\u4e0a\u751f\u50cf\u5374\u5f02\u5e38\u5a01\u731b\uff0c\u6d53\u7709\u73af\u76ee\uff0c\u6ee1\u8138\u4e8e\u601d\uff0c\u4e00\u770b\u4fbf\u77e5\u662f\u4e00\u6c5f\u6e56\u8c6a\u5bb9\uff0c\u867d\u5df2\u6b7b\u53bb\u591a \u65f6\uff0c\u4f46\u72de\u7709\u7acb\u76ee\uff0c\u4ecd\u7136\u864e\u864e\u6709\u5a01\u3002 \u3000\u3000\u795e\u7334\u94c1\u51cc\u6447\u5934\u9053\uff1a\u201c\u6211\u5c11\u5728\u4e2d\u539f\u8d70\u52a8\uff0c\u5c24\u5176\u6700\u8fd1\u5341\u6570\u5e74\u6211\u8001\u4eba\u5bb6\u672a\u613f\u6c5f\u6e56\uff0c\u4e0d\u77e5\u6b64\u4eba\u662f \u8c01\u3002\u201d \u3000\u3000\u5a49\u513f\u89c1\u90a3\u5927\u6c49\u6b7b\u72b6\u751a\u63ba\uff0c\u80f8\u524d\u9c9c\u8840\u7433\u6dcb\uff0c\u987f\u89c9\u8fd9\u53e4\u602a\u77f3\u6d1e\u4e4b\u4e2d\uff0c\u9690\u4f0f\u7740\u65e0\u9650\u6740\u673a\uff0c\u4e0d\u7531 \u5fc3\u751f\u602f\u610f\uff0c\u4f46\u5979\u5634\u4e2d\u4ecd\u4e0d\u80af\u793a\u5f31\u5730\u8bf4\u9053\uff1a\u201c\u6211\u770b\u8fd9\u6d1e\u91cc\u4e00\u5b9a\u9690\u85cf\u7740\u51f6\u4eba\uff0c\u8001\u524d\u8f88\uff0c\u6211\u4eec\u8fd8\u8981 \u4e0d\u8981\u5f80\u91cc\u8d70\uff1f\u201d \u3000\u3000\u795e\u7334\u94c1\u51cc\u4e00\u7b11\u9053\uff1a\u201c\u8fd9\u53ef\u8981\u7531\u59d1\u5a18\u62ff\u4e3b\u610f\u4e86\uff0c\u6211\u8001\u4eba\u5bb6\u672c\u662f\u4e0d\u4e3b\u5f20\u5230\u8fd9\u6d1e\u91cc\u6765\u7684\uff01\u201d \u3000\u3000\u5a49\u513f\u95fb\u8a00\u4e00\u6c14\uff0c\u4e0d\u518d\u7406\u4ed6\uff0c\u62e8\u817f\u4fbf\u5f80\u91cc\u95ef\uff0c\u5b9a\u51fa\u5341\u6570\u4e08\uff0c\u53c8\u9047\u5230\u4e24\u5177\u6b7b\u5c38\uff0c\u8fd9\u4e24\u5177\u6b7b\u5c38 \u8863\u7740\u4e0e\u524d\u8005\u65e0\u5f02\uff0c\u4e0d\u8fc7\u8fd9\u4e8c\u4eba\u5374\u662f\u540e\u5fc3\u88ab\u90a3\u68f1\u5f62\u6697\u5668\u6253\u4e2d\uff0c\u530d\u5310\u5728\u5730\uff0c\u8138\u5f62\u770b\u4e0d\u6e05\u695a\uff0c\u6bcf\u4eba \u5634\u8fb9\u5730\u4e0b\u6dcc\u7740\u4e00\u5806\u8840\u8ff9\uff0c\u5176\u4e2d\u4e00\u4e2a\u5934\u4e0a\u9576\u73e0\u7684\u6de1\u9752\u6b66\u58eb\u5dfe\uff0c\u5df2\u6eda\u843d\u8001\u8fdc\uff0c\u9732\u51fa\u6ee1\u5934\u5982\u523a\u732c\u7684 \u4e71\u53d1\u3002\u5a49\u513f\u867d\u7136\u5165\u76ee\u60ca\u5fc3\uff0c\u4f46\u7531\u4e8e\u6b63\u4e0e\u795e\u7334\u54bd\u6c14\uff0c\u811a\u4e0b\u6beb\u4e0d\u505c\u7559\uff0c\u4ecd\u7136\u5f80\u91cc\u8d70\uff0e\u795e\u7334\u867d\u7136\u4e0d \u8bf4\u8bdd\uff0c\u4e14\u5df2\u662f\u5c06\u6b7b\u4e4b\u4eba\uff0c\u4f46\u89c1\u6b64\u60c5\u666f\uff0c\u5fc3\u4e2d\u4e5f\u4e0d\u7531\u76f4\u5192\u51c9\u6c14\uff0c\u6697\u601d\u81ea\u5df1\u529f\u529b\u5df2\u5931\uff0c\u8fd9\u5973\u5a03\u513f \u6c5f\u6e56\u9605\u5386\u53c8\u5dee\uff0c\u53ea\u987e\u4efb\u6027\u5f80\u6d1e\u5185\u786c\u95ef\uff0c\u5047\u5982\u6709\u6b79\u4eba\u9690\u8eab\u6697\u5904\uff0c\u7a81\u65bd\u6697\u88ad\uff0c\u90a3\u51b3\u65e0\u6cd5\u5e94\u4ed8\uff0c\u843d \u4e2a\u6e85\u8840\u77f3\u6d1e\uff0c\u4e0e\u90a3\u4e09\u5177\u6b7b\u5c38\u4e00\u6837\u7684\u4e0b\u573a\u3002\u56e0\u6b64\uff1b\u5a49\u513f\u80cc\u4ed6\u5f80\u91cc\u8d70\u65f6\uff0c\u4ed6\u6b62\u4e0d\u4f4f\u4e3e\u76ee\u56db\u5904\u89c2 \u671b\u3002 \u3000\u3000\u53c8\u8d70\u51fa\u5341\u6570\u4e08\uff0c\u524d\u9762\u4e00\u5835\u77f3\u58c1\uff0c\u98ce\u52bf\u7565\u5c0f\uff0c\u5a49\u513f\u80cc\u7740\u795e\u7334\uff0c\u8d70\u8fd1\u77f3\u58c1\u524d\uff0c\u65c1\u4fa7\u6709\u4e00\u77f3 \u95e8\uff0c\u4f3c\u662f\u4e00\u5904\u77f3\u5ba4\uff0c\u5a49\u513f\u8fc8\u6b65\u5411\u77f3\u5ba4\u8d70\u8fdb\u3002 \u3000\u3000\u201c\u5c0f\u5fc3\uff01\u2026\u2026\u201d\u795e\u7334\u4e00\u8bed\u672a\u6bd5\uff0c\u5a49\u513f\u5df2\u8fc8\u6b65\u8d70\u8fdb\u77f3\u5ba4\u4e4b\u4e2d\uff0c\u7a81\u7136\u6697\u5f71\u91cc\u63a2\u51fa\u4e00\u53cc\u9ed1\u624b\uff0c \u6084\u65e0\u58f0\u606f\u5730\u6251\u9762\u6293\u81f3\u3002 \u3000\u3000\u5a49\u513f\u9a87\u7136\u60ca\u547c\uff1a\u54ce\u5440\uff01\u201d"}),showOnHomepage:!0},{name:"\u7b2c\u4e8c\u5341\u4e03\u7ae0\u3000\u201c\u7aef\u65b9\u516c\u5b50\u201d",content:(0,I.jsx)(I.Fragment,{children:"\u5a49\u513f\u60ca\u547c\u6025\u9000\uff0c\u4e3e\u638c\u6b32\u5411\u2026\u2026 \u3000\u3000\u795e\u7334\u867d\u7136\u529f\u529b\u5df2\u5931\uff0c\u4f46\u6c5f\u6e56\u9605\u5386\u5374\u6bd4\u5a49\u513f\u4e30\u5bcc\u591a\u4e86\uff0c\u5fd9\u9053\uff1a\u201c\u5a49\u513f\uff0c\u4e0d\u5fc5\u7d27\u5f20\uff01\u90a3\u662f\u4e00 \u4e2a\u6b7b\u4eba\uff01\u201d \u3000\u3000\u5a49\u513f\u5b9a\u775b\u4e00\u770b\uff0c\u679c\u7136\u53c8\u662f\u4e00\u6b7b\u4eba\uff0c\u5728\u5730\u4e0b\u6389\u6709\u4e00\u67c4\u51b7\u68ee\u68ee\u9690\u6cdb\u9752\u5149\u7684\u4e27\u95e8\u5251\uff0c\u8fd9\u624d\u77e5\u9053 \u6b7b\u8005\u5148\u524d\u9690\u8eab\u95e8\u540e\uff0c\u4ee5\u624b\u4e2d\u4e27\u95e8\u5251\u5bf9\u4ed8\u6765\u4eba\u6697\u88ad\uff0c\u5374\u88ab\u6765\u4eba\u8eb2\u8fc7\uff0c\u5e76\u7528\u91cd\u624b\u6cd5\u5c06\u4ed6\u6740\u6b7b\u3002 \u3000\u3000\u8fd9\u88ab\u6740\u7684\u4eba\uff0c\u80f8\u524d\u4e00\u4e2a\u5bf8\u8bb8\u5927\u7684\u8840\u6d1e\uff0c\u4e00\u62db\u6bd9\u547d\uff0c\u867d\u7136\u6740\u4eba\u8005\u5df2\u7ecf\u8d70\u4e86\uff0c\u4f46\u4ea6\u53ef\u770b\u51fa\u5176 \u6b66\u529f\u8fbe\u51fa\u795e\u5165\u5316\u4e4b\u5883\u3002 \u3000\u3000\u5728\u77f3\u5ba4\u9760\u5899\u58c1\u4e0a\uff0c\u53c8\u6709\u4e00\u5177\u6b7b\u5c38\uff0c\u624b\u62ff\u5224\u5b98\u53cc\u7b14\uff0c\u59d4\u5750\u5728\u5730\uff0c\u80f8\u524d\u4e5f\u6709\u4e00\u4f24\u53e3\uff0c\u4e0e\u95e8\u524d \u6b7b\u8005\u4e00\u822c\uff0c\u6b7b\u540e\u8138\u4e0a\u4ecd\u6b8b\u7559\u7740\u6781\u7aef\u6050\u6016\u4e4b\u8272\u3002\u770b\u6837\u5b50\u8fd9\u6b7b\u8005\u5bf9\u6765\u4eba\u975e\u5e38\u60e7\u6015\uff0c\u5224\u5bab\u53cc\u7b14\u53d6 \u51fa\uff0c\u672a\u53ca\u51fa\u62db\uff0c\u5373\u88ab\u6765\u4eba\u7528\u91cd\u624b\u6cd5\u6740\u6b7b\u3002 \u3000\u3000\u5a49\u513f\u6115\u7136\u9053\uff1a\u201c\u8fd9\u91cc\u4e8c\u4eba\u662f\u88ab\u4ec0\u4e48\u624b\u6cd5\u6240\u4f24\uff1f\u201d \u3000\u3000\u795e\u7334\u9762\u8272\u51dd\u91cd\u9053\uff1a\u770b\u6765\u597d\u50cf\u2018\u91d1\u521a\u6307\u2019\u3001\u2018\u4e00\u6307\u63b8\u2019\u7b49\u4e00\u7c7b\u6b66\u529f\uff0c\u4f46\u8fd9\u6740\u4eba\u8005\u2018\u91d1\u521a \u6307\u2019\u3001\u2018\u4e00\u6307\u63b8\u2019\u80fd\u7ec3\u5230\u5982\u6b64\u5883\u754c\uff0c\u529f\u529b\u4e4b\u9ad8\uff0c\u7684\u786e\u5df2\u5230\u4e86\u767b\u5cf0\u9020\u6781\u7684\u5730\u6b65\uff01\u201d \u3000\u3000\u201c\u54ce\u5440\uff01\u201d\u5a49\u513f\u53c8\u60ca\u53eb\u8d77\u6765\u9053\uff1a\u201c\u8001\u524d\u8f88\uff0c\u4f60\u770b\u8fd9\u91cc\u8fd8\u6709\u4e24\u7bb1\u73e0\u5b9d\u2026\u2026\u201d \u3000\u3000\u5a49\u513f\u8bf4\u7740\u4f38\u624b\u4e00\u6307\u77f3\u5ba4\u4e2d\u592e\uff0c\u5730\u4e0b\u653e\u6709\u4e24\u53ea\u4e8c\u5c3a\u89c1\u65b9\u7684\u94c1\u7bb1\uff0c\u7bb1\u76d6\u5df2\u88ab\u6253\u5f00\uff0c\u69b4\u5185\u5c3d\u8db3 \u73cd\u73e0\u739b\u7459\u3001\u91d1\u521a\u94bb\u77f3\u7b49\u7b49\u4e94\u989c\u516d\u8272\u7684\u6b8a\u5b9d\uff0c\u770b\u6765\u5149\u707f\u8000\u76ee\u3002 \u3000\u3000\u795e\u7334\u6027\u60c5\u867d\u7136\u602a\u8bde\uff0c\u4f46\u5b9e\u8d28\u4e0a\u5374\u662f\u4e00\u4e2a\u6c5f\u6e56\u602a\u6770\uff0c\u5bf9\u8fd9\u4e9b\u73e0\u5b9d\u5e76\u672a\u5174\u8d77\u8d2a\u5ff5\u3002 \u3000\u3000\u81f3\u4e8e\u5a49\u513f\u4e43\u5f53\u4eca\u56db\u5927\u8c6a\u95e8\u4e4b\u5973\uff0c\u5bb6\u4e2d\u73cd\u5b9d\u89c1\u5f97\u591a\u4e86\uff0c\u5bf9\u8fd9\u4e9b\u73e0\u5b9d\u4e5f\u672a\u653e\u8fdb\u773c\u5185\u3002 \u3000\u3000\u5a49\u513f\u628a\u795e\u7334\u653e\u5728\u5730\u4e0a\uff0c\u8d70\u5230\u73e0\u5b9d\u7bb1\u524d\u9010\u4e00\u68c0\u89c6\uff0c\u89c1\u90a3\u4e9b\u73e0\u5b9d\uff0c\u5e76\u4e0d\u662f\u5bfb\u5e38\u4e4b\u7269\uff0c\u73cd\u73e0\u90fd \u6709\u9f99\u773c\u5927\u5c0f\uff0c\u94bb\u77f3\u4e5f\u90fd\u662f\u5947\u5927\u65e0\u6bd4\uff0c\u8272\u8292\u4ff1\u4f73\uff0c\u6b64\u5916\u732b\u513f\u773c\u3001\u7956\u6bcd\u7eff\u3001\u7ea2\u73ca\u745a\u3001\u84dd\u5b9d\u77f3\u3001\u7eff \u60b2\u7fe0\u2026\u2026\u7b49\u7b49\u90fd\u662f\u5c18\u4e16\u7f55\u89c1\u4e4b\u7269\uff0c\u4ef6\u4ef6\u4ef7\u503c\u8fde\u57ce\uff0c\u6bd4\u81ea\u5df1\u5bb6\u4e2d\u73cd\u85cf\u4e4b\u7269\uff0c\u5e76\u4e0d\u900a\u8272\u3002 \u3000\u3000\u5c24\u5176\u88c5\u73e0\u5b9d\u7684\u4e24\u53ea\u94c1\u7bb1\uff0c\u96d5\u5851\u7cbe\u7f8e\uff0c\u9690\u6cdb\u4e4c\u5149\uff0c\u663e\u7136\u4e5f\u662f\u8d35\u91cd\u91d1\u5c5e\uff0c\u7531\u540d\u5320\u6253\u9020\uff0c\u4e00\u53ea \u8ddf\u81ea\u5df1\u5bb6\u4e2d\u8d2e\u85cf\u73cd\u5b9d\u7684\u94c1\u7bb1\u4e00\u822c\u65e0\u5a49\u513f\u6108\u770b\u6108\u5947\uff0c\u7ade\u4e00\u65f6\u5446\u4e86\uff0c\u6697\u60f3\uff1a\u201c\u83ab\u975e\u8fd9\u4e9b\u73cd\u5b9d\u662f\u4ece \u81ea\u5df2\u5bb6\u4e2d\u76d7\u51fa\u6765\u7684\uff1f\u201d \u3000\u3000\u201c\u5443\uff01\u201d\u795e\u7334\u5374\u5728\u4e00\u8fb9\u53eb\u9053\uff1a\u201c\u5a49\u513f\uff0c\u4ee5\u6211\u8001\u4eba\u5bb6\u4e4b\u610f\uff0c\u7528\u4e0d\u7740\u518d\u4e1c\u8dd1\u897f\u8dd1\uff0c\u5c31\u5728\u8fd9\u77f3 \u5ba4\u4e2d\u7531\u6211\u4f20\u4f60\u6b66\u529f\u597d\u4e86\uff01\u201d \u3000\u3000\u5a49\u513f\u7531\u6c89\u601d\u4e2d\u88ab\u795e\u7334\u5524\u9192\uff0c\u76b1\u4e86\u76b1\u7709\u9053\uff1a\u201c\u8ddf\u8fd9\u4e24\u4e2a\u6b7b\u4eba\u5904\u5728\u4e00\u8d77\u5417\uff1f\u6211\u4e0d\u8981\uff01\u201d \u3000\u3000\u795e\u7334\u9053\uff1a\u90a3\u6709\u4ec0\u4e48\u5173\u7cfb\uff1f\u4f60\u8981\u8ba8\u538c\u4ed6\u4eec\uff0c\u628a\u4ed6\u4eec\u4e22\u51fa\u53bb\u5582\u9e70\uff0c\u4e5f\u5c31\u662f\u4e86\uff01\u201d \u3000\u3000\u5a49\u513f\u53cc\u7709\u76b1\u5f97\u66f4\u7d27\u4e86\uff0c\u5979\u9053\uff1a\u201c\u8981\u4e22\u4f60\u53bb\u4e22\uff0c\u6211\u53ef\u4e0d\u7ba1\uff01\u201d \u3000\u3000\u795e\u7334\u82e6\u7b11\u4e00\u58f0\u9053\uff1a\u201c\u6211\u82e5\u81ea\u4f1a\u884c\u52a8\uff0c\u90a3\u8fd8\u9700\u8981\u4f60\u80cc\u8d1f\u5417\u2026\u2026\u201d \u3000\u3000\u5a49\u513f\u63a5\u53e3\u9053\uff1a\u201c\u90a3\u5c31\u4e0d\u8981\u591a\u8d39\u5507\u820c\u4e86\uff0c\u8bf4\u4ec0\u4e48\u6211\u4e5f\u4e0d\u4f1a\u53bb\u6cbb\u4ed6\u4eec\u4e00\u4e0b\u7684\uff0c\u54b1\u4eec\u8fd8\u662f\u8d70 \u5427\uff0c\u53e6\u5916\u627e\u5730\u65b9\u53bb\uff01\u201d \u3000\u3000\u8bf4\u7f62\u628a\u624b\u4e2d\u7684\u73e0\u5b9d\u53c8\u4e22\u8fdb\u7bb1\u5185\uff0c\u80cc\u8d77\u795e\u7334\u51fa\u4e86\u77f3\u5ba4\u3002 \u3000\u3000\u6ca1\u60f3\u5230\u624d\u8d70\u4e0d\u8fdc\uff0c\u7ade\u8d70\u51fa\u77f3\u6d1e\u6765\uff0c\u539f\u6765\u8fd9\u77f3\u6d1e\u4e24\u9762\u5c71\u58c1\u4e0a\u5747\u6709\u6d1e\u53e3\uff0c\u800c\u4e14\u8fd9\u77f3\u5ba4\u79bb\u4e8c\u4eba \u51fa\u53e3\u4e4b\u5904\u5f88\u8fd1\uff0c\u5a49\u513f\uff0c\u80cc\u7740\u795e\u7334\uff0c\u8d70\u51fa\u6d1e\u5916\uff0c\u7b97\u662f\u7531\u4e00\u5ea7\u5c71\u8179\u4e2d\u7a7f\u4e86\u51fa\u6765\uff0c\u4e4d\u7531\u9ed1\u6697\u6050\u6016\u72b9 \u5982\u5730\u72f1\u7684\u77f3\u6d1e\u4e4b\u4e2d\uff0c\u89c1\u5230\u6d1e\u5916\u6e5b\u6e5b\u7684\u84dd\u5929\uff0c\u5149\u8f89\u7684\u9633\u5149\uff0c\u4e0d\u7531\u5fc3\u5185\u4e00\u7545\uff0c\u539f\u6765\u4e8c\u4eba\u5728\u6d1e\u5185\u6478 \u7d22\u4e86\u7ea6\u6709\u4e24\u4e2a\u65f6\u8fb0\uff0c\u6b64\u65f6\u5929\u8272\u5df2\u7ecf\u5927\u4eae\u4e86\u3002 \u3000\u3000\u795e\u7334\u4e0e\u8463\u5343\u91cc\u82e6\u6218\u4e09\u5929\u4e09\u591c\uff0c\u5148\u5934\u4ed7\u7740\u6b66\u529f\u9ad8\u5f3a\uff0c\u8fd8\u4e0d\u611f\u5230\u600e\u4e48\u6837\uff0c\u5230\u7b2c\u4e09\u5929\u534a\u591c\u4e0e\u8463 \u5343\u91cc\u4e92\u62fc\u5185\u529b\u53d7\u4f24\uff0c\u53c8\u88ab\u5c55\u5a49\u513f\u80cc\u8d1f\u7740\u5954\u8dd1\u4e86\u534a\u591c\uff0c\u65e9\u5df2\u56f0\u987f\u4e0d\u582a\uff0c\u867d\u4ed7\u7740\u670d\u4e0b\u4ed6\u72ec\u95e8\u79d8\u836f \u201c\u7eed\u547d\u6563\u201d\uff0c\u4f46\u5df2\u662f\u53c8\u997f\u53c8\u6e34\uff0c\u4e00\u51fa\u6d1e\u53e3\uff0c\u770b\u89c1\u5c71\u6da7\u91cc\u6709\u4e00\u9053\u6d41\u6cc9\uff0c\u5728\u5a49\u513f\u80cc\u4e0a\u5fcd\u4e0d\u4f4f\u53eb \u9053\uff1a\u201c\u6c34\uff01\u6c34\uff01\u6211\u8001\u4eba\u5bb6\u8981\u559d\u70b9\u6c34\uff01\u201d \u3000\u3000\u5176\u5b9e\u5a49\u513f\u4e5f\u662f\u53c8\u6e34\u53c8\u997f\uff0c\u80cc\u7740\u795e\u7334\u8d70\u8fd1\u6eaa\u8fb9\uff0c\u4e8c\u4eba\u7acb\u523b\u4f0f\u6eaa\u72c2\u996e\u8d77\u6765\u2026\u2026 \u3000\u3000\u5ffd\u542c\u4e00\u4e2a\u6e05\u6717\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u6e34\u6b7b\u83ab\u996e\u5c71\u6eaa\u6c34\uff01\u4f60\u4e8c\u4eba\u80c6\u6562\u6df1\u5165\u2018\u4ea1\u9b42\u8c37\u2019\uff0c\u96be\u9053\u8fde\u8fd9 \u53e5\u8bdd\u90fd\u4e0d\u77e5\u9053\u5417\uff1f\u201d \u3000\u3000\u5a49\u513f\u4e0e\u795e\u7334\u540c\u65f6\u5403\u4e86\u4e00\u60ca\uff0c\u4ece\u6c34\u8fb9\u59cb\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u5c0f\u6eaa\u5bf9\u9762\u5c71\u5761\u4e0a\uff0c\u7ad9\u5b9a\u4e00\u4e2a\u4fca\u7f8e\u5c11 \u5e74\u3002 \u3000\u3000\u53ea\u89c1\u8fd9\u5c11\u5e74\u4e5f\u5c31\u52d8\u697c\u5219\u51fa\u5934\u6210\u5f97\u957f\u8eab\u7389\u7acb\uff0c\u5251\u7709\u661f\u76ee\uff0c\u8eab\u4e0a\u867d\u7136\u7a7f\u4e00\u88ad\u5e03\u8863\uff0c\u6709\u5982\u6a35\u519c \u6253\u626e\uff0c\u4f46\u8d1f\u624b\u7acb\u5728\u5c0f\u5c71\u5761\u4e0a\uff0c\u6cbf\u7740\u521d\u5347\u7684\u671d\u9633\uff0c\u7adf\u5982\u7389\u6811\u4e34\u98ce\uff0c\u522b\u6709\u4e00\u756a\u6734\u5b9e\u6e05\u65b0\u4e4b\u6c14\u5ea6\u3002 \u3000\u3000\u795e\u7334\u4e00\u77aa\u773c\u9053\uff1a\u201c\u4f60\u8fd9\u5a03\u5a03\uff01\u53ef\u4e0d\u8981\u4fe1\u53e3\u96cc\u9ec4\uff0c\u5982\u679c\u610f\u5b58\u6050\u5413\uff0c\u53ef\u522b\u8bf4\u6211\u8001\u4eba\u5bb6\u8981\u4f60\u6b7b \u65e0\u846c\u8eab\u4e4b\u5730\uff01\u201d \u3000\u3000\u5a49\u513f\u4e00\u542c\u795e\u7334\u53e3\u51fa\u5927\u8a00\uff0c\u4e0d\u7531\u6697\u81ea\u597d\u7b11\uff0c\u89c9\u5f97\u8fd9\u8001\u4eba\u5bb6\u5b9e\u5728\u9a84\u50b2\u5f97\u53ef\u7b11\uff0c\u81ea\u5df1\u4e00\u8eab\u529f\u884c \u52a8\u90fd\u4e0d\u80fd\u884c\u52a8\uff0c\u8fd8\u89c1\u4e86\u4eba\u4fbf\u8bf4\u5927\u8bdd\uff0c\u8981\u4eba\u5bb6\u6b7b\u65e0\u846c\u8eab\u4e4b\u5730\u5462\uff01\u770b\u90a3\u5c11\u5e74\u53cc\u76ee\u795e\u5149\u6e5b\u6e5b\uff0c\u5206\u660e \u6709\u4e0d\u5f31\u7684\u6b66\u529f\uff0c\u771f\u8981\u8ddf\u4ed6\u6253\uff0c\u4eba\u5bb6\u4e0d\u7528\u52a8\u624b\uff0c\u53ea\u8981\u968f\u4fbf\u8e22\u4ed6\u4e00\u811a\uff0c\u4fbf\u591f\u4ed6\u6eda\u534a\u5929\u7684\u4e86\u2026\u2026 \u3000\u3000\u5a49\u513f\u60f3\u7740\u597d\u7b11\uff0c\u4e0d\u7531\u4e00\u5f20\u5634\uff0c\u628a\u542b\u5728\u5634\u4e2d\u7684\u4e00\u53e3\u6c34\u53c8\u55b7\u51fa\u6765\uff0c\u6b62\u4e0d\u4f4f\u683c\u683c\u5a07\u7b11\u2026\u2026 \u3000\u3000\u90a3\u9752\u5e74\u4e4d\u89c1\u5a49\u513f\u7edd\u7f8e\u5bb9\u989c\uff0c\u51e0\u7591\u8eab\u901a\u4ed9\u4eba\uff0c\u80ce\u4e0a\u6d41\u9732\u8fc7\u4e00\u4e1d\u94a6\u7fa1\u795e\u8272\uff0c\u53c8\u89c1\u5a49\u513f\u55b7\u6c34\u800c \u7b11\uff0c\u4e0d\u7531\u5947\u9053\uff1a\u201c\u4e8c\u4f4d\u4e0d\u8981\u4e0d\u4fe1\uff0c\u770b\u4e86\u90a3\u8fb9\u5c31\u77e5\u9053\u4e86\uff01\u201d \u3000\u3000\u8bf4\u7740\u968f\u624b\u5411\u6eaa\u6c34\u4e0a\u6e38\u4e00\u6307\u3002 \u3000\u3000\u5a49\u513f\u4e0e\u4f38\u7334\u968f\u7740\u5c11\u5e74\u624b\u6307\u65b9\u5411\u671b\u53bb\uff0c\u53ea\u89c1\u6eaa\u6c34\u4e0a\u6e38\u6c34\u6e90\u4e4b\u5904\uff0c\u7acb\u7740\u4e00\u5757\u767d\u6728\u724c\u5b50\uff0c\u6728\u724c \u4e0a\u5199\u9053\uff1a\u6b64\u6eaa\u6c34\u542b\u6709\u5267\u6bd2\uff0c\u4e03\u6b65\u65ad\u9b42\uff0c\u5343\u4e07\u83ab\u996e\uff01\u201d \u3000\u3000\u5a49\u513f\u4e00\u770b\uff0c\u52b3\u5bb9\u954a\u53d8\uff0c\u60ca\u547c\u9053\uff1a\u54ce\u5440\uff0c\u6211\u5df2\u7ecf\u559d\u4e0b\u5f88\u591a\u4e86\uff0c\u8fd9\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u795e\u7334\u5374\u4e0d\u52a8\u58f0\u8272\u5730\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u5a49\u513f\uff0c\u7528\u4e0d\u7740\u614c\u5f20\uff0c\u4f60\u5c82\u4e0d\u77e5\u4ed6\u662f\u9a97\u6211\u4eec\uff1f\u201d \u3000\u3000\u5e03\u8863\u5c11\u5e74\u8bf4\u9053\uff1a\u201c\u5c0f\u751f\u4ece\u4e0d\u9a97\u4eba\uff0c\u6211\u8bf4\u6b64\u8bdd\u5b8c\u5168\u51fa\u81ea\u4e00\u7247\u597d\u610f\uff0c\u4e8c\u4f4d\u4e0d\u4fe1\u5c31\u7b97\u4e86\uff01\u201d \u3000\u3000\u795e\u7334\u7b11\u5bf9\u5a49\u513f\u9053\uff1a\u201c\u5982\u4f55\uff01\u6211\u8001\u4eba\u5bb6\u8bf4\u5f97\u4e0d\u9519\u5427\uff1f\u6211\u4e00\u731c\u4fbf\u77e5\u4ed6\u662f\u865a\u8a00\u5413\u4eba\uff0c\u5982\u662f\u8fd9\u6eaa \u6c34\u4e2d\u771f\u6709\u5267\u6bd2\uff0c\u6211\u4eec\u559d\u4e0b\u90a3\u4e48\u591a\u53bb\uff0c\u8179\u5185\u4f1a\u6beb\u65e0\u5f02\u6837\u611f\u89c9\uff1f\u201d \u3000\u3000\u5a49\u513f\u4e00\u542c\u8ba4\u4e3a\u4e5f\u5bf9\uff0c\u81ea\u5df1\u559d\u4e0b\u5f88\u591a\uff0c\u8179\u5185\u6beb\u65e0\u4e0d\u9002\uff0c\u5982\u82e5\u771f\u6709\u5267\u6bd2\uff0c\u600e\u4f1a\u5982\u6b64\u8f7b\u677e\uff1f\u4e0d \u7531\u6697\u6697\u4f69\u670d\u795e\u7334\u94c1\u51cc\uff0c\u5230\u5e95\u6bd4\u81ea\u5df1\u7ecf\u9a8c\u4e30\u5bcc\uff0c\u4e0d\u4f1a\u8f7b\u6613\u53d7\u9a97\u2026\u2026 \u3000\u3000\u8c01\u77e5\u90a3\u5c11\u5e74\u53c8\u9053\uff1a\u201c\u8fd9\u6eaa\u6c34\u6240\u542b\u7684\u5267\u6bd2\uff0c\u5f88\u662f\u7279\u522b\uff0c\u65e0\u58f0\u65e0\u5965\u65e0\u75db\u695a\uff0c\u4e0d\u8bba\u4eba\u755c\uff0c\u53ea\u8981 \u8bef\u996e\u6b64\u6c34\uff0c\u5982\u4e0d\u79fb\u52a8\uff0c\u4e00\u65e0\u611f\u89c9\uff0c\u4f46\u4f60\u8981\u7ad9\u8d77\u60f3\u8d70\uff0c\u4e0d\u51fa\u4e03\u6b65\uff0c\u5fc5\u81f4\u809d\u80a0\u5bf8\u65ad\u800c\u6b7b\uff01\u2026\u2026\u201d \u3000\u3000\u795e\u7334\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u60f3\u5f53\u5e74\u66f9\u5b50\u5efa\u4e03\u6b65\u6210\u8bd7\uff0c\u5982\u4eca\u6211\u8001\u4eba\u5bb6\u80fd\u6765\u4e2a\u4e03\u6b65\u65ad\u80a0\uff0c\u5c82\u4e0d\u662f\u53ef \u4e0e\u53e4\u4eba\u6bd4\u7f8e\uff0c\u53ea\u53ef\u60dc\u6211\u8001\u4eba\u6ca1\u6709\u817f\uff0c\u5982\u679c\u6211\u6709\u817f\uff0c\u8fd8\u771f\u60f3\u8d70\u4e03\u6b65\u8bd5\u8bd5\uff0c\u770b\u662f\u4e0d\u662f\u771f\u80fd\u4e03\u6b65\u65ad \u80a0\uff1f\u201d \u3000\u3000\u5a49\u513f\u9053\uff1a\u4f60\u6ca1\u6709\u817f\u6211\u6709\u817f\uff0c\u4e0d\u8981\u8bf4\u4e03\u6b65\uff0c\u4e03\u5341\u6b65\u4e03\u767e\u6b65\uff0c\u54b1\u4eec\u4e5f\u5f97\u8d70\uff0c\u6c34\u4e5f\u559d\u9971\u4e86\uff0c\u5c11 \u7f57\u55e6\uff0c\u8fd8\u662f\u8d76\u5feb\u53bb\u529e\u54b1\u4eec\u7684\u6b63\u7ecf\u4e8b\u53bb\u5427\uff01\u201d \u3000\u3000\u8bf4\u7f62\uff0c\u80cc\u8d77\u795e\u7334\u4fbf\u60f3\u79bb\u5f00\u2026\u2026 \u3000\u3000\u90a3\u5e03\u8863\u5c11\u5e74\u5374\u8eab\u5f62\u4e00\u63a0\uff0c\u5982\u4e00\u53ea\u63a0\u6ce2\u7d2b\u71d5\u4e00\u822c\uff0c\u4e09\u4e08\u4f59\u5bbd\u7684\u6c34\u9762\uff0c\u4ed6\u7ade\u4e00\u63a0\u800c\u8fc7\uff0c\u98d8\u7136 \u843d\u5728\u5a49\u513f\u8eab\u524d\u9053\uff1a\u59d1\u5a18\uff0c\u8fd8\u662f\u4e0d\u8981\u901e\u5f3a\uff0c\u5c0f\u751f\u53e5\u53e5\u5b9e\u8a00\uff0c\u7edd\u4e0d\u6b3a\u9a97\u4e8c\u4f4d\uff0c\u6b64\u6eaa\u540d\u4e3a\u2018\u6c34\u94f6\u5bd2 \u6cc9\u2019\uff0c\u522b\u770b\u521d\u996e\u4e0b\u4e4b\u9645\uff0c\u4e00\u65e0\u6240\u89c9\uff0c\u7684\u95f4\u4e00\u4e45\uff0c\u5fc5\u7136\u65ad\u80a0\u800c\u6b7b\uff0c\u5c31\u662f\u4e0d\u8d70\u52a8\uff0c\u4e5f\u4f1a\u6b7b\u3002\u56e0\u4e3a \u6eaa\u6c34\u4e2d\u542b\u6709\u6c34\u94f6\uff0c\u6c34\u94f6\u6781\u91cd\uff0c\u8db3\u53ef\u65ad\u80a0\u88c2\u8179\uff0c\u8bf4\u4e03\u6b65\u65ad\u80a0\u662f\u5938\u5927\u5176\u8bcd\uff0c\u4f46\u4eba\u4e00\u8d70\u52a8\uff0c\u4f7f\u6c34\u94f6 \u4e0b\u5760\u52a0\u901f\uff0c\u4fc3\u4f7f\u4f24\u52bf\u52a0\u5feb\u53d1\u4f5c\uff0c\u5c1a\u5e78\u5c0f\u751f\u8eab\u4e0a\u5e26\u6709\u89e3\u836f\uff0c\u656c\u8d60\u4e8c\u503c\u6bcf\u4eba\u4e00\u5305\u2026\u2026\u201d \u3000\u3000\u5c1a\u672a\u7b49\u5a49\u513f\u7b54\u8a00\uff0c\u795e\u7334\u65e9\u5df2\u4e0d\u8010\u70e6\u6325\u624b\u9053\uff1a\u201c\u6eda\u5f00\uff01\u5c11\u518d\u7f57\u55e6\uff01\u4e0d\u8981\u6c34\u4e2d\u65e0\u6bd2\uff0c\u6211\u4eec\u518d \u4e2d\u4e86\u4f60\u836f\u4e2d\u4e4b\u6bd2\uff0c\u90a3\u624d\u5212\u4e0d\u6765\u5462\uff01\u2026\u2026\u201d \u3000\u3000\u8bf4\u7f62\u4e00\u76f4\u50ac\u5a49\u513f\u5feb\u8d70\u3002 \u3000\u3000\u5a49\u513f\u867d\u7136\u770b\u8fd9\u5c11\u5e74\u4e0d\u4f3c\u574f\u4eba\uff0c\u4f46\u7d20\u6627\u5e73\u751f\uff0c\u968f\u4fbf\u62ff\u836f\u6765\u7ed9\u5979\u9662\uff0c\u5979\u4e5f\u4e0d\u6562\u8f7b\u6613\u63a5\u53d7\uff0c\u56e0 \u6b64\uff0c\u89c1\u90a3\u5c11\u5e74\u4ece\u6000\u4e2d\u53d6\u51fa\u4e24\u5305\u836f\u6765\uff0c\u5e76\u4e0d\u7528\u624b\u53bb\u63a5\uff0c\u5373\u9053\uff1a\u8c22\u8c22\u4f60\u7684\u597d\u610f\uff01\u6211\u4eec\u5fc3\u9886\u4e86\uff01\u201d \u3000\u3000\u8bf4\u7f62\u817e\u8eab\u4fbf\u8d70\u3002 \u3000\u3000\u8c01\u77e5\u5979\u8eab\u5f62\u817e\u8d77\uff0c\u7a81\u611f\u8179\u5185\u4e00\u6c89\uff0c\u4e00\u9635\u5de8\u75db\uff0c\u7adf\u4f7f\u5979\u4ece\u534a\u7a7a\u4e2d\u76f4\u683d\u4e0b\u6765\uff0c\u597d\u5728\u5979\u5bb6\u4f20 \u201c\u8e22\u7a7a\u5e7b\u5f71\u201d\u8eab\u6cd5\uff0c\u786e\u6709\u72ec\u5230\u4e4b\u5904\uff0c\u867d\u7136\u4e8b\u8d77\u4ed3\u731d\uff0c\u4ecd\u80fd\u53ca\u65f6\u53e0\u8170\u4ef0\u8eab\uff0c\u53cc\u811a\u843d\u4e8e\u5730\u9762\uff0c\u53ef \u662f\u4e5f\u8ddf\u8dea\u6570\u6b65\uff0c\u9669\u4e9b\u683d\u5012\u3002 \u3000\u3000\u5a49\u513f\u7389\u5bb9\u60e8\u53d8\uff0c\u8179\u5185\u53c8\u662f\u51e0\u4e0b\u75db\u5982\u5200\u6512\uff0c\u4e0d\u7981\u9891\u76b1\u86fe\u7709\uff0c\u60ca\u547c\u9053\uff1a\u54ce\u5438\uff01\u8001\u524d\u8f88\uff0c\u6211\u4eec \u771f\u4e2d\u6bd2\u4e86\uff01\u2026\u3002\u201d \u3000\u3000\u6b64\u65f6\uff0c\u795e\u7334\u56e0\u529f\u529b\u5c3d\u5931\uff0c\u63d0\u4e0d\u4f4f\u771f\u6c14\uff0c\u5728\u5a49\u513f\u817e\u8eab\u800c\u8d77\u3001\u7a81\u7136\u683d\u4e0b\u3001\u811a\u6b65\u8ddf\u8dea\u4e4b\u9645\uff0c\u53d7 \u4e86\u9707\u52a8\uff0c\u8179\u5185\u75db\u5982\u5200\u6405\uff0c\u773c\u524d\u4e00\u9ed1\uff0c\u65e9\u5df3\u75db\u660f\u8fc7\u53bb\uff0c\u662f\u4ee5\u5e76\u672a\u80fd\u56de\u7b54\u5a49\u513f\u7684\u8bf4\u8bdd\u2026\u2026 \u3000\u3000\u201c\u59d1\u5a18\uff01\u201d\u5e03\u8863\u7f8e\u5c11\u5e74\u6025\u7a9c\u4e0a\u524d\uff0c\u5fd9\u9053\uff1a\u201c\u73b0\u5728\u53ef\u77e5\u5c0f\u751f\u6240\u8a00\u975e\u865a\uff0c\u8fd8\u662f\u628a\u8fd9\u89e3\u836f\u773c\u4e0b \u5427\uff01\u201d \u3000\u3000\u5a49\u513f\u6b64\u65f6\u8179\u5185\u75db\u5f97\u5389\u5bb3\uff0c\u518d\u4e5f\uff0c\u63a5\u8fc7\u6765\u541e\u5728\u53e3\u4e2d\uff0c\u53ea\u611f\u6ee1\u53e3\u6e05\u51c9\uff0c\u5165\u53e3\u5373\u968f\u6d25\u6db2\u800c\u4e0b\u3002 \u3000\u3000\u679c\u7136\u8179\u5185\u75db\u75bc\u7acb\u6b62\uff0c\u4e0d\u7531\u7528\u65e2\u60ca\u5f02\u53c8\u611f\u6fc0\u7684\u773c\u5149\u671b\u4e86\u5e03\u8863\u5c11\u5e74\u4e00\u773c\u3002 \u3000\u3000\u5e03\u8863\u5c11\u5e74\u4ece\u5a49\u513f\u76ee\u5149\u4e2d\u611f\u5230\u6781\u5927\u7684\u5b89\u6170\uff0c\u4f3c\u662f\u89c9\u5f97\u53ea\u51ed\u5a49\u513f\u770b\u4ed6\u4e00\u773c\uff0c\u4fbf\u53d7\u591a\u5c11\u59d4\u5c48\u4e5f \u503c\u5f97\u4e86\uff0c\u56e0\u6b64\uff0c\u53c8\u4ece\u6000\u5185\u638f\u51fa\u4e00\u5305\u89e3\u836f\u6765\uff0c\u4ea4\u7ed9\u5a49\u513f\u9053\uff1a\u201c\u59d1\u5a18\u80cc\u8d1f\u7684\u8001\u4eba\u5bb6\uff0c\u5df2\u7ecf\u75db\u660f\u8fc7 \u53bb\u4e86\uff0c\u8bf7\u59d1\u5a18\u8d76\u5feb\u628a\u89e3\u836f\u7ed9\u4ed6\u8001\u4eba\u5bb6\u670d\u4e0b\u53bb\u5427\uff0c\u8fdf\u5219\u6050\u6015\u4e0d\u53ca\uff01\u201d \u3000\u3000\u5a49\u513f\u6b64\u65f6\u5df2\u76f8\u4fe1\u5e03\u8863\u5c11\u5e74\u5386\u8a00\u4e0d\u5047\uff0c\u5f53\u5373\u628a\u89e3\u836f\u63a5\u8fc7\uff0c\u53cd\u8eab\u53c8\u628a\u795e\u7334\u653e\u4e0b\u5730\u6765\uff0c\u64ac\u5f00\u4ed6 \u7684\u7259\u5173\uff0c\u5c06\u89e3\u836f\u5012\u8fdb\u795e\u7334\u53e3\u4e2d\u3002 \u3000\u3000\u4e0d\u8fc7\u7247\u523b\uff0c\u795e\u7334\u7741\u773c\u5927\u53eb\u9053\uff1a\u75db\u715e\u6211\u8001\u4eba\u5bb6\u4e86\uff01\u2026\u2026\u201d \u3000\u3000\u201c\u8c22\u8c22\u4f60\u5566\uff01\u201d\u5a49\u513f\u89c1\u795e\u7334\u4e5f\u83b7\u6551\uff0c\u4e0d\u7531\u5bf9\u5e03\u8863\u5c11\u5e74\u611f\u6fc0\u5730\u9053\uff1a\u201c\u8fd8\u6ca1\u6709\u8bf7\u6559\u516c\u5b50\u5927 \u540d\uff0c\u65b9\u624d\u6211\u8bf4\u8bdd\u5f97\u7f6a\u5904\u5c1a\u8bf7\u516c\u5b50\u539f\u8c05\uff01 \u3000\u3000\u201c\u59d1\u5a18\u5fd2\u8c26\u4e86\uff01\u5e03\u8863\u5c11\u5e74\u9053\uff1a\u5c0f\u751f\u53f8\u7a7a\u5e38\u9756\uff01\u2026\u2026\u201d \u3000\u3000\u4e00\u542c\u5e03\u8863\u7f8e\u5c11\u5e74\u81ea\u62a5\u59d3\u540d\uff0c\u5a49\u513f\u7acb\u523b\u8138\u73b0\u60ca\u5bb9\u9053\uff1a\u201c\u4f60\u5c31\u662f\u7aef\u65b9\u516c\u5b50\uff1f\u6c5f\u6e56\u4e0a\u76db\u4f20\u7684\u6b66 \u6797\u56db\u516c\u5b50\u4e4b\u4e00\u2018\u98d8\u96f6\u7aef\u65b9\u2019\u5c31\u662f\u4f60\uff1f\u201d \u3000\u3000\u5e03\u8863\u7f8e\u5c11\u5e74\u4e00\u7b11\u9053\uff1a\u6b63\u662f\u5c0f\u751f\uff01\u4e0d\u8fc7\u9519\u88ab\u6c5f\u6e56\u4e0a\u7684\u670b\u53cb\u62ac\u7231\u800c\u5df1\uff0c\u5c0f\u751f\u4e0e\u53e6\u4e09\u516c\u5b50\u5e76 \u8bba\uff01\u201d \u3000\u3000\u5a49\u513f\u4e0d\u7531\u53cc\u773c\u53d1\u76f4\uff0c\u671b\u7740\u5f53\u9762\u5e03\u8863\u7f8e\u5c11\u5e74\uff0c\u5fc3\u4e2d\u611f\u6982\u4e07\u5343\u3002 \u3000\u3000\u4ed6\u867d\u662f\u4e00\u8eab\u5e03\u8863\uff0c\u4f46\u63a9\u9970\u4e0d\u4f4f\u4ed6\u534e\u7f8e\u7684\u672c\u8d28\uff0c\u66f4\u663e\u5f97\u501c\u50a5\u4e0d\u7fa4\u3002\u4ed6\u867d\u662f\u8c26\u865a\u5bb9\u8ba9\uff0c\u4f46\u6beb \u65e0\u754f\u7f29\u602f\u61e6\u4e4b\u611f\uff0c\u5374\u66f4\u663e\u793a\u4e86\u4ed6\u7684\u96cd\u5bb9\u5927\u91cf\u3002\u4fca\u7f8e\u65e0\u94f8\uff0c\u597d\u50cf\u5929\u5730\u95f4\u7684\u949f\u7075\u79c0\u6c14\uff0c\u8ba9\u4ed6\u4e00\u4e2a \u4eba\u5168\u5305\u5a49\u513f\u4e0d\u7531\u82b3\u5fc3\u7a81\u7a81\u4e71\u8df3\uff0c\u5fc3\u4e2d\u6697\u6697\u559d\u91c7\uff1a\u5929\u4e0b\u771f\u6709\u8fd9\u6837\u4fca\u7f8e\u7684\u7537\u4eba\uff01\u2026\u2026\u201d \u3000\u3000\u4f46\u5979\u7a81\u60f3\u8d77\u81ea\u5df1\u949f\u60c5\u5c55\u54e5\u54e5\u5728\u5148\uff0c\u600e\u80fd\u53c8\u89c1\u5f02\u601d\u8fc1\uff1f\u4e0d\u7531\u8138\u5b54\u4e00\u7ea2\uff0c\u7f13\u7f13\u907f\u5f00\u773c\u5149\uff0c\u4f4e \u4e0b\u5934\u53bb\u2026\u2026 \u3000\u3000\u5a49\u513f\u82b3\u5fc3\u4e2d\u60ca\u5f02\u201c\u7aef\u65b9\u516c\u5b50\u201d\u7684\u7eaf\u7f8e\uff0c\u201c\u7aef\u65b9\u516c\u5b50\u201d\u5fc3\u4e2d\u53c8\u4f55\u5c1d\u4e0d\u4e3a\u5a49\u513f\u7684\u7edd\u4e16\u98ce\u59ff\u795e \u9b42\u98a0\u5012\u5462\uff1f \u3000\u3000\u4ed6\u89c1\u5a49\u513f\u6e05\u65b0\u5982\u51fa\u6c34\u8299\u84c9\uff0c\u4fca\u7f8e\u5982\u5929\u7a7a\u6697\u6708\uff0c\u7c89\u9762\u6731\u5507\uff0c\u7f8e\u8273\u800c\u4e0d\u5eb8\u4fd7\uff0c\u7709\u5982\u8fdc\u5c71\u66f4 \u79c0\uff0c\u773c\u6bd4\u6625\u6c34\u66f4\u6e05\uff0c\u79c0\u53d1\u5982\u4e91\uff0c\u5a07\u8eaf\u5982\u67f3\uff0c\u80a4\u767d\u8d5b\u96ea\uff0c\u808c\u5982\u58c1\u7389\u3002 \u3000\u3000\u5c24\u5176\u96be\u5f97\u7684\u662f\u5979\u90a3\u4f18\u7f8e\u7684\u6c14\u8d28\uff0c\u90a3\u662f\u5c5e\u4e8e\u7075\u6027\u65b9\u9762\u7684\uff0c\u7709\u68a2\u773c\u89d2\uff0c\u4e00\u77ac\u4e00\u77a5\uff0c\u65e0\u4e0d\u663e\u51fa \u5170\u5fc3\u60e0\u8d28\uff0c\u7389\u4f53\u5a07\u8eaf\uff0c\u4e3e\u624b\u6295\u8db3\uff0c\u65e0\u4e0d\u663e\u51fa\u4e3d\u8d28\u5929\u751f\uff0c\u5b8c\u5168\u5408\u4e4e\u5929\u7136\u97f5\u5f8b\uff0c\u8fd9\u662f\u4e00\u822c\u5eb8\u4fd7\u8102 \u7c89\u5b66\u4e5f\u5b66\u4e0d\u6765\u7684\uff0c\u4eff\u6548\u4e5f\u4eff\u6548\u4e0d\u5230\u7684\u3002 \u3000\u3000\u201c\u7aef\u65b9\u516c\u5b50\u201d\u53f8\u7a7a\u5e38\u9756\uff0c\u81ea\u8ba4\u4e4e\u751f\u89c1\u8fc7\u4f73\u4e3d\u65e0\u6570\uff0c\u4f46\u4ece\u672a\u6709\u89c1\u8fc7\u50cf\u5a49\u513f\u8fd9\u822c\u5341\u5168\u5341\u7f8e\u7684 \u7f8e\u4eba\u3002 \u3000\u3000\u5c24\u5176\u4ed6\u89c1\u5a49\u513f\u4e00\u53cc\u660e\u7741\u7d27\u76ef\u7740\u4ed6\u77a7\uff0c\u4e00\u4f1a\u513f\u79cb\u6ce2\u6d41\u8f6c\uff0c\u4e00\u4f1a\u51e0\u6655\u751f\u53cc\u988a\uff0c\u6843\u82b1\u592a\u5929\uff0c\u6ca1 \u6709\u5979\u8fd9\u822c\u9ad8\u8d35\uff0c\u6885\u82b1\u53c8\u592a\u51b7\uff0c\u6ca1\u6709\u5979\u8fd9\u822c\u591a\u60c5\u2026\u2026\u4e0d\u7531\u770b\u5f97\u5446\u4e86\uff0c\u5fc3\u4e2d\u66f4\u662f\u7eee\u601d\u4e3d\u5ff5\u63a9\u6620\u4e07 \u5343\u2026\u2026 \u3000\u3000\u4e8c\u4eba\u4e92\u76f8\u5f20\u671b\uff0c\u5fc3\u4e2d\u4e92\u76f8\u751f\u51fa\u60c5\u612b\uff0c\u867d\u7136\u65f6\u95f4\u6781\u77ed\uff0c\u4f46\u4e5f\u7792\u4e0d\u8fc7\u795e\u7334\u90a3\u8001\u6c5f\u6e56\u7684\u773c\u5149\uff0c \u4ed6\u751f\u6027\u602a\u50fb\uff0c\u5bf9\u8fd9\u79cd\u513f\u5973\u60c5\u957f\u7684\u4e8b\uff0c\u6700\u4e3a\u770b\u4e0d\u60ef\uff0c\u4e00\u89c1\u4e8c\u4eba\u53cc\u53cc\u53d1\u6014\uff0c\u773c\u4e2d\u6d41\u9732\u51fa\u4e92\u76f8\u94a6\u7fa1 \u795e\u8272\uff0c\u4e0d\u7531\u4e8e\u54b3\u4e00\u58f0\u9053\uff1a\u201c\u5a49\u513f\uff01\u5411\u4eba\u5bb6\u9053\u8c22\u8fc7\u4e86\uff0c\u54b1\u4eec\u4e5f\u8be5\u8d70\u5566\uff01\u201d \u3000\u3000\u5a49\u513f\u8138\u5b54\u4e00\u7ea2\uff0c\u4f46\u5979\u5c1a\u672a\u53d1\u8a00\uff0c\u201c\u7aef\u65b9\u516c\u5b50\u201d\u53c8\u9053\uff1a\u201c\u5c0f\u751f\u5df2\u77e5\u59d1\u5a18\u82b3\u540d\uff0c\u4f46\u4e0d\u77e5\u59d1\u5a18 \u4ed9\u4e61\u4f55\u5904\uff0c\u4e0e\u8fd9\u4f4d\u8001\u4eba\u5bb6\u53c8\u662f\u600e\u4e48\u79f0\u547c\uff1f \u3000\u3000\u4e0d\u77e5\u80fd\u89c1\u544a\u5426\uff1f\u201d \u3000\u3000\u5a49\u513f\u5c1a\u672a\u7b54\u8a00\uff0c\u795e\u7334\u5374\u53cc\u773c\u4e00\u77aa\uff0c\u62a2\u5148\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u5b50\uff0c\u522b\u4ee5\u4e3a\u7565\u65bd\u5c0f\u60e0\uff0c\u4fbf\u60f3\u8d2a\u56fe \u4ec0\u4e48\uff0c\u5982\u518d\u9976\u820c\uff0c\u522b\u8bf4\u6211\u8001\u4eba\u5bb6\u7ffb\u8138\u4e0d\u8ba4\u4eba\u3002\u201d \u3000\u3000\u5a49\u513f\u72e0\u72e0\u77aa\u4e86\u795e\u7334\u4e00\u773c\uff0c\u9053\uff1a\u201c\u4f60\u8fd9\u4e48\u51f6\u5e72\u4ec0\u4e48\uff1f\u4eba\u5bb6\u7aef\u65b9\u516c\u5b50\u6551\u4e86\u6211\u4fe9\u4e00\u547d\uff0c\u4e0d\u8981\u8bf4 \u4eba\u5bb6\u516c\u5b50\u8fd8\u5f88\u5ba2\u6c14\uff0c\u5c31\u662f\u5bf9\u6211\u4eec\u4e0d\u5ba2\u6c14\uff0c\u6211\u4eec\u4e5f\u4e0d\u80fd\u6069\u5c06\u4ec7\u62a5\uff01\u201d \u3000\u3000\u8bf4\u7f62\uff0c\u53c8\u8f6c\u5bf9\u7aef\u65b9\u516c\u5b50\u9053\uff1a\u201c\u6211\u8ddf\u8fd9\u4f4d\u8001\u524d\u8f88\u6beb\u65e0\u5173\u8fde\uff0c\u53ea\u662f\u673a\u7f18\u51d1\u5de7\u78b0\u5728\u4e00\u8d77\u7f62\u4e86\u3002 \u6211\u59d3\u5c55\uff0c\u5bb6\u4f4f\u5728\u6d4e\u5357\u2018\u8c79\u7a81\u5c71\u5e84\u2019\uff0c\u516c\u5b50\u6709\u6687\uff0c\u5e0c\u671b\u5230\u6211\u5bb6\u53bb\u73a9\u4e24\u5929\u2026\u2026\u201d \u3000\u3000\u5a49\u513f\u8fd9\u4e00\u62a5\u5bb6\u4e16\uff0c\u7aef\u65b9\u516c\u5b50\u4e5f\u81ea\u4e00\u60ca\uff0c\u63a5\u53e3\u9053\uff1a\u201c\u59d1\u5a18\u4e0e\u6458\u661f\u624b\u6155\u5bb9\u5e84\u4e3b\u662f\u4eb2\u621a\u5427\uff1f\u201d \u3000\u3000\u5a49\u513f\u7b11\u9053\uff1a\u201c\u90a3\u662f\u5bb6\u7236\uff01\u201d \u3000\u3000\u7aef\u65b9\u516c\u5b50\u4e00\u695e\u9053\uff1a\u201c\u90a3\u2014\u2014\u600e\u4e48\u59d1\u5a18\u59d3\u5c55\u5462\uff1f\u2026\u2026\u201d \u3000\u3000\u795e\u7334\u5927\u4e3a\u4e0d\u8010\uff0c\u66b4\u8df3\u8d77\u6765\uff0c\u53eb\u9053\uff1a\u201c\u5c0f\u5a03\u513f\uff01\u4f60\u4eec\u8fd8\u6709\u6ca1\u6709\u5b8c\uff01 \u3000\u3000\u518d\u8981\u5676\u5676\uff0c\u6211\u8001\u4eba\u5bb6\u53ef\u771f\u8981\u4e0d\u5ba2\u6c14\u4e86\uff01\u2026\u2026\u201d \u3000\u3000\u4ed6\u8fd9\u6837\u5927\u58f0\u53f1\u559d\uff0c\u5a49\u513f\u9887\u4e0d\u9ad8\u5174\uff0c\u6b63\u60f3\u53d1\u4f5c\u90a3\u7aef\u65b9\u516c\u5b50\u5374\u6de1\u7136\u4e00\u7b11\u9053\uff1a\u201c\u770b\u8fd9\u4f4d\u8001\u524d\u8f88 \u7709\u5fc3\u73b0\u51fa\u6666\u6c14\uff0c\u53cc\u76ee\u6df7\u4e71\u65e0\u795e\uff0c\u4e0d\u4f46\u8d1f\u4e86\u6781\u91cd\u7684\u5185\u4f24\u800c\u4e14\u79bb\u6b7b\u4ea6\u4e0d\u8fdc\uff0c\u6ca1\u60f3\u5230\u8fd8\u6709\u8fd9\u4e48\u5927\u7684 \u706b\u6c14\uff1f\u2026\u2026\u201d \u3000\u3000\u8fd9\u51e0\u53e5\u8bdd\u867d\u662f\u8f7b\u63cf\u6de1\u5199\uff0c\u5374\u628a\u813e\u6c14\u66b4\u8e81\u7684\u795e\u7334\u6c14\u5f97\u70b8\u4e86\u80ba\uff0c\u53ea\u89c1\u4ed6\u6b8b\u7709\u5012\u575a\uff0c\u53cc\u670d\u4e00 \u77aa\uff0c\u559d\u9053\uff1a\u201c\u7b97\u4f60\u5c0f\u5b50\u773c\u5149\u5389\u5bb3\uff01\u4f46\u6211\u8001\u4eba\u5bb6\u771f\u529b\u6e83\u6563\uff0c\u4e00\u6837\u53ef\u4ee5\u8981\u4f60\u7684\u72d7\u547d\uff01\u63a5\u62db\uff01\u2018\u6cdb \u4e39\u5357\u6d77\uff01\u201d \u3000\u3000\u4e00\u542c\u795e\u7334\u53eb\u201c\u63a5\u62db\u201d\uff01\u4e0d\u4f46\u7aef\u65b9\u516c\u5b50\u4e00\u6014\uff0c\u8fde\u5a49\u513f\u4e5f\u4e00\u6014\uff0c\u6697\u60f3\uff1a\u8fd9\u602a\u8001\u5934\u529f\u529b\u5df2\u5931\uff0c \u8fd8\u600e\u4e48\u51fa\u62db\u8ddf\u4eba\u5bb6\u6253\uff1f\u2026\uff0e\uff0e\uff0e\u201d\u518d\u4e00\u770b\u795e\u733f\u53eb\u51fa\u90a3\u62db\u2018\u6cdb\u821f\u5357\u6d77\u2019\uff0c\u534a\u622a\u65e0\u817f\u7684\u8eab\u5f62\u4ecd\u7136 \u5728\u5730\u4e0a\uff0c\u4e00\u52a8\u672a\u52a8\uff0c\u4e0d\u7531\u66f4\u611f\u5947\u602a\uff0c\u795e\u733f\u5374\u5750\u5728\u5730\u4e0b\u8bf4\u9053\uff1a\u201c\u6211\u8001\u4eba\u5bb6\u8fd9\u62db\u2018\u6cdb\u821f\u5357\u6d77\u2019\u65bd \u5c55\u65f6\uff0c\u53cc\u62f3\u865a\u63e1\uff0c\u731b\u88ad\u4f60\u5c0f\u5b50\u5de6\u53f3\u8033\u6839\u2018\u85cf\u8840\u2019\u91cd\u7a74\uff0c\u865a\u4e2d\u6709\u5b9e\uff0c\u5b9e\u4e2d\u6709\u865a\uff0c\u4f60\u5c0f\u4e8e\u82e5\u662f\u4e0d \u8eb2\uff0c\u85cf\u8840\u7a74\u4e3a\u4eba\u8eab\u6b7b\u7a74\uff0c\u7528\u4e0d\u5230\u5b9e\uff0c\u53ea\u8981\u88ab\u6211\u8001\u4eba\u5bb6\u62f3\u98ce\u7a0d\u4e3a\u626b\u4e2d\uff0c\u7acb\u53ef\u6bd9\u547d\uff01\u4f60\u5c0f\u5b50\u82e5\u5bb6 \u624d\u51fa\u7684\u53cc\u62f3\u4e0d\u6536\uff0c\u53ea\u53cc\u8098\u5411\u524d\u4e00\u7ffb\uff0c\u6b63\u8fce\u7740\u4f60\u5c0f\u5b50\u9003\u8def\uff0c\u5411\u5de6\u662f\u2018\u5fc3\u4fde\u7a74\u2019\uff0c\u649e\u4e0a\u6211\u8001\u4eba\u5bb6 \u53f3\u8098\u5c16\uff0c\u5411\u53f3\u662f\u2018\u80be\u95e8\u2019\uff0c\u641e\u4e0a\u6211\u8001\u4eba\u5bb6\u7684\u5de6\u8098\u5c16\uff0c\u4e0d\u7ba1\u4f60\u5c0f\u5b50\u5411\u5de6\u5411\u53f3\uff0c\u90fd\u662f\u6b7b\u8def\u4e00\u6761\uff01 \u518d\u5047\u5982\u4f60\u5c0f\u5b50\u82e5\u662f\u81ea\u5fd6\u80fd\u8ddf\u6211\u8001\u4eba\u5bb6\u82e6\u4fee\u4e00\u4e2a\u7532\u5b50\u4ee5\u4e0a\u7684\u2018\u771f\u5143\u7f62\u6c14\u2019\u76f8\u6297\uff0c\u786c\u6562\u4ee5\u201c\u62e8\u4e91 \u89c1\u65e5\u2019\u62db\u5f0f\u786c\u5c01\u786c\u67b6\uff0c\u6211\u8001\u4eba\u5bb6\u53cc\u62f3\u540e\u6536\uff0c\u53cc\u8098\u5916\u5f20\uff0c\u5185\u542b\u2018\u5438\u2019\u5b87\u8bc0\uff0c\u4f60\u5c0f\u5b50\u53cc\u81c2\u843d\u7a7a\uff0c \u8eab\u5f62\u524d\u8d8b\uff0c\u6211\u8001\u4eba\u5bb6\u53cc\u62f3\u518d\u5411\u5916\u4e00\u653e\uff0c\u6b63\u597d\u662f\u6cdb\u821f\u5212\u6d46\u4e4b\u52bf\uff0c\u53cc\u62f3\u7ed3\u7ed3\u5b9e\u5b9e\u5730\u6253\u5728\u4f60\u524d\u80f8\u8981 \u5bb3\uff0c\u4f60\u5c0f\u5b50\u60f3\u60f3\u770b\uff0c\u4f60\u80fd\u6709\u51e0\u6761\u72d7\u547d\uff0c\u4e0d\u6b7b\u5728\u6211\u8001\u4eba\u5bb6\u8fd9\u4e00\u62db\u201c\u6cdb\u821f\u5357\u6d77\u2019\u4e4b\u4e0b\uff1f\u201d \u3000\u3000\u5a49\u513f\u4e0e\u7aef\u65b9\u516c\u5b50\u8fd9\u624d\u7b97\u662f\u660e\u767d\uff0c\u539f\u6765\u8fd9\u602a\u8001\u4eba\u662f\u7528\u53e3\u8ff0\u5411\u7aef\u65b9\u516c\u5b50\u653b\u51fa\u4e86\u8fd9\u4e00\u7edd\u62db\u3002 \u3000\u3000\u867d\u7136\u602a\u4eba\u2014\u2014\u795e\u7334\u5185\u529f\u771f\u529b\u5df2\u5931\uff0c\u53ea\u662f\u5750\u5728\u5730\u4e0b\u53e3\u8ff0\uff0c\u5e76\u672a\u5f53\u771f\u4e0a\u524d\u52a8\u624b\uff0c\u4f46\u662f\u5a49\u513f\u4e0e \u7aef\u65b9\u516c\u5b50\u4e5f\u6697\u6697\u5fc3\u60ca\uff0c\u56e0\u4e3a\u602a\u8001\u4eba\u53e3\u8ff0\u7684\u8fd9\u4e00\u5927\u6740\u62db\u201c\u6cdb\u821f\u5357\u6d77\u201d\uff0c\u8fd8\u771f\u662f\u4f7f\u4eba\u67b6\u65e0\u53ef\u67b6\uff0c \u8eb2\u65e0\u53ef\u8eb2\u3002 \u3000\u3000\u5c24\u5176\u7aef\u65b9\u516c\u5b50\u542c\u5f97\u51b7\u6c57\u76f4\u6d41\uff0c\u81ea\u5df1\u7d22\u4ee5\u4e3a\u6b66\u529f\u4e0d\u9519\uff0c\u4f46\u771f\u8981\u9047\u5230\u602a\u8001\u4eba\u8fd9\u4e00\u62db\uff0c\u81ea\u5df1\u8fd8 \u771f\u662f\u53ea\u6709\u6b7b\u8def\u4e00\u6761\u3002 \u3000\u3000\u604d\u60da\u4e2d\uff0c\u771f\u50cf\u662f\u6709\u4e00\u654c\u4eba\u594b\u8d77\u53cc\u62f3\u5411\u81ea\u5df1\u8fce\u9762\u6251\u6765\uff0c\u6765\u52bf\u65e2\u731b\uff0c\u62db\u4e2d\u66f4\u5957\u62db\uff0c\u6b32\u8eb2\u4e0d \u80fd\uff0c\u6b32\u67b6\u65e0\u529b\u2026\u2026\u7a81\u7136\u51fa\u4e86\u4e00\u5934\u51b7\u6c57\uff0c\u5fc3\u4e2d\u609a\u7136\u800c\u60ca\uff0c\u60f6\u6050\u8a00\u9053\uff1a\u4e00\u62db\uff0c\u5c0f\u751f\u4fbf\u4e07\u65e0\u5e78 \u7406\u2026\u2026\u201d \u3000\u3000\u5a49\u513f\u5374\u7a81\u7136\u7075\u673a\u4e00\u52a8\uff0c\u63d2\u5634\u53eb\u9053\uff1a\u201c\u5e76\u4e0d\u662f\u6ca1\u6709\u529e\u6cd5\u8eb2\uff0c\u53ea\u8981\u5411\u4e0a\u4e00\u8dc3\u5c31\u53ef\u4ee5\u8eb2\u8fc7\uff0c\u800c \u4e14\u8fd8\u53ef\u4ee5\u7528\u2026\u2026\u201d \u3000\u3000\u795e\u7334\u7b11\u9053\uff1a\u201c\u4e0d\u7528\u2018\u800c\u4e14\u2019\u4e86\uff0c\u4f60\u95ee\u95ee\u4ed6\u5411\u4e0a\u4e00\u8dc3\uff0c\u80fd\u5426\u8eb2\u8fc7\u8d39\u8001\u4eba\u5bb6\u8fd9\u62db\u2018\u6cdb\u821f\u5357 \u6d77\u2019\uff1f\u201d \u3000\u3000\u7aef\u65b9\u516c\u5b50\u8bda\u6073\u6447\u5934\uff0c\u7b54\u9053\uff1a\u201c\u5411\u4e0a\u8dc3\uff0c\u5411\u4e0b\u8eba\uff0c\u90fd\u4e0d\u80fd\u8eb2\u8fc7\uff01\u8001\u524d\u8f88\u8fd9\u4e00\u62db\uff0c\u582a\u79f0\u4e00 \u7edd\uff01\u5c0f\u751f\u5e78\u5f97\u4e0d\u6b7b\uff0c\u6df1\u611f\u624b\u4e0b\u7559\u60c5\uff0c\u544a\u8f9e\u4e86\uff01\u201d \u3000\u3000\u8bf4\u7f62\uff0c\u4e00\u62b1\u62f3\u54e9\u5730\u4e00\u58f0\uff0c\u98de\u8eab\u63a0\u8fc7\u5c0f\u6eaa\uff0c\u53ea\u4e24\u4e09\u4e2a\u8d77\u843d\uff0c\u5df2\u9690\u6ca1\u4e8e\u4e71\u5c71\u4e1b\u4e2d\uff01\u2026\u2026 \u3000\u3000\u5a49\u513f\u6014\u6014\u5730\u671b\u7740\u7aef\u65b9\u516c\u5b50\u8d70\u5f97\u6ca1\u4e86\u8e2a\u5f71\uff0c\u8fd9\u624d\u7792\u8d77\u5c0f\u5634\uff0c\u8f6c\u5934\u5bf9\u795e\u7334\u9053\uff1a\u8001\u524d\u8f88\uff0c\u4f60\u771f \u662f\u4e86\u4e0d\u8d77\uff0c\u53ea\u7528\u4e00\u5f20\u5634\uff0c\u5c31\u628a\u9f0e\u9f0e\u5927\u540d\u7684\u2018\u7aef\u65b9\u516c\u5b50\u2019\u6253\u8dd1\u4e86\uff01\u2026\u2026\u201d \u3000\u3000\u795e\u7334\u9887\u4e3a\u81ea\u5f97\u5730\u9053\uff1a\u201c\u5a49\u513f\uff0c\u4f60\u4e0d\u7528\u4e0d\u4fe1\uff0c\u4f60\u53ea\u8981\u628a\u6211\u8001\u4eba\u5bb6\u53e3\u8ff0\u7684\u6b66\u529f\uff0c\u4fbf\u53ef\u4ee5\u7765\u7768 \u5929\u4e0b\u4e86\uff01\u201d \u3000\u3000\u201c\u54fc\uff01\u201d\u5a49\u513f\u5c0f\u5634\u4e00\u6487\uff0c\u6ee1\u8138\u4e0d\u5c51\u5730\u9053\uff1a\u201c\u80fd\u7ec3\u5230\u5341\u6210\uff0c\u50cf\u4f60\u4e00\u6837\uff0c\u8fd8\u4e0d\u662f\u6253\u4e0d\u8fc7\u4e00\u4e2a \u9a91\u9a74\u7684\u7cdf\u8001\u5934\uff01\u201d \u3000\u3000\u5c31\u8fd9\u4e00\u53e5\u8bdd\u53ea\u628a\u795e\u7334\u6c14\u5f97\u54c7\u54c7\u602a\u53eb\u9053\uff1a\u201c\u5973\u5a03\u513f\uff0c\u4f60\u4e0d\u7528\u6fc0\u6211\u8001\u4eba\u5bb6\uff01\u6211\u8001\u4eba\u5bb6\u76f4\u5230\u73b0 \u5728\u8ddf\u90a3\u9a91\u9a74\u8001\u9b3c\u4ea6\u662f\u4e2a\u4e0d\u80dc\u4e0d\u8d25\u4e4b\u5c40\uff01\u800c\u4e14\uff0c\u800c\u4e14\uff0c\u6211\u8001\u4eba\u5bb6\u8ddf\u90a3\u9a91\u9a74\u8001\u9b3c\u8fd8\u8981\u518d\u6bd4\u6597\u4e0b \u53bb\uff0c\u6211\u8001\u4eba\u5bb6\u6709\u628a\u63e1\u53ef\u4ee5\u8d62\u4ed6\uff01\u4e00\uff0e\u3002\uff0e\u201d\u201c\u7b97\u4e86\u5427\uff01\u201d\u5a49\u513f\u4ecd\u662f\u6ee1\u8138\u4e0d\u5c51\u4e4b\u8272\u9053\uff1a\u201c\u6211\u82e5 \u662f\u4e0d\u8ddf\u4f60\u5b66\u827a\u5462\uff1f\u518d\u8005\u8bf4\uff0c\u6211\u82e5\u662f\u8ddf\u4f60\u5b66\u4e86\u6b66\u529f\uff0c\u4e0d\u8ddf\u6211\u90a3\u5c55\u54e5\u54e5\u6bd4\u8bd5\u5462\uff1f\u4f60\u600e\u4e48\u8d62\u53c8\u600e\u4e48 \u80dc\uff1f\u201d \u3000\u3000\u795e\u7334\u4e00\u5446\uff0c\u6ee1\u8138\u5931\u671b\u5730\u9053\uff1a\u201c\u4f60\u4e0d\u4f1a\u8bf4\u4e86\u4e0d\u7b97\u8bdd\u5427\uff1f\u7b54\u5e94\u4e86\u6211\u8001\u4eba\u5bb6\uff0c\u53c8\u4e34\u65f6\u53cd\u6094\uff01\u201d \u3000\u3000\u5a49\u513f\u5657\u54e7\u4e00\u7b11\u9053\uff1a\u201c\u4f60\u522b\u81ea\u8bf4\u81ea\u8bdd\u4e86\uff0c\u6211\u4ec0\u4e48\u65f6\u5019\u7b54\u5e94\u8fc7\u4f60\uff1f \u3000\u3000\u4ece\u59cb\u81f3\u7ec8\uff0c\u53ea\u662f\u4f60\u4e00\u4e2a\u4eba\u5728\u5531\u72ec\u811a\u620f\u2026\u2026\u201d \u3000\u3000\u795e\u7334\u8138\u8272\u5927\u53d8\uff0c\u6025\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u4f60\u662f\u4e0d\u8ddf\u6211\u8001\u4eba\u5bb6\u5b66\u6b66\u529f\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u6211\u662f\u4e0d\u4f1a\u5b66\u4e86\uff01\u201d \u3000\u3000\u5a49\u513f\u65a9\u94a2\u622a\u94c1\u7b54\u9053\uff1a\u201c\u5c31\u7b97\u4f60\u8ddf\u9a91\u9a74\u7cdf\u8001\u5934\u4e0d\u5206\u80dc\u8d25\uff0c\u6253\u4e86\u4e2a\u5e73\u624b\uff0c\u628a\u4f60\u4eec\u4e8c\u4eba\u5408\u800c\u4e3a \u4e00\uff0c\u4e5f\u4e0d\u80fd\u8bf4\u662f\u7765\u7768\u5929\u4e0b\uff0c\u66f4\u8c08\u4e0d\u5230\u5929\u4e0b\u65e0\u654c\u3002\u2018\u9739\u96f7\u5251\u2019\u5c55\u4e91\u5929\u5c55\u5927\u4fa0\uff0c\u90a3\u624d\u662f\u53e4\u4eca\u7b2c\u4e00 \u9ad8\u624b\uff0c\u56e0\u4e3a\u4eba\u5bb6\u6ca1\u5403\u8d25\u4ed7\uff0c\u800c\u4e14\uff0c\u8fd8\u80dc\u8fc7\u4f60\u2014\u2014\u5251\u2026\u2026\u201d \u795e\u7334\u7a81\u7136\u5927\u53eb\u4e00\u58f0\uff1a\u201c\u6c14\u6b7b\u6211\u4e5f\uff01\u201d\u53e3\u55b7\u9c9c\u8840\uff0c\u4ef0\u8eab\u540e\u5012\u2026\u2026 \u7b2c\u4e8c\u5341\u516b\u7ae0\u3000\u201c\u9739\u9707\u5251\u201d\u6b7b\u4e8e\u8c01\u624b>> \u53e4\u9f99\u300a\u5251\u5ba2\u884c\u300b \u7b2c\u4e8c\u5341\u516b\u7ae0\u3000\u201c\u9739\u9707\u5251\u201d\u6b7b\u4e8e\u8c01\u624b \u3000\u3000\u5c55\u767d\u968f\u7740\u201c\u795e\u9a74\u94c1\u80c6\u201d\u81f3\u4e00\u9690\u79d8\u77f3\u6d1e\u4e2d\uff0c\u8fd9\u4f4d\u524d\u8f88\u5f02\u4fa0\u628a\u4e00\u5957\u5f97\u81ea\u897f\u57df\u7684\u201c\u96f7\u97f3\u4f5b\u638c\u201d \u4f20\u7ed9\u4e86\u5c55\u767d\uff0c\u5e76\u5c06\u5c55\u767d\u4e4b\u7236\u201c\u9739\u96f3\u5251\u201d\u5c55\u4e91\u5929\u88ab\u5bb3\u7ecf\u8fc7\u544a\u8bc9\u4e86\u4ed6\u3002 \u3000\u3000\u539f\u6765\u5c55\u767d\u7684\u7236\u4eb2\u201c\u9739\u96f7\u5251\u201d\u5c55\u4e91\u5929\uff0c\u4e0d\u4f46\u6b66\u529f\u79bb\u5f3a\uff0c\u800c\u4e14\u5fc3\u80f8\u5149\u660e\uff0c\u884c\u4e3a\u78ca\u843d\uff0c\u51ed\u638c\u4e2d \u4e00\u67c4\u201c\u65e0\u60c5\u78a7\u5251\u201d\uff0c\u884c\u4fa0\u4ed7\u4e49\uff0c\u5929\u4e0b\u65e0\u654c\u3002 \u3000\u3000\u7531\u4e8e\u4ed6\u516c\u6b63\u65e0\u79c1\uff0c\u5728\u6c5f\u6e56\u4e0a\u4fa0\u540d\u5353\u8457\uff0c\u65e0\u8bba\u9ed1\u767d\u4e24\u9053\u7684\u6b66\u6797\u4eba\u7269\uff0c\u5bf9\u5176\u5747\u751a\u656c\u4f69\u3002\u53ef\u662f \u7531\u4e8e\u4ed6\u6025\u516c\u597d\u4e49\uff0c\u5b88\u6b63\u4e0d\u963f\uff0c\u56fa\u7136\u6551\u52a9\u8fc7\u4e0d\u5c11\u4eba\uff0c\u4ea4\u4e86\u4e0d\u5c11\u81f3\u4ea4\u597d\u53cb\uff0c\u4f46\u4ed6\u4e5f\u5f97\u7f6a\u4e86\u4e0d\u5c11\u7a77 \u51f6\u5927\u6076\uff0c\u7ed3\u4e86\u4e0d\u5c11\u4ec7\u5bb6\u3002 \u3000\u3000\u51fa\u4e4e\u610f\u5916\u7684\u662f\u5c55\u4e91\u5929\u5e76\u6ca1\u6709\u6b7b\u5728\u4ec7\u4eba\u4e4b\u624b\uff0c\u800c\u662f\u6b7b\u4e8e\u516d\u4f4d\u4e49\u7ed3\u91d1\u5170\u7684\u76df\u5f1f\u4e4b\u6697\u7b97\u3002 \u3000\u3000\u5c55\u4e91\u5929\u539f\u4e0e\u201c\u6458\u661f\u624b\u201d\u6155\u5bb9\u6db5\u3001\u201c\u4e7e\u5764\u638c\u201d\u4e91\u5b97\u9f99\u3001\u201c\u9752\u86a8\u795e\u201d\u91d1\u4e5d\u3001\u201c\u6df7\u5143\u6307\u201d\u53f8\u7a7a \u664b\u3001\u201c\u9738\u738b\u97ad\u201d\u6a0a\u975e\uff0c\u4ee5\u53ca\u201c\u94f6\u6247\u5b50\u201d\u67f3\u5d07\u539a\u7b49\u4e03\u4eba\uff0c\u5408\u79f0\u4e3a\u201c\u6c5f\u5357\u4e03\u4fa0\u201d\uff0c\u4ee5\u5c55\u4e91\u5929\u4e3a \u9996\uff0c\u5728\u5f53\u65f6\u6c5f\u6e56\u9053\u4e0a\u4e43\u662f\u5a01\u540d\u663e\u8d6b\u7684\u4e03\u5f1f\u5144\u3002 \u3000\u3000\u4f46\u662f\u5c55\u4e91\u5929\u4fa0\u809d\u4e49\u80c6\uff0c\u6240\u4f5c\u6240\u4e3a\u5747\u662f\u53ea\u89c1\u4e00\u4e49\uff0c\u4e0d\u89c1\u751f\u6b7b\u3002 \u3000\u3000\u5176\u4f59\u516d\u4eba\u5374\u5404\u6709\u81ea\u79c1\u7684\u6253\u7b97\uff0c\u5e38\u5e38\u968f\u7740\u5c55\u4e91\u5929\u820d\u6b7b\u5fd8\u751f\u7684\u594b\u6597\u4e00\u573a\uff0c\u5230\u5934\u6765\u5374\u4e00\u70b9\u597d\u5904 \u4e5f\u5f97\u4e0d\u5230\uff0c\u5fc3\u4e2d\u6697\u6697\u4e0d\u6ee1\u3002 \u3000\u3000\u53c8\u52a0\u4e0a\u5c55\u4e91\u5929\u827a\u9ad8\u6c14\u50b2\uff0c\u96be\u514d\u6709\u4e9b\u72ec\u65ad\u4e13\u884c\uff0c\u51e1\u4e8b\u53ea\u95ee\u5408\u4e0d\u5408\u6b66\u6797\u9053\u4e49\uff0c\u5b8c\u5168\u4e0d\u987e\u516d\u4f4d \u76df\u5f1f\u5fc3\u4e2d\u6240\u60f3\uff0c\u56e0\u6b64\uff0c\u8fd9\u516d\u4eba\u5bf9\u4ed6\u6108\u6765\u6108\u611f\u4e0d\u5fff\uff0c\u53ea\u4e0d\u8fc7\u754f\u60e7\u5c55\u4e91\u5929\u6b66\u529f\u9ad8\u5f3a\uff0c\u4e0d\u6562\u516c\u7136\u53cd \u6297\uff0c\u53c8\u52a0\u4e0a\u5c55\u4e91\u5929\u6240\u4f5c\u6240\u4e3a\u786e\u662f\u5927\u4e49\u6240\u5728\uff0c\u4ed6\u4eec\u516d\u4eba\u81ea\u79c1\u81ea\u5229\u7684\u60f3\u6cd5\uff0c\u4e5f\u4e0d\u6562\u516c\u5f00\u8bf4\u51fa\u6765\uff0c \u800c\u4e14\u201c\u6c5f\u5357\u4e03\u4fa0\u201d\u7684\u540d\u671b\uff0c\u5728\u6c5f\u6e56\u4e0a\u6108\u6765\u6108\u5927\uff0c\u4ed6\u4eec\u4e5f\u4e0d\u597d\u610f\u601d\u4e0e\u4e03\u4fa0\u4e4b\u9996\u7684\u5927\u54e5\u95f9\u7ffb\u3002 \u3000\u3000\u4e5f\u662f\u5408\u8be5\u6709\u5408\u8be5\u6709\u4e8b\u201c\u6458\u661f\u624b\u201d\u6155\u5bb9\u6db5\u65e0\u610f\u4e2d\u5728\u54c0\u7262\u5c71\u7edd\u9876\u79d8\u7a9f\u4e2d\uff0c\u5f97\u5230\u4e00\u5e45\u85cf\u73cd\u56fe\uff0c \u6309\u56fe\u7d22\u9aa5\uff0c\u5f97\u77e5\u6d1e\u5ead\u6e56\u6c34\u5e95\u6c89\u57cb\u4e86\u4e00\u5b97\u5343\u5e74\u5b9d\u85cf\uff0c\u5f97\u5230\u8fd9\u6279\u5b9d\u85cf\uff0c\u7acb\u53ef\u81f4\u654c\u56fd\u4e4b\u5bcc\u3002\u6155\u5bb9\u6db5 \u5fc3\u4e2d\u5927\u559c\uff0c\u6697\u60f3\u81ea\u5df1\u95ef\u8361\u6c5f\u6e56\u534a\u751f\uff0c\u4ecd\u662f\u4e24\u624b\u7a7a\u7a7a\uff0c\u5982\u679c\u80fd\u53d6\u51fa\u8fd9\u7b14\u5b9d\u85cf\uff0c\u636e\u4e3a\u5df2\u6709\uff0c\u90a3\u540e \u534a\u4e16\u4fbf\u53ef\u4e30\u8863\u8db3\u98df\uff0c\u4e0d\u5fc5\u518d\u5728\u6c5f\u6e56\u4e0a\u5192\u98ce\u9669\u4e86\u3002 \u3000\u3000\u53ef\u662f\uff0c\u7b49\u5230\u6155\u5bb9\u6db5\u8d76\u81f3\u6d1e\u5ead\u6e56\u7554\uff0c\u5df2\u53d1\u73b0\u4e0d\u5c11\u624e\u773c\u4eba\u7269\u5728\u6e56\u8fb9\u9017\u7559\uff0c\u6155\u5bb9\u6db5\u5148\u4e0d\u53bb\u52d8\u5bdf \u5b9d\u85cf\uff0c\u9690\u5728\u6697\u4e2d\u4e00\u63a2\uff0c\u624d\u77e5\u9053\u6d1e\u5ead\u6e56\u5e95\u5b9d\u85cf\u5df2\u8d70\u6f0f\u4e86\u98ce\u58f0\uff0c\u4e0d\u5c11\u6b66\u6797\u9ad8\u624b\u5747\u8d76\u6765\u6d1e\u5ead\u6e56\u5bfb \u5b9d\uff0c\u800c\u4e14\u4f20\u8a00\u5343\u5e74\u5b9d\u85cf\u4e2d\uff0c\u9664\u4e86\u4ef7\u503c\u8fde\u57ce\u7684\u73e0\u5b9d\u4e4b\u5916\uff0c\u5c1a\u6709\u4e00\u518c\u300a\u6b66\u5b66\u771f\u7ecf\u300b\uff0c\u4e00\u65b9\u201c\u907f\u6c34 \u7389\u58c1\u201d\uff0c\u53ca\u4e09\u7c92\u201c\u5927\u7f57\u91d1\u4e39\u201d\u4e09\u5b97\u5f02\u5b9d\uff01 \u3000\u3000\u201c\u6458\u661f\u624b\u201d\u6155\u5bb9\u6db5\u63a2\u77e5\u8fd9\u4e9b\u6d88\u606f\uff0c\u5fc3\u4e2d\u53c8\u60ca\u53c8\u559c\uff0c\u559c\u7684\u662f\u5b9d\u85cf\u79d8\u56fe\u5728\u81ea\u5df2\u624b\u4e2d\uff0c\u60ca\u7684\u662f \u5b9d\u85cf\u6d88\u606f\u4e0d\u77e5\u5982\u4f55\u6cc4\u6f0f\u3002\u773c\u89c1\u6d1e\u5ead\u6e56\u7554\u9ad8\u624b\u5982\u4e91\uff0c\u4ec5\u51ed\u81ea\u5df1\u4e00\u4eba\u7684\u529b\u91cf\uff0c\u5df2\u65e0\u6cd5\u53d6\u5f97\u5b9d\u85cf\u3002 \u3000\u3000\u800c\u4e14\uff0c\u6700\u91cd\u8981\u7684\u8fd8\u662f\uff0c\u4ed6\u624b\u4e2d\u867d\u6709\u85cf\u5b9d\u79d8\u56fe\uff0c\u5374\u4e0d\u4f1a\u6c34\u529f\uff0c\u65e0\u6cd5\u8fdb\u5f97\u6c34\u5e95\u79d8\u9053\u3002 \u3000\u3000\u56e0\u6b64\uff0c\u6155\u5bb9\u6db5\u65e0\u6cd5\uff0c\u53ea\u6709\u5411\u4e49\u7ed3\u91d1\u5170\u7684\u201c\u6c5f\u5357\u4e03\u4fa0\u201d\u5546\u91cf\u3002 \u3000\u3000\u5f53\u7136\uff0c\u89c1\u5230\u201c\u6c5f\u5357\u4e03\u4fa0\u201d\u4e4b\u4e2d\u7684\u53e6\u516d\u4f4d\uff0c\u4ed6\u53c8\u6539\u4e86\u4e00\u4e2a\u8bf4\u6cd5\uff0c\u7edd\u4e0d\u4f1a\u900f\u9732\u81ea\u5df1\u60f3\u72ec\u541e\u5b9d \u85cf\uff0c\u53ea\u8bf4\u65e0\u610f\u4e2d\u83b7\u5f97\u79d8\u56fe\uff0c\u4e0d\u6562\u81ea\u73cd\uff0c\u613f\u4e0e\u516d\u76df\u5144\u5f1f\u5171\u4eab\u3002 \u3000\u3000\u8c01\u52a0\u201c\u9739\u96f3\u5251\u201d\u5c55\u4e91\u5929\u529b\u4e3b\u5c06\u5b9d\u85cf\u53d6\u51fa\uff0c\u4ee5\u6551\u6e56\u5e7f\u4e00\u5e26\u7684\u96be\u6c11\uff0c\u56e0\u4e3a\u6e56\u5e7f\u8fde\u5e74\u8352\u65e9\uff0c\u5c45 \u6c11\u5df2\u7ecf\u997f\u5f97\u5230\u4e86\u4e92\u98df\u4eba\u8089\u7684\u60b2\u60e8\u5730\u6b65\uff0c\u5b98\u5e9c\u6551\u6d4e\u53c8\u529e\u5f97\u4e0d\u529b\uff0c\u6bcf\u65e5\u5747\u6709\u6210\u5343\u8bba\u4e07\u7684\u4eba\u88ab\u6d3b\u6d3b \u997f\u6b7b\uff0c\u56e0\u6b64\uff0c\u52a8\u4e86\u5c55\u4e91\u5929\u7684\u4fa0\u4e49\u5fc3\u80a0\uff0c\u60f3\u628a\u8fd9\u6279\u5b9d\u85cf\u53d6\u51fa\uff0c\u53d8\u5356\u6362\u7cae\uff0c\u4ee5\u6551\u6d4e\u707e\u6c11\u3002 \u3000\u3000\u6155\u5bb9\u6db5\u4e00\u542c\uff0c\u5fc3\u4e2d\u51c9\u4e86\u534a\u622a\uff0c\u4ee5\u4e3a\u53c8\u548c\u5f80\u5e38\u4e00\u6837\uff0c\u820d\u6b7b\u5fd8\u751f\u53d6\u5f97\u5b9d\u85cf\uff0c\u53c8\u662f\u53ea\u4fbf\u5b9c\u4ed6 \u4eba\uff0c\u81ea\u5df1\u4e00\u70b9\u597d\u5904\u4e5f\u843d\u4e0d\u5230\uff0c\u4f46\u5c1a\u4e0d\u6b7b\u5fc3\uff0c\u4ecd\u6307\u671b\u73e0\u5b9d\u4e0d\u8981\uff0c\u53ea\u53d6\u5176\u4e2d\u4e09\u5b97\u5f02\u5b9d\u2026\u2026 \u3000\u3000\u4f46\u5176\u4ed6\u4e94\u4fa0\uff0c\u5374\u8ba4\u4e3a\u5728\u5929\u4e0b\u6b66\u6797\u9ad8\u624b\u864e\u89c6\u9e70\u803d\u4e4b\u4e0b\uff0c\u4e0b\u6e56\u53d6\u5b9d\uff0c\u65e0\u5f02\u706b\u4e2d\u53d6\u6817\uff0c\u5192\u7740\u5982 \u6b64\u5927\u9669\uff0c\u53d6\u51fa\u5b9d\u85cf\uff0c\u81ea\u5df1\u4e00\u65e0\u597d\u5904\uff0c\u5374\u53bb\u6551\u6d4e\u90a3\u4e9b\u4e0e\u81ea\u5df2\u6beb\u65e0\u5173\u8fde\u7684\u8fdc\u5730\u707e\u6c11\uff0c\u5b9e\u5728\u4e0d\u7518\u60c5 \u613f\uff0c\u56e0\u4e4b\uff0c\u4e00\u9f50\u529d\u8bf4\u5c55\u4e91\u5929\u6253\u6d88\u6b64\u5ff5\u3002 \u3000\u3000\u53ef\u662f\u5c55\u4e91\u5929\u5374\u4e0d\u8003\u8651\u8fd9\u4e9b\uff0c\u4ed6\u8ba4\u4e3a\u53ea\u8981\u5f53\u4e3a\uff0c\u5374\u4e0d\u987e\u4ec0\u4e48\u672c\u8eab\u5229\u5bb3\uff0c\u5e76\u4e3b\u5f20\u628a\u4e09\u5b97\u5f02\u5b9d \u8ba9\u7ed9\u5929\u4e0b\u6b66\u6797\uff0c\u53ea\u4ee5\u5176\u4e2d\u91d1\u94f6\u73e0\u5b9d\u4f5c\u4e3a\u6551\u6d4e\u707e\u6c11\u4e4b\u7528\u3002\u60f3\u5929\u4e0b\u6b66\u6797\u4eba\u7269\uff0c\u53ea\u91cd\u89c6\u90a3\u4e09\u5b97\u5f02 \u5b9d\uff0c\u5fc5\u4e0d\u4ee5\u91d1\u94f6\u73e0\u5b9d\u4e3a\u91cd\uff0c\u6653\u4ee5\u5927\u4e49\uff0c\u4e0d\u4f46\u4e0d\u4f1a\u53d7\u5230\u963b\u6320\uff0c\u800c\u4e14\u8fd8\u53ef\u5f97\u5230\u52a9\u529b\uff0c\u4f7f\u201c\u6c5f\u5357\u4e03 \u4fa0\u201d\u5b8c\u6210\u8fd9\u4e00\u4ef6\u4e49\u4e3e\u3002 \u3000\u3000\u6155\u5bb9\u6db5\u8fd9\u4e00\u542c\uff0c\u5fc3\u4e2d\u66f4\u51c9\u4e86\uff0c\u4e5f\u4e0e\u5176\u4ed6\u4e94\u4fa0\u4e00\u9f50\u4e3b\u5f20\u4e0d\u53bb\u53d6\u5b9d\u3002\u4f46\u5c55\u4e91\u5929\u6240\u51b3\u5b9a\u7684\u4e8b\uff0c \u4ece\u4e0d\u4f1a\u9000\u6b65\uff0c\u4e0d\u7ba1\u516d\u4f4d\u76df\u5f1f\u600e\u6837\u8bf4\uff0c\u4e00\u5b9a\u8981\u53bb\u6e56\u5e95\u53d6\u5b9d\u3002 \u3000\u3000\u7531\u4e8e\u5c55\u4e91\u5929\u4e43\u662f\u4e03\u4fa0\u4e4b\u9996\uff0c\u53c8\u52a0\u4e0a\u4ed6\u7684\u4e2a\u6027\u662f\u8bf4\u4e00\u4e0d\u4e8c\uff0c\u5176\u4f59\u516d\u4eba\u4e5f\u4e0d\u6562\u53cd\u5bf9\u4ed6\uff0c\u968f\u4ed6 \u4e00\u540c\u6765\u5230\u6e56\u8fb9\uff0c\u5bf9\u805a\u96c6\u5728\u6e56\u8fb9\u7684\u6b66\u6797\u4eba\u7269\u4e00\u5ba3\u79f0\uff0c\u679c\u7136\u53d7\u5230\u4e86\u62e5\u62a4\uff0c\u5e76\u7531\u6b66\u5f53\u3001\u5c11\u6797\u3001\u5ce8 \u5d4b\u2026\u2026\u7b49\u6570\u5927\u95e8\u6d3e\u638c\u95e8\u4eba\u8bae\u5b9a\uff0c\u5168\u4f53\u4eba\u5458\u4e00\u81f4\u534f\u52a9\u201c\u6c5f\u5357\u4e03\u4fa0\u201d\u4e0b\u6e56\u53d6\u5b9d\uff0c\u7136\u540e\u4ee5\u91d1\u94f6\u73e0\u5b9d \u53bb\u6551\u6d4e\u6e56\u5e7f\u707e\u6c11\uff0c\u90a3\u300a\u6b66\u5b66\u771f\u7ecf\u300b\u3001\u201c\u907f\u6c34\u7389\u58c1\u201d\u3001\u201c\u5927\u7f57\u91d1\u4e39\u201d\u4e09\u5b97\u5f02\u5b9d\uff0c\u5219\u5728\u541b\u5c71\u9876\u4e0a \u5f00\u4e00\u4e2a\u6b66\u6797\u5927\u4f1a\u4ee5\u516c\u5e73\u7684\u6bd4\u6b66\uff0c\u51b3\u5b9a\u4e09\u5b97\u5f02\u5b9d\u5c5e\u8c01\uff0c\u5c31\u8fde\u201c\u6c5f\u5357\u4e03\u4fa0\u201d\u4e5f\u7b97\u5728\u5185\uff0c\u4ee5\u514d\u4e03\u4fa0 \u5403\u4e8f\u3002 \u3000\u3000\u8fd9\u6837\u4e00\u8bf4\uff0c\u805a\u96c6\u5728\u6d1e\u5ead\u6e56\u8fb9\u7684\u5929\u4e0b\u7fa4\u96c4\uff0c\u5927\u591a\u6570\u5747\u65e0\u5f02\u8bae\uff0c\u8fde\u5fc3\u5df2\u51b7\u4e86\u7684\u6155\u5bb9\u6db5\u53ca\u53e6\u5916 \u4e94\u4fa0\uff0c\u4e5f\u90fd\u91cd\u65b0\u71c3\u8d77\u5e0c\u671b\u4e4b\u706b\u3002\u867d\u7136\u5c55\u4e91\u5929\u4e00\u518d\u8bda\u6073\u8868\u660e\uff0c\u5fd7\u4e0d\u5728\u593a\u5b9d\uff0c\u53ea\u5728\u6551\u4eba\uff0c\u4f46\u53ea\u8981 \u53d6\u51fa\u5b9d\u85cf\uff0c\u5929\u4e0b\u6b66\u6797\u5404\u95e8\u5404\u6d3e\u5fc5\u51fa\u624b\u4e89\u593a\uff0c\u6155\u5bb9\u6db5\u60f3\u7740\u4ee5\u201c\u6c5f\u5357\u4e03\u4fa0\u201d\u7684\u540d\u4e49\u52a0\u4e0a\u4e00\u624b\uff0c\u4efb \u4f55\u4e00\u4eba\u4e5f\u4e07\u65e0\u62d2\u7edd\u4e4b\u7406\u3002\u56e0\u6b64\uff0c\u4e5f\u5f88\u70ed\u5fc3\u5730\u628a\u85cf\u5b9d\u56fe\u53d6\u51fa\u6765\uff0c\u4e0e\u5404\u95e8\u5404\u6d3e\u63a8\u4e3e\u7684\u4ee3\u8868\uff0c\u5171\u540c \u63a2\u52d8\u4e0b\u6e56\u53d6\u5b9d\u4e4b\u8def\u7ebf\u3001\u5730\u70b9\u2026\u2026 \u3000\u3000\u4f46\u4f17\u4eba\u6309\u56fe\u7d22\u9aa4\uff0c\u52d8\u5bdf\u7684\u7ed3\u679c\uff0c\u90a3\u5343\u5e74\u5b9d\u85cf\u5374\u6c89\u57cb\u5728\u6d1e\u5ead\u6e56\u6b63\u4e2d\u5fc3\u7684\u6e56\u5e95\u3002 \u3000\u3000\u201c\u6c5f\u5357\u4e03\u4fa0\u201d\u4e2d\u53ea\u6709\u51fa\u8eab\u5728\u5de2\u6e56\u7554\u7684\u201c\u94f6\u6247\u5b50\u201d\u67f3\u795f\u539a\u7cbe\u901a\u6c34\u91cc\u529f\u592b\uff0c\u4f46\u67f3\u795f\u539a\u6f5c\u4e0b\u6e56 \u5e95\u4e4b\u540e\uff0c\u4e24\u5929\u4e24\u591c\uff0c\u624d\u6d6e\u4e86\u4e0a\u6765\uff0c\u5374\u5df2\u53d7\u4e86\u5185\u4f24\uff0c\u539f\u6765\u6e56\u5fc3\u4e2d\u592e\u6c34\u6df1\u6570\u767e\u4e08\uff0c\u538b\u529b\u6781\u5927\uff0c\u800c \u4e14\u6c34\u5e95\u6697\u6d41\u53c8\u6025\uff0c\u4ee5\u6c34\u529f\u89c1\u957f\u7684\u67f3\u795f\u539a\uff0c\u8fde\u6e56\u5e95\u90fd\u672a\u6e38\u5230\u5c31\u5dee\u4e00\u70b9\u9001\u4e86\u547d\u3002 \u3000\u3000\u4e4b\u540e\uff0c\u5f88\u591a\u8ba4\u4e3a\u6c34\u91cc\u529f\u592b\u4e0d\u9519\u7684\u6b66\u6797\u9ad8\u624b\uff0c\u76f8\u7ee7\u4e0b\u6c34\u4e00\u63a2\uff0c\u90fd\u4e0e\u201c\u94f6\u6247\u5b50\u201d\u67f3\u795f\u539a\u4e00 \u6837\uff0c\u4e0d\u4f46\u8eab\u8d1f\u91cd\u4f24\uff0c\u8fde\u6c34\u5e95\u90fd\u672a\u80fd\u5230\u8fbe\uff0c\u66f4\u4e0d\u8981\u8bf4\u5230\u6c34\u5e95\u53bb\u5bfb\u5b9d\u4e86\u3002 \u3000\u3000\u800c\u4e14\uff0c\u5c1a\u6709\u4e0d\u5c11\u6c34\u529f\u5185\u529b\u8f83\u5dee\uff0c\u5374\u81ea\u4e0d\u91cf\u529b\u7684\u4eba\uff0c\u4e0b\u6c34\u4e4b\u540e\uff0c\u5373\u9001\u4e86\u6027\u547d\u3002\u56e0\u6b64\uff0c\u6c34\u5e95 \u5b9d\u85cf\u65e0\u6cd5\u53d6\u51fa\uff0c\u805a\u96c6\u5728\u6e56\u8fb9\u7684\u6b66\u6797\u9ad8\u624b\uff0c\u60f3\u5c3d\u4e86\u79cd\u79cd\u65b9\u6cd5\uff0c\u53c8\u846c\u9001\u4e86\u4e0d\u5c11\u4eba\u547d\uff0c\u5230\u6b64\u77e5\u9053\u5df2 \u7ecf\u65e0\u671b\uff0c\u624d\u9646\u7eed\u5730\u8d70\u4e86\uff0c\u65f6\u95f4\u4e00\u4e45\uff0c\u805a\u96c6\u5230\u6e56\u8fb9\u5bfb\u5b9d\u7684\u6b66\u6797\u9ad8\u624b\uff0c\u5747\u5df2\u8d70\u6563\u51c0\u5c3d\uff0c\u5c31\u8fde\u201c\u6c5f \u5357\u4e03\u4fa0\u201d\u4e5f\u653e\u5f03\u6253\u635e\u6c89\u5b9d\u5e0c\u671b\uff0c\u79bb\u5f00\u6d1e\u5ead\u6e56\u3002 \u3000\u3000\u4e8b\u60c5\u8fc7\u4e86\u4e94\u3001\u516d\u5e74\uff0c\u4eba\u4eba\u5df2\u6e10\u6e10\u628a\u6d1e\u5ead\u6e56\u5e95\u5b9d\u85cf\u7684\u4e8b\u6de1\u5fd8\u4e86\u3002\u5c55\u4e91\u5929\u5374\u63a2\u542c\u51fa\u4e91\u5357\u9ece\u8d21 \u5c71\u201c\u795e\u7334\u201d\u94c1\u51cc\uff0c\u6536\u85cf\u6709\u4e00\u9897\u201c\u907f\u6c34\u795e\u73e0\u201d\uff0c\u6267\u6b64\u53ef\u4ee5\u5206\u6c34\u4eba\u6d77\uff0c\u8863\u5c65\u4e0d\u6e7f\u3002\u8fd9\u4e00\u53d1\u73b0\u53c8\u89e6 \u52a8\u4e86\u5c55\u4e91\u5929\u7684\u7075\u673a\uff0c\u8ba4\u4e3a\u5982\u501f\u6765\u201c\u907f\u6c34\u795e\u73e0\u201d\u4e00\u7528\uff0c\u4e0d\u96be\u628a\u6d1e\u5ead\u6e56\u5e95\u5343\u5e74\u5b9d\u85cf\u53d6\u51fa\u3002\u4e8e\u662f\u53ea \u8eab\u8d74\u82d7\u7586\uff0c\u5230\u9ece\u8d21\u5c71\u501f\u73e0\uff0c\u548c\u201c\u795e\u7334\u201d\u94c1\u51cc\u82e6\u6218\u4e09\u65e5\u4e09\u591c\uff0c\u624d\u80dc\u4e86\u201c\u795e\u7334\u201d\u4e00\u5251\uff0c\u501f\u5230\u201c\u907f \u6c34\u795e\u73e0\u201d\u3002\u53c8\u5230\u6c5f\u5357\uff0c\u53ca\u516d\u4f4d\u76df\u5f1f\u524d\u53bb\u6e56\u5e95\u53d6\u5b9d\u3002 \u3000\u3000\u6ca1\u60f3\u5230\u5c55\u4e91\u5929\u7ade\u56e0\u6b64\u88ab\u516d\u4f4d\u7ed3\u4e49\u76df\u5f1f\u6697\u7b97\u6740\u6b7b\u3002 \u3000\u3000\u56e0\u4e3a\u201c\u6c5f\u5357\u4e03\u4fa0\u201d\u8fd9\u6b21\u5728\u6d1e\u5ead\u6e56\u5e95\u53d6\u5b9d\uff0c\u662f\u5728\u6781\u7aef\u79d8\u5bc6\u4e4b\u4e0b\u8fdb\u884c\u7684\uff0c\u56e0\u6b64\uff0c\u5c55\u4e91\u5929\u88ab\u516d \u4f4d\u76df\u5f1f\u6740\u6b7b\uff0c\u6c5f\u6e56\u4e0a\u5f88\u5c11\u4eba\u77e5\u9053\u5185\u60c5\u3002 \u3000\u3000\u53c8\u52a0\u4e0a\u6e56\u5e95\u85cf\u5b9d\u4e4b\u4e8b\uff0c\u4e8b\u8fc7\u591a\u5e74\uff0c\u4eba\u4eec\u65e9\u5df2\u5fd8\u8bb0\u3002\u5728\u4ee5\u540e\u4e45\u5e74\u4e2d\uff0c\u201c\u6c5f\u5357\u4e03\u4fa0\u201d\u7eb7\u7eb7\u7ed3 \u5a5a\u751f\u5b50\uff0c\u89c5\u5730\u5b9a\u5c45\uff0c\u5f88\u5c11\u5728\u4e00\u8d77\uff0c\u6545\u800c\u5c55\u4e91\u5929\u4e4b\u6b7b\u6c5f\u6e56\u4e0a\u5747\u4ee5\u4e3a\u662f\u4ec7\u5bb6\u6240\u6740\uff0c\u4efb\u4f55\u4eba\u4e5f\u60f3\u4e0d \u5230\u7adf\u662f\u56e0\u5bfb\u5b9d\u88ab\u516d\u4f4d\u7ed3\u76df\u4e49\u5f1f\u6240\u5bb3\u3002 \u3000\u3000\u4ed7\u7740\u4ece\u6e56\u5e95\u53d6\u51fa\u7684\u65e0\u5c3d\u5b9d\u85cf\u201c\u6458\u661f\u624b\u201d\u6155\u5bb9\u6db5\u3001\u201c\u4e7e\u5764\u638c\u201d\u4e91\u5b97\u9f99\u3001\u201c\u9752\u86a8\u795e\u201d\u91d1\u4e5d\u3001 \u201c\u6df7\u5143\u6307\u201d\u53f8\u7a7a\u664b\uff0c\u6536\u4e70\u5929\u4e0b\u6b66\u6797\u9ad8\u624b\uff0c\u5f00\u521b\u9738\u4e1a\uff0c\u5df2\u6210\u4e3a\u5f53\u4eca\u6b66\u6797\u56db\u5927\u8c6a\u95e8\u3002\u5c31\u662f\u9547\u6c5f \u201c\u9738\u738b\u97ad\u201d\u6a0a\u975e\uff0c\u867d\u7136\u4e0d\u559c\u7f57\u96c6\u95e8\u5ba2\uff0c\u4e5f\u4e0e\u56db\u5927\u8c6a\u95e8\u5206\u5ead\u6297\u672d\uff0c\u52bf\u529b\u4e0d\u5c0f\u3002 \u3000\u3000\u53ea\u6709\u201c\u94f6\u6247\u5b50\u201d\u67f3\u5d07\u539a\uff0c\u5374\u4ea1\u547d\u6d77\u5916\uff0c\u4e0d\u77e5\u6240\u7ec8\uff0c\u4e5f\u8bb8\u662f\u5bb3\u6b7b\u7ed3\u4e49\u76df\u5144\uff0c\u4ed6\u826f\u5fc3\u611f\u5230\u4e0d \u5b89\u5427\uff1f \u3000\u3000\u8fd9\u4e8b\u5f88\u5c11\u4eba\u77e5\u9053\uff0c\u867d\u6709\u5c55\u4e91\u5929\u751f\u524d\u6545\u4ea4\uff0c\u5982\u201c\u592a\u767d\u53cc\u9038\u201d\u96f7\u9707\u8fdc\u3001\u201c\u65e0\u5f71\u795e\u5077\u201d\u534e\u6e05\u6cc9 \u7b49\u4f17\u4eba\u65e5\u591c\u67e5\u8bbf\uff0c\u4e5f\u672a\u77e5\u7aef\u502a\u3002 \u3000\u3000\u6700\u540e\u201c\u795e\u9a74\u94c1\u80c6\u201d\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u53ea\u6709\u8001\u592b\u4e00\u4eba\uff0c\u7ecf\u8fc7\u591a\u5e74\u660e\u67e5\u6697\u8bbf\uff0c\u624d\u7565\u77e5\u771f\u50cf\uff0c\u4f46\u5982 \u679c\u4e0d\u662f\u4eca\u591c\u9047\u5230\u2018\u795e\u7334\uff0c\u8bf4\u51fa\u4f60\u7236\u5f3a\u501f\u2018\u907f\u6c34\u795e\u73e0\u2019\u4e00\u8282\uff0c\u8fd8\u4e0d\u77e5\u9053\u4f60\u7236\u5982\u4f55\u5728\u6d1e\u5ead\u6e56\u5e95\u53d6 \u51fa\u5343\u5e74\u5b9d\u85cf\u2026\u2026\u201d \u3000\u3000\u5c55\u767d\u9759\u9759\u542c\u7740\u201c\u795e\u9a74\u94c1\u80c6\u201d\u8bf4\u5b8c\u7236\u4eb2\u88ab\u5bb3\u7ecf\u8fc7\uff0c\u7ade\u4e00\u6ef4\u773c\u6cea\u672a\u6d41\uff0c\u5374\u53cc\u76ee\u5ffd\u773a\u7686\u88c2\uff0c\u987a \u7740\u773c\u8138\u6c69\u6c69\u6d41\u4e0b\u4e24\u884c\u9c9c\u8840\u6765\u3002 \u3000\u3000\u201c\u795e\u9a74\u94c1\u80c6\u201d\u89c1\u5c55\u767d\u60b2\u75db\u6124\u6012\u5230\u5982\u6b64\u5730\u6b65\uff0c\u4e0d\u7531\u53f9\u9053\uff1a\u201c\u53ef\u60dc\u8001\u592b\u901e\u4e00\u65f6\u4e4b\u52c7\uff0c\u4e0e\u8001\u7334 \u5b50\u843d\u4e86\u4e2a\u4e24\u8d25\u4ff1\u4f24\uff0c\u65e0\u6cd5\u52a9\u4f60\u62a5\u4ec7\uff0c\u800c\u4e14\u8001\u592b\u6d3b\u65e5\u65e0\u591a\uff0c\u4e5f\u65e0\u6cd5\u518d\u591a\u4f20\u4f60\u6b66\u529f\uff0c\u6211\u770b\u4f60\u8fd8\u662f \u5fcd\u4f4f\u60b2\u4f24\uff0c\u5c31\u7740\u8001\u592b\u5c1a\u6709\u4e00\u53e3\u6c14\u5728\uff0c\u6536\u6444\u5fc3\u795e\uff0c\u542c\u8001\u592b\u7ed8\u4f60\u8bb2\u89e3\u51e0\u95e8\u7f1d\u4e16\u6b66\u529f\u7684\u8bc0\u7a8d\u5427\uff01\u201d \u3000\u3000\u5c55\u767d\u9053\uff1a\u201c\u8001\u524d\u8f88\uff0c\u4f60\u8bf4\u5f97\u5bf9\uff0c\u82f1\u96c4\u6709\u6cea\u4e0d\u8f7b\u5f39\uff0c\u665a\u8f88\u51b3\u4e0d\u5f92\u60b2\u4f24\uff0c\u53ea\u6709\u6ee1\u8154\u6124\u6012\uff01\u73b0 \u5728\u8bf7\u8001\u524d\u8f88\u8bb2\u5427E\uff0c\u665a\u8f88\u6d17\u8033\u606d\u542c\uff01\u201d \u3000\u3000\u4e8e\u662f\uff0c\u201c\u795e\u9a74\u94c1\u80c6\u201d\u4e3a\u5c55\u81ea\u8bb2\u8ff0\u5404\u79cd\u9ad8\u6df1\u8981\u8bc0\uff0c\u5404\u79cd\u62db\u5f0f\u7ae0\u6cd5\uff0c\u4ee5\u53ca\u4e34\u654c\u81f4\u80dc\u4e4b \u9053\u2026\uff0c\uff0e\xb7\u597d\u5728\u5c55\u767d\u6b66\u529f\u5df2\u6709\u826f\u597d\u6839\u57fa\uff0c\u53c8\u52a0\u4e0a\u4fee\u4e60\u300a\u9501\u9aa8\u7ecd\u9b42\u5929\u4f5b\u5377\u300b\uff0c\u5185\u529f\u5927\u589e\uff0c\u5bf9 \u201c\u795e\u9a74\u94c1\u80c6\u201d\u6240\u4f20\uff0c\u867d\u7136\u6b66\u529f\u535a\u5927\u7cbe\u6df1\uff0c\u7adf\u80fd\u4e00\u4e00\u9886\u4f1a\uff0c\u5b66\u4e60\u6781\u901f\u3002 \u3000\u3000\u5c55\u767d\u53ef\u4ee5\u8bf4\u6709\u5b66\u5fc5\u4f1a\uff0c\u8fd9\u4f7f\u201c\u795e\u9a74\u94c1\u80c6\u201d\u559c\u51fa\u671b\u5916\uff0c\u63d0\u9ad8\u4e86\u5174\u81f4\uff0c\u4e0d\u987e\u4f24\u91cd\u547d\u5371\uff0c\u5c06\u7ec8 \u751f\u7cbe\u7814\u7684\u9ad8\u5f3a\u6b66\u529f\u8bc0\u7a8d\u503e\u56ca\u76f8\u6388\u3002 \u3000\u3000\u53ef\u60dc\u65f6\u95f4\u592a\u77ed\u4e86\uff0c\u4e0d\u5230\u767e\u65e5\u7684\u65f6\u95f4\uff0c\u8f6c\u77ac\u5373\u5c4a\uff0c\u8fd9\u5929\u5df2\u662f\u201c\u795e\u9a74\u94c1\u80c6\u201d\u5728\u77f3\u6d1e\u4e2d\u4f20\u6388\u5c55 \u767d\u6b66\u529f\u7684\u7b2c\u4e5d\u5341\u5929\u4e86\uff0c\u6070\u597d\u4e09\u4e2a\u6708\u671f\u6ee1\uff0c\u4f46\u201c\u795e\u9a74\u94c1\u670d\u201d\u4ee5\u4f24\u6b8b\u4e4b\u8eab\uff0c\u663c\u591c\u4e0d\u606f\u4f20\u7ed8\u5c55\u767d\u6b66 \u529f\uff0c\u65e2\u4e0d\u80fd\u8c03\u517b\u751f\u60a3\uff0c\u7cbe\u795e\u8017\u8d39\u53c8\u5de8\uff0c\u7ade\u6cb9\u5c3d\u706f\u67af\uff0c\u5230\u4e86\u5f25\u7559\u72b6\u6001\u3002 \u3000\u3000\u5c55\u767d\u9189\u5fc3\u4e60\u6b66\uff0c\u5374\u4ece\u672a\u9047\u5230\u8fc7\u660e\u5e08\u3002\u867d\u6709\u4e00\u4ee3\u602a\u6770\u96f7\u9707\u8fdc\u6982\u7136\u76f8\u8d60\u4e16\u95f4\u7b2c\u4e00\u5947\u4e66\uff0c\u4ed6\u5374 \u662f\u8499\u7136\u65e0\u77e5\u3002 \u3000\u3000\u5982\u4eca\u9047\u5230\u201c\u795e\u9a74\u94c1\u963b\u201d\u8fd9\u6837\u6b66\u529f\u9ad8\u5f3a\u7684\u660e\u5e08\uff0c\u8a00\u8a00\u91d1\u7389\uff0c\u5b57\u5b57\u7384\u7391\uff0c\u6240\u4f20\u4ed6\u7684\u6b66\u529f\u8bc0\u7a8d \u5747\u662f\u7cbe\u5999\u65e0\u6bd4\uff0c\u5c55\u767d\u5168\u526f\u5fc3\u795e\u90fd\u653e\u5728\u5b66\u4e60\u6b66\u529f\u4e0a\u9762\uff0c\u53ef\u4ee5\u8bf4\u5230\u4e86\u53d1\u6124\u5fd8\u98df\u7684\u5730\u6b65\uff0c\u9664\u4e86\u9965\u98df \u6e34\u996e\u4e4b\u5916\uff0c\u518d\u4e5f\u4e0d\u987e\u5176\u4ed6\uff0c\u5bf9\u201c\u795e\u9a74\u94c1\u80c6\u201d\u6108\u6765\u6108\u8870\u5f31\u7684\u60c5\u5f62\uff0c\u7adf\u672a\u6ce8\u610f\u52306\u6d1e\u4e2d\u53c8\u6709\u201c\u795e \u9a74\u94c1\u80c6\u201d\u9884\u5148\u50a8\u5b58\u7684\u9ec4\u7cbe\u8089\u812f\uff0c\u6c34\u679c\u7518\u6cc9\uff0c\u6570\u91cf\u6781\u591a\uff0c\u996e\u98df\u65e0\u7f3a\uff0c\u4e09\u4e2a\u6708\u7684\u65f6\u95f4\uff0c\u5c55\u767d\u8db3\u4e0d \u51fa\u6d1e\uff0c\u628a\u201c\u795e\u9a74\u94c1\u80c6\u201d\u6240\u6388\u7684\u9ad8\u6df1\u6b66\u529f\u8bc0\u7a8d\uff0c\u5df2\u9886\u4f1a\u4e86\u5341\u4e4b\u516b\u4e5d\u3002\u5230\u4e86\u8fd9\u4e00\u5929\uff0c\u201c\u795e\u9a74\u94c1 \u80c6\u201d\u751f\u547d\u5df2\u5230\u4e86\u6781\u9650\uff0c\u8bb2\u51fa\u6700\u540e\u4e00\u53e5\u8bdd\u6765\u65f6\uff0c\u5df2\u662f\u4e0a\u6c14\u4e0d\u63a5\u4e0b\u6c14\uff0c\u95ed\u76ee\u4f11\u606f\u4e86\u4e00\u4e0b\uff0c\u624d\u7741\u773c \u9053\uff1a\u201c\u6211\u4f20\u4f60\u6b66\u529f\uff0c\u5230\u6b64\u4e3a\u6b62\uff0c\u597d\u5728\u4f60\u6709\u90a3\u518c\u5929\u4e0b\u7b2c\u4e00\u5947\u4e66\u300a\u9501\u9aa8\u9500\u7470\u5929\u4f5b\u5377\u300b\uff0c\u53ea\u8981\u6301\u4e4b \u4ee5\u6052\uff0c\u4e0d\u65ad\u5730\u7ec3\u4e0b\u53bb\uff0c\u4f60\u5c06\u6765\u7684\u6210\u5c31\uff0c\u5b9e\u53ef\u8d85\u51fa\u8001\u592b\u4e4b\u4e0a\uff0c\u597d\u4e86\uff0c\u6211\u4eec\u7f18\u5c3d\u4e8e\u6b64\uff0c\u4f60\u51fa\u6d1e\u53bb \u5427\u2026\u2026\u201d \u3000\u3000\u201c\u795e\u9a74\u94c1\u80c6\u201d\u8bf4\u81f3\u6700\u540e\uff0c\u6c14\u606f\u8870\u5f31\uff0c\u51e0\u81f3\u8bed\u4e0d\u6210\u58f0\uff0c\u5c55\u767d\u95fb\u8a00\u4e00\u6115\uff0c\u4ed6\u8fd9\u624d\u6ce8\u610f\u5230\u201c\u795e \u9a74\u94c1\u80c6\u201d\u53cc\u76ee\u795e\u5149\u5df2\u6563\uff0c\u9762\u5982\u81ea\u7eb8\uff0c\u80f8\u524d\u4e0d\u4f4f\u6025\u5267\u8d77\u4f0f\u7740\uff0c\u770b\u6837\u5b50\u5df2\u79bb\u5927\u9650\u4e0d\u8fdc\u3002 \u3000\u3000\u5c55\u767d\u5403\u60ca\u9053\uff1a\u201c\u8001\u524d\u8f88\uff0c\u4f60\u2026\u2026\u201d \u3000\u3000\u201c\u795e\u9a74\u94c1\u80c6\u201d\u7a81\u53c8\u7741\u5f00\u773c\u775b\u9053\uff1a\u201c\u4f60\u4e0d\u7528\u7ba1\u6211\uff0c\u53ea\u7d27\u8bb0\u62a5\u7236\u4ec7\u4e0d\u53ef\u9c81\u83bd\u4ece\u4e8b\uff0c\u52a0\u7d27\u4fee\u7ec3 \u6b66\u529f\uff0c\u591a\u591a\u7ed3\u4ea4\u5929\u4e0b\u82f1\u96c4\u597d\u6c49\uff0c\u518d\u628a\u4ed6\u7236\u4eb2\u88ab\u5bb3\u771f\u76f8\u516c\u5e03\u6b66\u6797\u5468\u77e5\uff0c\u81f3\u4e8e\u2026\u2026\u201d \u3000\u3000\u201c\u795e\u9a74\u94c1\u80c6\u201d\u521a\u521a\u8bf4\u81f3\u6b64\u5904\uff0c\u7a81\u542c\u6d1e\u5916\u4eba\u58f0\u77aa\u6742\uff0c\u6709\u4e00\u4eba\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u5728\u8fd9\u91cc\u4e86\uff01\u770b\u8fd9 \u6d1e\u53e3\uff0c\u5206\u660e\u6709\u4eba\u8fdb\u53bb\uff01\u201d \u3000\u3000\u63a5\u7740\u4f17\u4eba\u4e03\u5634\u516b\u820c\u5730\u56b7\u9053\uff1a\u201c\u8fdb\u53bb\u641c\u641c\uff01\u8fdb\u53bb\uff01\u8d70\u2026\u2026\u201d \u3000\u3000\u811a\u6b65\u6742\u6c93\uff0c\u542c\u58f0\u97f3\u5224\u65ad\uff0c\u5df2\u6709\u6570\u4eba\u5411\u6d1e\u4e2d\u5b9a\u6765\u3002 \u3000\u3000\u5c55\u767d\u770b\u51fa\u201c\u795e\u9a74\u94c1\u80c6\u201d\u547d\u5728\u65e6\u5915\uff0c\u6050\u6015\u6765\u4eba\u60ca\u6270\u4e86\u201c\u795e\u9a74\u94c1\u80c6\u201d\uff0c\u5f53\u5373\u8fce\u51fa\u6570\u6b65\uff0c\u53eb \u9053\uff1a\u201c\u6d1e\u5916\u4ec0\u4e48\u4eba\uff1f\u5c11\u5f80\u91cc\u95ef\u2014\u2014\u201d\u8c01\u77e5\u5c55\u767d\u8bdd\u672a\u8bf4\u5b8c\uff0c\u6d1e\u5916\u731b\u559d\u4e00\u58f0\uff1a\u201c\u6253\uff01\u201d \u3000\u3000\u968f\u7740\u6570\u9053\u5bd2\u8292\uff0c\u631f\u7740\u88ab\u7a7a\u52b2\u98ce\uff0c\u76f4\u5411\u5c55\u767d\u9762\u95e8\u6253\u6765\u3002 \u3000\u3000\u5c55\u767d\u89c1\u6765\u4eba\u4e0d\u95ee\u9752\u7ea2\u7682\u767d\uff0c\u9aa4\u7136\u65bd\u51fa\u6697\u5668\uff0c\u5fc3\u4e2d\u5927\u6012\uff0c\u4e3e\u638c\u4e00\u6325\uff0c\u628a\u88ad\u6765\u6697\u5668\u9707\u98de\uff0c\u53ee \u5f53\u51e0\u58f0\uff0c\u4e09\u652f\u4eae\u94f6\u9556\u4e00\u9f50\u6253\u8fdb\u6d1e\u5185\u77f3\u58c1\u4e0a\uff0c\u706b\u661f\u56db\u6e85\uff0c\u5c55\u767d\u63a5\u53d7\u201c\u795e\u9a74\u94c1\u80c6\u201d\u4f20\u529f\uff0c\u5185\u529f\u795e \u529b\u8fd0\u7528\u968f\u5fc3\uff0c\u5df2\u53d1\u6325\u4e86\u83ab\u5927\u5a01\u529b\uff0c\u5c31\u8fd9\u4e00\u624b\u201c\u6325\u91d1\u5165\u77f3\u201d\uff0c\u5df2\u53ef\u9707\u60ca\u6c5f\u6e56\u3002 \u3000\u3000\u5c55\u767d\u4e00\u638c\u628a\u88ad\u6765\u6697\u5668\u9707\u98de\uff0c\u6697\u6068\u6765\u4eba\u5fc3\u72e0\u624b\u8fa3\uff0c\u968f\u624b\u5411\u6d1e\u5916\u63a8\u51fa\u4e24\u638c\uff0c\u72c2\u98d8\u9aa4\u8d77\uff0c\u5c55\u767d \u7684\u4eba\u4e5f\u968f\u7740\u81ea\u5df1\u5f3a\u5927\u638c\u98ce\u7a9c\u51fa\u6d1e\u5916\u3002 \u3000\u3000\u638c\u98ce\u5c71\u6d8c\uff0c\u7531\u6d1e\u5185\u6c79\u6d8c\u800c\u51fa\uff0c\u540c\u65f6\u4e24\u58f0\u60e8\u55f7\uff0c\u4e09\u6761\u4eba\u5f71\uff0c\u5df2\u4ece\u6d1e\u5185\u98de\u51fa\u6d1e\u6765\u3002 \u3000\u3000\u201c\u7830\u7830\u201d\u4e24\u58f0\uff0c\u5148\u98de\u51fa\u6d1e\u6765\u7684\u4e24\u6761\u4eba\u5f71\uff0c\u6454\u843d\u5730\u4e0a\uff0c\u5012\u5730\u4e0d\u8d77\uff0c\u4e0d\u77e5\u6b7b\u6d3b\u3002\u843d\u540e\u51fa\u6765\u7684 \u7b2c\u4e09\u6761\u4eba\u5f71\u6b63\u662f\u5c55\u767d\u3002 \u3000\u3000\u539f\u6765\u90a3\u8fdb\u6d1e\u7684\u4e8c\u4eba\uff0c\u5df2\u88ab\u5c55\u767d\u638c\u529b\u9707\u51fa\u6d1e\u5916\u91cd\u4f24\u5012\u5730\u3002 \u3000\u3000\u5c55\u767d\u7eb5\u51fa\u6d1e\u5916\uff0c\u53f3\u638c\u84c4\u52bf\u5f85\u654c\uff0c\u5de6\u638c\u62a4\u80f8\uff0c\u4e3e\u76ee\u4e00\u770b\uff0c\u6d1e\u5916\u7ad9\u5b9a\u535c\u6570\u4e2a\u6b66\u6797\u4eba\u7269\uff0c\u5f53\u4e2d \u4e00\u4eba\uff0c\u6b63\u662f\u90a3\u4fca\u7f8e\u65e0\u6bd4\u7684\u201c\u7965\u9e9f\u516c\u5b50\u201d\u3002 \u3000\u3000\u4e0e\u201c\u7965\u9e9f\u516c\u5b50\u201d\u5e76\u80a9\u7ad9\u5b9a\u7684\u662f\u90a3\u4fca\u7f8e\u5982\u82b1\u7684\u201c\u6c5f\u5357\u7b2c\u4e00\u7f8e\u4eba\u201d\u3001\u201c\u7965\u9e9f\u516c\u5b50\u201d\u4e4b\u59b9\u91d1\u5f69 \u51e4\u3002 \u3000\u3000\u7ad9\u5728\u4ed6\u5144\u59b9\u4e8c\u4eba\u8eab\u540e\u7684\u662f\u201c\u91d1\u5e9c\u53cc\u94c1\u536b\u201d\u3001\u201c\u94c1\u80cc\u9a7c\u9f99\u201d\u516c\u5b59\u695a\u3001\u201c\u94c1\u7ffc\u98de\u9e4f\u201d\u5df4\u5929 \u8d6b\uff0c\u518d\u8005\u5c31\u662f\u91d1\u5e9c\u4e2d\u7684\u6b66\u6797\u9ad8\u624b\u3002\u4f46\u5c55\u767d\u4e00\u65f6\u4e4b\u95f4\u5df2\u53eb\u4e0d\u51fa\u4ed6\u4eec\u7684\u540d\u5b57\u3002 \u3000\u3000\u201c\u7965\u9e9f\u516c\u5b50\u201d\u5144\u59b9\u53ca\u91d1\u5e9c\u4f17\u591a\u6b66\u6797\u9ad8\u624b\uff0c\u89c1\u4ece\u6d1e\u4e2d\u7eb5\u51fa\u6765\u7684\u7adf\u662f\u5c55\u767d\u4e0d\u7531\u5747\u81ea\u4e00\u6014\u3002 \u3000\u3000\u53c8\u89c1\u8fdb\u6d1e\u641c\u7d22\u7684\u4e24\u5927\u9ad8\u624b\uff0c\u4ece\u6d1e\u5185\u76f4\u8dcc\u51fa\u6765\uff0c\u7acb\u523b\u5012\u5730\u4e0d\u8d77\uff0c\u5c31\u662f\u4e0d\u6b7b\u4e5f\u8d1f\u4e86\u91cd\u4f24\uff0c\u7b80 \u76f4\u4e0d\u77e5\u662f\u600e\u4e48\u4f24\u7684\uff1f\u4e07\u4e5f\u60f3\u4e0d\u5230\u662f\u88ab\u5c55\u767d\u4e00\u638c\u9707\u51fa\u6d1e\u5916\uff0c\u8fd8\u4ee5\u4e3a\u6d1e\u4e2d\u53e6\u85cf\u6709\u4ec0\u4e48\u6b66\u6797\u5f02\u58eb\u3002 \u3000\u3000\u201c\u7965\u9e9f\u516c\u5b50\u201d\u695e\u4e86\u4e00\u4e0b\uff0c\u7acb\u523b\u53c8\u6062\u590d\u4e86\u9547\u5b9a\uff0c\u4ece\u5bb9\u95ee\u9053\uff1a\u201c\u539f\u6765\u5c55\u5144\u5728\u6d1e\u4e2d\uff0c\u4f46\u4e0d\u77e5\u6d1e \u4e2d\u8fd8\u6709\u4f55\u4f4d\u6b66\u6797\u9ad8\u4eba\uff1f\u4f55\u4e0d\u8bf7\u51fa\u4e00\u89c1\uff01\u201d \u3000\u3000\u5c55\u767d\u4e5f\u60f3\u4e0d\u5230\u4e0d\u6309\u6c5f\u6e56\u89c4\u77e9\uff0c\u89c1\u9762\u65bd\u6697\u5668\u7684\u4eba\uff0c\u7adf\u662f\u9f0e\u9f0e\u5927\u540d\u7684\u201c\u6837\u9e9f\u516c\u5b50\u201d\u7387\u9886\u800c \u6765\uff0c\u95fb\u8a00\u51b7\u7b11\u9053\uff1a\u6d1e\u5185\u4e4b\u4eba\uff0c\u4e0d\u613f\u89c1\u4e0d\u61c2\u6c5f\u6e56\u89c4\u77e9\u3001\u89c1\u9762\u5c31\u65bd\u6697\u7b97\u7684\u9ad8\u4eba\uff0c\u8d35\u516c\u5b50\u6709\u4f55\u8d50 \u6559\uff0c\u7531\u5c55\u767d\u63a5\u4f4f\u5c31\u662f\u4e86\uff01\u201d \u3000\u3000\u8bed\u6c14\u76f8\u5f53\u4e0d\u5ba2\u6c14\u3002 \u3000\u3000\u91d1\u5f69\u51e4\u4e00\u89c1\u5c55\u767d\u6ce8\u6b64\u5730\u51fa\u73b0\uff0c\u82b3\u5fc3\u4e00\u9635\u4e71\u8df3\uff0c\u661f\u773c\u6d41\u6ce2\uff0c\u6a31\u5507\u542f\u52a8\uff0c\u4f46\u672a\u7b49\u5979\u53d1\u8a00\uff0c \u201c\u7965\u9e9f\u516c\u5b50\u201d\u5374\u82e6\u7b11\u4e00\u58f0\u9053\uff1a\u201c\u5c55\u5144\u4e0d\u5fc5\u8bef\u4f1a\uff0c\u7965\u9e9f\u4e0d\u77e5\u9053\u5c55\u5144\u9690\u8eab\u6d1e\u5185\u2026\u2026\u201d \u3000\u3000\u5c55\u767d\u4f9d\u7136\u51b7\u7b11\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u82e5\u662f\u522b\u4eba\u5728\u6d1e\u4e2d\uff0c\u4ee5\u8d35\u516c\u5b50\u7684\u8eab\u4efd\u5730\u4f4d\uff0c\u4fbf\u53ef\u4ee5\u9aa4\u65bd\u6697\u7b97\u4e86 \u5417\uff1f\u8fd9\u4e48\u8bf4\u6765\uff0c\u6211\u5c55\u767d\u8fd8\u8981\u611f\u8c22\u516c\u5b50\u7684\u76db\u60c5\u5450\uff01\u201d\u2019\u91d1\u5f69\u51e4\u63d2\u5634\u9053\uff1a\u201c\u4f60\u4e0d\u8981\u8bef\u4f1a\u6211\u54e5\u54e5\uff0c \u56e0\u4e3a\u6211\u4eec\u4e22\u4e86\u91cd\u8981\u7684\u4e1c\u897f\uff0c\u5df2\u6709\u4e24\u62e8\u4eba\u5728\u5ca9\u5c71\u5341\u4e8c\u6d1e\u6765\u5bfb\u65f6\u5403\u4e86\u4e8f\uff0c\u6240\u4ee5\u2026\u2026\u201d \u3000\u3000\u91d1\u5e9c\u6765\u4eba\u4e2d\uff0c\u4ee5\u201c\u94b1\u7ffc\u98de\u9e4f\u201d\u813e\u6027\u6700\u4e3a\u602a\u5f02\uff0c\u52a0\u4e0a\u4ed6\u6b66\u529f\u9ad8\u5f3a\uff0c\u773c\u9ad8\u4e8e\u9876\uff0c\u91d1\u5e9c\u5931\u7269\uff0c \u6570\u62e8\u4eba\u9a6c\uff0c\u90fd\u5728\u5ca9\u5c71\u5341\u4e8c\u6d1e\u5403\u4e86\u4e8f\uff0c\u5982\u4eca\u89c1\u5c55\u767d\u4ece\u6d1e\u5185\u5192\u51fa\u6765\uff0c\u53c8\u6709\u4e24\u540d\u9ad8\u624b\u9001\u547d\uff0c\u91d1\u6c0f\u5144 \u59b9\u5bf9\u5c55\u767d\u4e00\u518d\u5bb9\u8ba9\uff0c\u800c\u5c55\u81ea\u5374\u8868\u73b0\u5f97\u975e\u5e38\u51b7\u50b2\uff0c\u4e0d\u7531\u5fc3\u4e2d\u5927\u6012\uff0c\u56e0\u6b64\uff0c\u4e0d\u7b49\u91d1\u5f69\u51e4\u7684\u8bdd\u8bf4 \u5b8c\uff0c\u5927\u523a\u523a\u5730\u8dd1\u524d\u4e24\u6b65\u53f1\u9053\uff1a\u201c\u72d7\u80c6\u5c0f\u8f88\uff01\u7ed9\u8138\u4e0d\u8981\u8138\uff0c\u96be\u9053\u5802\u5802\u91d1\u5e9c\uff0c\u8fd8\u6015\u4e86\u8c01\u4e0d\u6210\uff01\u4f60 \u5982\u518d\u4e0d\u8bf4\u85cf\u5728\u6d1e\u4e2d\u8c79\u5371\u4ec0\u4e48\u4eba\uff0c\u522b\u8bf4\u4e8c\u7237\u5bf9\u4f60\u4e0d\u5ba2\u6c14\uff01\u201d \u3000\u3000\u5c55\u767d\u4f9d\u7136\u51b7\u7b11\u9053\uff1a\u201c\u4e0d\u5ba2\u6c14\uff0c\u4f60\u53c8\u80fd\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u201c\u5148\u628a\u4f60\u8fd9\u5c0f\u5b50\u62ff\u4e0b\u518d\u8bf4\uff01\u201d \u3000\u3000\u201c\u94c1\u7ffc\u98de\u9e4f\u201d\u6012\u53f1\u4e00\u58f0\uff0c\u8eab\u5f62\u7535\u95ea\u800c\u51fa\uff0c\u63a2\u81c2\u76f4\u5411\u5c55\u767d\u8fce\u9762\u6293\u6765\u3002 \u3000\u3000\u201c\u94e3\u7ffc\u98de\u9e4f\u201d\u4e0d\u6127\u4e3a\u5357\u4eac\u91d1\u5e9c\u7684\u5934\u53f7\u9ad8\u624b\uff0c\u8eab\u6cd5\u4fa0\uff0c\u62db\u5f0f\u5947\uff0c\u867d\u7136\u8fd9\u968f\u968f\u4fbf\u4fbf\u4e00\u4f38\u624b\uff0c \u7ade\u7136\u6307\u98ce\u75b2\u5578\u7834\u98ce\uff0c\u6307\u672a\u5230\uff0c\u5c55\u767d\u5c31\u611f\u5230\u529b\u52b2\u5982\u5200\uff0c\u523a\u80a4\u751f\u75db\u3002 \u3000\u3000\u4f46\u5c55\u767d\u6b64\u65f6\u5df2\u4eca\u975e\u6614\u6bd4\uff0c\u201c\u795e\u9a74\u94c1\u80c6\u201d\u4e09\u4e2a\u6708\u7684\u6089\u5fc3\u6559\u5bfc\uff0c\u8be1\u5947\u62db\u5f0f\u5b66\u4e86\u4e0d\u5c11\uff0c\u800c\u4e14\u5fc3 \u968f\u610f\u4f1a\uff0c\u5df2\u80fd\u5b8c\u5168\u53d1\u6325\u672c\u8eab\u6240\u5177\u6f5c\u5728\u7684\u795e\u529b\uff0c\u5c31\u5728\u201c\u94c1\u7ffc\u98de\u9e4f\u201d\u51cc\u5389\u65e0\u6bd4\u7684\u6307\u98ce\u5c06\u53ca\u9762\u95e8\u4e4b \u9645\uff0c\u8eab\u4e0d\u79fb\uff0c\u811a\u4e0d\u52a8\uff0c\u53ea\u53cc\u80a9\u5fae\u5fae\u4e00\u6643\uff0c\u8eb2\u8fc7\u8fce\u9762\u6307\u98ce\uff0c\u53cd\u800c\u63a2\u624b\u5411\u201c\u94c1\u5668\u98de\u9e4f\u201d\u81c2\u4e0a\u5173\u5143 \u9501\u6765\u3002 \u3000\u3000\u5c55\u767d\u4e5f\u662f\u968f\u968f\u4fbf\u4fbf\u4e00\u4f38\u624b\uff0c\u5999\u5230\u6beb\u5dc5\uff0c\u6b63\u597d\u7834\u4e86\u201c\u94c1\u7ffc\u98de\u9e4f\u201d\u8fd9\u770b\u4f3c\u5e73\u6de1\u3001\u5b9e\u9645\u5374\u5965\u5999 \u65e0\u6bd4\u7684\u201c\u9501\u9f99\u624b\u201d\u6740\u62db\uff0c\u94c1\u7ffc\u98de\u9e4f\u201d\u5fae\u5fae\u4e00\u61d4\uff0c\u89c1\u5c55\u767d\u4ee5\u62db\u7834\u62db\uff0c\u795e\u5947\u96be\u6d4b\uff0c\u7acb\u523b\u53d8\u62db\uff0c\u4f38 \u51fa\u53bb\u7684\u5de6\u624b\u8155\u4e00\u6c89\uff0c\u53cc\u6307\u5982\u94b3\uff0c\u731b\u6263\u5c55\u767d\u5589\u4e0b\u201c\u7487\u7391\u201d\u7a74\uff0c\u540c\u65f6\u4e0a\u6b65\u51fa\u53f3\u638c\uff0c\u638c\u7f18\u5982\u5200\uff0c\u731b \u524a\u5c55\u767d\u5de6\u808b\u8f6f\u9aa8\u3002 \u3000\u3000\u5c55\u767d\u4e5f\u81ea\u4e00\u60ca\uff0c\u89c9\u5f97\u8fd9\u201c\u94c1\u7ffc\u98de\u9e4f\u201d\u53d8\u62db\u4e4b\u75be\uff0c\u51fa\u624b\u4e4b\u5feb\uff0c\u7b80\u76f4\u662f\u65e0\u4e0e\u4f26\u6bd4\uff0c\u5e78\u4e8f\u53d7\u4e86 \u524d\u8f88\u5f02\u4eba\u795e\u9a74\u94c1\u80c6\u4e09\u6708\u4f20\u529f\uff0c\u82e5\u4e0d\u7136\u5c31\u8fd9\u51fa\u624b\u8fde\u73af\u4e24\u62db\uff0c\u81ea\u5df1\u975e\u843d\u8d25\u4e0d\u53ef\u3002 \u3000\u3000\u5c55\u767d\u5fc3\u4e2d\u8fd9\u6837\u8b66\u60d5\uff0c\u624b\u4e25\u53ef\u4e0d\u6562\u6020\u6162\uff0c\u8eab\u5f62\u5fae\u6643\uff0c\u53cc\u638c\u9f50\u51fa\uff0c\u65bd\u51fa\u521a\u5411\u201c\u795e\u9a74\u94c1\u80c6\u201d\u5b66 \u6765\u7684\u6740\u624b\u201c\u6355\u98ce\u6349\u5f71\u201d\uff0c\u4ecd\u7136\u662f\u4e0d\u5b88\u4e0d\u9000\uff0c\u89c1\u62db\u6253\u62db\u7684\u62db\u5f0f\uff0c\u53cc\u638c\u731b\u6253\u88ad\u6765\u53cc\u638c\u7684\u8981\u7a74\u3002 \u3000\u3000\u4e8c\u4eba\u8fd1\u8eab\u8089\u640f\uff0c\u8eab\u6cd5\u624b\u6cd5\u90fd\u662f\u4fa0\u5f97\u51fa\u5947\uff0c\u7728\u773c\u4e4b\u95f4\u4e92\u6362\u4e86\u516d\u3001\u4e03\u62db\uff0c\u53ea\u628a\u91d1\u5e9c\u6765\u7684\u4f17\u5c16 \u9ad8\u624b\uff0c\u770b\u4e86\u4e2a\u773c\u82b1\u7f2d\u4e71\uff01\u5c24\u5176\u201c\u94c1\u7ffc\u98de\u9e4f\u201d\u7684\u6b66\u529f\uff0c\u795e\u5947\u96be\u6d4b\uff0c\u7d20\u4e3a\u91d1\u5e9c\u4f17\u9ad8\u624b\u6240\u94a6\u4ef0\uff0c\u5982 \u4eca\u5c55\u767d\u7adf\u4e0e\u4e4b\u6218\u4e86\u4e2a\u5e73\u624b\u4e0d\u7531\u4f7f\u91d1\u5e9c\u968f\u6765\u7684\u6b66\u6797\u9ad8\u624b\u9707\u60ca\u5f97\u76ee\u77aa\u53e3\u5446\u3002 \u3000\u3000\u5c55\u767d\u529b\u6218\u6c5f\u6e56\u4e0a\u95fb\u540d\u4e27\u80c6\u3001\u5728\u91d1\u5e9c\u4e2d\u5217\u540d\u4e3a\u9879\u5c16\u9ad8\u624b\u7684\u201c\u94c1\u7ffc\u98de\u9e4f\u201d\uff0c\u6beb\u65e0\u602f\u8272\uff0c\u89c1\u62db \u6253\u62db\uff0c\u89c1\u5f0f\u6253\u5f0f\uff0c\u5b8c\u5168\u662f\u4ee5\u653b\u6b62\u653b\uff0c\u8eab\u5f62\u5fae\u79fb\u7a0d\u6643\u4e4b\u95f4\uff0c\u53cc\u638c\u5982\u98de\uff0c\u548c\u201c\u94c1\u7ffc\u98de\u9e4f\u201d\u6253\u4e86\u4e2a \u96be\u89e3\u96be\u5206\u3002 \u3000\u3000\u201c\u94c1\u7ffc\u98de\u9e4f\u201d\u6210\u540d\u6c5f\u6e56\u6570\u5341\u5e74\uff0c\u4ece\u672a\u9047\u8fc7\u654c\u624b\uff0c\u5728\u6740\u62db\u8fde\u51fa\u4e4b\u4e0b\uff0c\u6218\u4e0d\u8fc7\u4e00\u4e2a\u5c11\u5e74\u5c55 \u767d\uff0c\u5fc3\u4e2d\u65e2\u60ca\u4e14\u6012\uff0c\u51fa\u624b\u66f4\u52a0\u6bd2\u8fa3\uff0c\u638c\u6307\u5982\u98ce\uff0c\u6068\u4e0d\u5f97\u4e00\u638c\u628a\u5c55\u767d\u6253\u6b7b\u4ee5\u51fa\u80f8\u95f4\u4e00\u53e3\u95f7\u6c14\u3002 \u3000\u3000\u4e8c\u4eba\u8eab\u5f62\u7f20\u7ed5\u5728\u4e00\u8d77\uff0c\u5feb\u5982\u98d9\u98ce\u95ea\u7535\uff0c\u51e0\u81f3\u654c\u6211\u96be\u5206\uff0c\u500f\u5ffd\u95f4\u53ea\u542c\u51e0\u58f0\u201c\u556a\u556a\u201d\u6c14\u7206\u4e4b \u58f0\u4f20\u6765\uff0c\u4e8c\u4eba\u8eab\u5f62\u508d\u7136\u5de6\u53f3\u5206\u5f00\u4e24\u4e08\uff01 \u3000\u3000\u201c\u94e3\u7ffc\u98de\u9e4f\u201d\u6012\u775b\u7a81\u51fa\u7736\u5916\uff0c\u9ed1\u8138\u94c1\u9752\u3002 \u3000\u3000\u5c55\u767d\u4fca\u9762\u5e26\u715e\uff0c\u661f\u76ee\u5982\u70ac\u3002 \u3000\u3000\u4e8c\u4eba\u5404\u81ea\u6012\u89c6\u7740\u5bf9\u65b9\uff0c\u77aa\u4e86\u597d\u5927\u4e00\u4f1a\u53cc\u65b9\u5747\u672a\u51fa\u624b\u3002\u201c\u7965\u9e9f\u516c\u5b50\u201d\u5929\u751f\u6709\u201c\u7231\u624d\u4e4b \u7656\u201d\uff0c\u89c1\u5c55\u767d\u5e74\u7eaa\u8f7b\u8f7b\u7ade\u80fd\u8ddf\u81ea\u5df1\u89c6\u4e3a\u5de6\u80a1\u53f3\u81c2\u7684\u201c\u53cc\u94c1\u536b\u201d\u4e4b\u4e00\u6218\u4e86\u4e2a\u5e73\u624b\uff0c\u5fc3\u4e2d\u559c\u7231\u5c55 \u767d\u5df2\u6781\uff0c\u624d\u8981\u51fa\u58f0\u559d\u6b62\uff0c\u8c01\u77e5\u4ed6\u4e24\u4eba\u4e92\u76f8\u8e6c\u89c6\u4e86\u4e00\u4f1a\uff0c\u7a81\u7136\u5927\u559d\u4e00\u58f0\uff0c\u53c8\u53cc\u53cc\u731b\u6251\u800c\u4e0a\u3002 \u3000\u3000\u201c\u556a\uff01\u556a\uff01\u556a\uff01\u2026\u2026\u201d \u3000\u3000\u63a5\u8fde\u6570\u58f0\u7206\u54cd\uff0c\u4e8c\u4eba\u5728\u7a7a\u4e2d\u53c8\u4e92\u6362\u4e86\u6570\u638c\uff0c\u4f9d\u7136\u5de6\u53f3\u5206\u5f00\uff0c\u98d8\u843d\u5730\u4e0a\u4e92\u76f8\u6012\u89c6\u7740\uff0c\u65e2\u672a \u5206\u51fa\u80dc\u8d25\uff0c\u4e5f\u4e0d\u53d1\u51fa\u4e00\u8a00\u3002\u8fd9\u79cd\u6253\u6cd5\uff0c\u5012\u662f\u524d\u6240\u672b\u89c1\u3002 \u3000\u3000\u201c\u94c1\u80cc\u9a7c\u9f99\u201d\u516c\u5b59\u695a\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff01\u771f\u6709\u4f60\u7684\uff0c\u7ade\u8ddf\u6211\u4eec\u8001\u9e4f\u6253\u4e86\u4e2a\u5e73\u624b\uff01\u201d \u3000\u3000\u201c\u94c1\u80cc\u9a7c\u9f99\u201d\u6b64\u8a00\u4e00\u51fa\uff0c\u201c\u94c1\u7ffc\u98de\u9e4f\u201d\u8138\u4e0a\u66f4\u611f\u6302\u4e0d\u4f4f\uff0c\u5927\u559d\u4e00\u58f0\uff0c\u65bd\u51fa\u4e86\u5341\u6210\u529f\u529b\uff0c \u53cc\u638c\u731b\u5411\u5c55\u81ea\u6dee\u51fa\u3002\u638c\u98ce\u5c71\u6d8c\uff0c\u5982\u72c2\u98ce\u5de8\u6d6a\u822c\u5411\u5c55\u767d\u6c79\u6d8c\u800c\u81f3\u3002 \u3000\u3000\u5c55\u81ea\u51db\u7136\u4e0d\u60e7\uff0c\u8eab\u8eaf\u4e00\u8eac\uff0c\u8fd0\u8db3\u4e86\u5468\u8eab\u529f\u52b2\uff0c\u4ee5\u53cc\u638c\u76f4\u5411\u88ad\u6765\u638c\u98ce\u8fce\u53bb\u3002 \u3000\u3000\u201c\u8f70\u201d\u7136\u4e00\u58f0\u5927\u9707\uff0c\u4e24\u80a1\u5f3a\u5927\u638c\u98ce\u649e\u51fb\u5728\u4e00\u8d77\uff0c\u52b2\u98ce\u56db\u5c04\uff0c\u5377\u6284\u626c\u5c18\u3002 \u3000\u3000\u5c18\u6c99\u5bbd\u626c\u4e2d\uff0c\u53ea\u89c1\u4e00\u6761\u4eba\u5f71\u51b2\u5929\u800c\u8d77\uff0c\u5b9b\u5982\u4e00\u5934\u5de8\u5927\u98de\u9e1f\uff0c\u51cc\u7a7a\u4e09\u4e08\uff0c\u53c8\u731b\u6251\u800c\u4e0b\uff0c\u534a \u7a7a\u4e2d\u53cc\u638c\u53c8\u81ea\u731b\u5288\u4e0b\u6765\u3002 \u3000\u3000\u539f\u6765\u662f\u201c\u94c1\u7ffc\u98de\u9e4f\u201d\u5168\u529b\u4e00\u51fb\uff0c\u672a\u80fd\u6253\u5012\u5c55\u767d\uff0c\u5df2\u4ed7\u7740\u94c1\u7ffc\u4f38\u8863\u4e4b\u52a9\uff0c\u51cc\u7a7a\u5411\u5c55\u767d\u65bd\u51fa \u5a01\u529b\u66f4\u5927\u7684\u4e00\u638c\u3002\u5c55\u767d\u201c\u9738\u738b\u4e3e\u9f0e\u201d\uff0c\u53cc\u638c\u9ad8\u4e3e\u8fc7\u80a9\uff0c\u5df2\u5411\u201c\u94c1\u7ffc\u98de\u9e4f\u201d\u91cd\u903e\u5c71\u5cb3\u7684\u638c\u52bf\u8fce \u53bb\u3002 \u3000\u3000\u53c8\u662f\u4e00\u58f0\u8f70\u7136\u5de8\u9707\uff0c\u76f4\u5982\u77f3\u7834\u5929\u60ca\uff0c\u52b2\u98ce\u56db\u6fc0\u7adf\u5e7f\u8fbe\u4e24\u4e08\u5f00\u5916\uff0c\u91d1\u5e9c\u9ad8\u624b\u7eb7\u7eb7\u60ca\u547c\u56db \u9000\u3002 \u3000\u3000\u201c\u94c1\u7ffc\u98de\u9e4f\u201d\u51cc\u7a7a\u4e0b\u6251\u7684\u8eab\u5f62\uff0c\u7115\u7136\u53c8\u5347\u9ad8\u4e09\u5c3a\uff0c\u4e34\u7a7a\u4e00\u6298\uff0c\u7fe9\u7136\u843d\u4e8e\u5730\u4e0b\u3002 \u3000\u3000\u201c\u94c1\u7ffc\u98de\u9e4f\u201d\u4e24\u53ea\u602a\u670d\u5706\u7741\uff0c\u77aa\u89c6\u7740\u5c55\u767d\uff0c\u9759\u7b49\u7740\u5c55\u767d\u5012\u4e0b\u56e0\u4e3a\u4ed6\u8fd9\u4e34\u7a7a\u2014\u638c\uff0c\u5df2\u65bd\u51fa \u4e86\u5168\u529b\uff0c\u65e0\u4eba\u80fd\u6321\u201c\u94c1\u7ffc\u795e\u529f\u201d\u51cc\u7a7a\u5168\u529b\u4e00\u51fb\u3002 \u3000\u3000\u4faf\u77e5\u5c55\u767d\u4f9d\u7136\u5b8c\u597d\u65e0\u6059\u5730\u7ad9\u5728\u90a3\u91cc\uff0c\u7a33\u5982\u5c71\u5cb3\uff0c\u6c14\u5b9a\u795e\u95f2\uff0c\u800c\u4e14\u4e00\u53cc\u5927\u773c\u775b\u795e\u5149\u7a81\u53d8\uff0c \u66f4\u663e\u51fa\u4e86\u65e0\u6bd4\u795e\u5a01\u3002 \u3000\u3000\u8fd9\u4e00\u6765\u4e0d\u4f46\u201c\u94c1\u7ffc\u98de\u9e4f\u201d\u695e\u4e86\uff0c\u8fde\u5728\u4e00\u65c1\u89c2\u6218\u7684\u201c\u7965\u9e9f\u516c\u5b50\u201d\u5144\u59b9\u3001\u201c\u94c1\u80cc\u9a7c\u9f99\u201d\u4ee5\u53ca \u91d1\u5e9c\u9ad8\u624b\uff0c\u65e0\u4e0d\u5927\u51fa\u610f\u5916\u3002\u201c\u94c1\u80cc\u9a7c\u9f99\u201d\u4e0e\u201c\u94c1\u7ffc\u98de\u9e4f\u201d\u529f\u529b\u5728\u4f2f\u4ef2\u4e4b\u95f4\uff0c\u53c8\u7d20\u77e5\u201c\u94c1\u7ffc\u98de \u9e4f\u201d\u201c\u94c1\u7ffc\u795e\u529f\u201d\u7684\u5389\u5bb3\uff0c\u5c24\u5176\u90a3\u51cc\u7a7a\u4e0b\u51fb\u7684\u5343\u94a9\u4e4b\u52bf\uff0c\u5c31\u8fde\u81ea\u5df1\u4e5f\u4e0d\u4e00\u5b9a\u6709\u628a\u63e1\u5b89\u7136\u63a5 \u4e0b\uff0c\u5982\u4eca\u89c1\u5c55\u767d\u7ade\u80fd\u63a5\u4f4f\u4e86\uff0c\u8fde\u8c6a\u8fc8\u98ce\u8da3\u7684\u8bdd\u90fd\u5fd8\u4e86\u8bf4\uff0c\u53ea\u624b\u637b\u989a\u4e0b\u7eb5\u987b\uff0c\u73af\u773c\u5706\u7741\uff0c\u671b\u7740 \u5c55\u767d\u5446\u5446\u53d1\u6014\uff0c\u5fc3\u8bf4\uff1a\u201c\u8fd9\u5a03\u513f\uff0c\u8fd9\u70b9\u5b50\u5e74\u7eaa\uff0c\u8fd9\u529f\u592b\u662f\u600e\u4e48\u7ec3\u7684\u2026\u2026\u201d\u5c55\u767d\u5374\u7a81\u7136\u5927\u559d\u4e00 \u58f0\uff1a\u201c\u4f60\u4e5f\u63a5\u5c0f\u7237\u4e00\u638c\u8bd5\u8bd5\uff01\u201d \u3000\u3000\u559d\u7f62\uff0c\u6c89\u8170\u584c\u80a9\uff0c\u6c14\u8d2f\u4e39\u7530\uff0c\u53cc\u81c2\u2014\u5708\u4e00\u63c9\uff0c\u53cc\u638c\u7f13\u7f13\u63a8\u51fa\uff0c\u6b63\u662f\u5b66\u81ea\u201c\u795e\u9a74\u94c1\u80c6\u201d\u7684 \u897f\u57df\u7edd\u5b66\u201c\u96f7\u97f3\u4f5b\u638c\u201d\u4e4b\u4e2d\u7684\u4e00\u62db\u201c\u897f\u5929\u96f7\u97f3\u201d\u3002 \u7b2c\u4e8c\u5341\u4e5d\u7ae0\u3000\u201c\u96f7\u97f3\u4f5b\u638c\u201d>> \u53e4\u9f99\u300a\u5251\u5ba2\u884c\u300b \u7b2c\u4e8c\u5341\u4e5d\u7ae0\u3000\u201c\u96f7\u97f3\u4f5b\u638c\u201d \u3000\u3000\u4e00\u80a1\u67d4\u97e7\u4e4b\u529b\uff0c\u968f\u7740\u5c55\u767d\u7684\u53cc\u638c\u6d8c\u51fa\uff0c\u867d\u7136\u4e0d\u89c1\u638c\u98ce\u52b2\u5578\uff0c\u4f46\u4e00\u80a1\u5f3a\u5927\u7684\u6697\u52b2\uff0c\u6fc0\u8361\u800c \u51fa\uff0c\u7adf\u9690\u9690\u631f\u7740\u9686\u9686\u96f7\u9e23\u4e4b\u58f0\u3002 \u3000\u3000\u201c\u94c1\u7ffc\u98de\u9e4f\u201d\u6b66\u529f\u76d6\u4e16\uff0c\u773c\u9ad8\u4e8e\u9876\uff0c\u89c1\u5c55\u767d\u63a8\u51fa\u8fd9\u8f7b\u98d8\u98d8\u7684\u4e00\u638c\uff0c\u7adf\u81ea\u8138\u4e0a\u53d8\u8272\uff0c\u65e0\u5948 \u76db\u540d\u4e4b\u4e0b\uff0c\u4ed6\u4e0d\u597d\u610f\u601d\u8eb2\u95ea\uff0c\u53c8\u52a0\u4e0a\u521a\u624d\u773c\u524d\u5c11\u5e74\u5df2\u786c\u63a5\u4e86\u81ea\u5df1\u4e24\u638c\uff0c\u867d\u77e5\u5c11\u4e2d\u65bd\u51fa\u638c\u529f\uff0c \u5fc5\u662f\u6b66\u6797\u5931\u4f20\u7edd\u5b66\uff0c\u4f46\u4e5f\u53ea\u6709\u54ac\u7259\u786c\u63a5\uff0c\u8fd0\u8d77\u5468\u8eab\u529f\u529b\uff0c\u4e3e\u8d77\u53cc\u638c\u5411\u6765\u52bf\u8fce\u53bb\u3002 \u3000\u3000\u201c\u94c1\u80cc\u9a7c\u9f99\u201d\u89c1\u5c55\u767d\u63a8\u51fa\u638c\u98ce\u6709\u5f02\uff0c\u7075\u667a\u4e00\u52a8\uff0c\u9661\u7136\u8bb0\u8d77\u8fd9\u53ea\u542c\u4f20\u8bf4\u3001\u5374\u672a\u4eb2\u89c1\u5f97\u897f\u57df \u7edd\u5b66\uff0c\u4e0d\u7531\u8131\u53e3\u60ca\u547c\u9053\uff1a\u201c\u96f7\u97f3\u4f5b\u638c\uff01\u5df4\u8d24\u5f1f\uff01\u5c0f\u5fc3\u2026\u2026\u201d \u3000\u3000\u53ef\u662f\u665a\u4e86\uff0c\u201c\u94c1\u80cc\u9a7c\u9f99\u201d\u60ca\u547c\u672b\u6bd5\uff0c\u201c\u94c1\u7ffc\u98de\u9e4f\u201d\u53cc\u638c\uff0c\u5df2\u4e0e\u5c55\u767d\u7684\u638c\u98ce\u76f8\u63a5\u3002 \u3000\u3000\u53ea\u542c\u60ca\u5929\u52a8\u5730\u7684\u4e00\u58f0\u5927\u9707\uff0c\u52b2\u98ce\u547c\u5578\u800c\u8d77\uff0c\u65cb\u56de\u6fc0\u8361\uff0c\u56db\u5c04\u800c\u51fa\uff0c\u5377\u77f3\u626c\u6c99\uff0c\u5c18\u5934\u6d8c\u8d77 \u8db3\u6709\u5e7f\u6570\u4e08\u9ad8\u3002 \u3000\u3000\u8f70\uff01\u8f70\uff01\u5de8\u54cd\uff0c\u9e23\u3002 \u3000\u3000\u518d\u770b\u201c\u94c1\u7ffc\u98de\u9e4f\u201d\u5df2\u9707\u51fa\u4e08\u5916\uff0c\u5f53\u573a\u683d\u5012\uff0c\u9762\u8272\u94c1\u9752\uff0c\u6c14\u606f\u7c97\u91cd\uff0c\u8eab\u4e0a\u94c1\u7ffc\u4e00\u9f13\u4e00\u9f13\uff0c \u5982\u8239\u5e06\u4e00\u822c\uff0c\u51e0\u88ab\u968f\u98ce\u98de\u8d77\u2026\u2026 \u3000\u3000\u60f3\u201c\u96f7\u97f3\u4f5b\u638c\u201d\uff0c\u8fde\u201c\u795e\u7334\u201d\u94c1\u51cc\u90fd\u4e0d\u6562\u786c\u63a5\uff0c\u867d\u7136\u5c55\u767d\u65b0\u5b66\u4e4d\u7ec3\uff0c\u521d\u6b21\u65bd\u5c55\uff0c\u4f46\u5a01\u529b \u4e5f\u975e\u540c\u5c0f\u53ef\u3002 \u3000\u3000\u5c1a\u4e8f\u201c\u94c1\u7ffc\u98de\u9e4f\u201d\u6709\u201c\u94c1\u7ffc\u795e\u8863\u201d\u62a4\u4f53\uff0c\u4e0d\u7136\u7684\u8bdd\uff0c\u65e9\u5df2\u8d1f\u4f24\u591a\u65f6\u4e86\u3002 \u3000\u3000\u4ee5\u201c\u94c1\u7ffc\u98de\u9e4f\u201d\u5728\u6c5f\u6e56\u4e0a\u7684\u58f0\u671b\uff0c\u7ade\u88ab\u4e00\u4e2a\u521d\u51fa\u9053\u7684\u5c11\u4e2d\u4e00\u638c\u9707\u9000\uff0c\u4e0d\u7531\u628a\u81ea\u5938\u4e3a\u201c\u6c5f \u5357\u7b2c\u4e00\u5bb6\u201d\u7684\u91d1\u5e9c\u79bb\u624b\u3001\u7965\u9e9f\u516c\u5b50\u4ee5\u4e0b\u7684\u5341\u6570\u4eba\u9707\u60ca\u5f97\u5446\u4e86\u2026\u2026 \u3000\u3000\u7a81\u542c\u8eab\u540e\u4e0d\u8fdc\u6709\u4eba\u559d\u91c7\u9053\uff1a\u201c\u4e00\u638c\u9707\u98de\u9e4f\uff0c\u597d\u96c4\u539a\u7684\u638c\u529b\uff01\u201d \u3000\u3000\u53c8\u542c\u4e00\u4e2a\u5c11\u5973\u5a07\u547c\u9053\uff1a\u201c\u5c55\u54e5\u54e5\uff01\u201d \u3000\u3000\u4f17\u4eba\u6115\u7136\u56de\u987e\uff0c\u53ea\u89c1\u5c71\u8def\u4e0a\u6765\u4e86\u4e00\u4e2a\u5e03\u8863\u7f8e\u5c11\u5e74\uff0c\u53ca\u4e00\u4e2a\u7eaf\u7f8e\u65e0\u6bd4\u7684\u9526\u8863\u5c11\u5973\u3002 \u3000\u3000\u8fd9\u5c11\u5973\u5341\u516d\u3001\u4e03\u5c81\uff0c\u6167\u8d28\u5929\u751f\uff0c\u5a07\u7f8e\u5982\u82b1\u3002\u91d1\u5f69\u51e4\u867d\u6709\u201c\u6c5f\u5357\u7b2c\u4e00\u7f8e\u4eba\u201d\u4e4b\u79f0\uff0c\u4f46\u8fd9\u5c11 \u5973\u7eaf\u6d01\u81ea\u7136\uff0c\u7f8e\u4e3d\u5e76\u4e0d\u4e0b\u4e8e\u91d1\u5f69\u51e4\uff0c\u5c24\u5176\u5979\u4e00\u8138\u7a1a\u6c14\uff0c\u663e\u51fa\u4e00\u6d3e\u9752\u6625\u6c14\u606f\uff0c\u4e0e\u91d1\u5f69\u51e4\u534e\u4e3d\u4e4b \u7f8e\u6bd4\u8f83\u4e4b\u4e0b\uff0c\u53e6\u6709\u4e00\u756a\u6e05\u65b0\u6c14\u8c61\u3002 \u3000\u3000\u8fd9\u9752\u6625\u5c11\u5973\uff0c\u6b63\u662f\u5c55\u5a49\u513f\u3002 \u3000\u3000\u518d\u770b\u90a3\u5e03\u8863\u7f8e\u5c11\u5e74\uff0c\u9762\u5982\u6e29\u7389\uff0c\u5507\u5982\u4e39\u6d82\uff0c\u76ee\u5982\u6717\u661f\uff0c\u7709\u5206\u516b\u5f69\uff0c\u733f\u81c2\u8702\u8170\uff0c\u957f\u8eab\u7389 \u7acb\uff0c\u867d\u7136\u4ec5\u662f\u4e00\u8eab\u5e03\u8863\uff0c\u4f5c\u6a35\u519c\u6253\u626e\uff0c\u4f46\u4e5f\u63a9\u4e0d\u4f4f\u4ed6\u7eaf\u7f8e\u7684\u672c\u8d28\u3002\u8bba\u4fca\u7f8e\u4e0d\u4e0b\u4e8e\u5c55\u767d\uff0c\u4f46\u6bd4 \u5c55\u767d\u6f47\u6d12\uff0c\u8bba\u6f47\u6d12\u4e0d\u4e9a\u4e8e\u4ee5\u4fca\u7f8e\u98ce\u6d41\u8457\u79f0\u7684\u7965\u9e9f\u516c\u5b50\uff0c\u4f46\u53c8\u6bd4\u7965\u9e9f\u516c\u5b50\u663e\u5f97\u6734\u5b9e\uff0c\u5468\u8eab\u4e0d\u5e26 \u4e00\u70b9\u5bcc\u8d35\u6c14\u3002 \u3000\u3000\u8fd9\u5e03\u8863\u7f8e\u5c11\u5e74\uff0c\u5c55\u767d\u5374\u4e0d\u8ba4\u8bc6\uff0c\u4e0d\u8fc7\uff0c\u770b\u5230\u5a49\u513f\u4e0e\u4ed6\u5728\u4e00\u8d77\u51fa\u73b0\uff0c\u5fc3\u4e2d\u6709\u4e00\u79cd\u8bf4\u4e0d\u51fa\u7684 \u9178\u6da9\u4e4b\u6627\u3002\u8fd9\u9053\u7406\u5c55\u767d\u81ea\u5df1\u4e5f\u8bf4\u4e0d\u51fa\u4e2a\u6240\u4ee5\u7136\u6765\uff0c\u4ed6\u672c\u6765\u5bf9\u5a49\u513f\u4e0d\u5b58\u4e00\u70b9\u79c1\u5fc3\uff0c\u4e0d\u77e5\u4e3a\u4ec0\u4e48 \u89c1\u5a49\u513f\u4e0e\u4e00\u4fca\u7f8e\u5c11\u5e74\u5728\u4e00\u8d77\uff0c\u5fc3\u91cc\u4f1a\u611f\u5230\u4e0d\u6109\u5feb\u3002 \u3000\u3000\u5a49\u513f\u5374\u4e0d\u7ba1\u4f17\u4eba\u4f5c\u5982\u4f55\u60f3\u6cd5\uff0c\u53d1\u73b0\u5c55\u767d\u540e\uff0c\u5a07\u547c\u4e00\u58f0\uff0c\u5f20\u5f00\u53cc\u81c2\uff0c\u5982\u4e00\u53ea\u5c0f\u9e1f\u4e00\u822c\uff0c\u98de \u6251\u5411\u5c55\u767d\uff0c\u7ade\u6beb\u65e0\u5fcc\u8bb3\u5730\u53cc\u624b\u62b1\u4f4f\u5c55\u767d\u81c2\u8180\u4e00\u9635\u6447\u64bc\uff0c\u5e76\u9ad8\u5174\u5730\u53eb\u9053\uff1a\u5c55\u54e5\u54e5\uff01\u4f60\u7684\u6b66\u529f\u5b66 \u6210\u4e86\uff1f\u771f\u597d\u3002\u3002\u3002\u201d \u3000\u3000\u8d77\u521d\u5c55\u767d\u89c1\u5a49\u513f\u4e0e\u4e00\u5e03\u8863\u5c11\u5e74\u540c\u65f6\u51fa\u73b0\uff0c\u5fc3\u4e2d\u867d\u6709\u9178\u6e9c\u6e9c\u7684\u611f\u89c9\uff0c\u4f46\u8138\u4e0a\u5e76\u672a\u8868\u73b0\u51fa \u6765\u3002\u5982\u4eca\uff0c\u5a49\u513f\u5bf9\u5c55\u767d\u8fd9\u4e00\u4eb2\u70ed\u7684\u8868\u793a\uff0c\u90a3\u5e03\u8863\u7f8e\u5c11\u5e74\u5374\u7115\u7136\u8272\u53d8\uff0c\u4fca\u7f8e\u7684\u8138\u4e0a\u95ea\u8fc7\u4e00\u62b9\u5992 \u6068\u4e4b\u8272\uff0c\u968f\u81ea\u51b7\u7b11\u9053\uff1a\u539f\u6765\u4f60\u4eec\u4e8c\u4f4d\u65e9\u5c31\u8ba4\u8bc6\u2026\u2026\u201d \u3000\u3000\u91d1\u5f69\u51e4\u539f\u5148\u4e5f\u4e3a\u5e03\u8863\u7f8e\u5c11\u5e74\u4fca\u7f8e\u98ce\u4eea\u6240\u5438\u5f15\uff0c\u4e00\u53cc\u660e\u5a9a\u7684\u7f8e\u76ee\uff1b\u6014\u6014\u5730\u671b\u7740\u4ed6\uff0c\u73b0\u5728\u4e00 \u89c1\u5a49\u513f\u5bf9\u5c55\u767d\u5982\u6b64\u4eb2\u70ed\uff0c\u6536\u56de\u4e86\u76ee\u5149\uff0c\u8f6c\u671b\u7740\u5c55\u767d\u4e0e\u5a49\u513f\uff0c\u4fca\u7f8e\u5982\u82b1\u7684\u8138\u4e0a\u4e5f\u6d41\u9732\u51fa\u5992\u5fcc\u4e4b \u8272\uff0c\u7ea2\u5507\u4e00\u6487\u9053\uff1a\u4e0d\u4f46\u8ba4\u8bc6\uff0c\u770b\u6837\u5b50\u4eba\u5bb6\u4e24\u4e2a\u4eba\u60c5\u611f\u8fd8\u5f88\u597d\u5450\u2026\u2026\u201d \u3000\u3000\u7965\u9e9f\u516c\u5b50\u80f8\u6000\u5374\u6bd4\u8f83\u5f00\u9614\uff0c\u800c\u4e14\u4ed6\u96c4\u624d\u5927\u7565\uff0c\u6709\u5e7f\u63fd\u5929\u4e0b\u82f1\u624d\u3001\u4ee5\u8c0b\u5927\u4e3e\u7684\u58ee\u5fd7\uff0c\u89c1\u5e03 \u8863\u5c11\u5e74\u4eea\u8868\u4e0d\u89c1\uff0c\u968f\u4e00\u62b1\u62f3\u9053\uff1a\u5728\u4e0b\u7965\u9e9f\uff0c\u672a\u8bf7\u6559\u5c11\u5e74\u82f1\u96c4\u9ad8\u59d3\u5927\u540d\uff1f\u201d \u3000\u3000\u5e03\u8863\u7f8e\u5c11\u5e74\u9762\u8272\u53c8\u4e00\u53d8\uff0c\u7acb\u5411\u7965\u9e9f\u516c\u5b50\u4e00\u62b1\u62f3\u9053\uff1a\u5931\u656c\uff01\u539f\u6765\u662f\u5357\u4eac\u57ce\u7684\u7965\u9e9f\u516c\u5b50\uff01\u5bd2 \u751f\u7aef\u65b9\uff0c\u4eca\u65e5\u6709\u5e78\u8bc6\u8346\uff0c\u771f\u4e43\u4e09\u751f\u6709\u5e78\uff01 \u3000\u3000\u5e03\u8863\u7f8e\u5c11\u5e74\u8fd9\u4e00\u62a5\u59d3\u540d\uff0c\u4f17\u4eba\u65e0\u4e0d\u52a8\u5bb9\uff0c\u770b\u4e0d\u51fa\u4ed6\u7adf\u662f\u4e0e\u6837\u9e9f\u516c\u5b50\u5728\u6b66\u6797\u4e2d\u9f50\u4eab\u76db\u540d\u7684 \u7aef\u65b9\u516c\u5b50\u3002 \u3000\u3000\u7aef\u65b9\u516c\u5b50\u8fd9\u4e00\u62a5\u59d3\u540d\uff0c\u7965\u9e9f\u516c\u5b50\u8138\u8272\u4e5f\u81ea\u53d8\u4e86\u4e00\u4e0b\uff0c\u6df1\u77e5\u5bf9\u65b9\u4e0e\u81ea\u5df1\u5206\u5ead\u6297\u793c\uff0c\u4e0d\u518d\u5174 \u6536\u5f55\u62c9\u62e2\u4e4b\u610f\uff0c\u5374\u6697\u8d77\u8f83\u91cf\u538b\u5012\u5bf9\u65b9\u4e4b\u5fc3\uff0c\u4f46\u4ecd\u7136\u54c8\u54c8\u4e00\u9635\u5927\u7b11\u9053\uff1a\u771f\u662f\u5e78\u4f1a\u4e86\uff0c\u60f3\u4e0d\u5230\u5c0a \u9a7e\u7adf\u662f\u9f0e\u9f0e\u5927\u540d\u7684\u7aef\u65b9\u516c\u5b50\uff01\u201d \u3000\u3000\u5a49\u513f\u7a81\u5728\u4e00\u65c1\u63d2\u53e3\u9053\uff1a\u201c\u771f\u6709\u610f\u601d\uff01\u773c\u524d\u662f\u2018\u7965\u9e9f\u70ed\u80a0\u2019\u3001\u2018\u98d8\u96f6\u7aef\u65b9\u2019\uff0c\u5982\u679c\u628a\u6211\u54e5 \u54e5\u53eb\u6765\uff0c\u518d\u627e\u5230\u2018\u5b89\u4e50\u98ce\u6d41\u2019\uff0c\u5c82\u4e0d\u662f\u6b66\u6797\u56db\u5927\u516c\u5b50\u4f1a\u9f50\u4e86\u5417\uff01\u201d \u3000\u3000\u7965\u9e9f\u516c\u5b50\u95fb\u8a00\u56de\u987e\uff0c\u7591\uff0c\u9053\uff1a\u8fd9\u4e00\u8bf4\uff0c\u5c0f\u59d0\u7684\u54e5\u54e5\u5c31\u662f\u51cc\u98ce\u516c\u5b50\u4e86\uff01\u201d \u3000\u3000\u5a49\u513f\u70b9\u5934\u7b11\u9053\uff1a\u4e00\u70b9\u4e0d\u9519\uff01\u201d \u3000\u3000\u7965\u9e9f\u516c\u5b50\u8c6a\u6c14\u5e72\u4e91\u5730\u7b11\u9053\uff1a\u201c\u90a3\u4e48\u5c31\u8bf7\u6155\u5bb9\u5c0f\u59d0\u7ed9\u4ee4\u5144\u5e26\u4e00\u4e2a\u53e3\u4fe1\uff0c\u5c31\u8bf4\u5357\u4eac\u7965\u9e9f\uff0c\u4e9f \u601d\u4e00\u4f1a\uff01\u2026\u2026\u201d \u3000\u3000\u5a49\u513f\u5658\u5634\u9053\uff1a\u201c\u6211\u4e0d\u59d3\u6155\u5bb9\uff0c\u6211\u59d3\u5c55\uff01\u201d \u3000\u3000\u7965\u9e9f\u516c\u5b50\u4e00\u695e\u9053\uff1a\u5c0f\u59d0\u4e0e\u51cc\u98ce\u516c\u5b50\u4e0d\u662f\u4eb2\u5144\u59b9\u5417\uff1f\u201d \u3000\u3000\u5a49\u513f\u66f4\u4e0d\u9ad8\u5174\u9053\uff1a\u8c01\u8bf4\u6211\u4e0e\u54e5\u54e5\u4e0d\u662f\u4eb2\u5144\u59b9\uff1f\u6211\u59d3\u5c55\u2014\u2014\u201d\u8bf4\u5230\u8fd9\u91cc\uff0c\u5979\u660e\u5a9a\u7684\u5927\u773c\u775b \u7784\u4e86\u5c55\u767d\u4e00\u773c\uff0c\u4ee5\u7ea4\u624b\u63e1\u4f4f\u5c55\u767d\u7684\u624b\u9053\uff1a\u662f\u8ddf\u5c55\u54e5\u54e5\u4e00\u4e2a\u59d3\uff01\u201d \u3000\u3000\u5c55\u81ea\u542c\u5f97\u5fc3\u5934\u4e00\u9707\uff0c\u56fa\u7136\u4ed6\u65e9\u77e5\u9053\u5a49\u513f\u59d3\u5c55\u7684\u539f\u56e0\uff0c\u4f46\u4e5f\u60f3\u4e0d\u5230\u5a49\u513f\u5f53\u7740\u8fd9\u4e48\u591a\u4eba\u7684\u9762 \u524d\uff0c\u7adf\u6beb\u4e0d\u907f\u5acc\u5730\u8bf4\u51fa\u5982\u6b64\u591a\u60c5\u800c\u53c8\u5927\u80c6\u7684\u8bdd\u3002 \u3000\u3000\u540c\u65f6\uff0c\u4ed6\u66f4\u611f\u5230\u60ca\u5947\u7684\u662f\uff0c\u6b66\u6797\u56db\u516c\u5b50\u7684\u7236\u4eb2\u90fd\u662f\u6614\u5e74\u201c\u6c5f\u5357\u4e03\u4fa0\u201d\u4e4b\u4e2d\u7684\u4eba\u7269\uff0c\u600e\u4e48 \u7adf\u4e92\u4e0d\u76f8\u8bc6\uff1f\u800c\u4e14\u5bf9\u5404\u81ea\u7684\u5bb6\u65cf\u4e2d\u4e4b\u4e8b\uff0c\u4e5f\u4f3c\u4e4e\u9887\u4e3a\u964c\u751f\uff1f\u81ea\u5df1\u7684\u4e8b\uff0c\u4ed6\u4eec\u4e0d\u77e5\u9053\uff0c\u8fd8\u662f\u7236 \u4eb2\u88ab\u4ed6\u4eec\u5bb3\u6b7b\uff0c\u4ed6\u4eec\u907f\u800c\u4e0d\u8a00\uff0c\u96be\u9053\u56db\u5927\u8c6a\u95e8\u4e4b\u4e2d\uff0c\u4e5f\u4e0d\u4e92\u76f8\u5f80\u6765\u5417\uff1f \u3000\u3000\u5c82\u4e0d\u77e5\u5c55\u767d\u6b63\u731c\u4e2d\u4e86\uff0c\u5f53\u5e74\u201c\u6c5f\u5357\u4e03\u4fa0\u201d\u540e\u534a\u671f\u5404\u81ea\u5a36\u59bb\u6210\u5bb6\uff0c\u89c5\u5730\u9690\u5c45\uff0c\u4e3a\u4e86\u907f\u514d\u5728 \u6c5f\u6e56\u4e0a\u6240\u6811\u4ec7\u654c\u81f3\u5bb6\u4e2d\u5bfb\u4ec7\uff0c\u90fd\u662f\u5728\u6781\u7aef\u79d8\u5bc6\u4e4b\u4e0b\u8fdb\u884c\u7684\uff0c\u540e\u6765\u53d1\u751f\u4e86\u6d1e\u5ead\u6e56\u593a\u5b9d\u4e4b\u53d8\uff0c\u4ed6 \u4eec\u516d\u4eba\u6740\u6b7b\u4e86\u7ed3\u4e49\u76df\u5144\u5c55\u4e91\u5929\uff0c\u74dc\u5206\u5b9d\u85cf\uff0c\u5404\u81ea\u56de\u5bb6\uff0c\u5c31\u66f4\u5c11\u5f80\u6765\uff0c\u800c\u4e14\u5bf9\u5f53\u5e74\u201c\u6c5f\u5357\u4e03 \u4fa0\u201d\u4e4b\u4e8b\uff0c\u5728\u59bb\u5b50\u513f\u5973\u9762\u524d\u4e5f\u7edd\u53e3\u4e0d\u63d0\uff0c\u56e0\u6b64\u201c\u6c5f\u5357\u4e03\u4fa0\u201d\u7684\u540e\u4eba\uff0c\u5f88\u5c11\u77e5\u9053\u7236\u6267\u8f88\u7684\u9690 \u79d8\u3002 \u3000\u3000\u53ea\u6709\u6155\u5bb9\u6db5\u4e0e\u6a0a\u975e\u79c1\u4ea4\u5f25\u7b03\uff0c\u4e8c\u4eba\u7ee7\u7eed\u5f80\u6765\uff0c\u6ca1\u60f3\u5230\u6155\u5bb9\u6db5\u7684\u59bb\u5b50\u662f\u51fa\u4e86\u540d\u7684\u7f8e\u4eba\uff0c\u6155 \u5bb9\u6db5\u53c8\u5992\u5fc3\u5947\u91cd\uff0c\u4ed6\u7684\u59bb\u5b50\u4e0e\u4efb\u4f55\u7537\u6027\u63a5\u8fd1\uff0c\u54ea\u6015\u662f\u4e00\u65e0\u903e\u8d8a\uff0c\u4e5f\u4f1a\u5f15\u8d77\u4ed6\u7684\u5992\u706b\uff0c\u505a\u51fa\u4e9b \u5931\u53bb\u7406\u6027\u4e4b\u4e3e\u3002\u6709\u4e00\u6b21\u5927\u6691\u5929\u6a0a\u975e\u592b\u59bb\u53bb\u6155\u5bb9\u6db5\u5bb6\u4f5c\u5ba2\uff0c\u6070\u597d\u6155\u5bb9\u6db5\u4e0d\u5728\u5bb6\uff0c\u56e0\u4e3a\u9177\u6691\u708e \u70ed\uff0c\u6155\u5bb9\u6db5\u592b\u4eba\u62db\u5f85\u6a0a\u592a\u592a\u5728\u6d74\u5ba4\u51b2\u6fa1\uff0c\u6a0a\u975e\u4e00\u4eba\u5728\u5eca\u4e0b\u5c0f\u5750\uff0c\u5750\u5728\u51c9\u98ce\u5439\u62c2\u4e0b\u7761\u7740\u4e86\u3002\u4e0d \u77e5\u9053\u4ed6\u5750\u7684\u5730\u65b9\u6b63\u662f\u6155\u5bb9\u592b\u4eba\u5bdd\u5ba4\u7684\u95e8\u53e3\uff0c\u88ab\u6155\u5bb9\u6db5\u5f52\u6765\u649e\u89c1\u3002\u4ee5\u4e3a\u4ed6\u7684\u59bb\u5b50\u4e0e\u6a0a\u975e\u505a\u51fa\u6696 \u6627\u4e4b\u4e8b\uff0c\u53c8\u542c\u5230\u6d74\u5ba4\u7684\u6c34\u58f0\uff0c\u51b2\u5929\u5927\u6012\uff0c\u4e5f\u4e0d\u95ee\u660e\u767d\u4e8b\u60c5\u771f\u50cf\uff0c\u7acb\u5373\u95ef\u5165\u6d74\u5ba4\u4e4b\u4e2d\u3002\u5374\u6b63\u597d \u770b\u5230\u6a0a\u975e\u592a\u592a\u7684\u88f8\u4f53\uff0c\u6a0a\u975e\u4e4b\u59bb\u7f9e\u4e0d\u53ef\u6291\uff0c\u6155\u5bb9\u592b\u4eba\u5927\u9a82\u6155\u5bb9\u6db5\u4e0d\u61c2\u89c4\u77e9\uff0c\u4e00\u5435\u4e00\u95f9\uff0c\u4e11\u95fb \u5168\u5e9c\u7686\u77e5\uff0c\u6a0a\u975e\u4e0e\u59bb\u5b50\u5306\u5306\u79bb\u53bb\u3002\u6ca1\u60f3\u5230\u8c23\u8a00\u6ee1\u5929\u98de\uff0c\u7ade\u4f20\u8a00\u6155\u5bb9\u6db5\u4e0e\u5728\u6d74\u5ba4\u4e2d\u6613\u59bb\u53d6\u4e50\uff0c \u6a0a\u975e\u4e4b\u59bb\u6027\u70c8\uff0c\u5dee\u6124\u81ea\u6740\u3002 \u3000\u3000\u56e0\u6b64\uff0c\u5f15\u8d77\u5341\u6570\u5e74\u540e\u6a0a\u6c0f\u4e09\u6770\u4e00\u9e3e\uff0c\u7387\u9886\u6570\u5341\u6b66\u6797\u9ad8\u624b\uff0c\u5230\u6155\u5bb9\u6db5\u5bb6\u4e2d\u5bfb\u4ec7\u53ae\u6740\u7684\u90a3\u4e00 \u5e55\u3002\u2026\u2026 \u3000\u3000\u6b64\u4e8b\u6682\u4e14\u4e0d\u63d0\uff0c\u4e14\u8bf4\u5a49\u513f\u8bf4\u5c55\u662f\u8ddf\u5c55\u767d\u4e00\u4e2a\u59d3\uff0c\u7965\u9e9f\u516c\u5b50\u604d\u7136\u800c\u609f\uff0c\u7b11\u9053\uff1a\u201c\u539f\u6765\u6155\u5bb9 \u5c0f\u59d0\u4e0e\u5c55\u5144\u5df2\u7ed3\u79e6\u664b\u4e4b\u597d\uff0c\u771f\u4e43\u90ce\u624d\u5973\u8c8c\uff0c\u5929\u914d\u826f\u7f18\uff0c\u53ef\u559c\uff01\u53ef\u8d3a\uff01\u2026\u2026\u201d \u3000\u3000\u5176\u5b9e\u7965\u9e9f\u516c\u5b50\u662f\u9519\u4f1a\u610f\u4e86\uff0c\u6b64\u8a00\u4e00\u51fa\uff0c\u5c55\u767d\u3001\u5a49\u513f\u53cc\u53cc\u7f9e\u5f97\u9762\u5e03\u7ea2\u4e91\uff0c\u91d1\u5f69\u51e4\u4e0e\u7aef\u65b9\u516c \u4e8e\u5374\u540c\u65f6\u8138\u4e0a\u6d6e\u8d77\u5931\u671b\u7684\u795e\u8272\u2026\u3002\xb7\u5c55\u767d\u4fca\u8138\u901a\u7ea2\uff0c\u6025\u6025\u5426\u8ba4\u9053\uff1a\u201c\u516c\u5b50\u4e0d\u77e5\u4e8b\u5b9e\u771f\u50cf\uff0c\u600e \u53ef\u4e71\u8bf4\uff01\u6211\u4eec\u5e76\u6ca1\u6709\u2026\u2026\u201d \u3000\u3000\u672a\u7b49\u5c55\u767d\u7684\u8bdd\u8bf4\u5b8c\uff0c\u7aef\u65b9\u516c\u5b50\u5df2\u6389\u8f6c\u5934\u5927\u6b65\u800c\u53bb\u3002 \u3000\u3000\u7965\u9e9f\u516c\u5b50\u5374\u6ca1\u6709\u7406\u4f1a\u5c55\u767d\u7684\u5426\u8ba4\uff0c\u89c1\u7aef\u65b9\u516c\u5b50\u4e0d\u8f9e\u800c\u522b\uff0c\u7ade\u626c\u58f0\u53eb\u9053\uff1a\u201c\u7aef\u65b9\u516c\u4e88\u6162 \u6b65\uff01\u7965\u9e9f\u8fd8\u6709\u8bdd\u8bf4\uff01\u201d \u3000\u3000\u7aef\u65b9\u516c\u5b50\u5374\u5934\u4e5f\u4e0d\u56de\uff0c\u62b1\u62f3\u8fc7\u80a9\uff0c\u62f1\u624b\u9053\uff1a\u7aef\u65b9\u5c1a\u6709\u6025\u4e8b\uff0c\u544a\u8f9e\u4e86\uff01\u201d \u3000\u3000\u7965\u9e9f\u516c\u5b50\u89c1\u7aef\u65b9\u516c\u4e8e\u8bf4\u8d70\u5c31\u8d70\uff0c\u547c\u5524\u4e0d\u56de\uff0c\u7ade\u4e00\u6643\u80a9\u8dc3\u81f3\u7aef\u65b9\u516c\u5b50\u8eab\u524d\uff0c\u5728\u7aef\u65b9\u516c\u5b50\u9762 \u524d\u4e00\u7ad9\uff0c\u8bf4\u9053\uff1a\u201c\u7aef\u65b9\u516c\u5b50\u8981\u8d70\uff0c\u4e5f\u4e0d\u6025\u4e8e\u4e00\u65f6\u5440\uff01\u7965\u9e3e\u8bf7\u95ee\uff0c\u8d35\u516c\u5b50\u4e0d\u5728\u676d\u5dde\u897f\u6e56\u9038\u56ed\u7eb3 \u4eab\u6e05\u798f\uff0c\u5374\u8fdc\u6765\u91d1\u9675\u8fd1\u90ca\u8352\u5c71\uff0c\u4e0d\u77e5\u6709\u4f55\u8d35\u5e72\uff1f\u201d \u3000\u3000\u7aef\u65b9\u516c\u5b50\u9762\u9732\u4e0d\u6109\u4e4b\u8272\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u5c71\u6797\u65e0\u4e3b\uff0c\u6211\u7aef\u65b9\u98d8\u96f6\u56db\u6d77\uff0c\u96be\u9053\u8fd9\u4e2d\u539f\u8352\u5c71\uff0c \u4e5f\u4e0d\u51c6\u7aef\u65b9\u8d70\u4e86\u5417\uff1f\u201d \u3000\u3000\u7965\u9e9f\u516c\u5b50\u51b7\u7b11\u9053\uff1a\u201c\u5929\u4e0b\u8def\u5929\u4e0b\u4eba\u8d70\uff0c\u8c01\u53c8\u6562\u62e6\u963b\u4fa0\u8e2a\u904d\u56db\u6d77\u7684\u7aef\u65b9\u516c\u5b50\uff1f\u4f46\u4eca\u5929\u60c5\u51b5 \u7279\u6b8a\uff0c\u4e00\u5b9a\u8981\u8bf7\u8d35\u516c\u5b50\u8bf4\u51fa\u4e2a\u539f\u56e0\u6765\uff01\u201d \u3000\u3000\u7965\u9e9f\u516c\u5b50\u7d20\u5e38\u8c08\u7b11\u98ce\u751f\uff0c\u5c31\u662f\u9047\u5230\u7d27\u5f20\u6025\u8981\u4e4b\u4e8b\uff0c\u4e5f\u662f\u4ece\u5bb9\u9547\u5b9a\uff0c\u8138\u4e0a\u8f7b\u6613\u4e0d\u9732\u75d5\u8ff9\u3002 \u5982\u4eca\u8ddf\u7aef\u65b9\u516c\u5b50\u89c1\u9762\uff0c\u7adf\u8138\u73b0\u6012\u5bb9\uff0c\u8a00\u950b\u7280\u5229\uff0c\u8fd9\u662f\u5f88\u5c11\u89c1\u7684\uff0c\u56e0\u6b64\u91d1\u5e9c\u6b66\u6797\u9ad8\u624b\uff0c\u4e0d\u7ea6\u800c \u540c\u5730\u56f4\u4e86\u4e0a\u6765\uff0c\u800c\u4e14\u9690\u7136\u6697\u5bf9\u7aef\u65b9\u516c\u5b50\u91c7\u53d6\u5305\u56f4\u4e4b\u52bf\uff0c\u770b\u60c5\u5f62\u53ea\u8981\u7965\u9e9f\u516c\u5b50\u4e00\u58f0\u4ee4\u4e0b\uff0c\u91d1\u5e9c \u9ad8\u624b\u4fbf\u8981\u52a8\u624b\u62ff\u4eba\u4e86\uff01\u2026\u2026 \u3000\u3000\u7aef\u65b9\u516c\u5b50\u987e\u81ea\u4fca\u76ee\u6d41\u76fc\uff0c\u56db\u5468\u6253\u91cf\u4e86\u4e00\u4e0b\uff0c\u7a81\u7136\u4ef0\u5929\u5927\u7b11\u9053\uff1a\u201c\u6211\u82e5\u662f\u4e0d\u613f\u8bf4\u51fa\u539f\u56e0 \u5462\uff1f\u201d \u3000\u3000\u7965\u9e9f\u516c\u5b50\u9762\u8272\u4e00\u6c89\uff0c\u9053\uff1a\u201c\u90a3\u8bf4\u4e0d\u5f97\u7965\u9e9f\u5c31\u8981\u7559\u9a7e\u4e86\uff01\u201d\u7aef\u65b9\u516c\u5b50\u9053\uff1a\u201c\u83ab\u975e\u4ed7\u7740\u4eba \u591a\uff1f\u201d \u3000\u3000\u7965\u9e9f\u516c\u5b50\u9053\uff1a\u5c31\u7965\u9e9f\u4e00\u4eba\uff0c\u4e5f\u53ef\u7559\u5f97\u4f4f\u8d35\u516c\u5b50\uff01\u201d \u3000\u3000\u6b64\u65f6\u201c\u94c1\u80cc\u9a7c\u9f99\u201d\u5927\u8e0f\u6b65\u8d70\u4e0a\u524d\u6765\u9053\uff1a\u6349\u62ff\u4f60\u8fd9\u5c0f\u8f88\uff0c\u8fd8\u7528\u5f97\u7740\u516c\u5b50\u52a8\u624b\uff0c\u5f85\u516c\u5b59\u695a\u9886 \u6559\u5927\u540d\u6ee1\u5929\u4e0b\u7684\u7aef\u65b9\u516c\u5b50\uff0c\u7a76\u7adf\u6709\u4f55\u9ad8\u6df1\u827a\u4e1a\uff01\u201d \u3000\u3000\u7965\u9e9f\u516c\u5b50\u4e00\u4e3e\u624b\uff0c\u963b\u6b62\u4f4f\u6c14\u52bf\u6c79\u6c79\u7684\u94c1\u80cc\u9a7c\u9f99\uff0c\u4ecd\u7136\u5411\u7aef\u65b9\u516c\u5b50\u8bf4\u9053\uff1a\u201c\u5b9e\u8bdd\u544a\u8bc9\u8d35\u516c \u5b50\u5427\uff0c\u7965\u9e9f\u5bb6\u4e2d\u4e22\u4e86\u4e00\u70b9\u4e1c\u897f\uff0c\u843d\u5728\u6b64\u5c71\u4e2d\uff0c\u6070\u5de7\u8d35\u516c\u5b50\u5728\u6b64\u5c71\u51fa\u73b0\uff0c\u8fd9\u5c31\u4e0d\u80fd\u4e0d\u53eb\u4eba\u8d77 \u7591\uff01\u201d \u3000\u3000\u7aef\u65b9\u516c\u5b50\u51b7\u7b11\u9053\uff1a\u201c\u53ea\u6015\u4e0d\u662f\u4f60\u91d1\u5e9c\u4e22\u4e1c\u897f\uff0c\u800c\u662f\u5077\u62a2\u6765\u53c8\u5931\u53bb\u4e86\u5427\uff1f\u201d \u3000\u3000\u7aef\u65b9\u516c\u5b50\u6b64\u8a00\u4e00\u51fa\uff0c\u7965\u9e9f\u516c\u5b50\u9762\u8272\u5927\u53d8\uff0c\u731b\u559d\u4e00\u58f0\uff1a\u201c\u679c\u7136\u662f\u4f60\u6240\u4e3a\uff01\u63a5\u62db\uff01\u201d \u3000\u3000\u58f0\u51fa\u62db\u5230\uff0c\u5de6\u624b\u53cc\u6307\u731b\u6233\u7aef\u65b9\u516c\u5b50\u53cc\u76ee\uff0c\u53f3\u638c\u659c\u780d\u7aef\u65b9\u516c\u5b50\u5de6\u80a9\uff0c\u4e00\u62db\u4e24\u5f0f\uff0c\u800c\u4e14\u52b2\u75be \u5982\u98ce\uff0c\u51fa\u624b\u4e0d\u89c1\u3002 \u3000\u3000\u7aef\u65b9\u516c\u5b50\u5927\u5531\u4e00\u58f0\uff1a\u201c\u6765\u5f97\u597d\uff01\u201d \u3000\u3000\u6643\u80a9\u6518\u53f3\u81c2\uff0c\u5de7\u6253\u7965\u9e9f\u516c\u5b50\u5de6\u8098\u5173\u5143\uff0c\u5de6\u624b\u5982\u62e2\u731b\u5411\u7965\u9e9f\u516c\u5b50\u53f3\u8170\u6263\u53bb\uff0c\u7834\u62db\u6253\u62db\uff0c\u5947 \u5965\u65e0\u6bd4\u3002 \u3000\u3000\u4e24\u4eba\u624b\u6cd5\u5747\u5feb\uff0c\u4e09\u62c6\u4e24\u89e3\uff0c\u6643\u773c\u4e92\u76f8\u653b\u51fb\u4e86\u4e09\u4e94\u62db\uff0c\u201c\u7830\uff01 \u3000\u3000\u7830\uff01\u201d\u4e24\u58f0\u7206\u54cd\uff0c\u4e8c\u4eba\u53c8\u786c\u5bf9\u4e86\u4e24\u638c\uff0c\u8eab\u5f62\u500f\u7136\u5206\u5f00\u3002 \u3000\u3000\u7aef\u65b9\u516c\u5b50\u53ea\u89c9\u4e24\u81c2\u9178\u9ebb\uff0c\u7965\u9e9f\u516c\u5b50\u4ea6\u611f\u53cc\u638c\u706b\u75db\uff0c\u4e8c\u4eba\u7adf\u662f\u529f\u529b\u6089\u654c\u3002 \u3000\u3000\u4e8c\u4eba\u77aa\u4e86\u4e00\u4e0b\uff0c\u7965\u9e9f\u516c\u5b50\u53c8\u731b\u6251\u800c\u4e0a\uff0c\u731b\u653b\u731b\u6253\uff0c\u638c\u62f3\u5feb\u5982\u98d8\u98ce\u95ea\u7535\uff0c\u63a5\u8fde\u653b\u51fa\u4e09\u62f3\u4e03 \u638c\uff0c\u903c\u5f97\u7aef\u65b9\u516c\u5b50\u4e00\u8def\u540e\u9000\uff0c\u76f4\u9000\u51fa\u6570\u4e08\u5f00\u5916\u3002 \u3000\u3000\u7aef\u65b9\u516c\u5b50\u5927\u543c\u4e00\u58f0\uff0c\u8fde\u51fa\u6570\u62db\uff0c\u6273\u56de\u52a3\u52bf\uff0c\u7acb\u523b\u8fd8\u4ee5\u989c\u8272\uff0c\u4e5f\u662f\u5feb\u653b\u5feb\u6253\uff0c\u8fde\u7740\u653b\u51fa\u4e94 \u638c\u516d\u62f3\uff0c\u5916\u52a0\u8fde\u73af\u4e09\u817f\uff0c\u4e5f\u628a\u7965\u9e9f\u516c\u5b50\u903c\u9000\u6570\u4e08\u3002 \u3000\u3000\u4e8c\u4eba\u7ffb\u7ffb\u6eda\u6eda\uff0c\u6253\u5f97\u96be\u89e3\u96be\u5206\u3002 \u3000\u3000\u94c1\u80cc\u9a7c\u9f99\u552f\u6050\u4e3b\u4eba\u6709\u5931\uff0c\u5927\u543c\u4e00\u58f0\uff0c\u51e0\u5982\u5e73\u5730\u8d77\u4e86\u4e00\u4e2a\u7126\u96f7\uff0c\u731b\u6325\u4e00\u638c\uff0c\u76f4\u5411\u7aef\u65b9\u516c\u4e8e \u6253\u53bb\u3002 \u3000\u3000\u52b2\u98ce\u9510\u5578\u5411\u7740\u7aef\u65b9\u516c\u5b50\u80cc\u540e\u75be\u5377\u800c\u81f3\uff0c\u7aef\u65b9\u516c\u5b50\u5c3d\u529b\u5288\u51fa\u4e00\u638c\uff0c\u628a\u7965\u9e9f\u516c\u5b50\u903c\u9000\u4e24\u6b65\uff0c \u6a2a\u7a7a\u98d8\u51fa\u4e24\u4e08\u5f00\u5916\uff0c\u9002\u65f6\u8eb2\u8fc7\u94c1\u80cc\u9a7c\u9f99\u5a01\u731b\u7684\u638c\u98ce\u3002 \u3000\u3000\u91d1\u5e9c\u53e6\u5916\u7684\u6b66\u6797\u9ad8\u624b\uff0c\u5374\u5df2\u9f50\u5411\u7aef\u65b9\u516c\u5b50\u5305\u56f4\u4e0a\u53bb\u3002 \u3000\u3000\u7aef\u65b9\u516c\u5b50\u50b2\u7136\u800c\u7acb\uff0c\u4ef0\u5929\u72c2\u7b11\u9053\uff1a\u201c\u5a01\u540d\u6ee1\u5929\u4e0b\u7684\u2018\u7965\u9e9f\u516c\u5b50\u2019\uff0c\u4e5f\u4e0d\u8fc7\u662f\u501a\u591a\u4e3a\u80dc\u800c \u5df2\uff01\u201d \u3000\u3000\u8bf4\u7f62\u5c48\u5c0f\u6307\u653e\u8fdb\u53e3\u5185\uff0c\u5439\u4e86\u4e00\u58f0\u5c16\u9510\u9707\u8033\u7684\u957f\u54e8\u3002 \u3000\u3000\u54e8\u97f3\u8fc7\u540e\uff0c\u53ea\u89c1\u5de6\u8fb9\u5c71\u5934\u4e0a\uff0c\u5192\u8d77\u4e86\u5341\u6570\u6761\u4eba\u5f71\uff0c\u4e00\u5f8b\u90fd\u662f\u9e4a\u8863\u767e\u7ed3\u7684\u53eb\u5316\u5b50\uff0c\u624b\u62ff\u6253 \u72d7\u68d2\u3002 \u3000\u3000\u63a5\u7740\u4ece\u53f3\u8fb9\u6811\u6797\u91cc\uff0c\u53c8\u62e5\u51fa\u5341\u6570\u540d\u624b\u6267\u68cd\u68d2\u7684\u4eba\uff0c\u4e5f\u90fd\u662f\u84ec\u5934\u7834\u8863\u7684\u53eb\u5316\u3002 \u3000\u3000\u5239\u65f6\u95f4\uff0c\u5de6\u53f3\u524d\u540e\uff0c\u5c71\u9876\u6811\u6797\uff0c\u8349\u53f6\u5ca9\u6d1e\uff0c\u7eb7\u7eb7\u51fa\u73b0\u4e86\u4eba\u5f71\uff0c\u5747\u662f\u624b\u62ff\u9752\u7af9\u6756\uff0c\u84ec\u5934\u57a2 \u9762\u7684\u53eb\u5316\uff0c\u6015\u4e0d\u6709\u6570\u767e\u4eba\u4e4b\u591a\u3002 \u3000\u3000\u7965\u9e9f\u516c\u5b50\u53ca\u91d1\u5e9c\u9ad8\u624b\uff0c\u5de6\u770b\u53f3\u770b\uff0c\u84e6\u89c1\u51fa\u73b0\u8fd9\u4e48\u591a\u53eb\u5316\uff0c\u8138\u4e0a\u5747\u9732\u51fa\u60ca\u60f6\u4e4b\u8272\uff0c\u4e07\u4e5f\u60f3 \u4e0d\u5230\u7aef\u65b9\u516c\u5b50\u7ade\u9884\u5148\u5728\u5c71\u4e2d\u57cb\u4f0f\u4e86\u8fd9\u591a\u7a77\u5bb6\u5e2e\u7684\u4eba\u7269\u3002 \u3000\u3000\u8fd9\u4e9b\u53eb\u5316\u51fa\u73b0\u540e\uff0c\u7eb7\u7eb7\u53eb\u56b7\uff1a\u201c\u6709\u94b1\u7684\u8001\u7237\u884c\u884c\u597d\uff01\u201d \u3000\u3000\u201c\u53ef\u601c\u53eb\u5316\u82e6\u547d\u7684\u4eba\uff01\u201d \u3000\u3000\u201c\u60a8\u4e0d\u4fee\u4eca\u751f\u4fee\u6765\u4e16\uff01\u201d \u3000\u3000\u201c\u628a\u60a8\u90a3\u5403\u4e0d\u4e86\u7684\u6b8b\u83dc\u5269\u996d\uff0c\u82b1\u4e0d\u5b8c\u7684\u91d1\u94f6\u8d22\u5b9d\uff0c\u4e5f\u8d4f\u7ed9\u6211\u4eec\u7a77\u4eba\u51e0\u4e2a\u5427\u2026\u2026\u201d \u3000\u3000\u5728\u53eb\u56a3\u58f0\u4e2d\uff0c\u7834\u8349\u978b\u68af\u62d6\u68af\u62d6\uff0c\u7adf\u662f\u661f\u98de\u7535\u63a3\uff0c\u6643\u773c\u95f4\uff0c\u7eb7\u7eb7\u56f4\u4e86\u4e0a\u6765\u3002 \u3000\u3000\u5a49\u513f\u2014\u76b1\u7709\u9053\uff1a\u201c\u539f\u6765\u4ed6\u8fd8\u662f\u4e2a\u5316\u5b50\u5934\uff01\u201d \u3000\u3000\u5c55\u767d\u4e0d\u89e3\u9053\uff1a\u201c\u8c01\uff1f\u4f60\u8bf4\u8c01\u662f\u5316\u5b50\u5934\uff1f\u201d \u3000\u3000\u5a49\u513f\u4e00\u5974\u5634\uff0c\u6307\u5411\u7aef\u65b9\u516c\u5b50\u9053\uff1a\u201c\u558f\uff01\u5c31\u662f\u8fd9\u4e00\u4f4d\uff01\u201d \u3000\u3000\u5c55\u767d\u60f3\u8d77\u5a49\u513f\u4e0e\u7aef\u65b9\u516c\u5b50\u4e00\u8d77\u51fa\u73b0\u7684\u4e8b\uff0c\u4ecd\u7136\u672b\u91ca\u4e8e\u6000\uff0c\u8d81\u6b64\u95ee\u9053\uff1a\u201c\u5a49\u513f\uff0c\u4f60\u8ddf\u795e\u7334 \u5b66\u827a\u7684\u4e8b\u600e\u4e48\u6837\uff1f\u53c8\u600e\u4e48\u8ddf\u7aef\u65b9\u516c\u5b50\u5728\u4e00\u8d77\u5462\uff1f\u201d \u3000\u3000\u5a49\u513f\u968f\u628a\u80cc\u7740\u795e\u7334\u627e\u6d1e\uff0c\u8bef\u996e\u201c\u6c34\u94f6\u5bd2\u6cc9\u201d\u7684\u4e8b\u8bf4\u4e86\u4e00\u904d\u3002 \u3000\u3000\u53c8\u628a\u600e\u4e48\u4e3a\u7aef\u65b9\u516c\u5b50\u6240\u6551\uff0c\u795e\u7334\u53c8\u600e\u6837\u7528\u53e3\u8ff0\u4e00\u62db\u628a\u7aef\u65b9\u516c\u5b50\u6253\u8dd1\uff0c\u4ee5\u53ca\u795e\u7334\u7528\u53e3\u8ff0\u4f20 \u5979\u6b66\u529f\u3001\u5728\u7b2c\u4e5d\u5341\u5929\u5934\u4e0a\u795e\u7334\u600e\u4e48\u6b7b\u6389\uff0c\u7aef\u65b9\u516c\u4e8e\u53c8\u8d76\u6765\u5e2e\u5979\u57cb\u846c\u795e\u7334\u5c38\u4f53\uff0c\u4e4b\u540e\u51fa\u6d1e\u6765\u4fbf \u78b0\u5230\u4ed6\u5a49\u513f\u5728\u8bc9\u8bf4\u8fd9\u4e9b\u65f6\uff0c\u5c55\u767d\u5374\u4e3a\u4e00\u4ef6\u4e8b\u52fe\u8d77\u4e86\u7591\u5fc3\uff0c\u90a3\u5c31\u662f\u5a49\u513f\u8bf4\u5728\u6d1e\u5185\u770b\u5230\u7684\u4e94\u5177\u6b7b \u5c38\uff0c\u50cf\u8c8c\u8863\u8457\u90fd\u50cf\u662f\u5728\u5bc6\u677e\u6797\u5411\u4ed6\u52ab\u9556\u7684\u201c\u71d5\u4e91\u4e94\u9738\u5929\u201d\u3002 \u3000\u3000\u5c55\u767d\u5fc3\u4e2d\u5947\u602a\uff0c\u71d5\u4e91\u4e94\u9738\u5929\u52ab\u9556\u5e76\u672a\u5f97\u624b\uff0c\u600e\u4e48\u4f1a\u6709\u2018\u4e24\u7bb1\u73e0\u5b9d\uff1f\u800c\u4e14\u600e\u4e48\u53c8\u90fd\u6b7b\u5728\u8fd9 \u8352\u6d1e\u4e2d\u4e86\u5462\uff1f\u53ef\u662f\uff0c\u90a3\u4e9b\u7a77\u5bb6\u5e2e\u4eba\u7269\uff0c\u5df2\u7ecf\u56f4\u4e86\u4e0a\u6765\uff0c\u60c5\u52bf\u6108\u6765\u6108\u7d27\u5f20\uff0c\u5df2\u6ca1\u6709\u65f6\u95f4\u518d\u5bb9\u4ed6 \u5411\u5a49\u513f\u8be6\u7ec6\u8be2\u95ee\u2026\u2026 \u3000\u3000\u4e00\u7fa4\u53eb\u5316\uff0c\u95f9\u95f9\u56b7\u56b7\u5730\u8d76\u81f3\u8fd1\u524d\uff0c\u7aef\u65b9\u516c\u5b50\u54c8\u54c8\u4e00\u7b11\u9053\uff1a\u201c\u4ee5\u591a\u4e3a\u80dc\uff0c\u54b1\u4eec\u5c31\u4ee5\u591a\u4e3a\u80dc \u5427\uff01\u201d \u3000\u3000\u8bf4\u7f62\u7eb5\u8eab\u4e00\u8df3\uff0c\u8df3\u4e0a\u4e00\u5757\u6570\u4e08\u9ad8\u7684\u5ca9\u77f3\u4e0a\u53bb\uff0c\u5f80\u5730\u4e0b\u4e00\u5750\uff0c\u53c8\u8bf4\u9053\uff1a\u201c\u7a77\u5bb6\u5e2e\u7684\u5f1f\u5144 \u4eec\uff01\u773c\u524d\u53ef\u662f\u4e9b\u6700\u5bcc\u6709\u7684\u8d22\u795e\u7237\uff0c\u4f60\u4eec\u5c31\u597d\u597d\u5730\u53eb\u5316\u51e0\u4e2a\u5427\uff01\u201d \u3000\u3000\u8fd9\u7fa4\u53eb\u5316\u95fb\u8a00\uff0c\u62a1\u8d77\u624b\u4e2d\u9752\u7af9\u6756\uff0c\u4ea4\u9519\u6e38\u8d70\uff0c\u5e76\u5728\u5634\u4e2d\u9ad8\u58f0\u53eb\u5316\u8d77\u6765\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u8fd9\u4e00\u6b21\u5374\u4e0d\u662f\u7eb7\u4e71\u53eb\u56b7\uff0c\u800c\u662f\u7531\u4e00\u4eba\u9886\u5934\u559d\u4e00\u53e5\uff0c\u7136\u540e\u7fa4\u58f0\u5e94\u548c\uff0c\u58f0\u97f5\u60a0\u626c\uff0c\u5982 \u6b4c\u5982\u9882\uff0c\u7adf\u662f\u975e\u5e38\u60a6\u8033\u52a8\u542c\u3002 \u3000\u3000\u53ea\u542c\u4e00\u4eba\u9886\u5934\u5531\u9053\uff1a\u201c\u6211\u4eec\u90fd\u662f\u6ca1\u6709\u996d\u5403\u7684\u7a77\u670b\u53cb\uff01\u201d \u3000\u3000\u7fa4\u58f0\u5408\u5531\u9053\uff1a\u201c\u7a77\u670b\u53cb\uff01\u7a77\u670b\u53cb\uff01\u201d \u3000\u3000\u9886\u5934\u7684\u4eba\u63a5\u7740\u5531\uff1a\u201c\u4e00\u6735\u83b2\u82b1\u5f00\uff01\u201d \u3000\u3000\u7fa4\u58f0\u5408\u5531\uff1a\u201c\u83b2\u82b1\uff0c\u83b2\u82b1\uff0c\u83b2\u82b1\u5f00\u53c8\u843d\uff01\u2026\u2026\u201d \u3000\u3000\u5c31\u5728\u8fd9\u6b4c\u5531\u58f0\u4e2d\uff0c\u4e00\u7fa4\u53eb\u5316\u6325\u52a8\u9752\u7af9\u6756\uff0c\u811a\u6b65\u6742\u5bc6\uff0c\u4eba\u5f71\u6643\u52a8\uff0c\u7adf\u6446\u6210\u4e00\u5ea7\u770b\u6765\u975e\u5e38\u4e25 \u6574\u7684\u201c\u53eb\u5316\u5927\u9635\u201d\u3002 \u3000\u3000\u201c\u94c1\u80cc\u9a7c\u9f99\u201d\u90a3\u4e48\u8c6a\u8fc8\u7684\u6b66\u6797\u5de8\u64ce\uff0c\u4e5f\u4e0d\u7531\u7565\u663e\u7d27\u5f20\uff0c\u4f4e\u58f0\u5bf9\u7965\u9e9f\u516c\u5b50\u9053\uff1a\u201c\u516c\u5b50\uff01\u5c0f \u5fc3\u4e86\uff0c\u8fd9\u662f\u7a77\u5bb6\u5e2e\u6210\u540d\u7684\u2018\u53eb\u5316\u5927\u9635\u2019\uff0c\u5148\u7531\u8001\u592b\u4e0e\u5df4\u8d24\u5f1f\u6321\u4e00\u9635\uff0c\u516c\u5b50\u4e0e\u5c0f\u7ec4\u6682\u4e14\u540e\u9000\uff0c \u8131\u8eab\u9635\u5916\uff0c\u6709\u4ec0\u4e48\u4e8b\u4ee5\u540e\u518d\u8bf4\uff01\u201d \u3000\u3000\u770b\u90a3\u4e9b\u53eb\u5316\uff0c\u6570\u767e\u4eba\u4e00\u9f50\u6325\u52a8\u9752\u7af9\u6756\uff0c\u6756\u8d77\u5982\u6797\u6756\u843d\u5982\u98ce\uff0c\u52b2\u6d41\u6fc0\u8361\uff0c\u56de\u65cb\u751f\u98ce\uff0c\u7965\u9e9f \u516c\u5b50\u5144\u59b9\u4e8c\u4eba\u5fc3\u4e2d\u4e5f\u4e0d\u7531\u6697\u6697\u5403\u60ca\uff0c\u542c\u201c\u94c1\u80cc\u9a7c\u9f99\u201d\u5982\u6b64\u8bf4\uff0c\u53ea\u6709\u70b9\u5934\u9ed8\u8bb8\u3002 \u3000\u3000\u201c\u94c1\u80cc\u9a7c\u9f99\u201d\u53c8\u5728\u968f\u6765\u9ad8\u624b\u4e2d\uff0c\u6307\u5b9a\u5929\u95e8\u4e00\u5251\u6c6a\u5146\u660e\u3001\u5e38\u5c71\u65bd\u7126\u826f\uff0c\u4e0e\u8861\u5c71\u4e8c\u9e1f\u6b27\u9633\u5f1f \u5144\u4e00\u5171\u56db\u4eba\uff0c\u8d1f\u8d23\u4fdd\u62a4\u7965\u9e9f\u5144\u59b9\u7684\u5b89\u5168\uff0c\u5176\u4f59\u7684\u4eba\u8ddf\u968f\u53cc\u94c1\u536b\u95ef\u9635\u2026\u3002\u2019\u5728\u201c\u94c1\u80cc\u9a7c\u9f99\u201d\u8c03 \u5175\u9063\u5c06\u3001\u5b89\u6392\u5fa1\u654c\u65f6\uff0c\u7a77\u5bb6\u5e2e\u9635\u52bf\u50ac\u52a8\uff0c\u5df2\u7ecf\u903c\u4e0a\u524d\u6765\uff0c\u4ecd\u7531\u4e00\u4eba\u9886\u5934\u559d\u9053\uff1a\u201c\u809a\u76ae\u997f\u624d\u5411 \u6709\u94b1\u7684\u8001\u7237\u4f38\u4f38\u624b\uff01\u201d \u3000\u3000\u7fa4\u58f0\u5408\u5531\u9053\uff1a\u201c\u4f38\u4f38\u624b\uff01\u4f38\u4f38\u624b\uff01\u201d \u3000\u3000\u575a\u5728\u5c71\u5761\u4e0a\u7684\u7aef\u65b9\u516c\u5b50\u6b64\u65f6\u5374\u63d2\u53e3\u53eb\u9053\uff1a\u201c\u4e0d\u8981\u5149\u662f\u5531\u4e86\uff0c\u6709\u94b1\u7684\u4eba\u591a\u534a\u6027\u6025\uff0c\u4e0d\u8010\u70e6 \u4f60\u4eec\u5531\u82cf\uff0c\u8981\u4f38\u624b\u5c31\u4f38\u5427\uff01\u201d \u3000\u3000\u4f46\u9886\u5934\u7684\u4eba\u4ecd\u7136\u5531\u9053\uff1a\u4e24\u6735\u83b2\u82b1\u5f00\uff01\u201d \u3000\u3000\u8fd9\u201c\u5f00\u201d\u5b57\u51fa\u53e3\uff0c\u6570\u767e\u6761\u9752\u7af9\u6756\u500f\u7136\u4e3e\u8d77\u3002 \u3000\u3000\u7fa4\u58f0\u63a5\u7740\u5531\uff1a\u83b2\u82b1\uff0c\u83b2\u82b1\uff0c\u83b2\u82b1\u5f00\u53c8\u843d\uff01\u201d \u3000\u3000\u5728\u8fd9\u201c\u83b2\u82b1\uff0c\u83b2\u82b1\u201d\u58f0\u4e2d\uff0c\u6570\u767e\u6761\u9752\u7af9\u6756\u6325\u52a8\u72b9\u5982\u98ce\u4e91\u6d8c\uff0c\u201c\u83b2\u82b1\u5f00\u53c8\u843d\u201d\u5f00\u5b57\u51fa\uff0c\u5982 \u6797\u7684\u9752\u7af9\u6756\u4e00\u9635\u6405\u52a8\uff0c\u52b2\u6d41\u6fc0\u6e4d\uff0c\u56de\u65cb\u751f\u98ce\uff0c\u201c\u843d\u201d\u5b57\u51fa\u53e3\uff0c\u8f70\u7136\u5982\u60ca\u96f7\u6012\u96f7\uff0c\u6570\u767e\u6761\u9752\u7af9 \u6756\u4e00\u9f50\u5411\u7965\u9e9f\u516c\u5b50\u53ca\u91d1\u5e9c\u4e00\u5e72\u9ad8\u624b\u653b\u6765\u3002 \u3000\u3000\u94c1\u80cc\u9a7c\u9f99\u5927\u559d\u4e00\u58f0\uff0c\u820c\u7efd\u6625\u96f7\uff0c\u4f1a\u96c6\u4e86\u4ed6\u6027\u547d\u53cc\u4fee\u6709\u7740\u4e00\u4e2a\u7532\u5b50\u4ee5\u4e0a\u7684\u201c\u5f00\u7891\u638c\u529b\u201d\uff0c \u53cc\u638c\u5168\u529b\u5288\u51fa\u3002 \u3000\u3000\u638c\u98ce\u52b2\u5578\uff0c\u5982\u72c2\u98ce\u5de8\u6d6a\u3002\u540c\u65f6\uff0c\u201c\u94c1\u7ffc\u98de\u9e4f\u201d\u4e00\u58f0\u5389\u5578\uff0c\u94c1\u7ffc\u5c55\u5904\uff0c\u817e\u7a7a\u800c\u8d77\uff0c\u8eab\u5728\u6570 \u6587\u9ad8\u7a7a\uff0c\u628a\u4ed6\u90a3\u9707\u60ca\u6c5f\u6e56\u7684\u201c\u94c1\u7ffc\u795e\u529f\u201d\u65bd\u5c55\u4e86\u51fa\u6765\u3002 \u3000\u3000\u53ea\u89c1\u4ed6\u5982\u4e00\u53ea\u5de8\u5927\u82cd\u9e70\u4e00\u822c\uff0c\u6cbc\u5c48\u95ea\u95ea\u7684\u94c1\u7ffc\uff0c\u631f\u7740\u96f7\u96f9\u4e07\u94a9\u4e4b\u52bf\uff0c\u5c45\u9ad8\u4e34\u4e0b\uff0c\u51cc\u7a7a\u5411 \u7a77\u5bb6\u5e2e\u7684\u201c\u53eb\u5316\u5927\u9635\u201d\u51fb\u4e0b\u3002 \u3000\u3000\u201c\u91d1\u5e9c\u53cc\u94c1\u536b\u201d\u76db\u540d\u65e0\u865a\uff0c\u4e00\u5728\u7a7a\u4e2d\uff0c\u4e00\u5728\u5730\u4e0b\uff0c\u8054\u8d77\u624b\u6765\u5206\u529b\u5408\u51fb\uff0c\u52bf\u5a01\u529b\u731b\uff0c\u58f0\u52bf \u60ca\u4eba\u3002 \u3000\u3000\u5c31\u5728\u201c\u91d1\u5e9c\u53cc\u94c1\u536b\u201d\u4e00\u5728\u5929\u7a7a\uff0c\u4e00\u5728\u5730\u4e0b\uff0c\u51cc\u5389\u65e0\u6bd4\u7684\u653b\u52bf\u4e4b\u4e0b\uff0c\u53ea\u542c\u201c\u83b2\u82b1\u5f00\uff0c\u83b2\u82b1 \u843d\u201d\u2019\u4e00\u7247\u6b4c\u5531\u58f0\u4e2d\uff0c\u9635\u5f0f\u5408\u800c\u5ffd\u5206\uff0c\u5206\u800c\u590d\u5408\uff0c\u767e\u6570\u6761\u9752\u7af9\u6756\u6405\u52a8\u4e4b\u4e0b\uff0c\u7ade\u628a\u201c\u91d1\u5e9c\u53cc\u94c1 \u536b\u201d\u7684\u4e24\u80a1\u5de8\u5927\u638c\u98ce\uff0c\u5316\u4e8e\u65e0\u5f62\u3002 \u3000\u3000\u800c\u4e14\uff0c\u7fa4\u53eb\u5316\u8eab\u5f62\u6e38\u8d70\uff0c\u53cd\u800c\u628a\u91d1\u5e9c\u4f17\u4eba\u7965\u9e9f\u516c\u4e88\u4ee5\u4e0b\uff0c\u5168\u6570\u5305\u56f4\u5728\u6838\u5fc3\u3002 \u3000\u3000\u8fde\u5c55\u767d\u4e0e\u5a49\u513f\u90fd\u5305\u56f4\u5728\u5185\u3002 \u3000\u3000\u5a49\u513f\u89c1\u7fa4\u53eb\u5316\u8fde\u81ea\u5df1\u4e5f\u5305\u56f4\u8d77\u6765\uff0c\u9752\u7af9\u6756\u5f71\u5982\u5c71\u4f3c\u6797\uff0c\u5411\u81ea\u5df1\u8eab\u4e0a\u7f69\u6765\uff0c\u79c0\u7709\u4e00\u76b1\uff0c\u5a07 \u58f0\u5428\u9053\uff1a\u201c\u597d\u54c7\uff01\u8fde\u6211\u4e5f\u4e0d\u653e\u8fc7\u4e86\uff01\u201d \u3000\u3000\u559d\u58f0\u4e2d\u7ea4\u624b\u626c\u8d77\uff0c\u5c48\u4e24\u6307\u8f7b\u8f7b\u4e00\u5f39\uff0c\u4e24\u7f15\u6781\u7ec6\u7684\u52b2\u98ce\uff0c\u759a\u5578\u800c\u51fa\u201c\u6251\u901a\uff01\u6251\u901a\uff01\u201d \u3000\u3000\u4e24\u540d\u5e2e\u4f17\u7adf\u5e94\u6307\u800c\u5012\u3002 \u3000\u3000\u201c\u91d1\u5e9c\u53cc\u94c1\u536b\u201d\u4e24\u5927\u9ad8\u624b\u8054\u624b\u5408\u653b\uff0c\u90a3\u4e48\u521a\u70c8\u7684\u638c\u52b2\uff0c\u7a77\u5bb6\u5e2e\u7684\u201c\u53eb\u5316\u5927\u9635\u201d\u90fd\u80fd\u6321\u5f97 \u4f4f\uff0c\u4e3a\u4ec0\u4e48\u7adf\u63a5\u4e0d\u4e0b\u5a49\u513f\u2014\u2014\u4e00\u4e2a\u5341\u516d\u4e03\u5c81\u7684\u5c0f\u59d1\u5a18\u7ea4\u7ea4\u4e24\u6307\uff1f\u8fd9\u771f\u662f\u602a\u4e8b\uff01 \u3000\u3000\u4f46\u5c55\u767d\u5df2\u770b\u51fa\u4e86\u95e8\u9053\uff0c\u56e0\u4e3a\u795e\u9a74\u94c1\u80c6\u66fe\u544a\u8bc9\u8fc7\u4ed6\u201c\u795e\u7334\u201d\u94c1\u51cc\u7279\u6709\u7684\u5389\u5bb3\u529f\u592b\uff0c\u5a49\u513f\u8fd9 \u5c48\u6307\u8f7b\u5f39\uff0c\u6b63\u662f\u795e\u7334\u94c1\u51cc\u8eab\u8d1f\u7edd\u5b66\uff0c\u4e0d\u7531\u60ca\u5947\u53eb\u9053\uff1a\u2018\u641c\u9b42\u6307\uff01\u2019\u4f60\u628a\u2018\u641c\u9b42\u6307\u2019\u90fd\u5b66\u4f1a \u4e86\uff01\u201d \u3000\u3000\u8a00\u4e0b\u4e4b\u610f\uff0c\u4e0d\u80dc\u60ca\u9a87\u3002 \u3000\u3000\u5a49\u513f\u6485\u5634\u9053\uff1a\u201c\u6211\u8fd8\u4e0d\u613f\u5b66\u5462\uff01\u8001\u602a\u7269\u786c\u8981\u6559\u6211\u6709\u4ec0\u4e48\u529e\u6cd5\uff1f\u201d \u3000\u3000\u8f6c\u53c8\u629a\u5a9a\u4e00\u7b11\uff0c\u5bf9\u5c55\u767d\u9053\uff1a\u201c\u4e0d\u8fc7\u8bf7\u5c55\u54e5\u54e5\u4e0d\u7528\u62c5\u5fc3\uff0c\u59b9\u59b9\u4e0d\u4f1a\u5bf9\u5c55\u54e5\u54e5\u65bd\u5c55\u8fd9\u72e0\u6bd2\u7684 \u6740\u624b\u7684\u2026\u2026\u201d \u3000\u3000\u5c55\u767d\u4e00\u76b1\u7709\uff0c\u5fc3\u4e2d\u9887\u4e0d\u60a6\uff0c\u4e00\u662f\u5979\u7ba1\u795e\u7334\u53eb\u8001\u602a\u7269\uff0c\u4f7f\u4ed6\u542c\u6765\u9887\u4e0d\u987a\u8033\uff0c\u56e0\u4e3a\u795e\u7334\u94c1\u51cc \u518d\u602a\uff0c\u603b\u662f\u4f20\u8fc7\u5979\u6b66\u529f\uff0c\u6b66\u6797\u4f20\u7edf\u201c\u827a\u4e0d\u8f7b\u4f20\u201d\uff0c\u5982\u679c\u4f20\u4ee5\u6b66\u529f\uff0c\u867d\u6ca1\u6709\u62dc\u5e08\u4e4b\u540d\uff0c\u4e5f\u6709\u5e08 \u7236\u4e4b\u5b9e\uff0c\u6b66\u6797\u4e2d\u5bf9\u6388\u827a\u6069\u5e08\u6700\u4e3a\u770b\u91cd\uff0c\u5a49\u513f\u5982\u6b64\u79f0\u547c\u795e\u7334\uff0c\u5c31\u662f\u5bf9\u63f4\u827a\u4e4b\u5e08\u4e0d\u656c\uff0c\u8fdd\u72af\u6b66\u6797 \u4f20\u7edf\uff0c\u6240\u4ee5\u5c55\u767d\u542c\u6765\u975e\u5e38\u523a\u8033\u3002\u5176\u6b21\uff0c\u5979\u8bf4\u5b66\u6765\u6740\u624b\u4e0d\u5411\u5c55\u767d\u65bd\u5c55\uff0c\u8fd9\u56fa\u7136\u4f7f\u4ed6\u89c9\u5f97\u5a49\u513f\u6709 \u70b9\u77a7\u4e0d\u8d77\u4ed6\u7684\u6210\u4efd\u3002 \u3000\u3000\u5c55\u767d\u5251\u7709\u4e00\u76b1\uff0c\u624d\u8981\u53d1\u4f5c\uff0c\u4f46\u7a77\u5bb6\u5e2e\u7684\u5e2e\u4f17\u89c1\u5a49\u513f\u4e00\u51fa\u624b\uff0c\u4fbf\u70b9\u6bd9\u4e8c\u4eba\uff0c\u4e00\u9f50\u5927\u6012\uff0c\u9635 \u52bf\u50ac\u52a8\uff0c\u628a\u4e3b\u529b\u8f6c\u5411\u5a49\u513f\u4e0e\u5c55\u767d\u4e8c\u4eba\uff0c\u540c\u65f6\u542c\u4e00\u4eba\u5531\u9053\uff1a\u201c\u6709\u94b1\u7684\u5c0f\u59d0\u5c11\u7237\u591a\u6148\u60b2\uff01\u201d \u3000\u3000\u7fa4\u58f0\u548c\u9053\uff1a\u201c\u591a\u6148\u60b2\uff01\u591a\u6148\u60b2\uff01\u201d \u3000\u3000\u9886\u5934\u4eba\u53c8\u5531\u9053\uff1a\u201c\u4e09\u6735\u83b2\u82b1\u5f00\uff01\u201d \u3000\u3000\u7fa4\u58f0\u9f50\u5531\uff1a\u201c\u83b2\u82b1\uff0c\u83b2\u82b1\uff0c\u83b2\u82b1\u5f00\u53c8\u843d\uff01\u201d \u3000\u3000\u201c\u83b2\u82b1\uff0c\u83b2\u82b1\u201d\u58f0\u4e2d\uff0c\u7af9\u6756\u6405\u52a8\uff0c\u906e\u5929\u853d\u65e5\uff0c\u201c\u843d\u201d\u5b57\u4e00\u51fa\uff0c\u6f2b\u5929\u6756\u5f71\uff0c\u72b9\u5982\u6392\u5c71\u5012\u6d77 \u4e00\u822c\uff0c\u631f\u7740\u4f7f\u4eba\u5598\u4e0d\u8fc7\u6c14\u6765\u7684\u75be\u98ce\u52b2\u6d41\uff0c\u731b\u5411\u5c55\u767d\u3001\u5a49\u513f\u4e24\u4eba\u538b\u4e0b\u3002 \u3000\u3000\u663e\u89c1\u7a77\u5bb6\u5e2e\u631f\u6012\u800c\u6765\uff0c\u653b\u51fb\u5a01\u52bf\u6bd4\u653b\u5411\u201c\u91d1\u5e9c\u53cc\u94c1\u536b\u201d\u65f6\uff0c\u589e\u5f3a\u4e86\u4f55\u6b62\u6570\u500d\u3002 \u3000\u3000\u5a49\u513f\u53ea\u89c9\u6756\u5f71\u5982\u5c71\uff0c\u50cf\u6cf0\u5c71\u538b\u9876\u4e00\u822c\u6d8c\u70ed\u52bf\u5a01\u529b\u731b\uff0c\u524d\u6240\u672a\u7ecf\uff0c\u4e0d\u7531\u5fc3\u4e2d\u4e00\u61d4\uff0c\u53cc\u624b\u9f50 \u626c\uff0c\u56db\u624b\u9f50\u5f39(\u56e0\u4e3a\u5979\u5185\u529f\u771f\u529b\u4e0d\u5230\u706b\u5019\uff0c\u5bf9\u201c\u641c\u9b42\u6307\u201d\u7684\u6b66\u6797\u7edd\u5b66\uff0c\u53ea\u80fd\u5de6\u53f3\u5404\u7ec3\u4e24\u6307\uff0c \u672a\u80fd\u8fbe\u5230\u529f\u8d2f\u56db\u7a0d\u5883\u5730\uff0c\u6240\u4ee5\u518d\u7d27\u6025\u4e5f\u53ea\u80fd\u5f39\u56db\u6307)\uff0c\u60f3\u4ee5\u65b0\u5b66\u5230\u7684\u6740\u62db\u9000\u6548\u3002 \u3000\u3000\u201c\u641c\u9b42\u6307\u201d\u679c\u7136\u5389\u5bb3\uff0c\u968f\u7740\u5f3a\u52b2\u6307\u98ce\uff0c\u53c8\u6709\u4e24\u540d\u5e2e\u4f17\u5012\u5730\uff0c\u4f46\u5a49\u513f\u8fde\u4f24\u4e24\u4eba\uff0c\u7a77\u5bb6\u5e2e\u4e2d \u5df2\u6709\u9ad8\u624b\u4e0a\u6765\uff0c\u5a49\u513f\u5f39\u51fa\u56db\u6307\uff0c\u6307\u5411\u56db\u4eba\uff0c\u5374\u53ea\u6709\u4e24\u4eba\u8d1f\u4f24\uff0c\u53e6\u4e24\u6307\u5374\u843d\u4e86\u7a7a\u3002 \u3000\u3000\u800c\u9635\u5f0f\u4e2d\u7684\u6570\u6761\u6756\u5f71\uff0c\u5374\u5df2\u7eb5\u6a2a\u541e\u5410\uff0c\u9f50\u5411\u5a49\u513f\u6253\u5230\u3002 \u3000\u3000\u5a49\u513f\u60ca\u547c\u5931\u58f0\uff0c\u53cc\u7c89\u638c\u7eb7\u98de\u5982\u8776\uff0c\u5de6\u5288\u53f3\u6321\uff0c\u52a0\u4e0a\u5979\u72ec\u5177\u7684\u201c\u8e51\u7a7a\u5e7b\u5f71\u2019\u2019\u8eab\u6cd5\u8f7b\u7075\uff0c \u7a7f\u9699\u6e38\u8d70\uff0c\u867d\u7136\u4e00\u65f6\u672a\u53d7\u5230\u4f24\u5bb3\uff0c\u5374\u4e5f\u95f9\u4e86\u4e2a\u624b\u5fd9\u811a\u4e71\uff0c\u7c89\u8138\u715e\u767d\u2026\u2026 \u3000\u3000\u8fd9\u5f53\u513f\u5c55\u767d\u5df2\u5288\u51fa\u4e24\u8bb0\u201c\u96f7\u97f3\u4f5b\u638c\u201d\u3002 \u3000\u3000\u5c55\u767d\u4e5f\u662f\u8ddf\u5a49\u513f\u4e00\u6837\uff0c\u201c\u96f7\u97f3\u4f5b\u638c\u201d\u65b0\u5b66\u4e4d\u7ec3\uff0c\u672a\u80fd\u8fbe\u5230\u6536\u53d1\u968f\u5fc3\u5730\u6b65\uff0c\u9635\u4e2d\u538b\u529b\u4e00\u8fd1 \u8eab\uff0c\u6570\u767e\u6761\u6756\u5f71\u4e00\u9f50\u653b\u5230\u201c\u96f7\u97f3\u4f5b\u638c\u201d\u5306\u5fd9\u4e2d\u518d\u4e5f\u65bd\u5c55\u4e0d\u51fa\u6765\uff0c\u53ea\u6709\u5de6\u95ea\u53f3\u907f\uff0c\u638c\u6307\u517c\u65bd\uff0c \u5728\u9635\u4e2d\u82e6\u6218\u3002 \u3000\u3000\u5c31\u8fd9\u6325\uff0c\u4e09\u51b2\u4e24\u95ef\uff0c\u5a49\u513f\u4e0e\u5c55\u767d\u5df2\u540c\u65f6\u9677\u4e8e\u201c\u53eb\u5316\u5927\u9635\u201d\u4e4b\u4e2d\u3002 \u3000\u3000\u800c\u4e14\uff0c\u4e24\u4eba\u5df2\u88ab\u51b2\u6563\uff0c\u5404\u81ea\u4e3a\u6218\uff0c\u5728\u6570\u767e\u6761\u6756\u5f71\u4e4b\u4e2d\uff0c\u5de6\u7a81\u53f3\u95ef\uff0c\u5374\u6108\u9677\u6108\u6df1\u3002 \u3000\u3000\u8fd9\u8fb9\u5a49\u513f\u4e0e\u5c55\u767d\u9677\u5165\u9635\u4e2d\u82e6\u6597\uff0c\u90a3\u8fb9\u91d1\u5e9c\u9ad8\u624b\u3001\u53cc\u94c1\u536b\uff0c\u4ee5\u53ca\u7965\u9e9f\u516c\u5b50\u5144\u59b9\uff0c\u4e5f\u540c\u65f6\u9677 \u5165\u201c\u53eb\u5316\u5927\u9635\u201d\u4e4b\u4e2d\uff0c\u800c\u4e14\u4e00\u6837\u7684\u88ab\u51b2\u6563\u5404\u81ea\u4e3a\u6218\u3002 \u3000\u3000\u8fd9\u201c\u53eb\u5316\u5927\u9635\u201d\u771f\u662f\u5965\u5999\u65e0\u6bd4\uff0c\u867d\u7136\u4ec5\u7531\u6570\u767e\u4eba\u7ec4\u6210\uff0c\u5728\u9635\u5f0f\u53d8\u5316\u4e4b\u4e0b\uff0c\u7ade\u597d\u50cf\u6709\u6210\u5343 \u6210\u4e07\u7684\u9752\u7af9\u6756\u5f71\uff0c\u5f25\u7a7a\u906e\u65e5\u822c\u6ca1\u5934\u6ca1\u8138\u7ea7\u4e0b\u3002 \u3000\u3000\u52a0\u4e0a\u7fa4\u53eb\u5316\u4e00\u5f8b\u7684\u84ec\u5934\u57a2\u9762\uff0c\u9e4a\u8863\u767e\u7ed3\uff0c\u4e00\u6837\u7684\u624b\u6325\u9752\u7af9\u6756\uff0c\u6837\u5b50\u662f\u4e00\u6a21\u4e00\u6837\uff0c\u96be\u5206\u5f7c \u6b64\uff0c\u4ea4\u9519\u6e38\u8d70\u4e4b\u95f4\uff0c\u628a\u91d1\u5e9c\u4f17\u9ad8\u624b\u3001\u7965\u9e9f\u5144\u59b9\u3001\u5c55\u767d\u3001\u5a49\u513f\u7b49\u4eba\uff0c\u95f9\u4e86\u4e2a\u5934\u660f\u8111\u80c0\uff0c\u773c\u82b1\u7f2d \u4e71\u3002 \u3000\u3000\u53ea\u6709\u201c\u94c1\u7ffc\u98de\u9e4f\u201d\u60c5\u51b5\u6bd4\u8f83\u597d\u4e00\u70b9\uff0c\u56e0\u4e3a\u4ed6\u6709\u201c\u94c1\u7ffc\u795e\u8863\u201d\u4e4b\u52a9\uff0c\u80fd\u591f\u98de\u7fd4\u7a7a\u4e2d\uff0c\u4e0d\u5fc5 \u843d\u4e8e\u5730\u9762\uff0c\u6240\u4ee5\u53ef\u4e0d\u53d7\u6df1\u9677\u9635\u4e2d\u4e4b\u82e6\u3002 \u3000\u3000\u4f46\u4ed6\u5728\u7a7a\u4e2d\u9f13\u7fc5\u98de\u65cb\uff0c\u53ea\u89c1\u201c\u53eb\u5316\u5927\u9635\u201d\u7ffb\u7ffb\u6eda\u6eda\uff0c\u6ee1\u773c\u90fd\u662f\u84ec\u5934\u57a2\u9762\u7684\u53eb\u5316\u8111\u888b\uff0c\u4ee5 \u53ca\u6325\u821e\u5982\u6797\u7684\u9752\u7af9\u6756\u5f71\uff0c\u81f3\u4e8e\u9677\u8eab\u9635\u4e2d\u7684\u8bf8\u4eba\uff0c\u53cd\u800c\u4e0d\u6613\u770b\u89c1\u3002 \u3000\u3000\u5076\u800c\u770b\u5230\u94c1\u80cc\u9a7c\u9f99\u6ee1\u5934\u82cd\u82cd\u767d\u53d1\u77aa\u626c\uff0c\u9a7c\u80cc\u62f1\u8d77\u5982\u4e18\uff0c\u5728\u9752\u7af9\u6756\u5f71\u4e2d\u6643\u52a8\u800c\u51fa\uff0c\u594b\u638c\u529b \u6218\uff0c\u4ed6\u4fbf\u51cc\u7a7a\u4e0b\u6251\u63a5\u5e94\u4e00\u62db\uff0c\u4f46\u201c\u53eb\u5316\u5927\u9635\u201d\u64e6\u5206\u5373\u5408\uff0c\u94c1\u80cc\u9a7c\u9f99\u7684\u8eab\u5f71\u5019\u73b0\u5373\u9690\u6ca1\u4e8e\u6570\u767e \u6761\u9752\u7af9\u6756\u5f71\u4e4b\u4e2d\u3002 \u3000\u3000\u800c\u4e14\uff0c\u94c1\u7ffc\u98de\u9e4f\u6bcf\u6251\u4e0b\u4e00\u6b21\uff0c\u6570\u767e\u6761\u9752\u7af9\u6756\u90ce\u9ad8\u4e3e\u8d77\u6765\uff0c\u731b\u5411\u4ed6\u4e34\u7a7a\u653b\u81f3\uff0c\u5a01\u529b\u4e4b\u5927\uff0c \u7ade\u80fd\u903c\u5f97\u4ed6\u51cc\u7a7a\u8eab\u5f62\uff0c\u63a5\u5ffd\u4e0a\u5347\u5bfb\u4e08\u3002 \u3000\u3000\u94c1\u7ffc\u98de\u9e4f\u867d\u80fd\u51cc\u7a7a\u98de\u7fd4\uff0c\u4e5f\u4e0d\u8fc7\u5982\u5927\u6d77\u4e0a\u4e00\u53ea\u5b64\u98de\u7684\u6d77\u71d5\uff0c\u5bf9\u90a3\u4e9b\u6c89\u6d6e\u5728\u5de8\u6d6a\u4e2d\u7684\u540c \u4f34\uff0c\u4e5f\u65e0\u529b\u76f8\u6551\u3002 \u3000\u3000\u5012\u662f\u5c55\u767d\u4e0e\u5a49\u513f\uff0c\u4e00\u4e2a\u662f\u201c\u96f7\u97f3\u4f5b\u638c\u201d\u5a01\u731b\uff0c\u4e00\u4e2a\u662f\u201c\u641c\u9b42\u6307\u201d\u51cc\u5389\uff0c\u65f6\u65f6\u80fd\u8361\u5f00\u5bc6\u5982 \u4e1b\u6797\u7684\u9752\u7af9\u6756\u9635\uff0c\u73b0\u51fa\u98d8\u9038\u7684\u8eab\u5f71\u6765\u3002 \u3000\u3000\u5ffd\u542c\u4e00\u58f0\u60e8\u55e5\uff0c\u4e00\u540d\u91d1\u5e9c\u9ad8\u624b\u5c38\u8eab\u5df2\u88ab\u629b\u51fa\u9635\u5916\uff0c\u7528\u8eab\u6d6e\u80bf\uff0c\u8138\u4e0a\u9752\u7d2b\u8840\u75d5\u6ee1\u5e03\uff0c\u663e\u89c1 \u662f\u88ab\u4e71\u6756\u51fb\u6bd9\u3002 \u3000\u3000\u201c\u94c1\u7ffc\u98de\u9e4f\u201d\u53c8\u60ca\u53c8\u6012\uff0c\u8fd0\u8db3\u4e86\u201c\u94e3\u7ffc\u795e\u529f\u201d\u4ee5\u201c\u9e70\u640f\u4e5d\u9704\u201d\u8eab\u6cd5\uff0c\u94c1\u7ffc\u8fde\u6325\uff0c\u8fde\u756a\u731b \u653b\u3002 \u3000\u3000\u201c\u8f70\u8f70\u201d\u6570\u54cd\uff0c\u53eb\u5316\u5927\u9635\u201d\u5408\u800c\u500f\u5206\uff0c\u8ba9\u8fc7\u201c\u94c1\u7ffc\u98de\u9e4f\u201d\u5a01\u731b\u94c1\u7ffc\u52b2\u98ce\uff0c\u5019\u7136\u53c8\u5206\u800c\u590d \u5408\uff0c\u4f9d\u7136\u662f\u4e25\u6574\u65e0\u9699\u3002\u540c\u65f6\u201c\u83b2\u82b1\uff0c\u83b2\u82b1\u201d\u58f0\u4e2d\uff0c\u9752\u7af9\u6756\u9f50\u4e3e\uff0c\u53c8\u731b\u5411\u201c\u94c1\u7ffc\u98de\u9e4f\u201d\u5468\u8eab\u70b9 \u6765\u3002 \u3000\u3000\u201c\u94c1\u7ffc\u98de\u9e4f\u201d\u53ea\u6709\u5c55\u7fc5\u8180\u8d77\u4e24\u4e08\uff0c\u4ee5\u907f\u5e73\u5730\u9762\u8d77\u7684\u6570\u767e\u6756\u5f71\u3002 \u7b2c\u4e09\u5341\u7ae0\u3000\u201c\u53eb\u5316\u5927\u9635\u201d>> \u53e4\u9f99\u300a\u5251\u5ba2\u884c\u300b \u7b2c\u4e09\u5341\u7ae0\u3000\u201c\u53eb\u5316\u5927\u9635\u201d \u3000\u3000\u201c\u94c1\u7ffc\u98de\u9e4f\u201d\u4e34\u7a7a\u4e0b\u51fb\u65e0\u529f\uff0c\u5728\u9752\u7af9\u6756\u5f71\u731b\u653b\u4e4b\u4e0b\uff0c\u5c55\u7ffc\u6025\u8d77\uff0c\u5374\u88ab\u7cbe\u7075\u65e0\u6bd4\u7684\u5a49\u513f\u770b \u5230\u3002 \u3000\u3000\u5a49\u513f\u7075\u673a\u4e00\u52a8\uff0c\u5fc3\u60f3\uff1a\u201c\u4e0e\u5176\u5728\u9635\u4e2d\u53d7\u56f0\uff0c\u4e0d\u5982\u65bd\u5c55\u2018\u8e51\u7a7a\u5e7b\u5f71\u2019\u8eab\u6cd5\uff0c\u5b66\u90a3\u88c5\u4e86\u7fc5\u8180 \u7684\u8001\u5934\uff0c\u817e\u8eab\u7a7a\u4e2d\uff0c\u5c45\u9ad8\u4e34\u4e0b\uff0c\u5bfb\u9699\u6363\u865a\uff0c\u4e0d\u6bd4\u5728\u5730\u9762\u53d7\u56f0\u5f3a\u591a\u4e86\uff1f\u201d \u3000\u3000\u5a49\u513f\u60f3\u5230\u5c31\u505a\uff0c\u5a07\u53f1\u4e00\u58f0\uff0c\u5c48\u6307\u75be\u5f39\uff0c\u903c\u5f97\u9635\u5f0f\u4e00\u677e\uff0c\u500f\u7136\u51cc\u7a7a\u4e09\u6587\u3002 \u3000\u3000\u8eab\u5f62\u76d8\u7a7a\u4e00\u505c\uff0c\u52bf\u5c3d\u4e0b\u843d\u4e4b\u9645\uff0c\u811a\u5c16\u4e00\u70b9\u8db3\u4e0b\u9752\u7af9\u6756\u5f71\uff0c\u843d\u800c\u590d\u8d77\uff0c\u7adf\u5728\u201c\u53eb\u5316\u5927\u9635\u201d \u7684\u9752\u7af9\u6756\u5f71\u4e0a\u7a7a\u7fe9\u7fd4\u98de\u821e\u8d77\u6765\u3002 \u3000\u3000\u5a49\u513f\u4eba\u751f\u5f97\u7f8e\uff0c\u52a0\u4e0a\u4f53\u6001\u7a83\u5baa\uff0c\u8eab\u6cd5\u8f7b\u7075\uff0c\u53c8\u662f\u7a7f\u7684\u4e00\u8eab\u5f69\u7ee3\u9526\u8863\uff0c\u5728\u6362\u8d77\u5019\u843d\u7684\u9752\u7af9 \u6756\u4e0a\u7fe9\u7fe9\u8d77\u821e\uff0c\u4eff\u4f5b\u4e5d\u5929\u4ed9\u5973\u4e0b\u51e1\uff0c\u53c8\u5982\u201c\u9752\u5973\u5ae6\u5a25\u4ff1\u8010\u51b7\uff0c\u6708\u4e0b\u971c\u91cc\u9017\u8749\u5a1f\u201d\uff0c\u771f\u662f\u7f8e\u5999 \u5df2\u6781\u3002 \u3000\u3000\u5750\u5728\u5c71\u5761\u4e0a\u7684\u7aef\u65b9\u516c\u5b50\u4e0d\u7531\u8131\u53e3\u53eb\u9053\uff1a\u597d\u8eab\u6cd5\uff01\u7f8e\u6781\u4e86\uff01\u2026\u2026\u201d \u3000\u3000\u4ed6\u4e0d\u51fa\u58f0\u8fd8\u597d\uff0c\u4ed6\u8fd9\u4e00\u53eb\u597d\u5374\u63d0\u9192\u4e86\u5a49\u513f\u7684\u6ce8\u610f\u3002\u5a49\u513f\u83ab\u540d\u5176\u5999\u5730\u88ab\u56f0\u9635\u4e2d\uff0c\u4e00\u809a\u5b50\u4e0d \u9ad8\u5174\uff0c\u5982\u4eca\u624d\u594b\u529b\u8131\u51fa\u9635\u5916\uff0c\u7aef\u65b9\u516c\u5b50\u5750\u5728\u5c71\u5761\u4e0a\uff0c\u60a0\u60a0\u95f2\u95f2\u5730\u53eb\u8d77\u597d\u6765\uff0c\u4e0d\u7531\u5927\u6012\uff0c\u8eab\u5f62 \u731b\u7136\u4e00\u987f\uff0c\u811a\u5c16\u501f\u9752\u7af9\u6756\u6253\u51fa\u7684\u52b2\u98ce\u501f\u529b\u4f7f\u52b2\uff0c\u4e00\u9635\u5212\u52a8\uff0c\u7adf\u5e73\u98de\u76f4\u5c04\uff0c\u4ece\u9752\u7af9\u6756\u9635\u4e0a\u7a7a\u731b \u5411\u7aef\u65b9\u516c\u5b50\u6240\u5750\u5ca9\u77f3\u4e0a\u6251\u53bb\u3002 \u3000\u3000\u5341\u6570\u4e08\u7684\u8ddd\u79bb\uff0c\u5a49\u513f\u534a\u7a7a\u4e2d\u4e24\u6b21\u811a\u8e29\u9752\u7af9\u6756\u501f\u52b2\uff0c\u7adf\u7136\u811a\u4e0d\u6cbe\u5730\uff0c\u7a9c\u4e0a\u4e86\u8ddd\u5730\u9762\u5c1a\u6709\u4e09 \u4e08\u4f59\u9ad8\u7684\u5ca9\u77f3\u4e4b\u4e0a\u3002 \u3000\u3000\u7aef\u65b9\u516c\u5b50\u8fde\u8fde\u559d\u91c7\uff0c\u800c\u4e14\u7ad9\u8d77\u8eab\u5f62\uff0c\u4fca\u8138\u6cdb\u8d77\u7b11\u5bb9\uff0c\u770b\u90a3\u610f\u601d\u662f\u6b22\u8fce\u5a49\u513f\u7684\u6837\u5b50\u3002 \u3000\u3000\u4f46\u5a49\u513f\u6068\u4ed6\u8fde\u81ea\u5df1\u4e5f\u56f4\u56f0\u5728\u53eb\u5316\u9635\u4e2d\uff0c\u8eab\u672b\u5230\u5148\u5df2\u5c48\u6307\u5f39\u51fa\uff0c\u4e00\u7f15\u6781\u7ec6\uff0c\u4f46\u53c8\u6781\u4e3a\u5f3a\u52b2 \u7684\u75be\u98ce\uff0c\u7834\u7a7a\u9510\u5578\uff0c\u731b\u88ad\u7aef\u65b9\u516c\u5b50\u80f8\u524d\u201c\u4e09\u9633\u201d\u91cd\u7a74\u3002 \u3000\u3000\u7aef\u65b9\u516c\u5b50\u5927\u5403\u4e00\u60ca\uff0c\u89c1\u6765\u52bf\u751a\u6025\uff0c\u4e0d\u6562\u786c\u63a5\uff0c\u6025\u5fd9\u98d8\u8eab\u8eb2\u8fc7\uff0c\u4e00\u8fb9\u5634\u91cc\u6025\u53eb\u9053\uff1a\u201c\u59d1 \u5a18\uff01\u4f60\u600e\u4e48\u8fde\u5c0f\u751f\u4e5f\u6253\u8d77\u6765\uff1f\u201d \u3000\u3000\u5a49\u513f\u5df2\u5a09\u5a09\u5a77\u5a77\u5730\u7ad9\u7acb\u5ca9\u77f3\u4e4b\u4e0a\uff0c\u7476\u9f3b\u4e00\u76b1\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u5c11\u5ba2\u6c14\uff01\u4f60\u53eb\u4e00\u7fa4\u8981\u996d\u7684\u628a \u6211\u5305\u56f4\u8d77\u6765\uff0c\u53c8\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u7aef\u65b9\u516c\u5b50\u82e6\u7b11\u9053\uff1a\u4f60\u8981\u4e0d\u548c\u4ed6\u4eec\u7ad9\u5728\u4e00\u8d77\uff0c\u7a77\u5bb6\u5e2e\u7684\u4eba\u4e07\u4e0d\u6562\u627e\u59d1\u5a18\u7684\u9ebb\u70e6\u2026\u2026\u201d \u3000\u3000\u5a49\u513f\u9053\uff1a\u201c\u4f60\u8981\u4e0d\u7ad9\u5728\u8fd9\u91cc\uff0c\u6211\u4e5f\u4e0d\u4f1a\u6253\u4f60\uff01\u201d \u3000\u3000\u7aef\u65b9\u516c\u5b50\u88ab\u5a49\u513f\u53cd\u5507\u76f8\u8ba5\u5f97\u54ed\u7b11\u4e0d\u5f97\uff0c\u4f46\u4ecd\u7136\u6d8e\u8138\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u59d1\u5a18\u8981\u5c0f\u751f\u5230\u54ea\u91cc\u53bb \u5462\uff1f\u201d \u3000\u3000\u5a49\u513f\u4e00\u77aa\u773c\u9053\uff1a\u6211\u7ba1\u4f60\u5230\u54ea\u91cc\u53bb\uff1f\u4f46\u662f\uff0c\u4f60\u90a3\u4e9b\u8981\u996d\u7684\u5982\u679c\u4f24\u4e86\u6211\u5c55\u54e5\u54e5\u7684\u4e00\u6839\u5934\u53d1\uff0c \u6211\u4fbf\u62ff\u4f60\u507f\u547d\uff01\u201d \u3000\u3000\u8fd9\u4e00\u53e5\u8bdd\u6fc0\u8d77\u4e86\u7aef\u65b9\u516c\u5b50\u7684\u6012\u706b\uff0c\u4e00\u662f\u5a49\u513f\u8bf4\u5f97\u7edd\u60c5\u7edd\u4e49\uff0c\u518d\u8005\u5a49\u513f\u4e00\u5fc3\u7ef4\u62a4\u5c55\u767d\uff0c\u4e5f \u5f15\u8d77\u4ed6\u5185\u5fc3\u7684\u5ac9\u5992\uff0c\u4e0d\u7531\u5251\u7709\u4e00\u626c\uff0c\u4fca\u7f8e\u7684\u8138\u4e0a\uff0c\u95ea\u8fc7\u4e00\u62b9\u6740\u673a\uff0c\u563f\u563f\u7b11\u9053\uff1a\u201c\u7aef\u65b9\u516c\u5b50\u7684 \u547d\u8fd8\u4e0d\u90a3\u4e48\u4e0d\u503c\u94b1\uff01\u4f1a\u53bb\u4e3a\u522b\u4eba\u7684\u4e00\u6839\u5934\u53d1\u507f\u547d\uff01\u201d \u3000\u3000\u5a49\u513f\u9053\uff1a\u201c\u4f60\u4ee5\u4e3a\u6211\u6740\u4e0d\u4e86\u4f60\u5417\uff1f\u201d \u3000\u3000\u5c1a\u672a\u7b49\u7aef\u65b9\u516c\u5b50\u7b54\u8a00\uff0c\u9752\u7af9\u9635\u4e2d\u4e00\u58f0\u60e8\u964d\u4f20\u6765\uff0c\u5a49\u513f\u4e0e\u7aef\u65b9\u516c\u5b50\u540c\u65f6\u4f4e\u5934\u770b\u53bb\uff0c\u53c8\u662f\u4e00 \u540d\u91d1\u5e9c\u9ad8\u624b\u6bd9\u547d\u3002 \u3000\u3000\u5c38\u8eab\u88ab\u6570\u6839\u9752\u7af9\u6756\u5979\u51fa\u9635\u5916\uff0c\u4e00\u6837\u662f\u5468\u8eab\u6d6e\u80bf\uff0c\u6ee1\u8138\u9752\u7d2b\u8840\u75d5\uff0c\u6eda\u5728\u5730\u4e0a\uff0c\u4ef0\u8138\u671d\u5929\uff0c \u6b7b\u72b6\u6781\u60e8\u3002 \u3000\u3000\u9635\u5185\u542c\u5230\u7965\u9e9f\u516c\u5b50\u7684\u6012\u53f1h\uff0c\u53ca\u94c1\u80cc\u9a7c\u9f99\u7684\u5389\u543c\uff0c\u663e\u89c1\u4e8c\u4eba\u6124\u6012\u5df2\u6781\u3002 \u3000\u3000\u201c\u7830\u8a07\u201d\u5de8\u54cd\uff0c\u63a5\u4e8c\u8fde\u4e09\u4f20\u6765\uff0c\u4e5f\u53ef\u731c\u51fa\u91d1\u5e9c\u4e2d\u4eba\u5728\u4e00\u9f50\u594b\u529b\u731b\u653b\u3002 \u3000\u3000\u94c1\u7ffc\u98de\u9e4f\u66f4\u662f\u8fde\u58f0\u6012\u5578\uff0c\u94c1\u7ffc\u594b\u8d77\uff0c\u8fde\u756a\u731b\u6251\u3002 \u3000\u3000\u4f46\u201c\u53eb\u5316\u5927\u9635\u201d\u5a01\u529b\u5f3a\u5927\uff0c\u53d8\u5316\u4e07\u5343\uff0c\u7eb5\u7136\u5728\u7edd\u4e16\u9ad8\u624b\u731b\u51b2\u731b\u6253\u4e4b\u4e0b\uff0c\u4ecd\u7136\u662f\u4e25\u6574\u65e0 \u6bd4\uff0c\u4e1d\u6beb\u4e0d\u89c1\u7834\u7efd\u3002 \u3000\u3000\u7a81\u7136\u9752\u7af9\u6756\u5f71\u4e4b\u4e2d\uff0c\u4e00\u9053\u8000\u773c\u7684\u78a7\u7eff\u5149\u534e\u51b2\u5929\u800c\u8d77\uff0c\u63a5\u8fde\u51e0\u95ea\uff0c\u201c\u545b\uff01\u545b\uff01\u545b\uff01\u2026\u2026\u201d \u3000\u3000\u4e00\u9635\u9f99\u541f\u864e\u5578\u4e4b\u58f0\u4f20\u6765\uff0c\u4e25\u5bc6\u65e0\u6bd4\u7684\u201c\u53eb\u5316\u5927\u9635\u201d\u4e2d\u95f4\uff0c\u7acb\u523b\u7a7a\u51fa\u4e00\u4e2a\u4e94\u5c3a\u8303\u56f4\u7684\u5708\u5b50 \u6765\u3002 \u3000\u3000\u5708\u5b50\u7a7a\u5730\u5f53\u4e2d\uff0c\u624b\u6267\u201c\u65e0\u60c5\u78a7\u5251\u201d\u3001\u6e0a\u505c\u5cb3\u5cd9\u7ad9\u7740\u7684\u6b63\u662f\u5c55\u767d\u3002 \u3000\u3000\u539f\u6765\u5c55\u767d\u4ed7\u7740\u53cc\u624b\u5728\u9635\u4e2d\uff0c\u7adf\u6108\u51b2\u6108\u51b2\u4e0d\u51fa\u6765\uff0c\u5fc3\u4e2d\u6124\u6012\uff0c\u7adf\u6487\u51fa\u80cc\u4e0a\u7684\u201c\u65e0\u60c5\u78a7\u5251\u201d \u6765\u3002 \u3000\u3000\u201c\u65e0\u60c5\u78a7\u5251\u201d\u679c\u7136\u795e\u5175\u5229\u5668\uff0c\u53c8\u52a0\u4e0a\u5c55\u767d\u65b0\u4ece\u795e\u9a74\u94c1\u80c6\u5b66\u4f1a\u4e86\u201c\u98ce\u96f7\u516b\u5251\u201d\uff0c\u201c\u65e0\u60c5\u78a7 \u5251\u201d\u51fa\u9798\uff0c\u4e00\u62db\u201c\u98ce\u9707\u96f7\u9e23\u201d\u7acb\u523b\u8361\u5f00\u4e86\u4e94\u5c3a\u65b9\u5706\u7684\u4e00\u7247\u7a7a\u5730\uff0c\u800c\u4e14\uff0c\u201c\u53eb\u5316\u5927\u9635\u201d\u4e2d\u7684\u5e2e \u4f17\u6709\u907f\u8eb2\u4e0d\u53ca\u7684\uff0c\u624b\u4e2d\u9752\u7af9\u6756\u7acb\u523b\u88ab\u201c\u65e0\u60c5\u78a7\u5251\u201d\u524a\u65ad\u4e86\u4e94\u3001\u516d\u6839\u3002 \u3000\u3000\u7a77\u5bb6\u5e2e\u8fd9\u9752\u7af9\u6756\u53c8\u53eb\u201c\u6253\u72d7\u68d2\u201d\uff0c\u662f\u5e2e\u4e3b\u5728\u5f00\u9999\u5802\u65f6\u4eb2\u624b\u4f20\u6388\uff0c\u7d22\u4e3a\u5f92\u4f17\u6240\u91cd\u89c6\uff0c\u800c\u4e14 \u6bcf\u4e09\u5e74\u624d\u5f00\u9999\u5802\u4e00\u6b21\uff0c\u7b2c\u4e00\u6b21\u6240\u6388\u4e3a\u6728\u68d2\uff0c\u90a3\u662f\u5165\u95e8\u4e09\u4e2d\u4ee5\u540e\u7684\u5f1f\u5b50\u3002\u7b2c\u4e8c\u6b21\u6240\u6388\u4e3a\u9ec4\u7af9\uff0c \u53c8\u53eb\u82e6\u7af9\uff0c\u90a3\u662f\u5165\u95e8\u516d\u5e74\u4ee5\u4e0a\u7684\u5f1f\u5b50\u7b2c\u4e09\u6b21\u6240\u6388\u4e3a\u7eff\u7af9\uff0c\u90a3\u5df2\u7ecf\u662f\u5165\u95e8\u4e5d\u5e74\u4ee5\u4e0a\u7684\u5f1f\u5b50\u4e86\uff0c \u81f3\u4e8e\u8fd9\u9752\u7af9\u6756\uff0c\u4e43\u662f\u5165\u95e8\u5341\u4e8c\u5e74\u4ee5\u4e0a\uff0c\u6b66\u529f\u9ad8\u5f3a\u7684\u5f1f\u5b50\u624d\u914d\u643a\u5e26\uff0c\u6240\u4ee5\u4eca\u5929\u5728\u6b64\u6446\u9635\u7684\u5e2e\u4f17 \u90fd\u662f\u5e2e\u5185\u5341\u6570\u5e74\u4ee5\u4e0a\u7684\u9ad8\u624b\uff0c\u6240\u4ee5\u624d\u80fd\u56f0\u4f4f\u91d1\u5e9c\u53cc\u94c1\u536b\u3001\u7965\u9e9f\u516c\u5b50\u5144\u59b9\u53ca\u5c55\u767d\u7b49\u6b66\u6797\u9876\u5c16\u9ad8 \u624b\u3002 \u3000\u3000\u5c24\u5176\u662f\u9752\u7af9\u6756\u4e43\u662f\u6d77\u5357\u7279\u4ea7\uff0c\u5b9e\u5fc3\u94c1\u9aa8\uff0c\u575a\u786c\u65e0\u6bd4\uff0c\u5c31\u662f\u5bfb\u5e38\u5b9d\u5251\u4e5f\u96be\u635f\u5206\u6beb\uff0c\u5982\u4eca\u88ab \u5c55\u767d\u201c\u65e0\u60c5\u78a7\u5251\u201d\u4e00\u5251\u524a\u65ad\u4e94\u516d\u6839\uff0c\u7ade\u628a\u6570\u767e\u5e2e\u4f17\u2014\u65f6\u9707\u4f4f\u3002 \u3000\u3000\u201c\u53eb\u5316\u5927\u9635\u201d\u5239\u65f6\u95f4\u5fd8\u8bb0\u4e86\u50ac\u52a8\uff0c\u7a77\u5bb6\u5e2e\u4f17\u4e00\u9f50\u77aa\u89c6\u7740\u5c55\u767d\u5446\u5446\u53d1\u6014\uff0c\u5c24\u5176\u88ab\u524a\u65ad\u4e86\u9752 \u7af9\u6756\u7684\u5e2e\u4f17\uff0c\u9664\u4e86\u9707\u60ca\u4e4b\u5916\uff0c\u66f4\u662f\u6ee1\u8138\u6124\u6012\u60b2\u75db\u7684\u8868\u60c5\u3002 \u3000\u3000\u5c55\u767d\u5374\u540d\u5a01\u98ce\u516b\u9762\u7ad9\u5728\u90a3\u91cc\uff0c\u672a\u518d\u4e58\u80dc\u4f59\u5a01\uff0c\u51fa\u5251\u8ffd\u6740\u2026\u2026 \u3000\u3000\u5a49\u513f\u5374\u559c\u6781\u800c\u547c\uff1a\u5c55\u54e5\u54e5\uff01\u2026\u2026\u201d \u3000\u3000\u7aef\u65b9\u516c\u5b50\u500f\u7136\u53d8\u8272\uff0c\u6025\u547c\u9053\uff1a\u7a77\u670b\u53cb\u4eec\uff01\u8981\u996d\u7684\u5bb6\u4f19\u6bc1\u4e86\uff0c\u53ef\u6ca1\u6cd5\u53e9\u89c1\u7956\u5e08\u7237\uff01\u201d \u3000\u3000\u4e00\u53e5\u8bdd\u6fc0\u8d77\u4e86\u7a77\u5bb6\u5e2e\u4f17\u7684\u62fc\u547d\u4e4b\u5fc3\uff0c\u9f50\u58f0\u6012\u543c\uff0c\u6325\u8d77\u9752\u7af9\u6756\uff0c\u731b\u5411\u5c55\u767d\u653b\u53bb\u3002 \u3000\u3000\u6b64\u65f6\uff0c\u4ed6\u4eec\u4e0d\u518d\u201c\u83b2\u82b1\uff0c\u83b2\u82b1\u201d\u5531\u5f97\u597d\u542c\u4e86\uff0c\u800c\u662f\u6012\u543c\u72c2\u5578\uff0c\u6570\u767e\u6761\u9752\u7af9\u6756\u96e8\u70b9\u4f3c\u5730\u5411 \u5c55\u767d\u731b\u653b\u3002 \u3000\u3000\u53ef\u662f\u5c55\u767d\u4e00\u5251\u5728\u624b\uff0c\u5982\u864e\u6dfb\u7ffc\uff0c\u4ed6\u5148\u524d\u4e00\u5251\u903c\u9000\u5e2e\u4f17\uff0c\u4e0d\u4e58\u80dc\u8ffd\u6740\uff0c\u662f\u4e0d\u613f\u591a\u9020\u6740\u5b7d\uff0c \u5982\u4eca\u89c1\u7fa4\u53eb\u5316\u75af\u72c2\u5730\u5411\u4ed6\u6251\u6765\uff0c\u5c55\u767d\u4e00\u9707\u201c\u65e0\u60c5\u78a7\u5251\u201d\u2019\u201c\u98ce\u96f7\u513f\u5251\u201d\u7b2c\u4e8c\u62db\uff0c\u201c\u6012\u96f7\u72c2 \u98d9\u201d\u72b9\u5982\u6697\u9ed1\u4e91\u7aef\u51e0\u9053\u5389\u95ea\uff0c\u9690\u631f\u98ce\u96f7\u4e4b\u58f0\uff0c\u6fc0\u5c04\u800c\u51fa\u3002 \u3000\u3000\u201c\u545b\uff01\u545b\uff01\u201d \u3000\u3000\u65ad\u7af9\u6a2a\u98de\u3002 \u3000\u3000\u201c\u78c1\uff01\u78c1\uff01\u201d \u3000\u3000\u8840\u96e8\u56db\u6e85\u3002 \u3000\u3000\u7ade\u6709\u4e03\u516b\u540d\u5e2e\u4f17\u65ad\u7af9\u81c2\u4e00\u9f50\u88ab\u524a\u65ad\uff0c\u53d1\u51fa\u4e24\u58f0\u60e8\u55e5\u3002 \u3000\u3000\u201c\u547c\u5566\u201d\u4e00\u58f0\uff0c\u7fa4\u5316\u9707\u60ca\u540e\u9000\uff0c\u4e00\u9f50\u77aa\u7740\u5c55\u767d\uff0c\u867d\u7136\u8138\u4e0a\u8868\u60c5\u6124\u6012\u4e4b\u6781\uff0c\u5374\u518d\u4e0d\u6562\u5411\u4e0a \u5305\u56f4\u3002 \u3000\u3000\u5c55\u767d\u51db\u7136\u9053\uff1a\u201c\u5982\u518d\u4e0d\u9000\u53bb\uff0c\u53ef\u522b\u8bf4\u5c0f\u7237\u4e0d\u613f\u6740\u4f24\uff0c\u6211\u8981\u51fa\u624b\u653b\u51fb\u4e86\uff01\u201d \u3000\u3000\u4f17\u7a77\u5bb6\u5e2e\u88ab\u5c55\u767d\u5a01\u52bf\u6240\u6151\uff0c\u679c\u7136\u65e0\u4eba\u518d\u6562\u5411\u4e0a\u56f4\u62e2\u3002 \u3000\u3000\u4e00\u4e2a\u5e74\u7ea6\u516d\u65ec\u3001\u82b1\u81ea\u80e1\u987b\u7684\u5e74\u8001\u4e5e\u4e10\uff0c\u4e0a\u524d\u4e00\u6b65\u9053\uff1a\u7a77\u5bb6\u5e2e\u591a\u8499\u8d4f\u8d50\uff0c\u4e0d\u77e5\u9601\u4e0b\u80fd\u5426\u5c06 \u5927\u540d\u89c1\u544a\uff1f\u4ee5\u540e\u6211\u4eec\u7a77\u4eba\u4e5f\u597d\u611f\u6069\u56fe\u62a5\uff01\u201d \u3000\u3000\u5c55\u767d\u9053\uff1a\u533a\u533a\u5c55\u767d\uff0c\u5c31\u662f\u5728\u4e0b\uff01\u201d \u3000\u3000\u8001\u5e74\u4e5e\u5199\u9053\uff1a\u201c\u539f\u6765\u662f\u5c55\u5927\u4fa0\uff01\u6211\u7a77\u5bb6\u5e2e\u6c38\u8bb0\u5927\u5fb7\uff0c\u4e0d\u5fd7\u62a5\u7b54\u60a8\u5c31\u662f\u4e86\uff01\u201d \u3000\u3000\u5c55\u767d\u9053\uff1a\u201c\u5c55\u767d\u88ab\u903c\u51fa\u624b\uff0c\u65e0\u5fc3\u4e0e\u8d35\u5e2e\u7ed3\u4ec7\uff0c\u5047\u5982\u8d35\u5e2e\u8bb0\u7740\u8fd9\u7b14\u5e10\uff0c\u5c55\u767d\u4e5f\u4e0d\u80fd\u63a8\u8f9e\uff0c \u968f\u65f6\u63a5\u7740\u8d35\u5e2e\uff01\u201d \u3000\u3000\u8001\u53eb\u5316\u4e00\u7ad6\u5927\u62c7\u6307\u9053\uff1a\u82f1\u96c4\uff01\u8001\u53eb\u5316\u4f69\u670d\u4f60\u4e86\u2026\u2026\u201d \u3000\u3000\u7aef\u65b9\u516c\u5b50\u5374\u5728\u5ca9\u77f3\u4e0a\u6025\u53eb\u9053\uff1a\u201c\u9f9a\u8001\u53eb\u5316\uff01\u8fd9\u662f\u4ec0\u4e48\u8282\u53e3\uff1f\u5957\u7684\u662f\u54ea\u95e8\u5b50\u4ea4\u60c5\u5440\uff01\u770b\u90a3 \u8fb9\u70b9\u5b50\u4e5f\u8981\u95ef\u51fa\u6765\u4e86\u2026\u2026\u201d \u3000\u3000\u539f\u6765\u8fd9\u53eb\u505a\u9f9a\u8001\u53eb\u5316\u7684\u5e74\u8001\u4e5e\u4e10\uff0c\u4e0e\u5c55\u767d\u7b54\u8bdd\u7684\u5f53\u513f\uff0c\u5176\u4f59\u56f4\u56f0\u91d1\u5e9c\u7684\u5e2e\u4f17\uff0c\u773c\u89c1\u4e5f\u5b88 \u4e0d\u4f4f\u9635\u52bf\uff0c\u88ab\u91d1\u5e9c\u53cc\u94c1\u536b\u53ca\u7965\u9e9f\u516c\u5b50\u5144\u59b9\u4e00\u756a\u6025\u653b\uff0c\u9635\u5f0f\u663e\u51fa\u6e83\u4e71\u8ff9\u8c61\u3002 \u3000\u3000\u201c\u53eb\u5316\u5927\u9635\u201d\u56fa\u7136\u5389\u5bb3\uff0c\u6446\u9635\u4e4b\u4eba\u5374\u662f\u606f\u606f\u76f8\u5173\uff0c\u6b7b\u4f24\u4e00\u4e24\u4e2a\uff0c\u56fa\u7136\u4e0d\u4f1a\u5f71\u54cd\u5168\u76d8\uff0c\u4f46 \u5305\u56f4\u5c55\u767d\u7684\u6709\u6570\u5341\u4eba\u4e4b\u591a\uff0c\u4e00\u65e6\u5b8c\u5168\u505c\u6b62\u4e0b\u6765\uff0c\u9635\u5f0f\u50ac\u52a8\u53d7\u4e86\u5f88\u5927\u7684\u5f71\u54cd\uff0c\u6240\u4ee5\u5a01\u529b\u5927\u4e0d\u5982 \u524d\uff0c\u53c8\u52a0\u4e0a\u91d1\u5e9c\u7684\u4eba\u594b\u529b\u731b\u51b2\uff0c\u9635\u811a\u52a8\u6447\uff0c\u773c\u770b\u7740\u8981\u88ab\u91d1\u5e9c\u4f17\u9ad8\u624b\u51b2\u7a81\u51fa\u56f4\u3002 \u3000\u3000\u9f9a\u8001\u53eb\u5316\u88ab\u7aef\u65b9\u516c\u5b50\u4e00\u8bed\u63d0\u9192\uff0c\u820d\u4e86\u5c55\u767d\uff0c\u50ac\u52a8\u9635\u52bf\uff0c\u2018\u9f50\u5411\u91d1\u5e9c\u4f17\u4eba\u56f4\u53bb\u3002 \u3000\u3000\u4e00\u65f6\u95f4\u9752\u7af9\u6756\u9f50\u4e3e\uff0c\u6740\u58f0\u5927\u9707\uff0c\u6218\u51b5\u53c8\u8d8b\u70ed\u70c8\u8d77\u6765\u3002 \u3000\u3000\u7aef\u65b9\u516c\u5b50\u56fa\u7136\u53ca\u65f6\u63d0\u9192\u4e86\u7a77\u5bb6\u5e2e\u4f17\uff0c\u628a\u201c\u53eb\u5316\u5927\u9635\u201d\u518d\u5ea6\u50ac\u52a8\u8d77\u6765\uff0c\u4f46\u540c\u65f6\u4ed6\u7684\u53eb\u58f0\u4e5f \u63d0\u9192\u4e86\u5728\u7a7a\u4e2d\u98de\u7fd4\u4e0b\u51fb\u7684\u94c1\u7ffc\u98de\u9e4f\u3002 \u3000\u3000\u94c1\u7ffc\u98de\u9e4f\u89c1\u7aef\u65b9\u516c\u5b50\u4e00\u8bf4\u8bdd\uff0c\u9635\u5f0f\u4fbf\u8f83\u5389\u5bb3\uff0c\u5fc3\u60f3\uff1a\u201c\u64d2\u8d3c\u5148\u64d2\u738b\uff01\u597d\u5c0f\u5b50\uff0c\u4f60\u5728\u90a3\u91cc \u5750\u5c71\u89c2\u864e\u6597\uff0c\u8fd8\u5728\u6307\u6325\u7fa4\u53eb\u5316\u4f5c\u6218\uff0c\u6211\u5148\u628a\u4f60\u62ff\u4f4f\uff0c\u5c82\u4e0d\u5c31\u89e3\u4e86\u53eb\u5316\u5927\u964d\u4e4b\u5371\uff01\u201d \u3000\u3000\u60f3\u5230\u8fd9\u91cc\uff0c\u94c1\u7ffc\u4e00\u5c55\u4e00\u62c2\uff0c\u72b9\u5982\u5927\u9e1f\u6a2a\u7a7a\uff0c\u4eba\u672b\u5230\uff0c\u53cc\u94c1\u7ffc\u8fde\u7eed\u731b\u6325\uff0c\u631f\u7740\u7834\u7a7a\u52b2\u98ce\uff0c \u76f4\u5411\u505c\u8eab\u5ca9\u4e0a\u7684\u7aef\u65b9\u516c\u5b50\u6251\u53bb\u3002 \u3000\u3000\u7aef\u65b9\u516c\u5b50\u89c1\u201c\u94c1\u7ffc\u98de\u9e4f\u201d\u6765\u52bf\u51f6\u731b\uff0c\u63a5\u8fde\u8fce\u7a7a\u5288\u51fa\u4e24\u638c\u3002 \u3000\u3000\u638c\u98ce\u4e0e\u94c1\u7ffc\u96c4\u98ce\u4e00\u63a5\uff0c\u201c\u7830\uff01\u8a07\uff01\u201d\u4e24\u58f0\u5de8\u54cd\uff0c\u534a\u7a7a\u4e2d\u52b2\u6d41\u6fc0\u5c04\uff0c\u4f46\u5e76\u672a\u963b\u6b62\u201c\u94c1\u7ffc\u98de \u9e4f\u201d\u75be\u51b2\u4e4b\u52bf\uff0c\u53cc\u638c\u4e00\u6247\uff0c\u8fc5\u5982\u9e70\u7275\uff0c\u7167\u65e7\u5411\u7aef\u65b9\u516c\u5b50\u98de\u6251\u800c\u81f3\u3002 \u3000\u3000\u7aef\u65b9\u516c\u5b50\u5fc3\u4e2d\u4e00\u61d4\uff0c\u6025\u6b32\u817e\u8eab\u8d70\u907f\uff0c\u4f46\u94c1\u7ffc\u98de\u5feb\uff0c\u5019\u7136\u800c\u81f3\u7aef\u65b9\u516c\u5b50\u5934\u9876\u4e0a\u7a7a\uff0c\u201c\u94c1\u7ffc \u98de\u9e4f\u201d\u5389\u5578\u4e00\u58f0\uff1a\u5c0f\u8f88\uff0c\u7eb3\u547d\u6765\uff01\u201d \u3000\u3000\u559d\u58f0\u4e2d\u94c1\u7ffc\u4e00\u5c55\uff0c\u731b\u5411\u7aef\u65b9\u516c\u5b50\u8fce\u5934\u6325\u4e0b\u3002 \u3000\u3000\u8000\u5149\u95ea\u95ea\u7684\u94c1\u7ffc\uff0c\u72b9\u5982\u4e00\u5927\u7247\u4e4c\u4e91\uff0c\u8fce\u5934\u76d6\u9876\u800c\u4e0b\uff0c\u52b2\u98ce\u94a5\u5578\uff0c\u6251\u9762\u751f\u5bd2\uff0c\u7aef\u65b9\u516c\u5b50\u5927 \u5403\u4e00\u60ca\uff0c\u8131\u53e3\u60ca\u547c\uff1a\u4e0d\u597d\uff01\u2026\u2026\u201d \u3000\u3000\u4f46\u5a49\u513f\u4e5f\u7ad9\u5728\u7aef\u65b9\u516c\u5b50\u8eab\u8fb9\uff0c\u94e3\u7ffc\u52b2\u98ce\u8fde\u5e26\u6247\u5411\u5a49\u513f\uff0c\u5a49\u513f\u5a07\u53f1\u4e00\u58f0\uff1a\u201c\u4f60\u627e\u6b7b\uff01\u201d \u3000\u3000\u559d\u58f0\u4e2d\u5c48\u6307\u4e00\u5f39\uff0c\u4e00\u7f15\u6781\u7ec6\u7684\u75be\u98ce\uff0c\u5c16\u5578\u7740\u76f4\u5411\u94c1\u7ffc\u98de\u9e4f\u201c\u5fc3\u4fde\u201d\u91cd\u7a74\u5c04\u53bb\u3002 \u3000\u3000\u201c\u94c1\u7ffc\u98de\u9e4f\u201d\u5df2\u89c1\u8bc6\u8fc7\u5a49\u513f\u7684\u201c\u641c\u9b42\u6307\u201d\uff0c\u77e5\u9053\u5389\u5bb3\uff0c\u4e0d\u6562\u786c\u63a5\uff0c\u655b\u7fc5\u6025\u95ea\uff0c\u4f46\u4ecd\u7136\u6162 \u4e86\u4e00\u6162\uff0c\u8eb2\u5f00\u8981\u5bb3\uff0c\u5374\u6ca1\u6709\u8eb2\u8fc7\u94c1\u7ffc\uff0c\u53ea\u542c\u201c\u968f\u201d\u4e00\u58f0\u5fae\u54cd\uff0c\u5df4\u5929\u8d6b\u4e0d\u754f\u5200\u5251\u6697\u5668\u7684\u201c\u94c1\u7ffc \u795e\u8863\u201d\uff0c\u7ade\u88ab\u5a49\u513f\u201c\u641c\u9b42\u6307\u201d\u6d1e\u7a7f\u4e00\u4e2a\u624b\u6307\u5927\u7684\u6d1e\u3002 \u3000\u3000\u201c\u94c1\u7ffc\u98de\u9e4f\u201d\u5df4\u5929\u8d6b\u6025\u5fd9\u655b\u7fc5\u843d\u5730\uff0c\u8138\u8272\u60e8\u53d8\uff0c\u4ed6\u4e07\u4e5f\u60f3\u4e0d\u5230\u81ea\u5df2\u7684\u94c1\u7ffc\u795e\u8863\uff0c\u7adf\u88ab\u5a49 \u513f\u4e00\u6307\u635f\u574f\uff0c\u5fc3\u4e2d\u53c8\u60ca\u53c8\u6012\uff0c\u4e00\u53cc\u4e09\u89d2\u5389\u76ee\u6012\u89c6\u7740\u5a49\u513f\uff0c\u6ee1\u8138\u6740\u795d\uff0c\u54ac\u7259\u5207\u9f7f\u9053\uff1a\u201c\u5927\u80c6\u8d31 \u5a62\uff01\u7adf\u6562\u635f\u574f\u4e8c\u7237\u795e\u8863\uff0c\u4e8c\u7237\u8981\u4f60\u6b7b\u65e0\u846c\u8eab\u4e4b\u5730\uff01\u201d \u3000\u3000\u6012\u559d\u58f0\u4e2d\uff0c\u5341\u6307\u5c48\u4f38\uff0c\u5468\u8eab\u9aa8\u8def\u201c\u683c\u683c\u201d\u4f5c\u54cd\uff0c\u9634\u68ee\u51f6\u72e0\uff0c\u5927\u8e0f\u6b65\u5411\u5a49\u513f\u903c\u8fd1\u3002 \u3000\u3000\u5a49\u513f\u4e0d\u77e5\u4ed6\u8981\u65bd\u5c55\u4ec0\u4e48\u5389\u5bb3\u7684\u9634\u6bd2\u6b66\u529f\uff0c\u4f46\u770b\u4ed6\u51f6\u72e0\u4e4b\u6001\uff0c\u5fc3\u4e2d\u4e0d\u7531\u4e00\u61d4\uff0c\u6697\u6697\u84c4\u52bf\u6212 \u5907\u3002 \u3000\u3000\u4f46\u7aef\u65b9\u516c\u5b50\u88ab\u5a49\u513f\u4e00\u6307\u89e3\u5371\uff0c\u60ca\u9b42\u752b\u5b9a\uff0c\u5374\u9017\u8d77\u6ee1\u8154\u6012\u706b\uff0c\u89c1\u201c\u94c1\u7ffc\u98de\u9e4f\u201d\u5411\u5a49\u513f\u6b3a \u53bb\uff0c\u5927\u559d\u4e00\u58f0\uff0c\u8fd0\u8d77\u5bb6\u4f20\u7edd\u5b66\u201c\u6df7\u5143\u6307\u201d\uff0c\u731b\u5411\u201c\u94c1\u7ffc\u98de\u9e4f\u201d\u540e\u5fc3\u8981\u5bb3\u70b9\u53bb\u3002 \u3000\u3000\u201c\u94c1\u7ffc\u98de\u9e4f\u201d\u6b63\u8fd0\u96c6\u4e86\u5468\u8eab\u529f\u529b\uff0c\u60f3\u627e\u5a49\u513f\u53bb\u62a5\u6bc1\u8863\u4e4b\u4ec7\uff0c\u5ffd\u89c9\u811a\u540e\u52b2\u98ce\u7834\u7a7a\u800c\u81f3\uff0c\u77e5 \u9053\u6709\u4eba\u6697\u88ad\uff0c\u66f4\u5982\u706b\u4e0a\u52a0\u6cb9\uff0c\u66b4\u543c\u4e00\u58f0\uff0c\u53cd\u8eab\u51fa\u638c\uff0c\u4e00\u5f0f\u201c\u5012\u8f6c\u9634\u9633\u201d\uff0c\u628a\u8fd0\u96c6\u81f3\u989d\u5cf0\u7684\u4e00 \u8eab\u529f\u529b\uff0c\u53cc\u638c\u4e00\u53cd\u4e00\u6b63\uff0c\u731b\u5411\u8eab\u540e\u6253\u53bb\u3002 \u3000\u3000\u201c\u5657\uff01\u7830\uff01\u201d \u3000\u3000\u4e00\u58f0\u88c2\u5e1b\u8f7b\u54cd\uff0c\u4e00\u58f0\u8f70\u5929\u5de8\u9707\uff0c\u540c\u65f6\u4e24\u58f0\u60e8\u55e5\u4f20\u51fa\u3002 \u3000\u3000\u7aef\u65b9\u516c\u5b50\u88ab\u201c\u94c1\u7ffc\u98de\u9e4f\u201d\u5a01\u529b\u65e0\u6d9b\u7684\u638c\u98ce\uff0c\u9707\u4e0b\u6570\u4e08\u9ad8\u7684\u5ca9\u77f3\uff0c\u4e00\u58f0\u60e8\u55e5\uff0c\u5934\u4e0b\u811a\u4e0a\u5730 \u5411\u4e0b\u683d\u53bb\u3002 \u3000\u3000\u201c\u94c1\u7ffc\u98de\u9e4f\u201d\u4e00\u53ea\u5de6\u638c\u6b63\u597d\u8fce\u4f4f\u7aef\u65b9\u516c\u5b50\u5bb6\u4f20\u7edd\u827a\u201c\u6df7\u5143\u6307\u201d\uff0c\u4e00\u9635\u5947\u75db\u6c81\u4eba\u5fc3\u813e\uff0c\u4e5f \u53d1\u51fa\u4e00\u58f0\u60e8\u66dd\uff0c\u518d\u4e00\u770b\u5de6\u638c\u7ea2\u80bf\u8001\u9605\u3002 \u3000\u3000\u94c1\u7ffc\u98de\u9e4f\u7eb5\u6a2a\u6c5f\u6e56\u6570\u5341\u5e74\uff0c\u4ece\u672a\u9047\u5230\u654c\u624b\uff0c\u5982\u4eca\u7834\u8863\u4f24\u624b\uff0c\u8fde\u756a\u53d7\u632b\uff0c\u6fc0\u8d77\u4ed6\u51f6\u66b4\u91ce \u6027\uff0c\u6740\u5fc3\u5927\u8d77\uff0c\u54ac\u7259\u5fcd\u4f4f\u5de6\u638c\u4f24\u75db\uff0c\u89c1\u7aef\u65b9\u516c\u5b50\u5df2\u683d\u4e0b\u5730\u53bb\uff0c\u968f\u540e\u7eb5\u8eab\u8ffd\u4e0b\u5ca9\u77f3\u3002 \u3000\u3000\u7aef\u65b9\u516c\u5b50\u867d\u7136\u88ab\u94c1\u7ffc\u98de\u9e4f\u638c\u98ce\u9707\u4e0b\u5ca9\u77f3\uff0c\u53ea\u8d1f\u8f7b\u4f24\uff0c\u4e95\u672a\u81f4\u547d\uff0c\u5934\u4e0b\u811a\u4e0a\u683d\u4e0b\uff0c\u5c06\u53ca\u5730 \u9762\uff0c\u731b\u4e00\u63d0\u6c14\uff0c\u534a\u7a7a\u7ffb\u8f6c\uff0c\u4ecd\u7136\u53cc\u811a\u7a33\u7ad9\u4e8e\u5730\u9762\u3002 \u3000\u3000\u6b64\u65f6\u201c\u94c1\u7ffc\u98de\u9e4f\u201d\u5df2\u968f\u540e\u7eb5\u4e86\u4e0b\u6765\uff0c\u66f4\u4e0d\u7b54\u8bdd\uff0c\u4e3e\u8d77\u53f3\u638c\uff0c\u6076\u72e0\u72e0\u5730\u5411\u7aef\u65b9\u516c\u5b50\u5934\u9876\u5288 \u53bb\u3002 \u3000\u3000\u7aef\u65b9\u516c\u5b50\u77e5\u9053\u4ed6\u638c\u6c89\u529b\u731b\uff0c\u81ea\u5df2\u752b\u884c\u8d1f\u4f24\uff0c\u4e0d\u6562\u786c\u63a5\uff0c\u89c1\u94c1\u7ffc\u98de\u9e4f\u638c\u5230\uff0c\u98d8\u8eab\u95ea\u8fc7\u3002 \u3000\u3000\u4f46\u201c\u94c1\u7ffc\u98de\u9e4f\u201d\u5de6\u638c\u4f24\u5904\uff0c\u75db\u5f7b\u5fc3\u80ba\uff0c\u628a\u7aef\u65b9\u516c\u5b50\u6068\u5165\u9aa8\u9ad3\uff0c\u4e00\u638c\u843d\u7eb8\u4e0a\u6b65\u6a2a\u81c2\uff0c\u65bd\u51fa \u201c\u6a2a\u626b\u4e94\u5cb3\u201d\u62db\u5f0f\uff0c\u5411\u7aef\u65b9\u516c\u5b50\u62e6\u8170\u626b\u4f46\u7aef\u65b9\u516c\u5b50\u53d7\u4f24\u843d\u5ca9\uff0c\u4e00\u58f0\u60e8\u55e5\uff0c\u65e9\u5df3\u60ca\u52a8\u4e86\u7a77\u5bb6\u5e2e \u4f17\uff0c\u89c1\u7aef\u65b9\u516c\u5b50\u52bf\u5371\uff0c\u7eb7\u7eb7\u4ece\u9635\u5185\u8dd1\u6765\u6551\u63f4\u3002\u201c\u94c1\u7ffc\u98de\u9e4f\u201d\u7b2c\u4e8c\u638c\u672a\u5230\uff0c\u5341\u6570\u6761\u9752\u7af9\u6756\u5df2\u9f50 \u884c\u6325\u81f3\uff0c\u786c\u628a\u201c\u94c1\u7ffc\u98de\u9e4f\u201d\u903c\u9000\u3002 \u3000\u3000\u201c\u94c1\u7ffc\u98de\u9e4f\u201d\u6012\u4e0a\u52a0\u6012\uff0c\u94c1\u7ffc\u731b\u6251\uff0c\u53cc\u638c\u731b\u63a8\uff0c\u628a\u8702\u62e5\u800c\u81f3\u7684\u7a77\u5bb6\u5e2e\u4f17\uff0c\u6253\u7ffb\u4e86\u597d\u51e0 \u540d\u3002 \u3000\u3000\u4f46\u7a77\u5bb6\u5e2e\u4eba\u591a\uff0c\u6253\u9000\u4e00\u6279\u53c8\u4e0a\u6765\u4e00\u6279\uff0c\u4ecd\u7136\u628a\u7aef\u65b9\u516c\u5b50\u6551\u51fa\uff0c\u628a\u201c\u94c1\u7ffc\u98de\u9e4f\u201d\u56f4\u4f4f\u3002 \u3000\u3000\u6df7\u6218\u4e2d\u6b7b\u4f24\u4e92\u89c1\uff0c\u91d1\u5e9c\u9ad8\u624b\u56fa\u7136\u5df2\u6709\u6570\u540d\u9635\u4ea1\uff0c\u4f46\u7a77\u5bb6\u5e2e\u7684\u5e2e\u4f17\uff0c\u5728\u9635\u5f0f\u6563\u4e71\u4e4b\u540e\uff0c\u5df2 \u4e0d\u80fd\u53d1\u6325\u7edf\u5408\u6218\u529b\uff0c\u6b7b\u4f24\u5728\u91d1\u5e9c\u9ad8\u624b\u53ca\u5c55\u81ea\u201c\u65e0\u60c5\u78a7\u5251\u201d\u4e4b\u4e0b\u7684\u66f4\u591a\u3002 \u3000\u3000\u5c24\u5176\u201c\u91d1\u5e9c\u53cc\u94c1\u536b\uff1f\u529f\u529b\u9ad8\u5f3a\uff0c\u5fc3\u72e0\u624b\u8fa3\uff0c\u6bcf\u62db\u6bcf\u5f0f\u65bd\u51fa\uff0c\u5747\u6709\u4e09\u4e94\u540d\u53eb\u5316\u53d7\u4f24\u5012\u6bd9\u3002 \u3000\u3000\u8fd9\u771f\u662f\u4e00\u573a\u597d\u6740\uff0c\u53ea\u89c1\u5c18\u6284\u98de\u626c\uff0c\u558a\u6740\u9707\u5929\uff01\u7a81\u542c\u4e00\u58f0\u5927\u559d\uff1a\u201c\u4f4f\u624b\uff01\u201d \u3000\u3000\u8fd9\u559d\u58f0\u4e2d\u6c14\u5145\u8db3\uff0c\u58f0\u5982\u96f7\u9707\uff0c\u9707\u5f97\u4f17\u5165\u8033\u55e1\u9e23\uff0c\u8eab\u4e0d\u7531\u5df1\u5730\u5404\u81ea\u505c\u4e0b\u624b\u6765\u3002 \u3000\u3000\u53ea\u89c1\u5bf9\u9762\u5c71\u5cf0\u4e0a\u8f6c\u51fa\u3002\u8fd9\u7fa4\u6b66\u6797\u4eba\u7269\uff0c\u7ea6\u6709\u5341\u6570\u540d\u4e4b\u591a\uff0c\u559d\u58f0\u4e0d\u77e5\u7531\u4f55\u4eba\u53d1\u51fa\uff1f\u4f46\u8eab\u6cd5 \u5374\u90fd\u662f\u5feb\u5f97\u51fa\u5947\uff0c\u4ece\u5c71\u5cf0\u4e0a\u73b0\u8eab\uff0c\u5230\u8dd1\u81f3\u4f17\u4eba\u52a8\u624b\u4e4b\u5904\uff0c\u7ea6\u6709\u6570\u5341\u4e08\u7684\u8ddd\u79bb\uff0c\u604d\u773c\u5373\u81f3\u3002 \u3000\u3000\u53ea\u89c1\u2014\u535c\u6570\u6761\u4eba\u5f71\uff0c\u661f\u98de\u4e38\u5c04\uff0c\u98de\u5feb\u5730\u63a0\u81f3\u4f17\u4eba\u9762\u524d\uff0c\u4e2a\u4e2a\u90fd\u662f\u8eab\u8eaf\u527d\u608d\uff0c\u6b65\u5c65\u5982\u884c\u4e91 \u6d41\u6c34\uff0c\u53cc\u773c\u7cbe\u5149\u95ea\u95ea\uff0c\u770b\u6837\u5b50\u90fd\u662f\u8eab\u8d1f\u9ad8\u5f3a\u6b66\u529f\u4e4b\u58eb\u3002 \u3000\u3000\u4e3a\u9996\u662f\u4e00\u4e2a\u5bd2\u5112\u4f3c\u7684\u7a77\u9178\uff0c\u8eab\u7a7f\u69db\u697c\u957f\u886b\uff0c\u811a\u8e6c\u7834\u5e03\u978d\uff0c\u624b\u62ff\u4e00\u672c\u70c2\u4e66\uff0c\u4f46\u50cf\u8c8c\u5374\u751f\u5f97 \u9887\u4e3a\u4e0d\u51e1\uff0c\u56db\u65b9\u8138\uff0c\u767d\u51c0\u65e0\u987b\uff0c\u7ec6\u7709\u957f\u76ee\uff0c\u770b\u5e74\u7eaa\u4e0d\u5927\uff0c\u6700\u591a\u4e0d\u8fc7\u4e8c\u5341\u56db\u4e94\u5c81\uff0c\u5374\u9690\u7136\u6709\u5927 \u5bb6\u98ce\u8303\u3002 \u3000\u3000\u5c55\u767d\u9996\u5148\u8ba4\u51fa\u4e3a\u9996\u4e4b\u4eba\uff0c\u6b63\u662f\u201c\u5b89\u4e50\u516c\u5b50\u201d\uff0c\u6469\u4e91\u795e\u624b\u5411\u51b2\u5929\uff0c\u5c31\u8ddf\u5728\u5b89\u4e50\u516c\u5b50\u8eab\u540e\uff0c \u5176\u4f59\u9ad8\u624b\uff0c\u5c55\u767d\u5374\u90fd\u4e0d\u8ba4\u8bc6\u3002 \u3000\u3000\u5b89\u4e50\u5206\u5b50\u4e91\u6323\u7387\u9886\u5c5e\u4e0b\u5341\u6570\u540d\u9ad8\u624b\uff0c\u98de\u63a0\u800c\u81f3\uff0c\u9996\u5148\u4e5f\u770b\u5230\u5c55\u767d\uff0c\u5b89\u4e50\u516c\u5b50\u5fae\u7b11\u70b9\u5934\uff0c \u4f46\u4ed6\u7684\u773c\u5149\u5ffd\u7136\u770b\u5230\u5c55\u767d\u624b\u4e0a\u62ff\u7740\u7684\u201c\u65e0\u60c5\u78a7\u5251\u201d\uff0c\u4e0d\u7531\u773c\u9732\u5947\u5149\uff0c\u9053\uff1a\u201c\u606d\u559c\u5c55\u5144\uff0c\u5931\u5251 \u590d\u5f97\uff01\u201d \u3000\u3000\u5c55\u767d\u9053\uff1a\u201c\u6258\u516c\u5b50\u7684\u798f\uff01\u2026\u2026\u201d \u3000\u3000\u4ee5\u524d\u5c55\u767d\u89c1\u4e91\u6323\u6717\u6717\u4fa0\u884c\uff0c\u66fe\u6709\u7ed3\u4ea4\u4e4b\u5fc3\uff0c\u4f46\u81ea\u4ece\u77e5\u9053\u6b66\u6797\u56db\u516c\u5b50\u4e4b\u7236\u90fd\u662f\u81ea\u5df1\u6740\u7236\u4ec7 \u4eba\u4e4b\u540e\uff0c\u7acb\u523b\u6253\u6d88\u6b64\u5ff5\uff0c\u800c\u53e3\u5934\u4e0a\u4e5f\u5c31\u4e0d\u592a\u5ba2\u6c14\u3002 \u3000\u3000\u5b89\u4e50\u516c\u5b50\u8138\u4e0a\u5fae\u5fae\u4e00\u7ea2\uff01\u56e0\u4e3a\u662f\u5728\u4ed6\u624b\u4e2d\u628a\u5b9d\u5251\u88ab\u4eba\u593a\u8d70\uff0c\u4ed6\u8ffd\u4e86\u534a\u5929\u6ca1\u8ffd\u4e0a\uff0c\u5982\u4eca\u5b9d \u5251\u5374\u8ba9\u4eba\u5bb6\u81ea\u5df1\u627e\u56de\u6765\u4e86\uff0c\u8fd9\u4e2a\u8ddf\u5934\u683d\u5f97\u4e0d\u8f7b\uff0c\u7adf\u4e00\u65f6\u65e0\u8a00\u53ef\u5bf9\uff0c\u53ea\u6709\u82e6\u7b11\u4e86\u4e00\u4e0b\u2026\u2026 \u3000\u3000\u6b64\u65f6\u5a49\u513f\u5df2\u98de\u8eab\u63a0\u4e0b\u5ca9\u77f3\uff0c\u7ad9\u5728\u5c55\u767d\u8eab\u8fb9\uff0c\u542c\u5c55\u767d\u7ba1\u6765\u7684\u4e00\u4e2a\u7a77\u9178\u4e5f\u79f0\u547c\u201c\u516c\u5b50\u201d\uff0c\u4e0d \u7531\u7b11\u9053\uff1a\u201c\u53c8\u6765\u4e86\u4e00\u4e2a\u516c\u5b50\uff01\u516c\u5b50\u4f55\u5176\u591a\u5440\uff1f\u201d \u3000\u3000\u5979\u7684\u610f\u601d\u662f\uff0c\u77a7\u4e0d\u8d77\u773c\u524d\u7684\u4e5f\u914d\u79f0\u516c\u5b50\u3002 \u3000\u3000\u5c55\u767d\u9053\uff1a\u201c\u4f60\u4e0d\u8ba4\u8bc6\u5417\uff1f\u8fd9\u4f4d\u6b63\u662f\u4e0e\u4f60\u54e5\u54e5\u5728\u6b66\u6797\u9f50\u540d\u7684\u2018\u5b89\u4e50\u516c\u5b50\u2019\uff01\u201d \u3000\u3000\u4f17\u4eba\u95fb\u542c\uff0c\u4e00\u9f50\u8138\u73b0\u60ca\u5bb9\uff0c\u591a\u6253\u91cf\u4e86\u5b89\u4e50\u5206\u5b50\u51e0\u773c\u3002 \u3000\u3000\u5b89\u4e50\u516c\u5b50\u5374\u56db\u4e0b\u4e00\u62f1\u624b\u9053\uff1a\u201c\u5728\u4e0b\u4e91\u94ee\uff0c\u8499\u6c5f\u6e56\u670b\u53cb\u62ac\u7231\uff0c\u547c\u4e3a\u5b89\u4e50\u516c\u5b50\uff0c\u4eca\u65e5\u5076\u7136\u8def \u8fc7\u6b64\u5730\uff0c\u4e0d\u77e5\u8bfa\u4f4d\u670b\u53cb\u56e0\u4f55\u6545\u53ae\u6740\uff1f\u201d \u3000\u3000\u7965\u9e9f\u516c\u5b50\u4e5f\u4e00\u62b1\u62f3\u9053\uff1a\u201c\u4e45\u4ef0\uff01\u4e45\u4ef0\uff01\u5728\u4e0b\u7965\u9e9f\uff0c\u4e0e\u8d35\u516c\u5b50\u867d\u672a\u8c0b\u9762\uff0c\u53ef\u8bf4\u662f\u795e\u4ea4\u5df2 \u4e45\u3002\u201d \u3000\u3000\u5b89\u4e50\u516c\u5b50\u53ca\u5c5e\u4e0b\u9ad8\u624b\uff0c\u4e00\u542c\u7965\u9e9f\u81ea\u62a5\u540d\u53f7\uff0c\u4e5f\u4ff1\u81ea\u8138\u73b0\u60ca\u5bb9\uff0c\u5b89\u4e50\u5206\u5b50\u54c8\u54c8\u4e00\u7b11\u9053\uff1a \u201c\u5931\u656c\uff01\u5931\u656c\uff01\u539f\u6765\u662f\u9f0e\u9f0e\u5927\u540d\u7684\u7965\u9e9f\u516c\u5b50\uff01\u770b\u6765\u6211\u8fd9\u8d38\u7136\u51fa\u5934\uff0c\u662f\u591a\u6b64\u4e00\u4e3e\u4e86\uff01\u201d \u3000\u3000\u539f\u6765\u5b89\u4e50\u516c\u5b50\u559c\u7ba1\u95f2\u4e8b\uff0c\u4e00\u542c\u773c\u524d\u95f9\u4e8b\u4e4b\u4eba\u662f\u7965\u9e9f\u516c\u5b50\uff0c\u4fbf\u77e5\u4eca\u5929\u7684\u95f2\u4e8b\u81ea\u5df1\u4e0d\u4e00\u5b9a\u80fd \u63fd\u5f97\u4e0b\uff0c\u6545\u800c\u6709\u6b64\u4e00\u8bf4\u3002\u90a3\u8fb9\u7684\u7aef\u65b9\u516c\u5b50\uff0c\u89c1\u4e8c\u4eba\u4e92\u76f8\u5439\u6367\uff0c\u5374\u628a\u81ea\u5df1\u629b\u5728\u4e00\u8fb9\uff0c\u4e0d\u7531\u4e8e\u54b3 \u4e86\u4e00\u58f0\u3002 \u3000\u3000\u5a49\u513f\u5fc3\u601d\u7ec6\u5bc6\uff0c\u7aef\u65b9\u516c\u5b50\u5728\u65c1\u8fb9\u4e8e\u54b3\uff0c\u5979\u65e9\u77e5\u5176\u610f\uff0c\u5029\u7136\u7b11\u9053\uff1a\u201c\u4eca\u5929\u53ef\u662f\u5e78\u4f1a\uff0c\u6b66\u6797 \u56db\u516c\u5b50\uff0c\u5012\u6709\u4e09\u4f4d\u5728\u6b64\u5730\u9732\u9762\uff0c\u6765\uff01\u6211\u7ed9\u8bf8\u4f4d\u5f15\u89c1\uff01\u2014\u2014\u201d\u8bf4\u7740\u7ea4\u624b\u4e00\u6307\u7aef\u65b9\u516c\u5b50\u9053\uff1a\u201c\u8fd9 \u4f4d\u5c31\u662f\u7aef\u65b9\u516c\u5b50\uff01\u201d \u3000\u3000\u7aef\u65b9\u516c\u5b50\u4e00\u62b1\u62f3\uff0c\u8fde\u8bf4\uff1a\u201c\u5e78\u4f1a\uff01\u5e78\u4f1a\uff01\u201d \u3000\u3000\u5b89\u4e50\u516c\u5b50\u53ca\u5c5e\u4e0b\u9ad8\u624b\uff0c\u66f4\u662f\u4e00\u6014\uff0c\u60f3\u4e0d\u5230\u5f15\u8d77\u4e89\u7aef\u7684\u7adf\u662f\u6b66\u6797\u56db\u516c\u5b50\u4e4b\u4e2d\u7684\u4e24\u5927\u516c\u5b50\uff01 \u3000\u3000\u5a49\u513f\u63a5\u7740\u53c8\u8bf4\u9053\uff1a\u201c\u770b\u6765\u6211\u771f\u8981\u56de\u53bb\u53eb\u6211\u54e5\u54e5\u4e86\uff01\u201d \u3000\u3000\u5c55\u767d\u4e00\u65f6\u672b\u4f1a\u8fc7\u610f\u6765\uff0c\u6115\u7136\u9053\uff1a\u201c\u53eb\u4f60\u54e5\u54e5\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5b89\u4e50\u516c\u5b50\u65e9\u5df1\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u4e0d\u7528\u8bf4\uff0c\u8fd9\u4f4d\u59d1\u5a18\u4e00\u5b9a\u662f\u51cc\u98ce\u516c\u5b50\u4e4b\u59b9\u4e86\uff01\u201d \u3000\u3000\u5c55\u767d\u604d\u7136\u5475\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u540d\u91cd\u6b66\u6797\u7684\u56db\u516c\u5b50\u4f1a\u9762\uff0c\u5012\u7684\u786e\u662f\u6b66\u6797\u4e00\u5927\u80dc\u4e3e\uff01\u53ef\u60dc\uff0c\u4f60 \u54e5\u54e5\u8d76\u4e0d\u53ca\u6765\u6b64\u4e00\u4f1a\u4e86\uff01\u201d \u3000\u3000\u7965\u9e9f\u516c\u5b50\u5fc3\u4e2d\u4e00\u52a8\uff0c\u4ed6\u672c\u6765\u5fc3\u6000\u58ee\u5fd7\uff0c\u65e9\u6709\u538b\u5012\u5176\u4ed6\u4e09\u4f4d\u516c\u4e8e\u3001\u79f0\u9738\u6b66\u6797\u3001\u9886\u8896\u7fa4\u96c4\u4e4b \u5fc3\uff0c\u968f\u4e4b\u4ef0\u5929\u4e00\u9635\u8c6a\u7b11\u9053\uff1a\u8fd9\u6709\u4f55\u96be\uff01 \u3000\u3000\u6211\u4eec\u6b66\u6797\u56db\u516c\u5b50\uff0c\u6c5f\u6e56\u9f50\u540d\uff0c\u7965\u9e9f\u65e9\u60f3\u4e00\u4f1a\uff0c\u5982\u4eca\u5c55\u5144\u63d0\u8d77\uff0c\u7965\u9e9f\u5f88\u60f3\u501f\u6b64\u673a\u4f1a\uff0c\u7ea6\u8bf7 \u4e09\u4f4d\u516c\u5b50\u9a7e\u4e34\u5bd2\u820d\u4e00\u4f1a\uff0c\u4e0d\u77e5\u4e8c\u4f4d\u516c\u5b50\u53ca\u5c55\u5c0f\u59d0\u80af\u8d4f\u5149\u5426\uff1f\u201d \u3000\u3000\u7aef\u65b9\u516c\u5b50\u8138\u4e00\u5bd2\uff0c\u9053\uff1a\u201c\u90a3\u4e48\u6211\u4eec\u4eca\u5929\u7684\u4e8b\u5982\u4f55\u89e3\u51b3\uff1f\u201d \u3000\u3000\u7a77\u5bb6\u5e2e\u7684\u4eba\u7269\u56e0\u4e3a\u6b7b\u4f24\u60e8\u91cd\uff0c\u4e2a\u4e2a\u773c\u7ea2\uff0c\u95fb\u8a00\u5f80\u4e0a\u201d\u4e00\u56f4\uff0c\u610f\u6b32\u518d\u52a8\u2026\u2026 \u3000\u3000\u7965\u9e9f\u516c\u5b50\u51b7\u7b11\u4e00\u58f0\uff0c\u7b54\u9053\uff1a\u6b66\u6797\u56db\u516c\u5b50\u805a\u9f50\uff0c\u54b1\u4eec\u65b0\u5e10\u65e7\u5e10\uff0c\u4e00\u9f50\u7ed3\u7b97\uff0c\u4e0d\u662f\u66f4\u516c\u5e73\u5408 \u7406\u5417\uff1f\u201d\u7965\u9e9f\u516c\u5b50\u7d20\u5177\u5fc3\u673a\uff0c\u8fd9\u8bdd\u8868\u9762\u542c\u6765\u51a0\u5195\u5802\u7687\uff0c\u4e8b\u5b9e\u4e0a\u4ed6\u5374\u662f\u611f\u5230\u4eba\u5355\u52bf\u5b64\uff0c\u5b89\u4e50\u516c \u5b50\u654c\u53cb\u4e0d\u660e\uff0c\u518d\u8981\u6253\u4e0b\u53bb\uff0c\u6050\u6015\u5403\u4e86\u4e8f\uff0c\u6240\u4ee5\u6709\u6b64\u4e00\u8bf4\u3002 \u3000\u3000\u7aef\u65b9\u516c\u5b50\u4e5f\u6709\u987e\u5fcc\uff0c\u53ea\u56e0\u5c5e\u4e0b\u7a77\u5bb6\u5e2e\u7684\u4eba\u6b7b\u4f24\u592a\u91cd\uff0c\u4e0d\u80fd\u4e0d\u5145\u786c\u53d1\u72e0\uff0c\u4e3a\u5c5e\u4e0b\u6491\u8170\uff0c\u542c \u7965\u9e9f\u516c\u5b50\u4e00\u8bf4\uff0c\u4e5f\u987a\u5761\u4e0b\u53f0\u9053\uff1a\u201c\u4f46\u4e0d\u77e5\u54ea\u4e00\u5929\uff1f\u201d \u3000\u3000\u7965\u9e9f\u516c\u5b50\u4ef0\u8138\u601d\u7d22\u4e00\u9635\uff0c\u9053\uff1a\u201c\u5f53\u524d\u5e74\u5173\u5c06\u5c4a\uff0c\u60f3\u6bcf\u4e2a\u4eba\u53ef\u80fd\u90fd\u6709\u70b9\u79c1\u4e8b\uff0c\u800c\u4e14\u4e3a\u4e86\u4e0d \u5f71\u54cd\u5927\u5bb6\u5feb\u4e50\u8fc7\u4e2d\uff0c\u54b1\u4eec\u5c31\u8ba2\u4e3a\u660e\u5e74\u5143\u5bb5\u8282\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u672a\u7b49\u7aef\u65b9\u516c\u5b50\u7b54\u8a00\uff0c\u5b89\u4e50\u516c\u5b50\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u6700\u597d\u662f\u665a\u4e0a\uff0c\u5143\u5bb5\u8282\u8d4f\u706f\u5927\u4f1a\uff0c\u6b66\u6797\u56db\u516c \u5b50\u5357\u4eac\u57ce\u805a\u5e76\uff0c\u53ef\u5728\u6c5f\u6e56\u4e0a\u4f20\u4e3a\u4e00\u6bb5\u4f73\u8bdd\uff01\u201d\u7aef\u65b9\u516c\u5b50\u4e5f\u8c6a\u6c14\u5e72\u4e91\u5730\u7b54\u9053\uff1a\u201c\u5143\u5bb5\u8282\u524d\u7aef\u65b9 \u5fc5\u5230\uff0c\u800c\u4e14\u4e3a\u4e86\u51d1\u5174\u8d77\u89c1\uff0c\u7aef\u65b9\u5c06\u968f\u5e26\u6b66\u6797\u81f3\u5b9d\u2018\u907f\u6c34\u7389\u58c1\u2019\u4e00\u540c\u8d74\u4f1a\uff01\u201d \u3000\u3000\u4f17\u4eba\u95fb\u8a00\uff0c\u4e00\u9f50\u8272\u53d8\uff0c\u7965\u9e9f\u516c\u5b50\u8138\u8272\u66f4\u89c1\u96be\u770b\uff0c\u56e0\u4e3a\u7aef\u65b9\u516c\u5b50\u6240\u8bf4\u7684\u201c\u907f\u6c34\u7389\u74a7\u201d\uff0c\u6b63 \u662f\u4ed6\u5bb6\u4e2d\u7684\u4f20\u5bb6\u4e4b\u5b9d\uff0c\u4e09\u6708\u4e4b\u524d\u88ab\u76d7\uff0c\u4ed6\u4eca\u5929\u4e32\u9886\u5c5e\u4e0b\u9ad8\u624b\u4eb2\u81ea\u51fa\u52a8\uff0c\u4e5f\u5c31\u662f\u4e3a\u4e86\u5bfb\u627e\u6b64\u5931 \u5b9d\uff0c\u5148\u524d\u7591\u5fc3\u662f\u7aef\u65b9\u516c\u5b50\u652f\u4f7f\u624b\u4e0b\u76d7\u8d70\uff0c\u4f46\u8fd8\u672a\u6562\u8bc1\u5b9e\uff0c\u5982\u4eca\u542c\u7aef\u65b9\u516c\u5b50\u4eb2\u53e3\u8bf4\u51fa\uff0c\u4e0d\u7531\u53c8 \u60ca\u53c8\u6012\uff0c\u4e5f\u63a5\u53e3\u7b54\u9053\uff1a\u201c\u597d\uff01\u4e00\u8a00\u4e3a\u5b9a\uff01\u7965\u9e9f\u4e3a\u4e86\u916c\u8c22\u96c5\u610f\uff0c\u5728\u4f1a\u4e0a\u4e5f\u4f1a\u73b0\u51fa\u4e00\u5b97\u5f02\u5b9d\u2018\u5927 \u7f57\u91d1\u4e39\u2019\uff0c\u7ed9\u4e0e\u4f1a\u4e4b\u4eba\u89c1\u8bc6\u89c1\u8bc6\uff01\u201d \u3000\u3000\u8fd9\u56de\u8be5\u8bba\u5230\u7aef\u65b9\u516c\u5b50\u53d8\u989c\u8272\u4e86\uff0c\u56e0\u8fd9\u201c\u5927\u7f57\u91d1\u4e39\u201d\u4e5f\u6b63\u662f\u4ed6\u5bb6\u7684\u4f20\u5bb6\u4e4b\u5b9d\uff0c\u5728\u5317\u4eac\u8fd0\u5f80 \u676d\u5dde\u7684\u8def\u4e0a\u9057\u5931\uff0c\u4ed6\u7387\u9886\u7a77\u5bb6\u5e2e\u7684\u9ad8\u624b\uff0c\u4e5f\u6b63\u662f\u4e3a\u5bfb\u627e\u6b64\u5b9d\uff0c\u624d\u6765\u5230\u5ca9\u5c71\u5341\u4e8c\u6d1e\u4e00\u5e26\uff0c\u641c\u5bfb \u6570\u6708\u4e4b\u4e45\u5374\u8fde\u4e2a\u7709\u76ee\u4e5f\u6ca1\u6709\uff0c\u7965\u9e9f\u516c\u5b50\u8fd9\u4e00\u8bf4\uff0c\u624d\u77e5\u662f\u88ab\u91d1\u5e9c\u4e4b\u4eba\u62e6\u8def\u52ab\u8d70\u3002 \u3000\u3000\u5b89\u4e50\u516c\u5b50\u5374\u54c8\u54c8\u5927\u7b11\uff0c\u4e00\u9707\u624b\u4e2d\u70c2\u4e66\u9053\uff1a\u201c\u4e09\u4f4d\u516c\u5b50\u8c6a\u5174\u5e72\u4e91\uff0c\u4e0d\u541d\u4ee5\u6b66\u6797\u5f02\u5b9d\u4e3a\u6b66\u6797 \u4eba\u58eb\u5f00\u773c\uff0c\u4e91\u94ee\u4e5f\u4e0d\u80fd\u592a\u5c0f\u5bb6\u5b50\u6c14\uff0c\u5c4a\u65f6\u4e00\u5b9a\u643a\u5e26\u300a\u6b66\u5b66\u771f\u7ecf\u300b\u8d74\u4f1a\uff01\u201d \u3000\u3000\u5b89\u4e50\u516c\u5b50\u6b64\u8a00\u4e00\u51fa\uff0c\u4f17\u4eba\u66f4\u662f\u5403\u60ca\u2026\u2026 \u3000\u3000\u5c55\u767d\u5374\u6ee1\u8138\u60b2\u6124\uff0c\u6012\u773a\u89c1\u8840\uff0c\u5207\u9f7f\u5583\u5583\u81ea\u8bed\u9053\uff1a\u201c\u4e09\u5b97\u5f02\u5b9d\uff01\u4e09\u5b97\u5f02\u5b9d\uff01\u6ca1\u6709\u9519\uff0c\u4e00\u5b9a \u662f\u4ed6\u4eec\uff01\u2026\u2026\u201d \u7b2c\u4e09\u5341\u4e00\u7ae0\u3000\u5371\u673a\u56db\u4f0f>> \u53e4\u9f99\u300a\u5251\u5ba2\u884c\u300b \u7b2c\u4e09\u5341\u4e00\u7ae0\u3000\u5371\u673a\u56db\u4f0f \u5b8b\u4ee3\u624d\u5973\u6731\u6dd1\u8d1e\u6709\u4e00\u9996\u8bcd\uff0c\u6b4c\u8bf5\u5143\u5bb5\u4f73\u8282\u9752\u5e74\u7537\u5973\u7684\u6b22\u4e50\u4e0e\u79bb\u6101\u4e91\uff1a \u53bb\u5e74\u5143\u591c\u65f6\uff0c \u82b1\u5e02\u706f\u5982\u753b\uff0c \u6708\u4e0a\u67f3\u68a2\u5934\uff0c \u4eba\u7ea6\u9ec4\u660f\u540e\uff1b \u4eca\u5e74\u5143\u591c\u65f6\uff0c \u82b1\u4e0e\u706f\u4f9d\u65e7\uff0c \u4e0d\u89c1\u53bb\u5e74\u4eba\uff0c \u6cea\u6e7f\u7f57\u886b\u8896\u3002 \u8fd9\u56fa\u7136\u662f\u63cf\u5199\u604b\u7231\u3001\u9752\u5e74\u7537\u5973\u7684\u76f8\u805a\u4e4b\u6b22\u4e0e\u79bb\u522b\u4e4b\u82e6\uff0c\u4f46\u7531\u6b64\u4e5f\u53ef\u770b\u51fa\u5143\u5bb5\u4f73\u8282\u7684\u70ed\u95f9 \u60c5\u666f\u6765\u3002 \u5357\u4eac\uff0c\u4e3a\u516d\u671d\u53e4\u90fd\uff0c\u4eba\u6587\u835f\u8403\uff0c\u7269\u534e\u5929\u5b9d\uff0c\u5c24\u5176\u5230\u4e86\u5143\u5bb5\u4f73\u8282\uff0c\u5bb6\u5bb6\u60ac\u706f\uff0c\u6237\u6237\u7ed3\u5f69\uff0c \u5343\u5343\u4e07\u4e07\u7684\u82b1\u706f\uff0c\u7167\u8000\u5f97\u5927\u8857\u5c0f\u5df7\u8f89\u714c\u5982\u540c\u767d\u65e5\uff0c\u53ea\u89c1\u5404\u8272\u5404\u6837\u7684\u82b1\u706f\uff0c\u591a\u5982\u6ee1\u7a7a\u7e41\u661f\uff0c\u4ece \u592a\u9633\u672a\u843d\u5c71\u4fbf\u5df2\u7ecf\u60ac\u6302\u8d77\u6765\u4e86\uff0c\u4e00\u76f4\u4ece\u9ed1\u591c\u71c3\u4eae\u5230\u5929\u660e\u3002 \u5404\u79cd\u82b1\u706f\uff0c\u4e89\u5947\u6597\u5de7\uff0c\u5728\u5341\u5b57\u8857\u5546\uff0c\u66f4\u6709\u201c\u706f\u5c71\u201d\u3001\u201c\u706f\u724c\u697c\u201d\u7b49\u7b49\uff0c\u9664\u4e86\u82b1\u706f\u5f0f\u6837\u7ffb \u65b0\uff0c\u7f8e\u4e0d\u80dc\u6536\u4e4b\u5916\uff0c\u66f4\u6709\u5f15\u4eba\u4eba\u80dc\u7684\u201c\u706f\u864e\u201d\uff0c\u4e3a\u6e38\u4eba\u52a9\u5174\uff0c\u5c04\u4e2d\u7684\u8fd8\u6709\u5f69\u5934\u8d60\u5956\uff0c\u56e0\u6b64 \u4e0a\uff0c\u5929\u672a\u5165\u591c\uff0c\u5927\u8857\u4e0a\u5df2\u662f\u4eba\u5c71\u4eba\u6d77\uff0c\u5230\u5904\u90fd\u662f\u6469\u80a9\u63a5\u8e35\u7684\u770b\u706f\u4eba\u6f6e\u3002 \u5bcc\u7532\u5168\u56fd\u3001\u53f7\u79f0\u6c5f\u5357\u7b2c\u4e00\u5bb6\u7684\u5357\u4eac\u91d1\u5e9c\uff0c\u9ad8\u8038\u7684\u95e8\u524d\u9ad8\u642d\u5f69\u697c\uff0c\u6570\u767e\u76cf\u624e\u5236\u5f97\u5341\u5206\u7cbe\u5de7 \u7684\u7ea2\u4e1d\u5f69\u706f\uff0c\u628a\u4e00\u5ea7\u5dcd\u5ce8\u7684\u91d1\u5e9c\u5927\u95e8\uff0c\u7167\u8000\u5f97\u91d1\u58c1\u8f89\u714c\u3002 \u9ad8\u8fbe\u6570\u5341\u7ea7\u7684\u5927\u7406\u77f3\u53f0\u9636\u4e0a\uff0c\u96c1\u884c\u6392\u5f00\u4e24\u5217\u91d1\u76d4\u91d1\u7532\u7684\u8377\u6208\u6267\u5251\u7684\u6b66\u58eb\uff0c\u5728\u8f89\u714c\u8000\u773c\u7684 \u767e\u5343\u706f\u5149\u7167\u6620\u4e4b\u4e0b\uff0c\u5b9b\u5982\u65e0\u6570\u5c0a\u5927\u5e99\u524d\u7684\u91d1\u7532\u795e\u5c06\uff0c\u663e\u8d6b\u4e4b\u4e2d\uff0c\u5e26\u7740\u68ee\u4e25\u5a01\u731b\u7684\u6c14\u6c1b\uff0c\u4f7f\u4e00 \u5207\u591c\u6e38\u8d4f\u706f\u7684\u4eba\u4eec\uff0c\u53ea\u6709\u7ad9\u5f97\u8fdc\u8fdc\u5730\u77a0\u76ee\u7ed3\u820c\u5730\u7fa1\u671b\u7740\uff0c\u6ca1\u6709\u4eba\u6562\u63a5\u8fd1\u4e00\u6b65\u3002 \u53ef\u662f\uff0c\u8fd9\u753b\u621f\u68ee\u4e25\u7684\u91d1\u5e9c\uff0c\u5374\u4ecd\u7136\u70ed\u95f9\u65e0\u6bd4\uff0c\u53ea\u89c1\u4e2d\u95e8\u5927\u5f00\uff0c\u5954\u5411\u91d1\u5e9c\u7684\u4eba\u5ddd\u6d41\u4e0d\u606f\uff0c \u4e0d\u8fc7\u767b\u4e0a\u90a3\u9ad8\u5e7f\u77f3\u9636\u7684\u90fd\u662f\u4e9b\u4e0d\u89c1\u7684\u4eba\u7269\u7f62\u4e86\u3002 \u91d1\u5e9c\u5927\u95e8\u524d\uff0c\u9664\u4e86\u90a3\u4e9b\u5982\u91d1\u5851\u6ce5\u96d5\u7684\u91d1\u7532\u6b66\u58eb\uff0c\u6602\u7acb\u4e0d\u52a8\u4e4b\u5916\uff0c\u8fd8\u6709\u5341\u6570\u540d\u91d1\u5e9c\u98df\u5ba2\u4e2d \u7684\u6b66\u6797\u9ad8\u624b\uff0c\u7ad9\u5728\u95e8\u524d\u8fce\u63a5\u4f73\u5bbe\uff0c\u5176\u4e2d\u4e00\u4e2a\u4ec5\u6709\u4e00\u6761\u624b\u81c2\u7684\u4fca\u7f8e\u5c11\u5e74\uff0c\u7709\u76ee\u4e4b\u95f4\u8574\u85cf\u7740\u6076\u72e0 \u66b4\u623e\u4e4b\u6c14\uff0c\u6b63\u662f\u88ab\u5c55\u767d\u65ad\u4e86\u4e00\u81c2\u7684\u201c\u5c0f\u9752\u86a8\u201d\u5b5f\u5982\u840d\u3002 \u5b5f\u5982\u840d\u65ad\u81c2\u521d\u6108\uff0c\u628a\u5c55\u767d\u6068\u4e4b\u5165\u9aa8\uff0c\u672c\u60f3\u51fa\u53bb\u627e\u5c55\u767d\u62a5\u4ec7\uff0c\u540e\u6765\u542c\u8bf4\u5143\u5bb5\u8282\u8d4f\u706f\u5927\u4f1a\uff0c \u5c55\u767d\u4e5f\u8981\u6765\u53c2\u52a0\uff0c\u4f7f\u7b49\u5728\u91d1\u5e9c\uff0c\u53c8\u4fee\u7ec3\u4e86\u51e0\u624b\u6b79\u6bd2\u6b66\u529f\uff0c\u592a\u9633\u4e00\u843d\u5c71\u4fbf\u7ad9\u5728\u95e8\u524d\uff0c\u4e00\u8fb9\u8d1f\u8d23 \u8fce\u63a5\u524d\u6765\u4e0e\u4f1a\u5929\u4e0b\u7fa4\u96c4\uff0c\u4e00\u8fb9\u4e13\u7b49\u5c55\u767d\u524d\u6765\uff0c\u597d\u62a5\u96ea\u65ad\u81c2\u4e4b\u6068\u3002 \u53ef\u662f\uff0c\u4ece\u5348\u81f3\u9149\uff0c\u4e09\u5c71\u4e94\u5cb3\u7684\u82f1\u96c4\u3001\u56db\u6d77\u516b\u8352\u7684\u5f02\u58eb\uff0c\u5747\u5df2\u9732\u9762\u88ab\u8fce\u8fdb\u4e86\u91d1\u5e9c\uff0c\u5374\u4e0d\u89c1 \u5c55\u767d\u6765\u5230\u3002 \u201c\u5c0f\u9752\u86a8\u201d\u5b5f\u5982\u840d\u5fc3\u4e2d\u5931\u671b\uff0c\u4ee5\u4e3a\u5c55\u767d\u4e0d\u4f1a\u6765\u4e86\u3002\u4ed6\u6b63\u60f3\u4ea4\u4ee3\u95e8\u4e0b\u98df\u5ba2\u51e0\u53e5\uff0c\u8fdb\u5230\u5e9c \u5185\uff0c\u8c01\u77e5\u4ed6\u4e00\u811a\u8de8\u8fdb\u5927\u95e8\uff0c\u7a81\u542c\u94c3\u58f0\u7405\u7405\u8e44\u58f0\u5f97\u5f97\uff0c\u4e00\u4e2a\u82f1\u6c14\u52c3\u52c3\u7684\u9752\u5e74\uff0c\u80cc\u4e0a\u659c\u63d2\u4e00\u67c4\u770b \u6765\u5341\u5206\u540d\u8d35\u7684\u5b9d\u5251\uff0c\u80ef\u4e0b\u9a91\u7684\u5374\u662f\u4e00\u5339\u53c8\u7626\u53c8\u5c0f\u7684\u6bdb\u9a74\uff0c\u5728\u91d1\u5e9c\u95e8\u524d\u505c\u4e86\u4e0b\u6765\u3002 \u60f3\u4eca\u591c\u91d1\u5e9c\u4e4b\u4e2d\uff0c\u540d\u91cd\u6b66\u6797\u7684\u56db\u5927\u516c\u5b50\u4e4b\u4f1a\uff0c\u800c\u4e14\u8fd8\u6709\u6b66\u6797\u4eba\u773c\u89c1\u773c\u7ea2\u7684\u4e09\u5b97\u79d8\u5b9d\uff0c\u7279 \u5728\u6b66\u6797\u4eba\u58eb\u9762\u524d\u516c\u5f00\uff0c\u4e0e\u4f1a\u4e4b\u4eba\u4ff1\u662f\u5f53\u4eca\u6b66\u6797\u4e00\u65f6\u4fca\u5f66\uff0c\u6240\u6709\u6765\u53c2\u52a0\u5927\u4f1a\u4e4b\u4eba\uff0c\u4e2d\u662f\u9a91\u9ad8\u5934 \u9a8f\u9a6c\uff0c\u5373\u662f\u4e58\u5750\u8c6a\u534e\u8f7f\u8f66\uff0c\u54ea\u6709\u9a91\u8fd9\u79cd\u5bd2\u8f70\u6bdb\u9a74\u6765\u7684\uff1f \u53ef\u662f\uff0c\u8fd9\u9a91\u9a74\u5c11\u5e74\u6765\u5728\u91d1\u5e9c\u95e8\u53e3\uff0c\u5634\u4e2d\u201c\u5537\u201d\u7684\u4e00\u58f0\uff0c\u52d2\u6ce8\u7f30\u7ef3\uff0c\u7ade\u98d8\u8eab\u4e0b\u9a74\uff0c\u624b\u91cc\u7275 \u7740\u90a3\u6bd4\u4e00\u53ea\u72d7\u5927\u4e0d\u4e86\u591a\u5c11\u7684\u5c0f\u6bdb\u9a74\uff0c\u8fc8\u6b65\u5c31\u5f80\u91d1\u5e9c\u5927\u7406\u77f3\u7684\u53f0\u9636\u4e0a\u95ef\u3002 \u7ad9\u5728\u53f0\u9636\u4e0a\u7684\u91d1\u7532\u6b66\u58eb\uff0c\u6267\u5728\u624b\u4e2d\u7684\u957f\u671d\u5411\u5916\u2014\u63a8\u201c\u9535\uff01\u201d\u94c1\u621f\u4ea4\u53c9\uff0c\u963b\u4f4f\u53bb\u8def\uff0c\u540c\u65f6 \u4e00\u540d\u91d1\u7532\u6b66\u58eb\u5634\u4e2d\u559d\u9053\uff1a\u201c\u7ad9\u4f4f\uff01\u5357\u4eac\u91d1\u5e9c\uff0c\u4e5f\u662f\u53ef\u4ee5\u968f\u4fbf\u5f80\u91cc\u8d70\u7684\u5417\uff1f\u2026\u2026\u54ce\u5440\uff01\u2026\u2026\u201d \u672a\u89c1\u9a91\u9a74\u9752\u4e2d\u4e3e\u624b\uff0c\u90a3\u6a2a\u621f\u963b\u8def\u7684\u4e24\u540d\u91d1\u7532\u6b66\u58eb\uff0c\u559d\u58f0\u672a\u7adf\uff0c\u5374\u53e3\u53d1\u60ca\u547c\uff0c\u8ddf\u968f\u540e\u9000\u51fa \u4e94\u516d\u6b65\u53bb\uff0c\u800c\u90a3\u5c11\u5e74\u5374\u4ece\u5bb9\u4e3e\u6b65\u8de8\u4e0a\u77f3\u9636\u2026\u2026 \u8d1f\u8d23\u63a5\u5f85\u6765\u5bbe\u7684\u91d1\u5e9c\u95e8\u4e0b\u98df\u5bb9\uff0c\u90fd\u662f\u773c\u91cc\u64cd\u4e0d\u5f97\u534a\u7c92\u79d2\u5b50\u7684\u8001\u6c5f\u6e56\uff0c\u4e00\u89c1\u9a91\u9a74\u5c11\u5e74\u7fe9\u7fe9 \u795e\u91c7\uff0c\u4e0d\u7528\u51fa\u624b\uff0c\u4ee5\u5468\u8eab\u5e03\u6ee1\u7684\u7f61\u6c14\uff0c\u5c31\u53ef\u628a\u6b66\u529f\u4e0d\u5f31\u7684\u91d1\u7532\u6b66\u58eb\u9707\u9000\uff0c\u77e5\u9053\u6765\u4e86\u9ad8\u4eba\uff0c\u8fde \u5fd9\u51e0\u4e2a\u7eb5\u843d\uff0c\u4ece\u9ad8\u53f0\u9636\u4e0a\u9a70\u4e0b\u4e8c\u4eba\u6765\uff0c\u5411\u9a91\u9a74\u5c11\u5e74\u4e00\u62b1\u62f3\u9053\uff1a\u670b\u53cb\u662f\u54ea\u8def\u82f1\u96c4\u62a5\u4e2a\u540d\u6765\uff0c\u6211 \u4eec\u4e5f\u597d\u63a5\u2026\u2026\u201d \u8c01\u77e5\u8fd9\u4e8c\u4eba\u8bdd\u672b\u8bf4\u5b8c\uff0c\u9a91\u9a74\u5c11\u5e74\u4e00\u62ac\u5934\uff0c\u4e09\u4eba\u4e0d\u7981\u4e00\u6115\uff0c\u8fde\u5fd9\u6539\u53e3\u9053\uff1a\u201c\u539f\u6765\u662f\u5c55\u5c0f \u4fa0\uff0c\u8bf7\u8fdb\uff01\u8bf7\u8fdb\uff01\u201d \u56e0\u4e3a\u5c55\u767d\u5728\u91d1\u5e9c\u5927\u6218\u5b5f\u5982\u840d\uff0c\u4ed6\u4eec\u90fd\u4eb2\u773c\u770b\u5230\u8fc7\uff0c\u6240\u4ee5\u4e00\u89c1\u4fbf\u8ba4\u8bc6\uff0c\u5fd9\u53c8\u5429\u5490\u4e0b\u4eba\u63a5\u8fc7 \u5c55\u767d\u624b\u4e2d\u5c0f\u6bdb\u9a74\uff0c\u4ece\u508d\u95e8\u7275\u5165\u5b85\u5185\u2026\u2026 \u5c31\u5728\u4f17\u4eba\u8fd9\u4e00\u4e71\u7684\u5f53\u513f\uff0c\u5c55\u767d\u5374\u6602\u7136\u4e0d\u8bed\uff0c\u5507\u8fb9\u542b\u7740\u4e00\u4e1d\u51b7\u5cfb\u7b11\u610f\uff0c\u672a\u89c1\u4ed6\u811a\u5c16\u70b9\u5730\u8eab \u5f62\u5e73\u98de\u76f4\u5c04\u5730\u201c\u55d6\u201d\u4e00\u58f0\u5fae\u54cd\uff0c\u4eba\u5df2\u8de8\u767b\u6570\u5341\u7ea7\u9ad8\u53f0\u9636\uff0c\u5230\u8fbe\u4e86\u5927\u95e8\u4e4b\u524d\uff0e \u4f17\u4eba\u4e00\u9f50\u6628\u820c\uff0c\u6697\u8d5e\uff1a\u201c\u597d\u9ad8\u5f3a\u7684\u8f7b\u529f\u2026\u2014\u201d \u6b63\u597d\u201c\u5c0f\u9752\u86a8\u201d\u5b5f\u5982\u840d\u542c\u5230\u7eb7\u4e71\u4e4b\u58f0\uff0c\u8de8\u8fdb\u95e8\u7684\u4e09\u6761\u817f\u53c8\u6536\u56de\u4f17\u521a\u4e00\u8f6c\u8eab\uff0c\u773c\u524d\u4e00\u82b1\uff0c \u4e00\u4e2a\u82f1\u633a\u5c11\u5e74\u5e73\u7a7a\u6218\u5728\u95e8\u524d\u3002 \u5b5f\u5982\u840d\u62ac\u773c\u4e00\u770b\uff0c\u6765\u4eba\u6b63\u662f\u4e45\u7b49\u4e0d\u81f3\u7684\u5c55\u767d\uff0c\u8138\u8272\u5019\u53d8\uff0c\u731b\u53f1e\u9053\uff1a\u201c\u5c0f\u5b50\uff01\u4f60\u624d\u6765 \u5440\uff01\u201d \u8bf4\u7740\uff0c\u5355\u638c\u5982\u8001\u50e7\u95ee\u8baf\uff0c\u7f13\u7f13\u5411\u5c55\u767d\u80f8\u524d\u5370\u6765\u4e00\u638c\u3002 \u5c55\u767d\u53ea\u89c9\u4ed6\u638c\u52bf\u6c89\u7f13\uff0c\u5374\u9690\u7136\u6709\u4e00\u80a1\u9634\u5bd2\u4e4b\u6c14\uff0c\u8fce\u80f8\u538b\u6765\uff0c\u77e5\u662f\u9634\u6bd2\u638c\u529f\uff0c\u4f46\u6beb\u5b8b\u653e\u5728 \u5fc3\u4e0a\uff0c\u626c\u8896\u4e00\u62c2\uff0c\u9053\uff1a\u201c\u4f55\u5fc5\u591a\u672d\uff1f\u201d \u522b\u770b\u5c55\u767d\u8fd9\u8f7b\u8f7b\u4e00\u62c2\uff0c\u7ade\u628a\u51b3\u5fc3\u62a5\u4ec7\uff0c\u6697\u65bd\u6bd2\u638c\u7684\u5b5f\u5982\u840d\uff0c\u9707\u9000\u4e00\u4e08\u5f00\u5916\uff0c\u82e5\u4e0d\u662f\u8eab\u540e \u6709\u5899\u58c1\u6321\u7740\uff0c\u8bf4\u4e0d\u5b9a\u8fd8\u8981\u9000\u51fa\u597d\u8fdc\u3002 \u201c\u7830\u201d\u4e00\u58f0\uff0c\u5b5f\u5982\u840d\u810a\u7cb1\u641e\u5728\u5899\u58c1\u4e0a\uff0c\u4e94\u810f\u7ffb\u6eda\uff0c\u9762\u8272\u60e8\u767d\uff0c\u54ac\u7d27\u5634\u5507\uff0c\u72e0\u72e0\u77aa\u7740\u5c55\u767d \u4e00\u8a00\u4e0d\u53d1\u2026\u2026 \u60f3\u4e0d\u5230\u6570\u6708\u4e0d\u89c1\uff0c\u5c55\u767d\u529f\u529b\u6709\u5982\u6b64\u5927\u8fdb\uff0c\u5b5f\u5982\u840d\u82e6\u5fc3\u7ec3\u6765\u6b32\u627e\u5c55\u767d\u62a5\u4ec7\u7684\u6bd2\u638c\uff0c\u7adf\u662f\u4e0d \u582a\u4e00\u51fb\u3002 \u5b5f\u5982\u840d\u53c8\u60ca\u53c8\u6012\uff0c\u65e0\u5948\u8fd9\u4e00\u78b0\u9762\u4fbf\u53d7\u4f24\u4e0d\u8f7b\uff0c\u867d\u7136\u5185\u5fc3\u60b2\u6124\uff0c\u5374\u6beb\u65e0\u529e\u6cd5\uff0c\u773c\u770b\u7740\u5c55\u767d \u6570\u58f0\u51b7\u7b11\uff0c\u88ab\u63a5\u5f85\u5bbe\u5ba2\u7684\u91d1\u5e9c\u95e8\u4e0b\u5f15\u8fdb\u5927\u5802\u3002 \u5176\u5b9e\u5c55\u767d\u5fc3\u6000\u8840\u6d77\u6df1\u4ec7\uff0c\u5185\u5fc3\u60b2\u6124\u66f4\u4e0d\u77e5\u8981\u8d85\u8fc7\u5b5f\u5982\u840d\u591a\u5c11\u500d\u3002\u4eca\u591c\uff0c\u4e0a\u5143\u4f73\u8282\uff0c\u4ed6\u5355 \u4eba\u5339\u9a6c\u6765\u8d74\u56db\u516c\u5b50\u7684\u8d4f\u706f\u5927\u4f1a\uff0c\u662f\u62b1\u4e86\u7834\u91dc\u6c89\u821f\u7684\u51b3\u5fc3\uff0c\u6765\u4e3a\u4ed6\u51a4\u6b7b\u5341\u6570\u5e74\u7684\u7236\u4eb2\u62a5\u4ec7\u96ea\u6068 \u6765\u7684\u3002 \u56e0\u6b64\uff0c\u4ed6\u4e00\u5230\u91d1\u5e9c\u95e8\u524d\uff0c\u4fbf\u8fde\u65bd\u201c\u51cc\u7a7a\u865a\u6e21\u201d\u3001\u201c\u7f61\u6c14\u5e03\u4f53\u201d\u3001\u201c\u6d41\u4e91\u94c1\u8896\u201d\u7b49\u795e\u529f\uff0c \u4e0d\u662f\u4e3a\u70ab\u6280\u50b2\u4e16\uff0c\u800c\u662f\u4e3a\u4e86\u8981\u4e00\u5bd2\u654c\u80c6\u3002 \u5b5f\u5982\u840d\u4e0d\u8fc7\u662f\u9996\u5f53\u5176\u51b2\u7f62\u4e86\u3002 \u597d\u5728\u8fd9\u4e9b\u95e8\u4e0b\u98df\u5ba2\u5df2\u770b\u51fa\u5c11\u5e74\u5c55\u767d\u827a\u4e1a\u4e0d\u89c1\uff0c\u4e8b\u524d\u53c8\u53d7\u8fc7\u201c\u7965\u9e9f\u516c\u5b50\u201d\u7684\u4ea4\u4ee3\uff0c\u6240\u4ee5\u4e0d \u7ba1\u76c2\u5982\u840d\u53d7\u632b\uff0c\u4f9d\u7136\u606d\u800c\u656c\u4e4b\u5730\u628a\u5c55\u767d\u8ba9\u8fdb\u5927\u5385\u3002 \u8fd9\u6240\u5927\u5385\uff0c\u5c55\u767d\u66fe\u6765\u8fc7\u4e00\u6b21\uff0c\u4e0d\u8fc7\u90a3\u662f\u767d\u5929\uff0c\u800c\u4e14\u5fc3\u60c5\u4e5f\u6ca1\u6709\u8fd9\u4e00\u6b21\u6fc0\u52a8\u3002 \u8fd9\u6b21\u63a2\u77e5\u91d1\u5e9c\u8001\u4e3b\u4eba\u201c\u9752\u86a8\u4f38\u201d\u91d1\u4e5d\uff0c\u5c31\u662f\u4ed6\u6740\u7236\u7684\u4e3b\u51f6\uff0c\u6ee1\u8154\u70ed\u8840\u6cb8\u817e\uff0c\u62b1\u5fc5\u6b7b\u4e4b\u51b3 \u5fc3\u91cd\u4e34\u91d1\u5e9c\uff0c\u5df2\u65e0\u5fc3\u60c5\u6b23\u8d4f\u8fd9\u4ee5\u201c\u6c5f\u5357\u7b2c\u4e00\u5bb6\u201d\u81ea\u8c6a\u7684\u91d1\u5e9c\u8c6a\u534e\u6392\u573a\uff0c\u8fde\u63a5\u5728\u5ead\u9662\u4e2d\u7433\u7405\u6ee1 \u76ee\u7684\u5947\u5de7\u534e\u706f\uff0c\u4ed6\u8fde\u770b\u90fd\u4e0d\u770b\u4e00\u773c\uff0c\u53ea\u53cc\u773c\u5e73\u89c6\uff0c\u7b14\u76f4\u5730\u8de8\u8fdb\u5bb4\u5ba2\u7684\u5927\u5385\u3002 \u5927\u5385\u5185\u5f69\u706f\u7eed\u7eb7\uff0c\u745b\u6d1b\u5782\u73e0\uff0c\u5c24\u5176\u7ee3\u91d1\u76d8\u9f99\u7684\u58c1\u6821\u4e0a\uff0c\u9ad8\u70e7\u7740\u6570\u5341\u53ea\u7c97\u903e\u513f\u81c2\u7684\u9999\u6cb9\u5927 \u70db\uff0c\u628a\u4e00\u5ea7\u5bbd\u655e\u7684\u5927\u5385\u7167\u8000\u5f97\u5982\u540c\u767d\u663c\u3002 \u5f53\u771f\u662f\u9ad8\u670b\u6ee1\u5ea7\uff0c\u4e00\u5ea7\u5bbd\u655e\u5927\u5385\u4e4b\u4e2d\uff0c\u4e0d\u6015\u6709\u6570\u767e\u4eba\u4e4b\u591a\u3002 \u9a6c\u8e44\u5f62\u7684\u957f\u684c\uff0c\u9009\u9898\u6392\u5f00\uff0c\u684c\u9762\u4e0a\u6b63\u5bb4\u672a\u4e0a\uff0c\u5374\u5806\u96c6\u7740\u6ee1\u684c\u7684\u65f6\u9c9c\u6c34\u679c\u3001\u7f8e\u70b9\u7cbe\u7cd5\uff0c\u8fce \u9762\u5750\u7740\u540d\u91cd\u6b66\u6797\u7684\u56db\u5927\u516c\u5b50\uff0c\u4e24\u65c1\u5750\u7684\u4ff1\u90fd\u662f\u56db\u5927\u8c6a\u95e8\u7684\u9876\u5c16\u9ad8\u624b\uff0c\u4ee5\u53ca\u4e09\u5c71\u4e94\u5cb3\u3001\u56db\u6d77\u516b \u8352\u7684\u5947\u4eba\u5f02\u58eb\u3002 \u610f\u5916\u5730\uff0c\u8fd9\u5185\u8574\u65e0\u9650\u6740\u673a\u7684\u6b66\u6797\u56db\u516c\u5b50\u4e4b\u4f1a\uff0c\u8868\u9762\u4e0a\u7ade\u5145\u6512\u7740\u559c\u6d0b\u6d0b\u7684\u6c14\u6c1b\uff0c\u4f17\u4eba\u968f\u610f \u5403\u559d\u7740\u684c\u4e0a\u7684\u9c9c\u679c\u7f8e\u70b9\uff0c\u5634\u4e2d\u5374\u662f\u7b11\u8bed\u55a7\u54d7\uff0c\u4eba\u591a\u8bed\u6742\uff0c\u51e0\u81f3\u805a\u58f0\u6210\u96f7\uff0c\u6574\u5ea7\u5927\u5385\u4e2d\u663e\u5f97\u4e71 \u70d8\u70d8\u7684\u4e00\u56e2\u3002 \u53c8\u52a0\u4e0a\u65b0\u6765\u7684\u5bbe\u5ba2\uff0c\u4ee5\u53ca\u7aef\u8336\u9001\u6c34\u7684\u7537\u5973\u4f63\u4eba\uff0c\u8fdb\u51fa\u5ddd\u6d41\u4e0d\u606f\uff0c\u4ee5\u81f4\u9886\u5bfc\u5c55\u767d\u5165\u5185\u7684\u98df \u5ba2\uff0c\u9ad8\u58f0\u4e3a\u5c55\u767d\u62a5\u540d\u5f15\u8fdb\uff0c\u90fd\u6ca1\u6709\u4eba\u542c\u89c1\u3002 \u5c55\u767d\u56e0\u662f\u5b58\u5fc3\u627e\u4e8b\u6765\u7684\uff0c\u5c82\u7518\u53d7\u6b64\u51b7\u843d\uff1f\u62bd\u51fa\u80cc\u4e0a\u201c\u65e0\u60c5\u78a7\u5251\u201d\uff0c\u5c48\u6307\u4e00\u5f39\u201c\u6c6a\u201d\u7136\u4e00 \u58f0\u9f99\u541f\uff0c\u6c14\u8d2f\u4e39\u7530\uff0c\u5634\u4e2d\u9ad8\u541f\u9053\uff1a\u201c\u5f39\u5251\u4f5c\u6b4c\u594f\u82e6\u58f0\uff0c\u66f3\u88d9\u738b\u95e8\u4e0d\u79f0\u60c5\uff01\u6b66\u5b66\u672b\u8fdb\uff0c\u5c55\u767d\u9020 \u8bbf\uff01\u201d \u5c55\u81ea\u8fd9\u4e00\u5f39\u5251\u4f5c\u6b4c\uff0c\u58f0\u9707\u5168\u5385\uff0c\u6570\u767e\u4f4d\u9ad8\u624b\u7b11\u8bed\u58f0\u7acb\u6b62\uff0c\u4e00\u5ea7\u55a7\u54d7\u53eb\u56a3\u7684\u5927\u5385\uff0c\u5239\u65f6\u53d8 \u5f97\u9e26\u96c0\u65e0\u58f0\uff0c\u6570\u767e\u9053\u773c\u5149\uff0c\u4e00\u9f50\u9f13\u5c04\u5728\u5c55\u767d\u8eab\u4e0a\u3002 \u9996\u5148\u7965\u9e9f\u516c\u4e88\u79bb\u5ea7\u8d77\u8eab\uff0c\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u597d\u4e00\u4e2a\u2018\u5f39\u5251\u4f5c\u6b4c\u594f\u82e6\u58f0\uff0c\u66f3\u88d9\u738b\u95e8\u4e0d\u79f0 \u60c5\u2019\uff0c\u4f46\u5728\u7965\u9e9f\u5bb6\u4e2d\u5374\u4e0d\u4f1a\u6709\u2018\u6dee\u9634\u5e02\u4e95\u7b11\u97e9\u4fe1\uff0c\u6c49\u671d\u516c\u804a\u5fcc\u8d3e\u751f\u2019\u7684\u91cf\u5c0f\u4e4b\u4eba\uff0c\u6765\uff01\u7965\u9e9f \u5148\u4e3a\u5c55\u5144\u5f15\u89c1\u51e0\u4f4d\u524d\u8f88\u3002\u201d \u7965\u9e9f\u516c\u5b50\u8bf4\u7740\u8d70\u4e0b\u5ea7\u6765\uff0c\u62c9\u7740\u5c55\u767d\u7684\u624b\uff0c\u5148\u5411\u9996\u5ea7\u4e00\u4e2a\u53cc\u76ee\u7cbe\u5149\u5982\u70ac\u7684\u8001\u4eba\u9053\uff1a\u201c\u8fd9\u4fbf \u662f\u5bb6\u7236\u2026\u2026\u201d \u53ea\u8fd9\u4e94\u5b57\u51fa\u53e3\uff0c\u5c55\u767d\u7acb\u611f\u70ed\u8840\u4e0a\u51b2\uff0c\u5934\u8111\u201c\u8f70\u201d\u7684\u4e00\u58f0\uff0c\u4ee5\u4e0b\u7965\u9e9f\u516c\u5b50\u8bf4\u7684\u8bdd\u4fbf\u542c\u4e0d\u5230 \u4e86\u3002 \u56e0\u4e3a\u5c55\u767d\u542c\u201c\u795e\u9a74\u94c1\u80c6\u201d\u544a\u8bc9\u4ed6\uff0c\u201c\u9752\u86a8\u795e\u201d\u91d1\u4e5d\u4e43\u662f\u6740\u7236\u4ec7\u4eba\u7684\u4e3b\u51f6\uff0c\u9996\u5148\u51fa\u4e3b\u610f\u6740 \u5bb3\u7236\u4eb2\u7684\u662f\u4ed6\uff0c\u5728\u201c\u6c5f\u5357\u516d\u4fa0\u201d\u56f4\u6740\u7236\u4eb2\u65f6\uff0c\u9996\u5148\u7528\u6697\u5668\u4f7f\u7236\u4eb2\u53d7\u4f24\u7684\u4e5f\u662f\u4ed6\u3002 \u56e0\u6b64\uff0c\u5c55\u767d\u542c\u7965\u9e9f\u516c\u5b50\u8bf4\u51fa\uff0c\u77e5\u9053\u5f53\u4e2d\u9996\u5ea7\u8001\u4eba\u4fbf\u662f\u201c\u9752\u86a8\u795e\u201d\u91d1\u4e5d\uff0c\u4e0d\u7531\u53cc\u76ee\u6012\u7741\uff0c \u540c\u65f6\u5fc3\u4e2d\u7981\u4e0d\u4f4f\u70ed\u8840\u6cb8\u817e\u3002 \u53ea\u89c1\u8fd9\u8001\u4eba\u4e94\u65ec\u5f00\u5916\uff0c\u53cc\u76ee\u7cbe\u5149\u5982\u70ac\uff0c\u5145\u5206\u663e\u51fa\u673a\u667a\u65e0\u6bd4\u7684\u795e\u6001\uff0c\u540c\u65f6\u4ece\u5176\u5982\u70ac\u7684\u76ee\u5149 \u770b\u6765\uff0c\u77e5\u5176\u5185\u529f\u5fc5\u4ea6\u4e0d\u5f31\uff0c\u8eab\u7a7f\u56e2\u5e95\u95ea\u5149\u7ef8\u888d\uff0c\u9762\u5982\u6ee1\u6708\uff0c\u989d\u4e0b\u4e94\u7ecd\u82b1\u767d\u80e1\u5b50\uff0c\u673a\u667a\u7cbe\u660e\u4e4b \u4e2d\uff0c\u53c8\u663e\u51fa\u4e00\u6d3e\u5a01\u4e25\u4e4b\u8c61\u3002 \u5c55\u767d\u4e3a\u4e86\u4e0d\u5728\u4eba\u524d\u5931\u6001\uff0c\u54ac\u7259\u5f3a\u5fcd\u4f4f\u6ee1\u8179\u60b2\u6124\uff0c\u62b1\u62f3\u8bf4\u9053\uff1a\u201c\u4e45\u4ef0\uff01\u4e45\u4ef0\uff01\u2018\u9752\u86a8\u795e\u2019 \u91d1\u5927\u4fa0\uff0c\u5a01\u540d\u8fdc\u64ad\uff0c\u4eca\u5f97\u8bc6\u8346\uff0c\u4e09\u751f\u6709\u5e78\uff01\u201d \u5c55\u767d\u8bf4\u5b8c\uff0c\u90a3\u201c\u9752\u86a8\u795e\u201d\u91d1\u4e5d\u5374\u50b2\u4e0d\u4e3a\u793c\uff0c\u8fde\u7ad9\u90fd\u672a\u7ad9\u8d77\u6765\uff1b\u53ea\u5fae\u5fae\u70b9\u4e86\u70b9\u5934\uff0c\u5634\u4e2d\u8fde \u9053\u4e86\u4e24\u4e2a\u201c\u597d\u201d\u5b57\uff0c\u4e00\u53ea\u7cbe\u5149\u5982\u70ac\u7684\u8116\u5b50\uff0c\u5374\u500f\u7136\u4e0a\u4e0b\u6253\u91cf\u4e86\u5c55\u767d\u51e0\u773c\u3002 \u5c55\u767d\u4ee5\u4e3a\u201c\u9752\u86a8\u795e\u201d\u8f7b\u89c6\u81ea\u5df1\uff0c\u5fcd\u4e0d\u4f4f\u6012\u706b\u4e0a\u51b2\uff0c\u8138\u8272\u7acb\u53d8\u3002\u3002\u2014 \u201c\u7965\u9e9f\u516c\u5b50\u201d\u5df2\u770b\u51fa\u5c55\u767d\u795e\u60c5\u4e0d\u5bf9\uff0c\u5fd9\u9053\uff1a\u201c\u5bb6\u7236\u53cc\u817f\u4e0d\u4fbf\uff0c\u8bf7\u5c55\u5144\u591a\u591a\u5305\u6db5\uff01\u201d \u5c55\u767d\u8fd9\u624d\u770b\u51fa\uff0c\u201c\u9752\u86a8\u795e\u201d\u91d1\u4e5d\u6240\u5750\u7684\u4e0d\u662f\u6905\u5b50\uff0c\u800c\u662f\u4e00\u8f86\u8f6e\u8f66\uff0c\u53cc\u817f\u8986\u76cf\u4e86\u4e00\u6761\u539a\u6bdb \u6be1\uff0c\u4f46\u4e0d\u77e5\u56e0\u4f55\u53cc\u817f\u5931\u7075\u3002 \u7965\u9e9f\u516c\u5b50\u63a5\u7740\u4e3a\u5c55\u767d\u4e00\u4e00\u5f15\u89c1\u4e0e\u4f1a\u4f17\u4eba\uff0c\u6b66\u6797\u56db\u516c\u5b50\u5c55\u767d\u5747\u5df2\u4f1a\u8fc7\uff0c\u6b66\u6797\u56db\u516c\u5b50\u95e8\u4e0b\u7684 \u597d\u624b\uff0c\u4ee5\u53ca\u4e0e\u4f1a\u7684\u5929\u4e0b\u7fa4\u96c4\uff0c\u5c55\u767d\u5e76\u4e0d\u7559\u610f\uff0c\u4ec5\u6ce8\u610f\u201c\u4e7e\u5764\u638c\u201d\u4e91\u5b97\u9f99\u3001\u201c\u6df7\u5143\u6307\u201d\u53f8\u7a7a\u664b \u4ee5\u53ca\u9738\u738b\u97ad\u6a0a\u975e\u7b49\u3002 \u5947\u602a\u7684\u662f\u201c\u6458\u661f\u624b\u201d\u6155\u5bb9\u6db5\u5374\u672a\u4e0e\u4f1a\u201c\u8c79\u7a81\u5c71\u5e84\u201d\u65b9\u9762\u7684\u4eba\u53ea\u6709\u51b7\u50b2\u7684\u201c\u51cc\u98ce\u516c\u5b50\u201d\u53ca \u95e8\u4e0b\u9ad8\u624b\u591a\u4eba\u5728\u5ea7\uff0c\u8fde\u201c\u96f7\u5927\u53d4\u201d\u3001\u201c\u94f6\u7bab\u593a\u9b42\u201d\u7ae0\u58eb\u670b\u3001\u201c\u8ffd\u9b42\u94c3\u201d\u53f8\u9a6c\u656c\u3001\u201c\u72ec\u811a\u98de \u9b54\u201d\u674e\u4e3e\u3001\u201c\u8861\u5c71\u591c\u679c\u201d\u7707\u76ee\u9053\u4eba\u7b49\u5341\u5927\u9ad8\u624b\uff0c\u4e5f\u672a\u89c1\u9762 \u201c\u51cc\u98ce\u516c\u5b50\u201d\u4ecd\u7136\u662f\u90a3\u526f\u51b7\u6f20\u65e0\u60c5\u7684\u795e\u6001\uff0c\u5728\u7965\u9e9f\u516c\u5b50\u4e3a\u5c55\u767d\u5f15\u89c1\u65f6\uff0c\u773c\u89c6\u5c4b\u9876\uff0c\u6602\u7136 \u4e0d\u776c\uff0c\u4fca\u7f8e\u7684\u8138\u4e0a\u5634\u89d2\u4e0b\u6487\uff0c\u66f4\u662f\u4e00\u526f\u9ad8\u50b2\u65e0\u6bd4\u7684\u795e\u6c14\u3002 \u8fde\u505a\u4e3b\u4eba\u7684\u7965\u9e9f\u516c\u5b50\u90fd\u5927\u4e0d\u8fc7\u610f\uff0c\u4f46\u5c55\u767d\u53cd\u800c\u5fc3\u5e73\u6c14\u548c\u5730\u7b11\u9053\uff1a\u201c\u4e0d\u52b3\u516c\u5b50\u5f15\u89c1\uff0c\u6211\u4eec \u65e9\u5c31\u8ba4\u8bc6\u4e86\uff01\u201d \u201c\u51cc\u98ce\u516c\u5b50\u201d\u5374\u4e00\u77aa\u773c\uff0c\u4ee5\u5bd2\u51b7\u5982\u6c34\u7684\u8bdd\u8c03\u8bf4\u9053\uff1a\u4e00\u65e0\u540d\u5c0f\u8f88\u8033\uff0c\u672c\u516c\u5b50\u4e0d\u5c51\u8ba4\u8bc6\u4e8e \u4f60\uff01\u201d \u7965\u9e9f\u516c\u5b50\u5df2\u77e5\u5c55\u767d\u5fc3\u9ad8\u6c14\u50b2\uff0c\u6b66\u529f\u53c8\u9ad8\u5f3a\u65e0\u6bd4\uff0c\u51cc\u98ce\u516c\u5b50\u7684\u8ba5\u6d88\uff0c\u4e00\u5b9a\u5f15\u8d77\u5c55\u767d\u53d1\u6012\uff0c \u4ed6\u5e76\u4e0d\u662f\u6015\u4ed6\u4eec\u6253\u8d77\u6765\uff0c\u800c\u662f\u6015\u574f\u4e86\u81ea\u5df2\u9884\u5b9a\u7684\u8ba1\u5212\uff0c\u5fd9\u5728\u4e00\u8fb9\u8bf4\u9053\uff1a\u201c\u6155\u5bb9\u5144\u751f\u6027\u5982\u6b64\uff0c \u5c55\u5144\u4e14\u83ab\u4ecb\u610f\uff01\u201d \u8c01\u77e5\u5c55\u767d\u4e00\u70b9\u4e0d\u53d1\u706b\uff0c\u53cd\u800c\u7b11\u9053\uff1a\u201c\u5c55\u767d\u65e9\u5df2\u9886\u6559\uff01\u201d\u201c\u51cc\u98ce\u516c\u5b50\u201d\u53c8\u662f\u4e00\u58f0\u51b7\u7b11\u3002 \u5c55\u767d\u8d70\u5411\u522b\u5904\u65f6\uff0c\u56de\u987e\u4e86\u4ed6\u4e00\u773c\u9053\uff1a\u201c\u4e0d\u7528\u5fc3\u6025\uff0c\u5c55\u767d\u65e9\u665a\u6709\u8ba9\u6155\u5bb9\u516c\u5b50\u8ba4\u8bc6\u7684\u4e00 \u5929\uff01\u201d \u6155\u5bb9\u627f\u4e1a\u500f\u7136\u8272\u53d8\uff0c\u7acb\u8d77\u8eab\u6765\uff0c\u4e94\u6307\u4e00\u65cb\uff0c\u731b\u5411\u5c55\u767d\u8eab\u540e\u201c\u51e4\u773c\u201d\u4e09\u7a74\u5f39\u53bb\u3002 \u6b63\u662f\u5bb6\u4f20\u7edd\u827a\u201c\u6458\u661f\u624b\u201d\u7684\u4e00\u5f0f\u201c\u7d2b\u5fae\u4e09\u57a3\u201d\uff0c\u4e94\u4e1d\u5fae\u98ce\uff0c\u75be\u800c\u4e0d\u5578\uff0c\u88ad\u5411\u5c55\u767d\u3002 \u4f3c\u8fd9\u7b49\u9634\u67d4\u624b\u6cd5\uff0c\u4f7f\u654c\u624b\u4e0d\u6613\u8eb2\u907f\uff0c\u5c24\u5176\u5728\u80cc\u540e\u6697\u88ad\uff0c\u56e0\u4e3a\u5b83\u4e0d\u5e26\u4e00\u4e1d\u7834\u7a7a\u4e4b\u58f0\uff0c\u66f4\u4f7f \u4eba\u65e0\u6cd5\u4e8b\u5148\u9632\u8303\u3002 \u4f46\u5c55\u767d\u4eca\u975e\u6614\u6bd4\uff0c\u5df2\u5927\u6709\u5929\u6e0a\u4e4b\u522b\uff0c\u81ea\u7ecf\u201c\u795e\u9a74\u94c1\u80c6\u201d\u7684\u4e09\u6708\u6559\u8bf2\uff0c\u4ed6\u5bf9\u81ea\u5df1\u5185\u8eab\u6f5c\u4fee \u7684\u529f\u529b\uff0c\u5df2\u80fd\u81ea\u7531\u53d1\u6325\uff0c\u53c8\u52a0\u4e0a\u4e0e\u4f1a\u524d\u8fd9\u4e24\u4e2a\u591a\u6708\u7684\u65f6\u95f4\uff0c\u4ed6\u8db3\u4e0d\u51fa\u6237\u5730\u5728\u5ca9\u5c71\u5341\u4e8c\u6d1e\u4e2d\uff0c \u6f5c\u4fee\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\u4e0a\u6240\u8f7d\u7684\u5947\u7edd\u6b66\u5b66\uff0c\u6240\u8c13\u201c\u4e00\u7a8d\u901a\uff0c\u7a8d\u7a8d\u901a\u201d\uff0c\u8fdb\u6b65\u4f55\u6b62\u4e00\u65e5\u5343 \u91cc\u3002\u5c55\u81ea\u6b66\u529f\u5df2\u8fbe\u5fc3\u4e0e\u795e\u4f1a\u5883\u754c\uff0c\u201c\u51cc\u98ce\u516c\u5b50\u201d\u6697\u88ad\u624b\u6cd5\u867d\u7136\u51a0\u7edd\u2014\u2014\u65f6\uff0c\u4f46\u5c55\u767d\u81ea\u5df1\u53ca\u65f6 \u53d1\u89c9\uff0c\u5374\u4e0d\u9732\u75d5\u8ff9\u5730\u56de\u624b\u4e00\u62c2\uff0c\u628a\u201c\u51cc\u98ce\u516c\u5b50\u201d\u6781\u5389\u5bb3\u7684\u4e00\u62db\u201c\u7d2b\u5fae\u4e09\u57a3\u201d\u5316\u89e3\u4e8e\u65e0\u5f62\uff0c\u53e3 \u4e2d\u5e76\u8bf4\u9053\uff1a\u7b49\u4e00\u4f1a\u9886\u6559\uff01\u201d \u51cc\u98ce\u516c\u5b50\u7acb\u5373\u611f\u5230\u4e00\u822c\u6697\u52b2\uff0c\u62e5\u5411\u81ea\u5df1\uff0c\u4e0d\u4f46\u628a\u4ed6\u4e00\u62db\u201c\u6458\u661f\u624b\u201d\u7684\u7edd\u62db\u5316\u89e3\u6389\uff0c\u800c\u4e14 \u9707\u5f97\u81ea\u5df1\u7ad9\u4e0d\u4f4f\u811a\uff0c\u6251\u201d\u7684\u4e00\u58f0\uff0c\u53c8\u8dcc\u5750\u5728\u6905\u5b50\u4e0a\uff0c\u4e0d\u7981\u95f9\u4e86\u4e2a\u76ee\u77aa\u53e3\u5446\u3002 \u597d\u5728\u5c55\u767d\u7528\u7684\u8fd9\u4e00\u62db\u975e\u5e38\u542b\u84c4\uff0c\u4e0d\u662f\u7279\u522b\u7ec6\u5fc3\u7684\u4eba\u770b\u4e0d\u51fa\u6765\uff0c\u56e0\u6b64\u4e5f\u5e76\u672a\u5f15\u8d77\u591a\u5c11\u4eba\u6ce8 \u610f\u3002 \u5012\u662f\u201c\u5b89\u4e50\u516c\u5b50\u201d\u65e9\u60f3\u62c9\u62e2\u5c55\u767d\uff0c\u867d\u770b\u51fa\u5c55\u767d\u6298\u8fb1\u4e86\u51cc\u98ce\u516c\u5b50\u2014\u62db\uff0c\u4e5f\u4e0d\u70b9\u7834\uff0c\u5374\u8d70\u4e0b \u5ea7\u6765\uff0c\u62c9\u4f4f\u5c55\u767d\u5750\u5728\u81ea\u5df1\u5ea7\u65c1\u3002 \u5c55\u767d\u4e5f\u672a\u52a0\u63a8\u8f9e\uff0c\u5373\u5750\u5728\u5b89\u4e50\u516c\u5b50\u8eab\u65c1\uff0c\u62ac\u5934\u89c1\u8bc6\u4e0e\u4e0d\u8bc6\u7684\u5168\u5385\u4e4b\u4eba\u90fd\u5728\u77aa\u7740\u773c\u6253\u91cf \u4ed6\uff0c\u5c55\u767d\u5fc3\u4e2d\u4e0d\u77e5\u662f\u559c\u662f\u60b2\uff0c\u89c9\u5f97\u81ea\u5df1\u5b64\u8eab\u4e00\u5251\uff0c\u5c06\u8981\u5f53\u7740\u5929\u4e0b\u7fa4\u96c4\u9762\u524d\uff0c\u62a5\u96ea\u6740\u7236\u4e4b\u4ec7\uff0c \u56fa\u7136\u662f\u4eba\u751f\u4e00\u592a\u5feb\u4e8b\uff0c\u4f46\u53c8\u60f3\u8d77\u81ea\u5df2\u4eba\u5355\u52bf\u5b64\uff0c\u4e5f\u8bb8\u4e0d\u80fd\u62a5\u96ea\u6740\u7236\u4e4b\u4ec7\uff0c\u53cd\u800c\u6eaf\u8840\u5802\u524d\uff0c\u4e27 \u547d\u5728\u6b64\u2026\u3002\uff0e \u8f6c\u53c8\u4e00\u60f3\uff0c\u4e0d\u7ba1\u81ea\u5df1\u62a5\u5f97\u4e86\u62a5\u4e0d\u4e86\u7236\u4ec7\uff0c\u53ea\u8981\u80fd\u591f\u5f53\u7740\u5929\u4e0b\u7fa4\u96c4\u4e4b\u9762\uff0c\u628a\u7236\u4eb2\u88ab\u6740\u5bb3\u7ecf \u8fc7\uff0c\u516c\u5e03\u6b66\u6797\uff0c\u7eb5\u662f\u727a\u7272\u4e00\u547d\uff0c\u4e5f\u9887\u503c\u5f97\u4e86\uff0c\u60f3\u81f3\u6b64\u5904\uff0c\u5fc3\u60c5\u53cd\u800c\u51fa\u5947\u5730\u9547\u9759\u4e0b\u6765\u3002 \u7a81\u7136\uff0c\u5c55\u767d\u8f6c\u773c\u53c8\u770b\u5230\u4e00\u53cc\u5982\u5929\u9645\u6668\u661f\u7684\u5927\u773c\u775b\uff0c\u6b63\u51fa\u795e\u5730\u671b\u7740\u81ea\u5df1\uff0c\u4ed4\u7ec6\u4e00\u770b\u975e\u662f\u522b \u4eba\uff0c\u6b63\u662f\u90a3\u6709\u7740\u201c\u6c5f\u5357\u7b2c\u4e00\u7f8e\u4eba\u201d\u4e4b\u79f0\u7684\u91d1\u5f69\u51e4\uff01 \u5c55\u767d\u7a81\u7136\u4e00\u9707\uff0c\u53ea\u611f\u5fc3\u4e2d\u535c\u535c\u4e71\u8df3\uff0c\u8138\u5b54\u53d1\u70e7\uff0c\u987f\u65f6\u5174\u8d77\u65e0\u6570\u7684\u7eee\u601d\u9050\u60f3\uff0c\u5fc3\u4e2d\u9053\uff1a \u201c\u96be\u9053\u5979\u7231\u4e0a\u6211\u4e86\u5417\uff1f\u4e3a\u4ec0\u4e48\u6bcf\u6b21\u4f1a\u9762\uff0c\u603b\u662f\u7528\u8fd9\u79cd\u773c\u5149\u770b\u6211\uff1f\u2026\u2026\u201d \u8f6c\u53c8\u60f3\u9053\uff1a\u201c\u8fd9\u662f\u4e0d\u53ef\u80fd\u7684\uff01\u4eba\u5bb6\u662f\u6c5f\u5357\u9996\u5bcc\u7684\u5343\u91d1\u5c0f\u59d0\uff0c\u81ea\u5df1\u5374\u662f\u4e00\u4e2a\u843d\u9b44\u6c5f\u6e56\u7684\u7a77 \u5c11\u5e74\uff0c\u5c31\u7b97\u4eba\u5bb6\u7231\u4e0a\u81ea\u5df1\uff0c\u81ea\u5df2\u53c8\u62ff\u4ec0\u4e48\u53bb\u7231\u5979\uff1f\u2026\u2026\u201d \u5ffd\u7136\u53c8\u60f3\u8d77\uff1a\u201c\u5979\u7236\u4eb2\u662f\u81ea\u5df2\u7684\u6740\u7236\u4ec7\u4eba\uff0c\u9a6c\u4e0a\u4fbf\u8981\u6d41\u8840\u62fc\u547d\uff0c\u53cc\u65b9\u52bf\u540c\u5bc7\u4ec7\uff0c\u8fd8\u8c08\u4ec0 \u4e48\u7231\u4e0d\u7231\uff1f\u2026\u3002\u201d \u8fd9\u6837\u4e00\u60f3\uff0c\u5c55\u767d\u987f\u89c9\u51b7\u6c34\u6d47\u9876\uff0c\u6ee1\u8179\u7eee\u601d\u7acb\u523b\u5316\u4e3a\u4e4c\u6709\u3002 \u6b64\u65f6\uff0c\u90a3\u201c\u9752\u86a8\u795e\u201d\u91d1\u4e5d\uff0c\u5374\u628a\u7965\u9e9f\u516c\u5b50\u53eb\u5230\u8fd1\u524d\uff0c\u4fef\u8033\u4f4e\u8a00\u4e86\u51e0\u53e5\uff0c\u7965\u9e9f\u516c\u5b50\u7acb\u523b\u6602 \u8eab\u62b1\u62f3\uff0c\u56db\u4e0b\u4e00\u62f1\u9053\u571f\u201c\u8bf8\u4f4d\u82f1\u96c4\uff0c\u5916\u9762\u9152\u5bb4\u5df2\u7ecf\u6446\u597d\uff0c\u5c31\u8bf7\u8bf8\u4f4d\u79fb\u5750\u6708\u4e0b\uff0c\u8d4f\u706f\u996e\u9152\u5171\u8c0b \u4e00\u9189\u5982\u4f55\uff1f\u201d \u7aef\u65b9\u516c\u5b50\u9996\u5148\u4e0d\u8010\uff0c\u5f53\u4e0b\u8eab\u5f62\u4e00\u7acb\uff0c\u9053\uff1a\u201c\u4e0d\u77e5\u90a3\u2018\u5927\u7f57\u91d1\u4e39\u2019\u4f55\u65f6\u5c55\u51fa\uff1f\u4e5f\u53eb\u4e0e\u4f1a\u7fa4 \u96c4\u4e00\u5f00\u773c\u754c\uff01\u201d \u7965\u9e9f\u516c\u5b50\u6de1\u7136\u4e00\u7b11\u9053\uff1a\u201c\u53f8\u7a7a\u5144\u4e0d\u5fc5\u5fc3\u6025\uff0c\u4f60\u65e2\u7136\u628a\u2018\u907f\u6c34\u7389\u58c1\u2019\u5e26\u6765\u4e86\uff0c\u7965\u9e9f\u5bb6\u4e2d\u7684 \u2018\u5927\u7f57\u91d1\u4e39\u2019\u8fd8\u80fd\u81ea\u79d8\u5417\uff1f\u6211\u4eec\u5927\u5bb6\u5230\u5916\u9762\u996e\u9152\u8d4f\u706f\u571f\u65cf\u6d12\u5174\u6b63\u6d53\u65f6\uff0c\u4f60\u3001\u6211\u4ee5\u53ca\u4e91\u94ee\u5144\uff0c \u54b1\u4eec\u4e09\u5b97\u5f02\u5b9d\u3001\u540c\u5c55\u51fa\uff0c\u5c82\u4e0d\u591f\u5473\uff01\u201d \u5b89\u4e50\u516c\u5b50\u9996\u5148\u628a\u624b\u4e2d\u90a3\u672c\u70c2\u4e66\u4e00\u62cd\uff0c\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u6708\u4e0b\u706f\u524d\uff0c\u6709\u82b1\u6709\u9152\uff0c\u53c8\u6709\u6b66\u6797\u79d8 \u5b9d\u53ef\u5f00\u773c\u754c\uff0c\u4eca\u5bb5\u6b64\u4f1a\u4e0d\u865a\u4e86\uff0c\u597d\uff01\u597d\uff01\u53f8\u7a7a\u8001\u5f1f\uff0c\u54b1\u4eec\u5ba2\u968f\u4e3b\u4fbf\uff0c\u4e00\u5207\u542c\u7965\u9e9f\u5144\u7684\u4e86\uff01\u201d \u4e0e\u4f1a\u4f17\u4eba\uff0c\u8d4f\u706f\u996e\u9152\u662f\u5047\uff0c\u5374\u6025\u6b32\u4e00\u7779\u4e09\u5b97\u5f02\u5b9d\uff0c\u542c\u5b89\u4e50\u516c\u4e8e\u4e00\u8bf4\uff0c\u9f50\u9f50\u65af\u5e94\uff0c\u54c4\u7136\u9053 \u597d\uff0c\u4e00\u9f50\u79bb\u5ea7\u5411\u5927\u5385\u5916\u62e5\u53bb\u3002 \u5c55\u767d\u51b7\u773c\u65c1\u89c2\uff0c\u89c1\u201c\u9752\u86a8\u795e\u201d\u91d1\u4e5d\u3001\u201c\u4e7e\u5764\u638c\u201d\u4e91\u5b97\u9f99\u3001\u201c\u6df7\u5143\u6307\u201d\u53f8\u7a7a\u664b\uff0c\u4ee5\u53ca\u201c\u9738 \u738b\u97ad\u201d\u6a0a\u975e\u7b49\u8005\u4e00\u8f88\u7684\u4eba\uff0c\u7edd\u5c11\u53d1\u8a00\uff0c\u53ea\u9759\u5750\u5728\u4e00\u8fb9\u7531\u7740\u5c0f\u4e00\u8f88\u7684\u201c\u6b66\u6797\u56db\u516c\u5b50\u201d\u51fa\u9762\u4e89 \u5f3a\uff0c\u4e0d\u77e5\u4ed6\u4eec\u5b89\u7684\u662f\u4ec0\u4e48\u5fc3\uff1f\u6697\u5730\u91cc\u53c8\u6709\u4ec0\u4e48\u6253\u7b97\uff1f\u56e0\u6b64\uff0c\u5168\u795e\u8d2f\u6ce8\u5728\u4ed6\u51e0\u4eba\u7684\u8eab\u4e0a\uff0c\u4e00\u65f6 \u51fa\u4e86\u795e\u2026\u2026 \u5ffd\u89c1\u51cc\u98ce\u516c\u5b50\u51b7\u51b7\u8a00\u9053\uff1a\u201c\u7965\u9e9f\u70ed\u80a0\uff0c\u5b89\u4e50\u98ce\u6d41\uff0c\u98d8\u96f6\u7aef\u65b9\uff0c\u4f60\u4eec\u4e09\u516c\u5b50\u5747\u6709\u5b9e\u7269\u53ef \u732e\uff0c\u96be\u9053\u6211\u2018\u51cc\u98ce\u2019\u5c31\u6ca1\u6709\u73cd\u8d35\u4e4b\u7269\uff0c\u4ee5\u535a\u5929\u4e0b\u82f1\u96c4\u4e00\u987e\u4e86\u5417\uff1f\u201d \u51cc\u98ce\u516c\u5b50\u8fd9\u4e00\u53d1\u8a00\uff0c\u95f9\u54c4\u54c4\u5411\u5385\u5916\u8d70\u7684\u4eba\uff0c\u53c8\u5373\u65f6\u505c\u4f4f\u811a\u6b65\uff0c\u4e00\u9f50\u77aa\u7740\u773c\u775b\u671b\u7740\u51cc\u98ce\u516c \u5b50\uff0c\u770b\u4ed6\u6709\u4ec0\u4e48\u6bd4\u4e09\u5b97\u79d8\u5b9d\u66f4\u503c\u5f97\u73cd\u8d35\u7684\u4e1c\u897f\u62ff\u51fa\u6765\u3002 \u51cc\u98ce\u516c\u5b50\u89c1\u4ed6\u7684\u8bdd\u679c\u7136\u751f\u4e86\u6548\u679c\uff0c\u4e0d\u7531\u9762\u9732\u5f97\u8272\uff0c\u56de\u9996\u5411\u4e00\u957f\u53d1\u62ab\u80a9\u7684\u8001\u8005\u8bf4\u9053\uff1a\u201c\u8d6b \u8fde\u4e09\u53d4\uff01\u4f60\u628a\u54b1\u4eec\u7684\u5b9d\u7269\u732e\u51fa\u6765\u770b\u770b\uff01\u201d \u90a3\u88ab\u79f0\u505a\u201c\u8d6b\u8fde\u4e09\u53d4\u201d\u7684\u8001\u4eba\uff0c\u63a2\u624b\u6000\u5185\u53d6\u51fa\u4e00\u4e2a\u9526\u7f0e\u7ef8\u5305\u6765\uff0c\u6253\u5f00\u9526\u7f0e\u7ef8\u5305\uff0c\u91cc\u9762\u662f \u4e00\u4e2a\u6241\u65b9\u7684\u7ea2\u6728\u5323\u5b50\uff0c\u72ec\u5f00\u5323\u76d6\uff0c\u91cc\u8fb9\u53c8\u7528\u767d\u9526\u7eb8\u5305\u7740\uff0c\u91cc\u4e09\u5c42\u5916\u4e09\u5c42\uff0c\u957f\u53d1\u62ab\u80a9\u8001\u8005\u6162\u6761 \u65af\u7406\u5730\u4e00\u5c42\u4e00\u5c42\u5730\u6253\u5f00\uff0c\u4f46\u770b\u8fd9\u6536\u85cf\u7684\u4e25\u5bc6\uff0c\u4fbf\u77e5\u5b9a\u662f\u975e\u5e38\u73cd\u8d35\u7684\u5b9d\u7269\uff0c\u800c\u4e14\uff0c\u81ea\u4ece\u6253\u5f00\u5323 \u76d6\u4ee5\u540e\uff0c\u5373\u6709\u4e00\u79cd\u99a8\u99a5\u5e7d\u9999\u88ad\u8fdb\u4e86\u4f17\u4eba\u9f3b\u5b54\uff0c\u968f\u7740\u7eb8\u5305\u4e00\u5c42\u4e00\u5c42\u5730\u6253\u5f00\uff0c\uff0c\u5230\u540e\u6765\u7adf\u4f7f\u6574\u5ea7 \u5bbd\u655e\u5927\u5385\u90fd\u5f25\u6f2b\u4e86\u8fd9\u79cd\u9999\u5473\u2026\u2026 \u4f17\u4eba\u53cc\u773c\u4e00\u77ac\u4e0d\u77ac\u671b\u7740\u957f\u53d1\u62ab\u80a9\u8001\u4eba\u7684\u4e24\u53ea\u624b\uff0c\u8fd9\u867d\u4e0d\u662f\u4ec0\u4e48\u6d41\u8840\u53ae\u6740\uff0c\u6c14\u6c1b\u5374\u662f\u76f8\u5f53 \u7d27\u5f20\uff0c\u6570\u767e\u4eba\u62e5\u6324\u7684\u5927\u5385\uff0c\u7ade\u662f\u9e26\u96c0\u65e0\u58f0\uff0c\u8fd9\u65f6\u5c31\u662f\u6709\u4e00\u679a\u7ee3\u82b1\u9488\u843d\u5728\u5730\u4e0a\uff0c\u4e5f\u5fc5\u53ef\u6e05\u695a\u5440 \u5230\u3002 \u4eba\u4e1b\u4e2d\u4e0d\u77e5\u662f\u8c01\uff0c\u8f7b\u58f0\u8bf4\u9053\uff1a\u201c\u8fd9\u9999\u5473\u82e5\u662f\u542b\u6709\u5de8\u6bd2\uff0c\u5927\u5385\u4e4b\u4eba\u4e00\u4e2a\u4e5f\u522b\u60f3\u6d3b\u7740\u79bb \u5f00\uff01\u201d \u8fd9\u58f0\u97f3\u867d\u4f4e\uff0c\u542c\u5230\u4f17\u4eba\u8033\u4e2d\u5374\u4eff\u4f5b\u54cd\u8d77\u4e86\u4e00\u4e2a\u7126\u96f7\u3002\u4e0d\u5c11\u6c89\u4e0d\u4f4f\u6c14\u7684\u4eba\u8d76\u7d27\u95ed\u4f4f\u547c\u5438\u5e76 \u6697\u84c4\u771f\u529b\uff0c\u51c6\u5907\u4e8b\u6709\u8e4a\u8df7\u65f6\uff0c\u5192\u6b7b\u4e00\u51fb\u2026\u2026 \u5374\u89c1\u7aef\u65b9\u516c\u5b50\u624b\u4e0b\u4e00\u4e2a\u7ea2\u8138\u84ec\u5934\u7684\u8001\u5e74\u4e5e\u4e10\uff0c\u5e26\u7740\u718f\u4eba\u7684\u9152\u5473\uff0c\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u8fd9\u4e48 \u9999\uff01\u5c31\u662f\u7a7f\u80a0\u6bd2\u836f\uff0c\u8001\u53eb\u5316\u4e5f\u8981\u5403\u4e00\u70b9\u5c1d\u5c1d\uff01\u201d \u5634\u4e2d\u8bf4\u7740\uff0c\u53cc\u80a9\u4e00\u6643\uff0c\u672a\u89c1\u600e\u6837\u4f5c\u52bf\uff0c\u4eba\u5df2\u903c\u8fd1\u957f\u53d1\u62ab\u80a9\u8001\u4eba\u9762\u524d\uff0c\u4f38\u51fa\u2014\u53ea\u9e1f\u74dc\u4f3c\u7684 \u810f\u624b\uff0c\u731b\u5411\u957f\u53d1\u62ab\u80a9\u8001\u4eba\u624b\u4e2d\u7eb8\u5305\u62a2 \u957f\u53d1\u62ab\u80a9\u7684\u8001\u4eba\u8fde\u773c\u76ae\u90fd\u672a\u62ac\uff0c\u7ea2\u6728\u5323\u5b50\u653e\u5728\u684c\u4e0a\uff0c\u53cc\u624b\u62ff\u7740\u7eb8\u5305\u4e5f\u672a\u8eb2\uff0c\u53ea\u5728\u90a3\u7ea2\u8138 \u84ec\u5934\u7684\u5e74\u8001\u4e5e\u4e10\u9e1f\u722a\u4f3c\u7684\u53f3\u624b\u5c06\u89e6\u53ca\u7eb8\u5305\u65f6\uff0c\u5c48\u6307\u4e00\u5f39\uff0c\u90a3\u7ea2\u8138\u84ec\u5934\u7684\u8001\u5e74\u4e5e\u4e10\u4fbf\u5982\u89e6\u86c7\u874e \u822c\u8fb9\uff0c\u500f\u7136\u628a\u624b\u7f29\u4e86\u56de\u53bb\u3002 \u4f17\u4eba\u89c1\u90a3\u4f4d\u7ea2\u8138\u84ec\u5934\u7684\u4e2d\u8001\u4e5e\u4e10\uff0c\u7ea2\u8138\u6eda\u7d2b\uff0c\u53cc\u775b\u5495\u8f98\u5495\u8f98\u4e71\u8f6c\uff0c\u4fbf\u77e5\u4ed6\u5403\u4e86\u6697\u4e8f\u4e0d \u5c0f\u3002 \u7aef\u65b9\u516c\u5b50\u8eab\u540e\u7a77\u5bb6\u5e2e\u7684\u4eba\uff0c\u66f4\u662f\u5403\u60ca\uff0c\u56e0\u4e3a\u8fd9\u4f4d\u7ea2\u8138\u84ec\u5934\u7684\u8001\u5e74\u4e5e\u4e10\uff0c\u4e43\u7a77\u5bb6\u5e2e\u6709\u540d\u7684 \u201c\u9152\u4e10\u201d\uff0c\u540d\u53eb\u65b9\u5f3c\uff0c\u4e0e\u201c\u75af\u4e10\u201d\u7eea\u826f\u3001\u201c\u804b\u5de7\u201d\u5434\u5316\u5408\u79f0\u4e3a\u201c\u98ce\u5c18\u4e09\u4e10\u201d\uff0c\u4e0d\u4f46\u5728\u7a77\u5bb6\u5e2e \u8eab\u4efd\u6781\u5c0a\uff0c\u5c31\u662f\u5728\u6b66\u6797\u4e2d\u4e5f\u53ef\u79f0\u5f97\u8d77\u662f\u4e00\u6d41\u9ad8\u624b\uff0c\u5982\u4eca\u88ab\u51cc\u98ce\u516c\u5b50\u95e8\u4e0b\u98df\u5ba2\u3001\u4e00\u4e2a\u957f\u53d1\u62ab\u80a9 \u7684\u8001\u8005\uff0c\u4e00\u5f39\uff0c\u4fbf\u8d1f\u4e86\u4f24\uff0c\u4e0d\u7531\u5fc3\u4e2d\u65e2\u60ca\u4e14\u6012\uff0c\u4e00\u4e2a\u4e2a\u6012\u5f62\u4e8e\u8272\uff0c\u84c4\u52bf\u6b32\u6251\u2026\u2026 \u51cc\u98ce\u516c\u5b50\u5374\u51b7\u51b7\u8bf4\u9053\uff1a\u2018\u8c79\u7a81\u5c71\u5e84\u2019\u6c42\u624d\u82e5\u6e34\uff0c\u6155\u5bb9\u627f\u4e1a\u66f4\u662f\u793c\u8d24\u4e0b\u58eb\uff0c\u5c82\u80af\u5f53\u7740\u5929\u4e0b \u7fa4\u96c4\u65bd\u51fa\u6697\u7b97\u624b\u6bb5\uff1f\u8fd9\u8bf4\u8bdd\u4e4b\u4eba\uff0c\u5206\u660e\u5b58\u5fc3\u4e0d\u826f\uff0c\u6697\u60f3\u7834\u574f\u6155\u5bb9\u5e9c\u7684\u6e05\u8a89\uff0c\u5b9e\u5728\u53ef\u607c\uff01\u201d \u8bf4\u7740\u5411\u8eab\u540e\u4e00\u65bd\u773c\u8272\uff0c\u53ea\u89c1\u4e00\u4e2a\u7cbe\u7626\u7ec6\u957f\u9762\u5982\u8721\u7684\u52b2\u88c5\u6c49\u5b50\uff0c\u5355\u624b\u4e00\u6768\uff0c \u4eba\u7fa4\u4e2d\u7acb\u523b\u53d1\u51fa\u4e00\u58f0\u60e8\u55e5\uff0c\u4e00\u4e2a\u4e2d\u5e74\u58ee\u6c49\u53cc\u624b\u62b1\u7740\u80f8\u53e3\uff0c\u75db\u5f97\u5f2f\u4e0b\u8170\u53bb\u3002 \u4f17\u4eba\u867d\u672a\u770b\u6e05\u90a3\u7cbe\u7626\u6c49\u5b50\u662f\u7528\u4ec0\u4e48\u624b\u6cd5\u4f24\u4e86\u4e00\u4eba\uff0c\u4f46\u89c1\u51cc\u98ce\u516c\u5b50\u624b\u4e0b\u7adf\u662f\u8fd9\u822c\u98de\u626c\u8dcb \u6248\uff0c\u52a8\u8f84\u4f24\u4eba\uff0c\u4e0d\u7531\u4e2a\u4e2a\u6012\u5f62\u4e8e\u8272\u3002\u5c24\u5176\u201c\u9e92\u9e9f\u5e84\u201d\u6a0a\u975e\u95e8\u4e0b\uff0c\u66f4\u662f\u7eb7\u7eb7\u4f5c\u52bf\u6b32\u6251\u2026\u2026 \u539f\u6765\u90a3\u51b7\u8bed\u53d7\u4f24\u7684\u58ee\u6c49\uff0c\u4e43\u662f\u201c\u9738\u738b\u97ad\u201d\u6a0a\u975e\u624b\u4e0b\u3002 \u7965\u9e9f\u516c\u5b50\u89c1\u51cc\u98ce\u516c\u5b50\u5728\u81ea\u5df1\u5bb6\uff0c\u7adf\u7136\u5982\u6b64\u86ee\u6a2a\uff0c\u5fc3\u4e2d\u6012\u706b\u4e5f\u51e0\u4e4e\u6309\u538b\u4e0d\u66f4\u4e0d\u613f\u6b64\u65f6\u6709\u4efb \u4f55\u4e00\u65b9\u8d77\u4e86\u51b2\u7a81\uff0c\u4e8e\u662f\uff0c\u5f3a\u5fcd\u4f4f\u6ee1\u8154\u6012\u706b\uff0c\u9053\uff1a\u201c\u6155\u5bb9\u5144\uff0c\u65e2\u4e5f\u5e26\u6709\u79d8\u5b9d\uff0c\u4e0d\u59a8\u5728\u8d4f\u706f\u5927\u4f1a \u4e0a\uff0c\u4e00\u9f50\u5c55\u51fa\u4e8e\u5929\u4e0b\u6b66\u6797\u4e4b\u524d\uff0c\u73b0\u5728\u4e14\u8bf7\u6536\u8d77\u2026\u2026\u201d \u4f46\u4ed6\u8bdd\u5c1a\u672a\u8bf4\u5b8c\uff0c\u90a3\u957f\u53d1\u62ab\u80a9\u8001\u8005\u5df2\u5c06\u6700\u540e\u4e00\u5c42\u7eb8\u5305\u6253\u5f00\uff0c\u53ea\u89c1\u4ed6\u4e24\u6307\u4ece\u7eb8\u5305\u634f\u51fa\u4e00\u6bb5 \u767d\u85d5\u4f3c\u7684\u4e1c\u897f\uff0c\u4ecd\u662f\u5c48\u6307\u4e00\u5f39\uff0c\u4e00\u7f15\u767d\u5149\uff0c\u76f4\u5411\u201c\u9152\u4e10\u2019\u2019\u65b9\u5f3c\u9762\u95e8\u5c04\u53bb\uff0c\u540c\u65f6\u5634\u4e2d\u8bf4\u9053\uff1a \u8001\u53eb\u5316\uff01\u4f60\u65e2\u662f\u5634\u998b\uff0c\u5c31\u5148\u7ed9\u4f60\u4e00\u70b9\u5c1d\u5c1d\u9c9c\u5427\uff01\u4e5f\u514d\u5f97\u53eb\u5929\u4e0b\u6b66\u6797\u7b11\u8bdd\u2018\u8c79\u7a81\u5c71\u5e84\u2019\u5c0f \u6c14\uff01\u201d \u201c\u9152\u4e10\u2019\u2019\u65b9\u5f3c\uff0c\u5f53\u771f\u662f\u80c6\u5927\u5305\u5929\uff0c\u867d\u4e0d\u660e\u5bf9\u65b9\u771f\u6b63\u610f\u56fe\uff0c\u53c8\u672a\u770b\u6e05\u7eb8\u5305\u662f\u4f55\u4e8b\u7269\uff0c\u957f \u53d1\u62ab\u80a9\u8001\u8005\u5c48\u6307\u5f39\u6765\u767d\u5f71\uff0c\u4ed6\u8fde\u770b\u4e5f\u672a\u770b\uff0c\u7adf\u5f20\u53e3\u63a5\u4f4f\uff0c\u201c\u54af\u5d29\uff01\u54af\u5d29\uff01\u201d\u4e00\u9635\u5480\u56bc\uff0c\u201c\u54af \u54af\u201d\u4e24\u58f0\uff0c\u54bd\u4e0b\u8179\u53bb\u3002 \u4e0d\u4f46\u7aef\u65b9\u516c\u5b50\u65b9\u9762\u7684\u4eba\u4e2a\u4e2a\u5403\u60ca\uff0c\u5c31\u8fde\u4e0e\u201c\u9152\u4e10\u201d\u65e0\u5173\u7684\u4f17\u4eba\uff0c\u4e5f\u90fd\u6697\u6697\u4e3a\u4ed6\u62c5\u5fc3\u2026\u2026 \u8c01\u77e5\u201c\u9152\u4e10\u201d\u65b9\u5f3c\u54bd\u4e0b\u4e4b\u540e\uff0c\u8214\u5634\u4f38\u820c\uff0c\u610f\u72b9\u672b\u5c3d\uff0c\u53c8\u4ece\u80cc\u4e0a\u89e3\u4e0b\u4e00\u4e2a\u7ea2\u6f06\u5927\u9152\u846b\u82a6 \u6765\uff0c\u4ef0\u8d77\u8116\u5b50\u201c\u5495\u561f\uff01\u5495\u561f\uff01\u201d\u559d\u4e86\u51e0\u5927\u53e3\u9152\uff0c\u624d\u7528\u7834\u8896\u53e3\u64e6\u4e86\u64e6\u5634\u5507\u9053\uff1a\u201c\u957f\u6bdb\u8001\u602a\uff01\u4f60 \u7ed9\u8001\u53eb\u5316\u5403\u7684\uff0c\u662f\uff1a\u4e0d\u662f\u4ece\u5b59\u609f\u7a7a\u90a3\u91cc\u5077\u6765\u7684\u2018\u4eba\u53c2\u679c\u2019\uff1f\u600e\u4e48\u8fd9\u6837\u597d\u5403\uff1f\u201d \u957f\u53d1\u62ab\u80a9\u8001\u8005\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u8001\u53eb\u5316\uff01\u7b97\u4f60\u9020\u5316\uff01\u8fd9\u867d\u7136\u4e0d\u662f\u53ef\u4f7f\u4eba\u6210\u4ed9\u6210\u4f5b\u7684\u2018\u4eba\u53c2 \u679c\u2019\uff0c\u5374\u662f\u9053\u5730\u7684\u2018\u5343\u5e74\u53c2\u738b\u2019\uff0c\u4f60\u5982\u679c\u73b0\u5728\u8fd0\u529f\u4e00\u904d\uff0c\u53ef\u62b5\u4f60\u82e6\u4fee\u4e09\u4e2d\u7684\u529f\u529b\uff01\u201d \u4f17\u4eba\u95fb\u542c\uff0c\u7b80\u76f4\u4e0d\u6562\u76f8\u4fe1\uff0c\u5bf9\u6beb\u4e0d\u76f8\u5e72\u4e4b\u4eba\uff0c\u7ade\u80af\u65bd\u820d\u8fd9\u4e48\u73cd\u8d35\u7684\u7075\u836f\u3002 \u201c\u9152\u4e10\u201d\u65b9\u4e24\u5374\u6beb\u4e0d\u72b9\u8c6b\uff0c\u7acb\u523b\u76d8\u819d\u5750\u5728\u5730\u4e0b\uff0c\u4e94\u5fc3\u671d\u5929\uff0c\u53cc\u638c\u6263\u8bc0\uff0c\u884c\u8d77\u529f\u6765\u3002 \u4f17\u4eba\u88ab\u597d\u5947\u5fc3\u9a71\u4f7f\uff0c\u8c01\u4e5f\u6ca1\u52a8\uff0c\u4e5f\u6ca1\u6709\u4eba\u8bf4\u8bdd\uff0c\u4e00\u9f50\u77aa\u5927\u773c\u775b\u671b\u7740\u201c\u9152\u4e10\u201d\u5750\u5728\u5730\u4e0b\u884c \u529f\u3002 \u4e0d\u5230\u4e00\u76cf\u70ed\u8336\u5de5\u592b\uff0c\u201c\u9152\u4e10\u201d\u8df3\u8d77\u8eab\u6765\uff0c\u53cc\u76ee\u4e00\u7741\uff0c\u5947\u5149\u56db\u5c04\uff0c\u8fde\u9053\uff1a\u201c\u597d\u5b9d\uff01\u597d \u5b9d\uff01\u201d \u4f17\u4eba\u4e00\u89c1\uff0c\u201c\u9152\u4e10\u201d\u8d64\u7ea2\u7684\u8138\u4e0a\u6cdb\u8d77\u58eb\u5c42\u5b9d\u5149\uff0c\u53cc\u76ee\u795e\u5149\u4e5f\u4f3c\u6bd4\u65b9\u624d\u7cbe\u83b9\u591a\u4e86\u3002 \u60f3\u8fd9\u79cd\u589e\u5f3a\u529f\u529b\u7684\u7075\u836f\uff0c\u4e43\u6b66\u6797\u4eba\u68a6\u5bd0\u6c42\u4e4b\u800c\u4e0d\u53ef\u5f97\u7684\u4e1c\u897f\uff0c\u5728\u51cc\u98ce\u516c\u5b50\u624b\u4e0b\u5374\u6709\u6574\u6574 \u4e00\u5323\u5b50\uff0c\u4e0d\u7531\u4e00\u9f50\u5c06\u76ee\u5149\u6295\u5c04\u5230\u957f\u53d1\u62ab\u80a9\u8001\u4eba\u7684\u624b\u4e0a\u3002 \u90a3\u62ab\u53d1\u8001\u4eba\u5374\u81ea\u5b98\u81ea\u8bed\u5730\u9053\uff1a\u201c\u8c01\u8981\u6295\u9760\u2018\u8c79\u7a81\u5c71\u5e84\u2019\uff0c\u8c01\u4fbf\u6709\u798f\u4eab\u53d7\u4e00\u6bb5\u5343\u5e74\u53c2 \u738b\uff01\u201d \u5c55\u767d\u5728\u4e00\u4fa7\u51b7\u773c\u65c1\u89c2\uff0c\u81f3\u6b64\u65b9\u7b97\u660e\u767d\u51cc\u98ce\u516c\u5b50\u53eb\u95e8\u4e0b\u98df\u5ba2\u73b0\u51fa\u6b66\u6797\u81f3\u5b9d\u4e4b\u610f\uff0c\u539f\u662f\u60f3\u6536 \u4e70\u4eba\u5fc3\u3002 \u540c\u65f6\uff0c\u4e5f\u604d\u7136\u5927\u609f\uff0c\u4e3a\u4ec0\u4e48\u5176\u4ed6\u6b66\u6797\u4e09\u516c\u5b50\uff0c\u4e5f\u5747\u4ee5\u6b66\u6797\u4eba\u89c1\u773c\u7ea2\u7684\u5f02\u5b9d\uff0c\u5f53\u7740\u5929\u4e0b\u6b66 \u6797\u5356\u5f04\uff0c\u65e0\u975e\u90fd\u662f\u60f3\u501f\u6b64\u62c9\u62e2\u6b66\u6797\u4eba\u58eb\u5f52\u987a\u5230\u81ea\u5df1\u95e8\u4e0b\u6765\u3002 \u4f46\u8fd9\u79cd\u5351\u52a3\u7528\u5fc3\uff0c\u7684\u786e\u53eb\u4eba\u770b\u4e86\u4e0d\u987a\u773c\uff0c\u5c24\u5176\u60f3\u5230\u540d\u91cd\u6b66\u6797\u7684\u56db\u516c\u5b50\uff0c\u7adf\u662f\u7528\u8fd9\u79cd\u624b \u6bb5\uff0c\u6765\u6811\u7acb\u81ea\u5df1\u5e9e\u5927\u7684\u52bf\u529b\u3002\u60f3\u7740\u4e0d\u7531\u597d\u7b11\uff0c\u7981\u4e0d\u4f4f\u563f\u563f\u51b7\u7b11\u51fa\u58f0\u3002 \u51cc\u98ce\u516c\u5b50\u5374\u4e0d\u7ba1\u5c55\u767d\u51b7\u7b11\uff0c\u89c1\u81ea\u5df2\u72e1\u8ba1\u5f97\u552e\uff0c\u626c\u626c\u5f97\u610f\u5730\u9053\uff1a\u201c\u8c79\u7a81\u5c71\u5e84\u2019\u4e0d\u4ec5\u6709\u8fd9\u70b9 \u5343\u5e74\u53c2\u738b\uff0c\u8fd8\u6709\u2018\u4f55\u9996\u4e4c\u2019\u3001\u2018\u5343\u5e74\u7075\u829d\u6db2\u2019\u7b49\u7b49\u6570\u4e0d\u6e05\u7684\u5947\u73cd\u5f02\u5b9d\uff0c\u4e13\u7b49\u52a0\u76df\u6155\u5bb9\u95e8\u4e0b\u7684 \u56db\u65b9\u8c6a\u6770\u5171\u4eab\u2026\u2026\u201d \u6b66\u6797\u4eba\u58eb\u4e0d\u7231\u91d1\uff0c\u4e0d\u7231\u94f6\uff0c\u5374\u6700\u7231\u8fd9\u7b49\u589e\u957f\u529f\u529b\u7ea6\u7075\u836f\uff0c\u6709\u7684\u4e3a\u4e89\u593a\u4e00\u652f\u534a\u53f6\u7075\u836f\u73cd \u679c\uff0c\u4e0d\u501f\u62fc\u547d\u4ee5\u8d74\uff0e\u5982\u4eca\u542c\u8bf4\u53ea\u8981\u52a0\u76df\u8c79\u7a81\u5c71\u5e84\uff0c\u4fbf\u53ef\u4eab\u53d7\u8fd9\u79cd\u767e\u5e74\u96be\u5f97\u4e00\u9047\u7684\u7075\u836f\uff0c\u4e0d\u5c11 \u4eba\u84e6\u7136\u52a8\u5bb9\uff0c\u5c24\u5176\u770b\u5230\u201c\u9152\u4e10\u201d\u5f53\u573a\u670d\u8fc7\u7684\u529f\u6548\uff0c\u66f4\u662f\u7fa1\u6155\u4e0d\u5df2\u3002\u4f46\u4e3a\u4e86\u9762\u5b50\u95ee\u9898\uff0c\u4e00\u65f6\u4e4b \u95f4\u8fd8\u4e0d\u597d\u610f\u601d\u8702\u62e5\u5411\u524d\uff0c\u4f46\u5df2\u6709\u6570\u4eba\u811a\u6b65\u907f\u8d85\uff0c\u60f3\u51d1\u5411\u524d\u53bb\u4e86\u2026\u2026 \u7965\u9e9f\u516c\u5b50\u773c\u89c1\u51cc\u98ce\u516c\u5b50\u72e1\u8ba1\u5f97\u552e\uff0c\u5fc3\u4e2d\u5927\u6025\uff0c\u5fd9\u9053\uff1a\u201c\u8bf8\u4f4d\uff01\u5148\u6f14\u5165\u5e2d\u5427\uff01\u8d4f\u706f\u5927\u4f1a \u4e0a\uff0c\u8fd8\u6709\u66f4\u73cd\u8d35\u7684\u79d8\u5b9d\u53ef\u770b\u5462\uff01\u201d \u5728\u4eba\u4e1b\u4e2d\uff0c\u5c55\u767d\u7a81\u89c1\u4e00\u8f86\u8f6e\u8f66\u75be\u95ea\u800c\u8fc7\uff0c\u76f4\u5411\u5385\u540e\u6025\u53bb\u3002\u4ed6\u5982\u4eca\u76ee\u529b\u8fc7\u4eba\uff0c\u867d\u4f17\u4eba\u4ea4\u9519 \u62e5\u7acb\uff0c\u5df2\u770b\u51fa\u90a3\u6b63\u662f\u201c\u9752\u86a8\u795e\u201d\u91d1\u4e5d\u7684\u8f6e\u6905\uff0c\u4ed6\u6050\u6015\u201c\u9752\u86a8\u795e\u201d\u9690\u800c\u4e0d\u51fa\uff0c\u518d\u96be\u89c1\u9762\uff0c\u7acb\u523b \u9661\u7136\u5927\u559d\u4e00\u58f0\u201d\u201c\u91d1\u8001\u524d\u8f88\uff0c\u522b\u8d70\uff01\u201d \u7b2c\u4e09\u5341\u4e8c\u7ae0\u3000\u201c\u77f3\u77f6\u5927\u9635\u201d>> \u53e4\u9f99\u300a\u5251\u5ba2\u884c\u300b \u7b2c\u4e09\u5341\u4e8c\u7ae0\u3000\u201c\u77f3\u77f6\u5927\u9635\u201d \u5c55\u767d\u8fd9\u4e00\u58f0\u5927\u559d\uff0c\u58f0\u5982\u9707\u96f7\uff0c\u5927\u5385\u70db\u5149\u90fd\u4e3a\u4e4b\u4e00\u98a4\uff01 \u4f17\u4eba\u66f4\u662f\u6115\u7136\u60ca\u987e\uff0c\u5c55\u767d\u5374\u4e0d\u987e\u4e00\u5207\uff0c\u6392\u4f17\u800c\u524d\uff0c\u98de\u8eab\u63a0\u81f3\u201c\u9752\u86a8\u795e\u201d\u91d1\u4e5d\u9762\u524d\u4e08\u4f59\u4e4b \u5904\uff0c\u9053\uff1a\u5728\u4e0b\u5c55\u767d\uff0c\u60f3\u8bf7\u95ee\u91d1\u8001\u524d\u8f88\u4e00\u4ef6\u4e8b\uff0c\u4e0d\u77e5\u80fd\u5426\u89c1\u544a\uff1f\u201d \u201c\u9752\u86a8\u795e\u201d\u91d1\u4e5d\u867d\u7136\u505c\u4e0b\u8f6e\u6905\uff0c\u4f46\u8138\u4e0a\u4e00\u65e0\u8868\u60c5\uff0c\u53ea\u7565\u76b1\u5e9e\u7709\u9053\uff1a\u201c\u8001\u592b\u5341\u6570\u5e74\u4e0d\u51fa\u6c5f \u6e56\uff0c\u5bf9\u5916\u8fb9\u7684\u4e8b\u4e00\u65e0\u6240\u77e5\uff0c\u6709\u4ec0\u4e48\u95ee\u9898\uff0c\u4f60\u8fd8\u662f\u8bf7\u6559\u522b\u4eba\u53bb\u5427\uff01\u201d \u8bf4\u7f62\uff0c\u8f6c\u52a8\u8f6e\u6905\uff0c\u76f4\u5411\u5927\u5385\u540e\u5357\u8def\u9a70\u53bb\uff01 \u5c55\u767d\u6025\u9053\uff1a\u201c\u6162\u7740\uff01\u2026\u2026\u201d \u4f46\u662f\u201c\u9752\u86a8\u795e\u201d\u91d1\u4e5d\u518d\u4e0d\u7406\u4ed6\uff0c\u5934\u4e5f\u4e0d\u56de\u5730\u9a71\u6905\u75f0\u8f6c\u800c\u53bb\uff0c\u5c55\u767d\u98de\u8eab\u5411\u5176\u8ffd\u53bb\uff01 \u4f46\u4ed6\u8eab\u5f62\u624d\u8d77\uff0c\u4e24\u8def\u94c1\u95e8\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u81ea\u884c\u5173\u95ed\uff0c\u5c55\u767d\u6536\u52bf\u6025\u843d\uff0c\u9669\u4e9b\u649e\u5728\u94c1\u95e8\u4e0a\u3002 \u63a5\u7740\u96e8\u8def\u4e24\u4fa7\u8fb9\u95e8\u8fde\u95ea\u51fa\u516d\u4e2a\u767d\u8863\u5c0f\u7ae5\uff0c\u4e00\u9f50\u624b\u6a2a\u94f6\u5251\uff0c\u6321\u4f4f\u53bb\u8def\uff01 \u8fd9\u516d\u4e2a\u767d\u8863\u5c0f\u7ae5\u5e74\u7eaa\u5747\u4e0d\u5927\uff0c\u7ea6\u5728\u5341\u56db\u4e94\u5c81\u4e4b\u95f4\uff0c\u8eab\u6cd5\u5374\u5feb\u5f97\u51fa\u5947\uff0c\u4ee5\u5c55\u767d\u7684\u773c\u5149\uff0c\u7ade \u672a\u770b\u6e05\u516d\u4e2a\u4eba\u662f\u600e\u4e48\u7a9c\u51fa\u6765\u7684\uff0c\u597d\u50cf\u94c1\u95e8\u4e4b\u524d\u51ed\u7a7a\u591a\u4e86\u516d\u4e2a\u5c0f\u7ae5\u4e00\u822c\u3002\u8fd9\u516d\u4e2a\u5c0f\u7ae5\u4e00\u5f8b\u767d\u8863 \u5782\u53d1\uff0c\u624b\u6a2a\u94f6\u5251\uff0c\u6321\u5728\u5c55\u767d\u9762\u524d\uff0c\u800c\u4e14\u2014\u4e2a\u4e2a\u4e0d\u53d1\u4e00\u8a00\uff0c\u516d\u53cc\u5c0f\u773c\u77aa\u5f97\u6eda\u5706\uff0c\u770b\u6837\u5b50\u53ea\u8981\u5c55 \u767d\u518d\u524d\u8fdb\u4e00\u6b65\uff0c\u4f7f\u8981\u4e00\u9f50\u51fa\u624b\u653b\u51fb\uff01 \u5c55\u767d\u5fae\u5fae\u4e00\u6014\uff0c\u8eab\u540e\u5374\u4f20\u6765\u91d1\u5f69\u98ce\u5982\u94f6\u94c3\u7684\u58f0\u97f3\uff1a\u201c\u5c55\u5c11\u4fa0\uff01\u201d \u5c55\u767d\u95fb\u58f0\u56de\u5934\uff0c\u89c1\u91d1\u5f69\u51e4\u4e00\u53cc\u7f8e\u5f97\u51fa\u5947\u7684\u5927\u773c\u775b\uff0c\u6b63\u671b\u7740\u81ea\u5df1\uff0c\u773c\u4e2d\u6d41\u9732\u7740\u7591\u95ee\u4e0e\u8be7\u5f02 \u4e4b\u8272\u3002 \u8fd9\u662f\u91d1\u5f69\u51e4\u51fa\u4f17\u7684\u5730\u65b9\uff0c\u5f80\u5f80\u5979\u5fc3\u4e2d\u6240\u60f3\u4e0d\u7528\u8bf4\u51fa\u53e3\u6765\uff0c\u4fbf\u53ef\u4ee4\u5bf9\u65b9\u77e5\u9053\u5979\u7684\u5fc3\u610f\uff0c\u6240 \u8c13\u201c\u773c\u775b\u4f1a\u8bf4\u8bdd\u201d\uff0c\u6050\u6015\u5c31\u662f\u6307\u6b64\u800c\u8bf4\u7684\u4e86\u3002 \u5c55\u767d\u53f9\u4e86\u53e3\u6c14\uff0c\u5fc3\u60f3\uff1a\u201c\u51a4\u6709\u5934\uff0c\u503a\u6709\u4e3b\uff0c\u2018\u9752\u86a8\u795e\u2019\u91d1\u4e5d\u6740\u6b7b\u6211\u7684\u7236\u4eb2\uff0c\u5374\u4e0e\u4ed6\u7684\u4e8e \u5973\u65e0\u5173\uff0c\u2018\u9752\u86a8\u795e\u2019\u65e2\u5df2\u8d70\u4e86\uff0c\u62a5\u4ec7\u7684\u8bdd\u5148\u4e0d\u63d0\u4e5f\u7f62\uff01\u2026\u2026\u201d\u5c55\u767d\u60f3\u81f3\u6b64\u5904\uff0c\u4fbf\u6447\u4e86\u6447\u5934 \u9053\uff1a\u6ca1\u6709\u4ec0\u4e48\uff01\u201d \u8c01\u77e5\u91d1\u5f69\u51e4\u5e76\u4e0d\u653e\u8fc7\u4ed6\uff0c\u7528\u4e00\u79cd\u67d4\u60c5\u4e07\u79cd\u7684\u58f0\u8c03\u5e7d\u5e7d\u8bf4\u9053\uff1a\u201c\u6211\u770b\u5f97\u51fa\uff01\u2019 \u5979\u6ca1\u6709\u518d\u8bf4\u4e0b\u53bb\uff0c\u4f46\u5c31\u8fd9\u56db\u4e2a\u5b57\u52a0\u4e0a\u5979\u4f1a\u8bf4\u8bdd\u7684\u773c\u775b\uff0c\u4ea6\u53ef\u660e\u767d\u8868\u793a\u51fa\uff0c\u90a3\u4e0b\u8fb9\u7684\u610f\u601d \u662f\uff1a\u201c\u4f60\u4e00\u5b9a\u6709\u4ec0\u4e48\u7591\u96be\u4e0d\u51b3\u4e4b\u4e8b\uff0c\u85cf\u5728\u5fc3\u4e2d\uff0c\u8bf7\u544a\u8bc9\u6211\u5427\uff01\u6211\u4f1a\u5e2e\u52a9\u4f60\u7684\u2026\u2026\u201d \u5c55\u767d\u80fd\u786e\u5207\u4f53\u4f1a\u51fa\u91d1\u5f69\u51e4\u8a00\u7b80\u610f\u6df1\u7684\u60c5\u610f\uff0c\u4f46\u662f\u4ed6\u80fd\u53eb\u4ec7\u4eba\u7684\u4eb2\u751f\u5973\u513f\uff0c\u6765\u5e2e\u52a9\u4ed6\u53bb\u6740 \u5979\u7684\u7236\u4eb2\u5417\uff1f\u2026\u2026\u5c55\u767d\u53ea\u6709\u6447\u5934\u82e6\u7b11\u9053\uff1a\u201c\u771f\u7684\u6ca1\u6709\u4ec0\u4e48\uff01\u201d \u4f46\u91d1\u5f69\u51e4\u660e\u4e3d\u7684\u53cc\u775b\uff0c\u4ecd\u7136\u8109\u8109\u542b\u60c5\u5730\u671b\u7740\u4ed6\uff0c\u800c\u4e14\u90a3\u773c\u795e\u4e2d\u5c1a\u542b\u6709\u4e0d\u5c11\u5e7d\u6028\u7684\u6210\u4efd\uff0c \u4ece\u90a3\u6f06\u9ed1\u7684\u7167\u5f97\u89c1\u4eba\u7684\u53cc\u77b3\u4e2d\uff0c\u5c55\u767d\u4f3c\u5df2\u8bfb\u51fa\u4e86\u91cc\u8fb9\u7684\u610f\u601d\uff0c\u90a3\u662f\u8bda\u610f\u7684\u60f3\u5e2e\u52a9\u4ed6\u3002 \u7a81\u7136\uff0c\u5927\u5385\u4e4b\u4e2d\u706f\u5149\u5168\u7184\uff0c\u773c\u524d\u4e00\u7247\u6f06\u9ed1\uff0c\u5927\u5385\u4e2d\u7684\u7fa4\u96c4\u9f50\u58f0\u60ca\u53eb\uff01 \u63a5\u7740\u4e00\u58f0\u51c4\u5389\u7684\u957f\u7b11\uff0c\u72b9\u5982\u9b3c\u54ed\u72fc\u5578\uff0c\u4f7f\u4eba\u542c\u6765\u6bdb\u9aa8\u609a\u7136\uff01 \u8fd9\u901f\u53d8\u6765\u5f97\u592a\u7a81\u7136\uff0c\u6570\u767e\u4eba\u9f50\u96c6\u5728\u5927\u5385\u4e2d\u54d7\u7136\u4e00\u9635\u5927\u4e71\uff01 \u9f50\u58f0\u53eb\u56b7\uff1a\u201c\u563f\uff01\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d \u201c\u600e\u4e48\u706f\u5168\u706d\u4e86\uff1f\u201d \u201c\u4ec0\u4e48\u4eba\uff1f\u8fd9\u662f\u4ec0\u4e48\u4eba\u5728\u602a\u7b11\uff1f\u201d \u201c\u201c \u53ef\u662f\uff0c\u5c31\u5728\u4f17\u4eba\u4e03\u5634\u516b\u820c\u5730\u4e71\u56b7\u4e71\u53eb\u4e4b\u95f4\uff0c\u7a81\u7136\u201c\u8f70\u9686\uff01\u8f70\u9686\uff01\u2026\u2026\u201d\u4e00\u9635\u5de8\u54cd\u4f20\u6765\uff0c \u6574\u5ea7\u5927\u5385\u4e5f\u8ddf\u7740\u6447\u6643\u8d77\u6765\uff01 \u6709\u4eba\u9ad8\u58f0\u5927\u558a\uff0c\u4f46\u518d\u5f3a\u70c8\u7684\u5730\u9707\u4e5f\u6ca1\u6709\u8fd9\u4e48\u5927\u7684\u9707\u52a8\uff0c\u56e0\u4e3a\u6574\u5ea7\u5927\u5385\u90fd\u65cb\u8f6c\u8d77\u6765\u4e86\uff01 \u542c\u89c1\u6709\u4eba\u558a\u5730\u9707\uff0c\u4fbf\u6709\u4e0d\u5c11\u4eba\u593a\u95e8\u5411\u5385\u5916\u8dd1\u53bb\uff0c\u4f46\u662f\u4e0d\u8dd1\u8fd8\u597d\uff0c\u8fd9\u4e00\u8dd1\u7acb\u523b\u8eab\u8eaf\u5931\u53bb\u5e73 \u8861\uff0c\u659c\u98de\u6b6a\u649e\uff0c\u8dcc\u8dcc\u78b0\u78b0\uff0c\u4e0d\u5c11\u4eba\u8eab\u8eaf\u649e\u5728\u5899\u58c1\u4e0a\uff0c\u989d\u89d2\u649e\u5728\u5385\u679d\u4e0a\uff0c\u201c\u7830\uff01\u7830\uff01\u5f6d\uff01 \u5f6d\uff01\u201d\u54c0\u547c\u60e8\u55e5\uff0c\u63a5\u4e8c\u8fde\u4e09\u4f20\u51fa\uff0c\u5df2\u6709\u4e0d\u5c11\u4eba\u8d1f\u4e86\u4f24\uff01 \u614c\u4e71\u4e2d\uff0c\u4ece\u9ed1\u6697\u91cc\u54cd\u8d77\u7965\u9e9f\u516c\u5b50\u6124\u6012\u7684\u543c\u53eb\uff1a\u201c\u4ec0\u4e48\u4eba\u53d1\u52a8\u4e86\u2018\u77f3\u77f6\u5927\u9635\u2019\uff1f\u201d \u4f17\u4eba\u604d\u7136\u5927\u609f\uff0c\u539f\u6765\u8fd9\u5ea7\u5927\u5385\uff0c\u7adf\u662f\u91d1\u5e9c\u4e00\u4e2a\u57cb\u4f0f\u673a\u5173\uff01 \u53ef\u662f\uff0c\u8fd9\u57cb\u4f0f\u673a\u5173\u65e2\u662f\u8bbe\u5728\u91d1\u5e9c\uff0c\u64cd\u7eb5\u673a\u5173\u4e4b\u4eba\u5fc5\u4e5f\u662f\u91d1\u5e9c\u4e4b\u4eba\uff0c\u96be\u9053\u8fde\u4ed6\u4eec\u5e9c\u4e0a\u7684\u5c11 \u7237\u5c0f\u59d0\u4e5f\u5728\u5176\u4e2d\uff0c\u4ed6\u4eec\u4fbf\u8981\u65bd\u5c55\u6bd2\u624b\u4e86\u5417\uff1f \u8fd9\u771f\u662f\u4f7f\u4eba\u767e\u601d\u4e0d\u5f97\u5176\u89e3\u7684\u602a\u4e8b\uff01 \u7965\u9e9f\u516c\u5b50\u8fde\u58f0\u559d\u95ee\uff0c\u6124\u6012\u3001\u7126\u6025\u7684\u58f0\u8c03\uff0c\u4efb\u4f55\u4eba\u90fd\u53ef\u542c\u5f97\u51fa\uff0c\u8fd9\u53d1\u52a8\u9635\u52bf\u7684\u547d\u4ee4\uff0c\u51b3\u4e0d \u4f1a\u662f\u4ed6\u53d1\u51fa\uff0c\u800c\u4e14\u770b\u60c5\u5f62\uff0c\u5c31\u662f\u8fde\u4ed6\u4e5f\u9677\u4e8e\u5371\u673a\u4e4b\u4e2d\uff01 \u4f46\u90a3\u58f0\u58f0\u602a\u7b11\uff0c\u4ecd\u81ea\u5927\u5385\u5c4b\u9876\u8f6c\u6765\uff0c\u5bf9\u7965\u9e9f\u516c\u5b50\u7684\u6124\u6012\u559d\u95ee\uff0c\u7adf\u662f\u4e0d\u7406\u4e0d\u776c\uff01 \u7965\u9e9f\u516c\u5b50\u8fde\u8fde\u547c\u559d\u6570\u58f0\uff0c\u7adf\u4e0d\u80fd\u963b\u6b62\u53d1\u52a8\u9635\u52bf\u4e4b\u4eba\uff0c\u53ea\u6c14\u5f97\u4e09\u5c3a\u795e\u66b4\u8df3\u3002\u8eab\u8fb9\u5374\u4f20\u6765\u91d1 \u5f69\u98ce\u5e7d\u9759\u7684\u58f0\u8c03\uff1a\u201c\u54e5\u54e5\uff01\u90a3\u5728\u5c4b\u9876\u602a\u7b11\u7684\u4eba\u662f\u5e08\u5144\uff0c\u64c5\u81ea\u53d1\u52a8\u2018\u77f3\u77f6\u5927\u9635\u2019\u7684\u4eba\u4e5f\u4e00\u5b9a\u662f \u4ed6\u4e86\uff01\u201d \u7965\u9e9f\u516c\u5b50\u4e00\u542c\uff0c\u7adf\u662f\u7236\u4eb2\u7684\u7231\u5f92\u505a\u51fa\u8fd9\u7b49\u75af\u72c2\u4e4b\u4e8b\uff0c\u4e0d\u7531\u6012\u53eb\u9053\uff1a\u5b5f\u5982\u840d\uff01\u4f60\u75af\u4e86\uff1f\u8fd8 \u4e0d\u8d76\u5feb\u505c\u6b62\uff01\u201d \u8c01\u77e5\uff0c\u7965\u9e9f\u516c\u5b50\u6307\u540d\u559d\u95ee\uff0c\u90a3\u58f0\u58f0\u602a\u7b11\u4ecd\u672a\u6b47\u6b62\uff0c\u6574\u5ea7\u5927\u5385\u5374\u5728\u65cb\u8f6c\u6447\u64bc\u4e4b\u4e2d\uff0c\u5411\u5730\u4e0b \u8fc5\u75be\u5730\u6c89\u53bb\uff01 \u7a81\u542c\u7aef\u65b9\u516c\u5b50\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u91d1\u7965\u9e9f\uff01\u4f60\u8fd9\u4e00\u624b\u771f\u662f\u7edd\u6781\u4e86\uff01\u2018\u6068\u5929\u7edd\u6237\u8ba1\u2019\uff01\u60f3\u4e00\u7f51 \u628a\u5929\u4e0b\u6b66\u6797\u53ef\u662f\u4f60\u4e5f\u522b\u60f3\u6d3b\u7740\u51fa\u53bb\uff0c\u5728\u6211\u4eec\u5927\u5bb6\u5c1a\u672a\u6b7b\u5b8c\u4e4b\u524d\uff0c\u4f60\u5144\u59b9\u4e8c\u4eba\u5148\u5f97\u4e3a\u5929\u4e0b\u6b66\u6797 \u507f\u547d\uff01\u201d \u7965\u9e9f\u516c\u5b50\u6012\u9053\uff1a\u4f60\u8981\u600e\u4e48\u6837\uff1f\u201d \u7aef\u65b9\u516c\u5b50\u9053\uff1a\u201c\u4f60\u4ed7\u7740\u673a\u5173\u57cb\u4f0f\uff0c\u628a\u5929\u4e0b\u6b66\u6797\u9a97\u5728\u5927\u5385\uff0c\u60f3\u6765\u4e2a\u4e00\u7f51\u6253\u5c3d\uff0c\u6211\u7aef\u65b9\u5c31\u51ed \u7a77\u5bb6\u5e2e\u7684\u2018\u9752\u7af9\u9635\u2019\uff0c\u4fbf\u53ef\u53eb\u5c14\u5144\u59b9\u6b7b\u65e0\u846c\u8eab\u4e4b\u5730\uff01 \u7965\u9e9f\u516c\u5b50\u6012\u6781\u53cd\u7b11\uff1a\u201c\u54c8\u54c8\u54c8\u2026\u2026\u201d \u7b11\u58f0\u9ad8\u4ea2\u9707\u8033\uff0c\u4e0e\u5c4b\u9876\u7684\u51c4\u5389\u602a\u7b11\u9065\u76f8\u547c\u5e94\uff0c\u6c14\u6c1b\u663e\u5f97\u60b2\u60e8\u6050\u6016\u4e4b\u6781\uff01 \u7965\u9e9f\u516c\u5b50\u7b11\u7f62\u8bf4\u9053\uff1a\u201c\u53f8\u7a7a\u5144\uff01\u4f60\u7528\u4e0d\u5230\u8840\u53e3\u55b7\u4eba\uff0c\u91d1\u7965\u9e9f\u6ca1\u6709\u5bb3\u4eba\u4e4b\u610f\uff0c\u5c31\u662f\u6709\u5bb3\u4eba \u4e4b\u610f\uff0c\u4e5f\u4e0d\u4f1a\u8fde\u81ea\u5df1\u90fd\u5bb3\u5728\u5176\u4e2d\uff01\u201d \u9ed1\u6697\u4e2d\u4f20\u6765\u7aef\u65b9\u516c\u5b50\u7684\u51b7\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u8bf4\u6b64\u8bdd\u8c01\u4f1a\u6839\u4fe1\uff1f\u201d\u5df1\u5bb6\u4e2d\u7684\u673a\u5173\u4f1a\u628a\u81ea\u5df2\u5bb3 \u4e86\uff0c\u83ab\u975e\u4f60\u662f\u6015\u6b7b\u8d2a\u751f\u4e4b\u8f88\uff0c\u624d\u4e0d\u6562\u627f\u8ba4\u5bb3\u4eba\uff1f\u4e0d\u6562\u63a5\u53d7\u672c\u516c\u5b50\u7684\u6311\u6218\uff1f\u201d \u7965\u9e9f\u516c\u5b50\u5c1a\u672a\u7b54\u8a00\uff0c\u201c\u4e7e\u5764\u638c\u201d\u4e91\u5b97\u9f99\u51b7\u51b7\u5730\u8bf4\u9053\uff1a\u201c\u53f8\u5f92\u4e16\u5144\uff01\u4f60\u7eb5\u7136\u6709\u62fc\u547d\u4e4b\u5fc3\uff0c \u65e0\u5948\u9ed1\u6697\u4e4b\u4e2d\u654c\u53cb\u4e0d\u5206\uff0c\u4f60\u4e5f\u96be\u65bd\u8fa3\u624b\u3002\u201c \u201c\u9152\u4e10\u201d\u65b9\u5f3c\u7b11\u9053\u201c\u8981\u996d\u7684\u6709\u7684\u662f\u5077\u9e21\u6478\u72d7\u7684\u73a9\u610f\uff01\u201d \u7aef\u65b9\u516c\u5b50\u9053\uff1a\u201c\u7a77\u5bb6\u5e2e\u7684\u670b\u53cb\uff0c\u5c31\u732e\u51fa\u4e24\u624b\u6765\u7ed9\u5929\u4e0b\u7fa4\u96c4\u8fc7\u8fc7\u76ee\u5427\uff01\u201d \u7aef\u65b9\u516c\u5b50\u8bdd\u58f0\u624d\u843d\uff0c\u201c\u568e\uff01\u64e6\uff01\u568e\uff01\u2026\u2026\u201d\u4e00\u8fde\u7247\u5fae\u54cd\uff0c\u5239\u65f6\u95f4\u4eae\u8d77\u6570\u5341\u652f\u706b\u647a\u5b50\uff01 \u539f\u6765\u7a77\u5bb6\u5e2e\u7684\u5e2e\u4f17\uff0c\u6bcf\u4eba\u624b\u4e2d\u70b9\u71c3\u4e86\u4e00\u652f\u201c\u5343\u91cc\u706b\u201d\uff01 \u8fd9\u79cd\u2018\u2018\u5343\u91cc\u706b\u2019\u2019\u4e3a\u7a77\u5bb6\u5e2e\u72ec\u6709\u4e4b\u7269\uff0c\u4e0d\u6015\u98ce\uff0c\u4e0d\u6015\u96e8\uff0c\u800c\u4e14\u53ef\u5e38\u71c3\u4e0d\u7184\uff01 \u6570\u5341\u652f\u706b\u69b4\u5b50\u4e00\u4eae\uff0c\u6574\u5ea7\u5927\u5385\u5239\u65f6\u53d8\u5f97\u901a\u660e\uff01 \u5c31\u5728\u6574\u5ea7\u5927\u5385\u201c\u9686\u9686\u201d\u65cb\u8f6c\u3001\u60a0\u60a0\u4e0b\u6c89\u4e4b\u9645\uff0c\u7a77\u5bb6\u5e2e\u6570\u5341\u540d\u9ad8\u624b\uff0c\u6280\u51fa\u9752\u7af9\u6756\uff0c\u4ea4\u9519\u6e38 \u8d70\uff0c\u7adf\u7136\u5f62\u6210\u4e86\u4e00\u5ea7\u201c\u9752\u7af9\u5927\u9635\u201d\u628a\u7965\u9e9f\u516c\u5b50\u5144\u59b9\u5305\u56f4\u5728\u4e2d\u95f4\uff01 \u201c\u9752\u7af9\u5927\u9635\u201d\uff0c\u4ee5\u7a77\u5bb6\u5e2e\u7684\u201c\u98ce\u5c18\u4e09\u4e10\u201d\u4e3a\u9996\uff0c\u6b63\u597d\u662f\u4e5d\u4e5d\u516b\u5341\u4e00\u4eba\uff0c\u6697\u5408\u4e5d\u4e5d\u4e0d\u5c3d\u4e4b \u6570\uff0c\u6bcf\u4eba\u624b\u4e2d\u4e00\u652f\u9752\u7af9\u6756\uff0c\u6756\u8d77\u5982\u6797\uff0c\u53ea\u542c\u201c\u9152\u4e10\u201d\u65b9\u7ca5\u9886\u5934\u5531\u9053\uff1a\u201c\u5343\u6735\u83b2\u82b1\u8fce\u98ce\u5f00\uff01\u201d \u201c\u75af\u4e10\u201d\u891a\u826f\u63a5\u5531\u9053\uff1a\u201c\u897f\u5929\u4f5b\u7956\u4e0b\u51e1\u6765\uff01\u201d \u201c\u6765\u2019\u2019\u5b57\u51fa\u53e3\uff0c\u6f2b\u5929\u6756\u5f71\uff0c\u5982\u72c2\u98ce\u9aa4\u96e8\uff0c\u731b\u5411\u7965\u9e9f\u516c\u5b50\u7f69\u843d\uff01 \u201c\u94c1\u7ffc\u98de\u9e4f\u201d\u5df4\u5929\u8d6b\u4e0e\u201c\u94c1\u80cc\u9a7c\u9f99\u201d\u516c\u5b59\u695a\uff0c\u65e9\u5df2\u9886\u6559\u8fc7\u7a77\u5bb6\u5e2e\u201c\u53eb\u5316\u5927\u9635\u201d\u7684\u5389\u5bb3\uff0c \u89c1\u5bf9\u65b9\u9635\u5f0f\u53d1\u52a8\uff0c\u6bd4\u5728\u5ca9\u5c71\u5341\u4e8c\u6d1e\u90a3\u4e00\u6b21\u66f4\u52a0\u731b\u70c8\uff0c\u4e0d\u7531\u9f50\u58f0\u5389\u543c\uff0c\u638c\u7ffc\u731b\u6325\uff0c\u786c\u5411\u6765\u52bf\u8fce \u51fb\uff01 \u540c\u65f6\uff0c\u53e6\u5916\u5341\u6570\u540d\u91d1\u5e9c\u9ad8\u624b\uff0c\u4e5f\u968f\u7740\u201c\u91d1\u5e9c\u53cc\u94c1\u536b\u201d\u4e00\u9f50\u51fa \u94c1\u7ffc\u638c\u98ce\uff0c\u5200\u5149\u5251\u5f71\uff0c\u5a01\u52bf\u56fa\u7136\u4e0d\u5c0f\uff0c\u4f46\u4e0e\u6f2b\u7a7a\u538b\u6765\u7684\u9752\u7af9\u6756\u5f71\u4e00\u63a5\uff0c\u7acb\u523b\u7eb7\u7eb7\u60ca\u547c\u5012 \u9000\uff01 \u539f\u6765\u7aef\u65b9\u516c\u5b50\u5df2\u5c3d\u51fa\u201c\u7a77\u5bb6\u5e2e\u201d\u76d6\u4e16\u9ad8\u624b\uff0c\u52a0\u4e0a\u201c\u53eb\u5316\u5927\u9635\u201d\u7684\u5965\u5999\u65e0\u65b9\uff0c\u5a01\u529b\u7adf\u662f\u5927 \u5f97\u51fa\u5947\uff0c\u201c\u91d1\u5e9c\u53cc\u5feb\u536b\u201d\u4ee5\u53ca\u91d1\u5e9c\u98df\u5ba2\u4e2d\u7684\u5341\u6570\u9ad8\u624b\uff0c\u56fa\u7136\u90fd\u662f\u5f53\u4eca\u6b66\u6797\u4e00\u65f6\u4e4b\u9009\uff0c\u4f46\u8981\u62ff \u6765\u62b5\u6321\u7a77\u5bb6\u5e2e\u7684\u5947\u5999\u9635\u5f0f\uff0c\u4ecd\u7136\u662f\u65e0\u6cd5\u6297\u8861\uff01 \u7a77\u5bb6\u5e2e\u9635\u5f0f\u4e00\u53d1\u52a8\uff0c\u7acb\u628a\u91d1\u5e9c\u4f17\u9ad8\u624b\u8feb\u9000\uff0c\u53c8\u5728\u201c\u83b2\u82b1\uff0c\u83b2\u82b1\u2026\u2026\u201d\u7fa4\u58f0\u9f50\u5531\u4e4b\u4e2d\uff0c\u6797 \u6797\u603b\u603b\u7684\u80b2\u7af9\u6756\u5f71\u4e00\u9635\u6405\u52a8\uff0c\u72b9\u5982\u6012\u6d9b\u62cd\u5cb8\u4e00\u822c\uff0c\u4ee5\u65e0\u6bd4\u4f18\u52bf\u5411\u524d\u538b\u5230\uff01 \u9752\u7af9\u6756\u7eb7\u843d\u5982\u96e8\uff0c\u58f0\u58f0\u60e8\u66dd\u4f20\u51fa\uff0c\u91d1\u5e9c\u9ad8\u624b\u5df2\u6709\u591a\u4eba\u60e8\u6bd9\u6756\u4e0b\uff01 \u7965\u9e9f\u516c\u5b50\u9762\u8272\u60e8\u53d8\uff0c\u5251\u7709\u659c\u7acb\uff0c\u6012\u58f0\u53f1C\u9053\uff1a\u201c\u7a77\u5bb6\u5e2e\u7684\u670b\u53cb\uff0c\u6b3a\u4eba\u592a\u751a\uff0c\u5f53\u7740\u5929\u4e0b\u6b66 \u6797\uff0c\u6028\u4e0d\u5f97\u7965\u9e9f\u5fc3\u72e0\u624b\u8fa3\uff0c\u63a5\u6697\u5668\uff01\u201d\u7965\u9e9f\u516c\u5b50\u8bf4\u8bdd\u4e4b\u4e2d\uff0c\u53f3\u624b\u6f2b\u7a7a\u4e00\u626c\uff0c\u7acb\u6709\u58eb\u9635\u5578\u98ce\u4e4b \u58f0\uff0c\u75be\u5578\u800c\u51fa\uff01 \u6570\u5341\u70b9\u706b\u5149\u4e4b\u4e2d\uff0c\u53ea\u89c1\u4e00\u7247\u9752\u8499\u8499\u7684\u9ed1\u5f71\uff0c\u72b9\u5982\u4e07\u86a8\u9f50\u98de\uff0c\u53ea\u5411\u7a77\u5bb6\u5e2e\u4f17\u4e4b\u4e2d\u6253\u53bb\uff01 \u7adf\u662f\u201c\u9752\u86a8\u795e\u201d\u6210\u540d\u6697\u5668\u201c\u9752\u86a8\u9556\u201d\uff0c\u7965\u9e9f\u516c\u5b50\u4ee5\u201c\u4e07\u86a8\u5448\u6837\u201d\u624b\u6cd5\u6253\u51fa\uff01 \u201c\u75af\u4fa0\u201d\u891a\u826f\u563b\u563b\u602a\u7b11\uff0c\u9053\uff1a\u201c\u5230\u5e95\u662f\u516c\u5b50\u7237\u5927\u65b9\uff0c\u4e00\u51fa\u4e4e\u5c31\u662f\u6ee1\u628a\u91d1\u94b1\uff01\u201d \u4ed6\u5634\u91cc\u8f7b\u677e\uff0c\u5fc3\u4e2d\u4e5f\u4e3a\u8fd9\u91d1\u5bb6\u72ec\u95e8\u6697\u5668\u624b\u6cd5\u6697\u6697\u5403\u60ca\uff0c\u89c1\u6f2b\u7a7a\u9752\u5f71\uff0c\u75be\u5578\u800c\u81f3\uff0c\u7acb\u523b\u811a \u776c\u4e5d\u4e5d\uff0c\u50ac\u52a8\u9635\u5f0f\uff0c\u6025\u907d\u6325\u8d77\u9752\u7af9\u6756\uff0c\u5343\u767e\u6761\u9752\u7af9\u6756\u5f71\uff0c\u5e7b\u5316\u6210\u4e00\u9053\u9752\u8272\u7af9\u5899\uff0c\u4ee5\u5c01\u4f4f\u9635\u811a \u524d\u9762\uff01 \u201c\u53ee\uff01\u53ee\uff01\u201d\u4e00\u7247\u4e89\u9e23\uff0c\u5939\u7740\u4e1d\u4e1d\u7834\u98ce\u4e4b\u58f0\u8def\u9762\u800c\u8fc7\uff0c\u63a5\u7740\u53c8\u4f20\u51fa\u6570\u58f0\u95f7\u54fcI \u767e\u5fd9\u4e2d\u201c\u75af\u4e10\u201d\u56de\u5934\u4e00\u770b\uff0c\u8eab\u540e\u5e2e\u4f17\uff0c\u5df2\u6709\u4e0d\u5c11\u53d7\u4f24\uff01 \u5c31\u8fde\u6328\u80a9\u7ad9\u7acb\u7684\u201c\u804b\u4e10\u201d\u5434\u5316\uff0c\u5de6\u8111\u4e5f\u73b0\u51fa\u4e00\u9053\u8840\u75d5\uff0c\u6ef4\u6ef4\u9c9c\u8840\uff0c\u987a\u816e\u800c\u4e0b\uff01 \u201c\u804b\u4e10\u201d\u529f\u529b\u5e76\u4e0d\u5728\u201c\u9152\u201d\u3001\u201c\u75af\u201d\u4e8c\u4e10\u4e4b\u4e0b\uff0c\u4f46\u7531\u4e8e\u53cc\u8033\u5931\u806a\uff0c\u5bf9\u654c\u8fc7\u62db\uff0c\u5168\u51ed\u8d85\u4eba \u7684\u76ee\u529b\uff0c\u53ef\u662f\u5728\u8fd9\u9ed1\u6697\u4e4b\u4e2d\uff0c\u5168\u51ed\u6570\u5341\u652f\u706b\u647a\u5b50\u7684\u5fae\u5149\u7167\u4eae\uff0c\u53c8\u52a0\u4e0a\u4eba\u5f71\u4ea4\u9519\uff0c\u662f\u4ee5\u770b\u4e0d\u6e05 \u6ee1\u7a7a\u800c\u81f3\u7684\u201c\u9752\u86a8\u9556\u201d\uff0c\u52a0\u4e0a\u201c\u9752\u86a8\u9556\u201d\u7684\u4e0e\u4f17\u4e0d\u540c\uff0c\u5728\u7965\u9e9f\u516c\u5b50\u7279\u6b8a\u7684\u624b\u6cd5\u6253\u51fa\u4e4b\u9645\uff0c\u659c \u98de\u6a2a\u63a0\uff0c\u4e00\u4e2a\u8eb2\u907f\u4e0d\u53ca\uff0c\u8138\u4e0a\u4e00\u51c9\uff0c\u5df2\u77e5\u81ea\u5df2\u8d1f\u4e86\u4f24\uff01 \u4ed6\u5de6\u624b\u62ff\u7740\u706b\u647a\u5b50\uff0c\u53f3\u624b\u6267\u7740\u9752\u7af9\u6756\uff0c\u7a7a\u4e0d\u51fa\u624b\u6765\uff0c\u6307\u8d77\u624b\u81c2\uff0c\u7528\u8863\u8896\u4e00\u62b9\uff0c\u89c1\u8863\u8896\u4e0a \u67d3\u6ee1\u9c9c\u8840\uff0c\u4e0d\u7531\u52c3\u7136\u5927\u6012\uff0c\u5389\u5578\u4e00\u58f0\uff0c\u50ac\u52a8\u9635\u5f0f\uff0c\u6325\u8d77\u9752\u7af9\u6756\uff0c\u5e26\u8d77\u4e00\u7247\u52b2\u5578\uff0c\u731b\u5411\u7965\u9e9f\u516c \u5b50\u6251\u53bb\uff01 \u53ef\u662f\u7965\u9e9f\u516c\u5b50\u5de6\u624b\u4e00\u626c\uff0c\u53c8\u662f\u4e00\u84ec\u9752\u5f71\uff0c\u75be\u5578\u800c\u51fa\uff01\u201c\u804b\u4e10\u201d\u5927\u60ca\uff0c\u811a\u6b65\u989d\u632b\uff0c\u9635\u5f0f\u6536 \u4f4f\uff0c\u6539\u653b\u4e3a \u53ef\u662f\uff0c\u5728\u201c\u53ee\u53ee\u201d\u58f0\u4e2d\uff0c\u4ed6\u9752\u7af9\u6756\u4e0a\u63a5\u8fde\u5d4c\u5165\u5341\u6570\u6821\u9752\u94dc\u5236\u94b1\uff0c\u800c\u5728\u4ed6\u8eab\u65c1\u7684\u5e2e\u4f17\uff0c\u53c8 \u6709\u4e0d\u5c11\u4eba\u53d1\u51fa\u95f7\u54fc\uff0c\u4e2d\u94b1\u6302\u5f69\uff01 \u4f46\u201c\u53eb\u5316\u5927\u9635\u201d\u53d8\u5316\u5965\u5999\uff0c\u66f2\u6298\u8fdb\u9000\u6709\u5982\u7075\u86c7\uff0c\u5728\u201c\u804b\u4e10\u201d\u9635\u4f4d\u90e8\u4efd\u987f\u8db3\u540e\u64a4\u4e4b\u9645\uff0c \u201c\u9152\u4e10\u201d\u4e0e\u201c\u75af\u4e10\u201d\u6240\u7387\u9886\u4e4b\u90e8\u4efd\uff0c\u5374\u5df2\u5e94\u65f6\u653b\u51fa\uff0c\u662f\u4ee5\u7965\u9e9f\u516c\u5b50\u8fd9\u7b2c\u4e8c\u628a\u201c\u9752\u86a8\u9556\u201d\u51fa \u624b\uff0c\u628a\u201c\u804b\u4e10\u201d\u6253\u9000\u65f6\uff0c\u201c\u9152\u201d\u3001\u201c\u75af\u201d\u4e8c\u4e10\uff0c\u50ac\u52a8\u9635\u5f0f\uff0c\u5df2\u7531\u4fa7\u7ffc\u5377\u81f3\uff01 \u6570\u58f0\u60e8\u55e5\uff0c\u91d1\u5e9c\u9ad8\u624b\u53c8\u6709\u6570\u4eba\u6bd9\u547d\uff0c\u800c\u4e14\u6570\u5341\u6761\u9752\u7af9\u6756\u5f71\uff0c\u8fa3\u75be\u98ce\u8fc5\u96f7\u4e4b\u52bf\uff0c\u731b\u5411\u7965\u9e9f \u516c\u5b50\u5144\u59b9\u5468\u8eab\u70b9\u4e0b\uff01 \u7965\u9e9f\u516c\u5b50\u9762\u5bb9\u5931\u8272\uff0c\u91d1\u5f69\u51e4\u5931\u58f0\u60ca\u547c\uff0c\u770b\u6837\u5b50\u5144\u59b9\u4e24\u4eba\u96be\u9003\u80b2\u7af9\u6756\u4e71\u68d2\u4e4b\u5371\u2026\u2026 \u5c55\u767d\u4e5f\u4e0d\u77e5\u662f\u600e\u6837\u4e00\u79cd\u5fc3\u7406\uff0c\u53ea\u89c9\u5f97\u4e0d\u5fcd\u89c1\u90a3\u503e\u56fd\u503e\u57ce\u7684\u7edd\u4e16\u7f8e\u5973\uff0c\u5a49\u8f6c\u86fe\u7709\uff0c\u60e8\u6b7b\u4e71 \u6756\u4e4b\u4e0b\uff0c\u7adf\u7136\u4e0d\u7531\u81ea\u5df1\u5730\u817e\u8eab\u800c\u8d77\uff0c\u534a\u7a7a\u4e2d\u201c\u545b\u201d\u7136\u4e00\u58f0\u9f99\u541f\uff0c\u201c\u65e0\u60c5\u78a7\u5228\u201d\u62bd\u51fa\u9798\u6765\uff0c\u4e00 \u5f0f\u201c\u8fc5\u98ce\u75be\u96f7\u201d\u72b9\u5982\u957f\u8679\u7ecf\u5929\uff0c\u8fde\u4eba\u5e26\u5251\uff0c\u731b\u5411\u91d1\u5f69\u51e4\u8eab\u524d\u4e71\u68d2\u4e4b\u4e2d\u6321\u53bb\uff01 \u201c\u53ee\uff01\u545b\uff01\u53ee\u2026\uff0c\uff0e\uff0e\u201d \u4e00\u7247\u91d1\u97f3\u5fae\u9e23\uff0c\u9752\u7af9\u6756\u88ab\u5c55\u767d\u201c\u65e0\u60c5\u78a7\u5251\u201d\u5251\u950b\uff0c\u4e00\u4e0b\u5b50\u524a\u65ad\u4e86\u5341\u6570\u6839\uff01 \u7a77\u5bb6\u5e2e\u4f17\u201c\u7231\u6756\u903e\u547d\u201d\uff0c\u4e00\u7247\u60ca\u547c\u58f0\u4e2d\uff0c\u66b4\u7136\u9f50\u9000\uff01\u2026\u2026 \u91d1\u5f69\u51e4\u4fca\u7f8e\u65e0\u6bd4\u7684\u5a07\u5c4a\u4e0a\uff0c\u4e00\u7247\u60ca\u6050\u4e4b\u8272\uff0c\u4f46\u4e00\u53cc\u660e\u4eae\u7684\u5927\u773c\u775b\u4e2d\uff0c\u5374\u5145\u6ee1\u4e86\u611f\u6fc0\u4e4b \u60c5\uff0c\u671b\u7740\u5c55\u767d\u3002\u3002\u3002\u3002r \u5c55\u767d\u4ece\u90a3\u773c\u795e\u4e2d\uff0c\u671b\u5230\u4e86\u4f3c\u6d77\u7684\u6df1\u60c5\uff0c\u4fca\u9762\u4e00\u9635\u53d1\u70e7\uff0c\u53c8\u89c1\u4f17\u4eba\u90fd\u7528\u4e00\u79cd\u8be7\u5f02\u7684\u773c\u5149\u671b \u7740\u4ed6\uff0c\u4f3c\u662f\u8d23\u602a\u4ed6\u7684\u591a\u6b64\u4e00\u4e3e\uff0c\u4ed6\u53c8\u4e0d\u7531\u4e00\u9635\u7f9e\u60ed\u5730\u4f4e\u4e0b\u5934\u53bb\u2026\u2026 \u53ef\u662f\uff0c\u5c31\u5728\u8fd9\u7535\u5149\u77f3\u706b\u7684\u4e00\u5239\u90a3\uff0c\u5ffd\u542c\u53c8\u662f\u4e00\u9635\u6025\u5578\u7834\u98ce\u4e4b\u58f0\u4f20\u6765\uff0c\u4f17\u4eba\u60ca\u714c\u56db\u987e\uff0c\u4e00 \u84ec\u9752\u5f71\uff0c\u5374\u4ece\u5c4b\u9876\uff0c\u8fce\u5934\u6d12\u4e0b\uff01 \u72b9\u5982\u590f\u65e5\u9aa4\u7136\u6025\u964d\u7684\u51b0\u96f9\uff01\u7ade\u4e0d\u77e5\u662f\u8c01\uff0c\u7ee7\u7965\u9e9f\u516c\u5b50\u4e4b\u540e\uff0c\u7531\u5c4b\u9876\u6d12\u4e0b\u4e00\u628a\u201c\u9752\u86a8\u9556\u201d \u6765\uff01\u4e0d\u7531\u4e00\u9f50\u60ca\u559d\u51fa\u58f0\uff01 \u5c31\u5728\u4f17\u4eba\u60ca\u547c\u56db\u8eb2\u4e4b\u9645\uff0c\u4ecd\u6709\u4e0d\u5c11\u4eba\u8d1f\u4e86\u4f24\uff0c\u8fd9\u6b21\u5df2\u4e0d\u4ec5\u662f\u7a77\u5bb6\u5e2e\u7684\u4eba\uff0c\u800c\u662f\u5404\u95e8\u5404\u6d3e \u624b\u4e0b\u90fd\u6709\u4eba\u8d1f\u4f24\uff0c\u4e0d\u7531\u4e00\u4e2a\u4e2a\u6012\u6c14\u586b\u81ba\uff0c\u9f50\u6b32\u627e\u7965\u9e9f\u516c\u5b50\u62fc\u547d\u2026\u2026 \u53ef\u662f\uff0c\u8fd9\u6ee1\u7a7a\u6d12\u843d\u7684\u201c\u9752\u86a8\u9556\u201d\uff0c\u624b\u6cd5\u9ad8\u5999\u65e0\u6bd4\uff0c\u4e0d\u4f46\u6253\u4f24\u4e86\u4e0d\u5c11\u4eba\uff0c\u800c\u4e14\u628a\u7a77\u5bb6\u5e2e\u4f17 \u624b\u4e2d\u7684\u706b\u647a\u5b50\u4e00\u5f8b\u6253\u7184\uff01 \u5927\u5385\u4e4b\u4e2d\uff0c\u7115\u7136\u6f06\u9ed1\u4e0d\u89c1\u4e94\u6307\uff01\u5c31\u8fde\u5c55\u767d\u591c\u80fd\u89c6\u7269\u7684\u76ee\u529b\uff0c\u7531\u4e8e\u4e4d\u7136\u4ece\u660e\u5230\u6697\uff0c\u4e5f\u4e0d\u80fd \u770b\u6e05\u5468\u906d\u666f\u7269\uff01 \u7a81\u7136\uff0c\u5c4b\u9876\u53c8\u4f20\u6765\u4e00\u58f0\u82cd\u8001\u7684\u7b11\u58f0\uff0c\u7b11\u7f62\u8bf4\u9053\uff1a\u73b0\u5728\u4f60\u4eec\u90fd\u5df2\u843d\u5165\u8001\u592b\u638c\u4e2d\uff0c\u4e3a\u53cb\u4e3a \u654c\uff1f\u8bf7\u8bf8\u4f4d\u81ea\u62e9\uff0c\u613f\u505a\u91d1\u5e9c\u7684\u670b\u53cb\uff0c\u8bf7\u4e8b\u5148\u58f0\u660e\uff0c\u5f53\u4ee5\u2018\u4f20\u97f3\u5165\u5bc6\u2019\u4e4b\u6cd5\uff0c\u544a\u77e5\u8bf8\u4f4d\u51fa\u8def\uff0c \u5982\u679c\u84c4\u5fc3\u4e0e\u91d1\u5e9c\u4e3a\u654c\uff0c\u90a3\u5c31\u8981\u51ed\u5404\u4f4d\u81ea\u5df1\u7684\u672c\u4e8b\u4e86\uff0c\u80fd\u591f\u8d70\u51fa\u8fd9\u2018\u77f3\u77fe\u5927\u9635\u2019\uff0c\u91d1\u5e9c\u4e4b\u4eba\uff0c \u7edd\u4e0d\u518d\u52a0\u963b\u62e6\uff0c\u4efb\u51ed\u8bf8\u4f4d\u81ea\u53bb\u2026\u2026\u201d \u4ed6\u8bdd\u5c1a\u672a\u8bf4\u5b8c\uff0c\u5df2\u4e3a\u4f17\u4eba\u4e03\u5634\u516b\u820c\u7684\u6012\u53f1\u58f0\u6253\u65ad\u3002 \u53ea\u542c\u201c\u6df7\u5143\u6307\u201d\u53f8\u7a7a\u664b\u53eb\u9053\uff1a\u201c\u8001\u4e8c\uff01\u4f60\u8fd9\u6837\u505a\uff0c\u7b97\u662f\u5bf9\u5f85\u6545\u53cb\u4e4b\u9053\u5417\uff1f\u201d \u201c\u9752\u86a8\u795e\u201d\u91d1\u4e5d\u5728\u5c4b\u9876\u5916\u8fb9\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u53f8\u7a7a\u664b\uff0c\u5929\u4e0b\u4e4b\u5927\uff0c\u6050\u6015\u518d\u627e\u4e0d\u51fa\u50cf\u4f60\u8fd9\u6837 \u53cd\u8986\u9634\u9669\u7684\u5c0f\u4eba\u4e86\uff01\u4f60\u7528\u5047\u7684\u2018\u5927\u7f57\u53c2\u4e39\u2019\u5bb3\u5f97\u6211\u53cc\u817f\u5931\u7075\uff0c\u53c8\u9a71\u4f7f\u7a77\u5bb6\u5e2e\u7684\u2018\u907f\u6c34\u7389 \u74a7\u2019\uff0c\u4f60\u8fd9\u4e0d\u662f\u6bd4\u8981\u6211\u6b7b\u8fd8\u66f4\u5389\u5bb3\u5417\uff1f\u5230\u4e86\u73b0\u5728\u8fd8\u8c08\u4ec0\u4e48\u6545\u4eba\u4e0d\u6545\u4eba\uff1f\u8001\u5b9e\u8bf4\u7f62\uff0c\u4eca\u5929\u4e3b\u8981 \u5c31\u662f\u4e3a\u4e86\u5bf9\u4ed8\u4f60\uff0c\u522b\u4eba\u9677\u8eab\u2018\u77f3\u77fe\u5e7d\u51a5\u2019\uff0c\u4e27\u8eab\u5730\u5e95\uff0c\u53ef\u8bf4\u662f\u6cbe\u4e86\u4f60\u7684\u5149\u2026\u2026\u201d \u4f17\u4eba\u4e00\u542c\uff0c\u66f4\u662f\u6012\u4e0d\u53ef\u904f\uff0c\u539f\u6765\u4ed6\u4eec\u4e24\u5bb6\u4eba\u52fe\u5fc3\u6597\u89d2\uff0c\u5374\u9677\u5bb3\u4e86\u8bb8\u591a\u65e0\u8f9c\u4e4b\u4eba\uff01\u2026\u2026 \u53c8\u542c\u201c\u4e7e\u5764\u638c\u201d\u4e91\u5b97\u9f99\u8bf4\u9053\uff1a\u201c\u4e09\u54e5\uff01\u4f60\u603b\u4e0d\u80fd\u8fde\u6211\u4e5f\u5bb3\u5728\u91cc\u8fb9\u5427\uff1f\u60f3\u54b1\u4eec\u5f53\u521d\u60c5\u540c\u751f \u6b7b\u2026\u2026\u201d \u201c\u9752\u86a8\u795e\u201d\u91d1\u4e5d\u5728\u5c4b\u9876\u5916\uff0c\u54c8\u54c8\u72c2\u7b11\uff0c\u9053\uff1a\u201c\u54b1\u4eec\u5144\u5f1f\uff1f\u54c8\u54c8\uff01\u90a3\u5728\u5341\u51e0\u5e74\u524d\u65e9\u5df2\u7ea6\u675f \u4e86\uff0c\u800c\u4e14\u6211\u4eec\u4e5f\u66fe\u53d1\u8fc7\u8a93\uff0c\u4ece\u90a3\u6bb5\u516c\u6848\u540e\uff0c\u54b1\u4eec\u662f\u8c01\u4e5f\u4e0d\u7406\u8c01\uff0c\u8fc7\u53bb\u7684\u4e00\u7b14\u52fe\u9500\uff01\u60f3\u4e0d\u5230\u5371 \u96be\u5f53\u5934\u53c8\u53eb\u8d77\u4e8c\u54e5\u4e86\uff01\u6211\u518d\u8001\u5b9e\u544a\u8bc9\u4f60\u5427\uff01\u4eca\u5929\uff0c\u4efb\u4f55\u4eba\u90fd\u53ef\u4ee5\u653e\u5f00\uff0c\u552f\u72ec\u4f60\u4eec\u51e0\u4e2a\u4eba\u4e0d\u80fd \u9976\u8fc7\uff01\u2026\u2026\u201d \u201c\u9738\u738b\u97ad\u201d\u6a0a\u975e\u5bf9\u201c\u4e7e\u5764\u638c\u201d\u4e91\u5b97\u9f99\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u56db\u54e5\uff01\u4e0d\u7528\u591a\u8ddf\u4ed6\u8d39\u5507\u820c\uff01 \u201c\u60f3\u5f53\u521d\u4ed6\u8bbe\u8ba1\u6740\u5bb3\u5927\u54e5\u65f6\uff0c\u662f\u591a\u4e48\u6b8b\u9177\u65e0\u60c5\uff1f\u4eca\u65e5\u5df2\u9677\u5728\u4ed6\u7684\u673a\u5173\u4e4b\u4e2d\uff0c\u518d\u8bb2\u597d\u8bdd\u4e5f \u662f\u65e0\u7528\uff0c\u4e0d\u5982\u2026\u2026\u201d \u8bf4\u5230\u8fd9\u91cc\uff0c\u58f0\u97f3\u66f4\u4f4e\uff0c\u4f46\u5c55\u767d\u5947\u7ecf\u516b\u8109\u5df2\u901a\uff0c\u542c\u89c9\u7075\u654f\uff0c\u4f9d\u7a00\u80fd\u591f\u542c\u5230\u201c\u9738\u738b\u97ad\u201d\u8bf4 \u9053\uff1a\u201c\u2026\u3002\u5236\u4f4f\u8001\u9b3c\u72ac\u5b50\uff0c\u4ee5\u4f7f\u5176\u5c31\u8303\u2026\u2026\u201d \u4f46\u4ea6\u4e0d\u80fd\u5b8c\u5168\u542c\u6e05\u695a\uff0c\u53ef\u662f\u7531\u6b64\u4e5f\u5fd6\u5ea6\u51fa\u201c\u9738\u738b\u97ad\u201d\u6a0a\u975e\uff0c\u662f\u60f3\u631f\u5236\u7965\u9e9f\u516c\u5b50\uff0c\u4ee5\u6c42\u8981 \u631f\u201c\u9752\u86a8\u795e\u201d\u501f\u4ee5\u8131\u56f0\uff01\u2026\u2026 \u540c\u65f6\uff0c\u5c55\u767d\u4ece\u5176\u5bf9\u8bdd\u4e4b\u4e2d\uff0c\u4ea6\u53ef\u542c\u51fa\u5f53\u521d\u4ed6\u4eec\u51e0\u4eba\u9677\u5bb3\u7236\u4eb2\u4e4b\u65f6\uff0c\u5b9a\u7136\u4e5f\u662f\u7531\u8fd9\u201c\u9752\u86a8 \u795e\u201d\u91d1\u4e5d\u51fa\u8ba1\uff0c\u7136\u540e\u516d\u4eba\u8054\u624b\u628a\u7236\u4eb2\u6740\u5bb3\uff0c\u74dc\u5206\u5b9d\u7269\uff0c\u6563\u4f19\u800c\u53bb\u2026\u2026 \u5c55\u767d\u60f3\u5230\u8fd9\u91cc\uff0c\u7a81\u611f\u70ed\u8840\u4e0a\u51b2\uff0c\u5f88\u4e0d\u5f97\u7acb\u523b\u627e\u5230\u201c\u9752\u86a8\u795e\u201d\u91d1\u4e5d\uff0c\u4e3a\u7236\u62a5\u4ec7\u2026\u2026\u2606\u7a81\u7136 \u5c55\u767d\u611f\u5230\u4e00\u53ea\u6e29\u6696\u6ed1\u817b\u7684\u624b\u63e1\u4f4f\u4ed6\u7684\u624b\uff0c\u540c\u65f6\u4e00\u80a1\u4f3c\u5170\u4f3c\u9e9d\u7684\u5e7d\u9999\u4f20\u8fdb\u4e86\u4ed6\u7684\u9f3b\u5b54\uff0c\u5c1a\u6ca1\u7b49 \u4ed6\u4f1a\u610f\uff0c\u90a3\u6e29\u6696\u7684\u624b\u5df2\u62c9\u7740\u4ed6\u5411\u4e00\u4fa7\u8d70\u53bb\u3002 \u5c55\u767d\u5168\u8eab\u5982\u53d7\u7535\u63a3\uff0c\u53ea\u611f\u4e00\u822c\u6696\u6d41\uff0c\u7531\u624b\u4e0a\u901a\u8fc7\u5168\u8eab\u6bcf\u4e00\u6839\u795e\u7ecf\uff0c\u9ed1\u6697\u4e4b\u4e2d\uff0c\u4e5f\u4e0d\u77e5\u662f \u654c\u662f\u53cb\uff1f\u4ed6\u7ade\u5fd8\u8bb0\u4e86\u6297\u62d2\uff0c\u968f\u7740\u90a3\u62c9\u7740\u4ed6\u7684\u6e29\u6ed1\u624b\u638c\u8d70\u53bb\u3002 \u5c55\u767d\u5728\u90a3\u6e29\u6ed1\u5c0f\u624b\u7684\u7275\u5f15\u4e0b\uff0c\u8d70\u8fdb\u4e00\u9053\u77f3\u7f1d\u4f3c\u7684\u7a84\u95e8\uff0c\u63a5\u7740\u51e0\u4e2a\u8f6c\u5f2f\uff0c\u4f3c\u662f\u8fdb\u4e86\u4e00\u6761\u7a84 \u7a84\u7684\u5730\u4e0b\u752c\u9053\uff0c\u811a\u4e0b\u5df2\u4e0d\u518d\u8f6c\u52a8\uff0c\u800c\u4e14\u811a\u8e29\u77f3\u7ea7\u6b65\u6b65\u5411\u4e0a\uff0c\u663e\u7136\u5df2\u8131\u51fa\u201c\u77f3\u77fe\u5927\u9635\u201d\u4e4b\u5916\u3002 \u752c\u9053\u4e4b\u5185\uff0c\u867d\u7136\u4ecd\u662f\u6f06\u9ed1\uff0c\u4f46\u65f6\u95f4\u4e00\u4e45\uff0c\u5c55\u767d\u5df2\u6062\u590d\u4e86\u591c\u89c6\u7684\u76ee\u529b\uff0c\u4ed6\u7ea6\u7565\u770b\u51fa\uff0c\u62c9\u4ed6 \u5411\u524d\u8d70\u7684\uff0c\u5782\u7740\u73e0\u7a57\u84ec\u677e\u53d1\u8fab\u7a33\u538b\u7ea4\u7ec6\u8170\u8eab\u7684\u80cc\u5f71\uff0c\u6b63\u662f\u90a3\u6709\u7740\u201c\u6c5f\u5357\u7b2c\u4e00\u7f8e\u4eba\u201d\u4e4b\u79f0\u7684\u91d1 \u5f69\u51e4\u3002 \u5c55\u767d\u5fc3\u601d\u7535\u8f6c\uff0c\u51e0\u6b21\u60f3\u6323\u8131\u5979\u7684\u638c\u63e1\uff0c\u4f46\u4e0d\u77e5\u4ec0\u4e48\u5fc3\u7406\u7ade\u672a\u6323\u8131\uff1f\u51e0\u6b21\u60f3\u5f00\u53e3\u95ee\u5979\uff0c\u8981 \u628a\u81ea\u5df2\u62c9\u5230\u54ea\u91cc\u53bb\uff0c\u5374\u53c8\u4e0d\u77e5\u4e3a\u4f55\u6ca1\u6709\u95ee\u51fa\u53e3\u6765\uff1f\u53ea\u5728\u795e\u601d\u8ff7\u60af\u4e2d\u4efb\u5979\u6e29\u6696\u7684\u7389\u638c\u62c9\u7740\u81ea\u5df1 \u8d70\u53bb \u4e5f\u4e0d\u77e5\u8d70\u4e86\u591a\u8fdc\uff1f\u4f9d\u65f6\u95f4\u8ba1\u7b97\uff0c\u53cd\u6b63\u8def\u9014\u4e0d\u8fd1\u5c31\u662f\u4e86\u3002\u5ffd\u542c\u201c\u7830\u201d\u7684\u4e00\u58f0\uff0c\u4f3c\u662f\u4e00\u6247\u77f3 \u95e8\u88ab\u63a8\u5f00\uff0c\u5c55\u767d\u968f\u7740\u91d1\u5f69\u51e4\u7389\u638c\u4e00\u62c9\u7684\u52b2\u513f\uff0c\u98d8\u8eab\u8dc3\u51fa\u752c\u9053\uff0c\u7ade\u6765\u5728\u4e00\u5ea7\u5047\u5c71\u7684\u77f3\u4ead\u4e4b\u4e2d\uff0c \u53ea\u89c1\u697c\u53f0\u4ead\u9601\uff0c\u82b1\u6728\u6276\u758f\uff0c\u5728\u5929\u5fc3\u4e00\u8f6e\u660e\u6708\u7167\u8000\u4e4b\u4e0b\uff0c\u5206\u660e\u662f\u4e00\u5ea7\u5e9e\u5927\u7684\u82b1\u56ed\uff01 \u800c\u4e14\u8fdc\u5904\u8857\u9053\u4e0a\u7684\u706f\u5149\uff0c\u5728\u6708\u591c\u7684\u5929\u7a7a\u6cdb\u8d77\u4e00\u7247\u8499\u8499\u7ea2\u5149\u3002 \u91d1\u5f69\u51e4\u7684\u7389\u989c\uff0c\u5728\u660e\u6708\u6e05\u8f89\u7684\u6620\u7167\u4e0b\uff0c\u5a07\u7f8e\u5982\u82b1\uff0c\u53ea\u89c1\u5979\u7693\u9f7f\u5fae\u9732\uff0c\u5ae3\u7136\u7b11\u9053\uff1a\u201c\u5e78\u4e8f \u6211\u77e5\u9053\u8fd9\u6761\u79d8\u9053\uff0c\u4e0d\u7136\u7684\u8bdd\uff0c\u6211\u4eec\u4e5f\u8981\u8ddf\u7740\u4ed6\u4eec\u6c38\u6c89\u5730\u5e95\u4e86\uff01\u201d\u5c55\u767d\u542c\u91d1\u5f69\u51e4\u76f4\u79f0\u201c\u6211 \u4eec\u201d\uff0c\u8fd9\u4eb2\u70ed\u7684\u79f0\u547c\uff0c\u4f7f\u4ed6\u5fc3\u5934\u2014\u751c\uff0c\u4f46\u77ac\u5373\u7591\u95ee\u9053\uff1a\u201c\u6c38\u6c89\u5730\u5e95\uff01\u96be\u9053\u90a3\u5ea7\u5927\u5385\u4e0d\u80fd\u518d\u5347 \u4e0a\u5730\u9762\u6765\u4e86\u5417\uff1f\u201d \u91d1\u5f69\u51e4\u4e00\u56de\u8eab\uff0c\u5a07\u8eaf\u73af\u65cb\u4e86\u4e00\u4e2a\u5bf9\u5706\uff0c\u5c31\u52bf\u659c\u501a\u5728\u77f3\u4ead\u7684\u680f\u4e8e\u4e0a\uff0c\u90a3\u59ff\u6001\u5a07\u7f8e\u5df2\u6781\uff0c\u4ee5 \u79c0\u76ee\u7765\u7768\u7740\u5c55\u767d\uff0c\u9053\uff1a\u201c\u6211\u4e0d\u8be6\u7ec6\u77e5\u9053\uff0c\u53ea\u5728\u5c0f\u65f6\u5019\u542c\u7239\u7239\u8bf4\uff0c\u8fd9\u5927\u5385\u662f\u4e00\u5ea7\u201c\u77f3\u77fe\u5927 \u9635\u2019\uff0c\u53ea\u8981\u89e6\u52a8\u673a\u5173\uff0c\u6c89\u4e0b\u5730\u5e95\uff0c\u4fbf\u6c38\u8fdc\u4e0d\u80fd\u4e0a\u6765\uff0c\u4e0d\u8bba\u6709\u591a\u9ad8\u5f3a\u7684\u6b66\u529f\uff0c\u4e5f\u8981\u88ab\u6d3b\u846c\u5728\u91cc \u9762\uff01\u2026\u2026\u201d \u5c55\u767d\u8fd9\u6b21\u5374\u6bd4\u8f83\u6e05\u9192\uff0c\u672b\u4e3a\u91d1\u5f69\u51e4\u5a07\u7f8e\u65e0\u6bd4\u7684\u59ff\u6001\u8ff7\u60af\uff0c\u4e0d\u7b49\u91d1\u5f69\u51e4\u7684\u8bdd\u8bf4\u5b8c\uff0c\u5373\u51b7\u7b11 \u9053\uff1a\u4f60\u8fd9\u8bdd\uff0c\u5927\u6982\u65e0\u4eba\u4f1a\u76f8\u4fe1\u5427\uff01\u201d \u91d1\u5f69\u51e4\u5a07\u8eaf\u4e00\u633a\uff0c\u79bb\u5f00\u680f\u5e72\uff0c\u51d1\u524d\u4e24\u6b65\uff0c\u7ef7\u8d77\u79c0\u8138\u9053\uff1a\u4f60\u2014\u2014\u4ee5\u4e3a\u6211\u9a97\u4f60\u5417\uff1f\u201d \u5c55\u767d\u54c8\u54c8\u7b11\u9053\uff1a\u4f60\u54e5\u54e5\uff0c\u8fd8\u6709\u91d1\u5e9c\u90a3\u4e48\u591a\u4eba\uff0c\u96be\u9053\u548c\u4e0e\u4f1a\u7fa4\u96c4\u4e5f\u4e00\u540c\u6d3b\u846c\u5417\uff1f\u201d \u91d1\u5f69\u51e4\u4e5f\u201c\u5657\u54e7\u201d\u7b11\u51fa\u58f0\u6765\u9053\uff1a\u201c\u6211\u54e5\u54e5\u5f53\u7136\u4e0d\u4f1a\u90a3\u4e48\u50bb\uff0c\u4ed6\u4e5f\u77e5\u9053\u901a\u5411\u5916\u9762\u7684\u79d8 \u9053\uff01\u201d \u5c55\u767d\u9053\uff1a\u53ef\u662f\uff0c\u4f60\u54e5\u54e5\u5e76\u6ca1\u6709\u968f\u7740\u6211\u4eec\u51fa\u6765\u2026\u2026\u201d\u5c55\u767d\u65e0\u610f\u4e2d\uff0c\u4e5f\u7528\u4e86\u201c\u6211\u4eec\u201d\u8fd9\u4e2a\u8f83 \u4eb2\u5207\u7684\u4ee3\u540d\u8bcd\uff0c\u4e0d\u7531\u8138\u5b54\u4e00\u9635\u70e7\uff0c\u5fc3\u5934\u72c2\u8df3\uff0c\u56e0\u6b64\uff0c\u8bdd\u8bf4\u4e86\u4e00\u534a\u5373\u884c\u54bd\u4f4f\u3002 \u91d1\u5f69\u51e4\u5374\u7b11\u5f97\u66f4\u707f\u7136\u4e86\uff0c\u53ea\u89c1\u5979\u5a07\u56fe\u5982\u6625\u82b1\u76db\u5f00\uff0c\u9053\uff1a\u201c\u8fd9\u2018\u77f3\u77f6\u5927\u9635\u2019\u4e2d\u79d8\u9053\u4e0d\u6b62\u4e00 \u6761\uff0c\u901a\u5f80\u5916\u8fb9\u7684\u8def\u4e5f\u4e0d\u6b62\u4e00\u5904\uff0c\u800c\u4e14\u5728\u9635\u52bf\u53d1\u52a8\u540e\uff0c\u6b65\u6570\u8d70\u4e0d\u5bf9\uff0c\u4e5f\u8e0f\u4e0d\u8fdb\u90a3\u5bbd\u4ec5\u5bb9\u4e00\u4eba\u53ef \u901a\u8fc7\u7684\u77f3\u7f1d\u2026\u2026\u201d \u5c55\u767d\u9887\u4e3a\u6c14\u6124\u5730\u8bf4\u9053\uff1a\u5728\u4e0b\u5012\u662f\u6ca1\u60f3\u5230\uff0c\u5a01\u540d\u9707\u6b66\u6797\u7684\u5802\u5802\u91d1\u5e9c\uff0c\u7adf\u7528\u51fa\u8fd9\u79cd\u6697\u7b97\u5751\u4eba \u7684\u4e0b\u6d41\u624b\u6bb5\uff01\u73b0\u5728\uff0c\u5c55\u767d\u7b97\u662f\u9886\u6559\u8fc7\u4e86\uff0c\u597d\uff0c\u9752\u5c71\u4e0d\u6539\uff0c\u54b1\u4eec\u540e\u4f1a\u6709\u671f\uff0c\u518d\u89c1\uff01\u201d \u8bf4\u7f62\uff0c\u4e5f\u4e0d\u7b49\u91d1\u5f69\u51e4\u56de\u7b54\uff0c\u6389\u5934\u5927\u8e0f\u6b65\u8d70\u53bb\u2026\u2026 \u91d1\u5f69\u51e4\u88ab\u5c55\u767d\u8bf4\u5f97\u82b1\u5bb9\u60e8\u53d8\uff0c\u4e00\u65f6\u6014\u4f4f\uff0c\u5f85\u5c55\u767d\u6389\u5934\u800c\u53bb\u65f6\uff0c\u5979\u5fc3\u5982\u5200\u7ede\uff0c\u731b\u7136\u201c\u554a\u201d \u7684\u4e00\u58f0\u8f7b\u557c\uff0c\u98de\u6251\u5411\u5c55\u767d\uff0c\u5634\u4e2d\u6025\u9053s\u201c\u4f60\u2014\u2014\u4e0d\u8981\u8d70\uff01\u2026\u2026\u201d \u5c55\u767d\u7a81\u611f\u8111\u540e\u52b2\u98ce\u7834\u7a7a\uff0c\u4ee5\u4e3a\u91d1\u5f69\u98ce\u7f9e\u607c\u6210\u6012\uff0c\u5728\u81ea\u5df2\u80cc\u540e\u51fa\u624b\u6697\u88ad\uff0c\u7acb\u5373\u7529\u80a9\u8de8\u6b65\uff0c \u53cd\u8eab\u6253\u51fa\u4e00\u638c\uff01\u5374\u89c1\u91d1\u5f69\u98ce\u4e0d\u95ea\u4e0d\u907f\uff0c\u53cc\u80a9\u9f50\u5f20\uff0c\u633a\u7740\u9165\u80f8\uff0c\u7eb5\u8eab\u5411\u4ed6\u6251\u81f3\uff01 \u5c55\u767d\u638c\u52bf\u5df2\u7ecf\u6467\u51fa\uff0c\u624d\u770b\u51fa\u91d1\u5f69\u51e4\u4e0d\u662f\u5411\u4ed6\u6697\u88ad\uff0c\u800c\u662f\u6251\u5411\u4ed6\u7684\u6000\u62b1\u6765\uff0c\u5c55\u767d\u7eb5\u662f\u94c1\u77f3 \u5fc3\u80a0\uff0c\u5bf9\u8fd9\u6beb\u4e0d\u62b5\u6297\u3001\u6295\u5411\u81ea\u5df2\u6000\u62b1\u7684\u5982\u82b1\u4f3c\u7389\u7684\u5973\u90ce\uff0c\u4e5f\u4e0d\u5fcd\u907d\u4e0b\u6bd2\u624b\uff0c\u8fde\u5fd9\u5438\u6c14\u5378\u529b\uff0c \u632b\u81c2\u6536\u7559\uff01 \u53ef\u662f\uff0c\u6162\u4e86\uff0c\u5c55\u767d\u62bd\u638c\u4e0d\u53ca\uff0c\u867d\u7136\u5378\u53bb\u5927\u90e8\u5206\u638c\u529b\uff0c\u4f46\u5c1a\u6709\u4e09\u5206\u529b\u9053\uff0c\u201c\u7830\u201d\u7684\u4e00\u58f0\uff0c \u5c55\u767d\u4e00\u638c\u6b63\u6b63\u5730\u5370\u5728\u91d1\u5f69\u98ce\u67d4\u5a07\u9999\u5ae9\u7684\u9165\u80f8\u4e4b\u4e0a\uff01 \u91d1\u5f69\u98ce\u5a07\u54fc\u4e00\u58f0\uff0c\u524d\u6251\u7684\u8eab\u5f62\u987f\u7136\u9707\u4f4f\uff0c\u4e00\u526f\u5a07\u8eaf\uff0c\u6447\u4e86\u51e0\u6447\uff0c\u4ef0\u9762\u5411\u540e\u5012\u53bb\u2026\u2026 \u7b2c\u4e09\u5341\u4e09\u7ae0\u3000\u72e0\u6bd2\u65e0\u5e38\u201c\u9752\u86a8\u795e\u201d>> \u53e4\u9f99\u300a\u5251\u5ba2\u884c\u300b \u7b2c\u4e09\u5341\u4e09\u7ae0\u3000\u72e0\u6bd2\u65e0\u5e38\u201c\u9752\u86a8\u795e\u201d \u5c55\u767d\u817e\u8eab\u63a2\u81c2\uff0c\u9002\u65f6\u6276\u4f4f\u6b32\u5012\u7684\u91d1\u5f69\u51e4\uff0c\u6696\u7389\u6e29\u9999\uff0c\u62b1\u4e86\u4e2a\u6ee1\u6000\uff0c\u867d\u662f\u714c\u6025\u4e4b\u4e2d\u65e0\u5fc3\u4e4b \u4e3e\uff0c\u5c55\u767d\u4e5f\u7981\u4e0d\u4f4f\u5fc3\u5934\u731b\u8df3\u3002 \u53c8\u89c1\u91d1\u5f69\u51e4\u82b1\u5bb9\u60e8\u81ea\uff0c\u53cc\u7709\u5fae\u76b1\uff0c\u54ac\u7d27\u5634\u5507\uff0c\u9165\u80f8\u6025\u907d\u5730\u8d77\u4f0f\u7740\uff0c\u4f3c\u662f\u53d7\u4f24\u4e0d\u8f7b\u3002\u5c55\u767d \u770b\u5230\u8fd9\u5982\u82b1\u4f3c\u7684\u7f8e\u4eba\u4f24\u5728\u81ea\u5df1\u624b\u4e2d\uff0c\u5fc3\u4e2d\u4e0d\u7981\u53c8\u751f\u51fa\u4e00\u9635\u601c\u60dc\u4e4b\u60c5\uff0c\u5450\u5450\u5730\u9053\uff1a\u201c\u91d1\u59d1\u5a18\uff0c \u6211\u4e0d\u662f\u6709\u5fc3\u4f24\u4f60\u2026\u2026\u201d \u91d1\u5f69\u51e4\u661f\u76ee\u5fae\u7741\uff0c\u89c1\u5c55\u767d\u62b1\u7740\u81ea\u5df1\uff0c\u6ee1\u8138\u60f6\u6050\u4e4b\u8272\uff0c\u50cf\u5c0f\u5b69\u5b50\u505a\u9519\u4e86\u4e8b\uff0c\u5411\u81ea\u5df1\u8ba4\u9519\uff0c \u4f01\u6c42\u539f\u8c05\uff0c\u5fc3\u4e2d\u7ade\u611f\u5230\u4e86\u8bf4\u4e0d\u51fa\u7684\u6e29\u99a8\uff0c\u6a31\u53e3\u534a\u5f20\uff0c\u5a07\u5598\u9053\uff1a\u201c\u6211\u2014\u2014\u4e0d\u6028\u4f60\uff0c\u5c55\u2014\u2014\u53ea\u8981 \u4f60\u77e5\u9053\u6211\u7684\u5fc3\u5c31\u597d\u4e86\u2026\u2026\u201d \u5c55\u767d\u5934\u8111\u201c\u8f70\u201d\u7684\u4e00\u9707\uff0c\u5230\u5e95\u4ed6\u6240\u5bb3\u6015\u7684\u4e8b\u53d1\u751f\u4e86\uff0c\u8fd9\u88ab\u4ed6\u6253\u4f24\u4e86\u7684\u5343\u91d1\u5c0f\u59d0\uff0c\u7ade\u6beb\u4e0d \u6028\u6068\u4ed6\uff0c\u800c\u4e14\uff0c\u8fd9\u5145\u6ee1\u4e86\u67d4\u60c5\u871c\u610f\u7684\u4e24\u53e5\u8bdd\uff0c\u4e0d\u662f\u5979\u4ec0\u4e48\u90fd\u660e\u663e\u5417\uff1f \u5979\uff0c\u7231\u4e0a\u4e86\u4ed6\uff01\u800c\u4ed6\u5462\uff0c\u660e\u77e5\u5979\u662f\u4ec7\u4eba\u7684\u5973\u513f\uff0c\u4e07\u4e07\u4e0d\u80fd\u63a5\u53d7\u5979\u7684\u7231\uff0c\u53ef\u662f\uff0c\u73b0\u5728\u53ea\u6709 \u4ed6\u81ea\u5df1\u7684\u826f\u77e5\u6e05\u9192\uff0c\u4ec7\u6068\u3001\u7406\u667a\uff0c\u4e00\u5207\u90fd\u4e0d\u5b58\u5728\u4e86\uff0c\u4ed6\u4e0d\u80fd\u6b3a\u9a97\u81ea\u5df1\uff0c\u4ed6\u4e5f\u7231\u4e0a\u5979\u4e86\uff01 \u91d1\u5f69\u98ce\u52c9\u5f3a\u5f20\u53e3\u8bf4\u4e86\u90a3\u4e24\u53e5\u8bdd\uff0c\u7ade\u5fcd\u4e0d\u4f4f\u5185\u8151\u7ffb\u6d8c\u7684\u6c14\u8840\uff0c\u8bdd\u624d\u8bf4\u5b8c\uff0c\u4e00\u80a1\u9c9c\u8840\u5df2\u4ece\u5979 \u90a3\u534a\u5f20\u7684\u6a31\u53e3\u5185\u6d41\u51fa\uff01\u4e00 \u5c55\u767d\u60ca\u547c\u4e00\u58f0\uff0c\u518d\u4e5f\u4e0d\u987e\u4e00\u5207\uff0c\u4ece\u62e5\u62b1\u7740\u91d1\u5f69\u51e4\u7684\u5a07\u8eaf\u4e0a\u62bd\u51fa\u4e00\u53ea\u53f3\u624b\uff0c\u8fde\u70b9\u91d1\u5f69\u51e4\u80f8 \u540e\u4e09\u5904\u8981\u7a74\uff0c\u7136\u540e\u4ee5\u638c\u5fc3\u62b5\u4f4f\u91d1\u5f69\u51e4\u80f8\u524d\u201c\u671f\u95e8\u201d\u7a74\u4e0a\uff01 \u53ea\u89c9\u67d4\u8f6f\u7684\u80f8\u4e0a\uff0c\u9686\u8d77\u7684\u53cc\u4e73\uff0c\u4f7f\u4ed6\u89e6\u4e4b\u5982\u53d7\u7535\u51fb\uff0c\u5468\u8eab\u8840\u6db2\u5982\u4e07\u9a6c\u5954\u817e\uff0c\u51e0\u4e4e\u4f7f\u4ed6\u628a \u6301\u4e0d\u4f4f\u2026\uff0e\uff0c\u3002 \u201c\u5594\u3002\u3002\u3002\u201d\u4e0d\u77e5\u662f\u75db\u695a\uff1f\u8fd8\u662f\u6ee1\u8db3\u4e86\u68a6\u60f3\uff1f\u5f53\u5c55\u767d\u624b\u638c\u6309\u5728\u91d1\u5f69\u51e4\u7684\u80f8\u524d\u6765\uff0c\u5979\u5fae\u5fae \u53d1\u51fa\u4e00\u58f0\u5a07\u547c\uff01 \u5c55\u767d\u84e6\u7136\u60ca\u89c9\uff0c\u5f3a\u7a33\u4f4f\u5fc3\u733f\u610f\u9a6c\uff0c\u4ee5\u771f\u529b\u8d2f\u6ce8\u638c\u5fc3\uff0c\u5e76\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u91d1\u59d1\u5a18\uff0c\u5728\u4e0b\u8fd0\u529f \u4e3a\u59d1\u5a18\u7597\u4f24\uff0c\u8bf7\u59d1\u5a18\u4ee5\u6c14\u76f8\u5f15\u2026\u2026\u201d \u91d1\u5f69\u51e4\u9001\u7ed9\u4ed6\u4e00\u4e2a\u5a9a\u773c\uff0c\u4e0d\u77e5\u662f\u55d4\u662f\u559c\uff1f\u4f46\u5374\u6ca1\u8bf4\u8bdd\uff0c\u4f9d\u8a00\u8c03\u5747\u547c\u5438\uff0c\u6697\u6697\u4e0e\u5c55\u767d\u638c\u5fc3 \u8d2f\u6ce8\u771f\u529b\u76f8\u5408\uff0c\u7acb\u89c9\u4e00\u822c\u70ed\u6d41\uff0c\u7531\u671f\u95e8\u201d\u76f4\u8fbe\u7a7a\u6218\u201d\uff0c\u53c8\u7531\u201c\u4e09\u7126\u201d\u4e0b\u6c89\u4e39\u7530\u201d\uff0c\u5468\u8eab\u8212\u7545 \u65e0\u6bd4\uff0c\u800c\u4e14\uff0c\u80f8\u524d\u75db\u695a\u7acb\u5931\u3002 \u53c8\u89c9\u5f97\u5c55\u767d\u4e00\u53ea\u8d2f\u6ee1\u70ed\u6d41\u7684\u624b\u638c\uff0c\u5728\u80f8\u524d\u6e38\u52a8\u8d77\u6765\uff0c\u4ece\u4e0a\u81f3\u4e0b\uff0c\u5c06\u53ca\u5c0f\u8179\u2026\u2026\u5979\u9664\u4e86\u5468 \u8eab\u611f\u5230\u65e0\u6bd4\u8212\u9002\u4e4b\u5916\uff0c\u66f4\u6709\u4e00\u79cd\u4f3c\u75d2\u4f3c\u9165\u3001\u4ece\u672a\u7ecf\u9a8c\u8fc7\u7684\u611f\u89c9\uff0c\u4f7f\u5979\u7981\u4e0d\u4f4f\u6574\u4e2a\u5a07\u8eaf\u4e00\u9635\u5fae \u98a4\uff0c\u4e00\u5f20\u60e8\u767d\u65e0\u8840\u8272\u7684\u5a07\u7f8e\u9762\u5b54\uff0c\u7acb\u523b\u5e03\u6ee1\u4e86\u7ea2\u4e91\u2026\u2026\u201c\u5594\uff01\u201d\u91d1\u5f69\u51e4\u95ed\u7740\u773c\u775b\uff0c\u68a6\u5e7b\u822c\u5730 \u8bf4\uff1a\u201c\u6211\u4e0d\u8981\u4f60\u53eb\u6211\u91d1\u59d1\u5a18 \u5c55\u767d\u4e5f\u8ff7\u8ff7\u7cca\u7cca\u5730\u9053\uff1a\u201c\u90a3\u4e48\u53eb\u4f60\u4ec0\u4e48\u5462\uff1f\u201d \u91d1\u5f69\u51e4\u7b54\u9053\uff1a\u201c\u53eb\u6211\u51e4\u59b9\u59b9\u2026\u2026\u201d \u5c55\u767d\u5fc3\u5934\u4e00\u8361\uff0c\u5b8c\u5168\u5fd8\u8bb0\u4e86\u4ec7\u6068\uff0c\u7ade\u4f9d\u8a00\u53eb\u9053\uff1a\u201c\u51e4\u59b9\u59b9 \u201c\u5594\uff01\u201d\u91d1\u5f69\u51e4\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5c55\u54e5\u54e5\uff0c\u4f60\u771f\u597d\u2026\u2026\u201d \u7a81\u7136\uff0c\u4e00\u58f0\u6781\u7ec6\u5fae\u7684\u51b7\u7b11\uff0c\u8d77\u81ea\u6708\u4e0b\u82b1\u836b\uff01 \u5176\u5b9e\uff0c\u91d1\u5f69\u51e4\u53d7\u4f24\u5e76\u4e0d\u91cd\uff0c\u5728\u5c55\u767d\u4ee5\u5929\u4e0b\u5947\u5b66\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\u4e0a\u6240\u8f7d\u4e4b\u7597\u4f24\u5927\u6cd5\u4e3a \u4e4b\u7597\u4f24\uff0c\u53ef\u4ee5\u8bf4\u662f\u201c\u5927\u65b9\u5c0f\u7528\u201d\uff0c\u5979\u90a3\u4e00\u70b9\u5fae\u4f24\u65e9\u5df2\u75ca\u6108\u4e86\uff0c\u800c\u4e14\uff0c\u501f\u5c55\u767d\u201c\u771f\u6c14\u6e21\u7a74\u201d\uff0c \u5185\u529f\u771f\u529b\u65b9\u9762\u5c1a\u53d7\u60e0\u4e0d\u5c0f\uff0c\u81f3\u4e8e\u4e8c\u4eba\u4ecd\u5728\u62e5\u62b1\u629a\u6170\uff0c\u4e0d\u8fc7\u6c89\u9189\u5728\u7231\u60c5\u4e4b\u4e2d\u800c\u4e0d\u81ea\u89c9\u7f62\u4e86\uff0c \u4f46\u8fd9\u4e00\u58f0\u6765\u81ea\u6708\u4e0b\u82b1\u836b\u7684\u8f7b\u5fae\u51b7\u7b11\uff0c\u5374\u60ca\u9192\u4e86\u8fd9\u6c89\u9189\u4e8e\u7231\u6cb3\u7684\u4e00\u5bf9\u521d\u604b\u60c5\u4eba\uff01 \u5c55\u767d\u9996\u5148\u60ca\u89c9\uff0c\u5fd9\u628a\u504e\u5728\u6000\u4e2d\u7684\u91d1\u5f69\u51e4\u6276\u8d77\uff0c\u6c89\u58f0\u559d\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\uff1f\u201d \u4e00\u6761\u9ed1\u5f71\u4ece\u82b1\u836b\u6df1\u5904\u4e00\u95ea\u800c\u51fa\uff0c\u771f\u6bd4\u7535\u8fd8\u75be\uff0c\u731b\u7136\u6536\u52bf\u7ad9\u5728\u77f3\u4ead\u53f0\u9636\u4e0a\uff0c\u5a07\u8eaf\u5982\u8fce\u98ce\u5f31 \u67f3\uff0c\u7389\u989c\u5982\u6625\u82b1\u76db\u653e\uff0c\u79c0\u53d1\u968f\u98ce\uff0c\u9526\u8863\u5982\u4e91\uff0c\u6b63\u662f\u90a3\u5201\u94bb\u96be\u7f20\u3001\u5929\u771f\u672a\u51ff\u7684\u5a07\u61a8\u5c11\u5973\u5c55\u5a49 \u513f\uff01 \u5a49\u513f\u5374\u4e00\u76b1\u743c\u9f3b\uff0c\u201c\u5403\u201d\u7684\u4e00\u58f0\u7b11\u9053\uff1a\u201c\u6708\u4e0a\u67f3\u68a2\u5934\uff0c\u4eba\u7ea6\u9ec4\u660f\u540e\uff0c\u6ca1\u60f3\u5230\u6708\u5df2\u7ecf\u534a\u5929 \u9ad8\u4e86\uff0c\u8fd9\u7ea6\u4f1a\u7684\u4eba\u8fd8\u6ca1\u6709\u6563\u5462\uff01\u201d \u5c55\u767d\u5c34\u5c2c\u4e07\u5206\uff0c\u4f46\u786c\u7740\u5934\u76ae\u8bf4\u9053\uff1a\u201c\u5a49\u513f\uff0c\u4f60\u5230\u8fd9\u91cc\u6765\u505a\u4ec0\u4e48\uff1f\u2026\u2026\u201d \u201c\u54df\uff01\u201d\u5a49\u513f\u9178\u6e9c\u6e9c\u5730\u9053\uff1a\u201c\u6211\u6765\u8fd9\u91cc\u73a9\u5566\uff01\u53cd\u6b63\u6211\u4e0d\u4f1a\u8ba9\u4eba\u5bb6\u62b1\u7740\u53eb\u59b9\u59b9\u2026\u2026\u201d \u4e00\u53e5\u8bdd\u8bf4\u607c\u4e86\u91d1\u5f69\u51e4\uff0c\u9661\u7136\u62ac\u5934\u5a07\u53f1\u9053\uff1a\u201c\u4f60\u662f\u54ea\u91cc\u6765\u7684\u91ce\u4e2b\u5934\uff1f\u7adf\u6562\u8dd1\u5230\u5357\u4eac\u91d1\u5e9c\u6765 \u64a4\u91ce\uff01\u201d \u5a49\u513f\u67f3\u7709\u4e00\u7acb\uff0c\u9053\uff1a\u4f60\u8bb2\u8bdd\u6700\u597d\u5ba2\u6c14\u4e00\u70b9\uff0c\u8981\u4e0d\uff0c\u53ef\u522b\u8bf4\u59d1\u5a18\u8981\u4f60\u7684\u597d\u770b\uff01\u201d \u91d1\u5f69\u51e4\u5728\u91d1\u5e9c\u5bb6\u4e2d\u4e43\u662f\u5343\u91d1\u4e4b\u8eaf\uff0c\u5373\u662f\u5979\u7684\u7236\u6bcd\u5144\u957f\u5bf9\u5979\u4e5f\u4e0d\u4f1a\u6709\u534a\u53e5\u4e0d\u656c\u4e4b\u8a00\uff0c\u4eca\u88ab \u5a49\u513f\u62a2\u767d\u4e86\u51e0\u53e5\uff0c\u53ea\u6c14\u5f97\u5468\u8eab\u4e71\u6296\u9053\uff1a\u597d\uff01\u4f60\u5012\u6562\u6559\u8bad\u8d77\u6211\u6765\u4e86\uff0c\u4f60\u82e5\u662f\u4e0d\u7acb\u523b\u9053\u6b49\u5c31\u522b\u518d \u60f3\u6d3b\u7740\u79bb\u5f00\u91d1\u5e9c\uff01\u201d \u5a49\u513f\u51b7\u7b11\u9053\uff1a\u201c\u8bf4\u5927\u8bdd\u4e0d\u6015\u6298\u4e86\u820c\u5934\uff0c\u51ed\u4f60\uff0c\u8fd8\u4e0d\u89c1\u5f97\u80fd\u7559\u5f97\u4f4f\u4f60\u5bb6\u59d1\u5a18\uff01\u201d \u91d1\u5f69\u51e4\u6012\u6c14\u586b\u81ba\uff0c\u53f1\u9053\uff1a\u201c\u597d\u5229\u53e3\u7684\u4e2b\u5934\uff0c\u63a5\u62db\uff01\u201d \u58f0\u51fa\u62db\u5230\uff0c\u62ff\u624b\u9a88\u6307\u76f4\u70b9\u5a49\u513f\u53cc\u76ee\uff0c\u53f3\u638c\u5e73\u80f8\u63a8\u51fa\uff0c\u6a2a\u780d\u5a49\u513f\u5de6\u808b\uff0c\u7528\u7684\u662f\u201c\u6e38\u8702\u620f \u854a\u201d\u4e0e\u201c\u5f69\u8776\u604b\u82b1\u201d\uff0c\u4e24\u62db\u9f50\u51fa\uff0c\u624b\u6cd5\u4e0d\u51e1I \u4f46\u5a49\u513f\u6b66\u529f\u53ef\u8981\u6bd4\u91d1\u5f69\u51e4\u9ad8\u591a\u4e86\uff0c\u539f\u56e0\u662f\u5979\u5728\u5ca9\u5c71\u5341\u4e8c\u6d1e\uff0c\u5de7\u9047\u201c\u795e\u7334\u201d\u94c1\u51cc\uff0c\u767e\u65e5\u4f20 \u529f\uff0c\u5df2\u4f7f\u5979\u8e6c\u8eab\u5f53\u4eca\u6b66\u6797\u4e00\u6d41\u9ad8\u624b\u4e4b\u5217\u3002 \u91d1\u5f69\u51e4\u4e24\u62db\u9f50\u51fa\uff0c\u867d\u7136\u51cc\u5389\u65e0\u6bd4\uff0c\u4f46\u5979\u5374\u9762\u5e26\u8f7b\u8511\u51b7\u7b11\uff0c\u811a\u4e0b\u2018\u5fae\u5fae\u4e00\u6ed1\uff0c\u7acb\u523b\u8131\u51fa\u91d1 \u5f69\u51e4\u4e24\u62db\u4e4b\u5916\uff0c\u7d27\u8ddf\u7740\u5de6\u638c\u63a2\u51fa\uff0c\u731b\u6293\u91d1\u5f69\u51e4\u53f3\u8155\u201c\u5173\u5143\u201d\uff01 \u91d1\u5f69\u51e4\u5927\u5403\u4e00\u60ca\uff0c\u60f3\u4e0d\u5230\u7a81\u7136\u73b0\u8eab\u7684\u8fd9\u5e74\u7eaa\u8f7b\u8f7b\u7684\u5c0f\u59d1\u5a18\uff0c\u624b\u6cd5\u62db\u5f0f\u7adf\u7136\u5982\u6b64\u7cbe\u5947\uff0c\u4e0d \u4f46\u6f2b\u4e0d\u7ecf\u5fc3\u5730\u4fbf\u8eb2\u8fc7\u4e86\u81ea\u5df1\u4e24\u5927\u6740\u62db\uff0c\u800c\u4e14\u8fd8\u6293\u5411\u81ea\u5df1\u7684\u5de6\u624b\uff0c\u51fa\u62db\u4e4b\u5de7\uff0c\u62ff\u634f\u4e4b\u6dee\uff0c\u7b80\u76f4 \u8fbe\u5230\u795e\u5316\u7684\u5883\u754c\uff01 \u5c24\u5176\u90a3\u5f3a\u52b2\u6307\u98ce\uff0c\u7adf\u7136\u523a\u80a4\u751f\u75db\uff0c\u53ef\u89c1\u5176\u5185\u529f\u6f5c\u529b\u4e4b\u5927\uff0c\u4ea6\u5fc5\u60ca\u4eba\uff01 \u91d1\u5f69\u51e4\u61d4\u7136\u4e4b\u4e2d\uff0c\u53f3\u8155\u731b\u5411\u4e0b\u4e00\u6c89\uff0c\u9669\u4e9b\u672a\u88ab\u5a49\u513f\u6293\u4f4f\uff0c\u4f46\u6307\u98ce\u626b\u8fc7\u8155\u90e8\uff0c\u7adf\u4f7f\u5979\u75db\u5f97 \u5dee\u4e00\u70b9\u558a\u51fa\u58f0\u97f3\u6765\uff01 \u4f46\u5a49\u513f\u5e76\u4e0d\u5bb9\u5979\u62bd\u8eab\u6362\u62db\uff0c\u53f3\u638c\u659c\u7acb\u5982\u5203\uff0c\u5df2\u780d\u5411\u91d1\u5f69\u51e4\u201c\u80a9\u4e95\u201d\u91cd\u7a74\uff01 \u91d1\u5f69\u51e4\u867d\u7136\u52c9\u5f3a\u8eb2\u8fc7\u5a49\u513f\u7684\u5de6\u624b\u4e00\u6293\uff0c\u4f46\u5a49\u513f\u780d\u81f3\u7684\u53f3\u638c\uff0c\u5374\u518d\u4e5f\u65e0\u6cd5\u8eb2\u95ea\uff0c\u800c\u4e14\uff0c\u5979 \u81ea\u5df1\u70b9\u5411\u5a49\u513f\u53cc\u76ee\u7684\u4e00\u62db\u7528\u8001\uff0c\u60f3\u6536\u62db\u56de\u6765\u683c\u6321\u4e5f\u4e0d\u53ef\u80fd\uff0c\u773c\u770b\u5a49\u513f\u4e00\u638c\u4fbf\u8981\u780d\u5728\u91d1\u5f69\u98ce\u7684 \u9879\u4e0b\uff01\u2026\u2026 \u522b\u770b\u5a49\u513f\u90a3\u67d4\u82e5\u65e0\u9aa8\u7684\u5c0f\u5c0f\u4e00\u53ea\u624b\u638c\uff0c\u5185\u529b\u8d2f\u6ce8\u4e4b\u4e0b\uff0c\u771f\u4e0d\u4e9a\u4e8e\u950b\u5229\u7684\u5200\u5251\uff0c\u800c\u4e14\uff0c\u52b2 \u75be\u7834\u98ce\u751f\u51fa\u9510\u5578\uff0c\u4e5f\u53ef\u89c1\u5176\u8fd9\u4e00\u638c\u7684\u5a01\u529b\uff0c\u5b9e\u5728\u4e0d\u5c0f\uff01 \u5c55\u767d\u731b\u5403\u4e00\u60ca\uff0c\u6025\u559d\u9053\uff1a\u201c\u5a49\u513f\uff0c\u4f4f\u624b\uff01\u2026\u201d \u4f46\u5a49\u513f\u72b9\u5982\u672a\u95fb\uff0c\u5982\u5203\u7684\u638c\u950b\uff0c\u75be\u5578\u7834\u7a7a\uff0c\u7167\u76f4\u5411\u91d1\u5f69\u98ce\u7c89\u767d\u7389\u9888\u4e0a\u5207\u4e0b\u2026\u2026 \u5c55\u767d\u8eab\u5f62\u7535\u5c04\u800c\u51fa\uff0c\u5de6\u624b\u6293\u4f4f\u91d1\u5f69\u51e4\u987a\u52bf\u4e00\u5e26\uff0c\u628a\u91d1\u5f69\u51e4\u7529\u51fa\u4e94\u516d\u6b65\u53bb\uff0c\u53f3\u624b\u7531\u4e0b\u5411\u4e0a \u5f80\u5a49\u51e0\u638c\u52bf\u8fce\u53bb\uff01 \u201c\u556a\u201d\u7684\u4e00\u58f0\u8106\u54cd\uff0c\u5c55\u767d\u4e0e\u5a49\u513f\u786c\u5bf9\u4e86\u4e00\u638c\uff01 \u5a49\u513f\u5a07\u8eaf\u5fae\u6643\uff0c\u88ab\u9707\u5f97\u8fde\u9000\u4e09\u6b65\uff0c\u65b9\u624d\u7ad9\u7a33\uff0c\u7c89\u8138\u6c14\u5f97\u715e\u767d\uff0c\u4e00\u5bf9\u660e\u7738\u6012\u77aa\u7740\u5c55\u767d\uff0c\u51e0 \u6b32\u55b7\u51fa\u706b\u6765\u2026\u2026 \u91d1\u5f69\u51e4\u6b7b\u91cc\u9003\u751f\uff0c\u88ab\u5c55\u767d\u4e00\u624b\u7529\u51fa\uff0c\u4e5f\u662f\u7389\u989c\u4f3c\u7eb8\uff0c\u53c8\u7f9e\u53c8\u6012\uff0e\uff0e\uff0e\uff0e\uff0e\u2019\u5c55\u767d\u638c\u5fc3\u4e5f \u611f\u5230\u706b\u8fa3\u8fa3\u7684\u4e00\u75db\uff0c\u5fc3\u4e2d\u6697\u8d5e\u5a49\u513f\u6b66\u529f\u5185\u529b\u4e4b\u7eaf\uff0c\u4f46\u8868\u9762\u4e0a\u5374\u88c5\u5f97\u6781\u5176\u5e73\u9759\u5730\u8bf4\u9053\uff1a\u201c\u5a49 \u513f\uff0c\u4f60\u4e0e\u91d1\u5c0f\u59d0\u65e0\u4ec7\u65e0\u6028\uff0c\u4e3a\u4ec0\u4e48\u4e00\u89c1\u9762\u4fbf\u65bd\u6740\u624b\uff1f\u201d \u88ab\u5c55\u767d\u4ece\u638c\u4e0b\u6551\u8d70\u60c5\u654c\uff0c\u5a49\u513f\u5fc3\u4e2d\u5148\u5df2\u4e0d\u5feb\uff0c\u5982\u4eca\uff0c\u89c1\u5c55\u767d\u8bf4\u8bdd\u53c8\u5e2e\u7740\u91d1\u5f69\u51e4\uff0c\u4e0d\u7531\u6ee1 \u8179\u59d4\u5c48\uff0c\u6d8c\u51fa\u6ee1\u7736\u7684\u773c\u6cea\u6765\u3002 \u4f46\u5979\u5f3a\u5fcd\u4f4f\u6ee1\u7736\u7684\u773c\u6cea\uff0c\u4e0d\u4f7f\u5b83\u6389\u4e0b\u6765\uff0c\u731b\u4e00\u8dfa\u811a\uff0c\u54ac\u7740\u5634\u5507\u53d1\u72e0\u9053\uff1a\u201c\u6211\u4eca\u751f\u518d\u4e5f\u4e0d \u613f\u770b\u5230\u4f60\uff01\u2026\u2026\u201d \u8bf4\u7f62\uff0c\u56de\u5934\u98de\u8eab\u800c\u53bb\u3002 \u201c\u54ea\u91cc\u8d70\uff1f\u770b\u6253\uff01\u201d \u7a81\u7136\uff0c\u4ead\u5916\u4e00\u68f5\u5927\u6811\u540e\uff0c\u4f20\u51fa\u4e00\u58f0\u66b4\u559d\uff0c\u63a5\u7740\u4e00\u84ec\u9752\u5f71\uff0c\u731b\u5411\u51cc\u7a7a\u98de\u9a70\u7684\u5a49\u513f\u8fce\u5934\u5c04 \u53bb\uff01 \u5a49\u513f\u201c\u8e51\u7a7a\u5e7b\u5f71\u201d\u8eab\u6cd5\uff0c\u5df2\u5230\u51fa\u795e\u5165\u5316\u5883\u5730\uff0c\u867d\u7136\u4e8b\u8d77\u4ed3\u7cb9\uff0c\u6765\u4eba\u53c8\u662f\u4ee5\u6ee1\u628a\u6697\u5668\u901f\u65bd \u5077\u88ad\uff0c\u53ef\u4ee5\u8bf4\u662f\u9634\u6bd2\u800c\u60ca\u9669\u4e07\u72b6\uff0c\u4f46\u5a49\u513f\u4ecd\u80fd\u4e34\u5371\u4e0d\u4e71\uff0c\u534a\u7a7a\u4e2d\uff0c\u4e00\u4e2a\u5de7\u5999\u7684\u201c\u4e91\u91cc\u7ffb \u8eab\u201d\uff0c\u5a07\u8eaf\u5019\u7136\u63d0\u9ad8\u4e94\u5c3a\uff0c\u4e00\u84ec\u9752\u5f71\u7684\u6697\u5668\u4e0b\u6253\u8fc7\u2026\u2026\u9669\u4e9b\u4f24\u7740\u5a49\u513f\uff0c\u8fde\u5c55\u767d\u90fd\u66ff\u5979\u634f\u4e86\u4e00 \u628a\u51b7\u6c57\uff01 \u5a49\u513f\u7231\u5c55\u767d\u7231\u5f97\u53d1\u75af\uff0c\u7a81\u7136\u53d1\u73b0\u5c55\u767d\u4e0e\u522b\u7684\u5973\u4eba\u5e7d\u4f1a\uff0c\u5df2\u662f\u6c14\u5f97\u4e0d\u5f97\u4e86\u3002\u6b64\u65f6\uff0c\u5979\u90a3\u6e29 \u67d4\u548c\u987a\u7684\u5c11\u5973\u5fc3\uff0c\u65e9\u5df2\u88ab\u5992\u706b\u3001\u4ec7\u6068\u3001\u6124\u6012\uff0c\u71c3\u70e7\u7684\u53d8\u4e86\u6837\uff0c\u4e34\u7a7a\u4e00\u7ffb\uff0c\u8eb2\u8fc7\u5982\u6ee1\u7a7a\u6d41\u8424\u7684 \u6697\u5668\u4e4b\u540e\uff0c\u5979\u8eab\u5728\u534a\u7a7a\u4e2d\uff0c\u5ffd\u53f1h\u4e00\u58f0\uff0c\u5934\u4e0b\u811a\u4e0a\u5730\u731b\u5411\u6697\u88ad\u5979\u7684\u4eba\u6251\u53bb\uff01 \u540c\u65f6\uff0c\u501f\u4e0b\u6251\u4e4b\u52bf\uff0c\u8fd0\u8db3\u4e86\u5341\u6210\u529f\u529b\uff0c\u53cc\u638c\u731b\u5411\u6765\u4eba\u8fce\u5934\u5288 \u4e0b\uff01 \u6765\u4eba\u4f3c\u4e5f\u4f30\u4e0d\u5230\u5a49\u513f\u8eab\u5f62\u51cc\u7a7a\uff0c\u8e87\u80fd\u4e34\u7a7a\u6362\u6b65\uff0c\u8eb2\u8fc7\u4ed6\u7684\u72ec\u95e8\u6697\u5668\uff0c\u53c8\u89c1\u5a49\u513f\u51cc\u7a7a\u4e0b\u51fb \u6765\u52bf\u751a\u731b\uff0c\u4e0d\u7531\u811a\u6b65\u5fae\u632b\uff0c\u63a2\u624b\u4ece\u80cc\u4e0a\u8896\u51fa\u4e00\u652f\u7cbe\u5149\u8000\u76ee\u7684\u957f\u5251\u6765\uff01\u2026\u2026 \u4f46\u5c31\u5728\u6b64\u65f6\uff0c\u5728\u53e6\u4e00\u4e2a\u89d2\u843d\u91cc\u65e0\u58f0\u65e0\u606f\u5730\u53c8\u6253\u51fa\u4e00\u84ec\u9752\u8272\u6697\u5668\uff01 \u8fd9\u4e00\u628a\u6697\u5668\uff0c\u4e3a\u6570\u66f4\u591a\uff0c\u800c\u4e14\u4f3c\u4e4e\u4e5f\u7728\u521a\u624d\u90a3\u4e00\u628a\u6697\u5668\u6765\u5f97\u52bf\u731b\uff0c\u663e\u89c1\u8fd9\u7b2c\u4e8c\u6b21\u6253\u51fa\u6697 \u5668\u4e4b\u4eba\uff0c\u8981\u6bd4\u7b2c\u4e00\u6b21\u7684\u4eba\u6b66\u529f\u624b\u52b2\u8981\u9ad8\u5f3a\u5f97\u591a\uff01 \u4f46\u89c1\u6ee1\u7a7a\u9752\u5f71\uff0c\u5982\u7fa4\u8424\u9f50\u98de\uff0c\u75be\u5578\u7740\u5411\u5a49\u51e0\u5468\u8eab\u7f69\u4e0b\uff01 \u5c55\u767d\u5927\u5403\u4e00\u60ca\uff0c\u6025\u559d\u9053\uff1a\u5a49\u513f\u5c0f\u5fc3\uff01\u8eab\u540e\u53c8\u6709\u6697\u5668\u5230\u4e86\uff01\u201d \u5c55\u767d\u77e5\u9053\u5149\u662f\u63d0\u9192\uff0c\u5a49\u513f\u4e5f\u65e0\u6cd5\u7b2c\u4e8c\u6b21\u8eb2\u8fc7\u8fd9\u6ee1\u7a7a\u6697\u5668\uff0c\u5728\u559d\u58f0\u4e2d\u5df2\u7136\u817e\u8eab\u8fde\u5288\u4e24\u638c\uff0c \u5411\u5a49\u513f\u8eab\u540e\u7684\u6697\u5668\u6253\u51fb\uff01 \u6709\u4e0d\u5c11\u6697\u5668\uff0c\u88ab\u5c55\u767d\u638c\u529b\u9707\u98de\uff0c\u4f46\u56e0\u4e3a\uff0c\u638c\u529b\u4e0d\u80fd\u5b8c\u5168\u628a\u6ee1\u7a7a\u6697\u5668\u6321\u843d\uff0c\u56e0\u6b64\uff0c\u5c1a\u6709\u5341 \u6570\u679a\u52b2\u529b\u7279\u8db3\u7684\u9752\u5f71\uff0c\u4ecd\u7136\u5411\u7740\u5a49\u513f\u5c04\u53bb\uff01 \u5a49\u513f\u8eab\u5f62\u4e0b\u6251\uff0c\u4e5f\u53d1\u89c9\u8eab\u540e\u53c8\u6709\u6697\u5668\u6253\u81f3\uff0c\u4f46\u7531\u4e8e\u5979\u5fc3\u6068\u7b2c\u4e00\u6b21\u6697\u88ad\u4ed6\u7684\u4eba\uff0c\u4ee5\u81f4\u534a\u7a7a \u6b79\u6251\u51fa\u62db\u7528\u529b\u8fc7\u731b\uff0c\u5f85\u53d1\u89c9\u53c8\u6709\u4eba\u5728\u8eab\u540e\u6697\u88ad\u65f6\uff0c\u6b32\u60f3\u518d\u6b21\u51cc\u7a7a\u6362\u5f0f\uff0c\u5df2\u662f\u529b\u4e0d\u4ece\u5fc3\uff0c\u4f46\u6c42 \u751f\u7684\u672c\u80fd\uff0c\u4ecd\u4f7f\u5979\u5c3d\u91cf\u8eb2\u907f\uff0c\u731b\u6253\u201c\u5343\u65a4\u5760\u201d\uff0c\u8eab\u5f62\u6025\u901f\u4e0b\u5760\uff0c\u540c\u65f6\uff0c\u53cc\u638c\u4e5f\u66f4\u52a0\u7528\u529b\uff0c\u731b \u5411\u8eab\u4e0b\u4e4b\u4eba\u6253\u53bb\uff01 \u7b2c\u4e00\u6b21\u73b0\u8eab\u4e4b\u4eba\uff0c\u867d\u5df2\u4eae\u51fa\u5b9d\u5251\uff0c\u4f46\u89c1\u5a49\u513f\u638c\u52bf\u751a\u731b\uff0c\u4e0d\u6562\u786c\u63a5\uff0c\u6a2a\u98d8\u516b\u5c3a\uff0c\u4ee5\u8eb2\u5a49\u513f \u52bf\u5982\u60ca\u96f7\u7684\u51cc\u7a7a\u4e00\u51fb\uff01 \u201c\u7830\u201d\u7684\u4e00\u58f0\u5de8\u54cd\uff0c\u6c99\u77f3\u6a2a\u98de\uff0c\u5a49\u513f\u638c\u529b\u843d\u7a7a\uff0c\u7adf\u7136\u628a\u5730\u4e0b\u6253\u2018\u4e86\u4e24\u4e2a\u5927\u6df1\u5751\uff01 \u5982\u6b64\u5e74\u8f7b\u7684\u5c0f\u59d1\u5a18\uff0c\u80fd\u6709\u5982\u6b64\u96c4\u539a\u7684\u638c\u529b\uff0c\u771f\u662f\u4ee4\u4eba\u548b\u820c\uff01 \u4f46\u5a49\u513f\u638c\u529b\u51fb\u4e0b\uff0c\u4eba\u4e5f\u843d\u4e0b\u5730\u6765\uff0c\u811a\u6b65\u8e09\u8dc4\uff0c\u7389\u5bb9\u60e8\u767d\uff0c\u800c\u4e14\u6447\u6447\u6b32\u5012\uff0c\uff01 \u90a3\u7b2c\u4e00\u6b21\u73b0\u8eab\u4e4b\u4eba\uff0c\u54c8\u54c8\u72c2\u7b11\uff0c\u5374\u7f3a\u5c11\u5de6\u81c2\uff0c\u53ea\u7528\u4e00\u53ea\u53f3\u624b\uff0c\u4e3e\u8d77\u5bd2\u5149\u68ee\u68ee\u7684\u957f\u5251\uff0c\u6076 \u72e0\u72e0\u5730\u5411\u5a49\u513f\u80f8\u524d\u523a\u6765\uff01 \u5a49\u513f\u5728\u7a7a\u4e2d\uff0c\u5df2\u7136\u88ab\u90a3\u7834\u7a7a\u800c\u81f3\u7684\u6697\u5668\u6253\u4e2d\u4e24\u5904\uff0c\u4e00\u5728\u5927\u817f\u4e0a\uff0c\u4e00\u5728\u5de6\u808b\u9aa8\uff0c\u53ea\u611f\u5230\u5947 \u75db\u94bb\u5fc3\uff0c\u5947\u5bd2\u5165\u9aa8\uff0c\u8fde\u6253\u4e86\u51e0\u4e2a\u5bd2\u98a4\uff0c\u843d\u4e0b\u5730\u6765\u5df2\u7ecf\u7ad9\u4e0d\u4f4f\u811a\u6b65\uff0c\u80fd\u591f\u52c9\u5f3a\u4e0d\u4f8b\uff0c\u5df2\u662f\u5979\u8fc7 \u4eba\u7684\u5fcd\u8010\u529b\u4e86\uff0c\u54ea\u91cc\u8fd8\u6709\u4e3a \u4e0d\u8981\u8bf4\u6321\u67b6\uff0c\u5a49\u513f\u6b64\u65f6\u75db\u5f97\u8ddf\u524d\u76f4\u5192\u91d1\u661f\uff0c\u56db\u80a2\u53d1\u8f6f\uff0c\u5fae\u60f3\u8eb2\u5f00\u523a\u6765\u7684\u957f\u5251\uff0c\u4e5f\u65e0\u80fd\u4e3a \u529b\u4e86\uff0c\u4e0d\u7531\u6697\u53f9\u4e00\u58f0\u9053\uff1a\u201c\u5509\uff01\u60f3\u4e0d\u5230\u6211\u4f1a\u6b7b\u5728\u53d8\u4e86\u5fc3\u7684\u60c5\u4eba\u4e4b\u524d\uff0c\u65e9\u77e5\u5982\u6b64\uff0c\u4e0d\u5982\u65e9\u4e00\u70b9 \u6b7b\u4e86\u5012\u597d\uff01\u8fd8\u53ef\u4ee5\u7559\u4e0b\u4e00\u4e2a\u5b8c\u7f8e\u7684\u5370\u8c61\uff0c\u5982\u4eca\uff0c\u5982\u4eca\u2026\u2026\u201d \u5a49\u513f\u5583\u5583\u81ea\u8bed\uff0c\u5bf9\u8fce\u9762\u523a\u6765\u7684\u957f\u5251\uff0c\u5df2\u65e0\u529b\u8eb2\u907f\uff0c\u4f46\u4f7f\u5979\u4f24\u5fc3\u7684\u4e0d\u662f\u6b7b\u4ea1\uff0c\u800c\u662f\u4eb2\u773c\u770b \u5230\u68a6\u4e2d\u60c5\u90ce\u4e0e\u522b\u7684\u5973\u4eba\u5e7d\u4f1a\uff0c\u6253\u7834\u4e86\u5979\u9752\u6625\u5c11\u5973\u521d\u604b\u7684\u7f8e\u68a6\u2026\u2026 \u7a81\u542c\u5c55\u767d\u4e00\u58f0\u5927\u559d\uff1a\u201c\u5b5f\u5982\u840d\uff01\u4f4f\u624b\uff01\u201d \u63a5\u7740\u542c\u5230\u201c\u8f70\uff01\u7830\uff01\u201d\u4e00\u9635\u5de8\u54cd\uff0c\u5a49\u513f\u7741\u5f00\u5c06\u8981\u660f\u82b1\u7684\u53cc\u773c\uff0c\u6726\u80e7\u4e2d\u53ea\u89c1\u62ff\u5251\u523a\u5411\u81ea\u5df1 \u7684\u65ad\u81c2\u5c11\u5e74\uff0c\u8138\u8272\u60e8\u767d\uff0c\u76ee\u5149\u72de\u5389\uff0c\u4f46\u4ed6\u7684\u957f\u5251\u5df2\u7ecf\u8131\u624b\uff0c\u800c\u5634\u89d2\u4e0a\u6302\u7740\u6bb7\u7ea2\u7684\u8840\u8ff9\uff0c\u770b\u6837 \u5b50\u4ed6\u4e0d\u4f46\u6ca1\u6709\u6740\u4e86\u81ea\u5df1\uff0c\u800c\u4e14\u4ed6\u8fd8\u53d7\u4f24\u4e0d\u8f7b\uff01 \u5a49\u513f\u8f6c\u5934\u53c8\u89c1\u4f7f\u81ea\u5df1\u7231\u5f97\u53d1\u72c2\u3001\u53c8\u6068\u5f97\u53d1\u75af\u7684\u5c55\u54e5\u54e5\uff0c\u5df2\u7ecf\u6e0a\u505c\u5cb3\u5cd9\u5730\u7ad9\u5728\u81ea\u5df1\u8eab\u65c1\u3002 \u5a49\u513f\u4f24\u75db\u6b32\u7edd\u7684\u5fc3\u7075\uff0c\u7a81\u7136\u53c8\u611f\u5230\u4e00\u9635\u72c2\u559c\uff0c\u5634\u4e2d\u5583\u5583\u5730\u9053\uff1a\u201c\u662f\u767d\u54e5\u54e5\u6551\u4e86\u6211\uff01\u54e6\uff01 \u767d\u54e5\u54e5\u8fd8\u662f\u7231\u6211\u7684\uff01\u54e6\uff01\u8fd9\u8be5\u591a\u597d\uff01\u8fd9\u8be5\u591a\u597d\uff01\u767d\u54e5\u54e5\uff0c\u767d\u54e5\u54e5\uff0c\u59b9\u59b9\u5c31\u662f\u73b0\u5728\u6b7b\u4e86\uff0c\u4e5f\u4f1a \u611f\u5230\u5e78\u798f\u4e86\u2026\u2026\u201d\u5a49\u513f\u5583\u5583\u81ea\u8bed\u7740\uff0c\u4e00\u9635\u5de8\u75db\u94bb\u5fc3\uff0c\u5f53\u771f\u660f\u6b7b\u4e86\u8fc7\u53bb\u2026\uff0e\uff0e\uff0e \u5c55\u767d\u4e00\u638c\u9707\u98de\u5b5f\u5982\u840d\u523a\u5411\u5a49\u513f\u7684\u957f\u5251\uff0c\u5e76\u628a\u5b5f\u5982\u840d\u9707\u4f24\uff0c\u7a81\u89c1\u8de8\u524d\u2014\u6b65\uff0c\u6276\u4f4f\u4e86\u5dee\u70b9\u683d \u5012\u5730\u4e0a\u7684\u5a49\u513f\uff0c\u89c1\u5230\u5a49\u513f\u5944\u5944\u4e00\u606f\u7684\u60e8\u72b6\uff0c\u4e0d\u7531\u6012\u9053\uff1a\u201c\u5bf9\u4e00\u4e2a\u65e0\u8f9c\u7684\u5c11\u5973\uff0c\u7adf\u4e5f\u8fde\u65bd\u6697 \u7b97\uff0c\u4f60\u4eec\u91d1\u5e9c\u5728\u6c5f\u6e56\u4e0a\u8fd8\u79f0\u4ec0\u4e48\u5b57\u53f7\uff1f\u7b80\u76f4\u53eb\u4eba\u9f7f\u51b7\uff01\u2026\u2026\u201d \u7a81\u7136\u4e00\u9635\u9634\u68ee\u51b7\u7b11\uff0c\u4ece\u82b1\u836b\u4e2d\u8f6c\u51fa\u4e00\u5ea7\u8f6e\u6905\u6765\uff0c\u8f6e\u6905\u4e0a\u7aef\u5750\u7740\u7684\u8d6b\u7136\u7adf\u662f\u5a01\u540d\u663e\u8d6b\u7684 \u201c\u9752\u86a8\u795e\u201d\u91d1\u4e5d\uff01 \u5728\u201c\u9752\u86a8\u795e\u201d\u91d1\u4e5d\u80cc\u540e\uff0c\u7ad9\u5b9a\u5341\u6570\u4e2a\u52b2\u88c5\u5927\u6c49\uff0c\u516d\u540d\u624b\u6267\u94f6\u5251\u7684\u767d\u8863\u5c0f\u7ae5\uff0c\u4e00\u4e2a\u4e2a\u864e\u89c6 \u803d\u803d\u5730\u671b\u5b9a\u5c55\u767d\uff01 \u201c\u9752\u86a8\u795e\u201d\u91d1\u4e5d\u51b7\u7b11\u9053\uff1a\u201c\u80a1\u8005\u738b\u4faf\u8d25\u8005\u8d3c\uff01\u6c5f\u6e56\u4e0a\u4e89\u5f3a\u6597\u80dc\uff0c\u8c01\u80fd\u4e0d\u5012\u4e0b\u8c01\u662f\u82f1\u7ef4\uff0c \u53c8\u4f55\u8c13\u662f\u660e\u6253\uff1f\u4f55\u6e2d\u662f\u6697\u7b97\uff1f\u201d \u5c55\u767d\u4e00\u89c1\u201c\u9752\u86a8\u795e\u201d\u9732\u9762\uff0c\u53cc\u76ee\u5c3d\u8d64\uff0c\u54ac\u7259\u5207\u9f7f\u9053\uff1a\u201c\u6076\u8d3c\uff01\u8001\u5339\u592b\uff01\u4f60\u5192\u5145\u4f2a\u5584\uff01\u4f60 \u4ee5\u6740\u5bb3\u7ed3\u4e49\u76df\u5144\uff0c\u62a2\u63a0\u6765\u7684\u91d1\u94f6\u8d22\u5b9d\uff0c\u6536\u5f55\u5929\u4e0b\u4ea1\u547d\u4e4b\u5f92\uff0c\u4ee5\u5e7f\u6811\u4f60\u7684\u6076\u52bf\u529b\uff01\u4eca\u65e5\u78b0\u4e0a\u5c0f \u7237\uff0c\u7b97\u662f\u4f60\u7684\u6076\u8d2f\u6ee1\u76c8\uff0c\u5bff\u6570\u8be5\u5c3d\u4e86\uff01\u201d \u5c55\u767d\u8bf4\u81f3\u6b64\u5904\uff0c\u201c\u545b\u7405\u201d\u4e00\u58f0\u6e05\u8d8a\u9f99\u541f\uff0c\u4eae\u51fa\u201c\u65e0\u60c5\u78a7\u5251\u201d\uff0c\u54ac\u7259\u5207\u9f7f\u5730\u8bf4\u9053\uff1a\u201c\u91d1 \u4e5d\uff01\u4f60\u5c31\u7eb3\u547d\u6765\u5427\uff01\u201d \u5c55\u767d\u4e00\u987f\u6076\u9a82\uff0c\u7ade\u628a\u8fd9\u6027\u60c5\u602a\u50fb\u7684\u201c\u9752\u86a8\u795e\u201d\u91d1\u4e5d\u7ed9\u9a82\u695e\u4e86\uff0c\u4ed6\u77b3\u89c6\u7740\u5c55\u767d\uff0c\u597d\u5927\u4e00 \u4f1a\uff0c\u624d\u9053\uff1a\u201c\u4f60\u5c0f\u5b50\u7adf\u6562\u5f53\u9762\u5982\u6b64\u8fb1\u9a82\u8001\u592b\uff0c\u975e\u8981\u628a\u4f60\u788e\u5c38\u4e07\u6bb5\u4e0d\u884c\uff01\u53ef\u662f\uff0c\u770b\u4f60\u5c0f\u5c0f\u5e74 \u7eaa\uff0c\u5bf9\u8001\u592b\u7ade\u6709\u5982\u6b64\u6df1\u4ec7\u5927\u6068\uff0c\u8001\u592b\u5012\u975e\u8981\u95ee\u4e2a\u662f\u975e\u9ed1\u767d\u4e0d\u53ef\uff01\u4f60\u8bf4\u8bf4\u770b\uff0c\u8001\u592b\u5341\u6570\u5e74\u4e0d\u51fa \u6c5f\u6e56\uff0c\u53ea\u56e0\u88ab\u5978\u4eba\u8ba1\u7b97\uff0c\u624d\u843d\u4e86\u4e2a\u53cc\u817f\u6b8b\u5e9f\uff0c\u813e\u6c14\u662f\u66b4\u8e81\u4e00\u70b9\uff0c\u4f46\u4ece\u672a\u4e0e\u4eba\u7ed3\u4ec7\uff0c\u4f60\u5c0f\u5b50\u56e0 \u4f55\u4e0e\u8001\u592b\u7ade\u6709\u8840\u6d77\u6df1\u4ec7\u4f3c\u7684\uff0c\u51ed\u4f60\u8fd9\u70b9\u5e74\u7eaa\uff0c\u83ab\u975e\u4f60\u4e00\u51fa\u5a18\u80ce\u4fbf\u4e0e\u8001\u592b\u6709\u4ec7\uff1f\u8fd8\u662f\u53d7\u4e86\u522b\u4eba \u7684\u6559\u5506\u6765\u4e0e\u8001\u592b\u4f5c\u5bf9\uff1f\u8bf4\u7f62\uff01\u8001\u592b\u90fd\u542c\u7740\u4f60\u7684\u3002\u201d \u5c55\u81ea\u60b2\u6124\u83ab\u540d\uff0c\u4ef0\u5929\u4e00\u9635\u72c2\u7b11\uff0c\u9053\uff1a\u201c\u53eb\u4f60\u4e00\u8bf4\uff0c\u4f60\u2018\u9752\u86a8\u795e\u2019\u5012\u6210\u4e86\u597d\u4eba\u4e86\u3002\u6211\u4e14\u95ee \u4f60\uff0c\u5f53\u5e74\u2018\u6c5f\u5357\u4e03\u4fa0\u2019\u4e4b\u9996\u3001\u2018\u9739\u96f7\u5251\u2019\u5c55\u4e91\u5929\uff0c\u4f60\u53ef\u8ba4\u8bc6\uff1f\u201d \u4e0d\u4f46\u201c\u9752\u86a8\u795e\u201d\u91d1\u4e5d\u542c\u5230\u5c55\u767d\u63d0\u201c\u9739\u96f7\u5251\u201d\u4e4b\u540d\uff0c\u5927\u5927\u5403\u4e86\u4e00\u60ca\uff0c\u5c31\u662f\u5728\u573a\u4e4b\u4eba\uff0c\u4e5f\u83ab \u4e0d\u611f\u5230\u4e00\u60ca\u3002\u91d1\u4e5d\u72e0\u77aa\u4e86\u5c55\u767d\u4e00\u4f1a\uff0c\u624d\u9053\uff1a\u542c\u8bf4\u4f60\u59d3\u5c55\uff0c\u83ab\u975e\u4f60\u662f\u5c55\u4e91\u5929\u4e4b\u540e\uff1f\u201d \u5c55\u767d\u9053\uff1a\u201c\u4ee4\u4f60\u60ca\u5947\u5427\uff1f\u4f60\u4e5f\u8bb8\u8ba4\u4e3a\u5148\u7236\u4e0d\u4f1a\u6709\u540e\uff0c\u800c\u4f60\u6740\u5bb3\u7ed3\u4e49\u76df\u5144\u7684\u4e8b\uff0c\u4fbf\u53ef\u4ee5\u65e0 \u4eba\u627e\u4f60\u62a5\u4ec7\u4e86\uff01\u54c8\u54c8\uff01\u8c01\u77e5\u9053\u82cd\u5929\u6709\u773c\uff0c\u5f53\u5e74\u2018\u9739\u96f7\u5251\u2019\u5c55\u4e91\u5929\u4e4b\u5b50\uff0c\u7adf\u7136\u627e\u4f60\u8001\u513f\u7d22\u547d\u6765 \u4e86\uff01\u54c8\u54c8\u2026\u2026\u201d \u5c55\u767d\u8bf4\u5b8c\uff0c\u4e00\u9635\u9707\u5929\u7684\u54c8\u54c8\u72c2\u7b11\uff0c\u53ea\u7b11\u5f97\u201c\u9752\u86a8\u795e\u201d\u91d1\u4e5d\u8138\u4e0a\u9752\u4e00\u9635\u767d\u4e00\u9635\uff0c\u4e0d\u77e5\u662f\u60ca \u8fd8\u662f\u60e7\uff0c\u534a\u5929\u505a\u58f0\u4e0d\u5f97\u2026\u2026 \u201c\u7239\uff01\u201d\u7a81\u7136\u91d1\u5f69\u51e4\u6ee1\u773c\u75db\u6cea\uff0c\u6251\u5230\u201c\u9752\u86a8\u795e\u201d\u91d1\u4e5d\u8eab\u65c1\uff0c\u54ed\u58f0\u8bf4\u9053\uff1a\u201c\u5c55\u5c0f\u4fa0\u8bf4\u7684\u662f \u771f\u7684\u5417\uff1f\u7239\uff01\u5973\u513f\u60f3\u8fd9\u4e00\u5b9a\u662f\u8bef\u4f1a\uff0c\u4e00\u5b9a\u662f\u6709\u4eba\u4ece\u4e2d\u6311\u62e8\u79bb\u95f4\uff0c\u5973\u513f\u76f8\u4fe1\u7239\u662f\u597d\u4eba\uff0c\u4e0d\u4f1a\u5bb3 \u6b7b\u7ed3\u4e49\u76df\u5144\uff01\u7239\uff01\u60a8\u5feb\u5f53\u7740\u5c55\u5c0f\u4fa0\u7684\u9762\u628a\u8bdd\u8bf4\u660e\u767d\uff0c\u89e3\u91ca\u5f00\u8fd9\u8bef\u4f1a\uff0c\u7239\uff0c\u60a8\u8bf4\uff0c\u60a8\u8bf4\u554a\uff01\u201d \u2014\u3002\u201d\u2019 \u201c\u9752\u86a8\u795e\u201d\u91d1\u4e5d\u671b\u7740\u60b2\u54ed\u503e\u8bc9\u7684\u5973\u513f\uff0c\u8138\u8272\u77ac\u606f\u6570\u53d8\uff0c\u7a81\u7136\u53cc\u775b\u4e00\u77aa\uff0c\u773c\u9732\u51f6\u5149\uff0c\u671b\u7740 \u5c55\u767d\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u4f60\u5c0f\u5b50\u8bf4\u7684\u4e00\u70b9\u4e0d\u9519\uff01\u5c55\u4e91\u5929\u7684\u786e\u662f\u88ab\u8001\u592b\u6740\u6b7b\uff01\u4f46\u5e76\u4e0d\u50cf\u4f60\u5c0f\u5b50\u6240\u60f3 \u7684\u90a3\u6837\uff0c\u56e0\u4e3a\u4f60\u7238\u7238\u662f\u4e00\u4e2a\u6076\u9b54\uff0c\u6b3a\u4fae\u538b\u8feb\u6211\u4eec\u5144\u5f1f\u516d\u4eba\uff0c\u4f7f\u6211\u4eec\u5144\u5f1f\u516d\u4eba\u5fcd\u65e0\u53ef\u5fcd\uff0c\u624d\u5408 \u624b\u628a\u4ed6\u6740\u6b7b\uff0c\u662f\u4ed6\u5148\u65e0\u76df\u5144\u4e4b\u60c5\uff0c\u6211\u516d\u4eba\u624d\u65e0\u76df\u5f1f\u4e4b\u4e49\uff0c\u800c\u4e14\u516c\u5e73\u51b3\u6597\uff0c\u80dc\u5b58\u8d25\u5b8c\uff0c\u6beb\u4e0d\u4e3a \u8fc7\uff01\u201d \u5c55\u767d\u542c\u81f3\u6b64\u5904\uff0c\u76ee\u773a\u7686\u88c2\uff0c\u4f46\u4ecd\u54ac\u7259\u5f3a\u5fcd\u7740\uff0c\u4e0d\u4f7f\u6012\u706b\u7206\u53d1\uff0c\u56e0\u4e3a\u4ed6\u5fc3\u4e2d\u660e\u767d\uff0c\u80fd\u4e0d\u80fd \u4e3a\u7236\u62a5\u4ec7\uff0c\u5168\u5728\u4eca\u591c\u4e00\u6218\uff0c\u51b3\u4e0d\u80fd\u56e0\u51b2\u52a8\u66b4\u8e81\uff0c\u800c\u81f4\u529f\u8d25\u5782\u6210\uff0e\uff0e\uff0e\uff0c\uff0e\uff0e \u4f46\u91d1\u5f69\u51e4\u5374\u5df2\u60b2\u547c\u4e00\u58f0\uff1a\u201c\u4e0d\uff01\u8fd9\u4e0d\u662f\u771f\u7684\uff01\u2026\u2026\u201d \u5979\u660f\u5012\u5728\u201c\u9752\u86a8\u795e\u201d\u8f6e\u6905\u4e4b\u524d\u3002\u6709\u4eba\u8bf4\u201c\u7537\u4eba\u662f\u4e3a\u4e8b\u4e1a\u6d3b\u7740\uff0c\u800c\u5973\u4eba\u5374\u662f\u4e3a\u7231\u6d3b\u7740\u201d\uff0c \u91d1\u5f69\u51e4\u5e7c\u5931\u6148\u6bcd\uff0c\u7236\u4eb2\u5728\u5979\u5fc3\u76ee\u4e2d\u662f\u4e00\u5ea7\u201c\u6148\u7231\u4e4b\u795e\u201d\u7684\u5076\u50cf\uff0c\u8fd9\u5076\u50cf\u5728\u5979\u5c11\u5973\u7684\u5fc3\u4e2d\u4e00\u76f4 \u88ab\u795f\u62dc\u7740\uff0c\u662f\u4fe1\u6258\u7684\u8c61\u5f81\uff0c\u4e5f\u662f\u8363\u8a89\u7684\u6807\u5e1c\u3002 \u5982\u4eca\uff0c\u8fd9\u5ea7\u5076\u50cf\uff0c\u4e00\u9f50\u5728\u5979\u5c11\u5973\u5fc3\u4e4b\u5723\u6bbf\u4e2d\u5012\u584c\u4e86\uff01 \u4e00\u4e2a\u6148\u7236\uff0c\u4e0d\u662f\u5979\u60f3\u50cf\u4e2d\u7684\u5b8c\u7f8e\u65e0\u7406\uff0c\u5982\u662f\u5176\u4ed6\u7684\u9519\u8bef\uff0c\u5979\u8fd8\u80fd\u81ea\u5df1\u627e\u51fa\u7406\u7531\u6765\u539f\u8c05 \u4ed6\u3002 \u4f46\u90a3\u6148\u7236\u5374\u4e00\u4e0b\u5b50\u53d8\u6210\u6467\u6bc1\u5979\u201c\u604b\u7231\u4e4b\u795e\u201d\u7684\u523d\u5b50\u624b\uff0c\u8fd9\u65e0\u8bba\u5982\u4f55\u662f\u4f7f\u5979\u65e0\u6cd5\u539f\u8c05\u7684\uff0c \u800c\u4e14\uff0c\u5979\u4e5f\u770b\u6e05\u4e86\u8fa9\u6de1\u7684\u8fdc\u666f\u3002 \u4f46\u201c\u9752\u86a8\u795e\u201d\u91d1\u4e5d\u4e0d\u6127\u662f\u94c1\u77f3\u5fc3\u80a0\uff0c\u7adf\u4e0d\u77a5\u7231\u5973\u7684\u60b2\u75db\u660f\u53a5\uff0c\u4ecd\u7136\u9634\u68ee\u5730\u5bf9\u5c55\u767d\u8bf4\u9053\uff1a \u201c\u672c\u6765\u8fd9\u4ef6\u4e8b\u5df2\u9690\u6ca1\u4e86\u5341\u6570\u5e74\uff0c\u4e5f\u65e0\u4eba\u77e5\u9053\u5c55\u4e91\u5929\u5c1a\u6709\u4e00\u4e2a\u513f\u5b50\u7559\u5728\u4e16\u4e0a\uff0c\u4eca\u5929\uff0c\u8bdd\u5df2\u8bf4 \u660e\uff0c\u4f60\u5982\u6df1\u660e\u5927\u4f53\uff0c\u8001\u592b\u4e5f\u4e0d\u4e3a\u5df2\u751a\uff0c\u53ef\u4ee5\u4efb\u4f60\u4e00\u8d70\u4e86\u4e4b\uff0c\u5982\u679c\u4f60\u5c0f\u5b50\u81ea\u4e0d\u91cf\u529b\uff0c\u90a3\u4e5f\u968f \u4f60\uff01\u201d \u201c\u9752\u86a8\u795e\u201d\u91d1\u4e5d\u8bf4\u81f3\u6b64\u5904\uff0c\u53c8\u563f\u563f\u51b7\u7b11\u4e24\u58f0\uff0c\u63a5\u7740\uff1a\u201c\u548c\u8fc7\u8001\u592b\u53ef\u4ee5\u660e\u767d\u544a\u8bc9\u4f60\uff0c\u81ea\u5bfb \u6b7b\u8def\u800c\u5df2\uff01\u2019\u2606 \u5c55\u81ea\u53cc\u773c\u5ffd\u7741\uff0c\u6bd7\u88c2\u6d41\u8840\uff0c\u54ac\u7259\u5207\u9f7f\u9053\uff1a\u201c\u7236\u4ec7\u4e0d\u62a5\uff0c\u4f55\u4e3a\u4eba\u5b50\uff1f\u8001\u5339\u592b\uff0c\u4f60\u65e2\u6709\u80c6\u91cf \u6562\u81ea\u627f\u7f6a\u77ed\uff0c\u5c55\u767d\u4eca\u65e5\u8981\u4f60\u8001\u513f\u7684\u9c9c\u8840\uff0c\u4ee5\u796d\u5148\u7236\u5728\u5929\u4e4b\u7075\uff01\u201d \u8bf4\u7f62\uff0c\u4e22\u4e0b\u5a49\u513f\uff0c\u4e00\u633a\u624b\u4e2d\u201c\u65e0\u60c5\u78a7\u5251\u201d\uff0c\u817e\u8eab\u5c31\u5411\u201c\u9752\u86a8\u795e\u201d\u91d1\u4e5d\u523a\u53bb\uff01 \u5c55\u767d\u631f\u6012\u65bd\u5c55\uff0c\u7b2c\u4e00\u5251\u201d\uff01 \u201c\u98ce\u96f7\u516b\u5251\u201d\u4e0d\u6127\u4e3a\u5251\u6cd5\u4e2d\u4e4b\u81f3\u5c0a\uff0c\u4e00\u62db\u201c\u98ce\u9707\u96f7\u9e23\u201d\uff0c\u53ea\u89c1\u68ee\u68ee\u5251\u6c14\uff0c\u5f25\u5929\u800c\u8d77\uff0c\u5f53 \u771f\u662f\u98ce\u8d77\u4e91\u6d8c\uff0c\u631f\u7740\u9686\u9686\u96f7\u9e23\u4e4b\u58f0\uff0c\u201c\u65e0\u60c5\u78a7\u5251\u201d\u5bd2\u5149\u5982\u7ec3\uff0c\u6570\u9053\u98de\u8679\u4e00\u822c\uff0c\u731b\u5411\u201c\u9752\u65cf \u795e\u201d\u91d1\u4e5d\u524d\u80f8\u8981\u5bb3\u7f69\u4e0b\uff01 \u201c\u9752\u86a8\u795e\u2019\u2019\u91d1\u4e5d\u4e00\u89c1\u5c55\u767d\u5251\u62db\u5a01\u52bf\uff0c\u7adf\u7136\u8d85\u4e4e\u60f3\u8c61\u5730\u9ad8\u5f3a\uff0c\u51e0\u4e4e\u4e0d\u4e0b\u4e8e\u5f53\u5e74\u201c\u9739\u96f7 \u5251\u201d\u5c55\u4e91\u5929\u5a01\u9707\u5927\u6c5f\u5357\u5317\u7684\u201c\u9739\u96f7\u5251\u6cd5\u201d\uff0c\u5fc3\u4e2d\u4e0d\u7531\u4e00\u61d4\uff0c\u614c\u5fd9\u624b\u63a3\u8f6e\u6905\u8f6c\u76d8\uff0c\u5411\u4e00\u4fa7\u75be\u6ed1 \u800c\u51fa\uff01 \u201c\u9752\u86a8\u795e\u201d\u91d1\u4e5d\u53ea\u987e\u81ea\u5df1\u8eb2\u62db\u907f\u654c\uff0c\u53ef\u628a\u660f\u53a5\u5728\u8f6e\u6905\u8fb9\u7684\u5973\u513f\u5fd8\u4e86\uff0c\u4ed6\u8f6e\u6905\u5236\u4f5c\u7cbe\u5de7\uff0c \u7075\u6d3b\u975e\u5e38\uff0c\u56fa\u7136\u95ea\u8fc7\u5c55\u767d\u631f\u96f7\u9706\u4e07\u94a7\u4e4b\u52bf\u4e00\u51fb\uff0c\u4f46\u91d1\u5f69\u51e4\u5374\u81ea\u7136\u5012\u5728\u5730\u4e0a\uff01 \u800c\u5c55\u767d\u5fc3\u5207\u7236\u4ec7\uff0c\u51fa\u62db\u592a\u72e0\uff0c\u201c\u9752\u86a8\u795e\u201d\u91d1\u4e5d\u95ea\u5f00\uff0c\u4ecd\u7136\u5411\u524d\u51b2\u53bb\uff0c\u98ce\u8fc5\u75be\u96f7\u4f3c\u7684\u5251\u5149 \u5374\u5df2\u5411\u5012\u5728\u5730\u4e0b\u7684\u91d1\u5f69\u51e4 \u523a\u53bb\uff01 \u201c\u9752\u86a8\u795e\u201d\u91d1\u4e5d\u5fc3\u80a0\u7eb5\u662f\u94c1\u6253\u7684\uff0c\u89c1\u5230\u552f\u4e00\u7231\u5973\u5c06\u8981\u4f24\u5728\u5c55\u767d\u5251\u4e0b\uff0c\u4e5f\u4e0d\u7531\u9ad8\u58f0\u6025\u547c\uff1a \u201c\u4e0d\u8981\u4f24\u4e86\u6211\u7684\u5973\u513f\uff01\u2026\u2026\u201d \u5c55\u81ea\u4f3c\u4e5f\u60ca\u89c9\uff0c\u89c1\u81ea\u5df1\u62db\u75b2\u529b\u731b\uff0c\u541e\u5410\u5251\u5c16\u5c06\u8981\u5212\u53ca\u4f0f\u5012\u5728\u5730\u7684\u91d1\u5f69\u51e4\u8eab\u4e0a\uff0c\u4e0b\u610f\u8bc6\u5730 \u5fc3\u89c9\u4e0d\u5fcd\uff0c\u6025\u6253\u5343\u65a4\u5760\uff0c\u64a4\u5251\u6536\u62db \u4f46\u7ad9\u5728\u201c\u9752\u86a8\u795e\u201d\u91d1\u4e5d\u8eab\u540e\u7684\u516d\u540d\u767d\u8863\u5c0f\u7ae5\uff0c\u5374\u7ecf\u8fc7\u4e25\u683c\u7684\u8bad\u7ec3\uff0c\u4e14\u5e74\u5e7c\u5fc3\u5730\u5355\u7eaf\uff0c\u96be \u4ee5\u5224\u65ad\u6c5f\u6e56\u4e0a\u662f\u662f\u975e\u975e\uff0c\u4e00\u5fc3\u5fe0\u4e8e\u4e3b\u4eba\uff0c\u89c1\u5c55\u767d\u5251\u5230\uff0c\u7ade\u4e0d\u7ea6\u800c\u540c\u5730\uff0c\u94f6\u5251\u51fa\u9798\uff0c\u629b\u5251\u62db \u8d77\uff0c\u516d\u67c4\u94f6\u5251\uff0c\u5f62\u6210\u4e00\u62db\u201c\u5f25\u653e\u516d\u5408\u201d\uff0c\u51e0\u5982\u4e00\u7247\u94f6\u8272\u5251\u5899\uff0c\u9f50\u5411\u5c55\u767d\u6765\u52bf\u5c01\u53bb\uff01 \u201c\u53ee\uff01\u53ee\uff01\u2026\uff0c\uff0e\uff0e\u201d \u51e0\u58f0\u60a6\u8033\u91d1\u97f3\uff0c\u51e0\u5982\u8f7b\u6447\u7684\u94f6\u94c3\uff0c\u516d\u652f\u94f6\u5251\uff0c\u9f50\u88ab\u5c55\u767d\u4e00\u5251\u8361\u5f00\uff0c\u516d\u540d\u767d\u8863\u5c0f\u7ae5\u53ea\u611f\u864e \u53e3\u53d1\u70ed\uff0c\u624b\u4e2d\u94f6\u5251\u51e0\u4e4e\u4e00\u9f50\u8131\u624b\u98de\u53bb\uff01 \u4f46\u5c55\u767d\u5e76\u672a\u8ddf\u8e2a\u8fdb\u62db\uff0c\u800c\u662f\u98d8\u8eab\u540e\u9000\uff0c\u5c55\u767d\u5fc3\u5730\u5fe0\u539a\uff0c\u867d\u5fc3\u5207\u7236\u4ec7\uff0c\u6025\u6012\u653b\u5fc3\u4e4b\u4e0b\uff0c\u4ecd \u4e0d\u5931\u4ec1\u6148\u4e4b\u98ce\uff0c\u4ed6\u65e2\u4e0d\u613f\u4f24\u7740\u5012\u5730\u660f\u53a5\u7684\u91d1\u5f69\u51e4\uff0c\u4e5f\u4e0d\u613f\u6740\u5bb3\u516d\u4e2a\u79ef\u5e74\u5e7c\u7ae5\uff01 \u53ef\u662f\uff0c\u4ed6\u7684\u654c\u4eba\u5e76\u6ca1\u6709\u50cf\u4ed6\u4e00\u822c\u4ec1\u6148\uff0c\u5728\u4ed6\u98d8\u8eab\u540e\u9000\u4e4b\u9645\uff0c\u201c\u9752\u86a8\u795e\u201d\u91d1\u4e5d\u4e00\u626c\u624b\uff0c\u6253 \u51fa\u4e00\u84ec\u201c\u9752\u86a8\u9556\u201d\u6765\uff01 \u201c\u9752\u86a8\u795e\u201d\u91d1\u4e5d\u4e00\u751f\u4ed7\u7740\u201c\u9752\u86a8\u9556\u201d\u6210\u540d\uff0c\u665a\u5e74\u56e0\u8bef\u670d\u836f\u7269\uff0c\u7ec3\u529f\u5c94\u6c14\uff0c\u53cc\u817f\u53d8\u6210\u6b8b \u5e9f\uff0c\u5bf9\u201c\u9752\u86a8\u9556\u201d\u66f4\u662f\u52a0\u7d27\u82e6\u7ec3\uff0c\u53ef\u4ee5\u8bf4\u5230\u4e86\u72ec\u6b65\u5929\u4e0b\u7684\u5883\u754c\uff0c\u6ee1\u628a\u201c\u9752\u86a8\u9556\u201d\u51fa\u624b\uff0c\u867d\u7136 \u540c\u662f\u7528\u7684\u201c\u7792\u5929\u8fc7\u6d77\u201d\u624b\u6cd5\uff0c\u5374\u8981\u6bd4\u4ed6\u7684\u513f\u5b50\u201c\u7965\u9e9f\u516c\u5b50\u201d\u4ee5\u53ca\u4ed6\u7684\u5f92\u5f1f\u201c\u5c0f\u9752\u86a8\u201d\u5b5f\u5982\u840d \u8981\u9ad8\u51fa\u591a\u591a\u4e86\uff01 \u5c55\u767d\u8eab\u5f62\u672b\u7a33\uff0c\u53ea\u89c1\u6ee1\u7a7a\u9752\u5f71\uff0c\u75be\u5578\u7834\u7a7a\uff0c\u5411\u7740\u81ea\u5df1\u5468\u8eab\u7f69\u6765\uff0c\u5927\u559d\u4e00\u58f0\uff0c\u628a\u201c\u65e0\u60c5\u78a7 \u5251\u201d\u821e\u6210\u4e00\u9762\u5251\u5899\uff0c\u62a4\u4f4f\u5468\u8eab\u8981\u5bb3\uff01 \u201c\u9563\uff01\u6323\uff01\u2026\u2026\u201d\u5982\u9e23\u7434\u4e71\u5f39\uff0c\u201c\u9752\u86a8\u9556\u201d\u6ee1\u7a7a\u6a2a\u98de\uff0c\u5747\u88ab\u5c55\u767d\u78d5\u51fa\u5708\u5916\uff01 \u4f46\u201c\u9752\u86a8\u795e\u201d\u4e0d\u6127\u662f\u201c\u9752\u86a8\u9556\u201d\u7684\u884c\u5bb6\uff0c\u5728\u5c55\u767d\u6325\u5251\u628a\u6ee1\u7a7a\u7684\u201c\u9752\u86a8\u9556\u201d\u78d5\u98de\u4e4b\u9645\uff0c\u4ed6 \u53c8\u5728\u6905\u8fb9\u7684\u76ae\u515c\u4e2d\u6293\u51fa\u4e00\u628a\u201c\u9752\u86a8\u9556\u201d\u6765\uff0c\u7528\u624b\u6307\u5f39\u51fa\u4e00\u679a\uff0c\u76f4\u5411\u5730\u4e0b\u5f39\u53bb\uff01 \u5c55\u767d\u6b63\u4e0d\u77e5\u4ed6\u662f\u4f55\u7528\u610f\uff0c\u201c\u94ee\u201d\u7684\u4e00\u58f0\uff0c\u5982\u9ec4\u8702\u79bb\u5de2\uff0c\u90a3\u4e00\u679a\u201c\u9752\u86a8\u9556\u201d\u5df2\u7531\u5730\u4e0a\u5f39 \u8d77\uff0c\u5212\u534a\u4e2a\u5706\u5f26\u98de\u65cb\u7740\u5411\u5c55\u767d\u4e0b\u8179\u8df3\u81f3\uff01 \u5c55\u767d\u5fc3\u60f3\uff1a\u4f60\u6ee1\u628a\u91d1\u94b1\uff0c\u5c1a\u4e0d\u80fd\u5948\u4f55\u6211\uff0c\u4e00\u679a\u91d1\u94b1\u53c8\u6709\u4f55\u7528\u2026\u2026 \u8c01\u77e5\u4ed6\u5ff5\u5934\u5c1a\u672a\u8f6c\u5b8c\uff0c\u90a3\u4ece\u5730\u4e0b\u5f39\u8d77\u7684\u4e00\u679a\u201c\u9752\u86a8\u9556\u201d\uff0c\u5df2\u8df3\u8fd1\u4ed6\u7684\u5c0f\u8179\u524d\uff0c\u4ed6\u672c\u80fd\u5730 \u7528\u5251\u4e00\u6321\uff01 \u201c\u53ee\uff01\u201d \u4e00\u58f0\u8106\u9e23\uff0c\u90a3\u679a\u201c\u9752\u86a8\u9556\u201d\u7ed5\u4e86\u4e00\u4e2a\u5708\u5b50\uff0c\u53c8\u5411\u4ed6\u7684\u817f\u4e0a\u6253\u5230\uff01 \u5c55\u767d\u5413\u4e86\u4e00\u8df3\uff0c\u6025\u5fd9\u6536\u817f\u8f6c\u8eab\uff0c\u8eb2\u5f97\u518d\u5feb\uff0c\u90a3\u679a\u201c\u9752\u86a8\u9556\u201d\u4ecd\u7136\u201c\u968f\u201d\u7684\u4e00\u58f0\u5fae\u54cd\uff0c\u628a \u4ed6\u7684\u88e4\u7ba1\u5272\u7834\u4e86\u4e00\u6761\u53e3\u5b50\uff01 \u6240\u5e78\u672a\u4f24\u5230\u808c\u8089\uff0c\u4f46\u5df2\u628a\u5c55\u767d\u5413\u51fa\u4e00\u628a\u51b7\u6c57\u6765\uff0c\u56e0\u4e3a\u4ed6\u77e5\u9053\u8fd9\u201c\u9752\u86a8\u9556\u201d\u90fd\u5582\u6709\u5267\u6bd2\uff0c \u89c1\u8840\u5c01\u5589\uff0c\u5b50\u4e0d\u89c1\u5348\uff0c\u4fbf\u53ef\u81f4\u4eba\u4e8e\u6b7b\uff0c\u4e0a\u6b21\u4ed6\u88ab\u5b5f\u5982\u840d\u6253\u4e2d\u4e86\u4e00\u679a\uff0c\u591a\u4e8f\u91d1\u5f69\u51e4\u9002\u65f6\u7ed9\u4ed6\u670d \u4e0b\u89e3\u836f\uff0c\u624d\u4fdd\u65e0\u5dee\uff0c\u5982\u4eca\u81ea\u5df1\u8eab\u4efd\u5df2\u660e\uff0c\u53ef\u4e0d\u80fd\u518d\u5927\u610f\uff0c\u5982\u679c\u518d\u53d7\u4f24\u53ef\u5c31\u5f97\u4e0d\u5230\u89e3\u836f\u4e86\u3002 \u201c\u9752\u86a8\u795e\u201d\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u8fd9\u662f\u8001\u592b\u7684\u2018\u9752\u86a8\u62dc\u5bff\u2019\uff0c\u65bd\u5c55\u4e4b\u4e0b\uff0c\u4f60\u5c0f\u5b50\u5df2\u5e94\u4ed8\u4e0d\u4e86\uff0c \u5982\u679c\u8001\u592b\u66f4\u5389\u5bb3\u7684\u6740\u624b\u2018\u9752\u86a8\u6ee1\u5858\u2019\u65bd\u51fa\u6765\uff0c\u90a3\u5c31\u662f\u4f60\u5c0f\u5b50\u5bff\u7ec8\u6b63\u5bdd\u4e4b\u65f6\uff01\u201d \u8bf4\u7f62\u201c\u6323\u201d\u7684\u4e00\u58f0\uff0c\u53c8\u5c48\u6307\u5f39\u5728\u5730\u4e0b\u4e00\u679a\u201c\u9752\u86a8\u9556\u201d\uff0c\u5c55\u767d\u5df2\u77e5\u9053\u5389\u5bb3\uff0c\u4e0d\u6562\u518d\u7528\u5251\u53bb \u63a5\uff0c\u5f85\u4e00\u9053\u9752\u5f71\u98d8\u65cb\u800c\u81f3\u65f6\uff0c\u53ea\u6709\u95ea\u8eab\u8eb2\u8fc7\u3002 \u53ef\u662f\uff0c\u4e0d\u7b49\u4ed6\u8eb2\u8fc7\u7b2c\u4e00\u679a\uff0c\u201c\u9752\u86a8\u795e\u201d\u7b2c\u4e8c\u679a\u5df2\u51fa\u624b\uff0c\u63a5\u7740\u4e00\u679a\u4e00\u679a\u5730\u8fde\u4e32\u5f39\u6765\uff0c\u90fd\u662f \u7531\u5730\u4e0b\u53cd\u8df3\u800c\u81f3\uff0c\u50cf\u8fd9\u79cd\u4ece\u5730\u9762\u4e0a\u53cd\u5c04\u2014\u4ea1\u6765\u7684\u6697\u5668\uff0c\u4e71\u98de\u4e71\u8fdb\uff0c\u800c\u4e14\u6a2a\u98d8\u659c\u63a0\uff0c\u8981\u6bd4\u76f4\u63a5 \u6253\u6765\u7684\u96be\u8eb2\u591a\u4e86\uff0c\u5239\u65f6\u628a\u5c55\u767d\u95f9\u4e86\u4e2a\u624b\u5fd9\u811a\u4e71\uff01 \u5c55\u767d\u4e00\u8fb9\u8fde\u8e66\u5e26\u8df3\u8eb2\u95ea\u90a3\u4e9b\u6a2a\u98de\u659c\u63a0\u7684\u201c\u9752\u86a8\u9556\u201d\uff0c\u4e00\u8fb9\u5fc3\u4e2d\u6697\u6697\u7126\u6025\uff0c\u6697\u4ed8\u8fd9\u6837\u957f\u4e45 \u4e0b\u53bb\uff0c\u81ea\u5df1\u4e0d\u88ab\u6253\u6b7b\uff0c\u4e5f\u8981\u7d2f\u6b7b\uff0c\u83ab\u4e0d\u5982\u62fc\u547d\u51b2\u8fd1\u8001\u8d3c\u8eab\u8fb9\uff0c\u597d\u4e92\u4e5f\u8981\u8ddf\u8001\u8d3c\u4e00\u62fc\uff0c\u7eb5\u7136\u843d \u4e2a\u540c\u5f52\u4e8e\u5c3d\uff0c\u4e5f\u5728\u6240\u4e0d\u60dc\u2026\u2026 \u5c55\u767d\u51b3\u5fc3\u5df2\u5b9a\uff0c\u7acb\u523b\u8fdb\u8df3\u7740\u5411\u201c\u9752\u86a8\u795e\u201d\u63a5\u8fd1\uff0c\u4f46\u201c\u9752\u86a8\u795e\u201d\u91d1\u4e5d\u70ac\u76ee\u4e00\u8f6c\uff0c\u9634\u68ee\u7b11 \u9053\uff1a\u201c\u7528\u4e0d\u5230\u6789\u8d39\u5fc3\u673a\uff0c\u8fde\u4f60\u7236\u4eb2\u90fd\u9003\u4e0d\u8fc7\u8001\u592b\u7684\u624b\u6cd5\uff0c\u4f60\u5c0f\u5b50\u8fd8\u60f3\u6d3b\u5417\uff1f\u201d \u8bf4\u7740\u6ee1\u628a\u201c\u9752\u86a8\u9556\u201d\u5411\u5730\u4e0b\u4e00\u6454\uff0c\u53ea\u89c1\u6ee1\u5730\u9752\u5f71\u56db\u5c04\u6a2a\u98de\uff0c\u76f4\u5411\u5c55\u767d\u5468\u8eab\u91cd\u7a74\u88ad\u81f3\uff0c\u5c55 \u767d\u5927\u60ca\u201c\u9752\u86a8\u795e\u201d\u72c2\u7b11\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u2018\u9752\u86a8\u6ee1\u5858\u2019\u6765\u4e3a\u4f60\u5c0f\u5b50\u9001\u7ec8\u4e86\u2026\u2026\u201d \u7b2c\u4e09\u5341\u56db\u7ae0\u3000\u7f8e\u5999\u7edd\u4f26\u5e03\u8863\u5973>> \u53e4\u9f99\u300a\u5251\u5ba2\u884c\u300b \u7b2c\u4e09\u5341\u56db\u7ae0\u3000\u7f8e\u5999\u7edd\u4f26\u5e03\u8863\u5973 \u5c55\u767d\u611f\u5230\u817f\u3001\u81c2\u7b49\u5904\u51c9\u8fa3\u8fa3\u5212\u808c\u800c\u8fc7\uff0c\u5fc3\u4e2d\u4e00\u5bd2\uff0c\u8eab\u4e0a\u5df2\u6709\u6570\u5904\u88ab\u201c\u9752\u86a8\u9556\u201d\u6253\u4e2d\uff01 \u5c55\u767d\u5fc3\u4e2d\u6697\u53f9\uff1a\u201c\u5b8c\u4e86\u2026\u2026\u201d \u7a81\u53c8\u60f3\u8d77\u6740\u7236\u8840\u4ec7\uff0c\u5b8c\u5168\u5728\u81ea\u5df1\u8eab\u4e0a\uff0c\u5c82\u80fd\u7518\u5fc3\u5c31\u6b7b\uff1f \u60f3\u5230\u8fd9\u91cc\uff0c\u8fd0\u6c14\u95ed\u7a74\uff0c\u4ee5\u6682\u4f7f\u6bd2\u6c14\u4e0d\u81f4\u4fb5\u5165\u5fc3\u80ba\uff0c\u731b\u63d0\u4e00\u53e3\u771f\u6c14\uff0c\u4e5f\u4e0d\u77e5\u54ea\u91cc\u6765\u7684\u795e \u529b\uff0c\u91cd\u4f24\u4e4b\u4e0b\uff0c\u4ecd\u8ba9\u4ed6\u8dc3\u4e0a\u82b1\u56ed\u7684\u9ad8\u5899\uff01 \u201c\u9752\u86a8\u795e\u201d\u91d1\u4e5d\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5c0f\u8f88\uff01\u4f60\u8fd8\u60f3\u8dd1\u5417\uff1f\u4e2d\u4e86\u8001\u592b\u2018\u9752\u86a8\u9556\u2019\uff0c\u4e0d\u51fa\u4e09\u4e2a\u65f6\u8fb0 \u5fc5\u6b7b\u65e0\u6551\uff01\u201d\u5c55\u767d\u7ad9\u5728\u5899\u5934\uff0c\u8eab\u5f62\u6643\u4e86\u51e0\u6643\uff0c\u53cc\u773c\u53d1\u9ed1\uff0c\u51e0\u4e4e\u53c8\u683d\u4e0b\u5899\u53bb\uff0c\u4ed6\u4fbf\u54ac\u7259\u5f3a\u7a33\u4f4f \u8eab\u5f62\uff0c\u5207\u9f7f\u9a82\u9053\uff1a\u201c\u8001\u8d3c\uff01\u4eca\u591c\u6682\u9976\u4f60\u4e0d\u6b7b\uff0c\u5c0f\u7237\u65e9\u665a\u6709\u627e\u4f60\u7b97\u6e05\u8840\u5e10\u7684\u4e00\u5929\uff01\u2026\u2026\u201d \u8bf4\u7f62\uff0c\u7ffb\u8eab\u5411\u5899\u5916\u7a9c\u53bb\uff01 \u201c\u9752\u86a8\u795e\u201d\u91d1\u4e5d\u5728\u9662\u5b50\u91cc\u5927\u559d\u9053\uff1a\u201c\u4e0d\u8981\u8ba9\u5c0f\u8f88\u8d70\u4e86\uff0c\u7ed9\u6211\u62ff\u4e0b\uff01\u201d \u63a5\u7740\u6570\u58f0\u5389\u53f1C\uff0c\u91d1\u5e9c\u95e8\u4e0b\u6570\u5927\u9ad8\u624b\u817e\u8eab\u8ffd\u6765\u2026\u2026 \u5c55\u767d\u7a9c\u843d\u5899\u5916\uff0c\u5c1a\u542c\u5230\u91d1\u5f69\u51e4\u60e8\u547c\uff1a\u201c\u7239\uff01\u9976\u4e86\u4ed6\u2026\u2026\u201d\u7684\u54c0\u6c42\u58f0\u3002 \u4f46\u4ed6\u600e\u80af\u518d\u843d\u5165\u4ec7\u4eba\u624b\u4e2d\uff0c\u53c8\u77e5\u6709\u4eba\u8ffd\u4e86\u4e0b\u6765\u8df3\u51fa\u5899\u5916\uff0c\u54ac\u7259\u5f3a\u5fcd\u4efb\u6ee1\u8eab\u9178\u3001\u75db\u3001\u8f6f\u3001 \u9ebb\u4e4b\u611f\uff0c\u5fd8\u547d\u5730\u5411\u524d\u9003\u53bb\u2026\u2026 \u6b64\u65f6\u5df2\u662f\u534a\u591c\uff0c\u5357\u4eac\u57ce\u5df2\u9677\u5165\u4e86\u72c2\u6b22\u7684\u9ad8\u6f6e\uff0c\u8fd9\u662f\u4e0a\u5143\u8282\u6700\u70ed\u95f9\u7684\u8282\u76ee\uff0c\u201c\u653e\u706b\u7130\u201d\u53ca \u201c\u653e\u82b1\u706f\u201d\uff01 \u5f69\u706f\u5f0f\u6837\u7e41\u591a\uff0c\u989c\u8272\u5404\u5f02\uff0c\u5728\u6f6e\u6c34\u4f3c\u7684\u4eba\u5934\u4e0a\uff0c\u7ed3\u6210\u4e86\u4e00\u6761\u706b\u9f99\uff0c\u4eba\u7684\u8138\u4e0a\u6620\u7740\u706f\u5149\uff0c \u6709\u7684\u53d8\u7ea2\uff0c\u6709\u7684\u53d8\u7eff\uff0c\u6709\u7684\u4e94\u989c\u516d\u8272\u5730\u53d8\u5e7b\u7740\uff0c\u52a0\u4e0a\u72c2\u70ed\u7684\u8138\u4e0a\u5192\u7740\u6cb9\u5149\u6c57\u6c14\uff0c\u770b\u8d77\u6765\u6709\u70b9 \u5947\u5f62\u602a\u72b6\uff01 \u800c\u4e14\u8fd8\u6709\u5316\u88c5\u6210\u201c\u5927\u5934\u9b3c\u201d\u6e38\u884c\u7684\u884c\u5217\uff0c\u4e5f\u5939\u6742\u5728\u4eba\u7fa4\u4e4b\u4e2d\uff0c\u66f4\u663e\u5f97\u5947\u7279\uff01 \u603b\u4e4b\uff0c\u8fd9\u662f\u4e0a\u5143\u4f73\u8282\u72c2\u6b22\u7684\u9ad8\u6f6e\uff0c\u6574\u4e2a\u5357\u4eac\u57ce\u90fd\u6cb8\u817e\u4e86\uff0c\u4f46\u4e0d\u77e5\u600e\u7684\uff0c\u8fd9\u72c2\u6b22\u7684\u573a\u9762\uff0c \u53eb\u4eba\u770b\u4e86\u5374\u6709\u7740\u672b\u65e5\u6765\u4e34\u7684\u611f\u89c9\uff01 \u4f46\u8fd9\u5374\u7ed9\u4e86\u5c55\u767d\u9003\u4ea1\u7684\u65b9\u4fbf\uff0c\u4ed6\u6df7\u6742\u5728\u6c79\u6d8c\u7684\u72c2\u6b22\u4eba\u6d41\u4e2d\uff0c\u987a\u7740\u4eba\u6d41\u5411\u57ce\u5916\u6d8c\u53bb\uff0c\u90a3\u4e9b \u8ffd\u62ff\u4ed6\u7684\u91d1\u5e9c\u9ad8\u624b\uff0c\u7adf\u7136\u65e0\u6cd5\u770b\u6e05\u4ed6\u9003\u8d70\u7684\u65b9\u5411\u2026\u2026 \u5c55\u81ea\u5468\u8eab\u662f\u8840\uff0c\u201c\u9752\u86a8\u9556\u201d\u4e0a\u7684\u5267\u6bd2\u5df2\u6162\u6162\u884c\u5f00\uff0c\u4ed6\u51e0\u4e4e\u9677\u5165\u660f\u8ff7\u72b6\u6001\uff0c\u5e78\u56fa\u4eba\u6324\u4eba\u7684 \u5173\u7cfb\u624d\u672b\u683d\u5012\uff0c\u53ea\u8ddf\u8dea\u907f\u8d85\uff0c\u4e0d\u7531\u81ea\u4e3b\u5730\u968f\u7740\u62e5\u6324\u7684\u4eba\u7fa4\u5411\u524d\u8d70\u53bb\u2026\u2026 \u8ffd\u62ff\u4ed6\u7684\u91d1\u5e9c\u9ad8\u624b\u6570\u6b21\u9669\u4e9b\u6293\u4f4f\u4ed6\uff0c\u90fd\u7531\u4e8e\u4eba\u591a\u62e5\u6324\u4e0d\u582a\uff0c\u53c8\u4f7f\u4ed6\u9003\u8131\u2026\u3002\xb7 \u5c31\u8fd9\u6837\uff0c\u6df7\u4e71\u4e2d\u5c55\u767d\u968f\u7740\u4eba\u6d41\u62e5\u51fa\uff0c\u6765\u5230\u79e6\u6dee\u6cb3\u8fb9\u3002 \u4eba\u7fa4\u53d1\u4e86\u4e00\u58f0\u558a\uff0c\u4e00\u9f50\u5954\u5411\u6cb3\u8fb9\uff0c\u4e89\u5148\u6050\u540e\u5730\u628a\u624b\u4e2d\u7684\u5f69\u706f\u653e\u8fdb\u6cb3\u4e2d\u53bb\uff0c\u76cf\u76cf\u5f69\u706f\uff0c\u7acb \u5373\u987a\u7740\u6cb3\u6c34\u6d41\u53bb\uff0c\u5343\u4e07\u76cf\u4e0d\u540c\u5f62\u5f0f\u3001\u4e0d\u540c\u8272\u5f69\u7684\u706f\uff0c\u5728\u6cb3\u9762\u4e0a\u660e\u660e\u706d\u706d\uff0c\u987f\u65f6\u6210\u4e3a\u4e00\u7247\u5947 \u666f\uff01 \u4eba\u4eec\u6b64\u65f6\u5374\u9e26\u7559\u65e0\u58f0\uff0c\u671b\u7740\u6cb3\u6c34\u4e2d\u5404\u81ea\u653e\u7684\u706f\uff0c\u9ed8\u9ed8\u6790\u7977\u8d77\u6765\uff0c\u5404\u81ea\u5e0c\u671b\u7740\u81ea\u5df1\u7684\u706f\u4e0d \u8981\u7ffb\u8986\uff0c\u4e0d\u8981\u7184\u706d\uff0c\u6700\u597d\u662f\u968f\u7740\u6cb3\u6c34\u6d41\u5411\u9065\u8fdc\uff0c\u9065\u8fdc\u2026\u2026 \u636e\u8bf4\u90a3\u653e\u5165\u6cb3\u6c34\u7684\u706f\uff0c\u6d41\u5f97\u8d8a\u8fdc\uff0c\u798f\u5206\u8d8a\u957f\u2026\uff0e\uff0e\uff0e \u7531\u4e8e\u6cb3\u5cb8\u5bbd\u5e7f\uff0c\u4eba\u4eec\u5206\u6563\u5f00\u4e86\uff0c\u53c8\u4e0d\u518d\u75af\u72c2\u5730\u62e5\u6324\uff0c\u5931\u53bb\u4e86\u631f\u6301\u7684\u529b\u91cf\uff0c\u5c55\u767d\u7acb\u523b\u660f\u5012 \u5728\u6cb3\u8fb9\u2014\u2026\u2019 \u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u4e00\u9635\u5f7b\u9aa8\u7684\u5de8\u75db\uff0c\u628a\u5c55\u767d\u75db\u9192\uff0c\u4ed6\u7741\u773c\u4e00\u770b\uff0c\u81ea\u5df1\u7ade\u8eba\u5728\u4e00\u4e2a\u7834\u5e99\u7684 \u5927\u6bbf\u4e2d\uff01 \u8fd9\u5ea7\u5927\u6bbf\u5c4b\u9876\u6709\u6570\u5904\u900f\u7a7a\uff0c\u80fd\u591f\u4ece\u7834\u6d1e\u5904\u671b\u5230\u5929\u4e0a\u7684\u7e41\u5c4b\uff0c\u53ef\u89c1\u8fd8\u662f\u5728\u591c\u95f4\u3002 \u53ea\u89c1\u58c1\u5012\u6881\u9893\uff0c\u8fd9\u95f4\u5927\u6bbf\u7834\u70c2\u5f97\u53ef\u4ee5\uff0c\u7acb\u5728\u4f9b\u6848\u4e0a\u7684\u795e\u50cf\uff0c\u6ce5\u91d1\u6591\u843d\uff0c\u50cf\u4e00\u5177\u5177\u7684\u50f5 \u5c38\uff0c\u770b\u8d77\u6765\u6709\u70b9\u9634\u68ee\u5413\u4eba\uff01 \u56db\u58c1\u5012\u4e86\u4e09\u58c1\uff0c\u5269\u4e0b\u7684\u4e00\u58c1\u58c1\u89d2\u86db\u7f51\u60ac\u6302\uff0c\u5c18\u7070\u5bf8\u79ef\uff0c\u4f46\u5c55\u767d\u7761\u5367\u7684\u4e00\u5f20\u4f9b\u684c\u4e0a\uff0c\u5374\u6253 \u626b\u5f97\u5f88\u5e72\u51c0\uff0c\u800c\u4e14\uff0c\u5728\u4ed6\u8eab\u4e0b\u8fd8\u94fa\u4e0a\u4e86\u539a\u539a\u7684\u5e72\u8349\uff0c\u8eab\u4e0a\u76d6\u4e86\u4e00\u5e8a\u539a\u539a\u7684\u68c9\u88ab\uff0c\u56e0\u6b64\uff0c\u4ed6\u75db \u9192\u4e4b\u540e\uff0c\u7ade\u7565\u6709\u8212\u9002\u4e4b\u611f\uff01 \u4f46\u7b49\u4ed6\u795e\u667a\u6e05\u9192\uff0c\u6389\u5934\u4e00\u770b\u4e0d\u7531\u628a\u4ed6\u5413\u5f97\u5dee\u4e00\u70b9\u6ca1\u6709\u53eb\u51fa\u58f0\u6765\uff01 \u539f\u6765\u5728\u6848\u5934\u71c3\u4e86\u4e00\u76cf\u534a\u660e\u4e0d\u6697\u7684\u6cb9\u706f\uff0c\u5728\u90a3\u60e8\u6de1\u7684\u5149\u7ebf\u4e4b\u4e0b\uff0c\u89c1\u4e00\u4e2a\u9752\u8138\u7ea2\u53d1\u7684\u602a\u4eba\uff0c \u6b63\u624b\u62ff\u7740\u4e00\u67c4\u660e\u4eae\u7684\u89e3\u8155\u5c16\u5200\uff0c\u76f4\u5411\u4ed6\u8eab\u4e0a\u523a\u6765\uff01 \u5c55\u767d\u4e00\u773c\u770b\u5230\u8fd9\u5f62\u8c61\uff0c\u5413\u5f97\u5012\u62bd\u4e00\u53e3\u51b7\u6c14\uff0c\u4ee5\u4e3a\u81ea\u5df2\u662f\u843d\u5230\u4ec0\u4e48\u6076\u9b3c\u624b\u91cc\u4e86\uff0c\u624d\u201c\u554a\u201d \u51fa\u4e86\u534a\u58f0\uff0c\u817f\u4e0a\u4e00\u9635\u5f7b\u9aa8\u7684\u5947\u75db\uff0c\u53c8\u628a\u4ed6\u75db\u660f\u4e86\u8fc7\u53bb\u2026\u2026 \u5f85\u4ed6\u7b2c\u4e8c\u6b21\u9192\u6765\uff0c\u60ca\u9b42\u72b9\u5b58\uff0c\u8f6c\u773c\u5411\u56db\u4e0b\u4e00\u770b\uff0c\u4e0d\u7531\u53c8\u628a\u4ed6\u695e\u4f4f\u4e86\uff01 \u773c\u524d\u53c8\u51fa\u73b0\u4e86\u53e6\u5916\u4e00\u4e2a\u602a\u73b0\u8c61\uff0c\u90a3\u9752\u8138\u7ea2\u53d1\u7684\u602a\u4eba\u4e0d\u89c1\u4e86\uff0c\u5728\u4ed6\u8eab\u65c1\u5374\u5750\u7740\u4e00\u4e2a\u5343\u5a07\u767e \u5a9a\u7684\u5c11\u5973\uff01\u6b63\u4ee5\u5173\u5207\u3001\u601c\u60dc\u7684\u795e\u60c5\u671b\u7740\u4ed6\uff01 \u5c55\u767d\u731b\u7136\u62ac\u8eab\u5750\u8d77\uff0c\u60ca\u9053\uff1a\u201c\u6211\u8fd9\u662f\u5728\u4ec0\u4e48\u5730\u65b9\uff1f\u2026\u2026\u201d \u8eab\u4e0a\u4e00\u9635\u5de8\u75db\uff0c\u4f7f\u4ed6\u624d\u5750\u8d77\u4e00\u534a\uff0c\u4e00\u53e5\u8bdd\u672a\u8bf4\u5b8c\uff0c\u53c8\u201c\u54ce\u54df\u201d\u4e00\u58f0\uff0c\u7761\u5012\u4e0b\u53bb\uff01 \u90a3\u7f8e\u4e3d\u7684\u5c11\u5973\u74a8\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u624d\u628a\u4f60\u8eab\u4e0a\u7684\u6697\u5668\u8d77\u51fa\uff0c\u6bd2\u5df2\u5c3d\uff0c\u4f46\u4f24\u53e3\u672a\u6536\uff0c\u4f60\u4e0d \u8981\u4e71\u52a8\uff0c\u4f11\u517b\u4e24\u5929\u5c31\u597d\u4e86\u3002\u201d \u5c55\u767d\u53ea\u89c9\u8fd9\u5c11\u5973\u8bf4\u8bdd\u65f6\uff0c\u5439\u6c14\u5982\u5170\uff0c\u800c\u4e14\u7f8e\u8d28\u5929\u751f\uff0c\u4ed6\u89c1\u8fc7\u7684\u7f8e\u5973\u4e0d\u5c11\uff0c\u5982\u6155\u5bb9\u7ea2\u3001\u5c55 \u5a49\u513f\uff0c\u4ee5\u53ca\u76f4\u7740\u201c\u6c5f\u5357\u7b2c\u4e00\u7f8e\u4eba\u201d\u4e4b\u79f0\u7684\u91d1\u5f69\u51e4\uff0c\u90fd\u662f\u51fa\u7c7b\u62d4\u9769\u7684\u7f8e\u4eba\u80ce\u5b50\uff0c\u4f46\u773c\u524d\u8fd9\u5c11 \u5973\uff0c\u5bb9\u8c8c\u4e4b\u7f8e\u4e0d\u4e0b\u4e8e\u91d1\u5f69\u51e4\uff0c\u949f\u7075\u79c0\u6c14\u4e5f\u4e0d\u4e0b\u4e8e\u5c55\u5a49\u513f\uff0c\u51b7\u8273\u9ad8\u8d35\u66f4\u8d85\u8fc7\u6155\u5bb9\u7ea2\u51e0\u5206\uff0c\u800c\u4e14 \u80f6\u5bb9\u5982\u6625\u82b1\u671d\u9633\uff0c\u7709\u68a2\u773c\u89d2\u4e0d\u5e26\u4e00\u4e1d\u6101\u6028\uff01 \u7ade\u4f3c\u5348\u591c\u91cc\u51fa\u73b0\u4e86\u592a\u9633\uff0c\u5bd2\u51ac\u51b7\u96ea\u91cc\u5f00\u653e\u4e86\u7261\u4e39\uff0c\u90a3\u6e29\u99a8\u7684\u7f8e\uff0c\u7b80\u76f4\u65e0\u6cd5\u5f62\u5bb9\uff01 \u800c\u4e14\uff0c\u5979\u5e03\u8863\u9497\u88d9\uff0c\u4e0d\u65bd\u4e00\u4e9b\u513f\u8102\u7c89\uff0c\u5f53\u771f\u662f\u4e3d\u8d28\u5929\u751f\uff0c\u5c0f\u5bb6\u78a7\u4e3b\u7684\u6c14\u5ea6\uff0c\u5e26\u7740\u5927\u5bb6\u95fa \u8303\u7684\u9ad8\u8d35\uff0c\u9ad8\u8d35\u4fca\u7f8e\u4e4b\u4e2d\uff0c\u5374\u53c8\u4e0d\u5e26\u8c6a\u95e8\u5343\u91d1\u7684\u5bcc\u8d35\u5a07\u6c14\uff01 \u50cf\u8fd9\u6837\u7684\u7f8e\u5973\uff0c\u5c55\u81ea\u771f\u662f\u5e73\u751f\u4ec5\u89c1\uff01 \u5c55\u767d\u4e0d\u7531\u4e00\u6014\u518d\u6014\uff0c\u51fa\u4e86\u4f1a\u5b50\u795e\uff0c\u53cd\u800c\u6389\u5934\u56de\u987e\uff0c\u50cf\u662f\u5728\u627e\u5bfb\u4ec0\u4e48\u4e1c\u897f\uff01 \u90a3\u7f8e\u6781\u7684\u5e03\u8863\u5c11\u5973\u60c5\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u627e\u4ec0\u4e48\uff1f\u201d \u5c55\u767d\u832b\u7136\u9053\uff1a\u201c\u65b9\u624d\u6211\u597d\u50cf\u770b\u5230\u4e00\u4e2a\u7ea2\u53d1\u9752\u8138\u4e4b\u4eba\u2026\u2026\u201d \u5e03\u8863\u5c11\u5973\u53c8\u662f\u4e00\u7b11\uff0c\u56de\u624b\u62ff\u8d77\u4e00\u526f\u9762\u5177\u5728\u5c55\u767d\u773c\u524d\u6643\u4e86\u4e24\u6643\u3002 \u5c55\u767d\u7acb\u523b\u604d\u7136\u5927\u609f\uff0c\u539f\u6765\u90a3\u9752\u8138\u7ea2\u53d1\u4e4b\u4eba\uff0c\u7adf\u662f\u5c11\u5973\u624b\u4e2d\u62ff\u7684\u4e00\u526f\u5047\u9762\u53bf\uff0c \u5c55\u767d\u54e6\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u539f\u6765\u90a3\u662f\u59d1\u5a18\u7684\u4e00\u526f\u9762\u5177\uff01\u5509\uff01\u8fd9\u6837\u770b\u6765\uff0c\u5728\u4e0b\u7684\u4e00\u547d\uff0c\u4e5f\u662f\u59d1\u5a18 \u6551\u7684\u4e86\uff1f\u201d \u90a3\u5e03\u8863\u5c11\u5973\u7b11\u7740\u70b9\u4e86\u70b9\u5934\uff0c \u5c55\u767d\u9053\uff1a\u201c\u4e0d\u77e5\u59d1\u5a18\u8d35\u59d3\u5927\u540d\uff0c\u5c1a\u8bf7\u89c1\u544a\uff0c\u5728\u4e0b\u65e5\u540e\u4e5f\u597d\u62a5\u7b54\u5927\u6069\uff01\u201d \u90a3\u5e03\u8863\u5c11\u5973\u7b11\u800c\u4e0d\u7b54\uff0c\u53ea\u628a\u90a3\u9752\u9762\u7ea2\u53d1\u7684\u5047\u9762\u5177\uff0c\u53c8\u62ff\u8d77\u6765\u6643\u4e86\u6643\u3002 \u5c55\u767d\u7591\u60d1\u5730\u671b\u7740\u5979\uff0c\u4e0d\u77e5\u662f\u4f55\u7528\u610f\uff0c\u4f46\u795e\u8272\u4e2d\u5145\u6ee1\u4e86\u95ee\u9898\u3002 \u5e03\u8863\u5c11\u5973\u7b11\u9053\uff1a\u201c\u4f60\u731c\u6211\u53eb\u4ec0\u4e48\uff1f\u201d \u5c55\u767d\u7eb3\u95f7\u9053\uff1a\u201c\u59d1\u5a18\u662f\u8ddf\u5728\u4e0b\u8bf4\u7b11\u8bdd\u4e86\uff0c\u4eba\u7684\u540d\u5b57\u600e\u53ef\u4e71\u731c\uff1f\u201d \u5e03\u8863\u5c11\u5973\u660e\u5a9a\u7684\u5927\u773c\u775b\uff0c\u542b\u7740\u6df1\u610f\u5730\u76ef\u7740\u4ed6\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u771f\u4e0d\u77e5\u9053\uff0c\u8fd8\u662f\u6545\u610f\u88c5 \u50bb\uff1f\u201d \u5c55\u767d\u4e00\u618e\uff0c\u53c8\u4ed4\u7ec6\u671b\u4e86\u671b\u90a3\u7f8e\u5f97\u4f7f\u4eba\u660f\u7729\u7684\u9762\u5bb9\uff0c\u52aa\u529b\u641c\u5bfb\u81ea\u5df1\u7684\u8bb0\u5fc6\uff0c\u5b9e\u5728\u6ca1\u6709\u8ddf\u5979 \u4f1a\u8fc7\u9762\uff0c\u4e5f\u4ece\u672a\u542c\u8bf4\u9053\u6c5f\u6e56\u4e0a\u6709\u8fd9\u4e48\u7f8e\u7684\u4e00\u4f4d\u5e26\u7740\u9b3c\u9762\u5177\u7684\u5c11\u5973\uff0c\u6700\u540e\u82e6\u7b11\u7740\u6447\u4e86\u6447\u5934\u9053\uff1a \u201c\u5728\u4e0b\u4ece\u672a\u4e0e\u59d1\u5a18\u4f1a\u8fc7\u9762\uff0c\u800c\u4e14\u4e5f\u6ca1\u542c\u8bf4\u2026\u2026\u201d \u4ed6\u672c\u60f3\u8bf4\uff1a\u201c\u6ca1\u542c\u8bf4\u8fc7\u6c5f\u6e56\u4e0a\u6709\u4f60\u8fd9\u4e48\u4e00\u53f7\uff0c\u201d\u4f46\u6015\u60f3\u8d77\u5979\u7684\u4e0d\u5feb\uff0c\u6545\u6b64\u987f\u4f4f\u672a\u8bf4\u3002 \u5c11\u5973\u53c8\u4e3e\u8d77\u5047\u9762\u5177\u9053\uff1a\u201c\u96be\u9053\u4f60\u770b\u5230\u8fd9\u9762\u5177\uff0c\u8fd8\u4e0d\u77e5\u9053\u6211\u662f\u8c01\u5417\uff1f\u201d \u5c55\u767d\u66f4\u695e\u4f4f\u4e86\uff0c\u4e00\u65f6\u4e4b\u95f4\u7b54\u4e0d\u51fa\u8bdd\u6765\uff0c\u6697\u4ed8\u9053\u2018\u201c\u83ab\u975e\u5979\u5728\u6c5f\u6e56\u4e0a\u771f\u6709\u5f88\u5927\u7684\u540d\u58f0\uff1f\u81ea \u5df1\u5b64\u964b\u5be1\u95fb\uff0c\u624d\u4e0d\u77e5\u9053\u5979\uff1f\u2026\u2026\u201d \u5e03\u8863\u5c11\u5973\u5374\u5a49\u7136\u4e00\u7b11\uff0c\u50cf\u6148\u6bcd\u629a\u6170\u5a74\u513f\u4e00\u6837\u62cd\u4e86\u62cd\u4ed6\u9053\uff1a\u201c\u4e0d\u8981\u8d39\u8111\u7b4b\u60f3\u4e86\uff0c\u65e5\u540e\u4f60\u81ea \u7136\u4f1a\u77e5\u9053\uff0c\u73b0\u5728\u4f60\u4f24\u52bf\u672a\u597d\uff0c\u6211\u867d\u7136\u7ed9\u4f60\u53d6\u51fa\u8eab\u4e0a\u7684\u6697\u5668\uff0c\u53c8\u4e3a\u4f60\u89e3\u4e86\u6697\u5668\u4e0a\u7684\u5267\u6bd2\uff0c\u4f46\u4f60 \u6700\u5c11\u8fd8\u8981\u4f11\u517b\u4e09\u81f3\u4e94\u5929\uff0c\u624d\u80fd\u590d\u5143\uff0c\u73b0\u5728\u4f60\u521a\u82cf\u9192\u8fc7\u6765\uff0c\u6050\u6015\u809a\u5185\u65e9\u5c31\u997f\u4e86\uff0c\u4f60\u5728\u8fd9\u91cc\u7b49 \u7740\uff0c\u6211\u53bb\u7ed9\u4f60\u627e\u5403\u7684\u2026\u2026\u201d \u8bf4\u7f62\uff0c\u4e22\u4e0b\u516d\u679a\u9752\u94dc\u5236\u94b1\u5728\u5c55\u767d\u5934\u8fb9\uff0c\u8eab\u5f62\u4e00\u6643\uff0c\u5feb\u903e\u95ea\u7535\uff0c\u5979\u70af\u5a1c\u7684\u8eab\u8eaf\u604d\u5982\u4e00\u4e1d\u8f7b \u70df\uff0c\u7728\u773c\u6d88\u5931\u4e8e\u7a97\u5916\uff01 \u5c55\u767d\u4e0d\u7531\u6697\u6697\u548b\u820c\uff0c\u5fc3\u8bf4\uff1a\u597d\u5feb\u7684\u8eab\u6cd5\uff01\u4e0d\u8981\u8bf4\u81ea\u5df1\u6bd4\u4e0d\u4e0a\uff0c\u5c31\u8fde\u4ee5\u524d\u89c1\u7684\u201c\u96f7\u5927\u53d4\u201d \u90a3\u7edd\u5feb\u7684\u8eab\u6cd5\uff0c\u4ee5\u53ca\u8fde\u201c\u795e\u9a74\u94c1\u80c6\u201d\u90fd\u5f53\u9762\u5938\u5956\u8fc7\u5a49\u513f\u7684\u8f7b\u7075\u8eab\u6cd5\uff0c\u90fd\u65e0\u6cd5\u4e0e\u5176\u6297\u8861\uff01 \u5c55\u767d\u5fc3\u601d\u7535\u8f6c\uff0c\u770b\u8fd9\u5c11\u5973\u7684\u8eab\u6cd5\uff0c\u6b66\u529f\u4e00\u5b9a\u4e5f\u5f88\u9ad8\u5f3a\uff0c\u4ece\u5979\u8ba9\u81ea\u5df1\u731c\u540d\u5b57\u7684\u4e3e\u52a8\u770b\u6765\uff0c \u5206\u660e\u81ea\u6eba\u540d\u671b\u751a\u5927\uff0c\u4f46\u81ea\u5df1\u5374\u600e\u4e48\u4ece\u672a\u542c\u5230\u8bf4\u8fc7\u5462\uff1f \u5ffd\u7136\uff0c\u4ed6\u7684\u773c\u5149\u53c8\u6295\u5728\u5e03\u8863\u5c11\u5973\u4e34\u53bb\u65f6\uff0c\u4e22\u5728\u4ed6\u5934\u8fb9\u7684\u516d\u679a\u9752\u94dc\u5236\u94b1\u4e0a\uff0c\u4ed6\u4e0d\u770b\u8fd8\u597d\uff0c \u8fd9\u4e00\u770b\u4e0d\u7531\u70ed\u8840\u6cb8\u817e\uff0c\u53cc\u773c\u51e0\u4e4e\u5192\u51fa\u706b\u6765\uff01 \u90a3\u516d\u679a\u9752\u94dc\u5236\u94b1\uff0c\u8ddf\u4ed6\u7236\u4eb2\u4e34\u6b7b\u4e4b\u524d\u4ea4\u7ed9\u4ed6\u516d\u4ef6\u9057\u7269\u4e4b\u4e2d\u90a3\u679a\u9752\u94dc\u5236\u94b1\u4e00\u6a21\u4e00\u6837\uff0c\u6b63\u662f \u201c\u9752\u86a8\u795e\u201d\u91d1\u4e5d\u7684\u72ec\u95e8\u6697\u5668\uff0c\u79f0\u4e3a\u201c\u9752\u86a8\u9556\u201d\u7684\u4e1c\u897f\uff01 \u5c55\u767d\u60f3\u5230\u8fd9\u679a\u9752\u94dc\u5236\u94b1\uff0c\u53ef\u80fd\u5c31\u662f\u90a3\u7f8e\u7edd\u5929\u4eba\u7684\u5e03\u8863\u5c11\u5973\uff0c\u7528\u5c16\u5200\u4ece\u81ea\u5df2\u8eab\u4e0a\u525c\u51fa\u6765 \u7684\uff01\u6697\u60f3\u7236\u4eb2\u4e5f\u662f\u88ab\u8fd9\u79cd\u6b79\u6bd2\u6697\u5668\u6240\u4f24\uff0c\u624d\u9047\u5bb3\u8eab\u6b7b\uff0c\u5982\u4eca\u81ea\u5df2\u53c8\u9669\u4e9b\u6b7b\u5728\u8fd9\u79cd\u6b79\u6bd2\u6697\u5668\u4e4b \u4e0b\uff0c\u82e5\u4e0d\u662f\u8fd9\u4e0d\u77e5\u6765\u5386\u7684\u5c11\u5973\u6240\u6551\uff0c\u6050\u6015\u81ea\u5df1\u4e5f\u6b65\u4e86\u7236\u4eb2\u7684\u540e\u5c18\uff0c\u4e27\u547d\u5728\u8fd9\u679a\u201c\u9752\u86a8\u9556\u201d \u4e0b\uff01 \u5c55\u767d\u60b2\u6124\u4e0d\u5df2\uff0c\u7236\u5b50\u540c\u4ec7\uff0c\u7adf\u51e0\u4e4e\u90fd\u6bc1\u5728\u8fd9\u201c\u9752\u86a8\u9556\u201d\u4e0b\uff0c\u4e0d\u7531\u4f38\u51fa\u624b\u6765\uff0c\u6293\u90a3\u51e0\u6821\u9752 \u94dc\u5236\u94b1\u2026\u2026 \u7a81\u7136\uff0c\u6848\u4e0a\u7684\u6cb9\u706f\u4e00\u95ea\uff0c\u706f\u5149\u6447\u66f3\uff0c\u706d\u800c\u590d\u660e\uff0c\u90a3\u5e03\u8863\u5c11\u5973\u9762\u6234\u5047\u9762\u5177\u5df2\u7ad9\u5728\u5c55\u767d\u7684\u5367 \u94fa\u4e4b\u524d\uff01\u5979\u9762\u5177\u4e5f\u6765\u4e0d\u53ca\u63ed\u4e0b\u8f7b\u559d\u9053\u201c\u4e0d\u8981\u52a8\uff01\u90a3\u5236\u94b1\u4e0a\u542b\u6bd2\u5c1a\u672a\u53bb\u5c3d\uff01\u201d \u5c55\u767d\u95fb\u8a00\uff0c\u53c8\u7f29\u56de\u624b\u6765\uff0c\u90a3\u5c11\u5973\u9053\uff1a\u201c\u90a3\u5236\u94b1\u4e0a\u7684\u542b\u6bd2\uff0c\u8981\u4e09\u65e5\u4ee5\u540e\uff0c\u624d\u80fd\u892a\u5c3d\uff0c\u4eca\u5929 \u624d\u662f\u4e24\u5929\uff0c\u8fd8\u8981\u7b49\u5230\u5929\u660e\u624d\u6ca1\u6709\u5173\u7cfb\u4e86\u3002\u201c \u5c55\u767d\u60ca\u9053\uff1a\u201c\u600e\u4e48\uff0c\u6211\u6765\u6b64\u5df2\u7ecf\u4e24\u5929\u4e86\uff1f\u201d \u5c11\u5973\u5657\u54e7\u4e00\u7b11\u9053\uff1a\u201c\u5341\u4e94\u591c\u5230\u4eca\u5929\u5341\u4e03\u591c\uff0c\u521a\u597d\u6574\u6574\u4e24\u5929\uff0c\u5176\u5b9e\uff0c\u6211\u4e5f\u662f\u592a\u7d27\u5f20\u4e86\uff0c\u8fd9 \u5236\u94b1\u4e0a\u7684\u6bd2\u867d\u7136\u5389\u5bb3\uff0c\u4f46\u4e0d\u89c1\u8840\u662f\u6ca1\u6709\u5173\u7cfb\u7684\uff0c\u6211\u4e0d\u8fc7\u6015\u4f60\u4e0d\u77e5\u9053\uff0c\u62ff\u5728\u624b\u4e2d\u628a\u624b\u6307\u5272\u7834\uff0c \u589e\u6dfb\u9ebb\u70e6\u5c31\u662f\u4e86\u2026\u2026\u201d \u8bf4\u7740\u628a\u624b\u4e2d\u62ff\u6765\u7684\u98df\u7269\uff0c\u653e\u5728\u5c55\u767d\u7684\u9762\u524d\uff0c\u628a\u8138\u4e0a\u7684\u9762\u5177\u4e5f\u6458\u4e0b\uff0c\u53c8\u9053\uff1a\u201c\u5403\u7f62\uff01\u4f60\u6574 \u6574\u4e24\u5929\u6ca1\u6709\u5403\u4e1c\u897f\uff0c\u53ef\u80fd\u65e9\u5df2\u997f\u4e86\uff01\u201d \u5c55\u767d\u89c1\u5979\u62ff\u6765\u7684\u662f\u7528\u7eff\u8377\u53f6\u5305\u7740\u7684\u4e00\u6574\u53ea\u201c\u5357\u4eac\u677f\u9e2d\u201d\u3001\u5341\u6570\u5f20\u8471\u6cb9\u8584\u997c\u3002 \u53ef\u662f\uff0c\u677f\u9e2d\u548c\u6cb9\u997c\u90fd\u662f\u5e72\u7684\uff0c\u5c55\u767d\u5403\u5f97\u592a\u731b\uff0c\u585e\u4e86\u6ee1\u5634\u7ade\u4e00\u65f6\u4e4b\u95f4\u54bd\u4e0d\u4e0b\u809a\u53bb\u4e86\uff0c\u53ea\u564e \u4e86\u4e2a\u8138\u7ea2\u8116\u5b50\u7c97\uff0c\u90a3\u526f\u602a\u50cf\uff0c\u628a\u4e00\u4e2a\u7f8e\u903e\u5929\u4eba\u7684\u5c11\u5973\uff0c\u7b11\u5f97\u82b1\u679d\u4e71\u98a4\uff01 \u5e03\u8863\u5c11\u5973\u7b11\u5f97\u773c\u6cea\u90fd\u5feb\u8981\u6d41\u51fa\u6765\u4e86\uff0c\u4f46\u5979\u90a3\u5a07\u61a8\u7684\u7b11\u6001\uff0c\u7684\u786e\u53ef\u4ee5\u4ee4\u4eba\u610f\u4e71\u60c5\u8ff7\uff0c\u5ffd\u7136 \u5979\u7b11\u7740\u8bf4\u9053\uff1a\u201c\u6211\u7684\u5c0f\u7237\uff01\u4f60\u6162\u70b9\u5403\u597d\u4e0d\u597d\uff1f\u5982\u679c\u6ca1\u88ab\u6697\u5668\u6253\u6b7b\uff0c\u5403\u4e1c\u897f\u53cd\u800c\u564e\u6b7b\u4e86\uff0c\u90a3\u624d \u5212\u4e0d\u6765\u5462\u2026\u2026\u201d \u53ef\u662f\uff0c\u5979\u521a\u8bf4\u5230\u201c\u6211\u7684\u5c0f\u7237\uff01\u201d\u90a3\u53e5\u8bdd\u65f6\uff0c\u5c55\u767d\u201c\u55ef\u201d\u7684\u4e00\u58f0\uff0c\u628a\u4e00\u5927\u53e3\u7684\u98df\u7269\u54bd\u4e0b\u809a \u53bb\u4e86\uff0c\u597d\u50cf\u662f\u7b54\u5e94\u4e86\u5c11\u5973\u7684\u53eb\u7237\u58f0\uff0c\u90a3\u5c11\u5973\u521a\u4e00\u77aa\u773c\uff0c\u5c55\u767d\u5374\u5401\u4e86\u4e00\u53e3\u6c14\u9053\uff1a\u201c\u54ce\u5440\uff01\u6211\u7684 \u5988\u5440\uff01\u771f\u5dee\u4e00\u70b9\u6ca1\u6709\u564e\u6b7b\u2026\u2026\u201d\u5de7\u4e0d\u5de7\u5730\uff0c\u5c55\u767d\u4e00\u58f0\u201c\u6211\u7684\u5988\u5440\uff01\u201d\u90a3\u5e03\u8863\u5c11\u5973\u89c1\u81ea\u5df1\u65e0\u610f \u4e2d\u53eb\u51fa\u4e00\u58f0\u201c\u6211\u7684\u5c0f\u7237\u201d\uff0c\u5c55\u767d\u201c\u55ef\uff01\u201d\u5730\u4e00\u58f0\u7ade\u4f3c\u7b54\u5e94\uff0c\u65e0\u5f62\u4e2d\u5360\u4e86\u81ea\u5df2\u7684\u4fbf\u5b9c\uff0c\u5fd9\u9053\uff1a \u201c\u5443\uff01\u4f60\u600e\u4e48\u2026\u2026\u201d \u8c01\u77e5\u9053\u8fd9\u4e00\u201c\u5443\u201d\uff0c\u7adf\u53c8\u63a5\u4e0a\u4e86\u5c55\u767d\u7684\u90a3\u58f0\u201c\u6211\u7684\u5988\u5440\uff01\u201d\u4e8c\u4eba\u90fd\u662f\u51fa\u4e8e\u65e0\u5fc3\uff0c\u5374\u8d76\u5f97 \u8fd9\u6837\u5de7\uff0c\u597d\u50cf\u90fd\u662f\u6210\u5fc3\u5360\u5bf9\u65b9\u4fbf\u5b9c\u4f3c\u7684\uff0c\u5f85\u4e8c\u4eba\u4f1a\u8fc7\u610f\u6765\uff0c\u4e0d\u7531\u76f8\u5bf9\u5927\u7b11\u8d77\u6765\uff01 \u8fd9\u4e00\u7b11\uff0c\u4e8c\u4eba\u7684\u60c5\u611f\u65e0\u5f62\u4e2d\u5df2\u63a5\u8fd1\u5230\u6beb\u65e0\u9694\u9602\u7684\u5730\u6b65 \u5e03\u8863\u5c11\u5973\u4e00\u5934\u6251\u5230\u5c55\u767d\u6000\u91cc\u4e0d\u4f9d\u9053\uff1a\u201c\u4f60\u574f\uff01\u4f60\u574f\uff01\u51c0\u5360\u6211\u7684\u4fbf\u5b9c\u2026\u2026\u201d\u5c55\u767d\u53c8\u662f\u75db \u547c\uff0c\u53c8\u662f\u5927\u7b11\uff0c\u539f\u6765\u5e03\u8863\u5c11\u5973\u4e00\u64a4\u5a07\uff0c\u4fbf\u5fd8\u4e86\u5c55\u767d\u8eab\u4e0a\u8fd8\u6709\u4f24\uff0c\u8fd9\u4e00\u6251\u5728\u5c55\u767d\u8eab\u4e0a\uff0c\u4fbf\u628a\u5c55 \u767d\u7684\u4f24\u53e3\u78b0\u5230\u4e86\uff0c\u75db\u5f97\u5c55\u767d\u5c16\u58f0\u6025\u547c\uff1b\u53ef\u662f\u90a3\u5c11\u5973\u6251\u5728\u5c55\u81ea\u6000\u91cc\uff0c\u5a07\u5ae9\u7684\u624b\u8098\u53c8\u683c\u5230\u4e86\u5c55\u767d \u7684\u808b\u9aa8\uff0c\u6240\u4ee5\u5947\u75d2\u96be\u71ac\uff0c\u53c8\u7981\u4e0d\u4f4f\u5927\u7b11\u8d77\u6765\u2026\u2026\u7834\u5e99\u4e4b\u4e2d\u6625\u8272\u65e0\u8fb9\uff0c\u5145\u6ee1\u4e86\u4e24\u4e2a\u9752\u5e74\u7684\u5929\u771f \u65e0\u90aa\u7684\u6b22\u7b11\u2026\u2026 \u5ffd\u7136\u5e99\u5916\u4f20\u6765\u4e00\u58f0\u6781\u5fae\u5f31\u7684\u54cd\u58f0\uff0c\u4f3c\u4e4e\u662f\u79cb\u5929\u843d\u53f6\u7684\u58f0\u54cd\uff0c\u82e5\u4e0d\u7559\u5fc3\uff0c\u51b3\u542c\u4e0d\u51fa\u6765\uff0c\u53ef \u662f\u8fd9\u5fbd\u5f31\u7684\u58f0\u97f3\uff0c\u5374\u672a\u80fd\u7792\u8fc7\u90a3\u6b66\u529f\u7edd\u9ad8\u7684\u5e03\u8863\u5c11\u5973\uff01 \u53ea\u89c1\u5979\u7a81\u7136\u6536\u4f4f\u4e86\u5982\u94f6\u94c3\u7684\u7b11\u58f0\uff0c\u731b\u7136\u4ece\u5c55\u767d\u6000\u91cc\u7ad9\u8d77\uff0c\u5a07\u665a\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\uff1f\u5927\u80c6\u5728\u6b64 \u5077\u7aa5\uff01\u201d \u5728\u201c\u4eba\u201d\u5b57\u51fa\u53e3\u65f6\uff0c\u5979\u5df2\u98de\u8eab\u63a0\u51fa\u6bbf\u5916\uff0c\u201c\u5927\u80c6\u5728\u6b64\u5077\u7aa5\u201d\u7684\u51e0\u5b87\uff0c\u5df2\u7ecf\u662f\u5728\u6bbf\u5916\u623f\u9876 \u4e0a\u53d1\u7684\u4e86\uff01\u8fd9\u5e03\u8863\u5c11\u5973\u8eab\u6cd5\u53ef\u8bf4\u662f\u5feb\u5f97\u51fa\u5947\uff0c\u4f46\u5979\u9976\u662f\u5982\u6b64\u5feb\u901f\uff0c\u77f3\u5e99\u6b8b\u7834\u9662\u843d\u4e4b\u4e2d\uff0c\u51b7\u6708 \u5bc2\u5bc2\uff0c\u5374\u4e0d\u89c1\u4e00\u6761\u4eba\u5f71\uff01\u5e03\u8863\u5c11\u5973\u5bf9\u81ea\u5df1\u7684\u8033\u76ee\u4e4b\u806a\u654f\uff0c\u4f3c\u6709\u5145\u5206\u7684\u4fe1\u5fc3\uff0c\u7edd\u4e0d\u7591\u5fc3\u81ea\u5df1\u4f1a \u542c\u9519\uff0c\u5a07\u8eaf\u7ad9\u5728\u6bbf\u9876\u7684\u7834\u74e6\u6b8b\u680b\u4e0a\uff0c\u7476\u9f3b\u4e00\u76b1\uff0c\u51b7\u54fc\u4e86\u4e00\u58f0\uff0c\u7f13\u7f13\u8a00\u9053\uff1a\u201c\u6050\u6015\u4f60\u4e5f\u77e5\u9053\u662f \u8c01\u4efb\u5728\u6b64\u5730\uff0c\u82e5\u518d\u6562\u6765\u5077\u7aa5\uff0c\u522b\u8bf4\u59d1\u5a18\u5bf9\u4f60\u4e0d\u5ba2\u6c14\uff01\u201d\u5e03\u8863\u5c11\u5973\u8bf4\u6b64\u8bdd\u65f6\uff0c\u5a07\u7f8e\u7684\u8138\u4e0a\u7adf\u73b0 \u51fa\u4e00\u4e1d\u715e\u6c14\uff0c\u867d\u7136\u5979\u7684\u58f0\u97f3\u4e0d\u5927\uff0c\u4f46\u7530\u7ff1\u53ee\u5341\u6570\u91cc\u4e4b\u5916\uff01\u82e5\u6697\u4e2d\u6709\u4eba\u5077\u7aa5\u7684\u8bdd\uff0c\u51e1\u5728\u5341\u91cc\u65b9 \u5706\u4ee5\u5185\u7684\uff0c\u90fd\u53ef\u4ee5\u542c\u5230\u5979\u72b9\u5982\u9ec4\u83ba\u767e\u556d\u7684\u5456\u5456\u83ba\u58f0\uff0c\u4f46\u5374\u5145\u6ee1\u4e86\u9a87\u4eba\u5fc3\u9b42\u7684\u6050\u6016\u8bed\u6c14\uff01 \u5e03\u8863\u5c11\u5973\u8bf4\u7f62\uff0c\u4e5f\u4e0d\u7ba1\u662f\u5426\u6709\u4eba\u9690\u8eab\u9644\u8fd1\uff0c\u8eab\u5f62\u6a2a\u7a7a\u4e00\u64a9\uff0c\u534a\u7a7a\u91cc\u4e00\u4e2a\u76d8\u65cb\uff0c\u72b9\u5982\u5f52\u5de2 \u4e73\u71d5\u822c\uff0c\u7a7f\u7a97\u800c\u8fc7\uff0c\u53c8\u56de\u5230\u4e86\u623f\u4e2d\u3002\u5c55\u767d\u609f\u7136\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u53d1\u73b0\u4e86\u4ec0\u4e48\uff1f\u201d \u5e03\u8863\u5c11\u5973\u7b11\u5bb9\u707f\u7136\u5982\u82b1\uff0c\u8ddf\u5728\u623f\u9876\u8bf4\u8bdd\u7684\u8bed\u6c14\uff0c\u6709\u5982\u5929\u6e0a\u4e4b\u522b\uff0c\u5728\u6bbf\u5916\u53d1\u8bdd\u65f6\u51b7\u5982\u51b0 \u971c\uff0c\u89c1\u4e86\u5c55\u767d\u5374\u53c8\u8273\u5982\u6843\u674e\uff0c\u53ea\u89c1\u5979\u8d1d\u9f7f\u95ea\u5149\u7b11\u9053\uff1a\u201c\u53ef\u80fd\u6709\u4e00\u4e24\u4e2a\u80c6\u5b50\u5927\u7684\u5c0f\u8001\u9f20\uff0c\u8eb2\u5728 \u5927\u6881\u4e0a\u5077\u770b\u6211\u4eec\u8bf4\u7b11\u5566\uff01\u201d\u5e03\u8863\u5c11\u5973\u53c8\u8ddf\u5c55\u767d\u8bf4\u7b11\u4e86\u4e00\u4f1a\uff0c\u4fbf\u5b89\u629a\u5c55\u767d\u7761\u4e0b\uff0c\u5979\u81ea\u5df1\u4fbf\u5750\u5728 \u5c55\u767d\u69bb\u524d\u95ed\u76ee\u8c03\u606f\u8d77\u6765\u2026\u2026 \u5c55\u767d\u5fc3\u60c5\u8d77\u4f0f\uff0c\u601d\u6f6e\u6f8e\u6e43\u5982\u5927\u98ce\u66b4\u4e2d\u7684\u6d77\u6d0b\uff0c\u4e0d\u8981\u8bf4\u7761\u89c9\uff0c\u4ed6\u8fde\u5b89\u606f\u4e0b\u6765\u90fd\u4e0d\u53ef\u80fd\uff01 \u4ed6\u53c8\u4e0d\u65f6\u7741\u5f00\u773c\u775b\u6765\uff0c\u770b\u770b\u8fd9\u4e0d\u77e5\u6765\u5386\u3001\u5374\u6551\u4e86\u81ea\u5df2\u53c8\u8fd9\u80a1\u795e\u79d8\u83ab\u6d4b\u7684\u5e03\u8863\u5c11\u5973\uff01 \u90a3\u5e03\u8863\u5c11\u5973\u6253\u5750\u8c03\u606f\u7684\u59ff\u52bf\u5f88\u7279\u522b\uff0c\u65e2\u4e0d\u662f\u76d8\u819d\u8dc3\u5750\uff0c\u4e5f\u4e0d\u662f\u4e94\u5fc3\u671d\u5929\uff0c\u800c\u662f\u7389\u624b\u652f\u4f4f \u9999\u816e\uff0c\u4e24\u6761\u4fee\u957f\u7684\u7389\u817f\uff0c\u4e00\u5c48\u4e00\u4f38\uff0c\u97f5\u81f4\u5a49\u7136\uff0c\u957f\u957f\u7684\u773c\u776b\u6bdb\u8986\u76d6\u7740\u4f3c\u6c34\u53cc\u77b3\uff0c\u5634\u89d2\u542b\u7b11\uff0c \u82f9\u679c\u4f3c\u7684\u53cc\u988a\u4e0a\u68a8\u7978\u9690\u73b0\uff0c\u7ade\u50cf\u662f\u7f8e\u4eba\u5047\u5bd0\uff0c\u53c8\u597d\u50cf\u4e00\u5e45\u6d77\u68e0\u6625\u7761\u56fe\uff0c\u8fd9\u54ea\u91cc\u50cf\u662f\u5728\u9759\u5750\u884c \u529f\uff0c\u5206\u660e\u662f\u4e00\u5e45\u6625\u8272\u65e0\u8fb9\u7684\u8bf1\u4eba\u56fe\u753b\uff01\u4e00\u4f46\u770b\u5979\u8033\u3001\u9f3b\u53ca\u5fae\u5f20\u7684\u6a31\u53e3\u5185\u6709\u4e94\u7f15\u8f7b\u8885\u767d\u6c14\u5347 \u8d77\uff0c\u7adf\u5728\u5979\u5934\u9876\u4e0a\u805a\u96c6\u6210\u4e86\u4e09\u6735\u4e91\u82b1\u822c\u7684\u4e91\u96fe\uff0c\u4fbf\u77e5\u5979\u4e0d\u4f46\u5728\u9759\u5750\u884c\u529f\uff0c\u800c\u4e14\u663e\u89c1\u5176\u5185\u529f\u4fee \u4e3a\u5df2\u5230\u4e86\u201c\u4e09\u82b1\u805a\u9876\u201d\u3001\u201c\u4e94\u671d\u6c14\u5143\u201d\u7684\u7cbe\u5999\u5883\u754c\uff01 \u7b2c\u4e09\u5341\u4e94\u7ae0\u3000\u6cb3\u8fb9\u9b3c\u4e8b>> \u53e4\u9f99\u300a\u5251\u5ba2\u884c\u300b \u7b2c\u4e09\u5341\u4e94\u7ae0\u3000\u6cb3\u8fb9\u9b3c\u4e8b \u5979\u5b9e\u5728\u592a\u7f8e\u4e86\uff0c\u7f8e\u5f97\u65e0\u6cd5\u5f62\u5bb9\uff0c\u5c55\u767d\u7eb5\u7136\u5fc3\u65e0\u90aa\u5ff5\uff0c\u4e5f\u4e0d\u7531\u53cc\u773c\u6014\u6014\u5730\u671b\u7740\u5979\u51fa\u4e86 \u795e\u2026\u2026 \u5979\u5374\u7f13\u4f38\u4e24\u6761\u7389\u817f\uff0c\u7ea4\u8170\u4e00\u633a\uff0c\u811a\u5c16\u843d\u5730\uff0c\u8d70\u5411\u5c55\u767d\uff0c\u53cc\u624b\u8f7b\u8f7b\u5730\u629a\u5728\u5c55\u767d\u7684\u8eab\u4e0a\uff01 \u5c55\u767d\u53ea\u89c9\u5979\u7d20\u624b\u89e6\u5904\uff0c\u6e29\u6696\u9165\u9ebb\uff0c\u4e00\u822c\u8bf4\u4e0d\u51fa\u7684\u611f\u89c9\uff0c\u4f7f\u4ed6\u7528\u8eab\u8840\u6db2\u52a0\u901f\u5730\u6cb8\u817e\u8d77 \u6765\u2026\u2026 \u5c55\u767d\u8f7b\u8f7b\u5730\u201c\u554a\u201d\u4e86\u4e00\u58f0\uff0c\u5fc3\u65cf\u6447\u52a8\uff0c\u51e0\u4e4e\u628a\u63e1\u4e0d\u5b9a\u2026\u2026 \u90a3\u7edd\u7f8e\u7684\u5e03\u8863\u5c11\u5973\uff0c\u5374\u5439\u6c14\u5982\u5170\u5730\u5728\u4ed6\u8033\u8fb9\u8bf4\u9053\uff1a\u201c\u4e3a\u4e86\u8981\u4f60\u65e9\u4e9b\u590d\u539f\uff0c\u4e3a\u4f60\u8fd0\u529f\u7597 \u4f24\uff0c\u8bf4\u4e0d\u5f97\u8981\u635f\u8017\u6211\u4e00\u70b9\u771f\u5143\uff0c\u4f60\u73b0\u5728\u8fd0\u529f\u76f8\u5f15\u7f62\uff01\u201d \u5c55\u767d\u987f\u611f\u4e00\u9635\u5dee\u6127\uff0c\u4e0d\u81ea\u89c9\u5730\u8138\u4e0a\u53d1\u70e7\uff0c\u5fc3\u8bf4\uff1a\u201c\u5c55\u767d\u5440\u5c55\u767d\uff01\u4f60\u81ea\u547d\u4e3a\u4e00\u4e2a\u5947\u7537\u5b50\uff0c \u5c31\u4e0d\u8be5\u80e1\u601d\u4e71\u60f3\uff0c\u4f55\u51b5\u4eba\u5bb6\u8fd8\u662f\u4e00\u7247\u597d\u5fc3\uff0c\u8981\u4e3a\u81ea\u5df1\u7597\u4f24\u5462\u2026\u2026\u201d \u60f3\u81f3\u6b64\u5904\uff0c\u7acb\u523b\u6536\u56de\u7eee\u5ff5\uff0c\u6e05\u5fc3\u6f84\u5fd7\uff0c\u9ed8\u9ed8\u8fd0\u529f\u76f8\u5f15\uff0c\u53ea\u89c9\u5979\u624b\u89e6\u5904\uff0c\u4e00\u80a1\u6e29\u6696\u7684\u70ed \u6d41\uff0c\u5239\u65f6\u901a\u8fc7\u4e86\u81ea\u5df1\u5168\u8eab\uff01 \u90a3\u5e03\u8863\u5c11\u5973\u53cc\u624b\u4e0d\u4f4f\u5728\u5c55\u767d\u8eab\u4e0a\u6e38\u52a8\uff0c\u5c55\u767d\u6e10\u6e10\u89c9\u5f97\u56db\u80a2\u767e\u9ab8\u8212\u7545\u65e0\u6bd4\uff0c\u817f\u81c2\u4f24\u5904\u75db\u695a \u4e5f\u9010\u6e10\u6d88\u5931\u2026\u2026 \u6b63\u5728\u6df1\u7136\u5fd8\u6211\u4e4b\u5883\uff0c\u90a3\u5c11\u5973\u7a81\u7136\u53cc\u7709\u4e00\u76b1\uff0c\u505c\u4e0b\u624b\u6765\uff0c\u4fa7\u8033\u542c\u4e86\u542c\uff0c\u7f8e\u4e3d\u7684\u8138\u4e0a\u7a81\u7136\u6cdb \u8d77\u4e00\u62b9\u6740\u673a\uff01 \u5c55\u767d\u80e1\u601d\u4e71\u60f3\uff0c\u5374\u60f3\u4e0d\u51fa\u4e00\u4e2a\u6240\u4ee5\u7136\u6765\uff0c\u6b63\u5728\u6b64\u65f6\uff0c\u5ffd\u542c\u8863\u895f\u7834\u98ce\uff0c\u4ece\u5c4b\u5916\u98d8\u8fdb\u4e00\u4eba\u3002 \u5c55\u767d\u8fd8\u4ee5\u4e3a\u90a3\u795e\u79d8\u7f8e\u4e3d\u7684\u5e03\u8863\u5c11\u5973\u53bb\u800c\u590d\u56de\uff0c\u56e0\u6b64\u4e5f\u672a\u5728\u610f\u3002\u4f46\u7b49\u90a3\u4eba\u8d70\u81f3\u5c55\u767d\u9762\u524d\uff0c \u5c55\u767d\u4e0d\u7531\u5413\u5f97\u4e00\u6014\uff01 \u539f\u6765\u8fdb\u5c4b\u7684\u4e0d\u662f\u90a3\u5e03\u8863\u5c11\u5973\uff0c\u800c\u662f\u4e00\u4e2a\u767d\u8272\u5112\u670d\u7684\u5c11\u5e74\uff01\u65b0\u6b63\u5929\u5bd2\uff0c\u5c55\u81ea\u5367\u641e\u4e0a\u94fa\u7740\u5e72 \u5404\u8eab\u4e0a\u76d6\u7740\u539a\u88ab\uff0c\u8fd8\u89c9\u5f97\u6709\u70b9\u51b7\uff0c\u90a3\u767d\u8272\u5112\u670d\u5c11\u5e74\u624b\u4e2d\u5374\u62ff\u4e86\u4e00\u67c4\u94f6\u6247\uff0c\u770b\u6765\u5206\u5916\u624e\u773c\uff01 \u5c55\u767d\u89c1\u4e0e\u4ed6\u7d22\u4e0d\u76f8\u8bc6\uff0c\u5fae\u611f\u610f\u5916\uff0c\u5c1a\u672a\u53d1\u8a00\uff0c\u90a3\u767d\u8272\u5112\u670d\u5c11\u5e74\u5374\u829c\u5c14\u7b11\u9053\uff1a\u201c\u5144\u53f0\u8273\u798f \u4e0d\u6d45\uff0c\u6709\u2018\u7ea2\u989c\u9b54\u5973\u2019\u966a\u4f34\uff0c\u60f3\u5fc5\u662f\u7834\u5e99\u5f53\u7476\u53f0\uff0c\u4e50\u4e0d\u601d\u8700\u4e86\uff01\u201d \u5c55\u767d\u6115\u7136\u9053\uff1a\u201c\u9601\u4e0b\u4f55\u51fa\u6b64\u8a00\u2026\u2026\u201d \u81ea\u8272\u5112\u670d\u5c11\u5e74\u53f3\u624b\u94f6\u6247\u5728\u5de6\u638c\u638c\u5fc3\u8f7b\u8f7b\u4e00\u51fb\uff0c\u9053\uff1a\u201c\u7ea2\u7c89\u4f73\u4eba\u77f3\u8fc7\u662f\u5e26\u8089\u7684\u9ab7\u9ac5\uff0c\u7f8e\u5973 \u5982\u7389\u4e5f\u4e0d\u8fc7\u662f\u6709\u6bd2\u7684\u86c7\u874e\uff0c\u6050\u6015\u4f60\u6625\u68a6\u672a\u9192\uff0c\u4fbf\u5df2\u505a\u4e86\u8352\u5e99\u51a4\u9b42\u4e86\uff01\u201d \u5c55\u767d\u60ca\u9053\uff1a\u201c\u9601\u4e0b\u8bf4\u8fd9\u8bdd\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u767d\u8272\u5112\u670d\u5c11\u5e74\u624b\u62ff\u94f6\u6247\uff0c\u5374\u6447\u5934\u6643\u8111\u5730\u9053\uff1a\u201c\u8272\u5373\u7a7a\uff0c\u7a7a\u5373\u662f\u8272\uff01\u82e6\u6d77\u65e0\u8fb9\uff0c\u56de\u5934\u662f \u5cb8\uff01\u2026\u2026\u201d \u5c55\u767d\u88ab\u4ed6\u8bf4\u5f97\u8499\u4e86\u4e00\u5934\u96fe\u6c34\uff0c\u8d8a\u53d1\u4e0d\u89e3\u5730\u95ee\u9053\uff1a\u9601\u4e0b\u6709\u4ec0\u4e48\u89c1\u6559\uff1f\u4e0d\u59a8\u660e\u8bf4\uff0c\u4f55\u5fc5\u5982\u6b64 \u6545\u4f5c\u60ca\u4eba\u4e4b\u8a00\u2026\u2026\u201d \u767d\u8272\u5112\u670d\u5c11\u5e74\u5ffd\u7136\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u5f53\u771f\u4e0d\u77e5\u90a3\u9b54\u5973\u662f\u4f55\u8bb8\u4eba\u5417\uff1f\u7ade\u6709\u80c6\u91cf\u8ddf\u5979\u4eb2\u8fd1\uff01\u201d \u5c55\u767d\u867d\u89c9\u5f97\u90a3\u5e03\u8863\u7f8e\u5973\u795e\u79d8\u4e0d\u6d4b\uff0c\u4f46\u4eba\u5bb6\u6551\u4e86\u81ea\u5df1\u4e00\u547d\uff0c\u5bf9\u90a3\u5e03\u8863\u5c11\u5973\u6ca1\u6709\u4e00\u4e1d\u6076\u611f\uff0c \u89c1\u8fd9\u767d\u8863\u5c11\u5e74\u8bf4\u8bdd\u541e\u541e\u5410\u5410\uff0c\u53c8\u4e00\u5473\u5730\u6545\u4f5c\u60ca\u4eba\u4e4b\u8a00\uff0c\u4e0d\u7531\u5fc3\u4e2d\u8d77\u4e86\u53cd\u611f\u9053\uff1a\u201c\u5982\u679c\u9601\u4e0b\u6ca1 \u6709\u4ec0\u4e48\u4e8b\uff0c\u8fd8\u662f\u8bf7\u79bb\u5f00\u8fd9\u91cc\u5427\uff01\u5728\u4e0b\u6709\u75c5\u5728\u8eab\uff0c\u5b9e\u4e0d\u613f\u4e0e\u9601\u4e0b\u591a\u8c08\uff01\u201d \u767d\u8863\u5c11\u5e74\u9053\uff1a\u201c\u6211\u4f8b\u662f\u4e00\u756a\u597d\u610f\uff0c\u6ca1\u60f3\u5230\u4f1a\u5f15\u8d77\u4f60\u7684\u8bef\u4f1a\uff01\u5b9e\u5bf9\u4f60\u8bf4\u4e86\u7f62\uff0c\u90a3\u9b54\u5973\u4e43\u662f \u2018\u7ea2\u7c89\u9ab7\u9ac5\uff0c\u9b3c\u9762\u5a07\u5a03\u2019\uff01\u5929\u4e0b\u7b2c\u4e00\u53f7\u5927\u9b54\u5934\uff0c\u4f60\u8be5\u61c2\u4e86\u5427\uff01\u201d \u5c55\u767d\u95fb\u542c\u6b64\u8a00\uff0c\u5934\u8111\u201c\u8f70\u201d\u7684\u4e00\u58f0\uff0c\u5982\u53d7\u96f7\u51fb\uff01\u5fd9\u9053\uff1a\u201c\u6b64\u8bdd\u5f53\u771f\uff1f\u201d \u767d\u8272\u5112\u670d\u5c11\u5e74\u9053\uff1a\u201c\u6211\u4f55\u5fc5\u9a97\u4f60\u54e9\uff01\u6211\u7531\u4e1c\u6d77\u2018\u9b54\u9b3c\u5c9b\u2019\u8ddf\u8e2a\u8fd9\u9b54\u5934\uff0c\u4e00\u8def\u81f3\u6b64\uff0c\u96be\u9053 \u8fd8\u6709\u5047\u5417\uff1f\u201d \u5c55\u767d\u5583\u5583\u9053\uff1a\u7adf\u662f\u5979\uff01\u7adf\u662f\u5979\u2026\u2026\u771f\u4ee4\u4eba\u4e0d\u6562\u76f8\u4fe1\u2026\u2026\u201d \u539f\u6765\u8fd9\u201c\u7ea2\u7c89\u9ab7\u9ac5\uff0c\u9b3c\u9762\u5a07\u5a03\u201d\u5927\u7ea6\u662f\u5728\u6574\u5341\u5e74\u524d\u540d\u5b57\u8f70\u52a8\u8fc7\u6574\u4e2a\u6c5f\u6e56\uff0c\u539f\u56e0\u662f\u5979\u751f\u5f97 \u7f8e\u903e\u5929\u4ed9\uff0c\u5fc3\u5374\u6bd2\u8fa3\u6709\u5982\u86c7\u874e\uff0c\u52a0\u4e0a\u5979\u4e00\u8eab\u795e\u9b3c\u83ab\u6d4b\u7684\u9ad8\u5f3a\u6b66\u529f\uff0c\u751f\u6027\u6deb\u8361\u65e0\u6bd4\uff0c\u5374\u53c8\u5947 \u5992\u3002\u51e1\u662f\u7f8e\u8c8c\u7684\u9752\u5e74\u7537\u5b50\uff0c\u5979\u90fd\u8981\u63b3\u5728\u88d9\u4e0b\u79f0\u81e3\uff0c\u7a0d\u4e0d\u79f0\u610f\uff0c\u52a8\u8f84\u6740\u5bb3\uff0c\u9047\u5230\u8c8c\u7f8e\u7684\u5973\u5b50\u5200 \u66f4\u662f\u5fc5\u6740\u65e0\u8d66\uff0c\u800c\u4e14\u624b\u6bb5\u6bd2\u8fa3\u9634\u72e0\u65e0\u6bd4\uff0c\u4e00\u5b9a\u8981\u628a\u5973\u5b50\u525c\u76ee\u5272\u820c\uff0c\u6b8b\u9762\u6bc1\u5bb9\uff0c\u628a\u5176\u6298\u78e8\u5f97\u4e0d \u6210\u4eba\u5f62\uff0c\u65b9\u4efb\u5176\u60b2\u547c\u60e8\u53f7\u81ea\u884c\u6b7b\u53bb\uff01 \u56e0\u6b64\uff0c\u5979\u51fa\u9053\u4e0d\u4e45\uff0c\u5373\u9707\u52a8\u4e86\u6574\u4e2a\u6c5f\u6e56\uff0c\u56e0\u4e3a\u5979\u4e0d\u77e5\u4ece\u4f55\u5904\u5b66\u5f97\u4e00\u8eab\u8be1\u5f02\u6b66\u529f\uff0c\u6b66\u6797\u4e2d \u5f88\u5c11\u6709\u4eba\u662f\u5979\u5bf9\u624b\uff0c\u77ed\u77ed\u6570\u5e74\uff0c\u6bc1\u5728\u5979\u624b\u5e95\u4e0b\u7684\u9752\u5e74\u7537\u5973\u4e0d\u77e5\u6709\u591a\u5c11\uff01 \u56e0\u4e3a\u5979\u4f24\u5bb3\u6b66\u6797\u9752\u5e74\u592a\u591a\uff0c\u800c\u4e14\u5176\u4e2d\u4e0d\u5c11\u662f\u6b66\u6797\u5404\u5927\u95e8\u6d3e\u7684\u5e7c\u5e74\u5f1f\u5b50\uff0c\u5f53\u7136\u9ed1\u9053\u8c6a\u95e8\u6b7b \u5728\u5979\u624b\u4e0b\u7684\u4e5f\u4e0d\u5728\u5c11\u6570\uff0c\u56e0\u6b64\u5f15\u8d77\u6b66\u6797\u516c\u6124\uff0c\u4e0d\u8bba\u9ed1\u767d\u9762\u9053\u5747\u6068\u5979\u5165\u9aa8\uff0c\u6709\u4e00\u6b21\u96c6\u5408\u4e86\u9ed1\u767d \u4e24\u9053\u9876\u5c16\u9ad8\u624b\uff0c\u5408\u529b\u56f4\u527f\uff0c\u5728\u4e1c\u5cb3\u6cf0\u5c71\u89c2\u65e5\u5cf0\u4e00\u6218\uff0c\u628a\u5979\u6218\u8d25\uff0c\u800c\u4e14\u4f7f\u5979\u8eab\u8d1f\u6570\u5904\u91cd\u4f24\uff0c\u6ca1 \u60f3\u5230\u4ecd\u8ba9\u5979\u8dd1\u4e86\uff01 \u90a3\u4e00\u6218\uff0c\u6b66\u6797\u516b\u5927\u95e8\u6d3e\uff0c\u53ca\u5357\u4e03\u5317\u516d\u7684\u9ed1\u9053\u76df\u4e3b\uff0c\u4f24\u4ea1\u4e5f\u591f\u60e8\u91cd\uff0c\u5404\u95e8\u5404\u6d3e\u7cbe\u82f1\uff0c\u51e0\u4e4e \u4f24\u4ea1\u6b86\u5c3d\uff0c\u4ee5\u81f4\u4f7f\u516b\u5927\u95e8\u6d3e\u4eba\u624d\u51cb\u96f6\uff0c\u6b66\u529f\u5f0f\u5fae\uff0c\u6570\u5341\u5e74\u5043\u65d7\u606f\u9f13\uff0c\u624d\u4f7f\u201c\u6b66\u6797\u56db\u516c\u5b50\u201d\u76f8 \u673a\u5014\u8d77\uff01 \u53ef\u662f\uff0c\u4ece\u6b64\u6c5f\u6e56\u4e0a\u4e5f\u5c31\u5931\u53bb\u4e86\u201c\u7ea2\u7c89\u9ab7\u9ac5\uff0c\u9b3c\u9762\u5a07\u5a03\u201d\u7684\u884c\u8e2a\uff01 \u6709\u4eba\u8bf4\u5979\u4f24\u91cd\u6bd9\u547d\uff0c\u6b7b\u5728\u6cf0\u5c71\u89c2\u65e5\u5cf0\uff0c\u4e5f\u6709\u4eba\u8bf4\u5979\u6f5c\u5728\u4e1c\u6d77\u201c\u9b54\u9b3c\u5c9b\u201d\u9690\u4fee\uff0c\u6c5f\u6e56\u4e0a\u4eba \u8a00\u4eba\u6b8a\uff0c\u4e0d\u8fc7\uff0c\u4ece\u6b64\u518d\u672a\u89c1\u5979\u73b0\u8eab\u6c5f\u6e56\uff0c\u5374\u662f\u771f\u7684\u3002 \u65f6\u65e5\u5df2\u4e45\uff0c\u4eba\u4eec\u628a\u8fd9\u4ef6\u8f70\u52a8\u6c5f\u6e56\u7684\u5927\u4e8b\u4e5f\u5c31\u6de1\u5fd8\u4e86\uff0c\u5076\u5c14\u4e00\u4e9b\u8001\u4e00\u8f88\u7684\u6c5f\u6e56\u4fa0\u5ba2\uff0c\u8336\u4f59 \u9152\u540e\u8c08\u8d77\u6765\uff0c\u4e5f\u5f53\u662f\u8bb2\u5386\u53f2\u9648\u8ff9\u4e00\u6837\u4e86\u3002 \u5c55\u767d\u4e5f\u662f\u4ece\u9556\u5c40\u4e2d\uff0c\u542c\u5230\u5e74\u8001\u9556\u5e08\u4eec\u8c08\u8d77\u8fc7\uff0c\u5f53\u65f6\u4e0d\u8fc7\u662f\u5f53\u505a\u4f20\u5947\u6545\u4e8b\u542c\u7684\uff0c\u4e07\u4e5f\u60f3\u4e0d \u5230\u6551\u81ea\u5df1\u7684\u5e03\u8863\u5c11\u5973\uff0c\u5c31\u662f\u5f53\u5e74\u90a3\u201c\u7ea2\u7c89\u9ab7\u9ac5\uff0c\u9b3c\u9762\u5a07\u5a03\u201d\uff0c\u8fd9\u5c82\u4e0d\u60ca\u4eba\uff1f \u800c\u4e14\uff0c\u4e5f\u4f7f\u4eba\u4e0d\u6562\u76f8\u4fe1\u3002\u90a3\u7f8e\u7edd\u5929\u4eba\u7684\u5c11\u5973\uff0c\u7adf\u662f\u6570\u5341\u5e74\u524d\u6740\u4eba\u5982\u9ebb\u7684\u5934\u53f7\u9b54\u5934\uff1f \u5c55\u767d\u5f53\u65f6\u4e00\u60ca\uff0c\u77ac\u5373\u9192\u609f\u8fc7\u6765\uff0c\u8ba4\u4e3a\u8fd9\u662f\u7edd\u4e0d\u53ef\u80fd\u7684\u4e00\u4ef6\u4e8b\uff0c\u56e0\u4e3a\uff0c\u5c31\u7b97\u90a3\u5973\u9b54\u5934\u4ecd\u7136 \u6d3b\u7740\uff0c\u4e5f\u4e0d\u53ef\u80fd\u8fd9\u822c\u5e74\u8f7b\u3002\u4e8e\u662f\uff0c\u8c08\u7136\u7b11\u9053\uff1a\u201c\u9601\u4e0b\u8fd9\u6837\u4fe1\u53e3\u4e00\u8bf4\uff0c\u4ee5\u4e3a\u5728\u4e0b\u4f1a\u76f8\u4fe1\u5417\uff1f\u201d \u767d\u8272\u5112\u670d\u5c11\u5e74\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u4e0d\u4f1a\u76f8\u4fe1\uff0c\u53ef\u662f\u7b49\u5230\u4f60\u76f8\u4fe1\u7684\u65f6\u5019\uff0c\u540e\u6094\u5c31\u5df2\u7ecf\u665a \u4e86\u2026\u2026\u201d \u7a81\u7136\u542c\u5230\u8eab\u540e\u4e00\u58f0\u51b7\u54fc\uff0c\u767d\u8272\u5112\u670d\u5c11\u5e74\u9762\u8272\u4e00\u53d8\uff0c\u500f\u7136\u56de\u8eab\uff0c\u4e0d\u77e5\u4f55\u65f6\uff0c\u90a3\u8138\u7f69\u9b3c\u9762\u7684 \u5e03\u8863\u5c11\u5973\u5df2\u7136\u7ad9\u5728\u623f\u4e2d\uff01 \u5c55\u767d\u89c1\u90a3\u5e03\u8863\u5c11\u5973\uff0c\u8138\u4e0a\u7f69\u4e86\u4e00\u4e2a\u9b3c\u8138\u5177\uff0c\u9752\u8138\u7ea2\u53d1\uff0c\u5de8\u9f7f\u64a9\u7259\uff0c\u50cf\u8c8c\u72f0\u72de\u6016\u4eba\uff0c\u9664\u4e86 \u8eab\u6750\u7565\u663e\u77ee\u5c0f\u4e00\u70b9\u4e4b\u5916\uff0c\u7adf\u5982\u4e00\u4e2a\u5389\u9b3c\u4e00\u822c\uff0c\u82e5\u4e0d\u662f\u5c55\u767d\u89c1\u8fc7\u5979\u7684\u672c\u6765\u9762\u76ee\uff0c\u8bf4\u4ec0\u4e48\u4e5f\u60f3\u4e0d \u5230\uff0c\u8fd9\u5386\u9b3c\u4f3c\u7684\u602a\u7269\uff0c\u7adf\u662f\u7f8e\u903e\u5929\u4eba\u7684\u4e00\u4e2a\u7edd\u8272\u5c11\u5973\uff01 \u53ea\u542c\u5979\u51b7\u68ee\u68ee\u5730\u8bf4\u9053\uff1a\u201c\u6211\u4e00\u731c\u4fbf\u77e5\u662f\u4f60\uff01\u4efb\u4f60\u8be1\u8ba1\u591a\u7aef\uff0c\u4e5f\u96be\u4ee5\u6b3a\u9a97\u5f97\u4e86\u6211\uff0c\u4f46\u662f\uff0c \u6211\u5f88\u5947\u602a\uff0c\u4f60\u4e3a\u4ec0\u4e48\u4e00\u8def\u8ddf\u5b9a\u4e86\u6211\uff0c\u800c\u4e14\u5904\u5904\u8ddf\u6211\u6363\u4e71\uff0c\u7a76\u7adf\u4f60\u662f\u4f55\u5c45\u5fc3\uff1f\u2026\u2026\u201d \u767d\u8272\u5112\u670d\u5c11\u5e74\u5728\u9b3c\u9762\u5c11\u5973\u8bf4\u8bdd\u65f6\uff0c\u7a81\u7136\u653b\u51fa\u4e00\u638c\uff0c\u9b3c\u9762\u5c11\u5973\u968f\u624b\u4e00\u5c01\uff0c\u201c\u6666\u201d\u7684\u4e00\u58f0\u5927 \u9707\uff0c\u4f59\u529b\u56db\u6fc0\uff0c\u56de\u65cb\u751f\u98d9\uff0c\u7acb\u523b\u628a\u6848\u5934\u7684\u6cb9\u706f\u6253\u706d\uff01 \u623f\u4e2d\u53d8\u6210\u4e00\u7247\u6f06\u9ed1\uff0c\u770b\u6765\u4e8c\u4eba\u638c\u529b\u90fd\u591f\u60ca\u4eba\uff0c\u5c55\u767d\u5012\u5728\u5e8a\u4e0a\uff0c\u90fd\u80fd\u611f\u5230\u52b2\u98ce\u5228\u9762\u800c\u8fc7\uff0c \u89e6\u80a4\u751f\u75db\uff01 \u9ed1\u6697\u4e4b\u4e2d\uff0c\u542c\u5230\u9b3c\u9762\u5c11\u5973\u53f1\u9053\uff1a\u201c\u4f60\u53c8\u60f3\u8dd1\u5417\uff1f\xb7\uff0e\uff0c\uff0e\uff0e\uff0e\u201d \u8ddf\u7740\u542c\u5230\u638c\u62f3\u7834\u98ce\u4e4b\u58f0\uff0c\u731b\u70c8\u975e\u5e38\uff01 \u53c8\u542c\u5230\u90a3\u767d\u8272\u5112\u670d\u5c11\u5e74\u9053\uff1a\u201c\u5c11\u966a\u4e86\uff01\u4f46\u4f60\u522b\u5f97\u610f\uff0c\u2018\u6d77\u5916\u4e09\u715e\u2019\u4e00\u5230\uff0c\u4fbf\u662f\u4f60\u4e27\u547d\u4e4b \u65f6\u2026\u2026\u201d \u6700\u540e\u4e00\u53e5\u8bdd\uff0c\u58f0\u97f3\u5df2\u5728\u6570\u5341\u4e08\u5f00\u5916\uff0c\u663e\u89c1\u90a3\u767d\u8272\u5112\u670d\u5c11\u5e74\u5df2\u9003\u81f3\u5e99\u5916\uff0c\u5176\u8eab\u6cd5\u53ef\u8bf4\u662f\u5feb \u5f97\u51fa\u5947\uff01 \u63a5\u7740\u53c8\u542c\u5230\u9b3c\u9762\u5c11\u5973\u7684\u5ffd\u53f1\u58f0\uff1a\u201c\u8fd9\u56de\u4f60\u9003\u5230\u5929\u4e0a\u53bb\uff0c\u6211\u4e5f\u8981\u628a\u4f60\u6293\u56de\u6765\uff01\u2026\u2026\u201d \u8bdd\u58f0\u672a\u843d\uff0c\u90a3\u8eab\u5f71\u5df2\u98d8\u81f3\u6570\u5341\u4e08\u4e4b\u5916\u4e86\u3002\u5c55\u767d\u4e0d\u7531\u6697\u6697\u6628\u820c\uff0c\u8fd9\u4e8c\u4eba\u8eab\u6cd5\u4e4b\u5feb\uff0c\u53ef\u8bf4\u662f \u524d\u6240\u672a\u89c1\uff01 \u5c55\u767d\u6697\u60f3\uff1a\u201c\u8fd9\u5e03\u8863\u9b3c\u9762\u5c11\u5973\uff0c\u5982\u679c\u771f\u5982\u767d\u8863\u4e66\u751f\u6240\u8bf4\uff0c\u662f\u90a3\u9707\u60ca\u6c5f\u6e56\u7684\u2018\u7ea2\u7c89\u9ab7\u9ac5\uff0c \u9b3c\u9762\u5a07\u5a03\u2019\uff0c\u81ea\u5df1\u8fd8\u83ab\u4e0d\u597d\u8131\u8eab\u3002\u4f46\u7ec6\u60f3\u53c8\u4e0d\u53ef\u80fd\uff0c\u4e00\u4e2a\u6570\u5341\u4e2d\u524d\u540d\u6ee1\u6c5f\u6e56\u7684\u9b54\u5934\uff0c\u7adf\u662f\u4e00 \u4e2a\u5e74\u5c4a\u82b1\u4fe1\u7684\u5c11\u5973\uff0c\u8fd9\u65e0\u8bba\u5982\u4f55\u4f7f\u4eba\u4e0d\u80fd\u76f8\u4fe1\uff1b\u53c8\u60f3\u5230\u90a3\u767d\u8863\u5112\u670d\u5c11\u5e74\uff0c\u4e0e\u81ea\u5df2\u7d20\u4e0d\u76f8\u8bc6\uff0c \u4e3a\u4f55\u53c8\u5192\u7740\u751f\u547d\u7684\u5371\u9669\uff0c\u6765\u5411\u81ea\u5df1\u6cc4\u6f0f\u4ed6\u4eba\u7684\u9690\u79d8\uff1f\u800c\u4e14\u89c1\u4e86\u90a3\u5e03\u8863\u5c11\u5973\uff0c\u4fbf\u60f3\u5c3d\u4e86\u65b9\u6cd5\u9003 \u8dd1\uff1f\u53c8\u63d0\u51fa\u2018\u6d77\u5916\u4e09\u715e\u2019\u4e4b\u540d\u6765\u5413\u552c\u4eba\uff0c\u90a3\u4e48\u8fd9\u2018\u6d77\u5916\u4e09\u715e\u2019\u53c8\u662f\u4f55\u8bb8\u4eba\u5462\uff1f\u2026\u2026\u201d \u5c55\u767d\u8d8a\u60f3\u8d8a\u60f3\u4e0d\u51fa\u4e2a\u6240\u4ee5\u7136\uff0c\u53cd\u89c9\u5f97\u9b3c\u9762\u5c11\u5973\u4e0e\u767d\u8863\u4e66\u751f\u90fd\u4e0d\u662f\u4ec0\u4e48\u597d\u4eba\uff0c\u5c24\u5176\u8be1\u79d8\u884c \u8e2a\uff0c\u66f4\u4f7f\u4eba\u53ef\u7591\uff0c\u4e8c\u4eba\u53c8\u90fd\u4e0d\u8bf4\u51fa\u81ea\u5df2\u59d3\u540d\u51fa\u8eab\uff0c\u6765\u5386\u4e0d\u660e\uff0c\u6b66\u529f\u53c8\u9ad8\uff0c\u771f\u8981\u843d\u5728\u4ed6\u4eec\u624b \u4e2d\uff0c\u4e5f\u8bb8\u4f1a\u53d1\u751f\u4ec0\u4e48\u610f\u60f3\u4e0d\u5230\u7684\u7978\u5bb3\uff01\u8fd8\u662f\u65e9\u4e9b\u79bb\u5f00\u6b64\u5730\uff0c\u53e6\u627e\u9690\u79d8\u5730\u70b9\u517b\u4f24\uff0c\u4f24\u52bf\u597d\u540e\uff0c \u901f\u53bb\u4e3a\u7236\u62a5\u4ec7\u624d\u662f\u6b63\u4e8b\u2026\u2026 \u5c55\u767d\u6709\u4e86\u9003\u8dd1\u7684\u5ff5\u5934\uff0c\u624b\u811a\u6d3b\u52a8\u4e86\u4e00\u4e0b\uff0c\u7adf\u89c9\u5f97\u65e0\u751a\u75db\u695a\uff0c\u6697\u6697\u8fd0\u4e86\u8fd0\u6c14\uff0c\u4ea6\u89c9\u5f97\u8179\u5185 \u771f\u6c14\u80fd\u805a\u6563\u5982\u610f\uff0c\u8eab\u4e0a\u7684\u4f24\u5904\u7adf\u4f3c\u75ca\u6108\u4e86\uff0c\u77e5\u9053\u8fd9\u662f\u90a3\u9b3c\u9762\u5c11\u5973\u4e3a\u81ea\u5df1\u8fd0\u529f\u7597\u4f24\u6240\u5f97\u5230\u7684\u6548 \u679c\uff0c\u5fc3\u4e2d\u6697\u6697\u9ad8\u5174\u2026\u2026 \u53ef\u662f\u7b49\u4ed6\u7ffb\u8eab\u5750\u8d77\uff0c\u4e0d\u7531\u53c8\u695e\u4e86\uff0c\u539f\u6765\u4ed6\u4f24\u91cd\u660f\u8ff7\uff0c\u7adf\u672a\u53d1\u73b0\u81ea\u5df1\u8eab\u4e0a\u7684\u8863\u670d\uff0c\u5df2\u4e0d\u77e5 \u4f55\u65f6\u88ab\u8131\u5149\u4e86\uff01 \u5c55\u767d\u60ca\u60f6\u4e2d\u5468\u8eab\u4e00\u9635\u6478\u7d22\uff0c\u9664\u4e86\u81c2\u3001\u817f\u7b49\u4f24\u5904\u88f9\u4e86\u5e03\u5e26\u4e4b\u5916\uff0c\u8eab\u4e0a\u5df2\u662f\u4e00\u4e1d\u4e0d\u6302\uff01 \u5c55\u767d\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u5fd9\u7740\u5728\u635f\u4e0a\u4e00\u9635\u4e71\u6478\uff0c\u660f\u9ed1\u4e2d\u5012\u662f\u6478\u7740\u4e86\u81ea\u5df1\u7684\u8863\u670d\uff0c\u4f46\u5df2\u5168\u88ab \u6495\u7834\uff01\u8fd9\u65f6\uff0c\u4ed6\u624d\u660e\u767d\uff0c\u4e00\u5b9a\u662f\u90a3\u5e03\u8863\u5c11\u5973\u5728\u4e3a\u4ed6\u8d77\u51fa\u6697\u5668\u53ca\u88f9\u4f24\u65f6\uff0c\u628a\u4ed6\u7684\u8863\u670d\u6495\u7834 \u4e86\u2026\u2026 \u4ed6\u60f3\u5230\u88ab\u4e00\u4e2a\u5c11\u5973\u628a\u8eab\u4e0a\u7684\u8863\u670d\u5265\u5149\u7684\u60c5\u5f62\uff0c\u8138\u5b54\u4e0d\u7531\u80c0\u7ea2\u8d77\u6765\u2026\u2026 \u4f46\u6709\u4f7f\u4ed6\u6bd4\u5bb3\u7f9e\u66f4\u7740\u6025\u7684\u4e8b\uff0c\u662f\u4ed6\u90a3\u89c6\u5982\u6027\u547d\u3001\u6574\u5929\u8d34\u8eab\u85cf\u5728\u6000\u91cc\u7684\u5929\u4e0b\u7b2c\u4e00\u5947\u4e66\u300a\u9501 \u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\u7adf\u7136\u4e0d\u89c1\u4e86\uff01\u4ed6\u518d\u53bb\u6a21\uff0c\u8fde\u505a\u9699\u822c\u547d\u66f4\u4e3a\u91cd\u8981\u7684\u201c\u65e0\u60c5\u78a7\u5251\u201d\u4e5f\u4e0d\u89c1\u4e86\uff01 \u5c55\u767d\u6c14\u5f97\u5927\u9a82\uff0c\u8fd9\u65f6\u4ed6\u5012\u4e0d\u60f3\u9003\u4e86\uff0c\u53cd\u800c\u60f3\u7acb\u523b\u628a\u90a3\u5e03\u8863\u9b3c\u9762\u3001\u53c8\u7f8e\u903e\u5929\u4ed9\u7684\u795e\u79d8\u5c11\u5973 \u627e\u5230\uff0c\u5411\u5979\u8ffd\u8ba8\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\u4ee5\u53ca\u81ea\u5df1\u7684\u201c\u65e0\u60c5\u78a7\u5251\u201d\uff01 \u8863\u670d\u5df2\u65e0\u6cd5\u7a7f\uff0c\u4ed6\u6c14\u8d77\u6765\uff0c\u628a\u4e00\u6761\u68c9\u88ab\u626f\u6210\u4e00\u7247\u4e00\u7247\u7684\uff0c\u7528\u7834\u8863\u670d\u624e\u5728\u8eab\u4e0a\uff0c\u867d\u7136\u4e0d\u6210 \u4eba\u5f62\uff0c\u4f46\u603b\u7b97\u53ef\u4ee5\u853d\u4f53\u53ca\u7565\u906e\u98ce\u5bd2 \u4e00\u5207\u624e\u675f\u505c\u5f53\uff0c\u4ed6\u4ece\u5e99\u4e2d\u7a9c\u5230\u5916\u8fb9\u6765\uff0c\u53ea\u89c1\u51b7\u6708\u897f\u6c89\uff0c\u51c4\u51c9\u7684\u6708\u5149\u659c\u7167\u7740\u4e00\u7247\u65ad\u74e6\u6b8b\u57a3 \u7684\u7834\u5e99\uff0c\u666f\u8272\u7adf\u662f\u8352\u51c9\u65e0\u6bd4\uff1a \u5c55\u767d\u4e0d\u77e5\u8fd9\u5ea7\u7834\u5e99\u662f\u4f55\u6240\u5728\uff0c\u53ea\u6709\u671d\u7740\u5e03\u8863\uff01\u5c11\u5973\u8ffd\u8d76\u767d\u8863\u4e66\u751f\u7684\u65b9\u5411\u9a70\u53bb\uff01 \u5c55\u767d\u4e00\u76f4\u9a70\u51fa\u6570\u91cc\u4e4b\u9065\uff0c\u4e00\u4e1d\u58f0\u8ff9\u90fd\u672b\u53d1\u73b0\uff0c\u6a2a\u5728\u773c\u524d\u7684\u662f\u4e00\u6761\u6ed4\u6ed4\u5927\u6cb3\uff01 \u6cb3\u6c34\u5728\u9ed1\u591c\u4e2d\u95ea\u767d\u53d1\u4eae\uff0c\u6c34\u58f0\u54d7\u54d7\uff0c\u5374\u4e0d\u89c1\u4e00\u4e2a\u4eba\u5f71\uff01 \u5c55\u767d\u81ea\u77e5\u8ffd\u9519\u4e86\u65b9\u5411\uff0c\u624d\u8981\u8fd4\u8eab\u6574\u56de\uff0c\u5ffd\u542c\u5de6\u4fa7\u6cb3\u5cb8\u82a6\u82c7\u6df1\u5904\u6709\u811a\u6b65\u58f0\u8d70\u8fd1\uff01 \u5c55\u81ea\u542c\u90a3\u82a6\u82c7\u4e2d\u5237\u5237\u76f4\u664c\uff0c\u800c\u4e14\u811a\u6b65\u6742\u6c93\uff0c\u7edd\u4e0d\u6b62\u4e00\u3001\u4e8c\u4eba\uff0c\u5fc3\u4e2d\u4e00\u52a8\uff0c\u7acb\u523b\u627e\u4e86\u4e00\u4e2a \u8349\u4e1b\u628a\u8eab\u5f62\u9690\u853d\u8d77\u6765\uff01 \u6708\u591c\u4e2d\uff0c\u5929\u7a7a\u900f\u4eae\uff0c\u5c55\u767d\u4f0f\u5728\u5730\u9762\uff0c\u53cd\u800c\u66f4\u80fd\u770b\u5f97\u771f\u5207\uff0c\u53ea\u89c1\u4ece\u82a6\u82c7\u4e2d\u8fde\u7eed\u8d70\u51fa\u4e00\u961f\u4eba \u6765\uff01 \u8fd9\u4e9b\u4eba\u6392\u6210\u5355\u884c\uff0c\u9c7c\u8d2f\u800c\u51fa\uff0c\u6015\u4e0d\u6709\u6574\u5341\u4eba\u4e4b\u591a\uff0c\u6bcf\u4eba\u80a9\u5934\u625b\u7740\u4e00\u4e2a\u9ed1\u5ffd\u5ffd\u7684\u7bb1\u5b50\uff01 \u4e00\u76f4\u6765\u5230\u6cb3\u8fb9\uff0c\u624d\u4e00\u4e00\u628a\u80a9\u4e0a\u7bb1\u5b50\u653e\u4e0b\u5730\u6765\uff0c\u5c55\u767d\u9690\u8eab\u5730\u70b9\uff0c\u8ddd\u79bb\u90a3\u4e9b\u4eba\u5806\u653e\u7bb1\u5b50\u7684\u5730 \u70b9\u5f88\u8fd1\uff0c\u6240\u4ee5\u770b\u5f97\u5f88\u6e05\u695a\uff0c\u53ea\u89c1\u90a3\u4e9b\u4eba\u90fd\u662f\u52b2\u88c5\u5927\u6c49\uff0c\u800c\u4e14\u5176\u4e2d\u5c1a\u6709\u5341\u6570\u4e2d\u7a7f\u6234\u91d1\u76d4\u91d1\u7532\u7684 \u4eba\u7269\uff0c\u5c55\u767d\u770b\u7740\u773c\u719f\uff0c\u731b\u7136\u9192\u609f\uff0c\u8fd9\u4e0d\u662f\u91d1\u5e9c\u7684\u4eba\u5417\uff1f\u4ed6\u4eec\u4f55\u4ee5\u4f1a\u6df1\u591c\u4e4b\u95f4\uff0c\u625b\u8fd9\u4e48\u591a\u7bb1\u5b50 \u6765\u81f3\u6cb3\u8fb9\uff1f\u2026\u2026 \u5c55\u767d\u6b63\u5728\u770b\u7740\u8d77\u7591\uff0c\u5ffd\u542c\u4e00\u4e2a\u91d1\u7532\u6b66\u58eb\u5598\u5401\u547c\u5730\u8bf4\u9053\uff1a\u201c\u771f\u4e0d\u77e5\u6211\u4eec\u4e3b\u4eba\u662f\u505a\u4f55\u6253\u7b97\uff1f \u9ed1\u591c\u91cc\u8981\u6211\u4eec\u625b\u8fd9\u4e48\u6c89\u91cd\u7684\u7bb1\u5b50\u5230\u6cb3\u8fb9\u6765\uff0c\u53c8\u4e0d\u50cf\u662f\u642c\u5bb6\uff0c\u8fd9\u5012\u662f\u4ec0\u4e48\u5462\uff1f\u2026\u2026\u201d \u53e6\u5916\u4e00\u4e2a\u91d1\u7532\u6b66\u58eb\u4f4e\u58f0\u9053\uff1a\u201c\u8001\u56db\uff0c\u4f60\u8fd8\u4e0d\u77e5\u9053\u54c7\uff01\u524d\u5929\u665a\u4e0a\u2018\u77f3\u77f6\u5927\u9635\u2019\u56f0\u4f4f\u7684\u4eba\uff0c \u4eca\u5929\u4e0d\u77e5\u600e\u4e48\u90fd\u8dd1\u5149\u4e86\uff0c\u6211\u4eec\u4e3b\u4eba\u6015\u4eba\u5bb6\u96c6\u5408\u6765\u62a5\u4ec7\uff0c\u6240\u4ee5\u624d\u628a\u5bb6\u4e2d\u8d35\u91cd\u7269\u54c1\u5148\u642c\u51fa\u6765\uff0c\u4e07 \u4e00\u5e94\u4ed8\u4e0d\u4e86\uff0c\u4e5f\u597d\u6709\u4e2a\u9000\u8def\u3002\u201d \u90a3\u91d1\u7532\u6b66\u58eb\u4ee5\u60ca\u8be7\u7684\u8bed\u8c03\u9053\uff1a\u201c\u4e0d\u662f\u542c\u8bf4\u9677\u5165\u2018\u77f3\u77f6\u5927\u9635\u2019\u5c31\u6c38\u8fdc\u4e0d\u80fd\u8131\u8eab\u5417\uff1f\u600e\u4e48\u53c8 \u4f1a\u8dd1\u51fa\u6765\u4e86\u5462\uff1f\u201d \u201c\u90a3\u53ef\u5c31\u4e0d\u77e5\u9053\u4e86\uff0c\u542c\u8bf4\u53d1\u52a8\u2018\u77f3\u77f6\u5927\u9635\u2019\u5e76\u4e0d\u662f\u4e3b\u4eba\u4e4b\u547d\uff0c\u800c\u662f\u5b5f\u5c11\u7237\u64c5\u81ea\u53d1\u52a8\u7684\uff0c \u4e3a\u6b64\u4e3b\u4eba\u8fd8\u5927\u53d1\u813e\u6c14\uff0c\u8bf4\u5b5f\u5c11\u7237\u4e71\u4e86\u6b65\u9aa4\uff0c\u628a\u4e00\u4ef6\u5f88\u6709\u628a\u63e1\u7684\u4e8b\u5f04\u5f97\u4e0d\u53ef\u6536\u62fe\uff0c\u56e0\u6b64\u8fd8\u628a\u5b5f \u5c11\u7237\u5173\u8d77\u6765\u4e86\u3002\u201c \u201c\u5b5f\u5c11\u7237\u4e3a\u4ec0\u4e48\u4e0d\u542c\u4e3b\u4eba\u7684\u547d\u4ee4\u884c\u4e8b\uff0c\u96be\u9053\u4ed6\u75af\u4e86\u5417\uff1f\u201d \u201c\u4ed6\u53ef\u4e0d\u662f\u771f\u7684\u75af\u4e86\uff0c\u4f60\u60f3\u4ed6\u82e5\u4e0d\u75af\uff0c\u600e\u4e48\u4f1a\u8fde\u516c\u5b50\u5c0f\u59d0\u4e5f\u9677\u5165\u9635\u4e2d\u5462\uff1f\u201d \u53eb\u8001\u56db\u7684\u91d1\u7532\u6b66\u58eb\uff0c\u5634\u4e2d\u5567\u5567\u4e24\u58f0\uff0c\u53c8\u9053\uff1a\u201c\u4ed6\u8fd9\u53c8\u662f\u4e3a\u4ec0\u4e48\u5462\uff1f\u201d \u539f\u5148\u53d1\u8a00\u7684\u91d1\u7532\u6b66\u58eb\u9053\uff1a\u201c\u8fd8\u4e0d\u662f\u4e3a\u4e86\u59d3\u5c55\u7684\u90a3\u5c0f\u5b50\u3002\u672c\u6765\u5c0f\u59d0\u548c\u5c11\u7237\u4ece\u5c0f\u5728\u4e00\u8d77\u957f \u5927\uff0c\u60c5\u611f\u5f88\u597d\u7684\u5728\u5b5f\u5c11\u7237\u5fc3\u4e2d\u6253\u7b97\u957f\u5927\u4e86\u5a36\u5c0f\u59d0\u4e3a\u59bb\uff0c\u6211\u4eec\u8001\u4e3b\u4eba\u4e5f\u7b54\u5e94\u8fc7\u8fd9\u4ef6\u4e8b\uff0c\u53ef\u662f\uff0c \u81ea\u4ece\u59d3\u5c55\u7684\u90a3\u5c0f\u5b50\u6765\u4e86\u5e9c\u4e2d\u4e00\u8d9f\uff0c\u6211\u4eec\u5c0f\u59d0\u7a81\u7136\u5bf9\u5c11\u7237\u51b7\u6de1\u8d77\u6765\uff0c\u53cd\u800c\u5bf9\u90a3\u59d3\u5c55\u7684\u5f88\u4eb2\u8fd1\uff0c \u662f\u4ee5\u5b5f\u5c11\u7237\u6c14\u5f97\u4e0d\u5f97\u4e86\uff0c\u6ca1\u60f3\u5230\u4e0a\u6b21\u6bd4\u5251\u53c8\u88ab\u59d3\u5c55\u7684\u780d\u6389\u4e00\u53ea\u624b\u81c2\uff0c\u5b5f\u5c11\u7237\u84c4\u5fc3\u62a5\u4ec7\uff0c\u524d\u5929 \u665a\u4e0a\u53c8\u88ab\u59d3\u5c55\u7684\u5c11\u5e74\u4e00\u638c\u9707\u4f24\uff0c\u662f\u4ee5\u624d\u7a81\u7136\u53d1\u75af\uff0c\u8d81\u7740\u59d3\u5c55\u5c11\u5e74\u5728\u5927\u5385\u4e2d\uff0c\u7adf\u53d1\u52a8\u4e86\u2018\u77f3\u77f6 \u5927\u9635\u2019\u7684\u673a\u5173\u2014\u2014\u2014\u201d \u8fd9\u4e8c\u4eba\u8bf4\u8bdd\u58f0\u97f3\u5f88\u4f4e\uff0c\u4f46\u5c55\u767d\u5c31\u85cf\u5728\u9644\u8fd1\uff0c\u6240\u4ee5\u542c\u5f97\u5f88\u6e05\u695a\uff0c\u672c\u60f3\u518d\u542c\u4e0b\u53bb\uff0c\u5ffd\u89c1\u6cb3\u5bf9 \u5cb8\u73b0\u51fa\u4e86\u4e00\u4e2a\u6447\u52a8\u7684\u706f\u5149\uff0c\u4f3c\u4e4e\u662f\u5411\u8fd9\u8fb9\u6253\u6765\u7684\u6697\u53f7\u3002 \u4e00\u4e2a\u52b2\u88c5\u5927\u6c49\u8df3\u8d77\u6765\uff0c\u628a\u624b\u6307\u653e\u8fd1\u53e3\u4e2d\uff0c\u6253\u4e86\u4e00\u58f0\u5c16\u957f\u7684\u553f\u54e8\uff0c\u8ddf\u7740\u56de\u5934\u9053\uff1a\u201c\u51c6\u5907 \u7740\uff0c\u8239\u8981\u6765\u4e86\uff01\u201d \u90a3\u4e24\u4e2a\u91d1\u7532\u6b66\u4e3b\u7684\u8c08\u8bdd\uff0c\u56e0\u800c\u4e2d\u65ad\u3002\u500f\u65f6\u95f4\u6a79\u58f0\u4f9d\u5440\uff0c\u6cb3\u9762\u73b0\u51fa\u4e86\u4e00\u7247\u9ed1\u7cca\u7cca\u7684\u8239\u5f71\uff01 \u8fd9\u8239\u6765\u5f97\u6781\u5feb\uff0c\u6570\u5341\u4e08\u5bbd\u7684\u6cb3\u9762\uff0c\u8f6c\u773c\u6447\u4e86\u8fc7\u6765\uff0c\u7adf\u6709\u5341\u6570\u8258\u4e4b\u591a\uff0c\u539f\u6765\u90fd\u662f\u6cb3\u4e0a\u6e14\u592b \u6355\u9c7c\u7528\u7684\u5c0f\u821f\uff01 \u8fce\u5934\u5c0f\u821f\u4e0a\u7aef\u6446\u7740\u4e00\u5f20\u8f6e\u6905\uff0c\u8f6e\u6905\u4e0a\u5750\u7684\u6b63\u662f\u201c\u9752\u86a8\u795e\u201d\u91d1\u51e0\uff01 \u821f\u81f3\u6cb3\u8fb9\u9760\u5cb8\uff0c\u821f\u4e0a\u4e4b\u4eba\u7528\u94a9\u9976\u7a33\u4f4f\u8239\u8eab\uff0c\u5cb8\u4e0a\u4f17\u5927\u6c49\u53ca\u4e00\u4e9b\u91d1\u7532\u6b66\u58eb\uff0c\u7acb\u523b\u625b\u8d77\u7bb1\u5b50 \u5411\u8239\u4e0a\u642c\u53bb\uff01 \u5c55\u767d\u4e00\u89c1\u201c\u9752\u86a8\u795e\u201d\u91d1\u4e5d\uff0c\u771f\u662f\u4ec7\u4eba\u6839\u89c1\uff0c\u5206\u5916\u773c\u7ea2\uff0c\u53ea\u611f\u70ed\u8840\u6cb8\u817e\uff0c\u518d\u4e5f\u9690\u5fcd\u4e0d\u4f4f\uff0c \u731b\u7136\u8df3\u51fa\uff0c\u559d\u9053\uff1a\u91d1\u4e5d\uff01\u8001\u8d3c\uff01\u7eb3\u547d\u6765\u5427\uff01\u201d \u559d\u58f0\u4e2d\u4e00\u638c\u731b\u5411\u91d1\u4e5d\u5288\u53bb\uff01\u5c55\u767d\u7a81\u7136\u51fa\u73b0\uff0c\u51fa\u624b\u53c8\u5feb\uff0c\u5cb8\u4e0a\u90a3\u4e48\u591a\u4eba\u7ade\u6765\u4e0d\u53ca\u963b\u62e6\uff0c\u9f50 \u58f0\u60ca\u547c\uff0c\u5c55\u767d\u5982\u72c2\u98d9\u7684\u638c\u98ce\u5df2\u5411\u201c\u9752\u86a8\u795e\u201d\u91d1\u4e5d\u8fce\u80f8\u5377\u81f3\uff01 \u201c\u9752\u86a8\u795e\u201d\u91d1\u4e5d\u4f30\u4e0d\u5230\u4f1a\u7a81\u7136\u906d\u5230\u6697\u88ad\uff0c\u767e\u5fe7\u4e2d\u8fd0\u638c\u4e00\u5bf9\uff0c\u201c\u562d\u201d\u7684\u4e00\u58f0\u5927\u9707\uff0c\u7ade\u4f7f\u4ed6 \u7684\u8f6e\u6905\u5728\u8239\u4e0a\u4e00\u9635\u4e71\u6643\uff0c\u9669\u4e9b\u843d\u5230\u6cb3\u4e2d\uff01 \u5c1a\u591a\u4e8f\u4ed6\u95e8\u4e0b\u4e00\u540d\u9ad8\u624b\uff0c\u5728\u4ed6\u8eab\u540e\u6025\u7528\u53cc\u624b\u628a\u8f6e\u6905\u7a33\u4f4f\uff0c\u4f46\u4ed6\u5750\u7684\u4e00\u53ea\u5c0f\u8239\uff0c\u5374\u56e0\u638c\u529b \u4e00\u9707\uff0c\u5728\u6c34\u4e2d\u8361\u79bb\u4e86\u5cb8\u8fb9\uff01 \u201c\u9752\u86a8\u795e\u201d\u91d1\u4e5d\u89c1\u81ea\u5df1\u79d8\u5bc6\u88ab\u4eba\u7aa5\u7834\uff0c\u53c8\u60ca\u53c8\u6012\uff0c\u5728\u6cb3\u5fc3\u4e2d\u9ad8\u53eb\u9053\uff1a\u201c\u5feb\u628a\u6765\u4eba\u62ff\u4e0b\uff01 \u4e00\u4e2a\u4e0d\u653e\uff0c\u683c\u6740\u52ff\u8bba\uff01\u201d \u56e0\u4e3a\u4ed6\u4e0d\u77e5\u6765\u4e86\u591a\u5c11\u654c\u624b\uff0c\u6545\u6709\u6b64\u4e00\u8bf4\u3002\u5c55\u767d\u5374\u5fc3\u4e2d\u6697\u6697\u540e\u6094\uff0c\u4e0d\u8be5\u592a\u6c89\u4e0d\u4f4f\u6c14\uff0c\u5982\u4eca \u201c\u9752\u86a8\u795e\u201d\u5230\u4e86\u6cb3\u4e2d\u95f4\uff0c\u81ea\u5df1\u65e0\u6cd5\u518d\u52a0\u8ffd\u51fb\u2026\u2026 \u540c\u65f6\uff0c\u5cb8\u4e0a\u4f17\u5927\u6c49\u53ca\u91d1\u7532\u6b66\u58eb\uff0c\u5df2\u6709\u5341\u6570\u4eba\u4e22\u4e0b\u7bb1\u5b50\uff0c\u5411\u4ed6\u56f4\u4e86\u4e0a\u6765\uff01 \u4f17\u6b66\u58eb\u53ca\u52b2\u88c5\u5927\u6c49\u5411\u5c55\u767d\u56f4\u4e0a\u6765\u65f6\uff0c\u5df2\u7528\u76ee\u5411\u56db\u5468\u6253\u91cf\uff0c\u89c1\u6cb3\u8fb9\u4e0a\u5c31\u5c55\u767d\u4e00\u4eba\uff0c\u4e0d\u7531\u80c6 \u6c14\u9661\u58ee\uff0c\u5927\u559d\u4e00\u58f0\uff0c\u6709\u56db\u540d\u91d1\u7532\u6b66\u58eb\u4e3e\u5251\uff0c\u9f50\u5411\u5c55\u767d\u523a\u6765\uff01 \u7b2c\u4e09\u5341\u516d\u7ae0\u3000\u795e\u79d8\u9b3c\u9762\u5973>> \u53e4\u9f99\u300a\u5251\u5ba2\u884c\u300b \u7b2c\u4e09\u5341\u516d\u7ae0\u3000\u795e\u79d8\u9b3c\u9762\u5973 \u5c55\u767d\u8eab\u5f62\u4e00\u65cb\uff0c\u907f\u5f00\u5251\u950b\uff0c\u5355\u638c\u6a2a\u626b\uff0c\u72c2\u98d8\u6012\u5377\uff0c\u56db\u540d\u91d1\u7532\u6b66\u58eb\u9f50\u58f0\u60e8\u964d\uff0c\u56db\u6563\u7740\u8dcc\u51fa \u4e00\u4e8c\u4e08\u5916\uff01 \u4f17\u58ee\u6c49\u9f50\u58f0\u60ca\u547c\uff0c\u811a\u6b65\u7acb\u65f6\u505c\u6b62\uff0c\u663e\u7136\u88ab\u5c55\u767d\u4e00\u638c\u9707\u5012\u56db\u4eba\u7684\u5a01\u52bf\u9707\u4f4f\uff01 \u7a81\u542c\u4e00\u58f0\u5389\u5578\uff0c\u4e00\u6761\u9ed1\u5f71\u845b\u5730\u5347\u7a7a\u800c\u8d77\uff0c\u534a\u7a7a\u4e2d\u8eab\u5f62\u4e00\u65cb\uff0c\u5019\u7136\u75be\u6251\u4e0b\u6765\uff0c\u72b9\u5982\u4e00\u53ea\u670b \u5927\u5de8\u9e1f\uff0c\u94c1\u7ffc\u731b\u6325\uff0c\u76f4\u5411\u5c55\u767d\u8fce\u5934\u6251\u4e0b\uff01 \u5c55\u767d\u4ece\u90a3\u8eab\u6cd5\u53ca\u4e0b\u6251\u4e4b\u52bf\u7684\u5a01\u731b\uff0c\u5df2\u80fd\u5224\u65ad\u51fa\u6765\u4eba\u662f\u201c\u91d1\u5e9c\u53cc\u94c1\u536b\u201d\u4e4b\u4e00\u7684\u201c\u94c1\u7ffc\u98de \u9e4f\u201d\u5df4\u5929\u8d6b\uff01 \u4f46\u5c55\u767d\u7ecf\u8fc7\u201c\u795e\u9a74\u94c1\u80c6\u201d\u767e\u65e5\u4f20\u529f\uff0c\u6b66\u529f\u7a81\u98de\u731b\u8fdb\uff0c\u81ea\u4fe1\u5fc3\u4e5f\u5927\u4e3a\u589e\u5f3a\uff0c\u867d\u77e5\u201c\u94c1\u7ffc\u98de \u9e4f\u201d\u5df4\u5929\u8d6b\u4e43\u91d1\u5e9c\u9876\u5c16\u9ad8\u624b\uff0c\u800c\u4e14\u6709\u201c\u94c1\u7ffc\u795e\u8863\u201d\u4e4b\u52a9\uff0c\u51cc\u7a7a\u4e0b\u6251\u4e4b\u52bf\uff0c\u5a01\u4e0d\u53ef\u6321\uff0c\u4ecd\u7136\u4e0d \u8eb2\u4e0d\u95ea\uff0c\u6325\u81c2\u5411\u4e0a\u8fce\u53bb\uff01 \u4e00\u4e0a\u4e00\u4e0b\u4e24\u80a1\u6781\u5927\u65e0\u6bd4\u7684\u638c\u529b\u76f8\u63a5\uff0c\u534a\u7a7a\u6c14\u7206\uff0c\u5de8\u54cd\u5982\u96f7\uff0c\u96c7\u81ea\u6602\u7acb\u539f\u5730\u4e0d\u52a8\uff0c\u534a\u7a7a\u4e2d \u7684\u201c\u94c1\u7ffc\u98de\u9e4f\u201d\u5374\u88ab\u9707\u5f97\u8fde\u7ffb\u4e86\u4e24\u4e2a\u7b4b\u6597\uff0c\u624d\u655b\u7fc5\u659c\u63a0\uff0c\u843d\u4e8e\u5730\u9762\uff01 \u8fd9\u4e00\u6765\uff0c\u91d1\u5e9c\u4e4b\u4eba\u5bf8\u4e2a\u5fc3\u60ca\uff0c\u60f3\u5929\u4e0b\u6b66\u6797\u80fd\u591f\u63a5\u5f97\u4f50\u201c\u94c1\u7ffc\u98de\u9e4f\u201d\u4e34\u7a7a\u4e00\u51fb\u7684\uff0c\u53ef\u4ee5\u8bf4 \u662f\u5be5\u5be5\u53ef\u6570\uff0c\u4f55\u51b5\u8fd8\u80fd\u4f7f\u201c\u94c1\u7ffc\u98de\u9e4f\u201d\u5c48\u5c45\u4e0b\u98ce\u5462\uff01 \u201c\u94c1\u7ffc\u98de\u9e4f\u201d\u843d\u4e8e\u5730\u9762\uff0c\u53cc\u7ffc\u4e00\u6536\u4e00\u5f20\uff0c\u9ed1\u591c\u4e2d\u867d\u7136\u770b\u4e0d\u6e05\u4ed6\u7684\u8138\u8272\uff0c\u60f3\u5fc5\u4e5f\u662f\u5403\u60ca\u4e0d \u5c0f\uff0c\u6b63\u5728\u9f13\u7ffc\u7eb3\u6c14\uff0c\u51c6\u5907\u4f5c\u7b2c\u4e8c\u6b21\u7684\u6251\u51fb\uff01\u2026\u2026 \u7a81\u7136\u5c0f\u8239\u4e0a\u4eae\u8d77\u5341\u6570\u76cf\u5b54\u660e\u706f\uff0c\u5341\u6570\u9053\u706f\u7684\u5149\u8292\uff0c\u4ea4\u9519\u5730\u9f50\u5411\u5cb8\u4e0a\u5c55\u767d\u505c\u8eab\u4e4b\u5904\u7167\u5c04\u800c \u6765\uff01 \u5c55\u767d\u5728\u5341\u6570\u9053\u706f\u5149\u4ea4\u5c04\u4e4b\u4e0b\uff0c\u7acb\u523b\u7ea4\u5fae\u6bd5\u73b0\uff0c\u53ea\u89c1\u4ed6\u8eab\u4e0a\u88f9\u7740\u4e00\u6761\u7834\u68c9\u88ab\uff0c\u8170\u91cc\u624e\u7740\u51e0 \u6761\u7834\u5e03\uff0c\u53cc\u81c2\u53cc\u817f\u5747\u9732\u5728\u5916\u9762\uff0c\u800c\u4e14\u4e5f\u591a\u88f9\u7740\u7834\u5e03\u6761\uff0c\u8d64\u8db3\u672a\u7a7f\u9774\uff0c\u6837\u5b50\u72fc\u72c8\u5df2\u6781\uff01 \u4f46\u4ed6\u82f1\u4fca\u633a\u62d4\u7684\u8138\u4e0a\uff0c\u5374\u9732\u51fa\u51db\u7136\u4e0d\u53ef\u4fb5\u72af\u7684\u4e00\u80a1\u6740\u6c14\uff01 \u91d1\u5e9c\u4f17\u4eba\uff0c\u591a\u534a\u8ba4\u8bc6\u4ed6\uff0c\u89c1\u72b6\u4e0d\u7531\u9f50\u58f0\u60ca\u81c6\uff1a\u201c\u554a\uff01\u539f\u6765\u662f\u4ed6\uff01\u2026\u2026\u201d\u201c\u9752\u86a8\u795e\u8fd1\u4e5d\u66f4 \u662f\u5728\u8239\u4e0a\u9ad8\u58f0\u5927\u53eb\uff1a\u201c\u83ab\u8981\u653e\u8d70\u5c0f\u8d3c\uff01\u4e0a\u5440\uff0c\u62ff\u4f4f\u4ed6\uff01\u201d \u4e00\u7fa4\u58ee\u6c49\uff0c\u4ee5\u53ca\u5341\u6570\u540d\u91d1\u7532\u6b66\u58eb\u9f50\u63e1\u5175\u5668\uff0c\u5f80\u524d\u4e00\u56f4\uff0c\u7acb\u523b\u628a\u5c55\u767d\u56e0\u5728\u6838\u5fc3\uff0c\u4f46\u88ab\u5c55\u767d \u5a01\u52bf\u6240\u6151\uff0c\u4e00\u65f6\u8fd8\u5728\u8d8a\u8d85\uff0c\u672a\u66fe\u51fa \u7a81\u542c\u4e00\u58f0\u8c6a\u7b11\uff0c\u58f0\u53ef\u9707\u5929\uff0c\u7b11\u58f0\u4e2d\u4e00\u9a7c\u80cc\u8001\u8005\uff0c\u8d8a\u4f17\u800c\u51fa\uff01\u6b64\u4eba\u6b63\u662f\u201c\u91d1\u5e9c\u53cc\u94c1\u536b\u201d\u7684 \u53e6\u5916\u4e00\u4f4d\u201c\u94c1\u80cc\u9a7c\u9f99\u201d\u516c\u5b59\u695a\uff01 \u53ea\u89c1\u4ed6\u5927\u7b11\u8bf4\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\u6570\u65e5\u4e0d\u89c1\uff0c\u6b66\u529f\u53c8\u589e\u5f3a\u4e86\u4e0d\u5c11\uff01\u554a\u2014\u2014\u55ec\uff01\u53ef\u662f\uff0c\u8001\u592b\u6709\u4e00 \u4e8b\u4e0d\u660e\uff0c\u5c1a\u8bf7\u5c0f\u5144\u5f1f\u4e0d\u541d\u89c1\u544a\uff0c\u91d1\u5e9c\u7d20\u6765\u62ff\u5c0f\u5144\u5f1f\u5f53\u5ba2\u4eba\u4e00\u822c\u6b3e\u5f85\uff0c\u4e3a\u4f55\u5c0f\u5144\u5f1f\u4e09\u756a\u4e24\u6b21\u4e0e \u91d1\u5e9c\u4f5c\u5bf9\uff1f\u662f\u4f55\u9053\u7406\uff1f\u201d \u5c55\u767d\u7d20\u5bf9\u201c\u94c1\u80cc\u9a7c\u9f99\u201d\u6709\u597d\u611f\uff0c\u5c24\u5176\u81ea\u5df1\u521d\u8fdb\u91d1\u5e9c\u65f6\uff0c\u5c1a\u8499\u6b64\u8001\u4e0d\u5c11\u7167\u987e\uff0c\u95fb\u8a00\u5f3a\u5fcd\u4f50 \u5fc3\u4e2d\u4ec7\u706b\u9053\uff1a\u201c\u516c\u5b59\u524d\u8f88\u6709\u6240\u4e0d\u77e5\uff0c\u665a\u8f88\u5148\u7236\u88ab\u2018\u9752\u86a8\u795e\u2019\u91d1\u4e5d\u8001\u8d3c\u6240\u5bb3\uff0c\u665a\u8f88\u4e0e\u8001\u8d3c\u6709\u6740 \u7236\u4e4b\u4ec7\uff0c\u5c82\u53ef\u4e00\u65e5\u6216\u5fd8\uff0c\u4f46\u665a\u8f88\u6740\u7236\u4ec7\u4eba\u53ea\u8001\u8d3c\u4e00\u4eba\uff0c\u4e0e\u4f17\u4eba\u65e0\u5173\uff0c\u5982\u679c\u516c\u5b59\u524d\u8f88\u653e\u624b\u4e0d \u7ba1\uff0c\u5c55\u767d\u7edd\u4e0d\u4e0e\u524d\u8f88\u4e3a\u654c\uff01\u201d \u516c\u5b59\u695a\u695e\u7136\u52a8\u5bb9\u9053\uff1a\u201c\u4e0d\u77e5\u5c0f\u5144\u5f1f\u5148\u7236\u662f\u4f55\u4eba\uff1f\u201d \u5c55\u767d\u51c4\u7136\u9053\uff1a\u201c\u5b50\u4e0d\u8a00\u7236\u540d\uff0c\u4f46\u65e2\u662f\u524d\u8f88\u95ee\u8d77\uff0c\u665a\u8f88\u4e5f\u4e0d\u76f8\u7792\uff0c\u5148\u7236\u5c31\u662f\u2018\u9739\u96f3\u5251\u2019\u5c55 \u4e91\u5929\uff01\u201d \u201c\u94c1\u80cc\u9a7c\u9f99\u201d\u54e6\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u90a3\u4f8b\u662f\u5931\u656c\u4e86\uff0c\u539f\u6765\u5c0f\u5144\u5f1f\u4e43\u662f\u5c55\u5927\u4fa0\u7684\u540e\u4eba\u2026\u2026\u201d \u660f\u9ed1\u4e2d\u53ea\u542c\u6d46\u6a79\u6fc0\u6c34\u4e4b\u58f0\uff0c\u5c55\u81ea\u6015\u201c\u9752\u86a8\u795e\u201d\u501f\u661f\u591c\u6e9c\u8d70\uff0c\u5fd9\u9053\uff1a\u201c\u8bdd\u5df2\u8bf4\u660e\uff0c\u516c\u5b59\u524d \u8f88\uff0c\u662f\u5426\u53ef\u653e\u624b\u4e0d\u7ba1\uff1f\u201d \u201c\u94c1\u80cc\u9a7c\u9f99\u201d\u516c\u5b59\u695a\u9762\u73b0\u96be\u8272\uff0c\u5c1a\u5728\u6c89\u541f\u672a\u51b3\u3002\u5c55\u81ea\u5df2\u542c\u90a3\u8239\u821f\u6fc0\u6c34\u4e4b\u58f0\uff0c\u6e10\u53bb\u6e10\u8fdc\uff0c \u4f46\u6570\u9053\u5f3a\u70c8\u706f\u5149\u7167\u7740\u4ed6\uff0c\u654c\u6697\u6211\u660e\uff0c\u770b\u4e0d\u6e05\u695a\u6cb3\u4e2d\u60c5\u5f62\uff0c\u5c55\u767d\u4e0d\u7531\u6025\u8d77\u6765\uff0c\u5927\u53eb\u9053\uff1a\u91d1\u4e5d\u8001 \u8d3c\u4f11\u8d70\uff01\u2026\u2026\u201d \u559d\u58f0\u4e2d\uff0c\u98de\u8eab\u5411\u6cb3\u8fb9\u6251\u53bb\uff01 \u201c\u94c1\u80cc\u9a7c\u9f99\u201d\u516c\u5b59\u695a\uff0c\u4eba\u4e2d\u6781\u91cd\u4e49\u6c14\uff0c\u5bf9\u201c\u9739\u96f3\u5251\u201d\u5c55\u4e91\u5929\u7684\u4fa0\u540d\uff0c\u4e5f\u65e9\u6709\u8033\u95fb\uff0c\u542c\u5c55 \u767d\u81ea\u62a5\u8eab\u4efd\uff0c\u672c\u65e0\u610f\u51fa\u624b\uff0c\u4f46\u53c8\u62d8\u4e8e\u8eab\u5728\u91d1\u5e9c\uff0c\u98df\u4eba\u539a\u7984\uff0c\u65e0\u6cd5\u51b3\u5b9a\u81ea\u5df1\u8be5\u4e0d\u8be5\u51fa\u624b\uff0c\u6545\u6b64 \u4e00\u65f6\u59d4\u51b3\u4e0d\u4e0b\uff0c\u4eca\u89c1\u5c55\u81ea\u8eab\u5f62\u63a0\u8d77\uff0c\u4ed6\u60f3\u6321\u963b\u5c55\u767d\uff0c\u628a\u8bdd\u4ea4\u4ee3\u660e\u767d\uff0c\u7136\u540e\u518d\u4f5c\u5b9a\u593a\uff0c\u5fd9\u9053\uff1a \u201c\u5c0f\u5144\u5f1f\u6162\u6765\uff0c\u4e14\u542c\u8001\u592b\u4e00\u8a00\u2026\u2026\u201d \u540c\u65f6\uff0c\u4e5f\u63a2\u81c2\u5411\u5c55\u767d\u6293\u53bb\uff01 \u5c55\u767d\u8eab\u5f62\u63a0\u8d77\uff0c\u7a81\u89c1\u201c\u94c1\u80cc\u9a7c\u9f99\u201d\u5411\u81ea\u5df1\u53f3\u81c2\u6293\u6765\uff0c\u8bef\u4ee5\u4e3a\u201c\u94c1\u80cc\u9a7c\u9f99\u201d\u5df2\u5411\u81ea\u5df1\u51fa \u624b\u3002\u5fc3\u6025\u4ec7\u4eba\u53bb\u5411\uff0c\u4e5f\u4e0d\u8eb2\u95ea\uff0c\u5c31\u5728\u534a\u7a7a\u4e2d\u53f3\u81c2\u4e00\u6296\uff0c\u4e94\u6307\u53cd\u5f39\u201c\u94c1\u80cc\u9a7c\u9f99\u201d\u6293\u5411\u81ea\u5df1\u53f3\u81c2 \u7684\u8098\u8155\u5173\u5143\uff01 \u201c\u94c1\u80cc\u9a7c\u9f99\u201d\u672a\u9632\u5c55\u767d\u53cd\u51fb\uff0c\u4e5f\u4f30\u4e0d\u5230\u5c55\u767d\u6709\u5982\u6b64\u5feb\u6377\u624b\u6cd5\uff0c\u731b\u89c1\u5c55\u767d\u6296\u81c2\u8131\u51fa\u81ea\u5df1\u638c \u5916\uff0c\u521a\u4e00\u758f\u795e\uff0c\u5c55\u767d\u4e94\u6307\u7834\u98ce\uff0c\u5df2\u5f39\u5411\u81ea\u5df1\u201c\u5173\u5143\u201d\u91cd\u7a74\uff0c\u77e5\u9053\u8fd9\u201c\u5173\u5143\u7a74\u201d\u5982\u88ab\u5f39\u4e2d\uff0c\u81ea \u5df2\u4e00\u6761\u53f3\u81c2\u5c31\u7b97\u6bc1\u4e86\uff0c\u771f\u662f\u53c8\u60ca\u53c8\u6012\uff0c\u8c05\u7684\u662f\u5c55\u767d\u5c0f\u5c0f\u5e74\u7eaa\uff0c\u7adf\u5df2\u6709\u4e86\u8fd9\u79cd\u8d85\u4e4e\u60f3\u50cf\u7684\u9ad8\u5f3a \u624b\u6cd5\uff0c\u6012\u7684\u662f\u81ea\u5df2\u4e00\u756a\u597d\u610f\uff0c\u5c55\u767d\u7ade\u5bf9\u81ea\u5df1\u65bd\u51fa\u6740\u624b\uff0c\u66f4\u4f55\u51b5\u4ed6\u7d20\u6781\u81ea\u8d1f\uff0c\u5fc3\u76ee\u4e2d\u628a\u5c55\u767d\u770b \u6210\u665a\u8f88\uff0c\u5c55\u767d\u8fd9\u4e00\u62db\u65bd\u51fa\uff0c\u5206\u660e\u672b\u628a\u4ed6\u770b\u5728\u773c\u5185\uff0c\u60ca\u6012\u4e4b\u4e2d\u66f4\u52a0\u6c14\u6124\uff0c\u62fc\u7740\u53f3\u81c2\u53d7\u4f24\uff0c\u731b\u7136 \u5410\u6c14\u5f00\u58f0\uff0c\u4ee5\u5de6\u638c\u731b\u6263\u5c55\u767d\u540e\u5fc3\uff01 \u5c55\u767d\u81ea\u77e5\u5371\u673a\u4e00\u53d1\uff0c\u800c\u4e14\u4ed6\u4e5f\u65e0\u610f\u4e0e\u201c\u94c1\u80cc\u9a7c\u9f99\u201d\u62fc\u547d\uff0c\u6025\u5fd9\u6536\u62db\uff0c\u8170\u91cc\u4e00\u53e0\u52b2\uff0c\u7fe9\u7136 \u6a2a\u98d8\u4e00\u4e08\u5f00\u5916\uff01 \u201c\u94c1\u80cc\u9a7c\u9f99\u201d\u987b\u7709\u7686\u70b8\uff0c\u6012\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\u5982\u6b64\u72c2\u50b2\uff0c\u6562\u60c5\u662f\u81ea\u6043\u6b66\u529f\u9ad8\uff0c\u6765\uff01\u6765\u6765\uff01\u8001 \u592b\u5012\u8981\u9886\u6559\u51e0\u624b\u9ad8\u62db\uff01\u201d \u8bf4\u7f62\uff0c\u62f1\u8eab\u5f2f\u80cc\uff0c\u53cc\u624b\u5341\u6307\u7b95\u5f20\uff0c\u6f2b\u7a7a\u4e00\u821e\uff0c\u76f4\u5411\u5c55\u767d\u6293\u6765\uff01 \u5c55\u767d\u89c1\u4ed6\u795e\u6001\u5a01\u731b\uff0c\u5c48\u80cc\u5982\u5f13\uff0c\u4e24\u722a\u5982\u94a2\u94a9\u4e00\u822c\uff0c\u6f2b\u7a7a\u6325\u821e\u800c\u4e0b\uff0c\u52a0\u4e0a\u4ed6\u6ee1\u5934\u82cd\u53d1\u968f\u98ce \u5e61\u626c\uff0c\u989d\u4e0b\u7eb5\u987b\u7ed5\u988a\uff0c\u53cc\u773c\u6012\u7741\uff0c\u775b\u5149\u5982\u70ac\uff1b\u770b\u5230\u4ed6\u7684\u5f62\u8c61\uff0c\u5c55\u767d\u731b\u7136\u8bb0\u8d77\uff0c\u8fd9\u53ef\u80fd\u5c31\u662f\u6b64 \u8001\u201c\u94c1\u80cc\u9a7c\u9f99\u201d\u7ef0\u53f7\u7684\u6765\u6e90\u4e86\uff0c\u770b\u4ed6\u7684\u6837\u5b50\uff0c\u771f\u5982\u2014\u6761\u5f20\u7259\u821e\u722a\u7684\u5de8\u9f99\u4e00\u6837\uff01 \u5c55\u767d\u88ab\u4ed6\u7684\u5a01\u52bf\u6240\u6151\uff0c\u5fc3\u4e2d\u4e00\u64e6\uff0c\u4e0d\u6562\u786c\u63a5\uff0c\u98d8\u8eab\u5411\u4e00\u4fa7\u8eb2\u8fc7\uff01 \u53ef\u662f\uff0c\u5c55\u767d\u7acb\u8db3\u672a\u7a33\uff0c\u5ffd\u542c\u8111\u540e\u98ce\u751f\uff0c\u77e5\u6709\u6697\u88ad\uff0c\u4f46\u60c5\u52bf\u5371\u6025\uff0c\u6765\u4e0d\u53ca\u56de\u5934\u5bdf\u770b\u6c5f\u731b\u7136 \u56de\u8eab\u8fd0\u638c\u5f80\u5916\u4e00\u5bf9\uff0c\u201c\u562d\u201d\u7684\u4e00\u58f0\u5927\u9707\uff0c\u7ade\u9707\u5f97\u5c55\u767d\u53cc\u81c2\u53d1\u9ebb\uff0c\u5fc3\u8840\u7ffb\u6d8c\uff0c\u8e6c\uff01\u8e6c\uff01\u8e6c\uff01\u5012 \u9000\u4e09\u6b65\uff01 \u5c55\u767d\u6697\u9053\uff1a\u597d\u5927\u7684\u638c\u52b2\uff01\u201d \u8fd8\u4ee5\u4e3a\u53c8\u6765\u4e86\u4ec0\u4e48\u9ad8\u624b\uff0c\u5f85\u56de\u5934\u770b\u6e05\uff0c\u624d\u77e5\u53c8\u662f\u201c\u94c1\u7ffc\u98de\u9e4f\u201d\u5df4\u5927\u8d6b\uff01\u800c\u4e14\uff0c\u4e00\u51fb\u5f97 \u624b\uff0c\u8fd8\u5728\u90a3\u91cc\u53cc\u7ffc\u7fd5\u5f20\uff0c\u6012\u89c6\u7740\u5c55\u767d\uff01 \u7b2c\u4e00\u6b21\u51cc\u7a7a\u4e0b\u51fb\uff0c\u201c\u94c1\u7ffc\u98de\u9e4f\u201d\u53ea\u4ee5\u516d\u6210\u529f\u529b\uff0c\u5403\u4e86\u5c55\u767d\u4e00\u6b21\u6697\u4e8f\uff0c\u6240\u4ee5\u4ed6\u8fd9\u7b2c\u4e8c\u6b21\u4e0b \u51fb\uff0c\u5df2\u7528\u51fa\u4e86\u5168\u529b\u3002 \u5c55\u767d\u5374\u662f\u5306\u5fd9\u56de\u8eab\uff0c\u672a\u80fd\u8fd0\u51fa\u5168\u529b\uff0c\u662f\u4ee5\u53cd\u88ab\u201c\u94c1\u7ffc\u98de\u9e4f\u201d\u638c\u529b\u6240\u632b\uff01 \u5c55\u767d\u618e\u7136\u60ca\u89c6\u4e4b\u4e2d\uff0c\u8eab\u540e\u53c8\u4f20\u6765\u201c\u94c1\u80cc\u9a7c\u9f99\u201d\u5410\u6c14\u6c89\u559d\u4e4b\u58f0\uff0c\u63a5\u7740\u4e00\u822c\u72c2\u5927\u52b2\u6d41\u76f4\u5411\u81ea \u5df1\u8eab\u540e\u6d8c\u81f3\uff01\u60f3\u4e0d\u5230\u540d\u9707\u6c5f\u6e56\u7684\u4e24\u5927\u9ad8\u624b\uff0c\u7adf\u4e5f\u8054\u8d77\u624b\u6765\u5411\u4ed6\u653b\u51fb\uff0c\u5c55\u767d\u5019\u7136\u800c\u6012\uff0c\u4e5f\u6fc0\u53d1 \u4e86\u8c6a\u6027\uff0c\u4e0d\u518d\u8eb2\u95ea\uff0c\u628a\u201c\u795e\u9a74\u94c1\u80c6\u201d\u4f20\u6388\u4ed6\u7684\u201c\u98ce\u96f7\u516b\u5251\u201d\uff0c\u4ee5\u638c\u6cd5\u65bd\u51fa\uff0c\u4e00\u62db\u201c\u8fc5\u98ce\u75be \u96f7\u201d\uff0c\u4ee5\u638c\u4ee3\u5251\uff0c\u731b\u5411\u8eab\u540e\u780d\u53bb\uff01\u201c\u5657\u201d\u7684\u4e00\u58f0\uff0c\u5c55\u767d\u4e00\u638c\uff0c\u5982\u51fb\u8d25\u9769\uff01 \u539f\u6765\u201c\u94c1\u80cc\u9a7c\u9f99\u201d\u88ab\u5c55\u767d\u6fc0\u6012\uff0c\u4e00\u62db\u201c\u82cd\u9f99\u8212\u722a\u201d\uff0c\u53c8\u88ab\u5c55\u767d\u8eb2\u8fc7\uff0c\u6b64\u8001\u672c\u5c31\u6027\u70c8\u5982 \u706b\uff0c\u6b64\u65f6\u66b4\u6012\u66f4\u751a\uff0c\u89c1\u5c55\u767d\u53c8\u4e0e\u201c\u94c1\u7ffc\u98de\u9e4f\u201d\u786c\u5bf9\u4e86\u4e00\u638c\uff0c\u7acb\u5373\u65bd\u51fa\u4e00\u62db\u201c\u6f5c\u9f99\u63a2\u6d77\u201d\uff0c\u53cc \u624b\u9f50\u5f20\uff0c\u5168\u8eab\u5411\u5c55\u767d\u8eab\u540e\u6251\u53bb\uff0c\u4ed6\u672c\u60f3\u6293\u4f50\u5c55\u767d\uff0c\u4f46\u5c55\u767d\u8eab\u6cd5\u592a\u5feb\uff0c\u53cd\u81c2\u4e00\u638c\uff0c\u6b63\u597d\u780d\u5728 \u201c\u94c1\u80cc\u9a7c\u9f99\u201d\u7684\u9a7c\u80cc\u4e0a\uff01 \u201c\u94c1\u80cc\u9a7c\u9f99\u201d\u53ea\u770b\u4ed6\u8fd9\u7ef0\u53f7\uff0c\u4fbf\u77e5\u4ed6\u7136\uff0c\u5c55\u767d\u529b\u53ef\u5f00\u77f3\u65ad\u91d1\u7684\u4e00\u638c\uff0c\u7ed3\u7ed3\u5b9e\u5b9e\u5730\u780d\u5728\u4ed6 \u7684\u80cc\u4e0a\uff0c\u4e1d\u6beb\u672a\u4f7f\u4ed6\u8d1f\u4f24\uff0c\u53cd\u800c\u4f7f\u5c55\u767d\u638c\u7f18\u611f\u5230\u4e00\u9635\u5de8\u75db\uff01 \u4f46\u8fd9\u4e00\u638c\u7684\u529b\u9053\uff0c\u5374\u628a\u201c\u94c1\u80cc\u9a7c\u9f99\u201d\u76f4\u6253\u51fa\u4e00\u4e08\u5f00\u5916\uff0c\u201c\u94c1\u80cc\u9a7c\u9f99\u201d\u8ddf\u8dea\u524d\u6251\uff0c\u51e0\u4e4e\u6765 \u4e86\u4e2a\u5927\u9a6c\u722c\uff0c\u8fd9\u4e00\u4e0b\u201c\u94c1\u80cc\u9a7c\u9f99\u201d\u66f4\u662f\u66b4\u5ffd\u5982\u96f7\uff0c\u8fd9\u662f\u4ed6\u6709\u751f\u4ee5\u6765\uff0c\u4ece\u672a\u683d\u8fc7\u7684\u8ddf\u6597\uff0c\u6fc0\u6012 \u4e4b\u4e0b\u53cd\u8eab\u6025\u4e0a\uff0c\u201c\u6012\u9f99\u6405\u5c3e\u201d\uff0c\u4e00\u811a\u8e22\u5411\u5c55\u767d\u5c0f\u8179\uff0c\u8ddf\u7740\u4e0a\u76d8\u201c\u9f99\u51fa\u6df1\u6f6d\u201d\uff0c\u731b\u5411\u5c55\u767d\u8fce\u80f8 \u6363\u51fa\u4e00\u62f3\uff0c\u540c\u65f6\u5de6\u624b\u5e76\u6307\u75be\u70b9\u5c55\u767d\u7684\u53cc\u76ee\uff0c\u4e43\u662f\u4e00\u62db\u201c\u53cc\u9f99\u62a2\u73e0\u201d\uff0c\u8fde\u73af\u65bd\u51fa\u4e09\u5927\u6740\u62db\uff0c\u771f \u53ef\u4f7f\u98ce\u4e91\u53d8\u8272\uff01 \u5c55\u767d\u811a\u8e29\u201c\u4e5d\u4e5d\u5f52\u5143\u201d\u6b65\u6cd5\uff0c\u8fd9\u6b65\u6cd5\u4e5f\u662f\u201c\u795e\u9a74\u94c1\u80c6\u201d\u4f20\u6388\u4ed6\u7684\uff0c\u8e29\u5bf9\u4e86\u6b65\u4f4d\uff0c\u8eab\u5f62\u770b \u4f3c\u65e0\u751a\u8fdb\u9000\uff0c\u4f46\u4e0d\u8bba\u5bf9\u65b9\u4f7f\u4f55\u79cd\u62db\u5f0f\uff0c\u4e5f\u4f11\u60f3\u4f24\u5f97\u5206\u6beb\uff0c\u672c\u4e5f\u662f\u201c\u795e\u9a74\u94c1\u80c6\u201d\u7684\u4e00\u79cd\u7edd\u827a\uff0c \u53ea\u4e3a\u4e86\u4e0e\u201c\u795e\u7334\u201d\u6253\u8d4c\uff0c\u8981\u60f3\u80dc\u8fc7\u5a49\u513f\u7684\u201c\u8e51\u7a7a\u5e7b\u5f71\u201d\u6b65\u6cd5\uff0c\u624d\u4f20\u7ed9\u5c55\u767d\uff0c\u6ca1\u60f3\u5230\u5c55\u767d\u5728\u6b64 \u65f6\u6d3e\u4e0a\u4e86\u7528\u573a\uff0c\u201c\u94c1\u80cc\u9a7c\u9f99\u201d\u7684\u4e09\u5927\u6740\u62db\uff0c\u7adf\u88ab\u4ed6\u4ece\u4ece\u5bb9\u5bb9\u5730\u8eb2\u8fc7\uff01 \u201c\u94c1\u7ffc\u98de\u9e4f\u201d\u89c1\u201c\u94c1\u80cc\u9a7c\u9f99\u201d\u4e45\u6218\u5c55\u767d\u4e0d\u4e0b\uff0c\u94c1\u7ffc\u731b\u5c55\uff0c\u500f\u7136\u4e5f\u52a0\u5165\u4e86\u6218\u56e2\uff01 \u201c\u91d1\u5e9c\u53cc\u94c1\u536b\u201d\u8fd9\u4e00\u5408\u8d77\u624b\u6765\uff0c\u679c\u7136\u5a01\u731b\u975e\u5e38\uff0c\u4e00\u4e2a\u7a7a\u4e2d\uff0c\u4e00\u4e2a\u5730\u4e0b\uff0c\u94c1\u7ffc\u75be\u6325\uff0c\u94c1\u638c \u731b\u7ffb\uff0c\u52b2\u98ce\u6c14\u6d41\uff0c\u72b9\u5982\u6012\u6d77\u72c2\u98d9\uff01 \u5c55\u767d\u66fe\u5728\u201c\u5341\u4e8c\u5ca9\u6d1e\u201d\uff0c\u89c1\u8bc6\u8fc7\u201c\u91d1\u5e9c\u53cc\u94c1\u536b\u201d\u5408\u6218\u7a77\u5bb6\u5e2e\u7684\u201c\u9752\u7af9\u5927\u9635\u201d\uff0c\u4f46\u81ea\u5df1\u672b \u4eb2\u8eab\u9886\u6559\u8fc7\uff0c\u6ca1\u60f3\u5230\u4e8c\u4eba\u5408\u8d77\u624b\u6765\uff0c\u7ade\u6709\u5982\u8bb8\u5927\u7684\u5a01\u529b\uff01 \u201c\u94c1\u7ffc\u98de\u9e4f\u201d\u94c1\u7ffc\u731b\u6325\uff0c\u500f\u98de\u5019\u843d\uff0c\u501f\u90a3\u4fef\u51b2\u4e4b\u52bf\uff0c\u6bcf\u4e00\u4e0b\u51fb\u4e4b\u529b\uff0c\u6015\u4e0d\u6709\u5343\u94a7\u4e4b\u91cd\uff01 \u800c\u201c\u94c1\u80cc\u9a7c\u9f99\u201d\u6307\u6293\u638c\u6253\uff0c\u6bcf\u4e00\u62db\u51fa\u624b\uff0c\u4e5f\u8db3\u53ef\u88c2\u77f3\u5f00\u5c71\uff0c\u52a0\u4e0a\u4ed6\u4e0d\u65f6\u65bd\u51fa\u4ed6\u201c\u9760\u5c71\u80cc\u201d\u7684 \u72ec\u95e8\u529f\u592b\uff0c\u62f1\u8d77\u5982\u4e18\u7684\u94c1\u80cc\u9a7c\u5cf0\uff0c\u6a2a\u6324\u731b\u9760\uff0c\u5f80\u5f80\u903c\u5f97\u5c55\u767d\u65e0\u6cd5\u8eb2\u95ea\uff0c\u53ea\u6709\u786c\u78b0\u786c\u6253\uff0c\u517c\u4e4b \u201c\u94c1\u80cc\u9a7c\u9f99\u201d\u6709\u540d\u7684\u80cc\uff0c\u4e0d\u8981\u8bf4\u662f\u62f3\u638c\u6253\u4e0a\uff0c\u4ed6\u6602\u7136\u4e0d\u60e7\uff0c\u5c31\u662f\u5200\u65a7\u780d\u4e0a\uff0c\u4e5f\u4e0d\u80fd\u635f\u4f24\u4ed6\u5206 \u6beb\uff01 \u5c55\u767d\u7acb\u523b\u9677\u5165\u82e6\u51b5\uff0c\u4ee5\u4ed6\u73b0\u5728\u7684\u529f\u529b\u6765\u8bf4\uff0c\u867d\u7136\u201c\u91d1\u5e9c\u53cc\u94c1\u536b\u201d\u5408\u8d77\u624b\u6765\uff0c\u5a01\u731b\u65e0\u4fe6\uff0c \u4ed6\u8fd8\u80fd\u52c9\u529b\u652f\u6301\u4e0d\u8d25\uff0c\u53ef\u662f\u4ed6\u8eab\u4e0a\u7a7f\u7684\u5e76\u4e0d\u662f\u5408\u8eab\u8863\u670d\uff0c\u800c\u662f\u7528\u5e03\u6761\u6346\u5728\u8eab\u4e0a\u7684\u7834\u68c9\u88ab\uff0c\u4ed6 \u81ea\u5df1\u8fd0\u529b\u65bd\u529b\uff0c\u52a0\u4e0a\u201c\u91d1\u5e9c\u53cc\u94c1\u536b\u201d\u638c\u98ce\u7684\u6fc0\u8361\uff0c\u4ed6\u8eab\u4e0a\u7684\u5e03\u6761\u65e9\u5df2\u677e\u5f1b\uff0c\u7834\u68c9\u88ab\u4e00\u76f4\u5f80\u811a \u4e0b\u6ed1\u843d\u4f7f\u4ed6\u7f1a\u624b\u7f1a\u811a\uff0c\u5206\u5916\u4e0d\u4fbf\uff0c\u52a0\u4e0a\u88ab\u4e2d\u68c9\u7d6e\u88ab\u638c\u98ce\u6b27\u5f97\u6ee1\u7a7a\u6a2a\u98de\uff0c\u66f4\u4f7f\u4ed6\u72fc\u72c8\u4e0d\u582a\uff01 \u53ef\u662f\u82e6\u4e8e\u65e0\u6cd5\u8dd1\uff0c\u4e0d\u4f46\u7834\u68c9\u88ab\u5df2\u7f1a\u4f4f\u4ed6\u7684\u53cc\u817f\uff0c\u5c31\u662f\u6ca1\u6709\u7eca\u811a\u7684\u4e1c\u897f\uff0c\u4e5f\u96be\u9003\u51fa\u201c\u94c1\u7ffc \u98de\u9e4f\u201d\u7684\u94c1\u7ffc\u4e34\u7a7a\uff0c\u5c55\u81ea\u4e00\u8fb9\u5fc3\u4e2d\u6697\u6697\u7126\u6025\uff0c\u4e00\u8fb9\u53ea\u6709\u54ac\u7259\u82e6\u6491\uff01 \u53c8\u6218\u4e86\u6570\u5408\uff0c\u5c55\u767d\u8eab\u4e0a\u7684\u7834\u5e03\u6761\u5df2\u5b8c\u5168\u677e\u5f00\uff0c\u534a\u6761\u7834\u68c9\u88ab\u5df2\u6ed1\u8131\u5728\u5c0f\u8179\u4ee5\u4e0b\uff0c\u4e0a\u534a\u8eab\u79ef \u9664\u8f6c\u52a8\u66f4\u52a0\u4e0d\u4fbf\uff0c\u582a\u582a\u5c31\u8981\u843d\u8d25\u2026\u2026 \u5ffd\u542c\u4e00\u58f0\u5a07\u53f1\uff0c\u4e00\u6761\u4eba\u5f71\u7535\u95ea\u800c\u81f3\uff0c\u624b\u4e2d\u4e00\u9053\u9752\u8499\u8499\u7684\u5149\u534e\u51cc\u7a7a\u51e0\u95ea\uff0c\u7a7a\u4e2d\u7684\u201c\u94c1\u7ffc\u98de \u9e4f\u201d\u4e00\u58f0\u5389\u5578\uff0c\u6709\u5982\u65ad\u7ebf\u98ce\u7b2c\u822c\uff0c\u76f4\u8dcc\u51fa\u4e94\u516d\u4e08\u5916\uff01 \u201c\u94c1\u7ffc\u98de\u9e4f\u201d\u7ade\u4f3c\u5931\u4e86\u51ed\u6301\uff0c\u4ece\u7a7a\u4e2d\u60e8\u964d\u7740\u683d\u843d\u5730\u4e0b\uff0c\u7adf\u7136\u8dcc\u6eda\u51fa\u597d\u8fdc\uff0c\u65b9\u7ffb\u8eab\u722c\u4e86\u8d77 \u6765\uff0c\u518d\u4e00\u770b\uff0c\u4ed6\u8d56\u4ee5\u6210\u540d\u7684\u201c\u94c1\u7ffc\u795e\u8863\u201d\u7adf\u7136\u6298\u4e86\u4e00\u7ffc\uff01 \u800c\u4e14\u5de6\u81c2\u4e5f\u8d1f\u4e86\u4f24\uff0c\u9c9c\u8840\u987a\u6d41\u800c\u4e0b\uff01 \u201c\u94c1\u7ffc\u98de\u9e4f\u201d\u9762\u8272\u60e8\u767d\uff0c\u6ee1\u9762\u60ca\u5bb9\uff0c\u663e\u7136\u4ed6\u662f\u4e3a\u4e86\u81ea\u5df1\u201c\u94c1\u7ffc\u795e\u8863\u201d\u6bc1\u5728\u6765\u4eba\u5251\u4e0b\uff0c\u800c \u5fc3\u6000\u60e7\u610f\uff01 \u518d\u4e00\u770b\uff0c\u6218\u5708\u4e2d\u5df2\u591a\u4e86\u4e00\u4e2a\u624b\u6267\u957f\u5251\u3001\u8eab\u6750\u77ee\u5c0f\u7684\u9752\u9762\u7ea2\u53d1\u4e4b\u4eba\uff01 \u9664\u4e86\u5c55\u767d\u77e5\u9053\u6765\u4eba\u662f\u8c01\u4e4b\u5916\uff0c\u91d1\u5e9c\u4f17\u9ad8\u624b\u4e00\u9f50\u5403\u4e86\u4e00\u60ca\uff01 \u201c\u94c1\u80cc\u9a7c\u9f99\u201d\u4e00\u89c1\u8001\u642d\u6321\u65ad\u7ffc\uff0c\u60ca\u4e0a\u52a0\u5ffd\uff0c\u66b4\u543c\u4e00\u58f0\uff0c\u6325\u638c\u5411\u9752\u9762\u7ea2\u53d1\u4e4b\u4eba\u653b\u53bb\uff01 \u201c\u94c1\u80cc\u9a7c\u9f99\u201d\u638c\u529b\u672c\u5c31\u96c4\u539a\uff0c\u5982\u4eca\u79ef\u5ffd\u51fa\u624b\uff0c\u66f4\u89c1\u521a\u731b\uff0c\u53ea\u89c1\u638c\u5f71\u5982\u5c71\uff0c\u631f\u7740\u70c8\u70c8\u72c2 \u98d9\uff0c\u5377\u5411\u9752\u9762\u7ea2\u53d1\u602a\u4eba\uff01 \u9752\u9762\u7ea2\u53d1\u602a\u4eba\u5374\u6f2b\u4e0d\u7ecf\u5fc3\u5730\u6325\u51fa\u4e00\u638c\uff0c\u201c\u562d\u201d\u4e00\u58f0\u628a\u201c\u94c1\u80cc\u9a7c\u9f99\u201d\u9707\u9000\u4e86\u4e94\u6b65\uff01 \u201c\u94c1\u80cc\u9a7c\u9f99\u201d\u602a\u773c\u5706\u7741\uff0c\u989c\u4e0b\u7eb5\u987b\u6839\u6839\u76f4\u70b8\uff0c\u4ed6\u60f3\u4e0d\u5230\u6765\u4eba\u83ab\u6709\u5982\u6b64\u9ad8\u5f3a\u6b66\u529f\uff01\u4f46\u4ed6\u59dc \u6842\u4e4b\u6027\uff0c\u9047\u632b\u66f4\u6012\uff0c\u695e\u4e86\u4e00\u4f1a\uff0c\u7a81\u7136\u5927\u543c\u4e00\u58f0\uff0c\u8eab\u5f62\u5982\u8f66\u8f6e\u822c\u4e00\u65cb\uff0c\u4ee5\u4ed6\u6709\u540d\u7684\u201c\u94c1\u80cc\u201d\uff0c \u65bd\u51fa\u4e86\u4e00\u62db\u201d\u9760\u5c71\u529f\u201d\uff0c\u8038\u8d77\u5982\u5df3\u7684\u9a7c\u5cf0\uff0c\u731b\u5411\u9752\u9762\u7ea2\u53d1\u602a\u4eba\u9760\u53bb\uff01\u9752\u9762\u7ea2\u53d1\u602a\u4eba\u5fae\u5fae\u4e00 \u7b11\uff0c\u4f4e\u58f0\u53f1\u9053\uff1a\u201c\u4f60\u662f\u627e\u6b7b\uff01\u201d\u201c\u6b7b\u201d\u5b57\u51fa\u53e3\uff0c\u53ea\u89c1\u5979\u4e00\u633a\u624b\u4e2d\u78a7\u5251\uff0c\u201c\u6bd7\u201d\u7684\u4e00\u58f0\uff0c\u76f4\u523a \u8fdb\u201c\u94c1\u80cc\u9a7c\u9f99\u201d\u7684\u94c1\u80cc\u4e4b\u5185\uff01\u201c\u94c1\u80cc\u9a7c\u9f99\u201d\u60e8\u55e5\u4e00\u58f0\uff0c\u72b9\u5982\u91ce\u517d\u54c0\u9e23\uff0c\u524d\u51b2\u4e08\u5916\uff0c\u65b9\u624d\u7ad9 \u7a33\uff0c\u4f46\u4e00\u9053\u8840\u6cc9\uff0c\u5df2\u5982\u6c34\u7bad\u822c\u4ece\u4ed6\u7684\u9a7c\u80cc\u4e0a\u5c04\u8d77I \u201c\u94c1\u80cc\u9a7c\u9f99\u201d\u7ec3\u6709\u201c\u83bd\u725b\u7f61\u6c14\u201d\u62a4\u4f53\uff0c\u5468\u8eab\u5200\u67aa\u4e0d\u5165\uff0c\u5c24\u5176\u4ed6\u7684\u201c\u94c1\u80cc\u201d\uff0c\u66f4\u662f\u575a\u786c\u5982 \u94a2\uff0c\u529f\u8fd0\u81f3\u9876\u5cf0\uff0c\u53ef\u4ee5\u65e0\u575a\u4e0d\u514b\uff0c\u8bbe\u60f3\u5230\u7adf\u88ab\u9752\u9762\u7ea2\u53d1\u4e4b\u4eba\u4e00\u5251\u523a\u7834\uff01 \u201c\u94c1\u80cc\u9a7c\u9f99\u201d\u58f0\u58f0\u60e8\u964d\uff0c\u5468\u8eab\u808c\u8089\u98a4\u6296\uff0c\u89c1\u987b\u7ea0\u7ed3\uff0c\u8138\u4e0a\u75db\u695a\u7684\u795e\u60c5\uff0c\u66f4\u662f\u72f0\u72de\u53ef\u6015\uff0c \u60f3\u662f\u4ed6\u6a2a\u7ec3\u88ab\u7834\uff0c\u5468\u8eab\u6c14\u9006\u8840\u6cfb\uff0c\u6bd4\u666e\u901a\u672a\u7ec3\u529f\u4e4b\u4eba\u53d7\u4f24\u66f4\u52a0\u75db\u82e6\u5343\u500d\uff0c\u5373\u8fde\u5c55\u767d\u4e5f\u89c9\u4e0d \u5fcd\uff01 \u91d1\u5e9c\u4e4b\u4eba\u66f4\u662f\u4e2a\u4e2a\u5413\u5f97\u80c6\u843d\u9b42\u98de\uff0c\u8fd9\u662f\u4ed6\u4eec\u8fde\u60f3\u4e5f\u6ca1\u6709\u60f3\u5230\u7684\uff0c\u5357\u4eac\u91d1\u5e9c\u6307\u9760\u4e3a\u957f\u57ce\u9760 \u5c71\u7684\u4e24\u5927\u9ad8\u624b\uff0c\u7ade\u540c\u65f6\u53d7\u4f24\u60e8\u8d25\uff01 \u5c24\u5176\u201c\u94c1\u80cc\u9a7c\u9f99\u201d\u6781\u4e3a\u7231\u62a4\u665a\u8f88\uff0c\u751a\u5f97\u4eba\u671b\uff0c\u770b\u5230\u4ed6\u53d7\u4f24\u540e\u60e8\u6016\u7684\u60c5\u5f62\uff0c\u4e0d\u5c11\u4eba\u5413\u5f97\u60ca \u547c\u51fa\u58f0\uff01 \u4f46\u89c1\u90a3\u9752\u9762\u7ea2\u53d1\u4e4b\u4eba\u8eab\u5f62\u4e00\u65cb\uff0c\u771f\u6bd4\u8d61\u98ce\u8fd8\u75be\uff0c\u624b\u4e2d\u78a7\u5251\u63a5\u8fde\u51e0\u95ea\uff0c\u8840\u5149\u5d29\u73b0\uff0c\u6709\u51e0\u4e2a \u63ba\u547c\u5931\u58f0\u7684\u7261\u6c49\uff0c\u7acb\u523b\u8eab\u9996\u5f02\u5904\uff0c\u5012\u5730\u6b7b\u53bb\uff01 \u201c\u6709\u4ec0\u4e48\u5927\u60ca\u5c0f\u602a\u7684\uff01\u201d\u9752\u9762\u7ea2\u53d1\u602a\u4eba\u8fde\u65a9\u6570\u4eba\u4e4b\u540e\uff0c\u4ecd\u7136\u98d8\u8eab\u7acb\u4e8e\u539f\u5730\u9053\uff1a\u54ea\u4e2a\u518d\u6562 \u9b3c\u53eb\u9b3c\u53eb\u7684\uff0c\u8fd9\u5c31\u662f\u699c\u6837\uff01\u201d \u91d1\u5e9c\u90a3\u4e48\u591a\u9ad8\u624b\uff0c\u679c\u7136\u88ab\u5979\u9707\u4f4f\uff0c\u4e00\u5341\u4e2a\u5664\u82e5\u5bd2\u8749\uff0c\u6ca1\u6709\u4e00\u4e2a\u518d\u6562\u51fa\u58f0\uff0c\u90fd\u77aa\u5927\u4e86\u60ca\u6016 \u7684\u773c\u775b\u671b\u7740\u5979\uff0c\u8138\u8272\u5982\u6b7b\uff01 \u5c55\u767d\u2014\u76b1\u7709\uff0c\u5fc3\u9887\u4e0d\u5fcd\uff0c\u77e5\u9053\u90a3\u9752\u9762\u7ea2\u53d1\u7684\u9b3c\u8138\u4e4b\u540e\uff0c\u4e43\u662f\u4e00\u4f4d\u7f8e\u903e\u5929\u4ed9\u7684\u5c11\u5973\uff0c\u4e07\u4e5f \u6ca1\u60f3\u5230\u5979\u624b\u6bb5\u5982\u6b64\u6bd2\u8fa3\uff01\u53c8\u89c1\u5979\u7528\u7684\u662f\u81ea\u5df1\u7684\u201c\u65e0\u60c5\u78a7\u5251\u201d\uff0c\u4fbf\u8de8\u524d\u4e00\u6b65\u53eb\u9053\uff1a\u201c\u628a\u6211\u7684 \u2018\u65e0\u60c5\u78a7\u5251\u2019\u8fd8\u7ed9\u6211\uff01\u201d \u9752\u9762\u7ea2\u53d1\u602a\u4eba\u56de\u5934\u5bf9\u5c55\u767d\u9053\uff1a\u201c\u600e\u4e48\uff1f\u4f60\u5fc3\u8f6f\u4e86\uff01\u5fd8\u8bb0\u521a\u624d\u4ed6\u4eec\u662f\u600e\u6837\u6b3a\u4fae\u4f60\u2026\u2026\u201d \u8bf4\u81f3\u6b64\u5904\uff0c\u89d1\u7136\u4f4f\u53e3\u4e0d\u8bf4\u4e86\u3002 \u5979\u73b0\u5728\u662f\u6234\u7740\u9b3c\u9762\u5177\uff0c\u5982\u82e5\u4e0d\u7136\uff0c\u5c55\u767d\u53ef\u4ee5\u770b\u5230\u5979\u5df2\u7f9e\u5f97\u6ee1\u9762\u7ea2\u971e\uff01 \u539f\u6765\u5c55\u767d\u8eab\u4e0a\u88f9\u7684\u7834\u68c9\u88ab\uff0c\u5df2\u5760\u843d\u5230\u5927\u817f\u6839\u4ee5\u4e0b\uff0c\u4e0a\u534a\u8eab\u90fd\u5df2\u8d64\u88f8\uff0c\u4ec0\u4e48\u90fd\u9732\u51fa\u6765\u4e86\uff01 \u4f46\u5c55\u767d\u81ea\u5df2\u8fd8\u4e0d\u89c9\u5f97\uff0c\u95fb\u8a00\u9053\uff1a\u201c\u4e0d\u7ba1\u600e\u6837\uff0c\u6211\u4e5f\u4e0d\u613f\u89c1\u4f60\u7528\u6211\u7684\u5251\u72c2\u6740\u65e0\u8f9c\uff01\u5feb\u628a\u5251 \u8fd8\u6211\uff01\u201d \u9752\u9762\u7ea2\u53d1\u602a\u4eba\u201c\u55e4\u201d\u7684\u4e00\u7b11\uff0c\u80cc\u201c\u770b\u4f60\u7684\u602a\u6837\u5b50\uff01\u8fd8\u4e0d\u5feb\u628a\u8863\u670d\u7a7f\u4e0a\u2026\u2026\u201d \u5c55\u767d\u731b\u4e00\u4f4e\u5934\uff0c\u89c1\u81ea\u5df1\u8d64\u8eab\u88f8\u4f53\uff0c\u8138\u4e0a\u7f9e\u5f97\u50cf\u5927\u7ea2\u5e03\u4e00\u68a2\u5934\u8d76\u7d27\u628a\u892a\u81f3\u817f\u4e0b\u7684\u7834\u68c9\u88ab\u5f80 \u4e0a\u63d0\u4e86\u63d0\uff0c\u91cd\u65b0\u7528\u5e03\u6761\u624e\u7d27\u2026\uff0e\uff0e\uff0e \u5728\u5c55\u767d\u505a\u8fd9\u4e9b\u4e8b\u65f6\uff0c\u91d1\u5e9c\u4e4b\u4eba\u60ca\u9b42\u521d\u5b9a\uff0c\u5df2\u6709\u6570\u4e2a\u80c6\u5c0f\u7684\uff0c\u60f3\u5077\u5077\u5f00\u6e9c\u3002 \u6ca1\u60f3\u5230\u9003\u4e0d\u8fc7\u9752\u9762\u7ea2\u53d1\u602a\u4eba\u7684\u773c\u775b\uff0c\u53ea\u8eab\u5f62\u4e00\u6643\uff0c\u5019\u53bb\u5373\u56de\uff0c\u201c\u65e0\u60c5\u78a7\u5251\u201d\u4e00\u6643\uff0c\u7acb\u523b \u53c8\u6709\u6570\u4eba\u8111\u888b\u642c\u5bb6\uff0c\u9c9c\u8840\u56db\u6e85\uff01 \u5c55\u767d\u66f4\u89c9\u4e0d\u5fcd\uff0c\u5927\u53eb\u9053\uff1a\u201c\u5feb\u628a\u5251\u8fd8\u6765\uff01\u4f60\u8981\u518d\u6ee5\u6740\uff0c\u522b\u8bf4\u6211\u5bf9\u4f60\u4e0d\u5ba2\u6c14\u4e86\uff01\u201d \u9752\u9762\u7ea2\u53d1\u602a\u4eba\u7f13\u7f13\u628a\u5251\u9012\u7ed9\u5c55\u767d\uff0c\u9053\uff1a\u201c\u5251\u540d\u2018\u65e0\u60c5\u2019\uff0c\u96be\u9053\u8fd8\u6015\u6cbe\u8840\u5417\uff1f\u591a\u6740\u51e0\u4e2a\u8d70 \u72d7\uff0c\u53c8\u6709\u4ec0\u4e48\u503c\u5f97\u5927\u60ca\u5c0f\u602a\u7684\uff01\u201d \u5c55\u767d\u6c14\u547c\u547c\u5730\u628a\u5251\u63a5\u8fc7\uff0c\u9053\uff1a\u201c\u4e8f\u4f60\u8bf4\u5f97\u51fa\u53e3\uff0c\u96be\u9053\u4ed6\u4eec\u4fbf\u4e0d\u662f\u4eba\u5417\uff1f\u201d \u9752\u9762\u7ea2\u53d1\u602a\u4eba\u54df\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u5e72\u5417\u8fd9\u4e48\u51f6\uff1f\u6211\u4e0d\u662f\u4e3a\u6551\u4f60\uff0c\u624d\u6740\u4ed6\u4eec\u5417\uff01\u201d \u5979\u8fd9\u4e00\u201c\u54df\u201d\uff0c\u56de\u590d\u4e86\u5973\u58f0\uff0c\u914d\u4e0a\u5979\u8fd9\u526f\u9b3c\u9762\uff0c\u663e\u5f97\u6709\u70b9\u4e0d\u4f26\u4e0d\u7c7b\uff0c\u53d7\u4e86\u4f24\u7684\u201c\u91d1\u5e9c\u53cc \u94c1\u536b\u201d\uff0c\u4ee5\u53ca\u5413\u5446\u4e86\u7684\u91d1\u5e9c\u4f17\u9ad8\u624b\uff0c\u8138\u4e0a\u9732\u51fa\u7591\u60d1\u7684\u795e\u8272\uff0c\u4e00\u9f50\u6014\u5f81\u671b\u7740\u9752\u9762\u7ea2\u53d1\u602a\u4eba\u53d1 \u695e\uff01\u5fc3\u8bf4\uff1a\u201c\u8fd9\u6076\u9b3c\u4f3c\u7684\u602a\u4eba\uff0c\u8bf4\u8bdd\u600e\u4e48\u4f3c\u5973\u5b50\u58f0\u97f3\u2026\u2026\u201d \u5c55\u767d\u4e0d\u7406\u5979\uff0c\u63d0\u7740\u5251\u76f4\u5411\u6cb3\u8fb9\u8239\u4e0a\u8d70\u53bb\uff0c\u4f46\u5230\u4e86\u6cb3\u8fb9\u4e00\u770b\uff0c\u5cb8\u8fb9\u53ea\u9760\u7740\u6570\u8258\u7a7a\u8239\uff0c\u201c\u9752 \u8dcc\u795e\u201d\u91d1\u4e5d\u65e9\u5df3\u9e3f\u98de\u51a5\u51a5\uff0c\u8d70\u53bb\u591a\u65f6\u4e86\uff01 \u800c\u4e14\uff0c\u8fde\u90a3\u4e9b\u58ee\u6c49\u642c\u6765\u7684\u7bb1\u5b50\u4e5f\u90fd\u4e0d\u89c1\u4e86\uff0c\u60f3\u662f\u201c\u9752\u86a8\u795e\u201d\u91d1\u4e5d\u501f\u7740\u201c\u53cc\u94c1\u536b\u201d\u7f20\u4f4f\u5c55 \u767d\u65f6\uff0c\u65e9\u5df2\u7387\u4f17\u5750\u8239\u8d70\u4e86\uff0c\u53ea\u5269\u4e0b\u8fd9\u4e9b\u642c\u8fd0\u7bb1\u5b50\u7684\u58ee\u6c49\u5728\u6cb3\u8fb9\u4e0a\u505a\u66ff\u6b7b\u9b3c\uff01 \u5c55\u767d\u5728\u6cb3\u8fb9\u4e0a\u671b\u7740\u6cb3\u6c34\u53d1\u5446\uff0c\u9752\u9762\u7ea2\u53d1\u602a\u4eba\u5374\u6084\u6084\u6765\u5230\u5c55\u767d\u8eab\u540e\u9053\uff1a\u201c\u4f60\u627e\u4ec0\u4e48\uff1f\u201d \u5c55\u767d\u9053\uff1a\u201c\u6211\u7684\u4ec7\u4eba\u9003\u8dd1\u4e86\uff0c\u6211\u8981\u8fc7\u6cb3\uff01\u201d \u9752\u9762\u7ea2\u53d1\u602a\u4eba\u9053\uff1a\u90a3\u4e48\uff0c\u5c31\u4e0a\u8239\u5427\uff01\u201d \u7b2c\u4e09\u5341\u4e03\u7ae0\u3000\u5c0f\u821f\u60c5\u7f18>> \u53e4\u9f99\u300a\u5251\u5ba2\u884c\u300b \u7b2c\u4e09\u5341\u4e03\u7ae0\u3000\u5c0f\u821f\u60c5\u7f18 \u5c55\u767d\u6014\u4e86\u6014\u9053\uff1a\u201c\u53ef\u662f\uff0c\u6211\u4e0d\u4f1a\u5212\u8239\u554a\uff01\u201d \u9752\u9762\u7ea2\u53d1\u602a\u4eba\u5a07\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u4f1a\u6211\u4f1a\uff0c\u5305\u9001\u4f60\u5230\u5bf9\u5cb8\u53bb\u5c31\u662f\u4e86\uff01\u201d \u5c55\u767d\u5fc3\u6025\u8feb\u8e2a\u4ec7\u4eba\uff0c\u4e0d\u903c\u7ec6\u60f3\uff0c\u542c\u8bf4\u5979\u6709\u529e\u6cd5\u9001\u81ea\u5df1\u8fc7\u6cb3\uff0c\u7acb\u523b\u7eb5\u5230\u8239\u4e0a\u53bb\u3002 \u5c55\u767d\u4ece\u672a\u5750\u8fc7\u8239\uff0c\u8fd9\u8239\u8eab\u53c8\u7a84\u53c8\u5c0f\uff0c\u5c55\u81ea\u4ece\u5cb8\u4e0a\u63a0\u4e0b\u8239\u6765\uff0c\u811a\u5c16\u4e00\u70b9\u8239\u677f\uff0c\u8239\u8eab\u5411\u4e00\u4fa7 \u4e00\u6643\uff0c\u4ed6\u8d76\u7d27\u7528\u53e6\u4e00\u53ea\u811a\u53bb\u7a33\u4f4f\u8239\u8eab\uff0c\u8c01\u77e5\u7528\u529b\u8fc7\u731b\uff0c\u5c0f\u8239\u53cd\u5411\u53e6\u4e00\u4fa7\u503e\u659c\uff0c\u5c55\u767d\u5931\u53bb\u91cd \u5fc3\uff0c\u4e00\u4e2a\u8eab\u5b50\u76f4\u5411\u6cb3\u6c34\u4e2d\u5012\u53bb\uff01\u540c\u7684\u5f20\u5634\u60ca\u547c\uff1a\u201c\u54ce\u5440\uff01\u2026\u2026\u201d \u7a81\u7136\u8eab\u540e\u4f38\u6765\u4e24\u53ea\u624b\uff0c\u6b63\u597d\u628a\u4ed6\u6276\u4f4f\uff0c\u63a5\u7740\u5c0f\u8239\u50cf\u7bad\u4e00\u6837\uff0c\u76f4\u5411\u6cb3\u5fc3\u5c04\u53bb\uff01 \u8eab\u540e\u6276\u4ed6\u7684\u4eba\uff0c\u6b63\u662f\u6234\u4e86\u5047\u9762\u5177\u7684\u201c\u9b3c\u9762\u5a07\u5a03\u201d\uff0c\u5979\u4ece\u5c0f\u5728\u6d77\u5c9b\u4e0a\u957f\u5927\uff0c\u73a9\u8239\u5982\u9a91\u9a6c\u4e00 \u822c\u7075\u6d3b\uff0c\u4e0a\u8239\u6276\u4efb\u5c06\u843d\u6c34\u7684\u5c55\u767d\uff0c\u811a\u5c16\u4e00\u7528\u529b\uff0c\u5c0f\u8239\u5373\u5982\u79bb\u5f26\u4e4b\u6bcf\u4e00\u822c\u9a70\u5411\u6cb3\u5fc3\uff0c\u4f46\u5979\u4e5f\u6ca1 \u60f3\u5230\u5c55\u767d\u5728\u8239\u4e0a\u8fd9\u822c\u4e0d\u6d4e\u4e8b\uff0c\u5c55\u767d\u5411\u540e\u4e00\u5012\uff0c\u6b63\u597d\u5012\u8fdb\u5979\u7684\u6000\u91cc\uff0c\u5979\u4e5f\u662f\u4e1d\u6beb\u4e0d\u5907\uff0c\u8eab\u5f62\u53d7 \u4e86\u5c55\u767d\u91cd\u91cf\u4e00\u538b\uff0c\u52a0\u4e0a\u8239\u4e0a\u4e0d\u80fd\u501f\u52b2\uff0c\u5979\u7684\u4e00\u526f\u5a07\u8eaf\u7ade\u4e5f\u5012\u4e86\u4e0b\u53bb\uff01 \u4e8c\u4eba\u4e00\u9f50\u5012\u8fdb\u8239\u8231\uff0c\u8239\u5c0f\u6070\u597d\u5bb9\u5382\u4ed6\u4e8c\u4eba\u7684\u4f53\u79ef\uff0c\u4f46\u518d\u8981\u60f3\u8f6c\u4fa7\u53ef\u5c31\u56f0\u96be\u4e86\uff01 \u4e8c\u4eba\u90fd\u662f\u4ef0\u9762\u671d\u5929\uff0c\u5c55\u767d\u5728\u4e0a\uff0c\u201c\u9b3c\u9762\u5a07\u5a03\u201d\u5728\u4e0b\uff0c\u5e78\u597d\u5c0f\u8239\u672a\u7ffb\uff0c\u4e8c\u4eba\u90fd\u60f3\u6323\u624e\u7740\u7ad9 \u8d77\uff0c\u53ef\u662f\u8239\u8231\u592a\u5c0f\uff0c\u4e00\u65f6\u4e4b\u95f4\u7adf\u722c\u4e0d\u8d77\u6765\uff0c\u5c55\u767d\u7ffb\u8eab\u5411\u4e0a\u722c\uff0c\u201c\u9b3c\u9762\u5a07\u5a03\u201d\u5fd9\u9664\u5374\u8111\u4e0a\u7684\u9b3c \u9762\u5177\uff0c\u4e5f\u5411\u4e0a\u4ef0\u8eab\uff0c\u6070\u597d\u4e8c\u4eba\u6765\u4e86\u4e2a\u9762\u5bf9\u9762\uff01 \u5c55\u767d\u5728\u6de1\u6708\u6e05\u5149\u4e0b\u7a81\u7136\u77a5\u89c1\u90a3\u5982\u82b1\u4f3c\u7389\u7684\u5bb9\u8c8c\uff0c\u5df2\u4e0d\u518d\u662f\u9752\u9762\u7ea2\u53d1\u7684\u9b3c\u8138\uff0c\u5fc3\u60c5\u7acb\u523b\u8d77 \u4e86\u7edd\u5927\u7684\u53d8\u5316\uff0c\u53ea\u611f\u5979\u5a07\u8eaf\u6e29\u67d4\u9999\u6559\uff0c\u6a31\u53e3\u5439\u6c14\u5982\u5170\uff0c\u4e0d\u89c9\u624b\u811a\u4e00\u8f6f\uff0c\u624d\u4ef0\u8d77\u4e00\u534a\u7684\u8eab\u5f62\uff0c \u53c8\u8dcc\u5728\u201c\u9b3c\u9762\u5a07\u5a03\u201d\u7684\u8eab\u4e0a\uff0c\u534a\u664c\u4e0d\u80fd\u52a8\u5f39\u2026\u2026 \u201c\u9b3c\u9762\u5a07\u5a03\u201d\u2014\u2014\u5176\u5b9e\u5979\u5e76\u4e0d\u662f\u201c\u9b3c\u9762\u5a07\u5a03\u201d\uff0c\u201c\u9b3c\u9762\u5a07\u5a03\u201d\u5b9e\u53e6\u6709\u5176\u4eba\uff0c\u4e0d\u8fc7\u5979\u662f\u88ab \u4eba\u8bef\u4f1a\u7f62\u4e86\u3002\u81f3\u4e8e\u5979\u7684\u771f\u5b9e\u59d3\u540d\uff0c\u4e66\u540e\u53e6\u6709\u4ea4\u4ee3\uff0c\u6b64\u5904\u4e0d\u8d58-\u2014\u867d\u7136\u7565\u8131\u5f62\u9ab8\uff0c\u4f46\u90a3\u662f\u73af\u5883 \u4f7f\u7136\uff0c\u5979\u7684\u672c\u6027\u8fd8\u662f\u5584\u826f\u7684\uff0c\u800c\u4e14\uff0c\u5979\u53c8\u662f\u4e00\u4e2a\u60c5\u5b89\u521d\u5f00\u7684\u9ec4\u82b1\u5c11\u5973\uff0c\u5982\u4eca\u88ab\u4e00\u4e2a\u9752\u5e74\u7537\u5b50 \u538b\u5728\u8eab\u4e0a\uff0c\u5168\u8eab\u4e5f\u662f\u53c8\u8f6f\u53c8\u9ebb\uff0c\u8bf4\u4e0d\u51fa\u4ec0\u4e48\u6ecb\u5473\uff0c\u8fd9\u79cd\u60c5\u666f\u5979\u4ece\u672a\u7ecf\u9a8c\u8fc7\uff0c\u53ea\u611f\u5fc3\u5934\u5c0f\u9e7f\u7a81 \u7a81\u4e71\u649e\uff0c\u6b32\u8d77\u65e0\u529b\uff0c\u53ea\u5a07\u5598\u4e86\u4e24\u58f0\uff0c\u4fbf\u95ed\u76ee\u4e0d\u52a8\u4e86\uff01 \u4e8c\u4eba\u6682\u65f6\u9676\u9189\u5728\u5f02\u6837\u7684\u611f\u89e6\u4e2d\uff0c\u4e45\u4e45\u4e0d\u52a8\uff0c\u53ea\u4efb\u51ed\u90a3\u4e00\u65f6\u5b64\u821f\uff0c\u5728\u6cb3\u9762\u4e0a\u81ea\u884c\u98d8\u6d41\u2026\u2026 \u8fd9\u65f6\u5cb8\u4e0a\u7684\u91d1\u5e9c\u4f17\u9ad8\u624b\u53ca\u201c\u91d1\u5e9c\u53cc\u94c1\u536b\u201d\uff0c\u5374\u53c8\u60ca\u53c8\u6050\u5730\u5446\u671b\u7740\u4e8c\u4eba\u4e58\u821f\u79bb\u53bb\uff0c\u65e2\u672b\u6562 \u963b\u62e6\uff0c\u4ea6\u672a\u6562\u8ffd\u8d76\uff0c\u76f4\u5f85\u4e8c\u4eba\u7684\u5c0f\u821f\u6eb6\u6ca1\u4e8e\u6708\u5149\u4e0b\u7684\u6cb3\u5fc3\uff0c\u6e10\u53bb\u6e10\u8fdc\uff0c\u8fd9\u624d\u6536\u56de\u60ca\u6050\u4e4b\u5fc3\uff0c \u6276\u4f24\u62ac\u6b7b\uff0c\u609a\u7136\u82e5\u4e27\u5730\u8f6c\u56de\u91d1\u5e9c\uff0c\u8fd9\u4e14\u4e0d\u63d0\u2026\u2026 \u5c55\u767d\u4e0e\u90a3\u795e\u79d8\u9b3c\u9762\u7684\u7f8e\u4e3d\u5c11\u5973\uff0c\u4e92\u76f8\u5012\u5728\u8239\u8231\u4e2d\uff0c\u53cc\u53cc\u95ed\u7740\u773c\u775b\uff0c\u4eab\u53d7\u90a3\u8c1c\u4e00\u6837\u7684\u6e29\u67d4 \u6ecb\u5473\uff0c\u597d\u50cf\u5fd8\u8bb0\u4e86\u4e16\u5916\u7684\u4e00\u5207\uff0c\u53ea\u4efb\u90a3\u5c0f\u8239\u5728\u6cb3\u5fc3\u4e2d\u65e0\u76ee\u7684\u5730\u98d8\u6d41\u2026\u2026 \u6708\u591c\u9759\u6781\u4e86\uff0c\u6cb3\u4e0a\u4e5f\u9759\u6781\u4e86\uff0c\u4eff\u4f5b\u4e16\u754c\u4e0a\u7684\u4e00\u5207\u90fd\u5728\u9759\u8c27\u4e4b\u4e2d\uff0c\u53ea\u6709\u4ed6\u4e8c\u4eba\uff0c\u80fd\u591f\u4e92\u76f8 \u542c\u5230\u5bf9\u65b9\u5fc3\u8df3\u7684\u58f0\u97f3\uff0c\u7a0d\u4e3a\u6025\u4fc3\u7684\u547c\u5438\u58f0\u97f3\uff0c\u751a\u81f3\u5bf9\u65b9\u4f53\u5185\u8840\u6db2\u5954\u6d41\u7684\u58f0\u97f3\uff0c\u4f46\u5df2\u5206\u4e0d\u6e05\u90a3 \u8c01\u662f\u8c01\u7684\u4e86\uff0c\u597d\u50cf\u4e8c\u4eba\u5df2\u7ecf\u5408\u800c\u4e3a\u4e00\uff0c\u5bf9\u65b9\u5df2\u53d8\u6210\u81ea\u5df1\u8eab\u4f53\u7684\u4e00\u90e8\u4efd \u5c55\u767d\u795e\u601d\u604d\u60da\uff0c\u4e00\u5ffd\u513f\u89c9\u5f97\u81ea\u5df1\u50cf\u662f\u521d\u964d\u7684\u5a74\u513f\uff0c\u8eba\u5728\u82b1\u6735\u4e00\u822c\u7684\u9526\u8925\u4e2d\uff0c\u8d64\u88f8\u88f8\u7684\uff0c \u5374\u6e05\u65b0\u65e0\u6bd4\uff0c\u5723\u6d01\u65e0\u6bd4\uff0c\u5fc3\u91cc\u4e0d\u8d77\u4e00\u4e1d\u6742\u5ff5\uff1b\u4e00\u5ffd\u513f\u53c8\u89c9\u5f97\u81ea\u5df1\u50cf\u662f\u7761\u5728\u6bcd\u4eb2\u7684\u6000\u4e2d\uff0c\u6bcd\u4eb2 \u5fae\u8361\u7740\u6447\u7bee\uff0c\u8f7b\u5531\u7740\u7720\u6b4c\u50ac\u81ea\u5df1\u5165\u7761\uff0c\u53ea\u611f\u5230\u8212\u9002\u65e0\u6bd4\uff0c\u5b89\u7a33\u65e0\u6bd4\uff1b\u4e00\u5ffd\u513f\u53c8\u89c9\u5f97\u81ea\u5df2\u662f\u4e00 \u4e2a\u4f1f\u4e08\u592b\uff0c\u6b63\u5728\u62e5\u62b1\u81ea\u5df1\u5e74\u8f7b\u7684\u60c5\u4eba\uff0c\u4fdd\u62a4\u7740\u5979\uff0c\u6e29\u6696\u7740\u5979\uff0c\u8ba9\u90a3\u5c0f\u60c5\u4eba\u7761\u5728\u81ea\u5df2\u81c2\u5f2f\u91cc\uff0c \u8fde\u68a6\u4e2d\u4e5f\u4e0d\u4f7f\u5979\u53d7\u4e00\u4e9b\u513f\u60ca\u6050\uff1b\u4e00\u5ffd\u513f\u81ea\u5df1\u53d8\u6210\u2014\u4e2a\u6148\u7236\uff0c\u6b63\u5728\u4ee5\u65e0\u6bd4\u6837\u548c\u7684\u7231\u5fc3\uff0c\u5b88\u62a4\u7740 \u81ea\u5df1\u7684\u5973\u5a74\u7761\u7720\u2026\u2026 \u4f46\u4ed6\u7684\u601d\u7ef4\u4e2d\u867d\u79bb\u4e0d\u5f00\u7761\u7720\uff0c\u5934\u8111\u4e2d\u5374\u6e05\u9192\u5f97\u5f88\uff0c\u53ea\u662f\u6ca1\u6709\u7741\u5f00\u773c\u775b\u7f62\u4e86\u2026\u2026 \u4e0d\u77e5\u90a3\u8c1c\u4e00\u6837\u7684\u7f8e\u4e3d\u5c11\u5973\uff0c\u6b64\u65f6\u5fc3\u4e2d\u505a\u4f55\u611f\u60f3\uff1f\u4ed6\u6ca1\u6709\u7741\u5f00\u773c\u775b\u770b\uff0c\u4e5f\u4e0d\u60f3\u7741\u5f00\u773c\u775b \u770b\uff0c\u4eff\u4f5b\u662f\u5728\u505a\u4e00\u4e2a\u6781\u7f8e\u6ee1\u3001\u6781\u7f8e\u5999\u7684\u597d\u68a6\uff0c\u7741\u5f00\u773c\u775b\uff0c\u7f8e\u68a6\u4fbf\u6d88\u5931\u4e86\u2026\u2026 \u53ef\u65f6\uff0c\u90a3\u8eab\u4e0b\u7684\u7f8e\u4e3d\u5c11\u5973\uff0c\u5374\u5598\u606f\u4e86\u4e00\u58f0\uff0c\u5fae\u5fae\u52a8\u4e86\u4e00\u52a8\u5979\u90a3\u8f6f\u7ef5\u7f16\u7684\u80f6\u4f53\uff0c\u4e5f\u4e0d\u77e5\u662f \u5c55\u767d\u628a\u5979\u538b\u75db\u4e86\uff0c\u8fd8\u662f\u5979\u7684\u624b\u81c2\u88ab\u538b\u9ebb\u4e86\uff1f\u53cd\u6b63\u5c55\u767d\u88ab\u60ca\u89c9\u4e86\uff0c\u624d\u60f3\u722c\u8d77\u8eab\u6765\uff0c\u8c01\u77e5\u5979\u5374\u4ece \u8eab\u4e0b\u62bd\u51fa\u4e24\u53ea\u624b\u6765\uff0c\u7f13\u7f13\u629a\u6478\u5728\u5c55\u767d\u7684\u8eab\u4e0a\uff01 \u5c55\u81ea\u5982\u88ab\u7535\u6fc0\uff0c\u5979\u7684\u4e24\u53ea\u5c0f\u624b\u7ade\u5982\u5145\u6ee1\u4e86\u7535\u6d41\uff0c\u629a\u6478\u4e4b\u5904\uff0c\u7acb\u523b\u6709\u4e00\u80a1\u6696\u6d41\uff0c\u901a\u8fc7\u4e86\u5c55 \u767d\u7684\u5468\u8eab\uff0c\u4f7f\u5c55\u767d\u5468\u8eab\u53d1\u70ed\uff0c\u8840\u8109\u8d32\u80c0\u8d77\u6765\uff01 \u5c55\u767d\u8d6b\u7136\u7741\u5f00\u4e86\u773c\u775b\uff0c\u548c\u81ea\u5df2\u5507\u988a\u76f8\u63a5\uff0c\u518d\u770b\u90a3\u6000\u4e2d\u7684\u7f8e\u4eba\uff0c\u661f\u7738\u534a\u5f20\uff0c\u6a31\u53e3\u5fae\u542f\uff0c\u8138 \u6cdb\u6843\u82b1\uff0c\u7709\u542b\u6625\u9edb\uff0c\u4f3c\u4e4e\u671f\u5f85\u7740\u4ec0\u4e48\uff0c\u9165\u80f8\u8d77\u4f0f\uff0c\u53cc\u624b\u7d27\u7d27\u62b1\u5b9a\u5c55\u767d\uff0c\u800c\u4e14\u4e0d\u4f4f\u5730\u629a\u6478\u2026\u2026 \u5c55\u767d\u672c\u5c31\u672a\u7a7f\u8863\u670d\uff0c\u6240\u4ee5\u5979\u624b\u89e6\u5904\u5df2\u662f\u808c\u80a4\u76f8\u63a5\uff01\u8fd9\u5df2\u4e0d\u80fd\u8bf4\u662f\u8bf1\u60d1\uff0c\u4e5f\u4e0d\u80fd\u8bf4\u662f\u7f6a \u6076\uff0c\u5b8c\u5168\u662f\u53d1\u81ea\u672c\u80fd\u3001\u51fa\u4e8e\u81ea\u7136\u7684\u4e00\u79cd\u4e3e\u52a8\uff0c\u5c55\u767d\u7acb\u523b\u51b2\u52a8\u8d77\u6765\uff0c\u75af\u72c2\u5730\u8fd8\u4ee5\u62e5\u62b1\uff0c\u75af\u72c2\u5730 \u63a5\u543b\uff0c\u75af\u72c2\u5730\u2026\u2026 \u90a3\u5c11\u5973\u4f3c\u662f\u627f\u53d7\u4e0d\u4f4f\u5c55\u767d\u7684\u75af\u72c2\uff0c\u4e0d\u4f4f\u5730\u5a07\u5598\uff0c\u4e0d\u4f4f\u5730\u5a07\u547c\uff0c\u8eaf\u4f53\u50cf\u5782\u6b7b\u7684\u8235\u4e00\u6837\u626d \u7740\u2026\u2026 \u7a81\u7136\u4e00\u9635\u51c9\u4e91\uff0c\u63a9\u4f4f\u4e86\u5929\u4e0a\u7684\u660e\u6708\uff0c\u6c34\u4e0a\u7684\u6e05\u5149\u4e5f\u6d88\u5931\u4e86\uff0c\u6c34\u4e0a\u7684\u5c0f\u821f\uff0c\u53d8\u6210\u4e86\u4e00\u7247\u6a21 \u7cca\u7684\u9ed1\u5f71\uff0c\u8239\u4e0a\u7684\u5149\u666f\uff0c\u5df2\u65e0\u6cd5\u671b\u89c1\uff0c\u53ea\u80fd\u542c\u5230\u6c34\u6d6a\u649e\u51fb\u8239\u5e95\u7684\u5fae\u5fae\u58f0\u54cd\u2026\u2026 \u826f\u4e45\uff0c\u660e\u6708\u897f\u6c89\uff0c\u5929\u8fb9\u73b0\u51fa\u4e86\u7b2c\u4e00\u9053\u66d9\u5149\uff01\u826f\u591c\u4e0d\u80fd\u7559\uff0c\u591c\uff0c\u5df2\u7ecf\u8fc7\u53bb\uff0c\u767d\u65e5\u53c8\u964d\u4e34\u4e86 \u4eba\u95f4\uff0c\u4e00\u5207\u90fd\u6e05\u9192\u4e86\uff0c\u6e05\u9192\uff0c\u53c8\u5524\u56de\u4eba\u4eec\u5bf9\u4e16\u4fd7\u7684\u8bb0\u5fc6\uff01 \u8fd9\u4e16\u4fd7\u4e0d\u7ba1\u662f\u51f6\u6076\u8fd8\u662f\u826f\u5584\uff0c\u4f46\u4ece\u68a6\u4e2d\u82cf\u9192\u7684\u4eba\u4eec\uff0c\u5374\u5f97\u9762\u5bf9\u5b83\uff01 \u68a6\uff0c\u867d\u662f\u8352\u5510\u7684\uff0c\u4f46\u5374\u5145\u6ee1\u4e86\u5fd8\u5374\u4e16\u4fd7\u7684\u5feb\u4e50\uff01 \u90a3\u6cb3\u9762\u4e0a\u7684\u5c0f\u821f\uff0c\u4ecd\u7136\u65e0\u76ee\u7684\u5730\u98d8\u6d41\u5728\u5cb8\u8fb9\uff0c\u201c\u562d\u201d\u7684\u4e00\u58f0\uff0c\u8239\u5934\u649e\u5728\u5cb8\u8fb9\u7684\u6c99\u6ee9\uff01 \u8fd9\u4e00\u9707\u52a8\uff0c\u60ca\u9192\u4e86\u8239\u4e0a\u7684\u68a6\u4e2d\u4eba\uff0c\u4e8c\u4eba\u60f6\u7136\u722c\u8d77\uff0c\u7761\u773c\u60fa\u5fea\uff0c\u9996\u5148\u4e8c\u4eba\u5bf9\u671b\u4e86\u4e00\u773c\uff0c\u53c8 \u60f3\u6628\u591c\u7684\u65d6\u65ce\u98ce\u5149\uff0c\u4e0d\u7531\u53cc\u53cc\u7f9e\u7ea2\u4e86\u8138\uff01 \u671d\u9633\u4e5f\u5728\u6cb3\u5bf9\u5cb8\u7684\u5c71\u540e\uff0c\u63a2\u51fa\u5a07\u7ea2\u7684\u8138\u513f\uff0c\u4f3c\u4e4e\u8ddf\u4ed6\u4e8c\u4eba\u4e00\u6837\u7684\u5bb3\u7f9e\u3002\u90a3\u7f8e\u4e3d\u7684\u5c11\u5973\uff0c \u4f4e\u5934\u770b\u5230\u5c55\u767d\u8d64\u88f8\u7684\u60c5\u5f62\uff0c\u5a07\u7f9e\u4e0d\u80dc\u5730\u7b11\u9053\uff1a\u201c\u770b\u4f60\u2014\u2014\u201d \u8bdd\u672a\u8bf4\u5b8c\uff0c\u4eba\u5df2\u817e\u8eab\u63a0\u4e0a\u6cb3\u5cb8\uff0c\u4e0d\u77e5\u600e\u7684\u5979\u8eab\u8d77\u534a\u7a7a\uff0c\u7a81\u7136\u4e00\u76b1\u7709\uff0c\u5a07\u547c\u4e86\u4e00\u58f0\uff0c\u4e0d\u5230 \u4e00\u4e08\u7684\u8ddd\u79bb\uff0c\u5979\u7ade\u9669\u4e9b\u683d\u843d\u6c34\u4e2d\uff01 \u5c55\u81ea\u9002\u65f6\u817e\u8eab\u800c\u8d77\uff0c\u534a\u7a7a\u4e2d\u7528\u624b\u628a\u5979\u6276\u4f4f\uff0c\u53cc\u53cc\u843d\u4e0b\u5730\u6765\u3002 \u5c55\u767d\u7adf\u4e00\u53cd\u7d20\u5e38\u6728\u8bb7\u4e4b\u6001\uff0c\u591a\u60c5\u800c\u5173\u5fc3\u5730\u67d4\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u2014\u2014\u600e\u4e48\u4e86\uff1f\u8fd9\u4e48\u70b9\u8fdc\u90fd\u8df3\u4e0d \u4e0a\u6765\u4e86\uff01\u201d \u5979\u659c\u7747\u4e86\u5c55\u767d\u4e00\u773c\uff0c\u5676\u9053\uff1a\u201c\u8fd8\u4e0d\u90fd\u662f\u4f60\uff0c\u6628\u591c\u2014\u2014\u201d \u5c55\u767d\u867d\u4e0d\u660e\u767d\uff0c\u4f46\u4e5f\u4f1a\u610f\u4e86\u4e00\u4e8c\uff0c\u5fc3\u4e2d\u611f\u5230\u4e00\u751c\u3002\u4f46\u5374\u65e0\u9650\u6127\u759a\u5730\u9053\uff1a\u201c\u4e0d\u8981\u7d27 \u5427\uff1f\u2026\u2026\u201d \u90a3\u7f8e\u4e3d\u5c11\u5973\u5e7d\u5e7d\u5730\u9053\uff1a\u201c\u867d\u4e0d\u59a8\u4e8b\uff0c\u53ef\u662f\u6211\u7684\u4fee\u70bc\u7b97\u662f\u5b8c\u4e86\uff01\u518d\u4e0d\u80fd\u8fbe\u5230\u91d1\u521a\u4e0d\u574f\u4e4b\u4f53 \u7684\u5730\u6b65\u2026\u2026\u201d \u5c55\u767d\u9053\uff1a\u201c\u8fd8\u4e0d\u662f\u6211\u5bb3\u4e86\u4f60\uff01\u5509\uff01\u60f3\u8d77\u6765\uff0c\u6628\u591c\u771f\u4e0d\u8be5\u2026\u2026\u201d \u5979\u53cd\u800c\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e5f\u4e0d\u80fd\u5b8c\u5168\u602a\u4f60\uff0c\u6211\u81ea\u5df1\u4e5f\u6709\u8d23\u4efb\uff0c\u82e5\u4e0d\u2026\u2026\u201d \u8bf4\u81f3\u6b64\u5904\uff0c\u5979\u5ffd\u7136\u4f4f\u5634\u4e0d\u8bf4\u4e86\u3002\u5c55\u767d\u9053\uff1a\u201c\u82e5\u4e0d\u600e\u6837\u5462\uff1f\u201d \u7f8e\u4e3d\u5c11\u5973\u53f9\u9053\uff1a\u201c\u6211\u5728\u6765\u4e2d\u539f\u65f6\uff0c\u7236\u4eb2\u672c\u4e0d\u53eb\u6211\u6765\uff0c\u8bf4\u6211\u7684\u9053\u5fc3\u672a\u575a\uff0c\u5bb9\u6613\u5815\u5165\u60c5\u52ab\uff0c \u4f46\u6211\u81ea\u5df1\u8fd8\u4e0d\u76f8\u4fe1\uff0c\u56e0\u4e3a\u5929\u4e0b\u7684\u7537\u5b50\u6211\u4e00\u4e2a\u90fd\u770b\u4e0d\u4e0a\u773c\uff0c\u6240\u4ee5\u4e00\u5b9a\u8981\u6765\uff0c\u6ca1\u60f3\u5230\u7236\u4eb2\u8fd8\u662f\u8bf4 \u5bf9\u4e86\uff0c\u9047\u89c1\u4f60\u2026\u2026\u201d \u5c55\u767d\u9053\uff1a\u201c\u9047\u89c1\u6211\uff0c\u4f60\u5c31\u628a\u6301\u4e0d\u4f4f\u4e86\u2026\u2026\u201d \u6ca1\u60f3\u5230\u6728\u8bb7\u5982\u5c55\u767d\u7684\u5fe0\u539a\u9752\u5e74\uff0c\u4e00\u65e6\u7231\u60c5\u5f00\u7a8d\uff0c\u7adf\u7136\u4e5f\u4f1a\u8bf4\u8d77\u4fcf\u76ae\u8bdd\u6765\u3002\u90a3\u7f8e\u4e3d\u5c11\u5973\u8138 \u8272\u4e00\u7ea2\uff0c\u626c\u8d77\u7c89\u62f3\u9053\uff1a\u201c\u4f60\u6562\u7b11\u6211\uff0c\u6211\u5c31\u6376\u4f60\uff01\u201d \u5c55\u767d\u5fd9\u9053\uff1a\u201c\u6211\u600e\u6562\u7b11\u4f60\u5450\u2014\u2014\u5443\uff0c\u521a\u624d\u4f60\u8bf4\u7236\u4eb2\uff0c\u4f60\u7236\u4eb2\u662f\u8c01\uff1f\u4f60\u4e0d\u662f\u4ec0\u4e48\u2018\u7ea2\u7c89\u9ab7 \u9ac5\uff0c\u9b3c\u9762\u5a07\u5a03\u2019\u5417\uff1f\u2018\u7ea2\u7c89\u9ab7\u9ac5\uff0c\u9b3c\u9762\u5a07\u5a03\u2019\uff0c\u54ea\u91cc\u53c8\u6765\u7684\u7236\u4eb2\uff1f\u201d \u5c55\u767d\u8fde\u63d0\u51fa\u4e86\u4e00\u5927\u4e32\u7591\u95ee\uff0c\u90a3\u5c11\u5973\u9053\uff1a\u201c\u4f60\u542c\u8c01\u8bf4\u6211\u662f\u2018\u7ea2\u7c89\u9ab7\u9ac5\uff0c\u9b3c\u9762\u5a07\u5a03\u2019\uff1f\u201d \u5c55\u767d\u9053\uff1a\u201c\u5c31\u662f\u90a3\u624b\u62ff\u6247\u5b50\u7684\u767d\u8863\u4e66\u751f\u5bf9\u6211\u8bb2\u7684\uff0c\u8001\u5b9e\u8bf4\uff0c\u6211\u672c\u4e0d\u76f8\u4fe1\u4ed6\u7684\u8bdd\uff0c\u60f3\u90a3 \u201c\u7ea2\u7c89\u9ab7\u9ac5\uff0c\u9b3c\u9762\u5a07\u5a03\u2019\uff0c\u4e43\u662f\u6570\u5341\u5e74\u524d\u95fb\u540d\u6c5f\u6e56\u7684\u5927\u9b54\u5934\uff0c\u600e\u4f1a\u6709\u4f60\u8fd9\u822c\u5e74\u8f7b\uff01\u201d \u90a3\u5c11\u5973\u5374\u8be1\u8c32\u4e00\u7b11\u9053\uff1a\u201c\u4ed6\u8bf4\u5f97\u4e0d\u9519\uff0c\u6211\u5c31\u662f\u2018\u7ea2\u7c89\u9ab7\u9ac5\uff0c\u9b3c\u9762\u5a07\u5a03\u2019\uff01\u201d \u5c55\u767d\u95fb\u8a00\u4e00\u695e\uff0c\u6014\u671b\u7740\u7f8e\u4e3d\u5c11\u5973\u9053\uff1a\u201c\u6b64\u8bdd\u5f53\u771f\uff1f\u201d \u7f8e\u4e3d\u5c11\u5973\u54af\u54af\u4e00\u7b11\u9053\uff1a\u201c\u600e\u4e48\uff1f\u4f60\u5bb3\u6015\u4e86\u5417\uff1f\u201d \u5c55\u767d\u695e\u4e86\u4e00\u4f1a\uff0c\u624d\u9053\uff1a\u201c\u5982\u679c\u662f\u6628\u591c\u4ee5\u524d\uff0c\u6211\u6216\u8bb8\u4f1a\u5bb3\u6015\uff0c\u4f46\u7ecf\u8fc7\u4e86\u6628\u591c\u76f8\u805a\uff0c\u6211\u5374\u4e0d \u5bb3\u6015\u4e86\uff01\u800c\u4e14\uff0c\u6211\u77e5\u9053\u4f60\u662f\u8ddf\u6211\u8bf4\u7740\u73a9\u7684\uff0c\u4f60\u7edd\u4e0d\u4f1a\u662f\u2018\u9b3c\u9762\u5a07\u5a03\u2019\uff01\u201d \u7f8e\u4e3d\u5c11\u5973\u4ecd\u7136\u7d27\u76ef\u7740\u95ee\u9053\uff1a\u201c\u6211\u82e5\u662f\u2018\u9b3c\u9762\u5a07\u5a03\u2019\u600e\u4e48\u6837\uff1f\u6700\u5426\u4f60\u4fbf\u4e0d\u7231\u6211\u4e86\uff01\u6628\u591c\u8ddf \u6211\u8bf4\u7684\u6d77\u8a93\u5c71\u76df\u662f\u5426\u4fbf\u4e0d\u7b97\u4e86\uff1f\u201d \u5c55\u767d\u6115\u7136\u9053\uff1a\u4f60\u8bf4\u7684\u8bdd\uff0c\u6211\u867d\u7136\u4e0d\u6562\u76f8\u4fe1\uff0c\u4f46\u5982\u679c\u4f60\u771f\u662f\u2018\u9b3c\u9762\u5a07\u5a03\u2019\uff0c\u6211\u8fd8\u662f\u7167\u6837\u7231 \u4f60\uff0c\u800c\u4e14\u6628\u591c\u7684\u8a93\u8a00\uff0c\u5730\u8001\u5929\u8352\uff0c\u6c34\u4e0d\u6539\u53d8\uff01\u201d \u7f8e\u4e3d\u5c11\u5973\u88ab\u5c55\u767d\u771f\u60c5\u6240\u611f\uff0c\u4e0d\u7531\u4e00\u56de\u8eab\u53c8\u6251\u8fdb\u5c55\u767d\u6000\u91cc\uff0c\u543b\u4e86\u5c55\u767d\u4e00\u4e0b\u9053\uff1a\u201c\u4f60\u771f \u597d\uff01\u2026\u2026\u201d \u7a81\u7136\u53c8\u201c\u5440\u201d\u5730\u53eb\u4e86\u4e00\u58f0\uff0c\u63a8\u5f00\u5c55\u767d\u9053\uff1a\u201c\u4f60\u770b\u4f60\uff01\u5149\u987e\u4e86\u8bf4\u8bdd\uff0c\u8fd8\u4e0d\u6562\u5feb\u628a\u8863\u670d\u7a7f \u597d\uff0c\u5982\u679c\u6709\u4e2a\u4eba\u770b\u5230\u4e86\uff0c\u6210\u4ec0\u4e48\u6837\u5b50\uff01\u201d \u5c55\u767d\u4f4e\u5934\u4e00\u770b\uff0c\u8eab\u4e0a\u7684\u7834\u68c9\u88ab\u4ecd\u672b\u6346\u597d\uff0c\u53ea\u624e\u4f4f\u4e86\u534a\u8fb9\uff0c\u8d64\u8eab\u88f8\u4f53\u4e00\u76ee\u4e86\u7136\uff0c\u5e78\u4e8f\u6668\u8d77 \u5c71\u91ce\u65e0\u4eba\uff0c\u771f\u8981\u9047\u5230\u751f\u4eba\uff0c\u81ea\u5df1\u8d64\u8eab\u9732\u4f53\uff0c\u62ab\u7740\u534a\u7247\u7834\u68c9\u88ab\uff0c\u53c8\u4f34\u7740\u4e00\u4e2a\u5982\u82b1\u4f3c\u7389\u7684\u7f8e\u5973\uff0c \u8fd8\u662f\u771f\u4e0d\u597d\u610f\u601d\u3002\u5fd9\u628a\u7834\u68c9\u88ab\u624e\u597d\uff0c\u9053\uff1a\u201c\u771f\u8981\u627e\u4ef6\u8863\u670d\u7a7f\uff0c\u50cf\u8fd9\u526f\u5f62\u8c61\uff0c\u53ef\u662f\u65e0\u6cd5\u89c1 \u4eba\uff01\u201d \u628a\u5c11\u5973\u8bf4\u5f97\u7b11\u4e86\u3002\u5c55\u767d\u53c8\u9053\uff1a\u201c\u4ece\u6628\u591c\u8bf4\u5230\u73b0\u5728\uff0c\u8bf4\u4e86\u534a\u5929\u4f60\u8fd8\u6ca1\u6709\u628a\u4f60\u7236\u4eb2\u662f\u8c01\u544a\u8bc9 \u6211\uff01\u201d \u7f8e\u4e3d\u5c11\u5973\u9053\uff1a\u201c\u6211\u7236\u5973\u867d\u7136\u4e45\u5c45\u6d77\u5916\uff0c\u4f46\u63d0\u8d77\u6211\u7684\u7236\u4eb2\uff0c\u4f60\u4e00\u5b9a\u77e5\u9053\u3002\u201d \u5c55\u767d\u6025\u9053\uff1a\u201c\u6211\u7684\u5c0f\u59d0\uff0c\u522b\u5356\u5173\u5b50\u4e86\uff0c\u5feb\u8bf4\u5427\uff01\u4f60\u7684\u7236\u4eb2\u5230\u5e95\u662f\u8c01\uff1f\u201d \u7f8e\u4e3d\u5c11\u5973\u9053\uff1a\u201c\u94f6\u6247\u5b50\u2019\u67f3\u795f\u539a\uff0c\u6211\u53eb\u7fe0\u7fe0\u3002\u201d \u5c55\u767d\u60ca\u8df3\u8d77\u6765\uff0c\u6025\u9053\uff1a\u201c\u5f53\u5e74\u2018\u6c5f\u5357\u4e03\u4fa0\u2019\u8001\u4e03\uff1f\u201d \u7f8e\u4e3d\u5c11\u5973\u5e84\u91cd\u5730\u70b9\u4e86\u70b9\u5934\uff0c\u9053\uff1a\u201c\u6b63\u662f\uff01\u201d \u5c55\u767d\u72b9\u5982\u4e71\u7bad\u6512\u5fc3\uff0c\u4ef0\u9996\u5411\u5929\uff0c\u60b2\u547c\u9053\uff1a\u201c\u5929\u5440\uff01\u4e3a\u4ec0\u4e48\u8001\u662f\u8ba9\u6211\u9047\u5230\u4ec7\u4eba\u4e4b\u5973\uff1f\u5a49 \u513f\uff01\u6155\u5bb9\u7ea2\uff01\u6a0a\u7d20\u9e3e\uff01\u91d1\u5f69\u51e4\uff01\u73b0\u5728\u53c8\u9047\u4e0a\u4f60\uff01\u67f3\u7fe0\u7fe0\uff01\u7fe0\u7fe0\uff01\u6628\u591c\u6211\u8fd8\u4e0d\u77e5\u9053\u4f60\u59d3\u67f3\uff01\u4e3a \u4ec0\u4e48\u4f60\u4e0d\u65e9\u8bf4\uff1f\u4e3a\u4ec0\u4e48\u4f60\u4e0d\u65e9\u8bf4\uff1f\u2026\u2026\u201d \u5c55\u767d\u60b2\u547c\u60e8\u53f7\uff0c\u72b9\u5982\u53d1\u75af\u3002\u8c01\u77e5\u7fe0\u7fe0\u2014\u2014\u5c31\u662f\u90a3\u6234\u9b3c\u9762\u5177\u7684\u7f8e\u4e3d\u5c11\u5973\u2014\u2014\u5374\u5e73\u9759\u5f97\u51fa \u5947\uff0c\u7b49\u5c55\u767d\u53d1\u4e86\u534a\u5929\u75af\uff0c\u5979\u624d\u6162\u6761\u65af\u7406\u5730\u9053\uff1a\u201c\u8fd9\u4e8b\u6211\u6bd4\u4f60\u77e5\u9053\u5f97\u6e05\u695a\uff0c\u6740\u5bb3\u4f60\u7236\u4eb2\u7684\u662f \u2018\u6c5f\u5357\u4e94\u4fa0\u2019\uff0c\u6211\u7236\u4eb2\u6ca1\u6709\u4efd\uff01\u800c\u4e14\uff0c\u5c31\u662f\u56e0\u4e3a\u6211\u7236\u4eb2\u6ca1\u6709\u53c2\u52a0\uff0c\u624d\u88ab\u4ed6\u4eec\u903c\u5f97\u4e0d\u80fd\u5728\u4e2d\u539f \u7acb\u8db3\uff0c\u5e26\u7740\u6211\u6bcd\u5973\u4fe9\u9003\u5230\u6d77\u5916\u5b64\u5c9b\u4e0a\u53bb\u5b58\u8eab\uff01\u201d \u5c55\u767d\u672c\u5f85\u4e0d\u4fe1\uff0c\u4f46\u770b\u5979\u795e\u60c5\u4e0d\u4f3c\u8bf4\u8c0e\uff0c\u800c\u4e14\u8bf4\u5f97\u6709\u6761\u6709\u7406\uff0c\u4fbf\u95ee\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u4f60\u77e5\u9053\u6211 \u662f\u8c01\u5417\uff1f\u201d \u7fe0\u7fe0\u9053\uff1a\u201c\u600e\u4e48\u4e0d\u77e5\u9053\uff1f\u5c55\u4e91\u5929\u5c55\u4f2f\u4f2f\u4e4b\u5b50\uff0c\u59d3\u5c55\u540d\u767d\uff0c\u6211\u73b0\u5728\u7684\u60c5\u4eba\uff0c\u5c06\u6765\u7684\u4e08\u592b\uff01 \u4f60\u8fd9\u5c0f\u50bb\u74dc\uff0c\u4f60\u4ee5\u4e3a\u6211\u90a3\u4e48\u4e0d\u503c\u94b1\u5417\uff1f\u4e0d\u77e5\u9053\u4f60\u7684\u5e95\u7ec6\uff0c\u4fbf\u80af\u628a\u6211\u7684\u6e05\u767d\u7684\u5973\u513f\u8eab\u2026\u2026\u732e\u7ed9 \u4f60\u2026\u2026\u5417\uff1f\u201d \u7fe0\u7fe0\u672c\u5728\u8352\u5c9b\u91ce\u4eba\u90e8\u843d\u91cc\u957f\u5927\uff0c\u8131\u7565\u5f62\u9ab8\u60ef\u4e86\uff0c\u5c24\u5176\u5766\u7387\u5927\u65b9\uff0c\u6beb\u4e0d\u77eb\u63c9\u9020\u4f5c\uff0c\u4f46\u8bf4\u5230 \u6700\u540e\u51e0\u53e5\u8bdd\uff0c\u4e5f\u4e0d\u7531\u5a07\u7f9e\u4e0d\u80dc\uff0c\u541e\u541e\u5410\u5410\u3002 \u5c55\u767d\u9053\uff1a\u201c\u5947\u4e86\uff01\u6211\u6628\u5929\u591c\u91cc\u65b9\u4e0e\u4f60\u521d\u89c1\uff0c\u9664\u4e86\u540d\u5b57\u5916\uff0c\u6211\u53c8\u6ca1\u5bf9\u4f60\u8bf4\u8fc7\u6211\u7684\u5bb6\u4e16\uff0c\u4f60 \u600e\u4e48\u77e5\u9053\u5f97\u8fd9\u4e48\u6e05\u695a\uff1f\u201d \u7fe0\u7fe0\u7a81\u7136\u7b11\u4e86\uff0c\u9053\uff1a\u4f60\u77e5\u9053\u9677\u5728\u91d1\u5e9c\u2018\u77f3\u77f6\u5927\u9635\u2019\u4e2d\u7684\u4eba\uff0c\u662f\u8c01\u653e\u51fa\u6765\u7684\uff1f\u201d \u5c55\u81ea\u60ca\u9053\uff1a\u201c\u83ab\u975e\u662f\u4f60\uff1f\u201d \u7fe0\u7fe0\u70b9\u4e86\u70b9\u5934\uff0c\u53c8\u9053\uff1a\u201c\u4e0d\u4f46\u653e\u4eba\u7684\u662f\u6211\uff0c\u800c\u4e14\uff0c\u6211\u4e5f\u8ddf\u4f60\u4e00\u6837\uff0c\u8fd9\u6b21\u8fdc\u6765\u4e2d\u539f\uff0c\u4e5f\u662f \u627e\u4ed6\u4eec\u56db\u5927\u8c6a\u95e8\u6765\u4e3a\u6211\u7684\u7236\u6bcd\u62a5\u4ec7\u6765\u7684\uff01\u201d \u5c55\u767d\u66f4\u89c9\u5f97\u60ca\u5947\uff0c\u9053\uff1a\u201c\u96be\u9053\u4f60\u7684\u7236\u4eb2\u8fdc\u5728\u6d77\u5916\uff0c\u4e5f\u88ab\u4ed6\u4eec\u6240\u5bb3\uff1f\u201d \u7fe0\u7fe0\u53f9\u4e86\u4e00\u53e3\u6c14\u9053\uff1a\u201c\u770b\u6765\u6211\u4eec\u4e0a\u4e00\u4ee3\u7684\u4eba\u88ab\u5bb3\u8be6\u60c5\uff0c\u4f60\u4e0d\u5927\u77e5\u9053\u3002\u8fd9\u662f\u7236\u4eb2\u544a\u8bc9\u6211 \u7684\uff0c\u5f53\u521d\u4f60\u7236\u4eb2\u52a0\u4e0a\u5f53\u4eca\u56db\u5927\u8c6a\u95e8\u4e0e\u9547\u6c5f\u6a0a\u975e\uff0c\u539f\u662f\u7ed3\u4e49\u91d1\u5170\uff0c\u53f7\u79f0\u2018\u6c5f\u5357\u4e03\u4fa0\u201d\u2026\u3002\u201d \u5c55\u767d\u9053\uff1a\u201c\u8fd9\u4e2a\u6211\u77e5\u9053\uff01\u201d \u7fe0\u7fe0\u9053\uff1a\u201c\u4f60\u77e5\u9053\uff0c\u6211\u5c31\u4e0d\u5fc5\u8bf4\u4e86\u3002\u201d \u5c55\u767d\u6025\u8d77\u6765\uff0c\u5fd9\u9053\uff1a\u201c\u6211\u53ea\u77e5\u9053\u4e00\u70b9\uff0c\u540e\u6765\u7684\u5c31\u4e0d\u8be6\u7ec6\u4e86\uff0c\u8fd8\u662f\u8bf7\u4f60\u8bf4\u4e0b\u53bb\u5427\uff01\u201d \u7fe0\u7fe0\u9053\uff1a\u201c\u90a3\u5c31\u522b\u6253\u5c94\uff01\u201d \u8bf4\u7740\u53c8\u8f6c\u5934\u56db\u987e\uff0c\u6307\u7740\u6c34\u8fb9\u4e00\u4e2a\u6811\u6869\u9053\uff1a\u201c\u90a3\u8fb9\u65e2\u80cc\u98ce\uff0c\u53c8\u53ef\u8fce\u7740\u6c34\u9762\u770b\u65e5\u51fa\uff0c\u6211\u4eec\u5230 \u90a3\u8fb9\u53bb\u5750\u4e0b\u6765\u8c08\uff01\u201d \u4e8e\u662f\u4e8c\u4eba\u8d70\u4e0b\u6cb3\u5824\uff0c\u8fce\u7740\u592a\u9633\u5728\u4e00\u68f5\u5927\u6811\u6869\u4e0a\u5e76\u80a9\u5750\u4e0b\uff0c\u5a13\u5a13\u6e05\u8c08\u8d77\u6765\u3002\u539f\u6765\u201c\u6c5f\u5357\u4e03 \u4fa0\u201d\u5728\u6d1e\u5ead\u6e56\u53d6\u5b9d\uff0c\u201c\u9739\u96f3\u5251\u201d\u5c55\u4e91\u5929\u501f\u201c\u907f\u6c34\u73e0\u201d\u4e4b\u52a9\uff0c\u4e0e\u201c\u94f6\u6247\u5b50\u201d\u67f3\u795f\u539a\uff0c\u4e00\u9f50\u8fdb\u5165 \u6e56\u5e95\uff0c\u63a2\u5f97\u4e86\u85cf\u5b9d\u79d8\u9053\uff0c\u4f46\u7531\u4e8e\u5b9d\u85cf\u592a\u591a\uff0c\u4e00\u65f6\u4e0d\u80fd\u53d6\u51fa\uff0c\u4e8c\u4eba\u53c8\u4e0a\u5f97\u5cb8\u6765\uff0c\u4e0e\u53e6\u5916\u4e94\u4fa0\u5546 \u8bae\u53d6\u5b9d\u4e4b\u7b56\u3002 \u5c55\u4e91\u5929\u8fd8\u662f\u4e3b\u5f20\u628a\u8fd9\u4efd\u5b9d\u85cf\u53d6\u51fa\u6765\uff0c\u8d29\u6d4e\u6e56\u5e7f\u4e00\u5e26\u7684\u707e\u6c11\uff0c\u67f3\u5d07\u539a\u4e5f\u5f88\u8d5e\u6210\uff0c\u4f46\u53e6\u5916\u4e94 \u4fa0\u4e0d\u540c\u610f\u3002\u575a\u6301\u8981\u5206\u5b9d\u85cf\u5f52\u4e3a\u5df1\u6709\uff0c\u53ef\u662f\u5c55\u4e91\u5929\u4e43\u4e03\u4fa0\u4e4b\u9996\uff0c\u53c8\u52a0\u4e0a\u5c55\u4e91\u5929\u6027\u683c\u867d\u662f\u5927\u516c\u65e0 \u79c1\uff0c\u5374\u6709\u70b9\u521a\u610e\u81ea\u7528\uff0c\u4e0d\u7ba1\u4e94\u4eba\u610f\u4e0b\u5982\u4f55\uff0c\u575a\u51b3\u6309\u7167\u81ea\u5df1\u7684\u610f\u601d\u53bb\u529e\uff0c\u4e94\u4fa0\u62d7\u4e0d\u8fc7\u4ed6\uff0c\u53ea\u6709 \u52c9\u5f3a\u5e94\u5141\uff0c\u4f46\u5fc3\u4e2d\u5df2\u6697\u8d77\u4e0d\u5fff\uff0c\u79cd\u4e0b\u6697\u5bb3\u5c55\u4e91\u5929\u4e4b\u5fc3\uff01 \u504f\u504f\u90a3\u5b97\u5b9d\u85cf\u4e4b\u4e2d\uff0c\u4e0d\u4f46\u73cd\u5b9d\u91d1\u94f6\u4ef7\u503c\u8fde\u57ce\uff0c\u800c\u4e14\u8fd8\u6709\u4e09\u5b97\u6b66\u6797\u79d8\u5b9d\uff0c\u4e00\u662f\u201c\u907f\u6c34\u7389 \u58c1\u201d\uff0c\u4e8c\u662f\u201c\u5927\u7f57\u91d1\u4e39\u201d\uff0c\u4e09\u662f\u4e00\u672c\u300a\u6b66\u5b66\u771f\u7ecf\u300b\u8fd9\u4e09\u5b97\u79d8\u5b9d\u4ff1\u662f\u6b66\u6797\u5343\u5e74\u96be\u9022\u7684\u73cd\u8d35\u4e4b \u7269\uff0c\u8fd9\u4e00\u6765\u66f4\u5f15\u8d77\u4e94\u4fa0\u7684\u8d2a\u5fc3\uff01 \u56e0\u6b64\uff0c\u5728\u7b2c\u4e8c\u6b21\u51c6\u5907\u505c\u5f53\u3001\u8d74\u6d1e\u5ead\u6e56\u53d6\u5b9d\u65f6\uff0c\u8d81\u5c55\u4e91\u5929\u4e0d\u5907\uff0c\u4e94\u4fa0\u731b\u65bd\u6740\u624b\uff0c\u9996\u7531\u201c\u9752 \u86a8\u795e\u201d\u4ee5\u6210\u540d\u4e4b\u6697\u5668\u5077\u88ad\u5f97\u624b\uff0c\u63a5\u7740\u4e94\u4eba\u8054\u624b\u731b\u653b\uff0c\u5352\u4f7f\u5c55\u4e91\u5929\u4e00\u4ee3\u5927\u4fa0\u996e\u6068\u5f52\u5929\uff01 \u5728\u4ed6\u4eec\u4e94\u4e2a\u6697\u7b97\u6740\u6b7b\u5c55\u4e91\u5929\u4e4b\u65f6\uff0c\u6070\u503c\u201c\u94f6\u6247\u5b50\u201d\u67f3\u5b97\u539a\u5728\u6e56\u5e95\u5b89\u7f6e\u53d6\u5b9d\u5de5\u4f5c\uff0c\u56e0\u6b64\uff0c \u5e76\u4e0d\u77e5\u5cb8\u4e0a\u7684\u53d8\u5316\uff0c\u7b49\u67f3\u5d07\u539a\u7834\u9664\u85cf\u5b9d\u79d8\u5c01\uff0c\u6784\u7b51\u6c34\u5e95\u6697\u9053\uff0c\u4e00\u5207\u505a\u597d\u4e4b\u540e\uff0c\u56de\u5230\u5cb8\u4e0a\u5c55\u4e91 \u5929\u4ed6\u4eec\u7ed3\u4e49\u7684\u5927\u54e5\uff0c\u5374\u5df2\u4f24\u75d5\u7d2f\u7d2f\u9648\u5c38\u6e56\u8fb9\u4e86\uff01 \u7fe0\u7fe0\u8bf4\u81f3\u6b64\u5904\uff0c\u5c55\u767d\u5df2\u7ecf\u6ce3\u4e0d\u6210\u58f0\uff0c\u6ee1\u8138\u75db\u6cea\uff0c\u60b2\u58f0\u9053\uff1a\u201c\u6211\u7684\u7236\u4eb2\u5f53\u65f6\u5e76\u6ca1\u6709\u6b7b\uff0c\u53ea \u662f\u8d1f\u4e86\u6781\u91cd\u7684\u4f24\uff0c\u4ed6\u8fd8\u56de\u5230\u5bb6\u4e2d\uff0c\u4e0e\u6bcd\u4eb2\u548c\u6211\u89c1\u4e86\u6700\u540e\u4e00\u9762\u624d\u6b7b\u53bb\u7684\uff01\u201d \u7fe0\u7fe0\u9053\uff1a\u201c\u8fd9\u4e00\u70b9\uff0c\u6050\u6015\u8fde\u6211\u7236\u4eb2\u4e5f\u4e0d\u77e5\u9053\uff0c\u4ed6\u53ea\u8bf4\u540e\u6765\u5374\u4e0d\u89c1\u4e86\u4f60\u7236\u4eb2\u7684\u6b7b\u5c38\uff0c\u6050\u6015 \u662f\u88ab\u6c5f\u6e56\u670b\u53cb\u4ee3\u4e3a\u57cb\u846c\u4e86\uff0c\u5374\u6ca1\u60f3\u5230\u4f2f\u7236\u4ecd\u80fd\u56de\u5230\u5bb6\u4e2d\u4e0e\u4f60\u6bcd\u548c\u4f60\u89c1\u4e0a\u6700\u540e\u4e00\u9762\u3002\u201d \u5c55\u767d\u9053\uff1a\u201c\u8fd8\u6709\u4e00\u4ef6\uff0c\u6211\u81f3\u4eca\u4e0d\u660e\u767d\uff0c\u7236\u4eb2\u4e34\u6b7b\u4e4b\u524d\uff0c\u9664\u4e86\u4ea4\u7ed9\u6211\u8fd9\u4e00\u67c4\u201c\u65e0\u60c5\u78a7 \u5251\u2019\uff0c\u5631\u6211\u4e3a\u7236\u62a5\u4ec7\u4e4b\u5916\uff0c\u53e6\u5916\u8fd8\u4ea4\u7ed9\u6211\u8fd9\u516d\u6837\u4e1c\u897f\u2026\u2026\u201d \u8bf4\u7740\u4fbf\u5411\u6000\u4e2d\u53bb\u638f\uff0c\u8c01\u77e5\u4e00\u638f\u638f\u4e86\u4e2a\u7a7a\u3002\u7fe0\u7fe0\u53cd\u800c\u7b11\u7740\u4ece\u6000\u4e2d\u638f\u4e86\u51fa\u6765\uff0c\u9053\uff1a\u201c\u4e0d\u662f\u8fd9 \u516d\u6837\u4e1c\u897f\u5417\uff1f\u201d \u5c55\u767d\u8fd9\u624d\u77e5\u9053\uff0c\u81ea\u5df2\u8eab\u4e0a\u7684\u4e1c\u897f\u90fd\u5230\u5979\u8eab\u4e0a\u53bb\u5566\uff0c\u4e0d\u8fc7\u73b0\u5728\u4e8c\u4eba\u5df2\u7ecf\u4e0d\u5fc5\u5206\u5f7c\u6b64\u4e86\uff0c\u6545 \u6b64\u5c55\u767d\u4e5f\u4e0d\u518d\u7740\u6025\uff0c\u4fbf\u70b9\u5934\u9053\uff1a\u201c\u6b63\u662f\u4e86\uff0c\u5176\u4e2d\u9664\u4e86\u90a3\u679a\u9752\u94dc\u5236\u94b1\uff0c\u5df2\u7136\u77e5\u9053\u662f\u91d1\u4e5d\u8001\u8d3c\u7684 \u2018\u9752\u86a8\u91d1\u94b1\u9556\u2019\u4e4b\u5916\uff0c\u90a3\u53e6\u5916\u4e94\u6837\u4fbf\u4e0d\u77e5\u6765\u5386\u4e86\u3002\u201d \u7fe0\u7fe0\u9053\uff1a\u201c\u4f60\u4e0d\u77e5\u9053\uff0c\u6211\u5374\u77e5\u9053\uff01\u201d\u63a5\u7740\u5979\u53c8\u6ed4\u6ed4\u5730\u8bf4\u4e0b\u53bb\uff1a\u201c\u8fd9\u4e00\u65b9\u4e1d\u7ef8\uff0c\u662f\u4ece\u2018\u6458 \u661f\u624b\u2019\u6155\u5bb9\u6db5\u8863\u895f\u4e0a\u6495\u4e0b\u6765\u7684\uff01\u201d \u5c55\u767d\u70b9\u5934\u9053\uff1a\u201c\u6211\u65e9\u5c31\u770b\u7740\u50cf\uff01\u201d \u7fe0\u7fe0\u63a5\u7740\u9053\uff1a\u201c\u8fd9\u4e00\u7c92\u94a2\u73e0\uff0c\u662f\u2018\u9738\u738b\u97ad\u2019\u6a0a\u975e\u7684\u7edd\u95e8\u6697\u5668\uff0c\u540d\u4e3a\u2018\u5f39\u6307\u94f6\u4e38\u2019\uff01\u8fd9\u4e00 \u679a\u9752\u94dc\u94ae\u6263\u662f\u2018\u6df7\u5143\u6307\u2019\u53f8\u7a7a\u664b\u7684\uff0c\u8fd9\u4e00\u6bb5\u4e1d\u7ee6\u662f\u2018\u4e7e\u5764\u638c\u2019\u4e91\u5b97\u9f99\u7684\uff0c\u800c\u4e14\u8fd8\u62ff\u6765\u6346\u7ed1\u8fc7 \u6211\uff0c\u81f3\u4e8e\u8fd9\u4e00\u56e2\u4e71\u53d1\uff0c\u5374\u662f\u6211\u5934\u4e0a\u7684\u2026\u2026\u201d \u7fe0\u7fe0\u8d8a\u8bf4\u4e0b\u53bb\uff0c\u5c55\u767d\u8d8a\u5403\u60ca\uff0c\u5ffd\u542c\u4e00\u58f0\u6025\u98ce\u7834\u7a7a\u5411\u4e8c\u4eba\u5934\u9876\u88ad\u6765\uff0c\u5c55\u767d\u5927\u5403\u4e00\u60ca\uff01\u2026\u2026 \u7b2c\u4e09\u5341\u516b\u7ae0\u3000\u767d\u53d1\u5a46\u6210\u201c\u8840\u638c\u706b\u9f99\u201d>> \u53e4\u9f99\u300a\u5251\u5ba2\u884c\u300b \u7b2c\u4e09\u5341\u516b\u7ae0\u3000\u767d\u53d1\u5a46\u6210\u201c\u8840\u638c\u706b\u9f99\u201d \u5c55\u767d\u624d\u8981\u56de\u8eab\u8eb2\u95ea\uff0c\u67f3\u7fe0\u7fe0\u5374\u8fde\u5934\u4e5f\u672a\u56de\uff0c\u53ea\u53cd\u81c2\u4e00\u63a0\uff0c\u7528\u5c16\u5c16\u4e24\u6307\uff0c\u634f\u4f4f\u4e86\u4e00\u6761\u7ee3\u82b1 \u624b\u5e15\uff0c\u4e3e\u5230\u9762\u524d\u6765\uff01 \u5c55\u767d\u6697\u5403\u4e00\u60ca\uff0c\u662f\u8c01\u6709\u8fd9\u4e48\u9ad8\u7684\u529f\u529b\uff1f\u628a\u4e00\u6761\u67d4\u8f6f\u7684\u4e1d\u5dfe\u7ade\u80fd\u5f53\u6697\u5668\u6253\u51fa\uff0c\u800c\u4e14\u6025\u5578\u7834 \u98ce\uff0c\u90a3\u6765\u4eba\u7684\u5185\u529f\u4e4b\u9ad8\uff0c\u771f\u662f\u4e0d\u53ef\u60f3\u50cf\u4e86\uff01 \u60ca\u8be7\u4e4b\u4e2d\uff0c\u731b\u4e00\u56de\u5934\uff0c\u5374\u89c1\u5c55\u5a49\u513f\u7389\u9762\u715e\u767d\uff0c\u674f\u773c\u542b\u60b2\uff0c\u8863\u8882\u968f\u98ce\u5730\u7ad9\u5728\u6cb3\u63d0\u9ad8\u57e0\u4e4b \u5904\uff0c\u6014\u671b\u7740\u81ea\u5df1\uff01 \u5c55\u767d\u5927\u51fa\u610f\u5916\uff0c\u201c\u5440\u201d\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u539f\u6765\u662f\u4f60\uff01\u5a49\u513f\uff0c\u4f60\u600e\u4e48\u5230\u8fd9\u91cc\u6765\u4e86\uff1f\u201d \u5a49\u513f\u5634\u5507\u6487\u4e86\u6487\uff0c\u4e0d\u77e5\u662f\u54ed\u8fd8\u662f\u7b11\uff1f\u6ee1\u8179\u5e7d\u6028\u5730\u9053\uff1a\u201c\u6cb3\u5c71\u65e0\u4e3b\uff0c\u8fd9\u53c8\u4e0d\u662f\u4f60\u7684\u5bb6\uff0c\u600e \u4e48\uff1f\u4f60\u4eec\u6765\u6211\u4e0d\u80fd\u6765\u5417\uff1f\u662f\u4e0d\u662f\u59a8\u5bb3\u4e86\u4f60\u4eec\u7684\u2026\u2026\u201d \u5979\u672c\u60f3\u8bf4\u201c\u59a8\u5bb3\u4e86\u4f60\u4eec\u8c08\u60c5\u8bf4\u7231\u201d\uff01\u4f46\u7a76\u7adf\u5979\u8fd8\u662f\u4e00\u4e2a\u95fa\u9601\u5c11\u5973\uff0c\u867d\u7136\u918b\u706b\u51b2\u5929\uff0c\u90a3\u6837 \u9732\u9aa8\u7684\u8bdd\uff0c\u8fd8\u662f\u6ca1\u6709\u597d\u610f\u601d\u8bf4\u51fa\u53e3\u6765\uff01 \u5c55\u767d\u8138\u4e0a\u4e00\u7ea2\uff0c\u5450\u5450\u9053\uff1a\u201c\u6211\u662f\u95ee\u4f60\uff0c\u4f60\u4e0d\u662f\u5728\u91d1\u5e9c\u82b1\u56ed\u53d7\u4f24\u4e86\u5417\uff0c\u600e\u4f1a\u8dd1\u4e86\u51fa \u6765\uff1f\u2026\u2026\u201d \u5c55\u767d\u672c\u4e0d\u5584\u8a00\u8f9e\uff0c\u8fd9\u4e00\u95ee\u66f4\u663e\u4e0d\u5f53\u3002\u6b63\u89e6\u5230\u4e86\u5a49\u513f\u7684\u59d4\u5c48\u5904\uff0c\u773c\u5708\u4e00\u7ea2\uff0c\u9053\uff1a\u201c\u6211\u4f24\u4e0d \u4f24\u8ddf\u4f60\u6709\u4ec0\u4e48\u5173\u7cfb\uff1f\u6211\u5c31\u662f\u6b7b\u4e86\u4e5f\u7528\u4e0d\u7740\u4f60\u7ba1I\u2026\u2026\u201d \u8bf4\u5230\u8fd9\u91cc\uff0c\u7a81\u7136\u9762\u9732\u8be7\u5f02\u4e4b\u8272\uff0c\u671b\u5b9a\u5c55\u767d\u8eab\u540e\uff0c\u672a\u5b8c\u7684\u8bdd\u4e5f\u5fd8\u8bb0\u8bf4\u4e86\u3002 \u5c55\u767d\u6389\u5934\u671b\u53bb\uff0c\u5374\u662f\u67f3\u7fe0\u7fe0\u8e31\u4e86\u8fc7\u53bb\uff0c\u4f46\u5df2\u7136\u7f69\u4e0a\u4e86\u9b3c\u9762\u5177\u3002 \u5a49\u513f\u4f3c\u4e4e\u4e0d\u76f8\u4fe1\u81ea\u5df1\u7684\u773c\u775b\uff0c\u770b\u80cc\u5f71\uff0c\u5206\u660e\u662f\u4e00\u4e2a\u5973\u5b50\u4e0e\u5c55\u767d\u5e76\u80a9\u5750\u5728\u6cb3\u8fb9\u8c08\u8bdd\uff0c\u600e\u4e48 \u8f6c\u8138\u7adf\u662f\u4e00\u4e2a\u4e11\u9b3c\u4f3c\u7684\u602a\u7269\uff1f \u67f3\u7fe0\u7fe0\u4e00\u60ca\u800c\u524d\uff0c\u9634\u6c89\u6c89\u5730\u8bf4\u9053\uff1a\u201c\u5979\u662f\u8c01\uff1f\u201d\u98de\u8eab\u4e00\u63a0\uff0c\u201c\u545b\u201d\u7684\u4e00\u58f0\uff0c\u4ece\u5c55\u767d\u80cc\u4e0a \u62bd\u51fa\u4e86\u201c\u65e0\u60c5\u78a7\u5251\u201d\uff01 \u5c55\u767d\u4e0d\u5907\uff0c\u88ab\u5979\u5413\u4e86\u4e00\u8df3\uff0c\u53c8\u731b\u7136\u8bb0\u8d77\u6628\u591c\u67f3\u7fe0\u7fe0\u6740\u4eba\u5982\u513f\u620f\u7684\u4e8b\u60c5\uff0c\u6015\u5979\u7a81\u7136\u51fa\u624b\uff0c \u4f24\u4e86\u5a49\u513f\uff0c\u5fd9\u9053\uff1a\u201c\u6765\uff01\u6211\u7ed9\u4f60\u4eec\u4ecb\u7ecd\uff0c\u8fd9\u662f\u5a49\u513f\uff0c\u8fd9\u662f\u67f3\u2026\u2026\u201d \u5c55\u767d\u8fd8\u672a\u201c\u67f3\u201d\u51fa\u6765\uff0c\u67f3\u7fe0\u7fe0\u4e00\u9707\u624b\u4e2d\u201c\u65e0\u60c5\u78a7\u5251\u201d\uff0c\u4ecd\u7136\u4ee5\u51b0\u51b7\u7684\u8bed\u8c03\u8bf4\u9053\uff1a\u201c\u54e6\uff01 \u4f60\u4eec\u662f\u65e9\u8ba4\u8bc6\u4e86\uff01\u5feb\u8bf4\uff0c\u4f60\u8ddf\u5979\u662f\u4ec0\u4e48\u5173\u7cfb\uff1f\u201d \u5c55\u767d\u60f3\u4e0d\u5230\u67f3\u7fe0\u7fe0\u7ade\u4e5f\u662f\u8fd9\u4e48\u5927\u7684\u918b\u52b2\uff0c\u4f46\u89c9\u5f97\u5982\u4eca\u4e0e\u5979\u7684\u5173\u7cfb\u5df2\u4e0d\u6bd4\u5bfb\u5e38\uff0c\u53c8\u4e0d\u613f\u8ba9 \u5979\u4f24\u5bb3\u5a49\u513f\uff0c\u5fd9\u9053\uff1a\u201c\u5979\u662f\u6211\u4e00\u4e2a\u5c0f\u59b9\u59b9\u2026\u2026\u201d \u7fe0\u7fe0\u53f1\u9053\uff1a\u201c\u6ca1\u6709\u95ee\u4f60\uff0c\u4f60\u5c11\u63d2\u5634\uff01\u201d \u8bf4\u7740\uff0c\u53c8\u8f6c\u5411\u5a49\u513f\u9053\uff1a\u201c\u4f60\u4e0d\u7528\u53d1\u5446\uff0c\u5feb\u8bf4\u5b9e\u8bdd\uff01\u5426\u5219\uff0c\u522b\u8bf4\u6211\u5bf9\u4f60\u4e0d\u5ba2\u6c14\uff01\u201d\u5a49\u513f\u51b0 \u96ea\u806a\u660e\uff0c\u4e4d\u89c1\u7fe0\u7fe0\u6234\u7740\u9b3c\u9762\u5177\u7684\u5bb9\u8c8c\uff0c\u8fd8\u4ee5\u4e3a\u81ea\u5df1\u770b\u9519\u4eba\uff0c\u4f46\u7b49\u5230\u7fe0\u7fe0\u4e00\u5f00\u53e3\uff0c\u7ade\u4e5f\u662f\u9178\u52b2 \u51b2\u5929\uff0c\u867d\u7136\u7fe0\u7fe0\u5589\u97f3\u5df2\u6539\uff0c\u5979\u4e5f\u770b\u51fa\u4e86\u516b\u6210\uff0c\u8fd9\u4e11\u9b3c\u4f3c\u7684\u5973\u4eba\uff0c\u53ef\u80fd\u662f\u4f2a\u88c5\uff0c\u53c8\u89c1\u5c55\u767d\u5c34\u5c2c \u7684\u795e\u6001\uff0c\u4fbf\u5df2\u4e86\u822c\u5230\u961f\u4e8e\u662f\u4e5f\u51b7\u54fc\u4e86\u4e00\u58f0\u3002\u53cd\u95ee\u9053\uff1a\u201c\u4f60\u53c8\u662f\u5c55\u54e5\u54e5\u7684\u4ec0\u4e48\u4eba\uff1f\u80c6\u6562\u5bf9\u6211\u8fd9 \u6837\u51f6\uff1f\u201d \u7fe0\u7fe0\u9053\uff1a\u201c\u6211\u662f\u4ed6\u7684\u59bb\u5b50\uff0c\u4f60\u2026\u3002\u201d \u5a49\u513f\u51b7\u7b11\u9053\uff1a\u201c\u54c8\uff01\u6211\u4f8b\u6ca1\u542c\u8bf4\u5c55\u54e5\u54e5\u7ed3\u8fc7\u5a5a\uff0c\u5374\u8dd1\u51fa\u59bb\u5b50\u6765\u4e86\uff01\u800c\u4e14\uff0e\u2026\uff0e\uff0e\u201d \u7fe0\u7fe0\u4e00\u9707\u624b\u4e2d\u201c\u65e0\u60c5\u78a7\u5251\u201d\u6012\u53f1\u9053\uff1a\u201c\u800c\u4e14\u4ec0\u4e48\uff01\u201d \u5a49\u513f\u5c0f\u5634\u4e00\u6487\uff0c\u9053\uff1a\u201c\u800c\u4e14\u4f60\u4e5f\u4e0d\u7167\u955c\u5b50\uff0c\u5c31\u7b97\u6ca1\u6709\u955c\u5b50\uff0c\u4f60\u4e5f\u53ef\u4ee5\u5728\u6cb3\u6c34\u4e2d\u53bb\u77a7\u77a7\u81ea \u5df1\u914d\u4e0d\u914d\u505a\u5c55\u54e5\u54e5\u7684\u59bb\u5b50\u2026\u2026\u201d \u7fe0\u7fe0\u5927\u6012\uff0c\u6296\u624b\u5c31\u662f\u4e00\u5251\uff0c\u76f4\u5411\u5a49\u513f\u524d\u80f8\u8981\u5bb3\u523a\u53bb\uff01 \u7fe0\u7fe0\u6b66\u529f\u9ad8\u5f3a\uff0c\u5251\u51fa\u5982\u98ce\uff0c\u201c\u65e0\u60c5\u78a7\u5251\u201d\u95ea\u8d77\u4e00\u6e9c\u78a7\u5149\uff0c\u51e0\u4e4e\u4ee5\u8089\u773c\u96be\u89c1\u7684\u901f\u5ea6\uff0c\u9012\u5230 \u5a49\u513f\u80f8\u524d\uff01 \u8c01\u77e5\u5a49\u513f\u201c\u8e51\u7a7a\u5e7b\u5f71\u201d\u8eab\u6cd5\uff0c\u5df2\u8fbe\u7089\u706b\u7eaf\u9752\u4e4b\u5883\uff0c\u7a0d\u4e00\u9519\u6b65\uff0c\u5373\u884c\u95ea\u5f00\uff0c\u540c\u65f6\u731b\u7136\u63a8\u51fa \u4e00\u638c\uff01 \u638c\u98ce\u9510\u5578\uff0c\u52bf\u75be\u529b\u731b\uff01 \u5c55\u767d\u5927\u53eb\u4e00\u58f0\uff1a\u201c\u4e0d\u8981\u6253\uff01\u2026\u2026\u201d \u4eba\u4e5f\u98d8\u98ce\u4f3c\u7684\u5411\u4e8c\u4eba\u4e2d\u95ee\u6251\u53bb\uff0c\u60f3\u6321\u5f00\u4e8c\u4eba\uff0c\u4f46\u5c55\u767d\u6251\u8fc7\u53bb\u65f6\uff0c\u5a49\u513f\u7684\u4e00\u638c\u4e5f\u63a8\u4e86\u51fa \u6765\uff0c\u5f3a\u731b\u638c\u98ce\uff0c\u6b63\u597d\u5411\u5c55\u767d\u8eab\u4e0a\u5377\u6765\uff01 \u5c55\u767d\u6251\u52bf\u6e38\u75be\uff0c\u518d\u60f3\u8eb2\u5df2\u6765\u4e0d\u53ca\uff0c\u65e0\u5948\u4f55\u4e3e\u638c\u4e00\u5c01\uff0c\u201c\u562d\u201d\u7684\u4e00\u58f0\u5927\u9707\uff0c\u5c55\u767d\u4e0e\u5a49\u513f\uff0c \u540c\u65f6\u4e0a\u8eab\u8fde\u6643\uff0c\u5404\u81ea\u9000\u540e\u4e00\u6b65\uff01 \u5a49\u513f\u4ee5\u4e3a\u5c55\u767d\u5e2e\u7740\u9752\u9762\u7ea2\u53d1\u602a\u4eba\u6b3a\u4fae\u81ea\u5df1\uff0c\u6c14\u5f97\u773c\u6cea\u5728\u773c\u7736\u6253\u8f6c\uff0c\u7c89\u8138\u715e\u767d\uff0c\u6012\u9053\uff1a\u4f60 \u5e2e\u8c01\uff1f\u2026\u2026\u201d \u8fd8\u672a\u7b49\u5c55\u767d\u8bf4\u8bdd\uff0c\u90a3\u8fb9\u67f3\u7fe0\u7fe0\u5df2\u5a07\u53f1\u4e00\u58f0\uff0c\u7b2c\u4e8c\u6b21\u8fd0\u5251\u5411\u5a49\u513f\u523a\u6765\uff01 \u5c55\u767d\u542c\u5230\u8eab\u540e\u91d1\u5203\u5288\u98ce\uff0c\u8f6c\u8138\u77a5\u89c1\u78a7\u5251\u6beb\u5149\uff0c\u4e00\u95ea\u800c\u81f3\uff0c\u5ffd\u7136\u5927\u559d\u9053\uff1a\u201c\u4f4f\u624b\uff01\u201d \u540c\u65f6\uff0c\u89c1\u67f3\u7fe0\u7fe0\u5e76\u6ca1\u6709\u4f4f\u624b\u7684\u610f\u601d\uff0c\u60c5\u6025\u4e4b\u4e0b\uff0c\u7528\u51fa\u4e00\u62db\u201c\u5378\u5173\u70b9\u5143\u201d\uff0c\u53f3\u624b\u731b\u52fe\u67f3\u7fe0 \u7fe0\u53f3\u8155\uff0c\u60f3\u628a\u201c\u65e0\u60c5\u78a7\u5251\u201d\u593a\u8fc7\u6765\uff0c\u4ee5\u514d\u4e8c\u5973\u518d\u6253\uff01 \u4ee5\u67f3\u7fe0\u7fe0\u7684\u6b66\u529f\u6765\u8bf4\uff0c\u968f\u4fbf\u4e00\u6362\u62db\uff0c\u5c31\u53ef\u4ee5\u628a\u5c55\u767d\u4f38\u51fa\u7684\u53f3\u624b\u65a9\u4f24\uff0c\u4f46\u5979\u4e0d\u613f\u4f24\u5230\u5c55 \u767d\uff0c\u89c1\u5c55\u767d\u963b\u4f4f\u81ea\u5df1\u5251\u8def\uff0c\u53ea\u6709\u6536\u5251\uff0c\u5411\u4e00\u4fa7\u8eb2\u53bb\uff01 \u4f46\u5a49\u513f\u6012\u6025\u4e4b\u4e0b\uff0c\u5374\u5df2\u628a\u9b3c\u795e\u7686\u60ca\u7684\u201c\u6298\u9b42\u6307\u201d\u65bd\u5c55\u51fa\u6765\uff0c\u53ea\u89c1\u5979\u67f3\u7709\u4e00\u7acb\uff0c\u731b\u7136\u5411\u67f3 \u7fe0\u7fe0\u201c\u5fc3\u4fde\u201d\u91cd\u7a74\uff0c\u9065\u9065\u6233\u51fa\u4e00\u6307\uff01 \u5c55\u767d\u6025\u5fd9\u53bb\u6321\uff0c\u5a49\u513f\u867d\u5728\u6c14\u5934\u4e0a\uff0c\u4e5f\u6015\u8bef\u4f24\u5230\u5c55\u81ea\uff0c\u8fde\u5fd9\u6536\u52bf\uff01\u2026\u2026 \u800c\u67f3\u7fe0\u7fe0\u53c8\u4ed7\u5251\u653b\u6765\uff01 \u5c55\u767d\u5de6\u8f6c\u53f3\u6321\uff0c\u524d\u62e6\u540e\u963b\uff0c\u540c\u65f6\u8fde\u559d\u4e8c\u5973\u4f4f\u624b\uff0c\u4f46\u5374\u963b\u6321\u4e0d\u4f4f\u4e8c\u5973\u7684\u4e92\u76f8\u653b\u51fb\uff01 \u4e0d\u8fc7\u4e8c\u5973\u90fd\u4e0d\u613f\u4f24\u5230\u5c55\u767d\uff0c\u56e0\u6b64\u51fa\u624b\u90fd\u72e0\u6bd2\u65e0\u6bd4\uff0c\u4f46\u89c1\u5c55\u767d\u6765\u6321\uff0c\u4fbf\u7acb\u90fd\u6536\u62db\u6362\u5f0f\uff01 \u4e8c\u5973\u51fa\u62db\u867d\u72e0\uff0c\u4f46\u6253\u5f97\u5e76\u4e0d\u751a\u6fc0\u70c8\uff0c\u53ef\u662f\uff0c\u5374\u628a\u5c55\u767d\u5fd9\u4e86\u4e2a\u624b\u5fd9\u811a\u4e71\uff0c\u62e6\u4e86\u8fd9\u4e00\u4e2a\uff0c\u53c8 \u53bb\u6321\u90a3\u4e00\u4e2a\uff0c\u6643\u773c\u95f4\u4e8c\u5973\u7ade\u5404\u81ea\u653b\u51fa\u4e86\u5341\u6570\u62db\uff01 \u5c55\u767d\u8eab\u4e0a\u88f9\u7684\u7834\u68c9\u88ab\uff0c\u5e03\u6761\u53c8\u677e\u5f00\u4e86\uff0c\u4e1c\u8f6c\u897f\u8f6c\uff0c\u624b\u811a\u4e00\u52a8\uff0c\u7a81\u7136\u6ed1\u8131\u4e0b\u6765\uff0c\u4e00\u526f\u88f8 \u4f53\uff0c\u7acb\u523b\u5448\u73b0\u5728\u4e8c\u5973\u9762\u524d\uff01\u67f3\u7fe0\u7fe0\u5df2\u7ecf\u89c1\u8bc6\u8fc7\u4e00\u6b21\uff0c\u8fd8\u4e0d\u600e\u4e48\u6837\uff0c\u52a0\u4e0a\u5979\u8138\u4e0a\u7f69\u4e86\u4e00\u4e2a\u9b3c\u9762 \u5177\uff0c\u591a\u5c11\u8fd8\u80fd\u906e\u70b9\u7f9e\uff1b\u53ef\u662f\u5a49\u513f\u89c1\u5230\u5c55\u767d\u8fd9\u526f\u5f62\u50cf\uff0c\u7acb\u523b\u7f9e\u4e86\u4e2a\u9762\u7ea2\u8033\u8d64\uff0c\u518d\u4e0d\u987e\u5f97\u548c\u67f3\u7fe0 \u7fe0\u4e89\u98ce\u76f8\u6253\uff0c\u7ade\u767d\u4e86\u5c55\u81ea\u4e00\u773c\uff0c\u6389\u5934\u5c31\u8dd1\u3002 \u67f3\u7fe0\u7fe0\u89c1\u72b6\u563b\u563b\u7b11\u9053\uff1a\u201c\u5582\uff01\u4f60\u600e\u4e48\u8dd1\u4e86\uff1f\u8981\u60f3\u8dd1\u4e5f\u884c\uff0c\u5148\u5f97\u628a\u4f60\u9879\u4e0a\u7684\u4eba\u5934\u7559 \u4e0b\u2026\u2026\u201d \u8bf4\u7740\u98de\u8eab\u5411\u5a49\u513f\u8ffd\u53bb\uff01 \u5176\u5b9e\uff0c\u5c55\u767d\u8eab\u4e0a\u7684\u7834\u68c9\u88ab\u8131\u843d\uff0c\u8d64\u8eab\u88f8\u4f53\uff0c\u4e5f\u95f9\u4e86\u4e2a\u6ee1\u9762\u7f9e\u7ea2\uff0c\u8d76\u7d27\u628a\u7834\u68c9\u88ab\u62c9\u8d77\u6765\uff0c \u91cd\u65b0\u7528\u7834\u5e03\u6761\u6346\u597d\uff0c\u4e00\u8fb9\u6697\u9a82\u81ea\u5df1\u5012\u9709\uff0c\u65f6\u65f6\u51fa\u6d0b\u76f8\uff0c\u4f46\u7b49\u4ed6\u6536\u62fe\u505c\u5f53\uff0c\u4e8c\u5973\u5df2\u7ecf\u8dd1\u51fa\u4e86\u89c6 \u7ebf\u4ee5\u5916\uff01 \u5c55\u767d\u6015\u4e8c\u5973\u4e92\u76f8\u62fc\u547d\uff0c\u968f\u540e\u8ffd\u53bb\uff0c\u4f46\u4e8c\u5973\u8eab\u6cd5\u5947\u5feb\uff0c\u4ed6\u8fde\u8ffd\u8fc7\u4e86\u4e24\u5ea7\u5c71\u5934\uff0c\u4ecd\u4e0d\u89c1\u4e8c\u5973 \u7684\u8e2a\u5f71\uff01 \u5c55\u767d\u6ee1\u5fc3\u7126\u6025\uff0c\u811a\u4e0b\u6025\u8d76\uff0c\u53c8\u8f6c\u8fc7\u4e86\u4e00\u5ea7\u5c71\u5934\uff0c\u773c\u524d\u5df2\u6765\u5230\u4e00\u7247\u72ed\u8c37\uff01 \u8fd9\u72ed\u8c37\u4e24\u58c1\u9ad8\u5cd9\uff0c\u4e2d\u95f4\u6beb\u65e0\u963b\u788d\uff0c\u987a\u8c37\u671b\u53bb\uff0c\u5728\u4e00\u7247\u60ac\u5d16\u524a\u58c1\u7684\u5c71\u811a\u4e0b\uff0c\u8001\u8001\u5c11\u5c11\u5374\u805a \u96c6\u4e86\u6570\u767e\u4eba\u4e4b\u591a\uff0c\u5728\u90a3\u91cc\u56f4\u6210\u4e86\u4e00\u4e2a\u5927\u5706\u5708\u5b50\uff0c\u800c\u4e14\u9690\u89c1\u5200\u5149\u95ea\u70c1\uff0c\u5251\u6c14\u5f25\u7a7a\u4e3e\u62f3\u7834\u98ce\u4e4b \u58f0\uff0c\u501f\u7740\u72ed\u8c37\u4f20\u58f0\uff0c\u201c\u9686\u9686\u201d\u5982\u96f7\uff0c\u5206\u660e\u662f\u6709\u4eba\u5728\u90a3\u91cc\u53ae\u6740\uff01 \u53c8\u89c1\u5a49\u513f\u4e0e\u7fe0\u7fe0\u4e5f\u6742\u5728\u4eba\u7fa4\u4e4b\u4e2d\uff0c\u5947\u602a\u7684\u662f\u4e8c\u5973\u5374\u4e0d\u6253\u4e86\uff0c\u4f3c\u4e4e\u4e5f\u56f4\u5728\u90a3\u91cc\u770b\u70ed\u95f9\uff01 \u5c55\u767d\u597d\u5947\u5fc3\u5927\u53d1\uff0c\u811a\u4e0b\u4f7f\u52b2\uff0c\u63a5\u8fde\u51e0\u4e2a\u8d77\u843d\uff0c\u4eba\u5982\u98d8\u98ce\u95ea\u7535\u4e00\u822c\uff0c\u8d76\u8fd1\u524d\u53bb\uff0c\u4e00\u770b\u4e4b \u4e0b\uff0c\u5f53\u573a\u53d1\u8d77\u6014\u6765\uff01\u539f\u6765\uff0c\u90a3\u7fa4\u4eba\u4e4b\u4e2d\uff0c\u56db\u5927\u8c6a\u95e8\u7684\u9ad8\u624b\u90fd\u5728\uff0c\u591a\u534a\u90fd\u662f\u9677\u5728\u91d1\u5e9c\u201c\u77f3\u77f6\u5927 \u9635\u201d\u4e2d\u7684\u4eba\u7269\uff01 \u5c55\u767d\u5df2\u77e5\u4ed6\u4eec\u662f\u88ab\u7fe0\u7fe0\u6551\u51fa\uff0c\u4f46\u4f17\u4eba\u56f4\u62e2\u5728\u90a3\u91cc\u77aa\u773c\u671b\u7740\u5708\u4e2d\u6709\u6570\u4eba\u640f\u6597\uff0c\u7adf\u4f3c\u51fa\u4e86\u795e \u4f3c\u7684\uff0c\u5bf9\u8eab\u65c1\u7684\u4ec7\u654c\u90fd\u89c6\u82e5\u65e0\u7779\uff01 \u800c\u4e14\u5c55\u767d\u6765\u4e86\uff0c\u4e5f\u65e0\u4eba\u56de\u5934\u671b\u4e00\u4e0b\uff0c\u4ecd\u7136\u76ee\u6ce8\u5708\u4e2d\u7684\u640f\u6597\uff0c\u597d\u50cf\u5708\u4e2d\u7684\u640f\u6597\uff0c\u5177\u6709\u83ab\u5927 \u7684\u5438\u5f15\u529b\uff0c\u5bf9\u5916\u5728\u7684\u4e00\u5207\u90fd\u4e0d\u5173\u5fc3\u4e86\uff01 \u5c55\u767d\u4e5f\u63a2\u5934\u671b\u53bb\uff0c\u53ea\u89c1\u573a\u4e2d\u6709\u516d\u4eba\uff0c\u5206\u4e09\u5bf9\u5728\u90a3\u91cc\uff0c\u6253\u5f97\u96be\u89e3\u96be\u5206\uff01 \u5730\u4e0b\u5df2\u5012\u4e0b\u4e86\u5341\u6570\u6761\u6b7b\u5c38\uff0c\u60f3\u5fc5\u662f\u5148\u524d\u53ae\u6740\u6bd9\u547d\u7684\uff01 \u56db\u5916\u56f4\u89c2\u7684\u4eba\u4f17\u4e2d\uff0c\u4e5f\u6709\u4e0d\u5c11\u6302\u5f69\u7684\uff0c\u65ad\u81c2\u5c11\u817f\uff0c\u9c9c\u8840\u6ee1\u8eab\uff0c\u4f46\u4ecd\u7136\u6ca1\u5b9a\uff0c\u597d\u50cf\u4f24\u52bf\u7565 \u4e3a\u5305\u624e\u6cbb\u7597\uff0c\u5373\u7ad9\u5728\u5708\u5916\u53c2\u89c2\u2026\u2026 \u5c55\u767d\u770b\u90a3\u5012\u5730\u6b7b\u53bb\u4ee5\u53ca\u8d1f\u4f24\u4e4b\u4eba\uff0c\u5927\u90fd\u662f\u5f53\u4eca\u56db\u5927\u8c6a\u95e8\u7684\u9ad8\u624b\uff0c\u4e0d\u7531\u6697\u6697\u5fc3\u60ca\uff0c\u662f\u54ea\u6761 \u9053\u4e0a\u7684\u4eba\u7269\uff0c\u7adf\u6709\u8fd9\u4e48\u9ad8\u5f3a\u7684\u6b66\u529f\uff1f \u518d\u770b\u90a3\u52a8\u7740\u624b\u7684\u516d\u4eba\uff0c\u4e00\u4e2a\u767d\u987b\u8fc7\u8179\u7684\u957f\u9aef\u8001\u4eba\uff0c\u4e00\u4e2a\u9e21\u76ae\u6ee1\u8138\u7684\u4e11\u602a\u8001\u5987\uff0c\u4e00\u4e2a\u5374\u662f \u8eab\u5f62\u77ee\u80d6\uff0c\u5706\u58a9\u58a9\u7684\u4e00\u526f\u8eab\u6750\uff0c\u5374\u957f\u4e86\u4e00\u526f\u5a03\u5a03\u8138\u7684\u4e2d\u5e74\u548c\u5c1a\uff01 \u8fd9\u4e09\u4eba\u5374\u5f88\u9762\u751f\uff0c\u5c55\u767d\u4ece\u672a\u89c1\u8fc7\uff0c\u4f46\u770b\u5176\u8863\u9970\u6253\u626e\u51b3\u4e0d\u662f\u4e2d\u539f\u4eba\u58eb\uff01 \u4e0e\u8fd9\u4e09\u4eba\u52a8\u624b\u7684\uff0c\u4e00\u4e2a\u662f\u201c\u6469\u4e91\u795e\u624b\u201d\u5411\u51b2\u5929\uff0c\u4e00\u4e2a\u662f\u201c\u8840\u638c\u706b\u9f99\u201d\u59da\u70b3\u6606\uff0c\u53e6\u5916\u4e00\u4e2a \u5374\u662f\u5c55\u767d\u5c1a\u672a\u4f1a\u8fc7\u9762\u4f46\u5374\u65e9\u6709\u8033\u95fb\u7684\u201c\u6bd2\u5251\u7075\u86c7\u201d\u4fde\u5316\u5357\uff01 \u8fd9\u4e09\u4eba\u90fd\u662f\u201c\u4e91\u68a6\u5c71\u5e84\u201d\u201c\u5b89\u4e50\u516c\u5b50\u201d\u95e8\u4e0b\u7684\u4e00\u6d41\u9ad8\u624b\uff01 \u663e\u89c1\u8fd9\u4e00\u6218\uff0c\u662f\u4ee5\u201c\u5b89\u4e50\u516c\u5b50\u201d\u95e8\u4e0b\u4e3a\u4e3b\u8981\u7684\u5bf9\u8c61\u3002 \u5c55\u767d\u56db\u4e0b\u4e00\u770b\uff0c\u679c\u89c1\u201c\u5b89\u4e50\u516c\u5b50\u201d\u4e91\u94ee\uff0c\u4ee5\u53ca\u4ed6\u7684\u7236\u4eb2\u201c\u4e7e\u5764\u638c\u201d\u4e91\u5b97\u9f99\uff0c\u795e\u60c5\u6700\u4e3a\u7d27 \u5f20\uff0c\u53cc\u76ee\u4e00\u77ac\u4e0d\u77ac\u5730\u6ce8\u5b9a\u573a\u4e2d\uff0c\u597d\u50cf\u8fd9\u4e00\u573a\u7684\u80dc\u8d25\uff0c\u5173\u7cfb\u201c\u5b89\u4e50\u516c\u5b50\u201d\u95e8\u4e0b\u751a\u5927\uff01 \u201c\u5b89\u4e50\u516c\u5b50\u201d\u5df2\u5931\u53bb\u5f80\u6614\u7684\u6f47\u6d12\u4ece\u5bb9\uff0c\u53cc\u624b\u7d27\u6293\u7740\u65f6\u523b\u4e0d\u79bb\u624b\u7684\u90a3\u672c\u7834\u4e66\uff0c\u989d\u4e0a\u5df2\u7ecf\u9690 \u9690\u73b0\u6c57\uff01\u2026\u2026 \u5728\u53e6\u4e00\u8fb9\u5374\u7ad9\u7740\u90a3\u548c\u5c55\u767d\u89c1\u8fc7\u4e00\u9762\u7684\u767d\u8863\u4e66\u751f\uff0c\u867d\u7136\u662f\u5728\u51ac\u5929\uff0c\u4ed6\u624b\u4e2d\u4ecd\u6447\u7740\u4e00\u67c4\u94f6 \u6247\uff0c\u8138\u4e0a\u5806\u6ee1\u4e86\u8f7b\u677e\u7684\u7b11\u5bb9\uff0c\u770b\u6837\u5b50\u662f\u5f97\u610f\u6d0b\u6d0b\uff0c\u795e\u6c14\u5df2\u6781\uff01 \u5c55\u767d\u867d\u4e0d\u77e5\u4ed6\u4e3a\u4ec0\u4e48\u4e8b\u60c5\uff0c\u90a3\u822c\u5f97\u610f\uff1f\u4f46\u770b\u573a\u4e2d\u7684\u60c5\u5f62\uff0c\u90a3\u957f\u9aef\u8001\u4eba\uff0c\u767d\u53d1\u5a46\u5a46\u4ee5\u53ca\u90a3 \u4e2d\u5e74\u548c\u5c1a\uff0c\u5374\u662f\u5df2\u7ecf\u7a33\u64cd\u80dc\u7b97\uff01 \u573a\u4e2d\u516d\u4e2a\u4eba\uff0c\u5206\u4e09\u5bf9\u53ae\u6740\uff0c\u201c\u6469\u4e91\u795e\u624b\u201d\u5411\u51b2\u5929\u654c\u4f4f\u957f\u9aef\u8001\u4eba\u201c\u8840\u638c\u706b\u9f99\u201d\u59da\u70b3\u6606\u654c\u4f4f \u767d\u53d1\u5a46\u5a46\uff0c\u201c\u6bd2\u5251\u7075\u86c7\u201d\u654c\u4f4f\u4e2d\u5e74\u548c\u5c1a\uff01 \u5176\u4e2d\u6218\u201c\u6469\u4e91\u795e\u624b\u201d\u5411\u51b2\u5929\u60c5\u52bf\u6700\u4e3a\u5371\u6025\uff0c\u53ef\u89c1\u90a3\u957f\u9aef\u8001\u4eba\u6b66\u529f\u5b9e\u5728\u592a\u9ad8\uff0c\u5f53\u5e74\u201c\u6469\u4e91 \u795e\u624b\u201d\u5411\u51b2\u5929\u5355\u4eba\u72ec\u8e39\u6d59\u4e1c\u4e03\u5bb6\u9556\u5c40\uff0c\u96c1\u8361\u5c71\u638c\u5288\u6c5f\u5357\u5de8\u76d7\u201c\u94e3\u9a91\u91d1\u5200\u201d\u6234\u4e1c\u9aa5\uff0c\u58f0\u540d\u9e4a\u8d77 \u9707\u52a8\u6c5f\u6e56\uff01 \u90a3\u8fd8\u662f\u4ed6\u4e2d\u8f7b\u65f6\u7684\u8c6a\u4e3e\uff0c\u5982\u4eca\u5df2\u5c4a\u58ee\u5e74\uff0c\u6b66\u529f\u66f4\u52a0\u7cbe\u7eaf\uff0c\u4e00\u8eab\u8f6f\u786c\u529f\u592b\u5df2\u81fb\u5316\u5883\uff0c\u5c0f\u5de7 \u8f7b\u8eab\u672f\u66f4\u662f\u6240\u957f\uff0c\u4e0d\u4f46\u5728\u201c\u4e91\u68a6\u5c71\u5e84\u201d\u7a33\u5750\u7b2c\u4e00\u628a\u4ea4\u6905\uff0c\u5c31\u662f\u5728\u5f53\u4eca\u6b66\u6797\uff0c\u4e5f\u582a\u79f0\u4e3a\u9ad8\u624b\u4e4b \u4e2d\u7684\u9ad8\u624b\uff01 \u5c24\u5176\u4ed6\u624b\u4e2d\uff0c\u73b0\u5728\u62ff\u4e86\u4e00\u628a\u524a\u91d1\u5207\u7389\u7684\u201c\u7d2b\u91d1\u7535\u5149\u5200\u201d\uff0c\u66f4\u662f\u5982\u864e\u6dfb\u7ffc\uff0c\u5de6\u638c\u5df2\u6210\u540d\u591a \u5e74\u7684\u201c\u6469\u4e91\u795e\u624b\u201d\u780d\u3001\u5288\u3001\u63a8\u3001\u6253\uff0c\u53f3\u624b\u7d27\u63e1\u201c\u201d\u523a\u3001\u624e\u3001\u65a9\u3001\u64a9\uff0c\u5200\u5149\u638c\u5f71\uff0c\u65bd\u5c55\u5f97\u98ce\u4e91 \u4e0d\u900f\uff01 \u53ef\u662f\uff0c\u5374\u88ab\u90a3\u957f\u9aef\u8001\u4eba\u7684\u4e00\u53cc\u8089\u638c\u786c\u7ed9\u56f4\u4f0e\uff0c\u4e0d\u7ba1\u4ed6\u600e\u6837\u5de6\u51b2\u53f3\u95ef\uff0c\u4e5f\u8131\u4e0d\u51fa\u957f\u9aef\u8001\u4eba \u7684\u638c\u5708\uff0c\u201c\u6469\u4e91\u795e\u624b\u201d\u5411\u51b2\u5929\u5df2\u7136\u6c14\u5598\u547c\u547c\uff0c\u989d\u4e0a\u5192\u6c57\uff0c\u4f46\u90a3\u8001\u4eba\u5374\u4ece\u4ece\u5bb9\u5bb9\uff0c\u82e5\u65e0\u5176\u4e8b\u7684 \u4e00\u822c\uff01\u663e\u89c1\u5e76\u672a\u7528\u51fa\u5168\u529b\uff0c\u53ea\u662f\u4e00\u5473\u5730\u6e38\u6597\uff0c\u5de6\u4e00\u638c\uff0c\u53f3\u4e00\u638c\uff0c\u8f7b\u8f7b\u98d8\u98d8\u3002\u201c\u6469\u4e91\u795e\u624b\u201d\u5411 \u51b2\u5929\u5df2\u7ecf\u65e0\u6cd5\u5e94\u4ed8\uff0c\u582a\u582a\u5c31\u8981\u843d\u8d25\u2026\u2026 \u4e0e\u767d\u53d1\u5a46\u5a46\u52a8\u624b\u7684\u201c\u8840\u638c\u706b\u9f99\u201d\u59da\u70b3\u5e4c\uff0c\u4e5f\u4e0d\u89c1\u5f97\u8f7b\u677e\uff01 \u5c55\u767d\u66fe\u4f24\u5728\u201c\u8840\u638c\u706b\u9f99\u201d\u638c\u4e0b\u4e00\u6b21\uff0c\u77e5\u9053\u4ed6\u7684\u201c\u7ea2\u7802\u8840\u5f62\u638c\u201d\u5389\u5bb3\u3002\u53ef\u662f\u73b0\u5728\u767d\u53d1\u5a46\u5a46 \u624b\u4e0b\uff0c\u4ed6\u6253\u904d\u82cf\u9c81\u7684\u201c\u7ea2\u7802\u8840\u5f62\u638c\u201d\u5374\u4f3c\u5931\u53bb\u4e86\u6548\u7528\u3002\u800c\u4e14\uff0c\u8fde\u4ed6\u90a3\u63d2\u5728\u5de6\u80a9\u4e0a\u8f7b\u6613\u4e0d\u52a8\u7528 \u7684\u72ec\u95e8\u5175\u5668\u201c\u4ed9\u4eba\u638c\u201d\u4e5f\u65bd\u5c55\u4e86\u51fa\u6765\uff0c\u4ecd\u7136\u662f\u5b88\u591a\u653b\u5c11\uff0c\u5728\u767d\u53d1\u5a46\u5a46\u7684\u4e00\u53cc\u8089\u638c\u4e4b\u4e0b\uff0c\u624b\u5fd9 \u811a\u4e71\uff0c\u987e\u6b64\u5931\u5f7c\uff01\u2026\u2026 \u201c\u6bd2\u5251\u7075\u86c7\u201d\u4fde\u5316\u9664\u4ed7\u7740\u4e00\u67c4\u201c\u5582\u6bd2\u84dd\u5251\u201d\u4ee5\u53ca\u2014\u6761\u201c\u94c1\u7ebf\u7075\u86c7\u201d\uff0c\u8ddf\u90a3\u4e2d\u5e74\u548c\u5c1a\u52c9\u5f3a \u6253\u4e86\u4e2a\u5e73\u624b\uff0c\u4e00\u65f6\u4e4b\u95f4\u4e0d\u81f4\u843d\u8d25\uff01 \u201c\u5b89\u4e50\u516c\u5b50\u201d\u95e8\u4e0b\u4e09\u5927\u9ad8\u624b\uff0c\u5012\u6709\u4e24\u573a\u582a\u582a\u89c1\u8f93\uff0c\u96be\u602a\u4ed6\u8fd9\u6837\u7d27\u5f20\u4e86\uff01 \u5c55\u767d\u5374\u4e0d\u77e5\u4ed6\u4eec\u4e3a\u4ec0\u4e48\u8981\u6076\u6218\u8d77\u6765\uff0c\u800c\u4e14\u4e92\u76f8\u6c34\u706b\u7684\u56db\u5927\u8c6a\u95e8\uff0c\u7adf\u7136\u4e00\u9f50\u663e\u5f97\u8fd9\u4e48\u5173\u5fc3 \u6b64\u6218\uff1f\u540c\u65f6\uff0c\u66f4\u4f7f\u4ed6\u5947\u602a\u7684\u662f\uff0c\u5a49\u513f\u4e0e\u7fe0\u7fe0\u62fc\u547d\u4e89\u6253\uff0c\u81ea\u5df1\u62c9\u90fd\u62c9\u4e0d\u5f00\uff0c\u5230\u4e86\u6b64\u5730\u7adf\u4e5f\u5fd8\u8bb0 \u4e86\u6253\u6597\uff0c\u770b\u8d77\u70ed\u95f9\u6765\u4e86\uff01 \u96be\u9053\u8fd9\u4e00\u573a\u6bd4\u6b66\u6709\u4ec0\u4e48\u91cd\u5927\u7684\u610f\u4e49\u5417\uff1f\u2026\uff0c\uff0e\xb7 \u5c55\u767d\u4e00\u8fb9\u770b\uff0c\u4e00\u8fb9\u6c89\u601d\u3002\u7a81\u542c\u90a3\u767d\u53d1\u5a46\u5a46\u53eb\u9053\uff1a\u201c\u8001\u4e0d\u6b7b\uff01\u4f60\u65e2\u7136\u5360\u4e86\u4e0a\u98ce\uff0c\u4e3a\u4ec0\u4e48\u8fd8 \u4e0d\u65e9\u70b9\u628a\u90a3\u5c0f\u5b50\u6599\u7406\u4e86\uff1f\u4e00\u5473\u5730\u6e38\u6597\uff01\u56db\u5916\u8fd9\u4e48\u591a\u7b49\u7740\u9001\u6b7b\u7684\u4eba\uff0c\u6709\u5f97\u54b1\u4eec\u6253\u5462\uff0c\u65e9\u65bd\u6740 \u624b\uff01\u89e3\u51b3\u4e86\u8fd9\u4e00\u573a\uff0c\u597d\u6253\u4e0b\u4e00\u573a\uff01\u201d \u957f\u9aef\u8001\u4eba\u9053\uff1a\u201c\u8d3c\u5a46\u5a18\u869c\uff01\u600e\u4e48\u4e2d\u539f\u6b66\u6797\u51c0\u662f\u4e9b\u8113\u5305\uff0c\u53eb\u6211\u8001\u4eba\u5bb6\u6253\u4e0d\u8fc7\u763e\u5440\uff01\u201d \u957f\u9aef\u8001\u4eba\u5634\u4e2d\u867d\u5982\u6b64\u8bf4\u7740\uff0c\u624b\u5e95\u4e0b\u90e8\u5df2\u7ecf\u65bd\u51fa\u4e86\u6740\u62db\uff0c\u53ea\u89c1\u4ed6\u53cc\u638c\u4e00\u7d27\uff0c\u51b2\u7740\u201c\u6469\u4e91\u795e \u624b\u201d\u5411\u51b2\u5929\u563f\u563f\u7b11\u9053\uff1a\u201c\u9ed1\u5c0f\u5b50\uff01\u4f60\u6b7b\u4e86\u53ef\u522b\u6028\u6211\uff0c\u5230\u960e\u738b\u7237\u9762\u524d\u544a\u72b6\uff0c\u53ea\u8981\u544a\u90a3\u8d3c\u5a46\u5a18\u5c31 \u662f\u4e86\uff0c\u662f\u5979\u53eb\u6211\u6740\u4f60\u7684\uff01\u201d\u2018\u6469\u4e91\u795e\u624b\u201d\u5411\u51b2\u5929\u6c5f\u6e56\u9053\u4e0a\u6210\u540d\u591a\u5e74\uff0c\u4e2d\u539f\u6b66\u6797\u8c01\u89c1\u4e86\u4e0d\u5c0a \u656c\uff1f\u54ea\u91cc\u53d7\u8fc7\u8fd9\u7b49\u83ab\u843d\uff1f\u660e\u77e5\u4e0d\u654c\uff0c\u4e5f\u6c14\u5f97\u602a\u76ee\u5706\u7741\uff0c\u5389\u543c\u4e00\u58f0\uff0c\u53f3\u624b\u201c\u7d2b\u91d1\u7535\u5149\u5200\u201d\u201c\u6d6a \u5377\u6d41\u6c99\u201d\uff0c\u5de6\u624b\u201c\u6469\u4e91\u795e\u624b\u201d\u201c\u4e71\u77f3\u5d29\u4e91\u201d\uff0c\u4e24\u5927\u6740\u62db\u540c\u65f6\u65bd\u51fa\uff0c\u4ee5\u62fc\u547d\u7684\u62db\u5f0f\uff0c\u9f50\u5411\u957f\u9aef \u8001\u4eba\u653b\u53bb\uff01 \u957f\u9aef\u8001\u4eba\u5fae\u5fae\u4e00\u7b11\uff0c\u53ea\u53cc\u638c\u4e00\u63a8\u4e00\u632b\uff0c\u201c\u6469\u4e91\u795e\u624b\u201d\u5411\u51b2\u5929\u53d1\u51fa\u4e00\u58f0\u91ce\u517d\u822c\u7684\u60e8\u55e5\uff0c\u5012 \u8dcc\u51fa\u4e8c\u4e08\u5f00\u5916\uff01\u518d\u4e00\u770b\uff0c\u4ed6\u53f3\u624b\u4e2d\u7684\u201c\u7d2b\u91d1\u7535\u5149\u5200\u201d\u5df2\u4e0d\u77e5\u600e\u4e48\u53cd\u63d2\u8fdb\u81ea\u5df1\u7684\u5c0f\u8179\u4e2d\uff0c\u9762\u8272 \u63ba\u767d\uff0c\u4ef0\u9762\u8dcc\u5012\u5730\u4e0a\uff0c\u770b\u6837\u5b50\u5df2\u7ecf\u662f\u6b7b\u591a\u6d3b\u5c11\u4e86\uff01 \u4f17\u4eba\u65e0\u4e0d\u5403\u60ca\uff0c\u50cf\u8fd9\u6837\u4ee5\u81f4\u624b\u5175\u5668\uff0c\u53cd\u6740\u654c\u4eba\u81ea\u5df1\uff0c\u53ef\u8bf4\u662f\u95fb\u6240\u672a\u95fb\uff0c\u89c1\u6240\u672a\u89c1\uff01 \u957f\u9aef\u8001\u4eba\u6296\u987b\u4e00\u7b11\u9053\uff1a\u201c\u8d3c\u5a46\u5a18\uff01\u8001\u5934\u5b50\u7684\u8fd9\u4e00\u4e2a\u6599\u7406\u4e0a\uff0c\u73b0\u5728\u770b\u4f60\u8001\u5a46\u5b50\u7684\u4e86\uff01\u201d \u767d\u53d1\u5a46\u5a46\u201c\u5440\u5440\u201d\u7b11\u9053\uff1a\u201c\u4e0d\u51fa\u4e09\u62db\uff01\u8001\u5a18\u8fd9\u4e00\u4e2a\u4e5f\u53ef\u4ee5\u62a5\u9500\uff01\u201d \u201c\u8840\u638c\u706b\u9f99\u201d\u59da\u70b3\u5e4c\u6027\u5982\u70c8\u706b\uff0c\u542c\u8fd9\u4e8c\u8001\u62ff\u81ea\u5df1\u4e00\u5f00\u6325\uff0c\u4e0d\u7531\u6c14\u5f97\u4e09\u5c38\u66b4\u8df3\uff0c\u5389\u543c\u9053\uff1b \u201c\u5c11\u5f20\u72c2\uff01\u8001\u592b\u8ddf\u4f60\u62fc\u4e86\uff01\u201d\u543c\u58f0\u4e2d\u5de6\u638c\u80f8\u524d\u4e00\u62b9\uff0c\u53cd\u8f6c\u638c\u5fc3\u5411\u5916\uff0c\u638c\u5fc3\u7acb\u523b\u73b0\u51fa\u4e00\u7247\u8840 \u7ea2\uff01 \u767d\u53d1\u5a46\u5a46\u53c8\u5e72\u7b11\u4e86\u4e24\u58f0\u9053\uff1a\u201c\u7ea2\u5c0f\u5b50\uff01\u4f60\u628a\u624b\u638c\u78e8\u51fa\u8840\u6765\u4e5f\u6ca1\u6709\u7528\uff01\u8001\u5a18\u7167\u6837\u6253\u53d1\u4f60\u56de \u5916\u5a46\u5bb6\u53bb\u89c1\u59e5\u59e5\u2026\u2026\u201d \u767d\u53d1\u5a46\u5a46\u8bdd\u672a\u8bf4\u5b8c\uff0c\u201c\u8840\u638c\u706b\u9f99\u201d\u5df2\u5410\u6c14\u5f00\u58f0\uff0c\u628a\u201c\u7ea2\u7802\u8840\u5f62\u638c\u201d\u529f\u529b\u8fd0\u81f3\u5341\u6210\u731b\u7136\u63a8 \u51fa\uff01 \u4e00\u822c\u706b\u70ed\u7684\u5de8\u6d41\uff0c\u7834\u7a7a\u5411\u767d\u53d1\u5a46\u5a46\u6d8c\u81f3\uff01 \u767d\u53d1\u5a46\u5a46\u53cc\u638c\u4e00\u7ffb\uff0c\u7adf\u4e5f\u73b0\u51fa\u4e86\u4e00\u56e2\u8499\u8499\u81ea\u6c14\uff0c\u4ee5\u6c81\u9aa8\u7684\u6781\u5bd2\uff0c\u8fce\u7740\u90a3\u80a1\u70ed\u98ce\u5377\u53bb\uff01 \u638c\u98ce\u4e00\u63a5\uff0c\u201c\u8840\u638c\u706b\u9f99\u201d\u673a\u4f36\u4f36\u6253\u4e86\u4e24\u4e2a\u51b7\u6218\uff0c\u672c\u662f\u4e00\u5f20\u8d64\u7ea2\u8138\uff0c\u7acb\u523b\u53d8\u6210\u60e8\u767d\uff01 \u4f17\u4eba\u53c8\u662f\u4e00\u60ca\uff01\u8fd9\u662f\u4ec0\u4e48\u638c\u529f\uff1f\u65e0\u58f0\u65e0\u606f\u5730\u4fbf\u628a\u65e0\u4eba\u6562\u654c\u7684\u201c\u7ea2\u7802\u8840\u5f62\u638c\u201d\u7ed9\u7834\u4e86\u2026\u2026 \u201c\u8840\u638c\u706b\u9f99\u201d\u5fc3\u5185\u4e00\u51c9\uff0c\u77e5\u9053\u81ea\u5df1\u82e6\u7ec3\u4e00\u4e2a\u7532\u5b50\u7684\u6b66\u529f\uff0c\u7b97\u662f\u5b8c\u4e86\uff0c\u5fc3\u5185\u4e00\u60e8\uff0c\u4e24\u884c\u8001 \u6cea\u987a\u816e\u6d41\u4e0b\u6765\u3002\u4f46\u77ac\u5373\u54ac\u7259\u5207\u9f7f\u5730\u53c8\u628a\u4ed6\u53f3\u624b\u7684\u201c\u4ed9\u4eba\u638c\u201d\u98a4\u6296\u7740\u4e4e\u4e3e\u4e86\u8d77\u6765\uff01 \u201c\u4ed9\u4eba\u638c\u201d\u672c\u662f\u4ed6\u7684\u8d81\u624b\u5175\u5668\uff0c\u65b9\u624d\u8fd8\u6325\u821e\u5982\u98de\uff0c\u4f46\u73b0\u5728\u7528\u5c3d\u5468\u8eab\u4e4b\u529b\uff0c\u90fd\u96be\u4ee5\u5e73\u4e3e\u8d77 \u6765\uff01\u2026\u2026 \u201c\u4ed9\u4eba\u638c\u201d\u5728\u201c\u8840\u638c\u5927\u529b\u4e2d\u624b\u4e2d\u4e00\u5bf8\u3001\u4e8c\u5bf8\u2026\u2026\u7f13\u7f13\u5411\u4e0a\u5e73\u4e3e\u2026\u2026 \u56db\u5468\u56f4\u89c2\u4e4b\u4eba\uff0c\u8fd8\u4e0d\u660e\u5c31\u91cc\uff0c\u53ea\u89c1\u201c\u8840\u638c\u706b\u9f99\u201d\u9762\u8272\u60e8\u767d\uff0c\u773c\u6d41\u75db\u6cea\uff0c\u6d51\u8eab\u6218\u6296\u7740\u4e3e\u8d77 \u81ea\u5df2\u7684\u5175\u5668\uff0c\u8fd8\u4ee5\u4e3a\u4ed6\u662f\u60b2\u6124\u8fc7\u5ea6\u2026\uff0c\uff0e\uff0e \u767d\u53d1\u5a46\u5a46\u5806\u8d77\u6ee1\u8138\u76b1\u7eb9\uff0c\u5c1a\u5728\u53d6\u7b11\u8bf4\uff1a\u201c\u8fd9\u4e00\u638c\u5473\u9053\u600e\u4e48\u6837\uff1f\u4f60\u5e94\u8be5\u8c22\u8c22\u8001\u5a18\uff0c\u7ea2\u5c0f\u5b50 \u8ba9\u4f60\u53d8\u6210\u767d\u5c0f\u5b50\u2026\u2026\u201d \u957f\u9aef\u8001\u4eba\u7a81\u7136\u6025\u9053\uff1a\u201c\u8d3c\u5a46\u5a18\uff01\u5c0f\u5fc3\u6697\u7b97\u2026\u2026\u201d \u559d\u58f0\u4e2d\uff0c\u4ed6\u53c8\u6253\u51fa\u4e00\u638c\uff01\u4f46\u4ecd\u662f\u6162\u4e86\u4e00\u6b65\uff0c\u4ed6\u4e00\u638c\u5c1a\u672a\u6253\u5728\u201c\u8840\u638c\u706b\u9f99\u201d\u8eab\u4e0a\uff0c\u201c\u8840\u638c \u706b\u9f99\u201d\u5df2\u628a\u201c\u4ed9\u4eba\u638c\u201d\u5e73\u4e3e\u5728\u80f8\u524d\uff0c\u62c7\u6307\u4e00\u6309\u5f39\u7c27\uff0c\u201c\u5361\u8e66\u201d\u4e00\u58f0\u5fae\u54cd\uff0c\u4e00\u9053\u767d\u70df\uff0c\u76f4\u5954\u767d \u53d1\u5a46\u5a46\u5c04\u51fb\uff01 \u4ed6\u7684\u4eba\u5374\u4e5f\u88ab\u957f\u9aef\u8001\u4eba\u4e00\u638c\u9707\u98de\u2026\u2026 \u767d\u53d1\u5a46\u5a46\u88ab\u957f\u9aef\u8001\u4eba\u4e00\u8bed\u63d0\u9192\uff0c\u731b\u51fa\u4e00\u638c\u5411\u90a3\u767d\u70df\u5288\u53bb\uff01 \u201c\u5403\uff01\u201d\u4e00\u6e9c\u84dd\u8272\u706b\u7130\uff0c\u5982\u4e00\u83b2\u5149\u96e8\u822c\u6563\u4e86\u5f00\u6765\uff0c\u767d\u53d1\u5a46\u5a46\u867d\u7136\u53d1\u89c9\u5f97\u65e9\uff0c\u53ca\u65f6\u8fd0\u638c\u5c01 \u5f00\uff0c\u4f46\u4ecd\u6709\u6570\u70b9\u706b\u661f\uff0c\u6e85\u5728\u767d\u53d1\u4e0a\uff0c\u7acb\u523b\u8d77\u706b\u71c3\u70e7\u8d77\u6765\uff01\u2026\u2026 \u540c\u65f6\uff0c\u5149\u96e8\u56db\u6e85\uff0c\u56db\u5916\u56f4\u89c2\u4e4b\u4eba\uff0c\u4e5f\u6709\u4e0d\u5c11\u8863\u670d\u4e0a\u88ab\u6e85\u4e0a\u706b\u661f\uff0c\u5239\u65f6\uff0c\u706b\u7130\u56db\u8d77\uff0c\u4e00\u7247 \u60ca\u547c\u2026\u2026 \u7b2c\u4e09\u5341\u4e5d\u7ae0\u3000\u201c\u641c\u9b42\u6307\u201d>> \u53e4\u9f99\u300a\u5251\u5ba2\u884c\u300b \u7b2c\u4e09\u5341\u4e5d\u7ae0\u3000\u201c\u641c\u9b42\u6307\u201d \u4f17\u4eba\u4e00\u9635\u5927\u4e71\uff0c\u6251\u6253\u5468\u8eab\u706b\u7130\uff0c\u5f85\u628a\u706b\u7130\u6251\u706d\uff0c\u767d\u53d1\u5a46\u5a46\u5934\u4e0a\u7a00\u758f\u7684\u767d\u53d1\uff0c\u5df2\u51e0\u4e4e\u88ab\u70e7 \u79c3\uff01 \u767d\u53d1\u5a46\u5a46\u6c14\u5f97\u5389\u5578\u8fde\u58f0\uff0c\u731b\u7136\u53c8\u5411\u53d7\u4f24\u5012\u5730\u7684\u201c\u8840\u638c\u706b\u9f99\u201d\u63a5\u8fde\u70b9\u51fa\u4e09\u6307\uff01 \u201c\u5657\uff01\u5657\uff01\u5657\uff01\u201d \u4e09\u58f0\u8fde\u54cd\uff0c\u91cd\u4f24\u4f8b\u5730\u5c1a\u672a\u6c14\u7edd\u7684\u201c\u8840\u638c\u706b\u9f99\u201d\u5468\u8eab\u7acb\u523b\u73b0\u51fa\u4e09\u4e2a\u900f\u660e\u7a9f\u7abf\uff0c\u771f\u6bd4\u5229\u5251\u523a \u7a7f\u7684\u8fd8\u8981\u5927\u4e9b\uff0c\u9c9c\u8840\u809a\u80a0\u7acb\u523b\u6d41\u4e86\u6ee1\u5730\uff01 \u4f17\u4eba\u9f50\u5403\u4e00\u60ca\uff0c\u8fd9\u662f\u4ec0\u4e48\u529f\u592b\uff0c\u7adf\u6709\u8fd9\u822c\u5389\u5bb3\uff1f \u7ad9\u5728\u4e00\u8fb9\u7684\u5a49\u513f\uff0c\u5374\u8131\u53e3\u60ca\u547c\u9053\uff1a\u201c\u641c\u9b42\u6307\u2019\uff01\u201d \u5168\u573a\u90a3\u4e48\u591a\u4eba\uff0c\u53ea\u6709\u5979\u8ba4\u51fa\u767d\u53d1\u5a46\u5a46\u7684\u6307\u529f\uff0c\u56e0\u4e3a\u201c\u795e\u7334\u201d\u4f20\u7ed9\u5979\u7684\u4e5f\u662f\u8fd9\u79cd\u6307\u6cd5\uff0c\u4f46 \u706b\u5019\u5374\u6ca1\u6709\u8fd9\u767d\u53d1\u5a46\u5a46\u6765\u5f97\u6df1\u539a\uff01 \u767d\u53d1\u5a46\u5a46\u5411\u5a49\u513f\u9732\u9f7f\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u5c0f\u4e2b\u5934\u7adf\u80fd\u8ba4\u8bc6\u6211\u8001\u4eba\u5bb6\u7684\u6307\u6cd5\uff01\u2026\u2026\u201d \u5a49\u513f\u542c\u767d\u53d1\u5a46\u5a46\u7ba1\u5979\u53eb\u201c\u5c0f\u4e2b\u5934\u201d\uff0c\u79c0\u76ee\u4e00\u77aa\u3002 \u521a\u8981\u53d1\u4f5c\uff0c\u90a3\u767d\u53d1\u5a46\u5a46\u5374\u8f6c\u5411\u201c\u5b89\u4e50\u516c\u5b50\u201d\u9053\uff1a\u201c\u4f60\u2018\u4e91\u68a6\u5c71\u5e84\u2019\u8be5\u670d\u4e86\u5427\uff1f\u5feb\u628a\u5b9d\u7269 \u4ea4\u51fa\u6765\uff01\u201d \u201c\u5b89\u4e50\u516c\u5b50\u201d\u6ee1\u9762\u5fe7\u5bb9\uff0c\u4f46\u4ecd\u50b2\u7136\u4e00\u7b11\u9053\uff1a\u201c\u5148\u522b\u5fd9\uff0c\u8fd8\u6709\u4e00\u573a\u672a\u5206\u80dc\u8d1f\u54e9\uff01\u201d \u767d\u53d1\u5a46\u5a46\u6389\u5934\u4e00\u770b\uff0c\u6562\u60c5\u4e2d\u5e74\u77ee\u80d6\u548c\u5c1a\u4e0e\u90a3\u201c\u6bd2\u5251\u7075\u86c7\u201d\u6b63\u6253\u4e86\u4e2a\u68cb\u9022\u5bf9\u624b\uff0c\u80dc\u8d25\u96be \u5206\uff01 \u201c\u6bd2\u5251\u7075\u86c7\u201d\u4fde\u5316\u5357\uff0c\u5374\u628a\u4e24\u4ef6\u542b\u6709\u5de8\u6bd2\u7684\u5175\u5668\uff0c\u6325\u821e\u5f97\u5982\u98ce\u8f66\u76f8\u4eff\uff0c\u7740\u7740\u8fdb\u653b\uff01 \u53ea\u89c1\u201c\u94c1\u7ebf\u6bd2\u86c7\u201d\u541e\u3001\u5410\u3001\u76d8\u3001\u7ed5\uff0c\u5982\u957f\u7d22\uff0c\u5982\u8f6f\u97ad\uff0c\u86c7\u4fe1\u5982\u706b\uff0c\u86c7\u76ee\u5982\u7535\uff0c\u6f2b\u7a7a\u800c \u4e0b\uff01 \u4e00\u628a\u201c\u5582\u6bd2\u84dd\u5251\u201d\u66f4\u662f\u821e\u6210\u4e00\u9762\u84dd\u8272\u5149\u5899\uff0c\u70c8\u70c8\u751f\u98ce\uff0c\u6ca1\u5934\u6ca1\u8138\u5730\u5411\u77ee\u80d6\u548c\u5c1a\u5468\u8eab\u7f69 \u6765\uff01 \u767d\u53d1\u5a46\u5a46\u4e00\u76b1\u6b8b\u7709\uff0c\u7528\u90a3\u5e72\u762a\u5f97\u4f3c\u9e2d\u5b50\u53eb\u7684\u58f0\u97f3\u9053\uff1a\u201c\u77ee\u79c3\uff01\u6765\u65f6\u4f60\u5439\u725b\u5439\u5f97\u5c71\u54cd\uff0c\u5230 \u4e86\u73b0\u5728\u8fde\u4e00\u4e2a\u7626\u5c0f\u5b50\u4e5f\u6536\u62fe\u4e0d\u4e86\uff0c\u5c82\u4e0d\u73b0\u8ddf\uff1f\u8d76\u5feb\u65bd\u5c55\u51fa\u538b\u7bb1\u5e95\u7684\u529f\u592b\u6765\uff0c\u628a\u8fd9\u7626\u5c0f\u5b50\u5e9f \u4e86\uff0c\u8001\u5a18\u8fd8\u6709\u8981\u4e8b\uff01\u2026\u2026\u201d \u957f\u9aef\u8001\u4eba\u63a5\u53e3\u9053\uff1a\u7626\u5c0f\u5b50\u4e0d\u582a\u4e00\u51fb\uff01\u8d3c\u5a46\u5a18\uff0c\u4f60\u6ca1\u770b\u5230\u5417\uff1f\u7626\u5c0f\u5b50\u624b\u4e2d\u90a3\u4e24\u4ef6\u5bb6\u4f19\uff0c\u624d \u771f\u662f\u6709\u70b9\u4e0d\u597d\u5bf9\u4ed8\uff01\u2026\u2026\u201d \u767d\u53d1\u5a46\u5a46\u4e00\u77aa\u773c\uff0c\u5ffd\u9053\uff1a\u201c\u5c11\u7f57\u55e6\uff0c\u770b\u8001\u5a18\u628a\u4ed6\u5e9f\u4e86\uff01\u201d \u8bf4\u7f62\uff0c\u633d\u4e86\u633d\u8896\u5b50\uff0c\u5c31\u8981\u4e0a\u524d\u52a8\u624b\u3002\u2026\u2026 \u573a\u5916\u7684\u201c\u4e7e\u5764\u638c\u201d\u4e91\u5b97\u9f99\u6025\u9053\uff1a\u201c\u4e14\u4f4f\uff01\u522b\u5fd8\u4e86\u54b1\u4eec\u4e8b\u524d\u600e\u4e48\u8bf4\u7684\uff01\u8981\u60f3\u4e0d\u9075\u524d\u8a00\uff0c\u4ee5 \u591a\u4e3a\u80dc\uff0c\u773c\u524d\u4e4b\u4eba\uff0c\u8c01\u4e5f\u4e0d\u80fd\u52a8\u624b\uff01\u201d \u767d\u53d1\u5a46\u5a46\u4f3c\u662f\u65e0\u53ef\u5948\u4f55\u5730\u53c8\u505c\u4e0b\u624b\u6765\uff0c\u9887\u5fc3\u6025\u5730\u53eb\u9053\uff1a\u201c\u77ee\u8d3c\u79c3\uff01\u4f60\u5230\u5e95\u80fd\u4e0d\u80fd\u6253\u8d62 \u869c\uff1f\u2026\u2026\u201d \u52a8\u7740\u624b\u7684\u77ee\u80d6\u548c\u5c1a\u9053\uff1a\u201c\u81ed\u8001\u5a46\u5b50\uff01\u4f60\u7740\u7684\u4ec0\u4e48\u6025\u5440\uff1f\u4f5b\u7237\u5305\u7ba1\u9001\u8fd9\u7626\u5c0f\u5b50\u5230\u897f\u5929\u5c31\u662f \u4e86\u2026\u2026\u201d \u5c55\u767d\u6697\u6697\u7eb3\u7f55\uff0c\u770b\u8fd9\u4e09\u4eba\u5206\u660e\u662f\u4e00\u4f19\uff0c\u4f46\u8bf4\u8d77\u8bdd\u6765\u53c8\u662f\u4e92\u76f8\u8c29\u9a82\u7684\u53e3\u543b\uff0c\u4e0d\u77e5\u662f\u4f55\u6765 \u5386\uff1f\u53c8\u89c1\u201c\u4e7e\u5764\u638c\u201d\u8bf4\u4e8b\u524d\u6709\u7ea6\u5b9a\uff0c\u4e5f\u4e0d\u77e5\u662f\u4ec0\u4e48\u7ea6\u5b9a\uff1f\u800c\u4e14\uff0c\u201c\u4e91\u68a6\u5c71\u5e84\u201d\u8fde\u63a5\u4e24\u5927\u9ad8\u624b \u88ab\u6740\u6b7b\uff0c\u4e5f\u65e0\u4eba\u51fa\u624b\u6551\u7f13\uff1f\u56db\u5916\u8fd9\u4e48\u591a\u4eba\u770b\u7740\uff0c\u4e5f\u65e0\u4eba\u51fa\u5934\uff1f\u4e8b\u4e8b\u900f\u7740\u5947\u602a\uff0c\u4e0d\u7531\u62c9\u4e86\u62c9\u8eab \u65c1\u4e00\u4e2a\u52b2\u88c5\u5927\u6c49\u7684\u8863\u8896\u95ee\u9053\uff1a\u201c\u5582\uff01\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d \u90a3\u52b2\u88c5\u5927\u6c49\u4e00\u56de\u5934\uff0c\u89c1\u662f\u5c55\u767d\uff0c\u72e0\u72e0\u5730\u77aa\u4e86\u5c55\u767d\u4e00\u773c\uff0c\u53c8\u89c1\u5c55\u767d\u8eab\u4e0a\u88f9\u7740\u6761\u7834\u68c9\u88ab\uff0c\u6837 \u5b50\u72fc\u72c8\u4e0d\u582a\uff0c\u53c8\u5351\u89c6\u5730\u6487\u4e86\u6487\u5634\uff0c\u7136\u540e\u4e00\u8a00\u4e0d\u53d1\uff0c\u7ade\u81ea\u6389\u8f6c\u5934\u53bb\u6ce8\u89c6\u573a\u4e2d\u4e86\u2026\u2026 \u52b2\u88c5\u5927\u6c49\u56de\u8fc7\u5934\u6765\uff0c\u5c55\u767d\u5df2\u770b\u51fa\u8fd9\u5927\u6c49\u6b63\u662f\u91d1\u5e9c\u7684\u201c\u95f9\u6c5f\u732a\u201d\u6881\u8fd8\uff0c\u60f3\u8d77\u4ed6\u7684\u54e5\u54e5\u201c\u6df7 \u6c5f\u9f99\u201d\u6881\u670b\uff0c\u5728\u71d5\u5b50\u77fe\u8fb9\u88ab\u81ea\u5df1\u4e00\u638c\u51fb\u6bd9\uff0c\u56e0\u6b64\uff0c\u5bf9\u201c\u960e\u6c5f\u732a\u201d\u65e0\u793c\u7684\u4e3e\u52a8\u4e5f\u5c31\u5fcd\u4e0b\u4e86\u3002\u53ef \u662f\uff0c\u5bf9\u5f53\u524d\u7684\u72b6\u51b5\u4f9d\u7136\u662f\u6beb\u65e0\u6240\u77e5\u2026\u2026 \u8fd9\u65f6\uff0c\u201c\u6bd2\u5251\u7075\u86c7\u201d\u4fde\u5316\u5357\uff0c\u62db\u5f0f\u66f4\u89c1\u72e0\u8fa3\uff0c\u6bd2\u5251\u3001\u7075\u86c7\uff0c\u6325\u821e\u5982\u98ce\uff0c\u731b\u51b2\u731b\u6253\uff0c\u4f3c\u4e4e \u5df2\u77e5\u9053\u201c\u4e91\u68a6\u5c71\u5e84\u201d\u7684\u6210\u8d25\uff0c\u4ee5\u53ca\u81ea\u5df1\u4e00\u4e16\u82f1\u540d\uff0c\u5b8c\u5168\u51b3\u5b9a\u5728\u6b64\u4e00\u6218\u4e86\uff01 \u201c\u6bd2\u5251\u7075\u86c7\u201d\u653b\u52bf\u51cc\u5389\uff0c\u77ee\u80d6\u548c\u5c1a\u4f3c\u662f\u5c01\u6321\u4e0d\u4f4f\uff0c\u53cc\u638c\u731b\u7136\u5411\u524d\u4e00\u63a8\uff0c\u4eba\u5374\u5ffd\u7136\u5411\u540e\u4ef0 \u7ffb\u51fa\u4e94\u516d\u6b65\u53bb\uff01 \u4e4d\u89c1\u77ee\u80d6\u548c\u5c1a\u4f3c\u662f\u53d7\u4f24\u8dcc\u5012\uff0c\u201c\u6bd2\u5251\u7075\u86c7\u201d\u6293\u7d27\u5343\u8f7d\u96be\u9022\u7684\u673a\u4f1a\uff0c\u6296\u8d77\u5de6\u624b\u7075\u86c7\uff0c\u5339\u7ec3 \u4f3c\u5730\u5954\u5411\u77ee\u80d6\u548c\u5c1a\u9762\u95e8\u70b9\u53bb\uff0c\u53f3\u624b\u6bd2\u5251\u4ee5\u201c\u6d41\u661f\u8d76\u6708\u201d\u62db\u5f0f\uff0c\u731b\u5411\u77ee\u80d6\u7fd8\u5c1a\u524d\u5fc3\u523a\u4e0b\uff01 \u8fd9\u4e00\u62db\u770b\u6765\u60ca\u9669\u5df2\u6781\uff0c\u56e0\u4e3a\u77ee\u80d6\u548c\u5c1a\u5df2\u7ecf\u4ef0\u9762\u8dcc\u5012\uff0c\u201c\u6bd2\u5251\u7075\u86c7\u201d\u4e24\u62db\u9f50\u51fa\uff0c\u8fc5\u5982\u95ea \u7535\uff0c\u770b\u6765\u77ee\u80d6\u548c\u5c1a\u4e07\u96be\u8eb2\u95ea\u2026\u2026 \u957f\u9aef\u8001\u4eba\u7a81\u7136\u6296\u987b\u7b11\u9053\uff1a\u201c\u79c3\u9a74\uff01\u771f\u6709\u4f60\u7684\uff01\u65e9\u8981\u65bd\u51fa\u8fd9\u4e00\u624b\uff0c\u4e0d\u662f\u65e9\u5c31\u53ef\u4ee5\u8d62\u4e86 \u5417\uff1f\u2026\u2026\u201d \u4f17\u4eba\u624d\u4e00\u6115\u795e\uff0c\u5206\u660e\u77ee\u80d6\u548c\u5c1a\u5373\u5c06\u843d\u8d25\uff0c\u957f\u7559\u8001\u4eba\u4e3a\u4f55\u53cd\u800c\u9ad8\u5174\uff1f\u2026\u2026 \u773c\u89c1\u201c\u94c1\u7ebf\u6bd2\u86c7\u201d\u5f20\u53e3\u5410\u4fe1\uff0c\u5c06\u53ca\u77ee\u80d6\u548c\u5c1a\u9762\u95e8\uff0c\u201c\u5582\u6bd2\u84dd\u5251\u201d\u4e5f\u5c06\u523a\u5230\u77ee\u80d6\u548c\u5c1a\u524d \u80f8\u2026\u2026 \u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u77ee\u80d6\u548c\u5c1a\u7a81\u7136\u7b95\u8e1e\u800c\u8d77\uff0c\u4e24\u817f\u4e0b\u8e72\uff0c\u4e24\u624b\u6276\u5730\uff0c\u8179\u90e8\u9f13\u5f97\u6eda\u5706\uff0c\u5f62\u72b6 \u5982\u4e00\u4e2a\u5927\u9752\u86d9\u4e00\u822c\uff0c\u800c\u4e14\u5634\u4e2d\u201c\u5471\uff01\u5471\uff01\u201d\u602a\u53eb\u4e86\u4e24\u58f0\uff0c\u53cc\u638c\u731b\u7136\u5411\u524d\u6307\u51fa\uff01 \u201c\u8f70\uff01\u8f70\uff01\u201d \u4e24\u80a1\u7edd\u5927\u72c2\u98d9\u5377\u5730\u800c\u8d77\uff0c\u786c\u628a\u575a\u786c\u7684\u5730\u9762\u94f2\u8d77\u4e24\u9053\u6df1\u6c9f\uff0c\u98de\u7802\u626c\u5c18\uff0c\u58f0\u52bf\u60ca\u4eba\uff0c\u524d\u6240\u672a \u89c1\uff01 \u4f17\u4eba\u9f50\u58f0\u60ca\u547c\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\u6b66\u529f\uff1f\u201d \u201c\u6f8e\u201d\u7684\u4e00\u58f0\u5927\u9707\uff0c\u201c\u6bd2\u5251\u7075\u86c7\u201d\u4fde\u5316\u5357\u5df2\u5982\u65ad\u7ebf\u98ce\u7b2c\u4e00\u822c\uff0c\u76f4\u88ab\u9707\u98de\u4e09\u6587\u5f00\u5916\uff0c\u9752 \u5251\u3001\u7075\u86c7\u540c\u65f6\u51fa\u624b\uff0c\u51fa\u53bb\u8001\u8fdc\uff0c\u5c38\u4f53\u201c\u5427\u55d2\u201d\u6389\u843d\u5730\u4e0a\uff0c\u4eba\u5728\u534a\u7a7a\u4e2d\u5c31\u5df2\u4e94\u810f\u79bb\u4f4d\uff0c\u6b7b\u53bb\u591a \u65f6\u4e86\uff01 \u4f17\u4eba\u83ab\u4e0d\u5403\u60ca\uff0c\u77ee\u80d6\u548c\u5c1a\u5374\u5df2\u7f13\u7f13\u7ad9\u8d77\uff0c\u4f8b\u5634\u4e00\u7b11\u9053\uff1a\u201c\u600e\u4e48\u6837\uff1f\u81ed\u8001\u5a46\u5b50\uff0c\u6ca1\u6709\u9519 \u5427\uff01\u201d \u767d\u53d1\u5a46\u5a46\u762a\u5634\u4e00\u7b11\u9053\uff1a\u884c\uff01\u884c\uff01\u4f60\u8fd9\u77ee\u8d3c\u79c3\u8fd8\u7b97\u6709\u70b9\u95e8\u9053\u3002\u201d \u957f\u9aef\u8001\u4eba\u8d85\u6296\u987b\u5411\u201c\u5b89\u4e50\u516c\u5b50\u201d\u7236\u5b50\u8bf4\u9053\uff1a\u201c\u6ca1\u6709\u8bdd\u8bf4\u4e86\u5427\uff1f\u4ece\u73b0\u5728\u5f00\u59cb\u2018\u4e91\u68a6\u5c71\u5e84\u2019 \u7eb3\u4e8e\u2018\u6d77\u5916\u4e09\u715e\u2019\u95e8\u4e0b\uff01\u201d \u201c\u5b89\u4e50\u516c\u5b50\u201d\u671b\u4e86\u671b\u7236\u4eb2\u201c\u4e7e\u5764\u638c\u201d\u4e91\u5b97\u9f99\uff0c\u7236\u5b50\u4e8c\u4eba\u8138\u8272\u5982\u6b7b\uff0c\u65e0\u8a00\u5730\u4f4e\u4e0b\u5934\u53bb\uff0c\u770b \u6837\u5b50\u662f\u60b2\u4f24\u5df2\u6781\u2026\u2026 \u767d\u53d1\u5a46\u5a46\u7a81\u7136\u653e\u9ad8\u4e86\u5589\u5499\uff0c\u5411\u56db\u5468\u7fa4\u96c4\u53eb\u9053\uff1a\u8fd8\u6709\u54ea\u4e00\u5bb6\u4e0d\u670d\uff1f\u5feb\u6765\u62a5\u540d\u9886\u6b7b\uff01\u201d\u767d\u8863 \u4e66\u751f\u624b\u6447\u94f6\u6247\u4ee5\u900d\u9065\u65e0\u6bd4\u7684\u59ff\u6001\u9053\uff1a\u201c\u7aef\u65b9\u516c\u5b50\u2019\u3001\u2018\u7965\u9e9f\u516c\u5b50\u2019\uff0c\u52a0\u4e0a\u521a\u624d\u4eba\u76df\u7684\u2018\u5b89\u4e50 \u516c\u5b50\u2019\uff0c\u4e2d\u539f\u56db\u5927\u8c6a\u95e8\uff0c\u5df2\u6709\u4e09\u5bb6\u4eba\u76df\uff0c\u5c31\u5269\u4e0b\u4f60\u2018\u51cc\u98ce\u516c\u5b50\u2019\u4e86\uff01\u600e\u4e48\u6837\uff1f\u4f60\u4eec\u6d4e\u5357\u2018\u8c79 \u7a81\u5c71\u5e84\u2019\u8fd8\u662f\u5c31\u6b64\u52a0\u76df\u5462\uff1f\u8fd8\u662f\u8981\u627e\u51fa\u51e0\u4e2a\u66ff\u6b7b\u9b3c\u6765\u9001\u6b7b\u5462\uff1f\u201d \u4ee5\u65e0\u60c5\u8457\u540d\u6c5f\u6e56\u7684\u201c\u51cc\u98ce\u516c\u5b50\u201d\uff0c\u7d20\u5e38\u9ad8\u50b2\u7684\u8138\u4e0a\u9634\u6674\u4e0d\u5b9a\uff0c\u8f6c\u773c\u671b\u4e86\u671b\u624b\u4e0b\u7684\u9ad8\u624b\uff01 \u201c\u8c79\u7a81\u5c71\u5e84\u201d\u95e8\u4e0b\u90a3\u4e9b\u5e73\u65e5\u98de\u626c\u62d4\u6248\u7684\u9ad8\u624b\uff0c\u6b64\u65f6\u7ade\u4e00\u4e2a\u4e2a\u4f4e\u4e0b\u5934\u53bb\uff0c\u8fde\u773c\u5149\u90fd\u4e0d\u6562\u4e0e \u201c\u51cc\u98ce\u516c\u5b50\u201d\u76f8\u63a5\uff0c\u770b\u6837\u5b50\u662f\u6015\u88ab\u9009\u4e0a\u81ea\u5df1\u51fa\u9762\u5e94\u654c\u2026\u3002\u2019 \u767d\u8863\u4e66\u751f\u53c8\u52a0\u4e86\u4e00\u65ec\uff0c\u9053\uff1a\u201c\u9f0e\u9f0e\u5927\u540d\u7684\u2018\u51cc\u98ce\u516c\u5b50\u2019\uff0c\u505a\u4e8b\u600e\u4e48\u8fd9\u4e48\u4e0d\u723d\u5feb\uff01\u662f\u4fef\u9996 \u79f0\u81e3\uff1f\u8fd8\u662f\u8981\u8f83\u91cf\u4e2a\u9ad8\u4e0b\uff1f\u8bf4\u4e00\u53e5\u8bdd\u5417\uff01\u201d \u201c\u51cc\u98ce\u516c\u5b50\u201d\u957f\u8fd9\u4e48\u5927\uff0c\u4e5f\u6ca1\u6709\u5f53\u4f17\u53d7\u8fc7\u5982\u6b64\u5351\u89c6\uff0c\u65e0\u5948\u770b\u6837\u5b50\uff0c\u81ea\u5df2\u95e8\u4e0b\u9ad8\u624b\u7ade\u65e0\u4eba \u633a\u8eab\u800c\u51fa\uff0c\u4e3a\u81ea\u5df1\u6323\u9762\u5b50\uff0c\u540c\u65f6\uff0c\u770b\u4e86\u521a\u624d\u5341\u6570\u573a\u640f\u6597\uff0c\u5fc3\u91cc\u4e5f\u660e\u767d\u5c31\u662f\u81ea\u5df1\u95e8\u4e0b\u9ad8\u624b\u51fa \u624b\uff0c\u4e5f\u4e0d\u8fc7\u767d\u767d\u9001\u6b7b\u800c\u5df2\u3002\u4f46\u4e0d\u6218\u800c\u964d\uff0c\u5f53\u7740\u5929\u4e0b\u6b66\u6797\u4e4b\u9762\uff0c\u5b9e\u5728\u4e22\u4eba\u3002\u56e0\u6b64\uff0c\u7adf\u7f9e\u6127\u5f97\u8138 \u5982\u5927\u7ea2\u5e03\u4e00\u822c\uff0c\u4e00\u65f6\u4e4b\u95f4\u65e0\u6cd5\u4e0b\u53f0\u2026\u2026 \u7a81\u7136\uff0c\u4e00\u6761\u4eba\u5f71\u4ece\u5708\u5916\u7535\u5c04\u800c\u8d77\uff0c\u4e34\u7a7a\u4e00\u6298\uff0c\u7fe9\u7136\u843d\u4e8e\u573a\u4e2d\uff0c\u8eab\u6cd5\u4e0d\u4f46\u5feb\uff0c\u800c\u4e14\u7f8e\u5999\u60ca \u4eba\uff0c\u811a\u843d\u5b9e\u5730\u540e\u663e\u51fa\u4e00\u4e2a\u5bbd\u8863\u535a\u5e26\u3001\u98d8\u9038\u51fa\u5c18\u7684\u4e2d\u5e74\u72c2\u751f\uff01 \u53ea\u89c1\u4ed6\u5148\u5411\u201c\u51cc\u98ce\u516c\u5b50\u201d\u4e00\u62b1\u62f3\uff0c\u9053\uff1a\u201c\u8d24\u4e16\u4f84\uff01\u522b\u6765\u65e0\u6148\u4e4e\uff01\u201d \u201c\u51cc\u98ce\u516c\u5b50\u201d\u7a81\u770b\u201c\u5929\u6daf\u72c2\u751f\u201d\u9732\u9762\uff0c\u5fc3\u4e2d\u4e00\u559c\uff0c\u5fd9\u9053\uff1a\u201c\u8d75\u53d4\u53d4\u4e00\u5411\u53ef\u597d\uff1f\u201d \u201c\u5929\u6daf\u72c2\u751f\u201d\u70b9\u4e86\u70b9\u5934\uff0c\u7136\u540e\u50b2\u7136\u5411\u767d\u8863\u4e66\u751f\u9053\uff1a\u201c\u8d75\u67d0\u4e0d\u624d\uff0c\u613f\u610f\u4f1a\u4f1a\u8385\u4e34\u4e2d\u539f\u7684\u6d77 \u5916\u5947\u4eba\uff01\u201d \u767d\u8863\u4e66\u751f\u9053\uff1a\u201c\u9601\u4e0b\u53ef\u662f\u4ee3\u8868\u2018\u8c79\u7a81\u5c71\u5e84\u2019\u5417\uff1f\u90a3\u4e48\uff0c\u518d\u9009\u51fa\u4e8c\u4eba\uff0c\u4f9d\u7136\u6bd4\u8bd5\u4e09\u573a\uff01\u201d \u4e2d\u5e74\u72c2\u751f\u9053\uff1a\u201c\u8d75\u67d0\u4eba\u5c31\u51ed\u2018\u5929\u6daf\u72c2\u751f\u2019\u4e4b\u540d\uff0c\u9886\u6559\u51e0\u624b\u9ad8\u62db\uff0c\u65e2\u4e0d\u4ee3\u8868\u54ea\u4e00\u95e8\uff0c\u4e5f\u4e0d \u4ee3\u8868\u54ea\u4e00\u6d3e\uff01\u201d \u767d\u53d1\u5a46\u5a46\u63d2\u5634\u9053\uff1a\u201c\u72c2\u5c0f\u5b50\uff01\u90a3\u6211\u4eec\u628a\u4f60\u6740\u6b7b\uff0c\u4e0d\u662f\u767d\u8d39\u52b2\u5417\uff1f\u4e00\u70b9\u8d62\u5934\u90fd\u6ca1\u6709\uff01\u201d \u201c\u5929\u6daf\u72c2\u751f\u201d\u8d75\u4e5d\u5dde\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u8001\u5a46\u5a46\uff0c\u77e5\u9053\u4e0d\u77e5\u9053\u2018\u5929\u6daf\u72c2\u751f\u2019\u65e9\u5e74\u7684\u8a93\u8a00\uff1f\u201d \u767d\u53d1\u5a46\u5a46\u9053\uff1a\u201c\u4f60\u5341\u8a00\u4e5d\u8a00\u53c8\u6709\u4ec0\u4e48\u7528\uff0c\u53cd\u6b63\u4f60\u53c8\u4e0d\u80fd\u4ee3\u8868\u8c01\uff0c\u6740\u6b7b\u4f60\u4ee5\u540e\uff0c\u4e00\u4e2a\u8d62\u5934\u4e5f\u5f97 \u4e0d\u5230\uff0c\u603b\u662f\u767d\u8d39\u52b2\u5c31\u662f\u4e86\uff01\u201d \u201c\u5929\u6daf\u72c2\u751f\u201d\u4ee5\u72c2\u50b2\u95fb\u540d\u5929\u4e0b\uff0c\u89c1\u8fd9\u767d\u53d1\u5a46\u5a46\u6bd4\u4ed6\u66f4\u72c2\uff0c\u53cd\u800c\u4e0d\u5ffd\uff0c\u6015\u7136\u4e00\u7b11\u9053\uff1a\u201c\u8d75 \u83dc\u65e9\u5e74\u8a93\u8a00\uff0c\u51e1\u80fd\u80dc\u8fc7\u6211\u8005\uff0c\u8d75\u67d0\u5373\u62dc\u5176\u4e3a\u5e08\u3002\u4f46\u81f3\u4eca\u5345\u5e74\u6765\u5c1a\u672a\u9047\u5230\u8fc7\u654c\u624b\uff0c\u5982\u679c\u4f60\u4eec\u56db \u4f4d\u4e4b\u4e2d\uff0c\u4efb\u4f55\u4e00\u4f4d\u80fd\u80dc\u8fc7\u8d75\u67d0\u4e00\u62db\u4e00\u5f0f\uff0c\u8d75\u67d0\u7acb\u523b\u62dc\u8c01\u4e3a\u5e08\uff0c\u8fd9\u5c82\u4e0d\u4e5f\u662f\u8d62\u5934\u5417\uff01\u201d \u767d\u53d1\u5a46\u5a46\u6840\u6840\u602a\u7b11\u9053\uff1a\u201c\u72c2\u5c0f\u5b50\uff01\u771f\u6709\u4f60\u7684\u3002\u53ef\u662f\u4f60\u5df2\u5fd8\u4e86\u4e00\u4ef6\u4e8b\uff0c\u8981\u662f\u6211\u4eec\u56db\u4eba\u4e4b \u4e2d\uff0c\u4efb\u4f55\u4e00\u4eba\u4e00\u51fa\u624b\uff0c\u4fbf\u628a\u4f60\u6740\u6b7b\u4e86\uff0c\u4f60\u72c2\u5c0f\u5b50\u5230\u9634\u66f9\u5730\u5e9c\uff0c\u53c8\u53bb\u62dc\u8c01\u4e3a\u5e08\u5462\uff1f\u201d \u201c\u5929\u6daf\u72c2\u751f\u201d\u4e00\u6014\uff0c\u60f3\u4e0d\u5230\u81ea\u5df1\u72c2\u50b2\u4e00\u4e16\uff0c\u5982\u4eca\u9047\u5230\u6bd4\u81ea\u5df1\u66f4\u72c2\u5341\u500d\u7684\u4eba\uff01 \u767d\u53d1\u5a46\u5a46\u9053\uff1a\u201c\u72c2\u5c0f\u5b50\uff01\u4f60\u4e0d\u7528\u53d1\u6014\uff01\u4f60\u77e5\u9053\u6211\u4eec\u4e09\u4eba\u662f\u2018\u6d77\u5916\u4e09\u715e\u2019\uff0c\u81f3\u4e8e\u90a3\u4f4d\u5c0f\u7237 \u2014\u2014\u201d \u8bf4\u7740\u6307\u4e86\u6307\u767d\u8863\u94f6\u6247\u4e66\u751f\u3002 \u4f17\u4eba\u9f50\u90fd\u7eb3\u95f7\uff0c\u8fd9\u767d\u53d1\u5a46\u5a46\u5bf9\u8c01\u8bf4\u8bdd\u90fd\u662f\u4e71\u9a82\u4e00\u901a\uff0c\u4e3a\u4f55\u72ec\u5bf9\u8fd9\u767d\u8863\u4e66\u751f\u5982\u6b64\u5c0a \u656c\uff1f\u2026\u2026 \u767d\u53d1\u5a46\u5a46\u63a5\u7740\u9053\uff1a\u201c\u90a3\u4f4d\u5c0f\u7237\u5148\u4e0d\u5fc5\u8bf4\uff0c\u6211\u4eec\u4e09\u4e2a\u8001\u4e0d\u6b7b\u7684\u53eb\u505a\u2018\u6d77\u5916\u4e09\u715e\u2019\uff0c\u51e1\u662f\u4e0e \u6211\u4eec\u2018\u6d77\u5916\u4e09\u715e\u2019\u52a8\u624b\u7684\uff0c\u4ece\u6765\u4e0d\u7559\u6d3b\u547d\uff0c\u72c2\u5c0f\u5b50\uff0c\u73b0\u5728\u4f60\u8be5\u660e\u767d\u4e86\u5427\uff1f\u53ea\u8981\u4e00\u52a8\u624b\uff0c\u4f60\u662f \u5fc5\u6b7b\u65e0\u6d3b\uff0c\u8fd8\u62dc\u7684\u4ec0\u4e48\u5e08\uff1f\u8fd9\u5c82\u4e0d\u662f\u7b49\u4e8e\u767d\u8bf4\uff01\u201d \u201c\u5929\u6daf\u72c2\u751f\u201d\u5927\u6012\uff0c\u518d\u4e5f\u5fcd\u4e0d\u4f4f\uff0c\u9661\u7136\u4e00\u58f0\u5927\u559d\uff0c\u53cd\u81c2\u4e00\u638c\u201c\u62e6\u6c5f\u622a\u6597\u201d\uff0c\u76f4\u5411\u767d\u53d1\u5a46 \u5a46\u62e6\u8170\u626b\u53bb\uff01 \u767d\u53d1\u5a46\u5a46\u4f30\u4e0d\u5230\u4ed6\u7a81\u7136\u51fa\u624b\uff0c\u800c\u201c\u5929\u6daf\u72c2\u751f\u201d\u8fd9\u4e00\u62db\u53c8\u662f\u5feb\u903e\u95ea\u7535\uff0c\u767d\u53d1\u5a46\u5a46\u6765\u4e0d\u53ca\u51fa \u624b\u5e94\u654c\uff0c\u4f46\u8eab\u5f62\u4e00\u65cb\uff0c\u5df2\u8131\u51fa\u201c\u5929\u6daf\u72c2\u751f\u201d\u638c\u98ce\u4e4b\u5916\uff01 \u201c\u5929\u6daf\u72c2\u751f\u201d\u4e0d\u7b49\u767d\u53d1\u5a46\u5a46\u7ad9\u7a33\u201c\u6a2a\u6c5f\u65ad\u6d41\u201d\u3001\u201c\u6d6a\u51fb\u6d41\u6c99\u201d\u4e09\u62db\u8fde\u73af\u65bd\u51fa\uff01 \u767d\u53d1\u5a46\u5a46\u4e0d\u5907\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u88ab\u201c\u5929\u6daf\u72c2\u751f\u201d\u4e00\u8f6e\u5feb\u653b\uff0c\u7adf\u95f9\u4e86\u4e2a\u624b\u5fd9\u811a\u4e71\uff01 \u201c\u5929\u6daf\u72c2\u751f\u201d\u81ea\u521b\u7684\u4e00\u5957\u201c\u8ffd\u98ce\u5947\u5f62\u638c\u201d\uff0c\u4ee5\u5feb\u6377\u5947\u5965\u89c1\u957f\uff0c\u53c8\u88ab\u767d\u53d1\u5a46\u5a46\u6fc0\u6012\uff0c\u6740\u62db \u63a5\u8fde\u800c\u51fa\uff0c\u4e00\u53e3\u6c14\u8fde\u653b\u4e86\u5341\u6570\u62db\uff01 \u53ea\u89c1\u638c\u5f71\u5982\u5c71\uff0c\u638c\u98ce\u5982\u6012\u6d77\u6fc0\u6d6a\uff0c\u58f0\u52bf\u5a01\u731b\uff0c\u524d\u6240\u672a\u89c1\uff01 \u628a\u767d\u53d1\u5a46\u5a46\u903c\u5f97\u4e00\u8def\u75be\u8eb2\uff0c\u8fde\u8fd8\u624b\u7684\u673a\u4f1a\u90fd\u6ca1\u6709\uff0c\u53ef\u4e5f\u4e0d\u7531\u6c14\u5f97\u6ee1\u5934\u767d\u53d1\u5e61\u626c\uff0c\u5389\u5578\u8fde \u8fde\uff01 \u957f\u9aef\u8001\u4eba\u62c8\u987b\u4e0d\u8bed\uff0c\u53cc\u773c\u51fa\u795e\u671b\u5b9a\u201c\u5929\u6daf\u72c2\u751f\u201d\u51fa\u624b\u7684\u62db\u5f0f\uff0c\u8138\u4e0a\u7ade\u73b0\u51fa\u4e00\u4e1d\u656c\u4f69\u4e4b \u8272\u2026\u2026 \u767d\u8863\u4e66\u751f\u624b\u4e2d\u94f6\u6247\u5728\u638c\u5fc3\u8f7b\u6572\uff0c\u8138\u4e0a\u4e5f\u6d41\u9732\u4e00\u4e1d\u8be7\u5f02\u795e\u8272\uff0e\u2026\uff0e\u2019 \u53ea\u6709\u90a3\u77ee\u80d6\u548c\u5c1a\uff0c\u4f8b\u7740\u4e00\u5f20\u9614\u5634\uff0c\u5728\u4e00\u65c1\u62cd\u624b\u7b11\u9053\uff1a\u201c\u8001\u4e5e\u5a46\uff01\u8fd9\u4e00\u4e0b\u5b50\u5927\u8bdd\u5439\u7834\u4e86\uff0c \u6d3b\u8be5\u8981\u683d\u8ddf\u5934\uff01\u201d \u767d\u53d1\u5a46\u5a46\u4e00\u8fb9\u8eab\u5f62\u7535\u65cb\u661f\u98de\uff0c\u8eb2\u95ea\u201c\u5929\u6daf\u72c2\u751f\u201d\u4e00\u8f6e\u75be\u653b\uff0c\u8fb9\u5634\u4e2d\u9a82\u9053\uff1a\u201c\u597d\u8d3c\u79c3\uff01\u8001 \u5a18\u5403\u7d27\uff0c\u4f60\u4f8b\u5728\u4e00\u65c1\u770b\u70ed\u95f9\u2026\u2026\u201d \u201c\u5929\u6daf\u72c2\u751f\u201d\u8d75\u4e5d\u5dde\u6068\u8fd9\u767d\u53d1\u5a46\u5a46\u72c2\u50b2\uff0c\u6210\u5fc3\u8981\u628a\u767d\u53d1\u5a46\u5a46\u6298\u8fb1\u5728\u638c\u4e0b\uff0c\u4e00\u8def\u62a2\u5c3d\u5148\u673a \u4e4b\u4e2d\uff0c\u4ecd\u7136\u731b\u65bd\u6740\u624b\uff0c\u201c\u7ffb\u6c5f\u5012\u6d77\u201d\u3001\u201c\u8d74\u6c64\u8e48\u706b\u201d\u3001\u201c\u66b4\u96e8\u72c2\u98ce\u201d\uff0c\u63a5\u8fde\u53c8\u65bd\u5c55\u4e09\u5927\u6740 \u62db\uff01 \u638c\u5f71\u5f25\u5929\u76d6\u65e5\u800c\u4e0b\uff0c\u628a\u767d\u53d1\u5a46\u5a46\u6574\u4e2a\u7f69\u5728\u4e00\u7247\u7f61\u98ce\u9510\u5578\u7684\u638c\u5f71\u4e4b\u4e2d\uff01\u2026\u2026 \u957f\u9aef\u8001\u4eba\u9762\u9732\u60ca\u5bb9\uff01 \u8fde\u77ee\u80d6\u548c\u5c1a\u4e5f\u4e0d\u53d6\u7b11\u4e86\uff0c\u80a5\u80d6\u7684\u8138\u4e0a\u4e5f\u73b0\u51fa\u8be7\u5f02\u4e4b\u8272\uff01 \u663e\u89c1\u4ed6\u4eec\u4e8c\u4eba\u4e5f\u4f30\u4e0d\u5230\u4e2d\u539f\u6b66\u6797\uff0c\u8fd8\u6709\u8fd9\u7b49\u5947\u5965\u6b66\u529f\uff0c\u4f46\u4e8c\u4eba\u7adf\u7d27\u5b88\u6b66\u6797\u89c4\u77e9\uff0c\u767d\u53d1\u5a46 \u5a46\u518d\u5371\u6025\u4e5f\u4e0d\u80af\u51fa\u624b\u76f8\u52a9\u2026\u2026 \u767d\u53d1\u5a46\u5a46\u62a2\u4e0d\u56de\u5148\u673a\uff0c\u5371\u9669\u4e07\u72b6\u2026\u2026 \u90a3\u767d\u8863\u4e66\u751f\uff0c\u7a81\u7136\u4ee5\u94f6\u6247\u5728\u638c\u4e2d\u51fb\u8282\uff0c\u9ad8\u58f0\u541f\u9053\uff1a\u201c\u5c71\u7a77\u6c34\u5c3d\u7591\u65e0\u8def\u2026\u2026\u201d \u90a3\u767d\u53d1\u5a46\u5a46\u542c\u5230\u541f\u58f0\uff0c\u7a81\u7136\u4e00\u58f0\u5389\u5578\uff0c\u76f4\u53ef\u7a7f\u77f3\u88c2\u4e91\uff01 \u5389\u5578\u58f0\u4e2d\uff0c\u90a3\u767d\u53d1\u5a46\u5a46\u8eab\u5f62\u5982\u86c7\u87ba\u822c\u4e00\u9635\u6025\u65cb\uff0c\u7ade\u4ece\u201c\u5929\u6daf\u72c2\u751f\u201d\u90a3\u5f25\u5929\u76d6\u65e5\u7684\u638c\u5f71 \u4e2d\uff0c\u7a7f\u7a7a\u76f4\u4e0a\uff01 \u4e00\u6761\u7070\u767d\u8272\u8eab\u5f71\uff0c\u95ea\u7535\u822c\u5347\u8d77\uff0c\u76f4\u7ebf\u4e0a\u5347\u5dee\u4e0d\u591a\u6709\u56db\u4e08\u9ad8\uff0c\u534a\u7a7a\u4e2d\u53c8\u4f20\u51fa\u767d\u53d1\u5a46\u5a46\u7684\u4e00 \u58f0\u5389\u5578\uff0c\u5578\u58f0\u6447\u66f3\u4e4b\u4e2d\uff0c\u767d\u53d1\u5a46\u5a46\u5019\u7136\u6298\u8eab\u4e0b\u6251\uff0c\u5934\u4e0b\u811a\u4e0a\uff0c\u53cc\u638c\u5288\u7a7a\u9510\u5578\u800c\u4e0b\uff0c\u5982\u6cf0\u5c71\u538b \u9876\uff0c\u731b\u5411\u201c\u5929\u6daf\u72c2\u751f\u201d\u5f53\u9876\u5288\u6765\uff01 \u201c\u5929\u6daf\u72c2\u751f\u201d\u4ee5\u72c2\u51fa\u540d\uff0c\u867d\u89c1\u767d\u53d1\u5a46\u5a46\u638c\u52bf\u60ca\u4eba\uff0c\u4ecd\u7136\u4e0d\u8eb2\uff0c\u201c\u5929\u738b\u6258\u5854\u201d\uff0c\u8fd0\u8d77\u5468\u8eab \u529f\u529b\u731b\u5411\u6765\u52bf\u8fce\u53bb\uff01 \u201c\u8f70\u9686\u201d\u4e00\u58f0\u66b4\u54cd\uff0c\u5c71\u6447\u5730\u52a8\uff0c\u7fa4\u5cf0\u54c4\u9e23\uff01 \u72c2\u98d9\u6012\u5377\uff0c\u6298\u8349\u98de\u6284\uff0c\u8fde\u6570\u5341\u6587\u5916\u7684\u677e\u6811\u53f6\u5b50\u90fd\u88ab\u9707\u5f97\u5982\u843d\u96e8\u4e00\u822c\u54d7\u54d7\u6d12\u4e0b\uff01 \u4e8c\u4eba\u638c\u529b\u4e4b\u5927\uff0c\u5b9e\u5728\u60ca\u5fc3\u52a8\u9b44\uff01 \u56db\u5468\u56f4\u89c2\u7684\u7fa4\u96c4\uff0c\u6709\u4e0d\u5c11\u4eba\u7ad9\u4e0d\u4f4f\u811a\uff0c\u60ca\u547c\u540e\u9000\uff01\u2026\u2026 \u201c\u5929\u6daf\u72c2\u751f\u201d\u867d\u7136\u806a\u6167\u903e\u4eba\uff0c\u4f46\u5230\u5e95\u5185\u529f\u771f\u529b\u900a\u4e86\u767d\u53d1\u5a46\u5a46\u4e00\u7b79\uff0c\u800c\u4e14\u767d\u53d1\u5a46\u5a46\u5c45\u9ad8\u4e34 \u4e0b\uff0c\u65e0\u5f62\u4e2d\u5360\u4e86\u4fbf\u5b9c\uff0c\u56e0\u6b64\uff0c\u8fd9\u4e00\u638c\u7ade\u628a\u2018\u2018\u5929\u6daf\u72c2\u751f\u201d\u8d75\u4e5d\u5dde\u9707\u5f97\u8ddf\u8dea\u9000\u51fa\u4e94\u516d\u6b65\uff0c\u9762\u8272 \u60e8\u767d\uff0c\u6447\u6447\u6b32\u5012\uff01 \u767d\u53d1\u5a46\u5a46\u8eab\u5f62\u843d\u5730\uff0c\u6012\u5bb9\u6ee1\u9762\uff0c\u7f13\u6b65\u5411\u201c\u5929\u6daf\u72c2\u751f\u201d\u903c\u8fd1\uff0c\u6162\u6162\u62ac\u8d77\u624b\u6765\uff0c\u4e2d\u6307\u5916\u5410\uff0c \u8fd0\u8d77\u4e86\u795e\u9b3c\u7686\u60ca\u7684\u201c\u641c\u9b42\u6307\u201d\u795e\u529f\uff01\u6076\u72e0\u72e0\u5411\u201c\u5929\u6daf\u72c2\u751f\u201d\u524d\u80f8\u201c\u4e09\u9633\u201d\u91cd\u7a74\u63d2\u4e0b\u2026\u2026 \u7b2c\u56db\u5341\u7ae0\u3000\u518d\u5760\u9669\u5883>> \u53e4\u9f99\u300a\u5251\u5ba2\u884c\u300b \u7b2c\u56db\u5341\u7ae0\u3000\u518d\u5760\u9669\u5883 \u201c\u4f4f\u624b\uff01\u201d \u5c55\u767d\u66b4\u559d\u4e00\u58f0\uff0c\u98de\u8eab\u63a0\u5165\u573a\u4e2d\u3002 \u767d\u53d1\u5a46\u5a46\u5fae\u4e00\u695e\u795e\uff0c\u8f6c\u5934\u89c1\u662f\u4e00\u4e2a\u8eab\u88f9\u7834\u68c9\u88ab\u7684\u4fca\u7f8e\u5c11\u4e2d\uff0c\u4e0d\u7531\u54a7\u5634\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5c0f\u5a03 \u5b50\uff01\u662f\u4e0d\u662f\u4e5f\u6d3b\u5f97\u4e0d\u8010\u70e6\u4e86\uff1f\u7adf\u6562\u51fa\u9762\u4e0e\u6211\u8001\u4eba\u5bb6\u67b6\u6881\u5b50\uff01\u201d \u5c55\u767d\u9053\uff1a\u201c\u52a8\u624b\u6bd4\u6b66\uff0c\u80dc\u8005\u4e3a\u96c4\uff01\u8001\u5a46\u5a46\u8fd9\u4e48\u5927\u5e74\u7eaa\uff0c\u4e0d\u77e5\u4e3a\u4f55\u8fd8\u662f\u8fd9\u4e48\u5927\u7684\u706b\u6c14\uff0c\u6218 \u80dc\u4e5f\u5c31\u662f\u4e86\uff0c\u4f55\u5fc5\u4e00\u5b9a\u8981\u81f4\u4eba\u4e8e\u6b7b\u5730\uff1f\u201d \u767d\u53d1\u5a46\u5a46\u4e00\u77aa\u773c\uff0c\u545b\u9053\uff1a\u201c\u4e73\u81ed\u5c0f\u5b50\uff01\u4f60\u53ef\u77e5\u2018\u6d77\u5916\u4e09\u715e\u2019\u89c4\u77e9\uff1f\u201d \u5c55\u767d\u9053\uff1a\u201c\u5728\u4e0b\u4e0d\u77e5\uff01\u201d \u767d\u53d1\u5a46\u5a46\u8fd0\u81f3\u98a0\u5cf0\u7684\u201c\u641c\u9b42\u6307\u201d\u529f\u672a\u64a4\uff0c\u529b\u8d2f\u56db\u7a0d\uff0c\u5468\u8eab\u5fae\u5fae\u98a4\u6296\uff0c\u5934\u4e0a\u7684\u767d\u53d1\u53ca\u8eab\u4e0a \u7684\u8863\u886b\uff0c\u66f4\u662f\u65e0\u98ce\u800c\u52a8\uff0c\u524d\u4f38\u7684\u624b\u6307\uff0c\u80c0\u5927\u8db3\u6709\u4e00\u500d\u6709\u4f59\uff0c\u5ffd\u89c6\u7740\u5c55\u767d\u6840\u6840\u7b11\u9053\uff1a\u201c\u51e1\u4e0e \u2018\u6d77\u5916\u4e09\u715e\u2019\u52a8\u624b\u4e4b\u4eba\uff0c\u4ece\u6765\u4e0d\u7559\u6d3b\u547d\uff0c\u73b0\u5728\u4f60\u8be5\u77e5\u9053\u4e86\u5427\uff1f\u8fd8\u4e0d\u66ff\u6211\u5feb\u6eda\uff01\u201d \u5c55\u767d\u4e5f\u662f\u5929\u751f\u50b2\u9aa8\u4e4b\u4eba\uff0c\u89c1\u8fd9\u767d\u53d1\u5a46\u5a46\u5ffd\u559c\u5ffd\u6012\uff0c\u8bf4\u8bdd\u7684\u53e3\u6c14\uff0c\u66f4\u662f\u5484\u5484\u903c\u4eba\uff0c\u4e0d\u7531\u6fc0 \u8d77\u6012\u706b\uff0c\u5251\u7709\u4e00\u626c\u9053\uff1a\u201c\u4e0d\u7ba1\u4f60\u662f\u8c01\uff0c\u4e5f\u4e0d\u80fd\u4e0d\u987e\u6c5f\u6e56\u89c4\u77e9\uff0c\u5bf9\u4e00\u4e2a\u8d1f\u4f24\u4e4b\u4eba\uff0c\u4ecd\u7136\u65bd\u5c55\u8fa3 \u624b\uff01\u201d \u767d\u53d1\u5a46\u5a46\u6012\u6781\u53cd\u7b11\uff0c\u563f\u7136\u8bf4\u9053\uff1a\u201c\u8fd9\u6837\u8bf4\u6765\uff0c\u4f60\u662f\u6210\u5fc3\u60f3\u6b7b\u4e86\uff0c\u2019 \u8bf4\u7740\uff0c\u820d\u4e0b\u201c\u5929\u6daf\u72c2\u751f\u201d\uff0c\u8fd0\u6307\u5411\u5c55\u767d\u903c\u6765\uff01 \u5c55\u767d\u89c1\u90a3\u767d\u53d1\u5a46\u5a46\u9762\u76ee\u9634\u68ee\uff0c\u4e3e\u6307\u5411\u81ea\u5df1\u7f13\u7f13\u5b9a\u8fd1\uff0c\u6307\u950b\u672a\u51fa\uff0c\u5148\u611f\u4e00\u822c\u903c\u9762\u751f\u5bd2\u7684\u52b2 \u6d41\u6c79\u6d8c\u800c\u81f3\uff0c\u77e5\u9053\u767d\u53d1\u5a46\u5a46\u7684\u6307\u529f\u5389\u5bb3\uff0c\u6697\u6697\u8fd0\u8d77\u201c\u96f7\u97f3\u4f5b\u638c\u201d\u795e\u529f\u6212\u5907\uff01\u2026\u2026 \u6b64\u65f6\uff0c\u8eab\u540e\u8d1f\u4f24\u7684\u201c\u5929\u6daf\u72c2\u751f\u201d\u7ecf\u8fc7\u4e00\u756a\u8c03\u606f\uff0c\u5df2\u7f13\u8fc7\u4e00\u53e3\u6c14\u6765\uff0c\u89c1\u5371\u96be\u4e2d\u51fa\u9762\u6551\u52a9\u81ea \u5df1\u7684\uff0c\u7adf\u662f\u66fe\u8d25\u5728\u81ea\u5df1\u638c\u4e0b\u7684\u5c11\u5e74\u5c55\u767d\uff0c\u5fc3\u4e2d\u65e2\u611f\u4e14\u6127\uff0c\u53c8\u660e\u77e5\u5c55\u767d\u4e0d\u662f\u767d\u53d1\u5a46\u5a46\u7684\u5bf9\u624b\uff0c \u968f\u5728\u540e\u8fb9\u53eb\u9053\uff1a\u201c\u5c55\u5c11\u4fa0\uff01\u5feb\u9000\uff01\u5feb\u9000\uff01\u5f85\u8d75\u67d0\u4eba\u518d\u63a5\u5979\u51e0\u62db\uff01\u201d \u8bf4\u7740\u62a2\u524d\u4e00\u6b65\uff0c\u6c47\u96c6\u6b8b\u4f59\u771f\u529b\uff0c\u731b\u7136\u5288\u51fa\u4e00\u638c\uff01 \u767d\u53d1\u5a46\u5a46\u6840\u6840\u602a\u7b11\uff0c\u628a\u6307\u5411\u5c55\u767d\u7684\u6307\u950b\uff0c\u53cd\u4fa7\u4e00\u5212\uff0c\u4e00\u822c\u75be\u5578\u7684\u9510\u98de\uff0c\u731b\u5411\u201c\u5929\u6daf\u72c2 \u751f\u201d\u626b\u53bb\uff01 \u5c55\u767d\u89c1\u767d\u53d1\u5a46\u5a46\u4ecd\u53bb\u4f24\u201c\u5929\u6daf\u72c2\u751f\u201d\uff0c\u9661\u7136\u5927\u559d\u4e00\u58f0\uff0c\u628a\u8fd0\u81f3\u9876\u5cf0\u7684\u201c\u96f7\u97f3\u4f5b\u638c\u201d\u65bd \u51fa\uff0c\u75be\u5411\u767d\u53d1\u5a46\u5a46\u653b\u53bb\uff01 \u201c\u96f7\u97f3\u4f5b\u638c\u201d\u4e43\u897f\u57ce\u7edd\u5b66\uff0c\u65bd\u5c55\u51fa\u6765\uff0c\u867d\u4e0d\u89c1\u638c\u98ce\u72c2\u5578\uff0c\u4f46\u4e00\u822c\u5947\u5927\u65e0\u6bd4\u7684\u6697\u52b2\uff0c\u5982\u6d77 \u6d0b\u5de8\u6d41\u4e00\u822c\u6c79\u6d8c\u800c\u81f3\uff01\u767d\u53d1\u5a46\u5a46\u201c\u641c\u9b42\u6307\u201d\u795e\u529f\u672c\u5df2\u8f6c\u5411\u201c\u5929\u6daf\u72c2\u751f\u201d\uff0c\u5bf9\u201c\u5929\u6daf\u72c2\u751f\u201d\u4f24 \u540e\u62fc\u529b\u6253\u51fa\u4e00\u638c\uff0c\u5979\u4e95\u672b\u653e\u5728\u5fc3\u4e0a\uff0c\u4f46\u5c55\u767d\u638c\u51fa\uff0c\u5979\u7acb\u523b\u611f\u5230\u4e0d\u5bf9\u52b2\uff0c\u4f46\u4e00\u65f6\u4e4b\u95ee\u8fd8\u672a\u60f3 \u5230\uff0c\u4ee5\u773c\u524d\u4e00\u4e2a\u4e0d\u8d77\u773c\u7684\u5c11\u5e74\uff0c\u4f1a\u6709\u4ec0\u4e48\u4e86\u4e0d\u8d77\u7684\u6b66\u529f\uff0c\u56e0\u6b64\u201c\u641c\u9b42\u6307\u201d\u4ecd\u7136\u5411\u201c\u5929\u6daf\u72c2 \u751f\u201d\u70b9\u53bb\u2026\u2026 \u7ad9\u5728\u53e6\u4e00\u4fa7\u7684\u4e2d\u5e74\u548c\u5c1a\uff0c\u5374\u8131\u53e3\u53eb\u9053\uff1a\u201c\u8001\u5a46\u5a46\uff01\u5c0f\u5fc3\uff01\u90a3\u5c11\u5e74\u65bd\u51fa\u7684\u662f\u2018\u96f7\u97f3\u4f5b \u638c\u2019\uff01\u201d \u767d\u53d1\u5a46\u5a46\u95fb\u58f0\u4e00\u60ca\uff0c\u201c\u641c\u9b42\u6307\u201d\u5728\u534a\u7a7a\u4e2d\u5212\u4e86\u4e00\u4e2a\u534a\u5706\uff0c\u5374\u65bd\u51fa\u4e00\u62db\u201c\u6a2a\u626b\u4e94\u5cb3\u201d\u7684\u62db \u5f0f\uff0c\u6307\u98ce\u72c2\u5578\uff0c\u628a\u201c\u5929\u6daf\u72c2\u751f\u201d\u53ca\u5c55\u767d\u4e8c\u4eba\u5747\u7f69\u5728\u5f3a\u52b2\u7684\u201c\u641c\u9b42\u6307\u201d\u4e43 \u53ea\u542c\u201c\u7830\uff01\u8f70\uff01\u201d\u63a5\u8fde\u4e24\u58f0\u5de8\u54cd\uff0c\u201c\u5929\u6daf\u72c2\u751f\u201d\u75db\u5578\u4e00\u58f0\uff0c\u4ef0\u9762\u671d\u4e8e\u683d\u5012\uff01 \u767d\u53d1\u5a46\u5a46\u4e00\u58f0\u95f7\u54fc\uff0c\u7adf\u88ab\u5c55\u81ea\u201c\u96f7\u97f3\u4f5b\u638c\u201d\u9707\u5f97\u5411\u659c\u523a\u91cc\u8ddf\u8def\u4e94\u516d\u6b65\u51fa\u53bb\uff01\u201c\u2026\u2019 \u5c55\u767d\u53ea\u611f\u638c\u5fc3\u5982\u88ab\u94bb\u7a7f\uff0c\u4e00\u9635\u5de8\u75db\uff0c\u51e0\u4e4e\u4f7f\u4ed6\u7ad9\u8db3\u4e0d\u7a33\uff0c\u4e0a\u8eab\u6643\u4e86\u4e24\u6643\uff0c\u4ecd\u7136\u54ac\u7259\u5fcd \u4f50\uff0c\u4e00\u770b\u638c\u5fc3\u5374\u5df2\u80bf\u8d77\u5982\u6843\uff0c\u5fc3\u4e2d\u6697\u60ca\uff1a\u201c\u597d\u5389\u5bb3\u7684\u2018\u641c\u9b42\u6307\u2019\uff01\u201d \u4e2d\u5e74\u77ee\u80d6\u548c\u5c1a\u5374\u4e00\u63a0\u800c\u524d\uff0c\u5411\u5c55\u81ea\u5389\u58f0\u95ee\u9053\uff1a\u201c\u5c14\u662f\u4f55\u4eba\u95e8\u4e0b\uff1f\u2026\u2026\u201d \u767d\u53d1\u5a46\u5a46\u88ab\u5c55\u767d\u4e00\u638c\u9707\u9000\uff0c\u5f15\u4e3a\u5947\u803b\uff0c\u66b4\u6012\u5982\u96f7\uff0c\u53cd\u8eab\u53c8\u5411\u5c55\u767d\u6251\u6765\uff01 \u5c55\u767d\u4e3e\u638c\u6b32\u8fce\uff0c\u5374\u611f\u638c\u5fc3\u5de8\u75db\u5982\u952f\uff0c\u4e3e\u624b\u4e4f\u529b\uff0c\u4e0d\u7531\u6697\u9053\uff1a\u201c\u5b8c\u4e86\uff01\u2026\u2026\u201d \u5ffd\u542c\u4e24\u58f0\u5a07\u53f1\uff0c\u4e24\u6761\u5a07\u5c0f\u4eba\u5f71\uff0c\u72b9\u5982\u53cc\u98de\u7d2b\u71d5\uff0c\u4e00\u9f50\u63a0\u5165\u573a\u4e2d\uff01 \u63a5\u7740\u4e00\u6e9c\u78a7\u5149\u4e00\u95ea\uff0c\u4e00\u80a1\u52b2\u98ce\u9510\u5578\uff0c\u9f50\u5411\u767d\u53d1\u5a46\u5a46\u653b\u81f3\uff01 \u767d\u53d1\u5a46\u5a46\u8eab\u5f62\u7535\u65cb\uff0c\u8eb2\u8fc7\u4e00\u5251\u4e00\u638c\uff0c\u8eab\u5f62\u98d8\u843d\u4e00\u4e08\u5f00\u5916\uff0c\u626d\u5934\u4e00\u770b\uff0c\u6765\u4eba\u5374\u662f\u4e00\u4e2a\u5947\u7f8e \u7684\u9526\u8863\u5c11\u5973\u4e0e\u4e00\u9752\u8138\u7ea2\u53d1\u7684\u602a\u4eba\uff01 \u8fd9\u9526\u8863\u5c11\u5973\u4e0e\u9752\u8138\u7ea2\u53d1\u602a\u4eba\u6b63\u662f\u5c55\u5a49\u513f\u4e0e\u67f3\u7fe0\u7fe0\uff0c\u4e3a\u6551\u5c55\u767d\uff0c\u540c\u65f6\u51fa\u624b\uff01 \u767d\u53d1\u5a46\u5a46\u88ab\u4e8c\u4eba\u903c\u9000\uff0c\u672a\u80fd\u4f24\u5230\u5c55\u81ea\uff0c\u66f4\u5f62\u6fc0\u6012\uff0c\u8eab\u5f62\u7a9c\u843d\u5373\u8d77\uff0c\u817e\u8eab\u53c8\u5411\u4e8c\u5973\u6251 \u6765\uff01\u2026\u2026 \u94f6\u6247\u767d\u8863\u4e66\u751f\u5374\u5728\u4e00\u65c1\u6025\u53eb\u9053\uff1a\u201c\u7ea2\u59d1\uff01\u90a3\u9752\u8138\u7ea2\u53d1\u4e4b\u4eba\uff0c\u8981\u6d3b\u4e0d\u8981\u6b7b\uff0c\u5176\u4f59\u4e4b\u4eba\uff0c\u53ef \u4ee5\u683c\u6740\u52ff\u8bba\uff01\u2026\u2026\u201d \u767d\u53d1\u5a46\u5a46\u867d\u5728\u6fc0\u6012\u4e4b\u4e0b\uff0c\u4ecd\u7136\u542c\u767d\u8863\u4e66\u751f\u7684\u8bdd\uff0c\u534a\u7a7a\u4e2d\u201c\u641c\u9b42\u6307\u201d\u795e\u529f\u70b9\u5411\u5a49\u513f\uff0c\u5bf9\u67f3 \u7fe0\u7fe0\u7684\u4e00\u6307\uff0c\u5374\u53d8\u6307\u4e3a\u6293\uff0c\u4e94\u6307\u7b95\u5f20\uff0c\u731b\u7136\u6293\u4e0b\uff01 \u5a49\u513f\u8eab\u5f62\u4e00\u632b\uff0c\u4e24\u6307\u75be\u51fa\uff0c\u540c\u6837\u662f\u201c\u641c\u9b42\u6307\u201d\uff0c\u731b\u70b9\u767d\u53d1\u5a46\u5a46\u201c\u5fc3\u4fde\u201d\u3001\u201c\u6c14\u6d77\u201d\u4e24\u5927 \u91cd\u7a74\uff01 \u540c\u65f6\uff0c\u67f3\u7fe0\u7fe0\u4e00\u62db\u201c\u659c\u6708\u751f\u8f89\u201d\uff0c\u65e0\u60c5\u78a7\u5251\u95ea\u8d77\u4e00\u7247\u78a7\u8292\uff0c\u731b\u5411\u767d\u53d1\u5a46\u5a46\u53f3\u81c2\u524a\u53bb\uff01 \u767d\u53d1\u5a46\u5a46\u5fc3\u4e2d\u4e00\u61d4\uff0c\u4f30\u4e0d\u5230\u5a49\u513f\u4e0e\u7fe0\u7fe0\u7adf\u6709\u8fd9\u6837\u9ad8\u5f3a\u7684\u5251\u62db\u4e0e\u6307\u529f\uff0c\u5c24\u5176\u5a49\u513f\u7684\u6307\u6cd5\uff0c \u7adf\u4e0e\u81ea\u5df1\u7684\u201c\u641c\u9b42\u6307\u201d\u76f8\u4f3c\uff0c\u534a\u7a7a\u6298\u8170\uff0c\u98d8\u843d\u4e00\u6587\u5f00\u5916\uff0c\u5389\u58f0\u53f1\u9053\uff1a\u201c\u5c0f\u59ae\u5b50\uff01\u4f60\u8fd9\u6307\u6cd5\u8ddf \u8c01\u5b66\u7684\uff1f\u201d \u5a49\u513f\u9053\uff1a\u201c\u8fd9\u4e2a\u4f60\u7ba1\u4e0d\u7740\uff0c\u4f60\u53ea\u8981\u4f24\u5230\u5c55\u54e5\u54e5\u4e00\u6839\u6beb\u6bdb\uff0c\u6211\u4fbf\u62ff\u4f60\u507f\u547d\uff01\u201d \u767d\u53d1\u5a46\u5a46\u54a7\u5634\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u597d\uff01\u6211\u7d20\u6765\u4e0d\u8bb2\u7406\uff0c\u6ca1\u60f3\u5230\u4eca\u5929\u78b0\u5230\u4e00\u4e2a\u6bd4\u6211\u66f4\u4e0d\u8bb2\u7406\u7684 \u4eba\uff01\u6211\u95ee\u4f60\uff0c\u5c0f\u59d1\u5a18\uff0c\u8fd9\u5c11\u5e74\u662f\u4f60\u4ec0\u4e48\u4eba\uff0c\u503c\u5f97\u4f60\u8fd9\u4e48\u5173\u5fc3\u4ed6\uff1f\u2026\u2026\u201d\u5a49\u513f\u5c1a\u672a\u7b54\u8a00\uff0c\u67f3\u7fe0 \u7fe0\u5374\u770b\u5230\u5c55\u767d\u638c\u5fc3\u7ea2\u80bf\uff0c\u8138\u8272\u60e8\u767d\uff0c\u5fc3\u75db\u5fc3\u4e0a\u4eba\u8d1f\u4f24\uff0c\u6012\u53f1\u4e00\u58f0\uff0c\u6296\u5251\u5411\u767d\u53d1\u5a46\u5a46\u5206\u5fc3\u523a \u53bb\uff01 \u5251\u51fa\u5578\u98ce\uff0c\u5251\u5c16\u5bd2\u8292\u7adf\u7136\u95ea\u8d77\u4e00\u7247\u8000\u773c\u78a7\u5149\uff0c\u663e\u7136\u67f3\u7fe0\u7fe0\u7684\u5251\u672f\u4fee\u517b\u5df2\u5230\u4e86\u201c\u4ee5\u6c14\u5fa1 \u5251\u201d\u7684\u5730\u6b65\uff0c\u5251\u62db\u65bd\u51fa\uff0c\u58f0\u52bf\u60ca\u4eba\uff01 \u767d\u53d1\u5a46\u5a46\u5927\u60ca\u5931\u8272\uff0c\u6536\u8eab\u6025\u8eb2\uff0c\u4ecd\u7136\u6162\u4e86\u4e00\u6b65\uff0c\u201c\u5403\u201d\u7684\u4e00\u58f0\u5fae\u9e23\uff0c\u767d\u53d1\u5a46\u5a46\u7684\u534a\u622a\u8863 \u8896\u5df2\u88ab\u5251\u8292\u524a\u4e0b\uff01 \u5a49\u513f\u4e5f\u770b\u51fa\u5c55\u767d\u8d1f\u4f24\u7684\u60c5\u5f62\u6765\uff0c\u5728\u767d\u53d1\u5a46\u5a46\u7ad9\u811a\u672a\u7a33\u4e4b\u9645\uff0c\u731b\u7136\u5f39\u51fa\u4e24\u6307\uff0c\u76f4\u5411\u767d\u53d1\u5a46 \u5a46\u88ad\u81f3\uff01 \u540c\u65f6\uff0c\u67f3\u7fe0\u7fe0\u201c\u5bd2\u661f\u5954\u6708\u201d\uff0c\u624b\u4e2d\u201c\u65e0\u60c5\u78a7\u5251\u201d\u5982\u4e00\u9053\u64ce\u5929\u957f\u8679\uff0c\u51e0\u81f3\u201c\u4eba\u5251\u5408\u4e00\u201d\u7684 \u5251\u672f\u5316\u5883\uff0c\u4e0d\u89c1\u4eba\u5f71\uff0c\u53ea\u89c1\u5251\u5149\uff0c\u5411\u767d\u53d1\u5a46\u5a46\u7535\u5c04\u800c\u81f3\uff01 \u767d\u53d1\u5a46\u5a46\u6b64\u65f6\u72c2\u6001\u5168\u5931\uff0c\u53cd\u800c\u60ca\u5f97\u9762\u65e0\u4eba\u8272\uff0c\u5979\u600e\u4e48\u4e5f\u4f30\u4e0d\u5230\u5f53\u4eca\u4e4b\u4e16\u7adf\u6709\u5982\u8bb8\u9ad8\u5f3a\u5251 \u672f\u4e4b\u4eba\uff0c\u5c31\u662f\u90a3\u5a49\u513f\u7684\u201c\u641c\u9b42\u6307\u201d\u4e5f\u4e0d\u53ef\u8f7b\u89c6\uff0c\u89c1\u6307\u5251\u540c\u65f6\u653b\u5230\uff0c\u4e0d\u6562\u63a5\u67b6\uff0c\u6643\u8eab\u6025\u8eb2\u2026\u2026 \u5728\u56db\u5468\u56f4\u89c2\u4e4b\u4eba\uff0c\u867d\u4e0d\u4e4f\u6b66\u6797\u9ad8\u624b\uff0c\u4e14\u5927\u591a\u6570\u662f\u8d70\u5357\u95ef\u5317\u7684\u6c5f\u6e56\u6df7\u6df7\uff0c\u4f46\u4e5f\u4ece\u672a\u89c1\u8fc7\u5982 \u8bb8\u4e0a\u4e58\u7684\u5251\u672f\uff0c\u4e0d\u7531\u9f50\u58f0\u60ca\u547c\uff0c\u6115\u7136\u566b\u60ca\uff01\u2026\u2026 \u5728\u4e00\u65c1\u89c2\u6218\u7684\u767d\u8863\u94f6\u6247\u4e66\u751f\uff0c\u66f4\u662f\u53c8\u60ca\u53c8\u6025\uff0c\u9ad8\u53eb\u9053\uff1a\u201c\u4ec7\u516c\u516c\uff01\u2018\u4f5b\u5370\u6cd5\u5e08\u2019\uff01\u8fd8\u4e0d \u4e0a\u524d\uff0c\u66f4\u5f85\u4f55\u65f6\uff1f\u201d \u957f\u9aef\u8001\u4eba\u53ca\u4e2d\u5e74\u80a5\u80d6\u548c\u5c1a\u95fb\u8a00\uff0c\u53cc\u53cc\u7eb5\u51fa\uff0c\u4e00\u4e2a\u654c\u4f4f\u67f3\u7fe0\u7fe0\uff0c\u4e00\u4e2a\u654c\u4efb\u5c55\u5a49\u513f\uff01 \u5c31\u8fd9\u6837\uff0c\u628a\u56f4\u5728\u56db\u5468\u7684\u6b66\u6797\u7fa4\u96c4\uff0c\u4e5f\u770b\u5f97\u4e2a\u4e2a\u5fc3\u60ca\uff0c\u90a3\u9752\u8138\u7ea2\u53d1\u7684\u67f3\u7fe0\u7fe0\u4f17\u4eba\u4ece\u672a\u89c1 \u8fc7\uff0c\u6b66\u529f\u5251\u672f\u51fa\u795e\u5165\u5316\uff0c\u4e0d\u77e5\u662f\u4f55\u6765\u8def\uff0c\u60ca\u5947\u8fd8\u4e0d\u5728\u8bdd\u4e0b\uff0c\u4f46\u5c11\u5e74\u5c55\u767d\u5374\u662f\u4f17\u6240\u5468\u77e5\u7684\u4e00\u4e2a \u65b0\u51fa\u9053\u7684\u5c11\u5e74\uff0c\u4ed6\u6bcf\u6b21\u9732\u9762\uff0c\u6b66\u529f\u90fd\u6709\u8fdb\u6b65\uff0c\u4e00\u6b21\u6bd4\u4e00\u6b21\u7684\u5f3a\uff0c\u800c\u4e14\u8fdb\u5883\u4e4b\u901f\uff0c\u66f4\u662f\u5927\u8fdd\u5e38 \u60c5\uff0c\u4ee5\u767d\u53d1\u5a46\u5a46\u6765\u8bf4\uff0c\u56db\u5927\u8c6a\u95e8\u7684\u4e00\u6d41\u9ad8\u624b\u63a5\u8fde\u6b7b\u5728\u767d\u53d1\u5a46\u5a46\u624b\u4e0b\uff0c\u8fde\u201c\u5929\u6daf\u72c2\u751f\u201d\uff0c\u773c\u7a7a \u56db\u6d77\u7684\u6b66\u6797\u9ad8\u624b\uff0c\u90fd\u91cd\u4f24\u5728\u767d\u53d1\u5a46\u5a46\u624b\u4e2d\uff0c\u751f\u6b7b\u4e0d\u660e\uff0c\u800c\u5c0f\u5c0f\u5e74\u7eaa\u7684\u5c55\u767d\uff0c\u7adf\u80fd\u4e00\u638c\u628a\u767d\u53d1 \u5a46\u5a46\u9707\u9000\uff0c\u800c\u73b0\u5728\u53c8\u529b\u6218\u6570\u5341\u5408\u4e0d\u8d25\uff0c\u600e\u4e0d\u4f7f\u4eba\u60ca\u5f02\u3002 \u5a49\u513f\uff0c\u4e5f\u6709\u4e0d\u5c11\u4eba\u8ba4\u8bc6\uff0c\u89c1\u5979\u80fd\u529b\u6218\u201c\u4f5b\u5370\u6cd5\u5e08\u201d\uff0c\u80fd\u591f\u6218\u4e86\u4e2a\u5e73\u624b\uff0c\u540c\u6837\u4f7f\u7fa4\u96c4\u60ca\u8bb6 \u4e0d\u5df2\u3002\u5176\u4e2d\u5c24\u4ee5\u201c\u51cc\u98ce\u516c\u5b50\u201d\u53ca\u5176\u95e8\u4e0b\u9ad8\u624b\uff0c\u6700\u611f\u4e0d\u89e3\uff0c\u56e0\u4e3a\u5a49\u513f\u7684\u6b66\u529f\uff0c\u867d\u7136\u5f97\u5230\u5e84\u4e3b \u201c\u6458\u661f\u624b\u201d\u7684\u4eb2\u4f20\uff0c\u582a\u79f0\u4e0d\u9519\uff0c\u4f46\u8981\u60f3\u654c\u4f0e\u8fde\u6bd9\u56db\u5927\u8c6a\u95e8\u5341\u6570\u9ad8\u624b\u7684\u201c\u4f5b\u5370\u6cd5\u5e08\u201d\uff0c\u51e0\u4e4e\u662f \u4e0d\u53ef\u80fd\u7684\u2014\u2014\u4ef6\u4e8b\uff0c\u53ef\u662f\uff0c\u73b0\u5728\u5979\u4e0d\u4f46\u654c\u4f0e\u4e86\u4f7f\u7fa4\u96c4\u4e27\u80c6\u7684\u201c\u4f5b\u5370\u6cd5\u5e08\u201d\uff0c\u800c\u4e14\u4e0e\u201c\u4f5b\u5370\u6cd5 \u5e08\u201d\u7adf\u6253\u4e86\u4e00\u4e2a\u5e73\u624b\uff0c\u8fd9\u5c82\u4e0d\u662f\u7840\u7840\u602a\u4e8b\uff1f \u201c\u51cc\u98ce\u516c\u5b50\u201d\u6697\u6697\u7eb3\u95f7\uff0c\u5fc3\u60f3\uff1a\u201c\u51e0\u6708\u4e0d\u89c1\uff0c\u59b9\u59b9\u4ece\u4f55\u5904\u5b66\u6765\u8fd9\u7b49\u9ad8\u5f3a\u6b66\u529f\uff1f\u201c\u2026\u3002\u201d \u4e0d\u8bf4\u56f4\u89c2\u7fa4\u96c4\u4e2a\u4e2a\u5fc3\u5934\u63a0\u5f02\u7eb3\u7f55\uff0c\u4e14\u8bf4\u5c55\u767d\u529b\u6218\u767d\u53d1\u5a46\u5a46\u5374\u5df2\u5230\u4e86\u5371\u6025\u5173\u5934\uff01 \u5728\u4ed6\u6700\u540e\u4e00\u62db\u201c\u98ce\u9707\u96f7\u9e23\u201d\u65bd\u51fa\u4e4b\u9645\uff0c\u9f13\u8179\u7eb3\u6c14\uff0c\u53cc\u638c\u5411\u5916\u4e00\u9707\uff0c\u8fd9\u4e00\u62db\u786e\u662f\u5f88\u5389\u5bb3\uff0c \u786c\u628a\u72c2\u50b2\u65e0\u4fe6\u7684\u767d\u53d1\u5a46\u5a46\u903c\u9000\u4e86\u4e09\u6b65\uff0c\u4f46\u81ea\u5df2\u8eab\u4e0a\u6346\u7684\u7834\u5e03\u6761\uff0c\u4e5f\u56e0\u81ea\u5df1\u9f13\u8179\u7eb3\u6c14\u9707\u65ad\uff0c\u8eab \u4e0a\u7684\u88ab\u68c9\u88ab\u90e8\u987a\u52bf\u6ed1\u8131\u4e0b\u6765\uff01 \u9aa4\u7136\u95f9\u4e86\u4e2a\u8d64\u8eab\u88f8\u4f53\uff0c\u5728\u8fd9\u4e48\u7d27\u5f20\u7684\u573a\u5408\uff0c\u56db\u5468\u56f4\u89c2\u7684\u4eba\u90fd\u7981\u4e0d\u4f4f\u54c4\u7b11\u51fa\u58f0\uff01 \u5c55\u767d\u81ea\u5df2\u66f4\u662f\u95f9\u4e86\u4e2a\u9762\u7ea2\u8033\u8d64\uff0c\u624b\u5fd9\u811a\u4e71\uff0c\u4e00\u8fb9\u67a2\u654c\uff0c\u4e00\u8fb9\u62bd\u7a7a\u7528\u624b\u53bb\u63d0\u6ed1\u843d\u7684\u7834\u68c9 \u88ab\u2026\u2026 \u767d\u53d1\u5a46\u5a46\u5374\u6beb\u4e0d\u653e\u677e\uff0c\u4e00\u8fb9\u7740\u7740\u8fdb\u653b\uff0c\u4e00\u8fb9\u563b\u563b\u7b11\u9053\uff1a\u201c\u5c0f\u5a03\u5b50\uff0c\u6ca1\u60f3\u5230\u4f60\u7a77\u5f97\u8fde\u8863\u670d \u90fd\u6ca1\u5f97\u7a7f\uff0c\u8fd8\u6562\u5f3a\u51fa\u5934\u591a\u7ba1\u95f2\u4e8b\uff0c\u770b\u5976\u5976\u4e0d\u6bd9\u4e86\u4f60\u624d\u602a\u5450\uff01 \u8bf4\u7740\uff0c\u201c\u547c\uff01\u547c\uff01\u547c\uff01\u201d\u63a5\u8fde\u53c8\u5288\u51fa\u4e09\u638c\uff01 \u638c\u638c\u529b\u6c89\u52bf\u731b\uff0c\u5c55\u767d\u6b32\u60f3\u8eb2\u907f\uff0c\u65e0\u5948\u4e3e\u811a\u4e0d\u7075\uff0c\u53ea\u6709\u54ac\u7259\u8fd0\u638c\u786c\u63a5\uff01 \u201c\u562d\uff01\u562d\uff01\u562d\uff01\u201d \u4e09\u58f0\u5927\u9707\uff0c\u5c55\u767d\u53ea\u89c9\u8151\u5185\u6c14\u7ffb\u8840\u6d8c\uff0c\u53cc\u773c\u53d1\u9ed1\uff0c\u767d\u53d1\u5a46\u5a46\u7684\u638c\u529b\uff0c\u4e00\u638c\u6bd4\u4e00\u638c\u6c89\u91cd\uff0c\u51e0 \u5982\u4e07\u65a4\u91cd\u9524\u4e00\u822c\uff0c\u51fb\u649e\u5728\u81ea\u5df1\u53cc\u638c\u4e4b\u4e0a\uff0c\u51e0\u4e4e\u4f7f\u81ea\u5df1\u652f\u6491\u4e0d\u4f4f\uff01 \u767d\u53d1\u5a46\u5a46\u4e09\u638c\u5f97\u624b\uff0c\u53c8\u6062\u590d\u72c2\u6001\uff0c\u6840\u6840\u602a\u7b11\u7740\uff0c\u53cc\u638c\u9ad8\u4e3e\u8fc7\u9876\uff0c\u53c8\u731b\u7136\u5411\u5c55\u767d\u8fce\u5934\u6251\u4e0b \u4e86\uff01 \u5c55\u767d\u660f\u6726\u4e4b\u4e2d\uff0c\u53ea\u89c9\u767d\u53d1\u5a46\u5a46\u7684\u638c\u529b\uff0c\u5982\u6cf0\u5c71\u538b\u9876\u4e00\u822c\uff0c\u8fce\u9762\u538b\u81f3\uff0c\u8d76\u5feb\u62bd\u8eab\u8de8\u6b65\u53bb \u8eb2\u2026\u2026 \u53ef\u662f\u4ed6\u5fd8\u4e86\u6ed1\u843d\u81f3\u817f\u80eb\u7684\u7834\u641e\u88ab\u6b63\u7f20\u4f4f\u4e86\u4ed6\u7684\u53cc\u817f\uff0c\u521a\u4e00\u8de8\u6b65\uff0c\u4e00\u4e2a\u8e09\u8dc4\u9669\u4e9b\u683d\u5012\u2026\u2026 \u800c\u767d\u53d1\u5a46\u5a46\u91cd\u903e\u5c71\u5cb3\u7684\u53cc\u638c\uff0c\u5df2\u538b\u8fd1\u4ed6\u7684\u9762\u95e8\uff01 \u5c55\u767d\u65e0\u5948\uff0c\u594b\u529b\u4e3e\u8d77\u53cc\u638c\uff0c\u5411\u4e0a\u8fce\u53bb\uff01 \u201c\u7830\u201d\u7136\u4e00\u58f0\u5927\u9707\uff0c\u5c55\u767d\u5982\u88ab\u4e07\u65a4\u94c1\u9524\u51fb\u4e2d\uff0c\u55d3\u53e3\u4e00\u751c\uff0c\u5f20\u5634\u55b7\u51fa\u4e00\u53e3\u9c9c\u8840\uff0c\u5934\u8111\u8f70\u7684 \u4e00\u58f0\uff0c\u7acb\u523b\u660f\u5012\u4e0b\u53bb\u2026\u2026 \u660f\u8ff7\u4e4b\u4e2d\uff0c\u4ed6\u4ecd\u542c\u5230\u767d\u53d1\u5a46\u5a46\u58f0\u5982\u9e2d\u9e23\u7684\u602a\u7b11\u58f0\uff0c\u4ee5\u53ca\u91cd\u903e\u5c71\u5cb3\u7684\u5f3a\u52b2\u638c\u98ce\uff0c\u7b2c\u4e09\u6b21\u53c8 \u5411\u81ea\u5df1\u80f8\u524d\u538b\u6765\uff01 \u4e0d\u7531\u6697\u53f9\u4e00\u58f0\uff1a\u5b8c\u4e86\uff01\u60f3\u4e0d\u5230\u6211\u5c55\u767d\u8fd9\u4e00\u6b21\u7ade\u4e27\u547d\u4e8e\u6b64\u2026\u2026 \u7a81\u7136\uff0c\u4e00\u9053\u8000\u773c\u7684\u78a7\u6f84\u5251\u5149\u4e00\u95eaI \u63a5\u7740\u542c\u5230\u4e00\u58f0\u5a07\u53f1\uff0c\u56db\u7f15\u75be\u5578\u7684\u52b2\u98ce\uff0c\u76f4\u5411\u767d\u53d1\u5a46\u5a46\u5c04\u81f3\uff01 \u5c55\u767d\u5374\u5df2\u5b8c\u5168\u5931\u53bb\u77e5\u89c9\uff0c\u660f\u5012\u5f53\u5730\uff0c\u5bf9\u4e16\u754c\u4e0a\u53d1\u751f\u7684\u4efb\u4f55\u4e8b\u60c5\uff0c\u4ed6\u662f\u518d\u65e0\u6cd5\u77e5\u9053\u4e86\u2026\u2026 \u7b2c\u56db\u5341\u4e00\u7ae0\u3000\u201c\u5929\u4f5b\u964d\u9b54\u201d\u7edd\u5b66>> \u53e4\u9f99\u300a\u5251\u5ba2\u884c\u300b \u7b2c\u56db\u5341\u4e00\u7ae0\u3000\u201c\u5929\u4f5b\u964d\u9b54\u201d\u7edd\u5b66 \u201c\u767d\u4e91\u82cd\u72d7\uff0c\u6ca7\u6d77\u6851\u7530\u201d\uff0c\u4eba\u751f\u53d8\u5e7b\u65e0\u5e38\uff01 \u4e8b\u5b9e\u4e0a\uff0c\u4eba\u751f\u53d8\u5316\u4e4b\u5927\uff0c\u5f80\u5f80\u51fa\u4eba\u610f\u5916\uff01 \u53c8\u9053\u662f\u201c\u4eba\u751f\u68a6\u5982\u201d\u3002\u76f8\u4f20\u5e84\u5468\u4f5c\u68a6\u53d8\u4e3a\u8774\u8776\uff0c\u68a6\u9192\u540e\uff0c\u4e0d\u77e5\u81ea\u5df1\u662f\u8774\u8776\u4f5c\u68a6\u53d8\u4e3a\u4eba\uff1f \u8fd8\u662f\u4eba\u4f5c\u68a6\u53d8\u6210\u4e86\u8774\u8776\uff1f \u5c55\u767d\u5728\u660f\u8ff7\u4e2d\u82cf\u9192\uff0c\u4e5f\u6709\u7c7b\u4f3c\u7684\u611f\u89c9\uff01 \u4ed6\u88ab\u201c\u6d77\u5916\u4e09\u715e\u201d\u4e4b\u4e00\u7684\u767d\u53d1\u5a46\u5a46\u4e09\u638c\u9707\u4f24\uff0c\u660f\u6b7b\u8fc7\u53bb\uff0c\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u53c8\u60a0\u60a0\u9192 \u8f6c\uff01 \u4ed6\u8ff7\u8499\u4e2d\u6062\u590d\u4e86\u77e5\u89c9\uff0c\u4e0d\u77e5\u81ea\u5df1\u7a76\u7adf\u662f\u6b7b\u8fd8\u662f\u6d3b\uff1f\u9996\u5148\u4f20\u5165\u8033\u9f13\u7684\u662f\u76c8\u8033\u7684\u6c34\u58f0\uff01 \u90a3\u6c34\u58f0\u9e23\u91d1\u51fb\u7389\uff0c\u53ee\u53ee\u5580\u5580\uff0c\u72b9\u5982\u4e00\u66f2\u4ed9\u4e50\uff01 \u800c\u4e14\uff0c\u90a3\u4ed9\u4e50\u4f3c\u7684\u6d41\u6c34\u58f0\u4e2d\uff0c\u5c1a\u9690\u7ea6\u4ea4\u7ec7\u7740\u7f00\u6e3a\u7684\u6b4c\u58f0\uff0c\u542c\u6765\u5a49\u8f6c\u67d4\u548c\uff0c\u975e\u5e38\u60a6\u8033\uff01 \u5c55\u767d\u4e0d\u77e5\u662f\u771f\uff1f\u662f\u5e7b\uff1f\u662f\u68a6\uff1f\u662f\u9192\uff1f \u66f4\u4e0d\u77e5\u81ea\u5df1\u7f6e\u8eab\u4f55\u5730\uff1f\u4ed6\u5c3d\u529b\u56de\u60f3\u7740\u4ee5\u5f80\u7684\u7ecf\u5386\uff0c\u65e0\u8bba\u5982\u4f55\u4e5f\u60f3\u4e0d\u8d77\u81ea\u5df1\u662f\u5230\u4e86\u4e00\u4e2a\u4ec0 \u4e48\u5730\u65b9\uff1f \u7ec8\u4e8e\uff0c\u5c55\u767d\u7741\u5f00\u4e86\u773c\u775b\uff01 \u9996\u5148\u6620\u5165\u773c\u5e18\u7684\u662f\u851a\u84dd\u7684\u5929\uff01 \u84dd\u5929\u662f\u90a3\u4e48\u84dd\uff0c\u90a3\u4e48\u4eae\uff0c\u84dd\u4eae\u5f97\u51e0\u4e4e\u900f\u660e\uff0c\u72b9\u5982\u4e00\u6f6d\u51dd\u78a7\u96c6\u53ea\u7684\u6e5b\u84dd\u6e56\u6c34\uff0c\u53c8\u5982\u4e00\u6574\u5757 \u900f\u660e\u7684\u84dd\u7389\u77f3\u677f\u8986\u76d6\u5728\u4ed6\u7684\u5934\u9876\uff01 \u84dd\u5929\u4e0a\u6709\u51e0\u6735\u6d01\u767d\u65e0\u6bd4\u7684\u767d\u4e91\uff0c\u6015\u7136\u8212\u5377\uff0c\u4f7f\u4eba\u770b\u4e86\u6709\u8bf4\u4e0d\u51fa\u7684\u60a0\u95f2\u3001\u5b81\u9759\u4e4b\u611f\uff01 \u8f6c\u773c\u5411\u5de6\u770b\u53bb\uff0c\u4e00\u5e26\u5947\u5cf0\u7ade\u7acb\u7684\u743c\u5d16\u4e0a\uff0c\u5947\u5ca9\u602a\u77f3\uff0c\u7476\u8349\u6478\u82b1\uff0c\u51e0\u5982\u4e00\u9053\u9526\u7ee3\u7684\u5c4f\u98ce\u822c \u9009\u9898\u6446\u5f00\uff0c\u6b63\u597d\u505a\u4e86\u4ed6\u5b58\u8eab\u4e4b\u5904\u7684\u5929\u7136\u5c4f\u969c\uff01 \u743c\u5d16\u7edd\u9ad8\u5904\uff0c\u4e00\u9053\u6d41\u6cc9\uff0c\u55b7\u6b8a\u6e85\u52bf\uff0c\u76d8\u66f2\u4e09\u6298\uff0c\u5230\u4e86\u5c06\u8fd1\u5730\u9762\u4e00\u7247\u5e73\u53f0\u4f3c\u7684\u5c71\u77f3\u4e0a\uff0c\u5206 \u6210\u65e0\u6570\u7ec6\u6d41\uff0c\u6cea\u6cc4\u800c\u4e0b\uff0c\u6210\u4e3a\u4e00\u5927\u7247\u7684\u5929\u7136\u55b7\u6cc9\uff0c\u666f\u8c61\u6015\u4eba\u5df2\u6781\uff01 \u60f3\u90a3\u4ed9\u4e50\u4f3c\u7684\u6d41\u6c34\u58f0\uff0c\u5c31\u662f\u8fd9\u4e9b\u7ec6\u6d41\u5f62\u6210\u7684\u4e86\uff01 \u5728\u5c71\u811a\u4e0b\uff0c\u805a\u6c34\u6210\u6f6d\uff0c\u78a7\u6ce2\u8361\u6f3e\uff0c\u5012\u6620\u7740\u84dd\u5929\u767d\u4e91\uff0c\u53ca\u743c\u5d16\u4e0a\u7684\u5947\u82b1\u5f02\u8349\uff0c\u76f8\u6620\u6210\u8da3\uff0c \u666f\u81f4\u4e4b\u7f8e\uff0c\u72b9\u5982\u4ed9\u5883I \u518d\u770b\u81ea\u5df1\uff0c\u5219\u662f\u4ef0\u5367\u5728\u4e00\u5757\u6bd4\u5e8a\u94fa\u7565\u5927\u7684\u767d\u77f3\u4e0a\uff0c\u81ea\u77f3\u6210\u957f\u65b9\u5f62\uff0c\u800c\u4e14\u77f3\u5c71\u8fb9\u751f\u4e86\u4e00\u5c42 \u5982\u8335\u7684\u7ec6\u8349\uff0c\u5012\u5728\u4e0a\u8fb9\u7ade\u6bd4\u771f\u9526\u8925\u7ee3\u88ab\u7684\u5e8a\u94fa\u8fd8\u8981\u6765\u5f97\u8212\u670d\uff01 \u800c\u4e14\uff0c\u5728\u4ed6\u4ef0\u5367\u7684\u8eab\u8eaf\u56db\u5468\uff0c\u6446\u6ee1\u4e86\u5404\u8272\u5404\u6837\u7684\u9c9c\u82b1\uff0c\u7b80\u76f4\u628a\u4ed6\u88c5\u9970\u6210\u82b1\u56fd\u91cc\u7684\u4e00\u4f4d\u7761 \u738b\u5b50\uff01 \u5c55\u767d\u6697\u6697\u7eb3\u7f55\uff0c\u8fd9\u662f\u8c01\u628a\u81ea\u5df1\u653e\u7f6e\u5728\u8fd9\u4e48\u4e00\u4e2a\u5730\u65b9\uff1f\u8fd9\u662f\u54ea\u91cc\uff1f\u54ea\u91cc\u6709\u8fd9\u4e48\u7f8e\u7684\u6240\u5728\uff1f \u53c8\u662f\u8c01\u91c7\u6765\u8fd9\u4e48\u591a\u9c9c\u82b1\u653e\u5728\u81ea\u5df1\u7684\u8eab\u8fb9\uff1f \u95ee\u53f7\u5b9e\u5728\u592a\u591a\u4e86\uff01 \u4ed6\u4e0d\u7531\u53c8\u8f6c\u8138\u56db\u671b\uff0c\u5f53\u4ed6\u7684\u773c\u5149\u8f6c\u5230\u90a3\u78a7\u6ce2\u8361\u6f3e\u7684\u6e56\u6c34\u4e2d\u65f6\uff0c\u4f7f\u4ed6\u66f4\u6115\u4e86\uff01 \u539f\u6765\u5728\u90a3\u6625\u6c34\u78a7\u6ce2\u4e4b\u4e2d\uff0c\u5206\u660e\u6709\u4e00\u4e2a\u957f\u53d1\u62ab\u80a9\u3001\u80a4\u767d\u5982\u51dd\u8102\u4f3c\u7684\u8d64\u88f8\u7f8e\u5973\uff0c\u6b63\u5728\u4e00\u8fb9\u6b4c \u5531\u4e00\u8fb9\u620f\u6c34\uff01 \u90a3\u8d64\u88f8\u7f8e\u5973\uff0c\u539f\u662f\u5728\u6c34\u4e2d\u4fef\u6cf3\u7740\uff0c\u6b64\u65f6\uff0c\u5979\u5ffd\u7136\u6e38\u8fd1\u6d45\u5904\uff0c\u7f13\u7f13\u4ece\u6c34\u4e2d\u7ad9\u4e86\u8d77\u6765\uff01 \u90a3\u4e00\u6765\uff0c\u5c55\u767d\u66f4\u611f\u60ca\u5f02\u4e86\uff0c\u4ed6\u7eb5\u662f\u4e00\u4e2a\u4e0d\u51fa\u4e16\u7684\u5947\u7537\u5b50\uff0c\u89c1\u5230\u6b64\u79cd\u5149\u666f\uff0c\u4e5f\u4e0d\u7531\u7830\u7136\u5fc3 \u52a8\u2026\u2026 \u539f\u6765\u90a3\u8d64\u88f8\u7f8e\u5973\u7ad9\u7acb\u8d77\u6765\uff0c\u6c34\u6d45\u53ca\u819d\uff0c\u5979\u4e00\u526f\u7389\u77f3\u96d5\u523b\u822c\u7684\u8d64\u88f8\u80f4\u4f53\uff0c\u6beb\u65e0\u906e\u63a9\u5730\u6620\u5165 \u4e86\u5c55\u767d\u7684\u773c\u5e18\u3002 \u53ea\u89c1\u90a3\u5c11\u5973\u7684\u88f8\u4f53\u4e4b\u7f8e\uff0c\u771f\u7684\u96be\u63cf\u96be\u753b\uff0c\u6d01\u767d\u6676\u83b9\uff0c\u6d51\u5706\u4e30\u6ee1\uff0c\u5168\u8eab\u4e0a\u4e0b\u6bcf\u4e00\u6839\u66f2\u7ebf\uff0c \u90fd\u5145\u6ee1\u4e86\u548c\u8c10\u7684\u7f8e\uff01 \u90a3\u8d64\u88f8\u5c11\u5973\u867d\u662f\u80cc\u5411\u800c\u7acb\uff0c\u5c55\u767d\u4ece\u5979\u8eab\u540e\u671b\u53bb\uff0c\u53ea\u80fd\u770b\u5230\u4fa7\u80cc\uff0c\u4e0e\u4e00\u5c11\u90e8\u4efd\u659c\u524d\u9762\uff0c\u4f46 \u4e5f\u4e0d\u7531\u4ece\u5fc3\u5e95\u559d\u91c7\uff0c\u5973\u4eba\u80fd\u6709\u5982\u6b64\u5b8c\u7f8e\u5f62\u4f53\uff0c\u582a\u79f0\u5f97\u8d77\u662f\u4e0a\u5e1d\u7684\u6770\u4f5c\u4e86\uff01 \u90a3\u8d64\u88f8\u5c11\u5973\u5374\u4e0d\u77e5\u5c55\u767d\u5728\u5c3d\u60c5\u5730\u6b23\u8d4f\u7740\u5979\uff0c\u5634\u91cc\u4f4e\u54fc\u7740\u4e0d\u77e5\u540d\u7684\u66f2\u5b50\uff0c\u4f38\u51fa\u67d4\u9ec4\u7d20\u624b\uff0c \u5728\u6d45\u6c34\u5904\u6458\u4e0b\u4e00\u6735\u76db\u5f00\u7684\u767d\u83b2\uff0c\u63d2\u5728\u81ea\u5df1\u7684\u9b13\u8fb9\uff01 \u554a\uff01\u539f\u6765\u5979\u662f\u4e3a\u91c7\u6458\u90a3\u6735\u767d\u83b2\uff01 \u53ef\u662f\uff0c\u5c31\u5728\u5979\u91c7\u4e0b\u767d\u83b2\uff0c\u633d\u9996\u63d2\u5728\u9b13\u8fb9\u65f6\uff0c\u5fae\u4e00\u4fa7\u8138\uff0c\u5374\u770b\u5230\u4e86\u5c55\u767d\uff01 \u5979\u89c1\u5c55\u767d\u4e24\u53ea\u9ed1\u767d\u5206\u660e\u7684\u5927\u773c\u775b\uff0c\u51fa\u795e\u53d1\u5446\u5730\u671b\u7740\u5979\uff0c\u4e0d\u7531\u53d1\u51fa\u4e00\u58f0\u559c\u6781\u7684\u6b22\u547c\uff01 \u201c\u554a\uff01\u767d\u54e5\uff01\u4f60\u9192\u8f6c\u6765\u5566\uff01\u201d \u4e00\u8fb9\u6b22\u547c\uff0c\u4e00\u8fb9\u5728\u6c34\u4e2d\u8e22\u6c34\u6e85\u6ce2\u5730\u5411\u5c55\u767d\u8dd1\u6765\uff01 \u4e00\u8fb9\u5634\u4e2d\u9ad8\u5174\u5730\u53eb\u9053\uff1a\u201c\u767d\u54e5\uff01\u4f60\u9192\u4e86\uff01\u4f60\u9192\u4e86\uff0c\u53ef\u5f97\u966a\u6211\u73a9\uff0c\u6211\u4e00\u4e2a\u4eba\u5b88\u4e86\u4f60\u4e8c\u4e2a\u6708 \u4e86\uff01\u8fd9\u6837\u7684\u6df1\u5c71\u9759\u91ce\uff0c\u53ea\u662f\u6211\u4e00\u4e2a\u4eba\uff0c\u53ef\u95f7\u6b7b\u4e86\u2026\u2026\u201d \u5979\u4e00\u8def\u4e71\u53eb\uff0c\u5979\u8d64\u88f8\u7740\u7684\u8eab\u8eaf\u5728\u5c55\u767d\u8eab\u4e0a\u6eda\u52a8\u7740\uff0c\u7231\u5982\u71ce\u539f\u4e4b\u706b\u822c\u5730\u8fc5\u901f\u71c3\u70e7\u8d77\u6765\u2026\u2026 \u5c55\u767d\u4e0d\u662f\u4e2a\u4e16\u4fd7\u7684\u8272\u60c5\u7537\u5b50\uff0c\u5239\u65f6\u514b\u5236\u4f4f\u7231\u4e4b\u6b32\u706b\uff0c\u8bf4\u9053\uff1a\u201c\u7fe0\u7fe0\uff01\u5feb\u628a\u8863\u670d\u7a7f\u8d77 \u6765\u2026\u2026\u54ce\u2026\u2026\u6211\u2026\u2026\u6211\u600e\u4e48\u7761\u5728\u8fd9\u91cc\u4e86\u2026\u2026\u55ec\u2026\u2026\u554a\uff01\u8fd9\u662f\u4ec0\u4e48\u5730\u65b9\uff1f\u2026\u2026\u201d \u5c55\u767d\u867d\u7136\u5f3a\u6536\u5fc3\u795e\uff0c\u4f46\u88ab\u5979\u88f8\u4f53\u5728\u8eab\u4e0a\u4e00\u9635\u6eda\u52a8\u6251\u62b1\uff0c\u8bf4\u8bdd\u4e5f\u663e\u5f97\u4e0d\u6210\u8bed\u8c03\uff01 \u8c01\u77e5\u7fe0\u7fe0\u4e00\u6487\u5c0f\u5634\uff0c\u4ece\u5c55\u767d\u8eab\u4e0a\u7ad9\u76f4\uff0c\u4e00\u633a\u9165\u80f8\u9053\uff1a\u201c\u7a7f\u8863\u670d\uff01\u7a7f\u8863\u670d\uff0c\u7a7f\u4ec0\u4e48\u8863\u670d\uff1f \u6211\u4ece\u5c0f\u5728\u6d77\u5c9b\u4e0a\u751f\u957f\uff0c\u5728\u6d77\u91cc\u73a9\uff0c\u5728\u6c99\u6ee9\u4e0a\u8dd1\uff0c\u5728\u6811\u836b\u91cc\u7761\uff0c\u5411\u6765\u5c31\u662f\u4e0d\u7a7f\u8863\u670d\u7684\uff01\u201d \u5c55\u767d\u770b\u5979\u61a8\u6001\u53ef\u97a0\uff0c\u4e0d\u7531\u7b11\u9053\uff1a\u90a3\u65f6\u5019\u4f60\u8fd8\u5c0f\uff0c\u5f53\u7136\u53ef\u4ee5\u4e0d\u7a7f\u8863\u670d\uff0c\u53ef\u662f\uff0c\u73b0\u5728\u4f60\u957f\u5927 \u4e86\uff0c\u5e94\u8be5\u7a7f\u8863\u670d\u4e86\uff01\u201d \u7fe0\u7fe0\u79c0\u7709\u4e00\u6311\u9053\uff1a\u201c\u8c01\u8bf4\u7684\uff01\u5c31\u662f\u524d\u51e0\u4e2a\u6708\uff0c\u6211\u8fd8\u662f\u4e0d\u7a7f\u8863\u670d\u5728\u6d77\u8fb9\u73a9\uff01\u201d \u5c55\u767d\u9053\uff1a\u201c\u4f60\u90a3\u662f\u5728\u8352\u5c9b\u4e0a\u6ca1\u6709\u4eba\u7684\u5730\u65b9\uff0c\u5230\u4e86\u4e2d\u539f\uff0c\u5904\u5904\u6709\u4eba\uff0c\u5c31\u4e0d\u80fd\u4e0d\u7a7f\u8863 \u670d\u2026\u2026\u201d \u5c55\u767d\u8bdd\u672a\u8bf4\u5b8c\uff0c\u7fe0\u7fe0\u6487\u5634\u9053\uff1a\u201c\u5c41\uff01\u8352\u5c9b\u4e0a\u4e5f\u6709\u6e14\u6c11\uff0c\u8fd9\u5730\u65b9\u5012\u662f\u771f\u6ca1\u6709\u4e00\u4e2a\u4eba\uff01\u201d \u5c55\u767d\u88ab\u5979\u8bf4\u5f97\u8bed\u585e\uff0c\u4e00\u65f6\u7b54\u4e0d\u51fa\u8bdd\u6765\u3002\u53c8\u89c1\u5979\u7389\u4e73\u9165\u80f8\uff0c\u5f53\u9762\u800c\u7acb\uff0c\u8d64\u88f8\u7684\u80f4\u4f53\u6d01\u7f8e\u5f97 \u4f7f\u4eba\u7729\u76ee\uff0c\u5c24\u5176\u7981\u4e0d\u4f4f\u5fc3\u733f\u610f\u9a6c\uff0c\u5468\u8eab\u8840\u6db2\u5982\u4e07\u9a6c\u5954\u817e\u4e00\u822c\u5954\u6d41\u8d77\u6765\uff0c\u5012\u95f9\u4e86\u4e2a\u9762\u7ea2\u6c14\u5598\uff0c \u53ea\u7741\u5927\u4e86\u53cc\u773c\u5446\u5446\u53d1\u8d77\u6014\u6765\u2026\u2026 \u5728\u8fd9\u6837\u7f8e\u4e3d\u7684\u4ed9\u5883\uff0c\u6b23\u8d4f\u8fd9\u6837\u5973\u795e\u4e00\u6837\u5b8c\u7f8e\u7684\u88f8\u4f53\uff0c\u6050\u6015\u5728\u4eba\u4e16\u95f4\u662f\u68a6\u4e5f\u68a6\u4e0d\u5230\u7684\uff01 \u5c55\u767d\u6b63\u671b\u7740\u7fe0\u7fe0\u679c\u679c\u53d1\u6014\u7684\u5f53\u513f\uff0c\u7fe0\u7fe0\u5374\u5ffd\u7136\u5657\u54e7\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u767d\u54e5\uff01\u4f60\u4e3a\u4ec0\u4e48\u7528\u90a3\u79cd \u773c\u5149\u671b\u7740\u6211\uff1f\u201d \u5c55\u767d\u5982\u68a6\u5453\u822c\u5730\u5583\u5583\u9053\uff1a\u201c\u54ce\u2026\u2026\u4f60\u771f\u7f8e\uff01\u2026\u2026\u201d \u7fe0\u7fe0\u542c\u5c55\u767d\u8d5e\u7f8e\u5979\uff0c\u7b11\u5bb9\u5982\u6625\u82b1\u76db\u5f00\uff0c\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u4f60\u4e0d\u518d\u903c\u6211\u7a7f\u8863\u670d\u4e86\uff1f\u201d \u7ecf\u6b64\u4e00\u95ee\uff0c\u5c55\u767d\u7a81\u7136\u8bb0\u8d77\u5728\u201c\u8c79\u7a81\u5c71\u5e84\u201d\u5e84\u5916\u5c0f\u5c71\u4e0a\uff0c\u88ab\u201c\u94f6\u7bab\u593a\u9b42\u201d\u7ae0\u58eb\u670b\u7bab\u97f3\u6240 \u8ff7\uff0c\u81ea\u5df1\u6495\u7834\u8eab\u4e0a\u7684\u8863\u670d\uff0c\u4e5f\u6709\u7c7b\u4f3c\u7684\u884c\u52a8\uff0c\u89c9\u5f97\u201c\u4eba\uff0c\u539f\u662f\u5927\u81ea\u7136\u4e2d\u7684\u52a8\u7269\uff0c\u8d64\u88f8\u88f8\u5730 \u6765\uff0c\u8d64\u88f8\u88f8\u5730\u53bb\uff0c\u7a7f\u8863\u670d\u662f\u4eba\u4e3a\u7684\u77eb\u9970\uff0c\u4e0d\u7a7f\u8863\u670d\u53cd\u800c\u66f4\u63a5\u8fd1\u81ea\u7136\uff0c\u66f4\u7eaf\u771f\uff0c\u66f4\u5723\u6d01\u2026\u2026\u201d \u5c55\u767d\u5934\u8111\u4e2d\u6e10\u6e10\u63a5\u8fd1\u4e86\u81ea\u7136\uff0c\u7075\u5149\u8000\u95ea\uff0c\u6b32\u5ff5\u5168\u6d88\uff0c\u5fd9\u9053\uff1a\u201c\u662f\u7684\uff01\u4f60\u4e0d\u7a7f\u8863\u670d\u6bd4\u7a7f\u8863 \u670d\u66f4\u7f8e\uff01\u6211\u2026\u2026\u201d \u8c01\u77e5\u7fe0\u7fe0\u5374\u4e00\u6643\u5a07\u8eaf\uff0c\u9690\u8eab\u5728\u4e00\u652f\u77d7\u7acb\u7684\u77f3\u7b0b\u540e\u9762\uff0c\u563b\u563b\u54c8\u54c8\u5730\u7b11\u9053\uff1a\u4f60\u77e5\u9053\u5c31\u597d\u4e86\uff01 \u53ef\u4e0d\u80fd\u8ba9\u4f60\u770b\uff0c\u4f60\u7684\u773c\u775b\u597d\u50cf\u8981\u5403\u4eba\u7684\u6837\u5b50\uff0c\u4f7f\u4eba\u5bb3\u6015\u2026\u2026\u201d \u8bf4\u7f62\uff0c\u4ecd\u5403\u5403\u7b11\u7740\uff0c\u7ade\u4ece\u77f3\u540e\u62ab\u4e0a\u4e00\u4ef6\u95ea\u5149\u900f\u660e\uff0c\u5982\u7f51\u7edc\u7684\u957f\u888d\u8d70\u4e86\u51fa\u6765\uff01 \u5c55\u767d\u89c1\u5979\u5fae\u4f4e\u7c89\u9888\uff0c\u4e24\u53ea\u624b\u7075\u5de7\u5730\u7cfb\u7740\u8863\u5e26\uff0c\u9b13\u65c1\u659c\u7c2a\u7740\u4e00\u6735\u767d\u83b2\uff0c\u7f13\u7f13\u8d70\u6765\uff0c\u771f\u662f\u7f8e \u4e3d\u6781\u4e86\uff0c\u5fc3\u4e2d\u6697\u8d5e\u9053\uff1a\u201c\u554a\uff01\u592a\u7f8e\u4e86\uff01\u5982\u82e5\u4f34\u7740\u5982\u6b64\u7231\u4fa3\uff0c\u50b2\u5578\u5c71\u6797\uff0c\u900d\u9065\u4e00\u751f\uff0c\u4e16\u4e0a\u4e00\u5207 \u7684\u8363\u534e\u5bcc\u8d35\u3001\u6069\u6028\u60c5\u4ec7\uff0c\u771f\u53ef\u4e00\u6982\u4e0d\u95ee\u4e86\uff01\u2026\u2026\u201d \u7fe0\u7fe0\u8d70\u8fd1\u5c55\u767d\u8eab\u8fb9\uff0c\u4e3e\u624b\u63a0\u4e86\u63a0\u6563\u4e71\u7684\u9b13\u53d1\uff0c\u9053\uff1a\u201c\u767d\u54e5\u54e5\uff0c\u4f60\u5750\u8d77\u6765\u8fd0\u8fd0\u6c14\uff0c\u770b\u4f60\u7684 \u5185\u4f24\u597d\u4e86\u6ca1\u6709\uff1f\u5982\u679c\u662f\u597d\u4e86\uff0c\u6211\u6709\u597d\u4e1c\u897f\u7ed9\u4f60\u770b\uff01\u201d \u5c55\u767d\u9053\uff1a\u201c\u4ec0\u4e48\u597d\u4e1c\u897f\uff1f\u73b0\u5728\u7ed8\u6211\u770b\u4e0d\u662f\u4e00\u6837\u5417\uff1f\u201d \u7fe0\u7fe0\u6492\u5a07\u9053\uff1a\u201c\u4e0d\u561b\uff01\u4f60\u4e00\u5b9a\u5148\u8981\u8fd0\u529f\uff0c\u8bc1\u660e\u4f60\u7684\u4f24\u52bf\u597d\u4e86\uff0c\u6211\u624d\u7ed9\u4f60\u770b\uff01\u201d \u5c55\u767d\u53ea\u5f97\u4f9d\u5979\uff0c\u5750\u4e86\u8d77\u6765\uff0c\u53d1\u73b0\u81ea\u5df1\u8eab\u4e0a\u7ade\u7a7f\u4e86\u4e00\u4ef6\u4e0e\u7fe0\u7fe0\u8eab\u4e0a\u8863\u670d\u8d28\u6599\u76f8\u540c\u7684\u539f\u8272\u957f \u888d\u3002 \u5c55\u767d\u7528\u624b\u63c9\u4e86\u63c9\uff0c\u90a3\u8863\u6599\u67d4\u548c\u800c\u4e14\u95ea\u95ea\u751f\u5149\uff0c\u4f46\u5374\u662f\u975e\u4e1d\u975e\u7ef8\uff0c\u770b\u4e0d\u51fa\u662f\u4f55\u8d28\u6599\u7ec7\u6210\uff1f \u4e0d\u7531\u95ee\u9053\uff1a\u8fd9\u8863\u670d\u662f\u4f55\u5904\u6765\u7684\uff1f\u8d28\u6599\u8fd9\u6837\u597d\uff01\u201d \u7fe0\u7fe0\u9053\uff1a\u201c\u8fd9\u662f\u4f60\u75c5\u4e2d\uff0c\u6211\u91c7\u96c6\u5c71\u4e2d\u5929\u8695\u4e1d\u505a\u6210\u7684\uff0c\u56e0\u6ca1\u6709\u9488\u7ebf\uff0c\u8fb9\u4e0a\u90fd\u662f\u7528\u4e1d\u6761\u7a7f\u8fde \u8d77\u6765\uff0c\u600e\u4e48\u6837\uff1f\u4f60\u770b\u6211\u505a\u5f97\u8fd8\u4e0d\u9519\u5427\uff1f\u201d \u5c55\u767d\u7ecf\u7fe0\u7fe0\u8fd9\u4e00\u63d0\u9192\uff0c\u624d\u6ce8\u610f\u5230\uff0c\u8863\u8fb9\u4e0a\u679c\u7136\u662f\u7528\u8f83\u7ebf\u7565\u6781\u7684\u4e1d\u6761\u7f16\u7ec7\u800c\u6210\uff0c\u4e0d\u7531\u5fc3\u91cc \u6697\u8d5e\uff0c\u8fd9\u7fe0\u7fe0\u59d1\u5a18\u4e0d\u4f46\u4eba\u7f8e\u6b66\u529f\u9ad8\uff0c\u800c\u4e14\u5fc3\u7075\u624b\u5de7\uff0c\u60f3\u8fd9\u8352\u5c71\u91ce\u5cad\u65e0\u9488\u65e0\u7ebf\uff0c\u53c8\u65e0\u7ec7\u5e03\u673a\uff0c \u5979\u80fd\u5168\u51ed\u53cc\u624b\uff0c\u7ec7\u505a\u6210\u5982\u6b64\u5408\u4f53\u7f8e\u89c2\u7684\u8863\u670d\uff0c\u7684\u786e\u96be\u5f97\u3002\u4e0d\u7531\u8131\u53e3\u8d5e\u9053\uff1a\u201c\u771f\u597d\uff01\u4e8f\u4f60\u80fd\u505a \u5f97\u51fa\u6765\uff01\u8fd9\u6050\u6015\u8981\u8d39\u4f60\u5f88\u591a\u65f6\u95f4\u5427\uff01\u201d \u7fe0\u7fe0\u9053\uff1a\u201c\u4e0d\u591a\u4e0d\u5c11\uff0c\u6574\u6574\u516d\u5341\u5929\uff01\u201d \u5c55\u767d\u60ca\u9053\uff1a\u201c\u600e\u4e48\uff1f\u6211\u5728\u8fd9\u5730\u65b9\u660f\u8ff7\u4e86\u4e24\u4e2a\u6708\u5566\uff1f\u201d \u7fe0\u7fe0\u9053\uff1a\u4e24\u4e2a\u591a\u6708\u4e86\uff01\u4f60\u5fd8\u8bb0\u6765\u65f6\u662f\u51ac\u5929\uff0c\u73b0\u5728\u5df2\u7ecf\u5230\u4e86\u6625\u5929\u3002\u201d \u8bf4\u7740\u53c8\u54fc\u4e86\u4e00\u58f0\uff0c\u4f3c\u662f\u5e26\u7740\u5185\u5fc3\u59d4\u5c48\u8bf4\u9053\uff1a\u201c\u600e\u4e48\uff1f\u4f60\u5728\u660f\u8ff7\u4e0d\u9192\u4e2d\u8fd8\u5acc\u65e5\u5b50\u957f\u4e86\u5417\uff1f \u4e0d\u77e5\u9053\u6211\u72ec\u81ea\u4e00\u4e2a\u4eba\u5728\u8fd9\u4e8c\u4e2a\u591a\u6708\u7684\u65f6\u95f4\u4e2d\uff0c\u8be5\u662f\u591a\u4e48\u70e6\u95f7\u5450\uff01\u201d \u5c55\u767d\u5e76\u6ca1\u6709\u6ce8\u610f\u5230\u7fe0\u7fe0\u7684\u5e7d\u6028\uff0c\u53ea\u60f3\u5230\u81ea\u5df1\u7236\u4ec7\u672a\u62a5\uff0c\u6025\u9053\uff1a\u201c\u4e0d\u884c\uff01\u6211\u8981\u8d76\u5feb\u8d70\uff01\u201d \u8bf4\u7740\u7eb5\u8eab\u6b32\u8d77\u2026\u2026 \u7fe0\u7fe0\u53cc\u624b\u628a\u4ed6\u6309\u4efb\u9053\uff1a\u201c\u767d\u54e5\u54e5\uff0c\u4f60\u5230\u54ea\u91cc\u53bb\uff1f\u201d \u5c55\u767d\u9053\uff1a\u201c\u53bb\u627e\u56db\u5927\u8c6a\u95e8\u4e3a\u7236\u4eb2\u62a5\u4ec7\uff01\u201d \u7fe0\u7fe0\u9053\uff1a\u767d\u54e5\u54e5\uff0c\u4f60\u4e0d\u7528\u53bb\u62a5\u4ec7\u4e86\uff01\u56db\u5927\u8c6a\u95e8\u5747\u5df2\u661f\u6563\uff0c\u2018\u9752\u86a8\u795e\u2019\u91d1\u4e5d\u3001\u2018\u4e7e\u5764\u638c\u2019 \u4e91\u5b97\u9f99\u3001\u2018\u6df7\u5143\u6307\u2019\u53f8\u7a7a\u664b\u3001\u2018\u6458\u661f\u624b\u2019\u6155\u5bb9\u6db5\uff0c\u4ee5\u53ca\u2018\u9738\u738b\u97ad\u2019\u6a0a\u975e\u5747\u5df1\u88ab\u4eba\u6740\u6b7b \u4e86\uff01\u2026\u2026\u201d \u5c55\u767d\u4e0d\u4fe1\u9053\uff1a\u7fe0\u7fe0\uff0c\u4f60\u8fd9\u5c82\u4e0d\u662f\u4fe1\u53e3\u80e1\u8bf4\uff01\u4ed6\u4eec\u56db\u5927\u8c6a\u95e8\u52bf\u53ef\u76d6\u5929\uff0c\u4e2a\u4e2a\u95e8\u4e0b\u9ad8\u624b\u5982 \u4e91\uff0c\u600e\u4e48\u4f1a\u4e00\u65f6\u4e4b\u95f4\u90fd\u4f1a\u88ab\u4eba\u6740\u6b7b\uff01\u2026\u2026\u201d \u7fe0\u7fe0\u89c1\u5c55\u767d\u4e0d\u76f8\u4fe1\u81ea\u5df1\uff0c\u9887\u4e3a\u4e0d\u8bf4\u5730\u9053\uff1a\u767d\u54e5\u54e5\uff01\u4f60\u4ee5\u4e3a\u5c0f\u59b9\u4f1a\u9a97\u4f60\u5417\uff1f\u8fd9\u4e24\u4e2a\u591a\u6708\u7684 \u65f6\u95f4\uff0c\u4f60\u8fd8\u4e0d\u77e5\u9053\u4e2d\u539f\u6b66\u6797\u6709\u591a\u4e48\u5927\u7684\u53d8\u5316\uff1f\u5982\u4eca\u4e0d\u4f46\u56db\u5927\u8c6a\u95e8\u7684\u4e3b\u4eba\u5747\u5df2\u6bd9\u547d\uff0c\u800c\u4e14\u5929\u4e0b \u4e5d\u5927\u95e8\u6d3e\u7684\u638c\u95e8\u4eba\uff0c\u9ed1\u767d\u4e24\u9053\u7684\u76df\u4e3b\uff0c\u6c34\u9646\u7801\u5934\u7684\u603b\u74e2\u5b50\uff0c\u5747\u5df2\u6b7b\u7684\u6b7b\u3001\u964d\u7684\u964d\uff0c\u4eca\u65e5\u4e2d\u539f \u5df2\u7ecf\u81e3\u5c5e\u4e8e\u4e00\u5927\u9b54\u5934\u7684\u638c\u63e1\u4e4b\u4e2d\u4e86\uff01\u201d \u5c55\u767d\u8d8a\u53d1\u4e0d\u4fe1\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u2018\u6b66\u6797\u56db\u516c\u5b50\u2019\u5462\uff1f\u201d \u7fe0\u7fe0\u9053\uff1a\u201c\u2018\u6b66\u6797\u56db\u516c\u5b50\u2019\uff0c\u73b0\u5728\u4e0d\u8fc7\u662f\u5c40\u4fc3\u4e00\u9685\u7684\u4e00\u4e2a\u5c0f\u5934\u76ee\u7f62\u4e86\uff01\u201d \u5c55\u767d\u60ca\u9053\uff1a\u201c\u90a3\u9b54\u5934\u662f\u8c01\uff0c\u7adf\u4f1a\u6709\u8fd9\u4e48\u9ad8\u5f3a\u7684\u6b66\u529f\uff0c\u5728\u77ed\u77ed\u4e24\u4e2a\u591a\u6708\u7684\u65f6\u95f4\uff0c\u53ef\u4ee5\u5e76\u541e \u6151\u670d\u5929\u4e0b\u6b66\u6797\uff01\u201d \u7fe0\u7fe0\u9053\uff1a\u201c\u63d0\u8d77\u6765\u4f60\u4e5f\u4e0d\u77e5\u9053\uff0c\u4ed6\u662f\u4e45\u5904\u5357\u6d77\u2018\u9b54\u9b3c\u5c9b\u2019\u7684\u2018\u6d77\u9f99\u795e\u2019\uff0c\u53c8\u53eb\u2018\u5357\u6d77\u4e00 \u541b\u2019\uff0c\u59d3\u9f99\u540d\u5578\u5929\uff01\u201d\u5c55\u767d\u60f3\u4e86\u60f3\uff0c\u679c\u7136\u5728\u6b66\u6797\u4e2d\u4ece\u672a\u542c\u5230\u6b64\u4e00\u540d\u53f7\uff0c\u4e0d\u7531\u7eb3\u7f55\u9053\uff1a\u201c\u5c31\u51ed \u2018\u6d77\u9f99\u795e\u2019\u4e00\u4eba\uff0c\u5c31\u53ef\u4ee5\u81e3\u670d\u5929\u4e0b\u6b66\u6797\u4e86\u5417\uff1f\u201d \u7fe0\u7fe0\u7b11\u9053\uff1a\u201c\u5f53\u7136\u4e0d\u6b62\u4ed6\u4e00\u4eba\uff0c\u8fd8\u6709\u4ed6\u5c5e\u4e0b\u9ad8\u624b\u2018\u5357\u6d77\u9f99\u5973\u2019\u3001\u2018\u6843\u82b1\u56db\u4ed9\u2019\u3001\u2018\u9b54\u9b3c \u5c9b\u516b\u5996\u2019\uff0c\u4ee5\u53ca\u4f60\u5728\u2018\u4ea1\u9b42\u8c37\u2019\u4f1a\u8fc7\u7684\u2018\u6d77\u5916\u4e09\u715e\u2019\u4e0e\u90a3\u767d\u8863\u94f6\u6247\u7684\u4e66\u751f\uff01\u201d \u5c55\u767d\u9707\u60ca\u5f97\u77aa\u5927\u4e86\u773c\u775b\uff0c\u9053\uff1a\u201c\u90a3\u767d\u8863\u94f6\u6247\u665a\u751f\u53c8\u662f\u8c01\uff1f\u201d \u7fe0\u7fe0\u9053\uff1a\u201c\u4ed6\u5c31\u662f\u2018\u5357\u6d77\u4e00\u541b\u2019\u7684\u72ec\u751f\u513f\u5b50\uff0c\u540d\u53eb\u2018\u5357\u6d77\u5c11\u541b\u2019\u9f99\u98de\u76f8\u516c\uff0c\u53c8\u53eb\u2018\u9f99\u795e \u592a\u5b50\u2019\uff01\u5165\u4fb5\u4e2d\u539f\uff0c\u867d\u7136\u662f\u4ee5\u4ed6\u7236\u4eb2\u2018\u6d77\u9f99\u795e\u2019\u7684\u540d\u4e49\uff0c\u4e8b\u5b9e\u4e0a\u2018\u6d77\u9f99\u795e\u2019\u5e76\u672a\u5728\u4e2d\u539f\u9732 \u9762\uff0c\u5b8c\u5168\u662f\u4ed6\u4e00\u4eba\u4e3b\u6301\u5168\u5c40\uff0c\u53ef\u4ee5\u8bf4\u76ee\u524d\u4e2d\u539f\u6b66\u6797\uff0c\u5df2\u5c3d\u5c5e\u9f99\u5bb6\u4e00\u59d3\u4e86\uff01\u201d \u5c55\u767d\u89c1\u67f3\u7fe0\u7fe0\u8bf4\u5f97\u5934\u5934\u662f\u9053\uff0c\u7531\u4e0d\u5f97\u4e0d\u4fe1\uff0c\u4f46\u5374\u60ca\u5947\u5f97\u77a0\u76ee\u7ed3\u820c\u5730\u9053\uff1a\u201c\u771f\u60f3\u4e0d\u5230\uff01\u4e24 \u4e2a\u591a\u6708\u7684\u65f6\u95f4\uff0c\u6b66\u6797\u4e2d\u4f1a\u6709\u8fd9\u4e48\u5927\u7684\u53d8\u5316\uff1f\u2026\u2026\u201d \u7fe0\u7fe0\u89c1\u5c55\u767d\u5931\u9b42\u843d\u9b44\uff0c\u89c9\u5f97\u4e0d\u5fcd\uff0c\u5fd9\u9053\uff1a\u201c\u5148\u4e0d\u8981\u7ba1\u5916\u8fb9\u95f9\u5f97\u5929\u584c\u5730\u9677\uff0c\u53cd\u6b63\u6211\u4eec\u8fd9\u5730 \u65b9\u662f\u5757\u5b89\u4e50\u571f\uff0c\u5982\u6ca1\u6709\u6211\u7684\u8bb8\u53ef\uff0c\u4efb\u4f55\u4eba\u4e0d\u5f97\u64c5\u5165\u4e00\u6b65\uff01\u73b0\u5728\u4e0d\u8c08\u90a3\u4e9b\u4e86\uff0c\u767d\u54e5\u54e5\uff0c\u4f60\u5148\u8fd0 \u8fd0\u6c14\uff0c\u770b\u770b\u4f24\u52bf\u5168\u597d\u4e86\u6ca1\u6709\uff1f\u201d \u5c55\u767d\u4ecd\u7136\u653e\u4e0d\u4e0b\u5730\u95ee\u9053\uff1a\u201c\u6b64\u5904\u662f\u4ec0\u4e48\u5730\u65b9\uff1f\u2026\u2026\u201d \u7fe0\u7fe0\u5b89\u6170\u4ed6\u9053\uff1a\u201c\u767d\u54e5\u54e5\uff0c\u4f60\u5148\u4e0d\u8981\u591a\u95ee\uff0c\u8bdd\u8bf4\u8d77\u6765\u957f\u7740\u54e9\uff01\u4f60\u5feb\u8fd0\u6c14\u770b\u770b\uff0c\u7136\u540e\u6211\u7ed9 \u4f60\u770b\u4e00\u4ef6\u6700\u597d\u7684\u4e1c\u897f\uff01\u201d \u5c55\u767d\u89c1\u5979\u8bf4\u5f97\u6025\u5207\uff0c\u53ea\u6709\u6536\u62fe\u8d77\u6ee1\u809a\u5b50\u7684\u7591\u95ee\uff0c\u4f9d\u8a00\u5750\u8d77\uff0c\u6697\u8fd0\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\u4e0a \u7684\u9053\u95e8\u5927\u6cd5\uff0c\u95ed\u76ee\u5782\u7709\uff0c\u53cd\u7701\u5185\u89c6\uff0c\u903c\u4f4f\u4e00\u53e3\u771f\u6c14\uff0c\u8fd0\u884c\u56db\u80a2\u767e\u9ab8\u4e00\u5927\u5c40\u5929\uff0c\u611f\u5230\u771f\u6c14\u6d41 \u8f6c\uff0c\u4e0d\u4f46\u6beb\u65e0\u4e0d\u9002\uff0c\u53cd\u800c\u6bd4\u4ee5\u524d\u66f4\u89c1\u5145\u76c8\uff0c\u8fd9\u624d\u7741\u5f00\u773c\u775b\u9053\uff1a\u201c\u6211\u7684\u5185\u8151\u771f\u6c14\u6beb\u65e0\u963b\u6ede\uff0c\u60f3 \u662f\u4f24\u52bf\u5168\u597d\u4e86\uff01\u201d \u7fe0\u7fe0\u542c\u8bf4\u4ed6\u4f24\u52bf\u5168\u6108\uff0c\u4e0d\u7531\u5c55\u989c\u4e00\u7b11\uff0c\u6781\u662f\u5f00\u5fc3\u5730\u9053\uff1a\u201c\u672c\u6765\u767d\u54e5\u54e5\u53d7\u90a3\u4e00\u70b9\u8f7b\u4f24\uff0c\u662f \u4e0d\u4f1a\u660f\u8ff7\u5982\u6b64\u4e4b\u4e45\u7684\uff0c\u662f\u5c0f\u59b9\u7ed9\u767d\u54e5\u54e5\u670d\u4e86\u4e00\u79cd\u53eb\u505a\u2018\u7d2b\u6a80\u82b1\u2019\u7684\u7075\u836f\uff0c\u8fd9\u79cd\u2018\u7d2b\u6a80\u82b1\u2019\u636e \u300a\u4e91\u6c49\u5f02\u5fd7\u300b\u5386\u8f7d\u4e91\uff1a\u2018\u4ea7\u81ea\u592a\u534e\u5c71\u7edd\u9876\uff0c\u4e0d\u4f46\u80fd\u589e\u957f\u529f\u529b\uff0c\u7597\u4f24\u53bb\u6bd2\uff0c\u800c\u4e14\u53ef\u4ee5\u9a7b\u989c\u957f \u5bff\uff0c\u8f9f\u8c37\u7597\u9965\u3002\u2019\u4e0d\u8fc7\u6ca1\u6709\u6df1\u539a\u6839\u57fa\u4e4b\u4eba\uff0c\u4e0d\u80fd\u64c5\u670d\uff0c\u56e0\u4e3a\u836f\u6027\u592a\u70c8\uff0c\u666e\u901a\u4eba\u670d\u4e0b\u4f1a\u4f7f\u4eba\u4e94 \u5185\u7126\u67af\u3001\u4e03\u7a8d\u6d41\u8840\u800c\u6b7b\u3002\u5c0f\u59b9\u5728\u65e0\u610f\u4e2d\u5f97\u5230\u4e00\u682a\uff0c\u5e26\u5728\u8eab\u8fb9\uff0c\u4e00\u76f4\u4e0d\u6562\u670d\u7528\uff0c\u6ca1\u60f3\u5230\u6070\u5de7\u767d \u54e5\u54e5\u8d1f\u4f24\u5f88\u91cd\uff0c\u5c0f\u59b9\u6253\u9000\u2018\u6d77\u5916\u4e09\u715e\u2019\uff0c\u628a\u767d\u54e5\u54e5\u6551\u6765\u6b64\u5730\uff0c\u65e0\u836f\u7597\u4f24\uff0c\u8fd9\u624d\u60f3\u8d77\u8fd9\u682a\u2018\u7d2b \u6a80\u82b1\u2019\u6765\uff0c\u53c8\u5bdf\u77e5\u767d\u54e5\u54e5\u6839\u57fa\u6781\u4f73\uff0c\u6240\u4ee5\u5c31\u7ed9\u767d\u54e5\u54e5\u5403\u4e86\u2026\u2026\u201d \u5c55\u767d\u542c\u5979\u8bf4\u628a\u73cd\u8d35\u7684\u7075\u836f\u7ed9\u81ea\u5df1\u5403\u4e86\uff0c\u4e0d\u7531\u4e07\u5206\u611f\u6fc0\u5730\u9053\uff1a\u201c\u7fe0\u59b9\u59b9\uff0c\u4f60\u5f85\u6211\u771f\u662f\u592a\u597d \u4e86\uff01\u8fd9\u4e00\u751f\u6211\u4e0d\u77e5\u600e\u6837\u624d\u80fd\u62a5\u7b54\u4f60\u2026\u2026\u201d \u7fe0\u7fe0\u542c\u5230\u6b64\u8bdd\u4f3c\u662f\u611f\u5230\u83ab\u5927\u7684\u5b89\u6170\uff0c\u5a49\u7136\u4e00\u7b11\u9053\uff1a\u201c\u767d\u54e5\u54e5\uff0c\u59b9\u59b9\u5e76\u4e0d\u8981\u4f60\u600e\u6837\u8c22\u6211\uff0c \u53ea\u8981\u4f60\u5fc3\u91cc\u6c38\u8fdc\u4e0d\u5fd8\u8bb0\u59b9\u59b9\u5c31\u597d\u4e86\u3002\u73b0\u5728\u518d\u542c\u6211\u8bf4\u4e0b\u53bb\u3002\u5f53\u65f6\uff0c\u6211\u7ed9\u4f60\u670d\u4e0b\u90a3\u2018\u7d2b\u64c5\u82b1\u2019\u4e4b \u540e\uff0c\u4f60\u5468\u8eab\u706b\u7ea2\uff0c\u70ed\u5f97\u6bd4\u706b\u70ad\u8fd8\u8981\u70ed\uff0c\u6211\u4e00\u89c1\u5413\u614c\u4e86\uff0c\u53c8\u89c1\u4f60\u4e94\u516d\u5929\u8fc7\u53bb\u4e86\u4e00\u76f4\u660f\u8ff7\u4e0d\u9192\u800c \u4e14\u70ed\u5ea6\u4e0d\u9000\uff0c\u6211\u771f\u662f\u5413\u614c\u4e86\uff0c\u6015\u7ed9\u4f60\u670d\u9519\u4e86\u836f\uff0c\u51fa\u4e86\u5dee\u9519\uff0c\u4e07\u822c\u65e0\u5948\uff0c\u624d\u4ee5\u5c0f\u59b9\u672c\u8eab\u7684\u7eaf \u9634\u2026\u2026\u201d \u5c55\u767d\u81f3\u6b64\uff0c\u5bf9\u67f3\u7fe0\u7fe0\u7684\u611f\u6fc0\u5df2\u94ed\u611f\u4e94\u5185\uff0c\u4e0d\u77e5\u600e\u6837\u8868\u793a\u624d\u597d\uff0c\u53ea\u6709\u4ee5\u53cc\u624b\u70ed\u70c8\u5730\u62e5\u62b1\uff0c \u628a\u7fe0\u7fe0\u4fef\u5728\u81ea\u5df1\u6000\u4e2d\u7684\u4e00\u526f\u5a07\u8eaf\u62b1\u5f97\u7d27\u2026\u2026 \u7fe0\u7fe0\u4e5f\u5c31\u8fd8\u4ee5\u70ed\u70c8\u62e5\u62b1\uff0c\u800c\u4e14\u6c89\u9189\u5728\u7231\u4fa3\u7684\u6000\u62b1\u4e2d\u4e86\u2026\u2026 \u4e8c\u4eba\u4e5f\u4e0d\u77e5\u62e5\u62b1\u4e86\u591a\u4e45\uff0c\u6c89\u9189\u4e86\u591a\u4e45\uff1f\u8fd8\u662f\u5c55\u767d\u9996\u5148\u9192\u89c9\u8fc7\u6765\uff0c\u6276\u4f4f\u7fe0\u7fe0\u7684\u9999\u80a9\uff0c\u8f7b\u8f7b \u628a\u7fe0\u7fe0\u6276\u8d77\uff0c\u9053\uff1a\u201c\u7fe0\u59b9\uff0c\u4f60\u4e0d\u662f\u8bf4\u8981\u7ed9\u6211\u4e00\u4ef6\u597d\u4e1c\u897f\u5417\uff1f\u5230\u5e95\u662f\u4ec0\u4e48\u4e1c\u897f\uff1f\u62ff\u6765\u7ed9\u54e5\u54e5\u770b \u5427\uff01\u201d \u7fe0\u7fe0\u54df\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u767d\u54e5\u54e5\u4e0d\u8bf4\uff0c\u6211\u8fd8\u5fd8\u4e86\u5462\uff01\u558f\uff01\u767d\u54e5\u54e5\u4f60\u770b\uff0c\u5c31\u662f\u8fd9\u4e2a\uff01\u201d \u8bf4\u7740\u4ece\u6000\u4e2d\u638f\u51fa\u4e00\u672c\u5f69\u8272\u7eed\u7eb7\u7684\u4e66\u6765\u3002 \u5c55\u767d\u4e00\u770b\uff0c\u4e0d\u7531\u54d1\u7136\u5931\u7b11\u9053\uff1a\u201c\u8fd9\u4e0d\u662f\u6211\u7684\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\u5417\uff1f\u7fe0\u59b9\u8bf4\u7684\u597d\u4e1c\u897f\uff0c \u5c31\u662f\u8fd9\u4e2a\u5417\uff1f\u201d \u7fe0\u7fe0\u8138\u5b54\u65e0\u7aef\u4e00\u7ea2\uff0c\u9053\uff1a\u201c\u4e0d\u4ec5\u662f\u8fd9\u518c\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\uff0c\u4f60\u7ffb\u5f00\u4e00\u9875\u770b\u770b\uff01\u201d \u5c55\u767d\u4e0d\u89e3\uff0c\u5fc3\u60f3\uff0c\u8fd9\u4e0a\u8fb9\u7684\u56fe\u753b\uff0c\u4e0d\u770b\u4e0d\u770b\uff0c\u81ea\u5df1\u4e5f\u770b\u4e86\u767e\u6570\u5341\u6b21\u4e86\uff0c\u4e0a\u8fb9\u7684\u6697\u5b57\uff0c\u7528 \u624b\u66f4\u4e0d\u77e5\u89e6\u6478\u4e86\u591a\u5c11\u904d\u6570\uff0c\u4e0d\u8bba\u662f\u7741\u5f00\u773c\u775b\u770b\uff0c\u8fd8\u662f\u95ed\u4e0a\u773c\u775b\u6478\uff0c\u81ea\u5df1\u53ef\u4ee5\u8bf4\u662f\u719f\u4e60\u65e0\u6bd4\uff0c \u4e0d\u7528\u7ffb\u5f00\u6249\u9875\uff0c\u4fbf\u77e5\u9053\u5176\u4e2d\u662f\u4e9b\u4ec0\u4e48\u4e86\u3002 \u4f46\u770b\u7fe0\u7fe0\u8bf4\u5f97\u610f\u8bda\uff0c\u867d\u7136\u660e\u77e5\u9053\u5176\u4e2d\u662f\u5996\u51b6\u65e0\u6bd4\u7684\u201c\u5929\u4ed9\u9b54\u5973\u201d\u7684\u88f8\u4f53\u753b\u50cf\uff0c\u8fd8\u662f\u4f9d\u8a00 \u7ffb\u5f00\u4e86\u6249\u9875\uff01 \u5634\u4e2d\u4ecd\u7981\u4e0d\u4f4f\u7591\u60d1\u5730\u95ee\u9053\uff1a\u201c\u8fd9\u6709\u4ec0\u4e48\u597d\u770b\uff1f\u6211\u5df2\u7ecf\u770b\u8fc7\u591a\u6b21\u4e86\u2026\u2026\u201d \u8c01\u77e5\u5c55\u767d\u8bdd\u8fd8\u672a\u8bf4\u5b8c\uff0c\u67f3\u7fe0\u7fe0\u5a07\u8eaf\u4e00\u8f6c\uff0c\u53c8\u628a\u88f9\u5728\u8eab\u4e0a\u7684\u5929\u8695\u4e1d\u7ef8\u62b1\u8131\u4e86\u4e0b\u6765\uff0c\u63a5\u7740\u6446 \u8170\u626d\u81c0\uff0c\u8d64\u88f8\u7684\u80f4\u4f53\u5982\u4e00\u6761\u7075\u86c7\u4e00\u822c\uff0c\u5a49\u8f6c\u626d\u52a8\uff0c\u8bf4\u4e0d\u51fa\u7684\u98ce\u60c5\u4e07\u79cd\uff0c\u4e73\u6d6a\u81c0\u6ce2\uff0c\u51e0\u4e4e\u4f7f\u5c55 \u767d\u795e\u9b42\u4e3a\u4e4b\u4e00\u8361\uff01 \u5c24\u5176\u7fe0\u7fe0\u6b64\u65f6\u8138\u4e0a\u7684\u98ce\u60c5\uff0c\u7ade\u6d41\u9732\u65e0\u9650\u7684\u5996\u6cbb\u8273\u8361\u4e4b\u6001\uff0c\u8fd9\u54ea\u91cc\u8fd8\u50cf\u7eaf\u6d01\u5929\u771f\u7684\u5c11\u5973\uff0c \u5206\u660e\u662f\u51b6\u8361\u65e0\u6bd4\u7684\u6d6a\u5987\u6deb\u5a03\uff01 \u5c55\u767d\u5148\u662f\u4e00\u60ca\u7ee7\u4e4b\u4e00\u6012\uff0c\u8ddf\u7740\u800c\u6765\u7684\u662f\u5982\u91ce\u706b\u71ce\u539f\u7eb7\u6b32\u706b\u6625\u60c5\uff01 \u8fd9\u6cb8\u817e\u7684\u6b32\u706b\uff0c\u6765\u5f97\u662f\u90a3\u6837\u5feb\uff0c\u4ed6\u7684\u60ca\u6012\u60c5\u7eea\u7ade\u4e0d\u80fd\u9632\u6b62\u6b32\u706b\u9ad8\u6da8\uff0c\u53ea\u611f\u5468\u8eab\u8840\u6db2\u8d32 \u5f20\uff0c\u4e00\u822c\u70ed\u6d41\u4ece\u4e39\u7530\u76f4\u8fbe\u6ce5\u4e38\uff0c\u65cc\u65d7\u6447\u4e3e\uff0c\u51e0\u4e4e\u628a\u6301\u4e0d\u4f4f\u2026\u2026 \u5e78\u4e8f\u67f3\u7fe0\u7fe0\u8361\u6001\u4e00\u5c55\u5373\u6536\uff0c\u56de\u624b\u53c8\u62ab\u4e0a\u5929\u8695\u4e1d\u888d\uff0c\u5a07\u7f9e\u65e0\u9650\u5730\u7b11\u9053\uff1a\u201c\u767d\u54e5\u54e5\uff01\u4f60\u8bf4\u8fd9 \u7b97\u4e0d\u7b97\u6700\u597d\u770b\u7684\uff1f\u201d \u5c55\u767d\u60ca\u9b42\u752b\u5b9a\uff0c\u52aa\u529b\u6536\u6444\u5fc3\u795e\uff0c\u6697\u8fd0\u771f\u6c14\uff0c\u8c03\u606f\u4e00\u756a\u65b9\u628a\u9ad8\u6da8\u7684\u6b32\u706b\u5e73\u606f\u4e86\u4e0b\u53bb\uff0c\u4e0d\u7531 \u53f9\u9053\uff1a\u201c\u7fe0\u59b9\uff0c\u4f60\u662f\u4e00\u4e2a\u5723\u6d01\u7684\u5c11\u5973\uff0c\u4eca\u540e\u6211\u4e0d\u5e0c\u671b\u4f60\u518d\u505a\u51fa\u5982\u6b64\u4e11\u6076\u2026\u2026\u201d \u7fe0\u7fe0\u7b11\u9053\uff1a\u201c\u767d\u54e5\u54e5\uff01\u8fd8\u81ea\u4ee5\u4e3a\u6b63\u7ecf\uff0c\u96be\u9053\u4f60\u53ea\u662f\u770b\u5916\u8868\uff0c\u6ca1\u6709\u770b\u5230\u5185\u5bb9\u5417\uff1f\u201d \u5c55\u767d\u832b\u7136\u4e0d\u89e3\uff0c\u6014\u4e86\u4e00\u4e0b\u4e86\u773c\u5149\u53c8\u843d\u5728\u7ffb\u5f00\u7684\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\u7684\u753b\u9875\u4e0a\uff0c\u53ea\u89c1\u90a3\u6829 \u6829\u5982\u751f\u7684\u201c\u5929\u4ed9\u9b54\u5973\u201d\u753b\u50cf\uff0c\u626d\u8170\u6446\u81c0\uff0c\u4e73\u6d6a\u81c0\u6ce2\uff0c\u5c24\u5176\u8138\u4e0a\u90a3\u51b6\u8361\u6625\u60c5\uff0c\u6b63\u662f\u7fe0\u7fe0\u521a\u624d\u6240 \u505a\u7684\uff0c\u7ef4\u5999\u7ef4\u8096\uff0c\u4e00\u4e1d\u4e0d\u723d\uff01 \u5c55\u767d\u8be7\u7136\u9053\uff1a\u201c\u59b9\u59b9\u521a\u624d\u505a\u7684\u5c31\u662f\u8fd9\u2018\u5929\u4ed9\u56fe\u5973\u2019\u7684\u753b\u50cf\u5427\uff01\u4f46\u8fd9\u53c8\u6709\u4ec0\u4e48\u542b\u610f\u5462\uff1f\u201d \u7fe0\u7fe0\u6b63\u5bb9\u9053\uff1a\u201c\u767d\u54e5\u54e5\uff01\u4f60\u77e5\u9053\u6211\u7684\u5e08\u7236\u662f\u8c01\u5417\uff1f\u201d \u5c55\u767d\u9053\uff1a\u201c\u59b9\u59b9\u6ca1\u8ddf\u6211\u8bf4\u8fc7\uff0c\u6211\u600e\u4f1a\u77e5\u9053\uff1f\u201d \u7fe0\u7fe0\u9053\uff1a\u201c\u6211\u7684\u5e08\u7236\u5c31\u662f\u2018\u5929\u4ed9\u9b54\u5973\u2019\uff01\u201d \u5c55\u767d\u618e\u7136\u9053\uff1a\u201c\u90a3\u2026\u2026\u4e0d\u53ef\u80fd\u5427\uff01\u2018\u5929\u4ed9\u9b54\u5973\u2019\u636e\u4eca\u5df2\u6709\u4e8c\u767e\u4f59\u5e74\uff0c\u5979\u600e\u80fd\u6d3b\u5230\u73b0 \u5728\u2026\u2026\u201d \u7fe0\u7fe0\u767d\u4e86\u5c55\u767d\u4e00\u773c\u9053\uff1a\u201c\u6211\u7684\u8bdd\uff0c\u4f60\u8001\u662f\u8d77\u7591\u5fc3\uff0c\u96be\u9053\u6211\u4f1a\u9a97\u4f60\u5417\uff1f\u800c\u4e14\uff0c\u4e5f\u4e0d\u80fd\u62ff\u6069 \u5e08\u6765\u8bf4\u8c0e\u554a\uff01\u201d \u5c55\u767d\u89c1\u5979\u6025\u4e86\uff0c\u5fd9\u6539\u53e3\u9053\uff1a\u201c\u8fd9\u5148\u4e0d\u8c08\uff0c\u4f60\u90a3\u753b\u50cf\u7684\u59ff\u6001\uff0c\u8fd8\u6709\u4ec0\u4e48\u7528\u610f\u5417\u201d \u7fe0\u7fe0\u9053\uff1a\u201c\u8fd9\u753b\u50cf\u672c\u662f\u6211\u5e08\u7236\u4fee\u70bc\u7684\u4e00\u79cd\u7279\u6b8a\u6b66\u529f\uff0c\u540d\u4e3a\u2018\u59f9\u5973\u8ff7\u9b42\u5927\u6cd5\u2019\uff0c\u636e\u8bf4\u5389\u5bb3 \u65e0\u6bd4\uff0c\u4e0d\u8bba\u6b66\u529f\u591a\u4e48\u9ad8\u5f3a\u4e4b\u58eb\uff0c\u4e5f\u96be\u4ee5\u62b5\u6321\uff0c\u5c31\u662f\u4fee\u70bc\u81f3\u5fc3\u5982\u7a3f\u6728\u6b7b\u7070\u7684\u9053\u884c\u9ad8\u50e7\uff0c\u4e5f\u65e0\u6cd5 \u4e0e\u4e4b\u6297\u8861\u2026\u2026\u201d \u7fe0\u7fe0\u8bf4\u81f3\u6b64\u5904\uff0c\u5c55\u767d\u5ffd\u7136\u8bb0\u8d77\u5728\u5c0f\u5b64\u5c71\u77f3\u6d1e\u4e2d\uff0c\u96f7\u5927\u53d4\u6388\u4ed6\u6b64\u4e66\u65f6\uff0c\u6240\u8bf4\u7684\u4e00\u6bb5\u638c\u6545\uff0c \u4e00\u4ee3\u5947\u4eba\u201c\u53ea\u773c\u90ce\u541b\u201d\u5982\u4f55\u53d7\u60d1\uff0c\u4ee5\u81f3\u8d70\u706b\u5165\u9b54\u7684\u7ecf\u8fc7\uff0c\u4e0d\u7531\u6982\u7136\u9053\uff1a\u201c\u4f60\u5e08\u7236\u8fd9\u2018\u59f9\u5973\u8ff7 \u9b42\u5927\u6cd5\u2019\u4e0d\u7ba1\u600e\u4e48\u5389\u5bb3\uff0c\u7a76\u7adf\u4e0d\u80fd\u7b97\u6b66\u529f\u6b63\u9053\uff0c\u53ea\u80fd\u7b97\u5de6\u9053\u65c1\u95e8\uff0c\u800c\u4e14\u2026\u2026\u201d \u5c55\u767d\u4e0b\u8fb9\u7684\u8bdd\u662f\uff1a\u201c\u800c\u4e14\uff0c\u4e5f\u4e0d\u6b63\u5f53\uff01\u201d\u4f46\u8bdd\u5230\u5507\u8fb9\uff0c\u89c9\u5f97\u5982\u6b64\u76f4\u8a00\uff0c\u5bf9\u7fe0\u7fe0\u7684\u5e08\u7236\u5927 \u6709\u4e0d\u656c\uff0c\u56e0\u6b64\u4f4f\u53e3\u672b\u8bf4\u3002 \u4f46\u53ea\u6b64\u4e5f\u628a\u7fe0\u7fe0\u8bf4\u607c\u4e86\uff0c\u53ea\u89c1\u5979\u4e00\u77aa\u773c\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\u65c1\u95e8\u6b63\u95e8\uff1f\u6bd4\u529f\u8bba\u6b66\uff0c\u80dc\u8005\u4e3a\u5f3a\uff0c \u7528\u8272\u76f8\u5236\u654c\uff0c\u4e0e\u7528\u5200\u5251\u6740\u4eba\u6709\u4ec0\u4e48\u4e24\u6837\uff1f\u201d \u672c\u6765\u7fe0\u7fe0\u5df2\u7ecf\u4e0d\u5feb\uff0c\u82e5\u662f\u5706\u6ed1\u4e4b\u4eba\uff0c\u4e3a\u8ba8\u7231\u4fa3\u7231\u5fc3\uff0c\u6b64\u65f6\u5fc5\u4f1a\u6539\u53e3\uff0c\u4f46\u5c55\u767d\u5929\u751f\u6b63\u76f4\u4e0d \u963f\uff0c\u867d\u770b\u51fa\u7fe0\u7fe0\u7740\u607c\uff0c\u4ecd\u7387\u76f4\u8a00\u9053\uff1a\u201c\u8fd9\u5b9e\u5728\u5927\u6709\u5206\u522b\uff0c\u8b6c\u5982\u4e00\u4e2a\u4eba\u8981\u5230\u67d0\u4e00\u4e2a\u5730\u65b9\uff0c\u653e\u6b63 \u8def\u4e0d\u8d70\uff0c\u800c\u8d70\u5165\u6b67\u9014\uff0c\u867d\u7136\u5f00\u59cb\u6240\u5dee\u65e0\u51e0\uff0c\u4f46\u5374\u662f\u5dee\u4e4b\u6beb\u5398\uff0c\u8c2c\u4e4b\u5343\u91cc\u2026\u2026\u201d \u7fe0\u7fe0\u9053\uff1a\u201c\u6211\u4e0d\u8ddf\u4f60\u8bb2\u90a3\u4e48\u591a\u5927\u9053\u7406\uff0c\u73b0\u5728\u6211\u53ea\u8981\u544a\u8bc9\u4f60\uff0c\u5728\u8fd9\u4e00\u767e\u96f6\u516b\u5e45\u2018\u5929\u4ed9\u9b54 \u5973\u2019\u7684\u753b\u50cf\u4e4b\u4e2d\uff0c\u5374\u9690\u85cf\u7740\u4e00\u5957\u76f4\u53e4\u7edd\u4eca\u7684\u9ad8\u6df1\u5965\u5999\u7684\u638c\u6cd5\uff0c\u4e00\u5957\u7075\u5de7\u65e0\u6bd4\u7684\u8eab\u6cd5\uff0c\u548c\u4e00\u5957 \u53d8\u5e7b\u4e07\u7aef\u7684\u6b65\u6cd5\u3002\u638c\u6cd5\u4e3a\u2018\u5929\u4f5b\u964d\u9b54\u638c\u2019\uff0c\u8eab\u6cd5\u4e3a\u2018\u65e0\u8272\u65e0\u76f8\u8eab\u2019\uff0c\u6b65\u6cd5\u4e3a\u2018\u5343\u5e7b\u98d8\u9999 \u6b65\u2019\uff0c\u5176\u4e2d\u4efb\u4f55\u4e00\u9879\uff0c\u5747\u582a\u79f0\u5f97\u8d77\u662f\u6b66\u6797\u7edd\u54cd\uff0c\u5982\u679c\u518d\u628a\u2018\u65e0\u4f5b\u964d\u9b54\u638c\u2019\u4ee3\u4e4b\u4ee5\u5251\uff0c\u6f14\u91ca\u51fa \u6765\u4fbf\u662f\u2018\u5929\u4f5b\u964d\u9b54\u5251\u2019\uff0c\u6709\u4e86\u8fd9\u51e0\u5957\u7edd\u4e16\u6b66\u529f\uff0c\u76f4\u53ef\u7765\u7768\u5929\u4e0b\uff0c\u541b\u4e34\u4e07\u65b9\u4e86\uff01\u767d\u54e5\u54e5\uff0c\u4f60\u60f3 \u60f3\u770b\uff0c\u59b9\u59b9\u8fd9\u4e0d\u662f\u7ed9\u4f60\u770b\u5230\u4e00\u4ef6\u6700\u597d\u7684\u4e1c\u897f\u5417\uff1f\u6ca1\u60f3\u5230\u4f60\u72d7\u54ac\u5415\u6d1e\u5bbe\uff0c\u4e0d\u8bc6\u597d\u4eba\u5fc3\uff0c\u5012\u8ddf\u59b9 \u59b9\u8bf4\u6559\u8d77\u6765\u4e86\uff01\u201d \u7fe0\u7fe0\u6ed4\u6ed4\u8bf4\u4e0b\u53bb\uff0c\u5c55\u767d\u6108\u542c\u6108\u60ca\u5947\uff0c\u89c1\u7fe0\u7fe0\u505c\u4e0b\u53e3\u6765\uff0c\u624d\u5598\u4e86\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u771f\u7684\u5417\uff1f \u6211\u2026\u2026\u201d \u5c55\u767d\u672c\u60f3\u8bf4\u201c\u628a\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\u770b\u4e86\u8fd9\u4e48\u4e45\uff0c\u600e\u80fd\u4e00\u70b9\u5934\u7eea\u4e5f\u6ca1\u6709\u770b\u51fa\u6765\uff1f\u201d\u4f46\u4ed6 \u7684\u8bdd\u5c1a\u672a\u8bf4\u5b8c\uff0c\u7fe0\u7fe0\u5df2\u5a07\u55d4\u9053\uff1a\u201c\u6211\u6bcf\u6b21\u8bb2\u8bdd\uff0c\u767d\u54e5\u54e5\u90fd\u4e0d\u76f8\u4fe1\uff0c\u597d\uff0c\u7b97\u627e\u767d\u8bf4\uff0c\u6211\u4e0d\u8c08 \u4e86\uff01\u201d \u8bf4\u7f62\u626d\u8f6c\u5a07\u8eaf\u56de\u8eab\u4fbf\u8d70\u3002 \u5c55\u767d\u89c1\u7fe0\u7fe0\u4f7f\u6c14\u751f\u55d4\uff0c\u5fd9\u98d8\u8eab\u6321\u4f4f\u7fe0\u7fe0\u8eab\u524d\uff0c\u6df1\u6df1\u65bd\u4e86\u4e00\u793c\uff0c\u9053\uff1a\u201c\u7fe0\u59b9\u59b9\u4e0d\u8981\u751f\u6c14\uff0c \u54e5\u54e5\u5b8c\u5168\u76f8\u4fe1\u4f60\u7684\uff0c\u6b63\u8981\u5411\u59b9\u59b9\u9886\u53d7\u6559\u76ca\u54e9\uff0c\u90fd\u662f\u602a\u54e5\u54e5\u4e0d\u4f1a\u8bf4\u8bdd\uff0c\u73b0\u5728\u54e5\u54e5\u4e3a\u7fe0\u59b9\u59b9\u8d54 \u793c\uff0c\u5c31\u8bf7\u59b9\u59b9\u591a\u539f\u8c05\u5427\uff01\u201d \u6ca1\u60f3\u5230\u5c55\u767d\u7d20\u5e38\u6728\u8bb7\uff0c\u5982\u4eca\u7adf\u8c08\u5410\u8da3\u96c5\uff0c\u4ea6\u5e84\u4ea6\u8c10\uff0c\u53c8\u52a0\u4e0a\u4ed6\u6253\u8eac\u4f5c\u63d6\uff0c\u72b6\u751a\u6ed1\u7a3d\uff0c\u7ade \u628a\u4e2a\u67f3\u7fe0\u7fe0\u9017\u5f97\u5fcd\u4fca\u4e0d\u4f4f\uff0c\u5657\u54e7\u4e00\u58f0\u7b11\u4e86\u51fa\u6765\u3002 \u5c55\u81ea\u89c1\u7fe0\u7fe0\u56de\u55d4\u4f5c\u559c\uff0c\u8d81\u52bf\u8bf4\u9053\uff1a\u201c\u662f\u4e0d\u662f\u59b9\u59b9\u521a\u624d\u6446\u7684\u67b6\u5f0f\uff0c\u5c31\u662f\u4ec0\u4e48\u2018\u5929\u4f5b\u964d\u9b54 \u638c\u2019\u7684\u4e00\u62db\uff1f\u201d \u7fe0\u7fe0\u6536\u4f4f\u7b11\u9053\uff1a\u201c\u4e0d\u662f\uff0c\u521a\u624d\u90a3\u662f\u2018\u59f9\u5973\u8ff7\u9b42\u5927\u6cd5\u2019\u7684\u4e00\u5f0f\uff0c\u540d\u4e3a\u2018\u7389\u4f53\u98d8\u9999\u2019\uff0c\u4f46\u9488 \u5bf9\u2018\u7389\u4f53\u98d8\u9999\u2019\u7684\u62db\u5f0f\uff0c\u5bf9\u624b\u51fa\u638c\u7ad6\u6307\u6a2a\u524a\uff0c\u5c31\u6210\u4e86\u2018\u5929\u4f5b\u964d\u9b54\u638c\u2019\u7684\u7b2c\u4e00\u62db\u2018\u4f5b\u7956\u964d \u5ea7\u2019\uff0c\u800c\u4e14\u6643\u8eab\u9519\u6b65\uff0c\u4e5f\u5c31\u628a\u2018\u65e0\u8272\u65e0\u76f8\u2018\u5343\u5e7b\u98d8\u9999\u2019\u8eab\u6cd5\u6b65\u90fd\u65bd\u5c55\u51fa\u6765\u4e86\u3002\u201d \u5c55\u767d\u8bb2\u5fc3\u673a\u867d\u4e0d\u5927\u9ad8\u660e\uff0c\u4f46\u5bf9\u4e60\u6b66\u7684\u609f\u6027\u4e0a\u5374\u662f\u9ad8\u4eba\u4e00\u7b49\uff0c\u542c\u7fe0\u7fe0\u4e00\u8bf4\uff0c\u7acb\u523b\u4f1a\u610f\uff0c \u9053\uff1a\u201c\u8fd9\u6837\u8bf4\u6765\uff0c\u7ec3\u4e60\u8fd9\u2018\u5929\u4f5b\u964d\u9b54\u638c\u2019\u4e00\u5b9a\u8981\u4e8c\u4eba\u5408\u8d77\u6765\u7ec3\u624d\u884c\u4e86\uff1f\u201d\u7fe0\u7fe0\u9053\uff1a\u201c\u8fd9\u56de\u7b97 \u4f60\u806a\u660e\uff0c\u8fd9\u300a\u5929\u4f5b\u5377\u300b\u4e0a\u9690\u85cf\u7684\u4e09\u95e8\u5947\u529f\uff0c\u4e0d\u4f46\u4e00\u5b9a\u8981\u4e8c\u4eba\u5408\u7ec3\uff0c\u800c\u4e14\u8fd8\u975e\u8981\u8ddf\u59b9\u59b9\u5408\u7ec3\u4e0d \u53ef\uff0c\u4e0d\u662f\u59b9\u59b9\u5938\u53e3\uff0c\u5929\u4e0b\u867d\u5927\uff0c\u4f46\u9664\u4e86\u59b9\u59b9\u4e00\u4eba\uff0c\u4e0d\u505a\u7b2c\u4e8c\u4eba\u60f3\u2026\u2026\u540c\u65f6\u2026\u2026\u201d \u7fe0\u7fe0\u81f3\u6b64\uff0c\u5927\u773c\u73e0\u4e00\u8f6c\uff0c\u542b\u7740\u8be1\u8c32\u7684\u7b11\u5bb9\u9053\uff1a\u201c\u5929\u4e0b\u4e5f\u53ea\u6709\u767d\u54e5\u4e00\u4eba\uff0c\u80fd\u7ec3\u5230\u8fd9\u2018\u5929\u4f5b \u964d\u9b54\u2019\u7edd\u4e16\u6b66\u529f\u3002\u201d \u7fe0\u7fe0\u89c1\u5c55\u767d\u7591\u60d1\u5730\u671b\u7740\u81ea\u5df1\uff0c\u77e5\u9053\u4ed6\u53c8\u8981\u4e0d\u76f8\u4fe1\u81ea\u5df1\u6240\u8bf4\uff0c\u968f\u63a5\u4e0b\u53bb\u9053\uff1a\u201c\u767d\u54e5\u54e5\uff0c\u4f60 \u4e0d\u8981\u4e0d\u4fe1\uff01\u56e0\u4e3a\u6211\u7684\u6069\u5e08\u5df2\u7ecf\u5750\u5316\uff0c\u2018\u5929\u4ed9\u9b54\u5973\u2019\u7684\u2018\u59f9\u5973\u8ff7\u9b42\u5927\u6cd5\u2019\uff0c\u666e\u5929\u4e0b\u6ca1\u6709\u7b2c\u4e8c\u4e2a \u4eba\u518d\u4f1a\uff0c\u6240\u4ee5\u975e\u59b9\u59b9\u4e0d\u884c\u3002\u4f46\u82e5\u4e0d\u662f\u4e3a\u4e86\u7ed9\u767d\u54e5\u5582\u62db\uff0c\u59b9\u59b9\u5c82\u80af\u727a\u7272\u2026\u2026\u8272\u76f8\uff1f\u2026\u2026\u201d \u5c55\u767d\u81f3\u6b64\u604d\u7136\u5927\u609f\uff0c\u5fd9\u53c8\u6df1\u65bd\u4e00\u793c\uff0c\u9053\uff1a\u201c\u7fe0\u59b9\uff0c\u6211\u660e\u767d\u5566\uff01\u4e0d\u4f46\u6b64\u2018\u5929\u4f5b\u964d\u9b54\u2019\u7edd \u5b66\uff0c\u975e\u4f60\u7ed9\u6211\u4f5c\u7ec3\u4e60\u4e0d\u4e86\uff0c\u800c\u4e14\uff0c\u82e5\u6ca1\u6709\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\uff0c\u4e5f\u662f\u4e0d\u884c\uff0c\u53ef\u89c1\u771f\u662f\u5929\u52a9\u6211 \u4e5f\uff01\u59b9\u59b9\uff0c\u95f2\u8bdd\u5c11\u8bf4\uff0c\u5feb\u5e2e\u6211\u7ec3\u6b64\u7edd\u4e16\u6b66\u529f\u5427\uff01\u201d \u7fe0\u7fe0\u9053\uff1a\u201c\u5e2e\u4f60\u7ec3\u6b66\u529f\u4e0d\u96be\uff0c\u767d\u54e5\u54e5\uff0c\u6b66\u529f\u7ec3\u6210\u4e4b\u540e\uff0c\u4f60\u8be5\u600e\u4e48\u8c22\u6211\uff1f\u201d \u5c55\u767d\u5fc3\u6025\u7ec3\u6b66\u529f\uff0c\u53e3\u4e0d\u62e9\u8a00\u5730\u9053\uff1a\u201c\u4e00\u5207\u90fd\u4f9d\u59b9\u59b9\uff01\u53ea\u8981\u5e2e\u54e5\u54e5\u7ec3\u6210\u6b66\u529f\uff0c\u59b9\u59b9\u8981\u600e\u6837 \u5168\u884c\uff01\u201d \u7fe0\u7fe0\u53cc\u7709\u4e00\u6311\u9053\uff1a\u767d\u54e5\u54e5\uff0c\u8fd9\u662f\u4f60\u4eb2\u53e3\u8bf4\u7684\uff01\u4e8b\u540e\u53ef\u4e0d\u80fd\u8bf4\u4e86\u4e0d\u7b97\uff01\u201d \u5c55\u767d\u9053\uff1a\u201c\u5927\u4e08\u592b\u4e00\u8a00\u65e2\u51fa\uff0c\u5982\u767d\u67d3\u7682\uff0c\u5c82\u53ef\u8bf4\u4e86\u4e0d\u7b97\uff0c\u4e0d\u8981\u518d\u591a\u8bf4\u4e86\uff0c\u5feb\u5e2e\u6211\u7ec3 \u529f\uff01\u201d \u7fe0\u7fe0\u5ffd\u7136\u7eb5\u58f0\u5927\u7b11\u8d77\u6765\uff0c\u4e00\u4e2a\u5973\u5b69\u5b50\u8fd9\u6837\u72c2\u7b11\uff0c\u771f\u4f7f\u4eba\u5403\u60ca\uff0c\u5c55\u767d\u695e\u7136\uff0c\u90a3\u7f8e\u903e\u5929\u4ed9\u7684 \u7fe0\u7fe0\u5374\u4e00\u7529\u624b\u628a\u8eab\u4e0a\u8863\u670d\u8131\u5149\uff0c\u5a07\u5a9a\u5730\u8bf4\u9053\uff1a\u6765\u5427\uff01\u767d\u54e5\u54e5\uff0c\u6211\u4eec\u5f00\u59cb\u7ec3\u529f\u592b\u2026\u2026\u201d \u7b2c\u56db\u5341\u4e8c\u7ae0\u3000\u52ab\u9556\u8005\u662f\u8c01\uff1f>> \u53e4\u9f99\u300a\u5251\u5ba2\u884c\u300b \u7b2c\u56db\u5341\u4e8c\u7ae0\u3000\u52ab\u9556\u8005\u662f\u8c01\uff1f \u8f66\u78f7\u78f7\uff0c\u9a6c\u5578\u5578\uff0c\u4e00\u6761\u9ec4\u571f\u5927\u8def\uff0c\u72b9\u5982\u4e00\u6761\u5927\u87d2\u86c7\uff0c\u5a49\u8712\u4f38\u5c55\u81f3\u9065\u8fdc\u7684\u5929\u8fb9\uff01 \u5927\u8def\u4e0a\u5c18\u57c3\u98de\u626c\uff0c\u6b63\u6709\u4e00\u8def\u9556\u8f66\u8fe4\u9026\u7ecf\u8fc7\uff01 \u8fd9\u9556\u8f66\u90fd\u662f\u6cb9\u7bf7\u94c1\u8f6e\uff0c\u5927\u9a6c\u957f\u9a77\uff0c\u8db3\u6709\u6570\u5341\u8f86\u4e4b\u591a\uff0c\u8f66\u8f95\u4e0a\u90fd\u63d2\u7740\u4e00\u53ea\u7ee3\u6709\u624b\u638c\u7684\u4e09\u89d2 \u65d7\uff0c\u968f\u98ce\u98d8\u626c\uff0c\u730e\u730e\u4f5c\u54cd\uff01 \u9556\u8f66\u524d\u540e\u5de6\u53f3\u90fd\u6709\u968f\u884c\u9556\u5e08\uff0c\u9a91\u9a6c\u536b\u62a4\uff0c\u770b\u90a3\u4e9b\u9556\u5e08\u4e00\u4e2a\u4e2a\u8eab\u6750\u9b41\u68a7\uff0c\u53cc\u773c\u7cbe\u5149\u56db\u5c04\uff0c \u4fbf\u77e5\u60e7\u662f\u6b66\u529f\u4e0d\u5f31\u7684\u7ec3\u5bb6\u5b50\uff01 \u5728\u9556\u8f66\u524d\u65b9\u767e\u6b65\u4e4b\u5904\uff0c\u6709\u516b\u540d\u8d9f\u5b50\u624b\uff0c\u4e24\u540d\u4e00\u6311\uff0c\u5171\u5206\u56db\u6392\uff0c\u5728\u524d\u558a\u9556\u5f00\u8def\uff01 \u53ea\u542c\u516b\u540d\u8d9f\u5b50\u624b\u62c9\u957f\u4e86\u58f0\u97f3\u4e00\u9012\u4e00\u58f0\u5730\u558a\u9053\uff1a \u201c\u71d5\u2014\u2014\u96c4\u2014\u2014\u4eac\u5146\u2014\u2014 \u6211\u2014\u2014\u6b66\uff0c\u2014\u2014\u7ef4\u2014\u2014\u626c\u201d \u884c\u5bb6\u4e00\u542c\uff0c\u4fbf\u77e5\u662f\u201c\u71d5\u4eac\u9556\u5c40\u201d\u7684\u9556\u8f66\u5728\u6b64\u5730\u8def\u8fc7\u3002 \u5f53\u7136\uff0c\u82e5\u662f\u518d\u4e00\u770b\u90a3\u9556\u8f66\u4e0a\u63d2\u7684\u201c\u94c1\u638c\u201d\u9556\u65d7\uff0c\u5c31\u66f4\u77e5\u9053\u662f\u201c\u94c1\u638c\u9707\u6cb3\u6714\u201d\u8339\u8001\u9556\u5934\u7684 \u9556\u8f66\u4e86\uff01 \u9556\u8f66\u524d\u6709\u5341\u6570\u540d\u9556\u5e08\uff0c\u653e\u7559\u5f90\u884c\uff0c\u4e3a\u9996\u662f\u4e00\u4e2a\u767d\u53d1\u82cd\u82cd\u5e74\u7ea6\u516d\u65ec\u7684\u8001\u9556\u5e08\uff0c\u6b63\u662f\u201c\u94c1\u638c \u9707\u6cb3\u6714\u201d\u8339\u8001\u9556\u5934\u3002 \u8339\u8001\u9556\u5934\u5f88\u4e45\u4e0d\u51fa\u9a6c\u4e86\uff0c\u8fd9\u780d\u4eb2\u81ea\u51fa\u9a6c\uff0c\u60f3\u5fc5\u8fd9\u8d9f\u9556\u662f\u5f88\u91cd\u8981\u4e86\u3002 \u9760\u8339\u8001\u9556\u5934\u5de6\u9996\uff0c\u662f\u4e00\u4e2a\u9762\u56e2\u56e2\u7684\u5927\u80d6\u5b50\uff0c\u8eab\u7a7f\u516c\u670d\uff0c\u8111\u6ee1\u80a0\u80a5\uff0c\u770b\u6837\u5b50\u5486\u516d\u6247\u95e8\u5b98\u996d \u7684\u516c\u4eba\uff0c\u8339\u8001\u9556\u5934\u7684\u53f3\u9996\uff0c\u5374\u662f\u4e00\u4e2a\u7cbe\u76ae\u5be1\u7626\u7684\u7626\u5b50\uff0c\u548c\u90a3\u80d6\u5b50\u6210\u4e3a\u4e00\u4e2a\u9c9c\u660e\u7684\u5bf9\u6bd4\uff0c\u751f\u50cf \u5982\u7334\uff0c\u4f5c\u6355\u5934\u6253\u626e\u3002 \u8fd9\u4e00\u80d6\u4e00\u7626\u4e24\u4e2a\u6c49\u5b50\uff0c\u6b63\u662f\u6cb3\u6714\u540d\u6355\u201c\u80d6\u7075\u5b98\u201d\u90d1\u4f2f\u8c61\uff0c\u4e0e\u4eac\u57ce\u4fa0\u6355\u201c\u77f3\u7334\u201d\u4faf\u9e9f\u5584\u3002 \u770b\u6837\u5b50\uff0c\u8fd9\u8d9f\u9556\u6709\u516c\u4eba\u968f\u884c\uff0c\u60f3\u5fc5\u4e5f\u662f\u4e3a\u516c\u5bb6\u4fdd\u7684\u5b98\u9556\u4e86\u3002 \u6b64\u9645\u65f6\u503c\u521d\u590f\uff0c\u867d\u6ca1\u6709\u76db\u590f\u7684\u9177\u70ed\uff0c\u4f46\u9a84\u9633\u5f53\u9876\uff0c\u4f17\u4eba\u8111\u95e8\u4e0a\u90fd\u5df2\u5fae\u5fae\u89c1\u6c57\u3002 \u8339\u8001\u9556\u5934\u628a\u9a6c\u8fde\u5761\u7684\u5927\u8349\u5e3d\uff0c\u6380\u5728\u8111\u540e\uff0c\u76b1\u8d77\u591a\u76b1\u7eb9\u7684\u8138\uff0c\u624b\u642d\u51c9\u7bf7\uff0c\u772f\u773c\u5411\u524d\u9014\u671b\u4e86 \u671b\u3002 \u773c\u89c1\u884c\u8fd1\u4e00\u7247\u9ed1\u9e26\u9e26\u7684\u677e\u6797\uff0c\u8339\u8001\u6e90\u5934\u5e9e\u7709\u4e00\u76b1\uff0c\u5411\u5de6\u53f3\u8bf4\u9053\uff1a\u201c\u4f20\u4ee4\u4e0b\u53bb\uff0c\u8981\u5927\u5bb6\u63d0 \u795e\u6212\u5907\uff01\u201d \u8352\u5c71\u5bc6\u6797\uff0c\u6b63\u662f\u76d7\u532a\u51fa\u6ca1\u5578\u805a\u4e4b\u5904\uff0c\u8339\u8001\u9556\u5934\u7ecf\u9a8c\u8005\u5230\uff0c\u6240\u4ee5\u4e00\u89c1\u524d\u9762\u7684\u5bc6\u677e\u6797\uff0c\u7acb\u523b \u91c7\u53d6\u63aa\u65bd\u3002 \u8d1f\u8d23\u4f20\u4ee4\u7684\u201c\u5feb\u9a6c\u201d\u5218\u4e09\uff0c\u7acb\u523b\u98de\u5feb\u5730\u9a71\u9a6c\u7531\u524d\u5411\u540e\u4f20\u53bb\uff0c\u4e00\u8fb9\u7b56\u9a6c\u98de\u5954\uff0c\u4e00\u8fb9\u53e3\u4e2d\u558a \u9053\uff1a\u201c\u603b\u9556\u5934\u6709\u4ee4\uff0c\u5927\u5bb6\u6212\u5907\uff01\u201d \u4e00\u7247\u5200\u5251\u51fa\u9798\u4e4b\u58f0\uff0c\u5bd2\u5203\u950b\u8292\uff0c\u5728\u70c8\u65e5\u4e0b\u8000\u8000\u6253\u95ea\uff0c\u4f17\u9556\u5e08\u90fd\u628a\u968f\u8eab\u6b66\u5668\u53d6\u4e86\u51fa\u6765\uff0c\u5411 \u56db\u5468\u5168\u795e\u6212\u5907\uff01 \u9556\u8f66\u884c\u5217\u4e2d\u7acb\u523b\u589e\u6dfb\u4e86\u4e00\u822c\u8083\u6740\u4e4b\u6c14\uff0c\u770b\u90a3\u8b66\u536b\u68ee\u4e25\uff0c\u4ee4\u51fa\u5fc5\u884c\u4e4b\u52bf\uff0c\u771f\u6709\u5927\u519b\u4e34\u9635\u7684 \u6c14\u6d3e\uff01 \u201c\u80d6\u7075\u5b98\u201d\u90d1\u4f2f\u8c61\u80d6\u8138\u4e0a\u5bbd\u6170\u7684\u7b11\u5bb9\uff0c\u5411\u8339\u8001\u9556\u5934\u7b11\u9053\uff1a\u201c\u5230\u5e95\u662f\u8339\u8001\u9556\u5934\u4eb2\u81ea\u51fa\u9a6c\uff0c \u8fd9\u822c\u9635\u52bf\uff0c\u53eb\u6211\u4eec\u51fa\u5dee\u7684\u4eba\u4e5f\u5b89\u5fc3\u591a\u4e86\uff01\u201d \u201c\u77f3\u7334\u201d\u4faf\u9e9f\u5584\u4e5f\u63a5\u53e3\u9053\uff1a\u201c\u8bf4\u7684\u662f\uff01\u50cf\u4e0a\u4e00\u6b21\u51fa\u9556\uff0c\u7531\u4e00\u4e2a\u65b0\u51fa\u9053\u7684\u5e74\u8f7b\u9556\u5e08\uff0c\u540d\u53eb \u5c55\u767d\u7684\u62bc\u9556\uff0c\u90a3\u4e00\u8def\u4e0a\u771f\u53eb\u4eba\u63d0\u5fc3\u540a\u80c6\uff01\u201d \u201c\u77f3\u7334\u201d\u4e00\u63d0\u5230\u5c55\u767d\uff0c\u201c\u80d6\u7075\u5bab\u201d\u7a81\u7136\u6253\u4e86\u4e2a\u51b7\u6218\uff0c\u53c8\u772f\u8d77\u4e24\u53ea\u7ec6\u773c\u5411\u524d\u9014\u671b\u4e86\u671b\uff0c\u7136 \u540e\u538b\u4f4e\u4e86\u58f0\u97f3\u5728\u8339\u8001\u9556\u5934\u8033\u8fb9\u8bf4\u9053\uff1a\u201c\u542c\u4faf\u5144\u8fd9\u4e00\u63d0\u9192\uff0c\u6211\u5012\u60f3\u8d77\u6765\u4e86\uff0c\u4e0a\u6b21\u5c55\u767d\u62bc\u9556\uff0c\u5c31 \u662f\u5728\u524d\u9762\u8fd9\u5ea7\u5bc6\u6797\u51fa\u7684\u5c94\u5b50\uff01\u8001\u5f53\u5bb6\u7684\uff01\u4f60\u53ef\u8981\u5c0f\u5fc3\u4e86\uff01\u201d \u8339\u8001\u9556\u5934\u51dd\u91cd\u5730\u70b9\u4e86\u70b9\u5934\uff0c\u5e76\u672a\u4f5c\u7b54\u3002 \u53ef\u662f\u201c\u80d6\u7075\u5b98\u201d\u8bdd\u58f0\u867d\u4f4e\uff0c\u5374\u88ab\u8eab\u540e\u4e00\u4e2a\u53eb\u505a\u201c\u6d51\u94c1\u725b\u201d\u5468\u5927\u901a\u7684\u9556\u5e08\u542c\u5230\u4e86\u3002\u4e0a\u6b21\u672c \u8f6e\u5230\u4ed6\u51fa\u9a6c\uff0c\u8339\u8001\u6f02\u5934\u5374\u6d3e\u4e86\u5c55\u767d\uff0c\u4ed6\u5fc3\u4e2d\u2606\u76f4\u4e0d\u670d\u6c14\uff0c\u6070\u597d\u90a3\u8d9f\u9556\u51fa\u4e86\u4e8b\uff0c\u800c\u5c55\u767d\u4e5f\u7531\u6b64 \u5931\u8e2a\uff0c\u8fd9\u65f6\u4ed6\u542c\u201c\u80d6\u7075\u5b98\u201d\u63d0\u8d77\u65e7\u4e8b\uff0c\u6b63\u597d\u501f\u9898\u53d1\u6325\uff0c\u4ece\u9f3b\u54fc\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5e74\u8f7b\u4eba\u600e\u80fd\u5f53 \u5927\u4efb\uff0c\u4e0a\u6b21\u6211\u5c31\u8bf4\u8fc7\u4ed6\u4e0d\u884c\uff0c\u603b\u9556\u5934\u8fd8\u4e0d\u4fe1\uff0c\u7ed3\u679c\u8fd8\u4e0d\u662f\u51fa\u4e86\u4e8b\uff0c\u534a\u9014\u4ed6\u5c31\u5f03\u4e0b\u9556\u8f66\u81ea\u884c\u8d70 \u4e86\uff0c\u4e00\u76f4\u5230\u4eca\u5929\u8fd8\u6ca1\u6709\u97f3\u4fe1\u2026\u2026\u201d \u201c\u6d51\u94c1\u725b\u201d\u8fd8\u8981\u5520\u53e8\u4e0b\u53bb\uff0c\u8339\u8001\u9556\u5934\u5ffd\u7136\u503e0\u8033\u8c1b\u542c\uff0c\u9762\u73b0\u60ca\u8be7\u4e4b\u8272\uff0c\u731b\u7136\u6c89\u559d\u9053\uff1a \u201c\u5468\u5927\u901a\uff01\u4f4f\u53e3\uff01\u201d \u8339\u8001\u9556\u5934\u8fd9\u4e00\u58f0\u6c89\u559d\uff0c\u5a01\u5413\u610f\u5473\u5f88\u91cd\uff0c\u201c\u6d51\u94c1\u725b\u201d\u5468\u5927\u901a\u4e00\u695e\uff0c\u4ed6\u60f3\u4e0d\u5230\u8339\u8001\u9556\u5934\u9762\u8272 \u51dd\u91cd\uff0c\u4fa7\u8033\u5411\u524d\u8c1b\u542c\uff0c\u795e\u8272\u5927\u903e\u5bfb\u5e38\uff01 \u201c\u6d51\u94c1\u725b\u201d\u628a\u51b2\u5728\u53e3\u8fb9\u7684\u6012\u6c14\u5f3a\u538b\u4e0b\u53bb\uff0c\u4e5f\u503e\u8033\u7ec6\u542c\u4e86\u4e00\u4e0b\uff0c\u4e0d\u7531\u714c\u7136\u8272\u53d8\uff01\u539f\u6765\u5728\u524d \u9762\u558a\u9556\u7684\u8d9f\u5b50\u624b\uff0c\u5df2\u7ecf\u8d70\u8fdb\u4e86\u5bc6\u6797\uff0c\u4f466\u5165\u6797\u4ee5\u540e\u5c31\u5931\u53bb\u4e86\u58f0\u54cd\uff01 \u558a\u9556\u7684\u4e0d\u558a\u9556\u4e86\uff0c\u8fd9\u8bc1\u660e\u524d\u9014\u51fa\u4e86\u610f\u5916\uff01 \u201c\u80d6\u7075\u5b98\u201d\u4e0e\u201c\u77f3\u7334\u201d\u4e8c\u4eba\u8fd8\u4e0d\u77e5\u4f55\u6545\uff0c\u89c1\u8339\u8005\u9556\u5934\u559d\u4f4f\u5468\u5927\u901a\uff0c\u9762\u8272\u51dd\u91cd\uff0c\u4e00\u65f6\u7adf\u50bb \u4e86\uff0c\u8fde\u95ee\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u4ec0\u4e48\u4e8b\uff1f\u201d\u2026\u8339\u8001\u9556\u5934\u5411\u4e8c\u516c\u5dee\u671b\u4e86\u4e00\u773c\uff0c\u9762\u8272\u51dd\u91cd\uff0c\u9053\uff1a\u201c\u4e8c\u4f4d\u6355 \u5934\uff0c\u51c6\u5907\u5e94\u53d8\u5427\uff01\u201d \u8bf4\u5b8c\u4e5f\u4e0d\u7ba1\u4e8c\u516c\u5dee\u53cd\u5e94\u5982\u4f55\uff0c\u5708\u9a6c\u56de\u6765\uff0c\u4e3e\u624b\u628a\u9556\u8f66\u505c\u8f66\uff0c\u7acb\u523b\u91c7\u53d6\u4e86\u884c\u52a8\u3002 \u8339\u8001\u9556\u5934\u4e0d\u6127\u662f\u8001\u6c5f\u6e56\uff0c\u53ea\u89c1\u4ed6\u5429\u5490\u9556\u8f66\u5728\u539f\u5730\u56f4\u6210\u4e00\u5708\uff0c\u6570\u5341\u8f6e\u9556\u8f66\uff0c\u8f66\u8f95\u8f66\u5c3e\u76f8 \u63a5\uff0c\u7acb\u523b\u6210\u4e86\u4e00\u4e2a\u5706\u9635\uff0c\u7559\u4e0b\u4e00\u534a\u9556\u5e08\u5b88\u62a4\u9556\u8f66\uff0c\u8339\u8001\u9556\u5934\u4eb2\u81ea\u7387\u9886\u53e6\u5916\u4e00\u534a\u9556\u5e08\uff0c\u98de\u9a6c\u5954 \u5411\u6797\u4e2d\u53bb\u5bdf\u770b\u771f\u50cf\u3002 \u201c\u80d6\u7075\u5b98\u201d\u201c\u77f3\u7334\u201d\uff0c\u81f3\u6b64\u65b9\u7ea6\u7565\u731c\u5230\u53ef\u80fd\u6709\u53d8\uff0c\u4f46\u89c1\u8339\u8001\u9556\u5934\u6307\u6325\u82e5\u5b9a\uff0c\u7d27\u5f20\u4e2d\u653e\u5fc3 \u4e0d\u5c11\uff0c\u89c1\u8339\u8001\u9556\u5934\u7387\u4f17\u524d\u53bb\uff0c\u5f53\u5373\u4e00\u631f\u5750\u9a6c\uff0c\u4e5f\u4ece\u540e\u8fb9\u8ddf\u4e86\u6765\uff01 \u8339\u8001\u9556\u5934\u3001\u4e8c\u4f4d\u516c\u5dee\u3001\u4e00\u7fa4\u9556\u5e08\uff0c\u5171\u662f\u4e09\u5341\u4f59\u96f6\uff0c\u6e85\u6c99\u626c\u5c18\uff0c\u7b56\u9a6c\u6765\u5230\u6797\u8fb9\uff0c\u52d2\u4f4f\u5750 \u9a91\uff0c\u7eb5\u76ee\u5411\u6797\u5185\u671b\u53bb\uff01 \u53ea\u89c1\u6797\u5185\u9759\u8c27\u65e0\u4eba\uff0c\u9e1f\u96c0\u65e0\u58f0\uff0c\u9664\u4e86\u98ce\u8fc7\u6811\u68a2\uff0c\u53d1\u51fa\u545c\u545c\u677e\u6d9b\u4e4b\u5916\uff0c\u7ade\u4e0d\u95fb\u4e00\u4e9b\u58f0\u54cd\uff01 \u8fde\u90a3\u516b\u540d\u8d9f\u5b50\u624b\u4e00\u4e2a\u4e5f\u4e0d\u89c1\u3002\u662f\u8fde\u4eba\u5e26\u9a6c\u4e00\u9f50\u88ab\u4eba\u63b3\u53bb\u4e86\uff1f\u8fd8\u662f\u7a7f\u6797\u800c\u8fc7\u4e86\uff0c\u4e00\u65f6\u731c\u4e0d \u51fa\u53bb\u5411\uff01 \u5c31\u7b97\u7a7f\u6797\u800c\u8fc7\u4e86\uff0c\u4e5f\u5e94\u8be5\u542c\u5230\u7ee7\u7eed\u558a\u8198\u7684\u58f0\u97f3\uff1b\u82e5\u8bf4\u88ab\u4eba\u6740\u5bb3\u4e86\uff0c\u4e5f\u5e94\u6709\u5c38\u4f53\u9a6c\u5339\uff1b\u5c31 \u7b97\u88ab\u4eba\u8fde\u9a6c\u63b3\u8d70\u4e86\uff0c\u4e5f\u80fd\u770b\u51fa\u4e2a\u86db\u4e1d\u9a6c\u8ff9\uff0c\u6216\u542c\u5230\u9a6c\u5636\u4eba\u558a\uff0c\u4f46\u76ee\u524d\u677e\u6797\u5185\uff0c\u9759\u8c27\u65e0\u58f0\uff0c\u597d \u50cf\u6839\u672c\u672a\u7ecf\u8fc7\u4efb\u4f55\u53d8\u52a8\uff01 \u53ef\u662f\uff0c\u8fd9\u5b81\u9759\u65e0\u58f0\u7684\u5bc6\u6797\u5185\uff0c\u5728\u8339\u8001\u9556\u5934\u4e00\u884c\u4eba\u770b\u6765\uff0c\u5374\u9690\u85cf\u4e86\u65e0\u6570\u7684\u6050\u6016\u548c\u795e\u79d8\uff0c\u5145 \u6ee1\u4e86\u65e0\u6bd4\u7684\u6740\u673a\uff01 \u8339\u8001\u9556\u5934\u7eb5\u7136\u7ecf\u9a8c\u4e30\u5bcc\uff0c\u81f3\u6b64\u4e5f\u5fc3\u6000\u60ca\u6016\uff0c\u6293\u4e0d\u4f4f\u5934\u7eea\uff01\u2026\u2026 \u201c\u80d6\u7075\u5bab\u201d\u90d1\u4f2f\u8c61\u3001\u201c\u77f3\u7334\u201d\u4faf\u9e9f\u5584\uff0c\u6b64\u65f6\u4f3c\u4e5f\u660e\u767d\u4e8b\u6001\u7684\u4e25\u91cd\uff0c\u4e24\u4e2a\u4eba\u5413\u5f97\u8138\u65e0\u4eba \u8272\uff0c\u9762\u9762\u76f8\u89d1\uff01 \u4f17\u8198\u5e08\u4e5f\u4e00\u9f50\u7741\u5927\u4e86\u773c\u775b\uff0c\u9762\u542b\u60ca\u6016\u5730\u5411\u6797\u5185\u671b\u53bb\u3002\u2026\xb7 \u90a3\u4e48\u591a\u4eba\u6ca1\u6709\u4e00\u4e2a\u53d1\u51fa\u58f0\u97f3\u6765\uff01 \u201c\u80d6\u7075\u5b98\u201d\u8010\u4e0d\u4f4f\u8fd9\u6c89\u95f7\u7684\u6c14\u6c1b\uff0c\u7b56\u9a6c\u8d70\u8fd1\u8339\u8001\u9556\u5934\u8eab\u8fb9\uff0c\u538b\u4f4e\u4e86\u58f0\u97f3\u95ee\u9053\uff1a\u201c\u8001\u5f53\u5bb6 \u7684\uff01\u600e\u4e48\u56de\u4e8b\uff1f\u201d \u4f46\u5728\u4f17\u9ed8\u65e0\u58f0\u3001\u7d27\u5f20\u6050\u6016\u7684\u6c14\u6c1b\u4e4b\u4e2d\uff0c\u4ed6\u8fd9\u7b56\u9a6c\u4e00\u5b9a\u52a8\uff0c\u53c8\u538b\u4f4e\u4e86\u58f0\u97f3\u4e00\u95ee\uff0c\u66f4\u4f7f\u4f17\u4eba \u5413\u5f97\u5934\u76ae\u53d1\u9ebb\uff0c\u597d\u50cf\u6709\u4ec0\u4e48\u5927\u7978\u5c31\u8981\u4e34\u5934\u4e00\u822c\uff0c\u4e2a\u4e2a\u9762\u65e0\u4eba\u8272\uff01 \u8339\u8001\u9556\u5934\u672b\u7b54\u8a00\uff0c\u4f46\u5fc3\u4e2d\u5df2\u6697\u4e0b\u51b3\u5fc3\uff0c\u975e\u8981\u5bdf\u770b\u4e2d\u6c34\u843d\u77f3\u51fa\u4e0d\u53ef\uff0c\u56e0\u4e3a\u4ed6\u662f\u603b\u9556\u5934\uff0c\u603b \u4e0d\u80fd\u9556\u8f66\u4e0d\u8d70\uff0c\u53eb\u516b\u4e2a\u90e8\u4e0b\u767d\u767d\u5931\u8e2a\u5c31\u4e0d\u95fb\u4e0d\u95ee\u4e86\uff01 \u56e0\u6b64\uff0c\u4ed6\u5927\u7740\u80c6\u5b50\u7b56\u9a6c\u7a7f\u6797\u800c\u8fc7\uff0c\u540c\u65f6\u63d0\u795e\u56db\u5468\u6212\u5907\uff01 \u4f17\u9556\u5e08\u89c1\u603b\u9556\u5934\u5df2\u7b56\u9a6c\u4eba\u6797\uff0c\u4e5f\u53ea\u6709\u5927\u7740\u80c6\u5b50\u4e00\u4e2a\u4e00\u4e2a\u8ddf\u4e86\u4e0a\u6765\uff01 \u4f17\u4eba\u8fdb\u5165\u5bc6\u6797\uff0c\u7d27\u5f20\u7684\u4e00\u9897\u5fc3\u90fd\u8981\u8df3\u51fa\u8154\u5916\u6765\u4e86\uff01 \u56e0\u4e3a\u660e\u6740\u660e\u780d\uff0c\u8fd9\u4e9b\u4eba\u90fd\u662f\u5200\u5934\u8214\u8840\u7684\u6c49\u5b50\uff0c\u8fd8\u4e0d\u4f1a\u5bb3\u6015\uff0c\u4f46\u8fd9\u79cd\u72b6\u51b5\u4e0d\u660e\u3001\u83ab\u77e5\u6240 \u4ee5\uff0c\u624d\u4f7f\u4f17\u4eba\u7d27\u5f20\u6050\u6016\u5f97\u4e0d\u5f97\u4e86\uff01 \u6797\u4e2d\u677e\u58f0\u5982\u6d9b\uff0c\u9759\u8c27\u5f02\u5e38\uff0c\u4e09\u5341\u4f59\u4eba\u6000\u7740\u60ca\u614c\u6050\u6016\u4e4b\u60c5\uff0c\u8e44\u58f0\u5f97\u5f97\uff0c\u7f13\u7f13\u7a7f\u8fc7\u4e86\u6574\u5ea7\u677e \u6797\uff0c\u5df2\u7ecf\u8d8a\u6797\u800c\u8fc7\uff0c\u5230\u4e86\u677e\u6797\u7684\u90a3\u4e00\u8fb9\uff0c\u6570\u91cc\u4e4b\u9614\u7684\u677e\u6797\u5185\uff0c\u5374\u4e00\u65e0\u53d1\u73b0\uff01 \u8d70\u51fa\u6797\u5916\uff0c\u5927\u9053\u4ecd\u5728\u9762\u524d\u5c55\u4f38\uff0c\u4f9d\u7136\u662f\u4e00\u773c\u671b\u4e0d\u5230\u5c3d\u5934\uff0c\u4f46\u5927\u8def\u53e3\u5374\u8fde\u4e00\u4e2a\u4eba\u7684\u5f71\u5b50\u4e5f \u6ca1\u6709\uff01 \u90a3\u516b\u540d\u8d9f\u5b50\u624b\u54ea\u91cc\u53bb\u4e86\u5462\uff1f \u8fd9\u771f\u662f\u4e00\u4e2a\u65e0\u6cd5\u89e3\u900f\u7684\u8c1c\uff01 \u201c\u6d51\u94c1\u725b\u201d\u5468\u5927\u901a\u6027\u683c\u7c97\u9c81\uff0c\u6b64\u65f6\u4f4e\u4f4e\u8bc5\u5492\u4e86\u4e00\u53e5\uff1a\u201c\u4ed6\u5988\u7684\uff01\u771f\u662f\u78b0\u5230\u9b3c\u4e86\uff01\u2026\u2026\u201d \u4ed6\u4e00\u53e5\u8bdd\u672a\u8bf4\u5b8c\uff0c\u7a81\u542c\u8eab\u540e\u558a\u6740\u8fde\u5929\uff0c\u72b9\u5982\u5343\u519b\u4e07\u9a6c\u7a81\u7136\u51b2\u6740\u800c\u81f3\u4e00\u822c\uff01 \u90a3\u6740\u58f0\u662f\u7531\u4ed6\u4eec\u6765\u7684\u65b9\u5411\u4f20\u6765\uff0c\u4f17\u4eba\u9f50\u5403\u4e00\u60ca\uff0c\u7acb\u523b\u62c9\u8f6c\u9a6c\u5934\uff0c\u53c8\u5954\u56de\u6797\u4e2d\uff01 \u4e09\u5341\u4f59\u9a91\uff0c\u98ce\u9a70\u7535\u63a3\uff0c\u65b9\u5165\u6797\u4e2d\u4e00\u534a\uff0c\u7a81\u542c\u6811\u4e0a\u5f13\u5f26\u54cd\u5904\uff0c\u4e00\u6392\u6025\u8b66\uff0c\u6a2a\u7a7a\u5c04\u81f3\uff01 \u4f17\u4eba\u4e0d\u5907\uff0c\u7acb\u6709\u5341\u6570\u9556\u5e08\uff0c\u4e2d\u7bad\u843d\u9a6c\uff01 \u8339\u8001\u9556\u5934\u5fc3\u77e5\u4e2d\u8ba1\uff0c\u5fc3\u4e2d\u53c8\u6025\u53c8\u6012\uff0c\u9ad8\u58f0\u8d50\u9053\uff1a\u201c\u4f55\u65b9\u670b\u53cb\uff1f\u7ade\u7528\u5982\u6b64\u6bd2\u8ba1\u9677\u5bb3\u8001\u592b\uff01 \u6709\u79cd\u7684\u4f55\u4e0d\u73b0\u8eab\u76f8\u89c1\uff01\u201d \u7a81\u542c\u4e00\u58f0\u72c2\u7b11\uff0c\u53d1\u81ea\u6811\u9876\uff0c\u7b11\u58f0\u9ad8\u6602\uff0c\u9707\u5f97\u4f17\u4eba\u53cc\u8033\u8f70\u9e23\uff0c\u677e\u9488\u6d12\u843d\u5982\u96e8\uff01 \u4f17\u4eba\u9f50\u5403\u4e00\u60ca\uff0c\u542c\u8fd9\u7b11\u58f0\uff0c\u5df2\u77e5\u9690\u8eab\u4e4b\u4eba\u5185\u529f\u6df1\u539a\uff01 \u5c31\u5728\u4f17\u4eba\u9707\u60ca\u4e4b\u4e2d\uff0c\u201c\u55d6\u55d6\u201d\u8fde\u54cd\uff0c\u4e00\u9635\u8863\u631f\u88ab\u98ce\u4e4b\u58f0\uff0c\u63a5\u8fde\u4ece\u6811\u4e0a\u8dc3\u4e0b\u516b\u540d\u52b2\u88c5\u5927 \u6c49\uff01 \u8fd9\u516b\u540d\u52b2\u88c5\u5927\u6c49\uff0c\u4e00\u5f8b\u9ed1\u5dfe\u8499\u9762\uff0c\u906e\u4f4f\u53e3\u9f3b\uff0c\u53ea\u9732\u51fa\u7cbe\u8292\u56db\u5c04\u7684\u53cc\u773c\uff0c\u770b\u6765\u4e2a\u4e2a\u9634\u68ee\u5413 \u4eba\uff01 \u8339\u8001\u9556\u8fd1\u9ed1\u9053\u4e0a\u7684\u719f\u4eba\uff0c\u5f53\u5373\u50ac\u9a6c\u5411\u524d\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u8001\u592b\u2018\u94c1\u638c\u9707\u6cb3\u6714\u2019\u8339\u56fd\u96c4\uff0c\u4e0d \u77e5\u8bf8\u4f4d\u662f\u54ea\u6761\u9053\u4e0a\u7684\u670b\u53cb\uff0c\u8bf7\u62a5\u4e0a\u540d\u6765\uff0c\u8001\u592b\u4e5f\u597d\u6709\u4e2a\u63a5\u5f85\uff01\u201d \u4e3a\u9996\u4e00\u540d\u52b2\u88c5\u5927\u6c49\u53c8\u54c8\u54c8\u7b11\u4e86\u4e24\u58f0\uff0c\u9053\uff1a\u201c\u7ba1\u4f60\u8001\u513f\u662f\u4ec0\u4e48\u718a\uff01\u4eca\u5929\u4e00\u4e2a\u4e5f\u522b\u60f3\u6d3b\u7740\u56de \u53bb\uff01\u201d \u201c\u6d51\u94c1\u725b\u201d\u6b63\u81ea\u618b\u4e86\u4e00\u809a\u5b50\u6c14\u6ca1\u51fa\u5904\uff0c\u95fb\u8a00\u6012\u9053\uff1a\u201c\u80c6\u5927\u76d7\u8d3c\uff01\u7adf\u6562\u8ba1\u52ab\u71d5\u4eac\u9556\u5c40\u7684 \u9556\uff0c\u53c8\u6765\u5927\u8a00\u552c\u4eba\uff0c\u4f11\u8d70\uff01\u5148\u63a5\u4f60\u5468\u5927\u7237\u4e00\u638c\uff01\u201d \u559d\u7f62\uff0c\u8fde\u9a6c\u4e5f\u672a\u4e0b\uff0c\u5c31\u5728\u9a6c\u4e0a\uff0c\u4e00\u638c\u5411\u9996\u5148\u53d1\u8bdd\u7684\u8499\u9762\u5927\u6c49\u780d\u53bb\uff01 \u5468\u5927\u901a\u4eba\u79f0\u201c\u6d51\u94c1\u725b\u201d\uff0c\u50bb\u5927\u9ed1\u7c97\uff0c\u5468\u8eab\u6a2a\u7ec3\uff0c\u5728\u201c\u94c1\u7802\u638c\u201d\u4e0a\u66fe\u4e0b\u8fc7\u82b3\u529f\uff0c\u8fd9\u4e00\u638c\u51fa \u53bb\uff0c\u529b\u9053\u751a\u731b\uff0c\u53ea\u89c1\u52b2\u98ce\u7834\u7a7a\uff0c\u731b\u5288\u4eba\u8499\u9762\u5927\u6c49\u7684\u9762\u95e8\uff01 \u8499\u9762\u5927\u6c49\u4e00\u58f0\uff1a\u201c\u5c0f\u5b50\u627e\u6b7b\uff01\u201d \u559d\u58f0\u4e2d\u6325\u638c\u4e00\u8fce\uff0c\u201c\u7830\u201d\u7684\u4e00\u58f0\u66b4\u54cd\uff0c\u5468\u5927\u901a\u60e8\u547c\u4e00\u58f0\uff0c\u4e00\u4e2a\u5e9e\u5927\u8eab\u8eaf\u4ece\u9a6c\u4e0a\u88ab\u4eba\u4e00\u638c \u9707\u98de\uff0c\u76f4\u81f3\u5bfb\u4e08\uff0c\u624d\u649e\u5230\u6811\u62ab\u4e0a\uff0c\u6251\u901a\uff01\u6454\u843d\u5728\u5730\uff0c\u773c\u89c1\u6b7b\u591a\u6d3b\u5c11\uff01 \u4f17\u9556\u5e08\u4e00\u89c1\u9f50\u5403\u4e00\u60ca\uff0c\u60f3\u90a3\u5468\u5927\u901a\u5728\u9556\u5e08\u7fa4\u4e2d\uff0c\u6b66\u529f\u4e0d\u5f31\uff0c\u6ca1\u60f3\u5230\u53ea\u4e00\u638c\u4fbf\u88ab\u4eba\u9707\u98de\uff01 \u53ef\u89c1\u8fd9\u8499\u9762\u5927\u6c49\u6b66\u529f\u5b9e\u5728\u9ad8\u5f3a\uff01 \u90a3\u8499\u9762\u5927\u6c49\u4e00\u638c\u628a\u201c\u6d51\u94c1\u4e2d\u201d\u9707\u98de\uff0c\u6d12\u7136\u4e00\u7b11\u9053\uff1a\u201c\u6211\u4ee5\u4e3a\u6709\u4ec0\u4e48\u771f\u624d\u5b9e\u5b66\uff0c\u7adf\u6562\u5927\u8a00 \u4e0d\u60ed\uff0c\u539f\u6765\u662f\u4e0d\u582a\u4e00\u51fb\u7684\u5bb6\u4f19\uff01\u770b\u6837\u5b50\uff0c\u4f60\u4eec\u8fd9\u4e9b\u81ea\u547d\u4e0d\u89c1\u7684\u9556\u5e08\uff0c\u90fd\u662f\u4e9b\u9152\u56ca\u996d\u888b\uff0c\u6253\u7740 \u9556\u65d7\u6df7\u996d\u5403\uff01\u8fd8\u6709\u54ea\u4e00\u4e2a\u4e0d\u6015\u6b7b\u7684\uff0c\u5feb\u5feb\u4e0a\u524d\u9001\u6b7b\uff01\u201d \u8339\u8001\u9556\u5934\u89c1\u81ea\u5df1\u624b\u4e0b\u7684\u9556\u5e08\uff0c\u4ec5\u53ea\u4e00\u62db\uff0c\u4fbf\u88ab\u6765\u4eba\u51fb\u6bd9\uff0c\u5fc3\u4e2d\u53c8\u60ca\u53c8\u6012\uff0c\u9762\u5b54\u4e00\u6c89\uff0c \u9053\uff1a\u201c\u9601\u4e0b\u4e0d\u80af\u663e\u9732\u8eab\u4efd\uff0c\u53c8\u5bf9\u5c5e\u4e0b\u731b\u65bd\u8fa3\u624b\uff0c\u8bf4\u4e0d\u5f97\u8001\u592b\u8981\u9886\u6559\u51e0\u624b\u9ad8\u62db\uff01\u201d \u8bf4\u7f62\u98d8\u8eab\u4e0b\u9a6c\uff0c\u8de8\u9a6c\u8e6d\u6321\uff0c\u53cc\u81c2\u4e00\u5708\uff0c\u5df2\u628a\u4ed6\u6210\u540d\u591a\u5e74\u7684\u4e00\u53cc\u94c1\u638c\uff0c\u4eae\u4e86\u51fa\u6765\uff01 \u53ea\u89c1\u8339\u8001\u9556\u5934\u53cc\u638c\u7eb5\u7b4b\u66b4\u9732\uff0c\u6307\u9aa8\u5173\u8282\u54af\u54af\u66b4\u54cd\uff0c\u53ef\u89c1\u8fd9\u201c\u94c1\u638c\u9707\u6cb3\u6714\u201d\u4e4b\u540d\uff0c\u51b3\u4e0d\u662f \u6d6a\u5f97\u865a\u540d\uff0c\u786e\u5b9e\u6709\u70b9\u60ca\u4eba\u7684\u529f\u529b\uff01 \u90a3\u8499\u9762\u52b2\u88c5\u5927\u6c49\uff0c\u53cc\u773c\u7cbe\u5149\u66b4\u5c04\uff0c\u671b\u7740\u8339\u8001\u9556\u5934\u7684\u4e00\u53cc\u94c1\u638c\uff0c\u773c\u4e2d\u4e5f\u6d41\u9732\u51fa\u9707\u60ca\u4e4b\u8272\uff01 \u540c\u65f6\uff0c\u4ed6\u4e5f\u6697\u4e2d\u8fd0\u96c6\u5468\u8eab\u529f\u529b\uff0c\u84c4\u52bf\u5f85\u654c\uff01 \u8339\u8001\u9556\u5934\u624d\u8981\u51fa\u624b\uff0c\u7a81\u542c\u8eab\u540e\u4e00\u58f0\u5389\u543c\uff0c\u72b9\u5982\u6674\u7a7a\u8d77\u4e86\u4e00\u4e2a\u9739\u96f7\uff01\u4e00\u6761\u9ed1\u5f71\uff0c\u72b9\u5982\u534a\u622a \u5854\u76f8\u4eff\uff0c\u547c\u201d\u7684\u4e00\u58f0\uff0c\u8dc3\u81f3\u8339\u8001\u9556\u5e08\u8eab\u524d\uff01\u5389\u58f0\u5927\u543c\u9053\uff1a\u201c\u716e\u9e21\u7109\u7528\u725b\u9f0e\uff0c\u8ba9\u4ffa\u6765\u6536\u62fe\u8fd9\u5c0f \u5b50\u7ed9\u5468\u5927\u54e5\u62a5\u4ec7\uff01\u201d \u8339\u8001\u9556\u5934\u4e00\u770b\uff0c\u4e43\u662f\u9556\u5e08\u4e2d\u7684\u201c\u5de8\u65e0\u9738\u201d\u90b5\u7075\uff01\u8fd9\u90b5\u7075\u8eab\u9ad8\u516b\u5c3a\uff0c\u529b\u5927\u65e0\u7a77\uff0c\u4e0e\u5468\u5927\u901a \u79f0\u4e3a\u83ab\u9006\uff0c\u4eca\u89c1\u5468\u5927\u901a\u4f24\u5728\u6765\u4eba\u638c\u4e0b\uff0c\u6545\u800c\u633a\u8eab\u800c\u51fa\uff01 \u8304\u8001\u9556\u5934\u77e5\u9053\u90b5\u7075\u662f\u4e2a\u6d51\u4eba\uff0c\u4e5f\u51b3\u4e0d\u662f\u6765\u4eba\u7684\u5bf9\u624b\uff0c\u624d\u8981\u62e6\u963b\uff0c\u8c01\u77e5\u201c\u5de8\u65e0\u9738\u201d\u6027\u60c5\u706b \u66b4\uff0c\u53c8\u52a0\u4e0a\u5fc3\u5207\u53cb\u4ec7\uff0c\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u62db\u5df2\u65bd\u51fa\uff0c\u53ea\u89c1\u4ed6\u53cc\u638c\u4e00\u62a1\uff0c\u9e23\u949f\u51fb\u9f13\u201d\uff0c\u4e0a\u6253\u8499\u9762\u4eba\u9876 \u95e8\uff0c\u4e0b\u649e\u8499\u9762\u4eba\u524d\u80f8\uff0c\u4e00\u62db\u4e24\u5f0f\uff0c\u51fa\u624b\u4fbf\u662f\u62fc\u547d\u7684\u62db\u5f0f\uff01 \u540c\u65f6\u5634\u4e2d\u559d\u9a82\u9053\uff1a\u201c\u8d3c\u5b50\uff01\u8fd8\u4ffa\u5468\u5927\u54e5\u7684\u547d\u6765\uff01\u201d \u8499\u9762\u5927\u6c49\u563f\u563f\u51b7\u7b11\uff0c\u5bf9\u201c\u5de8\u65e0\u9738\u201d\u7684\u62db\u5f0f\uff0c\u4e0d\u8eb2\u4e0d\u95ea\uff0c\u76f4\u5f85\u62db\u5c06\u53ca\u8eab\uff0c\u624d\u731b\u7136\u8eab\u5f62\u4e00 \u65cb\uff0c\u7adf\u4e0d\u77e5\u4ed6\u662f\u7528\u7684\u4ec0\u4e48\u8eab\u6cd5\uff0c\u5019\u5ffd\u7ed5\u5230\u201c\u5de8\u65e0\u9738\u201d\u90b5\u7075\u8eab\u540e\u53bb\u4e86\uff01 \u90b5\u7075\u4e00\u62db\u6251\u7a7a\uff0c\u6765\u4e0d\u53ca\u8f6c\u8eab\uff0c\u90a3\u8499\u9762\u5927\u6c49\u4e00\u638c\u5411\u90b5\u7075\u540e\u5fc3\u8981\u7a74\u6309\u6765\uff01 \u8fd9\u56de\u90b5\u7075\u4e0d\u7075\u4e86\uff0c\u8981\u60f3\u8f6c\u8eab\u5e94\u654c\u5df2\u6765\u4e0d\u53ca\u2026\u2026 \u8339\u8001\u559d\u4e00\u58f0\uff0c\u628a\u6210\u540d\u591a\u5e74\u7684\u94c1\u638c\u65bd\u51fa\uff0c\u52b2\u98ce\u9510\u5578\uff0c\u76f4\u780d\u8499\u9762\u4eba\u6309\u5411\u90b5\u7075\u540e\u5fc3\u8981\u5bb3\u7684\u53f3 \u81c2\uff01 \u8339\u8001\u9556\u5934\u672c\u662f\u60f3\u6551\u90b5\u7075\uff0c\u8c01\u77e5\u53e6\u4e00\u8499\u9762\u5927\u6c49\u53ca\u65f6\u62a2\u4e0b\uff0c\u6325\u81c2\u63a5\u4e86\u8339\u8001\u9556\u5934\u4e00\u638c\uff01 \u201c\u8f70\uff01\u201d\u4e00\u58f0\u5de8\u9707\uff0c\u52b2\u98ce\u56db\u6fc0\uff0c\u98de\u6c99\u626c\u5c18\uff0c\u8339\u8001\u9556\u5934\u7adf\u88ab\u9707\u5f97\u8eab\u5f62\u8fde\u6643\uff0c\u624b\u81c2\u53d1\u9ebb\uff01 \u90a3\u8499\u9762\u5927\u6c49 \u5c31\u8fd9\u4e00\u638c\uff0c\u8339\u8001\u9556\u5934\u5fc3\u91cc\u6709\u6570\uff0c\u77e5\u9053\u6765\u7684\u516b\u4e2a\u8499\u9762\u4eba\uff0c\u6b66\u529f\u5b9e\u5728\u9ad8\u5f3a\uff0c\u4eca\u65e5\u6050\u6015\u8ba8\u4e0d\u4e86 \u597d\u8d70\uff01 \u540c\u65f6\uff0c\u5148\u524d\u90a3\u8499\u9762\u5927\u6c49\u4e00\u638c\u4e5f\u51fb\u5b9e\u5728\u201c\u5de8\u65e0\u9738\u201d\u7684\u540e\u5fc3\u4e4b\u4e0a\uff01 \u867d\u7136\u56e0\u8339\u8001\u9556\u5934\u51fa\u62db\u5728\u5148\uff0c\u5f3a\u52b2\u638c\u98ce\u903c\u5f97\u4ed6\u624b\u52bf\u7565\u504f\uff0c\u6ca1\u6709\u6253\u6b63\u90b5\u7075\u7684\u540e\u5fc3\u81f4\u547d\u8981\u7a74\uff0c \u4f46\u8fd9\u4e00\u638c\u4e5f\u628a\u90b5\u7075\u6253\u51fa\u4e03\u516b\u6b65\u8fdc\u53bb\uff01 \u201c\u5de8\u65e0\u9738\u201d\u90b5\u7075\u90a3\u4e48\u88ab\u4eba\u5bb6\u4e00\u638c\u6253\u5f97\u5411\u524d\u8ddf\u8def\u4e03\u516b\u6b65\u8fdc\uff0c\u52c9\u5f3a\u7ad9\u4f4f\u672b\u5012\uff0c\u5374\u5fcd\u4e0d\u4f4f\u5f20\u53e3 \u55b7\u51fa\u4e00\u53e3\u9c9c\u8840\u6765\uff01 \u8339\u8001\u9556\u5934\u4e00\u89c1\uff0c\u4e0d\u5230\u4e00\u62db\uff0c\u81ea\u5df1\u4e24\u540d\u9556\u5e08\u4e00\u6b7b\u4e00\u91cd\u4f24\uff0c\u53ef\u5c31\u7ea2\u4e86\u773c\u4e86\uff0c\u5c24\u5176\u542c\u5230\u653e\u9556\u8f66 \u5904\u558a\u6740\u8fde\u5929\uff0c\u77e5\u9053\u6797\u5916\u9556\u8f66\uff0c\u53d7\u5230\u653b\u51fb\uff0c\u53c8\u803d\u5fc3\u9556\u8f66\u7684\u5b89\u5371\uff0c\u6025\u6012\u4e4b\u4e2d\uff0c\u5389\u543c\u4e00\u58f0\uff0c\u63a5\u8fde\u5288 \u51fa\u5341\u6570\u638c\uff0c\u72c2\u98ce\u66b4\u96e8\u4e00\u822c\uff0c\u5411\u90a3\u5f53\u5934\u7684\u8499\u9762\u5927\u6c49\u653b\u53bb\uff01 \u90a3\u8499\u9762\u5927\u6c49\u867d\u88ab\u8339\u8001\u9556\u5934\u4e00\u62a1\u731b\u653b\uff0c\u9000\u51fa\u5341\u6570\u6b65\u53bb\uff0c\u4f46\u201c\u5237\u5237\u5237\u201d\u4e09\u811a\u4e24\u62f3\u8fde\u73af\u653b\u51fa\uff0c \u53c8\u53eb\u4ed6\u6273\u56de\u52a3\u52bf\uff0c\u7acb\u523b\u8fd8\u4ee5\u989c\u8272\uff0c\u53cc\u638c\u7ffb\u98de\uff0c\u5c3d\u662f\u5947\u5965\u65e0\u6bd4\u4e4b\u5b66\uff0c\u53c8\u628a\u8339\u8001\u9556\u5934\u903c\u9000\u4e86\u6570\u5341 \u6b65\uff01 \u8339\u8001\u9556\u5934\u8d70\u5357\u95ef\u5317\uff0c\u8eab\u7ecf\u6570\u767e\u6218\uff0c\u4ece\u672a\u5982\u6b64\u72fc\u72c8\u8fc7\uff0c\u88ab\u90a3\u8499\u9762\u5927\u6c49\u4e00\u8def\u75be\u653b\uff0c\u95f9\u4e86\u4e2a\u624b \u5fd9\u811a\u4e71\uff0c\u70ad\u70ad\u53ef\u5371\uff01 \u4f17\u9556\u5e08\u89c1\u603b\u9556\u5934\u9047\u9669\uff0c\u53d1\u4e86\u4e00\u58f0\u558a\uff0c\u7eb7\u7eb7\u62bd\u5175\u5668\u51b2\u4e86\u4e0a\u6765\u3002 \u4f46\u90a3\u516b\u540d\u52b2\u88c5\u5927\u6c49\uff0c\u4e5f\u4e00\u9f50\u52a8\u624b\uff0c\u5e76\u672b\u64a4\u51fa\u5175\u5668\uff0c\u53ea\u51ed\u5341\u516d\u53ea\u624b\u638c\uff0c\u4fbf\u628a\u4f17\u9556\u5e08\u6253\u5f97\u4eba \u4ef0\u9a6c\u7ffb\uff01 \u8fd9\u4e00\u573a\u6df7\u6218\uff0c\u9556\u5e08\u867d\u7136\u4eba\u591a\uff0c\u5374\u663e\u7136\u4e0d\u662f\u516b\u4e2a\u8499\u9762\u5927\u6c49\u7684\u5bf9\u624b\uff0c\u6253\u5f97\u4f17\u9556\u5e08\u53eb\u82e6\u8fde\u5929\uff01 \u201c\u80d6\u7075\u5b98\u201d\u90d1\u4f2f\u8c61\u3001\u201c\u77f3\u7334\u201d\u4faf\u9e9f\u5584\u5df2\u5413\u5f97\u817f\u809a\u5b50\u76f4\u8f6c\u7b4b\uff0c\u8fde\u8dd1\u90fd\u8dd1\u4e0d\u52a8\u4e86\uff0c\u522b\u8bf4\u518d\u51fa \u624b\u53c2\u6218\u3002 \u8339\u8001\u9556\u5934\u867d\u660e\u77e5\u4e0d\u654c\uff0c\u4f46\u88ab\u654c\u4eba\u7f20\u4f4f\uff0c\u65e0\u6cd5\u8131\u8eab\uff0c\u53ea\u6709\u62fc\u547d\u82e6\u6491\uff01 \u6b64\u65f6\uff0c\u4f17\u9556\u5e08\u5df2\u4f24\u4ea1\u5927\u534a\uff01 \u5ffd\u542c\u6797\u95f4\u6570\u58f0\u5c16\u5389\u957f\u54e8\u54cd\u8d77\uff01\u55d6\uff01\u55d6\uff01\u55d6\uff01\u63a5\u8fde\u53c8\u4ece\u6797\u5916\u7a9c\u8fdb\u4e94\u540d\u8499\u9762\u4eba\u6765\uff01 \u4f17\u9556\u5e08\u4e00\u89c1\u6697\u6697\u53eb\u82e6\uff0c\u53ea\u773c\u524d\u516b\u4e2a\u8499\u9762\u4eba\uff0c\u5c31\u5df2\u7ecf\u62b5\u6321\u4e0d\u4f4f\uff0c\u90a3\u582a\u654c\u4eba\u518d\u6765\u63f4\u624b\uff01 \u8339\u8001\u9556\u5934\u4ef0\u5929\u957f\u53f9\uff1a\u201c\u6b64\u4e43\u5929\u4ea1\u6211\u4e5f\uff01\u2026\u2026\u201d \u51fa\u4e4e\u610f\u5916\u5730\uff0c\u540e\u8fdb\u6765\u7684\u4e94\u540d\u8499\u9762\u4eba\uff0c\u5e76\u672a\u4e0a\u524d\u53c2\u6218\uff0c\u5374\u662f\u6253\u6697\u8bed\uff0c\u53eb\u516b\u4e2a\u4eba\u64a4\u8d70\uff01 \u516b\u540d\u8499\u9762\u4eba\u63a5\u8fde\u5288\u51fa\u6570\u638c\u5f3a\u5927\u638c\u98ce\uff0c\u903c\u5f97\u4f17\u9556\u5e08\u60ca\u547c\u6025\u9000\uff0c\u4ed6\u4eec\u516b\u4eba\u8d81\u52bf\u817e\u8eab\u5411\u6797\u5916\u9038 \u53bb\uff01 \u8339\u8001\u9556\u5934\u7387\u4f17\u8ffd\u51fa\u6797\u5916\uff0c\u518d\u4e00\u770b\u6797\u5916\u7684\u9556\u5e08\u6b7b\u4f24\u904d\u5730\uff0c\u90a3\u6570\u5341\u8f86\u9556\u8f66\u5df2\u88ab\u4eba\u52ab\u8d70\uff0c\u9a70\u51fa \u8001\u8fdc\uff01 \u8339\u8001\u9556\u5934\u53cc\u76ee\u5c3d\u8d64\uff0c\u7ea0\u96c6\u4f24\u6b8b\u4e4b\u4f59\u7684\u9556\u5e08\uff0c\u4ea1\u547d\u5730\u8ffd\u53bb\uff01 \u6cb3\u6714\u540d\u6355\uff0c\u4eac\u57ce\u5feb\u6355\uff0c\u4e24\u540d\u6355\u5934\u201c\u80d6\u7075\u5b98\u201d\u90d1\u4f2f\u8c61\u4e0e\u201c\u77f3\u7334\u201d\u4faf\u9e9f\u7edd\u5c18\u800c\u53bb\uff0c\u7ade\u8dcc\u811a\u75db \u54ed\u8d77\u6765\uff01\u2026\u2026 \u56e0\u4e3a\u8fd9\u8d9f\u9556\u91cd\u8981\u975e\u5e38\uff0c\u4e43\u5bb6\u4eba\u8eab\u5bb6\u6027\u547d\u6240\u7cfb\uff0c\u771f\u8981\u4e22\u4e86\uff0c\u5c31\u7b97\u4e0d\u6218\u6b7b\u5728\u6c99\u573a\uff0c\u56de\u5bb6\u4e5f\u65e0 \u6cd5\u6d3b\u547d\uff01 \u8339\u8001\u9556\u5934\u9664\u4e86\u8eab\u5bb6\u6027\u547d\u4e4b\u5916\uff0c\u66f4\u5173\u4e4e\u4e00\u4e16\u82f1\u540d\uff0c\u6240\u4ee5\u867d\u5728\u60e8\u8d25\u4e4b\u540e\uff0c\u4ecd\u7136\u7a77\u8ffd\u4e0d\u820d\uff01 \u4f17\u9556\u5e08\u611f\u5ff5\u603b\u9556\u5934\u5e73\u7d20\u6069\u9047\uff0c\u4e5f\u90fd\u820d\u751f\u5fd8\u6b7b\u5730\u968f\u7740\u8339\u8001\u9556\u5934\u731b\u8ffd\u8d3c\u4eba\uff01 \u90a3\u8499\u9762\u4eba\u9664\u4e86\u516b\u540d\u52b2\u88c5\u5927\u6c49\u4e4b\u5916\uff0c\u8fd8\u6709\u56db\u4e2a\u8eab\u6750\u5a07\u5c0f\u7c7b\u4f3c\u5973\u4eba\u7684\u8499\u9762\u5ba2\uff0c\u53e6\u5916\u4e00\u540d\u5149\u5934 \u548c\u5c1a\uff0c\u4e5f\u662f\u4ee5\u9ed1\u5dfe\u8499\u9762\uff01\u5171\u662f\u5341\u4e09\u4eba\u5728\u540e\u9762\u65ad\u540e\uff0c\u622a\u6740\u8ffd\u5f97\u4e14\u8fd1\u7684\u9556\u5e08\uff0c\u53e6\u5916\u6709\u4eba\u62bc\u7740\u52ab\u8d70 \u7684\u9556\u8f66\uff0c\u5df2\u7ecf\u75be\u9a70\u6570\u5341\u4e08\u5f00\u5916\uff01 \u9556\u5e08\u4e4b\u4e2d\uff0c\u8ffd\u5f97\u6700\u7d27\u7684\u662f\u767d\u53d1\u82cd\u82cd\u7684\u8339\u8001\u9556\u5934\uff01 \u53ea\u89c1\u8001\u9556\u5934\u6ee1\u5934\u5c3d\u8d64\uff0c\u72b6\u4f3c\u75af\u72c2\uff0c\u4e00\u8fb9\u6025\u8ffd\uff0c\u4e00\u8fb9\u4e24\u53ea\u94c1\u638c\u6ca1\u547d\u5730\u5411\u8d3c\u4eba\u5288\u780d\uff01 \u5728\u8d3c\u4eba\u770b\u6765\uff0c\u9556\u5e08\u4e4b\u4e2d\uff0c\u4e5f\u4ee5\u8339\u8001\u9556\u5934\u6700\u4e3a\u624e\u624b\uff0c\u4e0d\u4f46\u4e00\u53cc\u94c1\u638c\u51cc\u5389\u65e0\u5339\uff0c\u800c\u8f7b\u529f\u4e5f\u6700 \u9ad8\uff0c\u59cb\u7ec8\u7d27\u54ac\u4f4f\u8d3c\u4eba\u7684\u5c3e\u5df4\uff0c\u4f7f\u8d3c\u4eba\u4e00\u65f6\u4e4b\u95f4\uff0c\u4e0d\u6613\u8131\u8eab\u3002 \u60f9\u5f97\u90a3\u8499\u9762\u7684\u548c\u5c1a\u6027\u8d77\uff0c\u53ea\u89c1\u4ed6\u56de\u8fc7\u5934\u6765\uff0c\u8eab\u5f62\u5c31\u5730\u4e00\u8e72\uff0c\u4e24\u624b\u6276\u5730\uff0c\u72b6\u5982\u9752\u86d9\uff0c \u201c\u5471\uff01\u5471\u201d\u602a\u53eb\u4e24\u58f0\uff0c\u7a81\u7136\u626c\u8d77\u53cc\u638c\uff0c\u731b\u5411\u7d27\u8ffd\u4e0d\u820d\u7684\u8339\u8001\u9556\u5934\u649e\u6765\uff01 \u8339\u8001\u9556\u5934\u5939\u611f\u4e00\u822c\u91cd\u903e\u5c71\u5cb3\u7684\u5de8\u5927\u538b\u529b\u8fce\u80f8\u649e\u6765\uff0c\u6b32\u60f3\u62db\u67b6\uff0c\u4e3e\u624b\u65e0\u529b\uff0c\u6b32\u60f3\u8eb2\u95ea\uff0c\u6ca1 \u60f3\u5230\u5374\u88ab\u90a3\u5de8\u5927\u7684\u638c\u98ce\u52b2\u6d41\u7f69\u4f4f\uff0c\u7adf\u7136\u8eb2\u95ea\u4e0d\u5f00\uff0c\u4e0d\u7531\u6697\u53f9\u4e00\u58f0\uff1a\u201c\u543e\u547d\u4f11\u77e3\uff01\u2026\u2026\u201d \u5ffd\u542c\u5c71\u5d29\u5730\u88c2\u4e00\u58f0\u5de8\u54cd\uff0c\u52b2\u6d41\u5c71\u6d8c\uff0c\u628a\u5730\u4e0b\u7684\u5c18\u6c99\u67af\u8349\u76f4\u6380\u8d77\u534a\u5929\u9ad8\uff01 \u52bf\u5a01\u529b\u731b\uff0c\u524d\u6240\u672a\u89c1\uff01 \u8339\u8001\u9556\u5934\u81ea\u5fd6\u5fc5\u6b7b\uff0c\u53cc\u773c\u4e00\u95ed\uff0c\u5ffd\u542c\u4e00\u58f0\u5de8\u9707\u54cd\u8fc7\uff0c\u81ea\u5df2\u7ade\u5b89\u7136\u65e0\u6059\uff0c\u60ca\u8be7\u4e4b\u4e2d\uff0c\u5f20\u773c \u4e00\u770b\uff0c\u5374\u89c1\u5c11\u4e2d\u5c55\u767d\u82f1\u98ce\u7a81\u7a81\u5730\u7ad9\u5728\u81ea\u5df1\u8eab\u524d\uff01 \u518d\u770b\u90a3\u8499\u9762\u548c\u5c1a\uff0c\u9762\u4e0a\u9ed1\u5dfe\u5df2\u88ab\u9707\u843d\uff0c\u7070\u5934\u571f\u8138\uff0c\u9000\u51fa\u4e08\u5916\uff0c\u6b63\u602a\u76ee\u5706\u7741\uff0c\u5446\u671b\u7740\u5c55\u767d \u53d1\u695e\uff01 \u5c55\u767d\u54e6\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u539f\u6765\u662f\u4f60\uff01\u201d\u90a3\u548c\u5c1a\u4e5f\u54e6\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u539f\u6765\u662f\u4f60\uff01\u201d \u8339\u8001\u9556\u5934\u5374\u8bf4\u4ec0\u4e48\u4e5f\u4e0d\u4f1a\u76f8\u4fe1\uff0c\u7adf\u662f\u5c11\u5e74\u5c55\u767d\u6551\u4e86\u81ea\u5df1\uff01\u56e0\u4e3a\u4ed6\u66fe\u5e2e\u52a9\u8fc7\u5c55\u767d\uff0c\u800c\u4e14\u5c55 \u767d\u5728\u4ed6\u9556\u5c40\u4e2d\u4f5c\u8fc7\u51e0\u5929\u9556\u5e08\uff0c\u8001\u9556\u5934\u5bf9\u5c55\u767d\u7684\u6b66\u529f\uff0c\u77e5\u9053\u5f97\u6700\u6e05\u695a\u3002 \u53ef\u662f\uff0c\u773c\u524d\u660e\u6446\u7740\uff0c\u4e0d\u662f\u5c55\u767d\u6551\u4e86\u81ea\u5df1\uff0c\u90a3\u8fd8\u4f1a\u6709\u8c01\uff1f \u201c\u4f5b\u5370\u6cd5\u5e08\u201d\u60ca\u5ffd\u4ea4\u52a0\uff0c\u7a81\u7136\u5389\u5578\u4e00\u58f0\uff0c\u8eab\u5f62\u524d\u8e1e\uff0c\u201c\u5471\uff01\u5471\uff01\u201d\u602a\u53eb\uff0c\u8fd0\u8db3\u5168\u8eab\u7684\u529f \u529b\uff0c\u53cc\u638c\u5411\u5c55\u767d\u649e\u53bb\uff01 \u8fd9\u4e00\u6b21\uff0c\u56e0\u201c\u4f5b\u5370\u6cd5\u5e08\u201d\u662f\u5168\u529b\u65bd\u4e3a\uff0c\u5a01\u52bf\u66f4\u731b\uff0c\u53ea\u89c1\u4e24\u80a1\u5de8\u6d41\uff0c\u8f70\u8f70\u96f7\u9e23\uff0c\u76f4\u5411\u5c55\u767d \u5377\u53bb\uff01 \u5c55\u767d\u4fca\u9762\u5e26\u715e\uff0c\u51b7\u54fc\u4e00\u58f0\uff0c\u53f1\u9053\uff1a\u201c\u4f60\u662f\u627e\u6b7b\uff01\u201d \u201c\u6b7b\u201d\u5b57\u51fa\u53e3\uff0c\u65b0\u7ec3\u6210\u7684\u201c\u5929\u4f5b\u964d\u9b54\u638c\u201d\uff0c\u5df2\u968f\u624b\u6325\u51fa\uff01 \u4e24\u80a1\u5de8\u5927\u52b2\u6d41\uff0c\u534a\u7a7a\u76f8\u649e\uff0c\u53ea\u542c\u5de8\u54cd\u5982\u96f7\uff0c\u201c\u8f70\u201d\u7136\u4e00\u58f0\uff0c\u9707\u5f97\u5730\u52a8\u5c71\u6447\uff0c\u5c18\u6284\u853d\u5929\uff0c \u6570\u6587\u4e4b\u5916\u7684\u5bc6\u677e\u6797\u5185\uff0c\u677e\u9488\u88ab\u9707\u5f97\u6d12\u843d\u5982\u96e8\uff01\u5c31\u5728\u90a3\u906e\u5929\u853d\u65e5\u7684\u5c18\u6c99\u5f71\u4e2d\uff0c\u201c\u4f5b\u5370\u6cd5\u5e08\u201d\u80a5 \u80d6\u7684\u8eab\u8eaf\uff0c\u7adf\u5982\u65ad\u7ebf\u7eb8\u9e22\u4e00\u822c\uff0c\u76f4\u98de\u51fa\u4e8c\u4e08\u5f00\u5916\uff0c\u65b9\u52bf\u5c3d\u843d\u5730\u2014\u2014 \u4e0e\u201c\u4f5b\u5370\u6cd5\u5e08\u201d\u540c\u6765\u7684\u516b\u540d\u52b2\u88c5\u8499\u9762\u5927\u6c49\uff0c\u4e0e\u56db\u4e2a\u8499\u9762\u5c11\u5973\uff0c\u540c\u65f6\u60ca\u547c\u4e00\u58f0\uff0c\u6025\u5954\u81f3 \u201c\u4f5b\u5370\u6cd5\u5e08\u201d\u8ddf\u524d\uff0c\u628a\u201c\u4f5b\u5370\u6cd5\u5e08\u201d\u4ece\u5730\u4e0a\u6276\u8d77\uff0c\u53ea\u89c1\u201c\u4f5b\u5370\u6cd5\u5e08\u201d\u5634\u89d2\u6ea2\u8840\uff0c\u9762\u5982\u91d1\u7eb8\uff0c \u53cc\u773c\u534a\u7741\u534a\u95ed\uff0c\u770b\u6837\u5b50\u53d7\u4f24\u4e0d\u8f7b\uff01\u5176\u4e2d\u4e00\u4e2a\u8499\u9762\u5c11\u5973\uff0c\u201c\u5636\u201d\u7684\u4e00\u58f0\u628a\u8499\u9762\u9ed1\u7eb1\u626f\u4e0b\uff0c\u9732\u51fa \u4e00\u5f20\u5343\u5a07\u767e\u5a9a\u7684\u6843\u82b1\u8138\u6765\uff0c\u53ea\u89c1\u5979\u79c0\u76ee\u4e00\u77aa\uff0c\u5411\u5c55\u767d\u5a07\u53f1h\u9053\uff1a\u201c\u4f60\u80c6\u5b50\u4e0d\u5c0f\uff01\u7adf\u6562\u628a\u2018\u4f5b \u5370\u6cd5\u5e08\u2019\u6253\u4f24\uff0c\u4f60\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u201d \u5c55\u767d\u89c1\u8fd9\u5c11\u5973\u626f\u4e0b\u8499\u9762\u9ed1\u7eb1\uff0c\u7f8e\u662f\u591f\u7f8e\uff0c\u4f46\u773c\u6c6a\u6625\u6c34\uff0c\u7f8e\u8273\u4e2d\u5e26\u6709\u5996\u51b6\u4e4b\u6001\uff0c\u4e0d\u7531\u4e00 \u695e\uff0c\u9053\uff1a\u201c\u5728\u4e0b\u5c55\u767d\uff01\u4e0d\u4f46\u6562\u628a\u2018\u4f5b\u5370\u2019\u6253\u4f24\uff0c\u5982\u679c\u4f60\u4eec\u4e0d\u628a\u9556\u4ea4\u56de\uff0c\u5c31\u8fde\u4f60\u4eec\u51e0\u4e2a\u4eba\u4e5f\u5f97 \u7559\u4e0b\uff01\u201d \u7b2c\u56db\u5341\u4e09\u7ae0\u3000\u201c\u8c79\u7a81\u5c71\u5e84\u201d>> \u53e4\u9f99\u300a\u5251\u5ba2\u884c\u300b \u7b2c\u56db\u5341\u4e09\u7ae0\u3000\u201c\u8c79\u7a81\u5c71\u5e84\u201d \u53e6\u4e00\u4e2a\u5c11\u5973\u8885\u5a77\u8d70\u8fd1\u5c55\u767d\uff0c\u4e5f\u628a\u8499\u9762\u9ed1\u7eb1\u626f\u4e0b\uff0c\u72e0\u77aa\u5c55\u767d\u4e00\u773c\uff0c\u9053\uff1a\u201c\u7b97\u4f60\u6709\u80c6\u91cf\uff0c\u7adf \u6562\u4e0e\u2018\u5357\u6d77\u95e8\u2019\u67b6\u6881\u751f\u4e8b\uff0c\u4f60\u5c31\u5c0f\u5fc3\u597d\u4e86\uff0c\u2018\u5357\u6d77\u95e8\u2019\u5c06\u4e5f\u8981\u4f60\u7978\u5ef6\u4e09\u4ee3\uff01\u201d \u8bf4\u7f62\uff0c\u8f6c\u5934\u5bf9\u53e6\u5916\u4e24\u4e2a\u5c11\u5973\u53ca\u516b\u4e2a\u8499\u9762\u5927\u6c49\u8bf4\u9053\uff1a\u201c\u8d70\uff01\u54b1\u4eec\u56de\u53bb\uff01\u2026\u2026\u201d \u201c\u8d70\uff1f\u6740\u4eba\u3001\u52ab\u9556\uff01\u8bf4\u8d70\u5c31\u5b9a\uff0c\u6050\u6015\u6ca1\u6709\u90a3\u4e48\u5bb9\u6613\u5427\uff01\u201d \u8fd9\u8bdd\u58f0\u6765\u81ea\u6570\u4e08\u5f00\u5916\uff0c\u8bf4\u8bdd\u65f6\u6ca1\u6709\u770b\u5230\u4eba\u5f71\uff0c\u8bdd\u8bf4\u5b8c\uff0c\u5374\u51ed\u7a7a\u5728\u4f17\u4eba\u773c\u524d\u51fa\u73b0\u4e00\u4e2a\u9752\u8138 \u7ea2\u53d1\u7684\u602a\u4eba\uff01 \u4f17\u4eba\u4e0d\u7531\u9f50\u5403\u4e00\u60ca\uff01 \u8fd9\u9752\u8138\u7ea2\u53d1\u602a\u4eba\u7684\u8f7b\u8eab\u63d0\u7eb5\u672f\uff0c\u5230\u4e86\u80fd\u591f\u9690\u53bb\u8eab\u5f62\u7684\u5730\u6b65\uff0c\u90a3\u5feb\u5f97\u771f\u662f\u4e0d\u53ef\u60f3\u50cf\uff01 \u90a3\u4e24\u4e2a\u626f\u53bb\u8986\u9762\u9ed1\u7eb1\u7684\u7f8e\u888d\u5c11\u5973\uff0c\u770b\u5230\u8fd9\u9752\u8138\u7ea2\u53d1\u7684\u602a\u4eba\u51fa\u73b0\uff0c\u65b9\u624d\u90a3\u4e48\u72c2\u50b2\uff0c\u6b64\u65f6\u7adf \u5413\u5f97\u82b1\u5bb9\u5931\u8272\uff01 \u5c31\u8fde\u90a3\u8499\u9762\u7684\u4e24\u4e2a\u5c11\u5973\uff0c\u4e0e\u516b\u4e2a\u52b2\u88c5\u5927\u6c49\uff0c\u867d\u770b\u4e0d\u5230\u4ed6\u4eec\u7684\u9762\u5bb9\uff0c\u4f46\u4ece\u90a3\u6ef4\u6e9c\u6e9c\u4e71\u8f6c\u7684 \u53cc\u773c\u795e\u8272\u4e4b\u4e2d\uff0c\u4e5f\u53ef\u770b\u51fa\uff0c\u4ed6\u4eec\u5403\u60ca\u4e5f\u4e0d\u5728\u9732\u51fa\u7f8e\u8c8c\u5bb9\u989c\u7684\u5c11\u5973\u4e4b\u4e0b\uff01 \u8339\u8001\u9556\u5934\u5374\u662f\u53c8\u60ca\u53c8\u559c\uff0c\u4f30\u4e0d\u5230\u6570\u6708\u4e0d\u89c1\uff0c\u5c55\u767d\u6b66\u529f\u7adf\u6709\u5982\u8bb8\u7cbe\u8fdb\uff0c\u53c8\u89c1\u8fd9\u9752\u8138\u7ea2\u53d1\u602a \u4eba\u8bf4\u8bdd\u4e5f\u662f\u5e2e\u7740\u81ea\u5df2\uff0c\u77e5\u9053\u4eca\u5929\u6709\u4e86\u6551\u661f\uff0c\u5fd9\u9053\uff1a\u201c\u5c55\u8d24\u5f1f\uff01\u5343\u4e07\u4e0d\u80fd\u653e\u8d70\u4ed6\u4eec\uff01\u8001\u54e5\u54e5\u90a3 \u6570\u5341\u8f86\u9556\u8f66\u975e\u5e38\u7d27\u8981\u2026\u2026\u201d \u4e24\u4e2a\u7f8e\u8273\u5c11\u5973\u4e0d\u7ba1\u8339\u8001\u9556\u5934\u5bf9\u5c55\u767d\u8bf4\u4ec0\u4e48\uff0c\u5403\u60ca\u4e4b\u4e0b\uff0c\u4e92\u76f8\u5bf9\u671b\u4e00\u773c\uff0c\u53c8\u5411\u516b\u4e2a\u58ee\u6c49\u53ca \u53e6\u5916\u4e24\u4e2a\u8499\u9762\u5c11\u5973\u65bd\u4e00\u4e2a\u773c\u8272\uff0c\u7136\u540e\u8d70\u8d77\u6843\u82b1\u6b65\uff0c\u5a07\u8eaf\u5982\u5f31\u67f3\u968f\u98ce\uff0c\u5411\u9752\u8138\u7ea2\u53d1\u602a\u5ba2\u8885\u8885\u8d70 \u8fd1\u4e24\u6b65\uff0c\u5176\u4e2d\u4e00\u4e2a\u655b\u63a5\u4e00\u793c\uff0c\u9053\uff1a\u201c\u54df\uff01\u539f\u6765\u662f\u9b3c\u2026\u2026\u67f3\u59d0\u59d0\uff01\u6211\u4eec\u5c11\u541b\u60f3\u5ff5\u67f3\u59d0\u59d0\u60f3\u5f97\u53d1 \u75af\uff0c\u5c11\u541b\u53eb\u6211\u4eec\u89c1\u5230\u59d0\u2026\u2026\u201d \u5728\u8fd9\u7f8e\u8273\u5c11\u5973\u8bf4\u8bdd\u7684\u5f53\u513f\uff0c\u90a3\u516b\u540d\u8499\u9762\u52b2\u88c5\u5927\u6c49\u5df2\u631f\u8d77\u53d7\u4f24\u7684\u201c\u4f5b\u5370\u6cd5\u5e08\u201d\u75be\u9a70\u800c \u53bb\u2026\u3002\xb7 \u8fd9\u9752\u9762\u7ea2\u53d1\u602a\u5ba2\u6b63\u662f\u67f3\u7fe0\u7fe0\uff0c\u5979\u65b9\u5550\u4e86\u4e00\u53e3\uff0c\u9a82\u9053\uff1a\u201c\u8c01\u662f\u4f60\u4eec\u7684\u9b3c\u59d0\u59d0\uff01\u2026\u2026\u201d \u8339\u8001\u9556\u5934\u5df2\u8131\u53e3\u6025\u547c\u9053\uff1a\u201c\u5c55\u8d24\u5f1f\uff01\u522b\u8ba9\u8d3c\u4eba\u8dd1\u4e86\uff01\u2019\uff0e\u3002\uff0e\uff0c\uff0e\u3002 \u5c55\u767d\u9053\uff1a\u201c\u8001\u54e5\u54e5\u522b\u7740\u6025\uff01\u8d3c\u5b50\u4eec\u8dd1\u4e0d\u4e86\uff01\u201d \u559d\u58f0\u4e2d\uff0c\u53ea\u89c1\u4ed6\u8eab\u5f62\u5fae\u6643\uff0c\u72b9\u5982\u4e00\u7f15\u6de1\u70df\uff0c\u9976\u7136\u6321\u4f4f\u516b\u540d\u8499\u9762\u5927\u6c49\u7684\u53bb\u8def\uff01 \u8fd9\u516b\u540d\u8499\u9762\u58ee\u6c49\uff0c\u4e43\u662f\u201c\u9b54\u9b3c\u5c9b\u516b\u5996\u201d\uff0c\u6b66\u529f\u9ad8\u5f3a\uff0c\u4e14\u522b\u8d70\u8e4a\u5f84\uff0c\u5728\u201c\u5357\u6d77\u95e8\u201d\u4e0b\uff0c\u9664 \u4e86\u201c\u5357\u6d77\u5c11\u541b\u201d\u3001\u201c\u5357\u6d77\u9f99\u5973\u201d\u4ee5\u53ca\u201c\u6d77\u5916\u4e09\u715e\u201d\u51e0\u4e2a\u7edd\u4e16\u9ad8\u624b\u4e4b\u5916\uff0c\u4ed6\u4eec\u516b\u4eba\u7684\u6b66\u529f\u90fd\u53ef \u5217\u5165\u7b2c\u4e00\u6d41\uff0c\u8f7b\u8eab\u529f\u592b\u66f4\u6709\u72ec\u5230\u9020\u8be3\uff0c\u4eca\u89c1\u5c11\u4e2d\u5c55\u767d\u8d77\u6b65\u5728\u540e\uff0c\u5374\u80fd\u8d85\u5728\u4ed6\u4eec\u516b\u4eba\u7684\u524d\u9762\uff0c \u4e0d\u7531\u540c\u65f6\u2014\u6446I \u201c\u9b54\u9b3c\u5c9b\u516b\u5996\u201d\u5fc3\u77e5\u9047\u5230\u5f3a\u654c\uff0c\u4e0d\u65bd\u6740\u624b\uff0c\u6050\u6015\u96be\u4ee5\u8131\u8eab\uff0c\u4e92\u76f8\u4e00\u6253\u773c\u8272\uff0c\u56db\u5996\u540e\u9000\uff0c \u53cc\u81c0\u4e00\u9635\u5212\u52a8\uff0c\u65bd\u51fa\u201c\u9b54\u9b3c\u5c9b\u201d\u7684\u9b3c\u52a9\u7edd\u5b66\u201c\u9b45\u9b51\u638c\u201d\u6765\uff01 \u53ea\u89c1\u56db\u80a1\u87ba\u65cb\u5f62\u7684\u6c14\u6d41\uff0c\u534a\u7a7a\u6c47\u5408\uff0c\u6025\u901f\u65cb\u8f6c\u7740\u5411\u5c55\u767d\u9762\u95e8\u5377\u6765\uff01 \u5c55\u767d\u7a81\u611f\u5934\u660f\u76ee\u7729\uff0c\u89c1\u90a3\u98de\u8f6c\u7684\u87ba\u65cb\u5f62\u6c14\u6d41\u4e4b\u4e2d\uff0c\u7adf\u9690\u7136\u73b0\u51fa\u4e00\u4e2a\u9b54\u9b3c\u7684\u5de8\u5934\u6765\uff0c\u62ab\u6563 \u6ee1\u5934\u957f\u53d1\uff0c\u5472\u7259\u54a7\u5634\uff0c\u773c\u5982\u94dc\u94c3\uff0c\u821e\u7740\u4e24\u53ea\u9b3c\u722a\uff0c\u72f0\u72de\u6050\u6016\u5df2\u6781\uff0c\u731b\u5411\u81ea\u5df2\u9762\u95e8\u6293\u5230\uff0c\u4e0d\u7531 \u5927\u5403\u4e00\u60ca\uff01 \u8fd9\u662f\u201c\u9b45\u9b51\u638c\u201d\u6700\u5389\u5bb3\u7684\u5730\u65b9\uff0c\u7531\u56db\u5996\u5408\u624b\u65bd\u5c55\u51fa\u6765\uff0c\u6c14\u6d41\u6253\u65cb\uff0c\u5f62\u6210\u5e7b\u8c61\uff0c\u4f7f\u5bf9\u65b9\u7591 \u60e7\u5931\u795e\u4e4b\u4e2d\uff0c\u53d7\u4f24\u4e8e\u65e0\u5f62\uff0c\u53ef\u4ee5\u8bf4\u662f\u5389\u5bb3\u65e0\u6bd4\uff01 \u5c55\u767d\u867d\u7136\u8fde\u7ecf\u6076\u6218\uff0c\u4f1a\u8fc7\u4e0d\u5c11\u6b66\u6797\u9ad8\u624b\uff0c\u4f46\u4ece\u672a\u89c1\u8fc7\u5982\u6b64\u602a\u73b0\u8c61\uff0c\u4ee5\u4e3a\u5bf9\u65b9\u4f1a\u65bd\u90aa\u6cd5\uff0c \u60ca\u6016\u4e4b\u4e2d\u6e29\u634c\u9a70\u4e00\u638c\uff0c\u76f4\u5411\u90a3\u9b54\u9b3c\u7684\u5de8\u5934\u51fb\u51fa\uff01 \u201c\u5929\u4f5b\u964d\u9b54\u638c\u201d\u7acb\u663e\u65e0\u6bd4\u5a01\u529b\uff0c\u53ea\u89c1\u52b2\u6d41\u5c71\u6d8c\uff0c\u201c\u8f70\u9686\uff01\u201d\u4e00\u58f0\u767e\u9707\uff0c\u72b9\u5982\u6674\u5929\u8d77\u4e86\u4e00 \u4e2a\u9739\u96f7\uff0c\u90a3\u9b54\u9b3c\u5de8\u5934\u7684\u5e7b\u8c61\u7acb\u523b\u6d88\u5931\uff0c\u56db\u5996\u88ab\u9707\u5f97\u4e00\u8def\u8ddf\u8dea\uff0c\u56db\u6563\u9000\u51fa\u4e00\u6587\u5f00\u5916\uff0c\u53cc\u773c\u77b3\u5b54 \u6da8\u5927\uff0c\u8eab\u5f62\u64ad\u6447\u6b32\u5012\uff01 \u5c55\u767d\u4e00\u638c\u594f\u529f\uff0c\u795e\u5a01\u5927\u9707\uff0c\u53cc\u811a\u4e00\u8dfa\uff0c\u8eab\u5f62\u51cc\u7a7a\u800c\u8d77\uff0c\u76f4\u7a9c\u6709\u4e94\u516d\u6587\u9ad8\uff0c\u5019\u7136\u6253\u8170\u5c48 \u817f\uff0c\u5934\u4e0a\u811a\u5382\u9f9f\u8dc3\u659c\u63a0\uff0c\u534a\u7a7a\u4e2d\u4e00\u62db\u201c\u4f5b\u7956\u964d\u5ea7\u201d\uff0c\u53cc\u638c\u631f\u98ce\u8fc5\u96f7\u4e4b\u52bf\uff0c\u731b\u5411\u53e6\u5916\u56db\u5996\u5f53\u5934 \u5288\u4e0b\uff01 \u53e6\u5916\u56db\u5996\u89c1\u5c55\u767d\u4e00\u4e2a\u4e8c\u5341\u5c81\u5e74\u7eaa\u7684\u5c11\u5e74\uff0c\u6b66\u529f\u7adf\u5927\u5f97\u51fa\u5947\uff0c\u53ea\u4e00\u638c\u5c31\u628a\u81ea\u5df1\u56db\u4f4d\u5144\u5f1f\u9707 \u4f24\uff0c\u63a5\u7740\u53ea\u89c1\u5c55\u767d\u51cc\u7a7a\u5411\u4ed6\u56db\u4eba\u6251\u6765\uff0c\u62db\u5f0f\u66f4\u5a01\u731b\uff0c\u56db\u5996\u540c\u65f6\u5403\u60ca\uff0c\u4e0d\u6562\u786c\u63a5\uff0c\u65bd\u51fa\u201c\u9b3c\u98d8 \u98ce\u201d\u8eab\u6cd5\uff0c\u201c\u6ecb\u6e9c\uff01\u6ecb\u6e9c\uff01\u6ecb\u6e9c\uff01\u2026\u2026\u201d\u6570\u58f0\u5fae\u54cd\uff0c\u604d\u5982\u9b3c\u9b45\uff0c\u56db\u6563\u8dc3\u51fa\u4e00\u4e08\u5f00 \u5916\uff01 \u4f46\u5c55\u767d\u542c\u8339\u8001\u9556\u5934\u8bf4\u4e0d\u8981\u653e\u8d3c\u5b50\u8dd1\u4e86\uff0c\u5df2\u7ecf\u4e0b\u51b3\u5fc3\u8981\u628a\u51e0\u4eba\u6401\u4e0b\uff0c\u62db\u5f0f\u65bd\u51fa\uff0c\u89c1\u56db\u5996\u7115 \u7136\u9038\u51fa\u62db\u5916\uff0c\u5f53\u5373\u53cc\u81c2\u4e00\u5212\uff0c\u8eab\u5f62\u7adf\u5982\u4e00\u53ea\u5927\u9e70\u4e00\u822c\uff0c\u6a2a\u7a7a\u7ff1\u7fd4\uff0c\u534a\u7a7a\u4e2d\u65bd\u51fa\u201c\u5929\u4f5b\u964d\u9b54 \u638c\u201d\u7edd\u5b66\u4e2d\u4e00\u638c\u201c\u4f5b\u5149\u666e\u70c8\u201d\u6765\uff1a \u53ea\u89c1\u5c55\u767d\u5982\u5927\u9e1f\u6a2a\u7a7a\uff0c\u8eab\u5f62\u51cc\u7a7a\u98de\u8f6c\uff0c\u4f3c\u5f71\u968f\u5f62\uff0c\u6bcf\u81f3\u4e00\u5996\u4e0a\u7a7a\u5373\u53d1\u51fa\u4e00\u638c\uff01 \u53ea\u542c\u201c\u562d\uff01\u562d\uff01\u562d\uff01\u2026\u2026\u201d\u5de8\u54cd\u63a5\u8fde\u54cd\u8d77\uff01 \u4e00\u664c\u662f\u5c55\u767d\u65bd\u51fa\u7684\u4e00\u638c\uff0c\u4e00\u638c\u6253\u7ffb\u4e00\u5996\uff0c\u53ea\u89c1\u638c\u98ce\u547c\u547c\uff0c\u6f2b\u7a7a\u800c\u4e0b\uff0c\u201c\u562d\u562d\u201d\u5de8\u54cd\uff0c\u98de \u6284\u626c\u5c18\uff0c\u5c31\u5728\u90a3\u5c18\u98de\u6284\u626c\u7684\u638c\u98ce\u4e4b\u4e2d\uff0c\u516b\u5996\u6b21\u7b2c\u7ffb\u6eda\u8dcc\u722c\uff0c\u60e8\u547c\u5389\u543c\uff0c\u95f9\u6210\u4e00\u56e2\uff01 \u201c\u71d5\u4eac\u9556\u5c40\u201d\u4f17\u9556\u5e08\u6df1\u53d7\u516b\u5996\u837c\u6bd2\uff0c\u89c1\u72b6\u4e0d\u7531\u4e2a\u4e2a\u6b23\u7136\u8272\u559c\uff0c\u7adf\u9f50\u58f0\u6b22\u547c\u8d77 \u6765\uff01\u2026\uff0e\uff0e\xb7 \u8339\u8001\u9556\u5934\u4e0d\u4f4f\u5730\u5934\u70b9\u8d5e\u53f9\uff0c\u5982\u4e0d\u662f\u81ea\u5df1\u4eb2\u773c\u76ee\u7779\uff0c\u771f\u4e0d\u76f8\u4fe1\u4e16\u4e0a\u7ade\u6709\u5982\u8bb8\u9ad8\u5f3a\u6b66\u529f\uff01 \u66f4\u4e0d\u77e5\u5c55\u767d\u5c0f\u5c0f\u5e74\u7eaa\uff0c\u53ea\u4e0d\u5230\u4e00\u5e74\u672a\u89c1\uff0c\u8fd9\u4e00\u8eab\u7edd\u827a\u662f\u4f55\u5904\u5b66\u6765\uff1f \u5c31\u8fde\u6df1\u77e5\u5c55\u767d\u5e95\u7ec6\u7684\u67f3\u7fe0\u7fe0\uff0c\u89c1\u5c55\u767d\u80fd\u5fa1\u7a7a\u98de\u884c\uff0c\u8fde\u73af\u51fa\u62db\uff0c\u4e5f\u4e0d\u7531\u6697\u6697\u7eb3\u95f7\uff0c\u4e0d\u77e5\u9053 \u767d\u54e5\u54e5\u65e9\u6709\u7edd\u5b66\u5728\u8eab\u6df1\u85cf\u4e0d\u9732\uff0c\u8fd8\u662f\u53e6\u6709\u5947\u9047\uff1f\u7ade\u5177\u6709\u8fd9\u6837\u9a87\u4e16\u60ca\u4fd7\u7684\u7edd\u9ad8\u8eab\u624b\uff01 \u56e0\u4e3a\u8fd9\u201c\u5929\u4f5b\u964d\u9b54\u638c\u201d\u6cd5\uff0c\u662f\u5979\u6307\u70b9\u5c55\u767d\u7ec3\u6210\u7684\uff0c\u201c\u4f5b\u5149\u666e\u7167\u201d\u62db\u5f0f\uff0c\u56fa\u7136\u4e5f\u662f\u51cc\u7a7a\u4e0b \u51fb\uff0c\u4f46\u5374\u4e0d\u662f\u8fd9\u79cd\u51cc\u7a7a\u7ff1\u7fd4\u3001\u8fde\u73af\u51fa\u624b\u7684\u7ae0\u6cd5\uff01 \u4e8b\u5b9e\u4e0a\uff0c\u5c55\u81ea\u662f\u89e6\u7c7b\u65c1\u901a\uff0c\u81ea\u884c\u4f53\u4f1a\uff0c\u5bf9\u654c\u4e4b\u9645\u968f\u673a\u5e94\u53d8\uff0c\u4e34\u65f6\u60f3\u51fa\u6765\u7684\uff0c\u751a\u81f3\u8fde\u4ed6\u81ea \u5df1\u4e5f\u6ca1\u60f3\u5230\uff0c\u8eab\u5f62\u51cc\u7a7a\u8fde\u73af\u51fa\u62db\uff0c\u7adf\u80fd\u7ef4\u6301\u4e0d\u5760\uff01 \u4ed6\u51e0\u6b21\u89c1\u201c\u94c1\u7ffc\u98de\u9e4f\u201d\u5df4\u5929\u8d6b\u5bf9\u654c\u8fc7\u62db\uff0c\u94c1\u7ffc\u731b\u6325\uff0c\u51cc\u7a7a\u800c\u8d77\uff0c\u534a\u7a7a\u4e2d\u53cc\u81c2\u5212\u52a8\uff0c\u5c48\u817f \u8fde\u5f39\uff0c\u7eb5\u6a2a\u5982\u610f\uff0c\u62db\u5f0f\u5a01\u731b\uff0c\u73b0\u5728\u4ed6\u4ee5\u53cc\u81c2\u4ee3\u7ffc\uff0c\u51cc\u7a7a\u800c\u8d77\uff0c\u4e00\u9635\u5212\u72ec\uff0c\u7adf\u7136\u865a\u7a7a\u6a2a\u98de\uff0c\u53cc \u817f\u4e00\u5f39\uff0c\u7ade\u80fd\u8f6c\u6298\u5982\u610f\uff0c\u53c8\u501f\u7740\u638c\u529b\u4e0b\u5288\u7684\u53cd\u9707\u4e4b\u529b\uff0c\u4f7f\u4ed6\u80fd\u5728\u7a7a\u4e2d\u7ef4\u6301\u4e0d\u5760\uff0c\u56e0\u6b64\uff0c\u4ed6\u7adf \u5b66\u8d77\u201c\u94c1\u7ffc\u98de\u9e4f\u201d\u6765\uff0c\u4eba\u5982\u5927\u9e1f\uff0c\u5728\u534a\u7a7a\u4e2d\u5411\u201c\u9b54\u9b3c\u5c9b\u516b\u5996\u201d\u63a5\u8fde\u653b\u51fb\uff01 \u5f53\u7136\uff0c\u8fd9\u4e5f\u662f\u7531\u4e8e\u5c55\u767d\u5185\u529f\u771f\u529b\u5df2\u6709\u4e86\u76f8\u5f53\u9ad8\u7684\u706b\u5019\u80fd\u591f\u63d0\u4f4f\u4e00\u53e3\u771f\u6c14\uff0c\u6240\u4ee5\u624d\u80fd\u51cc\u7a7a \u4e0d\u5760\uff0c\u82e5\u662f\u5185\u529f\u706b\u5019\u4e0d\u5230\u5bb6\uff0c\u4f9d\u7136\u662f\u5b66\u4e0d\u6765\u7684\u3002 \u4e0d\u8fc7\uff0c\u4ed6\u521d\u6b21\u65bd\u5c55\uff0c\u638c\u529b\u65e0\u5f62\u4e2d\u6253\u4e86\u6298\u6263\uff0c\u82e5\u4e0d\u7136\u4ee5\u201c\u5929\u4f5b\u964d\u9b54\u638c\u201d\u4e98\u53e4\u7edd\u5b66\uff0c\u8fde\u756a\u6253 \u5728\u516b\u5996\u8eab\u4e0a\uff0c\u516b\u5996\u65e9\u5c31\u6ca1\u547d\u4e86\uff01 \u9976\u662f\u8fd9\u6837\uff0c\u628a\u516b\u5996\u4e5f\u6253\u5f97\u6655\u5934\u8f6c\u5411\uff0c\u6ee1\u5730\u8dcc\u6eda\uff0c\u72fc\u72c8\u4e07\u72b6\uff01 \u90a3\u56db\u4e2a\u8499\u9762\u5c11\u5973\u201c\u6843\u82b1\u56db\u4ed9\u201d\uff0c\u4e5f\u662f\u201c\u5357\u6d77\u95e8\u201d\u4e2d\u7684\u4e00\u6d41\u9ad8\u624b\uff0c\u89c1\u6b64\u5149\u666f\uff0c\u4e0d\u7531\u4e00\u4e2a\u4e2a \u5403\u60ca\u5f97\u7c89\u9762\u7ec6\u81f4 \u201c\u6843\u82b1\u56db\u4ed9\u201d\u60ca\u695e\u4e4b\u4e2d\uff0c\u89c1\u201c\u9b54\u9b3c\u5c9b\u516b\u5996\u201d\u72fc\u72c8\u4e4b\u72b6\uff0c\u53ea\u6709\u6328\u6253\u7684\u4efd\u513f\uff0c\u5df2\u65e0\u8fd8\u624b\u4e4b \u529b\uff0c\u731b\u7136\u5014\u609f\uff0c\u82e5\u8fd9\u6837\u957f\u4e45\u4e0b\u53bb\uff0c\u516b\u5996\u65e9\u665a\u4f1a\u88ab\u6253\u6b7b\uff0c\u5047\u5982\u516b\u5996\u4e00\u6b7b\uff0c\u5979\u4eec\u56db\u4eba\u4e5f\u96be\u4ee5\u9003 \u8131\uff0c\u4e8e\u662f\u4e92\u6253\u62db\u547c\uff0c\u63a2\u624b\u9769\u56ca\uff0c\u5404\u53d6\u51fa\u4e00\u628a\u201c\u6843\u82b1\u6bd2\u7634\u201d\u6765\uff0c\u8fce\u7a7a\u5411\u5c55\u767d\u6253\u53bb\uff01 \u53ea\u89c1\u56db\u84ec\u6311\u7ea2\u8272\u7684\u7ec6\u96fe\uff0c\u534a\u7a7a\u6c47\u805a\uff0c\u72b9\u5982\u4e00\u6735\u7c89\u7ea2\u8272\u7684\u7edb\u4e91\uff0c\u731b\u5411\u5c55\u767d\u5f53\u5934\u7f69\u6765\uff01 \u5c55\u81ea\u7a81\u55c5\u5230\u4e00\u822c\u5f02\u9999\u2026\u2026 \u67f3\u7fe0\u7fe0\u5728\u4e00\u65c1\u6025\u547c\u9053\uff1a\u767d\u54e5\u54e5\uff0c\u5feb\u8eb2\uff01\u90a3\u662f\u2018\u6843\u82b1\u6bd2\u7634\u2019\uff01\u201d \u6025\u547c\u58f0\u4e2d\uff0c\u7fe0\u7fe0\u8863\u8896\u4e00\u626c\uff0c\u65bd\u51fa\u4e00\u624b\u201c\u9999\u8896\u98d8\u98ce\u201d\uff0c\u72c2\u98d8\u75be\u5377\uff0c\u628a\u90a3\u5954\u5411\u5c55\u767d\u7684\u4e00\u56e2\u7c89 \u7ea2\u8272\u70df\u96fe\uff0c\u76f4\u5439\u6570\u4e08\u5f00\u5916\uff01 \u5c55\u767d\u542c\u5230\u7fe0\u7fe0\u6025\u547c\u793a\u8b66\uff0c\u53ca\u65f6\u95ed\u4efb\u547c\u5438\uff0c\u659c\u8eab\u98de\u63a0\u843d\u4e0b\u5730\u9762\uff01 \u5e78\u4e8f\u53d1\u89c9\u5c1a\u65e9\uff0c\u82e5\u4e0d\u7136\u5c55\u767d\u5df2\u88ab\u90a3\u7c89\u7ea2\u8272\u7684\u201c\u6843\u82b1\u6bd2\u7634\u201d\u6240\u4f24\uff01 \u518d\u770b\u88ab\u7fe0\u7fe0\u201c\u9999\u8896\u98d8\u98ce\u201d\u5439\u51fa\u6570\u4e08\u7684\u90a3\u56e2\u201c\u6843\u82b1\u6bd2\u7634\u201d\u5df2\u98d8\u6563\u4e86\u5f00\u6765\uff0c\u968f\u98ce\u98d8\u8361\uff0c\u7ade\u5e7f \u7f69\u5341\u6570\u4e08\u5916\u65b9\u5706\u5730\u9762\uff01 \u72b9\u5982\u5728\u4f17\u4eba\u773c\u524d\uff0c\u6d12\u4e0b\u4e86\u4e00\u5c42\u7c89\u7ea2\u8272\u7684\u6d53\u96fe\uff01 \u90a3\u7c89\u7ea2\u8272\u7684\u6d53\u96fe\uff0c\u7f13\u7f13\u8f6c\u6563\uff0c\u6240\u8fc7\u4e4b\u5904\uff0c\u5c45\u7136\u4f7f\u6b23\u6b23\u5411\u8363\u7684\u8349\u5730\u53d8\u67af\uff0c\u82cd\u7fe0\u78a7\u7eff\u7684\u6811\u6728 \u843d\u65f6\uff01 \u6709\u51e0\u4e2a\u9556\u5e08\u8eb2\u95ea\u7565\u6162\uff0c\u88ab\u7c89\u7ea2\u8272\u6d53\u96fe\u8fc7\u8eab\uff0c\u7adf\u7136\u5468\u8eab\u706b\u7ea2\uff0c\u5c16\u53f7\u7740\u5012\u5730\u6b7b\u53bb\uff01 \u597d\u5389\u5bb3\u7684\u201c\u6843\u82b1\u6bd2\u7634\u201d\uff01 \u4f17\u4eba\u4e0d\u7531\u548b\u820c\uff01 \u8db3\u6709\u4e00\u987f\u996d\u7684\u529f\u592b\uff0c\u90a3\u7c89\u7ea2\u6d53\u96fe\uff0c\u624d\u6e10\u6e10\u6d88\u6563\uff01 \u5f85\u90a3\u7c89\u7ea2\u8272\u7684\u6d53\u96fe\u6e10\u6e10\u6d88\u5931\uff0c\u518d\u770b\u201c\u9b54\u9b3c\u5c9b\u516b\u5996\u201d\u3001\u201c\u6843\u82b1\u56db\u4ed9\u201d\u53ca\u88ab\u5c55\u767d\u638c\u4f24\u7684\u201c\u4f5b \u5370\u6cd5\u5e08\u201d\uff0c\u65e9\u5df2\u9003\u4e4b\u5929\u5929\uff0c\u4e0d\u77e5\u53bb\u5411\u4e86\uff01 \u8339\u8001\u9556\u5934\u8dcc\u8db3\u957f\u53f9\uff0c\u4f17\u9556\u5e08\u6101\u7709\u82e6\u8138\uff0c\u4e24\u4f4d\u9556\u5934\u201c\u80d6\u7075\u5bab\u201d\u4e0e\u201c\u77f3\u7334\u201d\uff0c\u66f4\u662f\u6025\u5f97\u54c0\u54c0 \u75db\u54ed\u8d77\u6765\u4e86\uff01 \u5c55\u767d\u77e5\u9053\u4f17\u4eba\u662f\u4e3a\u88ab\u52ab\u8d70\u7684\u9556\u8f66\u7126\u6025\uff0c\u4f46\u4e5f\u4e0d\u5f97\u4e0d\u89c1\u89c1\u6545\u65e7\uff0c\u968f\u5411\u8339\u8001\u9556\u5934\u65bd\u4e86\u4e00\u793c\uff0c \u9053\uff1a\u201c\u8001\u54e5\u54e5\uff0c\u4e00\u5411\u53ef\u597d\uff0c\u5c55\u767d\u56e0\u8fde\u906d\u610f\u5916\uff0c\u4e45\u672a\u62dc\u5019\uff0c\u5c1a\u8bf7\u8001\u54e5\u54e5\u539f\u8c05\uff01\u201d \u8339\u8001\uff0c\u53c8\u89c1\u5c55\u767d\u5b66\u5230\u4e86\u60ca\u4eba\u7684\u6b66\u529f\uff0c\u8001\u6000\u4e5f\u9887\u611f\u6b23\u6170\uff0c\u4f46\u6b64\u65f6\u4ed6\u5374\u6ca1\u6709\u5fc3\u60c5\u4e3a\u5c55\u767d\u9ad8 \u5174\uff0c\u8fde\u6545\u53cb\u4e4d\u89c1\u7684\u4eb2\u70ed\u52b2\u4e5f\u63d0\u4e0d\u8d77\u6765\uff0c\u53ea\u4e0d\u4f4f\u5730\u6447\u5934\u957f\u53f9\uff01 \u5c55\u767d\u9053\uff1a\u201c\u8001\u54e5\u54e5\u4eb2\u81ea\u51fa\u9a6c\uff0c\u4e0d\u77e5\u4fdd\u7684\u662f\u4ec0\u4e48\u8d35\u91cd\u4e4b\u7269\uff1f\u201d \u8339\u8001\u9556\u5934\u53c8\u957f\u53f9\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u5c55\u8d24\u5f1f\uff0c\u5b9e\u4e0d\u76f8\u7792\uff0c\u8fd9\u8d9f\u9556\u4e43\u662f\u6d4e\u5357\u5e9c\u4e00\u767e\u96f6\u516b\u53bf\u7684\u5168 \u90e8\u9971\u94f6\uff0c\u5171\u662f\u9ec4\u91d1\u4e09\u5341\u4e07\u4e24\uff0c\u5982\u82e5\u5931\u4e86\uff0c\u8001\u54e5\u54e5\u5c31\u662f\u503e\u5bb6\u8361\u4ea7\u4e5f\u8d54\u4e0d\u8d77\uff01\u201d \u5c55\u767d\u4e00\u542c\uff0c\u4e5f\u6697\u6697\u4e3a\u4e4b\u5fc3\u6025\u3002\u2026\u2026 \u7fe0\u7fe0\u5728\u4e00\u65c1\u7b11\u9053\uff1a\u201c\u4e22\u4e86\u54ea\u91cc\u627e\uff0c\u5149\u662f\u7126\u6025\u6709\u4ec0\u4e48\u7528\uff01\u201d \u5c55\u767d\u4e00\u6401\u624b\uff0c\u9053\uff1a\u201c\u5bf9\uff01\u8d3c\u4eba\u5728\u6b64\u52ab\u9556\uff0c\u60f3\u5fc5\u5de2\u7a74\u4e5f\u79bb\u6b64\u4e0d\u8fdc\uff0c\u6211\u4eec\u5e2e\u5fd9\u8001\u54e5\u54e5\u628a\u9977\u94f6 \u627e\u56de\u5c31\u662f\u4e86\uff01\u201d \u8339\u8001\u9556\u5934\u5df2\u89c1\u8bc6\u8fc7\u5c55\u767d\u4e0e\u7fe0\u7fe0\u7684\u6b66\u529f\uff0c\u77e5\u9053\u6709\u4e86\u4e8c\u4eba\u5e2e\u5fd9\uff0c\u4e0d\u96be\u628a\u9977\u94f6\u627e\u56de\u6765\uff0c\u7acb\u523b\u8f6c \u5fe7\u4e3a\u559c\uff0c\u9053\uff1a\u201c\u80fd\u6709\u4e8c\u4f4d\u5e2e\u5fd9\uff0c\u8001\u592b\u611f\u6fc0\u4e0d\u5c3d\u4e86\uff01\u201d \u5c55\u767d\u9053\uff1a\u201c\u8001\u54e5\u54e5\uff0c\u54ea\u91cc\u8bdd\u6765\uff1f\u60f3\u5f53\u521d\u5c55\u767d\u843d\u9b44\u6c5f\u6e56\uff0c\u8fd8\u4e0d\u662f\u591a\u4e8f\u8001\u54e5\u54e5\u5e2e\u5fd9\uff0c\u73b0\u5728\u8001 \u54e5\u54e5\u6709\u4e8b\uff0c\u5c55\u767d\u7406\u5e94\u6548\u529b\uff01\u201d \u8339\u8001\u9556\u5934\u6447\u5934\u9053\uff1a\u201c\u8d24\u5f1f\u5e2e\u5fd9\uff0c\u8001\u54e5\u54e5\u5f53\u7136\u6ca1\u8bdd\u8bf4\uff0c\u53ef\u662f\uff0c\u8fd9\u4f4d\u2014\u2014\u201d \u8bf4\u7740\u5411\u67f3\u7fe0\u7fe0\u4e00\u62f1\u624b\uff0c\u9053\uff1a\u201c\u5144\u53f0\uff0c\u8001\u592b\u4e0e\u4e4b\u7d20\u6627\u5e73\u751f\uff0c\u7531\u5176\u4e49\u4f38\u63f4\u624b\uff0c\u8001\u592b\u5f53\u7136\u8981\u611f \u6fc0\u4e0d\u5c3d\u4e86\uff01\u201d \u2018\u5c55\u767d\u671b\u4e86\u7fe0\u7fe0\u4e00\u773c\uff0c\u9053\uff1a\u201c\u8fd9\u4f4d\u4e5f\u4e0d\u662f\u5916\u4eba\uff0c\u5979\u662f\u2026\u2026\u201d \u5c55\u767d\u672c\u60f3\u5411\u8339\u8001\u9556\u5934\u5f15\u89c1\u7fe0\u7fe0\uff0c\u4f46\u8bdd\u5230\u5507\u8fb9\uff0c\u60f3\u5230\u7fe0\u7fe0\u5e26\u4e86\u9762\u5177\uff0c\u4e0d\u597d\u66b4\u9732\u8eab\u4efd\uff0c\u6545\u800c \u53c8\u4e34\u65f6\u6536\u5634\u672a\u8bf4\u51fa\u6765\u3002 \u7fe0\u7fe0\u5374\u63a5\u53e3\u7b11\u9053\uff1a\u201c\u6211\u662f\u9b3c\u9762\u4eba\uff0c\u4ee5\u540e\u8bf7\u8001\u9556\u5e08\u591a\u6307\u6559\uff01\u201d \u8339\u8001\u9556\u5934\u4f55\u7b49\u9605\u5386\uff0c\u4ece\u7fe0\u7fe0\u8bf4\u8bdd\u4e0e\u8d70\u8def\u3001\u8eab\u6750\u7b49\u5404\u65b9\u9762\u60c5\u72b6\uff0c\u65e9\u5df2\u770b\u51fa\u7fe0\u7fe0\u7684\u9752\u8138\u7ea2\u53d1 \u662f\u5316\u88c5\uff0c\u4f46\u4e5f\u4e0d\u8bf4\u7834\uff0c\u968f\u6de1\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e45\u4ef0\uff01\u4e45\u4ef0\uff01\u201d \u4e8b\u5b9e\u4e0a\uff0c\u5728\u5f53\u4eca\u6b66\u6797\uff0c\u8c01\u4e5f\u6ca1\u542c\u8bf4\u8fc7\u6709\u201c\u9b3c\u9762\u4eba\u201d\u8fd9\u4e48\u4e00\u53f7\u3002 \u5c55\u767d\u89c1\u7fe0\u7fe0\u5982\u6b64\u8bf4\uff0c\u5c31\u66f4\u4e0d\u597d\u4e3a\u5176\u5410\u5b9e\uff0c\u968f\u8f6c\u53d8\u8bdd\u950b\u9053\uff1a\u201c\u65e5\u4e45\u751f\u53d8\uff0c\u6211\u4eec\u4e0d\u5b9c\u5ef6\u6401\uff0c \u8fd8\u662f\u65e9\u4e9b\u67e5\u8bbf\u8d3c\u4eba\u4e0b\u843d\uff0c\u65e9\u65e5\u8ffd\u56de\u9556\u94f6\uff0c\u65b9\u4e3a\u4e0a\u7b56\uff01\u201d \u8339\u8001\u9556\u5934\u5f53\u7136\u662f\u6c42\u4e4b\u4e0d\u5f97\uff0c\u5927\u5bb6\u805a\u5728\u4e00\u8d77\uff0c\u4e92\u76f8\u8ba8\u8bba\u4e86\u4e00\u756a\uff0c\u7fe0\u7fe0\u4f3c\u662f\u80f8\u6709\u6210\u7af9\uff0c\u544a\u8bc9 \u5927\u5bb6\u53ea\u8981\u5bfb\u7740\u9556\u8f66\u7684\u8f68\u8ff9\uff0c\u6309\u56fe\u7d22\u9aa5\uff0c\u4e00\u5b9a\u53ef\u4ee5\u627e\u5230\u8d3c\u4eba\u8001\u5de2\uff01 \u4f17\u4eba\u9f50\u8d5e\u9ad8\u660e\uff0c\u968f\u627e\u5230\u8198\u8f66\u7684\u8f68\u8ff9\u53bb\u5411\uff0c\u4e00\u8def\u8ffd\u5bfb\u4e0b\u53bb\uff01 \u90a3\u9556\u8f66\u8f68\u8ff9\uff0c\u4e00\u8def\u9a70\u5411\u6d4e\u5357\uff0c\u4f46\u4e34\u5230\u57ce\u5185\u65f6\uff0c\u9556\u8f66\u8f68\u8ff9\u62d0\u4e86\u5f2f\uff0c\u5230\u5929\u9ed1\u65f6\uff0c\u6765\u5230\u4e00\u5ea7\u9ad8 \u5927\u5e9c\u7b2c\u4e4b\u524d\uff01 \u4e00\u7247\u9ad8\u5927\u697c\u623f\uff0c\u8fde\u5730\u800c\u8d77\uff0c\u5360\u5730\u6015\u4e0d\u6709\u767e\u9877\u4e4b\u591a\uff0c\u90a3\u6797\u7acb\u7684\u9ad8\u697c\u98de\u6a90\u8038\u7259\uff0c\u5728\u6ee1\u5929\u971e\u5f71 \u91cc\u663e\u51fa\u4e00\u756a\u96c4\u4f1f\u6c14\u8c61\uff01 \u90a3\u9ad8\u697c\u56db\u5468\u6709\u4e00\u9053\u9ad8\u5899\u56f4\u7ed5\uff0c\u5899\u9ad8\u6709\u4e24\u4e08\uff0c\u95e8\u524d\u7bad\u8dfa\uff0c\u4e0d\u4e9a\u5982\u4e00\u5ea7\u5c0f\u57ce\uff0c\u5899\u5916\u8fd8\u6709\u4e00\u9053 \u62a4\u5899\u6cb3\uff0c\u770b\u8d77\u6765\u6c9f\u6df1\u57ce\u56fa\uff0c\u800c\u4e14\u95e8\u697c\u7bad\u8dfa\u4e0a\u5201\u6597\u68ee\u4e25\uff0c\u4eba\u5f71\u95ea\u52a8\uff0c\u770b\u6837\u5b50\u8b66\u536b\u4e5f\u9887\u4e25\u5bc6\uff01 \u90a3\u9556\u8f66\u7684\u8f68\u8ff9\uff0c\u5374\u76f4\u9a70\u5411\u697c\u623f\u4e4b\u5185\uff0c\u4f46\u6b64\u65f6\u62a4\u57ce\u6cb3\u7684\u540a\u6865\u5df2\u7ecf\u60ac\u8d77\uff0c\u90a3\u9053\u62a4\u57ce\u6cb3\uff0c\u8db3\u6709 \u5341\u6570\u4e08\u5bbd\uff0c\u800c\u4e14\u9ad8\u5899\u4e0a\u90a3\u4e48\u591a\u660e\u5361\u6697\u6869\uff0c\u770b\u6837\u5b50\u8981\u60f3\u8fdb\u53bb\uff0c\u771f\u6bd4\u767b\u5929\u8fd8\u96be\uff01 \u8339\u8001\u9556\u5934\u4e00\u6014\uff0c\u9053\uff1a\u201c\u8fd9\u4e0d\u662f\u2018\u6458\u661f\u624b\u2019\u6155\u5bb9\u6db5\u7684\u2018\u8c79\u7a81\u5c71\u5e84\u2019\u5417\u2019\uff1f\u96be\u9053\u540d\u5217\u2018\u6b66\u6797 \u56db\u5927\u516c\u5b50\u2019\u4e4b\u4e00\u7684\u51cc\u98ce\u516c\u5b50\u2019\uff0c\u4f1a\u4e0e\u2018\u5357\u6d77\u95e8\u2019\u6c86\u7023\u4e00\u6c14\uff0c\u62a2\u52ab\u8001\u592b\u7684\u9556\u8f66\u5417\uff1f\u201d \u5c55\u767d\u4e00\u542c\u8339\u8001\u9556\u5934\u8ba4\u51fa\u8fd9\u7247\u697c\u623f\u4fbf\u662f\u201c\u8c79\u7a81\u5c71\u5e84\u201d\uff0c\u4e0d\u7531\u4e00\u8def\uff0c\u4ed4\u7ec6\u8fa8\u8ba4\uff0c\u679c\u7136\u4f3c\u66fe\u76f8 \u8bc6\uff01 \u4e00\u5e55\u5e55\u5f80\u4e8b\u84e6\u7136\u515c\u4e0a\u5fc3\u6765\uff01 \u5c55\u767d\u611f\u6982\u4e07\u5343\uff0c\u4e00\u65f6\u6014\u4e86\u2026\u2014\u2019 \u8339\u8001\u9556\u5934\u5374\u6c14\u547c\u547c\u5730\u9053\uff1a\u201c\u5218\u4e09\uff01\u62ff\u62dc\u5e16\u6765\uff01\u201d \u201c\u5feb\u9a6c\u201d\u5218\u4e09\u5e94\u58f0\u4e0a\u524d\uff0c\u5531\u4e86\u4e2a\u5927\u8bfa\uff0c\u7acb\u523b\u4ece\u80cc\u56ca\u4e2d\uff0c\u53d6\u51fa\u4e00\u8c7a\u5927\u7ea2\u62dc\u5e16\uff0c\u53cc\u624b\u5949\u4e0a\uff01 \u7fe0\u7fe0\u5728\u4e00\u65c1\u7b11\u9053\uff1a\u201c\u8001\u9556\u5934\uff0c\u4f60\u62ff\u62dc\u5e16\u5e72\u4ec0\u4e48\uff1f\u201d \u8339\u8001\u9556\u5934\u9053\uff1a\u201c\u8001\u592b\u4e0e\u6155\u5bb9\u6db5\u6709\u4e00\u9762\u4e4b\u8bc6\uff0c\u6ca1\u60f3\u5230\u4ed6\u7ade\u6d3e\u4eba\u52ab\u593a\u8001\u592b\u7684\u9556\u8f66\uff0c\u73b0\u5728\u8001\u592b \u6309\u6b66\u6797\u89c4\u77e9\u62dc\u5e84\uff0c\u770b\u4ed6\u6709\u4f55\u8bdd\u8bf4\uff1f\u201d \u8bf4\u7f62\u6c14\u54bb\u54bb\u5730\u5bf9\u8eab\u8fb9\u4e00\u4e2a\u53eb\u505a\u201c\u591a\u81c2\u718a\u201d\u9b4f\u5929\u6210\u7684\u9556\u5e08\u8bf4\u9053\uff1a\u201c\u9b4f\u8001\u5e08\uff0c\u4f60\u8f9b\u82e6\u4e00\u8d9f\uff0c \u9762\u89c1\u6155\u5bb9\u6db5\uff0c\u8bf4\u8001\u592b\u2018\u94c1\u638c\u9707\u6cb3\u6714\u2019\uff0c\u4eb2\u81ea\u62dc\u5e84\uff01\u201d \u201c\u591a\u81c2\u718a\u201d\u9b4f\u5929\u6210\u63a5\u8fc7\u62dc\u5e16\uff0c\u5927\u8e0f\u6b65\u5730\u5411\u5e84\u524d\u8d70\u53bb\uff01 \u7fe0\u7fe0\u5728\u4e00\u65c1\u9053\uff1a\u6211\u770b\u514d\u4e86\u7f62\uff01\u6155\u5bb9\u6db5\u4e0d\u4e00\u5b9a\u505a\u5f97\u4e86\u4e3b\uff01\u800c\u4e14\u4ed6\u4e5f\u4e0d\u4f1a\u63a5\u89c1\u4f60\uff01\u201d \u8339\u8001\u9556\u5934\u9053\uff1a\u201c\u6b66\u6797\u89c4\u77e9\uff01\u8001\u592b\u60f3\u4ed6\u6155\u5bb9\u6db5\u518d\u72c2\uff0c\u4e5f\u4e0d\u4f1a\u4e0d\u9075\uff01\u9b4f\u8001\u5e08\uff01\u4f60\u53bb\u5427\uff01\u201d \u7fe0\u7fe0\u53ea\u5029\u7136\u4e00\u7b11\uff0c\u4e0d\u518d\u8a00\u8bed\u3002 \u201c\u591a\u81c2\u718a\u201c\u9b4f\u5929\u6210\u5374\u5927\u6b65\u5b9a\u81f3\u6865\u8fb9\uff0c\u626c\u58f0\u53eb\u9053\uff1a\u201c\u8c79\u7a81\u5c71\u5e84\u2019\u5b88\u95e8\u4eba\u542c\u7740\uff01\u4eca\u6709\u2018\u71d5\u4eac \u9556\u5c40\u201d\u94c1\u638c\u9707\u6cb3\u6714\u2019\u8339\u8001\u9556\u5934\uff0c\u4eb2\u81ea\u62dc\u5e84\uff01\u201d \u201c\u591a\u81c2\u718a\u201d\u4eba\u9ad8\u58f0\u5927\uff0c\u6c14\u8d2f\u4e39\u7530\uff0c\u653e\u5f00\u5589\u5499\u8fd9\u4e00\u558a\uff0c\u58f0\u9707\u56db\u91ce\uff0c\u6015\u4e0d\u4f20\u51fa\u6709\u6570\u91cc\u4e4b\u9065\uff01 \u4f46\u5899\u8dfa\u4e0a\u5374\u9605\u7136\u65e0\u58f0\uff01 \u201c\u591a\u81c2\u718a\u201d\u53c8\u8fde\u53eb\u4e86\u6570\u58f0\uff0c\u4ecd\u7136\u65e0\u4eba\u56de\u7b54\uff0c\u800c\u4e14\u8fde\u5899\u8dfa\u4e0a\u95ea\u663e\u7684\u4eba\u5f71\u4e5f\u9690\u853d\u4e86\u8eab\u5f62\uff0c\u5bc2 \u9759\u65e0\u58f0\uff0c\u6709\u5982\u4e00\u5ea7\u6b7b\u57ce\uff01 \u201c\u591a\u81c2\u718a\u201d\u706b\u4e0a\u6765\uff0c\u4ece\u574f\u5185\u638f\u51fa\u4e00\u679a\u201c\u6708\u82bd\u9556\u201d\u6765\uff0c\u6296\u624b\u5411\u60ac\u540a\u6865\u7684\u7ef3\u7d22\u4e0a\u6253\u53bb\uff01 \u201c\u54d7\u5566\u5566\uff01\u201d\u8f98\u8f73\u8fd0\u664c\uff0c\u60ac\u6302\u540a\u6865\u7684\u7ef3\u7d22\u7ade\u88ab\u201c\u591a\u81c2\u718a\u201d\u4e00\u9556\u6253\u65ad\uff0c\u4e00\u5ea7\u6c0f\u8fbe\u5341\u4f59\u4e08\u7684 \u540a\u6865\uff0c\u7ade\u8f70\u7136\u6389\u4e0b\u6765\uff01 \u201c\u591a\u81c2\u718a\u201d\u771f\u4e0d\u6127\u662f\u4e00\u6761\u786c\u6c49\uff0c\u7ade\u624b\u4e3e\u5927\u7ea2\u62dc\u5e16\uff0c\u6602\u7136\u8de8\u4e0a\u540a\u6865\uff01 \u5899\u8dfa\u4e0a\u4f9d\u7136\u6ca1\u6709\u54cd\u52a8\uff0c\u201c\u591a\u81c2\u718a\u201d\u8de8\u5f00\u5927\u6b65\u76f4\u8d70\u81f3\u6865\u4e2d\u95f4\uff0c\u5899\u8dfa\u4e0a\u8fd8\u662f\u5bc2\u9759\u65e0\u58f0\uff01 \u663e\u7136\u8fd9\u662f\u4e0d\u7965\u4e4b\u5146\uff01 \u5c55\u767d\u4e5f\u4e0d\u7981\u4e3a\u8fd9\u573a\u9762\u665a\u56de\u5fc3\u795e\uff0c\u770b\u201c\u591a\u81c2\u718a\u201d\u8fd9\u79cd\u4e3a\u603b\u9556\u5934\u4e00\u53e5\u8bdd\u5c31\u53bb\u5356\u547d\u7684\u8c6a\u4e3e\uff0c\u4e0d \u7531\u5927\u4e3a\u611f\u52a8\uff01 \u8fd9\u56fa\u7136\u662f\u7531\u4e8e\u8339\u8001\u9556\u5934\u7d20\u5f97\u4eba\u5fc3\uff0c\u4f46\u4e5f\u5f97\u9047\u7740\u50cf\u201c\u591a\u81c2\u718a\u201d\u8fd9\u822c\u8840\u6027\u7684\u6c49\u5b50\uff0c\u4e3a\u4e86\u8fbe\u6210 \u4f7f\u547d\uff0c\u771f\u662f\u8d74\u6eba\u8e48\u706b\u5728\u6240\u4e0d\u8f9e\uff01 \u773c\u770b\u201c\u591a\u81c2\u718a\u201d\u624b\u4e2d\u6367\u5927\u7ea2\u62dc\u5e16\uff0c\u5df2\u8d70\u8fc7\u540a\u6865\uff0c\u5c06\u81f3\u5e84\u95e8\u4e86\uff0c\u7a81\u7136\u5899\u8dfa\u4e0a\u4e00\u9635\u72e0\u54cd\uff0c\u6a2a \u7a7a\u5c04\u4e0b\u4e00\u6392\u786c\u5f29\u6765\uff01 \u201c\u591a\u81c2\u718a\u201d\u5927\u543c\u4e00\u58f0\uff0c\u53cc\u638c\u7ffb\u98de\uff0c\u7acb\u523b\u628a\u4e00\u6392\u786c\u5f29\u9707\u98de\uff1a \u4f46\u5899\u8dfa\u4e0a\u4e71\u7bad\uff0c\u5982\u98de\u8757\u4f3c\u7684\u7eed\u5c04\u4e0b\u6765\uff01 \u201c\u591a\u81c2\u718a\u201d\u624b\u5fd9\u811a\u4e71\uff0c\u7acb\u523b\u9677\u4e8e\u5371\u5883\uff01\u8339\u8001\u9556\u5934\u3001\u5c55\u767d\u3001\u7fe0\u7fe0\uff0c\u4ee5\u53ca\u4f17\u9556\u5e08\u89c1\u72b6\uff0c\u4e00\u9f50 \u98de\u8eab\u63a0\u81f3\u57ce\u8fb9\uff01 \u4f46\u201c\u591a\u81c2\u718a\u201d\u7531\u4e8e\u624b\u6267\u5927\u7ea2\u62dc\u5e16\uff0c\u51fa\u638c\u4e0d\u4fbf\uff0c\u8eab\u4e0a\u5df2\u88ab\u5c04\u4e2d\u5341\u4f59\u7bad\uff01 \u8339\u8001\u9556\u5934\u6025\u7a9c\u4e0a\u524d\uff0c\u4e00\u8fb9\u4e3e\u638c\u62e8\u6253\u4e71\u7bad\uff0c\u4e00\u8fb9\u628a\u201c\u591a\u81c2\u718a\u201d\u62d6\u81f3\u6cb3\u8fb9\u907f\u7bad\u4e4b\u5904\uff0c\u6025\u9053\uff1a \u201c\u5144\u5f1f\uff01\u82e6\u4e86\u4f60\u4e86\uff01\u201d \u201c\u591a\u81c2\u718a\u201d\u751f\u547d\u5782\u5371\uff0c\u4ecd\u5f3a\u6253\u7cbe\u795e\u628a\u62dc\u5e16\u4ea4\u8fd8\u8339\u8001\u9556\u5934\uff0c\u6709\u6c14\u65e0\u529b\u5730\u8bf4\u9053\uff1a\u201c\u5c0f\u5f1f\u6709\u8fb1 \u4f7f\u547d\uff01\u8001\u54e5\u54e5\uff01\u8fd9\u62dc\u5e16\u4ea4\u8fd8\u4f60\uff0c\u8bf7\u8001\u54e5\u54e5\u53e6\u9009\u80fd\u2026\u2026\u4eba\u2026\u2026\u201d \u8bf4\u7740\u5634\u5185\u6d8c\u51fa\u4e00\u822c\u9c9c\u8840\uff0c\u5934\u4e00\u6b6a\uff0c\u4eba\u5df2\u6b7b\u53bb\uff01 \u8fd9\u201c\u591a\u81c2\u718a\u201d\u4e43\u662f\u6253\u6697\u5668\u7684\u540d\u5bb6\uff0c\u6240\u4ee5\u624d\u5728\u6c5f\u6e56\u4e0a\u4e89\u4e86\u8fd9\u4e48\u4e2a\u7ef0\u53f7\uff0c\u6ca1\u60f3\u5230\u4ed6\u7ade\u6b7b\u5728\u4e71 \u7bad\u4e4b\u4e0b\uff01 \u8339\u8001\u82f1\u96c4\u6ef4\u4e0b\u4e24\u6ef4\u8001\u6cea\u6765\uff0c\u9ed8\u7977\u9053\uff1a\u201c\u9b4f\u5144\u5f1f\uff01\u4f60\u5b89\u606f\u5427\uff01\u8001\u54e5\u54e5\u4e00\u5b9a\u8981\u4e3a\u4f60\u62a5\u4ec7\uff01\u201d \u8bf4\u7f62\u653e\u4e0b\u201c\u591a\u81c2\u718a\u201d\u7684\u5c38\u4f53\uff0c\u7a9c\u4e0a\u6cb3\u5cb8\uff0c\u98de\u8eab\u63a0\u4e0a\u9ad8\u5899\uff01\u5899\u8dfa\u540e\u57cb\u4f0f\u7684\u7bad\u624b\uff0c\u5df2\u88ab\u4f17\u9556 \u5e08\u53ca\u5c55\u767d\u7b49\u4eba\u626b\u6e05\uff0c\u5899\u4e0a\u5899\u4e0b\uff0c\u5230\u5904\u662f\u6b7b\u5c38\uff0c\u800c\u4f17\u4eba\u5df2\u7ecf\u51b2\u5411\u5e84\u5185\u53bb\u4e86\uff01 \u8339\u8001\u9556\u5934\u867d\u7136\u4e22\u4e86\u6781\u91cd\u8981\u7684\u9556\uff0c\u5173\u7cfb\u4e00\u4e16\u82f1\u540d\u4e0e\u8eab\u5bb6\u6027\u547d\uff0c\u800c\u4e14\u53c8\u6b7b\u4f24\u4e86\u4e0d\u5c11\u9556\u5e08\u4f19 \u8ba1\uff0c\u6ee1\u80f8\u6c14\u6124\uff0c\u4f46\u5185\u5fc3\u91cc\u8fd8\u4e0d\u60f3\u591a\u9020\u6740\u5b7d\uff0c\u56e0\u4e3a\u4ed6\u4e0e\u201c\u6458\u661f\u624b\u201d\u6155\u5bb9\u6db5\u81f3\u5c11\u8fd8\u6709\u70b9\u4ea4\u60c5\uff0c\u4ed6 \u89c1\u5899\u4e0a\u5899\u4e0b\u6740\u622e\u4e4b\u60e8\uff0c\u6050\u6015\u624b\u4e0b\u9556\u5e08\u53ca\u5c55\u767d\u7b49\u8fd9\u822c\u5e74\u8f7b\u4eba\u95ef\u4e0b\u5927\u7978\uff0c\u628a\u5c40\u9762\u95f9\u5f97\u4e0d\u53ef\u6536\u62fe\uff0c \u4e8e\u662f\u7acb\u523b\u7d27\u8feb\u8fdb\u5e84\uff01 \u6b64\u65f6\u9ed1\u591c\u964d\u4e34\uff0c\u504c\u5927\u4e00\u5ea7\u201c\u8c79\u7a81\u5c71\u5e86\u201d\u7adf\u706f\u706b\u5168\u65e0\uff0c\u9ed1\u6c89\u6c89\u4e00\u5e62\u5e62\u7684\u9ad8\u697c\u5927\u53a6\uff0c\u5145\u6ee1\u4e86 \u795e\u79d8\u6050\u6016\u4e4b\u611f\uff01 \u8339\u8001\u9556\u5934\u98ce\u9a70\u7535\u63a3\uff0c\u7a9c\u623f\u8d8a\u810a\uff0c\u5411\u5e84\u5185\u7d27\u8d76\uff0c\u5c4b\u89d2\u6811\u4e1b\u7b49\u6697\u5f71\u4e4b\u5904\uff0c\u4e0d\u65f6\u6709\u6697\u5361\u5411\u8339\u8001 \u9556\u5934\u7a81\u88ad\uff01 \u8339\u8001\u9556\u5934\u4e14\u6218\u4e14\u8d70\uff0c\u4e14\u5c3d\u91cf\u907f\u514d\u6740\u4eba\uff0c\u53ea\u5411\u6709\u6740\u58f0\u4f20\u6765\u7684\u65b9\u5411\u8dd1\u53bb\uff01 \u4e00\u8fde\u95ef\u8fc7\u4e09\u5ea7\u9662\u843d\uff0c\u4f9d\u7136\u4e0d\u89c1\u4e00\u4e2a\u4eba\u5f71\uff01 \u4e0d\u4f46\u201c\u8c79\u7a81\u5c71\u5e84\u201d\u65b9\u9762\u7684\u4eba\u4e0d\u89c1\u9732\u9762\uff0c\u5373\u81ea\u5df1\u5e26\u6765\u7684\u4e8c\u4e09\u5341\u540d\u9556\u5e08\uff0c\u4ee5\u53ca\u5c55\u767d\u7fe0\u7fe0\u7b49 \u4eba\uff0c\u4e5f\u4e00\u4e2a\u90fd\u672a\u770b\u89c1\uff01 \u8339\u8001\u9556\u5934\u5fc3\u4e2d\u72af\u7591\uff0c\u4e00\u8fb9\u8dd1\u4e00\u8fb9\u6697\u81ea\u62ec\u6387\u9053\uff1a\u201c\u83ab\u975e\u81ea\u5df1\u7684\u4eba\u90fd\u88ab\u64d2\u4e86\uff1f\u201d \u201c\u5c55\u767d\u4e0e\u2018\u9b3c\u9762\u4eba\u2019\u6b66\u529f\u9ad8\u5f3a\uff0c\u83ab\u975e\u4e5f\u540c\u65f6\u9053\u4e86\u6bd2\u624b\uff1f\u201d \u201d\u8c79\u7a81\u5c71\u5e84\u2019\u9664\u4e86\u5e84\u4e3b\u2018\u6458\u661f\u624b\u2019\u6155\u5bb9\u6db5\u4e0e\u81ea\u5df1\u66fe\u6709\u8fc7\u4ece\u4e4b\u5916\uff0c\u5176\u4ed6\u8fd8\u6709\u4e0d\u5c11\u8ba4\u8bc6\u7684 \u4eba\uff0c\u600e\u4e48\u4e00\u4e2a\u719f\u4eba\u90fd\u770b\u4e0d\u89c1\uff1f\u201d \u201c\u201c \u8339\u8001\u9556\u5934\u5fc3\u601d\u7535\u8f6c\uff0c\u53c8\u8d8a\u8fc7\u4e24\u5ea7\u9662\u843d\uff0c\u6765\u5230\u4e00\u5ea7\u7c7b\u4f3c\u82b1\u56ed\u7684\u5ead\u9662\u4e4b\u4e2d\uff01 \u53ea\u89c1\u82b1\u6728\u6276\u758f\uff0c\u66f2\u680f\u5e7d\u5f84\uff0c\u663e\u51fa\u4e3b\u4eba\u5bb6\u7684\u5bcc\u8d35\u8c6a\u534e\uff0c\u4f46\u4f9d\u7136\u4e0d\u89c1\u4e00\u7ebf\u706f\u5149\uff0c\u4e00\u4e2a\u4eba\u5f71\uff01 \u4fa7\u8033\u7ec6\u542c\uff0c\u8fde\u9065\u9065\u4f20\u6765\u7684\u6740\u58f0\u90fd\u542c\u4e0d\u5230\u4e86\uff01 \u4e5f\u518d\u6ca1\u6709\u9047\u5230\u6697\u5f71\u7684\u5077\u88ad\uff01 \u6574\u4e2a\u5ead\u9662\u9759\u6e29\u5982\u6b7b\u57ce\uff0c\u7adf\u9690\u9690\u5145\u6ee1\u4e86\u6050\u6016\u7684\u6c14\u6c1b\uff01 \u8339\u8001\u9556\u5934\u9a87\u7136\u6b62\u6b65\uff0c\u53ea\u89c1\u697c\u5c16\u5929\u9645\u4e00\u94a9\u51b7\u6708\uff0c\u6ee1\u5929\u7e41\u661f\u4f3c\u4e4e\u9b3c\u8d3c\u773c\u775b\uff0c\u8377\u82b1\u6c60\u8fb9\u4e0a\u683d\u690d \u7684\u4e00\u884c\u5012\u5782\u6768\u67f3\uff0c\u5728\u591c\u8272\u4e2d\u695e\u507f\u72b9\u5982\u9b3c\u9b45\uff0c\u5e62\u5e62\u9ad8\u697c\uff0c\u706f\u5149\u5168\u65e0\uff0c\u90a3\u4e00\u4e2a\u4e2a\u9ed1\u6d1e\u7684\u697c\u7a97\uff0c\u7adf \u5982\u6076\u9b54\u5f20\u5f00\u5de8\u53e3\uff0c\u6b32\u652b\u4eba\u800c\u98df\u7684\u6837\u5b50\uff01 \u8fd9\u51fa\u4eba\u610f\u5916\u7684\u5bc2\u9759\uff0c\u7adf\u4f7f\u8d70\u5357\u95ef\u5317\u7ecf\u9a8c\u65e0\u6bd4\u4e30\u5bcc\u7684\u8339\u8001\u9556\u5934\uff0c\u5403\u60ca\u5f97\u5468\u8eab\u6c57\u6bdb\u7686\u70b8\uff01 \u56e0\u4e3a\u8fd9\u5bc2\u9759\u7684\u6c14\u6c1b\u592a\u53cd\u5e38\uff0c\u592a\u51fa\u4eba\u610f\u6599\u4e86\uff01 \u8339\u8001\u9556\u5934\u60ca\u9a87\u4e4b\u4e2d\uff0c\u7a81\u7136\u7075\u673a\u4e00\u52a8\uff0c\u65e2\u662f\u4e0d\u89c1\u4eba\u51fa\u9762\uff0c\u6211\u4f55\u4e0d\u6253\u4e2a\u62db\u547c\uff0c\u518d\u770b\u770b\u52a8\u9759\uff1f \u60f3\u81f3\u6b64\u5904\uff0c\u4e8e\u54b3\u4e86\u4e00\u58f0\uff0c\u5f00\u53e3\u53eb\u9053\uff1a\u201c\u5454\u2026\u2026\u201d \u8c01\u77e5\u4ed6\u8fd9\u4e00\u201c\u5439\u201d\uff0c\u6c34\u5858\u5047\u5c71\uff0c\u56db\u53a2\u697c\u623f\uff0c\u5904\u5904\u56de\u58f0\uff0c\u7ade\u4e00\u8fde\u4e32\u53d1\u51fa\u201c\u5454\uff01\u5454\uff01\u5454\uff01 \u5454\uff01\u2026\u2026\u201d\u7684\u559d\u58f0\uff0c\u7531\u9ad8\u800c\u4f4e\uff0c\u8fde\u7eed\u4e0d\u65ad\uff0c\u63a5\u8fde\u201c\u9632\u201d\u4e86\u6570\u5341\u58f0\uff0c\u624d\u6e10\u6e10\u9690\u606f\uff01\u201c\u560e\uff01\u560e\uff01 \u560e\uff01\u2026\u2026\u201d \u53ea\u60ca\u8d77\u4e86\u6c34\u6ee8\u4e00\u53ea\u98de\u9e1f\uff0c\u9707\u7fc5\u98de\u5411\u591c\u7a7a\uff0c\u5012\u628a\u8339\u8001\u9556\u5934\u5413\u51fa\u4e00\u8eab\u51b7\u6c57\u6765\uff01 \u8fc7\u4e86\u4e00\u4f1a\uff0c\u89c1\u56db\u5468\u4ecd\u65e0\u52a8\u9759\uff0c\u8339\u8001\u8198\u5934\u60ca\u9b42\u7a0d\u5b9a\uff0c\u624d\u5f85\u7ee7\u7eed\u53d1\u8a00\u559d\u95ee\uff0c\u7a81\u542c\u201c\u5440\u201d\u7684\u4e00 \u58f0\uff0c\u5411\u6708\u7684\u4e00\u6247\u697c\u7a97\u7a81\u7136\u5f00\u4e86\uff01 \u63a5\u7740\u53c8\u542c\u5230\u4e00\u58f0\u5e7d\u5e7d\u7684\u957f\u53f9\uff01 \u8fd9\u53f9\u606f\u58f0\u4eff\u4f5b\u662f\u575f\u5893\u4e2d\u7684\u5e7d\u9b42\u6240\u53d1\u51fa\u7684\uff0c\u90a3\u6837\u6df1\u6c89\uff0c\u5e7d\u957f\u51c4\u51c9\u800c\u60b2\u4f24\uff0c\u7b80\u76f4\u4f7f\u4eba\u4e0d\u5fcd\u5352 \u95fb\u3002 \u8339\u8001\u9556\u5934\u60ca\u6781\u56de\u987e\uff0c\u53ea\u89c1\u8fce\u7740\u60e8\u6de1\u7684\u6708\u5149\uff0c\u697c\u7a97\u5f00\u5904\u663e\u73b0\u4e86\u4e00\u4e2a\u957f\u53d1\u63a9\u9762\u3001\u9762\u767d\u5982\u7eb8\u7684 \u5973\u4eba\u5934\u8111\u6765\uff01 \u8339\u8001\u9556\u5934\u673a\u4f36\u4f36\u5730\u6253\u51e0\u4e2a\u51b7\u6218\uff0c\u810a\u6881\u80cc\u91cc\u76f4\u5192\u51b7\u6c14\uff0c\u5fc3\u8bf4\uff1a\u201c\u4eca\u591c\u53ef\u771f\u662f\u9047\u5230\u9b3c \u4e86\uff01\u2026\u2026\u201d \u56e0\u4e3a\u90a3\u5973\u4eba\u7684\u8138\uff0c\u60e8\u767d\u5982\u7eb8\uff0c\u957f\u53d1\u534a\u63a9\uff0c\u53ea\u9732\u51fa\u5446\u6ede\u7684\u53cc\u773c\u6765\uff0c\u771f\u5982\u4f20\u8bf4\u4e2d\u7684\u5973\u9b3c\uff0c\u4e00 \u822c\u65e0\u4e8c\uff01 \u4f46\u8339\u8001\u9556\u5934\u5230\u5e95\u662f\u5e38\u8d70\u6c5f\u6e56\u7684\u4eba\uff0c\u867d\u7136\u5bb3\u6015\uff0c\u8fd8\u4e0d\u81f4\u60ca\u60f6\u5931\u63aa\uff0c\u53ea\u6014\u6014\u5730\u671b\u7740\u90a3\u5973\u4eba\u53d1 \u6115\uff01 \u90a3\u697c\u7a97\u7528\u9e2d\u5375\u7c97\u7684\u94c1\u680f\u5c01\u4f4f\uff0c\u90a3\u5973\u9b3c\u53ef\u80fd\u662f\u9694\u7740\u94c1\u680f\u628a\u7a97\u63a8\u5f00\uff0c\u6b64\u65f6\u5979\u53cc\u624b\u63e1\u4f4f\u94c1\u680f\uff0c \u8fde\u90a3\u60e8\u767d\u7684\u8138\u513f\u4e5f\u7d27\u8d34\u5728\u51b0\u51b7\u7684\u94c1\u680f\u4e0a\uff0c\u7a97\u5916\u7ad9\u7740\u4e00\u4e2a\u5927\u6d3b\u4eba\uff0c\u5979\u5374\u72b9\u5982\u672a\u89c1\uff0c\u53ea\u53cc\u773c\u9065\u671b \u7740\u5929\u9645\u4e00\u94a9\u51b7\u6708\uff0c\u53e3\u4e2d\u5e7d\u5e7d\u5730\u541f\u9053\uff1a \u201c\u957f\u76f8\u601d\uff0c\u6467\u5fc3\u809d\u2026\u2026 \u6467\u7a97\u671b\u6708\u7a7a\u957f\u53f9\u2026\u2026 \u5b64\u706f\u4e0d\u660e\u601d\u6b32\u7edd\u2026\u2026 \u5fc6\u541b\u8fe2\u8fe2\u9694\u9752\u5929\u2026\u201d\uff0e \u5929\u957f\u8def\u8fdc\u9b42\u98de\u82e6\u2026\uff0e\uff0e\uff0e \u68a6\u9b42\u4e0d\u5230\u5173\u5c71\u96be\u2026\uff0e\uff0e\uff0e \u591c\u98ce\u51c4\u51c4\u591c\u8272\u5bd2\u2026\u201d\uff0e \u6708\u660e\u5982\u7d20\u6101\u4e0d\u7720\u2026\uff0e\uff0c\uff0e \u7ec8\u65e5\u601d\u541b\u4e0d\u89c1\u541b\u2026\uff0e\uff0e\uff0e \u613f\u968f\u6625\u98ce\u98de\u541b\u524d\u2026\u2026 \u6614\u65f6\u6a2a\u6ce2\u76ee\u2026\u2026 \u4eca\u4f5c\u6d41\u6cea\u6cc9\u2026\uff0e\uff0e\uff0e \u4e0d\u4fe1\u59be\u80a0\u65ad\u2026\u2026 \u5f52\u6765\u770b\u53d6\u660e\u955c\u2026\u2026\u201d \u5979\u628a\u674e\u767d\u7684\u4e24\u9996\u201c\u957f\u76f8\u601d\u201d\u98a0\u5012\u7be1\u6539\uff0c\u6df7\u5408\u5728\u4e00\u8d77\u968f\u53e3\u541f\u6765\uff0c\u4f7f\u4eba\u542c\u4e86\u67d4\u80a0\u767e\u8f6c\uff0c\u4e0d\u5fcd \u5352\u95fb\uff01 \u8339\u8001\u9556\u5934\u81f3\u6b64\u624d\u7b97\u660e\u767d\uff0c\u8fd9\u5973\u4eba\u5e76\u4e0d\u662f\u9b3c\uff0c\u81f3\u4e8e\u662f\u4e00\u4e2a\u88ab\u8d1f\u5fc3\u90ce\u629b\u5f03\u7684\u75f4\u60c5\u5c11\u5973\uff1f\u8fd8\u662f \u4e00\u4e2a\u6b7b\u53bb\u4e08\u592b\u5e74\u8f7b\u5b88\u5be1\u5c11\u5987\uff1f\u53ef\u5c31\u4e0d\u5f97\u800c\u77e5\u4e86\u3002\u4e0d\u8fc7\u770b\u8fd9\u5973\u4eba\u7684\u75f4\u60c5\uff0c\u771f\u53ef\u8bf4\u662f\u201c\u5bf8\u5bf8\u76f8\u601d \u5bf8\u5bf8\u7070\u201d\u4e86\uff01 \u8339\u8001\u9556\u5934\u58ee\u4e86\u58ee\u80c6\u5b50\uff0c\u4e8e\u54b3\u4e86\u4e00\u58f0\uff0c\u95ee\u9053\uff1a\u201c\u54ce\uff01\u59d1\u5a18\uff01\u4f60\u662f\u5e84\u4e0a\u7684\u4ec0\u4e48\u4eba\uff1f\u201d \u8c01\u77e5\u90a3\u5973\u4eba\u8fde\u770b\u90fd\u4e0d\u770b\u4ed6\u4e00\u773c\uff0c\u4ecd\u7136\u671b\u7740\u5929\u8fb9\u6b8b\u6708\uff0c\u5583\u5583\u4f4e\u8bc9\u9053\uff1a\u201c\u767d\u54e5\u54e5\uff01\u4f60\u5728\u54ea \u91cc\uff0c\u4f60\u53ef\u77e5\u9053\u7ea2\u59b9\u59b9\u60f3\u5f97\u4f60\u8fd9\u6837\u82e6\u5417\uff1f\u2026\u2026\u201d \u201c\u767d\u54e5\u54e5\uff1f\u201d\u201c\u7ea2\u59b9\u59b9\uff1f\u201d\u8339\u8001\u9556\u5934\u8499\u4e86\u4e00\u5934\u9732\u6c34\uff0c\u8c01\u53c8\u662f\u201c\u767d\u54e5\u54e5\uff1f\u201d\u4f46\u5374\u53ef\u65ad\u5b9a\uff0c \u8fd9\u5c11\u5973\u7684\u95fa\u540d\u4e00\u5b9a\u662f\u53eb\u4ec0\u4e48\u201c\u7ea2\u201d\u4e86\uff01 \u53c8\u542c\u90a3\u5c11\u5973\u5e7d\u5e7d\u5730\u8bf4\u9053\uff1a\u5509\uff01\u767d\u54e5\u54e5\uff01\u4f60\u53bb\u4e86\u5df2\u7ecf\u4e8c\u767e\u4e5d\u5341\u4e5d\u5929\u4e86\uff0c\u518d\u6709\u516d\u5341\u516d\u5929\uff0c\u5c31 \u6574\u6574\u6ee1\u4e86\u4e00\u5e74\uff0c\u8fd9\u4e00\u5e74\u6765\uff0c\u5c0f\u59b9\u7684\u773c\u6cea\u4e5f\u6d41\u5e72\u4e86\uff01\u2026\u2026\u767d\u54e5\u54e5\u2026\u2026\u4f60\u4e3a\u4ec0\u4e48\u8fd8\u4e0d\u56de\u6765\uff1f\u201d \u8bf4\u7740\uff0c\u5979\u53cc\u773c\u53c8\u6d41\u4e0b\u4e86\u4e24\u884c\u60c5\u6cea\u6765\u3002 \u8339\u8001\u9556\u5934\u4e00\u542c\uff0c\u8fd9\u5c11\u5973\u628a\u60c5\u90ce\u53bb\u7684\u65e5\u5b50\uff0c\u8bb0\u5f97\u6e05\u6e05\u695a\u695a\uff01\u2026\u2026 \u6b63\u5728\u6b64\u65f6\uff0c\u7a81\u542c\u4e00\u58f0\u51b7\u54fc\uff0c\u6765\u81ea\u8eab\u540e\u4e0d\u8fdc\uff01 \u8339\u8001\u9556\u5934\u5403\u4e00\u60ca\uff0c\u60f3\u4e0d\u5230\u6709\u4eba\u6765\u5230\u81ea\u5df1\u8eab\u540e\u8fd8\u672a\u53d1\u89c9\uff01\u90a3\u4e48\uff0c\u6765\u4eba\u7684\u8f7b\u8eab\u529f\u592b\uff0c\u5fc5\u5df2\u5230 \u4e86\u767b\u5cf0\u9020\u6781\u7684\u5730\u6b65\uff01 \u8339\u8001\u9556\u5934\u60ca\u6016\u4e4b\u4e2d\uff0c\u5019\u7136\u56de\u8eab\uff0c\u53cc\u638c\u62a4\u80f8\uff0c\u638c\u76ee\u4e00\u770b\uff0c\u5728\u8eab\u540e\u4e0d\u53ca\u4e09\u4e08\u4e4b\u5916\uff0c\u5e76\u6392\u7ad9\u5b9a \u4e09\u4e2a\u8001\u8005\uff01 \u53ea\u89c1\u5f53\u4e2d\u4e00\u4e2a\uff0c\u5e74\u7ea6\u4e94\u65ec\uff0c\u9762\u767d\u65e0\u987b\uff0c\u65b9\u9762\u9614\u53e3\uff0c\u8eab\u7a7f\u9526\u7ee3\uff0c\u4f0a\u7136\u5bcc\u7ec5\u6253\u626e\uff0c\u5de6\u624b\u63e1\u7740 \u4e00\u652f\u51c0\u5149\u95ea\u4eae\u7684\u70c2\u94f6\u7bab\uff01 \u53f3\u8fb9\u4e00\u4e2a\u79c3\u9876\u65e0\u6bdb\u7684\u8001\u8005\uff0c\u5de6\u81c2\u9f50\u80a9\u65ad\u53bb\uff0c\u53ea\u6709\u4e00\u53ea\u53f3\u624b\uff0c\u9ad8\u4e3e\u8fc7\u80a9\uff0c\u624b\u4e0a\u6258\u7740\u4e00\u4e2a\u949f \u5f62\u94dc\u94c3\uff0c\u5e74\u7eaa\u5df2\u81f3\u516d\u65ec\u5f00\u5916\uff01 \u5de6\u8fb9\u4e00\u4e2a\uff0c\u4e5f\u662f\u516d\u65ec\u4e0a\u4e0b\uff0c\u8eab\u4e0a\u53ea\u6709\u4e00\u6761\u817f\uff0c\u5947\u602a\u7684\u662f\uff0c\u4ed6\u5355\u817f\u7ad9\u7acb\uff0c\u65e2\u4e0d\u7528\u62d0\u4e5f\u4e0d\u7528 \u6756\uff0c\u771f\u4e0d\u77e5\u4ed6\u662f\u600e\u4e48\u884c\u8def\u6cd5\uff1f\u5374\u662f\u5f92\u624b\uff0c\u672a\u62ff\u5175\u5668\u3002 \u8fd9\u4e09\u4eba\u4e4b\u4e2d\uff0c\u8339\u8001\u9556\u5934\u5012\u8ba4\u8bc6\u4e24\u4e2a\uff0c\u90a3\u72ec\u81c2\u8001\u8005\u6b63\u662f\u201c\u8ffd\u9b42\u94c3\u201d\u53f8\u9a6c\u656c\uff0c\u90a3\u5355\u817f\u8001\u5934\u6b63 \u662f\u201c\u72ec\u811a\u98de\u9b54\u201d\u674e\u4e3e\uff0c\u8fd9\u4e8c\u4eba\u5408\u79f0\u201c\u585e\u5916\u53cc\u6b8b\u201d\uff0c\u662f\u897f\u5317\u7eff\u6797\u9053\u4e0a\u6709\u540d\u7684\u4e24\u5927\u9b54\u5934\uff01 \u6614\u5e74\u8339\u8001\u9556\u5934\u5728\u897f\u5317\u8d70\u9556\uff0c\u66fe\u4e0e\u4e8c\u4eba\u6709\u8fc7\u4e00\u9762\u4e4b\u8bc6\uff0c\u56e0\u4e8c\u4eba\u5f62\u8c61\u7279\u6b8a\uff0c\u653e\u800c\u5370\u8c61\u6df1\u523b\uff0c \u81f3\u4eca\u4e0d\u5fd8\u3002 \u81f3\u4e8e\u90a3\u4e2d\u5e74\u5bcc\u7ec5\uff0c\u8339\u8001\u9556\u5934\u867d\u672b\u4e0e\u5176\u4f1a\u8fc7\u9762\uff0c\u4f46\u770b\u4ed6\u90a3\u7a7f\u7740\u6253\u626e\uff0c\u4ee5\u53ca\u624b\u4e2d\u62ff\u7684\u90a3\u652f\u70c2 \u94f6\u7bab\uff0c\u8339\u8001\u9556\u5934\u7acb\u523b\u4ed8\u51fa\uff0c\u90a3\u5fc5\u662f\u4ee5\u201c\u97f3\u9b54\u7bab\u6cd5\u201d\u4eab\u8a89\u6b66\u6797\u7684\u201c\u94f6\u8427\u593a\u9b42\u201d\u7ae0\u58eb\u670b\u65e0\u7591\u4e86\u3002 \u56e0\u8fd9\u4e09\u4eba\u5728\u201c\u8c79\u7a81\u5c71\u5e84\u201d\uff0c\u540d\u5217\u5341\u5927\u9ad8\u624b\uff0c\u5929\u4e0b\u95fb\u540d\uff0c\u6545\u800c\u8339\u8001\u9556\u5934\u4e00\u770b\u4fbf\u80fd\u8ba4\u51fa\u3002 \u8339\u8001\u9556\u5934\u89c1\u4ed6\u4e09\u4eba\u7ad9\u7acb\u5728\u81ea\u5df1\u9762\u524d\uff0c\u9762\u76ee\u9634\u68ee\uff0c\u4e00\u8a00\u4e0d\u53d1\uff0c\u9690\u7136\u542b\u6709\u654c\u610f\uff0c\u968f\u4e00\u62b1\u62f3\uff0c \u9053\uff1a\u201c\u539f\u6765\u662f\u53f8\u9a6c\u5144\u3001\u674e\u5144\uff01\u60f3\u5fc5\u8fd9\u4f4d\u5c31\u662f\u4ee5\u7b56\u6cd5\u95fb\u540d\u5929\u4e0b\u7684\u2018\u94f6\u7bab\u593a\u9b42\u2019\u7ae0\u58eb\u670b\u7ae0\u5144 \u4e86\uff01\u201d \u201c\u94f6\u7bab\u593a\u9b42\u201d\u7ae0\u58eb\u670b\u563f\u7136\u4e00\u9635\u51b7\u7b11\uff0c\u9053\uff1a\u201c\u71d5\u4eac\u9556\u5c40\u2019\u7684\u603b\u9556\u5934\u3001\u94c1\u638c\u9707\u6cb3\u6714\u2019\u8339\u5144\uff0c \u679c\u7136\u540d\u4e0d\u865a\u4f20\uff0c\u4e00\u89c1\u9762\u5c31\u8ba4\u51fa\u8001\u673d\u7b49\u4eba\u6765\u4e86\uff01\u201d \u8339\u8001\u9556\u5934\u4e5f\u54c8\u54c8\u4e00\u7b11\u9053\uff1a\u201c\u6155\u5bb9\u5e9c\u5341\u5927\u9ad8\u624b\uff0c\u5929\u4e0b\u95fb\u540d\uff0c\u8001\u592b\u600e\u4f1a\u4e0d\u8bc6\uff1f\u2026\u2026\u201d \u201c\u6155\u5bb9\u5e9c\uff1f\u201d\u2018\u94f6\u8427\u593a\u9b42\u201d\u7ae0\u58eb\u670b\u6253\u65ad\u8339\u8001\u9556\u5934\u7684\u8bdd\uff0c\u4ef0\u5929\u957f\u7b11\u9053\uff1a\u201c\u6155\u5bb9\u5e9c\u5df2\u7ecf\u5728\u6c5f \u6e56\u9664\u540d\u4e86\uff0c\u5982\u4eca\u6b64\u5e84\u662f\u2018\u5357\u6d77\u95e8\u2019\u6d4e\u5357\u5206\u5802\uff0c\u8001\u5339\u592b\u4f11\u8981\u4fe1\u53e3\u6ee5\u8a00\u3002\u201c \u8339\u8001\u9556\u5934\u95fb\u8a00\u4e00\u6014\uff0c\u771f\u4e0d\u76f8\u4fe1\u6b64\u8bdd\u662f\u51fa\u81ea\u201c\u8c79\u7a81\u5c71\u5e84\u201d\u5341\u5927\u9ad8\u624b\u4e4b\u53e3\uff0c\u6014\u4e86\u4e00\u4f1a\u7591\u95ee \u9053\uff1a\u201c\u6b64\u8bdd\u5f53\u771f\uff1f\u201d \u201c\u8ffd\u9b42\u94c3\u201d\u53f8\u9a6c\u656c\u9053\uff1a\u8001\u9b3c\uff01\u4f60\u4ee5\u4e3a\u7ae0\u5144\u8fd8\u4f1a\u8ddf\u4f60\u8bf4\u8c0e\u5417\uff1f\u201d \u8339\u8001\u9556\u5934\u66f4\u89c1\u8d77\u7591\uff0c\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u6155\u5bb9\u5e84\u4e3b\u2018\u6458\u661f\u624b\u201d\u2026\uff0e\u201d \u201c\u72ec\u811a\u98de\u9b54\u201d\u674e\u4e3e\u9634\u607b\u607b\u5730\u9053\uff1a\u201c\u4e8b\u4e0d\u5173\u5df1\uff0c\u4f60\u8339\u8001\u513f\u4f55\u5fc5\u591a\u95ee\uff01\u201d \u8339\u8001\u9556\u5934\u89c1\u4e09\u4eba\u5f02\u53e3\u540c\u58f0\uff0c\u81f3\u6b64\u4e0d\u5f97\u4e0d\u76f8\u4fe1\u4e86\uff0c\u4f46\u8fd8\u89c9\u5f97\u5f88\u591a\u4e8b\u60c5\uff0c\u4f7f\u4eba\u65e0\u6cd5\u7406\u89e3\u3002\u8b6c \u5982\u4ed6\u4eec\u4e09\u4eba\u4e43\u662f\u201c\u6155\u5bb9\u5e9c\u201d\u5341\u5927\u9ad8\u624b\uff0c\u5982\u4eca\u201c\u6155\u5bb9\u5e9c\u201d\u6613\u4e3b\uff0c\u4ed6\u4eec\u4e09\u4eba\u600e\u4e48\u5982\u6b64\u6d0b\u6d0b\u81ea\u5f97\uff1f \u83ab\u975e\u4ed6\u4eec\u4e09\u4eba\u5df2\u80cc\u53db\u6155\u5bb9\u6db5\uff0c\u6295\u9760\u201c\u5357\u6d77\u95e8\u201d\u4e0b\u4e86\uff1f\u6c5f\u6e56\u4eba\u5fc3\u5982\u6b64\u865a\u8bc8\uff0c\u771f\u4f7f\u4eba\u9f7f\u51b7\u3002\u60f3\u81f3 \u6b64\u5904\uff0c\u53c8\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u4e09\u4f4d\u4ec1\u5144\u4e5f\u6295\u9760\u2026\u2026\u52a0\u76df\u2018\u5357\u6d77\u95e8\u2019\u4e86\uff1f\u201d \u201c\u6295\u9760\u201d\u4e24\u4e2a\u5b57\u592a\u523a\u8033\uff0c\u8339\u8001\u9556\u5934\u8bf4\u51fa\u53e3\u6765\uff0c\u4e34\u65f6\u53c8\u6539\u4e3a\u201c\u52a0\u76df\u201d\u4e8c\u5b87\u3002\u4f46\u8bdd\u51fa\u5982\u98ce\uff0c \u4e00\u51fa\u53e3\u4fbf\u65e0\u6cd5\u6536\u56de\u4e86\uff0c\u679c\u7136\uff0c\u542c\u5230\u8fd9\u8bdd\uff0c\u201c\u94f6\u7bab\u593a\u9b42\u201d\u7ae0\u58eb\u670b\u3001\u201c\u8ffd\u9b42\u94c3\u201d\u53f8\u9a6c\u656c\u3001\u201c\u72ec\u811a \u98de\u9b54\u201d\u674e\u4e3e\uff0c\u540c\u65f6\u8138\u8272\u4e00\u5bd2\uff0c\u7531\u7ae0\u58eb\u670b\u5f53\u5148\u5f00\u53e3\u9053\uff1a\u201c\u8339\u8001\u513f\u4f60\u77e5\u9053\u5c31\u597d\u4e86\uff0c\u5982\u4eca\u2018\u5357\u6d77 \u95e8\u2019\u5e94\u65f6\u800c\u5174\uff0c\u4e0d\u4e45\u5c06\u9886\u8896\u4e2d\u539f\uff0c\u4f60\u8339\u8001\u513f\u60f3\u52a0\u76df\u8fd8\u60f3\u4e0d\u5230\u5462\uff01\u201d \u8339\u8001\u9556\u5934\u867d\u7136\u4fee\u517b\u6781\u597d\uff0c\u4f46\u88ab\u4e09\u4eba\u4e00\u53e3\u4e00\u4e2a\u8001\u513f\uff0c\u4e5f\u53eb\u5f97\u6697\u6697\u53d1\u706b\uff0c\u5c24\u5176\u89c1\u4e09\u4eba\u606c\u989c\u65e0 \u803b\uff0c\u4e2d\u9014\u53d8\u8282\uff0c\u8fd8\u5927\u8a00\u4e0d\u60ed\uff0c\u4e5f\u4e0d\u7531\u8138\u8272\u4e00\u53d8\uff0c\u6012\u9053\uff1a\u8001\u592b\u8fd8\u4e0d\u60f3\u9ad8\u6500\uff0c\u4f46\u4f60\u4eec\u4e09\u4eba\u80cc\u4fe1\u6155 \u5bb9\u5e84\u4e3b\uff0c\u96be\u9053\u4e0d\u6015\u4f20\u51fa\u6c5f\u6e56\u88ab\u4eba\u803b\u7b11\u5417\uff1f\u201d \u201c\u8ffd\u9b42\u94c3\u201d\u53f8\u9a6c\u656c\u4ef0\u5929\u5927\u7b11\uff0c\u9053\uff1a\u201c\u4eca\u591c\u4f60\u8339\u8001\u513f\u8fd8\u60f3\u751f\u79bb\u6b64\u5e84\u5417\uff1f\u201d\u8bf4\u5b8c\u6b7b\u81ea\u54c8\u54c8\u5927 \u7b11\u4e0d\u6b62\uff01 \u8339\u8001\u9556\u5934\u89c1\u201c\u8ffd\u9b42\u94c3\u201d\u72c2\u50b2\u903c\u4eba\uff0c\u4e0d\u7531\u51b2\u7136\u5927\u6012\uff0c\u9053\uff1a\u201c\u5c31\u7b97\u8001\u592b\u6a2a\u5c38\u5f53\u573a\uff0c\u4f60\u4eec\u4e09\u4eba \u4e5f\u65e0\u6cd5\u4e00\u624b\u63a9\u76d6\u5929\u4e0b\u4eba\u7684\u8033\u76ee\uff01\u201d \u201c\u72ec\u811a\u98de\u9b54\u201d\u674e\u4e3e\u9053\uff1a\u201c\u4e3a\u4e86\u53eb\u4f60\u8001\u513f\u6b7b\u5f97\u7791\u76ee\uff0c\u5982\u4eca\u5b9e\u8bdd\u544a\u8bc9\u4f60\u5427\uff01\u6155\u5bb9\u6db5\u5341\u6570\u5e74\u524d \u7ed3\u4f19\u6697\u6740\u7ed3\u4e49\u76df\u5144\u2018\u9739\u96f7\u5251\u2019\u5c55\u4e91\u5929\uff0c\u5df2\u5931\u6389\u6b66\u6797\u76df\u4e3b\u7684\u8d44\u683c\uff0c\u2018\u5357\u6d77\u95e8\u2019\u63ed\u7a7f\u8fd9\u6bb5\u516c\u6848\u66ff \u5929\u884c\u9053\uff0c\u5df2\u4ee3\u4e4b\u800c\u8d77\uff0c\u4e09\u5e74\u4e4b\u5185\uff0c\u9886\u8896\u4e2d\u539f\u6b66\u6797\uff0c\u5e76\u516c\u5f00\u300a\u6b66\u5b66\u771f\u7ecf\u300b\uff0c\u7531\u6b66\u6797\u4eba\u58eb\u516c\u5f00\u7814 \u4e60\uff0c\u4e09\u5e74\u4e3e\u884c\u2018\u82f1\u96c4\u5927\u4f1a\u2019\uff0c\u5728\u897f\u5cb3\u534e\u5c71\uff0c\u4ee5\u516c\u5e73\u6bd4\u6b66\u4e89\u593a\u6b66\u6797\u76df\u4e3b\u5b9d\u5ea7\uff0c\u4eba\u4eba\u5b58\u4efd\u2014\u2014\u90a3 \u65f6\u5929\u4e0b\u6b66\u6797\u4e00\u7edf\u2026\u2026\u201d \u8bf4\u81f3\u6b64\u5904\uff0c\u53c8\u54c8\u54c8\u5927\u7b11\u4e86\u4e24\u58f0\uff0c\u9053\uff1a\u201c\u53ef\u60dc\u4f60\u8339\u8001\u513f\uff0c\u662f\u65e0\u6b64\u798f\u7f18\u4e86\uff01\u201d \u201c\u8ffd\u9b42\u94c3\u201d\u53f8\u9a6c\u656c\u63a5\u53e3\u9053\uff1a\u201c\u56e0\u4e3a\u4eca\u591c\u5c31\u662f\u4f60\u8339\u8001\u513f\u7684\u5bff\u7ec8\u6b63\u5bdd\u4e4b\u671f\uff01\u201d \u7b2c\u56db\u5341\u56db\u7ae0\u3000\u5730\u5ba4\u884c\u5211>> \u53e4\u9f99\u300a\u5251\u5ba2\u884c\u300b \u7b2c\u56db\u5341\u56db\u7ae0\u3000\u5730\u5ba4\u884c\u5211 \u53f8\u9a6c\u656c\u8bf4\u7f62\uff0c\u53cc\u817f\u4e00\u5f39\uff0c\u7535\u5c04\u800c\u8d77\uff0c\u534a\u7a7a\u4e2d\u72ec\u81c2\u4e00\u62a1\uff0c\u201c\u8ffd\u9b42\u94c3\u201d\u6643\u8d77\u4e00\u56e2\u9ec4\u8272\u5149\u5f71\uff0c \u631f\u7740\u201c\u94c3\uff01\u94c3\uff01\u201d\u9707\u8033\u91d1\u97f3\uff0c\u731b\u5411\u8339\u8001\u9556\u5934\u8fce\u9762\u7f69\u6765\uff01 \u8339\u8001\u9556\u5934\u4f30\u4e0d\u5230\u8fd9\u66fe\u4e0e\u81ea\u5df2\u6709\u4e00\u9762\u4e4b\u8bc6\u7684\u53f8\u9a6c\u656c\u7ade\u7136\u8bf4\u52a8\u624b\u5c31\u52a8\uff0c\u5fc3\u795e\u4e00\u61d4\uff0c\u89c1\u201c\u8ffd\u9b42 \u94c3\u201d\u94c3\u97f3\u9707\u8033\uff0c\u6398\u9b42\u593a\u9b44\uff0c\u4e00\u56e2\u9ec4\u8272\u5149\u5708\uff0c\u631f\u7740\u523a\u9aa8\u51b7\u98ce\u7f69\u5411\u9762\u95e8\uff0c\u6df1\u77e5\u5389\u5bb3\uff0c\u4e0d\u6562\u786c\u63a5\uff0c \u6643\u80a9\u632b\u6b65\uff0c\u98d8\u8eab\u4e00\u4e08\u5f00\u5916\uff0c\u540c\u65f6\u4ee5\u6210\u540d\u591a\u4e2d\u7684\u94c1\u638c\uff0c\u201c\u5468\u5904\u65a9\u86df\u201d\uff0c\u53cd\u81c2\u5411\u201c\u8ffd\u9b42\u94c3\u62e6\u8170\u5288 \u51fa\uff01 \u638c\u98ce\u72c2\u5578\uff0c\u5e2d\u5377\u800c\u81f3\uff0c\u201c\u8ffd\u9b42\u94c3\u201d\u53f8\u9a6c\u656c\u5927\u8d50\u4e00\u58f0\uff1a\u201c\u6765\u5f97\u597d\uff01\u201d \u534a\u7a7a\u4e2d\u6298\u8170\u6025\u964d\uff0c\u5355\u8db3\u4e00\u70b9\u5730\u9762\uff0c\u8eab\u5f62\u8d34\u5730\u6ed1\u51fa\u6570\u5c3a\uff0c\u201c\u8ffd\u9b42\u94c3\u201d\u6f14\u201c\u9707\u94c3\u60ca\u9f99\u201d\u7edd \u62db\uff0c\u731b\u6253\u8339\u8001\u9556\u5934\u524d\u80f8\uff01 \u53f8\u9a6c\u656c\u4e0d\u6127\u4e3a\u5341\u5927\u9ad8\u624b\u4e4b\u4e00\uff0c\u8eab\u6cd5\u5feb\uff0c\u62db\u5f0f\u5947\uff0c\u771f\u53ef\u4f7f\u4eba\u53f9\u4e3a\u89c2\u6b62\uff01 \u8339\u8001\u9556\u5934\u5fae\u5fae\u4e00\u60ca\uff0c\u7acb\u523b\u628a\u4e00\u53cc\u94c1\u638c\uff0c\u65bd\u4e86\u4e2a\u98ce\u96e8\u4e0d\u900f\uff0c\u4e0e\u201c\u8ffd\u9b42\u94c3\u201d\u53f8\u9a6c\u656c\u6253\u5728\u4e00 \u8d77\uff01 \u53ea\u89c1\u638c\u98ce\u9510\u5578\uff0c\u94c3\u97f3\u9707\u8033\uff0c\u4e8c\u4eba\u4ff1\u662f\u5feb\u653b\u5feb\u6253\uff0c\u6643\u773c\u6253\u4e86\u4e2a\u6570\u4e2a\u7167\u9762\uff01 \u7adf\u662f\u534a\u65a4\u516b\u4e24\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u96be\u5206\u80dc\u8d1f\uff01 \u201c\u94f6\u8427\u593a\u9b42\u201d\u7ae0\u58eb\u670b\u5728\u4e00\u8fb9\u770b\u5f97\u76f4\u76b1\u7709\uff01 \u56e0\u4e3a\u4ed6\u4eec\u8fd8\u6709\u8981\u4e8b\u6025\u5f85\u529e\u7406\uff0c\u4e0d\u8010\u4e45\u6218\u3002\u201c\u72ec\u811a\u98de\u9b54\u201d\u674e\u4e3e\u89c1\u76df\u5f1f\u4e45\u6218\u4e0d\u4e0b\uff0c\u5f53\u5373\u5389\u5578 \u4e00\u58f0\uff0c\u72ec\u811a\u4e00\u8dfa\uff0c\u8eab\u5f62\u51cc\u7a7a\u800c\u8d77\uff0c\u534a\u7a7a\u51fa\u62db\uff0c\u4e0e\u201c\u8ffd\u9b42\u94c3\u201d\u53f8\u9a6c\u656c\u53cc\u6218\u8339\u8001\u9556\u5934\uff01 \u8339\u8001\u9556\u5934\u72ec\u6218\u201c\u8feb\u9b42\u94c3\u201d\uff0c\u52c9\u5f3a\u6253\u4e86\u4e4e\u624b\uff0c\u5982\u4eca\u201c\u72ec\u811a\u98de\u9b54\u201d\u52a0\u5165\uff0c\u7acb\u611f\u538b\u529b\u52a0\u91cd\uff0c\u4f46 \u4ecd\u51ed\u7740\u4e00\u53cc\u94c1\u638c\uff0c\u594b\u529b\u82e6\u6218\uff01 \u201c\u94f6\u7bab\u593a\u9b42\u201d\u5fc3\u4e2d\u7126\u6025\uff0c\u89c1\u201c\u585e\u5916\u53cc\u6b8b\u201d\u4e8c\u4eba\u5408\u624b\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u4ecd\u4e0d\u80fd\u53d6\u80dc\uff0c\u53cc\u7709\u4e00 \u76b1\uff0c\u628a\u70c2\u94f6\u7bab\u51d1\u5728\u5634\u8fb9\uff01 \u201c\u545c\u5f8b\uff01\u545c\u5f8b\uff01\u201d\u5439\u4e86\u8d77\u6765\uff01 \u5176\u97f3\u6e05\u8d8a\uff0c\u5176\u5f8b\u60b2\u51c9\uff01 \u9690\u542b\u6709\u201c\u7f8e\u4eba\u8fdf\u66ae\uff0c\u82f1\u96c4\u7a77\u9014\u201d\u4e4b\u611f\uff01 \u52a8\u7740\u624b\u7684\u8339\u8001\u9556\u5934\u7acb\u523b\u5fc3\u795e\u4e00\u9707\uff0c\u6df1\u611f\u7a77\u9014\u672b\u8def\uff0c\u82f1\u96c4\u8001\u53bb\uff0c\u4e89\u5f3a\u6597\u80dc\u4e4b\u5fc3\u5168\u5931\uff0c\u624b\u811a \u4e3a\u4e4b\u4e00\u6162\uff01 \u53f8\u9a6c\u656c\u89c1\u72b6\uff0c\u6293\u4f50\u673a\u4f1a\uff0c\u201c\u8ffd\u9b42\u593a\u9b44\u201d\uff0c\u201c\u8ffd\u9b42\u94c3\u201d\u5e7b\u8d77\u4e94\u5c3a\u65b9\u5706\u7684\u4e00\u4e2a\u9ec4\u8272\u5149\u5708\uff0c\u94c3 \u97f3\u9510\u5578\uff0c\u731b\u7838\u8339\u8001\u9556\u5934\u9762\u95e8\uff01 \u8339\u8001\u6e90\u5934\u795e\u60c5\u604d\u60da\u4e4b\u4e2d\uff0c\u7a81\u611f\u51c9\u98ce\u538b\u9762\uff0c\u5b9a\u795e\u4e00\u770b\uff0c\u9ec4\u8272\u5149\u5f71\u5df2\u8fd1\u9762\u95e8\uff0c\u9a87\u6781\u4e4b\u4e2d\uff0c\u65bd \u51fa\u201c\u94c1\u677f\u6865\u201d\u529f\u592b\uff0c\u8eab\u5f62\u5f80\u540e\u4e00\u633a\uff0c\u4ef0\u9762\u671d\u5929\uff0c\u5012\u7eb5\u51fa\u53bb\uff01 \u8339\u8001\u9556\u5934\u6025\u4e2d\u751f\u667a\uff0c\u8fd9\u4e00\u62db\u672c\u53ef\u8eb2\u8fc7\u8fce\u9762\u4e0b\u7838\u7684\u201c\u8ffd\u9b42\u94c3\u201d\uff0c\u4f46\u65c1\u8fb9\u8fd8\u6709\u4e2a\u201c\u72ec\u811a\u98de \u9b54\u201d\uff0c\u89c1\u8339\u8001\u8198\u5934\u8eab\u5f62\u540e\u4ef0\uff0c\u5411\u540e\u7eb5\u53bb\uff0c\u524d\u80f8\u95e8\u6237\u6d1e\u5f00\uff0c\u98de\u8d77\u72ec\u811a\uff0c\u731b\u5411\u8339\u8001\u9556\u5934\u524d\u5fc3\u8e29 \u53bb\uff01 \u8fd9\u4e00\u811a\uff0c\u201c\u72ec\u811a\u98de\u9b54\u201d\u7528\u4e86\u5341\u6210\u529f\u529b\uff0c\u52a0\u4e0a\u5168\u8eab\u91cd\u91cf\uff0c\u6015\u4e0d\u6709\u5343\u94a9\u4e4b\u91cd\uff0c\u771f\u8981\u8e0f\u5b9e\uff0c\u53ef \u4ee5\u628a\u8339\u8001\u9556\u5934\u80f8\u9aa8\u8e29\u788e\uff01 \u8339\u8001\u9556\u5934\u8eab\u5f62\u540e\u4ef0\uff0c\u518d\u60f3\u6362\u5f0f\u5df2\u4e0d\u53ef\u80fd\uff0c\u4e07\u822c\u65e0\u5948\uff0c\u201c\u5de7\u71d5\u7ffb\u4e91\u201d\uff0c\u5411\u4e00\u4fa7\u6eda\u51fa\uff01 \u201c\u72ec\u811a\u98de\u9b54\u201d\u5927\u559d\u4e00\u58f0\uff1a\u201c\u77401\u201d \u5355\u811a\u4e00\u6311\uff0c\u8db3\u6b63\u8e4b\u5728\u8339\u8001\u9556\u5934\u808b\u4e0b\u201c\u8f6f\u9ebb\u7a74\u201d\u4e0a\uff0c\u8339\u8001\u9556\u5934\u201c\u542d\u201d\u4e86\u4e00\u58f0\uff0c\u4e00\u8def\u7ffb\u6eda\uff0c \u88ab\u201c\u72ec\u811a\u98de\u9b54\u201d\u4e00\u811a\u8e22\u51fa\u4e00\u4e08\u5f00\u5916\uff01 \u6240\u5e78\u672b\u8e22\u4e2d\u8981\u5bb3\uff0c\u4f46\u8339\u8001\u9556\u5934\u5df2\u660f\u8ff7\u8fc7\u53bb\uff01 \u201c\u94f6\u7bab\u593a\u9b42\u201d\u6536\u4f4f\u7b56\u58f0\uff0c\u53f8\u9a6c\u656c\u8d76\u4e0a\u524d\u53bb\u628a\u8339\u8001\u9556\u5934\u63d0\u4e86\u8fc7\u6765\uff0c\u540c\u65f6\u53c8\u5728\u8339\u8001\u9556\u5934\u201c\u660f \u7761\u7a74\u201d\u62cd\u4e86\u4e00\u638c\uff0c\u6b64\u65f6\u8339\u8001\u9556\u5934\u8fde\u660f\u5e26\u7761\uff0c\u771f\u6b63\u662f\u4e0d\u7701\u4eba\u4e8b\u4e86\u3002 \u201c\u8ffd\u9b42\u94c3\u201d\u53f8\u9a6c\u656c\u5411\u201c\u94f6\u8427\u593a\u9b42\u201d\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u8001\u513f\u771f\u624e\u624b\uff0c\u82e5\u4e0d\u662f\u7ae0\u5144\u7684\u7b56\u6cd5\u594f \u529f\uff0c\u8bf4\u4e0d\u5b9a\u8fd8\u8981\u591a\u8d39\u4e00\u756a\u624b\u811a\u5462\uff01\u201d \u201c\u94f6\u7bab\u593a\u9b42\u201d\u9053\uff1a\u201c\u4e0d\u8981\u591a\u8bf4\u4e86\uff01\u5feb\u8d70\u5427\uff01\u5c11\u541b\u53ef\u80fd\u5df2\u7b49\u6025\u4e86\u5462\u2026\u2026\u201d \u7a81\u542c\u697c\u7a97\u4f20\u6765\u5e7d\u5e7d\u7684\u58f0\u8c03\uff0c\u9053\uff1a\u201c\u4ee5\u591a\u4e3a\u80dc\uff0c\u7b97\u5f97\u4ec0\u4e48\u82f1\u96c4\uff1f\u2018\u8c79\u7a81\u5c71\u5e84\u2019\u7684\u4eba\u90fd\u53eb\u4f60 \u4eec\u4e22\u5149\u4e86\uff01\u201d \u201c\u72ec\u811a\u98de\u9b54\u201d\u8f6c\u8138\u671b\u4e86\u671b\uff0c\u697c\u7a97\u524d\u90a3\u957f\u53d1\u63a9\u9762\u7684\u5c11\u5973\uff0c\u56de\u5934\u5bf9\u201c\u94f6\u7bab\u593a\u9b42\u201d\u9053\uff1a\u201c\u6211\u4eec \u5012\u5fd8\u4e86\uff0c\u6b64\u5904\u8fd8\u6709\u6155\u5bb9\u5e9c\u7684\u4e00\u6761\u6839\uff0c\u4e0d\u5982\u4e00\u9f50\u5e72\u6389\uff0c\u7ed9\u4ed6\u6765\u4e2a\u65a9\u8349\u9664\u6839\u2026\u2026\u201d \u201c\u72ec\u811a\u98de\u9b54\u201d\u8bf4\u6b64\u8bdd\u65f6\uff0c\u58f0\u97f3\u5f88\u4f4e\uff0c\u4f46\u4e0d\u77e5\u90a3\u957f\u53d1\u63a9\u9762\u7684\u5c11\u5973\u5982\u4f55\u542c\u5230\uff0c\u53ea\u542c\u5979\u63a5\u53e3 \u9053\uff1a\u201c\u4f60\u4eec\u8981\u60f3\u6740\u6211\u706d\u53e3\u5417\uff1f\u90a3\u5c31\u5feb\u70b9\u52a8\u624b\uff01\u4e0d\u7136\uff0c\u5f85\u6211\u767d\u54e5\u54e5\u82e5\u662f\u6765\u4e86\uff0c\u4f60\u4eec\u4e00\u4e2a\u4e5f\u6d3b\u4e0d \u4e86\uff01\u201d \u53f8\u9a6c\u656c\u5927\u6012\uff0c\uff0c\u540c\u65f6\u6012\u559d\u9053\uff1a\u201c\u6211\u5c31\u5148\u628a\u4f60\u8fd9\u75af\u4e2b\u5934\u6bd9\u4e86\u518d\u8bf4\uff01\u2026\u2026\u201d \u201c\u94f6\u7bab\u593a\u9b42\u201d\u628a\u201c\u8ffd\u9b42\u94c3\u201d\u53eb\u4f4f\uff0c\u9053\uff1a\u201c\u5979\u4e00\u4e2a\u75af\u5973\uff0c\u8c05\u4e5f\u4e0d\u4f1a\u6210\u591a\u5927\u6c14\u5019\uff0c\u53f8\u9a6c\u5144\uff0c \u4e0d\u5fc5\u4e3a\u6b64\u803d\u8bef\u65f6\u95f4\u4e86\uff0c\u5feb\u56de\u53bb\u5411\u5c11\u541b\u8986\u547d\uff01\u201d \u4e09\u4eba\u8bf4\u7740\uff0c\u7531\u53f8\u9a6c\u656c\u5355\u81c2\u63d0\u8d77\u660f\u8ff7\u4e0d\u7701\u7684\u8339\u8001\u9556\u5934\uff0c\u5411\u524d\u9662\u8bae\u4e8b\u5385\u8d70\u53bb\u3002 \u8bae\u4e8b\u5385\u4ecd\u7136\u4e00\u7247\u6f06\u9ed1\uff0c\u8fdb\u5230\u5927\u5385\u4e4b\u4e2d\uff0c\u9760\u58c1\u4e00\u6761\u957f\u684c\u3001\u684c\u4e0a\u6446\u7740\u4e66\u7c4d\u53e4\u73a9\uff0c\u4f46\u5728\u684c\u4e0b\u5374 \u6709\u4e00\u4e2a\u6697\u95e8\uff0c\u4e09\u4eba\u8d70\u81f3\u684c\u524d\u201c\u94f6\u7bab\u593a\u9b42\u201d\u7ae0\u58eb\u670b\uff0c\u4ee5\u624b\u4e2d\u70c2\u94f6\u7bab\u5411\u684c\u4e0b\u6697\u7ebd\u4e00\u70b9\uff0c\u5440\u7136\u8f6c\u5f00 \u4e00\u9053\u6697\u95e8\uff0c\u4e09\u4eba\u9c7c\u8d2f\u800c\u5165\uff0c\u8d70\u8fc7\u4e00\u6761\u957f\u957f\u96e8\u8def\uff0c\u53ea\u89c1\u58c1\u4e0a\u6570\u5341\u652f\u706b\u6cb9\u5927\u70db\uff0c\u53a2\u5f97\u6ee1\u5ba4\u96ea\u4eae\uff0c \u5374\u662f\u4e00\u95f4\u975e\u5e38\u5bbd\u5e7f\u7684\u5730\u4e0b\u6697\u5ba4\uff01 \u5ba4\u4e2d\u592e\u6446\u7740\u4e00\u53ea\u957f\u6848\uff0c\u6848\u540e\u6570\u5341\u5f20\u91d1\u4ea4\u6905\uff0c\u5f53\u4e2d\u6905\u4e0a\u5750\u7684\uff0c\u6b63\u662f\u90a3\u81ea\u8863\u94f6\u6247\u5112\u751f1 \u767d\u8863\u94f6\u6247\u4e66\u751f\u5de6\u9996\u662f\u4e00\u4e2a\u8eab\u7a7f\u91d1\u5149\u95ea\u95ea\u3001\u9576\u5d4c\u6709\u7247\u7247\u9c7c\u9cde\u7247\u7684\u7fbd\u8863\u751f\u5f97\u975e\u5e38\u4fcf\u4e3d\u7684\u5c11 \u5973\uff01 \u8fd9\u5c11\u5973\u8863\u9970\u522b\u81f4\uff0c\u9664\u4e86\u90a3\u91d1\u5149\u95ea\u95ea\u7684\u9c7c\u9cde\u73e0\u7247\u7fbd\u8863\uff0c\u4e00\u652f\u98ce\u5934\u9497\uff0c\u90a3\u51e4\u51f0\u4f3c\u662f\u771f\u73e0\u4e32 \u6210\uff0c\u98a4\u5fae\u5fae\u6ef4\u73e0\u5782\u7fe0\uff0c\u968f\u7740\u4fcf\u4e3d\u5c11\u5973\u5de6\u53f3\u987e\u76fc\uff0c\u800c\u60a0\u60a0\u6643\u52a8\uff0c\u770b\u8d77\u6765\u97f5\u81f4\u5b9b\u7136\uff0c\u522b\u6709\u4e00\u756a\u97f5 \u6627\uff01 \u8fd9\u8eab\u6253\u626e\uff0c\u5728\u4e2d\u539f\u5987\u5973\u8eab\u4e0a\u4ece\u672a\u89c1\u8fc7\uff01 \u771f\u5b9b\u5982\u4e5d\u5929\u7384\u5973\u4e0b\u51e1\uff0c\u53c8\u4f3c\u9f99\u5bab\u7389\u5973\u4e34\u5c18\uff01 \u8fd9\u5c11\u5973\u7f8e\u662f\u591f\u7f8e\uff0c\u4f46\u7ec6\u7709\u5165\u9b03\uff0c\u51e4\u76ee\u542b\u5a01\uff0c\u4fcf\u4e3d\u4e4b\u4e2d\u7adf\u6d41\u9732\u7740\u65e0\u6bd4\u7684\u715e\u6c14\uff01 \u8fd9\u767d\u8863\u94f6\u6247\u4e66\u751f\u4e0e\u91d1\u8863\u51e4\u9497\u5c11\u5973\u9ad8\u8e1e\u9996\u4f4d\uff0c\u4e24\u65c1\u5750\u7740\u4e0d\u5c11\u52b2\u88c5\u75be\u670d\u7684\u6b66\u6797\u8c6a\u5ba2\uff01 \u5728\u5bc6\u677e\u6797\u52ab\u9556\u7684\u201c\u9b54\u9b3c\u5c9b\u516b\u5996\u201d\u3001\u201c\u6843\u82b1\u56db\u4ed9\u201d\u4e5f\u8d6b\u7136\u5728\u5ea7\uff01 \u201c\u4f5b\u5370\u6cd5\u5e08\u201d\u4f24\u52bf\u4f3c\u5df2\u75ca\u6108\uff0c\u4e0e\u767d\u53d1\u5a46\u5a46\u53ca\u957f\u9aef\u8001\u4eba\u5750\u5728\u4e00\u5904\uff0c\u8fd9\u4e09\u4eba\u5f62\u5f71\u4e0d\u79bb\uff0c\u6b63\u662f \u5a01\u540d\u663e\u8d6b\u7684\u201c\u6d77\u5916\u4e09\u715e\u201d\uff01 \u6b64\u5916\uff0c\u6155\u5bb9\u5e9c\u5341\u5927\u9ad8\u624b\uff0c\u5012\u6709\u4e03\u516b\u4f4d\u4e0e\u516b\u5996\u56db\u4ed9\u7b49\u4eba\u5750\u5728\u4e00\u8d77\uff01 \u4f46\u672c\u5e84\u7684\u4e3b\u4eba\uff0c\u201c\u6458\u661f\u624b\u201d\u6155\u5bb9\u6db5\uff0c\u4ee5\u53ca\u5176\u59bb\u5b50\u3001\u513f\u5973\uff0c\u4e5f\u5c31\u662f\u90a3\u6551\u8fc7\u5c55\u767d\u7684\u4e2d\u5e74\u8d35 \u5987\u3001\u51b7\u50b2\u65e0\u60c5\u7684\u201c\u51cc\u98ce\u516c\u5b50\u201d\uff0c\u4ee5\u53ca\u5c55\u5a49\u513f\uff0c\u5747\u88ab\u6346\u7ed1\u5728\u6697\u5ba4\u4e2d\u592e\u7684\u5de8\u67f1\u4e0a\uff01 \u70ed\u8840\u4fa0\u80a0\u7684\u4e71\u53d1\u602a\u4eba\u96f7\u5927\u53d4\uff0c\u8d6b\u7136\u4e5f\u4e0e\u6155\u5bb9\u5bb6\u7684\u4eba\u7ed1\u5728\u4e00\u8d77\uff01 \u8fd8\u6709\uff0c\u201c\u71d5\u4eac\u9556\u5c40\u201d\u7684\u4e00\u5e72\u9556\u5e08\uff0c\u7ea6\u6709\u56db\u4e94\u5341\u4eba\u5747\u88ab\u6346\u7ed1\u4f4f\u624b\u811a\uff0c\u4e22\u5728\u6848\u524d\uff01 \u8fd9\u663e\u7136\u662f\u201c\u8c79\u7a81\u5c71\u5e84\u201d\u5df2\u7ecf\u6613\u4e3b\uff0c\u201c\u5357\u6d77\u95e8\u201d\u9e4a\u5de2\u9e20\u5360\uff0c\u800c\u539f\u6765\u7684\u4e3b\u4eba\u3001\u6155\u5bb9\u6db5\u4e00\u5bb6\u5374 \u5df2\u4f5c\u4e86\u9636\u4e0b\u4e4b\u56da\uff01 \u6155\u5bb9\u5e9c\u5341\u5927\u9ad8\u624b\uff0c\u4ee5\u53ca\u4e09\u5343\u98df\u5ba2\uff0c\u5927\u591a\u6570\u53db\u4e3b\u964d\u654c\uff0c\u53ea\u6709\u5c11\u6570\u5fe0\u8d1e\u4e4b\u58eb\uff0c\u5982\u96f7\u5927\u53d4\u7b49 \u4eba\uff0c\u5374\u5df2\u540c\u65f6\u88ab\u64d2\uff01 \u771f\u662f\u201c\u5341\u5e74\u6cb3\u4e1c\uff0c\u5341\u5e74\u6cb3\u897f\u201d\uff0c\u6c5f\u6f5e\u4e0a\u4e89\u96c4\u79f0\u9738\uff0c\u6ce2\u8c32\u4e91\u8be1\uff0c\u4ee4\u4eba\u96be\u4ee5\u9884\u6d4b\uff01 \u201c\u94f6\u7bab\u593a\u9b42\u201d\u7ae0\u58eb\u670b\uff0c\u201c\u8ffd\u9b42\u94c3\u201d\u53f8\u9a6c\u656c\uff0c\u201c\u72ec\u811a\u98de\u9b54\u201d\u674e\u4e3e\uff0c\u628a\u8339\u8001\u9556\u5934\u4e22\u5728\u9636\u524d\uff0c \u5411\u767d\u8863\u94f6\u6247\u4e66\u751f\u4e00\u62f1\u624b\uff0c\u9053\uff1a\u201c\u5e78\u4e0d\u8fb1\u547d\uff0c\u2018\u94c1\u638c\u9707\u6cb3\u6714\u2019\u8339\u56fd\u96c4\u5df2\u7ecf\u62ff\u5230\uff01\u201d \u767d\u8863\u94f6\u6247\u4e66\u751f\u56de\u5934\u5411\u91d1\u8863\u98ce\u94a1\u7684\u5c11\u5973\u7b11\u9053\uff1a\u201c\u5e08\u59b9\uff0c\u53ef\u4ee5\u53d1\u843d\u4e86\u5427\uff1f\u201d \u90a3\u91d1\u8863\u51e4\u9497\u5c11\u5973\uff0c\u51e4\u76ee\u9661\u7741\uff0c\u773c\u4e2d\u7cbe\u5149\u6151\u4eba\uff0c\u5de6\u53f3\u626b\u5c04\u4e86\u4e00\u904d\uff0c\u773c\u5149\u6240\u53ca\u4e4b\u5904\uff0c\u7ade\u4f7f\u5728 \u5ea7\u7684\u7fa4\u96c4\uff0c\u4e2a\u4e2a\u5fc3\u4e2d\u5bd2\u98a4\uff0c\u614c\u5fd9\u4f4e\u4e0b\u5934\u53bb\uff01 \u53ea\u89c1\u90a3\u91d1\u8863\u51e4\u9497\u5c11\u5973\u60c5\u4e3d\u65e0\u6bd4\u7684\u8138\u4e0a\uff0c\u6d6e\u8d77\u4e00\u79cd\u4ee4\u4eba\u671b\u4e86\u5fc3\u80c6\u7686\u5bd2\u7684\u715e\u6c14\uff0c\u4ee5\u51b0\u51b7\u7684\u5982 \u540c\u4ece\u5317\u6781\u51b0\u539f\u91cc\u5439\u6765\u7684\u5bd2\u98ce\u4e00\u822c\u51b0\u51b7\u7684\u8bed\u8c03\uff0c\u8bf4\u9053\uff1a\u201c\u8fd8\u6709\u4e00\u7537\u4e00\u5973\u4e3a\u4ec0\u4e48\u6ca1\u6709\u64d2\u5230\uff1f\u201d \u201c\u6d77\u5916\u4e09\u715e\u201d\u614c\u5fd9\u7ad9\u8d77\uff0c\u8bda\u714c\u8bda\u6050\u5730\u8bf4\u9053\uff1a\u201c\u542f\u7980\u516c\u4e3b\uff0c\u90a3\u5c11\u5e74\u5c55\u81ea\u4e0e\u2018\u9b3c\u9762\u5a07\u5a03\u2019\uff0c \u4e0d\u5728\u672c\u5e84\u4e4b\u4e2d\uff0c\u5bb9\u4ee5\u540e\u6162\u6162\u67e5\u8bbf\u597d\u4e86\uff01\u201d \u91d1\u8863\u51e4\u9497\u5c11\u5973\uff0c\u9762\u6709\u4e0d\u6109\u4e4b\u8272\uff0c\u8fd8\u8981\u53d1\u4f5c\u2026\u2026 \u767d\u8863\u94f6\u6247\u5112\u751f\u63a5\u53e3\u9053\uff1a\u201c\u60f3\u4ed6\u4eec\u4e24\u4e2a\u4e43\u662f\u5c40\u5916\u4e4b\u4eba\uff0c\u73b0\u5728\u5148\u4e0d\u7ba1\u4ed6\u4eec\u4e86\uff0c\u8fd8\u662f\u5148\u2026\u2026\u201d \u91d1\u8863\u51e4\u9497\u5c11\u5973\u9762\u8272\u4e00\u6c89\uff0c\u9053\uff1a\u201c\u4f60\u7adf\u6562\u5e2e\u7740\u5916\u4eba\u8bf4\u8bdd\u4e86\uff01\u201d \u767d\u8863\u94f6\u6247\u4e66\u751f\u4f3c\u662f\u6781\u4e3a\u60e7\u6015\u8fd9\u5029\u4e3d\u5c11\u5973\uff0c\u8fde\u5fd9\u8d54\u7b11\u9053\uff1a\u201c\u5e08\u59b9\uff0c\u8a00\u91cd\u4e86\uff01\u5e08\u5144\u600e\u4f1a\u5e2e\u7740 \u5916\u4eba\u8bf4\u8bdd\uff1f\u201d \u91d1\u8863\u51e4\u9497\u5c11\u5973\u8fde\u58f0\u51b7\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u5fc3\u91cc\u60f3\u4ec0\u4e48\uff0c\u96be\u9053\u6211\u8fd8\u4e0d\u77e5\u9053\u5417\uff1f\u201d \u767d\u8863\u94f6\u6247\u4e66\u751f\u8fde\u58f0\u82e6\u7b11\uff0c\u53ea\u628a\u94f6\u6247\u5728\u638c\u5fc3\u8fde\u6572\uff0c\u5374\u518d\u4e5f\u7b54\u4e0d\u51fa\u8bdd\u6765\u2026\u2026 \u91d1\u8863\u51e4\u9497\u5c11\u5973\u767d\u4e86\u94f6\u6247\u4e66\u751f\u4e00\u773c\uff0c\u9661\u7136\u7389\u638c\u8f7b\u51fb\uff0c\u201c\u62cd\uff01\u62cd1\u201d\u4e24\u58f0\u8106\u54cd\uff0c\u53f1\u9053\uff1a\u5f00\u9999 \u5802\uff01\u201d \u7acb\u523b\u684c\u540e\u5e03\u5e54\u4e4b\u4e2d\uff0c\u8d70\u51fa\u5341\u4e8c\u4e2a\u77ed\u53d1\u5782\u7559\u3001\u624b\u6367\u9999\u7089\u7684\u767d\u8863\u7ae5\u5b50\uff0c\u9c7c\u8d2f\u8d70\u81f3\u684c\u524d\uff0c\u4e24\u65c1 \u96c1\u7fc5\u6392\u5f00\uff01 \u5341\u4e8c\u53ea\u9999\u7089\u5185\u6a80\u9999\u9ad8\u70e7\uff0c\u70df\u96fe\u888b\u888b\uff0c\u7acb\u523b\u5f25\u6f2b\u4e86\u5168\u5ba4\uff0c\u5ba4\u4e2d\u4e4b\u4eba\uff0c\u9f3b\u5b54\u4e2d\u90fd\u55c5\u5230\u4e86\u4e00\u822c \u6de1\u8c08\u7684\u5e7d\u99991 \u5947\u4e8b\u53d1\u751f\u4e86\uff0c\u4f17\u4eba\u4e00\u55c5\u5230\u8fd9\u6de1\u8c08\u5e7d\u9999\uff0c\u6e05\u9192\u7684\u4eba\u611f\u5230\u4e00\u9635\u8ff7\u60af\uff0c\u773c\u4e2d\u6240\u89c1\u90fd\u662f\u5e84\u4e25\u795e\u5723 \u4e4b\u50cf\uff0c\u5fc3\u4e2d\u6240\u60f3\u53ea\u6709\u5d07\u62dc\u4e0e\u670d\u4ece\uff0c\u8fde\u4e00\u70b9\u53cd\u6297\u7684\u610f\u5ff5\u90fd\u5174\u4e0d\u8d77\u4e86\uff01 \u800c\u660f\u8ff7\u7684\u4eba\uff0c\u53d7\u4f24\u660f\u8ff7\u4e0e\u88ab\u70b9\u4e2d\u7a74\u9053\u660f\u8ff7\u7684\u4eba\uff0c\u53cd\u800c\u6e05\u9192\u4e86\uff01 \u6240\u8c13\u6e05\u9192\uff0c\u4e0d\u8fc7\u6062\u590d\u4e86\u77e5\u89c9\u3001\u89c6\u89c9\u3001\u542c\u89c9\u2026\u2026\u5fc3\u4e2d\u7684\u7075\u667a\u5374\u4e00\u6837\u9677\u5165\u4e86\u8ff7\u60d8\u4e4b\u4e2d\uff01 \u6b64\u65f6\u8fde\u8339\u8001\u9556\u5934\u4e5f\u6e05\u9192\u4e86\u8fc7\u6765\uff0c\u5f20\u773c\u4e00\u770b\u773c\u524d\u5947\u5f02\u7684\u666f\u72b6\uff0c\u4e0d\u7531\u5f53\u573a\u6014\u4e861 \u5c31\u5728\u4f17\u4eba\u795e\u601d\u604d\u60da\u3001\u5fc3\u7075\u8ff7\u60d8\u4e4b\u65f6\uff0c\u53c8\u5728\u5e03\u5e54\u540e\u8fb9\u8d70\u51fa\u6570\u5341\u8499\u9762\u5927\u6c49\uff0c\u5728\u8fd9\u4e9b\u795e\u79d8\u8499\u9762 \u5927\u6c49\u5f80\u8fd4\u6643\u52a8\u3001\u642c\u6cc9\u62c9\u6905\u4e4b\u95f4\uff0c\u6574\u5ea7\u5730\u4e0b\u5bc6\u5ba4\uff0c\u53c8\u6362\u4e86\u4e00\u526f\u666f\u8c61\uff01 \u53ea\u89c1\u5f53\u4e2d\u60ac\u6302\u4e86\u4e00\u5e45\u957f\u53ef\u53ca\u4e08\u7684\u6761\u5e45\uff0c\u8fd9\u6761\u5e45\u4e0a\u975e\u5b57\u975e\u753b\uff0c\u5374\u662f\u4e00\u4e2a\u8292\u5934\u8d64\u5c3c\u7684\u5168\u8eab\u4eba \u50cf\uff01 \u8fd9\u753b\u4e2d\u4eba\u50cf\uff0c\u5947\u53e4\u7a81\u51e0\uff0c\u584c\u989d\u79c3\u9876\uff0c\u538b\u8033\u6beb\u6bdb\uff0c\u9f3b\u5b54\u7ffb\u5929\uff0c\u65b9\u989d\u9614\u53e3\uff0c\u5728\u8111\u95e8\u6b63\u4e2d\u6709\u4e00 \u6761\u88c2\u7f1d\uff0c\u4eff\u4f5b\u5934\u9876\u751f\u4e86\u4e00\u4e24\u4e2a\u8089\u89d2\u4e00\u822c\uff0c\u5176\u8c8c\u6709\u5982\u4f0f\u7fb2\uff0c\u7edd\u4e0d\u7c7b\u73b0\u4ee3\u751f\u4eba\uff01 \u6a2a\u989d\u5199\u4e86\u516b\u4e2a\u5927\u5b87\uff1a\u201c\u5f00\u5c71\u9f3b\u7956\uff0c\u5357\u6d77\u4e00\u541b\uff01\u201d \u50cf\u524d\u7f6e\u4e86\u4e00\u4e2a\u9ad8\u6709\u4e09\u5c3a\u3001\u5927\u6709\u5341\u56f4\u7684\u5de8\u5927\u94dc\u9f0e\uff0c\u94dc\u9f0e\u4e4b\u4e2d\u4e0d\u77e5\u6240\u71c3\u4f55\u9999\uff0c\u4e00\u7ebf\u767d\u70df\u7b14\u76f4 \u5730\u51b2\u5411\u5c4b\u9876\uff0c\u4e1d\u6beb\u4e0d\u501a\uff0c\u6709\u5982\u4e00\u9053\u55b7\u6cc9\uff0c\u88ab\u5c4b\u9876\u4e00\u963b\uff0c\u53c8\u4ee5\u6d53\u4e91\u72c2\u5377\u4e4b\u52bf\u5411\u56db\u58c1\u5012\u5782\u4e0b\u6765\uff01 \u4f7f\u6574\u4e2a\u5ba4\u4e2d\uff0c\u5145\u6ee1\u4e86\u70df\u5c9a\u7634\u6c14\uff0c\u4f7f\u4eba\u4eff\u4f5b\u6709\u7f6e\u8eab\u4e8e\u6d2a\u8352\u5927\u91ce\u3001\u6216\u4e91\u5916\u5947\u5cf0\u4e4b\u611f\uff01 \u94dc\u9f0e\u4e4b\u524d\uff0c\u5730\u4e0b\u53c8\u5e73\u653e\u4e86\u4e00\u5757\u56db\u5c3a\u89c1\u65b9\u7684\u539a\u6728\u677f\uff0c\u6728\u677f\u4e0a\u5200\u75d5\u5b9b\u7136\uff0c\u76f4\u6387\u6387\u5730\u63d2\u7740\u4e94\u67c4 \u660e\u6643\u6643\u7684\u9b3c\u5934\u5200\uff01 \u8fd9\u4e94\u67c4\u9b3c\u5934\u5200\uff0c\u94ee\u660e\u74e6\u4eae\uff0c\u6bd4\u4e00\u822c\u6c5f\u6e56\u4eba\u7269\u6240\u4f7f\u7528\u8005\u7565\u77ed\uff0c\u957f\u4e0d\u5230\u4e09\u5c3a\u3002\u4f46\u5200\u67c4\u4e0a\u4e94\u4e2a \u94c1\u94f8\u7684\u9b54\u5934\uff0c\u90fd\u6d82\u5f97\u4e94\u989d\u516d\u8272\uff0c\u9752\u7eff\u9b3c\u8138\uff0c\u8d64\u53d1\u64a9\u7259\uff0c\u72f0\u72de\u53ef\u6016\uff01 \u6b64\u65f6\u767d\u8863\u94f6\u6247\u4e66\u751f\u53ca\u91d1\u8863\u98ce\u6781\u5c11\u5973\u5df2\u5206\u5750\u94dc\u9f0e\u4e24\u65c1\uff0c\u539f\u5148\u6155\u5bb9\u5e9c\u7684\u95e8\u5ba2\u4ee5\u53ca\u201c\u5357\u6d77\u95e8\u201d \u7684\u5c5e\u4e0b\u9ad8\u624b\u6328\u80a9\u5217\u5750\u4e24\u8fb9\uff0c\u5341\u4e8c\u4e2a\u767d\u8863\u7ae5\u5b50\uff0c\u90fd\u7ad9\u5728\u4f17\u4eba\u5ea7\u4f4d\u4e4b\u540e\uff0c\u628a\u624b\u4e2d\u9999\u7089\u9ad8\u4e3e\u8fc7\u9876\uff0c \u4efb\u90a3\u6613\u5c9b\u6e05\u9999\u4e0e\u94dc\u9f0e\u7684\u70df\u5c9a\uff0c\u6df7\u5408\u4e00\u8d77\uff0c\u6c24\u6c32\u5982\u96fe\uff01\u4f7f\u6574\u5ea7\u5730\u4e0b\u6697\u5ba4\u5e73\u589e\u795e\u79d8\u4e4b\u611f\uff01 \u6697\u5ba4\u4e2d\u592e\uff0c\u6709\u56db\u6839\u6491\u6881\u7684\u5de8\u67f1\uff0c\u6bcf\u6839\u5de8\u67f1\u4e0a\u7ed1\u4e86\u4e00\u4eba\uff0c\u201c\u6458\u661f\u624b\u201d\u6155\u5bb9\u6db5\u3001\u6155\u5bb9\u6db5\u4e4b\u59bb \u4e2d\u5e74\u8d35\u5987\u3001\u201c\u51cc\u98ce\u516c\u5b50\u201d\u3001\u5c55\u5a49\u513f\uff0c\u56db\u6839\u5de8\u6821\u6b63\u597d\u7ed1\u4e86\u6155\u5bb9\u5bb6\u56db\u4e2a\u4eba\uff01 \u5176\u4f59\u7684\u6155\u5bb9\u5e9c\u4e0a\u5fe0\u5fc3\u98df\u5ba2\uff0c\u96f7\u5927\u53d4\u4ee5\u53ca\u71d5\u4eac\u9556\u5c40\u7684\u9556\u5e08\u3001\u8339\u8001\u9556\u5934\u7b49\uff0c\u5219\u5206\u522b\u53cd\u7f1a\u53cc \u624b\uff0c\u6563\u7f6e\u5730\u4e0a\uff01 \u8fd9\u573a\u9762\uff0c\u8fd9\u6c14\u6c1b\uff0c\u6709\u4f7f\u4eba\u900f\u4e0d\u51fa\u6c14\u6765\u7684\u6050\u6016\uff01 \u8fd9\u5206\u660e\u662f\u6c5f\u6e56\u9ed1\u9053\u4e0a\u4ee4\u4eba\u80c6\u88c2\u7684\u201c\u5211\u5802\u201d\u6c14\u6d3e\uff01 \u4e00\u5207\u5e03\u7f6e\uff0c\u5747\u5728\u6570\u5341\u8499\u9762\u5927\u6c49\u719f\u7ec3\u624b\u6cd5\u4e0b\u5b8c\u6210\uff0c\u524d\u540e\u4e0d\u5230\u987f\u996d\u65f6\u95f4\uff0c\u4e07\u4e8b\u5747\u5df2\u5c31\u7eea\uff0c\u91d1 \u8863\u51e4\u9497\u4fcf\u4e3d\u5c11\u5973\u4e00\u6446\u5934\uff0c\u5c31\u5728\u5979\u8fd9\u987e\u76fc\u751f\u59ff\u3001\u98ce\u60c5\u4e07\u79cd\u4e4b\u4e2d\uff0c\u900f\u51fa\u4e86\u4ee4\u4eba\u80c6\u5bd2\u5f7b\u9aa8\u7684\u6740\u6c14\uff01 \u5728\u5979\u6446\u5934\u7684\u5f53\u513f\uff0c\u90a3\u4e9b\u9ed1\u8863\u8499\u9762\u5927\u6c49\u606d\u65bd\u4e00\u793c\uff0c\u4e00\u9f50\u5f2f\u8170\u9000\u4e0b\uff0c\u53c8\u9000\u56de\u5e03\u5e54\u4e4b\u5185\u3002 \u731b\u7136\u201c\u5f53\u2019\u2019\u7684\u4e00\u68d2\u9523\u54cd\uff0c\u60cb\u7136\u9707\u9e23\uff0c\u5bc2\u7136\u65e0\u58f0\u4e4b\u4e2d\uff0c\u8fd9\u7a81\u7136\u7684\u9523\u58f0\uff0c\u9707\u5f97\u4f17\u4eba\u5fc3\u80c6\u7686 \u98a4\uff01 \u53ea\u542c\u90a3\u91d1\u8863\u51e4\u9497\u5c11\u5973\uff0c\u7528\u4e00\u79cd\u4eff\u4f5b\u6765\u81ea\u53e4\u5893\u5e7d\u9b42\u53e3\u4e2d\u7684\u9634\u6c89\u58f0\u8c03\u9053\uff1a\u201c\u2018\u5357\u6d77\u95e8\u2019\u6b64\u6b21 \u541b\u4e34\u4e2d\u539f\uff0c\u4e3a\u7684\u6d88\u9664\u4e2d\u539f\u6b66\u6797\u8d25\u7c7b\uff0c\u91cd\u6574\u4e2d\u539f\u6b66\u6797\u6e05\u89c4\uff0c\u51e1\u662f\u5de8\u6076\u5927\u5978\uff0c\u4ee5\u524d\u4f5c\u8fc7\u7792\u5fc3\u6627\u5df1 \u7684\u7f6a\u884c\u4e4b\u4eba\uff0c\u90fd\u5728\u6e05\u9664\u4e4b\u5217\uff01\u201d \u4f17\u4eba\u9e26\u96c0\u65e0\u58f0\uff0c\u5ba4\u4e2d\u7a7a\u6c14\u5747\u4e3a\u4e4b\u4e00\u7a92\uff01 \u91d1\u8863\u51e4\u9497\u5c11\u5973\u51e4\u76ee\u5f00\u5408\u4e4b\u95f4\uff0c\u7cbe\u5149\u6151\u4eba\uff0c\u5979\u90a3\u4e00\u8eab\u9576\u5d4c\u9c7c\u9cde\u73e0\u7247\u7684\u91d1\u8863\uff0c\u5728\u6570\u5341\u652f\u70db\u5149 \u7167\u6620\u4e4b\u4e0b\uff0c\u66f4\u662f\u95ea\u95ea\u653e\u5149\uff0c\u5979\u5468\u8eab\u90fd\u6563\u53d1\u7740\u4e00\u80a1\u9707\u6151\u4eba\u5fc3\u7684\u529b\u91cf\uff0c\u4ee5\u81f4\u65e0\u4eba\u6562\u6b63\u773c\u76f8\u89c6\u5979\u4e00 \u773c\uff01 \u5979\u89c1\u4f17\u4eba\u65e0\u4eba\u53d1\u58f0\uff0c\u94c1\u624b\u4e00\u6307\u201c\u6458\u661f\u624b\u201d\u9053\uff1a\u201c\u6155\u5bb9\u6db5\u662f\u5f53\u5e74\u2018\u6c5f\u5357\u4e03\u4fa0\u2019\u4e4b\u4e00\uff0c\u4e3a\u8d2a \u56fe\u5bcc\u8d35\uff0c\u6697\u7b97\u6740\u5bb3\u7ed3\u4e49\u76df\u5144\u2018\u9739\u96f3\u5251\u2019\u5c55\u4e91\u5929\uff0c\u8be5\u4e0d\u8be5\u6b7b\uff1f\u201d \u4f17\u4eba\u53e3\u4e0d\u7531\u5df1\uff0c\u540c\u58f0\u558a\u9053\uff1a\u201c\u8be5\u6b7b1\u201d \u201c\u6740\uff01\u201d \u88ab\u53cd\u7f1a\u7684\u8339\u8001\u9556\u5934\u6697\u5403\u4e00\u60ca\uff0c\u56e0\u4e3a\u4ed6\u53d1\u89c9\u81ea\u5df1\u53e3\u4e2d\uff0c\u7ade\u4e5f\u558a\u51fa\u4e00\u4e2a\u201c\u6740\u201d\u5b57\uff01 \u4ed6\u5fc3\u4e2d\u5e76\u65e0\u6b64\u5ff5\uff0c\u4f46\u53e3\u4e2d\u7adf\u4e0d\u7531\u81ea\u4e3b\u5730\u558a\u4e86\u51fa\u6765\uff0c\u8fd9\u5c82\u4e0d\u662f\u5484\u5484\u602a\u4e8b\uff1f \u91d1\u8863\u51e4\u9497\u5c11\u5973\u7ea4\u624b\u8f7b\u6307\uff0c\u8bf4\u4e86\u58f0\uff1a\u201c\u884c\u5211\uff01\u201d \u5e03\u5e54\u542f\u5904\uff0c\u4e94\u4e2a\u86ee\u58ee\u5982\u4e2d\u7684\u8d64\u818a\u5927\u6c49\uff0c\u731b\u7136\u51b2\u51fa\uff0c\u6bcf\u4eba\u5728\u539a\u6728\u677f\u4e0a\u62d4\u4e0b\u4e00\u67c4\u660e\u6643\u6643\u66f2\u9b3c \u5934\u5200\uff0c\u8de8\u6b65\u7ffb\u8eab\uff0c\u9b3c\u5934\u5200\u6253\u95ea\uff0c\u53ea\u542c\u201c\u55d6\u55d6\u201d\u8fde\u54cd\uff0c\u5200\u5d29\u8840\u73b0\uff0c\u201c\u6458\u661f\u624b\u201d\u6155\u5bb9\u6db5\uff0c\u4e00\u4ee3\u67ad \u96c4\uff0c\u53cc\u81c2\u53cc\u817f\uff0c\u4e00\u9897\u5934\uff0c\u5df2\u7ecf\u8ddf\u8eaf\u4f53\u5206\u4e86\u5bb6\uff01 \u8fd9\u6b63\u662f\u6c5f\u6e56\u9ed1\u9053\u4e0a\u201c\u4e94\u5200\u5206\u5c38\u201d\u9177\u5211\uff01 \u90a3\u4e94\u4e2a\u86ee\u725b\u4f3c\u7684\u8d64\u818a\u58ee\u6c49\uff0c\u8fd9\u62d4\u5200\u6740\u4eba\u7684\u52a8\u4f5c\uff0c\u5176\u5feb\u5982\u98ce\uff0c\u4f17\u4eba\u8fd8\u672a\u770b\u6e05\u695a\u662f\u600e\u4e48\u56de \u4e8b\uff0c\u4ed6\u4e94\u4eba\u5df2\u4e94\u5200\u5206\u5c38\u4e86\uff0c\u62ac\u817f\u642c\u8d77\u8f7b\u5e95\uff0c\u5404\u628a\u9b3c\u5934\u5200\u7684\u8840\u8ff9\u5728\u978b\u5e95\u4e0a\u64e6\u4e86\u64e6\uff0c\u540c\u65f6\u4e00\u7529 \u624b\uff0c\u201c\u54c61\u54c6\u54c6\uff01\u2026\u2026\u201d\u4e94\u67c4\u9b3c\u5934\u5200\uff0c\u4f9d\u7136\u76f4\u6387\u5730\u63d2\u5728\u6728\u677f\u4e4b\u4e0a\uff01 \u4e94\u4eba\u5982\u65cb\u98ce\u4f3c\u7684\u4e00\u8f6c\uff0c\u53c8\u9000\u56de\u5e03\u5e54\u4e4b\u4e2d\uff01 \u6155\u5bb9\u6db5\u88ab\u6740\uff0c\u6155\u5bb9\u6db5\u4e4b\u59bb\u5df2\u7ecf\u5413\u660f\u4e86\u8fc7\u53bb\uff0c\u201c\u51cc\u98ce\u516c\u5b50\u201d\u7684\u72c2\u50b2\u65e9\u5df3\u4e22\u5230\u4e4c\u6709\u4e4b\u4e61\u53bb \u4e86\uff0c\u6b64\u65f6\u5413\u5f97\u9762\u8272\u60e8\u767d\uff0c\u5634\u5507\u7d22\u7d22\u76f4\u6296\uff0c\u53ea\u6709\u5a49\u513f\uff0c\u90a3\u5a07\u5c0f\u67d4\u5f31\u7684\u59d1\u5a18\uff0c\u6b64\u65f6\u7ade\u674f\u773c\u6012\u7741\uff0c \u7834\u53e3\u5927\u9a82\uff1a\u201c\u72d7\u8d31\u4eba\uff01\u8fd9\u6740\u7236\u4e4b\u4ec7\uff0c\u5c31\u662f\u5230\u4e86\u9634\u66f9\u5730\u5e9c\uff0c\u59d1\u5a18\u4e5f\u8981\u627e\u4f60\u7d22\u56de\u8fd9\u7b14\u8840 \u503a\uff01\u2026\u2026\u201d \u5bf9\u5a49\u513f\u7684\u7834\u53e3\u5927\u9a82\uff0c\u90a3\u91d1\u8863\u51e4\u9497\u5c11\u5973\u7ade\u7f6e\u82e5\u7f54\u95fb\uff0c\u4f9d\u7136\u4ee5\u51b0\u51b7\u7684\u58f0\u8c03\uff0c\u6307\u7740\u5413\u5f97\u660f\u6b7b\u8fc7 \u53bb\u7684\u4e2d\u5e74\u8d35\u5987\u9053\uff1a\u201c\u8fd9\u5987\u4eba\uff0c\u52a9\u592b\u4e3a\u6076\uff0c\u4e14\u4e0d\u5b89\u5987\u9053\uff0c\u8be5\u4e0d\u8be5\u6b7b\uff01\u201d \u5979\u660e\u660e\u662f\u547d\u4ee4\u5f0f\u7684\u53e3\u543b\uff0c\u5374\u5411\u4f17\u4eba\u52a8\u95ee\u3002 \u8bf4\u4e5f\u5947\u602a\uff0c\u4f17\u4eba\u4ecd\u7136\u53e3\u4e0d\u5e94\u5fc3\u5730\u54c4\u7136\u5e94\u9053\uff1a\u8be5\u6b7b\uff01\u201d \u201c\u6740\uff01\u2026\u2026\u201d \u5728\u8fd9\u4f17\u4eba\u54c4\u5e94\u58f0\u4e2d\uff0c\u9694\u58c1\u9690\u9690\u4f20\u6765\u4e89\u8fa9\u4e4b\u58f0\uff01 \u542c\u4e00\u5a07\u5a9a\u7684\u5973\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u8bf4\u8fc7\u662f\u9886\u4f60\u6765\u770b\u70ed\u95f9\uff0c\u4e0d\u53eb\u4f60\u591a\u7ba1\u95f2\u4e8b\uff01\u201d \u63a5\u7740\u4e00\u7c97\u58ee\u7537\u58f0\u8bf4\u9053\uff1a\u201c\u8fd9\u592b\u4eba\u5bf9\u6211\u6709\u6551\u547d\u4e4b\u601d\uff0c\u6211\u600e\u80fd\u8896\u624b\u65c1\u89c2\uff01\u2026\u2026\u201d \u91d1\u8863\u51e4\u9497\u5c11\u5973\u5fae\u5fae\u4e00\u6115\uff0c\u51e4\u76ee\u9661\u7741\uff0c\u56de\u9996\u56db\u987e\uff0c\u4f3c\u5df2\u53d1\u89c9\u6709\u4eba\u9690\u8eab\u6697\u7aa5\uff01 \u767d\u8863\u94f6\u6247\u4e66\u751f\u4e5f\u6115\u7136\u53d8\u8272\uff0c\u56db\u4e0b\u800c\u89c2\u2026\u3002\xb7 \u7a81\u7136\uff0c\u201c\u8f70\u9686\uff01\u201d\u4e00\u58f0\u5927\u9707\uff0c\u7816\u77f3\u4e71\u8fdb\uff0c\u5c18\u7070\u98de\u626c\uff0c\u7816\u58c1\u77f3\u5899\u5ffd\u7136\u5012\u584c\u4e0b\u6765\uff01 \u5ba4\u4e2d\u4eba\u4e00\u9635\u5927\u4e71\uff0c\u7ad9\u5750\u5728\u5899\u58c1\u9644\u8fd1\u7684\u4eba\u7eb7\u7eb7\u8d77\u8eab\u56de\u907f\u2026\u2026 \u5c31\u5728\u90a3\u5012\u584c\u5899\u58c1\u3001\u7816\u77f3\u56db\u98de\u4e4b\u4e2d\uff0c\u731b\u7136\u7a9c\u51fa\u4e00\u6761\u4eba\u5f71\uff01 \u90a3\u4eba\u5f71\u4e00\u6643\uff0c\u5728\u5ba4\u4e2d\u6602\u7136\u7ad9\u5b9a\uff0c\u7adf\u662f\u4e00\u4e2a\u957f\u8eab\u7389\u7acb\u3001\u4e30\u795e\u4fca\u6717\u7684\u7fe9\u7fe9\u7f8e\u5c11\u5e74\u4e0a \u8fd9\u5c11\u5e74\u8eab\u7a7f\u5929\u8695\u4e1d\u7ec7\u6210\u7684\u7f51\u888d\uff0c\u6563\u53d1\u5728\u5934\u9876\u633d\u4e86\u4e00\u4e2a\u76f4\u9065\u53d1\uff0c\u72b9\u5982\u6d51\u91d1\u749e\u7389\uff0c\u4fca\u7f8e\u4e4b\u4e2d \u73b0\u51fa\u4e00\u56e2\u5fe0\u539a\u4e4b\u6c14\uff01\u8fd9\u4fca\u7f8e\u5c11\u5e74\u6b63\u662f\u5c55\u767d\uff01 \u4f17\u4eba\u4e00\u89c1\u662f\u5c55\u767d\u51fa\u73b0\uff0c\u4e0d\u7ba1\u8bc6\u4e0e\u4e0d\u8bc6\uff0c\u540c\u65f6\u4e3a\u4e4b\u4e00\u9707\uff01\u4e0d\u8fc7\u5374\u662f\u611f\u89c9\u4e0d\u540c\u3002 \u8339\u8001\u9556\u4e2a\u4e2a\u9762\u73b0\u559c\u8272\uff0c\u5fc3\u77e5\u6765\u4e86\u6551\u661f\uff01 \u96f7\u5927\u53d4\u6ee1\u8138\u60ca\u5947\uff01\u56e0\u4e3a\u4ed6\u4f30\u4e0d\u5230\u5c55\u767d\u6709\u5982\u6b64\u529f\u529b\uff0c\u80fd\u591f\u628a\u90a3\u4e48\u539a\u7684\u7816\u5899\u9707\u584c\uff01 \u201c\u51cc\u98ce\u516c\u5b50\u201d\u4e5f\u662f\u6ee1\u8138\u60ca\u5947\uff01\u4e0d\u77e5\u5c55\u767d\u600e\u4f1a\u9690\u8eab\u5728\u79d8\u5ba4\u5939\u58c1\u4e4b\u4e2d\uff1f \u5a49\u513f\u5374\u662f\u53c8\u60ca\u53c8\u559c\uff0c\u54c0\u58f0\u53eb\u4e86\u58f0\uff1a\u201c\u5c55\u54e5\u54e5\uff01\u201d \u201c\u6d77\u5916\u4e09\u715e\u201d\u60ca\u8be7\u5730\u7ad9\u4e86\u8d77\u6765\uff01\u767d\u8863\u94f6\u6247\u4e66\u751f\u9762\u8272\u500f\u53d8\uff01 \u90a3\u91d1\u8863\u51e4\u9497\u7684\u4fcf\u4e3d\u5c11\u5973\uff0c\u4e0d\u77e5\u600e\u7684\uff0c\u51b7\u60e8\u7684\u8138\u4e0a\u7ade\u51fa\u4e00\u7247\u8273\u5bb9\uff0c\u671b\u7740\u5c55\u767d\u51e4\u76ee\u653e\u5149\uff0c\u4f46 \u5df2\u4e0d\u662f\u6151\u4eba\u7684\u51b7\u5149\uff0c\u800c\u662f\u8ff7\u4eba\u7684\u5a07\u5a9a\u4e4b\u5149\uff01 \u5c55\u767d\u80fd\u53eb\u56db\u5927\u8c6a\u95e8\u4e4b\u5973\uff0c\u4e00\u89c1\u503e\u5fc3\uff0c\u81ea\u6709\u4ed6\u9189\u4eba\u7684\u4eea\u8868\uff0c\u82e5\u4e0d\u7136\u4ee5\u91d1\u8863\u51e4\u9497\u5c11\u5973\u7684\u51b7\u60e8 \u9ad8\u50b2\uff0c\u5c82\u4f1a\u4e00\u89c1\u52a8\u5bb9\uff01 \u767d\u8863\u94f6\u6247\u4e66\u751f\uff0c\u5373\u662f\u201c\u5357\u6d77\u5c11\u541b\u201d\u2019\u4e00\u89c1\u201c\u5357\u6d77\u9f99\u5973\u201d\u4e5f\u5c31\u662f\u90a3\u91d1\u8863\u51e4\u9497\u5c11\u5973\u4ece\u672a\u73b0\u8fc7 \u7b11\u5bb9\u7684\u8138\u4e0a\uff0c\u89c1\u5230\u5c55\u767d\uff0c\u8138\u4e0a\u7adf\u7136\u73b0\u51fa\u7b11\u5bb9\uff0c\u4e0d\u7531\u5fc3\u4e2d\u5927\u5992\uff0c\u6697\u60f3\uff1a\u597d\u4e8b\u600e\u4e48\u5168\u8ba9\u8fd9\u5c0f\u5b50\u5305 \u4e86\uff1f\u7acb\u5373\u731b\u53eb\u9053\uff1a\u201c\u6d77\u5916\u4e09\u715e\u2019\u4f55\u5728\uff1f\u53eb\u4f60\u4eec\u6349\u62ff\u7684\u6ca1\u62ff\u5230\uff0c\u5374\u5728\u8fd9\u91cc\u51fa\u73b0\u4e86\uff0c\u8fd8\u4e0d\u4e0a\u524d\u7ed9 \u6211\u62ff\u4e0b\uff01\u201d \u201c\u6d77\u5916\u4e09\u715e\u201d\u500f\u7136\u53d8\u8272\uff0c\u767d\u53d1\u5a46\u5a46\u201c\u51b7\u8273\u7ea2\u201d\u731b\u5730\u8d77\u8eab\u4e00\u7ad9\uff0c\u5389\u58f0\u543c\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff01\u59d1 \u5976\u5976\u627e\u4f60\u6ca1\u627e\u5230\uff0c\u4f60\u5012\u81ea\u5df1\u9001\u4e0a\u95e8\u6765\u4e861\u201d\u8d50\u58f0\u4e2d\uff0c\u5404\u5f62r\u63a0\u4e0a\u524d\uff0c\u731b\u7136\u5411\u5c55\u767d\u5288\u51fa\u4e00\u638c\uff01 \u5c55\u767d\u6325\u638c\u4e00\u63a5\uff0c\u201c\u562d\u201d\u7684\u4e00\u58f0\u5927\u9707\uff0c\u52b2\u98ce\u56db\u6fc0\uff0c\u6697\u5ba4\u4e2d\u6570\u5341\u652f\u706b\u6cb9\u5927\u70db\u6447\u6447\u6b32\u7184\uff01 \u5c55\u767d\u6e0a\u505c\u5cb3\u5d0e\uff0c\u7ad9\u5728\u90a3\u91cc\u7eb9\u4e1d\u672b\u52a8\uff0c\u767d\u53d1\u5a46\u5a46\u5374\u88ab\u9707\u5f97\u5012\u9000\u4e86\u4e09\u6b65\uff01 \u767d\u53d1\u5a46\u5a46\u602a\u76ee\u5706\u7741\uff0c\u6ee1\u5934\u767d\u53d1\u72b9\u5982\u72c2\u4e91\u98de\u84ec\uff0c\u72e0\u72e0\u671b\u7740\u5c55\u767d\uff0c\u5979\u771f\u4e0d\u6839\u4fe1\uff0c\u6570\u6708\u4e4b\u524d\u66fe \u4f24\u5728\u81ea\u5df1\u638c\u4e0b\u7684\u5c11\u5e74\uff0c\u7ade\u80fd\u4e00\u638c\u628a\u81ea\u5df1\u9707\u9000\uff01 \u201c\u4f5b\u5370\u6cd5\u5e08\u201d\u5728\u4e00\u65c1\u9053\uff1a\u201c\u8001\u592a\u5a46\uff0c\u5c0f\u5fc3\uff01\u8fd9\u5c0f\u5b50\u4e0d\u77e5\u5403\u4e86\u4ec0\u4e48\u836f\uff1f\u638c\u529b\u5ffd\u7136\u589e\u5f3a \u4e86\uff01\u201d \u201c\u6d77\u5916\u4e09\u715e\u201d\u867d\u7136\u5f62\u5f71\u4e0d\u79bb\uff0c\u5e38\u5728\u4e00\u8d77\uff0c\u4f46\u5e73\u65e5\u4e92\u76f8\u7b11\u8c08\u60ef\u4e86\uff0c\u6b63\u8bdd\u53cd\u8bf4\uff0c\u53cd\u8bdd\u6b63\u8bf4\uff0c \u4ece\u672a\u89c4\u89c4\u77e9\u77e9\u7684\u4ea4\u8c08\u8fc7\u3002\u201d\u4f5b\u5370\u6cd5\u5e08\u201d\u5728\u5bc6\u677e\u6797\u52ab\u9556\u5403\u4e86\u5c55\u767d\u7684\u4e8f\uff0c\u8fd9\u65f6\u63d0\u9192\u767d\u53d1\u5a46\u5a46\u7559\u795e \u672c\u662f\u597d\u610f\uff0c\u800c\u4e14\u8bdd\u4e5f\u662f\u8001\u5b9e\u8bdd\uff0c\u4f46\u767d\u53d1\u5a46\u5a46\u5374\u5411\u76f8\u53cd\u7684\u65b9\u9762\u60f3\u53bb\u4e86\uff0c\u5979\u4ee5\u4e3a\u201c\u4f5b\u5370\u6cd5\u5e08\u201d\u662f \u5632\u7b11\u4e8e\u5979\uff0c\u968f\u5440\u5440\u4e00\u9635\u5e72\u7b11\u9053\uff1a\u201c\u8d3c\u79c3\uff01\u4f11\u957f\u4ed6\u4eba\u5fd7\u6c14\uff0c\u770b\u8001\u5a18\u8fd9\u4e00\u638c\uff01\u201d \u7b11\u58f0\u4e2d\uff0c\u8fd0\u96c6\u4e86\u5341\u4e8c\u6210\u771f\u529b\uff0c\u53c8\u731b\u5411\u5c55\u767d\u88ad\u6765\uff01 \u5c55\u81ea\u89c1\u767d\u53d1\u5a46\u5a46\u8fd9\u4e00\u638c\u6bd4\u521a\u624d\u4e00\u638c\u66f4\u52a0\u731b\u70c8\uff0c\u4ed6\u4ea6\u5341\u6210\u529f\u529b\uff0c\u6253\u51fa\u4e00\u8bb0\u201c\u5929\u4f5b\u964d\u9b54\u638c\u201d \u7edd\u5b66\uff01 \u4e24\u80a1\u5de8\u5927\u65e0\u6d9b\u7684\u638c\u98ce\u52b2\u6d41\uff0c\u731b\u7136\u76f8\u649e\uff0c\u7a81\u542c\u5929\u5d29\u5730\u88c2\u4e00\u58f0\u54cd\uff0c\u7816\u77f3\u98de\u626c\uff0c\u5c18\u7070\u5f25\u7a7a\uff0c\u6574 \u5ea7\u5730\u4e0b\u5ba4\u90fd\u88ab\u9707\u5f97\u5012\u584c\u4e86\u4e0b\u6765\uff01 \u7b2c\u56db\u5341\u4e94\u7ae0\u3000\u6843\u82b1\u5973\u8bbe\u8ff7\u9b42\u9635>> \u53e4\u9f99\u300a\u5251\u5ba2\u884c\u300b \u7b2c\u56db\u5341\u4e94\u7ae0\u3000\u6843\u82b1\u5973\u8bbe\u8ff7\u9b42\u9635 \u767d\u53d1\u5a46\u5a46\u51b7\u8273\u7ea2\u88ab\u5c55\u767d\u4e00\u638c\u9707\u9000\u4e86\u4e94\u6b65\uff01 \u767d\u53d1\u5a46\u5a46\u602a\u76ee\u5706\u7741\uff0c\u6ee1\u5934\u767d\u53d1\u72e0\u6839\u76f4\u7ad6\uff0c\u5979\u771f\u4e0d\u76f8\u4fe1\u8fd9\u4e09\u4e2a\u6708\u4e4b\u524d\u4f24\u5728\u81ea\u5df1\u638c\u4e0b\u7684\u5c11 \u5e74\uff0c\u638c\u529f\u5185\u529b\u7adf\u4f1a\u7a81\u7136\u589e\u5f3a\uff01 \u602a\u5578\u4e00\u58f0\uff0c\u628a\u5979\u9b3c\u795e\u7686\u60ca\u7684\u201c\u641c\u9b42\u6307\u201d\u529f\u8fd0\u81f3\u5341\u6210\uff0c\u51fa\u6307\u5982\u671d\uff0c\u731b\u6233\u5c55\u767d\u201c\u5fc3\u4fde\u201d\u91cd \u7a74\uff01 \u6307\u98ce\u4e00\u51fa\uff0c\u9510\u98ce\u5c16\u5578\uff0c\u58f0\u52bf\u7684\u786e\u9a87\u4eba\u5df2\u6781\uff01 \u88ab\u7ed1\u5728\u67f1\u4e0a\u7684\u4e2d\u5e74\u8d35\u5987\u3001\u5c55\u5a49\u513f\uff0c\u4ee5\u53ca\u201c\u71d5\u4eac\u9556\u5c40\u201d\u7684\u4f17\u9556\u5e08\uff0c\u62c5\u5fc3\u5c55\u767d\u5b89\u5371\uff0c\u9f50\u58f0\u60ca \u547c\u2026\u2026 \u5c55\u767d\u811a\u8e29\u201c\u5343\u5e7b\u98d8\u9999\u6b65\u201d\uff0c\u4eba\u5f71\u4e00\u6643\u800c\u6ca1\uff0c\u767d\u53d1\u5a46\u5a46\u51cc\u5389\u6307\u529f\u843d\u7a7a\u201c\u5179\u201d\u7684\u4e00\u58f0\u5fae\u54cd\uff0c \u4e09\u6570\u6587\u5f00\u5916\u7684\u575a\u786c\u77f3\u58c1\u4e0a\u7adf\u73b0\u51fa\u4e86\u4e00\u4e2a\u6df1\u4e0d\u89c1\u5e95\u7684\u5927\u6d1e\uff01 \u53ef\u89c1\u767d\u53d1\u5a46\u5a46\u7684\u201c\u641c\u9b42\u6307\u201d\u5b9e\u5728\u5389\u5bb3\uff01 \u767d\u53d1\u5a46\u5a46\u624d\u5fae\u5fae\u4e00\u695e\u795e\uff0c\u6b63\u60ca\u5947\u5c55\u767d\u662f\u7528\u4ec0\u4e48\u8eab\u6cd5\uff0c\u7ade\u80fd\u8131\u51fa\u81ea\u5df1\u7684\u6307\u529b\u4e4b\u5916\uff1f\u2026\u2026 \u5ffd\u89c9\u80cc\u540e\u4e00\u70ed\uff0c\u4f3c\u6709\u4e00\u80a1\u706b\u82d7\u5411\u81ea\u5df1\u8eab\u540e\u70e7\u67651 \u767d\u53d1\u5a46\u5a46\u7acb\u611f\u4e0d\u5999\uff0c\u6643\u8eab\u6025\u8eb2\uff0c\u4f46\u4ecd\u7136\u662f\u6162\u4e86\u4e00\u6b65\uff0c\u53ea\u611f\u5de6\u80a9\u5934\u5982\u88ab\u70e7\u7ea2\u7684\u70d9\u94c1\u70eb\u4e86\u4e00 \u4e0b\u4f3c\u7684\uff0c\u5947\u75db\u65e0\u6bd4\uff0c\u8e22\u7740\u90a3\u80a1\u70ed\u529b\u5411\u5916\u4e00\u9707\uff0c\u767d\u53d1\u5a46\u5a46\u60e8\u55e5\u4e00\u58f0\uff0c\u659c\u523a\u91cc\u8def\u8def\u5341\u6570\u6b65\u51fa\u53bb\uff0c \u201c\u78b0\u201d\u7684\u4e00\u58f0\uff0c\u649e\u5728\u5899\u4e0a\uff01 \u53ea\u628a\u767d\u53d1\u5a46\u5a46\u649e\u5f97\u5934\u660f\u773c\u82b1\uff0c\u53cc\u773c\u7ffb\u767d\uff0c\u534a\u5929\u56de\u4e0d\u8fc7\u4e00\u53e3\u6c14\u6765\uff01 \u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\u4e0a\u7684\u7edd\u5b66\u679c\u7136\u5a01\u529b\u65e0\u8fb9\uff0c\u5c55\u767d\u9996\u6b21\u65bd\u5c55\uff0c\u4ec5\u53ea\u4e00\u4e2a\u7167\u9762\uff0c\u5c31\u628a\u96c4\u5fc3 \u4e07\u4e08\u3001\u6b32\u60f3\u628a\u4e2d\u539f\u6b66\u6797\u4e00\u62f3\u5f81\u670d\u7684\u201c\u6d77\u5916\u4e09\u715e\u201d\u4e2d\u540d\u5217\u7b2c\u4e8c\u7684\u767d\u53d1\u5a46\u5a46\u6253\u4f24\uff01 \u8fd9\u8fd8\u7b97\u662f\u767d\u53d1\u5a46\u5a46\u6b66\u529f\u767b\u5cf0\u9020\u6781\uff0c\u65b9\u80fd\u95ea\u8fc7\u540e\u5fc3\u81f4\u547d\u8981\u5bb3\uff0c\u4ec5\u662f\u80a9\u5934\u627f\u63a5\u4e86\u5c55\u767d\u4e00\u638c\uff01 \u5982\u82e5\u88ab\u5c55\u767d\u4e00\u638c\u6309\u5728\u5fc3\u4e0a\uff0c\u5c31\u7b97\u767d\u53d1\u5a46\u5a46\u5185\u529f\u6df1\u539a\uff0c\u4e5f\u96be\u9003\u4e00\u6b7b\uff01 \u5c55\u767d\u521d\u65bd\u201c\u5929\u4f5b\u7edd\u5b66\u201d\uff0c\u4e00\u638c\u628a\u767d\u53d1\u5a46\u5a46\u6253\u4f24\uff0c\u6574\u5ea7\u5730\u4e0b\u5ba4\u5185\uff0c\u4e0d\u5206\u654c\u6211\uff0c\u9f50\u58f0\u60ca\u547c\uff01 \u4e8c\u534a\u662f\u9707\u60ca\uff0c\u4e00\u534a\u662f\u6000\u7591\uff0c\u4efb\u4f55\u4eba\u4e5f\u4e0d\u76f8\u4fe1\uff0c\u5c55\u81ea\u7ade\u5177\u6709\u5982\u8bb8\u9ad8\u5f3a\u7684\u6b66\u529f\uff01 \u4f46\u8fd9\u4e00\u638c\u6253\u4f24\u767d\u53d1\u5a46\u5a46\uff0c\u5374\u628a\u4e0e\u767d\u53d1\u5a46\u5a46\u4f09\u4fea\u7684\u957f\u9aef\u8001\u4eba\u201c\u4ec7\u5982\u6d77\u201d\u4ec7\u516c\u516c\u7ed9\u6c14\u7ffb\u4e86 \u5929\uff01 \u53ea\u89c1\u4ed6\u628a\u8fc7\u8179\u7684\u957f\u9aef\u4e00\u6296\uff0c\u5389\u5578\u6012\u543c\u4e86\u4e00\u58f0\uff0c\u5c31\u5982\u4e00\u4e2a\u54cd\u96f7\u6253\u5728\u4e86\u6697\u5ba4\u4e4b\u4e2d\uff0c\u628a\u4f17\u4eba\u8033 \u9f13\u9707\u5f97\u75bc\u75db\u5982\u88c2\u2026\uff0e\uff0e\uff0e \u5c31\u5728\u8fd9\u6012\u543c\u5982\u96f7\u58f0\u4e2d\uff0c\u957f\u9aef\u8001\u4eba\u4e00\u53ea\u53f3\u638c\u771f\u529b\u8d2f\u6ce8\uff0c\u7ade\u731b\u7136\u6da8\u5927\u4e86\u6570\u500d\uff0c\u201c\u547c\u201d\u7684\u4e00 \u58f0\uff0c\u4ee5\u75be\u98ce\u8fc5\u96f7\u4e4b\u52bf\uff0c\u731b\u5411\u5c55\u767d\u9762\u95e8\u5288\u6765\uff01 \u5c55\u767d\u89c1\u90a3\u957f\u9aef\u8001\u4eba\u7684\u624b\u638c\uff0c\u7ade\u5927\u5982\u8f66\u8f6e\uff0c\u638c\u5fc3\u65cb\u8f6c\u7740\u6574\u4e2a\u7897\u53e3\u5927\u7684\u7f61\u98ce\u6c14\u5708\uff0c\u52b2\u6d41\u6fc0 \u6e4d\uff0c\u58f0\u52bf\u60ca\u4eba\uff0c\u524d\u6240\u672a\u89c1\uff0c\u4e0d\u7531\u5fc3\u4e2d\u4e00\u61d4\u2026\u2026 \u4e0d\u8981\u8bf4\u5c55\u767d\u5bb3\u6015\uff0c\u5c31\u5ba4\u4e2d\u6240\u6709\u4e4b\u4eba\uff0c\u5747\u662f\u6b66\u6797\u9876\u5c16\u9ad8\u624b\uff0c\u89c1\u4e86\u8fd9\u79cd\u7a7a\u524d\u7edd\u540e\u7684\u638c\u529f\uff0c\u4e5f \u83ab\u4e0d\u60dc\u7136\u8272\u53d8\u2026\u2026 \u5c55\u767d\u4ece\u672a\u89c1\u8fc7\u5982\u6b64\u5389\u5bb3\u7684\u638c\u529f\uff0c\u89c1\u72b6\u4e0d\u6562\u786c\u63a5\uff0c\u624d\u8981\u65bd\u51fa\u201c\u5343\u5e7b\u98d8\u9999\u6b65\u201d\u8eb2\u53bb\u2026\u2026 \u8033\u4e2d\u5ffd\u7136\u542c\u5230\u4e00\u4e2a\u7ec6\u5982\u868a\u7eb3\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u767d\u54e5\u54e5\uff01\u5bf9\u654c\u8981\u6709\u81ea\u4fe1\uff0c\u4f60\u4e0d\u8981\u6015\uff0c\u5148\u63a5\u4e00 \u638c\u8bd5\u8bd5\uff0c\u770b\u4f60\u7684\u2018\u5929\u4f5b\u7edd\u5b66\u2019\u7a76\u7adf\u6709\u4f55\u8fdb\u5883\uff1f\u2026\u2026\u201d \u5c55\u767d\u77e5\u662f\u7fe0\u7fe0\u5728\u6697\u4e2d\u63d0\u9192\u81ea\u5df1\uff0c\u96c4\u5fc3\u7acb\u9707\uff0c\u4e0d\u518d\u8eb2\u95ea\uff0c\u53cd\u81c2\u4e00\u638c\uff0c\u5411\u90a3\u5927\u5982\u8f66\u8f6e\u7684\u5de8\u638c \u8fce\u53bbr \u5c55\u767d\u65bd\u51fa\u7684\u8fd9\u62db\uff0c\u662f\u201c\u5929\u4f5b\u964d\u9b54\u638c\u201d\u7edd\u5b66\u4e2d\u7684\u4e00\u62db\u201c\u4f5b\u5149\u666e\u7167\u201d\uff01 \u53ea\u89c1\u52b2\u98ce\u5c71\u6d8c\uff0c\u968f\u638c\u800c\u51fa\uff01 \u521a\u731b\u65e0\u4f26\u7684\u638c\u98ce\uff0c\u4e0e\u957f\u56a3\u8001\u4eba\u5de8\u7075\u5927\u638c\u7684\u767b\u98ce\u52b2\u6d41\u4e00\u63a5\uff0c\u53ea\u542c\u201c\u8f70\u9686\u9686\uff01\u201d\u60ca\u5929\u52a8\u5730\u7684 \u4e00\u58f0\u5927\u9707\uff01 \u4f59\u529b\u56db\u6fc0\uff0c\u5c31\u5730\u5377\u8d77\u4e00\u9635\u72c2\u98d8\uff01 \u6ee1\u5ba4\u5c18\u6c99\u98de\u626c\uff0c\u5c4b\u9876\u7816\u77f3\u5760\u843d\u5982\u96e8\uff01 \u58c1\u6447\u6821\u52a8\uff0c\u6574\u5ea7\u5730\u4e0b\u5927\u5385\u51e0\u4e4e\u88ab\u4e8c\u4eba\u638c\u529b\u9707\u584c\uff01 \u4f17\u4eba\u5c16\u53eb\u60ca\u547c\u58f0\uff0c\u53ea\u89c1\u5c55\u81ea\u8eab\u4e0a\u6643\u4e86\u51e0\u6643\uff0c\u4ecd\u7136\u5728\u539f\u5730\u672b\u52a8\uff01 \u90a3\u7ad9\u5728\u4e00\u8fb9\u7684\u201c\u6d77\u5916\u4e8c\u715e\u201d\u53e6\u4e00\u4f4d\uff0c\u201c\u4f5b\u5370\u6cd5\u5e08\u201d\uff0c\u5374\u89d1\u51fa\u4fbf\u5b9c\uff0c\u6084\u65e0\u58f0\u606f\u5730\u8e6d\u4e0b\u8eab \u53bb\uff0c\u731b\u7136\u201c\u72d0\u72d0\u201d\u602a\u53eb\u4e24\u58f0\uff0c\u53cc\u638c\u731b\u5411\u5c55\u767d\u540e\u5fc3\u649e\u6765\uff01 \u201c\u4f5b\u5370\u6cd5\u5e08\u201d\u7684\u201c\u87c6\u819c\u529f\u201d\u4e43\u662f\u57df\u5916\u7edd\u5b66\uff0c\u4e5f\u662f\u5389\u5bb3\u975e\u51e1\uff01 \u4ed6\u89d1\u5b9a\u5c55\u767d\u8fde\u6218\u4e24\u5927\u9ad8\u624b\uff0c\u771f\u529b\u635f\u8017\u8fc7\u5de8\u4e4b\u9645\uff0c\u62bd\u51b7\u5b50\u6253\u51fa\uff0c\u60f3\u4e00\u51fb\u81f4\u547d\uff0c\u628a\u5c55\u767d\u6bc1\u5728 \u5f53\u573a\uff01\u2026\u2026 \u5a49\u513f\u4ece\u5c55\u767d\u4e00\u51fa\u73b0\uff0c\u5c31\u5168\u795e\u8d2f\u6ce8\u5728\u5c55\u767d\u8eab\u4e0a\uff0c\u9996\u89c1\u5c55\u54e5\u54e5\u8fde\u8d25\u4e24\u5927\u9ad8\u624b\uff0c\u82b3\u5fc3\u4e2d\u9664\u4e86\u6b22 \u559c\u4e4b\u5916\uff0c\u5bf9\u5979\u5fc3\u76ee\u4e2d\u552f\u4e00\u7684\u604b\u4eba\u66f4\u662f\u795f\u62dc\u4e07\u5206\uff0c\u65e0\u5948\u5979\u88ab\u7262\u7262\u5730\u7ed1\u5728\u5385\u6821\u4e0a\uff0c\u867d\u7136\u6709\u6ee1\u8179\u76f8 \u601d\uff0c\u5374\u65e0\u6cd5\u4e0a\u524d\u5bf9\u5fc3\u4e0a\u4eba\u503e\u8bc9\uff0c\u6b63\u5728\u4f4e\u5f8a\u4e0d\u5df2\uff0c\u7a81\u89c1\u201c\u4f5b\u5370\u6cd5\u5e08\u201d\u5077\u88ad\u5c55\u767d\uff0c\u53ea\u6025\u5f97\u5c16\u6025\u547c \u9053\uff1a\u201c\u5c55\u54e5\u54e5\uff01\u5c0f\u5fc3\u6697\u7b97\uff01\u201d \u5176\u5b9e\uff0c\u5a49\u513f\u4e0d\u558a\u53eb\uff0c\u5c55\u767d\u4e5f\u53d1\u89c9\u8eab\u540e\u6709\u4eba\u6697\u88ad\uff0c\u4ed6\u8fd0\u96c6\u771f\u529b\uff0c\u84c4\u52bf\u5f85\u654c\u65f6\uff0c\u53d1\u89c9\u81ea\u5df1\u786c \u997c\u4e86\u4e24\u5927\u9ad8\u624b\uff0c\u5185\u8179\u771f\u6c14\uff0c\u4e0d\u4f46\u4e0d\u89c1\u8870\u7aed\uff0c\u53cd\u800c\u66f4\u662f\u5145\u6ea2\uff0c\u77e5\u9053\u81ea\u5df2\u5185\u529f\u771f\u529b\u5df2\u8fbe\u6536\u53d1\u968f \u5fc3\u3001\u7089\u706b\u7eaf\u9752\u4e4b\u5883\uff0c\u5185\u5fc3\u7684\u6b23\u6170\u771f\u662f\u65e0\u53ef\u8a00\u55bb\uff0c\u6697\u60f3\u81ea\u5df1\u6b66\u529f\u6709\u6210\uff0c\u540d\u671b\u4e8b\u4e1a\uff0c\u7236\u4eb2\u7684\u8840 \u4ec7\uff0c\u90fd\u53ef\u4e00\u4e00\u8fbe\u5230\u5b8c\u6ee1\u7ed3\u679c\uff0c\u610f\u6c14\u98de\u626c\u4e4b\u4e2d\uff0c\u5bf9\u8eab\u540e\u6697\u88ad\u4e4b\u4eba\uff0c\u8fde\u770b\u4e5f\u4e0d\u770b\uff0c\u53cd\u81c2\u4e00\u638c\uff0c\u76f4 \u5411\u8eab\u540e\u64a9\u53bb\uff01 \u638c\u98ce\u52b2\u6d41\uff0c\u968f\u638c\u800c\u51fa\uff0c\u53ea\u542c\u201c\u7830\u78b0\u201d\u4e24\u58f0\u66b4\u54cd\uff0c\u4e00\u58f0\u95f7\u54fc\uff0c\u5728\u8eab\u540e\u5077\u88ad\u5c55\u767d\u7684\u201c\u4f5b\u5370\u6cd5 \u5e08\u201d\uff0c\u7adf\u88ab\u5c55\u767d\u4e00\u638c\u9707\u98de\u4e8c\u4e08\u5f00\u5916\uff01 \u201c\u6d77\u5916\u4e09\u715e\u201d\u4e4b\u4e2d\uff0c\u4ee5\u957f\u9aef\u8001\u4eba\u6b66\u529f\u6700\u9ad8\uff0c\u767d\u53d1\u5a46\u5a46\u6b21\u4e4b\uff0c\u201c\u4f5b\u5370\u6cd5\u5e08\u201d\u662f\u6700\u5f31\u7684\u4e00 \u4e2a\uff0c\u60f3\u4e24\u4e2a\u6bd4\u4ed6\u5f3a\u7684\u9ad8\u624b\uff0c\u90fd\u8d25\u5728\u5c55\u767d\u624b\u4e0b\uff0c\u4ed6\u66f4\u4e0d\u662f\u5c55\u767d\u7684\u5bf9\u624b\u4e86\uff01 \u4ed6\u4e00\u526f\u80d6\u56e2\u56e2\u7684\u8eab\u8eaf\uff0c\u88ab\u5c55\u767d\u4e00\u638c\u9707\u98de\u4e24\u4e08\u5f00\u5916\uff0c\u201c\u78b0\u201d\u7684\u4e00\u58f0\uff0c\u649e\u5728\u5899\u4e0a\uff0c\u53ea\u611f\u8179\u5185 \u6c14\u7ffb\u8840\u6d8c\uff0c\u53cc\u773c\u91d1\u661f\u4e71\u5192\uff0c\u55d3\u53e3\u4e00\u751c\uff0c\u201c\u54c7\u201d\u7684\u4e00\u58f0\uff0c\u55b7\u51fa\u4e00\u53e3\u9c9c\u8840\uff01 \u5c55\u767d\u8fde\u8d25\u201c\u6d77\u5916\u4e09\u715e\u201d\uff0c\u628a\u5927\u5385\u4e2d\u4f17\u4eba\uff0c\u4e0d\u5206\u654c\u6211\uff0c\u4e00\u9f50\u9707\u4f4f\uff01 \u4f17\u4eba\u5927\u7741\u7740\u53cc\u773c\u671b\u7740\u5c55\u767d\uff0c\u518d\u65e0\u4e00\u4eba\u6562\u4e0a\u524d\u2026\u2026 \u767d\u8863\u94f6\u6247\u7684\u201c\u9f99\u795e\u592a\u5b50\u201d\uff0c\u53cc\u624b\u6293\u7d27\u94f6\u6247\uff0c\u6ee1\u8138\u60ca\u614c\u4e4b\u8272\uff0c\u5374\u773c\u73e0\u4e71\u8f6c\uff0c\u770b\u6837\u5b50\u60ca\u6050\u4e4b \u4e2d\uff0c\u8fd8\u4e0d\u77e5\u5728\u6253\u4ec0\u4e48\u4e3b\u610f\uff1f\u2026\u2026 \u201c\u9b54\u9b3c\u5c9b\u516b\u5996\u201d\u66f4\u662f\u9762\u8272\u60e8\u767d\uff0c\u60f3\u4e0d\u5230\u4e2d\u539f\u6b66\u6797\uff0c\u8fd8\u6709\u8fd9\u7b49\u6b66\u529f\u9ad8\u624b\u2026\u2026 \u539f\u5148\u662f\u6155\u5bb9\u5e9c\u7684\u5341\u5927\u9ad8\u624b\uff0c\u73b0\u5df2\u5f52\u9644\u201c\u5357\u6d77\u95e8\u201d\u7684\u201c\u94f6\u7bab\u593a\u9b42\u201d\u7ae0\u58eb\u670b\u3001\u201c\u8ffd\u9b42\u94c3\u201d\u53f8 \u9a6c\u656c\u3001\u201c\u72ec\u811a\u98de\u9b54\u201d\u674e\u4e3e\uff0c\u5374\u4e00\u4e2a\u4e2a\u5982\u6728\u5851\u6ce5\u96d5\u4e00\u822c\uff0c\u7ad9\u5728\u90a3\u91cc\uff0c\u60f3\u662f\u8fd9\u5927\u5927\u51fa\u4e4e\u610f\u6599\u7684\u573a \u9762\uff0c\u628a\u4ed6\u4eec\u9707\u4f4f \u53ea\u6709\u201c\u6843\u82b1\u56db\u4ed9\u201d\u53ca\u90a3\u51e4\u9497\u91d1\u8863\u7684\u201c\u5357\u6d77\u9f99\u5973\u201d\uff0c\u4e00\u4e2a\u4e2a\u6843\u82b1\u7c89\u9762\u4e0a\uff0c\u4e0d\u4f46\u4e0d\u73b0\u60ca\u6050\u4e4b \u60c5\uff0c\u53cd\u800c\u73b0\u51fa\u4e00\u79cd\u559c\u60a6\u7684\u5149\u8f89\uff01 \u201c\u6843\u82b1\u56db\u4ed9\u201d\u539f\u5c31\u662f\u70df\u89c6\u5a9a\u884c\u3001\u5996\u8273\u65e0\u4f26\u7684\u56db\u4e2a\u5c24\u7269\uff0c\u4e0d\u505a\u6001\u5c1a\u4e14\u98ce\u60c5\u4e07\u79cd\uff0c\u5982\u4eca\u65bd\u51fa \u72d0\u5a9a\u4e4b\u672f\uff0c\u66f4\u662f\u7709\u773c\u64a9\u4eba\uff0c\u6d45\u7b11\u8f7b\u7ecd\uff0c\u52a0\u4e0a\u6414\u9996\u5f04\u59ff\uff0c\u771f\u4f7f\u4eba\u6709\u70b9\u773c\u82b1\u7f14\u4e71\u2026\u2606\u201d \u81f3\u4e8e\u90a3\u9890\u6307\u6c14\u4f7f\u3001\u4e0d\u53ef\u4e00\u4e16\u7684\u201c\u5357\u6d77\u9f99\u5973\u201d\uff0c\u672c\u662f\u4e00\u4e2a\u7f8e\u4eba\u80ce\u5b50\uff0c\u8bba\u7f8e\u4e3d\u4e0d\u4e0b\u4e8e\u67f3\u7fe0 \u7fe0\uff0c\u4f46\u5979\u7d20\u5e38\u9ad8\u50b2\u60ef\u4e86\uff0c\u800c\u4e14\u4e00\u5411\u5728\u201c\u5357\u6d77\u95e8\u201d\u7980\u638c\u5927\u6743\uff0c\u53d1\u53f7\u65bd\u4ee4\u60ef\u4e86\uff0c\u65e9\u628a\u5973\u6027\u5929\u8d4b\u7684 \u9b41\u529b\uff0c\u5fd8\u4e8e\u4e5d\u9704\u4e91\u5916\uff0c\u4f7f\u4eba\u89c1\u5230\u5979\u5a01\u98ce\u6740\u6c14\u7684\u4e00\u9762\uff0c\u751a\u81f3\u4e8e\u4e0d\u6562\u4ef0\u89c6\u7684\u5730\u6b65\uff01 \u662f\u4ee5\u5c55\u767d\u8fde\u4f24\u201c\u5357\u6d77\u95e8\u201d\u4e09\u5927\u9876\u5c16\u9ad8\u624b\uff0c\u5979\u4e0d\u4f46\u4e0d\u6012\uff0c\u53cd\u800c\u4e25\u8083\u7684\u8138\u4e0a\u73b0\u51fa\u4e00\u62b9\u8273\u4e3d\u7684 \u7b11\u5bb9\uff0c\u4ece\u5ea7\u4f4d\u4e0a\u76c8\u76c8\u8d77\u7acb\uff0c\u8d70\u81f3\u5c55\u767d\u4e09\u5c3a\u4e4b\u5904\uff0c\u542f\u9f7f\u7b11\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u6562\u4e0e\u6211\u2018\u5357\u6d77\u95e8\u2019\u4f5c \u5bf9\uff1f\u201d \u8fd9\u8bdd\u82e5\u5728\u65c1\u4eba\u5634\u4e2d\u8bf4\u51fa\uff0c\u5e76\u4e0d\u7b97\u4ec0\u4e48\uff0c\u4f46\u5728\u9890\u6307\u6c14\u4f7f\u60ef\u4e86\u7684\u201c\u5357\u6d77\u9f99\u5973\u201d\u5634\u4e2d\u8bf4\u51fa\uff0c\u5df2 \u662f\u5927\u903e\u5bfb\u5e38\u7684\u5ba2\u6c14\u4e86\uff0c\u4f55\u51b5\u9762\u5bf9\u7684\u8fd8\u662f\u8fde\u4f24\u5979\u95e8\u4e0b\u4e09\u5927\u9ad8\u624b\u7684\u654c\u4eba\uff01 \u4f46\u5c55\u767d\u600e\u77e5\u9053\u8fd9\u4e9b\uff0c\u89c1\u201c\u5357\u6d77\u9f99\u5973\u201d\u91d1\u8863\u51e4\u9497\uff0c\u8863\u8457\u534e\u4e3d\u5982\u4e5d\u5929\u7384\u5973\uff0c\u7f8e\u5f97\u51fa\u5947\u7684\u8138 \u4e0a\uff0c\u867d\u7136\u6d6e\u7740\u7b11\u5bb9\uff0c\u4f46\u5374\u9690\u542b\u6151\u4eba\u7684\u6740\u6c14\uff0c\u4e0d\u7531\u6717\u7136\u8bf4\u9053\uff1a\u201c\u5728\u4e0b\u5c55\u767d\uff0c\u5e76\u65e0\u5fc3\u4e0e\u4f60\u2018\u5357\u6d77 \u95e8\u2019\u4f5c\u5bf9\uff0c\u4f46\u770b\u4e0d\u60ef\u4f60\u7b49\u6ee5\u6740\u65e0\u8f9c\uff0c\u662f\u4ee5\u51fa\u9762\u4e3a\u6b66\u6797\u4e3b\u6301\u4e00\u70b9\u516c\u9053\uff01\u201d \u8fd9\u8bdd\u672c\u662f\u5b9e\u8bdd\uff0c\u4f46\u5c55\u767d\u4e0d\u5584\u8f9e\u4ee4\uff0c\u4e0d\u4f1a\u59d4\u5a49\u8a00\u4e4b\uff0c\u7387\u76f4\u9053\u51fa\uff0c\u663e\u5f97\u5484\u5484\u903c\u4eba\uff01\u4f55\u51b5\u9762\u5bf9 \u7740\u662f\u52bf\u529b\u5e9e\u5927\u3001\u60f3\u79f0\u9738\u6b66\u6797\u7684\u4e00\u5927\u95e8\u6d3e\u7684\u4ee3\u8868\u4eba\u5462\uff01 \u201c\u5357\u6d77\u9f99\u5973\u201d\u5bf9\u5c55\u767d\u867d\u7136\u6697\u751f\u60c5\u612b\uff0c\u4f46\u88ab\u5c55\u767d\u8fd9\u51e0\u53e5\u8bdd\u8bf4\u5f97\u4e0d\u7531\u6012\u6c14\u9661\u751f\uff0c\u51b7\u7b11\u9053\uff1a \u201c\u597d\u5927\u7684\u53e3\u6c14\uff01\u4e0d\u77e5\u662f\u51ed\u9601\u4e0b\u4e00\u4eba\u2018\u8fd8\u662f\u53e6\u6709\u4eba\u5728\u540e\u53f0\u6491\u8170\uff0c\u624d\u6562\u5982\u6b64\u5927\u8a00\u4e0d\u60ed\uff1f\u201d \u5c55\u767d\u4e5f\u88ab\u201c\u5357\u6d77\u9f99\u5973\u201d\u8fd9\u51e0\u53e5\u6fc0\u6012\uff0c\u56e0\u4e3a\u4ed6\u5e76\u4e0d\u77e5\u9053\u201c\u5357\u6d77\u9f99\u5973\u201d\u5728\u201c\u5357\u6d77\u95e8\u201d\u4e2d\u7684\u5730 \u4f4d\uff0c\u4ee5\u4e3a\u4f60\u4e0d\u8fc7\u4e5f\u662f\u4e00\u4e2a\u5eff\u5c81\u4e0d\u5230\u7684\u5c11\u5973\uff0c\u7adf\u6562\u5982\u6b64\u5351\u89c6\u4eba\uff1f\u4e0d\u7531\u6012\u9053\uff1a\u201c\u5c55\u767d\u72ec\u6765\u72ec\u5f80\uff0c \u4e00\u4eba\u505a\u4e8b\u4e00\u4eba\u5f53\uff0c\u53ea\u51ed\u624b\u4e2d\u4e00\u652f\u2018\u65e0\u60c5\u78a7\u5251\u201d\uff0c\u5fc3\u4e2d\u6ee1\u8154\u70ed\u8840\uff0c\u6253\u5c3d\u5929\u4e0b\u4e0d\u5e73\uff01\u201d \u201c\u5357\u6d77\u9f99\u5973\u201d\u89c1\u5c55\u767d\u8c6a\u6c14\u5e72\u4e91\uff0c\u53cc\u7709\u4e00\u6311\u9053\uff1a\u201c\u8fd9\u6837\u8bf4\u6765\uff0c\u4f60\u662f\u4e0e\u6211\u4eec\u2018\u5357\u6d77\u95e8\u2019\u4f5c\u5bf9 \u5b9a\u4e86\uff1f\u201d \u5c55\u81ea\u89c1\u8fd9\u91d1\u8863\u51e4\u6781\u5c11\u5973\u6ee1\u8138\u77a7\u4e0d\u8d77\u4ed6\u7684\u795e\u8272\uff0c\u4e0d\u5c51\u5730\u51b7\u54fc\u4e86\u4e00\u58f0\uff0c\u5374\u4e00\u8a00\u4e0d\u53d1\uff0c\u5927\u8e0f\u6b65 \u5730\u8d70\u5230\u7ed1\u4eba\u7684\u6309\u524d\uff0c\u4e3a\u4e2d\u5e74\u8d35\u5987\u2014\u2014\u4e5f\u5c31\u662f\u6155\u5bb9\u6db5\u7684\u592b\u4eba\u677e\u7ed1\u2026\u2026 \u201c\u5357\u6d77\u9f99\u4ea5\u201d\u4ece\u672a\u89c1\u8fc7\u5982\u6b64\u72c2\u50b2\u7684\u9752\u5e74\uff0c\u80c6\u6562\u5728\u5979\u9762\u524d\u5982\u6b64\u653e\u8086\uff0c\u7ade\u8fde\u7406\u4e5f\u4e0d\u5c51\u7406\u5979\uff0c \u5373\u53bb\u89e3\u653e\u72af\u4eba\uff0c\u5f53\u5373\u6012\u53f1\u4e00\u58f0\u9053\uff1a\u4e14\u4f4f\uff01\u201d \u803d\u58f0\u4e2d\uff0c\u4eba\u4e5f\u95ea\u7535\u4f3c\u5730\u6b3a\u81f3\u5c55\u767d\u9762\u524d\uff0c\u9762\u5b50L\u4e00\u6c89\uff0c\u53c8DC\u9053L\u201c\u4f60\u8981\u505a\u4ec0\u4e48\uff1f\u201d \u201c\u6551\u4eba\uff01\u201d \u5c55\u767d\u5634\u4e2d\u5982\u6b64\u8bf4\u7740\uff0c\u5374\u8fde\u5934\u4e5f\u672a\u62ac\uff0c\u4ecd\u7136\u53bb\u4e3a\u6155\u5bb9\u592b\u4eba\u677e\u7ed1\u2026\u2026 \u201c\u5357\u6d77\u9f99\u5973\u201d\u6012\u6781\uff0c\u624d\u8981\u51fa\u624b\u963b\u6b62\u2026\u2026\u201c\u6843\u82b1\u56db\u4ed9\u201d\u4e2d\u7684\u4e8c\u59d0\u201c\u8299\u84c9\u5983\u5b50\u201d\u6c34\u7389\u5a07\u67f3\u8170 \u4e00\u6446\uff0c\u8d77\u83b2\u82b1\u6b65\uff0c\u98d8\u524d\u4e24\u5c3a\uff0c\u5a07\u58f0\u8bf4\u9053\uff1a\u201c\u5bab\u4e3b\uff01\u60a8\u662f\u5343\u91d1\u4e4b\u4f53\uff0c\u62ff\u8fd9\u4e48\u4e00\u4e2a\u5c0f\u4e8e\uff0c\u4f55\u7528\u60a8 \u4eb2\u81ea\u51fa\u624b\uff0c\u8ba9\u6211\u4eec\u56db\u59ca\u59b9\u628a\u4ed6\u62ff\u4e0b\uff01\u201d \u8bf4\u7f62\u5411\u53e6\u5916\u201c\u6843\u82b1\u4e09\u4ed9\u201d\u65bd\u4e86\u4e2a\u773c\u8272\u3002 \u53e6\u5916\u201c\u6843\u82b1\u4e09\u4ed9\u201d\u4e00\u8d77\u8138\u73b0\u5a9a\u7b11\uff0c\u5404\u81ea\u67f3\u8170\u6b3e\u6446\uff0c\u98d8\u7136\u843d\u5728\u5c55\u767d\u56db\u65b9\uff0c\u4f0a\u7136\u91c7\u53d6\u5305\u56f4\u6001 \u52bf\uff01 \u201c\u5357\u6d77\u9f99\u5973\u201d\u4e5f\u56e0\u987e\u8651\u81ea\u5df1\u8eab\u4efd\uff0c\u89c1\u201c\u6843\u82b1\u56db\u4ed9\u201d\u4e00\u51fa\u9762\uff0c\u7acb\u5373\u4e00\u70b9\u5934\uff0c\u53c8\u5019\u7136\u9000\u56de\u5230 \u5ea7\u4f4d\u4e0a\u53bb\u3002 \u56e0\u4e3a\u5979\u5fc3\u91cc\u6709\u6570\uff0c\u82e5\u4ec5\u51ed\u6b66\u529f\u5185\u529b\uff0c\u201c\u6843\u82b1\u56db\u4ed9\u201d\u5c1a\u4e0d\u5982\u201c\u6d77\u5916\u4e09\u715e\u201d\uff0c\u5f53\u7136\u66f4\u4e0d\u662f\u5c55 \u767d\u7684\u5bf9\u624b\uff0c\u4f46\u201c\u6843\u82b1\u56db\u4ed9\u201d\u8be1\u8ba1\u591a\u7aef\uff0c\u4e14\u6ee1\u8eab\u8ff7\u836f\uff0c\u4ee5\u5176\u72d0\u9b45\u7684\u529b\u91cf\uff0c\u5bf9\u5f85\u4e00\u4e2a\u521d\u51fa\u9053\u7684\u5c11 \u5e74\uff0c\u7a33\u6709\u5236\u80dc\u628a\u63e1\uff0c\u6240\u4ee5\uff0c\u5979\u53c8\u5750\u56de\u4e86\u539f\u5904\uff0c\u53ea\u7b49\u201c\u6843\u82b1\u56db\u4ed9\u201d\u62ff\u4f4f\u4e86\u5c55\u767d\u518d\u8bf4\u2026\u2026 \u5c55\u767d\u5374\u4e0d\u7ba1\u90a3\u4e9b\uff0c\u89e3\u5f00\u4e86\u6155\u5bb9\u592b\u4eba\u7684\u7ed1\u7ef3\uff0c\u53c8\u53bb\u4e3a\u5a49\u513f\u89e3\u7ed1\u2026\u2026 \u5c55\u767d\u53cc\u624b\u521a\u6478\u5230\u6346\u4f50\u5a49\u513f\u7684\u7ef3\u5b50\uff0c\u5ffd\u7136\u8033\u7554\u54cd\u8d77\u4e00\u58f0\u7ec6\u82e5\u67d4\u4e1d\u7684\u51b7\u54fc\u2026\u2026 \u8fd9\u58f0\u51b7\u54fc\u918b\u5473\u6781\u91cd\uff0c\u5c55\u767d\u77e5\u7cfb\u9690\u8eab\u5899\u540e\u7684\u7fe0\u7fe0\u6240\u53d1\uff0c\u4f46\u4ecd\u4e0d\u987e\u4e00\u5207\u5730\u53bb\u4e3a\u5a49\u513f\u677e\u7ed1\u2026\u2026 \u6155\u5bb9\u592b\u4eba\u6d3b\u52a8\u4e86\u4e00\u4e0b\u88ab\u6346\u5f97\u9ebb\u75f9\u7684\u624b\u811a\uff0c\u5148\u5411\u5c55\u767d\u9053\u8c22\u4e86\u4e00\u58f0\uff0c\u7acb\u5373\u773c\u542b\u75db\u6cea\uff0c\u530d\u5310\u5728 \u5730\uff0c\u4e00\u8fb9\u6536\u62fe\u201c\u6458\u661f\u624b\u201d\u6155\u5bb9\u6db5\u2014\u2014\u4e08\u592b\u7684\u5c38\u8eab\uff0c\u4e00\u8fb9\u5583\u5583\u7948\u7977\uff0c\u4f3c\u662f\u4e3a\u5979\u90a3\u60e8\u6b7b\u7684\u4e08\u592b\u62db \u9b42\u2026\u2026 \u8fd9\u4e9b\u4e8b\u60c5\uff0c\u672c\u662f\u540c\u65f6\u95f4\u7684\u4e8b\uff0c\u56e0\u6b64\u90a3\u58f0\u4f20\u81ea\u58c1\u95f4\u7684\u51b7\u54fc\uff0c\u4e5f\u672a\u5f15\u8d77\u5728\u573a\u7684\u4eba\u6ce8\u610f\u2026\u2026 \u201c\u6843\u82b1\u56db\u4ed9\u201d\u89c1\u5c55\u767d\u89e3\u5f00\u5e55\u5bb9\u592b\u4eba\u7684\u7ed1\uff0c\u53c8\u53bb\u89e3\u5a49\u513f\uff0c\u968f\u5a9a\u7b11\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff01\u4f60\u81ea\u987e\u4e0d \u6687\uff0c\u8fd8\u53bb\u7ba1\u522b\u4eba\uff1f\u201d \u8bf4\u7f62\uff0c\u89c1\u5c55\u767d\u4e0d\u7b54\uff0c\u4ecd\u81ea\u677e\u7ed1\u6551\u4eba\uff0c\u201c\u6311\u82b1\u56db\u4ed9\u201d\u7684\u5927\u59d0\u201c\u9999\u4e91\u5983\u5b50\u201d\u82b1\u7389\u7814\uff0c\u9996\u5148\u5411 \u5c55\u767d\u9065\u9065\u62cd\u51fa\u4e00\u638c\uff01 \u638c\u98ce\u67d4\u8f6f\u65e0\u529b\uff0c\u5374\u6709\u4e00\u80a1\u5145\u9f3b\u7684\u6d53\u90c1\u7559\u9999\uff01 \u5c55\u767d\u6f2b\u4e0d\u7ecf\u5fc3\u5730\u6325\u638c\u8fce\u53bb\uff01\u2026\u2026 \u53ea\u542c\u7ec6\u5982\u868a\u7eb3\u7684\u58f0\u97f3\u5728\u5c55\u767d\u8033\u7554\u54cd\u8d77\uff1a\u201c\u50bb\u5c0f\u5b50\uff01\u63a5\u4e0d\u5f97\uff01\u8fd8\u4e0d\u8d76\u5feb\u95ed\u4f4f\u547c\u5438\uff01\u201d \u5c55\u767d\u95fb\u58f0\u4e00\u60ca\uff0c\u7acb\u5373\u95ed\u4f4f\u547c\u5438\uff0c\u4f46\u4ecd\u7136\u55c5\u5230\u5c11\u8bb8\u9999\u5473\uff0c\u7acb\u611f\u4e00\u9635\u5934\u660f\u773c\u82b1\uff01 \u53e6\u5916\u7684\u201c\u6311\u82b1\u4e09\u4ed9\u201d\uff0c\u89c1\u5927\u59d0\u5df2\u53d1\u52a8\u653b\u51fb\uff0c\u4e5f\u540c\u65f6\u5411\u5c55\u767d\u62cd\u51fa\u4e00\u638c\uff01 \u4e09\u53ea\u7c89\u767d\u624b\u638c\u8fc7\u5904\uff0c\u9999\u98ce\u5f25\u6f2b\uff0c\u540c\u65f6\u7f69\u5411\u5c55\u767d\uff01 \u540c\u65f6\uff0c\u516b\u53ea\u7c89\u767d\u7684\u624b\u638c\u4e00\u9635\u6325\u821e\uff0c\u4e00\u822c\u66f4\u5f62\u6d53\u90c1\u7684\u9999\u98ce\uff0c\u53c8\u5411\u5c55\u767d\u7f69\u53bb\uff01 \u597d\u5728\u5c55\u767d\u5df2\u95ed\u4f4f\u547c\u5438\uff0c\u4e0d\u60e7\u9999\u98ce\u8ff7\u96fe\uff0c\u89c1\u516b\u53ea\u624b\u638c\u4e00\u9f50\u653b\u5230\uff0c\u4e0d\u8eb2\u4e0d\u95ea\uff0c\u201c\u5929\u4f5b\u964d\u9b54 \u638c\u201d\u7b2c\u4e8c\u62db\u53c8\u5df2\u65bd\u51fa\uff0c\u5f3a\u5927\u638c\u98ce\u52b2\u6d41\uff0c\u5206\u56db\u65b9\u5411\u201c\u6843\u82b1\u56db\u4ed9\u201d\u5377\u53bb\uff01 \u65e0\u5948\u201c\u6843\u82b1\u56db\u4ed9\u201d\u8eab\u8eaf\u7075\u6d3b\uff0c\u8f7b\u82e5\u65e0\u9aa8\uff0c\u89c1\u4e00\u51fb\u65e0\u529f\uff0c\u5c55\u767d\u5f3a\u52b2\u638c\u98ce\u53c8\u5230\uff0c\u7acb\u5373\u53c8\u7eb7\u7eb7 \u95ea\u8fc7\uff01 \u5979\u56db\u4eba\u4e0d\u548c\u5c55\u767d\u5f3a\u52b2\u638c\u529b\u786c\u6253\uff0c\u4e00\u6cbe\u5373\u9000\uff0c\u4f46\u9999\u98ce\u8ff7\u96fe\u5374\u4e0d\u65ad\u5411\u5c55\u767d\u62cd\u6765\uff01 \u5728\u5979\u4eec\u56db\u4eba\u8ba4\u4e3a\uff0c\u5c31\u7b97\u5c55\u767d\u6b66\u529f\u9ad8\u5f3a\uff0c\u95ed\u4f4f\u547c\u5438\uff0c\u4e5f\u4e0d\u80fd\u95ed\u5f97\u592a\u4e45\uff01 \u8ff7\u9999\u62cd\u51fa\u9999\u6c14\uff0c\u53ea\u8981\u5c55\u767d\u95ed\u4e0d\u4f4f\u6c14\u65f6\uff0c\u7565\u4e00\u547c\u5438\uff0c\u5373\u53ef\u660f\u5012\uff01 \u8c01\u77e5\u5c55\u767d\u6240\u4e60\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\u7684\u6b63\u5b97\u5fc3\u6cd5\uff0c\u5927\u903e\u5bfb\u5e38\uff0c\u5410\u7eb3\u8fd0\u6c14\uff0c\u4e0d\u9700\u547c\u5438\uff0c\u53ea\u95ed \u4f4f\u4e00\u53e3\u771f\u6c14\uff0c\u5728\u5185\u8151\u6d41\u8f6c\uff0c\u5373\u53ef\u751f\u751f\u4e0d\u606f\uff0c\u5185\u529f\u771f\u529b\u4fbf\u53ef\u7528\u4e4b\u4e0d\u5b8c\u53d6\u4e4b\u4e0d\u7aed\uff01 \u662f\u4ee5\u201c\u6843\u82b1\u56db\u4ed6\u201d\u4e0e\u5c55\u767d\u8fde\u6253\u4e86\u5341\u6570\u4e2a\u7167\u9762\uff0c\u5c55\u767d\u4ecd\u672a\u88ab\u6d53\u9999\u8ff7\u5012\uff01 \u201c\u6843\u82b1\u56db\u4ed9\u201d\u6697\u6697\u5947\u602a\uff0c\u4e00\u8fb9\u52a8\u7740\u624b\u4e00\u8fb9\u6697\u81ea\u601d\u5fd6\uff0c\u83ab\u975e\u8fd9\u5c11\u5e74\u7ec3\u4f1a\u91d1\u521a\u4e0d\u574f\u4e4b\u8eab\uff0c\u7adf \u4f1a\u4e0d\u6015\u6d77\u5357\u7279\u4ea7\u7684\u201c\u8ff7\u9b42\u9999\u96fe\u201d\uff1f \u4f46\u5979\u56db\u4eba\u5374\u6fc0\u8d77\u4e86\u597d\u5f3a\u4e4b\u5fc3\uff0c\u53ea\u89c1\u5927\u59d0\u201c\u9999\u4e91\u628a\u5b50\u201d\u82b1\u7389\u7814\u5728\u5a07\u8eaf\u6025\u95ea\u3001\u8eb2\u8fc7\u5c55\u767d\u4e00\u822c \u5f3a\u5927\u638c\u98ce\u4e4b\u540e\uff0c\u6a31\u53e3\u4e00\u542f\uff0c\u5a9a\u58f0\u5531\u9053\uff1a\u201c\u4e91\u60f3\u8863\u88f3\u82b1\u60f3\u5bb9\uff01\u201d \u63a5\u7740\u53cc\u81c2\u4e00\u5c55\uff0c\u5a07\u8eaf\u591c\u534a\u7a7a\u4e2d\u5de7\u5999\u5730\u4e00\u7ffb\uff0c\u7ade\u628a\u4e00\u88ad\u8584\u5982\u8749\u7ffc\u7684\u5916\u8863\u8131\u843d\uff01 \u7acb\u523b\u9732\u51fa\u4e00\u526f\u6ed1\u817b\u5982\u86c7\u7684\u7389\u4f53\uff01 \u53ea\u89c1\u7c89\u81c2\u4e30\u81c0\uff0c\u66f2\u7ebf\u6bd5\u9732\uff0c\u5411\u7740\u5c55\u767d\u9165\u80f8\u4e00\u633a\uff0c\u771f\u662f\u98ce\u60c5\u4e07\u79cd\uff0c\u64a9\u4eba\u6b32\u9189\uff01 \u4e8c\u59d0\u201c\u8299\u84c9\u5983\u5b50\u201d\u6c34\u7389\u5a07\uff0c\u89c1\u5927\u59d0\u5df2\u65bd\u51fa\u201c\u7f57\u5239\u8ff7\u9b42\u5927\u9635\u201d\u7684\u89e3\u6570\uff0c\u5f53\u5373\u5a07\u8eaf\u4e00\u8f6c\uff0c\u4e5f \u63a5\u53e3\u5531\u9053\uff1a\u201c\u6625\u98ce\u4f5b\u69db\u9732\u534e\u6d53\uff01\u201d \u5a07\u53f1\u58f0\u4e2d\uff0c\u4e5f\u628a\u8eab\u4e0a\u7684\u7f57\u8863\u7529\u8131\u51fa\u8001\u8fdc\uff0c\u7c89\u817f\u7389\u81c2\uff0c\u4e00\u9f50\u5c55\u73b0\u4e86\u51fa\u6765\uff01 \u4e09\u59d0\u201c\u971c\u534e\u5983\u5b50\u201d\u4e5f\u63a5\u7740\u5531\u9053\uff1a\u201c\u82e5\u975e\u7fa4\u7389\u5c71\u5934\u89c1\uff01\u201d \u6b4c\u5531\u58f0\u4e2d\uff0c\u5a07\u8eaf\u4e00\u8f6c\uff0c\u7f57\u8863\u5c3d\u892a\uff0c\u6b63\u597d\u9732\u7ed9\u5c55\u767d\u4e00\u4e2a\u80a5\u5927\u4e30\u6ee1\u7684\u5c41\u80a1\uff01 \u56db\u59d0\u201c\u6f47\u6e58\u5983\u5b50\u201d\u82d7\u7389\u5a9a\u5531\u9053\uff1a\u201c\u4f1a\u5411\u7476\u53f0\u6708\u4e0b\u9022\uff01\u201d \u5979\u5374\u662f\u9762\u5411\u7740\u5c55\u767d\uff0c\u8863\u6263\u89e3\u5f00\uff0c\u8863\u8966\u534a\u89e3\uff0c\u5a07\u8eaf\u5982\u7075\u86c7\u822c\u626d\u52a8\u4e00\u4e24\u4e0b\uff0c\u80f8\u524d\u53cc\u4e73\uff0c\u5982\u65b0 \u5265\u9e21\u5934\u8089\uff0c\u96ea\u767d\u7684\u4e73\u8702\u4e0a\u9876\u7740\u4e24\u9897\u5c16\u5c16\u7ea2\u7c92\uff0c\u5411\u5c55\u767d\u98a4\u4e86\u51e0\u98a4\uff01 \u6b63\u597d\u5c55\u767d\u4e00\u638c\u6253\u5411\u201c\u6f47\u6e58\u628a\u5b50\u201d\u7684\u524d\u80f8\uff0c\u201c\u6f47\u6e58\u5983\u5b50\u201d\u4e0d\u4f46\u4e0d\u8eb2\u53cd\u800c\u4e00\u633a\u9165\u80f8\u8fce\u4e86\u4e0a \u6765\uff0c\u5413\u5f97\u5c55\u767d\u8d85\u7d27\u7f29\u624b\u6536\u62db\u3002 \u5c55\u767d\u76ee\u77aa\u53e3\u5446\uff0c\u867d\u7136\u8fd1\u6765\u4ed6\u8fde\u7ecf\u6076\u6218\uff0c\u5927\u573a\u9762\u89c1\u4e86\u4e0d\u5c11\uff0c\u53ef\u4e5f\u4ece\u6ca1\u6709\u8fd9\u6b21\u9677\u8eab\u8102\u7c89\u9635 \u4e2d\uff0c\u4f7f\u5f97\u4ed6\u8fdb\u9000\u5931\u63aa\uff0c\u4e0d\u77e5\u5982\u4f55\u662f\u597d\uff1f \u201c\u6843\u82b1\u56db\u4ed9\u201d\u6b4c\u5531\u58f0\u4e2d\uff0c\u4e00\u4e2a\u4e2a\u8131\u6389\u8eab\u4e0a\u7f57\u8863\uff0c\u7c89\u81c2\u7389\u817f\uff0c\u4e30\u4e73\u80a5\u81c0\uff0c\u628a\u7389\u4f53\u4e00\u9f50\u5c55\u73b0 \u4e86\u51fa\u6765\uff0c\u7ade\u4e0d\u518d\u62cd\u51fa\u8ff7\u9999\uff0c\u53ea\u6446\u817f\u626d\u81c2\uff0c\u56f4\u7740\u5c55\u767d\u5927\u8df3\u5176\u821e\u8d77\u6765\uff01 \u53ef\u662f\uff0c\u90a3\u9635\u9635\u7684\u6db2\u9999\uff0c\u5374\u968f\u7740\u56db\u526f\u5a07\u8eaf\u7684\u88f8\u9732\uff0c\u66f4\u5f62\u6d53\u90c1\uff01 \u6574\u5ea7\u5927\u5385\u4e2d\u5374\u5f25\u6f2b\u4e86\u8fd9\u79cd\u6d53\u90c1\u7684\u5f02\u9999\uff0c\u52a0\u4e0a\u6625\u5149\u6ee1\u773c\uff0c\u9664\u4e86\u201c\u5357\u6d77\u5c11\u541b\u201d\u3001\u201c\u5357\u6d77\u9f99 \u5973\u201d\u4ee5\u53ca\u5c11\u6570\u5b9a\u529b\u5f3a\u7684\u9ad8\u624b\u4ee5\u5916\uff0c\u5927\u5385\u4e4b\u4eba\u5747\u5df2\u660f\u8ff7\u6b32\u9189\uff0c\u6c89\u5165\u4e86\u656c\u4ed9\u6b32\u6b7b\u7684\u6e29\u67d4\u4e4b\u4e61\uff01 \u770b\u6765\u4e00\u4e2a\u4e2a\u662f\u6d51\u8eab\u65e0\u529b\uff0c\u4efb\u4eba\u5bb0\u5272\u4e86\uff01\u2026\u2026 \u5c31\u8fde\u6155\u5bb9\u592b\u4eba\u3001\u5a49\u513f\u7b49\u5973\u6027\uff0c\u4e5f\u90fd\u55c5\u5230\u4e86\u6d53\u9999\uff0c\u6c89\u5165\u534a\u660f\u534a\u9192\u7684\u72b6\u6001\u4e4b\u4e2d\u2026\u2026 \u771f\u60f3\u4e0d\u5230\u4e00\u66f2\u201c\u6e05\u5e73\u4e50\u201d\uff0c\u56db\u4e2a\u88f8\u4f53\u5c11\u5973\uff0c\u7ade\u6709\u8fd9\u822c\u8ff7\u4eba\u7684\u9b45\u529b\uff01 \u5c55\u767d\u5374\u53ea\u662f\u9664\u4e86\u611f\u5230\u4e8b\u51fa\u610f\u6599\u3001\u7565\u663e\u60ca\u618e\u4e4b\u5916\uff0c\u4e1d\u6beb\u672a\u88ab\u8ff7\u5012\uff01\u5e38\u8a00\u9053\u201c\u66fe\u7ecf\u6ca7\u6d77\u96be\u4e3a \u6c34\uff0c\u9664\u5374\u5deb\u5c71\u4e0d\u662f\u4e91\u201d\u3002\u5c55\u767d\u7ecf\u8fc7\u4e86\u201c\u5929\u4ed9\u9b54\u5973\u201d\u201c\u59f9\u5973\u8ff7\u9b42\u5927\u6cd5\u201d\u7684\u8bd5\u70bc\uff0c\u4e16\u4e0a\u8fd8\u6709\u4ec0\u4e48 \u4e1c\u897f\u53ef\u4ee5\u8ff7\u60d1\u5f97\u4e86\u5c55\u767d\uff1f \u4f46\u5c55\u767d\u5929\u6027\u5fe0\u539a\uff0c\u89c1\u56db\u4e2a\u5c11\u5973\u90fd\u8131\u5149\u4e86\u8863\u670d\uff0c\u9762\u5bf9\u7740\u81ea\u5df1\u6b4c\u5531\u821e\u8e48\uff0c\u4e0d\u518d\u51fa\u624b\u653b\u51fb\uff0c\u7adf \u4e5f\u4e0d\u601d\u51fa\u624b\u653b\u51fb\uff0c\u7acb\u5373\u6ce8\u624b\u4e0d\u653b\uff0c\u53ea\u5446\u671b\u7740\u56db\u526f\u5149\u6ed1\u7684\u88f8\u4f53\u51fa\u795e\u53d1\u695e\uff0c\u7ade\u4e0d\u77e5\u5982\u4f55\u662f\u597d\uff01 \u5ffd\u542c\u90a3\u7ec6\u5982\u868a\u7eb3\u7684\u58f0\u97f3\uff0c\u5728\u4ed6\u8033\u8fb9\u7b11\u9a82\u9053\uff1a\u201c\u5446\u5b50\uff01\u4f60\u53d1\u4ec0\u4e48\u50bb\uff1f\u8fd9\u662f\u2018\u6843\u82b1\u56db\u4ed9\u2019\u6700 \u5389\u5bb3\u7684\u2018\u7f57\u5239\u8ff7\u9b42\u5927\u9635\u2019\uff0c\u4f60\u5982\u518d\u4e0d\u7834\u9635\uff0c\u53ef\u5c31\u8981\u5403\u4e8f\u4e86\uff01\u201d \u5c55\u767d\u673a\u4f36\u4f36\u6253\u4e86\u4e2a\u51b7\u6218\uff0c\u77e5\u7cfb\u7fe0\u7fe0\u5728\u6697\u4e2d\u63d0\u9192\u81ea\u5df1\uff0c\u5b9a\u4e86\u5b9a\u5fc3\u795e\uff0c\u5c3d\u91cf\u4f7f\u81ea\u5df1\u795e\u667a\u6e05 \u9192\uff0c\u53cc\u638c\u84c4\u6ee1\u771f\u529b\uff0c\u5e73\u7f6e\u80f8\u524d\uff0c\u5bf9\u56db\u5973\u6012\u53f1\u9053\uff1a\u201c\u5c0f\u7237\u4e43\u9876\u5929\u7acb\u5730\u5947\u7537\u5b50\uff01\u4f11\u8981\u5728\u5c0f\u7237\u9762\u524d \u65bd\u5c55\u9b3c\u9b45\u4f0e\u4fe9\uff0c\u5982\u82e5\u518d\u4e0d\u9000\u53bb\uff0c\u53ef\u522b\u8bf4\u5c0f\u7237\u2026\u2026\u8981\u2026\u2026\u4e0d\u5ba2\u6c14\u4e86\uff01\u2026\u2026\u201d \u5c55\u767d\u53ea\u987e\u4e86\u5bf9\u56db\u5973\u53d1\u5a01\uff0c\u53ef\u5c31\u5fd8\u4e86\u4ed6\u5e94\u8be5\u95ed\u4f4f\u6c14\u4e0d\u80fd\u547c\u5438\uff0c\u8fd9\u4e00\u5f00\u53e3\u8bf4\u8bdd\uff0c\u5438\u4e86\u4e24\u53e3\u6c14 \u8fdb\u5230\u8179\u5185\uff0c\u7acb\u611f\u5934\u660f\u773c\u82b1\uff0c\u9676\u7136\u6b32\u9189\uff0c\u867d\u52c9\u5f3a\u628a\u8bdd\u8bf4\u5b8c\uff0c\u5230\u6700\u540e\u53ef\u4e5f\u6709\u70b9\u8bed\u4e0d\u6210\u58f0\u4e86\u3002 \u5c55\u767d\u5df2\u6e10\u660f\u8ff7\uff0c\u4f46\u4e00\u70b9\u7075\u667a\u672a\u6df7\uff0c\u8ff7\u7cca\u4e2d\u89c1\u662f\u6234\u4e86\u9b3c\u9762\u5177\u7684\u67f3\u7fe0\u7fe0\uff0c\u73b0\u8eab\u76f8\u6551\uff01 \u53ea\u542c\u201c\u5357\u6d77\u9f99\u5973\u201d\u6012\u53f1\u9053\uff1a\u201c\u5927\u80c6\u8d31\u5a62\uff01\u4f60\u80cc\u53db\u5e08\u95e8\uff0c\u4ecd\u6562\u5728\u672c\u5bab\u4e3b\u9762\u524d\u9732\u9762\uff0c\u8fd8\u4e0d\u7ed9 \u6211\u675f\u624b\u5c31\u7f1a\uff0c\u96be\u9053\u8fd8\u8981\u7b49\u672c\u5bab\u4e3b\u4e0b\u4ee4\u62ff\u4eba\u5417\uff1f\u201d \u7b2c\u56db\u5341\u516d\u7ae0\u3000\u53cd\u51fa\u201c\u5357\u6d77\u95e8\u201d>> \u53e4\u9f99\u300a\u5251\u5ba2\u884c\u300b \u7b2c\u56db\u5341\u516d\u7ae0\u3000\u53cd\u51fa\u201c\u5357\u6d77\u95e8\u201d \u67f3\u7fe0\u7fe0\u76c8\u76c8\u5411\u5357\u6d77\u9f99\u5973\u65bd\u4e86\u4e00\u793c\u9053\uff1a\u201c\u7fe0\u7fe0\u62dc\u89c1\u5bab\u4e3b\uff01\u4f46\u8fd9\u662f\u6700\u540e\u4e00\u6b21\u4e86\uff0c\u4ece\u4eca\u4ee5\u540e\u7fe0 \u7fe0\u8131\u79bb\u2018\u5357\u6d77\u95e8\u201d\u2026\u3002\u201d \u5c55\u767d\u542c\u81f3\u6b64\u5904\uff0c\u867d\u5728\u660f\u8ff7\u4e2d\u4ecd\u6697\u6697\u5403\u60ca\uff0c\u60f3\u4e0d\u5230\u7fe0\u7fe0\u7adf\u662f\u201c\u5357\u6d77\u95e8\u201d\u4e0b\uff01\u2026\u2026 \u7a81\u542c\u201c\u5357\u6d77\u9f99\u5973\u201d\u5428\u9053\uff1a\u201c\u4f4f\u53e3\uff01\u4f60\u5403\u4e86\u8c79\u80c6\u75af\u4e86\u5fc3\uff0c\u7adf\u6562\u8bf4\u51fa\u8fd9\u6837\u7684\u8bdd\u6765\uff01\u6211\u95ee\u4f601 \u4f60\u53ef\u77e5\u9053\u2018\u5357\u6d77\u95e8\u2019\u5904\u7f5a\u53db\u5f92\u7684\u5211\u7f5a\uff1f\u201d \u7fe0\u7fe0\u6253\u4e86\u4e00\u51b7\u6218\uff0c\u4f46\u60f3\u5230\u81ea\u5df1\u8ddf\u767d\u54e5\u54e5\u5df2\u7ecf\u6709\u4e86\u592b\u59bb\u4e4b\u5b9e\uff0c\u767d\u54e5\u54e5\u4e0e\u5357\u6d77\u95e8\u201d\u8a93\u540c\u6c34 \u706b\uff0c\u4eca\u65e5\u4e4b\u4e8b\u5982\u4e0d\u8ddf\u201c\u5357\u6d77\u95e8\u201d\u505a\u4e2a\u4e86\u65ad\uff0c\u65e5\u540e\u81ea\u5df1\u4fbf\u65e0\u6cd5\u4e0e\u81ea\u54e5\u54e5\u5e38\u76f8\u53ae\u5b88\uff0c\u4e8e\u662f\u4ed7\u80c6\u8bf4 \u9053\uff1a\u4eba\u5404\u6709\u5fd7\uff0c\u5c82\u80fd\u52c9\u5f3a1\u7fe0\u7fe0\u5df2\u51b3\u5fc3\u8131\u79bb\u2018\u5357\u6d77\u95e8\u2019\uff0c\u5bab\u4e3b\u770b\u5728\u5f80\u65e5\u7fe0\u7fe0\u60c5\u903e\u59d0\u59b9\u7684\u4efd \u4e0a\uff0c\u5c31\u653e\u8fc7\u7fe0\u7fe0DE\uff01\u201d \u201c\u5357\u6d77\u9f99\u5973\u201d\u563f\u563f\u51b7\u7b11\u9053\uff1a\u201c\u60c5\u903e\u59d0\u59b9\uff1f\u4f60\u522b\u5728\u81ea\u5df1\u8138\u4e0a\u8d34\u91d1\u4e86\uff01\u60f3\u4f60\u672c\u662f\u6211\u7684\u4e00\u4e2a\u5a62 \u5973\uff0c\u6211\u5bf9\u4f60\u597d\u4e86\u4e00\u70b9\uff0c\u4f60\u7adf\u5927\u80c6\u653e\u8086\uff0c\u5148\u5077\u4e86\u6211\u7684\u9b3c\u9762\u5177\uff0c\u4e0d\u8f9e\u800c\u522b\uff01\u5982\u4eca\u53c8\u80c6\u6562\u80cc\u53db\u5e08\u95e8 \u8fd8\u6562\u5728\u6211\u9762\u524d\u5de7\u8fa9\uff01\u2026\u2026\u201d \u7fe0\u7fe0\u89c1\u201c\u5357\u6d77\u9f99\u5973\u201d\u7edd\u60c5\u82e5\u6b64\uff0c\u4e5f\u4e0d\u7531\u6001\u5ea6\u8f6c\u786c\uff0c\u6297\u58f0\u9053\u3002\u201d\u6211\u4e3a\u4ec0\u4e48\u662f\u4f60\u7684\u5a62\u5973\uff1f\u4e0d \u8fc7\u6211\u7239\u7239\u5bc4\u4f4f\u5728\u4f60\u5bb6\uff0c\u6211\u624d\u597d\u5fc3\u670d\u4f8d\u4e8e\u4f60\uff01\u90a3\u662f\u4e3a\u4e86\u62a5\u7b54\u6211\u7239\u7239\u5c45\u4f4f\u4e4b\u6069\uff0c\u4f60\u4fbf\u771f\u7684\u62ff\u6211\u5f53 \u5974\u624d\u770b\u5f85\u4e86\u5417\uff1f\u90a3\u771f\u7b97\u4f60\u778e\u4e86\u773c\uff01\u81f3\u4e8e\u90a3\u9b3c\u9762\u5177\uff0c\u4e43\u662f\u5e08\u7236\u4e4b\u7269\uff0c\u5e08\u7236\u6b7b\u540e\uff0c\u5e76\u6ca1\u6709\u660e\u8a00\u4f20 \u4f60\u4e00\u4eba\uff0c\u5f53\u7136\u4f60\u53ef\u4ee5\u7528\uff0c\u6211\u4e5f\u53ef\u4ee5\u7528\uff01\u2026\u2026\u201d \u8fd9\u756a\u8bdd\u53ea\u628a\u201c\u5357\u6d77\u9f99\u5973\u201d\u6c14\u5f97\u534a\u5929\u8bf4\u4e0d\u51fa\u8bdd\u6765\uff0c\u51e4\u76ee\u5706\u7741\uff0c\u6012\u5428\u9053\uff1a\u201c\u53cd\u4e86\uff01\u53cd\u4e86\uff01\u4f60 \u7adf\u6562\u9876\u649e\u8d77\u6211\u6765\u4e86\uff01\u6211\u4e0d\u8981\u4f60\u6b7b\u65e0\u846c\u8eab\u4e4b\u5730\uff0c\u6211\u4e5f\u4e0d\u914d\u4e3b\u6301\u2018\u5357\u6d77\u95e8\u2019\u4e86\u3002\u201d \u63a5\u7740\u542c\u5230\u638c\u98ce\u7834\u7a7a\uff0c\u60ca\u5929\u52a8\u5730\uff0c\u60f3\u662f\u201c\u5357\u6d77\u9f99\u5973\u201d\u5df2\u4e0e\u67f3\u7fe0\u7fe0\u5927\u6253\u51fa\u624b\uff01 \u65c1\u8fb9\u8fd8\u4ea4\u6742\u7740\u201c\u5357\u6d77\u5c11\u541b\u201d\u7684\u529d\u89e3\u58f0\uff0c\u4f46\u5c55\u767d\u5df2\u6e10\u6e10\u660f\u8ff7\u8fc7\u53bb\uff0c\u90a3\u638c\u6307\u7834\u7a7a\u4e4b\u58f0\uff0c\u867d\u7136 \u731b\u70c8\u975e\u5e38\uff0c\u4f46\u4ed6\u5df2\u7ecf\u6e10\u6e10\u4e0d\u80fd\u542c\u5230 \u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u5c11\u65f6\u5019\uff0c\u5c55\u767d\u7a81\u611f\u8138\u4e0a\u4e00\u51c9\uff0c\u673a\u4f36\u4f36\u6253\u4e86\u4e00\u4e2a\u51b7\u6218\uff0c\u731b\u7136\u6e05\u9192\u8fc7\u6765\uff01\u7741\u773c \u4e00\u770b\uff0c\u4ed6\u5df2\u7f6e\u8eab\u4e8e\u53e6\u5916\u4e00\u4e2a\u623f\u4e2d\uff0c\u56f4\u7740\u4ed6\u7ad9\u4e86\u4e00\u5927\u5c4b\u5b50\u7684\u4eba\uff01 \u8fd9\u9526\u7ee3\u5e10\uff0c\u770b\u6765\u6709\u70b9\u773c\u719f\u3002\u4ed4\u7ec6\u4e00\u6253\u91cf\uff0c\u624d\u77e5\u662f\u81ea\u5df1\u5f53\u521d\u88ab\u6155\u5bb9\u592b\u4eba\u6551\u56de\u201c\u8c79\u7a81\u5c71 \u5e84\u201d\uff0c\u6240\u5367\u8fc7\u7684\u623f\u95f4\uff0c\u6b63\u662f\u201c\u51cc\u98ce\u516c\u5b50\u201d\u7684\u5bdd\u5ba4\u3002 \u4f46\u6b64\u6b21\u60c5\u5f62\u663e\u89c1\u4e0e\u4e0a\u6b21\u5927\u4e0d\u76f8\u540c\uff0e \u4e0a\u4e00\u6b21\u4ed6\u4f24\u75c5\u4e4b\u8eab\uff0c\u9ed8\u9ed8\u65e0\u95fb\uff0c\u51b7\u51b7\u6e05\u6e05\u8fd8\u9669\u4e9b\u88ab\u201c\u51cc\u98ce\u516c\u5b50\u201d\u4e22\u51fa\u623f\u5916\u3002 \u8fd9\u6b21\u5f88\u591a\u4eba\u56f4\u5728\u4ed6\u7684\u5e8a\u524d\uff0c\u60e7\u662f\u6ee1\u8138\u5173\u5207\u4e4b\u8272\uff0c\u597d\u50cf\u4ed6\u5df2\u6210\u4e3a\u4f17\u4eba\u5173\u5207\u7684\u4e2d\u5fc3\uff0c\u5927\u5bb6\u90fd \u5728\u671f\u671b\u7740\u4ed6\u9192\u8f6c\u6765\uff01 \u5c24\u5176\u6155\u5bb9\u592b\u4eba\u4e0e\u5a49\u513f\uff0c\u4e00\u4e2a\u5750\u5728\u5e8a\u6cbf\uff0c\u4e00\u4e2a\u534a\u4f18\u5728\u5e8a\u524d\uff0c\u5173\u5207\u5730\u671b\u7740\u5c55\u767d\uff0c\u773c\u5185\u7adf\u95ea\u52a8 \u7740\u6cea\u5149\uff01 \u67f3\u7fe0\u7fe0\u6b63\u62ff\u4e00\u53ea\u676f\u5b50\uff0c\u663e\u89c1\u5979\u662f\u7528\u676f\u4e2d\u51b7\u6c34\uff0c\u4f7f\u5c55\u767d\u6e05\u9192\u4e86\u8fc7\u6765\u3002 \u8339\u8001\u9556\u5934\u53ca\u4f17\u9556\u5e08\uff0c\u4e00\u9f50\u56f4\u5728\u5c55\u767d\u5e8a\u524d\uff0c\u8339\u8001\u9556\u5934\u7126\u6025\u5f97\u76f4\u6413\u624b\uff0c\u4f17\u9556\u5e08\u4ff1\u662f\u6ee1\u8138\u671f\u671b \u795e\u8272\uff0c\u76f4\u7b49\u7fe0\u7fe0\u4e00\u65e5\u51b7\u6c34\u55b7\u5728\u5c55\u767d\u8138\u4e0a\uff0c\u5c55\u767d\u6e05\u9192\u8fc7\u6765\uff0c\u624d\u4e00\u4e2a\u4e2a\u9762\u73b0\u559c\u8272\u3002 \u90a3\u51b7\u50b2\u7684\u201c\u51cc\u98ce\u516c\u5b50\u201d\u5374\u5446\u5750\u5728\u4e00\u8fb9\uff0c\u4e00\u8a00\u4e0d\u53d1\uff0c\u4e0d\u77e5\u5fc3\u4e2d\u5728\u60f3\u4e9b\u4ec0\u4e48\uff1f\u2026\u3002\u2019 \u5c55\u767d\u4e3e\u76ee\u56db\u987e\uff0c\u731b\u7136\u722c\u8d77\uff0c\u8131\u53e3\u95ee\u9053\uff1a\u201c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u2018\u5357\u6d77\u95e8\u2019\u7684\u4eba\u5462\uff0c\u96be\u9053\u90fd\u8dd1 \u4e86\u5417\uff1f\u201d \u8339\u8001\u9556\u5934\u8d8b\u524d\u4e00\u6b65\u8bf4\u9053\uff1a\u201c\u8d24\u5f1f\u9192\u8f6c\u4e86\uff01\u5148\u8fd0\u6c14\u770b\u770b\uff0c\u5185\u8151\u6709\u6ca1\u6709\u53d7\u4f24\uff0c\u4ee5\u540e\u7684\u4e8b\u6162\u6162 \u518d\u8bf4\uff01\u201d \u6930\u7fe0\u7fe0\u628a\u8336\u676f\u653e\u5728\u4e00\u8fb9\uff0c\u9053\uff1a\u201c\u4e0d\u59a8\u4e8b\u7684\uff01\u2018\u6843\u82b1\u56db\u4ed9\u2019\u7684\u2018\u8ff7\u9b42\u9999\u96fe\u2019\uff0c\u53ea\u80fd\u4f7f\u4eba\u660f \u8ff7\uff0c\u5e76\u4e0d\u80fd\u4f7f\u4eba\u53d7\u4f24\uff01\u767d\u54e5\u54e5\u9192\u6765\u4e5f\u5c31\u597d\u4e86\uff01\u2026\u2026\u201d \u5a49\u513f\u773c\u4e2d\u4ecd\u5e26\u7740\u6cea\u5149\uff0c\u559c\u6781\u800c\u547c\u9053\uff1a\u201c\u5c55\u54e5\u54e5\uff01\u4f60\u597d\u5566\uff01\u2026\u2026\u201d \u6155\u5bb9\u592b\u4eba\u4e0d\u4f4f\u5730\u7528\u8863\u8896\u64e6\u670d\uff0c\u53c8\u60b2\u53c8\u559c\u5730\u8bf4\u9053\uff1a\u201c\u5c55\u5c0f\u4fa0\uff01\u591a\u8c22\u4f60\u6551\u4e86\u6211\uff0c\u4f46\u6211\u7684\u4e08 \u592b\u2026\u2026\u201d \u8bf4\u81f3\u6b64\u5904\uff0c\u5df2\u545c\u54bd\u5f97\u4e0d\u80fd\u6210\u58f0\u4e86\u3002 \u201c\u51cc\u98ce\u516c\u4e88\u201d\u5634\u5507\u52a8\u4e86\u52a8\uff0c\u60f3\u8bf4\u8bdd\u5374\u672a\u8bf4\u51fa\u53e3\u6765\u2026\u3002\xb7 \u5c55\u767d\u7565\u4e00\u8fd0\u6c14\uff0c\u89c1\u5185\u8151\u771f\u6c14\u7545\u884c\u65e0\u963b\uff0c\u77e5\u9053\u7fe0\u7fe0\u6240\u8a00\u4e0d\u5047\uff0c\u7ffb\u8eab\u8df3\u4e0b\u5e8a\u6765\uff0c\u6293\u4f4f\u7fe0\u7fe0\u7684 \u624b\uff0c\u7565\u663e\u6fc0\u52a8\u5730\u95ee\u9053\uff1a\u201c\u7fe0\u7fe0\uff01\u6211\u8981\u4f60\u8bb2\u771f\u8bdd\uff01\u4f60\u771f\u662f\u2018\u5357\u6d77\u95e8\u2019\u4e0b\uff1f\u201d \u7fe0\u7fe0\u7709\u773c\u76c8\u76c8\uff0c\u6c89\u9ed8\u5730\u70b9\u4e86\u70b9\u5934\u3002 \u8fd9\u7b97\u662f\u9ed8\u8ba4\u4e86\u3002 \u5c55\u767d\u53cc\u7709\u4e00\u8038\uff0c\u6124\u5f62\u4e8e\u8272\u9053\uff1a\u201c\u90a3\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u65e9\u544a\u8bc9\u6211\uff1f\u201d \u7fe0\u7fe0\u4f4e\u8c13\u4e86\u4e00\u58f0\uff0c\u6323\u8131\u5c55\u767d\u63e1\u4f4f\u7684\u7389\u624b\uff0c\u7f13\u7f13\u8d70\u81f3\u684c\u524d\u5750\u4e0b\uff0c\u9ed8\u7136\u4e0d\u8bed\u2026\u2026 \u5c55\u767d\u5929\u6027\u75be\u6076\u5982\u4ec7\uff0c\u773c\u89c1\u201c\u5357\u6d77\u95e8\u201d\u79cd\u79cd\u66b4\u8650\uff0c\u53c8\u89c1\u201c\u6843\u82b1\u56db\u4ed9\u201d\u7684\u6deb\u8361\uff0c\u77e5\u5176\u7edd\u975e\u5584 \u7c7b\uff0c\u65e9\u5df2\u4e49\u6124\u586b\u81ba\uff0c\u89c1\u7fe0\u7fe0\u7adf\u9ed8\u8ba4\u4e0e\u201c\u5357\u6d77\u95e8\u201d\u662f\u4e00\u515a\uff0c\u60f3\u8d77\u81ea\u5df2\u4e0e\u7fe0\u7fe0\u53d1\u751f\u8d85\u53cb\u8c0a\u7684\u5173 \u7cfb\uff0c\u867d\u65e0\u592b\u59bb\u4e4b\u540d\u5df2\u6709\u592b\u59bb\u4e4b\u5b9e\uff0c\u4e0d\u7531\u53c8\u6025\u53c8\u6012\uff0c\u51b7\u54fc\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u597d\uff01\u7adf\u6562\u6b3a\u9a97\u4e8e \u6211\uff01\u201c\u3002\u3002\u201d \u8339\u8001\u9556\u5934\u4e0a\u524d\u529d\u9053\uff1a\u201c\u5c55\u8d24\u5f1f\uff01\u5148\u4e0d\u8981\u7740\u6025\uff0c\u67f3\u59d1\u5a18\u820d\u547d\u76f8\u6551\u4e8e\u4f60\uff0c\u53ef\u89c1\u5df2\u6709\u5f03\u6697\u5411\u5584 \u4e4b\u5fc3\u2026\u2026\u201d \u4f46\u5c55\u767d\u6b63\u5728\u6c14\u5934\u4e0a\uff0c\u4e0d\u7ba1\u8339\u8001\u9556\u5934\u7684\u529d\u89e3\uff0c\u6012\u58f0\u8bf4\u9053\uff1a\u201c\u4e0d\u7ba1\u600e\u4e48\u8bf4\uff0c\u5f53\u521d\u5979\u4e0d\u5bf9\u6211\u8bf4 \u660e\uff0c\u5c31\u662f\u6b3a\u9a97\u2026\u2026\u201d \u5c55\u767d\u6b64\u8a00\u4e00\u51fa\uff0c\u7fe0\u7fe0\u53ea\u611f\u4e00\u9635\u75db\u5fc3\uff0c\u7ade\u201c\u5207\u201d\u7136\u4e00\u58f0\uff0c\u54ed\u7740\u98de\u8eab\u63a0\u5411\u95e8\u5916\uff01\u2026\u2026 \u8339\u8001\u9556\u5934\u6025\u4ece\u540e\u8fb9\u8ffd\u51fa\u5ba4\u5916\uff0c\u4f46\u7fe0\u7fe0\u8eab\u6cd5\u4f55\u7b49\u5feb\u901f\uff0c\u7b49\u8339\u8001\u9556\u5934\u8ffd\u51fa\u5ba4\u5916\u65f6\uff0c\u65e9\u5df2\u5931\u53bb \u4e86\u7fe0\u7fe0\u7684\u8e2a\u8ff9\uff01\u8339\u8001\u9556\u5934\u8fde\u53eb\u6570\u58f0\u201c\u67f3\u59d1\u5a18\uff01\u201d\u4e0d\u89c1\u56de\u5e94\uff0c\u77e5\u5176\u53bb\u8fdc\uff0c\u9893\u7136\u8fd4\u56de\u623f\u4e2d\uff0c\u5bf9\u5c55 \u767d\u9053\uff1a\u201c\u5c55\u8d24\u5f1f\uff01\u4e0d\u662f\u8001\u54e5\u54e5\u8bf4\u4f60\uff0c\u5b9e\u5728\u662f\u4f60\u7684\u813e\u6c14\u592a\u6025\u4e86\uff0c\u67f3\u59d1\u5a18\u867d\u7136\u51fa\u8eab\u2018\u5357\u6d77\u95e8\u2019\uff0c \u4f46\u51e0\u6b21\u6551\u4f60\uff0c\u4e0d\u60dc\u4e0e\u2018\u5357\u6d77\u95e8\u2019\u6b63\u9762\u4e3a\u654c\uff0c\u53ef\u89c1\u5979\u5df2\u6709\u8131\u79bb\u2018\u5357\u6d77\u95e8\u2019\u7684\u51b3\u5fc3\uff0c\u5e38\u8a00\u9053\uff1a \u2018\u541b\u5b50\u4e0d\u963b\u4eba\u5411\u5584\u2019\uff0c\u4f60\u8fd9\u6837\u5f53\u4f17\u7ed9\u5979\u96be\u582a\uff0c\u5c82\u4e0d\u662f\u2018\u4fc3\u4eba\u4e3a\u6076\u2019\u4e86\u5417\uff1f\u201d \u5176\u5b9e\uff0c\u5c55\u767d\u5bf9\u67f3\u7fe0\u7fe0\u53d1\u706b\uff0c\u4e0d\u4ec5\u662f\u4e3a\u4e86\u53d1\u73b0\u7fe0\u7fe0\u51fa\u8eab\u201c\u5357\u6d77\u95e8\u201d\u7684\u4e00\u4ef6\u4e8b\uff0c\u800c\u662f\u6570\u6708\u76f8 \u5904\uff0c\u79cd\u79cd\u4e0d\u5982\u610f\u7d2f\u79ef\u5728\u5fc3\u4e2d\u7684\u6012\u706b\uff0c\u4e00\u65e6\u53d1\u4f5c\u7f62\u4e86\u3002 \u5c55\u767d\u4e0e\u7fe0\u7fe0\u7684\u7ed3\u8bc6\uff0c\u662f\u7531\u4e8e\u7fe0\u7fe0\u76f8\u6551\uff0c\u5c55\u767d\u90a3\u65f6\u5bf9\u5979\u53ea\u662f\u611f\u8c22\uff0c\u5e76\u6ca1\u6709\u7231\u3002\u4e4b\u540e\uff0c\u7fe0\u7fe0 \u884c\u8e2a\u8be1\u79d8\uff0c\u66fe\u5f15\u8d77\u5c55\u767d\u7684\u7591\u60e7\uff0c\u800c\u60f3\u5077\u5077\u79bb\u5f00\u5979\uff0c\u5f53\u53d1\u73b0\u8eab\u4e0a\u8d35\u91cd\u4e4b\u7269\u201c\u65e0\u60c5\u78a7\u5251\u201d\u53ca\u300a\u9501 \u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\u5df3\u88ab\u7fe0\u7fe0\u53d6\u53bb\u65f6\uff0c\u624d\u60f3\u627e\u7fe0\u7fe0\u8ffd\u6811\u5931\u7269\uff0c\u4e8c\u4eba\u5728\u6cb3\u8fb9\u529b\u6218\u201c\u91d1\u5e9c\u53cc\u94c1\u536b\u201d\u65f6 \u76f8\u9047\uff0c\u65e0\u5fc3\u4e2d\u5728\u5c0f\u8239\u4e0a\u8dcc\u5012\uff0c\u808c\u80a4\u76f8\u63a5\uff0c\u53d1\u751f\u4e86\u8d85\u53cb\u8c0a\u7684\u5173\u7cfb\uff0c\u53ef\u4ee5\u8bf4\u5b8c\u5168\u662f\u4e8b\u60c5\u7684\u5de7\u5408\u4e0e \u4e34\u65f6\u7684\u51b2\u52a8\uff0c\u5176\u4e2d\u5e76\u65e0\u6df1\u539a\u7684\u611f\u60c5\u505a\u57fa\u7840\uff01 \u5c55\u767d\u53ea\u662f\u89c9\u5f97\u4eba\u5bb6\u4e00\u4e2a\u5973\u513f\u6e05\u767d\u4e4b\u8eab\u732e\u7ed9\u4e86\u81ea\u5df2\uff0c\u4fbf\u6709\u63a8\u8131\u4e0d\u6389\u7684\u8d23\u4efb\uff0c\u4e00\u5b9a\u8981\u5a36\u5979\u4e3a \u59bb\uff0c\u505a\u4e3a\u81ea\u5df1\u7ec8\u8eab\u7684\u4f34\u4fa3\uff01\u8fd9\u662f\u5c55\u767d\u901a\u8fbe\u4eba\u60c5\u7684\u5730\u65b9\uff0c\u4e5f\u53ef\u8bf4\u662f\u5c55\u767d\u4f1f\u5927\u7684\u5730\u65b9\uff01 \u4f46\u7fe0\u7fe0\u662f\u771f\u5fc3\u7231\u7740\u5c55\u767d\u7684\uff0c\u9752\u6625\u5c11\u5973\uff0c\u60c5\u7aa6\u521d\u5f00\uff0c\u591a\u534a\u662f\u5982\u6b64\uff0c\u70ed\u60c5\u5982\u706b\uff0c\u4e00\u65e6\u949f\u60c5\uff0c \u4e00\u773c\u770b\u89c1\u610f\u4e2d\u4eba\uff0c\u4fbf\u4ee5\u8eab\u76f8\u8bb8\uff0c\u78b0\u5230\u5c55\u767d\u7b97\u662f\u5e78\u8fd0\uff0c\u5982\u679c\u9047\u4eba\u4e0d\u6dd1\uff0c\u78b0\u5230\u7684\u662f\u4e00\u4e2a\u82b1\u82b1\u516c \u5b50\uff0c\u90a3\u5c31\u53ea\u6709\u81ea\u6028\u7ea2\u989c\u8584\u547d\uff0c\u5f92\u53f9\u201c\u75f4\u5fc3\u5973\u5b50\u8d1f\u5fc3\u6c49\u201d\u4e86\uff01 \u7fe0\u7fe0\u7231\u5c55\u767d\u5230\u4e86\u65e0\u5fae\u4e0d\u81f3\u7684\u5730\u6b65\uff0c\u201c\u4ea1\u9b42\u8c37\u201d\u4e2d\u4e3a\u5c55\u767d\u7597\u4f24\uff0c\u4e09\u4e2a\u6708\u4e4b\u4e2d\uff0c\u88f8\u4f53\u504e\u5047\uff0c \u65bd\u5c55\u201c\u7eaf\u9634\u7597\u9633\u201d\uff0c\u5982\u9e21\u5b75\u5375\uff0c\u6551\u6d3b\u4e86\u5c55\u767d\uff0c\u53c8\u4ee5\u8d64\u88f8\u8892\u88ce\u4e4b\u8eab\uff0c\u65bd\u5c55\u201c\u59f9\u5973\u8ff7\u9b42\u5927\u6cd5\u201d\uff0c \u5e2e\u52a9\u5c55\u767d\u7ec3\u4f1a\u4e86\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\u4e0a\u9690\u79d8\u7684\u4e09\u79cd\u7edd\u4e16\u6b66\u529f\uff0c\u53ef\u4ee5\u8bf4\uff0c\u5c55\u767d\u80fd\u6709\u4eca\u5929\u7684\u6210 \u5c31\uff0c\u591a\u534e\u662f\u7fe0\u7fe0\u6240\u8d50\uff01 \u7fe0\u7fe0\u4e0e\u5c55\u767d\u4e00\u53e5\u620f\u8a00\uff0c\u201c\u53eb\u5c55\u767d\u4e00\u5207\u542c\u5979\u7684\uff01\u201d\u5979\u4e0d\u8be5\u8ba4\u771f\uff0c\u5728\u79bb\u5f00\u5c71\u8c37\uff0c\u4e00\u8def\u4e4b\u4e0a\uff0c \u5904\u5904\u4e8e\u6d89\u5c55\u767d\u7684\u884c\u52a8\uff0c\u5e76\u4ee5\u90a3\u53e5\u620f\u8a00\u8981\u631f\uff0c\u5f15\u8d77\u5c55\u767d\u5185\u5fc3\u7684\u4e0d\u6ee1\u3002 \u52a0\u4e0a\uff0c\u5979\u65f6\u65f6\u6234\u4e0a\u90a3\u526f\u72f0\u6076\u7684\u9b3c\u9762\u5177\uff0c\u4f7f\u5c55\u767d\u65f6\u65f6\u611f\u5230\u4e0d\u5feb\uff01 \u5c55\u767d\u7684\u53d1\u6012\u662f\u6765\u540d\u2014\u8d22\uff0c\u7fe0\u7fe0\u5374\u89c9\u5f97\u59d4\u5c48\u96be\u5fcd\uff0c\u6545\u800c\u4e00\u8d70\u4e86 \u8339\u8001\u9556\u5934\u4e0d\u77e5\u4e00\u5bf9\u5c0f\u60c5\u4fa3\u7684\u5185\u5fc3\u9690\u79d8\uff0c\u53ea\u8d23\u5c55\u767d\u592a\u8fc7\u4efd\u4e86\uff0c\u5c55\u767d\u5374\u6c14\u54fc\u54fc\u7684\uff0c\u5140\u81ea\u6012\u6c14 \u672a\u7184\uff01 \u56e0\u4e3a\u4ed6\u89c9\u5f97\u7fe0\u7fe0\u4e0d\u80fd\u6b3a\u9a97\u4ed6\uff0c\u4ed6\u5fc3\u76ee\u4e2d\u662f\u7fe0\u7fe0\u7684\u6587\u592b\uff0c\u4e08\u592b\u5c82\u53ef\u53d7\u59bb\u5b50\u7684\u6b3a\u9a97\uff1f \u6155\u5bb9\u592b\u4eba\u4e5f\u5728\u5c4b\u4e2d\u5bf9\u5c55\u767d\u9053\u201c\u90a3\u67f3\u59d1\u5a18\u7684\u4e3a\u4eba\u7684\u786e\u5f88\u597d\uff0c\u800c\u4e14\u6b66\u529f\u9ad8\u5f3a\uff0c\u5982\u4e0d\u662f\u67f3\u59d1\u5a18 \u80fd\u62b5\u4f4f\u2018\u5357\u6d77\u95e8\u2019\u4e2d\u7684\u4eba\uff0c\u6050\u6015\u4f17\u4eba\u90fd\u8981\u6b7b\u5728\u2018\u5357\u6d77\u95e8\u2019\u6bd2\u8fa3\u5c11\u5973\u4e4b\u624b1\u201d \u5a49\u513f\u5374\u5728\u4e00\u8fb9\u63d2\u53e3\u9053\uff1a\u201c\u5982\u679c\u4e0d\u662f\u90a3\u53eb\u505a\u4ec0\u4e48\u2018\u5357\u6d77\u5c11\u541b\u2019\u7684\u767d\u8863\u72c2\u751f\uff0c\u4e0e\u2018\u5357\u6d77\u9f99 \u5973\u2019\u8d77\u4e86\u51b2\u7a81\uff0c\u6050\u6015\u67f3\u7fe0\u7fe0\u4e5f\u662f\u65e0\u6cd5\u5ea7\u4ed8\uff01\u2026\u2026\u201d \u6155\u5bb9\u592b\u4eba\u77aa\u4e86\u5a49\u513f\u4e00\u773c\uff0c\u9053\uff1a\u201c\u5a49\u513f\uff01\u5c31\u662f\u4f60\u5634\u729f\uff01\u4f60\u8fd8\u4e0d\u662f\u88ab\u4eba\u64d2\u4f4f\uff0c\u591a\u4e8f\u67f3\u59d1\u5a18\u624d \u6551\u4e86\u4f60\u95e8\u2026\uff0e\u201d \u5a49\u513f\u9887\u4e0d\u670d\u6c14\u5730\u8bf4\u9053\uff1a\u201c\u5973\u513f\u82e5\u4e0d\u662f\u53d7\u4e86\u2018\u6843\u82b1\u56db\u4ed9\u2019\u7684\u6697\u7b97\uff0c\u4e5f\u4e0d\u4f1a\u8f7b\u6613\u88ab\u4eba\u64d2 \u4f4f\u2026\u2026\u201d \u8339\u8001\u9556\u5934\u89c1\u6bcd\u5973\u4e8c\u4eba\u8981\u5435\u8d77\u6765\uff0c\u5fd9\u7528\u8bdd\u9898\u5c94\u5f00\uff0c\u9053\uff1a\u201c\u7b97\u4e86\uff01\u8fc7\u53bb\u7684\u4e8b\u4e0d\u7528\u518d\u63d0\u4e86\uff0c \u2018\u5357\u6d77\u95e8\u2019\u79f0\u9738\u4e2d\u539f\uff0c\u5984\u6740\u65e0\u8f9c\uff0c\u5982\u4e0d\u8bbe\u6cd5\u6d88\u5f25\uff0c\u957f\u6b64\u4ee5\u5f80\uff0c\u6050\u6015\u4e2d\u539f\u6b66\u6797\u5c06\u8981\u53d8\u6210\u5c38\u5c71\u8840 \u6d77\uff0c\u8fd8\u4e0d\u77e5\u6709\u591a\u5c11\u4eba\u8981\u6ca6\u5165\u6740\u52ab\uff1f\u2026\u2026\u201d \u6b64\u65f6\uff0c\u4eba\u5f71\u4e00\u95ea\uff0c\u4e71\u53d1\u83b2\u84ec\u7684\u96f7\u5927\u53d4\u5ffd\u7136\u95ea\u4e86\u8fdb\u6765\uff0c\u5148\u5411\u6155\u5bb9\u592b\u4eba\u56de\u9053\uff1a\u201c\u542f\u4e8b\u592b\u4eba\uff0c \u80cc\u8282\u6295\u9760\u2018\u5357\u6d77\u95e8\u2019\u7684\u95e8\u5ba2\u90fd\u5df2\u8083\u6e05\uff0c\u4f59\u4e0b\u7684\u5fe0\u8d1e\u4e4b\u58eb\uff0c\u90fd\u9f50\u96c6\u5728\u9662\u4e2d\uff0c\u5c1a\u6709\u4e00\u767e\u4f59\u4eba\uff0c\u9759 \u5019\u592b\u4eba\u53d1\u843d\uff01\u201d \u6155\u5bb9\u592b\u4eba\u4e0d\u6127\u4e3a\u540d\u95e8\u8d35\u5987\uff0c\u867d\u7136\u906d\u5230\u5de8\u5927\u53d8\u6545\uff0c\u53c8\u662f\u5728\u6587\u541b\u65b0\u5be1\u7684\u60b2\u6f14\u4e4b\u4e2d\uff0c\u4ecd\u80fd\u4ece\u5bb9 \u9547\u9759\u5584\u5904\uff0c\u5148\u5411\u96f7\u5927\u53d4\u9053\u4e86\u52b3\uff0c\u5373\u523b\u81f3\u623f\u5916\u4e0e\u95e8\u4e0b\u98df\u5ba2\u89c1\u9762\uff0c\u5e76\u91cd\u65b0\u5206\u6d3e\u95e8\u5ba2\u5728\u5e84\u4e0a\u7684\u804c \u4f7f\u2026\u2026 \u96f7\u5927\u53d4\u501f\u6b64\u673a\u4f1a\u4ea6\u4e0e\u8339\u8001\u9556\u5934\u89c1\u4e86\uff0c\u53c8\u5728\u5e8a\u524d\u95ee\u5019\u5c55\u767d\uff0c\u5c55\u767d\u6b64\u65f6\u89c1\u4e86\u96f7\u5927\u53d4\uff0c\u5982\u89c1\u4eb2 \u4eba\uff0c\u5373\u628a\u81ea\u5df1\u7684\u51fa\u8eab\uff0c\u4ee5\u53ca\u7236\u4eb2\u7684\u8840\u4ec7\u4e00\u4e00\u8bf4\u51fa\u3002 \u96f7\u5927\u53d4\u4e0d\u514d\u7a00\u5618\u4e00\u756a\uff0c\u4f46\u6700\u540e\u544a\u8beb\u5c55\u81ea\u9053\uff1a\u201c\u8d24\u4f84\u7684\u7236\u4ec7\u56fa\u7136\u91cd\u8981\uff0c\u4f46\u73b0\u5728\u6155\u5bb9\u5e86\u4e3b\u5df2 \u6b7b\uff0c\u6240\u8c13\u2018\u4eba\u6b7b\u4e0d\u8bb0\u4ec7\u2019\uff0c\u8d24\u4f84\u4e0e\u6155\u5bb9\u5e84\u4e3b\u7684\u8fd9\u4e00\u6bb5\u8840\u4ec7\uff0c\u53ef\u4ece\u6b64\u4e00\u7b14\u52fe\u9500\uff0c\u76ee\u524d\u6700\u91cd\u8981\u7684 \u662f\u5982\u4f55\u8054\u5408\u4e2d\u539f\u6b66\u6797\uff0c\u4ee5\u5bf9\u6297\u2018\u5357\u6d77\u95e8\u2019\uff0c\u8fd9\u624d\u662f\u5f53\u52a1\u4e4b\u6025\uff0c\u4e0d\u77e5\u8d24\u4f84\u4ee5\u4e3a\u5982\u4f55\uff1f\u201d \u96f7\u5927\u53d9\u8bf4\u7f62\uff0c\u53cc\u76ee\u5982\u7535\uff0c\u671b\u7740\u5c55\u767d\uff0c\u89c1\u5c55\u767d\u9ed8\u7136\u70b9\u5934\uff0c\u77e5\u9053\u8fd9\u4f4d\u81f3\u4ea4\u597d\u53cb\u7684\u540e\u4eba\uff0c\u8fd8\u4e0d \u5931\u4e3a\u6062\u5b8f\u5927\u5ea6\uff0c\u5fc3\u4e2d\u751a\u6170\uff0c\u968f\u4e00\u56de\u624b\uff0c\u628a\u5a49\u513f\u4e0e\u201c\u51cc\u98ce\u516c\u5b50\u201d\u4e00\u624b\u6293\u4f4f\u4e00\u4e2a\uff0c\u62c9\u81f3\u5c55\u767d\u9762\u524d \u9053\uff1a\u201c\u8fd9\u662f\u6155\u5bb9\u5e86\u4e3b\u7684\u513f\u5973\uff0c\u4e0a\u4e00\u4ee3\u7684\u51a4\u4ec7\uff0c\u8ba9\u5b83\u968f\u7740\u6b7b\u8005\u6b7b\u53bb\u5427\uff01\u4f46\u613f\u4f60\u4eec\u4e0b\u4e00\u4ee3\u7684\u751f \u8005\uff0c\u80fd\u5316\u654c\u4e3a\u53cb\uff0c\u591a\u591a\u4eb2\u8fd1\u4eb2\u8fd1\uff01\u201d \u5a49\u513f\u542b\u60c5\u8109\u8109\uff0c\u56e0\u4e3a\u5979\u82b3\u5fc3\u4e2d\u65e9\u5df2\u7231\u4e0a\u4e86\u5c55\u54e5\u4e8c\u4eba\u7684\u7236\u4eb2\u7ade\u6709\u7740\u4e00\u6bb5\u4ec7\u6068\uff0c\u5982\u4eca\u542c\u96f7\u5927 \u53d4\u4e3a\u4ed6\u4eec\u5316\u89e3\uff0c\u5f53\u7136\u662f\u6c42\u4e4b\u4e0d\u5f97\u2026\u2026 \u90a3\u201c\u51cc\u98ce\u516c\u4e8e\u201d\u9ad8\u50b2\u60ef\u4e86\uff0c\u6b64\u65f6\uff0c\u53cd\u800c\u6709\u70b9\u6168\u59ae\u4e0d\u5b89\u3002 \u96f7\u5927\u53d4\u53c8\u9053\uff1a\u201c\u5c55\u8d24\u4f84\uff01\u600e\u6837\uff1f\u60f3\u6211\u90a3\u76df\u5144\u2018\u9739\u96f3\u5251\u2019\u5c55\u4e91\u5929\uff0c\u4e43\u662f\u4e00\u4e2a\u5bbd\u5b8f\u5927\u91cf\u4e4b \u4eba\uff0c\u4f60\u662f\u76df\u5144\u4e4b\u5b50\uff0c\u4e5f\u4e0d\u4f1a\u592a\u5c0f\u6c14\u91cf\u7a84\u5427\uff1f\u201d \u5c55\u767d\u6bc5\u7136\u5411\u5a49\u513f\u53ca\u201c\u51cc\u98ce\u516c\u5b50\u201d\u4f38\u51fa\u624b\u6765\uff01 \u96f7\u5927\u53d4\u53c8\u8f6c\u5934\u5bf9\u201c\u51cc\u98ce\u516c\u5b50\u201d\u9053\uff1a\u201c\u8d24\u4e16\u4f84\uff01\u770b\u4f60\u7684\u4e86\uff1f\u201d \u201c\u51cc\u98ce\u516c\u5b50\u201d\u8138\u4e00\u7ea2\uff0c\u4e5f\u4f38\u51fa\u624b\u6765\u4e0e\u5c55\u767d\u63e1\u5728\u4e00\u8d77\uff0c\u5e76\u9053\uff1a\u201c\u5c55\u5144\u90fd\u80fd\u653e\u8fc7\u4e86\uff01\u5c0f\u4f84\u8fd8 \u6709\u4ec0\u4e48\u8bdd\u8bf4\u3002\u201d \u5a49\u513f\u65e9\u5df3\u559c\u6781\u800c\u6ce3\uff0c\u63e1\u4f4f\u5c55\u767d\u7684\u53e6\u4e00\u53ea\u624b\u7d27\u7d27\u4e0d\u653e\uff0c\u82e5\u4e0d\u662f\u5c4b\u4e2d\u4eba\u591a\uff0c\u6050\u6015\u5979\u65e9\u5df2\u6295\u8fdb \u5c55\u54e5\u54e5\u7684\u6000\u62b1\u4e86\uff01 \u8339\u8001\u6e90\u5934\u53ca\u4f17\u9563\u5e08\uff0c\u89c1\u96f7\u5927\u53d4\u4e09\u8a00\u4e8c\u8bed\uff0c\u628a\u4e24\u5bb6\u8840\u4ec7\u89e3\u5f00\uff0c\u5316\u5e72\u6208\u4e3a\u7389\u5e1b\uff0c\u7eb7\u7eb7\u4e0a\u524d\u81f4 \u8d3a\u3002 \u96f7\u5927\u53d4\u66f4\u662f\u5f00\u5fc3\u5f97\u4ef0\u5929\u5927\u7b11\u8d77\u6765\uff01 \u4f46\u96f7\u5927\u53d4\u7b11\u7740\u7b11\u7740\uff0c\u5ffd\u7136\u53cc\u76ee\u5782\u6cea\uff0c\u53c8\u9e23\u9e23\u54ed\u4e86\uff01\u770b\u6837\u5b50\u7adf\u662f\u5f88\u4f24\u5fc3\u3002 \u96f7\u5927\u53d4\u8fd9\u7a81\u7136\u7684\u8f6c\u53d8\uff0c\u4f7f\u4f17\u4eba\u5747\u81ea\u4e00\u6115\u3002 \u6b64\u65f6\uff0c\u6155\u5bb9\u592b\u4eba\u5b89\u629a\u4e86\u5fe0\u5fc3\u7684\u95e8\u5ba2\uff0c\u53c8\u56de\u8fdb\u623f\u4e2d\u6765\uff0c\u5728\u5ba4\u5916\u5c31\u542c\u5230\u96f7\u5927\u53d4\u5982\u96f7\u7684\u7b11\u58f0\uff0c \u4f46\u8fdb\u5230\u5c4b\u4e2d\u89c1\u96f7\u5927\u53d4\u545c\u545c\u75db\u54ed\uff0c\u4e0d\u7531\u8be7\u5f02\u5730\u95ee\u9053\uff1a\u201c\u96f7\u5144\u5f1f\uff01\u6709\u4eba\u620f\u8a00\uff0c\u7ba1\u4f60\u53eb\u96f7\u75af\u5b50\uff01\u83ab \u975e\u5f53\u771f\u75af\u4e86\uff0c\u4e3a\u4ec0\u4e48\u90a3\u4e48\u5927\u5e74\u7eaa\u4e86\uff0c\u8fd8\u53c8\u54ed\u53c8\u7b11\u7684\uff1f\u2026\u2026\u201d \u96f7\u5927\u53d4\u62ac\u8d77\u6cea\u8138\uff0c\u968f\u628a\u5c55\u81ea\u4e4b\u7236\u4e0e\u6155\u5bb9\u5e84\u4e3b\u7684\u4e00\u6bb5\u6069\u6028\u5bf9\u6155\u5bb9\u592b\u4eba\u8bf4\u4e86\uff0c\u6700\u540e\u9053\uff1a\u201c\u5ac2 \u5b501\u4f60\u5f85\u6211\u5982\u4eb2\u5f1f\uff0c\u5144\u5f1f\u4e0e\u5c55\u8d24\u4f84\u4e4b\u7236\u5374\u662f\u520e\u9888\u4e4b\u4ea4\uff0c\u4e3a\u4f60\u4eec\u4e24\u5bb6\u628a\u8840\u4ec7\u5316\u89e3\u4e86\uff0c\u6211\u7109\u80fd\u4e0d \u7b11\uff1f\u53ef\u662f\uff0c\u6211\u53bb\u7684\u4e91\u5929\u5144\u62a5\u4ec7\uff0c\u53c8\u7109\u80fd\u4e0d\u54ed\uff1f\u201d \u96f7\u5927\u53d4\u8fd9\u4e00\u8bf4\uff0c\u6155\u5bb9\u592b\u4eba\u7a81\u7136\u62b1\u4f4f\u5c55\u767d\uff0c\u653e\u58f0\u60b2\u54ed\u8d77\u6765\uff01 \u8fd9\u4e00\u6765\uff0c\u628a\u96f7\u5927\u53d4\u4e5f\u95f9\u50bb\u4e86\uff0c\u6781\u529b\u628a\u6155\u5bb9\u592b\u4eba\u529d\u4f4f\uff0c\u9053\uff1a\u201c\u5ac2\u5b50\u521a\u624d\u4f60\u8bf4\u5144\u5f1f\u662f\u75af\u5b50\uff0c \u83ab\u975e\u4f60\u73b0\u5728\u4e5f\u75af\u4e86\uff0c\u4e3a\u4ec0\u4e48\u4e5f\u54ed\u8d77\u6765\uff1f\u201d \u6155\u5bb9\u592b\u4eba\u5fcd\u4f4f\u60b2\u58f0\u9053\uff1a\u201c\u6211\u4ece\u4e0d\u77e5\u9053\u90a3\u5929\u6740\u7684\u505a\u51fa\u8fd9\u7b49\u4e8b\u6765\uff01\u5c55\u5c0f\u4fa0\u8fd8\u662f\u6211\u5c55\u5bb6\u7684\u4f84\u5b50 \u5462\uff01\u201d \u539f\u6765\u5e55\u5bb9\u592b\u4eba\u5a18\u5bb6\u59d3\u5c55\uff0c\u6b63\u662f\u201c\u9739\u96f7\u5251\u201d\u5c55\u4e91\u5929\u7684\u4e00\u4e2a\u4eb2\u53d9\u4f2f\u5802\u59b9\uff0c\u7b97\u8d77\u6765\u4e24\u5bb6\u8fd8\u662f\u59d1 \u8868\u4e4b\u4eb2\uff01 \u8fd9\u4e00\u6765\uff0c\u4e24\u5bb6\u53c8\u8fd1\u4e86\u4e00\u5c42\uff0c\u6155\u5bb9\u592b\u4eba\u81ea\u4e0d\u5154\u62c9\u4f4f\u5c55\u767d\u7684\u624b\u7d6e\u8bdd\u8d77\u5bb6\u5e38\u6765\u2026\u2026 \u6b64\u65f6\uff0c\u8339\u8001\u9556\u5934\u611f\u6982\u8a00\u9053\uff1a\u201c\u6c5f\u6e56\u4e0a\u6069\u6028\u60c5\u4ec7\uff0c\u5b9e\u5728\u83ab\u6d4b\uff0c\u6709\u65f6\u4eb2\u8005\u6709\u4ec7\uff0c\u6709\u65f6\u4ec7\u8005\u6210 \u4eb2\u2026\u2026\u201d \u7a81\u7136\uff0c\u8339\u8001\u9556\u5934\u60f3\u8d77\u4e00\u4e8b\uff0c\u5411\u6155\u5bb9\u592b\u4eba\u9053\uff1a\u201c\u524d\u5929\u665a\u4e0a\uff0c\u6211\u95ef\u8fdb\u5e9c\u4e2d\uff0c\u8bef\u81f3\u4e00\u5ea7\u5927\u82b1 \u56ed\uff0c\u82b1\u56ed\u697c\u623f\u4e2d\u4f3c\u4e4e\u56da\u4f73\u4e00\u4e2a\u5185\u7737\uff0c\u4e0d\u77e5\u90a3\u662f\u5e9c\u4e2d\u7684\u4f55\u4eba\uff1f\u4e3a\u4ec0\u4e48\u53c8\u88ab\u56da\u5728\u82b1\u56ed\u2026\u2026\u201d \u8339\u8001\u9556\u5934\u8bdd\u672a\u8bf4\u5b8c\uff0c\u5a49\u513f\u5df2\u5c16\u53eb\u8d77\u6765\uff0c\u9053\uff1a\u201c\u54ce\u5440\uff01\u90a3\u662f\u6211\u59d0\u59d0\uff01\u201d \u6155\u5bb9\u592b\u4eba\u4e5f\u6025\u9053\uff1a\u201c\u662f\u7ea2\u513f\uff01\u4e5f\u662f\u88ab\u90a3\u5929\u6740\u7684\u6b7b\u9b3c\u5173\u8d77\u6765\u7684\uff01\u82e5\u4e0d\u662f\u8339\u8001\u9556\u5934\u63d0\u8d77\uff0c\u4e00 \u65f6\u4e4b\u95f4\u6211\u8fd8\u5fd8\u4e86\uff0c\u5feb\uff01\u6211\u4eec\u5feb\u53bb\u628a\u5979\u653e\u51fa\u6765\uff01\u4e0d\u77e5\u90a3\u5b69\u5b50\u53d7\u4e86\u591a\u5c11\u82e6\uff1f\u2026\u2026\u201d \u8bf4\u7f62\uff0c\u5f53\u5148\u5411\u95e8\u5916\u8d70\u53bb1 \u5a49\u513f\u3001\u5c55\u767d\u3001\u96f7\u5927\u53d4\u3001\u8339\u8001\u9556\u5934\u7b49\u4eba\uff0c\u968f\u540e\u8ddf\u4e86\u51fa\u6765\uff0c\u51e0\u4eba\u8eab\u6cd5\u90fd\u5feb\u5f97\u51fa\u5947\uff0c\u8f6c\u773c\u6765\u81f3 \u82b1\u56ed\uff0c\u53ea\u89c1\u4e00\u5ea7\u53e4\u8272\u53e4\u9999\u7684\u697c\u623f\uff0c\u697c\u95e8\u6df1\u9501\uff0c\u82b1\u53f6\u63a9\u6620\uff01 \u90a3\u9762\u8272\u82cd\u767d\u7684\u6155\u5bb9\u7ea2\uff0c\u6b63\u5c06\u8138\u8d34\u5728\u51b0\u51b7\u7684\u94c1\u680f\u4e0a\u4f4e\u541f\u7740\u5ff5\u4e86\u5343\u767e\u904d\u7684\u201c\u957f\u76f8\u601d\u201d\u7684\u53e4 \u8bd7\u3002 \u53ea\u4e9a\u5979\u5e7d\u5e7d\u5730\u5ff5\u9053\uff1a \u201c\u957f\u76f8\u601d\uff0c\u6467\u5fc3\u809d\u3002 \u7edc\u7eac\u79cb\u8e44\u91d1\u4e95\u680f\u3002 \u5fc6\u541b\u8fe2\u8fe2\u9694\u9752\u5929\uff0e \u5929\u957f\u8def\u8fdc\u9b42\u98de\u82e6\u3002 \u6bcd\u5973\u8fde\u5fc3\uff0c\u6155\u5bb9\u592b\u4eba\u4e00\u89c1\u5973\u513f\u8fd9\u822c\u60e8\u72b6\uff0c\u4e0d\u7531\u54ed\u53eb\u4e86\u4e00\u58f0\uff1a\u201c\u7ea2\u513f\uff01\u5988\u6765\u6551\u4f60\u2026\u2026\u201d \u5c55\u767d\u5df2\u98de\u8eab\u63a0\u81f3\u95e8\u524d\uff0c\u5355\u638c\u4e00\u6325\uff0c\u201c\u9526\u201d\u4e00\u58f0\uff0c\u628a\u4e00\u53ea\u7279\u5927\u7684\u94c1\u9501\u5288\u788e\uff0c\u6253\u5f00\u4e86\u697c\u95e81 \u6155\u5bb9\u7ea2\u51e0\u7591\u8eab\u5728\u68a6\u4e2d\uff0c\u4e24\u53ea\u5931\u795e\u7684\u5927\u773c\u775b\u6014\u671b\u4e86\u5c55\u767d\u597d\u5927\u4e00\u4f1a\uff0c\u624d\u731b\u7136\u6251\u8fdb\u5c55\u767d\u6000\u4e2d\uff0c \u7ade\u5624\u5624\u7f00\u6ce3\u8d77\u6765\uff01 \u4f17\u4eba\u89c1\u5979\u957f\u53d1\u6563\u4e71\uff0c\u7626\u5982\u9ec4\u82b1\uff0c\u4f0f\u5728\u5c55\u767d\u6000\u4e2d\u53cc\u80a9\u4e0d\u4f4f\u5730\u72ec\u6410\u60b2\u54ed\uff0c\u4e0d\u5c11\u4eba\u843d\u4e0b\u540c\u60c5\u7684 \u773c\u6cea\uff01 \u89c9\u5f97\u201c\u6458\u661f\u624b\u201d\u6155\u5bb9\u6db5\uff0c\u5bf9\u81ea\u5df1\u4eb2\u751f\u5973\u513f\uff0c\u5c1a\u4e14\u5982\u6b64\u8650\u5f85\uff0c\u83ab\u4e0d\u89c9\u5176\u6b7b\u6709\u4f59\u8f9c\uff01 \u6155\u5bb9\u592b\u4eba\u89c1\u5973\u513f\u4e0d\u6295\u5954\u81ea\u5df2\uff0c\u53cd\u800c\u6295\u8fdb\u5c55\u767d\u6000\u4e2d\uff0c\u4e5f\u89c9\u5f97\u81ea\u5df2\u592a\u5112\u5f31\u4e86\uff0c\u5f53\u521d\u672a\u8ddf\u4e08\u592b \u529b\u4e89\uff0c\u4ee5\u81f4\u4f7f\u5973\u513f\u53d7\u8fd9\u975e\u4eba\u7684\u75db\u82e6\uff0c\u4f46\u5973\u513f\u7a76\u7adf\u662f\u4e91\u82f1\u672a\u5ac1\uff0c\u8001\u5728\u4e00\u4e2a\u5927\u7537\u4eba\u7684\u6000\u4e2d\u54ed\u6ce3\uff0c \u4e5f\u4e0d\u96c5\u89c2\uff0c\u968f\u6781\u529b\u628a\u5973\u513f\u62c9\u5f00\uff0c\u9053\uff1a\u201c\u5b69\u5b50\uff01\u90fd\u662f\u5988\u4e0d\u597d\uff0c\u624d\u4f7f\u4f60\u88ab\u7239\u7239\u5173\u8fd9\u6837\u4e45\u2026\u2026\u201d \u6155\u5bb9\u7ea2\u53c8\u53cd\u8eab\u6295\u8fdb\u6bcd\u4eb2\u7684\u6000\u4e2d\uff0c\u60b2\u54ed\u8d77\u6765\uff01\u5979\u5b9e\u5728\u662f\u53d7\u7684\u75db\u82e6\u592a\u591a\u4e86\u2026\u2026 \u7ecf\u4f17\u4eba\u767e\u822c\u529d\u89e3\uff0c\u6155\u5bb9\u7ea2\u624d\u6e10\u6e10\u6536\u4f4f\u60b2\u58f0\uff0c\u7531\u6155\u5bb9\u592b\u4eba\u53ca\u5a49\u513f\u6400\u7740\u56de\u81f3\u524d\u5385\uff0c\u6155\u5bb9\u592b\u4eba \u547d\u5a49\u513f\u966a\u4f34\u59d0\u59d0\u53bb\u6c90\u6d74\u66f4\u8863\uff0c\u5374\u53c8\u53eb\u96f7\u5927\u53d4\u5728\u4e00\u8fb9\u7a83\u7a83\u601d\u8bae\u4e86\u8bb8\u4e45\u3002 \u96f7\u5927\u53d4\u6ee1\u53e3\u5e94\u627f\uff0c\u76f4\u62cd\u80f8\u812f\uff0c\u4e4b\u540e\u5373\u5bf9\u5c55\u767d\u8bf4\u4e86\u3002 \u539f\u6765\u6155\u5bb9\u592b\u4eba\u662f\u62dc\u6258\u96f7\u5927\u53d4\uff0c\u66ff\u5973\u513f\u505a\u5a92\u7ed9\u5c55\u767d\u8bf4\u4eb2\u7684\u3002\u56e0\u4e3a\u6155\u5bb9\u592b\u4eba\u65e9\u77e5\u5973\u513f\u5fc3\u4e8b\uff0c \u53c8\u89c1\u6251\u8fdb\u5c55\u767d\u6000\u4e2d\u4e00\u54ed\uff0c\u89c9\u5f97\u5973\u513f\u5df2\u662f\u975e\u5c55\u767d\u83ab\u5ac1\uff0c\u6545\u6b64\u65b9\u627e\u96f7\u5927\u53d4\u6210\u5168\u3002 \u7ecf\u96f7\u5927\u53d4\u4e00\u8bf4\uff0c\u5c55\u767d\u89c9\u5f97\u5b9e\u5728\u662f\u65e0\u53ef\u63a8\u6258\u7684\u4f59\u5730\uff0c\u5148\u6709\u4e00\u6bb5\u5f53\u4f17\u88f8\u4f53\u76f8\u5c31\u7684\u4e8b\u7aef\uff0c\u53c8\u6709 \u8fd9\u6b21\u6000\u4e2d\u60b2\u54ed\u7684\u4e8b\u60c5\uff0c\u540c\u65f6\u89c9\u5f97\u6155\u5bb9\u7ea2\u60c5\u6709\u72ec\u949f\uff0c\u81ea\u5df1\u4e5f\u4e0d\u5fcd\u5fc3\u62d2\u7edd\u4e00\u4e2a\u75f4\u5fc3\u5c11\u5973\u7684\u7231\uff0c\u52a0 \u4e4b\uff0c\u521a\u4e0e\u67f3\u7fe0\u7fe0\u95f9\u7ffb\uff0c\u5fc3\u4e2d\u4ecd\u5728\u8d4c\u6c14\uff0c\u5f53\u7136\u96f7\u5927\u53d4\u7684\u9762\u5b50\uff0c\u4ed6\u4e5f\u4e0d\u597d\u62e8\u56de\uff0c\u56e0\u6b64\uff0c\u4fbf\u70b9\u5934\u7b54 \u5e94\u4e86\u3002 \u6ca1\u60f3\u5230\u5c55\u767d\u6b64\u4e3e\uff0c\u5374\u4f24\u4e86\u53e6\u4e00\u4e2a\u5c11\u5973\u7684\u5fc3\u3002 \u90a3\u4fbf\u662f\u5c55\u5a49\u513f\uff01 \u5c55\u5a49\u513f\u966a\u7740\u59d0\u59d0\u6c90\u6d74\u66f4\u8863\uff0c\u5230\u524d\u5385\u5f97\u77e5\u8fd9\u4e2a\u559c\u8baf\uff0c\u59d0\u59d0\u5df2\u6210\u4e86\u5979\u5fc3\u76ee\u4e2d\u552f\u4e00\u7684\u60c5\u90ce\u7684\u672a \u5a5a\u59bb\uff01 \u5a49\u513f\u82b3\u5fc3\u5bf8\u65ad\uff0c\u6084\u6084\u51fa\u8d70\u4e86p \u5979\u65e2\u4e0d\u80fd\u4e0e\u59d0\u59d0\u4e89\uff0c\u53c8\u4e0d\u5fcd\u89c1\u8fd9\u65ad\u80a0\u7684\u559c\u4e8b\uff0c\u5c31\u7b97\u6b89\u60c5\u4e00\u6b7b\u5728\u5bb6\uff0c\u4e5f\u5f92\u7ed9\u6bcd\u4eb2\u6dfb\u70e6\uff0c\u601d \u6765\u60f3\u53bb\u53ea\u6709\u4e00\u5b9a\u4e86\u4e4b\uff0e \u5f85\u6155\u5bb9\u5e9c\u4e3a\u5e86\u4e3b\u529e\u5b8c\u4e86\u4e27\u4e8b\uff0c\u5c55\u767d\u4e0e\u6155\u5bb9\u7ea2\u7684\u559c\u4e8b\u63a5\u8e35\u800c\u5230\uff0c\u5fd9\u5f97\u4e00\u56e2\u7cdf\u7684\u65f6\u5019\uff0c\u53d1\u73b0 \u5a49\u513f\u5931\u8e2a\uff0c\u4f17\u4eba\u90fd\u731c\u4e0d\u900f\u5a49\u513f\u56e0\u4f55\u51fa\u8d70\uff1f \u53ea\u6709\u5c55\u767d\u5fc3\u4e2d\u660e\u767d\uff01 \u90a3\u75f4\u60c5\u7684\u5c11\u5973\uff0c\u662f\u4e3a\u4ed6\u79bb\u5bb6\u51fa\u8d70\u7684\uff01 \u5a49\u513f\u66fe\u6709\u6570\u6b21\u820d\u6b7b\u5fd8\u751f\u5730\u6551\u4ed6\uff0c\u5a49\u513f\u4ec5\u662f\u4e00\u4e2a\u5929\u771f\u672a\u51ff\u7684\u5a07\u61a8\u5c11\u5973\uff0c\u6beb\u65e0\u6c5f\u6e56\u7ecf\u9a8c\uff0c\u72ec \u81ea\u4e00\u4eba\u6d6a\u8ff9\u6c5f\u6e56\uff0c\u7684\u786e\u4f7f\u4eba\u62c5\u5fe7\uff0c\u5c55\u767d\u4e43\u662f\u4e00\u8840\u6027\u7537\u513f\uff0c\u5728\u8fd9\u79cd\u60c5\u51b5\u4e0b\uff0c\u600e\u80fd\u5b89\u4e0b\u5fc3\u6765\u5ea6\u81ea \u5df1\u7684\u82b1\u6708\u826f\u5bb5\uff0c\u65b0\u5a5a\u871c\u6708\uff1f \u56e0\u6b64\uff0c\u5c55\u767d\u8ddf\u6155\u5bb9\u7ea2\u5546\u91cf\uff0c\u5a5a\u671f\u540e\u5ef6\uff0c\u53c8\u5bdf\u660e\u4e86\u6155\u5bb9\u592b\u4eba\u4e0e\u96f7\u5927\u53d4\uff0c\u51b3\u5fc3\u51fa\u5916\u53bb\u627e\u5a49\u513f 1 \u8339\u8001\u9556\u5934\u8981\u7ee7\u7eed\u5bfb\u627e\u5931\u9556\uff0c\u56e0\u4e3a\u201c\u5357\u6d77\u95e8\u201d\u5728\u201c\u8c79\u7a81\u5c71\u5e84\u201d\u64a4\u8d70\u65f6\uff0c\u628a\u8d35\u91cd\u7269\u54c1\u53ca\u9556\u8f66 \u9556\u94f6\u90fd\u5e26\u8d70\u4e86\uff0c\u96f7\u5927\u53d4\u4e0d\u653e\u5fc3\u5c55\u767d\uff0e\u4e0e\u6155\u5bb9\u7ea2\u8fdc\u884c\uff0c\u56e0\u6b64\uff0c\u56db\u4eba\u8054\u6289\u79bb\u5bb6\uff01 \u56db\u4eba\u5728\u9a6c\u5ed3\u4e2d\u6311\u9009\u4e86\u56db\u5339\u5065\u9a6c\uff0c\u56db\u4eba\u56db\u9a91\uff0c\u51fa\u4e86\u201c\u8c79\u7a81\u5c71\u5e84\u201d\u3002\u4f46\u5929\u4e0b\u4e4b\u5927\uff0c\u8981\u60f3\u5728\u832b \u832b\u4eba\u6d77\u4e2d\u53bb\u627e\u4e00\u4e2a\u4eba\uff0c\u4f55\u5f02\u5927\u6d77\u635e\u9488\uff0c\u5b9e\u975e\u5bb9\u6613\uff01 \u636e\u5c55\u767d\u5224\u65ad\uff0c\u5a49\u513f\u522b\u5904\u8def\u5f84\u4e0d\u719f\uff0c\u53ea\u53bb\u8fc7\u5357\u4eac\u4e00\u8d9f\uff0c\u800c\u4e14\u5357\u4eac\u9644\u8fd1\uff0c\u5ca9\u5c71\u5341\u4e8c\u6d1e\u4e00\u5e26\uff0c \u6709\u4e00\u5904\u9c9c\u4e3a\u4eba\u77e5\u7684\u79d8\u5883\uff0c\u53eb\u505a\u201c\u4ea1\u9b42\u8c37\u201d\uff0c\u5a49\u513f\u66fe\u968f\u7740\u201c\u795e\u7334\u201d\u94c1\u51cc\u5b66\u827a\u65f6\u53bb\u8fc7\uff0c\u8bf4\u4e0d\u5b9a\u662f \u8dd1\u5230\u201c\u4ea1\u9b42\u8c37\u201d\u85cf\u8d77\u6765\u4e86\uff0c\u96f7\u5927\u53d4\u4e00\u542c\u6709\u7406\uff0c\u52a0\u4e4b\u8339\u8001\u9556\u5934\u5e76\u6ca1\u6709\u4e00\u5b9a\u7684\u76ee\u7684\u5730\uff0c\u53ea\u6709\u5230\u5904 \u67e5\u8bbf\uff0c\u8d70\u5230\u54ea\u91cc\u7b97\u54ea\u91cc\uff0c\u56e0\u6b64\uff0c\u56db\u4eba\u76f4\u5954\u5357\u4eac\u800c\u6765\uff01 \u56db\u4eba\u7531\u6d4e\u5357\u5954\u5357\u4eac\uff0c\u987b\u7ecf\u8fc7\u82cf\u3001\u9c81\u4e24\u7701\u8fb9\u5883\uff0c\u4e00\u8def\u4e0a\u5df2\u968f\u5904\u53ef\u53d1\u73b0\u6709\u7740\u201c\u5357\u6d77\u95e8\u201d\u6807\u5e1c \u4e4b\u4eba\uff01 \u800c\u4e14\uff0c\u6c5f\u6e56\u4e0a\u53c8\u6709\u65b0\u6d41\u884c\u7684\u56db\u53e5\u6b4c\u8c23\uff0e \u8857\u5934\u5df7\u5c3e\uff0c\u8fde\u5c0f\u513f\u90fd\u4f1a\u5531\u4e86\u3002 \u90a3\u56db\u53e5\u6b4c\u8c23\u662f\u8fd9\u6837\u7684\uff1a \u201c\u5b89\u4e50\u516c\u5b50\u4e0d\u98ce\u6d41\u3002 \u7965\u9e9f\u516c\u5b50\u4e0d\u70ed\u80a0\uff0c \u98d8\u96f6\u7aef\u65b9\u6709\u4e86\u4e3b\uff0c \u5357\u6d77\u795e\u9f99\u72ec\u4e3a\u5c0a\uff01\u2606\uff0c \u663e\u89c1\u8fd9\u56db\u53e5\u6b4c\u8c23\uff0c\u662f\u9488\u5bf9\u4ece\u524d\u201c\u6b66\u6797\u56db\u516c\u5b50\u201d\u5f97\u52bf\u65f6\uff0c\u90a3\u56db\u53e5\u201c\u5b89\u4e50\u98ce\u6d41\uff0c\u7965\u9e9f\u70ed\u80a0\uff0c \u98d8\u96f6\u7aef\u65b9\uff0c\u51cc\u98ce\u65e0\u60c5\u201d\u800c\u53d1\u7684\u3002 \u4e0d\u8fc7\uff0c\u5c1a\u672a\u628a\u201c\u51cc\u98ce\u516c\u5b50\u201d\u7b97\u5728\u5176\u5185\u3002 \u4f46\u7531\u6b64\u4e5f\u53ef\u89c1\u5f53\u524d\u6b66\u6797\u4e2d\uff0c\u56db\u5927\u8c6a\u95e8\u5df2\u7136\u5f0f\u5fae\uff0c\u4ee3\u4e4b\u800c\u8d77\u3001\u79f0\u96c4\u6b66\u6797\u7684\u5df2\u7ecf\u662f\u201c\u5357\u6d77 \u95e8\u201d\u7684\u201c\u9f99\u795e\u592a\u5b50\u201d\u4e86\u3002 \u53ea\u770b\u90a3\u201c\u5357\u6d77\u795e\u9f99\u72ec\u4e3a\u5c0a\u201d\u4e00\u53e5\uff0c\u4fbf\u77e5\u6897\u6982\u4e86\u3002 \u8fd9\u4e00\u65e5\uff0c\u56db\u4eba\u56db\u9a91\u8d2a\u8d76\u8def\u7a0b\uff0c\u5929\u5df2\u9ec4\u660f\uff0c\u4ecd\u672a\u5b9a\u8fd1\u4e00\u4e2a\u5e02\u9547\u3002 \u56db\u4eba\u5728\u65f7\u91ce\u4e2d\uff0c\u6b63\u5728\u50ac\u9a6c\u7d27\u8d70\uff0c\u7a81\u7136\u89c1\u8fdc\u5904\u5c71\u4e0a\u5347\u8d77\u4e00\u7247\u706b\u5149\uff01 \u66ae\u8272\u82cd\u832b\uff0c\u90a3\u706b\u5149\u5728\u534a\u5c71\u8170\u91cc\u71c3\u70e7\u8d77\u6765\uff0c\u663e\u5f97\u5206\u5916\u523a\u773c\uff01 \u706b\u52bf\u71c3\u70e7\u6781\u5feb\uff0c\u4eae\u5149\u4e00\u95ea\uff0c\u706b\u52bf\u817e\u7a7a\u800c\u8d77\uff0c\u52a0\u4e4b\u6709\u4e00\u70b9\u665a\u98ce\uff0c\u98ce\u52a9\u706b\u52bf\uff0c\u706b\u4ed7\u98ce\u5a01\uff0c\u60f3 \u5fc5\u90a3\u5c71\u4e0a\u6811\u6728\u53c8\u591a\uff0c\u5019\u65f6\u95f4\u8513\u5ef6\u4e86\u5927\u534a\u4e2a\u5c71\u5934\uff0c\u8fdc\u8fdc\u671b\u53bb\uff0c\u7ade\u5982\u4e00\u6761\u706b\u9f99\u76d8\u4f0f\u5728\u5c71\u8170\u91cc\u4e00 \u822c\uff01 \u5c55\u767d\u7565\u4e00\u5ba1\u5ea6\u65b9\u5411\uff0c\u7a81\u7136\u5728\u9a6c\u4e0a\u60ca\u53eb\u9053\uff1a\u201c\u4e0d\u597d\uff01\u90a3\u706b\u70e7\u7684\u5730\u65b9\uff0c\u662f\u5148\u7236\u4e24\u4f4d\u6545\u53cb\u9690\u5c45 \u4e4b\u5904\uff0c\u4e0d\u77e5\u600e\u4f1a\u8d77\u8fd9\u6837\u5927\u706b\uff1f\u201d \u96f7\u5927\u53d4\u52d2\u4f0e\u5ea7\u9a6c\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u7236\u4eb2\u6545\u4ea4\uff0c\u6211\u591a\u534a\u8ba4\u8bc6\uff0c\u5728\u90a3\u5c71\u4e0a\u9690\u5c45\u7684\u4e0d\u77e5\u662f\u54ea\u4e24\u4f4d\u9ad8 \u4eba\uff1f\u201d \u5c55\u767d\u9053\uff1a\u201d\u6d3b\u6b7b\u4eba\u2019\uff0c\u2018\u6b7b\u6d3b\u4eba\u2019\uff01\u201d \u96f7\u5927\u53d4\u4e00\u695e\uff0c\u9053\uff1a\u201c\u597d\u602a\u7684\u540d\u5b57\uff0c\u4ece\u672a\u542c\u8bf4\u8fc7\uff01\u201d \u5c55\u81ea\u9053\uff1a\u201c\u90a3\u662f\u4ed6\u4fe9\u7684\u5316\u540d\u3002\u201d \u63a5\u7740\u628a\u4e8c\u4eba\u8863\u7740\u5f62\u72b6\uff0c\u6b66\u529f\u8def\u6570\uff0c\u9010\u4e00\u5f62\u5bb9\u7ed9\u96f7\u5927\u53d4\u542c\u3002 \u96f7\u5927\u53d4\u4e00\u62cd\u5927\u817f\u9053\uff1a\u201c\u592a\u767d\u53cc\u9038\u20191\u90a3\u4e00\u5b9a\u662f\u4ed6\u4eec\u5144\u5f1f\u4e8c\u4eba\u4e86\uff0c\u56e0\u4e3a\u2018\u592a\u6781\u4e24\u4eea\u79bb\u9b42 \u638c\u2019\u522b\u4eba\u4e0d\u4f1a\uff0c\u5929\u4e0b\u6b66\u6797\uff0c\u53ea\u6709\u4ed6\u5144\u5f1f\u4e8c\u4eba\u4f1a\u6b64\u7edd\u5b66\u3002\u8d70\uff01\u54b1\u4eec\u77a7\u77a7\u53bb\uff01\u201d \u8bf4\u7f62\uff0c\u5f53\u5148\u7b56\u9a6c\uff0c\u5411\u90a3\u7740\u706b\u7684\u5c71\u5cf0\u4e0a\u8dd1\u53bb\uff01 \u5c55\u767d\u3001\u6155\u5bb9\u7ea2\u3001\u8339\u8001\u9556\u5934\u968f\u540e\u7b56\u9a6c\u8ddf\u4e0a\u3002 \u56db\u4eba\u56db\u9a91\uff0c\u98ce\u9a70\u7535\u63a3\uff0c\u767b\u5c71\u8d8a\u5cad\uff0c\u8f6c\u773c\u6765\u81f3\u8fd1\u5904\uff0c\u53ea\u89c1\u718a\u718a\u706b\u5149\u4e2d\uff0c\u6b63\u6709\u5341\u6570\u58ee\u6c49\uff0c\u56f4 \u7740\u4e24\u6761\u767d\u8272\u4eba\u5f71\u53ae\u6740\uff01 \u706b\u5149\u7167\u6620\u4e4b\u4e2d\uff0c\u5c55\u81ea\u8001\u8fdc\u770b\u89c1\uff0c\u5728\u5341\u6570\u58ee\u6c49\u56f4\u56f0\u4e2d\uff0c\u4ee5\u4e24\u53cc\u8089\u638c\u594b\u547d\u82e6\u6218\u7684\u6b63\u662f\u201c\u6b7b\u6d3b \u4eba\u201d\u4e0e\u201c\u6d3b\u6b7b\u4eba\u201d\uff01 \u53e6\u6709\u4e00\u9752\u8863\u5c11\u5e74\uff0c\u4e5f\u4e0e\u201c\u592a\u767d\u53cc\u9038\u201d\u534f\u529b\u4f5c\u6218\uff0c\u5c55\u767d\u8ba4\u51fa\u90a3\u9752\u8863\u5c11\u5e74\u6b63\u662f\u201c\u7965\u9e9f\u516c \u5b50\u201d\uff0c\u5fc3\u4e2d\u4e0d\u7531\u6697\u6697\u7eb3\u7f55\uff1a\u201c\u7965\u9e9f\u516c\u5b50\u201d\u600e\u4f1a\u5230\u4e86\u6b64\u5730\uff1f \u53c8\u600e\u4f1a\u8ddf\u201c\u592a\u767d\u53cc\u9038\u201d\u5408\u529b\u62d2\u654c\uff1f\u2026\u2026 \u90a3\u201c\u6b7b\u4eba\u5c45\u201d\u7684\u5947\u5f62\u5c0f\u697c\uff0c\u5374\u5df2\u9677\u4e8e\u706b\u6d77\u4e4b\u4e2d\uff01 \u5c55\u767d\u9a6c\u4e0a\u5927\u53eb\u9053\uff1a\u201c\u8001\u524d\u8f88\u52ff\u614c\uff01\u6211\u6765\u52a9\u4f60\uff01\u2026\u2026\u201d \u559d\u7f62\u98de\u8eab\u63a0\u4eba\u6218\u5708\uff0c\u53cc\u638c\u7ffb\u98de\uff0c\u201c\u562d\uff01\u562d\uff01\u201d\u63a5\u8fde\u9707\u9000\u4e8c\u4eba\u2026\u2026 \u96f7\u5927\u53d4\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u592a\u767d\u53cc\u9038\u2019\uff01\u4f60\u4eec\u5f1f\u5144\u4e00\u85cf\u5341\u51e0\u5e74\uff0c\u8ddf\u8001\u592b\u907f\u4e0d\u89c1\u9762\uff0c\u5982\u4eca\u8ba9\u4eba \u5bb6\u4fa5\u4e86\u5154\u5b50\u7a9d\u5566\u4e86\uff0c\u53ef\u85cf\u4e0d\u4f4f\u4e86\u5427\uff1f\u2026\u2026\u201d \u559d\u7b11\u58f0\u4e2d\uff0c\u96f7\u5927\u53d4\u98de\u8eab\u4e00\u63a0\uff0c\u5982\u4e00\u53ea\u5927\u9e70\u822c\uff0c\u4ece\u9a6c\u4e0a\u76f4\u5411\u5708\u6597\u573a\u4e2d\u63a0\u53bb\uff01"}),showOnHomepage:!0},{name:"\u7b2c\u56db\u5341\u4e03\u7ae0\u3000\u8c03\u864e\u79bb\u5c71",content:(0,I.jsx)(I.Fragment,{children:'\u5ffd\u542c\u4e00\u58f0\u8c6a\u7b11\uff0c\u6fc0\u6597\u573a\u4e2d\u4eba\u5f71\u4e00\u5206\uff0c\u95ea\u51fa\u4e00\u4eba\uff0c\u621f\u6307\u96f7\u5927\u53d4\u5389\u53f1\u9053\uff1a\u201c\u96f7\u75af\u5b50\uff01\u60f3\u4e0d\u5230 \u2018\u8c79\u7a81\u5c71\u5e84\u2019\u672a\u80fd\u8ba9\u4f60\u5200\u4e0b\u4ea1\u9b42\uff1f\u4eca\u591c\u5728\u6b64\u76f8\u89c1\uff0c\u6050\u6015\u4f60\u518d\u4e5f\u96be\u9003\u6d3b\u547d1\u201d \u96f7\u5927\u53d4\u5389\u76ee\u5982\u7535\uff0c\u4e00\u6253\u91cf\u6765\u4eba\uff0c\u6b63\u662f\u72ec\u81c2\u79c3\u9876\u3001\u9762\u76ee\u9634\u68ee\u7684\u201c\u8ffd\u9b42\u94c3\u201d\u53f8\u9a6c\u656c\uff01 \u96f7\u5927\u53d4\u5fcd\u4e0d\u4f4f\u4ef0\u5929\u5927\u7b11\u9053\uff1a\u201c\u6211\u8bf4\u662f\u8c01\uff0c\u5728\u8352\u5c71\u91ce\u90ca\uff0c\u505a\u8fd9\u6740\u4eba\u653e\u706b\u7684\u5f3a\u76d7\u52fe\u5f53\uff1f\u539f\u6765 \u662f\u5927\u540d\u9f0e\u9f0e\u7684\u53f8\u9a6c\u4ec1\u5144\uff01\u201d \u201c\u8feb\u9b42\u94c3\u201d\u53f8\u9a6c\u656c\u89c1\u96f7\u5927\u53d4\u8c03\u6d41\u4e8e\u4ed6\uff0c\u602a\u76ee\u5706\u7741\uff0c\u66b4\u6012\u53f1\u9053\uff1a\u201c\u96f7\u75af\u5b50\uff01\u4f60\u5c11\u5728\u8fd9\u91cc\u88c5 \u75af\u5356\u50bb\uff0c\u4e8c\u592a\u7237\u8feb\u9b42\u94c3\u4e0b\uff0c\u7ba1\u53eb\u4f60\u6b7b\u65e0\u846c\u8eab\u4e4b\u5730\uff01\u201d \u559d\u7f62\uff0c\u8ffd\u9b42\u94c3\u4e00\u6446\uff0c\u94c3\u58f0\u9707\u8033\uff0c\u731b\u7136\u5411\u96f7\u5927\u53d4\u9762\u95e8\u7f69\u6765\uff01 \u53ea\u89c1\u4e09\u4e2a\u6597\u5927\u7684\u8fde\u73af\u9ec4\u8272\u5149\u5708\uff0c\u631f\u7740\u6c81\u9aa8\u5bd2\u6c14\uff0c\u9510\u98ce\u72c2\u5578\u7838\u5411\u96f7\u5927\u53d4\uff0c\u53ef\u89c1\u201c\u8ffd\u9b42\u94c3\u201d \u53f8\u9a6c\u656c\uff0c\u7b2c\u4e00\u62db\u4fbf\u65bd\u51fa\u4e86\u62fc\u547d\u7684\u62db\u5f0f\uff01 \u96f7\u5927\u53d4\u6602\u7136\u800c\u7acb\uff0c\u5634\u89d2\u4e00\u6487\uff0c\u51b7\u7136\u9053\uff1a\u201c\u5c31\u51ed\u4f60\u8fd9\u6c5f\u6e56\u90ce\u4e2d\u5356\u91ce\u836f\u7684\u73a9\u610f\uff0c\u8fd8\u4e0d\u89c1\u5f97\u80fd \u4f24\u4e86\u4f60\u5bb6\u96f7\u5927\u7237\uff01\u201d \u5634\u4e2d\u8bf4\u7740\u8bdd\uff0c\u624b\u4e0b\u53ef\u672a\u95f2\u7740\uff0c\u89c1\u8ffd\u9b42\u94c3\u4ee5\u8fc5\u96f7\u4e4b\u52bf\uff0c\u5c06\u7838\u53ca\u9762\u95e8\uff0c\u5019\u7136\u811a\u4e0b\u4e00\u6ed1\uff0c\u8131\u51fa \u5708\u5916\uff0c\u8d81\u52bf\u63a8\u51fa\u4e00\u638c\uff0c\u76f4\u6363\u53f8\u9a6c\u656c\u5de6\u81c21 \u96f7\u5927\u53d4\u7684\u201c\u5929\u4f5b\u638c\u201d\u4ea6\u662f\u7ec3\u81ea\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u7737\u300b\uff0c\u867d\u6ca1\u6709\u5c5e\u767d\u7ecf\u8fc7\u67f3\u7fe0\u7fe0\u201c\u59f9\u5973\u8ff7\u9b42 \u5927\u6cd5\u201d\u7684\u7194\u70bc\uff0c\u90a3\u6837\u5a01\u4e0d\u53ef\u6321\uff0c\u4f46\u65bd\u5c55\u51fa\u6765\uff0c\u52b2\u98ce\u9510\u5578\uff0c\u5a01\u529b\u4e5f\u662f\u5927\u5f97\u60ca\u4eba\uff01\u201c\u8ffd\u9b42\u94c3\u201d\u53f8 \u9a6c\u656c\u5403\u4e8f\u5728\u7f3a\u5c11\u4e00\u53ea\u5de6\u81c2\uff0c\u6b66\u529f\u518d\u597d\uff0c\u603b\u662f\u5de6\u9762\u95e8\u6237\u7f3a\u5c11\u9632\u536b\uff0c\u653b\u5411\u96f7\u5927\u53d4\u7684\u4e00\u62db\uff0c\u7528\u529b\u8fc7 \u731b\uff0c\u88ab\u96f7\u5927\u53d4\u500f\u7136\u8eb2\u8fc7\uff0c\u52a0\u4e0a\u96f7\u5927\u53d4\u51fa\u624b\u7684\u62db\u5f0f\uff0c\u5b8c\u5168\u662f\u4ee5\u653b\u6b62\u653b\uff0c\u5728\u8eb2\u95ea\u7684\u540c\u65f6\uff0c\u4e00\u638c\u5df2 \u8feb\u8fd1\u53f8\u9a6c\u656c\u7684\u5de6\u81c21 \u96f7\u5927\u53d4\u90a3\u4e00\u638c\u529b\u731b\u75be\u8e72\uff0c\u5feb\u903e\u95ea\u7535\uff0c\u5f85\u53f8\u9a6c\u656c\u53d1\u89c9\u81ea\u5df2\u4e00\u62db\u843d\u7a7a\uff0c\u96f7\u5927\u53d4\u7684\u638c\u98ce\u5df2\u5c06\u6309 \u5b9e\u5728\u4ed6\u7684\u8eab\u4e0a\uff0c\u518d\u60f3\u8eb2\u5df2\u7ecf\u6765\u4e0d\u53ca\u4e86\uff0c\u8fd8\u7b97\u53f8\u9a6c\u656c\u6b66\u529f\u4e0d\u5f31\uff0c\u5728\u4e07\u96be\u8eb2\u95ea\u4e4b\u9645\uff0c\u8eab\u5f62\u4e00\u6d4b\uff0c \u8eb2\u8fc7\u5de6\u80f8\u8981\u5bb3\uff0c\u4ee5\u5de6\u80a9\u786c\u6328\u4e86\u96f7\u5927\u53d4\u4e00\u638c1 \u201c\u562d\u201d\u7684\u4e00\u58f0\u5927\u9707\uff0c\u53f8\u9a6c\u656c\u88ab\u96f7\u5927\u53d4\u4e00\u638c\u9707\u51fa\u516b\u5c3a\uff0c\u5de6\u80a9\u5982\u53d7\u94c1\u9524\uff0c\u75db\u75bc\u5982\u6298\uff01 \u8fd9\u4e00\u638c\u6253\u5f97\u53f8\u9a6c\u656c\u5472\u7259\u4f8b\u5634\uff0c\u8179\u5185\u6c14\u8840\u7ffb\u6d8c\uff0c\u8d76\u7d27\u8fd0\u6c14\u8c03\u606f\u3002\u3002\u201d \u96f7\u5927\u53d4\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u90a3\u4e00\u591c\uff0c\u5728\u2018\u8c79\u7a81\u5c71\u5e84\u2019\u4f60\u5411\u96f7\u5927\u7237\u627e\u78b4\uff0c\u96f7\u5927\u7237\u770b\u5728\u662f\u81ea\u5df1\u4eba \u4efd\u4e0a\u6ca1\u6709\u4f24\u4f60\uff0c\u73b0\u5728\u4f60\u8fd9\u4e00\u5339\u592b\u5012\u662f\u771f\u7684\u5403\u91cc\u6252\u5916\u4e86\uff0c\u5371\u96be\u4e2d\u5f03\u53cb\u964d\u654c\uff0c\u96f7\u5927\u7237\u624b\u4e0b\u4e0d\u518d\u7559 \u60c5\uff0c\u8ba9\u4f60\u5c1d\u5c1d\u2018\u5929\u4f5b\u638c\u2019\u7684\u771f\u6b63\u5473\u9053\uff01\u201d \u8bf4\u7f62\uff0c\u8eab\u5f62\u7535\u5c04\u800c\u8d77\uff0c\u534a\u7a7a\u4e2d\u4e00\u62db\u201c\u4f5b\u7956\u964d\u5ea7\u201d\uff0c\u5355\u638c\u7834\u98ce\uff0c\u731b\u5411\u53f8\u9a6c\u656c\u524d\u5fc3\u201c\u4e09\u9633\u201d \u91cd\u7a74\u6309\u4e0b\uff01 \u96f7\u5927\u53d4\u6068\u900f\u4e86\u8fd9\u53d8\u8282\u6295\u654c\u7684\u65e0\u803b\u8d25\u7c7b\uff0c\u867d\u7136\u53f8\u9a6c\u656c\u5df2\u53d7\u4f24\uff0c\u8fd9\u4e00\u638c\u4ecd\u7136\u7528\u4e86\u516b\u6210\u52b2\uff0c\u6210 \u5fc3\u8981\u8001\u9b3c\u7684\u6b7b\u547d\uff01 \u53f8\u9a6c\u656c\u5927\u610f\u8f7b\u654c\uff0c\u4e00\u7167\u9762\u4fbf\u6328\u4e86\u4e00\u638c\uff0c\u53d7\u4f24\u4e0d\u8f7b\uff0c\u6b63\u5728\u8fd0\u6c14\u8c03\u606f\uff0c\u89c1\u96f7\u5927\u53d4\u7b2c\u4e8c\u638c\u53c8 \u5230\uff0c\u4e14\u5a01\u529b\u66f4\u5f3a\u3002\u63a5\u4e0d\u6562\u63a5\uff0c\u8eb2\u65e0\u6cd5\u8eb2\uff0c\u53ea\u5413\u5f97\u9762\u8272\u60e8\u767d\uff0c\u51e0\u81f3\u5931\u58f0\u60ca\u547c\u2026\u2026 \u7a81\u7136\uff0c\u4e00\u6761\u4eba\u5f71\u659c\u523a\u91cc\u98de\u63a0\u800c\u81f3\uff0c\u534a\u7a7a\u4e2d\u786c\u63a5\u4e86\u96f7\u5927\u53d4\u4e00\u638c\uff01 \u201c\u562d\uff01\u201d \u4e00\u58f0\u5de8\u9707\uff0c\u96f7\u5927\u53d4\u8eab\u5f62\u6643\u4e86\u4e24\u6643\uff0c\u6697\u60ca\u6765\u4eba\u638c\u529b\u4e0d\u5f31\uff01 \u4f46\u6765\u4eba\u5df2\u88ab\u96f7\u5927\u53d4\u638c\u529b\u9707\u5f97\u4e34\u7a7a\u8fde\u7ffb\u4e86\u4e09\u4e2a\u7b4b\u6597\uff0c\u624d\u52bf\u5c3d\u843d\u5730\uff0c\u9762\u8272\u4e00\u7ea2\u4e00\u767d\uff0c\u663e\u7136\u4e5f \u5403\u4e86\u6697\u4e8f\uff01 \u6765\u4eba\u7ffb\u8eab\u843d\u5730\uff0c\u6b63\u662f\u201c\u72ec\u811a\u98de\u9b54\u201d\u674e\u4e3e\uff01 \u201c\u72ec\u811a\u98de\u9b54\u201d\u674e\u4e3e\u5410\u4e86\u4e00\u53e3\u5927\u6c14\uff0c\u624d\u5411\u96f7\u5927\u53d4\u53eb\u9053\uff1a\u201c\u96f7\u75af\u5b50\uff01\u4f60\u5c11\u5356\u72c2\uff0c\u4fa5\u5e78\u8d62\u4e86\u4e00 \u62db\u534a\u5f0f\u7684\uff0c\u4fbf\u6562\u5927\u8a00\u4e0d\u60ed\uff0c\u4f60\u4e5f\u63a5\u5927\u7237\u4e24\u62db\u8bd5\u8bd51\u201d \u8bf4\u7f62\uff0c\u201c\u91d1\u8c79\u9732\u722a\u201d\u3001\u201c\u5378\u5173\u70b9\u5143\u201d\u3001\u201c\u5206\u733f\u88c2\u864e\u201d\uff0c\u95ea\u7535\u822c\u5730\u8fde\u73af\u653b\u51fa\u4e09\u5927\u6740\u62db\uff01 \u201c\u72ec\u811a\u98de\u9b54\u201d\u674e\u4e3e\uff0c\u4ee5\u8eab\u6cd5\u5feb\uff0c\u62db\u5f0f\u5947\u89c1\u957f\uff0c\u8fd9\u4e00\u4e0a\u6765\u4fbf\u62fc\u547d\u7684\u6253\u6cd5\uff0c\u53ea\u89c1\u638c\u6307\u6ee1\u5929\uff0c \u5636\u5636\u7834\u98ce\uff0c\u96f7\u5927\u53d4\u8d38\u7136\u4e4b\u960e\uff0c\u7ade\u88ab\u4e00\u8def\u731b\u653b\uff0c\u63a5\u8fde\u9000\u540e\u4e86\u4e09\u4e94\u4e08\u51fa\u53bb\uff01 \u6b64\u65f6\uff0c\u8339\u8001\u9556\u5934\u4e0e\u6155\u5bb9\u7ea2\uff0c\u5df2\u7ecf\u5404\u63a5\u4f4f\u4e00\u4e2a\u654c\u4eba\u6740\u5728\u4e00\u8d77\uff0c\u5c55\u767d\u63a5\u8fde\u6253\u9000\u56db\u4e94\u540d\u52b2\u88c5\u5927 \u6c49\uff0c\u845b\u89c1\u96f7\u5927\u53d4\u52bf\u5371\uff0c\u98de\u8eab\u63a0\u6765\u76f8\u52a9\uff01 \u96f7\u5927\u53d4\u5927\u53eb\u9053\uff1a\u201c\u8d44\u4f84\u4e14\u4f4f\uff01\u8fd9\u72ec\u811a\u602a\u7269\uff0c\u5927\u53d4\u4e00\u4eba\u8fd8\u5e94\u4ed8\u5f97\u4e86\uff01\u201d \u8bf4\u7f62\uff0c\u5927\u543c\u4e00\u58f0\uff0c\u63a5\u8fde\u4e8c\u62db\uff0c\u6273\u56de\u52a3\u52bf\uff0c\u7acb\u523b\u8fd8\u4ee5\u989c\u8272\uff0c\u4e5f\u63a5\u8fde\u5411\u201c\u72ec\u811a\u98de\u9b54\u201d\u653b\u51fa\u56db \u4e94\u62db\uff01 \u8fd9\u4e00\u6765\uff0c\u53c8\u628a\u201c\u72ec\u811a\u98de\u9b54\u201d\u903c\u9000\u4e86\u4e94\u4e08\u5f00\u5916\uff01 \u201c\u8ffd\u9b42\u94c3\u201d\u53f8\u9a6c\u656c\u5df2\u501f\u673a\u4f1a\uff0c\u5728\u4e00\u8fb9\u8c03\u606f\u590d\u539f\uff0c\u89c1\u5c55\u767d\u795e\u52c7\uff0c\u540c\u6765\u7684\u201c\u5357\u6d77\u95e8\u201d\u9ad8\u624b\uff0c \u5747\u4e0d\u662f\u5c55\u81ea\u7684\u5bf9\u624b\uff0c\u5728\u201c\u8c79\u7a81\u5c71\u5e84\u201d\u5c55\u767d\u4e00\u638c\u654c\u4e09\u715e\uff0c\u53f8\u9a6c\u656c\u66fe\u4eb2\u773c\u76ee\u7779\uff0c\u89c1\u5c55\u767d\u4e00\u9732\u9762\uff0c \u4f7f\u77e5\u4eca\u591c\u7edd\u8ba8\u4e0d\u4e86\u597d\u53bb\uff0c\u7acb\u5373\u5c48\u5c0f\u6307\u653e\u8fdb\u53e3\u4e2d\uff0c\u5439\u4e86\u4e00\u58f0\u5c16\u54e8\u300f \u54e8\u97f3\u5c16\u9510\u523a\u8033\uff01 \u201c\u72ec\u811a\u98de\u9b54\u201d\u674e\u4e3e\uff0c\u731b\u7136\u865a\u6643\u4e00\u62db\uff0c\u9000\u51fa\u5708\u5916\uff0c\u9053\uff1a\u201c\u96f7\u75af\u5b50\uff01\u6682\u7559\u4f60\u591a\u6d3b\u4e24\u5929\uff0c\u8fd9\u7b14 \u8d26\u8fc7\u4e24\u5929\u8981\u52a0\u500d\u8ba8\u8fd8\uff01\u201d \u8bf4\u7f62\uff0c\u98de\u8eab\u5411\u5c71\u6df1\u6797\u5bc6\u4e4b\u5904\u7a9c\u53bb\uff01 \u63a5\u7740\uff0c\u201c\u8ffd\u9b42\u94c3\u201d\u53f8\u9a6c\u656c\u4ee5\u53ca\u5341\u6570\u52b2\u88c5\u5927\u6c49\uff0c\u7eb7\u7eb7\u7a9c\u8d70\u2026\u2026 \u539f\u6765\u90a3\u54e8\u97f3\uff0c\u662f\u8d3c\u4eba\u64a4\u9000\u7684\u4fe1\u53f7\u3002 \u5c55\u767d\u624d\u8981\u53bb\u8ffd\uff0c\u96f7\u5927\u53d4\u4f5c\u624b\u52bf\u628a\u5c55\u767d\u62e6\u4f4f\uff0c\u5374\u5411\u201c\u585e\u5916\u53cc\u6b8b\u201d\u5927\u58f0\u9053\uff1a\u201c\u59d3\u96f7\u7684\u968f\u65f6\u5019 \u6559\uff01\u201d \u201c\u585e\u5916\u53cc\u6b8b\u201d\u5374\u518d\u4e0d\u7b54\u8a00\uff0c\u4e0e\u90a3\u4e9b\u201c\u5357\u6d77\u95e8\u201d\u7684\u52b2\u88c5\u5927\u6c49\uff0c\u5239\u65f6\u4e4b\u95f4\uff0c\u53bb\u5f97\u5931\u4e86\u8e2a\u8ff9\uff01 \u201c\u592a\u767d\u53cc\u9038\u201d\u8d70\u4e0a\u524d\u6765\uff0c\u5148\u5411\u4f17\u4eba\u8c22\u8fc7\u76f8\u52a9\u4e4b\u601d\uff0c\u7136\u540e\u5bf9\u96f7\u5927\u53d4\u9053\uff1a\u201c\u96f7\u5144\uff0c\u5341\u6570\u5e74\u672a \u89c1\uff0c\u4e30\u91c7\u4f9d\u65e7\u2026\u2026\u201d \u96f7\u5927\u53d4\u6252\u4e86\u6252\u81ea\u5df1\u6ee1\u5934\u4e71\u53d1\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4e2a\u5c0f\u5b50\uff0c\u662f\u4e0d\u662f\u6210\u5fc3\u5455\u6211\uff1f\u770b\u6211\u8fd9\u4e00 \u8eab\uff0c\u90a3\u4e00\u70b9\u4e30\u91c7\u4f9d\u65e7\uff1f\u201d \u201c\u5927\u9038\u201d\u9053\uff1a\u201c\u6211\u4e0d\u662f\u6307\u5916\u8868\u8bf4\u7684\uff0c\u5149\u770b\u5916\u8868\uff0c\u5982\u679c\u4e0d\u662f\u5c0f\u6069\u516c\u2014\u2014\u2014\u201d \u8bf4\u7740\u4e00\u6307\u5c55\u767d\uff0c\u53c8\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u4e0a\u6b21\u6765\u65f6\u63d0\u8d77\u96f7\u5144\uff0c\u8fd9\u6b21\u2018\u585e\u5916\u53cc\u6b8b\u2019\u53c8\u4e00\u76f4\u53eb\u4f60\u96f7\u75af \u5b50\uff0c\u6050\u6015\u6211\u5f1f\u5144\u8fd8\u4e0d\u8ba4\u8bc6\u4f60\u54e9\uff0c\u6211\u662f\u8bf4\u96f7\u5144\u7684\u8c6a\u60c5\u4e0d\u51cf\u5f53\u5e74\uff01\u201d \u96f7\u5927\u53d4\u9053\uff1a\u201c\u8fc7\u53bb\u7684\u4e8b\u522b\u63d0\u4e86\uff0c\u5148\u8bf4\u8bf4\u4f60\u4e8c\u4eba\u9690\u9041\u4e16\u5916\uff0c\u600e\u4e48\u8ddf\u2018\u585e\u5916\u53cc\u6b8b\u2019\u7ed3\u4e86\u6881 \u5b50\uff1f\u7adf\u88ab\u627e\u4e0a\u95e8\u6765\u751f\u4e8b\uff1f\u90a3\u4e9b\u58ee\u6c49\u53c8\u662f\u4ec0\u4e48\u8def\u6570\uff1f\u201d \u201c\u4e8c\u9038\u201d\u9053\uff1a\u201c\u8bf4\u6765\u8bdd\u957f\uff0c\u6765\uff0c\u96f7\u5144\uff0c\u6211\u5148\u7ed9\u4f60\u5f15\u89c1\u4e00\u4f4d\u670b\u53cb\uff01\u201d \u8bf4\u7f62\u4e00\u6307\u8eab\u540e\u4e00\u4e2a\u8c8c\u5e74\u9053\uff1a\u201c\u8fd9\u4f4d\u4fbf\u662f\u5927\u540d\u9f0e\u9f0e\u7684\u2018\u7965\u9e9f\u516c\u5b50\u20191\u201d \u53c8\u5bf9\u201c\u7965\u9e9f\u516c\u5b50\u201d\u4e00\u6307\u96f7\u5927\u53d4\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u5341\u6570\u5e74\u524d\u540d\u6ee1\u5927\u6c5f\u5357\u5317\u7684\u2018\u5929\u4f5b\u638c\u2019\u96f7\u9707 \u8fdc\uff01\u201d \u201c\u7965\u9e9f\u516c\u5b50\u201d\u8d76\u7d27\u4e00\u62b1\u62f3\uff0c\u9053\uff1a\u201c\u4e45\u4ef0\uff01\u4e45\u4ef0\uff01\u201d \u96f7\u5927\u53d4\u53cc\u76ee\u4e00\u7741\uff0c\u773c\u653e\u5947\u5149\uff0c\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u76ee\u524d\u6c5f\u6e56\u76db\u4f20\u2018\u7965\u9e9f\u516c\u5b50\u4e0d\u70ed\u80a0\u2019\uff0c\u5c31\u662f \u9601\u4e0b\u4e861\u201d \u8bf4\u5f97\u201c\u7965\u9e9f\u516c\u5b50\u201d\u8138\u4e00\u7ea2\u3002 \u5c55\u767d\u89c1\u201c\u7965\u9e9f\u516c\u5b50\u201d\u4e00\u88ad\u9752\u886b\uff0c\u8863\u9970\u6ca1\u6709\u4ee5\u524d\u534e\u8d35\uff0c\u795e\u6001\u4e5f\u6ca1\u6709\u4ee5\u524d\u6f47\u6d12\uff0c\u7709\u76ee\u4e4b\u95f4\uff0c \u6697\u6cdb\u65e0\u9650\u9690\u5fe7\uff0c\u60f3\u8d77\u8fc7\u53bb\u201c\u7965\u9e9f\u516c\u5b50\u201d\u7684\u98ce\u6d41\u501c\u50a5\uff0c\u4e0d\u77e5\u5982\u4f55\u843d\u5230\u773c\u524d\u5149\u666f\uff1f\u867d\u7136\u4e0e\u4ed6\u7684\u7236 \u4eb2\u6709\u7740\u8840\u6d77\u6df1\u4ec7\uff0c\u4f46\u603b\u7b97\u4e0e\u4ed6\u6709\u6570\u9762\u4e4b\u8bc6\uff0c\u4e5f\u4e0a\u524d\u89c1\u4e86\uff0c\u5e76\u95ee\u9053\uff1a\u201c\u8d35\u516c\u5b50\u4e0d\u5728\u5357\u4eac\u91d1\u5e9c\uff0c \u6765\u8fd9\u2018\u6b7b\u4eba\u5c45\u2019\u6709\u4f55\u8d35\u5e72\uff1f\u201d \u201c\u7965\u9e9f\u516c\u5b50\u201d\u6ee1\u8138\u7f9e\u60ed\u4e4b\u8272\uff0c\u8e0f\u7136\u4f4e\u4e0b\u5934\u53bb\u3002\u2026\u2026 \u201c\u592a\u767d\u53cc\u9038\u201d\u7684\u201c\u5927\u9038\u201d\u2014\u2014\u4e5f\u5c31\u662f\u201c\u6d3b\u6b7b\u4eba\u201d\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u4f60\u4eec\u4e8c\u4f4d\u65e9\u8ba4\u8bc6\uff01\u2026\u2026\u201d \u8bf4\u5230\u6b64\u5904\uff0c\u56de\u5934\u671b\u4e86\u671b\u201c\u6b7b\u4eba\u5c45\u201d\u6089\u5df2\u70e7\u6bc1\uff0c\u70c8\u706b\u5c1a\u81ea\u672a\u7184\uff0c\u5411\u96f7\u5927\u53d4\u6447\u5934\u82e6\u7b11\u9053\uff1a \u201c\u591a\u5e74\u672a\u89c1\uff0c\u60f3\u4e0d\u5230\u8fde\u4e2a\u5750\u7684\u5730\u65b9\u4e5f\u6ca1\u6709 \u96f7\u5927\u53d4\u53cc\u7709\u4e00\u8038\uff0c\u9053\uff1a\u201c\u4e0d\u8981\u5ba2\u6c14\u4e86\uff01\u5feb\u8bf4\u51fa\u4e8b\u7684\u7ecf\u8fc7\u5427\uff01\u201d \u51e0\u4e2a\u4eba\u5c31\u7ad9\u5728\u706b\u573a\u9644\u8fd1\uff0c\u7531\u201c\u592a\u767d\u53cc\u9038\u201d\u8bf4\u51fa\u4e00\u756a\u8bdd\u6765\u3002 \u96f7\u5927\u53d4\u7b49\u4eba\u542c\u4e86\u4e0d\u7531\u6697\u6697\u548b\u820c\uff01 \u539f\u6765\uff0c\u201c\u6d3b\u6b7b\u4eba\u201d\u4e0d\u653e\u5fc3\u5c55\u767d\u72ec\u81ea\u4e00\u4eba\u81f3\u5357\u4eac\u91d1\u5e9c\u4e3a\u7236\u62a5\u4ec7\uff0c\u7b49\u5c55\u767d\u5b9a\u540e\uff0c\u5148\u628a\u5f1f\u5f1f \u201c\u6b7b\u6d3b\u4eba\u201d\u6551\u9192\uff0c\u4e8c\u4eba\u53c8\u628a\u5a49\u513f\u653e\u8d70\uff0c\u56e0\u4e3a\u201c\u6b7b\u6d3b\u4eba\u201d\u662f\u53d7\u4e86\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\u4e4b\u4e2d\u201c\u7f8e \u5973\u753b\u50cf\u201d\u7684\u8ff7\u60d1\uff0c\u624d\u8ff7\u5931\u672c\u6027\u505a\u51fa\u4fb5\u72af\u5a49\u513f\u7684\u884c\u4e3a\uff0c\u653e\u4e5f\u672a\u52a0\u6df1\u8d23\uff0c\u4e8c\u4eba\u8054\u6289\u81f3\u5ca9\u5c71\u5341\u4e8c\u6d1e \u5bfb\u627e\u5c55\u767d\u53ef\u662f\uff0c\u4e8c\u4eba\u5230\u4e86\u5ca9\u5c71\u5341\u4e8c\u6d1e\uff0c\u5e76\u6ca1\u6709\u627e\u5230\u5c55\u767d\uff0c\u53c8\u81f3\u57ce\u5185\u201c\u7965\u9e9f\u516c\u5b50\u201d\u5e9c\u5185\uff0c\u6697\u63a2 \u4e86\u51e0\u6b21\uff0c\u4e5f\u6ca1\u6709\u53d1\u73b0\u5c55\u767d\u8e2a\u8ff9\uff0c\u5374\u4e0e\u91d1\u5e9c\u9ad8\u624b\uff0c\u8fde\u8d77\u4e86\u6570\u6b21\u5927\u6218\uff0c\u5f1f\u5144\u4e8c\u4eba\u9669\u4e9b\u5403\u4e86\u5927\u4e8f\uff0c \u4f46\u66f4\u4e0d\u653e\u5fc3\u5c55\u767d\u7684\u5b89\u5371\uff0c\u4e8c\u4eba\u8bef\u4ee5\u4e3a\u5c55\u767d\u5931\u624b\u88ab\u91d1\u5e9c\u64d2\u83b7\uff0c\u66f4\u62c5\u5fc3\u88ab\u91d1\u5e9c\u6740\u5bb3\u4e86\uff0c\u65e0\u5948\u4e8c\u4eba \u4eba\u5355\u52bf\u5b64\uff0c\u4e0d\u662f\u91d1\u5e9c\u4f17\u9ad8\u624b\u7684\u5bf9\u624b\uff0c\u53c8\u820d\u4e0d\u4e0b\u907d\u7136\u79bb\u5f00\uff0c\u6545\u6b64\uff0c\u4fbf\u4f4f\u5728\u5357\u4eac\u4e00\u5bb6\u5ba2\u6808\u5185\uff0c\u4e00 \u8fb9\u5bfb\u8bbf\u5c55\u767d\u884c\u8e2a\uff0c\u4e00\u8fb9\u5f97\u673a\u4f1a\u6f5c\u5165\u91d1\u5e9c\u6697\u63a2\u3002\u2026\u2026 \u5c31\u8fd9\u6837\uff0c\u65bd\u4e86\u5c06\u8fd1\u4e00\u4e2a\u6708\uff0c\u65f6\u65f6\u542c\u5230\u4f20\u76f2\uff0c\u6709\u7684\u9053\uff1a\u201c\u5143\u5bb5\u8282\u2018\u6b66\u6797\u56db\u516c\u5b50\u2019\u8d4f\u706f\u5927\u4f1a \u4e0a\uff0c\u4e0e\u4f1a\u7fa4\u96c4\u4e00\u9f50\u6b7b\u5728\u91d1\u5e9c\u2018\u77f3\u77f6\u5927\u9635\u2019\u4e4b\u4e2d\uff0c\u5c55\u767d\u4e5f\u591c\u5176\u5185\u2026\u2026\u201d \u53c8\u6709\u7684\u9053\uff1a\u201c\u5c55\u767d\u6ca1\u6b7b\uff0c\u9003\u51fa\u4e86\u91d1\u5e9c\u7684\u2018\u77f3\u77f6\u5927\u9635\u2019\uff0c\u5374\u846c\u8eab\u5728\u5357\u4eac\u90ca\u5916\u7d2b\u91d1\u5c71\u4e2d\u7684 \u2018\u4ea1\u9b42\u8c37\u2019\u4e2d\u2026\u2026\u201d \u53c8\u6709\u7684\u9053\uff1a\u201c\u5c55\u767d\u5df2\u5f52\u964d\u4e86\u2018\u5357\u6d77\u95e8\u2019\u2026\u2026\u201d \u4eba\u8a00\u4eba\u6b8a\uff0c\u83ab\u8877\u4e00\u662f\u3002 \u4f46\u6700\u540e\u5374\u662f\u201c\u5357\u6d77\u95e8\u201d\u7684\u5014\u8d77\uff0c\u4e8c\u4eba\u5728\u6700\u540e\u4e00\u6b21\u6697\u63a2\u5357\u4eac\u91d1\u5e9c\u7684\u884c\u52a8\u4e2d\uff0c\u5374\u5728\u4e00\u5ea7\u77f3\u4e2d \u91cc\uff0c\u65e0\u610f\u4e2d\u6551\u51fa\u4e86\u201c\u7965\u9e9f\u516c\u5b50\u201d\uff01 \u201c\u592a\u767d\u53cc\u9038\u201d\u53ea\u77e5\u5728\u91d1\u5e9c\u77f3\u7262\u6551\u51fa\u4e00\u4e2a\u660f\u8ff7\u4e0d\u9192\u7684\u9752\u5e74\uff0c\u4e07\u4e5f\u60f3\u4e0d\u5230\u628a\u8fd9\u9752\u5e74\u6551\u6d3b\uff0c\u8fd9 \u9752\u5e74\u7adf\u662f\u91d1\u5e9c\u7684\u4e3b\u4eba\u3001\u6c5f\u6e56\u4e0a\u5927\u540d\u9f0e\u9f0e\u7684\u201c\u7965\u9e9f\u516c\u5b50\u201d\uff01 \u4e00\u95ee\uff0c\u624d\u77e5\u9053\u201c\u9e4a\u5de2\u9e20\u5360\u201d\uff0c\u5357\u4eac\u91d1\u5e9c\u5df2\u7ecf\u6613\u4e3b\u6210\u4e3a\u201c\u5357\u6d77\u95e8\u201d\u79f0\u9738\u4e2d\u539f\u53d1\u53f7\u65bd\u4ee4\u7684\u67a2 \u7ebd\u4e86\u3002 \u201c\u9752\u86a8\u795e\u201d\u91d1\u4e5d\u60e8\u88ab\u4e94\u5200\u5206\u5c38\uff0c\u201c\u7965\u9e9f\u516c\u5b50\u201d\u53ca\u5176\u59b9\u201c\u6c5f\u5357\u7b2c\u4e00\u7f8e\u4eba\u201d\u91d1\u5f69\u51e4\u88ab\u64d2\uff0c\u5206 \u7f6e\u56da\u7981\uff0c\u91d1\u5e9c\u98df\u5ba2\u6b7b\u7684\u6b7b\u964d\u7684\u964d\uff0c\u5df2\u7ecf\u8d70\u6b7b\u4e00\u7a7a\uff0c\u5357\u4eac\u91d1\u5e9c\u6210\u4e3a\u201c\u5357\u6d77\u95e8\u201d\u7684\u5927\u672c\u8425\u201c\u5357\u6d77 \u4e00\u541b\u201d\u5750\u9547\u5176\u4e2d\uff0c\u53d1\u53f7\u65bd\u4ee4\uff0c\u505a\u4fb5\u5360\u6574\u4e2a\u4e2d\u539f\u6b66\u6797\u7684\u9738\u4e1a\u5b8f\u56fe\uff01 \u6700\u540e\uff0c\u201c\u6d3b\u6b7b\u4eba\u201d\u9053\uff1a\u4eca\u591c\u2018\u585e\u5916\u53cc\u6b8b\u2019\u7387\u4f17\u5bfb\u4ec7\uff0c\u5c31\u662f\u5949\u4e86\u2018\u5357\u6d77\u95e8\u2019\u4e4b\u547d\uff0c\u6765\u62ff \u2018\u7965\u9e9f\u516c\u5b50\u2019\u7684\uff01\u201d \u60f3\u8fd9\u6837\u7684\u6d88\u606f\uff0c\u4f7f\u96f7\u5927\u53d4\u4f17\u4eba\u5982\u4f55\u4e0d\u5403\u60ca\uff1f \u5c55\u767d\u9664\u4e86\u5403\u60ca\u4e4b\u5916\uff0c\u66f4\u611f\u5230\u60ca\u5947\u4e0e\u7591\u60d11 \u56e0\u4e3a\uff0c\u8fd9\u4e9b\u6d88\u606f\uff0c\u4e0e\u4ed6\u4ece\u7fe0\u7fe0\u53e3\u4e2d\u4ee5\u53ca\u65e9\u65f6\u83b7\u77e5\u7684\u6d88\u606f\uff0c\u7adf\u662f\u5927\u6709\u51fa\u5165\uff01 \u5728\u201c\u4ea1\u9b42\u8c37\u201d\u4e2d\u7fe0\u7fe0\u544a\u8bc9\u4ed6\uff0c\u201c\u5357\u6d77\u95e8\u201d\u5df2\u7ecf\u79f0\u9738\u6b66\u6797\uff0c\u201c\u6b66\u6797\u56db\u516c\u5b50\u201d\u90fd\u6210\u4e86\u201c\u5357\u6d77 \u95e8\u201d\u7684\u5206\u5802\uff0c\u6216\u662f\u5c0f\u5934\u76ee\uff01 \u5728\u201c\u8c79\u7a81\u5c71\u5e84\u201d\u4ed6\u7a83\u542c\u5230\u201c\u5357\u6d77\u9f99\u5973\u201d\u7b49\u7684\u8c08\u8bdd\uff0c\u8bf4\u662f\u5165\u4fb5\u4e2d\u539f\u7528\u201c\u5357\u6d77\u5c11\u541b\u201d\u7684\u540d \u4e49\uff0c\u5b9e\u9645\u4e0a\u5374\u662f\u7531\u201c\u5357\u6d77\u9f99\u5973\u201d\u4e3b\u6301\u7684\uff0c\u201c\u5357\u6d77\u4e00\u541b\u201d\u6d77\u9f99\u795e\uff0c\u5e76\u672a\u4eb2\u81f3\u4e2d\u539f\uff01 \u83ab\u975e\u201c\u5357\u6d77\u5c11\u541b\u201d\u4e0e\u201c\u5357\u6d77\u9f99\u5973\u201d\u6240\u8a00\u4e0d\u786e\uff0c\u6d77\u9f99\u795e\u201c\u5357\u6d77\u4e00\u541b\u201d\u771f\u7684\u4eb2\u4e0b\u4e2d\u539f\u4e86\uff1f \u663e\u89c1\u7fe0\u7fe0\u8bf4\u7684\u8bdd\u4e5f\u4e0d\u7b97\u6570\uff0c\u56e0\u4e3a\u201c\u51cc\u98ce\u516c\u5b50\u201d\u3001\u201c\u7965\u9e9f\u516c\u5b50\u201d\u90fd\u5df2\u505a\u4e86\u9636\u4e0b\u4e4b\u56da\uff0c\u9669\u4e9b \u4e27\u4e86\u6027\u547d\uff0c\u53ef\u89c1\u201c\u6b66\u6797\u56db\u516c\u5b50\u201d\u5e76\u672a\u5728\u201c\u5357\u6d77\u95e8\u201d\u4e0b\u79f0\u81e3\uff01 \u53c8\u662f\u6b3a\u9a97\uff01 \u5c55\u767d\u60f3\u8d77\u7fe0\u7fe0\u66fe\u6b3a\u9a97\u8fc7\u4ed6\uff0c\u5fc3\u4e2d\u7684\u6012\u6c14\u4fbf\u6cb9\u7136\u800c\u751f\uff01 \u5982\u4eca\uff0c\u8bc1\u5b9e\u7fe0\u7fe0\u7684\u8bdd\uff0c\u53c8\u4e00\u6b21\u4e0d\u5b9e\uff0c\u4ed6\u5fc3\u4e2d\u7684\u6012\u6c14\u66f4\u751a\u2026\uff0e\uff0e\u3002 \u96f7\u5927\u53d4\u542c\u5b8c\u201c\u592a\u767d\u53cc\u9038\u201d\u7684\u53d9\u8ff0\uff0c\u6c89\u601d\u6709\u9877\u9053\uff1a\u201c\u770b\u6765\uff0c\u6211\u4eec\u8981\u5f80\u5357\u4eac\u91d1\u5e9c\u8d70\u4e00\u906d\uff0c\u8bf4 \u4e0d\u5b9a\u8339\u8001\u9556\u5934\u7684\u5931\u9556\uff0c\u5c31\u843d\u5728\u91d1\u5e9c\uff01\u201d\u8339\u8001\u9556\u5934\u8c26\u8f9e\u9053\u201c\u5931\u9556\u5176\u6b21\uff0c\u8fd8\u662f\u5148\u627e\u5a49\u513f\u8981\u7d27\uff01\u201d \u96f7\u5927\u53d4\u9053\uff1a\u201c\u4eba\u4e5f\u8981\u627e\uff0c\u5931\u9556\u4e5f\u8981\u8ffd\uff0c\u53cd\u6b63\u6211\u4eec\u662f\u8981\u5954\u5357\u4eac\u53bb\uff0c\u627e\u4eba\u627e\u9556\uff0c\u5b70\u5148\u5b70\u540e\uff0c \u770b\u60c5\u5f62\u518d\u51b3\u5b9a\uff01\u201d \u8bf4\u7f62\uff0c\u731b\u559d\u4e86\u4e00\u58f0\uff1a\u201c\u8d70\uff01\u201d \u5f53\u5148\u98de\u8eab\u4e0a\u9a6c\uff0c\u98ce\u9a70\u7535\u63a3\u5411\u5c71\u4e0b\u5954\u53bb\uff01 \u96f7\u5927\u53d4\u706b\u7206\u813e\u6c14\uff0c\u8bf4\u5b9a\u5c31\u8d70\uff0c\u8fde\u5411\u4f17\u4eba\u62db\u547c\u90fd\u4e0d\u6253\u3002 \u5c55\u767d\u5411\u201c\u592a\u767d\u53cc\u9038\u201d\u9053\uff1a\u201c\u4e8c\u4f4d\u524d\u8f88\uff0c\u662f\u4e0d\u662f\u4e5f\u8981\u540c\u53bb\uff1f\u201d \u201c\u592a\u767d\u53cc\u9038\u201d\u9053\uff1a\u201c\u4e49\u4e0d\u5bb9\u8f9e\uff0c\u4f55\u51b5\u6211\u5144\u5f1f\u7684\u623f\u5b50\u88ab\u4ed6\u4eec\u70e7\u5149\uff0c\u4e5f\u8981\u8ba8\u8fd8\u8fd9\u7b14\u8d26\uff01\u201d \u91d1\u516c\u5b50\u611f\u52a8\u5730\u63e1\u4f4f\u5c55\u767d\u7684\u624b\u9053\uff1a\u201c\u4eca\u540e\u5bd2\u820d\u7684\u4e8b\uff0c\u8981\u8499\u5c55\u5144\u591a\u5e2e\u5fd9\u4e86\uff01\u201d \u5c55\u767d\u51b7\u54fc\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8d35\u516c\u5b50\u77e5\u4e0d\u77e5\u9053\uff0c\u4ee4\u5c0a\u8fd8\u662f\u5728\u4e0b\u7684\u6740\u7236\u4ec7\u4eba\u54e9\uff1f\u201d \u201c\u7965\u9e9f\u516c\u5b50\u201d\u4e00\u695e\uff01\u2026o\xb7 \u201c\u6d3b\u6b7b\u4eba\u201d\u9053\uff1a\u201c\u73b0\u5728\u91d1\u5e84\u4e3b\u5df2\u6b7b\uff0c\u6240\u8c13\u2018\u4eba\u6b7b\u4e0d\u8bb0\u4ec7\u2019\uff0c\u54b1\u4eec\u8fd8\u662f\u5148\u5408\u8d77\u624b\u6765\uff0c\u5bf9\u4ed8 \u2018\u5357\u6d77\u95e8\u2019\u8981\u7d271\u201d \u201c\u6b7b\u6d3b\u4eba\u201d\u4e5f\u9053\uff1a\u201c\u5c0f\u6069\u516c\uff01\u54b1\u4eec\u8d70\uff01\u201d \u8bf4\u7f62\uff0c\u4e0e\u201c\u6d3b\u6b7b\u4eba\u201d\u53cc\u53cc\u968f\u7740\u96f7\u5927\u53d4\u5954\u53bb\uff01 \u5c55\u767d\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u5357\u6d77\u95e8\u2019\u66ff\u81ea\u5df1\u8bdb\u6740\u4e86\u4e24\u5927\u6740\u7236\u4ec7\u4eba\uff0c\u5982\u4eca\u81ea\u5df2\u90fd\u8981\u53bb\u627e\u2018\u5357\u6d77\u95e8\u2019 \u7b97\u8d26\uff0c\u8c01\u662f\u601d\uff1f\u8c01\u662f\u4ec7\uff1f\u5012\u771f\u7684\u96be\u8bf4 \u8fd9\u5947\u5f02\u7684\u601d\u60f3\uff0c\u7ade\u4e00\u65f6\u4f7f\u4ed6\u60f3\u5f97\u5446\u4e86\u2026\u2026 \u6155\u5bb9\u7ea2\u4e0e\u8339\u8001\u9556\u5934\u4e5f\u90fd\u98de\u8eab\u4e0a\u9a6c\uff0c\u89c1\u5c55\u767d\u5140\u81ea\u53d1\u6014\uff0c\u6155\u5bb9\u7ea2\u9053\uff1a\u201c\u767d\u54e5\u54e5\uff01\u6211\u4eec\u4e5f\u8d70 \u5427\uff01\u201d \u5c55\u767d\u8fd9\u624d\u60ca\u89c9\uff0c\u7acb\u523b\u2014\u968f\u8eab\u8df3\u4e0a\u81ea\u5df2\u5750\u9a6c\uff0c\u53e3\u4e2d\u201c\u554a\u201d\u7684\u66b4\u53f1\u4e86\u4e00\u58f0\uff0c\u5982\u4e00\u9635\u72c2\u98ce\uff0c\u7b56 \u9a6c\u51b2\u4e0b\u5c71\u53bb1 \u8339\u8001\u9556\u5934\u3001\u6155\u5bb9\u7ea2\u5148\u540e\u7b56\u9a6c\u4e0b\u5c71\uff01 \u201c\u7965\u9e9f\u516c\u5b50\u201d\u89c1\u4eba\u5bb6\u90fd\u9a91\u9a6c\u8d70\u4e86\uff0c\u60f3\u8d77\u81ea\u5df1\u4ece\u524d\u8f7b\u8f66\u5feb\u9a6c\uff0c\u4ec6\u6295\u6210\u7fa4\uff0c\u5982\u4eca\u7ade\u5b64\u96f6\u96f6\u7684 \u671b\u7740\u4eba\u5bb6\u9a91\u9a6c\u800c\u53bb\uff0c\u81ea\u5df1\u5374\u6210\u4e86\u9a6c\u540e\u968f\u884c\uff0c\u4e0d\u7981\u611f\u6982\u4e07\u5343\u2026\u2026 \u4f7f\u4ed6\u5fc3\u4e2d\u5927\u60d1\u4e0d\u89e3\u7684\u662f\uff0c\u5c55\u767d\u4e34\u884c\u90a3\u4e00\u53e5\u8bdd\uff1a\u201c\u4ee4\u5c0a\u8fd8\u662f\u5728\u4e0b\u4ec7\u4eba\uff01\u201d \u8fd9\u6ca1\u5934\u6ca1\u8111\u7684\u4e00\u53e5\u8bdd\uff0c\u4ed6\u5b9e\u5728\u60f3\u4e0d\u901a\uff01\u56e0\u4e3a\u4ed6\u5e76\u4e0d\u77e5\u9053\u4ed6\u7236\u4eb2\u201c\u9752\u86a8\u795e\u201d\u91d1\u4e5d\u6740\u5bb3\u5c55\u767d \u7236\u4eb2\u7684\u5b9e\u60c5\uff0c\u751a\u81f3\u4ed6\u8fde\u542c\u8bf4\u4e5f\u6ca1\u6709\u542c\u8bf4\u9053\u2026\u3002\uff0e \u4e00\u884c\u4e03\u4eba\uff0c\u56db\u9a6c\u4e09\u5f92\u6b65\uff0c\u9a6c\u817f\u4eba\u811a\u90fd\u591f\u5feb\u7684\uff0c\u5230\u5929\u4eae\u65f6\u5206\uff0c\u5df2\u7ecf\u8d76\u5230\u4e86\u5357\u4eac\u57ce\uff01 \u56e0\u4e3a\u65f6\u95f4\u8fd8\u65e9\uff0c\u5357\u4eac\u57ce\u95e8\u672b\u5f00\uff0c\u4e03\u4e2a\u4eba\u5c31\u5728\u5357\u4eac\u57ce\u5916\u9760\u57ce\u95e8\u9644\u8fd1\u4e00\u5bb6\u5ba2\u6808\u4f4f\u4e86\u4e0b\u6765\u3002 \u597d\u5728\u81f3\u5357\u4eac\u57ce\u5185\u8d76\u65e9\u5e02\u7684\u5546\u65c5\u4eba\u7b49\u751a\u591a\uff0c\u591a\u534a\u5728\u53e3\u51fa\u524d\u8d76\u5230\u57ce\u5916\u4f4f\u5e97\u4f11\u606f\uff0c\u5e76\u7565\u8fdb\u65e9 \u70b9\uff0c\u65e5\u51fa\u8fdb\u57ce\uff0c\u6545\u4e5f\u672b\u5f15\u8d77\u4eba\u6ce8\u610f\uff0c\u4e03\u4e2a\u4eba\u8981\u4e86\u70e7\u997c\u8c46\u6d46\u4e4b\u7c7b\u7684\u65e9\u70b9\uff0c\u8349\u8349\u5403\u7f62\uff0c\u5404\u81ea\u95ed\u95e8 \u4f11\u606f\uff0c\u4e13\u7b49\u591c\u95f4\u6765\u5230\uff0c\u53bb\u91d1\u5e9c\u8e29\u63a2\u3002 \u65f6\u95f4\u5982\u901d\uff0c\u4e00\u5929\u6613\u8fc7\uff0c\u7b49\u5230\u592a\u9633\u504f\u897f\uff0c\u628a\u9a6c\u5bc4\u653e\u5728\u5e97\u5185\u5f92\u6b65\u8fdb\u57ce\uff0c\u53c8\u5728\u9ed1\u6697\u4e4b\u5904\u6253\u5750\u8c03 \u606f\uff0c\u517b\u7cbe\u84c4\u9510\u4e86\u4e00\u756a\uff0c\u76f4\u7b49\u542c\u5230\u6a35\u697c\u9f13\u6253\u4e09\u66f4\uff0c\u4e03\u4e2a\u4eba\u624d\u8df3\u8d77\u8eab\u5f62\uff0c\u72b9\u5982\u4e03\u53ea\u5927\u9e1f\u4e00\u822c\uff0c\u98de \u8eab\u8dc3\u4e0a\u91d1\u5e9c\u9ad8\u5899\uff01 \u4e03\u4eba\u4e4b\u4e2d\uff0c\u4f8b\u6709\u56db\u4e2a\u4eba\u662f\u8f7b\u8f66\u719f\u8def\uff0c\u201c\u7965\u9e9f\u516c\u5b50\u201d\u4e0d\u5fc5\u8bf4\uff0c\u662f\u6765\u5230\u81ea\u5df1\u5bb6\u4e86\uff0c\u5f53\u7136\u662f\u719f \u6089\u65e0\u6bd4\uff0c\u5c31\u662f\u5c55\u767d\u3001\u201c\u592a\u767d\u53cc\u9038\u201d\u7b49\u4e09\u4eba\uff0c\u4e5f\u6570\u6b21\u8fdb\u8fc7\u91d1\u5e9c\uff0c\u5176\u4e2d\u53ea\u6709\u96f7\u5927\u53d4\u3001\u8339\u8001\u9556\u5934\uff0c \u4ee5\u53ca\u6155\u5bb9\u7ea2\u662f\u9996\u6b21\u6765\uff0c\u5728\u56db\u4e2a\u719f\u6089\u5730\u5f62\u7684\u4eba\u7269\u9886\u8def\u4e4b\u4e0b\uff0c\u603b\u4ee5\u4e3a\u53ef\u987a\u5229\u8fdb\u5165\u91d1\u5e9c\u3002 \u8c01\u77e5\u5927\u8c2c\u4e0d\u7136\uff0c\u91d1\u516c\u5b50\u3001\u5c55\u767d\u3001\u201c\u592a\u767d\u53cc\u9038\u201d\u7b49\u56db\u4eba\u7ad9\u5728\u9ad8\u5899\u4e0a\u4e00\u6253\u91cf\uff0c\u7adf\u5f53\u573a\u6014\u4f4f\u3002 \u539f\u6765\u91d1\u5e9c\u6700\u5927\u7684\u90a3\u95f4\u201c\u8bae\u4e8b\u5385\u201d\uff0c\u66fe\u5728\u5143\u5bb5\u8282\u201c\u6b66\u6797\u56db\u516c\u5b50\u201d\u5927\u4f1a\u4e0a\uff0c\u53d1\u52a8\u201c\u77f3\u77fe\u5927\u9635\u201d\u6c89 \u5165\u5730\u5e95\uff0c\u4f46\u73b0\u5728\u53c8\u5df2\u5347\u81f3\u5730\u9762\uff0c\u6562\u60c5\u201c\u5357\u6d77\u95e8\u201d\u7d20\u64c5\u6d88\u606f\u673a\u5173\u4e4b\u5b66\uff0c\u4e0d\u4f46\u628a\u91d1\u5e9c\u4e4b\u4eba\u90fd\u8ba4\u4e3a \u201c\u77f3\u77f6\u5927\u9635\u201d\u53d1\u52a8\u4e4b\u540e\uff0c\u4fbf\u6c89\u5165\u5730\u5e95\u6c38\u4e0d\u80fd\u5347\u4e0a\u6765\u7684\u5927\u5385\uff0c\u7ed8\u5347\u4e86\u8d77\u6765\uff0c\u800c\u4e14\u201c\u673a\u5173\u6d88\u606f\u201d \u5e03\u7f6e\u4e4b\u5999\uff0c\u53c8\u589e\u52a0\u4e86\u4e0d\u5c111 \u4e03\u4e2a\u4eba\u7531\u201c\u7965\u9e9f\u516c\u5b50\u201d\u9886\u8def\uff0c\u8dc3\u4e0a\u9ad8\u5899\uff0c\u56db\u4e0b\u4e00\u6253\u91cf\uff0c\u4e0d\u4f46\u5c55\u767d\u3001\u201c\u592a\u767d\u53cc\u9038\u201d\u7eb3\u95f7\uff0c \u5c31\u8fde\u201c\u7965\u9e9f\u516c\u5b50\u201d\u89c1\u5230\u4e86\u81ea\u5df1\u7684\u5bb6\uff0c\u4e5f\u4e0d\u7531\u95f9\u4e86\u4e2a\u76ee\u77aa\u53e3\u5446\uff01 \u504c\u5927\u4e00\u5ea7\u91d1\u5e9c\uff0c\u7ade\u8fde\u4e00\u76cf\u706f\u5149\u4e5f\u6ca1\u6709\uff01 \u800c\u4e14\uff0c\u5385\u3001\u5802\u3001\u697c\u3001\u9601\u7684\u4f4d\u7f6e\uff0c\u5927\u591a\u53d8\u4e86\u6837\uff0c\u8def\u5f84\u4e0d\u540c\uff0c\u65b9\u5411\u9519\u7f6e\uff0c\u8fde\u571f\u751f\u571f\u957f\u7684\u201c\u7965 \u9e9f\u516c\u5b50\u201d\u4e5f\u8ba4\u4e0d\u51fa\uff0c\u54ea\u95f4\u623f\u4e8e\u662f\u5e72\u4ec0\u4e48\u7528\u7684\uff0c\u54ea\u6761\u8def\u901a\u54ea\u91cc\uff1f \u201c\u7965\u9e9f\u516c\u5b50\u201d\u4fef\u5728\u96f7\u5927\u53d4\u8033\u8fb9\uff0c\u628a\u8fd9\u602a\u73b0\u8c61\u6084\u6084\u544a\u8bc9\u4e86\u96f7\u5927\u53d4\uff01 \u96f7\u5927\u53d4\u4e5f\u5fae\u611f\u610f\u5916\uff0c\u5c31\u8bf4\u201c\u5357\u6d77\u95e8\u201d\u628a\u8bfa\u5927\u7684\u4e00\u5ea7\u91d1\u5e9c\uff0c\u91cd\u65b0\u5efa\u9020\u8fc7\uff0c\u4e5f\u6ca1\u6709\u8fd9\u4e48\u5feb \u869c\uff01 \u4e03\u4e2a\u4eba\u53c8\u51d1\u5728\u8d77\u4e00\u5546\u91cf\uff0c\u5230\u5e95\u8339\u8001\u954d\u5934\u4e0d\u5080\u4e3a\u8001\u6c5f\u6e56\uff0c\u7ecf\u591a\u8bc6\u5e7f\uff0c\u5f53\u65f6\u8bf4\u9053\uff1a\u201c\u6211\u4ee5\u524d \u5728\u6cb3\u5357\u8d77\u9556\u65f6\uff0c\u8def\u901a\u85cf\u9f99\u5821\uff0c\u90a3\u5821\u4e3b\u4eba\u79f0\u2018\u5de7\u5320\u8d5b\u9c81\u73ed\u2019\uff0c\u636e\u8bf4\u85cf\u9f99\u5821\u5c31\u662f\u8fd9\u6837\uff0c\u5dee\u4e0d\u591a\u4e09 \u5929\u4e24\u5929\u6362\u4e00\u4e2a\u6837\u5b50\uff0c\u7b2c\u4e00\u5929\u53bb\u8fc7\u7684\u4eba\uff0c\u7b2c\u4e8c\u5929\u518d\u53bb\u4fbf\u627e\u4e0d\u51fa\u539f\u6765\u7684\u8def\u4e86\uff0c\u542c\u8bf4\u8fd9\u53eb\u505a\u2018\u5468\u5929 \u56f0\u5ea6\u2019\u4e4b\u5b66\uff0c\u4ee5\u623f\u5c4b\u7684\u5347\u964d\uff0c\u6539\u53d8\u4f4d\u7f6e\uff0c\u4ee5\u95e8\u7a97\u7684\u91cd\u5f00\uff0c\u6539\u53d8\u65b9\u5411\uff0c\u518d\u52a0\u4e0a\u82b1\u8349\u6811\u6728\u7684\u8fd0 \u7528\uff0c\u4f7f\u539f\u6765\u8def\u5f84\u6539\u53d8\uff0c\u5c31\u662f\u518d\u719f\u7684\u4eba\uff0c\u4e5f\u6478\u4e0d\u6e05\u95e8\u8def1\u201d \u201c\u90a3\u4e48\uff0c\u54b1\u4eec\u4eca\u591c\u4e0d\u662f\u767d\u6765\u4e86\u5417\uff1f\u201d \u8339\u8001\u9556\u5934\u9053\uff1a\u201c\u90a3\u4e5f\u4e0d\u5c3d\u7136\uff0c\u54b1\u4eec\u4e0d\u7ba1\u4ed6\u4ec0\u4e48\u95e8\u8def\uff0c\u53ea\u8981\u627e\u53ef\u7591\u4e4b\u5904\u8e29\u63a2\u5c31\u662f\u4e86\uff0c\u4e0d \u8fc7\uff0c\u5374\u8981\u5f53\u5fc3\u673a\u5173\u57cb\u4f0f\uff01\u2026\u2026\u201d \u7a81\u542c\u9ed1\u5f71\u91cc\u4e00\u58f0\u51b7\u54fc\uff0c\u4f20\u6765\u4e00\u4e2a\u7ec6\u5982\u868a\u7eb3\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u77e5\u4e4b\u4e3a\u77e5\u4e4b\uff0c\u4e0d\u77e5\u4e3a\u4e0d\u77e5\uff01\u81ea \u5df1\u4e0d\u77e5\u9053\u771f\u60c5\u5e95\u7ec6\uff0c\u600e\u53ef\u968f\u4fbf\u4e71\u731c\uff1f\u5982\u679c\u6559\u522b\u4eba\u5984\u7136\u5c1d\u8bd5\uff0c\u9001\u4e86\u6027\u547d\uff0c\u5c82\u4e0d\u51a4\u6821\uff01\u201d \u96f7\u5927\u53d4\u542c\u77e5\u53d1\u8bdd\u4e4b\u4eba\uff0c\u662f\u7528\u4e00\u79cd\u6781\u4e3a\u5947\u7279\u7684\u529f\u592b\uff0c\u540d\u4e3a\u201c\u8681\u8bed\u4f20\u97f3\u201d\u6765\u544a\u8b66\uff0c\u5f53\u5373\u4e5f\u7528 \u201c\u4f20\u97f3\u5165\u5bc6\u201d\u7684\u529f\u592b\uff0c\u4ee5\u4e00\u53e3\u771f\u6c14\uff0c\u628a\u8bdd\u58f0\u903c\u6210\u4e00\u7ebf\uff0c\u76f4\u5411\u53d1\u8bdd\u4e4b\u4eba\u9690\u8eab\u4e4b\u5904\u9001\u53bb\u9053\uff1a\u201c\u9601 \u4e0b\u662f\u654c\u662f\u53cb\uff1f\u4f55\u4e0d\u73b0\u8eab\u76f8\u89c1\uff1f\u201d \u4f46\u8fde\u95ee\u6570\u58f0\uff0c\u5374\u5982\u77f3\u6c89\u5927\u6d77\uff0c\u6e3a\u65e0\u56de\u97f3\uff01 \u96f7\u5927\u53d4\u5929\u751f\u50b2\u9aa8\uff0c\u5c55\u767d\u66f4\u662f\u5b81\u6298\u4e0d\u5f2f\uff0c\u4e8c\u4eba\u4e0d\u7ea6\u800c\u540c\u5730\uff0c\u4ece\u9ad8\u5899\u4e0a\u98d8\u8eab\u5165\u5185\uff0c\u5fc3\u8bf4\uff1a \u201c\u4f60\u6108\u662f\u5927\u8a00\u5413\u4eba\uff0c\u6211\u504f\u8981\u8bd5\u8bd5\uff0c\u770b\u770b\u8fd9\u5ea7\u9662\u843d\u4e2d\u6709\u4f55\u5389\u5bb3\uff1f\u2026\u2026\u201d \u8339\u8001\u9556\u5934\u3001\u6155\u5bb9\u7ea2\u60f3\u963b\u6b62\u5df2\u6765\u4e0d\u53ca\uff0c\u4e5f\u53ea\u6709\u968f\u540e\u7eb5\u843d\u9662\u4e2d\uff01 \u201c\u592a\u767d\u53cc\u9038\u201d\u53ca\u201c\u7965\u9e9f\u516c\u5b50\u201d\u4e5f\u4e0d\u7518\u4eba\u540e\uff0c\u5148\u540e\u5411\u9662\u4e2d\u7eb5\u843d\uff01 \u8c01\u77e5\u53ea\u5c55\u767d\u4e0e\u96f7\u5927\u53d4\u4e8c\u4eba\u811a\u5148\u843d\u5730\uff0c\u7a81\u542c\u4e00\u9635\u8b66\u94c3\u72c2\u9e23\uff01 \u201c\u53ee\uff01\u94c3\uff01\u94c3\uff01\u2026\u2026\u201d\u72c2\u6025\u800c\u54cd\u4eae\u7684\u94c3\u58f0\uff0c\u5728\u9759\u591c\u91cc\u542c\u6765\uff0c\u5206\u5916\u523a\u8033\uff01 \u52b2\u98ce\u7834\u7a7a\uff0c\u4e00\u6392\u786c\u6bcf\uff0c\u72b9\u5982\u6025\u98ce\u9aa4\u96e8\uff0c\u5728\u6025\u94c3\u58f0\u4e2d\u731b\u5411\u6570\u4eba\u5468\u8eab\u5c04\u6765\uff01 \u4e0d\u8fc7\uff0c\u8fd9\u4e00\u6b21\u6bd4\u5728\u201c\u8c79\u7a81\u5c71\u5e84\u201d\u66f4\u662f\u731b\u7136\uff0c\u4e14\u6709\u8b66\u94c3\u52a9\u5a01\uff0c\u94c3\u58f0\u4e2d\u5939\u7740\u4e71\u7bad\uff0c\u4f7f\u4eba\u5fc3\u795e \u4ff1\u9707\uff01 \u597d\u5728\u4e03\u4e2a\u4eba\u7684\u6b66\u529f\u90fd\u4e0d\u6bd4\u5bfb\u5e38\uff0c\u867d\u7136\u6765\u5f97\u7a81\u7136\uff0c\u4e14\u53c8\u591a\u53c8\u731b\uff0c\u4f46\u5728\u4e03\u4eba\u5341\u56db\u53ea\u624b\u638c\uff0c\u4e00 \u9f50\u6325\u62cd\u4e4b\u4e0b\uff0c\u6f2b\u7a7a\u4e71\u7bad\u7eb7\u7ea7\u5760\u5730\uff0c\u7ade\u672a\u4f24\u5230\u4e00\u4eba\uff01 \u4e03\u4eba\u5df2\u77e5\u88ab\u4eba\u5bb6\u53d1\u73b0\uff0c\u4f46\u5bf9\u65b9\u65e0\u4e00\u4eba\u9732\u9762\uff0c\u8fd9\u53cd\u800c\u663e\u5f97\u66f4\u52a0\u6050\u6016\u5413\u4eba\uff0c\u51a5\u51a5\u4e2d\u4eff\u4f5b\u6709\u65e0 \u6570\u53ea\u773c\u775b\u6b63\u77aa\u89c6\u7740\u4ed6\u4eec\u7684\u4e00\u4e3e\u4e00\u52a8\u3002 \u96f7\u5927\u53d4\u9996\u5148\u4e0d\u8010\uff0c\u6c89\u58f0\u559d\u9053\uff1a\u201c\u5929\u4f5b\u638c\u2019\u96f7\u9707\u8fdc\u9020\u8bbf\uff0c\u6709\u79cd\u7684\u73b0\u8eab\u76f8\u89c1\uff01\u201d \u9ed1\u6697\u4e2d\u53ea\u4f20\u6765\u6570\u58f0\u201c\u5403\u5403\u201d\u51b7\u7b11\uff01\u51b7\u7b11\u7684\u58f0\u97f3\u4e0d\u5927\uff0c\u5374\u5bd2\u98ce\u6c81\u9aa8\uff0c\u4ee4\u4eba\u5468\u8eab\u8d77\u9e21\u76ae\u7599 \u7629\uff01 \u96f7\u5927\u53d4\u5927\u5ffd\uff0c\u201c\u5929\u4f5b\u7edd\u638c\u201d\u7acb\u51fa\uff0c\u4e00\u62db\u201c\u897f\u65b9\u63a5\u5f15\u201d\uff0c\u6325\u638c\u5411\u90a3\u51b7\u7b11\u58f0\u4f20\u6765\u4e4b\u5904\u6253\u53bb\uff01 \u201c\u5929\u4f5b\u638c\u201d\u5a01\u529b\u5f3a\u5927\uff0c\u4e00\u638c\u5288\u51fa\uff0c\u53ea\u89c1\u52b2\u98ce\u72c2\u5578\uff0c\u76f4\u5411\u6697\u5f71\u91cc\u5377\u53bb\uff01 \u201c\u6ce2\u201d\u7684\u4e00\u58f0\u8106\u54cd\uff0c\u90a3\u5f3a\u5927\u638c\u98ce\u8fc7\u5904\uff0c\u5374\u5947\u5f02\u5730\u7206\u5f00\u4e86\u4e00\u84ec\u706b\u82b1\uff01 \u90a3\u84ec\u706b\u82b1\u5982\u8fc7\u5e74\u8fc7\u8282\u6240\u71c3\u653e\u7684\u7130\u706b\uff0c\u7eda\u70c2\u8000\u76ee\uff0c\u72b9\u5982\u4e00\u84ec\u5149\u96e8\u822c\u56db\u6563\u5f00\u6765\uff01 \u5149\u96e8\u6563\u540e\uff0c\u968f\u4e4b\u4e00\u84ec\u8f7b\u70df\uff0c\u70df\u96fe\u7f14\u7ed5\u4e2d\u73b0\u51fa\u4e00\u4e2a\u957f\u53d1\u63a9\u9762\u7684\u602a\u4eba\uff0c\u6447\u6563\u6ee1\u5934\u957f\u53d1\uff0c\u9732\u51fa \u4e00\u5f20\u72de\u6076\u5982\u9b3c\u7684\u4eba\u8138\u6765\uff0c\u5411\u7740\u96f7\u5927\u53d4\u9f87\u7259\u4e00\u7b11\uff01\u90a3\u4e24\u6392\u767d\u68ee\u68ee\u7684\u7259\u9f7f\uff0c\u591c\u9ed1\u591c\u91cc\u95ea\u95ea\u53d1\u5149\uff0c \u770b\u6765\u9634\u68ee\u68ee\u5413\u4eba\uff01 \u96f7\u5927\u53d4\u4e0d\u95ee\u4e09\u4e03\u4e8c\u5341\u4e00\uff0c\u5288\u9762\u53c8\u662f\u4e00\u638c\uff0c\u76f4\u5411\u90a3\u957f\u53d1\u602a\u4eba\u6253\u4e91\uff01 \u90a3\u957f\u53d1\u602a\u4eba\u4e0d\u95ea\u4e0d\u67b6\uff0c\u5374\u968f\u7740\u96f7\u5927\u53d4\u5f3a\u52b2\u7684\u638c\u98ce\uff0c\u98d8\u7136\u9038\u51fa\u4e00\u4e08\u5f00\u59161 \u770b\u6837\u5b50\u5c31\u5982\u4e00\u5177\u865a\u65e0\u98d8\u6e3a\u7684\u5e7d\u7075\uff0c\u4f53\u65e0\u5b9e\u8d28\uff0c\u968f\u98ce\u800c\u901d1 \u96f7\u5927\u53d4\u4e00\u8def\u731b\u653b\uff0c\u867d\u672b\u4f24\u5230\u957f\u53d1\u602a\u4eba\u7684\u4e00\u6839\u6beb\u6bdb\uff0c\u4f46\u4e5f\u628a\u957f\u53d1\u602a\u4eba\u903c\u9000\u4e86\u5341\u6570\u4e08\u5f00\u5916\uff0c \u8fd9\u65f6\uff0c\u96f7\u5927\u53d4\u5df2\u6df1\u5165\u91d1\u5e9c\u8179\u5730\uff0c\u89c1\u5341\u5927\u6740\u62db\u4ecd\u672b\u903c\u4f7f\u957f\u53d1\u602a\u4eba\u51fa\u624b\uff0c\u6253\u51fa\u4e86\u771f\u706b\uff0c\u731b\u7136\u66b4\u9640 \u4e00\u58f0\uff0c\u5de6\u3001\u53f3\u3001\u524d\u3001\u540e\u63a5\u8fde\u62cd\u51fa\u56db\u638c\uff0c\u7136\u540e\u817e\u8eab\u800c\u8d77\uff0c\u9661\u7136\u5347\u9ad8\u56db\u4e08\uff0c\u7136\u540e\u9876\u4e0b\u811a\u4e0a\uff0c\u731b\u7136 \u6251\u4e86\u4e0b\u6765\uff0c\u53cc\u638c\u4ee5\u5468\u8eab\u529f\u529b\uff0c\u731b\u5411\u957f\u53d1\u602a\u4eba\u5f53\u9876\u5288\u535c1\u8fd9\u662f\u201c\u5929\u4f5b\u638c\u201d\u4e2d\u6700\u5389\u5bb3\u7684\u4e00\u5927\u6740 \u62db\uff0c\u540d\u4e3a\u201c\u4f5b\u5149\u666e\u7167\uff0c\u4e0d\u4f46\u5a01\u529b\u751a\u731b\uff0c\u800c\u4e14\u638c\u98ce\u5c71\u6d8c\uff0c\u56db\u9762\u516b\u65b9\u800c\u81f3\uff0c\u654c\u4eba\u8eb2\u65e0\u53ef\u8eb2\uff0c\u975e\u8981 \u786c\u53d7\u8fd9\u4e00\u638c\u4e0d\u53ef\uff01 \u679c\u7136\uff0c\u957f\u53d1\u602a\u4eba\u518d\u65e0\u5904\u8eb2\u95ea\uff0c\u8eab\u5f62\u8fde\u6643\uff0c\u7ade\u4f3c\u4e0d\u6562\u786c\u63a5\uff0c\u53ea\u542c\u201c\u8f70\u201d\u7684\u4e00\u58f0\u5de8\u9707\uff0c\u96f7\u5927 \u53d4\u8fd9\u4e00\u638c\u7528\u8db3\u4e86\u5468\u8eab\u7684\u529f\u529b\uff0c\u53ea\u6253\u5f97\u7802\u77f3\u6a2a\u98de\uff0c\u8349\u6298\u6811\u65ad\uff01 \u90a3\u957f\u53d1\u602a\u4eba\u773c\u7741\u7741\u88ab\u96f7\u5927\u53d4\u51fb\u5b9e\uff0c\u5374\u4ec6\u5730\u5931\u53bb\u4e86\u8e2a\u8ff9\uff0c\u53ea\u4f59\u4e0b\u4e00\u7f15\u8c08\u6de1\u7684\u8f7b\u70df\uff0c\u968f\u98ce\u56db \u6563\uff01 \u96f7\u5927\u53d4\u8eab\u5f62\u843d\u5730\uff0c\u4e0d\u7981\u76ee\u8e6c\u53e3\u5446\uff0c\u6697\u60ca\u9053\uff1a\u83ab\u975e\u8fd9\u5c0f\u5b50\u4f1a\u4f7f\u90aa\u6cd5\uff1f\u501f\u5730\u9041\u9003\u8d70\u4e86\uff1f\u201d \u201c\u82e5\u4e0d\uff0c\u5c31\u662f\u9047\u5230\u9b3c\u4e86\uff01\u773c\u7741\u7741\u4e00\u638c\u6253\u5728\u4ed6\u8eab\u4e0a\uff0c\u600e\u4e48\u5019\u7136\u5931\u53bb\u4e86\u8e2a\u8ff9\uff1f\u2026\u2026\u201d \u96f7\u5927\u53d4\u5927\u60ca\u4e4b\u4e2d\u4e3e\u76ee\u56db\u5904\u5bdf\u770b\uff0c\u4e0d\u4f46\u90a3\u786c\u6328\u4ed6\u4e00\u638c\u7684\u957f\u53d1\u602a\u4eba\u5931\u4e86\u8e2a\u8ff9\uff0c\u8fde\u5c55\u767d\u3001\u6155\u5bb9 \u7ea2\u3001\u8339\u8001\u9556\u5934\u4ee5\u53ca\u201c\u592a\u767d\u53cc\u9038\u201d\u7b49\u4eba\uff0c\u4e5f\u4e00\u4e2a\u90fd\u4e0d\u89c1\u4e86\uff01 \u96f7\u5927\u53d4\u731b\u7136\u9192\u609f\uff0c\u53ef\u80fd\u662f\u53d7\u4e86\u654c\u4eba\u201c\u8c03\u864e\u79bb\u5c71\u201d\u4e4b\u8ba1\uff0c\u5fd9\u56de\u8eab\u53bb\u60f3\u627e\u5c55\u767d\uff0c\u4e0e\u4f17\u4eba\u518d\u4f1a \u5408\u4e00\u8d77\uff0c\u8c01\u77e5\u4ed6\u521a\u4e00\u4e3e\u6b65\uff0c\u7a81\u7136\u5237\uff01\u52371\u5237\uff01\u6697\u5f71\u4e2d\u63a5\u8fde\u7a9c\u51fa\u4e09\u6761\u4eba\u5f71\uff0c\u6a2a\u8eab\u963b\u4f4f\u53bb\u8def\uff01 \u5f53\u4e2d\u4e00\u4eba\u5934\u5982\u9ea6\u6597\uff0c\u8eab\u9ad8\u5374\u4e0d\u53ca\u4e94\u5c3a\uff0c\u989d\u4e0b\u84c4\u7740\u4e00\u5c0f\u7ecd\u5c71\u7f8a\u80e1\u5b50\uff0c\u5168\u8eab\u9ed1\u8863\u52b2\u88c5\uff0c\u7ad9\u5728 \u96f7\u5927\u53d4\u9762\u524d\uff0c\u6ee1\u8138\u8f7b\u5351\u4e4b\u8272\uff01\u563f\u563f\u7b11\u9053\uff1a\u201c\u5c31\u51ed\u4f60\u8fd9\u79cd\u4eba\u7269\uff0c\u8fde\u4e00\u4e2a\u2018\u5e7b\u5f62\u70df\u2019\u6240\u5f62\u6210\u7684\u5047 \u4eba\u4e5f\u770b\u4e0d\u51fa\u6765\uff0c\u4fbf\u76f2\u76ee\u653b\u6253\u4e86\u5341\u62db\uff0c\u8fd8\u6562\u53eb\u5b87\u53f7\u591c\u95ef\u2018\u5357\u6d77\u95e8\u2019\u5357\u4eac\u884c\u8f95\u603b\u575b\uff0c\u60f3\u5fc5\u662f\u6d3b\u5f97 \u4e0d\u8010\u70e6\u4e86\uff1f\u8fd8\u4e0d\u675f\u624b\u5c31\u7f1a\uff0c\u96be\u9053\u8fd8\u8981\u5927\u7237\u8d39\u4e8b\u5417\uff1f\u201d \u96f7\u5927\u53d4\u8001\u8138\u4e00\u7ea2\uff0c\u60f3\u4e0d\u5230\u81ea\u5df1\u521a\u624d\u731b\u653b\u4e86\u534a\u5929\u7684\u957f\u53d1\u602a\u4eba\u7adf\u662f\u4e00\u79cd\u70df\u96fe\u5e7b\u5316\u7684\u5047\u4eba\uff0c\u4f46 \u4e5f\u66f4\u5f62\u6fc0\u6012\uff0c\u6012\u9053\uff1a\u201c\u9601\u4e0b\u5927\u8a00\u4e0d\u60ed\uff0c\u60f3\u5fc5\u662f\u4e2a\u4eba\u7269\u4e86\uff01\u5148\u62a5\u540d\u4e0a\u6765\uff0c\u96f7\u5927\u7237\u2018\u5929\u4f5b\u638c\u2019 \u4e0b\uff0c\u4e5f\u4e0d\u6b7b\u65e0\u540d\u4e4b\u9b3c\uff01\u201d\u5927\u5934\u58ee\u6c49\u9762\u8272\u2014\u6c89\uff0c\u9053\uff1a\u544a\u8bc9\u4f60\u4e5f\u53eb\u4f60\u6b7b\u5f97\u660e\u767d\uff0c\u5927\u7237\u662f\u2018\u5357\u6d77 \u95e8\u201d\u9b54\u9b3c\u5c9b\u516b\u5996\u2019\u7684\u8001\u5927\uff0c\u2018\u5927\u5934\u9b3c\u738b\u2019\u7533\u516c\u660e\uff01\u4f60\u5c31\u7eb3\u547d\u6765\u5427\uff01\u201d \u8bf4\u7740\u8bdd\uff0c\u8eab\u5f62\u4e00\u95ea\uff0c\u5feb\u5982\u98d8\u98ce\uff0c\u6b3a\u8fd1\u96f7\u5927\u53d4\u9762\u524d\uff0c\u8fce\u9762\u5c31\u662f\u4e00\u4e8b\uff01 \u96f7\u5927\u53d4\u89c1\u4ed6\u8bf4\u6253\u5c31\u6253\uff0c\u800c\u4e14\u8eab\u6cd5\u5feb\u5f97\u51fa\u5947\uff0c\u9661\u7136\u5927\u559d\u4e00\u58f0\uff1a\u201c\u6765\u5f97\u597d\uff01\u201d \u96f7\u5927\u53d4\u6697\u6697\u5403\u60ca\uff0c\u60f3\u4e0d\u5230\u201c\u5357\u6d77\u95e8\u201d\u4e0b\u6b66\u529f\u5982\u6b64\u8be1\u5f02\u4e0d\u6d4b\uff0c\u62db\u6cd5\u8def\u6570\uff0c\u5747\u662f\u89c1\u6240\u672a\u89c1\u4e4b \u5b66\uff01 \u96f7\u5927\u53d4\u5403\u7d27\uff0c\u90a3\u201c\u5927\u5934\u9b3c\u738b\u201d\u610f\u72b9\u672a\u8db3\uff0c\u52a8\u7740\u624b\u5411\u4e00\u65c1\u89c2\u6218\u7684\u4e24\u4e2a\u52b2\u88c5\u5927\u6c49\u53eb\u9053\uff1a\u201c\u4f60 \u4eec\u4fe9\u8fd8\u770b\u4ec0\u4e48\uff1f\u8fd8\u4e0d\u4e0a\u524d\u5e2e\u52a9\u5927\u7237\u628a\u8fd9\u8001\u5339\u592b\u6599\u7406\u4e86\uff01\u201d \u90a3\u4e24\u4e2a\u52b2\u88c5\u5927\u6c49\u7acb\u5373\u731b\u6251\u800c\u4e0a\uff01\u672c\u6765\u4ec5\u662f\u4e00\u4e2a\u201c\u5927\u5934\u9b3c\u738b\u201d\u96f7\u5927\u53d4\u5df2\u611f\u4e0d\u652f\uff0c\u5982\u4eca\uff0c\u53c8 \u52a0\u4e0a\u4e24\u4e2a\u52a9\u624b\uff0c\u96f7\u5927\u53d4\u66f4\u611f\u5230\u5de6\u53f3\u652f\u7ecc\uff0c\u7acb\u523b\u9677\u4e8e\u5371\u673a\u4e4b\u4e2d\u2026\u3002\u2019 \u7b2c\u56db\u5341\u516b\u7ae0\u3000\u5f80\u4e8b\u60a0\u60a0>> \u53e4\u9f99\u300a\u5251\u5ba2\u884c\u300b \u7b2c\u56db\u5341\u516b\u7ae0\u3000\u5f80\u4e8b\u60a0\u60a0 \u4e14\u8bf4\u5c55\u767d\u3001\u6155\u5bb9\u7ea2\u7b49\u4eba\u89c1\u96f7\u5927\u53d4\u9047\u654c\uff0c\u624d\u5f80\u524d\u4e00\u51d1\uff0c\u60f3\u51fa\u624b\u76f8\u52a9\uff0c\u5ffd\u7136\u5fae\u98ce\u8bbd\u7136\uff0c\u6570\u9053 \u6781\u7ec6\u7684\u81ea\u7ebf\u2606\u52bf\u5982\u98de\u5929\uff0c\u5206\u88ad\u6570\u4eba\u9762\u95e8\uff01 \u51e0\u4eba\u4ee5\u4e3a\u662f\u9690\u8eab\u9ed1\u5904\u7684\u6697\u5361\u6240\u65bd\u653e\u7684\u6697\u5668\uff0c\u5404\u81ea\u6325\u638c\u8fce\u53bb\uff01 \u6570\u9053\u5f3a\u52b2\u638c\u98ce\u8fc7\u5904\uff0c\u201c\u6ce2\uff01\u6ce2\uff01\u201d\u6570\u58f0\u8106\u54cd\uff0c\u6735\u6735\u706b\u82b1\u6f2b\u7a7a\u7206\u70b8\uff0c\u72b9\u5982\u9668\u661f\u6d41\u96e8\u822c\u56db\u656c \u5f00\u6765\uff0c\u4e14\u6d53\u70df\u5f25\u6f2b\uff01 \u4f17\u4eba\u5fae\u5403\u4e00\u60ca\uff0c\u6015\u706b\u661f\u70df\u96fe\u6709\u6bd2\uff0c\u8d76\u7d27\u95ed\u4f4f\u547c\u5438\uff01 \u8c01\u77e5\u6d53\u70df\u51dd\u800c\u4e0d\u6563\uff0c\u5c31\u5730\u4e00\u5377\uff0c\u5ffd\u7136\u73b0\u51fa\u5341\u6570\u4e2a\u957f\u53d1\u6447\u6563\u7684\u72f0\u72de\u602a\u4eba\uff01 \u8fd9\u4e9b\u602a\u4eba\u884c\u6b62\u98d8\u5ffd\uff0c\u72de\u6076\u5982\u9b3c\uff0c\u51b2\u7740\u51e0\u4e2a\u4eba\u6bd7\u7259\u800c\u7b11\uff01 \u6570\u4eba\u60ca\u8be7\u4e4b\u4e2d\uff0c\u5404\u81ea\u6325\u638c\u653b\u51fb\uff0c\u90a3\u4e9b\u5e7d\u7075\u4f3c\u7684\u602a\u4eba\uff0c\u65e2\u4e0d\u63a5\u67b6\u4e5f\u4e0d\u8fd8\u51fb\uff0c\u53ea\u4e00\u5473\u5730\u98d8\u5ffd \u540e\u9000\uff0c\u6b63\u4e0e\u96f7\u5927\u53d4\u6240\u9047\u5230\u7684\u60c5\u5f62\u76f8\u4f3c\uff01 \u5c55\u767d\u6b64\u65f6\u529f\u529b\u9661\u589e\uff0c\u8db3\u53ef\u813e\u9634\u5929\u4e0b\u6b66\u6797\uff0c\u638c\u98ce\u7f62\u6c14\uff0c\u7ade\u53ef\u628a\u201c\u5357\u6d77\u95e8\u201d\u6709\u5f62\u65e0\u8d28\u7684\u201c\u5e7b \u5f62\u70df\u201d\u51dd\u7ed3\u7684\u602a\u4eba\u9707\u6563\uff0c\u53ef\u8bf4\u662f\u60ca\u4eba\u5df2\u6781\uff01 \u4f46\u5c55\u767d\u5374\u4e0d\u81ea\u77e5\uff0c\u89c1\u8fde\u65bd\u4e09\u5927\u6740\u62db\u4e4b\u540e\uff0c\u9762\u524d\u4eba\u5f71\u987f\u6e3a\uff0c\u7adf\u5f53\u573a\u4e00\u6014\u300f \u5fc3\u8bf4\uff1a\u201c\u96be\u9053\u771f\u7684\u9047\u5230\u9b3c\u4e86\uff1f\u8fd9\u4e9b\u4eba\u600e\u4f1a\u968f\u98ce\u800c\u901d\uff1f\u2026\uff0e\uff0e\u201d \u5c31\u5728\u8fd9\u4e00\u5f81\u7684\u5f53\u513f\uff0c\u7a81\u542c\u4e00\u58f0\u9634\u7b11\uff0c\u6765\u81ea\u82b1\u836b\uff01 \u5c55\u767d\u65bd\u5c55\u201c\u5343\u5e7b\u98d8\u9999\u6b65\u201d\uff0c\u51e0\u4e4e\u672b\u89c1\u4ed6\u79fb\u6b65\uff0c\u4eba\u5df2\u5982\u4e00\u7f15\u8f7b\u70df\u4e00\u822c\uff0c\u95ea\u8eab\u82b1\u836b\u4e4b\u4e2d1 \u4f46\u5947\u602a\u7684\u662f\u82b1\u836b\u4e2d\u5e7d\u9999\u6697\u9001\uff0c\u5374\u6ca1\u6709\u4e00\u4e2a\u4eba\u5f71\uff01\u4e3e\u4e16\u6b66\u6797\uff0c\u8fd8\u6709\u4ec0\u4e48\u6b65\u6cd5\uff0c\u4f1a\u6bd4\u201c\u5343\u5e7b \u98d8\u9999\u6b65\u201d\u66f4\u5feb\uff1f \u5c55\u767d\u7591\u771f\u7591\u5e7b\uff0c\u60ca\u609f\u4e00\u4f1a\uff0c\u731b\u7136\u9192\u609f\uff01\u201c\u4e0d\u8981\u4e2d\u4e86\u5bf9\u65b9\u8bf1\u654c\u4e4b\u8ba1\uff1f\u2026\u2026\u201d \u61ac\u609f\u4e4b\u4e2d\uff0c\u518d\u8fd4\u8eab\u8d76\u56de\u539f\u5148\u5730\u70b9\uff0c\u8339\u8001\u9556\u5934\u3001\u201c\u592a\u767d\u53cc\u9038\u201d\u4ee5\u53ca\u4ed6\u7684\u672a\u5a5a\u59bb\u6155\u5bb9\u7ea2\uff0c\u5747 \u5df2\u4e0d\u89c1\uff01 \u5ead\u9662\u6df1\u5904\uff0c\u5374\u9690\u9690\u4f20\u6765\u6740\u4f10\u4e4b\u58f0\uff01 \u5c55\u767d\u5173\u5fc3\u672a\u5a5a\u59bb\u7684\u5b89\u5371\uff0c\u6697\u6697\u634f\u4e86\u4e00\u628a\u51b7\u6c57\uff0c\u6025\u6025\u5411\u6709\u6740\u4f10\u4e4b\u58f0\u4f20\u6765\u7684\u65b9\u5411\u6251\u53bb\uff01 \u201c\u5343\u5e7b\u98d8\u9999\u6b65\u201d\u3001\u201c\u65e0\u8272\u65e0\u76f8\u8eab\u201d\u4ff1\u662f\u8574\u81ea\u5929\u4e0b\u7b2c\u4e00\u5947\u4e66\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\u4e2d\u7684\u7edd \u5b66\uff0c\u7ecf\u67f3\u7fe0\u7fe0\u914d\u5408\u7edd\u4ee3\u5996\u59ec\u201c\u5929\u4ed9\u9b54\u5973\u201d\u4f20\u6388\u7684\u201c\u59f9\u5973\u8ff7\u9b42\u5927\u6cd5\u201d\u966a\u7ec3\uff0c\u5c55\u767d\u624d\u80fd\u4fee\u7ec3\u800c \u6210\uff0c\u65bd\u5c55\u51fa\u6765\uff0c\u5f53\u771f\u662f\u8eab\u5f71\u4ff1\u6e3a\uff0c\u5feb\u903e\u7535\u5149\u77f3\u706b\uff01 \u6614\u65e5\u96c4\u952f\u91d1\u9675\u7684\u201c\u91d1\u5e9c\u201d\uff0c\u4eca\u65e5\u201c\u5357\u6d77\u95e8\u201d\u5165\u4fb5\u4e2d\u539f\u7684\u5927\u672c\u8425\uff0c\u4e09\u6b65\u4e00\u5c97\uff0c\u4e94\u6b65\u4e00\u54e8\uff0c \u660e\u6869\u6697\u5361\uff0c\u4e0d\u8ba1\u5176\u6570\uff0c\u8b66\u536b\u4e4b\u68ee\u4e25\uff0c\u4e0d\u4e9a\u4e8e\u5929\u7f57\u5730\u7f51\uff01 \u4f46\u7adf\u8fde\u5c55\u767d\u7684\u8eab\u5f71\u4e5f\u6ca1\u6709\u770b\u5230\uff0c\u5c55\u767d\u5df2\u7ecf\u8fde\u8d8a\u8fc7\u4e09\u5904\u5ead\u9662\uff0c\u6df1\u5165\u91d1\u5e9c\u5185\u5b85\uff01 \u5947\u602a\u7684\u662f\uff0c\u5c55\u767d\u672c\u662f\u8ffd\u5bfb\u6740\u4f10\u4e4b\u58f0\u800c\u6765\uff0c\u4f46\u5230\u4e86\u91d1\u5e9c\u5185\u5b85\uff0c\u5374\u9759\u6084\u6084\u7684\u9e21\u72ac\u4e0d\u95fb\u300f \u82b1\u6728\u6276\u758f\uff0c\u697c\u9601\u8fde\u4e91\uff0c\u5c55\u767d\u867d\u66fe\u4e09\u8fdb\u91d1\u5e9c\uff0c\u4f46\u52aa\u529b\u8fa8\u8ba4\uff0c\u4ea6\u4e0d\u77e5\u7f6e\u8eab\u662f\u4f55\u6240\u5728\uff01 \u9ed1\u591c\u661f\u7a7a\u4e4b\u4e0b\uff0c\u4e00\u5e62\u4e00\u5e62\u7684\u697c\u623f\uff0c\u5343\u7a97\u767e\u6237\uff0c\u8fde\u4e00\u4e1d\u706f\u5149\u7686\u65e0\uff0c\u900f\u51fa\u4e00\u79cd\u9634\u68ee\u6050\u6016\uff0c\u53ca \u6b7b\u6c14\u6c89\u6c89\u7684\u6c14\u6c1b\uff01 \u5c55\u767d\u6697\u6697\u5947\u602a\uff0c\u660e\u660e\u542c\u5230\u8fd9\u91cc\u6740\u58f0\u76c8\u8033\uff0c\u4e3a\u4ec0\u4e48\u8d76\u5230\u6b64\u5730\uff0c\u7adf\u4f1a\u4e0d\u89c1\u4e00\u4e1d\u4eba\u5f71\uff1f \u5c55\u767d\u5fd6\u601d\u4e2d\uff0c\u8eab\u5f62\u5982\u70df\u4e00\u822c\uff0c\u95ea\u8fc7\u4e00\u9053\u82b1\u5899\uff0c\u5c55\u73b0\u5728\u9762\u524d\u7684\u662f\u4e00\u6761\u7b14\u76f4\u7684\u752c\u8def\uff01 \u752c\u8def\u4e24\u8fb9\u662f\u683d\u690d\u7684\u6728\u68f5\u82b1\uff0c\u4fee\u526a\u5f97\u6574\u6574\u9f50\u9f50\uff0c\u72b9\u5982\u4e24\u884c\u77ed\u5899\uff0c\u906e\u4f0e\u4e86\u4e24\u65c1\u7684\u89c6\u7ebf\uff0c\u4f46\u987a \u8def\u5411\u524d\u671b\u53bb\uff0c\u5374\u4e00\u773c\u671b\u4e0d\u5230\u5c3d\u5934\uff0c\u4e0d\u77e5\u8def\u6709\u591a\u8fdc\uff1f \u752c\u8def\u6b63\u5f53\u4e2d\uff0c\u5308\u7538\u7740\u4e00\u56e2\u9ed1\u7cca\u7cca\u7684\u4e1c\u897f\uff01 \u5c55\u767d\u773c\u5149\u4f55\u7b49\u7280\u5229\uff0c\u672a\u7ecf\u7ec6\u770b\uff0c\u5373\u5df2\u8fa8\u8ba4\u51fa\u5012\u5728\u5730\u4e0b\u7684\u662f\u4e00\u4e2a\u4eba\uff01 \u5c55\u767d\u5fc3\u5934\u72c2\u8df3\uff0c\u4ee5\u4e3a\u662f\u8339\u8001\u9556\u5934\u3001\u201c\u592a\u767d\u53cc\u9038\u201d\u8fd8\u662f\u81ea\u5df1\u672a\u5a5a\u59bb\u5df2\u906d\u6740\u5bb3\uff0c\u98de\u63a0\u800c\u524d\uff0c \u6beb\u672a\u8003\u8651\uff0c\u5373\u5f2f\u8170\u4e0b\u53bb\u6276\u62b1\u90a3\u5177\u5c38\u4f53\uff01 \u8c01\u77e5\u5c55\u767d\u624b\u6307\u8fd8\u672a\u6328\u5230\u5730\u4e0b\u5c38\u4f53\u7684\u8863\u8fb9\uff0c\u90a3\u5177\u770b\u6765\u6781\u8c61\u5012\u6bd9\u7684\u6b7b\u5c38\uff0c\u7a81\u7136\u53cd\u8eab\u4e00\u638c\uff0c\u76f4 \u5411\u5c55\u767d\u9762\u95e8\u6253\u6765\uff01 \u638c\u98ce\u7f61\u731b\u7edd\u4f26\uff0c\u800c\u4e14\u631f\u7740\u4e00\u80a1\u6c81\u9aa8\u5bd2\u6c14\uff01 \u5c55\u767d\u4e8b\u5148\u6beb\u65e0\u9632\u5907\uff0c\u800c\u4e14\u8ddd\u79bb\u53c8\u8fd1\uff0c\u8fd9\u4e00\u638c\u5982\u88ab\u5288\u4e0a\uff0c\u76f4\u53ef\u628a\u5c55\u767d\u5288\u4e2a\u8111\u6d46\u8fdb\u88c2\uff01 \u6240\u5e78\u5c55\u767d\u6b66\u529f\u5927\u8fdb\uff0c\u4eca\u975e\u6614\u6bd4\uff0c\u4e00\u9047\u53d8\u6545\uff0c\u611f\u5e94\u7acb\u751f\uff0c\u7f61\u731b\u638c\u98ce\u5c06\u53ca\u9762\u95e8\u4e4b\u9645\uff0c\u201c\u5343\u5e7b \u98d8\u9999\u6b65\u201d\u9661\u7136\u65bd\u51fa\uff0c\u8eab\u5f62\u7acb\u5373\u6a2a\u98d8\u4e09\u5c3a\uff0c\u6070\u597d\u8eb2\u8fc7\u51fb\u5411\u9762\u95e8\u7684\u4e00\u638c1 \u638c\u98ce\u64e6\u9762\u800c\u8fc7\uff0c\u5c55\u767d\u5fc3\u4e3a\u4e4b\u4e00\u9707\uff0c\u6a2a\u98d8\u4e09\u5c3a\u4e4b\u540e\uff0c\u4e3e\u638c\u6b32\u5288\uff01 \u8c01\u77e5\u90a3\u5367\u5728\u5730\u4e0a\u6781\u4f3c\u5c38\u4f53\u4e4b\u4eba\uff0c\u731b\u88ad\u5c55\u767d\uff0c\u4e00\u638c\u843d\u7a7a\uff0c\u4ef0\u9762\u5411\u5929\uff0c\u55b7\u51fa\u4e00\u53e3\u9c9c\u8840\uff0c\u624b\u811a \u4e00\u9635\u62bd\u6410\uff0c\u6b64\u65f6\uff0c\u624d\u662f\u5f53\u771f\u6b7b\u53bb\uff01 \u5c55\u767d\u4e00\u770b\u90a3\u6b7b\u53bb\u4e4b\u4eba\uff0c\u9762\u8c8c\u6e05\u79c0\uff0c\u5e74\u7eaa\u751a\u8f7b\uff0c\u8eab\u4e0a\u5374\u53ea\u6709\u4e00\u53ea\u53f3\u81c2\uff0c\u6b63\u662f\u6570\u6b21\u4e0e\u81ea\u5df1\u4f5c \u5bf9\u5e76\u88ab\u81ea\u5df1\u5251\u65ad\u5de6\u81c2\u7684\u201c\u5c0f\u9752\u86a8\u201d\u76c2\u5982\u840d\uff01 \u4e0d\u77e5\u201c\u5c0f\u9752\u86a8\u201d\u88ab\u4f55\u4eba\u6240\u4f24\uff1f\u770b\u4ed6\u4e34\u9646\u6b7b\u4e4b\u524d\u51f6\u72e0\u4e4b\u8272\uff0c\u5e76\u5728\u6fd2\u6b7b\u4e4b\u9645\uff0c\u4e0d\u60dc\u81ea\u901f\u5176 \u6b7b\uff0c\u8fd0\u96c6\u5468\u8eab\u6b8b\u4f59\u771f\u529b\u5288\u4ed6\u4e00\u638c\uff0c\u8db3\u53ef\u89c1\u5176\u6028\u6bd2\u4e4b\u6df1\uff01\u5c55\u767d\u5374\u8bef\u4ee5\u4e3a\u96f7\u5927\u53d4\u6216\u662f\u8339\u8001\u9556\u5934 \u201c\u592a\u767d\u53cc\u9038\u201d\uff0c\u4ee5\u53ca\u81ea\u5df1\u672a\u5a5a\u59bb\u6155\u5bb9\u7ea2\u95ef\u81f3\u6b64\u5904\uff0c\u6253\u4f24\u4e86\u201c\u5c0f\u9752\u86a8\u201d\uff0c\u5f53\u5373\u987a\u7740\u752c\u8def\u5411\u524d\u8ffd \u5929\uff01 \u8d70\u5b8c\u957f\u957f\u7684\u4e00\u6761\u5357\u8def\uff0c\u524d\u9762\u5374\u662f\u4e00\u4e2a\u5360\u5730\u9887\u5e7f\u7684\u9662\u843d\uff0c\u9662\u843d\u4e4b\u4e2d\u6a2a\u4e03\u7ad6\u516b\uff0c\u8eba\u6ee1\u4e86\u4e00\u9662 \u5b50\u7684\u5c38\u4f53\uff01 \u53ea\u89c1\u65ad\u6208\u6b8b\u80a2\uff0c\u8840\u8165\u904d\u5730\uff0c\u771f\u662f\u60e8\u4e0d\u5fcd\u7779\uff01 \u5c55\u767d\u7acb\u523b\u65ad\u5b9a\u4e0d\u662f\u96f7\u5927\u53d4\u3001\u8339\u8001\u9556\u5934\u3001\u201c\u592a\u767d\u53cc\u9038\u201d\u4ee5\u53ca\u81ea\u5df1\u672a\u5a5a\u59bb\u6155\u5bb9\u7ea2\u6240\u4e3a\u3002\u56e0\u4e3a \u90a3\u4e94\u4eba\u65e0\u8bba\u5982\u4f55\u4e0d\u4f1a\u6709\u5982\u6b64\u6b8b\u9177\u7684\u624b\u6bb5\uff01 \u9662\u843d\u4e4b\u4e2d\uff0c\u8fce\u9762\u6709\u4e00\u6708\u95e8\uff0c\u501f\u7740\u6ee1\u5929\u7e41\u5c4b\u7684\u5fae\u5149\uff0c\u53ef\u770b\u6e05\u6708\u95e8\u4e0a\u9898\u7740\u201c\u6021\u60c5\u9662\u201d\u4e09\u4e2a\u5927 \u5b87\uff01 \u5de6\u53f3\u4e24\u53a2\u697c\u623f\u90fd\u662f\u6f06\u9ed1\uff0c\u552f\u6709\u8fce\u9762\u697c\u623f\u4e2d\u5c04\u51fa\u706f\u5149\u3002\u697c\u7a97\u7edb\u4e91\u8f7b\u7eb1\u4e2d\uff0c\u70db\u5f71\u6447\u7ea2\uff0c\u5fae\u95fb \u8863\u88d9\u88c2\u5e1b\u4e4b\u58f0\uff0c\u53ca\u5403\u5403\u7b11\u58f0\uff01 \u5c55\u767d\u6697\u6697\u7eb3\u7f55\uff0c\u83ab\u975e\u5728\u8fd9\u6ee1\u5730\u8840\u8165\u7684\u9662\u843d\u4e4b\u4e2d\uff0c\u697c\u623f\u91cc\u8fd8\u6709\u5c0f\u513f\u5973\u706f\u4e0b\u88c1\u8863\uff1f \u4e8b\u60c5\u5206\u660e\u6709\u70b9\u8e4a\u8df7\uff0c\u5c55\u767d\u65bd\u5c55\u201c\u65e0\u8272\u65e0\u76f8\u8eab\u6cd5\u201d\uff0c\u4eba\u5982\u4e00\u7f15\u8f7b\u70df\u822c\uff0c\u8e51\u8db3\u6f5c\u8e2a\uff0c\u63a9\u81f3\u7a97 \u524d\uff0c\u501f\u7740\u7eb1\u7a97\u5411\u623f\u5185\u770b\u53bb\uff01 \u8fd9\u79cd\u7edb\u7eb1\u7a97\uff0c\u672c\u662f\u4e00\u79cd\u7ec7\u8863\u7ec6\u7ee2\u505a\u6210\uff0c\u975e\u5bcc\u8d35\u4eba\u5bb6\u7528\u4e0d\u8d77\uff0c\u767d\u5929\u4ece\u5c4b\u5185\u671b\u5916\u8fb9\uff0c\u4e00\u76ee\u4e86 \u7136\uff0c\u9662\u5185\u82b1\u6811\u53ca\u6765\u5f80\u884c\u4eba\uff0c\u5747\u5386\u5386\u5728\u76ee\uff0c\u7740\u5728\u5916\u8fb9\u770b\u5c4b\u5185\u60c5\u5f62\u5374\u65e0\u6cd5\u770b\u5230\uff0c\u8fd9\u672c\u662f\u806a\u660e\u4eba\u8bbe \u8ba1\u7684\uff0c\u8c01\u77e5\u806a\u660e\u53cd\u88ab\u806a\u660e\u8bef\uff0c\u5230\u4e86\u591c\u95f4\uff0c\u5374\u9002\u5f97\u5176\u53cd\uff0c\u91cc\u660e\u5916\u6697\uff0c\u5c4b\u5185\u770b\u5916\u8fb9\u770b\u4e0d\u5230\uff0c\u5916\u8fb9 \u770b\u5c4b\u5185\u90e8\u770b\u5f97\u975e\u5e38\u6e05\u695a\uff01 \u662f\u4ee5\u5c55\u767d\u63a9\u81f3\u7a97\u524d\uff0c\u5bf9\u5ba4\u5185\u60c5\u5f62\u5df2\u662f\u4e00\u89c8\u65e0\u9057\uff01 \u4f46\u5c55\u767d\u4e0d\u770b\u8fd8\u597d\uff0c\u8fd9\u4e00\u770b\u4e0d\u7531\u5f53\u573a\u6014\u4f4f\u2026\u2026\u3002 \u539f\u6765\u5ba4\u4e2d\u592e\uff0c\u7acb\u5730\u7a7f\u8863\u5927\u955c\u4e4b\u524d\uff0c\u6b63\u6709\u4e00\u7edd\u8272\u5c11\u5973\uff0c\u8138\u6cdb\u6843\u82b1\uff0c\u674f\u773c\u4e5c\u659c\uff0c\u5728\u90a3\u91cc\u5f20\u81c2 \u65cb\u8170\uff0c\u5927\u8df3\u5176\u8131\u8863\u8273\u821e\uff01 \u90a3\u7edd\u8272\u5c11\u5973\uff0c\u98ce\u534e\u7edd\u4ee3\uff0c\u914d\u5408\u7740\u6b65\u4f10\u7684\u8282\u594f\uff0c\u6446\u81c2\u98a4\u4e73\uff0c\u968f\u7740\u4e24\u6761\u7c89\u81c2\u626c\u5904\uff0c\u8eab\u4e0a\u8f7b\u7eb1 \u4f3c\u7684\u8749\u8863\uff0c\u5df2\u6761\u6761\u6495\u788e\uff01 \u6bcf\u6495\u4e0b\u4e00\u6761\u8863\u886b\uff0c\u5373\u53d1\u51fa\u4e00\u58f0\u88c2\u5e1b\u7684\u8f7b\u54cd\uff0c\u968f\u5373\u5a07\u8eaf\u4f5c\u4e00\u4e2a\u56de\u65cb\uff01 \u6b64\u65f6\uff0c\u90a3\u7edd\u8272\u5c11\u5973\u5468\u8eab\u8863\u886b\u5df2\u5927\u90e8\u6495\u4e0b\uff0c\u7247\u7247\u8f7b\u7eb1\u4f5c\u8774\u8776\u98de\u6563\uff0c\u706f\u5149\u4e0b\u5df1\u66b4\u9732\u51fa\u5979\u66f2\u7ebf \u73b2\u73d1\u7684\u987e\u4f53\uff0c\u53ea\u89c1\u6447\u66f3\u751f\u59ff\uff0c\u5f53\u771f\u662f\u7f8e\u8272\u5df2\u6781\uff01 \u5c0f\u697c\u5185\u6625\u8272\u65e0\u8fb9\uff01 \u800c\u80cc\u7740\u7a97\uff0c\u5374\u7ad9\u5b9a\u4e00\u4e2a\u767d\u8863\u5c11\u5e74\uff01 \u8fd9\u767d\u8863\u5c11\u5e74\uff0c\u624b\u6447\u94f6\u6247\uff0c\u5fd8\u795e\u5730\u76ef\u7740\u90a3\u8131\u5f97\u7389\u4f53\u4e0d\u5269\u5bf8\u7f15\u7684\u7edd\u8272\u5c11\u5973\uff0c\u5468\u8eab\u6bcf\u4e00\u5bf8\u808c\u80a4 \u90fd\u653e\u5c04\u7740\u8bf1\u60d1\u7684\u5149\uff0c\u4e0d\u4f4f\u5730\u6447\u5934\u6643\u8111\uff0c\u5634\u4e2d\u53c8\u4e0d\u65ad\u53d1\u51fa\u6deb\u8361\u5df2\u6781\u7684\u5403\u5403\u6deb\u7b11\uff01 \u8fd9\u5927\u51fa\u610f\u5916\u7684\u60c5\u51b5\uff0c\u4f7f\u5c55\u767d\u60ca\u695e\u4e0d\u5df2\uff01 \u5c24\u5176\u770b\u6e05\u695a\u90a3\u72c2\u70ed\u5730\u5927\u8df3\u8131\u8863\u821e\u66f2\u7eaf\u8272\u5c11\u5973\uff0c\u7adf\u662f\u91d1\u5e9c\u5343\u91d1\u3001\u6709\u7740\u201c\u6c5f\u5357\u7b2c\u4e00\u7f8e\u4eba\u201d\u4e4b \u79f0\u7684\u91d1\u5f69\u51e4\uff0c\u66f4\u611f\u4e0d\u89e3\u300f \u518d\u770b\u90a3\u767d\u8863\u5c11\u5e74\uff0c\u867d\u7136\u4e0d\u80fd\u770b\u5230\u4ed6\u7684\u9762\u8c8c\uff0c\u4f46\u4ece\u5176\u80cc\u5f71\u53ca\u5176\u624b\u4e2d\u62ff\u7684\u94f6\u6247\u5224\u65ad\uff0c\u5fc5\u662f\u90a3 \u6709\u7740\u201c\u9f99\u795e\u592a\u5b50\u201d\u4e4b\u79f0\u7684\u201c\u5357\u6d77\u5c11\u541b\u201d\uff01 \u6b64\u65f6\uff0c\u91d1\u5f69\u51e4\u5df2\u628a\u5468\u8eab\u8863\u886b\u6495\u5f97\u5bf8\u7f15\u4e0d\u5269\uff0c\u800c\u90a3\u201c\u5357\u6d77\u5c11\u541b\u201d\uff0c\u5df2\u628a\u624b\u4e2d\u94f6\u6247\u6298\u8d77\u63d2\u5728 \u8863\u9886\uff0c\u5403\u5403\u6deb\u7b11\u7740\u4f38\u51fa\u53cc\u624b\u62e5\u62b1\u91d1\u5f69\u51e4\u8d64\u88f8\u7684\u80f4\u4f53\uff0c\u5634\u4e2d\u5e76\u6f2b\u541f\u9053\uff1a\u201c\u82e5\u975e\u7fa4\u7389\u5c71\u5934\u89c1\uff0c\u4f1a \u5411\u7476\u53f0\u6708\u4e0b\u9022\uff01\u7f8e\u4eba\u513f\uff0c\u6211\u4e0e\u4f60\u5171\u8d74\u9633\u53f0\u4e91\u96e8\u68a6\uff0c\u4f60\u518d\u4e0d\u80fd\u591a\u60c5\u80dc\u4f3c\u65e0\u60c5\u2026\u2026\u201d \u5c55\u767d\u7a81\u89c1\u91d1\u5f69\u51e4\u773c\u5149\u8ff7\u832b\uff0c\u4f3c\u662f\u795e\u601d\u4e0d\u5c5e\uff0c\u9661\u7136\u660e\u767d\u91d1\u5f69\u51e4\u53ef\u80fd\u662f\u88ab\u8ff7\u836f\u5931\u53bb\u672c \u6027\uff01\u2026\u2026 \u60f3\u5230\u81ea\u5df2\u5367\u75c5\u91d1\u5e9c\uff0c\u91d1\u5f69\u51e4\u4eb2\u5f85\u6c64\u836f\u4e4b\u60c5\uff0c\u53c8\u60f3\u5230\u81ea\u5df1\u9677\u4e8e\u201c\u77f3\u77f6\u5927\u9635\u201d\uff0c\u91d1\u5f69\u51e4\u5192\u6b7b \u76f8\u6551\u4e4b\u6069\uff0c\u773c\u770b\u91d1\u5f69\u51e4\u6e05\u767d\u5973\u513f\u8eab\uff0c\u5c06\u8981\u53d7\u5230\u6cbe\u6c61\uff0c\u518d\u4e5f\u4e0d\u8003\u8651\u540e\u679c\uff0c\u731b\u7136\u4e00\u638c\uff0c\u628a\u7edb\u7eb1\u7a97 \u9707\u4e86\u4e2a\u7c89\u788e\uff0c\u4eba\u4e5f\u968f\u7740\u63a0\u8fdb\u623f\u4e2d\uff01 \u201c\u9f99\u795e\u592a\u5b50\u201d\u6b63\u5728\u9971\u9910\u79c0\u8272\u4e4b\u540e\uff0c\u8272\u6388\u795e\u4e0e\uff0c\u51c6\u5907\u767b\u53f0\u5165\u6e2f\u4e4b\u65f6\uff0c\u60f3\u4e0d\u5230\u4f1a\u6709\u4eba\u8d38\u7136\u95ef \u4e86\u8fdb\u6765\uff0c\u731b\u7136\u8f66\u8f6c\u8eab\u5b50\uff0c\u89c1\u662f\u5c55\u767d\uff0c\u8138\u4e0a\u9661\u7136\u4e00\u60ca\uff01\u2026\u2026 \u4f46\u77ac\u5373\u5e73\u9759\u4e0b\u6765\uff0c\u8c32\u8be1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u662f\u5c55\u5144\uff01\u2018\u9b3c\u8138\u5a07\u5a03\u2019\u5df2\u8ba9\u4e0e\u5c55\u5144\u62bd\u4e86\u5934 \u7b79\uff0c\u603b\u4e0d\u80fd\u518d\u6765\u7834\u574f\u672c\u592a\u5b50\u7684\u597d\u4e8b\u5427\uff1f\uff0e\uff0e\uff0e\uff0e\uff0e\u201d \u5c55\u767d\u51b7\u7b11\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u5802\u5802\u7684\u2018\u9f99\u795e\u592a\u5b50\u2019\uff0c\u7adf\u4e5f\u662f\u8fd9\u79cd\u65bd\u5c55\u8ff7\u836f\u91c7\u82b1\u7684\u4e0b\u4e09\u6d41\u6deb \u8d3c\uff01\u2026\u2026\u201d \u201c\u9f99\u795e\u592a\u5b50\u201d\u8138\u4e0a\u7b11\u5bb9\u500f\u6536\uff0c\u53cd\u624b\u53d6\u51fa\u9886\u5185\u94f6\u6247\uff0c\u201c\u5237\u201d\u7684\u4e00\u58f0\uff0c\u731b\u5411\u5c55\u767d\u201c\u7709\u5fc3\u201d\u91cd \u7a74\u6572\u67651 \u4f46\u5c55\u767d\u81ea\u4ece\u8d2f\u901a\u4e86\u5929\u4f5b\u7edd\u5b66\uff0c\u6b66\u529f\u7cbe\u8fdb\uff0c\u8db3\u53ef\u7765\u7768\u5929\u4e0b\u6b66\u6797\uff0c\u201c\u9f99\u795e\u592a\u5b50\u201d\u51fa\u624b\u731b\u88ad\uff0c \u5c55\u767d\u7528\u4e0d\u7740\u601d\u7d22\uff0c\u7acb\u751f\u53cd\u5e94\uff0c\u811a\u8e29\u201c\u5343\u5e7b\u98d8\u9999\u6b65\u201d\uff0c\u4eba\u5df2\u98d8\u51fa\u4e09\u5c3a\uff0c\u540c\u65f6\uff0c\u53f3\u624b\u5019\u4f38\uff0c\u5c31\u5728 \u8eb2\u62db\u907f\u62db\u4e4b\u95f4\uff0c\u4e00\u62db\u201c\u5378\u5173\u70b9\u5143\u201d\uff0c\u8fc5\u5411\u201c\u9f99\u795e\u4f17\u5b50\u201d\u6267\u6247\u53f3\u624b\u5173\u8282\u9501\u53bb\uff01 \u201c\u9f99\u795e\u592a\u5b50\u201d\u5fc3\u4e2d\u4e00\u61d4\uff0c\u4eff\u4e0d\u5230\u5c55\u81ea\u8eab\u6cd5\u62db\u5f0f\u6bd4\u81ea\u5df2\u66f4\u5feb\u3002\u4f46\u4ed6\u65e2\u6562\u7387\u4f17\u5165\u4fb5\u4e2d\u539f\uff0c\u96c4 \u56fe\u79f0\u9738\uff0c\u81ea\u4e5f\u6709\u8d85\u4e16\u7edd\u4fd7\u7684\u6b66\u529f\uff0c\u5c31\u5728\u5c55\u81ea\u53f3\u624b\u5c06\u6293\u53ca\u624b\u8155\u4e4b\u9645\uff0c\u731b\u7136\u4e00\u6c89\u8155\u5b50\uff0c\u201c\u54d7\uff01\u201d \u628a\u624b\u4e2d\u94f6\u6247\u6296\u5f00\uff0c\u4e00\u5f0f\u201c\u91ce\u6e21\u6a2a\u821f\u201d\uff0c\u94f6\u4e1d\u6247\u9762\u95ea\u8d77\u4e00\u9053\u8000\u773c\u94f6\u5149\uff0c\u631f\u7740\u52b2\u98ce\uff0c\u731b\u88ad\u5c55\u767d\u524d \u80f8\uff01 \u5c55\u767d\u8fd0\u638c\u4e00\u5c01\uff0c\u201c\u562d\u201d\u7684\u4e00\u58f0\u5927\u9707\uff0c\u5c55\u767d\u4e0a\u8eab\u5fae\u6643\uff01 \u4f46\u90a3\u201c\u9f99\u795e\u592a\u5b50\u201d\u5df2\u88ab\u9707\u9000\u4e09\u6b65\uff01 \u4ed6\u672c\u662f\u5fc3\u673a\u6781\u91cd\u4e4b\u4eba\uff0c\u4e24\u62db\u88ab\u632b\uff0c\u5df4\u77e5\u5c55\u767d\u6b66\u529f\u975e\u540c\u5c0f\u53ef\uff0c\u6050\u6015\u8d25\u5728\u5c55\u767d\u624b\u4e2d\uff0c\u574f\u4e86\u540d \u5934\uff0c\u56e0\u6b64\uff0c\u88ab\u5c55\u81ea\u4e00\u638c\u9707\u9000\u4e09\u6b65\uff0c\u501f\u52bf\u817e\u8eab\u5411\u7a97\u5916\u63a0\u53bb\uff01 \u5728\u8dc3\u51fa\u697c\u7a97\u7684\u540c\u65f6\uff0c\u201c\u4e34\u53bb\u79cb\u6ce2\u201d\uff0c\u53cd\u624b\u7529\u51fa\u4e09\u6839\u94f6\u4e1d\u6247\u9aa8\uff0c\u5206\u88ad\u5c55\u767d\u9762\u95e8\u3001\u80f8\u3001\u8179\u4e09 \u5904\u8981\u5bb3\uff01 \u5728\u94f6\u4e1d\u6247\u9aa8\u51fa\u624b\uff0c\u53e3\u4e2d\u624d\u559d\u9053\uff1a\u201c\u672c\u592a\u5b50\u5c11\u966a\u4e86\uff01\u4f46\u4f60\u4eca\u591c\u4f11\u60f3\u751f\u79bb\u6b64\u697c\uff01\u201d \u5c55\u767d\u6643\u8eab\u8eb2\u8fc7\u8fce\u80f8\u5c04\u6765\u7684\u4e09\u70b9\u94f6\u8292\uff0c\u201c\u9f99\u795e\u592a\u5b50\u2606\u5df2\u7ecf\u9038\u51fa\u697c\u5916\uff01 \u5c55\u767d\u624d\u60f3\u817e\u8eab\u8ffd\u53bb\uff0c\u5ffd\u611f\u4e00\u4e2a\u70ed\u70d8\u4f9b\u7684\u8eab\u8eaf\uff0c\u5411\u4ed6\u8eab\u4e0a\u504e\u4e86\u8fc7\u6765\uff01 \u5c55\u767d\u4e00\u56de\u5934\uff0c\u8ddf\u90a3\u70ed\u70d8\u70d8\u7684\u8eab\u8eaf\uff0c\u649e\u4e86\u4e2a\u6ee1\u6000\uff01 \u539f\u6765\u662f\u8d64\u88f8\u88f8\u7684\u91d1\u5f69\u51e4\uff0c\u5df2\u6251\u6295\u8fdb\u4ed6\u7684\u6000\u4e2d[ \u8f6f\u7389\u6e29\u9999\u62b1\u4e86\u4e2a\u6ee1\u6000\uff0c\u5c55\u767d\u5fc3\u4e2d\u5148\u662f\u4e00\u9635\u8ff7\u60af\uff0c\u7ee7\u4e4b\u4e00\u61d4\uff01 \u4ed6\u53cc\u624b\u5fd9\u53bb\u63a8\u62d2\uff0c\u8c01\u77e5\u91d1\u5f69\u51e4\u670d\u4e0b\u70c8\u6027\u6625\u836f\uff0c\u529b\u91cf\u5927\u5f97\u51fa\u5947\uff0c\u5c55\u767d\u4e00\u63a8\u6ca1\u63a8\u5f00\uff0c\u5979\u4e24\u6761 \u7c89\u81c2\u5df2\u7ecf\u5982\u94a2\u7b8d\u4e00\u822c\uff0c\u628a\u5c55\u767d\u62b1\u4e86\u4e2a\u7ed3\u5b9e\uff01 \u5c55\u767d\u89c1\u5979\u79c0\u76ee\u534a\u95ed\uff0c\u6a80\u53e3\u5fae\u5f20\uff0c\u9165\u80f8\u6025\u907d\u5730\u8d77\u4f0f\u7740\uff0c\u8d64\u88f8\u80f4\u4f53\u5982\u4e00\u6761\u86c7\u7d27\u7d27\u628a\u4ed6\u7f20\u4f4f\uff0c \u5e76\u4e14\u4e0d\u65ad\u8815\u52a8\u7740\uff0c\u6a31\u5507\u5185\u5a07\u5598\u5401\u5401\uff0c\u53d1\u51fa\u65ad\u7eed\u7684\u566b\u566b\u6664\u6664\u4e4b\u58f0\uff01\u2026\u2026 \u8fd9\u54ea\u91cc\u50cf\u662f\u5e73\u65e5\u7aef\u5e84\u7a33\u91cd\u7684\u91d1\u5e9c\u5343\u91d1\uff0c\u5206\u660e\u662f\u4e00\u4e2a\u6deb\u5987\u5a07\u5a03\u2026\u2026 \u5c55\u767d\u6697\u6068\u201c\u9f99\u795e\u592a\u5b50\u201d\u4e0b\u6d41\uff0c\u8868\u9762\u9ad8\u8d35\uff0c\u6697\u5730\u91cc\u5374\u7528\u8fd9\u79cd\u5351\u52a3\u624b\u6bb5\uff0c\u6cbe\u6c61\u5c11\u5973\u6e05\u767d\uff01 \u53ef\u662f\uff0c\u773c\u524d\u60c5\u51b5\u5374\u4f7f\u4ed6\u5c34\u5c2c\u4e07\u5206\uff0c\u5bf9\u4e00\u4e2a\u8ff7\u5931\u672c\u6027\u7684\u8d64\u88f8\u5c11\u5973\uff0c\u4ed6\u65e0\u6cd5\u4e0b\u72e0\u624b\u4f24\u4e86\u5979\uff0c \u53ef\u662f\u4e00\u65f6\u53c8\u6323\u624e\u4e0d\u5f00\u5979\u7684\u7ea0\u7f20\u2026\u2026 \u6b63\u5728\u5de6\u53f3\u4e3a\u96be\u4e4b\u9645\uff0c\u7a81\u542c\u201c\u54af\u5577\uff01\u54af\u5577\uff01\u2019\u2026\uff0e\uff0e\u201d\u4e00\u9635\u8fde\u54cd\uff0c\u95e8\u7a97\u7b49\u5904\uff0c\u5747\u843d\u4e0b\u4e00\u5757 \u539a\u7ea6\u4e94\u5bf8\u7684\u94a2\u677f\u6765\uff0c\u60f3\u5c06\u95e8\u7a97\u4e25\u5bc6\u5730\u5c01\u95ed\u8d77\u7c73\uff01 \u5c55\u767d\u5927\u5403\u4e00\u60ca\uff01\u77e5\u662f\u201c\u9f99\u795e\u592a\u5b50\u201d\u5728\u5916\u8fb9\u53d1\u52a8\u4e86\u697c\u5185\u57cb\u4f0f\u7684\u673a\u5173\uff0c\u518d\u4e5f\u4e0d\u987e\u4e00\u5207\uff0c\u4e00\u4f38 \u624b\u70b9\u4e86\u91d1\u5f69\u51e4\u7684\u201c\u7761\u7a74\u201d\uff0c\u987a\u624b\u5c06\u91d1\u5f69\u98ce\u8d64\u88f8\u7684\u80f4\u4f53\uff0c\u653e\u5728\u4e00\u4e2a\u60ac\u6709\u9526\u5e10\u7684\u5e8a\u4e0a\uff01 \u5c55\u767d\u64a4\u51fa\u80cc\u4e0a\u201c\u65e0\u60c5\u78a7\u5251\u201d\uff0c\u60f3\u780d\u7834\u94a2\u677f\u8131\u8eab\uff0c\u4f46\u95e8\u7a97\u7f1d\u9699\u91cc\uff0c\u5df2\u5192\u8fdb\u6570\u7f15\u88f4\u662f\u7684\u767d\u70df \u6765\uff01 \u90a3\u767d\u70df\u5192\u8fdb\u6765\u5f97\u5f88\u5feb\uff0c\u5239\u65f6\u5df2\u6da8\u6f2b\u5168\u5ba4\uff0c\u5c55\u767d\u867d\u7136\u53ca\u65f6\u95ed\u4f4f\u547c\u5438\uff0c\u4ecd\u611f\u5230\u4e00\u9635\u660f\u7729\uff01 \u5c55\u767d\u60f3\u4e0d\u5230\u8fd9\u767d\u70df\u8fd9\u6837\u5389\u5bb3\uff0c\u95ed\u4f4f\u547c\u5438\uff0c\u4ecd\u4e0d\u80fd\u963b\u6b62\u6bd2\u6c14\u5185\u4fb5\uff0c\u518d\u60f3\u4ee5\u624b\u4e2d\u201c\u65e0\u60c5\u78a7 \u5251\u201d\uff0c\u53bb\u780d\u7834\u5c01\u95ed\u95e8\u7a97\u7684\u94a2\u677f\uff0c\u5df2\u7ecf\u529b\u4e0d\u4ece\u5fc3\uff0c\u9893\u7136\u683d\u5012\u4e8e\u5e8a\u8fb9\uff0c\u5374\u6b63\u597d\u662f\u91d1\u5f69\u51e4\u7684\u7389\u817f\u4e4b \u524d\u3002 \u5c55\u767d\u5fc3\u4e2d\u660e\u767d\uff0c\u4f46\u5c31\u662f\u624b\u811a\u53d1\u8f6f\uff0c\u4e0d\u80fd\u79fb\u52a8\u2026\u2026 \u7a81\u542c\u5c4b\u5916\u4e00\u4e2a\u5973\u5b50\u7684\u58f0\u97f3\uff0c\u51b7\u51b7\u5730\u8bf4\u9053\uff1a\u201c\u5e08\u5144\uff01\u4f60\u5728\u6b64\u5904\uff0c\u53c8\u73a9\u4ec0\u4e48\u82b1\u6837\uff1f\u201d \u4e00\u4e2a\u7537\u5b50\uff0c\u4f3c\u662f\u201c\u9f99\u795e\u592a\u5b50\u201d\u7684\u58f0\u8c03\uff0c\u966a\u7b11\u9053\uff1a\u201c\u5e08\u59b9\uff01\u4f60\u5fd2\u4ee5\uff0e\u591a\u5fc3\uff01\u4e3a\u5e08\u5144\u7684\u54ea\u91cc \u6709\u4ec0\u4e48\u82b1\u6837\uff1f\u4e0d\u8fc7\u5230\u662f\u6349\u5230\u4e00\u4e2a\u5f3a\u654c\u2026\u2026\u201d \u201c\u54fc\uff01\u201d \u90a3\u5973\u5b50\u4e00\u58f0\u51b7\u547c\uff0c\u4f3c\u662f\u4e0d\u4fe1\uff0c\u9053\uff1a\u201c\u4f60\u5f53\u6211\u8fd8\u4e0d\u77e5\u9053\u5450\uff01\u5206\u660e\u4f60\u628a\u672c\u5b85\u4e3b\u4eba\u7684\u5c0f\u59d0\uff0c\u64d2 \u6765\u6b64\u697c\u4e4b\u4e2d\uff0c\u8fd8\u4f1a\u505a\u51fa\u4ec0\u4e48\u597d\u4e8b\u6765\uff01\u201d \u201c\u9f99\u795e\u592a\u5b50\u201d\u4f3c\u662f\u800c\u975e\u7684\u8bed\u6c14\uff0c\u53ea\u563b\u563b\u966a\u7b11\uff0c\u672a\u7b54\u51fa\u8bdd\u6765\u3002 \u90a3\u5973\u5b50\u53c8\u51b7\u51b7\u5730\u8bf4\uff1a\u4f60\u5728\u5bb6\u4e2d\u80e1\u4f5c\u975e\u4e3a\uff0c\u4e5f\u8fd8\u7f62\u4e86\uff0c\u6b64\u6b21\u4e0b\u4e2d\u539f\u7236\u4eb2\u5c06\u4e00\u8eab\u5927\u4efb\u6258\u4ed8\u4e8e \u4f60\uff0c\u4f60\u8fd9\u822c\u80e1\u95f9\uff0c\u6050\u6015\u6709\u8d1f\u7236\u4eb2\u6240\u6258\uff0c\u96be\u6210\u5927\u4e8b\uff01\u8fd8\u4e0d\u5feb\u628a\u623f\u95e8\u6253\u5f00\uff1f\u201d \u201c\u9f99\u795e\u592a\u5b50\u201d\u5206\u660e\u4e0d\u613f\u6253\u5f00\u623f\u95f4\uff0c\u53ea\u8d54\u7b11\u9053\uff1a\u201c\u6765\u4eba\u6b66\u529f\u4e86\u5f97\uff0c\u4e3a\u5e08\u5144\u624d\u65bd\u5c55\u2018\u9f99\u6d8e \u9999\u2019\uff0c\u60f3\u628a\u4ed6\u8ff7\u5012\uff0c\u73b0\u5728\u4e3a\u65f6\u4e0d\u591a\uff0c\u6050\u6015\u4ed6\u8fd8\u672a\u88ab\u660f\u8ff7\u8fc7\u53bb\u2026\u2026\u201d \u90a3\u5c11\u5973\u4f3c\u662f\u4e0d\u8010\uff0c\u53eb\u9053\uff1a\u201c\u4e0d\u7528\u5de7\u8a00\u9970\u8fa9\uff0c\u6253\u5f001\u201d \u201c\u9f99\u795e\u592a\u5b50\u201d\u4ecd\u5728\u63a8\u4e09\u963b\u56db\uff0c\u90a3\u5973\u5b50\u53c8\u53eb\u9053\uff1a\u201c\u4f60\u4e0d\u6253\u5f00\uff0c\u96be\u9053\u6211\u4e0d\u4f1a\u6253\u5f00\u5417\uff1f\u201d \u63a5\u7740\uff0c\u201c\u54d7\u5577\u201d\u4e00\u58f0\uff0c\u7a97\u95e8\u4e4b\u5904\u7684\u94c1\u677f\uff0c\u5df2\u81ea\u884c\u79fb\u5f00\uff01 \u6ee1\u5ba4\u767d\u70df\uff0c\u8f7b\u6613\u56db\u6563\uff01 \u90a3\u5973\u5b50\u4e00\u6296\u624b\uff0c\u6253\u51fa\u4e24\u679a\u73bb\u7483\u7403\uff0c\u201c\u6ce2\uff01\u6ce2\uff01\u201d \u4e24\u58f0\u8f7b\u70b8\uff0c\u4e00\u84ec\u9752\u9732\u95ea\u8fc7\uff0c\u6ee1\u5ba4\u767d\u70df\u7acb\u523b\u6d88\u5c3d\uff01 \u201c\u9f99\u795e\u592a\u5b50\u201d\u4e5f\u7d27\u8ddf\u4e86\u8fdb\u6765\uff0c\u8c01\u77e5\u5728\u5ba4\u5185\u56db\u5904\u4e00\u770b\uff0c\u4e0d\u7531\u5927\u60ca\u5931\u8272\uff01 \u539f\u6765\u5ba4\u5185\u7a7a\u7a7a\u5982\u4e5f\uff0c\u4e0d\u4f46\u5c55\u767d\u4e0d\u89c1\uff0c\u8fde\u90a3\u8131\u5f97\u8d64\u6761\u7cbe\u5149\u3001\u88ab\u70c8\u6027\u6625\u836f\u8ff7\u5931\u4e86\u672c\u6027\u7684\u91d1\u5f69 \u98ce\uff0c\u4e5f\u5931\u53bb\u4e86\u8e2a\u5f71\uff01 \u201c\u9f99\u795e\u592a\u5b50\u201d\u60ca\u6115\u4e0d\u5df2\uff01 \u201c\u5357\u6d77\u9f99\u5973\u201d\u5374\u6a31\u5507\u4e00\u6487\uff0c\u9053\uff1a\u201c\u54e5\u54e5\uff0c\u4f60\u56f0\u4f4f\u7684\u4eba\u5462\uff1f\u201d \u4efb\u4ed6\u201c\u9f99\u795e\u592a\u5b50\u201d\u7cbe\u660e\u76d6\u4e16\uff0c\u6b64\u65f6\u4ea6\u4e0d\u7981\u77a0\u76ee\u7ed3\u820c\uff0c\u65e0\u8a00\u4ee5\u5bf9\u2026\u2026 \u539f\u6765\u5728\u201c\u5357\u6d77\u9f99\u5973\u201d\u903c\u7740\u201c\u9f99\u795e\u592a\u5b50\u201d\u6253\u5f00\u7a97\u95e8\u94a2\u677f\u7684\u77ac\u95f4\uff0c\u5c55\u767d\u53ca\u91d1\u5f69\u51e4\u5df2\u4e3a\u4e00\u6234\u4e86 \u9b3c\u9762\u5177\u7684\u5c11\u5973\u6551\u8d70\uff01 \u5c55\u767d\u811a\u867d\u4e0d\u80fd\u4e3e\u52a8\uff0c\u4f46\u5bf9\u8fd9\u9b3c\u9762\u5c11\u5973\u5374\u719f\u6089\u5f97\u5f88\uff0c\u90a3\u6b63\u662f\u4e0e\u81ea\u5df1\u5728\u79e6\u63a8\u6cb3\u5c0f\u8239\u4e0a\u6625\u98ce\u4e00 \u5ea6\uff0c\u5728\u201c\u8c79\u7a81\u5c71\u5e84\u201d\u53c8\u88ab\u81ea\u5df2\u4e00\u53e5\u8bdd\u6c14\u8d70\u4e86\u7684\u67f3\u7fe0\u7fe0\uff01 \u5c55\u767d\u5947\u602a\u67f3\u7fe0\u7fe0\u600e\u4f1a\u5728\u6b64\u5730\u51fa\u73b0\uff1f\u53c8\u8981\u628a\u81ea\u5df1\u5e26\u4e0a\u54ea\u91cc\u53bb\uff1f\u65e0\u5948\u4ed6\u53d7\u4e86\u201c\u9f99\u6d8e\u9999\u201d\u4e4b \u6bd2\uff0c\u53e3\u4e0d\u80fd\u8a00\uff0c\u65e0\u6cd5\u53d1\u95ee\uff01 \u67f3\u7fe0\u7fe0\u8f7b\u8212\u53cc\u81c2\uff0c\u4e00\u53ea\u624b\u631f\u4e86\u5c55\u767d\uff0c\u4e00\u53ea\u624b\u631f\u4e86\u91d1\u5f69\u51e4\uff0c\u8fd9\u59d1\u5a18\u6210\u4e5f\u5201\u94bb\uff0c\u5bf9\u91d1\u5f69\u98ce\u7684 \u88f8\u4f53\u8fde\u4e00\u7247\u5e03\u4e5f\u4e0d\u52a0\u63a9\u76d6\uff0c\u5c31\u90a3\u6837\u8d64\u6761\u7cbe\u5149\uff0c\u631f\u8d77\u91d1\u5f69\u51e4\u8fc5\u5982\u95ea\u7535\uff0c\u63a0\u51fa\u7a97\u5916\uff0c\u51e0\u4e2a\u8f6c\u6298\uff0c \u6765\u81f3\u4e00\u5ea7\u5e7f\u5927\u7684\u82b1\u56ed\u4e4b\u4e2d\uff0c\u5230\u4e86\u4e00\u4e2a\u5047\u5c71\u540e\u50fb\u9759\u4e4b\u5904\uff0c\u4e22\u624b\u628a\u4e8c\u4eba\u63b7\u5728\u5730\u4e0a\uff01 \u5bf9\u7740\u5c55\u767d\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6211\u672c\u4e0d\u60f3\u6551\u4f601\u4f46\u4e3a\u4e86\u8981\u8ba4\u6e05\u695a\u4f60\u4eec\u7537\u4eba\u7684\u5634\u8138\uff0c\u6240\u4ee5\u53c8\u628a \u4f60\u6551\u51fa\u6765\u3002\u6211\u95ee\u4f60\uff0c\u4f60\u65e2\u8ddf\u6211\u505a\u6210\u592b\u59bb\uff0c\u4e3a\u4ec0\u4e48\u4ee5\u524d\u6709\u4e2a\u5a49\u513f\uff0c\u73b0\u5728\u53c8\u6709\u4e2a\u91d1\u5f69\u51e4\uff01\u4f60\u5012\u5e95 \u8ba4\u8bc6\u591a\u5c11\u5973\u4eba\uff1f\u201d \u5c55\u767d\u5728\u5c4b\u5916\u88ab\u51b7\u98ce\u4e00\u5439\uff0c\u201c\u9f99\u6d8e\u9999\u201d\u7684\u6bd2\u5df2\u89e3\u53bb\u4e0d\u5c11\uff0c\u6b64\u65f6\uff0c\u5468\u8eab\u867d\u4ecd\u53d1\u8f6f\uff0c\u4f46\u53e3\u5df2\u80fd \u8a00\uff0c\u82e6\u7b11\u9053\uff1a\u4f60\u7684\u918b\u52b2\u53ef\u771f\u4e0d\u5c0f\uff01\u4f46\u5728\u4e8b\u60c5\u672a\u5f04\u6e05\u695a\u4e4b\u524d\uff0c\u6700\u597d\u5148\u4e0d\u8981\u968f\u53e3\u4e71\u8bf4\u2026\u2026\u201d \u67f3\u7fe0\u7fe0\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u7247\u9762\u4e4b\u8a00\u4e0d\u8db3\u4fe1\uff0c\u5f85\u6211\u628a\u5979\u6551\u9192\uff0c\u5f53\u9762\u5bf9\u8d28\uff0c\u5982\u679c\u5979\u8ddf\u4f60\u6709\u4ec0\u4e48\u5173 \u7cfb\uff0c\u90a3\u65f6\uff0c\u6211\u518d\u627e\u4f60\u7b97\u8d26\uff01\u201d \u8bf4\u7740\uff0c\u4ece\u6000\u5185\u638f\u51fa\u4e00\u9897\u836f\u4e38\uff0c\u4e3a\u91d1\u5f69\u51e4\u670d\u4e86\u4e0b\u53bb\u3002 \u91d1\u5f69\u51e4\u673a\u4f36\u4f36\u8fde\u6253\u4e24\u4e2a\u51b7\u6218\uff0c\u4eba\u5df2\u6e05\u9192\u8fc7\u6765\uff0c\u51b7\u6708\u661f\u5149\u4e0b\uff0c\u89c1\u5f53\u9762\u4e00\u4e2a\u9752\u9762\u7ea2\u53d1\u7684\u6076 \u9b3c\uff0c\u81ea\u5df1\u8d64\u8eab\u88f8\u4f53\u7f6e\u8eab\u623f\u5916\uff0c\u4e0d\u7531\u5413\u6014\u4e86\uff01 \u8f6c\u773c\u770b\u5230\u5c55\u767d\uff0c\u60c5\u4e0d\u7531\u5df1\uff0c\u5a07\u547c\u4e00\u58f0\uff0c\u76f4\u5411\u5c55\u767d\u6000\u4e2d\u6251\u53bb\uff01 \u67f3\u7fe0\u7fe0\u51b7\u54fc\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8fd8\u6709\u4ec0\u4e48\u8bdd\u8bf4\uff1f\u4e8b\u5b9e\u662f\u6700\u597d\u7684\u8bc1\u660e\uff01\u201d \u8bf4\u7740\uff0c\u201c\u545b\u201d\u7684\u4e00\u58f0\uff0c\u4ece\u5c55\u767d\u80cc\u4e0a\u6346\u51fa\u201c\u65e0\u60c5\u78a7\u5251\u201d\uff0c\u5206\u5fc3\u4fbf\u5411\u91d1\u5f69\u51e4\u523a\u53bb\uff01 \u5c55\u767d\u5927\u53eb\u9053\uff1a\u201c\u6162\u7740\uff01\u201d \u67f3\u7fe0\u7fe0\u51b7\u7b11\u9053\uff1a\u201c\u600e\u4e48\uff1f\u5fc3\u75db\u4e86\u5417\uff1f\u201d \u8bf4\u7740\u58f0\u8c03\u4e00\u53d8\uff0c\u5389\u53f1\u9053\uff1a\u201c\u627e\u975e\u8981\u5f53\u7740\u4f60\u7684\u9762\uff0c\u628a\u5979\u6740\u6b7b\u4e0d\u53ef\uff01\u201d \u5389\u53f1\u58f0\u4e2d\uff0c\u624b\u4e2d\u5251\u5df2\u523a\u4e86\u51fa\u53bb\uff01 \u5c55\u767d\u6b64\u65f6\u529f\u529b\u672a\u590d\uff0c\u773c\u770b\u5979\u201c\u65e0\u60c5\u78a7\u5251\u201d\uff0c\u5df2\u5c06\u523a\u81f3\u91d1\u5f69\u51e4\u524d\u5fc3\uff0c\u81ea\u5df2\u5374\u65e0\u6cd5\u51fa\u624b\u76f8 \u6551\uff0c\u4e0d\u7531\u6c14\u5f97\u8fde\u58f0\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u6bcd\u591c\u53c9\uff01\u96be\u9053\u4f60\u6740\u6b7b\u4e00\u4e2a\u65e0\u8f9c\u7684\u5c11\u5973\uff0c\u5c31\u4f1a\u4f7f\u6211\u56de\u5fc3\u8f6c\u610f \u4e86\u5417\uff1f\u563f\u563f\uff01\u7b80\u76f4\u662f\u68a6\u60f3\uff01\u2026\u2026\u201d \u67f3\u7fe0\u7fe0\u542c\u5230\u201c\u6bcd\u591c\u53c9\u201d\u4e09\u4e2a\u5b87\uff0c\u5fc3\u4e2d\u4e00\u9707\uff0c\u731b\u7136\u5239\u4f4f\u624b\u4e2d\u5251\uff0c\u54ed\u58f0\u9053\uff1a\u201c\u4f60\u8bf4\u8c01\u662f\u6bcd\u591c \u53c9\uff1f\u201d \u5c55\u767d\u5389\u58f0\u9053\uff0c\u201c\u6211\u8bf4\u4f60\u662f\u6bcd\u591c\u53c9\uff0c\u5728\u672a\u660e\u771f\u50cf\u4e4b\u524d\uff0c\u4fbf\u65e0\u7aef\u918b\u6d77\u5174\u6ce2\uff0c\u8fa3\u624b\u6467 \u82b1\uff01\u2026\u2026\u201d \u672a\u7b49\u5c55\u767d\u8bf4\u5b8c\uff0c\u67f3\u7fe0\u7fe0\u5df2\u5a07\u8eaf\u5fae\u98a4\uff0c\u628a\u201c\u65e0\u60c5\u78a7\u5251\u201d\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u6387\u5728\u5730\u4e0b\uff0c\u63a9\u9762\u60b2 \u6ce3\uff0c\u817e\u8eab\u800c\u8d70\uff01 \u67f3\u7fe0\u7fe0\u4ece\u5c0f\u5728\u8352\u5c9b\u957f\u5927\uff0c\u867d\u7136\u5c11\u4e0d\u66f4\u4e8b\uff0c\u4f46\u5bf9\u201c\u6bcd\u591c\u53c9\u201d\u4e09\u5b87\u7684\u6076\u6bd2\u542b\u610f\uff0c\u5374\u77e5\u4e4b\u751a \u5ff5\uff01 \u539f\u6765\u5979\u7684\u5e08\u7236\u201c\u9b3c\u9762\u5a07\u5a03\uff0c\u7ea2\u7c89\u9ab7\u9ac5\u201d\u906d\u9047\u5947\u60e8\uff0c\u5979\u672c\u662f\u4e00\u4e2a\u5f03\u5973\uff0c\u964d\u751f\u4e0d\u5230\u4e00\u6708\uff0c\u5373 \u88ab\u72e0\u5fc3\u7684\u7236\u4eb2\u5f03\u5728\u4e00\u5ea7\u8352\u5e99\u4e2d\uff0c\u5e78\u4e3a\u201c\u5929\u4ed9\u9b54\u5973\u201d\u6240\u6551\uff0c\u628a\u5979\u629a\u517b\u957f\u5927\uff0c\u5e76\u6388\u4ee5\u7edd\u4e16\u6b66\u529f\uff01 \u5230\u5979\u957f\u6210\u65f6\uff0c\u7adf\u7136\u51fa\u843d\u5f97\u5982\u82b1\u4f3c\u7389\uff0c\u52a0\u4e4b\u5979\u6b66\u529f\u9ad8\u5f3a\uff0c\u5f53\u65f6\u62dc\u5012\u77f3\u69b4\u88d9\u4e0b\u7684\u9752\u5e74\u7537\u5b50\u4e0d \u77e5\u51e1\u51e0\uff01 \u6070\u5de7\u201c\u5929\u4ed9\u9b54\u5973\u201d\u53d7\u4eba\u7eb5\u5bb9\uff0c\u4e00\u65f6\u901e\u5f3a\uff0c\u65bd\u5c55\u201c\u59f9\u5973\u8ff7\u9b42\u5927\u6cd5\u201d\uff0c\u574f\u4e86\u76d6\u4e16\u5947\u4eba\u201c\u53ea\u773c \u90ce\u541b\u201d\u7684\u4fee\u884c\uff0c\u5f15\u8d77\u6b66\u6797\u516c\u6124\uff0c\u8ba4\u201c\u5929\u4ed9\u9b54\u5973\u201d\u4e3a\u90aa\u9b54\u5916\u9053\uff0c\u7fa4\u8d77\u56f4\u527f\uff0c\u81f4\u4f7f\u201c\u5929\u4ed9\u9b54\u5973\u201d \u5728\u4e2d\u539f\u6b66\u6797\u4e0d\u80fd\u7acb\u8db3\uff0c\u4ea1\u547d\u6d77\u5916\u8352\u5c9b\uff0c\u6682\u65f6\u9690\u9041\u8d77\u6765\uff01 \u201c\u5929\u4ed9\u9b54\u5973\u201d\u672c\u5584\u4e8e\u5947\u95e8\u751f\u514b\u4e4b\u672f\uff0c\u4e3a\u4e86\u9632\u8303\u4ec7\u5bb6\u8ffd\u8e2a\uff0c\u53c8\u904d\u5e03\u8ff7\u5f84\u5e7b\u5883\uff0c\u673a\u5173\u57cb\u4f0f\uff0c \u628a\u4e00\u4e2a\u4e16\u5916\u8352\u5c9b\u5e03\u7f6e\u5f97\u795e\u4ed9\u96be\u6e21\uff0c\u5012\u4e5f\u80fd\u76f8\u5b89\u4e8e\u4e00\u65f6\uff01 \u5728\u505a\u8fd9\u4e9b\u5f00\u8352\u5de5\u4f5c\u4e4b\u65f6\uff0c\u56e0\u8352\u5c9b\u7f3a\u5c11\u98df\u7269\uff0c\u5e08\u5f92\u4e8c\u4eba\u5747\u4ee5\u6d77\u4e2d\u9c7c\u867e\u4e3a\u98df\uff0c\u6070\u5de7\u5728\u90a3\u8352\u5c9b \u6d77\u8fb9\u5ca9\u77f3\u6d1e\u7a74\u4e2d\uff0c\u751f\u4ea7\u4e00\u79cd\u201c\u8840\u9cd7\u201d\uff0c\u8fd9\u79cd\u8840\u7ec4\u5bf9\u4eba\u8eab\u662f\u4e00\u79cd\u5927\u8865\uff0c\u5e08\u5f92\u4e8c\u4eba\u5403\u5f97\u591a\u4e86\uff0c\u5185 \u529f\u771f\u529b\u7adf\u7136\u5927\u589e\uff0c\u6709\u4e00\u6b21\u5e08\u5f92\u4e8c\u4eba\u65e0\u610f\u4e2d\u6349\u5230\u4e00\u53ea\u201c\u5343\u5e74\u8840\u9cd7\u201d\uff0c\u5206\u800c\u98df\u4e4b\uff0c\u7ade\u8fbe\u5230\u5bb9\u989c\u5e38 \u9a7b\u3001\u957f\u751f\u4e0d\u8001\u7684\u5730\u6b65\uff01 \u76f4\u5230\u4e8c\u767e\u4f59\u5e74\uff0c\u201c\u5929\u4ed9\u9b54\u5973\u201d\u624d\u8089\u8eab\u5750\u5316\uff0c\u65e0\u75be\u800c\u7ec8\uff0c\u501f\u5927\u4e00\u5ea7\u5c9b\u4e0a\uff0c\u53ea\u5269\u4e0b\u201c\u9b3c\u9762\u5a07 \u5a03\uff0c\u7ea2\u7c89\u9ab7\u9ac5\u201d\u4e00\u4eba\uff01 \u9002\u503c\u4e2d\u539f\u6b66\u6797\u7fa4\u96c4\u4e89\u9738\uff0c\u4e92\u76f8\u4ec7\u6740\uff0c\u4e0d\u5c11\u90aa\u9b54\u5916\u9053\u4e4b\u5f92\uff0c\u4e2d\u539f\u4e0d\u80fd\u7acb\u8db3\uff0c\u4ea1\u547d\u6d77\u5916\uff0c\u6709 \u4e0d\u5c11\u4eba\u9003\u5230\u8fd9\u8352\u5c9b\u4e0a\u6765\uff01 \u201c\u9b3c\u9762\u5a07\u5a03\u201d\u6b63\u611f\u5b64\u5bc2\u65e0\u4f9d\uff0c\u5bf9\u9003\u4ea1\u5c9b\u4e0a\u7684\u4ea1\u547d\u4e4b\u5f92\uff0c\u5747\u52a0\u4ee5\u6536\u7559\uff0c\u50cf\u6843\u82b1\u5c9b\u56db\u5996\u5987 \u201c\u6843\u82b1\u56db\u4ed9\u201d\u3001\u5cad\u5357\u5927\u76d7\u201c\u5927\u5934\u9b3c\u738b\u201d\u7b49\u7ed3\u76df\u5144\u5f1f\u516b\u4eba\uff0c\u4e5f\u5c31\u662f\u540e\u6765\u6709\u540d\u7684\u201c\u9b54\u9b3c\u5c9b\u516b \u5996\u201d\uff0c\u4ee5\u53ca\u201c\u6d77\u5916\u4e09\u715e\u201d\u7b49\u4eba\uff0c\u90fd\u6210\u4e86\u5c9b\u4e0a\u7684\u9876\u5c16\u9ad8\u624b\uff0c\u6b64\u5916\uff0c\u4e0d\u77e5\u540d\u7684\u8fd8\u4e0d\u77e5\u6709\u591a\u5c11\u3002 \u6b64\u65f6\u201c\u9b3c\u9762\u5a07\u5a03\u201d\u7231\u4e0a\u4e86\u4e00\u4e2a\u540d\u53eb\u9f99\u5578\u5929\u7684\u5c11\u5e74\u6b66\u58eb\uff0c\u4e5f\u5c31\u662f\u540e\u6765\u7684\u201c\u5357\u6d77\u4e00\u541b\u201d\u201c\u6d77 \u9f99\u795e\u201d\uff01 \u4e8c\u4eba\u6b63\u5728\u6253\u5f97\u706b\u70ed\uff0c\u9f99\u5578\u5929\u4e0d\u77e5\u4e3a\u4ec0\u4e48\u4e22\u4e0b\u201c\u9b3c\u9762\u5a07\u5a03\u201d\uff0c\u53ea\u8eab\u6f5c\u8d74\u4e2d\u539f\uff0c\u4e0d\u8f9e\u800c\u522b\uff0c \u4e00\u53bb\u5e74\u4f59\uff0c\u6ca1\u6709\u97f3\u8baf\uff01 \u201c\u9b3c\u9762\u5a07\u5a03\u201d\u81ea\u7136\u96be\u5408\uff0c\u8d76\u81f3\u4e2d\u539f\u5bfb\u627e\uff0c\u5374\u53d1\u73b0\u9f99\u5578\u5929\u5df2\u4e0e\u53e6\u5916\u4e00\u4e2a\u5973\u4eba\u6210\u4e86\u5a5a\uff0c\u800c\u4e14 \u751f\u4e0b\u4e00\u5b50\uff01 \u201c\u9b3c\u9762\u5a07\u5a03\u201d\u53c8\u5992\u53c8\u6012\uff0c\u627e\u81f3\u9f99\u5578\u5929\u4f4f\u6240\uff0c\u6ca1\u60f3\u5230\u53c8\u88ab\u9f99\u5578\u65e0\u4e00\u4e2a\u574f\u670b\u53cb\u65bd\u7528\u8ff7\u836f\uff0c\u52a0 \u4ee5\u5978\u6deb\uff01 \u9f99\u5578\u5929\u501f\u6b64\u58f0\u8a00\u4e0e\u201c\u9b3c\u9762\u5a07\u5a03\u201d\u8131\u79bb\u5173\u7cfb\uff0c\u5e76\u76f4\u65a5\u201c\u9b3c\u9762\u5a07\u5a03\u201d\u4e3a\u6deb\u8361\u5973\u5b50\uff01 \u201c\u9b3c\u9762\u5a07\u5a03\u201d\u53d7\u6b64\u523a\u4fbf\u5728\u4e2d\u539f\u6b66\u6797\u5927\u4e8b\u6740\u622e\uff0c\u800c\u4e14\u7ade\u5f53\u771f\u53d8\u6210\u6deb\u8361\u65e0\u6bd4\uff0c\u6deb\u4e71\u9752\u5e74\uff0c\u7a0d \u4e0d\u968f\u610f\uff0c\u5373\u4e88\u6740\u5bb3\uff0c\u52a0\u4e4b\u5979\u6b66\u529f\u9ad8\u5f3a\uff0c\u53c8\u5e38\u6234\u4e00\u9b3c\u9762\u5177\u51fa\u73b0\uff0c\u56e0\u6b64\u201c\u9b3c\u9762\u5a07\u5a03\uff0c\u7ea2\u7c89\u9ab7\u9ac5\u201d \u4e4b\u540d\u5927\u566a\uff01 \u540e\u6765\u5f15\u8d77\u6b66\u6797\u516c\u6124\uff0c\u96c6\u5408\u9ed1\u767d\u4e24\u9053\u6570\u5341\u9ad8\u624b\uff0c\u5728\u6cf0\u5c71\u89c2\u65e5\u5cf0\u4e00\u6218\uff0c\u628a\u5979\u6253\u843d\u4e07\u4e08\u6df1\u6e0a\uff0c \u5e78\u800c\u672a\u6b7b\uff0c\u4f46\u843d\u4e86\u4e2a\u4f24\u75d5\u7d2f\u7d2f\uff0c\u5979\u8eab\u5fc3\u4e24\u4f24\uff0c\u6f5c\u56de\u201c\u9b54\u9b3c\u5c9b\u201d\uff0c\u907f\u5c45\u4e0d\u51fa\u3002 \u9f99\u5578\u5929\u56e0\u4e3a\u593a\u5f97\u4e00\u518c\u6b66\u5b66\u79d8\u5f55\uff0c\u4e5f\u88ab\u9ed1\u767d\u4e24\u9053\u5408\u529b\u8ffd\u6740\uff0c\u4e2d\u539f\u4e0d\u80fd\u7acb\u8db3\uff0c\u643a\u59bb\u5e26\u5b50\u7b2c\u4e8c \u6b21\u6f5c\u56de\u201c\u9b54\u9b3c\u5c9b\u201d\uff01 \u4e0d\u77e5\u4ed6\u7528\u4ec0\u4e48\u529e\u6cd5\uff0c\u8bf4\u670d\u4e86\u201c\u9b3c\u9762\u5a07\u5a03\u201d\uff0c\u4e8c\u4eba\u7ade\u53c8\u8a00\u5f52\u4e8e\u597d\uff0c\u4e0e\u9f99\u5578\u5929\u7684\u524d\u59bb\u4e09\u4eba\u4f4f \u5728\u4e00\u8d77\u3002 \u800c\u4e14\uff0c\u9f99\u5578\u5929\u6839\u636e\u90a3\u672c\u6b66\u5b66\u79d8\u5f55\uff0c\u7ade\u4fee\u7ec3\u6210\u4e86\u4e00\u8eab\u7edd\u4e16\u5947\u529f\uff01\u5e76\u81ea\u547d\u4e3a\u201c\u5357\u6d77\u4e00\u541b\u201d \u201c\u6d77\u9f99\u795e\u201d\uff0c\u5f00\u521b\u201c\u5357\u6d77\u201d\u4e00\u6d3e\uff0c\u5e7f\u6536\u95e8\u5f92\uff0c\u4f0a\u7136\u6210\u4e3a\u4e00\u65b9\u9738\u4e3b\uff01 \u4ed6\u7684\u513f\u5b50\u4e5f\u957f\u5927\uff0c\u5c31\u6210\u4e3a\u201c\u5357\u6d77\u5c11\u541b\u201d\u201c\u9f99\u795e\u592a\u5b50\u201d\uff0c\u540e\u6765\u4ed6\u7684\u524d\u59bb\u53c8\u751f\u4e0b\u4e00\u5973\uff0c\u662f\u73b0 \u5728\u5165\u4fb5\u4e2d\u539f\u3001\u638c\u751f\u6740\u5927\u6743\u7684\u201c\u5357\u6d77\u9f99\u5973\u201d\uff01 \u201c\u5357\u6d77\u9f99\u5973\u201d\u62dc\u4e8c\u5a18\u201c\u9b3c\u9762\u5a07\u5a03\u201d\u4e3a\u5e08\uff0c\u201c\u9b3c\u9762\u5a07\u5a03\u201d\u4e00\u65e0\u6240\u51fa\uff0c\u8fd8\u662f\u771f\u559c\u6b22\u201c\u5357\u6d77\u9f99 \u5973\u201d\uff0c\u4f46\u4e0d\u559c\u6b22\u201c\u5357\u6d77\u5c11\u541b\u201d\uff0c\u56e0\u6b64\u201c\u5357\u6d77\u5c11\u541b\u201d\u4e43\u968f\u7236\u5b66\u827a\uff01 \u540e\u6765\u201c\u6c5f\u5357\u4e03\u4fa0\u201d\u7684\u8001\u4e03\u3001\u201c\u94f6\u6247\u5b50\u201d\u67f3\u795f\u539a\uff0c\u56e0\u4e3a\u6d1e\u5ead\u6e56\u5e95\u85cf\u5b9d\uff0c\u5144\u5f1f\u502a\u5899\uff0c\u5927\u54e5 \u201c\u9739\u96f7\u5251\u201d\u5c55\u4e91\u5929\u9047\u5bb3\uff0c\u67f3\u795f\u539a\u7684\u59bb\u5b50\u4e5f\u88ab\u4e94\u4f4d\u76df\u5144\u6240\u6740\uff0c\u60c5\u52bf\u6240\u8feb\u4e2d\u539f\u4e0d\u80fd\u7acb\u8db3\uff0c\u643a\u5e26\u5e7c \u5973\u9003\u81f3\u5357\u6d77\u201c\u9b54\u9b3c\u5c9b\u201d\uff0c\u5bc4\u4f4f\u4e8e\u5357\u6d77\u95e8\u4e0b\uff01 \u4ed6\u7684\u5973\u513f\u67f3\u7fe0\u7fe0\uff0c\u5e74\u957f\u5230\u5341\u4e8c\u4e09\u5c81\uff0c\u7c89\u88c5\u7389\u7422\uff0c\u7f8e\u8c8c\u65e0\u53cc\uff0c\u751a\u5f97\u201c\u5357\u6d77\u4e00\u541b\u201d\u559c\u7231\uff0c\u88ab \u5f3a\u884c\u6307\u5b9a\u670d\u4f8d\u5c0f\u59d0\uff0c\u4e0e\u201c\u5357\u6d77\u9f99\u5973\u201d\u4e3a\u5a76\uff0e \u67f3\u5d07\u539a\u8eab\u4e3a\u201c\u6c5f\u5357\u4e03\u4fa0\u201d\u4e4b\u4e00\uff0c\u81ea\u5df1\u4eb2\u751f\u5973\u513f\uff0c\u4ea6\u662f\u5343\u91d1\u4e4b\u8eaf\uff0c\u5c82\u80af\u4e0e\u4ed6\u4eba\u4e3a\u5a62\uff0c\u4f46\u4eba \u5728\u77ee\u6a90\u4e0b\uff0c\u600e\u6562\u4e0d\u4f4e\u5934\u3002\u81ea\u5df1\u7236\u5973\u65e2\u7136\u8981\u8499\u201c\u5357\u6d77\u95e8\u201d\u5e87\u62a4\uff0c\u81ea\u5df1\u7231\u5973\u88ab\u5357\u6d77\u638c\u95e8\u6307\u5b9a\u4e3a \u5a62\uff0c\u6562\u6012\u800c\u4e0d\u6562\u8a00\uff0c\u8868\u9762\u8fd8\u5f97\u88c5\u505a\u5fc3\u7518\u60c5\u613f\uff01 \u8c01\u77e5\u67f3\u7fe0\u7fe0\u56e0\u7978\u5f97\u798f\uff0c\u56e0\u7ec8\u65e5\u670d\u4f8d\u201c\u5357\u6d77\u9f99\u5973\u201d\uff0c\u7adf\u4e5f\u88ab\u201c\u9b3c\u9762\u5a07\u5a03\u201d\u770b\u4e2d\uff0c\u6536\u5f55\u4e3a\u7b2c \u4e09\u5973\u5f1f\u5b50\uff01 \u52a0\u4e4b\u67f3\u7fe0\u7fe0\u6167\u8d28\u5929\u751f\uff0c\u7980\u8d4b\u7adf\u5728\u201c\u5357\u6d77\u9f99\u5973\u201d\u4e4b\u4e0a\uff0c\u56e0\u6b64\uff0c\u5c45\u7136\u5165\u95e8\u8f83\u665a\uff0c\u6b66\u529f\u6bd4\u201c\u5357 \u6d77\u9f99\u5973\u201d\u8fd8\u8981\u9ad8\uff0c\u5e76\u4e14\u8fde\u201c\u5929\u4ed9\u9b54\u5973\u201d\u5ae1\u4f20\u7ed9\u201c\u9b3c\u9762\u5a07\u5a03\u201d\u7684\u201c\u59f9\u5973\u8ff7\u9b42\u5927\u6cd5\u201d\u4e5f\u5b66\u4f1a\u4e86\u3002 \u201c\u94f6\u6247\u5b50\u201d\u67f3\u795f\u539a\uff0c\u867d\u7136\u672a\u53c2\u4e0e\u6740\u5bb3\u5927\u54e5\u201c\u9739\u96f3\u5251\u201d\u5c55\u4e91\u5929\u7684\u9634\u8c0b\uff0c\u4f46\u4ed6\u4e5f\u85cf\u4e86\u4e00\u70b9 \u79c1\u3002 \u90a3\u5c31\u662f\u4f9d\u4ed7\u4ed6\u8d85\u4eba\u7684\u6c34\u529f\uff0c\u5355\u72ec\u8fdb\u5165\u6d1e\u5ead\u5e95\u53d6\u5b9d\u65f6\uff0c\u628a\u90a3\u4e00\u518c\u300a\u6b66\u5b66\u771f\u7ecf\u300b\u6697\u6697\u85cf\u5728\u8eab \u4e0a\uff0c\u5374\u628a\u300a\u6b66\u5b66\u771f\u7ecf\u300b\u7684\u5c01\u76ae\u6495\u4e0b\uff0c\u5176\u4e2d\u5939\u4ee5\u53e6\u5916\u4e00\u672c\u7834\u4e66\uff0c\u662f\u4ee5\u201c\u6c5f\u5357\u4e94\u4fa0\u201d\u5bb3\u6b7b\u5927\u54e5\uff0c \u8d76\u8d70\u4e03\u5f1f\uff0c\u74dc\u5206\u6d1e\u5ead\u5b9d\u85cf\u65f6\uff0c\u8001\u56db\u201c\u4e7e\u5764\u638c\u201d\u4e91\u5b97\u9f99\u6240\u5f97\u7684\u4e00\u672c\u300a\u6b66\u5b66\u771f\u7ecf\u300b\u5df2\u7ecf\u662f\u8d5d\u54c1\uff01 \u90a3\u5c31\u662f\u4e91\u5b97\u9f99\u7684\u513f\u5b50\u3001\u201c\u5b89\u4e50\u516c\u5b50\u201d\u4e91\u94ee\u7ec8\u65e5\u4e0d\u79bb\u624b\u7684\u90a3\u672c\u7834\u4e66\uff01 \u201c\u5b89\u4e50\u516c\u5b50\u201d\u82e6\u7814\u4f2a\u672c\u300a\u6b66\u5b66\u771f\u7ecf\u300b\uff0c\u6c38\u8fdc\u4e0d\u80fd\u83b7\u5f97\u751a\u9ad8\u8fdb\u5883\uff0c\u4e5f\u5c31\u662f\u8fd9\u4e2a\u9053\u7406\uff01 \u82e5\u4e0d\u7136\uff0c\u6b66\u6797\u4e2d\u4e0d\u4f1a\u51fa\u73b0\u56db\u516c\u5b50\uff0c\u6050\u6015\u65e9\u6210\u4e3a\u201c\u5b89\u4e50\u516c\u5b50\u201d\u4e00\u4eba\u7684\u5929\u4e0b\u4e86\uff01 \u300a\u6b66\u5b66\u771f\u7ecf\u300b\u7684\u771f\u672c\u7531\u67f3\u795f\u539a\u5f97\u5230\uff0c\u67f3\u795f\u539a\u6f5c\u8eab\u6d77\u5916\uff0c\u5fcd\u8fb1\u8d1f\u91cd\uff0c\u60f3\u5b66\u5f97\u7edd\u4e16\u6b66\u5b66\u540e\uff0c \u518d\u627e\u5fd8\u6069\u8d1f\u4e49\u7684\u4e94\u4f4d\u76df\u5144\u7b97\u8d26\uff0c\u53ef\u662f\u5f85\u4ed6\u628a\u300a\u6b66\u5b66\u771f\u7ecf\u300b\u7684\u5927\u90e8\u5206\u6b66\u529f\u7ec3\u6210\u4e4b\u540e\uff0c\u7adf\u80fd\u9759\u5fc3 \u5fcd\u6027\uff0c\u628a\u706b\u6c14\u5b8c\u5168\u6d88\u5c3d\u4e86\u3002\u5e76\u628a\u4ed6\u90a3\u67c4\u8d56\u4ee5\u6210\u540d\u7684\u201c\u94f6\u6247\u201d\uff0c\u4e5f\u8d60\u7ed9\u4e86\u201c\u5357\u6d77\u5c11\u541b\u201d\uff01 \u53ea\u5728\u95f2\u6687\u4e4b\u65f6\uff0c\u628a\u8fc7\u53bb\u7684\u4e00\u5207\u5f80\u4e8b\uff0c\u4e0e\u5973\u513f\u8c08\u8c08\uff0c\u4ee5\u6253\u53d1\u8352\u5c9b\u4e0a\u957f\u65e5\u7684\u5bc2\u5bde\u3002 \u662f\u4ee5\u67f3\u7fe0\u7fe0\u5bf9\u201c\u6c5f\u5357\u4e03\u4fa0\u201d\u4e4b\u4e8b\uff0c\u77e5\u9053\u5f97\u751a\u591a\uff01 \u67f3\u795f\u539a\u6df1\u5f97\u9053\u5b66\u771f\u9ad3\uff0c\u628a\u6069\u6028\u60c5\u4ec7\u3001\u9738\u4e1a\u96c4\u56fe\u90fd\u770b\u6de1\u4e86\uff0c\u4f46\u67f3\u7fe0\u7fe0\u5374\u4e0d\u80fd\u91ca\u6000\u6740\u6bcd\u4e4b \u4ec7\uff01 \u6070\u5de7\u8fd9\u4e00\u4e2d\u201c\u9b3c\u9762\u5a07\u5a03\u201d\u5df2\u6b7b\uff0c\u5357\u6d77\u201c\u9b54\u9b3c\u5c9b\u201d\u52bf\u529b\u5c3d\u5165\u201c\u5357\u6d77\u4e00\u541b\u201d\u4e4b\u624b\uff0c\u201c\u5357\u6d77\u4e00 \u541b\u201d\u81ea\u8ba4\u7fbd\u6bdb\u5df2\u4e30\uff0c\u52bf\u529b\u58ee\u5927\uff0c\u53c8\u71c3\u8d77\u4ed6\u65e9\u5e74\u79f0\u9738\u4e2d\u539f\u7684\u91ce\u5fc3\uff0c\u800c\u4e14\u4ed6\u4e5f\u5fd8\u4e0d\u4e86\u65e9\u5e74\u88ab\u4e2d\u539f \u6b66\u6797\u8ffd\u8d76\u65e0\u6cd5\u7acb\u8db3\u7684\u4ec7\u6068\uff0c\u56e0\u6b64\uff0c\u4ee5\u81ea\u5df1\u7684\u513f\u5b50\u3001\u5973\u51e0\u5206\u8272\u8944\u52a9\u4ee5\u201c\u6d77\u5916\u4e09\u715e\u201d\u3001\u201c\u6843\u82b1\u56db \u4ed9\u201d\u3001\u201c\u9b54\u9b3c\u5c9b\u516b\u5996\u201d\uff0c\u7387\u9886\u5357\u6d77\u95e8\u4e0b\u9ad8\u624b\uff0c\u503e\u5de2\u4fb5\u72af\u4e2d\u539f\uff01\u67f3\u7fe0\u7fe0\u4e5f\u968f\u7740\u5c0f\u59d0\uff0c\u7b2c\u4e00\u6b21\u79bb \u5f00\u201c\u9b54\u9b3c\u5c9b\u201d\uff0c\u8e0f\u4e0a\u5927\u9646\u3002 \u5979\u4e34\u884c\u4e4b\u524d\uff0c\u5979\u7236\u4eb2\u67f3\u5d07\u539a\u544a\u8beb\u5979\u8bf4\uff1a\u201c\u4e2d\u539f\u5c11\u5e74\uff0c\u4e2a\u4e2a\u4fca\u5f66\uff0c\u4f60\u9053\u5fc3\u672a\u575a\uff0c\u6b64\u53bb\u6050\u6015 \u8981\u5815\u8eab\u60c5\u7f51\uff0c\u4e0d\u80fd\u5f97\u9053\u98de\u5347\uff01\u201d \u67f3\u7fe0\u7fe0\u4e00\u7b11\u7f6e\u4e4b\uff0c\u56e0\u4e3a\u5979\u72e1\u9ee0\u65e0\u6bd4\uff0c\u80fd\u6446\u8131\u201c\u5357\u6d77\u4e00\u541b\u201d\u3001\u201c\u5357\u6d77\u5c11\u541b\u201d\u7236\u5b50\u4e24\u5927\u8272\u72fc \u7684\u7ea0\u7f20\uff0c\u81ea\u8ba4\u5929\u4e0b\u7537\u4eba\u5df1\u4e0d\u8db3\u52a8\u5176\u5fc3\uff0c\u6545\u672a\u628a\u7236\u4eb2\u7684\u8bdd\u653e\u5728\u5fc3\u4e0a\u3002 \u8c01\u77e5\u5979\u521a\u5230\u4e2d\u539f\uff0c\u4e3a\u4e86\u5fc3\u5207\u6bcd\u4ec7\uff0c\u5077\u5077\u79bb\u5f00\u201c\u5357\u6d77\u9f99\u5973\u201d\uff0c\u524d\u5f80\u5357\u4eac\u91d1\u5e9c\uff0c\u6697\u63a2\u201c\u9752\u86a8 \u795e\u201d\u91d1\u4e5d\u884c\u8e2a\uff0c\u7b2c\u4e00\u6b21\u5728\u79e6\u6dee\u6cb3\u7554\u6551\u4e86\u5c55\u767d\uff0c\u4fbf\u5815\u5165\u4e86\u60c5\u7f51\uff01\u6b64\u65f6\uff0c\u5c55\u767d\u9a82\u5979\u201c\u6bcd\u591c\u53c9\u201d\uff0c \u5927\u5927\u5730\u4f24\u4e86\u5979\u7684\u5fc3\uff01 \u56e0\u4e3a\uff0c\u201c\u5357\u6d77\u4e00\u541b\u201d\u9f99\u5578\u5929\u975e\u5e38\u597d\u8272\uff0c\u9057\u5f03\u201c\u9b3c\u9762\u5a07\u5a03\u201d\uff0c\u5a36\u201c\u4e2d\u539f\u7389\u5973\u201d\u4e3a\u59bb\uff0c\u4e5f\u5c31 \u662f\u4e3a\u4e86\u201c\u4e2d\u539f\u7389\u5973\u201d\u6bd4\u201c\u9b3c\u9762\u5a07\u5a03\u201d\u751f\u5f97\u66f4\u7f8e\uff0c\u540e\u6765\u201c\u4e2d\u539f\u7389\u5973\u201d\u4e3a\u4ed6\u751f\u513f\u80b2\u5973\uff0c\u5e74\u8001\u8272 \u8870\uff0c\u201c\u9b3c\u9762\u5a07\u5a03\u201d\u53c8\u56e0\u88ab\u4e2d\u539f\u6b66\u6797\u9ad8\u624b\u5728\u6cf0\u5c71\u89c2\u65e5\u5cf0\u6253\u843d\u4e07\u4e08\u6df1\u6e0a\uff0c\u6bc1\u4e86\u5bb9\u8c8c\uff0c\u7ec8\u65e5\u6234\u4e86\u9b3c \u9762\u5177\uff0c\u66f4\u4f7f\u201c\u5357\u6d77\u4e00\u541b\u201d\u89c1\u4e86\u6076\u5fc3\uff0c\u6545\u5c14\u6697\u4e2d\u5df2\u4e0e\u5996\u59e3\u65e0\u6bd4\u7684\u201c\u6843\u82b1\u56db\u4ed9\u201d\u6709\u4e86\u6765\u5f80\u3002 \u201c\u9b3c\u9762\u5a07\u5a03\u201d\u80fd\u5bb9\u5f97\u4e0b\u201c\u4e2d\u539f\u7389\u5973\u201d\uff0c\u5374\u5bb9\u4e0d\u5f97\u4eba\u5c3d\u53ef\u592b\u7684\u201c\u6843\u82b1\u56db\u5996\u201d\uff0c\u4e3a\u6b64\u5e38\u4e0e \u201c\u5357\u6d77\u4e00\u541b\u201d\u4e89\u5435\uff0c\u201c\u5357\u6d77\u4e00\u541b\u201d\u628a\u201c\u9b3c\u9762\u5a07\u5a03\u201d\u6bcd\u591c\u53c9\u9a82\u4e0d\u79bb\u53e3\uff0c\u662f\u4ee5\u67f3\u7fe0\u7fe0\u5bf9\u201c\u6bcd\u591c \u53c9\u201d\u4e09\u5b57\u8033\u719f\u80fd\u8be6\u3002 \u201c\u5357\u6d77\u4e00\u541b\u201d\u56e0\u6709\u5916\u9047\uff0c\u624d\u9a82\u201c\u9b3c\u9762\u5a07\u5a03\u201d\u4e3a\u6bcd\u591c\u53c9\uff0c\u201c\u5357\u6d77\u4e00\u541b\u201d\u4e5f\u66fe\u60f3\u67d3\u6307\u67f3\u7fe0 \u7fe0\uff0c\u90fd\u88ab\u5979\u5de7\u5999\u5730\u6446\u8131\uff0c\u5979\u4e0e\u7236\u4eb2\u5bc4\u8eab\u5357\u6d77\u95e8\u4e0b\uff0c\u8868\u9762\u4e0a\u867d\u4e0d\u6562\u600e\u4e48\u6837\uff0c\u4f46\u9aa8\u5b50\u91cc\u5df2\u628a\u201c\u5357 \u6d77\u4e00\u541b\u201d\u5351\u89c6\u5230\u5341\u5206\uff0c\u4eca\u89c1\u5c55\u767d\u4ee5\u201c\u5357\u6d77\u4e00\u541b\u201d\u7684\u53e3\u543b\uff0c\u9a82\u5979\u4e3a\u6bcd\u591c\u53c9\uff0c\u662f\u4ee5\u4f24\u5fc3\u5df2\u6781\uff0c\u4e22 \u6389\u624b\u4e2d\u201c\u65e0\u60c5\u78a7\u5251\u201d\u63a9\u9762\u60b2\u6ce3\u800c\u8d70\uff01 \u8c01\u77e5\u5979\u521a\u4e00\u4e3e\u6b65\uff0c\u7a81\u542c\u4e00\u58f0\u51b7\u7b11\uff0c\u5047\u5c71\u540e\u4eba\u5f71\u4e00\u95ea\uff0c\u9762\u524d\u591a\u4e86\u4e00\u4e2a\u9526\u8863\u5c11\u5973\uff01 \u8fd9\u9526\u8863\u5c11\u5973\u6b63\u662f\u5c55\u5a49\u513f\uff0c\u5c55\u767d\u89c1\u5230\u5a49\u513f\uff0c\u6025\u547c\u9053\uff1a\u201c\u5a49\u513f\uff01 \u5a49\u513f\u5a07\u7f8e\u7684\u8138\u4e0a\uff0c\u5df1\u5931\u53bb\u4e86\u5f80\u65e5\u7684\u5a07\u61a8\u5929\u771f\uff0c\u4ee3\u4e4b\u7684\u662f\u4e00\u7247\u54c0\u6101\uff0c\u5979\u5bf9\u5c55\u767d\u70ed\u60c5\u6d0b\u6ea2\u7684 \u547c\u53eb\uff0c\u4e0d\u7406\u4e0d\u776c\uff0c\u8138\u4e0a\u73b0\u51fa\u4e00\u79cd\u5632\u5f04\u7684\u7b11\u5bb9\uff0c\u5634\u89d2\u5fae\u6487\uff0c\u8d1f\u624b\u7ed5\u7740\u8d64\u88f8\u7684\u91d1\u5f69\u51e4\u8e31\u4e86\u4e00\u4e2a\u5708 \u5b50\uff01 \u91d1\u5f69\u51e4\u88ab\u5c55\u5a49\u513f\u5947\u5f02\u7684\u773c\u5149\uff0c\u770b\u5f97\u5927\u662f\u96be\u4e3a\u60c5\uff0c\u867d\u540c\u662f\u5973\u6027\uff0c\u4f46\u5979\u7d20\u5e38\u4f55\u7b49\u9ad8\u50b2\uff0c\u5982\u4eca \u8d64\u88f8\u5730\u88ab\u5c55\u5a49\u513f\u50cf\u770b\u7a00\u7f55\u4f3c\u5730\u7ed5\u7740\u5708\u5b50\u770b\u4e86\u534a\u5929\uff0c\u4e0d\u7531\u7f9e\u5f97\u9762\u5982\u7ea2\u5e03\uff0c\u65e0\u5730\u81ea\u5bb9\u2026\u3002\xb7 \u7b2c\u56db\u5341\u4e5d\u7ae0\u3000\u201c\u6d77\u5916\u4e09\u715e\u201d>> \u53e4\u9f99\u300a\u5251\u5ba2\u884c\u300b \u7b2c\u56db\u5341\u4e5d\u7ae0\u3000\u201c\u6d77\u5916\u4e09\u715e\u201d \u5a49\u513f\u76f4\u56f4\u7740\u91d1\u5f69\u51e4\u7ed5\u4e86\u4e00\u4e2a\u5708\uff0c\u624d\u51b7\u7b11\u9053\uff1a\u201c\u5c55\u54e5\u2026\u2026\u5c0f\u4fa0\uff01\u4f60\u8ddf\u6211\u59d0\u59d0\u521a\u521a\u7ed3\u5a5a\uff0c\u4fbf \u5728\u8fd9\u91cc\u4e71\u52fe\u642d\u5973\u4eba\uff0c\u4e0d\u514d\u6709\u70b9\u5427\uff1f\u201d \u5a49\u513f\u7684\u4e00\u53e5\u8bdd\uff0c\u4f7f\u91d1\u5f69\u51e4\u4e0e\u67f3\u7fe0\u7fe0\u540c\u65f6\u4e00\u9707\uff01 \u67f3\u7fe0\u7fe0\u53c8\u8d70\u4e86\u56de\u6765\uff0c\u5fd8\u8bb0\u4e86\u4f24\u5fc3\u54ed\u6ce3\uff1b\u91d1\u5f69\u51e4\u4e5f\u5fd8\u8bb0\u4e86\u5bb3\u7f9e\uff0c\u4e00\u9f50\u7741\u5927\u773c\u775b\u671b\u5b9a\u5c55\u767d\uff0c \u5f20\u5927\u5634\u5df4\u9053\uff1a\u201c\u4f60\u2014\u2014\u201d \u5dee\u4e0d\u591a\u662f\u540c\u65f6\uff0c\u67f3\u7fe0\u7fe0\u4e0e\u91d1\u5f69\u51e4\u60ca\u671b\u7740\u5c55\u767d\u5f20\u53e3\u8bf4\u51fa\u4e00\u4e2a\u201c\u4f60\u201d\u5b57\uff0c\u4f46\u4e0b\u8fb9\u7684\u8bdd\uff0c\u4e00\u65f6 \u4e4b\u95f4\u518d\u4e5f\u63a5\u4e0d\u4e0b\u53bb\uff01 \u60f3\u5fc5\u4e24\u4e2a\u5c11\u5973\u542c\u5230\u8fd9\u610f\u5916\u7684\u6d88\u606f\uff0c\u82b3\u5fc3\u5df2\u7ecf\u788e\u4e86\u2026\u2026 \u5c55\u767d\u65e9\u5df2\u770b\u51fa\u4e8c\u5973\u5fc3\u610f\uff0c\u5fc3\u60f3\u201c\u5feb\u5200\u65a9\u4e71\u9ebb\uff0c\u6325\u6167\u5251\u65a9\u65ad\u4e09\u5973\u60c5\u4e1d\uff0c\u6b64\u5176\u65f6\u77e3\uff01\u201d \u60f3\u7f62\uff0c\u6b63\u5bb9\u8bf4\u9053\uff1a\u201c\u5a49\u513f\u59b9\u59b9\u8bf4\u5f97\u4e0d\u9519\uff0c\u5728\u660e\u5a92\u6b63\u5a36\u4e4b\u4e0b\uff0c\u6211\u5df2\u4e0e\u5a49\u513f\u7684\u59d0\u59d0\u6155\u5bb9\u7ea2\uff0c \u6b63\u5f0f\u7ed3\u4e3a\u592b\u5987\u2026\u2026\u201d \u672a\u7b49\u5c55\u767d\u7684\u8bdd\u8bf4\u5b8c\uff0c\u67f3\u7fe0\u7fe0\u51e4\u76ee\u6025\u7741\uff0c\u6012\u53f1L\u9053\uff1a\u201c\u6b64\u8bdd\u5f53\u771f\uff1f\u201d \u5a49\u513f\u5728\u4e00\u8fb9\u63d2\u624b\u51b7\u7b11\u9053\uff1a\u201c\u96be\u9053\u8fd8\u9a97\u4f60\u4e0d\u6210\u5417\uff01\u201d \u67f3\u7fe0\u7fe0\u53ea\u611f\u5934\u8111\u4e2d\u201c\u8f70\u201d\u7684\u4e00\u58f0\uff0c\u5a07\u8eaf\u6447\u6447\u6b32\u5012\uff0c\u51e4\u76ee\u4e2d\u6c69\u6c69\u6d41\u4e0b\u6cea\u6765\uff0c\u7d27\u54ac\u7740\u5634\u5507\uff0c \u5411\u5c55\u81ea\u60b2\u58f0\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u8fd9\u8d1f\u5fc3\u2026\uff0e\uff0e\uff0e\u7684\uff01\u4f60\u8bf4\uff01\u4f60\u8bf4\uff01\u4f60\u628a\u6211\u600e\u4e48\u529e\u2026\u2026\uff1f\u201d \u5c55\u767d\u89c1\u67f3\u7fe0\u7fe0\u4f24\u5fc3\u81f3\u6b64\uff0c\u624d\u660e\u767d\u67f3\u7fe0\u7fe0\u5bf9\u81ea\u5df2\u662f\u4e00\u7247\u771f\u60c5\uff0c\u4e5f\u4e0d\u7531\u6df1\u6094\u81ea\u5df2\u505a\u5f97\u5b5f\u6d6a\uff0c \u4e00\u65f6\u6c14\u6124\uff0c\u7b54\u5141\u4e86\u6155\u5bb9\u7ea2\u7684\u5a5a\u4e8b\uff0c\u770b\u773c\u524d\u60c5\u5f62\uff0c\u771f\u8981\u5982\u81ea\u5df1\u6240\u60f3\u201c\u6325\u6167\u5251\u65a9\u65ad\u60c5\u4e1d\u201d\uff0c\u53ef\u8bf4 \u662f\u8c08\u4f55\u5bb9\u6613\uff1f \u5c31\u5728\u5c55\u767d\u6df1\u611f\u61ca\u6094\uff0c\u65e0\u8a00\u4ee5\u5bf9\uff0c\u5fae\u4e00\u5f81\u795e\u4e4b\u9645\uff0c\u7a81\u542c\u4e00\u58f0\u60e8\u55e5\u9065\u9065\u4f20\u6765\uff0c\u58f0\u5982\u91ce\u517d\u4e34\u6b7b \u4e4b\u524d\u7684\u60b2\u9e23\uff0c\u4ee4\u4eba\u542c\u4e4b\uff0c\u98a4\uff01 \u8fd9\u4e00\u58f0\u60e8\u55e5\u58f0\u97f3\u867d\u4e0d\u5927\uff0c\u4f46\u4f20\u81f3\u51e0\u4eba\u8033\u4e2d\uff0c\u51e0\u4eba\u4e0d\u7531\u5413\u5f97\u4e00\u8eab\u51b7\u6c57\uff0c\u5c55\u767d\u4e0e\u91d1\u5f69\u51e4\u7684\u8138 \u4e0a\u66f4\u662f\u53d8\u4e86\u989c\u8272\uff01 \u91d1\u5f69\u51e4\u731b\u5730\u6251\u81f3\u5c55\u767d\u9762\u524d\uff0c\u60b2\u58f0\u53eb\u9053\uff1a\u201c\u5c55\u54e5\u2026\u2026\u5c0f\u4fa0\uff01\u8bf7\u4f60\u5e2e\u5fd9\uff0c\u6211\u7684\u7236\u4eb2\u6050\u6015\u51f6\u591a \u5409\u5c11\uff01\u2026\u2026\u201d \u5c55\u767d\u5bf9\u201c\u9752\u86a8\u795e\u201d\u91d1\u4e5d\u7684\u6b7b\u6d3b\uff01\u5e76\u4e0d\u5173\u5fc3\uff0c\u4ed6\u803d\u5fc3\u7684\u662f\u96f7\u5927\u53d4\u3001\u592a\u767d\u53cc\u9038\u3001\u8339\u8001\u9556\u5934\u4ee5 \u53ca\u81ea\u5df1\u7684\u672a\u5a5a\u59bb\u6155\u5bb9\u7ea2\u7684\u5b89\u5371\u3002\u4ed6\u6b64\u65f6\u5438\u5165\u5185\u8151\u7684\u201c\u9f99\u6d8e\u9999\u201d\u6bd2\u5df2\u5b8c\u5168\u6563\u53bb\uff0c\u5468\u8eab\u529f\u529b\u5df2 \u590d\uff0c\u95fb\u8a00\u987a\u624b\u628a\u81ea\u5df1\u7684\u5916\u8863\u8131\u4e0b\uff0c\u4e22\u7ed9\u91d1\u5f69\u51e4\uff0c\u7136\u540e\u5f2f\u8170\u62fe\u8d77\u5730\u4e0a\u7684\u201c\u65e0\u60c5\u78a7\u5251\u201d\uff0c\u9053\uff1a \u201c\u4f60\u7236\u4eb2\u5728\u4f55\u5904\u6211\u4e0d\u77e5\u9053\uff0c\u4f60\u81ea\u5df1\u53bb\u627e\u5427\uff01\u6211\u8fd8\u8981\u53bb\u63a5\u5e94\u4e24\u4e2a\u4eba1\u2026\u2026\u201d\u8bf4\u8bdd\u58f0\u4e2d\uff0c\u4eba\u5df2\u8dc3 \u4e0a\u5047\u5c71\u9876\uff0c\u7565\u4e3a\u8fa8\u522b\u4e00\u4e0b\u60e8\u55e5\u58f0\u4f20\u6765\u7684\u65b9\u5411\uff0c\u7acb\u5982\u8131\u5f26\u6025\u5f29\uff0c\u5411\u524d\u6251\u53bb\uff01 \u4e09\u4e2a\u5c11\u5973\uff0c\u5404\u6000\u4e0d\u540c\u7684\u5fc3\u60c5\uff0c\u4e92\u76f8\u5bf9\u671b\u4e86\u4e00\u773c\uff0c\u8c01\u4e5f\u6ca1\u7406\u8c01\uff0c\u91d1\u5f69\u51e4\u6700\u6025\uff0c\u628a\u5c55\u767d\u4e22\u7ed9 \u5979\u7684\u5916\u8863\uff0c\u80e1\u4e71\u7a7f\u5728\u8eab\u4e0a\uff0c\u5f53\u5148\u5411\u5047\u5c71\u4e4b\u5916\u8dd1\u53bb\uff01 \u67f3\u7fe0\u7fe0\u767d\u4e86\u5a49\u513f\u4e00\u773c\uff0c\u9053\uff1a\u201c\u6709\u80c6\u5417\uff1f\u201d \u5c55\u5a49\u513f\u9f3b\u5b54\u54fc\u4e86\u4e00\u58f0\uff0c\u6487\u4e86\u6487\u5634\u9053\uff1a\u201c\u6015\u4ec0\u4e48\uff1f\u201d \u8bf4\u7f62\uff0c\u4e00\u8dfa\u811a\u4eba\u5c31\u5982\u4e00\u7f15\u8f7b\u70df\u4e00\u822c\uff0c\u5411\u524d\u9a70\u53bb\u3002\u67f3\u7fe0\u7fe0\u968f\u540e\u8ffd\u6765\uff0c\u56db\u4e2a\u4eba\u5dee\u4e0d\u591a\u662f\u524d\u540e \u811a\u6765\u5230\u4e00\u6240\u5e7f\u5927\u7684\u5ead\u9662\u4e4b\u4e2d\uff01\u9662\u5b50\u91cc\u706f\u7b3c\u706b\u628a\uff0c\u7167\u8000\u5f97\u5982\u540c\u767d\u663c\uff0c\u662f\u4ee5\u770b\u5f97\u5206\u5916\u6e05\uff01 \u8fd9\u9662\u5b50\u5360\u5730\u9887\u5e7f\uff0c\u7eb5\u5bbd\u5404\u6709\u56db\u5341\u4f59\u6587\uff0c\u5730\u4e0b\u5b8c\u5168\u662f\u575a\u786c\u7684\u4e09\u5408\u571f\u94fa\u6210\uff0c\u4e09\u9762\u9ad8\uff0c\u5899\u58c1\u8fb9 \u5730\u4e0a\u653e\u6709\u77f3\u9501\u6c99\u888b\u53ca\u5200\u67aa\u5251\u671d\u7b49\u5341\u516b\u822c\u5175\u5668\uff0c\u770b\u6837\u5b50\u4f3c\u662f\u91d1\u5e9c\u7684\u7ec3\u6b66\u573a\uff01 \u8fce\u9762\u4e00\u5ea7\u9ad8\u53f0\uff0c\u9ad8\u7ea6\u4e8c\u6587\uff0c\u56db\u6587\u89c1\u65b9\uff0c\u6b63\u5f53\u4e2d\u6446\u4e86\u4e00\u5f20\u5927\u957f\u684c\uff0c\u684c\u540e\u5341\u6570\u5f20\u9ad8\u80cc\u91d1\u4ea4 \u6905\uff0c\u5f53\u4e2d\u5750\u7740\u201c\u5357\u6d77\u5c11\u541b\u201d\u53ca\u201c\u5357\u6d77\u9f99\u5973\u201d\u5144\u59b9\u4e8c\u4eba\uff0c\u5176\u4f59\u7684\u6905\u5b50\u5750\u7740\u201c\u6d77\u5916\u4e09\u715e\u201d\u201c\u6843\u82b1 \u56db\u4ed9\u201d\u7b49\u4eba\uff0c\u6905\u80cc\u540e\u7ad9\u7740\u9ad8\u77ee\u4e0d\u7b49\u7684\u5341\u6570\u4e2a\u52b2\u88c5\u5927\u6c49\uff01 \u8fce\u7740\u770b\u53f0\u7684\u5899\u8fb9\u4e0a\uff0c\u57cb\u7740\u5341\u6570\u6839\u9ad8\u5927\u7684\u67f1\u5b50\uff0c\u6bcf\u6839\u4f4f\u5b50\u4e0a\u90fd\u7ed1\u6709\u4e00\u4e2a\u4eba\uff0c\u6709\u7684\u5df2\u7ecf\u6b7b \u4e86\uff0c\u5f00\u80a0\u7834\u809a\uff0c\u65ad\u81c2\u5c11\u817f\uff0c\u63ba\u4e0d\u5fcd\u7779\uff01 \u6d3b\u7740\u7684\u4e5f\u90fd\u5413\u5f97\u9762\u65e0\u4eba\u8272\uff01 \u5728\u8fd9\u4e9b\u7ed1\u7740\u72af\u4eba\u7684\u6821\u5b50\u4e24\u65c1\uff0c\u5404\u7ad9\u6709\u4e24\u540d\u51f6\u7709\u6076\u773c\u3001\u6253\u7740\u8d64\u818a\u3001\u633a\u80f8\u53e0\u809a\u7684\u523d\u5b50\u624b\uff0c\u624b \u4e2d\u62b1\u7740\u660e\u6643\u6643\u7684\u9b3c\u5934\u5200\uff0c\u4e00\u4e2a\u4e2a\u72b9\u5982\u51f6\u795e\u6076\u9b3c\uff01 \u9662\u5b50\u91cc\u6b63\u6709\u6570\u5341\u4eba\u6349\u5bf9\u513f\u53ae\u6740\uff0c\u5200\u5149\u5251\u5f71\uff0c\u638c\u62f3\u547c\u547c\u7834\u98ce\uff0c\u6218\u51b5\u975e\u5e38\u731b\u70c8\uff01 \u770b\u201c\u6d77\u5916\u4e09\u715e\u201d\u3001\u201c\u6843\u82b1\u56db\u4ed9\u201d\u7b49\u4e00\u6d41\u9ad8\u624b\u90fd\u5750\u5728\u770b\u53f0\u4e0a\u770b\u70ed\u95f9\uff0c\u663e\u89c1\u201c\u5357\u6d77\u95e8\u201d\u4ec5\u662f \u4ee5\u4e8c\u4e09\u6d41\u7684\u89d2\u8272\u5e94\u654c\uff01 \u5c55\u767d\u5df2\u770b\u51fa\u4e0e\u201c\u5357\u6d77\u95e8\u201d\u4e0b\u8840\u6218\u7684\uff0c\u6b63\u662f\u96f7\u5927\u53d4\u3001\u201c\u592a\u767d\u53cc\u9038\u201d\u8339\u8001\u9556\u5934\u53ca\u6155\u5bb9\u7ea2\u7b49 \u4eba\uff01 \u96f7\u5927\u53d4\u4e0e\u201c\u592a\u767d\u53cc\u9038\u201d\u5b8c\u5168\u51ed\u4e00\u53cc\u8089\u638c\uff0c\u8339\u8001\u9556\u5934\u4f7f\u4e00\u67c4\u7eaf\u91d1\u541e\u624b\u7684\u7d2b\u80cc\u9c7c\u9cde\u5200\uff0c\u6155\u5bb9 \u7ea2\u7528\u77ed\u5251\uff0c\u51e0\u4e2a\u4eba\u5982\u751f\u9f99\u6d3b\u864e\u4e00\u822c\uff0c\u529b\u6218\u201c\u5357\u6d77\u95e8\u201d\u4e0b\u6570\u5341\u4eba\uff0c\u4ecd\u7136\u662f\u4ece\u4ece\u5bb9\u5bb9\uff0c\u7a33\u5360\u4e0a \u98ce\uff01\u201c\u5357\u6d77\u95e8\u201d\u4e0b\uff0c\u4e0d\u65f6\u6709\u4eba\u88ab\u6740\u4f24\u6216\u662f\u6253\u5012\uff01 \u201c\u5357\u6d77\u5c11\u541b\u201d\u9ad8\u5750\u770b\u53f0\u4e0a\uff0c\u624b\u6447\u94f6\u6247\uff0c\u89c1\u95e8\u4e0b\u4e45\u6218\u6765\u4eba\u4e0d\u4e0b\uff0c\u8038\u4e86\u8038\u7709\u6bdb\uff0c\u5411\u5de6\u53f3\u770b\u4e86 \u770b\u3002 \u201c\u6d77\u5916\u4e09\u715e\u201d\u4e4b\u4e00\u3001\u767d\u53d1\u5a46\u5a46\u51b7\u8273\u7ea2\u7acb\u523b\u5e72\u543c\u4e86\u4e00\u58f0\uff0c\u8eab\u5f62\u51cc\u7a7a\u800c\u8d77\uff0c\u5347\u81f3\u4e09\u4e08\u4f59\u9ad8\uff0c \u5f39\u817f\u6298\u8170\uff0c\u8eab\u5f62\u5e73\u5c04\u800c\u51fa\uff0c\u5feb\u903e\u98d8\u98ce\u95ea\u7535\u534a\u7a7a\u4e2d\u6325\u51fa\u4e00\u638c\uff0c\u731b\u5411\u96f7\u5927\u53d4\u5934\u4e0a\u780d\u53bb\uff01 \u96f7\u5927\u53d4\u529b\u6218\u56db\u4e94\u4eba\uff0c\u4ecd\u7136\u6e38\u5203\u6709\u4f59\uff0c\u7a81\u89c1\u767d\u53d1\u5a46\u5a46\u631f\u75be\u98ce\u8fc5\u96f7\u4e4b\u52bf\u653b\u5230\uff0c\u7acb\u523b\u6325\u5de6\u638c\u903c \u9000\u56db\u9762\u4e4b\u654c\uff0c\u53f3\u638c\u201c\u5929\u738b\u6258\u5854\u201d\uff0c\u786c\u5411\u767d\u53d1\u5a46\u5a46\u91cd\u903e\u5343\u94a9\u7684\u638c\u529b\u8fce\u53bb\uff01 \u4e24\u80a1\u5f3a\u70c8\u638c\u98ce\u534a\u7a7a\u76f8\u9047\uff0c\u201c\u562d\u201d\u7684\u4e00\u58f0\u5927\u9707\uff0c\u96f7\u5927\u53d4\u8e09\u8dc4\u56db\u4e94\u6b65\uff0c\u56f4\u4f4f\u96f7\u5927\u53d4\u7684\u201c\u5357\u6d77 \u95e8\u201d\u4e0b\uff0c\u4e5f\u90fd\u54c4\u7136\u56db\u64a4\uff0c\u767d\u53d1\u5a46\u5a46\u638c\u529b\u60ca\u4eba1 \u96f7\u5927\u53d4\u62ff\u6869\u7ad9\u7a33\u8eab\u5f62\uff0c\u6697\u60ca\u767d\u53d1\u5a46\u5a46\u5185\u529b\u60ca\u4eba\uff0c\u5fc3\u4e2d\u5fae\u5fae\u4e00\u61d41 \u6b64\u65f6\uff0c\u767d\u53d1\u5a46\u5a46\u8eab\u5f62\u5df2\u843d\u4e0b\u5730\u6765\uff0c\u4e8e\u53eb\u9053\uff1a\u201c\u518d\u63a5\u6211\u8001\u5a46\u5b50\u4e00\u638c\uff01\u201d \u8bf4\u7740\u8bdd\uff0c\u53cc\u638c\u5e73\u80f8\u63a8\u51fa\uff0c\u4e00\u822c\u72c2\u98d9\uff0c\u731b\u5411\u96f7\u5927\u53d4\u80f8\u524d\u5377\u81f3\uff01 \u6070\u597d\u96f7\u5927\u53d4\u4e5f\u662f\u50b2\u9aa8\u5929\u751f\uff0c\u89c1\u767d\u53d1\u5a46\u5a46\u76db\u6c14\u51cc\u4eba\uff0c\u4ed6\u7ade\u4e0d\u8eb2\u4e0d\u95ea\uff0c\u4e5f\u4ee5\u53f3\u638c\u5288\u51fa\uff0c\u786c\u5411 \u767d\u53d1\u5a46\u5a46\u5f3a\u70c8\u638c\u98ce\u63a5\u53bb\uff01 \u201c\u562d\uff01\u201d\u53c8\u662f\u4e00\u58f0\u66b4\u54cd\uff0c\u767d\u53d1\u5a46\u5a46\u8eab\u5f62\u8fde\u6643\uff0c\u96f7\u5927\u53d4\u5df2\u88ab\u9707\u9000\u4e94\u6b65\uff01 \u767d\u53d1\u5a46\u5a46\u89c1\u96f7\u5927\u638c\uff0c\u66f4\u5f62\u6fc0\u6012\uff0c\u53ea\u89c1\u5979\u6ee1\u5934\u767d\u53d1\u65e0\u98ce\u81ea\u52a8\uff0c\u55d4\u76ee\u5389\u53f1\u9053\uff1a\u201c\u597d\u5c0f\u5b501\u8001 \u5a46\u5b50\u8fd9\u7b2c\u4e09\u638c\uff0c\u8981\u4e0d\u4e86\u4f60\u7684\u72d7\u547d\uff0c\u6b64\u540e\u540d\u5b57\u5012\u7740\u5199\uff01\u201d \u8bf4\u7f62\uff0c\u8fd0\u8db3\u5468\u8eab\u5341\u4e8c\u6210\u7684\u529f\u529b\uff0c\u5f2f\u80cc\u584c\u8170\uff0c\u53cc\u624b\u98a4\u5dcd\u5dcd\u5730\u5e73\u80f8\u63a8\u51fa\uff01 \u96f7\u5927\u53d4\u8fde\u63a5\u5979\u4e24\u638c\uff0c\u5df2\u611f\u53cc\u81c2\u53d1\u9ebb\uff0c\u5185\u8151\u8840\u6c14\u7ffb\u6d8c\uff0c\u4f46\u4ed6\u662f\u4e2a\u5b81\u6298\u4e0d\u5f2f\u7684\u6027\u5b50\uff0c\u867d\u89c1\u767d \u53d1\u5a46\u5a46\u8fd9\u7b2c\u4e09\u6b21\u51fa\u638c\uff0c\u5a01\u529b\u524d\u6240\u672a\u89c1\uff0c\u4ed6\u4ecd\u7136\u54ac\u7259\u786c\u63a5\uff0c\u6697\u628a\u201c\u5929\u4f5b\u638c\u201d\u529b\u8fd0\u81f3\u98a0\u5cf0\uff0c\u5f85\u5f3a \u731b\u638c\u98ce\u683c\u53ca\u8eab\u524d\u4e4b\u9645\uff0c\u624d\u53cc\u638c\u4e00\u6325\uff0c\u5411\u524d\u8fce\u53bb\uff01 \u5c55\u767d\u7ad9\u5728\u5899\u9876\uff0c\u4e07\u6ca1\u60f3\u5230\u96f7\u5927\u53d4\u518d\u4f1a\u786c\u63a5\u767d\u53d1\u5a46\u5a46\u8fd9\u7b2c\u4e09\u638c\uff0c\u603b\u4ee5\u4e3a\u96f7\u5927\u53d4\u8981\u8eb2\u8fc7\u53bb\uff0c \u7136\u540e\u518d\u8fd8\u62db\uff0c\u6240\u4ee5\u4ed6\u672a\u505a\u51c6\u5907\u63a5\u5e94\uff01 \u4eca\u89c1\u96f7\u5927\u53d4\u4ecd\u7136\u786c\u63a5\uff0c\u4e0d\u7531\u8131\u53e3\u53eb\u9053\uff1a\u201c\u4e0d\u597d\u2026\u2026\u201d \u4f46\u672a\u7b49\u4ed6\u7a9c\u4e0b\u5899\u6765\uff0c\u96f7\u5927\u53d4\u53cc\u638c\u5df2\u4e0e\u767d\u53d1\u5a46\u5a46\u53cc\u638c\u63a5\u5728\u4e00\u8d77\uff01 \u4e24\u80a1\u5f3a\u70c8\u638c\u98ce\uff0c\u534a\u7a7a\u76f8\u649e\uff0c\u604d\u5982\u5e73\u5730\u54cd\u8d77\u4e00\u58f0\u7126\u96f7\uff01 \u4f59\u529b\u56db\u6fc0\uff0c\u5c18\u6c99\u98de\u626c\uff01 \u96f7\u5927\u53d4\u4e00\u8def\u8ddf\u8dea\uff0c\u76f4\u9000\u51fa\u4e08\u4f59\u8fdc\u8fd1\uff0c\u4f46\u8eab\u5f62\u6643\u4e86\u51e0\u6643\uff0c\u4ecd\u7136\u7ad9\u7740\u672a\u5012\uff01 \u767d\u53d1\u5a46\u5a46\u602a\u76ee\u5706\u7741\uff0c\u9759\u7b49\u7740\u96f7\u5927\u53d4\u4e0d\u652f\u5012\u4e0b\u2026\uff0e\uff0e\u2019 \u4f46\u96f7\u5927\u53d4\u4e0d\u4f46\u672a\u5012\uff0c\u7adf\u5f20\u53e3\u8bf4\u9053\uff1a\u201c\u8001\u5a46\u5a46\uff01\u4f60\u540d\u5b57\u600e\u4e48\u5199\u6cd5\uff1f\u201d \u53ef\u662f\u4e00\u5f20\u53e3\u8bf4\u8bdd\uff0c\u518d\u4e5f\u5fcd\u4e0d\u4f4f\u5185\u8151\u7ffb\u6eda\u7684\u6c14\u8840\uff0c\u7adf\u987a\u7740\u53e3\u89d2\u6d41\u4e0b\u6ee1\u5634\u9c9c\u8840\u6765\uff01 \u767d\u53d1\u5a46\u5a46\u72de\u7b11\u9053\uff1a\u201c\u4f60\u5230\u960e\u738b\u8001\u5b50\u9762\u524d\uff0c\u518d\u53bb\u95ee\u6211\u5012\u5199\u7684\u59d3\u540d\u5427\uff01\u201d \u5c55\u767d\u77e5\u9053\u90a3\u662f\u767d\u53d1\u5a46\u5a46\u9b3c\u795e\u7686\u60ca\u7684\u201c\u641c\u9b42\u6307\u201d\uff0c\u4e0d\u7531\u60ca\u53eb\u9053\uff1a\u201c\u5927\u53d4\uff0c\u95ea\u5f00\uff01\u2026\u2026\u201d \u53ef\u662f\uff0c\u5a49\u513f\u6bd4\u4ed6\u66f4\u5feb\uff0c\u5c31\u5728\u5c55\u767d\u60ca\u547c\u51fa\u58f0\u4e4b\u9645\uff0c\u5979\u5a07\u5c0f\u4eba\u5f71\uff0c\u5df2\u5982\u60ca\u9e3f\u98de\u71d5\u4e00\u822c\u63a0\u81f3\u5f53 \u573a\uff0c\u4eba\u672a\u843d\u5730\uff0c\u534a\u7a7a\u4e2d\u5355\u6307\u75be\u51fa\uff0c\u4e5f\u662f\u7528\u7684\u201c\u641c\u9b42\u6307\u201d\uff0c\u731b\u6233\u767d\u53d1\u5a46\u5a46\u53f3\u81c2\u201c\u5173\u5143\u201d\u91cd\u7a74\uff01 \u8fd9\u662f\u5a49\u513f\u806a\u660e\u7684\u5730\u65b9\uff0c\u5979\u81ea\u5df1\u201c\u641c\u9b42\u6307\u201d\u7684\u529f\u592b\uff0c\u4e0d\u5982\u767d\u53d1\u5a46\u5a46\u6df1\u539a\uff0c\u6545\u6b64\uff0c\u5979\u907f\u5f00\u6307 \u950b\uff0c\u51fa\u62db\u6307\u5411\u767d\u53d1\u5a46\u5a46\u53f3\u81c2\u8981\u5bb3\uff01 \u767d\u53d1\u5a46\u5a46\u6b64\u65f6\u82e5\u4e0d\u6536\u62db\u540e\u9000\uff0c\u56fa\u7136\u5979\u7684\u6307\u98ce\uff0c\u53ef\u4ee5\u8981\u4e86\u96f7\u5927\u53d4\u7684\u547d\uff0c\u4f46\u5979\u81ea\u5df1\u7684\u4e00\u6761\u53f3 \u81c2\uff0c\u4e5f\u5f97\u62a5\u95001 \u8fd9\u53eb\u201c\u653b\u654c\u6240\u5fc5\u6551\u201d\u7c7b\u4f3c\u5175\u6cd5\u4e2d\u201c\u56f4\u9b4f\u6551\u8d75\u201d\u7684\u65b9\u7b56\uff0c\u767d\u53d1\u5a46\u5a46\u65e0\u5948\uff0c\u53ea\u6709\u6c89\u8155\u5b50\uff0c\u5411 \u4e00\u4fa7\u95ea\u53bb\uff01 \u53ea\u542c\u201c\u6ecb\u201d\u7684\u4e00\u58f0\u8f7b\u54cd\uff0c\u767d\u53d1\u5a46\u5a46\u7684\u6307\u950b\u867d\u7136\u8ba9\u5f00\u96f7\u5927\u53d4\uff0c\u56e0\u4e3a\u64a4\u529f\u4e0d\u53ca\uff0c\u6307\u9551\u8fc7\u5904\uff0c \u7adf\u5728\u575a\u786c\u7684\u5730\u9762\u4e0a\u5212\u4e86\u4e00\u9053\u5bf8\u4f59\u6df1\u7684\u6c9f1 \u518d\u770b\u5a49\u513f\u90a3\u4e00\u6307\u843d\u7a7a\uff0c\u4e5f\u628a\u5730\u9762\u4e0a\u6233\u4e86\u4e00\u4e2a\u6df1\u4e0d\u89c1\u5e95\u7684\u5c0f\u6d1e\uff01 \u4e8c\u4eba\u7684\u201c\u641c\u9b42\u6307\u201d\u90fd\u591f\u60ca\u4eba\uff01 \u96f7\u5927\u53d4\u60ca\u9b42\u521d\u5b9a\uff0c\u6447\u6447\u6b32\u5012\uff0c\u5a49\u513f\u8d76\u4e0a\u4e00\u6b65\uff0c\u5fd9\u6276\u4f4f\u96f7\u5927\u53d4\uff0c\u5fd9\u9053\uff1a\u201c\u5927\u53d4\uff01\u4f60\u8d1f\u4f24 \u4e86\uff1f\u2026\u2026\u201d \u96f7\u5927\u53d4\u5728\u6155\u5bb9\u5e9c\u6700\u75bc\u7231\u5a49\u513f\uff0c\u5a49\u513f\u4e5f\u6700\u656c\u91cd\u96f7\u5927\u53d4\uff0c\u662f\u4ee5\u89c1\u5230\u96f7\u5927\u53d4\u8d1f\u4f24\uff0c\u82b3\u5fc3\u751a\u611f\u7126 \u6025\u2026\u2026 \u6b64\u65f6\uff0c\u5c55\u767d\u4e5f\u8df3\u843d\u9662\u4e2d\uff0c\u89c1\u96f7\u5927\u53d4\u6709\u5a49\u513f\u7167\u987e\uff0c\u9762\u5b54\u4e00\u6c89\uff0c\u5bf9\u767d\u53d1\u5a46\u5a46\u53f1\u9053\uff1a\u201c\u4f60\u8fd9\u4e48 \u592a\u5e74\u7eaa\u4e86\uff0c\u60f3\u4e0d\u5230\u8fd8\u90a3\u4e48\u5fc3\u9ed1\u624b\u8fa3\uff0c\u5bf9\u4e00\u4e2a\u8d1f\u4f24\u4e4b\u4eba\uff0c\u8fd8\u4e0b\u6bd2\u624b\uff0c\u4eca\u665a\uff0c\u5c0f\u7237\u5012\u8981\u9886\u6559\u9886\u6559 \u4f60\u6709\u591a\u5c11\u7edd\u5b66\uff1f\u201d \u559d\u7f62\uff0c\u53cc\u638c\u4e00\u7ffb\uff0c\u4eae\u51fa\u201c\u5929\u4f5b\u964d\u9b54\u638c\u201d\u67b6\u5f0f\uff0c\u9053\uff1a\u201c\u5c0f\u7237\u5148\u8ba9\u4f60\u52a8\u624b\uff0c\u4e09\u62db\u4e4b\u5185\uff0c\u7167\u6837 \u8981\u4f60\u7684\u8001\u547d\uff01\u201d \u767d\u53d1\u5a46\u5a46\u4e00\u89c1\u5c55\u767d\u51fa\u73b0\uff0c\u9762\u9732\u60ca\u60e7\u4e4b\u8272\uff0c\u5148\u524d\u7684\u72c2\u50b2\u65e9\u5df2\u5413\u8dd1\u3002\u56e0\u4e3a\u5979\u77e5\u773c\u524d\u7684\u5c0f\u4f19 \u5b50\uff0c\u4e43\u662f\u5979\u547d\u4e2d\u7684\u514b\u661f\uff0c\u4f46\u4e00\u65f6\u4e4b\u95f4\uff0c\u53c8\u4e0d\u597d\u610f\u601d\u6389\u5934\u5c31\u8dd1\uff0c\u662f\u4ee5\u6014\u5728\u90a3\u91cc\u2026\u2026 \u7a81\u7136\u4eba\u5f71\u4e00\u6643\uff0c\u957f\u9aef\u8001\u4eba\u4e0e\u201c\u4f5b\u5370\u6cd5\u5e08\u201d\u540c\u65f6\u63a0\u81f3\u5c55\u767d\u9762\u524d\uff01 \u957f\u9aef\u8001\u4eba\u6296\u987b\u7b11\u9053\uff1a\u201c\u5c0f\u54e5\u513f\uff01\u4e2d\u539f\u6b66\u6797\uff0c\u5c31\u4f60\u4e00\u4eba\uff0c\u4f7f\u6211\u4eec\u2018\u6d77\u5916\u4e09\u715e\u2019\u5fc3\u670d\u53e3\u670d\uff01 \u4f46\u4eca\u591c\u6211\u4eec\u4e09\u4e2a\u8001\u4e0d\u6b7b\u7684\u8981\u5408\u8d77\u624b\u6765\uff0c\u8ddf\u5c0f\u54e5\u513f\u9886\u6559\u51e0\u62db\u7edd\u4e16\u6b66\u5b66\uff0c\u8fd9\u8bdd\u8981\u8ddf\u522b\u4eba\u8bf4\u7b97\u6211\u4eec \u6b3a\u4fae\u4eba\uff01\u4f46\u5bf9\u4f60\u5c0f\u54e5\u513f\u8bf4\u6765\uff0c\u8c05\u5fc5\u5c0f\u54e5\u513f\u4e0d\u4f1a\u8bf4\u6211\u4eec\u4ee5\u4f17\u6b3a\u5be1\u2026\u2026\u201d \u5c55\u767d\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u5728\u4e0b\u5c0a\u4f60\u4e00\u58f0\u8001\u4eba\u5bb6\uff01\u4f60\u600e\u4e48\u4e0d\u8bf4\u662f\u8138\u76ae\u539a\u5462\uff1f\u201d \u957f\u9aef\u8001\u4eba\u8138\u4e00\u7ea2\uff0c\u77ac\u5373\u653e\u58f0\u7b11\u9053\uff1a\u201c\u5c31\u7b97\u6211\u8001\u4e0d\u6b7b\u8138\u76ae\u539a\uff0c\u4f46\u2018\u6d77\u5916\u4e09\u715e\u2019\u5408\u6218\u4f60\u4e00 \u4eba\uff0c\u672a\u5c1d\u4e0d\u662f\u5c0f\u54e5\u513f\u7684\u5927\u5149\u8363\u5462\uff01\u6211\u8001\u4e0d\u6b7b\u6597\u80c6\u8bf4\u4e00\u65ec\uff0c\u4e2d\u539f\u6b66\u6797\u9664\u4e86\u5c0f\u54e5\u513f\u4e00\u4eba\uff0c\u53ef\u8363\u83b7 \u8fd9\u4efd\u5149\u8363\u4e4b\u5916\u3002\u6b66\u6797\u4e2d\u627e\u4e0d\u51fa\u7b2c\u4e8c\u4eba\uff01\u201d \u5c55\u767d\u8c6a\u6c14\u5e72\u4e91\u5730\u7b11\u9053\uff1a\u201c\u8fd9\u6837\u8bf4\u6765\uff0c\u8fd9\u4efd\u5149\u8363\u5c55\u767d\u662f\u4e00\u5b9a\u8981\u62dc\u9886\u4e86\u8c22\u8bf7\u4e09\u4f4d\u8fdb\u62db\u5427\uff01\u201d \u8bf4\u7740\u62c9\u5f00\u201c\u5929\u4f5b\u964d\u9b54\u638c\u201d\u7684\u67b6\u5f0f\uff01 \u53cc\u65b9\u8fd9\u4e00\u5bf9\u7b54\u4e4b\u95f4\uff0c\u8fde\u52a8\u7740\u624b\u7684\u4eba\u90fd\u81ea\u52a8\u505c\u6b62\u4e86\u4e0b\u6765\uff0c\u7eb7\u7eb7\u5411\u56db\u5468\u9000\u53bb\uff0c\u5f53\u4e2d\u7acb\u523b\u73b0\u51fa \u4e00\u5927\u7247\u7a7a\u5730\u6765\uff0c\u4f3c\u662f\u4e13\u4e3a\u5c55\u767d\u731b\u6218\u4e09\u715e\u7a7a\u51fa\u7684\u573a\u5730\uff01 \u56e0\u4e3a\u5927\u5bb6\u5fc3\u91cc\u660e\u767d\uff0c\u8fd9\u56db\u4e2a\u4eba\u6253\u8d77\u6765\uff0c\u5fc5\u662f\u77f3\u7834\u5929\u60ca\uff01 \u8fde\u770b\u53f0\u4e0a\u90a3\u4e48\u9547\u9759\u7684\u201c\u795e\u9f99\u592a\u5b50\u201d\u4e0e\u201c\u5357\u6d77\u9f99\u5973\u201d\uff0c\u4e5f\u4e0d\u7531\u53cc\u53cc\u7ad9\u7acb\u4e86\u8d77\u9876\u5c16\u9ad8\u624b\uff0c\u5408 \u6218\u4e00\u4e2a\u5c11\u5e74\u80dc\u8d1f\u5982\u4f55\uff1f \u6155\u5bb9\u7ea2\u3001\u91d1\u5f69\u98ce\u4e0d\u77e5\u201c\u6d77\u5916\u4e09\u715e\u201d\u7684\u5b9e\u529b\u5982\u4f55\uff0c\u4f46\u89c1\u5c55\u767d\u4ee5\u4e00\u654c\u4e09\uff0c\u573a\u4e2d\u7d27\u5f20\u7684\u6c14\u6c1b\uff0c \u4e5f\u6599\u60f3\u5230\u5bf9\u65b9\u5fc5\u662f\u7edd\u4e16\u9ad8\u624b\uff0c\u7981\u4e0d\u4f4f\u4e3a\u5c55\u54e5\u54e5\u6697\u6697\u803d\u5fc3\u3002 \u5a49\u513f\u5374\u662f\u6df1\u77e5\u201c\u6d77\u5916\u4e09\u715e\u201d\u7684\u5389\u5bb3\uff0c\u89c1\u5c55\u54e5\u54e5\u7b54\u5e94\u4e86\u4eba\u5bb6\u7684\u6311\u6218\uff0c\u53ea\u6025\u5f97\u82b3\u5fc3\u65e0\u4e3b\u4e00\u2014 \u67f3\u7fe0\u7fe0\u867d\u7136\u4eb2\u8eab\u966a\u7ec3\uff0c\u77e5\u9053\u5c55\u54e5\u54e5\u6b64\u65f6\u7684\u529f\u529b\uff0c\u8db3\u53ef\u7765\u7768\u5929\u4e0b\uff0c\u4f46\u542c\u8bf4\u4ed6\u72ec\u6218\u5357\u6d77\u4e09\u5927 \u9876\u5c16\u9ad8\u624b\uff0c\u4e5f\u4e0d\u7531\u6697\u4e3a\u5c55\u54e5\u54e5\u634f\u4e86\u4e00\u628a\u51b7\u6c57\u2026\u2026 \u96f7\u5927\u53d4\u3001\u201c\u592a\u767d\u53cc\u9038\u201d\u53ca\u8339\u8001\u9556\u5934\uff0c\u521a\u521a\u89c1\u8bc6\u8fc7\u767d\u53d1\u5a46\u5a46\u7684\u6b66\u529f\uff0c\u4e00\u4e2a\u4eba\u529f\u529b\u5c31\u6709\u90a3\u4e48 \u5f3a\uff0c\u8981\u662f\u4e09\u4e2a\u4eba\u5408\u8d77\u624b\u6765\uff0c\u90a3\u5a01\u529b\u4e0d\u77e5\u8981\u5927\u591a\u5c11\u500d\uff1f\u66f4\u662f\u4e3a\u5c55\u767d\u7126\u6025\u4e07\u5206\u2026\xb7 \u81f3\u4e8e\u201c\u5357\u6d77\u95e8\u201d\u7684\u4eba\uff0c\u5374\u4e2a\u4e2a\u5fc3\u559c\uff0c\u867d\u7136\u4e0d\u5c11\u4eba\u66fe\u9886\u6559\u8fc7\u5c55\u767d\u9ad8\u5f3a\u7684\u6b66\u529f\uff0c\u4f46\u4ee5\u672c\u95e8\u4e09 \u5f20\u738b\u724c\uff0c\u8981\u6253\u4e00\u4e2a\u5c11\u5e74\u5c55\u767d\uff0c\u90a3\u662f\u7a33\u64cd\u80dc\u7b97\uff0c\u56e0\u6b64\uff0c\u4e00\u4e2a\u4e2a\u90fd\u6000\u4e86\u51e0\u5206\u6b23\u559c\u7684\u5fc3\u60c5\uff0c\u6765\u53c2\u89c2 \u8fd9\u4e00\u573a\u6b66\u6797\u7f55\u89c1\u7684\u5927\u6218\uff01 \u4e0d\u63d0\u53cc\u65b9\u89c2\u6218\u4e4b\u4eba\u7684\u5fe7\u559c\u53c2\u534a\uff0c\u5355\u8bf4\u957f\u9aef\u8001\u4eba\u89c1\u5c55\u767d\u63a5\u53d7\u4e86\u4ed6\u7684\u6311\u6218\uff0c\u6ee1\u8138\u6b23\u559c\u4e4b\u60c5\uff0c \u5411\u5c55\u767d\u6311\u8d77\u5927\u62c7\u6307\u6765\uff0c\u8d5e\u9053\uff1a\u201c\u5c0f\u54e5\u513f\uff0c\u4e86\u4e0d\u8d77\uff01\u4f60\u53ef\u79f0\u4e3a\u8fd1\u767e\u5e74\u6765\uff0c\u5929\u4e0b\u7b2c\u4e00\u5947\u4eba1\u201d \u5c55\u767d\u89c1\u957f\u9aef\u8001\u4eba\u51fa\u4e8e\u771f\u5fc3\u5730\u606d\u7ef4\u81ea\u5df1\uff0c\u53cd\u800c\u6709\u70b9\u4e0d\u597d\u610f\u601d\uff0c\u6536\u4e86\u62db\u5f0f\uff0c\u4e00\u62b1\u62f3\uff0c\u9053\uff1a \u201c\u8001\u5148\u751f\u8fc7\u5956\u4e86\u3002\u201d \u767d\u53d1\u5a46\u5a46\u5440\u5440\u5e72\u7b11\u9053\uff1a\u201c\u5c0f\u54e5\u513f\u4e0d\u5fc5\u5ba2\u6c14\uff0c\u6211\u4eec\u8001\u5934\u5b50\u8bf4\u7684\u662f\u5b9e\u8bdd\uff01\u8001\u5b9e\u544a\u8bc9\u4f60\u5427\uff0c\u6211 \u4eec\u4e09\u4e2a\u8001\u4e0d\u6b7b\u81ea\u8ba4\u4e3a\u5929\u4e0b\u65e0\u654c\uff0c\u8fde\u6211\u4eec\u7684\u4e3b\u5b50\u2018\u5357\u6d77\u4e00\u541b\u2019\uff0c\u4e5f\u4e0d\u6562\u8bf4\u662f\u6211\u4eec\u4e09\u4e2a\u4eba\u7684\u5bf9 \u624b\u2026\u2026\u201d \u767d\u53d1\u5a46\u5a46\u8bf4\u5230\u8fd9\u91cc\uff0c\u957f\u9aef\u8001\u4eba\u76f4\u6253\u773c\u8272\uff0c\u5728\u770b\u53f0\u4e0a\u7ad9\u7740\u7684\u201c\u9f99\u795e\u592a\u5b50\u201d\u4e0e\u201c\u5357\u6d77\u9f99\u5973\u201d \u66f4\u662f\u989c\u8272\u618e\u53d8\uff01 \u4f46\u8001\u5a46\u5b50\u5fc3\u76f4\u53e3\u5feb\uff0c\u4e0d\u7ba1\u4f17\u4eba\u7684\u53cd\u5e94\u5982\u4f55\uff0c\u4ecd\u7167\u76f4\u8bf4\u4e0b\u53bb\u9053\uff1a\u201c\u4eca\u591c\u5c0f\u54e5\u513f\u4e00\u4eba\u72ec\u6218\u6211 \u4eec\u4e09\u4eba\uff0c\u5c82\u4e0d\u662f\u524d\u65e0\u53e4\u4eba\uff1f\u201d \u5c55\u767d\u9887\u4e3a\u8be7\u5f02\u9053\uff1a\u201c\u4f60\u4eec\u4e09\u4f4d\uff0c\u65e2\u662f\u6b66\u529f\u76d6\u4e16\uff0c\u53c8\u4f55\u5fc5\u7518\u5fc3\u53d7\u522b\u4eba\u9a71\u4f7f\u2026\u2026\u201d \u201c\u4f5b\u5370\u6cd5\u5e08\u201d\u6025\u63d2\u53e3\u9053\uff1a\u201c\u81ed\u8001\u5a46\u5b50\uff0c\u5c31\u4f1a\u4fe1\u53e3\u5f00\u6cb3\u2026\u2026\u201d \u957f\u9aef\u8001\u4eba\u5fae\u5fae\u4e00\u53f9\u9053\uff1a\u201c\u8d3c\u79c3\uff0c\u4eca\u591c\u6211\u4eec\u4e09\u4eba\u9047\u5230\u7edd\u4ee3\u5947\u4eba\u8981\u8bf4\u8001\u5b9e\u8bdd\uff01\u201d \u63a5\u7740\u53c8\u5bf9\u5c55\u767d\u9053\uff1a\u201c\u6211\u4eec\u4e09\u4e2a\u8001\u4e0d\u6b7b\u7684\u81ea\u6709\u4e0d\u5f97\u5df2\u7684\u82e6\u8877\uff0c\u73b0\u5728\u6ca1\u6709\u65f6\u95f4\u591a\u8bf4\u2026\u2026\u201d \u5ffd\u7136\u9762\u8272\u4e00\u6574\uff0c\u9053\uff1a\u201c\u8fc7\u53bb\u7684\u4e0d\u8c08\uff01\u6211\u4eec\u4e09\u4e2a\u8001\u4e0d\u6b7b\u7684\u5408\u8d77\u624b\u6765\uff0c\u6253\u4f60\u4e00\u4e2a\u5c0f\u5a03\u5a03\uff01\u53ef \u8bf4\u662f\u5343\u8f7d\u96be\u9047\u7684\u76db\u4f1a\uff0c\u76db\u4f1a\u96be\u9022\uff0c\u54b1\u4eec\u4eca\u591c\u7684\u80dc\u8d25\uff0c\u8981\u6709\u4e2a\u8d4c\u6ce8\u201c \u5c55\u767d\u89c1\u4ed6\u4eec\u4e09\u4e2a\u8a00\u8bed\u7387\u76f4\u8bd9\u8c10\uff0c\u5fc3\u4e2d\u51cf\u5c11\u4e86\u51e0\u5206\u654c\u610f\uff0c\u95fb\u8a00\u9053\uff1a\u201c\u4e0d\u77e5\u8001\u524d\u8f88\u8981\u8d4c\u4e9b\u4ec0 \u4e48\uff1f\u201d \u957f\u9aef\u8001\u4eba\u542c\u5c55\u767d\u5c0a\u79f0\u4ed6\u4e3a\u8001\u524d\u8f88\uff0c\u4e00\u65f6\u559c\u5f97\u6293\u8033\u6414\u816e\uff0c\u6478\u4e86\u534a\u5929\u80e1\u5b50\uff0c\u624d\u8bf4\u9053\uff1a\u201c\u6211\u4eec \u4e09\u4e2a\u8001\u4e0d\u6b7b\u7684\u8981\u8d25\u4e86\uff0c\u4ece\u6b64\u9000\u51fa\u5357\u6d77\u95e8\uff0c\u4e0d\u95ee\u6c5f\u6e56\u662f\u975e\u3002\u5982\u679c\u5c0f\u54e5\u513f\u8d25\u4e86\uff0c\u4e5f\u8981\u548c\u6211\u4eec\u4e00 \u6837\uff0c\u9000\u51fa\u4e2d\u539f\u6b66\u6797\uff0c\u4e0d\u95ee\u6c5f\u6e56\u662f\u975e\u3002\u8fd9\u6837\u516c\u5e73\u7f62\uff1f\u201d \u5c55\u767d\u672c\u662f\u5fe0\u539a\u8bda\u5b9e\u7684\u9752\u5e74\uff0c\u6709\u70b9\u50bb\u91cc\u50bb\u6c14\uff0c\u4eca\u5929\u4e0d\u77e5\u600e\u4e48\u7a81\u7136\u806a\u660e\u8d77\u6765\uff0c\u4e4d\u4e00\u542c\u957f\u9aef\u8001 \u4eba\u6240\u63d0\u7684\u8d4c\u6ce8\uff0c\u4f3c\u4e4e\u5fae\u4e0d\u8db3\u9053\uff0c\u4f46\u4ed4\u7ec6\u4e00\u60f3\uff0c\u53ef\u53c8\u4e0d\u662f\u90a3\u4e48\u56de\u4e8b\uff01 \u4e8e\u662f\u95ee\u9053\uff1a\u201c\u8fd9\u8fd8\u8981\u591a\u52a0\u8bf4\u660e\uff0c\u4f60\u4eec\u4e09\u4f4d\u9000\u51fa\u2018\u5357\u6d77\u95e8\u2019\uff0c\u662f\u5426\u8fd8\u53ef\u4ee5\u5728\u4e2d\u539f\u7acb\u8db3\uff1f\u5728 \u4e0b\u9000\u51fa\u4e2d\u539f\u6b66\u6797\uff0c\u662f\u5426\u8fde\u5728\u4e2d\u539f\u7acb\u8db3\u90fd\u4e0d\u53ef\u4ee5\u4e86\u5462\uff1f\u201d \u957f\u9aef\u8001\u4eba\u4e00\u542c\uff0c\u9752\u5e74\u4eba\u4e0d\u7b80\u5355\uff0c\u4e8e\u662f\u7b11\u9053\uff1a\u4e0d\u7ba1\u5728\u54ea\u91cc\u7acb\u8db3\uff0c\u603b\u4ee5\u2018\u4e0d\u95ee\u6c5f\u6e56\u662f\u975e\u2019\u4e3a \u91cd\u70b9\u5c31\u597d\u4e86\u3002\u4e5f\u5c31\u662f\u8bf4\u51e1\u662f\u6b66\u6797\u4e2d\u7684\u4e8b\uff0c\u4e0d\u8bb8\u63d2\u624b\u8fc7\u95ee\uff0c\u81f3\u4e8e\u5b89\u8eab\u7acb\u811a\u7684\u5730\u70b9\uff0c\u4e0d\u9650\u4e8e\u4e2d\u539f \u8fd8\u662f\u6d77\u5916\uff0c\u603b\u4ee5\u907f\u514d\u4e0e\u6b66\u6797\u4eba\u7269\u89c1\u9762\u4e3a\u51c6\u3002\u600e\u4e48\u6837\uff1f\u201d \u6b64\u65f6\u201c\u5357\u6d77\u9f99\u5973\u201d\u98de\u5feb\u63a0\u4e86\u201c\u9f99\u795e\u592a\u5b50\u201d\u4e00\u773c\uff0c\u201c\u9f99\u795e\u592a\u5b50\u201d\u8138\u8272\u60e8\u53d8\uff0c\u521a\u8981\u8dc3\u8eab\u4e0b \u5730\u2026\u2026 \u4e0d\u8fc7\uff0c\u4f17\u4eba\u90fd\u6ce8\u610f\u5c55\u767d\u4e0e\u201c\u6d77\u5916\u4e09\u715e\u201d\u7684\u8c08\u5224\u4e0a\uff0c\u5bf9\u4ed6\u5144\u59b9\u4e8c\u4eba\u7684\u52a8\u4f5c\uff0c\u65e0\u4eba\u770b\u5230\uff01 \u5c31\u5728\u201c\u9f99\u795e\u592a\u5b50\u201d\u5c06\u8981\u8dc3\u4e0b\u4e4b\u9645\u3002 \u5c55\u767d\u9053\u3002\u201d\u8001\u524d\u8f88\u529e\u6cd5\u867d\u597d\uff0c\u4f46\u5728\u4e0b\u6b49\u96be\u7167\u529e1\u201d \u957f\u9aef\u8001\u4eba\u4e00\u6115\uff0c\u9053\uff1a\u201c\u5c0f\u54e5\u513f\uff0c\u8fd8\u6709\u4ec0\u4e48\u8bdd\u8bf4\uff1f\u201d \u5c55\u81ea\u9053\uff1a\u201c\u5728\u4e0b\u7236\u4ec7\u672b\u62a5\uff0c\u9664\u975e\u5c55\u767d\u5df2\u6b7b\uff0c\u53ea\u8981\u7559\u5c55\u767d\u4e09\u5bf8\u6c14\u5728\uff0c\u8fd9\u6740\u7236\u4e4b\u4ec7\uff0c\u975e\u62a5\u4e0d \u53ef\uff01\u201d \u957f\u9aef\u8001\u4eba\u4e00\u6296\u957f\u9aef\uff0c\u9053\uff1a\u201c\u7236\u4ec7\u4e0d\u5171\u6234\u5929\uff0c\u7406\u5e94\u5f53\u62a51\u4f46\u4e0d\u77e5\u5c0f\u54e5\u513f\u6740\u7236\u4ec7\u4eba\u662f\u8c01\uff1f\u201d \u5c55\u767d\u4e00\u5b57\u4e00\u987f\uff0c\u65a9\u9489\u622a\u94c1\u5730\u9053\uff1a\u201c\u9752\u86a8\u795e\u2019\u91d1\u4e5d\uff01\u201d \u767d\u53d1\u5a46\u5a46\u7a81\u7136\u54a7\u5634\u5927\u7b11\uff0c\u7b11\u5f97\u6ee1\u5934\u767d\u53d1\u4e71\u98a4\uff01 \u5c55\u767d\u89c1\u767d\u53d1\u5a46\u5a46\u7b11\u5f97\u7a81\u5140\uff0c\u9887\u4e0d\u9ad8\u5174\u5730\u9053\uff1a\u201c\u4e0d\u77e5\u8001\u524d\u8f88\u6709\u4ec0\u4e48\u597d\u7b11\u7684\uff1f\u201d \u767d\u53d1\u5a46\u5a46\u624b\u6307\u5899\u8fb9\u7ed1\u4eba\u67f1\u90a3\u8fb9\uff0c\u5374\u4e00\u65f6\u6b62\u4e0d\u4f4f\u7b11\u58f0\uff0c\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002 \u957f\u9aef\u8001\u4eba\u9053\uff1a\u201c\u9752\u86a8\u795e\u2019\u91d1\u4e5d\u5df2\u6b7b\uff0c\u5c0f\u54e5\u513f\u8fd9\u6869\u5fc3\u613f\u662f\u7b97\u4e86\u5566\uff01\u201d \u5c55\u767d\u987a\u7740\u767d\u53d1\u5a46\u5a46\u624b\u6307\u65b9\u5411\u770b\u53bb\uff0c\u5899\u8fb9\u5730\u4e0b\u4e00\u8f86\u7834\u788e\u4e86\u7684\u8f6e\u6905\uff0c\u7ed1\u4eba\u67f1\u4e0a\u7ed1\u7740\u8840\u6dcb\u6dcb\u7684 \u4e00\u6bb5\u6b8b\u80a2\uff0c\u53cc\u81c2\u53cc\u817f\u4ee5\u53ca\u9879\u4e0a\u4eba\u5934\uff0c\u5747\u5df2\u88ab\u5200\u780d\u843d\uff0c\u6b63\u662f\u201c\u5357\u6d77\u95e8\u201d\u201c\u4e94\u5200\u5206\u5c38\u201d\u7684\u6b8b\u9177\u5211 \u6cd5\uff01 \u518d\u4e00\u770b\u7ed1\u5728\u67f1\u5b50\u4e0a\u7684\u90a3\u4e00\u6bb5\u8840\u6dcb\u6dcb\u7684\u80a2\u4f53\uff0c\u56e2\u82b1\u5957\u80a9\u9526\u7f0e\u888d\uff0c\u54d1\u7136\u800c\u609f\uff0c\u90a3\u6b63\u662f\u201c\u9752\u86a8 \u795e\u201d\u91d1\u4e5d\u7684\u8863\u9970\uff01 \u7a81\u542c\u4e00\u58f0\u60b2\u60e8\u7684\u54ed\u53f7\uff0c\u63a5\u7740\u4e00\u6761\u4eba\u5f71\uff0c\u98de\u6251\u81f3\u201c\u9752\u86a8\u795e\u201d\u91d1\u4e5d\u7684\u6b8b\u8eaf\u65ad\u80a2\u4e4b\u524d\uff0c\u6251\u5730\u53f7 \u638f\u5927\u54ed\u8d77\u6765\uff01 \u4ed6\u52aa\u529b\u6452\u9664\u4e86\u5fc3\u4e2d\u6742\u4e71\u7684\u611f\u60f3\uff0c\u4ecd\u6602\u7136\u9053\uff1a\u201c\u9752\u9556\u795e\u2019\u56fa\u7136\u5df2\u6b7b\uff0c\u4f46\u5728\u4e0b\u6740\u7236\u4ec7\u4eba\uff0c\u8fd8 \u6709\u2018\u4e7e\u5764\u638c\u2019\u4e91\u5b97\u9f99\uff01\u201d \u957f\u9aef\u8001\u4eba\u9762\u542b\u6f0f\u8be1\u7b11\u5bb9\uff0c\u9053\uff1a\u201c\u770b\u6765\u5c0f\u54e5\u513f\u6740\u7236\u4ec7\u4eba\u8fd8\u4e0d\u5c11\uff0c\u4e0d\u77e5\u8fd8\u6709\u54ea\u4e00\u4e2a\uff1f\u201d \u5c55\u767d\u63a5\u7740\u9053\uff1a\u201c\u6df7\u5143\u6307\u2019\u53f8\u7a7a\u664b1\u201d \u957f\u9aef\u8001\u4eba\u9053\uff1a\u63a5\u7740\u8bf4\u4e0b\u53bb1\u201d \u5c55\u767d\u9053\uff1a\u201c\u9738\u738b\u97ad\u2019\u6a0a\u975e1\u201d \u957f\u9aef\u8001\u4eba\u54c8\u54c8\u5927\u7b11\u9053\u3002\u201d\u5c0f\u54e5\u513f\u6740\u7236\u4ec7\u4eba\uff0c\u90fd\u662f\u4e2d\u539f\u6b66\u6797\u8c6a\u95e8\u5de8\u9738\uff0c\u4f46\u4e0d\u77e5\u8fd8\u6709\u6ca1 \u6709\uff1f\u201d \u5c55\u767d\u9053\uff1a\u201c\u6ca1\u6709\u4e861\u201d \u767d\u53d1\u5a46\u5a46\u6324\u7740\u5806\u6ee1\u76b1\u7eb9\u7684\u8138\u9053\uff1a\u201c\u90a3\u5c0f\u54e5\u513f\uff0c\u4f60\u5c3d\u53ef\u4ee5\u8ddf\u6211\u4eec\u4e09\u4e2a\u8001\u4e0d\u6b7b\u7684\u653e\u624b\u4e00\u62fc\uff0c \u4e2d\u539f\u6b66\u6797\uff0c\u518d\u4e5f\u6ca1\u6709\u4ec0\u4e48\u4e8b\u53ef\u4ee4\u4f60\u653e\u5fc3\u4e0d\u4e0b\u7684\u4e86\uff01\u201d \u5c55\u767d\u4e0d\u4fe1\u7684\u9053\uff1a\u201c\u83ab\u975e\u4e2d\u539f\u56db\u5927\u8c6a\u95e8\uff0c\u9547\u6c5f\u6a0a\u975e\uff0c\u90fd\u88ab\u4f60\u4eec\u2018\u5357\u6d77\u95e8\u2019\u65a9\u5c3d\u6740\u7edd\u4e86\uff1f\u201d \u201c\u4f5b\u5370\u6cd5\u5e08\u201d\u9053\uff1a\u201c\u96be\u9053\u6211\u4eec\u4e09\u4e2a\u8001\u4e0d\u6b7b\u7684\uff0c\u8fd8\u4f1a\u9a97\u4f60\u4e00\u4e2a\u5a03\u5a03\u5417\uff1f\u201d \u5c55\u767d\u9053\uff1a\u201c\u6b64\u8bdd\u5f53\u771f\uff1f\u201d \u957f\u9aef\u8001\u4eba\u9762\u5bb9\u4e00\u6574\uff0c\u9053\uff1a\u201c\u5c0f\u54e5\u513f\u4f60\u4e5f\u5728\u6c5f\u6e56\u4e0a\u5b9a\u52a8\u8fc7\uff0c\u5f53\u77e5\u6c5f\u6e56\u4e0a\u7684\u4eba\u4e00\u8a00\u4e5d\u9f0e\uff0c \u2018\u6d77\u5916\u4e09\u715e\u2019\u4e0d\u662f\u6ca1\u6ca1\u65e0\u95fb\u4e4b\u8f88\uff01\u2026\u2026\u201d \u5c55\u767d\u7a81\u7136\u5411\u4e09\u4eba\u4e00\u62b1\u62f3\uff0c\u6df1\u65bd\u4e00\u672d\uff0c\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u5728\u4e0b\u6740\u7236\u4e4b\u4ec7\u5df2\u62a5\uff0c\u8981\u8c22\u8c22\u4e09\u4f4d \u4e86\uff01\u201d \u767d\u53d1\u5a46\u5a46\u9053\uff1a\u201c\u5c0f\u54e5\u513f\uff0c\u4e0d\u8981\u5148\u8c22\u6211\u4eec\u4e09\u4e2a\u8001\u4e0d\u6b7b\u7684\uff0c\u771f\u6b63\u4ee3\u4f60\u8bdb\u5374\u6740\u7236\u4ec7\u4eba\u7684\uff0c\u5e94\u8be5 \u662f\u6211\u4eec\u5c11\u4e3b\uff0c\u5c0f\u54e5\u513f\u5e94\u8be5\u8c22\u5979\uff01\u201d \u5c55\u767d\u6389\u5934\u4e00\u770b\uff0c\u767d\u53d1\u5a46\u5a46\u624b\u6307\u7684\u662f\u201c\u5357\u6d77\u9f99\u5973\u201d\uff0c\u800c\u201c\u5357\u6d77\u9f99\u5973\u201d\u4e00\u5bf9\u4eae\u5982\u6668\u661f\u7684\u5927\u773c \u775b\u4e5f\u6b63\u76ef\u7740\u671b\u4ed6\u3002 \u4ed6\u60c5\u5b7d\u7ea0\u7f20\uff0c\u5df2\u6709\u6570\u4e2a\u5c11\u5973\u5bf9\u4ed6\u949f\u60c5\uff0c\u4f7f\u4ed6\u65e0\u6cd5\u5e94\u4ed8\uff0c\u56e0\u6b64\uff0c\u4e00\u770b\u5230\u201c\u5357\u6d77\u9f99\u5973\u201d\u7528\u8fd9 \u79cd\u773c\u5149\u770b\u4ed6\uff0c\u5413\u5f97\u5fd9\u8f6c\u56de\u5934\u6765\uff0c\u5bf9\u201c\u6d77\u5916\u4e09\u715e\u201d\u9053\uff1a\u201c\u8c22\u4e00\u4e0d\u8c22\u4e8c\uff01\u201d \u201c\u5c55\u767d\u6b64\u65f6\u7236\u4ec7\u5df2\u62a5\uff0c\u5012\u771f\u65e0\u7275\u65e0\u6302\uff0c\u8fd9\u4e2a\u8d4c\u662f\u6253\u5b9a\u4e86\uff0c\u5c31\u8bf7\u4e09\u4f4d\u524d\u8f88\u8d50\u62db\u5427\uff01\u201d \u8bf4\u7f62\uff0c\u201c\u545b\u5577\u201d\u4e00\u58f0\uff0c\u62bd\u51fa\u80cc\u4e0a\u201c\u65e0\u60c5\u78a7\u5251\u201d\uff0c\u5de6\u624b\u85cf\u5251\uff0c\u7a33\u538b\u8098\u540e\uff0c\u53f3\u624b\u634f\u5251\u4fa0\uff0c\u659c \u6307\u9f50\u7709\uff0c\u5251\u62db\u4eae\u5f0f\uff0c\u4ecd\u53ea\u4ed6\u90a3\u4e00\u5957\u4e0d\u5927\u9ad8\u660e\u7684\u201c\u4e09\u624d\u5251\u201d\u6cd5\uff01 \u201c\u6d77\u5916\u4e09\u715e\u201d\u4e00\u89c1\uff0c\u5404\u81ea\u79fb\u8eab\u9519\u6b65\uff0c\u6210\u4e09\u89d2\u5f62\u628a\u5c55\u767d\u56f4\u5728\u4e2d\u592e\u3002\u957f\u9aef\u8001\u4eba\u8eab\u5f62\u76f4\u7acb\uff0c\u53cc \u638c\u4e00\u53cd\u4e00\u6b63\u5e73\u7f6e\u80f8\u524d\uff0c\u4e43\u662f\u201c\u6000\u62b1\u65e5\u7528\u201d\u7684\u59ff\u52bf\uff1b\u767d\u53d1\u5a46\u5a46\u584c\u80a9\u8de8\u6b65\uff0c\u53f3\u624b\u5355\u6307\u575a\u7acb\u5982\u9525\uff0c \u6446\u5728\u53f3\u8033\u4e0b\u65b9\uff1b\u4f5b\u5370\u6cd5\u5e08\uff0c\u5374\u662f\u53cc\u638c\u6276\u5730\u5176\u8e1e\u8e72\u5750\uff0c\u72b6\u5982\u9752\u86d9\uff0c\u770b\u6837\u5b50\u957f\u9aef\u8001\u4eba\u8981\u4ee5\u201c\u5927\u624b \u5370\u201d\u5e94\u654c\uff0c\u767d\u53d1\u5a46\u5a46\u4eae\u7684\u67b6\u5f0f\u5df2\u662f\u201c\u641c\u9b42\u6307\u201d\uff0c\u4f5b\u5370\u6cd5\u5e08\u201d\u662f\u7528\u7684\u201c\u86e4\u87c6\u529f\u201d\uff0c\u4e09\u8001\u5747\u8981\u4ee5 \u5e73\u751f\u7edd\u827a\u6765\u5408\u6218\u5c55\u767d\uff01 \u201c\u6d77\u5916\u4e09\u715e\u201d\u5355\u6253\u72ec\u6597\uff0c\u90fd\u5403\u8fc7\u5c55\u767d\u7684\u4e8f\uff0c\u6b64\u65f6\u4e09\u4eba\u5408\u6218\u5c55\u767d\uff0c\u4e00\u4eae\u5f0f\u4fbf\u628a\u5404\u4eba\u60ca\u4e16\u7684 \u529f\u592b\u6446\u4e86\u51fa\u6765\uff0c\u770b\u6837\u5b50\u5e76\u4e0d\u50cf\u201c\u9f99\u795e\u592a\u5b50\u201d\u6240\u803d\u5fc3\u7684\u90a3\u6837\u4f1a\u201c\u653e\u6c34\uff3c\u76f8\u53cd\u7684\uff0c\u4e09\u4eba\u662f\u6709\u628a\u5c55 \u767d\u6298\u8fb1\u5728\u624b\u4e0b\u7684\u51b3\u5fc3\u3002 \u5c55\u767d\u89c1\u4e09\u4eba\u5f92\u624b\u6446\u5f00\u67b6\u5f0f\uff0c\u6717\u58f0\u9053\uff1a\u201c\u8bf7\u4e09\u4f4d\u8001\u524d\u8f88\u4eae\u5175\u5668\uff01\u201d \u201c\u6d77\u5916\u4e09\u715e\u201d\u540c\u65f6\u8bf4\u9053\uff1a\u6211\u4eec\u4e09\u4e2a\u8001\u4e0d\u6b7b\u7684\uff0c\u4ece\u6765\u4e0d\u5e26\u5175\u5668\uff0c\u800c\u4e14\u6211\u4eec\u4e09\u654c\u4e00\uff0c\u5f92\u624b\u4e5f \u7f62\u2026\u2026\u201d \u672c\u6765\u5c55\u767d\u4ed7\u5251\u72ec\u6218\u4e09\u5927\u7edd\u4e16\u9ad8\u624b\uff0c\u4e5f\u7b97\u96be\u80fd\uff0c\u4f46\u5c55\u767d\u5929\u751f\u50b2\u9aa8\uff0c\u4ed6\u4e00\u56de\u624b\uff0c\u201c\u545b\u201d\u7684\u4e00 \u58f0\uff0c\u53c8\u628a\u201c\u65e0\u60c5\u78a7\u5251\u201d\u8fd8\u9798\u3002 \u5f53\u65f6\u53cc\u638c\u4e00\u632b\uff0c\u6446\u51fa\u4e86\u201c\u5929\u4f5b\u964d\u9b54\u638c\u201d\u7684\u62db\u5f0f\uff0c\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u5728\u4e0b\u4e5f\u5c31\u7a7a\u624b\u5949\u966a\u4e86\uff0c \u8bf7\uff01\u201d \u96f7\u5927\u53d4\u4e0e\u201c\u592a\u767d\u53cc\u9038\u201d\u6697\u6697\u6447\u6447\u5934\uff0c\u5fc3\u8bf4\uff1a\u201c\u5f53\u771f\u662f\u8ddf\u4ed6\u7236\u4eb2\u2018\u9739\u96f3\u5251\u2019\u5c55\u4e91\u5929\uff0c\u4e00\u4e2a \u6837\u7684\u813e\u6c14\uff01\u2026\u201d \u4e0d\u7531\u5f97\u66f4\u4e3a\u4ed6\u591a\u803d\u4e86\u4e00\u4efd\u5fc3\u3002\u4f46\u6b64\u65f6\u6b64\u9645\uff0c\u5c40\u5916\u4eba\u53c8\u65e0\u6cd5\u51fa\u9762\u963b\u6b62\uff0c\u53ea\u6709\u7ad9\u5728\u4e00\u65c1\u6697\u6697 \u4e3a\u5c55\u767d\u7126\u6025\u2026\u3002\xb7 \u957f\u9aef\u8001\u4eba\u7b11\u9053\uff1a\u201c\u5c0f\u54e5\u513f\u5148\u8bf7\uff01\u201d \u201c\u6211\u4eec\u4e09\u4e2a\u8001\u4e0d\u6b7b\uff0c\u4ee5\u4e09\u654c\u4e00\uff0c\u5df2\u7ecf\u5360\u4e86\u4fbf\u5b9c\u4e86\uff01\u201d \u5c55\u767d\u9053\uff1a\u201c\u5e7c\u4e0d\u6b3a\u957f\uff01\u8fd8\u662f\u4e09\u4f4d\u8001\u4eba\u5bb6\u5148\u8bf7\uff01\u201d \u767d\u53d1\u5a46\u5a46\u5440\u5440\u7b11\u9053\uff1a\u201c\u5c3d\u5ba2\u6c14\u4e2a\u4ec0\u4e48\u52b2\uff1f\u8001\u5a46\u5b50\u5148\u51fa\u624b\uff01\u201d \u8bf4\u7f62\uff0c\u201c\u6388\u9b42\u6307\u201d\u795e\u529f\uff0c\u8fd0\u81f3\u5dc5\u5cf0\uff0c\u731b\u7136\u5212\u51fa\uff01 \u5931\u98ce\u72c2\u5578\uff0c\u731b\u5411\u5c55\u767d\u5288\u9762\u70b9\u6765\uff01 \u7b2c\u4e94\u5341\u7ae0\u3000\u751f\u6b7b\u4e00\u638c>> \u53e4\u9f99\u300a\u5251\u5ba2\u884c\u300b \u7b2c\u4e94\u5341\u7ae0\u3000\u751f\u6b7b\u4e00\u638c \u8fd9\u771f\u662f\u6b66\u6797\u7f55\u89c1\u7684\u4e00\u573a\u6076\u6218\uff0c\u53ea\u89c1\u638c\u8d77\u5904\u98ce\u4e91\u53d8\u8272\uff0c\u6307\u843d\u5904\u7834\u5929\u60ca\uff0c\u53ea\u6253\u5f97\u5929\u660f\u5730\u6697\uff0c \u706f\u706b\u65e0\u5149\uff0c\u5c31\u5728\u575a\u786c\u903e\u77f3\u7684\u7ec3\u6b66\u573a\uff0c\u6380\u8d77\u7684\u5c18\u5934\u4e5f\u8db3\u6709\u5341\u6570\u6587\u9ad8\uff01 \u8fd9\u65f6\uff0c\u5df2\u770b\u4e0d\u6e05\u56db\u4eba\u7684\u4eba\u5f71\uff0c\u53ea\u80fd\u770b\u5230\u56db\u56e2\u58a8\u98ce\u6025\u65cb\uff0c\u72b9\u5982\u80a1\u5f3a\u70c8\u7684\u5377\u98ce\u7ea0\u7f20\u591c\u4e00\u8d77\uff0c \u7ffb\u817e\u6447\u6eda\uff0c\u72c2\u5578\u523a\u8033\uff01 \u6643\u773c\u95f4\uff0c\u5c55\u767d\u72ec\u6218\u4e09\u715e\uff0c\u5df2\u7136\u6253\u4e86\u4e09\u4e94\u5341\u4e2a\u7167\u9762\uff01 \u6b64\u65f6\uff0c\u638c\u98ce\u52b2\u6d41\uff0c\u6108\u6765\u6108\u731b\uff0c\u6fc0\u8d77\u6765\u7684\u5f81\u4f01\u6108\u5bbd\u6108\u9ad8\uff0c\u53ea\u89c1\u9ec4\u6eda\u6eda\uff0c\u6012\u6d41\u6fc0\u6e4d\uff0c\u9f99\u5377\u98ce \u5982\u66b4\u96e8\u8fc7\u9e23\uff0c\u53c8\u7ec6\u98ce\u5de8\u6d6a\u7684\u5927\u6d77\u4e0a\uff0c\u7fa4\u9f99\u95f9\u6d77\uff0c\u56db\u80a1\u9f99\u6e10\u6e10\u5408\u6210\u4e00\u822c\uff0c\u543c\u543c\u6012\u5377\uff0c\u52bf\u7b80\u76f4\u60ca \u4eba\u5df2\u6781\uff01 \u5728\u56db\u5468\u56f4\u89c2\u7684\u4f17\u4eba\u7ad9\u4e0d\u4f4f\u811a\uff0c\u8eab\u4e0d\u7531\u5df1\u5730\u7eb7\u7eb7\u540e\u9000\uff01\u4e8c\u4e09\u5341\u5f00\u5916\u7684\u706f\u7b3c\u706b\u628a\uff0c\u5747\u81ea\u6447\u6643 \u4e0d\u5b9a\uff0c\u660f\u660f\u6b32\u706d\uff01 \u8fd9\u65f6\u4f17\u4eba\u5df2\u65e0\u6cd5\u770b\u6e05\u56db\u4eba\u52a8\u624b\u7684\u62db\u5f0f\uff0c\u4e5f\u4e0d\u77e5\u6253\u4e86\u591a\u5c11\u4e2a\u9762\u3002\u53cd\u6b63\u65f6\u95f4\u4e0d\u77ed\u4e86\uff0c\u573a\u4e2d\u98ce \u58f0\u6e10\u51cf\uff0c\u4eba\u5f64\u53c8\u6e10\u6e10\u73b0\u4e86\u51fa\u6765E\u56db\u4e2a\u4eba\u5934\u4e0a\u5747\u5df2\u5192\u8d77\u817e\u817e\u7684\u6c57\u6c14\uff01 \u60f3\u4ee5\u8fd9\u79cd\u7edd\u4e16\u795e\u529f\u5e94\u654c\uff0c\u867d\u7136\u5a01\u529b\u5f3a\u5927\uff0c\u4f46\u4e5f\u6700\u8017\u771f\u529b\uff0c\u201c\u6d77\u4e09\u715e\u201d\u4e09\u767e\u4f59\u5e74\u7684\u82e6\u4fee\u82e6 \u7ec3\uff0c\u5c1a\u4e14\u5934\u4e0a\u89c1\u4e86\u6c57\uff0c\u5c55\u767d\u7eb5\u7136\u795e\u529f\u4e16\uff0c\u4e5f\u4e0d\u7531\u4e0d\u7d2f\u5f97\u6c14\u5598\u5401\u5401\uff01 \u573a\u5916\u89c2\u6218\u7684\u90a3\u4e48\u591a\u4eba\uff0c\u8fde\u6562\u5598\u4e00\u53e3\u5927\u6c14\u7684\u4eba\u90fd\u6ca1\u6709\uff0c\u663e\u89c1\u8fd9\u4e00\u6218\uff0c\u7684\u786e\u662f\u76db\u51b5\u7a7a\u524d\uff01 \u56db\u4e2a\u4eba\u7531\u5feb\u653b\u5feb\u6253\uff0c\u53d8\u6210\u56f4\u573a\u6e38\u5b9a\uff0c\u62db\u5f0f\u65e2\u6162\u4e86\uff0c\u4f17\u4eba\u5df3\u80fd\u770b\u5f97\u6e05\u695a\uff0c\u53ea\u8d1d\u56db\u4eba\u4e4b\u4e2d\uff0c \u65e0\u8bba\u4efb\u4f55\u4eba\u653b\u51fa\u4e00\u62db\uff0c\u5747\u662f\u5168\u8eab\u529f\u529b\u4e4b\u6240\u805a\uff0c\u6bcf\u4e00\u62db\u843d\u7a7a\uff0c\u5747\u53ef\u628a\u575a\u903e\u9752\u77f3\u7684\u5730\u9762\uff0c\u6253\u4e00\u9053 \u6df1\u6c9f\uff0c\u6216\u662f\u4e00\u4e2a\u5927\u5751\uff01 \u770b\u6837\u5b50\u56db\u4e2a\u4eba\u4efb\u4f55\u4e00\u4eba\u7684\u4e00\u6307\u4e00\u638c\uff0c\u5747\u53ef\u5f00\u91d1\u6d1e\u94c1\uff0c\u788e\u77f3\u6210\u7c89\uff01 \u767d\u53d1\u5a46\u5a46\u6027\u60c5\u6700\u6025\u8e81\uff0c\u5979\u5e73\u751f\u5f88\u5c11\u9047\u5230\u654c\u624b\uff0c\u66fe\u72c2\u8a00\u6709\u5728\u5979\u624b\u4e0b\u8d70\u8fc7\u4e09\u62db\u8005\uff0c\u5373\u4e0d\u4e88\u4ee5 \u6740\u5bb3\uff0c\u4eca\u89c1\u4e09\u4eba\u5408\u624b\uff0c\u4e45\u6218\u5c55\u767d\u4e0d\u4e0b\u7acb\u5373\u66b4\u6012\uff0c\u628a\u201c\u641c\u9b42\u6307\u201d\u8fd0\u529f\u8fd0\u81f3\u5dc5\u5cf0\uff1a\u5403\uff01\u55e4\uff01 \u55e4\uff01\u201d\u63a5\u8fde\u731b\u6233\u4e09\u6307\uff0c\u6307\u6307\u5c16\u5578\u7834\u98ce\uff0c\u6307\u6307\u6307\u5411\u5c55\u767d\u8981\u5bb3\uff01 \u5c55\u767d\u6253\u5f97\u4e45\u4e86\uff0c\u4e5f\u6fc0\u8d77\u5c11\u5e74\u50b2\u6027\uff0c\u201c\u5343\u95e8\u98d8\u9999\u6b65\u201d\u3001\u201c\u65e0\u8272\u65e0\u76f8\u8eab\u201d\uff0c\u7fe9\u82e5\u60ca\u9e3f\uff0c\u77eb\u4ea5 \u7684\u6c11\u65cf\uff0c\u63a5\u8fde\u8eb2\u8fc7\u4e09\u6307\uff0c\u201c\u5929\u4f5b\u964d\u9b54\u638c\u201d\u4e00\u62db\u201c\u4f5b\u5149\u666e\u7167\u201d\u65e0\u4fe6\u7f61\u98ce\uff0c\u72c2\u5377\u767d\u53d1\u5a46\u5a46\uff01 \u767d\u53d1\u5a46\u5a46\u62db\u5f0f\u51fa\u624b\u592a\u72e0\uff0c\u7528\u529b\u8fc7\u8001\uff0c\u4e00\u65f6\u6536\u62db\u4e0d\u53ca\uff0c\u8eb2\u5f97\u7565\u4e3a\u6162\u4e86\u4e00\u6162\uff0c\u5de6\u80a9\u88ab\u5c55\u767d\u65e0 \u4fe6\u7684\u638c\u98ce\u626b\u4e86\u4e00\u4e0b\uff0c\u767d\u53d1\u5a46\u5a46\u534a\u8fb9\u8eab\u5b50\u4e00\u9ebb\uff0c\u75db\u5f7b\u5fc3\u80ba\uff0c\u4e0d\u7531\u60e8\u53eb\u4e00\u58f0\uff0c\u4e00\u8def\u8ddf\u8231\u4e94\u516d\u6b65\uff0c \u88ab\u6253\u51fa\u5708\u5916\uff01 \u6240\u5e78\u53ea\u662f\u638c\u98ce\u626b\u4e2d\uff0c\u5982\u82e5\u88ab\u638c\u529b\u51fb\u5b9e\uff0c\u4ee5\u5c55\u767d\u76ee\u524d\u529f\u529b\u6765\u8bf4\uff0c\u7109\u6709\u767d\u53d1\u5a46\u5a46\u7684\u547d\u5728\uff1f \u201c\u4f5b\u5370\u6cd5\u5e08\u201d\u4e00\u89c1\u5c55\u767d\u638c\u4f24\u767d\u53d1\u5a46\u5a46\uff0c\u5fc3\u4e2d\u53c8\u6025\u53c8\u6012\uff0c\u201c\u5471\u5471\u201d\u5389\u5578\uff0c\u53cc\u638c\u5982\u98ce\u8f66\u4e00 \u822c\uff0c\u731b\u5411\u5c55\u767d\u8eab\u540e\u6251\u81f3\uff01 \u5c55\u767d\u4e00\u638c\u9707\u9000\u767d\u53d1\u5a46\u5a46\uff0c\u542c\u5230\u8eab\u540e\u63f4\u98ce\u6012\u543c\uff0c\u77e5\u6709\u6697\u88ad\uff0c\u4e0d\u8eb2\u4e0d\u95ea\uff0c\u201c\u5929\u4f5b\u964d\u9b54\u638c\u201d\u8fd0 \u81f3\u5341\u6210\uff0c\u53cd\u81c2\u5411\u540e\u64a9\u53bb\uff01 \u201c\u8f70\u9686\uff01\u516b\u2014\u58f0\u66b4\u54cd\uff0c\u72b9\u5982\u5730\u88c2\u5c71\u5d29\uff0c\u52b2\u98ce\u56db\u5c04\uff0c\u56de\u65cb\u751f\u98d8\uff0c\u5c18\u6284\u98de\u626c\u4e4b\u4e2d\uff0c\u201c\u4f5b\u5370\u6cd5 \u5e08\u201d\u5df2\u5982\u65ad\u7ebf\u7eb8\u8338\u4e00\u822c\uff0c\u5012\u98de\u51fa\u53bb\uff01 \u5c55\u767d\u8fde\u4f24\u4e8c\u715e\uff0c\u80dc\u5229\u5728\u671b\uff0c\u5c24\u5176\u638c\u4f24\u767d\u53d1\u5a46\u5a46\uff0c\u547c\u5438\u4e4b\u95f4\uff0c\u53cd\u81c2\u9707\u98de\u201c\u4f5b\u5370\u6cd5\u5e08\u201d\uff0c\u8eab \u6cd5\u5de7\uff0c\u62db\u5f0f\u5999\uff0c\u638c\u529b\u6177\u4eba\uff0c\u56db\u5468\u56f4\u89c2\u4e4b\u4eba\uff0c\u4e0d\u5206\u654c\u6211\uff0c\u54c4\u7136\u53eb\u597d\uff01 \u201c\u5357\u6d77\u9f99\u5973\u201d\u3001\u201c\u9f99\u795e\u592a\u5b50\u201d\u989c\u8272\u63ba\u53d8\u2026\u3002\u2019\u96f7\u5927\u53d4\u3001\u8339\u8001\u9556\u5934\u3001\u201c\u592a\u767d\u53cc\u9038\u201d\u4ee5\u53ca\u6155 \u5bb9\u59d0\u59b9\uff0c\u559c\u6781\u6b22\u547c\u2026oo\u2019\u4f46\u5c31\u4f17\u4eba\u7eb7\u7eb7\u4e00\u4e71\u7684\u5f53\u513f\u3002 \u7a81\u7136\u53c8\u662f\u4e00\u58f0\u66b4\u54cd\uff0c\u72b9\u5982\u4e00\u4e2a\u95f7\u96f7\u6253\u5728\u7ec3\u6b66\u573a\u4e0a\uff0c\u58f0\u97f3\u4e4b\u5927\uff0c\u53ea\u9707\u5f97\u4f17\u4eba\u53cc\u8033\u96f7\u9e23\uff0c\u5fc3 \u5934\u72c2\u8df3\uff01 \u4f17\u529b\u72c2\u5377\u800c\u51fa\uff0c\u7adf\u628a\u6570\u5341\u4e08\u5f00\u5916\u7684\u706f\u7b3c\u706b\u628a\u5439\u5f97\u4e00\u6697\uff01 \u4f17\u4eba\u9f50\u58f0\u60ca\u547c\uff0c\u5f85\u706f\u5149\u6697\u800c\u590d\u660e\u65f6\uff0c\u53ea\u89c1\u5c55\u767d\u4fca\u8138\u6cdb\u767d\uff0c\u5634\u89d2\u6ea2\u8840\uff01 \u518d\u4e00\u770b\u957f\u9aef\u8001\u4eba\uff0c\u53d1\u987b\u7686\u70b8\uff0c\u602a\u76ee\u6012\u7741\u5982\u70ac\uff01 \u663e\u89c1\u5c55\u767d\u5403\u4e86\u957f\u9aef\u8001\u4eba\u7684\u4e8f\uff0c\u800c\u4e14\u5185\u8151\u5fc5\u5df2\u8d1f\u4f24\uff1a \u5173\u5fc3\u5c55\u767d\u7684\u96f7\u5927\u53d4\u7b49\u4eba\uff0c\u4e00\u9635\u5927\u54d7\u2026\u2026 \u957f\u9aef\u8001\u4eba\u5927\u903e\u8f66\u8f6e\u7684\u53cc\u638c\uff0c\u53c8\u7f13\u7f13\u4e3e\u8d77\uff0c\u4f5c\u52bf\u5411\u5c55\u767d\u5f53\u9876\u5288\u535c\uff01 \u540c\u65f6\u5634\u4e2d\u563f\u563f\u7b11\u9053\uff1a\u201c\u5c0f\u54e5\u513f1\u8001\u4e0d\u6b7b\u4e09\u4f24\u5176\u4e8c\uff0c\u8fd9\u4e00\u638c\u4e0b\u53bb\u53ef\u8981\u4e86\u4f60\u7684\u5c0f\u547d\uff01\u201d \u5c55\u767d\u8fde\u4f24\u4e8c\u715e\uff0c\u4e0d\u614e\u88ab\u957f\u9aef\u8001\u4eba\u4e00\u638c\u9707\u4f24\uff0c\u6b64\u65f6\u8151\u5185\u8840\u7ffb\u6c14\u6d8c\uff0c\u4f46\u4ecd\u6602\u7136\u4e0d\u60e7\uff0c\u53cc\u638c\u4e4e \u80f8\u800c\u8d77\u529b\u5760\u201c\u4e0d\u89c1\u5f97\uff01\u8001\u524d\u8f88\u4e0e\u5728\u4e0b\u529f\u529b\u6089\u654c\uff0c\u8fd9\u4e00\u638c\u4e0b\u53bb\uff0c\u8fd8\u4e0d\u77e5\u80dc\u8d1f\u5c5e\u8c01\uff1f\u201d \u957f\u9aef\u8001\u4eba\u8f66\u8f6e\u5927\u7684\u5de8\u638c\uff0c\u4e00\u8fb9\u8fdc\u529b\u4e0b\u538b\uff0c\u4e00\u8fb9\u563f\u563f\u8a00\u9053\uff1a\u201c\u5c0f\u54e5\u513f\uff01\u4e0d\u8981\u518d\u901e\u5f3a\u4e86\uff0c\u4f60 \u5df2\u7ecf\u8d1f\u4f24\u5410\u8840\uff01\u201d \u5c55\u767d\u201c\u5929\u4f5b\u964d\u9b54\u638c\u201d\u8fd0\u81f3\u5341\u6210\uff0c\u4e00\u8fb9\u7f13\u7f13\u4e0a\u51fb\uff0c\u4e00\u8fb9\u9053\uff1a\u201c\u8001\u524d\u8f88\u81ea\u5df2\u5fc3\u4e2d\u660e\u767d\uff0c\u4f60\u5185 \u8151\u771f\u6c14\u5df2\u7ecf\u9006\u7a9c1\u201d\u957f\u9aef\u8001\u4eba\u5185\u8151\u771f\u6c14\u8fd8\u771f\u662f\u51e0\u4e4e\u88ab\u5c55\u767d\u638c\u529b\u9707\u6563\uff0c\u89c1\u5c55\u767d\u9053\u51fa\u4ed6\u7684\u9690\u79d8\uff0c \u4e0d\u7531\u6740\u673a\u9661\u8d77\uff0c\u72de\u58f0\u9053\uff1a\u201c\u6211\u8001\u4eba\u5bb6\u672c\u60f3\u4e0e\u4f60\u5206\u51fa\u80dc\u8d1f\u5373\u6b62\uff0c\u5982\u6b64\u8bf4\u6765\uff0c\u5c0f\u54e5\u513f\u4f60\u662f\u6b7b\u5b9a\u4e86 1\u201d\u8bf4\u7f62\uff0c\u771f\u6c14\u4e00\u632f\uff0c\u5185\u529b\u5d29\u51fa\uff0c\u5927\u638c\u5982\u5c71\u5d29\u6d77\u5578\u4e00\u822c\uff0c\u7a81\u7136\u52a0\u5feb\u4e86\u901f\u5ea6\uff0c\u5411\u5c55\u767d\u9876\u4e0a\u5288 \u6765\uff01 \u6155\u5bb9\u7ea2\u3001\u5c55\u5a49\u513f\uff0c\u4ee5\u81f3\u521a\u521a\u81ea\u884c\u82cf\u9192\u7684\u91d1\u5f69\u98ce\uff0c\u4e0d\u7531\u540c\u65f6\u60ca\u547c\uff0c\u731b\u7136\u6251\u4e86\u8fc7\u67651\u4f46\u6162 \u4e86\uff01 \u5c55\u767d\u53cc\u638c\u5df2\u7ecf\u8fce\u4e86\u4e0a\u53bb\uff01 \u9707\u58f0\u4e2d\uff0c\u72c2\u98d9\u56db\u5377\uff0c\u6155\u5bb9\u7ea2\u3001\u5c55\u5a49\u513f\u3001\u91d1\u5f69\u51e4\u4e09\u6761\u5a07\u5c0f\u8eab\u5f71\uff0c\u53c8\u88ab\u65e0\u508d\u7684\u7f62\u98ce\u52b2\u6d41\u9707\u9000 \u4e86\u56de\u53bb\uff01 \u5c18\u7070\u56db\u843d\uff0c\u5c55\u767d\u201c\u54c7\u201d\u7684\u4e00\u58f0\uff0c\u5f20\u53e3\u53c8\u55b7\u51fa\u4e00\u53e3\u9c9c\u8840\uff0c\u4f46\u4eba\u4ecd\u672a\u5012\uff0c\u594b\u8d77\u53cc\u638c\uff0c\u53eb\u9053\uff1a \u201c\u8001\u4eba\u5bb6\uff01\u518d\u6765\uff01\u201d \u957f\u9aef\u8001\u4eba\u8eab\u5f62\u6643\u4e86\u4e24\u6643\uff0c\u5230\u5e95\u5fcd\u4e0d\u4f4f\uff0c\u4e5f\u5f20\u53e3\u55b7\u51fa\u4e00\u80a1\u8840\u7bad\uff01\u89c1\u5c55\u767d\u4e3e\u638c\u53c8\u8981\u6253\u6765\uff0c\u76ee \u5c04\u5947\u5149\uff0c\u4e3e\u638c\u6b32\u8fce\u2026\u2026 \u7a81\u7136\u4ed6\u9762\u8272\u7f13\u548c\u4e86\u4e0b\u6765\uff0c\u6ee1\u9762\u6012\u5bb9\u6539\u53d8\u6210\u4e00\u8138\u8d5e\u4f69\u4e4b\u8272\uff0c\u4e00\u7fd8\u5927\u62c7\u6307\u9053\uff1a\u201c\u5c0f\u54e5\u513f\uff01\u4f60 \u771f\u884c\uff01\u201d \u5c55\u767d\u5929\u751f\u670d\u8f6f\u4e0d\u670d\u786c\uff0c\u89c1\u957f\u9aef\u8001\u4eba\u7a81\u7136\u5938\u5956\u4ed6\uff0c\u60f3\u5230\u4e09\u715e\u5df2\u7ecf\u4f24\u5728\u81ea\u5df1\u624b\u4e2d\u4e8c\u715e\uff0c\u518d\u4e0e \u8001\u4eba\u62fc\u4e0b\u53bb\uff0c\u4e5f\u4e0d\u8fc7\u662f\u843d\u4e2a\u4e24\u8d25\u4ff1\u4f24\uff0c\u81ea\u5df1\u7236\u4ec7\u5df2\u62a5\uff0c\u8fd8\u6709\u4ec0\u4e48\u503c\u5f97\u4e89\u7684\uff1f\u4e8e\u662f\uff0c\u7a81\u7136\u6536 \u638c\uff0c\u53cc\u638c\u4e00\u62b1\uff0c\u9053\uff1a\u201c\u5728\u4e0b\u8ba4\u8f93\u4e86\uff0c\u8001\u524d\u8f88\uff0c\u518d\u89c1\uff01\u201d\u8bf4\u7f62\uff0c\u56de\u5934\u5c31\u8d70\uff01 \u8fd9\u53c8\u5927\u51fa\u957f\u9aef\u8001\u4eba\u610f\u5916\uff0c\u89c1\u5c55\u767d\u8bf4\u8d70\u5c31\u8d70\uff0c\u4e00\u65f6\u6014\u5728\u90a3\u513f\xb7\uff0e\uff0e\u3002\u2019\u5c55\u767d\u8d70\u4e86\u51e0\u6b65\uff0c\u7a33 \u4f4f\u8179\u5185\u7ffb\u6eda\u7684\u6c14\u8840\uff0c\u7adf\u7ffb\u8eab\u5411\u5899\u5916\u9a70\u53bb1\u4ed6\u542c\u5230\u8eab\u540e\u6155\u5bb9\u7ea2\u3001\u5c55\u5a49\u513f\u3001\u91d1\u5f69\u51e4\uff0c\u4ee5\u53ca\u96f7\u5927\u53d4 \u7b49\u4eba\u5728\u547c\u53eb\u4ed6\uff0c\u4f46\u4ed6\u5934\u4e5f\u4e0d\u56de\u5730\u5411\u5357\u4eac\u90ca\u5916\u9a70\u53bb1\u9a70\u51fa\u5357\u4eac\u57ce\uff0c\u987a\u7740\u6c5f\u8fb9\u671d\u524d\u8dd1\uff0c\u8d8a\u8fc7\u5ca9\u5c71 \u5341\u4e8c\u6d1e\uff0c\u767b\u5c71\u8d8a\u5cad\uff0c\u7a7f\u8fc7\u4e00\u5ea7\u5c71\u5e95\u6db5\u6d1e\uff0c\u5c55\u767d\u53c8\u8dd1\u81f3\u201c\u4ea1\u9b42\u8c37\u201d\uff0c\u8033\u5185\u542c\u5230\u6dd9\u6dd9\u6cc9\u9e23\u773c\u775b\u53c8 \u770b\u5230\u7fe0\u7fe0\u66fe\u4e3a\u4ed6\u201c\u7eaf\u9634\u7597\u9633\u201d\u7684\u90a3\u631f\u957f\u6ee1\u4e86\u8338\u8338\u7ec6\u8349\u7684\u5927\u767d\u77f3\uff0c\u4ed6\u518d\u4e5f\u652f\u6301\u4e0d\u4f4f\uff0c\u4e00\u5934\u6251\u5012 \u5728\u5927\u767d\u77f3\u4e0a\uff0c\u7acb\u523b\u660f\u4e86\u8fc7\u53bb\uff01 \u4e5f\u4e0d\u77e5\u660f\u6b7b\u8fc7\u4e86\u591a\u4e45\uff0c\u4ed6\u89c9\u5f97\u9f3b\u5b54\u91cc\u975e\u5e38\u6414\u75d2\uff0c\u8fde\u6253\u4e24\u4e2a\u55b7\u568f\uff0c\u4eba\u53c8\u6e05\u9192\u4e86\u8fc7\u6765\uff01 \u53ea\u89c1\u7ea2\u65e5\u722c\u8fc7\u4e86\u5c71\u5cad\uff0c\u767e\u9e1f\u58f0\u55a7\uff0c\u539f\u6765\u662f\u591c\u5df2\u7ecf\u8fc7\u53bb\uff0c\u9ece\u660e\u53c8\u5df2\u964d\u4e34\uff01 \u4ed6\u7741\u773c\u4e00\u770b\uff0c\u67f3\u7fe0\u7fe0\u5a07\u8eaf\u659c\u501a\u884c\u524d\uff0c\u7389\u624b\u592e\u5c16\u4e24\u6307\uff0c\u62c8\u4e86\u4e00\u6839\u4e0d\u77e5\u843d\u81ea\u4ec0\u4e48\u9e1f\u8eab\u4e0a\u7684\u4e94 \u5f69\u7fbd\u6bdb\uff0c\u5982\u82b1\u7684\u7c89\u9762\u4e0a\u542b\u7740\u6de1\u6de1\u51d1\u7b11\uff0c\u6b63\u5728\u4ee5\u7fbd\u6bdb\u9017\u5f04\u4ed6\u7684\u9f3b\u5b54\uff01 \u5c55\u767d\u731b\u5730\u722c\u8d77\u9053\uff1a\u201c\u4f60\u2026\u2026\u201d \u7fe0\u7fe0\u4e22\u6389\u624b\u4e2d\u7fbd\u6bdb\u9053\uff1a\u201c\u6211\u8ddf\u4f60\u5929\u751f\u4e00\u5bf9\u795e\u4ed9\u4f34\u4fa3\uff0c\u4e16\u4e8b\u65e0\u6001\uff0c\u8363\u534e\u5bcc\u8d35\uff0c\u4e0d\u8fc7\u662f\u8fc7\u773c \u4e91\u70df\uff0c\u6069\u6028\u60c5\u4ec7\uff0c\u4e5f\u4e0d\u8fc7\u662f\u5357\u67ef\u4e00\u68a6\u3002 \u6211\u4e0e\u4f60\u4e0d\u7ba1\u6c5f\u6e56\u4e0a\u662f\u662f\u975e\u975e\uff0c\u627e\u4e00\u6d1e\u5929\u798f\u5730\uff0c\u4e50\u4eab\u7ec8\u751f\uff01\u767d\u54e5\u54e5\uff01 \u6b64\u65f6\u4f60\u518d\u4e0d\u80fd\u8bf4\u4e0d\u4e86\uff01\u201d \u5c55\u767d\u5fc3\u52a8\uff0c\u4f46\u4ecd\u89c9\u6709\u4e8b\u672a\u4e86\uff0c\u9053\uff1a\u201c\u6211\u2026\u2026\u201d \u7fe0\u7fe0\u806a\u6167\u903e\u4eba\uff0c\u4e0d\u7b49\u5c55\u767d\u8bf4\u5b8c\uff0c\u9053\uff1a\u201c\u767d\u54e5\u7236\u4ec7\u5df2\u62a5\uff0c\u65e0\u7275\u65e0\u6302\uff0c\u6b63\u53ef\u4e0e\u59b9\u59b9\u9080\u5578\u4e16 \u5916\uff0c\u5bc4\u60c5\u5c71\u6c34\uff0c\u540c\u6548\u9e33\u9e2f\u53cc\u98de\u2026\u2026\u201d \u7fe0\u7fe0\u89c1\u5c55\u767d\u60c5\u601d\u5c24\u6709\u672a\u902e\uff0c\u660e\u7741\u4e00\u8f6c\uff0c\u72e1\u9ee0\u7b11\u9053\uff1a\u201c\u83ab\u975e\u767d\u54e5\u54e5\u8fd8\u820d\u4e0d\u4e0b\u6155\u5bb9\u59d0\u59b9\u4e0e \u2018\u6c5f\u5357\u7b2c\u4e00\u7f8e\u4eba\u2019\uff1f\u201d \u5c55\u767d\u89c1\u5fc3\u4e8b\u88ab\u7fe0\u7fe0\u8bf4\u7834\uff0c\u7389\u9762\u4e00\u7ea2\uff0c\u4ecd\u5450\u5450\u9053\uff1a\u201c\u6211\u4e0e\u6155\u5bb9\u5927\u59d0\u5df2\u6709\u5a5a\u7ea6\u2026\u2026\u201d \u7fe0\u7fe0\u7b11\u9053\uff1a\u201c\u5979\u59d0\u59b9\u6069\u6028\u682a\u8fde\uff0c\u4e00\u65f6\u8131\u8eab\u4e0d\u5f00\uff0c\u663e\u7136\u4e0d\u80fd\u4e0e\u767d\u54e5\u7f6e\u8eab\u4e16\u5916\u3002\u5047\u5982\uff0c\u5979\u4eec \u4e09\u4eba\u4e4b\u4e2d\uff0c\u5bf9\u767d\u54e5\u54e5\u60c5\u6709\u72ec\u949f\uff0c\u820d\u5f97\u79bb\u5f00\u5bb6\u5ead\u7236\u5144\uff0c\u4e0d\u8fc7\u95ee\u7236\u6b7b\u8840\u4ec7\uff0c\u5f53\u7136\u8fd8\u53ef\u4ee5\u6765\u8ffd\u5bfb\u767d \u54e5\u54e5\u8eab\u4fa7\uff0c\u5c0f\u59b9\u5df2\u7ecf\u60f3\u5f00\u4e86\uff0c\u4e00\u5b9a\u4e0e\u5979\u4eec\u548c\u5e73\u76f8\u5904\uff0c\u5171\u6548\u5a25\u7687\u5973\u82f1\uff0c\u767d\u54e5\u54e5\uff0c\u4f60\u4e0d\u8981\u5c0f\u77a7\u59b9 \u59b9\uff0c\u59b9\u59b9\u51b3\u4e0d\u662f\u2018\u6bcd\u591c\u53c9\u2019\uff0c\u66f4\u4e0d\u662f\u918b\u7f50\u5b50\u2026\u3002 \u7b2c\u4e94\u5341\u4e00\u7ae0\u3000\u638c\u6bd9\u201c\u4e09\u5bf8\u4e01\u201d>> \u53e4\u9f99\u300a\u5251\u5ba2\u884c\u300b \u7b2c\u4e94\u5341\u4e00\u7ae0\u3000\u638c\u6bd9\u201c\u4e09\u5bf8\u4e01\u201d \u5c55\u767d\u957f\u53f9\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u96be\u5f97\u59b9\u59b9\u66ff\u6211\u8bbe\u60f3\u5f97\u5468\u5230\uff0c\u53ef\u662f\uff0c\u6211n\u2026\u201d\u8c01\u77e5\u67f3\u7fe0\u7fe0\u773c\u6b8a\u6eda \u52a8\uff0c\u5de6\u53f3\u4e00\u770b\uff0c\u626c\u58f0\u53eb\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\uff1f\u80c6\u6562\u5077\u542c\u59d1\u5a18\u8c08\u8bdd\uff01\u8fd8\u4e0d\u51fa\u6765\u53d7\u6b7b\uff01\u201d \u5c55\u767d\u4e00\u6115\uff0c\u771f\u60f3\u4e0d\u5230\u7fe0\u7fe0\u8033\u76ee\u5982\u6b64\u806a\u654f\uff0c\u81ea\u5df1\u4e00\u70b9\u672a\u53d1\u89c9\uff0c\u5979\u5df2\u89c9\u5bdf\u9644\u8fd1\u6709\u4eba\uff01 \u7fe0\u7fe0\u8bdd\u58f0\u624d\u843d\uff0c\u5c31\u5728\u4ed6\u4fe9\u505c\u8eab\u4e0d\u8fdc\u7684\u4e00\u68f5\u5927\u6811\u540e\u8fb9\uff0c\u9b3c\u9b45\u4f3c\u5730\u95ea\u51fa\u4e00\u4eba\uff01 \u6b64\u4eba\u5112\u5dfe\u98d8\u626c\uff0c\u6b65\u5c65\u6f47\u6d12\uff0c\u867d\u662f\u9ed1\u591c\u4e4b\u95f4\uff0c\u65e0\u6cd5\u770b\u6e05\u4ed6\u7684\u9762\u8c8c\uff0c\u4f46\u5fc5\u662f\u4e00\u4e2a\u98ce\u6d41\u4eba\u7269\uff0c \u53ea\u89c1\u4ed6\u54c8\u54c8\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u672c\u4eba\u5728\u6b64\u5b89\u7720\uff0c\u4f60\u4e8c\u4eba\u54ac\u5431\u55b3\u55b3\uff0c\u6270\u4eba\u6e05\u68a6\uff0c\u672c\u4eba\u8fd8\u6ca1\u8bf4\u8bdd\uff0c\u4f60\u4eec \u53cd\u800c\u602a\u7f6a\u6211\u6765\u4e86\uff01\u771f\u662f\u5c82\u6709\u6b64\u7406\uff01\u201d \u8bf4\u7f62\u62c2\u8896\u4fbf\u8d70\uff01 \u7fe0\u7fe0\u51b7\u54fc\u4e00\u58f0\uff0c\u672b\u89c1\u5979\u600e\u6837\u4f5c\u52bf\uff0c\u4eba\u5982\u98d8\u98ce\u95ea\u7535\uff0c\u53ea\u9999\u80a9\u5fae\u5fae\u4e00\u6643\uff0c\u5df2\u7136\u7ad9\u5728\u90a3\u4eba\u9762 \u524d\uff01 \u90a3\u4eba\u5012\u62bd\u4e00\u53e3\u51b7\u6c14\uff0c\u4f30\u4e0d\u5230\u7fe0\u7fe0\u7684\u8eab\u6cd5\u8d85\u4e4e\u60f3\u50cf\uff0c\u53c8\u4e0d\u77e5\u7fe0\u7fe0\u8fd1\u6765\u662f\u4f55\u7528\u610f\u3002\u7acb\u523b\u8fd0\u529f \u6212\u5907\uff0c\u53cc\u638c\u5fae\u63d0\uff0c\u62a4\u4f4f\u80f8\u819b\u8981\u5bb3\uff0c\u51c6\u5907\u968f\u65f6\u51fa\u624b\u5e94\u654c\uff01 \u5c55\u767d\u6b64\u65f6\u4e5f\u8d76\u4e86\u8fc7\u6765\uff0c\u8c01\u77e5\u501f\u7740\u661f\u6708\u5fae\u5149\uff0c\u770b\u6e05\u6765\u4eba\u9762\u8c8c\u4e4b\u540e\uff0c\u4e0d\u7531\u8131\u53e3\u60ca\u547c\u9053\uff1a\u201c\u539f \u6765\u662f\u4f60\uff01\u201d \u6765\u4eba\u4ef0\u5929\u6253\u4e86\u4e2a\u54c8\u54c8\uff0c\u7b11\u9053\uff1a\u201c\u600e\u4e48\uff0c\u4f60\u60f3\u4e0d\u5230\u662f\u6211\u5427\uff1f\u53ef\u662f\u672c\u4eba\u65e9\u5c31\u77e5\u9053\u662f\u4f60\u4e86\uff0c\u5802 \u5802\u7684\u2018\u65e0\u60c5\u5251\u5ba2\u2019\uff0c\u8273\u798f\u5012\u4e0d\u6d45\uff0c\u6709\u4e86\u8fd9\u4e48\u4e00\u4f4d\u5bbd\u5b8f\u5927\u91cf\u7684\u592b\u4eba\uff0c\u770b\u6837\u5b50\u4e09\u59bb\u56db\u59be\u662f\u5c11\u4e0d\u4e86 \u55bd\uff01\u201d \u5c55\u767d\u542c\u51fa\u4ed6\u8bed\u542b\u8ba5\u8bbd\uff0c\u4fca\u8138\u4e00\u7ea2\uff0c\u5450\u5450\u5730\u9053\uff1a\u201c\u6a0a\u7d20\u2026\u2026\u5144\uff0e\u2026\uff0e\xb7\u4e45\u672b\u76f8\u89c1\uff0c\u60f3\u4e0d\u5230 \u4f60\u7adf\u5b66\u4f1a\u4e86\u8bf4\u7b11\u8bdd\u4e86\uff01\u2026\u2026\u201d \u539f\u6765\u6b64\u4eba\u6b63\u662f\u9547\u6c5f\u201c\u9e92\u9e9f\u5e84\u201d\u7684\u6a0a\u7d20\u9e3e\uff0c\u4e0d\u8fc7\u5979\u6b64\u65f6\u4ecd\u662f\u7537\u88c5\uff0c\u5f53\u7740\u7fe0\u7fe0\uff0c\u5c55\u767d\u4e0d\u613f\u63ed \u9732\u5979\u7684\u8eab\u4efd\uff0c\u6545\u4ecd\u4ee5\u6a0a\u5144\u547c\u4e4b\u3002 \u8c01\u77e5\u7fe0\u7fe0\u5728\u4e00\u8fb9\u51b7\u7b11\u4e00\u58f0\uff0c\u7ea4\u6307\u6307\u7740\u6a0a\u7d20\u9e3e\u9053\uff1a\u201c\u4f60\u4e0d\u7528\u53cd\u7a7f\u76ae\u8884\uff0c\u5728\u6211\u9762\u524d\u88c5\u4f6f\uff01\u4f60 \u4ee5\u4e3a\uff0c\u6211\u770b\u4e0d\u51fa\u6765\u5417\uff1f\u54fc\uff01\u6211\u65e9\u5c31\u770b\u51fa\u4f60\u4e5f\u662f\u4e2a\u6bcd\u5b50\u8d27\uff01\u201d \u8fd9\u56de\uff0c\u8be5\u6a0a\u7d20\u9e3e\u8138\u7ea2\u4e86\uff0c\u5979\u4e07\u4e5f\u60f3\u4e0d\u5230\u773c\u524d\u8fd9\u7f8e\u903e\u5929\u4eba\u7684\u7edd\u8272\u5c11\u5973\uff0c\u773c\u5149\u7adf\u662f\u5982\u6b64\u5389 \u5bb3\uff1f\u4e00\u773c\u4fbf\u770b\u7834\u4e86\u81ea\u5df1\u7684\u8eab\u4efd\uff0c\u5979\u60f3\u53d6\u7b11\u522b\u4eba\u7684\uff0c\u53cd\u53eb\u522b\u4eba\u53d6\u7b11\u4e86\uff0c\u7acb\u523b\u95f9\u4e86\u4e2a\u819b\u76ee\u7ed3\u820c\uff0c \u65e0\u8a00\u4ee5\u5bf9\uff0e\u6709\u4e86\u5bf9\u5a49\u513f\u7684\u7ecf\u9a8c\uff0c\u5c55\u767d\u5370\u8c61\u4e2d\u8ba4\u5b9a\u7fe0\u7fe0\u5b9a\u4f1a\u918b\u6d77\u5174\u6ce2\uff0c\u6015\u5979\u518d\u8ddf\u6a0a\u7d20\u5f2f\u6253\u8d77 \u6765\uff0c\u5fd9\u5728\u4e00\u8fb9\uff0c\u9053\uff1a\u201c\u7fe0\u59b9\uff0c\u4f60\u4e0d\u5fc5\u591a\u5fc3\uff0c\u5979\u5411\u6765\u662f\u7a7f\u7537\u88c5\u7684\uff01\u2026\u2026\u201d \u7fe0\u7fe0\u7d27\u7ef7\u7740\u7684\u5c0f\u8138\uff0c\u201c\u5657\u201d\u7684\u4e00\u7b11\uff0c\u4eff\u4f5b\u7531\u8083\u6740\u7684\u4e25\u51ac\uff0c\u4e00\u4e0b\u5b50\u56de\u5230\u767e\u82b1\u76db\u5f00\u7684\u6625\u5929\u3002 \u5979\u54af\u7565\u7b11\u4e86\u534a\u5929\uff0c\u624d\u6536\u4f4f\u7b11\u58f0\u9053\uff1a\u6211\u7684\u767d\u54e5\u54e5\uff01\u4f60\u653e\u5fc3\u597d\u4e86\uff0c\u59b9\u59b9\u8bf4\u8fc7\u4e0d\u505a\u918b\u5a18\u5b50\uff0c\u4f60\u518d\u4e0d \u7528\u803d\u5fc3\u59b9\u59b9\u4f1a\u5403\u918b\uff0c\u5a49\u513f\u3001\u6155\u5bb9\u7ea2\uff0c\u91d1\u5f69\u51e4\uff0c\u4e09\u4e2a\u90fd\u4e0d\u5acc\u591a\uff0c\u4e0d\u5728\u4e4e\u518d\u591a\u5979\u4e00\u4e2a\uff01\u2026\u2026\u201d \u6a0a\u7d20\u9e3e\u7a81\u7136\u607c\u7f9e\u6210\u6012\uff0c\u9762\u5b54\u4e00\u6c89\uff0c\u53f1\u9053\uff1a\u201c\u4e0d\u8981\u8138\uff01\u201d \u7fe0\u7fe0\u500f\u7136\u6536\u4f4f\u7b11\u58f0\uff0c\u53cd\u624b\u2014\u638c\uff0c\u201c\u556a\u201d\u7684\u4e00\u58f0\u8106\u54cd\uff0c\u6a0a\u7d20\u9e3e\u7c89\u8138\u4e0a\u7acb\u523b\u80bf\u8d77\u4e86\u4e94\u4e2a\u7ea2\u6307 \u5370\uff01 \u7fe0\u7fe0\u7a81\u7136\u6253\u4e86\u6a0a\u7d20\u9e3e\u4e00\u4e2a\u8033\u5149\uff0c\u51fa\u624b\u5947\u5feb\uff0c\u4e0d\u4f46\u6a0a\u7d20\u9e3e\u672a\u80fd\u8eb2\u5f00\uff0c\u5c31\u8fde\u5c55\u767d\u60f3\u51fa\u624b\u963b\u62e6 \u90fd\u672a\u6765\u5f97\u53ca\uff01 \u8fd9\u4e00\u62db\u5927\u51fa\u4e8c\u4eba\u610f\u5916\uff01 \u4e00\u65f6\u628a\u6a0a\u7d20\u9e3e\u6253\u695e\u4e86\uff01 \u6027\u70c8\u7684\u6a0a\u7d20\u9e3e\uff0c\u5bb6\u5ead\u60e8\u53d8\uff0c\u6ee1\u8179\u60b2\u6124\uff0c\u54ea\u91cc\u53d7\u5f97\u4e86\u7fe0\u7fe0\u5982\u6b64\u8c03\u4f83\uff1f\u53c8\u611f\u6280\u4e0d\u5982\u4eba\uff0c\u8fde\u6025 \u5e26\u6c14\uff0c\u7389\u5bb9\u60e8\u53d8\uff0c\u201c\u5657\u565c\u201d\u4e00\u58f0\uff0c\u4ece\u8170\u4e2d\u6296\u51fa\u4e00\u6761\u5341\u516b\u8282\u4eae\u94f6\u97ad\u6765\uff01 \u8fd9\u662f\u5979\u7236\u4eb2\u201c\u9738\u738b\u97ad\u201d\u6a0a\u975e\u7684\u6210\u540d\u6b66\u5668\uff0c\u201c\u9738\u738b\u97ad\u201d\u6a0a\u975e\u4e09\u4e2a\u513f\u5b50\u6ca1\u6709\u4f20\uff0c\u5355\u5355\u4f20\u7ed9\u4e86 \u5973\u513f\u3002 \u5c55\u767d\u4ee5\u4e3a\u6a0a\u7d20\u9e3e\u53d6\u51fa\u5175\u5668\uff0c\u662f\u8981\u60c5\u6025\u62fc\u547d\uff0c\u77e5\u9053\u5979\u7edd\u4e0d\u662f\u7fe0\u7fe0\u7684\u5bf9\u624b\uff0c\u53c8\u6015\u7fe0\u7fe0\u5fc3\u72e0\u624b \u8fa3\uff0c\u52a8\u4e0a\u624b\u4f7f\u6a0a\u7d20\u9e3e\u5403\u4e86\u5927\u4e8f\uff0c\u5fd9\u4e0a\u524d\u4e24\u6b65\uff0c\u6025\u9053\uff1a\u201c\u6a0a\u7d20\u2026\u2026\u201d\u4ed6\u5df2\u4e0d\u77e5\u662f\u79f0\u547c\u5144\u597d\uff0c\u8fd8 \u662f\u79f0\u547c\u59d1\u5a18\u597d\uff0c\uff1a\u201c\u5343\u4e07\u4e0d\u8981\u8bef\u4f1a\uff0c\u7fe0\u7fe0\uff0c\u7fe0\u7fe0\u2026\u2026\u201d \u7fe0\u7fe0\u600e\u6837\u5462\uff1f\u4ed6\u672c\u5c31\u8bb7\u4e8e\u8a00\u8bcd\uff0c\u4e00\u7740\u6025\u66f4\u4e0d\u77e5\u5982\u4f55\u63aa\u8bcd\u624d\u597d\u3002 \u8c01\u77e5\u6a0a\u7d20\u9e3e\u94f6\u7259\u6697\u54ac\uff0c\u63e1\u4f4f\u4eae\u94f6\u97ad\u7684\u97ad\u5934\uff0c\u7adf\u53cd\u624b\u5411\u81ea\u5df1\u7075\u76d6\u4e0a\u6572\u53bb\uff01 \u539f\u6765\u8fd9\u70c8\u6027\u7684\u59d1\u5a18\u7ade\u60f3\u81ea\u6740\u4e86\uff01 \u5c55\u767d\u5927\u5403\u4e00\u60ca\uff0c\u987a\u624b\u4e00\u593a\uff0c\u7adf\u662f\u5927\u64d2\u62ff\u624b\u4e2d\u7684\u4e00\u5927\u7edd\u62db\u201c\u706b\u4e2d\u53d6\u6817\u201d\uff0c\u5371\u673a\u4e00\u53d1\u4e4b\u95f4\uff0c \u628a\u4eae\u94f6\u97ad\u4ece\u6a0a\u7d20\u9e3e\u624b\u4e2d\u593a\u4e86\u8fc7\u6765\uff01 \u201c\u6a0a\u59d1\u5a18\uff01\u4f60\u8fd9\u662f\u4f55\u82e6\uff1f\u2026\u2026\u201d \u8c01\u77e5\u5c55\u767d\u8bdd\u8fd8\u6ca1\u8bf4\u5b8c\uff0c\u6a0a\u7d20\u9e3e\u201c\u54e9\u201d\u7136\u4e00\u58f0\u60b2\u557c\uff0c\u6389\u5934\u5411\u6811\u4e1b\u4e2d\u8e7f\u53bb\uff01 \u201c\u6a0a\u59d1\u5a18\uff01\u6a0a\u59d1\u5a18\uff01\u2026\uff0e\uff0e\uff0e\u201d\u5c55\u767d\u4e00\u8fb9\u53e3\u4e2d\u6025\u53eb\uff0c\u4e00\u8fb9\u817e\u8eab\u53bb\u8ffd\u3002\u4e00\u65b9\u9762\u4ed6\u662f\u60f3\u628a\u8bef \u4f1a\u89e3\u91ca\u5f00\uff0c\u53e6\u4e00\u65b9\u9762\u4ed6\u662f\u6015\u59d1\u5a18\u4ecd\u7136\u60f3\u4e0d\u5f00\uff0c\u524d\u53bb\u5bfb\u6b7b\uff01 \u5929\u771f\u5a07\u61a8\u7684\u7fe0\u7fe0\u5374\u591c\u4e00\u8fb9\u695e\u4e86\uff01 \u5979\u60f3\u4e0d\u5230\u4e00\u53e5\u8bdd\u4f1a\u4f7f\u5bf9\u65b9\u75db\u4e0d\u6b32\u751f\uff01 \u5c55\u767d\u8ffd\u8d76\u6a0a\u7d20\u9e3e\uff0c\u4ee5\u76ee\u524d\u5c55\u767d\u7684\u8f7b\u8eab\u63d0\u7eb5\u672f\u6765\u8bf4\uff0c\u201c\u65e0\u8272\u65e0\u76f8\u8eab\u201d\u3001\u201c\u5343\u5e7b\u98d8\u9999\u6b65\u201d\uff0c \u53ef\u4ee5\u8bf4\u8ffd\u8d76\u6a0a\u7d20\u9e3e\uff0c\u771f\u8ba9\u5979\u8dd1\u4e0d\u51fa\u5341\u516b\u6b65\u53bb\uff0c\u4f46\u5f53\u4ed6\u773c\u770b\u8feb\u8fd1\u6a0a\u7d20\u5f2f\u65f6\uff0c\u7a81\u7136\u5728\u4e00\u68f5\u5927\u6811\u4e0a \u6eda\u2019\u4e0b\u4e00\u56e2\u9ed1\u7cca\u7cca\u7684\u9ed1\u5f71\uff0c\u76f4\u5411\u5c55\u767d\u649e\u6765\uff01 \u770b\u90a3\u9ed1\u5f71\uff0c\u5947\u5feb\u5982\u7535\uff0c\u65b9\u5706\u4e0d\u5230\u4e09\u601d\uff0c\u9ed1\u7cca\u7cca\u72b9\u5982\u4e00\u4e2a\u84b2\u56e2\u76f8\u4eff\uff0c\u4e0d\u77e5\u662f\u4f55\u4e8b\u7269\uff1f \u5c55\u767d\u5927\u5403\u4e00\u60ca\uff0c\u6025\u5239\u4f4f\u524d\u6251\u7684\u8eab\u5f62\uff0c\u500f\u7136\u659c\u98d8\u652f\u5916\uff0c\u843d\u4e0b\u5730\u6765\uff0c\u6389\u5934\u4e00\u770b\uff0c\u90a3\u56e2\u9ed1\u5f71\uff0c \u4e00\u4e2a\u201c\u9ca4\u9c7c\u6253\u633a\u201d\uff0c\u7adf\u7136\u7ad9\u5728\u5c55\u767d\u9762\u524d\uff01 \u5f71\u5b9a\u8eab\u663e\uff0c\u539f\u6765\u662f\u4e00\u4e2a\u5934\u5927\u817f\u77ed\u3001\u8eab\u9ad8\u4e0d\u53ca\u4e09\u5c3a\u7684\u682a\u5112\uff0c\u5634\u5507\u4e0a\u63a5\u7740\u4e24\u6761\u9752\u9f3b\u6d95\uff0c\u51b2\u7740 \u5c55\u767d\u6bd7\u7259\u76f4\u4e50\uff01 \u5c55\u767d\u5b9a\u775b\u4e00\u770b\uff0c\u9762\u524d\u7684\u4f8f\u5112\u6b63\u662f\u201c\u4e09\u5bf8\u4e01\u201d\u3002 \u5c55\u767d\u51b7\u54fc\u4e00\u58f0\uff0c\u9887\u4e3a\u4e0d\u5c51\u5730\u9053\uff1a\u201c\u4f60\u7684\u4e24\u4e2a\u5e08\u7236\u5462\uff1f\u201d \u5c55\u767d\u867d\u7136\u5fe0\u539a\u6728\u8bb7\uff0c\u4f46\u5374\u5ac9\u6076\u5982\u4ec7\uff0c\u81ea\u4ece\u5728\u201c\u795e\u7334\u201d\u94c1\u51cc\u53e3\u4e2d\uff0c\u77e5\u9053\u201c\u6c5f\u5357\u4e8c\u5947\u201d\u5fd8\u4e49 \u5f11\u5e08\uff0c\u4fbf\u5341\u5206\u4e0d\u9f7f\u5176\u4e3a\u4eba\uff0c\u6545\u6b64\u5bf9\u201c\u4e09\u5bf8\u4e01\u201d\u7684\u6311\u6218\u4e0d\u7406\u4e0d\u776c\uff0c\u53cd\u800c\u5411\u5176\u8be2\u95ee\u201c\u6c5f\u5357\u4e8c\u5947\u201d \u7684\u4e0b\u843d\uff01 \u201c\u4e09\u5bf8\u4e01\u201d\u4e00\u6487\u5634\uff0c\u6447\u5934\u6643\u8111\u5730\u9053\uff1a\u201c\u5c0f\u592a\u7237\u4fbf\u53ef\u8981\u4f60\u7684\u72d7\u547d\uff0c\u8fd8\u95ee\u4e24\u4f4d\u8001\u592a\u7237\u5e72\u4ec0 \u4e48\uff1f\u201d \u8bf4\u7f62\uff0c\u4e00\u62db\u201c\u5c0f\u9b3c\u63a8\u78e8\u201d\uff0c\u8eab\u5f62\u6ef4\u6e9c\u6e9c\u4e00\u8f6c\uff0c\u638c\u5b9a\u504f\u950b\uff0c\u95ea\u7535\u4f3c\u5730\u5411\u5c55\u81ea\u5de6\u81c2\u6263\u6765\uff01 \u5c55\u767d\u89c1\u4ed6\u8bf4\u6253\u5c31\u6253\uff0c\u6012\u6c14\u9661\u751f\uff0c\u7529\u5de6\u80a9\uff0c\u584c\u53f3\u6b65\uff0c\u53cd\u81c2\u4e00\u638c\uff0c\u5411\u201c\u4e09\u5bf8\u4e01\u201d\u540e\u80cc\u62cd\u53bb\uff01 \u8c01\u77e5\u201c\u4e09\u5bf8\u4e01\u201d\u8eab\u5f62\u6ed1\u6e9c\u5f97\u5f88\uff0c\u4e24\u6761\u77ed\u817f\u4e00\u8e6c\uff0c\u659c\u8e7f\u4e09\u5c3a\uff0c\u539f\u5f0f\u4e0d\u53d8\uff0c\u4ecd\u662f\u4e00\u62db\u201c\u5c0f\u9b3c \u63a8\u78e8\u201d\uff0c\u53cc\u624b\u4e4d\u5f00\uff0c\u7167\u65e7\u5411\u5c55\u81ea\u5de6\u81c2\u9501\u6765\uff01 \u5c55\u767d\u4f30\u4e0d\u5230\u201c\u4e09\u5bf8\u4e01\u201d\u51fa\u624b\u62db\u5f0f\uff0c\u6bd4\u5728\u6570\u6708\u4e4b\u524d\u71d5\u5b50\u77fe\u6c5f\u8fb9\u52a8\u624b\u65f6\u9ad8\u51fa\u751a\u591a\uff0c\u4e0d\u4f46\u51fa\u624b \u5feb\uff0c\u62db\u5f0f\u5947\uff0c\u800c\u4e14\u5341\u6307\u52b2\u98ce\u7adf\u80fd\u88ad\u4f53\u751f\u5bd2\uff0c\u5fc3\u4e2d\u5fae\u5fae\u4e00\u61d4\uff0c\u624b\u4e0b\u4e0d\u6562\u6020\u6162\uff0c\u6643\u8eab\u907f\u62db\uff0c\u8fc5\u7136 \u7528\u51fa\u4e00\u62db\u201c\u98ce\u9707\u96f7\u9e23\u201d\uff0c\u638c\u5203\u5982\u5200\u5411\u201c\u4e09\u5bf8\u4e01\u201d\u77ed\u7956\u7684\u5934\u4e0a\u780d\u53bb\uff01 \u201c\u4e09\u5bf8\u4e01\u201d\u5c16\u5578\u4e00\u58f0\uff0c\u5927\u8111\u888b\u4e00\u6643\uff0c\u201c\u6ecb\u6e9c\u201d\u4e00\u58f0\uff0c\u6389\u5934\u53c8\u5411\u5c55\u767d\u53f3\u81c2\u6293\u6765\uff0c\u4ecd\u7136\u662f\u4e00 \u62db\u201c\u5c0f\u9b3c\u63a8\u78e8\u201d\uff01 \u4e8c\u4eba\u6643\u773c\u6253\u4e86\u4e09\u4e94\u4e2a\u7167\u9762\uff0c\u201c\u4e09\u5bf8\u4e01\u201d\u500f\u5de6\u500f\u53f3\uff0c\u4f46\u4e0d\u8bba\u5411\u5de6\u5411\u53f3\uff0c\u90fd\u7528\u7684\u662f\u76f8\u540c\u4e00\u62db \u201c\u5c0f\u9b3c\u63a8\u78e8\u201d\uff01 \u5c55\u767d\u5fc3\u4e2d\u6697\u6697\u5403\u60ca\uff0c\u5176\u5b9e\uff0c\u201c\u4e09\u5bf8\u4e01\u201d\u5fc3\u4e2d\u66f4\u52a0\u5403\u60ca\uff01 \u56e0\u4e3a\u201c\u4e09\u5bf8\u4e01\u201d\u5728\u71d5\u5b50\u77fe\u6c5f\u8fb9\uff0c\u88ab\u5c55\u767d\u6253\u4e86\u4e00\u638c\uff0c\u5e08\u5f92\u4e09\u4eba\u56de\u5230\u96c1\u8361\u9b3c\u8c37\uff0c\u6f5c\u5fc3\u82e6\u7ec3\uff0c \u4f46\u7b49\u4ed6\u4eec\u5e08\u5f92\u4e09\u4eba\uff0c\u81ea\u8ba4\u4e3a\u7edd\u827a\u7ec3\u6210\uff0c\u8db3\u53ef\u4ee5\u548c\u201c\u795e\u7334\u201d\u5bf9\u6297\u65f6\uff0c\u6c5f\u6e56\u4f20\u51fa\u201c\u5357\u7334\u5317\u9a74\u201d\u706b \u62fc\uff0c\u4e00\u540c\u4e27\u547d\u5728\u201c\u4ea1\u9b42\u8c37\u201d\u7684\u6d88\u606f\uff01 \u5e08\u5f92\u4e09\u4eba\u4e3a\u4e86\u8bc1\u5b9e\u8fd9\u4e00\u6d88\u606f\uff0c\u53c8\u8dd1\u6765\u5357\u4eac\u8fd1\u90ca\u5ca9\u5c71\u5341\u4e8c\u6d1e\u4e00\u5e26\u6253\u63a2\uff0c\u679c\u7136\u53d1\u73b0\u201c\u795e\u7334\u201d \u94c1\u51cc\u6b7b\u5728\u4e00\u5ea7\u8352\u6d1e\u4e2d\uff01 \u201c\u6c5f\u5357\u4e8c\u5947\u201d\u5f53\u7136\u662f\u5982\u91ca\u91cd\u8d1f\uff0c\u201c\u4e09\u5bf8\u4e01\u201d\u5374\u5435\u7740\u8981\u627e\u5c55\u767d\u62a5\u4e00\u638c\u4e4b\u4ec7\uff01 \u201c\u90a3\u8fd8\u4e0d\u5bb9\u6613\uff01\u201d\u5927\u5947\u201c\u8d64\u53d1\u7075\u7334\u201d\u5e38\u53bb\u6076\u9053\uff1a\u201c\u5e08\u7236\u4f20\u4f60\u7684\u2018\u9b3c\u9b45\u638c\u2019\uff0c\u522b\u770b\u53ea\u6709\u4e09 \u62db\uff0c\u4f46\u8fd9\u4e09\u62db\u5a01\u529b\u5927\u5f97\u51fa\u5947\uff0c\u5176\u5b9e\u4e5f\u7528\u4e0d\u4e86\u4e09\u62db\uff0c\u5f92\u513f\uff0c\u53ea\u8981\u7528\u7b2c\u4e00\u62db\u201c\u5c0f\u9b3c\u63a8\u78e8\u201d\uff0c\u4fbf\u53ef \u7f6e\u5c11\u5e74\u5c55\u767d\u4e8e\u6b7b\u5730\uff01\u201d \u8fd9\u4e00\u8bf4\u201c\u4e09\u5bf8\u4e01\u201d\u66f4\u662f\u5fc3\u75d2\u96be\u71ac\uff0c\u6068\u4e0d\u5f97\u7acb\u65f6\u627e\u7740\u5c55\u767d\uff0c\u628a\u5c55\u767d\u5288\u6b7b\u638c\u4e0b\uff0c\u4ee5\u51fa\u80f8\u4e2d\u4e00 \u53e3\u6076\u6c14\uff01 \u8c01\u77e5\uff0c\u4eca\u5929\u4ed6\u9047\u5230\u5c55\u767d\uff0c\u628a\u4e00\u62db\u201c\u5c0f\u9b3c\u63a8\u78e8\u201d\u53cd\u590d\u7528\u4e86\u4e94\u4e03\u6b21\uff0c\u4ecd\u7136\u8fde\u5c55\u767d\u7684\u8863\u8fb9\u90fd\u6ca1 \u6478\u5230\uff01 \u800c\u4e14\uff0c\u770b\u5c55\u767d\u89c1\u62db\u62c6\u62db\uff0c\u89c1\u5f0f\u6253\u5f0f\uff0c\u6001\u5ea6\u4ece\u5bb9\uff0c\u597d\u50cf\u5c1a\u672a\u65bd\u51fa\u5168\u529b\uff0c\u201c\u4e09\u5bf8\u4e01\u201d\u4e0d\u7531\u53c8 \u60ca\u53c8\u6012\uff0c\u731b\u5428\u4e00\u58f0\uff0c\u624b\u6cd5\u7acb\u53d8\uff0c\u53cc\u638c\u5de6\u53f3\u4e00\u5206\uff0c\u6307\u5c16\u5411\u524d\uff0c\u4ee5\u201c\u71d5\u5b50\u7a7f\u4e91\u201d\u7684\u59ff\u5f0f\uff0c\u57cb\u5934\u5411 \u5c55\u767d\u80f8\u524d\u649e\u6765\uff01 \u5c55\u767d\u4e00\u695e\uff0c\u5fc3\u8bf4\uff1a\u8fd9\u662f\u4ec0\u4e48\u62db\u5f0f\uff1f\u2026\u2026\u201d \u672c\u6765\u201c\u4e09\u5bf8\u4e01\u201d\u8fd9\u4e00\u62db\uff0c\u4ece\u5916\u8868\u770b\u7ade\u4f3c\u60c5\u6025\u62fc\u547d\uff0c\u53c8\u50cf\u6768\u4ee4\u516c\u649e\u7891\u81ea\u6740\uff0c\u5929\u4e0b\u6b66\u6797\uff0c\u4efb \u4f55\u95e8\u6d3e\u4e4b\u4e2d\uff0c\u5747\u7f55\u89c1\u8fd9\u7b49\u62db\u5f0f\uff01 \u5c82\u4e0d\u77e5\u8fd9\u662f\u201c\u9b3c\u9b45\u638c\u201d\u7684\u7b2c\u4e8c\u6740\u62db\uff0c\u540d\u53eb\u201c\u9b3c\u738b\u649e\u949f\u201d\uff0c\u522b\u770b\u90a3\u7b80\u7b80\u5355\u5355\u7684\u57cb\u5934\u4e00\u649e\uff0c \u5185\u542b\u4e94\u4e2a\u53d8\u5316\uff0c\u5934\u3001\u53cc\u624b\u3001\u53cc\u811a\u5e76\u7528\uff0c\u65e0\u5f62\u4e2d\u6bd4\u4ee5\u53cc\u638c\u5e94\u654c\u7684\u4eba\uff0c\u591a\u51fa\u4e09\u6837\u4e1c\u897f\u6765\uff0c\u53ef\u4ee5\u8bf4 \u5389\u5bb3\u65e0\u6bd4\uff01 \u5c24\u5176\u201c\u4e09\u5bf8\u4e01\u201d\u6709\u540d\u7684\u94c1\u5934\uff0c\u81ea\u5e7c\u7ec3\u4e60\u201c\u6cb9\u9524\u8d2f\u9876\u201d\u786c\u529f\u592b\uff0c\u6240\u4ee5\uff0c\u4ed6\u90a3\u5934\u53f7\u5927\u8111\u888b\u4e00 \u649e\u4e4b\u529b\uff0c\u4f55\u6b62\u5343\u65a4\uff0c\u8db3\u53ef\u5012\u6811\u65ad\u7891\uff01 \u800c\u4ed6\u7684\u53cc\u811a\u7a33\u4e8e\u8eab\u540e\uff0c\u978b\u5c16\u4e24\u67c4\u77ed\u5251\uff0c\u5728\u53cc\u811a\u524d\u8e22\u65f6\uff0c\u53cc\u5251\u9f50\u51fa\uff0c\u4e13\u7834\u94c1\u5e03\u886b\u91d1\u949f\u7f69\u7b49 \u5916\u95e8\u786c\u529f\u6a2a\u7ec3\uff01 \u5c55\u767d\u4e0d\u77e5\u4ed6\u8fd9\u662f\u4ec0\u4e48\u62db\u5f0f\uff0c\u5355\u638c\u4e00\u6325\u201c\u8fc5\u98ce\u75be\u96f7\u201d\uff0c\u731b\u5411\u201c\u4e09\u5bf8\u4e01\u201d\u57cb\u5934\u524d\u51b2\u7684\u9876\u95e8\u6253 \u53bb\uff01 \u8c01\u77e5\u201c\u4e09\u5bf8\u4e01\u201d\u62ac\u8138\u4e0a\u4ef0\uff0c\u6025\u5c04\u5411\u524d\u80c1\u8eab\u5f62r\u78c1\u7136\u5347\u9ad8\u4e8c\u5c3a\uff0c\u582a\u582a\u907f\u8fc7\u5c55\u767d\u7684\u4e00\u638c\uff0c\u540c \u65f6\uff0c\u4ed6\u5206\u7f6e\u5de6\u53f3\u7684\u53cc\u638c\uff0c\u5411\u4e2d\u4e00\u5408\uff0c\u7ad6\u7acb\u7684\u6307\u5c16\uff0c\u731b\u53e5\u5c55\u767d\u5de6\u53f3\u201c\u592a\u9633\u7a74\u201d\u63d2\u4e0b\uff01 \u8fd9\u4e00\u53d8\u5316\uff0c\u5b9e\u5728\u5927\u51fa\u610f\u5916\uff0c\u5c55\u767d\u5927\u5403\u4e00\u60ca\uff0c\u8d76\u7d27\u7f29\u9888\u85cf\u5934\uff0c\u53cc\u638c\u64e6\u9876\u800c\u8fc7\uff01 \u5c55\u767d\u6697\u9053\u4e00\u58f0\uff1a\u201c\u597d\u9669\uff01\u2026\u2026\u201d \u90a3\u201c\u4e09\u5bf8\u4e01\u201d\u5374\u4e0d\uff0c\u51cc\u7a7a\u7684\u8eab\u5f62\u5411\u4e0a\u4e00\u633a\uff0c\u9690\u4e8e\u8eab\u540e\u7684\u53cc\u811a\u5df2\u5c31\u52bf\u8e22\u51fa\uff0c\u201c\u5403\uff01\u5403\uff01\u201d \u4e24\u67c4\u77ed\u5251\u5728\u978b\u5c16\u7a7f\u51fa\uff0c\u731b\u523a\u5c55\u767d\u53cc\u76ee\uff01 \u5c55\u767d\u5927\u559d\u4e00\u58f0\uff1a\u201c\u4e0d\u597d\uff01\u2026\u2026\u201d \u6240\u5e78\u4ed6\u5b66\u4f1a\u4e86\u201c\u5e72\u5e7b\u98d8\u9999\u6b65\u201d\uff0c\u4e00\u9047\u5371\u6025\uff0c\u53cd\u5e94\u7acb\u751f\uff0c\u5c31\u5728\u660e\u6643\u6643\u7684\u5251\u5c16\u5c06\u53ca\u9762\u95e8\u4e4b \u9645\uff0c\u9762\u524d\u4eba\u5f71\u4e00\u82b1\uff0c\u539f\u5730\u5df1\u5931\u53bb\u4e86\u5c55\u767d\u7684\u8e2a\u8ff9\uff01 \u201c\u4e09\u5bf8\u4e01\u201d\u53cc\u811a\u8d50\u7a7a\uff0c\u5728\u534a\u7a7a\u4e2d\u8eab\u5f62\u5212\u4e86\u4e00\u4e2a\u5706\u5f27\uff0c\u53c8\u843d\u56de\u5730\u9762\uff0c\u53cc\u773c\u4e00\u508d\uff0c\u4e0d\u7531\u50bb \u4e86\uff01 \u773c\u89c1\u81ea\u5df2\u4e00\u62db\u201c\u9b3c\u738b\u649e\u949f\u201d\uff0c\u53cc\u811a\u77ed\u5251\u8e22\u51fa\uff0c\u8fd9\u79cd\u811a\u4e2d\u85cf\u5203\uff0c\u5c55\u767d\u8d64\u624b\u7a7a\u62f3\u6321\u4e0d\u6562\u6321\uff0c \u8eb2\u4e0d\u80fd\u8eb2\uff0c\u773c\u7741\u7741\u5fc5\u53ef\u628a\u5c55\u767d\u53cc\u76ee\u523a\u7a7f\uff0c\u600e\u5730\u4e00\u6643\u773c\u4e0d\u89c1\u4e86\u5c55\u81ea\u7684\u8e2a\u8ff9\uff1f \u83ab\u975e\u5c55\u767d\u8fd8\u4f1a\u501f\u571f\u9041\u8d70\u4e86\u4e0d\u6210\uff1f \u201c\u4e09\u5bf8\u4e01\u201d\u50bb\u695e\u7740\u53cc\u773c\uff0c\u4e1c\u77a7\u897f\u671b\uff0c\u4e0d\u89c1\u5c55\u767d\u4f55\u5904\u53bb\u4e86\uff0c\u5fc3\u4e0b\u6b63\u72af\u5600\u5495\uff0c\u7a81\u542c\u8eab\u540e \u201c\u55e4\u201d\u7684\u4e00\u58f0\u51b7\u7b11\uff01 \u201c\u4e09\u5bf8\u4e01\u201d\u5413\u5f97\u4e00\u54c6\u55e6\uff0c\u970d\u7136\u8f6c\u8eab\u5b50\u4e00\u770b\uff0c\u89c1\u5c55\u767d\u6b63\u7ad9\u5728\u4ed6\u8eab\u540e\u4e0d\u53ca\u4e00\u4e08\u4e4b\u5904\uff0c\u8d1f\u624b\u51b7 \u7b11\uff01 \u201c\u4e09\u5bf8\u4e01\u201d\u53c8\u60ca\u53c8\u6012\uff0c\u5c16\u5578\u4e00\u58f0\uff0c\u57cb\u5934\u53c8\u5411\u5c55\u767d\u649e\u6765\uff0c\u4ecd\u7136\u662f\u90a3\u62db\u201c\u9b3c\u738b\u649e\u949f\u201d\uff01 \u5c55\u767d\u9762\u5b54\u4e00\u6c89\uff0c\u6012HL\u9053\uff1a\u201c\u4f60\u627e\u6b7b\uff01\u201d \u559d\u7f62\uff0c\u518d\u4e0d\u7559\u60c5\u8fd0\u8d77\u201c\u5929\u4f5b\u964d\u9b54\u638c\u201d\u529f\uff0c\u4e00\u62db\u201c\u626b\u6e05\u5996\u6c1b\u201d\uff01\u53ea\u89c1\u5982\u60ca\u98ce\u9a87\u6d6a\u4e00\u822c\u7684\u5de8 \u5927\u52b2\u6d41\uff0c\u72c2\u5578\u7740\u5954\u5411\u201c\u4e09\u5bf8\u4e01\u201d\u9876\u95e8\uff01 \u201c\u4e09\u5bf8\u4e01\u201d\u57cb\u5934\u524d\u51b2\uff0c\u53ea\u611f\u8fce\u5934\u5982\u96f7\u96f7\u4e07\u94a9\u7684\u638c\u98ce\u52b2\u6c79\u6d8c\u800c\u81f3\uff0c\u5fc3\u77e5\u4e0d\u597d\uff01 \u4f46\u4ed6\u7684\u524d\u51b2\u4e4b\u52bf\u751a\u731b\uff0c\u518d\u60f3\u6536\u62db\u6362\u5f0f\uff0c\u5df2\u7ecf\u665a\u4e86\uff0c\u53ea\u542c\u201c\u8f70\u201d\u7136\u4e00\u58f0\u5927\u9707\uff0c\u201c\u6ce2\u201d\u7684\u4e00 \u58f0\u8106\u54cd\uff0c\u201c\u4e09\u5bf8\u4e01\u201d\u77ed\u5c0f\u8eab\u6750\uff0c\u76f4\u88ab\u638c\u98ce\u9707\u51fa\u4e09\u4e08\u5f00\u5916\uff0c\u4ed6\u4e00\u5c55\u767d\u4e00\u638c\u62cd\u788e\uff0c\u534a\u7a7a\u4e2d\u8111\u8840\u56db \u6e85\uff0c\u5427\u7b54\u201d\u4e00\u58f0\uff0c\u5c38\u9996\u6454\u843d\u5730\u4e0a\uff0c\u56db\u80a2\u8fde\u52a8\u90fd\u6ca1\u6709\u52a8\uff0c\u4fbf\u5df2\u6b7b\u53bb\uff01 \u5c55\u767d\u7ad9\u5728\u90a3\u91cc\u679c\u5446\u53d1\u6014\u4e4b\u9645\uff0c\u7a81\u7136\u4e24\u58f0\u51c4\u5389\u957f\u5578\uff0c\u72b9\u5982\u8352\u591c\u9b3c\u54ed\uff0c\u5212\u7834\u4e86\u5468\u906d\u591c\u7a7a\uff01 \u5c55\u767d\u95fb\u58f0\u77e5\u60ca\uff0c\u62ac\u5934\u56db\u987e\uff0c\u53ea\u89c1\u4ece\u201c\u4e09\u5bf8\u4e01\u201d\u65b9\u624d\u73b0\u8eab\u7684\u6811\u4e1b\u91cc\uff0c\u5982\u98de\u7535\u6d41\u77e2\u822c\uff0c\u98de\u5c04 \u6765\u4e86\u4e24\u9053\u9ed1\u5f71\uff01 \u8fd9\u4e24\u9053\u9ed1\u5f71\u6765\u5f97\u6025\uff0c\u505c\u8eab\u4e5f\u6025\uff0c\u98de\u77e2\u6025\u5c04\uff0c\u201c\u50bb\u201d\u7684\u4e00\u58f0\u7a33\u7ad9\u5728\u5c55\u767d\u9762\u524d\uff0c\u7adf\u5982\u9489\u5b50\u9489 \u5728\u5730\u4e0b\u4e00\u822c\uff0c\u7eb9\u4e1d\u4e0d\u52a8\uff01 \u5c55\u767d\u5012\u6346\u4e00\u53e3\u51b7\u6c14\uff0c\u5149\u770b\u6765\u4eba\u7684\u8f7b\u529f\u8eab\u6cd5\uff0c\u5f53\u4eca\u6b66\u6797\u5df2\u662f\u7f55\u89c1\uff01 \u7528\u76ee\u4e00\u6253\u91cf\u4e8c\u4eba\uff0c\u53ea\u89c1\u4e00\u4e2a\u8d64\u53d1\u7334\u8138\u7684\u8001\u8005\uff0c\u53ca\u4e00\u4e2a\u590f\u7a7f\u51ac\u8863\u7684\u51ac\u70d8\u8001\u5934\uff01 \u5c55\u767d\u8ba4\u8bc6\uff0c\u6b63\u662f\u96c1\u8361\u9b3c\u8c37\u7684\u201c\u6c5f\u5357\u4e8c\u5947\u201d\u201c\u8d64\u53d1\u7075\u7334\u201d\u5e38\u53bb\u6076\u3001\u201c\u9b3c\u8c37\u9690\u91cc\u201d\u6587\u6b63\u5947\uff01 \u201c\u6c5f\u5357\u4e8c\u5947\u201d\u4ee5\u72e0\u6bd2\u7684\u773c\u5149\uff0c\u72e0\u72e0\u5730\u77aa\u7740\u5c55\u81ea\uff0c\u56db\u9053\u773c\u5149\uff0c\u72b9\u5982\u56db\u67c4\u5229\u5251\uff0c\u770b\u5f97\u5c55\u767d\u4ece \u5fc3\u5e95\u6cdb\u8d77\u4e00\u822c\u5bd2\u610f\uff01 \u53c8\u89c9\u5f97\u81ea\u5df1\u4e00\u638c\u6253\u6b7b\u4e8c\u4eba\u7684\u7231\u5f92\uff0c\u5fc3\u4e2d\u4e0d\u7531\u751f\u51fa\u6127\u759a\u4e4b\u5ff5\uff01 \u4f46\u77ac\u5373\u60f3\u5230\u4ed6\u4e8c\u4eba\u5fd8\u4e49\u5f11\u5e08\u7684\u7f6a\u884c\uff0c\u7acb\u523b\u53c8\u6fc0\u8d77\u4ed6\u5ac9\u6076\u5982\u4ec7\u7684\u6ee1\u8154\u70ed\u8840\uff0c\u80c6\u6c14\u4e3a\u4e4b\u4e00 \u58ee\uff01 \u201c\u8d64\u53d1\u7075\u7334\u201d\u4e00\u5b57\u4e00\u987f\uff0c\u6076\u72e0\u72e0\u5730\u9053\uff1a\u201c\u5c0f\u5b50\uff01\u6740\u6211\u7231\u5f92\uff01\u8001\u592b\u5982\u679c\u4e0d\u628a\u4f60\u788e\u5c38\u4e07\u6bb5\uff0c \u65ad\u96be\u6d88\u8001\u592b\u5fc3\u5934\u4e4b\u6068\uff01\u201d \u201c\u9b3c\u8c37\u9690\u53df\u201d\u51b7\u7b11\u8fde\u8fde\uff0c\u4e0d\u8fc7\u4ed6\u8fd9\u7b11\u58f0\uff0c\u6bd4\u9b3c\u54ed\u72fc\u53f7\u8fd8\u8981\u96be\u542c\u5341\u500d\uff0c\u789f\u789f\u4e4b\u58f0\uff0c\u542c\u5f97\u5c55 \u767d\u5468\u8eab\u9e21\u6817\u76f4\u7acb\uff01 \u201c\u5e08\u5144\uff01\u201d\u201c\u9b3c\u8c37\u9690\u53df\u201d\u6587\u6b63\u5947\u4ece\u54ac\u7d27\u7684\u7259\u9f7f\u4e2d\u8fdb\u51fa\u68ee\u51b7\u7684\u8bed\u8c03\u9053\uff1a\u201c\u628a\u8fd9\u5c0f\u5b50\u788e\u5c38\u4e07 \u6bb5\uff0c\u90fd\u4fbf\u5b9c\u4e86\u4ed6\uff01\u8001\u592b\u5374\u8981\u7528\u2018\u9634\u7a74\u622a\u8109\u2019\u2018\u4e94\u9634\u6388\u9b42\u2019\u2018\u6572\u9aa8\u62bd\u9ad3\u201d\u201c\u5265\u76ae\u62bd\u7b4b\u2019\uff0c\u6240\u6709 \u7684\u60e8\u6bd2\u624b\u6cd5\uff0c\u52a0\u8bf8\u5c0f\u5b50\u7684\u8eab\u4e0a\uff0c\u8981\u4ed6\u54c0\u53f7\u2018\u4e03\u4e03\u56db\u5341\u4e5d\u5929\uff0c\u7136\u540e\u518d\u628a\u5c0f\u5b50\u788e\u5272\u51cc\u8fdf\uff01\u201d \u5c55\u767d\u4ef0\u5929\u5927\u7b11\u9053\uff1a\u4f60\u4eec\u4e24\u4e2a\u8001\u8d3c\u518d\u72e0\uff01\u4f46\u4e5f\u4e0d\u80fd\u6d17\u5237\u4f60\u4eec\u5f11\u5e08\u7684\u7f6a\u540d\uff01\u201d \u201c\u6c5f\u5357\u4e8c\u5947\u201d\u72e0\u6bd2\u7684\u8138\u4e0a\uff0c\u540c\u65f6\u6d41\u8fc7\u4e00\u62b9\u60ca\u9a87\u4e4b\u8272\uff0c\u4e92\u76f8\u5bf9\u671b\u4e86\u4e00\u773c\uff0c\u4f46\u77ac\u5373\u4e00\u77aa\u773c\uff0c \u8138\u4e0a\u6740\u673a\u66f4\u6d53\uff0c\u5dee\u4e0d\u591a\u662f\u540c\u65f6\u66b4\u53f1\u9053\uff1a \u201c\u4f60\u5c0f\u5b50\u80e1\u8bf4\u4e9b\u4ec0\u4e48\uff1f\u4f60\u8fde\u6211\u4e8c\u4eba\u7684\u6069\u5e08\u662f\u8c01\u90fd\u4e0d\u77e5\u9053\uff0c\u7ade\u80c6\u6562\u80e1\u8a00\u4e71\u8bed\uff0c\u96be\u9053\u6b7b\u5728\u8ddf \u524d\uff0c\u8fd8\u8981\u542b\u6c99\u5c04\u5f71\uff0c\u542b\u8840\u55b7\u4eba\u5417\uff1f\u2026\u2026\u201d \u5c55\u767d\u7b11\u9053\uff1a\u201c\u8981\u60f3\u4eba\u4e0d\u77e5\uff0c\u9664\u975e\u5df1\u83ab\u4e3a\uff01\u770b\u4f60\u4e8c\u4eba\u8863\u51a0\u695a\u695a\uff0c\u53ef\u80fd\u4e5f\u8bfb\u8fc7\u51e0\u5929\u5723\u8d24\u4e4b \u4e66\uff0c\u8fde\u8fd9\u70b9\u6d45\u663e\u771f\u7406\u90fd\u4e0d\u77e5\u9053\u5417\uff1f\u2026\u2026\u201d \u201c\u6c5f\u5357\u4e8c\u5947\u201d\u8138\u4e0a\u60ca\u5bb9\u66f4\u751a\uff0c\u5927\u5947\u201c\u8d64\u53d1\u7075\u7334\u201d\u5c16\u8d77\u55d3\u5b50\u543c\u9053\uff1a \u201c\u4f60\u8bf4\uff01\u4f60\u8bf4\uff01\u6211\u4e8c\u4eba\u7684\u6069\u5e08\u662f\u8c01\uff1f\u5047\u5982\u8bf4\u4e0d\u51fa\u6765\uff0c\u8001\u592b\u53eb\u4f60\u6b7b\u65e0\u597d\u6b7b\uff01\u201d \u5c55\u767d\u671d\u4ed6\u8f7b\u5351\u5730\u4e00\u77a5\uff0c\u4e0d\u7d27\u4e0d\u6162\u5730\u9053\u3002\u201d\u2018\u795e\u7334\u2019\u94c1\u51cc\uff01\u8be5\u4e0d\u4f1a\u6709\u9519\u5427\uff1f \u201c\u6c5f\u5357\u9707\u5947\u201d\u5982\u88ab\u874e\u86f0\uff0c\u5468\u8eab\u808c\u8089\u540c\u65f6\u6296\u4e86\u4e00\u4e0b\u3002\u4f46\u201c\u9b3c\u8c37\u9690\u53df\u201d\u7acb\u5373\u66b4\u6012\uff0c\u53f1\u9053\uff1a \u201c\u5e08\u5144\uff01\u4e0d\u7528\u8ddf\u8fd9\u5c0f\u5b50\u9b3c\u626f\uff0c\u6211\u4eec\u5148\u5408\u8d77\u624b\u6765\uff0c\u628a\u4ed6\u5c0f\u5b50\u6bd9\u4e86\u518d\u8bf4\uff01\u201d \u5927\u5947\u201c\u8d64\u53d1\u7075\u7334\u201d\u4e5f\u89c9\u9690\u79d8\u88ab\u5c55\u767d\u63a2\u77e5\uff0c\u5173\u7cfb\u592a\u5927\uff0c\u8fd9\u4e8b\u60c5\u5982\u679c\u4f20\u51fa\u6c5f\u6e56\uff0c\u201c\u6c5f\u5357\u4e8c \u5947\u201d\u5fc5\u5c06\u4e3a\u6240\u6709\u6b66\u6797\u4eba\u7269\u4e0d\u9f7f\uff0c\u6240\u4ee5\u6bd2\u5ff5\u66f4\u70bd\uff0c\u7acb\u610f\u8981\u628a\u5c55\u767d\u6740\u4e4b\u706d\u53e3\u3002 \u542c\u5e08\u5f1f\u201c\u9b3c\u8c37\u9690\u53df\u201d\u8bf4\u5b8c\uff0c\u201c\u8d64\u53d1\u7075\u7334\u201d\u4e0d\u518d\u7b54\u8a00\uff0c\u9ed8\u8fd0\u7384\u529f\uff0c\u771f\u6c14\u8d2f\u6ce8\uff0c\u53ea\u542c\u4ed6\u5468\u8eab \u9aa8\u8282\u201c\u54af\u54af\u201d\u66b4\u54cd\uff01 \u201c\u9b3c\u8c37\u9690\u53df\u201d\u53cc\u624b\u6293\u7d27\u53c8\u653e\u677e\uff0c\u653e\u677e\u53c8\u6293\u7d27\uff0c\u663e\u89c1\u4ea6\u5728\u8fd0\u96c6\u5468\u8eab\u529f\u529b\uff01 \u5468\u906d\u7a7a\u6c14\u5374\u51e0\u4e4e\u4e3a\u4e4b\u51dd\u7ed3\uff0c\u5929\u7a7a\u4e00\u7247\u4e4c\u4e91\u63a9\u8fc7\uff0c\u628a\u4ec5\u4ec5\u6709\u4e00\u4e9b\u5fae\u5149\u7684\u5bd2\u661f\u4e5f\u906e\u853d\u4f4f\u4e86\uff0c \u9ed1\u591c\u8352\u5c71\uff0c\u66f4\u663e\u5f97\u65e0\u6bd4\u51c4\u5389\uff01 \u5c71\u96e8\u6b32\u6765\u98ce\u6ee1\u697c\uff0c\u8840\u6218\uff0c\u6709\u4e00\u89e6\u5373\u53d1\u4e4b\u52bf\uff01 \u5c55\u767d\u77e5\u9053\u4e00\u573a\u6076\u6218\uff0c\u5728\u6240\u96be\u514d\uff01 \u56e0\u6b64\uff0c\u5c55\u767d\u4e00\u8fb9\u6697\u4e2d\u6212\u5907\uff0c\u6dee\u5907\u968f\u65f6\u8fce\u654c\uff0c\u4e00\u8fb9\u5634\u4e2d\u8bf4\u9053\uff1a \u201c\u5728\u4e0b\u4e0e\u4eba\u6709\u7ea6\uff0c\u4eca\u540e\u4e0d\u95ee\u6c5f\u6e56\u662f\u975e\u3002\u4f60\u4e8c\u4f4d\u5982\u679c\u4e00\u5b9a\u8981\u627e\u5728\u4e0b\u52a8\u624b\uff0c\u90a3\u4e48\uff0c\u5f97\u8bf7\u4f60\u4eec \u4e8c\u4f4d\u7559\u4e0b\u70b9\u8bc1\u636e\uff0c\u8bc1\u660e\u662f\u4f60\u4eec\u4e8c\u4eba\u903c\u7740\u5728\u4e0b\u52a8\u624b\uff0c\u5728\u4e0b\u5949\u966a\u2026\u2026\u201d \u5927\u5947\u201c\u8d64\u53d1\u7075\u7334\u201d\u53f1h\u9053\uff1a\u201c\u5c11\u5e9f\u8bdd\uff01\u201d \u4e8c\u5947\u201c\u9b3c\u8c37\u9690\u53df\u201d\u540c\u65f6\u53f1\u9053\uff1a\u4eca\u5929\u4f60\u5c0f\u5b50\u662f\u6b7b\u5b9a\u4e86\uff01\u8fd8\u7559\u5f97\u4ec0\u4e48\u8bc1\u636e\uff1f\u201d \u4e8c\u5947\u559d\u7f62\uff0c\u5927\u5947\u5410\u6c14\u5f00\u58f0\uff0c\u4ee5\u4ed6\u82e6\u70bc\u65b0\u6210\u7684\u201c\u9ed1\u715e\u624b\u201d\uff0c\u4e00\u5f0f\u4e2a\u9ed1\u624b\u593a\u9b42\u201d\uff0c\u731b\u5411\u5c55\u767d \u9762\u95e8\u5288\u6765\uff01 \u5dee\u4e0d\u591a\u662f\u540c\u65f6\uff0c\u4e8c\u5947\u201c\u9b3c\u8c37\u9690\u53df\u201d\u80a5\u5927\u888d\u8896\u4e00\u626c\uff0c\u9732\u51fa\u4ed6\u7626\u5982\u9b3c\u722a\u4f3c\u7684\u4e00\u53ea\u53f3\u638c\uff0c\u4e5f\u662f \u4ee5\u65b0\u8fd1\u7ec3\u5c31\u7684\u201c\u9634\u98ce\u638c\u201d\uff0c\u914d\u5408\u7740\u5927\u5947\u7684\u653b\u52bf\uff0c\u4ece\u659c\u523a\u91cc\u5207\u51fa\u4e00\u638c\uff0c\u90e8\u4f4d\u662f\u5954\u5411\u5c55\u767d\u201c\u5de6\u80a9 \u4e95\u201d\uff01 \u4e24\u5927\u9690\u4e16\u9ad8\u624b\uff0c\u53c8\u662f\u6570\u5341\u5e74\u7cbe\u4fee\u82e6\u7ec3\uff0c\u5982\u4eca\u8054\u8d77\u624b\u6765\u51fa\u62db\uff0c\u4e0d\u4f46\u914d\u5408\u5f97\u201c\u5929\u8863\u65e0\u7f1d\u201d\uff0c \u800c\u4e14\u638c\u6307\u4e0a\u7684\u5a01\u529b\uff0c\u76f8\u6d4e\u76f8\u6210\uff0c\u7684\u786e\u5927\u5f97\u60ca\u4eba\uff01 \u5c55\u767d\u6697\u61d4\u4e8c\u5947\u6b66\u529f\uff0c\u786e\u662f\u4e16\u6240\u7f55\u89c1\uff0c\u56e0\u4e3a\u4ed6\u6ca1\u6709\u628a\u63e1\u7a33\u64cd\u80dc\u7b97\uff0c\u628a\u4e8c\u5947\u540c\u65f6\u9664\u53bb\uff0c\u56e0\u6b64 \u4e0d\u613f\u63a5\u62db\uff0c\u8e0f\u8d77\u201c\u5343\u5e7b\u98d8\u9999\u6b65\u201d\uff0c\u8c08\u7136\u9038\u51fa\u4e8c\u5947\u7684\u51cc\u5389\u62db\u5f0f\u4e4b\u5916\uff01 \u4e8c\u5947\u540c\u65f6\u4e00\u6115\uff0c\u53ef\u662f\u4ed6\u4e8c\u4eba\u5b9e\u6bd4\u201c\u4e09\u5bf8\u4e01\u201d\u9ad8\u660e\u591a\u4e86\uff0c\u5c55\u767d\u6b65\u6cd5\u867d\u7136\u795e\u5999\u65e0\u6bd4\uff0c\u4f46\u4e8c\u4eba \u8eab\u5f62\u4e00\u65cb\uff0c\u65e9\u53c8\u5404\u81ea\u653b\u51fa\u4e00\u638c\uff01 \u638c\u98ce\u9510\u5578\uff0c\u626c\u77f3\u98de\u7802\u3002 \u5c55\u81ea\u7b2c\u4e8c\u6b21\u95ea\u8eab\u8eb2\u8fc7\uff0c\u4f46\u4ecd\u672a\u8fd8\u624b\uff01 \u4e8c\u5947\u5f62\u540c\u75af\u72c2\uff0c\u5404\u81ea\u5389\u5578\u4e00\u58f0\uff0c\u8eab\u6cd5\u5c55\u5f00\uff0c\u540c\u65f6\u65bd\u51fa\u7edd\u5927\u6740\u62db\uff0c\u4f46\u89c1\u6ee1\u5929\u638c\u5f71\uff0c\u72c2\u5578\u800c \u4e0b\uff01 \u5c55\u767d\u201c\u5343\u5e7b\u98d8\u9999\u6b65\u201d\u56fa\u7136\u7075\u5de7\uff0c\u4f46\u770b\u6837\u5b50\u5149\u51ed\u8eb2\u95ea\uff0c\u4e0d\u51fa\u624b\u662f\u4e0d\u884c\u4e86\uff01 \u7a81\u7136\u2014\u2014\u4e00\u9635\u9999\u98ce\u5439\u8fc7\uff0c\u5982\u5c71\u7684\u52b2\u6d41\u968f\u4e4b\u800c\u81f3\uff0c\u201c\u8f70\u201d\u7136\u4e00\u58f0\uff0c\u201c\u6c5f\u5357\u4e8c\u5947\u201d\u8863\u98de\u53d1 \u4e3e\uff0c\u540c\u65f6\u88ab\u9707\u9000\u4e09\u6b65r\u201c\u6c5f\u5357\u4e8c\u5947\u201d\u540c\u65f6\u5403\u4e86\u4e00\u5ac1\uff0c\u53ea\u89c9\u5bf9\u65b9\u638c\u529b\u5927\u5f97\u51fa\u5947\uff0c\u7b80\u76f4\u4e0d\u53ef\u6297 \u62d2\uff0c\u54ac\u7259\u7ad9\u7a33\u8eab\u5f62\uff0c\u4e3e\u76ee\u4e00\u770b\uff0c\u9762\u524d\u591a\u4e86\u4e00\u4e2a\u8eab\u6750\u77ee\u5c0f\u3001\u9752\u9762\u7ea2\u53d1\u7684\u602a\u6c49\uff01 \u201c\u6c5f\u5357\u4e8c\u5947\u201d\u4e00\u770b\u4e0d\u8ba4\u8bc6\uff0c\u5927\u5947\u201c\u8d64\u53d1\u7075\u7334\u201d\u5e38\u53bb\u6076\u53cc\u773c\u6012\u7741\uff0c\u5389\u5317\u9053\uff1a\u201c\u4f60\u5c0f\u5b50\u662f\u4f55 \u4eba\uff1f\u7adf\u6562\u4e0e\u2018\u6c5f\u5357\u4e8c\u5947\u2019\u4f38\u624b\u67b6\u6881\uff01\u201d \u4e8c\u5947\u201c\u9b3c\u8c37\u9690\u53df\u201d\u4e5f\u53f1\u9053\uff1a\u201c\u5c0f\u5b50\u53ef\u662f\u6d3b\u5f97\u4e0d\u8010\u70e6\u4e86\uff01\u201d \u9752\u9762\u7ea2\u53d1\u602a\u4eba\uff0c\u5f53\u7136\u662f\u6234\u4e86\u9762\u5177\u7684\u67f3\u7fe0\u7fe0\uff0c\u58f0\u8c03\u4e00\u53d8\uff0c\u602a\u53f1\u9053\uff1a\u5c11\u7f57\u568e\uff01\u63a5\u62db\uff01\u201d \u542c\u5230\u62db\u51fa\uff0c\u53cc\u638c\u4e00\u5206\uff0c\u5de6\u53f3\u5206\u51fb\u4e8c\u4eba\uff01 \u201c\u6c5f\u5357\u4e8c\u5947\u201d\u540c\u65f6\u4e3e\u638c\u8fce\u4e0a\uff01 \u201c\u7830\uff01\u6666\uff01\u201d \u4e24\u58f0\u66b4\u54cd\uff0c\u4e8c\u5947\u540c\u65f6\u53c8\u88ab\u9707\u9000\u4e09\u6b65\uff01 \u67f3\u7fe0\u7fe0\u54af\u7565\u602a\u7b11\uff0c\u9053\uff1a\u201c\u5c31\u51ed\u4f60\u4e8c\u4eba\u8fd9\u7b49\u8d27\u8272\uff0c\u4e5f\u6562\u5927\u8a00\u4e0d\u60ed\uff1f\u201d \u2018\u2018\u6c5f\u5357\u4e8c\u5947\u201d\u540c\u65f6\u5927\u6012\uff0c\u602a\u5578\u5389\u543c\u7740\uff0c\u5404\u81ea\u8fd0\u51fa\u5468\u8eab\u529f\u529b\uff0c\u731b\u5411\u7fe0\u7fe0\u6251\u6765\uff01 \u7fe0\u7fe0\u5634\u4e2d\u7b11\u58f0\u672a\u505c\uff0c\u4e00\u53cc\u6d01\u767d\u7684\u7389\u624b\uff0c\u72b9\u5982\u8fce\u98ce\u8774\u8776\uff0c\u53ea\u89c1\u5979\u8f7b\u63cf\u6de1\u5199\u5730\uff0c\u53cc\u638c\u5411\u4e8c\u5947 \u6325\u53bb\uff01 \u201c\u7830\uff01\u7830\uff01\u201d \u53c8\u662f\u4e24\u58f0\u66b4\u54cd\uff0c\u201c\u6c5f\u5357\u4e8c\u5947\u201d\u8fd9\u6b21\u5374\u88ab\u9707\u51fa\u4e94\u516d\u6b65\u53bb\uff01 \u201c\u6c5f\u5357\u4e8c\u5947\u201d\u63a5\u8fde\u88ab\u9707\u9000\u4e09\u6b21\uff0c\u72c2\u6001\u5168\u6536\uff0c\u4e14\u8179\u5185\u771f\u6c14\u6d41\u7a9c\uff0c\u81ea\u77e5\u4e0d\u662f\u5bf9\u624b\uff0c\u5fc3\u4e2d\u5df2\u751f \u5feb\u610f\u3002 \u7fe0\u7fe0\u54af\u7565\u602a\u7b11\uff0c\u9053\uff1a\u201c\u6765\u5440\uff01\u600e\u4e48\u4e0d\u6562\u6253\u4e86\uff1f\u201d \u8bf4\u7740\u53cc\u638c\u7f13\u7f13\u4e3e\u8d77\uff0c\u4e24\u53ea\u767d\u5f97\u51fa\u5947\u7684\u5c0f\u624b\uff0c\u7ade\u653e\u51fa\u4e24\u9053\u8000\u773c\u7684\u767d\u5149\uff01 \u7b2c\u4e94\u5341\u4e8c\u7ae0\u3000\u8def\u9047\u201c\u7a77\u5bb6\u5e2e\u201d>> \u53e4\u9f99\u300a\u5251\u5ba2\u884c\u300b \u7b2c\u4e94\u5341\u4e8c\u7ae0\u3000\u8def\u9047\u201c\u7a77\u5bb6\u5e2e\u201d \u201c\u6c5f\u5357\u4e8c\u5947\u201d\u9762\u8272\u60e8\u53d8\uff0c\u8131\u53e3\u60ca\u547c\u9053\uff1a\u201c\u592a\u9634\u795e\u638c\uff01\u201d \u7fe0\u7fe0\u51b7\u7b11\u9053\uff1a\u201c\u6015\u4e86\u5417\uff1f\u201d \u8bf4\u7740\u53cc\u638c\u7f13\u7f13\u5411\u201c\u6c5f\u5357\u4e8c\u5947\u201d\u538b\u4e0b\uff01 \u4e00\u822c\u6c79\u6d8c\u7684\u5de8\u5927\u6c14\u6d41\uff0c\u6fc0\u8361\u800c\u51fa\uff01 \u201c\u6c5f\u5357\u4e8c\u5947\u201d\u9762\u65e0\u4eba\u8272\uff0c\u817e\u8eab\u60f3\u8dd1\uff0c\u4f46\u90a3\u80a1\u6c79\u6d8c\u6c14\u6d41\u7ade\u4f3c\u4e00\u4e2a\u65e0\u5f62\u7684\u5de8\u5927\u94a2\u7f69\u4e00\u822c\uff0c\u628a \u4e8c\u4eba\u7f69\u5b9a\uff0c\u60f3\u8dd1\u8fde\u811a\u6b65\u90fd\u65e0\u6cd5\u64d2\u8d77\uff01 \u8fd9\u662f\u201c\u6c5f\u5357\u4e8c\u5947\u201d\u6709\u751f\u4ee5\u6765\uff0c\u7ecf\u8fc7\u65e0\u6570\u9635\u4ed7\uff0c\u4ece\u672a\u7ecf\u8fc7\u7684\u73b0\u8c61\u3002\u4e8c\u4eba\u8138\u8272\u6d41\u9732\u51fa\u65e0\u6bd4\u60ca \u60e7\u7684\u795e\u8272\uff01 \u6ca1\u60f3\u5230\u4e24\u4e2a\u65e0\u7236\u65e0\u541b\u3001\u72c2\u50b2\u4e0d\u53ef\u4e00\u4e16\u7684\u4e24\u5927\u9ad8\u624b\uff0c\u7adf\u7136\u5982\u6b64\u6015\u6b7b\uff01 \u5c55\u767d\u7a81\u7136\u5728\u540e\u8fb9\u53eb\u9053\uff1a\u201c\u7fe0\u7fe0\uff01\u2026\u2026\u201d \u7fe0\u7fe0\u95fb\u5c55\u767d\u4e00\u53eb\uff0c\u731b\u7136\u8bb0\u8d77\u5979\u7684\u767d\u54e5\u54e5\u6700\u53cd\u5bf9\u5979\u6ee5\u6740\u65e0\u8f9c\uff0c\u4e3a\u4e86\u4e0d\u4f7f\u5c55\u767d\u4e0d\u9ad8\u5174\uff0c\u5979\u8fd9 \u65f6\u6536\u4f4f\u638c\u52bf\uff0c\u5378\u53bb\u529f\u529b\uff0c\u53cc\u624b\u4e0b\u5782\uff0c\u51b7\u51b7\u5730\u8bf4\u9053\uff1a\u201c\u770b\u5728\u767d\u54e5\u54e5\u4efd\u4e0a\uff0c\u9976\u4f60\u4e8c\uff1a\u4eba\u4e00\u6761\u72d7 \u547d\uff01\u6eda\u5427\uff01\u201d \u201c\u6c5f\u5357\u4e8c\u5947\u201d\u5982\u9022\u5927\u8d66\uff0c\u8bdd\u4e5f\u6ca1\u6562\u591a\u8bf4\u4e00\u53e5\uff0c\u6389\u5934\u9f20\u7a9c\u800c\u4e91\uff01 \u8fde\u4e22\u5728\u5730\u4e0b\u7684\u7231\u5f92\u201c\u4e09\u5bf8\u4e01\u201d\u7684\u5c38\u8eab\u4e5f\u987e\u4e0d\u5f97\u6536\u62fe\u4e86\u3002 \u5c55\u767d\u89c1\u7fe0\u7fe0\u653e\u8d70\u4e86\u201c\u6c5f\u5357\u4e8c\u5947\u201d\uff0c\u61ca\u6094\u5730\u8fde\u8fde\u8dfa\u811a\uff0c\u6028\u7fe0\u7fe0\u9053\uff1a\u201c\u7fe0\u7fe0\uff01\u4f60\u600e\u4e48\u628a\u4ed6\u4e8c \u4eba\u653e\u8d70\u5462\uff1f\u201d \u7fe0\u7fe0\u4e00\u624b\u63ed\u4e0b\u8111\u4e0a\u7684\u5965\u9762\u5177\uff0c\u4e00\u53cc\u5927\u773c\u775b\u7741\u5f97\u6eda\u5706\uff0c\u5947\u602a\u5730\u671b\u7740\u5c55\u767d\uff0c\u9053\uff1a\u201c\u54a6\uff01\u767d\u54e5 \u54e5\uff01\u4f60\u4e0d\u662f\u4e0d\u613f\u59b9\u59b9\u591a\u6740\u65e0\u8f9c\u5417\uff1f\u201d \u5c55\u767d\u8dcc\u811a\u9053\uff1a\u4ed6\u4e8c\u4eba\u662f\u6740\u5bb3\u81ea\u5df1\u6069\u5e08\uff0c\u6beb\u65e0\u4eba\u6027\u7684\u6076\u5f92\uff0c\u4e0d\u5ea7\u653e\u8d70\u7684\uff01\u201d \u7fe0\u7fe0\u5c0f\u5634\u4e00\u561f\uff0c\u9887\u4e0d\u9ad8\u5174\u5730\u9053\uff1a\u201c\u8fd9\u5012\u96be\u4e86\uff01\u6709\u65f6\u4f60\u4e0d\u8981\u6211\u6740\u4eba\uff0c\u6709\u65f6\u53c8\u53eb\u6211\u6740\uff0c\u8c01\u77e5 \u4f60\u7a76\u7adf\u8981\u600e\u6837\uff1f\u201d \u5c55\u767d\u4e5f\u6c14\u8d77\u6765\uff0c\u9053\uff1a\u201c\u6211\u8981\u4f60\u600e\u6837\uff1f\u53ea\u8981\u4f60\u4e0d\u7ed9\u6211\u6dfb\u9ebb\u70e6\u5c31\u597d\u4e86\uff01\u521a\u628a\u6a0a\u59d1\u5a18\u6c14\u8d70\uff0c\u53c8 \u653e\u8d70\u5341\u6076\u4e0d\u8d66\u7684\u6076\u4eba\uff01\u2026\u2026\u201d \u7fe0\u7fe0\u7389\u5bb9\u60e8\u53d8\uff0c\u4e0d\u7b49\u5c55\u767d\u628a\u8bdd\u8bf4\u5b8c\uff0c\u4fbf\u6c14\u9053\uff1a\u201c\u597d1\u6211\u4e0d\u7ed9\u4f60\u6dfb\u9ebb\u70e6\uff01\u6211\u8d70\uff01\u201d \u7531\u4e8e\u5c55\u767d\u4e5f\u5728\u6c14\u5934\u4e0a\uff0c\u7fe0\u7fe0\u8d70\u65f6\u4ed6\u8fde\u963b\u62e6\u90fd\u672a\u62e6\u963b\uff0c\u4e8b\u5b9e\u4e0a\u7fe0\u7fe0\u8eab\u6cd5\u592a\u5feb\uff0c\u8981\u62e6\u4e5f\u62e6\u4e0d \u53ca\u4e86\u3002\u76f4\u7b49\u5230\u7fe0\u7fe0\u7684\u4eba\u5f71\u5df2\u6d88\u5931\u5728\u82cd\u832b\u591c\u8272\u4e4b\u4e2d\uff0c\u5c55\u767d\u5140\u81ea\u5583\u5583\u9053\uff1a\u201c\u8d70\u5c31\u8d70\u5427\uff01\u6709\u4ec0\u4e48\u4e86 \u4e0d\u8d77\u2026\u2026\u201d \u5634\u4e2d\u867d\u5982\u6b64\u8bf4\uff0c\u5fc3\u4e2d\u5374\u4e0d\u5154\u5174\u8d77\u4e00\u79cd\u832b\u7136\u7a7a\u865a\u7684\u611f\u89c9\uff01 \u591c\u51c9\u5982\u6c34\uff0c\u4e91\u6de1\u661f\u7a00\uff01 \u5929\u7a7a\u7684\u9ed1\u4e91\uff0c\u4e5f\u4e0d\u77e5\u4f55\u65f6\u6d88\u6563\u4e86\uff0c\u4e1c\u65b9\u5929\u8fb9\u5df2\u73b0\u51fa\u9c7c\u809a\u767d\u8272\uff0c\u6b63\u662f\u201c\u803f\u803f\u661f\u6cb3\u6b32\u66d9\u5929\u201d \u7684\u65ad\u96c1\u65f6\u5206\u3002 \u201c\u72d01\u54d1\uff01\u201d\u4e00\u53ea\u5931\u7fa4\u7684\u5b64\u96c1\uff0c\u54c0\u9e23\u4e86\u4e24\u58f0\uff0c\u4ece\u6811\u68a2\u62cd\u7fc5\u98de\u8d77\uff0c\u5728\u660f\u6697\u7684\u5929\u7a7a\u7ed5\u4e86\u51e0\u4e2a \u5708\u5b50\uff0c\u98de\u5411\u832b\u832b\u8fdc\u65b91\u8fd9\u5931\u7fa4\u7684\u5b64\u96c1\uff0c\u5bc2\u5bde\u65e0\u4fa3\uff0c\u5929\u6daf\u832b\u832b\u4f55\u5904\u662f\u5b83\u7684\u5f52\u5bbf\uff1f \u4ed6\u6f2b\u65e0\u76ee\u7684\u5730\u7f13\u6b65\u8d70\u7740\uff0c\u5728\u8352\u5c71\u91ce\u5cad\u4e4b\u95f4\uff0c\u5728\u6668\u5149\u4eae\u5fae\u7684\u6653\u96fe\u4e4b\u4e2d\uff0c\u4ed6\u843d\u5bde\u7684\u8eab\u5f71\u8d8a\u53d1 \u663e\u5f97\u5b64\u72ec\u4e86\uff01 \u6b63\u662f\u201c\u9752\u773c\u76f8\u770b\u80fd\u6709\u51e0\uff0c\u82f1\u96c4\u7a77\u9014\u5c11\u4eba\u77e51\u201d\u7a81\u7136\u2014\u2014\u5bf9\u9762\u5c71\u5761\u4e0a\u8e7f\u51fa\u6570\u6761\u4eba\u5f71\uff01 \u8fd9\u4e9b\u4eba\u884c\u8272\u82cd\u4fc3\uff0c\u770b\u89c1\u5c55\u767d\uff0c\u4e00\u9f50\u98de\u5954\u7740\u8dd1\u6765\uff01 \u201c\u8d24\u4f84\uff01\u201d \u201c\u5c0f\u6069\u516c\uff01\u201d \u201c\u5c55\u54e5\u54e5\uff01\u201d \u8fd8\u79bb\u7740\u8001\u8fdc\uff0c\u4fbf\u4e00\u9f50\u9ad8\u58f0\u547c\u558a\u8d77\u6765\uff0c\u5c55\u767d\u4e3e\u76ee\u4e00\u770b\uff0c\u539f\u6765\u662f\u96f7\u5927\u8d85\u3001\u201c\u592a\u767d\u53cc\u9038\u201d\u3001\u8339 \u8001\u9556\u5934\u53ca\u6155\u5bb9\u7ea2\uff01 \u8fd9\u51e0\u4e2a\u6b66\u529f\u9ad8\u624b\uff0c\u8dd1\u5f97\u90fd\u6709\u70b9\u6c14\u5598\u5401\u5401\uff0c\u4e71\u53d1\u98de\u84ec\u7684\u96f7\u5927\u53d4\u9996\u5148\u53eb\u9053\uff1a\u201c\u8d24\u4f84\uff01\u4f60\u600e\u4e48 \u4e00\u4e2a\u4eba\u8dd1\u5230\u8fd9\u91cc\u6765\u4e86\uff1f\u201d \u201c\u592a\u767d\u53cc\u9038\u201d\u63a5\u7740\u9053\uff1a\u201c\u5c0f\u6069\u516c\uff01\u53eb\u6211\u4eec\u597d\u627e\uff01\u201d \u6155\u5bb9\u7ea2\u8131\u53e3\u53eb\u9053\uff1a\u201c\u5c55\u54e5\u54e5\u2026\u2026\u201d \u8fd9\u5fc3\u9ad8\u6c14\u50b2\u7684\u5c11\u5973\uff0c\u4e0d\u89c1\u4e86\u672a\u5a5a\u592b\uff0c\u6bd4\u4efb\u4f55\u4eba\u90fd\u8981\u7126\u6025\uff0c\u4f46\u89c1\u5230\u5c55\u767d\uff0c\u53ea\u8fd9\u4e00\u53e5\u201c\u5c55\u54e5 \u54e5\uff01\u201d\u4fbf\u4ee3\u8868\u4e86\u5343\u8a00\u4e07\u8bed\uff0c\u5176\u4ed6\u518d\u4e5f\u8bf4\u4e0d\u51fa\u6765\u4e86\u3002 \u5c55\u767d\u6447\u5934\u4e00\u56db\uff0c\u4f46\u4ed6\u7684\u5fc3\u91cc\u5374\u6d41\u8fc7\u4e86\u4e00\u822c\u6e29\u6696\uff0c\u56e0\u4e3a\u4ece\u8fd9\u4e9b\u4eba\u7684\u795e\u6001\u4e2d\uff0c\u4ed6\u5df2\u77e5\u9053\uff0c\u4ed6 \u5e76\u4e0d\u662f\u5b64\u72ec\u7684\uff0c\u4ecd\u6709\u8bb8\u591a\u4eba\u5173\u5fc3\u4ed6\uff0c\u656c\u7231\u4ed6\uff01 \u4eba\uff0c\u662f\u79bb\u4e0d\u5f00\u7231\u7684\uff01\u5c31\u662f\u82f1\u96c4\u4e5f\u4e0d\u4f8b\u5916\u3002 \u8339\u8001\u9556\u5934\u6c5f\u6e56\u9605\u5386\u6700\u4e30\uff0c\u4e09\u6559\u4e5d\u6d41\u7684\u4eba\u7269\u4ec0\u4e48\u6837\u7684\u4eba\u90fd\u63a5\u89e6\u8fc7\uff0c\u5584\u4e8e\u89c2\u4eba\u8fa8\u8272\u3002\u89c1\u5c55\u767d \u6447\u5934\u53f9\u606f\uff0c\u795e\u60c5\u843d\u5bde\uff0c\u65e9\u5df3\u770b\u51fa\u5176\u5fc3\u610f\uff0c\u4e8e\u662f\u5e72\u54b3\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5c55\u8d24\u5f1f\uff01\u4e9b\u5fae\u632b\u6298\uff0c\u4e0d\u7528 \u8001\u653e\u5728\u5fc3\u4e0a\uff01\u90a3\u2018\u957f\u9aef\u8001\u4eba\u2019\u6b66\u529f\u5185\u529b\uff0c\u867d\u6bd4\u8d44\u5f1f\u7565\u9ad8\u534a\u7b79\uff0c\u4f46\u8d24\u5f1f\u8fd8\u5e74\u8f7b\uff0c\u53ea\u8981\u52a0\u7d27\u7528 \u529f\uff0c\u5047\u4ee5\u65f6\u65e5\uff0c\u5fc5\u53ef\u51cc\u9a7e\u5176\u4e0a\uff0c\u90a3\u65f6\uff0c\u8d24\u5f1f\u4e0d\u4f46\u7a33\u53ef\u6218\u80dc\u957f\u9aef\u8001\u4eba\uff0c\u5c31\u662f\u5929\u4e0b\u6b66\u6797\uff0c\u6050\u6015\u4e5f \u8981\u4ee5\u8d37\u5f1f\u4e3a\u5c0a\u4e86\uff01\u54c8\u54c8\u300f\u2026\u2026\u201d \u8bf4\u7f62\uff0c\u8fd9\u6ee1\u9762\u98ce\u5c18\u7684\u8001\u9556\u5934\u8fd8\u6253\u4e86\u4e00\u4e2a\u54c8\u54c8\u3002 \u5c55\u767d\u80fd\u4f53\u4f1a\u51fa\u8339\u8001\u9556\u5934\u7684\u8bdd\u4e2d\u4e4b\u610f\u662f\u5728\u5b89\u6170\u4ed6\u3002\u4f46\u5728\u6b64\u60c5\u5f62\u4e0b\uff0c\u522b\u4eba\u8d8a\u5b89\u6170\u4ed6\uff0c\u4ed6\u6108\u89c9 \u60ed\u6127\uff0c\u56e0\u4e3a\u4ee5\u524d\u6709\u4eba\u8bf4\u8fc7\u201c\u4efb\u4f55\u4e1c\u897f\u4e5f\u4ee3\u8868\u4e0d\u4e86\u80dc\u5229\u201d\uff0c\u6240\u4ee5\u624d\u6709\u201c\u6210\u8005\u738b\u4faf\u8d25\u8005\u8d3c\u201d\u7684\u8bf4 \u6cd5\u3002\u5c55\u767d\u542c\u8339\u8001\u9556\u5934\u8bf4\u5b8c\uff0c\u4e00\u62f1\u624b\u9053\uff1a\u201c\u8c22\u8c22\u8001\u54e5\u54e5\uff01\u8fd9\u90fd\u6028\u5c0f\u5f1f\u65e0\u80fd\u2026\u2026\u201d \u201c\u592a\u767d\u53cc\u9038\u201d\u7684\u5927\u9038\u201c\u6d3b\u6b7b\u4eba\uff0c\u5446\u677f\u7684\u8138\u4e0a\u4e00\u9635\u6fc0\u52a8\uff0c\u62a2\u7740\u9053\uff1a\u201c\u5c0f\u6069\u516c1\u4f55\u5fc5\u8bf4\u8fd9\u79cd \u81ea\u9981\u7684\u8bdd\uff1f\u8981\u8bf4\u5c0f\u6069\u516c\u65e0\u80fd\uff0c\u90a3\u6211\u4eec\u8fd9\u4e9b\u8001\u4e0d\u6b7b\u7684\uff0c\u4e0d\u90fd\u6210\u4e86\u996d\u6876\u4e86\u5417\uff1f \u4e8c\u9038\u201c\u6b7b\u6d3b\u4eba\u201d\u6b7b\u4eba\u773c\u4e00\u7ffb\uff0c\u4e5f\u62a2\u7740\u9053\uff1a\u201c\u5e76\u4e0d\u662f\u5c0f\u6069\u516c\u6b66\u529f\u5dee\uff0c\u5c0f\u6069\u516c\u8fde\u6218\u4e09\u4eba\uff0c\u5403 \u4e86\u654c\u4f17\u6211\u5be1\u7684\u4e8f\uff0c\u5176\u5b9e\u8981\u662f\u4e00\u6253\u4e00\uff0c\u6211\u2018\u6b7b\u6d3b\u4eba\u2019\u6562\u6253\u8d4c\uff0c\u90a3\u957f\u9aef\u8001\u4eba\u51b3\u4e0d\u662f\u5c0f\u6069\u516c\u7684\u5bf9 \u624b\uff01\u201d \u5c55\u767d\u62f1\u624b\u9053\uff1a\u591a\u8c22\u8bf8\u4f4d\u524d\u8f88\u7684\u5938\u5956\uff0c\u4f46\u5c55\u767d\u6709\u8a00\u5728\u5148\uff0c\u65e2\u662f\u8f93\u7ed9\u4eba\u5bb6\u4e00\u638c\uff0c\u5f53\u7136\u53ea\u6709\u9053 \u5b88\u8bfa\u8a00\uff0c\u9000\u51fa\u6c5f\u6e561\u201d\u96f7\u5927\u53d4\u5728\u4e00\u8fb9\u9eef\u7136\u4e00\u53f9\uff0c\u9053\uff1a\u201c\u5c55\u7684\u7236\u4eb2\uff0c\u4e00\u6a21\u4e00\u6837\u7684\u813e\u6c14\uff0c\u4e00\u8a00\u65e2 \u51fa\uff0c\u81f3\u6b7b\u4e0d\u53d8\uff01\u201d \u8339\u8001\u9556\u5934\u6025\u9053\uff1a\u201c\u65e0\u8bba\u5982\u4f55\uff0c\u5c55\u8d24\u5f1f\u4e0d\u80fd\u9000\u51fa\u6c5f\u6e56\uff0c\u5982\u4eca\u2018\u5357\u6d77\u95e8\u2019\u5165\u4fb5\u4e2d\u539f\uff0c\u9ad8\u624b\u751a \u591a\uff0c\u6b66\u529f\u81ea\u8d70\u8e6c\u5f84\uff0c\u4e14\u6740\u9664\u5f02\u5df1\uff0c\u624b\u6bb5\u6bd2\u8fa3\uff0c\u653e\u773c\u4e2d\u539f\u6b66\u6797\uff0c\u9664\u4e86\u5c55\u8d24\u5f1f\u4e4b\u5916\uff0c\u5b9e\u5728\u627e\u4e0d\u51fa \u51e0\u4f4d\u53ef\u8d44\u4e0e\u2018\u5357\u6d77\u95e8\u2019\u9ad8\u624b\u5bf9\u654c\u4e4b\u4eba\uff0c\u5982\u679c\u5c55\u8d24\u5f1f\u518d\u9000\u51fa\u6b66\u6797\uff0c\u53ef\u8bf4\u6b63\u4e2d\u4e86\u2018\u5357\u6d77\u95e8\u2019\u7684\u5978 \u8ba1\uff0c\u66f4\u800d\u8086\u65e0\u5fcc\u60ee\u4e86\uff01\u90a3\uff0c\u4e2d\u539f\u6b66\u6797\u5c82\u4e0d\u8981\u53d8\u6210\u5c38\u5c71\u8840\u6d771\u201d\u201c\u592a\u767d\u53cc\u9038\u201d\u5dee\u4e0d\u591a\u540c\u65f6\u8bf4 \u9053\uff1a\u201c\u8339\u8001\u9556\u5934\u8bf4\u5f97\u5bf9\uff01\u5c0f\u6069\u516c\uff01\u5bf9\u8fd9\u4e9b\u51f6\u66b4\u6b8b\u5fcd\u7684\u6d77\u5916\u95e8\u6d3e\uff0c\u6211\u4eec\u4e0d\u80fd\u8ddf\u4ed6\u4eec\u8bb2\u6c5f\u6e56\u9053 \u4e49\uff0c\u7a7a\u8a00\u7ea6\u675f\u2026\u2026\u201d \u96f7\u5927\u53d4\u6253\u65ad\u201c\u592a\u767d\u53cc\u9038\u201d\u7684\u8bdd\uff0c\u9053\uff1a\u201c\u8bdd\u4e0d\u662f\u8fd9\u4e48\u8bf4\uff01\u6211\u4eec\u7acb\u8eab\u6c5f\u6e56\uff0c\u4ee5\u4fa0\u4e49\u81ea\u5c45\uff0c\u5fc5 \u5b9a\u8981\u8bda\u800c\u6709\u4fe1\uff0c\u8bf4\u51fa\u7684\u8bdd\uff0c\u51b3\u4e0d\u80fd\u4e0d\u7b97\u3002\u8981\u4e0d\u7136\u6211\u4eec\u8fd8\u79f0\u5f97\u4ec0\u4e48\u82f1\u96c4\uff1f\u5c82\u4e0d\u662f\u8ddf\u90a3\u4e9b\u9669\u8bc8\u65e0 \u4fe1\u7684\u5c0f\u4eba\u4e00\u6837\u4e86\u5417\uff1f\u201d \u96f7\u5927\u53d4\u8fd9\u51e0\u53e5\u8bdd\uff0c\u5c55\u767d\u542c\u5f97\u70b9\u5934\u94a6\u4f69\uff0c\u4f17\u4eba\u4e5f\u65e0\u4e0d\u5fc3\u670d\uff0c\u96f7\u5927\u53d4\u987f\u4e86\u4e00\u987f\uff0c\u63a5\u4f4f\u8bf4\u9053\uff1a \u201c\u8fd9\u4e8b\u8981\u4ece\u957f\u8ba1\u8bae\uff0c\u8d70\uff01\u6211\u4eec\u5148\u56de\u2018\u8c79\u7a81\u5c71\u5e84\u2019\u518d\u8bf4\u3002\u201d \u8bf4\u7f62\u56de\u5934\u5c31\u8d70\uff01 \u8fd9\u4fbf\u662f\u96f7\u5927\u53d4\u8c6a\u723d\u8fc7\u4eba\u7684\u5730\u65b9\uff0c\u8bf4\u884c\u4fbf\u8d70\uff0c\u51b3\u65e0\u865a\u4f2a\u5ba2\u5957\u3002 \u6155\u5bb9\u7ea2\u542c\u8bf4\u56de\u201c\u8c79\u7a81\u5c71\u5e84\u201d\uff0c\u5fc3\u82b1\u6012\u653e\uff0c\u671b\u7740\u5c55\u767d\u5ae3\u7136\u4e00\u7b11\u9053\uff1a\u201c\u6211\u4eec\u8d70\u5427\uff01\u201d \u5c55\u767d\u89c9\u5f97\u96f7\u5927\u53d4\u8bf4\u5f97\u4e49\u6b63\u8bcd\u4e25\uff0c\u65e0\u6cd5\u62d2\u7edd\uff0c\u4fbf\u8d81\u7740\u6155\u5bb9\u7ea2\u53eb\u4ed6\uff0c\u8fc8\u6b65\u8d70\u53bb\uff0c\u4f46\u968f\u53e3\u95ee \u9053\uff1a\u201c\u5a49\u513f\u5462\uff1f\u201d \u6155\u5bb9\u7ea2\u7b11\u9053\uff1a\u201c\u5979\u4e00\u4e2a\u4eba\u5148\u56de\u5bb6\u4e86\uff01\u201d \u5c55\u767d\u4e0d\u81ea\u957f\u53f9\u4e00\u58f0\u3002 \u6155\u5bb9\u7ea2\u5173\u6000\u5730\u671b\u4e86\u5c55\u767d\u4e00\u773c\uff0c\u4ee5\u4e3a\u5c55\u767d\u88ab\u957f\u9aef\u8001\u4eba\u638c\u9707\u5185\u4f24\u672a\u6108\uff0c\u5173\u5fc3\u5730\u95ee\u5230\uff1a\u201c\u5c55\u54e5 \u54e5\uff0c\u4f60\u6709\u4ec0\u4e48\u4e0d\u5bf9\u5417\uff1f\u201d \u201c\u6ca1\u4ec0\u4e48\uff0c\u6ca1\u4ec0\u4e48\u2026\u2026\u201d\u3002 \u5c55\u767d\u8fde\u5fd9\u5426\u8ba4\uff0c\u5176\u5b9e\u4ee5\u5c55\u767d\u76ee\u524d\u7684\u529f\u529b\u8bf4\uff0c\u4e9b\u5fae\u638c\u4f24\uff0c\u7565\u4ee5\u8c03\u606f\uff0c\u5373\u5df2\u6062\u590d\uff0c\u4ed6\u7684\u5fc3\u60c5 \u7d0a\u4e71\u662f\u56e0\u4e3a\u5a49\u513f\u6697\u604b\u7740\u4ed6\uff0c\u4f46\u8fd9\u53eb\u4ed6\u600e\u4e48\u8ddf\u6155\u5bb9\u7ea2\u8bf4\u5462\uff1f \u4e00\u884c\u4eba\u90fd\u662f\u6b66\u6797\u5065\u8005\uff0c\u811a\u7a0b\u751a\u5feb\uff0c\u4e2d\u5348\u65f6\u5206\uff0c\u5df2\u8d76\u5230\u82cf\u7696\u4ea4\u754c\u7684\u5174\u9686\u9547\u3002 \u5c55\u767d\u60f3\u8d77\u6570\u6708\u4e4b\u524d\uff0c\u5728\u6b64\u6295\u5e97\u53d7\u62d2\u7684\u60c5\u5f62\uff0c\u5411\u7ec8\u4eba\u9053\uff1a\u201c\u6b64\u5904\u662f\u2018\u5b89\u4e50\u516c\u5b50\u2019\u95e8\u4e0b\uff0c \u2018\u8840\u638c\u706b\u9f99\u2019\u59da\u51b0\u6606\u9a7b\u624e\uff0c\u2018\u8840\u638c\u706b\u9f99\u2019\u6b7b\u5728\u2018\u6d77\u5916\u4e09\u715e\u2019\u624b\u4e2d\uff0c\u73b0\u5728\u4e0d\u77e5\u9053\u8fd8\u6709\u4eba\u9a7b\u5b88\u6ca1 \u6709\uff1f\u201d \u8339\u8001\u9556\u5934\u60ca\u5230\uff1a\u201c\u8840\u638c\u706b\u9f99\u59da\u70b3\u6606\uff0c\u2018\u7ea2\u7802\u8840\u5f62\u638c\u2019\u7ec3\u6709\u5341\u6210\u706b\u5019\uff0c\u4e00\u67c4\u2018\u4ed9\u4eba\u638c\u2019\u5916 \u95e8\u5175\u5668\uff0c\u66f4\u662f\u6253\u904d\u82cf\u9c81\u65e0\u654c\u624b\uff0c\u5c24\u5176\u662f\u706b\u836f\u5389\u5bb3\uff0c\u600e\u4e48\uff1f\u4e5f\u6b7b\u5728\u2018\u6d77\u5916\u4e09\u715e\u2019\u624b\u4e2d\u4e86\uff1f\u201d \u96f7\u5927\u53d4\u6f2b\u4e0d\u7ecf\u5fc3\u5730\u9053\uff1a\u201c\u59da\u70b3\u6606\u53ea\u662f\u706b\u836f\u6697\u5668\u9738\u9053\uff0c\u5176\u4ed6\u6b66\u529f\u5e73\u5e73\uff0c\u4e0d\u8fc7\u5374\u9a84\u50b2\u5f97\u7d27\uff0c \u6b64\u4eba\u6211\u65e9\u601d\u4e00\u4f1a\uff0c\u53ef\u60dc\u73b0\u5728\u4f1a\u4e0d\u5230\u4e861\u201d\u8a00\u4e0b\uff0c\u8c6a\u6c14\u4e0d\u51cf\u5f53\u5e741\u4f17\u4eba\u8c08\u8c08\u8bf4\u8bf4\u8fdb\u5f97\u9547\u6765\uff0c\u53ea \u89c1\u8857\u4e0a\u5230\u5904\u662f\u8eab\u62ab\u9ebb\u888b\u7684\u53eb\u5316\u5b50\u3002 \u8fd9\u4e9b\u53eb\u5316\uff0c\u5747\u624b\u63d0\u6253\u72d7\u68d2\uff0c\u884c\u8272\u5306\u5306\uff0c\u90fd\u6cbf\u8857\u5411\u7740\u4e00\u4e2a\u65b9\u5411\u884c\u53bb\uff0c\u5c31\u662f\u9760\u7740\u5e97\u5bb6\u95e8\u53e3\u7ad9 \u7740\u7684\u53eb\u5316\uff0c\u4e5f\u4e0d\u5411\u5e97\u5bb6\u7d22\u8ba8\uff0c\u9047\u7740\u8def\u8fc7\u7684\u53eb\u5316\uff0c\u4e00\u65bd\u773c\u8272\uff0c\u4fbf\u7acb\u5373\u8ddf\u968f\u7740\u8d70\u53bb1\u4f17\u4eba\u4e4b\u4e2d\uff0c \u591a\u534a\u662f\u8001\u6c5f\u6e56\uff0c\u4e00\u770b\u5230\u8fd9\u4e9b\u53eb\u5316\u7684\u60c5\u5f62\uff0c\u4fbf\u90fd\u5639\u7136\uff0c\u5fc5\u662f\u201c\u7a77\u5bb6\u5e2e\u201d\u7684\u4eba\u7269\u5728\u6b64\u9547\u6709\u4ec0\u4e48\u96c6 \u4f1a\uff0c\u56e0\u6b64\u6beb\u4e0d\u4e3a\u7136\uff0c\u7167\u65e7\u5411\u9547\u4e2d\u8d70\u53bb\u3002 \u4f46\u6155\u5bb9\u7ea2\u4e43\u662f\u8c6a\u95e8\u5343\u91d1\uff0c\u5e73\u5e38\u8fde\u5927\u95e8\u90fd\u672a\u8d70\u51fa\u4e00\u6b65\uff0c\u89c1\u5230\u8fd9\u6837\u7684\u60c5\u5f62\uff0c\u4e0d\u7531\u5947\u9053\uff1a\u600e\u4e48 \u8fd9\u9547\u4e0a\u8fd9\u4e48\u591a\u53eb\u5316\u5b50\uff1f\u201d \u8339\u8001\u9556\u5934\u4f4e\u58f0\u9053\uff1a\u201c\u59d1\u5a18\uff01\u4e0d\u8981\u591a\u7ba1\u95f9\u4e8b\uff0c\u4ed6\u4eec\u90fd\u662f\u2018\u7a77\u5bb6\u5e2e\u2019\u7684\u4eba\uff01\u201d \u201c\u7a77\u5bb6\u201d\u8fd8\u6709\u201c\u5e2e\u201d\uff1f\u6155\u5bb9\u7ea2\u5fc3\u4e2d\u66f4\u5947\uff0c\u4f46\u5979\u5374\u6ca1\u6709\u518d\u95ee\uff0c\u53ea\u7741\u5927\u4e86\u5145\u6ee1\u597d\u5947\u7684\u773c\u775b\uff0c \u6ce8\u89c6\u7740\u90a3\u4e9b\u53eb\u5316\u7684\u884c\u6b62\u3002 \u8fd9\u4e9b\u53eb\u5316\u8eab\u4e0a\u7684\u8863\u670d\u867d\u7136\u7834\u7834\u70c2\u70c2\uff0c\u4f46\u4e00\u4e2a\u4e2a\u8eab\u8eaf\u5f6a\u58ee\uff0c\u5065\u6b65\u5982\u98de\uff0c\u8138\u4e0a\u66f4\u662f\u4e00\u8138\u7684\u527d \u608d\u4e4b\u8272\uff0c\u773c\u775b\u9f13\u7740\uff0c\u592a\u9633\u7a74\u9971\u6ee1\uff0c\u7cbe\u6c14\u795e\u5145\u8db3\uff0c\u770b\u6837\u5b50\u4fbf\u77e5\u9053\u90fd\u662f\u8eab\u6000\u9ad8\u5f3a\u6b66\u529f\u7684\u7ec3\u5bb6\u5b50\u3002 \u6bcf\u4e2a\u53eb\u5316\u7684\u80a9\u4e0a\u90fd\u642d\u7740\u9ebb\u888b\uff0c\u4e09\u56db\u6761\u4e0d\u7b49\uff0c\u6709\u591a\u8fbe\u4e03\u6761\u7684\uff0c\u6700\u5c11\u4e5f\u6709\u4e24\u6761\u3002\u624b\u4e2d\u62ff\u7684\u6253 \u72d7\u68d2\u4e5f\u5404\u4e0d\u76f8\u540c\uff0c\u6709\u9752\u7af9\uff0c\u6709\u9ec4\u7af9\uff0c\u8fd8\u6709\u7eff\u7af9\u3002\u6155\u5bb9\u7ea2\u4e00\u4e2a\u672b\u51fa\u8fc7\u95fa\u95e8\u7684\u5343\u91d1\u5c0f\u59d0\u5f53\u7136\u4e0d\u77e5 \u9053\uff0c\u8fd9\u53eb\u5316\u8eab\u4e0a\u7684\u9ebb\u888b\u53ca\u624b\u4e2d\u7684\u6253\u72d7\u68d2\u6709\u4f55\u610f\u4e49\uff1f\u4f46\u96f7\u5927\u53d4\u3001\u8339\u8001\u9556\u5934\u3001\u201c\u592a\u767d\u53cc\u9038\u201d\u7b49\u4eba \u5374\u4e86\u7136\u4e8e\u80f8\uff0c\u77e5\u9053\u8fd9\u4e9b\u9ebb\u888b\u7684\u591a\u5be1\u53ca\u6253\u72d7\u68d2\u7684\u989c\u8272\uff0c\u662f\u4ee3\u8868\u5728\u201c\u7a77\u5bb6\u5e2e\u201d\u5185\u7684\u8eab\u4efd\u5730\u4f4d\u7684\u3002 \u53ef\u662f\uff0c\u8fd9\u4e9b\u60c5\u5f62\u5c31\u662f\u8339\u8001\u8198\u5934\u8fd9\u7b49\u8001\u6c5f\u6e56\uff0c\u770b\u4e86\u4e5f\u775b\u6697\u7eb3\u95f7\uff0c\u56e0\u4e3a\u80cc\u4e03\u6761\u9ebb\u888b\u4ee5\u4e0a\u7684\u53eb \u5316\uff0c\u90fd\u662f\u201c\u7a77\u5bb6\u5e2e\u201d\u957f\u8001\u5730\u4f4d\u7684\u9876\u5c16\u9ad8\u624b\uff0c\u5c24\u5176\u624b\u62ff\u9752\u7af9\u6756\u7684\u90a3\u4e48\u591a\uff0c\u66f4\u77e5\u201c\u7a77\u5bb6\u5e2e\u201d\u9ad8\u624b \u805a\u96c6\u5728\u6b64\u9547\u4e0a\u7684\u5b9e\u5728\u4e0d\u5c11\uff01 \u518d\u5f80\u524d\u8d70\uff0c\u53eb\u5316\u6108\u591a\uff0c\u4e09\u4e2a\u4e00\u7fa4\uff0c\u4e94\u4e2a\u4e00\u4f19\uff0c\u90fd\u5728\u4e00\u4e2a\u5341\u5b57\u8def\u53e3\u5904\uff0c\u8f6c\u5165\u4e00\u6761\u957f\u5df7\u4e2d\u3002 \u5c55\u767d\u5b9e\u5728\u5fcd\u4e0d\u4f4f\u597d\u5947\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u7a77\u5bb6\u5e2e\u2019\u5411\u6765\u53d7\u2018\u7aef\u65b9\u516c\u5b50\u2019\u8282\u5236\uff0c\u600e\u4e48\u4eca\u5929\u90fd\u8dd1\u5230\u2018\u5b89 \u4e50\u516c\u5b50\u2019\u6240\u8f96\u7684\u5730\u9762\u6765\u4e86\uff1f\u83ab\u975e\u6709\u4ec0\u4e48\u4e8b\u6545\u53d1\u751f\u4e0d\u6210\uff1f\u201d \u96f7\u5927\u53d4\u70b9\u4e86\u70b9\u5934\uff0c\u9053\uff1a\u8d24\u4f84\u6240\u6599\u4e0d\u5dee\uff0c\u8d70\uff01\u54b1\u4eec\u770b\u770b\u53bb1\u201d\u8bf4\u7f62\uff0c\u4e5f\u968f\u7740\u90a3\u4e9b\u53eb\u5316\u8d70\u8fdb \u957f\u5df7\u4e2d\u53bb\u3002 \u96f7\u5927\u53d4\u8c6a\u8fc8\u7edd\u4f26\uff0c\u8339\u8001\u9556\u5934\u3001\u201c\u592a\u767d\u53cc\u9038\u201d\u867d\u7136\u4e0d\u613f\u591a\u4e8b\uff0c\u53ea\u6709\u8ddf\u7740\u8d70\u53bb\uff0c\u81f3\u4e8e\u5c55\u767d\u4e0e \u6155\u5bb9\u7ea2\u4e2d\u8f7b\u597d\u5947\uff0c\u66f4\u662f\u6b32\u89c2\u7a76\u7adf\uff0c\u6beb\u4e0d\u7591\u8fdf\u5730\u8ddf\u4e86\u8fdb\u53bb\u3002 \u8fd9\u6761\u957f\u5df7\uff0c\u8bf4\u957f\u662f\u771f\u957f\uff0c\u8d70\u4e86\u4e09\u4e94\u5341\u4e08\u8fdb\u53bb\uff0c\u4ecd\u7136\u6df1\u4e0d\u89c1\u5e95\u3002 \u4f17\u4eba\u6b63\u5f80\u524d\u8d70\uff0c\u7a81\u7136\u53c9\u8def\u91cc\u95ea\u51fa\u4e09\u540d\u53eb\u5316\uff0c\u5404\u81ea\u4e00\u6a2a\u624b\u4e2d\u6253\u72d7\u68d2\uff0c\u963b\u4f50\u53bb\u8def\u3002\u5f53\u4e2d\u4e00\u4e2a \u9e3d\u8863\u767e\u7ed3\u3001\u80a9\u80cc\u56db\u6761\u9ebb\u888b\u7684\u53eb\u5316\uff0c\u9886\u5934\u53eb\u9053\uff1a\u201c\u7ad9\u4f4f\uff01\u770b\u8bf8\u4f4d\u6709\u94b1\u7684\u7237\u4eec\uff0c\u4e5f\u662f\u6c5f\u6e56\u4e0a\u7684\u6df7 \u6df7\u513f\uff0c\u96be\u9053\u770b\u4e0d\u51fa\u524d\u9762\u662f\u7a77\u4eba\u96c6\u4f1a\u7684\u5730\u65b9\u5417\uff1f\u201d \u96f7\u5927\u53d4\u4ef0\u5929\u6253\u4e86\u54c8\u54c8\uff0c\u9053\uff1a\u201c\u6211\u4eec\u6b63\u662f\u7a77\u4eba\u7684\u670b\u53cb\uff0c\u524d\u6765\u89c2\u671b\u76db\u4f1a\u7684\uff01\u201d \u8fd9\u4e09\u4e2a\u53eb\u5316\u4e00\u695e\uff0c\u72e0\u72e0\u5730\u6253\u91cf\u4e86\u96f7\u5927\u53d4\u4e24\u773c\uff0c\u8138\u4e0a\u9634\u6674\u4e0d\u5b9a\uff0c\u6ee1\u662f\u7591\u95ee\u795e\u6c14\u3002 \u56e0\u4e3a\u96f7\u5927\u53d4\u4e71\u53d1\u98de\u84ec\uff0c\u989d\u4e0b\u94a2\u987b\u5982\u7316\uff0c\u867d\u7136\u8eab\u4e0a\u4e00\u88ad\u9752\u5e03\u888d\u8fd8\u7b97\u6574\u6d01\uff0c\u4f46\u5916\u5f62\u5df2\u8ddf\u201c\u7a77 \u5bb6\u5e2e\u201d\u7684\u4eba\u5dee\u4e0d\u591a\u3002 \u4e09\u4e2a\u53eb\u5316\u4e0a\u4e0b\u6253\u91cf\u4e86\u96f7\u5927\u53d4\u51e0\u773c\uff0c\u9760\u5de6\u9996\u4e00\u4e2a\u9762\u767d\u987b\u957f\u7684\u53eb\u5316\uff0c\u7a81\u7136\u51b7\u7b11\u9053\uff1a\u201c\u5149\u68cd\u773c \u91cc\u4e0d\u63c9\u7802\u5b50\uff01\u670b\u53cb\u8981\u60f3\u5728\u7a77\u4eba\u9762\u524d\u8499\u6df7\uff0c\u53ef\u8bf4\u662f\u778e\u4e86\u773c\u4e86\uff01\u7b2c\u4e00\uff0c\u4f60\u8eab\u4e0a\u4e0d\u5e26\u9636\u7ea7\uff0c\u7b2c\u4e8c\uff0c \u4f60\u624b\u4e2d\u4e0d\u62ff\u4fe1\u7269\u3002\u5c31\u51ed\u4e09\u8a00\u4e24\u8bed\u60f3\u89c1\u7956\u5e08\u7237\uff0c\u90a3\u662f\u68a6\u60f3\uff01\u201d \u8339\u8001\u9556\u5934\u5403\u4e86\u4e00\u60ca\uff0c\u5fd9\u8de8\u524d\u4e00\u6b65\uff0c\u63d2\u53e3\u9053\uff1a\u201c\u600e\u4e481\u2018\u7a77\u5bb6\u5e2e\u2019\u5728\u8fd9\u91cc\u6446\u9999\u5802\u5417T\u91cc\u4e09 \u4e2a\u53eb\u5316\u53ea\u662f\u4e00\u5473\u51b7\u7b11\uff0c\u5e76\u4e0d\u7b54\u8a00\u3002 \u96f7\u5927\u53d4\u6c14\u5f80\u4e0a\u51b2\uff0c\u9053\uff1a\u201c\u8001\u592b\u96f7\u9707\u8fdc\uff0c\u8fde\u4f60\u4eec\u5e2e\u4e2d\u957f\u8001\u2018\u98ce\u5c18\u4e09\u4e10\u2019\uff0c\u5bf9\u8001\u592b\u90fd\u4e0d\u6562\u65e0 \u793c\uff0c\u4f60\u4eec\u51e0\u4e2a\u665a\u751f\u540e\u8f88\uff0c\u7ade\u5982\u6b64\u5f20\u72c2\uff0c\u5b9e\u5728\u53ef\u607c1\u201d\u8bf4\u7f62\uff0c\u5927\u6b65\u95ef\u4e86\u8fc7\u53bb\uff01 \u4e09\u4e2a\u53eb\u5316\u66b4\u53f1\u4e00\u58f0\uff0c\u4e09\u6839\u6253\u72d7\u68d2\u4e00\u6296\uff0c\u55e1\u7136\u52b2\u5578\uff0c\u70b9\u5411\u96f7\u5927\u53d4\u9762\u524d\uff0c\u4e24\uff0c\u4e09\u540d\u53eb\u5316\u4e09\u6839 \u72d7\u68d2\u4e00\u9f50\u51fa\u624b\uff0c\u5dee\u4e0d\u591a\u5c01\u4f4f\u4e86\u6574\u4e2a\u5df7\u53e3\uff0c\u96f7\u5927\u53d4\u8d64\u624b\u7a7a\u62f3\uff0c\u5982\u679c\u4e0d\u6025\u884c\u540e\u6643\uff0c\u5fc5\u5b9a\u4f24\u5728\u4e09\u6839 \u6253\u72d7\u68d2\u4e4b\u4e0b\uff01 \u4f46\u96f7\u5927\u53d4\u662f\u4f55\u8bb8\u4eba\u4e5f\uff0c\u201c\u4e03\u5341\u4e8c\u8def\u5929\u4f5b\u638c\u201d\uff0c\u5728\u6570\u5341\u4e2d\u524d\u5373\u5df2\u540d\u626c\u6c5f\u6e56\uff0c\u53ea\u53ef\u60dc\u8fd9\u4e09\u4e2a \u53eb\u5316\u51fa\u9053\u751a\u665a\uff0c\u96f7\u5927\u53d4\u53c8\u9690\u9041\u4e86\u5341\u6570\u5e74\uff0c\u672b\u5728\u6c5f\u6e56\u4e0a\u8d70\u52a8\u8fc7\uff0c\u6545\u800c\u62a5\u540d\u51fa\u6765\uff0c\u4e09\u4e2a\u53eb\u5316\u8fd8\u4e0d \u77e5\u9053\u773c\u524d\u4e71\u53d1\u602a\u4eba\uff0c\u5c31\u662f\u6570\u5341\u5e74\u540d\u9707\u5927\u6c5f\u5357\u5317\u7684\u201c\u5929\u4f5b\u638c\u201d\u96f7\u9707\u8fdc\uff01 \u773c\u89c1\u4e09\u6839\u6253\u72d7\u68d2\uff0c\u72b9\u5982\u72c2\u98ce\u9aa4\u96e8\uff0c\u5c06\u8981\u6253\u5728\u96f7\u5927\u53d4\u8eab\u4e0a\uff0c\u96f7\u5927\u53d4\u9661\u53f1h\u4e00\u58f0\uff0c\u201c\u9000\uff01\u201d \u5927\u8896\u4e00\u62c2\uff0c\u52b2\u98ce\u72c2\u5578\uff0c\u4e09\u540d\u53eb\u5316\u5f53\u573a\u88ab\u9707\u9000\u4e94\uff0c\u516d\u6b65\uff0c\u8fde\u624b\u4e2d\u9ec4\u7af9\u6756\u90fd\u51e0\u4e4e\u8131\u624b\u98de\u53bb\uff01 \u4e09\u540d\u53eb\u5316\u62ff\u6756\u7684\u53f3\u624b\u864e\u53e3\u53d1\u70ed\uff0c\u534a\u8fb9\u8eab\u5b50\u53d1\u9ebb\uff0c\u8179\u5185\u771f\u6c14\u6d41\u8e7f\uff0c\u4e0d\u7531\u9f50\u58f0\u60ca\u547c\u9053\uff1a\u201c\u5e76 \u80a9\u5b50\uff01\u6709\u786c\u70b9\u5b50\u8981\u95ef\u5173\uff01\u201d \u559d\u58f0\u752b\u843d\uff0c\u55d6\uff01\u55d6\uff01\u55d6\uff01\u2026\u2026\u4e00\u7247\u8863\u631f\u7834\u98ce\u4e4b\u58f0\uff0c\u4ece\u56db\u5904\u95e8\u5185\u63a5\u8fde\u8e7f\u51fa\u5341\u6570\u540d\u53eb\u5316\uff01 \u8fd9\u4e9b\u53eb\u5316\u4e4b\u4e2d\uff0c\u5df2\u7ecf\u6709\u8eab\u62ab\u4e94\u6761\u9ebb\u888b\uff0c\u624b\u62ff\u7740\u7af9\u6756\u7684\u4eba\u7269\u51fa\u73b0\u3002 \u663e\u89c1\u5df2\u6765\u4e86\u201c\u7a77\u5bb6\u5e2e\u201d\u4e2d\u7684\u4e8c\u4ee3\u9ad8\u624b\uff01 \u540c\u65f6\uff0c\u623f\u9876\u4e0a\u5f13\u5f26\u8fde\u54cd\uff0c\u4f17\u4eba\u62ac\u5934\u4e00\u770b\uff0c\u4e24\u8fb9\u5c4b\u9876\u4e0a\u5df2\u7ad9\u6ee1\u4e86\u201c\u7a77\u5bb6\u5e2e\u201d\u7684\u5e2e\u5bb6\uff0c\u6bcf\u4eba \u624b\u4e2d\u62ff\u7740\u4e00\u53ea\u5f39\u5f13\uff0c\u6015\u4e0d\u6709\u6570\u5341\u540d\u4e4b\u4f17\uff0c\u4e00\u9f50\u7784\u51c6\u4f17\u4eba\uff0c\u5f15\u5f26\u5f85\u53d1\uff01 \u4f17\u4eba\u5fc3\u4e2d\u4e00\u61d4\uff0c\u4f30\u4e0d\u5230\u201c\u7a77\u5bb6\u5e2e\u201d\u65e9\u5c31\u4e25\u9635\u4ee5\u5f85\uff0c\u4ee5\u96f7\u5927\u53d4\u7b49\u4eba\uff0c\u867d\u4e0d\u6015\u533a\u533a\u5f39\u4e38\uff0c\u4f46 \u5728\u8fd9\u72ed\u957f\u5df7\u4e2d\uff0c\u624b\u811a\u65bd\u5c55\u4e0d\u5f00\uff0c\u5982\u679c\u5c4b\u9876\u4e0a\u7684\u6570\u5341\u53ea\u5f39\u5f13\u4e00\u9f50\u53d1\u5c04\uff0c\u8fd8\u771f\u662f\u4e0d\u597d\u5e94\u4ed8\uff01 \u5c55\u767d\u6025\u53eb\u9053\uff1a\u201c\u5728\u4e0b\u5c55\u767d\uff0c\u60f3\u6765\u62dc\u8bbf\u2018\u7aef\u65b9\u516c\u5b50\u2019\uff0c\u4e0d\u77e5\u8bf8\u4f4d\u80fd\u5426\u4ee3\u4e3a\u901a\u5bdf\u4e00\u58f0\uff01\u201d \u8c01\u77e5\u4e0a\u6765\u4e00\u540d\u624b\u62ff\u9752\u7af9\u6756\u3001\u80a9\u80cc\u4e94\u6761\u9ebb\u888b\u7684\u8001\u5e74\u53eb\u5316\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u65e9\u5728\u2018\u4ea1\u9b42\u8c37\u2019\u9886\u6559 \u8fc7\u4e86\uff01\u6ca1\u522b\u7684\uff0c\u8bf7\u5404\u4f4d\u5148\u59d4\u5c48\u4e00\u4e0b\u5427\uff01\u201d \u539f\u6765\u8fd9\u540d\u53eb\u5316\uff0c\u53c2\u52a0\u8fc7\u201c\u4ea1\u9b42\u8c37\u201d\u9752\u7af9\u6756\u5927\u6218\u201c\u91d1\u5e9c\u53cc\u94c1\u536b\u201d\u4e4b\u6218\uff0c\u90a3\u65f6\uff0c\u5c55\u767d\u4e0e\u5a49\u513f \u4e5f\u9002\u9022\u5176\u4f1a\uff0c\u800c\u4e14\u4f24\u4e86\u4e0d\u5c11\u201c\u7a77\u5bb6\u5e2e\u201d\u95e8\u4e0b\u9ad8\u624b\uff0c\u6545\u800c\u8ba4\u5f97\u5c55\u767d\u3002 \u4ed6\u8bf4\u7f62\uff0c\u4ece\u9ebb\u53e3\u4e0a\u89e3\u4e0b\u51e0\u6761\u7ef3\u7d22\uff0c\u5f80\u5c55\u767d\u7b49\u4eba\u9762\u524d\u4e00\u63b7\uff0c\u8bf4\u9053\uff1a\u201c\u5c14\u7b49\u675f\u624b\u81ea\u7f1a\uff0c\u54b1\u4eec \u7a77\u4eba\u51b3\u4e0d\u96be\u4e3a\u4f60\u4eec\uff0c\u53ea\u8981\u89c1\u4e86\u7956\u5e08\u7237\uff0c\u4e00\u53e5\u8bdd\uff0c\u4fbf\u53ef\u653e\u8bf8\u4f4d\u3002\u5982\u82e5\u4e0d\u7136\uff0c\u54fc\uff01\u54fc1\u201d\u4ed6\u5728\u9f3b \u5b54\u91cc\u51b7\u54fc\u4e24\u58f0\uff0c\u7a81\u7136\u7ffb\u670d\u671b\u5411\u4e24\u65c1\u5c4b\u9876\uff0c\u5389\u58f0\u53f1\u9053\uff0c\u201c\u5c31\u8bf7\u8bf8\u4f4d\u5c1d\u4e00\u5c1d\u2018\u8089\u4e38\u5b50\u2019\u7684\u5473 \u9053\uff01\u201d\u8fd9\u5c82\u4e0d\u662f\u4ee5\u751f\u547d\u76f8\u80c1\uff1f \u5c55\u767d\u5927\u6012\uff0c\u96f7\u5927\u53d4\u5374\u8d1f\u624b\u51b7\u7b11\u9053\uff1a\u2018\u7a77\u5bb6\u5e2e\u2019\u7d20\u5e38\u4ee5\u4e0e\u4eba\u65e0\u4ef6\uff0c\u7acb\u8db3\u6c5f\u6e56\uff0c\u624d\u80fd\u5e2e\u4f17\u904d \u5929\u4e0b\uff0c\u50cf\u5c14\u7b49\u8fd9\u822c\u8000\u6b66\u626c\u5a01\uff0c\u5e03\u4e0b\u9677\u9631\u8981\u631f\u6b66\u6797\u540c\u9053\uff0c\u54fc\uff01\u54fc1\u6050\u6015\u2018\u7a77\u5bb6\u5e2e\u2019\u8981\u51b0\u6d88\u74e6\u89e3 \u5728\u5c14\u7b49\u624b\u4e2d\uff01\u201d \u90a3\u624b\u62ff\u7af9\u6756\u7684\u53eb\u5316\uff0c\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6b7b\u5728\u9762\u524d\uff0c\u8fd8\u6562\u5927\u8a00\u4e0d\u60ed\uff01\u73b0\u5728\u6211\u6570\u5230\u4e94\uff0c\u5982\u679c \u4e94\u4e0b\u6570\u5b8c\uff0c\u5404\u4f4d\u518d\u4e0d\u81ea\u7f1a\uff0c\u6211\u4fbf\u8981\u4e0b\u4ee4\u5343\u4e38\u9f50\u98de\uff01\u201d \u8bf4\u7f62\uff0c\u4ed6\u4e3e\u8d77\u624b\uff0c\u4f38\u76f4\u4e94\u4e2a\u624b\u6307\uff0c\u5148\u5f2f\u4e0b\u4e00\u53ea\u624b\u6307\uff0c\u53e3\u4e2d\u559d\u9053\uff1a\u201c\u4e00\uff01\u201d \u96f7\u5927\u53d4\u8d1f\u624b\u800c\u7acb\uff0c\u5c55\u767d\u7b49\u4eba\u4e0d\u52a8\u58f0\u8272\u3002 \u9752\u7af9\u53eb\u5316\u5f2f\u4e0b\u7b2c\u4e8c\u4e2a\u624b\u6307\uff0c\u6717\u58f0\u9053\uff1a\u201c\u4e8c1\u201d\u96f7\u5927\u53d4\u4e0d\u52a8\u5982\u5c71\uff0c\u5c55\u767d\u7b49\u4eba\u5df2\u5728\u6697\u4e2d\u8fd0\u529f \u6212\u5907\uff01\u201c\u4e09\uff01\u201d \u9752\u7af9\u53eb\u5316\u53c8\u5f2f\u4e0b\u7b2c\u4e09\u4e2a\u624b\u6307\uff0c\u201c\u7565\u556a\uff01\u54af\u556a\uff01\u201d\u4e00\u7247\u66b4\u54cd\uff0c\u5c4b\u9876\u4e0a\u6570\u5341\u53eb\u5316\u94c1\u80ce\u5f39\u5f13\u90fd \u5f15\u6ee1\u4e86\u5f26\u3002 \u201c\u56db\uff01\u201d\u2606\u56db\u5b57\u51fa\u53e3\uff0c\u5c4b\u9876\u6570\u5341\u5f39\u5f13\uff0c\u90fd\u5f2f\u5934\u5411\u4e0b\uff0c\u7784\u51c6\u4e86\u4f17\u4eba\uff01 \u96f7\u5927\u53d4\u8339\u8001\u9556\u5934\u3001\u201c\u592a\u767d\u53cc\u9038\u201d\uff0c\u5df2\u7d27\u5f20\u5f97\u624b\u5fc3\u89c1\u6c57\uff0c\u7981\u4e0d\u4f4f\u8981\u51fa\u624b\u76f8\u640f\uff01\u2026\u2026 \u7ad9\u5f97\u8f83\u8fdc\u7684\u90a3\u4e9b\u201c\u7a77\u5bb6\u5e2e\u201d\u4f17\uff0c\u4e5f\u90fd\u7d27\u5f20\u5730\u671b\u5b9a\u4f17\u4eba\uff01 \u5927\u6218\u4e00\u89e6\u5373\u53d1\uff01 \u53ea\u8981\u90a3\u9752\u7af9\u53eb\u5316\u201c\u4e94\u201d\u5b57\u4e00\u51fa\u53e3\uff0c\u4e00\u573a\u8840\u6218\u4fbf\u8981\u7acb\u523b\u5c55\u5f00\u300f\u201c\u4f4f\u624b\uff01\u201d \u7a81\u7136\u4e00\u58f0\u66b4\u559d\uff0c\u98de\u5feb\u63a0\u6765\u4e09\u6761\u4eba\u5f71\uff01 \u7b2c\u4e94\u5341\u4e09\u7ae0\u3000\u53eb\u5316\u5927\u5bb4>> \u53e4\u9f99\u300a\u5251\u5ba2\u884c\u300b \u7b2c\u4e94\u5341\u4e09\u7ae0\u3000\u53eb\u5316\u5927\u5bb4 \u5f71\u5b9a\u4eba\u73b0\uff0c\u539f\u6765\u662f\u4e09\u4e2a\u5e74\u5c4a\u82b1\u7532\u7684\u8001\u53eb\u5316\u3002\u3001\u6b63\u662f\u201c\u7a77\u5bb6\u5e2e\u201d\u4e09\u5927\u957f\u8001\uff0c\u201c\u98ce\u5c18\u4e09 \u4e10\uff01\u201d \u201c\u75af\u4e10\u201d\u891a\u826f\u4e00\u770b\u624b\u4e0b\u5e2e\u5bb6\uff0c\u5f13\u4e0a\u5f26\uff0c\u5200\u51fa\u9798\uff0c\u5168\u795e\u6212\u5907\u8457\uff0c\u5389\u58f0\u545b\u9053\uff1a\u201c\u8fd8\u4e0d\u9000\u4e0b\uff01 \u5c31\u51ed\u4f60\u4eec\u8fd9\u4e9b\u8eab\u624b\uff0c\u80fd\u6321\u5f97\u4f4f\u5f53\u5e74\u540d\u6ee1\u5929\u4e0b\u7684\u2018\u5929\u4f5b\u638c\u2019\u96f7\u9707\u8fdc\u96f7\u5927\u4fa0\u5417\uff01\u201d \u5df7\u5185\u5e2e\u4f17\uff0c\u8fde\u623f\u9876\u4e0a\u7684\u5f13\u9a7d\u624b\uff0c\u4e00\u9f50\u552f\u552f\u606d\u8eab\u9000\u4e0b\u3002 \u201c\u75af\u4e10\u201d\u891a\u826f\u53c8\u5411\u96f7\u5927\u53d4\u9053\uff1a\u201c\u5c5e\u4e0b\u4e0d\u8ba4\u8bc6\u4f60\uff0c\u591a\u6709\u5192\u72af\uff0c\u770b\u5728\u6211\u4eec\u4e09\u4e2a\u8001\u53eb\u5316\u7684\u4efd \u4e0a\uff0c\u8bf7\u591a\u539f\u8c05\uff01\u201d \u96f7\u5927\u53d4\u6536\u53bb\u72c2\u50b2\u4e4b\u6001\uff0c\u9762\u5bb9\u4e00\u6574\uff0c\u9053\uff1a\u201c\u4ee5\u6211\u4eec\u6570\u5341\u5e74\u7684\u4ea4\u60c5\u6765\u8bf4\uff0c\u8fd8\u7528\u5f97\u7740\u5ba2\u6c14\u5417\uff1f \u53ef\u662f\uff0c\u4f60\u4eec\u2018\u7a77\u5bb6\u5e2e\u2019\u56fa\u7136\u662f\u884c\u904d\u5929\u4e0b\uff0c\u5403\u904d\u5929\u4e0b\uff0c\u600e\u4e48\u4f1a\u9c8d\u5230\u8fd9\u9547\u4e0a\u6765\u6446\u9999\u5802\u5462\uff1f\u201d \u201c\u804b\u4e10\u201d\u53cc\u8033\u5931\u806a\uff0c\u542c\u4e0d\u5230\u522b\u4eba\u8bf4\u8bdd\uff0c\u4ed6\u81ea\u5df1\u4e5f\u4e0d\u4f1a\u8bb2\u8bdd\uff0c\u6839\u672c\u4e0d\u5f00\u53e3\uff0c\u201c\u9152\u4e10\u201d\u4f3c\u662f \u5bbf\u9152\u672a\u9192\uff0c\u4e5f\u5f88\u5c11\u8bf4\u8bdd\u3002\u4ecd\u7531\u201c\u75af\u4e10\u201d\u7b54\u9053\uff1a\u201c\u8bf4\u8d77\u6765\u8bdd\u957f\uff0c\u6b64\u5730\u4e0d\u662f\u8c08\u8bdd\u4e4b\u6240\uff0c\u8d70\uff0c\u5230\u91cc \u8fb9\u53bb\u518d\u8be6\u8c08\uff01\u201d \u96f7\u5927\u53d4\u53c8\u7b11\u9053\uff1a\u201c\u6211\u4eec\u51e0\u4eba\uff0c\u884c\u8272\u4ed3\u4fc3\uff0c\u8fd8\u6ca1\u6709\u5403\u5348\u996d\uff0c\u4f60\u5148\u60f3\u60f3\u770b\uff0c\u4f60\u4eec\u2018\u7a77\u5bb6\u5e2e\u2019 \u5411\u6765\u5403\u4f38\u624b\u996d\uff0c\u6240\u8c13\u2018\u72d7\u53fc\u6765\u4e0d\u5582\u72fc\u2019\uff0c\u4f60\u4eec\u4f1a\u8bf7\u6211\u4eec\u8fd9\u4e00\u7fa4\u4e0d\u901f\u4e4b\u5ba2\u5403\u996d\u5417\uff1f\u6211\u770b\u8fd8\u662f\u7b49 \u6211\u4eec\u5728\u9152\u697c\u5403\u7f62\u996d\u518d\u6765\u62dc\u8bbf\u5427\uff01\u201d \u8bf4\u7f62\u4e00\u62f1\u624b\uff0c\u8f6c\u8eab\u5c31\u8d70\u3002 \u201c\u75af\u4e10\u201d\u4e00\u77aa\u773c\uff0c\u4f38\u624b\u62c9\u4f4f\u96f7\u5927\u53d9\u624b\u81c2\uff0c\u9053\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\u8bdd\uff1f\u6211\u4eec\u4e00\u7fa4\u8981\u996d\u7684\u867d\u7a77\uff0c\u8fd8 \u8bf7\u5f97\u8d77\u4f60\u4eec\u51e0\u4eba\u4e00\u987f\u4fbf\u996d\uff0c\u8d70\uff01\u518d\u8981\u63a8\u8f9e\u53ef\u5c31\u4e0d\u591f\u670b\u53cb\u5566\uff01\u201d \u201c\u9152\u4e10\u201d\u65b9\u7ca5\u9152\u6c14\u55b7\u4eba\uff0c\u626c\u58f0\u5927\u7b11\u9053\uff1a\u201c\u597d\u4e2a\u96f7\u75af\u5b50\uff01\u2018\u72fc\u53fc\u6765\u4e0d\u5582\u72d7\u2019\uff0c\u4f60\u7ed9\u6211\u4eec\u6539 \u4e3a\u2018\u72d7\u53fc\u6765\u4e0d\u5582\u72fc\u2019\uff0c\u628a\u6211\u4eec\u7a77\u8981\u996d\u7684\u90fd\u9a82\u4e0a\u4e86\uff01\u201d \u8bf4\u7740\u4ece\u80cc\u540e\u89e3\u4e0b\u4e00\u4e2a\u7ea2\u6f06\u5927\u846b\u82a6\u6765\uff0c\u9ad8\u4e3e\u5728\u624b\u4e2d\uff0c\u9053\uff1a\u201c\u522b\u770b\u8001\u53eb\u5316\u7a77\uff0c\u8eab\u4e0a\u4ece\u6765\u4e0d\u65ad \u4e0a\u597d\u7f8e\u9152\uff01\u558f\uff01\u8fd9\u846b\u82a6\u4e2d\u8fd8\u6709\u5341\u65a4\u2018\u8d35\u5dde\u8305\u2019\uff0c\u8001\u53eb\u5316\u8ddf\u4f60\u96f7\u75af\u5b50\u62fc\u4e09\u767e\u676f\uff01\u2026\u2026\u201d \u8bf4\u7740\u53c8\u62d4\u5f00\u585e\u5b50\uff0c\u5728\u4f17\u4eba\u9762\u524d\u6643\u4e86\u6643\uff0c\u53ea\u89c9\u9152\u9999\u6251\u9f3b\uff0c\u5f53\u771f\u662f\u5e02\u9762\u4e0a\u96be\u5f97\u4e00\u89c1\u7684\u597d\u9152\u3002 \u4f46\u5f53\u4ed6\u5728\u4f17\u4eba\u9762\u524d\u6643\u8fc7\uff0c\u53c8\u62ff\u5728\u81ea\u5df2\u9f3b\u7aef\u53bb\u95fb\u9152\u5473\u65f6\uff0c\u7ad9\u5728\u4e00\u8fb9\u7684\u201c\u804b\u4e10\u201d\u731b\u4f38\u624b\u628a\u201c\u9152 \u4e10\u201d\u624b\u81c2\u6293\u4f4f\uff0c\u8fde\u8fde\u6446\u624b\uff0c\u53c8\u505a\u4e86\u4e2a\u6101\u7709\u82e6\u8138\u3001\u6447\u6447\u6b32\u5012\u7684\u6837\u5b50\uff0c\u90a3\u610f\u601d\u662f\u963b\u6b62\u201c\u9152\u4e10\u201d\u4e0d \u8981\u518d\u559d\u4e86\uff0c\u518d\u559d\u4f1a\u9189\u5012\u4e86\u3002 \u201c\u9152\u4e10\u201d\u7528\u624b\u52bf\u544a\u8bc9\u201c\u804b\u4e10\u201d\uff0c\u4e0d\u662f\u4ed6\u8981\u559d\uff0c\u662f\u8981\u8bf7\u96f7\u5927\u53d4\u559d\u3002 \u201c\u804b\u4e10\u201d\u8fde\u8fde\u70b9\u5934\uff0c\u7acb\u523b\u653e\u624b\uff0c\u53c8\u5411\u96f7\u5927\u53d4\u6307\u624b\u5212\u811a\uff0c\u7ed5\u8116\u5b50\u3001\u77aa\u773c\u775b\uff0c\u6bd4\u4e86\u534a\u5929\u624b \u52bf\u3002 \u96f7\u5927\u53d4\u53ef\u662f\u4e00\u70b9\u4e0d\u61c2\u3002\u7531\u201c\u9152\u4e10\u201d\u544a\u8bc9\u96f7\u5927\u53d4\uff0c\u90a3\u201c\u804b\u4e10\u201d\u662f\u8bf4\u8fd8\u6709\u201c\u53eb\u5316\u9e21\u201d\uff0c\u6765\u62db \u5f85\u4f17\u4eba\u3002 \u96f7\u5927\u53d4\u54c8\u54c8\u5927\u7b11\uff0c\u8fde\u8fde\u5411\u201c\u804b\u4e10\u201d\u70b9\u5934\uff0c\u9053\uff1a\u201c\u4eca\u5929\uff0c\u662f\u5403\u5b9a\u4f60\u4eec\u4e86\uff01\u201d \u4f17\u4eba\u8bf4\u8bf4\u7b11\u7b11\uff0c\u53c8\u987a\u7740\u957f\u5df7\u8d70\u4e86\u5341\u6570\u4e08\u8fdc\uff0c\u624d\u5728\u4e00\u4e2a\u6a2a\u8def\u62d0\u5f2f\uff0c\u9762\u524d\u8c41\u7136\u5f00\u6717\uff0c\u56db\u91ce\u662f \u4e00\u671b\u65e0\u9645\u7684\u7530\u7574\uff0c\u8fce\u9762\u4e00\u6240\u5927\u9662\u843d\uff0c\u9ad8\u95e8\u5e7f\u6bbf\uff0c\u50cf\u662f\u5e99\u5b87\uff0c\u5374\u6ca1\u6709\u795e\u50cf\uff0c\u53c8\u50cf\u662f\u5bb6\u7960\uff0c\u53ef\u4e5f \u6ca1\u4f9b\u5b97\u4e3b\u724c\u4f4d\u3002 \u5e7f\u5927\u9662\u843d\u91cc\u5750\u6ee1\u4e86\u53eb\u5316\u5b50\uff0c\u89c1\u5230\u201c\u98ce\u5c18\u4e09\u4e10\u201d\u5f15\u4e86\u5ba2\u4eba\u6765\uff0c\u4e00\u9f50\u8d77\u8eab\u8ba9\u8def\uff0c\u606d\u7acb\u4e24\u65c1\uff0c \u72b6\u5176\u5c0a\u656c\u3002 \u8d70\u8fc7\u752c\u8def\uff0c\u8fdb\u4e86\u4e00\u95f4\u655e\u8f69\u5927\u5385\uff0c\u5927\u5385\u5730\u4e0b\u5206\u4e24\u884c\uff0c\u5750\u4e86\u5341\u6570\u4e2a\u5e74\u8001\u53eb\u5316\uff0c\u5927\u6982\u90fd\u662f\u201c\u7a77 \u5bb6\u5e2e\u201d\u5730\u4f4d\u8f83\u9ad8\u7684\u957f\u8001\u4e86\u3002\u5927\u5385\u8fce\u9762\u5899\u58c1\u4e0a\u60ac\u63a5\u7740\u4e00\u5e45\u957f\u7ea6\u4e00\u6587\u4f59\u957f\u7684\u753b\u50cf\uff0c\u50cf\u4e2d\u2014\u3002\u4e2a\u5e74 \u8001\u4e5e\u4e10\uff0c\u9e25[\u8863\u767e\u7ed3\uff0c\u9e20\u9762\u957f\u7709\uff0c\u6ee1\u5934\u4e71\u53d1\u5982\u732c\uff0c\u5371\u7136\u8dcc\u5750\uff0c\u4f46\u6c14\u6d3e\u5374\u9887\u4e3a\u4e0d\uff0c\u51e1\u3002 \u4e66\u4e0a\u9898\u7740\u5b57\uff0c\u4e0a\u6b3e\u5199\u201c\u5f00\u6d3e\u9f3b\u79df\u4e2d\u5dde\u795e\u4e10\u515a\u671d\u5b97\u201d\uff0c\u4e0b\u6b3e\u662f\uff0c\u201c\u7a77\u5bb6\u5e2e\u7b2c\u4e8c\u4ee3\u5f1f\u5b50\u4e39\u9752 \u5999\u624b\u9f50\u767e\u77f3\u656c\u7ed8\u201d\u3002 \u679c\u7136\u4e0d\u6127\u201c\u4e39\u9752\u5999\u624b\u201d\uff0c\u8fd9\u4e08\u4f59\u9ad8\u7684\u9762\u50cf\uff0c\u6829\u6829\u5982\u751f\u3002 \u753b\u50cf\u4e4b\u524d\u6446\u6709\u9999\u6848\uff0c\u671d\u82b1\u4f9b\u679c\uff0c\u9999\u70df\u7edf\u7ed5\uff0c\u6c14\u6c1b\u751a\u662f\u5e84\u4e25\uff01 \u5728\u9999\u6848\u4e24\u65c1\u5730\u4e0b\u5750\u7740\u7684\uff0c\u970d\u7136\u662f\u98ce\u6d41\u4e0d\u7f81\u7684\u201c\u5b89\u4e50\u516c\u5b50\u201d\u4e0e\u501c\u50a5\u4e0d\u7fa4\u7684\u201c\u7aef\u65b9\u516c\u5b50\u201d\uff01 \u770b\u6837\u5b50\u8fd9\u6b66\u6797\u4e24\u5927\u516c\u5b50\uff0c\u7adf\u662f\u5750\u7684\u9996\u4f4d\u3002 \u5c55\u767d\u5fae\u5fae\u4e00\u6115\uff01 \u4f46\u7b49\u5230\u5c55\u767d\u773c\u5149\u89e6\u5230\u201c\u7aef\u65b9\u516c\u5b50\u201d\u4e0b\u9996\uff0c\u5750\u7684\u662f\u4e00\u4e2a\u9526\u8863\u5c11\u5973\uff0c\u6b63\u4ee5\u8109\u8109\u542b\u60c5\u7684\u773c\u6ce2\u671b \u7740\u4ed6\u65f6\uff0c\u4ed6\u4e0d\u7531\u66f4\u6115\u4e86\uff01 \u4e0d\u8fc7\uff0c\u5c55\u767d\u9ad8\u51fa\u4e00\u822c\u4eba\u7684\u5730\u65b9\uff0c\u5c31\u662f\u4ed6\u77ac\u5373\u60f3\u5230\uff0c\u770b\u90a3\u201c\u7aef\u65b9\u516c\u5b50\u201d\u4e0e\u5a49\u513f\u76f8\u5f53\u5339\u914d\uff0c \u5982\u679c\u4ed6\u4e8c\u4eba\u80fd\u4e92\u6258\u7ec8\u8eab\uff0c\u81ea\u5df2\u5012\u53ef\u51cf\u5c11\u8bb8\u591a\u60c5\u5b7d\u7ea0\u7f20\uff0c\u56e0\u6b64\u90a3\u70b9\u9178\u610f\u4e5f\u5c31\u6d88\u5931\u4e86\u2026\u3002\u2019\u201c\u7aef \u65b9\u516c\u5b50\u201d\u4e5f\u662f\u4e00\u6115\uff01 \u4ed6\u4f30\u4e0d\u5230\u5c55\u767d\u4f1a\u5728\u6b64\u5730\u73b0\u8eab\uff01 \u5012\u662f\u201c\u5b89\u4e50\u516c\u5b50\u201d\u6bd4\u8f83\u723d\u6717\uff0c\u56e0\u4e3a\u4ed6\u80f8\u6000\u5927\u5fd7\uff0c\u65f6\u6709\u5e7f\u63fd\u5929\u4e0b\u7fa4\u96c4\u3001\u79f0\u9738\u6b66\u6797\u7684\u91ce\u5fc3\uff0c \u5728\u4ed6\u773c\u4e2d\uff0c\u5c55\u767d\u662f\u4e00\u4e2a\u8d85\u4f17\u7684\u4eba\u624d\uff0c\u65e9\u6709\u62c9\u62e2\u4e4b\u610f\uff0c\u56e0\u6b64\uff0c\u4e00\u89c1\u5c55\u767d\u7acb\u523b\u7ad9\u8d77\u6765\uff0c\u8d76\u4e0a\u524d\u4e0e \u5c55\u767d\u63e1\u624b\uff0c\u975e\u5e38\u70ed\u60c5\u5730\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u4f1a\u662f\u5c55\u5144\uff01\u5e78\u4f1a\uff01\u5e78\u4f1a\uff01\u201d \u5a49\u513f\u4e5f\u7ad9\u8d77\u6765\uff0c\u5411\u96f7\u5927\u53d4\u65bd\u793c\u3002\u7136\u540e\u4e0e\u59d0\u59d0\u76f8\u89c1\u3002 \u96f7\u5927\u53d4\u4f3c\u4e5f\u9887\u611f\u610f\u5916\uff0c\u60f3\u4e0d\u5230\u201c\u7a77\u5bb6\u5e2e\u201d\u4e3a\u4e3b\u4e4b\u4eba\uff0c\u7adf\u662f\u4e24\u4e2a\u5bcc\u5bb6\u5b50\u5f1f\uff1f\u53c8\u60f3\u4e0d\u5230\u5a49\u513f \u4f1a\u5148\u6765\u8fd9\u4e2a\u5730\u65b9\u3002\u4e8e\u662f\uff0c\u5411\u5a49\u513f\u8be7\u5f02\u5730\u95ee\u9053\uff1a\u201c\u5a49\u513f\uff01\u4f60\u600e\u4e48\u4f1a\u5728\u8fd9\u91cc\uff1f\u201d \u5a49\u513f\u7b11\u9053\uff1a\u201c\u4f84\u5973\u662f\u88ab\u62c9\u6765\u505a\u5ba2\u7684\uff01\u201d \u8bf4\u7740\uff0c\u5411\u5c55\u767d\u659c\u779f\u4e86\u4e00\u773c\u3002\u5c55\u767d\u518d\u7b28\uff0c\u6b64\u65f6\u4e5f\u660e\u5df3\u5a49\u513f\u662f\u6697\u793a\u7ed9\u4ed6\uff1a\u201c\u5979\u5e76\u4e0d\u662f\u4e0e\u2018\u7aef \u65b9\u516c\u5b50\u2019\u4e00\u8d77\u2026\u3002\u201d \u201c\u98ce\u5c18\u4e09\u4e10\u201d\u7acb\u523b\u4e3a\u4f17\u4eba\u4e00\u4e00\u5f15\u89c1\uff0c\u6b64\u65f6\uff0c\u96f7\u5927\u53d4\u624d\u60f3\u8d77\u6765\uff1a \u6570\u5e74\u4e4b\u524d\uff0c\u6c5f\u6e56\u4f20\u8a00\uff0c\u201c\u7a77\u5bb6\u5e2e\u201d\u4e0a\u4ee3\u638c\u95e8\u201c\u9752\u7af9\u53df\u201d\uff0c\u4e0d\u77e5\u56e0\u4f55\u6545\uff0c\u5c06\u95e8\u5f92\u904d\u5929\u4e0b\u7684 \u201c\u7a77\u5bb6\u5e2e\u201d\u638c\u95e8\u804c\u4f4d\u4f20\u7ed9\u4e86\u201c\u7aef\u65b9\u516c\u5b50\u201d\uff01 \u770b\u773c\u524d\u60c5\u5f62\uff0c\u8fd9\u4f20\u8a00\u7adf\u662f\u771f\u7684\u4e86\uff01 \u96f7\u5927\u53d4\u60f3\u5230\u6b64\u5904\uff0c\u4e0d\u7531\u591a\u6253\u91cf\u4e86\u201c\u7aef\u65b9\u516c\u5b50\u201d\u51e0\u773c\u3002 \u8c01\u77e5\u201c\u7aef\u65b9\u516c\u5b50\u201d\u5750\u5728\u9996\u4f4d\uff0c\u5fc3\u795e\u4e0d\u5c5e\uff0c\u53cc\u773c\u5de6\u987e\u53f3\u76fc\uff0c\u8eab\u8eaf\u4e0d\u65f6\u79fb\u52a8\u4e00\u4e0b\uff0c\u4f3c\u662f\u5e2d\u4e0d \u5b89\u4f4d\u7684\u6837\u5b50\u3002 \u867d\u7136\u751f\u5f97\u4fca\u7f8e\u975e\u51e1\uff0c\u4f46\u663e\u7136\u4e0d\u662f\u9886\u8896\u7fa4\u4f26\u7684\u6c14\u6d3e\uff01 \u96f7\u5927\u53d4\u6697\u53f9\uff1a\u201c\u9752\u7af9\u53df\uff0c\u6240\u4f20\u975e\u4eba\uff01\u2026\u2026\u201d \u201c\u7aef\u65b9\u516c\u5b50\u201d\u7a81\u7136\u4e00\u76b1\u7709\uff0c\u9053\uff1a\u201c\u6211\u4eec\u8bf7\u7684\u4eba\uff0c\u600e\u4e48\u8fd8\u6ca1\u6709\u5230\u9f50\uff1f\u201d \u201c\u98ce\u5c18\u4e09\u4e10\u201d\u53ca\u4f17\u5e74\u8001\u53eb\u5316\u4e00\u9f50\u7126\u5f62\u4e8e\u8272\uff0c\u4f3c\u662f\u4e5f\u5f88\u7740\u6025\u7684\u6837\u5b50\uff0c\u201c\u75af\u4e10\u201d\u9053\uff1a\u201c\u5c5e\u4e0b \u5f1f\u5b50\u8bf7\u5e16\u662f\u90fd\u9001\u5230\u4e86\uff0c\u800c\u4e14\u4e5f\u90fd\u5f97\u5230\u5bf9\u65b9\u56de\u4fe1\uff0c\u4e3a\u4ec0\u4e48\u5c4a\u65f6\u4e0d\u5230\uff1f\u5b9e\u5728\u8d39\u89e3\uff01\u201d \u201c\u9152\u4e10\u201d\u6b64\u65f6\u5df2\u6ca1\u6709\u534a\u70b9\u9189\u6001\uff0c\u6ee1\u8138\u5e84\u91cd\u795e\u60c5\uff0c\u9053\uff1a\u201c\u83ab\u975e\u51fa\u4e86\u4ec0\u4e48\u610f\u5916\u4e0d\u6210\uff1f\u201d \u201c\u5b89\u4e50\u5206\u5b50\u201d\u54e6\u4e86\u4e00\u58f0\uff0c\u7ecf\u5e38\u4e0d\u79bb\u624b\u7684\u7834\u4e66\u7528\u529b\u4e00\u62cd\uff0c\u9053\uff1a\u201c\u662f\u4e0d\u662f\u9001\u4fe1\u7684\u4eba\u8d70\u6f0f\u98ce \u58f0\uff0c\u53eb\u4eba\u5bb6\u6765\u4e86\u4e2a\u5148\u4e0b\u624b\u2026\u2026\u201d \u201c\u5b89\u4e50\u516c\u5b50\u201d\u8bf4\u5230\u6b64\u5904\uff0c\u201c\u7aef\u65b9\u516c\u5b50\u201d\u6025\u6253\u773c\u8272\uff0c\u201c\u5b89\u4e50\u516c\u5b50\u201d\u7acb\u523b\u4f4f\u53e3\u4e0d\u8bf4\u4e86\u3002 \u8339\u8001\u9556\u5934\u5750\u5728\u96f7\u5927\u53d4\u65c1\u8fb9\uff0c\u89c1\u6b64\u60c5\u5f62\uff0c\u7528\u624b\u6697\u6697\u89e6\u4e86\u96f7\u5927\u53d4\u96f7\u5927\u53d4\u5df2\u7136\u8b66\u89c9\uff0c\u9053\uff1a\u8d35\u5e2e \u65e2\u662f\u6709\u4e8b\uff0c\u6211\u4eec\u544a\u8f9e\uff01\u201d \u8bf4\u7f62\uff0c\u5411\u8339\u8001\u9556\u5934\u3001\u201c\u592a\u767d\u53cc\u9038\u201d\u3001\u5c55\u767d\u3001\u6155\u5bb9\u7ea2\u7b49\u4eba\u9053\uff1a\u201c\u54b1\u4eec\u8d70\uff01\u201d \u62c2\u8896\u800c\u8d77\uff0c\u4fbf\u6b32\u7387\u4f17\u79bb\u53bb\uff01 \u5a49\u513f\u4e5f\u7ad9\u8d77\u6765\uff0c\u9053\uff1a\u201c\u96f7\u5927\u53d4\uff0c\u4f84\u5973\u8ddf\u60a8\u4e00\u5757\u8d70\uff01\u2026\u2026\u201d \u201c\u5b89\u4e50\u516c\u5b50\u201d\u89c1\u96f7\u5927\u53d4\u4f17\u4eba\u544a\u8f9e\u8981\u8d70\uff0c\u5fd9\u8d77\u8eab\u76f8\u62e6\u9053\uff1a\u201c\u96f7\u5927\u4fa0\uff01\u5c55\u5144\uff01\u4e14\u8bf7\u7559\u6b65\uff0c\u7b49 \u4e00\u4e0b\u8fd8\u6709\u8981\u4e8b\u76f8\u5546\uff01\u201d \u201c\u98ce\u5c18\u4e09\u4e10\u201d\u4e5f\u4e00\u9f50\u633d\u7559\u96f7\u5927\u53d4\uff0c\u9053\uff1a\u201c\u65e0\u8bba\u5982\u4f55\u8bf7\u96f7\u5144\u8fc7\u4e86\u591c\uff0c\u660e\u5929\u518d\u8d70\uff01\u201d \u96f7\u5927\u53d4\u7684\u4e2a\u6027\uff0c\u8bf4\u8d70\u5c31\u8d70\uff0c\u4efb\u4f55\u4eba\u90fd\u633d\u7559\u4e0d\u4f4f\uff0c\u201c\u9152\u4e10\u201d\u7a81\u7136\u4e00\u77aa\u773c\uff0c\u5927\u58f0\u9053\uff1a\u201c\u5403\u4e24 \u53ea\u2018\u53eb\u5316\u9e21\u2019\uff0c\u559d\u5b8c\u4e86\u8001\u53eb\u5316\u8fd9\u4e00\u846b\u82a6\u2018\u8305\u53f0\u9152\u2019\u518d\u8d70\uff0c\u603b\u53ef\u4ee5\u4e86\u5427\uff01\u201d \u96f7\u5927\u53d4\u9053\uff1a\u201c\u9152\u9b3c\u53eb\u5316\u7684\u76db\u60c5\uff0c\u96f7\u67d0\u5fc3\u9886\u4e86\uff0c\u6539\u65e5\u53fc\u6270\uff01\u201d \u8bf4\u7f62\uff0c\u5927\u6b65\u5411\u95e8\u5916\u8d70\u53bb\u3002 \u201c\u75af\u4e10\u201d\u98de\u8eab\u63a0\uff0c\u534a\u771f\u534a\u5047\u5730\u5389\u76ee\u53f1\u9053\uff1a\u201c\u96f7\u75af\u5b50\uff01\u4f60\u662f\u4e0d\u662f\u770b\u4e0d\u8d77\u6211\u4eec\u4e09\u4e2a\u8001\u53eb \u5316\uff1f\u201d \u96f7\u5927\u53d4\u6602\u7136\u9053\uff1a\u5355\u51b2\u7740\u4f60\u4eec\u4e09\u4e2a\u8001\u53eb\u5316\uff0c\u96f7\u9707\u8fdc\u4e24\u808b\u63d2\u5200\uff0c\u82e5\u662f\u76b1\u4e00\u4e0b\u7709\uff0c\u4fbf\u4e0d\u7b97\u597d \u6c49\uff0c\u53ef\u662f\uff01\u54fc\uff01\u201d \u96f7\u5927\u53d4\u8bf4\u81f3\u6b64\u5904\uff0c\u4ece\u9f3b\u5b54\u91cd\u91cd\u54fc\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u82e5\u53eb\u96f7\u67d0\u5750\u51b7\u677f\u51f3\uff0c\u770b\u540e\u751f\u5c0f\u5b50\u7684\u8138 \u8272\uff0c\u53ea\u4e3a\u4e86\u5403\u4f60\u4eec\u4e09\u4e2a\u8001\u53eb\u5316\u4e24\u676f\u9152\uff0c\u96f7\u67d0\u53ef\u6ca1\u6709\u90a3\u4e48\u5927\u5bb9\u91cf\uff01\u201d \u6b64\u8a00\u4e00\u51fa\uff0c\u201c\u98ce\u5c18\u4e09\u4e10\u201d\u9762\u9732\u96be\u8272\uff0c\u767e\u5fd9\u4e2d\u5077\u779f\u4e86\u4e00\u773c\u7a33\u5750\u5934\u628a\u4ea4\u6905\uff0c\u7eb9\u4e1d\u4e0d\u52a8\u7684\u201c\u7aef \u65b9\u516c\u5b50\u201d\uff0c\u5fc3\u77e5\u662f\u201c\u7aef\u65b9\u516c\u5b50\u201d\u7684\u50b2\u6162\uff0c\u60f9\u5f97\u96f7\u5927\u53d4\u4e0d\u60a6\u3002\u53ef\u662f\uff0c\u201c\u7aef\u65b9\u516c\u5b50\u201d\u4e43\u662f\u4ed6\u4eec\u7684 \u638c\u95e8\u4eba\uff0c\u201c\u98ce\u5c18\u4e09\u4e10\u201d\u5728\u5e2e\u4e2d\u8f88\u4efd\u867d\u9ad8\uff0c\u5374\u4e5f\u4e0d\u6562\u6307\u8d23\u638c\u95e8\u4eba\u7684\u4e0d\u5bf9\uff0c\u56e0\u6b64\uff0c\u4e00\u65f6\u618e \u4f4f\uff01\u2026\u2026 \u201c\u7aef\u65b9\u516c\u5b50\u201d\u9ad8\u8e1e\u9996\u5ea7\uff0c\u59cb\u7ec8\u672b\u52a8\u3002\u6b64\u65f6\uff0c\u7a81\u7136\u53d1\u8bdd\u9053\uff1a\u201c\u6765\u8005\u81ea\u6765\uff0c\u53bb\u8005\u81ea\u53bb\uff01\u4e2d\u539f \u6b66\u6797\u4fa0\u4e49\u9053\u9ad8\u624b\u5982\u4e91\uff0c\u591a\u51e0\u4e2a\u4e0d\u591a\uff0c\u5c11\u51e0\u4e2a\u4e0d\u5c11\uff0c\u4f55\u5fc5\u52c9\u5f3a\u4eba\u5bb6\uff01\u201d \u96f7\u5927\u53d4\u84e6\u7136\u4e00\u8f6c\u8eab\u5b50\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u8bf7\u95ee\u9601\u4e0b\uff0c\u4e0d\u77e5\u4f55\u4eba\u53ef\u4ee5\u4ee3\u8868\u4e2d\u539f\u6b66\u6797\u4fa0\u4e49\u9053\uff1f\u201d \u59cb\u7ec8\u4e0d\u66fe\u5f00\u53e3\u7684\u201c\u592a\u767d\u53cc\u9038\u201d\u4e5f\u51b7\u51b7\u5730\u9053\uff1a\u201c\u770b\u8d77\u6765\u54b1\u4eec\u5012\u8981\u7559\u4e0b\u5566\uff0c\u89c1\u8bc6\u89c1\u8bc6\u4e2d\u539f\u6b66 \u6797\u4fa0\u4e49\u9053\u90fd\u662f\u4e9b\u4f55\u8bb8\u4eba\u7269\uff1f\u4e5f\u8ba9\u54b1\u4eec\u5f00\u5f00\u773c\uff01\u201d \u56e0\u201c\u7aef\u65b9\u516c\u5b50\u201d\u6697\u604b\u5a49\u513f\u975e\u53ea\u4e00\u65e5\uff0c\u4f46\u6bcf\u6b21\u501f\u673a\u4f1a\u4e0e\u5a49\u513f\u5230\u4e86\u4e00\u8d77\uff0c\u90fd\u662f\u88ab\u5c55\u767d\u62c6\u5f00\u3002 \u8fd9\u6b21\uff0c\u6070\u5de7\u5728\u4ed6\u4e8b\u4e1a\u9876\u5cf0\u7684\u65f6\u5019\uff0c\u9082\u9005\u5a49\u513f\uff0c\u767e\u822c\u62c9\u62e2\u628a\u5a49\u513f\u8bf7\u4e86\u6765\uff0c\u8ba9\u5fc3\u4e0a\u4eba\u770b\u4e86\u81ea\u5df1\u7684 \u5a01\u98ce\uff0c\u518d\u501f\u673a\u4f1a\u5411\u5a49\u513f\u6c42\u5a5a\uff0c\u201c\u82f1\u96c4\u4e8b\u4e1a\uff0c\u7f8e\u4eba\u7737\u5c5e\u201d\uff0c\u60f3\u4e0d\u5230\u6446\u9999\u5802\u7684\u6b63\u65e5\u5b50\uff0c\u6b66\u6797\u5e16\u7ea6 \u8bf7\u7684\u4e2d\u539f\u7fa4\u96c4\u672a\u5230\uff0c\u5374\u5148\u6765\u4e86\u96f7\u5927\u53d4\u4e0e\u5c55\u767d\u7b49\u4eba\u3002 \u800c\u4e14\uff0c\u5a49\u513f\u4e00\u89c1\u5c55\u767d\uff0c\u90a3\u7075\u6d3b\u7684\u773c\u5149\uff0c\u9a6c\u4e0a\u53c8\u4ece\u4ed6\u8eab\u4e0a\u8f6c\u79fb\u5230\u5c55\u767d\u8eab\u4e0a\u53bb\u4e86\u3002\u6b63\u5728\u5174\u5934 \u4e0a\uff0c\u8fd9\u4e00\u76c6\u51b7\u6c34\uff0c\u4e00\u7f50\u9178\u918b\uff0c\u53ef\u4ee5\u8bf4\u662f\u6cfc\u4e86\u6ee1\u5934\u6ee1\u8eab\uff01 \u201c\u5b89\u4e50\u516c\u5b50\u201d\u5230\u5e95\u6bd4\u201c\u7aef\u65b9\u516c\u5b50\u201d\u8f83\u5bcc\u5fc3\u673a\uff0c\u89c1\u53cc\u65b9\u5c06\u8981\u8bf4\u50f5\uff0c\u6df1\u6050\u7ed3\u76df\u672a\u7ed3\u6210\uff0c\u53cd\u800c \u591a\u6811\u5f3a\u654c\uff0c\u7acb\u523b\u4e0a\u524d\u4e24\u6b65\uff0c\u9053\uff1a\u201c\u8bf8\u4f4d\u5148\u4e0d\u8981\u505a\u65e0\u8c13\u7684\u4e49\u6c14\u4e4b\u4e89\uff0c\u5b9e\u5728\u8fd9\u6b21\u5144\u5f1f\u4e0e\u2018\u7aef\u65b9\u516c \u5b50\u2019\u7ea6\u4f1a\u5929\u4e0b\u7fa4\u96c4\uff0c\u5173\u7cfb\u975e\u5e38\u91cd\u5927\uff0c\u4e5f\u53ef\u8bf4\u662f\u4e2d\u539f\u6b66\u6797\u4eba\u58eb\u80fd\u5426\u4ecd\u5728\u4e2d\u539f\u7acb\u8db3\uff0c\u5b8c\u5168\u5728\u6b64\u4e00 \u4e3e\uff01\u201d \u201c\u5b89\u4e50\u516c\u5b50\u201d\u65e2\u505a\u4e86\u548c\u4e8b\u4f6c\uff0c\u53c8\u81ea\u62ac\u9ad8\u4e86\u8eab\u4efd\uff0c\u89c1\u4f17\u4eba\u90fd\u5728\u805a\u7cbe\u4f1a\u795e\u5730\u542c\u4ed6\u8bf4\u8bdd\uff0c\u4ece\u5fc3 \u5e95\u6cdb\u8d77\u4e00\u4e1d\u5f97\u610f\uff0c\u4f46\u4ecd\u4e0d\u52a8\u58f0\u8272\u5730\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u5357\u6d77\u95e8\u2019\u6b64\u6b21\u5927\u4e3e\u4fb5\u72af\u4e2d\u539f\uff0c\u8bdb\u9664\u5f02\u5df1\uff0c\u5df1 \u4f7f\u4e2d\u539f\u6b66\u6797\u6210\u4e3a\u5c38\u5c71\u8840\u6d77\uff0c\u8fd9\u4e2a\u60f3\u5fc5\u8bf8\u4f4d\u5df2\u6709\u4e2a\u8033\u95fb\uff0c\u751a\u81f3\u90fd\u5df2\u6709\u4eb2\u670b\u597d\u53cb\u88ab\u6740\u4e86\uff01\u6211\u4eec \u2018\u6b66\u6797\u56db\u516c\u5b50\u2019\u8054\u540d\u7ea6\u8bf7\u2018\u4e03\u5927\u95e8\u6d3e\u2019\u53ca\u4e2d\u539f\u6b66\u6797\u5404\u5730\u9ad8\u624b\uff0c\u4e3b\u8981\u7684\u662f\u8981\u9a71\u9010\u2018\u5357\u6d77\u95e8\u2019\uff0c \u4e00\u6765\u4e3a\u6b7b\u96be\u7684\u6b66\u6797\u4eba\u58eb\u62a5\u4ec7\uff0c\u4e8c\u6765\u662f\u91cd\u632f\u4e2d\u539f\u6b66\u6797\u58f0\u5a01\u2026\u2026\u201d \u201c\u5b89\u4e50\u516c\u5b50\u201d\u8bf4\u81f3\u6b64\u5904\uff0c\u201c\u7aef\u65b9\u516c\u5b50\u201d\u7a81\u7136\u5e72\u54b3\u4e86\u4e00\u58f0\uff0c\u6253\u65ad\u201c\u5b89\u4e50\u516c\u5b50\u201d\u7684\u8bdd\u5934\uff0c\u63d2 \u5634\u9053\uff1a\u201c\u672c\u516c\u4e88\u4ee5\u2018\u7a77\u5bb6\u5e2e\u2019\u638c\u95e8\u4eba\u5730\u4f4d\uff0c\u4e3b\u6301\u6b64\u4e8b\uff01\u201d \u8fd9\u8bdd\u72c2\u50b2\u5df2\u6781\uff0c\u800c\u4e14\u81ea\u5439\u81ea\u64c2\uff0c\u663e\u5f97\u65e0\u77e5\u800c\u5e7c\u7a1a\u3002 \u201c\u5b89\u4e50\u516c\u5b50\u201d\u9762\u8272\u500f\u5730\u4e00\u53d8\uff01\u2026\u2026 \u5c31\u8fde\u201c\u98ce\u5c18\u4e09\u4e10\u201d\u4e43\u81ea\u201c\u7a77\u5bb6\u5e2e\u201d\u7684\u957f\u8001\uff0c\u542c\u5230\u201c\u7aef\u65b9\u516c\u5b50\u201d\u5982\u6b64\u8bf4\u8bdd\uff0c\u4e5f\u5747\u81ea\u9762\u8272\u4e00 \u6014\uff01\u2026\u2026 \u201c\u5b89\u4e50\u516c\u5b50\u201d\u52c9\u5f3a\u96c4\u4e0b\u7b11\u8138\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff01\u76ee\u524d\u7684\u786e\u662f\u2018\u7aef\u65b9\u516c\u5b50\u2019\u4e3b\u6301\u6b64\u4e8b\uff0c\u7b79\u5212\u4e2d \u539f\u6b66\u6797\u5404\u6d3e\u9ad8\u624b\u5230\u9f50\u4e4b\u540e\uff0c\u5f53\u7136\u8fd8\u6709\u4e00\u756a\u91cd\u65b0\u8c03\u6574\u2026\u2026\u201d \u8fd9\u8bdd\u65e0\u5f02\u662f\u628a\u201c\u7aef\u65b9\u516c\u5b50\u201d\u7684\u8bdd\u6253\u4e86\u6298\u6263\uff01 \u201c\u7aef\u65b9\u516c\u5b50\u201d\u4f5b\u7136\u4e0d\u60a6\uff0c\u6389\u5934\u5bf9\u201c\u5b89\u4e50\u516c\u5b50\u201d\u9053\uff1a\u201c\u600e\u4e48\uff1f\u4e91\u94ee\u5144\uff01\u54b1\u4eec\u4e0d\u662f\u8bf4\u597d\u4e86 \u5417\uff1f\u201d \u201c\u5b89\u4e50\u516c\u5b50\u201d\u4e00\u7b11\uff0c\u9053\uff0c\u201c\u6211\u4eec\u8bf4\u7684\u53ea\u662f\u76ee\u524d\uff0c\u5047\u5982\u4e2d\u539f\u6b66\u6797\u5404\u6d3e\u4eba\u624b\u5230\u9f50\uff0c\u5185\u4e2d\u6709\u58f0 \u671b\u5730\u4f4d\u4ecd\u6bd4\u53f8\u7a7a\u5144\u9ad8\u7684\uff0c\u5f53\u7136\u5e94\u8be5\u53e6\u505a\u522b\u8bba\uff01\u201d \u96f7\u5927\u53d4\u4ece\u8fd9\u6b66\u6797\u4e24\u5927\u516c\u5b50\u7684\u5bf9\u8bdd\u4e2d\uff0c\u5df2\u7136\u542c\u51fa\u4e86\u7aef\u502a\uff0c\u89c1\u4ed6\u4eec\u4e8b\u60c5\u4ecd\u6ca1\u6709\u7709\u76ee\uff0c\u5374\u5df2\u7ecf \u6709\u4e86\u6743\u529b\u4e4b\u4e89\uff0c\u4e0d\u7531\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u8fd9\u6837\u8bf4\u6765\uff0c\u8fd9\u7684\u786e\u662f\u96be\u5f97\u4e00\u89c1\u7684\u4e00\u6b21\u76db\u4f1a\uff0c\u96f7\u67d0\u4eba\u5fdd\u9022\u76db \u4f1a\uff0c\u5012\u662f\u771f\u8981\u89c2\u671b\u4e00\u756a\u4e86\uff01\u8339\u5144\uff0c\u592a\u767d\u4e8c\u6606\u4ef2\uff0c\u610f\u4e0b\u5982\u4f55\uff1f\u201d \u8339\u8001\u9556\u5934\u3001\u201c\u592a\u767d\u53cc\u9038\u201d\u540c\u58f0\u9053\uff1a\u201c\u76db\u4f1a\u96be\u9022\uff0c\u5f53\u7136\u8981\u5f00\u5f00\u773c\u754c\uff01\u201d \u96f7\u5927\u53d4\u53c8\u8f6c\u5934\u5bf9\u5c55\u767d\u3001\u6155\u5bb9\u7ea2\u53ca\u5a49\u513f\u9053\uff1a\u201c\u5c55\u8d24\u4f84\uff0c\u4e24\u4f4d\u8d24\u4f84\u5973\uff0c\u4f60\u4eec\u7684\u610f\u89c1\u600e\u4e48 \u6837\uff1f\u201d \u5c55\u767d\u3001\u6155\u5bb9\u7ea2\u3001\u5a49\u513f\u4e09\u4eba\uff0c\u5728\u96f7\u5927\u53d4\u9762\u524d\uff0c\u4e43\u662f\u665a\u8f88\uff0c\u65e0\u5f02\u8bae\u5730\u901a\u8fc7\u3002 \u96f7\u5927\u53d4\u53c8\u54c8\u54c8\u4e00\u7b11\uff0c\u5411\u201c\u98ce\u5c18\u4e09\u4e10\u201d\u9053\uff1a\u201c\u53ea\u8981\u8d35\u5e2e\u4e0d\u4e0b\u9010\u5ba2\u4ee4\u5c31\u597d\u4e86\uff01\u201d \u201c\u98ce\u5c18\u4e09\u4e10\u201d\u6b63\u4e3a\u81ea\u5df1\u5e2e\u4e2d\u5e74\u8f7b\u7684\u638c\u95e8\u4eba\u5931\u5e38\u7684\u4e3e\u63aa\u3001\u72c2\u5984\u7684\u8a00\u8f9e\u800c\u611f\u5230\u5c34\u5c2c\u4e07\u5206\uff0c\u4eca \u89c1\u96f7\u5927\u53d4\u5982\u6b64\u4e00\u8bf4\uff0c\u7acb\u523b\u501f\u673a\u8f6c\u53d8\u8bdd\u9898\u9053\uff1a\u201c\u95f2\u8bdd\u5c11\u8bf4\uff01\u4eca\u5929\u6211\u4eec\u4e09\u4e2a\u8001\u53eb\u5316\uff0c\u8bf7\u4f60\u96f7\u75af\u5b50 \u5403\u4e00\u987f\u2018\u53eb\u5316\u5927\u83dc\u2019\uff01\u201d \u8bf4\u7740\u5429\u5490\u5c4b\u4e0b\u6dee\u5907\u3002 \u8339\u8001\u9556\u5934\u5728\u4e00\u65c1\uff0c\u4e5f\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u8001\u673d\u5b9a\u5357\u95ef\u5317\u4e00\u8f88\u5b50\uff0c\u5ddd\u83dc\u3001\u7ca4\u83dc\u90fd\u5403\u8fc7\u4e86\uff0c\u53ef\u5c31\u662f \u6ca1\u5403\u8fc7\u2018\u53eb\u5316\u5927\u83b1\u2019\uff01\u8fd9\u6b21\u4e0d\u4f46\u5f00\u773c\uff0c\u800c\u4e14\u4e5f\u8981\u592a\u5feb\u6735\u9890\u4e86\uff01\u201d \u201c\u592a\u767d\u53cc\u9038\u201d\u4e4b\u4e2d\u7684\u4e8c\u9038\u201c\u6b7b\u6d3b\u4eba\u201d\uff0c\u6b7b\u4eba\u773c\u4e00\u7ffb\uff0c\u9053\uff1a\u8339\u8001\u5934\uff01\u4f60\u522b\u662f\u998b\u75af\u4e86\u5427\uff1f\u4eba \u5bb6\u8bf7\u7684\u662f\u2018\u5929\u4f5b\u638c\u2019\u96f7\u5927\u4fa0\uff0c\u53ef\u6ca1\u6709\u8bf4\u8bf7\u4f60\uff01\u201d \u201c\u75af\u4e10\u201d\u891a\u826f\u9053\uff1a\u4f60\u662f\u2018\u6b7b\u6d3b\u4eba\u2019\uff1f\u8fd8\u662f\u2018\u6d3b\u6b7b\u4eba\u2019\uff1f\u8001\u53eb\u5316\u5bf9\u4f60\u4eec\u8d24\u6606\u4ef2\uff0c\u59cb\u7ec8\u5206\u4e0d \u6e05\u695a\uff01\u597d\u4e86\uff01\u522b\u518d\u9e21\u86cb\u91cc\u627e\u9aa8\u5934\u4e86\uff01\u8bf7\uff0c\u5f53\u7136\u662f\u90fd\u8bf7\uff01\u5c82\u80fd\u771f\u62ff\u4f60\u4eec\u4e8c\u4f4d\u5f53\u2018\u6b7b\u4eba\u2019\u770b \u5f85\uff01\u201d \u8fd9\u4e00\u8bf4\uff0c\u5927\u5bb6\u90fd\u7b11\u4e86\u3002 \u201c\u7a77\u5bb6\u5e2e\u201d\u95e8\u5f92\u4f17\u591a\uff0c\u4eba\u591a\u597d\u529e\u4e8b\uff0c\u5c31\u5728\u4f17\u4eba\u8bf4\u7b11\u4e4b\u95f4\uff0c\u5e2e\u4f17\u5ddd\u6d41\u4e0d\u606f\uff0c\u6446\u684c\u6905\uff0c\u7aef\u996d \u201c\u53eb\u5316\u5927\u83dc\u201d\u5df2\u7ecf\u6446\u597d\uff01 \u8fd9\u662f\u6446\u5728\u655e\u5385\u4e4b\u5185\u7684\uff0c\u81f3\u4e8e\u6446\u5728\u5e7f\u5927\u9662\u843d\u7684\u8fd8\u6709\u591a\u5c11\u684c\uff1f\u53ef\u5c31\u7b97\u4e0d\u6e05\u4e86\u3002 \u8c01\u8bf4\u53eb\u5316\u7a77\uff1f\u5149\u662f\u8fd9\u5e2d\u9762\uff0c\u5c31\u662f\u949f\u9e23\u9f0e\u98df\u7684\u8c6a\u5bcc\u4e4b\u5bb6\uff0c\u4e5f\u4e0d\u8fc7\u5982\u6b64\u4e86\u3002 \u96f7\u5927\u53d4\u3001\u8339\u8001\u9556\u5934\u53ca\u201c\u592a\u767d\u53cc\u9038\u201d\u7b49\u4eba\uff0c\u90fd\u662f\u8001\u6c5f\u6e56\uff0c\u5947\u95fb\u5f02\u4e8b\uff0c\u89c1\u5f97\u592a\u591a\u4e86\uff0c\u8fd8\u4e0d\u600e \u4e48\u4e3a\u602a\u3002 \u5c55\u767d\u6c5f\u6e56\u884c\u9053\u751a\u6d45\uff0c\u6155\u5bb9\u7ea2\u3001\u5a49\u513f\u867d\u662f\u6b66\u6797\u4e16\u5bb6\u7684\u513f\u5973\uff0c\u4f46\u662f\u201c\u6458\u661f\u624b\u201d\u6155\u5bb9\u6db5\u6cbb\u5bb6\u9887 \u4e25\uff0c\u5e73\u5e38\u628a\u5973\u513f\u5173\u5728\u6df1\u95fa\u5185\u9662\uff0c\u5927\u95e8\u8f7b\u6613\u4e0d\u8ba9\u79bb\u5f00\u4e00\u6b65\uff0c\u5982\u4eca\u89c1\u8fd9\u7a77\u53eb\u5316\u80fd\u6446\u51fa\u5982\u6b64\u5e2d\u9762\uff0c \u5c31\u662f\u4ee5\u8c6a\u95e8\u81ea\u8bb8\u7684\u5bb6\u4e2d\uff0c\u4e5f\u5f88\u5c11\u89c1\u8fc7\uff0c\u4e0d\u7531\u5747\u7741\u5927\u4e86\u773c\u775b\uff0c\u770b\u770b\u8fd9\u4e2a\uff0c\u671b\u671b\u90a3\u4e2a\uff0c\u5904\u5904\u611f\u5230 \u65b0\u5947\uff01 \u539f\u6765\u201c\u98ce\u5c18\u4e09\u4e10\u201d\u53e3\u4e2d\u7684\u8fd9\u201c\u53eb\u5316\u5927\u83dc\u201d\u7684\u786e\u4e0e\u4f17\u4e0d\u540c\uff01 \u6bcf\u684c\u516b\u789f\u516b\u7897\uff0c\u65e0\u975e\u662f\u9e21\uff0c\u9e2d\uff0c\u9c7c\uff0c\u8089\uff0c\u8fd9\u8fd8\u4e0d\u7b97\u51fa\u5947\uff0c\u6700\u5947\u7684\u662f\u4e2d\u95f4\u4e00\u4e2a\u64e6\u5f97\u6323\u4eae\u7684 \u94dc\u76d8\uff0c\u76d8\u4e2d\u653e\u7740\u4e00\u4e2a\u6ce5\u70ae\u5730\uff0c\u4e0d\u77e5\u662f\u4f55\u4e8b\u7269\uff1f\u800c\u4e14\uff0c\u90a3\u9ec4\u6ce5\u5df4\u4e00\u5927\u5806\uff0c\u66f4\u4e0d\u77e5\u5982\u4f55\u5403\u6cd5\uff1f \u5c31\u5728\u8fd9\u6446\u8bbe\u5e2d\u9762\u7684\u5f53\u513f\uff0c\u53c8\u6709\u51e0\u62e8\u6b66\u6797\u4eba\u58eb\u5230\u4f1a\u3002 \u9996\u5148\u5230\u4f1a\u7684\u662f\u201c\u7965\u9e9f\u516c\u5b50\u201d\u53ca\u6709\u7740\u201c\u6c5f\u5357\u7b2c\u4e00\u7f8e\u4eba\u201d\u4e4b\u79f0\u7684\u91d1\u5f69\u51e4\uff0c\u7387\u9886\u5c5e\u4e0b\u9ad8\u624b\u5341\u6570 \u4eba\uff0c\u5176\u4e2d\u201c\u91d1\u5e9c\u53cc\u94c1\u536b\u201d\u201c\u94c1\u7ffc\u98de\u9e4f\u201d\u5df4\u5929\u8d6b\uff0c\u80cc\u4e0a\u94c1\u7ffc\u9690\u7136\uff0c\u201c\u94c1\u7ffc\u795e\u8863\u201d\u867d\u7136\u4ecd\u5728\uff0c\u4f46 \u5de6\u81c2\u865a\u5782\uff0c\u663e\u7136\u90a3\u6761\u5de6\u81c2\u5df2\u7ecf\u5e9f\u4e86\uff01 \u201c\u94c1\u80cc\u9a7c\u9f99\u201d\u516c\u5b59\u695a\uff0c\u9762\u8272\u82cd\u767d\uff0c\u6709\u540d\u7684\u94c1\u80cc\u4e0a\u7eb1\u5e03\u672b\u9664\uff0c\u663e\u7136\u80cc\u4e0a\u5251\u4f24\u672b\u6108\uff01 \u7b2c\u4e8c\u62e8\u4eba\u9a6c\u662f\u201c\u51cc\u98ce\u516c\u5b50\u201d\u6155\u5bb9\u627f\u4e1a\uff0c\u5c5e\u4e0b\u4e5f\u5e26\u6709\u4e94\u516d\u540d\u9ad8\u624b\uff0c\u5176\u4e2d\u957f\u53d1\u62ab\u80a9\u7684\u8001\u8005\uff0c \u6b63\u662f\u90a3\u201c\u51cc\u98ce\u516c\u5b50\u201d\u79f0\u505a\u201c\u8d6b\u8fde\u4e09\u53d4\u201d\u3001\u5728\u91d1\u5e9c\u4ee5\u201c\u5f39\u6307\u795e\u901a\u201d\u4f24\u4e86\u201c\u9152\u4e10\u201d\u65b9\u7ca5\u4e00\u6307\u7684 \u201c\u6478\u5916\u795e\u541b\u201d\u8d6b\u8fde\u82f1\uff01 \u201c\u5929\u6daf\u72c2\u751f\u201d\u8d75\u4e5d\u5dde\uff0c\u4e5f\u8d6b\u7136\u540c\u6765\uff0c\u770b\u4ed6\u53cc\u76ee\u795e\u5149\u56db\u5c04\uff0c\u9762\u8272\u7ea2\u6da6\uff0c\u663e\u7136\u201c\u6d77\u5916\u4e09\u715e\u201d \u4e4b\u4e2d\u767d\u53d1\u5a46\u5a46\u7684\u201c\u641c\u9b42\u6307\u201d\uff0c\u5e76\u672a\u80fd\u4f7f\u4ed6\u5236\u547d\uff01 \u7b2c\u4e09\u62e8\u6765\u7684\u662f\u7ecf\u5e38\u7a7f\u7740\u7537\u88c5\u7684\u6a0a\u7d20\u9e3e\uff0c\u968f\u884c\u7684\u53ea\u6709\u83bd\u725b\u5c71\u201c\u91d1\u6c0f\u4e8c\u4e49\u201d\uff0c\u81f3\u4e8e\u5979\u7684\u4e09\u4f4d \u5144\u957f\u201c\u6a0a\u6c0f\u4e09\u5251\u201d\u5374\u672a\u4e0e\u4e4b\u540c\u6765\u3002 \u6b64\u5916\uff0c\u9646\u7eed\u6765\u7684\u5c1a\u6709\u201c\u6cd5\u534e\u5357\u5b97\u201d\u5f1f\u5b50\u201c\u4e94\u97f3\u80fd\u624b\u201d\u8427\u94dc\u3001\u201c\u4e03\u6b65\u8ffd\u9b42\u201d\u73ed\u6842\u3002\u5c11\u6797\u4fd7 \u5bb6\u5f1f\u5b50\u201c\u795e\u62f3\u6253\u4e95\u201d\u6b65\u4e91\u98de\uff0c\u53ca\u5c11\u6797\u5bfa\u7f57\u6c49\u5802\u9996\u5ea7\u201c\u864e\u76ee\u5c0a\u8005\u201d\u3001\u201c\u6b66\u5f53\u4e09\u9053\u201d\u3001\u201c\u5cfb\u86d4\u897f \u4e11\u201d\u3001\u201c\u70b9\u82cd\u53cc\u5251\u201d\u4ee5\u53ca\u201c\u6606\u4ed1\u6d3e\u201d\u7684\u201c\u5929\u5c71\u4e00\u9e64\u201d\u949f\u660e\uff01 \u9664\u4e86\u201c\u86fe\u7709\u6d3e\u201d\u672a\u89c1\u6765\u4eba\u53c2\u52a0\u4e4b\u5916\uff0c\u201c\u4e03\u5927\u95e8\u6d3e\u201d\u5747\u6709\u9ad8\u624b\u4e0e\u4f1a\uff01 \u9ad8\u624b\u4e91\u96c6\uff0c\u663e\u89c1\u8fd9\u4e00\u4f1a\u5b9e\u529b\u4e0d\u5c0f\uff01 \u9152\u8fc7\u4e09\u5de1\uff0c\u4f17\u4eba\u90fd\u5df2\u6709\u4e86\u4e03\u5206\u9189\u610f\uff0c\u8fd9\u4e9b\u6c5f\u6e56\u8c6a\u5ba2\uff0c\u5403\u4e86\u9152\u4fbf\u4e92\u76f8\u81ea\u5439\u81ea\u6367\uff0c\u201c\u5982\u5f53\u5e74 \u8fc7\u4e94\u5173\u65a9\u516d\u5c06\u201d\u5927\u8bdd\u6ee1\u5929\u98de\uff0c\u63d0\u8d77\u201c\u5357\u6d77\u95e8\u201d\u4e3a\u7978\u4e2d\u539f\uff0c\u66f4\u662f\u4e2a\u4e2a\u78e8\u62f3\u64e6\u638c\uff0c\u4f38\u80f3\u818a\u633d\u8896 \u5b50\uff0c\u5927\u6709\u7acb\u5373\u627e\u201c\u5357\u6d77\u95e8\u201d\u4e00\u62fc\u9ad8\u4e0b\u7684\u610f\u601d\uff01 \u8fd9\u5176\u4e2d\uff0c\u53ea\u6709\u5c55\u767d\u5fc3\u667a\u6bd4\u8f83\u6e05\u9192\uff0c\u56e0\u4e3a\u4ed6\u5f88\u5c11\u559d\u9152\uff0c\u201c\u5357\u6d77\u95e8\u201d\u7684\u6b66\u529f\uff0c\u4e5f\u6570\u4ed6\u77e5\u9053\u6700 \u6e05\u695a\uff0c\u5fc3\u60f3\uff1a\u2018\u6469\u4e91\u795e\u624b\u2019\u5411\u51b2\u5929\uff0c\u2018\u8840\u638c\u706b\u9f99\u2019\u59da\u70b3\u6606\uff0c\u4ee5\u53ca\u2018\u6bd2\u5251\u7075\u86c7\u2019\u4fde\u5316\u5357\uff0c\u90a3\u7b49 \u6b66\u529f\u9ad8\u624b\uff0c\u90fd\u6b7b\u5728\u2018\u5357\u6d77\u95e8\u2019\u624b\u4e0b\uff0c\u4ee5\u773c\u524d\u8fd9\u4e9b\u4eba\uff0c\u9664\u4e86\u6781\u5c11\u6570\u52c9\u53ef\u81ea\u4fdd\u5916\uff0c\u5176\u4f59\u8981\u60f3\u8ddf \u2018\u5357\u6d77\u95e8\u2019\u4e00\u89c1\u9ad8\u4e0b\uff0c\u65e0\u975e\u662f\u4ee5\u5375\u51fb\u77f3\uff0c\u767d\u767d\u9001\u6b7b\u2026\u2026\u2606\u6155\u5bb9\u7ea2\u3001\u5a49\u513f\uff0c\u66f4\u662f\u4e0d\u5403\u9152\uff0c\u53ea\u6ce8 \u610f\u9ec4\u94dc\u76d8\u5185\u90a3\u4e00\u5806\u9ec4\u6ce5\uff0c\u4e0d\u77e5\u600e\u4e48\u5403\u6cd5\uff1f \u81ea\u5df1\u53c8\u4e0d\u6562\u5148\u52a8\u624b\uff0c\u76f4\u7b49\u5230\u770b\u89c1\u522b\u7684\u684c\u4e0a\u7684\u4eba\uff0c\u628a\u9ec4\u6ce5\u6572\u5f00\uff0c\u91cc\u8fb9\u9732\u51fa\u8089\u9999\u56db\u6ea2\u7684\u767d\u9e21 \u8089\u6765\uff0c\u624d\u77e5\u9053\u9ec4\u6ce5\u5df4\u91cc\u8fb9\u7cca\u7684\u662f\u4e00\u53ea\u6574\u9e21\uff01 \u4e8c\u4eba\u4e5f\u7167\u7740\u522b\u4eba\u7684\u6837\u5b50\uff0c\u628a\u9ec4\u6ce5\u6572\u5f00\uff0c\u4ee5\u7b77\u5b50\u5939\u4e86\u4e00\u70b9\u5c1d\u5728\u5634\u91cc\uff0c\u53c8\u9999\u53c8\u8f6f\uff0c\u7adf\u662f\u4ee5\u524d \u4ece\u672a\u5403\u8fc7\u7684\u7f8e\u5473\u3002\u5a49\u513f\u7a76\u7adf\u8fd8\u662f\u4e2a\u5929\u771f\u5c11\u5973\uff0c\u4e0d\u7531\u8131\u53e3\u53eb\u9053\uff1a\u201c\u54ce\u5440\uff01\u59d0\u59d0\uff0c\u8fd9\u9e21\u8089\u771f\u597d\u5403 \u6781\u4e86\uff01\u4e0d\u77e5\u600e\u4e48\u505a\u7684\uff01\u201d \u201c\u75af\u4e10\u201d\u7b11\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u591a\u5403\u70b9\u5427\uff01\u8fd9\u53eb\u2018\u53eb\u5316\u9e21\u2019\uff0c\u9664\u4e86\u6211\u4eec\u7a77\u8981\u996d\u7684\uff0c\u5c31\u662f\u7687\u5bb6 \u540d\u53a8\u4e5f\u505a\u4e0d\u51fa\u6765\uff01\u201d \u201c\u9152\u4e10\u201d\u89c1\u5a49\u513f\u76f4\u6487\u5634\uff0c\u4f3c\u662f\u4e0d\u4fe1\uff0c\u4e00\u4ef0\u8116\u5b50\u53c8\u5e72\u4e86\u9762\u524d\u4e00\u6000\u9152\uff0c\u9152\u6c14\u55b7\u4eba\u5730\u5bf9\u5a49\u513f \u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u522b\u5c0f\u77a7\u4e86\u8fd9\u53ea\u2018\u53eb\u5316\u9e21\u2019\uff0c\u5c0f\u8981\u996d\u7684\u8ba8\u4e2a\u5341\u5e74\u516b\u5e74\u7684\u996d\uff0c\u5982\u679c\u4e0d\u62dc\u7956\u5e08\u8fd8\u5b66 \u4e0d\u5230\u3002\u4eca\u5929\uff0c\u8001\u53eb\u5316\u5411\u59d1\u5a18\u6cc4\u4e2a\u5e95\uff0c\u544a\u8bc9\u4f60\u600e\u4e48\u50b2\u6cd5\uff0c\u56de\u5230\u5bb6\u4e2d\u4f60\u4e5f\u53ef\u4ee5\u70e7\u6765\u5403\uff01\u201d \u8bf4\u7740\uff0c\u4ed6\u633d\u4e86\u633d\u8896\u5b50\uff0c\u505a\u4e2a\u624b\u52bf\uff0c\u9053\uff1a\u201c\u90fd\u8bf4\u53eb\u5316\u4e0d\u8981\u672c\u94b1\uff0c\u4e8b\u5b9e\u4e0a\u5077\u9e21\u4e5f\u8981\u4e00\u628a\u7c73\uff0c \u4f60\u62ff\u7740\u4e00\u628a\u7c73\uff0c\u770b\u89c1\u8c01\u5bb6\u7684\u9e21\u5728\u95e8\u53e3\u5bfb\u98df\uff0c\u56de\u987e\u65e0\u4eba\uff0c\u4f60\u4fbf\u628a\u7c73\u6258\u5728\u624b\u638c\u5fc3\u91cc\u8ba9\u9e21\u6765\u5403\uff0c\u53ef \u5343\u4e07\u8bf4\u597d\u4e86\uff0c\u4e0d\u80fd\u628a\u7c73\u64a4\u5728\u5730\u4e0b\uff0c\u628a\u7c73\u64a4\u5728\u5730\u4e0b\u7684\u90fd\u662f\u7b28\u8d3c\uff0c\u6240\u8c13\u2018\u5077\u9e21\u4e0d\u7740\u8680\u628a\u7c73\u2019\uff0c\u5c31 \u662f\u6307\u90a3\u4e9b\u7b28\u8d3c\u8bf4\u7684\uff01\u2026\u2026\u201d \u4f17\u4eba\u54c4\u8d77\u4e00\u9635\u7b11\u58f0\uff01 \u201c\u9152\u4e10\u201d\u5fd9\u9053\uff1a\u201c\u522b\u7b11\uff01\u522b\u7b11\uff01\u8ba9\u8001\u53eb\u5316\u4f20\u4e2a\u5973\u5f1f\u5b50\uff01\u201d \u5a49\u513f\u7ef7\u7740\u5634\uff0c\u5f3a\u5fcd\u4f4f\u4e0d\u7b11\uff0c\u542c\u4ed6\u8bf4\u4e0b\u53bb\u3002\u53ea\u89c1\u201c\u9152\u4e10\u201d\u63a5\u7740\u9053\uff1a\u7b49\u5230\u9e21\u6765\u5403\u7c73\uff0c\u4f60\u4f38\u624b \u6293\u4f4f\u9e21\u8116\u5b50\uff0c\u628a\u9e21\u5934\u5f80\u9e21\u7fc5\u8180\u4e0b\u4e00\u585e\uff0c\u4fdd\u9669\u4e00\u58f0\u4e0d\u53eb\uff0c\u4f60\u63a5\u7740\u9e21\u5c31\u8d70\uff0c\u5230\u4e00\u4e2a\u6ca1\u4eba\u7684\u5730\u65b9\uff0c \u7528\u9ec4\u6ce5\u5728\u9e21\u8eab\u4e0a\u4e00\u7cca\uff0c\u7136\u540e\u6536\u96c6\u6728\u67f4\u6797\u53f6\u751f\u706b\u53bb\u70e4\uff0c\u5dee\u4e0d\u591a\u987f\u996d\u65f6\u95f4\uff0c\u4f60\u6572\u5f00\u9ec4\u6ce5\uff0c\u5c31\u53ef\u4ee5 \u5403\u5230\u8ddf\u8fd9\u684c\u4e0a\u4e00\u6837\u7f8e\u5473\u7684\u2018\u53eb\u5316\u9e21\u2019\u4e86\uff01\u201d \u5a49\u513f\u7741\u5927\u4e86\u773c\u775b\u9053\uff1a\u201c\u4e5f\u4e0d\u892a\u6bdb\uff1f\u2606\u2018\u9152\u4e10\u201d\u9053\uff1a\u201c\u4e0d\u892a\u6bdb\uff01\u201d \u5a49\u513f\u773c\u775b\u7741\u5f97\u66f4\u5927\u4e86\uff0c\u9053\uff1a\u201c\u4e5f\u4e0d\u6740\uff1f\u201d\u201c\u9152\u4e10\u201d\u9053\uff1a\u201c\u4e0d\u6740\uff01\u201d \u5a49\u513f\u9053\uff1a\u201c\u4e0d\u6d17\uff1f\u201d\u201c\u9152\u4e10\u201d\u9053\uff1a\u201c\u6ca1\u6709\u65f6\u95f4\uff01\u201d \u5a49\u513f\u9053\uff1a\u201c\u90a3\uff0c\u9e21\u809a\u5b50\u91cc\u7684\u4e1c\u897f\uff1f\u2026\u2026\u2606\u2018\u9152\u4e10\u201d\u9053\uff1a\u201c\u5f53\u7136\u662f\u70e4\u5728\u91cc\u8fb9\u4e86\uff01\u201d \u5a49\u513f\u4e00\u6487\uff0c\u53eb\u9053\uff1a\u54ce\u5440\uff01\u810f\u6b7b\u4e86\uff01\u2019\u201c\u9152\u4e10\u201d\u7b11\u9053\uff1a\u201c\u6015\u810f\u7684\u5c31\u6ca1\u6709\u8d44\u683c\u505a\u53eb\u5316\u5b50 \u4e86\uff01\u2026\u2026\u201d \u8fd9\u4e00\u8bf4\uff0c\u5168\u5927\u5385\u7684\u4eba\u90fd\u54c4\u7136\u5927\u7b11\u8d77\u6765\uff01 \u201c\u7aef\u65b9\u516c\u5b50\u201d\u5ffd\u7136\u7ad9\u4e86\u8d77\u6765\uff0c\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u9759\uff01\u9759\uff01\u8bf7\u5927\u5bb6\u9759\u4e00\u4e0b\uff01\u201d \u5927\u5bb6\u7acb\u523b\u6536\u4f4f\u7b11\u58f0\uff0c\u4e00\u9f50\u671b\u7740\u4ed6\u3002\u201c\u7aef\u65b9\u516c\u5b50\u201d\u773c\u775b\u6545\u610f\u671b\u4e86\u671b\u5916\u8fb9\uff01 \u6b64\u65f6\uff0c\u5929\u8272\u5df2\u9ed1\uff0c\u7e41\u661f\u5728\u5c4b\u5fb7\u4e0a\u5411\u4f17\u4eba\u7728\u773c\uff01 \u9662\u5b50\u91cc\u7fa4\u53eb\u5316\u7684\u9152\u5e2d\u65e9\u5c31\u6563\u4e86\uff0c\u4f17\u591a\u7684\u53eb\u5316\uff0c\u5df2\u4e0d\u77e5\u8d70\u5411\u4f55\u5904\uff0c\u53ea\u95e8\u524d\u6709\u4e09\u4e24\u4e2a\u624b\u6267\u9752 \u7af9\u6756\u7684\u53eb\u5316\uff0c\u5f80\u8fd4\u6e38\u8d70\u7740\uff0c\u4f3c\u662f\u8b66\u536b\u4eba\u5458\u3002\u5927\u5385\u91cc\u8fd9\u5341\u6570\u684c\u9152\u5e2d\uff0c\u5374\u5df2\u7531\u5929\u4eae\u5403\u5230\u5929\u9ed1\uff01 \u201c\u7aef\u65b9\u516c\u5b50\u201d\u89c6\u7ebf\u5728\u95e8\u5916\u4e00\u8f6c\uff0c\u7acb\u523b\u53c8\u62c9\u56de\u76ee\u5149\u6295\u5728\u7fa4\u96c4\u8eab\u4e0a\uff0c\u4fe8\u7136\u4ee5\u201c\u76df\u4e3b\u201d\u8eab\u4efd\u53d1 \u8a00\u9053\uff1a\u201c\u4eca\u5929\uff0c\u672c\u516c\u5b50\u7ea6\u8bf7\u8bf8\u4f4d\u5927\u9a7e\u5149\u4e34\uff0c\u627f\u8499\u8bf8\u4f4d\u8d4f\u5149\uff0c\u5927\u8001\u8fdc\u7684\u8d77\u6765\u4e0e\u4f1a\uff0c\u8fd9\u5b9e\u5728\u662f\u672c \u4eba\u7684\u5149\u8363\uff01\u4e5f\u662f\u655e\u6d3e\u2018\u7a77\u5bb6\u5e2e\u2019\u5168\u5e2e\u7684\u5149\u8363\u2026\u2026\u201d \u7a81\u542c\u5c55\u767d\u51b7\u54fc\u4e00\u58f0\uff0c\u4ee5\u624b\u6307\u5728\u9762\u524d\u9152\u676f\u4e2d\u4e00\u6cbd\uff0c\u8fc5\u5373\u5c48\u6307\u5f39\u51fa\uff01\u4e00\u9053\u7834\u7a7a\u7684\u5c16\u5578\uff0c\u5728\u6574 \u5ea7\u5927\u5385\u5185\u7fa4\u96c4\u4e00\u9f50\u60ca\u547c\u58f0\u4e2d\uff0c\u4f20\u51fa\u4e00\u58f0\u95f7\u54fc\uff01\u7d27\u8ddf\u7740\u201c\u7830\u201d\u7684\u4e00\u58f0\u5de8\u54cd1\u2026\u2026 \u7b2c\u4e94\u5341\u56db\u7ae0\u3000\u4e09\u8f83\u7edd\u827a>> \u53e4\u9f99\u300a\u5251\u5ba2\u884c\u300b \u7b2c\u4e94\u5341\u56db\u7ae0\u3000\u4e09\u8f83\u7edd\u827a \u5c55\u767d\u7a81\u4ee5\u201c\u5f39\u6c34\u6210\u4e38\u201d\u7684\u624b\u6cd5\uff0c\u5c06\u6570\u6ef4\u9152\u5411\u7a97\u5916\u5f39\u53bb\uff01 \u73b0\u5728\u5c55\u767d\u8d2f\u901a\u4e86\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\u7edd\u5b66\uff0c\u5185\u529b\u6fc0\u589e\uff0c\u51e0\u6ef4\u9152\u4ee5\u6307\u5f39\u51fa\uff0c\u7adf\u7136\u88ab\u98ce\u55e1 \u9e23\uff01 \u8fd9\u4efd\u529f\u529b\uff0c\u7684\u786e\u53ef\u4f7f\u7fa4\u96c4\u819b\u76ee\uff01 \u9152\u4e38\u7834\u7a97\u800c\u51fa\uff0c\u7a97\u5916\u4f20\u6765\u4e00\u58f0\u95f7\u54fc\uff01 \u5927\u5385\u5185\u7fa4\u96c4\u4e00\u4e71\u2026\u2026 \u201c\u9152\u4e10\u201d\u3001\u201c\u75af\u4e10\u201d\u4e0d\u7ea6\u800c\u540c\uff0c\u53cc\u624b\u4e00\u6309\u684c\u9762\uff0c\u8eab\u5f62\u7535\u5c04\u800c\u8d77\uff0c\u534a\u7a7a\u4e2d\u4e00\u638c\u9707\u5f00\u7a97\u6846\uff0c \u4e24\u6761\u8eab\u5f71\u5982\u7070\u9e64\u6a2a\u7a7a\uff0c\u7fe9\u7136\u7a7f\u51fa\u7a97\u5916\uff01 \u7a97\u5916\u4f20\u6765\u8f70\u7136\u4e24\u58f0\u5927\u9707\uff01 \u53ea\u9707\u5f97\u7a97\u788e\u58c1\u6447\uff0c\u63a5\u7740\u4f20\u6765\u201c\u9152\u4e10\u201d\u3001\u201c\u75af\u4e10\u201d\u4e8c\u4eba\u7684\u6012\u53f1\u4e4b\u58f0\uff01 \u201c\u670b\u53cb\u662f\u54ea\u9053\u800c\u6765\uff1f\u7adf\u7136\u6562\u5230\u7a77\u4eba\u7956\u5e08\u5802\u6765\u6492\u91ce\uff01\u201d \u4e00\u9635\u9707\u5929\u8c6a\u7b11\u664c\u8d77\uff01 \u7b11\u58f0\u4e00\u4f4d\uff0c\u542c\u5230\u4e00\u79cd\u4f3c\u662f\u4ece\u5730\u72f1\u91cc\u6b27\u51fa\u6765\u7684\u5bd2\u98ce\u4e00\u6837\u9634\u68ee\u7684\u8bed\u8c03\u8bf4\u9053\uff1a\u201c\u6765\u9053\u800c\u6765\uff01\u4e2d \u539f\u6b66\u6797\u4ff1\u5df2\u79f0\u81e3\uff0c\u96be\u9053\u4f60\u4eec\u8fd9\u4e9b\u5403\u5269\u83b1\u7684\u5b59\u5b50\u4eec\uff0c\u8fd8\u6562\u624e\u523a\u5417\uff1f\u201d \u4f17\u4eba\u4e00\u542c\uff0c\u7eb7\u7eb7\u7eb5\u8eab\u63a0\u51fa\u5385\u5916\uff01 \u661f\u6708\u5149\u8f89\u4e4b\u4e0b\uff0c\u9662\u843d\u91cc\u4e0e\u201c\u98ce\u5c18\u4e09\u4e10\u201d\u5bf9\u9762\u800c\u7acb\uff0c\u4e00\u5b57\u6392\u5f00\uff0c\u6709\u56db\u4e2a\u8001\u4eba\uff01 \u4e00\u4e2a\u8499\u4eba\u88c5\u675f\uff0c\u9752\u888d\u5b50\uff0c\u7ea2\u574e\u80a9\uff0c\u811a\u767b\u8e22\u6b7b\u4e2d\u822c\u5c16\u5927\u76ae\u978b\uff0c\u751f\u5f97\u51f6\u7709\u6076\u773c\uff0c\u624b\u91cc\u63d0\u7740\u4e00 \u6302\u5ff5\u73e0\u3002 \u4e00\u4e2a\u8eab\u9ad8\u8180\u9614\uff0c\u795e\u6001\u975e\u5e38\u5a01\u731b\uff0c\u77fe\u987b\u7ed5\u988a\uff0c\u4e00\u8138\u7684\u6a2a\u8089\uff0c\u79c3\u8111\u95e8\u5728\u661f\u6708\u4ea4\u8f89\u4e4b\u4e0b\uff0c\u9690\u9690 \u653e\u5149\uff0c\u8eab\u4e0a\u7a7f\u7684\u5374\u662f\u4e00\u4ef6\u7f00\u9886\u7684\u5bbd\u5927\u957f\u888d\uff01 \u6328\u4e0b\u53bb\u662f\u4e00\u4e2a\u9762\u76ee\u7559\u9ed1\u7684\u5e72\u67af\u5c0f\u8001\u5934\uff0c\u4eba\u751f\u5f97\u77ee\uff0c\u800c\u4e14\u7f29\u80a9\u9a7c\u80cc\uff0c\u4e00\u9897\u8111\u888b\u751f\u5f97\u751a\u5c0f\uff0c \u4f46\u53cc\u8033\u5374\u5927\u5f97\u51fa\u5947\uff0c\u771f\u8f6e\u51e0\u4e4e\u8fc7\u9876\uff0c\u7a7f\u4e86\u4e00\u88ad\u711a\u8863\uff0c\u5c31\u50cf\u591a\u5e74\u9648\u4ed3\u91cc\u5927\u8001\u9f20\u6210\u7cbe\uff0c\u88ab\u4e86\u4e00\u4ef6 \u4eba\u8863\u4e00\u822c\u3002 \u8fd9\u50cf\u8c8c\u5982\u9f20\u7684\u8001\u4eba\u591c\u8111\u95e8\u6b63\u4e2d\u957f\u4e86\u4e00\u4e2a\u5927\u7624\uff0c\u4e0d\u77e5\u662f\u5929\u751f\u5982\u6b64\uff1f\u8fd8\u662f\u8d70\u8def\u4e0d\u5c0f\u5fc3\u5728\u95e8\u6846 \u78b0\u7684\uff1f \u6700\u540e\u4e00\u540d\uff0c\u9e70\u9f3b\u9e21\u773c\uff0c\u795e\u8272\u5341\u5206\u9634\u9e33\uff0c\u8138\u8272\u7070\u767d\uff0c\u72b9\u5982\u8d25\u9769\uff0c\u5782\u624b\u7ad9\u7acb\u4e00\u8fb9\uff0c\u4e0d\u8a00\u4e0d \u52a8\uff0c\u4eff\u4f5b\u4e00\u5177\u50f5\u5c38\uff01 \u770b\u8fd9\u56db\u4eba\u7684\u957f\u50cf\uff0c\u5df2\u7ecf\u662f\u5c71\u6d77\u4e0d\u7ecf\uff0c\u4e11\u602a\u5f97\u523a\u773c\uff0c\u518d\u542c\u6240\u8bf4\u7684\u8bdd\uff0c\u66f4\u662f\u72c2\u50b2\u7edd\u4f26\uff01 \u800c\u4e14\u4e00\u4e2a\u4e2a\u53cc\u773c\u795e\u5149\u66b4\u5c04\uff0c\u770b\u6837\u5b50\u90fd\u662f\u5185\u5916\u517c\u4fee\u7684\u6b66\u529f\u9ad8\u201c\u9152\u4e10\u201d\u4e0e\u201c\u75af\u4e10\u201d\u4e8c\u4eba\u95ef\u51fa \u7a97\u5916\u65f6\uff0c\u5df2\u9886\u6559\u4e86\u5bf9\u65b9\u7684\u638c\u529b\uff0c\u53ea\u89c9\u5bf9\u65b9\u638c\u529b\u5927\u5f97\u51fa\u5947\uff0c\u5404\u81ea\u63a5\u4e86\u4e00\u638c\uff0c\u53ea\u89c9\u6c14\u7ffb\u8840\u6d8c\uff0c\u8eab \u5f62\u843d\u5730\u8c03\u606f\u4e86\u4e00\u9635\u5b50\uff0c\u4ecd\u611f\u4e94\u810f\u751f\u75bc\uff0c\u597d\u5728\u4ed7\u7740\u5927\u63f4\u5728\u540e\uff0c\u5e76\u672a\u6c14\u9981\u3002 \u201c\u75af\u4e10\u201d\u4ef0\u5929\u6253\u4e86\u4e2a\u54c8\u54c8\uff0c\u9053\uff1a\u201c\u670b\u53cb\u65e2\u662f\u4e13\u4e3a\u6211\u4eec\u7a77\u4eba\u800c\u6765\uff0c\u53ef\u5426\u62a5\u4e0a\u4e2a\u4e07\u513f\u6765\uff1f\u4e5f \u8ba9\u6211\u4eec\u7a77\u4eba\u957f\u957f\u89c1\u8bc6\uff1f\u201d \u7eb5\u987b\u7ed5\u988a\u7684\u9ad8\u5927\u8001\u8005\uff0c\u4ee5\u6781\u4e3a\u5351\u89c6\u7684\u773c\u5149\u77a5\u4e86\u201c\u75af\u4e10\u201d\u4e00\u773c\u9053\uff1a\u8fde\u6211\u4eec\u8001\u54e5\u513f\u56db\u4e2a\u90fd\u4e0d \u8ba4\u8bc6\uff0c\u53ef\u89c1\u4f60\u4eec\u8fd9\u4e9b\u7a77\u8981\u996d\u7684\u90fd\u662f\u4e95\u5e95\u4e4b\u86d9\uff01\u201d \u201c\u9152\u4e10\u201d\u63a5\u53e3\u9053\uff1a\u201c\u4e95\u5e95\u4e4b\u86d9\u4e5f\u7f62\uff0c\u6d77\u5e95\u4e4b\u86d9\u4e5f\u7f62\uff0c\u597d\u6b79\u62a5\u51fa\u4e2a\u540d\u5b57\u6765\uff0c\u8fd9\u822c\u541e\u541e\u5410\u5410 \u83ab\u975e\u662f\u540d\u5b57\u89c1\u4e0d\u5f97\u4eba\uff1f\u201d \u7626\u957f\u9634\u9e37\u7684\u8001\u8005\uff0c\u53cc\u76ee\u9661\u7741\uff0c\u4e24\u9053\u78a7\u7eff\u7684\u76ee\u5149\u66b4\u5c04\u5982\u7535\uff0c\u563f\u563f\u9634\u7b11\u9053\uff1a\u201c\u6211\u8001\u4eba\u5bb6\u7684 \u540d\uff0c\u4e0d\u5165\u6d3b\u4eba\u8033\u6735\uff0c\u542c\u5230\u627e\u8001\u4eba\u5bb6\u7684\u540d\u4e4b\u65f6\uff0c\u4fbf\u662f\u4f60\u7b49\u4e27\u547d\u4e4b\u65e5\uff01\u201d \u201c\u75af\u4e10\u201d\u3001\u201c\u9152\u4e10\u201d\u540c\u65f6\u4e00\u6652\uff0c\u9053\uff1a\u201c\u591c\u98ce\u5927\uff0c\u522b\u95ea\u4e86\u820c\u5934\u2026\u3002\u3002\u201d \u7626\u957f\u9634\u9e37\u8001\u8005\u7a81\u7136\u6253\u65ad\u4e86\u75af\u3001\u9152\u4e8c\u4e10\u7684\u8bdd\uff0c\u4ece\u7259\u7f1d\u91cc\u5d29\u51fa\u6765\u56db\u4e2a\u5b57\uff1a\u201c\u8471\u5cad\u4e4b\u9e70\uff01\u201d \u9ad8\u5927\u5a01\u731b\u3001\u5c31\u987b\u7ed5\u988a\u7684\u8001\u8005\uff0c\u63a5\u53e3\u9053\uff1a\u9634\u5c71\u4e4b\u72fc\uff01\u201d \u9762\u76ee\u9ee7\u9ed1\u7684\u67af\u5e72\u5c0f\u8001\u5934\u9053\uff1a\u201c\u592a\u4ed3\u4e4b\u9f20\uff01\u201d \u8499\u4eba\u88c5\u675f\u7684\u8001\u8005\u4e5f\u63a5\u53e3\u9053\uff1a\u201c\u6c99\u6f20\u4e4b\u72d0\uff01\u201d \u201c\u75af\u4e10\u201d\u4ef0\u5929\u5927\u7b11\u9053\uff1a\u5927\u8bdd\u5439\u5f97\u5c71\u54cd\uff0c\u8bf4\u4e86\u534a\u5929\uff0c\u4e0d\u8fc7\u662f\u4e9b\u72d0\u9f20\u4e4b\u8f88\u2026\u2026\u201d \u201c\u75af\u4e10\u201d\u8bdd\u672a\u8bf4\u5b8c\uff0c\u81ea\u79f0\u4e3a\u201c\u592a\u4ed3\u4e4b\u9f20\u201d\u7684\u5e72\u67af\u5c0f\u8001\u5934\u53cc\u8033\u4e00\u8038\uff0c\u7a81\u7136\u5389\u53f1\u9053\uff1a\u201c\u81ed\u8981 \u996d\u7684\u627e\u6b7b\uff01\u201d \u201c\u6b7b\u201d\u5b57\u51fa\u53e3\uff0c\u53cc\u624b\u4e00\u7ffb\uff0c\u638c\u80cc\u671d\u5916\uff0c\u6b63\u53cd\u9762\u638c\uff0c\u731b\u5411\u201c\u75af\u4e10\u201d\u53cc\u988a\u5288\u6765\uff01 \u201c\u75af\u4e10\u201d\u9661\u559d\u4e00\u58f0\uff1a\u201c\u6765\u5f97\u597d\uff01\u201d \u4e00\u5f0f\u201c\u53eb\u5316\u56db\u65b9\u201d\uff0c\u53cc\u638c\u5411\u6765\u52bf\u8fce\u53bb\uff01 \u201c\u556a\uff01\u556a\uff01\u201d\u4e24\u58f0\u66b4\u54cd\uff0c\u201c\u75af\u4e10\u201d\u5f53\u573a\u88ab\u9707\u9000\u4e94\u6b65\uff01 \u4f17\u4eba\u9f50\u5403\u4e00\u60ca\uff0c\u4ee5\u201c\u75af\u4e10\u201d\u7684\u529f\u529b\u6765\u8bf4\uff0c\u8eab\u5217\u201c\u7a77\u5bb6\u5e2e\u201d\u4e09\u5927\u957f\u8001\u4e4b\u4e00\uff0c\u582a\u79f0\u4e3a\u6b66\u529f\u4e00 \u6d41\u9ad8\u624b\uff0c\u6ca1\u60f3\u5230\u88ab\u8fd9\u4e2a\u79f0\u4e3a\u201c\u592a\u4ed3\u4e4b\u9f20\u201d\u7684\u7626\u5e72\u6854\u8001\u5934\uff0c\u968f\u624b\u4e00\u6325\uff0c\u4fbf\u9707\u9000\u4e94\u6b65\uff01 \u4e8b\u5b9e\u4e0a\uff0c\u8fd9\u7a81\u7136\u73b0\u8eab\u7684\u56db\u4e2a\u5f62\u8c8c\u602a\u5f02\u7684\u8001\u5934\u4e00\u62a5\u51fa\u540d\u5b57\uff0c\u4e2d\u8f7b\u4e00\u8f88\u7684\u8fd8\u4e0d\u77e5\u9053\u5389\u5bb3\uff0c\u4f46 \u8001\u4e00\u8f88\u7684\u4eba\u7269\uff0c\u5fc3\u4e2d\u60e7\u81ea\u4e00\u9707\uff01 \u56e0\u4e3a\u8fd9\u56db\u4e2a\u540d\u5b57\uff0c\u867d\u5728\u6c5f\u6e56\u4e0a\u5df2\u6709\u4e8c\u4e09\u5341\u5e74\u6ca1\u542c\u4eba\u63d0\u8fc7\uff0c\u4f46\u5728\u4e09\u5341\u4e2d\u524d\u5374\u662f\u9707\u60ca\u6b66\u6797\u7684 \u56db\u4e2a\u90aa\u9053\u9ad8\u624b\u3002 \u542c\u5176\u8bdd\u4e2d\u4e4b\u610f\uff0c\u663e\u7136\u56db\u4e2a\u9b54\u5934\u5df2\u4e0e\u201c\u5357\u6d77\u95e8\u201d\u5408\u4e86\u4f19\uff1a \u8fd9\u4e00\u6765\uff0c\u201c\u5357\u6d77\u95e8\u201d\u65e0\u5f62\u4e2d\u53c8\u589e\u52a0\u4e86\u5b9e\u529b\uff0c\u800c\u4e14\u4f9d\u7167\u56db\u4e2a\u9b54\u5934\u5f53\u4e2d\u6240\u4f5c\u6240\u4e3a\u770b\u6765\uff0c\u6bd2\u8fa3 \u9634\u72e0\u6bd4\u201c\u5357\u6d77\u95e8\u201d\u6709\u8fc7\u4e4b\u800c\u65e0\u4e0d\u53ca\u3002\u4e2d\u539f\u6b66\u6797\u4eba\u58eb\u7684\u547d\u8fd0\uff0c\u66f4\u662f\u4e0d\u582a\u8bbe\u60f3\uff01 \u6240\u4ee5\uff0c\u65e0\u4e0d\u7d27\u7559\u53cc\u7709\uff0c\u6697\u6697\u62c5\u5fe7\u2026\u2026 \u5c31\u5728\u7fa4\u96c4\u5fc3\u601d\u7535\u8f6c\u4e4b\u9645\uff0c\u773c\u7741\u7741\u89c1\u201c\u592a\u4ed3\u4e4b\u9f20\u201d\u4e8c\u638c\u628a\u201c\u75af\u4e10\u201d\u9707\u9000\uff0c\u4e0d\u7ba1\u201c\u75af\u4e10\u201d\u5728 \u4e00\u8fb9\u9f13\u7740\u773c\u775b\u53d1\u695e\uff0c\u4ed6\u4e0a\u524d\u4e00\u6b65\uff0c\u53cc\u638c\u5728\u80f8\u524d\u4e00\u7acb\uff0c\u8de8\u9a6c\u8e72\u6321\uff0c\u5411\u4f17\u4eba\u626c\u58f0\u53eb\u9053\uff1a\u201c\u65b9\u624d\u662f \u54ea\u4e2a\u5c0f\u5b50\uff0c\u7528\u7eff\u8c46\u62db\u547c\u8001\u592b\uff1f\u8fd8\u4e0d\u51fa\u6765\u53d7\u6b7b\uff01\u201d \u539f\u6765\u4ed6\u8111\u95e8\u4e0a\u90a3\u4e00\u4e2a\u5927\u75a4\uff0c\u662f\u88ab\u5c55\u767d\u201c\u4ee5\u62ac\u5f39\u9152\u201d\u6254\u2019\u4f24\u7684\uff0c\u4ed6\u5374\u4ee5\u4e3a\u4eba\u5bb6\u662f\u7528\u7684\u7eff \u8c46\uff0c\u800c\u4e14\uff0c\u81f3\u4eca\u8fd8\u4e0d\u77e5\u662f\u88ab\u8c01\u6253\u7684\uff0c\u6545\u800c\u51fa\u9762\u559d\u95ee\u3002 \u5c55\u767d\u5fae\u5fae\u4e00\u7b11\uff0c\u4ece\u4eba\u7fa4\u4e2d\u7f13\u7f13\u5b9a\u4e86\u51fa\u6765\uff0c\u9053\uff1a\u201c\u533a\u533a\u5c55\u767d\uff0c\u4f46\u90a3\u4e0d\u662f\u7eff\u8c46\uff0c\u800c\u662f\u9694\u7a97\u5411 \u8001\u524d\u8f88\u656c\u7684\u4e00\u6ef4\u6c34\u9152\uff01\u201d \u201c\u592a\u4ed3\u4e4b\u9f20\u201d\u542c\u51fa\u5c55\u767d\u8bed\u542b\u8c03\u4f83\uff0c\u84e6\u7136\u66b4\u6012\uff0c\u628a\u8fd0\u81f3\u5dc5\u5cf0\u7684\u638c\u529b\uff0c\u7ffb\u624b\u4e00\u7529\uff0c\u4ecd\u7136\u7528 \u201c\u53cd\u80cc\u638c\u201d\uff0c\u638c\u80cc\u5411\u5916\uff0c\u731b\u5411\u5c55\u767d\u8fce\u9762\u5288\u6765\uff01 \u8fd9\u79cd\u820d\u638c\u5fc3\u4e0d\u7528\uff0c\u800c\u4ee5\u638c\u80cc\u653b\u654c\uff0c\u4e0e\u4e00\u822c\u6b66\u6797\u540d\u5bb6\u7684\u638c\u6cd5\u5927\u76f8\u5f84\u5ead\u3002\u4f46\u4ed6\u638c\u529b\u5288\u51fa\uff0c\u9510 \u5578\u4e2d\u9e23\uff0c\u529b\u9053\u7ade\u662f\u5927\u5f97\u60ca\u4eba\uff01 \u663e\u89c1\u4ed6\u662f\u79ef\u6012\u800c\u53d1\uff0c\u6bd4\u521a\u624d\u9707\u9000\u201c\u75af\u4e10\u201d\u7684\u4e00\u638c\uff0c\u66f4\u52a0\u4e86\u51e0\u6210\u529b\u9053\uff0c\u60f3\u4e00\u638c\u628a\u5c55\u767d\u7acb\u6bd9 \u638c\u4e0b\uff01 \u5c55\u767d\u770b\u51fa\u4ed6\u638c\u529b\u5947\u5927\uff0c\u4e0d\u5728\u201c\u6d77\u5916\u4e09\u715e\u201d\u4e4b\u4e0b\uff0c\u4f46\u5c55\u81ea\u6709\u5fc3\u715e\u715e\u5bf9\u65b9\u7684\u9510\u6c14\uff0c\u201c\u5929\u4f5b\u964d \u9b54\u638c\u201d\u8fd0\u4e86\u4e03\u6210\u529f\u529b\uff0c\u8f7b\u63cf\u6de1\u5199\u8fce\u7740\u5bf9\u65b9\u638c\u529b\u62cd\u53bb\uff01 \u201c\u7830\uff01\u201d \u4e00\u58f0\u66b4\u54cd\uff0c\u4f59\u529b\u56db\u5c04\uff0c\u5c18\u6284\u98de\u626c\uff01 \u8fd9\u56de\u5374\u662f\u201c\u592a\u4ed3\u4e4b\u9f20\u201d\u88ab\u9707\u9000\u4e86\u4e94\u6b65\uff01 \u5c55\u767d\u72b9\u5982\u7389\u6811\u65bd\u98ce\uff0c\u7ad9\u5728\u90a3\u91cc\u7eb9\u4e1d\u672a\u52a8\u3002 \u53ea\u56e0\u4ed6\u91ce\u5fc3\u52c3\u52c3\uff0c\u5b8c\u5168\u6ca1\u6709\u5f80\u90a3\u4e00\u65b9\u9762\u60f3\uff0c\u53ea\u77e5\u5403\u4e86\u5c55\u767d\u7684\u4e8f\uff0c\u4fbf\u8981\u62a5\u590d\u3002\u4e00\u638c\u88ab\u5c55\u767d \u9707\u9000\uff0c\u4e0d\u7531\u66f4\u5f62\u6fc0\u6012\uff0c\u7a0d\u4e3a\u695e\u4e86\u4e00\u4e0b\uff0c\u7acb\u523b\u8fd0\u8d77\u66f4\u4e3a\u6b79\u6bd2\u7684\u4e00\u79cd\u6b66\u529f\uff0c\u540d\u4e3a\u201c\u9b54\u529f\u201d\uff0c\u771f\u529b \u8d2f\u6ce8\u4e4b\u4e0b\uff0c\u53ea\u542c\u4ed6\u5468\u8eab\u9aa8\u9ab8\u201c\u5427\u5427\u201d\u4e00\u9635\u66b4\u54cd\uff0c\u8eab\u5f62\u987f\u7136\u77ee\u4e0b\u53bb\u4e86\u534a\u622a\uff01 \u4ed6\u8eab\u91cf\u672c\u5c31\u4e0d\u9ad8\uff0c\u8fd9\u5f80\u4e0b\u4e00\u7f29\uff0c\u8eab\u9ad8\u5df2\u4e0d\u53ca\u4e09\u5c3a\uff0c\u4f46\u4e24\u6761\u624b\u81c2\u5374\u4e00\u5c3a\uff0c\u8fd9\u4e00\u6765\uff0c\u4ed6\u7684\u624b \u81c2\u4e5f\u6709\u4e09\u5c3a\u957f\u4e86\uff0c\u800c\u4e14\u6574\u6761\u624b\u81c2\u5374\u53d8\u6210\u4e86\u9ed1\u8272\uff0c\u8eab\u77ee\u4e09\u5c3a\uff0c\u52a0\u4e0a\u4ed6\u90a3\u526f\u5c0a\u5bb9\uff0c\u9762\u76ee\u9ee7\u9ed1\uff0c\u53cc \u8033\u8fc7\u9876\uff0c\u8fd9\u5012\u4e0d\u50cf\u9f20\uff0c\u800c\u50cf\u4e00\u4e2a\u5927\u9a6c\u7334\u4e86\uff01 \u4ed6\u9a6c\u6b65\u4e00\u632b\uff0c\u7279\u957f\u7684\u624b\u81c2\u534a\u5c48\u534a\u4f38\uff0c\u53cc\u624b\u4f3c\u63e1\u4f3c\u62e2\uff0c\u56e0\u771f\u529b\u8fd0\u81f3\u98a0\u5cf0\u7684\u5173\u7cfb\uff0c\u4e24\u53ea\u5c0f\u5706 \u773c\u7cbe\u5149\u5982\u70ac\uff0c\u9762\u76ee\u9634\u68ee\u5730\u7f13\u7f13\u5411\u5c55\u767d\u6b3a\u8fd1\uff0c\u5f62\u72b6\u9a87\u4eba\u5df2\u6781\uff01 \u4f17\u4eba\u83ab\u4e0d\u5403\u60ca\uff0c\u5c55\u767d\u89c1\u5230\u8fd9\u822c\u5149\u666f\uff0c\u5fc3\u4e2d\u4e5f\u4e0d\u7531\u6697\u6697\u6253\u9f13\uff0c\u636e\u6240\u77e5\uff1a\u5185\u5bb6\u9ad8\u624b\uff0c\u771f\u529b\u8d2f \u6ce8\u5747\u662f\u624b\u811a\u6da8\u5927\uff0c\u5374\u4ece\u672a\u89c1\u8fc7\u8eab\u5f62\u53cd\u89c1\u7f29\u5c0f\u7684\uff0c\u65e2\u4e0d\u77e5\u8fd9\u4e8e\u67af\u5c0f\u8001\u5934\u8fd0\u7684\u662f\u4ec0\u4e48\u529f\uff1f\u66f4\u4e0d\u77e5 \u5176\u5389\u5bb3\u5982\u4f55\uff1f\u4e00\u8fb9\u5fc3\u4e2d\u6697\u61d4\uff0c\u4e00\u8fb9\u7acb\u628a\u201c\u5929\u4f5b\u964d\u9b54\u201d\u638c\u529b\u8fd0\u81f3\u5341\u6210\uff0c\u9a6c\u6b65\u624e\u7a33\uff0c\u5168\u795e\u6212 \u5907\u2026\u2026 \u5e7f\u5927\u5ead\u9662\u91cc\u4e00\u4e8c\u767e\u4eba\uff0c\u4e14\u5747\u662f\u6b66\u6797\u77e5\u540d\u4e4b\u58eb\uff0c\u6b64\u65f6\u9e26\u96c0\u65e0\u58f0\uff0c\u53cc\u773c\u5747\u671b\u5b9a\u5c55\u767d\u4e0e\u90a3\u201c\u592a \u4ed3\u4e4b\u9f20\u201d\uff0c\u77aa\u76ee\u770b\u8fd9\u77f3\u7834\u5929\u60ca\u7684\u4e00\u640f\uff01 \u661f\u6708\u5728\u5929\uff0c\u591c\u9759\u5982\u6c34\uff0c\u53ea\u6709\u5fae\u98ce\u7a7f\u8fc7\u6811\u68a2\u7684\u8f7b\u54cd\u3002\u6709\u8c01\u77e5\u5728\u8fd9\u591c\u9759\u66f4\u6df1\u7684\u5e7f\u5927\u5ead\u9662\u91cc\uff0c \u5c06\u6709\u4e00\u573a\u6076\u6218\u8981\u53d1\u751f\u5462\u2026\u2026 \u201c\u4e14\u4efb\uff01\u201d \u5c31\u5728\u8fd9\u7d27\u5f20\u5f97\u5982\u5f13\u5f15\u6ee1\u9f99\u4e4b\u9645\uff0c\u7a81\u7136\u90a3\u9ad8\u5927\u5a01\u731b\u3001\u7eb5\u987b\u7ed5\u988a\u7684\u201c\u9634\u5c71\u4e4b\u72fc\u201d\u6a2a\u624b\u62e6\u4f4f\u4e86 \u201c\u592a\u4ed3\u4e4b\u9f20\u201d\u6bb4\u524d\u8fdb\u4e4b\u52bf\uff0c\u5e76\u5411\u201c\u592a\u4ed3\u4e4b\u9f20\u201d\u505a\u4e86\u4e2a\u773c\u8272\uff0c\u7136\u540e\u9762\u5bf9\u7fa4\u96c4\u9053\u3002\u201d\u563f\u563f\uff0c\u6211\u4eec \u8001\u54e5\u513f\u56db\u4e2a\u2018\u9ed1\u9053\u56db\u51f6\u2019\uff0c\u60f3\u8bf8\u4f4d\u4e5f\u8bb8\u6709\u4e2a\u8033\u95fb\uff0c\u4eca\u591c\u4ee3\u8868\u2018\u5357\u6d77\u95e8\u2019\uff0c\u4e0e\u8bf8\u4f4d\u63a5\u5934\uff0c\u82e5\u662f \u8bf8\u4f4d\u80af\u8d4f\u9762\u5b50\uff0c\u52a0\u76df\u2018\u5357\u6d77\u95e8\u2019\uff0c\u90a3\u4e48\uff0c\u54b1\u4eec\u662f\u4e00\u6761\u7ebf\u4e0a\u670b\u53cb\uff0c\u4e07\u4e8b\u592a\u5e73\u3002\u5047\u5982\uff0c\u8bf8\u4f4d\u8ba4\u4e3a \u6211\u4eec\u8001\u54e5\u513f\u56db\u4e2a\u9762\u5b50\u4e0d\u591f\u5927\uff0c\u4e0d\u80af\u8d4f\u5149\uff0c\u54b1\u4eec\u662f\u53e6\u4f5c\u522b\u8c08\uff01\u8bf8\u4f4d\u4e4b\u4e2d\uff0c\u8c01\u662f\u9f99\u5934\uff1f\u51fa\u9762\u7b54\u4e2a \u8bdd\u513f\u5427\uff01\u201d \u8fd9\u8bdd\u8868\u9762\u4e0a\u8bf4\u5f97\u5ba2\u6c14\uff0c\u9aa8\u5b50\u91cc\u5374\u662f\u786c\u5f97\u5f88\uff0c\u65e0\u5f02\u662f\u5411\u7fa4\u96c4\u4e0b\u4e86\u62db\u964d\u4e66\u3002 \u6b64\u65f6\u201c\u7aef\u65b9\u516c\u5b50\u201d\u8eab\u4e3a\u201c\u7a77\u5bb6\u5e2e\u201d\u638c\u95e8\uff0c\u53c8\u81ea\u627f\u662f\u53ec\u96c6\u6b64\u6b21\u96c6\u4f1a\u7684\u4e3b\u6301\u4eba\uff0c\u867d\u7136\u770b\u51fa\u56db \u4eba\u6765\u610f\u4e0d\u5584\uff0c\u800c\u4e14\u58f0\u52bf\u8feb\u4eba\uff0c\u4f46\u4e8b\u60c5\u6324\u5230\u5934\u9876\u4e0a\uff0c\u53ef\u8bf4\u4e0d\u4e0a\u4e0d\u7b97\u6765\uff0c\u53ea\u6709\u786c\u7740\u5934\u76ae\uff0c\u6392\u4f17\u800c \u51fa\uff0c\u5fc3\u91cc\u76f4\u649e\u949f\uff0c\u8868\u9762\u4e0a\u5374\u529b\u6301\u9547\u9759\uff0c\u6602\u7136\u9053\uff1a\u5728\u4e0b\u2018\u7aef\u65b9\u516c\u5b50\u2019\uff0c\u2018\u7a77\u5bb6\u5e2e\u2019\u73b0\u4efb\u638c\u95e8\uff0c \u8d35\u5ba2\u6709\u4ec0\u4e48\u89c1\u6559\uff1f\u5728\u4e0b\u9759\u8046\u9ad8\u89c1\uff01\u201d \u201c\u9634\u5c71\u4e4b\u72fc\u201d\u89c1\u51fa\u9762\u7b54\u8bdd\u7684\u4ec5\u662f\u4e00\u4e2a\u4fca\u79c0\u5c11\u5e74\uff0c\u55e4\u5730\u4e00\u7b11\uff0c\u7528\u624b\u8f6e\u6307\u7fa4\u96c4\uff0c\u5411\u201c\u7aef\u65b9\u516c \u5b50\u201d\u9053\uff1a\u201c\u4f60\u80fd\u4ee3\u8868\u6240\u6709\u5728\u573a\u4e4b\u4eba\u5417\uff1f\u201d \u8bdd\u4e2d\u4e4b\u610f\uff0c\u663e\u7136\u6709\u70b9\u5351\u89c6\u7684\u5473\u9053\u3002 \u201c\u7aef\u65b9\u516c\u5b50\u201d\u8138\u4e00\u7ea2\uff0c\u8fc5\u901f\u56de\u5934\u5411\u7fa4\u96c4\u77a5\u4e86\u4e00\u773c\uff0c\u8bf4\u8001\u5b9e\u8bdd\uff0c\u4ed6\u6ca1\u6709\u628a\u63e1\u80fd\u4ee3\u8868\u5f97\u4e86\u5728 \u573a\u4e4b\u4eba\uff0c\u5c24\u5176\u96f7\u5927\u53d4\u4e0e\u5c55\u767d\u7b49\u4eba\uff0c\u9002\u9022\u5176\u4f1a\uff0c\u5e76\u4e0d\u662f\u63a5\u5230\u4ed6\u201c\u7a77\u5bb6\u5e2e\u201d\u6b66\u6797\u5e16\u624d\u6765\u7684\u3002 \u59cb\u7ec8\u7ad9\u5728\u4e00\u8fb9\u642d\u62c9\u7740\u773c\u76ae\u3001\u5f62\u5982\u50f5\u5c38\u7684\u201c\u8471\u5cad\u4e4b\u9e70\u201d\u6b64\u65f6\u7a81\u7136\u7b54\u4e86\u8bdd\uff0c\u53ea\u542c\u4ed6\u9634\u68ee\u68ee\u5730 \u9053\uff1a\u201c\u6717\u5144\uff01\u4e0d\u8981\u5c0f\u77a7\u4eba\u5bb6\u5a03\u513f\u5e74\u8f7b\uff0c\u4eba\u5bb6\u5a03\u513f\u53ef\u662f\u4e2d\u539f\u6b66\u6797\u9f0e\u9f0e\u5927\u540d\u7684\u2018\u56db\u5927\u516c\u5b50\u2019\u4e4b \u4e00\uff0c\u8bf4\u51fa\u8bdd\u6765\uff0c\u7167\u6837\u9e21\u6bdb\u53ef\u4ee5\u5f53\u4f5c\u4ee4\u7bad\uff01\u201d \u6b64\u8a00\u4e00\u51fa\uff0c\u201c\u9ed1\u9053\u56db\u51f6\u201d\u540c\u65f6\u54c8\u54c8\u5927\u7b11\u8d77\u6765\uff01 \u201c\u7aef\u65b9\u516c\u5b50\u201d\u4fca\u8138\u7f9e\u7ea2\u5f97\u5982\u5927\u7ea2\u5e03\u4e00\u822c\uff0c\u53ef\u5c31\u662f\u5f20\u53e3\u7ed3\u820c\u5730\u7b54\u4e0d\u51fa\u8bdd\u6765\u3002 \u201c\u7965\u9e9f\u516c\u5b50\u201d\u3001\u201c\u5b89\u4e50\u516c\u5b50\u201d\u3001\u201c\u51cc\u98ce\u516c\u5b50\u201d\u89c1\u5230\u201c\u7aef\u65b9\u516c\u5b50\u201d\u5403\u762a\uff0c\u53c8\u89c1\u5bf9\u65b9\u628a\u201c\u6b66 \u6797\u56db\u516c\u5b50\u201d\u4e00\u9f50\u6316\u82e6\u4e0a\uff0c\u540c\u65f6\uff0c\u76ee\u524d\u4ed6\u4eec\u56db\u516c\u5b50\u662f\u654c\u60c5\u540c\u4ec7\uff0c\u4e0d\u7ea6\u800c\u540c\uff0c\u9f50\u4e0a\u524d\u8de8\u4e86\u4e24\u6b65\uff0c \u201c\u51cc\u98ce\u516c\u5b50\u201d\u62a2\u5148\u8bf4\u9053\uff1a\u201c\u56db\u4f4d\u6709\u4ec0\u4e48\u771f\u7ae0\uff0c\u6446\u51fa\u6765\u4e5f\u5c31\u662f\u4e86\uff01\u4f55\u5fc5\u5149\u5728\u5634\u76ae\u4e0a\u901e\u80fd\uff1f\u201d \u201c\u9634\u5c71\u4e4b\u72fc\u201d\u90ce\u96c4\uff0c\u8bb8\u4e45\u624d\u6b62\u4f4f\u4e86\u7b11\u58f0\uff0c\u9053\uff1a\u201c\u5a03\u513f\uff01\u4f60\u4e5f\u662f\u9f0e\u9f0e\u5927\u540d\u7684\u2018\u6b66\u6797\u56db\u516c \u5b50\u2019\u4e4b\u4e00\u5427\uff1f\u201d \u201c\u7965\u9e9f\u516c\u5b50\u201d\u3001\u201c\u5b89\u4e50\u516c\u5b50\u201d\u540c\u65f6\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6b66\u6797\u56db\u516c\u5b50\u73b0\u5df2\u805a\u9f50\uff0c\u9601\u4e0b\u6709\u4ec0\u4e48\u9053\u513f \u5c3d\u7ba1\u5212\u51fa\u6765\u5427\uff01\u201d \u201c\u9634\u5c71\u4e4b\u72fc\u201d\u8fde\u8fde\u70b9\u5934\u9053\uff1a\u201c\u597d\uff01\u597d\uff01\u65e2\u7136\u9f0e\u9f0e\u5927\u5404\u7684\u2018\u6b66\u6797\u56db\u516c\u5b50\u2019\u90fd\u5728\u8fd9\u513f\uff0c\u6211\u4eec \u8001\u54e5\u56db\u4e2a\u4e5f\u7b97\u4e0d\u865a\u6b64\u884c\uff01\u8fd9\u4e48\u770b\uff0c\u6211\u4eec\u8001\u54e5\u56db\u4eba\u8868\u6f14\u4e00\u5957\u5c0f\u73a9\u610f\uff0c\u8868\u6f14\u5b8c\u4e86\uff0c\u53ea\u8981\u4f60\u4eec\u2018\u6b66 \u6797\u56db\u516c\u5b50\u2019\u7167\u6837\u4e5f\u80fd\u6765\u4e00\u624b\uff0c\u6211\u4eec\u8001\u54e5\u56db\u4e2a\u4fbf\u8ba4\u8d25\u670d\u8f93\uff0c\u62cd\u62cd\u5c41\u80a1\u8d70\u8def\uff0c\u5982\u679c\u505a\u4e0d\u5230\uff0c\u4f60\u4eec \u2018\u6b66\u6797\u56db\u516c\u5b50\u2019\u53ca\u95e8\u4e0b\u4e4b\u4eba\uff0c\u90fd\u5728\u6c5f\u6e56\u4e0a\u9664\u540d\uff0c\u4ee5\u540e\u7684\u4e00\u5207\u884c\u52a8\u90fd\u8981\u542c\u6211\u4eec\u8001\u54e5\u56db\u4e2a\u7684\u62db \u547c\uff0c\u600e\u4e48\u6837\uff1f\u6562\u6253\u8fd9\u4e2d\u201d\u8d4c\u5417\uff1f\u201d \u201c\u5b89\u4e50\u516c\u5b50\u201d\u6bd4\u8f83\u5bcc\u4e8e\u5fc3\u673a\uff0c\u95fb\u8a00\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u6837\u8d4c\u6cd5\uff0c\u4f60\u4eec\u56db\u4f4d\u4e0d\u592a\u5403\u4e8f\u4e86\u5417\uff1f\u201d \u201c\u9634\u5c71\u4e4b\u72fc\u201d\u6717\u96c4\uff0c\u5916\u8868\u7c97\u9c81\uff0c\u5374\u6700\u662f\u72e1\u733e\uff0c\u867d\u542c\u51fa\u201c\u5b89\u4e50\u516c\u5b50\u201d\u662f\u6b63\u8bdd\u53cd\u8bf4\uff0c\u5374\u88c5\u50bb \u5145\u695e\uff0c\u4e00\u6325\u624b\u9053\uff1a\u201c\u8fd9\u91cc\u8fb9\u6ca1\u6709\u8c01\u4f1a\u5403\u4e8f\uff0c\u5148\u770b\u8001\u592b\u7684\uff01\u201d \u8bf4\u7740\u8bdd\uff0c\u987a\u624b\u4e00\u638cj\u67e5\u5411\u4e8c\u4e08\u5f00\u5916\u4e00\u68f5\u68a7\u6850\u6811\u5288\u53bb\uff01 \u53ea\u542c\u201c\u64e6\u201d\u7684\u4e00\u58f0\uff0c\u6709\u5982\u5feb\u5200\u65a9\u83dc\u74dc\uff0c\u6c34\u6876\u7c97\u7684\u4e00\u68f5\u68a7\u6850\u6811\uff0c\u72b9\u5982\u65a7\u5288\u4e00\u822c\u62e6\u8170\u4e24\u65ad\uff0c \u201c\u8f70\u201d\u7684\u4e00\u58f0\uff0c\u6811\u5934\u5012\u843d\u4e00\u8fb9\uff01 \u8fd9\u201c\u9634\u5c71\u4e4b\u72fc\u201d\u7684\u201c\u638c\u5203\u5207\u6728\u201d\u529f\u592b\uff0c\u7684\u786e\u5230\u4e86\u767b\u5cf0\u9020\u6781\u7684\u5730\u6b65\uff0c\u8ddd\u79bb\u4e8c\u4e08\u591a\u8fdc\uff0c\u53ea\u968f \u624b\u4e00\u5288\uff0c\u672b\u89c1\u4ed6\u8fd0\u529f\u4f5c\u52bf\uff0c\u4e00\u638c\u80fd\u628a\u4e00\u88f8\u6c34\u6876\u7c97\u7684\u68a7\u6850\u6811\u65a9\u65ad\uff0c\u800c\u4e14\u65ad\u5904\u5982\u5200\u5207\u4e00\u822c\u6574\u9f50\uff0c \u8fd9\u4efd\u529f\u529b\uff0c\u7684\u786e\u9a87\u4eba\u542c\u95fb\uff01 \u5176\u5b9e\uff0c\u4ed6\u662f\u5077\u4e86\u5de7\uff0c\u56e0\u4e3a\u4ed6\u5728\u4e0e\u201c\u6b66\u6797\u56db\u516c\u5b50\u201d\u8bf4\u8bdd\u65f6\uff0c\u65e9\u628a\u5468\u8eab\u529f\u529b\u8d2f\u6ce8\u5728\u638c\u7f18\u4e4b \u4e0a\uff0c\u6545\u610f\u8868\u793a\u8f7b\u677e\uff0c\u662f\u60f3\u4e00\u4e3e\u9707\u670d\u7fa4\u96c4\uff0c\u4ee5\u507f\u4ed6\u521d\u6765\u4e4b\u613f\u3002 \u56db\u4eba\u4e4b\u4e2d\uff0c\u4ee5\u201c\u9634\u5c71\u4e4b\u72fc\u201d\u6700\u4e3a\u72e1\u733e\uff0c\u522b\u770b\u4ed6\u5916\u8c8c\u751f\u5f97\u5a01\u731b\uff0c\u6781\u50cf\u662f\u4e2a\u7c97\u4eba\uff0c\u4e8b\u5b9e\u4e0a\u5374 \u6700\u529f\u4e8e\u5fc3\u8ba1\uff0c\u8981\u4e0d\uff0c\u4e3a\u4ec0\u4e48\u79f0\u4e3a\u201c\u9634\u5c71\u4e4b\u72fc\u201d\u5462\uff1f \u4ed6\u770b\u51fa\u201c\u7a77\u5bb6\u5e2e\u201d\u5bb4\u8bf7\u4e4b\u4eba\uff0c\u5176\u4e2d\u4e0d\u4e4f\u6b66\u529f\u9ad8\u624b\uff0c\u4ed6\u4eec\u56db\u4eba\u867d\u7136\u81ea\u8d1f\u6b66\u529f\u751a\u9ad8\uff0c\u4f46\u8981\u786c \u6253\u786c\u62fc\uff0c\u4ee5\u529b\u80fd\u5426\u80dc\u5f97\u4e86\u773c\u524d\u6570\u5341\u6b66\u6797\u9ad8\u624b\uff1f\u53ef\u8bf4\u6beb\u65e0\u628a\u63e1\uff0c\u5c31\u662f\u7a33\u64cd\u80dc\u7b97\uff0c\u4e5f\u8981\u8d39\u4e00\u756a\u624b \u811a\uff0c\u653e\u800c\u5728\u201c\u592a\u4ed3\u4e4b\u9f20\u201d\u4e0e\u201c\u75af\u4e10\u201d\u53ca\u5c11\u5e74\u5c55\u767d\u7b54\u8bdd\u52a8\u624b\u4e4b\u95f4\uff0c\u4ed6\u773c\u73e0\u4e00\u8f6c\uff0c\u8ba1\u4e0a\u5fc3\u6765\u3002\u60f3 \u4ee5\u81ea\u5df1\u56db\u4eba\u5728\u201c\u9b54\u9b3c\u5c9b\u201d\u82e6\u7ec3\u6f5c\u4fee\u7684\u51e0\u95e8\u7edd\u62db\uff0c\u6765\u538b\u670d\u4f17\u4eba\uff0c\u8fd9\u6837\u4e00\u6765\uff0c\u81ea\u7136\u662f\u51fa\u529b\u5c0f\u800c\u6536 \u6548\u5927\u7684\u529e\u6cd5\u3002 \u56e0\u6b64\u4ed6\u62e6\u4f4f\u56e0\u4e3a\u66b4\u6012\u60f3\u8981\u4e0e\u5c55\u767d\u62fc\u547d\u7684\u201c\u592a\u4ed3\u4e4b\u9f20\u201d\uff0c\u62ff\u8bdd\u6fc0\u51fa\u201c\u6b66\u6797\u56db\u516c\u5b50\u201d\uff0c\u628a\u81ea \u5df1\u5728\u201c\u9b54\u9b3c\u5c9b\u201d\u82e6\u7ec3\u4e09\u5341\u5e74\u7684\u201c\u638c\u5203\u5207\u6728\u201d\u529f\u529b\u65bd\u5c55\u4e86\u51fa\u6765\u3002 \u4ed6\u4e00\u638c\u780d\u65ad\u4e00\u68f5\u68a7\u6850\u6811\uff0c\u5411\u201c\u6b66\u6797\u56db\u516c\u5b50\u201d\u563f\u563f\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e00\u624b\u5c0f\u73a9\u610f\uff0c\u96be\u767b\u5927\u96c5\u4e4b \u5802\uff0c\u7b97\u662f\u8001\u592b\u732e\u4e11\u4e86\uff01\u73b0\u5728\u8be5\u770b\u4f60\u4eec\u7684\u4e86\uff01\u201d \u8bf4\u5b8c\u4e86\uff0c\u5140\u81ea\u563f\u563f\u51b7\u7b11\u4e0d\u6b62\uff0c\u795e\u6001\u6d0b\u6d0b\u81ea\u5f97\uff0c\u56e0\u4e3a\u4ed6\u7b97\u51c6\u4e86\uff0c\u773c\u524d\u56db\u4e2a\u5e74\u8f7b\u4eba\uff0c\u6ca1\u6709\u8fd9 \u4efd\u529f\u529b\uff01 \u201c\u6b66\u6797\u56db\u516c\u5b50\u201d\u9762\u9762\u76f8\u89d1\uff0c\u4e8b\u524d\u65e0\u5907\uff0c\u60f3\u4e0d\u5230\u201c\u9634\u5c71\u4e4b\u72fc\u201d\u8d38\u7136\u6765\u4e86\u8fd9\u4e48\u4e00\u624b\uff0c\u4ed6\u4eec\u56db \u4eba\u81ea\u4ed8\u8fd8\u771f\u662f\u6ca1\u6709\u8fd9\u4efd\u529f\u529b\uff0c\u56e0\u6b64\u4f60\u671b\u6211\uff0c\u6211\u671b\u4f60\uff0c\u4e00\u65f6\u4e4b\u95f4\u7b54\u4e0d\u4e0a\u8bdd\u6765\u3002 \u201c\u9634\u5c71\u4e4b\u72fc\u201d\u53c8\u662f\u563f\u563f\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5982\u679c\u56db\u4f4d\u5ba2\u6c14\uff0c\u518d\u4e0d\u51fa\u624b\uff0c\u90a3\u4e48\uff0c\u8fd9\u7b2c\u4e00\u573a\u5c31\u7b97\u8001 \u592b\u4fa5\u5e78\u8d62\u4e86\uff0c\u54b1\u4eec\u518d\u8fdb\u884c\u7b2c\u4e8c\u573a\u3002\u201d \u201c\u6b66\u6797\u56db\u516c\u5b50\u201d\u4e4b\u4e2d\uff0c\u201c\u5b89\u4e50\u516c\u5b50\u201d\u5bcc\u4e8e\u5fc3\u8ba1\uff0c\u201c\u7965\u9e9f\u516c\u5b50\u201d\u80f8\u6709\u57ce\u5e9c\uff0c\u201c\u7aef\u65b9\u516c\u5b50\u201d \u8f83\u4e3a\u9634\u6c89\uff0c\u53ea\u6709\u201c\u51cc\u98ce\u516c\u5b50\u201d\u6027\u683c\u72c2\u50b2\u800c\u813e\u6c14\u66b4\u8e81\uff0c\u4ed6\u89c1\u4e09\u516c\u5b50\u90fd\u4e0d\u5f00\u53e3\uff0c\u4ed6\u867d\u7136\u6ca1\u628a\u63e1\u80fd \u628a\u4e00\u68f5\u5927\u6811\u4e00\u638c\u5288\u65ad\uff0c\u4f46\u53ef\u4e0d\u7518\u5fc3\u5c31\u6b64\u8ba4\u683d\uff0c\u4e8e\u662f\u8de8\u524d\u4e00\u6b65\uff0c\u9053\uff1a\u201c\u51cc\u98ce\u4e0d\u624d\uff0c\u613f\u610f\u8bd5\u8bd5 \u2018\u4ee5\u638c\u5207\u6728\u2019\u7684\u529f\u592b\uff01\u201d \u201c\u9634\u5c71\u4e4b\u72fc\u201d\u54c8\u54c8\u4e00\u7b11\uff0c\u6ee1\u9762\u8be1\u8bf1\u8f7b\u5351\u4e4b\u8272\uff0c\u9053\uff1a\u201c\u8bf7\uff01\u201d \u201c\u51cc\u98ce\u516c\u5b50\u201d\u9a6c\u6b65\u4e00\u6c89\uff0c\u957f\u957f\u5438\u4e86\u4e00\u53e3\u6c14\uff0c\u6697\u628a\u5468\u8eab\u529f\u529b\u8d2f\u6ce8\u5728\u638c\u7f18\u4e4b\u4e0a\uff0c\u5c48\u81c2\u6536\u638c\uff0c \u5bf9\u51c6\u4e86\u4e08\u8bb8\u4e4b\u5916\u53e6\u4e00\u68f5\u68a7\u6850\u6811\uff0c\u4e3e\u638c\u6b32\u5288\u2026\u2026 \u201c\u6162\u7740\uff01\u201d \u201c\u5929\u6daf\u72c2\u751f\u201d\u8d75\u4e5d\u5dde\u7a81\u7136\u98de\u8eab\u63a0\u5165\u573a\u4e2d\uff0c\u62e6\u4f4f\u201c\u51cc\u98ce\u516c\u5b50\u201d\uff0c\u9762\u5411\u201c\u9634\u5c71\u4e4b\u72fc\u201d\u4e00\u62b1 \u62f3\uff0c\u9053\u201c\u9601\u4e0b\u4ee5\u638c\u5207\u6728\uff0c\u529f\u529b\u9ad8\u5f3a\uff0c\u8d75\u67d0\u770b\u7740\u6280\u75d2\uff0c\u5c31\u7531\u8d75\u67d0\u63a5\u4e0b\u8fd9\u7b2c\u4e00\u573a\u5427\uff01\u201d \u8bf4\u7f62\uff0c\u4e5f\u4e0d\u7b49\u2018\u9634\u5c71\u4e4b\u72fc\u2019\u7b54\u8bdd\uff0c\u8eab\u5f62\u5c31\u5730\u4e00\u65cb\uff0c\u201c\u5237\u201d\u7684\u4e00\u58f0\uff0c\u5288\u51fa\u2014\u638c\uff01 \u4e8c\u4e08\u5916\u7684\u4e00\u68f5\u68a7\u6850\uff0c\u540c\u6837\u6709\u6c34\u6876\u7c97\u7ec6\uff0c\u5e94\u638c\u800c\u65ad\uff01 \u6811\u5934\u843d\u5730\uff0c\u201c\u5657\u201d\u7684\u4e00\u54cd\uff0c\u7adf\u63d2\u8fdb\u5730\u5185\uff0c\u7a33\u7136\u672a\u5012\uff01 \u201c\u5929\u6daf\u72c2\u751f\u201d\u638c\u529b\u4ee5\u7280\u5229\u5feb\u6377\u89c1\u957f\u3002 \u770b\u90a3\u65ad\u5904\uff0c\u4e00\u6837\u5982\u5200\u5207\u4e00\u822c\uff0c\u800c\u4e14\u5207\u65ad\u9762\u8f83\u659c\uff0c\u65ad\u5904\u5c16\u957f\u5982\u5200\uff0c\u6545\u80fd\u5c31\u4e0b\u843d\u4e4b\u52bf\u63d2\u5165\u58eb \u4e2d\u3002 \u663e\u7136\uff0c\u201c\u5929\u6daf\u72c2\u751f\u201d\u8fd9\u4e00\u624b\uff0c\u8981\u6bd4\u201c\u9634\u5c71\u4e4b\u72fc\u201d\u7565\u9ad8\u4e00\u7b79\uff01 \u201c\u9634\u5c71\u4e4b\u72fc\u201d\u4e00\u695e\uff0c\u60f3\u4e0d\u5230\u81ea\u5df2\u5728\u201c\u9b54\u9b3c\u5c9b\u201d\u82e6\u7ec3\u4e09\u5341\u5e74\u7684\u201c\u638c\u5203\u5207\u6728\u201d\u529f\u592b\uff0c\u4f1a\u6709\u4eba \u8f7b\u6613\u529e\u5230\uff01 \u4ed6\u4e00\u695e\u4e4b\u540e\uff0c\u53cc\u76ee\u4e00\u77aa\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u662f\u4f55\u4eba\uff1f\u201d \u201c\u533a\u533a\u4e0d\u624d\uff0c\u2018\u5929\u6daf\u72c2\u751f\u2019\u8d75\u4e5d\u5dde\uff01\u201d \u56e0\u201c\u5929\u6daf\u72c2\u751f\u201d\u867d\u662f\u6c5f\u6e56\u6210\u540d\u4eba\u7269\uff0c\u4f46\u51fa\u9053\u8f83\u665a\uff0c\u5728\u201c\u5929\u6daf\u72c2\u751f\u201d\u4ee5\u5929\u7eb5\u4e4b\u624d\uff0c\u957f\u81ea\u5c71 \u4e09\u5e74\u5e08\u6ee1\uff0c\u51fa\u9053\u6c5f\u6e56\u540d\u6ee1\u6b66\u6797\u65f6\uff0c\u201c\u9634\u5c71\u4e4b\u72fc\u201d\u5df2\u79bb\u5f00\u4e2d\u539f\uff0c\u6545\u800c\u201c\u5929\u6daf\u72c2\u751f\u201d\u62a5\u51fa\u540d\u5b57\u4e4b \u540e\uff0c\u201c\u9634\u5c71\u4e4b\u72fc\u201d\u4ef0\u8138\u60f3\u4e86\u4e00\u4f1a\uff0c\u4e0d\u8bb0\u5f97\u81ea\u5df1\u5728\u4e2d\u539f\u6b66\u6797\u66fe\u542c\u5230\u8fd9\u4e2a\u540d\u5b57\uff0c\u4e0d\u7531\u4e00\u77aa\u773c\uff0c\u53f1 \u9053\uff1a\u201c\u4f60\u80fd\u4ee3\u8868\u8c01\uff1f\u201d \u201c\u5929\u6daf\u72c2\u751f\u201d\u4ee5\u7259\u8fd8\u7259\uff0c\u9053\uff1a\u201c\u4f60\u53c8\u80fd\u4ee3\u8868\u8c01\uff1f\u201d \u4e8b\u5b9e\u4e0a\uff0c\u201c\u9634\u5c71\u4e4b\u72fc\u201d\u53ef\u4e5f\u4ee3\u8868\u4e0d\u4e86\u201c\u5357\u6d77\u95e8\u201d\u3002 \u201c\u9634\u5c71\u4e4b\u72fc\u201d\u51b2\u5929\u5927\u6012\uff0c\u53cc\u638c\u4e00\u7acb\uff0c\u9053\uff1a\u201c\u6211\u5288\u4e86\u4f60\uff01\u201d \u201c\u5929\u6daf\u72c2\u751f\u201d\u4ef0\u5929\u72c2\u7b11\uff0c\u9053\uff1a\u201c\u8d75\u67d0\u4e0d\u89c1\u5f97\u6015\u4f60\uff01\u4e0d\u8fc7\uff0c\u521a\u624d\u9601\u4e0b\u81ea\u5df1\u8bf4\u7684\u8bdd\uff0c\u8fd8\u7b97\u4e0d \u7b97\u6570\uff1f\u201d \u201c\u9634\u5c71\u4e4b\u72fc\u201d\u695e\u4e86\u4e00\u4e0b\uff0c\u4e0d\u9519\uff0c\u521a\u624d\u81ea\u5df1\u8bdd\u8bf4\u5f97\u592a\u6ee1\uff0c\u4ed6\u7eb5\u7136\u72e1\u733e\uff0c\u5f53\u7740\u8fd9\u4e48\u591a\u4eba\uff0c\u53ef \u4e0d\u80fd\u8bf4\u4e86\u8bdd\u4e0d\u7b97\u6570\uff0c\u4e0d\u7531\u72e0\u72e0\u5730\u9053\uff1a\u201c\u8fd9\u4e00\u573a\u5c31\u7b97\u4f60\u4eec\u529e\u5230\u4e86\uff01\u7b26\u5144\uff01\u201d \u8bf4\u7740\u8f6c\u5934\u5bf9\u201c\u592a\u4ed3\u4e4b\u9f20\u201d\u9053\uff1a\u201c\u7b26\u5144\uff01\u4f60\u4e5f\u9732\u4e00\u624b\u5427\uff01\u201d \u201c\u592a\u4ed3\u4e4b\u9f20\u201d\u540d\u53eb\u7b26\u8282\uff0c\u95fb\u8a00\u4e0d\u53d1\u58f0\uff0c\u8eab\u5f62\u4f7f\u7136\u4e00\u7f29\uff0c\u66b4\u77ee\u4e86\u4e24\u5c3a\uff0c\u4ecd\u7136\u7528\u7684\u662f\u201c\u7f29\u7b4b \u9b54\u529f\u201d\uff0c\u53ea\u89c1\u4ed6\u7279\u957f\u7684\u53cc\u81c2\u4e00\u62a1\uff0c\u8eab\u5f62\u5c31\u5730\u4e00\u4e2a\u6025\u65cb\uff01 \u4ed6\u77ee\u5c0f\u8eab\u5f62\u5c31\u5982\u4e00\u53ea\u6025\u884c\u65cb\u8f6c\u7684\u9640\u87ba\u4e00\u822c\uff0c\u53ea\u89c1\u6b6a\u98ce\u6253\u5377\uff0c\u6380\u77f3\u98de\u7802\uff0c\u597d\u50cf\u5e73\u5730\u8d77\u4e86\u9f99 \u5377\u98ce\uff0c\u626c\u8d77\u7684\u7802\u5c18\u76f4\u5347\u6709\u4e8c\u4e09\u5341\u4e08\u4e4b\u9ad8\uff0c\u543c\u543c\u96f7\u9e23\uff01 \u5f85\u90a3\u91cc\u98ce\u5377\u8d77\u7684\u5c18\u6c99\uff0c\u6240\u5f62\u6210\u7684\u4e00\u4e2a\u9ed1\u8272\u6c14\u5f80\u6d88\u5931\uff0c\u5c18\u6c99\u843d\u5c3d\u65f6\uff0c\u5730\u4e0b\u5df2\u73b0\u51fa\u4e86\u4e00\u4e2a\u50cf \u6c34\u4e95\u4f3c\u7684\u6df1\u6d1e\uff01 \u800c\u90a3\u201c\u592a\u4ed3\u4e4b\u9f20\u201d\u5df2\u7136\u4e0d\u89c1\uff01 \u4f17\u4eba\u4e00\u89c1\uff0c\u76ee\u77aa\u53e3\u5446\uff0c\u8fd9\u662f\u54ea\u95e8\u5b50\u529f\u592b\uff01 \u539f\u6765\u4ed6\u5229\u7528\u8eab\u5f62\u6025\u65cb\u4e4b\u52bf\uff0c\u4ee5\u53cc\u81c2\u4e4b\u529b\uff0c\u786c\u628a\u575a\u786c\u7684\u5730\u9762\uff0c\u65cb\u4e0b\u53bb\u4e86\u4e08\u4f59\u4e4b\u6df1\uff01 \u800c\u4e14\uff0c\u4ed6\u8fd9\u79cd\u529f\u592b\uff0c\u4e5f\u53ef\u4ee5\u8bf4\u662f\u6b66\u6797\u4e00\u7edd\uff01 \u636e\u8005\u4e00\u8f88\u7684\u4eba\u58eb\u4f20\u8a00\uff0c\u201c\u592a\u4ed3\u4e4b\u9f20\u201d\u6076\u4e8b\u4f5c\u5c3d\uff0c\u4e00\u751f\u4e4b\u4e2d\uff0c\u53ea\u505a\u8fc7\u4e00\u4ef6\u597d\u4e8b\u3002 \u90a3\u662f\u4e09\u5341\u4f59\u5e74\u524d\u7684\u4e8b\u4e86\uff0c\u5978\u76f8\u548c\u5764\uff0c\u4e3a\u5386\u4ee3\u6700\u5927\u7684\u4e00\u4e2a\u8d2a\u5bab\uff0c\u5728\u4efb\u65f6\u6240\u641c\u62ec\u7684\u8d22\u5bcc\uff0c\u5230 \u6848\u53d1\u706d\u95e8\u6c99\u5bb6\u65f6\uff0c\u5bb6\u8d22\u603b\u989d\u5df2\u8fbe\u56db\u767e\u4ebf\u4e07\uff0c\u6bd4\u56fd\u5e93\u7684\u6570\u5b57\u8fd8\u8981\u5927\uff0c\u5168\u56fd\u56db\u4ebf\u4eba\u53e3\uff0c\u4e0d\u5206\u5927\u4eba \u5c0f\u5b69\uff0c\u5982\u679c\u628a\u5978\u76f8\u7684\u8d22\u4ea7\u5e73\u5206\u7684\u8bdd\uff0c\u6bcf\u4eba\u53ef\u5f97\u767d\u94f6\u4e00\u767e\u4e24\uff01 \u6b64\u5916\uff0c\u73cd\u5b9d\u53e4\u73a9\u8fd8\u4e0d\u7b97\u5728\u5185\uff0c\u90a3\u65f6\u5019\u4e24\u6cb3\u707e\u8352\uff0c\u6570\u767e\u4e07\u707e\u6c11\u5df2\u5230\u4e86\u4e89\u98df\u4eba\u8089\u7684\u60e8\u51b5\uff0c\u800c \u5978\u76f8\u548c\u5764\u7684\u201c\u592a\u4ed3\u4e4b\u7c73\u201d\u5374\u5c6f\u79ef\u5f97\u6ee1\u8c37\u6ee1\u4ed3\uff0c\u4efb\u90a3\u767d\u7c73\u866b\u86c0\u9f20\u54ac\uff0c\u8150\u70c2\u53d1\u9709\uff0c\u4e5f\u4e0d\u80af\u62ff\u51fa\u6765 \u53d1\u653e\u9965\u6c11\uff01 \u201c\u592a\u4ed3\u4e4b\u9f20\u201d\u4e0d\u77e5\u600e\u7684\u5ffd\u53d1\u5584\u5fc3\uff0c\u5c31\u65bd\u5c55\u201c\u5c31\u5730\u6253\u6d1e\u201d\u7684\u529f\u592b\uff0c\u6f5c\u8fdb\u592a\u4ed3\uff0c\u795e\u4e0d\u77e5\u9b3c\u4e0d \u89c9\uff0c\u6570\u6708\u4e4b\u95f4\uff0c\u7adf\u628a\u5343\u4e07\u65a4\u7684\u592a\u4ed3\u4e4b\u7c73\u76d7\u8fd0\u4e86\u51c0\u5149\uff0c\u5b8c\u5168\u6551\u6d4e\u4e86\u4e24\u6cb3\u707e\u6c11\uff01 \u8fd9\u662f\u201c\u592a\u4ed3\u4e4b\u9f20\u201d\u6709\u751f\u4ee5\u6765\u6240\u505a\u7684\u552f\u4e00\u7684\u4e00\u4ef6\u597d\u4e8b\uff01 \u5c31\u56e0\u4e3a\u6b64\uff0c\u4ed6\u201c\u592a\u4ed3\u4e4b\u9f20\u201d\u7684\u7ef0\u53f7\uff0c\u54cd\u5f7b\u4e86\u5927\u6c5f\u5357\u5317\uff01 \u5f53\u7136\uff0c\u81ea\u4ece\u4ed6\u6f5c\u8e2a\u201c\u9b54\u9b3c\u5c9b\u201d\uff0c\u4e09\u5341\u4e2d\u7684\u57cb\u5934\u82e6\u7ec3\uff0c\u4ed6\u8fd9\u201c\u5c31\u5730\u6253\u6d1e\u201d\u7684\u529f\u529b\u8d8a\u53d1\u9ad8\u660e \u4e86\uff01 \u201c\u592a\u4ed3\u4e4b\u9f20\u201d\u4ece\u606b\u5185\u7a9c\u4e86\u4e0a\u6765\uff0c\u89c1\u4f17\u4eba\u60ca\u6446\u4e4b\u8272\uff0c\u4e0d\u7531\u6d0b\u6d0b\u5f97\u610f\u5730\u9053\uff1a\u5c0f\u5144\u5f1f\uff0c\u770b\u4f60\u4eec \u7684\u4e86\uff01\u201d \u8fd9\u6b21\u4e0d\u4f46\u201c\u6b66\u6797\u56db\u516c\u5b50\u201d\u77aa\u76ee\u7ed3\u820c\uff0c\u65e0\u8a00\u4ee5\u5bf9\uff0c\u90a3\u6240\u6709\u4e4b\u4eba\u83ab\u4e0d\u4e00\u6115\uff01 \u201c\u638c\u5203\u5207\u6728\u201d\u7684\u529f\u592b\uff0c\u867d\u7136\u96be\u80fd\uff0c\u4f46\u5185\u5bb6\u638c\u529b\u5230\u4e86\u4e00\u5b9a\u706b\u5019\u7684\u6b66\u529f\u9ad8\u624b\uff0c\u8fd8\u53ef\u52c9\u5f3a\u4e00 \u8bd5\u3002\u4f46\u51ed\u53cc\u638c\u65cb\u8f6c\u4e4b\u529b\uff0c\u80fd\u628a\u575a\u786c\u5730\u9762\u6316\u4e0b\u4e00\u4e2a\u4e08\u8bb8\u6df1\u7684\u6df1\u5751\uff0c\u53ef\u8bf4\u662f\u65e0\u4eba\u80fd\u591f\u505a\u5230\uff01 \u7b2c\u4e94\u5341\u4e94\u7ae0\u3000\u201c\u9ed1\u9053\u56db\u51f6\u201d\u901e\u5f3a\u6881>> \u53e4\u9f99\u300a\u5251\u5ba2\u884c\u300b \u7b2c\u4e94\u5341\u4e94\u7ae0\u3000\u201c\u9ed1\u9053\u56db\u51f6\u201d\u901e\u5f3a\u6881 \u201c\u592a\u4ed3\u4e4b\u9f20\u201d\u4e0d\u7531\u4e00\u6115\uff0c\u9053\uff1a\u4f60\u4e8c\u4eba\u8fd9\u7b97\u54ea\u95e8\u5b50\u6b66\u529f\uff1f\u201d \u201c\u6d3b\u6b7b\u4eba\u201d\u6b7b\u4eba\u773c\u4e00\u7ffb\uff0c\u9053\uff1a\u201c\u5c0a\u9a7e\u662f\u2018\u8001\u9f20\u6253\u6d1e\u2019\uff0c\u6211\u5144\u5f1f\u662f\u2018\u8001\u9f20\u94bb\u6d1e\u2019\uff0c\u53ef\u4ee5\u8bf4 \u662f\u534a\u65a4\u516b\u4e24\uff0c\u5982\u679c\u5c0a\u9a7e\u4e0d\u670d\u6c14\uff0c\u53ef\u7167\u6211\u4eec\u5144\u5f1f\u7684\u6837\u5b50\u91cd\u6765\u4e00\u4e2a\uff01\u201d \u4e00\u65f6\u4e4b\u95f4\uff0c\u201c\u592a\u4ed3\u4e4b\u9f20\u201d\u6ca1\u6709\u7b54\u4e0a\u8bdd\u6765\uff0e\u8499\u4eba\u88c5\u675f\u7684\u201c\u6c99\u6f20\u4e4b\u72d0\u201d\u7a81\u7136\u5389\u58f0\u9053\uff1a\u201c\u7528\u4e0d \u5230\u72e1\u8d56\uff01\u518d\u770b\u8001\u592b\u7684\uff01\u201d \u8bf4\u7f62\uff0c\u53cc\u6307\u4e00\u637b\uff0c\u201c\u5c97\u201d\u7684\u4e00\u58f0\uff0c\u628a\u624b\u7531\u4e09\u7a9c\u5ff5\u73e0\u5f53\u4e2d\u634f\u65ad\uff0c\u987a\u624b\u5f80\u4e0a\u4e00\u575a\uff0c\u90a3\u5f53\u4e2d\u7a7f \u4e00\u6839\u9ec4\u8272\u7ed2\u7ef3\uff0c\u4e00\u767e\u5355\u516b\u9897\u5ff5\u73e0\uff0c\u5728\u201c\u6c99\u6f20\u4e4b\u72d0\u201d\u624b\u4e2d\uff0c\u7adf\u7136\u5982\u4e00\u6839\u68cd\u68d2\u4e00\u822c\u7acb\u4e86\u8d77\u6765\uff01 \u672c\u6765\u5185\u5bb6\u529f\u529b\u7ec3\u5230\u706b\u5019\u7684\u6b66\u6797\u9ad8\u624b\uff0c\u5185\u529b\u8d2f\u6ce8\u4e4b\u4e0b\uff0c\u628a\u4e00\u6839\u7ef3\u5b50\u7acb\u5728\u638c\u5fc3\uff0c\u5e76\u4e0d\u7b97\u592a\u56f0 \u96be\u3002\u4f46\u8fd9\u201c\u6c99\u6f20\u4e4b\u72d0\u201d\u7acb\u5728\u638c\u5fc3\u7684\u5374\u662f\u4e00\u767e\u96f6\u516b\u9897\u6eda\u5706\u7684\u5ff5\u73e0\uff0c\u5176\u4e2d\u4ec5\u662f\u7a7f\u4e00\u6839\u975e\u5e38\u7ec6\u8f6f\u7684 \u9ec4\u7ed2\u7ef3\uff0c\u6beb\u65e0\u7740\u529b\u4e4b\u5904\uff0c\u800c\u4e14\u81ea\u6f20\u5916\u7684\u80e1\u6311\u6c34\u6240\u5236\uff0c\u575a\u903e\u7cbe\u94a2\uff0c\u5706\u6ed1\u65e0\u6bd4\uff0c\u591a\u8fbe\u4e00\u767e\u96f6\u516b \u9897\uff0c\u80fd\u591f\u7b14\u76f4\u5730\u7acb\u8d77\u6765\uff0c\u90a3\u8981\u6bd4\u5355\u662f\u5728\u624b\u4e2d\u7acb\u76f4\u4e00\u6839\u7ef3\u5b50\u8981\u56f0\u96be\u591a\u4e86\u3002 \u56e0\u6b64\u201c\u6c99\u6f20\u4e4b\u72d0\u201d\u8fd9\u4e00\u624b\uff0c\u8868\u9762\u4e0a\u770b\u8d77\u6765\u6beb\u4e0d\u60ca\u4eba\uff0c\u4f46\u4e8b\u5b9e\u4e0a\u53ef\u6bd4\u201c\u9634\u5c71\u4e4b\u72fc\u201d\u3001\u201c\u592a \u4ed3\u4e4b\u9f20\u201d\u521a\u624d\u6240\u8868\u9732\u7684\u90a3\u4e24\u624b\u8981\u9ad8\u660e\u591a\uff01\u201d \u8c01\u77e5\u8fd9\u8fd8\u4e0d\u7b97\u5b8c\uff0c\u201c\u6c99\u6f20\u4e4b\u72d0\u201d\u628a\u4e00\u4e32\u4f5b\u73e0\u7b14\u76f4\u5730\u7acb\u4e8e\u638c\u5fc3\uff0c\u8fc8\u6b65\u7ed5\u4e86\u4e00\u4e2a\u5708\u5b50\uff0c\u628a\u7acb \u4e8e\u638c\u5fc3\u7684\u4f5b\u73e0\u7ed9\u7fa4\u96c4\u770b\u4e86\u4e00\u904d\uff0c\u9053\uff1a\u201c\u770b\u6dee\u4e86\uff01\u201d \u559d\u7f62\uff0c\u53ea\u89c1\u4ed6\u9f13\u8179\u7eb3\u6c14\uff0c\u9752\u888d\u5b50\u3001\u7ea2\u574e\u80a9\u4e00\u9f50\u81a8\u81a8\u8d77\u6765\uff0c\u5634\u4e2d\u66b4\u559d\u4e00\u58f0\uff1a\u8d77\uff01\u201d \u4e00\u767e\u96f6\u516b\u9897\u73e0\uff0c\u7ade\u5982\u4e00\u4e32\u6d41\u661f\u4e00\u822c\uff0c\u5728\u4ed6\u559d\u58f0\u4e2d\u6328\u4e2a\u98de\u8d77\uff0c\u6302\u7740\u201c\u4e1d\u4e1d\u201d\u7834\u98ce\u4e4b\u58f0\uff0c\u76f4 \u5c04\u5411\u5be5\u5be5\u661f\u7a7a\uff01 \u4f17\u4eba\u76ee\u77aa\u53e3\u5446\uff0c\u50cf\u8fd9\u79cd\u624b\u638c\u5e01\u52a8\uff0c\u5b8c\u5168\u51ed\u5185\u529b\u628a\u7acb\u4e8e\u638c\u4e2d\u7684\u4e00\u767e\u96f6\u516b\u9896\u4f5b\u73e0\u4e00\u4e00\u9707\u98de\uff0c \u7684\u786e\u662f\u524d\u6240\u672a\u89c1\uff01 \u5c31\u5728\u4f17\u4eba\u9707\u60ca\u4e4b\u4e2d\uff0c\u2018\u6c99\u6f20\u4e4b\u72d0\u201d\u624b\u6237\u4e00\u767e\u96f6\u516b\u9897\u5ff5\u73e0\uff0c\u4e00\u9f50\u98de\u5411\u534a\u7a7a\uff0c\u53ea\u5269\u4e00\u6839\u7ebf \u7ef3\uff0c\u4ecd\u5728\u4ed6\u638c\u4e2d\u7b14\u76f4\u5730\u7ad6\u7acb\u7740\uff01 \u63a5\u7740\u201c\u5237\uff01\u5237\uff01\u5237\uff01\u2026\u2026\u201d\u4e00\u8fde\u4e32\u5fae\u54cd\uff0c\u8bf4\u4e5f\u5947\u602a\uff0c\u90a3\u98de\u5411\u534a\u7a7a\u7684\u5ff5\u73e0\uff0c\u53c8\u4e00\u4e00\u843d\u4e0b\uff0c \u4e00\u4e2a\u4e0d\u5c11\uff0c\u4ecd\u7136\u7a7f\u8fdb\u90a3\u7ad6\u7acb\u7740\u7684\u7ed2\u7ef3\u4e4b\u4e2d\uff01 \u8fd9\u54ea\u91cc\u662f\u7ec3\u6b66\u529f\uff0c\u5206\u660e\u8fd1\u4f3c\u90aa\u6cd5\u4e86\uff01 \u4f17\u4eba\u60c5\u4e0d\u81ea\u5df2\uff0c\u54c4\u7136\u559d\u8d77\u597d\u6765\uff01 \u201c\u6c99\u6f20\u4e4b\u72d0\u201d\u4f3c\u662f\u975e\u5e38\u5f97\u610f\uff0c\u6b22\u5578\u4e86\u4e24\u58f0\uff0c\u518d\u6b21\u8fd0\u6c14\uff0c\u9752\u888d\u5b50\u4ece\u574e\u80a9\u5904\u53c8\u9f13\u4e86\u8d77\u6765\uff0c\u7ad6 \u7acb\u5728\u638c\u4e2d\u7684\u4e00\u4e32\u5ff5\u73e0\uff0c\u518d\u6b21\u5347\u8d77\uff01 \u5c31\u8fd9\u6837\u4e00\u8d77\u4e00\u843d\uff0c\u63a5\u8fde\u6765\u4e86\u4e09\u6b21\uff0c\u559d\u5f69\u60ca\u547c\u7684\u4eba\u7fa4\u4e2d\uff0c\u7a81\u7136\u4f20\u51fa\u4e00\u58f0\u6781\u4e3a\u8f7b\u5351\u7684\u566c\u7b11\uff01 \u8fd9\u7b11\u58f0\u663e\u7136\u662f\u55e4\u4e4b\u4ee5\u9f3b\uff0c\u58f0\u97f3\u4e0d\u5927\uff0c\u4e14\u662f\u5728\u4e71\u56b7\u4e4b\u4e2d\uff0c\u7adf\u7136\u542c\u5f97\u975e\u5e38\u6e05\u695a\uff01 \u201c\u6c99\u6f20\u4e4b\u72d0\u201d\u6b63\u5728\u5174\u5934\u4e0a\uff0c\u7a81\u7136\u542c\u5230\u8fd9\u4e00\u58f0\u6781\u8f7b\u5351\u7684\u55e4\u7b11\uff0c\u500f\u7136\u6536\u4f50\u624b\u4e0a\u7684\u5ff5\u73e0\uff0c\u6012\u559d \u9053\uff1a\u201c\u662f\u8c01\u8ba5\u7b11\u8001\u592b\uff1f\u4f55\u4e0d\u73b0\u8eab\u51fa\u6765\uff01\u201d \u5e94\u58f0\u8d70\u51fa\u4e00\u4e2a\u4fca\u7f8e\u5c11\u5e74\uff0c\u53ea\u89c1\u4ed6\u9762\u542b\u5fae\u7b11\uff0c\u6b65\u6001\u5b89\u8be6\uff0c\u867d\u7136\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u5374\u9690\u7136\u6709\u4e00\u4ee3\u5b97 \u5e08\u7684\u98ce\u5ea6\uff01 \u4f17\u4eba\u4e00\u770b\uff0c\u6b63\u662f\u5c55\u767d\uff01 \u201c\u6c99\u6f20\u4e4b\u72d0\u201d\u5148\u5df2\u89c1\u8bc6\u8fc7\u5c55\u767d\u9707\u9000\u201c\u592a\u4ed3\u4e4b\u9f20\u201d\u7684\u4e00\u638c\uff0c\u73b0\u5728\u89c1\u5c55\u767d\u8d70\u5411\u524d\u6765\uff0c\u5fc3\u4e2d\u4e00 \u7d27\uff0c\u7acb\u523b\u5355\u638c\u4e0a\u63d0\uff0c\u62a4\u4f50\u80f8\u524d\uff0c\u84c4\u52bf\u4ee5\u5f85\uff01 \u5c55\u767d\u5374\u6781\u4e3a\u8f7b\u677e\u5730\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8bf7\u95ee\u9601\u4e0b\u624b\u4e2d\u8fd9\u4e00\u4e32\u5ff5\u73e0\uff0c\u5171\u662f\u591a\u5c11\u9897\uff1f\u201d \u201c\u6c99\u6f20\u4e4b\u72d0\u201d\u5fae\u5fae\u4e00\u6115\uff0c\u81ea\u5df1\u7d27\u5f20\u4e86\u534a\u5929\uff0c\u4f30\u4e0d\u5230\u5c55\u767d\u4f1a\u95ee\u51fa\u8fd9\u4e0d\u76f8\u5e72\u7684\u95ee\u9898\u3002\u4f46\u5634\u4e2d \u4ecd\u7b54\u9053\uff1a\u8001\u62d9\u8fd9\u4e32\u5ff5\u73e0\u5171\u662f\u4e00\u767e\u96f6\u516b\u9897\uff0c\u5c0f\u5144\u5f1f\u6709\u4f55\u89c1\u6559\uff1f\u201d \u5c55\u767d\u9053\uff1a\u201c\u6050\u6015\u4e0d\u5bf9\u5427\uff1f\u201d \u201c\u6c99\u6f20\u4e4b\u72d0\u201d\u4e00\u695e\uff0c\u9053\uff1a\u201c\u6709\u4ec0\u4e48\u4e0d\u5bf9\uff1f\u8001\u62d9\u8fd9\u4e32\u5ff5\u73e0\u968f\u8eab\u643a\u5e26\u4e86\u4e94\u5341\u4f59\u5e74\uff0c\u96be\u9053\u591a\u5c11 \u9897\u8fd8\u6570\u4e0d\u6e05\u695a\uff01\u201d \u5c55\u767d\u4ecd\u7136\u9762\u542b\u5fae\u7b11\uff0c\u4e0d\u7d27\u4e0d\u6f2b\u5730\u9053\uff1a\u4ee5\u5728\u4e0b\u773c\u5149\u770b\u6765\uff0c\u9601\u4e0b\u8fd9\u4e32\u5ff5\u73e0\u6050\u6015\u4e0d\u591f\u4e00\u767e\u96f6\u516b \u9897\uff01\u201d \u201c\u6c99\u6f20\u4e4b\u72d0\u201d\u88ab\u5c55\u767d\u4e00\u8a00\u63d0\u9192\uff0c\u518d\u7559\u795e\u4e00\u770b\uff0c\u624b\u4e2d\u5ff5\u73e0\u679c\u7136\u5c11\u4e86\u5341\u6570\u9897\uff01 \u201c\u6c99\u6f20\u4e4b\u72d0\u201d\u5fc3\u4e2d\u4e00\u61d4\uff0c\u60f3\u4e0d\u5230\u81ea\u5df1\u771f\u529b\u8d2f\u6ce8\u4e4b\u4e0b\uff0c\u5ff5\u73e0\u4f1a\u88ab\u4eba\u5bb6\u53d6\u8d70\uff0c\u800c\u81ea\u5df2\u7ade\u832b\u7136 \u4e0d\u89c9\uff01 \u773c\u524d\u4e4b\u4eba\uff0c\u90fd\u662f\u5927\u884c\u5bb6\uff0c\u89c1\u5230\u6b64\u79cd\u60c5\u5f62\uff0c\u4e0d\u7531\u9f50\u90fd\u4e00\u5671\uff01 \u201c\u6c99\u6f20\u4e4b\u72d0\u201d\u5148\u662f\u4e00\u7a98\uff0c\u7ee7\u4e4b\u5927\u6012\uff0c\u66b4\u53f1\u4e00\u58f0\uff0c\u6296\u624b\u7529\u51fa\u4e00\u9897\u5ff5\u73e0\uff0c\u76f4\u5411\u5c55\u767d\u9762\u95e8\u6253 \u6765\uff01 \u5ff5\u73e0\u98de\u65cb\u800c\u81f3\uff0c\u4e2d\u7a7a\u515c\u98ce\uff0c\u53d1\u51fa\u4e00\u58f0\u9707\u8033\u7684\u5c16\u5578\uff01 \u5c55\u767d\u5355\u638c\u4e00\u64a9\uff0c\u60f3\u628a\u6253\u6765\u5ff5\u73e0\u9707\u98de\uff01 \u6ca1\u60f3\u5230\u98de\u5954\u800c\u81f3\u7684\u5ff5\u73e0\u9047\u5230\u5f3a\u52b2\u7684\u638c\u98ce\u4e00\u963b\uff0c\u5728\u534a\u7a7a\u505c\u4e86\u505c\uff0c\u500f\u7136\u4e00\u9635\u6025\u65cb\uff0c\u4e0d\u4f46\u6ca1\u6709 \u88ab\u9707\u843d\u4e00\u8fb9\uff0c\u53cd\u800c\u52a0\u901f\u5ea6\u5730\u5411\u5c55\u767d\u9762\u95e8\u88ad\u6765\uff01 \u8fd9\u771f\u662f\u4e0d\u51fa\u610f\u6599\u7684\u4e8b\uff0c\u201c\u6c99\u6f20\u4e4b\u72d0\u201d\u53d1\u51fa\u7684\u8fd9\u9897\u5ff5\u6b8a\uff0c\u7adf\u80fd\u7a7f\u7834\u5c55\u767d\u795e\u9b3c\u7686\u60ca\u7684\u5185\u5bb6\u638c \u529b\uff1f \u5e78\u4e8f\u5c55\u767d\u201c\u5343\u5e7b\u98d8\u9999\u6b65\u201d\u3001\u201c\u65e0\u8272\u65e0\u76f8\u8eab\u201d\u5df2\u7ec3\u5230\u610f\u4e0e\u795e\u4f1a\u7684\u5730\u6b65\uff0c\u4e00\u638c\u672a\u80fd\u628a\u6025\u88ad\u800c \u6765\u7684\u5ff5\u73e0\u9707\u98de\uff0c\u8eab\u4e0a\u53cd\u5e94\u7acb\u751f\uff0c\u7115\u7136\u4e00\u4e2a\u6025\u65cb\uff0c\u5578\u58f0\u9707\u8033\u7684\u5ff5\u73e0\u64e6\u9762\u800c\u8fc7\uff01 \u5c55\u767d\u6697\u9053\u4e00\u58f0\uff1a\u201c\u597d\u9669\uff01\u2026\u2026\u201d \u201c\u6c99\u6f20\u4e4b\u72d0\u201d\u66f4\u6ca1\u60f3\u5230\u8ddd\u79bb\u5982\u6b64\u4e4b\u8fd1\uff0c\u53c8\u662f\u5728\u5c55\u7684\u60c5\u51b5\u4e0b\uff0c\u4ecd\u80fd\u8eb2\u8fc7\u81ea\u5df1\u4e13\u7834\u5185\u5bb6\u771f\u529b \u7684\u201c\u8ffd\u547d\u795e\u73e0\u201d\uff01\u5fc3\u4e2d\u4e0d\u7531\u4e00\u61d4\uff01 \u4f46\u4e5f\u4e0d\u7531\u66f4\u6012\uff0c\u53f1\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u771f\u6709\u4f60\u7684\uff01\u7adf\u80fd\u8eb2\u8fc7\u8001\u62d9\u7684\u2018\u8ffd\u547d\u795e\u73e0\u2019\uff01\u518d\u6765\u4e09\u9897 \u5c1d\u5c1d\uff01\u201d \u559d\u7f62\uff0c\u6296\u624b\u53c8\u6253\u51fa\u4e09\u9896\u5ff5\u73e0\uff0c\u4e09\u58f0\u9510\u5578\uff0c\u9707\u8033\u55e1\u9e23\uff0c\u6210\u201c\u4e09\u661f\u5728\u6237\u201d\u4e4b\u52bf\uff0c\u4e00\u5b57\u5f62\u5411\u5c55 \u767d\u80f8\u524d\u98de\u6765\uff01 \u5c55\u767d\u7b2c\u4e00\u6b21\u4e0d\u77e5\u9053\u8fd9\u8499\u53e4\u8001\u4eba\u7684\u5ff5\u73e0\u5389\u5bb3\uff0c\u4e3e\u638c\u53bb\u5bf9\uff0c\u9669\u4e9b\u5403\u4e86\u5927\u4e8f\u3002\u8fd9\u6b21\u77e5\u9053\u4e86\u5ff5\u73e0 \u53ef\u7834\u5185\u5bb6\u771f\u529b\uff0c\u4e0d\u518d\u51fa\u638c\uff0c\u5b9e\u65bd\u201c\u65e0\u8272\u65e0\u76f8\u8eab\u201d\u6cd5\uff0c\u867d\u7136\u201c\u6c99\u6f20\u4e4b\u72d0\u201d\u8fd9\u6b21\u4e09\u9897\u5ff5\u73e0\u4e00\u9f50\u51fa \u624b\uff0c\u6bcf\u9897\u5ff5\u73e0\u4e4b\u95f4\u95f4\u9694\u4e09\u5c3a\uff0c\u4e09\u9897\u5ff5\u73e0\u5e7f\u7f69\u4e00\u6587\u8303\u56f4\uff0c\u53cd\u800c\u8ba9\u5c55\u767d\u8f7b\u6613\u8eb2\u8fc7\uff01 \u7a81\u542c\u4e00\u58f0\u5a07\u53f1\u9053\uff1a\u597d\u4e2a\u4e0d\u8981\u8138\u7684\u8499\u53e4\u9791\u5b50\uff01\u5c55\u54e5\u54e5\u4e0d\u8fd8\u624b\uff0c\u4f60\u4fbf\u6253\u8d77\u6765\u6ca1\u5b8c\u4e86\u3002\u73b0\u5728\u8ba9 \u4f60\u81ea\u5df1\u4e5f\u5c1d\u2018\u8ffd\u547d\u795e\u73e0\u2019\u7684\u5473\u9053\uff01\u201d \u63a5\u7740\uff0c\u4e24\u58f0\u9707\u8033\u5c16\u5578\uff0c\u53cd\u5411\u201c\u6c99\u6f20\u4e4b\u72d0\u201d\u5c04\u81f3\uff01 \u201c\u6c99\u6f20\u4e4b\u72d0\u201d\u5927\u60ca\u5931\u8272\uff0c\u987e\u4e0d\u5f97\u518d\u53bb\u6253\u5c55\u767d\uff0c\u81ea\u5df2\u53c8\u77e5\u9053\u62db\u547d\u795e\u73e0\u201d\u7684\u5389\u5bb3\uff0c\u8d76\u7d27\u7528\u4e86 \u4e00\u4e2a\u201c\u5c31\u5730\u5341\u516b\u6eda\u201d\u7684\u62db\u5f0f\uff0c\u76f4\u6eda\u51fa\u4e00\u4e08\u5f00\u5916\uff0c\u624d\u633a\u8eab\u7ad9\u8d77\uff0c\u867d\u7136\u8eb2\u8fc7\u4e86\u4e24\u9897\u201c\u8ffd\u547d\u795e \u73e0\u201d\uff0c\u4f46\u5df2\u7ecf\u95f9\u4e86\u4e2a\u7070\u5934\u58eb\u8138\uff01 \u4f17\u4eba\u4e00\u8d77\u6ce8\u76ee\uff0c\u539f\u6765\u662f\u5a49\u513f\u6253\u51fa\u4e86\u4e24\u9897\u201c\u8ffd\u547d\u795e\u73e0\uff01\u201d \u4f17\u4eba\u611f\u5230\u5947\u602a\uff0c\u4e0d\u77e5\u5a49\u513f\u624b\u4e2d\uff0c\u600e\u4e48\u4e5f\u4f1a\u6709\u201c\u8ffd\u547d\u795e\u73e0\u201d\uff1f \u4e8b\u5b9e\u4e0a\uff0c\u5a49\u513f\u624b\u4e2d\u7684\u201c\u8ffd\u547d\u795e\u73e0\u201d\uff0c\u662f\u5728\u201c\u6c99\u6f20\u4e4b\u72d0\u201d\u5f53\u4f17\u8868\u6f14\u201c\u795e\u73e0\u5f52\u4f4d\u201d\u624b\u6cd5\u65f6\uff0c \u5a49\u513f\u7a81\u53d1\u5947\u60f3\uff0c\u4ee5\u201c\u641c\u9b42\u6307\u201d\u795e\u529f\u7684\u201c\u5438\u201d\u5b57\u8bc0\uff0c\u6697\u5730\u91cc\u5438\u5230\u624b\u4e2d\u5341\u6570\u9897\uff01 \u56e0\u201c\u6c99\u6f20\u4e4b\u72d0\u201d\u5174\u9ad8\u5f69\u70c8\uff0c\u4e14\u4e07\u4e5f\u60f3\u4e0d\u5230\u81ea\u5df1\u73a9\u5f04\u9ad8\u5f3a\u624b\u6cd5\u65f6\uff0c\u4f1a\u6709\u4eba\u5728\u6697\u4e2d\u6363\u9b3c\u3002\u4f17 \u4eba\u4e5f\u56e0\u4e3a\u88ab\u201c\u6c99\u6f20\u4e4b\u72d0\u201d\u9ad8\u5f3a\u624b\u6cd5\u6240\u5438\u5f15\uff0c\u6ce8\u610f\u529b\u5b8c\u5168\u653e\u5728\u201c\u6c99\u6f20\u4e4b\u72d0\u201d\u624b\u4e0a\u90a3\u4e32\u7b14\u76f4\u7acb\u7740 \u66f2\u5ff5\u73e0\u4e0a\u9762\uff0c\u4efb\u8c01\u4e5f\u6ca1\u6709\u53d1\u89c9\u5a49\u513f\u5728\u4e00\u65c1\u505a\u4e86\u624b\u811a\uff01 \u72ec\u6709\u5c55\u767d\u795e\u76ee\u5982\u7535\uff0c\u5728\u4e00\u65c1\u770b\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u53c8\u89c1\u201c\u6c99\u6f20\u4e4b\u72d0\u201d\u624b\u4e2d\u5c11\u4e86\u5ff5\u73e0\uff0c\u4ecd\u4e0d\u81ea \u77e5\uff0c\u8fd8\u5728\u90a3\u91cc\u6d0b\u6d0b\u5f97\u610f\u5730\u5356\u5f04\uff0c\u4e0d\u7531\u5f97\u53d1\u51fa\u58f0\u55e4\u7b11\uff01 \u201c\u6c99\u6f20\u4e4b\u72d0\u201d\u7f9e\u607c\u6210\u6012\uff0c\u62ff\u201c\u8ffd\u547d\u795e\u73e0\u201d\u8fde\u73af\u6253\u5411\u5c55\u767d\uff0c\u5c55\u767d\u4e0d\u77e5\u201c\u8ffd\u547d\u795e\u73e0\u201d\u53ef\u7834\u5185 \u5bb6\u771f\u529b\uff0c\u4e00\u65f6\u4e4b\u95f4\u65e0\u6cd5\u8fd8\u624b\uff0c\u5a49\u513f\u803d\u5fc3\u5c55\u767d\u5b89\u5371\uff0c\u6545\u800c\u201c\u4ee5\u5176\u4eba\u4e4b\u9053\u8fd8\u6cbb\u5176\u4eba\u4e4b\u8eab\u201d\uff0c\u62ff \u201c\u6c99\u6f20\u4e4b\u72d0\u201d\u7684\u201c\u8ffd\u547d\u795e\u73e0\u201d\uff0c\u53bb\u6253\u201c\u6c99\u6f20\u4e4b\u72d0\u201d\uff01 \u59cb\u7ec8\u9ed8\u7136\u7ad9\u5728\u4e00\u65c1\u3001\u5f62\u5982\u50f5\u5c38\u7684\u201c\u8471\u5cad\u4e4b\u9e70\u201d\uff0c\u4e0d\u7531\u6012\u4ece\u5fc3\u4e0a\u8d77\uff0c\u9634\u68ee\u68ee\u5730\u5428\u9053\uff1a\u201c\u4e2b \u5934\uff0c\u4f60\u627e\u6b7b\uff01\u201d \u559d\u58f0\u4e2d\uff0c\u4e00\u8bb0\u201c\u67af\u9aa8\u638c\u201d\u731b\u5411\u5a49\u513f\u8fce\u9762\u62cd\u51fa\uff01 \u201c\u8471\u5cad\u4e4b\u9e70\u201d\u53c8\u540d\u201c\u8471\u9e70\u53df\uff0c\u4ed6\u8fd9\u201c\u67af\u9aa8\u638c\u201d\u5389\u5bb3\u975e\u51e1\uff0c\u4e43\u662f\u8fd0\u96c6\u201c\u4e5d\u9633\u9b54\u706b\u201d\uff0c\u82e6\u7ec3 \u4e86\u6570\u5341\u5bd2\u6691\u800c\u6210\u7684\u3002\u800c\u4e14\u5728\u7ec3\u8fd9\u79cd\u638c\u529f\u65f6\uff0c\u5728\u8eab\u524d\u8981\u751f\u4e0a\u6781\u4e3a\u65fa\u76db\u7684\u7089\u706b\uff0c\u628a\u53cc\u638c\u653e\u5728\u706b\u82d7 \u4e0b\u7078\u70e4\uff0c\u4e00\u8fb9\u8fd0\u96c6\u672c\u8eab\u201c\u4e09\u5473\u771f\u706b\u201d\u4e0e\u5916\u706b\u76f8\u6297\uff0c\u529f\u6210\u4e4b\u65e5\uff0c\u53cc\u638c\u53ef\u53d8\u6210\u7126\u9ed1\uff0c\u5982\u679c\u6253\u5728\u4eba \u7684\u8eab\u4e0a\uff0c\u53ef\u4f7f\u4eba\u9aa8\u67af\u7126\u7cca\u800c\u6b7b\uff0c\u6545\u540d\u201c\u67af\u9aa8\u638c\u201d\u7aef\u7684\u5389\u5bb3\u65e0\u6bd4\uff01 \u201c\u9b54\u9b3c\u5c9b\u201d\u6070\u5de7\u6709\u4e00\u5ea7\u6d3b\u706b\u5c71\uff0c\u5e38\u5e74\u4e0d\u65ad\u5411\u5916\u55b7\u706b\uff0c\u8fd9\u79cd\u201c\u5730\u5fc3\u706b\u201d\u4e0d\u77e5\u53c8\u8981\u6bd4\u666e\u901a\u7089 \u706b\u70ed\u5ea6\u9ad8\u591a\u5c11\u500d\u201c\u8471\u5cad\u4e4b\u9e70\u201d\u5c31\u5728\u8fd9\u706b\u5c71\u53e3\u4e0a\uff0c\u82e6\u7ec3\u4e86\u4e09\u5341\u5e74\uff0c\u6240\u4ee5\uff0c\u4ed6\u7684\u201c\u67af\u9aa8\u638c\u201d\u5df2\u5230 \u4e86\u767b\u5cf0\u9020\u6781\u5730\u6b65\uff0c\u800c\u4e14\u53cc\u638c\u5df2\u53d8\u6210\u767d\u9aa8\uff0c\u751a\u81f3\u8fde\u4ed6\u672c\u4eba\u4e5f\u50cf\u50f5\u5c38\u5dee\u4e0d\u591a\u4e86\uff01 \u53ea\u89c1\u4ed6\u201c\u67af\u9aa8\u638c\u201d\u65bd\u51fa\u6765\u4e4b\u540e\uff0c\u867d\u5728\u661f\u6708\u5149\u8f89\u4e4b\u5382\uff0c\u4ecd\u7136\u6cdb\u8d77\u4e00\u7247\u8499\u8499\u7ea2\u5149\uff01 \u5a49\u513f\u4e0d\u77e5\u5389\u5bb3\uff0c\u89c1\u201c\u8471\u5cad\u4e4b\u9e70\u201d\u4e00\u638c\u5411\u5979\u6740\u6765\uff0c\u53cc\u638c\u4e00\u626c\uff0c\u4e00\u5f0f\u201c\u5fae\u98ce\u62c2\u5f31\u67f3\u201d\uff0c\u5411\u6765 \u638c\u8fce\u51fb\uff01 \u53cc\u638c\u5c1a\u672a\u51fa\u5b9e\uff0c\u5a49\u513f\u7a81\u611f\u4e00\u9635\u6781\u70ed\u7684\u70ed\u98ce\uff0c\u8fce\u9762\u522e\u6765\uff0c\u5168\u8eab\u4e00\u98a4\uff0c\u7acb\u611f\u5934\u70e7\u6b32\u88c2\uff0c\u5468\u8eab \u9aa8\u8282\u91cc\u4eff\u4f5b\u7a81\u7136\u6d47\u4e86\u4e00\u9505\u70ed\u918b\uff0c\u53c8\u9178\u53c8\u70e7\uff0c\u5947\u75db\u65e0\u6bd4\uff01 \u5a49\u513f\u6697\u9053\u4e00\u58f0\uff1a\u201c\u4e0d\u597d\uff01\u2026\u2026\u201d \u518d\u60f3\u56de\u8eab\u8eb2\u907f\uff0c\u5df2\u7ecf\u662f\u529b\u4e0d\u4ece\u5fc3\uff0c\u5a07\u8eaf\u5411\u4e0b\u4e00\u8f6f\uff0c\u4eba\u5df2\u660f\u4e86\u8fc7\u53bb\uff01 \u201c\u8471\u5cad\u4e4b\u9e70\u201d\u6840\u6840\u602a\u7b11\u7740\uff0c\u4e00\u53ea\u767d\u9aa8\u78f7\u78f7\u7684\u624b\u638c\uff0c\u53c8\u52a0\u5feb\u4e86\u901f\u5ea6\uff0c\u5411\u5a49\u513f\u9762\u95e8\u4e0a\u6309\u4e86\u4e0b \u6765\uff01 \u773c\u770b\u8001\u9b54\u5934\u8fa3\u624b\u6467\u82b1\uff0c\u8fd9\u4e00\u638c\u4e0b\u53bb\uff0c\u5a49\u513f\u5a07\u7f8e\u5982\u82b1\u7684\u4e00\u5f20\u7c89\u9762\uff0c\u5c31\u8981\u53d8\u6210\u8840\u7cca\u4e00\u56e2\u2026\u2026 \u201c\u4f4f\u624b\uff01\u201d \u7a81\u7136\u4e00\u58f0\u66b4\u559d\uff0c\u7d27\u63a5\u7740\u4e00\u80a1\u5de8\u5927\u638c\u98ce\u52b2\u6d41\uff0c\u5411\u7740\u201c\u8471\u5cad\u4e4b\u9e70\u201d\u5982\u5c71\u6d8c\u81f3\uff01 \u201c\u8471\u5cad\u4e4b\u9e70\u201d\u5fc3\u4e2d\u4e00\u61d4\uff0c\u731b\u7136\u9519\u638c\uff0c\u65bd\u5c55\u201c\u50f5\u5c38\u8df3\u201d\u602a\u5f02\u8eab\u6cd5\uff0c\u80a9\u4e0d\u6447\uff0c\u817f\u4e0d\u5f2f\uff0c\u76f4\u633a \u633a\u5730\u6a2a\u8dc3\u4e00\u4e08\u5f00\u5916\uff01 \u5c55\u767d\u4e00\u638c\u903c\u9000\u201c\u8471\u5cad\u4e4b\u9e70\u201d\uff0c\u89c1\u5a49\u513f\u7c89\u9762\u706b\u7ea2\uff0c\u72b9\u5982\u8840\u54ed\uff0c\u5a07\u8eaf\u6447\u6447\u6b32\u5012\uff0c\u4ed6\u4e0d\u987e\u4e00\u5207 \u817e\u8eab\u63a2\u81c2\u6276\u4f4f\u4e86\u5a49\u513f\uff01\u5c55\u767d\u7684\u624b\u65b9\u4e00\u6276\u5230\u5a49\u513f\u7684\u8eab\u4e0a\uff0c\u5c31\u5982\u89e6\u5230\u4e00\u4e2a\u7ea2\u706b\u70ad\u4e00\u822c\uff0c\u70ed\u5f97\u70eb \u624b\uff0c\u4e0d\u7531\u5927\u5403\u4e00\u60ca\uff0c\u4e5f\u987e\u4e0d\u5f97\u5a49\u513f\u5728\u540d\u4efd\u4e0a\u5982\u4eca\u5df2\u662f\u4ed6\u7684\u5c0f\u59e8\uff0c\u7acb\u523b\u4f38\u624b\u8fde\u62cd\u5a49\u513f\u524d\u5fc3\u540e\u5fc3 \u4e03\u5927\u91cd\u7a74\uff0c\u4ee5\u4fdd\u4efb\u5a49\u513f\u7684\u5fc3\u8109\uff0c\u4e0d\u81f4\u88ab\u9b54\u706b\u70e7\u65ad\u2026\u2026 \u96f7\u5927\u53d4\u3001\u6155\u5bb9\u7ea2\u3001\u201c\u51cc\u98ce\u516c\u5b50\u201d\u3001\u201c\u7aef\u65b9\u516c\u5b50\u201d\u4ee5\u53ca\u968f\u201c\u51cc\u98ce\u5212\u5b50\u201d\u524d\u6765\u7684\u957f\u53d1\u62ab\u80a9\u7684 \u8001\u8005\u201c\u6f20\u5916\u795e\u541b\u201d\u8d6b\u8fde\u82f1\uff0c\u90fd\u662f\u6700\u5173\u5fc3\u5a49\u513f\u7684\u4eba\uff0c\u4e00\u89c1\u5a49\u513f\u8d1f\u4f24\u9887\u91cd\uff0c\u4e00\u9f50\u8d76\u8fc7\u6765\uff01 \u96f7\u5927\u53d4\u4e00\u89c1\u5a49\u513f\u4f24\u5f97\u4e0d\u8f7b\uff0c\u7acb\u523b\u66b4\u6012\uff0c\u201c\u5929\u4f5b\u638c\u201d\u4e00\u8bb0\u7edd\u5b66\uff0c\u631f\u7834\u7a7a\u638c\u98ce\uff0c\u731b\u5411\u201c\u8471\u5cad \u4e4b\u9e70\u201d\u780d\u53bb\uff01 \u201c\u8471\u5cad\u4e4b\u9e70\u201d\u563f\u563f\u51b7\u7b11\uff0c\u201c\u67af\u9aa8\u638c\u201d\u8fd0\u81f3\u5341\u6210\uff0c\u5411\u96f7\u5927\u53d4\u638c\u52bf\u8fce\u6765\uff01 \u638c\u98ce\u672a\u5230\uff0c\u96f7\u5927\u53d4\u5148\u611f\u70ed\u98ce\u6251\u9762\uff0c\u5fc3\u4e2d\u4e00\u61d4\uff0c\u77e5\u9053\u5bf9\u65b9\u638c\u98ce\u6bd2\u8fa3\uff0c\u4e0d\u6562\u8ba9\u638c\u98ce\u63a5\u5b9e\uff0c\u8eab \u5f62\u7535\u95ea\uff0c\u98d8\u51fa\u4e94\u5c3a\uff01 \u201c\u51cc\u98ce\u516c\u5b50\u201d\u53ca\u6155\u5bb9\u7ea2\u53cc\u53cc\u6251\u4e0a\uff0c\u4ea6\u4e3a\u201c\u6c99\u6f20\u4e4b\u72d0\u201d\u5947\u70ed\u638c\u98ce\u903c\u9000\uff01 \u201c\u7aef\u65b9\u516c\u5b50\u201d\u8fd0\u51fa\u5bb6\u4f20\u7edd\u5b66\u201c\u6df7\u5143\u6307\u201d\u529f\uff0c\u5c48\u6307\u731b\u70b9\u201c\u8471\u5cad\u4e4b\u9e70\u201d\u4e09\u9633\u5927\u7a74\uff01 \u201c\u8471\u5cad\u4e4b\u9e70\u201d\u602a\u7b11\u8fde\u8fde\uff0c\u201c\u67af\u9aa8\u638c\u201d\u65bd\u5c55\u5f00\u4e86\uff0c\u70ed\u6d41\u6fc0\u8361\uff0c\u65e0\u4eba\u6562\u5851\u5176\u950b\uff01 \u201c\u9634\u5c71\u4e4b\u72fc\u201d\u89c1\u591a\u4eba\u56f4\u653b\u201c\u8471\u5cad\u4e4b\u9e70\u201d\u5927\u543c\u4e00\u58f0\uff0c\u4ee5\u4ed6\u201c\u638c\u5203\u5207\u6728\u201d\u7684\u529f\u592b\uff0c\u7acb\u638c\u5982\u5200 \u5411\u201c\u7aef\u65b9\u516c\u5b50\u201d\u80a9\u4e0a\u780d\u6765\uff01 \u8fd9\u201c\u9634\u5c71\u4e4b\u72fc\u201d\u5fc3\u8ba1\u8fc7\u4eba\uff0c\u4ed6\u89c1\u201c\u7aef\u65b9\u516c\u5b50\u201d\u81ea\u79f0\u662f\u201c\u7a77\u5bb6\u5e2e\u201d\u638c\u95e8\uff0c\u4fbf\u7acb\u4e86\u201c\u64d2\u8d3c\u5148 \u64d2\u738b\u201d\u7684\u6253\u7b97\uff1b\u60f3\u5148\u628a\u201c\u7aef\u65b9\u516c\u5b50\u201d\u62ff\u4e0b\uff0c\u4e0d\u51c6\u4f7f\u201c\u7a77\u5bb6\u5e2e\u201d\u5c31\u8303\uff01 \u4f46\u4ed6\u4e00\u653b\u5411\u201c\u7aef\u65b9\u516c\u5b50\u201d\uff0c\u201c\u7a77\u5bb6\u5e2e\u201d\u7684\u201c\u98ce\u5c18\u4e09\u4e10\u201d\u4e3a\u9996\u7684\u4e10\u5e2e\u957f\u8001\uff0c\u4e00\u9f50\u51fa\u624b\u5411\u4ed6 \u653b\u6765\uff01 \u201c\u592a\u4ed3\u4e4b\u9f20\u201d\u3001\u201c\u6c99\u6f20\u4e4b\u72d0\u201d\u5148\u540e\u51fa\u624b\uff0c\u8fd9\u8fb9\u7fa4\u96c4\u4e5f\u7eb7\u7eb7\u52a0\u5165\u6218\u56e2\uff0c\u5239\u65f6\u4e4b\u95f4\uff0c\u6df7\u6218\u5a01 \u4e00\u7247\uff01 \u5c55\u767d\u4e00\u624b\u6276\u4f4f\u5a49\u513f\u7684\u7ea4\u8170\uff0c\u4e00\u624b\u6309\u4f4f\u5a49\u513fL\u524d\u5fc3\u201c\u4e73\u4e2d\u7a74\u201d\uff0c\u8fd0\u96c6\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b \u7edd\u5b66\u4e2d\u7597\u4f24\u5927\u6cd5\uff0c\u4f7f\u771f\u529b\u4e0d\u65ad\u4ece\u638c\u5fc3\u6d8c\u51fa\uff0c\u6e90\u6e90\u8f93\u5165\u5a49\u513f\u4f53\u5185\uff0c\u4ee5\u4e3a\u5a49\u513f\u903c\u51fa\u6df1\u5165\u5185\u810f\u7684\u70ed \u6bd2\uff01 \u6240\u4ee5\uff0c\u5bf9\u8ddf\u524d\u7684\u4e00\u573a\u6df7\u6218\uff0c\u4ed6\u4e5f\u65e0\u5fc3\u4e00\u987e\uff0c\u53ea\u5168\u795e\u8d2f\u6ce8\u5728\u5a49\u513f\u8eab\u4e0a\uff01 \u5c55\u767d\u8d77\u521d\u53ea\u662f\u5fc3\u6025\u5a49\u513f\u53d7\u4f24\uff0c\u6ca1\u6709\u4efb\u4f55\u8003\u8651\u62a2\u4e0a\u524d\u6765\uff0c\u4e00\u624b\u62b1\u4f4f\u6447\u6447\u6b32\u5012\u7684\u5a49\u513f\uff0c\u62cd\u4e86 \u5a49\u513f\u8eab\u524d\u8eab\u540e\u4e03\u5927\u91cd\u7a74\uff0c\u7136\u540e\u4e00\u638c\u6309\u4f4f\u5a49\u513f\u524d\u5fc3\u201c\u4e73\u4e2d\u201d\u7a74\u4e0a\uff0c\u4ee5\u81ea\u5df2\u7eaf\u539a\u5185\u529b\u4e3a\u5a49\u513f\u8eaf\u70ed \u7597\u4f24\u3002 \u4ed6\u671b\u7740\u5a49\u513f\u9189\u4eba\u7684\u5bb9\u989c\uff0c\u60f3\u8d77\u81ea\u5df2\u7a77\u9014\u672b\u8def\uff0c\u8eab\u8d1f\u4e8c\u5904\u5200\u4f24\uff0c\u53c8\u517c\u91cd\u75c5\u4f8b\u5367\u5728\u677e\u6797\u8def \u65c1\uff0c\u88ab\u5a49\u513f\u4e4b\u6bcd\u201c\u6155\u5bb9\u592b\u4eba\u201d\u6551\u56de\u201c\u8c79\u7a81\u5c71\u5e84\u201d\uff0c\u5367\u75c5\u5728\u5e8a\u7684\u60c5\u666f\u3002 \u4ed6\u53c8\u60f3\u5230\uff1a\u5728\u5929\u8fb9\u67d3\u6ee1\u4e86\u9c9c\u8273\u665a\u971e\u7684\u9ec4\u660f\uff0c\u4e09\u4eba\u5728\u9759\u7a7a\u4e4b\u4e2d\uff0c\u4e92\u6807\u5bb6\u4e16\uff0c\u5a13\u5a13\u6e05\u6de1\uff0c\u5a49 \u513f\u7684\u5a07\u61a8\u5929\u771f\uff0c\u5999\u8bed\u5982\u6b8a\uff0c\u9752\u6625\u5c11\u5973\u65e0\u5fe7\u7684\u7b11\u58f0\uff0c\u7ed9\u4ed6\u5c1d\u5c3d\u4eba\u4e16\u708e\u51c9\u7684\u82e6\u95f7\u5fc3\u7075\uff0c\u5e26\u6765\u4e86\u591a \u5c11\u5b89\u6170\uff1f\u2026\u2026 \u4ed6\u4e5f\u60f3\u8d77\uff0c\u201c\u51cc\u98ce\u516c\u5b50\u201d\u8981\u628a\u81ea\u5df2\u4e22\u51fa\u5ba4\u5916\uff0c\u5979\u60f3\u5c3d\u65b9\u6cd5\u4e3a\u81ea\u5df2\u7f13\u988a\uff0c\u201c\u8c79\u7a81\u5c71\u5e84\u201d\u8840 \u6218\u4e4b\u540e\uff0c\u5979\u5192\u7740\u88ab\u7236\u4eb2\u8d23\u9a82\uff0c\u5f15\u81ea\u5df1\u8131\u79bb\u9669\u5730\uff0c\u4ee5\u540e\u81ea\u5df1\u79bb\u5f00\u6d4e\u5357\uff0c\u5979\u5339\u9a6c\u5929\u6daf\u6765\u5bfb\u627e\u81ea \u5df2\uff0c\u5728\u201c\u5174\u9686\u9547\u201d\uff0c\u81ea\u5df1\u4f24\u5728\u201c\u8840\u638c\u706b\u9f99\u201d\u638c\u4e0b\uff0c\u5979\u594b\u4e0d\u987e\u8eab\u76f8\u6551\u2026\u2026\u4e00\u5207\u7684\u4e00\u5207\uff0c\u7b14\u96be\u80dc \u4e66\uff0c\u53ea\u4f7f\u4ed6\u611f\u5230\u201c\u7f8e\u4eba\u6069\u6df1\u6df1\u4f3c\u6d77\u201d\uff01 \u201c\u5c55\u54e5\u2026\u2026\u5c0f\u4fa0\uff01\u201d \u5c55\u767d\u8033\u8fb9\u7a81\u7136\u54cd\u8d77\u83ba\u58f0\u5456\u5456\uff0c\u628a\u4ed6\u4ece\u7eee\u601d\u9065\u60f3\u4e2d\u60ca\u9192\uff0c\u56de\u5934\u4e00\u770b\uff0c\u539f\u6765\u662f\u201c\u6c5f\u5357\u7b2c\u4e00\u7f8e \u4eba\u201d\u91d1\u5f69\u51e4\uff0c\u4e0d\u77e5\u4f55\u65f6\u5df2\u6765\u5728\u4ed6\u7684\u8eab\u8fb9\uff0c\u6b63\u7528\u4e00\u53cc\u7f8e\u76ee\u7d27\u671b\u7740\u4ed6\u548c\u660f\u8ff7\u7684\u5a49\u513f\u3002 \u672a\u7b49\u5c55\u767d\u7b54\u8bdd\uff0c\u91d1\u5f69\u51e4\u53c8\u9053\uff1a\u5a49\u513f\u59d1\u5a18\u7684\u4f24\uff0c\u4e0d\u8981\u7d27\u5427\uff1f\u201d \u5c55\u767d\u770b\u51fa\u91d1\u5f69\u51e4\u773c\u5149\u4e2d\u4f3c\u6709\u4e00\u4e1d\u5992\u5ff5\uff0c\u4f46\u5a49\u513f\u4f24\u91cd\uff0c\u4f7f\u4ed6\u4e0d\u6687\u522b\u60f3\uff0c\u53ea\u5251\u7709\u4e00\u76b1\u9053\uff1a\u5f88 \u4e25\u91cd\u2026\u2026\u201d \u7a81\u7136\u6570\u58f0\u60e8\u55e5\uff0c\u628a\u5c55\u767d\u7684\u8bdd\u6253\u65ad\uff0c\u4e8c\u4eba\u540c\u65f6\u6115\u7136\u60ca\u89c6\uff0c\u53ea\u89c1\u573a\u4e2d\u5df2\u6709\u6570\u4eba\u5012\u5367\u8840\u6cca\u4e4b \u4e2d\uff01 \u4e00\u539f\u6765\u201c\u9ed1\u9053\u56db\u51f6\u201d\u6b66\u529f\u9ad8\u5f3a\uff0c\u624b\u6bb5\u6bd2\u8fa3\uff0c\u7fa4\u96c4\u867d\u7136\u4eba\u591a\uff0c\u4f46\u5374\u4e0d\u662f\u56db\u51f6\u7684\u5bf9\u624b\uff01 \u201c\u7a77\u5bb6\u5e2e\u201d\u5f1f\u5b50\u6b7b\u4f24\u60e8\u91cd\uff0c\u201c\u6b66\u5f53\u4e09\u9053\u201d\u6709\u4e8c\u9053\u8d1f\u4f24\uff0c\u201c\u70b9\u82cd\u53cc\u5251\u201d\u6b7b\u53bb\u4e00\u5251\uff0c\u201c\u6cd5\u534e \u5357\u5b97\u201d\u3001\u201c\u4e03\u6b65\u8ffd\u9b42\u201d\u9635\u4ea1\uff0c\u8fde\u5c11\u6797\u7f57\u6c49\u5802\u9996\u5ea7\u201c\u864e\u76ee\u5c0a\u8005\u201d\u90fd\u53d7\u4e86\u201c\u8471\u5cad\u4e4b\u9e70\u201d\u7684\u201c\u67af\u9aa8 \u638c\u201d\uff01 \u773c\u89c1\u4e00\u5e72\u4fa0\u4e49\u9053\u7fa4\u96c4\u6b7b\u4f24\u60e8\u91cd\uff0c\u800c\u201c\u9ed1\u9053\u56db\u51f6\u201d\u72b9\u5982\u56db\u4e2a\u51f6\u715e\u6076\u9b3c\uff0c\u602a\u5578\u72c2\u7b11\uff0c\u62db\u62db\u6740 \u624b\uff0c\u5c55\u767d\u5fc3\u4e2d\u53c8\u6025\u53c8\u6012\uff01 \u53ef\u662f\uff0c\u4ed6\u6b64\u65f6\u6b63\u4ee5\u5185\u529b\u4e3a\u5a49\u513f\u7597\u4f24\uff0c\u773c\u89c1\u5a49\u513f\u9b13\u89d2\u9f3b\u5c16\u5fae\u5fae\u89c1\u6c57\uff0c\u79c0\u7f8e\u7684\u8138\u4e0a\u75db\u82e6\u4e4b\u60c5 \u5df2\u89c1\u51cf\u5c0f\uff0c\u8eab\u4e0a\u7684\u70ed\u5ea6\u4e5f\u6e10\u6e10\u9000\u53bb\uff01 \u5047\u5982\u81ea\u5df2\u6b64\u65f6\u653e\u624b\uff0c\u53ef\u80fd\u4f1a\u843d\u4e2a\u529f\u8d25\u5782\u6210\uff01 \u4f46\u4e5f\u4e0d\u80fd\u773c\u770b\u7740\u4e00\u5343\u4fa0\u4e49\u9053\u7fa4\u96c4\uff0c\u5f15\u9888\u5c31\u622e\uff0c\u800c\u81ea\u5df1\u4e0d\u52a0\u62db\u624b\u554a\uff01 \u540c\u662f\u6551\u4eba\uff0c\u4f46\u6551\u6b64\u5fc5\u5931\u5f7c\uff01 \u8fd9\u4f7f\u5c55\u767d\u5927\u5927\u5730\u4e3a\u96be\u2026\u2026 \u5c31\u5728\u5c55\u767d\u8fd9\u72b9\u8c6b\u96be\u51b3\u4e4b\u9645\uff0c\u770b\u89c1\u201c\u592a\u4ed3\u4e4b\u9f20\u201d\u4ee5\u201c\u7f29\u7b4b\u9b54\u529f\u201d\u63a5\u8fde\u5411\u96f7\u5927\u53d4\u653b\u51fa\u4e09\u5927\u6740 \u62db\uff01 \u5c31\u5728\u96f7\u5927\u53d9\u5371\u9669\u4e07\u72b6\u4e4b\u95f4\uff0c\u201c\u8471\u5cad\u4e4b\u9e70\u201d\u4e00\u638c\u53c8\u5288\u7ffb\u4e86\u4e24\u540d\u201c\u7a77\u5bb6\u5e2e\u201d\u5f1f\u5b50\uff0c\u563f\u563f\u9634\u7b11 \u7740\uff0c\u4e3e\u638c\u5411\u96f7\u5927\u53d4\u5934\u9876\u201c\u767e\u4f1a\u201d\u91cd\u7a74\u62cd\u53bb\uff0e\uff0eo\u2019\u5c55\u767d\u4e00\u89c1\u5927\u6025\uff0c\u628a\u5a49\u513f\u4ea4\u7ed9\u91d1\u5f69\u51e4\uff0c\u6025 \u9053\uff1a\u201c\u8bf7\u4f60\u7167\u987e\u5979\u4e00\u4e0b\uff0c\u7528\u638c\u5fc3\u63a5\u4efb\u5979\u2018\u4e73\u4e2d\u2019\u7a74\u4e0a\uff0c\u8f93\u4ee5\u5185\u529b\uff0c\u4e3a\u5979\u9a71\u51fa\u4f53\u5185\u70ed\u6bd2\u2026\u2026\u201d \u6b64\u65f6\uff0c\u96f7\u5927\u53d4\u594b\u529b\u5288\u51fa\u4e00\u638c\u3001\u903c\u9000\u201c\u592a\u4ed3\u4e4b\u9f20\u201d\uff0c\u8eab\u5f62\u63a5\u8fde\u8f6c\u6362\u4e86\u4e09\u4e2a\u65b9\u4f4d\uff0c\u4ecd\u7136\u672a\u8eb2 \u51fa\u201c\u8471\u5cad\u4e4b\u9e70\u201d\u90a3\u201c\u67af\u9aa8\u638c\u201d\u4e00\u5927\u6740\u62db\u7684\u5a01\u529b\u8303\u56f4\uff01 \u201c\u8471\u5cad\u4e4b\u9e70\u201d\u50f5\u5c38\u4f3c\u7684\u8eab\u6750\uff0c\u5982\u5f71\u968f\u5f62\uff0c\u8ddf\u5b9a\u4e86\u96f7\u5927\u53d4\uff0c\u5f85\u4ed6\u90a3\u4e00\u638c\u98d8\u5ffd\u62cd\u81f3\u4e00\u4e2a\u6709\u5229 \u7684\u90e8\u4f4d\uff0c\u96f7\u5927\u53d4\u5df2\u65e0\u6cd5\u8eb2\u95ea\uff0c\u4ed6\u201c\u67af\u9aa8\u638c\u201d\u53c8\u52a0\u4e86\u51e0\u6210\u529b\u9053\uff0c\u731b\u7136\u538b\u4e86\u4e0b\u53bb\uff01 \u4e00\u8fb9\u53e3\u4e2d\u53d1\u51fa\u5982\u72fc\u55e5\u4f3c\u7684\u6840\u6840\u602a\u7b11\u2026\u2026 \u5c55\u767d\u8d76\u6551\u4e0d\u53ca\uff0c\u8d2f\u8db3\u4e86\u5185\u529b\uff0c\u5927\u559d\u4e00\u58f0\uff1a\u4f4f\u624b\uff01\u201d \u8fd9\u4e00\u58f0\u558a\uff0c\u56e0\u4e3a\u662f\u5c55\u81ea\u5168\u8eab\u529f\u529b\u52ab\u77aa\u8bf4\u5982\u5e73\u5730\u54cd\u8d77\u4e00\u4e2a\u7126\u96f7\uff01 \u53ea\u9707\u5f97\u4e07\u6728\u843d\u53f6\uff0c\u6570\u4e08\u4e4b\u5916\u7684\u5927\u5385\uff0c\u7a97\u6447\u58c1\u52a8\uff0c\u4f17\u4eba\u8033\u9f13\u66f4\u662f\u5982\u53d7\u94f2\u51fb\uff0c\u55e1\uff01\u55e1\uff01\u8f70\u9e23 \u4e0d\u5df2\uff01 \u8fd9\u4e00\u58f0\u5927\u558a\uff0c\u6709\u4f3c\u4f5b\u5bb6\u81f3\u9ad8\u65e0\u4e0a\u7684\u63b8\u529f\u201c\u72ee\u5b50\u543c\u201d\u4e0d\u4f46\u201c\u8471\u5cad\u4e4b\u9e70\u201d\u4f4f\u4e86\u624b\uff0c\u5373\u8fde\u6240\u6709 \u4e4b\u4eba\u90fd\u505c\u4e86\u4e0b\u6765\uff01 \u4e0a\u524d\u6765\uff0c\u4fca\u76ee\u5982\u7535\uff0c\u626b\u5c04\u4e86\u4e00\u773c\u60ca\u89c6\u7740\u4ed6\u7684\u4f17\u4eba\uff0c\u7136\u540e\u53cc\u811a\u53c9\u5f00\uff0c\u5dcd\u7136\u5982\u5c71\u5cb3\u822c\u5730\u633a\u7acb \u5f53\u573a\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u56db\u4f4d\u81ea\u79f0\u2018\u9ed1\u9053\u56db\u51f6\u2019\uff0c\u679c\u7136\u5fc3\u72e0\u624b\u8fa3\uff0c\u65e0\u7aef\u6740\u4f24\u591a\u4eba\uff0c\u5982\u518d\u4e0d\u9002\u53ef\u800c \u6b62\uff0c\u8bf4\u4e0d\u5f97\u5c55\u767d\u8981\u7ed9\u56db\u4f4d\u4e00\u70b9\u6559\u8bad\uff01\u201d \u5c55\u767d\u6b64\u8a00\u4e00\u51fa\uff0c\u56db\u51f6\u4f8b\u6709\u4e09\u51f6\u4ef0\u5929\u53d1\u51fa\u4e00\u9635\u72c2\u7b11\uff01 \u663e\u7136\u4ed6\u4eec\u672a\u628a\u5c55\u767d\u653e\u8fdb\u773c\u5185\uff01 \u201c\u6c99\u6f20\u4e4b\u72d0\u201d\u662f\u9634\u7b11\uff0c\u201c\u592a\u4ed3\u4e4b\u9f20\u201d\u662f\u51b7\u7b11\uff0c\u201c\u8471\u5cad\u4e4b\u9e70\u201d\u662f\u6840\u6840\u602a\u7b11\uff0c\u4e09\u79cd\u4e0d\u540c\u7684\u7b11 \u58f0\uff0c\u72b9\u5982\u4e00\u4e2a\u4e09\u91cd\u594f\uff0c\u4f46\u4ff1\u90fd\u6740\u6c14\u903c\u4eba\uff01 \u53ea\u6709\u201c\u9634\u5c71\u4e4b\u72fc\u201d\u6ca1\u7b11\uff0c\u4ed6\u4e00\u4e3e\u624b\u6b62\u4f4f\u4e09\u51f6\u7684\u7b11\u58f0\uff0c\u9762\u5411\u5c55\u767d\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\u83ab\u4e0d\u5c31\u662f\u4e00 \u4eba\u529b\u6218\u2018\u6d77\u5916\u4e09\u715e\u2019\u7684\u2018\u65e0\u60c5\u78a7\u5251\u2019\u5c55\u5c11\u4fa0\uff1f\u201d \u5c55\u767d\u9053\uff1a\u533a\u533a\u4e0d\u624d\uff0c\u6b63\u662f\u5728\u4e0b\uff01\u201d \u201c\u9ed1\u9053\u56db\u51f6\u201d\u540c\u65f6\u4e00\u695e\uff0c\u5728\u4ed6\u4eec\u752b\u5230\u4e2d\u539f\u65f6\uff0c\u5373\u542c\u201c\u5357\u6d77\u95e8\u201d\u7684\u4eba\u8bf4\uff0c\u4e2d\u539f\u6b66\u6797\u6709\u7edd\u9876 \u9ad8\u624b\uff0c\u8eab\u80cc\u201c\u65e0\u60c5\u78a7\u5251\u201d\uff0c\u540d\u53eb\u5c55\u767d\uff0c\u66fe\u4e00\u4eba\u529b\u6218\u201c\u6d77\u5916\u4e09\u715e\u201d\uff01 \u201c\u9ed1\u9053\u56db\u51f6\u201d\u603b\u4ee5\u4e3a\u201c\u65e0\u60c5\u5f71\u5251\u201d\u5c55\u767d\uff0c\u4e00\u5b9a\u662f\u9690\u9041\u4e16\u5916\u7684\u524d\u4ee3\u9ad8\u4eba\uff0c\u4f46\u6ca1\u60f3\u5230\u5c55\u767d\u4f1a \u5982\u6b64\u5e74\u8f7b\uff01 \u56e0\u6b64\uff0c\u5c55\u767d\u7b2c\u4e00\u6b21\u51fa\u9762\uff0c\u4e00\u638c\u9707\u9000\u201c\u592a\u4ed3\u4e4b\u9f20\u201d\uff0c\u4e14\u81ea\u62a5\u59d3\u540d\uff0c\u4ed6\u4eec\u56db\u4eba\u90fd\u672a\u66fe\u6ce8\u610f \u5230\uff0c\u773c\u524d\u5c11\u4e2d\u5373\u662f\u201c\u5357\u6d77\u95e8\u201d\u8ba4\u4e3a\u514b\u661f\u7684\u201c\u65e0\u60c5\u78a7\u5251\u201d\uff01 \u5c55\u767d\u7b2c\u4e8c\u6b21\u51fa\u9762\uff0c\u8fd9\u4e00\u58f0\u5927\u559d\uff0c\u663e\u793a\u51fa\u5185\u529b\u4e4b\u96c4\u539a\uff0c\u5176\u4ed6\u4e09\u51f6\u5c1a\u672a\u53d1\u89c9\uff0c\u53ea\u89c9\u773c\u524d\u9752\u5e74 \u4eba\u5185\u529f\u4e0d\u5f31\uff0c\u4f46\u5c31\u51ed\u5c55\u767d\u8fd9\u70b9\u5b50\u4e2d\u7eaa\uff0c\u9762\u5411\u201c\u9ed1\u9053\u56db\u51f6\u201d\u5356\u72c2\uff0c\u4ee5\u201c\u9ed1\u9053\u56db\u51f6\u201d\u7684\u6b66\u529f\u53ca\u8eab \u4efd\u6765\u8bf4\uff0c\u5b9e\u5728\u5fcd\u4e0d\u4f4f\u55e4\u4e4b\u4ee5\u9f3b\u3002 \u53ef\u662f\uff0c\u201c\u9634\u5c71\u4e4b\u72fc\u201d\u6bd4\u8f83\u5fc3\u7ec6\uff0c\u5c55\u767d\u4e24\u6b21\u62a5\u51fa\u540d\u5b57\uff0c\u4ed6\u9661\u7136\u8bb0\u8d77\u5728\u201c\u5357\u6d77\u95e8\u201d\u542c\u5230\u7684\u4f20 \u8bf4\uff0c\u6545\u800c\u62e6\u4f4f\u4e09\u51f6\u53d1\u7b11\uff0c\u5f85\u4ed6\u8bc1\u5b9e\u4e86\u773c\u524d\u5c11\u5e74\uff0c\u679c\u662f\u201c\u65e0\u60c5\u78a7\u5251\u201d\u5c55\u767d\u65f6\uff0c\u5fc3\u4e2d\u4e0d\u7531\u5fae\u61d4\uff01 \u4ee5\u4ed6\u201c\u638c\u5203\u5207\u6728\u201d\u7684\u529f\u592b\uff0c\u8fd0\u96c6\u4e86\u4e03\u6210\u529f\u529b\uff0c\u638c\u5203\u5982\u5200\uff0c\u201c\u5237\u201d\u7684\u4e00\u58f0\uff0c\u5411\u5c55\u767d\u780d\u51fa\u4e00 \u638c\uff01 \u5c55\u767d\u5fae\u5fae\u4e00\u6652\u201c\u5929\u4f5b\u964d\u9b54\u638c\u201d\u8fd0\u4e86\u516d\u6210\uff0c\u8f7b\u98d8\u98d8\u4e00\u638c\u5411\u6765\u52bf\u8fce\u51fb\uff01 \u201c\u7830\u201d\u7684\u4e00\u58f0\u66b4\u54cd\uff0c\u201c\u9634\u5c71\u4e4b\u72fc\u201d\u5f53\u573a\u88ab\u9707\u9000\u4e94\u6b65\uff01 \u5c55\u767d\u5374\u7a33\u7acb\u5982\u4ece\u7eb9\u4e1d\u4e0d\u52a8\uff0c\u53ea\u90a3\u9752\u886b\u4e0b\u6446\uff0c\u62c2\u62c2\u6446\u4e86\u6570\u4e0b\uff01 \u201c\u8471\u5cad\u4e4b\u9e70\u201d\u5927\u559d\u4e00\u58f0\uff0c\u201c\u67af\u9aa8\u638c\u201d\u8fd0\u81f3\u5341\u6210\uff0c\u53cc\u638c\u731b\u7136\u5411\u5c55\u767d\u7684\u80f8\u524d\u63a8\u53bb\uff01 \u638c\u98ce\u72c2\u5578\uff0c\u4e00\u7247\u8000\u773c\u7684\u7ea2\u5149\uff01 \u7b2c\u4e94\u5341\u516d\u7ae0\u3000\u201c\u5929\u4f5b\u7edd\u5b66\u201d\u626b\u5996\u6c1b>> \u53e4\u9f99\u300a\u5251\u5ba2\u884c\u300b \u7b2c\u4e94\u5341\u516d\u7ae0\u3000\u201c\u5929\u4f5b\u7edd\u5b66\u201d\u626b\u5996\u6c1b \u4f17\u4eba\u9f50\u58f0\u60ca\u81c6\uff01\u638c\u529b\u5982\u6b64\u5a01\u731b\uff0c\u4e16\u6240\u7f55\u89c1\uff01 \u4f46\u4e5f\u66f4\u6fc0\u8d77\u4e86\u5c55\u81ea\u7684\u8c6a\u5174\uff0c\u6697\u5fd6\uff1a\u201c\u770b\u6211\u2018\u5929\u4f5b\u7edd\u5b66\u2019\u662f\u5426\u5929\u4e0b\u65e0\u654c\uff1f\u201d \u5fc3\u4e2d\u60f3\u7740\uff0c\u610f\u968f\u529f\u81f3\uff0c\u4e5f\u628a\u7ec3\u81ea\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\u7edd\u9876\u5185\u529f\u8fc4\u81f3\u5341\u6210\uff0c\u4e00\u62db\u201c\u4f5b\u5149\u666e \u7167\u201d\uff0c\u786c\u5411\u65e0\u500d\u7684\u638c\u98ce\u5de8\u6d41\u8fce\u53bb\uff01 \u201c\u8f70\u9686\uff01\u201d\u4e00\u58f0\u60ca\u5929\u7684\u5de8\u9707\uff01 \u4f17\u4eba\u5931\u58f0\u60ca\u547c\u4e4b\u4e2d\uff0c\u4e24\u80a1\u5de8\u5927\u7684\u638c\u98ce\u5de8\u6d41\u51b2\u5929\u800c\u8d77\uff0c\u201c\u547c\u5566\u5566\uff01\u201d\u4e00\u7247\u66b4\u54cd\uff0c\u4e8c\u6587\u4f59\u5916 \u7684\u5927\u5385\u5c4b\u9876\u4e0a\u7684\u6ef4\u6c34\u6a90\uff0c\u88ab\u638c\u98ce\u4f59\u529b\u6380\u53bb\u4e86\u4e00\u89d2\uff0c\u7834\u7816\u788e\u74e6\uff0c\u6ee1\u7a7a\u6a2a\u98de\uff01 \u5730\u9762\u4e0a\u66f4\u662f\u5377\u77f3\u98de\u6c99\uff0c\u52b2\u98ce\u56db\u5c04\uff0c\u4f17\u4eba\u6c14\u5747\u4e3a\u4e4b\u4e00\u7a92\uff01 \u5de8\u529b\u53cd\u9707\uff0c\u5f25\u5929\u5c18\u6c99\u5f71\u4e2d\uff0c\u53ea\u89c1\u201c\u8471\u5cad\u4e4b\u9e70\u201d\u5bbd\u5927\u957f\u888d\u9f13\u8d77\u5982\u7bf7\uff0c\u6ee1\u5934\u4e71\u53d1\u98d8\u4e3e\uff0c\u77aa\uff01 \u77aa\uff01\u77aa\uff01\u5012\u9000\u4e09\u6b65\uff0c\u52c9\u5f3a\u62ff\u6869\u7ad9\u7a33\uff0c\u72b9\u81ea\u6447\u63f4\u4e0d\u6b62\uff0c\u51e0\u6b32\u4e58\u98ce\u98de\u53bb\uff01 \u5c55\u767d\u4e5f\u5012\u9000\u4e86\u4e00\u6b65\uff0c\u8863\u98de\u53d1\u626c\uff0c\u4e0a\u8eab\u6643\u4e86\u4e24\u6643\uff0c\u4fca\u7f8e\u7684\u8138\u4e0a\u4e00\u7247\u8273\u7ea2\uff01 \u98ce\u5b9a\u5c18\u6536\uff0c\u201c\u8471\u5cad\u4e4b\u9e70\u201d\u52aa\u529b\u8c03\u606f\uff0c\u5f3a\u538b\u4f4f\u5185\u8151\u7ffb\u817e\u7684\u6c14\u8840\uff0c\u60e8\u7b11\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u8001\u592b \u7684\u2018\u67af\u7763\u638c\u2019\u5473\u9053\u5982\u4f55\uff1f\u201d \u5c55\u767d\u521d\u63a5\u201c\u8471\u5cad\u4e4b\u9e70\u201d\u638c\u529b\u65f6\uff0c\u786e\u5b9e\u611f\u5230\u4e00\u822c\u5982\u706b\u7684\u70ed\u6d41\u901a\u8fc7\u5468\u8eab\uff0c\u4e09\u767e\u516d\u5341\u6839\u9aa8\u8282\u5374 \u50cf\u6d47\u4e0a\u4e86\u4e00\u74e2\u6eda\u6c34\uff0c\u4f46\u4ed6\u53ca\u65f6\u8fd0\u529f\u963b\u4f4f\uff0c\u7adf\u7136\u628a\u90a3\u4e00\u822c\u70ed\u6d41\u8feb\u51fa\u4f53\u5916\uff0c\u4ed6\u53c8\u6697\u884c\u8fd0\u529f\u4e00\u904d\uff0c \u53ea\u611f\u5468\u8eab\u6c14\u8840\u7545\u884c\u65e0\u963b\uff0c\u4fe1\u5fc3\u5927\u589e\uff0c\u89c1\u201c\u8471\u5cad\u4e4b\u9e70\u201d\u53c8\u5411\u4ed6\u5356\u72c2\uff0c\u4e0d\u7531\u4e00\u7b11\uff0c\u9053"\u4e0d\u8fc7\u5c14 \u5c14\uff01\u201d \u201c\u8471\u5cad\u4e4b\u9e70\u201d\u8138\u4e0a\u62b9\u8fc7\u4e00\u7f15\u5947\u5f02\u7684\u8272\u5f69\uff0c\u4ed6\u4e0d\u76f8\u4fe1\u773c\u524d\u5e74\u8f7b\u4eba\u786c\u63a5\u4e86\u4ed6\u7684\u201c\u67af\u9aa8\u638c\u201d\uff0c \u4f1a\u5b89\u7136\u65e0\u4e8b\uff0c\u4ed6\u5f3a\u63d0\u4e00\u53e3\u771f\u6c14\uff0c\u53c8\u602a\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u53ef\u6562\u518d\u63a5\u8001\u592b\u4e00\u638c\u8bd5\u8bd5\uff1f\u201d \u5c55\u767d\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e0d\u8981\u8bf4\u662f\u4e00\u638c\uff0c\u5341\u638c\u3001\u767e\u638c\uff0c\u5c55\u767d\u4e5f\u6562\u63a5\u4e0b\uff01\u201d \u201c\u8471\u5cad\u4e4b\u9e70\u201d\u867d\u6709\u70b9\u771f\u529b\u8870\u7aed\u7684\u611f\u89c9\uff0c\u4f46\u59dc\u8001\u5f25\u8fa3\uff0c\u4ed6\u6697\u5fd6\u5c55\u767d\u5b9a\u4e5f\u662f\u5f3a\u6bcf\u4e4b\u672b\uff0c\u5bf9\u654c \u5230\u4e86\u53cc\u65b9\u840e\u987f\u7684\u5173\u5934\uff0c\u8c01\u80fd\u575a\u6301\u5230\u6700\u540e\u4e00\u5239\u90a3\uff0c\u8c01\u4fbf\u662f\u80dc\u5229\u8005\u3002\u56e0\u6b64\uff0c\u4ed6\u8868\u9762\u4e0a\u4e0d\u9732\u4e00\u70b9\u8d25 \u8c61\uff0c\u529f\u8d2f\u53cc\u638c\uff0c\u568e\u53eb\u4e00\u58f0\u9053\uff1a\u201c\u597d\uff01\u518d\u63a5\u8001\u592b\u4e00\u638c\uff01\u201d \u5c55\u767d\u4e00\u6446\u624b\uff0c\u9053\uff1a\u201c\u6162\u70b9\uff01\u201d \u201c\u8471\u5cad\u4e4b\u9e70\u201d\u53cc\u638c\u4e3e\u52bf\u6b32\u5288\uff0c\u9053\uff1a\u201c\u53ef\u662f\u6015\u4e86\u5417\uff1f\u201d \u5c55\u767d\u5fae\u5fae\u4e00\u6652\uff0c\u9053\uff1a\u201c\u533a\u533a\u5728\u4e0b\uff0c\u5e73\u751f\u8fd8\u4e0d\u61c2\u5f97\u4ec0\u4e48\u53eb\u6015\uff1f\u4e0d\u8fc7\u6709\u51e0\u53e5\u8bdd\u5148\u8981\u8bf4\u660e\u3002\u201d \u201c\u8471\u5cad\u4e4b\u9e70\u201d\u9053\uff1a\u201c\u4ec0\u4e48\u8bdd\uff1f\u8bf7\u8bb2\uff01\u201d \u5c55\u767d\u9053\uff1a\u201c\u5728\u4e0b\u4e0e\u4eba\u6709\u7ea6\uff0c\u4e0d\u95ee\u6c5f\u6e56\u662f\u975e\uff0c\u662f\u4ee5\u4e0d\u4fbf\u4e0e\u9601\u4e0b\u52a8\u624b\u2026\u2026\u201c\u201c\u592a\u4ed3\u4e4b\u9f20\u201d\u7a81 \u7136\u4e00\u9635\u602a\u7b11\uff0c\u63d2\u53e3\u9053\uff1a\u201c\u8bf4\u6765\u8bf4\u53bb\uff0c\u8fd8\u4e0d\u662f\u6015\u4e86\uff01\u201d \u5c55\u767d\u77a5\u4e86\u4ed6\u4e00\u773c\uff0c\u8d1f\u624b\u7b11\u9053\uff1a\u201c\u4e0d\u89c1\u5f97\uff01\u201d \u201c\u9634\u5c71\u4e4b\u72fc\u201d\u51b7\u773c\u65c1\u89c2\uff0c\u4ee5\u4e3a\u5c55\u767d\u53ef\u80fd\u662f\u88ab\u201c\u8471\u5cad\u4e4b\u9e70\u201d\u201c\u67af\u9aa8\u638c\u201d\u6240\u4f24\uff0c\u501f\u53e3\u62d6\u5ef6\u65f6 \u95f4\uff0c\u4ee5\u4fbf\u8fd0\u529f\u7597\u4f24\uff0c\u773c\u73e0\u4e00\u8f6c\uff0c\u8ba1\u4e0a\u5fc3\u6765\u3002\u7acb\u523b\u63d2\u53e3\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\u4e0d\u8981\u63a8\u4e09\u963b\u56db\uff0c\u6211\u4eec\u8001\u54e5 \u56db\u4e2a\u521a\u521a\u56de\u5230\u4e2d\u539f\uff0c\u8fd8\u672a\u8e0f\u5165\u6c5f\u6e56\u4e00\u6b65\uff0c\u5c0f\u5144\u5f1f\u6709\u4ec0\u4e48\u7edd\u827a\uff0c\u5c3d\u7ba1\u65bd\u4e3a\uff0c\u5c31\u7b97\u4f60\u6ca1\u6709\u8fc7\u95ee\u6c5f \u6e56\u662f\u975e\u597d\u4e86\uff01\u201d \u5c55\u767d\u5251\u7709\u8f69\u52a8\uff0c\u661f\u76ee\u5982\u7535\uff0c\u626b\u4e86\u56db\u51f6\u4e00\u773c\uff0c\u9053\uff1a\u201c\u6b64\u8bdd\u80fd\u4ee3\u8868\u4f60\u4eec\u56db\u4f4d\u7684\u610f\u89c1\u5417\uff1f\u201d \u201c\u6c99\u6f20\u4e4b\u72d0\u201d\u9053\uff1a\u201c\u6709\u4f55\u4e0d\u53ef\uff01\u201d \u5c55\u767d\u7a81\u7136\u4ef0\u5929\u957f\u5578\u4e86\u4e00\u58f0\uff0c\u6709\u5982\u9e64\u552c\u9752\u7a7a\uff0c\u4f59\u97f3\u6276\u6447\u76f4\u4e0a\uff0c\u817e\u5165\u4e91\u9704\uff01 \u56db\u51f6\u9f50\u7686\u4e00\u6115\uff0c\u4e0d\u77e5\u5c55\u767d\u56e0\u4f55\u7a81\u7136\u957f\u5578\uff1f \u5c55\u767d\u4e00\u58f0\u957f\u5578\uff0c\u5374\u4eff\u4f5b\u5410\u5c3d\u4e86\u80f8\u4e2d\u7684\u95f7\u6c14\uff0c\u8c6a\u6c14\u5e72\u4e91\u5730\u9053\uff1a \u201c\u597d\u4e86\uff0c\u4f60\u4eec\u56db\u4f4d\u4e00\u9f50\u4e0a\u5427\uff01\u201d \u56db\u51f6\u53c8\u662f\u4e00\u6115\uff01 \u201c\u9634\u5c71\u4e4b\u72fc\u201d\u4f3c\u662f\u6ca1\u6709\u542c\u61c2\u5c55\u767d\u7684\u8bdd\u4e2d\u4e4b\u610f\uff0c\u7591\u95ee\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u521a\u624d\u600e\u4e48\u8bf4\uff1f\u201d \u5c55\u767d\u9053\uff1a\u201c\u5728\u4e0b\u4e00\u4eba\uff0c\u8981\u4f1a\u4f1a\u4f60\u4eec\u2018\u9ed1\u9053\u56db\u51f6\u2019\uff01\u201d \u90a3\u201c\u8471\u5cad\u4e4b\u9e70\u201d\u5df2\u7136\u66b4\u6012\uff0c\u5927\u559d\u4e00\u58f0\uff0c\u201c\u67af\u9aa8\u638c\u201d\u8fd0\u81f3\u98a0\u5cf0\uff0c\u731b\u7136\u5411\u5c55\u767d\u62cd\u53bb\u3002 \u7ea2\u5149\u8000\u773c\uff0c\u72c2\u5578\u76c8\u8033\uff01 \u5c55\u767d\u559d\u4e86\u58f0\uff1a\u201c\u6765\u5f97\u597d\uff01\u201d \u540c\u65f6\uff0c\u638c\u6f14\u201c\u5929\u4f5b\u7edd\u5b66\u201d\uff0c\u62db\u51fa\u201c\u6325\u6e05\u5996\u6c14\u201d\uff0c\u201c\u8f70\u201d\u7136\u5de8\u9707\u58f0\u4e2d\uff0c\u4e00\u638c\u628a\u201c\u8471\u5cad\u4e4b \u9e70\u201d\u53c8\u9707\u9000\u4e94\u6b65\uff01 \u201c\u8471\u5cad\u4e4b\u9e70\u201d\u88ab\u9707\u5f97\u4e94\u5185\u751f\u614c\uff0c\u6b64\u65f6\uff0c\u4ed6\u624d\u77e5\u5c55\u767d\u5185\u529b\u4e0d\u4f46\u4e1d\u6beb\u672a\u635f\uff0c\u800c\u4e14\u6bd4\u521a\u624d\u4e00\u638c \u529b\u9053\u66f4\u5927\uff0c\u5fc3\u4e2d\u6697\u61d4\uff1a\u201c\u8fd9\u5e74\u8f7b\u4eba\u679c\u7136\u4e0d\u7b80\u5355\u2026\u2026\u201d \u4f46\u5c55\u767d\u4e00\u638c\u9707\u9000\u201c\u8471\u5cad\u4e4b\u9e70\u201d\u8eab\u5f62\u7535\u65cb\uff0c\u5019\u7136\u53c8\u65bd\u51fa\u4e00\u62db\u201c\u98ce\u9707\u96f7\u9e23\u201d\u638c\u9707\u201c\u9634\u5c71\u4e4b \u72fc\u201d\u5929\u7075\u8981\u5bb3\uff01 \u201c\u9634\u5c71\u4e4b\u72fc\u201d\u89c1\u5c55\u767d\u638c\u52bf\u592a\u731b\uff0c\u4e0d\u6562\u786c\u63a5\uff0c\u7529\u5934\u659c\u6b65\uff0c\u8eb2\u8fc7\u5c55\u767d\u4e00\u638c\uff0c\u4ee5\u4ed6\u201c\u638c\u5203\u5207 \u6728\u201d\u7684\u7edd\u9876\u786c\u529f\uff0c\u53cd\u81c2\u5288\u51fa\u4e00\u638c\uff01 \u638c\u7f18\u5982\u5200\uff0c\u7834\u98ce\u5411\u5c55\u81ea\u5de6\u81c2\u5207\u5230\uff01 \u5c55\u767d\u5355\u811a\u4e00\u70b9\u5730\u9762\uff0c\u8eab\u5f62\u7115\u7136\u817e\u8d77\u516b\u5c3a\uff0c\u534a\u7a7a\u4e2d\u201c\u4f5b\u7956\u964d\u5ea7\u201d\uff0c\u53cc\u638c\u53c8\u731b\u5411\u201c\u592a\u4ed3\u4e4b \u9f20\u201d\u6253\u53bb\uff01 \u201c\u592a\u4ed3\u4e4b\u9f20\u201d\u8eab\u5f62\u4e00\u7f29\uff0c\u66b4\u77ee\u4e09\u5c3a\uff0c\u4e24\u6761\u957f\u81c2\u5982\u4e24\u6761\u7075\u86c7\u822c\uff0c\u5411\u5c55\u767d\u5de6\u53f3\u8033\u95e8\u62ff\u6765\uff01 \u5c55\u767d\u201c\u72ee\u5b50\u5927\u6447\u5934\u201d\uff0c\u6446\u5934\u8e6c\u817f\uff0c\u5728\u534a\u7a7a\u4e2d\u5212\u4e86\u4e2a\u5de7\u5999\u7684\u5706\u5f27\uff0c\u5148\u5347\u9ad8\uff0c\u540e\u964d\u4f4e\uff0c\u5c31\u7115 \u7136\u75be\u843d\u4e4b\u52bf\uff0c\u98de\u8d77\u4e00\u817f\uff0c\u76f4\u8e22\u201c\u6c99\u6f20\u4e4b\u72d0\u201d\u540e\u8170\uff01 \u201c\u6c99\u6f20\u4e4b\u72d0\u201d\u7ffb\u8eab\u5c48\u8098\uff0c\u4ee5\u624b\u4e2d\u5927\u4e32\u5ff5\u73e0\uff0c\u731b\u6572\u5c55\u767d\u817f\u5f2f\u201c\u73af\u8df3\u201d\uff01 \u5c55\u767d\u9707\u81c2\u6536\u817f\uff0c\u5c31\u8fd9\u5019\u5ffd\u4e4b\u95f4\uff0c\u4ed6\u5df2\u5411\u201c\u9ed1\u9053\u56db\u51f6\u201d\u5404\u653b\u51fa\u4e86\u4e00\u62db\u3002 \u8fd9\u56db\u62db\u4e00\u6c14\u5475\u6210\uff0c\u8fde\u653b\u56db\u5927\u9ad8\u624b\uff0c\u800c\u4e14\u662f\u62a2\u5c3d\u4e86\u5148\u673a\uff01 \u62db\u5f0f\u5e72\u51c0\u5229\u843d\uff0c\u65e0\u8bba\u624b\u3001\u773c\u3001\u8eab\u3001\u6cd5\u3001\u6b65\u5904\u5904\u6070\u5230\u597d\u5904\uff0c\u62db\u62db\u653b\u654c\u8981\u5bb3\uff01 \u201c\u9ed1\u9053\u56db\u51f6\u201d\u56db\u4e2a\u72c2\u50b2\u4e0d\u53ef\u4e00\u4e16\u7684\u9b54\u5934\uff0c\u4e09\u5341\u4e2d\u524d\u5373\u540d\u6ee1\u6b66\u6797\uff0c\u53c8\u6f5c\u8eab\u6d77\u5916\u82e6\u7ec3\u4e09\u5341\u5bd2 \u6691\uff0c\u4e8c\u6b21\u4e0b\u4e2d\u539f\uff0c\u672c\u60f3\u4e00\u9e23\u60ca\u4eba\uff0c\u8c01\u77e5\u7b2c\u4e00\u6b21\u5728\u4e2d\u539f\u9732\u9762\uff0c\u4fbf\u88ab\u5c55\u767d\u4ee5\u4e00\u654c\u56db\uff0c\u6253\u4e86\u4e2a\u624b\u5fd9 \u811a\u4e71\uff01 \u56db\u51f6\u540c\u65f6\u66b4\u6012\uff0c\u5404\u81ea\u6012\u5578\u5389\u543c\u7740\uff0c\u731b\u5411\u5c55\u767d\u653b\u6765\uff01 \u5c55\u767d\u6b64\u65f6\u8c6a\u6c14\u51b2\u6ee1\u80f8\u81c6\uff0c\u4ed6\u8fde\u653b\u56db\u51f6\u5c31\u662f\u4e3a\u4e86\u5f15\u8bf1\u56db\u51f6\u4e00\u9f50\u51fa\u624b\uff0c\u4eca\u89c1\u56db\u51f6\u540c\u65f6\u653b\u6765\uff0c \u4ed6\u7acb\u5c55\u201c\u5929\u4f5b\u7edd\u5b66\u201d\u4e0e\u56db\u51f6\u6218\u5728\u4e00\u8d77\uff01 \u5c55\u767d\u529b\u6218\u201c\u9ed1\u9053\u56db\u51f6\u201d\u56db\u4e2a\u7edd\u9876\u9ad8\u624b\uff0c\u5feb\u653b\uff0c\u4fa0\u6253\uff0c\u6643\u773c\u4e4b\u95f4\uff0c\u5df1\u7136\u4e92\u6218\u4e86\u4e09\u5341\u4f59\u4e2a\u56de \u5408\uff01 \u4e94\u4eba\u6108\u6253\u6108\u5feb\uff0c\u6b64\u65f6\u5df2\u4e0d\u80fd\u770b\u6e05\u4e94\u4e2a\u4eba\u7684\u8eab\u5f71\uff0c\u53ea\u80fd\u770b\u5230\u4e94\u56e2\u7f61\u98ce\uff0c\u5728\u4e00\u5757\u76d8\u7ed3\u7ea0\u7f20\uff0c \u70c8\u70c8\u5636\u543c\uff01 \u53ea\u80fd\u542c\u5230\u4e94\u4e2a\u4eba\u51fa\u638c\u5410\u6c14\u5f00\u58f0\u4e4b\u58f0\uff0c\u201c\u54fc\uff01\u563f\uff01\u201d\u4e0d\u7edd\uff0c\u638c\u529b\u51fb\u5b9e\uff0c\u201c\u7830\u7830\uff01\u78b0\u78b0\uff01\u201d \u66f4\u662f\u4e0d\u7edd\u4e8e\u8033\uff01 \u52b2\u6d41\u6fc0\u6e4d\uff0c\u4f59\u529b\u56db\u5c04\uff0c\u6570\u6587\u65b9\u5706\u4ee5\u5185\uff0c\u5df2\u7136\u4f7f\u4eba\u65e0\u6cd5\u7acb\u8db3\uff01 \u56db\u5468\u56f4\u89c2\u7684\u7fa4\u96c4\uff0c\u7eb7\u7eb7\u79fb\u52a8\u811a\u6b65\u540e\u9000\uff0c\u6e10\u6e10\u90fd\u9000\u81f3\u5899\u8fb9\u3001\u5c4b\u69b4\u4e0b\u53bb\u4e86\uff0c\u4e00\u4e2a\u4e2a\u77aa\u5927\u4e86\u773c \u775b\uff0c\u60ca\u89c6\u7740\u8fd9\u4e00\u573a\u60ca\u5929\u52a8\u5730\u5014\u5730\u6643\u773c\u4e4b\u95f4\uff0c\u53c8\u662f\u4e03\u5341\u4f59\u56de\u5408\u8fc7\u53bb\u4e86\uff0c\u53cc\u65b9\u5df2\u6fc0\u6218\u4e86\u5c06\u8fd1\u767e\u56de \u5408\uff01 \u5173\u5fc3\u5c55\u767d\u5b89\u5371\u7684\u4eba\u4e0d\u7528\u8bf4\uff0c\u4e00\u9897\u5fc3\u90fd\u540a\u5728\u55d3\u5b50\u773c\u4e0a\u6765\u4e86\uff0c\u751f\u6015\u5c55\u767d\u843d\u8d25\uff01 \u201c\u562d\uff01\u562d\uff01\u201d\u6570\u58f0\u6c14\u7206\u5de8\u54cd\uff0c\u72b9\u5982\u4e00\u4e32\u95f7\u96f7\u6253\u5728\u4f17\u4eba\u773c\u524d\uff01 \u79d2\u77f3\u56db\u6e85\uff0c\u5c18\u7070\u98de\u626c\u4e4b\u4e2d\uff0c\u53ea\u89c1\u7f20\u6218\u5728\u4e00\u56e2\u7684\u4eba\u5f71\uff0c\u7115\u7136\u5206\u6563\u5f00\u6765\uff01 \u201c\u9ed1\u9053\u56db\u51f6\u201d\u5206\u7ad9\u4e1c\u3001\u5357\u3001\u897f\u3001\u5317\u56db\u65b9\u3002 \u5c55\u767d\u7a33\u7ad9\u5f53\u4e2d\uff0c\u9759\u5982\u5c71\u5cb3\uff01 \u56db\u4e2a\u9ed1\u9053\u9876\u5c16\u9ad8\u624b\uff0c\u867d\u662f\u4e00\u80ce\u51f6\u6076\u4e4b\u6001\uff0c\u4f46\u60c5\u5f62\u975e\u5e38\u72fc\u72c8\uff0c\u4e00\u4e2a\u4e2a\u6c57\u900f\u8863\u895f\uff0c\u6c14\u606f\u7c97 \u91cd\uff0c\u663e\u89c1\u5f97\u65b9\u624d\u4e0e\u5c55\u81ea\u5168\u529b\u640f\u6597\u4e86\u5c06\u8fd1\u4e8c\u767e\u4e2a\u56de\u5408\uff0c\u5185\u529f\u771f\u529b\u5df2\u6709\u635f\u8017\u8fc7\u5de8\u7684\u73b0\u8c61\uff01 \u56db\u5468\u89c2\u6218\u7684\u7fa4\u96c4\uff0c\u5c4f\u606f\u4ee5\u89c2\uff0c\u773c\u524d\u867d\u662f\u9759\u6b62\u4e86\u4e0b\u6765\uff0c\u4f46\u6bcf\u4e00\u4e2a\u4eba\u5747\u77e5\u9053\uff0c\u8ddf\u7740\u6765\u7684\u5c06\u662f \u51f6\u6076\u7684\u640f\u6597\uff01 \u53cc\u65b9\u90fd\u5728\u517b\u7cbe\u84c4\u9510\uff0c\u8fd0\u96c6\u6240\u6709\u529f\u529b\uff0c\u5e76\u7d22\u89e3\u5bf9\u65b9\u7834\u7efd\uff0c\u51c6\u5907\u7ee7\u8d77\u53d1\u96be\uff0c\u4f5c\u81f4\u547d\u7684\u4e00\u51fb\uff01 \u51b7\u6708\u897f\u6c89\uff0c\u94f6\u6cb3\u661f\u7a00\uff0c\u773c\u770b\u591c\u8272\u5c06\u8981\u6b8b\u53bb\u3002\u4f46\u7d27\u5f20\u7684\u573a\u5408\uff0c\u7ee3\u9488\u843d\u5730\u53ef\u95fb\uff0c\u6ca1\u6709\u8c01\u4f1a\u6ce8 \u610f\u5230\u5929\u8272\u5c06\u4eae\uff01\u4e00\u201c\u8471\u5cad\u4e4b\u9e70\u201d\u9996\u5148\u53d1\u96be\uff0c\u66b4\u559d\u58f0\u4e2d\uff0c\u4e24\u53ea\u767d\u9aa8\u78f7\u78f7\u7684\u201c\u67af\u9aa8\u638c\u201d\uff0c\u7ea2\u5149\u66b4 \u6d8c\uff0c\u638c\u98ce\u72c2\u5578\uff0c\u9f50\u5411\u5c55\u767d\u5f53\u5934\u780d\u4e0b\uff01 \u201c\u592a\u4ed3\u4e4b\u9f20\u201d\u5982\u54cd\u65af\u5e94\uff0c\u4e24\u6761\u7279\u957f\u7684\u624b\u81c2\uff0c\u72b9\u5982\u4e24\u6761\u602a\u87d2\uff0c\u5411\u5e95\u4e0b\u4e00\u6284\uff0c\u5411\u5c55\u767d\u53cc\u8db3\u8e1d \u90e8\u6293\u53bb\uff01 \u201c\u9634\u5c71\u4e4b\u72fc\u201d\u7acb\u638c\u5982\u5200\uff0c\u4ee5\u201c\u638c\u5203\u5207\u6728\u201d\u529f\u592b\uff0c\u4e00\u638c\u5e73\u63d2\u5c55\u767d\u5de6\u81c2\uff0c\u4e00\u638c\u6a2a\u524a\u5c55\u81ea\u80f8\u8179 \u8981\u5bb3\uff01 \u201c\u6c99\u6f20\u4e4b\u72d0\u201d\u624b\u4e2d\u5927\u4e32\u5ff5\u73e0\uff0c\u5de7\u5999\u5730\u633d\u4e86\u4e00\u4e2a\u73e0\u82b1\uff0c\u731b\u7838\u5c55\u767d\u540e\u5fc3\u201c\u98ce\u5c3e\u201d\u3001\u201c\u795e \u5802\u201d\u3001\u201c\u5de8\u9aa8\u201d\u4e09\u5927\u91cd\u7a74\uff01 \u56db\u4e2a\u9ed1\u9053\u9876\u5c16\u9ad8\u624b\uff0c\u540c\u65f6\u51fa\u624b\uff0c\u84c4\u52bf\u800c\u53d1\uff0c\u4e09\u80a1\u5de8\u5927\u638c\u98ce\u5408\u6d41\uff0c\u543c\u543c\u5c71\u54cd\uff0c\u4e00\u4e32\u5ff5\u73e0\u5982 \u6f5c\u5728\u6697\u5904\u7684\u6bd2\u86c7\uff0c\u6beb\u65e0\u58f0\u606f\u5730\u5077\u88ad\u654c\u65b9\u4e0a\u3001\u4e2d\u3001\u4e0b\u4e09\u8def\uff0c\u524d\u3001\u540e\u3001\u5de6\u3001\u53f3\u56db\u65b9\uff0c\u5c55\u767d\u5df2\u662f\u8179 \u80cc\u53d7\u654c\uff0c\u800c\u4e14\u56db\u5927\u9ad8\u624b\u79ef\u5ffd\u800c\u53d1\uff0c\u5c55\u81ea\u60ca\u9669\u4e07\u72b6\u3002 \u56db\u5468\u56f4\u89c2\u4e4b\u4eba\uff0c\u4e0d\u5c11\u4eba\u5931\u58f0\u60ca\u547c\uff01 \u7a81\u89c1\u5c55\u81ea\u6e05\u5578\u4e00\u58f0\uff0c\u201c\u5929\u4f5b\u964d\u9b54\u638c\u201d\u62db\u6f14\u201c\u4f5b\u5149\u666e\u7167\u201d\uff0c\u53ea\u89c1\u638c\u5f71\u5982\u5c71\uff0c\u5206\u88ad\u524d\u540e\u5de6\u53f3 \u56db\u65b9\u4e4b\u654c\uff0c\u8eab\u6cd5\u4e4b\u5feb\u3001\u62db\u5f0f\u4e4b\u5999\u4ee5\u53ca\u5a01\u529b\u4e4b\u5f3a\uff0c\u5747\u662f\u4f17\u4eba\u524d\u6240\u672a\u89c1\uff01 \u5c31\u8fd9\u4e00\u62db\uff0c\u7acb\u628a\u201c\u9ed1\u9053\u56db\u51f6\u201d\u53c8\u540c\u65f6\u903c\u9000\uff01 \u201c\u9ed1\u9053\u56db\u51f6\u201d\u53c8\u5f00\u59cb\u56f4\u5728\u5c55\u767d\u56db\u5468\uff0c\u6076\u72e0\u72e0\u5730\u77aa\u7740\uff0c\u4e00\u8fb9\u8c03\u606f\u5168\u529b\u4e00\u62db\u6240\u635f\u8017\u8fc7\u5de8\u7684\u771f \u529b\uff0c\u4e00\u8fb9\u82e6\u601d\u5c55\u81ea\u7684\u62db\u5f0f\u7834\u7efd\uff0c\u84c4\u52bf\u800c\u6251\uff01 \u8bf4\u5b9e\u5728\u7684\uff0c\u5c55\u767d\u72ec\u6218\u201c\u9ed1\u9053\u56db\u51f6\u201d\uff0c\u6bd4\u72ec\u6218\u201c\u6d77\u5916\u4e09\u715e\u201d\u5e76\u4e0d\u7701\u52b2\uff0c\u201c\u6d77\u5916\u4e09\u715e\u201d\u4e4b\u4e2d \u9664\u4e86\u957f\u9aef\u8001\u4eba\u6b66\u529f\u7279\u9ad8\u4e4b\u5916\uff0c\u767d\u53d1\u5a46\u5a46\u4e0e\u201c\u4f5b\u5370\u6cd5\u5e08\u201d\u529f\u529b\u90fd\u7565\u900a\u4e00\u7b79\uff0c\u8fd9\u201c\u9ed1\u9053\u56db\u51f6\u201d\u529f \u529b\u90fd\u5dee\u4e0d\u591a\uff0c\u867d\u4e0d\u5982\u957f\u9aef\u8001\u4eba\u9ad8\uff0c\u5374\u5728\u767d\u53d1\u5a46\u5a46\u4e0e\u201c\u4f5b\u5370\u6cd5\u5e08\u201d\u4e4b\u4e0a\uff0c\u800c\u4e14\u56db\u4e2a\u4eba\u5404\u6709\u72ec\u7279 \u6740\u624b\uff0c\u56e2\u5408\u5f97\u5929\u8863\u65e0\u7f1d\uff0c\u56db\u4eba\u5408\u8d77\u624b\u6765\uff0c\u6bcf\u4e00\u62db\u653b\u51fa\u90fd\u662f\u4e0a\u3001\u4e2d\u3001\u4e0b\u4e09\u8def\uff0c\u5de6\u3001\u53f3\u3001\u524d\u3001\u540e \u56db\u65b9\uff0c\u5e7f\u7f69\u5468\u8eab\u8981\u5bb3\uff01 \u5176\u4e2d\u5c24\u4ee5\u201c\u8471\u5cad\u4e4b\u9e70\u201d\u4e0e\u201c\u592a\u4ed3\u4e4b\u9f20\u201d\u6700\u96be\u5e94\u4ed8\uff0c\u201c\u8471\u5cad\u4e4b\u9e70\u201d\u7684\u201c\u67af\u9aa8\u638c\u201d\uff0c\u6bcf\u4e00\u638c \u62cd\u6765\uff0c\u90fd\u5982\u4e00\u56e2\u70c8\u706b\u70e7\u5728\u8eab\u4e0a\u4e00\u822c\uff0c\u5c55\u767d\u867d\u4e0d\u60e7\u6015\uff0c\u4f46\u968f\u65f6\u8981\u8fd0\u8d77\u5468\u8eab\u529f\u529b\uff0c\u8feb\u51fa\u5f3a\u4fb5\u5165\u4f53 \u7684\u70ed\u6bd2\uff1a\u201c\u592a\u4ed3\u4e4b\u9f20\u201d\u7684\u4e24\u6761\u7279\u957f\u7684\u624b\u81c2\uff0c\u5374\u975e\u5e38\u8ba8\u538c\uff0c\u4e0d\u4f46\u62db\u5f0f\u8be1\u8fa3\uff0c\u4e14\u80fd\u62db\u51fa\u534a\u9014\uff0c\u7a81 \u7136\u8f6c\u5f2f\uff0c\u4e13\u5411\u4e0b\u4e09\u8def\uff0c\u4f7f\u5c55\u767d\u4e0d\u5f97\u4e0d\u65f6\u65f6\u63d0\u9632\u4ed6\u7684\u8be1\u5f02\u6740\u624b\uff01 \u201c\u6c99\u6f20\u4e4b\u72d0\u201d\u4e0e\u201c\u9634\u5c71\u4e4b\u72fc\u201d\u4e5f\u4e0d\u7b80\u5355\uff0c\u201c\u9634\u5c71\u4e4b\u72fc\u201d\u7684\u201c\u638c\u5203\u5207\u6728\u201d\u529f\u592b\uff0c\u706b\u5019\u5230 \u5bb6\uff0c\u6bcf\u4e00\u638c\u780d\u51fa\uff0c\u90fd\u6380\u8d77\u72c2\u5578\u7834\u98ce\u4e4b\u58f0\uff0c\u5f53\u771f\u662f\u638c\u7f18\u4f3c\u5200\uff0c\u5c55\u81ea\u6bcf\u6321\u5176\u4e00\u638c\uff0c\u867d\u80fd\u628a\u4ed6\u9707 \u9000\uff0c\u4f46\u638c\u7f18\u89e6\u53ca\u4e4b\u5904\uff0c\u83ab\u4e0d\u9690\u9690\u53d1\u75c5\u3002\u81f3\u4e8e\u201c\u6c99\u6f20\u4e4b\u72d0\u201d\u90a3\u4e00\u4e32\u5927\u6f20\u7279\u4ea7\u7684\u80e1\u6843\u6728\u5ff5\u73e0\uff0c\u66f4 \u80fd\u4e13\u7834\u5185\u5bb6\u771f\u529b\uff0c\u638c\u98ce\u5c01\u6321\u4e0d\u4f4f\uff0c\u5c55\u767d\u5b8c\u5168\u4ed7\u7740\u201c\u5343\u5e7b\u98d8\u9999\u6b65\u201d\u4e0e\u201c\u65e0\u8272\u65e0\u76f8\u8eab\u201d\u7684\u9ad8\u7edd\u8eab \u6cd5\uff0c\u95ea\u8eb2\u56de\u907f\u3002\u56e0\u6b64\uff0c\u5c55\u767d\u8fd9\u4e00\u6218\uff0c\u53ef\u8bf4\u662f\u5e73\u751f\u4ec5\u9047\u7684\u7b2c\u4e00\u6b21\u786c\u4ed7\uff01 \u5c55\u767d\u6218\u6765\u5403\u529b\u4e07\u5206\uff0c\u5176\u5b9e\u201c\u9ed1\u9053\u56db\u51f6\u201d\u56db\u4e2a\u9ed1\u9053\u9876\u5c16\u9ad8\u624b\uff0c\u5fc3\u4e2d\u7684\u5473\u9053\u66f4\u4e0d\u597d\u53d7\u3002 \u56db\u5468\u89c2\u6218\u7fa4\u96c4\uff0c\u867d\u7136\u4e0d\u4e4f\u8001\u6c5f\u6e56\uff0c\u5927\u573a\u9762\u89c1\u591a\u4e86\uff0c\u4f46\u773c\u524d\u8fd9\u4e00\u6218\uff0c\u7684\u786e\u662f\u751f\u5e73\u4ec5\u89c1\uff0c\u4e0d \u4f46\u53cc\u65b9\u7684\u6b66\u529f\u8def\u6570\uff0c\u5747\u662f\u89c1\u6240\u672a\u89c1\uff0c\u800c\u90a3\u5185\u529f\u771f\u529b\u66f4\u662f\u5927\u5f97\u60ca\u4eba\uff0c\u6bcf\u62db\u65bd\u51fa\uff0c\u90fd\u662f\u60ca\u5929\u52a8 \u5730\uff0c\u6bcf\u4e00\u638c\u5288\u51fa\u5747\u53ef\u88c2\u77f3\u5f00\u5c71\uff01 \u6b64\u65f6\uff0c\u201c\u9ed1\u9053\u56db\u51f6\u201d\u4e0d\u518d\u77a0\u76ee\u6012\u89c6\uff0c\u84c4\u52bf\u800c\u6251\uff0c\u800c\u662f\u7531\u201c\u9634\u5c71\u4e4b\u72fc\u201d\u77fe\u54e9\u5495\u778e\u8bf4\u4e86\u51e0\u65ec \u8499\u53e4\u8bdd\u4e4b\u540e\uff0c\u56db\u51f6\u7acb\u523b\u6539\u53d8\u6218\u7565\uff0c\u98de\u5feb\u5730\u56f4\u7740\u5c55\u767d\u7ed5\u8d77\u5708\u5b50\u6765\uff01 \u5c55\u767d\u4e0d\u61c2\u8499\u8bed\uff0c\u4f46\u770b\u56db\u51f6\u60c5\u5f62\u5df2\u7ecf\u660e\u767d\uff0c\u5fc5\u662f\u53e6\u6709\u8be1\u8ba1\uff0c\u5f53\u5373\u62b1\u5143\u5b88\u4e00\uff0c\u84c4\u6c14\u51dd\u795e\uff0c\u5168 \u795e\u6212\u5907\u8d77\u6765\uff01 \u56db\u51f6\u6108\u8f6c\u6108\u5feb\uff0c\u7a81\u7136\u201c\u9634\u5c71\u4e4b\u72fc\u201d\u4e00\u58f0\u60e8\u5389\u957f\u5578\uff0c\u5982\u5200\u7684\u638c\u950b\uff0c\u4e00\u7247\u5200\u5c71\u4f3c\u5730\u5411\u5c55\u767d\u601d \u8eab\u8981\u5bb3\u731b\u5288\u72c2\u780d\uff01 \u770b\u5176\u6765\u52bf\u4e4b\u51f6\u731b\uff0c\u5fc5\u7cfb\u5168\u8eab\u529f\u529b\u6240\u805a\uff0c\u5c55\u767d\u4e0d\u6562\u4e1d\u6beb\u5927\u610f\uff0c\u4ee5\u8fc7\u4eba\u7684\u76ee\u529b\uff0c\u89d1\u51c6\u5176\u638c\u529b \u7a7a\u9699\uff0c\u8fd0\u8d77\u4e5d\u6210\u529f\u529b\uff0c\u72c2\u63a8\u800c\u51fa\uff01 \u52b2\u98ce\u5c71\u6d8c\uff0c\u201c\u4f46\u201c\u9634\u5c71\u4e4b\u72fc\u201d\u6765\u52bf\u867d\u731b\uff0c\u9000\u52bf\u66f4\u5feb\uff0c\u4ed6\u4e0d\u518d\u4e0e\u5c55\u767d\u786c\u62fc\u5185\u529b\uff0c\u5c55\u767d\u62db\u5f0f \u672a\u5230\uff0c\u4ed6\u5df2\u64a4\u62db\u7eb5\u8eab\u659c\u98d8\u516b\u5c3a\u5f00\u5916\uff01 \u5c55\u767d\u5fae\u611f\u610f\u5916\uff0c\u4e00\u638c\u5288\u7a7a\uff0c\u4f46\u8eab\u540e\u75be\u98ce\u72c2\u5578\uff0c\u663e\u7136\u80cc\u540e\u6709\u4eba\u6025\u88ad\uff01 \u5c55\u767d\u201c\u56de\u5934\u671b\u6708\u201d\uff0c\u8de8\u6b65\u7529\u8138\uff0c\u501f\u56de\u8eab\u4e4b\u52bf\uff0c\u53c8\u8fd0\u96c6\u4e86\u4e5d\u6210\u529f\u529b\uff0c\u5411\u8eab\u540e\u731b\u5288\u51fa\u4e00\u638c\uff01 \u5411\u5c55\u767d\u80cc\u540e\u5077\u88ad\u7684\u662f\u201c\u592a\u4ed3\u4e4b\u9f20\u201d\uff0c\u4e00\u722a\u6293\u7a7a\uff0c\u5c55\u767d\u638c\u529b\u5df2\u5230\uff0c\u4ed6\u4e5f\u548c\u201c\u9634\u5c71\u4e4b\u72fc\u201d\u4e00 \u6837\uff0c\u4e0d\u518d\u4e0e\u5c55\u767d\u786c\u62fc\u5185\u529b\uff0c\u5c31\u5728\u5c55\u767d\u638c\u950b\u672b\u5230\u90e8\u4f4d\u65f6\uff0c\u4f7c\u7136\u6536\u8eab\u540e\u9000\uff0c\u534a\u7a7a\u4e2d\u53c8\u6298\u4e86\u56de\u53bb\uff01 \u7d27\u8ddf\u7740\u201c\u8471\u5cad\u4e4b\u9e70\u201d\u7684\u201c\u67af\u9aa8\u638c\u201d\u2019\u4e00\u9635\u7078\u70ed\u72c2\u9556\uff0c\u5019\u53c8\u6d8c\u5230\uff01 \u5c55\u767d\u8eab\u5f62\u7535\u8f6c\uff0c\u8eb2\u8fc7\u7078\u70ed\u638c\u98ce\u3002\u540c\u65f6\u4e5f\u5411\u201c\u8471\u5cad\u4e4b\u9e70\u201d\u653b\u51fa\u4e00\u62db\u72e0\u7684\uff01 \u201c\u8471\u5cad\u4e4b\u9e70\u201d\u540c\u6837\u4e0d\u4e0e\u5c55\u767d\u62db\u5f0f\u51fb\u5b9e\uff0c\u4e00\u6cbe\u5373\u9000\uff01 \u63a5\u7740\u201c\u6c99\u6f20\u4e4b\u72d0\u201d\u53c8\u4ece\u4fa7\u80cc\u653b\u5230\uff01\u2026\u2026 \u56db\u51f6\u6b64\u9000\u5f7c\u8fdb\uff0c\u5747\u662f\u4e00\u6cbe\u5373\u9000\uff0c\u5c55\u767d\u867d\u7136\u5fe0\u539a\u6728\u6cb9\uff0c\u5fc3\u5730\u8001\u5b9e\uff0c\u4f46\u6570\u62db\u4e00\u8fc7\uff0c\u7acb\u523b\u660e\u767d \u201c\u9ed1\u9053\u56db\u51f6\u201d\u662f\u60f3\u501f\u6b64\u6d88\u8017\u81ea\u5df1\u7684\u5185\u529b\uff0c\u7b49\u5230\u81ea\u5df1\u75b2\u4e4f\u4e0d\u652f\u65f6\uff0c\u56db\u4eba\u518d\u9f50\u65bd\u6740\u624b\uff01 \u5c55\u767d\u4e0d\u7531\u5fae\u5fae\u4e00\u7b11\uff0c\u5fc3\u8bf4\uff1a\u201c\u4f60\u4eec\u8fd9\u6837\u6b63\u662f\u7ed9\u4e86\u6211\u8c03\u606f\u7684\u673a\u4f1a\u2026\u2026\u201d \u8c01\u77e5\u5c55\u767d\u5ff5\u5934\u8fd8\u672a\u8f6c\u5b8c\uff0c\u201c\u6c99\u6f20\u4e4b\u72d0\u201d\u5019\u7136\u9038\u51fa\u5708\u5916\uff0c\u201c\u4e34\u53bb\u79cb\u88ab\u201d\uff0c\u56de\u624b\u5411\u5c55\u767d\u6253\u51fa \u4e09\u9897\u5ff5\u73e0\uff01 \u540c\u65f6\uff0c\u4e09\u51f6\u62fc\u51fa\u5168\u529b\uff0c\u5de6\u3001\u53f3\u3001\u540e\u4e09\u65b9\uff0c\u4e00\u9f50\u5411\u5c55\u767d\u653b\u5230\uff01 \u660e\u773c\u4eba\u4e00\u770b\u5373\u77e5\u201c\u9ed1\u9053\u56db\u51f6\u201d\u7528\u610f\u6076\u6bd2\uff0c\u662f\u60f3\u4ee5\u786c\u62fc\u5c55\u767d\uff0c\u5c01\u6321\u4f4f\u5c55\u767d\u7684\u817e\u95ea\u632a\u79fb\u7684\u53bb \u8def\uff0c\u7136\u540e\u4ee5\u201c\u6c99\u6f20\u4e4b\u72d0\u201d\u4e13\u7834\u5185\u5bb6\u771f\u529b\u7684\u5ff5\u73e0\u6765\u4f24\u5c55\u767d\uff01 \u5c55\u767d\u5fc3\u4e2d\u4e00\u61d4\uff0c\u98de\u5feb\u5730\u5288\u51fa\u4e09\u638c\uff0c\u9707\u9000\u4e09\u9762\u5f3a\u654c\uff0c\u4f46\u4e09\u9762\u538b\u6765\u7684\u53cd\u9707\u4e4b\u529b\uff0c\u5374\u4f7f\u5c55\u767d\u8eab \u5f62\u518d\u65e0\u6cd5\u79fb\u52a8\u4e00\u6b65\uff0c\u800c\u2018\u7a7f\u7834\u5185\u5bb6\u771f\u529b\u7684\u4e09\u9897\u5ff5\u73e0\uff0c\u5df2\u63a5\u7740\u7834\u7a7a\u5c16\u5578\u5982\u98de\u800c\u81f3\uff01 \u4e09\u9897\u5ff5\u73e0\uff0c\u6210\u54c1\u5b57\u5f62\uff0c\u4e00\u9896\u76f4\u5954\u5c55\u767d\u9762\u95e8\uff0c\u4e24\u9896\u5e73\u5c04\u5c55\u767d\u524d\u80f8\uff01 \u201c\u6c99\u6f20\u4e4b\u72d0\u201d\u8fd9\u79cd\u4e13\u653b\u5185\u5bb6\u771f\u529b\u7684\u5ff5\u73e0\uff0c\u4e43\u6f20\u5317\u82e6\u5bd2\u4e4b\u5730\u7279\u4ea7\u7684\u80e1\u6843\u6728\u5236\u6210\uff0c\u575a\u903e\u7cbe \u94a2\uff0c\u91cd\u903e\u91d1\u77f3\uff0c\u5916\u5706\u4e2d\u7a7a\uff0c\u4e2d\u7a7a\u7684\u87ba\u65cb\u5f62\u5706\u5b54\uff0c\u53ef\u4ee5\u9047\u5230\u963b\u529b\u52a0\u901f\u65cb\u8f6c\u800c\u8fdb\uff0c\u662f\u4ee5\u638c\u98ce\u5185\u529b \u65e0\u6cd5\u963b\u6321\uff0c\u53ef\u4ee5\u8bf4\u5389\u5bb3\u65e0\u6bd4\uff01 \u5c31\u662f\u518d\u5f3a\u7684\u5185\u529f\u9ad8\u624b\uff0c\u9047\u5230\u8fd9\u79cd\u9738\u9053\u6697\u5668\uff0c\u9664\u4e86\u8eb2\u95ea\u4e4b\u5916\uff0c\u4e5f\u662f\u675f\u624b\u65e0\u7b56\uff01 \u5c55\u767d\u5185\u529b\u6df1\u539a\uff0c\u867d\u80fd\u628a\u4e09\u5927\u9ed1\u9053\u5546\u624b\u540c\u65f6\u9707\u9000\uff0c\u8eab\u5f62\u53d7\u90a3\u4e09\u65b9\u9762\u7684\u53cd\u9707\u4e4b\u529b\u5df2\u65e0\u6cd5\u8eb2 \u95ea\uff0c\u53c8\u77e5\u90a3\u7535\u5c04\u800c\u81f3\u7684\u5ff5\u73e0\u638c\u98ce\u5185\u529b\u5747\u65e0\u6cd5\u963b\u6321\uff0c\u5fc3\u4e2d\u4e00\u60ca\u2026\u2026\u89c2\u6218\u7fa4\u96c4\u7eb7\u7eb7\u60ca\u547c\u2026\u2026 \u5c55\u767d\u9047\u9669\uff0c\u6155\u5bb9\u7ea2\u3001\u91d1\u5f69\u98ce\u3001\u5a49\u513f\u3001\u6a0a\u7d20\u5bc6\uff0c\u66f4\u662f\u9a87\u5f97\u5931\u53eb\u8d77\u6765\uff01 \u53ea\u542c\u201c\u5636\u201d\u7684\u4e00\u58f0\u5fae\u54cd\uff0c\u8840\u5149\u5d29\u73b0\uff01 \u5c55\u767d\u867d\u4ee5\u201c\u65e0\u8272\u65e0\u76f8\u201d\u8eab\u6cd5\uff0c\u53ca\u65f6\u7529\u5934\u4fa7\u8eab\uff0c\u8fde\u8eb2\u8fc7\u4e24\u9897\u5ff5\u73e0\uff0c\u4f46\u7b2c\u4e09\u9896\u5ff5\u73e0\u5374\u5728\u4ed6\u7684 \u5de6\u81c2\u4e0a\u5212\u4e86\u4e00\u6761\u8840\u69fd\uff01\u5c55\u767d\u611f\u5230\u5de6\u81c2\u4e00\u51c9\uff0c\u5ff5\u73e0\u7a7f\u81c2\u800c\u8fc7\uff01\u8863\u7834\u8089\u98de\uff0c\u9c9c\u8840\u76f4\u6d41\u4e0b\u6765\uff01 \u5c31\u5728\u5c55\u767d\u53d7\u4f24\u3001\u5fae\u5fae\u4e00\u6115\u7684\u77ac\u95f4\uff0c\u201c\u9634\u5c71\u4e4b\u72fc\u201d\u6084\u65e0\u58f0\u606f\u5730\u5728\u5c55\u767d\u540e\u80cc\u6253\u51fa\u4e00\u638c\uff01 \u4ed6\u201c\u638c\u5203\u5207\u6728\u201d\u7684\u529f\u592b\uff0c\u638c\u5203\u5982\u5200\uff0c\u201c\u5eb7\u201d\u7684\u4e00\u58f0\uff0c\u51fb\u5b9e\u5728\u5c55\u767d\u80cc\u4e0a\uff0c\u628a\u5c55\u81ea\u6253\u5f97\u5411\u524d \u8ddf\u968f\u4e86\u4e94\u516d\u5927\u6b65\uff01 \u5c55\u767d\u53ea\u611f\u773c\u524d\u53d1\u9ed1\uff0c\u5fc3\u80f8\u6c14\u7ffb\u8840\u6d8c\uff0c\u5411\u524d\u62a2\u4e86\u4e94\u516d\u5927\u6b65\uff0c\u8eab\u5f62\u6447\u6447\u6b32\u5012\u2026\u2026 \u201c\u9634\u5c71\u4e4b\u72fc\u201d\u4e00\u9635\u5f97\u610f\u7684\u72c2\u7b11\uff0c\u4f3c\u5200\u7684\u638c\u9563\u7b2c\u4e8c\u6b21\u53c8\u4e3e\u8d77\u2026\u2026 \u5176\u4ed6\u4e09\u51f6\u4e5f\u5404\u81ea\u72de\u7b11\u7740\u903c\u8fd1\u5c55\u767d\uff01\u773c\u770b\u5c55\u767d\u5c31\u8981\u547d\u4e27\u56db\u51f6\u4e4b\u624b\uff01 \u6447\u6447\u6b32\u5012\u7684\u5c55\u767d\uff0c\u7a81\u7136\u55d4\u76ee\u5927\u8d50\u4e00\u58f0\uff0c\u4e00\u638c\u5411\u201c\u6c99\u6f20\u4e4b\u72d0\u201d\u5288\u4e91\uff01 \u638c\u98ce\u72c2\u5578\uff0c\u4e00\u822c\u6c79\u6d8c\u7684\u5de8\u6d41\uff0c\u5982\u5c71\u6d8c\u51fa\uff01 \u201c\u6c99\u6f20\u4e4b\u72d0\u201d\u4e00\u73e0\u594f\u529f\uff0c\u5fc3\u4e2d\u4e00\u559c\uff0c\u4f30\u4e0d\u5230\u5c55\u767d\u53d7\u4f24\u4e4b\u540e\uff0c\u4ecd\u80fd\u53d1\u51fa\u5a01\u529b\u8fd9\u4e48\u5927\u7684\u638c \u529b\uff01 \u5fc3\u795e\u7a0d\u4e3a\u4e00\u61c8\uff0c\u5982\u6d77\u6d0b\u5de8\u6d41\u7684\u638c\u529b\u5df2\u51fb\u5411\u80f8\u524d\uff0c\u518d\u60f3\u8eb2\u95ea\uff0c\u7109\u5f97\u80fd\u591f\uff1f \u201c\u6c99\u6f20\u4e4b\u72d0\u201d\u53ea\u611f\u80f8\u524d\u5982\u53d7\u4e07\u65a4\u91cd\u9524\uff0c\u95f7\u54fc\u4e00\u58f0\uff0c\u4ef0\u8eab\u7ffb\u4f8b\uff0c\u5f20\u53e3\u55b7\u51fa\u4e00\u53e3\u9c9c\u8840\uff01 \u201c\u9634\u5c71\u4e4b\u72fc\u201d\u7684\u4e00\u638c\u8fd8\u672a\u6253\u5230\uff0c\u5c55\u767d\u4e00\u638c\u5df2\u628a\u201c\u6c99\u6f20\u4e4b\u72d0\u201d\u9707\u4f24\u5410\u8840\uff0c\u5c31\u5728\u8fd9\u7535\u5149\u77f3\u706b \u7684\u4e00\u5239\u90a3\uff0c\u5c55\u767d\u53cd\u81c2\u4e00\u6293\uff0c\u201c\u5927\u64d2\u62ff\u624b\u201d\u4e2d\u7684\u7edd\u62db\u201c\u6a2a\u4e91\u65ad\u5cf0\u201d\uff0c\u201c\u562d\u201d\u7684\u4e00\u58f0\uff0c\u6b63\u597d\u628a \u201c\u9634\u5c71\u4e4b\u72fc\u201d\u780d\u5230\u7684\u624b\u8155\u6263\u4f4f\uff01 \u5c55\u767d\u72ec\u6218\u201c\u9ed1\u9053\u56db\u51f6\u201d\uff0c\u672c\u65e0\u4f24\u4eba\u4e4b\u610f\uff0c\u53ea\u60f3\u4f7f\u56db\u51f6\u77e5\u96be\u800c\u9000\uff0c\u4e5f\u5c31\u7b97\u4e86\uff0c\u6ca1\u60f3\u5230\u201c\u4eba \u65e0\u4f24\u864e\u5fc3\uff0c\u864e\u6709\u4f24\u4eba\u610f\u201d\uff0c\u4ed6\u4e00\u5ff5\u4e4b\u6148\uff0c\u53cd\u800c\u8fde\u53d7\u4e24\u5904\u91cd\u4f24\uff01 \u800c\u4e14\u201c\u9ed1\u9053\u56db\u51f6\u201d\u5fc3\u72e0\u624b\u8fa3\uff0c\u5728\u4ed6\u53d7\u4f24\u4e4b\u540e\uff0c\u4ecd\u60f3\u628a\u4ed6\u6740\u6b7b\uff0c\u56e0\u6b64\uff0c\u5c55\u767d\u6fc0\u6012\u4e4b\u4e0b\uff0c\u5f3a \u5fcd\u4f4f\u4f24\u52bf\uff0c\u4e00\u638c\u628a\u201c\u6c99\u6f20\u4e4b\u72d0\u201d\u9707\u98de\uff0c\u53cd\u81c2\u4e00\u6293\uff0c\u53c8\u6263\u4f4f\u201c\u9634\u5c71\u4e4b\u72fc\u201d\u7684\u8155\u5b50\uff01 \u6b64\u65f6\uff0c\u5c55\u767d\u5df2\u6740\u7ea2\u4e86\u773c\uff0c\u4e0b\u624b\u4e0d\u518d\u7559\u60c5\uff0c\u6263\u4f4f\u201c\u9634\u5c71\u4e4b\u72fc\u201d\u7684\u624b\u4e00\u4f7f\u52b2\uff0c\u201c\u5361\u64e6\u201d\u4e00 \u58f0\uff0c\u7d27\u8ddf\u7740\u4e00\u58f0\u51c4\u5389\u7684\u60e8\u55e5\uff0c\u201c\u9634\u5c71\u4e4b\u72fc\u201d\u7684\u4e00\u53ea\u53f3\u638c\uff0c\u5df2\u88ab\u5c55\u767d\u9f50\u8155\u6298\u65ad\uff01 \u201c\u9634\u5c71\u4e4b\u72fc\u201d\u9762\u8272\u6e17\u767d\uff0c\u8e09\u8dc4\u9000\u540e\u4e94\u6b65\uff01 \u4f17\u4eba\u4e00\u9f50\u695e\u4e86\uff01\u4efb\u8c01\u4e5f\u60f3\u4e0d\u5230\u5c55\u767d\u91cd\u4f24\u4e4b\u4e0b\uff0c\u4e3e\u624b\u6295\u8db3\uff0c\u4ecd\u80fd\u8fde\u4f24\u4e24\u5927\u9ed1\u9053\u9ad8\u624b\uff01 \u5c55\u767d\u4ee5\u624b\u80cc\u64e6\u4e86\u64e6\u5634\u89d2\u7684\u6ea2\u8840\uff0c\u4fca\u9762\u542b\u5a01\uff0c\u7f13\u6b65\u5411\u201c\u8471\u5cad\u4e4b\u9e70\u201d\u4e0e\u201c\u592a\u4ed3\u4e4b\u9f20\u201d\u8d70 \u53bb\u2026\u2026 \u201c\u8471\u5cad\u4e4b\u9e70\u201d\u4e0e\u201c\u592a\u4ed3\u4e4b\u9f20\u201d\u90a3\u4e48\u72c2\u7684\u9ed1\u9053\u9b54\u5934\uff0c\u89c1\u5230\u5c55\u767d\u8fd9\u822c\u6c14\u52bf\uff0c\u7adf\u7136\u5413\u5f97\u8138\u53d8 \u8272\uff0c\u8eab\u4e0d\u7531\u4e3b\u5730\u5012\u9000\u4e86\u4e24\u6b65\uff01\u5c55\u767d\u51b7\u51b7\u5730\u901a\uff1a\u201c\u4eca\u5929\u4f60\u4eec\u4e00\u4e2a\u4e5f\u522b\u60f3\u6d3b\u7740\u56de\u53bb\uff01\u201d\u8bf4\u7740\uff0c\u53cc \u638c\u5e73\u80f8\u4e3e\u8d77\u2026\u2026 \u7b2c\u4e94\u5341\u4e03\u7ae0\u3000\u201c\u5357\u6d77\u9f99\u5973\u201d\u9732\u82b3\u60c5>> \u53e4\u9f99\u300a\u5251\u5ba2\u884c\u300b \u7b2c\u4e94\u5341\u4e03\u7ae0\u3000\u201c\u5357\u6d77\u9f99\u5973\u201d\u9732\u82b3\u60c5 \u5c55\u767d\u7389\u9762\u751f\u5a01\uff0c\u82f1\u633a\u7709\u5b87\u95f4\u4e00\u7247\u6740\u673a\uff0c\u529f\u8d2f\u53cc\u638c\uff0c\u7f13\u5411\u201c\u8471\u5cad\u4e4b\u9e70\u201d\u53ca\u201c\u592a\u4ed3\u4e4b\u9f20\u201d\u903c \u8fd1\uff01 \u201c\u8471\u5cad\u4e4b\u9e70\u201d\u3001\u201c\u592a\u4ed3\u4e4b\u9f20\u201d\u90a3\u4e48\u72c2\u7684\u9ed1\u9053\u9ad8\u624b\uff0c\u7ade\u88ab\u5c55\u767d\u7684\u6c14\u52bf\u6240\u6151\uff0c\u5413\u5f97\u9762\u76ee\u53d8 \u8272\uff0c\u53cc\u811a\u4e0d\u7531\u81ea\u4e3b\u5730\u968f\u7740\u5c55\u81ea\u903c\u8fd1\u7684\u811a\u6b65\u8d8a\u8d85\u540e\u9000\uff01\u5c55\u767d\u5251\u7709\u4e00\u626c\uff0c\u53cc\u638c\u7f13\u7f13\u4e3e\u8d77\uff0c\u72e0\u72e0\u5730 \u9053\uff1a\u201c\u4eca\u5929\u4f60\u4eec\u4e00\u4e2a\u4e5f\u5254\u60f3\u5168\u8eab\u800c\u9000\uff01\u201d \u8bf4\u7740\u4e3e\u638c\u6b32\u5288\u2026\u3002\u2019\u5ffd\u7136\u2014\u2014\u4e00\u9635\u9707\u5929\u8c6a\u7b11\u54cd\u8d77\uff01 \u8c6a\u7b11\u58f0\u4e2d\uff0c\u6570\u6761\u4eba\u5f71\u75be\u5982\u98de\u77e2\u800c\u81f3\uff01 \u6765\u4eba\u8eab\u6cd5\u592a\u5feb\uff0c\u4f17\u4eba\u8ddf\u524d\u4e00\u82b1\uff0c\u573a\u4e2d\u5df2\u591a\u4e86\u4e94\u4e2a\u4ebar\u5f53\u5148\u4e24\u4eba\uff0c\u4e00\u4e2a\u662f\u767d\u8863\u94f6\u6247\u5112\u751f\u516c \u5b50\uff0c\u4e00\u4e2a\u662f\u7fbd\u8863\u9713\u88f3\u8c8c\u5982\u5929\u4ed9\u4f3c\u7684\u76db\u88c5\u4e3d\u4eba\u3002\u968f\u540e\u4e09\u4eba\uff0c\u4e00\u4e2a\u957f\u9aef\u8fc7\u8179\u7684\u845b\u8863\u8001\u8005\uff0c\u4e00\u4e2a\u9e21 \u76ae\u575e\u9762\u7684\u767d\u53d1\u5a46\u5a46\uff0c\u53e6\u5916\u4e00\u4e2a\u5219\u662f\u4e2d\u5e74\u77ee\u80d6\u7684\u548c\u5c1a\uff01 \u8fd9\u4e94\u4eba\u4e00\u9732\u9762\uff0c\u5728\u573a\u4e4b\u4eba\u5fc3\u4e2d\u65e0\u4e0d\u4e00\u61d4\uff0c\u539f\u6765\u6b63\u662f\u201c\u5357\u6d77\u5c11\u541b\u201d\u201c\u9f99\u795e\u592a\u5b50\u201d\u3001\u201c\u5357\u6d77 \u9f99\u5973\u201d\u9f99\u73e0\u513f\uff0c\u4ee5\u53ca\u201c\u6d77\u5916\u4e09\u715e\u201d\uff01 \u201c\u9f99\u795e\u592a\u5b50\u201d\u201c\u5357\u6d77\u5c11\u541b\u201d\u94f6\u6247\u4e00\u6296\uff0c\u767d\u8863\u98d8\u98d8\uff0c\u723d\u6717\u4e00\u7b11\uff0c\u5411\u5c55\u767d\u9053\uff1a\u6562\u83ab\u662f\u5c55\u5144\u53c8 \u8981\u5728\u6c5f\u6e56\u4e0a\u63d2\u4e00\u811a\u4e86\u5417\uff01\u201d \u957f\u9aef\u8001\u4eba\u4ec7\u5982\u6d77\u6380\u987b\u8c6a\u7b11\u9053\uff1a\u201c\u5c0f\u54e5\u513f\uff0c\u54b1\u4eec\u7684\u8d4c\u7ea6\u662f\u5426\u5df2\u6210\u4e3a\u8fc7\u53bb\uff1f\u201d \u6b66\u6797\u4eba\u7269\uff0c\u8bb2\u7a76\u4e00\u8a00\u4e5d\u9f0e\uff0c\u5c24\u5176\u4ee5\u5c55\u767d\u7684\u803f\u76f4\u4e2a\u6027\u6765\u8bf4\uff0c\u81ea\u5df2\u7684\u8bfa\u8a00\u66f4\u662f\u65e0\u6cd5\u4e0d\u4e88\u9075 \u5b88\u3002\u5982\u4eca\u88ab\u4e8c\u4eba\u62ff\u8bdd\u4e00\u6572\uff0c\u4e0d\u7531\u4fca\u8138\u6cdb\u7ea2\uff0c\u8bb7\u8bb7\u5730\u9053\uff1a\u201c\u672c\u4eba\u8bf4\u51fa\u53e3\u7684\u8bdd\uff0c\u5411\u6765\u7b97\u6570\u3002\u53ea\u662f \u8fd9\u56db\u4f4d\u4e8b\u524d\u5df2\u58f0\u660e\u4e0d\u662f\u6c5f\u6e56\u4eba\u7269\uff0c\u800c\u4e14\u5176\u5fc3\u72e0\u624b\u8fa3\u5230\u4e86\u4ee4\u4eba\u2026\u2026\u201d \u201c\u5357\u6d77\u9f99\u5973\u201d\u7f8e\u76ee\u6d41\u76fc\uff0c\u77a5\u4e86\u5c55\u767d\u4e00\u773c\uff0c\u9053\uff1a\u201c\u770b\u5916\u8868\u5c55\u516c\u5b50\u50cf\u4e2a\u8001\u5b9e\u4eba\uff0c\u6ca1\u60f3\u5230\u5f53\u7740 \u8fd9\u4e48\u591a\u4eba\u4f1a\u8bf4\u7b11\u8bdd\uff0c\u4ed6\u56db\u4eba\u4e0d\u662f\u6c5f\u6e56\u4eba\u7269\uff0c\u600e\u4e48\u4f1a\u6b66\u529f\uff1f\u5206\u660e\u662f\u51fa\u5c14\u53cd\u5c14\uff0c\u8bf4\u4e86\u8bdd\u4e0d\u7b97\u6570\uff0c \u4e2d\u539f\u6b66\u6797\u7684\u8138\uff0c\u6050\u6015\u88ab\u4f60\u4e00\u4eba\u4e22\u5c3d\u4e86\uff01\u2026\u2026\u201d \u8fd9\u4e00\u6bb5\u8bdd\uff0c\u53ef\u8bf4\u662f\u6781\u5c3d\u6316\u82e6\u8bbd\u523a\u4e4b\u80fd\u4e8b\uff0c\u5c55\u767d\u5b9e\u5728\u5fcd\u65e0\u53ef\u5fcd\uff0c\u4e0d\u7531\u55d4\u76ee\u53f1\u9053\uff1a\u201c\u4f4f\u53e3\uff01 \u6211\u8981\u4e0d\u770b\u4f60\u662f\u4e2a\u5973\u6d41\u4e4b\u8f88\uff0c\u6211\u5c31\u8981\u2026\u2026\u201d \u8bf4\u5230\u6b64\u5904\uff0c\u5c55\u767d\u7a81\u7136\u60f3\u5230\u81ea\u5df2\u4e00\u8a00\u51fa\u53e3\uff0c\u4e0d\u95ee\u6c5f\u6e56\u662f\u975e\uff0c\u65b9\u624d\u4e0e\u201c\u9ed1\u9053\u56db\u51f6\u201d\u4e00\u6218\uff0c\u5df2 \u843d\u4e86\u628a\u67c4\uff0c\u5982\u4eca\u5c82\u80fd\u518d\u81ea\u5df1\u6253\u81ea\u5df1\u5634\u5df4\uff0c\u548c\u4eba\u5bb6\u52a8\u624b\uff1f\u56e0\u6b64\u8bdd\u5230\u5507\u8fb9\uff0c\u53c8\u4e34\u65f6\u6536\u53e3\u3002 \u5a49\u513f\u4f24\u540e\u865a\u5f31\uff0c\u5c1a\u672a\u590d\u539f\uff0c\u89c1\u5c55\u54e5\u54e5\u81c2\u4e0a\u9c9c\u8840\u6dcb\u6dcb\uff0c\u7ade\u5fae\u79fb\u83b2\u6b65\uff0c\u8885\u8885\u5a77\u5a77\u5730\u8d70\u81f3\u5c55\u767d \u8eab\u8fb9\uff0c\u7389\u624b\u6276\u4f4f\u5c55\u767d\u95ee\u9053\uff1a\u201c\u5c55\u54e5\u54e5\uff01 \u4f60\u7684\u4f24\uff0c\u4e0d\u8981\u7d27\u5427\uff1f\u201d \u5173\u6000\u4e4b\u60c5\u6ea2\u4e8e\u8a00\u8868\u3002 \u5c55\u767d\u5fc3\u5934\u6d41\u8fc7\u4e00\u80a1\u6696\u6d41\uff0c\u5a49\u513f\u7684\u5173\u5207\uff0c\u4f7f\u4ed6\u5927\u4e3a\u611f\u52a8\uff0c\u4f46\u5f53\u7740\u8fd9\u4e48\u591a\u4eba\uff0c\u4ed6\u4e0d\u4fbf\u8868\u73b0\u51fa \u6765\uff0c\u53ea\u8c08\u8c08\u4e00\u7b11\u9053\uff0c\u201c\u5a49\u59b9\uff0c\u4f60\u653e\u5fc3\uff01\u8fd9\u70b9\u4f24\u4e0d\u7b97\u4ec0\u4e48\uff01\u201d \u5a49\u513f\u6b64\u65f6\u5df2\u770b\u8fc7\u5c55\u767d\u7684\u4f24\u81c2\uff0c\u5fc3\u4e2d\u4e0d\u7531\u53c8\u60ca\u53c8\u559c\uff0c\u56e0\u4e3a\u77ed\u77ed\u7684\u65f6\u95f4\uff0c\u4e5f\u672a\u89c1\u5c55\u767d\u8c03\u606f\uff0c \u90a3\u81c2\u4e0a\u7684\u4f24\u5904\u4e0d\u4f46\u6b62\u8840\u751f\u808c\uff0c\u800c\u4e14\u597d\u50cf\u5df2\u590d\u539f\u4e86\uff0c\u53ea\u5728\u81c2\u4e0a\u7559\u4e0b\u624b\u6307\u957f\u7684\u4e00\u4e2a\u75a4\u75d5\uff01 \u5a49\u513f\u4e0d\u77e5\u5c55\u767d\u670d\u98df\u8fc7\u5343\u5e74\u7075\u836f\u201c\u7d2b\u6a80\u82b1\u201d\uff0c\u53ea\u4ee5\u4e3a\u5c55\u54e5\u54e5\u529f\u529b\u6df1\u539a\u81f3\u6b64\uff0c\u5b9e\u4e0d\u4e9a\u4e8e\u91d1\u521a \u4e0d\u574f\u4e4b\u8eaf\uff0c\u4e0d\u7531\u559c\u6781\u800c\u547c\u9053\uff1a\u201c\u5c55\u54e5\u54e5\uff0c\u4f24\u5904\u5df2\u597d\u4e86\u561b\uff01\u201d \u5a49\u513f\u559c\u6781\u5fd8\u5f62\uff0c\u53cc\u624b\u62b1\u7740\u5c55\u767d\u81c2\u8180\uff0c\u4e00\u9635\u6447\u64bc\uff0c\u5979\u4f24\u540e\u865a\u5f31\u82cd\u767d\u7684\u7c89\u8138\uff0c\u7adf\u4e5f\u9690\u6cdb\u8d77\u4e00 \u5c42\u7ea2\u6da6\uff0c\u7709\u773c\u76c8\u76c8\uff0c\u900f\u51fa\u5979\u5fc3\u5e95\u662f\u591a\u4e48\u9ad8\u5174\uff01 \u6155\u5bb9\u7ea2\u5728\u540d\u4efd\u4e0a\u5df2\u662f\u5c55\u767d\u7684\u672a\u5a5a\u59bb\uff0c\u770b\u5230\u59b9\u59b9\u5bf9\u5c55\u767d\u5982\u6b64\u4eb2\u70ed\uff0c\u5fc3\u4e2d\u987f\u7136\u9192\u609f\uff1a\u5a49\u513f\u542c \u5230\u81ea\u5df1\u4e0e\u5c55\u767d\u8ba2\u5a5a\u7684\u6d88\u606f\uff0c\u6084\u7136\u51fa\u8d70\u7684\u539f\u56e0\uff0c\u56de\u60f3\u4ee5\u524d\u79cd\u79cd\uff0c\u624d\u77e5\u9053\u4ed6\u4eec\u65e9\u5df2\u76f8\u7231\uff0c\u81f3\u6b64\uff0c \u5979\u5fc3\u4e2d\u5012\u6ca1\u6709\u5992\u610f\uff0c\u53ea\u5947\u602a\u8fd9\u4e48\u91cd\u8981\u7684\u4e8b\uff0c\u81ea\u5df1\u4ee5\u524d\u600e\u4e48\u6ca1\u7559\u5fc3\uff0c\u7adf\u4e00\u70b9\u4e5f\u6ca1\u6709\u770b\u51fa\u6765\u2026\u2026 \u91d1\u5f69\u51e4\u5374\u6709\u70b9\u60cb\u60dc\u7684\u611f\u89c9\uff0c\u5979\u5fc3\u4e2d\u5173\u6000\u5c55\u767d\u4f24\u52bf\uff0c\u65e9\u5c31\u60f3\u4e0a\u524d\u53bb\u95ee\u5019\uff0c\u53ef\u662f\u95fa\u9601\u7684\u6559 \u517b\uff0c\u4f7f\u5979\u77dc\u6301\u4f4f\u4e86\uff0c\u73b0\u5728\u89c1\u5a49\u513f\u505a\u4e86\u81ea\u5df1\u60f3\u505a\u800c\u672a\u6562\u505a\u7684\u4e8b\uff0c\u4e14\u5f97\u5230\u5c55\u767d\u611f\u6fc0\uff0c\u5979\u6697\u6697\u540e\u6094 \u81ea\u5df1\u7f3a\u5c11\u52c7\u6a0a\u7d20\u9e3e\u6bd4\u8f83\u542b\u84c4\uff0c\u89c1\u5a49\u51e0\u5bf9\u5c55\u767d\u4e00\u5f80\u60c5\u6df1\uff0c\u5fc3\u60f3\u81ea\u5df1\u628a\u5fc3\u60c5\u6df1\u9690\u4e0d\u9732\uff0c\u4e5f\u8bb8\u662f\u505a \u5bf9\u4e86\u2026\u2026 \u201c\u7aef\u65b9\u516c\u5b50\u201d\u918b\u706b\u4e2d\u70e7\u2026\u2026 \u201c\u51cc\u98ce\u516c\u5b50\u201d\u77aa\u5927\u4e86\u773c\u775b\uff0c\u4ed6\u59d0\u59d0\u5df2\u5ac1\u7ed9\u5c55\u767d\uff0c\u8fd9\u59b9\u59b9\u53c8\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u2026\u2026 \u201c\u5357\u6d77\u9f99\u5973\u201d\u5374\u7a81\u5730\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4e0d\u8981\u8138\uff01\u201d \u5a49\u513f\u7c89\u8138\u715e\u767d\uff0c\u56de\u5934\u6012\u89c6\u7740\u201c\u5357\u6d77\u9f99\u5973\u201d\uff0c\u5a07\u5428\u9053\uff1a\u201c\u4f60\u9a82\u54ea\u4e00\u4e2a\uff1f\u201d \u201c\u5357\u6d77\u9f99\u5973\u201d\u4e5f\u4e0d\u77e5\u9053\u81ea\u5df1\u4e3a\u4ec0\u4e48\u8868\u73b0\u5f97\u8fd9\u4e48\u91cf\u7a84\uff0c\u4ee5\u5979\u7684\u8eab\u4efd\u6765\u8bb2\uff0c\u662f\u4e0d\u5e94\u8be5\u8fd9\u4e48\u6ca1 \u6709\u98ce\u5ea6\u7684\u3002\u4f46\u65e2\u662f\u5df2\u9a82\u51fa\u53e3\u6765\u4e86\uff0c\u4fbf\u4e0d\u80fd\u518d\u6536\u56de\uff0c\u88ab\u5a49\u513f\u5389\u58f0\u4e00\u95ee\uff0c\u4e0d\u7531\u66b4\u6012\uff0c\u4e5f\u5389\u58f0\u9053\uff1a \u201c\u6211\u9a82\u8c01\u96be\u9053\u4f60\u5fc3\u91cc\u8fd8\u4e0d\u660e\u767d\u5417\uff1f\u201d \u5a49\u513f\u6012\u6781\uff0c\u51fa\u624b\u4e00\u62db\u201c\u641c\u9b42\u6307\u201d\uff0c\u75be\u5411\u201c\u5357\u6d77\u9f99\u5973\u201d\u201c\u5fc3\u4fde\u201d\u91cd\u7a74\u70b9\u53bb\uff01 \u53ef\u662f\u5979\u4f24\u540e\u672a\u590d\u539f\uff0c\u6307\u6cd5\u867d\u51cc\u5389\uff0c\u4f46\u52b2\u529b\u4e0d\u8db3\uff0c\u88ab\u201c\u5357\u6d77\u9f99\u5973\u201d\u2019\u638c\u62c2\u9000\u4e03\u5927\u6b65\uff01 \u201c\u5357\u6d77\u9f99\u5973\u201d\u51b7\u7b11\u4e00\u58f0\uff0c\u72e0\u72e0\u5730\u9053\uff1a\u201c\u4e0d\u77e5\u6b7b\u6d3b\u7684\u4e2b\u5934\uff0c\u6211\u770b\u4f60\u662f\u6d3b\u591f\u4e86\uff01\u201d\u8bf4\u7f62\u7ea4\u638c \u4e3e\u8d77\uff0c\u84c4\u529b\u6b32\u53d1\u2026\u3002\u2019\u201c\u6162\u7740\uff01\u201d \u5c55\u767d\u6a2a\u8de8\u4e00\u6b65\uff0c\u6321\u4f4f\u201c\u5357\u6d77\u9f99\u5973\u201d\uff0c\u9053\uff1a\u201c\u5a49\u513f\u59d1\u5a18\u8d1f\u4f24\u672a\u6108\uff0c\u4f60\u600e\u53ef\u2026\u2026\u201d \u201c\u5357\u6d77\u9f99\u5973\u201d\u638c\u529b\u672a\u6536\uff0c\u7f8e\u76ee\u51dd\u6ce8\u5c55\u767d\uff0c\u9053\uff1a\u201c\u4f60\u662f\u5426\u53c8\u60f3\u63d2\u4e0a\u4e00\u624b\uff1f\u201d \u5c55\u767d\u9053\uff1a\u201c\u4f60\u4e0d\u7528\u5904\u5904\u4ee5\u6b64\u8981\u631f\u5728\u4e0b\uff0c\u8981\u77e5\u9053\u4e0d\u6253\u8d1f\u4f24\u4e4b\u4eba\uff0c\u4e5f\u662f\u6b66\u6797\u4f20\u7edf\uff0c\u8fd9\u89c4\u77e9\u4f60 \u61c2\u4e0d\u61c2\uff1f\u201d \u201c\u5357\u6d77\u9f99\u5973\u201d\u7c89\u9762\u4e00\u7ea2\u3002\u2026\u2019\u201c\u5357\u6d77\u5c11\u541b\u201d\u201c\u9f99\u795e\u592a\u5b50\u201d\u4e00\u6447\u94f6\u6247\uff0c\u4e0a\u524d\u4e09\u6b65\uff0c\u5411\u5c55\u81ea \u9053\uff1a\u201c\u9601\u4e0b\u6ca1\u6709\u8d44\u683c\u8fc7\u95ee\u6c5f\u6e56\u662f\u975e\uff0c\u73b0\u5728\u8bf7\u4f60\u9000\u540e\uff01\u201d \u5c55\u81ea\u8fd8\u4ee5\u4e3a\u4ed6\u8981\u4f24\u5bb3\u5a49\u513f\uff0c\u522b\u7684\u4e8b\u60c5\u4ed6\u53ef\u4ee5\u4e0d\u7ba1\uff0c\u552f\u72ec\u6b64\u4e8b\uff0c\u4ed6\u4e0d\u80fd\u64a4\u624b\u4e0d\u7ba1\uff0c\u95fb\u4e4b \u9053\uff1a\u201c\u4e0d\u77e5\u5c0a\u9a7e\u6b64\u8bdd\u6307\u4f55\u800c\u8bf4\uff1f\u201d \u201c\u9f99\u795e\u592a\u5b50\u201d\u5fae\u5fae\u4e00\u7b11\uff0c\u5374\u4e0d\u7406\u5c55\u767d\uff0c\u8f6c\u5411\u7fa4\u96c4\u9053\uff1a\u201c\u672c\u6d3e\u5165\u4e3b\u4e2d\u539f\uff0c\u539f\u662f\u60f3\u7edf\u4e00\u5929\u4e0b \u6b66\u6797\uff0c\u4ee5\u514d\u95e8\u6237\u4e4b\u89c1\uff0c\u4e89\u7aef\u65f6\u8d77\uff0c\u5e78\u8499\u6b66\u6797\u4eba\u58eb\u4e0d\u5f03\uff0c\u7eb7\u7eb7\u52a0\u76df\u672c\u6d3e\uff0c\u5171\u8c0b\u80dc\u4e3e\uff0c\u4f7f\u672c\u6d3e\u65e5 \u76ca\u5149\u5927\uff0c\u773c\u770b\u2018\u5929\u4e0b\u4e00\u5bb6\u2019\u5927\u529f\u544a\u6210\uff0c\u6ca1\u60f3\u5230\u8fd8\u6709\u67d0\u4e9b\u91ce\u5fc3\u4e0d\u6b7b\u7684\u4eba\u7269\uff0c\u65f6\u65f6\u60f3\u7834\u574f\u672c\u6d3e\u4e49 \u4e3e\uff0c\u73b0\u5728\u672c\u6d3e\u8ba2\u4e5d\u4e5d\u91cd\u9633\uff0c\u5728\u7bd9\u5c71\u5c11\u6797\u5bfa\u65e7\u5740\u53ec\u5f00\u2018\u82f1\u96c4\u5927\u4f1a\u2019\uff0c\u51e1\u662f\u4e0d\u670d\u672c\u6d3e\u7684\u90fd\u53ef\u4e0e \u4f1a\uff0c\u5c4a\u65f6\u4ee5\u6b66\u4f1a\u53cb\uff0c\u4e0d\u8bba\u4f55\u95e8\u4f55\u6d3e\uff0c\u786e\u5b9e\u6709\u6bd4\u672c\u6e90\u6b66\u529f\u9ad8\u5f3a\u7684\uff0c\u672c\u6d3e\u4ece\u6b64\u9000\u51fa\u4e2d\u539f\uff01\u201d \u4ed6\u8bf4\u81f3\u6b64\uff0c\u5fae\u5fae\u4e00\u987f\uff0c\u89c1\u7fa4\u96c4\u90fd\u5927\u7741\u7740\u53cc\u773c\uff0c\u6ce8\u610f\u5730\u542c\u7740\u4ed6\u7684\u8bdd\uff0c\u4ed6\u5f97\u610f\u5730\u4e00\u7b11\uff0c\u53c8\u63a5 \u4e0b\u53bb\u9053\uff1a\u5982\u679c\u672c\u6e90\u6b66\u529f\u786e\u80fd\u80dc\u8fc7\u5404\u95e8\u5404\u6d3e\uff0c\u90a3\u4e48\u6ca1\u6709\u4ec0\u4e48\u8bf4\u7684\uff0c\u4ece\u4eca\u4ee5\u540e\u4e2d\u539f\u6b66\u6797\u552f\u2018\u5357\u6d77 \u95e8\u2019\u4e3a\u5c0a\uff0c\u518d\u6709\u4e0d\u670d\u6d3e\u4ee4\u8005\uff0c\u53ef\u4e0d\u8981\u8bf4\u672c\u6d3e\u624b\u6bb5\u6bd2\u8fa3\u8d76\u5c3d\u6740\u7edd\uff01\u201d \u4ed6\u8fd9\u8bdd\u8d8a\u8bf4\u8d8a\u6fc0\u6602\uff0c\u5230\u6700\u540e\u5df2\u662f\u58f0\u8272\u60e7\u5389\uff01 \u7fa4\u96c4\u7eb7\u7eb7\u5403\u60ca\uff0c\u5357\u6d77\u95e8\u201d\u541b\u4e34\u5929\u4e0b\u6b66\u6797\uff0c\u53ef\u80fd\u5df2\u6709\u4e86\u5341\u6210\u628a\u63e1\uff0c\u82e5\u4e0d\u7136\u4e0d\u4f1a\u5982\u6b64\u660e\u76ee\u5f20 \u80c6\uff0c\u516c\u5f00\u5411\u5929\u4e0b\u6b66\u6797\u53eb\u9635\uff01 \u5047\u5982\uff0c\u4e2d\u539f\u6b66\u6797\u771f\u8981\u88ab\u201c\u5357\u6d77\u95e8\u201d\u4e00\u4e3e\u5f81\u670d\uff0c\u4eca\u540e\u6b66\u6797\u4eba\u7269\u7684\u547d\u8fd0\u771f\u662f\u4e0d\u582a\u8bbe\u60f3\uff01 \u5c24\u5176\u5c11\u6797\u6d3e\u51fa\u8eab\u7684\u201c\u795e\u62f3\u6253\u4e95\u201d\u6b66\u9707\u98de\uff0c\u53ca\u5c11\u6797\u5bfa\u7f57\u6c49\u5802\u9996\u5ea7\u201c\u864e\u76ee\u5c0a\u8005\u201d\uff0c\u66f4\u662f\u5403 \u60ca\uff01 \u201c\u9f99\u795e\u592a\u5b50\u201d\u58f0\u8a00\u4e5d\u4e38\u91cd\u9633\u5728\u7bd9\u5c71\u5c11\u6797\u5bfa\u53ec\u5f00\u82f1\u96c4\u5927\u4f1a\uff0c\u83ab\u975e\u5c11\u6797\u5bfa\u5df2\u4e3a\u201c\u5357\u6d77\u95e8\u201d\u5360 \u636e\u4e0d\u6210\uff1f \u4e0d\u7136\u7684\u8bdd\uff0c\u5c11\u6797\u5bfa\u81ea\u6709\u4e3b\u6301\u4eba\uff0c\u5bfa\u5185\u53ec\u5f00\u4e0d\u53ec\u5f00\u82f1\u96c4\u5927\u4f1a\uff0c\u81ea\u6709\u4e3b\u6301\u4f5c\u4e3b\uff0c\u5c82\u80fd\u4efb\u5916\u4eba \u8d8a\u6cae\u4ee3\u5e99\uff1f\u2026\u2026 \u4e0d\u7ba1\u7fa4\u96c4\u5403\u60ca\uff0c\u201c\u9f99\u795e\u592a\u5b50\u201d\u8138\u4e0a\u9634\u68ee\u8868\u60c5\u4e00\u6536\uff0c\u7115\u7136\u4e00\u7b11\u9053\uff1a\u201c\u5929\u4e0b\u6b66\u6797\uff0c\u51e1\u81ea\u8ba4\u662f \u82f1\u96c4\u4eba\u7269\u8005\uff0c\u5747\u53ef\u4e0e\u4f1a\u3002\u201d \u8bf4\u7740\u53c8\u8f6c\u5411\u5c55\u767d\uff0c\u9053\uff1a\u201c\u552f\u72ec\u9601\u4e0b\u6ca1\u6709\u8d44\u683c\u53c2\u52a0\uff01\u201d \u8bf4\u5b8c\u4ef0\u5929\u4e00\u9635\u5927\u7b11\uff0c\u7b11\u7f62\u5bf9\u201c\u5357\u6d77\u4e09\u715e\u201d\u53ca\u201c\u9ed1\u9053\u56db\u51f6\u201d\u9053\uff1a \u201c\u54b1\u4eec\u8d70\uff01\u201d \u201c\u8d70\u201d\u5b57\u51fa\u53e3\uff0c\u4eba\u5df2\u5f53\u5148\u5411\u9662\u5899\u5916\u63a0\u53bb\uff0c\u968f\u540e\u201c\u8471\u5cad\u4e4b\u9e70\u201d\u53ca\u201c\u592a\u4ed3\u4e4b\u9f20\u201d\u5206\u522b\u5c06\u53d7\u4f24 \u7684\u201c\u6c99\u6f20\u4e4b\u72d0\u201d\u53ca\u201c\u9634\u5c71\u4e4b\u72fc\u201d\u62b1\u8d77\uff0c\u968f\u7740\u201c\u6d77\u5916\u4e09\u715e\u201d\u5982\u98de\u800c\u53bb\uff01 \u201c\u5357\u6d77\u9f99\u5973\u201d\u4e34\u8d70\uff0c\u5411\u5c55\u767d\u77a5\u4e86\u4e00\u773c\uff0c\u90a3\u773c\u5305\u542b\u7740\u201c\u6068\u201d\u4e0e\u201c\u7231\uff1f \u4ea4\u7ec7\u7684\u60c5\u7eea\uff0c\u5c55\u767d\u5fc3\u5934\u4e00\u9707\uff0c\u76ee\u524d\u4ed6\u60c5\u5b7d\u7ea0\u7f20\uff0c\u65e0\u6cd5\u81ea\u5904\uff0c\u6700\u6015\u770b\u5230\u5973\u4eba\u7528\u8fd9\u79cd\u773c\u5149\u770b \u4ed6\uff0c\u89c1\u201c\u5357\u6d77\u9f99\u5973\u201d\u4e5f\u7528\u8fd9\u79cd\u773c\u5149\u770b\u4ed6\uff0c\u4e0d\u7531\u6253\u4e86\u4e2a\u51b7\u6218\uff0c\u8d76\u7d27\u4f4e\u4e0b\u5934\u53bb\u2026\u2026 \u76f4\u5230\u201c\u5357\u6d77\u5c11\u541b\u201d\u201c\u9f99\u795e\u592a\u5b50\u201d\u7387\u4f17\u8d70\u4e86\u5f88\u4e45\uff0c\u88ab\u5413\u6014\u7684\u7fa4\u96c4\u624d\u53d1\u51fa\u4e00\u9635\u55e1\u55e1\u58f0\uff01\u7fa4\u96c4 \u7eb7\u7eb7\u8bae\u8bba\uff0c\u5404\u627e\u5bf9\u8c61\u6292\u53d1\u5df1\u89c1\uff0c\u4ee5\u81f4\u4eba\u58f0\u5608\u6742\uff0c\u5e7f\u5927\u9662\u843d\uff0c\u4e09\u4e2a\u4e00\u7fa4\uff0c\u4e94\u4e2a\u4e00\u4f19\uff0c\u7eb7\u7eb7\u53d1 \u8a00\u3002\u8339\u8001\u9556\u5934\u4e0e\u96f7\u5927\u53d4\u53ca\u201c\u592a\u767d\u53cc\u9038\u201d\u5728\u4e00\u8d77\uff0c\u8339\u8001\u9556\u5934\u671b\u4e86\u671b\u957f\u7709\u6df1\u9501\u7684\u96f7\u5927\u53d4\uff0c\u5fcd\u4e0d\u4f4f \u95ee\u9053\uff1a\u201c\u770b\u6765\u8fd9\u2018\u5357\u6d77\u95e8\u2019\u5df1\u6210\u4e86\u6c14\u5019\uff0c\u7adf\u5f53\u4f17\u5411\u5929\u4e0b\u6b66\u6797\u6311\u6218\u4e86\uff01\u201d \u201c\u592a\u767d\u53cc\u9038\u201d\u9f50\u58f0\u9644\u548c\u9053\uff1a\u201c\u4e0d\u7b80\u5355\uff01\u4eca\u540e\u4e2d\u539f\u6b66\u6797\uff0c\u6050\u6015\u53c8\u8981\u6380\u8d77\u4e00\u7247\u8165\u98ce\u8840\u96e8\uff01\u201d \u96f7\u5927\u53d4\u6df1\u9501\u53cc\u7709\uff0c\u9891\u9891\u6447\u5934\uff0c\u5fe7\u5f62\u4e8e\u8272\u9053\uff1a\u201c\u503c\u5f97\u5fe7\u8651\uff01\u4e0d\u6015\u2018\u5357\u6d77\u95e8\u52bf\u529b\u6d69\u5927\uff0c\u6015\u7684 \u662f\u4e2d\u539f\u6b66\u6797\u5404\u95e8\u5404\u6d3e\u6210\u89c1\u592a\u6df1\uff0c\u4e0d\u80fd\u63a8\u8bda\u5408\u70b8\uff0c\u96be\u514d\u88ab\u5404\u4e2a\u51fb\u7834\u2026\u2026\u201d \u5728\u65c1\u8fb9\u7684\u201c\u9152\u4e10\u201d\u65b9\u7ca5\uff0c\u4e3e\u8d77\u94c1\u846b\u82a6\uff0c\u4ef0\u8116\u5b50\u704c\u4e86\u4e00\u53e3\u9152\uff0c\u7528\u624b\u4e00\u62b9\u5634\u4e0a\u9152\u6e0d\uff0c\u9053\uff1a \u201c\u96f7\u75af\u5b50\u5927\u6709\u89c1\u5730\uff01\u5e38\u8a00\u9053\u56e2\u7ed3\u624d\u80fd\u53d1\u751f\u529b\u91cf\uff0c\u90a3\u54b1\u4eec\u5728\u573a\u4e4b\u4eba\uff0c\u4eca\u5929\u5c31\u6765\u4e2a\u2018\u6b43\u8840\u4e3a \u76df\u2019\uff0c\u5171\u540c\u5bf9\u6297\u2018\u5357\u6d77\u95e8\u2019\uff0c\u96f7\u75af\u5b50\u610f\u4e0b\u5982\u4f55\uff1f\u201d \u96f7\u5927\u53d4\u9eef\u7136\u4e00\u7b11\uff0c\u672a\u7f6e\u53ef\u5426\u3002\u56e0\u4e3a\u96f7\u5927\u4e9b\u4eba\u7269\uff0c\u51b3\u4e0d\u662f\u201c\u5357\u6d77\u95e8\u201d\u4e4b\u654c\uff0c\u51b5\u4e14\u201c\u6b66\u6797\u56db \u516c\u5b50\u201d\u5404\u5b58\u5f02\u5fd7\uff0c\u90fd\u6709\u9886\u8896\u7fa4\u4f26\u7684\u91ce\u5fc3\uff0c\u5374\u90fd\u6ca1\u6709\u9886\u8896\u7fa4\u4f26\u4ee5\u62b5\u6297\u201c\u5357\u6d77\u95e8\u201d\u7684\u80fd\u529b\u3002 \u53ef\u662f\u4ed6\u4e0d\u597d\u610f\u601d\u5f53\u9762\u8bf4\u51fa\uff0c\u53ea\u6709\u4e00\u7b11\u7f6e\u4e4b\u3002 \u201c\u75af\u4e10\u201d\u891a\u826f\u4e5f\u8fc7\u6765\uff0c\u63d2\u53e3\u9053\uff1a\u201c\u603b\u4e4b\uff0c\u6211\u4eec\u4e2d\u539f\u6b66\u6797\u8981\u4e0e\u2018\u5357\u6d77\u95e8\u2019\u4e00\u62fc\uff0c\u51b3\u4e0d\u80fd\u7518 \u5fc3\u4fef\u9996\u79f0\u81e3\uff01\u201d \u201c\u6d3b\u6b7b\u4eba\u201d\u6b7b\u7709\u584c\u773c\u5730\u9053\uff1a\u201c\u8001\u53eb\u5316\u58ee\u5fd7\u53ef\u5609\uff0c\u53ef\u662f\u4e0d\u60f3\u4e00\u60f3\uff0c\u773c\u524d\u81ea\u547d\u4e0d\u51e1\u7684\u82f1\u96c4\u4eba \u7269\uff0c\u9664\u4e86\u5c55\u5144\u5f1f\u4e00\u4eba\uff0c\u6709\u8c01\u662f\u2018\u4e09\u715e\u2019\u3001\u2018\u56db\u51f6\u2019\u7684\u5bf9\u624b\uff1f\u4f55\u51b5\u8fd8\u4e0d\u77e5\u2018\u5357\u6d77\u95e8\u2019\u6709\u591a\u5c11\u6210 \u540d\u9ad8\u624b\u5c1a\u672a\u9732\u9762\uff1f\u201d \u96f7\u5927\u53d4\u671b\u4e86\u671b\u5c55\u767d\uff0c\u8138\u4e0a\u5fe7\u8272\u66f4\u91cd\uff0c\u4f46\u4ecd\u672b\u53d1\u8a00\u3002 \u201c\u5b89\u4e50\u516c\u5b50\u201d\u7a81\u7136\u5927\u6b65\u8d70\u4e86\u8fc7\u6765\uff0c\u9053\uff1a\u201c\u5c55\u5144\u6b66\u529f\u8fdb\u5883\u4e4b\u901f\uff0c\u5b9e\u6709\u2018\u58eb\u522b\u4e09\u65e5\uff0c\u4ee4\u4eba\u522e \u76ee\u76f8\u770b\u2019\u4e4b\u611f\u3002\u5c31\u51ed\u5c55\u5144\u4e00\u4eba\u4fbf\u53ef\u72ec\u6218\u2018\u4e09\u715e\u2019\u3001\u2018\u56db\u51f6\u2019\uff0c\u90a3\u4e48\u6211\u4eec\u8fd9\u822c\u4eba\uff0c\u5c31\u662f\u518d\u4e0d \u6d4e\uff0c\u62b5\u6321\u5176\u4e8c\u4e09\u6d41\u811a\u8272\uff0c\u603b\u4e0d\u6210\u95ee\u9898\u4e86\u5427\uff1f\u201d \u5c55\u767d\u4e00\u62f1\u624b\u9053\uff1a\u201c\u627f\u8499\u4e91\u94ee\u5144\u8c2c\u5956\uff0c\u5728\u4e0b\u5b9e\u4e0d\u6562\u5f53\u3002\u51b5\u4e14\uff0c\u5728\u4e0b\u4e0e\u4eba\u6709\u7ea6\uff0c\u4ece\u6b64\u4e0d\u518d\u8fc7 \u95ee\u6c5f\u6e56\u662f\u975e\uff0c\u6700\u597d\u662f\u4e0d\u8981\u628a\u5728\u4e0b\u7b97\u5728\u5176\u5185\uff01\u201d \u201c\u7965\u9e9f\u516c\u5b50\u201d\u4e5f\u8d70\u4e86\u8fc7\u6765\u9053\uff1a\u201c\u5c55\u5144\u5927\u53ef\u4e0d\u5fc5\u4e3a\u7a7a\u8a00\u7ea6\u675f\uff0c\u4f55\u51b5\u4e34\u9635\u8fd8\u53ef\u518d\u5411\u2018\u4e09\u715e\u2019 \u6311\u6218\uff0c\u4ee5\u96ea\u5f53\u65e5\u4e00\u638c\u4e4b\u8fb1\u2026\u2026\u201d \u5c55\u81ea\u9887\u4e3a\u4e0d\u60a6\u5730\u9053\uff1a\u201c\u91d1\u5144\u6b64\u8bdd\u4e0d\u77e5\u662f\u606d\u7ef4\u5728\u4e0b\uff1f\u8fd8\u662f\u6316\u82e6\u5728\u4e0b\uff1f\u5c55\u767d\u867d\u662f\u6b66\u5b66\u672a\u8fdb\uff0c \u4f46\u5927\u4e08\u592b\u4e00\u8a00\u65e2\u51fa\uff0c\u56db\u9a6c\u96be\u8ffd\uff0c\u5728\u4e0b\u4eb2\u53e3\u7b54\u5e94\u4eba\u5bb6\u7684\u8bdd\uff0c\u5c82\u80fd\u8bf4\u4e86\u4e0d\u7b97\u6570\uff1f\u201d \u89c1\u5c55\u767d\u7740\u607c\uff0c\u201c\u7965\u9e9f\u516c\u5b50\u201d\u8138\u5b54\u5fae\u5fae\u4e00\u7ea2\uff0c\u5fd9\u9053\uff1a\u201c\u8bf7\u5c55\u5144\u4e0d\u8981\u8bef\u4f1a\uff0c\u7965\u9e9f\u53ea\u4e3a\u5927\u5c40\u8bbe \u60f3\u800c\u5df2\uff01\u201d \u201c\u6b7b\u6d3b\u4eba\u201d\u7a81\u5730\u4e00\u62cd\u624b\uff0c\u9053\uff1a\u201c\u6709\u4e86\uff01\u201d \u4ed6\u8fd9\u4e00\u58f0\uff0c\u558a\u5f97\u58f0\u97f3\u5f88\u5927\uff0c\u597d\u50cf\u6709\u4ec0\u4e48\u91cd\u5927\u53d1\u73b0\u4f3c\u7684\uff0c\u4f17\u4eba\u4e00\u9f50\u8be7\u5f02\u5730\u8f6c\u8138\u671b\u7740\u4ed6\u3002\u53ea \u89c1\u4ed6\u7528\u624b\u5728\u8138\u4e0a\u4e00\u62b9\uff0c\u6458\u4e0b\u4e00\u4e2a\u4eba\u76ae\u9762\u7f69\u6765\uff0c\u4f17\u4eba\u518d\u4e00\u770b\uff0c\u53ea\u89c1\u4ed6\u9762\u81ea\u5fae\u987b\uff0c\u50cf\u8c8c\u6e05\u5947\uff0c\u54ea \u91cc\u518d\u662f\u90a3\u79cd\u6b7b\u76ee\u584c\u773c\u7684\u6b7b\u4eba\u50cf\uff1f \u4f17\u4eba\u9f50\u7686\u4e00\u6115\uff0c\u60f3\u4e0d\u5230\u201c\u592a\u81ea\u53cc\u9038\u201d\u662f\u6234\u4e86\u4eba\u76ae\u9762\u5177\uff0c\u5c24\u5176\u8339\u8001\u9556\u5934\u3001\u6155\u5bb9\u7ea2\u53ca\u5c55\u767d\u7b49 \u4eba\uff0c\u4e0e\u201c\u592a\u767d\u53cc\u9038\u201d\u76f8\u805a\u751a\u4e45\uff0c\u7adf\u4e5f\u6ca1\u6709\u770b\u51fa\u6765\uff01 \u201c\u6b7b\u6d3b\u4eba\u201d\u4e0d\u7ba1\u522b\u4eba\u77a7\u7740\u4ed6\u53d1\u6115\uff0c\u559c\u60a0\u60a0\u5730\u5411\u5c55\u767d\u9053\uff1a\u201c\u5c0f\u6069\u516c\u6234\u4e0a\u8fd9\u4eba\u76ae\u9762\u5177\uff0c\u518d\u4e0d \u4f1a\u6709\u4eba\u8ba4\u51fa\uff0c\u4fbf\u53ef\u5802\u5802\u7687\u7687\u5730\u53c2\u52a0\u4e5d\u4e5d\u91cd\u9633\u82f1\u96c4\u5927\u4f1a\uff0c\u5230\u65f6\u5019\u7ed9\u2018\u5357\u6d77\u95e8\u2019\u4e00\u4e2a\u5927\u5927\u7684\u6253 \u51fb\u2026\u2026\u201d \u5c55\u767d\u5374\u4e0d\u7528\u624b\u53bb\u63a5\u90a3\u4eba\u76ae\u9762\u5177\uff0c\u53ea\u6de1\u8c08\u5730\u9053\uff1a\u201c\u5c31\u7b97\u522b\u4eba\u8ba4\u4e0d\u51fa\u6765\uff0c\u5c55\u767d\u4e5f\u4e0d\u80fd\u8fdd\u5fc3\u505a \u4e8b\uff01\u201d \u4ed6\u8fd9\u8bdd\u65a9\u9489\u65ad\u94c1\uff0c\u6beb\u65e0\u8f6c\u5706\u4f59\u5730\uff0c\u4f17\u4eba\u4e0d\u7981\u5c3d\u7686\u9ed8\u7136\uff01 \u96f7\u5927\u53d4\u5728\u4e00\u65c1\u70b9\u5934\uff0c\u8d5e\u53f9\u9053\uff1a\u201c\u5f53\u771f\u662f\u8ddf\u4ed6\u6b7b\u53bb\u7684\u7236\u4eb2\uff0c\u4e00\u4e2a\u6837\u7684\u813e\u6c14\u2026\u2026\u201d \u6a0a\u7d20\u9e3e\u5728\u4e00\u65c1\u51b7\u51b7\u5730\u8bf4\u9053\uff1a\u201c\u4e0d\u77e5\u901a\u8fbe\u5e94\u53d8\uff0c\u5b9e\u662f\u8fc2\u8150\u5f97\u4e0d\u8fd1\u4eba\u60c5\uff01\u201d \u5c55\u767d\u5468\u8eab\u4e00\u98a4\uff01\u8fd9\u8bdd\u5bf9\u4ed6\u523a\u6fc0\u4e0d\u5c0f\uff0c\u201c\u8c79\u7a81\u5c71\u5e84\u201d\u6c34\u7262\u4e4b\u4e2d\u8ba2\u4ea4\uff0c\u4ed6\u5fc3\u4e2d\u628a\u6a0a\u7d20\u9e3e\u5f53\u4e00 \u4e2a\u77e5\u5df1\u670b\u53cb\u770b\u5f85\uff0c\u5982\u4eca\u6a0a\u7d20\u9e3e\u7ade\u4e5f\u8bf4\u4ed6\u505a\u5f97\u4e0d\u5bf9\uff0c\u4ed6\u53ea\u89c9\u81ea\u5df1\u51ed\u826f\u5fc3\u505a\u4e8b\uff0c\u8bda\u5b9e\u65e0\u6b3a\uff0c\u4e0d\u6b3a \u4eba\u4e5f\u4e0d\u6b3a\u5df1\uff0c\u8fd9\u8bef\u89e3\u4f7f\u4ed6\u5fc3\u4e2d\u53d1\u75db\uff0c\u4e0d\u7531\u98a4\u58f0\u9053\uff1a\u201c\u6a0a\u7d20\u5144\u2026\u2026\u6a0a\u59d1\u5a18\uff01\u5c55\u767d\u51ed\u5929\u826f\u505a\u4e8b\uff0c \u4e0d\u77e5\u6709\u4f55\u4e0d\u5bf9\uff1f\u201d \u6a0a\u7d20\u9e3e\u73b0\u4e0b\u4ecd\u662f\u7537\u88c5\uff0c\u5c55\u767d\u53e3\u6025\u4e0d\u77e5\u600e\u6837\u79f0\u547c\u5979\u624d\u597d\uff0c\u8bf4\u5b8c\u4e4b\u540e\uff0c\u4e00\u53cc\u5927\u773c\u775b\u76f4\u76f4\u77aa\u7740 \u6a0a\u7d20\u9e3e\u7684\u7c89\u9762\uff0c\u4f3c\u662f\u7b49\u7740\u5979\u7ed9\u4e00\u4e2a\u6ee1\u610f\u7684\u7b54\u590d\uff01 \u6a0a\u7d20\u9e3e\u7389\u9762\u6cdb\u7ea2\uff0c\u56e0\u5c55\u767d\u7684\u79f0\u547c\u4f7f\u5979\u5728\u4eba\u524d\u6709\u5c34\u5c2c\u7684\u611f\u89c9\u3002 \u4f46\u5979\u7d20\u4ee5\u7537\u513f\u4f5c\u98ce\uff0c\u51cf\u5c11\u5979\u5ff8\u6029\u4e4b\u6001\uff0c\u95fb\u8a00\u723d\u6717\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u73b0\u5728\u6570\u4f60\u6b66\u529f\u6700\u9ad8\uff0c\u4e5f\u552f \u6709\u4f60\u53ef\u62b5\u6321\u2018\u5357\u6d77\u95e8\u2019\u7684\u9ad8\u624b\uff0c\u633d\u6551\u4e2d\u539f\u6b66\u6797\u7684\u4e00\u5927\u6d69\u52ab\uff0c\u552f\u6709\u4f60\u65b9\u80fd\u80dc\u4efb\uff0c\u8fd9\u4e43\u662f\u7a7a\u524d\u4e49 \u4e3e\uff0c\u4f60\u6709\u529b\u4e3a\u4e4b\u5374\u4e0d\u4e3a\uff0c\u800c\u7518\u613f\u53d7\u7a7a\u8a00\u7ea6\u675f\uff0c\u5c82\u4e0d\u662f\u8fc2\u8150\u4e0d\u8fd1\u4eba\u60c5\uff1f\u201d \u8fd9\u8bdd\u4e49\u6b63\u8f9e\u4e25\uff0c\u5728\u573a\u4e4b\u4eba\u542c\u4e86\u65e0\u4e0d\u52a8\u5bb9\uff0c\u5c55\u767d\u4e5f\u4e0d\u7531\u7f9e\u6127\u5f97\u4f4e\u4e0b\u5934\u53bb\uff01 \u5c55\u767d\u5fc3\u4e2d\u5de6\u53f3\u4e3a\u96be\uff0c\u6b63\u5982\u6a0a\u7d20\u9e3e\u6240\u8bf4\uff0c\u633d\u6551\u6b66\u6797\u6d69\u52ab\uff0c\u4e43\u662f\u7a7a\u524d\u4e49\u4e3e\uff0c\u81ea\u5df2\u65e2\u81ea\u8ba4\u662f\u4fa0 \u4e49\u7684\u4eba\u7269\uff0c\u89c1\u4e49\u4fbf\u4e0d\u80fd\u4e0d\u4e3a\uff01\u53ef\u662f\uff0c\u5927\u4e08\u592b\u4e5f\u8bb2\u7a76\u4e00\u8bfa\u5343\u91d1\uff0c\u81ea\u5df1\u4eb2\u53e3\u7b54\u5e94\u4eba\u5bb6\u7684\u8bdd\uff0c\u5c82\u80fd \u53cd\u6094\uff1f \u7fa4\u96c4\u671b\u7740\u5c55\u767d\uff0c\u4e00\u8a00\u4e0d\u53d1\uff0c\u4e71\u54c4\u54c4\u7684\u9662\u4e2d\uff0c\u90a3\u4e48\u591a\u4eba\uff0c\u7ade\u4e00\u9f50\u505c\u6b62\u4e86\u8ba8\u8bba\uff0c\u767e\u5341\u9053\u773c\u5149 \u4e00\u9f50\u671b\u7740\u5c55\u767d\uff0c\u90fd\u671f\u671b\u7740\u4ed6\u7684\u51b3\u5b9a\uff0c\u201c\u662f\u201d\uff01\u8fd8\u662f\u201c\u4e0d\u201d\uff1f\u4eff\u4f5b\u6574\u4e2a\u4e2d\u539f\u6b66\u6797\u7684\u547d\u8fd0\uff0c\u5374\u7cfb \u5728\u5c55\u767d\u4e00\u4eba\u8eab\u4e0a\u4e86\uff01 \u5c55\u767d\u6c89\u601d\u6709\u9877\uff0c\u62ac\u8d77\u773c\u5149\uff0c\u89c1\u4f17\u4eba\u90fd\u5728\u671f\u671b\u7740\u4ed6\u7684\u56de\u7b54\uff0c\u4ed6\u731b\u7136\u4e86\u89e3\u81ea\u5df1\u4f7f\u547d\u7684\u91cd\u5927\uff0c \u5f53\u771f\u662f\u201c\u5929\u4e0b\u5b89\u5371\u7cfb\u4e8e\u4e00\u8eab\u201d\uff01\u4ed6\u7075\u673a\u4e00\u52a8\uff0c\u9053\uff1a\u201c\u5c55\u767d\u4e00\u4e2a\u6b66\u5b66\u672a\u8fdb\uff0c\u627f\u8499\u8bf8\u4f4d\u6b66\u6797\u524d\u8f88 \u5982\u6b64\u770b\u91cd\uff0c\u5b9e\u5728\u4f7f\u5c55\u767d\u611f\u6fc0\u83ab\u540d\uff0c\u4f55\u51b5\u5927\u4e49\u5f53\u524d\uff0c\u5c31\u662f\u4f7f\u5c55\u767d\u8d74\u6c64\u8e48\u706b\uff0c\u5c55\u767d\u4e5f\u4e07\u6b7b\u4e0d \u8f9e\uff01\u201d \u4ed6\u8bf4\u81f3\u6b64\u987f\u4e86\u4e00\u987f\uff0c\u89c1\u7fa4\u7ef4\u4e00\u773c\u4e0d\u7728\u5730\u671b\u7740\u4ed6\uff0c\u5e7f\u5927\u7684\u5ead\u9662\u4e4b\u4e2d\uff0c\u5f53\u771f\u662f\u7ee3\u9488\u843d\u5730\u53ef \u95fb\uff0c\u4ed6\u53c8\u7ee7\u7eed\u8bf4\u4e0b\u53bb\uff0c\u9053\uff1a\u201c\u53ef\u662f\uff0c\u5c55\u767d\u4eb2\u53e3\u7b54\u5e94\u4eba\u5bb6\uff0c\u4e0d\u95ee\u6c5f\u6e56\u662f\u975e\uff0c\u60f3\u4f17\u4f4d\u4e5f\u5747\u662f\u4fa0\u4e49 \u9053\u524d\u8f88\uff0c\u5fc5\u5b9a\u77e5\u9053\u7acb\u8eab\u6c5f\u6e56\uff0c\u5f53\u4ee5\u4fe1\u4e49\u4e3a\u91cd\uff01\u5728\u6b64\u79cd\u60c5\u5f62\u4e4b\u4e0b\uff0c\u5c55\u767d\u88f9\u8db3\u4e0d\u524d\uff0c\u6b63\u5982\u6a0a \u5144\u2026\u2026\u6a0a\u59d1\u5a18\u6240\u8d23\u5907\u5728\u4e0b\u7684\uff0c\u4fbf\u662f\u4e0d\u4e49\uff1b\u4f46\u5c55\u767d\u5982\u679c\u8fdd\u80cc\u7ea6\u8a00\uff0c\u53bb\u53c2\u52a0\u82f1\u96c4\u5927\u4f1a\uff0c\u4fbf\u662f\u80cc \u4fe1\uff01\u4e0d\u4e49\u548c\u80cc\u4fe1\uff0c\u5747\u662f\u5c55\u767d\u6240\u4e0d\u53d6\uff0c\u8fd9\u4e00\u70b9\u60f3\u8bf8\u4f4d\u5148\u8fdb\u5fc5\u5b9a\u53ef\u4ee5\u8c05\u89e3\uff01\u201d \u5c55\u767d\u8bf4\u81f3\u6b64\u5904\uff0c\u7fa4\u96c4\u8138\u4e0a\u4e00\u7247\u8ff7\u60af\u4e4b\u8272\uff0c\u4e0d\u77e5\u5c55\u767d\u8bdd\u4e2d\u771f\u610f\u7a76\u7adf\u4e3a\u4f55\uff1f\u4f46\u4e5f\u5c31\u66f4\u6ce8\u610f\u5730 \u542c\u4e0b\u53bb\u3002 \u5c55\u767d\u7ee7\u7eed\u6ed4\u6ed4\u5730\u8bf4\u4e0b\u53bb\uff0c\u9053\uff1a\u201c\u5728\u4e0b\u60f3\u51fa\u4e00\u4e2a\u4e24\u5168\u5176\u7f8e\u7684\u529e\u6cd5\uff0c\u4f46\u9700\u5f81\u6c42\u96f7\u5927\u53d4\u7684\u540c \u610f\uff01\u201d \u4f17\u4eba\u7684\u773c\u5149\uff0c\u4e0d\u7ea6\u800c\u540c\u5730\u4e00\u9f50\u8f6c\u5411\u96f7\u5927\u53d4\uff01 \u96f7\u5927\u53d4\u6b64\u65f6\u611f\u52a8\u5f97\u70ed\u6cea\u76c8\u7736\uff0c\u4ed6\u4ece\u5c55\u767d\u7684\u5b88\u6b63\u4e0d\u963f\uff0c\u53ca\u201c\u53ea\u89c1\u4e00\u4e49\uff0c\u4e0d\u89c1\u751f\u6b7b\u201d\u7684\u80f8 \u895f\uff0c\u4eff\u4f5b\u53c8\u770b\u5230\u5c55\u767d\u7236\u4eb2\u2014\u2014\u4ed6\u7ed3\u4e49\u76df\u5144\u201c\u9739\u96f3\u5251\u201d\u5c55\u4e91\u5929\u7684\u5f71\u5b50\uff01 \u89c1\u5c55\u767d\u8981\u8bf7\u6c42\u4ed6\u7684\u540c\u610f\uff0c\u4ed6\u6beb\u672a\u8003\u8651\u662f\u4ec0\u4e48\u4e8b\u60c5\uff0c\u8ba9\u70ed\u6cea\u5728\u773c\u7736\u91cc\u6253\u8f6c\uff0c\u4ed6\u4e5f\u672a\u64e6\uff0c\u5e94 \u58f0\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4f60\u8bf4\u4e0b\u53bb\uff01\u201d \u5c55\u767d\u89c1\u96f7\u5927\u53d4\u5609\u8bb8\u7684\u773c\u5149\uff0c\u7ed9\u4e86\u4ed6\u81ea\u4fe1\u548c\u52c7\u6c14\uff0c\u4ed6\u89c9\u5f97\u81ea\u5df1\u7684\u51b3\u5b9a\uff0c\u662f\u6ca1\u6709\u9519\u7684\uff0c\u4e8e\u662f \u63a5\u4e0b\u53bb\u9053\uff1a\u201c\u6b63\u5982\u2018\u5b89\u4e50\u516c\u5b50\u2019\u4e91\u94ee\u5144\u6240\u8bf4\uff0c\u5728\u4e0b\u6b66\u529f\u8fdb\u5883\u795e\u901f\uff0c\u4f46\u8bf8\u4f4d\u77e5\u9053\u4e3a\u4ec0\u4e48\u5728\u4e0b\u6b66 \u529f\u4f1a\u8fdb\u5883\u795e\u901f\u5417\uff1f\u201d \u7fa4\u96c4\u773c\u91cc\u53c8\u6d41\u9732\u51fa\u7591\u95ee\u4e4b\u8272\uff0c\u8c01\u77e5\u9053\u5c55\u767d\u4e3a\u4ec0\u4e48\u4f1a\u6b66\u529f\u8fdb\u5883\u795e\u901f\uff1f \u5c55\u767d\u5374\u63a5\u4e0b\u53bb\u9053\uff1a\u201c\u90a3\u662f\u56e0\u4e3a\u96f7\u5927\u53d4\u8d60\u9001\u4e86\u5728\u4e0b\u4e00\u672c\u5929\u4e0b\u7b2c\u4e00\u5947\u4e66\uff0c\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b \u5377\u300b\u6240\u81f4\uff01\u201d \u5c55\u767d\u6b64\u8a00\u4e00\u51fa\uff0c\u7fa4\u96c4\u54d7\u7136\uff01\u5747\u4e0d\u7981\u6b3a\u8eab\u4e0a\u524d\uff0c\u7eb7\u7eb7\u8a00\u9053\uff1a\u201c\u6b64\u4e00\u5947\u4e66\uff0c\u73b0\u5728\u4f55\u5904\uff0c\u80fd\u5426 \u53d6\u51fa\u4e00\u89c2\uff0c\u7ed9\u5927\u5bb6\u5f00\u5f00\u773c\u754c\uff1f\u201d \u201c\u62ff\u6765\u7ed9\u5927\u5bb6\u770b\u770b\uff01\u201d \u201c\u4e66\u5728\u54ea\u91cc\uff1f\u201d \u201c\u62ff\u51fa\u6765\u2026\u2026\u201d \u7eb7\u7eb7\u53eb\u56b7\uff0c\u4e71\u6210\u4e00\u7247\uff01 \u96f7\u5927\u53d4\u4e5f\u4e0d\u7531\u9707\u60ca\u5f97\u77aa\u5927\u4e86\u773c\u775b\uff0c\u4ed6\u8fd8\u4e0d\u660e\u767d\u5728\u8fd9\u79cd\u573a\u5408\uff0c\u5c55\u767d\u4e3a\u4ec0\u4e48\u628a\u8fd9\u4e00\u5927\u79d8\u5bc6\u900f \u9732\u51fa\u6765\uff1f \u5c55\u767d\u6b64\u4e3e\uff0c\u5b9e\u5728\u662f\u4e0d\u667a\u4e4b\u6781\uff01 \u679c\u7136\u201c\u5d06\u5cd2\u56db\u4e11\u201d\u5df2\u9996\u5148\u53d1\u96be\uff0c\u5927\u4e11\u201c\u5929\u6b8b\u201d\uff0c\u72ec\u76ee\u51f6\u5149\u66b4\u9732\uff0c\u4e00\u63a2\u9e1f\u722a\u7684\u53cc\u638c\uff0c\u817e\u8eab \u5411\u5c55\u767d\u6000\u4e2d\u6293\u6765\uff01 \u4e00\u8fb9\u5634\u4e2d\u559d\u9053\uff1a\u201c\u4e0d\u8981\u8fd9\u822c\u541e\u541e\u5410\u5410\uff0c\u62ff\u51fa\u6765\u77a7\u77a7\u5427\uff01\u201d \u5c55\u767d\u4e5f\u4f30\u4e0d\u5230\uff0c\u8fd9\u822c\u81ea\u8be9\u4e3a\u4fa0\u4e49\u9053\u7684\u4eba\u7269\uff0c\u8d2a\u5fc3\u7adf\u662f\u5982\u6b64\u4e4b\u5927\uff01\u4e00\u8bed\u8bf4\u51fa\uff0c\u7acb\u523b\u7fa4\u76f8\u62a2 \u593a\uff01 \u5c31\u5728\u4ed6\u5fae\u4e00\u6115\u795e\u4e4b\u9645\uff0c\u5927\u4e11\u201c\u5929\u6b8b\u201d\u7684\u53cc\u722a\uff0c\u5df2\u63a5\u7740\u52b2\u98ce\uff0c\u6293\u4f50\u4ed6\u80f8\u524d\u4e0d\u53ca\u4e00\u5c3a\u4e4b\u5904\uff01 \u5c55\u767d\u5fae\u6e29\uff0c\u53cc\u6307\u4e00\u6572\uff0c\u4e00\u5f0f\u201c\u5927\u64d2\u62ff\u624b\u201d\u7684\u201c\u6a2a\u96ea\u65ad\u5cf0\u201d\uff0c\u731b\u5411\u201c\u5927\u4e11\u5929\u6b8b\u201d\u53cc\u722a\u70b9 \u53bb\uff01 \u5927\u4e11\u2018\u2018\u5929\u6b8b\u201d\u5982\u89e6\u86c7\u874e\uff0c\u4f38\u51fa\u7684\u53cc\u722a\u75be\u7f29\uff0c\u4eba\u4e5f\u8ddf\u7740\u66b4\u9000\u4e00\u4e08\u5f00\u5916\uff01 \u9976\u4ed6\u9000\u5f97\u5feb\uff0c\u53cc\u81c2\u201c\u66f2\u5c3a\u201d\u7a74\u4e0a\uff0c\u4e5f\u88ab\u5c55\u767d\u6307\u98ce\u626b\u4e2d\uff0c\u53ea\u75db\u5f97\u4ed6\u66b4\u7741\u72ec\u76ee\uff0c\u7529\u7740\u53cc\u624b\u8fde \u8fde\u547c\u75db\u4e0d\u6b62\u2026\u2026 \u5c55\u767d\u4e00\u62db\u903c\u9000\u5927\u4e11\u201c\u5929\u6b8b\u201d\uff0c\u6602\u7136\u9053\uff1a\u201c\u7eea\u4f4d\u8fd8\u662f\u8001\u8001\u5b9e\u5b9e\u5730\u7ad9\u7740\uff0c\u542c\u5728\u4e0b\u8bf4\u4e0b\u53bb \u5427\uff01\u201d \u4f17\u4eba\u8fd9\u65f6\u624d\u60f3\u5230\u5c55\u767d\u53ef\u4ee5\u72ec\u6218\u201c\u56db\u51f6\u201d\uff0c\u5982\u679c\u771f\u8981\u51fa\u624b\u786c\u62a2\uff0c\u53ef\u4ee5\u8bf4\u65e0\u4eba\u4f1a\u5f97\u4e86\u597d\u53bb\uff01 \u5c55\u767d\u89c1\u4f17\u4eba\u4e00\u65f6\u4e0d\u6562\u4e0a\u524d\uff0c\u624d\u53c8\u63a5\u4e0b\u9053\uff1a\u201c\u5c55\u767d\u4e3a\u4e86\u5b88\u4fe1\uff0c\u4e0d\u80fd\u53c2\u52a0\u82f1\u96c4\u5927\u4f1a\u3002\u4f46\u662f\uff0c \u773c\u770b\u2018\u5357\u6d77\u95e8\u2019\u8086\u8650\uff0c\u4e2d\u539f\u6b66\u6797\u9762\u4e34\u6d69\u52ab\uff0c\u53c8\u4e0d\u80fd\u4e0d\u5c3d\u4e00\u5df1\u4e4b\u529b\uff0c\u8bbe\u6cd5\u6d88\u5f25\uff0c\u56e0\u6b64\u4e4b\u4e0a\uff0c\u5c55 \u767d\u60f3\u628a\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\u4e0a\u7684\u5947\u7edd\u6b66\u5b66\u4f20\u7ed9\u7eea\u4f4d\uff0c\u4f7f\u8bf8\u4f4d\u6709\u80fd\u529b\u5bf9\u6297\u2018\u5357\u6d77\u95e8\u2019\uff0c\u8fd9\u6837\uff0c \u4e5f\u4e0d\u9700\u8981\u5c55\u767d\u4eb2\u81ea\u51fa\u9762\u4e86\uff01\u201d \u5c55\u767d\u6b64\u8a00\u4e00\u51fa\uff0c\u7fa4\u96c4\u6115\u4e86\uff01 \u5929\u4e0b\u54ea\u6709\u8fd9\u6837\u6beb\u4e0d\u81ea\u79c1\u4e4b\u4eba\uff0c\u7ade\u80af\u628a\u522b\u4eba\u73cd\u89c6\u79d8\u85cf\u90fd\u6765\u4e0d\u53ca\u7684\u6b66\u6797\u79d8\u63a5\u516c\u8bf8\u6b66\u6797\uff1f \u5c55\u767d\u53c8\u9053\uff1a\u201c\u53ef\u662f\uff0c\u8fd9\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\u4e43\u662f\u96f7\u5927\u53d4\u6240\u8d60\uff0c\u5728\u672a\u516c\u8bf8\u6b66\u6797\u4e4b\u524d\uff0c\u5148\u8981 \u5f81\u6c42\u96f7\u5927\u53d4\u7684\u540c\u610f\uff01\u201d \u5c55\u767d\u8bf4\u81f3\u6b64\u5904\uff0c\u4e00\u56de\u8eab\u5411\u96f7\u5927\u53d4\u6df1\u65bd\u4e00\u672d\uff0c\u9053\uff1a\u201c\u5927\u53d4\uff01\u60a8\u5bf9\u5c0f\u4f84\u7684\u9020\u5c31\u4e4b\u6069\uff0c\u53ef\u8bf4\u662f \u5929\u9ad8\u5730\u539a\uff0c\u4e3a\u4e86\u633d\u6551\u6b66\u6797\u6d69\u52ab\uff0c\u60a8\u4e0d\u4f1a\u602a\u7f6a\u5c0f\u4f84\u9c81\u83bd\u65e0\u77e5\u5427\uff1f\u201d \u96f7\u5927\u53d4\u8138\u4e0a\u8001\u6cea\u7eb5\u6a2a\uff0c\u4f38\u624b\u6400\u8d77\u4e86\u5c55\u767d\uff0c\u9053\uff1a\u201c\u8d77\u6765\uff01\u8d24\u4f84\uff0c\u4f60\u4f7f\u5927\u53d4\u611f\u5230\u9a84\u50b2\uff0c\u8001\u592b \u7ec8\u751f\u672a\u5a36\uff0c\u80fd\u6709\u4f60\u8fd9\u4e48\u4e00\u4f4d\u8d24\u4e16\u4f84\uff0c\u53ef\u4ee5\u8bf4\u662f\u767e\u5e74\u4e4b\u540e\uff0c\u6b7b\u800c\u65e0\u61be\u3002\u800c\u4e14\uff0c\u8001\u592b\u4e5f\u4e3a\u6211\u90a3\u6b7b \u53bb\u7684\u7ed3\u4e49\u76df\u5144\u3001\u4f60\u7684\u7236\u4eb2\u611f\u5230\u9a84\u50b2\uff0c\u867d\u7136\u4ed6\u884c\u4fa0\u4ed7\u4e49\u4e00\u751f\uff0c\u6b7b\u5f97\u4e0d\u660e\u4e0d\u767d\uff0c\u4f46\u5728\u4e5d\u6cc9\u4e4b\u4e0b\uff0c \u89c1\u5230\u8d24\u4e16\u4f84\u4f60\u514b\u5c3d\u4ed6\u672a\u7adf\u7684\u5fd7\u613f\uff0c\u4ed6\u5c31\u662f\u6b7b\u5728\u4e5d\u6cc9\u4e4b\u4e0b\uff0c\u4e5f\u8be5\u611f\u5230\u83ab\u5927\u7684\u5b89\u6170\u4e86\uff01\u2026\u2026\u201d \u96f7\u5927\u53d9\u8bf4\u81f3\u6b64\u5904\uff0c\u987f\u4e86\u4e00\u987f\uff0c\u89c1\u5c55\u767d\u773c\u4e2d\u6cea\u6c34\u4e5f\u5728\u6253\u8f6c\uff0c\u89c9\u5f97\u6b64\u65f6\u4e0d\u5b9c\u4f24\u611f\uff0c\u5fd9\u628a\u8bdd\u9898 \u7eb3\u5165\u6b63\u8f68\uff0c\u9053\uff1a\u201c\u65b9\u624d\u8d24\u4f84\u6240\u4e3a\uff0c\u6b63\u5408\u8001\u592b\u5fc3\u610f\uff01\u53ea\u662f\u90a3\u672c\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\uff0c\u8d24\u4f84\u5343\u4e07 \u4e0d\u80fd\u516c\u5f00\uff0c\u4ee5\u514d\u5f15\u8d77\u65e0\u8c13\u7684\u4e89\u7aef\uff0c\u800c\u4e14\uff0c\u90a3\u4e1c\u897f\u4e5f\u5b9e\u5728\u5bb3\u4eba\uff0c\u4e0d\u662f\u5b9a\u529b\u751a\u9ad8\u4e4b\u571f\uff0c\u4e07\u4e07\u4e0d\u80fd \u4eba\u76ee\u3002\u73b0\u5728\u4f60\u53ea\u8981\u628a\u4e0a\u8fb9\u7684\u6b66\u529f\uff0c\u6458\u8981\u4f20\u7ed9\u5927\u5bb6\u4e5f\u5c31\u662f\u4e86\uff01\u201d \u96f7\u5927\u53d4\u6b64\u8a00\u4e00\u51fa\uff0c\u5bf9\u96f7\u5927\u53d4\u7684\u5f00\u660e\uff0c\u4f17\u4eba\u65e0\u4e0d\u611f\u6fc0\uff0c\u4f46\u4e5f\u6709\u4e9b\u5fae\u7684\u5931\u671b\uff0c\u56e0\u4e3a\u96f7\u5927\u53d4\u8fd9 \u6837\u4e00\u8bf4\uff0c\u8fd9\u76f8\u4f20\u4e8c\u767e\u4f59\u5e74\u7684\u5929\u4e0b\u7b2c\u4e00\u5947\u4e66\uff0c\u662f\u770b\u4e0d\u5230\u4e86\u3002 \u5c55\u767d\u53c8\u5411\u96f7\u5927\u53d4\u65bd\u4e86\u4e00\u793c\uff0c\u7136\u540e\u6574\u5bb9\u5411\u7fa4\u96c4\u9053\uff1a\u201c\u5c31\u9075\u7167\u96f7\u5927\u53d4\u6240\u793a\uff0c\u53ea\u8981\u4f17\u4f4d\u9886\u63a5\u5f97 \u4e86\u7684\uff0c\u5c55\u767d\u7edd\u4e0d\u85cf\u79c1\uff0c\u53ea\u662f\u539f\u4e66\u4e0d\u80fd\u516c\u5f00\uff0c\u4e8b\u5b9e\u4e0a\u90a3\u672c\u79d8\u5f55\u5b9e\u5728\u4e5f\u8bf1\u60d1\u592a\u5927\uff0c\u5c31\u7b97\u5b9a\u529b\u751a\u9ad8 \u4e4b\u4eba\uff0c\u770b\u4e86\u4e5f\u65e0\u6cd5\u81ea\u6301\uff01\u201d \u96f7\u5927\u53d4\u4ee5\u8863\u8896\u62c2\u53bb\u8138\u4e0a\u611f\u52a8\u7684\u70ed\u6cea\uff0c\u9053\uff1a\u201c\u73b0\u5728\u8ddd\u79bb\u4e5d\u4e5d\u91cd\u9633\u82f1\u96c4\u5927\u4f1a\uff0c\u8fd8\u6709\u4e09\u4e2a\u591a\u6708 \u7684\u65f6\u95f4\uff0c\u8bf8\u4f4d\u5982\u679c\u76f8\u4fe1\u5f97\u8fc7\uff0c\u5c31\u8bf7\u5b89\u6392\u5730\u70b9\uff0c\u8ba9\u5c55\u8d24\u4f84\u4f20\u7ed9\u5927\u5bb6\u2018\u5929\u4f5b\u7edd\u5b66\u2019\u5427\uff01\u201d \u4f17\u4eba\u4e00\u7247\u6b22\u547c\uff0c\u5c11\u5fc3\u673a\u7684\u4eba\uff0c\u8ba4\u4e3a\u8fd9\u662f\u767e\u5e74\u96be\u9047\u7684\u5947\u9047\uff0c\u96c0\u8dc3\u4e0d\u5df2\uff01 \u5c55\u767d\u4e00\u7247\u4e3a\u516c\u4f17\u7684\u597d\u5fc3\uff0c\u5374\u4e0d\u77e5\u5728\u6697\u5730\u91cc\u5df2\u8574\u85cf\u4e86\u4e00\u6b21\u5de8\u5927\u7684\u98ce\u66b4\uff01 \u5f53\u5373\u96f7\u5927\u53d4\u63d0\u51fa\u5b89\u6392\u5c55\u767d\u4f20\u827a\u4e4b\u4e8b\uff0c\u201c\u7aef\u65b9\u516c\u5b50\u201d\u4e0e\u201c\u5b89\u4e50\u516c\u4e8e\u201d\u4ee5\u5730\u4e3b\u7684\u8eab\u4efd\u51fa\u9762\u4e3a \u4f17\u4eba\u5b89\u6392\u4e00\u5207\u3002 \u5305\u62ec\u573a\u5730\u53ca\u4f17\u4eba\u8d77\u5c45\u5b89\u606f\u4e4b\u5904\uff0c\u5728\u201c\u7a77\u5bb6\u5e2e\u201d\u7684\u5f1f\u5b50\u5949\u547d\u529e\u7406\u8fd9\u4e9b\u4e8b\u52a1\u3001\u5fd9\u5f97\u4e71\u54c4\u54c4\u7684 \u65f6\u5019\uff0c\u4e03\u5927\u95e8\u6d3e\u7684\u4eba\uff0c\u7eb7\u7eb7\u544a\u8f9e\u8d70\u201c\u864e\u76ee\u5c0a\u8005\u201d\u53ca\u201c\u795e\u62f3\u6253\u4e95\u201d\u6b66\u9707\u98de\u8981\u8d76\u56de\u5c11\u6797\u5bfa\u4ee5\u89c2\u7a76 \u7adf\uff0c\u5176\u4ed6\u5404\u95e8\u5404\u6d3e\uff0c\u8bf4\u8981\u56de\u5c71\u518d\u53bb\u7ea6\u8bf7\u95e8\u4e0b\u9ad8\u624b\uff0c\u5171\u8c0b\u4e49\u4e3e\uff0c\u4e94\u65e5\uff0c\u591a\u8005\u5341\u6570\u5929\u5f53\u53ef\u8d76 \u56de\u2026\uff0e\u201d \u7b2c\u4e94\u5341\u516b\u7ae0\u3000\u6c5f\u6e56\u6ed4\u5929\u6d6a>> \u53e4\u9f99\u300a\u5251\u5ba2\u884c\u300b \u7b2c\u4e94\u5341\u516b\u7ae0\u3000\u6c5f\u6e56\u6ed4\u5929\u6d6a \u4e0d\u51fa\u6570\u5929\uff0c\u6c5f\u6e56\u4e0a\u5df2\u6380\u8d77\u6ed4\u5929\u98ce\u6ce2\uff01 \u6574\u4e2a\u4e2d\u539f\u6b66\u6797\u6cb8\u6cb8\u626c\u626c\uff0c\u540c\u65f6\u8f70\u4f20\u7740\u4e24\u6869\u5927\u4e8b\uff0c\u4f7f\u6574\u4e2a\u4e2d\u539f\u6b66\u6797\u5982\u540c\u4e00\u9505\u716e\u6cb8\u7684\u6eda\u6c34\uff0c \u6270\u6518\u7ec8\u65e0\u5b81\u65e5\uff01 \u5934\u4e00\u6869\u5927\u4e8b\uff0c\u662f\u201c\u5357\u6d77\u95e8\u201d\u7ea6\u4f1a\u5929\u4e0b\u6b66\u6797\uff0c\u4e5d\u4e5d\u91cd\u9633\uff0c\u5728\u5d69\u5c71\u5c11\u6797\u5bfa\u53ec\u5f00\u7684\u82f1\u96c4\u5927\u4f1a\uff01 \u8fd9\u573a\u82f1\u96c4\u5927\u4f1a\uff0c\u4e0d\u4f46\u67ec\u7ea6\u4e2d\u539f\u6b66\u6797\u4e5d\u5927\u95e8\u6d3e\uff0c\u6b66\u6797\u56db\u5927\u516c\u5b50\uff0c\u51e1\u662f\u5728\u6c5f\u6e56\u4e0a\u6709\u5934\u6709\u8138\u7684 \u4eba\u7269\u90fd\u7ea6\u8bf7\u5230\u4e86\uff0c\u800c\u4e14\u8fde\u56db\u6d77\u516b\u8352\u3001\u8fb9\u7586\u6f20\u8352\u5916\u6240\u6709\u7684\u6b66\u6797\u4eba\u7269\u90fd\u63a5\u5230\u4e86\u8bf7\u5e16\uff01 \u8ddf\u89c1\u8fd9\u4e5d\u4e5d\u91cd\u9633\u5d69\u5c71\u5c11\u6797\u5bfa\u7684\u4e00\u573a\u82f1\u96c4\u5927\u4f1a\uff0c\u5c06\u53ef\u51b3\u5b9a\u4e2d\u539f\u6b66\u6797\u672a\u6765\u7684\u547d\u8fd0\uff01 \u4f46\u6709\u4e00\u4ef6\u66f4\u4e3a\u8f70\u52a8\u7684\u5927\u4e8b\uff0c\u53cd\u800c\u628a\u80fd\u591f\u51b3\u5b9a\u4e2d\u539f\u6b66\u6797\u547d\u8fd0\u8fd9\u4e48\u91cd\u5927\u7684\u4e8b\u6545\u51b2\u6de1\u4e86\uff01 \u539f\u6765\u5c55\u767d\u4e00\u7247\u597d\u5fc3\uff0c\u900f\u9732\u51fa\u4e8c\u767e\u4f59\u5e74\u4ee5\u524d\u5929\u4e0b\u7b2c\u4e00\u5947\u4eba\u201c\u53ea\u773c\u90ce\u541b\u201d\u6240\u624b\u8457\u7684\u5929\u4e0b\u7b2c\u4e00 \u5947\u4e66\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\u7684\u6d88\u606f\uff0c\u6b64\u65f6\u5df2\u4f20\u904d\u4e86\u6574\u4e2a\u6b66\u6797\uff01 \u5c55\u767d\uff0c\u8fd9\u5fe0\u539a\u6734\u5b9e\u7684\u9752\u5e74\uff0c\u4e07\u4e07\u4e5f\u60f3\u4e0d\u5230\uff0c\u4ed6\u7684\u4e00\u7247\u4fa0\u4e49\u5fc3\u80a0\uff0c\u7adf\u62db\u6765\u8fd9\u4e48\u5927\u7684\u7978\u5bb3\uff01 \u4e8b\u5b9e\u4e0a\u5f53\u5929\u591c\u91cc\uff0c\u4fbf\u53d1\u751f\u4e86\u4e8b\u7aef\uff01 \u90a3\u5929\u591c\u91cc\u5c55\u767d\u72ec\u6218\u56db\u51f6\uff0c\u5f85\u201c\u9f99\u795e\u592a\u5b50\u201d\u53ca\u201c\u5357\u6d77\u9f99\u5973\u201d\u7387\u9886\u201c\u6d77\u5916\u4e09\u715e\u201d\uff0c\u5e26\u56de\u56db\u51f6 \u5e76\u7ea6\u4f1a\u7fa4\u96c4\u53c2\u52a0\u4e5d\u4e5d\u91cd\u9633\u82f1\u96c4\u5927\u4f1a\u8d70\u4e86\u4e4b\u540e\uff0c\u5929\u8272\u5df2\u7ecf\u5927\u4eae\uff01 \u4e3a\u4e86\u6d88\u5f25\u6b66\u6797\u6d69\u52ab\uff0c\u5c55\u767d\u4e00\u7247\u597d\u5fc3\uff0c\u900f\u9732\u51fa\u5929\u4e0b\u7b2c\u4e00\u5947\u4e66\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\u7684\u6d88\u606f\uff0c \u5e76\u613f\u516c\u7b4b\u6b66\u6797\u540c\u597d\uff0c\u201c\u7a77\u5bb6\u5e2e\u201d\u5e2e\u4f17\uff0c\u7acb\u523b\u51c6\u5907\u4e00\u5207\u4e8b\u5b9c\uff01 \u76f4\u5fd9\u4e86\u4e00\u5929\uff0c\u5927\u5bb6\u5747\u662f\u4e00\u5929\u672a\u7761\uff0c\u5165\u591c\uff0c\u5403\u8fc7\u996d\u540e\uff0c\u7fa4\u96c4\u90fd\u6309\u7167\u201c\u7a77\u5bb6\u5e2e\u201d\u6240\u5206\u914d\u7684\u623f \u5c4b\uff0c\u5404\u81ea\u5c31\u5bdd\uff0c\u60f3\u65e9\u4e00\u70b9\u4f11\u606f\uff0c\u7b2c\u4e8c\u5929\u6e05\u65e9\u4fbf\u5f00\u59cb\u7531\u5c55\u767d\u4f20\u7ed9\u5927\u5bb6\u201c\u5929\u4f5b\u7edd\u5b66\u201d\u4e86\u3002 \u6309\u5206\u914d\u5c55\u767d\u4e0e\u96f7\u5927\u53d4\u540c\u4f4f\u4e00\u95f4\u623f\uff0c\u201c\u592a\u767d\u53cc\u9038\u201d\u4e0e\u8339\u8001\u9556\u5934\u4f4f\u4e00\u95f4\uff0c\u6155\u5bb9\u7ea2\u4e0e\u5a49\u513f\u4f4f\u4e00 \u95f4\uff0c\u8fd9\u4e09\u95f4\u623f\u662f\u8fde\u5728\u4e00\u8d77\u7684\u4e00\u5ea7\u53a2\u623f\u3002 \u5c55\u767d\u4e0e\u96f7\u5927\u53d4\u624d\u522b\u8fc7\u4f17\u4eba\u56de\u81f3\u623f\u4e2d\uff0c\u4e00\u76cf\u70ed\u8336\u5c1a\u672a\u5403\u5b8c\uff0c\u7a81\u542c\u95e8\u4e0a\u6709\u4eba\u6263\u95e8\u4e4b\u58f0\u3002 \u623f\u95e8\u5e76\u672a\u5173\uff0c\u96f7\u5927\u53d4\u8bf4\u4e86\u58f0\uff1a\u201c\u8fdb\u6765\uff01\u201d \u968f\u58f0\u8fdb\u6765\u7684\u662f\u6155\u5bb9\u7ea2\uff0c\u6155\u5bb9\u7ea2\u7a7f\u4e86\u4e00\u4ef6\u9ed1\u7f0e\u957f\u888d\uff0c\u8170\u675f\u7ef8\u5dfe\uff0c\u79c0\u53d1\u62ab\u80a9\uff0c\u96ea\u767d\u7684\u76ae\u80a4\u88ab \u9ed1\u8863\u4e00\u886c\uff0c\u66f4\u663e\u5f97\u6b3a\u96ea\u8d5b\u971c\uff0c\u4f3c\u662f\u521a\u521a\u51b0\u6d74\u8fc7\uff0c\u867d\u7136\u8102\u7c89\u672b\u65bd\uff0c\u4f46\u81ea\u5982\u68a8\u82b1\u7684\u5bb9\u8c8c\u5728\u706f\u5149\u7167 \u5c04\u4e0b\uff0c\u500d\u89c9\u6e05\u65b0\u5b9c\u4eba\u3002 \u6155\u5bb9\u7ea2\u8fdb\u5f97\u623f\u6765\uff0c\u5148\u77a5\u4e86\u5c55\u767d\u4e00\u773c\uff0c\u90a3\u773c\u5149\u4e2d\u542b\u7740\u65e0\u9650\u6df1\u60c5\uff0c\u7136\u540e\u5411\u96f7\u5927\u53d4\u655b\u887d\u4e00\u793c\u3002 \u96f7\u5927\u53d4\u4ee5\u4e3a\u4ed6\u4eec\u5c0f\u592b\u59bb\u6709\u4ec0\u4e48\u79c1\u8bdd\u8981\u8bf4\uff0c\u4ed6\u4e00\u4e2a\u957f\u8f88\uff0c\u4e0d\u597d\u610f\u601d\u5728\u5ea7\uff0c\u5e72\u54b3\u4e86\u4e00\u58f0\uff0c \u9053\uff1a\u201c\u4f60\u4eec\u5728\u8fd9\u91cc\u5750\u4e00\u4f1a\u513f\uff0c\u6211\u51fa\u53bb\u8d70\u8d70\u3002\u201d \u5c55\u767d\u6d51\u539a\uff0c\u61f5\u7136\u4e0d\u89c9\uff0c\u4f46\u6155\u5bb9\u7ea2\u806a\u6167\u8fc7\u4eba\uff0c\u96f7\u5927\u53d4\u4e00\u501f\u53e3\u79bb\u5f00\uff0c\u5fc3\u4e2d\u65e9\u77e5\u5176\u610f\uff0c\u79c0\u8138\u4e0d \u7981\u4e00\u7ea2\uff0c\u5fd9\u9053\uff1a\u201c\u5927\u53d4\uff0c\u60a8\u4e0d\u8981\u51fa\u53bb\uff0c\u4f84\u5973\u6709\u8bdd\u6b63\u60f3\u8ddf\u5927\u53d4\u5546\u91cf\uff01\u201d \u96f7\u5927\u53d4\u53c8\u505c\u4e86\u811a\u6b65\uff0c\u5634\u4e2d\u5475\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u6155\u5bb9\u7ea2\u9053\uff1a\u201c\u4ed6\u628a\u90a3\u4ef6\u4e8b\u6cc4\u6f0f\u51fa\u53bb\uff0c\u201d\u8bf4\u7740\u53c8\u671b\u4e86\u5c55\u767d\u4e00\u773c\uff0c\u9053\uff1a\u201c\u4f84\u5973\u4e0d\u6562\u8bf4\u4ed6\u505a\u5f97 \u4e0d\u5bf9\uff0c\u4f46\u5df2\u5f15\u8d77\u5927\u90e8\u4efd\u4eba\u7684\u7591\u5fc3\uff01\u201d \u96f7\u5927\u53d4\u6d69\u53f9\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4ed6\u4eec\u7591\u5fc3\u4e5f\u6ca1\u6709\u529e\u6cd5\uff0c\u90a3\u90e8\u4e66\u7684\u786e\u4e0d\u80fd\u7ed9\u4eba\u770b\uff0c\u5c55\u8d24\u4f84\u4e3a\u4e86 \u633d\u6551\u6b66\u6797\u6d69\u52ab\uff0c\u7b54\u5e94\u6559\u4f17\u4eba\u4e66\u4e0a\u7684\u6b66\u529f\u5df2\u662f\u5927\u5f00\u2018\u827a\u4e0d\u8f7b\u4f20\u2019\u7684\u5148\u4f8b\u4e86\uff0c\u5982\u679c\u518d\u6709\u4eba\u2018\u5f97\u9647 \u671b\u8700\u2019\u5fc3\u751f\u8d2a\u5ff5\uff0c\u90a3\u53ef\u8bf4\u662f\u4e0d\u77e5\u597d\u6b79\uff01\u201d \u6155\u5bb9\u7ea2\u9053\uff1a\u201c\u90a3\u4e66\u4f84\u5973\u66fe\u770b\u8fc7\uff0c\u7684\u786e\u4e0d\u582a\u5165\u76ee\u2026\u2026\u201d \u968f\u53c8\u63a5\u4e0b\u53bb\u9053\uff1a\u201c\u4f84\u5973\u65e0\u610f\u4e2d\u542c\u5230\u6709\u4eba\u5bc6\u8c0b\u52ab\u593a\u6b64\u4e66\uff0c\u4e14\u6709\u5bf9\u5c55\u54e5\u54e5\u4e0d\u5229\u4f01\u56fe\uff0c\u4eca\u591c\u5927 \u53d4\u4e0e\u54e5\u54e5\u8fd8\u8981\u591a\u52a0\u5c0f\u5fc3\uff01\u201d \u5c55\u767d\u4e00\u6115\uff0c\u60f3\u4e0d\u5230\u81ea\u5df1\u4e00\u7247\u597d\u610f\uff0c\u53cd\u800c\u62db\u8d77\u522b\u4eba\u7978\u5fc3\uff0c\u95fb\u8a00\u9053\uff1a\u201c\u6709\u8fd9\u7b49\u4e8b\uff1f\u201d \u96f7\u5927\u53d4\u9762\u8272\u51dd\u91cd\uff0c\u9053\uff1a\u201c\u8c01\uff1f\u662f\u54ea\u4e00\u95e8\u6d3e\u4e0b\uff1f\u201d \u6155\u5bb9\u7ea2\u9053\uff1a\u201c\u4f84\u5973\u5728\u540e\u9662\u4e00\u95f4\u79d8\u5ba4\u95e8\u524d\u7ecf\u8fc7\uff0c\u65e0\u610f\u4e2d\u542c\u5230\u79d8\u5ba4\u4e2d\u4f20\u51fa\u2018\u5929\u4e0b\u7b2c\u4e00\u5947 \u4e66\u2026\u2026\u5fc5\u987b\u593a\u53d6\u5230\u624b\u2026\u2026\u4e0d\u60dc\u628a\u59d3\u5c55\u7684\u2026\u2026\u2019\u65ad\u65ad\u7eed\u7eed\u51e0\u53e5\u8bdd\uff0c\u518d\u60f3\u7ec6\u542c\uff0c\u5373\u4e3a\u5176\u8b66\u536b\u4eba\u5458 \u6240\u963b\uff0c\u6545\u4e5f\u4e0d\u77e5\u79d8\u5ba4\u4e2d\u662f\u4e9b\u4ec0\u4e48\u4eba\u3002\u201d \u5c55\u767d\u9053\uff1a\u201c\u4f55\u4eba\u62c5\u4efb\u8b66\u536b\u6155\u5bb9\u7ea2\u9053\uff1a\u2018\u7a77\u5bb6\u5e2e\u2019\u5f1f\u5b50\uff01\u201d \u96f7\u5927\u53d4\u9762\u8272\u9634\u6c89\uff0c\u542c\u81f3\u6b64\u5904\uff0c\u7a81\u7136\u5f80\u8d77\u4e00\u7ad9\uff0c\u9053\uff1a\u201c\u6211\u8981\u53bb\u95ee\u95ee\u2018\u98ce\u5c18\u4e09\u4e10\u2019\uff0c\u8fd9\u5230\u5e95 \u662f\u4f55\u4eba\u7978\u8d77\u8427\u5899\uff1f\u201d \u5a49\u513f\u7a81\u7136\u63a8\u95e8\u8fdb\u6765\u62e6\u4f4f\u96f7\u5927\u53d4\uff0c\u9053\uff1a\u201c\u4e0d\u7528\u95ee\u4e86\uff0c\u8d77\u6b79\u5fc3\u7684\u4e0d\u6b62\u2018\u7a77\u5bb6\u5e2e\u2019\u4e00\u6d3e\uff0c\u53ea\u8981 \u4eca\u591c\u6211\u4eec\u5c0f\u5fc3\u63d0\u9632\u5c31\u662f\u4e86\uff01\u201d \u5a49\u513f\u7ecf\u8fc7\u4e00\u65e5\u8c03\u606f\uff0c\u4f24\u52bf\u5df2\u7ecf\u590d\u539f\uff0c\u9526\u8863\u7389\u9762\uff0c\u8c8c\u7f8e\u5982\u82b1\uff0c\u5468\u8eab\u5145\u6ee1\u4e86\u9752\u6625\u6c14\u606f\u3002 \u5c55\u767d\u5fd9\u9053\uff1a\u201c\u5a49\u59b9\uff0c\u4f60\u662f\u4e0d\u662f\u4e5f\u6709\u4ec0\u4e48\u53d1\u73b0\uff1f\u201d \u5a49\u513f\u9053\uff1a\u201c\u73b0\u5728\u7fa4\u96c4\u4e09\u4e09\u4e94\u4e94\u805a\u5728\u4e00\u8d77\u79d8\u8bae\uff0c\u867d\u4e0d\u77e5\u4ed6\u4eec\u79d8\u8bae\u7684\u8be6\u7ec6\u5185\u5bb9\uff0c\u4f46\u603b\u4e0d\u5916\u662f \u60f3\u4e89\u593a\u90a3\u672c\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\uff0c\u8fd9\u5374\u662f\u53ef\u4ee5\u65ad\u8a00\u7684\uff01\u201d \u5c55\u767d\u95fb\u8a00\u4e0d\u7981\u6697\u6697\u540e\u4fae\uff0c\u60f3\u4e0d\u5230\u81ea\u5df1\u4e3a\u6d88\u5f25\u6b66\u6797\u6d69\u52ab\u7684\u4e00\u7247\u597d\u5fc3\uff0c\u53cd\u800c\u5f15\u8d77\u8fd9\u4e48\u5927\u7684\u9690 \u5fe7\uff0c\u8fd9\u5e74\u5934\u5b9e\u5728\u662f\u597d\u4e8b\u96be\u505a\uff01 \u96f7\u5927\u53d4\u957f\u7709\u4e00\u8038\uff0c\u6012\u5bb9\u6ee1\u9762\uff0c\u9053\uff1a\u201c\u771f\u662f\u4e0d\u77e5\u597d\u6b79\uff01\u5982\u679c\u80c6\u6562\u6709\u4eba\u751f\u4e8b\uff0c\u5fc5\u5b9a\u8981\u7ed9\u4ed6\u4eec \u4e00\u70b9\u989c\u8272\u770b\u770b\uff01\u201d \u8bf4\u81f3\u6b64\u5904\u4e00\u987f\uff0c\u53c8\u8f6c\u5934\u5bf9\u6155\u5bb9\u7ea2\u3001\u5a49\u513f\u8bf4\u9053\uff1a\u201c\u597d\u4e86\uff0c\u4f60\u4eec\u56de\u53bb\u4f11\u606f\u53bb\u5427\uff01\u201d \u6155\u5bb9\u7ea2\u3001\u5a49\u513f\u8f9e\u522b\u51fa\u53bb\uff0c\u96f7\u5927\u53d4\u5bf9\u5c55\u767d\u9053\uff1a\u201c\u6211\u4eec\u4e5f\u8be5\u4f11\u606f\u4e86\uff01\u201d \u8bf4\u7f62\u4e0a\u5e8a\uff0c\u548c\u8863\u800c\u5367\u3002\u5c55\u767d\u77e5\u9053\u96f7\u5927\u53d4\u7684\u6027\u683c\uff0c\u5f53\u4e0b\u4e5f\u4e0d\u591a\u8a00\uff0c\u7acb\u523b\u4e00\u638c\u6247\u706d\u4e86\u706f\u706b\uff0c \u767b\u5e8a\u5c31\u5bdd\uff0c\u4e8c\u4eba\u53ef\u8bf4\u662f\u827a\u9ad8\u4eba\u80c6\u5927\uff0c\u660e\u77e5\u5371\u673a\u56db\u4f0f\uff0c\u5374\u6545\u793a\u4ece\u5bb9\uff0c\u4e0d\u4f46\u6beb\u65e0\u6212\u5907\uff0c\u53cd\u800c\u767b\u5e8a \u5c31\u5bdd\uff0c\u5239\u65f6\u95f4\u96f7\u5927\u53d4\u5df2\u662f\u65af\u58f0\u5982\u96f7\uff01 \u4f46\u5c55\u767d\u5374\u65e0\u8bba\u5982\u4f55\u65e0\u6cd5\u5165\u7761\uff0c\u4ed6\u5fc3\u4e2d\u6240\u60f3\u7684\u4e8b\u60c5\u5b9e\u5728\u592a\u591a\u4e86\uff01 \u4ed6\u4e0d\u77e5\u90fd\u662f\u4e9b\u4ec0\u4e48\u4eba\u8981\u5411\u4ed6\u6697\u4e0b\u6bd2\u624b\uff1f\u4f46\u4ece\u6155\u5bb9\u7ea2\u53ca\u5a49\u513f\u7684\u53e3\u4e2d\uff0c\u77e5\u9053\u6b32\u60f3\u62a2\u593a\u300a\u9501\u9aa8 \u9500\u9b42\u5929\u4f5b\u5377\u300b\u7684\u4eba\u5f53\u4e0d\u5728\u5c11\u6570\uff01 \u7531\u6b64\uff0c\u4ed6\u53c8\u60f3\u5230\u5a49\u513f\u7684\u8eab\u4e0a\uff0c\u81ea\u5df1\u4e0e\u6155\u5bb9\u7ea2\u5df2\u8ba2\u5a5a\uff0c\u6155\u5bb9\u7ea2\u662f\u59d0\u59d0\uff0c\u65e2\u4e0e\u59d0\u59d0\u8ba2\u5a5a\uff0c\u603b \u4e0d\u80fd\u518d\u4e0e\u59b9\u59b9\u6709\u4ec0\u4e48\u8d85\u8d8a\uff0c\u4f46\u6628\u591c\u5a49\u513f\u5bf9\u4ed6\u7684\u5173\u5207\uff0c\u6ea2\u4e8e\u8a00\u8868\uff0c\u90a3\u6d77\u6837\u7684\u6df1\u60c5\uff0c\u4ed6\u80fd\u786e\u5207\u5730 \u4f53\u4f1a\u5f97\u51fa\u6765\uff01 \u4f46\u7eb5\u662f\u5a49\u513f\u5bf9\u4ed6\u60c5\u6df1\u4f3c\u6d77\uff0c\u4ed6\u53c8\u80fd\u5982\u4f55\u5462\uff1f\u2026\u2026 \u84e6\u89c1\u7a97\u4e0a\u7eff\u5149\u4e00\u95ea\u3002 \u4ed6\u4ee5\u4e3a\u662f\u4e00\u53ea\u8424\u706b\u866b\uff0c\u521d\u5c1a\u672a\u7559\u610f\uff0c\u4f46\u8fc5\u5373\u5192\u51fa\u4e00\u80a1\u767d\u70df\uff0c\u8f7b\u6613\u5411\u5c4b\u5185\u98d8\u4e86\u8fdb\u6765\uff01 \u4e00\u822c\u9999\u5473\u94b1\u8fdb\u5c55\u767d\u9f3b\u5b54\uff0c\u4f7f\u5f97\u4ed6\u5934\u8111\u4e3a\u4e4b\u4e00\u660f\uff01 \u5c55\u767d\u7acb\u523b\u8b66\u89c9\u4e0d\u597d\uff0c\u53ef\u80fd\u662f\u6c5f\u6e56\u4eba\u7269\u65bd\u653e\u7684\u4ec0\u4e48\u85b0\u9999\u6bd2\u6c14\u4e4b\u7c7b\u7684\u6697\u5668\uff01 \u4ed6\u7acb\u523b\u95ed\u6ce8\u547c\u5438\uff0c\u6697\u8fd0\u4e00\u53e3\u771f\u6c14\uff0c\u628a\u5438\u8fdb\u8179\u5185\u7684\u4e9b\u8bb8\u6bd2\u6c14\u8feb\u51fa\u4f53\u5916\uff01 \u6240\u5e78\u4ed6\u5185\u529f\u6df1\u539a\u65e0\u6bd4\uff0c\u53c8\u670d\u98df\u8fc7\u5343\u5e74\u7075\u836f\u201c\u7d2b\u64c5\u82b1\u201d\uff0c\u56e0\u6b64\uff0c\u90a3\u6bd2\u6c14\u672a\u80fd\u4fb5\u5bb3\u5230\u4ed6\uff01 \u4e8b\u5b9e\u4e0a\uff0c\u7a97\u5916\u4eba\u65bd\u653e\u7684\u6bd2\u6c14\uff0c\u975e\u5e38\u5389\u5bb3\uff0c\u540d\u4e3a\u201c\u4e94\u9f13\u65ad\u9b42\u9999\u201d\uff0c\u7eb5\u662f\u8eab\u8d1f\u7edd\u4e16\u6b66\u529f\u4e4b\u4eba \u55c5\u5230\u540e\uff0c\u4e5f\u8981\u5f53\u573a\u660f\u8ff7\u4e0d\u9192\uff01 \u5982\u679c\u5929\u4eae\u65f6\u5206\uff0c\u4e0d\u4ee5\u72ec\u95e8\u89e3\u836f\u6551\u6cbb\uff0c\u4fbf\u53ef\u6b7b\u4ea1\uff01 \u65bd\u6bd2\u4e4b\u4eba\uff0c\u5fc3\u72e0\u624b\u8fa3\uff0c\u7b97\u5b9a\u5c55\u767d\u6b66\u529f\u9ad8\u5f3a\uff0c\u660e\u6253\u660e\u6597\uff0c\u51b3\u4e0d\u662f\u5c55\u767d\u7684\u5bf9\u624b\uff0c\u6545\u6b64\u65bd\u51fa\u8fd9 \u79cd\u72e0\u6bd2\u4e4b\u8ba1\uff01 \u60f3\u4e0d\u5230\u5c55\u767d\u5185\u529f\u60e8\u4e3a\u5df2\u5230\u767e\u6bd2\u4e0d\u6d78\u7684\u5730\u6b65\uff0c\u7565\u4e00\u8fd0\u6c14\uff0c\u5373\u628a\u5438\u5165\u8179\u5185\u7684\u6bd2\u6c14\u8feb\u51fa\uff01 \u800c\u4e14\u5185\u8eab\u771f\u5143\u4e1d\u6beb\u672a\u53d7\u5230\u5f71\u54cd\uff01 \u5c55\u767d\u903c\u51fa\u8179\u5185\u6bd2\u6c14\uff0c\u7acb\u523b\u60f3\u5411\u96f7\u5927\u53d4\u6253\u62db\u547c\uff0c\u8c01\u77e5\u4e00\u8f6c\u773c\uff0c\u5e8a\u4e0a\u5df2\u5931\u53bb\u4e86\u96f7\u5927\u53d4\u7684\u8e2a \u8ff9\uff01 \u7a97\u5916\u7a81\u7136\u4f20\u6765\u4e00\u58f0\u95f7\u54fc\uff01 \u4f3c\u662f\u6709\u4eba\u53d7\u4e86\u4e00\u4e0b\u91cd\u51fb\uff0c\u63a5\u7740\u53d1\u51fa\u96f7\u5927\u53d4\u7684\u8c6a\u7b11\u9053\uff1a\u201c\u9601\u4e0b\u8499\u4f4f\u8138\u5b54\uff0c\u4ee5\u4e3a\u522b\u4eba\u5c31\u8ba4\u4e0d \u51fa\u6765\u4e86\u5417\uff1f\u54fc\uff01\u2018\u7a77\u5bb6\u5e2e\u2019\u51fa\u4e86\u4f60\u4eec\u8fd9\u4e9b\u8d25\u7c7b\uff0c\u5b9e\u5728\u4ee4\u4eba\u9f7f\u51b7\uff01\u201d \u8bf4\u7740\uff0c\u638c\u98ce\u7834\u7a7a\uff0c\u811a\u6b65\u6742\u7763\uff0c\u52a8\u624b\u7684\u4f3c\u662f\u4e0d\u6b62\u4e00\u4e8c\u4eba\u2026\u2026 \u5c55\u767d\u6697\u9053\u4e00\u58f0\uff1a\u201c\u60ed\u6127\uff01\u201d \u5728\u8fd9\u79cd\u5730\u65b9\uff0c\u5c31\u770b\u51fa\u6c5f\u6e56\u7ecf\u9a8c\u5c1a\u662f\u4e0d\u591f\u4e30\u5bcc\uff0c\u660e\u660e\u96f7\u5927\u53d4\u5728\u5e8a\u4e0a\u6253\u9f3e\uff0c\u6ca1\u60f3\u5230\u6bd4\u4ed6\u53d1\u73b0 \u7a97\u5916\u6765\u4eba\u8fd8\u8981\u65e9\u3002 \u5c55\u767d\u6b64\u65f6\uff0c\u7adf\u4e5f\u591a\u4e86\u4e00\u4e2a\u5fc3\u773c\uff0c\u542c\u5230\u96f7\u5927\u53d4\u5728\u7a97\u5916\u4e0e\u4eba\u52a8\u624b\uff0c\u4ed6\u5374\u4ece\u540e\u7a97\u6084\u65e0\u58f0\u606f\u5730\u63a0 \u51fa\uff0c\u534a\u7a7a\u4e2d\u5fae\u4e00\u63d0\u6c14\uff0c\u811a\u672a\u6cbd\u5730\uff0c\u5373\u5df2\u98d8\u8eab\u4e0a\u4e86\u5c4b\u9876\uff01 \u8fd9\u201c\u9648\u4ed3\u6697\u6e21\u201d\u4e4b\u8ba1\uff0c\u8fd8\u662f\u8ba9\u5c55\u767d\u4f7f\u5bf9\u4e86\uff0c\u9664\u4e86\u9662\u4e2d\u4e0e\u96f7\u5927\u53d4\u52a8\u624b\u7684\u8d3c\u4eba\u4e4b\u5916\uff0c\u5c4b\u9876\u4e0a \u679c\u7136\u7ad9\u7740\u56db\u4e94\u4eba\u4e4b\u591a\uff0c\u6b63\u5728\u6ce8\u89c6\u7740\u9662\u5b50\u91cc\u6570\u4eba\u56f4\u7740\u96f7\u5927\u53d4\u53ae\u6740\uff01 \u4e00\u662f\u5c55\u767d\u8f7b\u8eab\u63d0\u7eb5\u672f\u5df2\u81f3\u7edd\u9876\uff0c\u8dc3\u4e0a\u5c4b\u9876\u58f0\u606f\u7686\u65e0\uff0c\u518d\u8005\u7ad9\u5728\u5c4b\u9876\u4e4b\u4eba\uff0c\u5168\u795e\u8d2f\u6ce8\u5728\u9662 \u4e2d\u6570\u4eba\u640f\u6597\u4e0a\uff0c\u5c55\u767d\u5df2\u7ad9\u5728\u4ed6\u4eec\u8eab\u540e\u4e0d\u8db3\u4e94\u5c3a\u4e4b\u5904\uff0c\u4ed6\u4eec\u5c1a\u672a\u53d1\u89c9\uff0c\u5c55\u767d\u76ee\u529b\u8fc7\u4eba\uff0c\u867d\u662f\u9ed1 \u591c\uff0c\u5df2\u4f9d\u7a00\u770b\u51fa\u5f53\u4e2d\u4e00\u4eba\u7684\u80cc\u5f71\u6781\u50cf\u201c\u7aef\u65b9\u516c\u5b50\u201d\uff0c\u5176\u4f59\u4e09\u4eba\uff0c\u7686\u662f\u201c\u7a77\u5bb6\u5e2e\u201d\u7684\u5e2e\u4f17\uff01 \u5c55\u767d\u7a81\u5730\u53d1\u51fa\u4e00\u58f0\u51b7\u7b11\uff01 \u90a3\u6570\u4eba\u5413\u5f97\u5468\u8eab\u4e00\u98a4\uff0c\u63a5\u7136\u6025\u8f6c\u8eab\u5b50\uff01 \u5c55\u767d\u89c1\u4ed6\u4eec\u90fd\u7528\u9ed1\u5dfe\u8499\u9762\uff0c\u4e0d\u7531\u51b7\u7b11\u9053\uff1a\u201c\u5f53\u771f\u662f\u9e21\u9e23\u72d7\u76d7\u4e4b\u8f88\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u6562\u4ee5\u771f\u9762\u76ee \u793a\u4eba\uff1f \u5f53\u4e2d\u4e00\u4eba\uff0c\u53cc\u773c\u9732\u51fa\u9a87\u4eba\u7684\u5149\u8292\uff0c\u731b\u7136\u5411\u5c55\u767d\u5288\u51fa\u4e00\u638c\uff01 \u638c\u98ce\u9510\u5578\uff0c\u529f\u529b\u4e0d\u5f31\uff01 \u4f46\u5c55\u767d\u54ea\u91cc\u628a\u4ed6\u653e\u5728\u5fc3\u4e0a\uff0c\u8fce\u7740\u6765\u52bf\u6325\u638c\u786c\u63a5\uff01 \u8c01\u77e5\u6765\u4eba\u4e0d\u7b49\u638c\u98ce\u63a5\u5b9e\uff0c\u8eab\u5982\u6d41\u661f\u75f0\u6cc4\uff0c\u76f4\u5411\u90ca\u5916\u843d\u8352\u800c\u8d70\uff01 \u968f\u7740\u53e6\u5916\u7684\u56db\u4e94\u4eba\uff0c\u4e5f\u56db\u6563\u9003\u53bb\uff01 \u663e\u89c1\u4ed6\u4eec\u4e0d\u662f\u5c55\u767d\u7684\u5bf9\u624b\uff0c\u89c1\u6697\u7b97\u5931\u8d25\uff0c\u5c55\u767d\u4e00\u9732\u9762\uff0c\u5373\u523b\u9041\u8d70\uff01\u800c\u4e14\uff0c\u4f3c\u4e4e\u6781\u4e0d\u613f\u8ba9 \u5c55\u81ea\u8ba4\u51fa\u672c\u6765\u9762\u76ee\u3002 \u5c55\u767d\u5927\u559d\u4e00\u58f0\uff1a\u54ea\u91cc\u8d70\uff1f\u201d \u817e\u8eab\u5411\u90a3\u4e3a\u9996\u4e4b\u4eba\u8ffd\u53bb\uff01 \u90a3\u4eba\u56de\u624b\u4e00\u626c\uff0c\u4e00\u9053\u767d\u5149\uff0c\u75be\u5411\u5c55\u767d\u9762\u95e8\u6253\u6765\uff1a \u5c55\u767d\u4e00\u638c\u628a\u90a3\u9053\u767d\u5149\u9707\u98de\u534a\u7a7a\uff0c\u201c\u556a\u201d\u7684\u4e00\u58f0\uff0c\u90a3\u9053\u767d\u5149\u7adf\u7136\u7206\u70b8\u5f00\u6765\uff0c\u706b\u5149\u4e00\u95ea\uff0c\u5149 \u96e8\u56db\u6e85\uff01 \u7a81\u7136\u9662\u843d\u56db\u5468\u4e00\u58f0\u5450\u558a\uff0c\u6570\u4e0d\u6e05\u7684\u5f39\u4e38\uff0c\u5982\u6ee1\u5929\u98de\u8757\u4e00\u822c\uff0c\u9f50\u5411\u5c55\u81ea\u5c04\u6765\uff01 \u60f3\u4e0d\u5230\u8fd9\u7adf\u662f\u4e07\u5f29\u9f50\u53d1\u7684\u6697\u53f7\uff01 \u5c55\u767d\u6e05\u5578\u4e00\u58f0\uff0c\u5728\u5468\u8eab\u821e\u8d77\u4e00\u7247\u638c\u98ce\u52b2\u6d41\uff0c\u628a\u6240\u6709\u5f39\u4e38\u6089\u6570\u9707\u98de\uff01 \u518d\u770b\u9662\u5b50\u91cc\u4e0e\u96f7\u5927\u53d4\u52a8\u624b\u7684\u8d3c\u4eba\uff0c\u4e5f\u540c\u65f6\u9038\u53bb\uff0c\u201c\u592a\u767d\u53cc\u9038\u201d\u3001\u6155\u5bb9\u59d0\u59b9\u53ca\u8339\u8001\u9556\u5934\u5747 \u5df2\u4ece\u623f\u5185\u51fa\u6765\uff0c\u8fde\u96f7\u5927\u53d4\u7b97\u4e0a\uff0c\u73b0\u5728\u51e0\u4e2a\u4eba\u5747\u88ab\u8fde\u73e0\u6025\u7559\u5305\u56f4\u8d77\u6765\uff01 \u4f17\u4eba\u638c\u5288\u8896\u6325\uff0c\u9707\u843d\u56db\u56f4\u4e71\u5c04\u7684\u5f39\u4e38\uff0c\u4f46\u5f39\u4e38\u6570\u91cf\u6781\u591a\uff0c\u800c\u4e14\u50cf\u6025\u98ce\u9aa4\u96e8\u4e00\u822c\u6253\u6765\uff0c\u4e00 \u65f6\u4e4b\u95f4\uff0c\u7ade\u4f7f\u4f17\u4eba\u624b\u5fd9\u811a\u4e71\uff01 \u8fd9\u65f6\u5206\u6563\u4f4f\u5728\u5404\u623f\u7684\u7fa4\u96c4\uff0c\u5747\u5df2\u542c\u5230\u6253\u6597\u4e4b\u58f0\uff0c\u5954\u81f3\u9662\u843d\u4e2d\u6765\uff0c\u7a81\u95fb\u4e00\u58f0\u5927\u559d\uff1a\u201c\u8fd8\u4e0d \u7ed9\u6211\u4f4f\u624b\u300f\u201d\u8ddf\u7740\u4e09\u6761\u4eba\u5f71\uff0c\u75be\u5982\u98d8\u98ce\u95ea\u7535\uff0c\u98d8\u843d\u9662\u4e2d\uff01 \u6b63\u662f\u201c\u7a77\u5bb6\u5e2e\u201d\u4e09\u5927\u957f\u8001\u201c\u98ce\u5c18\u4e09\u4e10\u201d\uff01\u2019\u201c\u75af\u4e10\u201d\u9517\u826f\u770b\u51fa\u57cb\u4f0f\u56db\u5468\u53d1\u5c04\u5f39\u4e38\u7684\u662f \u201c\u7a77\u5bb6\u5e2e\u201d\u5f1f\u5b50\uff0c\u53c8\u89c1\u88ab\u56f4\u653b\u7684\u7adf\u662f\u96f7\u5927\u53d4\u3001\u5c55\u767d\u7b49\u4eba\uff0c\u4e0d\u7531\u6012\u5bb9\u773a\u88c2\uff0c\u9ad8\u53eb\u9053\uff1a\u201c\u4f4f\u624b\uff01 \u662f\u8c01\u4e0b\u7684\u547d\u4ee4\uff0c\u4f60\u4eec\u7adf\u6562\u56f4\u653b\u81ea\u5df1\u4eba\uff0c\u83ab\u975e\u662f\u75af\u4e86\uff1f\u201d \u56db\u5468\u53d1\u5c04\u5f39\u4e38\u7684\u201c\u7a77\u5bb6\u5e2e\u201d\u5f1f\u5b50\uff0c\u88ab\u201c\u98ce\u5c18\u4e09\u4e10\u201d\u4e00\u559d\uff0c\u7acb\u65f6\u6536\u624b\u3002\u96f7\u5927\u53d4\u54c8\u54c8\u5927\u7b11\uff0c \u9053\uff1a\u201c\u81ed\u53eb\u5316\u5b50\uff01\u8fd9\u4ef6\u4e8b\u4f60\u4eec\u5982\u679c\u4e0d\u80fd\u89e3\u91ca\u6e05\u695a\uff0c\u8001\u592b\u4e0e\u4f60\u4eec\u4e09\u4e2a\u7acb\u523b\u5212\u5730\u7edd\u4ea4\uff01\u201d \u201c\u75af\u4e10\u201d\u6b64\u65f6\u4e00\u6536\u7d20\u5e38\u7684\u5b09\u620f\u4e4b\u6001\uff0c\u9762\u8272\u51dd\u91cd\u5730\u9053\uff1a\u201c\u8001\u54e5\u54e5\u4e0d\u8bf4\uff0c\u6211\u4eec\u4e5f\u8981\u67e5\u4e2a\u6c34\u843d \u77f3\u51fa\uff0c\u8fd9\u7b80\u76f4\u7ed9\u6211\u4eec\u7a77\u4eba\u4e22\u8138\uff01\u201d \u96f7\u5927\u53d4\u8d70\u81f3\u7a97\u524d\u5730\u4e0b\uff0c\u62fe\u8d77\u4e00\u7269\u6765\uff0c\u6296\u624b\u63b7\u7ed9\u201c\u75af\u4e10\u201d\u9053\uff1a\u201c\u81ed\u53eb\u5316\uff01\u4f60\u518d\u770b\u770b\u8fd9\u4e2a\uff0c \u60f3\u4e0d\u5230\u4f60\u4eec\u2018\u7a77\u5bb6\u5e2e\u2019\uff0c\u7adf\u4e5f\u5e72\u8d77\u8fd9\u7b49\u6ca1\u672c\u94b1\u7684\u4e70\u5356\u6765\u4e86\uff01\u201d \u201c\u75af\u4e10\u201d\u4f38\u624b\u63a5\u4f4f\u96f7\u5927\u53d4\u63b7\u6765\u4e4b\u7269\uff0c\u62ff\u5728\u773c\u524d\u4e00\u770b\uff0c\u539f\u6765\u662f\u4e00\u5177\u5236\u4f5c\u5f97\u5341\u5206\u7cbe\u5de7\u7684\u201c\u767d \u94dc\u4ed9\u9e64\u201d\uff01 \u8fd9\u73a9\u610f\u6c5f\u6e56\u4eba\u7269\u5c31\u662f\u6ca1\u89c1\u8fc7\uff0c\u4e5f\u6709\u4e2a\u8033\u95fb\uff0c\u6b63\u662f\u6c5f\u6e56\u9ed1\u9053\u4e0b\u4e09\u6d41\u6deb\u52ab\u5077\u76d7\u4e4b\u8f88\u4f5c\u6848\u65f6\uff0c \u6240\u60ef\u7528\u7684\u65bd\u653e\u8ff7\u9999\u7684\u7528\u5177\uff01 \u201c\u75af\u4e10\u201d\u6c14\u5f97\u53d1\u7709\u7686\u70b8\uff0c\u4e00\u65f6\u4e4b\u95f4\u695e\u4f4f\uff0c\u6ca1\u6709\u8bf4\u51fa\u8bdd\u6765\uff01 \u201c\u7a77\u5bb6\u5e2e\u201d\u867d\u662f\u7a77\u4eba\u7684\u5927\u7ed3\u5408\uff0c\u4f46\u5386\u4ee3\u7956\u5e08\u7684\u5782\u8bad\uff0c\u7b2c\u4e00\u6212\u4fbf\u662f\u201c\u997f\u6b7b\u4e0d\u4f5c\u8d3c\u201d\uff01\u5982 \u4eca\uff0c\u8fd9\u4f5c\u8d3c\u7684\u5de5\u5177\u51fa\u81ea\u201c\u7a77\u5bb6\u5e2e\u201d\u95e8\u4e0b\u5f1f\u5b50\u4e4b\u624b\uff0c\u800c\u4e14\u8fd8\u843d\u5728\u96f7\u5927\u53d4\u624b\u91cc\uff0c\u771f\u4f7f\u201c\u98ce\u5c18\u4e09 \u4e10\u201d\u611f\u5230\u7406\u62d9\u6c14\u77ed\uff0c\u8138\u4e0a\u65e0\u5149\uff01 \u201c\u9152\u4e10\u201d\u9762\u8272\u9634\u5bd2\u5982\u51b0\uff0c\u731b\u4e00\u56de\u8eab\uff0c\u5411\u9690\u5728\u56db\u5468\u7684\u201c\u7a77\u5bb6\u5e2e\u201d\u5f1f\u5b50\u5389\u58f0\u53f1\u9053\uff1a\u201c\u662f\u54ea\u4e00 \u4e2a\u95e8\u4e0b\u5f1f\u5b50\u5f53\u503c\uff0c\u8fd8\u4e0d\u6eda\u51fa\u6765\uff1f\u201d \u5e94\u58f0\u8e31\u51fa\u4e00\u4e2a\u5e74\u7ea6\u56db\u65ec\u7684\u53eb\u5316\uff0c\u201c\u98ce\u5c18\u4e09\u4e10\u201d\u4e43\u201c\u7a77\u5bb6\u5e2e\u201d\u7684\u957f\u8001\uff0c\u638c\u6709\u751f\u6740\u5927\u6743\uff0c\u6b64 \u65f6\u66b4\u6012\u5982\u96f7\uff0c\u90a3\u4e2d\u5e74\u53eb\u5316\u5df2\u5413\u5f97\u8138\u8272\u6e17\u767d\uff0c\u8d70\u81f3\u201c\u9152\u4e10\u201d\u4e08\u4f59\u4e4b\u5904\uff0c\u7ad9\u4f4f\u8eab\u5f62\uff0c\u6309\u5e2e\u89c4\u65bd\u4e86 \u4e00\u793c\uff0c\u9053\uff1a\u5f1f\u5b50\u2018\u8349\u4e0a\u98de\u2019\u767d\u4eae\u9886\u73ed\uff01\u201d \u201c\u5478\uff01\u201d \u201c\u9152\u5f13\u201d\u65b9\u7ca5\u4e00\u53e3\u6d53\u75f0\u5410\u5728\u767d\u4eae\u8111\u4e0a\uff0c\u9a82\u9053\uff1a\u201c\u4f60\u5403\u4e86\u6cb9\u6ce5\u8499\u4e86\u5fc3\uff01\u4e3a\u4ec0\u4e48\u5411\u96f7\u5927\u4fa0\u65bd \u653e\u4e71\u5f29\uff1f\u201d \u201c\u8349\u4e0a\u98de\u201d\u767d\u4eae\u8138\u4e0a\u7684\u6d53\u75f0\u64e6\u90fd\u4e0d\u6562\u64e6\uff0c\u8eac\u8eab\u9053\uff1a\u201c\u5f1f\u5b50\u53ea\u662f\u5949\u547d\u884c\u4e8b\uff0c\u5c1a\u8bf7\u4e09\u957f\u8001\u660e \u5bdf\uff01\u201d \u201c\u75af\u4e10\u201d\u51fa\u624b\u5982\u98ce\uff0c\u63a2\u81c2\u5201\u4f4f\u767d\u4eae\u7684\u53f3\u624b\u8155\u5b50\uff0c\u5389\u58f0\u9053\uff1a\u201c\u5feb\u8bf4\uff01\u4f60\u5949\u8c01\u4e4b\u547d\u884c\u4e8b\uff1f\u201d \u201c\u75af\u4e10\u201d\u6012\u6781\uff0c\u51fa\u624b\u6781\u91cd\uff0c\u8fd9\u4e00\u6293\u9669\u4e9b\u628a\u201c\u8349\u4e0a\u98de\u201d\u767d\u4eae\u7684\u4e00\u53ea\u624b\u6298\u65ad\uff0c\u767d\u4eae\u75db\u5f97\u989d\u4e0a \u5192\u51fa\u8c46\u5927\u7684\u6c57\u73e0\uff0c\u4f46\u54ac\u7259\u786c\u633a\u7740\uff0c\u9053\uff1a\u5949\u638c\u95e8\u4eba\u4e4b\u547d\uff01\u201d \u201c\u98ce\u5c18\u4e09\u4e10\u201d\u6781\u5feb\u5730\u4ea4\u6362\u4e86\u4e00\u4e2a\u773c\u8272\uff0c\u5bf9\u4e8b\u5b9e\u7684\u771f\u76f8\u4f3c\u662f\u5df2\u660e\u767d\u4e86\u5927\u534a\u3002\u4f46\u201c\u75af\u4e10\u201d\u6293 \u4efb\u767d\u4eae\u7684\u53f3\u624b\u672a\u653e\uff0c\u7d27\u76ef\u4e86\u4e00\u53e5\uff0c\u9053\uff1a \u201c\u6ca1\u6709\u9519\u5417\uff1f\u201d \u767d\u4eae\u75db\u5f97\u8138\u4e0a\u8c46\u5927\u7684\u6c57\u7c92\u5411\u4e0b\u76f4\u6eda\uff0c\u4f46\u4ecd\u606d\u8c28\u8a00\u9053\uff1a\u201c\u5f1f\u5b50\u7109\u6562\u6492\u8c0e\uff01\u201d \u201c\u75af\u4e10\u201d\u628a\u624b\u4e00\u677e\uff0c\u9000\u56de\u4e24\u6b65\uff0c\u671b\u4e86\u5176\u4ed6\u4e8c\u5f13\u4e00\u773c\uff0c\u957f\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u9ed8\u9ed8\u4e0d\u8bed\uff01 \u6b64\u65f6\uff0c\u7fa4\u96c4\u90fd\u7ad9\u5728\u9662\u843d\u56db\u5468\uff0c\u9759\u671b\u7740\u201c\u7a77\u5bb6\u5e2e\u201d\u5982\u4f55\u53d1\u843d\u6b64\u4e8b\uff1f\u867d\u7136\u90a3\u4e48\u591a\u4eba\uff0c\u5374\u662f\u55eb \u5685\u65e0\u58f0\u3002 \u5982\u679c\u662f\u5e2e\u4e2d\u67d0\u4e00\u4eba\u6240\u4e3a\uff0c\u201c\u98ce\u5c18\u4e09\u4e10\u201d\u53ef\u4ee5\u4ee5\u5e2e\u4e2d\u957f\u8001\u7684\u8eab\u4efd\uff0c\u4ece\u4e8b\u6574\u8083\u5e2e\u89c4\uff0c\u8fd9\u6837\u4e00 \u6765\u5bf9\u96f7\u5927\u53d4\u4fbf\u7b97\u6709\u4e86\u4e00\u4e2a\u4ea4\u5f85\u3002\u4f46\u767d\u4eae\u8bf4\u51fa\u662f\u5949\u638c\u95e8\u4eba\u4e4b\u547d\uff0c\u505a\u51fa\u8fd9\u7b49\u89c1\u4e0d\u5f97\u4eba\u7684\u4e8b\uff0c\u4fbf\u6210 \u4e3a\u201c\u7a77\u5bb6\u5e2e\u201d\u5168\u5e2e\u4e4b\u803b\uff0c\u800c\u4e14\u4e5f\u4e0d\u662f\u8f7b\u6613\u4fbf\u53ef\u5904\u7406\u7684\u4e86\uff0c\u56e0\u201c\u7a77\u5bb6\u5e2e\u201d\u5386\u4ee3\u76f8\u4f20\uff0c\u638c\u95e8\u4eba\u81f3 \u5c0a\u65e0\u4e0a\uff0c\u5e2e\u89c4\u4e2d\u6ca1\u6709\u5904\u7f5a\u638c\u95e8\u4eba\u7684\u89c4\u5b9a\uff01 \u201c\u98ce\u5c18\u4e09\u4e10\u201d\u56fa\u7136\u5728\u5e2e\u4e2d\u8eab\u4efd\u6781\u5c0a\uff0c\u4f46\u5bf9\u638c\u95e8\u4eba\u72af\u4e86\u8fc7\u9519\uff0c\u4e5f\u611f\u675f\u624b\u65e0\u7b56\uff01 \u4e09\u4eba\u4e92\u76f8\u53d1\u4e86\u4e00\u4f1a\u5b50\u6014\uff0c\u201c\u9152\u4e10\u201d\u7a81\u7136\u7075\u673a\u4e00\u52a8\uff0c\u53c8\u628a\u7f13\u7f13\u540e\u9000\u7684\u767d\u4eae\u559d\u4f4f\uff0c\u9053\uff1a\u201c\u7ad9 \u4f4f\uff01\u53ef\u662f\u638c\u95e8\u4eba\u4eb2\u81ea\u5bf9\u4f60\u4e0b\u7684\u547d\u4ee4\u5417\uff1f\u201d \u201c\u8349\u4e0a\u98de\u201d\u767d\u4eae\u5c1a\u672a\u7b54\u8a00\uff0c\u7a81\u542c\u4e00\u58f0\u957f\u7b11\u54cd\u81ea\u4e00\u9685\uff0c\u63a5\u7740\u98de\u5feb\u5730\u63a0\u6765\u4e00\u6761\u4eba\u5f71\uff0c\u90a3\u4eba\u5f71 \u8eab\u5f62\u4e00\u5b9a\uff0c\u5f71\u5b9a\u4eba\u73b0\uff0c\u6b63\u662f\u201c\u7a77\u5bb6\u5e2e\u201d\u5386\u4ee3\u6700\u4e2d\u8f7b\u7684\u638c\u95e8\u4eba\u201c\u7aef\u65b9\u516c\u5b50\u201d\uff01 \u201c\u7aef\u65b9\u516c\u5b50\u201d\u4e00\u73b0\u8eab\uff0c\u5c55\u767d\u5728\u4e00\u65c1\u6697\u6697\u6487\u5634\uff0c\u5fc3\u8bf4\uff1a\u201c\u770b\u4f60\u8fd9\u6b21\u600e\u4e48\u4e0b\u53f0\uff1f\u201d \u201c\u7aef\u65b9\u516c\u5b50\u201d\u5411\u201c\u98ce\u5c18\u4e09\u4e10\u201d\u6de1\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u8981\u67e5\u95ee\u6e05\u695a\uff01\u201d\u8bf4\u7740\u53c8\u8f6c\u5bf9\u767d\u4eae \u559d\u9053\uff1a\u201c\u53ef\u662f\u672c\u638c\u95e8\u4eb2\u81ea\u5bf9\u4f60\u4e0b\u7684\u547d\u4ee4\u5417\uff1f\u201d \u767d\u4eae\u55eb\u5685\u8a00\u9053\uff1a\u4e43\u638c\u95e8\u5ea7\u524d\u201c\u795e\u884c\u592a\u4fdd\u201d\u8463\u6e05\u4f20\u4e0b\u53e3\u8c15\uff0c\u800c\u4e14\u5949\u6709\u2018\u7eff\u7389\u6756\u4ee4\u2019\uff01\u201d \u201c\u7aef\u65b9\u516c\u5b50\u201d\u9762\u8272\u4e00\u5bd2\uff0c\u5386\u53f1\u9053\uff1a\u201c\u4f20\u2018\u795e\u884c\u592a\u4fdd\u2019\u8463\u6e05\uff01\u201d \u63a5\u7740\u201c\u4f20\u2018\u795e\u884c\u592a\u4fdd\u2019\u8463\u6e05\uff01\u201d\u4e4b\u559d\u58f0\uff0c\u6b64\u8d77\u5f7c\u843d\uff0c\u5239\u90a3\u65f6\u4f20\u56de\u8bdd\u6765\uff0c\u201c\u795e\u884c\u592a\u4fdd\u201d\u8463 \u6e05\u4e0d\u89c1\uff0c\u4e5f\u4e0d\u77e5\u4f55\u5f80\u3002 \u201c\u7aef\u65b9\u516c\u5b50\u201d\u9762\u5bd2\u4f3c\u6c34\uff0c\u9053\uff1a\u201c\u8bf7\u2018\u7eff\u7389\u6756\u4ee4\u2019\uff01\u201d \u201c\u98de\u53c9\u592a\u4fdd\u201d\u97e9\u7389\u5e94\u58f0\u524d\u5f80\uff0c\u4e0d\u4e45\uff0c\u53c8\u98de\u5feb\u5730\u8dd1\u56de\uff0c\u6c14\u6781\u8d25\u574f\u5730\u9053\uff1a\u201c\u542f\u7968\u638c\u95e8\uff0c\u2018\u7eff \u7389\u6756\u4ee4\u2019\u4e0d\u5728\u9999\u5802\uff01\u201d \u201c\u98ce\u5c18\u4e09\u4e10\u201d\u9762\u8272\u500f\u53d8\uff0c\u201c\u7aef\u65b9\u516c\u5b50\u201d\u795e\u8272\u66f4\u89c1\u6fc0\u52a8\uff0c\u6025\u9053\uff1a\u201c\u4f55\u4eba\u5728\u9999\u5802\u5f53\u503c\uff1f\u201d \u201c\u6253\u864e\u592a\u4fdd\u2019\u6797\u7acb\u4e0e\u2018\u91d1\u67aa\u592a\u4fdd\u2019\u845b\u6d2a\uff01\u201d \u201c\u7aef\u65b9\u516c\u5b50\u201d\u9762\u8272\u94c1\u9752\uff0c\u559d\u9053\uff1a\u201c\u4f20\u4ed6\u4e8c\u5165\u6765\u89c1\uff01\u201d \u201c\u98de\u53c9\u592a\u4fdd\u97e9\u7389\u8eac\u8eab\u9053\uff1a\u201c\u4ed6\u4e8c\u4eba\u4e5f\u5747\u4e0d\u5728\uff01\u201d \u6b64\u8a00\u4e00\u51fa\uff0c\u201c\u98ce\u5c18\u4e09\u4e10\u201d\u53ca\u201c\u7aef\u65b9\u516c\u5b50\u201d\u540c\u65f6\u9762\u8272\u4e00\u53d8\u3002\u56e0\u8fd9\u201c\u795e\u884c\u201d\u3001\u201c\u6253\u864e\u201d\u3001 \u201c\u98de\u53c9\u201d\u3001\u201c\u91d1\u67aa\u201d\u56db\u592a\u4fdd\uff0c\u4e43\u201c\u7a77\u5bb6\u5e2e\u201d\u638c\u95e8\u4eba\u5ea7\u524d\u201c\u56db\u5927\u62a4\u6cd5\u201d\uff0c\u5982\u4eca\u56db\u5931\u5176\u4e09\uff0c\u5012\u662f \u5341\u5206\u5c11\u89c1\u4e4b\u4e8b\uff01\u540c\u65f6\uff0c\u5e2e\u5185\u638c\u95e8\u4eba\u4fe1\u7269\u201c\u7eff\u7389\u6756\u4ee4\u201d\u5931\u8e2a\uff0c\u5179\u4e8b\u4f53\u5927\uff01 \u201c\u7a77\u5bb6\u5e2e\u201d\u4f17\u7cdf\u4e71\u4e00\u56e2\uff0c\u201c\u7aef\u65b9\u516c\u5b50\u201d\u4e0b\u4ee4\u5bfb\u627e\u201c\u4e09\u5927\u592a\u4fdd\u201d\u53ca\u641c\u7d22\u201c\u7eff\u7389\u6756\u4ee4\u201d\uff0c\u7ed3 \u679c\u662f\u77f3\u6c89\u5927\u6d77\uff0c\u4e00\u65e0\u6240\u5f97\uff01 \u81f3\u6b64\u201c\u7aef\u65b9\u516c\u5b50\u201d\u4e0b\u4e86\u4e2a\u5224\u65ad\uff0c\u4e00\u5b9a\u662f\u201c\u4e09\u592a\u4fdd\u201d\u501f\u201c\u7eff\u7389\u6756\u4ee4\u201d\u5047\u4f20\u638c\u95e8\u4eba\u65e8\u610f\uff0c\u52ab \u593a\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\uff0c\u4e8b\u8d25\u9003\u8d70\u3002 \u5bf9\u201c\u7aef\u65b9\u516c\u5b50\u201d\u7684\u5224\u65ad\uff0c\u65e0\u4eba\u53ef\u63d0\u51fa\u53cd\u8bc1\uff0c\u53ea\u6709\u5c55\u767d\u5fc3\u4e2d\u72af\u7591\uff0c\u56e0\u4ed6\u4ece\u540e\u7a97\u7ffb\u4e0a\u5c4b\u9876\uff0c \u770b\u89c1\u56db\u4e94\u4e2a\u8499\u9762\u4eba\uff0c\u5176\u4e2d\u4e4b\u4e00\u660e\u660e\u662f\u201c\u7aef\u65b9\u516c\u5b50\u201d\u3002\u53ef\u662f\uff0c\u4ed6\u6ca1\u6709\u628a\u90a3\u4eba\u5f53\u573a\u6349\u4f4f\uff0c\u56e0\u6b64\uff0c \u4e5f\u672a\u70b9\u7834\u3002 \u5f53\u201c\u7a77\u5bb6\u5e2e\u201d\u4f17\u5728\u201c\u7aef\u65b9\u516c\u5b50\u201d\u4ee4\u4e0b\uff0c\u628a\u201c\u56db\u5927\u592a\u4fdd\u201d\u4ec5\u5b58\u5176\u4e00\u7684\u201c\u98de\u53c9\u592a\u4fdd\u201d\u4e5f\u62bc\u8d77 \u6765\uff0c\u5e76\u5411\u96f7\u5927\u53d4\u9053\u6b49\u65f6\uff0c\u5c55\u767d\u7075\u673a\u4e00\u52a8\uff0c\u5f53\u65f6\u53d1\u8bdd\u53eb\u4f4f\u5373\u5c06\u6563\u53bb\u7684\u7fa4\u96c4\uff0c\u9053\uff1a\u201c\u4e3a\u4e86\u6574\u4e2a\u4e2d \u539f\u6b66\u6797\u7684\u4e00\u6b21\u6d69\u52ab\uff0c\u5c55\u767d\u5192\u6627\uff0c\u81f4\u628a\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\u5f53\u4f17\u6cc4\u6f0f\uff0c\u636e\u5728\u4e0b\u6240\u77e5\uff0c\u4e0d\u5c11\u4eba\u5df2 \u8d77\u5fc3\u4e89\u593a\u6b64\u4e00\u5947\u4e66\uff0c\u6050\u6015\u56e0\u6b64\u53c8\u8981\u6380\u8d77\u53e6\u5916\u4e89\u7aef\uff0c\u73b0\u5728\u552f\u4e00\u7684\u529e\u6cd5\u2014\u2014\u201d\u8bf4\u7740\u4ed6\u5728\u6000\u4e2d\u638f\u51fa \u4e00\u672c\u5f69\u8272\u6591\u8c30\u3001\u5c01\u9762\u6781\u4e3a\u534e\u4e3d\u7684\u5c0f\u518c\u5b50\u201d\u5411\u7740\u7fa4\u96c4\u4e00\u626c\uff0c\u9053\uff1a\u201c\u5c31\u662f\u5f53\u7740\u7fa4\u96c4\u4e4b\u9762\uff0c\u5c06\u8fd9\u672c \u79d8\u5f55\u6bc1\u53bb\uff01\u201d \u63a5\u7740\u4e24\u624b\u4e00\u62cd\uff0c\u201c\u556a\u201d\u7684\u4e00\u58f0\u66b4\u54cd\uff0c\u7adf\u628a\u90a3\u672c\u6b66\u6797\u4eba\u7269\u4eba\u4eba\u68a6\u60f3\u4e89\u593a\u7684\u5929\u4e0b\u7b2c\u4e00\u5947\u4e66 \u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\u62cd\u4e86\u4e2a\u7c89\u788e\uff01 \u5c55\u767d\u6b64\u4e3e\uff0c\u5927\u5927\u51fa\u4e4e\u7fa4\u96c4\u610f\u6599\u4e4b\u5916\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u90fd\u9707\u60ca\u5f97\u77aa\u5927\u4e86\u773c\u775b\uff01 \u5c31\u8fde\u96f7\u5927\u53d4\u4e5f\u9707\u60ca\u5f97\u8bf4\u4e0d\u51fa\u8bdd\u6765\uff01\u5c55\u767d\u53cc\u624b\u4e00\u626c\uff0c\u5f69\u8272\u7eb8\u5c51\u5982\u4e94\u5f69\u8774\u8776\u7eb7\u7eb7\u98d8\u6fc0\uff01 \u5c55\u767d\uff0c\u6df1\u65bd\u4e00\u672d\uff0c\u9053\uff1a\u201c\u5c0f\u4f84\u6b64\u4e3e\uff0c\u5c1a\u8bf7\u5927\u53d4\u539f\u8c05\uff01\u201d \u96f7\u5927\u53d4\u6447\u5934\u4e00\u56db\uff0c\u9053\uff1a\u201c\u6bc1\u4e86\u5c31\u6bc1\u4e86\uff0c\u53ea\u53ef\u60dc\u2018\u53ea\u773c\u6717\u541b\u2019\u8001\u524d\u8f88\u4e00\u751f\u5fc3\u8840\uff01\u2026\u2026\u201d \u8bf4\u81f3\u6b64\uff0c\u96f7\u5927\u53d4\u5df2\u9eef\u7136\u8bf4\u4e0d\u4e0b\u53bb\u4e86\u3002 \u6e43\u96c4\u81f3\u6b64\uff0c\u624d\u5598\u8fc7\u4e00\u53e3\u6c14\u6765\uff0c\u7eb7\u7eb7\u53f9\u611f\uff0c\u6000\u7740\u5f02\u6837\u7684\u5fc3\u60c5\u51c6\u5907\u6563\u53bb\u2026\u2026 \u53ef\u4e5f\u6709\u4e0d\u5c11\u4eba\u6697\u6697\u8d77\u7591\uff1a\u201c\u96be\u9053\u4ed6\u771f\u7684\u4f1a\u628a\u8fd9\u6b66\u6797\u4eba\u7269\u89c6\u4e3a\u7470\u5b9d\u7684\u5929\u4e0b\u7b2c\u4e00\u5947\u4e66\u6bc1 \u53bb\uff1f\u2026\u2026\u201d \u201c\u4f1a\u4e0d\u4f1a\u6709\u5047\uff1f\u2026\u2026\u201d\u201c\u2026\u2019\u4f46\u8c01\u4e5f\u6ca1\u6709\u628a\u5fc3\u4e2d\u7684\u7591\u5ff5\u8bf4\u51fa\u6765\uff01 \u201c\u7aef\u65b9\u516c\u5b50\u201d\u5446\u82e5\u6728\u9e21\uff0c\u4ed6\u4e00\u4e07\u4e2a\u4e5f\u60f3\u4e0d\u5230\u5c55\u767d\u4f1a\u5f53\u7740\u7fa4\u96c4\u4e4b\u9762\uff0c\u628a\u6b66\u6797\u4eba\u7269\u68a6\u5bd0\u6c42\u4e4b \u7684\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\u6bc1\u53bb\uff01\u81f3\u6b64\uff0c\u4ed6\u4e00\u5207\u8ba1\u5212\u843d\u4e86\u7a7a\uff0c\u5fc3\u4e2d\u7684\u5931\u671b\u4e4b\u611f\uff0c\u6bd4\u4efb\u4f55\u4eba\u6765\u5f97\u5f3a \u70c8\uff01 \u7a81\u5730\u4ed6\u5fc3\u4e2d\u4e00\u52a8\uff0c\u8131\u53e3\u95ee\u9053\uff1a\u201c\u5c55\u5144\uff01\u6b64\u4e66\u4f1a\u4e0d\u4f1a\u6709\u526f\u672c\uff1f\u2026\u2026\u201d \u5c55\u767d\u5fc3\u4e2d\u5fae\u6120\uff0c\u60f3\u4e0d\u5230\u6b64\u4eba\u4efb\u4e00\u6d3e\u638c\u95e8\u4e4b\u5c0a\uff0c\u7ade\u5982\u6b64\u591a\u7591\uff1f \u4e0d\u7531\u51b7\u7b11\u9053\uff1a\u201c\u526f\u672c\u5012\u6709\uff0c\u53ea\u662f\u5728\u5c55\u767d\u5fc3\u4e2d\uff01\u83ab\u975e\u8d35\u516c\u5b50\u5c1a\u6709\u628a\u5c55\u767d\u5256\u8179\u6316\u5fc3\u4e4b\u610f \u5417\uff1f\u201d \u201c\u7aef\u65b9\u516c\u5b50\u201d\u5c34\u5c2c\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5c55\u5144\u8bf4\u7b11\u8bdd\u4e86\uff01\u672c\u638c\u95e8\u53ea\u4e0d\u8fc7\u8ba4\u4e3a\u5982\u6b64\u5947\u4e66\uff0c\u6bc1\u4e86\u53ef \u60dc\uff0c\u968f\u53e3\u95ee\u4e00\u53e5\u800c\u5df2\uff01\u201d\u5c55\u767d\u7a81\u5730\u811a\u6b65\u524d\u4e09\u540e\u4e94\uff0c\u8eab\u5f62\u98d8\u98de\u5982\u7535\uff0c\u500f\u5ffd\u4e4b\u95f4\uff0c\u63a5\u8fde\u5288\u51fa\u56db \u638c\uff01 \u638c\u98ce\u5578\u5c18\uff0c\u58f0\u52bf\u9a87\u4eba\uff01\u4f46\u4ed6\u53ef\u90fd\u662f\u5411\u7a7a\u5904\u800c\u53d1\u7684\u3002 \u9976\u5c31\u8fd9\u6837\uff0c\u7ad9\u7acb\u4ed6\u9644\u8fd1\u7684\u201c\u7aef\u65b9\u516c\u5b50\u201d\u53ca\u201c\u98ce\u5c18\u4e09\u4e10\u201d\u4e5f\u4e00\u9f50\u9a87\u5f97\u60ca\u547c\u66b4\u9000\u4e8c\u4e08\u5f00\u5916\uff01 \u695e\u4e86\uff01\u6240\u6709\u7684\u4eba\u90fd\u695e\u4e86\uff01\u4e0d\u77e5\u5c55\u767d\u7a81\u5c55\u7edd\u5b66\u662f\u4f55\u7528\u610f\uff1f \u7b2c\u4e94\u5341\u4e5d\u7ae0\u3000\u201c\u6c5f\u5357\u4e8c\u5947\u201d>> \u53e4\u9f99\u300a\u5251\u5ba2\u884c\u300b \u7b2c\u4e94\u5341\u4e5d\u7ae0\u3000\u201c\u6c5f\u5357\u4e8c\u5947\u201d \u5c55\u767d\u914d\u5408\u201c\u5343\u5e7b\u98d8\u9999\u6b65\u201d\u3001\u201c\u65e0\u8272\u65e0\u76f8\u8eab\u201d\uff0c\u500f\u7136\u5288\u51fa\u56db\u638c\uff0c\u731b\u5730\u6536\u6ce8\u52bf\u5b50\u6717\u58f0\u9053\uff1a \u201c\u8fd9\u662f\u2018\u5929\u4f5b\u5f55\u201d\u4e00\u62db\u7edd\u5b66\uff0c\u2018\u4f5b\u5149\u666e\u7167\u2019\uff0c\u600e\u4e48\u6837\uff1f\u53ef\u4ee5\u4ee4\u8d35\u638c\u95e8\u53c2\u8be6\u51e0\u5929\u7684\u4e86\u5427\uff1f\u201d \u8a00\u4e0b\u4e4b\u610f\u662f\uff1a\u201c\u4f60\u4e0d\u8981\u8d2a\u5fc3\u5984\u60f3\uff0c\u5438\u6536\u4e98\u53e4\u7edd\u5b66\u5e76\u4e0d\u90a3\u4e48\u5bb9\u6613\uff01\u201d \u5c55\u767d\u89c1\u4f17\u4eba\u77aa\u7740\u773c\u775b\u53d1\u6014\uff0c\u53c8\u63a5\u8fde\u65bd\u51fa\u4e09\u62db\uff0c\u201c\u4f5b\u7956\u964d\u751f\u201d\u3001\u201c\u626b\u6e05\u5996\u6c14\u201d\u3001\u201c\u98ce\u9707\u96f7 \u9e23\u201d\u3002 \u4ed6\u201c\u5929\u4f5b\u964d\u9b54\u201d\u4e0e\u201c\u98ce\u96f7\u516b\u638c\u201d\u540c\u65f6\u5e76\u7528\uff0c\u4e09\u62db\u8fde\u73af\u65bd\u51fa\uff0c\u53ea\u89c1\u638c\u98ce\u5c71\u6d8c\uff0c\u72b9\u5982\u6012\u9f99\u95f9 \u6d77\uff0c\u5e73\u5730\u5377\u8d77\u4e00\u80a1\u72c2\u98d9\uff0c\u5c18\u5934\u6380\u8d77\u8db3\u6709\u5341\u6570\u4e08\u4e4b\u9ad8\uff01 \u8fd9\u54ea\u91cc\u662f\u7ec3\u4e60\u6b66\u529f\uff0c\u51e0\u5982\u5e73\u5730\u522e\u8d77\u4e00\u4e2a\u9f99\u5377\u98ce\uff0c\u98ce\u58f0\u96f7\u543c\uff0c\u58f0\u52bf\u60ca\u4eba\uff01 \u628a\u90a3\u4e9b\u81ea\u547d\u4e0d\u51e1\u7684\u6b66\u6797\u4eba\u7269\uff0c\u770b\u5f97\u4e2a\u4e2a\u548b\u820c\u3001\u5fc3\u60b8\uff0c\u8fd9\u79cd\u524d\u6240\u672a\u89c1\u7684\u7edd\u5f3a\u638c\u529f\u2026\u2026 \u5c55\u767d\u6536\u4f4f\u52bf\u5b50\uff0c\u89c1\u4f17\u4eba\u832b\u7136\u5730\u671b\u7740\u4ed6\uff0c\u4e0d\u7531\u6447\u5934\u4e00\u53f9\u9053\uff1a\u201c\u9ad8\u5f3a\u6b66\u529f\uff0c\u5e76\u975e\u5e78\u81f4\uff0c\u6211\u4eec \u8fd8\u662f\u4ece\u5934\u6765\u5427\uff01\u201d \u63a5\u7740\uff0c\u4ed6\u6b63\u895f\u5371\u5750\uff0c\u72b9\u5982\u8001\u50e7\u8bf4\u6cd5\uff0c\u53e3\u4e2d\u4f4e\u8bf5\u9053\uff1a\u201c\u6c14\uff0c\u6df7\u6df9\u6e05\u6d4a\uff0c\u6e05\u5347\u6d4a\u964d\uff0c\u8fd9\u4e00\u6cd5 \u4f17\u2026\u2026\u201d \u5c55\u767d\u7adf\u5f00\u59cb\u8bb2\u6388\u5929\u4e0b\u7b2c\u4e00\u5947\u4e66\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\u4e0a\u7684\u5965\u5999\u7ecf\u6587\u4e86\uff01 \u5929\u4e0b\u7fa4\u7ef4\u8eab\u4e0d\u7531\u5df1\u5730\u7eb7\u7eb7\u56f4\u4e86\u4e0a\u6765\uff0c\u51dd\u795e\u503e\u8033\uff0c\u5fc3\u65e0\u65c1\u7528\u5730\u8c08\u542c\u4e0b\u53bb\uff0c\u53ea\u542c\u5c55\u767d\u7ee7\u7eed \u9053\uff1a\u201c\u2026\u2026\u6e05\u8005\u4e3a\u5929\uff0c\u6d4a\u8005\u4e3a\u5730\uff0c\u5929\u5730\u4ea4\u6cf0\uff0c\u4eba\u795e\u5408\u4e00\u2026\u2026\u201d \u542c\u8bb2\u7684\u4eba\uff0c\u90fd\u662f\u6b66\u6797\u55dc\u5bbf\uff0c\u5c31\u7b97\u5e74\u8f7b\u4eba\u4e5f\u5747\u6709\u4e0d\u5f31\u7684\u6b66\u529f\u9020\u8be3\uff0c\u5927\u5bb6\u542c\u5c55\u767d\u8bb2\u7684\uff0c\u7adf\u662f \u95fb\u6240\u672a\u95fb\u7684\u5947\u5965\u6b66\u5b66\uff0c\u8ddf\u81ea\u5df2\u5f80\u6614\u6240\u5b66\u7684\u4e00\u6bd4\uff0c\u771f\u662f\u5224\u82e5\u4e91\u6ce5\uff0c\u4e0d\u7531\u4e00\u4e2a\u4e2a\u5982\u9189\u5982\u75f4\uff0c\u6d51\u7136 \u7adf\u6210\uff0c\u628a\u5168\u526f\u5fc3\u795e\u90fd\u6c89\u6d78\u5728\u5176\u4e2d\u4e86\uff01 \u5c55\u767d\u4e0d\u5f90\u4e0d\u75be\u7684\u53e5\u5b50\uff0c\u4e0d\u4f4e\u4e0d\u6602\u7684\u8bed\u8c03\uff0c\u9ed8\u8bf5\u4e00\u822c\u505a\u6cd5\uff0c\u8bed\u8bed\u73e0\u73b0\uff0c\u5b57\u5b57\u91d1\u7389\uff0c\u4f7f\u4f17\u4eba \u5982\u996e\u9187\u9192\uff0c\u5982\u555c\u743c\u6d46\uff0c\u7b80\u76f4\u5e9f\u5bdd\u5fd8\u98df\u4e86\uff01 \u8fd9\u6837\uff0c\u5c55\u767d\u8bb2\u6388\u4e86\u4e03\u5929\uff0c\u4f17\u4eba\u8fb9\u542c\u8fb9\u505a\uff0c\u6309\u5c55\u767d\u6240\u6388\u5fc3\u6cd5\u6765\u4fee\u4e60\u5185\u529f\uff0c\u65e0\u6bd4\uff0c\u4f17\u4eba\u5fc3\u91cc \u4e0d\u7981\u5174\u8d77\u540c\u4e00\u7684\u611f\u89c9\uff0c\u90a3\u5c31\u662f\u4ee5\u524d\u4e60\u6b66\uff0c\u591a\u8d70\u4e86\u4e0d\u5c11\u51a4\u6789\u8def\u2026\u2026 \u5176\u4e2d\u5c24\u4ee5\u96f7\u5927\u53d4\u3001\u6155\u5bb9\u7ea2\u3001\u5a49\u513f\u3001\u91d1\u5f69\u51e4\u53ca\u6a0a\u7d20\u9e3e\u7b49\u51e0\u4eba\uff0c\u8fdb\u5883\u66f4\u8fdc\u8d85\u8fc7\u522b\u4eba\uff01 \u56e0\u96f7\u5927\u53d4\u5df2\u4fee\u4e60\u8fc7\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\uff0c\u4e14\u4fee\u4e60\u7684\u5e74\u5934\u751a\u4e45\uff0c\u53ea\u662f\u6ca1\u6709\u5c55\u767d\u7ecf\u8fc7\u67f3\u7fe0\u7fe0 \u4ee5\u201c\u59f9\u5973\u8ff7\u9b42\u5927\u6cd5\u201d\u914d\u5408\u53c2\u609f\u6765\u5f97\u900f\u5f7b\uff0c\u6709\u5f88\u591a\u65e0\u6cd5\u8d2f\u7565\u4e00\u89e3\u91ca\uff0c\u7acb\u523b\u604d\u7136\u5927\u609f\uff0c\u56e0\u4e4b\uff0c\u8fdb \u5883\u662f\u4e00\u65e5\u5343\u91cc\uff01 \u539f\u6765\u201c\u53ea\u773c\u90ce\u541b\u201d\u7ed8\u5236\u8fd9\u672c\u5929\u4e0b\u7b2c\u4e00\u5947\u4e66\u65f6\uff0c\u5728\u90a3\u4e9b\u5996\u5a06\u65e0\u6bd4\u7684\u8d64\u88f8\u5973\u50cf\u4e0a\u9762\uff0c\u8fd8\u8574\u84c4 \u4e86\u4e0d\u5c11\u7384\u79d8\uff0c\u4e0d\u7ecf\u5f53\u4e8b\u4eba\u70b9\u900f\uff0c\u5149\u5728\u7ecf\u6587\u4e0a\u4e0b\u6b7b\u529f\u592b\u662f\u4e0d\u591f\u7684\uff01 \u8fd9\u6050\u6015\u662f\u96f7\u5927\u53d4\u505a\u68a6\u4e5f\u6ca1\u6709\u60f3\u5230\u7684\uff0c\u4ed6\u8d60\u7ed9\u5c55\u767d\u90a3\u672c\u79d8\u5f55\uff0c\u53cd\u4ece\u5c55\u767d\u90a3\u5f97\u6765\u4e86\u597d\u5904\uff01 \u53ef\u89c1\u4e16\u4e8b\u4e00\u996e\u4e00\u5544\uff0c\u83ab\u975e\u524d\u5b9a\uff1f \u81f3\u4e8e\u6155\u5bb9\u7ea2\u3001\u5a49\u513f\uff0c\u91d1\u5f69\u51e4\u3001\u6a0a\u7d20\u9e3e\u56db\u4eba\u8fdb\u5883\u6bd4\u522b\u4eba\u5feb\u7684\u539f\u56e0\uff0c\u662f\u56e0\u5979\u56db\u4eba\u6b7b\u5fc3\u584c\u5730\u5730 \u4fe1\u8d56\u5c55\u767d\uff01 \u5e38\u8a00\u9053\uff1a\u201c\u5fc3\u8bda\u5219\u7075\u201d\uff0c\u53c8\u9053\u662f\u2018\u7cbe\u8bda\u542c\u81f3\uff0c\u91d1\u77f3\u4e3a\u5f00\uff0c\u56e0\u4e3a\u5979\u56db\u4eba\u4e00\u5fc3\u4e00\u610f\u4fe1\u8d56\u5c55 \u767d\uff0c\u628a\u5c55\u767d\u7684\u6bcf\u4e00\u53e5\u8bdd\u90fd\u5f53\u505a\u91d1\u79d1\u7389\u5f8b\uff0c\u6beb\u65e0\u4e00\u70b9\u7591\u95ee\u5730\u7167\u7740\u53bb\u505a\uff0c\u6240\u4ee5\u8fdb\u6b65\u5c31\u5feb\u4e86\u3002 \u5c55\u767d\u4f20\u6388\u4f17\u4eba\u6b66\u5b66\u79d8\u5f55\uff0c\u8fd9\u5df2\u662f\u7b2c\u516b\u5929\u5934\u4e0a\uff0c\u5929\u665a\u65f6\u4ed6\u56de\u623f\u7761\u89c9\uff0c\u5c1a\u672a\u5c31\u5bdd\uff0c\u7a81\u542c\u7a97\u5916 \u6709\u8863\u631f\u7834\u7a7a\u4e4b\u58f0\uff01 \u8fd9\u58f0\u97f3\u6781\u5fae\uff0c\u5f88\u50cf\u6a90\u524d\u9635\u98ce\uff0c\u4f46\u5374\u7792\u4e0d\u8fc7\u5c55\u767d\u7684\u8033\u76ee\uff0c\u5c55\u767d\u6b64\u65f6\u529f\u529b\uff0c\u5728\u65b9\u5706\u4e2d\u6570\u4e08\u4ee5 \u5185\uff0c\u5c31\u662f\u98de\u82b1\u843d\u53f6\u4e4b\u58f0\uff0c\u4e5f\u53ef\u6e05\u6670\u95fb\u5230\uff01 \u56e0\u6b64\uff0c\u8fd9\u8863\u631f\u7834\u98ce\u4e4b\u58f0\u867d\u7136\u6781\u5fae\uff0c\u4f46\u5c55\u767d\u5df2\u7ecf\u53d1\u89c9\uff0c\u4ee5\u4e3a\u4ecd\u50cf\u524d\u6b21\u4e00\u6837\uff0c\u6709\u4eba\u4e0d\u6b7b\u5fc3\uff0c \u53c8\u5411\u4ed6\u6253\u4ec0\u4e48\u6b79\u4e3b\u610f\u6765\u4e86\uff1f\u53ea\u89c1\u4ed6\u8eab\u5f62\u4e00\u95ea\uff0c\u51e0\u4e4e\u4ee5\u8089\u773c\u96be\u89c1\u7684\u901f\u5ea6\uff0c\u5df2\u4ece\u623f\u5185\u63a0\u51fa\uff01 \u5c55\u767d\u63a0\u51fa\u7a97\u5916\uff0c\u51ed\u7a7a\u500f\u89c1\u4e24\u6761\u9ed1\u5f71\u4f3c\u6d41\u661f\u75be\u5c04\uff0c\u5728\u773c\u524d\u4e00\u6643\uff0c\u5df2\u98d8\u843d\u5728\u9662\u5899\u4e4b\u5916\uff01 \u5c55\u767d\u201c\u65e0\u8272\u65e0\u76f8\u8eab\u201d\u6cd5\u5df2\u81f3\u8ffd\u98ce\u65e0\u5f71\u5730\u6b65\uff0c\u968f\u540e\u8ffd\u4e86\u53bb\uff0c\u53ea\u4e09\u4e94\u4e2a\u8d77\u843d\uff0c\u5df1\u8feb\u4e86\u4e2a\u9996\u5c3e \u76f8\u8fde\uff01 \u661f\u6708\u5fae\u5149\u4e0b\uff0c\u5c55\u767d\u5df2\u770b\u51fa\u5728\u524d\u5954\u8dd1\u7684\u4e8c\u4eba\uff0c\u7adf\u662f\u8eab\u8eaf\u5a07\u5c0f\u7684\u4e24\u4e2a\u5c11\u5973\uff01 \u5c55\u767d\u6697\u6697\u7eb3\u95f7\uff0c\u9ed1\u591c\u4e4b\u95f4\uff0c\u4e24\u4e2a\u5c11\u5973\u5230\u8352\u91ce\u53bb\u6709\u4ec0\u4e48\u4e8b\u505a\uff1f \u90a3\u4e24\u6761\u5a07\u5c0f\u4eba\u5f71\uff0c\u5230\u4e86\u4e00\u7247\u6811\u6797\u8fb9\u4e0a\u505c\u4e86\u4e0b\u6765\uff0c\u5c55\u767d\u63a9\u81f3\u6811\u540e\u4e00\u671b\uff0c\u539f\u6765\u662f\u6155\u5bb9\u7ea2\u4e0e\u5c55 \u5a49\u513f\uff01 \u5c55\u767d\u66f4\u89c9\u5947\u602a\uff0c\u5979\u4eec\u59d0\u59b9\u4fe9\u4e2a\uff0c\u9ed1\u591c\u4e4b\u95f4\u8dd1\u5230\u8352\u91ce\u5bc6\u6797\u6765\u505a\u4ec0\u4e48\uff1f \u6709\u4e86\u8fd9\u7591\u95ee\uff0c\u5c55\u81ea\u53ef\u5c31\u6ca1\u9732\u9762\uff0c\u9690\u8eab\u6811\u540e\uff0c\u4e00\u89c2\u7a76\u7adf\u3002\u53ea\u542c\u5f97\u6155\u5bb9\u7ea2\u5657\u54e7\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u59b9\u59b9\uff01\u4f60\u628a\u6211\u5f15\u5230\u8fd9\u91cc\u6765\u5e72\u4ec0\u4e48\uff1f\u201d \u5a49\u513f\u4f3c\u662f\u624d\u53d1\u89c9\u5230\u8ffd\u6765\u7684\u591c\u884c\u4eba\uff0c\u7adf\u662f\u59d0\u59d0\uff0c\u5f53\u5373\u4e00\u695e\uff0c\u201c\u554a\u201d\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6211\u4e0d\u77e5 \u662f\u59d0\u59d0\uff01\u6709\u4ec0\u4e48\u4e8b\u5417\uff1f\u201d \u6155\u5bb9\u7ea2\u54c8\u54c8\u7b11\u9053\uff1a\u59b9\u59b9\uff01\u8bf4\u771f\u5fc3\u8bdd\uff0c\u4f60\u5f88\u7231\u5c55\u54e5\u54e5\u5427\uff1f\u201d \u5a49\u513f\u60f3\u4e0d\u5230\u59d0\u59d0\u4f1a\u5f00\u95e8\u89c1\u5c71\u5730\u4e00\u8bed\u9053\u51fa\u81ea\u5df2\u7684\u5fc3\u4e8b\uff0c\u53c8\u60f3\u5230\u59d0\u59d0\u4e43\u662f\u5c55\u54e5\u54e5\u7684\u672a\u5a5a\u59bb\uff0c \u4e0d\u7531\u79c0\u8138\u901a\u7ea2\uff0c\u6025\u9053\uff1a\u201c\u59d0\u59d0\uff01\u4f60\u4e0d\u8981\u4e71\u731c\uff01\u6211\u2026\u2026\u6211\u2026\u2026\u201d \u4f46\u662f\u5979\u80fd\u8fdd\u5fc3\u800c\u8bba\uff0c\u5426\u8ba4\u81ea\u5df1\u7231\u5c55\u767d\u5417\uff1f \u56e0\u6b64\uff0c\u5979\u201c\u6211\u201d\u4e86\u534a\u5929\uff0c\u518d\u4e5f\u5450\u5450\u5730\u63a5\u4e0d\u4e0b\u53bb\u3002 \u6155\u5bb9\u7ea2\u4e24\u53ea\u6c34\u6c6a\u6c6a\u7684\u660e\u7741\uff0c\u6df1\u60c5\u6b3e\u6b3e\u5730\u671b\u7740\u59b9\u59b9\uff0c\u4eb2\u5207\u4e00\u7b11\uff0c\u4f38\u51fa\u7389\u624b\u62c9\u4f4f\u5a49\u513f\u7684\u7ed3 \u8155\uff0c\u4ee5\u65e0\u6bd4\u6e29\u67d4\u7684\u8bed\u8c03\u8bf4\u9053\uff1a\u201c\u59b9\u59b9\uff0c\u4f60\u4e0d\u8981\u7792\u6211\u4e86\uff01\u4ece\u8fd9\u51e0\u5929\u7684\u60c5\u5f62\uff0c\u6211\u5df2\u770b\u51fa\u4f60\u662f\u4e07\u5206 \u7231\u7740\u5c55\u54e5\u54e5\u7684\uff01\u800c\u4e14\uff0c\u6211\u4e5f\u662f\u6700\u8fd1\u51e0\u5929\u624d\u53d1\u89c9\uff0c\u4f60\u7231\u5c55\u54e5\u54e5\uff0c\u53ef\u80fd\u6bd4\u6211\u8fd8\u8981\u65e9\uff0c\u5728\u5c55\u54e5\u54e5\u7b2c \u4e00\u6b21\u5230\u6211\u5bb6\u4e2d\uff0c\u4f60\u627e\u6211\u53bb\u5411\u5f1f\u5f1f(\u51cc\u98ce\u516c\u5b50)\u8bf4\u60c5\uff0c\u4e0d\u8981\u628a\u5c55\u54e5\u54e5\u8d76\u8d70\u2026\u2026\u59b9\u59b9\uff01\u4ece\u90a3\u65f6\u4f60\u4fbf \u7231\u4e0a\u5c55\u54e5\u54e5\u4e86\uff0c\u5bf9\u4e0d\u5bf9\uff1f\u201d \u5a49\u513f\u8138\u5b54\u7f9e\u7ea2\u5f97\u5982\u5927\u7ea2\u5e03\u4e00\u822c\uff0c\u53ea\u4efb\u59d0\u59d0\u7275\u7740\u624b\u513f\uff0c\u5782\u5934\u9ed8\u7136\u65e0\u8bed\u3002 \u6155\u5bb9\u7ea2\u53c8\u9053\uff1a\u201c\u6211\u53ea\u662f\u5947\u602a\uff0c\u81ea\u5df1\u600e\u4e48\u5982\u6b64\u7c97\u5fc3\u5927\u610f\uff0c\u8fd9\u4e48\u91cd\u5927\u7684\u4e8b\uff0c\u7ade\u672a\u80fd\u63d0\u65e9\u53d1 \u73b0\uff1f\u5982\u679c\u65e9\u77e5\u9053\u59b9\u59b9\u7231\u4e0a\u5c55\u54e5\u54e5\uff0c\u59d0\u59d0\u4e5f\u4e0d\u4f1a\u505a\u51fa\u9519\u4e8b\u2026\u2026\u201d \u6155\u5bb9\u7ea2\u8bf4\u81f3\u6b64\u5904\uff0c\u5a49\u513f\u7a81\u5730\u201c\u5624\u201d\u7136\u4e00\u58f0\u60b2\u6ce3\uff0c\u6323\u8131\u88ab\u6155\u5bb9\u7ea2\u63e1\u4f4f\u7684\u624b\uff0c\u98de\u8eab\u5411\u56de\u8def\u63a0 \u53bb\uff01 \u6155\u5bb9\u7ea2\u6025\u547c\u9053\uff1a\u201c\u59b9\u59b9\uff01\u201d \u88ab\u8fd9\u4e00\u559d\uff0c\u5a49\u513f\u6536\u4f4f\u6025\u53bb\u4e4b\u52bf\uff0c\u4f46\u4ecd\u80cc\u8138\u7ad9\u7740\uff0c\u9999\u80a9\u8d77\u4f0f\uff0c\u5df2\u7ecf\u4f24\u5fc3\u5f97\u54ed\u4e86\uff01 \u6155\u5bb9\u7ea2\u6025\u63a0\u81f3\u5a49\u513f\u8eab\u8fb9\uff0c\u53c8\u6280\u4f4f\u5a49\u513f\u7d20\u624b\uff0c\u4ee5\u540c\u80de\u59d0\u59d0\u5929\u6027\u7684\u81f3\u7231\uff0c\u8f7b\u8f7b\u9053\uff1a\u201c\u59b9\u59b9\uff0c \u4f60\u4e0d\u8981\u4f24\u5fc3\uff0c\u542c\u59d0\u59d0\u8bf4\u597d\u5417\uff1f\u201d \u5a49\u513f\u7a81\u5730\u5931\u58f0\u75db\u54ed\uff0c\u53cd\u8eab\u6251\u5728\u59d0\u59d0\u6000\u5185\uff0c\u54ed\u9053\uff1a\u201c\u59d0\u59d0\uff01\u6211\u5bf9\u4e0d\u8d77\u4f60\uff01\u2026\u2026\u201d \u5a49\u513f\u54ed\u5f97\u60b2\u5207\uff0c\u6155\u5bb9\u7ea2\u60f3\u8d77\u5bb6\u5ead\u60e8\u53d8\uff0c\u59d0\u59b9\u76f8\u4f9d\u4e3a\u547d\uff0c\u4e0d\u7531\u4e5f\u6d41\u4e0b\u6cea\u6765\u2026\u2026 \u9690\u8eab\u6811\u540e\u7684\u5c55\u767d\uff0c\u53ea\u89c9\u5fc3\u5982\u5200\u6512\uff0c\u4ed6\u6697\u6697\u81ea\u8d23\uff1a\u201c\u5c55\u767d\u5440\uff01\u5c55\u767d\uff01\u4f60\u53ea\u56e0\u4e00\u65f6\u6c14\u6124\uff0c\u7b54 \u5e94\u4e86\u6155\u5bb9\u7ea2\u7684\u4eb2\u4e8b\uff0c\u4f60\u53c8\u600e\u4e48\u5bf9\u5f97\u8d77\u60c5\u6df1\u4f3c\u6d77\u7684\u5a49\u513f\uff1f\u2026\u2026\u201d \u6155\u5bb9\u7ea2\u7a81\u5730\u60f3\u8d77\u81ea\u5df1\u7684\u7528\u610f\uff0c\u5fd9\u62ed\u53bb\u8138\u4e0a\u7684\u6cea\u6c34\uff0c\u7528\u624b\u6276\u8d77\u6000\u4e2d\u7684\u5a49\u513f\uff0c\u8c03\u51fa\u4e1d\u5dfe\u4e3a\u5a49 \u513f\u64e6\u53bb\u8138\u4e0a\u7684\u6cea\u75d5\uff0c\u9053\uff1a\u201c\u59b9\u59b9\u522b\u54ed\uff01 \u59d0\u59d0\u8fd8\u6709\u8bdd\u8bf4\uff01\u201d \u6155\u5bb9\u7ea2\u4ee5\u65e0\u6bd4\u4eb2\u5207\u7684\u8bed\u8c03\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u5c31\u662f\u4eb2\u59d0\u59b9\u4e8c\u4eba\uff0c\u867d\u5c1a\u6709\u4e00\u4e2a\u5144\u5f1f\uff0c\u53ef\u662f\u4ed6\u81ea\u5e7c \u4e0e\u6211\u4eec\u6027\u60c5\u4e0d\u6295\uff0c\u4e0d\u662f\u8ddf\u6211\u5435\uff0c\u5c31\u662f\u8ddf\u4f60\u6253\u3002\u5982\u4eca\uff0c\u7236\u4eb2\u53c8\u88ab\u4eba\u6740\u5bb3\uff0c\u5bb6\u9053\u5927\u4e0d\u5982\u524d\uff0c\u4ece\u6b64 \u4ee5\u540e\u6211\u4eec\u59d0\u59b9\u66f4\u8981\u76f8\u4f9d\u4e3a\u547d\uff0c\u6c38\u8fdc\u76f8\u4eb2\u76f8\u7231\u5728\u4e00\u8d77\uff0c\u59b9\u59b9\uff0c\u4f60\u7b54\u5e94\u6211\u5417\uff1f\u201d \u5a49\u513f\u4e0d\u77e5\u59d0\u59d0\u5c45\u5fc3\uff0c\u89c1\u59d0\u59d0\u8bf4\u5f97\u6073\u5207\uff0c\u8fde\u8fde\u70b9\u5934\u3002 \u6155\u5bb9\u7ea2\u5b89\u6170\u5730\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u59b9\u59b9\u65e2\u662f\u4e5f\u7231\u5c55\u54e5\u54e5\uff0c\u90a3\u6211\u4eec\u59d0\u59b9\u4e3a\u4e86\u6c34\u4e0d\u5206\u79bb\uff0c\u6211\u4eec\u5c31\u5171 \u6548\u2018\u5a25\u7687\u5973\u82f1\u2019\uff0c\u4e00\u540c\u5ac1\u7ed9\u5c55\u54e5\u54e5\uff0c\u5171\u4e8b\u4e00\u592b\uff0c\u59b9\u59b9\u610f\u4e0b\u5982\u4f55\uff1f\u201d \u6155\u5bb9\u7ea2\u6b64\u8bdd\u4e00\u51fa\uff0c\u4e0d\u4f46\u5a49\u513f\u5403\u60ca\uff0c\u5c31\u8fde\u9690\u8eab\u6811\u540e\u7684\u5c55\u767d\uff0c\u4e5f\u4e0d\u7531\u5927\u5927\u5730\u4e00\u9707\uff01 \u5a49\u513f\u62ac\u8d77\u5934\u6765\uff0c\u4ee5\u4e00\u53cc\u660e\u4e3d\u7684\u5927\u773c\u775b\u6014\u671b\u4e86\u59d0\u59d0\u534a\u5929\uff0c\u89c1\u59d0\u59d0\u8138\u4e0a\u6148\u7231\u7684\u5149\u8f89\u53ca\u8bda\u631a\u7684 \u7b11\u5bb9\uff0c\u77e5\u9053\u4e0d\u662f\u8ddf\u5979\u8bf4\u7740\u73a9\u513f\uff0c\u5fc3\u4e2d\u4e0d\u7531\u4e00\u9635\u72c2\u8df3\uff0c\u4f46\u500f\u7136\u53c8\u628a\u5e26\u6cea\u7684\u7c89\u8138\u57cb\u5728\u59d0\u59d0\u6000\u4e2d\uff0c \u60c5\u4e0d\u81ea\u7981\u5730\u547c\u4e86\u58f0\uff1a\u201c\u59d0\u59d0\uff01\u2026\u2026\u201d\u5c31\u518d\u4e5f\u64d2\u4e0d\u8d77\u5934\u6765\uff01 \u867d\u7136\u5979\u672a\u4eb2\u53e3\u8bf4\u51fa\u7b54\u5e94\u59d0\u59d0\u7684\u63d0\u8bae\uff0c\u4f46\u4ece\u5979\u7684\u6fc0\u52a8\u4e4b\u60c5\uff0c\u53ca\u773c\u91cc\u95ea\u8000\u7684\u60ca\u559c\u5149\u8f89\uff0c\u6155\u5bb9 \u7ea2\u5df2\u77e5\u59b9\u59b9\u9ed8\u5141\u4e86\uff0c\u5fc3\u5934\u4e00\u9635\u8f7b\u677e\uff0c\u8ddf\u7740\u60c5\u7eea\u4e5f\u597d\u8f6c\u8d77\u6765\uff0c\u5979\u89c9\u5f97\u51e0\u65e5\u6765\u60ac\u5728\u5fc3\u4e2d\u7684\u6b7b\u7ed3\uff0c \u603b\u7b97\u8ba9\u81ea\u5df1\u6240\u60f3\u7684\u529e\u6cd5\u89e3\u5f00\u4e86\u3002\u53c8\u5174\u8d77\u987d\u5fc3\uff0c\u7b11\u63fd\u7740\u5a49\u513f\u7684\u7ec6\u8170\uff0c\u8bf4\u9053\uff1a \u201c\u8fd9\u662f\u59b9\u59b9\u7684\u7ec8\u8eab\u5927\u4e8b\uff0c\u5982\u679c\u4f7f\u59b9\u59b9\u53d7\u4e86\u59d4\u5c48\uff0c\u59d0\u59d0\u53ef\u4e0d\u613f\u610f\u3002\u73b0\u5728\u8981\u59b9\u59b9\u4eb2\u53e3\u7b54\u5e94\uff0c \u4f60\u662f\u4e0d\u662f\u540c\u610f\u4e86\u59d0\u59d0\u8fd9\u4e2a\u529e\u6cd5\uff1f\u201d \u5a49\u513f\u5934\u4e5f\u4e0d\u62ac\uff0c\u53ea\u4e00\u5473\u5730\u62b1\u7740\u6155\u5bb9\u7ea2\u53eb\u59d0\u59d0\uff0c\u4f46\u4ece\u5979\u7684\u58f0\u97f3\u4e2d\uff0c\u5df2\u53ef\u542c\u51fa\u6ca1\u6709\u4e00\u4e1d\u60b2\u4f24 \u7684\u6210\u4efd\uff0c\u800c\u662f\u5145\u6ee1\u4e86\u6b23\u559c\uff01 \u6155\u5bb9\u7ea2\u5374\u6210\u5fc3\u9017\u5979\uff0c\u4e00\u4e2a\u52b2\u5730\u95ee\u9053\uff1a\u4f60\u5012\u662f\u7b54\u5e94\u4e0d\u7b54\u5e94\uff0c\u53ea\u4e00\u5473\u5730\u53eb\u59d0\u59d0\u5e72\u4ec0\u4e48\uff1f\u201d \u5979\u89c1\u5a49\u513f\u4ecd\u57cb\u5934\u5728\u5979\u6000\u5185\uff0c\u53c8\u9053\uff1a\u201c\u4f60\u8981\u4e0d\u613f\u610f\u5c31\u7b97\u4e86\uff01\u6211\u53bb\u544a\u8bc9\u5c55\u54e5\u54e5\uff0c\u8bf4\u4f60\u6839\u672c\u4e0d \u7231\u4ed6\u2026\u2026\u201d \u5a49\u513f\u5a07\u8eaf\u4e00\u626d\uff0c\u53cc\u624b\u626f\u4f4f\u6155\u5bb9\u7ea2\u7684\u8863\u895f\u9053\uff1a\u201c\u59d0\u59d0\uff01\u4f60\u597d\u574f\uff01\u2026\u2026\u201d \u6155\u5bb9\u7ea2\u9053\uff1a\u201c\u597d\uff01\u4f60\u65e2\u662f\u9a82\u6211\uff0c\u5c31\u662f\u8868\u793a\u4e0d\u613f\u610f\uff0c\u6211\u8fd9\u5c31\u544a\u8bc9\u5c55\u54e5\u54e5\u53bb\u2026\u2026\u201d \u8bf4\u7740\u63a8\u5f00\u5a49\u513f\uff0c\u4e3e\u6b65\u5c31\u8d70\uff01 \u201c\u59d0\u2014\u2014\u59d0\uff01\u2026\u2026\u201d \u5a49\u513f\u4e00\u58f0\u5a07\u547c\uff0c\u5979\u5fc3\u4e2d\u660e\u767d\u662f\u59d0\u59d0\u6210\u5fc3\u5455\u5979\uff0c\u6b63\u60f3\u518d\u4e0e\u59d0\u59d0\u6492\u5a07\uff0c\u7a81\u7136\u4e00\u62ac\u5934\uff0c\u4e0d\u7531\u60ca \u5f97\u62bd\u4e86\u4e00\u53e3\u51b7\u6c14\uff01 \u4e0d\u77e5\u4f55\u65f6\uff0c\u5728\u5979\u4e8c\u4eba\u8eab\u4fa7\u4e0d\u8fdc\u4e4b\u5904\uff0c\u5e7d\u7075\u9b3c\u80a0\u4f3c\u5730\u7ad9\u7740\u4e24\u4e2a\u6d3b\u4eba\uff01 \u6155\u5bb9\u7ea2\u4e5f\u662f\u5728\u63a8\u5f00\u6000\u4e2d\u7684\u5a49\u513f\uff0c\u624d\u7a81\u7136\u89c1\u5230\u8eab\u524d\u5982\u5e7d\u7075\u4f3c\u5730\u7ad9\u7740\u4e24\u4e2a\u5927\u6d3b\u4eba\uff0c\u548c\u5a49\u513f\u4e00 \u6837\u9707\u60ca\u5f97\u819b\u76ee\u7ed3\u820c\uff01 \u59d0\u59b9\u4e8c\u4eba\u6b66\u529f\u4e0d\u5f31\uff0c\u5c31\u7b97\u56e0\u4e3a\u8bf4\u8bdd\u5206\u5fc3\uff0c\u4e5f\u4e0d\u80fd\u8bf4\u6709\u4e24\u4e2a\u5927\u6d3b\u4eba\u6765\u81f3\u8eab\u4fa7\uff0c\u7ade\u4e00\u70b9\u672a\u53d1 \u89c9\u4e4b\u7406\uff1f \u90a3\u4e48\uff0c\u8fd9\u4e24\u4e2a\u4eba\u6b66\u529f\u4e4b\u9ad8\u5fc5\u5df2\u5230\u4e86\u4ee4\u4eba\u4e0d\u53ef\u601d\u8bae\u7684\u5730\u6b65\uff01 \u518d\u4e00\u770b\u90a3\u4e8c\u4eba\uff0c\u5747\u5df2\u81f3\u82b1\u7532\u4e4b\u5e74\uff0c\u4e00\u4e2a\u751f\u7740\u6ee1\u5934\u7ea2\u53d1\uff0c\u76ae\u80a4\u9157\u9ed1\uff0c\u53ea\u6709\u8138\u86cb\u4e2d\u95f4\u5706\u5706\u7684 \u4e00\u5757\u767d\u5ae9\u9c9c\u7ea2\uff0c\u706b\u773c\u91d1\u775b\uff0c\u6781\u8096\u4f3c\u4e00\u53ea\u5927\u9a6c\u7334\uff01 \u53e6\u5916\u4e00\u4e2a\u7626\u5c0f\u67af\u5e72\u8001\u5934\uff0c\u65f6\u503c\u76db\u590f\uff0c\u5374\u7a7f\u4e86\u4e00\u88ad\u53c8\u539a\u53c8\u91cd\u7684\u68c9\u957f\u888d\uff0c\u5916\u8fb9\u5c1a\u7f69\u4e86\u4e00\u4ef6\u5927 \u9a6c\u8902\uff0c\u8138\u4e0a\u4e00\u4e2a\u901a\u7ea2\u7684\u5927\u9152\u7cdf\u9f3b\u5b50\uff0c\u2019\u4e2a\u8db3\u7684\u4e09\u5bb6\u6751\u6559\u5b66\u51ac\u70d8\u5148\u751f\u6a21\u6837\uff01 \u8fd9\u4e24\u5168\u5f62\u72b6\u602a\u5f02\u7684\u8001\u5934\uff0c\u4e24\u53cc\u773c\u775b\u5982\u56db\u628a\u5bd2\u5149\u67ab\u67ab\u7684\u5229\u5251\uff0c\u76f4\u76ef\u76ef\u5730\u671b\u5b9a\u6155\u5bb9\u7ea2\u59d0\u59b9\uff0c \u563f\u7136\u9634\u68ee\uff0c\u4e00\u8bed\u4e0d\u53d1\uff01 \u6155\u5bb9\u7ea2\u4e0e\u5a49\u513f\u867d\u7136\u4e0d\u8ba4\u8bc6\u8fd9\u4e24\u4e2a\u5e7d\u7075\u822c\u7a81\u7136\u51fa\u73b0\u7684\u602a\u8001\u4eba\uff0c\u4f46\u9690\u5728\u6811\u540e\u7684\u5c55\u767d\u5374\u8ba4\u8bc6\u4ed6 \u4eec\u4e8c\u4eba\uff0c\u6b63\u662f\u4e24\u6b21\u4e0e\u81ea\u5df1\u4f5c\u5bf9\u7684\u201c\u6c5f\u5357\u96c1\u8361\u5c71\u9b3c\u8c37\u4e8c\u5947\u201d\uff01 \u5176\u5b9e\u5c55\u767d\u65e9\u5c31\u53d1\u73b0\u4ed6\u4e8c\u4eba\u954a\u8db3\u6f5c\u8e2a\uff0c\u6b3a\u8fd1\u6155\u5bb9\u59d0\u59b9\u8eab\u8fb9\u3002\u4e00\u662f\u6155\u5bb9\u59d0\uff0c\u518d\u8005\u4ed6\u4e5f\u60f3\u6697\u5bdf \u4e00\u4e0b\u8fd9\u6210\u540d\u6c5f\u6e56\u6570\u5341\u5e74\u7684\u4e24\u5927\u9ad8\u624b\uff0c\u4ee5\u82b1\u7532\u4e4b\u5e74\uff0c\u9b3c\u9b3c\u795f\u795f\u5730\u63a9\u81f3\u4e24\u4e2a\u534e\u5e74\u5c11\u5973\u8eab\u8fb9\uff0c\u7a76\u7adf \u610f\u6b32\u4f55\u4e3a\uff1f \u53cd\u6b63\u4ed6\u6709\u628a\u63e1\uff0c\u5c31\u7b97\u4e8c\u5947\u4e0d\u987e\u8eab\u4efd\uff0c\u5411\u4e09\u5973\u5077\u88ad\uff0c\u6216\u8005\u731b\u65bd\u8fa3\u624b\uff0c\u4ee5\u4ed6\u9690\u8eab\u5730\u70b9\u4e0e\u4e8c\u5973 \u7684\u8ddd\u79bb\uff0c\u4ea6\u53ef\u9002\u65f6\u51fa\u624b\u62a2\u6551\uff0c\u662f\u4ee5\u4ecd\u7136\u9690\u5728\u6811\u540e\u672a\u52a8\uff01 \u4e5f\u5c31\u662f\u6155\u5bb9\u7ea2\u4e0e\u5a49\u513f\u4e00\u6014\u7684\u5f53\u513f\uff0c\u201c\u9b3c\u8c37\u4e8c\u5947\u201d\u540c\u65f6\u563f\u563f\u4e00\u9635\u51b7\u7b11\u3002\u5927\u5947\u201c\u8d64\u53d1\u7075\u7334\u201d \u5e38\u53bb\u6076\u8bf4\u9053\uff1a\u4e24\u4e2a\u5973\u5a03\u513f\uff0c\u5728\u6b64\u5546\u91cf\u540c\u5ac1\u4e00\u4e2a\u4e08\u592b\uff0c\u5c82\u4e0d\u662f\u5929\u4e0b\u5947\u95fb\uff1f\u201d \u5a49\u513f\u6bd4\u8f83\u5201\u94bb\uff0c\u4e00\u89c1\u8eab\u8fb9\u6084\u65e0\u58f0\u606f\u5730\u63a9\u6765\u4e24\u4e2a\u602a\u8001\u4eba\uff0c\u800c\u4e14\u51fa\u53e3\u8bbd\u523a\uff0c\u4e0d\u7531\u79c0\u76ee\u4e00\u77aa\uff0c \u5a07\u53f1\u9053\uff1a\u201c\u8c01\u8981\u4f60\u7ba1\uff01\u5582\uff0c\u4f60\u4eec\u662f\u5e72\u4ec0\u4e48\u7684\uff0c\u600e\u4e48\u4e0d\u58f0\u4e0d\u54cd\u5730\u8dd1\u6765\u5413\u4eba\uff1f\u5982\u679c\u8bf4\u4e0d\u51fa\u4e2a\u7406\u7531 \u6765\uff0c\u522b\u8bf4\u59d1\u5a18\u5bf9\u4f60\u4eec\u4e0d\u5ba2\u6c14\uff01\u201d \u4e8c\u5947\u201c\u9b3c\u8c37\u9690\u91cc\u201d\u6587\u6b63\u5947\u51b7\u68ee\u68ee\u5730\u8bf4\u9053\uff1a\u201c\u5c0f\u59ae\u5b50\u4f60\u5148\u522b\u51f6\uff01 \u8001\u592b\u95ee\u4f60\uff0c\u4f60\u4eec\u4e8c\u4eba\u65b9\u624d\u53e3\u53e3\u58f0\u58f0\u5c55\u54e5\u54e5\uff0c\u662f\u4e0d\u662f\u8bf4\u7684\u5c55\u767d\u90a3\u5c0f\u5b50\uff1f\u201d \u5a49\u513f\u5389\u58f0\u9053\uff1a\u201c\u4f60\u8bf4\u8bdd\u5ba2\u6c14\u4e00\u70b9\uff01\u4ec0\u4e48\u5c0f\u5b50\u5c0f\u5b50\u7684\uff1f\u201d \u201c\u9b3c\u8c37\u9690\u91cc\u201d\u7a81\u7136\u63a2\u81c2\u5411\u5a49\u513f\u6293\u6765\uff0c\u540c\u65f6\u5634\u4e2d\u559d\u9053\u2018\u201c\u8001\u592b\u53ea\u95ee\u4f60\u662f\u4ed6\u4e0d\u662f\uff1f\u201d \u201c\u9b3c\u8c37\u9690\u53df\u201d\u8fd9\u968f\u4fbf\u4e00\u6293\uff0c\u7adf\u662f\u5feb\u5f97\u51fa\u5947\uff0c\u5982\u679c\u4e0d\u662f\u5a49\u513f\u521a\u53c8\u5b66\u4f1a\u201c\u5343\u5e7b\u98d8\u9999\u6b65\u201d\uff0c\u5c31 \u51ed\u201c\u8e51\u7a7a\u5e7b\u5f71\u201d\u8eab\u6cd5\uff0c\u51e0\u4e4e\u8eb2\u4e0d\u9053\u201c\u9b3c\u8c37\u9690\u53df\u201d\u8fd9\u4e00\u6293\uff01 \u968f\u90a3\u4e00\u6293\u4e4b\u52bf\uff0c\u5a49\u513f\u4e00\u632b\u6b65\uff0c\u98d8\u7136\u8dc3\u5f00\u4e09\u5c3a\uff0c\u6012\u9053\uff1a\u201c\u662f\u53c8\u600e\u4e48\u6837\uff1f\u6ca1\u60f3\u5230\u59d1\u5a18\u656c\u4f60\u5e74 \u8001\uff0c\u4f60\u5c31\u5148\u51fa\u624b\u4e86\uff0c\u4f60\u4e5f\u63a5\u59d1\u5a18\u4e00\u62db\u8bd5\u8bd5\uff01\u201d \u8bf4\u7f62\uff0c\u51fa\u4e4e\u5982\u9525\uff0c\u7adf\u662f\u201c\u641c\u9b42\u6307\u201d\u795e\u529f\uff0c\u731b\u70b9\u201c\u9b3c\u8c37\u9690\u53df\u201d\u5de6\u80f8\u201c\u671f\u95e8\u201d\u91cd\u7a74\uff01 \u201c\u9b3c\u8c37\u9690\u53df\u201d\u5403\u4e86\u4e00\u60ca\uff0c\u5148\u662f\u4e00\u6293\u843d\u7a7a\uff0c\u5df2\u7ecf\u5927\u51fa\u610f\u5916\uff0c\u53c8\u89c1\u5a49\u513f\u6307\u51fa\u5578\u98ce\uff0c\u4e14\u51fa\u62db\u4e4b \u75be\u4e1d\u6beb\u4e0d\u5f31\u4e8e\u81ea\u5df2\uff0c\u5fc3\u4e2d\u66f4\u662f\u4e00\u61d4\uff0c\u60f3\u4e0d\u5230\u770b\u6765\u4e00\u4e2a\u5f31\u4e0d\u7981\u98ce\u7684\u5e74\u8f7b\u5973\u5b50\uff0c\u7adf\u6709\u51fa\u4e4e\u610f\u5916\u7684 \u9ad8\u5f3a\u6b66\u529f\uff1f \u4ed6\u770b\u51fa\u5a49\u513f\u6307\u98ce\u51cc\u5389\uff0c\u4e0d\u6562\u786c\u63a5\uff0c\u95ea\u8eab\u8dc3\u51fa\u4e00\u4e08\u5f00\u5916\uff0c\u4f46\u500f\u9000\u53c8\u8fdb\uff0c\u53ea\u811a\u5c16\u5fae\u4e00\u70b9\u5730\uff0c \u8eab\u5982\u98d8\u98ce\u53c8\u8dc3\u4e86\u56de\u6765\uff0c\u540c\u65f6\u5411\u5a49\u513f\u62cd\u51fa\u4e24\u638c\uff01 \u8fd9\u4e24\u638c\u4e00\u5148\u4e00\u540e\uff0c\u4f46\u540e\u53d1\u5148\u81f3\uff0c\u53c8\u4f3c\u4e00\u540e\u4e00\u5148\uff0c\u62db\u5f0f\u602a\u5f97\u51fa\u5947\uff0c\u800c\u4e14\u4e00\u80a1\u9634\u67d4\u6697\u52b2\u6c79\u6d8c \u6d8c\u6765\uff0c\u7adf\u662f\u5927\u5f97\u8d85\u4e4e\u60f3\u8c61\uff01 \u5a49\u513f\u4e5f\u5403\u4e86\u4e00\u60ca\uff0c\u770b\u4e0d\u51fa\u8fd9\u51ac\u70d8\u5148\u751f\u4e00\u6837\u7684\u7cdf\u8001\u5934\uff0c\u638c\u529b\u4e4b\u5927\u7adf\u662f\u524d\u6240\u672b\u89c1\uff01 \u5a49\u513f\u4e00\u6837\u7684\u4e0d\u6562\u786c\u63a5\uff0c\u98d8\u8eab\u9000\u51fa\u4e00\u6587\u5f00\u5916\uff01 \u6155\u5bb9\u7ea2\u89c1\u8fd9\u602a\u8001\u4eba\u51fa\u624b\u6b66\u529f\u9ad8\u5f3a\u96be\u6d4b\uff0c\u4e14\u63a2\u542c\u5c55\u767d\u4e0b\u843d\uff0c\u867d\u7136\u51fa\u8a00\u4e0d\u900a\uff0c\u4f46\u5979\u7d20\u77e5\u8fd9\u822c \u6c5f\u6e56\u5947\u4eba\u5f02\u58eb\u591a\u6709\u602a\u7656\uff0c\u8bf4\u4e0d\u5b9a\u662f\u5c55\u54e5\u54e5\u7684\u670b\u53cb\uff0c\u5f53\u4e0b\u62e6\u4f4f\u59b9\u59b9\uff0c\u5411\u201c\u9b3c\u8c37\u4e8c\u5947\u201d\u655b\u8884\u9053\uff1a \u201c\u4e0d\u77e5\u4e24\u4f4d\u8001\u524d\u8f88\u8be2\u95ee\u5c55\u5c0f\u4fa0\u6709\u4f55\u8d35\u5e72\uff1f\u201d \u201c\u9b3c\u8c37\u9690\u53df\u201d\u4e0d\u7b54\u53cd\u95ee\uff0c\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u4f60\u4eec\u4e24\u4e2a\u59ae\u5b50\u53e3\u4e2d\u7684\u5c55\u54e5\u54e5\uff0c\u5f53\u771f\u662f\u5c55\u767d \u4e86\uff1f\u201d \u6155\u5bb9\u7ea2\u70b9\u5934\u627f\u8ba4\u9053\uff1a\u201c\u6b63\u662f\uff01\u201d \u5728\u4e00\u65c1\u7684\u201c\u8d64\u53d1\u7075\u7334\u201d\u7a81\u7136\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u5c55\u767d\u8fd9\u5c0f\u5b50\u8273\u798f\u4e0d\u6d45\uff0c\u7ade\u6709\u4e24\u4e2a\u82b1\u6735 \u4f3c\u7684\u5973\u5a03\u513f\uff0c\u201c\u8fd9\u8bdd\u8bf4\u5f97\u6155\u5bb9\u7ea2\u6ee1\u80ce\u751f\u971e\uff0c\u534a\u7f9e\u534a\u55d4\u5730\u9053\uff1a\u201c\u522b\u4e3a\u8001\u4e0d\u5c0a\uff0c\u4e8c\u4f4d\u7a76\u7adf\u627e\u5c55\u5c0f \u4fa0\u6709\u4ec0\u4e48\u4e8b\uff1f\u201d \u5a49\u513f\u9053\uff1a\u201c\u59d0\u59d0\uff0c\u4f60\u7406\u4ed6\u4eec\u505a\u751a\uff01\u8fd9\u4e24\u4e2a\u8001\u4e0d\u6b7b\u51b3\u4e0d\u662f\u4ec0\u4e48\u597d\u4eba\uff01\u201d \u201c\u9b3c\u8c37\u9690\u53df\u201d\u7a81\u5730\u4ef0\u5929\u6253\u4e86\u54c8\u54c8\uff0c\u7136\u540e\u8138\u5b54\u4e00\u6c89\uff0c\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u59ae\u5b50\u6d3b\u817b\u4e86\uff01\u7adf\u6562\u5f53\u9762 \u9a82\u6211\u8001\u4eba\u5bb6\uff0c\u4eca\u5929\uff0c\u4f60\u662f\u6b7b\u5b9a\u4e86\uff01\u201d\u8bf4\u7f62\u4e3e\u638c\u6b32\u5288\u2026\u2026 \u201c\u8d64\u53d1\u7075\u7334\u201d\u4e00\u624b\u62e6\u4f4f\u201c\u9b3c\u8c37\u9690\u53df\u201d\uff0c\u9053\uff1a\u201c\u5e08\u5f1f\uff0c\u522b\u5fd9\uff01\u8fd9\u4e24\u4e2a\u5973\u5a03\u513f\u65e2\u662f\u5c55\u767d\u90a3\u5c0f \u5b50\u7684\u672a\u5a5a\u59bb\uff0c\u54b1\u4eec\u62ff\u4f4f\u5979\u4e24\u4e2a\uff0c\u4e0d\u6015\u5c55\u767d\u90a3\u5c0f\u5b50\u4e0d\u628a\u5929\u4e0b\u7b2c\u4e00\u5947\u4e66\u4ea4\u51fa\u6765\uff01\u2026\u2026\u201d \u5a49\u513f\u4e00\u542c\u51e0\u4e4e\u6c14\u70b8\u4e86\u80ba\uff0c\u5f53\u4e0b\u63d2\u5634\u9a82\u9053\uff1a\u201c\u522b\u505a\u68a6\u4e86\uff01\u51ed\u4f60\u4eec\u4e24\u4e2a\u8001\u4e0d\u6b7b\uff0c\u5984\u60f3\u4e89\u593a\u5929 \u4e0b\u7b2c\u4e00\u5947\u4e66\uff0c\u8fd8\u5dee\u5f97\u8fdc\u5462\uff01\u201d \u81f3\u6b64\uff0c\u6155\u5bb9\u7ea2\u4e5f\u5019\u7136\u660e\u767d\uff0c\u539f\u6765\u8fd9\u4e24\u4e2a\u602a\u8001\u4eba\u8be2\u95ee\u5c55\u767d\uff0c\u662f\u60f3\u4e89\u593a\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b \u5377\u300b\uff0c\u4f46\u5979\u6bd4\u5a49\u513f\u8f83\u4e3a\u6c89\u7740\uff0c\u5c55\u54e5\u54e5\u58f0\u8a00\u9000\u51fa\u6c5f\u6e56\uff0c\u5979\u4e0d\u613f\u518d\u4e3a\u5c55\u54e5\u54e5\u591a\u6811\u5f3a\u654c\u3002\u7acb\u5373\u8bf4 \u9053\uff1a\u201c\u53ef\u60dc\u4e24\u4f4d\u8001\u524d\u8f88\u6765\u665a\u4e86\u4e00\u6b65\uff0c\u5c55\u5c0f\u4fa0\u5df2\u5728\u524d\u51e0\u5929\uff0c\u5f53\u7740\u7fa4\u96c4\u4e4b\u9762\uff0c\u628a\u90a3\u4e2d\u300a\u9501\u9aa8\u9500\u9b42 \u5929\u4f5b\u5377\u300b\u6495\u6bc1\u4e86\uff01\u201d\u201c\u9b3c\u8c37\u9690\u53df\u201d\u8138\u8272\u4e00\u53d8\uff0c\u6025\u95ee\u9053\uff1a\u201c\u6b64\u8bdd\u5f53\u771f\uff1f\u201d \u6155\u5bb9\u7ea2\u9053\uff1a\u201c\u8c01\u8fd8\u9a97\u4f60\u4e0d\u6210\uff01\u201d \u5979\u89c1\u201c\u9b3c\u8c37\u9690\u53df\u201d\u4f3c\u662f\u4e0d\u60c5\uff0c\u53c8\u63a5\u7740\u9053\uff1a\u201c\u6b64\u4e8b\u4efb\u4eba\u7686\u77e5\uff0c\u4e0d\u76f8\u4fe1\uff0c\u4e8c\u4f4d\u8001\u524d\u8f88\u53ef\u4ee5\u53bb \u95ee\u95ee\uff01\u201d \u6b64\u4e8b\uff0c\u53c8\u5927\u51fa\u201c\u9b3c\u8c37\u9690\u53df\u201d\u610f\u5916\uff0c\u770b\u6155\u5bb9\u7ea2\u6240\u8bf4\u53c8\u4e0d\u4f3c\u6709\u5047\uff0c\u4e00\u65f6\u6025\u5f97\u5439\u80e1\u5b50\u5e72\u77aa\u773c\uff0c \u534a\u5929\u8bf4\u4e0d\u51fa\u8bdd\u6765\uff01 \u201c\u8d64\u53d1\u7075\u7334\u201d\u5e38\u53bb\u6076\u4e24\u53ea\u7334\u773c\u4e00\u8f6c\uff0c\u542b\u7740\u8bf1\u8be1\u7684\u7b11\u5bb9\u9053\uff1a\u201c\u5973\u5a03\u513f\uff01\u4f60\u4ee5\u4e3a\u8fd9\u4e24\u53e5\u8bdd\u5c31 \u53ef\u4ee5\u9a97\u5f97\u4e86\u6211\u4fe9\u8001\u4eba\u5bb6\u5417\uff1f\u201d \u5a49\u513f\u89c1\u4ed6\u4e8c\u4eba\u80e1\u6405\u86ee\u7f20\uff0c\u5fc3\u4e2d\u5927\u6012\uff0c\u4e0d\u7b49\u59d0\u59d0\u56de\u7b54\uff0c\u7acb\u5373\u63a5\u53e3\u9053\uff1a\u201c\u5c31\u662f\u9a97\u4f60\u4eec\u4e24\u4e2a\u8001 \u4e0d\u6b7b\uff0c\u53c8\u8be5\u600e\u4e48\u6837\uff1f\u201d \u201c\u9b3c\u8c37\u9690\u53df\u201d\u4e5f\u4f3c\u604d\u7136\u5927\u609f\uff0c\u6012\u9053\uff1a\u201c\u8001\u592b\u5c31\u8981\u4f60\u4eec\u4e24\u4e2a\u5c0f\u59ae\u4e8e\u7684\u6b7b\u547d\uff01\u201d\u8bf4\u7f62\u4e3e\u624b\u53c8 \u8981\u6253\u2026\u2026 \u201c\u8d64\u53d1\u7075\u7334\u201d\u518d\u62e6\u4f4f\u66b4\u6012\u5982\u96f7\u7684\u5e08\u5f1f\uff0c\u9634\u6c89\u6c89\u5730\u7b11\u9053\uff1a\u201c\u6211\u4eec\u4e0d\u8981\u5979\u4e24\u4e2a\u6b7b\uff0c\u53ea\u8981\u6349\u6d3b \u7684\uff0c\u627e\u5c55\u767d\u90a3\u5c0f\u5b50\u53bb\u4ea4\u6362\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\uff01\u201d \u5a49\u513f\u6012\u6781\uff0c\u8131\u53e3\u9a82\u9053\uff1a\u201c\u8bf4\u7684\u6bd4\u5531\u7684\u597d\u542c\uff0c\u60f3\u662f\u5403\u591a\u4e86\u706f\u8349\u7070\uff0c\u51c0\u653e\u8f7b\u5de7\u5c41\uff01\u201d \u201c\u8d64\u53d1\u7075\u7334\u201d\u8138\u8272\u4e00\u6c89\uff0c\u4e5f\u6012\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4e2a\u5973\u5a03\u513f\uff0c\u662f\u4e56\u4e56\u7684\u8ddf\u6211\u4e24\u4f4d\u8001\u4eba\u5bb6\u8d70\uff1f\u8fd8 \u662f\u8981\u7b49\u6211\u4e24\u4f4d\u8001\u4eba\u5bb6\u52a8\u624b\u62ff\u4eba\uff1f\u201d \u5a49\u513f\u51b7\u7b11\u9053\uff1a\u201c\u8bf4\u5927\u8bdd\u4e0d\u6015\u6298\u4e86\u820c\u5934\uff01\u201d \u201c\u8d64\u53d1\u7075\u7334\u201d\u9762\u8272\u9634\u6c89\u9053\uff1a\u201c\u771f\u8981\u52a8\u624b\uff0c\u4e0d\u51fa\u5341\u62db\uff0c\u6211\u4e24\u4f4d\u8001\u4eba\u5bb6\u4fbf\u53ef\u628a\u4f60\u4eec\u4e24\u4e2a\u5973\u5a03 \u513f\u62ff\u4e0b\uff01\u201d \u5a49\u513f\u9053\uff1a\u201c\u771f\u8981\u52a8\u624b\uff0c\u4e0d\u51fa\u4e09\u62db\u6211\u4e24\u4f4d\u59d1\u5976\u5976\u4fbf\u53ef\u628a\u4f60\u4eec\u4e24\u4e2a\u8001\u4e0d\u6b7b\u6253\u53d1\u8d70\uff01\u201d \u5a49\u513f\u820c\u950b\u7280\u5229\uff0c\u53ea\u6c14\u5f97\u201c\u9b3c\u8c37\u4e8c\u5947\u201d\u4e94\u5185\u751f\u70df\uff01 \u9690\u8eab\u6811\u540e\u7684\u5c55\u767d\uff0c\u53ef\u6b62\u4e0d\u4f4f\u76f4\u8981\u7b11\u2026\u2026\u201c\u9b3c\u8c37\u9690\u91cc\u201d\u66b4\u559d\u9053\uff1a\u90a3\u54b1\u4eec\u4e0d\u59a8\u8bd5\u8bd5\u770b\uff01\u201d \u66b4\u559d\u58f0\u4e2d\uff0c\u201c\u9634\u98ce\u638c\u201d\u4e00\u62db\u201c\u9b3c\u738b\u649e\u949f\u201d\uff0c\u731b\u5411\u5a49\u513f\u80f8\u524d\u6293\u6765\uff0c\u5bf9\u4e00\u4e2a\u5c11\u5973\u6765\u8bf4\uff0c\u8fd9\u62db \u5f0f\u6709\u70b9\u4e0b\u6d41\uff01 \u5a49\u513f\u7c89\u8138\u4e00\u7ea2\uff0c\u5fc3\u4e2d\u66b4\u6012\uff0c\u4f46\u89c1\u4ed6\u638c\u98ce\u9634\u6c14\u68ee\u68ee\uff0c\u72c2\u5578\u66b4\u5377\uff0c\u5a01\u52bf\u751a\u731b\uff0c\u4e5f\u4e0d\u6562\u8f7b\u6613\u53bb \u63a5\uff0c\u7acb\u523b\u65bd\u51fa\u201c\u5343\u5e7b\u98d8\u9999\u6b65\u201d\uff0c\u5a07\u8eaf\u53ea\u8f7b\u4e00\u8f6c\uff0c\u5df2\u8131\u51fa\u201c\u9b3c\u8c37\u9690\u53df\u201d\u638c\u98ce\u4e4b\u5916\uff01 \u5a49\u513f\u8eab\u6cd5\u4ee5\u8f7b\u7075\u89c1\u957f\uff0c\u4ece\u5b66\u4f1a\u4e86\u201c\u5343\u5e7b\u98d8\u9999\u6b65\u201d\uff0c\u66f4\u662f\u5982\u864e\u6dfb\u7ffc\uff0c\u51e0\u4e0b\u52a8\u4f5c\uff0c\u5feb\u5f97\u72b9\u5982 \u795e\u9f99\u89c1\u9996\u4e0d\u89c1\u5c3e\uff0c\u201c\u9b3c\u8c37\u9690\u53df\u201d\u7684\u201c\u9634\u98ce\u638c\u201d\u7edd\u5927\u6740\u62db\uff0c\u5df2\u544a\u843d\u7a7a\uff01 \u8fd9\u8eab\u6cd5\uff0c\u4f7f\u201c\u9b3c\u8c37\u9690\u53df\u201d\u6697\u6697\u5403\u60ca\uff0c\u4f46\u4e5f\u66f4\u6fc0\u8d77\u4e86\u4ed6\u7684\u72c2\u6012\uff0c\u53ea\u89c1\u4ed6\u98d5\u98ce\u95ea\u7535\u4f3c\u7684\u53c8\u63a5 \u8fde\u5288\u51fa\u4e03\u638c\uff01 \u8fd9\u4e03\u638c\u4e00\u6c14\u5475\u6210\uff0c\u6a2a\u5288\u76d8\u6253\uff0c\u5982\u7535\u5149\u77f3\u706b\uff0c\u529b\u9053\u4e4b\u731b\uff0c\u52bf\u5982\u9a87\u98ce\u60ca\u6d6a\uff0c\u4f46\u5728\u5a49\u513f\u7edd\u5999\u8eab \u6cd5\u4e4b\u4e0b\uff0c\u638c\u638c\u5747\u544a\u843d\u7a7a\uff0c\u5f92\u7136\u6253\u5f97\u98ce\u5578\u5750\u98de\uff0c\u8349\u6298\u6811\u65ad\uff01 \u53ef\u4e5f\u628a\u5a49\u513f\u903c\u9000\u4e00\u4e08\u5f00\u5916\uff01 \u5a49\u513f\u5927\u6012\uff0c\u5728\u98d8\u7136\u8eb2\u8fc7\u7b2c\u4e03\u638c\u4e4b\u540e\uff0c\u5a07\u53f1\u4e00\u58f0\uff0c\u65bd\u51fa\u201c\u641c\u9b42\u6307\u201d\u795e\u529f\uff0c\u6307\u98ce\u9510\u5578\uff0c\u731b\u70b9 \u201c\u9b3c\u8c37\u9690\u53df\u201d\u524d\u80f8\u4e09\u5927\u8981\u5bb3\uff01 \u89c1\u5a49\u513f\u62db\u98ce\u51cc\u5389\uff0c\u201c\u9b3c\u8c37\u9690\u53df\u201d\u4e00\u6837\u4e0d\u6562\u786c\u63a5\uff0c\u659c\u8eab\u4e00\u8dc3\uff0c\u5a49\u513f\u8d81\u673a\u6273\u56de\u52a3\u52bf\uff0c\u4e5f\u63a5\u8fde \u653b\u51fa\u4e09\u6307\u56db\u638c\uff0c\u4e00\u6837\u4e03\u62db\uff01 \u8fd9\u4e03\u62db\u8fde\u73af\u65bd\u51fa\uff0c\u4e5f\u628a\u201c\u9b3c\u8c37\u9690\u91cc\u201d\u903c\u9000\u4e00\u6587\uff01 \u4e8c\u4eba\u7ffb\u7ffb\u6eda\u6eda\uff0c\u6643\u773c\u6253\u4e86\u5341\u6570\u62db\uff0c\u4ecd\u7136\u662f\u4e2a\u4e0d\u80dc\u4e0d\u8d25\u4e4b\u5c40\uff01 \u201c\u8d64\u53d1\u7075\u7334\u201d\u7334\u773c\u4e00\u8f6c\uff0c\u76ef\u5728\u6155\u5bb9\u7ea2\u7684\u7c89\u8138\u4e4b\u4e0a\uff0c\u9634\u68ee\u68ee\u5730\u7b11\u9053\uff1a\u201c\u4ed6\u4eec\u4e24\u4e2a\u6253\u4e0a\u4e86\uff0c \u5973\u5a03\u513f\uff0c\u54b1\u4eec\u4e5f\u522b\u95f2\u7740\uff0c\u8001\u592b\u966a\u4f60\u73a9\u73a9\uff01\u201d \u8fd9\u8bdd\u8f7b\u8584\u5df2\u6781\uff0c\u6155\u5bb9\u7ea2\u7389\u9762\u5982\u51b0\uff0c\u6316\u82e6\u9053\uff1a\u521a\u624d\u4f60\u4e0d\u662f\u8bf4\u5341\u62db\u5417\uff1f\u5982\u4eca\u5eff\u51e0\u62db\u90fd\u8fc7\u4e86\uff0c \u4f60\u90a3\u540c\u4f34\u5e76\u672a\u80fd\u628a\u6211\u59b9\u59b9\u600e\u4e48\u6837\uff01\u2026\u2026\u201d \u201c\u8d64\u53d1\u7075\u7334\u201d\u8d3c\u5fd2\u5fd2\u4e00\u7b11\uff0c\u9053\uff1a\u5341\u62db\uff0c\u5eff\u62db\uff0c\u6709\u4ec0\u4e48\u5173\u7cfb\uff0c\u53cd\u6b63\u65e9\u665a\u4f60\u4e8c\u4eba\u662f\u6211\u4eec\u4e24\u4f4d \u8001\u4eba\u5bb6\u56ca\u4e2d\u4e4b\u7269\uff01\u201d\u8bf4\u7740\u4e00\u62db\u201c\u9ed1\u624b\u5957\u9b42\u201d\uff0c\u6284\u624b\u5411\u6155\u5bb9\u7ea2\u4e0b\u8179\u90e8\u6293\u6765\uff01 \u6155\u5bb9\u7ea2\u79c0\u8138\u4e00\u7ea2\uff0c\u5979\u4e00\u76f4\u5c0a\u656c\u4ed6\u4e8c\u4eba\u5e74\u8001\uff0c\u8bf4\u8bdd\u90fd\u5f88\u5ba2\u6c14\uff0c\u6ca1\u60f3\u5230\u8fd9\u4e8c\u4eba\u4e3a\u8001\u4e0d\u5c0a\uff0c\u65e2 \u4e0d\u8981\u8138\u53c8\u51fa\u624b\u4e0b\u6d41\uff0c\u5979\u518d\u51fa\u4e0d\u7559\u60c5\u9762\uff0c\u642c\u8eab\u6a2a\u6b65\u4e00\u62db\u201c\u98ce\u9707\u96f7\u9e23\u201d\uff0c\u731b\u5411\u5bf9\u65b9\u5f53\u5934\u62cd\u53bb\uff01 \u201c\u8d64\u53d1\u7075\u7334\u201d\u95ea\u8eab\u51fa\u638c\uff0c\u4e8c\u4eba\u4e5f\u6253\u5728\u4e00\u8d77\uff0c\u6643\u773c\u4e5f\u8fc7\u4e86\u5341\u6570\u4e2a\u7167\u9762\uff01 \u4f46\u52a8\u624b\u65f6\u95f4\u4e00\u957f\uff0c\u6155\u5bb9\u7ea2\u5374\u4e0d\u662f\u201c\u8d64\u53d1\u7075\u7334\u201d\u7684\u5bf9\u624b\uff01 \u201c\u6c5f\u5357\u4e8c\u5947\u201d\u4ee5\u201c\u8d64\u53d1\u7075\u7334\u201d\u6b66\u529f\u8f83\u9ad8\uff0c\u800c\u6155\u5bb9\u59d0\u59b9\uff0c\u5374\u4ee5\u6155\u5bb9\u7ea2\u8f83\u5f31\uff0c\u4e00\u5f3a\u5bf9\u4e00\u5f31\uff0c \u51e0\u5341\u4e2a\u7167\u9762\u4e00\u8fc7\uff0c\u6155\u5bb9\u7ea2\u5df2\u7d2f\u5f97\u5a07\u5598\u5401\u5401\uff0c\u5468\u8eab\u89c1\u6c57\uff01 \u5a49\u513f\u4e0e\u201c\u9b3c\u8c37\u9690\u53df\u201d\uff0c\u5374\u6253\u4e86\u4e2a\u68cb\u9022\u5bf9\u624b\uff01 \u8ba4\u771f\u8bf4\u8d77\u6765\uff0c\u5a49\u513f\u4e0e\u201c\u9b3c\u8c37\u9690\u53df\u201d\u8fd8\u4ece\u4e00\u4f4d\u5e08\u7236\u5b66\u8fc7\u827a\uff0c\u201c\u9b3c\u8c37\u9690\u53df\u201d\u662f\u201c\u795e\u7334\u201d\u94c1\u51cc \u4e4b\u5f92\uff0c\u5a49\u513f\u4e5f\u8ddf\u201c\u795e\u7334\u201d\u94c1\u51cc\u767e\u65e5\u4e60\u529f\uff0c\u6253\u7740\u6253\u7740\uff0c\u4e8c\u4eba\u62db\u6cd5\u7ade\u6709\u4e0d\u5c11\u662f\u76f8\u540c\u7684\uff01 \u53ef\u662f\u4e8c\u4eba\u5374\u4ece\u672a\u89c1\u8fc7\u9762\uff0c\u5f53\u7136\u8c01\u4e5f\u4e0d\u8ba4\u8bc6\u8c01\u3002 \u4e0d\u8fc7\u90fd\u5728\u5fc3\u91cc\u7eb3\u95f7\uff0c\u4e3a\u4ec0\u4e48\u5bf9\u65b9\u62db\u5f0f\uff0c\u6709\u8bb8\u591a\u4e0e\u81ea\u5df1\u4e00\u6837\uff1f \u90a3\u8fb9\uff0c\u6155\u5bb9\u7ea2\u5374\u5df2\u9669\u8c61\u73af\u751f\uff01 \u201c\u8d64\u53d1\u7075\u7334\u201d\u7a33\u64cd\u80dc\u7b97\uff0c\u53c8\u5f00\u59cb\u5e7d\u9ed8\u8d77\u6765\uff0c\u4e0d\u65f6\u6cb9\u5634\u6ed1\u820c\uff0c\u8868\u9762\u4e0a\u8d5e\u6155\u5bb9\u7ea2\u5bb9\u8c8c\u7f8e\u4e3d\u9aa8 \u5b50\u91cc\u5374\u662f\u7a77\u5403\u8c46\u8150\u3002 \u4ee5\u4ed6\u7684\u5c81\u6570\u6765\u8bb2\uff0c\u5c3d\u53ef\u505a\u6155\u5bb9\u7ea2\u7684\u7956\u7236\uff0c\u5f53\u771f\u662f\u4e3a\u8001\u4e0d\u5c0a\uff01 \u6155\u5bb9\u7ea2\u53c8\u7f9e\u53c8\u6c14\uff0c\u201c\u8d64\u53d1\u7075\u7334\u201d\u201c\u9ed1\u715e\u624b\u201d\u906e\u5929\u76d6\u65e5\uff0c\u6ca1\u5934\u6ca1\u8138\u5411\u5979\u76d6\u4e0b\u6765\uff0c\u5979\u5df2\u611f\u529b \u5c3d\u7b4b\u75b2\uff0c\u65e0\u6cd5\u5e94\u4ed8\u8fd9\u52bf\u5a01\u529b\u731b\u7684\u4e00\u62db\uff0c\u4f46\u5979\u6050\u6015\u5931\u624b\u906d\u64d2\u53d7\u8fb1\uff0c\u6709\u635f\u5c55\u767d\u8138\u9762\uff0c\u7ade\u62b1\u4e86\u540c\u5f52 \u4e8e\u5c3d\u4e4b\u5fc3\uff0c\u4e0d\u7ba1\u7f69\u5411\u5468\u8eab\u8981\u5bb3\u7684\u638c\u5f71\uff0c\u4e00\u62db\u201c\u706b\u4e2d\u53d6\u6817\u201d\uff0c\u8fd0\u8d77\u5bb6\u4f20\u7edd\u827a\u201c\u6458\u661f\u624b\u201d\uff0c\u5411\u5bf9 \u65b9\u6251\u53bb\uff01 \u731b\u542c\u4e00\u58f0\u4e71\u54cd\uff0c\u4e00\u58f0\u66b4\u559d\uff0c\u4e00\u58f0\u60e8\u55e5\u2026\u2026 \u7b2c\u516d\u5341\u7ae0\u3000\u7fa4\u96c4\u593a\u5b9d>> \u53e4\u9f99\u300a\u5251\u5ba2\u884c\u300b \u7b2c\u516d\u5341\u7ae0\u3000\u7fa4\u96c4\u593a\u5b9d \u6fc0\u6218\u7684\u4eba\u5f71\u500f\u7136\u5206\u5f00\uff0c\u4e00\u4eba\u6367\u7740\u8155\u5b50\uff0c\u60e8\u547c\u540e\u9000\uff0c\u76f4\u8e09\u8dc4\u9000\u51fa\u4e2a\u6570\u6b65\u53bb\uff01 \u518d\u4e00\u770b\u53d7\u4f24\u540e\u9000\u7684\u4e0d\u662f\u6155\u5bb9\u7ea2\uff0c\u800c\u662f\u201c\u8d64\u53d1\u7075\u7334\u201d\u5e38\u53bb\u6076\uff01 \u6155\u5bb9\u7ea2\u5a07\u5598\u65e0\u529b\uff0c\u7c89\u8138\u715e\u767d\uff01 \u4f46\u8138\u4e0a\u5374\u62b9\u8fc7\u4e00\u7247\u6b23\u559c\u4e4b\u8272\uff01 \u539f\u6765\u6b64\u65f6\u573a\u4e2d\u5df2\u591a\u4e86\u4e00\u4eba\uff01\u6765\u4eba\u4e30\u795e\u5982\u7389\uff0c\u6001\u5ea6\u4ece\u5bb9\uff0c\u4e00\u4e2a\u5341\u8db3\u7684\u7f8e\u7537\u5b50\uff0c\u5e74\u7eaa\u751a\u8f7b\uff0c \u4f46\u7709\u5b87\u95f4\u82f1\u6c14\u8feb\u4eba\uff0c\u4f0a\u7136\u6709\u4e00\u4ee3\u5b97\u5e08\u7684\u98ce\u5ea6\uff0c\u671b\u7740\u201c\u8d64\u53d1\u7075\u7334\u201d\u7684\u72fc\u72c8\u50cf\uff0c\u5634\u89d2\u4e0a\u63a5\u7740\u4e00\u62b9 \u6de1\u6de1\u5632\u7b11\uff01 \u6b63\u662f\u9690\u8eab\u6811\u540e\u7684\u5c55\u767d\uff0c\u89c1\u6155\u5bb9\u7ea2\u52bf\u5371\uff0c\u9002\u65f6\u51fa\u9762\u62a2\u6551\uff0c\u53ea\u4e00\u638c\u4fbf\u628a\u72c2\u50b2\u4e0d\u53ef\u4e00\u4e16\u7684\u201c\u8d64 \u53d1\u7075\u7334\u201d\u9707\u51fa\u4e86\u4e8c\u4e08\u5f00\u5916\uff01 \u5a49\u513f\u4e00\u89c1\u5c55\u767d\u51fa\u73b0\uff0c\u5fc3\u4e2d\u4e00\u559c\uff0c\u63a5\u8fde\u4e24\u638c\u628a\u201c\u9b3c\u8c37\u9690\u53df\u201d\u903c\u8fdf\uff0c\u5a07\u8eaf\u4e00\u6643\u6251\u5411\u5c55\u767d\uff0c\u540c \u65f6\u53e3\u4e2d\u6025\u547c\u9053\uff1a\u767d\u54e5\u54e5\u2026\u2026\u201d \u5979\u79ef\u538b\u5728\u5fc3\u5e95\u7684\u70ed\u60c5\u4e00\u4e0b\u5b50\u7206\u53d1\u51fa\u6765\uff0c\u4f46\u53eb\u4e86\u4e00\u58f0\u767d\u54e5\u54e5\uff0c\u7a81\u60f3\u8d77\u7b54\u5e94\u59d0\u59d0\u5171\u5ac1\u5c55\u767d\u7684 \u4e8b\uff0c\u4e0d\u80fd\u5f53\u7740\u4eba\u8bf4\u51fa\uff0c\u4e0d\u7531\u5a07\u5c4a\u7f9e\u7ea2\uff0c\u5343\u8a00\u4e07\u8bed\uff0c\u4e00\u65f6\u5012\u8bf4\u4e0d\u51fa\u8bdd\u6765\uff01 \u4f46\u4ece\u5979\u90a3\u6fc0\u52a8\u7684\u795e\u60c5\uff0c\u4ee5\u53ca\u56e0\u4e3a\u5185\u5fc3\u6b23\u559c\u800c\u5728\u773c\u4e2d\u653e\u5c04\u51fa\u6765\u7684\u5149\u8f89\uff0c\u4ea6\u53ef\u77e5\u9053\u5979\u5fc3\u4e2d\u662f \u591a\u4e48\u9ad8\u5174\u4e86\u3002 \u5c55\u767d\u8fd8\u7ed9\u5979\u4e00\u4e2a\u4f1a\u5fc3\u7684\u5fae\u7b11\uff0c\u7136\u540e\u5bf9\u201c\u9b3c\u8c37\u4e8c\u5947\u201d\u9053\uff1a\u201c\u4e8c\u4f4d\u6709\u4e8b\u627e\u5728\u4e0b\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u76f4 \u63a5\u6765\u627e\uff0c\u5374\u5411\u4e24\u4e2a\u5973\u5b69\u5b50\u72e0\u4e0b\u8fa3\u624b\uff0c\u96be\u9053\u8fd9\u4e5f\u662f\u82f1\u96c4\u884c\u5f84\u5417\uff1f\u201d \u201c\u8d64\u53d1\u7075\u7334\u201d\u6b63\u5728\u8fd0\u529f\u7597\u4f24\uff0c\u65e0\u6cd5\u7b54\u8bdd\u3002\u201c\u9b3c\u8c37\u9690\u53df\u201d\u7ffb\u4e86\u4e2a\u767d\u773c\uff0c\u9053\uff1a\u201c\u59d3\u5c55\u7684\uff01 \u2018\u4ea1\u9b42\u8c37\u2019\u8ba9\u4f60\u6b7b\u91cc\u9003\u751f\uff0c\u4eca\u591c\u76f8\u9047\uff0c\u8bf4\u4e0d\u5f97\u8981\u4f60\u7684\u6b7b\u547d\uff01\u201d \u5c55\u767d\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4ea1\u9b42\u8c37\u2019\u5c55\u767d\u4e5f\u6ca1\u6709\u8f93\u7ed9\u4e8c\u4f4d\uff0c\u8fd9\u6b21\u4e8c\u4f4d\u60f3\u8981\u5c55\u767d\u7684\u547d\uff0c\u6050\u6015\u4e5f \u6ca1\u6709\u90a3\u4e48\u5bb9\u6613\uff01\u201d \u201c\u9b3c\u8c37\u9690\u53df\u201d\u5077\u773c\u671b\u4e86\u671b\u5e08\u5144\uff0c\u89c1\u201c\u8d64\u53d1\u7075\u7334\u201d\u4ecd\u5728\u95ed\u76ee\u8dcc\u5750\uff0c\u8fd0\u529f\u7597\u4f24\u3002\u4ed6\u5fc3\u4e2d\u660e \u767d\uff0c\u5c31\u51ed\u81ea\u5df1\u4e00\u4eba\u4e0d\u4e00\u5b9a\u662f\u5c55\u767d\u7684\u5bf9\u624b\uff0c\u4f46\u5634\u4e2d\u4e0d\u7518\u793a\u5f31\uff0c\u9634\u68ee\u4e00\u9635\u51b7\u7b11\uff0c\u9053\uff1a\u201c\u5982\u679c\u4f60\u5c0f \u5b50\u628a\u90a3\u672c\u5929\u4e0b\u7b2c\u4e00\u5947\u4e66\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\u7ed9\u4ea4\u51fa\u6765\uff0c\u8001\u592b\u4fbf\u53ef\u653e\u8fc7\u90a3\u6bb5\u8fc7\u8282\uff0c\u751a\u81f3\u6740\u6b7b\u8001 \u592b\u7231\u5f92\u2018\u4e09\u5bf8\u4e01\u2019\u90a3\u4ef6\u4e8b\uff0c\u4e5f\u4ece\u6b64\u4e0d\u63d0\u4e86\uff01\u201d \u5c55\u767d\u9053\uff1a\u201c\u5c0a\u9a7e\u653e\u5f03\u524d\u4ec7\uff0c\u8fd9\u4efd\u5bbd\u5927\u5fc3\u80f8\uff0c\u5c55\u767d\u611f\u6fc0\u4e0d\u5c3d\u3002\u4f46\u60f3\u8981\u5728\u4e0b\u4ea4\u51fa\u300a\u9501\u9aa8\u9500\u9b42 \u5929\u4f5b\u7737\u300b\uff0c\u4e8c\u4f4d\u53ef\u662f\u6765\u665a\u4e86\u4e00\u6b65\uff01\u201d \u201c\u9b3c\u8c37\u9690\u53df\u201d\u51b7\u51b7\u5730\u9053\uff1a\u201c\u8fd9\u6837\u8bf4\uff0c\u4f60\u5c0f\u5b50\u8fd8\u662f\u4e0d\u60f3\u628a\u4e66\u4ea4\u51fa\u6765\uff1f\u201d \u5c55\u767d\u9053\uff1a\u4fe1\u4e0d\u4fe1\u7531\u4f60\uff0c\u5728\u4e0b\u5b9e\u5728\u662f\u5f53\u7740\u5929\u4e0b\u7fa4\u96c4\u4e4b\u9762\uff0c\u628a\u90a3\u672c\u4e66\u6495\u6bc1\u4e86\uff01\u201d \u2026\u8d64\u53d1\u7075\u7334\u201d\u5df2\u8fd0\u529f\u5b8c\u6bd5\uff0c\u6643\u6643\u60a0\u60a0\u8d70\u4e0a\u524d\u6765\uff0c\u6076\u72e0\u72e0\u5730\u9053\uff1a \u201c\u8fd9\u70b9\u9b3c\u8ba1\u8c0b\uff0c\u9a97\u5f97\u4e86\u522b\u4eba\uff0c\u53ef\u9a97\u4e0d\u4e86\u6211\u8001\u4eba\u5bb6\u3002\u73b0\u5728\u8001\u54e5\u4fe9\u53ea\u95ee\u4e00\u53e5\uff0c\u4f60\u662f\u4ea4\uff1f\u8fd8\u662f \u4e0d\u4ea4\uff1f\u201d \u6c14\u52bf\u6c79\u6c79\uff0c\u5484\u5484\u903c\u4eba\uff01 \u201c\u9b3c\u8c37\u9690\u53df\u201d\u89c1\u5e08\u5144\u4f24\u597d\uff0c\u80c6\u6c14\u4e3a\u4e4b\u4e00\u58ee\uff0c\u4e5f\u8de8\u524d\u4e24\u6b65\uff0c\u84c4\u6ee1\u5468\u8eab\u529f\u529b\uff0c\u540c\u65f6\u5634\u4e2d\u559d \u9053\uff1a\u201c\u83ab\u975e\u4e00\u5b9a\u8981\u7b49\u6211\u4e24\u4f4d\u8001\u4eba\u5bb6\u52a8\u624b\u5417\uff01\u201d \u5c55\u767d\u89c1\u4ed6\u4e8c\u4eba\u86ee\u4e0d\u8bb2\u7406\u7684\u51f6\u72e0\u4e4b\u6001\uff0c\u5fc3\u4e2d\u4e00\u6c14\uff0c\u9053\uff1a\u201c\u522b\u8bf4\u8fd9\u672c\u79d8\u5f55\u5df2\u6bc1\uff0c\u5c31\u662f\u8fd8\u5728\uff0c \u5c55\u767d\u4e5f\u4e0d\u4f1a\u628a\u5b83\u4ea4\u7ed9\u6b3a\u5e08\u706d\u7956\u4e4b\u4eba\uff01\u2026\u2026\u201d \u8fd9\u53e5\u8bdd\u53ef\u4ee5\u8bf4\u662f\u6316\u4e86\u4e8c\u4eba\u7684\u75ae\u75a4\uff0c\u4e8c\u4eba\u540c\u65f6\u66b4\u559d\u4e86\u4e00\u58f0\uff1a \u201c\u5c0f\u5b50\u627e\u6b7b\uff01\u201d \u66b4\u559d\u58f0\u4e2d\uff0c\u4e00\u4e2a\u201c\u9ed1\u715e\u624b\u201d\uff0c\u4e00\u4e2a\u201c\u9634\u98ce\u638c\u201d\uff0c\u4e24\u79cd\u4e0d\u540c\u7684\u529b\u9053\uff0c\u540c\u65f6\u5411\u5c55\u767d\u653b\u5230\uff01 \u5c55\u767d\u4ee5\u201c\u5343\u5e7b\u98d8\u9999\u6b65\u201d\u6cd5\uff0c\u7565\u4e00\u56de\u65cb\uff0c\u5373\u5df2\u8131\u51fa\u4e8c\u4eba\u62db\u5f0f\u4e4b\u5916\uff0c\u4f46\u5e76\u672a\u51fa\u624b\u8fd8\u51fb\uff0c\u51b7\u51b7 \u7b11\u9053\uff1a\u201c\u771f\u7684\u8981\u52a8\u624b\uff0c\u5c55\u767d\u4e0d\u89c1\u5f97\u60e7\u6015\u4e8c\u4f4d\uff0c\u8fd8\u662f\u90a3\u53e5\u8001\u8bdd\uff0c\u5c55\u767d\u9000\u51fa\u6c5f\u6e56\uff0c\u4e0d\u613f\u518d\u4e0e\u4e8c\u4f4d \u7ed3\u6881\u5b50\uff01\u201d \u4e8c\u4eba\u540c\u65f6\u66b4\u6012\uff0c\u5927\u5947\u201c\u8d64\u53d1\u7075\u7334\u201d\u53f1\u9053\uff1a\u201c\u8c01\u7ba1\u4f60\u9000\u51fa\u6c5f\u6e56\u4e0d\u9000\u51fa\u6c5f\u6e56\uff01\u201d \u4e8c\u5947\u201c\u9b3c\u8c37\u9690\u53df\u201d\u4e5f\u53f1\u9053\uff1a\u201c\u4e0d\u4ea4\u51fa\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\u4fbf\u6c34\u4e0e\u4f60\u6ca1\u5b8c\uff01\u201d \u8bf4\u7740\u201c\u9634\u98ce\u638c\u201d\u3001\u201c\u9ed1\u715e\u624b\u201d\u53c8\u540c\u65f6\u653b\u5230\uff01 \u5c55\u767d\u65cb\u8eab\u95ea\u5f00\uff0c\u53ea\u4e0d\u8fd8\u624b\uff0c\u201c\u6c5f\u5357\u4e8c\u5947\u201d\u500f\u5ffd\u4e4b\u95f4\uff0c\u540c\u65f6\u62a2\u653b\u4e86\u4e94\u516d\u62db\uff01 \u62db\u62db\u90fd\u662f\u81f4\u547d\u6740\u62db\uff01 \u5728\u8fd9\u4e24\u5927\u9ad8\u624b\u5168\u7ec8\u672b\u8fd8\u624b\uff0c\u8fd9\u4efd\u8eab\u6cd5\u7684\u8f7b\u7075\u66fc\u5999\uff0c\u786e\u65f7\u53e4\u672b\u95fb\uff0c\u4f46\u4e5f\u88ab\u201c\u6c5f\u5357\u4e8c\u5947\u201d\u903c \u9000\u4e86\u5341\u6570\u6b65\u51fa\u53bb\uff01 \u5c55\u767d\u5df2\u88ab\u8feb\u5f97\u975e\u8981\u51fa\u624b\u81ea\u536b\u4e0d\u53ef\u4e86\uff0c\u5a49\u513f\u4e0e\u6155\u5bb9\u7ea2\u4e5f\u540c\u65f6\u8d76\u4e0a\u524d\u6765\uff0c\u51c6\u5907\u968f\u65f6\u63a5\u5e94\u5c55 \u767d\u2026\u2026 \u7a81\u7136\u2014\u2014\u8fdc\u5904\u4f20\u6765\u4e00\u58f0\u8c6a\u7b11\uff0c\u4e00\u58f0\u5389\u543c\uff0c\u4ea4\u6742\u4e86\u4e00\u58f0\u4ee4\u4eba\u6bdb\u53d1\u7ad6\u7acb\u7684\u60e8\u55e5\uff01 \u51e0\u4eba\u540c\u65f6\u4e00\u6115\uff0c\u8fde\u201c\u6c5f\u5357\u4e8c\u5947\u201d\u4e5f\u7981\u4e0d\u4f4f\u540c\u65f6\u4f4f\u624b\uff0c\u63a0\u9000\u4e00\u4e08\u5f00\u5916\uff0c\u8f6c\u8138\u5411\u53d1\u51fa\u58f0\u54cd\u4e4b \u5904\u671b\u53bb\u2026\u2026 \u56e0\u8fd9\u4e9b\u58f0\u97f3\uff0c\u663e\u793a\u51fa\u6050\u6016\uff0c\u4f3c\u4e4e\u6709\u4ec0\u4e48\u91cd\u5927\u7684\u7978\u5bb3\uff0c\u5c31\u8981\u53d1\u751f\u5728\u773c\u524d\u4f3c\u7684\uff01 \u5c31\u5728\u4f17\u4eba\u4e00\u6115\u4e4b\u95f4\uff0c\u4e00\u6761\u9ed1\u5f71\u75be\u5c04\u800c\u6765\uff01 \u9ed1\u5f71\u98de\u5c04\u75be\u63a0\uff0c\u811a\u4e0d\u6cbe\u5730\uff0c\u4eba\u672a\u81f3\u5148\u53d1\u58f0\u53eb\u9053\uff1a\u201c\u5c55\u54e5\u2026\u2026\u5c0f\u4fa0\uff01\u6709\u4eba\u627e\u4f60\uff01\u201d \u5c55\u767d\u5df2\u542c\u51fa\u6765\u4eba\u662f\u6709\u7740\u201c\u6c5f\u5357\u7b2c\u4e00\u7f8e\u4eba\u201d\u4e4b\u79f0\u7684\u91d1\u5f69\u51e4\uff0c\u4f46\u4ed6\u542c\u51fa\u91d1\u5f69\u51e4\u8bed\u8c03\u4e2d\u5145\u6ee1\u4e86 \u60ca\u9a87\uff0c\u4f3c\u4e4e\u662f\u9047\u5230\u4e86\u4ec0\u4e48\u91cd\u5927\u7684\u7978\u4e8b\uff0c\u5f53\u5373\u4e00\u6446\uff0c\u7b54\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\u627e\u6211\uff1f\u4f7f\u5f97\u59d1\u5a18\u8fd9\u6837\u60ca \u60f6\uff1f\u201d \u91d1\u5f69\u51e4\u4fca\u7f8e\u65e0\u6bd4\u7684\u8138\u4e0a\u4e00\u7247\u60ca\u5bb9\uff0c\u5a07\u5598\u5401\u5401\uff0c\u9053\uff1a\u201c\u4e5d\u5927\u95e8\u6d3e\uff0c\u8fd8\u6709\u2026\u2026\u5176\u4ed6\u5f88\u591a\u4eba\uff0c \u4ee5\u53ca\u2018\u5357\u6d77\u95e8\u2019\u7684\u8bb8\u591a\u9ad8\u624b\uff0c\u90fd\u8981\u627e\u5c55\u5c0f\u4fa0\uff01\u201d \u5c55\u767d\u4e00\u695e\uff0c\u9053\uff1a\u201c\u8fd9\u4e48\u591a\u4eba\uff0c\u90fd\u662f\u627e\u6211\u7684\u5417\uff1f\u201d \u91d1\u5f69\u98ce\u54bd\u4e86\u53e3\u53e3\u6c34\uff0c\u8fde\u8fde\u70b9\u5934\uff0c\u53c8\u9053\uff1a\u201c\u4ed6\u4eec\u4f3c\u4e4e\u662f\u7ea6\u96c6\u9f50\u4e86\u6765\u627e\u5c55\u5c0f\u4fa0\uff0c\u96f7\u5927\u53d9\u8bf4\u5c55 \u5c0f\u4fa0\u4e0d\u5728\uff0c\u4ed6\u4eec\u4e0d\u60c5\uff0c\u8981\u6328\u5c4b\u641c\u67e5\uff0c\u2018\u7a77\u5bb6\u5e2e\u2019\u7684\u4eba\u51fa\u9762\u62e6\u963b\uff0c\u4ed6\u4eec\u5df2\u51fa\u624b\u4f24\u4e86\u4eba\uff0c\u800c\u4e14\u58f0 \u8a00\u4e0d\u5bfb\u51fa\u5c55\u5c0f\u4fa0\uff0c\u8981\u628a\u5728\u573a\u6240\u6709\u4e4b\u4eba\uff0c\u4e00\u4e00\u6740\u6b7b\uff01\u2026\u2026\u201d \u91d1\u5f69\u51e4\u4e00\u53e3\u6c14\u8bf4\u5230\u8fd9\u91cc\uff0c\u5c55\u767d\u5df2\u6025\u9053\uff1a\u6709\u8fd9\u7b49\u4e8b\uff0c\u6211\u53bb\u770b\u770b\uff01\u201d \u8bf4\u7f62\u98de\u8eab\u5411\u6765\u8def\u63a0\u56de\uff01 \u201c\u6c5f\u5357\u4e8c\u5947\u201d\u9661\u559d\u4e00\u58f0\uff1a\u201c\u54ea\u91cc\u8d70\uff01\u201d \u559d\u58f0\u4e2d\u968f\u540e\u8ffd\u53bb\uff01 \u5a49\u513f\u3001\u6155\u5bb9\u7ea2\u3001\u91d1\u5f69\u51e4\u4e5f\u5148\u540e\u811a\u7d27\u8ddf\u7740\u8ffd\u4e0a\uff01 \u5c1a\u8ddd\u79bb\u9662\u5899\u6709\u5341\u6570\u4e08\u4e4b\u8fdc\uff0c\u5c55\u767d\u8feb\u4e0d\u53ca\u5f85\uff0c\u4e00\u5f0f\u201c\u76f4\u4e0a\u9752\u4e91\u201d\uff0c\u98de\u8eab\u8dc3\u8d77\uff0c\u534a\u7a7a\u4e2d\u53cc\u81c2 \u4e00\u6296\uff0c\u4e2d\u95f4\u672b\u501f\u4efb\u4f55\u57ab\u811a\u4e4b\u529b\uff0c\u5df2\u8dc3\u4e0a\u4e86\u9ad8\u5899\uff01 \u653e\u773c\u5411\u9662\u4e2d\u4e00\u770b\uff0c\u5e7f\u5927\u9662\u843d\u91cc\u8db3\u6709\u4e8c\u3001\u4e09\u767e\u4eba\u6df7\u6218\u5728\u4e00\u8d77[\u638c\u98ce\u6307\u5f71\uff0c\u5200\u5251\u5149\u5bd2\uff0c\u4e0d\u65f6 \u4f20\u51fa\u53d7\u4f24\u4e4b\u4eba\u7684\u95f7\u54fc\u60e8\u55e5\uff01 \u5730\u4e0b\u5df2\u5012\u6709\u5341\u6570\u5177\u65ad\u5934\u6b8b\u80f6\u7684\u5c38\u4f53\uff01 \u6218\u51b5\u5341\u5206\u60e8\u70c8\uff01 \u5c55\u767d\u9661\u559d\u4e00\u58f0\uff1a\u201c\u4f4f\u624b\uff01\u201d \u8fd9\u4e00\u58f0\u5927\u559d\uff0c\u58f0\u5982\u96f7\u9707\uff0c\u9662\u843d\u4e2d\u6df7\u6218\u4e4b\u4eba\uff0c\u540c\u65f6\u6536\u62db\u505c\u8eab\uff0c\u8dc3\u6b65\u9000\u51fa\u5708\u5916\uff01 \u5657\uff01\u5657\uff01\u5657\uff01\u2026\u2026\u5148\u540e\u516d\u6761\u4eba\u5f71\u7eb5\u843d\u9662\u4e2d\uff01 \u5f53\u5148\u4fca\u7f8e\u5c11\u5e74\uff0c\u98ce\u5ea6\u9ad8\u96c5\uff0c\u7389\u9762\u751f\u6210\uff0c\u6b63\u662f\u5c55\u767d\uff01 \u540e\u8fb9\u8ddf\u7740\u7684\u662f\u201c\u6c5f\u5357\u4e8c\u5947\u201d\u3001\u5a49\u513f\u3001\u6155\u5bb9\u7ea2\u53ca\u91d1\u5f69\u51e4\u3002 \u5c55\u767d\u4fca\u76ee\u4e00\u626b\u5730\u4e0b\u7684\u6a2a\u5c38\uff0c\u5fc3\u4e2d\u4e00\u9635\u6fc0\u8d8a\u3002\u6602\u58f0\u9053\uff1a\u201c\u4f55\u65b9\u9ad8\u4eba\u5bfb\u627e\u5728\u4e0b\uff1f\u4e3a\u4ec0\u4e48\u4e00\u8a00 \u4e0d\u5408\u5373\u5f00\u8fd9\u4e48\u5927\u7684\u6740\u52ab\uff0c\u96be\u9053\u8fd9\u503c\u5f97\u4e48\uff1f\u201d \u4e00\u58f0\u5b8f\u4eae\u4f5b\u53f7\uff0c\u4eba\u7fa4\u4e2d\u8d70\u51fa\u4e00\u4f4d\u8eab\u8eaf\u9ad8\u5927\u3001\u7740\u7070\u8272\u67b6\u88df\u3001\u767d\u53d1\u767d\u7709\uff0c\u4f46\u9762\u8272\u7ea2\u6da6\u7684\u8001 \u50e7\u3002 \u9ad8\u5927\u8001\u50e7\u5411\u5c55\u767d\u5355\u638c\u95ee\u8baf\uff0c\u9053\uff1a\u201c\u5c0f\u65bd\u4e3b\u60f3\u5fc5\u5c31\u662f\u5f53\u5e74\u4fa0\u540d\u6ee1\u56db\u6d77\u2018\u9739\u96f3\u5251\u2019\u5c55\u4e91\u5929\u5c55 \u5927\u4fa0\u7684\u540e\u4eba\u4e86\uff1f\u201d \u5c55\u767d\u70b9\u5934\u79f0\u662f\u3002 \u9ad8\u5927\u8001\u50e7\u53c8\u9053\uff1a\u201c\u8001\u8896\u4e43\u5f53\u4eca\u5c11\u6797\u5bfa\u62ff\u95e8\u4e07\u4e08\u2018\u667a\u6d77\u2019\uff0c\u73b0\u5728\u501f\u91cd\u4e5d\u5927\u638c\u95e8\u7684\u9762\u5b50\uff0c\u60f3 \u5411\u5c55\u5c0f\u4fa0\u8ba8\u8981\u4e00\u4ef6\u4e1c\u897f\uff0c\u4e0d\u77e5\u5c55\u5c0f\u4fa0\u80af\u8d4f\u8138\u5426\uff1f\u201d \u5c11\u6797\u638c\u95e8\u201c\u667a\u6d77\u201d\u8bdd\u672b\u8bf4\u5b8c\uff0c\u5ce8\u5d4b\u3001\u5d06\u5cd2\u3001\u6b66\u5f53\u3001\u6606\u4ed1\u3001\u70b9\u82cd\u3001\u534e\u5c71\u3001\u957f\u767d\u3001\u6cd5\u534e\u516b\u5927 \u638c\u95e8\u4eba\uff0c\u540c\u65f6\u8de8\u524d\u4e24\u6b65\uff0c\u5728\u201c\u667a\u6d77\u201d\u8eab\u540e\u4e00\u5b57\u5e76\u80a9\u6392\u671b\u5b9a\u5c55\u767d\uff01 \u5c55\u767d\u89c1\u8fd9\u5c11\u6797\u638c\u95e8\u8bdd\u4e2d\u542b\u610f\uff0c\u6ee1\u662f\u5f3a\u7d22\u786c\u8ba8\u53e3\u543b\uff0c\u53c8\u89c1\u516b\u5927\u638c\u95e8\u864e\u89c6\u803d\u65e2\u7684\u60c5\u5f62\uff0c\u663e\u7136 \u6709\u70b9\u4ed7\u52bf\u6b3a\u4eba\u7684\u5473\u9053\uff0c\u6697\u5fd6\uff1a\u201c\u6028\u4e0d\u5f97\u638c\u6b66\u6797\u725b\u8033\u8fbe\u6570\u767e\u5e74\u7684\u4e5d\u5927\u95e8\u6d3e\uff0c\u4f1a\u5ffd\u7136\u6d88\u6c89\u4e0d\u632f\uff0c \u51ed\u8fd9\u4e9b\u638c\u95e8\u4eba\u7684\u5634\u8138\uff0c\u5f88\u96be\u6210\u5c31\u5927\u4e8b\u2026\u2026\u201d \u56e0\u4e3a\u4e5d\u5927\u638c\u95e8\u8a00\u8c08\u4e3e\u6b62\u5a01\u80c1\u6027\u751a\u5927\uff0c\u5f15\u8d77\u5c55\u767d\u4e0d\u5feb\uff0c\u5f53\u4e0b\u77a5\u4e86\u4e5d\u5927\u638c\u95e8\u4e00\u773c\uff0c\u51b7\u51b7\u7b11 \u9053\uff1a\u201c\u6709\u4ec0\u4e48\u9700\u6c42\uff0c\u5927\u5e08\u8bf7\u8bf4\u5c31\u662f\uff01\u53ea\u8981\u4e0d\u8fdd\u80cc\u6b66\u6797\u6b63\u4e49\uff0c\u5c55\u767d\u80fd\u529b\u6240\u53ca\uff0c\u4e00\u5b9a\u7167\u529e\uff01\u201d \u5c55\u767d\u8fd9\u8bdd\u8bf4\u5f97\u4e49\u6b63\u8bcd\u4e25\uff0c\u4e0d\u5351\u4e0d\u4ea2\uff0c\u4e0d\u5c11\u4eba\u5fc3\u4e2d\u6697\u6697\u94a6\u4f69\uff1a \u201c\u51ed\u8fd9\u70b9\u5b50\u5e74\u7eaa\uff0c\u5f53\u7740\u4e5d\u5927\u95e8\u6d3e\u638c\u95e8\uff0c\u4e0d\u8c08\u6b66\u529f\uff0c\u5149\u662f\u8fd9\u4efd\u8c08\u5410\u548c\u98ce\u5ea6\uff0c\u4fbf\u4ee4\u4eba\u5fc3 \u6298\u2026\u2026\u201d \u4f46\u5c55\u767d\u8bdd\u4e2d\u4e4b\u610f\uff0c\u5bf9\u5c11\u6797\u638c\u95e8\u65e0\u672d\u7684\u8a00\u8bcd\uff0c\u4e5f\u9690\u542b\u8bbd\u523a\u5473\u6e29\u3002 \u201c\u667a\u6d77\u201d\u8eab\u4e3a\u5c11\u6797\u638c\u95e8\uff0c\u5f53\u7136\u4e0d\u4f1a\u542c\u4e0d\u51fa\u6765\uff0c\u4f46\u56e0\u4e3a\u6b64\u4e3e\u5173\u7cfb\u91cd\u5927\uff0c\u4ec5\u8001\u8138\u4e00\u7ea2\uff0c\u4ecd\u7ee7 \u7eed\u8a00\u9053\uff1a\u201c\u5176\u5b9e\u8001\u94a0\u4e5f\u4e0d\u4f1a\u6709\u4ec0\u4e48\u989d\u5916\u7684\u82db\u6c42\uff0c\u53ea\u5e0c\u671b\u5c0f\u4fa0\u628a\u90a3\u672c\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\uff0c\u4ea4 \u56de\u8001\u94a0\uff0c\u8001\u8896\u8fde\u540c\u516b\u5927\u638c\u95e8\u8f6c\u8138\u5c31\u8d70\uff0c\u7edd\u4e0d\u591a\u8bf4\u534a\u53e5\u5e9f\u8bdd\uff01\u201d \u5c55\u767d\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u5047\u5982\u90a3\u672c\u79d8\u5f55\u8fd8\u5728\u672c\u4eba\u8eab\u4e0a\uff0c\u5c31\u51ed\u638c\u95e8\u4eba\u4e00\u53e5\u8bdd\uff0c\u5c55\u767d\u5373\u5f53\u53cc\u624b\u5949 \u4e0a\u3002\u53ef\u662f\uff0c\u5927\u5e08\u6765\u665a\u4e86\u4e00\u6b65\uff0c\u90a3\u672c\u79d8\u5f55\u5728\u6570\u65e5\u4e4b\u524d\uff0c\u5373\u5df2\u5f53\u4f17\u6495\u6bc1\uff0c\u6b64\u6765\u6050\u6015\u8981\u4f7f\u4e5d\u5927\u638c\u95e8 \u4e4b\u5c0a\u7a7a\u8dd1\u2014\u8d9f\u4e86\uff01\u201d \u5c55\u767d\u5b9e\u8bdd\u5b9e\u8bf4\uff0c\u63a8\u5378\u5c11\u6797\u638c\u95e8\u201c\u667a\u6d77\u63b8\u5e08\u201d\u8138\u8272\u4e0d\u53d8\uff0c\u4ecd\u5411\u5c55\u767d\u9053\uff1a\u201c\u8001\u8872\u4e0d\u59a8\u5b9e\u8bdd\u5b9e \u8bf4\uff0c\u90a3\u672c\u79d8\u5f55\uff0c\u4e43\u662f\u672c\u5bfa\u524d\u4ee3\u638c\u95e8\u2018\u82e6\u6c34\u5927\u5e08\u2019\u4f1a\u540c\u6b66\u5f53\u524d\u4ee3\u638c\u95e8\u2018\u94c1\u5fc3\u9053\u957f\u2019\uff0c\u5bf9\u4e8c\u767e\u4f59 \u5e74\u4e4b\u524d\u4e00\u4ee3\u5947\u4eba\u2018\u53ea\u773c\u6717\u541b\u2019\u52a0\u4e86\u4e00\u6b21\u6388\u624b\u4e4b\u6069\uff0c\u2018\u53ea\u773c\u90ce\u541b\u2019\u4e3a\u611f\u6069\u56fe\u62a5\uff0c\u5c06\u8017\u5c3d\u7ec8\u751f\u5fc3 \u8840\u6240\u8457\u7684\u4e00\u672c\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\u8d60\u4e0e\u672c\u5bfa\u2018\u82e6\u6c34\u5927\u5e08\u2019\u53ca\u6b66\u5f53\u2018\u94c1\u5fc3\u9053\u957f\u2019\uff0c\u56e0\u4e3a\u8fd9\u672c\u79d8 \u5f55\u4e8e\u6b66\u6797\u5173\u7cfb\u751a\u5927\uff0c\u4e24\u4f4d\u524d\u4ee3\u638c\u95e8\u5546\u8bae\u7684\u7ed3\u679c\uff0c\u5c06\u8fd9\u672c\u79d8\u5f55\u4ea4\u4e0e\u672c\u5bfa\u4fdd\u7ba1\uff0c\u672c\u5bfa\u5386\u4ee3\u638c\u95e8\u5747 \u5c06\u8fd9\u672c\u79d8\u5f55\u73cd\u85cf\u4e8e\u672c\u5bfa\u2018\u85cf\u7ecf\u697c\u2019\u4f5b\u9f9b\u4e2d\uff0c\u5982\u4eca\uff0c\u6c5f\u6e56\u8f70\u4f20\u672c\u79d8\u5f55\u5728\u5c0f\u65bd\u4e3b\u624b\u4e2d\uff0c\u8001\u94a0\u4e00\u67e5 \u85cf\u7ecf\u697c\u7684\u85cf\u73cd\uff0c\u679c\u7136\u8fd9\u672c\u79d8\u5f55\u5931\u8e2a\uff01\u201d \u201c\u667a\u6d77\u63b8\u5e08\u201d\u8bf4\u5230\u8fd9\u91cc\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u4f3c\u662f\u60cb\u60dc\u5bfa\u4e2d\u5386\u4ee3\u9547\u5bfa\u4e4b\u5b9d\u7684\u5931\u7a83\uff0c\u7adf\u662f\u8f6e\u5728\u4ed6\u62c5 \u4efb\u638c\u95e8\u4e4b\u4efb\u65f6\u53d1\u751f\u3002 \u4f17\u4eba\u4e5f\u4e3a\u8fd9\u4ece\u672a\u542c\u8bf4\u8fc7\u7684\u4e8c\u767e\u4f59\u5e74\u4e2d\u7684\u79d8\u95fb\uff0c\u542c\u5f97\u5165\u4e86\u795e\uff0c\u90fd\u5927\u7741\u53cc\u773c\uff0c\u671b\u7740\u5c11\u6797\u638c\u95e8 \u8bf4\u4e0b\u53bb\u3002 \u201c\u667a\u6d77\u63b8\u5e08\u201d\u7ee7\u7eed\u9053\uff1a\u201c\u8001\u8872\u867d\u7136\u4ece\u672a\u4e0e\u5c0f\u65bd\u4e3b\u4f1a\u8fc7\u9762\uff0c\u4f46\u4ece\u95e8\u4e0b\u5f1f\u5b50\u4e4b\u62a5\u544a\uff0c\u53ca\u95fb\u542c \u6c5f\u6e56\u4f20\u8a00\uff0c\u77e5\u4e00\u4ee3\u5927\u4fa0\u2018\u9739\u96f7\u5251\u2019\u5c55\u4e91\u5929\u4e4b\u5b50\uff0c\u800c\u4e14\u5149\u660e\u78ca\u843d\u5927\u4e49\u51db\u7136\uff0c\u4e1d\u6beb\u4e0d\u4f1a\u662f\u6f5c\u5165\u672c \u5bfa\u5077\u7a83\u79d8\u5f55\u4e4b\u4eba\uff0c\u60f3\u662f\u5c0f\u65bd\u4e3b\u4ece\u522b\u4eba\u624b\u4e2d\u8f97\u8f6c\u5f97\u5230\uff0c\u8001\u8872\u4e3a \u7b2c\u516d\u5341\u4e00\u7ae0\u3000\u529b\u632b\u201c\u6392\u9aa8\u4ed9\u201d>> \u53e4\u9f99\u300a\u5251\u5ba2\u884c\u300b \u7b2c\u516d\u5341\u4e00\u7ae0\u3000\u529b\u632b\u201c\u6392\u9aa8\u4ed9\u201d \u60f3\u96f7\u5927\u53d4\u706b\u66b4\u813e\u6c14\uff0c\u5c82\u80fd\u4e00\u518d\u88ab\u903c\uff1f\u5f53\u4e0b\u4ef0\u5929\u4e00\u9635\u72c2\u7b11\uff01 \u7b11\u7f62\uff0c\u9762\u8272\u4e00\u6c89\uff0c\u9053\uff1a\u201c\u8001\u548c\u5c1a\uff0c\u8fd9\u53ef\u662f\u4f60\u903c\u51fa\u6765\u7684\uff0c\u4e00\u5207\u8d23\u4efb\u7531\u4f60\u8d1f\u62c5\uff01\u8001\u592b\u518d\u95ee\u4f60 \u4e00\u53e5\uff1a\u4f60\u53ef\u77e5\u9053\u8fd9\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\uff0c\u662f\u600e\u6837\u843d\u5728\u8d35\u5bfa\u624b\u4e2d\u7684\u5417\uff1f\u201d \u201c\u667a\u6d77\u7985\u5e08\u201d\u4e5f\u5728\u706b\u5934\u4e0a\uff0c\u95fb\u8a00\u6beb\u4e0d\u8003\u8651\u5730\u7b54\u9053\uff1a\u201c\u8001\u8872\u5df2\u7ecf\u8bf4\u8fc7\u4e00\u904d\u4e86\uff0c\u4e43\u2018\u53ea\u773c\u90ce \u541b\u2019\u8001\u524d\u8f88\uff0c\u611f\u5ff5\u672c\u5bfa\u4e0a\u4ee3\u638c\u95e8\u2018\u82e6\u6c34\u4e0a\u4eba\u2019\u63f4\u624b\u4e4b\u6069\uff0c\u8d60\u4e0e\u672c\u5bfa\u7684\uff01\u201d \u96f7\u5927\u53d4\u9053\uff1a\u90a3\u4e48\uff0c\u9002\u624d\u5c55\u5c0f\u4fa0\u6240\u8bf4\u7684\u5462\uff1f\u201d \u201c\u667a\u6d77\u7985\u5e08\u201d\u9053\uff1a\u201c\u5206\u660e\u662f\u98a0\u5012\u9ed1\u81ea\uff0c\u4e00\u6d3e\u80e1\u8a00\uff01\u201d \u5c55\u767d\u4ece\u6765\u672a\u5f53\u4f17\u88ab\u4eba\u5982\u6b64\u9a82\u8fc7\uff0c\u5f53\u65f6\u8e0f\u524d\u4e24\u6b65\uff0c\u559d\u9053\uff1a\u201c\u4f4f\u5634\uff01\u2026\u2026\u201d \u96f7\u5927\u53d4\u62e6\u4f4f\u5c55\u767d\uff0c\u5411\u201c\u667a\u6d77\u7985\u5e08\u201d\u9053\uff1a\u201c\u8001\u592b\u53ef\u4ee5\u544a\u8bc9\u4f60\uff0c\u5c55\u5c0f\u4fa0\u6240\u8bf4\uff0c\u4e00\u70b9\u4e0d\u5047\uff0c\u90a3 \u672c\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\u786e\u4e3a\u6b66\u529f\u5e73\u5e73\u3001\u4f46\u5fc3\u667a\u8fc7\u4eba\u7684\u2018\u4e94\u722a\u7075\u72d0\u2019\u5f97\u53bb\uff01\u201d \u201c\u6392\u9aa8\u4ed9\u201d\u6115\u7136\u52a8\u5bb9\u2026\u2026 \u201c\u667a\u6d77\u7985\u5e08\u201d\u51b7\u7b11\u9053\uff1a\u201c\u4e00\u65ec\u8c0e\u8bdd\uff0c\u518d\u52a0\u4e0a\u9601\u4e0b\u4e00\u4eba\uff0c\u4fbf\u53ef\u4ee5\u6210\u771f\u4e86\u5417\uff1f\u201d \u96f7\u5927\u53d4\u5ffd\u9053\uff1a\u201c\u4e8b\u5b9e\u5982\u6b64\uff0c\u7531\u4e0d\u5f97\u4f60\u4e0d\u4fe1\uff01\u201d \u201c\u667a\u6d77\u63b8\u5e08\u201d\u9762\u5bd2\u5982\u51b0\uff0c\u9053\uff1a\u201c\u7167\u4f60\u8bf4\u6765\uff0c\u8fd9\u672c\u79d8\u5f55\uff0c\u53c8\u600e\u4e48\u5230\u4e86\u672c\u5bfa\u524d\u4ee3\u638c\u95e8\u624b\u4e2d\uff0c \u53c8\u600e\u6210\u4e86\u672c\u5bfa\u5386\u4ee3\u76f8\u4f20\u9547\u5c71\u4e4b\u5b9d\uff1f\u96be\u9053\u4ee5\u5802\u5802\u5c11\u6797\u6d3e\uff0c\u4e5f\u4f1a\u5b66\u90a3\u4e0b\u4e94\u95e8\u7684\u2018\u4e94\u722a\u7075\u72d0\u2019\uff0c\u4ece \u522b\u4eba\u624b\u4e2d\u62a2\u52ab\u6765\u4e0d\u6210\uff1f\u201d \u201c\u667a\u6d77\u7985\u5e08\u201d\u6012\u6781\uff0c\u4ee5\u81f4\u53e3\u4e0d\u62e9\u8a00\uff0c\u4ed6\u6ca1\u60f3\u5230\u6b64\u8a00\u4e00\u51fa\uff0c\u5d06\u5cd2\u638c\u95e8\u5c06\u505a\u5982\u4f55\u611f\u60f3\uff1f \u5f53\u65f6\u5d06\u5cd2\u638c\u95e8\u201c\u6392\u9aa8\u4ed9\u201d\u9762\u8272\u7acb\u53d8\uff0c\u56de\u5934\u4ee5\u4e24\u9053\u51b7\u5251\u4f3c\u7684\u76ee\u5149\uff0c\u72e0\u72e0\u5730\u77aa\u4f4f\u201c\u667a\u6d77\u7985 \u5e08\u201d\uff0c\u8138\u4e0a\u6296\u9732\u51fa\u65e0\u9650\u6740\u673a\u2026\u2026 \u4f46\u672a\u5bb9\u4ed6\u53d1\u4f5c\uff0c\u96f7\u5927\u53d4\u5feb\u5634\u63a5\u8fc7\u6765\uff0c\u51b7\u51b7\u5730\u9053\uff1a\u201c\u6b63\u662f\u5982\u6b64\uff01\u201d \u201c\u667a\u6d77\u7985\u5e08\u201d\u9762\u8272\u7acb\u53d8\u2026\u2026 \u96f7\u5927\u53d4\u4e5f\u672b\u7b49\u4ed6\u53d1\u4f5c\uff0c\u63a5\u4e0b\u53bb\u9053\uff1a\u201c\u5c31\u4e8b\u8bba\u4e8b\uff0c\u5c11\u6797\u524d\u4ee3\u638c\u95e8\u2018\u82e6\u6c34\u4e0a\u4eba\u2019\u52ab\u593a\u6b64\u4e66\u51fa \u53d1\u70b9\u4e0e\u2018\u4e94\u722a\u7075\u72d0\u2019\u4e0d\u540c\u3002\u2018\u4e94\u722a\u7075\u72d0\u2019\u6697\u4e0b\u6bd2\u624b\uff0c\u52ab\u593a\u6b64\u4e66\uff0c\u662f\u60f3\u636e\u4e3a\u5df1\u6709\uff0c\u7ec3\u6210\u7edd\u4e16\u6b66 \u529f\uff0c\u4ee5\u4fbf\u79f0\u9738\u6c5f\u6e56\uff1b\u4f46\u2018\u82e6\u6c34\u4e0a\u4eba\u2019\u52ab\u593a\u6b64\u4e66\uff0c\u5374\u662f\u60f3\u6d88\u5f25\u6b66\u6797\u6d69\u52ab\uff0c\u79d8\u5f55\u5230\u624b\uff0c\u8fde\u770b\u90fd\u4e0d \u770b\u4e00\u773c\uff0c\u5373\u675f\u4e4b\u9ad8\u9601\uff0c\u8fd9\u4e5f\u5c31\u662f\u5c11\u6797\u5386\u4ee3\u76f8\u4f20\u8fd9\u672c\u79d8\u5f55\u3001\u5c11\u6797\u5f1f\u5b50\u5374\u4ece\u65e0\u4e00\u4eba\u4e60\u8fc7\u79d8\u5f55\u4e0a\u4e4b \u6b66\u529f\u7684\u539f\u56e0\uff0c\u800c\u4e14\u5386\u4ee3\u638c\u95e8\u4ea4\u63a5\u65f6\uff0c\u90fd\u9686\u91cd\u5ba3\u8a93\uff0c\u4e0d\u8bb8\u79c1\u9605\u79d8\u5f55\uff0c\u5982\u80c6\u6562\u5077\u770b\uff0c\u5373\u4e3a\u5c11\u6797\u53db \u9006\u3002\u6240\u8c13\u2018\u5343\u52ab\u4e00\u5ff5\uff0c\u4e00\u5ff5\u5343\u52ab\u2019\uff0c\u5c11\u6797\u524d\u4ee3\u638c\u95e8\u8fd9\u4e00\u7247\u4fa0\u5fc3\u4e49\u80a0\uff0c\u8001\u592b\u51b3\u4e0d\u62b9\u6740\uff01\u201d \u8fd9\u4e43\u662f\u5c11\u6797\u638c\u95e8\u4e4b\u79d8\uff0c\u53ea\u6709\u72ec\u4efb\u638c\u95e8\u4e4b\u4eba\uff0c\u5728\u63a5\u4efb\u638c\u95e8\u65f6\uff0c\u65b9\u7531\u4e0a\u4e00\u4ee3\u638c\u95e8\u544a\u77e5\uff0c\u5c31\u7b97 \u5bfa\u5185\u6700\u4eb2\u4fe1\u5f1f\u5b50\u4e5f\u4e0d\u5f97\u4e0e\u95fb\u3002 \u5982\u4eca\uff0c\u7531\u8fd9\u5bfa\u5916\u4e4b\u4eba\u53e3\u4e2d\u9053\u51fa\uff0c\u201c\u667a\u6d77\u7985\u5e08\u201d\u7684\u9707\u60ca\u662f\u53ef\u60f3\u800c\u77e5\u7684\uff01 \u201c\u667a\u6d77\u7985\u5e08\u201d\u65e2\u60ca\u4e14\u6012\uff0c\u66b4\u559d\u9053\uff1a\u201c\u5927\u80c6\u72c2\u5f92\uff01\u6b64\u4e8b\u4f60\u5982\u4f55\u5f97\u77e5\uff1f\u201d \u96f7\u5927\u53d4\u9762\u8272\u4e00\u5bd2\uff0c\u9053\uff1a\u201c\u79c3\u9a74\u6562\u5bf9\u8001\u592b\u65e0\u793c\uff01\u8001\u592b\u518d\u95ee\u4f60\u4e00\u53e5\uff0c\u4f60\u53ef\u77e5\u9053\u4f60\u5bfa\u4e2d\u4e0a\u4e00\u4ee3 \u638c\u95e8\u4eba\u4e09\u4f4d\u5e08\u5144\u600e\u4e48\u6b7b\u7684\uff1f\u201d \u8fd9\u4e00\u95ee\uff0c\u628a\u201c\u667a\u6d77\u7985\u5e08\u201d\u95ee\u5f97\u819b\u76ee\u7ed3\u820c\uff0c\u534a\u664c\u65e0\u8a00\u4ee5\u5bf9\uff01 \u4f46\u8bdd\u6324\u5728\u5634\u53e3\u4e0a\uff0c\u201c\u667a\u6d77\u7985\u5e08\u201d\u5c82\u7518\u7f04\u9ed8\uff0c\u5f53\u4e0b\u6b8b\u7709\u5012\u7acb\uff0c\u6012\u76ee\u5706\u7741\uff0c\u665a\u9053\uff1a\u201c\u672c\u95e8\u4e09 \u4f4d\u5e08\u4f2f\u4e3a\u4ec0\u4e48\u6b7b\u7684\uff0c\u96be\u9053\u4f60\u4f1a\u6e05\u695a\uff1f\u201d \u96f7\u5927\u53d4\u54c8\u54c8\u4e00\u7b11\u9053\uff1a\u201c\u6211\u4e0d\u6e05\u695a\uff1f\u8001\u592b\u6562\u8bf4\uff0c\u2018\u6b63\u5fc3\u2019\u4e00\u6b7b\uff0c\u5929\u4e0b\u77e5\u9053\u6b64\u4e8b\u7684\uff0c\u552f\u6709\u8001 \u592b\u4e00\u4eba\uff01\u201d \u201c\u667a\u6d77\u7985\u5e08\u201d\u6012\u6781\uff0c\u53f1\u9053\uff1a\u201c\u5b8c\u5168\u662f\u4e00\u6d3e\u80e1\u8a00\uff01\u201d \u96f7\u5927\u53d4\u4e00\u77aa\u773c\uff0c\u9053\uff1a\u201c\u770b\u6765\u4f60\u662f\u975e\u8981\u903c\u7740\u8001\u592b\u628a\u5168\u90e8\u79d8\u95fb\u90fd\u6296\u9732\u51fa\u6765\u4e0d\u53ef\u4e86\uff01\u6211\u8001\u5b9e\u544a \u8bc9\u4f60\u5427\uff01\u4f60\u4e09\u4f4d\u5e08\u4f2f\uff0c\u5c31\u662f\u56e0\u4e3a\u5077\u770b\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\uff0c\u89e6\u72af\u4e86\u5bfa\u89c4\uff0c\u81ea\u884c\u9707\u788e\u5929\u7075\u800c\u6b7b \u7684\uff01\u201d \u201c\u667a\u6d77\u7985\u5e08\u201d\u9762\u8272\u4e00\u53d8\u518d\u53d8\uff0c\u5389\u53f1\u9053\uff1a\u201c\u8001\u8872\u4e09\u4f4d\u5e08\u4f2f\uff0c\u4e43\u4e0a\u4ee3\u638c\u95e8\u4eba\u5e08\u5144\uff0c\u5c31\u662f\u6697\u5730 \u91cc\u770b\u4e86\u79d8\u5f55\uff0c\u4e5f\u4e0d\u81f4\u4e8e\u72af\u7f6a\uff0c\u4f60\u8fd9\u72c2\u5f92\uff0c\u5206\u660e\u4e00\u6d3e\u80e1\u8a00\u4e71\u8bed\u2026\u2026\u201d \u96f7\u5927\u53d4\u9053\uff1a\u201c\u5047\u5982\u4f60\u4e09\u4f4d\u5e08\u4f2f\u5077\u770b\u79d8\u5f55\uff0c\u88ab\u638c\u95e8\u4eba\u53d1\u73b0\u559d\u6b62\u65f6\uff0c\u4e0d\u4f46\u4e0d\u542c\u638c\u95e8\u4eba\u4e4b\u547d\uff0c \u53cd\u800c\u7a81\u7136\u51fa\u624b\uff0c\u628a\u638c\u95e8\u4eba\u5236\u4f4f\uff0c\u8981\u7f6e\u638c\u95e8\u4eba\u4e8e\u6b7b\u5730\u65f6\uff0c\u4ee5\u4f60\u4eec\u5c11\u6797\u5bfa\u89c4\u6765\u8bf4\uff0c\u8be5\u5f53\u72af\u7684\u4f55 \u7f6a\uff1f\u201d \u201c\u667a\u6d77\u7985\u5e08\u201d\u53c8\u662f\u4e00\u695e\uff01 \u638c\u95e8\u4eba\u804c\u6743\u9ad8\u4e8e\u4e00\u5207\uff0c\u4e0d\u8981\u8bf4\u662f\u540c\u8f88\u7684\u5e08\u5144\uff0c\u5c31\u662f\u9ad8\u4e00\u8f88\u7684\u5e08\u4f2f\uff0c\u5e08\u53d4\uff0c\u5982\u679c\u4fb5\u5bb3\u5230\u638c \u95e8\u4eba\uff0c\u4e5f\u662f\u6b7b\u5f8b\u4e00\u6761\uff01 \u201c\u667a\u6d77\u7985\u5e08\u201d\u695e\u4e86\u4e00\u4f1a\uff0c\u7a81\u7136\u5ff5\u73e0\u4e00\u8f6c\uff0c\u9053\uff1a\u201c\u4e8b\u5173\u672c\u5bfa\u9690\u79d8\uff0c\u8fde\u672c\u638c\u95e8\u90fd\u4e0d\u77e5\u9053\uff0c\u4f60 \u8fd9\u72c2\u5f92\u600e\u4e48\u4f1a\u77e5\u9053\u5f97\u8fd9\u4e48\u6e05\u695a\uff1f\u4e0d\u662f\u80e1\u8a00\u4e71\u8bed\uff0c\u4f01\u56fe\u6df7\u6dc6\u89c6\u542c\u662f\u4ec0\u4e48\uff1f\u201d \u96f7\u5927\u53d4\u4e0d\u7d27\u4e0d\u6162\u5730\u7b54\u9053\uff1a\u201c\u5f53\u65f6\u8001\u592b\u5728\u573a\uff0c\u600e\u4f1a\u4e0d\u6e05\u695a\uff1f\u201d \u201c\u667a\u6d77\u7985\u5e08\u201d\u9707\u58f0\u9053\uff1a\u4ec0\u4e48\uff0c\u4f60\u5728\u573a\uff1f\u201d \u96f7\u5927\u53d4\u6c89\u7740\u5730\u70b9\u4e86\u70b9\u5934\uff0c\u9053\uff1a\u201c\u4e00\u70b9\u4e0d\u9519\uff01\u82e5\u4e0d\u662f\u8001\u592b\u5728\u573a\uff0c\u2018\u6b63\u5fc3\u65b9\u4e08\u2019\u65e9\u5df2\u6ca1\u547d \u4e86\uff01\u5c31\u662f\u56e0\u4e3a\u8001\u592b\u6551\u4e86\u4ed6\u4e00\u547d\uff0c\u4ed6\u624d\u80fd\u4ee5\u5927\u4e49\u65a5\u8d23\u4f60\u7684\u4e09\u4f4d\u5e08\u4f2f\uff0c\u4f7f\u4f60\u4e09\u4f4d\u5e08\u4f2f\u7f9e\u6127\u96be\u5f53\uff0c \u81ea\u788e\u5929\u7075\u800c\u6b7b\uff01\u201d \u6b64\u65f6\uff0c\u201c\u667a\u6d77\u7985\u5e08\u201d\u5df2\u9707\u60ca\u5f97\u900f\u4e0d\u8fc7\u6c14\u6765\uff01\u2026\u2026 \u7fa4\u96c4\u66f4\u662f\u773c\u775b\u7741\u5f97\u6eda\u5706\uff0c\u9e26\u96c0\u65e0\u58f0\u5730\u60ca\u89c6\u7740\u96f7\u5927\u53d9\uff01 \u96f7\u5927\u53d4\u63a5\u4e0b\u53bb\u9053\uff1a\u201c\u4e5f\u5c31\u662f\u4e3a\u6b64\uff0c\u4f60\u5e08\u7236\u2018\u6b63\u5fc3\u65b9\u4e08\u2019\u4e5f\u770b\u5230\u4e86\u7ffb\u5f00\u9761\u9875\u7684\u79d8\u5f55\uff0c\u89c9\u5f97 \u6b64\u4e66\u592a\u8273\uff0c\u4e0d\u5b9c\u5728\u5bfa\u4e2d\u4fdd\u5b58\uff0c\u53c8\u611f\u5ff5\u8001\u592b\u63f4\u624b\u4e4b\u6069\uff0c\u4f7f\u5c11\u6797\u5bfa\u6570\u767e\u5e74\u547d\u8109\u4e0d\u81f4\u4e2d\u65ad\uff0c\u624d\u628a\u6b64 \u4e66\u8f6c\u8d60\u8001\u592b\uff0c\u5e76\u4e14\u8d60\u9001\u4e86\u5c11\u6797\u5bfa\u4e09\u9897\u4ef7\u503c\u8fde\u57ce\u7684\u79d8\u5236\u4e39\u836f\u2018\u9f99\u864e\u7eed\u547d\u4e39\u2019\uff01\u201d \u542c\u81f3\u6b64\u5904\uff0c\u2018\u667a\u6d77\u7985\u5e08\u2019\u5df2\u5982\u51b7\u6c34\u6d47\u5934\uff0c\u5168\u8eab\u51c9\u4e86\uff01 \u56e0\u4e3a\u5c11\u6797\u5bfa\u5df2\u843d\u4e8e\u201c\u5357\u6d77\u95e8\u201d\u638c\u63e1\u4e4b\u4e2d\uff0c\u4ed6\u7387\u9886\u95e8\u4e0b\u6f5c\u51fa\u5c11\u6797\u5bfa\uff0c\u4e0e\u95e8\u4e0b\u5546\u8bae\u7684\u7ed3\u679c\uff0c \u60f3\u4ece\u201c\u5357\u6d77\u95e8\u201d\u624b\u4e2d\u593a\u56de\u6570\u767e\u5e74\u7684\u57fa\u4e1a\uff0c\u4ee5\u5c11\u6797\u5bfa\u79d8\u4f20\u7684\u201c\u4e03\u5341\u4e8c\u79cd\u7edd\u827a\u201d\u5df2\u4e0d\u6577\u5e94\u7528\uff0c\u975e \u8981\u628a\u8fd9\u4e2d\u6b66\u529f\u5947\u5965\u3001\u522b\u8d70\u8e4a\u5f84\u7684\u79d8\u5f55\u593a\u56de\u6765\u4e0d\u53ef\uff01 \u4ed6\u63a2\u542c\u51fa\u8fd9\u4e2d\u79d8\u5f55\uff0c\u843d\u4e8e\u5c11\u5e74\u5251\u5bb9\u5c55\u81ea\u4e4b\u624b\uff0c\u53c8\u98ce\u95fb\u5c55\u767d\u6b66\u529f\u9ad8\u5f3a\uff0c\u72ec\u6218\u201c\u4e09\u715e\u201d\u3001 \u201c\u56db\u51f6\u201d\u51e0\u81f3\u5929\u4e0b\u65e0\u654c\uff0c\u662f\u4ee5\u8d39\u4e86\u4e0d\u5c11\u5507\u820c\uff0c\u5e76\u7b54\u5e94\u4e86\u4e0d\u5c11\u597d\u5904\uff0c\u624d\u7ea6\u96c6\u4e86\u516b\u5927\u95e8\u6d3e\u638c\u95e8 \u4eba\uff0c\u5171\u6765\u7d22\u56de\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\uff01 \u5982\u4eca\u542c\u96f7\u5927\u53d4\u4e00\u8bf4\uff0c\u65e0\u5f02\u662f\u628a\u5c11\u6797\u5bfa\u5bf9\u8fd9\u672c\u79d8\u5f55\u7684\u4e3b\u6743\u7ed9\u5426\u51b3\u4e86\uff01 \u542c\u96f7\u5927\u53d9\u8bf4\u5b8c\u8fd9\u6bb5\u8fde\u4ed6\u672c\u4eba\u4e5f\u4e0d\u77e5\u9053\u7684\u5c11\u6797\u5bfa\u524d\u4ee3\u79d8\u95fb\uff0c\u201c\u667a\u6d77\u7985\u5e08\u201d\u6012\u706b\u4e2d\u70e7\uff0c\u60ca\u9b42 \u752b\u5b9a\uff0c\u6df1\u89c9\u5c11\u6797\u5bfa\u6570\u767e\u5e74\u57fa\u4e1a\uff0c\u6210\u8d25\u5728\u6b64\u4e00\u4e3e\u3002\u4e0d\u7531\u6012\u53f1\u9053\uff1a\u201c\u5c14\u662f\u4f55\u4eba\uff1f\u201d \u96f7\u5927\u53d4\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6c5f\u6e56\u65e0\u540d\u5c0f\u5352\uff0c\u2018\u5929\u4f5b\u638c\u2019\u96f7\u9707\u8fdc\uff01\u201d \u201c\u667a\u6d77\u7985\u5e08\u201d\u9762\u8272\u7acb\u53d8\uff0c\u7531\u72de\u6076\u4e4b\u6001\u500f\u7136\u53d8\u6210\u60f6\u6050\u4e0e\u5d07\u656c\uff0c\u8fde\u5fd9\u5f2f\u4e0b\u8170\u53bb\uff0c\u6df1\u65bd\u4e86\u4e00 \u793c\uff0c\u606d\u8c28\u8a00\u9053\uff1a\u201c\u963f\u5f25\u9640\u4f5b\uff01\u5584\u88c1\uff01\u5584\u54c9\uff01\u539f\u6765\u662f\u96f7\u6069\u516c\uff01\u8001\u50e7\u4e0d\u77e5\uff0c\u591a\u6709\u5192\u72af\u2026\u2026\u201d \u96f7\u5927\u53d4\u89c1\u62a5\u51fa\u81ea\u5df2\u540d\u5b57\uff0c\u5f53\u4eca\u5c11\u6797\u638c\u95e8\u6001\u5ea6\u7acb\u523b\u53d8\u5f97\u606d\u656c\uff0c\u53cd\u6709\u70b9\u4e0d\u597d\u610f\u601d\uff0c\u8fde\u5fd9\u4f38\u624b \u6400\u6276\uff0c\u4f46\u5c1a\u672a\u7b49\u4ed6\u8bf4\u51fa\uff1a\u201c\u5927\u5e08\u514d\u793c\uff0c\u4e0d\u77e5\u8005\u4e0d\u7f6a\u2026\u2026\u201d\u4e4b\u8bdd\u65f6\uff0c\u201c\u667a\u6d77\u7985\u5e08\u201d\u7a81\u5730\u8111\u7b4b\u4e00 \u8f6c\uff0c\u89c9\u5f97\u4e0d\u5bf9\uff0c\u5982\u679c\u81ea\u5df1\u5f53\u4f17\u5411\u96f7\u5927\u53d4\u4e00\u8ba4\u7f6a\uff0c\u90a3\u4e48\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\u5c11\u6797\u6d3e\u5c31\u7b97\u662f\u6ca1\u6709 \u8d44\u683c\u95ee\u9f0e\u4e86\uff01 \u8fd9\u6837\u4e00\u6765\uff0c\u5c11\u6797\u5bfa\u5404\u95e8\u6b66\u529f\uff0c\u5747\u4e0d\u80fd\u80dc\u8fc7\u201c\u5357\u6d77\u95e8\u201d\uff0c\u5c11\u6797\u5bfa\u6570\u767e\u4e2d\u57fa\u4e1a\uff0c\u5c82\u4e0d\u5c31\u4ece\u81ea \u5df2\u624b\u4e0a\u65ad\u9001\u4e86\u5417\uff1f \u5343\u53e4\u7f6a\u4eba\uff0c\u4ed6\u81ea\u627f\u62c5\u5f53\u4e0d\u8d77\uff01 \u60f3\u5230\u6b64\u5904\uff0c\u201c\u667a\u6d77\u7985\u5e08\u201d\u8138\u8272\u500f\u5730\u4e00\u53d8\uff0c\u633a\u8eab\u53f1\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u5c31\u51ed\u4f60\u4e00\u9762\u4e4b\u8bcd\uff0c\u672c\u638c\u95e8 \u600e\u80fd\u8f7b\u4fe1\uff0c\u4f60\u53ef\u6709\u4e2a\u51ed\u8bc1\u62ff\u6765\u7ed9\u8001\u8872\u8fc7\u76ee\u5417\uff1f\u201d \u96f7\u5927\u53d4\u4e5f\u4e00\u695e\uff0c\u60f3\u4e0d\u5230\u5c11\u6797\u638c\u95e8\u6001\u5ea6\u53d8\u5f97\u8fd9\u6837\u5feb\uff01 \u4e8e\u662f\u6115\u9053\uff1a\u201c\u4ec0\u4e48\u51ed\u8bc1\uff1f\u201d \u201c\u667a\u6d77\u5927\u5e08\u201d\u9053\uff1a\u201c\u4f60\u8bf4\u672c\u5bfa\u4e0a\u4ee3\u638c\u95e8\uff0c\u9001\u4e86\u4f60\u4e09\u7c92\u5c11\u6797\u72ec\u95e8\u79d8\u836f\u2018\u9f99\u864e\u7eed\u547d\u4e39\u2019\uff0c\u53c8 \u8bf4\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\u4e5f\u8f6c\u8d60\u4e86\u4f60\uff0c\u8fd9\u4e24\u6837\u4e1c\u897f\uff0c\u4f60\u80fd\u62ff\u51fa\u6765\uff0c\u7ed9\u8001\u8872\u8fc7\u8fc7\u76ee\uff0c\u4f60\u6240\u8bf4\u4e00 \u5207\uff0c\u8001\u8872\u4fbf\u90fd\u60c5\u4ee5\u4e3a\u771f\uff01\u5982\u82e5\u62ff\u4e0d\u51fa\u6765\uff0c\u54fc\uff01\u54fc\uff01\u4fbf\u662f\u9601\u4e0b\u4fe1\u53e3\u5f00\u6cb3\uff0c\u620f\u5f04\u8001\u8872\uff0c\u90a3\u65f6\uff0c\u522b \u8bf4\u8001\u8872\u5bf9\u4e0d\u8d77\u4f60\uff01\u201d \u96f7\u5927\u53d4\u5fc3\u4e2d\u8f6c\u5ff5\uff1a\u201c\u9f99\u864e\u7eed\u547d\u4e39\u201d\u4e09\u7c92\uff0c\u4e8c\u7c92\u5728\u65e9\u5e74\u6551\u4e86\u4eba\uff0c\u4e00\u7c92\u5728\u201c\u8c79\u7a81\u5c71\u5e84\u201d\u9001\u7ed9 \u5c55\u767d\u65f6\uff0c\u5c55\u767d\u4e0d\u63a5\u53d7\uff0c\u5df2\u88ab\u81ea\u5df1\u6454\u7834\u5730\u4e0b\uff0c\u5982\u4eca\u662f\u4e00\u7c92\u4e0d\u5b58\u4e86\uff1b\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\u5df2\u88ab\u5c55 \u767d\u6495\u6bc1\uff0c\u4e24\u6869\u4fe1\u7269\u4e00\u4ef6\u4e5f\u6ca1\u6709\uff01 \u4f46\u96f7\u5927\u53d4\u5fc3\u91cc\u660e\u767d\uff0c\u5c31\u662f\u8fd9\u4e24\u6869\u4fe1\u7269\u90fd\u8fd8\u5b58\u5728\uff0c\u201c\u667a\u6d77\u63b8\u5e08\u201d\u4e5f\u4e0d\u4f1a\u5c31\u6b64\u7f62\u624b\u3002\u96f7\u5927\u53d4 \u4e3a\u4e86\u5bfb\u8bbf\u76df\u5144\u5c55\u4e91\u5929\uff0c\u591c\u5165\u5c11\u6797\u5bfa\uff0c\u9002\u9022\u51d1\u5de7\uff0c\u633d\u6551\u4e86\u5c11\u6797\u5bfa\u4e00\u573a\u6d69\u52ab\uff0c\u8fd9\u4ef6\u4e8b\u9664\u4e86\u5f53\u65f6\u5c11 \u6797\u638c\u95e8\u201c\u6b63\u5fc3\u65b9\u4e08\u201d\u4ee5\u5916\uff0c\u6ca1\u6709\u7b2c\u4e8c\u4e2a\u4eba\u77e5\u9053\uff0c\u4ee5\u773c\u524d\u60c5\u52bf\u6765\u770b\uff0c\u5c31\u7b97\u4ed6\u62ff\u51fa\u8fd9\u4e24\u6869\u4fe1\u7269 \u6765\uff0c\u201c\u667a\u6d77\u7985\u5e08\u201d\u4e5f\u4e0d\u4f1a\u8ba4\u8d26\uff01 \u96f7\u5927\u53d4\u60f3\u5230\u8fd9\u91cc\uff0c\u4ef0\u5929\u4e00\u9635\u8c6a\u7b11\uff0c\u9053\uff1a\u201c\u96f7\u9707\u8fdc\u867d\u662f\u4e00\u540d\u6c5f\u6e56\u5c0f\u7387\uff0c\u4f46\u5e73\u751f\u6240\u4e3a\uff0c\u5927\u51e1 \u6c5f\u6e56\u4e0a\u7684\u670b\u53cb\u4e5f\u6709\u4e2a\u8033\u95fb\uff0c\u662f\u5426\u60ef\u6253\u8bf3\u8bed\u4e4b\u4eba\uff1f\u60f3\u5fc5\u8001\u548c\u5c1a\u4e5f\u8be5\u77e5\u9053\uff01\u8bdd\uff0c\u5230\u6b64\u4e3a\u6b62\u3002\u4fe1\u4e0d \u4fe1\uff0c\u5168\u5728\u8001\u548c\u5c1a\u81ea\u5df1\u4e86\uff01\u201d \u201c\u667a\u6d77\u7985\u5e08\u201d\u5c1a\u672a\u7b54\u8a00\uff0c\u5d06\u5cd2\u638c\u95e8\u201c\u6392\u9aa8\u4ed9\u201d\u8e0f\u524d\u4e24\u6b65\uff0c\u6a2a\u7709\u51b7\u76ee\u5730\u9053\uff1a\u201c\u7985\u5e08\uff0c\u6211\u4eec \u4e0d\u5fc5\u8ddf\u4ed6\u8bf4\u90a3\u591a\uff0c\u95ee\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\u5728\u4f55\u4eba\u8eab\u4e0a\uff1f\u201d \u96f7\u5927\u53d4\u51b7\u7b11\u4e00\u58f0\u9053\uff1a\u201c\u51ed\u4f60\u4e5f\u914d\uff01\u201d \u201c\u6392\u9aa8\u4ed9\u201d\u6027\u597d\u6e14\u8272\uff0c\u591c\u4e0d\u865a\u5ea6\uff0c\u79bb\u5f00\u5973\u5b50\u4fbf\u591c\u4e0d\u6210\u7720\uff0c\u65e6\u65e6\u800c\u4f10\uff0c\u4e0d\u4f46\u5f04\u5f97\u4e00\u526f\u8eab\u5b50 \u9aa8\u7626\u5982\u67f4\uff0c\u8d62\u6765\u4e86\u201c\u6392\u9aa8\u4ed9\u201d\u7684\u96c5\u53f7\uff0c\u800c\u4e14\u58f0\u540d\u72fc\u85c9\uff0c\u7d20\u4e3a\u6c5f\u6e56\u6b63\u6d3e\u4eba\u58eb\u6240\u4e0d\u9f7f\u3002 \u96f7\u5927\u53d4\u6b63\u4e49\u51db\u7136\uff0c\u6700\u662f\u77a7\u4e0d\u8d77\u8fd9\u4e9b\u9e21\u9e23\u72d7\u76d7\u4e4b\u8f88\uff0c\u662f\u4ee5\u89c1\u201c\u6392\u9aa8\u4ed9\u201d\u4e00\u51fa\u9762\uff0c\u4fbf\u6c14\u6124\u586b \u80f8\uff0c\u8bf4\u8bdd\u4e5f\u5c31\u6beb\u4e0d\u5ba2\u6c14\u4e86\u3002 \u4f46\uff0c\u96f7\u5927\u53d4\u8fd9\u4e00\u53e5\u8bdd\u5374\u6fc0\u6012\u4e86\u201c\u6392\u9aa8\u4ed9\u201d\uff01 \u53ea\u89c1\u201c\u6392\u9aa8\u4ed9\u201d\u60e8\u767d\u5be1\u7eff\u7684\u8138\u4e0a\uff0c\u4e00\u9635\u626d\u66f2\uff0c\u4e24\u53ea\u6b7b\u9c7c\u773c\u4e00\u77aa\uff0c\u6012\u53f1\u9053\uff1a\u201c\u96f7\u75af\u5b50\uff0c\u5c11 \u72c2\uff01\u4f60\u5148\u63a5\u672c\u638c\u95e8\u4e00\u638c\u8bd5\u8bd5\uff01\u201d \u6012\u53f1\u58f0\u4e2d\uff0c\u628a\u8fd0\u81f3\u98a0\u5cf0\u7684\u638c\u529f\uff0c\u5e73\u80f8\u5411\u96f7\u5927\u53d4\u63a8\u6765\uff01 \u638c\u51fa\uff0c\u5c31\u5730\u5377\u8d77\u4e00\u9635\u72c2\u98d8\uff0c\u5411\u96f7\u5927\u53d4\u547c\u5578\u800c\u81f3\uff01 \u201c\u6392\u9aa8\u4ed9\u201d\u638c\u529b\u4e0d\u5f31\uff01 \u96f7\u5927\u53d4\u51b7\u6652\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u50cf\u9601\u4e0b\u8fd9\u7b49\u811a\u8272\uff0c\u5341\u638c\uff0c\u767e\u638c\uff0c\u8001\u592b\u4e5f\u63a5\u7740\u4f60\u7684\uff01\u201d \u8bf4\u8bdd\u58f0\u4e2d\uff0c\u53f3\u638c\u4e00\u6325\uff0c\u4e00\u822c\u52b2\u6d41\uff0c\u76f4\u5411\u6765\u52bf\u8fce\u53bb\uff01 \u4e24\u80a1\u529b\u9053\u5728\u534a\u7a7a\u76f8\u63a5\uff0c\u201c\u8f70\u201d\u7684\u4e00\u58f0\uff0c\u5c18\u6c99\u56db\u6e85\uff0c\u201c\u6392\u9aa8\u4ed9\u201d\u5f53\u573a\u88ab\u9707\u9000\u4e09\u6b65\uff01\u96f7\u5927\u53d4 \u5374\u7eb9\u4e1d\u672b\u52a8\u3002 \u201c\u6392\u9aa8\u4ed9\u201d\u4eba\u524d\u53d7\u632b\uff0c\u66b4\u6012\u5982\u96f7\uff0c\u864e\u543c\u4e86\u4e00\u58f0\uff0c\u731b\u6251\u9762\u4e0a\uff0c\u65bd\u51fa\u201c\u5d06\u5cd2\u6d3e\u201d\u770b\u5bb6\u672c\u9886\uff0c \u6362\u5ffd\u4e4b\u95f4\uff0c\u63a5\u8fde\u653b\u51fa\u4e09\u638c\u4e24\u811a\uff01 \u53ea\u89c1\u638c\u5f71\u5982\u5c71\uff0c\u5939\u7740\u6761\u6761\u817f\u5f71\uff0c\u5982\u72c2\u98ce\u9aa4\u96e8\u4e00\u822c\u5411\u96f7\u5927\u53d4\u5468\u8eab\u7f69\u5382\uff01 \u201c\u4f60\u8fd9\u662f\u627e\u6b7b\uff01\u201d \u96f7\u5927\u53d4\u4e5f\u88ab\u6fc0\u8d77\u771f\u706b\uff0c\u6012\u5428\u4e00\u58f0\uff0c\u65bd\u51fa\u201c\u5929\u4f5b\u7edd\u5b66\u4e61\u571f\u9669\u72c2\u98ce\u9aa4\u96e8\u7684\u62db\u5f0f\uff0c\u4e0d\u8eb2\u4e0d\u95ea\uff0c \u53cd\u800c\u638c\u6307\u9f50\u51fa\uff0c\u89c1\u62db\u6253\u62db\uff0c\u89c1\u5f0f\u6253\u5f0f\uff0c\u786c\u78b0\u786c\uff0c\u548c\u201c\u6392\u9aa8\u4ed9\u201d\u5bf9\u6298\u4e86\u4e09\u62db\uff01 \u4f17\u4eba\u53ea\u89c1\u4e24\u6761\u4eba\u5f71\u5f80\u4e0a\u4e00\u51d1\uff0c\u638c\u98ce\u817f\u5f71\u7eed\u7eb7\u4ea4\u9519\uff0c\u201c\u556a\uff01\u556a\uff01\u201d \u4f20\u51fa\u51e0\u58f0\u6c14\u7206\uff0c\u7f20\u6597\u5728\u4e00\u8d77\u7684\u4e24\u6761\u4eba\u5f71\uff0c\u53c8\u4f7c\u7136\u5206\u5f00\uff01 \u96f7\u5927\u53d4\u50b2\u7136\u800c\u7acb\uff0c\u4e71\u53d1\u5728\u591c\u98ce\u98d8\u62c2\u4e0b\uff0c\u98d8\u98de\u5982\u84ec\uff01 \u201c\u6392\u9aa8\u4ed9\u201d\u8138\u8272\u60e8\u767d\uff0c\u7626\u957f\u6761\u7684\u8eab\u8eaf\u6447\u4e86\u4e24\u6447\uff0c\u53cc\u76ee\u6124\u6012\u5f97\u51e0\u4e4e\u55b7\u51fa\u706b\u6765\uff0c\u77aa\u89c6\u7740\u96f7\u5927 \u53d4\uff0c\u50cf\u662f\u4e9f\u6b32\u518d\u6b21\u6251\u4e0a\u524d\u6765\u62fc\u547d\u7684\u6837\u5f85\u4e8c\u4eba\u4e4d\u5408\u5373\u5206\uff0c\u4f17\u4eba\u8fd8\u672a\u770b\u51fa\u4e2a\u6240\u4ee5\u7136\u6765\uff0c\u53ef\u662f\uff0c\u6fc0 \u6597\u7684\u4e8c\u4eba\u80dc\u8d1f\u5df1\u5206\uff0c\u5f3a\u5f31\u5df2\u5224\uff01 \u5c31\u5728\u4f17\u4eba\u60ca\u89c6\u7740\u53cc\u65b9\u3001\u8fd8\u4e0d\u77e5\u4e8c\u4eba\u8c01\u5360\u4e86\u4e0a\u98ce\u4e4b\u9645\uff0c\u53ea\u89c1\u201c\u6392\u9aa8\u4ed9\u201d\u8eab\u5f62\u53c8\u6447\u4e86\u4e24\u64ad\uff0c \u7a81\u7136\u5f20\u53e3\u55b7\u51fa\u4e00\u53e3\u9c9c\u8840\uff0c\u4eba\uff0c\u4e5f\u8ddf\u7740\u5012\u4e0b\u53bb\u2026\u2026 \u5c11\u6797\u638c\u95e8\u201c\u667a\u6d77\u7985\u5e08\u201d\u6025\u63a0\u800c\u81f3\uff0c\u9002\u65f6\u4f38\u624b\u6276\u4f4f\u6447\u6447\u6b32\u5012\u7684\u201c\u6392\u9aa8\u4ed9\u201d\uff0c\u4e00\u8fb9\u4ece\u6000\u4e2d\u638f \u51fa\u4e00\u7c92\u4e38\u836f\u653e\u8fdb\u201c\u6392\u9aa8\u4ed9\u201d\u53e3\u4e2d\uff0c\u4e00\u8fb9\u56de\u5934\u6012\u89c6\u7740\u96f7\u5927\u53d4\u559d\u9053\uff1a\u201c\u96f7\u9707\u8fdc\uff01\u597d\u72e0\u7684\u624b\u6bb5\uff01\u738b \u638c\u95e8\u4e43\u662f\u8001\u8872\u8bf7\u6765\u7684\uff0c\u5982\u4eca\u4f24\u5728\u4f60\u7684\u638c\u4e0b\uff0c\u8fd9\u6bb5\u6881\u5b50\uff0c\u4e5d\u5927\u95e8\u6d3e\u662f\u8ddf\u4f60\u7ed3\u5b9a\u4e86\u201c\u672b\u7b49\u201c\u667a\u6d77 \u7985\u5e08\u201d\u7684\u8bdd\u8bf4\u5b8c\uff0c\u201c\u6cd5\u534e\u5357\u5b97\u201d\u638c\u95e8\u4eba\u201c\u5f39\u7b5d\u5ba2\u201d\u5f20\u5f3a\uff0c\u53ca\u201c\u6cd5\u534e\u5317\u5b97\u201d\u638c\u95e8\u4eba\u201c\u94c1\u7435\u7436 \u624b\u201d\u8303\u4e39\u540c\u65f6\u66b4\u6012\uff0c\u4e0d\u7ea6\u800c\u540c\uff0c\u6012\u53f1\u4e86\u4e00\u58f0\u3002\u4e00\u4e2a\u7528\u201c\u5f39\u7b5d\u6307\u201d\u4e00\u4e2a\u7528\u201c\u7435\u7436\u624b\u201d\uff0c\u5206\u5de6\u53f3 \u540c\u65f6\u5411\u96f7\u5927\u53d4\u653b\u81f3\uff01 \u96f7\u5927\u53d4\u7167\u6837\u4e0d\u95ea\u4e0d\u907f\uff0c\u786c\u63a5\u4e24\u5927\u638c\u95e8\u4e00\u62db\uff0c\u540c\u65f6\uff0c\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u5802\u5802\u4e5d\u5927\u95e8 \u6d3e\u4e4b\u5c0a\uff0c\u4e5f\u662f\u4ee5\u591a\u4e3a\u80dc\u4e4b\u8f88\uff01\u201d \u8fd9\u8bdd\u663e\u7136\u6709\u70b9\u4f24\u4f17\uff01 \u201c\u6cd5\u534e\u201d\u5357\u3001\u5317\u4e24\u5927\u638c\u95e8\u5fae\u5fae\u4e00\u632b\uff0c\u6b3a\u8eab\u53c8\u8fdb\uff01 \u5176\u4ed6\u4e03\u5927\u638c\u95e8\u4eba\uff0c\u4e00\u9f50\u6643\u8eab\u6b3a\u4e0a\u524d\u6765\uff0c\u4e2a\u4e2a\u8138\u9732\u6740\u89c6\uff01 \u5c11\u6797\u638c\u95e8\u628a\u201c\u6392\u9aa8\u4ed9\u201d\u5b89\u987f\u5750\u5728\u5730\u4e0b\uff0c\u4ee4\u5176\u9759\u5750\u8c03\u606f\uff0c\u4e5f\u6643\u8eab\u6251\u4e0a\u524d\u6765\uff0c\u6ee1\u9762\u6012\u5bb9\u5730 \u9053\uff1a\u201c\u4e8b\u5173\u6574\u4e2a\u4e2d\u539f\u6b66\u6797\uff0c\u5982\u679c\u4e0d\u628a\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\u732e\u51fa\u6765\uff0c\u8bf4\u4e0d\u5f97\u4e5d\u5927\u638c\u95e8\u8981\u5168\u529b\u4ee5 \u8d74\uff01\u201d \u96f7\u5927\u53d4\u6643\u8eab\u51fa\u638c\uff0c\u62b5\u4f4f\u201c\u6cd5\u534e\u201d\u5357\u3001\u5317\u5b97\u4e24\u5927\u638c\u95e8\u7684\u653b\u52bf\uff0c\u540c\u65f6\u4e00\u9635\u72c2\u7b11\uff0c\u9053\uff1a\u201c\u96f7\u67d0 \u4eba\u5f62\u5c06\u5c31\u6728\u4e4b\u5e74\uff0c\u80fd\u9886\u6559\u4e5d\u5927\u638c\u95e8\u7684\u7edd\u827a\uff0c\u5b9e\u4e43\u4e09\u751f\u6709\u5e78\uff0c\u4f60\u4eec\u4e5d\u5927\u95e8\u6d3e\u4e00\u9f50\u4e0a\u5427\uff01\u201d \u8bf4\u8bdd\u4e4b\u95f4\uff0c\u500f\u5ffd\u5288\u51fa\u4e03\u638c\uff0c\u5206\u88ad\u53e6\u5916\u4e03\u5927\u638c\u95e8\uff01 \u96f7\u5927\u53d4\u662f\u6012\u6781\u4e86\uff0c\u5df2\u5fd8\u8bb0\u773c\u524d\u5c40\u9762\u7684\u4e25\u91cd\u6027\uff0c\u8fde\u73af\u5288\u51fa\u4e03\u638c\uff0c\u638c\u638c\u52b2\u98ce\u72c2\u5578\uff0c\u5206\u5411\u4e03\u5927 \u638c\u95e8\u5377\u81f3\uff01 \u4e03\u5927\u638c\u95e8\u9f50\u58f0\u6012\u53f1\uff0c\u7eb7\u7eb7\u51fa\u638c\u5e94\u654c\uff01 \u53ea\u89c1\u4eba\u5f71\u6643\u52a8\uff0c\u638c\u6307\u6f2b\u5929\uff0c\u9f50\u5411\u96f7\u5927\u53d4\u653b\u5230\uff01 \u8fd9\u65f6\uff0c\u96f7\u5927\u53d4\u5df2\u662f\u529b\u654c\u516b\u5927\u638c\u95e8\uff0c\u516b\u5927\u62ff\u95e8\u529f\u529b\u5747\u975e\u7b49\u95f2\u4e4b\u8f88\uff0c\u5982\u4eca\u4e00\u9f50\u51fa\u624b\uff0c\u5a01\u529b\u66f4 \u662f\u5927\u5f97\u51fa\u5947\uff01 \u96f7\u5927\u53d4\u4e71\u53d1\u98de\u84ec\uff0c\u602a\u76ee\u5389\u7741\uff0c\u8eab\u5f62\u7535\u8f6c\u5c4b\u98de\uff0c\u957f\u888d\u5927\u8896\u88ab\u51cc\u5389\u638c\u98ce\u5439\u5f97\u730e\u730e\u4f5c\u54cd\uff0c\u4f46\u4ed6 \u5728\u516b\u5927\u638c\u95e8\u56f4\u653b\u4e4b\u4e0b\uff0c\u7adf\u7136\u662f\u6709\u653b\u6709\u5b88\uff0c\u6602\u7136\u4e0d\u60e7\uff01 \u4f46\u4efb\u4f55\u4eba\u90fd\u53ef\u770b\u5f97\u51fa\u6765\uff0c\u96f7\u5927\u53d4\u4e00\u4eba\u529b\u654c\u516b\u5927\u638c\u95e8\uff0c\u5b8c\u5168\u662f\u51ed\u4e00\u53e3\u771f\u6c14\uff0c\u5047\u5982\u65f6\u95f4\u957f \u4e86\uff0c\u51b3\u4e0d\u662f\u516b\u5927\u638c\u95e8\u7684\u653e\u624b\uff01 \u7a81\u89c1\u4e00\u6761\u4eba\u5f71\uff0c\u75be\u5982\u95ea\u7535\uff0c\u76f4\u5411\u6fc0\u6597\u7684\u4eba\u5f71\u4e2d\u5c04\u53bb\uff01 \u8fd9\u65f6\uff0c\u516b\u5927\u638c\u95e8\u8054\u624b\u5408\u6218\u96f7\u5927\u53d4\u4e00\u4eba\uff0c\u6bcf\u4e2a\u4eba\u7684\u529f\u529b\u90fd\u5728\u4e00\u4e2a\u7532\u5b50\u4ee5\u4e0a\uff0c\u800c\u4e14\u90fd\u662f\u65bd\u7684 \u81f4\u547d\u6740\u62db\uff0c\u6bcf\u4e00\u638c\u6bcf\u4e00\u6307\uff0c\u5747\u53ef\u5f00\u7891\u88c2\u77f3\uff01 \u6fc0\u6597\u573a\u4e2d\uff0c\u638c\u98ce\u5c71\u6d8c\uff0c\u52b2\u98ce\u72c2\u5578\uff0c\u4e5d\u4e2a\u9876\u5c16\u9ad8\u624b\u7684\u638c\u98ce\u52b2\u6d41\u6fc0\u8361\u5728\u4e00\u8d77\uff0c\u72b9\u5982\u4e00\u4e2a\u5de8\u5f62 \u7684\u9f99\u5377\u98ce\uff01 \u5371\u9669\u4e4b\u72b6\uff0c\u53ef\u8bf4\u662f\u95f4\u4e0d\u5bb9\u53d1\uff01 \u662f\u8c01\u6709\u8fd9\u4e48\u5927\u7684\u80c6\u91cf\uff0c\u7adf\u6562\u5408\u8eab\u6251\u5411\u6fc0\u6597\u573a\u4e2d\uff1f \u5c31\u5728\u4f17\u4eba\u7eb7\u7eb7\u5ac1\u8be7\u58f0\u4e2d\uff0c\u7a81\u89c1\u90a3\u6761\u4eba\u5f71\u4ee5\u8089\u773c\u96be\u89c1\u7684\u901f\u5ea6\uff0c\u75be\u5c04\u81f3\u6fc0\u6597\u573a\u4e2d\uff0c\u53ea\u4e00\u4e2a\u6025 \u65cb\uff01 \u4e5f\u6ca1\u6709\u770b\u51fa\u4ed6\u662f\u7528\u4ec0\u4e48\u62db\u5f0f\uff0c\u53ea\u89c1\u56f4\u653b\u96f7\u5927\u53d4\u7684\u516b\u5927\u638c\u95e8\uff0c\u2014\u3002\u4e2a\u4e2a\u5982\u89e6\u86c7\u874e\uff0c\u7eb7\u7eb7\u60ca \u545b\uff0c\u5206\u5411\u56db\u65b9\u7a9c\u51fa\u5708\u5916\uff01 \u573a\u4e2d\u592e\u4e0e\u96f7\u5927\u53d4\u5e76\u80a9\u800c\u7acb\u7684\u6b63\u662f\u5c11\u5e74\u5c55\u767d\uff01 \u4f17\u4eba\u6697\u6697\u5403\u60ca\uff01 \u4e5f\u6709\u4e0d\u5c11\u4eba\u5728\u5fc3\u4e2d\u6697\u6697\u559d\u5f69\uff01 \u96f7\u5927\u53d4\u72ec\u6218\u516b\u5927\u638c\u95e8\uff0c\u5df2\u5c5e\u60ca\u4eba\uff0c\u4f46\u96f7\u5927\u53d4\u8fd8\u662f\u6210\u540d\u6c5f\u6e56\u591a\u5e74\u7684\u6b66\u529f\u9ad8\u624b\uff01 \u800c\u4e14\uff0c\u529b\u6218\u516b\u5927\u638c\u95e8\uff0c\u4e5f\u4e0d\u8fc7\u662f\u52c9\u5f3a\u652f\u6301\u4e0d\u8d25\uff01 \u5982\u4eca\uff0c\u5c55\u767d\u4e00\u4e2a\u5e74\u4ec5\u4e8c\u5341\u4f59\u5c81\u7684\u5c11\u4e2d\uff0c\u7adf\u80fd\u4e00\u62db\u628a\u516b\u5927\u638c\u95e8\u4e00\u9f50\u903c\u51fa\u5708\u5916\uff0c\u5b9e\u662f\u51fa\u4e4e\u4f17 \u4eba\u60f3\u8c61\u4e4b\u5916\uff01 \u73b0\u5b58\u7b97\u662f\u5f00\u4e86\u773c\u5566\uff0c\u5c11\u5e74\u5c55\u767d\uff0c\u5f53\u771f\u662f\u4e00\u638c\u9707\u516b\u65b9\uff01 \u5c55\u767d\u4e00\u62db\u201c\u4f5b\u5149\u666e\u7167\u201d\uff0c\u903c\u9000\u516b\u5927\u638c\u95e8\uff0c\u5728\u96f7\u5927\u53d4\u8eab\u524d\u4e00\u7ad9\uff0c\u4fa0\u80c6\u82f1\u98ce\uff0c\u4fca\u7f8e\u7684\u4eea\u8868\uff0c \u771f\u5982\u7389\u6811\u742a\u82b1\uff0c\u795e\u91c7\u7167\u4eba\uff01 \u4ed6\u7528\u4fca\u76ee\u4e00\u626b\u6ee1\u8138\u60ca\u5bb9\u7684\u516b\u5927\u638c\u95e8\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u6570\u767e\u5e74\u6765\u4e5d\u5927\u95e8\u6d3e\u4e00\u76f4\u638c\u4e2d\u539f\u6b66\u6797\u4e4b \u725b\u8033\uff0c\u8bbe\u60f3\u5230\u8f6e\u5230\u51e0\u4f4d\u638c\u95e8\u5934\u4e0a\uff0c\u7adf\u8fd9\u822c\u662f\u975e\u4e0d\u5206\uff0c\u5584\u6076\u4e0d\u660e\uff01\u201d \u8fd9\u8bdd\u4fe8\u7136\u6709\u6559\u8bad\u53e3\u543b\uff01 \u201c\u667a\u6d77\u7985\u5e08\u201d\u6709\u9274\u4e8e\u5c55\u767d\u9002\u624d\u7684\u4e00\u62db\uff0c\u5a01\u529b\u5927\u5f97\u60ca\u4eba\uff0c\u95fb\u8a00\u867d\u7136\u66b4\u6012\uff0c\u4f46\u5e76\u672b\u6562\u8f7b\u6613\u51fa \u624b\uff0c\u53ea\u6ee1\u8138\u6012\u5bb9\u5730\u53f1\u9053\uff1a\u201c\u6b64\u8bdd\u600e\u8bb2\uff1f\u201d \u5c55\u767d\u9053\uff1a\u201c\u65b9\u624d\u5728\u4e0b\u4e8b\u5148\u5df2\u7ecf\u8bf4\u660e\uff0c\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\u662f\u7531\u5728\u4e0b\u6bc1\u53bb\uff0c\u4e0e\u96f7\u5927\u53d4\u6beb\u4e0d \u76f8\u5e72\uff0c\u56e0\u4f55\u4e5d\u5927\u638c\u95e8\u4e0d\u95ee\u9752\u7ea2\u7682\u767d\uff0c\u7adf\u7136\u5192\u6627\u5411\u96f7\u5927\u53d4\u51fa\u624b\uff1f\u2026\u2026\u201d \u5c55\u767d\u6b64\u8a00\u4e00\u51fa\uff0c\u5c1a\u672a\u7b49\u201c\u667a\u6d77\u7985\u5e08\u201d\u7b54\u8bdd\uff0c\u7a81\u89c1\u4e24\u6761\u4eba\u5f71\u75be\u5c04\u800c\u81f3\uff01 \u4e24\u6761\u4eba\u5f71\u811a\u672a\u843d\u5730\uff0c\u534a\u7a7a\u4e2d\u5373\u53d1\u8a00\u9053\uff1a\u201c\u59d3\u5c55\u7684\u5c0f\u5b50\uff01\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\u65e2\u662f\u5728\u4f60\u624b \u4e2d\uff0c\u90a3\u5c31\u8d76\u7d27\u62ff\u51fa\u6765\uff0c\u4ea4\u7ed9\u6211\u8001\u4eba\u5bb6\uff01\u201d \u8bdd\u843d\u4eba\u81f3\uff0c\u6b63\u662f\u201c\u6c5f\u5357\u4e8c\u5947\u201d\uff01 \u5927\u5947\u201c\u8d64\u53d1\u7075\u7334\u201d\u5e38\u53bb\u6076\uff0c\u4e8c\u5947\u201c\u9b3c\u8c37\u9690\u53df\u201d\u6587\u6b63\u5947\uff0c\u8863\u7740\u523a\u773c\uff0c\u800c\u4e14\u5728\u6c5f\u4e56\u5f20\uff0c\u662f\u4ee4 \u4eba\u95fb\u540d\u4e27\u80c6\u7684\u4e24\u9b54\u5934\uff01 \u5982\u4eca\u8054\u631f\u51fa\u73b0\uff0c\u573a\u4e2d\u4e0d\u5c11\u4eba\u6697\u6697\u5fc3\u60ca\uff01 \u5c11\u6797\u638c\u95e8\u201c\u667a\u6d77\u7985\u5e08\u201d\u5fc3\u4e2d\u4e5f\u5fae\u5fae\u4e00\u61d4\uff0c\u9053\uff1a\u201c\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\u4e43\u672c\u5bfa\u4e4b\u7269\uff0c\u4e0d\u5e0c \u671b\u4e8c\u4f4d\u518d\u63d2\u4e0a\u4e00\u624b\uff01\u201d \u201c\u8d64\u53d1\u7075\u7334\u201d\u5e38\u53bb\u6076\u563f\u7136\u4e00\u9635\u51b7\u7b11\uff0c\u9053\uff1a\u201c\u5c11\u6797\u5bfa\u5f92\u5177\u865a\u540d\uff0c\u81ea\u5df1\u7684\u5bfa\u9662\u90fd\u4fdd\u6301\u4e0d\u4f4f\uff0c \u6709\u4ec0\u4e48\u8d44\u683c\u5927\u8a00\u4e0d\u60ed\uff0c\u6562\u8bf4\u5929\u4e0b\u7b2c\u4e00\u5947\u4e66\u662f\u4f60\u4eec\u5bfa\u4e2d\u6240\u6709\uff1f\u201d \u201c\u9b3c\u8c37\u9690\u53df\u201d\u6587\u6b63\u5947\u4e5f\u51b7\u68ee\u68ee\u5730\u9053\uff1a\u201c\u6211\u4eec\u8001\u54e5\u4fe9\u52bf\u5728\u5fc5\u5f97\uff0c\u8c01\u8981\u4e0d\u670d\uff0c\u8fd9\u5c31\u662f\u699c \u6837\uff01\u201d \u8bdd\u672a\u8bf4\u5b8c\uff0c\u53ea\u89c1\u4ed6\u5355\u638c\u4e00\u6325\uff0c\u4e00\u822c\u51db\u9aa8\u5bd2\u98d8\uff0c\u968f\u638c\u53d1\u51fa\uff0c\u72c2\u5578\u7740\u5377\u5411\u6b66\u5f53\u638c\u95e8\u201c\u7384\u6e05 \u5b50\u201d\uff01 \u638c\u98ce\u672b\u81f3\uff0c\u5148\u611f\u4e00\u80a1\u5bd2\u6c14\u6c81\u4eba\uff0c\u6b66\u5f53\u638c\u95e8\u201c\u7384\u6e05\u5b50\u201d\u7a81\u7136\u53d7\u88ad\uff0c\u53c8\u89c1\u638c\u52bf\u6765\u5f97\u5389\u5bb3\uff0c\u60ca \u547c\u4e00\u58f0\uff0c\u6643\u8eab\u8eb2\u8fc7\uff01 \u201c\u7384\u6e05\u5b50\u201d\u6a2a\u98d8\u4e09\u5c3a\uff0c\u51db\u51bd\u5bd2\u98d9\u64e6\u8eab\u800c\u8fc7\uff0c\u4ed6\u4e0d\u7531\u673a\u4f36\u4f36\u6253\u4e86\u4e00\u4e2a\u5bd2\u6218\uff01 \u4f46\u5728\u4ed6\u8eab\u540e\u4e8c\u4e08\u5f00\u5916\u7684\u4e24\u540d\u201c\u7a77\u5bb6\u5e2e\u201d\u5f1f\u5b50\uff0c\u5374\u672a\u80fd\u8eb2\u5f00\uff0c\u638c\u98ce\u8fc7\u5904\uff0c\u53ea\u89c1\u4e8c\u4eba\u53d1\u51fa\u4e8c \u58f0\u60e8\u55e5\uff0c\u8138\u8272\u53d8\u6210\u7eb8\u4e00\u822c\u60e8\u767d\uff0c\u5468\u8eab\u4e00\u9635\u8896\u6410\uff0c\u7adf\u7136\u840e\u987f\u5012\u5730\u6b7b\u53bb\uff01 \u8fd9\u662f\u201c\u9b3c\u8c37\u9690\u53df\u201d\u4e0d\u4e45\u4e4b\u524d\u624d\u4fee\u7ec3\u6210\u7684\u201c\u9634\u98ce\u638c\u201d\uff0c\u9694\u7740\u4e8c\u4e08\u4f59\u8fdc\uff0c\u7ade\u80fd\u4e00\u638c\u8fde\u6bd9\u4e8c \u4eba\uff01 \u638c\u529f\u4e4b\u6b79\u6bd2\u5389\u5bb3\uff0c\u5b9e\u5728\u60ca\u4eba\uff01 \u8c01\u77e5\u6b66\u5f53\u638c\u95e8\u201c\u7384\u6e05\u5b50\u201d\u5e76\u4e0d\u662f\u6d6a\u5f97\u865a\u540d\u4e4b\u8f88\uff0c\u8eab\u4efb\u4e00\u6d3e\u638c\u95e8\uff0c\u786e\u5b9e\u6709\u4e9b\u4e0d\u5f31\u7684\u827a\u4e1a\uff0c \u5c31\u5728\u51b7\u4e0d\u9632\u7684\u6697\u88ad\u4e4b\u4e0b\uff0c\u4ecd\u80fd\u53ca\u65f6\u65bd\u5c55\u72ec\u95e8\u8eab\u6cd5\uff0c\u5728\u5371\u673a\u4e00\u77ac\u95f4\u8eb2\u8fc7\u4e86\uff0c\u9b3c\u8c37\u9690\u53df\u201d\u2014\u8bb0\u6740 \u62db\uff01 \u201c\u7a77\u5bb6\u5e2e\u201d\u4e24\u540d\u5e2e\u4f17\uff0c\u68da\u91cc\u7cca\u6d82\u505a\u4e86\u66ff\u6b7b\u9b3c\uff01 \u201c\u9b3c\u8c37\u9690\u53df\uff01\u4e00\u638c\u51fb\u6bd9\u201c\u7a77\u5bb6\u5e2e\u201d\u4e24\u540d\u5f1f\u5b50\uff0c\u201c\u7a77\u5bb6\u5e2e\u201d\u7684\u4e09\u5927\u957f\u8001\u201c\u98ce\u5c18\u4e09\u4e10\u201d\u51b2\u51b2 \u5927\u6012\uff0c\u4e0d\u7ea6\u800c\u540c\uff0c\u66b4\u53f1\u4e86\u4e00\u58f0\uff0c\u540c\u65f6\u817e\u8eab\u6251\u4e0a\uff0c\u534a\u7a7a\u4e2d\u5404\u81ea\u5288\u51fa\u4e00\u638c\uff01 \u4e09\u80a1\u638c\u98ce\uff0c\u5408\u800c\u4e3a\u4e00\uff0c\u72c2\u5377\u201c\u9b3c\u8c37\u9690\u53df\u201d\uff01 \u672a\u7b49\u201c\u9b3c\u8c37\u9690\u53df\u201d\u51fa\u624b\uff0c\u201c\u8d64\u53d1\u7075\u7334\u201d\u5927\u8896\u4e00\u6325\uff0c\u53f1\u9053\uff1a\u201c\u7a77\u8981\u996d\u7684\uff01\u4f60\u4eec\u7ed9\u6211\u9000 \u4e0b\uff01\u201d \u201c\u8d64\u53d1\u7075\u7334\u201d\u8fd9\u968f\u624b\u4e00\u6325\uff0c\u770b\u6765\u8f7b\u98d8\u98d8\u7684\u672b\u65bd\u51fa\u5b9e\u529b\uff0c\u4e8b\u5b9e\u4e0a\u5374\u662f\u4ed6\u9b3c\u795e\u7686\u60ca\u7684\u201c\u9ed1\u715e \u624b\u201d\u4e00\u5927\u6740\u62db\u201c\u51a4\u9b3c\u6321\u8def\u201d\uff0c\u7ade\u628a\u201c\u98ce\u5c18\u4e09\u4e10\u201d\u5408\u529b\u653b\u51fa\u7684\u4e09\u80a1\u529b\u9053\uff0c\u5316\u4e8e\u65e0\u5f62\uff01 \u201c\u98ce\u5c18\u4e09\u4e10\u201d\u4e3a\u201c\u7a77\u5bb6\u5e2e\u201d\u76d6\u4e16\u9ad8\u624b\uff0c\u5c31\u662f\u6c5f\u6e56\u4e0a\u58f0\u540d\u4e5f\u751a\u663e\u8457\uff0c\u4e09\u4eba\u6b66\u529f\u4e0d\u5f31\uff0c\u5982\u4eca \u4e09\u4eba\u5408\u624b\u653b\u51fa\u4e00\u638c\uff0c\u88ab\u201c\u8d64\u53d1\u7075\u7334\u201d\u6325\u8896\u4e00\u6321\uff0c\u4e09\u4eba\u53ea\u89c9\u50cf\u662f\u9047\u5230\u4e00\u9762\u65e0\u5f62\u7684\u94a2\u5899\uff0c\u4e0d\u8981\u8bf4 \u638c\u529b\u53d1\u4e0d\u51fa\u53bb\uff0c\u8fde\u817e\u7a7a\u7684\u8eab\u5f62\uff0c\u4e5f\u88ab\u963b\u6321\u843d\u4e0b\u5730\u6765\uff01 \u201c\u98ce\u5c18\u4e09\u4e10\u201d\u4e0d\u7531\u4e00\u6115\uff01 \u5fc3\u8bf4\uff1a\u8fd9\u770b\u6765\u5f62\u72b6\u5947\u7279\u7684\u7334\u50cf\u8001\u4eba\uff0c\u6b66\u529f\u4e4b\u9ad8\uff0c\u5b9e\u5728\u524d\u6240\u672a\u89c1\uff01\u2026\u2026 \u4e5f\u5c31\u5728\u201c\u98ce\u5c18\u4e09\u4e10\u201d\u4e00\u6014\u7684\u5f53\u513f\uff0c\u201c\u5357\u6d77\u95e8\u201d\u7684\u9ad8\u624b\uff0c\u6392\u6210\u4e00\u7ebf\uff0c\u7f13\u6b65\u8d70\u4e86\u4e0a\u6765\uff01 \u5f53\u4e2d\u662f\u6f47\u6d12\u65e0\u6bd4\u7684\u767d\u8863\u94f6\u6247\u201c\u9f99\u795e\u592a\u5b50\u201d\u53ca\u8c8c\u5982\u5929\u4ed9\u7684\u7fbd\u8863\u4e3d\u4eba\u201c\u5357\u6d77\u9f99\u5973\u201d\uff01 \u5de6\u4fa7\u201c\u6d77\u5916\u4e09\u715e\u201d\u53f3\u7ffc\u201c\u9ed1\u9053\u56db\u51f6\u201d\uff0c\u4e03\u4e2a\u201c\u5357\u6d77\u95e8\u201d\u9876\u5c16\u9ad8\u624b\uff0c\u5728\u201c\u9f99\u795e\u592a\u5b50\u201d\u5144\u59b9 \u4e24\u65c1\u96c1\u7fc5\u6392\u5f00\uff01 \u8fd9\u4e5d\u4e2a\u4eba\u5411\u524d\u4e00\u8fc8\u6b65\uff0c\u9690\u7136\u6709\u4e00\u822c\u9707\u6151\u4eba\u5fc3\u7684\u5a01\u52bf\uff01 \u201c\u9f99\u795e\u592a\u5b50\u201d\u7528\u624b\u4e2d\u94f6\u6247\u4e00\u6307\u201c\u6c5f\u5357\u4e8c\u5947\u201d\uff0c\u65c1\u82e5\u65e0\u4eba\u5730\u9053\uff1a \u201c\u4f60\u4eec\u4e24\u4e2a\u95ea\u5f00\uff01\u201d \u201c\u6c5f\u5357\u4e8c\u5947\u201d\u4e43\u6c5f\u6e56\u9ed1\u9053\u4e0a\u6709\u540d\u7684\u4e24\u5927\u602a\u7269\uff0c\u7d20\u5e38\u72c2\u50b2\u900f\u9876\uff0c\u4ece\u672a\u88ab\u4eba\u5982\u6b64\u547c\u559d\u8fc7\uff0c\u95fb \u8a00\u5ffd\u9053\uff1a\u201c\u4f60\u5c0f\u5b50\u662f\u5bf9\u8c01\u8bf4\u8bdd\uff1f\u201d \u201c\u6d77\u5916\u4e09\u715e\u201d\u4e4b\u4e2d\u7684\u767d\u53d1\u5a46\u5a46\u4e0a\u524d\u2014\u6b65\uff0c\u9053\uff1a\u201c\u5c31\u662f\u51b2\u4f60\u4eec\u4e24\u4e2a\u8001\u7334\u5d3d\u5b50\u8bf4\u7684\uff0c\u600e\u4e48\uff0c \u8fd8\u4e0d\u670d\u6c14\u5417\uff1f\u201d \u201c\u8d64\u53d1\u7075\u7334\u201d\u518d\u4e0d\u7b54\u8a00\uff0c\u201c\u9ed1\u715e\u624b\u201d\u4e00\u62db\u201c\u9ed1\u624b\u593a\u9b42\u201d\uff0c\u731b\u5411\u767d\u53d1\u5a46\u5a46\u9762\u95e8\u6293\u53bb\uff01 \u201c\u4f60\u627e\u6b7b\uff01\u201d\u767d\u53d1\u5a46\u5a46\u6012\u53f1\u4e00\u58f0\uff0c\u5355\u6307\u5982\u9525\uff0c\u75be\u70b9\u201c\u8d64\u53d1\u7075\u7334\u201d\u638c\u5fc3\u7a74\uff01 \u201c\u9648\uff01\u201d\u4e00\u58f0\u5fae\u54cd\uff0c\u201c\u8d64\u53d1\u7075\u7334\u201d\u5982\u88ab\u86c7\u54ac\uff0c\u60e8\u55e5\u4e00\u58f0\uff0c\u98de\u8eab\u8dc3\u51fa\u4e8c\u6587\u5f00\u5916\uff01 \u4e00\u770b\u638c\u5fc3\uff0c\u5df2\u7ea2\u80bf\u5982\u6311\uff0c\u539f\u6765\u662f\u88ab\u767d\u53d1\u5a46\u5a46\u201c\u641c\u9b42\u6307\u201d\u6240\u4f24\uff01 \u201c\u8d64\u53d1\u7075\u7334\u201d\u53c8\u60ca\u53c8\u6012\uff0c\u8d76\u7d27\u4ece\u676f\u91cc\u638f\u51fa\u7597\u4f24\u5723\u836f\uff0c\u653e\u5728\u5634\u91cc\u56bc\u4e86\u56bc\uff0c\u542b\u7740\u53e3\u6c34\u5410\u5728\u638c \u5fc3\u4e0a\uff0c\u4e00\u8fb9\u8d76\u5fd9\u8fd0\u529f\u8c03\u606f\u2026\u2026 \u63a5\u7740\u201c\u562d\u201d\u7684\u4e00\u58f0\uff0c\u4e00\u6761\u4eba\u5f71\u8e09\u8dc4\u9000\u51fa\uff0c\u8dcc\u8dcc\u649e\u649e\uff0c\u76f4\u5411\u201c\u8d64\u53d1\u7075\u7334\u201d\u6000\u91cc\u649e\u6765\uff01 \u201c\u8d64\u53d1\u7075\u7334\u201d\u4e3e\u8d77\u672a\u8d1f\u4f24\u7684\u5de6\u638c\uff0c\u5c31\u8981\u5411\u6765\u4eba\u5288\u4e0b\uff01 \u7a81\u7136\uff0c\u4ed6\u770b\u6e05\u4e86\u90a3\u662f\u4ed6\u7684\u5e08\u5f1f\u201c\u9b3c\u8c37\u9690\u53df\u201d\uff0c\u6025\u6025\u6536\u4f4f\u5288\u51fa\u7684\u6500\u52bf\uff0c\u53d8\u6253\u4e3a\u6276\uff0c\u4e00\u624b\u6276 \u4f4f\u201c\u9b3c\u8c37\u9690\u53df\u201d\uff01 \u201c\u9b3c\u8c37\u9690\u53df\u201d\u9762\u8272\u60e8\u767d\uff0c\u88ab\u201c\u8d64\u53d1\u7075\u7334\u201d\u6276\u4f4f\uff0c\u867d\u672b\u683d\u5012\uff0c\u5374\u5f20\u53e3\u55b7\u51fa\u4e00\u53e3\u9c9c\u8840\uff01 \u201c\u8d64\u53d1\u7075\u7334\u201d\u5927\u60ca\uff0c\u8c01\u80fd\u6709\u5982\u6b64\u9ad8\u5f3a\u6b66\u529f\uff0c\u4e3e\u624b\u6295\u8db3\u628a\u81ea\u5df1\u5e08\u5144\u5f1f\u4e8c\u4eba\u5148\u540e\u6253\u4f24\uff1f \u4f46\u4ed6\u987e\u4e0d\u5f97\u591a\u52a0\u601d\u7d22\uff0c\u8d76\u7d27\u53c8\u638f\u51fa\u4e00\u9896\u7597\u4f24\u5723\u836f\uff0c\u7ed9\u201c\u9b3c\u8c37\u9690\u53df\u201d\u670d\u4e86\u4e0b\u53bb\u2026\u3002\u2019 \u7b2c\u516d\u5341\u4e8c\u7ae0\u3000\u7a7a\u524d\u4e00\u6218>> \u53e4\u9f99\u300a\u5251\u5ba2\u884c\u300b \u7b2c\u516d\u5341\u4e8c\u7ae0\u3000\u7a7a\u524d\u4e00\u6218 \u539f\u6765\u767d\u53d1\u5a46\u5a46\u7528\u201c\u641c\u9b42\u6307\u201d\u4f24\u4e86\u201c\u8d64\u53d1\u7075\u7334\u201d\uff0c\u672a\u7b49\u201c\u9b3c\u8c37\u9690\u53df\u201d\u51fa\u624b\uff0c\u201c\u6d77\u5916\u4e09\u715e\u201d \u4e4b\u4e2d\u7684\u201c\u4f5b\u5370\u6cd5\u5e08\u201d\u4e00\u638c\u6572\u5728\u201c\u9b3c\u8c37\u9690\u53df\u201d\u7684\u540e\u5fc3\u4e0a\uff0c\u628a\u201c\u9b3c\u8c37\u9690\u53df\u201d\u6253\u51fa\u4e8c\u4e08\u5f00\u5916\uff01 \u201c\u5357\u6d77\u95e8\u201d\u7684\u4eba\uff0c\u8fde\u4f24\u4e24\u5927\u9ad8\u624b\uff0c\u4ecd\u7136\u6392\u6210\u4e00\u7ebf\uff0c\u82e5\u65e0\u5176\u4e8b\u5730\u7f13\u7f13\u903c\u8fd1\u5c55\u767d\uff01 \u201c\u5357\u6d77\u9f99\u5973\u201d\u7ea4\u6307\u6f2b\u7a7a\u4e00\u62a1\uff0c\u6307\u7740\u4e5d\u5927\u638c\u95e8\uff0c\u9053\uff1a\u4f60\u4eec\u4e5f\u95ea\u5f00\uff01\u201d \u88ab\u201c\u5357\u6d77\u95e8\u201d\u6c14\u52bf\u6240\u6151\uff0c\u4e5d\u5927\u638c\u95e8\u5664\u82e5\u5bd2\u8749\uff0c\u5404\u81ea\u9000\u540e\u6570\u6b65\uff01 \u201c\u9f99\u795e\u592a\u5b50\u201d\u94f6\u6247\u4e00\u6296\uff0c\u6307\u7740\u5c55\u767d\u6162\u6761\u65af\u7406\u5730\u9053\uff1a\u201c\u8bf7\u9601\u4e0b\u628a\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\u4ea4\u51fa \u6765\u5427\uff01\u201d \u8bed\u8c03\u867d\u662f\u4e0d\u75be\u4e0d\u5f90\uff0c\u4f46\u53e3\u6c14\u5374\u662f\u5f3a\u786c\u65e0\u6bd4\uff0c\u6beb\u65e0\u8f6c\u56ed\u4f59\u5730\uff01 \u5c55\u767d\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u5c0a\u9a7e\u6709\u4ec0\u4e48\u8d44\u683c\uff0c\u53ef\u4ee5\u5927\u8a00\u4e0d\u60ed\uff0c\u53eb\u5728\u4e0b\u628a\u300a\u9501\u7763\u9500\u9b42\u5929\u4f5b\u5377\u300b\u4ea4 \u7ed9\u4f60\uff1f\u201d \u201c\u5357\u6d77\u9f99\u5973\u201d\u7f8e\u4e2d\u542b\u715e\u7684\u660e\u7738\uff0c\u72e0\u72e0\u5730\u76ef\u4e86\u5c55\u767d\u4e00\u773c\uff01 \u201c\u5357\u6d77\u9f99\u5973\u2019\u2019\u4e00\u53cc\u7f8e\u76ee\u4ecd\u7136\u671b\u7740\u5c55\u81ea\uff0c\u83ba\u58f0\u6ca5\u6ca5\u5730\u9053\uff1a\u201c\u96be\u9053\u4f60\u4e0d\u77e5\u9053\u90a3\u672c\u300a\u9501\u9aa8\u9500 \u9b42\u5929\u4f5b\u5377\u300b\uff0c\u5e94\u8be5\u662f\u5c5e\u4e8e\u2018\u5357\u6d77\u95e8\u2019\u4e4b\u7269\u5417\uff1f\u201d \u6b64\u8a00\u4e00\u51fa\uff0c\u5c55\u767d\u5fc3\u4e2d\u4e00\u9707\uff0c\u7fa4\u96c4\u4e5f\u65e0\u4e0d\u4e00\u6115\uff01 \u4f17\u4eba\u5fc3\u4e2d\u8be7\u5f02\uff1a\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\u53c8\u600e\u4f1a\u7275\u626f\u5230\u201c\u5357\u6d77\u95e8\u201d\u5934\u4e0a\u53bb\uff1f \u5c55\u767d\u4e0d\u7981\u53c8\u62ac\u8d77\u5934\u6765\uff0c\u82e6\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u8bf4\u7b11\u8bdd\u4e86\uff01\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\u53c8\u600e\u4f1a \u662f\u4f60\u4eec\u2018\u5357\u6d77\u95e8\u2019\u7684\u2026\u2026\u201d \u4f46\u4ed6\u8bdd\u5230\u4e00\u534a\uff0c\u89c1\u201c\u5357\u6d77\u9f99\u5973\u201d\u60c5\u7130\u7194\u7194\u7684\u773c\u5149\u4e00\u76f4\u76ef\u7740\u4ed6\uff0c\u4f3c\u4e4e\u773c\u5149\u4e2d\u6709\u4e24\u652f\u5229\u7bad\uff0c \u76f4\u8981\u5c04\u8fdb\u4ed6\u7684\u5fc3\u7a9d\uff0c\u5413\u5f97\u4ed6\u4e00\u54c6\u55e6\uff0c\u8d76\u7d27\u53c8\u4f4e\u4e0b\u5934\u53bb\u3002 \u89c1\u5c55\u767d\u53d7\u7a98\u7684\u6837\u5b50\uff0c\u201c\u5357\u6d77\u9f99\u5973\u201d\u76c8\u76c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u770b\u8fc7\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\u4e0a\u7684\u7ed8 \u50cf\u5427\uff01\u201d \u5c55\u767d\u8138\u4e00\u7ea2\uff0c\u9053\uff1a\u201c\u770b\u8fc7\uff01\u201d \u201c\u4f60\u53ef\u77e5\u9053\u90a3\u7ed8\u50cf\u662f\u8c01T\u201d\u201d\u5929\u4ed9\u9b54\u5973\u2019\uff01\u201d \u201c\u5929\u4ed9\u9b54\u5973\u2019\u662f\u8c01\uff1f\u201d \u5c55\u767d\u4e00\u6115\uff1a\u2018\u5929\u4ed9\u9b54\u5973\u2019\u5c31\u662f\u2018\u5929\u4ed9\u9b54\u5973\u2019\uff0c\u600e\u4e48\u8fd8\u4f1a\u95ee\u2018\u5929\u4ed9\u9b54\u5973\u2019\u662f\u8c01\uff1f\u201d \u201c\u5357\u6d77\u9f99\u5973\u201d\u89c1\u5c55\u767d\u6115\u7136\u7684\u50bb\u50cf\uff0c\u4e0d\u7531\u839e\u5c14\u4e00\u7b11\u9053\uff1a\u201c\u5b9e\u8bdd\u544a\u8bc9\u4f60\u5427\uff01\u2018\u5929\u4ed9\u9b54\u5973\u2019\u4e43 \u662f\u6211\u2018\u5357\u6d77\u95e8\u2019\u7684\u5e08\u7956\uff01\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\u4e0a\u7ed8\u7684\u90fd\u662f\u6211\u2018\u5357\u6d77\u95e8\u2019\u5e08\u7956\u7684\u9057\u50cf\uff0c\u5c82\u5bb9\u843d \u4eba\u522b\u4eba\u4e4b\u624b\uff1f\u201d \u5c55\u767d\u55e4\u4e4b\u4ee5\u9f3b\uff0c\u7b11\u9053\uff1a\u201c\u5728\u4e0b\u4ece\u672a\u542c\u8bf4\u9053\uff0c\u2018\u5929\u4ed9\u9b54\u5973\u2019\u4ec0\u4e48\u65f6\u5019\u6210\u4e86\u8d35\u6d3e\u7684\u5e08\u7956\uff01\u201d \u201c\u5357\u6d77\u95e8\u201d\u4f17\u4eba\u540c\u65f6\u8138\u8272\u4e00\u53d8\uff0c\u8138\u9732\u51f6\u5149\uff0c\u51e0\u6b32\u540c\u65f6\u6124\u7136\u51fa\u624b\uff0e\uff0c\uff0e\uff0e\xb7\u201c\u5357\u6d77\u9f99\u5973\u201d \u505a\u4e86\u4e2a\u624b\u52bf\uff0c\u963b\u6b62\u4f4f\u95e8\u4e0b\u4f17\u4eba\uff0c\u795e\u60c5\u9887\u4e3a\u4e0d\u60a6\u5730\u5bf9\u5c55\u767d\u9053\uff1a\u201c\u6211\u5e08\u7236\u662f\u2018\u9b3c\u9762\u5a07\u5a03\u2019\u2019\u2018\u9b3c \u9762\u5a07\u5a03\u2019\u4e43\u2018\u5929\u4ed9\u9b54\u5973\u2019\u4e4b\u5f92\uff0c\u2018\u5929\u4ed9\u9b54\u5973\u2019\u4e0d\u662f\u6211\u5e08\u7956\u662f\u8c01\uff1f\u96be\u9053\u5e08\u7956\u8fd8\u6709\u5192\u8ba4\u7684\u5417\uff1f\u5982 \u679c\u628a\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\u4e56\u4e56\u5730\u4ea4\u51fa\u6765\u4fbf\u7f62\uff0c\u5426\u5219\uff0c\u54fc\uff01\u5c06\u53eb\u4f60\u6b7b\u65e0\u846c\u8eab\u4e4b\u5730\uff01\u201d \u201c\u5357\u6d77\u9f99\u5973\u201d\u6b64\u8a00\u4e00\u51fa\uff0c\u5929\u4e0b\u7fa4\u96c4\u83ab\u4e0d\u5403\u60ca\uff0c\u81f3\u6b64\uff0c\u5927\u5bb6\u624d\u660e\u767d\uff0c\u201c\u5357\u6d77\u95e8\u201d\u539f\u662f\u6e90\u51fa \u201c\u5929\u4ed9\u9b54\u5973\u201d\u95e8\u4e0b\uff01 \u4f46\u5c55\u767d\u5374\u662f\u5929\u751f\u670d\u8f6f\u4e0d\u670d\u786c\u7684\u4e2a\u6027\uff0c\u201c\u5357\u6d77\u9f99\u5973\u201d\u8fd9\u6781\u5177\u5a01\u80c1\u6027\u7684\u51e0\u53e5\u8bdd\u4e00\u51fa\u53e3\uff0c\u5c55\u767d \u5251\u7709\u4e00\u8038\uff0c\u51b7\u7b11\u4e00\u58f0\u9053\uff1a\u201c\u6211\u518d\u8bf4\u4e00\u904d\uff0c\u4f17\u4f4d\u6765\u665a\u4e86\u4e00\u6b65\uff01\u201d \u201c\u9f99\u795e\u592a\u5b50\u201d\u63a5\u53e3\u9053\uff1a\u201c\u516d\u6708\u91cc\u8d34\u95e8\u795e\uff0c\u6211\u4eec\u2018\u5357\u6d77\u95e8\u2019\u505a\u4e8b\u5411\u6765\u4e0d\u4f1a\u665a\u7684\uff01\u201d \u5c55\u767d\u9053\uff1a\u201c\u6570\u65e5\u4e4b\u524d\uff0c\u5728\u4e0b\u5df2\u5f53\u7740\u7fa4\u96c4\u4e4b\u9762\uff0c\u628a\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\u6495\u6bc1\uff0c\u5c31\u662f\u5c0a\u9a7e\u518d \u80fd\u8a00\u5584\u9053\uff0c\u4e5f\u770b\u4e0d\u89c1\u6b64\u5929\u4e0b\u7b2c\u4e00\u5947\u4e66\u4e86\uff01\u201d \u201c\u9f99\u795e\u592a\u5b50\u201d\u94f6\u6247\u8f7b\u6447\uff0c\u6f2b\u4e0d\u7ecf\u5fc3\u5730\u9053\uff1a\u201c\u9601\u4e0b\u8bf4\u6b64\u8bdd\uff0c\u8bf7\u95ee\u4e00\u95ee\u5728\u573a\u4e4b\u4eba\u6709\u8c01\u4f1a\u76f8 \u4fe1\uff1f\u201d \u5c55\u767d\u62a1\u76ee\u671b\u4e86\u671b\u56db\u5468\u864e\u89c6\u803d\u803d\u7684\u7fa4\u96c4\uff0c\u4e8b\u5b9e\u4e0a\uff0c\u4f17\u4eba\u8138\u4e0a\u7684\u795e\u8272\uff0c\u4f3c\u4e4e\u65e0\u4e00\u4eba\u76f8\u4fe1\u81ea\u5df1 \u4e4b\u8a00\u3002\u4e0d\u7531\u6697\u53f9\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4e0d\u76f8\u4fe1\u4e5f\u662f\u6789\u7136\uff0c\u5728\u4e0b\u5b9e\u5728\u662f\u628a\u6b64\u4e66\u6495\u6bc1\u4e86\uff01\u201d \u201c\u5357\u6d77\u9f99\u5973\u201d\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u770b\u5c55\u5c0f\u4fa0\u8fd8\u662f\u628a\u5b83\u4ea4\u51fa\u6765\u7684\u597d\uff01\u201d \u5c55\u767d\u4e00\u518d\u8868\u660e\uff0c\u65e0\u5948\u522b\u4eba\u4e0d\u4fe1\uff0c\u4ed6\u5df2\u88ab\u903c\u53d1\u706b\uff0c\u5f53\u4e0b\u6012\u9053\uff1a \u201c\u522b\u8bf4\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\u5df2\u7ecf\u6ca1\u6709\u4e86\uff0c\u5c31\u662f\u8fd8\u5728\uff0c\u5c55\u767d\u51fa\u4e0d\u4f1a\u628a\u5b83\u4ea4\u7ed9\u6740\u622e\u4e2d\u539f\u6b66\u6797 \u4eba\u58eb\u7684\u6d77\u5916\u95e8\u6d3e\u624b\u91cc\uff01\u201d \u201c\u9f99\u795e\u592a\u5b50\u201d\u9762\u8272\u4e00\u6c89\uff0c\u6740\u673a\u751a\u6d53\u5730\u9053\uff1a\u201c\u8fd9\u6837\u8bf4\u6765\uff0c\u6211\u4eec\u662f\u975e\u8981\u51fa\u624b\u4e0d\u53ef\u4e86\uff01\u201d \u8bf4\u7740\u94f6\u6247\u5411\u540e\u4e00\u62db\uff0c\u201c\u4e09\u715e\u201d\u3001\u201c\u56db\u51f6\u201d\u9f50\u4e0a\u524d\u8de8\u4e86\u4e00\u6b65\uff0c\u84c4\u52bf\u6b32\u53d1\uff01 \u5c55\u767d\u6a2a\u626b\u4e86\u201c\u5357\u6d77\u95e8\u201d\u4e03\u5927\u9ad8\u624b\u4e00\u773c\uff0c\u9053\uff1a\u201c\u83ab\u975e\u5c0a\u9a7e\u5fd8\u4e86\u8d35\u6d3e\u4e0e\u5728\u4e0b\u4e4b\u7ea6\u5417\uff1f\u201d \u201c\u9f99\u795e\u592a\u5b50\u201d\u9762\u8272\u9634\u68ee\uff0c\u563f\u563f\u51b7\u7b11\u9053\uff1a\u201c\u82e5\u662f\u6015\u4e86\uff0c\u5c31\u8d81\u65e9\u628a\u79d8\u5f55\u732e\u51fa\u6765\uff01\u201d \u5c55\u767d\u5251\u7709\u4e00\u7acb\uff0c\u9053\uff1a\u201c\u5c55\u767d\u751f\u5e73\u4e0d\u77e5\u4ec0\u4e48\u53eb\u2018\u6015\u2019\u5b57\uff0c\u53ea\u662f\u606a\u5b88\u4fe1\u4e49\uff0c\u4e0d\u613f\u518d\u4e0e\u6b66\u6797\u4eba \u58eb\u52a8\u6b66\uff01\u201d \u767d\u53d1\u5a46\u5a46\u6840\u6840\u4e00\u9635\u602a\u7b11\uff0c\u9053\uff1a\u201c\u6ca1\u5173\u7cfb\uff01\u6211\u4eec\u4e09\u4e2a\u8001\u4e0d\u6b7b\u7684\uff0c\u53ef\u4ee5\u628a\u7ea6\u8a00\u6536\u56de\uff01\u201d \u201c\u4f5b\u5370\u6cd5\u5e08\u201d\u4e5f\u9634\u4fa7\u4fa7\u5730\u7b11\u9053\uff1a\u201c\u5c0f\u54e5\u54e5\uff0c\u4e0a\u6b21\u8f93\u5f97\u4e5f\u8bb8\u4e0d\u5927\u5fc3\u670d\uff0c\u4f55\u59a8\u91cd\u65b0\u6bd4\u5212\u4e00 \u6b21\uff01\u201d\u201c\u8471\u5cad\u4e4b\u9e70\u201d\u51b7\u51b7\u5730\u53c8\u52a0\u4e0a\u4e86\u4e00\u53e5\uff1a\u8fd9\u6b21\u51b3\u4e0d\u518d\u53eb\u4f60\u6d3b\u7740\u79bb\u5f00\u6b64\u5730\uff01\u201d \u5c55\u767d\u5251\u7709\u8f69\u52a8\uff0c\u5185\u5fc3\u6fc0\u52a8\u4e0d\u5df2\uff01\u2026\u2026 \u201c\u592a\u767d\u53cc\u9038\u201d\u8ba4\u4e3a\u8fd9\u662f\u5c55\u767d\u6d17\u96ea\u524d\u803b\u7684\u826f\u673a\uff0c\u540c\u65f6\u63a0\u8eab\u4e0a\u524d\uff0c\u5411\u5c55\u767d\u9053\uff1a\u201c\u5c0f\u6069\u516c\uff0c\u63a5 \u53d7\u4ed6\u4eec\u7684\u6311\u6218\uff01\u201d \u5c55\u767d\u4e5f\u6025\u6b32\u6253\u7834\u8a93\u8a00\u7684\u7ea6\u675f\uff0c\u95fb\u8a00\u70b9\u4e86\u70b9\u5934\uff0c\u9762\u5411\u201c\u4e09\u715e\u201d\u8bf4\u9053\uff1a\u201c\u65e2\u7136\u8bf7\u4f4d\u4e00\u518d\u76f8 \u903c\uff0c\u5c55\u767d\u613f\u610f\u820d\u547d\u76f8\u966a\uff01\u4f46\u4e0d\u77e5\u662f\u4e0d\u662f\u8fd8\u662f\u7531\u4f60\u4eec\u4e09\u4f4d\u4e00\u9f50\u51fa\u624b\uff1f\u201d \u957f\u9aef\u8001\u4eba\u7ad6\u4e86\u7ad6\u5927\u62c7\u6307\uff0c\u8d5e\u9053\uff1a\u201c\u5c0f\u54e5\u513f\uff0c\u5f53\u771f\u662f\u8c6a\u6c14\u5e72\u4e91\uff01\u2026\u2026 \u201c\u9f99\u795e\u592a\u5b50\u201d\u9762\u9732\u6740\u673a\uff0c\u6c89\u58f0\u8d50\u9053\uff1a\u201c\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\u2018\u5357\u6d77\u95e8\u2019\u8a93\u5728\u5fc5\u5f97\uff0c\u672c\u592a \u5b50\u4ee5\u4ee3\u7406\u6559\u4e3b\u8eab\u4efd\u4e0b\u4ee4\uff0c\u2018\u4e09\u715e\u2019\u3001\u2018\u56db\u51f6\u2019\u4e00\u9f50\u4e0a\u9635\uff01\u201d \u201c\u9f99\u795e\u592a\u5b50\u201d\u6b64\u8a00\u4e00\u51fa\uff0c\u5173\u5fc3\u5c55\u767d\u7684\u4eba\u5fc3\u4e2d\u6697\u6697\u53d1\u751f\u51c9\u610f\uff01 \u201c\u9f99\u795e\u592a\u5b50\u201d\u72e1\u8bc8\u591a\u7aef\uff0c\u5728\u573a\u7fa4\u7ee7\u6697\u4e3a\u5c55\u767d\u634f\u4e86\u4e00\u628a\u51b7\u6c57\uff0c\u663e\u7136\u8fd9\u662f\u4e00\u4e2a\u5708\u5957\uff0c\u8981\u4f7f\u5c55 \u767d\u4e0a\u5f53\u3002\u8c01\u77e5\u5c55\u767d\u50b2\u7136\u4e00\u7b11\u9053\uff1a\u201c\u5c55\u767d\u6709\u673a\u4f1a\u80fd\u9886\u6559\u4e00\u756a\u2018\u5357\u6d77\u95e8\u2019\u4e03\u5927\u9ad8\u624b\u7684\u7edd\u62db\uff0c\u5b9e\u5728 \u8363\u5e78\u4e4b\u81f3\uff01\u201d \u5c55\u767d\u6b64\u8a00\u4e00\u51fa\uff0c\u957f\u9aef\u8001\u4eba\u7ad6\u8d77\u7684\u5927\u62c7\u6307\u6ca1\u6709\u653e\u4e0b\uff0c\u8fde\u58f0\u8d5e\u9053\uff1a\u201c\u597d\uff01\u8001\u592b\u5c0a\u4f60\u4e3a\u6b66\u6797\u7b2c \u4e00\u4eba\uff01\u201d \u201c\u9f99\u795e\u592a\u5b50\u201d\u9887\u4e3a\u4e0d\u60a6\u5730\u4e00\u7529\u888d\u8896\uff0c\u51b7\u51b7\u5730\u9053\uff1a\u201c\u4ec7\u516c\u516c\uff01\u4e0d\u8981\u957f\u654c\u4eba\u5fd7\u6c14\uff01\u4f60\u4eec\u4e03\u4f4d \u4e0a\u9635\u5427\uff01\u65e0\u8bba\u5982\u4f55\u8981\u628a\u8fd9\u72c2\u5984\u7684\u5c0f\u5b50\uff0c\u6bd9\u5728\u638c\u4e0b\uff01\u201d \u201c\u5357\u6d77\u9f99\u5973\u201d\u770b\u51fa\u54e5\u54e5\u7684\u6740\u6c14\u751a\u91cd\uff0c\u5f53\u7740\u95e8\u4e0b\uff0c\u4e0d\u597d\u610f\u601d\u51fa\u8a00\u53cd\u5bf9\uff0c\u4f46\u671b\u7740\u8de8\u6b65\u4e0a\u524d\u7684 \u95e8\u4e0b\u4e03\u5927\u9ad8\u624b\u52a0\u4e0a\u4e86\u4e00\u53e5\u9053\uff1a\u201c\u53ea\u8981\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\u5230\u624b\uff01\u201d \u4e0d\u77e5\u201c\u4e09\u715e\u201d\u3001\u201c\u56db\u51f6\u201d\u6709\u6ca1\u6709\u4f53\u4f1a\u201c\u5357\u6d77\u9f99\u5973\u201d\u8bdd\u4e2d\u4e4b\u610f\uff0c\u4f46\u89c1\u4e00\u4e2a\u4e2a\u6740\u6c14\u817e\u817e\u6b3a\u8eab \u6765\uff0c\u5404\u81ea\u5708\u81c2\u7acb\u638c\uff0c\u4eae\u5f00\u672c\u95e8\u67b6\u5f0f\uff0c\u957f\u9aef\u8001\u4eba\u9886\u5148\u53d1\u8a00\u9053\uff1a\u201c\u4eca\u65e5\u662f\u7a7a\u524d\u7edd\u540e\u7684\u4e00\u6218\uff0c\u8bf7\u5c0f \u54e5\u513f\u53d1\u62db\u5427\uff01\u201d \u201c\u6162\u7740\uff01\u201d \u5c49\u767d\u5c1a\u672a\u7b54\u8a00\uff0c\u5a49\u513f\u6025\u63a0\u4e0a\u524d\uff0c\u4ee5\u4e07\u79cd\u67d4\u60c5\u7684\u773c\u5149\u770b\u4e86\u5c55\u767d\u4e00\u773c\uff0c\u9053\uff1a\u201c\u767d\u54e5\u54e5\uff01\u5c0f\u59b9 \u613f\u52a9\u767d\u54e5\u54e5\u4e00\u81c2\u4e4b\u529b\uff01\u201d \u5c55\u767d\u611f\u52a8\u5730\u671b\u4e86\u5a49\u51e0\u4e00\u773c\uff0c\u9053\uff1a\u5a49\u59b9\uff0c\u4f60\u4e0d\u8981\u7ba1\uff01\u8fd8\u662f\u8ba9\u6211\u81ea\u5df1\u6765\u5427\uff01\u201d \u5c55\u767d\u8bf4\u7f62\uff0c\u63d0\u638c\u5f53\u80f8\uff0c\u84c4\u52bf\u4ee5\u5f85\uff01 \u6155\u5bb9\u7ea2\u4ea6\u98de\u8eab\u4e0a\u524d\uff0c\u9053\uff1a\u201c\u8fd9\u4e9b\u4eba\u4e0d\u8981\u8138\uff0c\u501a\u591a\u4e3a\u80dc\uff01\u6211\u613f\u4e0e\u5c55\u54e5\u54e5\u540c\u751f\u5171\u6b7b\uff0c\u62b5\u6297\u4ed6 \u4eec\u4e00\u9635\u3002\u201d \u6155\u5bb9\u59d0\u59b9\u633a\u8eab\u800c\u51fa\uff0c\u5c55\u767d\u5fc3\u4e2d\u5927\u53d7\u611f\u52a8\uff0c\u4f46\u4ee5\u4ed6\u7684\u4e2a\u6027\u6765\u8bf4\uff0c\u662f\u65e0\u8bba\u5982\u4f55\u4e5f\u4e0d\u4f1a\u8ba9\u6df1\u7231 \u81ea\u5df1\u7684\u4e24\u4e2a\u5c11\u5973\uff0c\u5728\u6b64\u5371\u9669\u573a\u5408\uff0c\u5e2e\u81ea\u5df1\u51fa\u9762\u7684\u3002 \u56e0\u6b64\uff0c\u5c55\u767d\u82e6\u7b11\u4e86\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u7ea2\u59b9\uff0c\u4f60\u4e5f\u4e0d\u8981\u51fa\u9762\uff0c\u5c31\u8ba9\u6211\u4e00\u4e2a\u4eba\u5e94\u4ed8\u597d\u4e86\uff01\u201d \u91d1\u5f69\u51e4\u7ad9\u7acb\u4e00\u8fb9\uff0c\u5fc3\u4e2d\u6697\u6697\u6028\u6068\u81ea\u5df1\u80c6\u91cf\u4e0d\u591f\uff0c\u89c1\u6155\u5bb9\u59ca\u59b9\u80fd\u5f53\u4f17\u8868\u793a\u51fa\u6df1\u60c5\u6d53\u610f\uff0c\u81ea \u5df1\u4e5f\u5728\u7231\u7740\u5c55\u767d\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u6562\u633a\u8eab\u800c\u51fa\u5462\uff1f\u4e5f\u8bb8\u5979\u662f\u987e\u8651\u81ea\u5df1\u7684\u95e8\u9600\u548c\u5730\u4f4d\uff1f\u8fd8\u662f\u522b\u7684\u4ec0\u4e48 \u539f\u56e0\uff1f \u5979\u5fc3\u4e2d\u72b9\u8c6b\uff0c\u89c1\u5c55\u767d\u62d2\u7edd\u4e86\u6155\u5bb9\u7ea2\u59d0\u59b9\uff0c\u5979\u6551\u66f4\u6ca1\u6709\u52c7\u6c14\u4e0a\u524d\u4e86\u2026\u2026 \u6a0a\u7d20\u9e3e\u4e5f\u4e9f\u6709\u633a\u8eab\u4e0a\u524d\u7684\u51b2\u52a8\uff0c\u4f46\u5979\u5374\u5fcd\u9690\u4e86\u4e0b\u6765\uff0c\u6697\u60f3\uff1a \u7231\u4ed6\u7684\u5973\u5b69\u5b50\u592a\u591a\u4e86\uff0c\u81ea\u5df1\u4f55\u5fc5\u518d\u63d2\u4e0a\u4e00\u811a\u2026\u2026 \u201c\u592a\u767d\u53cc\u9038\u201d\u3001\u8339\u8001\u9556\u5934\uff0c\u90fd\u662f\u6c5f\u6e56\u4e0a\u786c\u94ee\u94ee\u7684\u6c49\u5b50\uff0c\u4e3a\u670b\u53cb\u53ef\u4ee5\u4e24\u808b\u63d2\u5200\uff0c\u867d\u660e\u77e5\u6b66 \u529f\u4e0d\u662f\u201c\u4e09\u715e\u201d\u3001\u201c\u56db\u51f6\u201d\u7684\u5bf9\u624b\uff0c\u6b64\u65f6\uff0c\u5374\u4e00\u9f50\u8d70\u4e0a\u524d\uff0c\u4e0e\u5c55\u767d\u5e76\u80a9\u800c\u7ad9\uff0c\u540c\u65f6\u8bf4\u9053\uff1a\u6211 \u4eec\u90fd\u613f\u52a9\u5c55\u5c0f\u4fa0\u4e00\u81c2\u4e4b\u529b\uff01\u201d \u53ea\u6709\u96f7\u5927\u53d4\u7ad9\u7acb\u539f\u5730\u672b\u52a8\uff0c\u56e0\u4e3a\u4ed6\u77e5\u9053\u5c55\u767d\u7684\u6027\u5b50\uff0c\u65e2\u7ecf\u51b3\u5b9a\u7684\u4e8b\uff0c\u522b\u4eba\u7edd\u65e0\u6cd5\u4f7f\u4ed6\u6539 \u53d8\uff01 \u201c\u4e09\u715e\u201d\u3001\u201c\u56db\u51f6\u201d\u540c\u65f6\u563f\u563f\u51b7\u7b11\uff0c\u9053\uff1a\u201c\u591a\u591a\u76ca\u5584\uff0c\u9ec4\u6cc9\u8def\u4e0a\u7edd\u5bf9\u4e0d\u5728\u4e4e\u591a\u6dfb\u51e0\u4e2a\u65b0 \u9b3c\uff01\u201d \u5c55\u767d\u8861\u91cf\u773c\u524d\u60c5\u52bf\uff0c\u6df1\u77e5\u5df1\u65b9\u6570\u4eba\u4e0e\u201c\u4e09\u715e\u201d\u3001\u201c\u56db\u51f6\u201d\u76f8\u6bd4\uff0c\u6b66\u529f\u5b9e\u5728\u5dee\u5f97\u592a\u8fdc\uff0c\u4eba \u591a\u4e86\u4e0d\u4f46\u767d\u767d\u9001\u6b7b\uff0c\u8bf4\u4e0d\u5b9a\u8fd8\u4f1a\u788d\u624b\u788d\u811a\uff0c\u59a8\u5bb3\u81ea\u5df1\u5c3d\u91cf\u65bd\u5c55\u6240\u5b66\uff0c\u4e8e\u662f\uff0c\u50b2\u7136\u4e00\u7b11\uff0c\u9053\uff1a \u4e0d\u7528\u5728\u5634\u76ae\u5b50\u4e0a\u5356\u72c2\uff01\u5c55\u767d\u8bf4\u662f\u4e00\u4eba\uff0c\u5c31\u662f\u4e00\u4eba\uff0c\u770b\u770b\u4f60\u4eec\u2018\u4e09\u715e\u2019\u3001\u2018\u56db\u51f6\u2019\uff0c\u7a76\u7adf\u6709\u591a \u5c11\u9ad8\u6df1\u7684\u827a\u4e1a\uff01\u201d \u8bf4\u7740\u53c8\u8f6c\u5934\u5bf9\u201c\u592a\u767d\u53cc\u9038\u201d\u53ca\u8339\u8001\u9556\u5934\u9053\uff1a\u201c\u51e0\u4f4d\u524d\u8f88\u7684\u597d\u610f\uff0c\u5c55\u81ea\u5fc3\u9886\uff0c\u4f46\u5c55\u767d\u65e2\u5df2 \u51b3\u5b9a\u4e4b\u4e8b\uff0c\u7edd\u4e0d\u66f4\u6539\uff0c\u6682\u8bf7\u51e0\u4f4d\u524d\u8f88\u9000\u4e0b\uff01\u201d \u201c\u592a\u767d\u53cc\u9038\u201d\u3001\u8339\u8001\u9556\u5934\uff0c\u65e0\u53ef\u5948\u4f55\u5730\u6447\u4e86\u6447\u5934\uff0c\u9eef\u7136\u9000\u540e\u6570\u6b65\u2026\u2026 \u957f\u9aef\u8001\u4eba\u53c8\u628a\u5927\u62c7\u6307\u7ad6\u4e86\u8d77\u6765\uff0c\u8fde\u8d5e\u9053\uff1a\u201c\u82f1\u96c4\uff01\u8c6a\u6770\uff01\u597d\u6c49\uff01\u2026\u2026\u201d \u5c55\u767d\u5708\u81c2\u7acb\u638c\uff0c\u529f\u8d2f\u56db\u68a2\uff0c\u9053\uff1a\u201c\u8bf7\u51fa\u62db\u5427\uff01\u201d \u767d\u53d1\u5a46\u5a46\u6027\u5982\u70c8\u706b\uff0c\u53cc\u65b9\u7f57\u55e6\u4e86\u534a\u5929\uff0c\u5fc3\u4e2d\u65e9\u5df2\u4e0d\u8010\uff0c\u95fb\u5c55\u767d\u9053\u8bf7\uff0c\u518d\u4e0d\u53d1\u8bdd\uff0c\u201c\u641c\u9b42 \u6307\u201d\u795e\u529f\u8fd0\u81f3\u5341\u6210\uff0c\u6307\u51fa\u7834\u98ce\u201c\u5403\u201d\u7684\u4e00\u58f0\uff0c\u70b9\u5411\u5c55\u767d\u201c\u4e09\u9633\u201d\u91cd\u7a74\uff01 \u62db\u51fa\uff0c\u624d\u5e72\u568e\u4e86\u4e00\u58f0\uff1a\u7956\u5976\u5976\u5148\u6253\u7b2c\u4e00\u62db\uff01\u201d \u5c55\u767d\u4e0a\u8eab\u5fae\u6643\uff0c\u638c\u6253\u767d\u53d1\u5a46\u5a46\u53f3\u8155\u201c\u5173\u5143\u201d\uff01 \u8fd9\u907f\u62db\u6253\u62db\uff0c\u5feb\u903e\u7535\u5149\u77f3\u706b\uff01 \u767d\u53d1\u5a46\u5a46\u4f30\u4e0d\u5230\u5c55\u767d\u6b66\u529f\u53c8\u6709\u8fdb\u5883\uff0c\u81ea\u5df1\u62db\u672a\u51fa\u6ee1\uff0c\u5c55\u767d\u62db\u5df2\u6253\u81f3\uff0c\u5979\u9a87\u7136\u800c\u60ca\uff0c\u6025\u5fd9 \u6536\u62db\u540e\u9000\uff01 \u53ea\u542c\u638c\u98ce\u88ab\u7a7a\u201c\u592a\u4ed3\u4e4b\u9f20\u201d\u5411\u5c55\u767d\u8111\u540e\u5288\u51fa\u4e00\u638c\uff01 \u5c55\u767d\u632b\u6b65\u56de\u8eab\uff0c\u8fde\u770b\u4e5f\u4e0d\u770b\u201c\u5237\u201d\u7684\u4e00\u638c\uff0c\u5288\u5411\u201c\u592a\u4ed3\u4e4b\u9f20\u201d\u5de6\u81c2\uff01 \u4ecd\u662f\u89c1\u62db\u6253\u62db\uff0c\u5feb\u5982\u95ea\u7535\uff01 \u201c\u592a\u4ed3\u4e4b\u9f20\u201d\u60ca\u547c\u66b4\u9000\uff01 \u5de6\u8fb9\u201c\u4f5b\u5370\u6cd5\u5e08\u201d\u7684\u53cc\u649e\u638c\uff0c\u53f3\u8fb9\u201c\u8471\u5cad\u4e4b\u9e70\u201d\u7684\u4e5d\u9633\u9b54\u706b\u529f\uff0c\u540c\u65f6\u88ad\u81f3\uff01 \u5c55\u767d\u201c\u5de7\u6253\u8fde\u73af\u201d\uff0c\u5de6\u638c\u53f3\u817f\uff0c\u540c\u65f6\u628a\u201c\u4f5b\u5370\u6cd5\u5e08\u201d\u53ca\u201c\u8471\u5cad\u4e4b\u9e70\u201d\u903c\u9000\uff01 \u4f46\u524d\u3001\u540e\u53cc\u65b9\u7684\u201c\u6c99\u6f20\u4e4b\u72d0\u201d\u3001\u201c\u9634\u5c71\u4e4b\u72fc\u201d\uff0c\u5404\u65bd\u715e\u624b\uff0c\u53c8\u5939\u653b\u800c\u5230\uff01 \u540c\u65f6\uff0c\u659c\u523a\u91cc\u201c\u957f\u9aef\u8001\u4eba\u201d\u8fd0\u8d77\u201c\u5927\u624b\u5370\u201d\u7edd\u4e16\u638c\u529f\uff0c\u5982\u6cf0\u5c71\u538b\u9876\u4e00\u822c\uff0c\u5411\u5c55\u767d\u5f53\u9876\u76d6 \u4e0b\uff01 \u5c55\u81ea\u524d\u3001\u540e\u3001\u5de6\u3001\u53f3\u53d7\u654c\uff0c\u5f53\u9876\u53c8\u6709\u529b\u903e\u4e07\u65a4\u7684\u638c\u529b\u538b\u4e0b\uff0c\u8fde\u817e\u8eab\u56de\u907f\u90fd\u4e0d\u53ef\u80fd\uff01 \u8fd9\u201c\u5357\u6d77\u95e8\u201d\u4e03\u5927\u7edd\u4e16\u9ad8\u624b\uff0c\u5404\u51fa\u4e00\u62db\uff0c\u5373\u5df2\u9707\u60ca\u6240\u6709\u5728\u573a\u4e4b\u4eba\uff01 \u597d\u4e2a\u5c55\u767d\uff0c\u53ea\u89c1\u4ed6\u5728\u5bc6\u5982\u72c2\u98ce\u66b4\u96e8\u7684\u653b\u52bf\u4e4b\u4e0b\uff0c\u76d8\u6253\u6a2a\u626b\uff0c\u903c\u9000\u524d\u540e\u8179\u80cc\u4e4b\u654c\uff0c\u4e00\u62db \u201c\u8fc5\u98ce\u75be\u96f7\u201d\u201c\u8f70\u201d\u7684\u4e00\u58f0\u66b4\u54cd\uff0c\u4e0e\u957f\u9aef\u8001\u4eba\u786c\u5bf9\u4e86\u4e00\u638c\uff01 \u957f\u77ed\u8001\u4eba\u90a3\u4e48\u9ad8\u5f3a\u7684\u6b66\u529f\uff0c\u88ab\u5c55\u767d\u4e00\u638c\u9707\u5f97\u4e0a\u8eab\u8fde\u6643\uff01\u77aa\uff01 \u77aa\uff01\u540e\u9000\u4e86\u4e24\u6b65\uff01 \u957f\u9aef\u8001\u4eba\u957f\u7559\u6839\u6839\u76f4\u7acb\uff0c\u72c2\u7b11\u9053\uff1a\u201c\u597d\u96c4\u539a\u7684\u638c\u529b\uff01\u5c0f\u54e5\u513f\u6b66\u529f\u53c8\u5927\u6709\u8fdb\u5883\u4e86\uff01\u201d \u8bf4\u7f62\uff0c\u7b2c\u4e8c\u6b21\u731b\u6251\u6025\u4e0a\uff01 \u5c55\u767d\u65bd\u5c55\u5f00\u201c\u65e0\u8272\u65e0\u76f8\u8eab\u6cd5\u201d\u811a\u8e0f\u201c\u5343\u5e7b\u98d8\u9999\u6b65\u201d\uff0c\u8eab\u5f62\u4e4b\u5feb\uff0c\u5f53\u771f\u662f\u7535\u8f6c\u661f\u98de\uff0c\u624b\u4e0a \u662f\u201c\u5929\u4f5b\u964d\u9b54\u638c\u201d\u4e0e\u201c\u98ce\u96f7\u516b\u638c\u201d\u4ea4\u4e92\u8fd0\u7528\uff0c\u53ea\u89c1\u4ed6\u8eab\u5f62\u5982\u5e7b\uff0c\u638c\u529b\u5982\u98d9\uff0c\u624b\u3001\u773c\u3001\u8eab\u3001 \u6cd5\u3001\u6b65\uff0c\u51e0\u4e4e\u4ee5\u8089\u773c\u96be\u89c1\u7684\u901f\u5ea6\uff0c\u4e0e\u201c\u5357\u6d77\u95e8\u201d\u4e03\u5927\u9876\u5c16\u9ad8\u624b\uff0c\u6218\u5728\u4e00\u8d77\uff01 \u201c\u5357\u6d77\u95e8\u201d\u4e03\u5927\u9876\u5c16\u9ad8\u624b\uff0c\u201c\u6d77\u5916\u4e09\u715e\u201d\uff0c\u201c\u9ed1\u9053\u56db\u51f6\u201d\uff0c\u63a8\u51fa\u5176\u4e2d\u4efb\u4f55\u4e00\u4f4d\uff0c\u5747\u53ef\u9707 \u60ca\u6c5f\u6e56\uff0c\u5982\u4eca\u4e03\u4eba\u5408\u624b\uff0c\u6218\u4e00\u4e2a\u5c11\u5e74\u5c55\u767d\uff0c\u66f4\u662f\u4e2a\u4e2a\u2019\u62fc\u547d\uff0c\u5747\u4ee5\u672c\u8eab\u82e6\u7ec3\u6570\u5341\u4e2d\u7684\u7edd\u827a\u76f8 \u62fc\uff01 \u770b\u5f97\u4f17\u4eba\u6697\u6697\u5403\u60ca\uff0c\u5fc3\u751f\u9a87\u610f\uff01 \u767d\u8863\u94f6\u6247\u7684\u201c\u9f99\u795e\u592a\u5b50\u201d\u5df2\u5931\u53bb\u5f80\u5e38\u8f7b\u677e\u6f47\u6d12\u4e4b\u6001\uff0c\u53cc\u624b\u7d27\u63e1\u4f4f\u94f6\u6247\uff0c\u4e24\u53ea\u773c\u775b\u4e00\u811a\u4e0d \u77ac\u5730\u6ce8\u89c6\u7740\u573a\u4e2d\u7684\u6253\u6597\uff01 \u8c8c\u5982\u5929\u4ed9\u7684\u201c\u5357\u6d77\u9f99\u5973\u201d\uff0c\u53cc\u76ee\u653e\u5149\uff0c\u7c89\u8138\u4e0a\u5ffd\u559c\u5ffd\u5fe7\uff0c\u663e\u793a\u7740\u5979\u5185\u5fc3\u60c5\u7eea\u7684\u77db\u76fe\u548c\u53d8 \u5316\u2026\u2026 \u96f7\u5927\u53d9\u602a\u76ee\u5389\u7741\uff0c\u6ee1\u5934\u4e71\u53d1\u5982\u84ec\u2026\u2026 \u201c\u592a\u767d\u53cc\u9038\u201d\u3001\u8339\u8001\u9556\u5934\u90fd\u7d27\u5f20\u5f97\u77aa\u5927\u4e86\u773c\u775b\u2026\u3002\xb7\u6155\u5bb9\u7ea2\u3001\u5a49\u513f\u7d27\u5f20\u5f97\u7c89\u8138\u715e\u767d\u2026\u2026 \u91d1\u5f69\u51e4\u3001\u6a0a\u7d20\u5f2f\u8868\u9762\u4e0a\u770b\u6765\u5e73\u9759\uff0c\u4f46\u53ea\u8981\u4ed4\u7ec6\u6ce8\u610f\u5979\u4fe9\u7d27\u63e1\u7740\u7684\u53cc\u624b\uff0c\u53ca\u9165\u80f8\u6025\u9aa4\u5730\u547c \u5438\u8d77\u4f0f\u7740\uff0c\u4fbf\u77e5\u5979\u4fe9\u4e5f\u662f\u7d27\u5f20\u4e07\u5206\uff0c\u8868\u9762\u7684\u5e73\u9759\uff0c\u4e0d\u8fc7\u662f\u6781\u529b\u77dc\u6301\u7740\u88c5\u51fa\u6765\u7684\uff01 \u5728\u201c\u6d77\u5916\u4e09\u715e\u201d\u4e4b\u4e2d\uff0c\u867d\u4ee5\u957f\u9aef\u8001\u4eba\u5fc3\u80f8\u6bd4\u8f83\u8c26\u548c\uff0c\u4f46\u957f\u9aef\u8001\u4eba\u7d20\u5e38\u4ee5\u6b66\u529f\u5929\u4e0b\u7b2c\u4e00\u81ea \u8bb8\uff0c\u4e00\u751f\u672a\u9047\u654c\u624b\uff0c\u5982\u4eca\u4e00\u65e6\u9047\u5230\u52bf\u5747\u529b\u654c\u7684\u5bf9\u624b\uff0c\u53c8\u6fc0\u8d77\u4e86\u4ed6\u7684\u4e89\u80dc\u4e4b\u5fc3\uff0c\u65e9\u628a\u90a3\u8c26\u548c\u4e4b \u5fc3\u4e0e\u656c\u4f69\u5c55\u767d\u4e4b\u5ff5\u4e22\u5728\u4e5d\u9704\u4e91\u5916\uff0c\u201c\u5927\u624b\u5370\u201d\u638c\u529f\u8fd0\u81f3\u5341\u6210\uff0c\u8f66\u8f6e\u5927\u7684\u5de8\u7075\u5927\u638c\uff0c\u638c\u638c\u6251\u5411 \u5c55\u767d\u7684\u8981\u5bb3\u3002 \u65e0\u5948\uff0c\u5c55\u767d\u56e0\u670d\u98df\u5343\u5e74\u5723\u836f\u201c\u7d2b\u6a80\u82b1\u201d\uff0c\u79ef\u4e8e\u5185\u8179\u7684\u6de4\u8840\u88ab\u957f\u9aef\u8001\u4eba\u9707\u5f00\uff0c\u5410\u51fa\u4e4b\u540e\uff0c \u8151\u5185\u771f\u6c14\u6d41\u8f6c\uff0c\u5185\u529b\u6e90\u6e90\u800c\u751f\uff0c\u638c\u529b\u5927\u5f97\u51fa\u5947\uff0c\u957f\u9aef\u8001\u4eba\u6bcf\u4e0e\u5c55\u767d\u786c\u5bf9\u4e00\u638c\uff0c\u201c\u8f70\u201d\u7136\u66b4\u54cd \u58f0\u4e2d\uff0c\u4fbf\u88ab\u5c55\u767d\u9707\u5f97\u4e94\u5185\u751f\u614c\uff01 \u957f\u9aef\u8001\u4eba\u6012\u5578\u5982\u96f7\uff0c\u4f46\u518d\u6012\u4e5f\u6ca1\u6709\u7528\uff0c\u56e0\u6b64\u65f6\u4ed6\u7684\u638c\u529b\u5df2\u6ca1\u6709\u5c55\u767d\u7684\u638c\u529b\u96c4\u539a\uff01 \u96f7\u5927\u53d4\u3001\u201c\u592a\u767d\u53cc\u9038\u201d\u3001\u8339\u8001\u9556\u5934\u4ee5\u53ca\u6155\u5bb9\u7ea2\u4e0e\u5a49\u513f\uff0c\u5374\u6e10\u6e10\u5b89\u5fc3\u4e0b\u6765\uff0c\u56e0\u5c55\u767d\u529b\u6218 \u201c\u5357\u6d77\u95e8\u201d\u4e03\u5927\u9ad8\u624b\uff0c\u663e\u7136\u5df2\u5360\u4e86\u4e0a\u98ce\uff01 \u4e5d\u5927\u95e8\u6d3e\u638c\u95e8\u3001\u6b66\u6797\u56db\u516c\u5b50\uff0c\u4ee5\u53ca\u5929\u4e0b\u7fa4\u96c4\u603b\u7b97\u5f00\u4e86\u773c\u754c\uff0c\u6709\u8c01\u80fd\u60f3\u50cf\u6b66\u6797\u4e2d\u4f1a\u4ea7\u751f\u50cf \u5c55\u767d\u8fd9\u6837\u4e00\u4f4d\u540e\u8d77\u4e4b\u79c0\uff01 \u4e00\u4eba\u529b\u654c\u201c\u5357\u6d77\u95e8\u201d\u4e03\u5927\u7edd\u4e16\u9ad8\u624b\uff0c\u8fd9\u51e0\u4e4e\u662f\u4e0d\u53ef\u80fd\u7684\u4e00\u4ef6\u4e8b\uff01 \u201c\u9f99\u4f38\u592a\u5b50\u201d\u5fc3\u4e2d\u5374\u5728\u6253\u9f13\uff01 \u4ed6\u6ee1\u4ee5\u4e3a\u5c55\u767d\u5c31\u7b97\u6b66\u529f\u9ad8\u5f3a\uff0c\u80fd\u591f\u529b\u6218\u201c\u4e09\u715e\u201d\uff0c\u72ec\u6218\u201c\u56db\u51f6\u201d\uff0c\u8981\u8ba9\u201c\u4e09\u715e\u201d\u3001\u201c\u56db \u51f6\u201d\u5408\u529b\u51fa\u624b\uff0c\u5fc5\u4f1a\u6253\u8d25\u5c55\u767d\uff0c\u5f88\u5feb\u628a\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\u62a2\u593a\u5230\u624b\uff01 \u8c01\u77e5\u5408\u201c\u4e09\u715e\u201d\u3001\u201c\u56db\u51f6\u201d\u4e4b\u529b\uff0c\u4ecd\u7136\u6218\u4e0d\u8fc7\u4e00\u4e2a\u5c55\u767d\uff01\u4ed6\u6e10\u6e10\u4f53\u4f1a\u5230\u4e8b\u6001\u7684\u4e25\u91cd\u6027\uff01 \u4ed6\u6df1\u6050\u81ea\u5df2\u7387\u9886\u201c\u5357\u6d77\u95e8\u201d\uff0c\u503e\u5de2\u4fb5\u72af\u4e2d\u539f\uff0c\u521a\u521a\u5efa\u7acb\u8d77\u6765\u7684\u9738\u4e1a\uff0c\u5c06\u8981\u65ad\u9001\u5728\u5c55\u767d\u4e00 \u4eba\u624b\u4e2d\uff01 \u8fd9\u4e00\u6218\u5173\u7cfb\u592a\u5927\uff0c\u4ed6\u5fc3\u4e2d\u7684\u60ca\u6050\u4e4b\u60c5\u662f\u53ef\u60f3\u800c\u77e5\u7684\uff01 \u4f46\u8fd9\u4e5f\u66f4\u589e\u5f3a\u4e86\u4ed6\u7684\u6740\u673a\uff01 \u4ed6\u4fca\u7f8e\u7684\u8138\u4e0a\u9634\u6674\u4e0d\u5b9a\uff0c\u624b\u63e1\u94f6\u6247\uff0c\u773c\u73e0\u5de6\u8f6c\u53f3\u8f6c\uff0c\u663e\u7136\u4e5f\u662f\u76d8\u7b97\u5982\u4f55\u4e00\u4e3e\u51fb\u6bd9\u5c55\u767d\u4e4b \u7b56\uff01 \u7a81\u7136\u2014\u2014\u201c\u9f99\u795e\u592a\u5b50\u201d\u6e05\u541f\u4e00\u58f0\uff0c\u6717\u6717\u5ff5\u9053\uff1a\u201c\u4e07\u7eff\u4e1b\u4e2d\u4e00\u70b9\u7ea2\uff01\u201d \u6fc0\u6597\u6b63\u70c8\uff0c\u201c\u9f99\u795e\u592a\u5b50\u201d\u5ffd\u7136\u541f\u8d77\u8bd7\u6765\uff0c\u4f17\u4eba\u4e0d\u89c9\u4e00\u6115\u2026\u2026 \u4f46\u5c31\u5728\u4f17\u4eba\u4e00\u609f\u4e4b\u95f4\uff0c\u53ea\u89c1\u6fc0\u6597\u4e2d\u4eba\u5f71\u4ea4\u9519\u6e38\u8d70\uff0c\u201c\u592a\u4ed3\u4e4b\u9f20\u201d\u5ffd\u7136\u201c\u5431\u201d\u7684\u4e00\u58f0\u5c16 \u54e8\uff0c\u817e\u8eab\u5728\u534a\u7a7a\u4e2d\u5212\u4e86\u4e00\u4e2a\u5706\u5f27\uff0c\u53cc\u638c\u731b\u5411\u5c55\u767d\u80f8\u524d\u649e\u53bb\uff01 \u5c55\u767d\u4e0d\u77e5\u201c\u592a\u4ed3\u4e4b\u9f20\u201d\u4e3a\u4f55\u5ffd\u7136\u7528\u51fa\u540c\u5f52\u4e8e\u5c3d\u7684\u6253\u6cd5\uff0c\u5c24\u5176\u53e6\u5916\u516d\u5927\u9ad8\u624b\u62db\u5f0f\u66f4\u4f7f\u4ed6\u65e0 \u6cd5\u95ea\u907f\uff0c\u53ea\u6709\u8fd0\u8db3\u5185\u529b\u4e00\u638c\u8fce\u7740\u98de\u6251\u6765\u7684\u201c\u592a\u4ed3\u4e4b\u9f20\u201d\u5288\u53bb\uff01 \u201c\u8f70\u201d\u7684\u4e00\u58f0\u66b4\u54cd\uff0c\u201c\u592a\u4ed3\u4e4b\u9f20\u201d\u534a\u7a7a\u4e2d\u88ab\u5c55\u767d\u638c\u529b\u9707\u98de\u4e8c\u4e08\u5f00\u5916\uff01 \u63a5\u7740\u201c\u542d\u201d\u7684\u4e00\u58f0\u5389\u5578\uff0c\u201c\u9634\u5c71\u4e4b\u72fc\u201d\u7528\u201c\u592a\u4ed3\u4e4b\u9f20\u201d\u540c\u6837\u7684\u8eab\u6cd5\u548c\u62db\u5f0f\uff0c\u817e\u7a7a\u5411\u5c55\u767d \u649e\u6765\uff01 \u5c55\u767d\u4e00\u638c\u518d\u628a\u201c\u9634\u5c71\u4e4b\u72fc\u201d\u6253\u51fa\u5708\u5916\uff01\u7d27\u8ddf\u7740\u53c8\u662f\u4e00\u58f0\u6012\u543c\uff0c\u201c\u6c99\u6f20\u4e4b\u72d0\u201d\u6251\u7a7a\u53c8\u5230\uff01 \u8bdd\u52ff\u7d6e\u53fc\uff0c\u201c\u56db\u51f6\u201d\u3001\u201c\u4e09\u715e\u201d\u8f6e\u6d41\u5411\u5c55\u767d\u731b\u51b2\u786c\u6253\uff0c\u5b8c\u5168\u662f\u786c\u78b0\u786c\uff0c\u800c\u4e14\u90fd\u91c7\u53d6\u540c\u4e00 \u65b9\u5f0f\uff0c\u4ece\u540c\u4e00\u89d2\u5ea6\u5411\u5c55\u767d\u51b2\u51fb\uff01 \u4f17\u4eba\u6b64\u65f6\u624d\u7b97\u660e\u767d\uff0c\u539f\u6765\u201c\u9f99\u795e\u592a\u5b50\u201d\u5ff5\u7684\u90a3\u4e00\u53e5\u8bd7\uff0c\u662f\u6307\u793a\u201c\u4e09\u715e\u201d\u3001\u201c\u56db\u51f6\u201d\u6539\u53d8 \u6218\u672f\u65b9\u6cd5\u4e4b\u7528\uff01\u201c\u4e09\u715e\u201d\u3001\u201c\u56db\u51f6\u201d\u4e03\u5927\u7edd\u4e16\u9ad8\u624b\uff0c\u6210\u8f6e\u5e26\u65b9\u5f0f\uff0c\u8f6e\u756a\u5411\u5c55\u767d\u731b\u51b2\uff0c\u867d\u7136\u638c \u529f\u5185\u529b\u90fd\u4e0d\u53ca\u5c55\u767d\u6df1\u539a\uff0c\u6bcf\u5bf9\u4e00\u638c\uff0c\u5373\u611f\u5934\u660f\u773c\u82b1\uff0c\u8151\u5185\u771f\u6c14\u56db\u7a9c\uff0c\u53ef\u662f\u4ed6\u4eec\u4e03\u4e2a\u4eba\u90fd\u6709\u7f13 \u53e3\u6c14\u8c03\u606f\u7684\u673a\u4f1a\uff01 \u4f46\u5c55\u767d\u5374\u662f\u8fde\u5598\u53e3\u6c14\u7684\u673a\u4f1a\u90fd\u6ca1\u6709\uff0c\u63a5\u4e86\u4e00\u4e2a\uff0c\u53c8\u6765\u4e00\u4e2a\uff0c\u4e09\u4e2a\u5faa\u73af\u4ee5\u540e\uff0c\u5c55\u81ea\u5df2\u786c\u78b0 \u786c\uff0c\u8fde\u63a5\u4e86\u4e09\u4e03\u5eff\u4e00\u638c\uff0c\u5c55\u767d\u7eb5\u662f\u94c1\u6253\u94a2\u94f8\u4e4b\u4eba\uff0c\u4e5f\u67b6\u4e0d\u4f4f\u4e03\u5927\u5730\u4e16\u9ad8\u624b\u8fd9\u822c\u4e0d\u987e\u6027\u547d\u7684\u8f6e \u756a\u731b\u88ad\uff01 \u201c\u9f99\u795e\u592a\u5b50\u201d\u679c\u7136\u662f\u8be1\u8bc8\u591a\u7aef\u4e4b\u4eba\uff0c\u8fd9\u4e00\u62db\u8fd8\u771f\u8ba9\u4ed6\u7528\u5bf9\u4e86\uff0c\u4e09\u4e2a\u5faa\u73af\uff0c\u56db\u4e2a\u5faa\u73af\uff0c\u4e94 \u4e2a\u5faa\u73af\u4ee5\u540e\uff0c\u5c55\u767d\u989d\u89d2\u4e0a\u6eda\u4e0b\u8c46\u5927\u7684\u6c57\u6b8a\uff0c\u53d1\u51fa\u89e3\u638c\u529b\u4ea6\u6e10\u6765\u6e10\u5f31\uff0c\u4e0d\u80fd\u628a\u51cc\u7a7a\u731b\u7136\u786c\u51b2\u5411 \u4ed6\u7684\u201c\u4e09\u715e\u201d\u3001\u201c\u56db\u51f6\u201d\u9707\u9000\u4e86\uff01\u4f17\u4eba\u5df2\u770b\u5f97\u660e\u767d\uff0c\u201c\u4e09\u715e\u201d\u3001\u201c\u56db\u51f6\u201d\u8fd9\u79cd\u786c\u6253\u786c\u6328\u7684\u4f5c \u98ce\uff0c\u662f\u60f3\u628a\u5c55\u767d\u5185\u529b\u6d88\u8017\u6b86\u5c3d\uff0c\u6d3b\u6d3b\u5730\u66b4\u6b7b\uff01\u5a49\u513f\u4e00\u5fc3\u7231\u5c55\u767d\uff0c\u6700\u4e3a\u5173\u5fc3\uff0c\u89c1\u72b6\u4e0d\u7531\u5c16\u53eb \u9053\uff1a\u4f60\u4eec\u8fd9\u662f\u4ec0\u4e48\u6253\u6cd5\uff1f\u201d \u6155\u5bb9\u7ea2\u6025\u5f97\u6cea\u73e0\u5728\u773c\u7736\u5185\u6253\u8f6c\uff0c\u8131\u53e3\u9a82\u9053\uff1a\u201c\u7b80\u76f4\u4e0d\u8981\u8138\uff01\u201d \u201c\u9f99\u795e\u592a\u5b50\u201d\u89c1\u72e1\u8ba1\u5df2\u552e\uff0c\u624b\u6447\u94f6\u6247\uff0c\u6d0b\u6d0b\u5f97\u610f\u5730\u7b11\u9053\uff1a\u201c\u6210\u8005\u738b\u4faf\u8d25\u8005\u8d3c\uff01\u6218\u573a\u4e0a\u80dc \u8005\u4e3a\u96c4\uff0c\u96be\u9053\u6253\u4ed7\u8fd8\u6709\u4e00\u5b9a\u7684\u65b9\u6cd5\u5417\uff1f\u54c8\u54c8\u54c8\u2026\u2026\u201d\u7b11\u58f0\u5f97\u610f\u5df2\u6781\uff01 \u5a49\u513f\u6c14\u5f97\u4e00\u8dfa\u811a\uff0c\u56de\u5934\u5bf9\u6155\u5bb9\u7ea2\u9053\uff1a\u201c\u59d0\u59d0\uff01\u6211\u4eec\u51b2\u8fc7\u53bb\uff01\u201d \u6155\u5bb9\u7ea2\u5fae\u4e00\u70b9\u5934\uff0c\u4f46\u5979\u59d0\u59b9\u4e8c\u4eba\u521a\u4e00\u4e3e\u6b65\uff0c\u201c\u5357\u6d77\u9f99\u5973\u201d\u98de\u8eab\u963b\u5728\u4e8c\u4eba\u9762\u524d\uff0c\u9762\u90e8\u5bd2 \u971c\uff0c\u53f1\u9053\uff1a\u201c\u4f60\u4eec\u8001\u8001\u5b9e\u5b9e\u5730\u7ed9\u6211\u7ad9\u5728\u6b64\u5730\uff0c\u8c01\u8981\u4e0a\u524d\u4e00\u6b65\uff0c\u59d1\u5a18\u4fbf\u53eb\u5979\u6b7b\u65e0\u846c\u8eab\u4e4b\u5730\uff01\u201d \u6155\u5bb9\u7ea2\u51b7\u51b7\u4e00\u58f0\uff1a\u201c\u672a\u5fc5\uff01\u201d \u5a49\u513f\u5fc3\u6025\uff0c\u6012\u53f1\u4e00\u58f0\uff0c\u201c\u641c\u9b42\u6307\u201d\u795e\u529f\u76f4\u5411\u201c\u5357\u6d77\u9f99\u5973\u201d\u8f6f\u808b\u4e0b\u70b9\u53bb\uff01 \u201c\u5357\u6d77\u9f99\u5973\u201d\u5a07\u53f1\u4e00\u58f0\uff1a\u201c\u4f60\u627e\u6b7b\uff01\u201d \u5a07\u8eaf\u4e00\u8f6c\uff0c\u53cd\u81c2\u4e00\u638c\uff0c\u201c\u562d\u201d\u7684\u4e00\u58f0\uff0c\u628a\u5a49\u513f\u9707\u98de\u4e00\u4e08\u5f00\u5916\uff01 \u6155\u5bb9\u7ea2\u6012\u53f1\u9053\uff1a\u201c\u59d1\u5a18\u8ddf\u4f60\u62fc\u4e86\uff01\u2026\u2026\u201d \u7a81\u65ad\u5c55\u767d\u4e00\u58f0\u60e8\u547c\uff0c\u63a5\u7740\u4f20\u6765\u201c\u7830\u201d\u7684\u4e00\u58f0\u66b4\u54cd\uff01\u2026\u3002 \u7b2c\u516d\u5341\u4e09\u7ae0\u3000\u76d6\u4e16\u6b66\u795e>> \u53e4\u9f99\u300a\u5251\u5ba2\u884c\u300b \u7b2c\u516d\u5341\u4e09\u7ae0\u3000\u76d6\u4e16\u6b66\u795e \u5c55\u767d\u4e00\u638c\uff0c\u628a\u201c\u592a\u4ed3\u4e4b\u9f20\u201d\u9707\u9000\u4e00\u4e08\u5f00\u5916\u3002 \u4f46\u7531\u4e8e\u201c\u592a\u4ed3\u4e4b\u9f20\u201d\u8fd9\u8fce\u5934\u4e00\u51fb\uff0c\u662f\u6bd5\u751f\u529f\u529b\u4e4b\u6240\u805a\uff0c\u5c55\u767d\u4e5f\u88ab\u9707\u5f97\u8eab\u5f62\u8fde\u6643\u4e86\u6570\u6643\uff01 \u6b64\u65f6\uff0c\u4e00\u58f0\u60e8\u547c\u4f20\u6765\uff0c\u6b63\u662f\u5a49\u513f\u8d76\u6765\u76f8\u52a9\u5c55\u767d\uff0c\u88ab\u201c\u5357\u6d77\u9f99\u5973\u201d\u4e00\u638c\u6253\u5012\uff01 \u5c55\u767d\u7565\u4e00\u5206\u795e\uff0c\u8fce\u9762\u4e00\u80a1\u52b2\u98ce\u53c8\u544a\u649e\u6765\uff01 \u5c55\u767d\u6b64\u65f6\u662f\u771f\u6025\u4e86\uff0c\u201c\u5929\u4f5b\u964d\u9b54\u638c\u201d\u8fd0\u96c6\u5168\u8eab\u529f\u529b\u72c2\u5288\u800c\u51fa\uff01 \u201c\u8f70\u201d\u7684\u4e00\u58f0\u5927\u9707\uff0c\u98ce\u5578\u5c18\u98de\uff0c\u5939\u7740\u4e00\u58f0\u95f7\u54fc\uff0c\u4e00\u4e2a\u8fce\u9762\u649e\u5411\u5c55\u767d\u7684\u9ad8\u5927\u9ed1\u5f71\uff0c\u5df2\u88ab\u5c55 \u767d\u638c\u529b\u9707\u98de\uff01 \u8fd9\u6b21\u56e0\u5c55\u767d\u4f7f\u51fa\u5168\u529b\uff0c\u90a3\u9ad8\u5927\u8eab\u5f71\uff0c\u76f4\u88ab\u9707\u51fa\u4e09\u4e08\u4f59\u8fdc\uff0c\u624d\u201c\u7830\u201d\u7684\u4e00\u58f0\uff0c\u8dcc\u5728\u5730\u4e0a\uff0c \u90a3\u9ad8\u5927\u9ed1\u5f71\u8e09\u8dc4\u722c\u8d77\uff0c\u5634\u4e2d\u55b7\u51fa\u4e00\u53e3\u9c9c\u8840\uff0c\u53cc\u76ee\u72de\u6076\u5730\u671b\u7740\u5c55\u767d\uff0c\u6b63\u662f\u201c\u9634\u5c71\u4e4b\u72fc\u201d\uff01 \u4f46\u5c55\u767d\u4e5f\u88ab\u4ed6\u8fd9\u5168\u529b\u4e00\u51fb\uff0c\u9707\u9000\u4e86\u4e00\u6b65\uff01 \u201c\u4f5b\u5370\u6cd5\u5e08\u201d\u66f4\u4e0d\u5f85\u5c55\u767d\u5598\u8fc7\u4e00\u53e3\u6c14\u6765\uff0c\u8eab\u5f62\u8e72\u4fef\u5728\u5730\uff0c\u8179\u90e8\u8fd0\u6c14\u5982\u9f13\uff0c\u731b\u5730\u201c\u5471\uff01 \u5471\uff01\u201d\u5389\u9e23\u4e24\u58f0\uff0c\u4e24\u817f\u4e00\u5f39\uff0c\u8eab\u5f62\u7535\u5c04\u800c\u8d77\uff0c\u75f0\u5411\u5c55\u767d\u6251\u81f3\uff01 \u534a\u7a7a\u4e2d\u53cc\u638c\u731b\u6467\uff0c\u201c\u86e4\u87c6\u7f61\u6c14\u201d\u7684\u638c\u529f\uff0c\u4ee5\u5c71\u5d29\u6d77\u5578\u4e4b\u52bf\u5411\u5c55\u767d\u8fce\u9762\u649e\u6765\uff01 \u5c55\u767d\u5173\u5fc3\u5a49\u513f\u5b89\u5371\uff0c\u53c8\u9047\u201c\u4e09\u715e\u201d\u3001\u201c\u56db\u51f6\u201d\u8f6e\u756a\u731b\u51b2\uff0c\u6beb\u4e0d\u7ed9\u4ed6\u5598\u606f\u4f59\u5730\uff0c\u5fc3\u4e2d\u53c8\u6025 \u53c8\u6012\uff0c\u731b\u89c1\u201c\u4f5b\u5370\u6cd5\u5e08\u201d\u53c8\u631f\u6cf0\u5c71\u538b\u9876\u4e4b\u52bf\u5411\u4ed6\u649e\u6765\uff0c\u7acb\u5373\u5410\u6c14\u5f00\u58f0\uff0c\u53cc\u638c\u4ee5\u5468\u8eab\u529f\u529b\uff0c\u731b \u5288\u800c\u51fa\uff01 \u201c\u8f70\u9686\uff01\u201d\u77f3\u7834\u5929\u60ca\u7684\u4e00\u58f0\u5927\u9707\uff0c\u7802\u77f3\u56db\u5377\u201c\u4f5b\u5370\u6cd5\u5e08\u201d\u2014\u526f\u80a5\u80d6\u7684\u77ed\u77ee\u8eab\u8eaf\uff0c\u72b9\u5982\u4e00 \u4e2a\u5927\u76ae\u7403\u4e00\u822c\uff0c\u76f4\u88ab\u5c55\u767d\u4e00\u638c\u9707\u51fa\u5c06\u53ca\u56db\u4e08\u5f00\u5916\uff01 \u56db\u5468\u89c2\u6218\u7684\u7fa4\u96c4\uff0c\u4e0d\u8bba\u654c\u6211\u53cc\u65b9\uff0c\u5747\u4e0d\u7531\u5012\u62bd\u4e00\u53e3\u51b7\u6c14\uff0c\u8fde\u559d\u5f69\u90fd\u5fd8\u4e86\uff0c\u53ea\u6697\u60ca\u5c55\u767d\u7684 \u638c\u529b\u5b9e\u5728\u60ca\u4eba\uff01 \u53ef\u662f\uff0c\u5c55\u767d\u4e5f\u88ab\u8fd9\u4e00\u638c\u7684\u53cd\u9707\u4e4b\u529b\uff0c\u9707\u9000\u4e86\u4e09\u5927\u6b65\uff0c\u624d\u62ff\u88ab\u7ad9\u7a33\u2026\u2026 \u5728\u5c55\u767d\u63a5\u8fde\u4e0e\u201c\u9634\u5c71\u4e4b\u72fc\u201d\u53ca\u201c\u4f5b\u5370\u6cd5\u5e08\u201d\u786c\u62fc\u4e24\u638c\u7684\u540c\u65f6\uff0c\u6155\u5bb9\u7ea2\u3001\u201c\u592a\u767d\u53cc\u9038\u201d\u53ca \u8339\u8001\u9556\u5934\uff0c\u66fe\u5148\u540e\u8fdb\u4e0a\u524d\u6765\u60f3\u52a9\u5c55\u767d\uff01 \u4f46\u4ed6\u51e0\u4eba\u600e\u662f\u201c\u5357\u6d77\u9f99\u5973\u201d\u7684\u5bf9\u624b\uff0c\u65b9\u5f80\u524d\u4e00\u51d1\uff0c\u90fd\u88ab\u201c\u5357\u6d77\u9f99\u5973\u201d\u7ea4\u624b\u9065\u9065\u4e00\u62cd\uff0c\u7ed9 \u6321\u4e86\u56de\u53bb\uff01 \u96f7\u5927\u53d4\u6b63\u5728\u4e00\u638c\u6309\u5728\u5a49\u513f\u540e\u5fc3\u201c\u547d\u95e8\u201d\u7a74\u4e0a\uff0c\u4e3a\u5a49\u513f\u8fd0\u529f\u7597\u4f24\uff0c\u89c1\u72b6\u6c14\u5f97\u6ee1\u5934\u4e71\u53d1\u65e0\u98ce \u81ea\u52a8\uff0c\u4e00\u53cc\u602a\u76ee\u66f4\u662f\u7741\u5f97\u51e0\u4e4e\u7a81\u51fa\u7736\u5916\uff0c\u4f46\u53ea\u662f\u5fc3\u4e2d\u5e72\u7740\u6025\uff0c\u5374\u65e0\u6cd5\u5206\u8eab\u4e0a\u524d\u76f8\u52a9\uff01\u2026\u2026 \u6b64\u65f6\uff0c\u5de8\u5927\u65e0\u94f8\u7684\u58f0\uff0c\u76f8\u7ee7\u4f20\u6765\uff0c\u201c\u8f70\uff01\u8f70\uff01\u201d\u72b9\u5982\u5de8\u96f7\u60ca\u5ead\uff0c\u7802\u98de\u77f3\u8d70\uff0c\u9707\u5730\u60ca\u5929\uff0c \u58f0\u52bf\u60ca\u4eba\u5df2\u6781\uff01 \u5982\u4eca\uff0c\u5728\u201c\u9f99\u795e\u592a\u5b50\u201d\u6307\u6325\u4e4b\u4e0b\uff0c\u201c\u4e09\u715e\u201d\u3001\u201c\u56db\u51f6\u201d\u5747\u4ee5\u5168\u8eab\u529f\u529b\uff0c\u8f6e\u756a\u5411\u4ed6\u731b\u51b2\uff0c \u6bcf\u4e00\u6b21\u51b2\u51fb\u4e4b\u529b\u9053\uff0c\u5747\u4e0d\u4e0b\u5343\u65a4\u4e4b\u91cd\uff0c\u800c\u4e14\uff0c\u4ed6\u4eec\u914d\u5408\u5f97\u5929\u8863\u65e0\u7f1d\uff0c\u5728\u5176\u4e2d\u4e00\u4eba\u8fd0\u96c6\u5468\u8eab\u529f \u529b\u3001\u8fce\u5934\u505a\u62fc\u547d\u4e00\u51fb\u4e4b\u65f6\uff0c\u5176\u4f59\u516d\u4eba\u5fc5\u4e92\u76f8\u4ee5\u62db\u5f0f\u914d\u5408\u653b\u51fb\uff0c\u903c\u4f7f\u5c55\u767d\u9664\u5f00\u786c\u67b6\u4e4b\u5916\uff0c\u522b\u65e0 \u4ed6\u9014\uff01 \u8981\u77e5\u201c\u4e09\u715e\u201d\u3001\u201c\u56db\u51f6\u201d\u90fd\u4e0d\u662f\u666e\u901a\u6b66\u6797\u9ad8\u624b\u53ef\u6bd4\uff0c\u5c55\u767d\u6b66\u529f\u518d\u9ad8\uff0c\u867d\u80fd\u4e00\u4e00\u628a\u5176\u9707 \u98de\uff0c\u4f46\u4ed6\u81ea\u5df1\u4e5f\u7981\u4e0d\u4f4f\u4e0a\u8eab\u8fde\u6643\uff0c\u811a\u6b65\u8e09\u8dc4\u540e\u9000\uff01 \u540c\u65f6\uff0c\u4e03\u5927\u9ad8\u624b\u5747\u6709\u8c03\u606f\u590d\u539f\u7684\u65f6\u95f4\uff0c\u5c55\u767d\u5374\u6beb\u65e0\u5598\u606f\u7684\u673a\u4f1ar\u800c\u4e14\uff0c\u5c55\u767d\u6bcf\u628a\u4e03\u5927\u9ad8 \u624b\u9707\u4f24\u4e4b\u540e\uff0c\u7ad9\u5728\u4e00\u65c1\u7763\u6218\u7684\u201c\u9f99\u795e\u592a\u5b50\u201d\u5373\u523b\u6388\u4e88\u4e00\u9896\u7855\u5927\u7684\u6731\u7ea2\u836f\u4e38\uff0c\u90a3\u8d1f\u4f24\u4e4b\u4eba\u628a\u836f \u4e38\u541e\u4e0b\uff0c\u7acb\u65f6\u4f24\u6108\uff0c\u91cd\u65b0\u52a0\u5165\u56f4\u653b\u5c55\u767d\uff01 \u6b64\u65f6\uff0c\u5df2\u662f\u7b2c\u4e8c\u4e2a\u5faa\u73af\uff0c\u6362\u53e5\u8bdd\u8bf4\uff0c\u5c55\u767d\u5df2\u63a5\u8fde\u628a\u201c\u4e09\u715e\u201d\u3001\u201c\u56db\u51f6\u201d\u6bcf\u4eba\u9707\u9000\u4e00\u6b21\uff0c \u201c\u592a\u4ed3\u4e4b\u9f20\u201d\u3001\u201c\u9634\u5c71\u4e4b\u72fc\u201d\u53c8\u505a\u4e86\u7b2c\u4e8c\u6b21\u5168\u529b\u7684\u4e00\u51fb\uff0c\u5c55\u767d\u63a5\u8fde\u53c8\u628a\u8fd9\u4e24\u5927\u9ad8\u624b\u9707\u9000\u4e4b \u540e\uff0c\u4ed6\u81ea\u5df1\u5df2\u611f\u5230\u638c\u5fc3\u706b\u70ed\uff0c\u53cc\u81c2\u53d1\u9ebb\uff0c\u5185\u8151\u6c14\u8840\uff0c\u66f4\u662f\u7ffb\u6eda\u4e0d\u5df2\uff01 \u201c\u592a\u4ed3\u4e4b\u9f20\u201d\u3001\u201c\u9634\u5c71\u4e4b\u72fc\u201d\u8fd9\u56de\u88ab\u5c55\u767d\u638c\u4f24\u66f4\u91cd\uff0c\u8dcc\u6eda\u4e24\u4e08\u5f00\u5916\uff0c\u4ece\u5730\u4e0a\u722c\u8d77\u6765\uff0c\u5df2 \u7ecf\u6ea2\u51fa\u6ee1\u53e3\u9c9c\u8840\uff01 \u201c\u9f99\u795e\u592a\u5b50\u201d\u624b\u4e2d\u62ff\u7740\u4e00\u4e2a\u73bb\u7483\u74f6\uff0c\u8fd9\u65f6\u53c8\u4ece\u73bb\u7483\u74f6\u4e2d\u5012\u51fa\u4e24\u7c92\u836f\u4e38\uff0c\u8fd9\u6b63\u662f\u201c\u5357\u6d77 \u95e8\u201d\u72ec\u5236\u79d8\u836f\uff0c\u4e13\u6cbb\u8dcc\u6253\u635f\u4f24\uff0c\u4e14\u80fd\u632f\u594b\u7cbe\u529b\uff0c\u540d\u4e3a\u201c\u9f99\u864e\u5927\u529b\u4e38\u201d\uff0c\u4e5f\u53ef\u8bf4\u662f\u4e00\u6781\u70c8\u6027\u7684 \u5174\u594b\u5242\uff0c\u666e\u901a\u4eba\u5c79\u4e86\u90fd\u53ef\u589e\u5f3a\u6c14\u529b\u6570\u500d\uff0c\u5fc5\u987b\u627e\u4eba\u6251\u6253\uff0c\u6216\u505a\u6781\u4e3a\u52b3\u529b\u7684\u5de5\u4f5c\uff0c\u628a\u836f\u6027\u53d1\u6cc4 \u4e4b\u540e\uff0c\u624d\u4f1a\u5b89\u9759\u4e0b\u6765\u3002\u7ec3\u6b66\u4e4b\u4eba\u5403\u4e86\uff0c\u518d\u7ecf\u8fd0\u6c14\u4e00\u50ac\uff0c\u836f\u6027\u6fc0\u70c8\u884c\u5f00\uff0c\u66f4\u975e\u8981\u627e\u4eba\u89d2\u529b\uff0c\u6216 \u62fc\u6597\u4e0d\u53ef\uff01 \u201c\u9f99\u795e\u592a\u5b50\u201d\u7adf\u4ee5\u8fd9\u79cd\u70c8\u6027\u5174\u594b\u5242\uff0c\u5206\u7ed9\u4e03\u5927\u9ad8\u624b\u670d\u7528\uff0c\u6240\u4ee5\u201c\u4e09\u715e\u201d\u3001\u201c\u56db\u51f6\u201d\u7adf\u80fd \u4e0d\u987e\u4f24\u52bf\uff0c\u524d\u8d74\u540e\u7ee7\u4e0e\u5c55\u767d\u529b\u62fc\uff01 \u201c\u9f99\u795e\u592a\u5b50\u201d\u624b\u6307\u4e00\u5f39\uff0c\u4e24\u7c92\u836f\u4e38\u5206\u5c04\u5411\u201c\u592a\u4ed3\u4e4b\u9f20\u201d\u53ca\u201c\u9634\u5c71\u4e4b\u72e0\u201d\uff0c\u4e8c\u4eba\u628a\u836f\u4e38\u63a5 \u8fc7\uff0c\u770b\u4e5f\u6ca1\u770b\u4fbf\u653e\u5165\u5634\u4e2d\uff0c\u201c\u54af\u5494\uff01\u54af\u5494\u201d\u4e00\u9635\u5927\u56bc\uff0c\u54bd\u4e0b\u809a\u53bb\uff0c\u957f\u5438\u4e86\u4e00\u53e3\u6c14\uff0c\u7528\u624b\u6478\u4e86 \u6478\u809a\u5b50\uff0c\u7acb\u523b\u864e\u543c\u4e00\u58f0\uff0c\u53c8\u53cc\u53cc\u6251\u4e0a\uff01 \u5728\u8fd9\u6bb5\u65f6\u5546\uff0c\u5c55\u767d\u53c8\u63a5\u8fde\u9707\u9000\u201c\u4f5b\u5370\u6cd5\u5e08\u201d\u53ca\u201c\u82cd\u5cad\u4e4b\u9e70\u201d\uff0c\u201c\u592a\u4ed3\u4e4b\u9f20\u201d\u3001\u201c\u9634\u5c71\u4e4b \u72fc\u201d\uff0c\u6b63\u597d\u8865\u4e0a\u7a7a\u7f3a\uff01 \u767d\u53d1\u5a46\u5a46\u6ee1\u5934\u767d\u53d1\u5df1\u62ab\u6563\u4e86\u6ee1\u8138\uff0c\u6447\u4e86\u6447\u5934\uff0c\u9732\u51fa\u767d\u53d1\u906e\u62d6\u4f4f\u7684\u4e00\u53cc\u56e0\u6fc0\u6012\u800c\u706b\u7ea2\u7684\u53cc \u773c\uff0c\u7ecd\u6881\u60e8\u7b11\u9053\uff1a\u201c\u5c0f\u54e5\u513f\uff01\u518d\u63a5\u8001\u592a\u5a46\u4e00\u62db\uff01\u201d \u8bdd\u5230\u4eba\u5230\uff0c\u817e\u8eab\u731b\u6251\uff0c\u53ca\u81f3\u5c55\u767d\u8eab\u524d\uff0c\u8fd0\u8db3\u4e86\u6bd5\u751f\u529f\u529b\u7684\u201c\u641c\u9b42\u6307\u201d\u795e\u529f\uff0c\u201c\u78c1\u78c1\u201d\u7834 \u98ce\uff0c\u731b\u5411\u5c55\u767d\u524d\u5fc3\u201c\u4e09\u9633\u91cd\u7a74\u201d\u70b9\u5230\uff01 \u957f\u80c3\u8001\u4eba\u3001\u201c\u6c99\u6f20\u4e4b\u72d0\u201d\u3001\u201c\u592a\u4ed3\u4e4b\u9f20\u201d\u3001\u201c\u9634\u5c71\u4e4b\u72fc\u201d\uff0c\u56db\u4eba\u516b\u53ea\u624b\u638c\uff0c\u51e0\u5982\u4e00\u7247\u638c \u5c71\uff0c\u9f50\u5411\u5c55\u767d\u5de6\u3001\u53f3\u3001\u540e\u4e09\u65b9\u653b\u81f3\uff01 \u8fd9\u6b63\u662f\u4e3a\u4e86\u914d\u5408\u767d\u53d1\u5a46\u5a46\u51b7\u8273\u7ea2\u7684\u653b\u52bf\uff0c\u56db\u5927\u9ad8\u624b\u903c\u4f7f\u5c55\u767d\u975e\u8981\u786c\u62fc\u4e00\u62db\u4e0d\u53ef\uff01 \u5c55\u767d\u6b64\u65f6\u5df2\u6ef4\u8138\u662f\u6c57\uff0c\u6563\u4e71\u7684\u989d\u53d1\uff0c\u6709\u4e24\u7efa\u5782\u81f3\u7709\u95f4\uff0c\u5251\u7709\u8f69\u7acb\uff0c\u53cc\u773c\u5ffd\u7741\uff0c\u4ed6\u811a\u8e0f \u201c\u5343\u5e7b\u98d8\u9999\u6b65\u201d\uff0c\u5de6\u51b2\u53f3\u95ef\uff0c\u5374\u672a\u80fd\u628a\u8eab\u5f62\u8361\u51fa\u56db\u5927\u9ad8\u624b\u7684\u638c\u98ce\u4e4b\u5916\uff0c\u800c\u767d\u53d1\u5a46\u5a46\u51cc\u5389\u65e0\u5339 \u7684\u201c\u641c\u9b42\u6307\u201d\u5df2\u631f\u7740\u7834\u7a7a\u9510\u54cd\u5954\u4ed6\u80f8\u524d\uff01 \u5c55\u767d\u54ac\u4e86\u54ac\u94a2\u7259\uff0c\u201c\u5929\u4f5b\u964d\u9b54\u638c\u201d\u529b\u8fd0\u81f3\u5341\u6210\uff0c\u7528\u201c\u4f5b\u7956\u53c2\u7985\u201d\u62db\u5f0f\uff0c\u8fce\u7740\u5c16\u9510\u7684\u6307\u98ce \u63a8\u53bb\uff01 \u201c\u8f70\uff01\u201d\u201c\u566c\uff01\u201d \u4e00\u58f0\u9707\u5929\u7684\u5de8\u54cd\uff0c\u767d\u53d1\u5a46\u5a46\u5e94\u58f0\u88ab\u9707\u98de\u4e00\u4e08\u5f00\u5916\uff01 \u534a\u7a7a\u4e2d\u5373\u55b7\u51fa\u4e00\u53e3\u8840\u7bad\uff0c\u6eda\u8dcc\u5730\u4e0a\uff0c\u8fd9\u6b21\u7ffb\u4e86\u4e24\u4e2a\u8eab\uff0c\u5374\u518d\u4e5f\u722c\u4e0d\u8d77\u6765\uff01 \u201c\u9f99\u795e\u592a\u5b50\u201d\u4e00\u63a0\u81f3\u524d\uff0c\u642c\u8d77\u767d\u53d1\u5a46\u5a46\u5934\u9885\uff0c\u63a5\u8fde\u63a5\u5728\u5979\u5634\u91cc\u4e09\u9897\u201c\u9f99\u864e\u5927\u529b\u4e38\u201d\uff0c\u5355 \u638c\u5e76\u5728\u767d\u53d1\u5a46\u5a46\u540e\u5fc3\u4e00\u9635\u6309\u63c9\uff0c\u767d\u53d1\u5a46\u5a46\u98a4\u5dcd\u5dcd\u53c8\u7ad9\u4e86\u8d77\u6765\uff0c\u5598\u4e86\u4e24\u53e3\u957f\u6c14\uff0c\u5389\u5578\u4e86\u4e00\u58f0\uff0c \u91cd\u53c8\u6251\u5411\u5c55\u767d\u2026\u2026 \u8fd9\u671f\u95f4\u201c\u6c99\u6f20\u4e4b\u72d0\u201d\u4e5f\u817e\u8eab\u5411\u5c55\u767d\u731b\u51b2\u800c\u81f3\uff01 \u65b9\u624d\u5c55\u767d\u638c\u9707\u767d\u53d1\u5a46\u5a46\u51b7\u8273\u7ea2\uff0c\u90a3\u201c\u54e7\u201d\u7684\u4e00\u58f0\u5fae\u54cd\uff0c\u4e43\u662f\u767d\u53d1\u5a46\u5a46\u7684\u201c\u641c\u9b42\u6307\u201d\u795e \u529f\uff0c\u5df2\u4f7f\u5c55\u767d\u638c\u5fc3\u53d7\u4f24\uff01 \u8bb2\u529b\u5927\u52bf\u731b\uff0c\u662f\u6307\u4e0d\u5982\u62f3\uff0c\u62f3\u4e0d\u5982\u638c\uff0c\u8981\u8bb2\u5c16\u5229\u575a\u9510\uff0c\u5219\u662f\u638c\u4e0d\u5982\u62f3\uff0c\u62f3\u4e0d\u5982\u6307\u3002 \u5c55\u767d\u90a3\u4e00\u638c\u56fa\u7136\u628a\u767d\u53d1\u5a46\u5a46\u6253\u5012\uff0c\u5e76\u4f7f\u767d\u53d1\u5a46\u5a46\u53d7\u4f24\u5410\u8840\uff0c\u4e00\u65f6\u722c\u4e0d\u8d77\u6765\uff0c\u4f46\u767d\u53d1\u5956\u5a46 \u90a3\u201c\u641c\u9b42\u6307\u201d\u5374\u662f\u65e0\u575a\u4e0d\u6467\uff0c\u6781\u4e3a\u5389\u5bb3\u7684\u4e00\u79cd\u6307\u529f\uff0c\u4e14\u80fd\u7a7f\u7834\u5185\u5bb6\u638c\u529b\u4f24\u4eba\uff01 \u5c55\u767d\u53ea\u611f\u638c\u5fc3\u4e00\u9635\u706b\u7099\u822c\u7684\u75db\u695a\uff0c\u5df2\u77e5\u638c\u5fc3\u53d7\u4f24\uff0c\u6697\u9053\u4e86\u4e00\u58f0\uff1a\u201c\u4e0d\u597d\uff01\u2026\u2026\u201d \u53ef\u662f\uff0c\u6ca1\u5bb9\u4ed6\u770b\u89c6\u638c\u5fc3\u7684\u4f24\u52bf\u201c\u6c99\u6f20\u4e4b\u72d0\u201d\u5df2\u547c\u5578\u7740\u5411\u4ed6\u51b2\u6765\uff01 \u201c\u6c99\u6f20\u4e4b\u72d0\u201d\u8fd9\u62db\u66f4\u72e0\uff0c\u53f3\u624b\u4e5d\u5341\u516b\u9897\u80e1\u6843\u6728\u7ea6\u5ff5\u6b8a(\u5171\u662f\u4e00\u767e\u96f6\u516b\u9897\uff0c\u4e0a\u6b21\u800d\u4e22\u4e86\u5341 \u9896\u3002)\u8fde\u6210\u4e00\u4e32\uff0c\u72b9\u5982\u4e00\u6761\u602a\u87d2\uff0c\u76f4\u6253\u5c55\u767d\u9762\u95e8\uff0c\u5de6\u624b\u5c1a\u4ee5\u201c\u5f00\u7891\u638c\u529b\u201d\u72c2\u5288\u5c55\u767d\u9888\u4e0b\uff01 \u4e00\u62db\u4e24\u5f0f\uff0c\u820d\u8eab\u731b\u6251\uff0c\u58f0\u52bf\u9a87\u4eba\uff01 \u5c55\u767d\u53f3\u638c\u638c\u5fc3\u706b\u75db\uff0c\u4e00\u65f6\u65e0\u6cd5\u8fd0\u529f\uff0c\u5f53\u65f6\u5927\u559d\u4e00\u58f0\uff0c\u5de6\u638c\u201c\u7a7a\u624b\u5165\u81ea\u5203\u201d\uff0c\u5728\u5927\u4e32\u5ff5\u73e0 \u5c06\u6253\u5728\u9762\u95e8\u4e0a\u65f6\uff0c\u53cd\u628a\u6293\u4f7f\u73e0\u4e32\uff0c\u7528\u529b\u5f80\u6000\u4e2d\u4e00\u5e26\uff01 \u540c\u65f6\uff0c\u504f\u8111\u8eb2\u8fc7\u780d\u5411\u9888\u4e0a\u7684\u4e00\u638c\uff0c\u8fd9\u65f6\u201c\u6c99\u6f20\u4e4b\u72d0\u201d\u4e50\u5b50\u53ef\u5927\u4e86\uff01 \u56e0\u4ed6\u662f\u5408\u8eab\u731b\u6251\uff0c\u624b\u4e2d\u73e0\u4e32\u88ab\u5c55\u767d\u5f80\u524d\u4e00\u5e26\uff0c\u5c31\u7740\u4ed6\u672c\u8eab\u524d\u51b2\u4e4b\u52bf\uff0c\u6574\u4e2a\u8eab\u8eaf\u90fd\u5411\u5c55\u767d \u6000\u4e2d\u649e\u53bb\uff01 \u201c\u6c99\u6f20\u4e4b\u72d0\u201d\u65e0\u9700\u501f\u529b\uff0c\u8eab\u5f62\u5411\u524d\u4e00\u6cfb\uff0c\u5df2\u5fc3\u77e5\u4e0d\u5999\uff0c\u53e3\u4e2d\u201c\u54ce\u2014\u2014\u5440\uff01\u2026\u2026\u201d\u624d\u5c16\u53eb \u4e86\u534a\u58f0\uff01 \u5c55\u767d\u4e00\u817f\u98de\u53bb\uff0c\u6b63\u8e22\u5728\u4ed6\u7684\u809a\u5b50\u4e0a\uff01 \u201c\u562d\uff01\u201d \u4e00\u58f0\u5de8\u54cd\uff0c\u201c\u6c99\u6f20\u4e4b\u72d0\u201d\u5c31\u50cf\u65ad\u4e86\u7ebf\u7684\u98ce\u7b5d\uff0c\u76f4\u88ab\u8e22\u5411\u534a\u5929\u4e91\u91cc\uff0c\u6015\u4e0d\u6709\u4e09\u3001\u56db\u4e08\u4e4b \u9ad8\uff01 \u5c55\u767d\u8fd9\u4e00\u811a\u795e\u529b\u60ca\u4eba\uff01 \u201c\u9f99\u795e\u592a\u5b50\u201d\u60ca\u547c\u4e00\u58f0\uff0c\u53ca\u65f6\u8d76\u81f3\uff0c\u4f38\u624b\u628a\u201c\u6c99\u6f20\u4e4b\u72d0\u201d\u63a5\u4f4f\uff0c\u5426\u5219\u201c\u6c99\u6f20\u4e4b\u72d0\u201d\u91cd\u4f24 \u4e4b\u4e0b\uff0c\u4ece\u4e09\u3001\u56db\u4e08\u9ad8\u5904\u8dcc\u4e0b\u6765\uff0c\u5fc5\u88ab\u6454\u6b7b\u65e0\u7591\uff01 \u5c31\u8fd9\u6837\u88ab\u201c\u9f99\u795e\u592a\u5b50\u201d\u63a5\u4f4f\uff0c\u53c8\u8fde\u5728\u4ed6\u5634\u4e2d\u585e\u4e86\u4e09\u9897\u836f\u4e38\uff0c\u5f53\u201c\u9f99\u795e\u592a\u5b50\u201d\u628a\u4ed6\u653e\u4e0b\u5730 \u6765\u65f6\uff0c\u4ecd\u7136\u7ad9\u7acb\u4e0d\u4f4f\uff0c\u8eab\u5f62\u64ad\u4e86\u4e24\u6447\uff0c\u53cc\u8ddf\u4e0a\u7ffb\uff0c\u4ecd\u7136\u5012\u4e86\u4e0b\u53bb\uff01 \u663e\u89c1\u662f\u51f6\u591a\u5409\u5c11\uff01 \u201c\u9f99\u795e\u592a\u5b50\u201d\u5927\u5403\u4e00\u60ca\uff0c\u518d\u770b\u573a\u4e2d\uff0c\u5c55\u767d\u4f9d\u7136\u5982\u751f\u9f99\u6d3b\u864e\uff0c\u5728\u201c\u4e09\u715e\u201d\u3001\u201c\u4e09\u51f6\u201d(\u5c11 \u4e86\u4e00\u51f6)\u5168\u529b\u731b\u51b2\u4e4b\u4e0b\uff0c\u638c\u6253\u811a\u8e22\uff0c\u5f53\u8005\u62ab\u9761\uff01 \u201c\u9f99\u795e\u592a\u5b50\u201d\u8138\u8272\u4e00\u53d8\uff0c\u6c89\u58f0\u559d\u9053\uff1a\u201c\u5c71\u7a77\u6c34\u5c3d\u7591\u65e0\u8def\uff01\u201d \u8fd9\u65e0\u7591\u662f\u4e00\u9053\u50ac\u6218\u7b26\uff0c\u201c\u4e09\u715e\u201d\u3001\u201c\u4e09\u51f6\u201d\u51b2\u52bf\u66f4\u731b\uff01 \u5728\u8fd9\u79cd\u60c5\u5f62\u4e0b\uff0c\u5c55\u767d\u7eb5\u662f\u94dc\u6253\u94c1\u94f8\uff0c\u4e5f\u4e0d\u7981\u7d2f\u5f97\u6c57\u6d41\u64e6\u80cc\uff0c\u547c\u547c\u6c14\u5598\uff01 \u5c24\u5176\u4ed6\u53f3\u624b\u638c\u5fc3\u88ab\u767d\u53d1\u5a46\u5a46\u201c\u641c\u9b42\u6307\u201d\u6240\u4f24\uff0c\u6b64\u65f6\u5df2\u7ea2\u80bf\u597d\u9ad8\uff0c\u706b\u70e7\u4f3c\u7684\u75db\uff0c\u51e0\u4f7f\u4ed6\u65e0 \u6cd5\u51fa\u53f3\u638c\u5e94\u654c\uff01 \u8fd9\u65e0\u5f62\u4e2d\u4f7f\u4ed6\u7684\u529f\u529b\uff0c\u51cf\u4f4e\u4e86\u4e00\u534a\u5de6\u53f3\uff01 \u73b0\u5728\u201c\u9f99\u795e\u592a\u5b50\u201d\u4ee5\u6697\u8bed\u50ac\u6218\uff0c\u201c\u4e09\u715e\u201d\u3001\u201c\u4e09\u51f6\u201d\u51b2\u52bf\u66f4\u731b\uff0c\u6bcf\u4e00\u4eba\u5168\u529b\u51fb\u51fa\uff0c\u5747\u6709 \u6d77\u5578\u5c71\u5d29\u4e4b\u52bf\uff01 \u8fd9\u5df2\u4e0d\u77e5\u662f\u7b2c\u51e0\u4e2a\u5faa\u73af\u4e86\uff0c\u53ea\u89c1\u201c\u4e09\u715e\u201d\u3001\u201c\u4e09\u51f6\u201d\u516d\u5927\u7edd\u4e16\u9ad8\u624b\u524d\u8d74\u540e\u7ee7\uff0c\u8f6e\u756a\u731b \u51b2\uff0c\u98ce\u8d77\u4e91\u6d8c\uff0c\u7535\u8f6c\u661f\u98de\uff01\u53cc\u65b9\u638c\u529b\u51fb\u649e\u4e4b\u58f0\uff0c\u8f70\u8f70\u4e0d\u7edd\uff01 \u5c55\u767d\u8fd0\u8db3\u5468\u8eab\u529f\u529b\uff0c\u63a5\u8fde\u9707\u98de\u201c\u9634\u5c71\u4e4b\u72fc\u201d\u53ca\u201c\u8471\u5cad\u4e4b\u9e70\u201d\u56e0\u7528\u529b\u8fc7\u731b\uff0c\u5185\u8151\u6c14\u8840\u4e00\u9635 \u7ffb\u6d8c\uff0c\u7ec8\u4e8e\u5fcd\u4e0d\u4f4f\uff0c\u5f20\u53e3\u55b7\u51fa\u4e00\u53e3\u9c9c\u8840\u7c73\uff01 \u767d\u53d1\u5a46\u5a46\u770b\u51fa\u4fbf\u5b9c\uff0c\u9661\u7136\u602a\u5578\u4e00\u58f0\uff0c\u53f3\u624b\u201c\u641c\u9b42\u6307\u201d\u5de6\u624b\u201c\u5288\u7a7a\u638c\u201d\u4e24\u5927\u6740\u62db\u540c\u65f6\u51fa \u624b\uff0c\u731b\u5411\u5c55\u767d\u6251\u6765\uff01 \u5c55\u767d\u4e0d\u6562\u518d\u786c\u63a5\u5979\u7684\u201c\u641c\u9b44\u6307\u201d\uff0c\u4e0a\u8eab\u4e00\u6d4b\uff0c\u201c\u5693\u201d\u7684\u4e00\u58f0\uff0c\u4e00\u822c\u52b2\u98ce\uff0c\u64e6\u8033\u800c\u8fc7\uff01 \u5c55\u767d\u6697\u9053\u4e00\u58f0\uff1a\u201c\u597d\u9669\uff01\u201d \u4f46\u767d\u53d1\u5a46\u5a46\u7684\u5de6\u638c\uff0c\u8fce\u9762\u62cd\u81f3\uff0c\u5c55\u767d\u5374\u518d\u4e5f\u65e0\u6cd5\u8eb2\u8fc7\uff0c\u767e\u5fd9\u4e2d\u4e3e\u5de6\u638c\u4e00\u5c01\uff0c\u201c\u562d\u201d\u7684\u4e00 \u58f0\u66b4\u54cd\uff0c\u767d\u53d1\u5a46\u5a46\u88ab\u9707\u9000\u4e94\u6b65\uff01 \u5c55\u767d\u4e0a\u8eab\u4e5f\u4e00\u9635\u6447\u6643\uff01 \u7a81\u542c\u8eab\u540e\u201c\u5471\uff01\u5471\uff01\u201d\u4e24\u58f0\u6025\u557c\uff0c\u6709\u5982\u6012\u86d9\u54c0\u9e23\uff0c\u8ddf\u7740\u4e24\u80a1\u52b2\u98ce\uff0c\u4ece\u8eab\u540e\u649e\u5230\uff01 \u8fd9\u6b21\u201c\u4f5b\u5370\u6cd5\u5e08\u201d\u4ece\u8eab\u540e\u62fc\u5168\u529b\u4e00\u51fb\uff0c\u5374\u662f\u7206\u51fa\u51b7\u95e8\uff01 \u8bf4\u662f\u6697\u88ad\u4e5f\u597d\uff0c\u8bf4\u662f\u4ed6\u6293\u4f4f\u673a\u4f1a\u4e5f\u597d\uff0c\u53cd\u6b63\u8fd9\u4e00\u51fb\u6765\u5f97\u4e0d\u857e\uff01 \u56e0\u6253\u4e86\u534a\u5929\uff0c\u5de6\u3001\u53f3\u3001\u540e\u4e09\u65b9\u654c\u4eba\uff0c\u5747\u662f\u51fa\u62db\u52a9\u653b\uff0c\u5e76\u4e0d\u65bd\u4ee5\u5168\u529b\uff0c\u53ea\u6709\u8fce\u9762\u6765\u654c\u662f\u81f4 \u547d\u4e00\u51fb\uff01 \u8fd9\u8eab\u540e\u4e00\u62db\uff0c\u53ef\u8bf4\u5b9e\u5728\u662f\u72ec\u51b7\u5b50\uff01 \u597d\u5c55\u767d\uff01\u5f53\u771f\u662f\u773c\u89c2\u56db\u8def\uff0c\u8033\u542c\u516b\u65b9\uff0c\u5c31\u5728\u529b\u654c\u4e03\u5927\u7edd\u4e16\u9ad8\u624b\u3001\u8eab\u8d1f\u91cd\u521b\u4e4b\u9645\uff0c\u5c24\u5176\u80fd \u4e34\u5371\u4e0d\u4e71\uff01 \u53ea\u89c1\u4ed6\u8eab\u5f62\u4e00\u65cb\uff0c\u5288\u9762\u4e00\u638c\uff0c\u5411\u201c\u4f5b\u5370\u6cd5\u5e08\u201d\u6253\u53bb\uff01\u201c\u8f70\uff01\u201d\u4e00\u58f0\u66b4\u54cd\uff0c\u201c\u4f5b\u5370\u6cd5\u5e08\u201d \u77ee\u80d6\u8eab\u6750\uff0c\u5c31\u5730\u4e00\u8def\u7ffb\u6eda\uff0c\u76f4\u88ab\u5c55\u81ea\u4e00\u638c\u5288\u51fa\u4e09\u6587\u8fdc\u8fd1\u3002 \u4f46\u5c55\u767d\u4e5f\u88ab\u8fd9\u53cd\u9707\u4e4b\u529b\uff0c\u9707\u5f97\u8ddf\u8dea\u540e\u9000\u4e86\u4e09\u6b65\uff0c\u8eab\u5f62\u6447\u4e86\u4e24\u6447\uff0c\u773c\u524d\u4e00\u9ed1\uff0c\u5f20\u53e3\u53c8\u55b7\u51fa \u4e00\u53e3\u9c9c\u8840\uff01 \u957f\u9aef\u8001\u4eba\u7a81\u7136\u5927\u8d50\u4e00\u58f0\uff1a\u201c\u5c0f\u5144\u5f1f\uff01\u518d\u63a5\u8001\u592b\u4e00\u638c\uff01\u201d \u66b4\u559d\u58f0\u4e2d\uff0c\u201c\u5927\u624b\u5370\u201d\u638c\u529f\u8fd0\u81f3\u5341\u8db3\uff0c\u624b\u638c\u5927\u5982\u8f66\u8f6e\uff0c\u52b2\u5578\u7834\u7a7a\uff0c\u72b9\u5982\u6cf0\u5c71\u538b\u9876\u4e00\u822c\uff0c \u731b\u5411\u5c55\u767d\u5f53\u9876\u62cd\u4e0b\uff01 \u8fd9\u4e00\u638c\u6765\u52bf\u592a\u731b\uff0c\u5de6\u3001\u53f3\u3001\u540e\u4e09\u65b9\u53c8\u6709\u654c\u4eba\u963b\u8def\uff0c\u5c55\u767d\u4e00\u54ac\u7259\uff0c\u201c\u4f5b\u5149\u666e\u7167\u201d\uff0c\u4ee5\u5168\u8eab \u529f\u529b\u5411\u6765\u52bf\u8fce\u53bb\uff01 \u201c\u8f70\u9686\uff01\u201d\u5929\u5d29\u5730\u88c2\u4f3c\u7684\u4e00\u58f0\u5927\u9707\uff0c\u6c99\u98de\u77f3\u8d70\uff0c\u6811\u6298\u8349\u98de\uff01 \u957f\u9aef\u8001\u4eba\u9ad8\u5927\u8eab\u8eaf\u540e\u9000\u4e86\u4e94\u4e2a\u5927\u6b65\uff01 \u6ee1\u5934\u767d\u53d1\u98d8\u626c\uff0c\u6839\u6839\u957f\u9aef\u70b8\u7acb\uff0c\u8179\u5185\u771f\u6c14\u6d41\u7a9c\uff0c\u5f20\u53e3\u6ea2\u51fa\u4e00\u53e3\u9c9c\u8840\uff01 \u8fd9\u56de\u957f\u9aef\u8001\u4eba\u56e0\u7528\u771f\u529b\u8fc7\u5de8\uff0c\u88ab\u5c55\u767d\u6f5c\u5b58\u4f53\u5185\u7684\u5148\u5929\u7f61\u6c14\u9707\u4f24\uff01 \u5c55\u767d\u4e0a\u8eab\u4e00\u6643\uff0c\u5374\u5f3a\u5fcd\u4f4f\u4e00\u53e3\u9c9c\u8840\u672a\u55b7\u51fa\u6765\uff01 \u4f46\u4ed6\u4ed3\u5352\u8fce\u654c\uff0c\u5374\u5fd8\u4e86\u81ea\u5df1\u53f3\u638c\u7684\u4f24\u52bf\uff0c\u6b64\u65f6\u88ab\u957f\u9aef\u8001\u4eba\u638c\u529b\u4e00\u9707\uff0c\u53ea\u75db\u5f97\u4ed6\u54ac\u7259\u54a7 \u5634\uff0c\u51b7\u6c57\u76f4\u6d41\uff01 \u201c\u8471\u5cad\u4e4b\u9e70\u201d\u3001\u201c\u9634\u5c71\u4e4b\u72fc\u201d\u89d1\u51fa\u4e86\u597d\u673a\u4f1a\uff0c\u201c\u4e5d\u626c\u9b54\u706b\u529f\u201d\u3001\u201c\u638c\u5203\u5207\u6728\u201d\u4e24\u79cd\u60ca\u4e16 \u9a87\u4fd7\u7684\u786c\u6740\u624b\uff0c\u4e00\u5de6\u4e00\u53f3\uff0c\u540c\u65f6\u6251\u4e0a\uff01 \u5c55\u767d\u53cc\u773c\u53d1\u9ed1\uff0c\u5f3a\u5fcd\u4f4f\u4e00\u95e8\u9c9c\u8840\u672b\u55b7\u51fa\u53e3\u6765\uff0c\u660f\u6726\u4e2d\u53c8\u89c1\u4e24\u80a1\u5de8\u98ce\u822c\u7684\u638c\u529b\uff0c\u5de6\u53f3\u6251 \u81f3\uff0c\u66b4\u559d\u4e00\u58f0\uff0c\u201c\u626b\u6e05\u5996\u6c1b\u201d\u500f\u544a\u51fa\u624b\uff01 \u201c\u8471\u5cad\u4e4b\u9e70\u201d\u8fce\u638c\u9000\u51fa\u5341\u4e2a\u5927\u6b65\uff0c\u201c\u5361\u5693\u201d\u4e00\u58f0\uff0c\u201c\u9634\u5c71\u4e4b\u72fc\u201d\u7684\u638c\u5203\u5207\u6728\uff0c\u5374\u6309\u5728\u4e86 \u5c55\u767d\u5de6\u81c2\u4e4b\u4e0a\uff01 \u5c55\u767d\u8eab\u5f62\u4e00\u8def\u8e09\u8dc4\uff0c\u6447\u6447\u6b32\u5012\uff0c\u5f20\u53e3\u55b7\u51fa\u4e00\u53e3\u8840\u7bad\uff01 \u4e8c\u62f3\u96be\u654c\u56db\u624b\uff0c\u597d\u6c49\u67b6\u4e0d\u4f4f\u4eba\u591a\uff01\u8c01\u8bf4\u5f3a\u4eba\u4e0d\u4f1a\u5012\uff1f \u4f46\u5c55\u767d\u786c\u662f\u6ca1\u5012\u4e0b\uff01 \u201c\u9634\u5c71\u4e4b\u72fc\u201d\u5f97\u610f\u5df2\u6781\uff0c\u54c8\u54c8\u4e00\u9635\u72c2\u7b11\uff0c\u66b4\u53f1\u9053\uff1a\u201c\u8eba\u4e0b\uff01\u201d \u66b4\u53f1\u58f0\u4e2d\uff0c\u201c\u638c\u5203\u5207\u6728\u201d\u7b2c\u4e8c\u638c\u53c8\u5411\u5c55\u767d\u780d\u81f3\uff01 \u4e0d\u5c11\u4eba\u51fa\u58f0\u60ca\u547c\uff01 \u8fd9\u79cd\u60ca\u5fc3\u52a8\u9b44\u7684\u6253\u6597\uff0c\u7b80\u76f4\u662f\u524d\u6240\u672a\u89c1\uff0c\u89c2\u6218\u7fa4\u96c4\u90fd\u60ca\u5446\u4e86\uff0c\u8fd9\u65f6\u624d\u662f\u7b2c\u4e00\u6b21\u53d1\u51fa\u58f0 \u6765\uff01 \u5173\u5fc3\u5c55\u767d\u5b89\u5371\u7684\u4eba\uff0c\u66f4\u662f\u4e00\u9f50\u5927\u60ca\u5931\u8272\u2026\u2026 \u5c31\u5728\u8fd9\u5343\u94a7\u4e00\u53d1\u3001\u6781\u4e3a\u5371\u9669\u7684\u5883\u5730\uff0c\u5c55\u767d\u7a81\u7136\u661f\u76ee\u5706\u7741\uff0c\u5bf9\u201c\u9634\u5c71\u4e4b\u72fc\u201d\u5982\u5203\u7684\u638c\u950b\uff0c \u4e0d\u4f46\u4e0d\u8eb2\uff0c\u53cd\u800c\u7528\u81c2\u5f2f\u8098\uff0c\u731b\u5411\u201c\u9634\u5c71\u4e4b\u72fc\u201d\u5fc3\u7a9d\u4e0a\u649e\u53bb\uff01 \u201c\u9634\u5c71\u4e4b\u72fc\u201d\u4e07\u4e5f\u60f3\u4e0d\u5230\uff0c\u5c55\u767d\u91cd\u4f24\u4e4b\u4e0b\uff0c\u8fd8\u4f1a\u6709\u8fd9\u4e00\u624b\uff01 \u800c\u8fd9\u4e00\u624b\uff0c\u5374\u6b63\u662f\u5927\u64d2\u62ff\u624b\u4e2d\u7684\u4e00\u5f0f\uff0c\u201c\u6a2a\u4e91\u65ad\u5cf0\u201d\uff01 \u4e3a\u8fd1\u8eab\u640f\u6597\u7684\u9669\u62db\uff01 \u201c\u9634\u5c71\u4e4b\u72fc\u201d\u518d\u60f3\u8eb2\u53ef\u5c31\u6765\u4e0d\u53ca\u4e86\uff01 \u53ea\u542c\u201c\u5543\u201d\u7684\u4e00\u58f0\uff0c\u90a3\u4e00\u8098\u6b63\u649e\u5728\u201c\u9634\u5c71\u4e4b\u72fc\u201d\u5fc3\u7a9d\u4e4b\u4e0a\uff01 \u201c\u9634\u5c71\u4e4b\u72fc\u201d\u60e8\u55e5\u4e00\u58f0\uff0c\u9c9c\u8840\u72c2\u55b7\uff0c\u7ffb\u8eab\u683d\u5012\uff01 \u8fd9\u6b21\u5012\u5728\u5730\u4e0b\uff0c\u4ed6\u5374\u518d\u722c\u4e0d\u8d77\u6765\u4e86\uff01 \u201c\u9f99\u795e\u592a\u5b50\u201d\u614c\u5fd9\u8d76\u81f3\uff0c\u8fde\u5728\u201c\u9634\u5c71\u4e4b\u72fc\u201d\u53e3\u4e2d\u585e\u4e0b\u6570\u9897\u836f\u4e38\uff01 \u4f46\u201c\u9634\u5c71\u4e4b\u72fc\u201d\u8fde\u5f20\u5634\u90fd\u4e0d\u4f1a\u5f20\u5634\u4e86\uff01 \u201c\u9f99\u795e\u592a\u5b50\u201d\u7528\u624b\u4e00\u6478\u201c\u9634\u5c71\u4e4b\u72fc\u201d\u8109\u95e8\uff0c\u624d\u77e5\u5fc3\u8109\u649e\u65ad\uff0c\u65e9\u5df2\u6b7b\u53bb\u591a\u65f6\uff01 \u201c\u9f99\u795e\u592a\u5b50\u201d\u5927\u6012\uff0c\u9ad8\u559d\u4e00\u53e5\uff1a\u201c\u67f3\u6697\u82b1\u660e\u53c8\u4e00\u6751\uff01\u201d \u5c55\u767d\u53ea\u611f\u5de6\u81c2\u75db\u5165\u9aa8\u9ad3\uff0c\u4e3e\u8d77\u4e00\u770b\uff0c\u624d\u77e5\u5de6\u81c2\u5df2\u88ab\u201c\u9634\u5c71\u4e4b\u72fc\u201d\u638c\u5203\u5207\u6728\u529f\u592b\u628a\u81c2\u9aa8\u5207 \u65ad\uff01 \u5c55\u767d\u53f3\u624b\u8d1f\u4f24\uff0c\u5982\u4eca\u5de6\u81c2\u53c8\u65ad\uff0c\u5fc3\u4e2d\u4e00\u51c9\uff0c\u6697\u9053\uff1a\u201c\u8fd9\u56de\uff0c\u53ef\u771f\u5b8c\u4e86\u201c\u4ed6\u4e00\u4e2a\u5ff5\u5934\u672b\u8f6c \u5b8c\uff0c\u201c\u9f99\u795e\u592a\u5b50\u201d\u6700\u7d27\u6025\u7684\u50ac\u6218\u7b26\u53c8\u4e0b\uff0c\u201c\u4f5b\u5370\u6cd5\u5e08\u201d\u201c\u5471\u5471\u201d\u602a\u557c\u4e24\u58f0\uff0c\u201c\u86e4\u87c6\u7f61\u6c14\u201d\u638c \u529f\uff0c\u9996\u5148\u5411\u5c55\u767d\u63a8\u6765\uff01 \u5c55\u767d\u53cc\u624b\u5747\u4f24\uff0c\u89c1\u201c\u4f5b\u5370\u6cd5\u5e08\u201d\u53c8\u5411\u4ed6\u51b2\u6765\uff0c\u4ed6\u4e00\u54ac\u7259\uff0c\u201c\u5343\u5e7b\u98d8\u9999\u6b65\u201d\uff0c\u8dfa\u8d77\u8eab\u5f62\u5982 \u4e00\u80a1\u8f7b\u70df\u822c\u4e00\u95ea\uff0c\u4e24\u80a1\u52b2\u98ce\uff0c\u64e6\u8db3\u800c\u8fc7\uff01 \u5c55\u767d\u81ea\u77e5\u4e0d\u4e0b\u72e0\u5fc3\u662f\u4e0d\u884c\u4e86\uff01 \u6b63\u662f\u201c\u4eba\u65e0\u5bb3\u864e\u5fc3\uff0c\u864e\u6709\u4f24\u4eba\u610f\u201d\uff01\u4f60\u4e0d\u6740\u4ed6\uff0c\u4ed6\u5c31\u8981\u6740\u4f60\uff01 \u56e0\u6b64\uff0c\u4ed6\u518d\u4e0d\u7559\u60c5\uff0c\u9669\u4e0a\u52a0\u9669\u5730\u4ee5\u6b65\u6cd5\u8eb2\u8fc7\u201c\u4f5b\u5370\u6cd5\u5e08\u201d\u81f4\u547d\u7684\u4e00\u51fb\uff0c\u98de\u8d77\u2014\u811a\uff0c\u76f4\u5411 \u201c\u4f5b\u5370\u6cd5\u5e08\u201d\u5149\u5934\u4e0a\u8e22\u53bb\uff01 \u201c\u6ce2\u201d\u7684\u4e00\u58f0\u8106\u54cd\uff0c\u8111\u6d46\u56db\u6e85\uff0c\u201c\u4f5b\u5370\u6cd5\u5e08\u201d\u5149\u5934\u5f00\u4e86\u82b1\uff0c\u7acb\u5373\u5012\u5730\u6b7b\u53bb\uff01 \u201c\u4f5b\u5370\u6cd5\u5e08\u201d\u7eb5\u6a2a\u4e00\u751f\uff0c\u6740\u4eba\u65e0\u7b97\uff0c\u60f3\u4e0d\u5230\u4eca\u591c\u6b7b\u5728\u5c55\u767d\u811a\u4e0b\uff01 \u201c\u4e09\u715e\u201d\u3001\u201c\u56db\u51f6\u201d\u90fd\u662f\u529f\u9ad8\u76d6\u4e16\u7684\u9ad8\u624b\uff0c\u5176\u4e2d\u4efb\u4f55\u4e00\u4f4d\uff0c\u90fd\u53ef\u6210\u4e3a\u72ec\u9738\u4e00\u65b9\u7684\u5de8\u5934\uff01 \u6ca1\u60f3\u5230\u4e03\u5927\u7edd\u4e16\u9ad8\u624b\uff0c\u5408\u6218\u5c55\u767d\u4e00\u4eba\uff0c\u7adf\u63a5\u8fde\u6b7b\u4e86\u4e00\u715e\u4e00\u51f6\uff0c\u53e6\u6709\u4e00\u51f6\u4e5f\u8d1f\u4e86\u91cd\u4f24\uff0c\u722c \u4e0d\u8d77\u6765\u4e86\uff01 \u4e03\u5927\u9ad8\u624b\u53bb\u5176\u4e09\uff0c\u53ea\u5269\u4e0b\u4e86\u201c\u4e8c\u715e\u201d\u3001\u201c\u4e8c\u51f6\u201d\uff01 \u4f46\u8fd9\u201c\u4e8c\u715e\u201d\u3001\u201c\u4e8c\u51f6\u201d\u66f4\u52a0\u66b4\u6012\uff01 \u6bcf\u4e2a\u4eba\u90fd\u662f\u62fc\u547d\u5730\u731b\u51b2\u731b\u6253\uff01 \u5c55\u8272\u4e5f\u8eab\u8d1f\u91cd\u4f24\uff01 \u4ed6\u53f3\u638c\u7ea2\u80bf\u597d\u9ad8\uff0c\u75db\u5f7b\u5fc3\u80ba\uff0c\u51e0\u4e4e\u65e0\u6cd5\u51fa\u638c\uff0c\u5de6\u81c2\u81c2\u9aa8\u6298\u65ad\uff0c\u5df2\u662f\u6839\u672c\u4e0d\u80fd\u4f7f\u7528\u4e86\uff0c\u5185 \u8151\u66f4\u662f\u6c14\u8840\u7ffb\u6d8c\uff0c\u4e0d\u4f4f\u53e3\u5730\u55b7\u51fa\u9c9c\u8840\uff0c\u53cc\u773c\u53d1\u9ed1\uff0c\u6447\u6447\u6b32\u5012\uff01 \u4f46\u4ed6\u786c\u662f\u672a\u5012\uff01 \u811a\u6b65\u8e09\u8dc4\uff0c\u4ee5\u5947\u5e7b\u83ab\u6d4b\u7684\u8eab\u6cd5\u548c\u6b65\u6cd5\uff0c\u4e0e\u72b6\u5982\u75af\u864e\u4f3c\u7684\u56db\u5927\u7edd\u4e16\u9ad8\u624b\u558b\u8840\u82e6\u6218\uff01 \u800c\u4e14\uff0c\u4ed6\u4ee5\u53cc\u8098\uff0c\u53cc\u811a\u51fa\u62db\uff0c\u4ecd\u7136\u662f\u6709\u653b\u6709\u5b88\uff01 \u4ed6\u662f\u4e00\u4e2a\u91cc\u521b\u72b9\u6218\u7684\u5de8\u4eba\uff01 \u56db\u5468\u89c2\u6218\u7fa4\u96c4\uff0c\u867d\u7136\u5927\u591a\u662f\u8001\u6c5f\u6e56\uff0c\u5200\u5934\u62b5\u8840\uff0c\u6cb9\u9505\u91cc\u6253\u6eda\uff0c\u5927\u6c5f\u5927\u6d6a\u7ecf\u8fc7\u4e0d\u5c11\uff0c\u4f46\u4e5f \u4ece\u672a\u89c1\u8fc7\u5982\u6b64\u60e8\u70c8\u7684\u6253\u6597\uff01 \u5c55\u767d\u8d1f\u4f24\u7684\u60c5\u5f62\uff0c\u7fa4\u96c4\u4e0d\u662f\u6ca1\u6709\u770b\u51fa\u6765\uff0c\u4f46\u89c1\u6b64\u60e8\u70c8\u7684\u640f\u6597\uff0c\u7adf\u65e0\u4eba\u6562\u63d2\u624b\u4e0a\u524d\uff01 \u518d\u8005\uff0c\u4e5f\u8bb8\u662f\u57fa\u4e8e\u5fae\u5999\u7684\u5fc3\u7406\uff0c\u7fa4\u96c4\u90fd\u9ed8\u7136\u5982\u4e00\u6bb5\u5446\u6728\u5934\uff0c\u8896\u624b\u4f5c\u58c1\u4e0a\u89c2\uff01 \u5f53\u7136\uff0c\u65c1\u8fb9\u201c\u5357\u6d77\u9f99\u5973\u201d\u4e0e\u201c\u9f99\u795e\u592a\u5b50\u201d\u662f\u4e00\u5927\u5413\u963b\u529b\u91cf\uff0c\u53ea\u8981\u8c01\u4e00\u4e0a\u524d\uff0c\u4ed6\u4e8c\u4eba\u7acb\u523b \u51fa\u624b\u62e6\u51fb\uff0c\u770b\u8d77\u6765\uff0c\u201c\u9f99\u795e\u592a\u5b50\u201d\u5144\u59b9\u662f\u6709\u5fc3\u4f7f\u95e8\u4e0b\u9ad8\u624b\uff0c\u628a\u5c55\u767d\u6bc1\u5728\u5f53\u573a\uff01 \u6b64\u671f\u95f4\u53ea\u6709\u6155\u5bb9\u7ea2\u3001\u201c\u592a\u767d\u53cc\u9038\u201d\u3001\u8339\u8001\u9556\u5934\uff0c\u6570\u6b21\u4e0a\u524d\u60f3\u52a9\u5c55\u81ea\uff0c\u4f46\u5747\u88ab\u201c\u5357\u6d77\u9f99 \u5973\u201d\u51fb\u9000\uff01 \u5a49\u513f\u7ecf\u8fc7\u96f7\u5927\u53d4\u4e00\u9635\u63a8\u5bab\u6d3b\u7a74\uff0c\u4f24\u52bf\u5df2\u6108\uff0c\u8f6c\u773c\u770b\u5230\u5c55\u767d\u6d74\u8840\u594b\u6218\u7684\u82e6\u51b5\uff0c\u5fc3\u4e2d\u5927\u6025\uff0c \u5a07\u8eaf\u4e00\u633a\u800c\u8d77\uff0c\u5a07\u547c\u4e86\u4e00\u58f0\uff0c\u98de\u8eab\u5411\u573a\u5185\u6251\u53bb\uff01 \u4f46\u5979\u5c1a\u672a\u6251\u8fd1\u573a\u4e2d\uff0c\u201c\u5357\u6d77\u9f99\u5973\u201d\u5728\u4e00\u65c1\u5389\u559d\u4e86\u4e00\u58f0\uff1a\u201c\u5c0f\u4e2b\u5934\uff0c\u4f60\u7ed9\u6211\u5728\u65c1\u8fb9\u8001\u5b9e\u4e00 \u4f1a\u513f\uff01\u201d \u559d\u58f0\u4e2d\uff0c\u7ea4\u624b\u4e00\u626c\uff0c\u4e00\u822c\u52b2\u98ce\uff0c\u76f4\u5411\u5a49\u513f\u649e\u4e86\u8fc7\u53bb\uff01 \u5a49\u51e0\u60c5\u6025\u62fc\u547d\uff0c\u4e0d\u7763\u6765\u52bf\u51f6\u731b\uff0c\u4e3e\u638c\u4e00\u8fce\uff01 \u201c\u7830\uff01\u201d\u4e00\u58f0\u66b4\u9707\uff0c\u9707\u5f97\u5a49\u513f\u5a07\u8eaf\u8fde\u6643\uff0c\u4e00\u8fde\u9000\u4e86\u4e94\u516d\u6b65\uff0c\u624d\u62ff\u6869\u7ad9\u4f4f\uff01 \u5a49\u513f\u5927\u6012\uff0c\u8fd0\u8d77\u201c\u641c\u9b42\u6307\u201d\u795e\u529f\uff0c\u817e\u8eab\u5411\u201c\u5357\u6d77\u9f99\u5973\u201d\u5fc3\u6108\u91cd\u7a74\u523a\u53bb\uff01 \u6307\u98ce\u9510\u5578\uff0c\u7834\u7a7a\u800c\u81f3\uff01 \u5a49\u513f\u60c5\u6025\u62fc\u547d\uff0c\u8fd9\u4e00\u6307\u786e\u4e0d\u7b80\u5355\uff01 \u4f46\u201c\u5357\u6d77\u9f99\u5973\u201d\u4e43\u6570\u5341\u5e74\u524d\u628a\u4e2d\u539f\u6b66\u6797\u95f9\u5f97\u5929\u7ffb\u5730\u8986\u7684\u4e00\u4ee3\u5973\u9b54\u5934\u201c\u9b3c\u9762\u5a07\u5a03\u201d\u4eb2\u4f20\u5f1f \u5b50\uff0c\u6b66\u529f\u548c\u7fe0\u7fe0\u5728\u4f2f\u4ef2\u4e4b\u95f4\uff0c\u5b9e\u6bd4\u5a49\u513f\u8981\u9ad8\u51fa\u751a\u591a\uff0c\u5c31\u5728\u5a49\u513f\u6307\u98ce\u9510\u5578\u800c\u51fa\u4e4b\u9645\uff0c\u53ea\u542c\u5979\u5a07 \u559d\u4e00\u58f0\uff1a\u201c\u4e2b\u5934\uff0c\u4f60\u627e\u6b7b\uff01\u201d \u6b7b\u5b57\u51fa\u53e3\uff0c\u5a07\u8eaf\u5fae\u6643\uff0c\u4fa7\u8eab\u8eb2\u8fc7\u8fce\u80f8\u4e00\u6307\uff0c\u7fe0\u8896\u731b\u6325\uff0c\u5de7\u6253\u5a49\u513f\u53f3\u80a9\uff01 \u8fd9\u907f\u62db\u51fa\u62db\uff0c\u5f53\u771f\u662f\u5feb\u5f97\u5413\u4eba\uff01 \u522b\u5c0f\u77a7\u90a3\u4e00\u53ea\u8749\u7ffc\u8584\u7eb1\u7684\u6c34\u8896\uff0c\u5728\u201c\u5357\u6d77\u9f99\u5973\u201d\u4ee5\u201c\u6d41\u4e91\u953b\u8896\u201d\u529f\u592b\u65bd\u51fa\uff0c\u4e0d\u4e9a\u5982\u4e00\u67c4 \u94c1\u68d2\uff0c\u4e0d\u8981\u8bf4\u53eb\u5979\u6253\u4e0a\uff0c\u5c31\u662f\u88ab\u8896\u98ce\u626b\u4e2d\uff0c\u4e5f\u53ef\u628a\u5a49\u513f\u4e00\u534a\u9999\u80a9\u6253\u788e\uff01 \u5a49\u513f\u5fae\u5403\u4e00\u60ca\uff0c\u4e0d\u6562\u786c\u8fdb\uff0c\u53ea\u6709\u8e29\u8d77\u201c\u8e51\u7a7a\u5e7b\u5f71\u201d\u6b65\u6cd5\uff0c\u4e0a\u5de6\u6b65\uff0c\u7529\u53f3\u80a9\uff0c\u582a\u582a\u8eb2\u8fc7\u90a3 \u81f4\u547d\u7684\u4e00\u51fb\uff01 \u8c01\u77e5\u201c\u5357\u6d77\u9f99\u5973\u201d\u8fd9\u4e00\u62db\u201c\u8fce\u98ce\u7fe0\u8896\u201d\u5374\u662f\u865a\u62db\uff0c\u5c31\u5728\u5a49\u513f\u6643\u80a9\u4e00\u8eb2\u7684\u540c\u65f6\uff0c\u5979\u5634\u4e2d\u4f4e \u559d\u4e86\u4e00\u58f0\uff1a\u770b\uff01\u201d \u53f3\u638c\u95ea\u7535\u7a7f\u51fa\uff0c\u731b\u5411\u5a49\u513f\u9165\u80f8\u4e0a\u62cd\u4e2d\uff01 \u201c\u7830\uff01\u201d \u4e00\u58f0\u66b4\u54cd\uff0c\u8fd9\u4e00\u638c\u53c8\u628a\u5a49\u513f\u9707\u51fa\u4e86\u5341\u4e2a\u5927\u6b65\uff01 \u5a49\u513f\u7c89\u8138\u715e\u767d\uff0c\u79c0\u76ee\u542b\u6cea\uff0c\u81ea\u77e5\u662f\u65e0\u6cd5\u4e0a\u524d\u5e2e\u52a9\u5c55\u54e5\u54e5\uff01 \u518d\u770b\u5c55\u767d\uff0c\u5df2\u5230\u4e86\u6700\u5371\u6025\u5173\u5934\uff01 \u6b64\u65f6\u56f4\u653b\u4ed6\u7684\u4e03\u5927\u9ad8\u624b\u867d\u7136\u4e8c\u6b7b\u4e00\u4f24\uff0c\u4f46\u5269\u4e0b\u7684\u201c\u4e8c\u715e\u201d\u3001\u201c\u4e8c\u51f6\u201d\u653b\u52bf\u66f4\u89c1\u731b\u70c8\uff01 \u767d\u53d1\u5a46\u5a46\u7684\u201c\u641c\u9b42\u6307\u201d\u98d2\u98d2\u7834\u98ce\uff0c\u6307\u6307\u653b\u5411\u5c55\u767d\u8981\u5bb3\uff01 \u201c\u8471\u5cad\u4e4b\u9e70\u201d\u4e5d\u9633\u9b54\u706b\u529b\u201c\u67af\u9aa8\u638c\u201d\u65b9\u5c55\u5f00\u6765\uff0c\u70ed\u6d6a\u6eda\u6eda\uff0c\u715e\u662f\u96be\u5f53\uff01 \u201c\u592a\u4ed3\u4e4b\u9f20\u201d\u7f29\u7b4b\u529f\uff0c\u4e24\u6761\u957f\u81c2\uff0c\u72b9\u5982\u4e24\u6839\u94a2\u6761\uff0c\u62db\u62db\u5747\u53ef\u81f4\u547d\uff01 \u8fd9\u5176\u4e2d\u4ecd\u8981\u6570\u957f\u9aef\u8001\u4eba\u201c\u4ec7\u5982\u6d77\u201d\u7684\u201c\u5927\u624b\u5370\u201d\u6700\u4e3a\u5389\u5bb3\uff01 \u53ea\u89c1\u4ed6\u90a3\u5927\u5982\u8f66\u8f6e\u7684\u624b\u638c\uff0c\u6325\u821e\u8d77\u6765\uff0c\u52b2\u98ce\u6fc0\u8361\uff0c\u638c\u5f71\u906e\u5929\uff0c\u5b9e\u6709\u5c71\u5d29\u6d77\u5578\u3001\u5730\u88c2\u5929\u5f00 \u4e4b\u52bf\uff01 \u8fd9\u65f6\u5c55\u767d\u4f3c\u6709\u70b9\u660f\u8ff7\u4e4b\u72b6\uff0c\u5634\u89d2\u4e0a\u67d3\u6ee1\u4e86\u8840\u8ff9\uff0c\u4fca\u7f8e\u7684\u8138\u4e0a\u4e00\u7247\u60e8\u767d\uff01 \u4f46\u4ecd\u7136\u62fc\u547d\u529b\u6218\uff01 \u53ea\u89c1\u4ed6\u8eb2\u8fc7\u767d\u53d1\u5a46\u5a46\u2018\u641c\u9b42\u6307\u201d\uff0c\u9707\u98de\u201c\u592a\u4ed3\u4e4b\u9f20\u201d\uff0c\u6253\u9000\u201c\u8471\u5cad\u4e4b\u9e70\u201d\uff0c\u201c\u8f70\u201d\u7684\u4e00 \u58f0\u5de8\u9707\uff0c\u518d\u4e0e\u957f\u9aef\u8001\u4eba\u786c\u62fc\u4e86\u4e00\u638c\uff01 \u957f\u9aef\u8001\u4eba\u88ab\u9707\u5f97\u4ef0\u8eab\u540e\u9000\u4e86\u4e94\u6b65\uff0c\u51e0\u4e4e\u7ffb\u8eab\u8dcc\u5012\uff01 \u5c55\u767d\u8eab\u5f62\u4e71\u6643\uff0c\u8fde\u55b7\u6570\u53e3\u9c9c\u8840\uff01 \u98ce\u5578\u5c18\u98de\u4e4b\u4e2d\uff0c\u957f\u9aef\u8001\u4eba\u53cc\u76ee\u5389\u7741\uff0c\u4f3c\u662f\u7b49\u5f85\u7740\u5c55\u767d\u5012\u4e0b\uff01\u2026\u2026 \u4f46\u5c55\u767d\u786c\u662f\u672a\u5012\uff01 \u957f\u9aef\u8001\u4eba\u4e0d\u7531\u4e00\u7ad6\u62c7\u6307\uff0c\u54d1\u58f0\u8d5e\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff01\u4f60\u662f\u5929\u4e0b\u6b66\u6797\u7b2c\u4e00\u4eba\uff01\u201d \u5c55\u767d\u4ee5\u624b\u80cc\u62b9\u53bb\u5634\u89d2\u4e0a\u7684\u8840\u8ff9\uff0c\u9053\uff1a\u201c\u8001\u524d\u8f88\u8fc7\u5956\u4e86\uff01\u201d \u957f\u9aef\u8001\u4eba\u4e5f\u64e6\u4e86\u64e6\u957f\u9aef\u4e0a\u6e85\u6ee1\u4e86\u7684\u8840\u8ff9\uff0c\u9053\uff1a\u201c\u8001\u592b\u751f\u5e73\u4ece\u6765\u672a\u670d\u8fc7\u8c01\uff0c\u66f4\u4e0d\u4f1a\u5984\u8d5e\u654c \u4eba\uff0c\u4f46\uff0c\u8001\u592b\u4eca\u5929\u8981\u8bf4\u53e5\u516c\u9053\u8bdd\uff0c\u4ee5\u5c0f\u5144\u5f1f\u7684\u5c81\u6570\uff0c\u80fd\u6709\u5982\u6b64\u9ad8\u5f3a\u7684\u6b66\u529f\u9020\u8be3\uff0c\u4e0d\u4f46\u582a\u79f0\u5929 \u4e0b\u7b2c\u4e00\uff0c\u5373\u79f0\u4e3a\u2018\u6b66\u795e\u2019\uff0c\u4e5f\u5f53\u4e4b\u65e0\u6127\uff01\u201d \u5c55\u767d\u82e6\u7b11\u9053\uff1a\u201c\u8d8a\u53d1\u7684\u4e0d\u6562\u5f53\uff01\u201d \u957f\u9aef\u8001\u4eba\u53cc\u624b\u64e6\u7740\u957f\u9aef\u8a00\u9053\uff1a\u201c\u4e66\u4e91\uff1a\u2018\u624d\u667a\u80dc\u4e2a\u4eba\u8005\u4e3a\u82f1\uff0c\u80dc\u767e\u4eba\u8005\u4e3a\u96c4\uff0c\u5343\u4eba\u4e07\u4eba \u4e0d\u53ca\u4e3a\u5723\uff0c\u5723\u4eba\u4e5f\u4e0d\u53ef\u6bd4\u8005\u4e3a\u795e\u3002\u2019\u4e0d\u662f\u5439\u4e2d\uff0c\u2018\u4e09\u715e\u2019\u3001\u2018\u56db\u51f6\u2019\u4efb\u4f55\u2014\u4f4d\u90fd\u6562\u62cd\u80f8\u812f\u8bf4 \u2014\u58f0\u6709\u2018\u4e07\u592b\u83ab\u654c\u2019\u4e4b\u52c7\uff0c\u4f46\u5408\u6211\u4eec\u4e03\u4eba\u4e4b\u529b\uff0c\u6218\u4e0d\u8fc7\u5c0f\u5144\u5f1f\u2014\u4eba\uff0c\u5c0f\u5144\u5f1f\u4e0d\u662f\u2018\u795e\u2019\u662f\u4ec0 \u4e48\uff1f\u2026\u2026\u201d \u957f\u9aef\u8001\u4eba\u4f83\u4f83\u800c\u8c08\uff0c\u5927\u5927\u5730\u51b2\u6de1\u4e86\u51e0\u5206\u654c\u610f\uff01 \u5c31\u8fde\u72c2\u50b2\u65e0\u6bd4\u7684\u767d\u53d1\u5a46\u5a46\uff0c\u4ee5\u53ca\u6840\u50b2\u4e0d\u9a6f\u7684\u201c\u592a\u4ed3\u4e4b\u9f20\u201d\u4e0e\u201c\u8471\u5cad\u4e4b\u9e70\u201d\u8138\u4e0a\u4e5f\u83ab\u4e0d\u900f \u51fa\u94a6\u656c\u4e4b\u8272\u3002 \u201c\u5357\u6d77\u9f99\u5973\u201d\u4fcf\u76ee\u51dd\u89c6\u5c55\u767d\uff0c\u653e\u51fa\u5947\u5f02\u5149\u91c7\u2026\u3002\xb7\u201c\u9f99\u795e\u592a\u5b50\u201d\u89c1\u72b6\u5927\u6025\uff0c\u7ffb\u5f00\u8863\u895f\uff0c \u4ece\u8170\u5185\u638f\u51fa\u4e00\u4e2a\u91d1\u5149\u95ea\u95ea\u7684\u5c0f\u91d1\u9f99\u6765\uff0c\u9ad8\u4e3e\u8fc7\u9876\uff0c\u9ad8\u58f0\u5ba3\u9053\uff1a\u91d1\u9f99\u4ee4\u4e0b\uff01\u2018\u4e8c\u71ac\u2019\u3001\u2018\u4e8c \u51f6\u2019\u901f\u628a\u5c55\u59d3\u5c11\u5e74\u51fb\u6bd9\u5f53\u573a\uff0c\u5982\u6709\u5bbd\u7eb5\uff0c\u4e25\u60e9\u4e0d\u8d37\uff01\u201d \u957f\u9aef\u8001\u4eba\u8138\u8272\u4e00\u53d8\uff0c\u8f6c\u773c\u671b\u4e86\u671b\u201c\u9f99\u795e\u592a\u5b50\u201d\u9ad8\u4e3e\u8fc7\u9876\u7684\u201c\u91d1\u9f99\u4ee4\u201d\uff0c\u957f\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c \u53c8\u56de\u8fc7\u5934\u6765\u5bf9\u5c55\u767d\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff01\u51c6\u5907\u63a5\u62db\u5427\uff01\u770b\u6765\u6211\u4eec\u4e0d\u5206\u751f\u6b7b\uff0c\u662f\u65e0\u6cd5\u4f4f\u624b\u4e86\uff01\u201d \u8bf4\u7740\u529f\u8d2f\u53cc\u638c\uff0c\u90a3\u4e24\u53ea\u5927\u638c\u53c8\u731b\u5730\u6da8\u5927\u4e86\u6570\u500d\uff0c\u7f13\u7f13\u4e3e\u4e86\u8d77\u805a\u2026\u2026 \u5927\u5982\u8f66\u8f6e\u7684\u5de8\u638c\uff0c\u9752\u7b4b\u7ea0\u7ed3\uff0c\u597d\u4f3c\u76d8\u8e1e\u4e86\u65e0\u6570\u6761\u9752\u8272\u5c0f\u86c7\uff0c\u770b\u6765\u51f6\u6076\u5413\u4eba\u5df2\u6781\uff01 \u5a49\u513f\u7a81\u7136\u5c16\u53eb\u9053\uff1a\u6162\u7740\uff01\u2026\u2026\u201d \u7b2c\u516d\u5341\u56db\u7ae0\u3000\u5927\u4fa0\u4e4b\u6b7b>> \u53e4\u9f99\u300a\u5251\u5ba2\u884c\u300b \u7b2c\u516d\u5341\u56db\u7ae0\u3000\u5927\u4fa0\u4e4b\u6b7b \u957f\u9aef\u8001\u4eba\u5de8\u638c\u505c\u5728\u7a7a\u4e2d\uff0c\u51b7\u773c\u671b\u4e86\u671b\u5a49\u513f\uff0c\u4e00\u8bed\u4e0d\u53d1\uff01 \u5a49\u513f\u5374\u62a1\u6307\u70b9\u7740\u56db\u5468\u89c2\u6218\u7fa4\u96c4\u9053\uff1a\u201c\u4f60\u4eec\uff01\u9f0e\u9f0e\u5927\u540d\u7684\u2018\u6b66\u6797\u56db\u516c\u5b50\u2019\uff01\u5802\u5802\u4e5d\u5927\u95e8\u6d3e \u7684\u638c\u95e8\u4eba\uff01\u4fa0\u540d\u6ee1\u56db\u6d77\u7684\u2018\u7a77\u5bb6\u5e2e\u2019\uff01\u96be\u9053\u5c31\u5927\u7741\u767d\u773c\uff0c\u5e72\u770b\u7740\u7531\u5c55\u5c0f\u4fa0\u4e00\u4eba\uff0c\u4e3a\u4f60\u4eec\u5927\u5bb6 \u5356\u547d\u5417\uff1f\u201d \u8fd9\u8bdd\u8bf4\u5f97\u7fa4\u96c4\u673a\u4f36\u4f36\u6253\u4e86\u4e00\u4e2a\u51b7\u6218\uff01 \u4e2a\u4e2a\u9762\u73b0\u6127\u8272\uff01\u7565\u6709\u8840\u6027\u7684\u4eba\uff0c\u5df2\u8dc3\u8dc3\u6b32\u8bd5\uff0c\u4f46\u5c1a\u8fdf\u7591\u7740\uff0c\u672a\u6709\u4e00\u4eba\u51fa\u624b\uff01 \u5a49\u513f\u62a1\u773c\u770b\u89c1\u5c55\u767d\u9762\u767d\u5982\u7eb8\uff0c\u5634\u89d2\u8840\u8ff9\u6bb7\u7136\uff0c\u7ad9\u5728\u90a3\u91cc\u5df2\u662f\u6447\u6447\u6b32\u5012\uff0c\u800c\u957f\u9aef\u8001\u4eba\u5de8\u638c \u5c06\u6b32\u51fb\u4e0b\uff0c\u201c\u592a\u4ed3\u4e4b\u9f20\u201d\u3001\u201c\u8471\u5cad\u4e4b\u9e70\u201d\u53ca\u767d\u53d1\u5a46\u5a46\uff0c\u5747\u5df2\u84c4\u52bf\u5f85\u53d1\uff0c\u4e0d\u7531\u6025\u9053\uff1a\u4f60\u4eec\u90fd\u662f \u6015\u6b7b\u9b3c\uff01\u4f46\u613f\u5c0f\u4fa0\u82e5\u662f\u6218\u6b7b\u4e86\uff0c\u4f60\u4eec\u4e00\u4e2a\u4e5f\u522b\u60f3\u6d3b\uff01\u2026\u2026\u201d \u6240\u8c13\u201c\u9063\u5c06\u4e0d\u5982\u6fc0\u5c06\u201d\u3002\u5a49\u513f\u6b64\u8a00\u4e00\u51fa\uff0c\u7fa4\u96c4\u84e6\u7136\u4ece\u60ca\u9a87\u4e2d\u9192\u609f\u5404\u4eba\u7684\u5904\u5883\uff0c\u66f4\u660e\u767d\u4e86 \u5c55\u767d\u62fc\u6b7b\u529b\u6218\u539f\u662f\u4e3a\u4e86\u529b\u62d2\u201c\u5357\u6d77\u95e8\u201d\uff0c\u4ee5\u4fdd\u5b58\u4e2d\u539f\u6b66\u6797\u5404\u95e8\u6d3e\u7684\u547d\u8109\uff0c\u4e0d\u5f85\u5a49\u513f\u8bdd\u4e86\uff0c\u5df2 \u7eb7\u7eb7\u6b3a\u4e0a\u524d\u6765\u2026\u2026 \u201c\u9f99\u795e\u592a\u5b50\u201d\u4f30\u4e0d\u5230\u5a49\u513f\u4e24\u53e5\u8bdd\uff0c\u4fbf\u6fc0\u4f7f\u7fa4\u96c4\u51fa\u624b\u3002\u5f53\u65f6\u4e00\u58f0\u66b4\u559d\u9053\uff1a\u7ad9\u4f4f\uff01\u8c01\u8981\u80c6\u6562 \u4e0a\u524d\u4e00\u6b65\uff0c\u5fc5\u6740\u65e0\u8d66\uff01\u201d \u8fd9\u4e00\u58f0\u66b4\u559d\u58f0\u5982\u96f7\u9707\uff01 \u4e0a\u9762\u6b3a\u8fd1\u7684\u7fa4\u96c4\uff0c\u4e0d\u7531\u811a\u6b65\u4e00\u987f\u2026o\u2019\u201c\u6015\u6b7b\u9b3c\uff01\u9003\u547d\u53bb\u5427\uff01\u59d1\u5a18\u8ddf\u4ed6\u4eec\u62fc\u4e86\u2026\u2026\u201d \u5a49\u513f\u6012\u53f1\u58f0\u4e2d\uff0c\u5a07\u8eaf\u4e00\u987f\u800c\u8d77\uff0c\u5de6\u638c\u6a2a\u5288\u201c\u5357\u6d77\u9f99\u5973\u201d\u9762f\u4e86\uff0c\u53f3\u624b\u201c\u641c\u9b42\u6307\u201d\uff0c\u5c31\u8eab \u5f62\u75be\u6251\u4e4b\u52bf\uff0c\u731b\u5411\u201c\u9f99\u795e\u592a\u5b50\u201d\u70b9\u53bb\uff01 \u5a49\u51e0\u51b0\u96ea\u806a\u660e\uff0c\u5979\u5df2\u770b\u51fa\u201c\u9f99\u795e\u592a\u5b50\u201d\u624b\u6267\u201c\u91d1\u9f99\u4ee4\u201d\uff0c\u4e43\u662f\u53d1\u53f7\u65bd\u4ee4\u4e4b\u4eba\uff0c\u6545\u800c\u865a\u6253 \u201c\u5357\u6d77\u9f99\u5973\u201d\u4e00\u62db\uff0c\u800c\u4ee5\u5168\u8eab\u529f\u529b\u5411\u201c\u9f99\u795e\u592a\u5b50\u201d\u653b\u53bb\uff01 \u5979\u60f3\u4e00\u51fb\u594f\u529f\uff0c\u628a\u201c\u9f99\u795e\u592a\u5b50\u201d\u6bd9\u5728\u6307\u4e0b\uff0c\u5219\u201c\u86c7\u65e0\u5934\u4e0d\u884c\u201d\uff0c\u65b9\u53ef\u6551\u5c55\u54e5\u54e5\u4e00\u547d\uff01 \u4f46\u5979\u4f4e\u4f30\u4e86\u201c\u9f99\u795e\u592a\u5b50\u201d\u5144\u59b9\u7684\u6b66\u529f\uff0c\u5c82\u662f\u4e00\u62db\u4fbf\u53ef\u4ee4\u5979\u5f97\u624b\u7684\u5e73\u5eb8\u4e4b\u8f88\uff1f \u5c31\u5728\u5979\u8eab\u5f62\u521a\u4e00\u7eb5\u8d77\u7684\u77ac\u95f4\uff0c\u53ea\u542c\u201c\u5357\u6d77\u9f99\u5973\u201d\u5a07\u559d\u4e86\u4e00\u58f0\uff1a \u201c\u4e2b\u5934\uff01\u4f60\u5f53\u771f\u662f\u6d3b\u817b\u4e86\uff01\u201d \u58f0\u51fa\u62db\u81f3\uff0c\u4e09\u5c3a\u957f\u7684\u6c34\u8896\u201c\u5237\u201d\u7684\u2014\u3002\u58f0\uff0c\u6b63\u62bd\u5728\u5a49\u513f\u7684\u7ea4\u8170\u4e4b\u4e0a\uff01 \u8fd9\u4e00\u8896\u5b50\uff0c\u8db3\u628a\u5a49\u513f\u6253\u5f97\u6eda\u51fa\u4e00\u4e08\u5f00\u5916\uff0c\u7acb\u523b\u5012\u5730\u660f\u6b7b\u8fc7\u53bb\uff01 \u53ef\u662f\uff0c\u66b4\u559d\u58f0\u4e2d\uff0c\u7fa4\u96c4\u5df2\u7eb7\u7eb7\u6251\u4e86\u4ea1\u6765\uff01 \u201c\u9f99\u795e\u592a\u5b50\u201d\u4e00\u89c1\u60c5\u52bf\u4e0d\u597d\uff0c\u624b\u6267\u201c\u91d1\u9f99\u4ee4\u201d\u724c\uff0c\u6025\u53eb\u9053\uff1a\u201c\u4ec7\u516c\u516c\u542c\u7740\uff01\u59d3\u5c55\u7684\u5c0f\u5b50 \u662f\u4ea4\u7ed9\u4f60\u4e86\uff0c\u2018\u91d1\u9f99\u4ee4\u2019\u4e0b\u6709\u6b7b\u65e0\u56de\uff01\u5982\u679c\u59d3\u5c55\u7684\u5c0f\u5b50\u6d3b\u8fc7\u4eca\u591c\uff0c\u4e00\u5207\u552f\u4f60\u662f\u95ee\uff01\u201d \u559d\u7f62\uff0c\u6536\u8d77\u201c\u91d1\u9f99\u4ee4\u201d\uff0c\u4e0e\u201c\u5357\u6d77\u9f99\u5973\u201d\u5408\u529b\u62b5\u6321\u7fa4\u96c4D\u6b66\u529f\u8f83\u5dee\u3001\u8ddd\u79bb\u53c8\u8fd1\u7684\uff0c\u7ade\u88ab \u9707\u5f97\u8dcc\u6eda\u5728\u5730\uff01 \u4f17\u4eba\u60ca\u6115\u800c\u89c6\uff0c\u5f85\u5c18\u5b9a\u4eba\u663e\uff0c\u624d\u770b\u51fa\u90a3\u4e09\u58f0\u5de8\u9707\uff0c\u539f\u662f\u957f\u9aef\u8001\u4eba\u4ec7\u5982\u6d77\uff0c\u5728\u201c\u91d1\u9f99\u4ee4\u201d \u7684\u903c\u8feb\u4e4b\u4e0b\uff0c\u6c47\u96c6\u4e86\u767e\u6570\u5341\u5e74\u7684\u5185\u529f\u4fee\u4e3a\uff0c\u4ee5\u5168\u529b\u51fb\u51fa\u4e86\u4e09\u638c\uff01 \u800c\u5c55\u767d\u7adf\u628a\u90a3\u4e09\u638c\u786c\u63a5\u4e86\u4e0b\u6765\uff1a \u6b64\u65f6\uff0c\u4e8c\u4eba\u5bf9\u9762\u800c\u7acb\uff0c\u8138\u4e0a\u8868\u60c5\u6728\u7136\uff0c\u4f46\u5374\u5145\u6ee1\u4e86\u51dd\u91cd\u4e4b\u8272\uff0c\u56db\u76ee\u4e92\u7741\uff0c\u77aa\u89c6\u7740\u5bf9\u65b9\uff0c \u4f3c\u662f\u7b49\u5f85\u7740\u5bf9\u65b9\u5012\u4e0b\u2026\u2026 \u4f17\u4eba\u90fd\u770b\u5f97\u51fa\uff0c\u957f\u9aef\u8001\u4eba\u4e0e\u5c55\u767d\u93d6\u6597\u4e86\u4e00\u591c\uff0c\u60e7\u5df2\u8d1f\u4f24\u5410\u8840\uff0c\u65b9\u624d\u90a3\u4e09\u638c\u786c\u62fc\uff0c\u5fc5\u90fd\u5df2 \u7528\u51fa\u4e86\u5468\u8eab\u6700\u540e\u6240\u6709\u7684\u6b8b\u4f59\u771f\u529b\uff01 \u8fd9\u4e09\u638c\u5fc5\u5df2\u751f\u6b7b\u7acb\u5224\uff0c\u80dc\u8d1f\u7acb\u5206\uff01 \u4f46\u5728\u4e8c\u4eba\u5bf9\u7acb\u77a0\u89c6\u3001\u8fd8\u6ca1\u6709\u4e00\u65b9\u5012\u4e07\u4e4b\u65f6\uff0c\u4efb\u4f55\u4eba\u770b\u4e0d\u51fa\uff0c\u4ed6\u4e8c\u4eba\u7a76\u7adf\u662f\u8c01\u5360\u4e86\u4e0a\u98ce\uff1f \u5927\u5bb6\u7b49\u7740\u4e00\u65b9\u5012\u4e0b\u53bb\uff01 \u8fd9\u6837\u7684\u7b49\u5f85\uff0c\u4e00\u5239\u90a3\u7b49\u4e8e\u4e00\u5e74\u3001\u5341\u4e2d\u4e4b\u4e45\uff01 \u7ec8\u4e8e\u2014\u2014\u5c55\u767d\u8eab\u5f62\u5fae\u6643\u4e86\u6643\u2026\u2026 \u957f\u9aef\u8001\u4eba\u6446\u4e86\u6446\u2026\u2026 \u201c\u8f70\u901a\uff01\u201d \u72b9\u5982\u5012\u4e86\u5ea7\u5c71\uff01 \u4f17\u4eba\u9f50\u58f0\u53d1\u51fa\u60ca\u564e\uff01 \u660e\u663e\u5730\u542c\u5f97\u51fa\u6765\uff0c\u60ca\u566b\u58f0\u6709\u7684\u9707\u60ca\uff0c\u6709\u7684\u6b23\u559c\uff01 \u539f\u6765\u5148\u5012\u4e0b\u7684\u7adf\u662f\u957f\u9aef\u8001\u4eba\u4ec7\u5982\u6d77\uff01 \u5c55\u767d\u6b64\u65f6\uff0c\u811a\u6b65\u624d\u8e09\u8dc4\u4e86\u4e24\u6b65\uff0c\u5f20\u53e3\u53c8\u6ea2\u51fa\u6ee1\u5634\u9c9c\u8840\u4ed6\u6295\u7ed9\u5012\u5728\u811a\u4e0b\u7684\u957f\u9aef\u8001\u4eba\u60cb\u60dc\u7684 \u4e00\u77a5\uff0c\u5583\u5583\u81ea\u8bed\uff1a \u201c\u4f46\u613f\u6211\u6ca1\u6709\u6740\u6b7b\u4f60\u2014\u2014\u8001\u4eba\u5bb6\u2026\u2026\u201d \u8bf4\u7f62\uff0c\u4ed6\u773c\u91cc\u7ade\u6eda\u52a8\u51fa\u7c92\u7c92\u5982\u73cd\u73e0\u7684\u773c\u6cea\uff0c\u8fd9\u624d\u6389\u8f6c\u5934\u6765\uff0c\u811a\u6b65\u8e09\u8dc4\u5730\u8d70\u53bb\uff01 \u604b\u4eba\u3001\u672a\u5a5a\u59bb\u3001\u670b\u53cb\u3001\u77e5\u5df1\u3001\u654c\u4eba\u3001\u4ec7\u5bb6\uff0c\u751a\u81f3\u8fde\u96f7\u5927\u53d4\uff0c\u4ed6\u4e0d\u770b\u4efb\u4f55\u4eba\u4e00\u773c\uff0c\u4e00\u76f4\u5411 \u524d\u8d70\u53bb\uff01 \u4ed6\u4f4e\u7740\u5934\uff0c\u811a\u6b65\u8e09\u8dc4\uff0c\u8d70\u81f3\u4efb\u4f55\u4eba\u8eab\u524d\uff0c\u90fd\u8f7b\u8f7b\u5730\u629a\u4e00\u4e0b\u90a3\u4eba\u7684\u80a9\u8180\uff0c\u4f46\u5374\u4e00\u8a00\u4e0d\u53d1\uff01 \u4efb\u4f55\u4eba\u770b\u5f97\u51fa\uff0c\u663e\u89c1\u8fd9\u4e00\u573a\u6beb\u65e0\u610f\u4e49\u7684\u8840\u8165\u60e8\u6740\uff0c\u4f24\u900f\u4e86\u8fd9\u4f4d\u5fe0\u539a\u8bda\u5b9e\u3001\u5fc3\u5730\u5149\u660e\u7684\u5c11 \u5e74\u4fa0\u58eb\u7684\u5fc3\uff01 \u4f17\u4eba\u4e00\u9f50\u6728\u7136\u77a0\u89c6\u7740\u4ed6\u9ed8\u7136\u79bb\u53bb\uff01 \u7a81\u7136\u2014\u2014\u4e00\u58f0\u60b2\u60e8\u7684\u54ed\u53f7\uff0c\u5212\u7834\u6c89\u9ed8\u7684\u7a7a\u6c14\uff01 \u8fd9\u60b2\u53f7\u4e4b\u60e8\uff0c\u72b9\u5982\u675c\u9e43\u6ce3\u8840\uff0c\u6df1\u95fa\u65ad\u80a0\uff0c\u95fb\u4e4b\u4ee4\u4eba\u9f3b\u9178\uff01 \u4f17\u4eba\u6115\u7136\u60ca\u89c6\uff01 \u539f\u6765\u662f\u767d\u53d1\u82cd\u82cd\u7684\u767d\u53d1\u5a46\u5a46\u51b7\u8273\u7ea2\uff01 \u5979\u7a81\u5730\u6251\u81f3\u957f\u9aef\u8001\u4eba\u8eab\u8fb9\uff0c\u4f38\u51fa\u6296\u98a4\u7684\u53cc\u624b\uff0c\u4e00\u6478\u957f\u9aef\u8001\u4eba\u7684\u5fc3\u7a9d\uff01 \u89e6\u624b\u51b0\u51b7\uff0c\u8fd9\u6b66\u529f\u76d6\u4e16\uff0c\u7eb5\u6a2a\u4e00\u751f\u7684\u957f\u9aef\u8001\u4eba\uff0c\u5fc3\u8109\u5df2\u7edd\uff0c\u65e9\u5df3\u547d\u7edd\u591a\u65f6\uff01 \u767d\u53d1\u5a46\u5a46\u5fc3\u5982\u5200\u6405\uff01 \u8fd9\u4e00\u5239\u90a3\uff0c\u65f6\u95f4\u9759\u6b62\u4e86\uff01 \u5979\u60f3\u8d77\u4e86\u81ea\u5df1\u5c11\u5973\u65f6\u4ee3\uff0c\u7eee\u5e74\u7389\u8c8c\uff0c\u6b66\u529f\u9ad8\u5f3a\uff0c\u7a81\u7136\u9047\u4e0a\u4e86\u6b66\u529f\u6bd4\u81ea\u5df1\u66f4\u9ad8\u7684\u4e00\u4e2a\u7f8e\u5c11 \u5e74\u4ec7\u5982\u6d77\u2014\u2014\u5c31\u662f\u73b0\u5728\u9648\u5c38\u5730\u4e0a\u7684\u957f\u9aef\u8001\u4eba\uff01 \u5979\u4e0e\u4ed6\u4e00\u89c1\u949f\u60c5\uff0c\u4e92\u76f8\u7231\u6155\uff0c\u4e92\u8ba2\u7ec8\u8eab\uff0c\u65b0\u5a5a\u71d5\u5c14\u7684\u6b22\u4e50\uff1b\u4ed6\u8d1f\u6709\u4e00\u8eab\u8840\u6d77\u6df1\u4ec7\u2014\u2014\u5426 \u5219\uff0c\u4ed6\u600e\u4f1a\u8d77\u90a3\u4e48\u4e2a\u602a\u540d\u201c\u4ec7\u5982\u6d77\u201d\u2014\u2014\u5979\u52a9\u4ed6\u96ea\u6068\uff0c\u5feb\u610f\u6069\u4ec7\uff0c\u7136\u540e\u4e8c\u4eba\u5e76\u9053\u6c5f\u6e56\uff0c\u6e38\u904d \u4e86\u56fd\u5185\u540d\u5c71\u5927\u5ddd\uff0c\u8e2a\u8ff9\u904d\u56db\u6d77\uff0c\u90ce\u624d\u5973\u8c8c\uff0c\u6b66\u529f\u813e\u89c6\u5b87\u5185\uff0c\u7fa1\u715e\u4e86\u5f53\u65f6\u591a\u5c11\u9752\u5e74\u7537\u5973\uff1f \u4ed6\u4e8c\u4eba\u6bd4\u7ffc\u53cc\u98de\uff0c\u671d\u5915\u4e0d\u79bb\uff0c\u6069\u7231\u903e\u6052\uff0c\u6570\u5341\u5e74\u5982\u4e00\u65e5\uff0c\u767d\u9996\u5055\u8001\uff0c\u53ea\u7fa1\u9e33\u9e2f\u4e0d\u7fa1\u4ed9\uff01 \u4f46\u4e8c\u4eba\u6b66\u529f\u7edd\u9ad8\uff0c\u773c\u7a7a\u56db\u6d77\uff0c\u5c11\u5e74\u5f97\u610f\uff0c\u96be\u514d\u884c\u4e8b\u6709\u4e9b\u4efb\u6027\uff0c\u5fc3\u76ee\u4e2d\u53ea\u77e5\u6709\u5df1\uff0c\u4e0d\u77e5\u6709 \u4eba\uff01 \u4e3a\u6240\u6b32\u4e3a\u7684\u540e\u679c\uff0c\u5c31\u662f\u79ef\u6028\u62db\u6094\uff01 \u654c\u4eba\u8d8a\u6765\u8d8a\u591a\uff0c\u670b\u53cb\u8d8a\u6765\u8d8a\u5c11\uff01 \u540e\u6765\uff0c\u4e0d\u4e3a\u4ec7\u5bb6\u6240\u5bb9\uff0c\u88ab\u4ec7\u5bb6\u7ea0\u96c6\u4e86\u4e2d\u539f\u6b66\u6797\u6570\u5341\u540d\u6b66\u6797\u9ad8\u624b\uff0c\u56f4\u6bb4\u8ffd\u6740\uff01 \u4e8c\u4eba\u5728\u4e2d\u539f\u4e0d\u80fd\u7acb\u8db3\uff0c\u76f8\u5055\u4ea1\u547d\u6d77\u5916\uff0c\u5e76\u6709\u591a\u5e74\u597d\u53cb\u3001\u65b9\u5916\u81f3\u4ea4\u201c\u4f5b\u5370\u6cd5\u5e08\u201d\u540c\u884c\uff01 \u4ea1\u547d\u6d77\u5916\u7684\u751f\u6daf\uff0c\u53cd\u800c\u4f7f\u4ed6\u4eec\u592b\u59bb\u5ea6\u8fc7\u4e86\u4e00\u6bb5\u5e73\u9759\u65e0\u6ce2\u7684\u7231\u60c5\u751f\u6d3b\uff01 \u821f\u884c\u4e8e\u6d77\uff0c\u5e76\u80a9\u64cd\u6868\uff0c\u4f9d\u504e\u5c71\u5934\uff0c\u5750\u770b\u4e91\u8d77\uff1b\u7761\u5367\u6797\u6cc9\uff0c\u4ee5\u5927\u5730\u4e3a\u5e8a\u2026\u2026\u6709\u7231\uff0c\u4fbf\u6709\u4e86 \u4e00\u5207\uff01 \u76f4\u5230\u5934\u53d1\u767d\u4e86\uff0c\u5df2\u5c4a\u8004\u800b\u4e4b\u5e74\uff0c\u4ed6\u4e8c\u4eba\u4f09\u4fea\u60c5\u6df1\uff0c\u72b9\u80dc\u9752\u5e74\uff01 \u7231\u5f53\u771f\u662f\u6108\u8001\u5f25\u575a\uff01 \u53ef\u662f\uff0c\u4ed6\u4eec\u4f9d\u4ed7\u5e87\u62a4\u7684\u4e3b\u5b50\u201c\u5357\u6d77\u4e00\u541b\u201d\u6d77\u9f99\u795e\u5374\u7a81\u7136\u5174\u8d77\u4e86\u79f0\u9738\u4e2d\u539f\u7684\u5fc3\u7406\uff01 \u201c\u5403\u4eba\u5bb6\u7684\u996d\uff0c\u7ed9\u4eba\u5bb6\u5e72\uff01\u201d\u4ed6\u592b\u59bb\u5f53\u7136\u201c\u4e49\u4e0d\u5bb9\u8f9e\u201d\uff0c\u800c\u4e14\uff0c\u4ed6\u592b\u59bb\u9759\u6781\u601d\u52a8\uff0c\u4e5f\u5174 \u8d77\u4e86\u8dc3\u9a6c\u4e2d\u539f\u9a70\u9a8b\u58ee\u5fd7\u7684\u96c4\u5fc3\uff01 \u52a0\u4e0a\u6b66\u529f\u672c\u5c31\u9ad8\u5f3a\u4eba\u53c8\u5728\u5c9b\u58eb\u57cb\u5934\u6f5c\u4fee\u4e86\u6570\u5341\u5e74\uff0c\u81ea\u8ba4\u53ef\u4ee5\u6253\u904d\u5929\u4e0b\u65e0\u654c\u624b\uff01 \u5c11\u5e74\u8c0b\u5212\uff0c\u8001\u5e74\u6c42\u540d\uff0c\u4e43\u662f\u4eba\u4e4b\u5e38\u60c5\u2026\u2026 \u53ef\u662f\u2014\u2014\u73b0\u5728\u5462\uff1f\u9e33\u9e2f\u6298\u7ffc\uff0c\u8fde\u7406\u65ad\u679d\uff0c\u53d1\u73b0\u957f\u9aef\u8001\u4eba\u5df2\u6b7b\uff0c\u767d\u53d1\u5a46\u5a46\u7684\u4f24\u611f\u662f\u65e0\u6cd5\u5f62 \u5bb9\u7684\uff01 \u5979\u7a81\u7136\u5389\u53eb\u9053\uff1a\u201c\u59d3\u5c55\u7684\u5c0f\u5b50\uff01\u7ad9\u4f4f\uff01\u201d \u5c55\u767d\u5374\u5145\u8033\u4e0d\u95fb\uff0c\u4f9d\u7136\u8e09\u8dc4\u7740\u811a\u6b65\uff0c\u5411\u524d\u8d70\u53bb\uff01 \u6b8b\u591c\u5df2\u901d\uff0c\u9ece\u660e\u6765\u4e34\uff01 \u4f46\u5929\u8fb9\u6709\u4e00\u5c42\u7070\u8499\u8499\u7684\u81ea\u4e91\uff0c\u671d\u9633\u6709\u6c14\u65e0\u529b\u7684\u6697\u6de1\u5149\u7ebf\uff0c\u7167\u7740\u5e7f\u5927\u9662\u843d\u4e2d\u6ee1\u5730\u6a2a\u5c38\uff0c\u6709 \u4e00\u79cd\u4ee4\u4eba\u8bf4\u4e0d\u51fa\u7684\u51c4\u60e8\u666f\u8c61\uff01 \u767d\u53d1\u5a46\u5a46\u89c1\u5c55\u767d\u7406\u4e5f\u4e0d\u7406\uff0c\u5fc3\u4f24\u8001\u4f34\u6b7b\u4ea1\uff0c\u75db\u4e0d\u6b32\u751f\uff0c\u5389\u5578\u4e86\u4e00\u58f0\uff0c\u731b\u5411\u5c55\u767d\u8eab\u540e\u6251 \u81f3\uff01 \u4eba\u672a\u5230\uff0c\u62db\u5df2\u51fa\uff0c\u201c\u641c\u9b42\u6307\u201d\u795e\u529f\u8fd0\u96c6\u6307\u7aef\uff0c\u8fde\u5403\u5976\u7684\u529b\u6c14\u90fd\u7528\u4e0a\u4e86\uff0c\u5411\u7740\u5c55\u81ea\u540e\u5fc3\uff0c \u731b\u63d2\u800c\u4e0b\uff01 \u5c55\u767d\u8151\u5185\u771f\u6c14\u56db\u7a9c\uff0c\u53cc\u773c\u53d1\u9ed1\uff0c\u53f3\u638c\u5fc3\u3001\u5de6\u81c2\u7763\uff0c\u91cd\u4f24\u4e4b\u540e\uff0c\u53c8\u4e0e\u957f\u9aef\u8001\u4eba\u786c\u62fc\u4e86\u4e09 \u638c\uff0c\u8fde\u4ed6\u81ea\u5df1\u4e5f\u4e0d\u76f8\u4fe1\uff0c\u4e09\u638c\u786c\u62fc\u4e0b\u6765\uff0c\u6b7b\u7684\u7adf\u662f\u957f\u9aef\u8001\u4eba\u800c\u4e0d\u662f\u81ea\u5df1\uff01 \u65b9\u624d\u8840\u6e85\u8089\u98de\uff0c\u809d\u8111\u6d82\u5730\u7684\u60e8\u72b6\uff0c\u72b9\u5728\u773c\u524d\u6643\u52a8\uff0c\u4ed6\u6df1\u6df1\u89c9\u5f97\u8fd9\u6837\u75af\u72c2\u7684\u60e8\u6740\uff0c\u5b9e\u5728\u6beb \u65e0\u610f\u4e49\uff01 \u4ed6\u53cd\u590d\u5730\u5728\u5fc3\u4e2d\u81ea\u95ee\uff1a\u201c\u8fd9\u6837\uff0c\u7a76\u7adf\u662f\u4e3a\u4e86\u4ec0\u4e48\uff1f\u2026\u2026\u201d \u201c\u4e3a\u4e86\u540d\uff1f\u4e3a\u4e86\u5229\uff1f\u4e3a\u4e86\u90a3\u672c\u5929\u4e0b\u7b2c\u4e00\u5947\u4e66\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\uff1f\u2026\u2026\u201d \u201c\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\uff0c\u81ea\u5df1\u65e9\u5df2\u7ecf\u5f53\u4f17\u6495\u6bc1\u4e86\uff01\u4f46\u8d2a\u5a6a\u611a\u5984\u7684\u4eba\u4eec\uff0c\u786c\u662f\u4e0d\u76f8\u4fe1\u81ea \u5df1\uff01\u727a\u7272\u4e86\u6027\u547d\uff0c\u5374\u4e89\u5f97\u662f\u4e00\u573a\u7a7a\u7684\u4e1c\u897f\uff01\u2026\u3002\u201d \u201c\u4e00\u573a\u7a7a\uff01\u4e00\u573a\u7a7a\uff01\u540d\u548c\u5229\uff0c\u8fd8\u4e0d\u4e5f\u662f\u4e00\u573a\u7a7a\uff1f\u2018\u5343\u79cb\u4e07\u5c81\u540d\uff0c\u5bc2\u5bde\u8eab\u540e\u4e8b\uff01\u2019\u4eba\u751f\u6570 \u5341\u5e74\u7684\u751f\u547d\uff0c\u79f0\u96c4\uff0c\u79f0\u9738\uff0c\u53c8\u80fd\u5982\u4f55\uff1f\u2026\u2026\u201d \u201c\u91d1\u94f6\u8d22\u5b9d\u5806\u6210\u5c71\uff0c\u5bcc\u5c06\u738b\u4faf\uff0c\u53c8\u80fd\u5982\u4f55\uff1f\u767e\u5e74\u5927\u9650\u4e00\u5230\uff0c\u8fd8\u4e0d\u662f\u4e00\u573a\u7a7a\uff1f\u51c6\u4fdd\u4e00\u4e2a\u5b50 \u4e5f\u5e26\u4e0d\u8d70\uff01\u2026\u2026\u201d \u4ed6\u8eab\u5fc3\u4e24\u4f24\uff0c\u56e0\u6b64\uff0c\u4efb\u4f55\u4eba\u4e0d\u613f\u7406\uff0c\u53ea\u60f3\u65e9\u4e00\u70b9\u79bb\u5f00\u6b64\u5730\uff0c\u79bb\u5f00\u8fd9\u611a\u6627\u7684\u4eba\u7fa4\uff0c\u79bb\u5f00\u8fd9 \u8840\u8165\u7684\u6218\u573a\u2026n\uff0e\u7a81\u7136\u2014\u2014\u8eab\u540e\u4e00\u80a1\u52b2\u98ce\uff0c\u75be\u5578\u800c\u81f3\uff01 \u4ed6\u4e0b\u610f\u8bc6\u5730\u4fa7\u8eab\u4e00\u8eb2\uff01 \u4f46\u4ed6\u91cd\u4f24\u4e4b\u4e0b\uff0c\u8eab\u6cd5\u5df2\u5931\u53bb\u7075\u6d3b\uff0c\u8fd9\u4e00\u8eb2\uff0c\u5e76\u672a\u80fd\u8eb2\u5f00\uff01 \u53ea\u8eb2\u8fc7\u4e86\u540e\u5fc3\u8981\u5bb3\uff0c\u201c\u5657\u201d\u7684\u4e00\u58f0\uff0c\u4ed6\u53ea\u611f\u5230\u53f3\u80a9\u813e\u4e00\u9635\u9525\u9aa8\u7684\u5de8\u75db\uff01 \u53cc\u773c\u4e00\u9ed1\uff0c\u4ed6\u518d\u4e5f\u652f\u6301\u4e0d\u4f4f\uff0c\u4f1f\u5cb8\u7684\u8eab\u8eaf\u6447\u4e86\u4e24\u6447\uff0c\u7ec8\u4e8e\uff0c\u63a8\u91d1\u5c71\u3001\u5012\u7389\u67f1\u4f3c\u5730\u5012\u4e86\u4e0b \u53bb\uff01 \u8fd9\u56de\u771f\u7684\u5012\u4e86\uff01 \u4e00\u4e2a\u5927\u82f1\u96c4\u7684\u5012\u9893\uff01\u4e00\u9897\u5de8\u57a6\u7684\u6b92\u843d\uff01 \u65e0\u6570\u7684\u4f24\u5bb3\uff0c\u65e0\u6570\u7684\u6253\u51fb\uff0c\u4f7f\u4ed6\u7684\u601d\u7ef4\u505c\u6b62\uff0c\u8109\u640f\u4e0d\u518d\u8df3\u52a8\uff0c\u7075\u9b42\u6c89\u5165\u4e86\u9ed1\u6697\u3001\u65e0\u77e5\u3001 \u6df7\u6c8c\u2026\u2026 \u91cd\u56de\u5230\u964d\u751f\u4ee5\u524d\u7684\u6e3a\u832b\uff01\u2026\u2026 \u8fd9\u4e00\u51fb\u4f7f\u5c55\u767d\u6bd9\u547d\u7684\uff0c\u6b63\u662f\u767d\u53d1\u5a46\u5a46\u51b7\u8273\u7ea2\u60b2\u6124\u4e2d\u96c6\u5168\u8eab\u529f\u529b\u7684\u4e00\u6307\uff01 \u5979\u4e00\u6307\u51fb\u6bd9\u5c55\u767d\uff0c\u4ec5\u4ec5\u5446\u4e86\u4e00\u4e0b\uff0c\u6ca1\u6709\u5f97\u80dc\u7684\u9a84\u50b2\uff0c\u4e5f\u6ca1\u6709\u6218\u8d25\u654c\u4eba\u7684\u5feb\u4e50\uff01 \u5979\u53cd\u8eab\u6251\u81f3\u957f\u9aef\u8001\u4eba\u7684\u5c38\u8eab\u65c1\u8fb9\uff0c\u4e00\u6078\u800c\u7edd\uff01 \u767d\u53d1\u5a46\u5a46\u51b7\u8273\u7ea2\u4e5f\u6b7b\u4e86\uff01 \u75db\u54ed\u5979\u4e08\u592b\uff0c\u54ed\u6b7b\u7684\uff01 \u800c\u5979\u6b7b\u65f6\uff0c\u4e0e\u4e08\u592b\u5e76\u80a9\u800c\u5367\uff0c\u53cc\u624b\u7d27\u62b1\u7740\u4e08\u592b\u7684\u8116\u9888\uff01 \u8bf8\u541b\uff01\u60a8\u80fd\u8bf4\u8fd9\u7231\u60c5\u4e0d\u4f1f\u5927\uff1f\u60a8\u80fd\u8bf4\u5979\u4e0d\u662f\u81f3\u60c5\u81f3\u6027\u4e4b\u4eba\uff1f \u4e0d\u95ee\u5979\u7684\u6027\u683c\u5982\u4f55\u6840\u50b2\uff0c\u53ea\u51ed\u8fd9\u4e13\u60c5\uff0c\u5929\u4e0b\u6709\u60c5\u4eba\uff0c\u5fc5\u4f1a\u4e3a\u4e4b\u4e00\u54ed\uff01 \u4eba\u4eba\u5e0c\u671b\u6709\u8fd9\u6837\u4e13\u4e00\u7684\u7231\u4fa3\uff0c\u201c\u751f\u800c\u540c\u88f3\uff0c\u6b7b\u800c\u540c\u68fa\u201d\uff01 \u8fd9\u7a81\u7136\u7684\u53d8\u5316\uff0c\u4f7f\u5f53\u573a\u4e4b\u4eba\u5927\u5927\u5730\u4e00\u618e\uff01 \u4f46\u771f\u6b63\u7684\u597d\u4eba\uff0c\u771f\u6b63\u820d\u5df1\u52a9\u4eba\u7684\u4eba\uff0c\u6b7b\u540e\u662f\u4e0d\u4f1a\u5bc2\u5bde\u7684\uff01 \u56e0\u5f88\u591a\u7684\u4eba\u4f1a\u6000\u5ff5\u4ed6\uff01 \u9996\u5148\u662f\u6155\u5bb9\u7ea2\uff0c\u201c\u5624\u201d\u7684\u4e00\u58f0\u60b2\u6ce3\uff0c\u6251\u5012\u5728\u5c55\u767d\u8eab\u4e0a\uff01 \u63a5\u7740\u662f\u90a3\u6709\u7740\u201c\u6c5f\u5357\u7b2c\u4e00\u7f8e\u4eba\u201d\u4e4b\u79f0\u7684\u91d1\u5f69\u51e4\uff0c\u8fd9\u4f4d\u5a07\u8d35\u7684\u5bcc\u5bb6\u5343\u91d1\u5c0f\u59d0\uff0c\u7d20\u5e38\u662f\u6781\u529b \u7ea6\u675f\u7740\u81ea\u5df1\u7684\u60c5\u611f\uff0c\u6b64\u65f6\uff0c\u5fc3\u4e0a\u4eba\u4e00\u6b7b\uff0c\u518d\u4e5f\u6291\u5236\u4e0d\u4f4f\uff0c\u5979\u60b2\u54ed\u4e86\u4e00\u58f0\uff0c\u4fef\u5728\u5c55\u767d\u7684\u8eab\u4e0a\uff01 \u5979\u7684\u7231\u5fc3\uff0c\u7b2c\u4e00\u6b21\u5f53\u4f17\u8868\u660e\uff0c\u4f46\u5979\u7684\u7231\u4eba\u5df2\u7ecf\u6b7b\u4e86\uff01 \u6155\u5bb9\u7ea2\u62ac\u8d77\u6cea\u773c\uff0c\u770b\u4e86\u770b\u8fd9\u4f4d\u7f8e\u903e\u5929\u4ed9\u7684\u5973\u4eba\uff0c\u4f46\u5979\u5df2\u7ecf\u6ca1\u6709\u4e1d\u6beb\u918b\u610f\uff01 \u53cd\u800c\u89c9\u5f97\u5979\u662f\u8ddf\u81ea\u5df2\u4e00\u6837\uff0c\u503c\u5f97\u540c\u60c5\u7684\u53ef\u601c\u5973\u4eba\u3002 \u6a0a\u7d20\u9e3e\uff0c\u90a3\u6709\u7740\u7537\u513f\u98ce\u7684\u5c11\u5973\uff0c\u6b64\u65f6\u4e5f\u5fcd\u4e0d\u4f4f\u8e31\u5230\u5c55\u767d\u8eab\u8fb9\uff0c\u4ece\u6000\u91cc\u638f\u51fa\u4e00\u65b9\u4e1d\u5dfe\uff0c\u8f7b \u8f7b\u4e3a\u5c55\u767d\u62ed\u53bb\u8138\u4e0a\u7684\u8840\u8ff9\uff01 \u5979\u6ca1\u6709\u75db\u54ed\uff0c\u5979\u53ea\u662f\u5305\u7740\u6ee1\u7736\u6676\u83b9\u7684\u773c\u6cea\uff0c\u54c0\u60bc\u5c55\u767d\uff0c\u50cf\u54c0\u60bc\u5979\u7684\u4e00\u4e2a\u77e5\u5df2\uff01 \u5e78\u4e8f\u5a49\u513f\u5148\u5df2\u660f\u6b7b\u8fc7\u53bb\uff0c\u5426\u5219\uff0c\u4e0d\u77e5\u5979\u600e\u6837\u75db\u54ed\u54e9\uff01 \u201c\u592a\u767d\u53cc\u9038\u201d\u7684\u54ed\u58f0\uff0c\u5f53\u771f\u662f\u60ca\u5929\u52a8\u5730\uff01 \u56e0\u4e3a\u6b7b\u7684\u662f\u4ed6\u4e8c\u4eba\u7684\u201c\u5c0f\u6069\u516c\u201d\uff01 \u4ed6\u5144\u5f1f\u8eab\u53d7\u201c\u9739\u96f3\u5251\u201d\u5c55\u4e91\u5929\u7684\u5927\u6069\uff0c\u5c55\u4e91\u5929\u51a4\u6b7b\uff0c\u4ed6\u5144\u5f1f\u65e0\u4e00\u4ee5\u4e3a\u62a5\uff0c\u624d\u4ee5\u201c\u6d3b\u6b7b \u4eba\u201d\u3001\u201c\u6b7b\u6d3b\u4eba\u201d\u81ea\u79f0\uff01 \u540e\u6765\u9047\u5230\u5c55\u767d\uff0c\u77e5\u662f\u6069\u516c\u540e\u4eba\uff0c\u60f3\u5bf9\u6069\u516c\u4e4b\u5b50\u5c3d\u5df1\u5fc3\u529b\u4ee5\u62a5\u5927\u6069\uff0c\u5374\u8bbe\u60f3\u5230\u5c0f\u6069\u516c\u7adf\u7136 \u6218\u6b7b\uff0c\u4f7f\u4ed6\u5144\u5f1f\u6709\u5fc3\u65e0\u6cd5\u5c3d\uff0c\u6240\u4ee5\u54ed\u5f97\u6700\u6078\uff01 \u8339\u8001\u9556\u5934\u8001\u6cea\u7eb5\u6a2a\uff0c\u4f46\u4ed6\u8fd8\u6c89\u5f97\u4f4f\u6c14\uff0c\u8fde\u9053\uff1a\u201c\u5148\u522b\u4e71\u54ed\uff01\u770b\u770b\u5c55\u5c0f\u4fa0\u8fd8\u6709\u6551\u6ca1\u6709\uff1f\u201d \u5f85\u4ed6\u4e00\u63a2\u5c55\u767d\u66fc\u606f\uff0c\u4e0d\u7531\u5c31\u51c9\u4e86\uff01 \u539f\u6765\u5c55\u767d\u65e9\u5df2\u6b7b\u4e86\uff01 \u96f7\u5927\u53d4\u5374\u786c\u633a\u7740\u6ca1\u6389\u6cea\uff0c\u4ed6\u6728\u7136\u5353\u7acb\uff0c\u5634\u4e2d\u5583\u5583\u9053\uff1a\u201c\u8d24\u4f84\uff01 \u4f60\u6b7b\u5f97\u6709\u4ef7\u503c\uff0c\u8f70\u8f70\u70c8\u70c8\uff01\u4e0d\u6127\u5c55\u4e91\u5929\u540e\u4eba\uff01\u4e0d\u6127\u5c55\u4e91\u5929\u540e\u4eba\uff01\u2026\u2026\u201d \u5c31\u8fde\u5929\u4e0b\u7fa4\u96c4\uff0c\u4e5f\u83ab\u4e0d\u8d70\u81f3\u5c55\u767d\u7684\u8eab\u8fb9\uff0c\u6c89\u54c0\u81f4\u610f\u2026\u2026 \u201c\u95ea\u5f00\uff01\u201d\u7a81\u7136\uff0c\u4f17\u4eba\u8eab\u540e\u4f20\u6765\u4e00\u58f0\u66b4\u559d\uff01 \u4f17\u4eba\u60ca\u7136\u800c\u60ca\uff01\u6446\u7136\u56de\u5934\u4e00\u770b\uff01 \u53ea\u89c1\u201c\u9f99\u4f38\u592a\u5b50\u201d\u50b2\u7136\u800c\u7acb\uff0c\u4fca\u9762\u542b\u715e\uff0c\u6740\u6c14\u8feb\u4eba\uff01 \u5728\u4ed6\u8eab\u540e\u7ad9\u5b9a\u201c\u592a\u4ed3\u4e4b\u9f20\u201d\u4e0e\u201c\u8471\u5cad\u4e4b\u9e70\u201d\uff0c\u540c\u662f\u9762\u76ee\u72de\u6076\uff0c\u9634\u68ee\u5982\u9b3c\uff01 \u201c\u5357\u6d77\u9f99\u5973\u201d\u5374\u80cc\u8138\u7ad9\u7acb\u4e00\u65c1\uff0c\u53cc\u80a9\u62bd\u6410\uff0c\u770b\u6837\u5b50\u5979\u4e5f\u54ed\u4e86\uff0c\u4f46\u4e0d\u77e5\u5979\u662f\u54ed\u6dee\uff1f \u2014\u2014\u8fd9\u53ea\u6709\u5979\u81ea\u5df1\u5fc3\u91cc\u660e\u767d\uff0c\u56e0\u4e3a\uff0c\u5979\u79c1\u604b\u7740\u5c55\u767d\uff0c\u4f46\u603b\u65e0\u6cd5\u5f53\u7740\u7fa4\u96c4\u53bb\u54ed\u654c\u4eba\uff01\u2026\u2026 \u201c\u9f99\u795e\u592a\u738b\u201d\u53ca\u201c\u4e8c\u51f6\u201d\u7684\u6b66\u529f\uff0c\u7fa4\u96c4\u65e9\u5df2\u89c1\u8bc6\u8fc7\uff0c\u5c55\u767d\u4e00\u6b7b\uff0c\u53ef\u8bf4\u65e0\u4eba\u6562\u4e0e\u4ed6\u4e09\u4eba\u4e3a \u654c\uff01 \u8fd9\u4e09\u4eba\u81ea\u6709\u5176\u4e0d\u53ef\u8f7b\u89c6\u7684\u5a01\u6151\u529b\u91cf\uff01 \u5927\u591a\u6570\u4eba\u5747\u5413\u5f97\u8eab\u4e0d\u7531\u5df1\u540e\u9000\u4e86\u4e24\u6b65\uff01 \u53ea\u6709\u90a3\u4e09\u4e2a\u75f4\u60c5\u7684\u5c11\u5973\uff0c\u54c0\u54ed\u604b\u4eba\u5df2\u6b7b\uff0c\u6ca1\u6709\u7406\u4f1a\u201c\u9f99\u795e\u592a\u5b50\u201d\u7684\u559d\u53f1\uff01 \u201c\u9f99\u795e\u592a\u5b50\u201d\u4e24\u9053\u7ec6\u7709\u4e00\u6311\uff0c\u6ee1\u9762\u6740\u673a\uff0c\u8d6b\u7136\u5288\u51fa\u4e00\u638c\uff01 \u638c\u98ce\u5982\u98d8\uff0c\u4e09\u5973\u60ca\u89c9\u9f50\u547c\u66b4\u9000\uff01\u2026\u2026 \u96f7\u5927\u53d4\u6012\u76ee\u500f\u7741\uff0c\u53f1\u9053\uff1a\u201c\u597d\u72c2\u5984\u4e3a\u5c0f\u8f88\uff01\u8001\u592b\u63a5\u4f60\u4e00\u638c\uff01\u201d \u6b64\u58f0\u4e2d\u53cc\u638c\u4e00\u7ffb\uff0c\u731b\u5411\u6765\u52bf\u8fce\u53bb\uff01 \u201c\u8f70\uff01\u201d\u66b4\u559d\u58f0\u4e2d\uff0c\u96f7\u5927\u53d4\u88ab\u9707\u5f97\u8eab\u5f62\u8fde\u6643\uff0c\u540e\u9000\u4e86\u4e09\u6b65\uff01 \u201c\u9f99\u795e\u592a\u5b50\u201d\u66f4\u4e0d\u5f85\u6162\uff0c\u5237\uff01\u5237\uff01\u5237\uff01\u63a5\u8fde\u4e09\u638c\uff0c\u73af\u653b\u800c\u51fa\uff01 \u96f7\u5927\u53d4\u7adf\u4e0d\u662f\u4ed6\u7684\u5bf9\u624b\uff0c\u88ab\u4e09\u638c\u731b\u653b\uff0c\u903c\u9000\u4e09\u56db\u4e08\u5f00\u5916\uff01 \u201c\u9f99\u795e\u592a\u5b50\u201d\u500f\u7136\u6536\u4f4f\u653b\u52bf\uff0c\u56de\u5934\u5bf9\u201c\u4e8c\u51f6\u201d\u559d\u9053\u201c\u641c\uff01\u770b\u59d3\u5c55\u5c0f\u5b50\u8eab\u4e0a\u6709\u6ca1\u6709\u90a3\u672c\u5929 \u4e0b\u7b2c\u4e00\u5947\u4e66\uff1f\u201d \u201c\u592a\u4ed3\u4e4b\u9f20\u201d\u3001\u201c\u8471\u5cad\u4e4b\u9e70\u201d\u5927\u6b65\u8d70\u81f3\u5c55\u767d\u5c38\u8eab\u4e4b\u524d\uff0c\u63a2\u624b\u5411\u5c55\u81ea\u6000\u4e2d\u6293\u53bb\uff01 \u7a81\u7136\u4f20\u6765\u4e00\u58f0\u5a07\u53f1\uff1a\u201c\u4e0d\u8bb8\u52a8\u4ed6\uff01\u201d \u98de\u63a0\u800c\u524d\u4e00\u6761\u5a07\u5c0f\u4eba\u5f71\uff0c\u6a2a\u6321\u5728\u5c55\u767d\u8eab\u524d\uff0c\u6b63\u662f\u6ee1\u9762\u6cea\u75d5\u3001\u4f24\u5fc3\u656c\u7edd\u7684\u6155\u5bb9\u7ea2\uff01 \u5979\u7c89\u8138\u4f3c\u51b0\uff0c\u5ffd\u5411\u201c\u4e8c\u51f6\u201d\u53f1\u9053\uff1a\u201c\u8c01\u6562\u52a8\u4ed6\u4e00\u4e0b\uff0c\u59d1\u5a18\u4fbf\u8ddf\u8c08\u62fc\u547d\uff01\u201d \u5979\u770b\u6765\u5a07\u5f31\u4e0d\u80dc\uff0c\u4f46\u4e3a\u4e86\u4e00\u5fc3\u7ef4\u62a4\u4e08\u592b\u7684\u9057\u4f53\uff0c\u7adf\u6709\u4e00\u80a1\u795e\u5723\u4e0d\u53ef\u4fb5\u72af\u7684\u5a01\u4eea\uff01 \u201c\u4e8c\u51f6\u201d\u90a3\u4e48\u72c2\u50b2\u7684\u4e24\u592a\u9b54\u5934\uff0c\u7ade\u5f53\u65f6\u4e00\u6014\uff01\u2026\u2026 \u8eab\u540e\u4e00\u58f0\u51b7\u54fc\uff01 \u201c\u5357\u6d77\u9f99\u5973\u201d\u4f7f\u5730\u6b3a\u8eab\u800c\u524d\uff0c\u7c89\u9762\u51b0\u5bd2\uff0c\u4ee5\u9634\u68ee\u5df2\u6781\u7684\u8bed\u8c03\uff0c\u5bf9\u6155\u5bb9\u7ea2\u559d\u9053\uff1a\u201c\u4ed6\u662f\u4f60 \u4ec0\u4e48\u4eba\uff1f\u6b7b\u540e\u8fd8\u503c\u5f97\u4e3a\u4ed6\u62fc\u547d\uff01\u201d \u6155\u5bb9\u7ea2\u773c\u6cea\u5728\u773c\u7736\u91cc\u6253\u8f6c\uff0c\u4f46\u5728\u4ec7\u4eba\u9762\u524d\uff0c\u5979\u786c\u662f\u6ca1\u54ed\uff0c\u9165\u80f8\u4e00\u633a\uff0c\u6602\u7136\u9053\uff1a\u201c\u4ed6\u662f\u6211 \u7684\u4e08\u592b\u2026\u2026\u201d \u672a\u5bb9\u5979\u628a\u8bdd\u8bf4\u5b8c\uff0c\u201c\u5357\u6d77\u9f99\u5973\u201d\u8138\u8272\u4e00\u53d8\uff0c\u72e0\u53f1\u9053\uff1a\u201c\u53bb\u4f60\u7684\uff01\u201d \u6c89\u53f1\u4e2d\uff0c\u4e00\u638c\u5411\u6155\u5bb9\u7ea2\u9762\u95e8\u62cd\u53bb\uff01 \u6155\u5bb9\u7ea2\u53cc\u638c\u4e00\u8fce\uff0c\u201c\u7830\u201d\u7684\u4e00\u58f0\u66b4\u54cd\uff0c\u8fde\u88ab\u9707\u9000\u5341\u6570\u6b65\u51fa\u53bb\uff01 \u201c\u5357\u6d77\u9f99\u5973\u201d\u4e00\u638c\u9707\u9000\u6155\u5bb9\u7ea2\uff0c\u5e76\u672a\u8d81\u52bf\u8ffd\u51fb\uff0c\u62ac\u624b\u7406\u4e86\u7406\u9b03\u8fb9\u4e71\u53d1\uff0c\u53ea\u89c1\u5979\u4e00\u53cc\u7ea4\u624b \u5fae\u98a4\u5730\u5411\u5c55\u767d\u8eab\u4e0a\u6478\u53bb\uff01 \u5979\u8fd9\u662f\u6709\u751f\u4ee5\u6765\uff0c\u7b2c\u4e00\u6b21\u63a5\u89e6\u5f02\u6027\u7684\u8eaf\u4f53\uff0c\u4e5f\u662f\u5979\u7b2c\u4e00\u6b21\u949f\u60c5\u2014\u4e2a\u7537\u4eba\uff0c\u867d\u7136\u90a3\u662f\u4e00\u4e2a \u6b7b\u4eba\uff01 \u5979\u5a07\u989c\u9161\u7ea2\uff0c\u82b3\u5bb9\u5982\u9189\uff0c\u4ee5\u4e00\u79cd\u4ece\u672a\u4f53\u4f1a\u8fc7\u7684\u5947\u5999\u5fc3\u60c5\uff0c\u53cc\u624b\u98a4\u6296\u7740\u629a\u6478\u5c55\u767d\u7684\u5468 \u8eab\u2026\u2026 \u201c\u59b9\u59b9\uff01\u79d8\u5f55\u5728\u4e0d\u5728\u4ed6\u8eab\u4e0a\uff1f\u201d \u201c\u5357\u6d77\u9f99\u5973\u201d\u5feb\u7136\u4e00\u60ca\uff0c\u62ac\u5934\u89c1\u662f\u201c\u9f99\u795e\u592a\u5b50\u201d\u5411\u5979\u53d1\u95ee\uff0c\u4e0d\u7531\u8138\u5b54\u4e00\u7ea2\uff01\u5979\u53ea\u987e\u4e86\u6c89 \u9189\u5728\u4e00\u79cd\u7384\u5999\u7684\u5e7b\u60f3\u4e4b\u4e2d\uff0c\u5374\u5fd8\u4e86\u662f\u8981\u5728\u5c55\u767d\u5c38\u4f53\u4e0a\u6765\u641c\u7d22\u90a3\u672c\u5929\u4e0b\u7b2c\u4e00\u5947\u4e66\u7684\u3002 \u5979\u4e00\u8bed\u4e0d\u53d1\uff0c\u8d77\u8eab\u4fbf\u8d70\uff01 \u7ecf\u201c\u9f99\u795e\u592a\u5b50\u201d\u4e00\u95ee\uff0c\u5979\u624d\u8bb0\u8d77\u81ea\u5df1\u7684\u8eab\u4efd\uff0c\u96be\u4e3a\u60c5\uff0c\u4f7f\u5979\u4e0d\u77e5\u600e\u6837\u8bf4\u624d\u597d\uff0c\u53ea\u6709\u4e00\u8d70 \u4e86\u4e4b\u3002 \u201c\u9f99\u795e\u592a\u5b50\u201d\u8ffd\u95ee\u4e86\u4e00\u65ec\u201c\u6ca1\u6709\u5728\u4ed6\u8eab\u4e0a\u5417\uff1f\u201d \u201c\u6ca1\u6709\uff01\u201d \u201c\u5357\u6d77\u9f99\u5973\u201d\u5934\u4e5f\u4e0d\u56de\u5730\u7b54\u9053\u3002 \u201c\u6211\u5374\u4e0d\u76f8\u4fe1\uff01\u201d\u201c\u9f99\u795e\u592a\u5b50\u201d\u8bf4\u7f62\uff0c\u5927\u6b65\u8d70\u5411\u5c55\u767d\u5c38\u4f53\uff01 \u91d1\u5f69\u51e4\u7eb5\u8eab\u6321\u5728\u5c55\u767d\u8eab\u524d\uff0c\u9053\uff1a\u4ed6\u5df1\u6b7b\u4e86\uff01\u8bf7\u4f60\u4e0d\u8981\u518d\u52a8\u4ed6\uff0c\u4f7f\u4ed6\u6b7b\u800c\u4e0d\u5b89\uff01\u201d \u8fd9\u51e0\u53e5\u8bdd\uff0c\u8bf4\u5f97\u5f88\u662f\u59d4\u5a49\uff0c\u4f3c\u662f\u4e00\u79cd\u54c0\u6c42\uff0c\u7531\u8fd9\u7f8e\u903e\u5929\u4eba\u7684\u91d1\u5f69\u51e4\u53e3\u4e2d\u8bf4\u51fa\uff0c\u66f4\u89c9\u51c4\u695a \u52a8\u4eba\uff01 \u201c\u9f99\u795e\u592a\u5b50\u201d\u773c\u524d\u4e00\u4eae\uff0c\u91d1\u5f69\u51e4\u4e4b\u7f8e\uff0c\u771f\u53ef\u79f0\u5f97\u8d77\u662f\u8273\u5149\u7167\u4eba\uff01 \u201c\u9f99\u795e\u592a\u5b50\u201d\u672c\u662f\u6709\u540d\u7684\u82b1\u82b1\u516c\u5b50\uff0c\u597d\u8272\u6210\u6027\uff0c\u521d\u8e1e\u91d1\u5e9c\uff0c\u4ed6\u4ee5\u8ff7\u836f\u628a\u91d1\u5f69\u51e4\u8ff7\u4f4f\uff0c\u91d1 \u5f69\u51e4\u6495\u7834\u5468\u8eab\u8863\u670d\uff0c\u51e0\u4e4e\u5f97\u624b\u3002\u60f3\u8d77\u90a3\u65d6\u65ce\u98ce\u5149\uff0c\u4ed6\u5fc3\u4e2d\u4e0d\u7531\u4e00\u8361\uff01 \u4f46\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\u7684\u5438\u5f15\u529b\uff0c\u5374\u53c8\u6bd4\u7f8e\u8272\u91cd\u8981\u5f97\u591a\u3002\u201c\u9f99\u795e\u592a\u5b50\u201d\u4e0d\u6127\u6709\u201c\u67ad\u96c4\u4e4b \u624d\u201d\uff0c\u867d\u559c\u5973\u8272\uff0c\u4f46\u5e76\u4e0d\u8ff7\u7cca\uff0c\u7f8e\u5973\u5230\u5904\u53ef\u6c42\uff0c\u300a\u9501\u9aa8\u9500\u9b42\u5929\u4f5b\u5377\u300b\u5374\u662f\u767e\u4e16\u96be\u9047\uff0c\u4ed6\u6743\u8861 \u8f7b\u91cd\uff0c\u5f53\u7136\u662f\u5f03\u7f8e\u8272\uff0c\u53d6\u5947\u4e66\uff01\u5f53\u65f6\uff0c\u5411\u91d1\u5f69\u51e4\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u6211\u9519\u8fc7\u4e86\u4e00\u6b21\u673a\u4f1a\uff0c\u81f3 \u4eca\u540e\u4fae\uff01\u4e0d\u8fc7\uff0c\u6211\u53ef\u4ee5\u8bf4\u53e5\u8001\u5b9e\u8bdd\uff0c\u59d1\u5a18\u4e4b\u7f8e\uff0c\u53ef\u79f0\u5f97\u8d77\u5929\u4e0b\u65e0\u53cc\uff01\u201d \u91d1\u5f69\u51e4\u5fc3\u4e2d\u4e5f\u4e00\u52a8\u3002\u4eba\uff0c\u6ca1\u6709\u4e0d\u559c\u6b22\u8d5e\u7f8e\u7684\uff0c\u5c24\u5176\u662f\u5973\u4eba\u3002 \u201c\u9f99\u795e\u592a\u5b50\u201d\u4fca\u7f8e\u51fa\u4fd7\uff0c\u98ce\u5ea6\u6eaf\u8111\uff0c\u542b\u7b11\u671b\u7740\u5979\uff0c\u8bb2\u6e05\u79c0\u4fca\u7f8e\u5b9e\u8981\u8d85\u8fc7\u5c55\u767d\u4e4b\u4e0a\uff0c\u4f46\u8138 \u4e0a\u8f7b\u8584\u7684\u7b11\u5bb9\u53ca\u53cc\u773c\u7684\u90aa\u6c14\u7684\u5149\u8f89\uff0c\u5374\u4f7f\u4eba\u89c9\u5f97\u4ed6\u8fdc\u4e0d\u53ca\u5c55\u767d\u5fe0\u539a\u53ef\u9760\u3002 \u5979\u671b\u7740\u8fd9\u6697\u7528\u8ff7\u836f\u3001\u5dee\u4e00\u70b9\u4f7f\u81ea\u5df1\u5931\u8eab\u4e8e\u4ed6\u7684\u7f8e\u7537\u5b50\uff0c\u79c0\u8138\u4e00\u7ea2\uff0c\u82b3\u5fc3\u4e71\u8df3\uff0c\u4e00\u65f6\u4e4b\u95f4 \u4e0d\u77e5\u8bf4\u4ec0\u4e48\u624d\u597d\u2026\u2026\u201c\u9f99\u795e\u592a\u5b50\u201d\u7a81\u5730\u4f38\u51fa\u4e24\u6307\uff0c\u634f\u4f4f\u91d1\u5f69\u51e4\u7c89\u8138\u4e0a\u7684\u4e00\u7247\u5ae9\u8089\uff0c\u6447\u4e86\u6447\uff0c \u54ad\u54ad\u7b11\u9053\uff1a \u201c\u73b0\u5728\u672c\u592a\u5b50\u6ca1\u6709\u5174\u8da3\uff0c\u7b49\u4e8b\u8fc7\u4e4b\u540e\u518d\u627e\u4f60\u2026\u2026\u201d\u90a3\u52a8\u4f5c\u8f7b\u8584\u5df2\u6781\uff01 \u91d1\u5f69\u51e4\u60f3\u4e0d\u5230\u5802\u5802\u7684\u201c\u9f99\u795e\u592a\u5b50\u201d\uff0c\u7adf\u5f53\u4f17\u505a\u51fa\u5982\u6b64\u4e0b\u6d41\u52a8\u4f5c\uff0c\u60f3\u5979\u4e43\u662f\u540d\u95e8\u95fa\u79c0\uff0c\u5fc3 \u6027\u7d20\u6781\u9ad8\u50b2\uff0c\u54ea\u80fd\u5f53\u4f17\u53d7\u5230\u5982\u6b64\u4fae\u8fb1\uff1f \u4e0d\u7531\u6012\u6781\uff01\u4e5f\u6ca1\u6709\u8bf4\u8bdd\uff0c\u53cd\u624b\u4e00\u638c\uff0c\u5411\u201c\u9f99\u795e\u592a\u5b50\u201d\u8138\u4e0a\u63b4\u53bb\uff01 \u201c\u556a\uff01\u201d \u4e00\u58f0\u8106\u54cd\uff0c\u201c\u9f99\u795e\u592a\u5b50\u201d\u8138\u4e0a\u7acb\u523b\u66b4\u8d77\u4e94\u6761\u7ea2\u8272\u6307\u5370\uff01 \u4e00\u662f\u201c\u9f99\u795e\u592a\u5b50\u201d\u4e0d\u9632\uff0c\u518d\u8005\u201c\u9f99\u795e\u592a\u5b50\u201d\u5bf9\u7740\u7f8e\u8272\u7a76\u7adf\u6709\u70b9\u8272\u6388\u9b42\u4e0e\uff0c\u795e\u4e0d\u5b88\u820d\u3002\u5426 \u5219\uff0c\u4ee5\u4ed6\u7684\u6b66\u529f\u6765\u8bf4\uff0c\u91d1\u5f69\u51e4\u65e0\u8bba\u5982\u4f55\u662f\u4e0d\u4f1a\u4e00\u638c\u5f97\u624b\u7684\uff01 \u4f46\uff0c\u8fd9\u4e00\u638c\u5374\u6253\u51fa\u201c\u9f99\u4f38\u592a\u5b50\u201d\u7684\u6012\u706b\uff01\u53ea\u89c1\u4ed6\u8138\u8272\u4e00\u53d8\uff0c\u5389\u53f1\u9053\uff1a\u201c\u4e2b\u5934\uff0c\u4f60\u627e \u6b7b\uff01\u201d \u5389\u53f1\u58f0\u4e2d\uff0c\u5de6\u624b\u4e94\u6307\u53c9\u5f00\u731b\u5411\u91d1\u5f69\u51e4\u5982\u82b1\u7c89\u56e0\u4e0a\u6293\u53bb\uff0c\u53f3\u638c\u5374\u7531\u8098\u5e95\u7a7f\u51fa\uff0c\u75be\u5411\u91d1\u5f69\u51e4 \u9165\u80f8\u4e0a\u6309\u53bb\uff01\u4e00\u62db\u4e24\u5f0f\uff0c\u5feb\u903e\u7535\u5149\u77f3\u706b\uff01 \u770b\u6765\u4ed6\u5c31\u8981\u65bd\u5c55\u72e0\u6bd2\u62db\u5f0f\uff0c\u4e0d\u60dc\u8fa3\u624b\u6467\u82b1\uff01'}),showOnHomepage:!0}]}}]);