"use strict";(self.webpackChunkwebsite=self.webpackChunkwebsite||[]).push([[17320],{81067:(e,n,s)=>{s.r(n),s.d(n,{default:()=>a});var t=s(74848);const a=[{name:"\u8bf7\u4f60\u52a8\u624b\u665a\u4e00\u70b9",content:(0,t.jsx)(t.Fragment,{children:"\u7130\u7130\u7684\u56de\u5fc6\uff1a\u5973\u4eba\u603b\u662f\u4e3a\u60c5\u6240\u82e6\u7684 \u3000\u3000\u6211\u4ece\u6765\u6ca1\u6709\u540e\u6094\u8fc7\u8fd9\u4ef6\u4e8b\u3002 \u3000\u3000\u4e3a\u4e86\u9ad8\u66fe\u82b1\uff0c\u4e00\u5207\u90fd\u662f\u503c\u5f97\u7684\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u53ef\u4ee5\u4e3a\u4e00\u4ef6\u4e8b\u6216\u53e6\u4e00\u4e2a\u4eba\u5927\u6012\u5927\u559c\u3001\u5927\u8d77\u5927\u843d\uff0c\u751a\u81f3\u4e00\u751f\u7684\u7cbe\u529b\u90fd\u732e\u51fa\u53bb\uff0c\u90a3\u662f\u4e0d\u865a\u6b64\u751f\u7684\uff1b\u6015\u53ea\u6015\u8fd9\u4e00\u751f\u4e2d\u6ca1\u6709\u76ee\u6807\uff0c\u4e0d\u503c\u5f97\u4e3a\u4efb\u4f55\u4e8b\u60c5\u4ed8\u51fa\u5fc3\u529b\uff0c\u6df7\u6df7\u6c8c\u6c8c\u832b\u832b\u7136\u4f46\u53c8\u8fc7\u5206\u6e05\u9192\u5730\u62d6\u8457\u6765\u6d3b\u3002 \u3000\u3000\u6234\u51b2\u5bd2\u6700\u597d\u4e0d\u8981\u6765\u627e\u6211-\u4e3a\u4e86\u8fd9\u4ef6\u4e8b\uff0c\u4ed6\u4e00\u5b9a\u4f1a\u6740\u6211\u3001\u6740\u66fe\u82b1\uff0c\u4e3a\u4e86\u66fe\u82b1\uff0c\u6211\u53ea\u597d\u6740\u4e86\u4ed6\u3002 \u3000\u3000\u9664\u4e86\u5e08\u5085\uff0c\u9664\u4e86\u66fe\u82b1\uff0c\u8c01\u90fd\u4e0d\u77e5\u9053\u6211\u5df2\u7ec3\u6210\u4e86\u201c\u795e\u624b\u5927\u5288\u68fa\u201d\u3002 \u3000\u3000\u6211\u77e5\u9053\u6234\u5e08\u5144\u662f\u4e2a\u597d\u4eba\u3002\u6234\u5e08\u54e5\u5f88\u4fe1\u4efb\u6211\u3002\u4ed6\u662f\u4e2a\u6b66\u5b66\u5947\u624d\uff0c\u4ed6\u7684\u201c\u5927\u6298\u679d\u624b\u201d\u662f\u201c\u5b64\u5c71\u95e8\u201d\u91cc\u81ea\u5f53\u4eca\u7684\u5927\u5e08\u7236\u590f\u5019\u695a\u5531\u5916\uff0c\u6050\u6015\u5c31\u6570\u4e00\u4e0d\u6570\u4e8c\u7684\u4e86\u3002\u6211\u5bf9\u4e0d\u8d77\u4ed6\u3002\u6211\u4f69\u670d\u4ed6\u3002\u53ef\u662f\u4e3a\u4e86\u66fe\u82b1\uff0c\u6211\u4e0d\u7ba1\u4e86\u3002\u9ad8\u66fe\u82b1\u662f\u6211\u7684\uff0c\u4e0d\u662f\u4ed6\u7684\u3002\u4ed6\u6765\u6740\u6211\uff0c\u53ef\u4ee5\u3002\u4ed6\u8981\u4f24\u5bb3\u66fe\u82b1\uff0c\u6211\u6740\u4e86\u4ed6\u3002\u6211\u4e0d\u7b49\u201c\u5b64\u5c71\u4e00\u8109\u7ade\u6b66\u5927\u8d5b\u201d\u90a3\u4e00\u5929\u4e86\u3002\u6211\u8981\u7528\u201c\u795e\u624b\u5927\u5288\u68fa\u201d\uff0c\u6740\u4e86\u4ed6\u3002 \u3000\u3000\u5728\u9047\u89c1\u6234\u5927\u5ac2-\u4e0d\uff0c\u9ad8\u66fe\u82b1\u4e4b\u524d-\u6211\u4e0d\u6653\u5f97\u81ea\u5df1\u5e94\u8be5\u4e3a\u8c01\u800c\u6d3b\uff1f\u4e3a\u201c\u7ade\u6280\u5927\u8d5b\u201d\u7684\u4e00\u5915\u626c\u540d\uff1f\u6211\u5c82\u4e0d\u662f\u53d8\u6210\u4e86\u201c\u5927\u5b64\u5c71\u6d3e\u201d\u548c\u201c\u5b64\u5c71\u95e8\u201d\u4e4b\u95f4\u7684\u201c\u79d8\u5bc6\u6b66\u5668\u201d\u4e86\uff1f\u4f46\u5728\u9047\u89c1\u4e4b\u524d\u7684\u5c81\u6708\u91cc\uff0c\u9ad8\u66fe\u82b1\u60f3\u5fc5\u662f\u4e3a\u60c5\u6240\u82e6\u3002 \u3000\u3000\u6234\u5e08\u54e5\u662f\u4e2a\u4e86\u4e0d\u8d77\u7684\u4eba\u3002\u5f53\u5e74\uff0c\u201c\u4e03\u5e2e\u516b\u4f1a\u4e5d\u8054\u76df\u201d\u7684\u201c\u591a\u8001\u4f1a\u201d\u3001\u201c\u5b64\u5bd2\u76df\u201d\u3001\u201c\u731b\u9b3c\u5e2e\u201d\u4e09\u8d77\u4eba\u9a6c\uff0c\u805a\u4f17\u8981\u6b7c\u706d\u201c\u5b64\u5c71\u95e8\u201d\u548c\u201c\u5927\u5b64\u5c71\u6d3e\u201d\uff0c\u5c31\u662f\u90a3\u65f6\u4faf\uff0c\u6234\u5e08\u54e5\u4e0d\u907f\u4f17\u8bb3\uff0c\u529b\u4e3b\u628a\u201c\u5b64\u5c71\u4e00\u8109\u201d\u7684\u6240\u6709\u5b9e\u529b\u5408\u800c\u4e3a\u4e00\uff0c\u4ee5\u5e94\u6218\u6765\u654c\u3002 \u3000\u3000\u4ed6\u8bf4\u5f97\u4e00\u70b9\u4e5f\u4e0d\u9519\u3002\u4ed6\u8981\u6bd4\u6211\u957f\u5341\u4e8c\u3001\u4e09\u5c81\u5427\uff1f\u4ed6\u8bf4\u7684\u8bdd\uff0c\u4e00\u662f\u4e00\u3001\u4e8c\u662f\u4e8c\uff0c\u5c31\u7b97\u662f\u58f9\u4e07\u53c1\u5343\u9646\u767e\u8086\u62fe\u58f9\uff0c\u4e5f\u5c31\u662f\u4e00\u4e07\u4e09\u5343\u516d\u767e\u56db\u5341\u4e00\uff0c\u4e0d\u591a\u4e5f\u4e0d\u5c11\u3002\u4ed6\u5c31\u662f\u8fd9\u4e48\u4e00\u4e2a\u4eba\u3002\u6709\u5ca9\u77f3\u4e00\u822c\u7684\u610f\u5fd7\uff0c\u8c79\u4e00\u822c\u7684\u80c6\u5b50\uff0c\u9e70\u4e00\u822c\u7684\u773c\uff0c\u94c1\u4e00\u822c\u7684\u62f3\u5934\u3002 \u3000\u3000\u4ed6\u6562\u62c5\u5f53\u3002 \u3000\u3000\u4ed6\u4e88\u4eba\u4fe1\u5fc3\uff0c\u8ba9\u4eba\u5b89\u5b9a\u3002 \u3000\u3000\u90a3\u4e00\u6b21\uff0c\u6211\u8fd8\u5c0f\uff0c\u6211\u5728\u4eba\u4e1b\u4e2d\u542c\u4ed6\u6765\u5927\u5b64\u5c71\u6177\u6168\u9648\u8bcd\uff0c\u6211\u8840\u6c14\u4e00\u76f4\u51b2\u4e0a\u4e86\u53d1\u68a2\uff0c\u4e45\u4e45\u4e0d\u80fd\u81ea\u5df2\u3002\u5f53\u65f6\uff0c\u6709\u4eba\u8d5e\u6210\u4ed6\uff0c\u4f46\u4e0d\u6562\u58f0\u5f20\uff0c\u6709\u4eba\u53cd\u5bf9\u4ed6\uff0c\u9a82\u4ed6\u662f\u201c\u5899\u5934\u8349\u201d\uff0c\u201c\u5927\u5b64\u5c71\u6d3e\u201d\u7684\u5927\u7f6a\u4eba\u80c6\u5c0f\u9b3c\uff0c\u628a\u553e\u6cab\u661f\u5b50\u5550\u5230\u4ed6\u8138\u4e0a\u3002 \u3000\u3000\u53ef\u662f\uff0c\u90a3\u4e00\u5f79\uff0c\u4ed6\u8054\u540c\u4e86\u201c\u5b64\u5c71\u201d\u95e8\u91cc\u5e2e\u4e2d\u7684\u4e00\u5207\u529b\u91cf\uff0c\u6740\u9000\u4e86\u6211\u4eec\u5171\u540c\u7684\u654c\u4eba\u3002\u800c\u4ed6\u8eab\u4e0a\u7684\u8840\uff0c\u5c31\u7b97\u5728\u6d53\u90c1\u7684\u591c\u8272\u91cc\u591c\u8272\u91cc\u4e5f\u6d41\u5f97\u50cf\u4e00\u626d\u626d\u7684\u9ed1\u6cb3\u3002\u4f46\u4ed6\u4ecd\u662f\u7ad9\u5f97\u7b14\u76f4\uff0c\u6276\u7740\u8ddf\u4ed6\u4e00\u8d77\u53bb\u62fc\u547d\u800c\u8d1f\u4f24\u7684\u5144\u5f1f\uff0c\u5c31\u50cf\u6bd4\u6d77\u6c34\u8fd8\u8001\u3001\u6bd4\u96d5\u50cf\u66f4\u786c\u3002\u5728\u90a3\u4e00\u523b\uff0c\u6211\u5c31\u5728\u5fc3\u4e2d\u8d77\u8a93\uff1a\u6709\u4e00\u5929\uff0c\u6211\u8981\u5b66\u6234\u5e08\u5144\uff0c\u8ddf\u4ed6\u53bb\u6740\u654c\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u5f85\u5927\u654c\u9000\u8d70\u540e\uff0c\u4ed6\u4ecd\u662f\u4ed6\uff0c\u6211\u8fd8\u662f\u6211\u3002\u6211\u662f\u201c\u5927\u5b64\u5c71\u6d3e\u201d\u8001\u5e08\u7236\u695a\u5bfb\u9b42\u7684\u4eb2\u4f20\u5f1f\u5b50\u3002\u4ed6\u662f\u201c\u5b64\u5c71\u95e8\u201d\u7684\u7b2c\u4e09\u4ee3\u5f1f\u5b50\u4e2d\u7b2c\u4e00\u597d\u624b\u3002 \u3000\u3000\u540e\u6765\uff0c\u6211\u4eec\u6d3e\u91cc\u4f5c\u8fc7\u68c0\u8ba8\uff0c\u90fd\u4e00\u81f4\u8ba4\u4e3a\u4e0d\u8be5\u8ba9\u6234\u51b2\u5bd2\u72ec\u5360\u9ccc\u5934\u3002\u6d3e\u91cc\u597d\u624b\uff0c\u5e94\u52e4\u52a0\u7528\u529f\uff0c\u5e94\u5934\u8d76\u4e0a\uff0c\u4e00\u811a\u8e29\u4e0b\u3002\u4e5f\u5927\u6982\u662f\u5728\u90a3\u65f6\u4faf\u5427\uff0c\u5e08\u7236\u5c31\u628a\u201c\u795e\u624b\u5927\u5288\u68fa\u201d\u6709\u6b65\u9aa4\u7684\u4f20\u6388\u4e86\u7ed9\u6211\u3002 \u3000\u3000\u800c\u4eca\uff0c\u6211\u5df2\u7ecf\u5b66\u6210\u3002 \u3000\u3000\u5bf9\u201c\u5927\u5b64\u5c71\u6d3e\u201d\u800c\u8a00\uff0c\u7ec3\u6210\u201c\u795e\u624b\u5927\u5288\u68fa\u201d\uff0c\u5c31\u662f\u6211\u6d3e\u58ee\u5927\u4e2d\u5174\u65f6\uff1b\u4f46\u5bf9\u6211\u800c\u8a00\uff0c\u7ec3\u5f97\u201c\u795e\u624b\u5927\u5288\u68fa\u201d\u53ea\u662f\u6211\u7684\u5410\u82bd\uff0c\u89c1\u5230\u6234\u5927\u5ac2-\u9ad8\u66fe\u82b1\u624d\u662f\u6211\u7684\u82b1\u5f00\u3002\u5168\u76db\u7684\u82b1\u5f00\u3002 \u3000\u3000\u521d\u89c1\u9ad8\u66fe\u82b1\uff0c\u662f\u4e00\u4e2a\u607c\u4eba\u7684\u610f\u5916\u3002\u5979\u5f88\u9759\u3002\u9759\u5f97\u50cf\u4e00\u6735\u591c\u91cc\u7684\u82b1\uff0c\u767d\u8272\u7684\uff0c\u5f00\u5f97\u707f\u70c2\u3001\u98ce\u534e\u3001\u7edd\u8273\u5374\u6ca1\u6709\u4eba\u770b\u89c1\u6ca1\u6709\u4eba\u77e5\u3002\u4f46\u6211\u770b\u89c1\u3002\u6211\u77e5\u9053\u3002\u4f46\u6211\u4e0d\u77e5\u9053\u8fd9\u4e2a\u5728\u6211\u5fc3\u91cc\u5343\u547c\u4e07\u5524\u597d\u50cf\u966a\u6211\u8d70\u8fc7\u4e03\u4e16\u4e09\u751f\u719f\u6089\u7684\u964c\u751f\u5973\u5b50\u662f\u8c01\u3002 \u3000\u3000\u90a3\u65f6\u4faf\uff0c\u6234\u51b2\u5bd2\u4e0d\u6b62\u5728\u4e09\u5e74\u4e00\u5ea6\u7684\u5b64\u5c71\u4e00\u8109\u6bd4\u6b66\u4e2d\u5168\u80dc\uff0c\u800c\u4e14\u8fd8\u662f\u5168\u76db\u65f6\u671f\u3002\u4ed6\u9ad8\u5927\u3001\u8c6a\u58ee\uff0c\u654c\u624b\u90fd\u6298\u670d\u5728\u4ed6\u7684\u201c\u5927\u6298\u679d\u624b\u201d\u4e0b\u3002\u4ed6\u4e00\u80dc\u518d\u80dc\uff0c\u4f46\u7edd\u4e0d\u8dbe\u9ad8\u6c14\u626c\u3002\u4ed6\u5c31\u50cf\u4e00\u682a\u795e\u6728\uff0c\u4e0b\u4e86\u64c2\u53f0\uff0c\u4ed6\u627e\u5230\u9ad8\u66fe\u82b1\uff0c\u5979\u5c31\u50cf\u8001\u6811\u65c1\u7684\u4e00\u4e1b\u5c0f\u82b1\u3002\u6708\u591c\u7684\u82b1\u3002 \u3000\u3000\u5f53\u6211\u770b\u89c1\u6234\u51b2\u5bd2\u7528\u4e00\u79cd\u8001\u6811\u7684\u60c5\u6000\u6765\u770b\u5979\u7684\u65f6\u4faf\uff0c\u6211\u624d\u77e5\u9053\u6211\u5199\u4e0d\u4e0b\u5531\u4e0d\u51fa\u6325\u821e\u4e0d\u53bb\u6211\u7684\u75db\u82e6\u3002\u554a\uff0c\u662f\u8fd9\u6837\u4ee4\u4eba\u75db\u82e6\u7684\u4f24\u5fc3\uff0c\u662f\u8fd9\u6837\u4ee4\u4eba\u4f24\u5fc3\u7684\u75db\u82e6\u3002\u4e3a\u4ec0\u4e48\u8981\u8ba9\u6211\u9047\u4e0a\u8fd9\u6837\u4e00\u4e2a\u6bd4\u82b1\u8fd8\u5973\u6027\u7684\u5973\u5b50\uff0c\u5979\u8eab\u8fb9\u5374\u53c8\u6709\u6bd4\u5c71\u66f4\u7537\u6027\u7684\u7537\u4eba\u3002 \u3000\u3000\u6211\u8fd9\u624d\u77e5\u9053\u6709\u6068\u3002\u8981\u5fd8\u6389\u504f\u504f\u5fd8\u4e0d\u6389\u3002\u8bbe\u6cd5\u5fd8\u8bb0\u7684\u53ea\u662f\u5fd8\u8bb0\u800c\u4e0d\u662f\u8bb0\u5fc6\u3002\u5979\u957f\u5728\u6211\u5185\u5fc3\u4e86\uff1a\u6811\u5927\u6839\u6df1\uff0c\u5c31\u7b97\u8fde\u6839\u62d4\u8d77\u4e5f\u4f9d\u6837\u987d\u5f3a\u7684\u751f\u5b58\u3002 \u3000\u3000\u6211\u5728\u6df1\u591c\u91cc\u4ecd\u4e0d\u80fd\u505c\u6b62\u6211\u5bf9\u5979\u7684\u601d\u5ff5\u3002\u90a3\u591c\uff0c\u6234\u51b2\u5bd2\u548c\u5979\u7ecf\u8fc7\u6211\u7684\u9762\u524d\u3002\u6234\u5e08\u54e5\u8bf4\uff1a\u201c\u66fe\u82b1\uff0c\u4ed6\u5c31\u662f\u6211\u4e09\u5e74\u540e\u7684\u8fd1\u654c\uff1b\u7130\u7130\uff0c\u4ed6\u7684\u4eba\u5c31\u50cf\u4e00\u6735\u706b\u7130\u3002\u201d\u5979\u4e00\u7b11\uff0c\u53eb\u4e86\u4e00\u58f0\uff1a\u201c\u6218\u7130\u7130\u3002\u201d\u6709\u4e00\u79cd\u51fa\u5947\u7684\u8d1e\u9759\u3002\u90a3\u65f6\u4faf\uff0c\u6234\u5e08\u54e5\u6b63\u5728\u5168\u76db\u7684\u5168\u80dc\u4e2d\u3002\u4f46\u6211\u77e5\u9053\uff0c\u771f\u6b63\u5168\u80dc\u548c\u5168\u76db\u7684\uff0c\u662f\u4ed6\u8eab\u65c1\u7684\u5973\u5b50\u3002\u9047\u89c1\u5979\u662f\u4e00\u79cd\u5e78\u798f\u800c\u5fe7\u4f24\u7684\u611f\u89c9\u3002\u5c31\u7b97\u4e00\u5411\u4ece\u4e0d\u559d\u9152\u800c\u53ea\u597d\u53bb\u559d\u9189\u4e86\u7684\u6211\uff0c\u8fd8\u6ca1\u6709\u6cd5\u5b50\u53bb\u5fd8\u4e86\u5979\u7684\u4e00\u98a6\u4e00\u7b11\uff0c\u5e26\u70b9\u51f6\u608d\u7684\u6e29\u67d4\u3002 \u3000\u3000\u4ece\u6b64\u6211\u6015\u591c\u665a\u3002\u6015\u60f3\u8d77\u5979\u3002\u5bf9\u6211\u6765\u8bf4\uff0c\u5df2\u6ca1\u6709\u4ec0\u4e48\u662f\u91cd\u8981\u7684\u3002\u4e0d\u6c42\u65e0\u654c\uff0c\u53ea\u6015\u68a6\u788e\u3002 \u3000\u3000\u6211\u4e0d\u80fd\u505c\u6b62\u6211\u7684\u601d\u5ff5\uff0c\u53ea\u597d\u5411\u5e08\u95e8\u8981\u6c42\u6210\u4e3a\u8ddf\u201c\u5b64\u5c71\u95e8\u201d\u7684\u201c\u9cd4\u201d\u3002\u201c\u5927\u5b64\u5c71\u6d3e\u201d\u548c\u201c\u5b64\u5c71\u95e8\u201d\u4e0d\u80fd\u5929\u5929\u660e\u7740\u6bb4\u6597\u4e0d\u4f11\uff0c\u201c\u9cd4\u201d\u5c31\u662f\u4e24\u6d3e\u4e4b\u95f4\u5bfb\u6c42\u52fe\u901a\u7684\u4e2d\u95f4\u4eba\u7269\uff0c\u4f46\u4f5c\u4e3a\u4e00\u4e2a\u201c\u9cd4\u201d\uff0c\u6d3e\u91cc\u7684\u6c49\u5b50\u90fd\u4e0d\u80af\u5e72\uff1b\u6211\u80af\uff0c\u56e0\u4e3a\u6234\u5927\u5ac2\u5c31\u662f\u201c\u5b64\u5c71\u6d3e\u201d\u7684\u201c\u9cd4\u201d\u3002 \u3000\u3000\u4e8e\u662f\u6211\u624d\u80fd\u5e38\u5e38\u63a5\u89e6\u5230\u8c22\u5927\u5ac2\uff0c\u4e0d\uff0c\u9ad8\u66fe\u82b1\uff0c\u66fe\u82b1\u3002\u95e8\u89c4\u68ee\u4e25\uff0c\u52bf\u6210\u6c34\u706b\uff0c\u6211\u9664\u4e86\u7528\u8fd9\u79cd\u529e\u6cd5\u4e89\u5f97\u4e8e\u5979\u76f8\u89c1\uff0c\u90a3\u8fd8\u6709\u4ec0\u4e48\u529e\u6cd5\uff0c\u5979\u8eab\u8fb9\u5e26\u7740\u4e24\u5e74\u524d\u751f\u4e0b\u7684\u5b69\u5b50\uff0c\u50cf\u4e00\u4e2a\u94c1\u94f8\u7684\u9992\u5934\uff0c\u8ddf\u4ed6\u7239\u4e00\u6837\uff0c\u53ea\u4e0d\u8fc7\u4ed6\u7239\u5df2\u6210\u4e86\u4e00\u5ea7\u8c6a\u58ee\u7684\u5c71\u3002\u5c82\u77e5\u89c1\u591a\u4e86\u66f4\u75db\u82e6\u3002\u6211\u50cf\u662f\u60a3\u4e86\u75be\u75c5\uff0c\u4f46\u6ca1\u6709\u75c5\u6e90\u3002\u6211\u4eec\u670d\u4e86\u6bd2\uff0c\u4f46\u4e0d\u80fd\u56e0\u6bd2\u800c\u628a\u80c3\u5272\u53bb\u3002\u6211\u53ea\u80fd\u5bf9\u82b1\u5bf9\u6708\u8bf4\u5fc3\u58f0\uff0c\u5bf9\u5979\uff1f\u6211\u53ea\u80fd\u8bf4\u725b\u5e84\u3001\u8001\u8fb9\u3001\u4e09\u5c94\u6cb3\u90fd\u662f\u54b1\u4eec\u7684\u5730\uff0c\u5343\u5c71\u4e5f\u662f\u6211\u4eec\u5927\u5b64\u5c71\u7684\u5730\u76d8\uff0c\u81f3\u4e8e\u6469\u5929\u5cad\u5219\u5f52\u5979\u90a3\u4e00\u95e8\u3002\u8fd9\u662f\u54ea\u4e00\u95e8\u7684\u8bdd\u9898\uff1f\u5230\u5934\u6765\uff0c\u89c1\u591a\u4e86\uff0c\u8d8a\u53d1\u89c9\u5f97\u5979\u6709\u4e00\u79cd\u5206\u660e\u4e0d\u77e5\u60c5\u7684\u7f8e\uff0c\u800c\u6211\u7684\u601d\u5ff5\uff0c\u5355\u8c03\u800c\u75af\u72c2\uff0c\u5c45\u7136\u4e50\u6b64\u4e0d\u75b2\u3002 \u3000\u3000\u8fd9\u6837\u4e0b\u53bb\uff0c\u6211\u5c31\u5b8c\u4e86\u3002\u8fc7\u5206\u8106\u5f31\u662f\u4e00\u79cd\u81ea\u6211\u7684\u6298\u65ad\u3002\u6211\u4e0d\u7ba1\u4e86\uff0c\u6211\u8981\u51b2\u51fa\u53bb\uff0c\u81f3\u5c11\uff0c\u51b2\u7834\u603b\u597d\u8fc7\u8ba4\u547d\u3002\u90a3\u4e00\u56de\uff0c\u53bb\u5587\u561b\u6d1e\u548c\u6768\u6811\u6e7e\u5b50\u7684\u5404\u8def\u82f1\u96c4\u5546\u8ba8\u53cd\u6251\u201c\u4e03\u5e2e\u516b\u4f1a\u4e5d\u8054\u76df\u201d\u7684\u5927\u8ba1\uff0c\u5e76\u7814\u7a76\u5982\u4f55\u6551\u56de\u7ed9\u201c\u8863\u51a0\u5e2e\u201d\u63b3\u53bb\u7684\u5e08\u59b9\u94a6\u5c0f\u4f69\u7684\u4e8b\u3002\u90a3\u665a\u6211\u4eec\u5728\u767d\u5e99\u5b50\u8fc7\u5bbf\u3002\u4e94\u89d2\u513f\u548c\u6c88\u621a\u4eb2\u9080\u6211\u6d17\u6e29\u6cc9\u3001\u5ad6\u7a91\u5b50\u3002\u6211\u53bb\u4e86\u3002\u6211\u4ece\u6765\u6ca1\u6709\u5ad6\u8fc7\uff0c\u4e8b\u5b9e\u4e0a\u4e5f\u6ca1\u8ddf\u5973\u5b50\u597d\u8fc7\u3002\u4f46\u6211\u53bb\u4e86\u3002 \u3000\u3000\u767d\u5e99\u5b50\u91cc\u6700\u6709\u540d\u7684\u74e6\u5b50\u662f\u201c\u4e00\u64ae\u7ea2\u201d\u3002\u6211\u539f\u5148\u4e0d\u77e5\u9053\uff0c\u4e94\u89d2\u5b50\u4ed6\u4eec\u544a\u8bc9\u6211\u7684\u3002\u201c\u4e00\u64ae\u7ea2\u201d\u91cc\u6700\u7ea2\u7684\u59d1\u5a18\u662f\u201c\u7389\u677f\u767d\u201d\u3002\u5927\u6982\u662f\u6211\u6b63\u7ecf\u7684\u51fa\u4e86\u540d\u5427\uff0c\u4ece\u4e0d\u53bb\u80e1\u95f9\uff0c\u4e5f\u4ece\u4e0d\u6cbe\u5973\u4eba\uff0c\u800c\u4eca\u53c8\u559d\u9189\u4e86\u8fd8\u901b\u74e6\u5b50\uff0c\u4ed6\u4eec\u90fd\u76f4\u4e86\u773c\uff0c\u628a\u201c\u7389\u677f\u767d\u201d\u201c\u8ba9\u201d\u7ed9\u4e86\u6211\u3002 \u3000\u3000\u201c\u7389\u677f\u767d\u201d\u7684\u786e\u662f\u767d\u3002\u5979\u9ad8\u3001\u7626\uff0c\u50cf\u4e00\u634f\u513f\u767d\u9762\u6761\uff0c\u773c\u8033\u5634\u5507\u9f3b\u90fd\u7cbe\u96d5\u7ec6\u523b\u51fa\u6765\u4f3c\u7684\u3002\u8863\u88f3\u91cc\u7684\u8eab\u5b50\u66f4\u767d\uff0c\u56e0\u800c\u66f4\u663e\u5f97\u5979\u5934\u53d1\u4e0d\u53ef\u601d\u8bae\u7684\u9ed1\u3002\u9ed1\u767d\u5206\u660e\u3002\u5206\u660e\u8fd9\u662f\u67d4\u5a9a\u7684\u5973\u4f53\uff0c\u53ef\u662f\u6211\u5c31\u662f\u4e0d\u80fd\u96c6\u4e2d\u3001\u65e0\u6cd5\u4e13\u5fc3\u3002\u504f\u662f\u90a3\u591c\u201c\u4e00\u64ae\u7ea2\u201d\u5ba2\u6ee1\uff0c\u4e94\u89d2\u5b50\u90a3\u574f\u5c0f\u5b50\u6324\u5230\u6211\u623f\u91cc\u6765\uff0c\u4ed6\u62e5\u7740\u53e6\u4e00\u4e2a\u5973\u5b50\u72ce\u620f\u8c03\u7b11\uff0c\u5e76\u4ee5\u4e00\u79cd\u5f3a\u66b4\u7684\u65b9\u5f0f\u6495\u788e\u90a3\u5973\u5b50\u7684\u8863\u670d\uff0c\u8fd8\u6cbe\u6cbe\u81ea\u559c\uff0c\u5f15\u4ee5\u4e3a\u96c4\u3002 \u3000\u3000\u4ed6\u4ee4\u6211\u65e0\u6cd5\u5fcd\u53d7\u3002\u201c\u7389\u677f\u767d\u201d\u5bf9\u6211\u5f88\u597d\uff0c\u5f88\u8f7b\u67d4\uff0c\u4e5f\u5f88\u8010\u5fc3\u3002\u4f46\u8fd9\u4f7f\u6211\u66f4\u6cae\u4e27\u3001\u632b\u6298\u3001\u9893\u7136\u3002\u4e94\u89d2\u513f\u968f\u65f6\u8fc7\u6765\u8868\u793a\u5173\u6ce8\uff0c\u5728\u4ed6\u5fc3\u91cc\uff0c\u4e00\u5b9a\u5728\u8c03\u7b11\u8fd9\u4e2a\u6240\u8c13\u4e00\u5e2e\u5e08\u5144\u5f1f\u91cc\u7b2c\u4e00\u597d\u624b\uff0c\u5728\u8fd9\u79cd\u60c5\u5f62\u7adf\u8fd9\u822c\u4e0d\u6d4e\u5427\uff01\u6216\u8bb8\u662f\u4ed6\u4f7f\u6211\u5206\u5fc3\uff0c\u6216\u8bb8\u4e0d\u662f\u4ed6\u3002\u6211\u53ea\u6709\u628a\u201c\u7389\u677f\u767d\u201d\u9063\u8d70\uff0c\u5f53\u7136\uff0c\u201c\u5ea6\u591c\u8d44\u201d\u6211\u662f\u7167\u6837\u5982\u6570\u4ed8\u51fa\uff0c\u8fd8\u591a\u7ed9\u4e86\u5979\u51e0\u4e24\u94f6\u5b50\u3002\u4e94\u89d2\u513f\u5927\u547c\u53ef\u60dc\uff0c\u8bf4\u6211\u4e0d\u8981\u4ed6\u53ef\u8981\u3002\u6211\u51e0\u4e4e\u6ca1\u6709\u628a\u4e94\u89d2\u513f\u6253\u4e0b\u69bb\u6765\u3002 \u3000\u3000\u4ed6\u4e00\u5b9a\u662f\u4ee5\u4e3a\u6211\u8001\u7f9e\u6210\u6012\u4e86\u5427\uff1f\u8c01\u77e5\u9053\uff01\u6211\u8fde\u591c\u6253\u9a6c\u72c2\u9a70\uff0c\u8d76\u56de\u5b64\u5c71\uff0c\u7ecf\u8fc7\u82cf\u5b50\u6c9f\uff0c\u5c31\u770b\u89c1\u4e00\u4e2a\u767d\u5f71\u6084\u7136\u800c\u7acb\u3002\u6c34\u6d41\u50cf\u5b89\u5b9a\u7684\u4e73\u6cb3\uff0c\u5728\u6708\u5149\u4e0b\u95ea\u95ea\u70c1\u70c1\u3002\u5728\u6cb3\u90a3\u7aef\u7684\u5973\u5b50\u5728\u8fdc\u5904\u964c\u751f\uff0c\u5728\u8fd1\u5904\u719f\u6089\uff0c\u5979\u662f\u9ad8\u66fe\u82b1\u3002 \u3000\u3000\u8fd9\u4e48\u665a\u4e86\uff0c\u5979\u51fa\u6765\u505a\u4ec0\u4e48\uff1f \u3000\u3000\u6cb3\u5e8a\u4e0a\u6709\u4e71\u9a6c\u8e0f\u8fc7\u7684\u75d5\u8ff9\u3002\u540e\u6765\u6211\u624d\u77e5\u9053\uff0c\u6234\u5e08\u5144\u521a\u7387\u4e00\u961f\u4eba\u9a6c\u79bb\u53bb\u3002\u4ed6\u662f\u60f3\u5728\u6211\u4eec\u53d1\u52a8\u653b\u6253\u548c\u8425\u6551\u8ba1\u5212\u4e4b\u524d\uff0c\u5148\u51fa\u5947\u5175\uff0c\u5077\u88ad\u654c\u5be8\uff0c\u6551\u51fa\u5e08\u59b9\u94a6\u5c0f\u4f69\u3002\u4ed6\u4e5f\u7684\u786e\u505a\u5230\u4e86\u8fd9\u70b9\u3002\u53ef\u662f\uff0c\u4ed6\u4e5f\u8bb8\u6c38\u8fdc\u60f3\u4e0d\u5230\uff0c\u90a3\u665a\uff0c\u4ed6\u628a\u5ac2\u5b50\uff0c\u5662\uff0c\u9ad8\u66fe\u82b1\u7559\u5728\u82cf\u5b50\u6c9f\uff0c\u90a3\u662f\u9519\u7684\u3002 \u3000\u3000\u90a3\u665a\uff0c\u6211\u559d\u4e86\u9152\uff0c\u521a\u9192\u3002\u5979\u4e5f\u559d\u4e86\u9152\uff0c\u624d\u9189\u3002 \u3000\u3000\u5979\u542c\u5230\u9a6c\u8e44\u58f0\uff0c\u5b81\u9759\u5730\u62ac\u5934\uff0c\u8fde\u7f8e\u4e3d\u4e5f\u4e03\u5b81\u516b\u9759\u7684\uff0c\u6bd4\u6708\u4eae\u768e\u6d01\uff0c\u4e5f\u6bd4\u6708\u4eae\u8083\u6740\u3002\u5979\u597d\u50cf\u5148\u770b\u5230\u5979\u548c\u6211\u7684\u6c34\u4e2d\u5012\u5f71\uff0c\u624d\u770b\u89c1\u4e86\u6211\u3002 \u3000\u3000\u8fd9\u4e00\u523b\u6211\u89c1\u5230\u4e86\u5979\u3002\u624d\u77e5\u9053\u6211\u5bf9\u5979\u5df2\u7ecf\u6b7b\u5fc3\u584c\u5730\u4e86\u3002\u5979\u7684\u773c\u8272\u51b7\u5f97\u50cf\u6697\u6740\u7684\u5315\u9996\uff0c\u70b8\u51fa\u5343\u94a7\u4e00\u53d1\u7684\u5149\u91c7\uff0c\u4f46\u5979\u8eab\u5b50\u5374\u662f\u70ed\u7684\u3002\u8fd9\u4e00\u523b\uff0c\u5979\u5c31\u662f\u6211\u7684\u523a\u5ba2\u3002\u6211\u5bf9\u5979\u62ff\u4e0d\u8d77\u3001\u653e\u4e0d\u4e0b\u3001\u79bb\u4e0d\u5f00\u3001\u5f03\u4e0d\u5f97\u3002\u5979\u662f\u6211\u7684\u6240\u7231\uff0c\u6211\u7684\u6240\u7231\u5728\u6c38\u8fdc\u3002 \u3000\u3000\u5979\u5728\u6708\u4e0b\u51b7\u5982\u5f03\u5315\u3002\u201c\u6211\u7684\u8138\u7ea2\u5417\uff1f\u201d\u5979\u95ee\u6211\u3002\u6211\u5df2\u77e5\u9053\u5979\u559d\u4e86\u9152\u3002\u201c\u6211\u7684\u8138\u70ed\u5417\uff1f\u201d\u5979\u53c8\u95ee\u3002\u6211\u70b9\u5934\u3002\u5979\u8e72\u4e0b\u53bb\u7167\u6620\u6c34\u6d41\u3002\u9ed1\u53d1\u62ab\u7740\u767d\u8863\uff0c\u4ee4\u6211\u5589\u5934\u5ffd\u611f\u5230\u5e72\u6e34\u3002\u6211\u77e5\u9053\u6211\u63a5\u4e0b\u6765\u6240\u4f5c\u6240\u4e3a\u4f1a\u5728\u4e00\u606f\u95f4\u6539\u53d8\u6211\u4e00\u751f\uff0c\u53ef\u662f\u6211\u4e0d\u7ba1\u4e86\u3002 \u3000\u3000\u6211\u7528\u624b\u5927\u529b\u5730\u62e5\u4f4f\u5979\u7684\u80a9\u3002\u5979\u7684\u80a9\u6bd4\u53d1\u8fd8\u67d4\u3002\u5979\u54fc\u4e86\u4e00\u58f0\uff0c\u50cf\u9aa8\u788e\u4e86\uff0c\u53c8\u4f3c\u5fc3\u788e\u4e86\u3002\u6211\u95ee\u5979\uff1a\u201c\u4f60\u2026\u2026\u201d\u5ffd\u7136\u95ee\u4e0d\u4e0b\u53bb\u4e86\u3002\u6211\u543b\u5979\u3002\u4eb2\u5979\u3002\u5979\u6123\u4f4f\u4e86\u3002\u5b8c\u5168\u6123\u4f4f\uff0c\u7136\u540e\u53c8\u662f\u4e00\u79cd\u5f02\u5e38\u7684\u7099\u70ed\uff0c\u4ece\u63a8\u5f00\u6211\u5230\u8fce\u5408\u6211\uff0c\u90fd\u662f\u8f7b\u67d4\u800c\u7099\u70ed\u7684\u3002 \u3000\u3000\u6211\u62e5\u4f4f\u5979\uff0c\u50cf\u62e5\u4f4f\u6240\u6709\u7684\u5e78\u798f\u3002\u6211\u4e0d\u80fd\u653e\u624b\uff0c\u56e0\u4e3a\u8fd9\u5df2\u662f\u6211\u7684\u5168\u90e8\u3002\u201c\u7130\u7130\uff0c\u6211\u4eec\u8fd9\u662f\u4f24\u4eba\u4f24\u5df1\u3002\u201d\u5979\u8bf4\u3002 \u3000\u3000\u53ef\u662f\u5979\u65e0\u6cd5\u9690\u7792\uff0c\u56e0\u4e3a\u8eab\u4f53\u7684\u8bed\u8a00\u624d\u662f\u6700\u76f4\u63a5\u7684\u8bed\u8a00\uff0c\u800c\u5979\u7684\u8eab\u4f53\u662f\u5bc2\u5bde\u7684\u3002 \u3000\u3000\u4e5f\u8bb8\u6211\u6562\u4e8e\u4ea4\u51fa\u771f\u60c5\uff0c\u6709\u672c\u4e8b\u53bb\u505a\u8fd9\u4e0d\u987e\u4e00\u5207\u7684\u4e8b\uff0c\u6211\u7528\u624b\u8bfb\u7740\u5979\uff0c\u4e00\u6000\u90fd\u662f\u871c\u610f\u3002\u53ea\u6709\u5728\u8fd9\u4e00\u523b\u6211\u786e\u77e5\u5979\u5728\u6d41\u6cea\uff0c\u4ee5\u6b64\u6765\u629a\u5e73\u6211\u4eec\u7684\u5598\u606f\u3002\u5979\u4f3c\u4e4e\u5728\u996e\u6ce3\u4e2d\u8bf4\u4e86\u51e0\u53e5\u8bdd\uff0c\u4f46\u6211\u90fd\u6ca1\u542c\u6e05\u695a\u3002 \u3000\u3000\u6211\u4e0d\u77e5\u9053\u5973\u4eba\u3002\u4f46\u8981\u4e0d\u662f\u6211\u6df1\u7231\u5979\u5230\u4e86\u4e0d\u80fd\u65ad\u81c2\u7edd\u6bd2\u7684\u5730\u6b65\uff0c\u6211\u60f3\u6211\u662f\u672a\u5fc5\u80fd\u627f\u53d7\u5979\u65f6\u800c\u8d1e\u9759\u6e29\u67d4-\u65f6\u800c\u60b2\u72c2\u5267\u70c8\u3002\u90a3\u5927\u6982\u662f\u71c3\u70e7\u7684\u96ea\u8fd8\u662f\u7ed3\u51b0\u7684\u706b\u5427\uff1f\u90a3\u4e48\u5c31\u70e7\u6b7b\u6211\u5427\uff0c\u4e0d\u7136\uff0c\u5c31\u628a\u6211\u7ed3\u6210\u5343\u5e74\u7684\u51b0\u3002 \u3000\u3000\u5973\u4eba\u603b\u662f\u4e3a\u60c5\u6240\u82e6\u7684\u3002\u5927\u6982\u6234\u51b2\u5bd2\u662f\u4e2a\u4e0d\u89e3\u98ce\u60c5\u7684\u4eba\u3002 \u3000\u3000\u751a\u81f3\u8fde\u6211\u81ea\u5df1\u90fd\u4e0d\u76f8\u4fe1\u4f1a\u6709\u90a3\u4e48\u5e78\u7684\u8fd0\u3002\u6211\u5f97\u5230\u4e86\u5979\uff0c\u66f4\u73cd\u60dc\u7740\u5979\u3002\u4e4b\u540e\uff0c\u5979\u8bf4\uff1a\u201c\u6211\u4eec\u90fd\u559d\u9189\u4e86\u3002\u201d\u8981\u4e0d\u662f\u5979\u68b3\u7406\u90a3\u4e00\u628a\u9ed1\u5982\u957f\u7011\u7684\u53d1\uff0c\u6211\u4e0d\u6562\u7f6e\u4fe1\u524d\u4e00\u523b\u5728\u63e1\u81c2\u5f2f\u91cc\u5598\u606f\u7684\u4f1a\u662f\u5979-\u9ad8\u66fe\u82b1\u3002 \u3000\u3000\u5979\u51b7\u7136\u5728\u6c34\u8fb9\u518d\u7aef\u8be6\u4e00\u773c\uff0c\u9762\u988a\u5df2\u4e0d\u518d\u9161\u7ea2\uff0c\u50cf\u6708\u591c\u91cc\u5076\u5c14\u4e34\u7167\u7684\u5029\u5973\uff0c\u7834\u6653\u65f6\u4fbf\u8981\u5316\u4f5c\u5e7d\u9b42\u4e00\u7f15\u3002 \u3000\u3000\u6211\u80fd\u518d\u89c1\u5979\u5417\uff1f\u6211\u65e2\u4e0d\u80fd\u5fd8\u8bb0\u5979\uff0c\u6211\u4e5f\u4e0d\u8981\u5979\u7684\u5fd8\u8bb0\u3002\u8fd9\u4e00\u591c\u4e4b\u540e\uff0c\u6211\u51e0\u7591\u5728\u68a6\u4e2d\uff0c\u76f4\u5230\u76f8\u601d\u53d8\u6210\u4e00\u79cd\u60ca\u4eba\u7684\u5355\u601d\u3002\u8fd9\u4f7f\u6211\u8d8a\u53d1\u80af\u5b9a\uff1a\u4e3a\u4e86\u5979\uff0c\u6211\u53ef\u4ee5\u653e\u5f03\u6bd4\u6b66\uff0c\u4e0d\u5f53\u638c\u95e8\uff0c\u751a\u81f3\u53ef\u4ee5\u8131\u79bb\u201c\u5927\u5b64\u5c71\u6d3e\u201d\uff0c\u4f34\u5979\u5230\u5929\u6daf\u6d77\u89d2\u53bb\uff0c\u4e0d\u60dc\u4e00\u751f\u9003\u4ea1\u3002 \u3000\u3000\u5982\u679c\u6234\u51b2\u5bd2\u8981\u6740\u6211\uff0c\u6211\u4e0d\u8fd8\u624b\u3002\u53ef\u662f\u5982\u679c\u4ed6\u8981\u4f24\u5bb3\u5979\uff0c\u6211\u5c31\u62d4\u9664\u4ed6\uff0c\u50cf\u94f2\u9664\u4e00\u68f5\u6321\u5728\u8def\u592e\u7684\u53e4\u6811\u3002 \u3000\u3000\u4e0d\u8981\u903c\u6211\u8fd9\u6837\u505a\u3002 \u3000\u3000\u6211\u4eec\uff01 \u3000\u3000\u8fd9\u662f\u201c\u6211\u4eec\u201d\u7684\u65f6\u4faf\u4e86\u3002 \u3000\u3000\u8c01\u90fd\u4e0d\u80fd\u5fcd\u53d7\u8fd9\u60c5\u666f\u3002\u4ed6\u4e00\u5b9a\u4f1a\u6740\u6b7b\u66fe\u82b1\u7684\u3002\u5c31\u7b97\u4ed6\u6740\u6b7b\u7684\u662f\u6211\uff0c\u66fe\u82b1\u4e5f\u6d3b\u4e0d\u4e86\u3002\u5b64\u5c71\u4e00\u8109\uff0c\u95e8\u89c4\u6781\u4e25\uff0c\u53d4\u5ac2\u4e4b\u9632\uff0c\u66f4\u4e0d\u53ef\u903e\u3002\u800c\u4eca\u4e00\u5207\u8be5\u72af\u7684\u90fd\u72af\u4e86\uff0c\u4e0d\u8be5\u72af\u7684\u4e5f\u72af\u4e86\uff0c\u53ea\u5269\u4e0b\u8840\u548c\u529b\u7684\u5bf9\u51b3\uff0c\u770b\u8c01\u8de8\u8c01\u7684\u5c38\u4f53\u8fc7\u53bb\u2026\u2026\u3002 \u3000\u3000\u6211\u8981\u6740\u4ed6\u3002 \u3000\u3000-\u50cf\u780d\u4e00\u68f5\u6811\u3002 \u3000\u3000\u5de8\u5927\u7684\u6811\u3002 \u3000\u3000-\u7528\u6211\u7684\u201c\u795e\u624b\u5927\u5288\u68fa\u201d\uff01 \u3000\u3000\u4e5f\u8bb8\u6211\u8be5\u60ed\u6127\uff0c\u4f46\u6211\u7edd\u4e0d\u540e\u6094\u3002 \u3000\u3000\u9ad8\u66fe\u82b1\u7684\u72ec\u767d\uff1a\u5973\u4eba\u662f\u4e0d\u53ef\u4ee5\u865a\u63b7\u5149\u9634\u7684 \u3000\u3000\u4ed6\u4e0d\u80fd\u6740\u4ed6\u3002 \u3000\u3000\u65e0\u8bba\u5982\u4f55\uff0c\u7130\u7130\u90fd\u4e0d\u80fd\u6740\u51b2\u5bd2\uff01 \u3000\u3000\u51b2\u5bd2\u4e0d\u8be5\u6b7b\uff0c\u8be5\u6b7b\u7684\u662f\u6211\u4eec\u3002 \u3000\u3000-\u7130\u7130\u8fd8\u6709\u6211\u3002 \u3000\u3000\u6211\u4e00\u5411\u90fd\u662f\u4e2a\u6069\u6028\u5206\u660e\u7684\u5973\u4eba\u3002\u4e00\u5411\u90fd\u662f\u3002\u8fd9\u4e00\u523b\u4e5f\u662f\u3002 \u3000\u3000\u6211\u521d\u4e0e\u51b2\u5bd2\u76f8\u8bc6\u7684\u65f6\u4faf\uff0c\u4f69\u670d\u4ed6\u5df2\u5230\u4e86\u5d07\u62dc\u7684\u5730\u6b65\u3002\u5176\u5b9e\u8c01\u90fd\u4e00\u6837\uff0c\u5b64\u5c71\u4e00\u8109-\u4e0d\u7ba1\u662f\u201c\u5927\u5b64\u5c71\u6d3e\u201d\u8fd8\u662f\u201c\u5b64\u5c71\u95e8\u201d\u7684\u5999\u9f84\u5973\u5b50\uff0c\u6709\u8c01\u80fd\u4e0d\u5bf9\u201c\u5927\u6298\u679d\u624b\u201d\u6234\u51b2\u5bd2\u82b3\u5fc3\u9ed8\u8bb8\uff1f\u5c24\u5176\u5728\u4ed6\u5b64\u8eab\u594b\u6218\uff0c\u4e03\u5ea6\u9000\u654c\u7684\u90a3\u6bb5\u5149\u8f89\u5c81\u6708\u91cc\uff0c\u4efb\u4f55\u5973\u5b50\u53ea\u8981\u7ed9\u4ed6\u770b\u4e0a\u4e00\u773c\uff0c\u5fc3\u91cc\u96be\u514d\u90fd\u4f1a\u5931\u58f0\u60ca\u547c\u3002 \u3000\u3000\u5c31\u7b97\u662f\u201c\u4e03\u5e2e\u516b\u4f1a\u4e5d\u8054\u76df\u201d\u7684\u5973\u5b50\uff0c\u53c8\u6709\u54ea\u4e2a\u4e0d\u4e3a\u6234\u51b2\u5bd2\u8fd9\u4e2a\u540d\u5b57\u800c\u5fc3\u5934\u4e00\u70ed\u8fc7\uff1f \u3000\u3000\u6211\u4e5f\u662f\u3002 \u3000\u3000-\u5728\u201c\u5b64\u5c71\u95e8\u201d\u91cc\uff0c\u6211\u7684\u201c\u5c0f\u6843\u82b1\u5251\u201d\u4e5f\u7b97\u662f\u51fa\u7c7b\u62d4\u8403\u7684\uff0c\u4f46\u6bcf\u9047\u5927\u6218\uff0c\u53ea\u8981\u662f\u6234\u5e08\u54e5\u4e00\u4e0a\u9635\uff0c\u6211\u7684\u201c\u5c0f\u6843\u82b1\u5251\u201d\u5f53\u771f\u53ea\u80fd\u6311\u6311\u82b1\uff0c\u4e0d\u80fd\u7b97\u5251\uff0c\u66f4\u4e0d\u80fd\u7b97\u662f\u5251\u6cd5\u3002 \u3000\u3000\u2026\u2026\u6234\u5e08\u54e5\u5230\u5e95\u662f\u4e2a\u600e\u4e48\u6837\u7684\u6c49\u5b50\u54ea\uff01 \u3000\u3000\u6211\u5728\u8fd9\u4e48\u60f3\u7684\u65f6\u4faf\uff0c\u7edd\u6ca1\u6709\u60f3\u5230\uff0c\u6709\u4e00\u5929\u6211\u4f1a\u662f\u4ed6\u7684\u59bb\u5b50\uff0c\u4e5f\u6c38\u8fdc\u6ca1\u6709\u60f3\u5230\uff0c\u4eca\u5929\u4f1a\u95f9\u6210\u8fd9\u6837\u5b50\u3002 \u3000\u3000\u90a3\u65f6\u5019\uff0c\u6211\u6709\u4e2a\u5e08\u59b9\u94a6\u5c0f\u4f69\uff0c\u5979\u4f69\u670d\u94a6\u7fa1\u6234\u5e08\u54e5\u4e00\u5982\u5979\u7684\u59d3\u540d\u4e00\u6837\u3002\u5979\u5e38\u5e38\u5bf9\u6211\u8c08\u8d77\uff1a\u6234\u5e08\u54e5\u600e\u6837\u600e\u6837\u2026\u2026\u6234\u5e08\u54e5\u5982\u4f55\u5982\u4f55\u2026\u2026 \u3000\u3000\u4e0d\u7ba1\u5982\u4f55\u600e\u6837\uff0c\u6211\u90fd\u662f\u4e2a\u7231\u6068\u5206\u660e\u7684\u5973\u4eba\u3002\u6211\u4eec\u95e8\u89c4\u4e25\u5cfb\uff0c\u7981\u5fcc\u7e41\u591a\uff0c\u7537\u5973\u4e4b\u9632\uff0c\u5c24\u5176\u82db\u5389\u3002\u4f46\u8c01\u8ba9\u6211\u4eec\u90fd\u662f\u540c\u4e00\u95e8\u91cc\u7684\u4eba\u5462\uff1f\u6709\u6b21\u6211\u89c1\u4ed6\u5728\u4eba\u7fa4\u91cc\u770b\u7740\u6211\u6123\u6123\u7684\uff0c\u4e5f\u4e0d\u77e5\u662f\u4e0d\u662f\u770b\u7740\u6211\u3002\u6211\u60f3\uff1a\u8981\u662f\u770b\u5230\u6211\u8fd9\u6837\u4e00\u4e2a\u7f8e\u4e3d\u5973\u5b50\u53c8\u600e\u4f1a\u8fd9\u6837\u6123\uff1f\u4e00\u65f6\u597d\u73a9\uff0c\u4fbf\u8fc7\u53bb\u9017\u4ed6\uff1a\u201c\u5582\uff0c\u4f60\u5728\u770b\u6211\uff1f\u201d \u3000\u3000\u54c8\uff01\u4ed6\u7adf\u50bb\u5927\u4e2a\u513f\u7684\u7ea2\u4e86\u8138\uff0c\u6123\u4e86\u534a\u5929\uff0c\u6211\u6211\u6211\u6211\u6211\u6211\u4e0d\u51fa\u4e00\u4e2a\u6240\u4ee5\u7136\u6765\u3002 \u3000\u3000\u6211\u89c9\u5f97\u597d\u7b11\uff0c\u5c31\u5b66\u7740\u4ed6\u7684\u58f0\u8c03\u8ddf\u4ed6\u80e1\u95f9\uff1a\u201c\u5927\u5927\u5927\u5927\u5927\u5927\u5927\u54e5\u4e07\u5c81\uff01\u201d \u3000\u3000\u4ed6\u5927\u6982\u7ed9\u6211\u5413\u50bb\u4e86\u5427\uff01\u54ea\u91cc\u8dd1\u51fa\u6765\u4e00\u4e2a\u75f4\u5973\u5b69\uff1f \u3000\u3000\u6b21\u65e5\uff0c\u6211\u5949\u547d\u53bb\u5f90\u5bb6\u5c6f\u3001\u4e07\u5bb6\u5cad\u3001\u960e\u5bb6\u5e97\u5f81\u6536\u8336\u4e0e\u9999\u836f\u7684\u4ea4\u5f15\uff0c\u4ee5\u77bb\u4eac\u5e08\u3002\u4e0d\u6599\uff0c\u5728\u534a\u5f84\u4e0a\uff0c\u6234\u5e08\u54e5\u7a81\u5730\u8df3\u51fa\u6765\uff0c\u62e6\u4f4f\u4e86\u8def\uff0c\u6211\u8fd8\u4ee5\u4e3a\u662f\u5c71\u91cc\u54ea\u4e00\u5934\u7ed9\u679c\u5b50\u5927\u6655\u4e86\u7684\u8822\u8001\u864e\uff0c\u6216\u662f\u54ea\u5ea7\u5c71\u5934\u4e0a\u7684\u54ea\u6839\u8471\u7684\u62e6\u8def\u52ab\u532a\uff0c\u5374\u4e0d\u6599\u662f\u4ed6\u3002 \u3000\u3000\u4ed6\u62e6\u53ea\u4e3a\u4e86\u8bf4\uff1a\u201c\u6211\u2026\u2026\u6211\u6211\u6211\u2026\u2026\u6211\u6628\u5929\u662f\u5728\u770b\u4f60\u3002\u201d\u4ee5\u4e00\u79cd\u8ba4\u9519\u7684\u53e3\u543b\u3002 \u3000\u3000\u4ed6\u8fd8\u53cd\u95ee\u5462\u3002\u201c\u4f60\u4e3a\u4ec0\u4e48\u8981\u53eb\u6211\u505a\u5927\u5927\u5927\u5927\u5927\u5927\u5927\u54e5\uff1f\u201d\u4ed6\u8bb0\u6027\u53ef\u771f\u597d\uff0c\u6211\u603b\u5171\u53eb\u4e86\u4e03\u4e2a\u201c\u5927\u201d\u5b57\uff0c\u4ed6\u5982\u6570\u8bb0\u4f4f\u4e86\u3002 \u3000\u3000\u7b11\u5f97\u6211\u3002 \u3000\u3000\u8fde\u6211\u540c\u884c\u7684\u5e08\u5f1f\u59b9\u4eec\uff0c\u4e5f\u7b11\u5f97\u524d\u4fef\u540e\u5408\u3002 \u3000\u3000\u6ca1\u6599\uff0c\u6211\u4eec\u5728\u90a3\u4e00\u6761\u8def\u4e0a\uff0c\u540e\u6765\u771f\u906d\u4e86\u52ab\u3002\u6765\u7684\u662f\u201c\u731b\u9b3c\u5e2e\u201d\u7684\u4e03\u540d\u597d\u624b\uff0c\u6740\u4e86\u6211\u4eec\u4e09\u4eba\uff0c\u4f24\u4e86\u6211\u4eec\u4e24\u4eba\uff0c\u5c31\u53ea\u52ab\u4e86\u6211\u53bb\u3002 \u3000\u3000\u6211\u4eec\u7684\u4eba\u9a6c\u7acb\u5373\u56de\u53bb\u5b64\u5c71\u8bf7\u6551\u5175\u3002\u8def\u4e0a\u6b63\u8d76\u4e0a\u600f\u600f\u56de\u5c71\u7684\u6234\u5e08\u54e5\u3002\u4ed6\u9a6c\u4e0a\u5355\u4eba\u5339\u9a6c\u53cd\u6251\u201c\u731b\u9b3c\u5e2e\u201d\uff0c\u8fde\u6bd9\u654c\u516d\u4eba\uff0c\u5269\u4e0b\u4e00\u4eba\uff0c\u56e0\u4e3a\u4e00\u76f4\u662f\u90a3\u4eba\u529b\u963b\u522b\u4eba\u73b7\u6c61\u6211\uff0c\u6211\u53eb\u6234\u5e08\u54e5\u4e0d\u8981\u6740\u4ed6\uff0c\u4ed6\u5c31\u9976\u4e86\u4ed6\u3002 \u3000\u3000\u4ed6\u53ef\u4e0d\u6b62\u4e00\u6b21\u6551\u4e86\u6211\u3002 \u3000\u3000\u6bcf\u6b21\u6211\u6709\u96be\uff0c\u4e0d\u7ba1\u4ed6\u5728\u54ea\u91cc\uff0c\u4e0d\u7ba1\u4ed6\u5728\u505a\u4ec0\u4e48\uff0c\u4e00\u5b9a\u90fd\u662f\u4ed6\u7b2c\u4e00\u4e2a\u98de\u9a91\u6765\u6551\u6211\u3002\u4ed6\u50cf\u4e00\u68f5\u53e4\u6811\uff0c\u6258\u7740\u6211\u5934\u4e0a\u7684\u534a\u58c1\u5c71\uff0c\u8ba9\u6211\u906e\u98ce\u907f\u96e8\uff0c\u8ba9\u6211\u6500\u9644\u7f20\u7ed5\uff0c\u8ba9\u6211\u89c9\u5f97\uff0c\u4eca\u751f\u4f55\u59a8\u5c31\u8fd9\u6837\u8fc7\uff01 \u3000\u3000\u5509\uff0c\u5c31\u662f\u8fd9\u6837\uff0c\u6211\u5728\u522b\u4eba\u7684\u8273\u7fa1\u4e2d\uff0c\u4ee5\u53ca\u94a6\u5c0f\u4f69\u7684\u5992\u6068\u91cc\uff0c\u548c\u4ed6\u6210\u4e86\u4eb2\u3002 \u3000\u3000\u4ece\u6b64\u4ee5\u540e\u6234\u51b2\u5bd2\u548c\u9ad8\u66fe\u82b1\u5c31\u8fc7\u8fd9\u5e78\u798f\u7f8e\u6ee1\u7684\u65e5\u5b50\u2026\u2026\u662f\u8fd9\u6837\u7684\u5417\uff1f\u6545\u4e8b\u90fd\u8fd9\u4e48\u8bf4\u3002\u4f20\u8bf4\u4e5f\u8fd9\u4e48\u8bf4\u3002\u53ef\u662f\uff0c\u4e8b\u5b9e\u5e76\u4e0d\u3002 \u3000\u3000\u4e5f\u8bb8\uff0c\u6234\u51b2\u5bd2\u4e0d\u662f\u8ddf\u6211\u6210\u4eb2\uff0c\u800c\u662f\u9009\u62e9\u4e86\u53ae\u6740\u505a\u4f34\u3002\u6bcf\u665a\u7761\u524d\uff0c\u6bcf\u6668\u9192\u540e\uff0c\u4ed6\u90fd\u4e0d\u5728\u6211\u8eab\u8fb9\u3002\u4ed6\u8fd8\u5728\u4e3a\u201c\u5b64\u5c71\u4e00\u8109\u201d\u7684\u524d\u666f\u548c\u5c06\u6765\u53bb\u51b2\u3001\u53bb\u649e\u3001\u53bb\u95ef\u3001\u53bb\u7ee7\u7eed\u4ed6\u7684\u4e00\u4ed7\u529f\u6210\u4e07\u9aa8\u67af\u3002\u7136\u800c\u4ed6\u7684\u529f\u672a\u6210\uff0c\u9aa8\u4e5f\u6ca1\u67af\uff0c\u53ea\u5e38\u5e26\u56de\u6765\u4e00\u8eab\u7684\u8840\u548c\u4e0d\u542d\u4e00\u58f0\u7684\u4f24\u53e3\u3002 \u3000\u3000\u539f\u6765\u4ed6\u662f\u7559\u4e0d\u4f4f\u7684\u3002\u6765\u662f\u50cf\u4e00\u4e2a\u6df7\u6c8c\uff0c\u53bb\u65f6\u5982\u4e00\u9053\u65cb\u98ce\u3002 \u3000\u3000\u6211\u6015\u51b7\u3002\u53ef\u662f\u5728\u79cb\u5929\uff0c\u4ed6\u6ca1\u6709\u7559\u4e0b\u6765\u4f34\u6211\uff0c\u6e29\u6696\u6211\u3002\u6211\u754f\u5bd2\u3002\u53ef\u662f\u4ed6\u5728\u51ac\u5929\u91cc\u4e5f\u6ca1\u6709\u6765\u8fc7\uff0c\u4ee5\u5475\u6e29\u6765\u5475\u6e29\u6211\u5df2\u7ed3\u51b0\u7684\u6307\u5c16\u3002\u6211\u5df2\u50cf\u4e00\u53ea\u6b8b\u8749\uff0c\u53ef\u662f\u4ed6\u751a\u81f3\u542c\u4e0d\u5230\u6211\u5fae\u5f31\u7684\u547c\u5524\u3002 \u3000\u3000\u521d\u65f6\u4e0d\u662f\u8fd9\u6837\u5b50\u7684\u3002\u4ed6\u5982\u706b\u5982\u837c\u7684\u94c1\u9aed\u8001\u7231\u5370\u6233\u5728\u6211\u7684\u9888\u4e0a\uff0c\u6211\u5728\u4ed6\u7684\u624b\u81c2\u91cc\u662f\u4e00\u53ea\u9c7c\u7f51\u91cc\u7684\u4e00\u53ea\u867e\u3002\u4ed6\u7528\u72c2\u70c8\u5199\u4e0b\u4e86\u6211\u7684\u7a7a\u865a\u3001\u6211\u7684\u5bc2\u5bde\u3001\u6211\u7684\u51b7\u3002\u4ed6\u90a3\u6e29\u67d4\u7684\u51f6\u608d\uff0c\u4f7f\u6211\u5feb\u4e50\u7684\u75db\u695a\u3002\u6211\u4eec\u5c31\u5728\u90a3\u65f6\u4faf\u6709\u4e86\u9e70\u9e70\u3002 \u3000\u3000\u4f46\u4e3a\u4ec0\u4e48\u4f1a\u6f14\u53d8\u6210\u8fd9\u6837\u5b50\u5462\uff1f\u6709\u4e86\u5b69\u5b50\u7684\u524d\u540e\uff0c\u6211\u5df2\u61c2\u5f97\u505a\u4e3a\u4e00\u4e2a\u5987\u4eba\u7684\u6b22\u6109\u4e86\u3002\u6211\u5e38\u5728\u72c2\u6b22\u4e2d\u8986\u8eab\u4e8e\u4ed6\uff0c\u53ef\u662f\u4ed6\u603b\u5728\u8e81\u90c1\u548c\u6cae\u9893\u4e2d\u4e8c\u9009\u5176\u4e00\u3002\u6211\u4e0d\u660e\u767d\u4e3a\u4ec0\u4e48\u4f1a\u8fd9\u6837\u3002\u91d1\u6208\u94c1\u9a6c\uff0c\u51b3\u6218\u6c5f\u6e56\uff0c\u8fd9\u6837\u4e00\u4e2a\u6c49\u5b50\uff0c\u7adf\u53ea\u80fd\u8d74\u6c99\u573a\uff0c\u800c\u4e0d\u80fd\u53bb\u9762\u5bf9\u4e00\u5f20\u5e8a\uff1f \u3000\u3000\u6211\u60f3\u4ed6\u8fd8\u662f\u75bc\u6211\u7684\u3002\u4e00\u5b9a\u662f\u6211\u505a\u5f97\u4e0d\u591f\u597d\u3002\u4f46\u5f53\u6211\u505a\u5f97\u66f4\u597d\u7684\u65f6\u4faf\u4ed6\u6012\u6c14\u51b2\u51b2\u7684\u5c31\u8d70\u4e86\uff0c\u5b81\u53ef\u5e26\u7740\u4f24\u548c\u4f24\u53e3\u56de\u6765\uff0c\u4e0d\u75db\u547c\u534a\u58f0\u3002\u90a3\u65f6\u4faf-\u90a3\u6bb5\u65e5\u5b50\u91cc\uff0c\u6211\u771f\u662f\u2026\u2026\u6211\u4ee5\u4e3a\u6211\u4e0d\u662f\u4e00\u4e2a\u5973\u4eba\u3002 \u3000\u3000\u6211\u5927\u6982\u4e0d\u662f\u4e00\u4e2a\u5973\u4eba\u5427\u3002\u6216\u8bb8\u6211\u53ea\u662f\u4e00\u4e2a\u5728\u98ce\u4e2d\u54ed\u6ce3\u7684\u5973\u5b50\uff0c\u5728\u98ce\u4e2d\u555c\u6ce3\u7684\u5973\u5b50\u3002\u6211\u4e0d\u80fd\u8bf4\u4e88\u4eba\u542c\uff0c\u53ea\u80fd\u8bf4\u4e88\u5c0f\u5c0f\u7684\u9e70\u9e70\u542c\u3002\u9e70\u9e70\u662f\u4e0d\u4f1a\u660e\u767d\u7684\uff0c\u4f46\u5c0f\u5c0f\u800c\u53ef\u601c\u7684\u4ed6\uff0c\u66fe\u7528\u4ed6\u5c0f\u5c0f\u800c\u53ef\u601c\u7684\u624b\u6765\u89e6\u6478\u4ed6\u5a18\u4eb2\u7684\u773c\u6cea\u3002\u6211\u8ddf\u9e70\u9e70\u8bf4\uff0c\u662f\u56e0\u4e3a\u51b2\u5bd2\u6bcf\u6b21\u4f1a\u6765\uff0c\u90fd\u4e0d\u770b\u6211\uff0c\u5b81\u53ef\u53bb\u770b\u9e70\u9e70\uff0c\u6402\u4ed6\u3001\u555c\u4ed6\u3001\u543b\u4ed6\u3001\u628a\u4ed6\u629b\u9ad8\u53c8\u63a5\u4f4f\uff0c\u5728\u5c0f\u5b69\u4e00\u6b21\u53c8\u4e00\u6b21\u60ca\u7b11\u58f0\u91cc\u4ed6\u50cf\u4e00\u4e2a\u7537\u5b50\u6c49\u7684\u5475\u5475\u5927\u7b11\u7740\u2026\u2026\u9e70\u9e70\u53ef\u66fe\u628a\u5a18\u7684\u8bdd\u544a\u8bc9\u4e86\u7239\uff1f \u3000\u3000-\u5b69\u5b50\u7684\u7239\uff0c\u5230\u5e95\u4f60\u751f\u6c14\u6211\u4ec0\u4e48\uff1f \u3000\u3000\u8c01\u80fd\u62d2\u7edd\u5c81\u6708\u65e0\u60c5\uff1f\u6211\u7684\u773c\u89d2\u5df2\u5f00\u59cb\u6709\u9c7c\u7b11\u7eb9\u4e86\u3002\u5c31\u522b\u8ba9\u6211\u76fc\u5230\u767d\u9996\u5427\uff01\u6211\u4eec\u4e3a\u4f55\u6e10\u6e10\u53d8\u7684\u6c89\u9ed8\uff1f\u96be\u9053\u6211\u4eec\u5df2\u65e0\u8bdd\u53ef\u8bf4\uff1f\u4f60\u5728\u5f39\u6307\u5343\u91cc\u53d6\u4eba\u5934\uff0c\u800c\u6211\u6b63\u7ea2\u989c\u5f39\u6307\u5b83\u3002\u65f6\u95f4\u603b\u662f\u63b3\u8d70\u5e0c\u671b\u7684\u52ab\u532a\uff0c\u6211\u7684\u5c81\u6708\u5df2\u5370\u5728\u7709\u95f4\u3002\u4f60\u4e0d\u662f\u8981\u6211\u8001\u65f6\u624d\u6765\u5bf9\u6211\u4f5c\u8fdf\u5230\u7684\u5173\u6ce8\u5427\uff1f\u54ce\uff0c\u5973\u4eba\u662f\u4e0d\u80fd\u865a\u63b7\u9752\u6625\u7684\u3002 \u3000\u3000\u6211\u8981\u6495\u88c2\u7a7a\u865a\uff0c\u51fb\u788e\u5bc2\u5bde\u3001\u71c3\u70e7\u51b7\u3002\u6211\u660e\u660e\u662f\u5f53\u5e74\u591a\u4eba\u8ffd\u6c42\u7684\u4e00\u4e2a\u5973\u5b50\uff0c\u4e3a\u4f55\u5374\u8ba9\u4f60\u4e0d\u5f53\u662f\u4e00\u4e2a\u5987\u4eba\uff01\u5c31\u5728\u8fd9\u822c\u5c81\u6708\u91cc\uff0c\u8bb0\u4e0d\u6e05\u9664\u5230\u5e95\u662f\u90a3\u4e00\u5929\uff0c\u6211\u89c1\u5230\u6234\u51b2\u5bd2\u5e38\u5e38\u5728\u5634\u91cc\u63d0\u8d77\uff0c\u5341\u5206\u7540\u91cd\u7684-\u6218\u7130\u7130\u3002 \u3000\u3000-\u4e00\u4e2a\u8fd9\u4e48\u7f8e\u8273\u7684\u7537\u5b50\uff0c\u5374\u6709\u8fd9\u4e48\u5fe7\u4f24\u7684\u773c\u775b\u3002 \u3000\u3000\u6211\u7b2c\u4e00\u773c\u770b\u5230\u4ed6\uff0c\u5c31\u89c9\u5f97\u4ed6\u8be5\u7a7f\u84dd\u8272\u7684\u8863\u88f3\uff0c\u5531\u8d77\u6b4c\u6765\u4e00\u5b9a\u5f88\u597d\u542c\u3002\u6211\u60f3\u5230\u4ed6\u7684\u6b4c\u58f0\u7684\u65f6\u4faf\uff0c\u4ed6\u8fd8\u6ca1\u6709\u5f00\u53e3\u8ddf\u6211\u8bf4\u4e00\u53e5\u8bdd\u3002\u7b2c\u4e8c\u4e2a\u60f3\u6cd5\u662f\uff0c\u8fd9\u6837\u4e00\u4e2a\u7537\u5b50\uff0c\u53ef\u4ee5\u63d0\u7b14\u753b\u753b\u3001\u53ef\u4ee5\u5f39\u7434\u8d4b\u8bd7\uff0c\u4f46\u4e0d\u50cf\u662f\u62ff\u5200\u6740\u4eba\u7684\u6b66\u6797\u4eba\u3002 \u3000\u3000\u4f46\u4ed6\u786e\u662f\uff01\u800c\u4e14\u8fd8\u662f\u540c\u4e3a\u4e00\u8109\u5374\u4e3a\u5bbf\u654c-\u201c\u5927\u5b64\u5c71\u6d3e\u201d\u8fd1\u5e74\u6765\u5e74\u9752\u4e00\u4ee3\u7684\u4f7c\u4f7c\u8005\uff0c\u6b66\u529f\u542c\u8bf4\u76f4\u63a5\u5a01\u80c1\u5230\u6234\u51b2\u5bd2\u7684\u6218\u7130\u7130\u3002\u4ed6\u79c0\u6c14\u3001\u65af\u6587\u3002\u4f46\u540c\u95e8\u91cc\u6b63\u6d41\u4f20\u7740\u4ed6\u6740\u654c\u5982\u4e00\u6735\u8273\u4e3d\u7684\u706b\u7130\u3002 \u3000\u3000\u8fd9\u771f\u662f\u4e00\u79cd\u6f47\u6d12\u7684\u4e0d\u5e78\u3002 \u3000\u3000\u66f4\u4e0d\u5e78\u5f97\u662f\u6211\u770b\u51fa\u4e86\u4ed6\u770b\u6211\u7684\u773c\u795e\uff0c\u4f3c\u6000\u7740\u4f24\u5fc3\u7684\u7275\u75db\uff0c\u90a3\u6b63\u662f\u6620\u7167\u7740\u6211\uff0c\u71c3\u70e7\u4e0d\u606f\u7684\u65e7\u68a6\u524d\u666f\u3002 \u3000\u3000\u4e0d\u5e78\u867d\u7136\u4e0d\u5e78\uff0c\u4f46\u4e0d\u4e00\u5b9a\u5c31\u4f1a\u53d1\u751f-\u5982\u679c\u90a3\u665a\uff0c\u51b2\u5bd2\u4e0d\u987e\u6211\u7684\u633d\u963b\uff0c\u4e00\u542c\u5230\u94a6\u5c0f\u4f69\u88ab\u201c\u8863\u51a0\u5e2e\u201d\u7684\u4eba\u63b3\u8d70\uff0c\u4ed6\u7acb\u523b\u4fbf\u8981\u53bb\u6551\u7684\u8bdd\u2026\u2026 \u3000\u3000\u6211\u4e0d\u8bb8\u3002\u6211\u77e5\u9053\u5c0f\u4f69\u7684\u4e3a\u4eba\u3002\u5979\u662f\u4e2a\u4e3a\u4e86\u7537\u4eba\u4ec0\u4e48\u4e8b\u90fd\u505a\u5f97\u51fa\u6765\u7684\u5973\u4eba\u3002\u5979\u6839\u672c\u8ddf\u201c\u8863\u51a0\u5e2e\u201d\u7684\u949f\u64d2\u3001\u949f\u6388\u6709\u4ea4\u60c5\uff0c\u662f\u4e0d\u662f\u771f\u5f97\u88ab\u4eba\u52ab\u8d70\u8fd8\u4e0d\u6653\u5f97\uff0c\u4f46\u8981\u5f15\u51b2\u5bd2\u53bb\u6551\u5979\u5012\u662f\u80af\u5b9a\u3002\u5979\u4e00\u76f4\u90fd\u5728\u5992\u5fcc\u6211\u548c\u51b2\u5bd2\u5728\u4e00\u8d77\uff0c\u5979\u60f3\u5c3d\u529e\u6cd5\u8ba9\u51b2\u5bd2\u6ce8\u610f\u5979\uff0c\u820d\u5f03\u6211\u3002 \u3000\u3000\u6211\u62e6\u4f4f\u51b2\u5bd2\u7684\u9a6c\u3002\u90a3\u5339\u67a3\u9a9d\u9a6c\u957f\u5636\u8d77\u6765\uff0c\u50cf\u5b83\u8e0f\u7740\u7684\u662f\u706b\u7684\u76d6\u5b50\uff0c\u8981\u8854\u4f4f\u81ea\u5df1\u9a6c\u5c3e\u4e00\u822c\u7684\u56de\u65cb\u8e22\u7740\u8e44\uff0c\u4e0d\u77e5\u9053\u4e3a\u4ec0\u4e48\u8fd9\u822c\u6124\u6012\u3002\u51b2\u5bd2\u95ee\u6211\uff1a\u201c\u4f60\u8981\u5e72\u4ec0\u4e48\uff1f\u201d\u6211\u8bf4\uff1a\u201c\u4e0d\u8bb8\u4f60\u53bb\u6551\u5979\u3002\u201d\u51b2\u5bd2\u70e6\u71e5\u8d77\u6765\uff0c\u5c31\u50cf\u4ed6\u80ef\u4e0b\u7684\u9a6c\uff1a\u201c\u5979\u662f\u6211\u4eec\u7684\u4eba\uff0c\u6211\u600e\u4e48\u80fd\u89c1\u6b7b\u4e0d\u6551\uff1f\uff01\u201d\u6211\u86ee\u8d77\u4e86\u5fc3\uff1a\u201c\u5979\u4f1a\u6ca1\u4e8b\u7684\u3002\u201d\u4ed6\u864e\u864e\u5730\u95ee\u6211\uff0c\u6211\u4ece\u6765\u6ca1\u89c1\u8fc7\u4ed6\u90a3\u4e48\u51f6\u8fc7\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u201d\u6211\u53ea\u80fd\u8bf4\uff1a\u201c\u5c31\u4f60\u4e0d\u77e5\u9053\u3002\u201d\u4ed6\u7528\u529b\u97ad\u9791\u4ed6\u7684\u9a6c\uff0c\u6211\u53eb\u4e86\u8d77\u6765\uff1a\u201c\u4f60\u5c31\u6487\u4e0b\u6211\u5728\u8fd9\u91cc\u4e48\uff1f\u201d\u4ed6\u5df2\u8fdc\u53bb\uff0c\u58f0\u97f3\u900f\u8fc7\u6e10\u8fdc\u7684\u8e44\u58f0\u9006\u98ce\u4f20\u8fc7\u6765\uff1a\u201c\u4f60\u6709\u6b66\u529f\uff0c\u4f60\u4f1a\u7167\u987e\u81ea\u5df1\u3002\u201d \u3000\u3000\u4e8e\u662f\u6211\u60f3\u8d77\u4ed6\u7684\u7edd\u60c5\u3002\u96be\u602a\u4ed6\u4f1a\u5192\u6b7b\u53bb\u6551\u94a6\u5c0f\u4f69\uff01\u60f3\u5230\u4ed6\u4ee5\u524d\u4e3a\u4e86\u6551\u6211\u800c\u6d41\u7684\u8840\uff0c\u800c\u4eca\u5374\u6b63\u4e3a\u53e6\u4e00\u4e2a\u5973\u5b50\u6d41\u4e86\uff0c\u6211\u5c31\u6068\u4e0d\u5f97\u8ba9\u4ed6\u56de\u6765\u7684\u65f6\u4faf\u53ea\u770b\u5230\u6c5f\u8fb9\u6211\u7684\u5c38\u4f53\u3002-\u81f3\u5c11\uff0c\u8fd9\u6837\u4e5f\u8bb8\u80fd\u6362\u6765\u4ed6\u4e00\u65f6\u7684\u4f24\u5fc3\u5427\uff1f \u3000\u3000\u8fd9\u65f6\u4faf\uff0c\u4e0a\u5929\u548c\u6708\u4eae\u5374\u504f\u504f\u8ba9\u6211\u9047\u89c1\u7130\u7130\u3002 \u3000\u3000\u6211\u6ca1\u6709\u529e\u6cd5\u53bb\u62b5\u53d7\u4ed6\u7684\u773c\u795e\uff0c\u4e3a\u4ec0\u4e48\u8fd9\u4e48\u6e05\u4fca\u7eaf\u60c5\u7684\u8138\u5b54\u5374\u6709\u8fd9\u6837\u4e00\u53cc\u5fe7\u4f24\u7684\u773c\u795e\uff1f\u5927\u6982\u662f\u4ed6\u5bf9\u6211\u7684\u76f8\u601d\u5df2\u5230\u4e86\u4e00\u5b9a\u706b\u5019\u4e4b\u6545\u5427\uff0c\u773c\u91cc\u6709\u7740\u5343\u822c\u75db\u82e6\u548c\u6068-\u6211\u5728\u4ed6\u773c\u4e2d\u7167\u51fa\u6211\u81ea\u5df1\u7684\u3002 \u3000\u3000\u4ed6\u78b0\u89e6\u6211\u7684\u65f6\u4faf\uff0c\u6211\u5df2\u5fd8\u8bb0\u4e86\u611f\u52a8\uff0c\u5fd8\u8bb0\u4e86\u611f\u89c9\u3002\u6211\u63a5\u89e6\u5230\u4ed6\u8eab\u5b50\u7684\u65f6\u4faf\uff0c\u5ffd\u7136\uff0c\u53d1\u73b0\u4ed6\u4e24\u808b\u4e4b\u4e0b\u6709\u6c14\u7a74\u9f13\u8361\uff0c\u90a3\u6b63\u662f\u7ec3\u201c\u795e\u624b\u5927\u5288\u68fa\u201d\u7684\u5f81\u5146\u3002-\u83ab\u975e\u4ed6\u2026\u2026\u201c\u795e\u624b\u5927\u5288\u68fa\u201d\u6b63\u662f\u8db3\u4ee5\u514b\u5236\u201c\u5927\u6298\u679d\u624b\u201d\u7684\u72ec\u95e8\u7edd\u62db\uff01 \u3000\u3000\u5239\u90a3\u95f4\u6211\u6709\u8fd9\u6837\u53ef\u8c2c\u7684\u60f3\u6cd5\uff0c\u5982\u679c\u6211\u968f\u4ed6\u7684\u5fc3\u613f\uff0c\u60f3\u4ed6\u8fd9\u6837\u4e00\u4e2a\u4e3a\u6211\u800c\u6d3b\u7684\u7537\u5b50\uff0c\u4ed6\u65e5\u6211\u53bb\u6c42\u4ed6\u4e0d\u8981\u8ddf\u51b2\u5bd2\u51b3\u6218\u7684\u8bdd\uff0c\u4ed6\u7406\u5e94\u4f1a\u7b54\u5e94\u6211\u7684\u5427\u2026\u2026\uff1f \u3000\u3000\u5c31\u4e3a\u4e86\u8fd9\u6837\u4e00\u4e2a\u53ef\u4e3a\u81ea\u5df1\u89e3\u8131\u7684\u85c9\u53e3\uff0c\u6211\u4eff\u4f5b\u6709\u4e86\u4e00\u5207\u7406\u7531\uff0c\u505a\u4e00\u4e2a\u6709\u53cd\u5e94\u7684\u5973\u4eba\u5427\u2026\u2026\u6211\u4ee5\u81ea\u5df1\u4e5f\u89c9\u5f97\u5403\u60ca\u7f9e\u803b\u7684\u70ed\u70c8\uff0c\u4f7f\u6211\u8ff7\u7729\u4e8e\u81ea\u6215\u4e00\u822c\u7684\u6b22\u72c2\u548c\u72c2\u6b22\u4e2d\u3002 \u3000\u3000\u201c\u505a\u51fa\u8fd9\u6837\u7684\u4e8b\u2026\u2026\u201d\u6211\u66fe\u996e\u6ce3\u7740\u8bf4\uff1a\u201c\u2026\u2026\u6211\u4eec\u90fd\u4e0d\u8981\u6d3b\u4e86\u2026\u2026\u201d\u6211\u4e0d\u77e5\u9053\u7130\u7130\u6709\u6ca1\u6709\u542c\u89c1\u3002\u4ed6\u50cf\u4e00\u4e2a\u5927\u5b69\u5b50\uff0c\u4ee5\u4f9d\u4f9d\u4e0d\u820d\u548c\u4e00\u5fc3\u5f85\u6211\u6765\u53d8\u6210\u4e00\u4e2a\u6210\u957f\u7684\u6c49\u5b50\u3002 \u3000\u3000\u7ecf\u8fc7\u8fd9\u4ef6\u4e8b\u4e4b\u540e\uff0c\u6211\u6574\u4e2a\u4eba\u50cf\u88ab\u638f\u7a7a\u4e86\u4f3c\u7684\u3002\u56de\u5230\u5bb6\u91cc\uff0c\u8fde\u9e70\u9e70\u5728\u5555\u54ed\u6211\u4e5f\u4e0d\u6562\u53bb\u62b1\u4ed6\u3002\u51b2\u5bd2\u4e00\u591c\u6ca1\u6709\u56de\u6765\u3002\u5230\u4e86\u7b2c\u4e8c\u5929\uff0c\u94a6\u5c0f\u4f69\u5374\u56de\u6765\u4e86\uff0c\u62ab\u5934\u6563\u53d1\u7684\u51b2\u4e86\u8fdb\u6765\uff0c\u5728\u6211\u9762\u524d\u5927\u9a82\u51b2\u5bd2\u4e0d\u662f\u4eba\uff0c\u800c\u4e14\u8fd8\u4e0d\u662f\u7537\u4eba\u3002 \u3000\u3000\u6211\u522e\u4e86\u5979\u4e00\u5df4\u638c\uff0c\u51b2\u5bd2\u521a\u597d\u56de\u6765\uff0c\u770b\u4e5f\u4e0d\u770b\u94a6\u5c0f\u4f69\uff0c\u53ea\u8ddf\u6211\u7528\u8bda\u631a\u5982\u8015\u5730\u7684\u58f0\u97f3\u8bf4\uff1a\u201c\u5bf9\u4e0d\u8d77\uff0c\u5979\u7684\u786e\u662f\u8bf1\u6211\u8fc7\u53bb\uff0c\u6211\u9519\u770b\u5979\u4e86\u3002\u201d \u3000\u3000\u4f46\u613f\u6211\u4e0d\u66fe\u542c\u5230\u8fd9\u5e94\u8be5\u7531\u6211\u8868\u8fbe\u7684\u6b49\u610f\u5374\u51fa\u81ea\u4ed6\u7684\u53e3\u3002\u8fd9\u6837\u53ea\u8bc1\u660e\u4e86\uff1a\u4ed6\u8ddf\u5979\u662f\u6ca1\u6709\u55f3\u6627\u7684\uff0c\u4f46\u6211\u8ddf\u7130\u7130\u5df2\u2026\u2026 \u3000\u3000\u201c\u5b64\u5c71\u95e8\u201d\u548c\u201c\u5927\u5b64\u5c71\u6d3e\u201d\u7684\u4eba\u600e\u80fd\u5bb9\u5f97\u4e0b\u6211\u4eec\uff1f\u51b2\u5bd2\u600e\u4e48\u5bb9\u5f97\u4e0b\u6211\u548c\u4ed6\uff1f\u6211\u81ea\u5df1\u5fc3\u91cc\u2026\u2026\u53c8\u600e\u5bb9\u5f97\u4e0b\u81ea\u5df1\uff01\uff01\u5929\u54ea\uff0c\u6211\u539f\u53ea\u60f3\u4f7f\u4ed6\u4f24\u4e00\u56de\u5fc3\uff0c\u7ed3\u679c\u6211\u4f24\u4e86\u8c01\uff1f \u3000\u3000\u5982\u679c\u6211\u53ea\u4e3a\u51b2\u5bd2\u4e0d\u8be5\u628a\u6211\u7684\u6df1\u60c5\u7f6e\u8bf8\u4e0d\u987e\uff0c\u800c\u4e00\u65f6\u884c\u62a5\u590d\u4e4b\u5ff5\uff0c\u5f80\u540e\u6211\u8ddf\u7130\u7130\u7684\u5077\u6b22\u53c8\u600e\u80fd\u4f5c\u4f55\u89e3\u91ca\u5462\uff1f\u554a\uff0c\u6211\u7adf\u662f\u8fd9\u6837\u4e00\u4e2a\u5987\u4eba-\u6211\u7adf\u662f\u8fd9\u6837\u4e00\u4e2a\u5fd8\u5f62\u7684\u5973\u4eba\uff01\u5728\u60e9\u7f5a\u964d\u4e34\u4e4b\u524d\uff0c\u6211\u5e94\u8be5\u5f53\u5148\u884c\u7070\u98de\u70df\u706d\uff01 \u3000\u3000\u6211\u6bcf\u8ddf\u4ed6\u597d\u4e00\u6b21\uff0c\u5c31\u60f3\uff0c\u6211\u8981\u6c42\u4ed6\u8ddf\u51b2\u5bd2\u4ea4\u624b\u65f6\u8ba4\u683d\u2026\u2026\u597d\u50cf\u8fd9\u6837\u60f3\u7740\uff0c\u5fc3\u91cc\u5c31\u4f1a\u597d\u8fc7\u4e00\u70b9\u4f3c\u7684\u3002\u6211\u4eca\u665a\u4e3b\u52a8\u53bb\u627e\u4ed6\uff0c\u5c31\u662f\u8981\u8bf4\u660e\u8fd9\u4e00\u5207\u7684\u3002\u6211\u89c9\u5f97\u5df2\u7ecf\u6709\u4eba\u5728\u7559\u610f\u6211\u4eec\u7684\u4e8b\u4e86\u3002\u6c88\u621a\u4eb2\u548c\u4e94\u89d2\u513f\u8fd9\u4e24\u4e2a\u5bb6\u4f19\uff0c\u795e\u795e\u79d8\u79d8\u4e5f\u9b3c\u9b3c\u795f\u795f\u7684\uff0c\u5e38\u5728\u4ea4\u5934\u63a5\u8033\u6307\u6307\u70b9\u70b9\u3002\u8fd9\u6837\u7684\u5173\u7cfb\u4e0d\u53ef\u518d\u7ee7\u7eed\u2026\u2026\u4eca\u665a\u4ed6\u53c8\u6765\u4e86\uff0c\u6211\u8981\u5bf9\u4ed6\u8bf4\u6e05\u695a\uff0c\u4ed6\u6b20\u6211\u7684\u60c5\uff0c\u5c31\u8fd8\u7ed9\u51b2\u5bd2\uff0c\u4ed6\u4e0d\u80fd\u65bd\u5c55\u201c\u795e\u624b\u5927\u5288\u500c\u201d\u2026\u2026\u6211\u4eec\u7edd\u4e0d\u80fd\u518d\u8fd9\u6837\u4e0b\u53bb\u3002 \u3000\u3000\u6ca1\u60f3\u5230\uff0c\u95e8\u7a81\u88ab\u8e22\u5f00\uff0c\u51b2\u5bd2\u51b2\u4e86\u8fdb\u6765\uff0c\u8fde\u540c\u4e00\u9635\u6740\u6c14\u817e\u817e\u7684\u5bd2\u610f\u3002\u7130\u7130\u9a6c\u4e0a\u62e6\u5728\u6211\u8eab\u524d\uff0c\u5411\u6211\u4e08\u592b\u51fa\u624b\uff0c\u4ee5\u4ed6\u7684\u201c\u795e\u624b\u5927\u5288\u500c\u201d\u3002 \u3000\u3000\u4e0d\u884c\uff0c\u4ed6\u4e0d\u80fd\u6740\u4ed6\u3002\u6211\u8ddf\u4ed6\u597d\uff0c\u90a3\u662f\u6211\u9519\uff0c\u6211\u8fd8\u662f\u652f\u6301\u6211\u7684\u4e08\u592b\uff0c\u6211\u7a81\u7136\u62d4\u5251\uff0c\u523a\u8fdb\u4ed6\u80cc\u540e\uff0c\u4e5f\u8bb8\u4e5f\u523a\u8fdb\u4ed6\u7684\u5fc3\u623f\u5427\uff0c\u4ed6\u5f88\u75db\u7684\u56de\u8fc7\u5934\u6765\u671b\u6211\uff0c\u5927\u6982\u6ca1\u610f\u6599\u5230\u6211\u5bf9\u4ed6\u4ee5\u8fd9\u6837\u7edd\u60c5\u7684\u7b54\u8c22\uff0c\u800c\u4e14\u6211\u4eec\u662f\u8fd9\u6837\u7684\u672a\u66fe\u6df1\u7231\u5df2\u7edd\u60c5\u3002\u4ed6\u773c\u91cc\u6d41\u9732\u51fa\u4e00\u79cd\u75db\u5fc3\u7684\u51c4\u8273\uff0c\u4f46\u5f88\u5feb\u53c8\u6342\u7740\u80f8\uff0c\u5b89\u8be6\u5730\u95ed\u4e0a\u4e86\u773c\u3001\u5e73\u548c\u5730\u901d\u53bb\u3002\u4ed6\u662f\u4f24\u5fc3\u800c\u6b7b\u7684\u5427\uff1f\u5927\u6982\u6ca1\u6709\u4eba\u50cf\u4ed6\u6b7b\u5f97\u90a3\u4e48\u4f24\u5fc3\u4e86\u5427\uff1f\u6b7b\u5f97\u50cf\u5728\u4e00\u4e2a\u4f24\u5fc3\u751c\u68a6\u4e2d\uff0c\u6240\u4ee5\u4e5f\u90a3\u4e48\u5730\u5b89\u5b81\u3002 \u3000\u3000\u5509\uff0c\u4eba\u751f\u4e0d\u5916\u662f\u5728\u5bfb\u627e\u4e00\u4e2a\u7b80\u5355\u800c\u7f8e\u597d\u7684\u7ed3\u675f\u3002\u201c\u6211\u53ea\u662f\u8981\u4f60\u4f24\u5fc3\u523a\u6fc0\uff1b\u201d\u6211\u544a\u8bc9\u50cf\u5f53\u5e74\u62e6\u8def\u65f6\u695e\u4f4f\u4e86\u4e00\u822c\u7684\u6234\u51b2\u5bd2\u8bf4\uff1a\u201c\u4f46\u4ed6\u4e0d\u53ef\u4ee5\u6740\u4f60\u3002\u4ed6\u8981\u6740\u4f60\uff0c\u6211\u5c31\u6740\u4ed6\u3002\u201d\u6211\u77e5\u9053\u5df2\u5931\u53bb\u7684\u548c\u5373\u5c06\u901d\u53bb\u7684\u90fd\u4e0d\u80fd\u518d\u4f5c\u633d\u7559\u2026\u2026\u7136\u800c\uff0c\u9e70\u9e70\u8fd8\u5728\u5e8a\u8925\u4e0a\uff0c\u5b89\u8be6\u5730\u606c\u7761\uff0c\u4ed6\u7684\u597d\u68a6\u72b9\u672a\u60ca\u9192\u5462\u2026\u2026 \u3000\u3000\u4ed6\u77e5\u9053\u4ed6\u9192\u6765\u4e4b\u540e\u5c31\u662f\u4e2a\u5931\u53bb\u5a18\u4eb2\u7684\u5b69\u5b50\u5417\uff1f \u3000\u3000\u6234\u51b2\u5bd2\u7684\u60f3\u6cd5\uff1a\u5973\u4eba\u662f\u4e0d\u53ef\u4ee5\u72ec\u7720\u7684 \u3000\u3000\u6211\u9519\u4e86\u3002"}),showOnHomepage:!0},{name:"\u56e0\u4e3a\u6211\u9519\u4e86\uff0c\u6240\u4ee5\u4ed6\u4eec\u5fc5\u987b\u6b7b\u3002",content:(0,t.jsx)(t.Fragment,{children:"-\u4e00\u4e2a\u662f\u6211\u6700\u8bc6\u91cd\u7684\u5e08\u5f1f\uff0c\u6211\u4ee5\u4e3a\u4ed6\u65e5\u540e\u80fd\u53d6\u4ee3\u6211\uff0c\u4fc3\u4f7f\u201c\u5b64\u5c71\u95e8\u201d\u548c\u201c\u5927\u5b64\u5c71\u6d3e\u201d\u7d27\u7d27\u7684\u56e2\u7ed3\u5728\u4e00\u8d77\uff0c\u518d\u4e5f\u4e0d\u4f1a\u5206\u88c2\uff0c\u518d\u4e5f\u4e0d\u5fc5\u53d7\u5916\u5f3a\u4e4b\u8fb1\u4e86\uff01 \u3000\u3000-\u4e00\u4e2a\u662f\u6211\u6240\u7231\uff0c\u6211\u6e29\u67d4\u7684\u59bb\u3002 \u3000\u3000\u6211\u4e00\u5411\u90fd\u8ba4\u4e3a\uff0c\u5b64\u5c71\u4e00\u8109\u4e0d\u8be5\u518d\u5206\u88c2\uff0c\u751a\u81f3\u654c\u5bf9\u3002\u5927\u5b64\u5c71\u6d3e\u805a\u5c45\u5728\u5927\u5b64\u5c71\u4e0a\uff0c\u81ea\u8ba4\u4e3a\u90a3\u662f\u5b97\u6d3e\u7684\u539f\u521b\u5730\uff0c\u7528\u5fc3\u4e60\u6b66\uff0c\u4e0d\u6c42\u5916\u9a9b\uff0c\u4e00\u4e2a\u5b64\u5c71\u5f1f\u5b50\u5e94\u4ee5\u6b66\u6797\u4e8b\u4e3a\u804c\u5fd7\uff0c\u4e0d\u53ef\u968f\u6ce2\u9010\u6d41\uff0c\u5e76\u51fa\u4e16\u4e3a\u4fa0\u3002\u5b64\u5c71\u95e8\u5219\u4e3b\u5f20\u4e00\u95e8\u4e00\u6d3e\u8981\u58ee\u5927\u81ea\u5f3a\uff0c\u5fc5\u987b\u5f97\u8981\u6709\u8d22\u529b\u548c\u4eba\u4e8b\u4e0a\u7684\u534f\u529b\uff0c\u624d\u80fd\u521b\u4e00\u65b0\u5c40\uff0c\u51b5\u4e14\uff0c\u4eba\u5728\u6c5f\u6e56\uff0c\u600e\u53ef\u6c42\u81ea\u6e05\u4e8e\u6d4a\uff0c\u800c\u4e0d\u53bb\u6d4a\u626c\u6e05\uff1f\u4e8e\u662f\u805a\u8d27\u4e8e\u5b64\u5c71\u5e02\uff0c\u7acb\u610f\u5165\u4e16\u4e3a\u4fa0\u3002\u5f7c\u6b64\u90fd\u662f\u4ee5\u4fa0\u9053\u81ea\u5c45\uff0c\u4f46\u5404\u6267\u5176\u662f\uff0c\u4e92\u4e0d\u76f8\u8ba9\uff0c\u53cc\u65b9\u6597\u4e86\u5341\u51e0\u5e74\uff0c\u5230\u73b0\u5728\uff0c\u8fd8\u662f\u7f20\u6218\u4e2a\u4e0d\u4f11\u3002\u6211\u751a\u81f3\u89c9\u5f97\u6709\u654c\u4eba\u6f5c\u5165\u4e86\u6211\u4eec\u5185\u90e8\uff0c\u6765\u79bb\u95f4\u5206\u5316\u6211\u4eec\uff0c\u8ba9\u6211\u4eec\u4e92\u76f8\u731c\u5fcc\u3001\u4e92\u76f8\u6b8b\u6740\uff0c\u800c\u4ed6\u4eec\u5219\u5750\u6536\u6e14\u4eba\u4e4b\u5229\u3002\u5b64\u5c71\u4e00\u8109\u59cb\u7ec8\u4e0d\u80fd\u5f3a\u5927\uff0c\u9971\u53d7\u201c\u4e03\u5e2e\u516b\u6d3e\u4e5d\u8054\u76df\u201d\u5f97\u4fb5\u63a0\uff0c\u4fbf\u662f\u804c\u662f\u4e4b\u6545\u3002 \u3000\u3000\u6211\u77e5\u9053\u7130\u7130\u5e08\u5f1f\u8d44\u8d28\u5f88\u597d\u3002\u5982\u679c\u4ed6\u6bd4\u6211\u5f3a\uff0c\u6211\u539f\u610f\u8ba9\u8d24\u3002\u4e8b\u5b9e\u4e0a\uff0c\u8fd9\u4e9b\u5e74\u6765\uff0c\u6211\u81ea\u5b64\u5c71\u5927\u6bd4\u6b66\u53d6\u4e86\u201c\u6218\u5c06\u201d\u7684\u865a\u540d\uff0c\u4ece\u6b64\uff0c\u4e0d\u662f\u4f24\u4eba\u5c31\u662f\u4e3a\u4eba\u6240\u4f24\u3002 \u3000\u3000\u6211\u4e5f\u662f\u4e2a\u6709\u8840\u6709\u8089\u7684\u4eba\uff0c\u65e2\u4e0d\u662f\u77f3\u5934\uff0c\u53c8\u4e0d\u662f\u94dc\u50cf\uff0c\u6240\u6709\u7684\u4f24\u4e5f\u662f\u4f1a\u75db\u7684\u3002\u6211\u53bb\u6551\u66fe\u82b1\u88ab\u63b3\u7684\u90a3\u4e00\u5f79\u4e4b\u540e\uff0c\u5df2\u6e10\u6e10\u89c9\u5f97\u6709\u8bb8\u591a\u4e8b\uff0c\u5df2\u5f00\u59cb\u529b\u4e0d\u4ece\u5fc3\u4e86\u2026\u2026\u3002\u6211\u591a\u60f3\u628a\u81ea\u5df1\u7684\u865a\u8854\u4ea4\u51fa\u53bb\uff0c\u7136\u540e\u5f00\u59cb\u9759\u4e0b\u6765\uff0c\u7231\u62a4\u66fe\u82b1\uff0c\u517b\u80b2\u9e70\u9e70\uff0c\u751a\u81f3\u4e0d\u60dc\u79bb\u5f00\u5b64\u5c71\uff0c\u9000\u51fa\u6c5f\u6e56\uff0c\u548c\u66fe\u82b1\u4e00\u8d77\u53bb\u6d41\u6d6a\u2026\u2026 \u3000\u3000\u53ef\u662f\u5df2\u7ecf\u6765\u4e0d\u53ca\u4e86\u3002 \u3000\u3000\u539f\u6765\u5f97\u5230\u7684\u4e0d\u7b49\u4e8e\u62e5\u6709\uff0c\u5931\u53bb\u7684\u5df2\u6765\u4e0d\u53ca\u633d\u7559\u3002 \u3000\u3000\u6211\u9519\u4e86\uff0c\u4e5f\u8bb8\uff0c\u4e94\u89d2\u513f\u867d\u7136\u4e0d\u957f\u8fdb\uff0c\u53ef\u662f\u4ed6\u6709\u4e00\u53e5\u8bdd\u6562\u60c5\u662f\u5bf9\u7684\uff1a \u3000\u3000\u201c\u5973\u4eba\u662f\u4e0d\u8be5\u72ec\u7720\u7684\u3002\u201d \u3000\u3000\u6211\u4e0d\u76f8\u4fe1\u4ed6\u7684\u8bdd\u3002\u4ed6\u4e00\u5411\u662f\u4e2a\u4e0d\u5b66\u597d\u7684\u65e0\u8d56\u3002\u53ef\u662f\u7b49\u5230\u6c88\u621a\u4eb2\u4e5f\u8fd9\u4e48\u8bf4\u7684\u65f6\u4faf\uff0c\u6211\u63cd\u4e86\u4ed6\uff0c\u4f46\u6211\u7684\u5fc3\u50cf\u6c89\u8239\u4e00\u822c\u4e00\u76f4\u6c89\u843d\u5230\u4e86\u5e95\u3002 \u3000\u3000\u6211\u80fd\u602a\u66fe\u82b1\u5417\uff1f\u6211\u81ea\u5df1\u5fc3\u91cc\u6bd4\u8c01\u90fd\u66f4\u6e05\u695a\u3002\u8fd9\u4e5d\u5e74\u6765\uff0c\u6211\u628a\u7231\u4f3c\u4e4e\u90fd\u4ece\u66fe\u82b1\u8eab\u4e0a\u8f6c\u79fb\u5230\u9e70\u9e70\u8eab\u4e0a\uff0c\u5176\u5b9e\u5979\u4e0d\u77e5\u9053\u6211\u662f\u60f3\u85c9\u5bf9\u9e70\u9e70\u7684\u7231\u6765\u4f20\u8baf\u6211\u5bf9\u5979\u7684\u7231\uff0c\u56e0\u4e3a\u6211\u53ea\u80fd\u9694\u7740\u4e00\u5343\u4e94\u767e\u91cc\u7684\u8ddd\u79bb\u6765\u770b\u8fd9\u8ff7\u4eba\u7684\u57ce\uff0c\u4f46\u5df2\u4e0d\u80fd\u518d\u53bb\u653b\u5360\u6216\u4f4f\u5165\u8fd9\u5ea7\u8ff7\u57ce\u91cc\u3002\u6bcf\u4e00\u6b21\uff0c\u5728\u6e29\u70ed\u7684\u591c\u665a\u91cc\uff0c\u5979\u8986\u8fc7\u8eab\u5b50\u7f20\u5728\u6211\u8eab\u4e0a\uff0c\u5598\u7740\u606f\u8bf4\uff1a\u201c\u9e70\u9e70\u5df2\u7ecf\u7761\u4e86\u2026\u2026\u201d\u6211\u4fbf\u89c9\u5f97\u9ed1\u6697\u662f\u4e00\u79cd\u5b9e\u4f53\uff0c\u538b\u5f97\u6211\u65e0\u6cd5\u900f\u51fa\u4e00\u53e3\u6c14\u3002\u201c\u4f60\u4e0d\u8981\u6211\u5417\u2026\u2026\uff1f\u201d\u5979\u53c8\u95ee\u3002\u6211\u4f46\u613f\u90a3\u662f\u4e00\u573a\u53ae\u6740\uff0c\u5c31\u7b97\u6218\u8840\u6362\u6218\u8840\uff0c\u5c38\u5c71\u8e0f\u5c38\u5c71\uff0c\u6211\u90fd\u80fd\u5e94\u4ed8\u3002 \u3000\u3000\u6709\u65f6\uff0c\u6211\u4e0d\u5fcd\u5fc3\u5916\u9762\u7684\u971c\u96ea\u50ac\u53d1\u5979\u5728\u623f\u91cc\u7684\u51b0\u51bb\uff0c\u60f3\u53bb\u5475\u6696\u5979\u7684\u865a\u7a7a\u3002\u53ef\u662f\uff0c\u6211\u4f9d\u7136\u65e0\u6cd5\u628a\u6211\u7684\u6696\u610f\u4f38\u5ef6\u5230\u5979\u7684\u4f53\u5185\uff0c\u6211\u4e3a\u6211\u662f\u4e00\u628a\u6e29\u70ed\u5f97\u4e86\u81ea\u5df1\u4f46\u6e29\u70ed\u4e0d\u4e86\u522b\u4eba\u7684\u706b\u3002\u6211\u60f3\uff0c\u6211\u662f\u4e00\u79cd\u6ca1\u6709\u989c\u8272\u7684\u706b\u5427\u3002\u5f53\u706b\u6ca1\u6709\u4e86\u5149\u548c\u70ed\uff0c\u90a3\u4e48\uff0c\u5927\u6982\u53ea\u5269\u4e0b\u4e86\u7edd\u671b\u5427\u3002 \u3000\u3000\u6211\u53d8\u5f97\u66b4\u8e81\uff0c\u50cf\u4e00\u53ea\u51b0\u4e0a\u7684\u8682\u8681\u3002\u6211\u8fd8\u8981\u5411\u7740\u6b22\u8273\u7684\u4eba\u524d\uff0c\u5047\u88c5\u65e0\u61be\uff0c\u88c5\u4f5c\u5f00\u5fc3\u2026\u2026\u7136\u540e\u6211\u53d1\u73b0\u66fe\u82b1\u4e5f\u4e00\u6837\u3002\u5979\u4e5f\u5728\u90a3\u4e48\u505a\u3002\u6211\u4e3a\u4e86\u8fd9\u4e00\u70b9\u5fc3\u90fd\u51bb\u4e86\uff0c\u56e0\u4e3a\u5171\u540c\u63a5\u53d7\u7684\u4e8b\u5b9e\u624d\u4f1a\u6210\u4e3a\u79d8\u5bc6\u3002\u5728\u5b81\u613f\u75db\u82e6\u4e5f\u4e0d\u613f\u9003\u907f\u548c\u5b81\u613f\u9003\u907f\u4e5f\u4e0d\u613f\u75db\u82e6\u91cc\uff0c\u6211\u9009\u62e9\u4e86\u4e00\u9762\u75db\u82e6\uff0c\u4e00\u9762\u9003\u907f\u3002 \u3000\u3000\u6211\u770b\u7740\u66fe\u82b1\u9010\u6e10\u9eef\u6de1\u4e0b\u53bb\u7684\u5bb9\u989c\u3002 \u3000\u3000-\u6211\u80fd\u505a\u4ec0\u4e48\uff1f\u96be\u9053\u7ed9\u5979\u4e00\u8bb0\u8033\u5149\uff0c\u7136\u540e\u53eb\u5979\u8fdc\u8fdc\u7684\u79bb\u5f00\u6211\uff0c\u6216\u8005\u53eb\u5979\u627e\u4e2a\u4eba\u79c1\u5954\u53bb\uff1f \u3000\u3000\u6551\u94a6\u5c0f\u4f69\uff0c\u4e5f\u8bb8\u4e5f\u662f\u6211\u7684\u4e00\u4e2a\u6551\u8d4e\u5427\uff1f\u6211\u77e5\u9053\u90a3\u5973\u5b50\u4e0d\u6000\u597d\u610f\uff0c\u4f46\u6216\u80fd\u4f7f\u66fe\u82b1\u4ece\u6b64\u79bb\u5f00\u4e86\u6211\uff0c\u653e\u5f03\u4e86\u6211\uff0c\u4e5f\u672a\u5c1d\u4e0d\u662f\u597d\u4e8b\u3002\u4e0d\u8fc7\u8fd9\u6709\u4e8e\u4e8b\u4f55\u8865\uff1f\u96be\u9053\u6211\u771f\u5f97\u653e\u5f97\u4e0b\u653e\u5f03\u5f97\u4e86\u9e70\u9e70\u5417\uff1f\u6240\u4ee5\uff0c\u5230\u4e86\u7b2c\u4e8c\u5929\uff0c\u6211\u8fd8\u662f\u6ee1\u6000\u6b49\u610f\u7684\u5411\u66fe\u82b1\u8ba4\u90a3\u8ba4\u4e0d\u4e86\u4e07\u5206\u4e4b\u4e00\u7684\u9519\u3002\u66fe\u82b1\u53ea\u662f\u6c89\u9ed8\u4e0d\u8bed\uff0c\u6ca1\u6709\u62ac\u5934\u3002 \u3000\u3000\u6211\u5b81\u613f\u53bb\u51b3\u6218\u3001\u53bb\u53d7\u4f24\u3002\u800c\u4e0d\u6562\u53bb\u9762\u5bf9\u66fe\u82b1\u90a3\u5f20\u5fe7\u4e3d\u7684\u8138\u3002\u6211\u4e0d\u8981\u5979\u7684\u8273\u4e3d\uff0c\u8fd9\u662f\u6211\u5fc3\u91cc\u4e00\u8bb0\u72c2\u558a\uff0c\u50cf\u6211\u7684\u5f71\u5b50\u4e00\u822c\u591c\u4f0f\u663c\u51fa\u3002 \u3000\u3000\u76f4\u81f3\u8fd9\u6837\u4e00\u4e2a\u665a\u4e0a- \u3000\u3000\u6211\u88ab\u5927\u5e08\u7236\u53eb\u53bb\u3002\u4ed6\u624b\u4e0a\u6446\u5f04\u7740\u4e00\u67c4\u50cf\u6bd2\u86c7\u773c\u775b\u8272\u6cfd\u7684\u5315\u9996\uff0c\u4e0d\u770b\u6211\u4e00\u773c\u5c31\u95ee\uff1a\u201c\u8981\u662f\u6709\u4e00\u4e2a\u4eba\u628a\u5341\u9053\u95e8\u89c4\u72af\u4e86\u56db\u6761\uff0c\u4f60\u600e\u4e48\u5904\u7f6e\uff1f\u201d\u6211\u5403\u4e86\u4e00\u60ca\uff0c\u201c\u56db\u6761\uff1f\u201d\u5fc3\u91cc\u8f6c\u5ff5\uff1a\u600e\u4e48\u5e08\u95e8\u91cc\u6709\u8fd9\u79cd\u4eba\uff01\u5927\u5e08\u7236\u4ecd\u7136\u4ee5\u4e00\u79cd\u4e25\u5389\u5f97\u9a6c\u4e0a\u5c31\u8981\u5f00\u5211\u5802\u7684\u8083\u6740\u8981\u6211\u56de\u7b54\u4ed6\u7684\u95ee\u9898\u3002\u6211\u53ea\u6709\u8bf4\uff1a\u201c\u4f9d\u4f8b\u5904\u6b7b\u3002\u201d\u4ed6\u53c8\u95ee\uff1a\u201c\u600e\u4e48\u5904\u6b7b\u6cd5\uff1f\u201d\u6211\u53ea\u6709\u6309\u89c4\u77e9\u56de\u7b54\uff1a\u201c\u51cc\u8fdf\u3002\u201d\u5927\u5e08\u7236\u53c8\u6ce8\u89c6\u7740\u6211\uff0c\u4ee5\u4e00\u79cd\u5947\u602a\u7684\u773c\u795e\uff0c\u6709\u70b9\u50cf\u540c\u60c5\u6216\u601c\u60af\u7684\uff0c\u4f46\u80af\u5b9a\u4e0d\u662f\u6000\u7591\u3002\u4f46\u6211\u4e0d\u6562\u95ee\u5927\u5e08\u7236\u4e3a\u4f55\u8981\u8fd9\u6837\u95ee\u6211\uff0c\u6211\u53ea\u77e5\u9053\u5927\u5e08\u7236\u5728\u5e94\u8be5\u8bf4\u8bdd\u7684\u65f6\u4faf\u5c31\u4f1a\u8bf4\u51fa\u6765\uff0c\u800c\u6211\u4e0d\u5e94\u8be5\u5728\u4e0d\u8be5\u95ee\u7684\u65f6\u4faf\u53d1\u95ee\u3002 \u3000\u3000\u53c8\u8fc7\u4e86\u51e0\u5929\uff0c\u201c\u5927\u5b64\u5c71\u6d3e\u201d\u548c\u201c\u5b64\u5c71\u95e8\u201d\u7684\u5143\u8001\u5728\u5bc6\u53d9\u3002\u8fd9\u6b21\u5546\u8ba8\u7684\u4e00\u5b9a\u662f\u5927\u4e8b\uff0c\u56e0\u4e3a\u8fde\u5927\u5e08\u7236\u590f\u4faf\u695a\u5531\u548c\u8001\u5e08\u7236\u695a\u5bfb\u9b42\u90fd\u6765\u4e86\u3002\u6211\u4e0d\u77e5\u9053\u4ed6\u4eec\u5728\u5bc6\u8bae\u4ec0\u4e48\uff0c\u6211\u4e5f\u65e0\u6cd5\u60f3\u8c61\u4ed6\u4eec\u8ba8\u8bba\u7684\u4e8b\u60c5\u7adf\u662f\u8fde\u6211\u4e5f\u4e0d\u80fd\u53c2\u4e0e\u7684\u3002 \u3000\u3000\u6700\u540e\uff0c\u4ed6\u4eec\u628a\u6211\u53eb\u4e86\u8fdb\u53bb\u3002 \u3000\u3000\u6211\u8fdb\u53bb\u7684\u65f6\u4faf\uff0c\u5176\u4ed6\u7684\u5143\u8001\u90fd\u8d70\u4e86\uff0c\u53ea\u5269\u4e0b\u4e00\u5bf9\u5bbf\u654c-\u5927\u5e08\u7236\u590f\u4faf\u695a\u5531\u548c\u8001\u5e08\u7236\u695a\u5bfb\u9b42\u3002 \u3000\u3000\u4e24\u4f4d\u8001\u4eba\u90fd\u4f59\u6012\u672a\u6d88\uff0c\u4f46\u5374\u53c8\u660e\u663e\u5730\u56e0\u4e3a\u6211\u800c\u5f3a\u6291\u4f4f\u6012\u6c14\u3002 \u3000\u3000\u7136\u540e\u4ed6\u4eec\u5c31\u5c0f\u5fc3\u7ffc\u7ffc\u5730\u544a\u8bc9\u6211\uff0c\u8fde\u72af\u201c\u56db\u6212\u201d\u7740\u7684\u540d\u5b57\uff1a \u3000\u3000\u6218\u7130\u7130\u548c\u9ad8\u66fe\u82b1\uff01 \u3000\u3000\u6211\u7684\u5e08\u5f1f\u548c\u6211\u7684\u59bb\uff01 \u3000\u3000\u201c\u4fb5\u4eba\u59bb\u5973\u201d\u3001\u201c\u8d25\u5fb7\u4e27\u884c\u201d\u3001\u201c\u52fe\u7ed3\u79c1\u901a\u201d\u3001\u201c\u7f54\u987e\u5e38\u4f26\u201d\u2026\u2026\u201c\u56db\u5927\u6212\u201d\u4ed6\u4eec\u90fd\u72af\u4e0a\u4e86\uff01\u4e24\u8001\u5df2\u641c\u96c6\u4e86\u7130\u7130\u548c\u66fe\u82b1\u5728\u4e00\u8d77\u7684\u8bc1\u636e\u3002 \u3000\u3000\u6211\u542c\u5230\u7684\u65f6\u4faf\u8eab\u5b50\u50cf\u9010\u90e8\u5206\u6b7b\u53bb\uff0c\u4f46\u66f4\u7126\u8651\u7684\u662f\u4ed6\u4eec\u4f1a\u53d7\u5230\u4ec0\u4e48\u6837\u7684\u5904\u7f6e\u3002\u4ed6\u4eec\u8981\u6211\u6740\u4e86\u7130\u7130\u548c\u66fe\u82b1\uff0c\u56e0\u4e3a\u59d1\u5ff5\uff1a\u201c\u7130\u7130\u5e74\u5c11\uff0c\u800c\u4e14\u5bf9\u5b64\u5c71\u4e00\u8109\u7acb\u8fc7\u5927\u529f\uff1b\u800c\u9ad8\u66fe\u82b1\u662f\u4f60\u7684\u59bb\u5b50\uff0c\u4f60\u52b3\u82e6\u529f\u9ad8\uff0c\u4e0d\u80fd\u4e0d\u8ba9\u4f60\u81ea\u5df1\u6765\u6536\u62fe\u8fd9\u4e2a\u6b8b\u5c40\u3002\u201d \u3000\u3000\u6211\u529b\u8a00\u66fe\u82b1\u4e4b\u6240\u4ee5\u5982\u6b64\uff0c\u5b8c\u5168\u662f\u56e0\u6211\u800c\u8d77\uff0c\u6211\u89c9\u5f97\u7130\u7130\u548c\u5979\u66f4\u5339\u914d\u2026\u2026\u5927\u5e08\u7236\u51b7\u51b7\u5730\u8bf4\uff1a\u201c\u51b2\u5bd2\uff0c\u6211\u4eec\u660e\u767d\u4f60\u7684\u5fc3\u60c5\uff0c\u4f46\u4f60\u65e0\u987b\u4e3a\u4ed6\u4eec\u5f00\u8131\uff0c\u53cd\u6b63\uff0c\u4ed6\u4eec\u662f\u6b7b\u7f6a\u96be\u9003\u4e86\u3002\u5426\u5219\uff0c\u5b64\u5c71\u4e00\u8109\uff0c\u4e5f\u6559\u4eba\u8d3b\u7b11\u5929\u4e0b\u3002\u201d\u6211\u60f3\u6323\u5f97\u4e00\u4e1d\u5e0c\u671b\u5f3a\u8c03\u7130\u7130\u548c\u66fe\u82b1\u805a\u5728\u4e00\u8d77\uff0c\u5c31\u7b97\u6709\u4e8f\u8282\u884c\uff0c\u4f46\u65e0\u8d1f\u5927\u4e49\uff0c\u4e0d\u89c1\u5f97\u5c31\u662f\u72af\u4e86\u6700\u5fcc\u8bb3\u7684\u201c\u52fe\u7ed3\u79c1\u901a\u201d\uff0c\u6211\u6015\u4e24\u4f4d\u5e08\u7236\u5bf9\u6211\u7684\u8bdd\u55e4\u4e4b\u4ee5\u9f3b\uff0c\u4e0d\u60dc\u63f4\u5f15\u4e86\u4e00\u4e2a\u773c\u524d\u7684\u4f8b\u5b50\uff1a\u201c\u4e24\u4f4d\u5e08\u7236\uff0c\u4f60\u4eec\u529b\u4e3b\u4e24\u5bb6\u5f1f\u5b50\u4e0d\u80fd\u6765\u5f80\u3002\u4f46\u4f60\u4eec\u53c8\u5171\u805a\u4e00\u5ba4\u4ee5\u5546\u5927\u8ba1\uff0c\u522b\u4eba\u53c8\u600e\u4e48\u60f3\u2026\u2026\uff1f\u201d \u3000\u3000\u6ca1\u6599\u8fd9\u8bdd\u624d\u4e00\u51fa\u53e3\uff0c\u8001\u5e08\u7236\u5df2\u4e00\u62cd\u684c\u5b50\uff0c\u684c\u88c2\u4e3a\u4e8c\uff0c\u4ed6\u5927\u559d\u9053\uff1a\u201c\u5927\u80c6\uff01\u201d\u5927\u5e08\u7236\u4e5f\u53f1\u9053\uff1a\u201c\u7adf\u6562\u7ba1\u8d77\u5e08\u7236\u4eec\u7684\u4e8b\u6765\uff01\u201d\u8001\u5e08\u7236\u8fd9\u624d\u6765\u505a\u597d\u505a\u6b79\uff0c\u201c\u6211\u4eec\u660e\u767d\u4f60\u7684\u5fc3\u610f\u2026\u2026\u5982\u679c\u4f60\u4e0d\u5fcd\u5fc3\u4e0b\u624b\uff0c\u4fbf\u7531\u6211\u4eec\u6d3e\u4eba\u6765\u6267\u884c\uff0c\u90a3\u65f6\uff0c\u53ea\u6015\u4ed6\u4eec\u4e24\u4eba\u7684\u82e6\u5b50\u53ef\u66f4\u5927\u4e86\u2026\u2026\u4f60\u53ef\u8bb0\u5f97\u72af\u4e86\u4e09\u6761\u6212\u5f8b\u4ee5\u4e0a\u7684\u53db\u5f92\u4e4b\u5904\u6b7b\u65b9\u6cd5\uff1f\u201d \u3000\u3000\u6211\u4e00\u542c\uff0c\u53ea\u80fd\u6253\u4e86\u4e00\u4e2a\u5bd2\u6817\u3002\u4e09\u5200\u516d\u6d1e\uff0c\u8702\u8682\u87ab\u8eab\uff0c\u706b\u70d9\u571f\u63a9\uff0c\u525c\u773c\u5256\u5fc3\u2026\u2026\u5927\u5e08\u7236\u4e0d\u8bf4\u8bdd\uff0c\u7136\u540e\u628a\u90a3\u67c4\u50cf\u6bd2\u86c7\u773c\u775b\u7684\u5315\u9996\u4ea4\u7ed9\u4e86\u6211\u3002\u6211\u63a5\u8fc7\u5200\u5b50\uff0c\u72b9\u5982\u7ed9\u6bd2\u86c7\u566c\u4e86\u4e00\u53e3\u3002 \u3000\u3000\u51fa\u5f97\u4e86\u95e8\uff0c\u5c31\u89c1\u4e94\u89d2\u513f\u548c\u6c88\u621a\u4eb2\u4ed6\u4eec\u8fce\u9762\u800c\u6765\uff0c\u7528\u4e00\u79cd\u592a\u6545\u610f\u88c5\u7684\u65e0\u4e8b\u7684\u795e\u60c5\u8ddf\u6211\u6253\u4e86\u4e2a\u62db\u547c\uff0c\u7136\u540e\u8d70\u5982\u5bc6\u8bae\u5ba4\u91cc\u3002\u6211\u751a\u81f3\u542c\u89c1\u5927\u5e08\u7236\u8fd9\u6837\u9ad8\u58f0\u7684\u8bf4\uff1a\u201c\u8981\u662f\u6234\u51b2\u5bd2\u4eca\u665a\u52a8\u4e0d\u4e86\u624b\uff0c\u6211\u4eec\u5c31\u66ff\u4ed6\u52a8\u624b\u3002\u4eca\u665a\u4e00\u5b9a\u8981\u4e25\u5bc6\u76d1\u89c6\uff0c\u4e0d\u53ef\u653e\u8fc7\u90a3\u5bf9\u5978\u592b\u6deb\u5987\u3002\u201d \u3000\u3000\u6211\u6000\u7740\u90a3\u628a\u5200\u5b50\uff0c\u50cf\u63e3\u4e86\u4e00\u6761\u6bd2\u86c7\u5728\u6000\u91cc\u3002\u6211\u59cb\u7ec8\u65e0\u6cd5\u4f7f\u90a3\u628a\u5315\u9996\u6e29\u70ed\u8d77\u6765\u3002\u73b0\u5728\u5230\u4e86\u8fd9\u4e2a\u5730\u6b65\uff0c\u5c40\u9762\u5df2\u65e0\u6cd5\u6536\u62fe\u4e86\u3002\u6211\u53ea\u6709\u6536\u62fe\u4e86\u4ed6\uff0c\u8fd8\u6709\u5979\u3002 \u3000\u3000\u6211\u5728\u4e00\u811a\u8e22\u5f00\u4e86\u95e8\u7684\u5239\u90a3\uff0c\u8fd8\u6ca1\u6709\u4e0b\u5f97\u4e86\u51b3\u5fc3\u8be5\u53eb\u66fe\u82b1\u548c\u7130\u7130\u8d76\u5feb\u9003\u8d70\u3001\u8fd8\u662f\u6211\u4e00\u5200\u6740\u4e86\u4ed6\u6216\u4ed6\u4eec\u3002 \u3000\u3000\u7130\u7130\u5374\u5982\u4e00\u9053\u72c2\u7130\uff0c\u4ed6\u5411\u6211\u53cd\u6251\u3002 \u3000\u3000\u8fd9\u60c5\u72b6\u903c\u5f97\u6211\u53ea\u597d\u8fce\u6218\u3002 \u3000\u3000\u6211\u5374\u6ca1\u6599\u5230\u66fe\u82b1\u4f1a\u7a81\u7136\u51fa\u624b\u3002 \u3000\u3000\u5411\u7130\u7130\u80cc\u540e\u51fa\u624b\u3002 \u3000\u3000-\u5f53\u7130\u7130\u5012\u5728\u4ed6\u81ea\u5df1\u7684\u8840\u6cca\u4e2d\u65f6\uff0c\u6211\u7684\u5315\u9996\u6296\u5f97\u8fd8\u4e0d\u5982\u6211\u7684\u5fc3\u5267\u70c8-\u6211\u5df2\u4e0d\u77e5\u5982\u4f55\u53bb\u6536\u62fe\u8fd9\u4e2a\u6b8b\u5c40\u3002 \u3000\u3000\u201c\u4f46\u613f\u6211\u6c38\u8fdc\u4e0d\u66fe\u77e5\u9053\u4f60\u7684\u79d8\u5bc6\u3002\u201d\u6211\u8fd9\u6837\u5bf9\u5979\u8bf4\u3002\u5176\u5b9e\u6211\u4e5f\u613f\u6211\u81ea\u5df1\u9192\u6765\u5728\u767e\u5e74\u4e4b\u540e\uff0c\u53ef\u4ee5\u4e0d\u5fc5\u53bb\u9762\u5bf9\u8fd9\u4e00\u5239\u90a3\u7684\u96be\u53d7\u3002 \u3000\u3000\u201c\u6211\u8fd9\u6837\u505a\uff0c\u201d\u5979\u4ee5\u4e00\u79cd\u51fa\u5947\u7684\u5e73\u9759\u3001\u5b89\u8be6\u548c\u7f8e\u4e3d\uff0c\u53bb\u7ec6\u5bdf\u548c\u6ce8\u89c6\uff0c\u76f4\u81f3\u786e\u5b9a\u4e86\u7130\u7130\u771f\u7684\u5df2\u7ecf\u6b7b\u4e86\uff0c\u5979\u624d\u8ddf\u6211\u8bf4\u8bdd\uff1a\u201c\u5f00\u59cb\u65e0\u975e\u662f\u8981\u4f60\u4f24\u5fc3\u523a\u6fc0\u3002\u201d \u3000\u3000\u5979\u8fd9\u53e5\u8bdd\u6bd4\u5979\u8bf4\u771f\u7684\u7231\u7130\u7130\u8fd8\u4ee4\u6211\u4f24\u5fc3\u3002\u201c\u4f60\u8d70\u5427-\u201d\u6211\u5411\u5979\u5927\u529b\u7684\u6325\u624b\uff0c\u4f46\u5979\u4ee5\u4e00\u79cd\u575a\u6e05\u6253\u65ad\u4e86\u6211\u7684\u8bdd\uff0c\u4ee5\u4e00\u79cd\u51c4\u5389\u7684\u575a\u6301\uff0c\u8bf4\uff1a\u201c\u6211\u4eec\u4e09\u4eba\u4e2d\uff0c\u4ed6\u662f\u6700\u65e0\u8f9c\u7684\uff0c\u4f46\u6211\u5df2\u6740\u4e86\u4ed6\uff0c\u4f60\u60f3\u6211\u4f1a\u8d70\u5417\uff1f\u201d \u3000\u3000\u7136\u540e\u4ed6\u95ee\u6211\uff1a\u201c\u4f60\u77e5\u9053\u7130\u7130\u5df2\u7ec3\u6210\u2018\u795e\u624b\u5927\u5288\u500c\u2019\u4e86\u5417\uff1f\u201d \u3000\u3000\u6211\u4e0d\u77e5\u9053\u3002\u5982\u679c\u4e0d\u662f\u6211\u5fc3\u91cc\u592a\u96be\u8fc7\uff0c\u4e3a\u8fd9\u4e00\u70b9\u6211\u81f3\u5c11\u5403\u4e00\u4e2a\u4e0d\u5c0f\u7684\u60ca\u3002 \u3000\u3000\u201c\u6240\u4ee5\u6211\u624d\u4f1a\u5411\u4ed6\u52a8\u624b\u3002\u201d\u9ad8\u66fe\u82b1\u4f3c\u7b11\u975e\u7b11\u91cc\u6cdb\u51fa\u4e00\u4e1d\u4f3c\u50b2\u975e\u50b2\uff0c\u201c\u590f\u4faf\u5927\u5e08\u7236\u548c\u695a\u8001\u5e08\u7236\u8981\u4f60\u5411\u6211\u4eec\u52a8\u624b\u7684\u5427\uff1f\u4ed6\u4eec\u8fde\u8fd9\u4e00\u70b9\u90fd\u4e0d\u544a\u8bc9\u4f60\uff0c\u8fd9\u5c45\u5fc3\u672a\u514d\u592a\u53f5\u6d4b\u4e86\u2026\u2026\u201d\u5c31\u5728\u8fd9\u65f6\u4faf\uff0c\u9e70\u9e70\u5927\u6982\u662f\u5728\u4e00\u4e2a\u68a6\u4e2d\u60ca\u9192\u5427\uff1f\u4ed6\u9192\u4e86\u8fc7\u6765\uff0c\u5ffd\u7136\u5730\uff0c\u50cf\u6253\u7ffb\u4e86\u4e00\u676f\u6c34\u4f3c\u7684\u65e0\u53ef\u6536\u62fe\u5730\u54ed\u4e86\u8d77\u6765\uff0c\u5f88\u5feb\u7684\u4fbf\u4ece\u5fae\u5f31\u7684\u54ed\u58f0\u53d8\u6210\u568e\u5555\u5927\u54ed\u3002 \u3000\u3000\u66fe\u82b1-\u6211\u7684\u59bb-\u8fc7\u53bb\u5e8a\u69bb\u90a3\u8fb9\uff0c\u85c9\u7740\u5fae\u5f31\u7684\u70db\u5149\uff0c\u5728\u67d4\u58f0\u5730\u54c4\u5b69\u5b50\u518d\u5ea6\u9163\u7761\uff0c\u8bed\u97f3\u751c\u7684\u8c61\u4efb\u4f55\u6bcd\u4eb2\u7ed9\u5979\u5b69\u5b50\u7684\u4e73\u6c34\u3002\u6211\u770b\u7740\u5979\u5fae\u4e71\u7684\u4e91\u9b13\uff0c\u7c97\u5e03\u7684\u8863\u88f3\uff0c\u8fd9\u4e00\u970e\u95f4\uff0c\u6211\u60f3\u8fc7\u53bb\u62e5\u62b1\u5979\uff0c\u7d27\u7d27\u5730\u62e5\u62b1\u5979\u548c\u5b69\u5b50\u3002 \u3000\u3000\u201c\u8bf7\u4f60\u52a8\u624b\u665a\u4e00\u70b9\u3002\u201d\u6211\u542c\u5230\u5979\u7684\u8bed\u97f3\u8fd9\u6837\u4f4e\u67d4\u7684\u4f20\u6765\uff0c\u201c\u7b49\u5b69\u5b50\u7761\u7740\u4ee5\u540e\uff0c\u4f60\u624d\u52a8\u624b\u6740\u4e86\u6211\u597d\u5417\uff1f\u8fd9\u6837\u624d\u4e0d\u4f1a\u8ba9\u5b69\u5b50\u770b\u89c1\u4ed6\u7236\u4eb2\u6740\u6b7b\u4ed6\u7684\u5a18\u4eb2\u2026\u2026\u201d \u3000\u3000\u201c\u5c31\u4e3a\u8fd9\u4e00\u70b9\uff0c\u5b69\u5b50\u7684\u7239\u2026\u2026\u201d\u5979\u8bf4\uff0c\u201c\u6c42\u4f60\uff0c\u6c42\u4f60\u52a8\u624b\u665a\u4e00\u70b9\u3002\u201d \u3000\u3000\u4f5c\u8005\u9644\u8bc6\uff1a\u5c0f\u65f6\u4faf\uff0c\u5728\u9a6c\u6765\u897f\u4e9a\uff0c\u9739\u96f3\u5dde\uff0c\u7f8e\u7f57\u5c71\u57ce\u91cc\uff0c\u542c\u5230\u4e00\u9996\u6b4c\uff0c\u540d\u5b57\u5c31\u53eb\u201c\u8bf7\u4f60\u52a8\u624b\u665a\u4e00\u70b9\u201d\uff0c\u4e00\u76f4\u5230\u957f\u5927\u4ee5\u540e\uff0c\u8fd8\u5e38\u5e38\u5728\u5fc3\u91cc\u65e0\u7531\u5730\u54fc\u8d77\u3002\u4e8e\u662f\u4e3a\u5b83\u8bbe\u60f3\u4e86\u4e00\u4e2a\u6545\u4e8b\uff0c\u4e00\u79cd\u60c5\u5883\uff0c\u5e76\u4e14\u7528\u8fd9\u9996\u6b4c\u540d\u4e3a\u7bc7\u540d\u3002 \u3000\u3000\uff08\u5168\u6587\u5b8c\uff09"}),showOnHomepage:!0}]}}]);