"use strict";(self.webpackChunkwebsite=self.webpackChunkwebsite||[]).push([[32582],{18218:(e,n,t)=>{t.r(n),t.d(n,{default:()=>a});var s=t(74848);const a=[{name:"\u7b2c\u4e00\u7ae0\u3000\u65e0\u7709\u753b\u7709",content:(0,s.jsx)(s.Fragment,{children:"\u73b0\u5728\uff0c\u662f\u9ec4\u660f\u3002 \u3000\u3000\u8fd9\u91cc\u662f\u4e2a\u5f88\u70ed\u95f9\u7684\u57ce\u5e02\uff0c\u8857\u9053\u4e0a\u6324\u6ee1\u4e86\u5404\u5f0f\u5404\u6837\u7684\u4eba\uff0c\u7537\u7684\u3001\u5973\u7684\u3001\u8001\u7684\u3001\u5c11\u7684\uff0c\u6276\u7740 \u8001\u4eba\u7684\uff0c\u62b1\u7740\u5a74\u513f\u7684\u2026\u2026\u2026 \u3000\u3000\u5927\u591a\u6570\u4eba\u770b\u6765\u90fd\u5f88\u6109\u5feb\uff0c\u56e0\u4e3a\u4ed6\u4eec\u7ecf\u8fc7\u4e00\u5929\u5de5\u4f5c\u7684\u8f9b\u52b3\uff0c\u73b0\u5728\u6b63\u7a7f\u7740\u4e7e\u51c0\u7684\u8863\u670d\uff0c\u8212\u670d \u7684\u978b\u5b50\uff0c\u56ca\u4e2d\u591a\u591a\u5c11\u5c11\u90fd\u6709\u4e9b\u81ea\u8282\u4fed\u7684\u751f\u6d3b\u4e2d\u7701\u4e0b\u6765\u7684\u94b1\uff0c\u6240\u4ee5\u4ed6\u4eec\u5df2\u7ecf\u53ef\u4ee5\u5c3d\u60c5\u6765\u4eab\u53d7\u95f2 \u6687\u7684\u4e50\u8da3\u3002 \u3000\u3000\u53e6\u4e00\u4e9b\u4eba\uff0c\u5374\u4ece\u6765\u4e0d\u77e5\u9053\u5de5\u4f5c\u7684\u8f9b\u52b3\uff0c\u81ea\u7136\u4e5f\u4e0d\u77e5\u9053\u95f2\u6687\u7684\u8da3\u5473\uff0c\u6240\u4ee5\u770b\u6765\u5c31\u6709\u4e9b\u6ca1\u7cbe \u6253\u91c7\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u4e0d\u53bb\u8015\u8018\uff0c\u5c31\u60f3\u6c42\u6536\u83b7\uff0c\u662f\u6c38\u8fdc\u4e5f\u4e0d\u4f1a\u6109\u5feb\u7684\u3002 \u3000\u3000\u8fd9\u6761\u8857\u9053\u7ea6\u4e24\u65c1\uff0c\u6709\u5404\u5f0f\u5404\u6837\u7684\u5e97\u94fa\uff0c\u6709\u7684\u5356\u6742\u8d27\uff0c\u6709\u7684\u5356\u8336\u53f6\uff0c\u6709\u7684\u5356\u8863\u670d\uff0c\u6709\u7684\u5356 \u82b1\u7c89\uff0c\u5927\u591a\u6570\u5e97\u94fa\u90fd\u5c06\u4ed6\u4eec\u6700\u597d\u7684\u8d27\u5f0f\u9648\u5217\u51fa\u6765\uff0c\u6765\u5f15\u8bf1\u8def\u4eba\u7684\u773c\u775b\u3002 \u3000\u3000\u4ed6\u4eec\u4e5f\u5728\u77a7\u7740\u8def\u4e0a\u7684\u884c\u4eba\uff0c\u90a3\u773c\u8272\u5c31\u597d\u50cf\u884c\u4eba\u77a7\u8d27\u7269\u4e00\u6837\uff0c\u8def\u4eba\u7684\u5174\u8da3\u5728\u4ed6\u4eec\u7684\u8d27\u7269\uff0c \u4ed6\u4eec\u7684\u5174\u8da3\u5374\u5728\u8def\u4eba\u7684\u94b1\u888b\u3002 \u3000\u3000\u8fd9\u4e9b\u4eba\u5f7c\u6b64\u6253\u91cf\u7740\uff0c\u5f7c\u6b64\u5fae\u7b11\u7740\uff0c\u5927\u591a\u6570\u4eba\u90fd\u5f7c\u6b64\u76f8\u8bc6\uff0c\u53ea\u6709\u4e24\u4e2a\u4eba\uff0c\u5728\u8fd9\u91cc\u662f\u5b8c\u5168\u964c \u751f\u90a3\u5c31\u662f\u80e1\u94c1\u82b1\u548c\u695a\u7559\u9999\u3002 \u3000\u3000\u695a\u7559\u9999\u548c\u80e1\u94c1\u82b1\u751a\u81f3\u8fde\u57ce\u5e02\u7684\u5730\u540d\u90fd\u4e0d\u77e5\u9053\uff0c\u4ed6\u4eec\u65e2\u6ca1\u6709\u6253\u542c\uff0c\u4e5f\u7edd\u4e0d\u5173\u5fc3\uff0c\u56e0\u4e3a\u4ed6\u4eec \u7684\u5174\u8da3\u5e76\u4e0d\u5728\u8fd9\u57ce\u5e02\u3002 \u3000\u3000\u4ed6\u4eec\u7684\u5174\u8da3\u5c31\u5728\u8fd9\u4e9b\u4eba\u7684\u8eab\u4e0a\u3002 \u3000\u3000\u81ea\u4e00\u671b\u5343\u91cc\u65e0\u4eba\u70df\u7684\u5927\u6c99\u6f20\u5f52\u6765\uff0c\u518d\u89c1\u5230\u8fd9\u4e9b\u548c\u6c14\u7684\u3001\u6109\u5feb\u7684\u3001\u5584\u826f\u7684\u4eba\uff0c\u5b9e\u5728\u6bd4\u4ec0\u9ebd \u4e8b\u90fd\u80fd\u4eca\u4ed6\u4eec\u5f00\u5fc3\u3002 \u3000\u3000\u8fd9\u70ed\u95f9\u7684\u57ce\u5e02\u6700\u70ed\u95f9\u7684\u5730\u65b9\u5c31\u662f\u8fd9\u6761\u8857\uff0c\u8fd9\u6761\u8857\u6700\u70ed\u95f9\u7684\u57ce\u5e02\u5c31\u662f\u8fd9\u5bb6\u9152\u697c\uff0c\u4ed6\u4eec\u5c31\u9009 \u4e86\u8fd9\u5730\u65b9\uff0c\u5750\u5728\u4e34\u8857\u7684\u7a97\u5b50\u65c1\uff0c\u671b\u7740\u697c\u4e0b\u8857\u9053\u4e0a\u7199\u6765\u6518\u5f80\u7684\u4eba\u7fa4\uff0c\u671b\u7740\u4eba\u4eec\u7684\u7b11\u5bb9\uff0c\u95fb\u7740\u4eba \u4eec\u7684\u547c\u5438\u3002 \u3000\u3000\u4ed6\u4eec\u5c31\u8fd9\u6837\u5750\u7740\uff0c\u8fd9\u6837\u671b\u7740\uff0c\u4e5f\u4e0d\u77e5\u671b\u4e86\u591a\u4e45\uff0c\u684c\u5b50\u4e0a\u5df2\u5806\u6ee1\u4e86\u9521\u9152\u58f6\uff0c\u9152\u58f6\u5df2\u90fd\u662f\u7a7a \u7684\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u90a3\u5f20\u88ab\u5927\u6f20\u70c8\u65e5\u6652\u5f97\u53d1\u9ed1\u7684\u7751\u4e0a\uff0c\u5df2\u900f\u51fa\u4e86\u7ea2\u5149\uff0c\u7b49\u5230\u9152\u58f6\u5df2\u5f00\u59cb\u5f80\u5730\u4e0b\u6446\u7684\u65f6 \u5019\uff0c\u4ed6\u624d\u53f9\u4e86\u53e3\u6c14\uff0c\u5583\u5583\u9053\uff1a\u201c\u6211\u73b0\u5728\u624d\u77e5\u9053\uff0c\u4e16\u4e0a\u6700\u53ef\u7231\u7684\uff0c\u5c31\u662f\u8fd9\u4e9b\u5e73\u51e1\u7684\u4eba\uff0c\u4f60\u7ec8\u65e5 \u548c\u4ed6\u4eec\u76f8\u5904\u5728\u4e00\u8d77\uff0c\u4e5f\u8bb8\u8fd8\u4e0d\u4f1a\u89c9\u5f97\u4ed6\u4eec\u6709\u4ec0\u9ebd\u53ef\u7231\uff0c\u4f46\u4f60\u82e5\u662f\u5230\u90a3\u89c1\u9b3c\u7684\u5927\u6c99\u6f20\u53bb\u4e86\u4e00 \u8d9f\uff0c\u4f60\u5c31\u4f1a\u77e5\u9053\u4e16\u4e0a\u518d\u4e5f\u6ca1\u6709\u4ec0\u9ebd\u6bd4\u4eba\u66f4\u53ef\u7231\u7684\u4e1c\u897f\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u4e86\uff0c\u7b11\u7740\u9053\uff1a\u201c\u8fd9\u4e5f\u6b63\u662f\u4f60\u53ef\u7231\u7684\u5730\u65b9\uff0c\u4e00\u4e2a\u5bf9\u4eba\u7c7b\u5982\u6b64\u70ed\u7231\u7684\u4eba\uff0c\u7edd\u4e0d\u4f1a\u662f \u574f\u86cb\uff0c\u4e00\u4e2a\u574f\u86cb\u5c31\u7edd\u4e0d\u4f1a\u6709\u4f60\u8fd9\u6837\u7684\u60f3\u6cd5\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u5927\u7b11\u9053\uff1a\u201c\u591a\u627f\u5938\u5956\uff0c\u6211\u53ea\u5e0c\u671b\u8001\u59ec\u4e5f\u80fd\u542c\u5230\u4f60\u8fd9\u53e5\u8bdd\u3002\u201d \u3000\u3000\u63d0\u8d77\u59ec\u51b0\u96c1\uff0c\u4ed6\u5f00\u6717\u7684\u7b11\u8138\u4e0a\u5ffd\u7136\u6709\u4e86\u9634\u5f71\uff0c\u8fde\u704c\u4e86\u53c1\u676f\u9152\u4e0b\u809a\uff0c\u91cd\u91cd\u62cd\u4e86\u62cd\u684c\u5b50\uff0c\u5927 \u58f0\u9053\uff1a\u201c\u6211\u771f\u4e0d\u61c2\u8fd9\u6b7b\u516c\u9e21\u4e3a\u4ec0\u9ebd\u4e0d\u80af\u548c\u54b1\u4eec\u4e00\u9f50\u8d70\uff0c\u4e3a\u4ec0\u9ebd\u8981\u56de\u5bb6\uff1f\u201d \u3000\u3000\u7559\u9999\u5fae\u7b11\u9053\uff1a\u201c\u4f60\u82e5\u77e5\u9053\u5bb6\u91cc\u6709\u4eba\u5728\u7b49\u7740\u4f60\u65f6\uff0c\u4f60\u4e5f\u4f1a\u6025\u7740\u56de\u5bb6\u7684\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u8bb8\u4e45\u6ca1\u6709\u8bf4\u8bdd\uff0c\u53c8\u704c\u4e86\u53c1\u676f\u9152\u4e0b\u53bb\uff0c\u624d\u957f\u53f9\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u65e0\u8bba\u5982\u4f55\uff0c\u4e00\u4e2a\u7537\u4eba\u82e5 \u77e5\u9053\u4ed6\u7684\u5bb6\u91cc\u968f\u65f6\u90fd\u6709\u4eba\u5728\u7b49\u7740\u4ed6\uff0c\u60f3\u5ff5\u4ed6\uff0c\u90a3\u5b9e\u5728\u662f\u4ef6\u4ee4\u4eba\u6109\u5feb\u7684\u4e8b\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u9053\uff1a\u201c\u4f46\u6700\u91cd\u8981\u7684\uff0c\u8fd8\u662f\u4ed6\u5fc3\u91cc\u5fc5\u5b9a\u8981\u6709\u4e2a\u503c\u5f97\u4ed6\u6000\u5ff5\u7684\u4eba\uff0c\u5426\u5219\u4ed6\u7684\u5bb6\u5c31\u7b97 \u662f\u4e16\u4e0a\u6700\u7f8e\u4e3d\u7684\u5730\u65b9\uff0c\u4f60\u5c31\u7b97\u7528\u97ad\u5b50\u53bb\u8d76\u4ed6\uff0c\u4ed6\u4e5f\u4e0d\u4f1a\u56de\u53bb\u7684\u3002\u201d \u3000\u3000\u4ed6\u867d\u7136\u8fd8\u5728\u7b11\u7740\uff0c\u4f46\u7b11\u5bb9\u770b\u6765\u5374\u5df2\u6709\u4e9b\u6c88\u91cd\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u7728\u4e86\u7728\u773c\u775b\uff0c\u7b11\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u53c8\u60f3\u8d77\u4e86\u84c9\u513f\u5979\u4eec\uff0c\u662f\u9ebd\uff1f\u201d \u3000\u3000\u4ed6\u4e0d\u7b49\u695a\u7559\u9999\u56de\u7b54\uff0c\u5c31\u53c8\u63a5\u7740\u9053\uff1a\u201c\u5176\u5b9e\u5979\u4eec\u65e2\u5df2\u56de\u6765\u4e86\uff0c\u4f60\u6839\u672c\u5c31\u7528\u4e0d\u7740\u518d\u4e3a\u5979\u4eec\u62c5 \u5fc3\uff0c\u5c31\u51ed\u5979\u4eec\u53c1\u4e2a\u4eba\uff0c\u5357\u4e03\u5317\u516d\u5341\u53c1\u7701\uff0c\u53c8\u6709\u8c01\u6562\u52a8\u5979\u4eec\u4e00\u6839\u5934\u53d1\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u53ea\u6709\u82e6\u7b11\uff0c\u80e1\u94c1\u82b1\u4e5f\u4e0d\u8bf4\u8bdd\u4e86\uff0c\u56e0\u4e3a\u4ed6\u5df2\u77a7\u89c1\u6709\u4e2a\u9752\u8863\u5c11\u5e74\u6b63\u5728\u5411\u4ed6\u4eec\u8fd9\u8fb9\u8d70\u8fc7 \u6765\u3002 \u3000\u3000\u8fd9\u5c11\u5e74\u672c\u6765\u5c31\u5750\u5728\u4ed6\u4eec\u65c1\u8fb9\u4e00\u5f20\u684c\u5b50\u4e0a\u7684\uff0c\u4eba\u957f\u5f97\u4e0d\u4f46\u5f88\u82f1\u4fca\uff0c\u800c\u4e14\u770b\u6765\u5f88\u65af\u6587\uff0c\u5f88\u79c0 \u6c14\uff0c\u7a7f\u7684\u8863\u7740\u867d\u7136\u5e76\u4e0d\u5341\u5206\u534e\u4e3d\uff0c\u4f46\u526a\u88c1\u5f97\u5374\u6781\u5408\u8eab\uff0c\u8d28\u6599\u4e5f\u5f88\u9ad8\u8d35\uff0c\u663e\u7136\u662f\u5f88\u6709\u6559\u517b\u7684\u4e16 \u5bb6\u5b50\u5f1f\u3002 \u3000\u3000\u8fd9\u6837\u7684\u4eba\uff0c\u65e0\u8bba\u8d70\u5230\u90a3\u91cc\uff0c\u90fd\u4e00\u5b9a\u4f1a\u60f9\u4eba\u6ce8\u610f\u7684\uff0c\u4f55\u51b5\u4ed6\u8eab\u65c1\u8fd8\u6709\u4e2a\u975e\u5e38\u7f8e\u4e3d\u7684\u59bb\u5b50\u3002 \u3000\u3000\u695a\u7559\u9999\u548c\u80e1\u94c1\u82b1\u4e5f\u65e9\u5df2\u6ce8\u610f\u5230\u8fd9\u592b\u59bb\u4e24\u4eba\u4e86\uff0c\u4ed6\u4eec\u5728\u559d\u7740\u9152\u65f6\uff0c\u8fd9\u592b\u59bb\u4e24\u4eba\u4e5f\u5728\u559d\u7740\uff0c \u4ed6\u4eec\u7684\u9152\u867d\u7136\u559d\u5f97\u4ee4\u4eba\u5403\u60ca\uff0c\u8fd9\u592b\u59bb\u4e24\u4eba\u559d\u7684\u7adf\u4e5f\u4e0d\u5c11\uff0c\u4e08\u592b\u559d\u9152\u65f6\uff0c\u59bb\u5b50\u5c45\u7136\u80fd\u966a\u7740\u4ed6\uff0c \u80e1\u94c1\u82b1\u65e9\u5c31\u89c9\u5f97\u7fa1\u6155\u5f97\u5f88\u3002 \u3000\u3000\u73b0\u5728\u8fd9\u5c11\u5e74\u5c45\u7136\u629b\u4e0b\u4ed6\u7684\u59bb\u5b50\u8d70\u8fc7\u6765\uff0c\u80e1\u94c1\u82b1\u6b63\u4e0d\u77e5\u4ed6\u662f\u4e3a\u4e86\u4ec0\u9ebd\uff0c\u9752\u886b\u5c11\u5e74\u5374\u5df2\u8d70\u5230 \u4ed6\u9762\u524d\uff0c\u62b1\u62f3\u5fae\u7b11\u9053\uff1a\u201c\u5c0f\u5f1f\u672c\u4e0d\u6562\u8fc7\u6765\u6253\u6270\u4e8c\u4f4d\u559d\u9152\u7684\u96c5\u5174\uff0c\u4f46\u89c1\u5230\u4e24\u4f4d\u8fd9\u6837\u7684\u597d\u9152\u91cf\uff0c \u5374\u53c8\u5fcd\u4e0d\u4f4f\u8981\u8fc7\u6765\u8bf7\u6559\uff0c\u4f46\u671b\u4e24\u4f4d\u83ab\u8981\u602a\u7f6a\u624d\u597d\u3002\u201d \u3000\u3000\u7231\u8d4c\u94b1\u7684\u4eba\uff0c\u5c31\u7b97\u8fde\u88e4\u5b50\u90fd\u8f93\u5149\u4e86\uff0c\u4e5f\u8fd8\u662f\u559c\u6b22\u522b\u4eba\u8bf4\u4ed6\u8d4c\u5f97\u7cbe\u3001\u8d4c\u5f97\u597d\uff1b\u7231\u559d\u9152\u7684 \u4eba\uff0c\u66f4\u6ca1\u6709\u4e00\u4e2a\u4e0d\u559c\u6b22\u522b\u4eba\u8bf4\u4ed6\u9152\u91cf\u597d\u7684\u3002\u4f55\u51b5\u8fd9\u5c11\u5e74\u81ea\u5df1\u9152\u91cf\u4e5f\u4e0d\u9519\uff0c\u8fd9\u79cd\u8bdd\u4ece\u4ed6\u5634\u91cc\u8bf4 \u51fa\u6765\uff0c\u81ea\u7136\u66f4\u4ee4\u4eba\u542c\u7740\u5f00\u5fc3\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u65e9\u5df2\u7ad9\u4e86\u8d77\u6765\uff0c\u5927\u7b11\u9053\uff1a\u201c\u56db\u6d77\u4e4b\u5185\u7686\u5144\u5f1f\u4e5f\uff0c\u4f60\u80af\u8fc7\u6765\uff0c\u5c31\u662f\u4f60\u77a7\u5f97\u8d77\u54b1\u4eec\uff0c \u54b1\u4eec\u82e5\u8fd8\u8981\u602a\u4f60\uff0c\u90a3\u5c31\u7b80\u76f4\u4e0d\u662f\u4e1c\u897f\u4e86\u3002\u201d \u3000\u3000\u9752\u886b\u5c11\u5e74\u7b11\u9053\uff1a\u201c\u5c0f\u5f1f\u82e5\u975e\u65e9\u5df2\u770b\u51fa\u4e24\u4f4d\u662f\u8c6a\u8fc8\u4e0d\u7f81\u7684\u4fa0\u58eb\uff0c\u4e5f\u4e07\u4e07\u4e0d\u6562\u8fc7\u6765\u7684\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u5ffd\u7136\u6c88\u4e0b\u4e86\u8138\uff0c\u6b63\u8272\u9053\uff1a\u201c\u4f60\u672c\u6765\u5c31\u4e0d\u8be5\u8fc7\u6765\u7684\u3002\u201d \u3000\u3000\u9752\u886b\u5c11\u5e74\u521a\u6014\u4e86\u6014\uff0c\u80e1\u94c1\u82b1\u5df2\u63a5\u7740\u9053\uff1a\u201c\u4f60\u82e5\u60f3\u627e\u54b1\u4eec\u559d\u9152\uff0c\u53eb\u54b1\u4eec\u8fc7\u53bb\u5c31\u662f\uff0c\u600e\u9ebd\u80fd \u5c06\u5ac2\u592b\u4eba\u4e00\u4e2a\u4eba\u7559\u5728\u90a3\u8fb9\u684c\u5b50\u4e0a\uff0c\u8fd9\u81f3\u5c11\u8be5\u5148\u7f5a\u4f60\u53c1\u676f\u3002\u201d \u3000\u3000\u9752\u886b\u5c11\u5e74\u6841\u638c\u7b11\u9053\uff1a\u201c\u4e24\u4f4d\u82e5\u80af\u79fb\u9a7e\u8fc7\u53bb\uff0c\u5c31\u7b97\u7f5a\u5c0f\u5f1f\u53c1\u5341\u676f\u4e5f\u6ca1\u5173\u7cfb\u3002\u201d \u3000\u3000\u53c1\u676f\u9152\u4e0b\u809a\uff0c\u80e1\u94c1\u82b1\u5df2\u548c\u8fd9\u5c11\u5e74\u79f0\u5144\u9053\u5f1f\u8d77\u6765\u3002 \u3000\u3000\u695a\u7559\u9999\u867d\u6ca1\u6709\u80e1\u94c1\u82b1\u8fd9\u9ebd\u5bb9\u6613\u5c31\u80fd\u548c\u522b\u4eba\u4ea4\u670b\u53cb\uff0c\u5374\u4e5f\u4e0d\u662f\u4e2a\u53e4\u602a\u5b64\u50fb\u7684\u4eba\uff0c\u4f55\u51b5\u8fd9\u5c11 \u5e74\u592b\u59bb\u4e24\u4eba\uff0c\u53c8\u5b9e\u5728\u4ee4\u4eba\u89c9\u5f97\u613f\u610f\u548c\u4ed6\u4eec\u4eb2\u8fd1\u3002 \u3000\u3000\u8fd9\u5c11\u5e74\u4e0d\u4f46\u98ce\u5ea6\u597d\uff0c\u9152\u91cf\u597d\uff0c\u800c\u4e14\u53e3\u624d\u4e5f\u597d\uff0c\u4ed6\u7684\u59bb\u5b50\u86fe\u7709\u6de1\u626b\uff0c\u4e0d\u65bd\u8102\u7c89\uff0c\u66f4\u7f8e\u5f97\u4e0d \u5e26\u4e1d\u6beb\u70df\u706b\u6c14\u3002 \u3000\u3000\u53ea\u4e0d\u8fc7\u7709\u5b87\u95f4\u603b\u50cf\u662f\u5e26\u7740\u53c1\u5206\u5fe7\u90c1\uff0c\u8138\u8272\u4e5f\u82cd\u767d\u5f97\u4e0d\u592a\u6b63\u5e38\uff0c\u7adf\u50cf\u662f\u5728\u751f\u75c5\uff0c\u800c\u4e14\u75de\u5f97 \u8fd8\u4e0d\u4f46\u8fd9\u79cd\u75c5\u6001\u7684\u7f8e\uff0c\u5374\u6700\u8ff7\u4eba\u3002 \u3000\u3000\u9152\u697c\u4e0a\u5341\u4e2a\u4eba\u4e2d\uff0c\u5012\u6709\u4e5d\u4e2a\u4eba\u7684\u773c\u775b\u662f\u5728\u77aa\u7740\u5979\u7684\u3002 \u3000\u3000\u53ea\u8981\u5979\u773c\u6ce2\u4e00\u8f6c\uff0c\u56db\u5ea7\u7537\u4eba\u4eec\u7684\u773c\u775b\u90fd\u53d1\u4e86\u76f4\uff0c\u82e5\u8fd8\u6709\u4eba\u4e0d\u77a7\u5979\uff0c\u90a3\u4eba\u5fc5\u5b9a\u5df2\u9189\u5f97\u4eba\u4e8b \u4e0d\u77e5\u3002 \u3000\u3000\u8fd9\u9752\u886b\u5c11\u5e74\u7adf\u6beb\u4e0d\u5728\u610f\uff0c\u522b\u4eba\u8fd9\u9ebd\u6837\u77a7\u4ed6\u7684\u59bb\u5b50\uff0c\u4ed6\u975e\u4f46\u4e0d\u751f\u6c14\uff0c\u53cd\u800c\u50cf\u662f\u89c9\u5f97\u5f88\u9ad8 \u5174\u3002 \u3000\u3000\u6700\u5947\u602a\u7684\u662f\uff0c\u8fd9\u592b\u59bb\u4e24\u4eba\u770b\u6765\u867d\u90fd\u5f88\u65af\u6587\u79c0\u6c14\uff0c\u751a\u81f3\u53ef\u4ee5\u8bf4\u662f\u5f31\u4e0d\u7981\u98ce\uff0c\u4f46\u4e00\u53cc\u773c\u775b\u5374 \u662f\u795e\u5149\u5145\u8db3\uff0c\u660e\u5982\u79cb\u6c34\u3002 \u3000\u3000\u695a\u7559\u9999\u77e5\u9053\u53ea\u6709\u5185\u529f\u6781\u6df1\u7684\u4eba\uff0c\u624d\u4f1a\u6709\u8fd9\u6837\u7684\u773c\u795e\uff0c\u8fd9\u592b\u59bb\u4e24\u4eba\u65e0\u7591\u662f\u6b66\u529f\u6781\u9ad8\u660e\u7684\u4eba \u7269\u3002 \u3000\u3000\u4f46\u4ed6\u4eec\u65e0\u8bba\u8a00\u8c08\u548c\u4e3e\u52a8\uff0c\u5374\u53c8\u504f\u504f\u4e0d\u5e26\u534a\u5206\u6c5f\u6e56\u6c14\uff0c\u65e0\u8bba\u600e\u9ebd\u770b\uff0c\u4e5f\u7edd\u4e0d\u50cf\u662f\u6b66\u6797\u4e2d \u4eba\u3002 \u3000\u3000\u695a\u7559\u9999\u4e5f\u4e0d\u7981\u8d8a\u6765\u8d8a\u89c9\u5f97\u8fd9\u4e24\u4eba\u6709\u8da3\u4e86\u3002 \u3000\u3000\u5bf9\u522b\u4eba\u7684\u59bb\u5b50\uff0c\u4ed6\u81ea\u7136\u4e0d\u4fbf\u77a7\u5f97\u592a\u4ed4\u7ec6\uff0c\u4f46\u6b64\u523b\u8fd9\u5c11\u5e74\u6b63\u5411\u80e1\u94c1\u82b1\u9891\u9891\u529d\u9152\uff0c\u4ed6\u7684\u59bb\u5b50 \u4e5f\u5782\u7740\u5934\u5728\u8f7b\u8f7b\u54b3\u55fd\u3002 \u3000\u3000\u706f\u5149\u659c\u659c\u7167\u8fc7\u6765\uff0c\u6b63\u597d\u7167\u5728\u5979\u7684\u8138\u4e0a\u3002 \u3000\u3000\u695a\u7559\u9999\u7684\u76ee\u5149\uff0c\u4e5f\u548c\u706f\u5149\u540c\u65f6\u843d\u5728\u5979\u8138\u4e0a\u3002 \u3000\u3000\u8fd9\u51e0\u4e4e\u662f\u4e00\u5f20\u6beb\u65e0\u7455\u75b5\u7684\u9669\uff0c\u8138\u4e0a\u7684\u8f6e\u5ed3\u548c\u7ebf\u6761\uff0c\u7b80\u76f4\u5b8c\u7f8e\u5f97\u548c\u4e00\u4ef6\u7cbe\u5fc3\u7684\u96d5\u523b\u4e00\u6837\u3002 \u3000\u3000\u4f46\u8fd9\u5f20\u79c0\u7f8e\u7684\u8138\u4e0a\uff0c\u7adf\u7f3a\u5c11\u4e86\u6837\u4e1c\u897f\u3002 \u3000\u3000\u4ece\u695a\u7559\u9999\u8fd9\u65b9\u5411\u770b\u8fc7\u53bb\uff0c\u6070\u5de7\u53ef\u4ee5\u5f88\u6e05\u695a\u5730\u770b\u5230\u5979\u7684\u53cc\u7709\uff0c\u4f46\u5979\u7adf\u7136\u662f\u6ca1\u6709\u7709\u6bdb\u7684\uff0c\u5979 \u7684\u7709\u6bdb\u7adf\u5b8c\u5168\u662f\u753b\u4e0a\u53bb\u7684\u3002 \u3000\u3000\u695a\u7559\u9999\u8fde\u547c\u5438\u90fd\u505c\u4f4f\u4e86\u3002 \u3000\u3000\u201c\u753b\u7709\u9e1f\u201d\uff1f\u8fd9\u7f8e\u4e3d\u7684\u5c11\u5987\u96be\u9053\u5c31\u662f\u753b\u7709\u9e1f\uff1f \u3000\u3000\u5728\u8fd9\u4e00\u5239\u90a3\u95f4\uff0c\u79d8\u8c37\u4e2d\u90a3\u4e9b\u5c11\u5973\u4eec\u7684\u5c38\u8eab\u5ffd\u7136\u53c8\u51fa\u73b0\u5728\u695a\u7559\u9999\u773c\u524d\uff0c\u6bcf\u4e00\u4e2a\u4eba\u90fd\u6b7b\u5f97\u90a3 \u9ebd\u60e8\uff0c\u6bcf\u4e00\u4e2a\u4eba\u8138\u4e0a\u7709\u6bdb\u90fd\u5df2\u88ab\u4eba\u524a\u53bb\u2026\u2026\u8fd9\u96be\u9053\u5c31\u662f\u56e0\u4e3a\u5979\u81ea\u5df1\u6ca1\u6709\u7709\u6bdb\uff0c\u6240\u4ee5\u5979\u6bcf\u6740\u6b7b \u4e00\u4e2a\u5973\u4eba\u65f6\uff0c\u90fd\u5148\u5c06\u5979\u4eec\u7684\u7709\u6bdb\u524a\u5149\uff1f \u3000\u3000\u695a\u7559\u9999\u53ea\u77a7\u4e86\u4e00\u773c\uff0c\u5c31\u7acb\u523b\u62ac\u8d77\u5934\uff0c\u90a3\u9752\u886b\u5c11\u5e74\u5df2\u5fae\u7b11\u7740\u5411\u4ed6\u4e3e\u676f\uff0c\u695a\u7559\u9999\u4e5f\u4e3e\u8d77\u9152 \u676f\uff0c\u5fae\u7b11\u9053\uff1a\u201c\u5c0f\u5f1f\u5df2\u53e8\u6270\u4e86\u5144\u53f0\u8bb8\u591a\u676f\u4e86\uff0c\u5374\u8fde\u5144\u53f0\u7684\u5c0a\u59d3\u5927\u540d\u8fd8\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u5927\u7b11\u9053\uff1a\u201c\u4e0d\u9519\u4e0d\u9519\uff0c\u6211\u53ea\u987e\u559d\u5f97\u75db\u5feb\uff0c\u5374\u5c06\u8fd9\u4ef6\u4e8b\u5fd8\u4e86\uff0c\u8fd9\u5b9e\u5728\u8be5\u7f5a\u53c1\u676f\u3002\u201d \u3000\u3000\u9752\u886b\u5c11\u5e74\u7b49\u4ed6\u559d\u5b8c\u4e86\u53c1\u676f\u9152\uff0c\u624d\u7b11\u7740\u9053\uff1a\u201c\u5c0f\u5f1f\u674e\u7389\u51fd\u2026\u2026\u2026\u201d \u3000\u3000\u4ed6\u8bdd\u8fd8\u672b\u8bf4\u5b8c\uff0c\u90a3\u5c11\u5987\u7adf\u4e5f\u4e3e\u676f\u7b11\u9053\uff1a\u201c\u4e24\u4f4d\u4e3a\u4f55\u4e0d\u95ee\u6211\u7684\u540d\u5b57\u5462\uff1f\u96be\u9053\u56e0\u4e3a\u6211\u662f\u4e2a\u5973 \u4eba\uff1f\u8fd8\u662f\u56e0\u4e3a\u5973\u4eba\u5ac1\u4e86\u4eba\u5f8c\uff0c\u5c31\u4e0d\u8be5\u518d\u6709\u540d\u5b57\u4e86\u9ebd\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u77a7\u4e86\u695a\u7559\u9999\u4e00\u773c\uff0c\u7b11\u9053\uff1a\u201c\u770b\u6765\u54b1\u4eec\u53c8\u8be5\u7f5a\u53c1\u676f\u4e86\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u7b11\u9053\uff1a\u201c\u8d31\u5185\u67f3\u65e0\u7709\uff0c\u4e24\u4f4d\u83ab\u770b\u5979\u597d\u50cf\u5f31\u4e0d\u7981\u98ce\uff0c\u5176\u5b9e\u5979\u4e0d\u4f46\u813e\u6c14\u548c\u7537\u4eba\u4e00\u6837\uff0c \u6253\u8d77\u67b6\u6765\uff0c\u4e5f\u7edd\u4e0d\u4f1a\u8f93\u7ed9\u7537\u4eba\u7684\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u54e6\uff1a\u60f3\u4e0d\u5230\u5927\u5ac2\u8fd8\u662f\u4f4d\u5973\u4e2d\u8c6a\u6770\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u5ae3\u7136\u9053\uff1a\u201c\u5176\u5b9e\u6211\u672c\u6765\u8fde\u540d\u5b57\u4e5f\u548c\u7537\u4eba\u4e00\u6837\uff0c\u53ea\u4e0d\u8fc7\u5c0f\u7684\u65f6\u5019\u751f\u4e86\u573a\u5927\u75c5\uff0c\u867d\u7136 \u6ca1\u6b7b\uff0c\u4f46\u7709\u6bdb\u5374\u6389\u5149\u4e86\u2026\u2026\uff1a\u6211\u73b0\u5728\u7684\u7709\u6bdb\u662f\u753b\u4e0a\u53bb\u7684\uff0c\u4e24\u4f4d\u96be\u9053\u770b\u4e0d\u51fa\u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u672c\u4ee5\u4e3a\u5979\u4e00\u5b9a\u8981\u5c06\u8fd9\u4ef6\u4e8b\u6781\u529b\u9690\u7792\uff0c\u8c01\u77e5\u5979\u7adf\u81ea\u5df1\u8bf4\u4e86\u51fa\u6765\uff0c\u695a\u7559\u9999\u4e0d\u7981\u53c8\u89c9\u5f97 \u5f88\u610f\u5916\u3002 \u3000\u3000\u53ea\u542c\u674e\u7389\u51fd\u9053\uff1a\u201c\u73b0\u5728\u8be5\u8f6e\u5230\u5c0f\u5f1f\u8bf7\u6559\u4e24\u4f4d\u7684\u5927\u540d\u4e86\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u6211\u59d3\u80e1\uff0c\u53eb\u80e1\u94c1\u82b1\uff0c\u4ed6\u2026\u2026\u2026\u201d \u3000\u3000\u695a\u7559\u9999\u6b63\u4e0d\u77e5\u662f\u5426\u5e94\u8be5\u8ba9\u4ed6\u8bf4\u4e0b\u53bb\uff0c\u5c31\u5728\u8fd9\u65f6\uff0c\u7adf\u5ffd\u7136\u6709\u4e2a\u4eba\u76f4\u51b2\u4e86\u8fc7\u6765\uff0c\u6307\u7740\u695a\u7559\u9999 \u5927\u53eb\u9053\uff1a\u201c\u5404\u4f4d\u53ef\u77a7\u89c1\u4e86\u9ebd\uff0c\u8fd9\u4f4d\u5c31\u662f\u540d\u6ee1\u5929\u4e0b\u7684\u695a\u7559\u9999\uff0c\u695a\u9999\u5e05\uff0c\u5404\u4f4d\u6709\u5e78\u80fd\u89c1\u5230\u695a\u9999\u5e05 \u7684\u771f\u9762\u76ee\uff0c\u5b9e\u5728\u90fd\u5e94\u8be5\u7ad9\u8d77\u6765\u559d\u4e00\u676f\u3002\u201d \u3000\u3000\u4ed6\u55d3\u5b50\u5c31\u50cf\u662f\u5356\u72d7\u76ae\u5bb5\u836f\u7684\uff0c\u8fd9\u9ebd\u6837\u76f4\u7740\u5589\u5499\u4e00\u56b7\uff0c\u6ee1\u697c\u7684\u9152\u5ba2\u90fd\u5403\u4e86\u4e00\u60ca\uff0c\u867d\u7136\u6709\u4e9b \u4eba\u6839\u672c\u4e0d\u77e5\u9053\u695a\u9999\u5e05\u662f\u4f55\u8bb8\u4eba\u4e5f\uff0c\u4f46\u53ea\u8981\u662f\u5728\u6c5f\u6e56\u4e0a\u8dd1\u8dd1\u7684\u4eba\uff0c\u542c\u5230\u695a\u7559\u9999\u8fd9\u540d\u5b57\uff0c\u9762\u4e0a\u90fd \u4e0d\u7981\u53d8\u4e86\u989c\u3002 \u3000\u3000\u6700\u5403\u60ca\u7684\u4eba\uff0c\u81ea\u7136\u8fd8\u662f\u695a\u7559\u9999\u81ea\u5df1\u3002 \u3000\u3000\u53ea\u89c1\u8fd9\u4eba\u84dd\u886b\u7070\u88e4\uff0c\u7528\u9ed1\u5e03\u624e\u7740\u88e4\u811a\uff0c\u5374\u655e\u5f00\u4e86\u8863\u895f\uff0c\u5de6\u8fb9\u592a\u9633\u7a74\u4e0a\uff0c\u8d34\u7740\u5757\u91d1\u94b1\u818f \u836f\uff0c\u770b\u6765\u6b63\u662f\u4e2a\u6807\u51c6\u7684\u6d41\u6c13\u5730\u75de\uff0c\u8fd9\u53e5\u8bdd\u56b7\u5b8c\u4e86\uff0c\u5c45\u7136\u8f6c\u8eab\u5c31\u8981\u8d70\uff0c\u695a\u7559\u9999\u8fd8\u6c88\u5f97\u4f4f\u6c14\uff0c\u80e1 \u94c1\u82b1\u5374\u5df2\u4e00\u628a\u62c9\u4f4f\u4ed6\u8180\u5b50\uff0c\u7b11\u563b\u563b\u9053\uff1a\u201c\u670b\u53cb\u8d35\u59d3\u5440\uff1f\u600e\u4f1a\u8ba4\u5f97\u695a\u7559\u9999\u7684\uff1f\u201d \u3000\u3000\u8fd9\u4eba\u8fd8\u60f3\u6323\u8131\u4ed6\u7684\u624b\uff0c\u4f46\u80e1\u94c1\u82b1\u8f7b\u8f7b\u4e00\u7528\u529b\uff0c\u4ed6\u5934\u4e0a\u5df2\u75bc\u5f97\u76f4\u5192\u6c57\u73e0\u5b50\uff0c\u54a7\u7740\u5634\u7b11\u9053\uff1a \u201c\u5c0f\u7684\u53ea\u662f\u4e2a\u5356\u818f\u836f\u7684\uff0c\u600e\u9ebd\u4f1a\u8ba4\u5f97\u695a\u7559\u9999\u8fd9\u6837\u7684\u6c5f\u6e56\u9ad8\u4eba\uff0c\u8fd9\u4e0d\u8fc7\u662f\u6709\u4eba\u7ed9\u4e86\u5c0f\u7684\u5341\u4e24\u94f6 \u5b50\uff0c\u53eb\u5c0f\u4eba\u6765\u8fd9\u91cc\u56b7\u4e00\u56b7\u7684\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u77e5\u9053\u4ed6\u8fd9\u8bdd\u8bf4\u7684\u4e0d\u5047\uff0c\u56e0\u4e3a\u5c31\u51ed\u4ed6\u8fd9\u70b9\u672c\u4e8b\uff0c\u60f3\u8ba4\u8bc6\u695a\u7559\u9999\u4e5f\u4e0d\u53ef\u80fd\uff0c\u695a\u7559\u9999\u5df2 \u76b1\u7740\u7709\u95ee\u9053\uff1a\u201c\u662f\u8c01\u7ed9\u4e86\u4f60\u5341\u4e24\u94f6\u5b50\uff0c\u53eb\u4f60\u6765\u7684\uff1f\u201d \u3000\u3000\u8fd9\u5927\u6c49\u82e6\u7740\u8138\u9053\uff1a\u201c\u90a3\u4eba\u8bf4\u662f\u695a\u9999\u5e05\u7684\u670b\u53cb\uff0c\u5c0f\u4eba\u4e5f\u672b\u77a7\u6e05\u4ed6\u7684\u6a21\u6837\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u77aa\u773c\u9053\uff1a\u201c\u4f60\u96be\u9053\u662f\u778e\u5b50\u4e0d\u6210\uff1f\u201d \u3000\u3000\u8fd9\u5927\u6c49\u9053\uff1a\u201c\u4ed6\u5c06\u5c0f\u4eba\u62c9\u5230\u4e00\u4e2a\u9ed1\u9edd\u9edd\u7684\u89d2\u843d\u91cc\uff0c\u53c8\u80cc\u7740\u5149\uff0c\u5c0f\u4eba\u53ea\u77a7\u89c1\u4ed6\u624b\u91cc\u63d0\u7740\u4e2a \u9e1f\u7b3c\u5b50\uff0c\u7b3c\u5b50\u91cc\u597d\u50cf\u6709\u53ea\u753b\u7709\u9e1f\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u5931\u58f0\u9053\uff1a\u201c\u753b\u7709\u9e1f\uff1f\u201d \u3000\u3000\u4ed6\u7acb\u523b\u8f6c\u8fc7\u53bb\u77a7\u695a\u7559\u9999\uff0c\u695a\u7559\u9999\u5374\u5b8c\u5168\u4e0d\u52a8\u58f0\u8272\uff0c\u53ea\u662f\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u90a3\u4eba\u662f\u6211 \u4eec\u7684\u670b\u53cb\uff0c\u4ed6\u8fd9\u662f\u548c\u6211\u4eec\u5f00\u73a9\u7b11\u7684\uff0c\u4f60\u8d70\u5427\uff01\u201d \u3000\u3000\u80e1\u94c1\u82b1\u53ea\u6709\u653e\u5f00\u624b\uff0c\u8fd9\u5927\u6c49\u5c31\u4e00\u6e9c\u70df\u4f3c\u7684\u9003\u4e0b\u697c\u53bb\u3002 \u3000\u3000\u674e\u7389\u51fd\u50cf\u662f\u4e5f\u6014\u4f4f\u4e86\uff0c\u8fd9\u65f6\u624d\u957f\u957f\u5410\u51fa\u53e3\u6c14\uff0c\u9644\u638c\u9053\uff1a\u201c\u7709\u513f\u7709\u513f\uff0c\u4f60\u542c\u89c1\u4e86\u9ebd\uff1f\u4f60\u6700 \u94a6\u4f69\u7684\u695a\u9999\u5e05\uff0c\u73b0\u5728\u5c31\u5750\u5728\u4f60\u9762\u524d\u4e86\uff0c\u4f60\u8fd8\u4e0d\u656c\u4ed6\u4e00\u676f\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u7b11\u9053\uff1a\u201c\u6211\u5f53\u7136\u60f3\u656c\u4e00\u676f\uff0c\u6015\u695a\u9999\u5e05\u73b0\u5728\u5df2\u559d\u4e0d\u4e0b\u53bb\u4e86\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u9053\uff1a\u201c\u559d\u4e0d\u4e0b\u53bb\uff1f\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u67f3\u65e0\u7709\u9053\uff1a\u201c\u4f60\u82e5\u88ab\u8fd9\u9ebd\u591a\u53cc\u773c\u775b\u76f4\u52fe\u52fe\u7684\u77aa\u7740\uff0c\u4f60\u8fd8\u559d\u5f97\u4e0b\u9152\u9ebd\uff1f\u201d \u3000\u3000\u5979\u53c8\u5411\u695a\u7559\u9999\u5ae3\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6240\u4ee5\u9999\u5e05\u4f60\u4e5f\u7528\u4e0d\u7740\u518d\u966a\u7740\u6211\u4eec\uff0c\u4f60\u82e5\u8981\u8d70\uff0c\u6211\u4eec\u4e5f\u7edd \u4e0d\u4f1a\u602a\u4f60\u7684\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u53f9\u4e86\u53e3\u6c14\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u5728\u4e0b\u672c\u4e0d\u613f\u8d70\u7684\uff0c\u4f46\u73b0\u5728\u2026\u2026\u73b0\u5728\u4e5f\u53ea\u597d\u544a\u8f9e\u4e86\u3002\u201d \u3000\u3000\u4e00\u8d70\u5230\u697c\u4e0b\uff0c\u80e1\u94c1\u82b1\u5c31\u7528\u529b\u4e00\u62cd\u695a\u7559\u9999\u80a9\u5934\uff0c\u9053\uff1a\u201c\u8001\u81ed\u866b\uff0c\u4f60\u4e0d\u662f\u89c1\u7684\u5973\u4eba\u5f88\u591a\u9ebd\uff0c \u4f46\u50cf\u67f3\u65e0\u7709\u8fd9\u6837\u7684\u5973\u4eba\uff0c\u4f60\u6015\u4e5f\u6ca1\u6709\u89c1\u8fc7\u5427\uff1f\u5979\u4eba\u957f\u5f97\u6f02\u4eae\u8fd8\u4e0d\u8bf4\uff0c\u800c\u4e14\u2026\u2026\u2026\u800c\u4e14\u53c8\u8c6a \u723d\u3001\u53c8\u59a9\u5a9a\u3001\u53c8\u4f53\u8d34\uff0c\u5979\u5bf9\u4f60\u90fd\u90a3\u9ebd\u4f53\u8d34\uff0c\u77e5\u9053\u4f60\u5750\u4e0d\u4f4f\u4e86\uff0c\u7acb\u523b\u5c31\u8ba9\u4f60\u8d70\uff0c\u4f55\u51b5\u5bf9\u5979\u7684\u4e08 \u592b\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u5fae\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u70b9\u5012\u7684\u786e\u5f88\u96be\u5f97\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u96be\u5f97\uff1f\u53c8\u4f55\u6b62\u96be\u5f97\u800c\u5df2\uff0c\u50cf\u5979\u8fd9\u6837\u7684\u5973\u4eba\uff0c\u6211\u6562\u8bf4\u5929\u4e0b\u518d\u4e5f\u627e\u4e0d\u51fa\u6709\u7b2c\u4e8c \u4e2a\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u54e6\uff01\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u6709\u4e9b\u5973\u4eba\u4e5f\u6709\u8bb8\u591a\u597d\u5904\uff0c\u4f46\u5973\u4eba\u5c31\u662f\u5973\u4eba\uff0c\u6bcf\u4e2a\u5973\u4eba\u591a\u591a\u5c11\u5c11\u90fd\u6709\u4e9b\u6bdb \u75c5\uff0c\u6709\u7684\u565c\u91cc\u565c\u82cf\uff0c\u6709\u7684\u88c5\u8154\u4f5c\u52bf\uff0c\u6709\u7684\u51b7\u82e5\u51b0\u971c\uff0c\u6709\u7684\u5374\u53c8\u592a\u6c34\u6027\u6768\u82b1\uff0c\u6709\u7684\u4e0d\u8bb8\u4e08\u592b\u559d \u9152\uff0c\u81ea\u5df1\u5374\u62da\u547d\u5403\u918b\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u9053\uff1a\u201c\u65e2\u7136\u6bcf\u4e2a\u5973\u4eba\u90fd\u6709\u6bdb\u75c5\uff0c\u5979\u96be\u9053\u4e0d\u662f\u5973\u4eba\u9ebd\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u4e00\u62cd\u5df4\u638c\uff0c\u9053\uff1a\u201c\u5999\u5c31\u5999\u5728\u8fd9\u91cc\uff0c\u6240\u6709\u5973\u4eba\u7684\u597d\u5904\uff0c\u5979\u5168\u6709\u4e86\uff0c\u4f46\u5973\u4eba\u7684\u6bdb\u75c5\uff0c \u5979\u5374\u4e00\u6837\u90fd\u6ca1\u6709\uff0c\u6240\u6709\u7537\u4eba\u7684\u597d\u5904\u5979\u4e5f\u5168\u6709\u4e86\uff0c\u5374\u53c8\u504f\u504f\u662f\u4e2a\u4e0d\u6298\u4e0d\u6263\u7684\u5973\u4eba\uff0c\u8fd9\u6837\u7684\u5973\u4eba \u8fd8\u6709\u7b2c\u4e8c\u4e2a\uff0c\u6211\u62da\u547d\u4e5f\u8981\u5a36\u5979\u505a\u8001\u5a46\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4f60\u624d\u89c1\u4e86\u5979\u4e00\u9762\uff0c\u5c31\u5bf9\u5979\u5982\u6b64\u6e05\u695a\u4e86\u9ebd\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u633a\u4e86\u633a\u80f8\uff0c\u5927\u58f0\u9053\uff1a\u201c\u4f60\u83ab\u4ee5\u4e3a\u53ea\u6709\u4f60\u4e86\u89e3\u5973\u4eba\uff0c\u6211\u59d3\u80e1\u7684\u6bd4\u4f60\u4e5f\u672a\u5fc5\u5c31\u5dee\u4e86\u8bb8 \u591a\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u6de1\u6de1\u9053\uff1a\u201c\u4f60\u96be\u9053\u6ca1\u6709\u60f3\u5230\uff0c\u5979\u53ef\u80fd\u5c31\u662f\u753b\u7709\u9e1f\u9ebd\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u7b80\u76f4\u8981\u8df3\u4e86\u8d77\u6765\uff0c\u77aa\u773c\u9053\uff1a\u201c\u5979\u662f\u753b\u7709\u9e1f\uff1f\u4f60\u53ef\u662f\u6709\u6bdb\u75c5\u9ebd\uff1f\u5979\u82e5\u662f\u753b\u7709\u9e1f\uff0c\u90a3 \u63d0\u7740\u9e1f\u7b3c\u5b50\u7684\u4eba\u53c8\u662f\u8c01\u5462\uff1f\u2026\u2026\u5979\u82e5\u662f\u753b\u7709\u9e1f\uff0c\u6211\u5c31\u5c06\u8111\u888b\u5207\u4e0b\u6765\u7ed9\u4f60\u5f53\u591c\u58f6\u3002\u201d \u3000\u3000\u695a\u56fd\u9999\u7b11\u4e86\u7b11\uff0c\u4e0d\u518d\u8bf4\u4ec0\u9ebd\uff0c\u56e0\u4e3a\u4ed6\u81ea\u5df1\u73b0\u5728\u4e5f\u5bf9\u81ea\u5df1\u7684\u60f3\u6cd5\u6709\u4e86\u6000\u7591\uff0c\u8fc7\u4e86\u534a\u664c\uff0c\u624d \u5583\u5583\u9053\uff1a\u201c\u4eca\u65e5\u6211\u4eec\u5403\u4e86\u4eba\u5bb6\u4e00\u987f\uff0c\u660e\u5929\u603b\u8be5\u60f3\u6cd5\u5b50\u8fd8\u4eba\u5bb6\u4e00\u987f\u624d\u662f\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u62cd\u638c\u9053\uff1a\u201c\u4f60\u8bf4\u4e86\u534a\u5929\uff0c\u53ea\u6709\u8fd9\u53e5\u8fd8\u50cf\u662f\u4eba\u8bdd\u3002\u201d \u3000\u3000\u4ed6\u4eec\u672c\u5c31\u51c6\u5907\u5728\u8fd9\u91cc\u4f4f\u4e00\u5bb5\u7684\uff0c\u6240\u4ee5\u65e9\u5df2\u627e\u4e86\u5bb6\u4e7e\u51c0\u7684\u5ba2\u6808\uff0c\u8ba2\u4e0b\u4e86\u4e24\u95f4\u4e7e\u51c0\u7684\u5c4b\u5b50\u3002 \u3000\u3000\u6708\u5149\u7167\u7740\u7a97\u524d\u7684\u68a7\u6850\uff0c\u79cb\u610f\u5df2\u7ecf\u5f88\u6d53\u4e86\uff0c\u4e0d\u77e5\u4ece\u90a3\u91cc\u74e2\u6765\u4e00\u9635\u9635\u6842\u5b50\u7684\u6e05\u9999\uff0c\u4f3c\u4e4e\u5728\u50ac \u4eba\u5165\u68a6\u3002 \u3000\u3000\u4f46\u80e1\u94c1\u82b1\u8fd8\u5750\u5728\u695a\u7559\u9999\u5c4b\u5b50\u91cc\u6ca1\u6709\u8d70\uff0c\u695a\u7559\u9999\u4e5f\u6ca1\u6709\u50ac\u4ed6\u53bb\u7761\uff0c\u56e0\u4e3a\u695a\u7559\u9999\u77e5\u9053\u4ed6\u6700\u6015 \u7684\u5c31\u662f\u5bc2\u5bde\u3002 \u3000\u3000\u4f55\u51b5\uff0c\u5982\u6b64\u661f\u8fb0\uff0c\u5982\u6b64\u6708\u591c\uff0c\u4e00\u4e2a\u4eba\u8eab\u65c1\u4e5f\u5b9e\u5728\u4e0d\u80fd\u6ca1\u6709\u4e2a\u597d\u670b\u53cb\uff0c\u695a\u7559\u9999\u671b\u7740\u7a97\u5916\u7684 \u660e\u6708\uff0c\u60a0\u7136\u9053\uff1a\u201c\u6842\u82b1\u8fd9\u9ebd\u9999\uff0c\u4e2d\u79cb\u6015\u5df2\u5728\u6211\u4eec\u4e0d\u77e5\u4e0d\u89c9\u95f4\u8fc7\u53bb\u4e86\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u606c\u7136\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4e5f\u4e0d\u77e5\u6709\u591a\u5c11\u4e8b\u90fd\u5728\u6211\u4eec\u4e0d\u77e5\u4e0d\u89c9\u95f4\u8fc7\u53bb\u4e86\uff0c\u53c8\u4f55\u6b62\u4e2d \u79cb\u2026\u2026\u201d \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u7a81\u542c\u4e00\u9635\u5608\u6742\u7684\u4eba\u58f0\u4f20\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u6309\u7740\uff0c\u4e00\u4eba\u5927\u547c\u7740\u9053\uff1a\u201c\u695a\u9999\u5e05\u5c31\u4f4f\u5728\u8fd9\u91cc\u9ebd\uff1f\u59da\u957f\u534e\u7279\u6765\u62dc\u8bbf\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u76b1\u7709\u9053\uff1a\u201c\u4e0d\u597d\uff0c\u539f\u6765\u753b\u7709\u9e1f\u53eb\u4eba\u5728\u90a3\u9152\u697c\u4e0a\u4e00\u56b7\uff0c\u662f\u60f3\u66ff\u54b1\u4eec\u627e\u9ebb\u70e6\u7684\u3002\u201d \u3000\u3000\u4ed6\u4e00\u53e5\u8bdd\u521a\u8bf4\u5b8c\uff0c\u9662\u5b50\u91cc\u5df2\u95ef\u5165\u4e00\u5927\u5806\u4eba\u6765\u3002 \u3000\u3000\u8fd9\u4e9b\u4eba\u6709\u7684\u624b\u91cc\u63d0\u7740\u706f\u7b3c\uff0c\u6709\u7684\u7adf\u62b1\u7740\u9152\u5b50\uff0c\u6709\u7684\u5df2\u9189\u6001\u53ef\u63ac\uff0c\u6709\u7684\u5374\u662f\u7761\u773c\u60fa\u5fea\uff0c\u50cf \u662f\u521a\u4ece\u5e8a\u4e0a\u88ab\u4eba\u62c9\u8d77\u6765\u7684\u3002 \u3000\u3000\u8d70\u5728\u6700\u524d\u9762\u7684\u4e00\u4eba\uff0c\u624b\u957f\u811a\u957f\uff0c\u53c8\u9ed1\u53c8\u7626\uff0c\u53c1\u4e24\u6b65\u5c31\u62a2\u5230\u7a97\u5b50\u524d\uff0c\u773c\u73e0\u5343\u6ef4\u6e9c\u6e9c\u4e00\u8f6c\uff0c \u62b1\u62f3\u7b11\u9053\uff1a\u201c\u90a3\u4e00\u4f4d\u662f\u695a\u9999\u5e05\uff1f\u5728\u4e0b\u59da\u957f\u534e\uff0c\u672c\u662f\u5c11\u6797\u95e8\u4e0b\u7684\u4fd7\u5bb6\u5f1f\u5b50\uff0c\u73b0\u5728\u5728\u8fd9\u91cc\u5f00\u4e86\u5bb6 \u5c0f\u9556\u5c40\uff0c\u4e45\u4ef0\u695a\u9999\u5e05\u7684\u5927\u540d\uff0c\u695a\u9999\u5e05\u65e2\u7136\u5149\u4e34\u6b64\u5730\uff0c\u82e5\u4e0d\u8ba9\u5728\u4e0b\u4e00\u5c3d\u5730\u4e3b\u4e4b\u8c0a\uff0c\u90a3\u5c31\u592a\u77a7\u4e0d \u8d77\u5728\u4e0b\u4e86\u3002\u201d \u3000\u3000\u8fd9\u4eba\u8bf4\u8bdd\u53c8\u6025\u53c8\u5feb\uff0c\u5c31\u50cf\u662f\u8fde\u73e0\u70ae\uff0c\u8bf4\u5230\u201c\u5c11\u6797\u95e8\u4e0b\u201d\u56db\u4e2a\u5b57\u65f6\uff0c\u4ed6\u4e00\u5f20\u9ed1\u8138\u4e0a\u5df2\u6ee1\u662f \u5f97\u610f\u4e4b\u8272\u3002 \u3000\u3000\u5bf9\u4ed8\u8fd9\u79cd\u81ea\u547d\u4e0d\u51e1\u7684\u4eba\uff0c\u80e1\u94c1\u82b1\u5b9e\u5728\u4e00\u70b9\u6cd5\u5b50\u4e5f\u6ca1\u6709\uff0c\u4ed6\u6b63\u60f3\u6084\u6084\u6e9c\u5f00\uff0c\u8c01\u77e5\u695a\u7559\u9999\u7adf \u62cd\u7740\u4ed6\u80a9\u5934\u7b11\u9053\uff1a\u201c\u770b\u6765\u4f60\u7684\u9762\u5b50\u771f\u4e0d\u5c0f\uff0c\u7adf\u52b3\u52a8\u8fd9\u8bb8\u591a\u670b\u53cb\u6765\u770b\u4f60\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u773c\u775b\u5374\u53d1\u76f4\u4e86\uff0c\u4f46\u8fd9\u65f6\u7a97\u5916\u4e00\u5927\u5806\u4eba\u90fd\u5728\u5411\u4ed6\u62b1\u62f3\u65bd\u793c\uff0c\u4ed6\u518d\u60f3\u5426\u8ba4\uff0c\u5df2\u6765\u4e0d\u53ca \u4e86\u3002 \u3000\u3000\u53ea\u542c\u5927\u5bb6\u4e03\u5634\u516b\u820c\uff0c\u90fd\u5728\u8bf4\u4ec0\u9ebd\u2026\u2026\u201c\u4e45\u4ef0\u695a\u9999\u5e05\u7684\u5927\u540d\u5566\uff01\u4eca\u65e5\u80fd\u89c1\u5230\u695a\u9999\u5e05\uff0c\u5b9e\u5728 \u592a\u9ad8\u5174\u5566\uff01\u201d \u3000\u3000\u80e1\u94c1\u82b1\u89c1\u5230\u695a\u7559\u9999\u5df2\u8eb2\u5230\u4e00\u8fb9\u53bb\uff0c\u53ea\u6068\u5f97\u7259\u8d53\u75d2\u7684\uff0c\u773c\u73e0\u5b50\u4e00\u8f6c\uff0c\u5ffd\u7136\u5927\u7b11\u8d77\u6765\uff0c\u9053\uff1a \u201c\u4e0d\u9519\uff0c\u5728\u4e0b\u5c31\u662f\u695a\u7559\u9999\uff0c\u4f46\u695a\u7559\u9999\u53ea\u4e0d\u8fc7\u662f\u4e2a\u5f3a\u76d7\u5c0f\u5077\u800c\u5df2\uff0c\u53c8\u600e\u6562\u52b3\u52a8\u5404\u4f4d\u7684\u5927\u9a7e\u5230\u8fd9 \u91cc\u6765\u770b\u6211\u3002\u201d \u3000\u3000\u4ed6\u4e00\u9762\u8bf4\uff0c\u4e00\u9762\u779f\u7740\u695a\u7559\u9999\uff0c\u600e\u5948\u695a\u7559\u9999\u8fd8\u662f\u7b11\u563b\u563b\u7684\u8d1f\u624b\u7ad9\u5728\u90a3\u91cc\uff0c\u7adf\u4e00\u70b9\u4e5f\u4e0d\u751f \u6c14\u3002 \u3000\u3000\u59da\u957f\u534e\u5374\u542c\u5f97\u6014\u4e86\u6014\uff0c\u8fc7\u4e86\u534a\u664c\uff0c\u624d\u76b1\u7709\u7b11\u9053\uff1a\u201c\u695a\u9999\u5e05\u5b9e\u5728\u592a\u8c26\u4e86\uff0c\u6c5f\u6e56\u4e2d\u8c01\u4e0d\u77e5\u9053 \u695a\u9999\u5e05\u52ab\u5bcc\u6d4e\u8d2b\uff0c\u5927\u4ec1\u5927\u4e49\uff0c\u8fd9\u5f3a\u76d7\u5c0f\u5077\u56db\u4e2a\u5b57\uff0c\u8c01\u6562\u7528\u5728\u9999\u5e05\u8eab\u4e0a\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u5f53\u7740\u6211\u7684\u9762\u4e0d\u6562\uff0c\u80cc\u5f8c\u6015\u5728\u9a82\u695a\u9999\u5e05\u4e0d\u4f46\u662f\u5f3a\u76d7\uff0c\u8fd8\u662f\u4e2a\u6df7\u86cb \u54e9\uff01\u201d \u3000\u3000\u59da\u957f\u534e\u53c8\u6014\u4e86\u6014\uff0c\u4e7e\u7b11\u9053\uff1a\u201c\u9999\u5e05\u5f53\u771f\u98ce\u8da3\u5f97\u5f88\uff0c\u98ce\u8da3\u5f97\u5f88\u3002\u201d \u3000\u3000\u4ed6\u50cf\u662f\u751f\u6015\u8fd9\u4f4d\u695a\u9999\u5e05\u53c8\u8bf4\u51fa\u4ec0\u9ebd\u60ca\u4eba\u7684\u8bdd\u6765\uff0c\u8d76\u7d27\u63a5\u7740\u9053\uff1a\u201c\u5728\u4e0b\u5148\u66ff\u9999\u5e05\u5f15\u89c1\u51e0\u4f4d \u670b\u53cb\u2026\u2026\u8fd9\u4f4d\u6bdb\u5065\u6241\uff0c\u4eba\u79f0\u201c\u795e\u62f3\u65e0\u654c\u5927\u9556\u5ba2\u201d\uff0c\u8fd9\u4f4d\u8d75\u5927\u6d77\u2026\u2026\u3002\u201d \u3000\u3000\u4ed6\u4e00\u53e3\u6c14\u8bf4\u4e86\u5341\u6765\u4e2a\u540d\u5b57\uff0c\u4e0d\u662f\u201c\u795e\u62f3\u201d\uff0c\u5c31\u662f\u201c\u795e\u5200\u201d\uff0c\u4e0d\u662f\u201c\u65e0\u654c\u201d\uff0c\u5c31\u662f\u201c\u5a01 \u9547\u201d\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u77a7\u7740\u8fd9\u4e9b\u4eba\u7684\u5c0a\u5bb9\uff0c\u518d\u542c\u5230\u8fd9\u4e9b\u54cd\u5f53\u5f53\u7684\u5916\u53f7\uff0c\u7b80\u76f4\u8fde\u5927\u7259\u90fd\u8981\u7b11\u6389\uff0c\u5fcd\u4f4f\u7b11 \u9053\uff1a\u201c\u5404\u4f4d\u6b64\u756a\u524d\u6765\uff0c\u7a76\u7adf\u6709\u4f55\u6307\u6559\u5440\uff1f\u201d \u3000\u3000\u8d75\u5927\u6d77\u62a2\u7740\u9053\uff1a\u201c\u5728\u4e0b\u7b49\u4e45\u4ef0\u695a\u9999\u5e05\u975e\u4f46\u8f7b\u529f\u5929\u4e0b\u65e0\u654c\uff0c\u9152\u91cf\u4e5f\u662f\u5929\u4e0b\u65e0\u53cc\u7684\uff0c\u8fd9\u6b21\u6709 \u4e86\u673a\u4f1a\uff0c\u5927\u5bb6\u90fd\u60f3\u656c\u9999\u5e05\u51e0\u676f\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u5927\u7b11\u9053\uff1a\u201c\u9519\u4e86\u9519\u4e86\uff0c\u4f60\u4eec\u5168\u9519\u4e86\uff0c\u6211\u695a\u7559\u9999\u8f7b\u529f\u867d\u9a6c\u9a6c\u864e\u864e\uff0c\u4f46\u9152\u91cf\u5374\u6bd4\u8001\u81ed \u866b\u4e5f\u5927\u4e0d\u4e86\u597d\u591a\uff0c\u771f\u6b63\u9152\u91cf\u65e0\u654c\u7684\u4eba\uff0c\u5728\u90a3\u91cc\u54e9\uff01\u201d \u3000\u3000\u4ed6\u7684\u624b\u5f80\u90a3\u8fb9\u4e00\u6307\uff0c\u5927\u5bb6\u7684\u773c\u775b\u90fd\u8ddf\u7740\u77a7\u4e86\u8fc7\u53bb\uff0c\u695a\u7559\u9999\u518d\u60f3\u8d70\u4e5f\u8d70\u4e0d\u4e86\uff0c\u80e1\u94c1\u82b1\u5927\u7b11 \u7740\u63a5\u9053\uff1a\u201c\u558f\u558f\u558f\uff01\u8fd9\u4f4d\u80e1\u94c1\u82b1\u80e1\u5927\u4fa0\uff0c\u624d\u771f\u6b63\u662f\u9152\u4e2d\u7684\u5927\u8c6a\u6770\u3001\u5927\u82f1\u96c4\uff0c\u5404\u4f4d\u82e5\u4e0d\u591a\u656c\u4ed6 \u51e0\u676f\uff0c\u90a3\u624d\u771f\u662f\u9057\u61be\u5f97\u5f88\u3002\u201d \u3000\u3000\u4ed6\u8bdd\u672b\u8bf4\u5b8c\uff0c\u4e00\u7fa4\u4eba\u5df2\u90fd\u6d8c\u8fdb\u5c4b\u5b50\u91cc\uff0c\u5341\u4e2a\u4eba\u4e2d\u5df2\u6709\u4e94\u4e2a\u4eba\u5411\u695a\u7559\u9999\u90a3\u8fb9\u6324\u8fc7\u53bb\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u8fd9\u4e0b\u5b50\u624d\u7b97\u62a5\u4e86\u4ec7\u4e86\uff0c\u4e5f\u4e0d\u7b49\u522b\u4eba\u656c\u4ed6\uff0c\u81ea\u5df1\u5148\u62a2\u8fc7\u9152\u676f\uff0c\u5495\u561f\u5495\u561f\u704c\u4e86\u53c1\u676f\u4e0b \u809a\uff0c\u53c8\u5927\u7b11\u9053\uff1a\u201c\u5176\u5b9e\u6211\u695a\u7559\u9999\u975e\u4f46\u9152\u91cf\u4e0d\u5982\u8fd9\u4f4d\u80e1\u5927\u4fa0\uff0c\u6b66\u529f\u4e5f\u4e0d\u5982\u4ed6\u7684\uff0c\u6709\u5929\u6211\u5b9a\u8981\u548c \u4ed6\u6bd4\u6b66\uff0c\u4e94\u5341\u62db\u5185\u5c31\u88ab\u4ed6\u6454\u4e86\u4e2a\u5927\u7b4b\u6597\uff0c\u5934\u90fd\u6454\u7834\u4e86\u2026\u2026\u4f60\u4eec\u770b\uff0c\u8fd9\u91cc\u8fd8\u6709\u4e2a\u5927\u75a4\u54e9\uff0c\u82e5\u4e0d \u662f\u4ed6\u624b\u4e0b\u7559\u60c5\uff0c\u8fd9\u75a4\u6015\u8fd8\u8981\u5927\u53c1\u500d\u3002\u201d \u3000\u3000\u5927\u5bb6\u542c\u5f97\u90fd\u77aa\u5927\u4e86\u773c\u775b\u53bb\u77a7\u695a\u7559\u9999\uff0c\u7eb7\u7eb7\u9053\uff1a\u201c\u771f\u7684\u9ebd\uff1f\u80e1\u5927\u4fa0\u4f60\u2026\u2026\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u5934\u90fd\u88ab\u5435\u6655\u4e86\uff0c\u4e5f\u542c\u4e0d\u51fa\u8fd9\u4e9b\u4eba\u4e71\u5608\u5608\u7684\u5728\u8bf4\u4ec0\u9ebd\uff0c\u53ea\u6709\u6478\u7740\u9f3b\u5b50\u82e6\u7b11\uff0c\u5fc3\u91cc\u5374 \u6068\u4e0d\u5f97\u5c06\u80e1\u94c1\u82b1\u7684\u8fd9\u5f20\u5927\u5634\u7528\u8349\u585e\u4f4f\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u7a81\u542c\u201c\u547c\u201d\u4e00\u58f0\uff0c\u4e00\u6837\u9ed1\u5ffd\u5ffd\u7684\u4e1c\u897f\u81ea\u7a97\u5916\u98de\u4e86\u8fdb\u6765\uff0c\u5e26\u7740\u4e00\u80a1\u5f3a\u98ce\uff0c\u5c06\u7a97 \u5b50\u90fd\u9707\u5f97\u201c\u5431\u5431\u683c\u683c\u201d\u7684\u54cd\u3002 \u3000\u3000\u4f17\u4eba\u5927\u60ca\u8d70\u907f\uff0c\u8fd9\u6837\u4e1c\u897f\u5df2\u201c\u7830\u201d\u7684\u843d\u5728\u684c\u5b50\u4e0a\uff0c\u5c06\u684c\u4e0a\u7684\u4e1c\u897f\u90fd\u9707\u5f97\u98de\u4e86\u8d77\u6765\uff0c\u7adf\u662f \u6446\u5728\u9662\u5b50\u91cc\u7684\u5927\u91d1\u9c7c\u7f38\u3002 \u3000\u3000\u8fd9\u91d1\u9c7c\u7f38\u5c11\u8bf4\u4e5f\u6709\u53c1\u4e94\u767e\u65a4\u91cd\uff0c\u6b64\u523b\u7adf\u88ab\u4eba\u81ea\u7a97\u5916\u629b\u4e86\u8fdb\u6765\uff0c\u4e0d\u504f\u4e0d\u501a\u5730\u843d\u5728\u684c\u5b50\u4e0a\uff0c \u800c\u4e14\u7f38\u91cc\u7684\u6c34\u7adf\u534a\u70b9\u6ca1\u6709\u6e85\u51fa\uff0c\u8fd9\u4efd\u624b\u529b\u8155\u529b\uff0c\u5b9e\u5728\u4ee4\u4eba\u5403\u60ca\uff0c\u4f17\u4eba\u4e0d\u7981\u4e00\u9f50\u5411\u7a97\u5916\u77a7\u51fa \u53bb\u3002 \u3000\u3000\u7e41\u661f\u6ee1\u5929\uff0c\u6708\u5149\u5982\u6c34\uff0c\u9662\u5b50\u91cc\u7684\u68a7\u6850\uff0c\u5c31\u50cf\u7834\u6c34\u6d17\u8fc7\u4e86\u4f3c\u7684\uff0c\u82cd\u7fe0\u6b32\u6ef4\uff0c\u68a7\u6850\u4e0b\u5374\u5df2\u591a \u4e86\u4e24\u6761\u4eba\u5f71\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u4e5f\u4e0d\u77e5\u662f\u4f55\u65f6\u6765\u7684\uff1f\u4ece\u90a3\u91cc\u6765\u7684\uff0c\u4e24\u4eba\u90fd\u7a7f\u7740\u4e00\u8eab\u9ed1\u8272\u7684\u957f\u888d\uff0c\u9762\u4e0a\u5374\u5404\u6234\u7740\u4e2a \u9762\u5177\u3002 \u3000\u3000\u77ee\u7684\u4e00\u4eba\u6234\u7684\u9762\u5177\uff0c\u6b63\u54a7\u5f00\u5927\u5634\u5728\u7b11\uff0c\u9ad8\u7684\u4e00\u4eba\u6234\u7684\u9762\u5177\uff0c\u5374\u6487\u7740\u5634\u5728\u54ed\uff0c\u4e24\u4e2a\u9762\u5177\u4e00 \u54ed\u4e00\u7b11\uff0c\u4e00\u9752\u4e00\u767d\uff0c\u5728\u767d\u5929\u770b\u6765\uff0c\u4e5f\u8bb8\u5f88\u6ed1\u7a3d\uff0c\u4f46\u5728\u8fd9\u9759\u9759\u7684\u9ed1\u591c\u4e2d\u770b\u6765\uff0c\u5374\u663e\u5f97\u8bf4\u4e0d\u51fa\u7684 \u8be1\u5f02\u3002 \u7b2c\u4e8c\u7ae0\u3000\u82f1\u96c4\u4f1a>> \u53e4\u9f99\u300a\u695a\u7559\u9999\u7cfb\u5217\xb7\u753b\u7709\u9e1f\u300b \u7b2c\u4e8c\u7ae0\u3000\u82f1\u96c4\u4f1a \u3000\u3000\u665a\u98ce\u5439\u8fc7\uff0c\u5c06\u4e24\u4eba\u9ed1\u8272\u7684\u957f\u888d\u5439\u5f97\u730e\u730e\u98de\u821e\uff0c\u4e5f\u5c06\u4e00\u9635\u5bd2\u6c14\u5439\u8fdb\u4e86\u7a97\u6237\uff0c\u59da\u957f\u534e\u5fcd\u4e0d\u4f4f \u673a\u4f36\u4f36\u6253\u4e86\u4e2a\u5bd2\u5664\uff0c\u5403\u5403\u9053\uff1a\u201c\u8fd9\u2026\u2026\u8fd9\u4e24\u4f4d\u4e5f\u662f\u9999\u5e05\u7684\u670b\u53cb\u9ebd\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u6447\u5934\u9053\uff1a\u201c\u975e\u4e5f\u3002\u201d \u3000\u3000\u59da\u957f\u534e\u9a87\u7136\u9053\uff1a\u201c\u90a3\u9ebd\u8fd9\u4e24\u4eba\u662f\u8c01\u5462\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u54a7\u5634\u4e00\u7b11\u9053\uff1a\u201c\u4f60\u600e\u9ebd\u95ee\u8d77\u6211\u6765\u4e86\uff0c\u4f60\u662f\u5802\u5802\u5c11\u6797\u95e8\u4e0b\uff0c\u53c8\u662f\u8fd9\u91cc\u7684\u5730\u4e3b\uff0c\u5730\u9762 \u4e0a\u82e5\u6709\u4e86\u6765\u5386\u4e0d\u660e\u7684\u4eba\uff0c\u4f60\u600e\u4f1a\u4e0d\u77e5\u9053\uff1f\u201d \u3000\u3000\u59da\u957f\u534e\u633a\u4e86\u633a\u80f8\uff0c\u5730\u60f3\u6446\u51fa\u5c11\u6797\u5f1f\u5b50\u7684\u67b6\u5b50\u6765\uff0c\u4f46\u62ac\u5934\u4e00\u671b\uff0c\u7a97\u5916\u56db\u53ea\u773c\u775b\u6b63\u51b7\u51b0\u51b0\u77a7 \u7740\u4ed6\uff0c\u51b7\u5f97\u5c31\u50cf\u5200\u3002 \u3000\u3000\u6234\u7740\u7b11\u8138\u7684\u90a3\u4eba\u683c\u683c\u4e00\u7b11\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u8fd9\u91cc\u8fd8\u6709\u5c11\u6797\u95e8\u4e0b\uff0c\u5931\u656c\u4e86\uff0c\u5931\u656c\u4e86\u3002\u201d \u3000\u3000\u4ed6\u5634\u91cc\u4e00\u9762\u8bf4\u7740\u8bdd\uff0c\u4e00\u9762\u81ea\u5730\u4e0a\u6361\u8d77\u5757\u7816\u5934\u5939\u5728\u4e24\u638c\u4e4b\u95f4\uff0c\u8bf4\u5230\u201c\u5931\u656c\u4e86\uff0c\u5931\u656c\u4e86\u201d\u8fd9 \u5757\u7816\u5934\u5ffd\u7136\u201c\u7c0c\u843d\u7c0c\u843d\u201d\u5730\u843d\u4e86\u4e0b\u6765\uff0c\u843d\u6ee1\u4e86\u4e00\u5730\uff0c\u8fd9\u5757\u7816\u5934\u88ab\u4ed6\u4e24\u53ea\u624b\u8f7b\u8f7b\u4e00\u5939\uff0c\u7adf\u5df2\u53d8 \u5f97\u7c89\u788e\u3002 \u3000\u3000\u8fd9\u624b\u638c\u4e0a\u529f\u592b\u9732\u51fa\u6765\uff0c\u83ab\u8bf4\u59da\u957f\u534e\u7b49\u4eba\u65e9\u5df2\u5413\u5f97\u9762\u65e0\u4eba\u8272\uff0c\u5c31\u8fde\u695a\u7559\u9999\u548c\u80e1\u94c1\u82b1\u90fd\u4e0d\u514d \u4e3a\u4e4b\u9a87\u7136\u3002 \u3000\u3000\u6234\u7740\u54ed\u7751\u7684\u90a3\u4eba\u9634\u607b\u607b\u9053\uff1a\u201c\u4e45\u95fb\u5c11\u6797\u795e\u62f3\u5929\u4e0b\u65e0\u654c\uff0c\u670b\u53cb\u53ef\u613f\u610f\u51fa\u6765\u8d50\u6559\u51e0\u62db\u9ebd\uff1f\u201d \u3000\u3000\u8fd9\u4eba\u8bf4\u8bdd\u9634\u9633\u602a\u6c14\uff0c\u7adf\u771f\u7684\u50cf\u662f\u5728\u54ed\u3002 \u3000\u3000\u59da\u957f\u534e\u9f3b\u5b50\u91cc\u76f4\u5598\u6c14\u9053\uff1a\u201c\u6211\u2026\u2026\u5728\u4e0b\u2026\u2026\u201d \u3000\u3000\u8bdd\u672b\u8bf4\u5b8c\uff0c\u4ed6\u8eab\u5b50\u5ffd\u7136\u5012\u5728\u8d75\u5927\u6d77\u8eab\u4e0a\uff0c\u7adf\u662f\u4e24\u6761\u817f\u53d1\u8f6f\uff0c\u8fde\u7ad9\u90fd\u7ad9\u4e0d\u4f4f\u4e86\uff0c\u6bdb\u5065\u6241\u77a7 \u4e86\u80e1\u94c1\u82b1\u4e00\u773c\uff0c\u5ffd\u7136\u58ee\u8d77\u80c6\u5b50\uff0c\u5927\u58f0\u9053\uff1a\u201c\u670b\u53cb\u662f\u90a3\u6761\u9053\u4e0a\u7684\uff1f\u96be\u9053\u4e0d\u6653\u5f97\u4f4f\u5728\u8fd9\u91cc\u7684\u662f\u4ec0 \u9ebd\u4eba\uff1f\u201d \u3000\u3000\u6234\u7740\u54ed\u8138\u7684\u4eba\u9053\uff1a\u201c\u662f\u4ec0\u9ebd\u4eba\uff1f\u201d \u3000\u3000\u6234\u7740\u7b11\u8138\u7684\u4eba\u5927\u7b11\u9053\uff1a\u201c\u770b\u6765\u4e5f\u4e0d\u8fc7\u662f\u51e0\u4e2a\u53ea\u4f1a\u5927\u8a00\u6b3a\u4eba\u7684\u9f20\u8f88\u800c\u5df2\u3002\u201d \u3000\u3000\u6bdb\u5065\u6241\u6da8\u7ea2\u4e86\u8138\u9053\uff1a\u201c\u670b\u53cb\u5634\u4e0a\u6700\u597d\u653e\u4e7e\u51c0\u4e9b\uff0c\u53ef\u77e5\u9053\u540d\u6ee1\u5929\u4e0b\u7684\u80e1\u5927\u4fa0\u548c\u695a\u9999\u5e08\u90fd\u5728 \u8fd9\u91cc\u3002\u201d \u3000\u3000\u6234\u7740\u54ed\u8138\u7684\u4eba\u9053\uff1a\u201c\u6211\u7b49\u4eca\u65e5\u6b63\u662f\u6765\u627e\u80e1\u5927\u4fa0\u548c\u695a\u9999\u5e05\u7684\uff0c\u53ea\u8981\u662f\u8fd9\u4e24\u4eba\u7684\u670b\u53cb\uff0c\u4e5f\u5168 \u90fd\u7b97\u4e0a\uff0c\u548c\u8fd9\u4e24\u4eba\u6ca1\u5173\u7cfb\u7684\uff0c\u6700\u597d\u7ad9\u5230\u4e00\u8fb9\u53bb\u3002\u201d \u3000\u3000\u4ed6\u4e00\u9762\u8bf4\u8bdd\uff0c\u4e00\u9762\u8f7b\u629a\u7740\u6811\u5e72\uff0c\u8bf4\u5230\u6700\u5f8c\u4e00\u53e5\u8bdd\u65f6\uff0c\u6811\u4e0a\u7684\u68a7\u6850\u53f6\u5ffd\u7136\u96e8\u70b9\u822c\u843d\u4e86\u4e0b \u6765\u3002 \u3000\u3000\u5c4b\u5b50\u91cc\u7684\u4eba\u5c31\u50cf\u662f\u88ab\u4eba\u7528\u97ad\u4e8e\u8d76\u7740\u4f3c\u7684\uff0c\u201c\u5ffd\u62c9\u201d\u4e00\u58f0\uff0c\u90fd\u6563\u5230\u4e24\u8fb9\u53bb\u4e86\uff0c\u53ea\u7559\u4e0b\u80e1\u94c1 \u5316\u548c\u695a\u7559\u9999\u5728\u4e2d\u95f4\u3002 \u3000\u3000\u6bdb\u5065\u6241\u966a\u7b11\u9053\uff1a\u201c\u54b1\u4eec\u548c\u695a\u7559\u9999\u53ef\u6ca1\u6709\u4ec0\u9ebd\u5173\u7cfb\uff0c\u7b80\u76f4\u8fde\u8ba4\u90fd\u4e0d\u8ba4\u5f97\uff0c\u662f\u9ebd\uff1f\u201d \u3000\u3000\u522b\u7684\u4eba\u7acb\u523b\u7eb7\u7eb7\u966a\u7b11\u9053\uff1a\u201c\u6839\u672c\u5c31\u4e0d\u8ba4\u5f97\u2026\u2026\u8c01\u662f\u695a\u7559\u9999\u5440\uff1f\u201d \u3000\u3000\u6234\u7740\u54ed\u8138\u7684\u4eba\u51b7\u51b7\u9053\uff1a\u201c\u679c\u7136\u662f\u4e00\u7fa4\u9f20\u8f88\u3002\u201d \u3000\u3000\u6234\u7740\u7b11\u8138\u7684\u4eba\u9053\uff1a\u201c\u65e2\u662f\u5982\u6b64\uff0c\u4f60\u4eec\u4e24\u4eba\u5c31\u51fa\u6765\u5427\uff01\u201d \u3000\u3000\u80e1\u94c1\u82b1\u5ffd\u7136\u8d70\u5230\u6bdb\u5065\u6241\u9762\u524d\uff0c\u7b11\u563b\u563b\u9053\uff1a\u201c\u6bdb\u5927\u9556\u5ba2\uff0c\u4f60\u6211\u591a\u5e74\u7684\u4ea4\u60c5\uff0c\u4f60\u4e0d\u5e2e\u5e2e\u6211\u7684 \u5fd9\u9ebd\uff1f\u201d \u3000\u3000\u6bdb\u5065\u6241\u8fde\u5634\u5507\u90fd\u53d1\u81ea\u4e86\uff0c\u98a4\u58f0\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u662f\u4ec0\u9ebd\u4eba\uff0c\u6211\u6839\u672c\u4e0d\u8ba4\u5f97\u4f60\uff0c\u4f60\u600e\u80fd\u8840\u53e3 \u55b7\u4eba\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u7b11\u9053\uff1a\u201c\u4f60\u65e2\u4e0d\u8ba4\u5f97\u6211\uff0c\u8fd9\u676f\u9152\u5c31\u8fd8\u7ed9\u4f60\u5427\uff01\u201d \u3000\u3000\u4ed6\u4e3e\u8d77\u9152\u676f\uff0c\u5c06\u676f\u4e2d\u7684\u9152\u6162\u6162\u5012\u5728\u6bdb\u5065\u6241\u5934\u4e0a\uff0c\u6bdb\u5065\u6241\u5df2\u5413\u5f97\u5446\u5982\u6728\u9e21\uff0c\u8fde\u8eb2\u90fd\u4e0d\u6562 \u8eb2\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u770b\u6765\u4f60\u771f\u8be5\u6539\u4e2a\u540d\u5b57\uff0c\u53eb\u5927\u5ad6\u5ba2\u8fd8\u597d\u4e9b\u3002\u201d \u3000\u3000\u7b11\u58f0\u4e2d\uff0c\u4ed6\u5df2\u7a7f\u7a97\u800c\u51fa\u3002 \u3000\u3000\u5916\u9762\u4e24\u4e2a\u4eba\u4e5f\u7acb\u523b\u98de\u8eab\u800c\u8d77\uff0c\u4e00\u95ea\u4fbf\u63a0\u51fa\u5899\u5916\uff0c\u518d\u4e00\u95ea\u5df2\u6ca1\u5165\u9ed1\u6697\u91cc\uff0c\u8f7b\u529f\u4e4b\u9ad8\uff0c\u7adf\u4e5f \u4ee4\u4eba\u5403\u60ca\u3002 \u3000\u3000\u4f46\u695a\u7559\u9999\u548c\u80e1\u94c1\u82b1\u7684\u8f7b\u8eab\u529f\u592b\u6bd4\u8c01\u4e5f\u4e0d\u5dee\uff0c\u53ea\u662f\u4e24\u4eba\u89c1\u5230\u5bf9\u624b\u5982\u6b64\u9ad8\u660e\uff0c\u8c01\u4e5f\u4e0d\u6562\u5927 \u610f\u3002 \u3000\u3000\u4e24\u4eba\u5e76\u80a9\u98de\u63a0\uff0c\u8fdc\u8fdc\u8ddf\u7740\u524d\u9762\u7684\u4e24\u6761\u4eba\u5f71\uff0c\u4e00\u65f6\u95f4\u5e76\u4e0d\u6562\u903c\u5f97\u592a\u8fd1\uff0c\u80e1\u94c1\u82b1\u77a7\u4e86\u695a\u7559\u9999 \u4e00\u773c\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u770b\u6765\u4f60\u5389\u5bb3\u7684\u5bf9\u5934\u5012\u771f\u4e0d\u5c11\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u8fd9\u4e24\u4eba\u4e0d\u662f\u4f60\u7684\u4ec7\u4eba\u9ebd\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u6014\u4e86\u6014\uff0c\u9053\uff1a\u201c\u8fd9\u4e24\u4eba\u6211\u6068\u672c\u8fde\u89c1\u90fd\u6ca1\u6709\u89c1\u8fc7\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u6211\u4e5f\u6ca1\u89c1\u8fc7\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u4f60\u518d\u60f3\u60f3\uff0c\u8fd9\u4e24\u4eba\u4e00\u5b9a\u662f\u6765\u627e\u4f60\u7684\uff0c\u6211\u7684\u4ec7\u4eba\u90fd\u6ca1\u6709\u8fd9\u9ebd\u597d\u7684\u529f\u592b\uff0c\u53ea\u6709 \u4e00\u4e2a\u201c\u9b3c\u738b\u201d\u97e9\u975e\uff0c\u4f46\u53c1\u5e74\u524d\u4e5f\u5df2\u771f\u7684\u505a\u9b3c\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u6211\u4e5f\u60f3\u4e0d\u51fa\u6709\u8fd9\u6837\u7684\u5bf9\u5934\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u4f60\u8fde\u4ed6\u4eec\u7684\u8eab\u6cd5\u529f\u592b\u90fd\u770b\u4e0d\u51fa\u9ebd\uff1f\u6c5f\u6e56\u4e2d\u8fd9\u6837\u7684\u9ad8\u624b\u5e76\u4e0d\u591a\u5440\uff01\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u8fd9\u4e24\u4eba\u638c\u529b\u4ff1\u9634\u67d4\u5df2\u6781\uff0c\u50cf\u662f\u5357\u5b97\u7684\u201c\u91d1\u4e1d\u7ef5\u638c\u201d\uff0c\u4f46\u80fd\u5c06\u91d1\u4e1d\u7ef5\u638c\u7ec3\u5230 \u8fd9\u79cd\u706b\u5019\u7684\uff0c\u53c1\u5341\u5e74\u6765\u4e5f\u4e0d\u8fc7\u53ea\u6709\u65b9\u4ed9\u5ba2\u4e00\u4eba\u800c\u5df2\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u53ef\u662f\u65b9\u4ed9\u5ba2\u53ea\u6709\u4e00\u53ea\u624b\uff0c\u53c8\u600e\u4f1a\u662f\u8fd9\u4e24\u4eba\u5462\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u6211\u4e5f\u77e5\u9053\u4ed6\u4eec\u7edd\u4e0d\u4f1a\u662f\u65b9\u4ed9\u5ba2\uff0c\u6240\u4ee5\u627e\u4e5f\u731c\u4e0d\u51fa\u4ed6\u4eec\u662f\u8c01\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u65e0\u8bba\u8fd9\u4e24\u4eba\u662f\u8c01\uff0c\u54b1\u4eec\u4eca\u5929\u90fd\u5c11\u4e0d\u5f97\u8981\u7ecf\u4e00\u756a\u6076\u6218\u4e86\uff0c\u6211\u672c\u4ee5 \u4e3a\u56de\u6765\u5f8c\u53ef\u4ee5\u8fc7\u4e24\u5929\u592a\u5e73\u65e5\u5b50\uff0c\u8c01\u77e5\u4e00\u56de\u6765\u5c31\u9047\u4e0a\u8fd9\u9ebd\u6837\u4e24\u4e2a\u4eba\uff0c\u65e9\u77e5\u5982\u6b64\uff0c\u6211\u5b81\u53ef\u8ddf\u7435\u7436 \u516c\u4e3b\u56de\u9f9f\u5179\u56fd\u53bb\u4e86\u3002\u201d \u3000\u3000\u4ed6\u4eec\u5634\u91cc\u5728\u8bf4\u8bdd\uff0c\u8eab\u6cd5\u5374\u4e1d\u6beb\u672b\u505c\uff0c\u524d\u9762\u4e24\u4e2a\u4eba\u8eab\u6cd5\u4e5f\u4e1d\u6beb\u672b\u505c\u4e0b\u6765\uff0c\u4e2d\u6c14\u4e4b\u5145\u8db3\uff0c\u7adf \u4e0d\u5728\u4ed6\u4eec\u4e4b\u4e0b\u3002 \u3000\u3000\u53ea\u89c1\u4e24\u65c1\u7684\u666f\u8272\uff0c\u8d8a\u6765\u8d8a\u8352\u51c9\uff0c\u8fdc\u5904\u4f3c\u6709\u70b9\u70b9\u9b3c\u706b\u5728\u968f\u98ce\u98d8\u52a8\uff0c\u7adf\u4f3c\u5230\u4e86\u4e00\u7247\u8352\u575f\u95f4\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u76b1\u7709\u9053\uff1a\u201c\u53c8\u662f\u4e2a\u575f\u573a\uff0c\u4e3a\u4ec0\u9ebd\u6bcf\u6b21\u6709\u4eba\u627e\u6211\u6253\u67b6\u65f6\uff0c\u603b\u662f\u8981\u5c06\u6211\u5e26\u5230\u575f\u573a\u571f \u6765\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u5fae\u7b11\u9053\uff1a\u201c\u4ed6\u82e5\u60f3\u627e\u4f60\u559d\u9152\uff0c\u81ea\u7136\u4f1a\u5c06\u4f60\u5e26\u5230\u9152\u697c\u4e0a\u53bb\uff0c\u53ef\u662f\u4ed6\u73b0\u5728\u5374\u60f3\u8981\u4f60\u7684 \u547d\uff0c\u81ea\u7136\u53ea\u6709\u5728\u575f\u573a\u4e0a\u6700\u65b9\u4fbf\u3002\u201d \u3000\u3000\u4e00\u9635\u51b7\u98d5\u98d5\u7684\u98ce\u5439\u8fc7\uff0c\u70b9\u70b9\u9b3c\u706b\u6251\u9762\u800c\u6765\u3002 \u3000\u3000\u5230\u4e86\u8fd9\u91cc\uff0c\u6708\u5149\u4e5f\u4f3c\u4e4e\u53d8\u5f97\u51c4\u51c4\u51c9\u51c9\u7684\uff0c\u51c4\u51c4\u51c9\u51c9\u7684\u6708\u5149\uff0c\u7167\u7740\u4e00\u5ea7\u5ea7\u957f\u6ee1\u8352\u8349\u7684\u575f \u5806\uff0c\u8fdc\u5904\u4e0d\u65f6\u4f20\u6765\u4e00\u58f0\u58f0\u91ce\u72d7\u7684\u54c0\u9e23\uff0c\u5c31\u50cf\u662f\u9b3c\u54ed\uff0c\u5374\u6bd4\u9b3c\u54ed\u8fd8\u8981\u96be\u542c\uff0c\u80e1\u94c1\u82b1\u6e10\u6e10\u5df2\u89c9\u5f97 \u7b11\u4e0d\u51fa\u6765\u4e86\u3002 \u3000\u3000\u90a3\u4e24\u4e2a\u9ed1\u8863\u4eba\u5df2\u5728\u4e71\u575f\u95f4\u505c\u4e86\u4e0b\u6765\uff0c\u51b7\u51b7\u7684\u77a7\u7740\u4ed6\u4eec\uff0c\u695a\u7559\u9999\u548c\u80e1\u94c1\u82b1\u4e5f\u653e\u7f13\u8eab\u5f62\uff0c\u4e00 \u6b65\u6b65\u8d70\u8fc7\u53bb\u3002 \u3000\u3000\u53ea\u89c1\u575f\u5806\u91cc\u5df2\u6446\u597d\u4e86\u56db\u53e3\u5f88\u5c0f\u7684\u68fa\u6750\uff0c\u68fa\u6750\u4e0a\u7adf\u8fd8\u94fa\u7740\u5f20\u8349\u5e2d\uff0c\u6234\u7740\u54ed\u8138\u7684\u4eba\u4f38\u624b\u5411\u68fa \u6750\u4e00\u6307\uff0c\u9053\uff1a\u201c\u8bf7\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u63c9\u4e86\u63c9\u9f3b\u5b50\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u68fa\u6750\u82e5\u662f\u4e3a\u6211\u51c6\u5907\u7684\uff0c\u5c31\u672a\u514d\u592a\u5c0f\u4e86\u4e9b\u3002\u201d \u3000\u3000\u6234\u7740\u7b11\u8138\u90a3\u4eba\u683c\u683c\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u82e5\u662f\u5c06\u4f60\u5207\u6210\u4e24\u534a\uff0c\u5c82\u975e\u5c31\u6b63\u5408\u9002\u4e86\u9ebd\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u4e5f\u5b66\u7740\u4ed6\u683c\u683c\u7b11\u9053\uff1a\u201c\u4f60\u8eab\u6750\u4e5f\u548c\u6211\u5dee\u4e0d\u591a\uff0c\u8fd9\u68fa\u6750\u88c5\u4f60\u4e5f\u5408\u9002\u5f97\u5f88\u3002\u201d \u3000\u3000\u6234\u7740\u54ed\u8138\u90a3\u4eba\u5374\u53c8\u5411\u68fa\u6750\u4e00\u6307\uff0c\u9053\uff1a\u201c\u8bf7\u5750\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u7b11\u9053\uff1a\u201c\u96be\u602a\u6700\u8fd1\u68fa\u6750\u5e97\u751f\u610f\u5174\u9686\uff0c\u539f\u6765\u7adf\u6709\u4eba\u5c06\u68fa\u6750\u5f53\u51f3\u5b50\u3002\u201d \u3000\u3000\u4ed6\u77a7\u695a\u7559\u9999\u5df2\u5750\u4e0b\uff0c\u4e5f\u53ea\u597d\u751f\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u56db\u4e2a\u4eba\u7adf\u5404\u636e\u4e00\u53e3\u68fa\u6750\uff0c\u9762\u9762\u76f8\u5bf9\uff0c\u5750\u5728\u575f\u5806\u91cc\u3002 \u3000\u3000\u695a\u7559\u9999\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e0d\u77e5\u4e24\u4f4d\u9ad8\u59d3\u5927\u540d\uff1f\u7a76\u7adf\u662f\u4f55\u610f\uff0c\u662f\u5426\u548c\u5728\u4e0b\u6709\u4ec0\u9ebd\u8fc7\u8282\uff1f\u201d \u3000\u3000\u4ed6\u4e00\u8fde\u95ee\u4e86\u53c1\u53e5\u8bdd\uff0c\u5bf9\u65b9\u5374\u8fde\u4e00\u53e5\u4e5f\u4e0d\u56de\u7b54\u3002 \u3000\u3000\u6234\u7740\u54ed\u8138\u90a3\u4eba\u5ffd\u7136\u6325\u4e86\u6325\u624b\uff0c\u9053\uff1a\u201c\u6446\u9152\u571f\u6765\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u6014\u4e86\u6014\uff0c\u5931\u7b11\u9053\uff1a\u201c\u4e24\u4f4d\u7adf\u662f\u8bf7\u54b1\u4eec\u6765\u559d\u9152\u7684\u9ebd\uff1f\u201d \u3000\u3000\u6234\u7740\u54ed\u8138\u90a3\u4eba\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u8fd9\u5730\u65b9\u6ca1\u4ec0\u9ebd\u597d\u4e1c\u897f\u53ef\u5949\u656c\u4e24\u4f4d\u3002\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u521a\u8bf4\u5b8c\uff0c\u4e71\u575f\u5f8c\u5df2\u8d70\u51fa\u4e24\u4e2a\u4eba\u6765\uff0c\u8eab\u4e0a\u4e5f\u7a7f\u7740\u4ef6\u9ed1\u888d\u5b50\uff0c\u8138\u4e0a\u4e5f\u6234\u7740\u8be1\u79d8\u7684\u9762 \u5177\u3002 \u3000\u3000\u4e24\u4eba\u624b\u91cc\u7adf\u62ac\u7740\u53e3\u68fa\u6750\u3002 \u3000\u3000\u53e3\u53e3\u53e3 \u3000\u3000\u8fd9\u53e3\u68fa\u6750\u5927\u5f97\u591a\u4e86\uff0c\u4e24\u4e2a\u9ed1\u8863\u4eba\u5c06\u68fa\u6750\u62ac\u5230\u4ed6\u4eec\u56db\u4e2a\u4eba\u4e2d\u95f4\uff0c\u8eac\u884c\u4e00\u793c\uff0c\u53c8\u8f6c\u8eab\u8d70\u5165\u4e71 \u575f\u91cc\u3002 \u3000\u3000\u5f77\u4f5b\u672c\u5c31\u662f\u4ece\u8352\u575f\u91cc\u8d70\u51fa\u6765\u7684\u3002 \u3000\u3000\u6234\u7740\u54ed\u8138\u90a3\u4eba\u53c8\u4f38\u624b\u5411\u8fd9\u53e3\u68fa\u6750\u4e00\u6307\uff0c\u9053\uff1a\u201c\u8bf7\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u8bf7\uff1f\u8bf7\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u6234\u7740\u54ed\u8138\u7684\u4eba\u9053\uff1a\u201c\u8bf7\u5403\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u6014\u4e86\u6014\uff0c\u5927\u7b11\u9053\uff1a\u201c\u4e24\u4f4d\u96be\u9053\u8981\u8bf7\u6211\u5403\u6b7b\u4eba\u9ebd\uff1f\u201d \u3000\u3000\u6234\u7740\u54ed\u8138\u7684\u4eba\u51b7\u51b7\u9053\uff1a\u201c\u5230\u4e86\u8fd9\u5730\u65b9\uff0c\u4e0d\u5403\u6b7b\u4eba\u5403\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u53c8\u6014\u4e86\u6014\uff0c\u683c\u683c\u7b11\u9053\uff1a\u201c\u6709\u8da3\u6709\u8da3\uff0c\u5b9e\u5728\u6709\u8da3\u6781\u4e86\u3002\u201d \u3000\u3000\u4ed6\u7b11\u58f0\u5ffd\u7136\u505c\u4f4f\uff0c\u6234\u7740\u7b11\u8138\u7684\u4eba\u7adf\u5df2\u5c06\u624b\u4f38\u8fdb\u68fa\u6750\uff0c\u201c\u606a\u53f1\u201d\u4e00\u58f0\uff0c\u50cf\u662f\u62d7\u65ad\u4e86\u6837\u4e1c \u897f\u3002 \u3000\u3000\u7b49\u5230\u4ed6\u624b\u4f38\u51fa\u6765\u65f6\uff0c\u5df2\u62ff\u7740\u6761\u8840\u6dcb\u6dcb\u7684\u8180\u5b50\uff0c\u4ed6\u5c06\u9762\u5177\u5411\u4e0a\u4e00\u6380\uff0c\u201c\u5580\u53f1\u201d\u4e00\u58f0\uff0c\u5c06\u8fd9 \u6761\u8180\u5b50\u54ac\u4e0b\u4e86\u4e00\u5927\u5757\uff0c\u5927\u7b11\u9053\uff1a\u201c\u8bf7\u8bf7\u8bf7\uff0c\u8fd9\u4eba\u6b7b\u4e86\u6ca1\u591a\u4e45\uff0c\u8fd8\u65b0\u9c9c\u5f97\u5f88\u3002\u201d \u3000\u3000\u4ed6\u4e00\u9762\u7b11\uff0c\u4e00\u9762\u56bc\uff0c\u9c9c\u8840\u6cbf\u7740\u5634\u89d2\u5f80\u4e0b\u76f4\u6d41\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u53c8\u662f\u5403\u60ca\uff0c\u53c8\u662f\u6076\u5fc3\uff0c\u5927\u6012\u9053\uff1a\u201c\u4f60\u4eec\u7a76\u7adf\u2026\u2026\u201d \u3000\u3000\u8c01\u77e5\u4ed6\u8bdd\u8fd8\u672b\u8bf4\u51fa\uff0c\u695a\u7559\u9999\u7adf\u4e5f\u5c06\u624b\u4f38\u8fdb\u68fa\u6750\u53bb\uff0c\u201c\u5580\u53f1\u201d\u4e00\u58f0\uff0c\u4e5f\u62d7\u4e0b\u6761\u8840\u6dcb\u6dcb\u7684\u8180 \u5b50\u3002 \u3000\u3000\u6309\u7740\uff0c\u53c8\u662f\u201c\u683c\u53f1\u53f1\u201d\u4e00\u58f0\uff0c\u4ed6\u7adf\u4e5f\u5c06\u8fd9\u6761\u8180\u5b50\u54ac\u4e0b\u4e86\u4e00\u5927\u5757\uff0c\u9c9c\u8840\u4e5f\u6cbf\u7740\u5634\u89d2\u5f80\u4e0b\u76f4 \u6d41\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u77a7\u5f85\u5168\u8eab\u5bd2\u6bdb\u76f4\u7ad6\uff0c\u5ffd\u7136\u8df3\u8d77\u6765\uff0c\u5927\u559d\u9053\uff1a\u201c\u695a\u7559\u9999\uff0c\u4f60\u4ec0\u9ebd\u65f6\u5019\u4e5f\u5b66\u4f1a\u5403\u6b7b\u4eba \u4e86\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u9053\uff1a\u201c\u8fd9\u4eba\u679c\u7136\u65b0\u9c9c\u5f97\u5f88\uff0c\u6ecb\u5473\u597d\u6781\u4e86\uff0c\u4f60\u4e5f\u5c1d\u4e00\u5757\u5427\uff01\u201d \u3000\u3000\u80e1\u94c1\u82b1\u53c8\u60ca\u53c8\u6012\uff0c\u6b63\u4e0d\u77e5\u8be5\u600e\u9ebd\u529e\uff0c\u90a3\u4e24\u4e2a\u9ed1\u8863\u4eba\u5ffd\u7136\u5927\u7b11\u8d77\u6765\uff0c\u6234\u7740\u54ed\u8138\u7684\u4eba\u7adf\u94f6\u94c3 \u822c\u7b11\u9053\uff1a\u201c\u6211\u65e9\u5c31\u77e5\u9053\u8fd9\u9a97\u4e0d\u8fc7\u695a\u9999\u5e05\u7684\u3002\u201d \u3000\u3000\u7b11\u58f0\u4e2d\uff0c\u56db\u9762\u5ffd\u7136\u6311\u8d77\u4e86\u6570\u5341\u76cf\u706f\u7b3c\uff0c\u5c06\u4e00\u7247\u8352\u575f\u7167\u8000\u5f97\u4eae\u5982\u767d\u663c\uff0c\u80e1\u94c1\u82b1\u8fd9\u624d\u770b\u6e05 \u695a\uff0c\u90a3\u6761\u201c\u8840\u6dcb\u6dcb\u7684\u8180\u5b50\u201d\uff0c\u7adf\u53ea\u4e0d\u8fc7\u662f\u4e00\u822c\u4e0a\u9762\u4fa5\u7740\u7ea2\u7cd6\u6c41\u7684\u767d\u85d5\uff0c\u5728\u8fd9\u9634\u68ee\u68ee\u7684\u575f\u5806 \u91cc\uff0c\u51b7\u51c4\u51c4\u7684\u6708\u5149\u4e0b\uff0c\u867d\u9a97\u8fc7\u4e86\u80e1\u94c1\u82b1\u7684\u773c\u775b\uff0c\u5374\u8fd8\u662f\u6ca1\u6709\u9a97\u8fc7\u7981\u7559\u9999\u7684\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u5f20\u53e3\u7ed3\u820c\uff0c\u62da\u547d\u63c9\u7740\u9f3b\u5b50\uff0c\u9053\uff1a\u201c\u8fd9\u2026\u2026\u8fd9\u7a76\u7adf\u662f\u5728\u641e\u4ec0\u9ebd\u9b3c\uff1f\u201d \u3000\u3000\u6234\u7740\u7b11\u8138\u7684\u4eba\u5c06\u9762\u5177\u6458\u4e86\u4e0b\u6765\uff0c\u5927\u7b11\u9053\uff1a\u201c\u5c0f\u5f1f\u5b9e\u5728\u8352\u5510\uff0c\u4f46\u671b\u80e1\u5144\u6055\u7f6a\u3002\u201d \u3000\u3000\u8fd9\u4eba\u7709\u6e05\u76ee\u79c0\uff0c\u7adf\u662f\u4ed6\u65b0\u4ea4\u7684\u670b\u53cb\u674e\u7389\u51fd\u3002 \u3000\u3000\u6234\u7740\u54ed\u7751\u7684\u4eba\u81ea\u7136\u5c31\u662f\u67f3\u65e0\u7709\u4e86\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u53c8\u8df3\u4e86\u8d77\u6765\uff0c\u5927\u7b11\u9053\uff1a\u201c\u6709\u8da3\u6709\u8da3\uff0c\u8fd9\u771f\u7684\u6709\u8da3\u6781\u4e86\uff0c\u6211\u8fd9\u4e00\u8f88\u5b50\u90fd\u6ca1\u6709\u9047\u7740\u5982 \u6b64\u6709\u8da3\u7684\u4e8b\uff0c\u4f60\u4eec\u4e24\u4eba\u5b9e\u5728\u6709\u4e24\u4e0b\u5b50\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u5ae3\u7136\u9053\uff1a\u201c\u6211\u77e5\u9053\u4e24\u4f4d\u4e00\u5b9a\u88ab\u90a3\u4e9b\u6076\u5ba2\u7ea0\u7f20\u5f97\u65e0\u6cd5\u8131\u8eab\uff0c\u6240\u4ee5\u4e86\u60f3\u51fa\u8fd9\u6cd5\u5b50\u6765\uff0c \u8ba9\u4e24\u4f4d\u89e3\u89e3\u95f7\uff0c\u5f00\u5f00\u5fc3\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9644\u638c\u9053\uff1a\u201c\u5999\u6781\u5999\u6781\uff0c\u8fd9\u6cd5\u5b50\u5b9e\u5728\u662f\u5999\u7edd\u5929\u4e0b\uff0c\u5999\u7edd\u53e4\u4eca\uff0c\u9664\u4e86\u5ac2\u592b\u4eba\uff0c\u6015\u5929\u4e0b \u518d\u4e5f\u6ca1\u6709\u7b2c\u4e8c\u4e2a\u4eba\u80fd\u60f3\u51fa\u8fd9\u6cd5\u5b50\u6765\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u7b11\u9053\uff1a\u201c\u4f46\u5979\u65e0\u8bba\u60f3\u5f97\u591a\u5999\uff0c\u5374\u8fd8\u662f\u7792\u4e0d\u8fc7\u695a\u5144\u7684\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u60a0\u7136\u7b11\u9053\uff1a\u201c\u4ed6\u7684\u786e\u751f\u4e86\u53cc\u5229\u773c\uff0c\u53ef\u662f\u6211\u5e76\u4e0d\u7fa1\u6155\u4ed6\uff0c\u56e0\u4e3a\u8fd9\u6837\u4ed6\u53cd\u800c\u4f1a\u5c11\u4e86\u8bb8 \u591a\u4e50\u8da3\uff0c\u6c38\u8fdc\u90fd\u4e0d\u4f1a\u50cf\u6211\u8fd9\u9ebd\u6837\u5f00\u5fc3\u3002\u201d \u3000\u3000\u5f0a\u6750\u91cc\u4e0d\u4f46\u6709\u85d5\uff0c\u8fd8\u6709\u65b0\u6a59\u3001\u9c9c\u83f1\u3001\u751c\u74dc\u3001\u9999\u679c\uff0c\u8fd9\u5bf9\u65bc\u80e1\u94c1\u82b1\u548c\u695a\u7559\u9999\u5df2\u585e\u6ee1\u4e86\u592a\u591a \u9152\u8089\u7684\u80a0\u80c3\u8bf4\u6765\uff0c\u5b9e\u5728\u518d\u4e5f\u5408\u9002\u6ca1\u6709\u4e86\uff0c\u4f55\u51b5\uff0c\u8fd9\u4e9b\u6c34\u679c\u867d\u975e\u73cd\u8d35\u4e4b\u7269\uff0c\u4f46\u5728\u8fd9\u79cd\u5730\u65b9\uff0c\u8fd9 \u79cd\u5b63\u8282\uff0c\u5374\u6015\u6bd4\u96c0\u820c\u718a\u638c\u8fd8\u8981\u73cd\u8d35\uff0c\u7531\u6b64\u53ef\u89c1\uff0c\u4e3b\u4eba\u975e\u4f46\u53c8\u4f53\u8d34\uff0c\u53c8\u5468\u5230\uff0c\u800c\u4e14\u8fb9\u6177\u6168\u5f97 \u5f88\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u4e3e\u9152\u5927\u7b11\u9053\uff1a\u201c\u6211\u751f\u5e73\u867d\u7136\u505a\u8fc7\u4e0d\u5c11\u8352\u5510\u4e8b\uff0c\u4f46\u5750\u5728\u575f\u573a\u91cc\u7684\u68fa\u6750\u4e0a\u559d\u9152\uff0c\u8fd9\u5012 \u771f\u8fd8\u662f\u751f\u5e73\u7b2c\u4e00\u6b21\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u8d76\u7d27\u7684\u9053\uff1a\u201c\u80e1\u5144\u662f\u5426\u89c9\u5f97\u6709\u4e9b\u4e0d\u5feb\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u4e0d\u5feb\uff1f\u6211\u7b80\u76f4\u89c9\u5f97\u6109\u5feb\u6781\u4e86\uff0c\u548c\u8fd9\u5730\u65b9\u4e00\u6bd4\uff0c\u5ba2\u6808\u91cc\u90a3\u95f4\u5c0f\u5c4b\u5b50\u7b80\u76f4\u5c31\u95f7 \u5f97\u50cf\u68fa\u6750\uff0c\u548c\u8d24\u592b\u5987\u4e00\u6bd4\uff0c\u90a3\u4e9b\u5927\u9556\u5ba2\u7b80\u76f4\u5c31\u50cf\u662f\u4e00\u7fa4\u6d3b\u9b3c\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u5931\u7b11\u9053\uff1a\u201c\u90a3\u65f6\u6211\u867d\u6234\u7740\u54ed\u8138\uff0c\u4f46\u542c\u89c1\u4f60\u66ff\u90a3\u4f4d\u5927\u9556\u5ba2\u6539\u7684\u5916\u53f7\uff0c\u51e0\u4e4e\u5fcd\u4e0d\u4f4f\u8981 \u7b11\u51fa\u58f0\u6765\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u6478\u4e86\u6478\u9f3b\u4e8e\uff0c\u9053\uff1a\u201c\u65e9\u77e5\u5ac2\u592b\u4eba\u4e5f\u542c\u5f97\u89c1\uff0c\u90a3\u53e5\u8bdd\u6211\u5c31\u4e0d\u6562\u8bf4\u51fa\u6765\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u5ffd\u7136\u9053\uff1a\u201c\u6c5f\u6e56\u4e2d\u4eba\u4eba\u90fd\u77e5\u9053\uff0c\u5f53\u4eca\u6b66\u6797\u6709\u53c1\u5927\u4e16\u5bb6\uff0c\u5176\u5386\u53f2\u4e4b\u60a0\u4e45\uff0c\u540d\u58f0\u4e4b\u54cd \u4eae\uff0c\u4ff1\u4e0d\u5728\u53c1\u5927\u5e2e\uff0c\u4e03\u5927\u6d3e\u4e4b\u4e0b\uff0c\u800c\u4e14\u6bcf\u4e00\u5bb6\u90fd\u6709\u4e16\u4ee3\u76f8\u4f20\u7684\u6b66\u529f\u79d8\u7b08\uff0c\u8db3\u4ee5\u4e0e\u5c11\u6797\u7684\u7f57\u6c49 \u795e\u62f3\uff0c\u6b66\u5f53\u7684\u4e24\u4eea\u5251\u6cd5\u5206\u5ead\u6297\u793c\uff0c\u53ea\u4e0d\u8fc7\u8fd9\u53c1\u5bb6\u95e8\u4e0b\u5b50\u5f1f\uff0c\u4ff1\u90fd\u8c28\u5b88\u5bb6\u89c4\uff0c\u6781\u5c11\u5728\u6c5f\u6e56\u95f4\u8d70 \u52a8\u800c\u5df2\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u8c08\u8bba\u8d77\u5f53\u4eca\u7684\u6b66\u6797\u5927\u52bf\u6765\uff0c\u522b\u4eba\u4e5f\u4e0d\u77e5\u8be5\u5982\u4f55\u63d2\u53e3\uff0c\u53ea\u6709\u9759\u9759\u7684\u542c\u4ed6\u8bf4\u4e0b\u53bb\u3002 \u3000\u3000\u695a\u7559\u9999\u53c8\u9053\uff1a\u201c\u8fd1\u6570\u5341\u5e74\u6765\uff0c\u8fd9\u53c1\u5927\u6b66\u6797\u4e16\u5bb6\uff0c\u66f4\u662f\u4eba\u624d\u8f88\u51fa\uff0c\u4ed6\u4eec\u867d\u4e0d\u5e38\u5728\u6c5f\u6e56\u8d70 \u52a8\uff0c\u4f46\u795e\u9f99\u5076\u73b0\uff0c\u6240\u505a\u6240\u4e3a\uff0c\u5fc5\u662f\u8db3\u4ee5\u9707\u60ca\u5929\u4e0b\u7684\u5927\u4e8b\uff0c\u8b6c\u5982\u8bf4\u2026\u2026\u2026\u201d \u3000\u3000\u80e1\u94c1\u82b1\u5fcd\u4e0d\u4f4f\u63d2\u53e3\u9053\uff1a\u201c\u8b6c\u5982\u8bf4\uff0c\u201c\u5357\u5bab\u4e16\u5bb6\u201d\u7684\u5357\u5bab\u5e73\uff0c\u6614\u5e74\u5c31\u66fe\u5728\u4e00\u591c\u4e4b\u95f4\uff0c\u626b\u5e73 \u592a\u884c\u5341\u516b\u5be8\uff0c\u800c\u4ee4\u6a2a\u884c\u5929\u4e0b\u56db\u5341\u5e74\u7684\u592a\u884c\u7fa4\u5bc7\uff0c\u4ece\u6b64\u4e00\u8e76\u4e0d\u632f\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u5fae\u7b11\u9053\uff1a\u201c\u8fd9\u5df2\u662f\u4e94\u5341\u5e74\u524d\u7684\u65e7\u4e8b\u4e86\uff0c\u6614\u5e74\u98ce\u91c7\u7fe9\u7fe9\u7684\u5357\u5bab\u516c\u5b50\uff0c\u4e5f\u5df2\u5728\u5341\u5e74\u524d \u4fbf\u5df2\u7fbd\u5316\u767b\u4ed9\uff0c\u8fd1\u4e8c\u53c1\u5341\u5e74\u6765\u2026\u2026\u201d \u3000\u3000\u80e1\u94c1\u82b1\u53c8\u5fcd\u4e0d\u4f4f\u63d2\u53e3\u9053\uff1a\u201c\u8fd1\u4e8c\u53c1\u5341\u5e74\u6765\uff0c\u6700\u8822\u52a8\u6b66\u6797\u7684\u5927\u4e8b\uff0c\u5c31\u662f\u201c\u62e5\u7fe0\u5c71\u5e84\u201d\u7684\u674e \u89c2\u9c7c\u674e\u8001\u524d\u8f88\uff0c\u4ed6\u5728\u5251\u6c60\u7684\u8bd5\u5251\u77f3\u7554\uff0c\u67ec\u9080\u5929\u4e0b\u53c1\u5341\u4e00\u4f4d\u6700\u7740\u540d\u7684\u5251\u5ba2\uff0c\u716e\u8336\u8bd5\u5251\uff0c\u800c\u674e\u8001 \u524d\u8f88\u5374\u4ee5\u4e00\u53e3\u53e4\u9c7c\u80a0\u5251\uff0c\u4e5d\u4e5d\u516b\u5341\u4e00\u624b\u51cc\u98ce\u5251\u6cd5\uff0c\u4ee4\u53c1\u5341\u4e00\u4f4d\u540d\u5251\u5ba2\u90fd\u5fc3\u60a6\u8bda\u670d\uff0c\u63a8\u4e3a\u5929\u4e0b \u7b2c\u4e00\u5251\u5ba2\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9644\u638c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u53c1\u5927\u4e16\u5bb6\u6b66\u529f\uff0c\u867d\u7136\u5404\u6709\u5343\u79cb\uff0c\u4f46\u8fd1\u53c1\u5341\u5e74\u6765\uff0c\u5374\u8fd8\u662f\u8981\u4ee5 \u59d1\u82cf\u6d77\u6d8c\u4e0a\uff0c\u201c\u62e5\u7fe0\u5c71\u5e84\u201d\u4e3a\u5176\u4e2d\u7fd8\u695a\u3002\u201d \u3000\u3000\u4ed6\u5fae\u5fae\u4e00\u7b11\uff0c\u5ffd\u7136\u8f6c\u5411\u674e\u7389\u51fd\uff0c\u5fae\u7b11\u7740\u9053\uff1a\u201c\u674e\u5144\u5c11\u5e74\u82f1\u4fca\uff0c\u6b66\u529f\u4e4b\u9ad8\uff0c\u66f4\u662f\u6c5f\u6e56\u5c11 \u89c1\uff0c\u82e5\u662f\u5728\u4e0b\u731c\u5f97\u4e0d\u9519\uff0c\u60f3\u5fc5\u5b9a\u662f\u201c\u62e5\u7fe0\u5c71\u5e84\u201d\u7684\u95e8\u4e0b\u5b50\u5f1f\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u9053\uff1a\u201c\u60ed\u6127\uff0c\u5c0f\u5f1f\u4e0d\u5b66\u65e0\u672f\uff0c\u59d4\u5b9e\u8f9c\u8d1f\u4e86\u5bb6\u95e8\u65e7\u8a89\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u674e\u5144\u592a\u8c26\u4e86\uff0c\u4e0d\u77e5\u674e\u5144\u548c\u674e\u89c2\u9c7c\u674e\u8001\u524d\u8f88\u5982\u4f55\u79f0\u547c\uff1f\u201d \u3000\u3000\u674e\u7389\u51fd\u8083\u7136\u9053\uff1a\u201c\u6b63\u662f\u5bb6\u7236\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u65e9\u5df2\u542c\u5f97\u7709\u98de\u8272\u821e\uff0c\u5fcd\u4e0d\u4f4f\u62cd\u624b\u5927\u7b11\u9053\uff1a\u201c\u96be\u602a\u8d24\u4f09\u4fea\u98ce\u91c7\u5982\u6b64\u7167\u4eba\uff0c\u6b66\u6797\u4e16\u5bb6 \u7684\u5b50\u5f1f\uff0c\u679c\u7136\u662f\u4e0d\u540c\u51e1\u4fd7\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u7b11\u9053\uff1a\u201c\u6c5f\u5c71\u4ee3\u6709\u624d\u4eba\u51fa\uff0c\u5404\u9886\u98ce\u9a9a\u6570\u5341\u5e74\uff0c\u975e\u4f46\u201c\u62e5\u7fe0\u5c71\u5e84\u201d\u7684\u540d\u58f0\uff0c\u65e9\u5df2\u88ab \u6211\u8fd9\u79cd\u4e0d\u8096\u5b50\u5f1f\u8d25\u574f\uff0c\u5c31\u8fde\u5bb6\u7236\u4e5f\u4e45\u4e0d\u6562\u518d\u81ea\u5c45\u4e3a\u5929\u4e0b\u7b2c\u4e00\u5251\u5ba2\u3002\u201d \u3000\u3000\u4ed6\u4e0d\u7b49\u695a\u7559\u9999\u548c\u80e1\u94c1\u82b1\u8bf4\u8bdd\uff0c\u62a2\u7740\u53c8\u9053\uff1a\u201c\u6614\u65e5\u5728\u5251\u6c60\u65c1\u9646\u7fbd\u8336\u4ead\u4e2d\u716e\u8336\u8bd5\u5251\u7684\u524d\u8f88\u5251 \u5ba2\u4eec\uff0c\u81f3\u4eca\u591a\u5df2\u51cb\u96f6\uff0c\u4f46\u6c5f\u6e56\u4e2d\u7684\u5f8c\u8d77\u5251\u5ba2\uff0c\u5374\u591a\u80dc\u524d\u4eba\uff0c\u636e\u5bb6\u7236\u770b\u6765\uff0c\u5f53\u4eca\u5929\u4e0b\u7684\u540d\u5bb6\u9ad8 \u624b\uff0c\u5355\u4ee5\u5251\u6cd5\u800c\u8bba\uff0c\u5c31\u8981\u6570\u859b\u8863\u4eba\u859b\u5927\u4fa0\u4e3a\u5929\u4e0b\u7b2c\u4e00\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u90a3\u53ea\u4e0d\u8fc7\u662f\u674e\u8001\u524d\u8f88\u5956\u6396\u5f8c\u8fdb\u4e4b\u610f\uff0c\u5728\u4e0b\u867d\u4e5f\u66fe\u542c\u8bf4\u8fd9\u4f4d\u859b\u8863\u4eba\u7684\u5251\u6cd5\u5947 \u5e7b\u7470\u4e3d\uff0c\u4e0d\u53ef\u65b9\u7269\uff0c\u4f46\u65e0\u8bba\u7ecf\u9a8c\u706b\u5019\uff0c\u6bd4\u8d77\u674e\u8001\u524d\u8f88\u6765\uff0c\u65e0\u7591\u8fd8\u662f\u8981\u5dee\u5f97\u5f88\u591a\uff0c\u674e\u5144\u53c8\u4f55\u5fc5 \u592a\u8c26\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8c26\u865a\u867d\u662f\u7f8e\u5fb7\uff0c\u4f46\u82e5\u592a\u8c26\u865a\uff0c\u5c31\u53cd\u800c\u5047\u4e86\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u9eef\u7136\u9053\uff1a\u201c\u4e24\u4f4d\u6709\u6240\u4e0d\u77e5\uff0c\u5bb6\u7236\u591a\u5e74\u524d\u4fbf\u5df2\u4e0d\u5e78\u67d3\u4e0a\u4e00\u79cd\u4e0d\u6cbb\u4e4b \u75c7\uff0c\u81f3\u4eca\u7ec8\u5e74\u7f20\u7ef5\u75c5\u69bb\uff0c\u5df2\u6709\u5341\u5e74\u672a\u66fe\u63d0\u5251\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u548c\u80e1\u94c1\u82b1\u90fd\u6014\u4e86\u6014\uff0c\u4e3a\u4e4b\u627c\u8155\u53f9\u606f\u3002 \u3000\u3000\u997c\u4e86\u534a\u664c\uff0c\u674e\u7389\u51fd\u5c55\u989c\u4e00\u7b11\uff0c\u53c8\u9053\uff1a\u201c\u5149\u5355\u4ee5\u5251\u800c\u8bba\uff0c\u867d\u63a8\u859b\u8863\u4eba\uff0c\u4f46\u82e5\u8bba\u673a\u667a\u6b66\u529f\uff0c \u4e34\u654c\u51b3\u80dc\uff0c\u666e\u5929\u4e4b\u4e0b\uff0c\u8fd8\u6709\u8c01\u6bd4\u5f97\u4e0a\u695a\u9999\u5e05\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u7b11\u9053\uff1a\u201c\u4ed6\u867d\u7136\u4e0d\u9519\uff0c\u4f46\u4f60\u4e5f\u83ab\u5c06\u4ed6\u6367\u5f97\u592a\u9ad8\uff0c\u4ed6\u53ef\u6ca1\u6709\u4f60\u5982\u6b64\u8c26\u865a\u7684\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u81f3\u65bc\u8bf4\uff0c\u8fd1\u5e74\u6765\u6700\u8f70\u52a8\u6b66\u6797\u7684\u5927\u4e8b\uff0c\u81ea\u7136\u4e5f\u5f97\u7b97\u695a\u9999\u5e05\u4ee5\u4e00\u4eba\u4e4b \u529b\uff0c\u63ed\u53d1\u4e86\u5357\u5bab\u7075\u548c\u201c\u5999\u50e7\u201d\u65e0\u82b1\u7684\u9634\u8c0b\uff0c\u633d\u6551\u4e86\u5c11\u6797\u548c\u4e10\u5e2e\u7684\u58f0\u8a89\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u9053\uff1a\u201c\u8fd9\u53ea\u4e0d\u8fc7\u662f\u4ef6\u5c0f\u4e8b\u800c\u5df2\uff0c\u540c\u8db3\u6302\u9f7f\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u5927\u7b11\u9053\uff1a\u201c\u4f60\u4e5f\u4e0d\u5fc5\u592a\u8c26\u4e86\uff0c\u8fd9\u4ef6\u4e8b\u82e5\u4e5f\u7b97\u662f\u5c0f\u4e8b\uff0c\u8fd8\u6709\u4ec0\u9ebd\u4e8b\u624d\u80fd\u7b97\u5f97\u4e0a\u662f\u5927 \u4e8b\uff1f\u201d \u3000\u3000\u67f3\u65e0\u7709\u5ffd\u7136\u7b11\u9053\uff1a\u201c\u82e5\u8bba\u673a\u667a\u6b66\u529f\uff0c\u4e34\u654c\u51b3\u80dc\uff0c\u56fa\u7136\u65e0\u4eba\u80fd\u53ca\u695a\u9999\u5e05\uff0c\u4f46\u8bba\u80f8\u6000\u78ca\u843d\uff0c \u6d12\u8131\u4e0d\u7f81\uff0c\u53c8\u6709\u8c01\u80fd\u6bd4\u5f97\u4e0a\u80e1\u94c1\u82b1\u5462\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5ac2\u592b\u4eba\u8bf4\u5bf9\u4e86\uff0c\u82e5\u4ee5\u559d\u9152\u800c\u8bba\uff0c\u624d\u771f\u6ca1\u6709\u4eba\u6bd4\u5f97\u4e0a\u6211\u7684\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u5fae\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u666e\u5929\u4e4b\u4e0b\uff0c\u7684\u786e\u6ca1\u6709\u4eba\u6bd4\u4f60\u9189\u5f97\u66f4\u5feb\u4e86\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u53eb\u4e86\u8d77\u6765\uff0c\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u4f60\u7adf\u6562\u5728\u675c\u5eb7\u95e8\u524d\u5356\u4e94\u52a0\u76ae\uff1f\u603b\u6709\u4e00\u5929\uff0c\u6211\u8981\u548c\u4f60\u62da \u4e00\u62da\uff0c\u770b\u770b\u7a76\u7adf\u8c01\u5148\u5012\u4e0b\u53bb\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u5ae3\u7136\u9053\uff1a\u201c\u675c\u5eb7\u95e8\u524d\u5356\u4e94\u52a0\u76ae\uff0c\u8fd9\u53e5\u8bdd\u5b9e\u5728\u8bf4\u5f97\u5999\u6781\uff0c\u5b9e\u5728\u6bd4\u5b54\u592b\u4e8e\u95e8\u524d\u5356\u767e\u5bb6 \u59d3\u8981\u751f\u52a8\u6d3b\u6cfc\u591a\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u9053\uff1a\u201c\u9664\u4e86\u4ed6\u8fd9\u79cd\u9152\u9b3c\uff0c\u8c01\u4e5f\u60f3\u4e0d\u51fa\u8fd9\u79cd\u8bdd\uff0c\u8fd9\u5c31\u53eb\u53c1\u53e5\u4e0d\u79bb\u672c\u884c\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u9053\uff1a\u201c\u4e24\u4f4d\u5b9e\u5728\u90fd\u662f\u5d4c\u5d0e\u78ca\u843d\uff0c\u809d\u80c6\u7167\u4eba\u7684\u597d\u670b\u53cb\uff0c\u5c0f\u5f1f\u80fd\u76f8\u4ea4\u4e24\u4f4d\uff0c\u5b9e\u662f\u4e0d\u80dc \u4e4b\u559c\uff0c\u5b9e\u5728\u6068\u4e0d\u5f97\u548c\u4e24\u4f4d\u591a\u76d8\u6853\u51e0\u65e5\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u9053\uff1a\u201c\u6240\u4ee5\u6211\u4eec\u5b9e\u5728\u60f3\u8bf7\u4e24\u4f4d\u5230\u201c\u62e5\u7fe0\u5c71\u5e84\u201d\u53bb\u4f5c\u5e73\u539f\u5341\u65e5\u4e4b\u996e\uff0c\u90a3\u91cc\u7684\u9646\u7fbd\u8336 \u4e95\uff0c\u53f7\u79f0\u5929\u4e0b\u7b2c\u53c1\u6cc9\uff0c\u70f9\u8336\u56fa\u5999\uff0c\u5236\u9152\u4e5f\u4e0d\u9519\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u773c\u775b\u7acb\u523b\u4eae\u4e86\u8d77\u6765\uff0c\u9644\u638c\u9053\uff1a\u201c\u6211\u65e9\u5df2\u542c\u8bf4\u201c\u62e5\u7fe0\u5c71\u5e84\u201d\u80cc\u5c71\u9762\u6c34\uff0c\u98ce\u7269\u7edd\u4f73\uff0c \u65e9\u5df2\u5df4\u4e0d\u5f97\u80fd\u5230\u90a3\u91cc\u53bb\u901b\u901b\u4e86\uff0c\u4e5f\u597d\u4e00\u7779\u5929\u4e0b\u7b2c\u4e00\u5251\u5ba2\u7684\u624b\u91c7\u3002\u201d \u3000\u3000\u4ed6\u77a7\u4e86\u695a\u7559\u9999\u4e00\u773c\uff0c\u53c8\u4e0d\u7981\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u6211\u8fd8\u8981\u966a\u4ed6\u53bb\u627e\u51e0\u4e2a\u4eba\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u7acb\u523b\u6309\u7740\u9053\uff1a\u201c\u5728\u4e0b\u53c8\u4f55\u5c1d\u4e0d\u60f3\u62dc\u8c12\u674e\u8001\u524d\u8f88\uff0c\u53ea\u6068\u4fd7\u52a1\u592a\u591a\uff0c\u8fd9\u6b21\u6015\u4e0d\u80fd\u53bb \u4e86\uff0c\u597d\u5728\u6765\u65e5\u65b9\u957f\uff0c\u4ee5\u5f8c\u5fc5\u5b9a\u8fd8\u6709\u673a\u4f1a\u7684\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u773c\u6ce2\u6d41\u52a8\uff0c\u60a0\u7136\u9053\uff1a\u201c\u90a3\u5b9e\u5728\u592a\u9057\u61be\uff0c\u6211\u4eec\u5bb6\u91cc\u6709\u51e0\u4e2a\u4eba\u6b63\u5728\u6025\u7740\u60f3\u89c1\u89c1\u695a\u9999\u5e05 \u54e9\uff1a\u201c\u695a\u7559\u9999\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u4f60\u4e5f\u4e0d\u5fc5\u95ee\uff0c\u60f3\u89c1\u4f60\u7684\u4eba\uff0c\u4e00\u5b9a\u662f\u5341\u516d\u4e03\u5c81\u7684\u5c0f\u6cf5\u5a18\uff0c\u4ec0\u9ebd\u4e8b\u4e5f\u4e0d\u61c2\uff0c\u4e5f \u4e0d\u77e5\u4ece\u90a3\u91cc\u542c\u8bf4\u4ec0\u9ebd\u201c\u76d7\u5e05\u591c\u7559\u9999\u201d\u7f57\u4e86\uff01\u6d41\u6c13\u4e2d\u7684\u516c\u738b\u7f57\uff01\u5c31\u4e00\u5fc3\u8ba4\u5b9a\u4f60\u662f\u4e2a\u5f88\u4e86\u4e0d\u8d77\u7684 \u4eba\uff0c\u674e\u5144\uff0c\u6211\u8bf4\u7684\u5bf9\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u67f3\u65e0\u7709\u5931\u7b11\u9053\uff1a\u201c\u90a3\u51e0\u4f4d\u7684\u786e\u90fd\u662f\u8c46\u853b\u5e74\u534e\u7684\u5c11\u5973\uff0c\u4f46\u4f60\u8bf4\u5979\u4eec\u4e0d\u61c2\u4e8b\uff0c\u53ef\u5c31\u5927\u9519 \u4e86\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u67f3\u65e0\u7709\u9053\uff1a\u201c\u90a3\u51e0\u4f4d\u59d1\u5a18\u975e\u4f46\u90fd\u662f\u6587\u6b66\u5168\u624d\uff0c\u806a\u660e\u7f8e\u4e3d\uff0c\u800c\u4e14\u5176\u4e2d\u8fd8\u6709\u4e00\u4f4d\u66f4\u662f\u5b66\u5bcc\u4e94 \u8f66\uff0c\u624d\u9ad8\u516b\u6597\u7684\u626b\u7709\u624d\u5b50\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u54e6\uff1a\u5979\u53eb\u4ec0\u9ebd\u540d\u5b57\uff1f\u201d \u3000\u3000\u67f3\u65e0\u7709\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u5979\u7684\u540d\u5b57\u53eb\u82cf\u84c9\u84c9\u3002\u201d \u7b2c\u4e09\u7ae0\u3000\u6697\u5668\u4e4b\u738b>> \u53e4\u9f99\u300a\u695a\u7559\u9999\u7cfb\u5217\xb7\u753b\u7709\u9e1f\u300b \u7b2c\u4e09\u7ae0\u3000\u6697\u5668\u4e4b\u738b \u3000\u3000\u5929\u9ad8\u6c14\u723d\uff0c\u53c1\u8f86\u534e\u4e3d\u7684\u9a6c\u8f66\uff0c\u5954\u884c\u5728\u5939\u836b\u5927\u9053\u4e0a\u3002 \u3000\u3000\u6700\u524d\u9762\u4e00\u8f86\u9a6c\u8f66\uff0c\u8f66\u5b50\u91cc\u597d\u50cf\u5e76\u6ca1\u6709\u4eba\uff0c\u5374\u627e\u516d\u6761\u52a8\u88c5\u6025\u670d\u7684\u5927\u6c49\uff0c\u8de8\u7740\u8f66\u8f95\uff0c\u4e00\u4e2a\u4e2a \u4ff1\u662f\u795e\u60c5\u9aa0\u608d\uff0c\u76ee\u5149\u654f\u9510\uff0c\u4e00\u671b\u800c\u77e5\u90fd\u662f\u6c5f\u6e56\u597d\u624b\uff0c\u8fd9\u79cd\u4eba\u5c45\u7136\u4e5f\u4f1a\u505a\u522b\u4eba\u7684\u5bb6\u5974\uff0c\u4ed6\u4eec\u7684 \u4e3b\u4eba\u5982\u4f55\uff0c\u81ea\u7136\u53ef\u60f3\u800c\u77e5\u3002 \u3000\u3000\u6700\u5f8c\u4e00\u8f86\u8f66\u5b50\u91cc\uff0c\u4e0d\u65f6\u4f20\u51fa\u5a07\u5a9a\u7684\u83ba\u58f0\u71d5\u8bed\uff0c\u53ea\u53ef\u60dc\u8f66\u7a97\u95ed\u5f97\u90a3\u9ebd\u7d27\uff0c\u8c01\u4e5f\u4f11\u60f3\u77a7\u5f97\u89c1 \u8f66\u4e2d\u4eba\u7684\u9762\u76ee\u3002 \u3000\u3000\u4e2d\u95f4\u7684\u90a3\u4e00\u8f86\u8f66\u53a2\u6700\u5bbd\u655e\uff0c\u4e5f\u6700\u534e\u4e3d\uff0c\u8f66\u7a97\u867d\u662f\u655e\u5f00\u7740\u7684\uff0c\u5374\u6302\u7740\u7af9\u5e18\uff0c\u5e18\u5b50\u91cc\u4e0d\u65f6\u4f20 \u51fa\u4e00\u9635\u9635\u723d\u6717\u7684\u7b11\u58f0\u3002 \u3000\u3000\u8fd9\u7b11\u58f0\u6b63\u662f\u695a\u7559\u9999\u548c\u80e1\u94c1\u82b1\u53d1\u51fa\u6765\u7684\u2014\u2014\u542c\u89c1\u82cf\u84c9\u84c9\u5979\u4eec\u5c31\u5728\u62e5\u7fe0\u5c71\u5e84\uff0c\u4ed6\u4eec\u600e\u4f1a\u4e0d\u8ddf \u674e\u7389\u51fd\u4e00\u9f50\u56de\u53bb\u3002 \u3000\u3000\u8fd9\u8f86\u9a6c\u8f66\u5236\u4f5c\u5f97\u867d\u4e0d\u5982\u59ec\u51b0\u96c1\u90a3\u8f86\u5de7\u5999\uff0c\u4f46\u5374\u66f4\u5bbd\u655e\uff0c\u66f4\u8212\u670d\uff0c\u4ee4\u4eba\u4e0d\u89c9\u65c5\u9014\u52b3\u987f\u4e4b \u82e6\u3002 \u3000\u3000\u695a\u7559\u9999\u867d\u4e0d\u6b62\u4e00\u6b21\u5728\u95ee\uff1a\u201c\u84c9\u513f\u5979\u4eec\u662f\u600e\u9ebd\u5230\u4e86\u62e5\u7fe0\u5c71\u5e84\u7684\uff1f\u201d \u3000\u3000\u67f3\u65e0\u7709\u5374\u603b\u662f\u7b11\u7740\u9053\uff1a\u201c\u6211\u73b0\u5728\u53ef\u8981\u5356\u4e2a\u5173\u5b50\uff0c\u53cd\u6b63\u4f60\u89c1\u5230\u82cf\u59d1\u5a18\u5f8c\uff0c\u5c31\u4f1a\u77e5\u9053\u7684\u3002\u201d \u3000\u3000\u8f66\u884c\u975e\u6b62\u4e00\u65e5\uff0c\u53c8\u56de\u5230\u4e86\u4e2d\u539f\uff0c\u9053\u4e0a\u7684\u8f66\u9a6c\u6e10\u591a\uff0c\u77a7\u89c1\u8fd9\u9ebd\u6837\u53c1\u8f86\u9a6c\u8f66\uff0c\u81ea\u7136\u4eba\u4eba\u4e3a\u4e4b \u4fa7\u76ee\u3002 \u3000\u3000\u8fd9\u4e00\u65e5\u5230\u4e86\u5f00\u5c01\uff0c\u6b63\u662f\u508d\u665a\uff0c\u4e00\u884c\u4eba\u5c31\u5728\u57ce\u91cc\u6b47\u4e0b\u3002 \u3000\u3000\u5403\u8fc7\u4e86\u665a\u996d\uff0c\u559d\u8fc7\u4e86\u51e0\u676f\u9152\u5f8c\uff0c\u5927\u5bb6\u5c31\u5206\u522b\u56de\u623f\u5b89\u6b47\u4e86\uff0c\u53ea\u6709\u80e1\u94c1\u82b1\u8fd8\u662f\u8001\u813e\u6c14\u5750\u5728\u695a \u7559\u9999\u5c4b\u91cc\u4e0d\u80af\u8d70\u3002 \u3000\u3000\u695a\u7559\u9999\u60f3\u5230\u4e0d\u4e45\u4ee5\u524d\u8fd9\u53e4\u57ce\u91cc\u906d\u9047\u5230\u7684\u79cd\u79cd\u60ca\u9669\u5947\u79d8\u4e4b\u4e8b\u4e5f\u4e0d\u7981\u4e3a\u4e4b\u5fc3\u9a70\u795e\u52a8\u6b63\u597d\u4e5f\u7761 \u4e0d\u7740\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u7b11\u9053\uff1a\u201c\u4f60\u773c\u5149\u5b9e\u5728\u4e0d\u9519\uff0c\u674e\u7389\u51fd\u592b\u5987\u4f7f\u7684\u7684\u786e\u662f\u201c\u91d1\u4e1d\u7ef5\u638c\u201d\uff0c\u65b9\u4ed9\u5ba2\u7d20\u65e0\u4f20 \u4eba\uff0c\u5374\u548c\u674e\u89c2\u9c7c\u662f\u751f\u6b7b\u4e4b\u4ea4\uff0c\u6240\u4ee5\u5c31\u5c06\u4e00\u8eab\u7edd\u6280\u4f20\u7ed9\u4ed6\u7684\u513f\u5b50\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u957f\u53f9\u9053\uff1a\u201c\u4ee4\u4eba\u60f3\u4e0d\u5230\u7684\u662f\uff0c\u6614\u65e5\u7684\u7b2c\u4e00\u5251\u5ba2\uff0c\u5982\u201d\u7adf\u5df2\u6210\u4e86\u5e9f\u4eba\uff0c\u6b66\u6797\u4e00\u8f88\u65e5 \u6e10\u51cb\u96f6\uff0c\u5b9e\u5728\u4ee4\u4eba\u53ef\u60b2\u53ef\u53f9\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u597d\u5728\u4ed6\u8fd8\u6709\u8fd9\u9ebd\u4e00\u4e2a\u4ed4\u513f\u5b50\uff0c\u201c\u4e5d\u4e5d\u516b\u5341\u4e00\u5f0f\u51cc\u98ce\u5251\u201d\uff0c\u518d\u52a0\u4e0a\u201c\u91d1\u4e1d\u7ef5 \u638c\u201d\uff0c\u62e5\u7fe0\u5c71\u5e84\u8fd8\u6015\u4e0d\u5728\u4ed6\u624b\u91cc\u66f4\u53d1\u626c\u5149\u5927\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4ee5\u6211\u770b\u6765\uff0c\u67f3\u65e0\u7709\u7684\u6b66\u529f\u975e\u4f46\u4e0d\u5728\u5979\u592b\u5a7f\u4e4b\u4e0b\uff0c\u800c\u4e14\u8fd8\u50cf\u662f\u6bd4\u674e\u7389\u51fd\u9ad8 \u4e9b\uff0c\u5c24\u5176\u662f\u5979\u7684\u8f7b\u529f\u8eab\u6cd5\uff0c\u66f4\u9ad8\u51fa\u8bb8\u591a\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u53c1\u5927\u6b66\u6797\u4e16\u5bb6\u7684\u7edd\u6280\u4ff1\u662f\u4f20\u5ab3\u4e0d\u4f20\u5973\uff0c\u5979\u65e2\u7136\u505a\u4e86\u674e\u89c2\u9c7c\u7684\u5ab3\u5987\uff0c\u6b66\u529f\u81ea \u7136\u4e5f\u7edd\u4e0d\u4f1a\u5dee\u7684\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u5979\u5ac1\u5230\u674e\u5bb6\u53bb\uff0c\u7edd\u4e0d\u4f1a\u8d85\u8fc7\u5341\u5e74\uff0c\u800c\u8fd9\u79cd\u6b66\u6797\u4e16\u5bb6\u7684\u5b50\u5f1f\uff0c\u5927\u591a\u4ece\u53c1\u4e94\u5c81 \u65f6\u5c31\u5f00\u59cb\u7ec3\u6b66\uff0c\u674e\u7389\u51fd\u81ea\u4e5f\u4e0d\u4f1a\u4f8b\u5916\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u770b\u4ed6\u8eab\u4e0a\u6700\u5c11\u4e5f\u6709\u7740\u5341\u5e74\u7684\u82e6\u529f\u592b\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u67f3\u65e0\u7709\u7684\u6b66\u529f\u5c31\u4e0d\u8be5\u6bd4\u674e\u7389\u51fd\u9ad8\uff0c\u9664\u975e\u5979\u7684\u5a18\u5bb6\u4e5f\u662f\u6b66\u6797\u540d \u5bb6\uff0c\u4f46\u73af\u987e\u5929\u4e0b\uff0c\u53c8\u6709\u51e0\u4e2a\u4eba\u6559\u5f92\u5f1f\u80fd\u6bd4\u674e\u89c2\u9c7c\u6559\u5f97\u597d\u5462\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u76b1\u7709\u9053\uff1a\u201c\u4f60\u83ab\u975e\u53c8\u5728\u731c\u7591\u4eba\u5bb6\u7684\u6765\u5386\u4e86\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u6211\u51e0\u6b21\u60f3\u63a2\u95ee\u5979\u7684\u5e08\u627f\uff0c\u5979\u603b\u662f\u5c94\u4e86\u5f00\u53bb\uff0c\u7531\u6b64\u53ef\u89c1\uff0c\u5979\u7edd\u4e0d\u4f1a\u662f\u56db\u5927 \u5e2e\uff0c\u4e03\u5927\u6d3e\u7684\u95e8\u4e0b\uff0c\u6211\u4e5f\u60f3\u4e0d\u51fa\u5f53\u4eca\u6b66\u6797\u4e2d\u6709\u4ec0\u9ebd\u59d3\u67f3\u7684\u524d\u8f88\u9ad8\u4eba\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u65e0\u8bba\u5982\u4f55\uff0c\u4f60\u603b\u4e0d\u80fd\u6000\u7591\u674e\u89c2\u9c7c\u7684\u5ab3\u5987\u4f1a\u662f\u753b\u7709\u9e1f\u5427\uff1a\u4f55\u51b5\uff0c\u5c31\u7b97\u5b83\u662f\u753b \u7709\u9e1f\u53c8\u600e\u6837\uff1f\u753b\u7709\u9e1f\u5bf9\u54b1\u4eec\u53ef\u53ea\u6709\u597d\u5904\uff0c\u6ca1\u6709\u8fc7\u8282\uff0c\u8fde\u6211\u8fd9\u6761\u547d\uff0c\u8fd8\u662f\u753b\u7709\u9e1f\u6551\u56de\u6765\u7684\u54e9\uff01 \u5979\u82e5\u662f\u753b\u7709\u9e1f\uff0c\u6211\u53ea\u6709\u66f4\u611f\u6fc0\u5979\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u4e86\u7b11\uff0c\u4e0d\u518d\u8bf4\u8bdd\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u7a81\u542c\u4e00\u9635\u53eb\u558a\u58f0\u81ea\u9694\u58c1\u5c4b\u5b50\u4f20\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u76b1\u7709\u7b11\u9053\uff1a\u201c\u5982\u6b64\u6069\u7231\u7684\u5c0f\u4e24\u53e3\u5b50\uff0c\u96be\u9053\u4e5f\u4f1a\u6253\u67b6\u9ebd\uff1f\u201d \u3000\u3000\u53ea\u542c\u90a3\u53eb\u558a\u58f0\u8d8a\u6765\u8d8a\u5c16\u9510\uff0c\u800c\u4e14\u50cf\u662f\u5145\u6ee1\u4e86\u75db\u82e6\uff0c\u6b63\u662f\u67f3\u65e0\u7709\u53d1\u51fa\u7684\uff0c\u80e1\u94c1\u82b1\u5634\u91cc\u8bf4\u7740 \u8bdd\uff0c\u4eba\u5df2\u51b2\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u695a\u7559\u9999\u4e5f\u53ea\u6709\u968f\u5f8c\u800c\u51fa\uff0c\u53ea\u89c1\u9662\u5b50\u91cc\u9759\u6084\u6084\u7684\uff0c\u8ddf\u7740\u8fd9\u592b\u5987\u4e24\u4eba\u7684\u5bb6\u4e01\u4f8d\u5973\u4eec\uff0c\u7adf\u6ca1\u6709 \u4e00\u4e2a\u4eba\u51fa\u6765\u63a2\u671b\u3002 \u3000\u3000\u4ed6\u4eec\u82e5\u4e0d\u662f\u804b\u5b50\uff0c\u5c31\u5fc5\u5b9a\u542c\u5230\u8fd9\u53eb\u558a\u58f0\uff0c\u5374\u4e3a\u4ec0\u9ebd\u7adf\u6ca1\u6709\u4eba\u51fa\u6765\u77a7\u4e2a\u7a76\u7adf\u5462\uff1f\u96be\u9053\u4ed6\u4eec \u5df2\u542c\u60ef\u4e86\u4e0d\u6210\u3002 \u3000\u3000\u67f3\u65e0\u7709\u7684\u5c4b\u5b50\u91cc\uff0c\u706f\u8fd8\u662f\u4eae\u7740\u7684\u3002 \u3000\u3000\u53ea\u542c\u67f3\u65e0\u7709\u98a4\u58f0\u9053\uff1a\u201c\u4f60\u6740\u4e86\u6211\u5427\uff01\u6740\u4e86\u6211\u5427\uff01\u201d \u3000\u3000\u80e1\u94c1\u82b1\u8138\u4e0a\u53d8\u4e86\u989c\u8272\uff0c\u521a\u60f3\u51b2\u8fdb\u53bb\uff0c\u53c8\u542c\u5f97\u674e\u7389\u51fd\u9053\uff1a\u201c\u5fcd\u8010\u4e9b\uff0c\u5fcd\u8010\u4e9b\uff0c\u83ab\u5435\u9192\u4e86\u522b \u4eba\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u5636\u58f0\u9053\uff1a\u201c\u5b9e\u5728\u5fcd\u8010\u4e0d\u4f4f\u4e86\uff0c\u4e0e\u5176\u8fd9\u6837\u53d7\u82e6\uff0c\u5012\u4e0d\u5982\u6b7b\u4e86\u7684\u597d\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u8fd9\u624d\u77e5\u9053\u4ed6\u4eec\u592b\u5987\u5e76\u4e0d\u662f\u6253\u67b6\uff0c\u5fcd\u4e0d\u4f4f\u9053\uff1a\u201c\u83ab\u975e\u5979\u5ffd\u7136\u5f97\u4e86\u6025\u75db\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u6c88\u58f0\u9053\uff1a\u201c\u8fd9\u75db\u6015\u5e76\u4e0d\u662f\u7a81\u53d1\u7684\uff0c\u800c\u662f\u5bbf\u75be\uff0c\u800c\u4e14\u8fd8\u5fc5\u5b9a\u65f6\u5e38\u53d1\u4f5c\uff0c\u6240\u4ee5\u8fde\u4ed6\u4eec \u7684\u4f63\u4eba\u90fd\u5df2\u542c\u60ef\u4e86\uff0c\u5426\u5219\u600e\u4f1a\u4e00\u4e2a\u4e2a\u8eb2\u5728\u5c4b\u91cc\u4e0d\u51fa\u6765\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u53f9\u9053\uff1a\u201c\u8fd9\u75db\u82e6\u4e00\u53d1\u4f5c\u60f3\u5fc5\u5c31\u5f88\u5389\u5bb3\uff0c\u5426\u5219\u50cf\u67f3\u65e0\u7709\u8fd9\u6837\u7684\u4eba\u7edd\u4e0d\u4f1a\u558a\u51fa\u58f0\u6765 \u7684\uff0c\u5374\u4e0d\u77e5\u5979\u751f\u7684\u7a76\u7adf\u662f\u4ec0\u9ebd\u75c5\u5462\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u6c88\u541f\u9053\uff1a\u201c\u5979\u5e73\u65f6\u770b\u6765\u5012\u4e5f\u548c\u5e38\u4eba\u65e0\u5f02\uff0c\u60f3\u4e0d\u5230\u4e00\u53d1\u4f5c\u5c31\u5982\u6b64\u53ef\u6015\uff0c\u6211\u770b\uff0c\u5979\u8fd9 \u4e5f\u8bb8\u5e76\u4e0d\u662f\u75c5\uff0c\u800c\u662f\u4e2d\u4e86\u4ec0\u9ebd\u6781\u5389\u5bb3\u7684\u6bd2\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u53d8\u8272\u9053\uff1a\u201c\u6bd2\uff1f\u5979\u82e5\u4e2d\u4e86\u6bd2\uff0c\u674e\u89c2\u9c7c\u4e3a\u4f55\u4e0d\u60f3\u6cd5\u5b50\u6551\u4ed6\uff0c\u4eba\u95fb\u674e\u89c2\u9c7c\u533b\u9053\u6781\u9ad8 \u660e\uff0c\u62e5\u7fe0\u5c71\u5e84\u4e2d\u6765\u5f80\u7684\u53c8\u90fd\u662f\u524d\u8f88\u9ad8\u4eba\uff0c\u65b9\u4ed9\u5ba2\u66f4\u662f\u89e3\u6bd2\u7684\u540d\u5bb6\uff0c\u8fd9\u8bb8\u591a\u4eba\u96be\u9053\u90fd\u65e0\u6cd5\u89e3\u5f97 \u4e86\u5979\u7684\u6bd2\uff1f\u5374\u773c\u89c1\u7740\u5979\u53d7\u82e6\u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u53f9\u4e86\u53e3\u6c14\uff0c\u53c8\u4e0d\u8bf4\u8bdd\u4e86\u3002 \u3000\u3000\u5c4b\u5b50\u91cc\u4e0d\u65ad\u4f20\u51fa\u67f3\u65e0\u7709\u7684\u547b\u541f\u5598\u606f\u58f0\uff0c\u674e\u7389\u51fd\u7684\u4f4e\u8bed\u5b89\u6170\u58f0\uff0c\u5e8a\u677f\u88ab\u538b\u7684\u5431\u5431\u683c\u683c\u58f0\u3002 \u3000\u3000\u663e\u89c1\u67f3\u65e0\u7709\u7684\u75db\u82e6\u5e76\u672a\u51cf\u8f7b\uff0c\u5979\u53d7\u82e6\u4e0d\u8fc7\uff0c\u6b63\u5728\u4e0d\u505c\u7684\u6323\u624e\uff0c\u674e\u7389\u51fd\u6b63\u5728\u52aa\u529b\u538b\u5236\u7740 \u5979\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u9ebd\u4e0d\u8fdb\u53bb\u77a7\u77a7\uff0c\u6216\u8bb8\u4f60\u80fd\u89e3\u5f97\u4e86\u5979\u7684\u6bd2\u4e5f\u672b\u53ef\u77e5\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u53f9\u9053\uff1a\u201c\u67f3\u65e0\u7709\u662f\u4e2a\u5f88\u597d\u5f3a\u7684\u5973\u4eba\uff0c\u5fc5\u5b9a\u4e0d\u613f\u610f\u7834\u4eba\u770b\u5230\u5979\u5982\u6b64\u72fc\u72c8\u7684\u6a21\u6837\uff0c\u6709 \u4ec0\u9ebd\u8bdd\uff0c\u8fd8\u662f\u7b49\u5230\u660e\u5929\u518d\u8bf4\u5427\uff1a\u201c\u7a81\u542c\u201c\u6251\u843d\u201d\u4e00\u58f0\uff0c\u9662\u5b50\u7684\u68a7\u6850\u6811\u4e0a\uff0c\u4e00\u53ea\u5bbf\u96c1\u60ca\u8d77\uff0c\u695a \u7559\u9999\u773c\u89d2\u4f3c\u4e4e\u77a5\u89c1\u6728\u53f6\u4e2d\u6709\u94f6\u5149\u4e00\u95ea\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u5df2\u6709\u4e00\u84ec\u94f6\u4e24\u81ea\u6811\u4e1b\u4e2d\u66b4\u5c04\u800c\u51fa\uff0c\u76f4\u6253\u695a\u7559\u9999\uff0c\u6765\u52bf\u4e4b\u6025\u7edd\u975e\u8a00\u8bed\u6240\u80fd\u5f62 \u5bb9\u3002 \u3000\u3000\u82e5\u4e0d\u662f\u90a3\u53ea\u60ca\u8d77\u7684\u5bbf\u96c1\uff0c\u6b64\u756a\u695a\u7559\u9999\u5c31\u5f97\u4e27\u751f\u5728\u8fd9\u4e00\u84ec\u94f6\u5149\u4e4b\u4e0b\uff0c\u53ea\u56e0\u7b49\u4ed6\u542c\u5230\u98ce\u58f0 \u65f6\uff0c\u518d\u95ea\u907f\u5df2\u6765\u4e0d\u53ca\u4e86\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u95f4\u4e0d\u5bb9\u53d1\u7684\u5239\u90a3\u95f4\uff0c\u4ed6\u4e00\u62f3\u5c06\u80e1\u94c1\u82b1\u6253\u5f97\u4ef0\u5929\u8dcc\u5012\uff0c\u81ea\u5df1\u7684\u8eab\u5b50\u4e5f\u6251\u5012\u5728\u80e1\u94c1\u5316 \u8eab\u4e0a\u3002 \u3000\u3000\u53ea\u542c\u201c\u53ee\u53ee\u53ee\u201d\u4e00\u9635\u6025\u54cd\uff0c\u5982\u66b4\u96e8\u6572\u7816\uff0c\u6570\u5341\u70b9\u94f6\u661f\u5df2\u9489\u5728\u4ed6\u8eab\u65c1\u7684\u5730\u4e0a\uff0c\u76f4\u6ca1\u5165\u571f\u3002 \u3000\u3000\u63a5\u7740\uff0c\u4e00\u6761\u4eba\u5f71\u81ea\u6811\u5f71\u4e2d\u7684\u5899\u5934\u4e0a\u51b2\u5929\u800c\u8d77\uff0c\u51cc\u7a7a\u4e00\u6298\uff0c\u540c\u5899\u5916\u7684\u6c88\u6c88\u591c\u8272\u4e2d\u7a9c\u4e86\u51fa \u53bb\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u8fd8\u672b\u5f04\u6e05\u662f\u600e\u9ebd\u56de\u4e8b\uff0c\u695a\u7559\u9999\u7684\u8eab\u5f62\u4e5f\u5df2\u63a0\u51fa\u5899\u5916\uff0c\u80e1\u94c1\u82b1\u77a7\u4e86\u6ee1\u5730\u7684\u94f6\u661f\u4e00 \u773c\uff0c\u5ffd\u4f3c\u60f3\u8d77\u4e86\u4ec0\u9ebd\uff0c\u53d8\u8272\u5927\u53eb\u9053\uff1a\u201c\u8001\u81ed\u866b\uff0c\u5c0f\u5fc3\u4e86\uff0c\u8fd9\u597d\u50cf\u662f\u201c\u66b4\u96e8\u68a8\u82b1\u9488\u201d\u3002\u201d \u3000\u3000\u547c\u58f0\u4e2d\uff0c\u4ed6\u7684\u4eba\u4e5f\u8ffd\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u51c4\u8ff7\u7684\u591c\u8272\u4e2d\uff0c\u6709\u8584\u96fe\u5347\u8d77\uff0c\u695a\u7559\u9999\u7684\u8eab\u5f62\u8fd8\u4f9d\u7a00\u53ef\u4ee5\u5206\u8fa8\uff0c\u524d\u9762\u90a3\u4eba\u5374\u8fde\u5f71\u5b50\u90fd\u77a7\u4e0d \u89c1\u4e86\u3002 \u3000\u3000\u53e3\u53e3\u53e3 \u3000\u3000\u96fe\uff0c\u672c\u6765\u8fd8\u662f\u8f7b\u8f7b\u7684\uff0c\u6de1\u6de1\u7684\uff0c\u4f46\u7247\u523b\u95f4\u5c31\u5df2\u6d53\u5f97\u50cf\u662f\u767d\u70df\uff0c\u6e10\u6e10\u8fde\u695a\u7559\u9999\u7684\u4eba\u90fd\u5df2\u77a7 \u4e0d\u89c1\u3002 \u3000\u3000\u8fdc\u5904\u672c\u6765\u8fd8\u6709\u70b9\u70b9\u706f\u706b\uff0c\u4f46\u73b0\u5728\u8fde\u706f\u5149\u4e5f\u6ca1\u5165\u6d53\u96fe\u91cc\uff0c\u80e1\u94c1\u82b1\u7b80\u76f4\u5feb\u6025\u75af\u4e86\uff0c\u5374\u53c8\u4e0d\u6562 \u51fa\u58f0\u547c\u5524\u3002 \u3000\u3000\u56e0\u4e3a\u5728\u8fd9\u79cd\u60c5\u51b5\u4e0b\uff0c\u53ea\u8981\u4e00\u51fa\u58f0\uff0c\u5c31\u53ef\u53d8\u6210\u6697\u5668\u7684\u9776\u5b50\uff0c\u80e1\u94c1\u82b1\u77e5\u9053\u8fd9\u65f6\u82e5\u6709\u6697\u5668\u5c04 \u6765\uff0c\u4ed6\u662f\u4e07\u4e07\u8eb2\u4e0d\u5f00\u7684\u3002 \u3000\u3000\u4ed6\u4e0d\u7981\u66f4\u66ff\u695a\u7559\u9999\u7740\u6025\uff0c\u56e0\u4e3a\u695a\u7559\u9999\u7684\u5904\u5883\u66f4\u5371\u9669\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u4ed6\u5ffd\u7136\u77a5\u89c1\u524d\u9762\u7684\u5730\u4e0a\u6709\u4eae\u5149\u95ea\u95ea\u7684\u4e1c\u897f\uff0c\u6361\u8d77\u6765\u4e00\u770b\uff0c\u7adf\u662f\u4e2a\u6241\u6241\u7684\u94f6\u5323 \u5b50\u3002 \u3000\u3000\u8fd9\u94f6\u5323\u5b50\u4e03\u5bf8\u957f\uff0c\u53c1\u5bf8\u539a\uff0c\u5236\u4f5c\u5f97\u6781\u4e3a\u7cbe\u81f4\uff0c\u5323\u5b50\u7684\u4e00\u65c1\u6392\u5217\u7740\u53c1\u884c\u6781\u7ec6\u7684\u9488\u5b54\uff0c\u6bcf\u884c \u4e5d\u5b54\u3002 \u3000\u3000\u5323\u5b50\u7684\u4e0a\u9762\uff0c\u96d5\u523b\u7740\u6781\u7ec6\u7684\u82b1\u7eb9\uff0c\u4ed4\u7ec6\u4e00\u770b\uff0c\u624d\u77e5\u9053\u8fd9\u82b1\u7eb9\u7adf\u662f\u4e24\u884c\u5b57\uff0c\u4f3c\u662f\u5c0f\u7bc6\uff0c\u53c8 \u4f3c\u949f\u9f0e\u6587\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u770b\u4e86\u534a\u5929\uff0c\u4e5f\u8ba4\u4e0d\u51fa\u7a76\u7adf\u662f\u4ec0\u9ebd\uff0c\u4ed6\u5fcd\u4e0d\u4f4f\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u5583\u5583\u9053\uff1a\u201c\u770b\u6765\u4ee5\u5f8c \u6211\u975e\u4f46\u8981\u591a\u7ec3\u7ec3\u8f7b\u529f\uff0c\u8fd8\u5f97\u591a\u8bfb\u4e9b\u4e66\u624d\u884c\u3002\u201d \u3000\u3000\u4ed6\u6b63\u60f3\u518d\u5f80\u524d\u8d70\uff0c\u5ffd\u89c9\u4e00\u9635\u6025\u98ce\u81ea\u8eab\u65c1\u63a0\u6765\uff0c\u4e00\u53ea\u624b\u5207\u5411\u4ed6\u7684\u8f6f\u80c1\u4e0b\uff0c\u53e6\u4e00\u53ea\u5374\u53bb\u62a2\u90a3 \u94f6\u5323\u5b50\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u6697\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u6211\u6b63\u6101\u627e\u4e0d\u7740\u4f60\uff0c\u4f60\u5374\u9001\u4e0a\u95e8\u6765\u4e86\u3002\u201d\u5fc3\u5ff5\u4e00\u95ea\u95f4\uff0c\u5df2\u51fb\u51fa\u4e00 \u62f3\uff0c\u8e22\u51fa\u4e00\u811a\u3002 \u3000\u3000\u8fd9\u4e00\u62f3\u4e00\u811a\u8bf4\u6765\u7b80\u5355\uff0c\u5176\u5b9e\u5374\u5927\u4e0d\u7b80\u5355\uff0c\u53ea\u56e0\u8fd9\u4eba\u81ea\u4ed6\u5de6\u8fb9\u6251\u6765\uff0c\u4ed6\u4e00\u5b9a\u8981\u5c06\u6574\u4e2a\u8eab\u5b50 \u90fd\u626d\u8f6c\u8fc7\u53bb\uff0c\u624d\u80fd\u907f\u5f97\u5f00\u5bf9\u65b9\u7684\u653b\u51fb\uff0c\u624d\u80fd\u53cd\u51fb\uff0c\u7531\u6b64\u53ef\u89c1\u80e1\u94c1\u82b1\u7684\u9152\u867d\u559d\u5f97\u4e0d\u5c11\uff0c\u4f46\u8170\u8eab \u4ecd\u7075\u6d3b\u5982\u86c7\u3002 \u3000\u3000\u8c01\u77e5\u5bf9\u65b9\u7684\u8eab\u5f62\u5374\u6bd4\u4ed6\u66f4\u7075\u6d3b\uff0c\u8f7b\u8f7b\u4e00\u95ea\u5df2\u5230\u4e86\u4ed6\u8eab\u5f8c\uff0c\u80e1\u94c1\u82b1\u8fd9\u624d\u771f\u5403\u4e86\u4e00\u60ca\uff0c\u521a\u60f3 \u8f6c\u8eab\u3002 \u3000\u3000\u90a3\u4eba\u7adf\u6c88\u58f0\u9053\uff1a\u201c\u5c0f\u80e1\uff0c\u662f\u4f60\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u5ffd\u7136\u95f4\u677e\u4e86\u4e00\u5927\u53e3\u6c14\uff0c\u82e6\u7b11\u7740\u9053\uff1a\u201c\u4f60\u73b0\u5728\u600e\u5730\u4e5f\u548c\u6211\u4e00\u6837\uff0c\u8fde\u62db\u547c\u4e5f\u4e0d\u6253\u5c31\u51fa \u624b\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u4e5f\u4e0d\u7981\u82e6\u7b11\u9053\uff1a\u201c\u6211\u89c1\u5230\u4f60\u624b\u4e0a\u6709\u94f6\u5149\u95ea\u52a8\uff0c\u81ea\u7136\u8ba4\u5b9a\u4e86\u4f60\u5fc5\u5b9a\u662f\u90a3\u53d1\u6697\u5668\u7684 \u4eba\uff0c\u53c8\u8c01\u60f3\u5f97\u5230\u8fd9\u4e1c\u897f\u7adf\u4f1a\u5230\u4e86\u4f60\u624b\u4e0a\u5462\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u7728\u4e86\u7728\u773c\u775b\uff0c\u9053\uff1a\u201c\u8fd9\u4f60\u90fd\u60f3\u4e0d\u5230\u9ebd\uff1f\u6211\u53c1\u62f3\u4e24\u811a\uff0c\u5c06\u90a3\u5c0f\u5b50\u6253\u5f97\u72fc\u72c8\u800c\u9003\uff0c\u8fd9 \u4e1c\u897f\u81ea\u7136\u5c31\u5230\u4e86\u6211\u7684\u624b\u4e0a\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u6014\u4e86\u6014\uff0c\u9053\uff1a\u201c\u771f\u7684\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u5047\u7684\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u4e5f\u5fcd\u4e0d\u4f4f\u7b11\u4e86\uff0c\u9053\uff1a\u201c\u5176\u5b9e\u6211\u4e5f\u77e5\u9053\u4f60\u662f\u4e07\u4e07\u8ffd\u4e0d\u7740\u4ed6\u7684\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u6211\u8ffd\u4e0d\u4e0a\u4ed6\u8fd8\u6709\u7406\u53ef\u8bf4\uff0c\u8f7b\u529f\u5929\u4e0b\u7b2c\u4e00\u7684\u695a\u7559\u5e05\uff0c\u600e\u9ebd\u8ffd\u4e86\u534a\u5929\uff0c\u4e5f\u5c06\u4ed6 \u7684\u4eba\u8ffd\u53bb\u4e86\u5462\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u53f9\u9053\uff1a\u201c\u82e5\u4e0d\u662f\u8fd9\u573a\u96fe\uff0c\u6211\u4e5f\u8bb8\u8fd8\u80fd\u8ffd\u5f97\u4e0a\u4ed6\u7684\uff0c\u4f46\u6b64\u4eba\u7684\u8f7b\u529f\u4e5f\u5b9e\u5728\u4e0d\u5f31\uff0c\u6211 \u8ffd\u51fa\u5899\u5916\u65f6\uff0c\u4ed6\u7684\u4eba\u5df2\u63a0\u51fa\u53bb\u6709\u56db\u4e94\u5341\u5973\u4e86\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u52a8\u5bb9\u9053\uff1a\u201c\u5c31\u90a3\u9ebd\u4e00\u7728\u773c\u7684\u529f\u592b\uff0c\u4ed6\u5df2\u63a0\u51fa\u53bb\u56db\u4e94\u5341\u4e08\uff0c\u5982\u6b64\u8bf4\u6765\uff0c\u4ed6\u7684\u8f7b\u529f\u5c82 \u975e\u6bd4\u674e\u7389\u51fd\u592b\u59bb\u8fd8\u9ad8\u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u6015\u662f\u8981\u9ad8\u51fa\u4e00\u7b79\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u6bd4\u6211\u5462\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u53c8\u7b11\u4e86\uff0c\u5fcd\u4f4f\u7b11\u9053\uff1a\u201c\u4f60\u82e5\u5c11\u559d\u4e9b\u9152\uff0c\u4ed6\u8f7b\u529f\u4e5f\u8bb8\u4e0d\u5982\u4f60\u7684\uff0c\u4f46\u73b0\u5728\u2026\u2026\u201d \u3000\u3000\u80e1\u94c1\u82b1\u677f\u8d77\u8138\u9053\uff1a\u201c\u73b0\u5728\u53c8\u600e\u6837\uff1f\u73b0\u5728\u6211\u96be\u9053\u8fde\u674e\u7389\u51fd\u592b\u59bb\u90fd\u4e0d\u5982\u9ebd\uff1f\u201d \u3000\u3000\u4ed6\u4e0d\u7b49\u695a\u7559\u9999\u8bf4\u8bdd\uff0c\u81ea\u5df1\u5148\u7b11\u4e86\uff0c\u9053\uff1a\u201c\u4f60\u7528\u4e0d\u7740\u56de\u7b54\u6211\u8fd9\u53e5\u8bdd\uff0c\u4e5f\u514d\u5f97\u6211\u542c\u4e86\u4f24 \u5fc3\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u5176\u5b9e\u4f60\u7684\u8f7b\u529f\u548c\u674e\u7389\u51fd\u592b\u59bb\u3001\u4e00\u70b9\u7ea2\u3001\u5357\u5bab\u7075\uff0c\u90fd\u5dee\u4e0d\u591a\uff0c\u90fd\u5df2\u53ef\u7b97\u662f\u4e00 \u7b49\u4e00\u7684\u529f\u592b\uff0c\u4f46\u8fd9\u4eba\u7684\u8f7b\u529f\u5374\u5df2\u548c\u65e0\u82b1\u4e0d\u76f8\u4e0a\u4e0b\uff0c\u8fd9\u6b21\u82e5\u4e0d\u662f\u6211\u4eb2\u773c\u89c1\u5230\u65e0\u82b1\u7684\u54bd\u5589\u5df2\u88ab\u5229 \u7bad\u7a7f\u8fc7\uff0c\u6015\u53c8\u8981\u4ee5\u4e3a\u662f\u65e0\u82b1\u590d\u6d3b\u4e86\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u6c5f\u6e56\u4e2d\u80fd\u6709\u4ed6\u8fd9\u6837\u8f7b\u529f\u7684\u4eba\u5e76\u4e0d\u591a\uff0c\u662f\u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u5b9e\u5728\u4e0d\u591a\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u6447\u5934\u53f9\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u9ebd\u603b\u662f\u4f1a\u9047\u89c1\u4e00\u4e9b\u5389\u5bb3\u7684\u5bf9\u5934\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9ed8\u7136\u534a\u664c\uff0c\u624d\u95ee\u9053\uff1a\u201c\u4f60\u624b\u4e0a\u8fd9\u4e1c\u897f\u662f\u90a3\u91cc\u6765\u7684\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u6361\u6765\u7684\uff0c\u4e0a\u9762\u73af\u523b\u7740\u5b57\uff0c\u4f60\u77a7\u77a7\u8ba4\u4e0d\u8ba4\u5f97\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u6309\u7740\u90a3\u94f6\u5323\u5b50\uff0c\u8138\u8272\u5c31\u53d8\u4e86\u53d8\uff0c\u9053\uff1a\u201c\u8fd9\u662f\u5c0f\u7bc6\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u6068\u6068\u9053\uff1a\u201c\u660e\u660e\u662f\u6740\u4eba\u7684\u5229\u5668\uff0c\u5374\u504f\u504f\u8981\u6587\u7ec9\u7ec9\u7684\u523b\u4e9b\u4eba\u5bb6\u4e0d\u8ba4\u5f97\u7684\u5b57\u5728\u4e0a\u9762\uff0c \u8fd9\u7b80\u76f4\u597d\u50cf\u660e\u660e\u662f\u5993\u5973\uff0c\u5374\u504f\u504f\u8981\u7a7f\u4e03\u516b\u6761\u88e4\u5b50\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u8fd9\u5012\u5e76\u975e\u662f\u6545\u610f\u5356\u5f04\uff0c\u53ea\u56e0\u8fd9\u6697\u5668\u5b9e\u5728\u662f\u4ef6\u53e4\u7269\uff0c\u800c\u4e14\u8fd8\u662f\u4e2a\u4e0d\u4f1a\u6b66\u529f\u7684 \u4eba\u5236\u6210\u7684\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u4e5f\u542c\u8bf4\u8fc7\u8fd9\u201c\u66b4\u96e8\u68a8\u82b1\u9489\u201d\u7684\u638c\u6545\uff0c\u4f46\u4e0a\u9762\u523b\u7684\u7a76\u7adf\u662f\u4ec0\u9ebd \u5462\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u4e00\u5b57\u5b57\u9053\uff1a\u201c\u4e0a\u9762\u523b\u7684\u662f\uff1a\u51fa\u5fc5\u89c1\u8840\u3002\u7a7a\u56de\u4e0d\u7965\u3002\u6025\u4e2d\u4e4b\u6025\u3002\u6697\u5668\u4e4b\u738b\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u5931\u7b11\u9053\uff1a\u201c\u6587\u4eba\u90fd\u4f1a\u8bf4\u5927\u8bdd\uff0c\u770b\u6765\u679c\u7136\u4e0d\u9519\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u53f9\u9053\uff1a\u201c\u8fd9\u5012\u4e5f\u4e0d\u662f\u4ed6\u5728\u6545\u610f\u8bf4\u5927\u8bdd\u9a87\u4eba\u3002\u201d \u3000\u3000\u201c\u6697\u5668\u5236\u4f5c\u4e4b\u7cbe\u5de7\uff0c\u53d1\u5c04\u529b\u91cf\u4e4b\u731b\uff0c\u5b9e\u5728\u4e0d\u6127\u4e3a\u201c\u6697\u5668\u4e4b\u738b\u201d\u56db\u5b57\uff0c\u5f53\u4eca\u6b66\u6797\u4e2d\u51e0\u4ef6\u6709 \u540d\u7684\u6697\u5668\uff0c\u548c\u6b64\u7269\u4e00\u6bd4\uff0c\u901f\u5ea6\u81f3\u5c11\u8981\u76f8\u5dee\u4e24\u6210\uff0c\u800c\u6697\u5668\u4e00\u543b\uff0c\u51b3\u80dc\u4f24\u4eba\uff0c\u5c31\u5728\u4e00\u5239\u90a3\u95f4\uff0c\u7eb5 \u7136\u662f\u6beb\u5398\u4e4b\u5dee\uff0c\u4e5f\u5dee\u5f97\u5927\u591a\u4e86\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u6b64\u7269\u96be\u9053\u6bd4\u77f3\u89c2\u97f3\u6240\u5236\u7684\u9488\u7b52\u8fd8\u5f3a\u5f97\u591a\u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u77f3\u89c2\u97f3\u90a3\u9488\u7b52\u5c04\u51fa\u6765\u7684\u6bd2\u9488\u867d\u6025\uff0c\u4f46\u4f60\u7b49\u5b83\u53d1\u5c04\u5f8c\u518d\u95ea\u907f\uff0c\u4e5f\u8fd8\u6765\u5f97\u53ca \u7684\uff0c\u800c\u8fd9\u201c\u66b4\u96e8\u68a8\u82b1\u9489\u201d\u53d1\u5c04\u5f8c\uff0c\u5929\u4e0b\u5374\u65e0\u4e00\u4eba\u80fd\u95ea\u505c\u5f00\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u53ef\u662f\u4f60\u65b9\u5374\u95ea\u907f\u5f00\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u82e6\u7b11\u9053\uff1a\u201c\u90a3\u5b9e\u5728\u662f\u8fd0\u6c14\uff0c\u53ea\u56e0\u6211\u5728\u5b83\u8fd8\u672b\u53d1\u5c04\u524d\uff0c\u5c31\u6709\u8b66\u89c9\uff0c\u4f46\u7eb5\u7136\u5982\u6b64\uff0c\u90a3 \u4eba\u53d1\u5c04\u7684\u4f4d\u7f6e\u82e5\u518d\u8fd1\u51e0\u5c3a\uff0c\u6211\u8fd8\u662f\u907f\u4e0d\u5f00\u7684\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u76b1\u7709\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u8fd9\u6697\u5668\u5c82\u975e\u73cd\u8d35\u5df2\u6781\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u5728\u6b66\u6797\u4e2d\u4eba\u773c\u91cc\u770b\u6765\uff0c\u5b83\u5b9e\u5728\u53ef\u8bf4\u662f\u65e0\u4ef7\u4e4b\u5b9d\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u65e2\u662f\u5982\u6b64\uff0c\u90a3\u4eba\u4e3a\u4ec0\u9ebd\u8981\u5c06\u5b83\u629b\u5728\u5730\u4e0a\u5462\uff1f\u4ed6\u65e2\u7136\u6709\u90a3\u9ebd\u9ad8\u7684\u529f\u592b\uff0c\u96be\u9053 \u8fde\u8fd9\u5c0f\u5323\u5b50\u90fd\u62ff\u4e0d\u7a33\u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u8fd9\u7684\u786e\u662f\u4ef6\u5f88\u5947\u602a\u7684\u4e8b\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u5c4b\u5b50\u91cc\u706f\u5df2\u7184\u4e86\uff0c\u8fd9\u592b\u59bb\u50cf\u662f\u5df2\u7761\u7740\u3002 \u3000\u3000\u695a\u7559\u9999\u548c\u80e1\u94c1\u82b1\u6084\u6084\u56de\u5230\u5c4b\u5b50\u91cc\uff0c\u4ed6\u4eec\u5c4b\u91cc\u7684\u706f\u5374\u8fd8\u662f\u4eae\u7740\u7684\uff0c\u53ea\u662f\u706f\u82af\u4e5f\u5df2\u5c06\u71c3\u5c3d\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u5c06\u706f\u82af\u6311\u5927\u4e86\u4e9b\uff0c\u53f9\u9053\uff1a\u201c\u54b1\u4eec\u7a77\u8ffd\u4e86\u534a\u591c\uff0c\u5374\u8fde\u4eba\u5bb6\u7684\u5f71\u5b50\u4e5f\u672b\u89c1\u7740\uff0c\u518d\u4e0d\u5feb \u559d\u676f\u9152\uff0c\u6211\u7b80\u76f4\u5c31\u8981\u88ab\u6d3b\u6d3b\u6c14\u6b7b\u4e86\u3002\u201d \u3000\u3000\u684c\u4e0a\u6709\u4e00\u53ea\u8336\u58f6\uff0c\u4e00\u53ea\u9152\u58f6\uff0c\u80e1\u94c1\u82b1\u5374\u5acc\u9152\u676f\u592a\u5c0f\uff0c\u4e00\u9762\u8bf4\u7740\u8bdd\uff0c\u4e00\u9762\u5df2\u5728\u8336\u676f\u91cc\u5012\u6ee1 \u4e86\u9152\u3002 \u3000\u3000\u695a\u7559\u9999\u6447\u4e86\u6447\u5934\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u8fdf\u4e9b\u559d\u9152\u4e5f\u4e00\u5b9a\u6b7b\u4e0d\u4e86\u7684\uff0c\u54b1\u4eec\u8fd8\u662f\u5148\u5230\u9662\u5b50\u91cc\u77a7\u77a7\u90a3\u4e9b \u201c\u66b4\u96e8\u68a8\u82b1\u9489\u201d\u662f\u5426\u8fd8\u5728\u90a3\u91cc\u3002\u201d \u3000\u3000\u4ed6\u62ff\u8d77\u4e86\u706f\uff0c\u62c9\u7740\u80e1\u94c1\u82b1\u8d70\u51fa\u53bb\u3002 \u3000\u3000\u5c4b\u5b50\u91cc\u6709\u53ea\u5c0f\u866b\uff0c\u4e5f\u968f\u7740\u706f\u5149\u5411\u5916\u98de\u51fa\uff0c\u4f46\u98de\u8fc7\u9152\u676f\u4e0a\u9762\u65f6\uff0c\u7adf\u5ffd\u7136\u6389\u4e86\u4e0b\u6765\uff0c\u6389\u8fdb\u9152 \u676f\u91cc\u3002 \u3000\u3000\u8fd9\u5c0f\u866b\u96be\u9053\u662f\u88ab\u9152\u6c14\u91ba\u9189\uff0c\u624d\u98de\u4e0d\u52a8\u4e86\u3002 \u3000\u3000\u4f46\u9152\u6c14\u53c8\u600e\u4f1a\u6709\u5982\u6b64\u5f3a\u70c8\uff1f \u3000\u3000\u695a\u7559\u9999\u6b64\u523b\u82e5\u8fd8\u6ca1\u6709\u8d70\u51fa\u53bb\uff0c\u5c31\u53ef\u53d1\u73b0\u5c0f\u866b\u6389\u8fdb\u9152\u676f\u5f8c\uff0c\u9152\u676f\u91cc\u7adf\u53d1\u51fa\u201c\u55e4\u201d\u7684\u4e00\u54cd\uff0c \u5192\u51fa\u4e00\u80a1\u6de1\u6de1\u7684\u9752\u70df\u3002 \u3000\u3000\u518d\u770b\u90a3\u5c0f\u866b\u5df2\u65e0\u5f71\u65e0\u8e2a\uff0c\u5c31\u5728\u8fd9\u4e00\u970e\u773c\u7684\u529f\u592b\uff0c\u7adf\u5df2\u5b8c\u5168\u6eb6\u5316\u5728\u9152\u91cc\uff0c\u53d8\u6210\u4e00\u7247\u6ce1\u6cab\u3002 \u3000\u3000\u518d\u4e00\u970e\u773c\uff0c\u8fde\u6ce1\u6cab\u90fd\u77a7\u4e0d\u89c1\u4e86\uff0c\u4e00\u676f\u9152\u8fd8\u662f\u4e00\u676f\u9152\uff0c\u800c\u4e14\u770b\u6765\u4e5f\u8fd8\u662f\u90a3\u9ebd\u6e05\u51bd\uff0c\u8fde\u4e00\u70b9 \u6e23\u6ed3\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u8fd9\u676f\u9152\u82e5\u662f\u559d\u5230\u80e1\u94c1\u82b1\u7684\u809a\u5b50\u91cc\u53bb\uff0c\u80e1\u94c1\u82b1\u7ea6\u4e94\u810f\u516d\u8151\u5c82\u975e\u7acb\u523b\u5c31\u8981\u88ab\u5b83\u8150\u8680\u5f97\u7a00\u70c2\u3002 \u3000\u3000\u5f00\u5c01\u57ce\u5e76\u4e0d\u5e38\u4e0b\u96e8\uff0c\u9662\u5b50\u91cc\u7684\u571f\u5730\u53c8\u4e7e\u53c8\u786c\uff0c\u7b80\u76f4\u548c\u77f3\u5934\u5dee\u4e0d\u591a\uff0c\u5c31\u7b97\u7528\u94c1\u9524\u6572\uff0c\u4e5f\u8981 \u6572\u534a\u5929\u624d\u80fd\u5c06\u9489\u5b50\u6572\u4e0b\u53bb\u3002 \u3000\u3000\u4f46\u6b64\u523b\u5728\u706f\u5149\u6620\u7167\u4e0b\uff0c\u695a\u7559\u9999\u5374\u53d1\u73b0\u8fd9\u4e8c\u5341\u4e03\u679a\u201c\u66b4\u96e8\u68a8\u82b1\u9489\u201d\uff0c\u7adf\u5168\u90fd\u5165\u5730\u4e0b\uff0c\u8fde\u4e00 \u70b9\u5934\u90fd\u6ca1\u6709\u9732\u51fa\u6765\u3002 \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4f60\u770b\u4ed6\u53d1\u5c04\u6697\u5668\u7684\u5730\u65b9\uff0c\u8ddd\u79bb\u8fd9\u91cc\u6709\u591a\u8fdc\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u6253\u91cf\u4e86\u4e00\u4f1a\u9053\uff1a\u201c\u6015\u6709\u56db\u4e94\u4e08\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u53f9\u9053\uff1a\u201c\u8fd9\u4e9b\u68a8\u82b1\u9489\u5728\u56db\u4e94\u4e08\u5916\u5c04\u8fc7\u6765\uff0c\u5c45\u7136\u8fd8\u80fd\u76f4\u6ca1\u5165\u571f\uff0c\u8fd9\u79cd\u6697\u5668\u7684\u529b\u91cf\u662f \u4f55\u7b49\u5f3a\u731b\uff0c\u4f60\u5c31\u53ef\u60f3\u800c\u77e5\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u6211\u771f\u60f3\u5c06\u8fd9\u5323\u4e8e\u62c6\u5f00\u6765\u770b\u770b\uff0c\u770b\u770b\u91cc\u9762\u7684\u673a\u7c27\u7a76\u7adf\u662f\u600e\u9ebd\u505a\u51fa\u6765\u7684\uff0c\u8fd9\u5323 \u5b50\u7b80\u76f4\u5c31\u597d\u50cf\u6709\u4e8c\u5341\u4e03\u4e2a\u5c0f\u8868\u5728\u62c9\u7740\u5f13\u5f26\u4f3c\u7684\u3002\u201d \u3000\u3000\u4ed6\u5634\u91cc\u8bf4\u7740\u8bdd\uff0c\u5df2\u7528\u4e00\u67c4\u5c0f\u5200\u5c06\u5730\u4e0a\u7684\u201c\u66b4\u96e8\u68a8\u82b1\u9489\u201d\u6316\u51fa\u4e86\u4e24\u679a\uff0c\u53ea\u89c1\u8fd9\u68a8\u82b1\u9489\u540d\u867d \u662f\u201c\u9489\u201d\uff0c\u5176\u5b9e\u5374\u548c\u7ee3\u82b1\u9488\u5dee\u4e0d\u7684\uff0c\u53ea\u4e0d\u8fc7\u5c3e\u7aef\u6bd4\u8f83\u7c97\u4e9b\uff0c\u4f46\u653e\u5728\u624b\u91cc\u8fd8\u662f\u8f7b\u98d8\u74e2\u7684\uff0c\u4f3c\u4e4e \u8fde\u98ce\u90fd\u5439\u5f97\u8d70\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u9a87\u7136\u9053\uff1a\u201c\u8fd9\u9ebd\u5c0f\u7684\u4e00\u6839\u9488\u4e5f\u80fd\u9489\u5165\u5730\u4e0b\uff0c\u6211\u82e5\u975e\u4eb2\u773c\u77a7\u89c1\uff0c\u968f\u4fbf\u600e\u9ebd\u6211\u4e5f\u4e0d\u4f1a \u76f8\u4fe1\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u5c31\u56e0\u4e3a\u5b83\u7684\u901f\u5ea6\u5feb\uff0c\u6240\u4ee5\u529b\u91cf\u624d\u5927\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u53f9\u9053\uff1a\u201c\u8fd9\u5c0f\u5c0f\u4e00\u6839\u9489\u6253\u5728\u5730\u4e0a\uff0c\u4fbf\u76f4\u6ca1\u5165\u571f\uff0c\u82e5\u662f\u6253\u5728\u4eba\u8eab\u4e0a\uff0c\u90a3\u8fd8\u5f97\u4e86\u2026\u2026 \u6211\u4e00\u5b9a\u8981\u5c06\u5b83\u4eec\u88c5\u56de\u53bb\uff0c\u8bd5\u8bd5\u5b83\u4eec\u5c04\u51fa\u6765\u65f6\u7a76\u7adf\u6709\u591a\u5feb\uff1f\u201d \u3000\u3000\u4ed6\u679c\u7136\u5c06\u4e8c\u5341\u4e03\u679a\u68a8\u82b1\u9489\u90fd\u6316\u4e86\u51fa\u6765\uff0c\u6367\u5728\u624b\u91cc\u3002 \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u6b64\u7269\u770b\u6765\u6781\u4e3a\u950b\u5229\uff0c\u4f60\u8981\u5c0f\u5fc3\u4e86\u3002\u201d \u7b2c\u56db\u7ae0\u3000\u66b4\u96e8\u68a8\u82b1\u9489>> \u53e4\u9f99\u300a\u695a\u7559\u9999\u7cfb\u5217\xb7\u753b\u7709\u9e1f\u300b \u7b2c\u56db\u7ae0\u3000\u66b4\u96e8\u68a8\u82b1\u9489 \u3000\u3000\u80e1\u94c1\u82b1\u7b11\u9053\uff1a\u201c\u6ca1\u5173\u7cfb\uff0c\u6211\u77e5\u9053\u8fd9\u201c\u66b4\u96e8\u68a8\u82b1\u9489\u201d\u4ece\u6765\u4e0d\u4e0a\u6bd2\u7684\uff0c\u53ea\u56e0\u5b83\u7528\u4e0d\u7740\u4e0a\u6bd2\uff0c \u5df2\u8db3\u591f\u8981\u4eba\u5bb6\u7684\u547d\u4e86\u3002\u201d \u3000\u3000\u4e24\u4eba\u56de\u5230\u5c4b\u91cc\uff0c\u80e1\u94c1\u82b1\u5c31\u5c06\u68a8\u82b1\u9489\u5168\u5012\u5728\u684c\u4e0a\uff0c\u7aef\u8d77\u9152\u676f\u7b11\u9053\uff1a\u201c\u73b0\u5728\u6211\u603b\u53ef\u4ee5\u559d\u676f\u9152 \u4e86\u5427\uff1a\u4f60\u8981\u4e0d\u8981\u4e5f\u6765\u4e00\u676f\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u6211\u559d\u8336\u3002\u201d \u3000\u3000\u4ed6\u653e\u4e0b\u706f\uff0c\u53bb\u62ff\u8336\u58f6\u3002 \u3000\u3000\u8fd9\u65f6\u80e1\u94c1\u82b1\u5df2\u5c06\u9152\u676f\u4e3e\u5230\u5634\u8fb9\u3002 \u3000\u3000\u4ed6\u65e2\u672b\u77a7\u89c1\u90a3\u53ea\u88ab\u6bd2\u6c14\u91ba\u5f97\u6389\u4e0b\u6765\u7684\u5c0f\u866b\uff0c\u81ea\u7136\u4e5f\u4e0d\u77e5\u9053\u53ea\u8981\u8fd9\u676f\u9152\u4e00\u4e0b\u4e86\u809a\uff0c\u4ed6\u8fd9\u4eba \u5c31\u7b97\u62a5\u9500\u4e86\u3002 \u3000\u3000\u8fd9\u5df2\u662f\u4ed6\u6700\u5f8c\u4e00\u676f\u9152\uff0c\u773c\u89c1\u4ed6\u5c31\u8981\u559d\u4e0b\u53bb\u3002 \u3000\u3000\u8c01\u77e5\u5c31\u5728\u8fd9\u65f6\uff0c\u695a\u7559\u9999\u5ffd\u7136\u4e00\u6325\u624b\uff0c\u5c06\u8fd9\u676f\u9152\u6253\u5f97\u98de\u4e86\u51fa\u53bb\uff0c\u80e1\u94c1\u82b1\u5413\u4e86\u4e00\u8df3\uff0c\u5931\u58f0 \u9053\uff1a\u201c\u4f60\u53d1\u4e86\u7f8a\u766b\u75af\u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u4e5f\u4e0d\u7406\u4ed6\uff0c\u5374\u9053\uff1a\u201c\u4f60\u77a7\u89c1\u8fd9\u8336\u58f6\u5417\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u6211\u5f53\u7136\u77a7\u89c1\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4f60\u518d\u77a7\u77a7\u6211\u7684\u624b\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u5561\u4e86\u8d77\u6765\uff0c\u9053\uff1a\u201c\u4f60\u7a76\u7adf\u6709\u4ec0\u9ebd\u6bdb\u75c5\uff0c\u4e3a\u4ec0\u9ebd\u53eb\u6211\u77a7\u4f60\u7684\u624b\uff0c\u4f60\u8fd9\u53ea\u624b\u4e0a\u96be\u9053\u5ffd \u7136\u957f\u51fa\u4e00\u6735\u5587\u53ed\u82b1\u6765\u4e0d\u6210\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u6211\u8fd9\u53ea\u624b\uff0c\u672c\u6765\u662f\u6765\u62ff\u8336\u58f6\u7684\uff0c\u4f46\u4f60\u53ef\u7559\u610f\u5230\uff0c\u73b0\u5728\u8336\u58f6\u7684\u628a\u5b50\u5374\u5df2\u4e0d\u5728 \u6211\u7684\u624b\u8fd9\u4e00\u8fb9\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u4e0d\u5728\u4f60\u624b\u8fd9\u8fb9\u53c8\u600e\u6837\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u6211\u65b9\u5c31\u5750\u5728\u8fd9\u91cc\uff0c\u5012\u8fc7\u4e00\u676f\u8336\uff0c\u53c8\u5c06\u8336\u58f6\u653e\u5728\u539f\u6765\u7684\u5730\u65b9\uff0c\u4f46\u73b0\u5728\u8336\u58f6\u7684 \u628a\u5b50\u5374\u4e0d\u5728\u6211\u7684\u624b\u8fd9\u8fb9\u4e86\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u7b11\u9053\uff1a\u201c\u8fd9\u53c8\u6709\u4ec0\u9ebd\u597d\u5927\u60ca\u5c0f\u655d\u7684\uff0c\u4f60\u4e5f\u8bb8\u6362\u4e86\u53ea\u624b\u53bb\u62ff\u8336\u58f6\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u6211\u5012\u8336\u4e00\u5411\u662f\u7528\u5de6\u624b\u7684\uff0c\u4eba\u5df2\u6210\u4e86\u4e60\u60ef\uff0c\u7edd\u4e0d\u4f1a\u6539\u53d8\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u8fd9\u2026\u2026\u8fd9\u53c8\u600e\u6837\u5462\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u8bf4\uff0c\u6211\u5012\u8fc7\u8336\u5f8c\uff0c\u8fd9\u8336\u58f6\u4e00\u5b9a\u6709\u4eba\u52a8\u8fc7\uff0c\u800c\u4f60\u9664\u4e86\u751f\u5927\u75c5\u7684\u65f6\u5019 \u5916\uff0c\u662f\u7edd\u4e0d\u4f1a\u52a8\u8336\u58f6\u7684\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u6211\u5c31\u7b97\u751f\u5927\u75c5\u65f6\uff0c\u4e5f\u7edd\u4e0d\u4f1a\u78b0\u8336\u58f6\u7684\uff0c\u53ea\u56e0\u522b\u4eba\u559d\u9152\u9189\uff0c\u996e\u8336\u89e3\u9152\uff0c\u6211\u5374 \u4e00\u55c5\u5230\u8336\u7684\u5473\u9053\u5c31\u66f4\u9189\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4f60\u65e2\u7136\u672b\u52a8\u8336\u58f6\uff0c\u8fd9\u8336\u58f6\u81ea\u5df1\u4e5f\u4e0d\u4f1a\u52a8\uff0c\u5374\u53c8\u600e\u4f1a\u53d8\u4e86\u4f4d\u7f6e\u5462\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u4f60\u8fd9\u9ebd\u4e00\u8bf4\uff0c\u6211\u4e5f\u89c9\u5f97\u6709\u4e9b\u5947\u602a\u7684\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u6c89\u58f0\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u8bf4\u4f60\u6211\u65b9\u51fa\u53bb\u65f6\uff0c\u4e00\u5b9a\u6709\u4eba\u8fdb\u6765\u52a8\u8fc7\u8336\u58f6\uff0c\u4ed6\u65e0\u7f18\u65e0\u6545\u7684\u8fdb\u6765 \u52a8\u8fd9\u8336\u58f6\u5e72\u4ec0\u9ebd\u5462\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u52a8\u5bb9\u9053\uff1a\u201c\u4ed6\u83ab\u975e\u662f\u5728\u8336\u58f6\u91cc\u4e0b\u4e86\u6bd2\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4ed6\u7b97\u51c6\u6211\u4eec\u56de\u6765\u65f6\u4e00\u5b9a\u4f1a\u53e3\u6e34\uff0c\u4e00\u5b9a\u4f1a\u559d\u8336\uff0c\u6240\u4ee5\u5c31\u5728\u8336\u58f6\u91cc\u4e0b\u4e86 \u6bd2\uff0c\u4f46\u4ed6\u5374\u672b\u60f3\u5230\u6211\u4e00\u5411\u90fd\u662f\u7528\u5de6\u624b\u5012\u8336\u7684\uff0c\u6240\u4ee5\u4e0b\u8fc7\u6bd2\u5f8c\uff0c\u968f\u624b\u5c06\u8336\u58f6\u653e\u56de\u4e86\u53bb\uff0c\u8336\u58f6\u7684 \u628a\u5b50\u624d\u4f1a\u6362\u4e86\u4e2a\u65b9\u5411\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u542c\u5f97\u5446\u4f4f\u4e86\uff0c\u8fc7\u4e86\u534a\u664c\uff0c\u624d\u8bf4\u9053\uff1a\u201c\u4ed6\u65e2\u5728\u8336\u91cc\u4e0b\u4e86\u6bd2\uff0c\u9152\u91cc\u81ea\u7136\u5c11\u4e0d\u4e86\u4e5f\u6709\u6bd2 \u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u5426\u5219\u6211\u4e3a\u4f55\u8981\u5c06\u4f60\u7684\u9152\u6253\u7ffb\uff1f\u5929\u4e0b\u867d\u6709\u5404\u5f0f\u5404\u6837\u7684\u9152\u9b3c\uff0c\u4f46\u6bcf\u4e2a \u9152\u9b3c\u90fd\u6709\u4e2a\u540c\u6837\u7684\u6bdb\u75c5\uff0c\u90a3\u5c31\u662f\u5c06\u9152\u770b\u5f97\u6bd4\u547d\u8fd8\u7ae5\uff0c\u4f60\u70e7\u4e86\u4ed6\u7684\u623f\u5b50\u4ed6\u90fd\u4e0d\u4f1a\u751f\u6c14\uff0c\u4f46\u4f60\u82e5 \u6253\u7ffb\u4ed6\u7684\u9152\uff0c\u4ed6\u5c31\u8981\u6c14\u5f97\u53d1\u75af\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u82e6\u7b11\u9053\uff1a\u201c\u9a82\u5f97\u597d\uff0c\u9a82\u5f97\u597d\u2026\u2026\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u9053\uff1a\u201c\u6211\u5e76\u4e0d\u662f\u9a82\u4f60\uff0c\u53ea\u4e0d\u8fc7\u8981\u4f60\u77e5\u9053\u6211\u5e76\u6ca1\u6709\u53d1\u7f8a\u766b\u75af\u800c\u5df2\u3002\u201d \u3000\u3000\u4ed6\u5c06\u534a\u58f6\u8336\u90fd\u5012\u5165\u9152\u58f6\u91cc\uff0c\u53ea\u542c\u201c\u55e4\u201d\u7684\u4e00\u58f0\uff0c\u9752\u70df\u9aa4\u8d77\uff0c\u5c31\u597d\u50cf\u5c06\u51b7\u6c34\u5012\u5165\u70ed\u6cb9\u9505\u91cc \u4e00\u6837\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u5012\u62bd\u4e86\u53e3\u51c9\u6c14\uff0c\u9053\uff1a\u201c\u597d\u5389\u5bb3\u7684\u6bd2\uff0c\u770b\u6765\u7adf\u548c\u77f3\u89c2\u97f3\u4f7f\u7684\u6bd2\u5dee\u4e0d\u591a\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u6c89\u4f4f\u8138\u6ca1\u6709\u8bf4\u8bdd\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u53c8\u9053\uff1a\u201c\u5982\u6b64\u770b\u6765\uff0c\u653e\u6697\u5668\u7684\u4eba\u548c\u4e0b\u6bd2\u7684\u4eba\u5fc5\u7136\u662f\u4e00\u8def\u7684\uff0c\u662f\u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u55ef\uff01\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9ed8\u7136\u534a\u664c\u7684\u5ffd\u53c8\u7b11\u4e86\uff0c\u9053\uff1a\u201c\u6211\u5b9e\u5728\u4e5f\u6ca1\u6709\u7559\u610f\u4f60\u662f\u7528\u5de6\u624b\u5012\u8336\u7684\uff0c\u4f60\u505a\u522b\u7684\u4e8b \u90fd\u7528\u53f3\u624b\uff0c\u4e3a\u4ec0\u9ebd\u8981\u7528\u5de6\u624b\u5012\u8336\u5462\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u56e0\u4e3a\u8fd9\u8bb8\u591a\u5e74\u6765\uff0c\u6211\u4e00\u76f4\u4f4f\u5728\u8239\u4e0a\uff0c\u8239\u8231\u91cc\u7684\u5730\u65b9\u5f88\u5c0f\uff0c\u6240\u4ee5\u6bcf\u6837\u4e1c\u897f\u90fd \u4e00\u5b9a\u8981\u653e\u7f6e\u5728\u6700\u5408\u9002\u7684\u5730\u65b9\uff0c\u5c24\u5176\u662f\u8336\u58f6\u8fd9\u79cd\u4e1c\u897f\uff0c\u82e5\u662f\u653e\u7684\u5730\u65b9\u4e0d\u5bf9\uff0c\u5c31\u5e38\u5e38\u4f1a\u88ab\u6253\u7ffb\uff0c \u6240\u4ee5\u84c9\u513f\u5c31\u5728\u6211\u5e38\u5750\u7684\u90a3\u5f20\u6905\u5b50\u5de6\u8fb9\uff0c\u505a\u4e86\u4e2a\u653e\u8336\u58f6\u7684\u67b6\u5b50\uff0c\u6211\u53ea\u8981\u4e00\u4f38\u624b\u5c31\u53ef\u4ee5\u62ff\u5230\u3002\u201d \u3000\u3000\u4ed6\u7b11\u4e86\u7b11\uff0c\u6309\u7740\u9053\uff1a\u201c\u7ecf\u8fc7\u8fd9\u8bb8\u4e45\uff0c\u6211\u5c31\u517b\u6210\u4e86\u7528\u5de6\u624b\u5012\u8336\u7684\u4e60\u60ef\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u7b11\u9053\uff1a\u201c\u5999\u6781\u5999\u6781\uff0c\u4f46\u84c9\u513f\u4e3a\u4ec0\u9ebd\u4e0d\u5c06\u90a3\u67b6\u5b50\u505a\u5728\u4f60\u53f3\u8fb9\u5462\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u8fd9\u9053\u7406\u7b80\u5355\u7684\u5f88\uff0c\u53ea\u56e0\u53f3\u8fb9\u5df2\u6ca1\u6709\u7a7a\u5730\u65b9\u53ef\u5b89\u88c5\u90a3\u67b6\u5b50\u4e86\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u53f9\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u4f4f\u5728\u8239\u4e0a\u8fd8\u6709\u8fd9\u9ebd\u591a\u597d\u5904\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4f4f\u5728\u8239\u4e0a\u867d\u7136\u6709\u65f6\u4f1a\u89c9\u5f97\u592a\u5c40\u4fc3\u4e86\u4e9b\uff0c\u4f46\u4f4f\u7684\u5730\u65b9\u8d8a\u5c0f\uff0c\u8d8a\u5bb9\u6613\u517b\u6210\u4f60\u4e0d \u968f\u624b\u4e71\u653e\u4e1c\u897f\u7684\u597d\u4e60\u60ef\uff0c\u505a\u4e8b\u4e5f\u4f1a\u6e10\u6e10\u53d8\u5f97\u6709\u89c4\u5f8b\uff0c\u8fd9\u79cd\u4e60\u60ef\u5728\u5e73\u65f6\u4e5f\u770b\u4e0d\u51fa\u6709\u4ec0\u9ebd\u597d\u5904\uff0c \u4f46\u5728\u5371\u9669\u65f6\uff0c\u5374\u5f80\u5f80\u4f1a\u6551\u4e86\u4f60\u7684\u547d\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u7b11\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u6211\u82e5\u642c\u5230\u9e3d\u5b50\u7b3c\u91cc\u53bb\u4f4f\uff0c\u5c82\u975e\u5c31\u4e00\u5b9a\u4f1a\u53d8\u6210\u4e16\u4e0a\u6700\u6709\u89c4\u5f8b\u7684 \u4eba\u4e86\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u4f3c\u60f3\u8d77\u4e86\u4ec0\u9ebd\uff0c\u8138\u4e0a\u7684\u7b11\u5bb9\u7acb\u523b\u6d88\u5931\u4e0d\u89c1\uff0c\u5931\u58f0\u9053\uff1a\u201c\u674e\u7389\u51fd\u7684\u5c4b\u5b50\u91cc\u8fde\u4e00\u70b9\u58f0\u97f3 \u90fd\u6ca1\u6709\uff0c\u83ab\u975e\u5df2\u906d\u4e86\u4ed6\u4eec\u7684\u6bd2\u624b\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u9053\uff1a\u201c\u4e0d\u4f1a\u7684\uff0c\u65e0\u8bba\u8c01\u8981\u5bb3\u6b7b\u8fd9\u592b\u5987\u4e24\u4eba\uff0c\u90fd\u4e0d\u662f\u4ef6\u5bb9\u6613\u4e8b\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u4f46\u4ed6\u4eec\u6765\u7684\u65f6\u5019\uff0c\u67f3\u65e0\u7709\u6b63\u5728\u53d1\u7740\u75c5\uff0c\u6015\u5df2\u6ca1\u6709\u62b5\u6297\u4e4b\u529b\u2026\u2026\u65e0\u8bba\u5982\u4f55\uff0c \u6211\u90fd\u5f97\u77a7\u77a7\u4ed6\u4eec\u53bb\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u6c89\u541f\u9053\uff1a\u201c\u53bb\u77a7\u77a7\u4e5f\u597d\uff0c\u4e5f\u8bb8\u4ed6\u4eec\u4f1a\u542c\u89c1\u4ec0\u9ebd\u58f0\u606f\u2026\u2026\u201d \u3000\u3000\u80e1\u94c1\u82b1\u4e0d\u7b49\u4ed6\u8bdd\u8bf4\u5b8c\uff0c\u5df2\u51b2\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u8fd9\u65f6\u5929\u867d\u8fd8\u6ca1\u6709\u4eae\uff0c\u4f46\u8fdc\u5904\u5df2\u6709\u9e21\u557c\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u547c\u5524\u4e86\u4e24\u58f0\uff0c\u674e\u7389\u51fd\u5df2\u71c3\u8d77\u706f\uff0c\u5f00\u4e86\u95e8\uff0c\u62ab\u7740\u8863\u670d\u8d70\u51fa\u6765\uff0c\u9762\u4e0a\u867d\u6709\u4e9b\u60ca\u5947\u4e4b \u8272\uff0c\u5374\u8fd8\u662f\u5e26\u7740\u7b11\u9053\uff1a\u201c\u4e24\u4f4d\u8d77\u6765\u5f97\u5012\u771f\u65e9\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u89c1\u5230\u4ed6\u6d3b\u751f\u751f\u8d70\u51fa\u6765\uff0c\u5df2\u677e\u4e86\u53e3\u6c14\uff0c\u7b11\u9053\uff1a\u201c\u6211\u4eec\u4e0d\u662f\u8d77\u5f97\u65e9\uff0c\u800c\u662f\u8fd8\u6ca1\u6709\u7761 \u54e9\uff01\u201d \u3000\u3000\u674e\u7389\u51fd\u76ee\u5149\u95ea\u52a8\uff0c\u9053\uff1a\u201c\u83ab\u975e\u51fa\u4e86\u4ec0\u9ebd\u4e8b\u9ebd\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8bf4\u6765\u8bdd\u957f\uff0c\u4f60\u65e2\u5df2\u8d77\u6765\uff0c\u7d22\u6027\u5230\u6211\u4eec\u5c4b\u91cc\u53bb\u804a\u804a\u5427\uff01\u201d \u3000\u3000\u674e\u7389\u51fd\u56de\u5934\u77a7\u4e86\u4e00\u773c\uff0c\u6084\u6084\u5e26\u8d77\u623f\u95e8\uff0c\u4e5f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5185\u4eba\u6709\u4e9b\u4e0d\u8212\u670d\uff0c\u5c0f\u5f1f\u5176\u5b9e \u4e5f\u521a\u7761\u7740\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u5ac2\u592b\u4eba\u7684\u2026\u2026\u75c5\u4e0d\u788d\u4e8b\u9ebd\uff1f\u201d \u3000\u3000\u674e\u7389\u51fd\u82e6\u7b11\u9053\uff1a\u201c\u8fd9\u662f\u5979\u7684\u8001\u6bdb\u75c5\uff0c\u6bcf\u4e2a\u6708\u90fd\u8981\u53d1\u4f5c\u4e24\u6b21\uff0c\u5012\u6ca1\u6709\u4ec0\u9ebd\u592a\u5927\u7684\u5173\u7cfb\uff0c\u53ea \u4e0d\u8fc7\u9ebb\u70e6\u5f97\u5f88\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u77a7\u4e86\u695a\u7559\u9999\u4e00\u773c\uff0c\u597d\u50cf\u662f\u5bf9\u4ed6\u8bf4\uff1a\u201c\u4f60\u731c\u9519\u4e86\uff0c\u5979\u5e76\u6ca1\u6709\u4e2d\u6bd2\uff0c\u53ea\u4e0d\u8fc7\u662f\u8001\u6bdb\u75c5 \u53d1\u4f5c\u800c\u5df2\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u4e86\u7b11\uff0c\u5374\u9053\uff1a\u201c\u674e\u5144\u65e2\u7136\u521a\u7761\u7740\uff0c\u4e0d\u77e5\u53ef\u66fe\u542c\u5230\u4ec0\u9ebd\u54cd\u52a8\uff1f\u201d \u3000\u3000\u674e\u7389\u51fd\u53f9\u9053\uff1a\u201c\u5185\u4eba\u4e00\u76f4\u5728\u7ffb\u6765\u590d\u53bb\u7684\u53eb\u82e6\uff0c\u5c31\u50cf\u5c0f\u5b69\u5b50\u4f3c\u7684\uff0c\u6211\u53ea\u597d\u60f3\u5c3d\u6cd5\u5b50\u53bb\u54c4 \u5979\uff0c\u522b\u7684\u4e8b\u5012\u6ca1\u6709\u7559\u610f\u5230\u3002\u201d \u3000\u3000\u4ed6\u521a\u505c\u4f4f\u53e3\uff0c\u5ffd\u53c8\u95ee\u9053\uff1a\u201c\u7a76\u7adf\u53d1\u751f\u4e86\u4ec0\u9ebd\u4e8b\uff0c\u83ab\u975e\u2026\u2026\u201d \u3000\u3000\u80e1\u94c1\u82b1\u7b11\u9053\uff1a\u201c\u4e5f\u6ca1\u4ec0\u9ebd\u5927\u4e0d\u4e86\u7684\u4e8b\uff0c\u53ea\u4e0d\u8fc7\u662f\u6709\u4e24\u4e2a\u4eba\u60f3\u8981\u695a\u7559\u9999\u7684\u547d\u800c\u5df2\uff0c\u8fd9\u4e5f\u662f \u4ed6\u7684\u8001\u6bdb\u75c5\u4e86\uff0c\u6bcf\u4e2a\u6708\u90fd\u8981\u53d1\u4f5c\u51e0\u6b21\u7684\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u52a8\u5bb9\u9053\uff1a\u201c\u6709\u4eba\u60f3\u6765\u6697\u7b97\u695a\u5144\uff1f\u662f\u4ec0\u9ebd\u4eba\u6709\u5982\u6b64\u5927\u7684\u80c6\u5b50\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u82e6\u7b11\u9053\uff1a\u201c\u6211\u548c\u4ed6\u8ffd\u4e86\u534a\u5929\uff0c\u5374\u8fde\u4eba\u5bb6\u7684\u5f71\u5b50\u90fd\u6ca1\u8ffd\u4e0a\uff0c\u6c5f\u6e56\u4e2d\u529f\u592b\u9ad8\u7684\u597d\u624b\uff0c \u770b\u6765\u7adf\u50cf\u662f\u4e00\u5929\u6bd4\u4e00\u5929\u591a\u4e86\u3002\u201d \u3000\u3000\u8fd9\u65f6\u4ed6\u4eec\u5df2\u8d70\u56de\u5c4b\u91cc\uff0c\u674e\u7389\u51fd\u77a5\u89c1\u684c\u4e0a\u7684\u94f6\u9489\uff0c\u5ffd\u53c8\u53d8\u8272\u9053\uff1a\u201c\u8fd9\u684c\u4e0a\u7684\u6697\u5668\u83ab\u975e\u5c31\u662f \u90a3\u4eba\u8981\u7528\u6765\u6697\u7b97\u695a\u5144\u7684\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u51dd\u6ce8\u7740\u4ed6\u7684\u8138\uff0c\u9053\uff1a\u201c\u8fd9\u6697\u5668\u674e\u5144\u83ab\u975e\u4e5f\u8ba4\u5f97\uff1f\u201d \u3000\u3000\u674e\u7389\u51fd\u9053\uff1a\u201c\u8fd9\u770b\u6765\u7adf\u4f3c\u662f\u66b4\u96e8\u68a8\u82b1\u9489\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u53f9\u606f\u7740\u5fae\u7b11\u9053\uff1a\u201c\u695a\u9999\u5e05\u679c\u7136\u662f\u540d\u4e0b\u65e0\u865a\uff0c\u636e\u5c0f\u5f1f\u6240\u77e5\uff0c\u8fd9\u66b4\u96e8\u68a8\u82b1\u9489\u52bf\u6025\u529b \u731b\uff0c\u53ef\u79f0\u5929\u4e0b\u7b2c\u4e00\uff0c\u6bcf\u4e00\u5c04\u51fa\uff0c\u5fc5\u5b9a\u89c1\u8840\uff0c\u6c5f\u6e56\u4e2d\u81f3\u4eca\u597d\u50cf\u8fd8\u6ca1\u6709\u4e00\u4eba\u80fd\u95ea\u907f\u505c\u5f00\uff0c\u8fde\u6614\u65e5 \u7eb5\u6a2a\u5357\u8352\u7684\u4e00\u5c18\u9053\u957f\uff0c\u90fd\u662f\u6b7b\u5728\u8fd9\u6697\u5668\u4e0b\u7684\uff0c\u800c\u695a\u5144\u80fd\u5b89\u7136\u65e0\u6059\uff0c\u7531\u6b64\u53ef\u89c1\uff0c\u695a\u5144\u7684\u6b66\u529f\u7adf \u6bd4\u6614\u5e74\u90a3\u4f4d\u4e00\u5251\u5e73\u5357\u8352\u7684\u5927\u5251\u5ba2\u8fd8\u9ad8\u51fa\u4e00\u7b79\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u7b11\u9053\uff1a\u201c\u4ed6\u53ea\u4e0d\u8fc7\u662f\u8fd0\u6c14\u4e00\u5411\u6bd4\u522b\u4eba\u597d\u4e9b\u800c\u5df2\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u9053\uff1a\u201c\u5728\u8fd9\u66b4\u96e8\u68a8\u82b1\u9489\u4e0b\uff0c\u7edd\u65e0\u201c\u8fd0\u6c14\u201d\u4e24\u5b57\uff0c\u9664\u4e86\u695a\u5144\u5916\uff0c\u522b\u4eba\u7684\u8fd0\u6c14\u5c31\u7b97\u518d \u597d\uff0c\u4e5f\u662f\u4e07\u4e07\u907f\u4e0d\u5f00\u8fd9\u4e8c\u5341\u4e03\u679a\u94f6\u9489\u7684\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u4f60\u5bf9\u8fd9\u89c1\u9b3c\u7684\u6697\u5668\u5012\u597d\u50cf\u77e5\u9053\u5f97\u8fd8\u4e0d\u5c11\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u9053\uff1a\u201c\u8fd9\u662f\u5929\u4e0b\u6700\u6709\u540d\u7684\u6697\u5668\uff0c\u5bb6\u7236\u5728\u5c0f\u5f1f\u542f\u8499\u5b66\u6b66\u65f6\uff0c\u5c31\u66fe\u5c06\u6709\u5173\u8fd9\u6697\u5668\u7684\u4e00 \u5207\u544a\u8bc9\u4e86\u6211\uff0c\u8fd8\u53eb\u6211\u4ee5\u5f8c\u4efd\u5916\u7559\u610f\uff0c\u4ed6\u8001\u4eba\u5bb6\u8bf4\uff0c\u5929\u4e0b\u6709\u516d\u6837\u6700\u53ef\u6015\u7684\u4e1c\u897f\uff0c\u8fd9\u201c\u66b4\u96e8\u68a8\u82b1 \u9489\u201d\u5c31\u662f\u5176\u4e2d\u4e4b\u4e00\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u674e\u8001\u524d\u8f88\u89c1\u8bc6\u6e0a\u535a\uff0c\u60f3\u5fc5\u4e5f\u66fe\u5c06\u8fd9\u6697\u5668\u7684\u51fa\u5904\u544a\u8bc9\u8fc7\u5b63\u5144\u4e86\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u9053\uff1a\u201c\u5236\u9020\u8fd9\u6697\u5668\u7684\u4eba\uff0c\u4e5f\u662f\u4f4d\u6b66\u6797\u4e16\u5bb6\u7684\u5b50\u5f1f\uff0c\u53eb\u505a\u5468\u4e16\u660e\uff0c\u4ed6\u7684\u7236\u4eb2\u5c31\u662f\u5f53 \u65f6\u6781\u8d1f\u76db\u540d\u7684\u5357\u6e56\u53cc\u5251\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u636e\u6211\u4eec\u77e5\u9053\uff0c\u5236\u4f5c\u8fd9\u6697\u5668\u7684\u4eba\uff0c\u4e00\u70b9\u6b66\u529f\u4e5f\u4e0d\u4f1a\uff0c\u201c\u5357\u6e56\u53cc\u5251\u201d\u7684\u513f\u5b50\uff0c \u53c8\u600e\u4f1a\u4e0d\u901a\u6b66\u529f\u5462\uff1f\u96be\u9053\u4f20\u95fb\u6709\u8bef\uff1f\u201d \u3000\u3000\u674e\u7389\u51fd\u9053\uff1a\u201c\u80e1\u5144\u542c\u5230\u7684\u4f20\u95fb\u5e76\u6ca1\u6709\u9519\uff0c\u8fd9\u5468\u4e16\u660e\u7684\u786e\u4e0d\u4f1a\u6b66\u529f\uff0c\u53ea\u56e0\u4ed6\u4ece\u5c0f\u5c31\u60a3\u4e86\u4e00 \u79cd\u6781\u5947\u5f02\u7684\u8f6f\u9aa8\u9ebb\u75f9\u75c7\uff0c\u975e\u4f46\u4e0d\u80fd\u5b66\u6b66\uff0c\u800c\u4e14\u6839\u672c\u8fde\u7ad9\u90fd\u7ad9\u4e0d\u8d77\u6765\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u53f9\u9053\uff1a\u201c\u53ef\u601c\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u9053\uff1a\u201c\u4ed6\u4eec\u5bb6\u4e00\u5171\u6709\u4e94\u5144\u5f1f\uff0c\u5468\u4e16\u660e\u6392\u884c\u7b2c\u53c1\uff0c\u4ed6\u7684\u667a\u6167\u672c\u6bd4\u53e6\u56db\u4e2a\u5144\u5f1f\u90fd\u9ad8\u5f97 \u591a\uff0c\u53ea\u6068\u8eab\u5b50\u6b8b\u5e9f\uff0c\u773c\u89c1\u4ed6\u7684\u5144\u5f1f\u4eec\u9131\u5728\u6c5f\u6e56\u4e2d\u6210\u4e86\u5927\u540d\uff0c\u5fc3\u91cc\u81ea\u7136\u96be\u514d\u60b2\u6124\uff0c\u5c31\u53d1\u8a93\u603b\u6709 \u4e00\u5929\u8981\u505a\u4ef6\u60ca\u4eba\u7684\u5927\u4e8b\u7ed9\u522b\u4eba\u770b\u770b\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4ed6\u7684\u5144\u5f1f\u83ab\u975e\u5c31\u662f\u6614\u5e74\u4eba\u79f0\u201c\u6c5f\u5357\u56db\u4e49\u201d\u7684\u56db\u4f4d\u524d\u8f88\u9ebd\uff1f\u201d \u3000\u3000\u674e\u7389\u51fd\u9053\uff1a\u201c\u6b63\u662f\u3002\u201d \u3000\u3000\u4ed6\u6309\u7740\u53c8\u9053\uff1a\u201c\u8fd9\u5468\u4e16\u660e\u7ec8\u5e74\u7f20\u7ef5\u75c5\u69bb\uff0c\u9664\u4e86\u770b\u4e66\u4e4b\u5916\uff0c\u5c31\u4ee5\u524a\u6728\u4e3a\u620f\uff0c\u4ed6\u4e0d\u4f46\u5929\u8d44\u7edd \u9876\uff0c\u800c\u4e14\u4e00\u53cc\u624b\u66f4\u5de7\u5f97\u5f88\uff0c\u636e\u8bf4\u4ed6\u4f4f\u7684\u90a3\u95f4\u5c4b\u5b50\u91cc\uff0c\u5230\u5904\u90fd\u662f\u6781\u7075\u5de7\u7684\u6d88\u606f\u673a\u5173\uff0c\u800c\u4eff\u6548\u8bf8 \u845b\u6b66\u4faf\u7684\u6728\u725b\u6d41\u9a6c\uff0c\u505a\u51fa\u8bb8\u591a\u53ef\u4ee5\u6d3b\u52a8\u7684\u6728\u4eba\uff0c\u53ea\u8981\u4ed6\u4e00\u6293\u673a\u7c27\uff0c\u8fd9\u4e9b\u6728\u4eba\u5c31\u4f1a\u4e3a\u4ed6\u9001\u4e0a\u8336 \u6c34\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u7b11\u9053\uff1a\u201c\u8fd9\u5c4b\u5b50\u4e00\u5b9a\u6709\u8da3\u5f97\u5f88\uff0c\u82e5\u975e\u8fd9\u4f4d\u5468\u5148\u751f\u65e9\u5df2\u7269\u6545\uff0c\u6211\u4eec\u771f\u60f3\u53bb\u62dc\u671b\u62dc\u671b \u8fd9\u4f4d\u5947\u4eba\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u9053\uff1a\u201c\u8fd9\u9ebd\u8fc7\u4e86\u8bb8\u591a\u5e74\uff0c\u4ed6\u4ee5\u6728\u5934\u524a\u6210\u4e00\u4e2a\u673a\u7c27\u5323\u5b50\uff0c\u8981\u4ed6\u5144\u5f1f\u53bb\u627e\u4e2a\u5de7\u624b\u7684\u94f6 \u5320\u6765\u540c\u6837\u6253\u9020\u4e00\u53ea\uff0c\u4ed6\u5144\u5f1f\u4ee5\u4e3a\u8fd9\u53c8\u662f\u4ed6\u7684\u73a9\u5177\uff0c\u4e5f\u672b\u5728\u610f\uff0c\u5c31\u66ff\u4ed6\u5728\u59d1\u82cf\u627e\u6765\u4e2a\u5f53\u65f6\u6700\u7740 \u540d\u7684\u94f6\u5320\uff0c\u53eb\u5de7\u624b\u5b8b\u7684\u3002\u201d \u3000\u3000\u4ed6\u6b47\u4e86\u53e3\u6c14\uff0c\u624d\u6309\u7740\u9053\uff1a\u201c\u8fd9\u5de7\u624b\u5b8b\u5728\u5468\u4e16\u660e\u90a3\u5c4b\u4e8e\u91cc\u4e00\u803d\u5c31\u662f\u53c1\u5e74\uff0c\u8c01\u4e5f\u4e0d\u77e5\u9053\u4ed6\u4eec \u5728\u5c4b\u5b50\u91cc\u5e72\u4ec0\u9ebd\uff0c\u53ea\u4e0d\u8fc7\u5468\u4e16\u660e\u6bcf\u4e2a\u6708\u90fd\u4ee4\u4eba\u5c06\u4e00\u7b14\u6570\u76ee\u5f88\u5927\u7684\u5b89\u5bb6\u8d39\u9001\u56de\u53bb\u7ed9\u5de7\u624b\u5b8b\u7684\u5bb6 \u4eba\uff0c\u6240\u4ee5\u4ed6\u7684\u59bb\u5b50\u4e5f\u5c31\u5f88\u653e\u5fc3\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5979\u6015\u4e0d\u77e5\u9053\u8fd9\u4e9b\u94b1\u5c31\u662f\u5468\u4e16\u660e\u7528\u6765\u4e70\u5979\u4e08\u592b\u547d\u7684\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u53c1\u5e74\u5f8c\uff0c\u5de7\u624b\u5b8b\u4e00\u8d70\u51fa\u90a3\u5c4b\u5b50\uff0c\u5c31\u5012\u5730\u4e0d\u8d77\uff0c\u636e\u8bf4\u662f\u56e0\u4e3a\u5fc3\u529b\u4ea4\u7601 \u800c\u5df2\uff0c\u4f46\u771f\u76f8\u7a76\u7adf\u5982\u4f55\uff0c\u8c01\u4e5f\u4e0d\u77e5\u9053\uff0c\u5357\u6e56\u5468\u5bb6\u5728\u5f53\u65f6\u4e5f\u662f\u8d22\u96c4\u52bf\u5927\uff0c\u8d6b\u8d6b\u6709\u540d\uff0c\u662f\u4ee5\u5de7\u624b \u5b8b\u7684\u5bb6\u4eba\u4e5f\u4e0d\u6562\u8ffd\u95ee\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u53f9\u9053\uff1a\u201c\u8fd9\u5de7\u624b\u5b8b\u65e2\u7136\u77e5\u9053\u5236\u4f5c\u201c\u66b4\u96e8\u68a8\u82b1\u9489\u201d\u7684\u79d8\u5bc6\uff0c\u5468\u4e16\u660e\u81ea\u7136\u7edd\u4e0d\u4f1a\u8ba9\u4ed6 \u518d\u6d3b\u5728\u4e16\u4e0a\u7684\uff0c\u4ed6\u6015\u5c31\u662f\u4e3a\u201c\u66b4\u96e8\u68a8\u82b1\u9488\u201d\u800c\u6b7b\u7684\u7b2c\u4e00\u4e2a\u4eba\u4e86\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u9053\uff1a\u201c\u53c8\u8fc7\u4e86\u534a\u4e2a\u6708\u5f8c\uff0c\u5468\u4e16\u660e\u5ffd\u7136\u53d1\u4e86\u5f88\u591a\u5e16\u5b50\uff0c\u5c06\u5f53\u65f6\u6700\u6709\u540d\u7684\u51e0\u4f4d\u6697\u5668\u9ad8 \u624b\u90e8\u8bf7\u4e86\u6765\uff0c\u90a3\u65e5\u6b63\u662f\u4e2d\u79cb\uff0c\u6708\u8272\u751a\u660e\uff0c\u6c5f\u6e56\u4eba\u770b\u5728\u6c5f\u5357\u56db\u4e49\u7684\u9762\u4e0a\uff0c\u5230\u7684\u4eba\u53ef\u4e0d\u5c11\uff0c\u6b63\u5728 \u7eb7\u7eb7\u731c\u6d4b\uff0c\u4e0d\u77e5\u8fd9\u4f4d\u4ece\u672a\u6d89\u8db3\u6c5f\u6e56\u7684\u5468\u516c\u5b50\u662f\u4e3a\u4ec0\u9ebd\u8981\u8bf7\u8fd9\u8bb8\u591a\u82f1\u96c4\u8c6a\u6770\u6765\u8d74\u5bb4\u7684\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u4f3c\u4e4e\u60f3\u63d2\u53e3\uff0c\u4f46\u7ec8\u65bc\u53c8\u5fcd\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u53ea\u542c\u674e\u7389\u51fd\u63a5\u9053\uff1a\u201c\u8c01\u77e5\u9152\u8fc7\u53c1\u5de1\u4e4b\u5f8c\uff0c\u5468\u4e16\u660e\u7adf\u5ffd\u7136\u8981\u6c42\u4faf\u5357\u8f89\u6765\u548c\u4ed6\u4e00\u8f83\u6697\u5668\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u8fd8\u662f\u5fcd\u4e0d\u4f4f\u63d2\u53e3\u9053\uff1a\u201c\u8fd9\u4faf\u5357\u8f89\u53ef\u662f\u4eba\u79f0\u201c\u516b\u81c2\u795e\u733f\u201d\u7684\u90a3\u4e00\u4f4d\u9ebd\uff1f\u201d \u3000\u3000\u674e\u7389\u51fd\u9053\uff1a\u201c\u6b63\u662f\uff0c\u6b64\u4eba\u4e0d\u4f46\u5168\u8eab\u4e0a\u4e0b\u90fd\u662f\u6697\u5668\uff0c\u636e\u8bf4\u540c\u65f6\u7adf\u53ef\u53d1\u51fa\u516b\u79cd\u6697\u5668\u6765\uff0c\u800c\u4e14 \u63a5\u6697\u5668\u7684\u529f\u592b\u4e5f\u51fa\u7c7b\u62d4\u8403\uff0c\u5b9b\u5982\u751f\u7740\u516b\u53ea\u624b\u4f3c\u7684\uff0c\u5b9e\u5728\u53ef\u79f0\u5f97\u4e0a\u662f\u6b66\u6797\u4e00\u7b49\u4e00\u7684\u6697\u5668\u540d\u5bb6\uff0c \u8fd9\u6837\u7684\u4eba\u600e\u80af\u548c\u4e00\u4e2a\u6b8b\u5e9f\u6765\u6bd4\u6697\u5668\u529f\u592b\uff0c\u4f55\u51b5\u4ed6\u53c8\u662f\u201c\u6c5f\u5357\u56db\u4e49\u201d\u7684\u670b\u53cb\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4ed6\u5c31\u7b97\u80dc\u4e86\uff0c\u4e5f\u6ca1\u4ec0\u9ebd\u5149\u91c7\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u9053\uff1a\u201c\u5927\u5bb6\u4e5f\u90fd\u4ee5\u4e3a\u5468\u4e16\u660e\u662f\u5728\u8bf4\u7b11\u7684\uff0c\u8c01\u77e5\u5468\u4e16\u660e\u7adf\u975e\u8981\u4faf\u5357\u8f89\u52a8\u624b\u4e0d\u53ef\uff0c\u800c \u4e14\u8fd8\u8bf4\u4e86\u8bb8\u591a\u5f88\u5c16\u523b\u7684\u8bdd\uff0c\u903c\u5f97\u4faf\u5357\u8f89\u8138\u4e0a\u6e10\u6e10\u6302\u4e0d\u4f4f\u4e86\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u5f8c\u6765\u5462\uff1f\u201d \u3000\u3000\u674e\u7389\u51fd\u9053\uff1a\u201c\u957f\u8bdd\u77ed\u8bf4\uff0c\u5f8c\u6765\u975e\u4f46\u4faf\u5357\u8f89\u6b7b\u5728\u8fd9\u201c\u66b4\u96e8\u68a8\u82b1\u9489\u201d\u4e0b\uff0c\u8fd8\u6709\u51e0\u4f4d\u6697\u5668\u9ad8\u624b \u4e5f\u4e00\u9f50\u9001\u4e86\u547d\uff0c\u5927\u5bb6\u660e\u77e5\u9053\u6697\u5668\u662f\u4ece\u5468\u4e16\u660e\u624b\u91cc\u4e00\u4e2a\u5c0f\u94f6\u5323\u5b50\u91cc\u5c04\u51fa\u6765\u7684\uff0c\u7adf\u504f\u504f\u5c31\u6ca1\u6709\u4e00 \u4e2a\u4eba\u80fd\u95ea\u907f\u5f97\u5f00\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u53f9\u9053\uff1a\u201c\u8fd9\u4f4d\u5468\u516c\u5b50\u597d\u8fa3\u7684\u624b\uff01\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u8fd9\u4eba\u4ece\u5c0f\u6b8b\u5e9f\uff0c\u6027\u60c5\u81ea\u7136\u96be\u514d\u504f\u6fc0\u53e4\u602a\uff0c\u4f46\u201c\u5357\u6e56\u53cc\u5251\u201d\u548c\u201c\u6c5f\u5357\u56db\u4e49\u201d \u96be\u9053\u4e5f\u4e0d\u7ba1\u4ed6\u9ebd\uff1f\u201d \u3000\u3000\u674e\u7389\u51fd\u9053\uff1a\u201c\u90a3\u65f6\u5357\u6e56\u53cc\u5251\u8001\u5144\u5f1f\u4e24\u4eba\u90fd\u5df2\u7269\u6545\uff0c\u6c5f\u5357\u56db\u4e49\u5374\u522b\u6709\u5c45\u5fc3\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u4ec0\u9ebd\u5c45\u5fc3\uff1f\u201d \u3000\u3000\u674e\u7389\u51fd\u9053\uff1a\u201c\u4ed6\u4eec\u89c1\u5230\u81ea\u5df1\u7684\u5144\u5f1f\u6709\u5982\u6b64\u5389\u5bb3\u7684\u6697\u5668\uff0c\u7adf\u4e5f\u60f3\u501f\u6b64\u6811\u7acb\u592a\u6e56\u5468\u5bb6\u7684\u5a01 \u540d\uff0c\u4ed6\u4eec\u5374\u672b\u60f3\u5230\uff0c\u8fd9\u9ebd\u6837\u4e00\u6765\uff0c\u6c5f\u6e56\u4e2d\u4eba\u4eba\u90fd\u5c06\u5468\u5bb6\u5144\u5f1f\u89c6\u4e3a\u516c\u654c\uff0c\u8c01\u90fd\u4e0d\u613f\u8fd9\u79cd\u6697\u5668\u7559 \u5728\u5468\u5bb6\u5144\u5f1f\u624b\u91cc\uff0c\u6b63\u662f\u4eba\u4eba\u90fd\u60f3\u9664\u4e4b\u800c\u5f8c\u5feb\uff0c\u56e0\u4e3a\u5927\u5bb6\u90fd\u6015\u4ed6\u4eec\u7528\u8fd9\u79cd\u6697\u5668\u6765\u5bf9\u4ed8\u81ea\u5df1\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u5c24\u5176\u662f\u90a3\u4e9b\u5e73\u65f6\u548c\u5468\u5bb6\u5144\u5f1f\u6709\u4e9b\u8fc7\u8282\u7684\u4eba\uff0c\u77e5\u9053\u4ed6\u4eec\u624b\u91cc\u6709\u5982\u6b64\u6b79\u6bd2\u7684\u6697 \u5668\uff0c\u6015\u665a\u4e0a\u8fde\u89c9\u90fd\u7761\u4e0d\u7740\u4e86\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u9053\uff1a\u201c\u6240\u4ee5\u8fd9\u4e9b\u4eba\u5c31\u5148\u4e0b\u624b\u4e3a\u5f3a\uff0c\u60f3\u5c3d\u9551\u79cd\u65b9\u6cd5\uff0c\u5c06\u6c5f\u5357\u56db\u4e49\u4e00\u4e00\u9664\u53bb\uff0c\u53c8\u653e\u4e86 \u628a\u706b\u5c06\u5468\u5bb6\u5e84\u70e7\u5f97\u4e7e\u4e7e\u51c0\u51c0\uff0c\u5468\u4e16\u660e\u4e5f\u846c\u8eab\u5728\u706b\u7a9f\u4e4b\u4e2d\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u5230\u8fd9\u65f6\u624d\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u90a3\u9ebd\u5f8c\u6765\u8fd9\u201c\u66b4\u96e8\u68a8\u82b1\u9489\u201d\u662f\u843d\u5230\u4ec0\u9ebd\u4eba\u624b\u91cc\u4e86\u5462\uff1f\u201d \u3000\u3000\u674e\u7389\u51fd\u9053\uff1a\u201c\u8c01\u4e5f\u4e0d\u77e5\u8fd9\u6697\u5668\u7a76\u7adf\u843d\u5230\u8c01\u624b\u91cc\u4e86\uff0c\u56e0\u4e3a\u65e0\u8bba\u8c01\u5f97\u5230\u5b83\u90fd\u4e07\u4e07\u4e0d\u80af\u8bf4\u51fa\u6765 \u7684\uff0c\u4f46\u6bcf\u9694\u53c1\u4e94\u4e2a\u6708\uff0c\u6c5f\u6e56\u4e2d\u603b\u6709\u4e2a\u4eba\u6b7b\u5728\u8fd9\u201c\u66b4\u96e8\u68a8\u82b1\u9489\u201d\u4e0b\uff0c\u6301\u6709\u201c\u66b4\u96e8\u68a8\u82b1\u9489\u201d\u7684 \u4eba\uff0c\u4e5f\u5e76\u4e0d\u80fd\u4fdd\u5b58\u5f88\u4e45\uff0c\u56e0\u4e3a\u53ea\u8981\u6709\u4e00\u4e1d\u98ce\u58f0\u6f0f\u51fa\uff0c\u5c31\u4f1a\u6709\u4eba\u5c06\u6697\u5668\u593a\u53bb\uff0c\u5c06\u4ed6\u7684\u4eba\u4e5f\u6740 \u6b7b\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u8fd9\u6697\u5668\u5c82\u975e\u5df2\u53d8\u6210\u4e0d\u7965\u4e4b\u7269\u4e86\uff1f\u201d \u3000\u3000\u674e\u7389\u51fd\u53f9\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6570\u5341\u5e74\u6765\uff0c\u8fd9\u6697\u5668\u4e5f\u4e0d\u77e5\u6613\u624b\u8fc7\u82e5\u5e72\u6b21\uff0c\u5f97\u5230\u5b83\u7684\u4eba\uff0c\u603b\u662f\u4e0d\u5f97 \u5584\u7ec8\uff0c\u76f4\u5230\u591a\u5e74\u524d\uff0c\u8fd9\u6697\u5668\u5ffd\u7136\u9500\u58f0\u533f\u8ff9\uff0c\u60f3\u5fc5\u662f\u56e0\u4e3a\u8fd9\u6b21\u5f97\u5230\u5b83\u7684\u4eba\uff0c\u5e76\u6ca1\u6709\u4f7f\u7528\u5b83\uff0c\u662f \u4ee5\u8fd9\u4e00\u4ee3\u7684\u6b66\u6797\u8c6a\u6770\u867d\u4ecd\u65f6\u5e38\u90fd\u4f1a\u542c\u5230\u6709\u5173\u201c\u66b4\u96e8\u68a8\u82b1\u9489\u201d\u7684\u4f20\u8bf4\uff0c\u751a\u81f3\u8fd8\u6709\u8bb8\u591a\u4eba\u77e5\u9053\u5b83 \u7684\u5f62\u72b6\u548c\u5a01\u529b\uff0c\u4f46\u5374\u5df2\u6ca1\u6709\u4e00\u4e2a\u4eba\u771f\u6b63\u77a7\u89c1\u8fc7\u5b83\u7684\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u77a7\u4e86\u695a\u7559\u9999\u4e00\u773c\uff0c\u7b11\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u54b1\u4eec\u7684\u8fd0\u6c14\u5012\u4e0d\u9519\u4e86\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u76b1\u7709\u9053\uff1a\u201c\u6b64\u6b21\u8fd9\u4eba\u60f3\u5fc5\u662f\u4e3a\u4e86\u8981\u5bf9\u4ed8\u695a\u5144\uff0c\u662f\u4ee5\u624d\u8bbe\u6cd5\u5c06\u8fd9\u6697\u5668\u5f04\u6765\uff0c\u7531\u6b64\u53ef \u89c1\uff0c\u8fd9\u4eba\u5fc5\u5b9a\u548c\u695a\u5144\u6709\u6781\u5927\u7684\u4ec7\u6068\uff0c\u56e0\u4e3a\u4ed6\u65e0\u8bba\u662f\u501f\u3001\u662f\u62a2\u3001\u662f\u76d7\uff0c\u80fd\u5c06\u8fd9\u6697\u5668\u5f04\u5230\u624b\u90e8\u7edd \u4e0d\u662f\u4ef6\u5bb9\u6613\u4e8b\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u8fd9\u5c31\u66f4\u5947\u602a\u4e86\uff0c\u4ed6\u8f9b\u8f9b\u82e6\u82e6\u624d\u5c06\u8fd9\u6697\u5668\u5f04\u5230\u624b\uff0c\u4e3a\u4ec0\u9ebd\u53c8\u968f\u968f\u4fbf\u4fbf\u5c31\u4e22\u4e86 \u5462\uff1f\u201d \u3000\u3000\u674e\u7389\u51fd\u6c89\u541f\u9053\uff1a\u201c\u8fd9\u4e5f\u8bb8\u662f\u56e0\u4e3a\u4ed6\u89c1\u5230\u8fd9\u6697\u5668\u65e2\u7136\u4f24\u4e0d\u4e86\u695a\u5144\uff0c\u7559\u7740\u4e5f\u6ca1\u6709\u7528\u4e86\uff0c\u4e5f\u8bb8 \u662f\u56e0\u4e3a\u8fd9\u6697\u5668\u672c\u662f\u4ed6\u5077\u6765\u7684\uff0c\u4ed6\u751f\u6015\u6697\u5668\u7684\u4e3b\u4eba\u627e\u4ed6\u7b97\u5e10\uff0c\u6240\u4ee5\u7d22\u6027\u968f\u624b\u4e00\u629b\uff0c\u597d\u6559\u522b\u4eba\u518d \u4e5f\u67e5\u4e0d\u51fa\u662f\u8c01\u5077\u7684\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9644\u638c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4e00\u5b9a\u5c31\u662f\u8fd9\u539f\u56e0\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u9053\uff1a\u201c\u800c\u4e14\u542c\u8bf4\u8fd9\u6697\u5668\u53d1\u51fa\u5fc5\u5b9a\u8981\u89c1\u8840\uff0c\u5426\u5219\u5c31\u4f1a\u5bf9\u4e3b\u4eba\u4e0d\u5229\uff0c\u4ed6\u60f3\u5fc5\u4e5f\u5df2\u4e45\u95fb \u8fd9\u6697\u5668\u4e4b\u4e0d\u7965\uff0c\u600e\u6562\u518d\u5c06\u4e4b\u5e26\u5728\u8eab\u8fb9\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u4e5f\u6709\u53ef\u80fd\uff0c\u53ef\u662f\u2026\u2026\u201d \u3000\u3000\u674e\u7389\u51fd\u9053\uff1a\u201c\u53ef\u662f\u6b64\u4eba\u7a76\u7adf\u662f\u8c01\u5462\uff1f\u695a\u5144\u96be\u9053\u8fde\u4e00\u70b9\u4e5f\u731c\u4e0d\u5230\u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u5fae\u7b11\u9053\uff1a\u201c\u6211\u65e2\u672b\u80fd\u89c1\u5230\u6b64\u4eba\u9762\u76ee\uff0c\u5984\u52a0\u731c\u6d4b\u53ea\u4e0d\u8fc7\u5f92\u4e71\u4eba\u5fc3\u800c\u5df2\uff0c\u4f46\u4ed6\u65e2\u7136\u5982 \u6b64\u5904\u5fc3\u79ef\u8651\u7684\u8981\u6740\u6211\uff0c\u4e00\u6b21\u4e0d\u6210\uff0c\u5fc5\u6709\u4e8c\u6b21\uff0c\u6211\u603b\u6709\u4e00\u5929\u4f1a\u77e5\u9053\u4ed6\u662f\u8c01\u7684\u3002\u201d \u3000\u3000\u53ea\u542c\u4e00\u4eba\u94f6\u94c3\u822c\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u9ebd\u591a\u5e74\u6765\uff0c\u6211\u8fd8\u6ca1\u542c\u8bf4\u8fc7\u6709\u4e00\u4e2a\u4eba\u80fd\u9003\u5f97\u8fc7\u695a\u9999\u5e05\u638c \u5fc3\u7684\u3002\u201d \u3000\u3000\u53e3\u53e3\u53e3 \u3000\u3000\u6740\u4eba\u7684\u591c\uff0c\u5947\u8be1\u7684\u6697\u5668\uff0c\u795e\u79d8\u7684\u523a\u5ba2\uff0c\u8840\u8165\u7684\u6545\u4e8b\uff0c\u8fd9\u5c4b\u5b50\u91cc\u7684\u6c14\u6c1b\u672c\u6765\u5df2\u6c89\u91cd\u5f97\u4ee4\u4eba \u7a92\u606f\u3002 \u3000\u3000\u4f46\u67f3\u65e0\u7709\u4e00\u8d70\u8fdb\u6765\uff0c\u8fd9\u5c4b\u5b50\u5c31\u4f3c\u4e4e\u5ffd\u7136\u53d8\u5f97\u6709\u4e86\u5149\u91c7\uff0c\u6709\u4e86\u751f\u6c14\uff0c\u8fde\u90a3\u76cf\u5df2\u6447\u6447\u6b32\u706d\u7684 \u6cb9\u706f\uff0c\u90fd\u4f3c\u4e4e\u53d8\u5f97\u660e\u4eae\u8d77\u6765\u3002 \u3000\u3000\u5979\u53ea\u662f\u5c06\u5934\u53d1\u677e\u677e\u7684\u633d\u4e86\u4e2a\u9afb\uff0c\u6de1\u626b\u5ce8\u7709\uff0c\u672b\u65bd\u8102\u7c89\uff0c\u4f46\u9762\u4e0a\u5374\u4e1d\u6beb\u6ca1\u6709\u6194\u60b4\u75b2\u5026\u4e4b \u8272\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u51e0\u4e4e\u4e0d\u76f8\u4fe1\u7ad9\u5728\u81ea\u5df1\u9762\u524d\u7684\u8fd9\u8273\u5149\u7167\u4eba\u7684\u5973\u5b50\uff0c\u65b9\u8fd8\u5728\u75db\u82e6\u4e2d\u6323\u624e\u640f\u6597\uff0c\u8f97\u8f6c\u547b \u541f\u3002 \u3000\u3000\u6700\u5999\u7684\u662f\uff0c\u5979\u624b\u91cc\u7adf\u8fd8\u6367\u7740\u58f6\u9152\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u7684\u773c\u775b\u53c8\u4eae\u4e86\uff0c\u5fcd\u4e0d\u4f4f\u5c31\u8981\u53bb\u5c06\u8fd9\u58f6\u9152\u63a5\u8fc7\u6765\u3002 \u3000\u3000\u8c01\u77e5\u4ed6\u7684\u624b\u521a\u4f38\u51fa\uff0c\u695a\u7559\u9999\u5ffd\u7136\u95ea\u7535\u822c\u6263\u4f4f\u4e86\u4ed6\u8109\u95e8\uff0c\u6389\u8f6c\u4ed6\u624b\u81c2\uff0c\u80e1\u94c1\u82b1\u53eb\u4e86\u8d77\u6765\uff0c \u9053\u3002 \u3000\u3000\u201c\u4f60\u53c8\u72af\u4e86\u4ec0\u9ebd\u6bdb\u75c5\uff1f\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u8fd8\u672b\u8bf4\u5b8c\uff0c\u695a\u7559\u9999\u51fa\u624b\u5982\u7535\uff0c\u5df2\u70b9\u4e86\u4ed6\u201c\u5929\u6cc9\u201d\u3001\u201c\u4fa0\u767d\u201d\u3001\u201c\u5c3a\u6cfd\u201d\u3001\u201c\u5b54 \u6700\u201d\u3001\u201c\u5927\u51cc\u201d\u4e94\u5904\u7a74\u9053\u3002 \u7b2c\u4e94\u7ae0\u3000\u75c5\u56f0\u82f1\u96c4>> \u53e4\u9f99\u300a\u695a\u7559\u9999\u7cfb\u5217\xb7\u753b\u7709\u9e1f\u300b \u7b2c\u4e94\u7ae0\u3000\u75c5\u56f0\u82f1\u96c4 \u3000\u3000\u80e1\u94c1\u82b1\u975e\u4f46\u624b\u4e0d\u80fd\u52a8\uff0c\u534a\u8fb9\u8eab\u4e5f\u53d1\u4e86\u9ebb\uff0c\u201c\u5657\u201d\u5730\u5750\u5230\u6905\u5b50\u4e0a\uff0c\u7741\u5927\u4e86\u773c\u77a7\u695a\u7559\u9999\u3002 \u3000\u3000\u674e\u7389\u51fd\u592b\u5987\u4e5f\u89c9\u5f97\u5f88\u60ca\u5947\u3002 \u3000\u3000\u67f3\u65e0\u7709\u5ae3\u7136\u9053\uff1a\u201c\u695a\u7559\u9999\u96be\u9053\u6015\u6211\u8fd9\u58f6\u9152\u91cc\u4e5f\u6709\u6bd2\u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u9152\u4e2d\u7eb5\u7136\u65e0\u6bd2\uff0c\u4ed6\u8eab\u5b50\u91cc\u5374\u5df2\u6709\u6bd2\u4e86\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u52a8\u5bb9\u9053\uff1a\u201c\u80e1\u5144\u65b9\u96be\u9053\u5df2\u559d\u4e0b\u90a3\u676f\u6bd2\u9152\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u8fd9\u6b21\u5012\u4e0d\u662f\u9152\u5bb3\u4e86\u4ed6\uff0c\u800c\u662f\u4ed6\u7684\u624b\u3002\u201d \u3000\u3000\u5927\u5bb6\u8fd9\u624d\u53d1\u73b0\uff0c\u80e1\u94c1\u82b1\u7684\u4e00\u53ea\u624b\u5df2\u80bf\u4e86\u8d77\u6765\uff0c\u800c\u4e14\u8fd8\u4f3c\u9690\u9690\u6709\u9ed1\u6c14\u900f\u51fa\uff0c\u674e\u7389\u51fd\u5931\u8272 \u9053\uff1a\u201c\u80e1\u5144\u662f\u600e\u9ebd\u4e2d\u7684\u6bd2\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u7528\u53e6\u4e00\u53ea\u624b\u63c9\u4e86\u63c9\u9f3b\u5b50\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u6211\u6015\u662f\u649e\u89c1\u4e86\u4e2a\u5927\u5934\u9b3c\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4f60\u65b9\u53ef\u662f\u7528\u624b\u5c06\u90a3\u66b4\u96e8\u68a8\u82b1\u94bf\u4e00\u679a\u679a\u81ea\u5730\u4e0a\u62d4\u51fa\u6765\u7684\u9ebd\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u55ef\uff01\u201d \u3000\u3000\u695a\u7559\u9999\u957f\u53f9\u9053\uff1a\u201c\u8fd9\u5c31\u5bf9\u4e86\uff0c\u4f60\u4ee5\u4e3a\u4f60\u7684\u624b\u65e2\u6ca1\u6709\u7834\uff0c\u6bd2\u6c14\u5c31\u4e0d\u4f1a\u81ea\u624b\u4e0a\u900f\u5165\uff0c\u5374\u4e0d\u77e5 \u9488\u4e0a\u7684\u6bd2\u5df2\u7531\u4f60\u6307\u7532\u7f1d\u91cc\u900f\u4e86\u8fdb\u53bb\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u5fcd\u4e0d\u4f4f\u63d2\u53e3\u9053\uff1a\u201c\u53ef\u662f\uff0c\u636e\u6211\u6240\u77e5\uff0c\u8fd9\u66b4\u96e8\u68a8\u82b1\u9489\u4e0a\uff0c\u4ece\u6765\u4e0d\u6dec\u6bd2\uff0c\u53ea\u56e0\u8fd9\u6697\u5668 \u529b\u9053\u5b9e\u5728\u592a\u731b\uff0c\u7eb5\u7136\u65e0\u6bd2\uff0c\u4e2d\u4eba\u4e5f\u5fc5\u6b7b\u65e0\u6551\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u53c8\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u674e\u5144\u8bdd\u867d\u8bf4\u5f97\u4e0d\u9519\uff0c\u4f46\u8fd9\u4f4d\u4ec1\u5144\u5374\u8fd8\u751f\u6015\u6211\u6b7b\u5f97\u4e0d\u591f\u5feb\uff0c\u6240 \u4ee5\u53c8\u5728\u65e0\u6bd2\u7684\u66b4\u96e8\u68a8\u82b1\u9489\u4e0a\u6dec\u4e86\u5267\u6bd2\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u592b\u5987\u5bf9\u671b\u4e00\u773c\uff0c\u4e0d\u518d\u8bf4\u8bdd\uff0c\u5374\u5c06\u6cb9\u706f\u79fb\u5230\u90a3\u5806\u68a8\u82b1\u9489\u65c1\uff0c\u67f3\u65e0\u7709\u81ea\u5934\u53d1\u4e0a\u62d4\u4e0b\u4e00 \u6839\u94f6\u7c2a\uff0c\u8f7b\u8f7b\u6311\u8d77\u4e86\u4e00\u679a\u68a8\u82b1\u9489\uff0c\u4ed4\u7ec6\u77a7\u4e86\u534a\u664c\uff0c\u706f\u5149\u4e0b\uff0c\u53ea\u89c1\u4e24\u4eba\u7684\u8138\u8272\u90fd\u8d8a\u6765\u8d8a\u6c89\u91cd\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u8f7b\u8f7b\u54b3\u4e86\u96e8\u58f0\uff0c\u9053\uff1a\u201c\u4e0a\u9762\u53ef\u662f\u771f\u7684\u6709\u6bd2\u9ebd\uff1f\u201d \u3000\u3000\u674e\u7389\u51fd\u592b\u5987\u53c8\u5bf9\u671b\u4e86\u4e00\u773c\uff0c\u67f3\u65e0\u7709\u9053\uff1a\u201c\u55ef\uff01\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4e45\u95fb\u674e\u8001\u524d\u8f88\u5b66\u7a76\u5929\u4eba\uff0c\u867d\u4ece\u4e0d\u5c51\u4ee5\u6bd2\u836f\u6697\u5668\u4f24\u4eba\uff0c\u4f46\u5bf9\u6b64\u9053\u5374\u6781\u6709\u7814 \u7a76\uff0c\u674e\u5144\u5bb6\u5b66\u6e0a\u6e90\uff0c\u6240\u77e5\u81ea\u4e5f\u975e\u6cdb\u6cdb\u4e4b\u8f88\u53ef\u6bd4\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u82e6\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u4eec\u4e24\u53e3\u5b50\u65e2\u7136\u4e5f\u8bf4\u9489\u4e0a\u6709\u6bd2\uff0c\u90a3\u662f\u4e07\u4e07\u9519\u4e0d\u4e86\u7684\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u6c89\u58f0\u9053\uff1a\u201c\u662f\u4ee5\u5728\u4e0b\u60f3\u8bf7\u6559\u674e\u5144\uff0c\u4e0d\u77e5\u8fd9\u6697\u5668\u4e0a\u6dec\u7684\u662f\u90a3\u4e00\u79cd\u6bd2\uff1f\u201d \u3000\u3000\u674e\u7389\u51fd\u4e5f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4e16\u4e0a\u6bd2\u836f\u7684\u79cd\u7c7b\u5b9e\u5728\u592a\u591a\uff0c\u5c31\u8fde\u5bb6\u7236\u6015\u4e5f\u672b\u5fc5\u80fd\u4e00\u4e00\u5206\u8fa8\u5f97 \u51fa\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u679c\u5728\u90a3\u91cc\uff0c\u4f3c\u4e4e\u518d\u4e5f\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u77aa\u4e86\u77aa\u773c\u775b\uff0c\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u6211\u8fd9\u6bd2\u662f\u6ca1\u6cd5\u5b50\u53ef\u89e3\u7684\u4e86\uff1f\u201d \u3000\u3000\u67f3\u65e0\u7709\u52c9\u5f3a\u7b11\u9053\uff1a\u201c\u8c01\u8bf4\u6ca1\u6cd5\u5b50\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u7f13\u7f13\u9053\uff1a\u201c\u4f60\u4eec\u4f55\u5fc5\u7792\u6211\uff0c\u96be\u9053\u5f53\u6211\u662f\u5c0f\u5b69\u9ebd\uff1f\u4f60\u4eec\u65e2\u7136\u8fde\u6211\u4e2d\u7684\u662f\u4ec0\u9ebd\u6bd2\u90fd\u4e0d \u77e5\u9053\uff0c\u53c8\u600e\u9ebd\u80fd\u4e3a\u6211\u89e3\u6bd2\uff1f\u201d \u3000\u3000\u674e\u7389\u51fd\u592b\u5987\u9762\u9762\u76f8\u89d1\uff0c\u4e5f\u90fd\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u773c\u73e0\u5b50\u4e00\u8f6c\uff0c\u5ffd\u7136\u5927\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u4e00\u4e2a\u4e2a\u90fd\u54ed\u4e27\u8138\u5e72\u4ec0\u9ebd\uff0c\u81f3\u5c11\u6211\u73b0\u5728\u603b\u8fd8\u6ca1\u6709 \u6b7b\u5440\uff01\u6765\uff0c\u4eca\u671d\u6709\u9152\u4eca\u671d\u9189\uff0c\u5148\u75db\u75db\u5feb\u5feb\u559d\u4e00\u987f\u518d\u8bf4\u3002\u201d \u3000\u3000\u4ed6\u8fd8\u6709\u4e00\u53ea\u624b\u53ef\u4ee5\u52a8\uff0c\u5c45\u7136\u5c31\u60f3\u7528\u8fd9\u53ea\u624b\u53bb\u62ff\u9152\u58f6\uff0c\u53ef\u662f\u695a\u7559\u9999\u53c8\u5c06\u4ed6\u8fd9\u53ea\u624b\u62c9\u4f4f\u4e86\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u9ebd\u4e0d\u8ba9\u6211\u8d81\u8fd9\u65f6\u5019\u591a\u559d\u4e24\u676f\uff0c\u7b49\u6211\u6b7b\u4e86\uff0c\u4f60\u5c31\u7b97\u5929\u5929\u5c06\u9152\u6cfc\u5728\u6211\u7684 \u575f\u5934\u4e0a\uff0c\u6211\u4e5f\u8fde\u4e00\u6ef4\u90fd\u5c1d\u4e0d\u5230\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u6211\u73b0\u5728\u5df2\u5c06\u6bd2\u6c14\u5168\u90fd\u5c01\u95ed\u5728\u4f60\u624b\u81c2\u91cc\uff0c\u53ea\u8981\u4f60\u4e0d\u559d\u9152\uff0c\u4e00\u4e2a\u5bf9\u65f6\u4e4b\u5185\uff0c\u6bd2 \u6027\u5c31\u7edd\u4e0d\u4f1a\u8513\u5ef6\u2026\u2026\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u4e00\u4e2a\u5bf9\u65f6\u4e4b\u5f8c\u5462\uff1f\u5728\u8fd9\u5341\u4e8c\u4e2a\u65f6\u8fb0\u91cc\uff0c\u4f60\u96be\u9053\u5c31\u80fd\u627e\u5f97\u5230\u4e3a\u6211\u89e3\u6bd2\u7684\u4eba \u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u5782\u4e0b\u4e86\u5934\uff0c\u9053\uff1a\u201c\u65e0\u8bba\u5982\u4f55\uff0c\u8fd9\u603b\u6bd4\u7edd\u671b\u4e86\u7684\u597d\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u53c8\u5927\u7b11\u8d77\u6765\uff0c\u9053\uff1a\u201c\u597d\u5144\u5f1f\uff0c\u4f60\u4e5f\u7528\u4e0d\u4e3a\u6211\u7a9d\u7a9d\u56ca\u56ca\u7684\u53bb\u6c42\u4eba\uff0c\u53ea\u8981\u8ba9\u6211\u628a\u8fd9\u58f6 \u9152\u559d\u4e0b\u53bb\uff0c\u6211\u4e00\u5b9a\u6b7b\u4e0d\u4e86\u7684\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u81ea\u9774\u7b52\u91cc\u62bd\u51fa\u4e00\u67c4\u77ed\u5251\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u770b\uff0c\u8fd9\u5c31\u662f\u6211\u89e3\u6bd2\u7684\u6cd5\u5b50\uff0c\u8fd9\u6cd5\u5b50\u5c82\u975e\u518d\u597d \u4e5f\u6ca1\u6709\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u5931\u58f0\u9053\uff1a\u201c\u4f60\u96be\u9053\u60f3\u2026\u2026\u201d \u3000\u3000\u80e1\u94c1\u82b1\u5927\u7b11\u9053\uff1a\u201c\u5e38\u8a00\u9053\uff1a\u877c\u86c7\u566c\u624b\uff0c\u58ee\u58eb\u65ad\u8155\uff0c\u8fd9\u53c8\u6709\u4ec0\u9ebd\u4e86\u4e0d\u8d77\uff0c\u4f60\u4f55\u5fc5\u5927\u60ca\u5c0f \u655d\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u671b\u4ed6\u624b\u4e2d\u8fd9\u67c4\u96ea\u4eae\u7684\u77ed\u5251\uff0c\u5df2\u662f\u6ee1\u5934\u5927\u6c57\uff0c\u800c\u80e1\u94c1\u82b1\u81ea\u5df1\u5374\u8fde\u8138\u8272\u90fd\u6ca1\u6709\u53d8\u3002 \u3000\u3000\u674e\u7389\u51fd\u957f\u53f9\u9053\uff1a\u201c\u80e1\u5144\u679c\u7136\u4e0d\u6127\u4e3a\u58ee\u58eb\uff0c\u53ea\u4e0d\u8fc7\u2026\u2026\u201d \u3000\u3000\u67f3\u65e0\u7709\u5ffd\u7136\u62a2\u9053\uff1a\u201c\u53ea\u4e0d\u8fc7\u4f60\u4e00\u5b9a\u8981\u518d\u7b49\u5341\u4e8c\u4e2a\u65f6\u8fb0\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u67f3\u65e0\u7709\u9053\uff1a\u201c\u53ea\u56e0\u6211\u5df2\u60f3\u8d77\u4e86\u4e00\u4e2a\u53ef\u4ee5\u4e3a\u4f60\u89e3\u6bd2\u7684\u4eba\u3002\u201d \u3000\u3000\u5979\u4e0d\u7b49\u522b\u4eba\u8bf4\u8bdd\uff0c\u773c\u89d2\u4e00\u779f\u674e\u7389\u51fd\uff0c\u53c8\u62a2\u9053\uff1a\u201c\u4f60\u96be\u9053\u5fd8\u4e86\u90a3\u4f4d\u53ea\u6709\u4e03\u6839\u624b\u6307\u7684\u524d\u8f88\u4e86 \u9ebd\uff1f\u201d \u3000\u3000\u674e\u7389\u51fd\u76ee\u5149\u4e00\u95ea\uff0c\u5927\u559c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u7adf\u9669\u4e9b\u5fd8\u4e86\uff0c\u524d\u4e24\u5929\u56db\u8868\u5f1f\u8fd8\u4f1a\u63d0\u8d77\u8fd9\u4f4d\u524d\u8f88\uff0c \u8bf4\u4ed6\u8001\u4eba\u5bb6\u5df2\u5728\u201d\u53e4\u677e\u5e84\u201d\u548c\u718a\u8001\u4f2f\u62da\u4e86\u4e03\u5929\u4e03\u591c\u7684\u9152\u4e86\uff0c\u8fd8\u672b\u5206\u51fa\u80dc\u8d1f\uff0c\u53ea\u8981\u4ed6\u73b0\u5728\u8fd8\u6ca1 \u6709\u8d70\uff0c\u80e1\u5144\u5c31\u4e00\u5b9a\u6709\u6551\u4e86\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u7b11\u9053\uff1a\u201c\u65e2\u7136\u8fd8\u672b\u5206\u51fa\u80dc\u8d1f\uff0c\u4ed6\u5c31\u7b97\u8981\u8d70\uff0c\u718a\u8001\u4f2f\u4e5f\u4e0d\u4f1a\u653e\u4ed6\u8d70\u7684\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u5fcd\u4e0d\u4f4f\u95ee\u5230\uff1a\u201c\u53e4\u677e\u5e84\u5728\u90a3\u91cc\uff1f\u718a\u8001\u4f2f\u662f\u4ec0\u9ebd\u4eba\uff1f\u90a3\u4f4d\u4e03\u6839\u624b\u6307\u5934\u7684\u524d\u8f88\u53c8\u662f\u4f55 \u8bb8\u4eba\u4e5f\uff1f\u4f60\u4eec\u8bf4\u7684\u8fd9\u4e9b\u4eba\uff0c\u6211\u600e\u5730\u5168\u6ca1\u6709\u542c\u8fc7\uff1f\u201d \u3000\u3000\u674e\u7389\u51fd\u9053\uff1a\u201c\u8fd9\u4f4d\u718a\u8001\u4f2f\u867d\u7136\u548c\u5bb6\u7236\u90a3\u4e00\u8f88\u7684\u8bb8\u591a\u6b66\u6797\u524d\u8f88\u90fd\u662f\u597d\u670b\u53cb\uff0c\u81ea\u5df1\u5374\u5e76\u975e\u6c5f \u6e56\u4e2d\u4eba\uff0c\u80e1\u5144\u81ea\u7136\u6ca1\u6709\u542c\u8d77\u8fc7\u4ed6\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u9053\uff1a\u201c\u81f3\u65bc\u90a3\u4f4d\u4e03\u6839\u624b\u6307\u7684\u8001\u524d\u8f88\uff0c\u80e1\u5144\u5374\u4e00\u5b9a\u542c\u8fc7\u4ed6\u5927\u540d\u7684\uff0c\u53ea\u4e0d\u8fc7\u4ed6\u8001\u4eba\u5bb6 \u8fd1\u5e74\u4e3a\u4e86\u4e00\u4ef6\u4f24\u5fc3\u4e8b\uff0c\u5df2\u4e0d\u8bb8\u522b\u4eba\u518d\u63d0\u8d77\u4ed6\u7684\u540d\u59d3\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u966a\u7b11\u9053\uff1a\u201c\u8fd9\u4f4d\u8001\u524d\u8f88\u4eba\u867d\u70ed\u80a0\uff0c\u813e\u6c14\u5374\u5341\u5206\u53e4\u602a\uff0c\u82e5\u662f\u77e5\u9053\u6211\u4eec\u5728\u80cc\u5f8c\u72af\u4e86\u4ed6 \u7684\u5fcc\u8bb3\uff0c\u6211\u592b\u59bb\u6015\u5c31\u4f11\u60f3\u518d\u6709\u4e00\u5929\u597d\u65e5\u5b50\u8fc7\u4e86\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u7b11\u9053\uff1a\u201c\u6b64\u4eba\u813e\u6c14\u65e2\u5982\u6b64\u53e4\u602a\uff0c\u53c8\u548c\u6211\u7d20\u4e0d\u76f8\u8bc6\uff0c\u6211\u82e5\u53bb\u78b0\u4e2a\u5927\u9489\u5b50\u56de\u6765\uff0c\u5c82\u975e \u6bd4\u6b7b\u8fd8\u96be\u53d7\u5f97\u591a\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u5ae3\u7136\u9053\uff1a\u201c\u7528\u4e0d\u4f60\u53bb\u78b0\u9489\u5b50\uff0c\u6211\u4eec\u53bb\u5c31\u591f\u4e86\uff0c\u53ea\u8981\u6211\u7092\u4e24\u6837\u83dc\u7ed9\u4ed6\u5403\uff0c\u4ed6\u5c31\u518d\u4e5f \u4e0d\u4f1a\u62d2\u7edd\u4e86\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u53ef\u662f\u6211\u4eec\u5374\u5f97\u5feb\u8d70\uff0c\u53e4\u677e\u5e84\u7684\u8def\u867d\u4e0d\u8fdc\uff0c\u53ef\u4e5f\u4e0d\u8fd1\uff0c\u4f55\u51b5\uff0c\u4f60\u81f3 \u5c11\u8fd8\u8981\u5728\u90a3\u91cc\u5f04\u4e00\u4e2a\u65f6\u8fb0\u7684\u83dc\u54e9\uff01\u201d \u3000\u3000\u80e1\u94c1\u82b1\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4e24\u4f4d\u5982\u6b64\u70ed\u80a0\uff0c\u6211\u82e5\u518d\u63a8\u53c1\u963b\u56db\uff0c\u5c31\u4e0d\u662f\u4e1c\u897f\u4e86\uff0c\u53ef\u662f\u2026\u2026\u8001 \u81ed\u866b\uff0c\u4f60\u4e5f\u8be5\u966a\u4ed6\u53bb\u4e00\u8d9f\u624d\u5bf9\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u9053\uff1a\u201c\u7528\u4e0d\uff0c\u695a\u5144\u8fd8\u662f\u2026\u2026\u201d \u3000\u3000\u5979\u9aa4\u7136\u987f\u4f4f\u4e86\u8bed\u58f0\uff0c\u53ea\u56e0\u5979\u5ffd\u7136\u53d1\u89c9\u695a\u7559\u9999\u867d\u7136\u7aef\u7aef\u6b63\u6b63\u5750\u5728\u90a3\u91cc\uff0c\u5374\u5df2\u662f\u5168\u8eab\u53d1\u6296\uff0c \u9762\u5982\u91d1\u7eb8\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u7b80\u76f4\u5413\u5446\u4e86\uff0c\u98a4\u58f0\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u2026\u2026\u201d \u3000\u3000\u4ed6\u8bdd\u8fd8\u6ca1\u6709\u8bf4\u51fa\u6765\uff0c\u695a\u7559\u9999\u5df2\u5012\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u674e\u7389\u51fd\u3001\u67f3\u65e0\u7709\uff0c\u53cc\u53cc\u62a2\u53bb\u6276\u4ed6\uff0c\u89e6\u624b\u4e00\u6478\uff0c\u53ea\u89c9\u4ed6\u7684\u8eab\u5b50\u867d\u8fd8\u9694\u5c42\u8863\u670d\uff0c\u5374\u5df2\u6bd4\u70d9\u94c1 \u8fd8\u70eb\u624b\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u7ec8\u65bc\u4e5f\u6251\u4e86\u8fc7\u6765\uff0c\u5636\u58f0\u9053\uff1a\u201c\u4f60\u96be\u9053\u4e5f\u4e2d\u4e86\u6bd2\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u6447\u4e86\u6447\u5934\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u4e0d\u662f\u4e2d\u6bd2\u662f\u600e\u9ebd\u56de\u4e8b\uff0c\u674e\u5144\uff0c\u4f60\u2026\u2026\u4f60\u5feb\u77a7\u77a7\u4ed6\uff0c\u5feb\u2026\u2026\u201d \u3000\u3000\u695a\u7559\u9999\u54ac\u7259\uff0c\u5374\u8fd8\u662f\u52c9\u5f3a\u7b11\u9053\uff1a\u201c\u4f60\u96be\u9053\u4ece\u672a\u89c1\u8fc7\u4eba\u751f\u75c5\u9ebd\uff1f\u53c8\u4f55\u5fc5\u5927\u60ca\u5c0f\u602a\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u53ef\u662f\u4f60\u8eab\u4f53\u5c31\u50cf\u6761\u725b\u4e00\u6837\uff0c\u8fd9\u9ebd\u591a\u5e74\u6765\uff0c\u6211\u4ece\u6765\u4e5f\u6ca1\u6709\u89c1\u8fc7\u4f60\u751f\u75c5\uff0c\u8fd9\u6b21 \u600e\u9ebd\u4f1a\u75c5\u4e86\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u82e6\u7b11\u9053\uff1a\u201c\u8fd9\u6b21\u6211\u5b9e\u5728\u75c5\u5f97\u4e0d\u662f\u65f6\u5019\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u65b9\u8981\u5c06\u81ea\u5df1\u624b\u81c2\u780d\u4e0b\u6765\u65f6\uff0c\u8fd8\u662f\u8c08\u7b11\u81ea\u82e5\uff0c\u6b64\u523b\u5374\u4e5f\u5df2\u6025\u5f97\u6ee1\u5934\u5927\u6c57\uff0c\u55c4\u58f0\u9053\uff1a \u201c\u4ece\u6765\u4e0d\u751f\u75c5\u7684\u4eba\uff0c\u4e00\u75c5\u5c31\u4e0d\u8f7b\uff0c\u674e\u5144\u4f60\u2026\u2026\u201d \u3000\u3000\u67f3\u65e0\u7709\u67d4\u58f0\u9053\uff1a\u201c\u4f60\u4e5f\u7528\u4e0d\u592a\u6025\uff0c\u6211\u770b\u695a\u5144\u8fd9\u662f\u56e0\u4e3a\u8fd1\u65e5\u52b3\u7d2f\u8fc7\u5ea6\uff0c\u53c8\u53d7\u4e86\u98ce\u5bd2\uff0c\u518d\u52a0 \u4e0a\u65b9\u4e3a\u4f60\u4e00\u6025\uff0c\u5c31\u6025\u51fa\u75c5\u6765\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u75c5\u4e0d\u2026\u2026\u4e0d\u59a8\u4e8b\u7684\uff0c\u4e24\u4f4d\u8fd8\u662f\u2026\u2026\u8fd8\u662f\u5148\u53bb\u627e\u2026\u2026\u627e\u89e3\u836f\u8981 \u7d27\u3002\u201d \u3000\u3000\u4ed6\u867d\u7136\u5728\u8bf4\u201c\u4e0d\u59a8\u4e8b\u201d\uff0c\u4f46\u5634\u5507\u5374\u5df2\u6296\u5f97\u8fde\u8bdd\u90fd\u8bf4\u4e0d\u6e05\u695a\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u6211\u7684\u6bd2\u624d\u4e0d\u59a8\u4e8b\uff0c\u4f60\u4eec\u8fd8\u662f\u5148\u6cbb\u4ed6\u7684\u75db\u8981\u7d27\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u76b1\u7709\u9053\uff1a\u201c\u80e1\u8bf4\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u5927\u58f0\u9053\uff1a\u201c\u4f60\u82e5\u4e0d\u80af\u8ba9\u4ed6\u4eec\u5148\u4e3a\u4f60\u6cbb\u75c5\uff0c\u5c31\u7b97\u5c06\u89e3\u836f\u62ff\u6765\uff0c\u6211\u4e5f\u4e0d\u5403\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u6012\u9053\uff1a\u201c\u4f60\u6d3b\u5230\u8fd9\u9ebd\u5927\u5e74\u7eaa\uff0c\u600e\u5730\u8fd8\u4e0d\u77e5\u8f7b\u91cd\uff0c\u6211\u2026\u2026\u6211\u8fd9\u75c5\u5c31\u7b97\u518d\u7b49\u53c1\u5929\u518d\u6cbb \u4e5f\u6ca1\u5173\u7cfb\uff0c\u4f46\u4f60\u7684\u6bd2\u5374\u8fde\u4e00\u65f6\u534a\u523b\u4e5f\u803d\u8bef\u4e0d\u5f97\u3002\u201d \u3000\u3000\u4ed6\u6323\u624e\u8981\u7ad9\u8d77\u6765\uff0c\u4f46\u521a\u7ad9\u8d77\u6765\u5c31\u53c8\u8dcc\u5012\u3002\u80e1\u94c1\u82b1\u6025\u53bb\u6276\u4ed6\uff0c\u8fde\u8bdd\u4e5f\u987e\u4e0d\u5f97\u8bf4\u4e86\uff0c\u53ea\u662f\u8fde \u8fde\u987f\u8db3\u3002 \u3000\u3000\u674e\u7389\u51fd\u957f\u7b11\u9053\uff1a\u201c\u4e24\u4f4d\u5b9e\u662f\u4e49\u6c14\u4e8e\u4e91\uff0c\u53ea\u4e0d\u8fc7\u2026\u2026\u201d \u3000\u3000\u67f3\u65e0\u7709\u9053\uff1a\u201c\u53ea\u4e0d\u8fc7\u695a\u5144\u8fd9\u75c5\uff0c\u6700\u662f\u4e0d\u80fd\u52a8\u6c14\uff0c\u6211\u4eec\u82e5\u4e0d\u4f9d\u4ed6\uff0c\u53ea\u6709\u8ba9\u4ed6\u7684\u75db\u52a0\u91cd\uff0c\u597d \u5728\u6211\u8fd9\u91cc\u8fd8\u6709\u4e9b\u201d\u6e05\u5999\u6563\u201d\uff0c\u51b6\u8fd9\u79cd\u75c5\u6700\u6709\u6548\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u7acb\u523b\u6309\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u695a\u5144\u53ea\u8981\u6bcf\u9694\u4e24\u4e2a\u65f6\u8fb0\u5403\u4e00\u5305\uff0c\u7eb5\u7136\u672a\u5fc5\u5c31\u80fd\u75ca\u6108\uff0c\u4f46\u5728\u6211 \u4eec\u56de\u6765\u4e4b\u524d\uff0c\u75c5\u60c5\u5b9a\u7edd\u4e0d\u4f1a\u6076\u5316\u7684\u3002\u201d \u3000\u3000\u82e5\u7528\u201c\u5ea6\u65e5\u5982\u5e74\u201d\u8fd9\u56db\u4e2a\u5b57\u6765\u5f62\u5bb9\u80e1\u94c1\u82b1\u6b64\u523b\u7684\u5fc3\u60c5\uff0c\u5b9e\u5728\u662f\u518d\u6070\u5f53\u4e5f\u6ca1\u6709\u4e86\uff0c\u6700\u5148\u662f \u67f3\u65e0\u7709\u6bdb\u75c5\u53d1\u4f5c\uff0c\u7136\u5f8c\u662f\u65e0\u540d\u51f6\u624b\u7684\u6740\u4eba\u6697\u5668\uff0c\u73b0\u5728\u975e\u4f46\u4ed6\u81ea\u5df1\u4e2d\u4e86\u6bd2\uff0c\u8fde\u695a\u7559\u9999\u4e5f\u75c5\u5012\u5728 \u5e8a\uff0c\u4e0d\u80fd\u52a8\u4e86\u3002 \u3000\u3000\u8fd9\u9ebd\u591a\u70e6\u6b7b\u4eba\u7684\u4e8b\u52a0\u5728\u4e00\u8d77\uff0c\u5374\u504f\u504f\u8fde\u559d\u676f\u9152\u6d47\u6d47\u6101\u90fd\u4e0d\u884c\uff0c\u8fd9\u65e5\u5b50\u5374\u53eb\u80e1\u94c1\u82b1\u5982\u4f55\u5ea6 \u8fc7\uff1f \u3000\u3000\u597d\u4e0d\u5bb9\u6613\u7b49\u4e86\u4e24\u4e2a\u65f6\u8fb0\uff0c\u80e1\u94c1\u82b1\u7528\u4e00\u53ea\u624b\u6367\u8336\u7897\u548c\u6e05\u5999\u6563\u8fc7\u53bb\uff0c\u8c01\u77e5\u695a\u7559\u9999\u7adf\u8fde\u836f\u90fd\u62ff \u4e0d\u4f4f\uff0c\u5168\u6492\u5230\u5730\u4e0a\u4e86\u3002 \u3000\u3000\u5e78\u597d\u695a\u7559\u9999\u867d\u672b\u5403\u836f\uff0c\u75c5\u52bf\u5374\u4e5f\u672b\u6076\u5316\uff0c\u53cd\u800c\u6e10\u6e10\u7761\uff0c\u80e1\u94c1\u82b1\u809a\u5b50\u5df2\u997f\u5f97\u76f4\u53eb\uff0c\u5c31\u53eb\u5e97 \u4f19\u9001\u996d\u6765\u3002 \u3000\u3000\u90a3\u5e97\u4f19\u504f\u504f\u8fd8\u60f3\u8ba8\u597d\uff0c\u966a\u7b11\u9053\uff1a\u201c\u5ba2\u5b98\u6628\u5929\u559d\u7684\u4e0a\u597d\u6c7e\u9152\uff0c\u5c0f\u5e97\u6070\u5de7\u8fd8\u6709\u4e00\u575b\uff0c\u8fd8\u662f\u5c71 \u897f\u6765\u7684\u539f\u88c5\u8d27\u3002\u201d \u3000\u3000\u4e0d\u63d0\u201c\u9152\u201d\u5b57\u8fd8\u597d\uff0c\u4e00\u63d0\u201c\u9152\u201d\u5b57\uff0c\u80e1\u94c1\u82b1\u66f4\u662f\u6ee1\u809a\u5b50\u51a4\u6c14\u6ca1\u5904\u53d1\u4f5c\uff0c\u8df3\u8d77\u6765\u5927\u5b54\u9053\uff1a \u201c\u8001\u5b50\u53c8\u4e0d\u662f\u9152\u9b3c\uff0c\u5927\u767d\u5929\u559d\u4ec0\u9ebd\u9e1f\u9152\uff1f\u201d \u3000\u3000\u90a3\u5e97\u4f19\u518d\u4e5f\u4e0d\u660e\u767d\u8fd9\u9a6c\u5c41\u600e\u4f1a\u62cd\u5728\u9a6c\u817f\u4e0a\u4e86\uff0c\u5413\u5f97\u8dcc\u8dcc\u649e\u649e\u7684\u9003\u4e86\u51fa\u53bb\uff0c\u518d\u9001\u996d\u6765\u65f6\u5374 \u4e0d\u6562\u8fdb\u6765\u3002 \u3000\u3000\u695a\u7559\u9999\u8fd9\u4e00\u514d\u7adf\u7761\u4e86\u4e94\u4e2a\u65f6\u8fb0\uff0c\u5230\u9ec4\u660f\u65f6\uff0c\u624d\u60a0\u60a0\u9192\u6765\uff0c\u80e1\u94c1\u82b1\u672c\u6765\u51e0\u4e4e\u5df2\u4ee5\u4e3a\u4ed6\u7761\u6655 \u8fc7\u53bb\u4e86\uff0c\u8fd9\u65f6\u624d\u677e\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u89c9\u5f97\u597d\u4e9b\u4e86\u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u4e86\u7b11\uff0c\u8fd8\u672b\u8bf4\u8bdd\uff0c\u80e1\u94c1\u82b1\u53c8\u9053\uff1a\u201c\u4f60\u7528\u4e0d\u62c5\u5fc3\u6211\uff0c\u6211\u7684\u6bd2\u5012\u4e0d\u59a8\u4e8b\uff0c\u9664\u4e86\u8fd9\u6761 \u8180\u5b50\u88ab\u4f60\u70b9\u4f4f\u7a74\u9053\uff0c\u4e0d\u80fd\u52a8\u5916\uff0c\u5403\u4e5f\u80fd\u5403\uff0c\u5c31\u8ddf\u597d\u4eba\u5168\u6ca1\u6709\u4ec0\u9ebd\u4e24\u6837\u3002\u201d \u3000\u3000\u8fd9\u65f6\u5c4b\u5b50\u91cc\u5df2\u6e10\u6e10\u6697\u4e86\u4e0b\u6765\uff0c\u80e1\u94c1\u82b1\u70b9\u8d77\u4e86\u706f\uff0c\u8ba9\u695a\u7559\u9999\u559d\u4e86\u7897\u7ca5\uff0c\u695a\u7559\u9999\u7684\u624b\u8fd8\u662f\u5728 \u53d1\u6296\uff0c\u8fde\u7897\u90fd\u62ff\u4e0d\u4f4f\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u9762\u4e0a\u867d\u5728\u7b11\uff0c\u5fc3\u60c5\u5374\u4e0d\u7981\u8d8a\u6765\u8d8a\u6c89\u91cd\u3002 \u3000\u3000\u695a\u7559\u9999\u5598\u6c14\u9053\uff1a\u201c\u4ed6\u4eec\u8fd8\u6ca1\u6709\u56de\u6765\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u77a7\u7a97\u5916\u7684\u591c\u8272\uff0c\u9ed8\u7136\u534a\u664c\uff0c\u7ec8\u65bc\u5fcd\u4e0d\u4f4f\u9053\uff1a\u201c\u6c5f\u6e56\u4e2d\u90a3\u91cc\u6709\u4e03\u6839\u624b\u6307\u7684\u6b66\u6797\u524d \u8f88\uff1f\u6211\u600e\u9ebd\u60f3\u4e5f\u60f3\u4e0d\u51fa\u6765\uff1f\u4ee5\u524d\u867d\u6709\u4e2a\u201d\u4e03\u6307\u795e\u5077\u201d\uff0c\u4f46\u4ed6\u5374\u5e76\u975e\u53ea\u6709\u4e03\u6839\u6307\u5934\uff0c\u800c\u662f\u53f3\u624b \u4e0a\u591a\u51fa\u4e24\u6839\u679d\u6307\uff0c\u5982\u8d77\u6765\u4e00\u5171\u6709\u5341\u4e8c\u6839\u4e86\uff0c\u4f55\u51b5\uff0c\u6b64\u4eba\u975e\u4f46\u4e0d\u4f1a\u89e3\u6bd2\uff0c\u800c\u4e14\u65e9\u5df2\u6b7b\u4e86\u5f88 \u4e45\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u4f60\u8ba4\u4e3a\u8fd9\u592b\u59bb\u4e24\u4eba\u662f\u5728\u8bf4\u8c0e\u9ebd\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u7b11\u4e86\uff0c\u9053\uff1a\u201c\u4ed6\u4eec\u4e3a\u4ec0\u9ebd\u8981\u8bf4\u8c0e\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u53f9\u4e86\u53e3\u6c14\uff0c\u53c8\u95ed\u8d77\u773c\u775b\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u7b11\u9053\uff1a\u201c\u6211\u53ea\u5e0c\u671b\u4ed6\u4eec\u5feb\u4e9b\u56de\u6765\u624d\u597d\uff0c\u5426\u5219\u6628\u5929\u665a\u4e0a\u90a3\u4f4d\u4ec1\u5144\u82e5\u53c8\u95ef\u6765\uff0c\u6211\u4eec\u4e24 \u4e2a\u53ea\u6015\u552f\u6709\u4efb\u51ed\u4ed6\u5bb0\u5272\u4e86\u3002\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u8bf4\u51fa\u6765\uff0c\u695a\u7559\u9999\u8fd8\u672b\u600e\u6837\uff0c\u80e1\u94c1\u82b1\u81ea\u5df1\u5374\u4e0d\u7981\u673a\u4f36\u4f36\u6253\u4e86\u4e2a\u5bd2\u5664\u2014\u2014\u6b64\u523b\u695a\u7559\u9999 \u8fde\u7897\u90fd\u62ff\u4e0d\u4f4f\uff0c\u4ed6\u81ea\u5df1\u4e5f\u53ea\u5269\u4e0b\u4e00\u53ea\u624b\u53ef\u4ee5\u52a8\uff0c\u90a3\u8be1\u79d8\u7684\u523a\u5ba2\u82e5\u518d\u5ea6\u524d\u6765\uff0c\u4ed6\u4e24\u4eba\u7b80\u76f4\u8fde\u8fd8 \u624b\u4e4b\u529b\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u201c\u4f46\u6b64\u4eba\u65e2\u7136\u5982\u6b64\u5904\u5fc3\u79ef\u8651\u8981\u6740\u6211\uff0c\u4e00\u6b21\u4e0d\u6210\uff0c\u5fc5\u6709\u4e8c\u6b21\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u8bf4\u8fd9\u8bdd\u65f6\uff0c\u80e1\u94c1\u82b1\u4e5f\u672b\u89c9\u5f97\u600e\u6837\uff0c\u4f46\u73b0\u5728\u8d8a\u60f3\u8d8a\u89c9\u5f97\u53ef\u6015\uff0c\u60c5\u4e0d\u81ea\u7981\uff0c\u7d27\u7d27\u95ed\u8d77 \u4e86\u7a97\u5b50\u3002 \u3000\u3000\u53ea\u542c\u695a\u7559\u9999\u53f9\u9053\uff1a\u201c\u4ed6\u82e5\u8981\u6765\uff0c\u4f60\u5173\u4e0a\u7a97\u5b50\u53c8\u6709\u4f55\u7528\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u6014\u4e86\u534a\u664c\uff0c\u5934\u4e0a\u53c8\u6c81\u51fa\u4e86\u51b7\u6c57\u3002 \u3000\u3000\u53c8\u8fc7\u4e86\u7247\u523b\uff0c\u661f\u6708\u90fd\u672b\u5347\u8d77\uff0c\u96e8\u70b9\u5374\u5df2\u843d\u4e0b\u3002 \u3000\u3000\u56db\u9762\u7684\u4eba\u58f0\uff0c\u7acb\u523b\u9759\u4e86\u4e0b\u6765\uff0c\u53ea\u6709\u96e8\u70b9\u6572\u7a97\u6237\uff0c\u8d8a\u6765\u8d8a\u6025\uff0c\u8d8a\u6765\u8d8a\u54cd\uff0c\u5230\u5f8c\u6765\u7adf\u5982\u6218\u9f13 \u8f7b\u51fb\uff0c\u50ac\u4eba\u70ed\u8840\u3002 \u3000\u3000\u8fd9\u65f6\u82e5\u6709\u591c\u884c\u4eba\u8d70\u52a8\uff0c\u975e\u4f46\u65e0\u6cd5\u542c\u5f97\u5230\u4ed6\u7684\u811a\u6b65\u58f0\uff0c\u5c31\u8fde\u4ed6\u7684\u5927\u8882\u5e26\u98ce\u65f6\u90fd\u542c\u4e0d\u5230\u4e86\u3002 \u3000\u3000\u201c\u5077\u96e8\u4e0d\u5077\u96ea\u3002\u201d \u3000\u3000\u96e8\u591c\u6b63\u662f\u591c\u884c\u4eba\u51fa\u6ca1\u7684\u597d\u65f6\u5019\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u5ffd\u7136\u63a8\u5f00\u7a97\u5b50\uff0c\u77aa\u5927\u4e86\u773c\u775b\uff0c\u77ac\u4e5f\u4e0d\u77ac\u5730\u671b\u7a97\u5916\uff0c\u9662\u5b50\u91cc\u7684\u68a7\u6850\u4e5f\u50cf\u662f\u53d8\u6210\u4e86\u5e62 \u5e62\u9b45\u5f71\uff0c\u5728\u77aa\u4ed6\u3002 \u3000\u3000\u7a81\u542c\u201c\u55d6\u201d\u7684\u4e00\u58f0\uff0c\u4e00\u6761\u9ed1\u5f71\u5728\u7a97\u524d\u7a9c\u8fc7\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u4e00\u60ca\uff0c\u7b49\u4ed6\u770b\u51fa\u8fd9\u53ea\u4e0d\u8fc7\u662f\u6761\u732b\u65f6\uff0c\u5df2\u88ab\u5413\u51fa\u4e00\u8eab\u51b7\u6c57\u3002 \u3000\u3000\u695a\u7559\u9999\u4e5f\u5931\u58f0\u9053\uff1a\u201c\u6709\u4eba\u6765\u4e86\u9ebd\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u52c9\u5f3a\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u53ea\u4e0d\u8fc7\u662f\u53ea\u761f\u732b\u800c\u5df2\u3002\u201d \u3000\u3000\u4ed6\u53e3\u6c14\u542c\u6765\u867d\u8f7b\u677e\uff0c\u5176\u5b9e\u5fc3\u91cc\u5374\u662f\u8bf4\u4e0d\u51fa\u7684\u9178\u695a\u3002 \u3000\u3000\u4ed6\u4e24\u4eba\u7eb5\u6a2a\u6c5f\u6e56\uff0c\u7b11\u50b2\u751f\u6b7b\uff0c\u51e0\u66fe\u5c06\u522b\u4eba\u653e\u5728\u773c\u91cc\uff0c\u5c31\u7b97\u662f\u9762\u5bf9\u5343\u519b\u4e07\u9a6c\uff0c\u4ed6\u4eec\u4e5f\u4e0d\u4f1a \u76b1\u4e00\u76b1\u7709\u5934\u3002 \u3000\u3000\u4f46\u73b0\u5728\uff0c\u53ea\u4e0d\u8fc7\u662f\u53ea\u732b\uff0c\u5c31\u5413\u51fa\u4e86\u51b7\u6c57\u3002 \u3000\u3000\u591c\u96e8\u79cb\u706f\uff0c\u4e00\u706f\u5982\u8c46\uff0c\u7edd\u4e16\u7684\u82f1\u96c4\uff0c\u7adf\u75c5\u56f0\u5728\u8fd9\u51c4\u51c9\u7684\u6597\u5ba4\u4e2d\uff0c\u80e1\u94c1\u82b1\u77a7\u4e86\u695a\u7559\u9999\u4e00 \u773c\uff0c\u51e0\u4e4e\u5fcd\u4e0d\u4f4f\u8981\u6d41\u4e0b\u6cea\u6765\u3002 \u3000\u3000\u591c\u96e8\u79cb\u706f\uff0c\u4e00\u706f\u5982\u8c46\u3002 \u3000\u3000\u4f46\u90a3\u4e8c\u5341\u4e03\u679a\u201c\u66b4\u96e8\u68a8\u82b1\u9489\u201d\uff0c\u5374\u8fd8\u5728\u684c\u4e0a\u95ea\u5149\uff0c\u5c31\u50cf\u662f\u5728\u5bf9\u80e1\u94c1\u82b1\u793a\u5a01\u4f3c\u7684\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u773c\u775b\u5ffd\u4e5f\u4e00\u4eae\u201c\u8fd9\u6697\u5668\u65e2\u80fd\u6740\u4eba\uff0c\u4fbf\u4e5f\u80fd\u9632\u8eab\uff0c\u73b0\u5728\u5b83\u65e2\u7136\u5728\u6211\u624b\u4e0a\uff0c\u6211\u4e3a\u4f55\u4e0d \u80fd\u7528\u4ed6\u5939\u6740\u522b\u4eba\uff1f\u201d \u3000\u3000\u4ed6\u867d\u7136\u53ea\u5269\u4e0b\u4e00\u53ea\u624b\u80fd\u52a8\uff0c\u4f46\u8fd9\u53ea\u624b\u5374\u662f\u53d7\u8fc7\u4e25\u683c\u8bad\u7ec3\u7684\uff0c\u4e94\u6839\u624b\u6307\uff0c\u6bcf\u4e00\u6839\u90fd\u5f88\u7075 \u6d3b\uff0c\u5f88\u6709\u7528\u3002 \u3000\u3000\u4ed6\u867d\u7136\u672a\u66fe\u89c1\u8fc7\u8fd9\u201c\u66b4\u96e8\u68a8\u82b1\u9489\u201d\uff0c\u4f46\u5341\u6765\u5c81\u7684\u65f6\u5019\uff0c\u5c31\u5df2\u5c06\u6c5f\u6e56\u4e2d\u6bcf\u4e00\u79cd\u8896\u7bad\u7684\u5f29\u7b52 \u90fd\u62c6\u5f00\u6765\u7814\u7a76\u8fc7\u3002 \u3000\u3000\u53ea\u8d39\u4e86\u4e0d\u5230\u4e00\u987f\u996d\u7684\u529f\u592b\uff0c\u4ed6\u5c31\u5df2\u5c06\u8fd9\u68a8\u82b1\u9489\u7684\u5f29\u5323\u6253\u5f00\uff0c\u7528\u7b77\u5b50\u5c06\u94f6\u9489\u90fd\u6311\u5728\u5f29\u5323\u7684 \u9489\u69fd\u4e2d\u3002 \u3000\u3000\u53c8\u8d39\u4e86\u76cf\u8336\u529f\u592b\uff0c\u4ed6\u5c31\u5c06\u5f29\u5323\u91cd\u65b0\u88c5\u597d\u3002 \u3000\u3000\u5230\u4e86\u8fd9\u65f6\uff0c\u4ed6\u624d\u957f\u957f\u5410\u51fa\u53e3\u6c14\uff0c\u5583\u5583\u9053\uff1a\u201c\u597d\uff0c\u4f60\u5c0f\u5b50\u8981\u6765\u5c31\u6765\u5427\uff01\u201d \u3000\u3000\u5ffd\u7136\u95f4\uff0c\u53c8\u662f\u201c\u55d6\u201d\u7684\u4e00\u58f0\u54cd\u3002 \u3000\u3000\u53c8\u6709\u6761\u9ed1\u5f71\u81ea\u7a97\u524d\u7a9c\u8fc7\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u8fd9\u6b21\u5df2\u9547\u5b9a\u5f97\u591a\uff0c\u5df2\u770b\u51fa\u8fd9\u9ed1\u5f71\u53ea\u4e0d\u8fc7\u8fd8\u662f\u6761\u91ce\u732b\uff0c\u8c01\u77e5\u8fd9\u6b21\u732b\u7adf\u7b14\u76f4\u7a9c\u5165\u7a97 \u6237\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u7b11\u9a82\u9053\uff1a\u201c\u864e\u843d\u5e73\u9633\uff0c\u8fde\u4f60\u8fd9\u6761\u761f\u732b\u4e5f\u60f3\u6765\u6b3a\u8d1f\u4eba\u9ebd\uff1f\u201d \u3000\u3000\u4ed6\u6325\u624b\u53bb\u8d76\u732b\uff0c\u8c01\u77e5\u732b\u5ffd\u7136\u81ea\u534a\u7a7a\u4e2d\u6389\u4e0b\u6765\uff0c\u6389\u5728\u684c\u5b50\u4e0a\uff0c\u201c\u7830\u201d\u7684\u4e00\u58f0\uff0c\u684c\u4e0a\u7684\u706f\u51e0 \u4e4e\u88ab\u9707\u5012\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u7684\u624b\u53bb\u6276\u706f\uff0c\u773c\u775b\u5374\u77a7\u732b\uff0c\u53ea\u89c1\u8fd9\u53ea\u732b\u8eba\u5728\u684c\u5b50\u4e0a\u52a8\u4e5f\u4e0d\u52a8\uff0c\u7adf\u5df2\u5944\u5944\u4e00\u606f\uff0c\u5c31 \u5feb\u6b7b\u4e86\u3002 \u3000\u3000\u732b\u7684\u8116\u5b50\u4e0a\uff0c\u8fd8\u7cfb\u5f20\u7eb8\u6761\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u89e3\u4e0b\u6765\u4e00\u770b\uff0c\u53ea\u89c1\u4e0a\u9762\u8d6b\u7136\u5199\uff1a\u201c\u695a\u7559\u9999\uff0c\u695a\u7559\u9999\uff0c\u4f60\u770b\u770b\u81ea\u5df1\u73b0\u5728\u662f\u5426\u5df2\u548c\u8fd9 \u53ea\u732b\u76f8\u5dee\u65e0\u51e0\uff0c\u4f60\u8fd8\u60f3\u518d\u6d3b\u4e0b\u53bb\u9ebd\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u53c8\u60ca\u53c8\u6012\uff0c\u51e0\u4e4e\u5fcd\u4e0d\u4f4f\u5c31\u8981\u5927\u58f0\u559d\u9a82\u51fa\u6765\uff0c\u4f46\u5374\u53c8\u6015\u60ca\u52a8\u4e86\u695a\u7559\u9999\uff0c\u53ea\u6709\u54ac\u7259\u5fcd \u4f4f\u3002 \u3000\u3000\u8fd9\u5f20\u7eb8\u6761\u975e\u4f46\u662f\u4ed6\u4eec\u7684\u50ac\u547d\u7b26\uff0c\u800c\u4e14\u7b80\u76f4\u662f\u4e00\u79cd\u4fae\u8fb1\uff0c\u695a\u7559\u9999\u82e5\u662f\u77a7\u89c1\u4e86\u8fd9\u51e0\u53e5\u8bdd\uff0c\u5fc3 \u91cc\u53c8\u8be5\u662f\u4ec0\u9ebd\u6ecb\u5473\uff1f \u3000\u3000\u80e1\u94c1\u82b1\u77e5\u9053\u7eb8\u6761\u4e00\u5230\uff0c\u5bf9\u65b9\u7684\u4eba\u4e5f\u5feb\u6765\u4e86\uff0c\u4ed6\u4eec\u8fd9\u6b21\u7adf\u4e0d\u518d\u4ee5\u5351\u9119\u7684\u624b\u6bb5\u6765\u6697\u7b97\uff0c\u53cd\u800c \u5149\u660e\u5802\u7687\u7684\u6765\u53eb\u9635\uff0c\u81ea\u7136\u662f\u65e9\u5df2\u7b97\u8d70\u4e86\u695a\u7559\u9999\u975e\u4f46\u5df2\u6ca1\u6709\u62b5\u6297\u4e4b\u529b\uff0c\u800c\u4e14\u6839\u672c\u8fde\u9003\u90fd\u5df2\u9003\u4e0d \u4e86\u3002 \u3000\u3000\u4ed6\u77a7\u4e86\u77a7\u684c\u5b50\u4e0a\u5944\u5944\u4e00\u606f\u7684\u732b\uff0c\u53c8\u77a7\u4e86\u77a7\u5e8a\u4e0a\u7684\u695a\u7559\u9999\uff0c\u5ffd\u7136\u6293\u8d77\u90a3\u201c\u9489\u5323\u201d\uff0c\u7a9c\u51fa\u7a97 \u5916\u3002 \u3000\u3000\u4e0e\u5176\u7b49\u5bf9\u65b9\u8fdb\u6765\uff0c\u5012\u4e0d\u5982\u7d22\u6027\u95ef\u51fa\u53bb\u548c\u4ed6\u4eec\u4e00\u62da\u6b7b\u6d3b\uff0c\u80e1\u94c1\u82b1\u8fd9\u79cd\u5b81\u6298\u6bcb\u7531\u7684\u813e\u6c14\uff0c\u6b63 \u662f\u6b7b\u4e5f\u6539\u4e0d\u8fc7\u6765\u7684\u3002 \u3000\u3000\u4ed6\u53ea\u89c9\u5168\u8eab\u70ed\u8840\u5982\u6cb8\uff0c\u7adf\u5fd8\u4e86\u695a\u7559\u9999\u6b64\u523b\u5df2\u5168\u65e0\u62b5\u6297\u4e4b\u529b\uff0c\u4ed6\u51b2\u51fa\u53bb\u4e4b\u5f8c\uff0c\u82e5\u518d\u6709\u4eba\u6765 \u5bc2\u695a\u7559\u9999\u7684\u6027\u547d\uff0c\u5c82\u975e\u6b63\u5982\u63a2\u56ca\u53d6\u7269\uff0c\u624b\u5230\u64d2\u6765\u3002 \u3000\u3000\u96e8\u4e1d\u7ec6\u5bc6\uff0c\u7ed9\u672c\u5df2\u9ed1\u6697\u7684\u591c\u8272\u53c8\u52a0\u91cd\u4e86\u5e18\u5e55\uff0c\u90bb\u9662\u9690\u9690\u6709\u5973\u5b50\u7684\u7b11\u58f0\u4f20\u6765\uff0c\u66f4\u886c\u6258\u51fa\u8fd9 \u9662\u4e8e\u7684\u51c4\u51c9\u4e0e\u5bc2\u5bde\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u63a0\u51fa\u7a97\u5b50\uff0c\u63a0\u4e0a\u5c4b\u810a\uff0c\u5389\u58f0\u9053\uff1a\u201c\u670b\u53cb\u4f60\u65e2\u5df2\u6765\u4e86\uff0c\u6709\u79cd\u7684\u5c31\u5148\u6765\u548c\u6211\u59d3\u80e1\u7684\u4e00 \u5206\u9ad8\u4e0b\uff0c\u8eb2\u5728\u9ed1\u6697\u4e2d\u4e0d\u6562\u89c1\u4eba\uff0c\u7b97\u4e0d\u5f97\u82f1\u96c4\u597d\u6c49\u3002\u201d \u3000\u3000\u4ed6\u751f\u6015\u60ca\u52a8\u4e86\u695a\u7559\u9999\uff0c\u8bf4\u8bdd\u7684\u58f0\u97f3\u8fd8\u662f\u4e0d\u6562\u592a\u5927\uff0c\u5374\u53c8\u751f\u6015\u5bf9\u65b9\u542c\u4e0d\u89c1\uff0c\u4e00\u9762\u8bf4\u8bdd\uff0c\u4e00 \u9762\u987f\u8db3\u3002 \u3000\u3000\u8c01\u77e5\u4ed6\u8bdd\u8fd8\u672b\u8bf4\u5b8c\uff0c\u8eab\u5f8c\u7a81\u7136\u4f20\u6765\u201c\u55e4\u201d\u7684\u4e00\u7b11\u3002 \u3000\u3000\u4e00\u4eba\u51b7\u7b11\u9053\uff1a\u201c\u6211\u65e9\u5c31\u5728\u8fd9\u91cc\u7b49\u4f60\u4e86\uff0c\u8c01\u53eb\u4f60\u77a7\u4e0d\u89c1\u6211\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9aa4\u7136\u7ffb\u8eab\uff0c\u53ea\u89c1\u4eba\u5f71\u4e00\u95ea\uff0c\u5df2\u5230\u4e86\u53e6\u4e00\u91cd\u5c4b\u80cc\u4e0a\uff0c\u8fd9\u4eba\u5168\u8eab\u9ed1\u8863\uff0c\u8138\u4e0a\u4e5f\u6709\u9ed1\u5dfe \u8499\u9762\uff0c\u51b7\u7b11\u53c8\u9053\uff1a\u201c\u4f60\u82e5\u8981\u548c\u6211\u52a8\u624b\uff0c\u4e3a\u4f55\u4e0d\u8fc7\u6765\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u6012\u559d\u4e00\u58f0\u6251\u4e86\u8fc7\u53bb\uff0c\u4f46\u7b49\u4ed6\u63a0\u4e0a\u90a3\u9762\u5c4b\u810a\u4e0a\uff0c\u8fd9\u4eba\u5374\u5df2\u53c8\u8fdc\u5728\u4e03\u516b\u4e08\u5916\uff0c\u671b\u4ed6\u4e0d \u4f4f\u51b7\u7b11\u3002 \u3000\u3000\u4e24\u4eba\u4e00\u9003\u4e00\u8ffd\uff0c\u7728\u773c\u95f4\u4fbf\u79bb\u5f00\u5ba2\u6808\u5f88\u8fdc\uff0c\u80e1\u94c1\u82b1\u624b\u91cc\u867d\u6709\u4e16\u4e0a\u6700\u9738\u9053\u7684\u6697\u5668\uff0c\u600e\u5948\u90a3\u4eba \u603b\u662f\u548c\u4ed6\u4fdd\u6301\u4e03\u516b\u5973\u8ddd\u79bb\uff0c\u80e1\u94c1\u82b1\u65e2\u8ffd\u4e0d\u4e0a\uff0c\u53c8\u6015\u6697\u5668\u529b\u9053\u4e0d\u591f\uff0c\u8fd9\u6697\u5668\u5df2\u662f\u4ed6\u6700\u5f8c\u4e00\u6740 \u624b\uff0c\u4ed6\u600e\u6562\u8f7b\u4e3e\u5984\u52a8\uff0c\u4f5c\u5b64\u6ce8\u4e00\u63b7\u3002 \u3000\u3000\u8981\u77e5\u80e1\u94c1\u82b1\u7684\u8f7b\u529f\u672c\u6765\u4e0d\u9519\uff0c\u53ef\u662f\u6b64\u523b\u4ed6\u4e00\u6761\u624b\u81c2\u5df2\u88ab\u70b9\u4e86\u7a74\u9053\uff0c\u975e\u4f46\u6c14\u8840\u4e0d\u80fd\u7545\u901a\uff0c \u98de\u63a0\u65f6\u4e5f\u4e0d\u80fd\u4fdd\u6301\u5e73\u8861\u3002 \u3000\u3000\u4ed6\u7eb5\u7136\u7528\u5c3d\u5168\u529b\uff0c\u4e24\u4eba\u7684\u8ddd\u79bb\u53cd\u800c\u8d8a\u6765\u8d8a\u8fdc\u4e86\u3002 \u3000\u3000\u90a3\u9ed1\u8863\u4eba\u5ffd\u53c8\u63a0\u4e0b\u5c4b\u810a\uff0c\u4e0d\u8d70\u5927\u8def\uff0c\u4e13\u7a7f\u5c0f\u5df7\uff0c\u53ea\u89c1\u4ed6\u8eab\u5f62\u5982\u6e38\u9c7c\u822c\u4e1c\u4e00\u6ed1\uff0c\u897f\u4e00\u6298\uff0c \u5ffd\u7136\u4e0d\u89c1\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u6012\u543c\u9053\uff1a\u201c\u4f60\u65e2\u7136\u8981\u6740\u6211\uff0c\u6211\u5c31\u5728\u8fd9\u91cc\uff0c\u4f60\u4e3a\u4f55\u4e0d\u8fc7\u6765\u52a8\u624b\uff1f\u201d \u3000\u3000\u8bdd\u672b\u8bf4\u5b8c\uff0c\u524d\u9762\u8f6c\u89d2\u5904\u7a81\u53c8\u4f20\u51fa\u201c\u55e4\u201d\u7684\u4e00\u7b11\u3002 \u3000\u3000\u90a3\u4eba\u63a2\u51fa\u534a\u4e2a\u5934\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u6211\u8fd8\u662f\u5728\u7b49\u4f60\uff0c\u4f60\u53c8\u4e3a\u4f55\u4e0d\u8fc7\u6765\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u4e0d\u7b49\u4ed6\u8bf4\u5b8c\uff0c\u5df2\u7528\u5c3d\u5168\u529b\uff0c\u6251\u4e86\u8fc7\u53bb\uff0c\u8eab\u5b50\u521a\u8f6c\u8fc7\u5899\u89d2\uff0c\u53ea\u89c1\u4e00\u4e2a\u5356\u9984\u9968\u9762\u7684\u8001 \u5934\u6311\u62c5\u5b50\u8fce\u9762\u800c\u6765\u3002 \u3000\u3000\u4ed6\u518d\u60f3\u6536\u52bf\uff0c\u5df2\u6765\u4e0d\u53ca\u4e86\u3002 \u3000\u3000\u53ea\u542c\u201c\u54d7\u5566\u5566\u201d\u4e00\u58f0\u54cd\uff0c\u4ed6\u4eba\u5df2\u649e\u5728\u9984\u9968\u62c5\u5b50\u4e0a\uff0c\u9505\u91cc\u7684\u70ed\u6c64\uff0c\u67b6\u4e0a\u7684\u9171\u918b\uff0c\u5168\u90fd\u5012\u5728 \u4ed6\u8eab\u4e0a\uff0c\u4e00\u5927\u53e0\u9762\u4e5f\u6454\u5f97\u7cbe\u5149\u7c89\u788e\uff0c\u96e8\u5f8c\u7684\u77f3\u5730\u672c\u6765\u5df2\u5f88\u6ed1\uff0c\u518d\u52a0\u4e0a\u6ee1\u5730\u9ebb\u6cb9\uff0c\u80e1\u94c1\u82b1\u4e00\u649e \u4e4b\u5f8c\uff0c\u90a3\u91cc\u8fd8\u80fd\u7ad9\u5f97\u4f4f\u811a\u3002 \u7b2c\u516d\u7ae0\u3000\u51fa\u4e4e\u610f\u5916>> \u53e4\u9f99\u300a\u695a\u7559\u9999\u7cfb\u5217\xb7\u753b\u7709\u9e1f\u300b \u7b2c\u516d\u7ae0\u3000\u51fa\u4e4e\u610f\u5916 \u3000\u3000\u90a3\u9ed1\u8863\u4eba\u5374\u5728\u8fdc\u5904\u62cd\u624b\u5927\u7b11\u9053\uff1a\u201c\u5999\u6781\u5999\u6781\uff0c\u82b1\u8774\u8776\u4eca\u65e5\u53d8\u6210\u4e86\u843d\u6c64\u9e21\u4e86\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u6012\u543c\u521a\u722c\u8d77\u6765\uff0c\u90a3\u5356\u9762\u7684\u8001\u5934\u5b50\u5374\u5df2\u6eda\u8fc7\u6765\uff0c\u4e00\u628a\u63ea\u4f4f\u4ed6\u7684\u8863\u895f\uff0c\u6251\u5728\u4ed6\u8eab\u4e0a\uff0c \u5636\u58f0\u9053\uff1a\u201c\u4f60\u8d70\u8def\u4e0d\u5e26\u773c\u775b\u7684\u9ebd\uff1f\u4ffa\u4e00\u5bb6\u5927\u5c0f\uff0c\u5168\u90fd\u6307\u671b\u8fd9\u526f\u62c5\u5b50\u6d3b\u547d\uff0c\u4f60\u649e\u7ffb\u4e86\u4ffa\u7684\u547d\u6839 \u5b50\uff0c\u4ffa\u8ddf\u4f60\u62da\u4e86\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u8981\u60f3\u5c06\u8fd9\u8001\u5934\u5b50\u7528\u8131\uff0c\u81ea\u7136\u5bb9\u6613\u5f97\u5f88\uff0c\u53ea\u4e0d\u8fc7\u4ed6\u4e5f\u77e5\u9053\uff0c\u7406\u4e8f\u7684\u786e\u662f\u81ea\u5df1\uff0c\u53ea\u6709 \u5fcd\u4f4f\u6c14\u9053\uff1a\u201c\u4f60\u653e\u624b\uff0c\u6454\u574f\u4e86\u7684\u4e1c\u897f\uff0c\u6211\u8d54\u4f60\u3002\u201d \u3000\u3000\u90a3\u8001\u5934\u5b50\u9053\uff1a\u201c\u597d\uff0c\u4f60\u8d54\uff0c\u4f60\u62ff\u94b1\u5939\uff0c\u4ffa\u8fd9\u62c5\u5b50\u662f\u4e03\u4e24\u94f6\u5b50\u505a\u6210\u7684\uff0c\u518d\u52a0\u4e0a\u4e8c\u5341\u516b\u4e2a\u9752 \u74f7\u7897\uff0c\u4e00\u9505\u597d\u6c64\uff0c\u81f3\u5c11\u4e5f\u5f97\u8981\u5341\u4e24\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u597d\uff0c\u5341\u4e24\u5c31\u5341\u4e24\u3002\u201d \u3000\u3000\u4ed6\u8bdd\u867d\u8bf4\u5f97\u75db\u5feb\uff0c\u5fc3\u91cc\u5374\u5728\u6697\u6697\u53eb\u82e6\u3002 \u3000\u3000\u53ea\u56e0\u4ed6\u8fd9\u4eba\u5b9e\u5728\u662f\u5929\u751f\u7684\u7a77\u547d\uff0c\u888b\u91cc\u5c31\u7b97\u6709\u4e00\u4e07\u4e24\u94f6\u5b50\uff0c\u4e5f\u7edd\u4e0d\u4f1a\u5b58\u5f97\u4f4f\u53c1\u5929\uff0c\u6b64\u523b\u5b9e \u662f\u8fde\u4e00\u4e24\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u90a3\u8001\u5934\u4e0d\u4f4f\u9053\uff1a\u201c\u5341\u4e24\u5c31\u5341\u4e24\uff0c\u4f60\u8fd8\u4e0d\u62ff\u51fa\u6765\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u6211\u2026\u2026\u2026\u6211\u660e\u5929\u4e00\u5b9a\u7ed9\u4f60\u3002\u201d \u3000\u3000\u90a3\u8001\u5934\u5b50\u6012\u9053\uff1a\u201c\u6211\u65e9\u5c31\u77e5\u9053\u4f60\u662f\u4e2a\u7a77\u9aa8\u5934\uff0c\u4f60\u4e0d\u62ff\u51fa\u5341\u4e24\u94f6\u5b50\u6765\uff0c\u4f11\u60f3\u6211\u653e\u4f60\u8d70\u3002\u201d \u3000\u3000\u90a3\u9ed1\u8863\u4eba\u6b64\u523b\u8fd8\u6ca1\u6709\u8d70\uff0c\u8fd8\u7ad9\u5728\u90a3\u8fb9\u7b11\u563b\u563b\u7684\u77a7\uff0c\u4f46\u80e1\u94c1\u82b1\u5374\u8fd8\u662f\u4e0d\u514d\u6025\uff0c\u4e5f\u6012\u9053\uff1a \u201c\u6211\u8bf4\u660e\u5929\u7ed9\u4f60\u5c31\u660e\u5929\u7ed9\u4f60\uff0c\u5feb\u653e\u624b\u3002\u201d \u3000\u3000\u4ed6\u7ffb\u8eab\u5c31\u60f3\u5c06\u8fd9\u8001\u5934\u5b50\u7529\u8131\uff0c\u8c01\u77e5\u8fd9\u8001\u5934\u5b50\u529b\u6c14\u7adf\u5927\u5f97\u9a87\u4eba\uff0c\u63e1\u4f4f\u4ed6\u7684\u624b\uff0c\u7adf\u50cf\u662f\u9053\u94c1 \u7b8d\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u8fd9\u624d\u5927\u5403\u4e00\u60ca\uff0c\u539f\u6765\u8fd9\u5356\u9984\u9968\u9762\u7684\u8001\u5934\u5b50\u7adf\u4e5f\u662f\u4f4d\u9ad8\u624b\uff0c\u82e5\u60c5\u5f62\u7adf\u597d\u50cf\u662f\u548c\u9ed1\u8863 \u4eba\u4e00\u8def\u7684\u3002 \u3000\u3000\u82e5\u5728\u5e73\u65f6\uff0c\u80e1\u94c1\u82b1\u4e5f\u4e0d\u6015\u4ed6\uff0c\u4f46\u6b64\u523b\u4ed6\u975e\u4f46\u53ea\u5269\u4e0b\u4e00\u53ea\u624b\u4e0d\u80fd\u52a8\uff0c\u800c\u4e14\u529f\u529b\u4e5f\u81f3\u5c11\u8981\u6253 \u4e86\u4e2a\u4e03\u6298\u516b\u6263\u3002 \u3000\u3000\u4ed6\u7684\u624b\u88ab\u63e1\uff0c\u7adf\u8fde\u52a8\u90fd\u52a8\u4e0d\u4e86\uff0c\u5355\u53ea\u90a3\u4e00\u4e2a\u9ed1\u8863\u4eba\uff0c\u4ed6\u5df2\u65e0\u6cd5\u5e94\u5fd6\uff0c\u518d\u52a0\u4e0a\u8fd9\u8001\u5934\u5b50\uff0c \u4ed6\u90a3\u91cc\u8fd8\u6709\u751f\u8def\u3002 \u3000\u3000\u53ea\u542c\u8fd9\u8001\u5934\u5b50\u8fd8\u5728\u7a77\u56b7\uff0c\u4e0d\u4f4f\u9053\uff1a\u201c\u4e0d\u62ff\u94f6\u5b50\u6765\uff0c\u4ffa\u8ddf\u4f60\u62da\u4e86\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u4ee5\u4e3a\u6211\u4e0d\u77e5\u9053\u4f60\u2026\u2026\u201d \u3000\u3000\u4ed6\u8bdd\u672b\u8bf4\u5b8c\uff0c\u90a3\u8001\u5934\u5b50\u5ffd\u7136\u63a9\u4f4f\u4ed6\u7684\u53e3\uff0c\u6084\u58f0\u9053\uff1a\u201c\u90a3\u5c0f\u5b50\u8fd8\u5728\u90a3\u8fb9\u7ad9\uff0c\u6211\u52a9\u4f60\u4e00\u81c2\u4e4b \u529b\uff0c\u4ed6\u9003\u4e0d\u4e86\u7684\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u4e00\u6014\uff0c\u90a3\u8001\u5934\u5b50\u53c8\u7834\u53e3\u5927\u9a82\u8d77\u6765\uff0c\u5634\u91cc\u867d\u5728\u9a82\uff0c\u773c\u775b\u5374\u5728\u5411\u80e1\u94c1\u82b1\u6253\u773c\u8272\uff0c\u53eb\u4ed6 \u51c6\u5907\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u5c31\u52bf\u4e00\u7ffb\u8eab\u5b50\uff0c\u8fd9\u8001\u5934\u5b50\u7684\u53cc\u624b\u5df2\u6258\u4ed6\u9001\u4e86\u51fa\u53bb\uff0c\u80e1\u94c1\u82b1\u5c31\u501f\u8fd9\u4e00\u6258\u4e4b\u529b\uff0c\u8dc3\u51fa \u4e86\u516d\u4e03\u4e08\u3002 \u3000\u3000\u90a3\u9ed1\u8863\u4eba\u5927\u5403\u4e00\u60ca\uff0c\u5931\u58f0\u9053\uff1a\u201c\u4f60\u2026\u2026\u201d \u3000\u3000\u4e00\u4e2a\u5b57\u521a\u8bf4\u51fa\uff0c\u80e1\u94c1\u82b1\u5df2\u51c9\u5230\u4ed6\u9762\u524d\u4e00\u4e08\u5916\uff0c\u624b\u91cc\u62ff\u201c\u66b4\u96e8\u68a8\u82b1\u9489\u201d\u7684\u5f29\u5323\uff0c\u5389\u58f0\u9053\uff1a \u201c\u6211\u624b\u91cc\u62ff\u7684\u662f\u4ec0\u9ebd\uff0c\u4f60\u603b\u8be5\u77e5\u9053\uff0c\u4f60\u5168\u8eab\u4e0a\u4e0b\u53ea\u8981\u6709\u4e00\u4e2a\u5730\u65b9\u52a8\u4e0a\u4e00\u52a8\uff0c\u6211\u5c31\u5c06\u4f60\u5c04\u51fa\u4e8c \u5341\u4e03\u4e2a\u900f\u660e\u7a9f\u7abf\u6765\u3002\u201d \u3000\u3000\u90a3\u9ed1\u8863\u4eba\u957f\u957f\u5438\u8fdb\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u8981\u600e\u6837\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u4f60\u548c\u695a\u7559\u9999\u7a76\u7adf\u6709\u4ec0\u9ebd\u4ec7\u6068\uff0c\u8981\u5982\u6b64\u6697\u7b97\u65bc\uff1f\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u6211\u548c\u4ed6\u6ca1\u6709\u4ec0\u9ebd\u4ec7\u6068\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u6012\u9053\uff1a\u201c\u4f60\u96be\u9053\u662f\u53d7\u4eba\u4e3b\u4f7f\u800c\u6765\u7684\u9ebd\uff1f\u201d \u3000\u3000\u662f \u3000\u3000\u9ed1\u8863\u4eba\u6447\u4e86\u6447\u5934\uff0c\u9053\uff1a\u201c\u4e0d\u662f\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u773c\u73e0\u5b50\u4e00\u8f6c\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u4f60\u5148\u63ed\u4e0b\u8138\u4e0a\u7684\u9ed1\u5e03\u6765\uff0c\u8ba9\u6211\u770b\u770b\u4f60\u7a76\u7adf\u662f \u4ec0\u9ebd\u53d8\u7684\uff1f\u201d \u3000\u3000\u90a3\u9ed1\u8863\u4eba\u8eab\u5b50\u4e00\u9707\uff0c\u4f3c\u4e4e\u88ab\u5413\u5f97\u6014\u4f4f\u4e86\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u5927\u7b11\u9053\uff1a\u201c\u6211\u65e9\u5c31\u77e5\u9053\u6211\u5fc5\u5b9a\u662f\u8ba4\u5f97\u4f60\u7684\uff0c\u6240\u4ee5\u4f60\u624d\u85cf\u5934\u9732\u5c3e\uff0c\u4e0d\u6562\u89c1\u4eba\uff0c\u73b0\u5728 \u4f60\u65e2\u5df2\u843d\u5728\u6211\u624b\u4e0a\uff0c\u8fd8\u60f3\u518d\u7792\u5f97\u4e0b\u53bb\u9ebd\uff1f\u201d \u3000\u3000\u4ed6\u987f\u4f4f\u7b11\u58f0\uff0c\u5927\u559d\u9053\uff1a\u201c\u4f60\u82e5\u8fd8\u4e0d\u80af\u6380\u8d77\u4f60\u8138\u4e0a\u7684\u9ed1\u5c71\uff0c\u6211\u5c31\u5148\u5c04\u65ad\u4f60\u7684\u4e24\u6761\u817f\uff0c\u4f60\u8fdf \u65e9\u8fd8\u2026\u2026\u201d \u3000\u3000\u4ed6\u8bdd\u672b\u8bf4\u5b8c\uff0c\u90a3\u9ed1\u8863\u4eba\u7adf\u4e5f\u5ffd\u7136\u4ef0\u9762\u5927\u7b11\u8d77\u6765\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u6012\u9053\uff1a\u201c\u4f60\u7b11\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u6211\u53ea\u662f\u7b11\u6211\u81ea\u5df1\uff0c\u4e3a\u4f55\u8981\u559c\u6b22\u591a\u4e8b\uff0c\u53c1\u756a\u4e24\u6b21\u7684\u6551\u4e86\u4f60\u6027\u547d\uff0c\u53cd\u88ab\u4f60\u6069\u5c06 \u4ec7\u62a5\uff0c\u4ee5\u5982\u6b64\u6b79\u6bd2\u7684\u6697\u5668\u6765\u5bf9\u4ed8\u6211\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u6014\u4e86\u6014\uff0c\u9053\uff1a\u201c\u4f60\u6551\u8fc7\u6211\u7684\u547d\uff1f\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u4f60\u88ab\u77f3\u89c2\u97f3\u56f0\u65f6\uff0c\u662f\u8c01\u4e3a\u4f60\u6740\u4e86\u77f3\u89c2\u97f3\u7684\u95e8\u4e0b\uff1f\u4f60\u559d\u4e86\u77f3\u89c2\u97f3\u7684\u6bd2\u9152\u65f6\uff0c \u662f\u8c01\u7ed9\u7684\u89e3\u836f\uff1f\u4f60\u96be\u9053\u5df2\u5fd8\u4e86\u9ebd\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u4e0d\u7b49\u4ed6\u8bdd\u8bf4\u5b8c\uff0c\u5df2\u5403\u60ca\u5f97\u53eb\u4e86\u8d77\u6765\uff0c\u5931\u58f0\u9053\uff1a\u201c\u753b\u7709\u9e1f\uff0c\u4f60\u5c31\u662f\u753b\u7709\u9e1f\uff1f\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u54fc\uff01\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u65e2\u7136\u6570\u6b21\u6551\u6211\uff1f\u73b0\u5728\u4e3a\u4f55\u53c8\u60f3\u6765\u8981\u6211\u7684\u547d\uff1f\u201d \u3000\u3000\u9ed1\u8863\u4eba\u51b7\u51b7\u9053\uff1a\u201c\u6211\u82e5\u60f3\u8981\u4f60\u7684\u547d\uff0c\u4f60\u8fd8\u80fd\u6d3b\u5230\u73b0\u5728\u9ebd\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u53c8\u6014\u4e86\u534a\u664c\uff0c\u9053\uff1a\u201c\u4f46\u4f60\u2026\u2026\u4f60\u4e3a\u4ec0\u9ebd\u2026\u2026\u201d \u3000\u3000\u9ed1\u8863\u4eba\u5389\u58f0\u9053\uff1a\u201c\u4f60\u4e0d\u5fc5\u518d\u95ee\uff0c\u6211\u73b0\u5c31\u8981\u8d70\u4e86\uff0c\u4f60\u82e5\u5fd8\u6069\u8d1f\u4e49\uff0c\u8981\u6069\u5c06\u4ec7\u62a5\uff0c\u53ea\u7ba1\u5c06 \u90a3\u201d\u66b4\u96e8\u68a8\u82b1\u9489\u201d\u5c04\u51fa\u6765\u5427\uff01\u201d \u3000\u3000\u4ed6\u5634\u91cc\u8bf4\u8bdd\uff0c\u5df2\u8f6c\u8eab\u800c\u884c\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u5927\u547c\u9053\uff1a\u201c\u6162\u8d70\uff0c\u7b49\u4e00\u7b49\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u5934\u4e5f\u4e0d\u56de\uff0c\u8f6c\u773c\u95f4\u4fbf\u8d70\u5f97\u8e2a\u5f71\u4e0d\u89c1\uff0c\u80e1\u94c1\u82b1\u773c\u7741\u7741\u77a7\u4ed6\u626c\u957f\u800c\u53bb\uff0c\u8fde\u4e00\u70b9\u6cd5\u5b50\u4e5f \u6ca1\u6709\u3002 \u3000\u3000\u53ea\u56e0\u4ed6\u5b9e\u5728\u4e0d\u662f\u4e2a\u5fd8\u6069\u8d1f\u4e49\u7684\u4eba\uff0c\u65e0\u8bba\u8fd9\u201c\u753b\u7709\u9e1f\u201d\u7684\u884c\u4e8b\u591a\u9ebd\u8be1\u79d8\u96be\u6d4b\uff0c\u603b\u7b97\u66fe\u7ecf\u6551 \u8fc7\u4ed6\u7684\u6027\u547d\u3002 \u3000\u3000\u53ea\u542c\u8eab\u5f8c\u6709\u4eba\u4e7e\u54b3\u4e00\u58f0\uff0c\u7b11\u9053\uff1a\u201c\u5173\u592b\u5b50\u534e\u5bb9\u9053\u4e0a\uff0c\u4e5f\u66fe\u653e\u8fc7\u66f9\u5b5f\u5fb7\u4e00\u9a6c\uff0c\u80e1\u5927\u4fa0\u4eca\u65e5 \u6b64\u4e3e\uff0c\u5df2\u8db3\u53ef\u548c\u6614\u65e5\u7684\u5173\u592b\u5b50\u524d\u5f8c\u8f89\u6620\u4e86\u3002\u201d \u3000\u3000\u90a3\u8001\u5934\u4e88\u539f\u6765\u4e5f\u4e00\u76f4\u7559\u5728\u90a3\u91cc\u6ca1\u6709\u8d70\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u8f6c\u8eab\u4e00\u63d6\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u5728\u4e0b\u4e0e\u8001\u4e08\u7d20\u6627\u5e73\u751f\uff0c\u591a\u627f\u8001\u4e08\u4ed7\u4e49\u76f8\u52a9\uff0c\u611f\u6fc0\u4e0d\u5c3d\u3002\u201d \u3000\u3000\u90a3\u8001\u5934\u5b50\u7b11\u9053\uff1a\u201c\u80e1\u5927\u4fa0\u867d\u4e0d\u8ba4\u5f97\u8001\u673d\uff0c\u8001\u673d\u5374\u5df2\u4e45\u95fb\u80e1\u5927\u4fa0\u7684\u5927\u540d\u4e86\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u60ed\u6127\uff0c\u6562\u8bf7\u6559\u8001\u4e08\u5927\u540d\uff1f\u201d \u3000\u3000\u90a3\u8001\u5934\u4e8e\u9053\uff1a\u201c\u8001\u673d\u6234\u72ec\u884c\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u5931\u58f0\u9053\uff1a\u201c\u539f\u6765\u662f\u4e10\u5e2e\u7684\u524d\u8f88\u5148\u4eba\u201d\u4e07\u91cc\u72ec\u884c\u201d\u6234\u8001\u7237\u5b50\uff0c\u96be\u602a\u65b9\u8f7b\u8f7b\u4e00\u6258\uff0c\u5728 \u4e0b\u5c31\u89c9\u5f97\u6709\u5982\u817e\u4e91\u9a7e\u96fe\u4e00\u822c\uff0c\u5728\u4e0b\u5f53\u771f\u5931\u656c\u5f97\u5f88\u3002\u201d \u3000\u3000\u6234\u72ec\u884c\u9053\uff1a\u201c\u4e0d\u6562\u4e0d\u6562\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u5fcd\u4e0d\u4f4f\u9053\uff1a\u201c\u4f46\u524d\u8f88\u53c8\u600e\u4f1a\u2026\u2026\u600e\u4f1a\u2026\u2026\u201d \u3000\u3000\u6234\u70db\u884c\u9053\uff1a\u201c\u4f60\u662f\u60f3\u95ee\u6211\uff0c\u8981\u996d\u7684\u600e\u4f1a\u6539\u884c\u5356\u8d77\u9984\u9968\u9762\u6765\u4e86\uff0c\u662f\u9ebd\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u4e5f\u7b11\u4e86\uff0c\u9053\uff1a\u201c\u5728\u4e0b\u5b9e\u5728\u6709\u4e9b\u5947\u602a\u3002\u201d \u3000\u3000\u6234\u72ec\u884c\u53f9\u9053\uff1a\u201c\u672c\u5e2e\u5f1f\u5b50\u9e51\u8863\u7ed3\u53d1\uff0c\u672c\u4e3a\u7684\u9690\u8ff9\u7ea2\u5c18\uff0c\u505a\u4e8b\u4e5f\u8f83\u65b9\u4fbf\u4e9b\uff0c\u8c01\u77e5\u8fd1\u5e74\u6765\u60c5 \u52bf\u7adf\u53d8\u4e86\uff0c\u6c5f\u6e56\u4e2d\u4eba\u89c1\u5230\u8981\u996d\u7684\uff0c\u53cd\u800c\u89c9\u5f97\u4efd\u5916\u624e\u773c\uff0c\u662f\u4ee5\u73b0\u5728\u4ee5\u8981\u996d\u7684\u59ff\u6001\u884c\u8d70\u6c5f\u6e56\uff0c\u975e \u4f46\u5f97\u4e0d\u5230\u65b9\u4fbf\uff0c\u53cd\u800c\u4f1a\u610f\u9ebb\u70e6\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4eba\u95fb\u524d\u8f88\u5ac9\u6076\u5982\u4ec7\uff0c\u6700\u559c\u6b22\u6253\u62b1\u4e0d\u5e73\uff0c\u662f\u4ee5\u5e38\u5e74\u6e38\u8e2a\u4e0d\u5b9a\uff0c\u751a\u81f3\u8fdc \u53bb\u7a77\u8352\uff0c\u5c31\u4e3a\u7684\u662f\u8981\u770b\u4e00\u770b\u4eba\u95f4\u6709\u4ec0\u9ebd\u4e0d\u5e73\u4e4b\u4e8b\uff0c\u5047\u5982\u6709\u4eba\u80fd\u770b\u5f97\u51fa\u524d\u8f88\u7684\u8eab\u4efd\uff0c\u524d\u8f88\u6015\u5c31 \u8fde\u4e00\u4ef6\u4e0d\u5e73\u4e4b\u4e8b\u4e5f\u770b\u4e0d\u5230\u4e86\u3002\u201d \u3000\u3000\u4ed6\u7b11\u63a5\u9053\uff1a\u201c\u56e0\u4e3a\u6709\u80c6\u5b50\u6562\u5728\u201d\u4e07\u91cc\u72ec\u884c\u201d\u773c\u524d\u505a\u574f\u4e8b\u7684\u4eba\uff0c\u5929\u4e0b\u8fd8\u6ca1\u6709\u51e0\u4e2a\uff0c\u65b9\u90a3\u753b \u7709\u9e1f\u82e5\u77e5\u9053\u5356\u9984\u9968\u9762\u7684\u5c31\u662f\u201c\u4e07\u91cc\u70db\u884c\u201d\uff0c\u6015\u4e5f\u65e9\u5df2\u6e9c\u4e4b\u5927\u5409\u3002\u201d \u3000\u3000\u6234\u72ec\u884c\u5fae\u5fae\u4e00\u7b11\uff0c\u53c8\u53f9\u606f\u9053\uff1a\u201c\u8001\u673d\u8fdc\u6e38\u5357\u8352\u5f52\u6765\uff0c\u4fbf\u542c\u5f97\u672c\u5e2e\u6240\u53d1\u751f\u7684\u4e0d\u5e78\u4e4b\u4e8b\uff0c\u82e5 \u975e\u695a\u9999\u5e05\u4ed7\u4e49\u63f4\u624b\uff0c\u672c\u5e2e\u6570\u5341\u5e74\u7684\u58f0\u540d\u4fbf\u96be\u514d\u8981\u6bc1\u5728\u90a3\u53db\u5f92\u624b\u4e2d\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u7b11\u9053\uff1a\u201c\u695a\u7559\u9999\u4e5f\u6b63\u548c\u524d\u8f88\u4e00\u6837\uff0c\u662f\u5929\u751f\u597d\u7ba1\u95f2\u4e8b\u7684\u813e\u6c14\u3002\u201d \u3000\u3000\u6234\u72ec\u884c\u542b\u7b11\u9053\uff1a\u201c\u8001\u673d\u4e5f\u65e9\u5df2\u4e45\u95fb\u80e1\u5927\u4fa0\u4e0e\u695a\u9999\u5e05\u662f\u8fc7\u547d\u7684\u4ea4\u60c5\uff0c\u662f\u4ee5\u65b9\u542c\u90a3\u753b\u7709\u9e1f\u8bf4 \u51fa\u201d\u82b1\u8774\u8776\u201d\u53c1\u5b57\uff0c\u8fd9\u95f2\u4e8b\u66f4\u662f\u975e\u7ba1\u4e0d\u53ef\u7684\u4e86\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u76ee\u5149\u95ea\u52a8\uff0c\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u524d\u8f88\u4e45\u8d70\u6c5f\u6e56\uff0c\u53ef\u66fe\u542c\u8bf4\u8fc7\u753b\u7709\u9e1f\u7684\u6765\u5386\u9ebd\uff1f\u201d \u3000\u3000\u6234\u72ec\u884c\u9053\uff1a\u201c\u8fd9\u4e5f\u6b63\u662f\u8001\u673d\u89c9\u5f97\u5947\u602a\u4e4b\u5904\uff0c\u770b\u90a3\u753b\u7709\u9e1f\u7684\u8f7b\u529f\uff0c\u867d\u4e0d\u80fd\u4e0e\u695a\u9999\u5e05\u76f8\u63d0\u5e76 \u8bba\uff0c\u4f46\u5728\u6c5f\u6e56\u4e2d\uff0c\u5df2\u53ef\u8bf4\u662f\u4e00\u7b49\u4e00\u7684\u8eab\u624b\uff0c\u6728\u5e94\u5728\u6b66\u6797\u4e2d\u4eab\u6709\u5927\u540d\uff0c\u4f46\u201d\u753b\u7709\u9e1f\u201d\u8fd9\u540d\u5b57\uff0c \u8001\u673d\u504f\u504f\u53c8\u4ece\u672a\u542c\u8bf4\u8fc7\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u76b1\u8d77\u4e86\u7709\uff0c\u9053\uff1a\u201c\u8fd9\u4eba\u96be\u9053\u53ea\u662f\u4e2a\u521d\u51fa\u9053\u7684\u4eba\u7269\uff1f\u4f46\u770b\u4ed6\u884c\u4e8b\u4e4b\u8001\u8fa3\u5468\u5230\uff0c\u5374\u53c8 \u7edd\u4e0d\u50cf\u662f\u4e2a\u96cf\u513f\u5440\uff01\u201d \u3000\u3000\u6234\u72ec\u884c\u9053\uff1a\u201c\u4f9d\u8001\u673d\u770b\u6765\uff0c\u6b64\u4eba\u6015\u662f\u4e2a\u4e45\u5df2\u6210\u540d\u7684\u6c5f\u6e56\u8001\u624b\u6539\u626e\u7684\u3002\u53ea\u4e0d\u8fc7\u662f\u4ed6\u7684\u5316 \u540d\uff0c\u800c\u4e14\u6b64\u4eba\u8bf4\u4e0d\u5b9a\u8fd8\u662f\u80e1\u5927\u4fa0\u7684\u76f8\u8bc6\uff0c\u662f\u4ee5\u624d\u4e0d\u613f\u88ab\u80e1\u5927\u4fa0\u770b\u5230\u4ed6\u7684\u6728\u6765\u9762\u76ee\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u6211\u4e5f\u65e9\u5df2\u60f3\u5230\u8fd9\u4e00\u70b9\u4e86\uff0c\u6240\u4ee5\u624d\u903c\u4ed6\u5c06\u8499\u9762\u7684\u9ed1\u5dfe\u6380\u8d77\u6765\uff0c\u4f46\u6211\u5374\u53c8\u5b9e\u5728 \u60f3\u4e0d\u51fa\u6211\u7684\u670b\u53cb\u4e2d\u6709\u8fd9\u9ebd\u4e00\u4e2a\u4eba\u3002\u201d \u3000\u3000\u6234\u72ec\u884c\u9053\uff1a\u201c\u8fd8\u6709\u4e00\u70b9\uff0c\u8001\u673d\u4e5f\u89c9\u5f97\u5f88\u5947\u602a\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u5662\uff01\u201d \u3000\u3000\u6234\u72ec\u884c\u9053\uff1a\u201c\u6b64\u4eba\u65e2\u65e0\u5bb3\u80e1\u5927\u4fa0\u4e4b\u610f\uff0c\u4e3a\u4f55\u8981\u5f15\u80e1\u5927\u4fa0\u6765\u8ffd\u4ed6\u5462\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u6014\u4e86\u6014\uff0c\u5ffd\u7136\u89c9\u5f97\u5168\u8eab\u90fd\u51c9\u4e86\uff0c\u5931\u58f0\u9053\uff1a\u201c\u4e0d\u597d\uff0c\u8fd9\u6015\u662f\u4ed6\u7684\u8c03\u864e\u79bb\u5c71\u4e4b\u8ba1\u3002\u201d \u3000\u3000\u6234\u70db\u884c\u52a8\u5bb9\u9053\uff1a\u201c\u4ec0\u9ebd\u8c03\u864e\u79bb\u5c71\u4e4b\u8ba1\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u5df2\u6765\u4e0d\u53ca\u56de\u7b54\u4ed6\u8fd9\u53e5\u8bdd\uff0c\u8fde\u62db\u547c\u90fd\u672b\u6253\uff0c\u5c31\u98de\u4e5f\u4f3c\u7684\u8d70\u4e86\uff0c\u53ea\u56e0\u4ed6\u5df2\u60f3\u5230\u695a\u7559\u9999 \u6b64\u523b\u5904\u5883\u4e4b\u5371\u9669\u3002 \u3000\u3000\u53ea\u4e0d\u8fc7\uff0c\u4ed6\u73b0\u5728\u624d\u60f3\u5230\uff0c\u5df2\u7ecf\u592a\u8fdf\u4e86\u3002 \u3000\u3000\u7a97\u5b50\u6ca1\u6709\u5173\uff0c\u732b\u5df2\u6b7b\u4e86\uff0c\u4e00\u9635\u5bd2\u98ce\u5377\u5165\u4e86\u7a97\u6237\uff0c\u5377\u8d77\u4e86\u684c\u4e0a\u7684\u7eb8\u6761\uff0c\u5439\u7184\u4e86\u706f\u3002 \u3000\u3000\u8fd9\u5c4b\u5b50\u6709\u706f\u5149\u65f6\u5df2\u662f\u90a3\u9ebd\u9eef\u6de1\u51c4\u51c9\uff0c\u6b64\u523b\u9aa4\u7136\u9ed1\u6697\u4e0b\u6765\uff0c\u8f68\u66f4\u663e\u5f97\u8bf4\u4e0d\u51fa\u7684\u60b2\u60e8\u8427\u7d22\u3002 \u3000\u3000\u90bb\u9662\u9690\u7ea6\u6709\u6b4c\u58f0\u4f20\u6765\uff0c\u5531\u7684\u5f77\u4f5b\u662f\u674e\u5f8c\u4e3b\u7684\u8bcd\u66f2\u3002 \u3000\u3000\u4f5c\u5ba2\u5f02\u4e61\uff0c\u6295\u5bbf\u9006\u65c5\uff0c\u5728\u8fd9\u51b7\u6e05\u6e05\u7ea6\u4e24\u591c\u91cc\uff0c\u559d\u4e00\u676f\u6de1\u6de1\u7684\u7af9\u53f6\u9752\uff0c\u542c\u542c\u62b1\u7435\u7436\u7684\u6b4c\u5993 \u5531\u4e24\u66f2\u52a8\u4eba\u7684\u5c0f\u8c03\uff0c\u672c\u662f\u4eba\u751f\u96be\u5f97\u51e0\u56de\u518d\u7684\u4eab\u53d7\u3002 \u3000\u3000\u53ef\u662f\u5979\u4eec\u4e3a\u4ec0\u9ebd\u504f\u504f\u8981\u5531\u5f8c\u4e3b\u7684\u8bcd\u5462\uff1f \u3000\u3000\u96be\u9053\u8fd9\u4e9b\u4eba\u524d\u5f3a\u7b11\uff0c\u6614\u4eba\u5f39\u6cea\u7684\u5973\u5b69\u4e8e\uff0c\u8981\u5c06\u5fc3\u91cc\u7684\u54c0\u6028\uff0c\u501f\u8fd9\u4ea1\u56fd\u4e4b\u4e3b\u7684\u51c4\u5a49\u4e4b\u8bcd\u5531 \u51fa\u6765\u9ebd\uff1f \u3000\u3000\u695a\u7559\u9999\u5c31\u548c\u684c\u4e0a\u7684\u6b7b\u732b\u4e00\u6837\uff0c\u8eba\u5728\u5e8a\u4e0a\u52a8\u4e5f\u4e0d\u52a8\uff0c\u4ed6\u6b64\u523b\u7684\u906d\u9047\uff0c\u662f\u5426\u4e5f\u548c\u90a3\u7edd\u4e16\u624d \u4eba\uff0c\u672b\u8def\u738b\u5b59\u6709\u51e0\u5206\u76f8\u4f3c\u5462\uff1f \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u7a81\u6709\u4e00\u6761\u4eba\u5f71\u63a0\u5230\u7a97\u524d\u3002 \u3000\u3000\u8fd9\u4eba\u4e5f\u7a7f\u4e00\u4ef6\u6781\u7d27\u8eab\u7684\u9ed1\u8863\uff0c\u8138\u4e0a\u4e5f\u6709\u9ed1\u5dfe\u8499\u9762\uff0c\u884c\u52a8\u4e4b\u95f4\uff0c\u5c31\u5982\u732b\u822c\u8f7b\u6377\u65e0\u58f0\u3002 \u3000\u3000\u4ed6\u80cc\u4e0a\u4ee5\u5341\u5b57\u5e26\u624e\u4e2a\u5251\u9798\uff0c\u957f\u5251\u5374\u65e9\u5df2\u62bd\u4e86\u51fa\u6765\uff0c\u9690\u5728\u8098\u5f8c\uff0c\u4e00\u53cd\u624b\uff0c\u5251\u950b\u4fbf\u53ef\u53d6\u4eba\u54bd \u5589\u3002 \u3000\u3000\u4f46\u4ed6\u5e76\u6ca1\u6709\u63a0\u5165\u7a97\u6237\uff0c\u53ea\u662f\u4f0f\u5728\u7a97\u4e0b\uff0c\u9759\u9759\u503e\u542c\u3002 \u3000\u3000\u53ea\u542c\u695a\u7559\u9999\u7684\u547c\u5438\u58f0\u6709\u65f6\u5fae\u5f31\uff0c\u6709\u65f6\u6c89\u91cd\uff0c\u5fae\u5f31\u65f6\u5982\u6e38\u4e1d\u5c06\u65ad\uff0c\u6c89\u91cd\u65f6\u5374\u53c8\u6709\u5982\u725b\u5598\u3002 \u3000\u3000\u8fd9\u9ed1\u8863\u4eba\u542c\u4e86\u534a\u664c\uff0c\u4e00\u53cc\u70af\u70af\u6709\u5149\u7684\u773c\u775b\u91cc\uff0c\u9732\u51fa\u6ee1\u610f\u4e4b\u8272\uff0c\u4ed6\u5df2\u542c\u51fa\u695a\u7559\u9999\u7684\u75db\u52bf\u975e \u4f46\u6ca1\u51cf\u8f7b\uff0c\u53cd\u800c\u66f4\u91cd\u4e86\u3002 \u3000\u3000\u4f46\u4ed6\u8fd8\u662f\u6ca1\u6709\u6025\u63a0\u5165\u7a97\u6237\uff0c\u5148\u5728\u7a97\u5916\u4f38\u81c2\u4f5c\u52bf\uff0c\u201c\u5530\u201d\u7684\u524c\u51fa\u4e00\u5251\uff0c\u957f\u5251\u5288\u7a7a\uff0c\u98ce\u58f0\u523a \u8033\u3002 \u3000\u3000\u82e5\u5728\u5e73\u65f6\uff0c\u695a\u7559\u9999\u5fc5\u5b9a\u65e9\u5df2\u8b66\u89c9\u3002 \u3000\u3000\u4f46\u73b0\u5728\u4ed6\u5374\u8fde\u4e00\u70b9\u53cd\u5e94\u4e5f\u6ca1\u6709\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u8fd9\u624d\u957f\u8eab\u800c\u8d77\uff0c\u4ed6\u8eab\u6750\u770b\u6765\u6bd4\u65b9\u90a3\u9ed1\u8863\u4eba\u201c\u753b\u7709\u9e1f\u201d\u9ad8\u5f97\u591a\uff0c\u4e5f\u58ee\u5f97\u591a\uff0c\u4f46\u8f7b\u529f \u5374\u4f3c\u5dee\u4e86\u4e00\u7b79\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u7279\u522b\u8c28\u614e\uff0c\u4efd\u5916\u5c0f\u5fc3\uff0c\u5e76\u6ca1\u6709\u4e00\u63a0\u800c\u5165\uff0c\u5374\u7528\u624b\u4e00\u6309\u7a97\u5e18\uff0c\u501f\u8fd9\u4e00\u6309\u4e4b\u529b\u7a9c\u4e86\u8fdb \u53bb\u3002 \u3000\u3000\u5c4b\u5b50\u91cc\u9ed1\u6697\u5f97\u51e0\u4e4e\u4f38\u624b\u4e0d\u89c1\u4e94\u6307\uff0c\u8fd9\u9ed1\u8863\u4eba\u5b9b\u5982\u5df2\u548c\u9ed1\u6697\u6eb6\u4e3a\u4e00\u4f53\uff0c\u5c31\u7b97\u7ad9\u5728\u7a97\u5916\uff0c\u4e5f \u77a7\u4e0d\u89c1\u4ed6\u7684\u8eab\u5f62\u3002 \u3000\u3000\u4ed6\u7ad9\u5728\u9ed1\u6697\u4e2d\u53c8\u9759\u9759\u7b49\u4e86\u534a\u664c\uff0c\u5e8a\u4e0a\u7684\u695a\u7559\u9999\u547c\u5438\u8fd8\u662f\u6781\u4e0d\u89c4\u5219\uff0c\u751a\u81f3\u5df2\u53ef\u8bf4\u662f\u5944\u5944\u4e00 \u606f\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u8fd9\u624d\u4e00\u6b65\u6b65\u5411\u5e8a\u524d\u8d70\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u4ed6\u811a\u6b65\u6781\u8f7b\u3001\u6781\u7a33\uff0c\u53ef\u662f\u5916\u9762\u7684\u8def\u5f88\u6e7f\u4ed6\u978b\u5e95\u4e5f\u96be\u514d\u6cbe\u4e0a\u4e86\u6c34\uff0c\u8d70\u4e86\u4e24\u6b65\uff0c\u5ffd\u7136\u53d1\u51fa \u201c\u5431\u201d\u7684\u4e00\u54cd\u3002 \u3000\u3000\u8fd9\u58f0\u97f3\u867d\u7136\u6781\u8f7b\u5fae\uff0c\u4f46\u5728\u6b64\u65f6\u6b64\u5730\u542c\u8d77\u6765\uff0c\u5374\u5b9e\u5728\u6b64\u751f\u4e86\u9508\u7684\u5200\u5251\u78e8\u64e6\u8fd8\u8981\u523a\u8033\u5f97\u591a\u3002 \u3000\u3000\u695a\u7559\u9999\u4f3c\u4e4e\u88ab\u60ca\u9192\uff0c\u7adf\u5728\u5e8a\u4e0a\u52a8\u4e86\u52a8\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u6574\u4e2a\u4eba\u90fd\u51bb\u7ed3\u4f4f\u4e86\uff0c\u8fde\u547c\u5438\u90fd\u4e0d\u6562\u547c\u5438\u3002 \u3000\u3000\u695a\u7559\u9999\u5374\u53ea\u4e0d\u8fc7\u7ffb\u4e86\u4e2a\u8eab\uff0c\u53cd\u800c\u9762\u671d\u5899\uff0c\u9ed1\u8863\u4eba\u6697\u4e2d\u677e\u4e86\u53e3\u6c14\uff0c\u53c8\u7b49\u4e86\u534a\u664c\uff0c\u5ffd\u7136\u4e00\u4e2a \u7bad\u6b65\u7a9c\u5230\u5e8a\u524d\u3002 \u3000\u3000\u4ed6\u638c\u4e2d\u5251\u5df2\u6bd2\u86c7\u822c\uff0c\u5411\u695a\u7559\u9999\u523a\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u4e00\u9762\u72c2\u5954\uff0c\u4e00\u9762\u4e0d\u505c\u7684\u9a82\u81ea\u5df1\uff0c\u695a\u7559\u9999\u6b64\u756a\u82e5\u7834\u4eba\u6697\u7b97\uff0c\u4ed6\u5c31\u7b97\u80fd\u6d3b\u4e0b\u53bb\uff0c\u4e5f\u6ca1 \u8138\u89c1\u4eba\u4e86\u3002 \u3000\u3000\u4ed6\u53ea\u671b\u80cc\u751f\u53cc\u7fc5\uff0c\u4e00\u4e0b\u5b50\u80fd\u98de\u56de\u53bb\u3002 \u3000\u3000\u53ef\u662f\uff0c\u5ffd\u7136\u95f4\uff0c\u4ed6\u53c8\u505c\u4f4f\u4e86\u811a\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u53d1\u73b0\u81ea\u5df1\u627e\u4e0d\u51fa\u56de\u90a3\u5ba2\u6808\u7684\u8def\u4e86\u3002 \u3000\u3000\u65b9\u90a3\u753b\u7709\u9e1f\u5f15\u4ed6\u4e1c\u6298\u897f\u8f6c\uff0c\u4ed6\u81ea\u5df1\u4e5f\u4e0d\u77e5\u9053\u81ea\u5df1\u8d70\u5230\u4ec0\u9ebd\u5730\u65b9\uff0c\u4e5f\u5b8c\u5168\u8fa8\u4e0d\u51fa\u65b9\u5411\u3002 \u3000\u3000\u5728\u8fd9\u9ed1\u6f06\u6f06\u7ea6\u4e24\u591c\u91cc\uff0c\u5728\u8fd9\u964c\u751f\u7684\u57ce\u5e02\u4e2d\uff0c\u6bcf\u6761\u8857\u770b\u6765\u90fd\u5dee\u4e0d\u591a\uff0c\u90a3\u95f4\u5c4b\u5b50\u770b\u6765\u90fd\u51e0\u4e4e \u5b8c\u5168\u4e00\u6837\u3002 \u3000\u3000\u4ed6\u60f3\u62cd\u5f00\u4e00\u5bb6\u4eba\u7684\u95e8\uff0c\u95ee\u95ee\u8def\uff0c\u4f46\u5ffd\u53c8\u53d1\u73b0\u81ea\u5df1\u7adf\u8fde\u90a3\u5ba2\u6808\u7684\u540d\u5b57\u90fd\u5df2\u5fd8\u8bb0\uff0c\u8981\u95ee\u8def\u90fd \u65e0\u4ece\u95ee\u8d77\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u7b80\u76f4\u5feb\u6025\u75af\u4e86\uff0c\u6728\u7acb\u5728\u96e8\u4e2d\uff0c\u5168\u8eab\u90fd\u5df2\u6e7f\u900f\uff0c\u8138\u4e0a\u4e5f\u5728\u6dcc\u6c34\uff0c\u5df2\u5206\u4e0d\u51fa\u662f\u96e8\uff1f\u662f \u6c57\uff0c\u8fd8\u662f\u6025\u51fa\u6765\u7684\u773c\u6cea\uff1f \u3000\u3000\u9ed1\u8863\u4eba\u4e00\u5251\u5df2\u523a\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u8fd9\u4e00\u5251\u5982\u86c7\u874e\uff0c\u5feb\u5982\u95ea\u7535\uff0c\u800c\u4e14\u76f4\u53d6\u695a\u7559\u9999\u7684\u8981\u5bb3\uff0c\u663e\u89c1\u5f97\u6b64\u4eba\u5b9e\u5728\u662f\u6740\u4eba\u7684\u8001\u624b\u3002 \u3000\u3000\u53ea\u542c\u201c\u5657\u201d\u7684\u4e00\u58f0\uff0c\u96ea\u4eae\u7684\u5251\u950b\u5df2\u76f4\u523a\u800c\u5165\u2014\u2014\u4f46\u5374\u5e76\u4e0d\u662f\u5229\u5165\u695a\u7559\u9999\u7684\u8eab\u5b50\uff0c\u800c\u662f\u5229 \u5165\u4e00\u4e2a\u6795\u5934\u4e2d\u3002 \u3000\u3000\u539f\u6765\u5c31\u5728\u65b9\u90a3\u95f4\u4e0d\u5bb9\u53d1\u7684\u5239\u90a3\u95f4\uff0c\u5944\u5944\u4e00\u606f\u7684\u695a\u7559\u9999\u5ffd\u7136\u4e00\u4e2a\u7ffb\u8eab\uff0c\u4ee5\u6795\u5934\u8fce\u4e0a\u4e86\u957f \u5251\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u5927\u60ca\uff0c\u62d4\u5251\uff0c\u62d4\u4e0d\u51fa\uff0c\u8f68\u60f3\u9003\u3002 \u3000\u3000\u4ed6\u5e94\u53d8\u5df2\u4e0d\u80fd\u7b97\u4e0d\u5feb\uff0c\u600e\u5948\u695a\u7559\u9999\u5374\u6bd4\u4ed6\u66f4\u5feb\uff0c\u4ed6\u8fd8\u6ca1\u6709\u6765\u5f97\u53ca\u6492\u624b\uff0c\u695a\u7559\u9999\u5df2\u6263\u4f4f\u4e86 \u4ed6\u7684\u624b\u8155\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u5de6\u624b\u7acb\u638c\u5982\u5200\uff0c\u53cd\u5411\u695a\u7559\u9999\u8155\u5b50\u4e0a\u65a9\u4e0b\u3002 \u3000\u3000\u8c01\u77e5\u695a\u7559\u9999\u5ffd\u7136\u5c06\u4ed6\u7684\u53f3\u624b\u5f80\u524d\u4e00\u62c9\uff0c\u4ed6\u8fd9\u4e00\u638c\u5c31\u65a9\u5728\u81ea\u5df1\u7684\u624b\u81c2\u4e0a\uff0c\u75bc\u5f97\u5fcd\u4e0d\u4f4f\u54fc\u51fa \u58f0\u6765\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u695a\u7559\u9999\u7684\u5de6\u638c\u5df2\u5230\u4e86\u4ed6\u80c1\u4e0b\uff0c\u8f7b\u8f7b\u4e00\u5207\uff0c\u4ed6\u534a\u8fb9\u8eab\u5b50\u7acb\u523b\u90fd\u53d1\u4e86\u9ebb\uff0c\u8fde\u52a8\u90fd\u4e0d\u80fd \u52a8\u4e86\u3002 \u3000\u3000\u9ed1\u6697\u4e2d\uff0c\u53ea\u89c1\u695a\u7559\u9999\u7684\u4e00\u53cc\u7738\u5b50\u6bd4\u660e\u661f\u66f4\u4eae\uff0c\u90a3\u91cc\u6709\u4e1d\u6beb\u75c5\u5bb9\uff0c\u9ed1\u8863\u4eba\u8eab\u5b50\u53d1\u6296\uff0c\u55c4\u58f0 \u9053\uff1a\u201c\u4f60\u2026\u2026\u201d \u3000\u3000\u4ed6\u53ea\u8bf4\u4e86\u4e00\u4e2a\u5b57\uff0c\u4e0b\u9762\u7684\u8bdd\u5c31\u518d\u4e5f\u8bf4\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u695a\u7559\u9999\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5728\u4e0b\u65e9\u5df2\u7b97\u51c6\u9601\u4e0b\u5fc5\u5b9a\u8981\u6765\u7684\uff0c\u65e9\u5df2\u5728\u6b64\u606d\u5019\u591a\u65f6\u4e86\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u6ee1\u5934\u6c57\u51fa\u5982\u96e8\uff0c\u98a4\u58f0\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u6ca1\u6709\u75c5\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u9053\uff1a\u201c\u6211\u8eab\u5b50\u867d\u6ca1\u6709\u75c5\uff0c\u5374\u6709\u4e2a\u5fc3\u75c5\uff0c\u82e5\u4e0d\u5f04\u6e05\u695a\u9601\u4e0b\u7684\u6765\u5386\u548c\u6765\u610f\uff0c\u6211\u8fd9\u5fc3 \u75c5\u662f\u518d\u4e5f\u4e0d\u4f1a\u6cbb\u597d\u7684\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u695a\u9999\u5e05\u679c\u7136\u540d\u4e0d\u865a\u4f20\uff0c\u7684\u786e\u6709\u4e24\u4e0b\u5b50\uff0c\u4eca\u5929\u6211\u5df2\u8ba4\u683d\u4e86\uff0c \u4f60\u8981\u600e\u9ebd\u6837\uff0c\u6211\u65e0\u4e0d\u4ece\u547d\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u4e00\u7b11\uff0c\u53c8\u9053\uff1a\u201c\u6211\u77e5\u9053\u695a\u9999\u7684\u624b\u4e0b\u662f\u4ece\u4e0d\u4f24\u4eba\u7684\uff0c\u662f\u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f46\u4f60\u82e5\u4e0d\u8bf4\u51fa\u4f60\u7684\u8eab\u4efd\u6765\u5386\uff1f\u4e3a\u4f55\u53c1\u756a\u51e0\u6b21\u7684\u6765\u6697\u7b97\u65bc\u6211\uff0c\u6211\u7eb5\u4e0d \u4f24\u4f60\u6027\u547d\uff0c\u6015\u4e5f\u8981\u5f97\u7f6a\u4e86\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u6211\u548c\u4f60\u672c\u65e0\u51a4\u4ec7\uff0c\u66f4\u6ca1\u6709\u51e0\u6b21\u8981\u6765\u6740\u4f60\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4f60\u96be\u9053\u8fd8\u662f\u7b2c\u4e00\u6b21\u5939\u6740\u6211\u9ebd\uff1f\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u81ea\u7136\u662f\u7b2c\u4e8c\u6b21\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u76ee\u5149\u95ea\u52a8\uff0c\u5ffd\u53c8\u95ee\u9053\uff1a\u201c\u4f60\u96be\u9053\u53ea\u4e0d\u8fc7\u662f\u53d7\u4eba\u4e3b\u4f7f\u800c\u6765\u7684\uff1f\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u53ea\u662f\u2026\u2026\u201d \u3000\u3000\u4ed6\u7684\u8bdd\u8fd8\u6ca1\u6709\u8bf4\u51fa\u53e3\u6765\uff0c\u7a81\u542c\u201c\u5636\u201d\u7684\u4e00\u58f0\uff0c\u9ed1\u6697\u4e2d\u4f3c\u4e4e\u6709\u6781\u7ec6\u7684\u5149\u8292\u95ea\u4e86\u95ea\uff0c\u53c8\u6d88\u5931 \u4e0d\u89c1\u3002 \u3000\u3000\u695a\u7559\u9999\u53ea\u89c9\u8fd9\u9ed1\u8863\u4eba\u7684\u624b\u8155\u5ffd\u7136\u4e00\u9635\u75c9\u631b\uff0c\u8eab\u5b50\u5ffd\u7136\u4e00\u9635\u98a4\u6296\uff0c\u76ee\u4e2d\u5ffd\u7136\u73b0\u51fa\u4e86\u60ca\u60e7\u6b32 \u7edd\u4e4b\u8272\uff0c\u55c4\u58f0\u9053\uff1a\u201c\u662f\u2026\u2026\u662f\u2026\u2026\u662f\u2026\u2026\u201d \u3000\u3000\u695a\u7559\u9999\u53d8\u8272\u9053\uff1a\u201c\u662f\u8c01\uff1f\u5feb\u8bf4\uff1f\u201d \u7b2c\u4e03\u7ae0\u3000\u804c\u4e1a\u6740\u624b>> \u53e4\u9f99\u300a\u695a\u7559\u9999\u7cfb\u5217\xb7\u753b\u7709\u9e1f\u300b \u7b2c\u4e03\u7ae0\u3000\u804c\u4e1a\u6740\u624b \u3000\u3000\u9ed1\u8863\u4eba\u54bd\u5589\u4e2d\u201c\u5495\u561f\u201d\u4e00\u54cd\uff0c\u4ec0\u9ebd\u58f0\u97f3\u90fd\u518d\u4e5f\u53d1\u4e0d\u51fa\u6765\uff0c\u8fd9\u79d8\u5bc6\u5c31\u53c8\u968f\u4ed6\u6700\u5f8c\u4e00\u53e3\u6c14\u88ab \u54bd\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u8fd9\u65f6\u5916\u9762\u5df2\u4f20\u6765\u4e86\u674e\u7389\u51fd\u7126\u6025\u7684\u547c\u5524\u58f0\uff0c\u9053\uff1a\u201c\u695a\u5144\uff0c\u695a\u5144\uff0c\u4f60\u53ef\u66fe\u53d7\u4f24\u9ebd\uff1f\u201d \u3000\u3000\u547c\u58f0\u4e2d\uff0c\u674e\u7389\u51fd\u548c\u67f3\u65e0\u7709\u5df2\u53cc\u53cc\u63a0\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u67f3\u65e0\u7709\u968f\u624b\u4eae\u8d77\u4e86\u4e2a\u706b\u647a\u5b50\uff0c\u77a7\u89c1\u695a\u7559\u9999\u597d\u751f\u751f\u7684\u5750\u5728\u5e8a\u4e0a\uff0c\u5c31\u957f\u957f\u677e\u4e86\u53e3\u6c14\uff0c\u5c55\u989c\u7b11 \u9053\uff1a\u201c\u8c22\u5929\u8c22\u5730\uff0c\u6211\u4eec\u603b\u7b97\u53ca\u65f6\u8d76\u56de\u6765\u4e86\u3002\u201d \u3000\u3000\u8fd9\u4e24\u4eba\u5168\u8eab\u4e5f\u5df2\u6e7f\u900f\uff0c\u800c\u4e14\u795e\u60c5\u770b\u6765\u5341\u5206\u52b3\u7d2f\uff0c\u663e\u89c1\u8fd9\u4e00\u65e5\u4e00\u591c\u95f4\u8d76\u8def\u5fc5\u5b9a\u5341\u5206\u52b3\u82e6\u3002 \u3000\u3000\u695a\u7559\u9999\u76ef\u4ed6\u4eec\u77a7\u4e86\u534a\u664c\uff0c\u4e5f\u957f\u957f\u53f9\u4e86\u53e3\u6c14\u7f13\u7f13\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4e24\u4f4d\u56de\u6765\u5f97\u7684\u786e\u6070\u662f\u65f6 \u5019\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u71c3\u8d77\u4e86\u706f\uff0c\u77aa\u5730\u4e0a\u90a3\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u6211\u4eec\u8981\u770b\u770b\u8fd9\u4eba\u7a76\u7adf\u662f\u8c01\uff0c\u4e3a\u4f55\u8981\u82e6\u82e6\u6697\u7b97\u695a \u5144\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u73b0\u5728\u6c38\u8fdc\u4e5f\u65e0\u6cd5\u77e5\u9053\u4ed6\u662f\u4e3a\u4ec0\u9ebd\u6765\u7684\u4e86\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u9053\uff1a\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u51b7\u51b7\u9053\uff1a\u201c\u53ea\u56e0\u6b7b\u4eba\u662f\u7edd\u4e0d\u4f1a\u8bf4\u8bdd\u7684\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u6014\u4e86\u534a\u664c\uff0c\u957f\u53f9\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u7684\u786e\u4e0d\u8be5\u6740\u4e86\u4ed6\u7684\uff0c\u53ef\u662f\u6211\u9aa4\u7136\u89c1\u5230\u4e00\u4e2a\u4eba\u63d0\u5251 \u7ad9\u5728\u695a\u5144\u5e8a\u524d\uff0c\u53c8\u4e0d\u77e5\u695a\u5144\u75c5\u52bf\u5df2\u75ca\u6108\uff0c\u60c5\u6025\u4e4b\u4e0b\uff0c\u7adf\u5fd8\u4e86\u672c\u8be5\u7559\u4e0b\u4ed6\u7684\u6d3b\u53e3\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u76b1\u7709\u53f9\u9053\uff1a\u201c\u6211\u5c31\u77e5\u9053\u4f60\u8fd9\u79cd\u8f7b\u7387\u7684\u813e\u6c14\uff0c\u603b\u6709\u4e00\u5929\u4f1a\u8bef\u4e8b\u7684\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u4e00\u7b11\u9053\uff1a\u201c\u8fd9\u600e\u9ebd\u80fd\u602a\u5ac2\u592b\u4eba\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u5782\u9996\u9053\uff1a\u201c\u8fd9\u5b9e\u5728\u5e94\u8be5\u602a\u6211\uff0c\u4f46\u671b\u9999\u5e05\u4f60\u2026\u2026\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u5ac2\u592b\u4eba\u6551\u4e86\u6211\u6027\u547d\uff0c\u6211\u5fc3\u4e2d\u53ea\u6709\u611f\u6fc0\uff0c\u7edd\u65e0\u4ed6\u610f\uff0c\u5ac2\u592b\u4eba\u82e5\u518d\u8bf4\u8fd9\u6837\u7684 \u8bdd\uff0c\u53cd\u5012\u4ee4\u6211\u65e0\u5730\u81ea\u5bb9\u4e86\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u7ec8\u65bc\u4e5f\u5c55\u989c\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u695a\u5144\u7684\u75db\u7adf\u597d\u5f97\u8fd9\u9ebd\u5feb\uff0c\u53ef\u89c1\u5409\u4eba\u5fc5\u6709\u5929\u76f8\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u9053\uff1a\u201c\u8bf4\u6765\u60ed\u6127\uff0c\u6211\u7cca\u91cc\u7cca\u6d82\u7684\u7761\u4e86\u4e00\u5929\uff0c\u75c5\u5c45\u7136\u5c31\u597d\u4e86\uff0c\u5374\u7d2f\u5f97\u8d24\u592b\u5987\u4e3a\u6211 \u6025\uff0c\u5b9e\u5728\u62b1\u6b49\u5f97\u5f88\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u5ffd\u7136\u6380\u8d77\u4e86\u90a3\u9ed1\u8863\u4eba\u8499\u9762\u7684\u9ed1\u5dfe\uff0c\u6068\u6068\u9053\uff1a\u201c\u695a\u5144\u4f60\u8ba4\u5f97\u8fd9\u4eba\u662f\u8c01\u9ebd\uff1f\u201d \u3000\u3000\u706f\u5149\u4e0b\uff0c\u53ea\u89c1\u8fd9\u4eba\u9752\u60e8\u60e8\u7684\u4e00\u5f20\u8138\u4e0a\uff0c\u867d\u7136\u8fd8\u5b58\u6709\u4e34\u6b7b\u524d\u7684\u60ca\u9a87\u4e4b\u8272\uff0c\u4f46\u81ea\u7709\u76ee\u95f4\u72b9\u53ef \u770b\u51fa\u4ed6\u751f\u524d\u7684\u9aa0\u608d\u548c\u6b8b\u9177\u3002 \u3000\u3000\u695a\u7559\u9999\u53f9\u9053\uff1a\u201c\u6211\u975e\u4f46\u4e0d\u8ba4\u5f97\u6b64\u4eba\u662f\u8c01\uff0c\u800c\u4e14\u8fde\u89c1\u90fd\u672b\u89c1\u8fc7\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u76b1\u7709\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u4ed6\u4e3a\u4f55\u8981\u6765\u6697\u7b97\u695a\u5144\u5462\uff1f\u96be\u9053\u5e55\u5f8c\u8fd8\u6709\u522b\u4eba\u4e3b\u4f7f\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u4e5f\u4e0d\u7b54\u8bdd\uff0c\u5374\u81ea\u5ff1\u5934\u91cc\u62d4\u51fa\u4e86\u90a3\u67c4\u5251\uff0c\u5728\u706f\u4e0b\u51dd\u4f4f\u4e86\u534a\u664c\uff0c\u53c8\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c \u9053\uff1a\u201c\u8fd9\u67c4\u5251\u5f53\u771f\u662f\u6740\u4eba\u7684\u5229\u5668\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u67c4\u5251\u6bd4\u6c5f\u6e56\u4e2d\u901a\u5e38\u6240\u7528\u7684\u5251\uff0c\u81f3\u5c11\u8981\u957f\u53c1\u5bf8\uff0c\u4f46\u5374\u8584\u5f97\u591a\uff0c\u4e5f\u7a84 \u5f97\u591a\uff0c\u51e0\u4e4e\u6bd4\u6d77\u5357\u5251\u6d3e\u7684\u96ea\u86c7\u5251\u8fd8\u8981\u7a84\u4e24\u5206\uff0c\u4f7f\u8fd9\u79cd\u5251\u7684\u4eba\uff0c\u5251\u6cd5\u60f3\u5fc5\u4e5f\u548c\u6d77\u5357\u5251\u6d3e\u4e00\u6837\uff0c \u8d70\u7684\u662f\u8f7b\u6377\u72e0\u6bd2\u90a3\u4e00\u8def\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u5fae\u7b11\u9053\uff1a\u201c\u674e\u5144\u89c1\u89e3\u7cbe\u8f9f\uff0c\u679c\u7136\u4e0d\u6127\u4e3a\u7b2c\u4e00\u5251\u5ba2\u7684\u4f20\u4eba\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u4f3c\u4e4e\u60f3\u8c26\u8c22\u4e24\u65ec\uff0c\u695a\u7559\u9999\u5374\u53c8\u6309\u9053\uff1a\u201c\u4f7f\u5251\u7684\u8fd9\u4eba\uff0c\u6211\u867d\u4e0d\u8ba4\u5f97\uff0c\u4f46\u8fd9\u6837\u7684\u5251\u6211 \u5374\u89c1\u8fc7\u4e00\u6b21\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4e0d\u77e5\u674e\u5144\u53ef\u542c\u8d77\u8fc7\u201c\u4e2d\u539f\u4e00\u70b9\u7ea2\u201d\u7684\u540d\u5b57\uff1f\u201d \u3000\u3000\u674e\u7389\u51fd\u52a8\u5bb9\u9053\uff1a\u201c\u695a\u5144\u8bf4\u7684\u83ab\u975e\u662f\u90a3\u53ea\u8ba4\u94b1\uff0c\u4e0d\u8ba4\u4eba\u7684\u804c\u4e1a\u523a\u5ba2\uff0c\u4eba\u79f0\u201c\u6740\u4eba\u4e0d\u89c1\u8840\uff0c \u5251\u4e0b\u4e00\u70b9\u7ea2\u201d\u7684\u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u9053\uff1a\u201c\u5bb6\u7236\u8bc4\u8bba\u5f53\u4ee3\u540d\u5bb6\u5251\u6cd5\u65f6\uff0c\u4e5f\u66fe\u63d0\u8d77\u8fc7\u6b64\u4eba\u7684\u540d\u5b57\uff0c\u8bf4\u4ed6\u7684\u5251\u6cd5\u81ea\u6210\u4e00 \u683c\uff0c\u672c\u53ef\u548c\u859b\u8863\u4eba\u859b\u5927\u4fa0\u4e89\u4e00\u65e5\u4e4b\u77ed\u957f\uff0c\u53ea\u53ef\u60dc\u4ed6\u7684\u4e3a\u4eba\u504f\u6fc0\uff0c\u884c\u4e8b\u4e5f\u592a\u6bd2\u8fa3\uff0c\u662f\u4ee5\u5251\u6cd5\u4e0d \u89c9\u4e5f\u8d70\u5165\u4e86\u90aa\u8def\u3002\u81ea\u53e4\u4ee5\u6765\uff0c\u90aa\u4e0d\u80dc\u6b63\uff0c\u6240\u4ee5\u65e0\u8bba\u4ed6\u5929\u8d44\u591a\u9ebd\u9ad8\uff0c\u7528\u529f\u591a\u9ebd\u52e4\uff0c\u4e5f\u5fc5\u7136\u65e0\u6cd5 \u767b\u5cf0\u9020\u6781\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u53f9\u9053\uff1a\u201c\u5c31\u51ed\u8fd9\u4e00\u756a\u8bdd\uff0c\u674e\u8001\u524d\u8f88\u5df2\u65e0\u6127\u4e3a\u5f53\u4ee3\u7b2c\u4e00\u5251\u5ba2\uff0c\u666e\u5929\u4e4b\u4e0b\uff0c\u53ea\u8981\u662f\u5b66 \u5251\u7684\u4eba\uff0c\u90fd\u8be5\u5c06\u8fd9\u756a\u8bdd\u7262\u8bb0\u5728\u5fc3\uff0c\u7ec8\u751f\u5949\u884c\u4e0d\u6e1d\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u9053\uff1a\u201c\u5c0f\u6b63\u5219\u5251\u6b63\uff0c\u5fc3\u90aa\u5219\u5251\u90aa\uff0c\u8fd9\u7684\u786e\u662f\u5343\u53e4\u4e0d\u79fb\u7684\u9053\u7406\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u5ffd\u7136\u9053\uff1a\u201c\u8fd9\u523a\u5ba2\u7528\u7684\u5251\uff0c\u83ab\u975e\u548c\u4e2d\u539f\u4e00\u70b9\u7ea2\u540c\u6837\u7684\u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u9664\u4e86\u5251\u67c4\u7565\u6709\u4e0d\u540c\uff0c\u5176\u9980\u65e0\u8bba\u957f\u77ed\u3001\u5bbd\u7a84\u90fd\u5b8c\u5168\u4e00\u6837\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u773c\u6ce2\u6d41\u52a8\uff0c\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u8fd9\u523a\u5ba2\u7adf\u662f\u4e2d\u539f\u4e00\u70b9\u7ea2\u6d3e\u6765\u7684\u4e86\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u5012\u7edd\u65e0\u53ef\u80fd\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u8f7b\u8f7b\u54ac\u4e86\u54ac\u5979\u90a3\u8f6e\u5ed3\u4f18\u7f8e\u7684\u5634\u5507\uff0c\u9053\uff1a\u201c\u90a3\u9ebd\u695a\u5144\u7684\u610f\u601d\u662f\u2026\u2026\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u6211\u7684\u610f\u601d\u53ea\u4e0d\u8fc7\u662f\u8bf4\uff0c\u8fd9\u523a\u5ba2\u672c\u8eab\u548c\u6211\u7edd\u6ca1\u6709\u4ec0\u9ebd\u74dc\u845b\uff0c\u751a\u81f3\u6839\u672c\u4e0d\u8ba4\u5f97 \u6211\uff0c\u4ed6\u8fd9\u6b21\u6765\u884c\u523a\uff0c\u53ea\u4e0d\u8fc7\u662f\u88ab\u522b\u4eba\u6536\u4e70\u7684\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u6c89\u541f\u4e86\u534a\u664c\uff0c\u70b9\u5934\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u4eba\u7528\u7684\u5251\u65e2\u7136\u548c\u4e00\u70b9\u7ea2\u5b8c\u5168\u4e00\u6837\uff0c\u60f3\u5fc5\u5c31\u662f\u4e00 \u70b9\u7ea2\u7684\u540c\u95e8\uff0c\u81ea\u7136\u4e5f\u548c\u4e00\u70b9\u7ea2\u540c\u6837\u662f\u4ee5\u6740\u4eba\u4e3a\u4e1a\u7684\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u76b1\u7709\u9053\uff1a\u201c\u6c5f\u6e56\u4e2d\u771f\u6709\u8fd9\u8bb8\u591a\u4ee5\u6740\u4eba\u4e3a\u4e1a\u7684\u4eba\u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u53f9\u9053\uff1a\u201c\u770b\u6765\u6015\u662f\u5982\u6b64\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u89e3\u5f00\u4e86\u8fd9\u523a\u5ba2\u7684\u8863\u895f\uff0c\u91cc\u9762\u662f\u7a7a\u7684\uff0c\u8fd9\u79cd\u4eba\u81ea\u7136\u7edd\u4e0d\u4f1a\u5c06\u4e00\u4e9b\u96f6\u96f6\u788e\u788e\u7684\u4e1c\u897f\u5e26 \u5728\u8eab\u4e0a\u505a\u8d58\u3002 \u3000\u3000\u4f46\u695a\u7559\u9999\u5374\u5728\u4ed6\u8d34\u8eab\u7684\u5c0f\u8863\u91cc\u53d1\u73b0\u5e02\u6837\u4e1c\u897f\u2014\u2014\u4e00\u5f20\u6570\u76ee\u5f88\u5927\u7684\u94f6\u7968\uff0c\u548c\u4e00\u9762\u5f62\u72b6\u5f88\u53e4 \u602a\u7684\u94dc\u724c\u3002 \u3000\u3000\u94f6\u7968\u662f\u5f53\u65f6\u6700\u901a\u884c\uff0c\u6700\u53ef\u9760\u7684\u4e00\u79cd\uff0c\u65e0\u8bba\u5728\u4efb\u4f55\u5730\u65b9\u90fd\u53ef\u4ee5\u63d0\u73b0\uff0c\u695a\u7559\u9999\u53f9\u4e86\u53e3\u6c14\uff0c\u82e6 \u7b11\u9053\uff1a\u201c\u4e8c\u5341\u4e07\u4e24\uff0c\u8fd9\u5c31\u96be\u602a\u4ed6\u8981\u6765\u6740\u6211\u4e86\uff0c\u4e3a\u4e86\u4e8c\u5341\u4e07\u4e24\uff0c\u6211\u8bf4\u4e0d\u5b9a\u81ea\u5df1\u4f1a\u5c06\u81ea\u5df1\u6740\u4e86 \u7684\uff0c\u6211\u5012\u672b\u60f3\u5230\u6211\u8fd9\u6761\u547d\u7adf\u5982\u6b64\u503c\u94b1\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u53f9\u9053\uff1a\u201c\u8fd9\u4eba\u7adf\u4e0d\u60dc\u82b1\u4e8c\u5341\u4e07\u4e24\u6765\u5bc2\u695a\u5144\u7684\u6027\u547d\uff0c\u770b\u6765\u4ed6\u548c\u695a\u5144\u7684\u4ec7\u6068\u5fc5\u5b9a\u4e0d \u5c0f\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u5ffd\u7136\u9053\uff1a\u201c\u6211\u5df2\u7ecf\u53ef\u4ee5\u67e5\u51fa\u8fd9\u4eba\u662f\u8c01\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u67f3\u65e0\u7709\u9053\uff1a\u201c\u8fd9\u9ebd\u5927\u6570\u76ee\u7684\u94f6\u7968\uff0c\u4efb\u4f55\u94f6\u5e84\u90fd\u4e0d\u4f1a\u968f\u4fbf\u7528\u51fa\u6765\u7684\uff0c\u4ed6\u5e10\u672c\u4e0a\u4e00\u5b9a\u6709\u8bb0 \u8f7d\uff0c\u6211\u4eec\u53ea\u8981\u5230\u8fd9\u94f6\u5e84\u53bb\u67e5\u67e5\u8fd9\u5f20\u94f6\u7968\u662f\u4ed8\u7ed9\u8c01\u7684\uff0c\u5c82\u975e\u5c31\u53ef\u77e5\u9053\u8fd9\u4eba\u662f\u8c01\u4e86\u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u5012\u4e0d\u5fc5\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u773c\u775b\u77aa\u5f97\u66f4\u5927\uff0c\u9053\uff1a\u201c\u4e3a\u4ec0\u9ebd\uff1f\u96be\u9053\u695a\u5144\u5df2\u7ecf\u77e5\u9053\u8fd9\u4eba\u662f\u8c01\u4e86\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u6211\u82e5\u8981\u6536\u4e70\u523a\u5ba2\u53bb\u884c\u523a\u522b\u4eba\uff0c\u4e5f\u7edd\u4e0d\u4f1a\u7528\u81ea\u5df1\u94f6\u7968\u7684\uff0c\u6240\u4ee5\u6211\u4eec\u5c31\u7b97\u53bb \u67e5\uff0c\u975e\u4f46\u6ca1\u6709\u7528\uff0c\u800c\u4e14\u8fd8\u4f1a\u88ab\u8bf1\u5165\u6b67\u9014\uff0c\u627e\u5230\u4e2a\u4e0d\u76f8\u5e72\u7684\u4eba\u8eab\u4e0a\u53bb\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u9ed8\u7136\u534a\u664c\uff0c\u8f7b\u53f9\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u4e5f\u6709\u9053\u7406\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u5fae\u7b11\u9053\uff1a\u201c\u4f46\u6211\u73b0\u5728\u5df2\u81f3\u5c11\u67e5\u51fa\u6765\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u7acb\u523b\u95ee\u9053\uff1a\u201c\u695a\u5144\u5df2\u67e5\u51fa\u4e86\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u7f13\u7f13\u9053\uff1a\u201c\u73b0\u5728\u6211\u81f3\u5c11\u5df2\u77e5\u9053\u8fd9\u4eba\u5fc5\u5b9a\u662f\u4e2a\u5bcc\u7fc1\uff0c\u56e0\u4e3a\u968f\u968f\u4fbf\u4fbf\u5c31\u80fd\u82b1\u5f97\u8d77\u4e8c\u5341 \u4e07\u4e24\u7684\u4eba\uff0c\u8fd9\u4e16\u4e0a\u6bd5\u7adf\u662f\u4e0d\u591a\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u5df2\u6c89\u9ed8\u4e86\u8bb8\u4e45\uff0c\u6b64\u523b\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u8fd9\u94dc\u724c\u5374\u662f\u4ec0\u9ebd\u4e1c\u897f\u5462\uff1f\u201d \u3000\u3000\u53ea\u89c1\u8fd9\u9762\u94dc\u724c\u6b63\u9762\u7684\u82b1\u7eb9\uff0c\u96d5\u523b\u5341\u53c1\u67c4\u5251\u73af\u7ed5\u4e00\u53ea\u624b\uff0c\u5251\u7684\u5f62\u72b6\uff0c\u6b63\u90fd\u548c\u8fd9\u523a\u5ba2\u6240\u4f7f\u7684 \u5b8c\u5168\u4e00\u6837\u3002 \u3000\u3000\u94dc\u724c\u7684\u53cd\u9762\uff0c\u5374\u53ea\u523b\u4e2a\u201c\u516b\u201d\u5b57\u3002 \u3000\u3000\u674e\u7389\u51fd\u76b1\u7709\u9053\uff1a\u201c\u8fd9\u5341\u53c1\u67c4\u5251\u662f\u4ec0\u9ebd\u610f\u601d\u5462\uff1f\u201d \u3000\u3000\u67f3\u65e0\u7709\u76ee\u5149\u95ea\u52a8\uff0c\u62cd\u624b\u9053\uff1a\u201c\u8fd9\u610f\u601d\u6211\u5df2\u7ecf\u660e\u767d\u4e86\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u6c89\u541f\u9053\uff1a\u201c\u5341\u53c1\u67c4\u5251\uff0c\u96be\u9053\u5c31\u662f\u8c61\u5fb5\u5341\u53c1\u4e2a\u4eba\u9ebd\uff1f\u201d \u3000\u3000\u67f3\u65e0\u7709\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u5341\u53c1\u4e2a\u4eba\u60f3\u5fc5\u90fd\u662f\u4ee5\u6740\u4eba\u4e3a\u4e1a\uff0c\u8fd9\u53ea\u624b\u4ee3\u8868\u4ed6\u4eec\u7684\u9996\u8111\uff0c\u8fd9\u4eba\u5728 \u540c\u95e8\u4e2d\u6392\u884c\u7b2c\u516b\uff0c\u6240\u4ee5\u53cd\u9762\u6709\u4e2a\u201c\u516b\u201d\u5b57\u3002\u201d \u3000\u3000\u5979\u5411\u695a\u7559\u9999\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u800c\u90a3\u4e2d\u539f\u4e00\u70b9\u7ea2\uff0c\u6015\u5c31\u662f\u5176\u4e2d\u7684\u7b2c\u4e00\u628a\u4ea4\u6905\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u53f9\u9053\uff1a\u201c\u770b\u6765\u6015\u6b63\u662f\u5982\u6b64\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u9053\uff1a\u201c\u4f46\u6700\u53ef\u6015\u7684\uff0c\u81ea\u7136\u8fd8\u662f\u90a3\u53ea\u624b\uff0c\u4ed6\u867d\u4e0d\u51fa\u9762\uff0c\u5374\u5728\u6697\u4e2d\u63a7\u5236\u8fd9\u79d8\u5bc6\u7684\u96c6 \u56e2\uff0c\u5229\u7528\u8fd9\u5341\u53c1\u4e2a\u4eba\u505a\u6740\u4eba\u7684\u4e70\u5356\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u9a87\u7136\u9053\uff1a\u201c\u6c5f\u6e56\u4e2d\u7adf\u6709\u4e86\u4ee5\u6740\u4eba\u4e3a\u4e1a\u7684\u96c6\u56e2\uff0c\u90a3\u5c82\u975e\u53ef\u6015\u5f97\u5f88\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u53f9\u9053\uff1a\u201c\u8fd9\u6015\u5df2\u4e0d\u7b97\u662f\u8fd1\u767e\u5e74\u6765\u6700\u53ef\u6015\u7684\u4e8b\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u867d\u672b\u8bf4\u8bdd\uff0c\u5fc3\u91cc\u5374\u5f88\u96be\u53d7\uff1a\u201c\u96be\u602a\u4e00\u70b9\u7ea2\u770b\u6765\u50cf\u662f\u5fc3\u4e8b\u91cd\u91cd\uff0c\u539f\u6765\u4ed6\u5c31\u662f\u56e0\u4e3a\u9677 \u8eab\u5728\u8fd9\u8840\u8165\u7684\u79d8\u5bc6\u96c6\u56e2\u4e2d\uff0c\u4e0d\u80fd\u81ea\u62d4\u3002\u201d \u3000\u3000\u201c\u96be\u602a\u4ed6\u51b3\u5b9a\u4e0d\u518d\u51b7\u8840\u6740\u4eba\u5f8c\uff0c\u5c31\u7acb\u523b\u8fdc\u8d70\u7a77\u8352\uff0c\u9003\u5165\u5927\u6f20\uff0c\u56e0\u4e3a\u4ed6\u77e5\u9053\uff0c\u90a3\u53ea\u624b\u7edd\u4e0d \u4f1a\u653e\u8fc7\u4ed6\u7684\u3002\u201d \u3000\u3000\u4efb\u4f55\u4eba\u53ea\u8981\u52a0\u5165\u8fd9\u79cd\u7ec4\u7ec7\uff0c\u9664\u4e86\u6b7b\uff0c\u6015\u5c31\u6ca1\u6709\u522b\u7684\u6cd5\u5b50\u53ef\u4ee5\u8131\u79bb\u4e86\u3002 \u3000\u3000\u695a\u7559\u9999\u73b0\u5728\u624d\u77e5\u9053\u4e00\u70b9\u7ea2\u7684\u773c\u775b\u4e3a\u4f55\u603b\u662f\u90a3\u9ebd\u6df1\u6c89\uff0c\u90a3\u9ebd\u5fe7\u90c1\uff0c\u4ed6\u53ea\u5f8c\u6094\u81ea\u5df1\u4ee5\u524d\u4e3a\u4f55 \u4e00\u76f4\u6ca1\u6709\u60f3\u5230\u3002 \u3000\u3000\u53ea\u542c\u67f3\u65e0\u7709\u5ffd\u53c8\u7b11\u9053\uff1a\u201c\u4f46\u8fd9\u96c6\u56e2\u73b0\u5728\u5df2\u6ca1\u6709\u4ec0\u9ebd\u53ef\u6015\u4e86\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u9053\uff1a\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u67f3\u65e0\u7709\u9053\uff1a\u201c\u56e0\u4e3a\u7528\u4e0d\u518d\u8fc7\u591a\u4e45\uff0c\u8fd9\u53ea\u624b\u4e0a\u5c31\u8981\u88ab\u52a0\u4e0a\u4e00\u526f\u624b\u94d0\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u60f3\u4e86\u60f3\uff0c\u5c55\u989c\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u73b0\u5728\u4ed6\u4eec\u65e2\u7136\u5df2\u60f9\u5230\u695a\u9999\u5e05\u5934\u4e0a\u6765\u4e86\uff0c\u695a\u5144\u8fd8\u4f1a\u653e \u8fc7\u4ed6\u4eec\u9ebd\uff1f\u201d \u3000\u3000\u67f3\u65e0\u7709\u9053\uff1a\u201c\u4f55\u51b5\uff0c\u8fd9\u96c6\u56e2\u7684\u7ec4\u7ec7\u65e2\u7136\u5982\u6b64\u4e25\u5bc6\uff0c\u6bcf\u4e00\u7968\u4e70\u5356\u5c31\u5fc5\u5b9a\u90fd\u8981\u7ecf\u8fc7\u90a3\u53ea \u201c\u624b\u201d\u7684\uff0c\u695a\u5144\u53ea\u8981\u67e5\u51fa\u8fd9\u53ea\u624b\uff0c\u4e5f\u5c31\u80fd\u67e5\u51fa\u6536\u4e70\u523a\u5ba2\u7684\u4eba\u662f\u8c01\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u5ffd\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u5e76\u4e0d\u6025\u627e\u4ed6\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u7eb5\u7136\u6700\u80fd\u63a7\u5236\u81ea\u5df1\u60c5\u7eea\uff0c\u6b64\u523b\u9762\u4e0a\u4e5f\u4e0d\u7981\u9732\u51fa\u60ca\u8bb6\u4e4b\u8272\uff0c\u5931\u58f0\u9053\uff1a\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u5fae\u7b11\u9053\uff1a\u201c\u8fd9\u79cd\u4eba\u8fde\u6740\u4eba\u90fd\u4e0d\u6562\u81ea\u5df1\u52a8\u624b\uff0c\u6211\u89c1\u4e86\u4ed6\u53cd\u800c\u751f\u6c14\uff0c\u6211\u73b0\u5728\u60f3\u53bb\u62dc\u89c1 \u5f53\u4ee3\u7b2c\u4e00\u5251\u5ba2\u7684\u624b\u91c7\uff0c\u8fd9\u5c82\u975e\u6bd4\u82e6\u82e6\u53bb\u627e\u90a3\u79cd\u8df3\u6881\u5c0f\u4e11\u6109\u5feb\u5f97\u591a\u3002\u201d \u3000\u3000\u4ed6\u51dd\u6ce8\u67f3\u65e0\u7709\u7684\u8138\uff0c\u7f13\u7f13\u6309\u9053\uff1a\u201c\u4f55\u51b5\uff0c\u4ed6\u53cd\u6b63\u8fdf\u65e9\u8fd8\u8981\u6765\u627e\u6211\u7684\uff0c\u6211\u53c8\u4f55\u5fc5\u6025\u53bb\u627e \u4ed6\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u5374\u62bf\u5634\u4e00\u7b11\uff0c\u5ae3\u7136\u9053\uff1a\u201c\u6700\u4e3b\u8981\u7684\uff0c\u6015\u8fd8\u662f\u695a\u5144\u6015\u82cf\u59d1\u5a18\u5979\u4eec\u7b49\u5f97\u6025\u5427\uff1f\u201d \u3000\u3000\u4e24\u4eba\u76f8\u89c6\u800c\u7b11\uff0c\u674e\u7389\u51fd\u9762\u4e0a\u5374\u5ffd\u7136\u53d8\u4e86\u989c\u8272\uff0c\u5931\u58f0\u9053\uff1a\u201c\u80e1\u5144\u5462\uff1f\u80e1\u5144\u5230\u90a3\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u4ed6\u4f3c\u4e4e\u76f4\u5230\u6b64\u523b\u624d\u53d1\u73b0\u80e1\u94c1\u82b1\u5df2\u4e0d\u5728\u8fd9\u5c4b\u5b50\u91cc\uff0c\u695a\u7559\u9999\u5c45\u7136\u4e5f\u4e00\u76f4\u6ca1\u6709\u6025\uff0c\u7b49\u4ed6\u95ee\u8d77\uff0c \u624d\u6de1\u6de1\u9053\uff1a\u201c\u4ed6\u65b9\u597d\u50cf\u4e5f\u53d1\u73b0\u4e86\u4e2a\u53ef\u7591\u7684\u4eba\uff0c\u5c31\u8ffd\u51fa\u53bb\u4e86\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u4e5f\u5931\u58f0\u9053\uff1a\u201c\u80e1\u5144\u5df2\u6709\u4e00\u53ea\u624b\u4e0d\u80fd\u52a8\u5f39\uff0c\u600e\u9ebd\u53ef\u4ee5\u8f7b\u8eab\u8ffd\u654c\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u8fd9\u5012\u65e0\u59a8\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u9053\uff1a\u201c\u65e0\u59a8\uff1f\u695a\u5144\u96be\u9053\u4e0d\u6015\u4ed6\u906d\u4e86\u522b\u4eba\u6bd2\u624b\u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u4ed6\u7edd\u4e0d\u4f1a\u6709\u610f\u5916\u7684\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u9053\uff1a\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u56e0\u4e3a\u522b\u4eba\u53ea\u4e0d\u8fc7\u60f3\u8981\u6211\u7684\u547d\uff0c\u5e76\u4e0d\u60f3\u8981\u4ed6\u7684\uff0c\u65b9\u53ea\u4e0d\u8fc7\u662f\u8981\u5c06\u4ed6\u8bf1\u51fa\u53bb\uff0c \u597d\u52a8\u624b\u6740\u4e86\u6211\u800c\u5df2\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u9053\uff1a\u201c\u4f46\u2014\u2014\u4f46\u4ed6\u4e3a\u4ec0\u9ebd\u76f4\u5230\u6b64\u523b\u8fd8\u6ca1\u6709\u56de\u6765\u5462\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u60a0\u7136\u9053\uff1a\u201c\u4ed6\u82e5\u4e0d\u662f\u5728\u5916\u9762\u5077\u559d\u9152\uff0c\u5c31\u4e00\u5b9a\u662f\u8ff7\u4e86\u8def\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u53f9\u9053\uff1a\u201c\u695a\u5144\u5012\u771f\u6c89\u5f97\u4f4f\u6c14\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u9053\uff1a\u201c\u6211\u5012\u4e0d\u662f\u771f\u6c89\u5f97\u4f4f\u6c14\uff0c\u53ea\u4e0d\u8fc7\u662f\u5df2\u542c\u89c1\u4e86\u4ed6\u7684\u58f0\u97f3\u800c\u5df2\u3002\u201d \u3000\u3000\u5f88\u5c11\u6709\u4eba\u77e5\u9053\u4ec0\u9ebd\u65f6\u5019\u4f1a\u4e0b\u96e8\uff0c\u8fd9\u5e76\u4e0d\u5947\u602a\uff0c\u56e0\u4e3a\u80fd\u50cf\u8bf8\u845b\u4eae\u90a3\u6837\u4e0a\u77e5\u5929\u6587\u7684\u4eba\u6bd5\u7adf\u4e0d \u591a\u3002 \u3000\u3000\u5947\u602a\u7684\u662f\uff0c\u4e5f\u5f88\u5c11\u6709\u4eba\u77e5\u9053\u96e8\u662f\u4ec0\u9ebd\u65f6\u5019\u505c\u7684\u3002 \u3000\u3000\u96e8\u597d\u50cf\u603b\u662f\u5728\u4eba\u4eec\u4e0d\u77e5\u4e0d\u89c9\u4e2d\u5c31\u505c\u4e86\u3002 \u3000\u3000\u9759\u591c\u7684\u5fae\u98ce\u4e2d\uff0c\u679c\u7136\u4f20\u6765\u80e1\u94c1\u82b1\u7684\u58f0\u97f3\uff0c\u9053\uff1a\u201c\u5c31\u662f\u8fd9\u4e00\u5bb6\u3002\u201d \u3000\u3000\u53e6\u5916\u7adf\u8fd8\u6709\u4e2a\u82cd\u8001\u7684\u58f0\u97f3\u9053\uff1a\u201c\u8fd9\u6b21\u4e0d\u4f1a\u9519\u9ebd\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u9519\u4e0d\u4e86\u3002\u201d \u3000\u3000\u542c\u5230\u8fd9\u53e5\u8bdd\u7684\u65f6\u5019\uff0c\u4ed6\u7684\u4eba\u5df2\u63a0\u5165\u9662\u5b50\uff0c\u5c31\u50cf\u662f\u53ea\u521a\u7834\u4eba\u8e29\u5c3e\u5df4\u7684\u732b\u4e00\u822c\u51b2\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u6309\uff0c\u5c31\u662f\u4e00\u58f0\u6b22\u547c\uff0c\u9053\uff1a\u201c\u539f\u6765\u4f60\u4eec\u5df2\u56de\u6765\u4e86\u3002\u201d \u3000\u3000\u6b22\u547c\u8fc7\u5f8c\uff0c\u53c8\u77aa\u8d77\u773c\u775b\uff0c\u9053\uff1a\u201c\u8001\u81ed\u866b\uff0c\u4f60\u600e\u9ebd\u5ffd\u7136\u722c\u8d77\u6765\u7684\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u8fd8\u672b\u8bf4\u8bdd\uff0c\u5916\u9762\u5df2\u53c8\u4f20\u6765\u90a3\u82cd\u8001\u7684\u58f0\u97f3\uff0c\u9053\uff1a\u201c\u695a\u9999\u5e05\u6ca1\u4ec0\u9ebd\u4e8b\u5417\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u6478\u4e86\u6478\u9f3b\u5b50\uff0c\u9053\uff1a\u201c\u591a\u8c22\u9601\u4e0b\u5173\u5fc3\uff0c\u4e3a\u4f55\u4e0d\u8bb2\u8fdb\u6765\u4e00\u89c1\uff1f\u201d \u3000\u3000\u5916\u9762\u7684\u4eba\u9053\uff1a\u201c\u8001\u673d\u975e\u4f46\u65e9\u5c31\u60f3\u89c1\u9999\u5e05\u4e00\u9762\u4e86\uff0c\u4f46\u5f8c\u6765\u4e00\u60f3\uff0c\u73b0\u5728\u8fd8\u662f\u83ab\u8981\u89c1\u9762\u7684 \u597d\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u90a3\u4eba\u7b11\u9053\uff1a\u201c\u73b0\u5728\u6211\u4e00\u89c1\u4f60\uff0c\u81f3\u5c11\u4e5f\u8be5\u78d5\u5341\u4e03\u516b\u4e2a\u54cd\u5934\u624d\u5bf9\uff0c\u53ef\u662f\u6211\u8001\u5934\u5b50\u8fd9\u9ebd\u5927\u4e00\u628a \u5e74\u7eaa\u4e86\uff0c\u5230\u522b\u4eba\u9762\u524d\u53e9\u5934\u5b9e\u5728\u4e0d\u597d\u610f\u601d\uff0c\u8fd8\u662f\u7b49\u6211\u4ee5\u5f8c\u60f3\u6cd5\u5b50\u62a5\u4e86\u4f60\u7684\u5927\u6069\u4e4b\u5f8c\uff0c\u518d\u6765\u627e\u4f60 \u75db\u75db\u5feb\u5feb\u559d\u51e0\u676f\u5427\uff01\u201d \u3000\u3000\u8bf4\u5230\u6700\u5f8c\u4e00\u53e5\u8bdd\u65f6\uff0c\u4ed6\u8bed\u58f0\u5df2\u8fdc\u5728\u6570\u4e08\u5916\u3002 \u3000\u3000\u695a\u7559\u9999\u8bb6\u7136\u9053\uff1a\u201c\u6b64\u4eba\u7a76\u7adf\u662f\u8c01\uff1f\u6211\u51e0\u65f6\u6709\u6069\u65bc\u4ed6\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u4f60\u5bf9\u4ed6\u5012\u6ca1\u4ec0\u9ebd\u597d\u5904\uff0c\u4f46\u5bf9\u4e10\u5e2e\u5374\u6709\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u5931\u58f0\u9053\uff1a\u201c\u4ed6\u4e5f\u662f\u4e10\u5e2e\u5f1f\u5343\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u7b11\u9053\uff1a\u201c\u4e0d\u662f\u5f1f\u5b50\uff0c\u662f\u957f\u8001\uff0c\u7b97\u8d77\u8f88\u4efd\u6765\uff0c\u597d\u50cf\u8fde\u6614\u5e74\u7684\u4efb\u6148\u4e5f\u6bd4\u4ed6\u8981\u5c0f\u4e00 \u8f88\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u773c\u73e0\u5b50\u4e00\u8f6c\uff0c\u8038\u7136\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u83ab\u975e\u662f\u201c\u4e07\u91cc\u72ec\u884c\u201d\u6234\u8001\u524d\u8f88\u9ebd\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u4f60\u600e\u4f1a\u8ba4\u5f97\u8fd9\u4f4d\u524d\u8f88\u5947\u4eba\u7684\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u96be\u9053\u53ea\u6709\u4f60\u624d\u80fd\u8ba4\u5f97\u8fd9\u4e9b\u524d\u8f88\u5947\u4eba\uff0c\u5c31\u4e0d\u8bb8\u6211\u8ba4\u5f97\u4e00\u4e24\u4e2a\u9ebd\uff1f\u201d \u3000\u3000\u4ed6\u5927\u7b11\u6309\u9053\uff1a\u201c\u4f60\u82e5\u5403\u918b\uff0c\u6211\u4e0d\u59a8\u518d\u544a\u8bc9\u4f60\uff0c\u4eca\u5929\u665a\u4e0a\u6211\u8fd8\u9047\u89c1\u4e86\u4e00\u4e2a\u4eba\uff0c\u4e5f\u662f\u4f60\u65e9\u5c31 \u60f3\u548c\u4ed6\u89c1\u9762\u7684\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u8c01\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u753b\u7709\u9e1f\u3002\u201d \u3000\u3000\u4ed6\u8fd8\u60f3\u518d\u8bf4\u4ec0\u9ebd\uff0c\u8c01\u77e5\u695a\u7559\u9999\u5ffd\u7136\u585e\u4e86\u6837\u4e1c\u897f\u5230\u4ed6\u5634\u91cc\u53bb\uff0c\u80e1\u94c1\u82b1\u5410\u4e5f\u5410\u4e0d\u51fa\uff0c\u5403\u5403 \u9053\uff1a\u201c\u8fd9\u2026\u2026\u8fd9\u662f\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u5fae\u7b11\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u674e\u5144\u4f09\u4fea\u8f9b\u8f9b\u82e6\u82e6\u4e3a\u4f60\u53d6\u56de\u6765\u7684\u89e3\u836f\uff0c\u4f60\u8fd8\u662f\u5148\u8001\u8001\u5b9e\u5b9e\u7761\u4e00 \u89c9\u518d\u8bf4\u8bdd\u5427\uff01\u201d \u3000\u3000\u66d9\u8272\u597d\u50cf\u4e5f\u603b\u662f\u5728\u4eba\u4eec\u4e0d\u77e5\u4e0d\u89c9\u4e2d\u6765\u5230\u7684\u3002 \u3000\u3000\u4e3a\u4e86\u5927\u5bb6\u90fd\u8981\u8d76\u8def\uff0c\u6240\u4ee5\u5929\u4e00\u4eae\u5c31\u4e0a\u9053\uff0c\u4e3a\u4e86\u5927\u5bb6\u90fd\u8981\u7761\u89c9\uff0c\u6240\u4ee5\u695a\u7559\u9999\u4ed6\u4eec\u5c31\u4e0d\u80fd\u518d \u548c\u674e\u6c0f\u592b\u5987\u540c\u4e58\u4e00\u8f86\u9a6c\u8f66\u3002 \u3000\u3000\u53ef\u662f\u80e1\u94c1\u82b1\u600e\u9ebd\u7761\u5f97\uff0c\u8f66\u9a6c\u4e00\u5f00\u59cb\u884c\u8d70\uff0c\u4ed6\u5c31\u77aa\u695a\u7559\u9999\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u9ebd\u4e0d\u8ba9\u6211\u8bf4\u8bdd\uff1f\u4f60 \u7a76\u7adf\u6709\u4ec0\u9ebd\u4e8b\u8981\u7792\u4eba\u5bb6\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u6211\u8981\u7792\u8c01\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u4ee5\u4e3a\u4eba\u5bb6\u8fd8\u770b\u4e0d\u51fa\u6765\u9ebd\uff1f\u4eba\u5bb6\u6545\u610f\u4e0d\u548c\u54b1\u4eec\u540c\u4e58\u4e00\u8f86\u8f66\uff0c\u5c31\u4e3a\u7684\u662f \u8981\u8ba9\u4f60\u6211\u9b3c\u9b3c\u795f\u795f\u7684\u8bf4\u8bdd\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u5fae\u7b11\u9053\uff1a\u201c\u4f60\u600e\u77e5\u8fd9\u4e0d\u662f\u4ed6\u4eec\u81ea\u5df1\u60f3\u9b3c\u9b3c\u795f\u795f\u7684\u8bf4\u8bdd\u5462\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u4eba\u5bb6\u6709\u4ec0\u9ebd\u9b3c\u9b3c\u795f\u795f\u8bdd\u597d\u8bf4\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4e5f\u6ca1\u4ec0\u9ebd\u522b\u7684\u8bdd\u597d\u8bf4\uff0c\u53ea\u4e0d\u8fc7\u662f\u5728\u731c\u6211\u7a76\u7adf\u5df2\u77e5\u9053\u4e86\u591a\u5c11\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u77e5\u9053\u591a\u5c11\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u77e5\u9053\u4ed6\u4eec\u6697\u4e2d\u6240\u73a9\u7684\u82b1\u6837\u9b3c\u8ba1\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u51e0\u4e4e\u8df3\u4e86\u8d77\u6765\uff0c\u6012\u9053\uff1a\u201c\u4eba\u5bb6\u5f53\u4f60\u662f\u597d\u670b\u53cb\uff0c\u975e\u4f46\u8bf7\u4f60\u5403\uff0c\u8bf7\u4f60\u559d\uff0c\u8fd8\u8981\u62db\u5f85\u4f60 \u5230\u5bb6\u53bb\uff0c\u6709\u4eba\u6765\u5bb3\u4f60\uff0c\u4eba\u5bb6\u5c31\u66ff\u4f60\u5c06\u523a\u5ba2\u6740\u4e86\uff0c\u73b0\u5728\u4f60\u5374\u8bf4\u4eba\u5bb6\u5728\u5bf9\u4f60\u73a9\u82b1\u6837\u8be1\u8ba1\uff0c\u6211\u95ee \u4f60\uff0c\u4eba\u5bb6\u8d2a\u56fe\u4f60\u4ec0\u9ebd\uff1f\u8981\u4f60\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u6de1\u6de1\u9053\uff1a\u201c\u4e5f\u4e0d\u8981\u6211\u4ec0\u9ebd\u522b\u7684\uff0c\u53ea\u4e0d\u8fc7\u8981\u6211\u7684\u547d\u800c\u5df2\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u77aa\u4e86\u4ed6\u51e0\u773c\uff0c\u53cd\u800c\u7b11\u4e86\u8d77\u6765\uff0c\u6447\u5934\u7b11\u9053\uff1a\u201c\u6211\u770b\u4f60\u8fd9\u4eba\u771f\u548c\u66f9\u64cd\u5dee\u4e0d\u591a\uff0c\u53ea\u8981\u522b \u4eba\u77a7\u4f60\u4e00\u773c\uff0c\u4f60\u5c31\u4ee5\u4e3a\u4eba\u5bb6\u53c8\u662f\u5728\u6253\u4f60\u7684\u4e3b\u610f\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u90a3\u9ebd\u6211\u95ee\u4f60\uff0c\u84c9\u513f\u4ed6\u4eec\u82e5\u5728\u201c\u62e5\u7fe0\u5c71\u5e84\u201d\uff0c\u4ed6\u4eec\u4e3a\u4ec0\u9ebd\u8981\u51fa\u6765\u6e38\u5c71\u73a9\u6c34\uff1f \u53c8\u201c\u6070\u5de7\u201d\u9047\u89c1\u4e86\u6211\u4eec\uff0c\u4e16\u4e0a\u771f\u6709\u8fd9\u9ebd\u5de7\u7684\u4e8b\u9ebd\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u5c31\u7b97\u4ed6\u4eec\u662f\u6545\u610f\u51fa\u6765\u627e\u4f60\u7684\uff0c\u4e5f\u662f\u4eba\u5bb6\u7684\u4e00\u756a\u597d\u610f\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u65e2\u7136\u662f\u597d\u610f\uff0c\u4e3a\u4ec0\u9ebd\u4e0d\u8bf4\u660e\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u53c8\u5f00\u59cb\u6478\u9f3b\u5b50\u4e86\uff0c\u76b1\u7709\u9053\uff1a\u201c\u96be\u9053\u4f60\u8ba4\u4e3a\u84c9\u513f\u662f\u88ab\u4ed6\u4eec\u52ab\u53bb\u7684\u4e0d\u6210\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u70b9\u4e86\u70b9\u5934\uff0c\u53c8\u9053\uff1a\u201c\u8fd8\u6709\uff0c\u6211\u5ffd\u7136\u75c5\u5012\uff0c\u5e76\u6ca1\u6709\u522b\u4eba\u77e5\u9053\uff0c\u90a3\u523a\u5ba2\u662f\u600e\u9ebd\u6765 \u7684\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u8fd9\u4e5f\u8bb8\u662f\u4ed6\u4eec\u5df2\u5728\u6697\u4e2d\u7aa5\u63a2\u5230\u4e86\uff0c\u4e5f\u8bb8\u662f\u5e97\u5c0f\u4e8c\u5728\u901a\u98ce\u62a5\u4fe1\u3002\u201d \u7b2c\u516b\u7ae0\u3000\u6b32\u53d6\u5148\u4e88>> \u53e4\u9f99\u300a\u695a\u7559\u9999\u7cfb\u5217\xb7\u753b\u7709\u9e1f\u300b \u7b2c\u516b\u7ae0\u3000\u6b32\u53d6\u5148\u4e88 \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u4e5f\u6709\u53ef\u80fd\uff0c\u53ea\u4e0d\u8fc7\uff0c\u4ed6\u4eec\u4e00\u8d76\u56de\u6765\uff0c\u521a\u63a0\u5165\u9662\u5b50\uff0c\u5c31\u5c06\u90a3\u523a\u5ba2\u6740 \u4e86\uff0c\u800c\u90a3\u65f6\u9662\u5b50\u8fd8\u6709\u4e9b\u706f\u5149\uff0c\u5c4b\u5b50\u91cc\u5374\u662f\u4e00\u56e2\u6f06\u9ed1\uff0c\u4ed6\u4eec\u82e5\u975e\u65e9\u5df2\u77e5\u9053\u90a3\u523a\u5ba2\u5728\u5c4b\u5b50\u91cc\uff0c\u6839 \u672c\u5c31\u8fde\u4eba\u5f71\u4e5f\u77a7\u4e0d\u89c1\u7684\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u7709\u5934\u76b1\u5f97\u66f4\u7d27\uff0c\u9053\uff1a\u201c\u4f46\u90a3\u523a\u5ba2\u82e5\u662f\u4ed6\u4eec\u4e70\u6765\u7684\uff0c\u4ed6\u4eec\u4e3a\u4f55\u8981\u6740\u4ed6\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u81ea\u7136\u662f\u4e3a\u4e86\u8981\u6740\u4eba\u706d\u53e3\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u4f46\u5c06\u6211\u8bf1\u51fa\u53bb\u7684\u4eba\uff0c\u5374\u662f\u753b\u7709\u9e1f\uff0c\u753b\u7709\u9e1f\u4e5f\u548c\u4ed6\u4eec\u662f\u540c\u8def\u7684\u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4f60\u60f3\u5fc5\u4e5f\u77e5\u9053\u753b\u7709\u9e1f\u662f\u522b\u4eba\u5316\u540d\u6539\u626e\u7684\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u90a3\u9ebd\u4f60\u600e\u77e5\u753b\u7709\u9e1f\u4e0d\u662f\u4ed6\u4eec\u5316\u540d\u6539\u626e\u7684\u5462\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u6014\u4e86\u534a\u664c\uff0c\u9053\uff1a\u201c\u753b\u7709\u9e1f\u884c\u52a8\u867d\u7136\u8be1\u79d8\uff0c\u4f46\u5bf9\u54b1\u4eec\u5e76\u6ca1\u6709\u4ec0\u9ebd\u6076\u610f\uff0c\u4f60\u82e5\u8bf4\u67f3\u65e0 \u7709\u60f3\u5bb3\u4f60\uff0c\u4ed6\u4eec\u5c31\u7edd\u4e0d\u53ef\u80fd\u662f\u540c\u4e00\u4e2a\u4eba\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4e3a\u4ec0\u9ebd\u4e0d\u53ef\u80fd\uff1f\u6211\u65e9\u5df2\u8bf4\u8fc7\uff0c\u753b\u7709\u9e1f\u90a3\u6837\u505a\uff0c\u5fc5\u5b9a\u662f\u5728\u6545\u610f\u65bd\u6069\u65bc\u6211\uff0c\u8981 \u6211\u62a5\u7b54\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u4ed6\u65e2\u7136\u8981\u5bb3\u4f60\uff0c\u662f\u8981\u4f60\u62a5\u7b54\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u89c1\u5230\u753b\u7709\u9e1f\uff0c\u5e76\u6ca1\u6709\u52a8\u624b\u6740\u5979\uff0c\u662f\u9ebd\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u6211\u5f53\u7136\u4e0d\u80fd\u6740\u5979\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u8fd9\u5c31\u5bf9\u4e86\uff0c\u753b\u7709\u9e1f\u90a3\u6837\u505a\uff0c\u5c31\u662f\u8981\u6211\u4eec\u4ee5\u5f8c\u4e0d\u80fd\u6740\u5979\u2026\u2026\u5c31\u7b97\u6211\u5df2\u77e5\u9053\u67f3 \u65e0\u7709\u5c31\u662f\u753b\u7709\u9e1f\uff0c\u5c31\u7b97\u6211\u77e5\u9053\u5979\u8981\u5bb3\u6211\uff0c\u6211\u4e5f\u53ea\u597d\u653e\u8fc7\u5979\uff0c\u56e0\u4e3a\u5979\u66fe\u7ecf\u5bf9\u6211\u6709\u6069\u2014\u2014\u5979\u8981\u5bb3 \u6211\u4e4b\u524d\uff0c\u65e9\u5df2\u7559\u4e0b\u4e86\u9000\u8def\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u9ebd\u4e00\u5b9a\u8981\u6000\u7591\u67f3\u65e0\u7709\u662f\u753b\u7709\u9e1f\u5462\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u53f9\u9053\uff1a\u201c\u8fd9\u5176\u4e2d\u81ea\u7136\u6709\u8bb8\u591a\u539f\u56e0\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u5ffd\u53c8\u5927\u58f0\u9053\uff1a\u201c\u4f46\u81f3\u5c11\u90a3\u7528\u201c\u66b4\u96e8\u68a8\u82b1\u9489\u201d\u6253\u4f60\u7684\u4eba\uff0c\u603b\u4e0d\u4f1a\u662f\u4ed6\u4eec\u5427\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4e3a\u4f55\u4e0d\u4f1a\u662f\u4ed6\u4eec\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u56e0\u4e3a\u90a3\u65f6\u4ed6\u4eec\u660e\u660e\u8fd8\u5728\u5c4b\u5b50\u91cc\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4f60\u770b\u5230\u4ed6\u4eec\u4e86\u9ebd\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u6014\u4e86\u6014\uff0c\u9053\uff1a\u201c\u6211\u867d\u672b\u770b\u5230\uff0c\u4f46\u660e\u660e\u542c\u5230\u4ed6\u4eec\u5728\u8bf4\u8bdd\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4f60\u5e76\u6ca1\u6709\u542c\u5230\u4ed6\u4eec\u5728\u8bf4\u8bdd\uff0c\u4f60\u53ea\u662f\u542c\u5230\u4ed6\u4eec\u5728\u6323\u624e\u3001\u547c\u558a\u3001\u547b\u541f\uff0c\u662f \u9ebd\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u6bcf\u4e2a\u4eba\u5728\u547c\u558a\u547b\u541f\u65f6\uff0c\u58f0\u97f3\u90fd\u4f1a\u56e0\u75db\u82e6\u800c\u6539\u53d8\u7684\uff0c\u6240\u4ee5\u6211\u4eec\u5c31\u7b97\u542c\u51fa\u4ed6\u4eec \u7684\u58f0\u97f3\u6709\u4e9b\u4e0d\u5bf9\uff0c\u4e5f\u4e0d\u66fe\u5728\u610f\uff0c\u662f\u9ebd\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u53c8\u6014\u4f4f\u4e86\uff0c\u8bb7\u8bb7\u9053\uff1a\u201c\u96be\u9053\u90a3\u65f6\u4ed6\u4eec\u5df2\u4e0d\u5728\u5c4b\u5b50\u91cc\uff0c\u90a3\u58f0\u97f3\u53ea\u662f\u522b\u4eba\u88c5\u51fa\u6765 \u7684\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u8fd9\u96be\u9053\u4e0d\u53ef\u80fd\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u957f\u957f\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u4e0d\u8bf4\u8bdd\u4e86\u3002 \u3000\u3000\u695a\u7559\u9999\uff1a\u201c\u56e0\u4e3a\u4f60\u4e00\u76f4\u8ba4\u4e3a\u662f\u4ed6\u4eec\u5728\u5c4b\u5b50\u91cc\uff0c\u6240\u4ee5\u4f60\u5c31\u4e0d\u4f1a\u60f3\u5230\u90a3\u662f\u522b\u4eba\u8bf4\u8bdd\u7684\u58f0\u97f3\uff0c \u8fd9\u662f\u6bcf\u4e2a\u4eba\u90fd\u96be\u514d\u4f1a\u53d1\u751f\u7684\u9519\u89c9\u3002\u201d \u3000\u3000\u4ed6\u4e5f\u53f9\u4e86\u53e3\u6c14\uff0c\u63a5\u9053\uff1a\u201c\u67f3\u65e0\u7709\u4e0d\u4f46\u5f88\u806a\u660e\uff0c\u800c\u4e14\u505a\u4e8b\u975e\u5e38\u5c0f\u5fc3\uff0c\u5979\u4e5f\u77e5\u9053\u8981\u5bb3\u6211\u5e76\u4e0d \u5bb9\u6613\uff0c\u6240\u4ee5\u5979\u6bcf\u6b21\u4e0b\u624b\u4e4b\u524d\uff0c\u5fc5\u5b9a\u5148\u7559\u597d\u9000\u6b65\uff0c\u8ba9\u6211\u6c38\u8fdc\u4e0d\u4f1a\u6000\u7591\u5230\u4ed6\u4eec\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u62da\u547d\u63c9\u9f3b\u5b50\uff0c\u5583\u5583\u9053\uff1a\u201c\u4f46\u6211\u8fd8\u662f\u4e0d\u660e\u767d\uff0c\u4e5f\u4e0d\u76f8\u4fe1\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u82e6\u7b11\u9053\uff1a\u201c\u5176\u5b9e\u6211\u4e5f\u5e76\u6ca1\u6709\u5b8c\u5168\u5f04\u660e\u767d\uff0c\u53ea\u4e0d\u8fc7\u5927\u6982\u7684\u60c5\u51b5\uff0c\u6211\u5df2\u7ecf\u53ef\u4ee5\u60f3\u50cf\u5f97 \u51fa\u4e86\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u4f60\u8bf4\u6765\u542c\u542c\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u67f3\u65e0\u7709\u592b\u5987\u4e3a\u4e86\u67d0\u4e00\u79cd\u539f\u56e0\uff0c\u4e00\u5b9a\u8981\u627e\u5230\u6211\uff0c\u4f46\u4ed6\u4eec\u627e\u5230\u6211\u7684\u8239\u4e0a\u65f6\uff0c\u6211 \u5df2\u4e0d\u5728\u4e86\uff0c\u4ed6\u4eec\u9000\u56de\u6765\u65f6\uff0c\u5374\u9047\u5230\u4e86\u84c9\u513f\u5979\u4eec\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u4ed6\u4eec\u600e\u4f1a\u9047\u5230\u84c9\u513f\u7684\u5462\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u84c9\u513f\u5979\u4eec\u8981\u627e\u6211\uff0c\u81ea\u7136\u8981\u5148\u56de\u5bb6\u53bb\u770b\u770b\uff0c\u50cf\u5979\u4eec\u90a3\u6837\u7684\u4eba\uff0c\u8d70\u5728\u8def\u4e0a\u81ea\u7136 \u5f88\u5f15\u4eba\u6ce8\u76ee\uff0c\u662f\u9ebd\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u55ef\uff01\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u864e\u4e18\u674e\u5bb6\u58f0\u52bf\u8d6b\u8d6b\uff0c\u5728\u6c5f\u6e56\u4e2d\u81ea\u7136\u8033\u76ee\u5f88\u591a\uff0c\u81ea\u7136\u65e9\u5df2\u542c\u8bf4\u8fc7\u84c9\u513f\u5979\u4eec\u548c \u6211\u7684\u5173\u7cfb\uff0c\u77e5\u9053\u5979\u4eec\u7684\u884c\u8e2a\u5f8c\uff0c\u81ea\u7136\u4f1a\u627e\u4e0a\u95e8\u53bb\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u55ef\uff01\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u50cf\u67f3\u65e0\u7709\u90a3\u6837\u7684\u4eba\uff0c\u81ea\u7136\u5f88\u5bb9\u6613\u5c31\u80fd\u548c\u84c9\u513f\u4ea4\u4e0a\u670b\u53cb\uff0c\u84c9\u513f\u4e5f\u8bb8\u8fd8\u4e0d\u4f1a\u591a \u8bdd\uff0c\u4f46\u751c\u513f\u5374\u548c\u4f60\u4e00\u6837\uff0c\u662f\u4e2a\u76f4\u5fc3\u76f4\u80a0\u7684\u4eba\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u54fc\uff01\u4f60\u8fd9\u7b97\u662f\u6367\u6211\uff0c\u8fd8\u662f\u9a82\u6211\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u4e5f\u4e0d\u7406\u4ed6\uff0c\u63a5\u9053\uff1a\u201c\u67f3\u65e0\u7709\u8981\u60f3\u81ea\u751c\u515c\u53e3\u4e2d\u95ee\u6211\u7684\u6d88\u606f\uff0c\u81ea\u7136\u5e76\u4e0d\u56f0\u96be\u2026\u2026\u201d \u3000\u3000\u80e1\u94c1\u82b1\u622a\u53e3\u9053\uff1a\u201c\u5979\u8ba4\u4e3a\u4f60\u4e5f\u8bb8\u8fd8\u7559\u5728\u6c99\u6f20\u91cc\uff0c\u6240\u4ee5\u5c31\u53bb\u6c99\u6f20\u627e\u627e\u770b\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u5979\u53ea\u6709\u8fd9\u4e00\u70b9\u7ebf\u7d22\uff0c\u53ea\u6709\u53bb\u78b0\u78b0\u8fd0\u6c14\u4e86\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u4f46\u84c9\u513f\u5979\u4eec\u4e3a\u4f55\u6ca1\u6709\u4e00\u9f50\u53bb\uff0c\u53cd\u800c\u5230\u4e86\u201c\u62e5\u7fe0\u5c71\u5e84\u201d\u5462\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u53f9\u9053\uff1a\u201c\u5979\u4eec\u4e5f\u8bb8\u662f\u88ab\u9a97\uff0c\u4e5f\u8bb8\u662f\u88ab\u52ab\uff0c\u4e5f\u8bb8\u2026\u2026\u201d \u3000\u3000\u4ed6\u560e\u7136\u987f\u4f4f\u4e86\u8bed\u58f0\uff0c\u9762\u4e0a\u5df2\u9732\u51fa\u8651\u4e4b\u8272\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u52a8\u5bb9\u9053\uff1a\u201c\u4f60\u96be\u9053\u662f\u8bf4\uff0c\u84c9\u513f\u5979\u4eec\u6839\u672c\u4e0d\u5728\u201c\u62e5\u7fe0\u5c71\u5e84\u201d\uff0c\u800c\u4e14\u8bf4\u4e0d\u5b9a\u5df2\u906d\u4e86\u67f3 \u65e0\u7709\u592b\u5987\u7684\u6bd2\u624b\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u957f\u53f9\u9053\uff1a\u201c\u8fd9\u81ea\u7136\u4e5f\u6709\u53ef\u80fd\uff0c\u5e78\u597d\u67f3\u65e0\u7709\u5e76\u4e0d\u662f\u6b8b\u6740\u65e0\u8f9c\u7684\u4eba\uff0c\u5979\u8981\u5bf9\u4ed8\u7684\u53ea\u662f \u6211\uff0c\u800c\u4e14\u5979\u65e2\u7136\u8981\u65bd\u6069\u65bc\u6211\uff0c\u4ee5\u7559\u9000\u8def\uff0c\u4e5f\u4e0d\u81f3\u65bc\u6740\u5979\u4eec\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u76b1\u7709\u6c89\u601d\u4e86\u534a\u664c\uff0c\u5ffd\u7136\u9053\uff1a\u201c\u4f46\u4ee5\u65f6\u95f4\u63a8\u7b97\uff0c\u5979\u4e00\u5230\u6c99\u6f20\uff0c\u5c31\u627e\u6211\u4eec\u4e86\uff0c\u662f \u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u674e\u7389\u51fd\u65e2\u662f\u6c5f\u5357\u7684\u4e16\u5bb6\u5b50\uff0c\u600e\u4f1a\u5bf9\u6c99\u6f20\u7684\u5730\u5f62\u90a3\u6837\u719f\u6089\uff0c\u4f55\u51b5\uff0c\u77f3\u89c2\u97f3\u7684 \u4f4f\u5904\u53c8\u662f\u90a3\u9ebd\u79d8\u5bc6\uff0c\u4ed6\u4eec\u600e\u80fd\u4e00\u4e0b\u5b50\u5c31\u627e\u5230\u4e86\u5462\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u7f13\u7f13\u9053\uff1a\u201c\u73b0\u5728\u6211\u8fd8\u6709\u4e24\u6837\u60f3\u4e0d\u901a\u7684\u4e8b\uff0c\u8fd9\u5c31\u662f\u5176\u4e2d\u4e4b\u4e00\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u8fd8\u6709\u4e00\u6837\u5462\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u957f\u53f9\u9053\uff1a\u201c\u6211\u5b9e\u5728\u60f3\u4e0d\u901a\u8fd9\u592b\u5987\u4e24\u4eba\u4e3a\u4f55\u4e00\u5b9a\u8981\u6211\u7684\u547d\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u53c8\u76b1\u8d77\u7709\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u73b0\u5728\uff0c\u4ed6\u4eec\u65e2\u5df2\u77e5\u9053\u4f60\u5bf9\u4ed6\u4eec\u8d77\u4e86\u6000\u7591\uff0c\u4e14\u4e00\u5b9a\u770b\u51fa\u4f60\u6628 \u5929\u665a\u4e0a\u662f\u5728\u88c5\u75c5\uff0c\u4f60\u7684\u5904\u5883\u5c82\u975e\u5c31\u66f4\u5371\u9669\u4e86\u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f46\u73b0\u5728\u6211\u65e2\u672b\u63ed\u7a7f\u4ed6\u4eec\uff0c\u4ed6\u4eec\u81ea\u7136\u66f4\u4e0d\u4f1a\u8bf4\u7834\uff0c\u4ed6\u4eec\u73b0\u5df2\u77e5\u9053 \u6211\u5bf9\u4ed6\u4eec\u8d77\u4e86\u6000\u7591\uff0c\u8fd9\u4e00\u8def\u4e0a\u5c31\u4e0d\u6562\u8f7b\u4e3e\u5984\u52a8\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u4ed6\u4eec\u96be\u9053\u8981\u7b49\u4f60\u5230\u4e86\u201c\u62e5\u7fe0\u5c71\u5e84\u201d\u5f8c\u518d\u51fa\u624b\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u770b\u6765\u60f3\u5fc5\u662f\u5982\u6b64\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u82e5\u662f\u5982\u6b64\uff0c\u4ed6\u4eec\u5728\u201c\u62e5\u7fe0\u5c71\u5e84\u201d\u4e2d\u5fc5\u5df2\u51c6\u5907\u4e86\u5bf9\u4ed8\u4f60\u7684\u6cd5\u5b50\uff0c\u4ee5\u864e\u4e18\u674e\u5bb6 \u5728\u6c5f\u6e56\u4e2d\u7684\u58f0\u52bf\uff0c\u8fd9\u4e00\u5fc5\u5b9a\u975e\u540c\u5c0f\u53ef\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u4f60\u65e2\u7136\u77e5\u9053\uff0c\u8fd8\u8981\u53bb\u9001\u6b7b\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u53f9\u9053\uff1a\u201c\u4e8b\u5df2\u81f3\u6b64\uff0c\u6211\u80fd\u4e0d\u53bb\u9ebd\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9ed8\u7136\u534a\u664c\uff0c\u53f9\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u81ea\u7136\u4e0d\u80fd\u5c06\u84c9\u513f\u5979\u4eec\u629b\u5728\u90a3\u91cc\uff0c\u53ef\u662f\u2026\u2026\u201d \u3000\u3000\u695a\u7559\u9999\u5ffd\u53c8\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u53ef\u662f\u4f60\u4e5f\u4e0d\u5fc5\u592a\u62c5\u5fc3\uff0c\u6211\u4eec\u6b64\u884c\u867d\u51f6\u9669\uff0c\u4f46\u81f3\u5c11\u4e0d\u4f1a\u518d\u9047\u5230 \u50cf\u201c\u66b4\u96e8\u68a8\u82b1\u9489\u201d\u90a3\u6837\u7684\u6697\u7b97\u4e86\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u4f55\u4ee5\u89c1\u5f97\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4ee5\u674e\u5bb6\u5728\u6c5f\u6e56\u4e2d\u7684\u58f0\u52bf\uff0c\u4ed6\u4eec\u8981\u6697\u7b97\u6211\uff0c\u4e5f\u53ea\u80fd\u5728\u522b\u7684\u5730\u65b9\uff0c\u7528\u522b\u4eba\u505a\u66ff \u6b7b\u9b3c\uff0c\u5230\u4e86\u201c\u62e5\u7fe0\u5c71\u5e84\u201d\u5f8c\uff0c\u8fd9\u4e9b\u5351\u9119\u7684\u624b\u6bb5\uff0c\u4ed6\u4eec\u600e\u6562\u518d\u7528\u51fa\u6765\uff1f\u4ed6\u4eec\u600e\u6562\u5c06\u201c\u62e5\u7fe0\u5c71 \u5e84\u201d\u6570\u5341\u5e74\u7684\u4fa0\u540d\u6bc1\u65bc\u4e00\u65e6\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4ed6\u4eec\u4e0d\u7528\u81ea\u5df1\u7684\u6697\u5668\uff0c\u800c\u7528\u201c\u66b4\u96e8\u68a8\u82b1\u9489\u201d\uff0c\u5c31\u662f\u4e3a\u4e86\u6015\u73b7\u6c61\u201c\u62e5 \u7fe0\u5c71\u5e84\u201d\u7684\u58f0\u52bf\uff0c\u4f60\u82e5\u6b7b\u5728\u68a8\u82b1\u9489\u4e0b\uff0c\u81ea\u7136\u8c01\u90fd\u4e0d\u8ba4\u4e3a\u8fd9\u662f\u674e\u5bb6\u5b50\u5f1f\u4e0b\u7684\u6bd2\u624b\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u5fae\u7b11\u9053\uff1a\u201c\u73b0\u5728\u4f60\u5df2\u60f3\u901a\u4e86\u9ebd\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u53f9\u9053\uff1a\u201c\u96be\u602a\u90a3\u4eba\u4e00\u51fb\u4e0d\u4e2d\uff0c\u5c31\u5c06\u90a3\u9ebd\u73cd\u8d35\u7684\u6697\u5668\u629b\u5374\uff0c\u539f\u6765\u5c31\u662f\u6015\u4f60\u53d1\u73b0\u201c\u66b4 \u96e8\u68a8\u82b1\u9489\u201d\u5728\u4ed6\u4eec\u624b\u91cc\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u5176\u5b9e\u4f60\u65e9\u5c31\u8be5\u60f3\u5230\u7684\uff0c\u9664\u4e86\u674e\u5bb6\u5b50\u5f1f\u5916\uff0c\u53c8\u6709\u8c01\u80fd\u5f97\u5230\u90a3\u9ebd\u73cd\u8d35\u7684\u6697 \u5668\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u62a2\u9053\uff1a\u201c\u9664\u4e86\u5bcc\u7532\u6c5f\u5357\u7684\u674e\u5bb6\u5b50\u5f1f\u5916\uff0c\u53c8\u6709\u8c01\u4e00\u51fa\u624b\u5c31\u80fd\u82b1\u4e8c\u5341\u4e07\u4e24\u94f6\u5b50\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u4ed6\u4eec\u5077\u9e21\u4e0d\u8680\u4e86\u628a\u7c73\uff0c\u5374\u4fbf\u5b9c\u4e86\u4f60\uff0c\u5e73\u767d\u5f97\u4e86\u4e00\u6837\u6bd4\u91d1\u5b50\u8fd8\u73cd\u8d35 \u7684\u6697\u5668\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u5927\u7b11\u9053\uff1a\u201c\u4f46\u6211\u5374\u60c5\u613f\u8981\u4e8c\u5341\u4e07\u4e24\u94f6\u5b50\u3002\u201d \u3000\u3000\u4e24\u4eba\u76f8\u5bf9\u5927\u7b11\uff0c\u7adf\u4f3c\u53c8\u5c06\u6b64\u884c\u7684\u51f6\u9669\u5168\u90fd\u5fd8\u4e86\uff0c\u7adf\u5fd8\u4e86\u4ed6\u4eec\u82e5\u6b7b\u5728\u201c\u62e5\u7fe0\u5c71\u5e84\u201d\uff0c\u94f6\u5b50 \u548c\u6697\u5668\u8fd8\u662f\u522b\u4eba\u7684\u3002 \u3000\u3000\u8fd9\u4e24\u4e2a\u4eba\u8111\u888b\u91cc\u7adf\u4f3c\u6839\u672c\u6ca1\u6709\u201c\u5371\u9669\u201d\u4e24\u4e2a\u5b57\u5b58\u5728\u3002 \u3000\u3000\u864e\u4e18\uff0c\u5c71\u540d\uff0c\u539f\u540d\u6d77\u6d8c\u5c71\uff0c\u5728\u82cf\u5dde\u95fe\u95e8\u5916\uff0c\u6545\u8001\u76f8\u4f20\uff0c\u5434\u738b\u9616\u95fe\u5c31\u846c\u5728\u6b64\u5c71\u4e2d\uff0c\u6c34\u94f6\u4e3a \u68fa\uff0c\u91d1\u94f6\u4e3a\u5751\u3002 \u3000\u3000\u53f2\u8bb0\uff1a\u9616\u95fe\u51a2\u5728\u5434\u53bf\u95fe\u95e8\u5916\uff0c\u4ee5\u5341\u4e07\u4eba\u51b6\u51a2\uff0c\u53d6\u571f\u4e34\u6e56\uff0c\u846c\u5f8c\u53c1\u65e5\uff0c\u767d\u864e\u8e1e\u5176\u4e0a\uff0c\u6545\u540d \u864e\u4e18\u3002 \u3000\u3000\u8fd9\u5ea7\u5c71\u5e76\u4e0d\u9ad8\uff0c\u4f46\u5374\u5145\u6ee1\u4e86\u4e00\u4e9b\u7f8e\u4e3d\u7684\u4f20\u8bf4\u548c\u795e\u8bdd\uff0c\u81ea\u53e4\u4ee5\u6765\uff0c\u5c31\u662f\u624d\u5b50\u9a9a\u4eba\u7684\u5fc5\u6e38\u4e4b \u5730\u3002 \u3000\u3000\u695a\u7559\u9999\u4ed6\u4eec\u679c\u7136\u4e00\u8def\u5e73\u5b89\uff0c\u5230\u4e86\u59d1\u82cf\u3002 \u3000\u3000\u4ed6\u4eec\u5e76\u6ca1\u6709\u5728\u57ce\u5916\u7ed5\u8fc7\u53bb\uff0c\u5374\u7a7f\u57ce\u800c\u8fc7\uff0c\u674e\u7389\u51fd\u548c\u67f3\u65e0\u7709\u4ecd\u662f\u8c08\u7b11\u98ce\u751f\uff0c\u8c01\u4e5f\u770b\u4e0d\u51fa\u4ed6 \u4eec\u5fc3\u6000\u6740\u673a\u3002 \u3000\u3000\u695a\u7559\u9999\u96be\u9053\u731c\u9519\u4e86\u9ebd\uff1f \u3000\u3000\u5230\u4e86\u8fd9\u4ee5\u7f8e\u4e3d\u95fb\u540d\u7684\u57ce\u5e02\uff0c\u6bcf\u4e2a\u4eba\u5fc3\u91cc\u90fd\u4e0d\u7981\u6cdb\u8d77\u4e00\u79cd\u6e29\u67d4\u4e4b\u610f\uff0c\u8fd8\u6709\u8c01\u4f1a\u60f3\u6740\u4eba\u5462\uff1f \u3000\u3000\u6e05\u6d01\u7684\u8857\u9053\u4e0a\uff0c\u5f77\u4f5b\u5230\u5904\u90fd\u5145\u6ee1\u4e86\u7f8e\u4e3d\u7684\u5c11\u5973\uff0c\u957f\u957f\u7684\u8fab\u5b50\u968f\u98ce\u6447\u52a8\uff0c\u65f6\u65f6\u5411\u4eba\u5ae3\u7136\u5de7 \u7b11\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u773c\u775b\u90fd\u53d1\u76f4\u4e86\uff0c\u5ffd\u7136\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u53ef\u66fe\u53d1\u73b0\u4e00\u6837\u6709\u8da3\u7684\u4e8b\u9ebd\uff1f\u8fd9\u91cc\u7684\u4eba\u539f\u6765\u90fd\u4e0d \u559c\u6b22\u7a7f\u978b\u5b50\u3002\u201d \u3000\u3000\u53ea\u89c1\u5728\u8857\u4e0a\u662f\u6765\u8d70\u53bb\u7684\u4eba\uff0c\u679c\u7136\u90fd\u4e0d\u559c\u6b22\u7a7f\u978b\u5b50\uff0c\u6709\u4e9b\u8d64\u8db3\uff0c\u6709\u7684\u62d6\u62d6\u978b\uff0c\u5c31\u7b97\u6709\u7a7f\u978b \u5b50\u7684\uff0c\u4e5f\u6ca1\u6709\u5c06\u978b\u8ddf\u62d4\u8d77\u6765\uff0c\u4f46\u4e00\u53cc\u53cc\u5e95\u5e73\u8dbe\u655b\uff0c\u83b9\u767d\u5982\u7389\u7684\u7ea4\u8db3\uff0c\u5957\u5728\u63cf\u91d1\u7684\u6728\u5c50\u91cc\uff0c\u5c82 \u975e\u66f4\u4ee4\u4eba\u5176\u610f\u4e5f\u6d88\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u53c8\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u53ef\u77e5\u9053\u5979\u4eec\u4e3a\u4ec0\u9ebd\u4e0d\u559c\u6b22\u7a7f\u978b\u5b50\u9ebd\uff1f\u8fd9\u539f\u56e0\u6211\u5df2\u53d1\u73b0\u4e86\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u5fcd\u4e0d\u4f4f\u9053\uff1a\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9644\u638c\u9053\uff1a\u201c\u5c31\u56e0\u4e3a\u5979\u4eec\u7684\u811a\u751f\u5f97\u6bd4\u522b\u5904\u7684\u4eba\u6f02\u4eae\uff0c\u82e5\u4e0d\u8ba9\u4eba\u77a7\u77a7\uff0c\u5c82\u975e\u66b4\u6b84\u5929 \u7269\uff1f\u201d \u3000\u3000\u82cf\u5dde\u59d1\u5a18\u4e0d\u4f46\u811a\u751f\u5f97\u7f8e\uff0c\u800c\u4e14\u5927\u591a\u662f\u5929\u8db3\uff0c\u5230\u4e86\u57ce\u5916\uff0c\u5c31\u53ef\u4ee5\u77a7\u89c1\u4e00\u4e2a\u4e2a\u63d0\u8309\u8389\u82b1\u7bee\u7684 \u5c11\u5973\uff0c\u8f7b\u76c8\u5730\u8ffd\u9010\u6765\u5f80\u7684\u8f66\u9a6c\uff0c\u5ffd\u800c\u8dc3\u4e0a\u8f66\u8f95\uff0c\u5ffd\u800c\u8dc3\u4e0b\uff0c\u542c\u5230\u5979\u4eec\u90a3\u5982\u9ec4\u83ba\u5a49\u8f6c\u7684\u5434\u4fac\u8f6f \u8bed\uff0c\u6709\u8c01\u5fcd\u5fc3\u4e0d\u4e70\u5979\u4eec\u4e24\u6735\u82b1\u3002 \u3000\u3000\u57ce\u5916\u4e03\u91cc\uff0c\u624d\u662f\u864e\u4e18\u5c71\u3002 \u3000\u3000\u4f46\u4e00\u51fa\u57ce\u95e8\uff0c\u4fbf\u53ef\u9065\u9065\u671b\u89c1\u90a3\u9752\u8471\u800c\u96c4\u4f1f\u7684\u5c71\u52bf\uff0c\u6b63\u50cf\u662f\u4e00\u53ea\u731b\u864e\u8e72\u8e1e\u5728\u90a3\u91cc\uff0c\u751f\u6c14\u52c3 \u52c3\uff0c\u5934\u5c3e\u5cb8\u7136\u3002 \u3000\u3000\u4ed6\u4eec\u5f92\u6b65\u7a7f\u8fc7\u59d1\u82cf\uff0c\u8fd9\u65f6\u53c8\u56de\u5230\u8f66\u4e0a\uff0c\u80e1\u94c1\u82b1\u6253\u5f00\u8f66\u7a97\uff0c\u77a7\u8fd9\u4e9b\u5e74\u8f7b\u6d3b\u6cfc\u7684\u5c11\u5973\u4eec\uff0c\u5fcd \u4e0d\u4f4f\u5411\u695a\u7559\u9999\u7b11\u9053\uff1a\u201c\u8fd9\u4e9b\u5c0f\u6cf5\u5a18\u8eab\u5b50\u53ef\u771f\u8f7b\u5feb\uff0c\u5012\u771f\u90fd\u662f\u7ec3\u6b66\u7684\u597d\u6750\u6599\uff0c\u82e5\u662f\u7ec3\u8d77\u8f7b\u529f \u6765\uff0c\u6211\u4fdd\u9669\u7edd\u4e0d\u4f1a\u6bd4\u4f60\u5dee\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u5fae\u7b11\u9053\uff1a\u201c\u5979\u4eec\u8fd9\u4e5f\u662f\u4ece\u5c0f\u7ec3\u51fa\u6765\u7684\uff0c\u6bcf\u5929\u4e5f\u4e0d\u77e5\u8981\u5728\u9a6c\u8f66\u4e0a\u8df3\u4e0a\u8df3\u4e0b\u591a\u5c11\u6b21\uff0c \u5b9e\u5728\u6bd4\u6211\u4eec\u7ec3\u529f\u592b\u8fd8\u8981\u52e4\u5feb\u591a\u4e86\u3002\u201d \u3000\u3000\u8bdd\u672b\u8bf4\u5b8c\uff0c\u5df2\u6709\u4e2a\u7a7f\u9752\u5e03\u77ed\u886b\uff0c\u6d41\u6761\u6cb9\u5149\u6c34\u6ed1\u5927\u8fab\u5b50\u7684\u59d1\u5a18\u8df3\u4e0a\u8f66\u8f95\u6765\uff0c\u624b\u91cc\u62ff\u8309\u8389\u82b1 \u7403\uff0c\u76c8\u76c8\u7b11\u9053\uff1a\u201c\u597d\u9999\u597d\u9999\u7684\u8309\u8389\u82b1\uff0c\u516c\u5b50\u7237\u4e70\u4e24\u6735\u5427\uff01\u201d \u3000\u3000\u80e1\u94c1\u82b1\u77a7\u5979\u90a3\u6625\u8471\u822c\u7684\u5c0f\u624b\uff0c\u5fcd\u4e0d\u4f4f\u7b11\u9053\uff1a\u201c\u662f\u82b1\u9999\uff1f\u8fd8\u662f\u4f60\u7684\u624b\u9999\uff1f\u201d \u3000\u3000\u90a3\u5c0f\u6cf5\u5a18\u98de\u7ea2\u4e86\u8138\uff0c\u62bf\u5634\u7b11\u9053\uff1a\u201c\u81ea\u7136\u662f\u82b1\u9999\uff0c\u4e0d\u4fe1\u516c\u5b50\u7237\u5c31\u95fb\u95fb\u770b\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u5927\u7b11\u5c31\u8981\u53bb\u63a5\u82b1\uff0c\u8c01\u77e5\u695a\u7559\u9999\u5374\u5148\u62a2\u4e86\u8fc7\u6765\uff0c\u7b11\u9053\uff1a\u201c\u597d\u82b1\u90fd\u591a\u523a\uff0c\u8fd9\u82b1\u53ef\u6709\u523a \u9ebd\uff1f\u83ab\u8981\u624e\u7834\u4e86\u6211\u7684\u9f3b\u5b50\u3002\u201d \u3000\u3000\u90a3\u5c0f\u6cf5\u5a18\u5403\u5403\u7b11\u9053\uff1a\u201c\u516c\u5b50\u7237\u771f\u4f1a\u8bf4\u7b11\u8bdd\uff0c\u4e16\u4e0a\u90a3\u6709\u591a\u523a\u7684\u8309\u8389\u82b1\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u6211\u5c31\u4e70\u51e0\u6735\u5427\uff0c\u53ea\u53ef\u60dc\u6b64\u82b1\u867d\u597d\uff0c\u5374\u6ca1\u6709\u6234\u82b1\u7684\u4eba\u2026\u2026\u6211\u4e5f\u53ea \u6709\u5c06\u8fd9\u6735\u82b1\u518d\u8f6c\u9001\u7ed9\u59d1\u5a18\u4e86\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u5c06\u5316\u7403\u53c8\u9001\u5230\u90a3\u5c0f\u6cf5\u5a18\u9762\u524d\u3002 \u3000\u3000\u90a3\u5c0f\u6cf5\u5a18\u9762\u8272\u5ffd\u7136\u53d8\u4e86\uff0c\u7adf\u51cc\u7a7a\u4e00\u4e2a\u7ffb\u8eab\uff0c\u9000\u51fa\u4e00\u4e08\u591a\u8fdc\uff0c\u8f6c\u8fc7\u8eab\u5c31\u98de\u4e5f\u4f3c\u7684\u9003\u8d70\u4e86\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u76b1\u7709\u9053\uff1a\u201c\u4f60\u770b\u4f60\u8fd9\u8001\u8272\u9b3c\uff0c\u628a\u4eba\u5bb6\u5c0f\u6cf5\u5a18\u5413\u6210\u8fd9\u6837\u5b50\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6211\u82e6\u4e0d\u5c06\u5979\u9a87\u8d70\uff0c\u5979\u5c31\u8981\u4e86\u4f60\u6211\u7684\u547d\u4e86\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u5931\u58f0\u9053\uff1a\u201c\u4f60\u8bf4\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u4e5f\u4e0d\u7b54\u8bdd\uff0c\u5374\u5c06\u90a3\u8309\u8389\u82b1\u7403\u8f7b\u8f7b\u6495\u788e\uff0c\u53ea\u89c1\u82b1\u7403\u4e2d\u7adf\u8d6b\u7136\u6709\u5341\u51e0\u6839\u53d1\u4e4c\u5149\u7684\u5c0f \u9488\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u9a87\u7136\u9053\uff1a\u201c\u6bd2\u9488\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u82e6\u7b11\u9053\uff1a\u201c\u82e5\u4e0d\u662f\u6211\u63a5\u5f97\u5feb\uff0c\u53ea\u8981\u5b83\u7684\u5c0f\u624b\u4e00\u7529\uff0c\u4f60\u6211\u6b64\u523b\u8fd8\u60f3\u6709\u547d\u9ebd\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9ed8\u7136\u534a\u664c\uff0c\u64e6\u4e86\u64e6\u6c57\uff0c\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u8fd9\u6b21\u4f60\u53c8\u662f\u600e\u9ebd\u770b\u51fa\u6765\u7684\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u53f9\u9053\uff1a\u201c\u8fd9\u4e9b\u5c0f\u6cf5\u5a18\u4ece\u5c0f\u5c31\u5728\u8fd9\u6761\u9053\u4e0a\u5356\u82b1\uff0c\u53ef\u89c1\u90fd\u662f\u7a77\u82e6\u4eba\u5bb6\u7684\u5b69\u5b50\uff0c\u767d\u5929\u5356 \u82b1\uff0c\u665a\u4e0a\u8fd8\u5f97\u5e2e\u505a\u5bb6\u4e8b\uff0c\u90a3\u91cc\u4f1a\u6709\u5979\u90a3\u9ebd\u6837\u53c8\u767d\u53c8\u5ae9\u7684\u624b\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u6014\u4e86\u534a\u664c\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u53cc\u8d3c\u773c\u5b9e\u5728\u592a\u5389\u5bb3\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u8fd8\u6709\uff0c\u8fd9\u4e9b\u5c0f\u6cf5\u5a18\u90fd\u662f\u5728\u8fd9\u91cc\u571f\u751f\u571f\u957f\u7684\uff0c\u600e\u9ebd\u4f1a\u8bf4\u5979\u90a3\u6837\u6807\u51c6\u7684\u5b98\u8bdd\uff0c \u6211\u542c\u5979\u4e00\u5f00\u53e3\uff0c\u5c31\u77e5\u9053\u4e0d\u5bf9\u4e86\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u53f9\u9053\uff1a\u201c\u770b\u6765\u6c5f\u6e56\u4e2d\u7684\u4f20\u8a00\u5e76\u6ca1\u6709\u9519\uff0c\u695a\u7559\u9999\u679c\u7136\u662f\u4e2a\u9b3c\u7075\u7cbe\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u53c8\u538b\u4f4e\u8bed\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u770b\u8fd9\u5c0f\u6cf5\u5a18\u4e5f\u662f\u4ed6\u592b\u59bb\u6d3e\u6765\u7684\u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u5c06\u6bd2\u9488\u5168\u90fd\u7528\u4e00\u5757\u65b9\u5dfe\u5305\u4e86\u8d70\u6765\uff0c\u9053\uff1a\u201c\u5230\u4e86\u8fd9\u91cc\uff0c\u600e\u9ebd\u8fd8\u4f1a\u6709\u522b\u4eba\uff0c\u8fd9\u6b21\u4e8b\u82e5 \u6210\u4e86\uff0c\u4ed6\u4eec\u56fa\u7136\u53ef\u4ee5\u63a8\u8bf4\u4e0d\u77e5\u9053\uff0c\u4e8b\u82e5\u4e0d\u6210\uff0c\u66f4\u548c\u4ed6\u4eec\u6ca1\u6709\u534a\u70b9\u5173\u7cfb\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9ed8\u7136\u534a\u664c\uff0c\u5583\u5583\u9053\uff1a\u201c\u770b\u6765\u6211\u4eec\u73b0\u5728\u8981\u53bb\u7684\u5730\u65b9\u5e76\u4e0d\u662f\u864e\u4e18\uff0c\u800c\u662f\u864e\u7a74\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u5fae\u7b11\u9053\uff1a\u201c\u4e0d\u5165\u864e\u7a74\uff0c\u7109\u5f97\u864e\u5b50\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u7b11\u9053\uff1a\u201c\u4e5f\u4e0d\u662f\u864e\u5b50\uff0c\u662f\u864e\u5973\u3002\u201d \u3000\u3000\u5230\u4e86\u864e\u80dc\u6b63\u5c71\u95e8\u524d\uff0c\u5927\u5bb6\u5c31\u4e0b\u4e86\u9a6c\uff0c\u674e\u7389\u51fd\u548c\u67f3\u65e0\u7709\u4ecd\u662f\u8c08\u7b11\u98ce\u751f\uff0c\u5c31\u50cf\u662f\u6839\u672c\u4e0d\u77e5\u9053 \u65b9\u53d1\u751f\u8fc7\u4ec0\u9ebd\u4e8b\u3002 \u3000\u3000\u90a3\u5c0f\u6cf5\u5a18\u83ab\u975e\u548c\u4ed6\u4eec\u65e0\u5173\uff1f \u3000\u3000\u695a\u7559\u9999\u83ab\u975e\u53c8\u731c\u9519\u4e86\uff1f \u3000\u3000\u5c71\u95e8\u5916\uff0c\u6709\u4e2a\u5c0f\u5c0f\u7684\u5e02\u96c6\uff0c\u5c0f\u6cb3\u4e00\u9053\uff0c\u873f\u8713\u6d41\u8fc7\uff0c\u6cb3\u7554\u505c\u53c1\u4e94\u753b\u822b\uff0c\u753b\u822b\u4e2d\u4e0d\u65f6\u4f20\u51fa\u94f6 \u94c3\u822c\u7684\u5a07\u7b11\u3002 \u3000\u3000\u5165\u4e86\u5c71\u95e8\uff0c\u4e24\u65c1\u4e5f\u6709\u8bb8\u591a\u5c0f\u8086\uff0c\u8fd8\u6709\u8bb8\u8bb8\u591a\u591a\u4e5e\u4e10\uff0c\u770b\u5230\u6709\u4eba\u6765\u4e86\uff0c\u5c31\u56f4\u4e0a\u6765\u4e5e\u8ba8\uff0c\u8fd8 \u6709\u4eba\u8fdc\u8fdc\u5c31\u606d\u8eab\u966a\u7b11\u9053\uff1a\u201c\u674e\u516c\u5b50\u56de\u6765\u4e86\u9ebd\uff1f\u592b\u4eba\u597d\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u548c\u80e1\u94c1\u82b1\u5bf9\u671b\u4e86\u4e00\u773c\uff0c\u5fc3\u91cc\u5374\u5728\u6697\u6697\u731c\u6d4b\uff1a\u201c\u4e0d\u77e5\u9053\u8fd9\u4e9b\u4e5e\u4e10\u4e2d\uff0c\u6709\u6ca1\u6709\u771f\u6b63\u7684 \u4e10\u5e2e\u5f1f\u4e8e\uff1f\u201d \u3000\u3000\u601d\u5fd6\u65f6\uff0c\u5df2\u5230\u4e86\u90a3\u95fb\u540d\u7684\u5343\u4eba\u77f3\u3002 \u3000\u3000\u53ea\u89c1\u4e00\u65b9\u5927\u77f3\uff0c\u53ef\u5750\u5343\u4eba\uff0c\u4e00\u773c\u671b\u53bb\uff0c\u975e\u4f46\u770b\u4e0d\u5230\u8fb9\uff0c\u8fde\u4e00\u6839\u5c0f\u8349\u4e5f\u770b\u4e0d\u5230\uff0c\u5927\u77f3\u7684\u5317 \u9762\u8fd8\u6709\u4e2a\u5c0f\u5c0f\u7684\u77f3\u53f0\u3002 \u3000\u3000\u53ea\u542c\u67f3\u65e0\u7709\u60a0\u7136\u9053\uff1a\u201c\u6545\u8001\u76f8\u4f20\uff0c\u6614\u65e5\u5434\u738b\u9616\u95fe\u5728\u8fd9\u91cc\u9020\u575f\u5893\uff0c\u7528\u4e86\u5de5\u5320\u5343\u4eba\uff0c\u7b49\u5230\u5893 \u6210\u4e4b\u5f8c\uff0c\u5434\u738b\u6015\u4ed6\u4eec\u6cc4\u6f0f\u5893\u4e2d\u7684\u673a\u5173\u79d8\u5bc6\uff0c\u5c31\u628a\u8fd9\u4e00\u5343\u4eba\u5168\u90fd\u6d3b\u57cb\u5728\u8fd9\u77f3\u5934\u4e0b\uff0c\u6240\u4ee5\u8fd9\u77f3\u5934 \u5c31\u53eb\u505a\u201c\u5343\u4eba\u77f3\u201d\u3002\u201d \u3000\u3000\u8fd9\u6b8b\u9177\u7684\u6545\u4e8b\uff0c\u4ece\u5979\u5634\u91cc\u5a13\u5a13\u8bf4\u6765\uff0c\u5374\u50cf\u662f\u8fbe\u4e00\u4e1d\u8840\u8165\u6c14\u90fd\u6ca1\u6709\u4e86\uff0c\u80e1\u94c1\u82b1\u5fcd\u4e0d\u4f4f\u95ee \u9053\uff1a\u201c\u90a3\u77f3\u53f0\u53c8\u662f\u4ec0\u9ebd\u5462\uff1f\u201d \u3000\u3000\u67f3\u65e0\u7709\u9053\uff1a\u201c\u90a3\u5c31\u662f\u795e\u50e7\u7afa\u9053\u751f\u7684\u8bb2\u7ecf\u53f0\uff0c\u4e0a\u9762\u8fd8\u6709\u5510\u4ee3\u674e\u9633\u51b0\u7684\u56db\u4e2a\u7bc6\u5b57\uff0c\u4e3a\u7684\u5c31\u662f \u201c\u751f\u516c\u8bb2\u53f0\u201d\uff0c\u767d\u83b2\u6c60\u65c1\u7684\u90a3\u5757\u77f3\u5934\uff0c\u5c31\u662f\u6709\u540d\u7684\u70b9\u5934\u77f3\uff0c\u5e38\u8a00\u9053\uff1a\u201c\u751f\u516c\u8bf4\u6cd5\uff0c\u987d\u77f3\u70b9 \u5934\u201d\uff0c\u8fd9\u5178\u6545\u5c31\u4ece\u6b64\u5904\u6765\u7684\u3002\u201d \u3000\u3000\u5979\u6b65\u5c65\u5c31\u548c\u8bed\u58f0\u540c\u6837\u8f7b\u76c8\uff0c\u5c71\u98ce\u81ea\u77f3\u5f8c\u5439\u6765\uff0c\u5439\u6563\u4e86\u5979\u7684\u53d1\u9afb\uff0c\u5439\u821e\u8d77\u5979\u7684\u8863\u895f\uff0c\u5979\u6574 \u4e2a\u4eba\u90fd\u4f3c\u5c06\u4e58\u98ce\u800c\u53bb\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u542c\u5f97\u75f4\u4e86\uff0c\u4e5f\u77a7\u5f97\u75f4\u4e86\uff0c\u5fc3\u91cc\u5374\u4e0d\u7981\u6697\u6697\u53f9\u606f\u9053\uff1a\u201c\u8fd9\u9ebd\u6837\u4e00\u4e2a\u4ed9\u5b50\u822c\u7684\u7f8e\u5973\uff0c \u771f\u4f1a\u662f\u6740\u4eba\u7684\u51f6\u624b\u9ebd\uff1f\u201d \u3000\u3000\u7136\u5f8c\u4ed6\u4eec\u5c31\u8d70\u4e0a\u5251\u6c60\u3002 \u3000\u3000\u53ea\u89c1\u56db\u9762\u6797\u6728\u68ee\u68ee\uff0c\u8427\u78a7\u5e7d\u7fe0\u3002\u4e00\u9053\u6728\u6865\u5982\u5f69\u8679\u822c\u6a2a\u5367\u6c60\u4e0a\uff0c\u6c60\u6c34\u9752\u7eff\u800c\u51b7\u51bd\uff0c\u4e0a\u9762\u70b9 \u70b9\u6d6e\u840d\u3002 \u3000\u3000\u695a\u7559\u9999\u7ad9\u5728\u6c60\u7554\uff0c\u4fbf\u89c9\u4e00\u80a1\u6e05\u5bd2\u4e4b\u6c14\u6251\u9762\u800c\u6765\uff0c\u9752\u78a7\u7684\u6c60\u6c34\u4e2d\uff0c\u7adf\u50cf\u662f\u9690\u85cf\u9635\u9635\u6740\u6c14\u3002 \u3000\u3000\u8fdc\u5904\u79cb\u4e91\u56db\u5408\u6e05\u98ce\u4e2d\u6709\u66ae\u949f\u58f0\u7f25\u7f08\u4f20\u6765\u3002 \u3000\u3000\u695a\u7559\u9999\u5fae\u7b11\u9053\uff1a\u201c\u5510\u4ee3\u540d\u58eb\u674e\u79c0\u537f\uff0c\u54c1\u8bc4\u6b64\u6c34\u4e3a\u5929\u4e0b\u7b2c\u4e94\u6cc9\uff0c\u5374\u4e0d\u77e5\u6b64\u6c34\u6700\u5b9c\u6dec\u5251\uff0c\u6b63 \u662f\u53e4\u5251\u5ba2\u7684\u6dec\u5251\u4e4b\u5730\uff0c\u5728\u53c8\u6709\u5f53\u4ee3\u7b2c\u4e00\u5251\u5ba2\u674e\u8001\u524d\u8f88\u65f6\u6765\u54c1\u9898\uff0c\u8fd9\u201c\u5251\u6c60\u201d\u4e8c\u5b57\uff0c\u5012\u4e5f\u771f\u53ef \u8bf4\u662f\u540d\u4e0b\u65e0\u865a\u4e86\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u5a9a\u7136\u9053\uff1a\u201c\u636e\u8bf4\u8fd9\u540d\u5b57\u8fd8\u6709\u4e2a\u6765\u5386\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u5594\uff1f\u201d \u3000\u3000\u67f3\u65e0\u7709\u9053\uff1a\u201c\u76f8\u4f20\u5434\u738b\u9616\u95fe\u7684\u575f\u5893\u5c31\u5728\u8fd9\u5251\u6c60\u4e0b\uff0c\u4ed6\u6b7b\u65f6\u5ddd\u53c1\u5341\u67c4\u540d\u5251\u6b89\u846c\uff0c\u8fde\u4e13\u8bf8\u7528 \u7684\u9c7c\u85cf\u5251\u7b49\u4e5f\u5728\u5176\u4e2d\uff0c\u6240\u4ee5\u8fd9\u91cc\u624d\u53eb\u505a\u5251\u6c60\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u82e5\u4e5f\u846c\u8eab\u6b64\u5904\uff0c\u548c\u5434\u738b\u9616\u95fe\u8fd9\u6837\u7684\u96c4\u9b3c\u4e3a\u90bb\uff0c\u5012\u4e5f\u53ef\u7b97\u662f\u4e0d \u865a\u6b64\u751f\uff0c\u6b7b\u5f97\u5176\u6240\u4e86\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u795e\u8272\u4e0d\u52a8\uff0c\u5ae3\u7136\u7b11\u9053\uff1a\u201c\u695a\u5144\u65e2\u7136\u77e5\u9053\u8fd9\u662f\u5929\u4e0b\u7b2c\u4e94\u6cc9\uff0c\u53ef\u77e5\u9053\u5929\u4e0b\u7b2c\u53c1\u6cc9\u4e5f\u5728 \u8fd9\u91cc\u9ebd\uff1f\u201d \u3000\u3000\u7ed5\u8fc7\u5251\u6c60\uff0c\u5c31\u53ef\u77a7\u89c1\u4e00\u4e2a\u5f88\u5927\u5f88\u5927\u7684\u77f3\u4e95\uff0c\u9762\u9614\u4e08\u9980\uff0c\u4e95\u65c1\u8fd8\u6709\u4e2a\u6731\u680f\u66f2\u7ed5\u7ea6\u516d\u89d2\u5c71 \u4ead\u3002 \u3000\u3000\u695a\u7559\u9999\u7b11\u9053\uff1a\u201c\u8fd9\u91cc\u6015\u5c31\u662f\u5929\u4e0b\u7b2c\u53c1\u6cc9\u201c\u9646\u7fbd\u8336\u4e95\u201d\u4e86\uff0c\u6614\u5e74\u674e\u8001\u524d\u8f88\u9080\u96c6\u4e86\u5929\u4e0b\u540d\u5251 \u5ba2\uff0c\u5728\u8fd9\u91cc\u70f9\u8336\u54c1\u5251\uff0c\u524d\u8f88\u98ce\u6d41\uff0c\u5b9e\u5728\u4ee4\u4eba\u4e0d\u80dc\u4ef0\u6155\u4e4b\u81f3\u3002\u201d \u3000\u3000\u7a81\u7136\u4e00\u4eba\u957f\u53f9\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u6c5f\u5c71\u867d\u4f9d\u65e7\uff0c\u4eba\u9762\u5374\u5df2\u5168\u975e\u4e86\u3002\u201d \u7b2c\u4e5d\u7ae0\u3000\u5929\u4e0b\u65e0\u654c>> \u53e4\u9f99\u300a\u695a\u7559\u9999\u7cfb\u5217\xb7\u753b\u7709\u9e1f\u300b \u7b2c\u4e5d\u7ae0\u3000\u5929\u4e0b\u65e0\u654c \u3000\u3000\u8fd9\u65f6\u66ae\u8272\u5df2\u4e34\uff0c\u665a\u971e\u6d41\u4e3d\uff0c\u5c71\u5dc5\u4e0a\u7684\u864e\u4e18\u5854\u5f71\u95f4\uff0c\u6709\u5b64\u9e70\u76d8\u65cb\uff0c\u5374\u5c06\u8fd9\u5982\u56fe\u753b\u822c\u7684\u7f8e\u666f \u886c\u6258\u5f97\u65e0\u6bd4\u82cd\u51c9\u800c\u8427\u7d22\u3002 \u3000\u3000\u8fd9\u4e00\u58f0\u53f9\u606f\u4e5f\u6b63\u662f\u65e0\u6bd4\u7684\u82cd\u51c9\uff0c\u65e0\u6bd4\u7684\u8427\u7d22\u3002 \u3000\u3000\u53ea\u89c1\u4e00\u7f15\u5b64\u70df\uff0c\u81ea\u90a3\u516d\u89d2\u5c71\u4ead\u4e2d\u8885\u5a1c\u98de\u51fa\uff0c\u77ac\u5373\u56db\u6563\uff0c\u7f25\u7f08\u7684\u70df\u96fe\u4e2d\uff0c\u51c4\u51c9\u7684\u4e0a\u4ead\u91cc\uff0c \u6b63\u6709\u4e2a\u7fbd\u8863\u9ad8\u51a0\u7684\u767d\u53d1\u8001\u8005\uff0c\u5728\u72ec\u5750\u70f9\u8336\uff0c\u4ed6\u7684\u5bc2\u5bde\uff0c\u770b\u6765\u4e5f\u6b63\u548c\u90a3\u5728\u7edd\u5dc5\u9ad8\u5854\u65c1\u76d8\u65cb\u7684\u5b64 \u9e70\u4e00\u6837\u3002 \u3000\u3000\u695a\u7559\u9999\u76ee\u5149\u95ea\u52a8\uff0c\u901a\uff1a\u201c\u8001\u5148\u751f\u6614\u5e74\u83ab\u975e\u4e5f\u662f\u5728\u6b64\u95f4\u70f9\u8336\u54c1\u5251\u7684\u76db\u4f1a\u4e2d\u4eba\u9ebd\uff1f\u201d \u3000\u3000\u90a3\u8001\u8005\u53c8\u957f\u957f\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u53ea\u53ef\u60dc\u6545\u4eba\u4eec\u591a\u5df2\u4ed9\u53bb\uff0c\u53ea\u5269\u4e0b\u8001\u673d\u767e\u75c5\u4e4b \u8eab\uff0c\u8fd8\u5728\u4eba\u95f4\u6d41\u6d6a\uff0c\u518d\u60f3\u627e\u4e00\u4e2a\u80fd\u4f34\u6211\u5728\u6b64\u70f9\u8336\u8bd5\u5251\u7684\u4eba\uff0c\u4ea6\u4e0d\u53ef\u5f97\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u548c\u80e1\u94c1\u82b1\u5bf9\u671b\u4e00\u773c\uff0c\u5fc3\u91cc\u5374\u4e0d\u89c9\u6709\u4e9b\u5bd2\u610f\u3002 \u3000\u3000\u6614\u5e74\u80fd\u5728\u8fd9\u91cc\u70f9\u8336\u54c1\u5251\u7684\u4eba\uff0c\u53ef\u8bf4\u65e0\u4e00\u4e0d\u662f\u7edd\u9876\u7684\u5251\u5ba2\uff0c\u81f3\u4eca\u82e5\u80fd\u4e0d\u6b7b\uff0c\u5251\u6cd5\u65e0\u7591\u66f4\u51fa \u795e\u5165\u5316\u3002 \u3000\u3000\u8fd9\u8001\u8005\u6070\u5de7\u5728\u4eca\u65e5\u65e7\u5730\u91cd\u6e38\uff0c\u67af\u5750\u6b64\u95f4\uff0c\u60f3\u6765\u5fc5\u975e\u5076\u7136\uff0c\u4ed6\u82e5\u662f\u5728\u7b49\u4eba\uff0c\u90a3\u9ebd\u7b49\u7684\u662f\u8c01 \u5462\uff1f \u3000\u3000\u80e1\u94c1\u82b1\u5fcd\u4e0d\u4f4f\u9053\uff1a\u201c\u4e0d\u77e5\u8001\u5148\u751f\u5c0a\u59d3\u5927\u540d\uff1f\u201d \u3000\u3000\u90a3\u8001\u8005\u5e76\u672a\u56de\u5934\uff0c\u53ea\u662f\u7f13\u7f13\u9053\uff1a\u201c\u8001\u673d\u5e05\u4e00\u5e06\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u8038\u7136\u9053\uff1a\u201c\u83ab\u975e\u662f\u6614\u5e74\u4e00\u5251\u52a8\u53c1\u5c71\uff0c\u529b\u65a9\u8fc7\u5929\u661f\u7684\u201c\u6458\u661f\u7fbd\u58eb\u201d\u5e05\u8001\u524d\u8f88\uff1f\u201d \u3000\u3000\u90a3\u8001\u8005\u970d\u7136\u7ad9\u8d77\uff0c\u4ef0\u5929\u957f\u7b11\uff0c\u5c71\u4ead\u56db\u9762\u7684\u79cb\u53f6\uff0c\u90fd\u88ab\u4ed6\u7684\u7b11\u58f0\u9707\u5f97\u6709\u5982\u96ea\u82b1\u822c\u74e2\u74e2\u843d \u4e0b\u3002 \u3000\u3000\u53ea\u542c\u4ed6\u957f\u7b11\u9053\uff1a\u201c\u695a\u7559\u9999\u679c\u7136\u4e0d\u51e1\uff0c\u8001\u673d\u65b0\u8336\u521d\u6cb8\uff0c\u9601\u4e0b\u4f55\u4e0d\u8fdb\u6765\u5171\u996e\u4e00\u676f\u3002\u201d \u3000\u3000\u4ed6\u751a\u81f3\u8fde\u5934\u90fd\u6ca1\u6709\u56de\uff0c\u5df2\u77e5\u9053\u6765\u7684\u662f\u695a\u7559\u9999\u4e86\uff0c\u663e\u7136\u65e9\u5df2\u5f97\u5230\u4e86\u674e\u7389\u51fd\u7684\u6d88\u606f\uff0c\u6b63\u662f\u5728 \u8fd9\u91cc\u7b49\u695a\u7559\u9999\u7684\u3002 \u3000\u3000\u518d\u770b\u674e\u7389\u51fd\u592b\u59bb\uff0c\u4e0d\u77e5\u4f55\u65f6\u5df2\u8e2a\u5f71\u4e0d\u89c1\u3002 \u3000\u3000\u695a\u7559\u9999\u6697\u4e2d\u53f9\u4e86\u53e3\u6c14\uff0c\u9762\u4e0a\u5374\u5fae\u7b11\u9053\uff1a\u201c\u70f9\u8336\u5728\u4e0b\u5949\u966a\uff0c\u82e5\u8981\u8bd5\u5251\u9ebd\uff0c\u5728\u4e0b\u5374\u2026\u2026\u201d \u3000\u3000\u5e05\u4e00\u5e06\u970d\u7136\u56de\u9996\uff0c\u5389\u58f0\u9053\uff1a\u201c\u5374\u600e\u6837\uff1f\u8001\u673d\u4eba\u867d\u5df2\u8001\uff0c\u5251\u5374\u8fd8\u672a\u8001\u54e9\uff01\u201d \u3000\u3000\u53ea\u542c\u201c\u545b\u201d\u7684\u4e00\u58f0\u9f99\u541f\uff0c\u4ed6\u638c\u4e2d\u5df2\u591a\u4e86\u67c4\u78a7\u5982\u79cb\u6c34\u7684\u957f\u5251\uff0c\u695a\u7559\u9999\u7ad9\u5728\u6570\u4e08\u5916\uff0c\u5df2\u89c9\u5251 \u6c14\u903c\u4eba\u7709\u776b\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u7adf\u5fcd\u4e0d\u4f4f\u8131\u53e3\u8d5e\u9053\uff1a\u201c\u597d\u5251\uff01\u201d \u3000\u3000\u5e05\u4e00\u5e06\u50b2\u7136\u7b11\u9053\uff1a\u201c\u81ea\u7136\u662f\u597d\u5251\u3002\u201d \u3000\u3000\u4ed6\u76ee\u5149\u4f3c\u4e4e\u6bd4\u5251\u5149\u66f4\u5389\uff0c\u77aa\u695a\u7559\u9999\u9053\uff1a\u201c\u8001\u592b\u6b64\u5251\u5df2\u6709\u5341\u53c1\u5e74\u672a\u66fe\u79bb\u9798\uff0c\u4eca\u65e5\u4e3a\u4f60\u800c \u51fa\uff0c\u4f60\u4e5f\u53ef\u5f15\u4ee5\u4e3a\u50b2\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u957f\u53f9\u9053\uff1a\u201c\u540d\u5251\u51fa\u9798\uff0c\u4f8b\u4e0d\u7a7a\u56de\uff0c\u524d\u8f88\u4eca\u65e5\u83ab\u975e\u5b9a\u8981\u53d6\u5728\u4e0b\u9879\u4e0a\u4eba\u5934\u9ebd\uff1f\u201d \u3000\u3000\u5e05\u4e00\u5e06\u5389\u58f0\u9053\uff1a\u201c\u6211\u8f88\u6b66\u592b\uff0c\u6b63\u5f53\u6b7b\u5728\u5251\u4e0b\uff0c\u4f60\u96be\u9053\u8fd8\u6015\u6b7b\u4e0d\u6210\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9ed8\u7136\u534a\u664c\uff0c\u9053\uff1a\u201c\u524d\u8f88\u82e5\u5b9a\u8981\u8d50\u6559\uff0c\u5728\u4e0b\u4e5f\u53ea\u6709\u5949\u966a\uff0c\u4f46\u5374\u8981\u8bf7\u6559\u524d\u8f88\u4e00\u4ef6\u4e8b\uff0c \u4ee5\u524d\u8f88\u7684\u58f0\u671b\uff0c\u60f3\u5fc5\u4e0d\u81f4\u9690\u7792\u3002\u201d \u3000\u3000\u5e05\u4e00\u5e06\u9053\uff1a\u201c\u4ec0\u9ebd\u4e8b\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u5728\u4e0b\u4e0e\u524d\u8f88\u7d20\u65e0\u6028\u4ec7\uff0c\u524d\u8f88\u5374\u5b9a\u8981\u53d6\u5728\u4e0b\u6027\u547d\uff0c\u83ab\u975e\u662f\u53d7\u4eba\u6240\u6258\uff1f\u201d \u3000\u3000\u5e05\u4e00\u5e06\u6d53\u7709\u8f69\u8d77\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f46\u5bf9\u624b\u82e5\u975e\u695a\u7559\u9999\uff0c\u8001\u592b\u8fd8\u4e0d\u5c51\u52a8\u624b\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5728\u4e0b\u82e5\u8fd8\u8981\u95ee\u524d\u8f88\u662f\u53d7\u8c01\u6240\u6258\uff0c\u524d\u8f88\u60f3\u5fc5\u4e5f\u4e0d\u80af\u8bf4\u7684\uff0c\u53ea\u4e0d\u8fc7 \u524d\u8f88\u7eb5\u7136\u4e0d\u8bf4\uff0c\u5728\u4e0b\u4e5f\u80fd\u731c\u5f97\u51fa\u4e03\u516b\u5206\u3002\u201d \u3000\u3000\u5e05\u4e00\u5e06\u9053\uff1a\u201c\u5f88\u597d\uff0c\u62d4\u51fa\u4f60\u7684\u5175\u5203\u6765\u52a8\u624b\u5427\uff01\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u9075\u547d\u3002\u201d \u3000\u3000\u77ed\u77ed\u4e24\u4e2a\u5b57\u8fd8\u672b\u8bf4\u5b8c\uff0c\u4ed6\u8eab\u5f62\u5ffd\u7136\u51b2\u5929\u800c\u8d77\uff0c\u63a0\u5230\u4e00\u682a\u6728\u53f6\u672b\u67af\u7684\u5927\u6811\u4e0a\uff0c\u91c7\u4e0b\u4e86\u4e00\u6761 \u67d4\u679d\u5e05\u4e00\u5e06\u53f7\u79f0\u201c\u6458\u661f\u201d\uff0c\u8f7b\u7a7a\u4e4b\u9ad8\uff0c\u81ea\u53ef\u60f3\u50cf\uff0c\u4f46\u4ed6\u89c1\u5230\u695a\u7559\u9999\u8fd9\u4e00\u8dc3\u4e4b\u52bf\uff0c\u4ecd\u4e0d\u7981\u4e3a\u4e4b \u58f0\u7136\u5931\u8272\u3002 \u3000\u3000\u53ea\u89c1\u695a\u7559\u9999\u5c06\u90a3\u6bb5\u67d4\u679d\u62d7\u6210\u4e94\u5c3a\u957f\u77ed\uff0c\u679d\u5934\u8fd8\u7559\u53c1\u4e94\u7247\u6811\u53f6\uff0c\u4ed6\u6a2a\u679d\u5f53\u80f8\uff0c\u793a\u793c\u9053\uff1a \u201c\u524d\u8f88\u8bf7\u3002\u201d \u3000\u3000\u5e05\u4e00\u5e06\u76b1\u7709\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u4f60\u7684\u5175\u5668\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u6b63\u662f\u3002\u201d \u3000\u3000\u5e05\u4e00\u5e06\u6012\u9053\uff1a\u201c\u597d\u8f7b\u72c2\u7684\u5c11\u5e74\u4eba\uff0c\u7eb5\u662f\u674e\u89c2\u9c7c\uff0c\u4e5f\u4e0d\u6562\u5bf9\u8001\u592b\u5982\u6b64\u8f7b\u6162\u65e0\u793c\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u5728\u4e0b\u6beb\u65e0\u4e0d\u656c\u4e4b\u610f\u3002\u201d \u3000\u3000\u5e05\u4e00\u5e06\u6012\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u662f\u4ec0\u9ebd\u610f\u601d\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u53ea\u8981\u8fd0\u7528\u5f97\u5f53\uff0c\u5927\u5730\u4e07\u7269\uff0c\u83ab\u4e0d\u662f\u4f24\u4eba\u7684\u5229\u5668\uff0c\u82e5\u662f\u8fd0\u7528\u4e0d \u5f53\uff0c\u7eb5\u662f\u4e0a\u53e4\u795e\u5175\uff0c\u4e5f\u96be\u4f24\u4eba\u6beb\u53d1\uff0c\u524d\u8f88\u9ad8\u4eba\uff0c\u600e\u4f1a\u4e0d\u89e3\u6b64\u610f\uff1f\u201d \u3000\u3000\u8fd9\u4e24\u53e5\u8bdd\u4ed6\u6de1\u6de1\u8bf4\u6765\uff0c\u5176\u4e2d\u5374\u5145\u6ee1\u4e86\u903c\u4eba\u7684\u950b\u8292\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u6697\u4e2d\u4e00\u7b11\uff0c\u77e5\u9053\u8fd9\u4e5f\u662f\u695a\u7559\u9999\u7684\u6218\u7565\u4e4b\u4e00\uff0c\u5bf9\u624b\u82e5\u662f\u592a\u5f3a\uff0c\u695a\u7559\u9999\u5c31\u4e00\u5b9a\u8981\u5148\u6740 \u6740\u4ed6\u7684\u5a01\u98ce\u50b2\u6c14\u3002 \u3000\u3000\u4f55\u51b5\uff0c\u5e05\u4e00\u5e06\u638c\u4e2d\u8fd9\u67c4\u5251\uff0c\u663e\u7136\u662f\u524a\u94c1\u5982\u6ce5\u7684\u5229\u5668\uff0c\u695a\u7559\u9999\u82e5\u4ee5\u91d1\u94c1\u4e4b\u5c5e\u548c\u4ed6\u52a8\u624b\uff0c\u4e5f \u6321\u4e0d\u4e86\u4ed6\u5b9d\u5251\u7684\u94b1\u9510\u3002 \u3000\u3000\u6b64\u756a\u4ed6\u4ee5\u67d4\u679d\u5e94\u654c\uff0c\u53d6\u7684\u6b63\u662f\u201c\u4ee5\u67d4\u514b\u521a\u201d\u4e4b\u610f\uff0c\u5c31\u7b97\u5360\u4e0d\u4e86\u4ec0\u9ebd\u4fbf\u5b9c\uff0c\u81f3\u5c11\u4e5f\u4e0d\u4f1a\u5403 \u592a\u5927\u7684\u4e8f\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u73b0\u5728\u624d\u77e5\u9053\u695a\u7559\u9999\u4e0e\u4eba\u4ea4\u624b\u65f6\u7684\u673a\u667a\uff0c\u786e\u975e\u4ed6\u4eba\u80fd\u53ca\uff0c\u4ed6\u51e0\u4e4e\u5fcd\u4e0d\u4f4f\u8981\u529d\u529d\u5e05\u4e00 \u5e06\u3002 \u3000\u3000\u201c\u4f60\u4f55\u5fc5\u5b9a\u8981\u548c\u8fd9\u8001\u81ed\u866b\u52a8\u624b\u5462\uff01\u201c\u6458\u661f\u7fbd\u58eb\u201d\u8fd9\u540d\u5b57\u5e76\u4e0d\u662f\u5bb9\u6613\u5f97\u6765\u7684\uff0c\u4f60\u4f55\u82e6\u5b9a\u8981 \u5c06\u4e4b\u6bc1\u65bc\u4e00\u65e6\uff1f\u201d \u3000\u3000\u5c71\u4ead\u4e2d\u7684\u8336\u70df\u5df2\u6563\u4e86\u3002 \u3000\u3000\u5e05\u4e00\u5e06\u4e0d\u518d\u8bf4\u8bdd\uff0c\u4e00\u6b65\u6b65\u8d70\u4e86\u51fa\u6765\uff0c\u4ed6\u811a\u6b65\u8d70\u5f97\u6781\u7f13\uff0c\u53ea\u8d70\u4e86\u4e24\u6b65\uff0c\u80e1\u94c1\u82b1\u5df2\u5403\u4e86\u4e00 \u60ca\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u5c11\u5e74\u65f6\u597d\u52c7\u6597\u72e0\uff0c\u957f\u5927\u5f8c\u813e\u6c14\u4e5f\u6ca1\u6709\u6539\u5f88\u591a\uff0c\u5e73\u751f\u4e0e\u4eba\u52a8\u624b\u6253\u67b6\uff0c\u7b80\u76f4\u8ddf\u5403\u5bb6\u5e38 \u4fbf\u996d\u4e00\u6837\u3002 \u3000\u3000\u8fd9\u5341\u591a\u5e74\u6765\uff0c\u4e5f\u53ef\u8bf4\u4ec0\u9ebd\u6837\u7684\u5bf9\u624b\u90e8\u9047\u89c1\u8fc7\uff0c\u5176\u4e2d\u81ea\u7136\u4e5f\u6709\u4e00\u4e9b\u5251\u6cd5\u6709\u72ec\u5230\u5904\u7684\u5251\u672f\u540d \u5bb6\u3002 \u3000\u3000\u8fd9\u4e9b\u4eba\u5251\u6cd5\u6709\u7684\u8f7b\u7075\uff0c\u6709\u7684\u8f9b\u6377\uff0c\u6709\u7684\u72e0\u8fa3\uff0c\u4f46\u65e0\u8bba\u4ec0\u9ebd\u4eba\uff0c\u4e5f\u90fd\u8981\u7b49\u5230\u5251\u5f0f\u524c\u51fa\u5f8c\uff0c \u624d\u80fd\u7ed9\u522b\u4eba\u5a01\u80c1\u3002 \u3000\u3000\u53ef\u662f\u6b64\u523b\u8fd9\u201c\u6458\u661f\u7fbd\u58eb\u201d\u5e05\u4e00\u5e06\uff0c\u4ed6\u975e\u4f46\u957f\u5251\u8fd8\u672b\u51fa\u624b\uff0c\u751a\u81f3\u8fde\u4eba\u90fd\u8fd8\u6ca1\u6709\u8d70\u51fa\u6765\uff0c\u80e1 \u94c1\u82b1\u5c31\u5df2\u9690\u9690\u89c9\u51fa\u4ed6\u5251\u6c14\u7684\u903c\u4eba\u4e86\u2014\u2014\u4ed6\u6574\u4e2a\u4eba\u90fd\u50cf\u662f\u5df2\u88ab\u78e8\u70bc\u6210\u4e00\u628a\u5200\u5b50\uff0c\u5168\u8eab\u90fd\u6563\u53d1\u51fa \u903c\u4eba\u7684\u6740\u6c14\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u8eab\u5728\u5c40\u5916\uff0c\u5df2\u6709\u8fd9\u79cd\u611f\u89c9\uff0c\u4f55\u51b5\u695a\u7559\u9999\u3002 \u3000\u3000\u8c01\u4e5f\u60f3\u4e0d\u5230\u8fd9\u7fbd\u8863\u9ad8\u51a0\uff0c\u74e2\u7136\u6709\u51fa\u5c18\u4e4b\u60f3\u7684\u8001\u8005\uff0c\u7adf\u80fd\u5728\u5239\u90a3\u4e4b\u95f4\uff0c\u53d8\u5f97\u5982\u6b64\u950b\u5229\u53ef \u6015\u3002 \u3000\u3000\u5c71\u98ce\u5439\u8fc7\uff0c\u5c06\u4ed6\u7684\u8863\u886b\u5439\u5f97\u730e\u730e\u98de\u821e\uff0c\u4ed6\u7684\u811a\u6b65\u4e5f\u59cb\u7ec8\u4e0d\u505c\u5730\u5411\u5916\u8d70\uff0c\u4f46\u522b\u4eba\u7adf\u4f3c\u89c9\u4e0d \u51fa\u4ed6\u8eab\u5b50\u5728\u52a8\u3002 \u3000\u3000\u53ea\u56e0\u4ed6\u5df2\u5c06\u5168\u8eab\u7684\u7cbe\u795e\u6c14\u529b\uff0c\u90fd\u5316\u4e3a\u4e00\u80a1\u5251\u6c14\uff0c\u522b\u4eba\u53ea\u80fd\u89c9\u51fa\u4ed6\u5251\u6c14\u7684\u903c\u4eba\uff0c\u5df2\u5fd8\u4e86\u4ed6 \u81ea\u8eab\u7684\u5b58\u5728\u3002 \u3000\u3000\u4ed6\u7684\u4eba\u5df2\u548c\u5251\u6c14\u6eb6\u800c\u4e3a\u4e00\uff0c\u5145\u6c9b\u5728\u5929\u5730\u95f4\uff0c\u6240\u4ee5\u4ed6\u52a8\u7684\u65f6\u5019\uff0c\u4e5f\u4f3c\u4e0d\u52a8\uff0c\u4e0d\u52a8\u7684\u65f6\u5019\uff0c \u4e5f\u4f3c\u5728\u52a8\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u7ec8\u65bc\u4e5f\u53d1\u73b0\u8fd9\u79cd\u524d\u8f88\u540d\u5251\u5ba2\u7684\u6c14\u9b44\uff0c\u5b9e\u975e\u4ed6\u4eba\u6240\u80fd\u60f3\u50cf\uff0c\u4ed6\u672c\u60f3\u529d\u529d\u5e05\u4e00\u5e06\u7684\uff0c \u73b0\u5728\u5374\u5f00\u59cb\u4e3a\u695a\u7559\u9999\u62c5\u5fc3\u4e86\u3002 \u3000\u3000\u4ed6\u81ea\u5df1\u5b9e\u5728\u60f3\u4e0d\u51fa\u4ec0\u9ebd\u6cd5\u5b50\u80fd\u5c06\u8fd9\u80a1\u5251\u6c14\u51fb\u7834\u3002 \u3000\u3000\u5c71\u98ce\u867d\u7136\u5f88\u5f3a\u52b2\uff0c\u4f46\u6574\u4e2a\u5929\u5730\u90fd\u4f3c\u5df2\u51dd\u7ed3\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u53ea\u89c9\u6c57\u73e0\u4e00\u6ef4\u6ef4\u6c81\u4e86\u51fa\u6765\uff0c\u5929\u5730\u4e07\u7269\u5374\u50cf\u662f\u5df2\u9759\u6b62\u4e0d\u52a8\u4e86\uff0c\u5c31\u8fde\u65f6\u95f4\u90fd\u4f3c\u5df2\u505c \u987f\u3002 \u3000\u3000\u4ed6\u53ea\u89c9\u4f3c\u4e4e\u6709\u4e00\u53cc\u65e0\u5f62\u7684\u624b\uff0c\u5728\u627c\u4f4f\u4ed6\u7684\u8116\u5b50\u3002 \u3000\u3000\u4ed6\u5df2\u900f\u4e0d\u8fc7\u6c14\u6765\u3002 \u3000\u3000\u4ed6\u65e0\u6cd5\u60f3\u50cf\u695a\u7559\u9999\u6b64\u523b\u7684\u611f\u89c9\u591a\u9ebd\u96be\u53d7\uff0c\u4f46\u662f\uff0c\u5c31\u5728\u8fd9\u65f6\u2014\u2014\u695a\u7559\u9999\u7684\u8eab\u5f62\u7a81\u53c8\u4e00\u98de\u51b2 \u5929\u3002 \u3000\u3000\u8c01\u4e5f\u60f3\u4e0d\u5230\u4ed6\u5728\u8fd9\u9ebd\u5927\u7684\u538b\u529b\u4e0b\u8fd8\u80fd\u51b2\u5929\u98de\u8d77\uff0c\u8c01\u4e5f\u60f3\u4e0d\u5230\u4ed6\u8fd9\u4e00\u8dc3\u4e4b\u52bf\uff0c\u7adf\u5982\u8eab\u5316\u7bad \u77e2\u3002 \u3000\u3000\u5e05\u4e00\u5e06\u4ecd\u5982\u78d0\u77f3\u822c\u575a\u51dd\u4e0d\u52a8\uff0c\u53ea\u662f\u638c\u4e2d\u5251\u5df2\u4e00\u5bf8\u5bf8\u62ac\u8d77\uff0c\u5251\u4e0a\u4f3c\u4e4e\u5e26\u6709\u5343\u4e07\u65a4\u7684\u91cd\u7269\uff0c \u770b\u6765\u8bf4\u4e0d\u51fa\u7684\u6c89\u6ede\u3002 \u3000\u3000\u4f46\u80e1\u94c1\u82b1\u5374\u5df2\u770b\u51fa\u4ed6\u5251\u5f0f\u6b63\u662f\u914d\u5408\u695a\u7559\u9999\u8eab\u5f62\u7684\u53d8\u5316\uff0c\u695a\u7559\u9999\u8eab\u5f62\u7eb5\u7136\u77e2\u77eb\u5982\u9f99\uff0c\u4ed6\u5251 \u5931\u5374\u59cb\u7ec8\u4e0d\u79bb\u695a\u7559\u9999\u65b9\u5bf8\u4e4b\u95f4\uff0c\u65e0\u8bba\u695a\u7559\u9999\u4ece\u4ec0\u9ebd\u65b9\u4f4d\u843d\u4e0b\uff0c\u90fd\u9003\u4e0d\u5f00\u4ed6\u8fd9\u67c4\u5251\u4e4b\u4e00\u523a\u3002 \u3000\u3000\u695a\u7559\u9999\u7ec8\u65bc\u5df2\u843d\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u4ed6\u4e0a\u51b2\u4e4b\u52bf\u5982\u7bad\u77e2\u7834\u7a7a\uff0c\u4e00\u98de\u51b2\u5929\uff0c\u4e0b\u843d\u4e4b\u52bf\u5374\u5982\u795e\u9f99\u77e2\u77eb\uff0c\u76d8\u65cb\u98de\u821e\uff0c\u53d8\u5316\u4e07\u5343\uff0c\u4e0d \u53ef\u65b9\u7269\u3002 \u3000\u3000\u5e05\u4e00\u5e06\u638c\u4e2d\u5251\u4e5f\u84c4\u52bf\u5f85\u53d1\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u695a\u7559\u9999\u624b\u91cc\u7684\u67d4\u679d\u5ffd\u7136\u5212\u4e86\u4e2a\u5706\u5f27\uff0c\u679d\u5934\u7684\u51e0\u7247\u6811\u53f6\uff0c\u5374\u5ffd\u7136\u79bb\u679d\u5411\u5e05\u4e00\u5e06 \u5c04\u51fa\u3002 \u3000\u3000\u5e05\u4e00\u5e06\u957f\u5578\u4e00\u58f0\uff0c\u957f\u5251\u5df2\u5316\u4e3a\u4e00\u7247\u5149\u5e55\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u53ea\u89c1\u5251\u5149\u5df2\u5c06\u695a\u7559\u9999\u541e\u6ca1\uff0c\u90a3\u51e0\u7247\u6811\u53f6\u7adf\u5df2\u88ab\u8fd9\u51cc\u5389\u7684\u5251\u6c14\u6240\u7c89\u788e\uff0c\u6d88\u706d\u5f97\u65e0\u5f71 \u65e0\u8e2a\u3002 \u3000\u3000\u7136\u5f8c\uff0c\u5251\u6c14\u987f\u6d88\uff0c\u5e05\u4e00\u5e06\u638c\u4e2d\u5251\u5df2\u5782\u843d\uff0c\u9762\u4e0a\u6728\u65e0\u8868\u60c5\uff0c\u5168\u8eab\u7684\u808c\u8089\u90fd\u50cf\u662f\u5df2\u5728\u8fd9\u4e00\u5239 \u90a3\u4e2d\u50f5\u786c\u3002 \u3000\u3000\u4ed6\u672c\u6765\u82e5\u662f\u628a\u5200\uff0c\u73b0\u5728\u5c31\u5df2\u53d8\u4e3a\u6728\u5200\uff0c\u5df2\u53d8\u5f97\u9eef\u6de1\u65e0\u5149\uff0c\u4ed6\u7684\u950b\u8292\u4e0e\u6740\u6c14\uff0c\u4e5f\u5df2\u65e0\u5f71\u65e0 \u8e2a\u3002 \u3000\u3000\u518d\u770b\u695a\u7559\u9999\u5374\u5df2\u843d\u5230\u4ed6\u9762\u524d\u4e00\u4e08\u5916\uff0c\u4ed6\u638c\u4e2d\u7684\u67d4\u679d\uff0c\u5df2\u53d8\u5f97\u5149\u79c3\u79c3\u7684\uff0c\u7adf\u8fde\u6811\u76ae\u90fd\u5df2\u88ab \u5251\u6c14\u5265\u5149\u4e86\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u65e2\u4e0d\u77e5\u9053\u695a\u7559\u9999\u662f\u600e\u9ebd\u6837\u81ea\u5251\u6c14\u5305\u56f4\u4e2d\u51b2\u51fa\u6765\u7684\uff0c\u4e5f\u4e0d\u77e5\u9053\u8fd9\u4e24\u4eba\u7a76\u7adf\u662f\u8c01\u80dc\u8c01 \u8d1f\u3002 \u3000\u3000\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u695a\u7559\u9999\u8eac\u8eab\u4e3a\u793c\uff0c\u9053\uff1a\u201c\u524d\u8f88\u5251\u6cd5\u7cbe\u5999\uff0c\u5b9e\u4e3a\u5728\u4e0b\u5e73\u751f\u4ec5\u89c1\u3002\u201d \u3000\u3000\u5e05\u4e00\u5e06\u832b\u7136\u671b\u4e86\u4e00\u773c\uff0c\u5583\u5583\u9053\uff1a\u201c\u5f88\u597d\uff0c\u5f88\u597d\uff0c\u5f88\u597d\u2026\u2026\u201d \u3000\u3000\u4ed6\u4e00\u8fde\u8bf4\u4e86\u53c1\u53e5\uff0c\u957f\u5251\u5ffd\u7136\u5316\u4e3a\u98de\u8679\uff0c\u5728\u82cd\u832b\u7684\u66ae\u8272\u4e2d\u95ea\u4e86\u95ea\uff0c\u4fbf\u6d41\u661f\u822c\u6447\u66f3\u5411\u5251\u6c60\u4e2d \u843d\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u997c\u4e86\u534a\u664c\uff0c\u624d\u542c\u5f97\u201c\u5657\u901a\u201d\u4e00\u54cd\u3002 \u3000\u3000\u65bc\u662f\u5251\u6c60\u4e2d\u53c8\u591a\u4e86\u67c4\u7edd\u4e16\u7684\u540d\u5251\u3002 \u3000\u3000\u5e05\u4e00\u5e06\u832b\u7136\u671b\u8fdc\u65b9\uff0c\u5168\u8eab\u90fd\u5df2\u865a\u8131\uff0c\u4ed6\u7684\u751f\u547d\u4e0e\u7075\u9b42\u90fd\u4f3c\u5df2\u968f\u8fd9\u67c4\u5251\u843d\u5165\u5251\u4ed6\u4e2d\u3002 \u3000\u3000\u695a\u7559\u9999\u5377\u56dbp101- \u3000\u3000\u695a\u7559\u9999\u9762\u4e0a\u4e0d\u7981\u9732\u51fa\u9eef\u7136\u4e4b\u8272\uff0c\u957f\u53f9\u9053\uff1a\u201c\u5728\u4e0b\u53d6\u5de7\uff0c\u867d\u4fa5\u5e78\u9003\u8131\u524d\u8f88\u5251\u4e0b\uff0c\u4f46\u4e5f\u672a\u80fd \u53d6\u80dc\uff0c\u524d\u8f88\u4f55\u82e6\u2026\u2026\u2026\u201d \u3000\u3000\u5e05\u4e00\u5e06\u5389\u58f0\u9053\uff1a\u201c\u4f60\u4e0d\u5fc5\u8bf4\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u5e05\u4e00\u5e06\u76ee\u5149\u51dd\u6ce8\u7740\u4ed6\uff0c\u826f\u4e45\u826f\u4e45\uff0c\u4e5f\u6ca1\u6709\u518d\u8bf4\u4e00\u4e2a\u5b57\uff0c\u5ffd\u7136\u8f6c\u8fc7\u8eab\uff0c\u5927\u6b65\u5411\u5c71\u4e0b\u884c\u53bb\u3002 \u3000\u3000\u695a\u7559\u9999\u76ee\u9001\u7740\u4ed6\u8eab\u5f62\u8fdc\u53bb\uff0c\u957f\u53f9\u9053\uff1a\u201c\u524d\u8f88\u98ce\u8303\uff0c\u679c\u7136\u4e0d\u540c\u2026\u2026\u2026\u201d \u3000\u3000\u4ed6\u8bdd\u58f0\u5f88\u8f7b\uff0c\u4f46\u5e05\u4e00\u5e06\u5ffd\u53c8\u56de\u8fc7\u5934\uff0c\u671b\u7740\u4ed6\uff0c\u50cf\u662f\u8981\u8bf4\u4ec0\u9ebd\u3002 \u3000\u3000\u695a\u7559\u9999\u8eac\u8eab\u9053\uff1a\u201c\u524d\u8f88\u8fd8\u6709\u5429\u5490\uff1f\u201d \u3000\u3000\u5e05\u4e00\u5e06\u9ed8\u7136\u534a\u664c\uff0c\u7ec8\u65bc\u4e5f\u957f\u957f\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u80dc\u800c\u4e0d\u9a84\uff0c\u8c26\u606d\u6709\u793c\uff0c\u5f8c\u8f88\u5982\u4f60\uff0c\u53c8\u5c82 \u662f\u524d\u8f88\u80fd\u53ca\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u591a\u627f\u524d\u8f88\u8d5e\u8bb8\uff0c\u5728\u4e0b\u5374\u4e0d\u6562\u8a00\u80dc\u3002\u201d \u3000\u3000\u5e05\u4e00\u5e06\u53c8\u671b\u4e86\u4ed6\u8bb8\u4e45\uff0c\u5ffd\u7136\u9053\uff1a\u201c\u4f60\u548c\u674e\u89c2\u9c7c\u7a76\u7adf\u6709\u4f55\u4ec7\u6068\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u5728\u4e0b\u4e0e\u674e\u8001\u524d\u8f88\u7d20\u6627\u5e73\u751f\uff0c\u4ec7\u6068\u4e24\u5b57\uff0c\u66f4\u662f\u65e0\u4ece\u8bf4\u8d77\u3002\u201d \u3000\u3000\u5e05\u4e00\u5e06\u76ee\u4e2d\u900f\u51fa\u8be7\u5f02\u4e4b\u8272\uff0c\u9053\uff1a\u201c\u65e2\u662f\u5982\u6b64\uff0c\u674e\u89c2\u9c7c\u4e3a\u4f55\u8981\u6740\u4f60\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u82e6\u7b11\u9053\uff1a\u201c\u5728\u4e0b\u4e0d\u77e5\u9053\uff0c\u674e\u8001\u524d\u8f88\u96be\u9053\u4e5f\u672b\u66fe\u8bf4\u8d77\u9ebd\uff1f\u201d \u3000\u3000\u5e05\u4e00\u5e06\u4ef0\u5929\u957f\u53f9\uff0c\u9053\uff1a\u201c\u674e\u89c2\u9c7c\u6614\u5e74\u66fe\u6709\u6069\u65bc\u6211\uff0c\u53ea\u8981\u4ed6\u4fe1\u7b26\u6240\u81f3\uff0c\u7eb5\u7136\u8981\u6211\u5272\u4e0b\u81ea\u5df1 \u5934\u9885\uff0c\u6211\u4e5f\u5728\u6240\u4e0d\u8f9e\uff0c\u4f60\u660e\u767d\u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u5728\u4e0b\u660e\u767d\u3002\u201d \u3000\u3000\u5e05\u4e00\u5e06\u9053\uff1a\u201c\u5f88\u597d\uff0c\u5f88\u597d\uff0c\u5f88\u597d\u2026\u2026\u2026\u201d\u4ed6\u53c8\u5c06\u8fd9\u53e5\u8bdd\u8bf4\u4e86\u53c1\u904d\uff0c\u8eab\u5f62\u5728\u66ae\u8272\u4e2d\u4e00\u95ea\uff0c \u4fbf\u5df2\u4e0d\u89c1\u3002 \u3000\u3000\u695a\u7559\u9999\u6447\u5934\u957f\u53f9\u9053\uff1a\u201c\u6b64\u4eba\u679c\u7136\u4e0d\u6127\u4e3a\u82f1\u96c4\uff0c\u53ea\u53ef\u60dc\u8fd9\u6837\u7684\u4eba\uff0c\u6c5f\u6e56\u4e2d\u5df2\u8d8a\u6765\u8d8a\u5c11 \u4e86\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u8fd9\u624d\u677e\u4e86\u53e3\u6c14\uff0c\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u4ed6\u6700\u5f8c\u4e00\u53e5\u8bdd\uff0c\u7a76\u7adf\u6709\u4ec0\u9ebd\u542b\u610f\uff1f\u4f60\u771f\u7684\u660e\u767d \u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u53f9\u9053\uff1a\u201c\u4ed6\u8fd9\u662f\u5728\u544a\u8bc9\u6211\uff0c\u4ed6\u4e3a\u4e86\u8981\u62a5\u674e\u89c2\u9c7c\u4e4b\u6069\uff0c\u5c31\u7b97\u8981\u4ed6\u6027\u547d\u4e5f\u5728\u6240\u4e0d\u60dc\uff0c \u6240\u4ee5\u4ed6\u867d\u7136\u5e76\u4e0d\u77e5\u9053\u674e\u89c2\u9c7c\u4e3a\u4f55\u8981\u6740\u6211\uff0c\u8fd8\u662f\u6765\u52a8\u624b\u4e86\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u76b1\u7709\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u4ed6\u771f\u662f\u53d7\u674e\u89c2\u9c7c\u6240\u6258\u800c\u6765\u7684\u4e86\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u5f53\u7136\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u4f46\u674e\u89c2\u9c7c\u7a76\u7adf\u4e3a\u4f55\u8981\u6740\u4f60\u5462\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9eef\u7136\u9053\uff1a\u201c\u4e00\u4e2a\u8001\u4eba\u4e3a\u4e86\u4ed6\u7684\u5b50\u5ab3\uff0c\u662f\u4ec0\u9ebd\u4e8b\u90fd\u505a\u5f97\u51fa\u7684\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9ed8\u7136\u534a\u664c\uff0c\u5ffd\u53c8\u7b11\u9053\uff1a\u201c\u4f60\u65b9\u624d\u662f\u600e\u9ebd\u6837\u80dc\u4ed6\u7684\uff1f\u6211\u975e\u4f46\u6ca1\u770b\u51fa\u6765\uff0c\u8fde\u60f3\u90fd\u60f3\u4e0d \u51fa\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u53f9\u9053\uff1a\u201c\u6b64\u4eba\u5251\u6cd5\u5b9e\u5df2\u767b\u5cf0\u9020\u6781\uff0c\u5df2\u5c06\u6709\u5f62\u4e4b\u5251\uff0c\u5316\u4e3a\u65e0\u5f62\u4e4b\u6c14\uff0c\u6211\u5168\u8eab\u90fd\u5df2\u88ab \u4ed6\u7b3c\u7f69\uff0c\u51e0\u4e4e\u8fd0\u6c14\u90fd\u5df2\u900f\u4e0d\u51fa\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u8fde\u6211\u90fd\u900f\u4e0d\u8fc7\u6c14\u6765\u4e86\uff0c\u4f55\u51b5\u4f60\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u6211\u82e6\u4e0d\u5148\u8bbe\u6cd5\u51b2\u51fa\u4ed6\u7684\u5251\u6c14\uff0c\u5c31\u53ea\u6709\u4efb\u51ed\u4ed6\u5bb0\u5272\uff0c\u6240\u4ee5\u6211\u4e5f\u53ea\u6709\u5192\u4e00\u5192\u9669 \u4e86\uff0c\u4e58\u4ed6\u6362\u6c14\u65f6\uff0c\u5ffd\u7136\u8dc3\u8d77\u3002\u201d\u4ed6\u82e6\u7b11\u7740\u63a5\u9053\uff1a\u201c\u4f60\u603b\u8be5\u77e5\u9053\uff0c\u5bf9\u4ed8\u5e05\u4e00\u5e06\u8fd9\u6837\u7684\u9ad8\u624b\uff0c\u8fd9 \u4e0d\u4f46\u662f\u5728\u5192\u9669\uff0c\u7b80\u76f4\u548c\u9001\u6b7b\u5dee\u4e0d\u591a\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u662f\u5440\uff01\u8fd9\u79cd\u51cc\u7a7a\u98de\u51fb\u7684\u62db\u6570\uff0c\u53ea\u6709\u5728\u4ee5\u5f3a\u640f\u5f31\u65f6\u624d\u80fd\u7528\u7684\uff0c\u56e0\u4e3a\u53ea\u8981\u4e00\u51fb \u4e0d\u4e2d\uff0c\u5c31\u8981\u81ea\u9677\u7edd\u5883\uff0c\u6240\u4ee5\u627e\u770b\u4f60\u4f7f\u51fa\u8fd9\u79cd\u62db\u5f0f\u6765\uff0c\u4e5f\u5413\u4e86\u4e00\u8df3\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u6211\u8eab\u5f62\u51cc\u7a7a\u5f8c\uff0c\u66f4\u770b\u51fa\u4ed6\u5251\u6c14\u51dd\u70bc\uff0c\u5b9e\u662f\u65e0\u61c8\u53ef\u51fb\uff0c\u6240\u4ee5\u6211\u53ea\u6709\u5148\u4ee5\u6811\u679d \u4e0a\u7684\u53f6\u5b50\uff0c\u6765\u8bf1\u53d1\u4ed6\u7684\u5251\u6c14\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u6478\u4e86\u6478\u9f3b\u5b50\uff0c\u9053\uff1a\u201c\u8fd9\u9053\u7406\u6211\u5c31\u4e0d\u61c2\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u90a3\u65f6\u4ed6\u5251\u6c14\u5df2\u5b8c\u5168\u53d1\u6325\uff0c\u6b63\u5982\u5f13\u5df2\u5f15\u6ee1\uff0c\u7bad\u5728\u5f26\u4e0a\uff0c\u53ea\u8981\u8f7b\u8f7b\u4e00\u89e6\uff0c\u5f26\u4e0a \u7684\u7bad\u4fbf\u4e0d\u5f97\u4e0d\u53d1\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u55ef\uff01\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u6211\u6240\u7528\u7684\u5c31\u662f\u8fd9\u9053\u7406\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u76f4\u7740\u773c\u9053\uff1a\u201c\u4ec0\u9ebd\u9053\u7406\uff1f\u6211\u8fd8\u662f\u4e0d\u61c2\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u6211\u5c06\u6811\u53f6\u4ee5\u5185\u529b\u903c\u51fa\uff0c\u89e6\u53ca\u4ed6\u7684\u5251\u6c14\uff0c\u4ed6\u5251\u6c14\u672c\u5df2\u9971\u6da8\uff0c\u53ea\u8981\u88ab\u5916\u7269\u89e6 \u53ca\uff0c\u5c31\u7acb\u523b\u8981\u53d1\u4f5c\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u55ef\uff01\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u5251\u6c14\u4e00\u53d1\uff0c\u4fbf\u4e0d\u53ef\u6536\u62fe\uff0c\u975e\u4f46\u90a3\u51e0\u7247\u6811\u53f6\u8981\u88ab\u5b8c\u5168\u6bc1\u706d\uff0c\u5c31\u662f\u6574\u4e2a\u4e00\u4e2a \u4eba\uff0c\u53ea\u6015\u4e5f\u8981\u88ab\u8f97\u5f97\u7c89\u788e\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9a87\u7136\u9053\uff1a\u201c\u597d\u5389\u5bb3\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4f46\u5251\u6c14\u88ab\u5f15\u53d1\u5f8c\uff0c\u5c31\u6709\u4e86\u7a7a\u9699\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u56e0\u4e3a\u4ed6\u529b\u91cf\u5df2\u96c6\u4e2d\u5728\u90a3\u51e0\u70b9\u4e0a\uff0c\u522b\u7684\u5730\u65b9\u81ea\u7136\u5c31\u96be\u514d\u8981\u9732\u51fa\u7a7a\u9699\uff0c\u6240\u4ee5\u6211 \u5c31\u4e58\u9699\u4ee5\u6811\u679d\u5728\u4ed6\u5934\u4e0a\u8f7b\u8f7b\u4e00\u70b9\u3002\u201d\u4ed6\u957f\u7b11\u7740\u63a5\u9053\uff1a\u201c\u4f46\u9976\u662f\u5982\u6b64\uff0c\u6211\u8fd8\u662f\u88ab\u90a3\u5251\u6c14\u53cd\u6fc0\u8fc7 \u6765\uff0c\u9707\u5f97\u98de\u4e86\u51fa\u53bb\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u64e6\u4e86\u64e6\u6c57\uff0c\u5c55\u989c\u7b11\u9053\uff1a\u201c\u53ef\u662f\u65e0\u8bba\u5982\u4f55\uff0c\u4f60\u8fd8\u662f\u4e00\u62db\u5c31\u80dc\u4e86\u4ed6\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u82e6\u7b11\u9053\uff1a\u201c\u8fd9\u4e00\u62db\u770b\u6765\u867d\u8f7b\u677e\uff0c\u5176\u5b9e\u5374\u6bd4\u5343\u4e07\u62db\u8fd8\u8981\u8270\u82e6\uff0c\u4f55\u51b5\uff0c\u6211\u90a3\u6811\u679d\u867d\u70b9 \u7740\u4e86\u4ed6\uff0c\u5374\u7edd\u5bf9\u65e0\u6cd5\u4f24\u5f97\u4e86\u4ed6\uff0c\u4ed6\u672c\u6765\u4e0d\u5fc5\u8ba4\u8f93\u7684\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u52a8\u5bb9\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u4ed6\u90a3\u65f6\u82e5\u4e0d\u8ba4\u8f93\uff0c\u518d\u4e58\u52bf\u8ffd\u51fb\uff0c\u4f60\u5c82\u975e\u5c31\u5b8c\u86cb\u4e86\u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u90a3\u5012\u4e5f\u672a\u5fc5\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u4e3a\u4ec0\u9ebd\u53c8\u672a\u5fc5\u4e86\u5462\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u53ea\u56e0\u6211\u8fd9\u4e00\u7bb8\uff0c\u5df2\u5c06\u4ed6\u5251\u6c14\u7834\u53bb\uff0c\u4ed6\u82e5\u60f3\u518d\u5c06\u5251\u6c14\u51dd\u70bc\uff0c\u6211\u4e5f\u4e0d\u4f1a\u7ed9\u4ed6\u673a \u4f1a\u4e86\uff0c\u6240\u4ee5\u4ed6\u82e5\u518d\u8ffd\u51fb\uff0c\u53ea\u6709\u51ed\u62db\u5f0f\u548c\u6211\u52a8\u624b\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u4f60\u600e\u77e5\u4ed6\u62db\u5f0f\u5c31\u80dc\u4e0d\u4e86\u4f60\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u53c8\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u82e5\u8bba\u62db\u5f0f\u4e4b\u7cbe\u5999\uff0c\u666e\u5929\u4e4b\u4e0b\uff0c\u53ea\u6015\u8fd8\u65e0\u4e00\u4eba\u80fd\u80dc\u5f97\u8fc7\u77f3\u89c2\u97f3 \u7684\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u7728\u4e86\u7728\u773c\uff0c\u5ffd\u53c8\u95ee\u9053\uff1a\u201c\u82e5\u8981\u5e05\u4e00\u5e06\u548c\u77f3\u89c2\u97f3\u4ea4\u624b\u5462\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u77f3\u89c2\u97f3\u5fc5\u80dc\u65e0\u7bfe\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u4f55\u4ee5\u89c1\u5f97\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u56e0\u4e3a\u5e05\u4e00\u5e06\u8fd8\u662f\u672b\u80fd\u5c06\u5251\u6c14\u7ec3\u5f97\u51fa\u795e\u5165\u5316\uff0c\u6536\u653e\u81ea\u5982\uff0c\u4e5f\u672b\u80fd\u5c06\u5251\u6c14\u6eb6\u5165 \u5251\u7684\u62db\u5f0f\u53d8\u5316\u4e2d\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u4ed6\u82e5\u80fd\u5c06\u5251\u6c14\u6eb6\u5165\u5251\u62db\u4e2d\u5462\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u90a3\u5c31\u65e0\u654c\u65bc\u5929\u4e0b\u4e86\uff01\u201d \u7b2c\u5341\u7ae0\u3000\u5947\u5f02\u592b\u59bb>> \u53e4\u9f99\u300a\u695a\u7559\u9999\u7cfb\u5217\xb7\u753b\u7709\u9e1f\u300b \u7b2c\u5341\u7ae0\u3000\u5947\u5f02\u592b\u59bb \u3000\u3000\u80e1\u94c1\u82b1\u7b11\u9053\uff1a\u201c\u6211\u4f46\u613f\u4e16\u4e0a\u6709\u8fd9\u9ebd\u6837\u4e00\u4e2a\u4eba\uff0c\u8ba9\u4f60\u4e5f\u5403\u5403\u82e6\u5934\uff0c\u4f60\u603b\u662f\u6253\u80dc\u4ed7\uff0c\u82e5\u4e0d\u8d25 \u4e00\u6b21\uff0c\u53ea\u6015\u6b66\u529f\u6c38\u8fdc\u4e5f\u4e0d\u80fd\u767b\u5cf0\u9020\u6781\u7684\u3002\u201d \u3000\u3000\u4ed6\u8fd9\u672c\u662f\u53e5\u5f00\u73a9\u7b11\u7684\u8bdd\uff0c\u8c01\u77e5\u695a\u7559\u9999\u5374\u8083\u7136\u9053\uff1a\u201c\u6b63\u662f\u5982\u6b64\uff0c\u8fd9\u6b63\u662f\u6b66\u529f\u4e2d\u81f3\u6df1\u81f3\u5999\u7684 \u9053\u7406\uff0c\u53ea\u53ef\u60dc\u6211\u751f\u6765\u559c\u6b22\u5192\u9669\uff0c\u9047\u89c1\u9ad8\u624b\u65f6\uff0c\u60c5\u4e0d\u81ea\u7981\u603b\u8981\u4f7f\u51fa\u9669\u62db\uff0c\u53ea\u8981\u51fa\u624b\u4e00\u8d25\u6211\u5fc5\u6b7b \u65e0\u7591\uff0c\u6240\u4ee5\u627e\u867d\u7136\u77e5\u9053\u8fd9\u9053\u7406\uff0c\u5374\u8fd8\u662f\u60f3\u884c\u9669\u4fa5\u5e78\u4ee5\u6c42\u80dc\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u513f\u4ed6\u8bf4\u5f97\u5982\u6b64\u90d1\u91cd\uff0c\u53cd\u800c\u6014\u4e86\u6014\uff0c\u9053\uff1a\u201c\u4f60\u4e5f\u5e76\u975e\u53ea\u60f3\u6c42\u80dc\u800c\u662f\u4f60\u82e5\u4e0d\u884c\u9669\uff0c\u4e5f \u5fc5\u6b7b\u65e0\u7591\uff0c\u53ea\u56e0\u4f60\u867d\u4e0d\u6740\u522b\u4eba\uff0c\u522b\u4eba\u5374\u8981\u6740\u4f60\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u53f9\u9053\uff1a\u201c\u6240\u4ee5\u627e\u8fdf\u65e9\u603b\u6709\u4e00\u5929\uff0c\u8981\u6b7b\u5728\u522b\u4eba\u624b\u4e0a\u7684\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u7b11\u9053\uff1a\u201c\u4f60\u653e\u5fc3\uff0c\u80fd\u6740\u4f60\u7684\u4eba\uff0c\u5230\u73b0\u5728\u53ea\u6015\u8fd8\u672b\u751f\u51fa\u6765\u54e9\uff01\u201d \u3000\u3000\u66ae\u8272\u8d8a\u6765\u8d8a\u6d53\uff0c\u79cb\u610f\u4e5f\u8d8a\u6765\u8d8a\u6d53\u3002 \u3000\u3000\u4ed6\u4eec\u5728\u66ae\u8272\u4e2d\u767b\u5c71\uff0c\u7ecf\u8fc7\u4e86\u9e33\u9e2f\u51a2\u3001\u5b5d\u5b50\u5893\u3001\u65ad\u6881\u6bbf\u3001\u61a8\u61a8\u6cc9\u3001\u8bd5\u5251\u77f3\u3001\u53c1\u4ed9\u4ead\u3001\u4ed9\u4eba \u6d1e\u2026 \u3000\u3000\u2026 \u3000\u3000\u4f46\u4ed6\u4eec\u5374\u627e\u4e0d\u5230\u76f4\u4e0a\u201c\u62e5\u7fe0\u5c71\u5e84\u201d\u7684\u9014\u5f84\u3002\u80e1\u94c1\u82b1\u51e0\u4e4e\u5df2\u5fcd\u4e0d\u4f4f\u8981\u6000\u7591\u8fd9\u201c\u62e5\u7fe0\u5c71\u5e84\u201d \u662f\u5426\u5728\u864e\u4e18\u5c71\u4e0a\u4e86\u3002 \u3000\u3000\u767d\u6768\u8427\u8427\uff0c\u79cb\u58f0\u4e00\u7247\uff0c\u5bbf\u8349\u6ca1\u5f84\uff0c\u79cb\u8272\u6ee1\u5929\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u76b1\u7709\u9053\uff1a\u201c\u4f60\u4e5f\u6ca1\u53bb\u8fc7\u90a3\u62e5\u7fe0\u5c71\u5e84\u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u6ca1\u6709\uff0c\u6211\u53ea\u542c\u8bf4\u8fd9\u62e5\u7fe0\u5c71\u5e84\u6000\u62b1\u8fdc\u5c71\uff0c\u9065\u671b\u592a\u6e56\uff0c\u6c99\u4e4c\u98ce\u5e06\uff0c\u70df\u4e91\u7af9\u6811\uff0c \u4e43\u662f\u5168\u5c71\u98ce\u7269\u6700\u7f8e\u4e4b\u5904\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u8fd8\u60f3\u518d\u8bf4\u4ec0\u9ebd\uff0c\u5ffd\u7136\u53d1\u73b0\u8fdc\u5904\u6311\u8d77\u4e86\u4e00\u76cf\u7ea2\u706f\uff0c\u968f\u98ce\u6447\u66f3\uff0c\u4f3c\u4e4e\u5728\u5c71\u5dc5\u6700\u9ad8\u5904\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u76b1\u7709\u9053\uff1a\u201c\u8fd9\u53c8\u662f\u4ec0\u9ebd\u82b1\u6837\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u6211\u4eec\u597d\u6b79\u4e5f\u5f97\u53bb\u77a7\u77a7\u3002\u201d \u3000\u3000\u4e24\u4eba\u5c55\u5f00\u8eab\u5f62\uff0c\u63a0\u4e86\u4e0a\u53bb\uff0c\u53ea\u89c1\u5de8\u5854\u5dcd\u5ce8\uff0c\u4e34\u7acb\u5728\u665a\u98ce\u4e2d\uff0c\u5854\u9ad8\u4e03\u5c42\uff0c\u6bcf\u4e00\u5c42\u90fd\u6709\u98de\u6a90 \u659c\u5185\u3002 \u3000\u3000\u90a3\u4e00\u76cf\u7ea2\u706f\uff0c\u5c31\u6b63\u6302\u5728\u5854\u6a90\u4e0a\uff0c\u4f46\u56db\u4e0b\u51c4\u51c4\u51b7\u51b7\uff0c\u4f46\u89c1\u767d\u6768\u682a\u682a\uff0c\u5374\u77a7\u4e0d\u89c1\u6709\u4eba\u7684\u5f71 \u5b50\u3002 \u3000\u3000\u8fd9\u706f\u7b3c\u662f\u8c01\u6302\u5728\u8fd9\u91cc\u7684\uff0c\u4e3a\u7684\u662f\u4ec0\u9ebd\uff1f\u706f\u5149\u5982\u8840\u3002 \u3000\u3000\u8840\u7ea2\u7684\u706f\u5149\u4e2d\uff0c\u77f3\u642d\u4e0a\u7adf\u8fd8\u5199\u7740\u4e00\u884c\u5b57\u3002\u4f46\u5374\u5199\u5728\u77f3\u5854\u7684\u6700\u4e0a\u5c42\uff0c\u4ece\u4e0b\u9762\u671b\u4e0a\u53bb\uff0c\u6839\u672c \u5c31\u77a7\u4e0d\u6e05\u695a\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u76b1\u7709\u9053\uff1a\u201c\u4f60\u773c\u775b\u6bd4\u6211\u597d\uff0c\u4f60\u770b\u4e0d\u770b\u5f97\u6e05\u90a3\u5199\u7684\u662f\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u4f3c\u5728\u601d\u7d22\uff0c\u53ea\u6447\u4e86\u6447\u5934\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u6211\u4e0a\u53bb\u77a7\u77a7\u3002\u201d \u3000\u3000\u4ed6\u8eab\u5b50\u521a\u8981\u8dc3\u8d77\uff0c\u8f68\u88ab\u695a\u7559\u9999\u4e00\u628a\u62c9\u4f4f\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u6211\u4e5f\u77e5\u9053\u8fd9\u5fc5\u5b9a\u53c8\u662f\u4ed6\u4eec\u7684\u8be1\u8ba1\uff0c\u4f46\u82e5\u4e0d\u4e0a\u53bb\u77a7\u77a7\uff0c\u5fc3\u91cc\u66f4\u96be\u53d7\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u6211\u53bb\u3002\u201d \u3000\u3000\u4ed6\u4e0d\u4e0e\u80e1\u94c1\u82b1\u4e89\u8bba\uff0c\u8eab\u5f62\u5df2\u63a0\u8d77\uff0c\u4ed6\u81ea\u4e5f\u77e5\u9053\u8fd9\u5fc5\u5b9a\u662f\u4e2a\u9677\u9631\uff0c\u662f\u4ee5\u884c\u52a8\u4e1d\u6beb\u4e0d\u6562\u5927 \u610f\u3002 \u3000\u3000\u53ea\u89c1\u4ed6\u8eab\u5b50\u8f7b\u8f7b\u843d\u5728\u7b2c\u516d\u5c42\u5854\u6a90\u4e0a\uff0c\u7ec8\u65bc\u770b\u6e05\u4e86\u4e0a\u9762\u5199\u7684\u5b57--\u5199\u7684\u8d6b\u7136\u7adf\u662f\uff1a\u201c\u695a\u7559\u9999 \u6bd5\u547d\u65bc\u6b64\u3002\u201d \u3000\u3000\u8fd9\u4e03\u4e2a\u5b57\u4ed6\u4e00\u773c\u4fbf\u5df2\u626b\u8fc7\uff0c\u5fc3\u91cc\u867d\u6709\u4e9b\u5403\u60ca\uff0c\u4f46\u5374\u4e1d\u6beb\u4e0d\u4e71\uff0c\u518d\u4e5f\u4e0d\u77a7\u7b2c\u4e8c\u773c\uff0c\u4fbf\u5f85\u8dc3 \u4e0b\u3002 \u3000\u3000\u8c01\u77e5\u5c31\u5728\u8fd9\u65f6\uff0c\u5854\u9876\u4e0a\u5ffd\u7136\u64a4\u4e0b\u4e00\u7247\u5de8\u7f51\u6765\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u4e00\u76f4\u5728\u4ef0\u9996\u800c\u671b\uff0c\u53ea\u89c1\u8fd9\u7247\u7f51\u5149\u8292\u95ea\u52a8\uff0c\u4f3c\u4e4e\u662f\u4ee5\u91d1\u4e1d\u94c1\u4e1d\u7ec7\u6210\u7684\uff0c\u867d\u7136\u6781\u8f7b\u6781 \u8f6f\uff0c\u6765\u52bf\u5374\u6781\u5feb\u3002 \u3000\u3000\u773c\u89c1\u695a\u7559\u9999\u5c31\u8981\u88ab\u8fd9\u5f20\u7f51\u5305\u4f4f\uff0c\u80e1\u94c1\u82b1\u4e0d\u7981\u60ca\u547c\u9053\uff1a\u201c\u5c0f\u5fc3\u3002\u201d \u3000\u3000\u559d\u58f0\u4e2d\uff0c\u695a\u7559\u9999\u8eab\u5b50\u5df2\u731b\u7136\u4e0b\u5760\uff0c\u5de8\u7f51\u7684\u843d\u52bf\u867d\u6025\uff0c\u695a\u7559\u9999\u7684\u4e0b\u5760\u4e4b\u52bf\u5374\u66f4\u5feb\uff0c\u80e1\u94c1\u82b1 \u521a\u677e\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u8c01\u77e5\u7b2c\u4e94\u5c42\u77f3\u5854\u4e2d\uff0c\u5ffd\u7136\u95ea\u7535\u822c\u98de\u51fa\u4e00\u6839\u94f6\u5149\uff0c\u7adf\u662f\u67c4\u6781\u5c11\u89c1\u7684\u5916\u95e8\u5175\u5203\u201c\u94a9\u9570\u67aa\u201d\uff0c \u67aa\u5c16\u76f4\u52fe\u695a\u7559\u9999\u7684\u53cc\u819d\u3002 \u3000\u3000\u695a\u7559\u9999\u5927\u60ca\u4e4b\u4e0b\uff0c\u8eab\u6cd5\u4ecd\u4e0d\u4e71\uff0c\u9aa4\u7136\u51fa\u624b\u5728\u7b2c\u4e94\u5c42\u5854\u6a90\u4e0a\u4e00\u62cd\uff0c\u8eab\u5b50\u5df2\u8ddf\u7740\u5012\u7ffb\u800c\u8d77\u3002 \u3000\u3000\u4f46\u8fd9\u9ebd\u6837\u4e00\u6765\uff0c\u4ed6\u867d\u907f\u5f00\u4e86\u94a9\u9570\u67aa\uff0c\u5374\u518d\u4e5f\u8eb2\u4e0d\u8fc7\u90a3\u5f20\u5de8\u7f51\uff0c\u6574\u4e2a\u4eba\u90fd\u88ab\u5de8\u7f51\u5305\u4f4f\uff0c\u7ffb \u6eda\u7740\u843d\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u90a3\u67c4\u94a9\u9570\u67aa\u518d\u4e58\u52bf\u4e00\u53e5\uff0c\u4fbf\u5c06\u5de8\u7f51\u6311\u8d77\uff0c\u65bc\u662f\u695a\u7559\u9999\u5c31\u88ab\u540a\u5728\u534a\u7a7a\u4e2d\uff0c\u7eb5\u7136\u7528\u5c3d\u5168\u529b\uff0c \u4e5f\u6323\u624e\u4e0d\u8131\uff0c\u90a3\u7f51\u4e1d\u7adf\u4e00\u6839\u6839\u52d2\u5165\u4ed6\u8089\u91cc\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u548c\u695a\u7559\u9999\u5e76\u80a9\u4f5c\u6218\uff0c\u4e00\u751f\u4e5f\u4e0d\u77e5\u9762\u5bf9\u8fc7\u591a\u5c11\u5371\u673a\uff0c\u4f46\u5374\u4e5f\u4ece\u672a\u89c1\u8fc7\u5982\u6b64\u8be1\u79d8\u7684\u5175 \u5203\uff0c\u5982\u6b64\u8be1\u79d8\u7684\u51fa\u624b\u3002 \u3000\u3000\u4ed6\u5e94\u53d8\u672c\u6781\u5feb\uff0c\u6b64\u756a\u7adf\u8fd8\u4e0d\u53ca\u8fd9\u53d8\u5316\u53d1\u751f\u4e4b\u5feb\uff0c\u4ed6\u751a\u81f3\u6ca1\u6709\u770b\u6e05\u695a\u695a\u7559\u9999\u662f\u600e\u9ebd\u843d\u5165\u7f51 \u91cc\u7684\u3002 \u3000\u3000\u53ea\u89c1\u94f6\u5149\u95ea\u52a8\u4e0d\u606f\uff0c\u695a\u7559\u9999\u5df2\u88ab\u540a\u8d77\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u4e00\u63a2\u624b\u62d4\u51fa\u9774\u7b52\u4e2d\u7684\u77ed\u5200\uff0c\u8eab\u5b50\u5df2\u4e58\u52bf\u8dc3\u8d77\uff0c\u5200\u5149\u5316\u505a\u4e00\u9053\u98de\u8679\uff0c\u540c\u90a3\u5f20\u5de8\u7f51\u5272 \u53bb\u3002 \u3000\u3000\u4f46\u695a\u7559\u9999\u5728\u7f51\u4e2d\u5927\u559d\u9053\uff1a\u201c\u5feb\u9000\u4e0b\u53bb\uff0c\u8fd9\u4e24\u4eba\u4e0d\u53ef\u529b\u654c\u2026\u2026\u201d \u3000\u3000\u559d\u58f0\u672a\u4e86\uff0c\u5854\u9876\u4e0a\u5df2\u98de\u9e1f\u822c\uff0c\u5760\u4e0b\u4e00\u4e2a\u4eba\u6765\u3002 \u3000\u3000\u591c\u8272\u867d\u7136\u770b\u4e0d\u6e05\u4ed6\u6a21\u6837\uff0c\u4f46\u5df2\u53ef\u770b\u51fa\u4ed6\u8eab\u5f62\u4e4b\u9ad8\u5927\uff0c\u7adf\u50cf\u662f\u4e0a\u53e4\u6d2a\u8352\u65f6\u4ee3\u7684\u5de8\u4eba\u4e00\u822c\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u53ea\u89c9\u773c\u524d\u4e00\u7779\uff0c\u5f77\u4f5b\u6574\u4e2a\u4e00\u5ea7\u77f3\u5854\u90fd\u5df2\u5411\u4ed6\u538b\u4e86\u4e0b\u6765\uff0c\u4ed6\u65e0\u8bba\u5411\u90a3\u65b9\u95ea\u907f\uff0c\u90fd\u5728 \u8fd9\u56e2\u9ed1\u6697\u7b3c\u7f69\u4e4b\u4e0b\u3002 \u3000\u3000\u82e5\u662f\u6362\u4e86\u6cdb\u6cdb\u4e4b\u8f88\uff0c\u6b64\u523b\u60ca\u60f6\u4e4b\u4e0b\uff0c\u8eab\u5b50\u5fc5\u5b9a\u8981\u5411\u4e0b\u9762\u9003\u907f\uff0c\u90a3\u5c31\u4e07\u4e07\u9003\u4e0d\u8fc7\u8fd9\u52bf\u5982\u6cf0 \u5c71\u538b\u9876\u4e4b\u4e00\u51fb\u3002 \u3000\u3000\u4f46\u80e1\u94c1\u82b1\u7a76\u7adf\u4e0d\u662f\u7b49\u95f2\uff0c\u8eab\u5b50\u975e\u4f46\u6ca1\u6709\u5411\u4e0b\u843d\uff0c\u53cd\u800c\u8fde\u4eba\u5e26\u5200\uff0c\u4e00\u9f50\u8fce\u7740\u9ed1\u5f71\u5411\u4e0a\u649e\u4e86 \u8fc7\u53bb\u3002 \u3000\u3000\u8fd9\u79cd\u5b58\u5fc3\u548c\u5bf9\u65b9\u540c\u5f52\u65bc\u5c3d\u7684\u62da\u547d\u62db\u5f0f\uff0c\u672c\u4e3a\u9ad8\u624b\u4e0d\u5c51\uff0c\u4f46\u6709\u65f6\u5374\u7684\u786e\u80fd\u626d\u8f6c\u9006\u52bf\uff0c\u62a2\u5f97 \u5148\u673a\u3002 \u3000\u3000\u53ea\u56e0\u5bf9\u65b9\u65e2\u5df2\u7a33\u64cd\u80dc\u7b97\uff0c\u81ea\u7136\u4e0d\u613f\u518d\u548c\u4ed6\u62da\u547d\uff0c\u53ef\u662f\u65e0\u8bba\u8c01\u8981\u5728\u8fd9\u5feb\u5982\u767d\u9a79\u8fc7\u9699\u7684\u4e00\u5239 \u90a3\u95f4\u6539\u53d8\u62db\u5f0f\uff0c\u90fd\u4e0d\u662f\u4ef6\u5bb9\u6613\u4e8b\u3002 \u3000\u3000\u8c01\u77e5\u9053\u8fd9\u9ed1\u5f71\u4eba\u867d\u662f\u4e2a\u5e9e\u7136\u5927\u7269\uff0c\u8eab\u6cd5\u5374\u7075\u5de7\u5df2\u6781\uff0c\u5ffd\u7136\u95f4\u8eab\u5f62\u4e00\u8f6c\uff0c\u5df2\u51ed\u7a7a\u6ed1\u5f00\u4e86\u56db \u4e94\u5c3a\u3002 \u3000\u3000\u4e5f\u5c31\u5728\u8fd9\u5239\u90a3\u4e4b\u95f4\uff0c\u90a3\u67c4\u94a9\u9570\u67aa\u5ffd\u7136\u7f29\u4e86\u56de\u53bb\uff0c\u88ab\u540a\u5728\u534a\u7a7a\u95f4\u7684\u695a\u7559\u9999\uff0c\u5c31\u8fde\u4eba\u5e26\u7f51\u4e00 \u9f50\u6389\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u695a\u7559\u9999\u5f80\u4e0b\u843d\uff0c\u80e1\u94c1\u82b1\u5f80\u4e0a\u649e\uff0c\u773c\u89c1\u80e1\u94c1\u82b1\u975e\u4f46\u4eba\u8981\u649e\u5230\u695a\u7559\u9999\u8eab\u4e0a\uff0c\u5200\u4e5f\u8981\u6233\u8fdb\u695a\u7559 \u9999\u7684\u80f8\u819b\u3002 \u3000\u3000\u4ed6\u8fd9\u4e00\u649e\u7528\u5c3d\u5168\u529b\uff0c\u518d\u4e5f\u6536\u52bf\u4e0d\u53ca\u4e86\u3002\u53ea\u6709\u9aa4\u7136\u5c06\u5168\u8eab\u771f\u6c14\u5168\u90fd\u51fa\uff0c\u4ed6\u5b81\u53ef\u81ea\u5df1\u53d7\u4f24\uff0c \u4e5f\u4e0d\u613f\u4f24\u4e86\u695a\u7559\u9999\u3002 \u3000\u3000\u53ea\u542c\u201c\u7830\u201d\u7684\u4e00\u58f0\uff0c\u695a\u7559\u9999\u6574\u4e2a\u4eba\u90fd\u649e\u4e0a\u4e86\u80e1\u94c1\u82b1\u3002 \u3000\u3000\u8fd9\u65f6\u80e1\u94c1\u82b1\u5168\u8eab\u5df2\u65e0\u4e1d\u6beb\u6c14\u529b\uff0c\u88ab\u8fd9\u9ebd\u6837\u4e00\u649e\uff0c\u53ea\u649e\u5f97\u4ed6\u8111\u888b\u53d1\u660f\uff0c\u4e71\u5192\u91d1\u661f\uff0c\u7adf\u88ab\u649e \u8364\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u660f\u660f\u6c89\u6c89\u4e2d\uff0c\u4ed6\u53ea\u89c9\u695a\u7559\u9999\u5df2\u538b\u5728\u4ed6\u8eab\u4e0a\u3002 \u3000\u3000\u5bf9\u65b9\u7b80\u76f4\u8fde\u4e00\u62db\u90fd\u6ca1\u6709\u51fa\u624b\uff0c\u4ed6\u5c31\u5df2\u88ab\u51fb\u5012\u3002 \u3000\u3000\u997c\u4e86\u534a\u664c\uff0c\u53ea\u542c\u4e00\u4eba\u683c\u683c\u7b11\u9053\uff1a\u201c\u522b\u4eba\u90fd\u8bf4\u8fd9\u4e24\u4eba\u5982\u4f55\u5982\u4f55\u5389\u5bb3\uff0c\u539f\u6765\u4e5f\u4e0d\u8fc7\u5982\u6b64\u800c \u5df2\u3002\u201d \u3000\u3000\u8fd9\u4eba\u8bf4\u8bdd\u7684\u58f0\u97f3\u53c8\u5c16\u53c8\u7ec6\u53c8\u5feb\uff0c\u5c31\u50cf\u662f\u4e2a\u672a\u6210\u5e74\u7684\u5b69\u5b50\uff0c\u4f46\u6bcf\u4e2a\u5b57\u8bf4\u51fa\u6765\uff0c\u8fdc\u5904\u90fd\u80fd\u4f20 \u9001\u51fa\u53bb\uff0c\u5185\u529b\u4e4b\u5f3a\u6c9b\uff0c\u81f3\u5c11\u4e5f\u5f97\u6709\u51e0\u5341\u5e74\u7684\u7eaf\u529f\u592b\u3002 \u3000\u3000\u53e6\u4e00\u4eba\u7f13\u7f13\u9053\uff1a\u201c\u6c5f\u6e56\u4e2d\u591a\u7684\u662f\u5f92\u6709\u865a\u540d\u4e4b\u8f88\uff0c\u8fd9\u4e24\u4eba\u5df2\u7ecf\u7b97\u662f\u4e0d\u9519\u7684\u4e86\u3002\u201d \u3000\u3000\u8fd9\u4eba\u8bf4\u8bdd\u7684\u58f0\u97f3\uff0c\u5374\u5982\u6d2a\u949f\u5927\u5415\u4e00\u822c\uff0c\u800c\u4e14\u7f13\u6162\u5df2\u6781\uff0c\u4ed6\u8bf4\u4e00\u53e5\u8bdd\uff0c\u53e6\u5916\u90a3\u4eba\u81f3\u5c11\u53ef\u4ee5 \u8bf4\u53c1\u53e5\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u8033\u6735\u88ab\u9707\u5f97\u201c\u55e1\u55e1\u201d\u76f4\u54cd\uff0c\u5f20\u5f00\u773c\u775b\u4e00\u770b\uff0c\u8f68\u77a7\u89c1\u9762\u524d\u5df2\u5e76\u80a9\u7ad9\u7740\u4e00\u9ad8\u4e00\u77ee\u4e24\u4e2a \u4eba\u3002 \u3000\u3000\u77ee\u7684\u8fd9\u4eba\u5c31\u7b97\u57ab\u8d77\u811a\u5c16\uff0c\u4e5f\u672b\u5fc5\u80fd\u591f\u5f97\u7740\u9ad8\u7684\u90a3\u4eba\u80a9\u5934\uff0c\u8eab\u5b50\u4e5f\u53c8\u7626\u53c8\u4e7e\uff0c\u5934\u4e0a\u5374\u6234\u7740 \u9876\u8f66\u8f6e\u822c\u7684\u5927\u8349\u5e3d\u3002 \u3000\u3000\u5c31\u50cf\u662f\u534a\u622a\u7b77\u5b50\u4e0a\u9876\u7740\u4e2a\u83dc\u789f\u4f3c\u7684\uff0c\u6574\u4e2a\u4eba\u90fd\u7b3c\u7f69\u5728\u8fd9\u8349\u5e3d\u7684\u9634\u5f71\u4e0b\uff0c\u6839\u672c\u77a7\u4e0d\u89c1\u4ed6\u7684 \u9762\u76ee\u3002 \u3000\u3000\u6591\u7684\u90a3\u4eba\u5374\u662f\u773c\u5982\u94dc\u94c3\uff0c\u8170\u5927\u5341\u56f4\uff0c\u6ee1\u5934\u4e71\u53d1\uff0c\u677e\u677e\u7684\u633d\u4e86\u4e2a\u9afb\uff0c\u770b\u6765\u5c31\u50cf\u662f\u5c71\u795e\u5e99\u91cc \u7684\u4e08\u4e8c\u91d1\u521a\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u7684\u8863\u670d\u672c\u90fd\u5341\u5206\u534e\u8d35\uff0c\u526a\u88c1\u4e5f\u663e\u7136\u662f\u4e0a\u7b49\u624b\u5de5\uff0c\u4f46\u4e00\u7a7f\u5728\u4ed6\u4eec\u8eab\u4e0a\uff0c\u5c31\u53d8\u5f97\u4e0d\u6210 \u6837\u5b50\u3002 \u3000\u3000\u77ee\u7684\u8fd9\u4eba\u597d\u597d\u4e00\u4ef6\u6c34\u6e56\u7f0e\u886b\u4e0a\uff0c\u5230\u5904\u90fd\u662f\u6cb9\u6e0d\uff0c\u660e\u660e\u662f\u7b2c\u4e00\u7c92\u6263\u5b50\uff0c\u4ed6\u5374\u6263\u5230\u7b2c\u53c1\u4e2a\u94ae \u6d1e\u91cc\u3002 \u3000\u3000\u6591\u7684\u90a3\u4eba\u4e00\u4ef6\u888d\u5b50\u7adf\u662f\u6c34\u7ea2\u8272\u7684\uff0c\u800c\u4e14\u81f3\u5c11\u5c0f\u4e86\u53c1\u53f7\uff0c\u77ed\u4e86\u4e24\u5c3a\uff0c\u7a7f\u5728\u8eab\u4e0a\uff0c\u5c31\u50cf\u662f\u5077 \u6765\u7684\u3002 \u3000\u3000\u8fd9\u9ebd\u6837\u4e24\u4e2a\u4eba\uff0c\u7adf\u6709\u90a3\u9ebd\u9ad8\u660e\u7684\u529f\u592b\uff0c\u80e1\u94c1\u82b1\u51e0\u4e4e\u4e0d\u76f8\u4fe1\u81ea\u5df1\u7684\u773c\u775b\uff0c\u5fcd\u4e0d\u4f4f\u5927\u58f0\u95ee \u9053\uff1a\u201c\u4f60\u4eec\u662f\u4ec0\u9ebd\u4eba\uff1f\u4e3a\u4ec0\u9ebd\u2026\u2026\u2026\u201d \u3000\u3000\u4ed6\u8bdd\u672b\u8bf4\u5b8c\uff0c\u90a3\u77ee\u5b50\u5df2\u53eb\u4e86\u8d77\u6765\uff0c\u9053\uff1a\u201c\u4f60\u8fde\u6211\u90fd\u4e0d\u8ba4\u5f97\u9ebd\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u51b7\u7b11\u9053\uff1a\u201c\u5802\u5802\u7684\u80e1\u94c1\u82b1\u80e1\u5927\u4fa0\uff0c\u600e\u4f1a\u8ba4\u5f97\u4f60\u4eec\u8fd9\u6837\u7684\u4eba\uff1f\u201d \u3000\u3000\u90a3\u77ee\u5b50\u53f9\u4e86\u53e3\u6c14\uff0c\u5583\u5583\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u8fd9\u5c0f\u5b50\u5728\u6c5f\u6e56\u4e2d\u6df7\u4e86\u8fd9\u9ebd\u591a\u5e74\uff0c\u7adf\u5b8c\u5168\u662f\u767d\u6df7\u7684\uff0c \u7adf\u8fde\u6211\u8001\u4eba\u5bb6\u4ed6\u90fd\u4e0d\u8ba4\u8bc6\u3002\u201d \u3000\u3000\u4ed6\u4e00\u9762\u8bf4\u7740\u8bdd\uff0c\u4e00\u9762\u5df2\u5c06\u5934\u4e0a\u90a3\u9876\u5927\u8349\u5e3d\u6458\u4e86\u4e0b\u6765\uff0c\u9053\uff1a\u201c\u4f60\u518d\u770b\u770b\u6211\u662f\u8c01\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u8fd9\u624d\u53d1\u73b0\uff0c\u8fd9\u4eba\u5934\u4e0a\u5149\u79c3\u79c3\u7684\u8fde\u4e00\u6839\u5934\u53d1\u90fd\u6ca1\u6709\uff0c\u800c\u4e14\u4e00\u4e2a\u5934\u81f3\u5c11\u6bd4\u522b\u4eba\u8981\u5927\u4e00 \u534a\u3002 \u3000\u3000\u8fd9\u53c8\u50cf\u5728\u534a\u622a\u7b77\u5b50\u4e0a\u63d2\u7740\u4e2a\u9992\u5934\uff0c\u80e1\u94c1\u82b1\u82e5\u975e\u5168\u8eab\u53d1\u9ebb\uff0c\u6b64\u523b\u771f\u5fcd\u4e0d\u4f4f\u8981\u7b11\u4e86\u51fa\u6765\u3002 \u3000\u3000\u90a3\u77ee\u5b50\u9053\uff1a\u201c\u73b0\u5728\u4f60\u8fd8\u672a\u770b\u51fa\u6211\u8001\u4eba\u5bb6\u662f\u8c01\u9ebd\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u6211\u53ea\u4e0d\u8fc7\u5df2\u770b\u51fa\u4f60\u662f\u4e2a\u79c3\u5b50\u800c\u5df2\uff0c\u8fd9\u4e5f\u6ca1\u4ec0\u9ebd\u7a00\u5947\u3002\u201d \u3000\u3000\u90a3\u77ee\u5b50\u4e5f\u4e0d\u751f\u6c14\uff0c\u53cd\u800c\u7b11\u563b\u563b\u9053\uff1a\u201c\u79c3\u5b50\u5c31\u6ca1\u6709\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u6ca1\u6709\u4ec0\u9ebd\uff1f\u2026\u2026\u2026\u81ea\u7136\u662f\u6ca1\u6709\u5934\u53d1\u3002\u201d \u3000\u3000\u90a3\u77ee\u5b50\u9053\uff1a\u201c\u6ca1\u6709\u5934\u53d1\uff0c\u5c31\u662f\u201c\u65e0\u53d1\u201d\uff0c\u5bf9\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u4ece\u6765\u4e5f\u6ca1\u6709\u89c1\u8fc7\u5982\u6b64\u565c\u55e6\u7684\u4eba\uff0c\u7b80\u76f4\u61d2\u5f97\u7406\u4ed6\u4e86\u3002 \u3000\u3000\u8fd9\u77ee\u5b50\u5df2\u53c8\u5c06\u90a3\u9876\u5927\u8349\u5e3d\u6234\u5728\u5934\u4e0a\uff0c\u62ac\u8d77\u5934\u6765\uff0c\u7b11\u563b\u563b\u9053\uff1a\u201c\u5929\u5728\u90a3\u91cc\uff0c\u5929\u600e\u9ebd\u4e0d\u89c1 \u4e86\uff1f\u201d \u3000\u3000\u4ed6\u6570\u4e86\u9876\u8fd9\u9ebd\u5927\u7684\u8349\u5e3d\uff0c\u7684\u786e\u518d\u4e5f\u77a7\u4e0d\u89c1\u5929\uff0c\u80e1\u94c1\u82b1\u53c8\u5fcd\u4e0d\u4f4f\u8981\u7b11\uff0c\u4f46\u8f6c\u5ff5\u4e00\u60f3\uff0c\u8138\u4e0a \u7684\u8089\u5ffd\u7136\u5168\u90fd\u50f5\u4f4f\u3002 \u3000\u3000\u90a3\u77ee\u5b50\u7b11\u9053\uff1a\u201c\u73b0\u5728\u4f60\u603b\u8be5\u77e5\u9053\u6211\u8001\u4eba\u5bb6\u662f\u8c01\u4e86\u5427\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u55c4\u58f0\u9053\uff1a\u201c\u4f60\u2026\u2026\u2026\u4f60\u83ab\u975e\u5c31\u662f\u201c\u65e0\u6cd5\u65e0\u5929\u201d\u5c60\u72d7\u7fc1\uff1f\u201d \u3000\u3000\u90a3\u77ee\u5b50\u62cd\u624b\u5927\u7b11\u9053\uff1a\u201c\u4f60\u5c0f\u5b50\u603b\u7b97\u8fd8\u6709\u70b9\u89c1\u8bc6\uff0c\u5b7a\u5b50\u53ef\u6559\uff0c\u5b7a\u5b50\u53ef\u6559\u3002\u201d \u3000\u3000\u90a3\u53c8\u4f38\u624b\u5411\u90a3\u5de8\u4eba\u4e00\u6307\uff0c\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\u4ed6\u662f\u8c01\u9ebd\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u53f9\u4e86\u53e3\u6c14\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u5c60\u72d7\u7fc1\u548c\u675c\u6e14\u5a46\u7d20\u6765\u79e4\u4e0d\u79bb\u9524\uff0c\u9524\u4e0d\u79bb\u79e4\uff0c\u6211\u600e\u4f1a\u4e0d\u77e5 \u9053\u3002\u201d \u3000\u3000\u5c60\u72d7\u7fc1\u5927\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u5c31\u662f\u6211\u7684\u8001\u5a46\u201c\u5929\u7f57\u5730\u7f51\u201d\u675c\u6e14\u5a46\uff0c\u6211\u8001\u4eba\u5bb6\u867d\u7136\u65e0\u6cd5\u65e0 \u5929\uff0c\u4f46\u4e00\u8fdb\u4e86\u5b83\u7684\u5929\u7f57\u5730\u7f51\uff0c\u5c31\u518d\u4e5f\u7ffb\u4e0d\u4e86\u8eab\u3002\u201d \u3000\u3000\u8fd9\u5de8\u7075\u795e\u822c\u7684\u5e9e\u7136\u5927\u7269\uff0c\u7adf\u662f\u4e2a\u5973\u4eba\uff0c\u5df2\u4ee4\u4eba\u4e0d\u53ef\u601d\u8bae\u4e86\uff0c\u5979\u7adf\u4f1a\u662f\u8fd9\u4f8f\u5112\u7684\u8001\u5a46\uff0c\u66f4 \u4ee4\u4eba\u8981\u7b11\u7834\u809a\u5b50\u3002 \u3000\u3000\u53ef\u662f\u80e1\u94c1\u82b1\u5374\u5df2\u7b11\u4e0d\u51fa\u6765\u4e86\u3002 \u3000\u3000\u53ea\u56e0\u4ed6\u77e5\u9053\u8fd9\u4e24\u4eba\u6a21\u6837\u6ed1\u7a3d\uff0c\u5374\u662f\u767e\u5e74\u6765\u6b66\u6797\u6700\u8d1f\u76db\u540d\uff0c\u6b66\u529f\u4e5f\u6700\u9ad8\u7684\u56db\u5bf9\u592b\u59bb\u4e4b\u4e00\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u975e\u4f46\u7528\u7684\u90fd\u662f\u6c5f\u6e56\u4e2d\u6781\u7f55\u89c1\u7684\u5916\u95e8\u5175\u5203\uff0c\u800c\u4e14\u6b66\u529f\u8be1\u5f02\uff0c\u884c\u4e8b\u96be\u6d4b\uff0c\u4ece\u6765\u6ca1\u6709\u4eba \u77e5\u9053\u8fd9\u592b\u59bb\u4e24\u4eba\u7684\u5e08\u627f\uff0c\u4e5f\u6c38\u8fdc\u6ca1\u6709\u4eba\u77e5\u9053\u4ed6\u4eec\u66fe\u5f80\u4ec0\u9ebd\u65f6\u5019\u51fa\u73b0\uff0c\u6709\u65f6\u8fd9\u4e24\u4eba\u5c31\u4f1a\u50cf\u4e00\u9635 \u98ce\u4f3c\u7684\uff0c\u7a81\u7136\u6d88\u5931\uff0c\u4e8c\u53c1\u5341\u5e74\u90fd\u542c\u4e0d\u5230\u4ed6\u4eec\u7684\u6d88\u606f\uff0c\u66f4\u6ca1\u6709\u4eba\u77e5\u9053\u4ed6\u4eec\u5230\u90a3\u91cc\u53bb\u4e86\u3002 \u3000\u3000\u4f46\u6c5f\u6e56\u4e2d\u4eba\u4eba\u90fd\u77e5\u9053\u4e00\u4ef6\u4e8b\uff0c\u90a3\u5c31\u662f\uff1a\u5b81\u53ef\u5f97\u7f6a\u5929\u738b\u8001\u5b50\uff0c\u4e5f\u4e0d\u80fd\u5f97\u7f6a\u8fd9\u592b\u5987\u4e24\u4eba\uff0c\u65e0 \u8bba\u8c01\u82e5\u5f97\u7f6a\u4e86\u4ed6\u4eec\uff0c\u5c31\u4f11\u60f3\u518d\u8fc7\u4e00\u5929\u597d\u65e5\u5b50\u3002 \u3000\u3000\u53ea\u89c1\u5c60\u72d7\u7fc1\u8fd8\u5728\u54c8\u54c8\u5927\u7b11\uff0c\u7b11\u5f97\u51e0\u4e4e\u8fde\u6c14\u90fd\u5598\u4e0d\u8fc7\u6765\u4e86\uff0c\u4f46\u675c\u6e14\u5a46\u77aa\u4e86\u4ed6\u4e00\u773c\u5f8c\uff0c\u4ed6\u5c31 \u7acb\u523b\u518d\u4e5f\u4e0d\u6562\u7b11\u4e00\u58f0\u3002 \u3000\u3000\u5979\u4e0d\u77aa\u773c\u775b\u8fd8\u597d\uff0c\u8fd9\u4e00\u77aa\u773c\uff0c\u4e00\u751f\u6c14\uff0c\u5168\u8eab\u7684\u8863\u670d\u90fd\u50cf\u662f\u8981\u80c0\u88c2\u4e86\uff0c\u80e1\u94c1\u82b1\u4e5f\u4e0d\u61c2\u5979\u4e3a \u4f55\u8981\u7a7f\u8fd9\u9ebd\u5c0f\u7684\u8863\u670d\u3002 \u3000\u3000\u5374\u4e0d\u77e5\u5927\u811a\u7684\u5973\u4eba\u4e00\u5b9a\u90fd\u559c\u6b22\u7a7f\u5c0f\u978b\u5b50\uff0c\u80d6\u7684\u5973\u4eba\u4e5f\u4e00\u5b9a\u559c\u6b22\u7a7f\u5c0f\u8863\u670d\uff0c\u9ad8\u7684\u5973\u4eba\u82e5\u5ac1 \u4e86\u4e2a\u77ee\u4e08\u592b\uff0c\u66f4\u6068\u4e0d\u5f97\u5c06\u81ea\u5df1\u7684\u817f\u952f\u6389\u4e00\u622a--\u817f\u65e2\u4e0d\u80fd\u952f\uff0c\u5c06\u8863\u670d\u505a\u77e9\u4e8c\u5c3a\uff0c\u4e5f\u662f\u8212\u670d\u7684\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u5ffd\u7136\u51b7\u7b11\u9053\uff1a\u201c\u522b\u4eba\u90fd\u8bf4\u5c60\u72d7\u7fc1\u592b\u59bb\u5982\u4f55\u5982\u4f55\u5389\u5bb3\uff0c\u539f\u6765\u4e5f\u4e0d\u8fc7\u5982\u6b64\u800c\u5df2\u3002\u201d \u3000\u3000\u5c60\u72d7\u7fc1\u9053\uff1a\u201c\u6211\u8001\u4eba\u5bb6\u8fde\u624b\u90e8\u6ca1\u6709\u52a8\uff0c\u4f60\u5df2\u7ecf\u8eba\u4e0b\u4e86\uff0c\u96be\u9053\u8fd8\u4e0d\u670d\u6c14\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u5389\u58f0\u9053\uff1a\u201c\u4f60\u82e5\u6562\u548c\u6211\u5149\u660e\u6b63\u5927\u7684\u52a8\u624b\uff0c\u80fd\u80dc\u5f97\u4e86\u6211\u4e00\u62db\u534a\u5f0f\uff0c\u6211\u81ea\u7136\u6ca1\u6709\u8bdd \u8bf4\uff0c\u4f46\u7528\u8fd9\u6837\u7684\u8be1\u8ba1\u4f24\u4eba\uff0c\u5374\u7b97\u4e0d\u4e86\u82f1\u96c4\u3002\u201d \u3000\u3000\u5c60\u72d7\u7fc1\u5927\u7b11\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u8fd9\u5c31\u662f\u5916\u884c\u8bdd\u4e86\uff0c\u4e24\u4eba\u52a8\u624b\uff0c\u53ea\u8981\u80fd\u5c06\u5bf9\u65b9\u6253\u8eba\u4e0b\uff0c\u65e0\u8bba\u7528\u4ec0 \u9ebd\u6cd5\u5b50\u90fd\u662f\u672c\u4e8b\uff0c\u6211\u8001\u4eba\u5bb6\u82e5\u80fd\u653e\u4e2a\u5c41\u5c31\u5c06\u4f60\u718f\u6b7b\uff0c\u4f60\u66f4\u8be5\u670d\u6c14\u624d\u662f\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u7adf\u88ab\u4ed6\u6c14\u5f97\u8fde\u8bdd\u90fd\u8bf4\u4e0d\u51fa\u6765\u4e86\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u53d1\u89c9\uff0c\u6b64\u523b\u975e\u4f46\u81ea\u5df1\u5168\u8eab\u53d1\u9ebb\uff0c\u538b\u5728\u4ed6\u8eab\u4e0a\u7684\u695a\u7559\u9999\uff0c\u66f4\u662f\u8fde\u52a8\u90fd\u6ca1\u6709\u52a8\uff0c\u50cf\u662f \u8fde\u6c14\u90fd\u6ca1\u6709\u4e86\u3002 \u3000\u3000\u4ed6\u5927\u9a87\u4e4b\u4e0b\uff0c\u5931\u58f0\u9053\uff1a\u201c\u8001\u2026\u2026\u2026\u8001\u695a\uff0c\u4f60\u4e3a\u4ec0\u9ebd\u4e0d\u8bf4\u8bdd\uff1f\u4f60\u96be\u9053\u2026\u2026\u2026\u201d \u3000\u3000\u5c60\u72d7\u7fc1\u683c\u683c\u7b11\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u8fd9\u53c8\u662f\u5916\u884c\u8bdd\u4e86\uff0c\u4f60\u96be\u9053\u672a\u77a7\u89c1\uff0c\u6211\u8001\u4eba\u5bb6\u65b9\u624d\u5c06\u67aa\u62bd\u51fa\u6765 \u7684\u65f6\u5019\uff0c\u5df2\u987a\u624b\u6253\u4e86\u4ed6\u4e24\u5904\u7a74\u9053\u3002\u201d \u3000\u3000\u4ed6\u7b11\u7740\u8d70\u4e86\u8fc7\u6765\uff0c\u53c8\u9053\uff1a\u201c\u8fd9\u4e5f\u8bb8\u662f\u6211\u8001\u4eba\u5bb6\u65b9\u624d\u51fa\u624b\u592a\u5feb\u4e86\uff0c\u6240\u4ee5\u4f60\u77a7\u4e0d\u6e05\u695a\uff0c\u73b0 \u5728\u2026\u2026\u2026\u201d \u3000\u3000\u4ed6\u8bdd\u8fd8\u6ca1\u6709\u8bf4\u5b8c\uff0c\u4eba\u521a\u8d70\u5230\u695a\u7559\u9999\u9762\u524d\uff0c\u5ffd\u7136\u95f4\uff0c\u695a\u7559\u9999\u7684\u4e00\u53cc\u624b\u7adf\u95ea\u7535\u822c\u81ea\u7f51\u773c\u91cc\u4f38 \u4e86\u51fa\u6765\u5c60\u72d7\u7fc1\u663e\u7136\u505a\u68a6\u4e5f\u672a\u60f3\u5230\u6709\u6b64\u4e00\u7740\uff0c\u5927\u60ca\u4e4b\u4e0b\uff0c\u4e00\u53cc\u817f\u5df2\u88ab\u695a\u7559\u9999\u6293\u4f4f\uff0c\u987a\u624b\u4e00\u6296\uff0c \u4ed6\u7684\u4eba\u4e5f\u8eba\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u675c\u6e14\u5a46\u6012\u543c\u4e00\u58f0\uff0c\u98de\u6251\u800c\u8d77\u3002 \u3000\u3000\u53ea\u542c\u695a\u7559\u9999\u53f1\u9053\uff1a\u201c\u7ad9\u4f4f\uff0c\u5426\u5219\u4f60\u7684\u8001\u516c\u5c31\u6ca1\u6709\u547d\u4e86\u3002\u201d \u3000\u3000\u675c\u6e14\u5a46\u679c\u7136\u4e0d\u6562\u518d\u5f80\u524d\u8d70\u4e00\u6b65\uff0c\u76ee\u5149\u4e2d\u5145\u6ee1\u4e86\u5173\u5207\u7126\u6025\u4e4b\u8272\uff0c\u663e\u89c1\u5979\u5bf9\u8fd9\u77ee\u5c0f\u7684\u4e08\u592b\uff0c \u5b9e\u662f\u60c5\u6df1\u7231\u91cd\u3002 \u3000\u3000\u5c60\u72d7\u7fc1\u5df2\u7834\u53e3\u5927\u9a82\u9053\uff1a\u201c\u5c0f\u6742\u79cd\uff0c\u7528\u8fd9\u79cd\u624b\u6bb5\uff0c\u7b97\u4e0d\u5f97\u82f1\u96c4\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u9053\uff1a\u201c\u4e24\u4eba\u52a8\u624b\uff0c\u53ea\u8981\u80fd\u5c06\u5bf9\u65b9\u6253\u8eba\u4e0b\uff0c\u5c31\u662f\u672c\u4e8b\u2026\u2026\u2026\u8fd9\u8bdd\u662f\u4f60\u81ea\u5df1\u65b9\u624d\u8bf4 \u7684\uff0c\u4f60\u73b0\u5728\u96be\u9053\u5c31\u5fd8\u4e86\u9ebd\uff1f\u201d \u3000\u3000\u5c60\u72d7\u7fc1\u6014\u4e86\u6014\uff0c\u80e1\u94c1\u82b1\u5df2\u5fcd\u4e0d\u4f4f\u5927\u7b11\uff0c\u9053\uff1a\u201c\u5999\u6781\u5999\u6781\uff0c\u8fd9\u5c31\u53eb\u81ea\u642c\u7816\u5934\u81ea\u7838\u811a\uff0c\u81ea\u5df1 \u653e\u5c41\u81ea\u5df1\u55c5\u3002\u201d \u3000\u3000\u8c01\u77e5\u5c60\u72d7\u7fc1\u4e5f\u5927\u7b11\u8d77\u6765\uff0c\u9053\uff1a\u201c\u597d\u597d\u597d\uff0c\u695a\u7559\u9999\u679c\u7136\u6709\u4e24\u4e0b\u5b50\uff0c\u96be\u602a\u522b\u4eba\u6015\u4f60\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u5c82\u6562\u5c82\u6562\u3002\u201d \u3000\u3000\u5c60\u72d7\u7fc1\u9053\uff1a\u201c\u4f46\u6709\u4ef6\u4e8b\u6211\u5b9e\u5728\u4e0d\u660e\u767d\uff0c\u6211\u65b9\u624d\u660e\u660e\u70b9\u4e86\u4f60\u7684\u7a74\u9053\uff0c\u7b97\u51c6\u4f60\u5728\u4e00\u4e2a\u5bf9\u65f6\u4e2d \u8fde\u5c41\u90fd\u653e\u4e0d\u51fa\u7684\uff0c\u4f60\u600e\u9ebd\u5ffd\u7136\u80fd\u52a8\u624b\u4e86\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u4f60\u70b9\u4e86\u6211\u7a74\u9053\u65f6\uff0c\u6211\u8eab\u5b50\u5df2\u843d\u4e86\u4e0b\u53bb\u3002\u201d \u3000\u3000\u5c60\u72d7\u7fc1\u622a\u53e3\u9053\uff1a\u201c\u4f60\u975e\u4f46\u7acb\u523b\u5c31\u6389\u4e86\u4e0b\u53bb\uff0c\u800c\u4e14\u7acb\u523b\u5c31\u649e\u4e0a\u4e86\u8fd9\u59d3\u80e1\u7684\u5c0f\u5b50\uff0c\u90a3\u91cc\u6709\u673a \u4f1a\u9f99\u884c\u529f\u8fd0\u6c14\uff0c\u81ea\u5df1\u89e3\u5f00\u7a74\u9053\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u5728\u4e0b\u8fd8\u6ca1\u6709\u90a3\u79cd\u884c\u529f\u8fd0\u6c14\uff0c\u81ea\u89e3\u7a74\u9053\u7684\u7edd\u9876\u529f\u592b\uff0c\u9601\u4e0b\u672a\u514d\u8fc7\u5956\u4e86\u3002\u201d \u3000\u3000\u5c60\u72d7\u7fc1\u9053\uff1a\u201c\u90a3\u9ebd\u4f60\u7528\u7684\u662f\u4ec0\u9ebd\u6cd5\u5b50\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4efb\u4f55\u4eba\u5728\u7a74\u9053\u88ab\u70b9\u5f8c\u7684\u90a3\u4e00\u5239\u95f4\uff0c\u5374\u8fd8\u80fd\u52a8\u4e00\u52a8\u7684\uff0c\u662f\u9ebd\uff1f\u201d \u3000\u3000\u5c60\u72d7\u7fc1\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u56e0\u4e3a\u90a3\u65f6\u4ed6\u7a74\u9053\u867d\u5df2\u88ab\u5c01\u6b7b\uff0c\u4f46\u8eab\u5b50\u91cc\u8fd8\u6709\u4e00\u4e1d\u6b8b\u9980\u7684\u771f\u6c14\u6d41\u52a8\uff0c \u4f46\u8fd9\u4e5f\u53ea\u4e0d\u8fc7\u80fd\u52a8\u4e00\u4e0b\u800c\u5df2\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u52a8\u4e00\u4e0b\u5b50\u5c31\u5df2\u8db3\u591f\u4e86\u3002\u201d \u3000\u3000\u5c60\u72d7\u7fc1\u773c\u775b\u4e00\u4eae\uff0c\u5931\u58f0\u9053\uff1a\u201c\u6211\u660e\u767d\u4e86\uff0c\u90a3\u65f6\u4f60\u77e5\u9053\u81ea\u5df1\u201c\u6c14\u8840\u6d77\u7a74\u201d\u88ab\u70b9\uff0c\u8f68\u7acb\u523b\u5c06 \u8eab\u5b50\u52a8\u4e86\u52a8\uff0c\u8ba9\u8fd9\u59d3\u80e1\u7684\u5c0f\u5b50\u649e\u5f00\u4e86\u8fd9\u4e24\u5904\u7a74\u9053\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u5fae\u7b11\u9053\uff1a\u201c\u6b63\u662f\u5982\u6b64\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u542c\u5f97\u53c8\u60ca\u53c8\u559c\uff0c\u53c8\u5927\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u8001\u5934\u5b50\u603b\u7b97\u8fd8\u6709\u4e9b\u89c1\u8bc6\uff0c\u5b7a\u5b50\u53ef\u6559\uff0c\u5b7a\u5b50\u53ef \u6559\u3002\u201d \u3000\u3000\u5c60\u72d7\u7fc1\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u695a\u7559\u9999\u5440\u695a\u7559\u9999\uff0c\u4f60\u679c\u7136\u662f\u4e2a\u9b3c\u7075\u7cbe\uff0c\u60f3\u4e0d\u5230\u6211\u8001\u4eba\u5bb6\u6d3b\u4e86\u516d \u4e03\u5341\u5c81\uff0c\u4eca\u5929\u7adf\u6211\u5728\u4f60\u8fd9\u6bdb\u5934\u5c0f\u4f19\u5b50\u624b\u91cc\u3002\u201d \u3000\u3000\u675c\u6e14\u5a46\u773c\u775b\u59cb\u7ec8\u77aa\u7740\u695a\u7559\u9999\uff0c\u55c4\u58f0\u9053\uff1a\u201c\u73b0\u5728\u4f60\u60f3\u600e\u9ebd\u6837\uff1f\u201d \u3000\u3000\u8fd9\u65f6\u80e1\u94c1\u82b1\u5df2\u81ea\u695a\u7559\u9999\u8eab\u5b50\u4e0b\u722c\u4e86\u8d77\u6765\uff0c\u800c\u4e14\u5df2\u7ecf\u89e3\u5f00\u4e86\u90a3\u9762\u5de8\u7f51\u3002 \u3000\u3000\u675c\u6e14\u5a46\u4e5f\u53ea\u6709\u773c\u7741\u7741\u7684\u77a7\u7740\u3002 \u3000\u3000\u695a\u7559\u9999\u957f\u8eab\u800c\u8d77\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u4e24\u4f4d\u548c\u5728\u4e0b\u6709\u4ec0\u9ebd\u51a4\u4ec7\u9ebd\uff1f\u201d \u3000\u3000\u675c\u6e14\u5a46\u7acb\u523b\u9053\uff1a\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u4e24\u4f4d\u65e2\u7136\u548c\u5728\u4e0b\u7d20\u65e0\u51a4\u4ec7\uff0c\u4e3a\u4f55\u8981\u5bf9\u5728\u4e0b\u5982\u6b64\uff1f\u201d \u3000\u3000\u675c\u6e14\u5a46\u9ed8\u7136\u534a\u664c\uff0c\u957f\u53f9\u9053\uff1a\u201c\u6211\u592b\u59bb\u505a\u4e8b\u7d20\u6765\u6069\u6028\u5206\u660e\uff0c\u672c\u65e0\u4f24\u4f60\u4e4b\u610f\uff0c\u53ea\u4e0d \u8fc7\u2026\u2026\u2026\u201d \u3000\u3000\u695a\u7559\u9999\u63a5\u53e3\u9053\uff1a\u201c\u53ea\u4e0d\u8fc7\u4e24\u4f4d\u6614\u5e74\u66fe\u7ecf\u53d7\u8fc7\u674e\u89c2\u9c7c\u7684\u6069\uff0c\u6240\u4ee5\u8981\u5c06\u6211\u6349\u4f4f\uff0c\u9001\u5230\u201c\u62e5\u7fe0 \u5c71\u5e84\u201d\u53bb\uff0c\u662f\u9ebd\uff1f\u201d \u3000\u3000\u675c\u9c7c\u5a46\u8fd8\u672b\u8bf4\u8bdd\uff0c\u5c60\u72d7\u7fc1\u5df2\u5927\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u8001\u4eba\u5bb6\u672c\u6765\u662f\u60f3\u5c06\u4f60\u4eec\u4e24\u4e2a\u5c0f\u5a03\u513f\u9001\u53bb \u505a\u4eba\u60c5\uff0c\u6240\u4ee5\u4f60\u73b0\u5728\u82e5\u8981\u6740\u6211\uff0c\u4e5f\u662f\u5929\u7ecf\u5730\u4e49\u7684\u4e8b\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u6211\u82e5\u4e0d\u60f3\u6740\u4f60\u5462\uff1f\u201d \u3000\u3000\u5c60\u72d7\u7fc1\u9053\uff1a\u201c\u6211\u529d\u4f60\u8fd8\u662f\u6740\u4e86\u6211\u597d\uff0c\u6211\u8fd9\u4eba\u6c14\u91cf\u6700\u7a84\uff0c\u4eca\u65e5\u65e2\u7136\u683d\u5728\u4f60\u624b\u91cc\uff0c\u4f60\u5c31\u7b97\u653e \u4e86\u6211\uff0c\u4ee5\u5f8c\u8bf4\u4e0d\u5b9a\u6211\u8fd8\u662f\u4f1a\u6765\u627e\u4f60\u9ebb\u70e6\u7684\u3002\u201d \u3000\u3000\u675c\u6e14\u5a46\u53d8\u8272\u9053\uff1a\u201c\u4f60\u2026\u2026\u2026\u4f60\u8fd9\u662f\u5728\u529d\u522b\u4eba\u6740\u4f60\u9ebd\uff1f\u201d \u3000\u3000\u5c60\u72d7\u7fc1\u7b11\u9053\uff1a\u201c\u8fd9\u4e5f\u6ca1\u4ec0\u9ebd\u5173\u7cfb\uff0c\u53cd\u6b63\u6211\u505a\u7537\u4eba\u5df2\u7ecf\u505a\u817b\u4e86\uff0c\u65e9\u6b7b\u65e9\u6295\u80ce\uff0c\u4e0b\u8f88\u5b50\u4e00\u5b9a \u6295\u80ce\u505a\u4e2a\u5973\u4eba\uff0c\u518d\u5ac1\u7ed9\u4f60\uff0c\u8ba9\u4f60\u4e5f\u505a\u4e08\u592b\u7684\u6ecb\u5473\uff0c\u8fd9\u6837\u6211\u4eec\u4e24\u4e2a\u4eba\u624d\u7b97\u626f\u5e73\u3002\u201d \u3000\u3000\u675c\u6e14\u5a46\u8138\u8272\u6c14\u5f97\u94c1\u9752\uff0c\u5636\u58f0\u9053\uff1a\u201c\u4f60\u771f\u6562\u5bf9\u6211\u5982\u6b64\u8bf4\u8bdd\u3002\u201d \u3000\u3000\u5c60\u72d7\u7fc1\u9053\uff1a\u201c\u4e00\u4e2a\u4eba\u82e5\u662f\u53cd\u6b63\u90fd\u8981\u6b7b\u4e86\uff0c\u8fd8\u6709\u4ec0\u9ebd\u8bdd\u4e0d\u6562\u8bf4\u7684\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u5fcd\u4e0d\u4f4f\u9053\uff1a\u201c\u695a\u7559\u9999\u82e5\u662f\u5c06\u4f60\u653e\u4e86\u5462\uff1f\u201d \u3000\u3000\u5c60\u72d7\u7fc1\u9053\uff1a\u201c\u4ed6\u4e3a\u4ec0\u9ebd\u8981\u653e\u6211\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u4ed6\u4e3a\u4ec0\u9ebd\u4e0d\u80fd\u653e\u4f60\uff1f\u201d \u3000\u3000\u5c60\u72d7\u7fc1\u9053\uff1a\u201c\u6211\u90a3\u6837\u5bf9\u4ed8\u4ed6\uff0c\u4ed6\u82e5\u8fd8\u4f1a\u653e\u4e86\u6211\uff0c\u4ed6\u5c31\u662f\u4e2a\u75af\u5b50\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u7b11\u9053\uff1a\u201c\u4ed6\u5e76\u4e0d\u662f\u75af\u5b50\uff0c\u53ea\u4e0d\u8fc7\u662f\u4e2a\u541b\u5b50\u800c\u5df2\uff0c\u4f60\u4ee5\u5c0f\u4eba\u4e4b\u5fc3\u5ea6\u541b\u5b50\u4e4b\u8179\uff0c\u624d\u4ee5 \u4e3a\u4ed6\u4f1a\u6740\u4f60\u3002\u201d \u3000\u3000\u5c60\u72d7\u7fc1\u6014\u4e86\u6014\uff0c\u9053\uff1a\u201c\u4ed6\u82e5\u4e0d\u6740\u6211\uff0c\u90a3\u5c31\u771f\u7684\u7cdf\u4e86\u3002\u201d"}),showOnHomepage:!0},{name:"\u7b2c\u5341\u4e00\u7ae0\u3000\u5251\u9053\u65b0\u8bba",content:(0,s.jsx)(s.Fragment,{children:"\u5c60\u72d7\u7fc1\u592b\u59bb\u5df2\u8d70\u4e86\uff0c\u7ea2\u706f\u5374\u8fd8\u60ac\u6302\u5728\u90a3\u91cc\uff0c\u96fe\u5df2\u7b3c\u7f69\u7740\u5c71\u5dc5\uff0c\u4e73\u767d\u8272\u7684\u6d53\u96fe\u5728\u7ea2\u706f\u6620\u7167 \u4e0b\uff0c\u770b\u6765\u5c31\u50cf\u662f\u4e00\u7247\u98de\u6e85\u51fa\u7684\u8840\u82b1\u3002 \u3000\u3000\u4f46\u56db\u9762\u4ecd\u662f\u65e0\u8fb9\u7684\u9ed1\u6697\uff0c\u4ecd\u7136\u548c\u695a\u7559\u9999\u4ed6\u4eec\u6765\u7684\u65f6\u5019\u4e00\u6837\uff0c\u80e1\u94c1\u82b1\u51dd\u671b\u7740\u8fdc\u65b9\uff0c\u50cf\u662f\u8fd8 \u60f3\u627e\u51fa\u90a3\u592b\u5987\u4e24\u4eba\u7684\u53bb\u5411\u3002 \u3000\u3000\u4f46\u8fd9\u4e00\u53cc\u5947\u5f02\u7684\u592b\u5987\u5df2\u50cf\u98ce\u4e00\u822c\u6d88\u5931\u4e86\uff0c\u4ece\u6b64\u4ee5\u5f8c\uff0c\u80e1\u94c1\u82b1\u4e5f\u8bb8\u518d\u4e5f\u770b\u4e0d\u5230\u4ed6\u4eec\uff0c\u518d\u4e5f \u542c\u4e0d\u5230\u4ed6\u4eec\u7684\u6d88\u606f\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u7ec8\u65bc\u56de\u5934\u5411\u695a\u7559\u9999\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u65e9\u5c31\u77e5\u9053\u4f60\u4f1a\u653e\u4e86\u4ed6\u4eec\uff0c\u6211\u731c\u7684\u679c\u7136\u4e0d\u9519\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u60a0\u7136\u9053\uff1a\u201c\u4f60\u82e5\u662f\u6211\uff0c\u4f60\u96be\u9053\u4f1a\u6740\u4e86\u4ed6\u5417\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u7b11\u9053\uff1a\u201c\u6211\u81ea\u7136\u4e0d\u4f1a\uff0c\u6211\u7edd\u4e0d\u4f1a\u6740\u4e00\u4e2a\u6015\u8001\u5a46\u7684\u4eba\uff0c\u56e0\u4e3a\u6015\u8001\u5a46\u7684\u5927\u591a\u90fd\u4e0d\u4f1a\u662f \u574f\u4eba\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u4e00\u4e2a\u4eba\u82e5\u8fde\u81ea\u5df1\u7684\u8001\u5a46\u90fd\u6015\uff0c\u4ed6\u600e\u9ebd\u8fd8\u4f1a\u6709\u80c6\u5b50\u505a\u522b\u7684\u574f\u4e8b\u3002\u201d \u3000\u3000\u4ed6\u62cd\u7740\u695a\u7559\u9999\u7684\u80a9\u5934\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u653e\u4e86\u90a3\u5c60\u72d7\u7fc1\u65f6\uff0c\u4f60\u53ef\u770b\u5230\u4ed6\u7684\u7751\u8272\u9ebd\uff1f\u6211\u5374\u770b\u5230 \u4e86\uff0c\u6211\u7b80\u76f4\u4ece\u6765\u4e5f\u6ca1\u6709\u89c1\u8fc7\u6bd4\u4ed6\u90a3\u65f6\u66f4\u96be\u770b\u7684\u8138\u8272\uff0c\u4ed6\u597d\u50cf\u771f\u7684\u5b81\u53ef\u88ab\u4f60\u6740\u6b7b\uff0c\u4e5f\u4e0d\u613f\u8ddf\u4ed6 \u8001\u5a46\u56de\u53bb\uff0c\u4ed6\u56de\u53bb\u4e4b\u5f8c\u4f1a\u53d7\u4ec0\u9ebd\u6837\u7684\u7f6a\uff0c\u6211\u7b80\u76f4\u4e0d\u6562\u60f3\u50cf\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u9053\uff1a\u201c\u4f60\u8ba4\u4e3a\u4ed6\u662f\u5728\u53d7\u7f6a\uff0c\u4ed6\u81ea\u5df1\u4e5f\u8bb8\u5374\u8ba4\u4e3a\u662f\u79cd\u4eab\u53d7\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u53eb\u4e86\u8d77\u6765\uff0c\u9053\uff1a\u201c\u4eab\u53d7\uff1f\u8dea\u7b97\u76d8\uff0c\u9876\u591c\u58f6\uff0c\u4e5f\u80fd\u7b97\u662f\u4eab\u53d7\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4e3a\u4ec0\u9ebd\u4e0d\u80fd\u7b97\u662f\u4eab\u53d7\uff1f\u675c\u6e14\u5a46\u4f1a\u8981\u4f60\u9876\u591c\u58f6\u9ebd\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u53eb\u9053\uff1a\u201c\u5f53\u7136\u4e0d\u4f1a\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u8fd9\u5c31\u5bf9\u4e86\uff0c\u675c\u6e14\u5a46\u7edd\u4e0d\u4f1a\u8981\u4f60\u9876\u591c\u58f6\uff0c\u53ea\u56e0\u5979\u4e0d\u559c\u6b22\u4f60\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u5979\u8981\u5c60\u72d7\u7fc1\u9876\u591c\u58f6\uff0c\u5c31\u4e3a\u7684\u662f\u5979\u559c\u6b22\u4ed6\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u6b63\u8272\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u5c31\u53eb\u7231\u4e4b\u6df1\uff0c\u8d23\u4e4b\u5207\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u62b1\u7740\u5934\u547b\u541f\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5047\u5982\u6bcf\u4e2a\u5973\u4eba\u90fd\u50cf\u5979\u8fd9\u6837\u7684\u7231\u6cd5\uff0c\u6211\u5012\u4e0d\u5982\u53bb\u505a\u548c\u5c1a \u7684\u597d\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u53f9\u9053\uff1a\u201c\u8fd9\u56e0\u4e3a\u4f60\u6839\u672c\u4e0d\u61c2\u5f97\u4ed6\u4eec\u592b\u5987\u95f4\u7684\u60c5\u611f\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u4f60\u61c2\u5f97\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4f60\u4ee5\u4e3a\u5c60\u72d7\u7fc1\u771f\u7684\u5f88\u6015\u675c\u6e14\u5a46\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u5f53\u7136\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u90a3\u9ebd\u6211\u95ee\u4f60\uff0c\u4ed6\u4e3a\u4ec0\u9ebd\u8981\u6015\u5979\uff1f\u4f60\u96be\u9053\u770b\u4e0d\u51fa\u5c60\u72d7\u7fc1\u7684\u6b66\u529f\u8981\u6bd4\u675c\u6e14\u5a46\u9ad8 \u5f97\u591a\u9ebd\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u6014\u4e86\u6014\uff0c\u5583\u5583\u9053\uff1a\u201c\u662f\u5440\uff01\u675c\u6e14\u5a46\u7684\u8eab\u6cd5\u867d\u5947\u8be1\uff0c\u4f46\u5c60\u72d7\u7fc1\u7684\u5185\u529b\u5374\u66f4\u6df1\u539a\uff0c\u4e24 \u4eba\u82e5\u6253\u8d77\u6765\uff0c\u675c\u6e14\u5a46\u4e00\u5b9a\u4e0d\u662f\u5c60\u72d7\u7fc1\u7684\u654c\u624b\uff0c\u5c60\u72d7\u7fc1\u4e3a\u4ec0\u9ebd\u6015\u5979\u5462\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u544a\u8bc9\u4f60\uff0c\u8fd9\u5c31\u56e0\u4e3a\u5c60\u72d7\u7fc1\u4e5f\u5f88\u7231\u4ed6\u8001\u5a46\uff0c\u4e00\u4e2a\u7537\u4eba\u82e5\u4e0d\u7231\u4ed6\u7684\u8001\u5a46\uff0c\u5c31\u7edd \u4e0d\u4f1a\u6015\u5979\u7684\uff0c\u8fd9\u5c31\u53eb\u56e0\u7231\u800c\u751f\u754f\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u6447\u5934\u9053\uff1a\u201c\u4e0d\u901a\u4e0d\u901a\uff0c\u4f60\u8fd9\u9053\u7406\u7b80\u76f4\u4e0d\u901a\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u9053\uff1a\u201c\u4f60\u5a36\u4e86\u8001\u5a46\u4e4b\u5f8c\uff0c\u5c31\u77e5\u9053\u6211\u8fd9\u9053\u7406\u901a\u4e0d\u901a\u4e86\u3002\u201d \u3000\u3000\u4e24\u4eba\u65b9\u624d\u51fa\u751f\u5165\u6b7b\uff0c\u51e0\u51e0\u4e4e\u5c31\u88ab\u4eba\u5bb6\u9001\u4e86\u7ec8\uff0c\u695a\u7559\u9999\u867d\u4ee5\u4ed6\u7684\u673a\u667a\u53c8\u6253\u4e86\u6b21\u80dc\u4ed7\uff0c\u4f46\u4ee5 \u5f8c\u4ecd\u662f\u8270\u9669\u91cd\u91cd\u3002 \u3000\u3000\u674e\u7389\u51fd\u592b\u5987\u65e2\u80fd\u627e\u5f97\u51fa\u5e05\u4e00\u5e06\u548c\u5c60\u72d7\u7fc1\u8fd9\u6837\u7684\u9ad8\u624b\u6765\u5bf9\u4ed8\u695a\u7559\u9999\uff0c\u4e5f\u5c31\u80fd\u627e\u5f97\u5230\u66f4\u5389\u5bb3 \u7684\u3002 \u3000\u3000\u695a\u7559\u9999\u867d\u7136\u51fb\u9000\u4e86\u5e05\u4e00\u5e06\u548c\u5c60\u72d7\u7fc1\u592b\u5987\uff0c\u4f46\u4e00\u4e2a\u4eba\u7684\u7cbe\u529b\u6bd5\u7adf\u6709\u9650\uff0c\u7a76\u7adf\u8fd8\u80fd\u518d\u6253\u591a\u5c11 \u6b21\u80dc\u4ed7\u4f55\u51b5\uff0c\u82cf\u84c9\u84c9\u3001\u674e\u7ea2\u8896\u3001\u5b8b\u751c\u513f\u548c\u9ed1\u73cd\u73e0\u8fd8\u5728\u5bf9\u65b9\u638c\u63e1\u4e4b\u4e2d\uff0c\u8fd9\u5c31\u50cf\u4e00\u4e2a\u4eba\u7684\u54bd\u5589\u5df2 \u88ab\u5bf9\u65b9\u627c\u4f4f\u3002 \u3000\u3000\u8fd9\u7b80\u76f4\u4ee4\u695a\u7559\u9999\u8fd0\u6c14\u90fd\u900f\u4e0d\u8fc7\u6765\u3002 \u3000\u3000\u5728\u8fd9\u79cd\u8270\u82e6\u5371\u9669\u7684\u60c5\u51b5\u4e0b\uff0c\u4ed6\u4eec\u4e24\u4eba\u5374\u8ba8\u8bba\u8d77\u201c\u6015\u8001\u5a46\u201d\u7684\u95ee\u9898\u6765\uff0c\u522b\u4eba\u542c\u4e86\uff0c\u4e00\u5b9a\u8981 \u4ee5\u4e3a\u4ed6\u4eec\u6709\u4ec0\u9ebd\u6bdb\u75c5\u3002 \u3000\u3000\u5176\u5b9e\u4ed6\u4eec\u5c31\u6b63\u56e0\u4e3a\u77e5\u9053\u672a\u6765\u7684\u8270\u9669\u5c1a\u591a\uff0c\u6240\u4ee5\u6b64\u523b\u624d\u5c3d\u91cf\u4f7f\u81ea\u5df2\u7684\u795e\u7ecf\u677e\u5f1b\uff0c\u624d\u597d\u53bb\u5bf9 \u4ed8\u66f4\u5927\u7684\u5371\u673a\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u7684\u795e\u7ecf\u82e5\u662f\u4eba\u7d27\u5f20\u4e86\uff0c\u5c31\u50cf\u662f\u4e00\u6839\u88ab\u7ef7\u7d27\u7684\u7434\u5f26\uff0c\u53ea\u8981\u88ab\u4eba\u8f7b\u8f7b\u4e00\u78b0\uff0c\u5c31\u4f1a\u65ad \u7684\u3002 \u3000\u3000\u997c\u4e86\u534a\u664c\uff0c\u80e1\u94c1\u82b1\u5ffd\u53c8\u7b11\u9053\uff1a\u201c\u675c\u6e14\u5a46\u5c31\u7b97\u62e7\u7740\u5c60\u72d7\u7fc1\u7684\u8033\u6735\u8d70\uff0c\u751a\u81f3\u63d0\u7740\u4ed6\u7684\u811a\u5728\u5730 \u4e0a\u62d6\uff0c\u6211\u90fd\u4e0d\u4f1a\u5947\u602a\u7684\uff0c\u53ef\u662f\u6211\u5b9e\u5728\u60f3\u4e0d\u5230\u5979\u4f1a\u5c06\u5c60\u72d7\u7fc1\u88c5\u5728\u6e14\u7f51\u91cc\u5e26\u8d70\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u6240\u4ee5\u5c60\u72d7\u7fc1\u81ea\u5df1\u4e5f\u8bf4\uff1a\u300e\u65e0\u6cd5\u65e0\u5929\u201d\u4e00\u8fdb\u4e86\u201c\u5929\u7f57\u5730\u7f51\u201d\uff0c\u5c31\u4e00\u8f88\u5b50\u518d\u4e5f \u4f11\u60f3\u7ffb\u5f97\u4e86\u8eab\u4e86\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u6447\u7740\u5934\u7b11\u9053\uff1a\u201c\u65e0\u8bba\u5982\u4f55\uff0c\u8fd9\u5b9e\u5728\u662f\u5bf9\u5f88\u5947\u602a\u7684\u592b\u5987\uff0c\u4e5f\u5b9e\u5728\u6709\u8da3\u5f97\u5f88\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u53f9\u4e86\u53e3\u6c14\uff0c\u6df1\u6df1\u9053\uff1a\u201c\u4f46\u5728\u6211\u770b\u6765\uff0c\u674e\u7389\u51fd\u548c\u67f3\u65e0\u7709\u90a3\u5bf9\u592b\u5987\uff0c\u5374\u6bd4\u4ed6\u4eec\u8fd8\u8981\u6709 \u8da3\u5f97\u591a\u3002\u201d \u3000\u3000\u6df1\u9083\u7684\u5385\u5802\uff0c\u4e00\u91cd\u53c8\u4e00\u91cd\u3002 \u3000\u3000\u4e00\u91cd\u53c8\u4e00\u91cd\u7af9\u6df1\u91cd\uff0c\u5c06\u5341\u4e08\u7ea2\u5c18\u5168\u90fd\u9694\u7edd\u5728\u5916\uff0c\u5374\u5c06\u6ee1\u5c71\u79cb\u97f5\u5168\u90fd\u6df1\u6df1\u7684\u85cf\u5728\u5385\u5802\u4e2d\u3002 \u3000\u3000\u7af9\u95f4\u6709\u71d5\u5b50\u76d8\u65cb\u6881\u4e0a\uff0c\u6614\u65e5\u738b\u8c22\u5802\u524d\u71d5\uff0c\u4eca\u65e5\u83ab\u975e\u5df2\u98de\u6765\u6b64\u5bb6\u9662\uff1f\u6848\u5934\u7684\u949f\u9f0e\uff0c\u83ab\u975e\u662f \u91d1\u8c37\u6545\u7269\uff1f\u4e00\u62b9\u671d\u9633\uff0c\u6ee1\u5730\u82b1\u836b\uff0c\u5916\u9e1f\u8bed\u5541\u557e\uff0c\u66f4\u886c\u5f97\u5385\u5802\u91cc\u5206\u5916\u5b81\u9759\uff0c\u53c1\u4e94\u5782\u9aeb\u7ae5\u5b50\uff0c\u6b63 \u5728\u7b49\u7740\u5377\u8fce\u5ba2\u3002 \u3000\u3000\u80e1\u94c1\u5316\u548c\u695a\u7559\u9999\u5c31\u662f\u4ed6\u4eec\u7684\u5ba2\u4eba\u3002 \u3000\u3000\u674e\u7389\u51fd\u548c\u67f3\u65e0\u7709\u6ee1\u9762\u7b11\u5bb9\uff0c\u63d6\u5ba2\u3002 \u3000\u3000\u67f3\u65e0\u7709\u9053\uff1a\u201c\u6211\u4eec\u8d70\u7740\u8d70\u7740\uff0c\u5ffd\u7136\u77a7\u4e0d\u89c1\u4f60\u4eec\u4e86\uff0c\u6df1\u66f4\u534a\u591c\u7684\uff0c\u627e\u53c8\u627e\u4e0d\u7740\uff0c\u53ef\u771f\u662f\u628a \u4eba\u6025\u5f97\u8981\u547d\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u9053\uff1a\u201c\u5c0f\u5f1f\u6b63\u60f3\u4ee4\u4eba\u53bb\u5bfb\u627e\u4e8c\u4f4d\uff0c\u60f3\u4e0d\u5230\u4e24\u4f4d\u5df2\u7ecf\u6765\u4e86\uff0c\u771f\u662f\u53eb\u4eba\u6b22\u559c\u3002\u201d \u3000\u3000\u8fd9\u4e24\u4eba\u5c45\u7136\u8fd8\u80fd\u505a\u51fa\u8fd9\u526f\u6837\u5b50\u6765\uff0c\u80e1\u94c1\u82b1\u7b80\u76f4\u5df2\u5feb\u6c14\u7834\u4e86\u809a\u5b50\uff0c\u695a\u7559\u9999\u5374\u4e5f\u8fd8\u662f\u58f0\u8272\u4e0d \u52a8\uff0c\u5fae\u5fae\u7b11\u7740\u9053\uff1a\u201c\u5c0f\u5f1f\u4eec\u8d2a\u770b\u5c71\u8272\uff0c\u8ff7\u4e86\u8def\u9014\uff0c\u4e0d\u60f3\u7adf\u5bb3\u5f97\u8d24\u4f09\u4fea\u5982\u6b64\u7740\u6025\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u7b11\u9053\uff1a\u201c\u864e\u4e18\u6708\u591c\uff0c\u6b63\u662f\u522b\u6709\u4e00\u756a\u60c5\u8da3\uff0c\u4f46\u82e5\u975e\u695a\u5144\u548c\u80e1\u5144\u8fd9\u6837\u7684\u96c5\u4eba\uff0c\u53ea\u6015\u4e5f \u662f\u65e0\u6cd5\u9886\u7565\u7684\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u5fcd\u4e0d\u4f4f\u9053\uff1a\u201c\u5176\u5b9e\u6211\u4eec\u4e5f\u6ca1\u6709\u9886\u7565\u5230\u4ec0\u9ebd\u60c5\u8da3\uff0c\u53ea\u4e0d\u8fc7\u5728\u864e\u4e18\u7761\u4e86\u4e00\u5927\u89c9\uff0c\u5012\u505a \u4e86\u51e0\u4e2a\u5f88\u6709\u8da3\u7684\u68a6\u800c\u5df2\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u5ae3\u7136\u9053\uff1a\u201c\u80e1\u5144\u539f\u6765\u5728\u68a6\u6e38\u864e\u4e18\uff0c\u90a3\u4e00\u5b9a\u66f4\u6709\u8da3\u4e86\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u5176\u5b9e\u6211\u505a\u7684\u68a6\u4e5f\u5e76\u975e\u771f\u7684\u5f88\u6709\u8da3\uff0c\u53ea\u4e0d\u8fc7\u68a6\u89c1\u6709\u51e0\u4e2a\u4eba\u60f3\u6765\u8981\u6211\u4eec\u7684\u547d\u800c \u5df2\uff0c\u6709\u8da3\u7684\u53ea\u662f\u8fd9\u4e9b\u4eba\u7adf\u662f\u4f60\u4eec\u627e\u53bb\u7684\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u7b11\u9053\uff1a\u201c\u54e6\uff01\u90a3\u771f\u7684\u6709\u8da3\u6781\u4e86\uff0c\u53ea\u53ef\u60dc\u6211\u4eec\u5e76\u6ca1\u6709\u505a\u8fd9\u6837\u7684\u68a6\uff0c\u5426\u5219\u5927\u5bb6\u4e00\u9f50\u5728 \u68a6\u4e2d\u76f8\u9047\uff0c\u5c82\u975e\u66f4\u6709\u8da3\u4e86\u3002\u201d \u3000\u3000\u8fd9\u65f6\u4ed6\u4eec\u5df2\u8d70\u5165\u56db\u4e94\u91cd\u7af9\uff0c\u9752\u8863\u5782\u9aeb\u7684\u7ae5\u5b50\u4eec\uff0c\u5c06\u7af9\u5377\u8d70\uff0c\u53c8\u653e\u4e0b\uff0c\u65bc\u662f\u4ed6\u4eec\u5c31\u66f4\u8fdc\u79bb \u4e86\u7ea2\u5c18\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u773c\u73e0\u5b50\u76f4\u8f6c\uff0c\u4f3c\u4e4e\u8fd8\u60f3\u8bf4\u4ec0\u9ebd\uff0c\u674e\u7389\u51fd\u5df2\u7b11\u9053\uff1a\u201c\u4e24\u4f4d\u60f3\u89c1\u7684\u4eba\uff0c\u8fd9\u5c31\u5feb\u89c1\u5230 \u4e86\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u77a7\u4e86\u695a\u7559\u9999\u4e00\u773c\uff0c\u518d\u4e5f\u4e0d\u8bf4\u4e00\u4e2a\u5b57\uff0c\u65e0\u8bba\u6709\u4ec0\u9ebd\u8bdd\uff0c\u90fd\u53ea\u597d\u7b49\u5230\u89c1\u4e86\u82cf\u84c9\u84c9\u5979\u4eec \u4e4b\u5f8c\u518d\u8bf4\u3002 \u3000\u3000\u695a\u7559\u9999\u867d\u4ecd\u9762\u5e26\u5fae\u7b11\uff0c\u4f46\u5fc3\u60c5\u4e5f\u5df2\u4e0d\u514d\u6709\u4e9b\u7d27\u5f20\u3002 \u3000\u3000\u53ea\u89c1\u9752\u8863\u7ae5\u5b50\u53c8\u5c06\u524d\u9762\u4e00\u9053\u7af9\u5377\u8d77\uff0c\u4e00\u9635\u9635\u6de1\u6de1\u7684\u6a80\u9999\uff0c\u4fbf\u968f\u7740\u5377\u8d77\u7684\u7af9\u98d8\u6563\u4e86\u51fa\u6765\u3002 \u3000\u3000\u9999\u70df\u7f2d\u7ed5\u4e2d\uff0c\u6709\u4e2a\u767d\u53d1\u82cd\u82cd\u7684\u8001\u4eba\u6b63\u9759\u5750\u5728\u90a3\u91cc\u3002 \u3000\u3000\u4ed6\u6e05\u5d14\u7684\u9762\u5bb9\uff0c\u770b\u6765\u4f3c\u4e4e\u5f88\u6194\u60b4\uff0c\u5f88\u75b2\u5026\uff0c\u76ee\u5149\u66f4\u662f\u8bf4\u4e0d\u51fa\u7684\u5446\u6ede\uff0c\u770b\u6765\u51e0\u4e4e\u5df2\u5168\u65e0\u751f \u6c14\u3002 \u3000\u3000\u4ed6\u6574\u4e2a\u4eba\u4f3c\u4e4e\u5df2\u53ea\u5269\u4e0b\u4e00\u526f\u8eaf\u58f3\uff0c\u6ca1\u6709\u7075\u9b42\uff0c\u4e5f\u6ca1\u6709\u751f\u8da3\uff0c\u4ed6\u6d3b\u7740\u53ea\u4e0d\u8fc7\u662f\u5728\u7b49\u6b7b\u800c \u5df2\u3002 \u3000\u3000\u4f46\u4ed6\u7684\u9762\u524d\uff0c\u5374\u6709\u4e00\u67c4\u5149\u5f69\u593a\u76ee\u7684\u5251\u3002 \u3000\u3000\u5251\u8eab\u6c89\u78a7\uff0c\u5982\u4e00\u6cd3\u79cb\u6c34\uff0c\u65c1\u8fb9\u7684\u5251\u9798\u4e0a\u867d\u7136\u7f00\u6ee1\u4e86\u73cd\u8d35\u7684\u5b9d\u77f3\uff0c\u4f46\u5728\u5251\u5149\u6620\u7167\u4e0b\uff0c\u5df2\u5931 \u5c3d\u989c\u8272\u3002 \u3000\u3000\u8fd9\u8001\u4eba\u53ea\u662f\u75f4\u75f4\u7684\u77a7\u7740\u8fd9\u67c4\u5251\uff0c\u52a8\u4e5f\u4e0d\u52a8\u3002\u4ed6\u751f\u547d\u7684\u5149\u5f69\uff0c\u4f3c\u53ea\u6709\u9760\u7740\u8fd9\u67c4\u5251\u624d\u80fd\u5ef6 \u7eed\u3002 \u3000\u3000\u8fd9\u96be\u9053\u5c31\u662f\u5e74\u8f7b\u65f6\u53f1\u5412\u98ce\u4e91\u7684\u5929\u4e0b\u7b2c\u4e00\u5251\u5ba2\u674e\u89c2\u9c7c\u9ebd\uff1f\u695a\u7559\u9999\u548c\u80e1\u94c1\u82b1\u4e0d\u89c9\u5df2\u6014\u5728\u90a3 \u91cc\uff0c\u5fc3\u91cc\u65e2\u662f\u60ca\u5947\uff0c\u53c8\u662f\u4f24\u611f--\u8fd9\u9ebd\u5f3a\u7684\u4eba\uff0c\u751f\u547d\u7adf\u4e5f\u5982\u6b64\u8106\u5f31\u3002 \u3000\u3000\u90a3\u9ebd\uff0c\u751f\u547d\u7684\u672c\u8eab\uff0c\u5c82\u975e\u5c31\u662f\u4e2a\u60b2\u5267\u3002 \u3000\u3000\u6700\u4ee4\u695a\u7559\u9999\u5403\u60ca\u7684\uff0c\u81ea\u7136\u8fd8\u662f\u82cf\u84c9\u84c9\u5979\u4eec\u5e76\u4e0d\u5728\u8fd9\u91cc\uff0c\u4ed6\u5fcd\u4e0d\u4f4f\u60f3\u95ee\uff0c\u4f46\u674e\u7389\u51fd\u592b\u5987\u5df2 \u8d70\u4e0a\u524d\u53bb\u3002 \u3000\u3000\u4e24\u4eba\u4e00\u9f50\u8eac\u8eab\u884c\u793c\uff0c\u674e\u7389\u51fd\u9053\uff1a\u201c\u5b69\u513f\u6709\u4e24\u4f4d\u597d\u53cb\uff0c\u4e0d\u8fdc\u5343\u91cc\u800c\u6765\uff0c\u4e3a\u7684\u5c31\u662f\u60f3\u89c1\u4f60\u8001 \u4eba\u5bb6\u4e00\u9762\uff0c\u6240\u4ee5\u5b69\u5144\u6211\u5c06\u4ed6\u4eec\u5e26\u5230\u8fd9\u91cc\u672a\u4e86\u3002\u201d \u3000\u3000\u8001\u4eba\u5e76\u6ca1\u6709\u62ac\u5934\uff0c\u751a\u81f3\u8fde\u76ee\u5149\u90fd\u6ca1\u6709\u79fb\u52a8\u3002 \u3000\u3000\u674e\u7389\u51fd\u9053\uff1a\u201c\u5b69\u513f\u8fd9\u4e24\u4f4d\u670b\u53cb\uff0c\u4f60\u8001\u4eba\u5bb6\u4e5f\u65f6\u5e38\u63d0\u8d77\u7684\uff0c\u8fd9\u4f4d\u5c31\u662f\u540d\u6ee1\u5929\u4e0b\u7684\u695a\u9999\u5e05\uff0c \u8fd9\u4f4d\u5c31\u662f\u548c\u695a\u9999\u5e05\u9f50\u540d\u7684\u82b1\u8774\u8776\u3002\u201d \u3000\u3000\u8001\u4eba\u8fd9\u624d\u62ac\u8d77\u5934\u671b\u4e86\u4e00\u773c\uff0c\u4f46\u76ee\u4e2d\u4ecd\u662f\u4e00\u7247\u75f4\u8ff7\u832b\u7136\uff0c\u4e5f\u4e0d\u77e5\u662f\u5426\u542c\u61c2\u4e86\u674e\u7389\u51fd\u7684\u8bdd\u3002 \u3000\u3000\u695a\u7559\u9999\u548c\u80e1\u94c1\u82b1\u9eef\u7136\u550f\u5618\uff0c\u90fd\u4e0d\u77e5\u8be5\u8bf4\u4ec0\u9ebd\u3002 \u3000\u3000\u674e\u7389\u51fd\u8fd9\u624d\u8f6c\u8fc7\u8eab\uff0c\u966a\u7b11\u9053\uff1a\u201c\u5bb6\u7236\u8fd1\u5e74\u6765\u8033\u76ee\u4e5f\u6709\u4e9b\u5931\u806a\uff0c\u4e0d\u5468\u4e4b\u5904\uff0c\u8fd8\u671b\u4e24\u4f4d\u6055 \u7f6a\u3002\u695a\u7559\u9999\u9053\uff1a\u201c\u4e0d\u6562\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u7acb\u523b\u63a5\u7740\u9053\uff1a\u201c\u665a\u8f88\u7b49\u4e5f\u4e0d\u6562\u518d\u6253\u6270\u524d\u8f88\u4e86\uff0c\u8fd8\u662f\u544a\u9000\u5427\uff01\u201d \u3000\u3000\u4ed6\u4eec\u867d\u7136\u6025\u7740\u60f3\u89c1\u82cf\u84c9\u84c9\uff0c\u6025\u7740\u60f3\u5c06\u674e\u7389\u51fd\u592b\u5987\u62c9\u5230\u4e00\u8fb9\u53bb\u95ee\u4e2a\u7a76\u7adf\uff0c\u5374\u53c8\u4e0d\u5fcd\u5728\u8fd9\u5782 \u8001\u4eba\u9762\u524d\u8bf4\u4ec0\u9ebd\u5931\u793c\u7684\u8bdd\u6765\uff0c\u656c\u8001\u53ea\u8d24\uff0c\u6b63\u662f\u6c5f\u6e56\u4fa0\u4e49\u9053\u7684\u89c4\u77e9\uff0c\u8fd9\u79cd\u89c4\u77e9\u695a\u7559\u9999\u662f\u7edd\u4e0d\u4f1a \u7684\u3002 \u3000\u3000\u8001\u4eba\u7684\u5634\u5507\u5ffd\u7136\u52a8\u4e86\u52a8\uff0c\u4f3c\u4e4e\u60f3\u8bf4\u4ec0\u9ebd\uff0c\u5374\u53d1\u4e0d\u51fa\u58f0\u97f3\u6765\uff0c\u4ed6\u8138\u4e0a\u7684\u808c\u8089\u4f3c\u5df2\u5168\u90fd\u9ebb \u6728\u3002 \u3000\u3000\u201c\u5bb6\u7236\u7ec8\u5e74\u5bc2\u5bde\uff0c\u96be\u5f97\u6709\u4eba\u8fc7\u8bbf\uff0c\u4e24\u4f4d\u65e2\u7136\u6765\u4e86\uff0c\u53c8\u4e0d\u80af\u591a\u5750\u7247\u523b\uff0c\u662f\u4ee5\u5bb6\u7236\u53c8\u89c9\u5f97\u9057 \u61be\u5f97\u5f88\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u548c\u80e1\u94c1\u82b1\u5bf9\u671b\u4e86\u4e00\u773c\uff0c\u53ea\u6709\u5750\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u4ed6\u4eec\u867d\u7136\u6709\u529b\u640f\u4e07\u519b\u7684\u52c7\u6c14\uff0c\u7b11\u50b2\u738b\u4faf\u7684\u80c6\u5305\uff0c\u4f46\u5728\u8fd9\u5782\u66ae\u5c06\u6b7b\u7684\u8001\u4eba\u9762\u524d\uff0c\u5374\u53ea\u662f\u4fef \u9996\u542c\u547d\u3002 \u3000\u3000\u674e\u7389\u51fd\u5c55\u989c\u7b11\u9053\uff1a\u201c\u4e24\u4f4d\u5982\u6b64\u4ec1\u539a\uff0c\u5bb6\u7236\u5fc5\u5b9a\u611f\u6fc0\u5f97\u5f88\u3002\u201d \u3000\u3000\u8001\u4eba\u7684\u5634\u53c8\u52a8\u4e86\u52a8\uff0c\u795e\u60c5\u5f77\u4f5b\u6709\u4e9b\u60b2\u54c0\uff0c\u6709\u4e9b\u7126\u6025\u3002 \u3000\u3000\u674e\u7389\u51fd\u76b1\u7709\u9053\uff1a\u201c\u5bb6\u7236\u4e0d\u77e5\u662f\u5426\u6709\u4ec0\u9ebd\u8bdd\u8981\u5bf9\u4e24\u4f4d\u8bf4\u2026\u2026\u201d \u3000\u3000\u4ed6\u4e00\u9762\u8bf4\u8bdd\uff0c\u4e00\u9762\u5df2\u8d70\u5230\u8001\u4eba\u9762\u524d\u4fef\u9996\u5728\u8001\u4eba\u5634\u8fb9\u3002 \u3000\u3000\u695a\u7559\u9999\u65e2\u542c\u4e0d\u5230\u8001\u4eba\u7684\u8bed\u58f0\uff0c\u4e5f\u770b\u4e0d\u5230\u8001\u4eba\u7684\u5634\uff0c\u53ea\u80fd\u770b\u5230\u674e\u7389\u51fd\u5728\u4e0d\u505c\u7684\u70b9\u9996\uff0c\u4e0d\u4f4f \u62b1\u58f0\u9053\uff1a\u201c\u662f\u2026\u2026\u2026\u662f\u2026\u2026\u2026\u5b69\u513f\u660e\u767d\u3002\u201d \u3000\u3000\u4ed6\u56de\u8fc7\u5934\u6765\u65f6\uff0c\u9762\u4e0a\u4e5f\u6ee1\u662f\u6c89\u75db\u4e4b\u8272\uff0c\u5374\u52c9\u5f3a\u7b11\u9053\uff1a\u201c\u591a\u5e74\u4ee5\u6765\uff0c\u5bb6\u7236\u53ea\u6709\u4e00\u4ef6\u5fc3\u613f\u672a \u4e86\uff0c\u4eca\u65e5\u4e24\u4f4d\u6070\u5de7\u6765\u4e86\uff0c\u6b63\u53ef\u4e3a\u5bb6\u7236\u4e86\u6b64\u5fc3\u613f\uff0c\u53ea\u770b\u4e24\u4f4d\u662f\u5426\u80af\u51fa\u624b\u76f8\u52a9\u4e86\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u6c89\u4f4f\u4e86\u6c14\uff0c\u5fae\u7b11\u9053\uff1a\u201c\u4e0d\u77e5\u524d\u8f88\u6709\u4f55\u5fc3\u613f\u672a\u4e86\uff1f\u665a\u8f88\u7b49\u82e5\u80fd\u6548\u529b\uff0c\u6562\u4e0d\u4ece\u547d\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u5927\u559c\u9053\uff1a\u201c\u65e2\u662f\u5982\u6b64\uff0c\u5c0f\u5f1f\u5c31\u5148\u4ee3\u5bb6\u7236\u5411\u4e24\u4f4d\u8c22\u8fc7\u4e86\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u5fcd\u4e0d\u4f4f\u9053\uff1a\u201c\u4f46\u8fd9\u4e5f\u8fd8\u662f\u8981\u770b\u524d\u8f88\u7a76\u7adf\u6709\u4ec0\u9ebd\u5fc3\u613f\uff1f\u6211\u4eec\u662f\u5426\u6709\u80fd\u591f\u6548\u529b\u4e4b \u5904\uff1f\u201d \u3000\u3000\u674e\u7389\u51fd\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u9053\u7406\u5c0f\u5f1f\u81ea\u7136\u660e\u767d\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u4e5f\u6253\u4e86\u4e2a\u54c8\u54c8\uff0c\u9053\uff1a\u201c\u6211\u81ea\u7136\u4e5f\u77e5\u9053\u524d\u8f88\u7edd\u4e0d\u81f3\u65bc\u5f3a\u4eba\u6240\u96be\u7684\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u4f3c\u4e4e\u5168\u672a\u542c\u51fa\u5b83\u7684\u8a00\u4e0b\u4e4b\u610f\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u5bb6\u7236\u4ee5\u5251\u6210\u540d\uff0c\u4e5f\u89c6\u5251\u5982\u547d\uff0c\u53ea\u8981\u548c\u5251 \u6709\u5173\u7cfb\u7684\u4e8b\uff0c\u4ed6\u8001\u4eba\u5bb6\u90fd\u5f88\u6709\u5174\u8da3\uff0c\u662f\u4ee5\u4ed6\u8001\u4eba\u5bb6\u4e0d\u4f46\u5c06\u53e4\u5f80\u4eca\u6765\u7ea6\u6709\u540d\u671b\u5251\u8c31\uff0c\u5168\u90fd\u8bbe\u6cd5 \u627e\u6765\u7814\u7a76\u8fc7\uff0c\u800c\u4e14\u8fd8\u4ed4\u7ec6\u7814\u7a76\u8fc7\u6240\u6709\u6210\u540d\u5251\u5ba2\u7684\u6e0a\u6e90\u5386\u53f2\uff0c\u4ee5\u53ca\u4ed6\u4eec\u751f\u5e73\u6240\u6709\u7684\u91cd\u8981\u6218 \u5f79\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u77a7\u4e86\u90a3\u8001\u4eba\u4e00\u773c\uff0c\u6697\u6697\u5fd6\u9053\uff1a\u201c\u522b\u4eba\u53ea\u77e5\u9053\u5341\u8f7d\u5bd2\u7a97\uff0c\u78e8\u7a7f\u94c1\u781a\uff0c\u91d1\u699c\u9898\u540d\u5f97\u6765 \u975e\u6613\uff0c\u5374\u4e0d\u77e5\u4e00\u4e2a\u5251\u5ba2\u82e5\u8981\u6210\u540d\uff0c\u6240\u4e0b\u7684\u529f\u592b\u53ea\u6015\u66f4\u8270\u82e6\u5341\u500d\uff0c\u800c\u4ed6\u4eec\u4e0d\u4f46\u8981\u727a\u7272\u529f\u540d\u5bcc \u8d35\uff0c\u8fd8\u8981\u5fcd\u53d7\u522b\u4eba\u4f3c\u4e0d\u80fd\u5fcd\u7684\u5bc2\u5bde\uff0c\u4f46\u5f97\u5230\u7684\u53c8\u662f\u4ec0\u9ebd\u5462\uff1f\u53ea\u4e0d\u8fc7\u662f\u6c5f\u6e56\u4e2d\u6570\u5341\u5e74\u865a\u540d\u800c \u5df2\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u5df2\u6309\u7740\u9053\uff1a\u201c\u5bb6\u7236\u82e6\u5fc3\u7814\u7a76\u6570\u5341\u5e74\uff0c\u5251\u6cd5\u56fa\u7136\u5f97\u5230\u5f88\u5927\u7684\u8fdb\u76ca\uff0c\u5374\u4e5f\u53d1\u73b0\u51e0\u4ef6\u5f88 \u5947\u602a\uff0c\u53c8\u5f88\u6709\u8da3\u7684\u4e8b\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u672c\u6765\u867d\u7136\u6b65\u6b65\u4e3a\u8425\uff0c\u6b64\u523b\u5374\u5df2\u542c\u5f97\u5165\u795e\uff0c\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u4ec0\u9ebd\u4e8b\uff1f\u201d \u3000\u3000\u674e\u7389\u51fd\u9053\uff1a\u201c\u5bb6\u7236\u53d1\u73b0\u81ea\u53e4\u4ee5\u6765\u6700\u8d1f\u76db\u540d\u7684\u51e0\u5957\u5251\u6cd5\uff0c\u5e76\u4e0d\u662f\u6700\u5de7\u5999\u7684\u90a3\u51e0\u5957\u5251\u6cd5\uff0c\u8fd9 \u5c31\u662f\u4ed6\u8001\u4eba\u5bb6\u8ba4\u4e3a\u6700\u5947\u602a\u7684\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u76b1\u7709\u9053\uff1a\u201c\u8fd9\u2026\u2026\u2026\u8fd9\u610f\u601d\u6211\u8fd8\u662f\u4e0d\u592a\u61c2\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u9053\uff1a\u201c\u8b6c\u5982\u8bf4\uff0c\u9b54\u6559\u4e2d\u7684\u201c\u4e07\u5999\u65e0\u65b9\u3001\u6151\u9b42\u5927\u4e5d\u5f0f\u300f\uff0c\u62db\u4e2d\u6709\u62db\uff0c\u53d8\u5316\u65e0\u7a77\uff0c\u7adf \u53ef\u6f14\u53d8\u4e3a\u4e03\u767e\u4e8c\u5341\u4e5d\u62db\uff0c\u82e5\u8bba\u5176\u51fa\u624b\u4e4b\u5947\u8be1\u98d8\u5ffd\uff0c\u62db\u5f0f\u4e4b\u60c5\u5999\u5468\u5bc6\uff0c\u59d4\u5b9e\u8fdc\u5728\u6b66\u5f53\u6d3e\u7684\u201c\u4e24 \u4eea\u5251\u6cd5\u300f\u4e4b\u4e0a\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u4e5f\u542c\u8bf4\u8fc7\u8fd9\u9b54\u6559\u79d8\u5251\u7684\u5389\u5bb3\uff0c\u636e\u8bf4\u76f4\u5230\u4eca\u65e5\u4e3a\u6b62\uff0c\u5929\u4e0b\u8fd8\u6ca1\u6709\u4e00 \u4e2a\u4eba\u80fd\u63a5\u6ee1\u4ed6\u4e03\u767e\u4e8c\u5341\u4e5d\u62db\u7684\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u9053\uff1a\u201c\u83ab\u8bf4\u65e0\u4eba\u80fd\u63a5\u6ee1\u4ed6\u4e03\u767e\u4e8c\u5341\u4e5d\u62db\uff0c\u751a\u81f3\u8fde\u80fd\u591f\u63a5\u4f4f\u4ed6\u524d\u4e03\u5f0f\u7684\u4eba\u90fd\u5f88\u5c11\uff0c \u4f46\u6570\u767e\u5e74\u6765\uff0c\u6c5f\u6e56\u4e2d\u4eba\u53ea\u77e5\u9053\u6b66\u5f53\u201c\u4e24\u4eea\u5251\u6cd5\u300f\u5929\u4e0b\u65e0\u53cc\uff0c\u65e0\u53ef\u6bd4\u62df\uff0c\u201c\u4e07\u5999\u65e0\u65b9\u3001\u6444\u9b42\u5927 \u4e5d\u5f0f\u201d\uff0c\u5374\u8fde\u540d\u5b57\u90fd\u5df2\u5f88\u5c11\u6709\u4eba\u77e5\u9053\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u6c89\u541f\u9053\uff1a\u201c\u8fd9\u4e5f\u8bb8\u662f\u56e0\u4e3a\u6c5f\u6e56\u4e2d\u89c1\u8fc7\u8fd9\u5957\u5251\u6cd5\u7684\u4eba\u5e76\u4e0d\u591a\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u9053\uff1a\u201c\u89c1\u8fc7\u8fd9\u5957\u5251\u6cd5\u7684\u4eba\u867d\u4e0d\u591a\uff0c\u89c1\u8fc7\u201c\u4e24\u4eea\u5251\u6cd5\u201d\u7684\u4eba\u53c8\u6709\u591a\u5c11\u5462\uff1f\u6b66\u5f53\u95e8\u4e0b \u4e00\u5411\u62e9\u5f92\u6700\u4e25\uff0c\u5f53\u5e74\u6700\u76db\u65f6\u4e5f\u672a\u8d85\u8fc7\u516b\u5341\u4e00\u4e2a\uff0c\u800c\u4e14\u8fd9\u516b\u5341\u4e00\u4f4d\u6b66\u5f53\u5f1f\u5b50\uff0c\u4e5f\u5e76\u975e\u6bcf\u4e2a\u4eba\u90fd \u7ec3\u8fc7\u201c\u4e24\u4eea\u5251\u6cd5\u201d\u7684\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u4e5f\u77e5\u9053\u8fd9\u300e\u4e24\u4eea\u5251\u6cd5\u201d\u4e00\u5b9a\u8981\u7ecf\u638c\u6559\u771f\u4eba\u4eb2\u81ea\u4f20\u6388\uff0c\u662f\u4ee5\u6b66\u5f53\u5b50 \u5f1f\u771f\u80fd\u5f97\u5230\u201c\u4e24\u4eea\u201d\u771f\u4f20\u7684\uff0c\u6700\u591a\u4e5f\u53ea\u4e0d\u8fc7\u5341\u4e4b\u53c1\u5339\u800c\u5df2\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u9053\uff1a\u201c\u4f46\u9b54\u6559\u5374\u4e00\u5411\u5584\u95e8\u5927\u5f00\uff0c\u800c\u4e14\u4e00\u5165\u95e8\u5c31\u80fd\u7ec3\u5251\uff0c\u6b66\u5f53\u95e8\u4e0b\u6781\u5c11\u51fa\u5c71\uff0c\u9b54\u6559 \u5b50\u5f1f\u5374\u5728\u6c5f\u6e56\u4e2d\u6a2a\u51b2\u76f4\u95ef\uff0c\u662f\u4ee5\u65e0\u8bba\u600e\u9ebd\u8bf4\uff0c\u89c1\u8fc7\u8fd9\u300e\u6151\u9b42\u5927\u4e5d\u5f0f\u201d\u7684\u4eba\uff0c\u81f3\u5c11\u4e5f\u8981\u6bd4\u89c1\u8fc7 \u201c\u4e24\u4eea\u5251\u6cd5\u201d\u7684\u4eba\u591a\u51e0\u500d\uff0c\u4f46\u201c\u6151\u9b42\u5927\u4e5d\u5f0f\u201d\u5374\u8fdd\u4e0d\u53ca\u201c\u4e24\u4eea\u5251\u6cd5\u201d\u7740\u540d\uff0c\u8fd9\u662f\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u60c5\u4e0d\u81ea\u7981\uff0c\u53c8\u6478\u4e86\u6478\u9f3b\u5b50\uff0c\u5583\u5583\u9053\uff1a\u201c\u8fd9\u5012\u7684\u786e\u662f\u4ef6\u602a\u4e8b\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u7b11\u4e86\u7b11\u9053\uff1a\u201c\u8fd9\u786e\u662f\u4ef6\u602a\u4e8b\uff0c\u5bb6\u7236\u5374\u4e5f\u60f3\u901a\u4e86\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u5ffd\u7136\u5927\u58f0\u9053\uff1a\u201c\u6211\u4e5f\u660e\u767d\u4e86\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u9053\uff1a\u201c\u8bf78\u6559\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u5c31\u56e0\u4e3a\u8fd9\u201c\u4e07\u5999\u65e0\u65b9\u3001\u6151\u9b42\u5927\u4e5d\u5f0f\u201d\uff0c\u5251\u6cd5\u592a\u5947\u5965\u7cbe\u5999\uff0c\u662f\u4ee5\u5b66\u7684\u4eba\uff0c\u80fd 88\u5b66\u7cbe\u7684\u5374\u5f88\u5c11\uff0c\u4ed6\u4eec\u5251\u6cd5\u5c1a\u672a\u5b66\u7cbe\uff0c\u5c31\u5728\u6c5f\u6e56\u4e2d\u6a2a\u51b2\u76f4\u95ef\uff0c\u4e00\u5b9a\u96be\u514d\u5230\u5904\u78b0\u58c1\uff0c\u6240\u4ee5\u522b \u4eba\u4e5f\u5c3188\u4f1a\u8ba4\u4e3a\u4ed6\u4eec\u7684\u5251\u6cd5\u5e76\u4e0d\u9ad8\u660e\u4e86\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u5fae\u7b11\u9053\uff1a\u201c\u8fd9\u867d\u7136\u4e5f\u6709\u9053\u7406\uff0c\u4f46\u5374\u4e0d\u662f\u6700\u4e3b\u8981\u7684\u539f\u56e0\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u54e6\uff01\u90a3\u9ebd\u6700\u4e3b\u8981\u7684\u539f\u56e0\u662f\u4ec0\u9ebd\u5462\uff1f\u201d \u3000\u3000\u674e\u7389\u51fd\u9053\uff1a\u201c\u53ea\u56e0\u5251\u662f\u6b7b\u7684\uff0c\u4eba\u5374\u662f\u6d3b\u7684\uff0c\u4e00\u5b9a\u8981\u4f7f\u5251\u7684\u4eba\u80fd\u5c06\u5251\u6cd5\u6d3b\u7528\uff0c\u624d\u80fd\u663e\u5f97\u51fa \u90a3\u5251\u6cd5\u7684\u7cbe\u5999\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u6211\u65b9\u624d\u8bf4\u7684\uff0c\u5c82\u975e\u5c31\u662f\u8fd9\u610f\u601d\u5417\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u5ffd\u7136\u7b11\u9053\uff1a\u201c\u9b54\u6559\u5b50\u5f1f\u5e76\u975e\u5251\u6cd5\u4e0d\u7cbe\uff0c\u800c\u662f\u4ed6\u4eec\u7684\u5fc3\u672f\u4e0d\u6b63\uff0c\u884c\u4e8b\u592a\u90aa\uff0c8\u6240\u4ee5 \u548c\u4eba\u52a8\u624b\u65f6\uff0c\u5c31\u4e0d\u80fd\u7406\u76f4\u6c14\u58ee\uff0c\u6240\u4ee5\u4ed6\u4eec\u7684\u5251\u6cd5\u5c31\u7b97\u6bd4\u522b\u4eba\u9ad8\uff0c\u4e5f\u96be\u514d\u843d\u8d25\uff0c\u300e\u90aa\u4e0d\u80dc \u6b63\u201d\uff0c\u8fd9\u53e5\u8bdd\u6b63\u662f\u5343\u53e4\u4e0d\u6613\u7684\u9053\u7406\u3002\u201d\u4ed6\u8f6c\u5411\u67f3\u65e0\u7709\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8d24\u4f09\u4fea\u8ba4\u4e3a\u5728\u4e0b\u8bf4\u7684\u662f\u5426 \u8fd8\u6709\u4e9b\u9053\u7406\uff1f\u201d \u3000\u3000\u67f3\u65e0\u7709\u8f7b\u8f7b\u54b3\u55fd\u96e8\u58f0\uff0c\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4e24\u4eba\u52a8\u624b\uff0c\u6b66\u529f\u9ad8\u7684\u5e76\u4e0d\u4e00\u5b9a\u80fd\u53d6\u80dc\uff0c\u4e00\u4e2a\u4eba\u53ea \u8981\u6709\u5fc5\u80dc\u7684\u4fe1\u5fc3\uff0c\u4ed6\u6b66\u529f\u5c31\u7b97\u5dee\u4e9b\uff0c\u5f80\u5f80\u4e5f\u80fd\u4ee5\u5f31\u80dc\u5f3a\u7684\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u76ee\u5149\u70af\u70af\uff0c\u51dd\u6ce8\u7740\u5979\uff0c\u4e00\u5b57\u5b57\u9053\uff1a\u201c\u4f46\u4e00\u4e2a\u4eba\u53ea\u6709\u5728\u77e5\u9053\u81ea\u5df1\u505a\u7684\u4e8b\u662f\u5bf9\u7684\u65f6 \u5019\uff0c\u624d\u4f1a\u6709\u5fc5\u80dc\u7684\u4fe1\u5fc3\uff0c\u662f\u9ebd\uff1f\u201d \u3000\u3000\u67f3\u65e0\u7709\u6c89\u9ed8\u4e86\u534a\u664c\uff0c\u5ae3\u7136\u7b11\u9053\uff1a\u201c\u8fd9\u9053\u7406\u9999\u5e05\u4f60\u81ea\u7136\u662f\u6700\u660e\u767d\u7684\uff0c\u53ea\u56e0\u6211\u65e9\u5df2\u542c\u8bf4\u8fc7\uff0c \u695a\u9999\u5e05\u6218\u65e0\u4e0d\u80dc\uff0c\u65e0\u8bba\u9047\u7740\u591a\u9ebd\u5f3a\u7684\u5bf9\u624b\uff0c\u4e5f\u6709\u4e0d\u8d25\u7684\u81ea\u4fe1\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u6c89\u58f0\u9053\uff1a\u201c\u90a3\u53ea\u56e0\u5728\u4e0b\u81ea\u4fe1\u6240\u505a\u6240\u4e3a\uff0c\u8fd8\u6ca1\u6709\u4e00\u4ef6\u5bf9\u4e0d\u8d77\u4eba\u7684\uff0c\u5426\u5219\u5728\u4e0b\u5c31\u7b97\u6b66 \u529f\u518d\u9ad8\uff0c\u4e5f\u5df2\u4e0d\u77e5\u6b7b\u8fc7\u591a\u5c11\u6b21\u4e86\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u8fd8\u672a\u8bf4\u8bdd\uff0c\u674e\u7389\u51fd\u5df28\u62a2\u7740\u7b11\u9053\uff1a\u201c\u6570\u767e\u5e74\u6765\u6b66\u6797\u7740\u540d\u7684\u6218\u5f79\u4e2d\uff0c\u5c31\u6709\u8bb8\u591a\u662f\u4ee5 \u5f31\u80dc\u5f3a\u7684\uff0c\u8fd9\u4e5f\u6b63\u662f\u5bb6\u7236\u89c9\u5f97\u5f88\u5947\u602a\u7684\u4e8b\uff0c\u8b6c\u5982\u8bf4\uff0c\u6614\u5e74\u9b54\u6559\u6559\u4e3b\u72ec\u5b64\u6b8b\u548c\u4e2d\u539f\u5927\u4fa0\u94c1\u4e2d\u68e0 \u51b3\u6218\u65bc\u96c1\u8361\u7edd\u9876\uff0c\u6218\u524d\u6c5f\u6e56\u4e2d\u90fd\u8ba4\u4e3a\u5f53\u65f6\u5e74\u7eaa\u672a\u6ee1\u53c1\u5341\u7684\u94c1\u4e2d\u68e0\uff0c\u7edd\u6ca1\u6709\u72ec\u5b64\u6b8b\u529f\u529b\u6df1\u539a\uff0c \u94c1\u8840\u5927\u8179\u95e8\u7684\u6b66\u529f\uff0c\u4e5f\u4e0d\u53ca\u9b54\u6559\u5947\u8be1\u7cbe\u5999\uff0c\u662f\u4ee5\u6c5f\u6e56\u4e2d\u4eba\u4eba\u90fd\u770b\u597d\u72ec\u72d0\u6b8b\uff0c\u751a\u81f3\u6709\u4eba\u4ee5\u5341\u535a \u4e00\uff0c\u8d4c\u4ed6\u5728\u516b\u767e\u62db\u5185\u4fbf\u80fd\u53d6\u80dc\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u6211\u4e5f\u542c\u8bf4\u8fc7\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u9053\uff1a\u201c\u8c01\u77e5\u4e24\u4eba\u7adf\u51b3\u6218\u4e86\u53c1\u5929\u53c1\u591c\uff0c\u5230\u5f8c\u6765\u94c1\u5927\u4fa0\u867d\u5df2\u8d1f\u4f24\u5341\u53c1\u5904\uff0c\u5168\u8eab\u8863\u88f3\u90fd \u5df2\u88ab\u8840\u67d3\u900f\uff0c\u8fd8\u662f\u4ee5\u5c0f\u5929\u661f\u7684\u638c\u529b\uff0c\u9707\u65ad\u4e86\u72ec\u5b64\u6b8b\u7684\u5fc3\u8109\uff0c\u72ec\u5b64\u6b8b\u76f4\u5230\u4e34\u6b7b\u4e4b\u524d\uff0c\u8fd8\u65e0\u6cd5\u76f8 \u4fe1\u81ea\u5df1\u7adf\u4f1a\u843d\u8d25\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u542c\u5f97\u7709\u98de\u8272\u821e\uff0c\u51fb\u638c\u9053\uff1a\u201c\u8fd9\u4f4d\u94c1\u4e2d\u68e0\u94c1\u5927\u4fa0\u7aef\u7684\u662f\u6761\u6c49\u5b50\uff0c\u6211\u65e5\u5f8c\u82e5\u6709\u673a\u4f1a\u89c1 \u7740\u4ed6\uff0c\u80fd\u548c\u4ed6\u75db\u996e\u4e2a\u53c1\u5929\u53c1\u591c\uff0c\u4e5f\u7b97\u4e0d\u865a\u6b64\u751f\u4e86\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u9053\uff1a\u201c\u4f46\u4ee4\u5bb6\u7236\u89c9\u5f97\u6700\u5947\u602a\u7684\uff0c\u5374\u8fd8\u662f\u6b66\u6797\u4e2d\u81ea\u53e4\u81f3\u4eca\uff0c\u90fd\u6ca1\u6709\u4e00\u79cd\u80fd\u591f\u7b97\u5f97\u4e0a \u6218\u65e0\u4e0d\u80dc\u7684\u201c\u5251\u9635\u300f\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a8\u201c\u5251\u9635\uff1f\u201d \u3000\u3000\u674e\u7389\u51fd\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5251\u9635\uff0c\u5168\u771f\u6559\u7684\u201c\u5317\u6597\u4e03\u771f\u9635\u201d\uff0c\u6b66\u5f53\u5c71\u7684\u201c\u516b\u5366\u5251\u9635\u201d\u867d\u7136\u90fd\u4e45 \u5df2\u540d\u52a8\u6c5f\u6e56\uff0c\u4f46\u82e5\u9047\u7740\u771f\u6b63\u7684\u6b66\u6797\u9ad8\u624b\uff0c\u597d\u50cf\u5c31\u90fd\u53d8\u5f97\u6ca1\u4ec0\u9ebd\u7528\u4e86\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u81f3\u4eca\u8fd8\u672a\u542c\u8bf4\u8fc7\u6709\u90a3\u4e00\u4f4d\u9ad8\u624b\u662f\u88ab\u56f0\u6b7b\u5728\u5251\u9635\u4e2d\u7684\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u9053\uff1a\u201c\u6c5f\u6e56\u9ad8\u624b\u6b7b\u5728\u6b66\u5f53\u5251\u5ba2\u624b\u91cc\u7684\u5e76\u4e0d\u5c11\uff0c\u4f46\u5374\u6ca1\u6709\u4e00\u4eba\u6b7b\u5728\u201c\u516b\u5366\u5251\u9635\u201d \u91cc\uff0c\u8fd9\u4ef6\u4e8b\u80e1\u5144\u96be\u9053\u4e0d\u89c9\u5f97\u6709\u4e9b\u5947\u602a\u9ebd\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u542c\u4f60\u4e00\u8bf4\uff0c\u6211\u4e5f\u89c9\u5f97\u6709\u4e9b\u5947\u602a\u4e86\uff0c\u201c\u516b\u5366\u5251\u9635\u201d\u81f3\u5c11\u4e5f\u8981\u6709\u516b\u4eba\u8054\u624b\uff0c\u800c \u4e14\u5fc5\u5b9a\u4e45\u7ecf\u8bad\u7ec3\uff0c\u51fa\u624b\u4e00\u5b9a\u914d\u5408\u5f97\u5f88\u5de7\u5999\uff0c\u6309\u7406\u8bf4\uff0c\u7528\u8fd9\u201c\u516b\u5366\u5251\u9635\u201d\u8fce\u654c\uff0c\u4e00\u5b9a\u4f1a\u6bd4\u5355\u72ec \u548c\u4eba\u4ea4\u624b\u6709\u6548\u5f97\u591a\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u9053\uff1a\u201c\u53ef\u662f\u8fd9\u201c\u516b\u5366\u5251\u9635\u201d\u8fce\u6218\u9ad8\u624b\u65f6\uff0c\u5374\u504f\u504f\u53d8\u5f97\u65e0\u6548\u4e86\uff0c\u6b66\u6797\u4e2d\u7b80\u76f4\u5c31\u6ca1\u6709 \u4e00\u79cd\u7edd\u5bf9\u6709\u6548\u7684\u5251\u9635\uff0c\u8fd9\u662f\u4e3a\u4ec0\u9ebd\u5462\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u6c89\u541f\u9053\uff1a\u201c\u8fd9\u4e5f\u8bb8\u662f\u56e0\u4e3a\u65e0\u8bba\u90a3\u4e00\u79cd\u5251\u9635\uff0c\u90fd\u96be\u514d\u6709\u7834\u7efd\u6f0f\u6d1e\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u9053\uff1a\u201c\u5251\u9635\u7eb5\u6709\u7834\u7efd\uff0c\u4f46\u666e\u5929\u4e4b\u4e0b\uff0c\u65e0\u8bba\u90a3\u4e00\u79cd\u5251\u6cd5\u4e5f\u90fd\u96be\u514d\u6709\u7834\u7efd\u7684\uff0c\u90a3\u9ebd\uff0c \u4eba\u4eba\u8054\u624b\u7684\u5251\u9635\uff0c\u4e3a\u4ec0\u9ebd\u8fd8\u4e0d\u5982\u4e00\u4eba\u4f7f\u51fa\u7684\u5251\u6cd5\u6709\u6548\u5462\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u5fcd\u4e0d\u4f4f\u53c8\u6478\u4e86\u6478\u9f3b\u5b50\uff0c\u9053\uff1a\u201c\u8fd9\u539f\u56e0\u4ee4\u5c0a\u96be\u9053\u4e5f\u60f3\u901a\u4e86\u9ebd\uff1f\u201d \u3000\u3000\u674e\u7389\u51fd\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u539f\u56e0\u5c31\u662f\u56e0\u4e3a\u201c\u516b\u5366\u5251\u9635\u201d\u867d\u5999\uff0c\u6b66\u5f53\u6d3e\u5374\u627e\u4e0d\u51fa\u516b\u4e2a\u6b66\u529f\u76f8 \u7b49\u7684\u9ad8\u624b\uff0c\u8fd9\u5251\u9635\u867d\u5389\u5bb3\uff0c\u51fa\u624b\u7684\u4eba\u529f\u529b\u82e5\u4e0d\u591f\uff0c\u4e00\u9047\u89c1\u9ad8\u624b\uff0c\u5c31\u96be\u514d\u88ab\u6253\u5f97\u6e83\u4e0d\u6210\u519b\uff0c\u8b6c \u5982\u8bf4\uff0c\u5c0f\u5f1f\u5c31\u7b97\u80fd\u7ec3\u6210\u4e00\u5957\u4e3e\u4e16\u65e0\u53cc\u7684\u5251\u6cd5\uff0c\u4f46\u82e5\u9047\u89c1\u4e86\u695a\u5144\u8fd9\u6837\u7684\u5185\u5bb6\u9ad8\u624b\uff0c\u4e5f\u8fd8\u662f\u5fc5\u8d25 \u65e0\u7591\u3002\u201d \u7b2c\u5341\u4e8c\u7ae0\u3000\u591a\u8c22\u501f\u5251>> \u53e4\u9f99\u300a\u695a\u7559\u9999\u7cfb\u5217\xb7\u753b\u7709\u9e1f\u300b \u7b2c\u5341\u4e8c\u7ae0\u3000\u591a\u8c22\u501f\u5251 \u3000\u3000\u695a\u7559\u9999\u5fae\u7b11\u6e05\uff1a\u201c\u674e\u5144\u592a\u8c26\u4e86\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u4f46\u6b66\u5f53\u6d3e\u4e2d\uff0c\u81f3\u5c11\u6709\u4e94\u4e2a\u4eba\u529f\u529b\u4e0d\u5f31\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u9053\uff1a\u201c\u80e1\u5144\u8bf4\u7684\u53ef\u662f\u6b66\u5f53\u638c\u6559\uff0c\u548c\u56db\u5927\u62a4\u6cd5\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u9053\uff1a\u201c\u5c31\u7b97\u8fd9\u4e94\u4e2a\u4eba\u90fd\u53c2\u52a0\u516b\u5366\u5251\u9635\uff0c\u4e5f\u8fd8\u662f\u5dee\u4e86\u53c1\u4e2a\uff0c\u82e5\u53e6\u5916\u627e\u53c1\u4e2a\u4eba\u51d1\u6570\uff0c \u8fd9\u5251\u9635\u5c31\u6709\u4e86\u6f0f\u6d1e\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u9053\uff1a\u201c\u5251\u9635\u4e00\u6709\u4e86\u6f0f\u6d1e\uff0c\u9047\u89c1\u771f\u6b63\u9ad8\u624b\u65f6\uff0c\u3001\u4e00\u5b9a\u4f1a\u627e\u5230\u4ed6\u4eec\u7684\u5f31\u70b9\u8fdb\u653b\uff0c\u53ea\u8981 \u5176\u4e2d\u4e00\u4eba\u7684\u653b\u52bf\u9047\u963b\uff0c\u6574\u4e2a\u9635\u6cd5\u5c31\u65e0\u6cd5\u63a8\u52a8\uff0c\u5230\u4e86\u90a3\u65f6\uff0c\u516b\u4e2a\u4eba\u8054\u624b\u3002\u5c31\u4f1a\u53d8\u5f97\u53cd\u4e0d\u5982\u4e00\u4e2a \u4eba\u52a8\u624b\u65b9\u4fbf\u6709\u6548\u3002\u201d \u3000\u3000\u4ed6\u53c8\u7b11\u4e86\u7b11\uff0c\u6309\u7740\u9053\uff1a\u201c\u4f55\u51b5\uff0c\u6b66\u5f53\u56db\u5927\u62a4\u6cd5\uff0c\u529f\u529b\u4e5f\u672a\u5fc5\u90fd\u76f8\u7b49\uff0c\u66f4\u672a\u5fc5\u4f1a\u90fd\u662f\u9ad8 \u624b\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u4e5f\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u800c\u4e14\u771f\u6b63\u7684\u7edd\u9876\u9ad8\u624b\uff0c\u662f\u7edd\u4e0d\u4f1a\u53c2\u4e0e\u4efb\u4f55\u5251\u9635\u7684\uff0c\u4ed6\u4eec\u4ea4\u624b \u65f6\uff0c\u8bb2\u7a76\u7684\u5c31\u662f\u5355\u6253\u72ec\u6597\uff0c\u600e\u80af\u548c\u522b\u4eba\u8054\u624b\u8fce\u654c\uff1f\u201d \u3000\u3000\u674e\u7389\u51fd\u9644\u638c\u9053\uff1a\u201c\u6b63\u662f\u5982\u6b64\uff0c\u5386\u4ee3\u6b66\u5f53\u638c\u6559\uff0c\u5c31\u6ca1\u6709\u4e00\u4f4d\u80af\u52a0\u5165\u201c\u516b\u5366\u5251\u9635\u201d\u7684\uff0c\u50cf\u6b66 \u5f53\u8fd9\u6837\u58f0\u52bf\u6d69\u5927\u7684\u5251\u6d3e\uff0c\u90fd\u627e\u4e0d\u51fa\u80fd\u914d\u5408\u5251\u9635\u7684\u516b\u4e2a\u4eba\u6765\uff0c\u4f55\u51b5\u5176\u4ed6\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u5ffd\u53c8\u5927\u58f0\u9053\uff1a\u201c\u4f46\u4f60\u8bf4\u4e86\u534a\u5929\uff0c\u8fd8\u662f\u672a\u8bf4\u51fa\u4ee4\u5c0a\u7a76\u7adf\u6709\u4ec0\u9ebd\u5fc3\u613f\u672a\u4e86\uff1f\u4e5f\u672a\u8bf4\u51fa \u6709\u4ec0\u9ebd\u4e8b\u662f\u8981\u6211\u4eec\u6548\u52b3\u7684\uff1f\u201d \u3000\u3000\u674e\u7389\u51fd\u9053\uff1a\u201c\u5bb6\u7236\u5c06\u53e4\u5f80\u4eca\u6765\uff0c\u6bcf\u4e00\u79cd\u7740\u540d\u7684\u5251\u9635\u90fd\u7814\u7a76\u8fc7\u4e4b\u5f8c\uff0c\u81ea\u5df1\u4e5f\u521b\u51fa\u4e00\u79cd\u9635\u6cd5 \u6765\uff0c\u4ed6\u8001\u4eba\u5bb6\u8ba4\u4e3a\u666e\u5929\u4e4b\u4e0b\uff0c\u7edd\u6ca1\u6709\u4e00\u4e2a\u4eba\u80fd\u7834\u89e3\u6b64\u9635\uff0c\u4f46\u5374\u4e00\u76f4\u65e0\u6cd5\u8bc1\u660e\u3002\u8fd9\u4e5f\u662f\u4ed6\u8001\u4eba \u5bb6\u5e73\u751f\u6700\u5927\u7684\u9057\u61be\u3002\u201d\u4ed6\u53f9\u4e86\u53e3\u6c14\uff0c\u6309\u7740\u9053\uff1a\u201c\u56e0\u4e3a\u60f3\u8981\u8bc1\u660e\u8fd9\u4ef6\u4e8b\uff0c\u6709\u96e8\u70b9\u6700\u5927\u7684\u56f0\u96be\uff0c \u7b2c\u4e00\uff0c\u5c31\u662f\u4ed6\u8001\u4eba\u5bb6\u867d\u5df2\u5c06\u8fd9\u9635\u6cd5\u7684\u4eba\u6570\u51cf\u5230\u6700\u5c11\uff0c\u5374\u8fd8\u662f\u65e0\u6cd5\u627e\u5230\u516d\u4f4d\u529f\u529b\u76f8\u82e5\u7684\u7edd\u9876\u9ad8 \u624b\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u5374\u4e0d\u77e5\u5728\u4ed6\u8001\u4eba\u5bb6\u773c\u4e2d\uff0c\u600e\u9ebd\u6837\u7684\u4eba\u624d\u7b97\u662f\u7edd\u9876\u9ad8\u624b\u5462\uff1f\u201d \u3000\u3000\u674e\u7389\u51fd\u6c89\u541f\u7740\u9053\uff1a\u201c\u6b64\u4eba\u7684\u529f\u529b\u81f3\u5c11\u8981\u80fd\u548c\u5f53\u4eca\u4e03\u5927\u6d3e\u7684\u638c\u95e8\u5206\u5ead\u6297\u793c\uff0c\u800c\u4e14\u5fc5\u987b\u8981\u662f \u4f7f\u5251\u7684\u540d\u5bb6\uff0c\u8b6c\u5982\u8bf4\u2026\u2026\u2026\u201d \u3000\u3000\u695a\u7559\u9999\u6de1\u6de1\u9053\uff1a\u201c\u8b6c\u5982\u8bf4\uff0c\u5e05\u4e00\u5e06\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u9762\u4e0d\u6539\u8272\uff0c\u53f9\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u53ea\u53ef\u60dc\u50cf\u5e05\u8001\u524d\u8f88\u8fd9\u6837\u7684\u5251\u6cd5\u9ad8\u624b\uff0c\u627e\u4e00\u4e2a\u5df2\u5f88\u56f0 \u96be\uff0c\u82e5\u60f3\u627e\u516d\u4e2a\uff0c\u90a3\u5b9e\u5728\u96be\u5982\u767b\u5929\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u76ee\u5149\u95ea\u52a8\uff0c\u9053\uff1a\u201c\u522b\u4eba\u8981\u627e\u8fd9\u6837\u7ea6\u516d\u4f4d\u9ad8\u624b\uff0c\u56fa\u7136\u96be\u5982\u767b\u5929\uff0c\u4f46\u4ee5\u4ee4\u5c0a\u7684\u4eba\u671b\u548c \u58f0\u8a89\uff0c\u5374\u5e76\u975e\u5b8c\u5168\u4e0d\u53ef\u80fd\u7684\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5bb6\u7236\u7684\u77e5\u4ea4\u597d\u53cb\u4e2d\uff0c\u7684\u786e\u6709\u51e0\u4f4d\u53ef\u79f0\u5f97\u4e0a\u7edd\u9876\u9ad8\u624b\uff0c\u53ea\u4e0d\u8fc7\u8fd9\u4e9b\u524d \u8f88\u90fd\u6709\u5982\u95f2\u4e91\u91ce\u9e64\uff0c\u6e38\u8e2a\u4e0d\u5b9a\uff0c\u662f\u4ee5\u5bb6\u7236\u76f4\u5230\u4eca\u5929\uff0c\u624d\u603b\u7b97\u627e\u5230\u4e86\u516d\u4f4d\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u8038\u7136\u52a8\u5bb9\uff0c\u5931\u58f0\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u4ee4\u5c0a\u7684\u5fc3\u613f\u5c82\u975e\u5df2\u53ef\u8fbe\u6210\u4e86\u9ebd\uff1f\u201d \u3000\u3000\u674e\u7389\u51fd\u53f9\u9053\uff1a\u201c\u80e1\u5144\u83ab\u5fd8\u4e86\uff0c\u8fd9\u4ef6\u4e8b\u8fd8\u6709\u7b2c\u4e8c\u70b9\u56f0\u96be\u4e4b\u5904\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u8fd8\u6709\u4ec0\u9ebd\u56f0\u96be\uff1f\u201d \u3000\u3000\u674e\u7389\u51fd\u7f13\u7f13\u9053\uff1a\u201c\u8981\u8bc1\u660e\u8fd9\u9635\u6cd5\u662f\u5426\u771f\u7684\u7edd\u65e0\u7834\u7efd\uff0c\u5c31\u4e00\u5b9a\u8981\u627e\u4e00\u4e2a\u4eba\u6765\u7834\u5b83\uff0c\u8fd9\u4eba\u5374 \u66f4\u96be\u4e86\uff0c\u53ea\u56e0\u4ed6\u4e0d\u4f46\u8981\u6709\u7edd\u9876\u7684\u6b66\u529f\uff0c\u7edd\u9876\u7684\u673a\u667a\uff0c\u8fd8\u5fc5\u987b\u8981\u6709\u975e\u5e38\u8f89\u714c\u7684\u6218\u8ff9\uff0c\u66fe\u7ecf\u51fb\u8d25 \u8fc7\u8bb8\u591a\u9876\u5c16\u9ad8\u624b\u3002\u201d \u3000\u3000\u4ed6\u671b\u7740\u695a\u7559\u9999\u4e00\u7b11\uff0c\u63a5\u7740\u9053\uff1a\u201c\u56e0\u4e3a\u53ea\u6709\u8fd9\u6837\u7684\u4eba\uff0c\u624d\u80fd\u8bd5\u51fa\u8fd9\u9635\u6cd5\u7684\u4f18\u52a3\uff0c\u662f\u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u58f0\u8272\u4e0d\u52a8\uff0c\u5fae\u7b11\u9053\uff1a\u201c\u5374\u4e0d\u77e5\u5728\u674e\u5144\u5fc3\u76ee\u4e2d\uff0c\u8981\u600e\u9ebd\u6837\u7684\u4eba\u624d\u591f\u8d44\u683c\u5462\uff1f\u201d \u3000\u3000\u674e\u7389\u51fd\u9053\uff1a\u201c\u5c0f\u5f1f\u60f3\u6765\u60f3\u53bb\uff0c\u8fd9\u6837\u7684\u4eba\u5929\u4e0b\u53ea\u6709\u4e00\u4e2a\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u662f\u8c01\uff1f\u201d \u3000\u3000\u674e\u7389\u51fd\u9053\uff1a\u201c\u5c31\u662f\u695a\u5144\u3002\u201d\u4ed6\u773c\u775b\u77aa\u7740\u695a\u7559\u9999\uff0c\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u53ea\u8981\u695a\u5144\u80af\u51fa\u624b\uff0c\u5bb6\u7236\u7684 \u5fc3\u613f\u5c31\u53ef\u4ee5\u8fbe\u5230\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u8fd8\u662f\u58f0\u8272\u4e0d\u52a8\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u5c0f\u5f1f\u53ef\u6709\u9009\u62e9\u7684\u9980\u5730\u9ebd\uff1f\u201d \u3000\u3000\u674e\u7389\u51fd\u9053\uff1a\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u518d\u4e5f\u5fcd\u4e0d\u4f4f\u8df3\u4e86\u8d77\u6765\uff0c\u53d8\u8272\u9053\uff1a\u201c\u4f60\u5c45\u7136\u8981\u4ed6\u548c\u516d\u4e2a\u50cf\u5e05\u4e00\u5e06\u90a3\u6837\u7684\u4eba\u4ea4\u624b\uff0c\u4f60 \u8fd9\u4e0d\u662f\u8981\u4ed6\u7684\u547d\uff1f\u201d \u3000\u3000\u674e\u7389\u51fd\u5fae\u7b11\u4e0d\u8bed\uff0c\u7adf\u7136\u9ed8\u8ba4\u4e86\u3002 \u3000\u3000\u695a\u7559\u9999\u6de1\u6de1\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u7528\u7740\u6025\uff0c\u6211\u8fd9\u6761\u547d\u53cd\u6b63\u662f\u6361\u56de\u6765\u7684\uff0c\u82e5\u80fd\u6b7b\u5728\u201c\u62e5\u7fe0\u5c71\u5e84\u201d\uff0c \u5c82\u975e\u4e5f\u53ef\u7b97\u662f\u6b7b\u5f97\u5176\u6240\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u6014\u4e86\u6014\uff0c\u5ffd\u7136\u5c06\u4ed6\u62c9\u5230\u4e00\u8fb9\uff0c\u55c4\u58f0\u9053\uff1a\u201c\u4f60\u2026\u2026\u2026\u4f60\u662f\u4e0d\u662f\u6709\u628a\u63e1\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u987f\u8db3\u9053\uff1a\u201c\u65e2\u7136\u6ca1\u6709\u628a\u63e1\uff0c\u4f60\u4e3a\u4ec0\u9ebd\u8fd8\u53eb\u6211\u4e0d\u8981\u62c5\u5fc3\u7740\u6025\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4e8b\u5df2\u81f3\u6b64\uff0c\u7740\u6025\u53c8\u767e\u4ec0\u9ebd\u7528\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u773c\u73e0\u5b50\u4e00\u8f6c\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u54b1\u4eec\u73b0\u5728\u5c31\u51b2\u51fa\u53bb\uff0c\u53ea\u6015\u8fd8\u6765\u5f97\u53ca\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u53ea\u6015\u5df2\u6765\u4e0d\u53ca\u4e86\u3002\u201d \u3000\u3000\u7af9\u5df2\u53c8\u5377\u8d77\uff0c\u51e0\u4e2a\u4eba\u5df2\u9c7c\u8d2f\u8d70\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u8fd9\u51e0\u4eba\u90fd\u7a7f\u7740\u7eaf\u9ed1\u8272\u7684\uff0c\u6781\u67d4\u8f6f\u7684\u4e1d\u888d\u3002\u95ea\u7740\u5149\u7684\u4e1d\u888d\uff0c\u67d4\u8f6f\u5f97\u5f77\u4f5b\u6d41\u6c34\uff0c\u4f46\u4ed6\u4eec\u8d70\u52a8 \u65f6\uff0c\u5374\u8fde\u8fd9\u6d41\u6c34\u822c\u67d4\u8f6f\u7684\u4e1d\u888d\u90fd\u6ca1\u6709\u6ce2\u52a8\u3002 \u3000\u3000\u4ed6\u4eec\u7684\u811a\u6b65\uff0c\u6b63\u4e5f\u6ed1\u5982\u6d41\u6c34\uff0c\u8f7b\u5982\u5e7d\u7075\u3002 \u3000\u3000\u4ed6\u4eec\u7684\u8138\u4e0a\uff0c\u4e5f\u8499\u7740\u4e00\u5c42\u9ed1\u8272\u7684\u4e1d\u5dfe\uff0c\u751a\u81f3\u8fde\u773c\u775b\u90fd\u88ab\u8499\u4f4f\uff0c\u6ca1\u6709\u4eba\u80fd\u8ba4\u5f97\u51fa\u4ed6\u4eec\u7a76\u7adf \u662f\u8c01\uff1f\u4ed6\u4eec\u884c\u52a8\u95f4\uff0c\u5374\u81ea\u7136\u800c\u7136\u7ea6\u6709\u4e00\u79cd\u6151\u4eba\u7684\u5a01\u4e25\u6d41\u9732\u51fa\u6765\uff0c\u867d\u7136\u8c01\u90fd\u77a7\u4e0d\u51fa\u4ed6\u4eec\u7684\u8eab \u4efd\uff0c\u4f46\u8c01\u4e5f\u4e0d\u6562\u5bf9\u4ed6\u4eec\u7a0d\u5b58\u8f7b\u89c6\u3002 \u3000\u3000\u7b2c\u4e00\u4e2a\u4eba\uff0c\u8eab\u6750\u7626\u524a\u800c\u9880\u957f\uff0c\u7b14\u633a\u7684\u7ad9\u7740\uff0c\u5c31\u50cf\u662f\u4e00\u67aa\uff0c\u624b\u91cc\u63d0\u7740\u7684\u662f\u4e00\u67c4\u5947\u5f62\u53e4\u602a\u7684 \u94dc\u5251\u3002 \u3000\u3000\u7b2c\u4e8c\u4e2a\u4eba\uff0c\u77ee\u800c\u7626\uff0c\u7b2c\u53c1\u4e2a\u4eba\uff0c\u9ad8\u5927\u800c\u9b41\u4f1f\uff0c\u4e24\u4eba\u8d70\u5728\u4e00\u8d77\uff0c\u5c31\u663e\u5f97\u5206\u5916\u523a\u773c\uff0c\u5206\u5916\u7a81 \u51fa\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u7684\u638c\u4e2d\u5251\u4ff1\u662f\u5149\u8292\u707f\u70c2\uff0c\u663e\u89c1\u7edd\u975e\u51e1\u54c1\uff0c\u4f46\u5251\u7684\u5f62\u72b6\uff0c\u5374\u4e0d\u7279\u522b\uff0c\u8c01\u4e5f\u53ef\u4ee5\u8fa8\u51fa \u8fd9\u4e24\u67c4\u5251\u7684\u6765\u5386\u51fa\u5904\u3002 \u3000\u3000\u7b2c\u56db\u4e2a\u4eba\uff0c\u8eab\u6750\u5f88\u666e\u901a\uff0c\u4f7f\u7684\u4e5f\u662f\u67c4\u5f88\u666e\u901a\u7684\u9752\u94dc\u5251\uff0c\u5c31\u7b97\u8d70\u5728\u8def\u4e0a\uff0c\u53ea\u6015\u4e5f\u6ca1\u6709\u4eba\u4f1a \u591a\u770b\u4ed6\u4e00\u773c\u3002 \u3000\u3000\u7b2c\u4e94\u4e2a\u4eba\uff0c\u53c8\u77ee\u53c8\u80d6\uff0c\u8179\u51f8\u5982\u73e0\uff0c\u638c\u4e2d\u5251\u975e\u91d1\u975e\u94c1\uff0c\u4ed4\u7ec6\u4e00\u770b\uff0c\u7adf\u7136\u662f\u7528\u6728\u5934\u524a\u6210\u7684\u3002 \u3000\u3000\u8fd9\u4e94\u4e2a\u4eba\u8c01\u4e5f\u6ca1\u6709\u8bf4\u8bdd\uff0c\u4e5f\u6ca1\u6709\u4ec0\u9ebd\u52a8\u4f5c\uff0c\u4f46\u4e00\u8d70\u8fdb\u6765\uff0c\u8fd9\u5385\u5802\u4e2d\u5f77\u4f5b\u5c31\u7acb\u523b\u5145\u6ee1\u4e86\u903c \u4eba\u7684\u6740\u6c14\uff0c\u4ee4\u4eba\u4e0d\u5bd2\u800c\u6817\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u4e0d\u7981\u66f4\u4e3a\u695a\u7559\u9999\u62c5\u5fc3\uff0c\u53ea\u56e0\u4ed6\u4e00\u773c\u4fbf\u77a7\u51fa\uff0c\u8fd9\u4e94\u4eba\u65e0\u8bba\u8eab\u4efd\u5730\u4f4d\u6b66\u529f\uff0c\u7edd\u65e0\u4e00\u4eba \u5728\u5e05\u4e00\u5e06\u4e4b\u4e0b\u3002 \u3000\u3000\u695a\u7559\u9999\u8fd8\u662f\u9762\u5e26\u5fae\u7b11\uff0c\u540c\u8fd9\u4e94\u4eba\u62b1\u62f3\u4e00\u63d6\uff0c\u9053\uff1a\u201c\u5728\u4e0b\u95fb\u5f97\u201c\u62e5\u7fe0\u5c71\u5e84\u201d\u4e2d\u5230\u4e86\u51e0\u4f4d\u7edd \u4ee3\u9ad8\u624b\uff0c\u77e5\u9053\u4eca\u65e5\u5b9a\u80fd\u4e00\u7779\u524d\u8f88\u540d\u5bb6\u7684\u4e30\u91c7\uff0c\u5b9e\u662f\u559c\u4e0d\u81ea\u80dc\uff0c\u8c01\u77e5\u524d\u8f88\u4eec\u7adf\u4e0d\u80af\u4e00\u793a\u5e90\u5c71\u771f \u9762\u76ee\uff0c\u672a\u514d\u4ee4\u4eba\u89c9\u5f97\u9057\u61be\u4e86\u3002\u201d \u3000\u3000\u4e94\u4e2a\u9ed1\u8863\u4eba\u53ea\u662f\u52a8\u4e5f\u4e0d\u52a8\u7684\u7ad9\u7740\uff0c\u6ca1\u6709\u4eba\u5f00\u53e3\u3002 \u3000\u3000\u695a\u7559\u9999\u7b11\u9053\uff1a\u201c\u524d\u8f88\u4eec\u5c31\u7b97\u4e0d\u613f\u4ee5\u771f\u9762\u76ee\u793a\u4eba\uff0c\u53c8\u4f55\u5fc5\u8fde\u773c\u775b\u90fd\u4e00\u9f50\u8499\u4f4f\u5462\uff1f\u201d \u3000\u3000\u90a3\u9ad8\u5927\u800c\u9b41\u4f1f\u7684\u9ed1\u8863\u4eba\u5ffd\u7136\u9053\uff1a\u201c\u6211\u8f88\u4ee5\u5fc3\u9a6d\u5251\uff0c\u4f55\u9700\u773c\u76ee\uff1f\u201d \u3000\u3000\u4ed6\u867d\u7136\u53ea\u8bf4\u4e86\u77ed\u77ed\u5341\u4e2a\u5b57\uff0c\u4f46\u6574\u4e2a\u5385\u5802\u95f4\u90fd\u4f3c\u5df2\u5145\u6ee1\u4e86\u4ed6\u6d2a\u4eae\u7684\u8bed\u58f0\uff0c\u8fde\u51e0\u4e0a\u7684\u8336\u76cf\u90fd \u88ab\u9707\u5f97\u201c\u683c\u683c\u201d\u54cd\u52a8\u3002 \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u5728\u4e0b\u4e5f\u77e5\u9053\u540d\u5bb6\u51fa\u624b\uff0c\u81ea\u6709\u5206\u5bf8\uff0c\u6839\u672c\u7528\u4e0d\u7740\u7528\u773c\u775b\u770b\u7684\uff0c\u4f46\u524d\u8f88\u4eec\u96be\u9053 \u4e5f\u4e0d\u60f3\u770b\u770b\u4eca\u65e5\u7684\u5bf9\u624b\u662f\u4e2a\u600e\u9ebd\u6837\u7684\u4eba\u5417\uff1f\u201d \u3000\u3000\u8fd9\u6b21\u53c8\u6ca1\u6709\u4eba\u56de\u7b54\u5b83\u7684\u8bdd\u4e86\u3002 \u3000\u3000\u997c\u4e86\u534a\u664c\uff0c\u674e\u7389\u51fd\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u4e94\u4f4d\u524d\u8f88\u5e73\u751f\u4ece\u672a\u548c\u4eba\u8054\u624b\u4f5c\u6218\uff0c\u4eca\u65e5\u4e4b\u5f8c\uff0c\u4e5f \u7edd\u4e0d\u4f1a\u518d\u548c\u522b\u4eba\u8054\u624b\u4f5c\u6218\uff0c\u6240\u4ee5\u4ed6\u4eec\u66f4\u4e0d\u5fc5\u5728\u4f60\u9762\u524d\u663e\u9732\u8eab\u4efd\uff0c\u4e5f\u7528\u4e0d\u7740\u77e5\u9053\u4f60\u662f\u4ec0\u9ebd\u4eba\uff0c \u8fd9\u4e94\u4f4d\u524d\u8f88\u4eca\u65e5\u53ea\u4e0d\u8fc7\u662f\u4e3a\u5bb6\u7236\u4e86\u4e00\u5fc3\u613f\u800c\u5df2\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u6de1\u6de1\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u4e5f\u77e5\u9053\u8fd9\u4e94\u4f4d\u524d\u8f88\u4eca\u5929\u6765\u5230\u8fd9\u91cc\uff0c\u662f\u4e3a\u4e86\u4ed6\u4eec\u548c\u4ee4\u5c0a\u7684\u4ea4 \u60c5\uff0c\u4f46\u4eca\u65e5\u4e4b\u4e8b\uff0c\u7a76\u7adf\u662f\u4ee4\u5c0a\u7684\u5fc3\u613f\uff0c\u6291\u6216\u53ea\u4e0d\u8fc7\u662f\u9601\u4e0b\u7684\u5fc3\u613f\u5462\uff1f\u201d \u3000\u3000\u674e\u7389\u51fd\u8138\u4e0a\u53d8\u4e86\u53d8\u989c\u8272\uff0c\u9053\uff1a\u201c\u81ea\u7136\u662f\u5bb6\u7236\u7684\u5fc3\u613f\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u773c\u775b\u77aa\u7740\u4ed6\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u90a3\u9ebd\uff0c\u4ee4\u5c0a\u7684\u5fc3\u613f\u662f\u60f3\u8bd5\u4e00\u8bd5\u8fd9\u9635\u6cd5\u5462\uff1f\u8fd8\u662f\u60f3\u6740\u4e86 \u6211\uff1f\u201d \u3000\u3000\u674e\u7389\u51fd\u9762\u8272\u82cd\u767d\uff0c\u4e00\u65f6\u95f4\u7adf\u7b54\u4e0d\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u67f3\u65e0\u7709\u5ae3\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u65e0\u8bba\u5982\u4f55\uff0c\u8fd9\u90fd\u5df2\u6ca1\u4ec0\u9ebd\u5206\u522b\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u67f3\u65e0\u7709\u59a9\u5a9a\u7684\u773c\u6ce2\uff0c\u5ffd\u4e5f\u53d8\u5f97\u5229\u5982\u5200\u526a\uff0c\u77aa\u7740\u4ed6\u4e00\u5b57\u5b57\u9053\uff1a\u201c\u53ea\u56e0\u8fd9\u9635\u6cd5\u82e5\u65e0\u7834\u7efd\uff0c\u9601 \u4e0b\u53ea\u6015\u5c31\u96be\u514d\u8981\u6210\u4e3a\u6b64\u9635\u7684\u796d\u793c\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u8fd9\u9635\u6cd5\u82e5\u6709\u7834\u7efd\u53c8\u5982\u4f55\uff1f\u201d \u3000\u3000\u67f3\u65e0\u7709\u60a0\u7136\u9053\uff1a\u201c\u8fd9\u9635\u6cd5\u7eb5\u6709\u7834\u7efd\uff0c\u4f46\u7ecf\u8fc7\u4e94\u4f4d\u524d\u8f88\u4e4b\u624b\u4f7f\u51fa\u6765\uff0c\u9601\u4e0b\u53ea\u6015\u4e5f\u65e0\u6cd5\u51b2\u5f97 \u51fa\u53bb\u5427\uff01\u201d \u3000\u3000\u695a\u7559\u9999\u4ef0\u82dc\u5927\u7b11\u9053\uff1a\u201c\u8fd9\u5c31\u5bf9\u4e86\uff0c\u8fd9\u9635\u6cd5\u7eb5\u7136\u7834\u7efd\u767e\u51fa\uff0c\u7eb5\u7136\u4e0d\u6210\u9635\u6cd5\uff0c\u6709\u8fd9\u4e94\u4f4d\u524d\u8f88 \u8054\u624b\u4f5c\u6218\uff0c\u5929\u4e0b\u53ea\u6015\u4e5f\u6ca1\u6709\u4eba\u80fd\u62b5\u6321\u7684\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u90a3\u9ebd\uff0c\u4f60\u4eec\u53c8\u4f55\u5fc5\u8fd8\u8981\u8bf4\u4ec0\u9ebd\u9635\u6cd5\uff0c\u8bba\u4ec0\u9ebd\u4f18\u52a3\uff0c\u4e0d\u5982\u4e7e\u8106\u8bf4\u4eca\u65e5\u8981\u5c06\u6211 \u7684\u6027\u547d\u7559\u5728\u8fd9\u91cc\uff0c\u5c82\u975e\u66f4\u7b80\u5355\u660e\u767d\u5f97\u591a\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u9053\uff1a\u201c\u8fd9\u5176\u4e2d\u5012\u6709\u4e9b\u5206\u522b\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u67f3\u65e0\u7709\u9053\uff1a\u201c\u8fd9\u4e94\u4f4d\u524d\u8f88\u8054\u624b\u4f5c\u6218\uff0c\u4f60\u867d\u4e0d\u80fd\u62b5\u6321\uff0c\u4f46\u5374\u53ef\u4ee5\u9003\u8d70\uff0c\u9601\u4e0b\u7684\u8f7b\u529f\u5929\u4e0b\u65e0 \u53cc\uff0c\u8fd9\u662f\u8c01\u90fd\u77e5\u9053\u7684\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u8fc7\u5956\u8fc7\u5956\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u9053\uff1a\u201c\u4f46\u8fd9\u9635\u6cd5\u4e00\u53d1\u52a8\uff0c\u9601\u4e0b\u5c31\u7b97\u80cc\u63d2\u53cc\u7fc5\uff0c\u4e5f\u4f11\u60f3\u9003\u5f97\u51fa\u53bb\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9ed8\u7136\u534a\u664c\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u5728\u4e0b\u548c\u8d24\u4f09\u4fea\u7a76\u7adf\u6709\u4ec0\u9ebd\u4ec7\u6068\uff0c\u5b9a\u8981\u5728\u4e0b\u5c06\u547d\u7559\u5728\u8fd9 \u91cc\uff1f\u201d \u3000\u3000\u67f3\u65e0\u7709\u773c\u73e0\u5b50\u4e00\u8f6c\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u6211\u65e9\u5c31\u8bf4\u8fc7\uff0c\u8fd9\u4e0d\u662f\u6211\u4eec\u7684\u610f\u601d\uff0c\u662f\u5bb6\u7236\u7684\u610f\u601d\u3002\u201d \u3000\u3000\u53ea\u89c1\u90a3\u8001\u4eba\u674e\u89c2\u9c7c\u8fd8\u662f\u832b\u7136\u5750\u5728\u90a3\u91cc\uff0c\u53ea\u662f\u4f4e\u5782\u7740\u76ee\u5149\uff0c\u75f4\u75f4\u7684\u77a7\u7740\u9762\u524d\u90a3\u67c4\u79cb\u6c34\u957f \u5251\u3002 \u3000\u3000\u695a\u7559\u9999\u53f9\u4e86\u53e3\u6c14\uff0c\u5583\u5583\u9053\uff1a\u201c\u8fd9\u65e0\u8bba\u662f\u4e0d\u662f\u4ed6\u7684\u610f\u601d\uff1f\u53cd\u6b63\u90fd\u6ca1\u6709\u4eba\u80fd\u95ee\u5f97\u51fa\u6765\u7684\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u5ffd\u7136\u5927\u58f0\u9053\uff1a\u201c\u8fd9\u9635\u6cd5\u53d1\u52a8\uff0c\u81f3\u5c11\u8981\u6709\u516d\u4e2a\u4eba\uff0c\u662f\u9ebd\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u76ee\u5149\u95ea\u52a8\uff0c\u9053\uff1a\u201c\u4f46\u73b0\u5728\u5374\u53ea\u5230\u4e86\u4e94\u4f4d\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u5fc3\u91cc\u6697\u6697\u6b22\u559c\uff0c\u5fcd\u4e0d\u4f4f\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u53ea\u6015\u672a\u66fe\u60f3\u5230\u5e05\u4e00\u5e06\u5df2\u4e0d\u522b\u800c\u53bb\u4e86\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u51b7\u51b7\u7684\u9053\uff1a\u201c\u5e05\u8001\u524d\u8f88\u6765\u4e0d\u6765\u90fd\u6ca1\u4ec0\u9ebd\u5173\u7cfb\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9aa4\u7136\u987f\u4f4f\u7b11\u58f0\uff0c\u9053\uff1a\u201c\u6ca1\u5173\u7cfb\uff1f\u600e\u4f1a\u6ca1\u5173\u7cfb\uff1f\u9635\u6cd5\u82e5\u662f\u5c11\u4e86\u4e00\u4eba\u2026\u2026\u2026\u201d \u3000\u3000\u67f3\u65e0\u7709\u4e00\u7b11\u6253\u65ad\u4e86\u4ed6\u7684\u8bdd\uff0c\u9053\uff1a\u201c\u4f60\u96be\u9053\u672a\u66fe\u542c\u8bf4\u8fc7\uff0c\u6ee5\u7afd\u6709\u65f6\u4e5f\u53ef\u5145\u6570\u7684\u3002\u201d \u3000\u3000\u5979\u4e0d\u518d\u7406\u4f1a\u80e1\u94c1\u82b1\uff0c\u8f6c\u8eab\u5411\u90a3\u4e94\u4e2a\u9ed1\u8863\u4eba\u6df1\u6df1\u4e00\u62dc\uff0c\u9053\uff1a\u201c\u8fd9\u9635\u6cd5\u665a\u8f88\u4e5f\u66fe\u7ec3\u8fc7\uff0c\u81f3\u4eca \u7262\u8bb0\u5728\u5fc3\uff0c\u5e05\u8001\u524d\u8f88\u672a\u5230\uff0c\u665a\u8f88\u53ea\u6709\u52c9\u5f3a\u5145\u6570\uff0c\u4f46\u613f\u524d\u8f88\u4eec\u591a\u591a\u7ef4\u62a4\uff0c\u665a\u8f88\u611f\u6fc0\u4e0d\u5c3d\u3002\u201d \u3000\u3000\u4e94\u4e2a\u9ed1\u8863\u4eba\u65e2\u6ca1\u6709\u7b54\u5e94\uff0c\u4e5f\u6ca1\u6709\u53cd\u5bf9\u3002 \u3000\u3000\u9996\u5148\u90a3\u7626\u524a\u9880\u957f\u7684\u9ed1\u8863\u4eba\u5ffd\u7136\u9053\uff1a\u201c\u4e3a\u4f55\u4e0d\u8ba9\u4f60\u592b\u5a7f\u51fa\u624b\uff1f\u201d \u3000\u3000\u67f3\u65e0\u7709\u6014\u4e86\u6014\u9053\uff1a\u201c\u8fd9\u2026\u2026\u2026\u201d \u3000\u3000\u90a3\u77ee\u5c0f\u7684\u9ed1\u8863\u4eba\u5df2\u5389\u58f0\u9053\uff1a\u201c\u4f60\u96be\u9053\u8ba4\u4e3a\u4f60\u7684\u5251\u6cd5\uff0c\u6bd4\u674e\u5bb6\u7684\u4f20\u4eba\u8fd8\u9ad8\u9ebd\uff1f\u201d \u3000\u3000\u559d\u58f0\u4e2d\uff0c\u4ed6\u638c\u4e2d\u5251\u5df2\u5316\u4e3a\u4e07\u70b9\u94f6\u661f\uff0c\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u67f3\u65e0\u7709\u773c\u775b\u7d27\u76ef\u7740\u8fd9\u6ee1\u5929\u94f6\u661f\uff0c\u8eab\u5b50\u5374\u52a8\u4e5f\u4e0d\u52a8\uff0c\u7adf\u4e0d\u95ea\u907f\u62db\u67b6\uff0c\u4f3c\u4e4e\u65e9\u5df2\u770b\u51fa\u8fd9\u4e00\u5251 \u4e43\u662f\u865a\u62db\u3002 \u3000\u3000\u6ee1\u5929\u94f6\u661f\u5230\u4e86\u5979\u9762\u524d\uff0c\u679c\u7136\u5947\u8ff9\u822c\u6d88\u5931\u4e86\u3002 \u3000\u3000\u90a3\u7626\u524a\u7684\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u5982\u4f55\uff1f\u201d \u3000\u3000\u77ee\u5c0f\u7684\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u8fd8\u597d\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u5ae3\u7136\u9053\uff1a\u201c\u591a\u8c22\u524d\u8f88\u3002\u201d \u3000\u3000\u5979\u5ffd\u53c8\u8f6c\u8eab\u8d70\u5230\u674e\u89c2\u9c7c\u9762\u524d\uff0c\u8eac\u8eab\u9053\uff1a\u201c\u5973\u513f\u60f3\u6c42\u60a8\u8001\u4eba\u5bb6\u8d4f\u5251\u4e00\u7528\u3002\u201d \u3000\u3000\u90a3\u8001\u4eba\u832b\u7136\u77a7\u4e86\u5979\u4e00\u773c\uff0c\u53c8\u5782\u4e0b\u5934\u3002 \u3000\u3000\u67f3\u65e0\u7709\u5374\u5df2\u518d\u62dc\u9053\uff1a\u201c\u591a\u8c22\u60a8\u8001\u4eba\u5bb6\u6069\u5178\u3002\u201d\u5979\u7adf\u7136\u81ea\u8bf4\u81ea\u8bdd\u7684\u5c31\u5c06\u8001\u4eba\u9762\u524d\u7684\u5251\u62ff\u4e86 \u8fc7\u6765\u3002 \u3000\u3000\u8001\u4eba\u9762\u4e0a\u7684\u808c\u8089\u4f3c\u4e4e\u8d77\u4e86\u4e00\u9635\u98a4\u6296\uff0c\u76ee\u4e2d\u4e5f\u7206\u51fa\u4e00\u661f\u706b\u5149\uff0c\u53ea\u4e0d\u8fc7\u8fde\u4e00\u4e2a\u5b57\u90fd\u6ca1\u6709\u8bf4\u51fa \u6765\u800c\u5df2\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u5ffd\u7136\u51b2\u4e86\u8fc7\u53bb\uff0c\u7ad9\u5230\u695a\u7559\u9999\u8eab\u65c1\u3002 \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4f60\u8981\u5e72\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u5927\u58f0\u9053\uff1a\u201c\u4ed6\u4eec\u65e2\u7136\u6709\u516d\u4e2a\u4eba\uff0c\u54b1\u4eec\u4e3a\u4f55\u4e0d\u80fd\u4e24\u4e2a\u4eba\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u82e6\u7b11\u9053\uff1a\u201c\u4e3a\u4f55\u8981\u4e24\u4e2a\u4eba\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u4e24\u4e2a\u4eba\u603b\u6bd4\u4e00\u4e2a\u4eba\u597d\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u53f9\u9053\uff1a\u201c\u4e24\u4e2a\u4eba\u82e5\u4e00\u9f50\u6b7b\uff0c\u5c31\u6ca1\u6709\u4e00\u4e2a\u4eba\u597d\u4e86\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u7d27\u63e1\u53cc\u62f3\uff0c\u8fd8\u672a\u8bf4\u8bdd\uff0c\u67f3\u65e0\u7709\u5df2\u60a0\u7136\u9053\uff1a\u201c\u4f60\u8fd8\u662f\u542c\u4ed6\u7684\u8bdd\u5427\uff01\u4ed6\u4e00\u4e2a\u4eba\u4e5f\u8bb8\u8fd8 \u6709\u4e00\u4e24\u5206\u9003\u51fa\u53bb\u7684\u673a\u4f1a\uff0c\u82e5\u52a0\u4e0a\u4f60\uff0c\u5c31\u8fde\u534a\u5206\u673a\u4f1a\u90fd\u6ca1\u6709\u4e86\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u8138\u6da8\u5f97\u901a\u7ea2\uff0c\u77aa\u7740\u695a\u7559\u9999\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u4e0d\u613f\u548c\u6211\u4e00\u8d77\u52a8\u624b\u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u63e1\u7740\u4ed6\u7684\u624b\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u4f60\u4ed4\u7ec6\u518d\u60f3\u4e00\u60f3\uff0c\u5c31\u4f1a\u660e\u767d\u6211\u7684\u610f\u601d\u4e86\u3002\u201d \u3000\u3000\u4ed6\u5634\u91cc\u8bf4\u8bdd\u65f6\uff0c\u5df2\u5728\u80e1\u94c1\u82b1\u638c\u5fc3\u4e3a\u4e86\u4e2a\u5b57\uff1a\u201c\u6551\u201d\u3002 \u3000\u3000\u4ed6\u7684\u610f\u601d\u81ea\u7136\u662f\u8981\u80e1\u94c1\u82b1\u53bb\u5c06\u82cf\u84c9\u84c9\u5979\u4eec\u6551\u51fa\u6765\u3002 \u3000\u3000\u56e0\u4e3a\u73b0\u5728\u674e\u7389\u51fd\u592b\u5987\u9131\u5728\u8fd9\u5385\u5802\u4e2d\uff0c\u800c\u4e14\u7edd\u4e0d\u4f1a\u79bb\u5f00\uff0c\u201c\u62e5\u7fe0\u5c71\u5e84\u201d\u4e2d\u522b\u7684\u5730\u65b9\uff0c\u6211\u5fc5 \u5b9a\u751a\u662f\u7a7a\u865a\u3002 \u3000\u3000\u8fd9\u6b63\u662f\u6551\u4eba\u7684\u597d\u673a\u4f1a\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u957f\u957f\u5410\u51fa\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6211\u660e\u767d\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u5fae\u7b11\u9053\uff1a\u201c\u5f88\u597d\uff0c\u6211\u77e5\u9053\u4f60\u6c38\u8fdc\u4e0d\u4f1a\u8ba9\u6211\u5931\u671b\u7684\u3002\u201d \u3000\u3000\u4ed6\u4e00\u9762\u8bf4\u7740\u8bdd\uff0c\u4e00\u9762\u53c8\u5728\u80e1\u94c1\u82b1\u638c\u5fc3\u4e3a\u4e86\u4e2a\u5b57\uff1a\u201c\u8d70\u201d\u3002 \u3000\u3000\u8fd9\u610f\u601d\u81ea\u7136\u662f\u8981\u80e1\u94c1\u82b1\u5c06\u5979\u4eec\u6551\u51fa\u5f8c\uff0c\u7acb\u523b\u5c31\u8d70\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u8138\u4e0a\u53c8\u53d8\u4e86\u989c\u8272\uff0c\u5931\u58f0\u9053\uff1a\u201c\u4f46\u662f\u4f60\u2026\u2026\u2026\u201d \u3000\u3000\u695a\u7559\u9999\u7528\u624b\u634f\u4e86\u634f\u4ed6\u7684\u624b\uff0c\u542b\u7b11\u9053\uff1a\u201c\u4f60\u82e5\u662f\u6211\u7684\u597d\u670b\u53cb\uff0c\u5c31\u8be5\u8ba9\u6211\u4e13\u5fc3\u4e00\u610f\u7684\u52a8\u624b\uff0c \u4f60\u603b\u8be5\u77e5\u9053\u6211\u7684\u813e\u6c14\uff0c\u82e5\u6709\u522b\u7684\u4e8b\u5206\u4e86\u6211\u7684\u5fc3\uff0c\u6211\u5c31\u771f\u7684\u8fde\u8fd9\u534a\u5206\u53d6\u80dc\u7684\u673a\u4f1a\u90fd\u6ca1\u6709\u4e86\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9ed8\u7136\u534a\u664c\uff0c\u6c89\u91cd\u7684\u70b9\u4e86\u70b9\u5934\uff0c\u53ea\u89c9\u695a\u7559\u9999\u7684\u624b\u4ecd\u662f\u90a3\u9ebd\u6e29\u6696\uff0c\u90a3\u9ebd\u575a\u5b9a\uff0c\u4ed6\u81ea\u5df1 \u7684\u624b\u5374\u5df2\u53d8\u5f97\u51b0\u51b7\u3002 \u3000\u3000\u4ed6\u5fcd\u4e0d\u4f4f\u4e5f\u7528\u529b\u63e1\u4e86\u63e1\u695a\u7559\u9999\u7684\u624b\uff0c\u4e45\u4e45\u4e0d\u5fcd\u653e\u5f00\uff0c\u597d\u50cf\u8fd9\u5df2\u662f\u4ed6\u4eec\u4e4b\u95f4\uff0c\u6700\u5f8c\u4e00\u6b21\u63e1 \u624b\u4e86\u3002 \u3000\u3000\u695a\u7559\u9999\u62cd\u4e86\u62cd\u4ed6\u80a9\u5934\uff0c\u4e24\u4eba\u9762\u5bf9\u9762\uff0c\u4e92\u76f8\u51dd\u6ce8\u4e86\u534a\u664c\uff0c\u7136\u5f8c\uff0c\u695a\u7559\u9999\u5ffd\u7136\u8f6c\u8fc7\u8eab\uff0c\u7f13\u7f13 \u9053\u201c\u5728\u4e0b\u5df2\u51c6\u5907\u597d\u4e86\uff0c\u524d\u8f88\u4eec\u5c31\u8bf7\u51fa\u624b\u5427\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u5e76\u4e0d\u662f\u4e2a\u591a\u6101\u5584\u611f\u7684\u4eba\uff0c\u800c\u4e14\u4e00\u5411\u5bf9\u695a\u7559\u9999\u7684\u6b66\u529f\u5f88\u6709\u4fe1\u5fc3\uff0c\u4f46\u73b0\u5728\uff0c\u4ed6\u773c\u775b\u5374 \u4e0d\u77e5\u600e\u5730\u6709\u4e9b\u53d1\u7ea2\u4e86\u3002 \u3000\u3000\u67f3\u65e0\u7709\u671b\u7740\u695a\u7559\u9999\u5ae3\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u96be\u9053\u8fd8\u662f\u4e0d\u7528\u5175\u5668\u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u6de1\u6de1\u9053\uff1a\u201c\u5230\u4e86\u8fd9\u79cd\u65f6\u5019\uff0c\u7528\u4e0d\u7528\u5175\u5668\u53cd\u6b63\u90fd\u5df2\u6ca1\u4ec0\u9ebd\u4e24\u6837\u4e86\u3002\u90a3\u53c8\u77ee\u53c8\u80d6\u7684\u9ed1 \u8863\u4eba\uff0c\u5ffd\u7136\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6b64\u4eba\u7684\u80c6\u5b50\u5012\u4e0d\u5c0f\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u524d\u8f88\u8fc7\u5956\u4e86\uff0c\u5176\u5b9e\u5728\u4e0b\u7684\u80c6\u5b50\u4e00\u5411\u4e0d\u5927\uff0c\u6bcf\u6b21\u548c\u522b\u4eba\u4ea4\u5b50\u4e4b\u524d\uff0c\u5fc3\u91cc\u90fd\u5bb3 \u6015\u5f97\u5f88\uff0c\u53ef\u662f\u7b49\u5230\u51fa\u624b\u4e4b\u5f8c\uff0c\u5c31\u5c06\u5bb3\u6015\u5fd8\u8bb0\u4e86\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u5230\u6700\u5f8c\u4e00\u53e5\u8bdd\u65f6\uff0c\u5ffd\u7136\u95ea\u7535\u822c\u51fa\u624b\uff0c\u66f2\u6307\u5982\u94a9\uff0c\u201c\u53cc\u9f99\u593a\u73e0\u201d\uff0c\u76f4\u53d6\u67f3\u65e0\u7709\u7684\u53cc \u76ee\u3002 \u3000\u3000\u67f3\u65e0\u7709\u9aa4\u51fa\u4e0d\u610f\uff0c\u5927\u60ca\u9000\u6b65\u3002 \u3000\u3000\u8c01\u77e5\u695a\u7559\u9999\u8fd9\u4e00\u7740\u7adf\u662f\u865a\u62db\uff0c\u5de6\u624b\u653b\u51fa\uff0c\u53f3\u624b\u7684\u62c7\u6307\u548c\u98df\u4e2d\u4e24\u6307\uff0c\u5df2\u634f\u4f4f\u4e86\u67f3\u65e0\u7709\u638c\u4e2d \u5251\u7684\u5251\u5c16\u3002 \u3000\u3000\u67f3\u65e0\u7709\u53ea\u89c9\u4e00\u80a1\u5947\u5f02\u7684\u9707\u52a8\uff0c\u81ea\u5251\u8eab\u4e0a\u4f20\u4e86\u8fc7\u6765\uff0c\u9707\u5f97\u5979\u624b\u8155\u53c8\u9178\u53c8\u9ebb\uff0c\u957f\u5251\u518d\u4e5f\u628a\u63e1 \u4e0d\u4f4f\u3002 \u3000\u3000\u53ea\u542c\u695a\u7559\u9999\u7b11\u9053\uff1a\u201c\u591a\u8c22\u5ac2\u592b\u4eba\u501f\u5251\uff0c\u591a\u8c22\u591a\u8c22\u3002\u201d \u7b2c\u5341\u4e09\u7ae0\u3000\u4e16\u5bb6\u5927\u65cf>> \u53e4\u9f99\u300a\u695a\u7559\u9999\u7cfb\u5217\xb7\u753b\u7709\u9e1f\u300b \u7b2c\u5341\u4e09\u7ae0\u3000\u4e16\u5bb6\u5927\u65cf \u3000\u3000\u7b11\u8bed\u58f0\u4e2d\uff0c\u90a3\u67c4\u7cbe\u5149\u56db\u5c04\u7684\u79cb\u6c34\u957f\u5251\u5df2\u5230\u4e86\u4ed6\u624b\u91cc\uff0c\u4ed6\u4ecd\u7136\u4ee5\u53c1\u6839\u624b\u6307\u63e1\u7740\u5251\u5c16\uff0c\u5374\u4ee5 \u5251\u67c4\u5411\u90a3\u7626\u957f\u9ed1\u8863\u4eba\u7684\u80f8\u819b\u649e\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u90a3\u9ed1\u8863\u4eba\u8f7b\u53f1\u9053\uff1a\u201c\u597d\u5feb\u7684\u624b\uff01\u201d \u3000\u3000\u77ed\u77ed\u7684\u56db\u4e2a\u5b57\u8bf4\u5b8c\uff0c\u4ed6\u4e0d\u4f46\u8eb2\u5f00\u4e86\u695a\u7559\u9999\u653b\u6765\u7684\u8fd9\u4e00\u62db\uff0c\u800c\u4e14\u5251\u5149\u95ea\u52a8\uff0c\u4e5f\u5df2\u8fd8\u4e86\u4e24 \u62db\u3002 \u3000\u3000\u67f3\u65e0\u7709\u60ca\u9b42\u672a\u5b9a\uff0c\u50cf\u662f\u8fd8\u5728\u53d1\u6014\uff0c\u773c\u89c1\u5251\u9635\u5df2\u5c06\u53d1\u52a8\uff0c\u674e\u7389\u51fd\u8dfa\u4e86\u8dfa\u811a\uff0c\u62d4\u5251\u8fce\u4e86\u4e0a \u53bb\u3002 \u3000\u3000\u65bc\u662f\u5251\u5149\u7a81\u70bd\uff0c\u51b7\u98ce\u9aa4\u8d77\u3002 \u3000\u3000\u8fd9\u67c4\u5251\u5df2\u5316\u4e3a\u4e00\u7247\u5149\u5e55\uff0c\u5377\u53bb\u4e86\u695a\u7559\u9999\u7684\u8eab\u5f71\u3002 \u3000\u3000\u67f3\u65e0\u7709\u8e09\u8dc4\u5f8c\u9000\uff0c\u8fd4\u5230\u5899\u89d2\uff0c\u8138\u4e0a\u5df2\u6ca1\u6709\u4e1d\u6beb\u8840\u8272\uff0c\u8fc7\u4e86\u534a\u664c\uff0c\u4e00\u6ef4\u6ef4\u773c\u6cea\u6e90\u6e90\u81ea\u773c\u89d2 \u6d41\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u695a\u7559\u9999\u51fa\u624b\u3001\u593a\u5251\u3001\u53d1\u62db\uff0c\u67f3\u65e0\u7709\u9000\u4e0b\uff0c\u674e\u7389\u51fd\u51b2\u51fa\uff0c\u5251\u9635\u53d1\u52a8\uff0c\u8fd9\u51e0\u4e4e\u90fd\u662f\u5728\u540c\u4e00\u65f6 \u95f4\u5185\u53d1\u751f\u7684\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u53ea\u77a7\u5f97\u5fc3\u52a8\u9b44\uff0c\u53c8\u9e70\u53c8\u559c\uff0c\u51e0\u4e4e\u5fcd\u4e0d\u4f4f\u8981\u5927\u58f0\u559d\u8d77\u91c7\u6765\uff0c\u695a\u7559\u9999\u8fd9\u4e00\u624b\uff0c\u5b9e\u5728\u503c \u5f97\u559d\u91c7\u3002 \u3000\u3000\u8fd9\u4e00\u573a\u51b3\u6218\u7684\u80dc\u8d25\uff0c\u867d\u7136\u8fd8\u4e0d\u53ef\u77e5\uff0c\u4f46\u695a\u7559\u9999\u81f3\u5c11\u5df2\u62a2\u5f97\u4e00\u7740\u5148\u673a\uff0c\u4ee4\u8fd9\u5251\u9635\u4e00\u65f6\u95f4\u65e0 \u6cd5\u53d1\u6325\u51fa\u6700\u5927\u7684\u5a01\u529b\u3002 \u3000\u3000\u800c\u4e14\u674e\u7389\u51fd\u5bf9\u8fd9\u9635\u6cd5\u663e\u7136\u4e0d\u53ca\u67f3\u65e0\u7709\u719f\u6089\uff0c\u73b0\u5728\u7531\u4ed6\u6765\u4ee3\u66ff\u67f3\u65e0\u7709\u7684\u4f4d\u7f6e\uff0c\u8fd9\u9635\u6cd5\u52bf\u5fc5 \u53c8\u8981\u6253\u4e2a\u6298\u6263\u3002 \u3000\u3000\u5982\u6b64\u9a7d\u5fc3\u52a8\u9b44\u7684\u5927\u6218\u5f53\u524d\uff0c\u80e1\u94c1\u82b1\u5b9e\u5728\u4e0d\u820d\u5f97\u8d70\uff0c\u66f4\u4e0d\u5fcd\u5c06\u695a\u7559\u9999\u4e00\u4e2a\u4eba\u7559\u5728\u8fd9\u91cc\u62da \u547d\u3002 \u3000\u3000\u4f46\u4ed6\u5374\u975e\u8d70\u4e0d\u53ef\uff0c\u53ea\u56e0\u4ed6\u77e5\u9053\u695a\u7559\u9999\u770b\u89c1\u4ed6\u8fd8\u6ca1\u6709\u8d70\uff0c\u4e00\u5b9a\u96be\u514d\u8981\u5206\u5fc3\u7684\uff0c\u4ed6\u81ea\u7136\u4e5f\u77e5 \u9053\u5728\u8fd9\u6837\u7684\u6076\u6218\uff0c\u65e0\u8bba\u8c01\u53ea\u8981\u7a0d\u4e00\u5206\u5fc3\uff0c\u5c31\u53ef\u80fd\u4f7f\u51fa\u9519\u8bef\u7684\u62db\u5f0f\uff0c\u65e0\u8bba\u591a\u9ebd\u5c0f\u7684\u9519\u8bef\uff0c\u90fd\u8db3 \u4ee5\u81f4\u547d\u3002 \u3000\u3000\u6591\u624b\u5bf9\u62db\uff0c\u6b66\u529f\u5f3a\u5f31\u56fa\u7136\u662f\u80dc\u8d1f\u7684\u6700\u5927\u5173\u952e\uff0c\u4f46\u51fa\u624b\u65f6\u7684\u5224\u65ad\u662f\u5426\u6b63\u786e\uff0c\u66f4\u662f\u81f4\u547d\u7684\u56e0 \u7d20\u3002 \u3000\u3000\u89d2\u843d\u91cc\u6709\u6247\u7a97\u5b50\u662f\u5f00\u7740\u7684\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u54ac\u4e86\u54ac\u7259\uff0c\u659c\u659c\u7a9c\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u5ead\u56ed\u4e2d\u6d53\u836b\u6ee1\u5730\uff0c\u9759\u5bc2\u65e0\u4eba\uff0c\u53ea\u6709\u201c\u5636\u5636\u201d\u7684\u5251\u98ce\uff0c\u81ea\u5385\u5802\u4e2d\u4f20\u51fa\uff0c\u5251\u98ce\u867d\u6025\uff0c\u5374\u6ca1\u6709 \u5251\u5200\u76f8\u51fb\u58f0\u3002 \u3000\u3000\u8fd9\u5251\u9635\u51fa\u624b\u914d\u5408\u4e4b\u96b9\u5999\uff0c\u5b9e\u5df2\u5999\u5230\u5cf0\u5dc5\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u53c8\u5fcd\u4e0d\u4f4f\u56de\u9996\u77a7\u4e86\u4e00\u773c\uff0c\u53ea\u89c1\u90a3\u5251\u5149\u5316\u6210\u7684\u5149\u5e55\uff0c\u5df2\u6108\u6765\u6108\u5bc6\uff0c\u5df2\u77a7\u4e0d\u51fa\u4e1d\u6beb\u6f0f \u6d1e\u3002 \u3000\u3000\u4ed6\u5b9e\u5728\u60f3\u4e0d\u51fa\u695a\u7559\u9999\u80fd\u6709\u4ec0\u9ebd\u6cd5\u5b50\u81ea\u8fd9\u5251\u9635\u4e2d\u51b2\u51fa\u6765\uff0c\u8fd9\u4e00\u773c\u77a7\u51fa\uff0c\u4ed6\u7684\u811a\u5df2\u65e0\u6cd5\u79fb\u52a8 \u534a\u6b65\u3002 \u3000\u3000\u4ed6\u5728\u5fc3\u91cc\u66ff\u81ea\u5df1\u89e3\u91ca\uff1a\u201c\u8fd9\u5e84\u9662\u5982\u6b64\u5e7f\u5927\uff0c\u8981\u627e\u53c1\u4e2a\u4eba\uff0c\u5b9e\u5982\u5927\u6d77\u635e\u9488\uff0c\u6211\u53cd\u6b63\u4e00\u5b9a\u627e \u4e0d\u7740\u7684\uff0c\u8fd8\u662f\u7559\u5728\u8fd9\u91cc\u66ff\u4ed6\u628a\u573a\u5b50\u7684\u597d\uff0c\u4ed6\u82e5\u62b5\u6321\u4e0d\u4f4f\u65f6\uff0c\u4e5f\u8bb8\u6211\u8fd8\u80fd\u5e2e\u4e2a\u5fd9\u3002\u201d \u3000\u3000\u5fae\u98ce\u5439\u52a8\uff0c\u6728\u53f6\u8427\u8427\u3002 \u3000\u3000\u903c\u6b66\u6797\u4e16\u5bb6\u7684\u89c4\u77e9\u663e\u7136\u4e0d\u5c0f\uff0c\u6b64\u95f4\u867d\u7136\u53d1\u751f\u4e86\u8fd9\u9ebd\u5927\u7684\u4e8b\uff0c\u4f46\u4e5f\u7edd\u6ca1\u6709\u4e00\u4e2a\u4eba\u6562\u6765\u770b\u70ed \u95f9\u3002 \u3000\u3000\u8fdc\u5904\uff0c\u6b63\u6709\u4e00\u7f15\u708a\u70df\u8885\u8885\u5347\u8d77\uff0c\u5fae\u98ce\u4e2d\u9690\u9690\u6709\u4e00\u9635\u7ca5\u9999\u4f20\u6765\uff0c\u663e\u7136\u6b63\u662f\u65e9\u996d\u5df2\u70ed\u7684\u65f6 \u5019\u3002 \u3000\u3000\u65e0\u8bba\u53d1\u751f\u591a\u9ebd\u5927\u7684\u4e8b\uff0c\u8fd9\u201c\u62e5\u7fe0\u5c71\u5e84\u201d\u4e2d\u7684\u4eba\uff0c\u90fd\u4e0d\u6562\u6539\u53d8\u65e5\u5e38\u7684\u89c4\u77e9\uff0c\u66f4\u4e0d\u6562\u653e\u4e0b\u624b \u8fb9\u7684\u5de5\u4f5c\u3002 \u3000\u3000\u8fd9\u79cd\u4e16\u5bb6\u5927\u65cf\uff0c\u6b63\u5982\u78d0\u77f3\u822c\u4e0d\u53ef\u64bc\u52a8\u3002 \u3000\u3000\u60f3\u5230\u8fd9\u91cc\uff0c\u80e1\u94c1\u82b1\u4e0d\u7981\u53c8\u53f9\u4e86\u53e3\u6c14\uff0c\u53ef\u662f\u8fd9\u65f6\u7ca5\u7684\u9999\u6c14\u66f4\u6d53\uff0c\u4ed6\u8fd9\u624d\u53d1\u89c9\u81ea\u5df1\u5df2\u7ecf\u5f88\u997f \u4e86\u3002 \u3000\u3000\u4e5f\u5c31\u5728\u8fd9\u65f6\uff0c\u4ed6\u5fc3\u91cc\u5ffd\u7136\u6709\u7075\u5149\u95ea\u52a8\uff1a\u201c\u4e00\u4e2a\u4eba\u65e0\u8bba\u5728\u4ec0\u9ebd\u60c5\u51b5\u4e0b\uff0c\u90fd\u4e00\u5b9a\u8981\u5403\u996d \u7684\u3002\u201d \u3000\u3000\u5e1d\u738b\u56fa\u7136\u8981\u5403\u996d\uff0c\u8d31\u6c11\u4e5f\u662f\u8981\u5403\u996d\u7684\uff0c\u201c\u62e5\u7fe0\u5c71\u5e84\u201d\u4e2d\u7684\u4eba\u8981\u5403\u996d\uff0c\u82cf\u84c9\u84c9\u5979\u4eec\u4e5f\u975e\u5403 \u996d\u4e0d\u53ef\u3002 \u3000\u3000\u674e\u7389\u51fd\u592b\u59bb\u8981\u4ee5\u5979\u4eec\u4f5c\u8981\u80c1\u695a\u7559\u9999\u7684\u628a\u67c4\uff0c\u5c31\u4e0d\u80fd\u8ba9\u5979\u4eec\u997f\u6b7b\uff0c\u81f3\u5c11\u603b\u4e0d\u80fd\u4e0d\u7ed9\u5979\u4eec\u996d \u5403\u3002 \u3000\u3000\u708a\u70df\uff0c\u81ea\u4e1c\u65b9\u7684\u4e00\u68da\u7d2b\u82b1\u5f8c\u5347\u8d77\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u7acb\u523b\u5c55\u52a8\u8eab\u5f62\uff0c\u540c\u90a3\u8fb9\u51c9\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u82b1\u68da\u5f8c\u5c31\u662f\u8fd9\u5ead\u56ed\u7684\u56f4\u5899\uff0c\u5899\u5916\u53c8\u6709\u91cd\u5c0f\u5c0f\u7684-\u843d\uff0c\u9662\u5b50\u91cc\u6ee1\u4e86\u4e00\u7aff\u7aff\u8863\u88f3\u65c1\u8fb9\u6709\u4e24\u6392 \u74e6\u623f\uff0c\u663e\u7136\u6b63\u662f\u201c\u62e5\u7fe0\u5c71\u5e84\u201d\u4e2d\u5974\u4ec6\u5bb6\u4e01\u4eec\u7684\u5c45\u5904\uff0c\u6b64\u523b\u6b63\u6709\u51e0\u4eba\u5728\u6a90\u4e0b\u78e8\u5200\u64e6\u67aa\uff0c\u6574\u7406\u7740 \u5200\u67c4\u67aa\u4e0a\u7684\u7ea2\u7ef8\u3002 \u3000\u3000\u8fd8\u6709\u51e0\u4e2a\u8d64\u7740\u4e0a\u8eab\u7684\u58ee\u6c49\uff0c\u6b63\u5728\u9662\u5b50\u91cc\u7684\u7a7a\u5730\u4e0a\u7ec3\u62f3\uff0c\u4e00\u9762\u8fd8\u5583\u5583\u62b1\u6028\u7740\u9662\u5b50\u91cc\u6652\u7684\u8863 \u670d\u592a\u591a\uff0c\u5bb3\u5f97\u4ed6\u4eec\u62f3\u811a\u65bd\u5c55\u4e0d\u5f00\u3002 \u3000\u3000\u518d\u8fc7\u53bb\uff0c\u53c8\u6709\u4e00\u6392\u5e73\u623f\uff0c\u623f\u9876\u4e0a\u6709\u597d\u51e0\u4e2a\u70df\u56f1\uff0c\u5176\u4e2d\u6709\u53c1\u4e2a\u6b63\u5728\u5192\u7740\u70df\uff0c\u8fd9\u663e\u7136\u5c31\u662f\u674e \u5bb6\u7684\u53a8\u623f\u4e86\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u672c\u6765\u8fd8\u6709\u4e9b\u7d27\u5f20\uff0c\u4f46\u7acb\u523b\u5c31\u53d1\u73b0\u8fd9\u9662\u5b50\u91cc\u7684\u4eba\u867d\u591a\uff0c\u795e\u60c5\u5374\u90fd\u5f88\u60a0\u95f2\uff0c\u751a\u81f3\u90fd\u6709 \u4e9b\u61d2\u6d0b\u6d0b\u7684\u3002 \u3000\u3000\u56e0\u4e3a\u8fd9\u91cc\u5df2\u662f\u4ed6\u4eec\u7684\u5929\u4e0b\uff0c\u4ed6\u4eec\u65e2\u7528\u4e0d\u7740\u62c5\u5fc3\u4e0a\u9762\u7684\u4eba\u4f1a\u6765\u67e5\u52d8\uff0c\u4e5f\u7528\u4e0d\u7740\u62c5\u5fc3\u5f3a\u76d7\u5c0f \u5077\u3002 \u3000\u3000\u4e16\u4e0a\u6700\u7b28\u7684\u5f3a\u76d7\uff0c\u4e5f\u4e0d\u4f1a\u7167\u987e\u5230\u4ed6\u4eec\u8fd9\u4e9b\u4eba\u8eab\u4e0a\u6765\u7684\uff0c\u5c31\u7b97\u771f\u7684\u6709\u4eba\u6562\u6765\u627e\u201c\u62e5\u7fe0\u5c71 \u5e84\u201d\u7684\u9709\u6c14\uff0c\u4e5f\u7edd\u4e0d\u4f1a\u62ff\u4ed6\u4eec\u505a\u5bf9\u8c61\uff0c\u6240\u4ee5\u4ed6\u4eec\u6bcf\u4e2a\u4eba\u90fd\u653e\u5fc3\u5f97\u5f88--\u65bc\u662f\u80e1\u94c1\u82b1\u4e5f\u5c31\u653e\u5fc3\u5f97 \u5f88\u3002 \u3000\u3000\u4ed6\u773c\u73e0\u5b50\u4e00\u8f6c\uff0c\u5ffd\u7136\u8131\u4e0b\u8eab\u4e0a\u7684\u8863\u670d\uff0c\u7cbe\u8d64\u7740\u4e0a\u8eab\uff0c\u81ea\u6811\u4e1b\u4e2d\u7a9c\u4e86\u51fa\u6765\uff0c\u627e\u4e86\u4e2a\u592a\u9633\u6652 \u4e0d\u5230\u7684\u5899\u89d2\u5750\u4e0b\uff0c\u4f38\u7740\u61d2\u8170\uff0c\u5598\u7740\u6c14\uff0c\u505a\u51fa\u4e00\u526f\u521a\u7ec3\u62f3\u7ec3\u5b8c\u7684\u6a21\u6837\uff0c\u91cc\u91cc\u5916\u5916\u5c45\u7136\u6ca1\u6709\u4e00\u4e2a \u4eba\u6ce8\u610f\u5230\u4ed6\u3002 \u3000\u3000\u53ea\u89c1\u53a8\u623f\u90a3\u8fb9\u7684\u6811\u836b\u4e0b\uff0c\u4e5f\u5750\u7740\u4e00\u5806\u4eba\uff0c\u6709\u7537\u6709\u5973\uff0c\u7537\u7684\u6b63\u5728\u60f3\u6cd5\u5b50\u9017\u5973\u7684\u8bf4\u8bdd\uff0c\u5973\u7684 \u5374\u5047\u88c5\u4e0d\u7406\u3002 \u3000\u3000\u5929\u4e0b\u4e4c\u9e26\u4e00\u822c\u9ed1\uff0c\u5929\u4e0b\u7684\u5974\u4ec6\u4e5f\u5168\u90fd\u4e00\u6837\uff0c\u201c\u62e5\u7fe0\u5c71\u5e84\u201d\u7684\u89c4\u77e9\u867d\u4e25\uff0c\u4f46\u53ea\u8981\u4e00\u79bb\u5f00\u4e3b \u5b50\u7684\u773c\u775b\uff0c\u4ed6\u4eec\u7684\u80c6\u5b50\u4e5f\u5c31\u5927\u4e86\uff0c\u82e5\u60f3\u8981\u5974\u624d\u4e0d\u5411\u4e2b\u5934\u52fe\u642d\uff0c\u90a3\u53ea\u6015\u6bd4\u8981\u72d7\u4e0d\u5403\u7caa\u66f4\u56f0\u96be\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u77a7\u5f97\u6697\u6697\u597d\u7b11\uff0c\u53ea\u89c9\u8fd9\u4e9b\u5c0f\u4e2b\u5934\u7684\u7751\u957f\u5f97\u867d\u4e0d\u5927\u600e\u9ebd\u6837\uff0c\u4f53\u6001\u5012\u8fd8\u52a8\u4eba\uff0c\u5176\u4e2d\u6709 \u4e24\u4e2a\u770b\u6765\u8fd8\u6ee1\u4e0d\u9519\u3002 \u3000\u3000\u5c24\u5176\u7b49\u592a\u9633\u4e00\u7167\u5728\u5979\u4eec\u8eab\u4e0a\uff0c\u7d27\u7ef7\u5728\u8eab\u4e0a\u7684\u8584\u7ef8\u8863\u670d\uff0c\u5c31\u597d\u50cf\u53d8\u5f97\u900f\u660e\u4e86\uff0c\u8fde\u7ea2\u7ea2\u7684\u809a \u515c\u90fd\u53ef\u4ee5\u770b\u5f97\u5230\uff0c\u76f4\u77a7\u5f97\u90a3\u4e9b\u7cbe\u529b\u8fc7\u5269\u7684\u5927\u7537\u4eba\u4eec\uff0c\u4e00\u4e2a\u4e2a\u773c\u73e0\u5b50\u90fd\u51f8\u4e86\u51fa\u6765\uff0c\u4e0d\u505c\u7684\u7740\u53e3 \u6c34\u3002 \u3000\u3000\u997c\u4e86\u534a\u664c\uff0c\u53a8\u623f\u91cc\u5ffd\u7136\u4f20\u51fa\u4e00\u9635\u94c1\u677f\u54cd\u3002 \u3000\u3000\u6811\u4e0b\u7684\u7537\u7537\u5973\u5973\u4e00\u7ad9\u4e86\u8d77\u6765\uff0c\u6709\u4e2a\u5c0f\u4f19\u5b50\u7b11\u563b\u563b\u9053\uff1a\u201c\u4ed6\u4eec\u996d\u600e\u5730\u8d8a\u716e\u8d8a\u5feb\u4e86\uff0c\u6211\u7684\u8bdd \u8fd8\u6ca1\u8bf4\u5b8c\u54e9\uff01\u201d \u3000\u3000\u90a3\u4fcf\u4e2b\u5934\u5c31\u62bf\u7740\u5634\u7b11\u788e\u9053\uff1a\u201c\u4eca\u5929\u996d\u5403\u5b8c\u4e86\uff0c\u660e\u5929\u5c31\u4e0d\u5403\u4e86\u9ebd\uff1f\u201d \u3000\u3000\u90a3\u5c0f\u4f19\u5b50\u773c\u775b\u4e00 \u3000\u3000\u4eae\uff0c\u6084\u58f0\u9053\uff1a\u201c\u660e\u5929\u4f60\u80af\u4e0d\u80af\u2026\u2026\u201d \u3000\u3000\u8fd9\u65f6\u522b\u7684\u4eba\u5df2\u4e00\u7a9d\u8702\u5411\u53a8\u623f\u6d8c\u4e86\u8fc7\u53bb\uff0c\u811a\u6b65\u58f0\u6df9\u6ca1\u4e86\u4ed6\u4eec\u7684\u8bed\u58f0\uff0c\u4e00\u6761\u633a\u80f8\u51f8\u809a\u7684\u5927\u6c49 \u8d70\u51fa\u6765\u5f80\u95e8\u53e3\u4e00\u7ad9\uff0c\u82e5\u975e\u6ee1\u8eab\u90fd\u662f\u6cb9\uff0c\u770b\u6765\u5012\u50cf\u662f\u4e2a\u5de8\u65e0\u9738\u4f3c\u7684\uff0c\u624b\u53c9\u7740\u8170\uff0c\u77aa\u5927\u4e86\u773c\u775b\u543c \u9053\uff1a\u201c\u4eba\u4eba\u90fd\u6709\u4efd\u7684\uff0c\u62a2\u4ec0\u9ebd\uff1f\u4e00\u4e2a\u4e2a\u6765\u3002\u201d \u3000\u3000\u6709\u4e2a\u9a6c\u8138\u6c49\u5b50\u5927\u58f0\u9053\uff1a\u201c\u6211\u4eec\u9a6c\u623f\u91cc\u7684\u4eba\u5929\u6ca1\u4eae\u5c31\u5f97\u8d77\u6765\u670d\u4f8d\u755c\u7272\uff0c\u6bcf\u5929\u8d77\u6765\u5f97\u6700\u65e9\uff0c \u809a\u5b50\u997f\u5f97\u6700\u5feb\uff0c\u8d75\u8001\u5927\uff0c\u4f60\u5c31\u5e2e\u4e2a\u5fd9\u5427\uff01\u201d \u3000\u3000\u90a3\u8d75\u8001\u5927\u8fde\u671b\u90fd\u4e0d\u671b\u4ed6\uff0c\u8f6c\u8eab\u63d0\u4e86\u98df\u76d2\u51fa\u6765\uff0c\u9053\uff1a\u201c\u4e0a\u623f\u7684\u59d1\u5a18\u4eec\u6765\u4e86\u9ebd\uff1f\u201d \u3000\u3000\u90a3\u9a6c\u8138\u6c49\u5b50 \u3000\u3000\u8138\u90fd\u6c14\u7ea2\u4e86\uff0c\u9053\uff1a\u201c\u4f60\u660e\u660e\u77e5\u9053\u53ea\u8981\u5c11\u5e84\u4e3b\u4e00\u56de\u6765\uff0c\u4e0a\u623f\u7684\u59d1\u5a18\u5c31\u90fd\u8ddf\u7740\u5403\u5c0f\u53a8\u623f\u7684\u4f19 \u98df\u4e86\uff0c\u4e3a\u4ec0\u9ebd\u8fd8\u8981\u51c6\u5907\u4ed6\u4eec\u7684\uff1f\u201d \u3000\u3000\u8d75\u8001\u5927\u8fd8\u662f\u4e0d\u7406\u4ed6\uff0c\u5374\u5411\u90a3\u4fcf\u4e2b\u5934\u7b11\u9053\uff1a\u201c\u4e0a\u623f\u7684\u59d1\u5a18\u4e0d\u6765\uff0c\u8fd9\u5c31\u4fbf\u5b9c\u4e86\u4f60\u5427\uff1a\u201c\u90a3\u4fcf \u4e2b\u5934\u4e00\u626d\u4e00\u626d\u7684\u8d70\u8fc7\u53bb\uff0c\u6293\u8d77\u98df\u76d2\u7684\u76d6\u5b50\u779f\u4e86\u4e00\u773c\uff0c\u53c8\u540c\u8d75\u8001\u5927\u779f\u4e86\u4e00\u773c\uff0c\u6084\u7b11\u9053\uff1a\u201c\u83dc\u8fd8 \u4e0d\u9519\uff0c\u4f46\u53ea\u6709\u8fd9\u9ebd\u51e0\u4e2a\u5305\u5b50\uff0c\u516b\u4e2a\u4eba\u600e\u9ebd\u591f\u5403\uff1f\u201d \u3000\u3000\u8d75\u8001\u5927\u5927\u7b11\u9053\uff1a\u201c\u5c0f\u4e2b\u5934\u4eec\uff0c\u4e00\u5929\u5230\u665a\u5c31\u77e5\u9053\u5403\uff0c\u4e5f\u4e0d\u6015\u628a\u809a\u5b50\u5403\u5927\u4e86\u6ca1\u4eba\u8981\u9ebd\uff1f\u201d \u3000\u3000\u90a3\u4fcf\u4e2b\u5934\u8dfa\u7740\u811a\u9053\uff1a\u201c\u597d\u5440\uff1a\u4f60\u5403\u6211\u7684\u8c46\u8150\uff0c\u770b\u6211\u4e0d\u544a\u8bc9\u7fe0\u51e4\u59d0\uff0c\u53eb\u5979\u4eca\u5929\u665a\u4e0a\u7f5a\u4f60\u8dea \u591c\u58f6\u3002\u201d \u3000\u3000\u8d75\u8001\u5927\u8d76\u7d27\u9053\uff1a\u201c\u597d\u4e86\uff01\u597d\u4e86\uff01\u5c0f\u7956\u5b97\uff0c\u7b97\u6211\u6015\u4f60\uff0c\u518d\u52a0\u4e00\u7b3c\u591f\u4e86\u9ebd\uff1f\u201d \u3000\u3000\u90a3\u4fcf\u4e2b\u5934\u8fd9\u624d\u7b11\u9053\uff1a\u201c\u8fd9\u8fd8\u5dee\u4e0d\u591a\u3002\u201d \u3000\u3000\u65bc\u662f\u5979\u5c31\u63d0\u8d77\u98df\u76d2\uff0c\u4e00\u626d\u4e00\u626d\u7684\u8d70\u4e86\uff0c\u4e34\u8d70\u65f6\u8fd8\u4e0d\u5fd8\u4e86\u9001\u8d75\u8001\u5927\u4e2a\u5a9a\u773c\uff0c\u81ea\u7136\u4e5f\u9001\u4e86\u90a3 \u5c0f\u4f19\u5b50\u4e00\u4e2a\u3002 \u3000\u3000\u53e6\u5916\u51e0\u4e2a\u4e2b\u5934\u4e5f\u90fd\u62ff\u5230\u98df\u76d2\u8d70\u4e86\uff0c\u6709\u7684\u5c41\u80a1\u4e0a\u8fd8\u88ab\u8d75\u8001\u5927\u90a3\u53ea\u6cb9\u624b\u634f\u4e86\u4e00\u628a\uff0c\u90a3\u9a6c\u8138\u6c49 \u5b50\u543c\u9053\uff1a\u201c\u8fd8\u6ca1\u6709\u8f6e\u5230\u9a6c\u623f\u9ebd\uff1f\u201d \u3000\u3000\u8d75\u8001\u5927\u50cf\u662f\u6839\u672c\u6ca1\u542c\u89c1\uff0c\u6162\u541e\u541e\u63d0\u8d77\u4e2a\u98df\u76d2\uff0c\u4e00\u4e2a\u8138\u4e0a\u957f\u7740\u51e0\u7c92\u767d\u9ebb\u5b50\u7684\u8001\u5988\u5b50\u7acb\u523b\u8d76 \u8fc7\u53bb\uff0c\u7b11\u9053\uff1a\u201c\u59d1\u5a18\u4eec\u7684\u4e00\u5206\u5b8c\uff0c\u6211\u5c31\u77e5\u9053\u8be5\u8f6e\u5230\u54b1\u4eec\u4e86\u3002\u201d \u3000\u3000\u5979\u4e5f\u6293\u8d77\u98df\u76d2\u4e00\u770b\uff0c\u53c8\u7b11\u9053\uff1a\u201c\u54b1\u4eec\u623f\u91cc\u7684\u4eba\u5e72\u7684\u662f\u7c97\u6d3b\uff0c\u4e0d\u6bd4\u90a3\u79c0\u91cc\u79c0\u6c14\u7684\u59d1\u5a18\u4eec\uff0c \u8fd9\u9ebd\u70b9\u83dc\u996d\u600e\u9ebd\u591f\u5403\uff1f\u54b1\u4eec\u4e5f\u4e0d\u8981\u83dc\u597d\uff0c\u996d\u2026\u2026\u2026\u201d \u3000\u3000\u8d75\u8001\u5927\u6c89\u7740\u7751\u9053\uff1a\u201c\u996d\u5c31\u53ea\u6709\u8fd9\u9ebd\u591a\uff0c\u5403\u4e0d\u5403\u968f\u4fbf\u4f60\uff0c\u5e84\u5b50\u91cc\u7684\u4eba\u82e5\u90fd\u50cf\u4f60\u4eec\u8fd9\u6837\u5403 \u6cd5\uff0c\u674e\u5bb6\u5c82\u975e\u65e9\u5c31\u88ab\u5403\u7a77\u4e86\u3002\u201d \u3000\u3000\u90a3\u8001\u5988\u5b50\u8fd8\u662f\u966a\u7740\u7b11\u9053\uff1a\u201c\u662f\uff0c\u662f\uff0c\u662f\uff0c\u6211\u4eec\u5b9e\u5728\u5403\u5f97\u592a\u591a\uff0c\u4f46\u6211\u4eec\u4e5f\u4e0d\u662f\u6ca1\u6709\u5fc3\u7684 \u4eba\uff0c\u5927\u5bb6\u65e9\u5df2\u51c6\u5907\u597d\u51e0\u5339\u5e03\uff0c\u66ff\u53a8\u623f\u91cc\u7684\u5927\u54e5\u4eec\u505a\u68c9\u8884\u4e86\u3002\u201d \u3000\u3000\u8d75\u8001\u5927\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u8138\u8272\u679c\u7136\u5927\u4e3a\u7f13\u548c\uff0c\u53ea\u6325\u4e86\u6325\u624b\uff0c\u5c31\u6709\u4e24\u53ea\u5927\u6d77\u7897\u88ab\u585e\u5165\u90a3\u8001\u5988 \u5b50\u7684\u98df\u76d2\u91cc\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u89c9\u5f97\u53c8\u597d\u6c14\uff0c\u53c8\u597d\u7b11\uff0c\u5fd6\u9053\uff1a\u201c\u8fde\u4e00\u4e2a\u53a8\u5b50\u90fd\u5982\u6b64\u4f5c\u5a01\u4f5c\u798f\uff0c\u4ed6\u82e5\u505a\u4e86\u5b98\uff0c\u90a3\u8fd8 \u5f97\u4e86\uff1f\u201d \u3000\u3000\u53ea\u89c1\u4e00\u623f\u623f\u7684\u98df\u76d2\u90fd\u88ab\u63d0\u8d70\uff0c\u6700\u5f8c\u624d\u8f6e\u5230\u9a6c\u623f\uff0c\u90a3\u9a6c\u8138\u6c49\u5b50\u5fcd\u4f4f\u6c14\uff0c\u62ff\u5230\u81ea\u5df1\u7684\u4e00\u4efd\uff0c \u6380\u8d77\u76d6\u5b50\u4e00\u770b\uff0c\u7acb\u523b\u53d8\u8272\u9053\uff1a\u201c\u623f\u91cc\u4e94\u4e2a\u5927\u4eba\uff0c\u56db\u4e2a\u5b69\u5b50\uff0c\u5c31\u53ea\u6709\u8fd9\u4e00\u9505\u7a00\u7ca5\u9992\u5934\u9ebd\uff1f\u201d \u3000\u3000\u8d75\u8001\u5927\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5c31\u53ea\u8fd9\u9ebd\u591a\u3002\u201d \u3000\u3000\u9a6c\u8138\u6ee1\u5b50\u6c14\u5f97\u624b\u76f4\u53d1\u6296\uff0c\u9053\uff1a\u201c\u59d3\u8d75\u7684\uff0c\u4f60\u2026\u2026\u4f60\u672a\u514d\u592a\u6b3a\u8d1f\u4eba\u4e86\u3002\u201d \u3000\u3000\u8d75\u8001\u5927\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u60f3\u600e\u9ebd\u6837\uff1f\u4e0d\u60f3\u5403\u8fd9\u7897\u996d\u4e86\u9ebd\uff1f\u201d \u3000\u3000\u9a6c\u8138\u6c49\u5b50\u72c2\u543c\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8001\u5b50\u5b81\u53ef\u4e0d\u5403\u8fd9\u7897\u996d\uff0c\u4eca\u5929\u4e5f\u8981\u548c\u4f60\u62da\u4e86\u3002\u201d \u3000\u3000\u4ed6\u62a1\u8d77\u90a3\u98df\u76d2\uff0c\u5c31\u5f80\u8d75\u8001\u5927\u5934\u4e0a\u6454\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u8c01\u77e5\u8fd9\u8d75\u8001\u5927\u7adf\u6709\u4e24\u4e0b\u5b50\uff0c\u8eab\u5b50\u4e00\u8f6c\uff0c\u53cd\u624b\u4e00\u5df4\u638c\u4e86\u8fc7\u53bb\uff0c\u5e95\u4e0b\u8ddf\u7740\u53c8\u662f\u4e00\u811a\uff0c\u5389\u58f0 \u9053\uff1a\u201c\u4f60\u7adf\u6562\u627e\u53a8\u623f\u7684\u9ebb\u70e6\uff0c\u4f60\u6d3b\u5f97\u4e0d\u8010\u70e6\u4e86\u5417\uff1f\u201d \u3000\u3000\u90a3\u9a6c\u8138\u6c49\u5b50\u6328\u4e86\u4e00\u811a\uff0c\u53c8\u722c\u8d77\u6765\uff0c\u8fd8\u60f3\u62da\u547d\uff0c\u4f46\u53a8\u623f\u91cc\u5df2\u6d8c\u51fa\u4e03\u516b\u4e2a\u4eba\u6765\uff0c\u4ed6\u773c\u770b\u5c31\u8981 \u6328\u4e00\u987f\u75db\u6253\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u7b49\u4e86\u534a\u5929\uff0c\u4e5f\u672a\u89c1\u5230\u6709\u4eba\u662f\u4e3a\u82cf\u84c9\u84c9\u5979\u4eec\u9001\u996d\u7684\uff0c\u5fc3\u91cc\u6b63\u5728\u7740\u6025\uff0c\u5fd6\u9053\uff1a\u201c\u5979\u4eec \u83ab\u975e\u6839\u672c\u4e0d\u5728\u8fd9\u5e84\u5b50\u91cc\uff1f\u201d \u3000\u3000\u4ed6\u7b49\u4e86\u534a\u5929\uff0c\u7adf\u767d\u7b49\u4e86\uff0c\u6b63\u60f3\u5230\u522b\u5904\u53bb\u627e\u627e\uff0c\u4f46\u89c1\u5230\u8fd9\u9a6c\u8138\u6c49\u5b50\u88ab\u4eba\u5982\u6b64\u6b3a\u8d1f\uff0c\u5b9e\u5728\u6012 \u6c14\u96be\u5fcd\u3002 \u3000\u3000\u4ed6\u4e5f\u77e5\u9053\u73b0\u5728\u4e0d\u662f\u7ba1\u95f2\u4e8b\u62b1\u4e0d\u5e73\u7684\u65f6\u5019\uff0c\u4f46\u8fd8\u662f\u5fcd\u4e0d\u4f4f\u51b2\u4e86\u8fc7\u53bb\uff0c\u8d75\u8001\u5927\u6b63\u63d0\u7740\u7897\u5927\u7684 \u62f3\u5934\uff0c\u5f80\u90a3\u9a6c\u8138\u6c49\u5b50\u8eab\u4e0a\u62db\u547c\uff0c\u7a81\u89c1\u4e00\u4e2a\u4eba\u51b2\u4e86\u8fc7\u6765\uff0c\u53cd\u624b\u4e00\u4e2a\u8033\u5149\uff0c\u5c31\u5c06\u53a8\u623f\u91cc\u7684\u4e8c\u628a\u624b \u6253\u4e86\u4e2a\u5927\u6597\u3002 \u3000\u3000\u53e6\u5916\u51e0\u4e2a\u4eba\u7acb\u523b\u6012\u543c\u7740\u56f4\u4e86\u4e0a\u53bb\uff0c\u6709\u7684\u624b\u4e0a\u8fd8\u63d0\u7740\u83dc\u5200\uff0c\u4f46\u80e1\u94c1\u82b1\u600e\u4f1a\u5c06\u8fd9\u4e9b\u4eba\u653e\u5728\u773c \u91cc\u3002 \u3000\u3000\u4ed6\u5c31\u7b97\u4e0d\u4fbf\u4f7f\u51fa\u771f\u529f\u592b\u6765\uff0c\u4f46\u53c1\u62f3\u4e24\u811a\uff0c\u4e03\u4e2a\u4eba\u5df2\u88ab\u4ed6\u6253\u5012\u4e86\u56db\u4e2a\uff0c\u8d75\u8001\u5927\u8138\u90fd\u9a87\u767d \u4e86\uff0c\u9053\uff1a\u201c\u4f60\u2026\u2026\u2026\u4f60\u5c0f\u5b50\u4e5f\u662f\u9a6c\u623f\u91cc\u7684\u9ebd\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u51b7\u7b11\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u4ee5\u4e3a\u9a6c\u623f\u91cc\u7684\u4eba\u90fd\u597d\u6b3a\u8d1f\uff1f\u201d \u3000\u3000\u8d75\u8001\u5927\u5ffd\u7136\u6361\u8d77\u628a\u83dc\u5200\uff0c\u5411\u4ed6\u817f\u4e0a\u780d\u4e86\u4e0b\u53bb\uff0c\u8c01\u77e5\u80e1\u94c1\u82b1\u4e00\u62ac\u811a\uff0c\u5c31\u5c06\u4ed6\u7684\u5200\u8e22\u98de\uff0c\u518d \u4e00\u811a\u5c31\u5c06\u4ed6\u7684\u4eba\u8e22\u5012\u3002 \u3000\u3000\u90a3\u9a6c\u8138\u6c49\u5b50\u7acb\u523b\u9a91\u5230\u4ed6\u8eab\u4e0a\uff0c\u7ed9\u4e86\u4ed6\u5341\u6765\u62f3\uff0c\u65b9\u624d\u5a01\u98ce\u4e0d\u53ef\u4e00\u4e16\u7684\u8d75\u8001\u5927\uff0c\u7adf\u88ab\u6253\u5f97\u558a \u8d77\u6551\u547d\u6765\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u6b63\u6253\u5f97\u75db\u5feb\uff0c\u7a81\u542c\u4e00\u4eba\u53f1\u9053\uff1a\u201c\u4f60\u4eec\u8981\u9020\u53cd\u9ebd\uff1f\u5168\u7ed9\u6211\u4f4f\u624b\u3002\u201d \u3000\u3000\u6709\u4e9b\u4eba\u672c\u5df2\u7aef\u7740\u996d\u7897\u5728\u65c1\u8fb9\u770b\u70ed\u95f9\uff0c\u4e00\u542c\u5230\u8fd9\u4eba\u7684\u58f0\u97f3\uff0c\u7acb\u523b\u5168\u90fd\u6e9c\u8d70\u4e86\uff0c\u90a3\u9a6c\u8138\u6c49\u5b50 \u4e5f\u9a87\u5f97\u9762\u65e0\u4eba\u8272\uff0c\u62f3\u5934\u5df2\u63d0\u8d77\u6765\uff0c\u7adf\u4e0d\u6562\u653e\u4e0b\u53bb\u3002 \u3000\u3000\u4f46\u8fd9\u4eba\u7684\u58f0\u97f3\u5374\u662f\u53c8\u5a07\u67d4\uff0c\u53c8\u6e05\u8106\uff0c\u975e\u4f46\u4e00\u70b9\u4e5f\u4e0d\u53ef\u6015\uff0c\u800c\u4e14\u8fd8\u597d\u542c\u5f97\u5f88\uff0c\u5979\u4e0d\u4f46\u58f0\u97f3 \u597d\u542c\uff0c\u4eba\u4e5f\u5f88\u597d\u770b\u3002 \u3000\u3000\u53ea\u89c1\u5979\u67f3\u7709\u674f\u773c\uff0c\u4fcf\u751f\u751f\u7684\u4e00\u5f20\u74dc\u5b50\u8138\uff0c\u6b64\u523b\u867d\u7136\u5728\u751f\u6c14\uff0c\u4f46\u770b\u6765\u4e5f\u8fd8\u662f\u90a3\u9ebd\u59a9\u5a9a\u52a8 \u4eba\u3002 \u3000\u3000\u770b\u5979\u7684\u88c5\u675f\u6253\u626e\uff0c\u548c\u522b\u7684\u4e2b\u5934\u4e5f\u5dee\u4e0d\u591a\u5c11\u3002 \u3000\u3000\u6700\u591a\u4e5f\u53ea\u4e0d\u8fc7\u662f\u6bd4\u8f83\u4f53\u9762\u7684\u4e2b\u5934\u800c\u5df2\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u771f\u4e0d\u61c2\u8fd9\u4e9b\u4eba\u4e3a\u4f55\u4f1a\u5982\u6b64\u6015\u5979\u3002 \u3000\u3000\u5fcd\u4e0d\u4f4f\u591a\u77a7\u5979\u4e24\u773c\uff0c\u8fd9\u5927\u59d1\u5a18\u7684\u773c\u775b\u6b63\u4e5f\u5728\u77aa\u7740\u4ed6\uff0c\u5389\u58f0\u9053\uff1a\u201c\u8fd9\u662f\u600e\u9ebd\u56de\u4e8b\uff1f\u4f60\u4eec\u4e3a \u4ec0\u9ebd\u5728\u6253\u67b6\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u63c9\u4e86\u63c9\u9f3b\u5b50\uff0c\u7b11\u9053\uff1a\u201c\u6211\u4eec\u4e5f\u4e0d\u662f\u60f3\u6253\u67b6\uff0c\u53ea\u4e0d\u8fc7\u8fd9\u8d75\u8001\u5927\u592a\u6b3a\u8d1f\u4eba\u4e86\uff0c\u6211\u4eec\u9a6c \u623f\u91cc\u6ca1\u6709\u4e1c\u897f\u5b5d\u656c\u4ed6\uff0c\u4ed6\u5c31\u627e\u6211\u4eec\u7684\u9ebb\u70e6\uff0c\u4e0d\u7ed9\u6211\u4eec\u5403\u9971\u3002\u201d \u3000\u3000\u8d75\u8001\u5927\u62a2\u7740\u9053\uff1a\u201c\u5e73\u59d1\u5a18\uff0c\u4f60\u5343\u4e07\u4e0d\u80fd\u542c\u4ed6\u7684\uff0c\u4ed6\u2026\u2026\u201d \u3000\u3000\u5e73\u59d1\u5a18\u8138\u4e00\u6c89\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u6211\u542c\u4e0d\u542c\u4ed6\u7684\uff0c\u662f\u6211\u7684\u4e8b\uff0c\u7528\u4e0d\u7740\u4f60\u591a\u5634\uff0c\u6211\u65e9\u5c31\u77e5\u9053\u4f60\u4eec \u53a8\u623f\u91cc\u7684\u4eba\u8d8a\u6765\u8d8a\u4e0d\u50cf\u8bdd\u4e86\u3002\u201d \u3000\u3000\u8d75\u8001\u5927\u54ed\u4e27\u7740\u8138\uff0c\u7adf\u771f\u7684\u4e0d\u6562\u518d\u5f00\u53e3\u3002 \u3000\u3000\u5e73\u59d1\u5a18\u4e0a\u4e0a\u4e0b\u4e0b\uff0c\u53c8\u77a7\u4e86\u80e1\u94c1\u82b1\u51e0\u773c\uff0c\u6de1\u6de1\u9053\uff1a\u201c\u4f60\u7684\u529f\u592b\u5012\u4e0d\u9519\u561b\uff0c\u6211\u600e\u5730\u4e00\u76f4\u6ca1\u89c1 \u8fc7\u4f60\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u7b11\u9053\uff1a\u201c\u5c0f\u4eba\u4eec\u6574\u5929\u8ddf\u9a6c\u6253\u4ea4\u9053\uff0c\u59d1\u5a18\u81ea\u7136\u77a7\u4e0d\u89c1\u7684\u3002\u201d \u3000\u3000\u5e73\u59d1\u5a18\u51b7\u51b7\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u9a6c\u623f\u91cc\u7684\u4eba\u4e5f\u6709\u4f60\u8fd9\u9ebd\u597d\u7684\u8eab\u624b\uff0c\u770b\u6765\u4f60\u5012\u662f\u5927\u624d\u5c0f\u7528\u4e86\u3002\u201d \u3000\u3000\u5979\u5ffd\u7136\u56de\u5934\u77aa\u7740\u90a3\u9a6c\u8138\u6c49\u5b50\uff0c\u5389\u58f0\u9053\uff1a\u201c\u4ed6\u771f\u662f\u9a6c\u623f\u91cc\u7684\u4eba\u9ebd\uff1f\u201d \u3000\u3000\u90a3\u9a6c\u7751\u6c49\u5b50\u5782\u7740\u8138\uff0c\u5077\u5077\u779f\u4e86\u80e1\u94c1\u82b1\u4e00\u773c\uff0c\u80e1\u94c1\u82b1\u8138\u4e0a\u867d\u7136\u8fd8\u5728\u7b11\uff0c\u4f46\u5df2\u51c6\u5907\u6253\u4e00\u573a\u771f \u7684\u4e86\u3002 \u3000\u3000\u53ea\u56e0\u4ed6\u5df2\u770b\u51fa\u8fd9\u5e73\u59d1\u5a18\u957f\u5f97\u867d\u7136\u5f88\u79c0\u6c14\uff0c\u4f46\u773c\u775b\u70af\u70af\u6709\u5149\uff0c\u7adf\u662f\u4e2a\u5185\u5916\u517c\u4fee\u7684\u9ad8\u624b\uff0c\u770b \u6765\u5f88\u4e0d\u597d\u5bf9\u4ed8\u7684\u3002 \u3000\u3000\u8c01\u77e5\u90a3\u9a6c\u8138\u6c49\u5b50\u5c45\u7136\u70b9\u4e86\u5934\uff0c\u966a\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4ed6\u5c31\u662f\u5c0f\u4eba\u7684\u5927\u8205\u5b50\uff0c\u8fd9\u51e0\u5929\u624d\u6765\u5e2e\u5fd9 \u7684\u3002\u201d \u3000\u3000\u5e73\u59d1\u5a18\u76ee\u5149\u56de\u5230\u80e1\u94c1\u82b1\u8eab\u4e0a\uff0c\u8138\u8272\u4e5f\u5927\u4e3a\u7f13\u548c\uff0c\u9053\uff1a\u201c\u4f60\u6765\u5e2e\u5fd9\u53ef\u4ee5\uff0c\u4f46\u8981\u5e2e\u4ed6\u6253\u67b6\u5374 \u4e0d\u884c\uff0c\u77e5\u9053\u9ebd\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u6697\u4e2d\u677e\u4e86\u53e3\u6c14\uff0c\u7b11\u9053\uff1a\u201c\u662f\uff0c\u53ea\u8981\u59d1\u5a18\u5429\u5490\uff0c\u5c0f\u4eba\u4e00\u5b9a\u542c\u8bdd\u3002\u201d \u3000\u3000\u5e73\u59d1\u5a18\u4f3c\u7b11\u975e\u7b11\u5730\u77a7\u7740\u4ed6\uff0c\u60a0\u7136\u9053\uff1a\u201c\u770b\u4f60\u7684\u8eab\u624b\uff0c\u5728\u9a6c\u623f\u91cc\u505a\u672a\u514d\u592a\u53ef\u60dc\u4e86\uff0c\u8fc7\u4e24\u5929 \u6765\u627e\u6211\uff0c\u6211\u60f3\u6cd5\u5b50\u66ff\u4f60\u5b89\u63d2\u4e2a\u597d\u4f4d\u5b50\u3002\u201d \u3000\u3000\u90a3\u9a6c\u8138\u6c49\u5b50\u63a8\u7740\u80e1\u94c1\u82b1\uff0c\u9053\uff1a\u201c\u5e73\u59d1\u5a18\u5728\u5c11\u5e84\u4e3b\u592b\u4eba\u9762\u524d\u8bf4\u8bdd\uff0c\u5c06\u6765\u53ea\u8981\u5e73\u59d1\u5a18\u80af\u683d\u57f9 \u4f60\uff0c\u4f60\u5c31\u7b97\u8d70\u8fd0\u4e86\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u53ea\u6709\u966a\u7b11\u9053\uff1a\u201c\u591a\u8c22\u5e73\u59d1\u5a18\uff0c\u8fc7\u4e24\u5929\u6211\u4e00\u5b9a\u53bb\u62dc\u8c12\u5e73\u59d1\u5a18\u3002\u201d \u3000\u3000\u4ed6\u77a7\u7740\u8fd9\u5e73\u59d1\u5a18\u7ea4\u7ec6\u7684\u8170\u80a2\uff0c\u7b14\u76f4\u7684\u817f\uff0c\u548c\u90a3\u53cc\u53c8\u767d\u53c8\u5ae9\u7684\u5c0f\u624b\uff0c\u5fc3\u91cc\u5012\u5b9e\u5728\u5f88\u60f3\u53bb \u201c\u62dc\u671b\u62dc\u671b\u201d\u5979 \u7b2c\u5341\u56db\u7ae0\u3000\u6069\u5c06\u4ec7\u62a5>> \u53e4\u9f99\u300a\u695a\u7559\u9999\u7cfb\u5217\xb7\u753b\u7709\u9e1f\u300b \u7b2c\u5341\u56db\u7ae0\u3000\u6069\u5c06\u4ec7\u62a5 \u3000\u3000\u5e26\u7740\u82b1\u9999\u7684\u5fae\u98ce\u5439\u5728\u4ed6\u8eab\u4e0a\uff0c\u5e73\u59d1\u5a18\u8fd8\u4e0d\u65f6\u5411\u4ed6\u56de\u7738\u4e00\u7b11\uff0c\u4ed6\u5fc3\u91cc\u5b9e\u5728\u6109\u5feb\u6781\u4e86\uff0c\u4e5f\u5f97 \u610f\u6781\u4e86\u3002 \u3000\u3000\u695a\u7559\u9999\u627e\u4e86\u51e0\u4e2a\u6708\u90fd\u6ca1\u6709\u627e\u5230\u7684\u4eba\uff0c\u73b0\u5728\u4ed6\u5374\u4e0d\u8d39\u5439\u7070\u4e4b\u529b\u5c31\u53ef\u4ee5\u627e\u5230\uff0c\u7136\u5f8c\uff0c\u4ed6\u5c31\u53ef \u4ee5\u5e26\u7740\u82cf\u84c9\u84c9\u3001\u674e\u7ea2\u8896\u3001\u5b8b\u751c\u513f\u548c\u9ed1\u73cd\u73e0\u56db\u4e2a\u4eba\u53bb\u5e2e\u695a\u7559\u9999\u7684\u5fd9\uff0c\u4ee5\u4ed6\u4eec\u516d\u4e2a\u4eba\u4e4b\u529b\uff0c\u8fd8\u6015 \u4e0d\u80fd\u5c06\u8fd9\u201c\u62e5\u7fe0\u5c71\u5e84\u201d\u95f9\u4e2a\u5929\u7ffb\u5730\u8986\uff1f\u201c\u5230\u4e86\u90a3\u65f6\uff0c\u90a3\u8001\u81ed\u866b\u8fd8\u80fd\u4e0d\u4f69\u670d\u6211\u9ebd\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u53ea\u89c9\u5168\u8eab\u8f7b\u98d8\u98d8\u7684\uff0c\u4e00\u9897\u5fc3\u90fd\u4f46\u8981\u98de\u4e0a\u4e86\u5929\u3002 \u3000\u3000\u4ed6\u76ee\u5149\u79fb\u5230\u5e73\u59d1\u5a18\u90a3\u6d51\u5706\u7684\uff0c\u4e30\u6ee1\u7684\uff0c\u88ab\u8584\u7ef8\u88e4\u5b50\u7d27\u7ef7\u7684\u81c0\u90e8\uff0c\u53c8\u4e0d\u7981\u5077\u5077\u7684\u7b11\u4e86\u8d77 \u6765\u3002 \u3000\u3000\u4ed6\u60f3\uff1a\u201c\u5230\u4e86\u90a3\u65f6\uff0c\u6211\u4e00\u5b9a\u8981\u5728\u4e0a\u9762\u8f7b\u8f7b\u626d\u4e00\u628a\uff0c\u8fd9\u591a\u60c5\u7684\u5927\u4e2b\u5934\u8fd8\u4e0d\u7acb\u523b\u5c31\u4f1a\u6251\u5230\u6211 \u6000\u91cc\u6765\u3002\u201d \u3000\u3000\u4ed6\u4e0d\u4f46\u5fc3\u91cc\u75d2\u75d2\u7684\uff0c\u624b\u4e0a\u4e5f\u5728\u53d1\u75ff\uff0c\u5df2\u8d70\u8fc7\u4e9b\u4ec0\u9ebd\u5730\u65b9\uff0c\u5df2\u8d70\u5230\u90a3\u91cc\uff0c\u4ed6\u6839\u672c\u5c31\u8fde\u77a7\u90fd \u6ca1\u6709\u77a7\u4e00\u773c\u3002 \u3000\u3000\u5ffd\u7136\u59d1\u5a18\u9053\uff1a\u201c\u5230\u4e86\uff0c\u4f60\u8fd8\u5f80\u524d\u8d70\u5e72\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u8fd9\u624d\u56de\u8fc7\u795e\u6765\uff0c\u966a\u7b11\u9053\uff1a\u201c\u5c31\u5728\u8fd9\u91cc\u9ebd\uff1f\u201d \u3000\u3000\u5e73\u59d1\u5a18\u9053\uff1a\u201c\u55ef\uff01\u5c31\u5728\u8fd9\u5c4b\u5b50\u91cc\u3002\u201d \u3000\u3000\u53ea\u89c1\u73e0\u4f4e\u5782\uff0c\u5c4b\u5b50\u91cc\u4e5f\u662f\u9759\u6084\u6084\u7684\uff0c\u4e0d\u65f6\u6709\u4e00\u80a1\u6de1\u6de1\u7684\u9999\u6c14\u98d8\u8fc7\u6765\uff0c\u4e5f\u4e0d\u77e5\u662f\u82b1\u9999\uff0c\u8fd8 \u662f\u4eba\u9999\uff1f\u5e73\u59d1\u5a18\u201c\u5657\u55e4\u201d\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u8fd8\u53d1\u4ec0\u9ebd\u5446\uff0c\u5feb\u5c06\u98df\u76d2\u4ea4\u7ed9\u6211\u5427\uff01\u201d \u3000\u3000\u5979\u4e00\u53ea\u624b\u53bb\u63a5\u80e1\u94c1\u82b1\u63d0\u7740\u7684\u98df\u5323\uff0c\u4e00\u53ea\u624b\u5374\u642d\u4e0a\u4e86\u80e1\u94c1\u82b1\u80a9\u5934\uff0c\u6084\u6084\u9053\uff1a\u201c\u4eca\u5929\u665a\u4e0a\u6765 \u627e\u6211\uff0c\u77e5\u9053\u9ebd\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u5fc3\u91cc\u867d\u6b22\u559c\uff0c\u5374\u53c8\u4e0d\u7981\u89c9\u5f97\u6709\u4e9b\u60cb\u60dc\uff0c\u56e0\u4e3a\u4ed6\u5df2\u4e0d\u5f97\u4e0d\u8f9c\u8d1f\u8fd9\u591a\u60c5\u59d1\u5a18\u7684\u597d\u610f \u4e86\u3002 \u3000\u3000\u4ed6\u6b63\u60f3\u8bf4\u4e24\u53e5\u5a49\u8f6c\u7684\u8bdd\u4e4b\u5f8c\u518d\u52a8\u624b\uff0c\u8c01\u77e5\u2026\u2026 \u3000\u3000\u8c01\u77e5\u8fd9\u591a\u60c5\u7684\u5e73\u59d1\u5a18\u7adf\u5148\u52a8\u624b\u4e86\u3002 \u3000\u3000\u5979\u7684\u624b\u5ffd\u7136\u81ea\u80e1\u94c1\u82b1\u7684\u80a9\u5934\u6ed1\u4e0b\u53bb\uff0c\u4e00\u8fde\u70b9\u4e86\u4ed6\u5de6\u81c2\u56db\u5904\u7a74\u9053\uff0c\u4ed6\u7684\u53f3\u624b\u8fd8\u63d0\u7740\u90a3\u98df \u5323\uff0c\u8fde\u52a8\u90fd\u4e0d\u80fd\u52a8\u3002 \u3000\u3000\u7b49\u4ed6\u7529\u5f00\u8fd9\u98df\u76d2\u65f6\uff0c\u53f3\u8155\u7684\u8109\u95e8\u4e5f\u88ab\u6263\u4f4f\u3002 \u3000\u3000\u53ea\u542c\u5e73\u59d1\u5a18\u60a0\u7136\u9053\uff1a\u201c\u591a\u60c5\u7684\u5c0f\u4f19\u5b50\uff0c\u4f60\u867d\u5bf9\u6211\u4e0d\u9519\uff0c\u6211\u5374\u4e0d\u80fd\u4e0d\u8f9c\u8d1f\u4f60\u7684\u4e00\u756a\u597d\u610f \u4e86\u3002\u201d \u3000\u3000\u5979\u53cd\u624b\u4e00\u638c\uff0c\u5c06\u80e1\u94c1\u82b1\u6253\u5012\uff0c\u8fd8\u5728\u80e1\u94c1\u82b1\u5c41\u80a1\u4e0a\u634f\u4e86\u4e00\u628a--\u80e1\u94c1\u82b1\u7b80\u76f4\u8fde\u809a\u5b50\u90fd\u5feb\u6c14\u7834 \u4e86\u3002 \u3000\u3000\u6b64\u65f6\u4ed6\u975e\u4f46\u518d\u4e5f\u7b11\u4e0d\u51fa\u6765\uff0c\u7b80\u76f4\u8fde\u54ed\u90fd\u54ed\u4e0d\u51fa\u3002 \u3000\u3000\u5e73\u59d1\u5a18\u62cd\u4e86\u62cd\u624b\uff0c\u9053\uff1a\u201c\u6765\u4eba\u5440\uff01\u201d \u3000\u3000\u5c4b\u5b50\u91cc\u7acb\u523b\u8d70\u51fa\u4e86\u51e0\u4e2a\u9752\u8863\u5782\u9aeb\u7684\u7ae5\u5b50\u5e73\u59d1\u5a18\u9053\uff1a\u201c\u8fd9\u8fd9\u62ac\u8fdb\u53bb\uff0c\u7528\u725b\u7b4b\u56f0\u4e0a\uff0c\u518d\u53bb\u56de \u7980\u5c11\u5e84\u4e3b\u592b\u4eba\uff0c\u5c31\u8bf4\u5979\u8981\u6211\u7559\u610f\u5bfb\u627e\u7684\u4eba\uff0c\u73b0\u5728\u5df2\u7ecf\u627e\u5230\u4e86\u3002\u201d \u3000\u3000\u9752\u8863\u7ae5\u5b50\u8eac\u8eab\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u5e73\u59d1\u5a18\u9053\uff1a\u201c\u8fd8\u6709\uff0c\u53eb\u5f20\u7ba1\u5bb6\u5230\u9a6c\u623f\u53bb\uff0c\u5c06\u9a6c\u8138\u738b\u53c1\u5148\u6253\u4e94\u5341\u677f\u5b50\uff0c\u518d\u9001\u5230\u9ec4\u7ba1\u5bb6\u90a3 \u91cc\uff0c\u7ed9\u4ed6\u4e00\u4e2a\u6b3a\u4e0a\u901a\u654c\u4e4b\u7f6a\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u6ee1\u5634\u90fd\u662f\u82e6\u6c34\uff0c\u5fcd\u4e0d\u4f4f\u9053\uff1a\u201c\u4f60\u2026\u2026\u2026\u4f60\u96be\u9053\u65e9\u5df2\u77e5\u9053\u6211\u662f\u8c01\u4e86\uff1f\u201d \u3000\u3000\u5e73\u59d1\u5a18\u5ae3\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u9f0e\u9f0e\u5927\u540d\u7684\u80e1\u94c1\u82b1\u80e1\u5927\u4fa0\uff0c\u8fd8\u6709\u8c01\u4e0d\u77e5\u9053\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u4f46\u4f60\u2026\u2026\u2026\u201d \u3000\u3000\u5e73\u59d1\u5a18\u9053\uff1a\u201c\u5c11\u5e84\u4e3b\u592b\u4eba\u7b97\u51c6\u4e86\u4f60\u8981\u6765\u627e\u90a3\u56db\u4f4d\u59d1\u5a18\uff0c\u6240\u3001\u5c31\u8981\u6211\u7559\u610f\u4f60\uff0c\u6211\u60f3\uff0c\u73b0\u5728 \u6b63\u662f\u5403\u65e9\u996d\u7684\u65f6\u5019\uff0c\u4f60\u4e5f\u8bb8\u5c31\u4f1a\u4ece\u201c\u5403\u996d\u201d\u8fd9\u7ebf\u7d22\u4e0a\u7740\u624b\uff0c\u56e0\u4e3a\u9664\u6b64\u4e4b\u5916\uff0c\u4f60\u5b9e\u5728\u6ca1\u6709\u4ec0\u9ebd \u522b\u7684\u7ebf\u7d22\u3002\u201d\u5979\u53c8\u7b11\u4e86\u7b11\uff0c\u6309\u7740\u9053\uff1a\u201c\u82e5\u975e\u5982\u6b64\uff0c\u6211\u600e\u4f1a\u5982\u6b64\u8f7b\u6613\u5c31\u4fe1\u4efb\u4e86\u4f60\u5462\uff1f\u8fd9\u4e5f\u8bb8\u662f \u56e0\u4e3a\u5929\u4e0b\u7684\u7537\u4eba\u603b\u6709\u8fd9\u79cd\u6bdb\u75c5\uff0c\u603b\u4ee5\u4e3a\u81ea\u5df1\u53c1\u8a00\u4e24\u8bed\uff0c\u5c31\u53ef\u4ee5\u5c06\u5973\u4eba\u9a97\u8fc7\u4e86\uff0c\u5374\u4e0d\u77e5\u5973\u4eba\u8981 \u9a97\u7537\u4eba\uff0c\u5b9e\u5728\u6bd4\u7537\u4eba\u9a97\u5973\u4eba\u5bb9\u6613\u5f97\u591a\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u5583\u5583\u9053\uff1a\u201c\u6211\u660e\u660e\u65e9\u5df2\u61c2\u5f97\u8fd9\u9053\u7406\u4e3a\u4ec0\u9ebd\u8fd8\u4f1a\u5982\u6b64\u8f7b\u6613\u5c31\u4fe1\u4efb\u4e86 \u4f60\u5462\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u4ee5\u624b\u6307\u634f\u7740\u5251\u5c16\uff0c\u4ee5\u5251\u67c4\u653b\u51fb\u3002 \u3000\u3000\u5251\u5c16\u662f\u63e1\u4e0d\u4f4f\u7684\uff0c\u975e\u4f46\u96be\u4ee5\u628a\u63e1\uff0c\u4e5f\u4f7f\u4e0d\u4e0a\u529b\uff0c\u4ee5\u5251\u67c4\u6765\u653b\u51fb\uff0c\u81ea\u7136\u66f4\u8fd8\u4e0d\u53ca\u5251\u5c16\u4fbf\u6377 \u950b\u5229\u3002 \u3000\u3000\u666e\u5929\u4e4b\u4e0b\uff0c\u7edd\u6ca1\u6709\u4e00\u4e2a\u4eba\u4f1a\u7528\u8fd9\u79cd\u59ff\u52bf\u6765\u63e1\u5251\uff0c\u9664\u975e\u4ed6\u610f\u5b58\u8f7b\u4fae\uff0c\u6839\u672c\u672a\u5c06\u5bf9\u624b\u653e\u5728\u773c \u91cc\u3002 \u3000\u3000\u4f46\u73b0\u5728\uff0c\u695a\u7559\u9999\u6240\u9762\u5bf9\u7684\u5374\u662f\u65e0\u53ef\u6bd4\u62df\u7684\u53ef\u6015\u5bf9\u624b\uff0c\u800c\u4e14\u5251\u9635\u53d1\u52a8\u5f8c\u8fd8\u4e0d\u5230\u76cf\u8336\u529f\u592b\uff0c \u4ed6\u5df2\u5c61\u7ecf\u9669\u62db\uff0c\u6709\u4e24\u6b21\u5bf9\u624b\u7684\u5251\u950b\u7b80\u76f4\u5c31\u662f\u8d34\u7740\u4ed6\u7684\u808b\u9aa8\u699b\u8fc7\u53bb\u7684\u3002\u4ed6\u7adf\u8fd8\u662f\u4fdd\u6301\u8fd9\u7b28\u62d9\u7684 \u63e1\u5251\u59ff\u52bf\u4e0d\u53d8\u3002 \u3000\u3000\u4ed6\u8fd9\u662f\u4e3a\u4e86\u4ec0\u9ebd\u5462\uff1f\u8c01\u4e5f\u731c\u4e0d\u900f\u4ed6\u7684\u7528\u610f\u3002 \u3000\u3000\u522b\u4eba\u867d\u7136\u660e\u77e5\u695a\u7559\u9999\u7edd\u4e0d\u4f1a\u505a\u6ca1\u6709\u610f\u4e49\u7684\u4e8b\uff0c\u4f46\u8c01\u90fd\u6ca1\u6709\u53bb\u4ed4\u7ec6\u601d\u7d22\u4ed6\u7684\u7528\u610f\uff0c\u66f4\u6ca1\u6709 \u53bb\u95ee\u3002 \u3000\u3000\u56e0\u4e3a\u73b0\u5728\u65e2\u4e0d\u662f\u7528\u5fc3\u601d\u7d22\u7684\u65f6\u5019\uff0c\u4e5f\u4e0d\u662f\u7528\u5634\u95ee\u7684\u65f6\u5019\u3002 \u3000\u3000\u73b0\u5728\u662f\u7528\u5251\u7684\u65f6\u5019\u3002 \u3000\u3000\u5251\u5149\u7684\u6d41\u52a8\u5982\u7d2b\u8679\u95ea\u7535\uff0c\u5251\u5f0f\u7684\u53d8\u5316\u66f4\u662f\u77ac\u606f\u4e07\u5b50\uff0c\u8fd9\u5176\u95f4\u6839\u672c\u5c31\u4e0d\u5bb9\u4eba\u6709\u601d\u7d22\u7684\u673a \u4f1a\u3002 \u3000\u3000\u6bcf\u4e2a\u4eba\u6240\u6709\u7684\u7cbe\u795e\uff0c\u6240\u6709\u7684\u529b\u91cf\uff0c\u5168\u90fd\u5df2\u8d2f\u6ce8\u5728\u624b\u4e2d\u7684\u4e00\u67c4\u5251\u4e0a\uff0c\u6bcf\u4e2a\u4eba\u7684\u5fc3\u4e0e\u521b\u5df2\u5408 \u800c\u4e3a\u4e00\u4f53\u3002\u90a3\u516d\u67c4\u957f\u77ed\u4e0d\u4e00\uff0c\u5f62\u5f0f\u5404\u5f02\u7684\u5251\uff0c\u5df2\u5316\u4e3a\u4e00\u67c4\uff0c\u516d\u4e2a\u4eba\u7684\u7cbe\u3001\u6c14\u3001\u795e\u3001\u529b\uff0c\u4e5f\u90fd \u5df2\u6eb6\u4e3a\u4e00\u4f53\u3002 \u3000\u3000\u5251\u7f51\u5df2\u7f16\u7ec7\u5f97\u66f4\u5bc6\uff0c\u5df2\u6e10\u6e10\u5f00\u59cb\u6536\u7f29\uff0c\u695a\u7559\u9999\u5c31\u662f\u8fd9\u7f51\u4e2d\u7684\u9c7c--\u4ed6\u53c8\u4e00\u6b21\u843d\u5165\u7f51\u4e2d\u3002 \u3000\u3000\u8fd9\u4e00\u6b21\uff0c\u4ed6\u4e1a\u5df2\u65e0\u8def\u53ef\u8d70\u3002 \u3000\u3000\u8fdc\u8fdc\u671b\u53bb\uff0c\u53ea\u89c1\u5251\u6c14\u5343\u5e7b\uff0c\u5982\u5341\u5f69\u5b9d\u5e62\uff0c\u68ee\u4e25\u7684\u5251\u6c14\u4f7f\u5ba4\u5185\u7684\u6e29\u5ea6\u9aa4\u7136\u964d\u4f4e\uff0c\u5ffd\u7136\u53d8\u4e3a \u5bd2\u51ac\u3002 \u3000\u3000\u67f3\u65e0\u7709\u7684\u9762\u8272\u4e5f\u4e00\u76f4\u5728\u53d8\u5e7b\u4e0d\u505c\uff0c\u76f4\u5230\u73b0\u5728\uff0c\u5979\u624d\u9732\u51fa\u4e00\u4e1d\u5fae\u7b11\uff0c\u56e0\u4e3a\u5979\u5df2\u770b\u51fa\u695a\u7559\u9999 \u662f\u65e0\u8bba\u5982\u4f55\u4e5f\u51b2\u4e0d\u51fa\u8fd9\u5251\u9635\u4e86\u3002 \u3000\u3000\u8fd9\u5251\u9635\u7684\u5a01\u529b\u5b9e\u662f\u65e0\u671b\u4e0d\u6467\uff0c\u65e0\u61c8\u53ef\u51fb\u3002 \u3000\u3000\u751a\u81f3\u8fde\u90a3\u5782\u6b7b\u7684\u8001\u4eba\u76ee\u4e2d\uff0c\u90fd\u5df2\u9732\u51fa\u4e86\u6fc0\u52a8\u4e4b\u8272\uff0c\u8fd9\u903c\u4eba\u7684\u5251\u6c14\uff0c\u4f3c\u5df2\u6fc0\u53d1\u4e86\u4ed6\u751f\u547d\u4e2d \u6700\u5f8c\u4e00\u4e1d\u6d3b\u529b\u3002 \u3000\u3000\u4ed6\u7684\u5e73\u5e38\u5728\u98a4\u6296\u7740\u7684\u3001\u67af\u7626\u7684\u624b\u6307\uff0c\u4e0d\u4f4f\u7684\u4f38\u5c48\uff0c\u4ed6\u4f3c\u4e4e\u4e5f\u60f3\u594b\u8eab\u800c\u8d77\uff0c\u91cd\u63e1\u5251\u67c4\uff0c\u6295 \u8eab\u65bc\u6218\u5f79\u4e4b\u4e2d\u3002 \u3000\u3000\u4ed6\u4f3c\u5df2\u4e0d\u7518\u5750\u89c6\u3002 \u3000\u3000\u8fd9\u65f6\u5251\u7f51\u6536\u7f29\u5f97\u66f4\u7d27\uff0c\u695a\u7559\u9999\u8eab\u4e0a\u7684\u8863\u670d\u90fd\u88ab\u5251\u6c14\u6495\u5f97\u7c89\u788e\uff0c\u4ed6\u51e0\u4e4e\u5df2\u5b8c\u6ca1\u6709\u56de\u624b\u4e4b \u529b\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u4e00\u4e2a\u9752\u8863\u5782\u9aeb\u7ae5\u5b50\uff0c\u6cbf\u7740\u5899\u89d2\u6084\u6084\u8d70\u4e86\u8fdb\u6765\uff0c\u5728\u67f3\u65e0\u7709\u8033\u7554\u8f7b\u8f7b\u8bf4\u4e86\u51e0\u53e5 \u8bdd\u3002 \u3000\u3000\u67f3\u65e0\u7709\u73b0\u5728\u77e5\u9053\uff0c\u80e1\u94c1\u82b1\u4e5f\u5df2\u843d\u5165\u7f51\u4e2d\u4e86\u3002 \u3000\u3000\u65bc\u662f\u5979\u7b11\u5f97\u66f4\u6109\u5feb\uff0c\u5728\u5f69\u971e\u822c\u6d41\u52a8\u4e0d\u606f\u7684\u5251\u5149\u4e2d\uff0c\u5b83\u7684\u7b11\u5bb9\u770b\u6765\u662f\u90a3\u9ebd\u6b8b\u9177\uff0c\u5374\u53c8\u662f\u90a3 \u9ebd\u7f8e\u4e3d\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u6d41\u52a8\u7684\u5251\u6c14\u5ffd\u7136\u51dd\u7ec3\uff0c\u6ee1\u5929\u5251\u6c14\u5df2\u51dd\u7ec3\u4e3a\u516d\u9053\u98de\u8679\uff0c\u4ea4\u9519\u7740\u5411\u695a\u7559\u9999\u526a\u4e0b\u3002 \u3000\u3000\u5251\u9635\u7684\u5a01\u529b\uff0c\u5df2\u5148\u5c06\u695a\u7559\u9999\u903c\u5165\u6b7b\u89d2\u3002 \u3000\u3000\u8fd9\u4e00\u5251\u524c\u51fa\u65f6\uff0c\u695a\u7559\u9999\u5b9e\u5df2\u5230\u4e86\u5c71\u7a77\u6c34\u5c3d\u7684\u65f6\u5019\uff0c\u4ed6\u65e0\u8bba\u7528\u4ec0\u9ebd\u8eab\u6cd5\u95ea\u907f\uff0c\u90fd\u96be\u514d\u8981\u88ab \u523a\u7a7f\u80f8\u819b\u3002 \u3000\u3000\u666e\u5929\u4e4b\u4e0b\uff0c\u5b9e\u5df2\u7edd\u65e0\u4e00\u4eba\u80fd\u5c06\u8fd9\u516d\u67c4\u5251\u5168\u90fd\u8eb2\u5f00\u7684\u3002 \u3000\u3000\u7a81\u7136\u95f4\uff0c\u53ea\u542c\u201c\u545b\u201d\u7684\u4e00\u58f0\u9f99\u541f\u3002 \u3000\u3000\u7136\u5f8c\uff0c\u5251\u6c14\u98de\u8679\u7adf\u5168\u90fd\u5947\u8ff9\u822c\u6d88\u5931\u4e0d\u89c1\uff0c\u674e\u7389\u51fd\u548c\u90a3\u4e94\u4e2a\u9ed1\u8863\u8001\u4eba\u7684\u8eab\u5b50\uff0c\u7adf\u50cf\u662f\u5ffd\u7136 \u5728\u7a7a\u6c14\u4e2d\u51dd\u7ed3\u4f4f\u4e86\u3002 \u3000\u3000\u67f3\u65e0\u7709\u8138\u4e0a\u7684\u7b11\u5bb9\u4e5f\u51dd\u7ed3\u4f4f\u4e86\u3002 \u3000\u3000\u5979\u53d1\u73b0\u695a\u7559\u9999\u7684\u8eab\u5f62\u5df2\u6b3a\u4eba\u4e86\u674e\u7389\u51fd\u80c1\u4e0b\uff0c\u5de6\u638c\u6309\u5728\u674e\u7389\u51fd\u7684\u80f8\u819b\u4e0a\uff0c\u53f3\u624b\u5374\u634f\u4f4f\u4e86\u4ed6 \u7684\u624b\u8155\u3002 \u3000\u3000\u695a\u7559\u9999\u638c\u4e2d\u7684\u5251\u5df2\u4e0d\u5728\uff0c\u4ed6\u7adf\u4ee5\u674e\u7389\u51fd\u638c\u4e2d\u7684\u5251\uff0c\u67b6\u4f4f\u4e86\u90a3\u6e05\u77bf\u9880\u957f\u7684\u9ed1\u8863\u8001\u4eba\u638c\u4e2d\u7684 \u5251\u3002 \u3000\u3000\u7b2c\u4e8c\u4e2a\u67af\u7626\u77ee\u5c0f\u7684\u9ed1\u8863\u8001\u4eba\u5de6\u53f3\u53cc\u624b\u4e2d\uff0c\u7adf\u5404\u63e1\u7740\u4e00\u67c4\u5251--\u695a\u7559\u9999\u7684\u5251\u4e5f\u4e0d\u77e5\u600e\u5730\uff0c\u7adf \u5230\u4e86\u8fd9\u8001\u4eba\u624b\u91cc\u3002 \u3000\u3000\u8fd9\u5251\u9635\u7684\u6bcf\u4e00\u4e2a\u53d8\u5316\uff0c\u6bcf\u4e00\u62db\u51fa\u624b\uff0c\u90fd\u7ecf\u8fc7\u6781\u7cbe\u5bc6\u7684\u8ba1\u7b97\uff0c\u516d\u67c4\u5251\u914d\u5408\u5f97\u6b63\u662f\u6ef4\u6c34\u4e0d \u504f\uff0c\u5929\u8863\u65e0\u7f1d\u3002 \u3000\u3000\u6241\u5c11\u4e86\u4e00\u67c4\u5251\uff0c\u8fd9\u5251\u9635\u4fbf\u6709\u4e86\u6f0f\u6d1e\uff0c\u751a\u81f3\u6839\u672c\u4e0d\u80fd\u53d1\u52a8\uff0c\u82e5\u591a\u4e86\u4e00\u67c4\u5251\uff0c\u4e5f\u6210\u4e86\u591a\u9980\u7684 \u86c7\u8db3\u3002 \u3000\u3000\u6b64\u523b\uff0c\u8fd9\u5251\u9635\u4e2d\u6b63\u5df2\u591a\u4e86\u4e00\u67c4\u5251\uff0c\u65bc\u662f\u5176\u9980\u53c1\u67c4\u5251\u7684\u53bb\u52bf\uff0c\u5c31\u5168\u90fd\u88ab\u8fd9\u67c4\u591a\u9980\u7684\u5251\u62e6 \u963b\u3002 \u3000\u3000\u4ed6\u4eec\u8fd9\u4e00\u5251\u65e2\u5df2\u88ab\u62e6\u963b\uff0c\u7b2c\u4e8c\u5251\u5c31\u518d\u4e5f\u4e0d\u80fd\u53d1\u51fa\uff0c\u56e0\u4e3a\u695a\u7559\u9999\u7684\u624b\u638c\uff0c\u5df2\u62cd\u4e0a\u4e86\u674e\u7389\u51fd \u7684\u8981\u5bb3\u3002 \u3000\u3000\u4e3a\u4e86\u674e\u7389\u51fd\u7684\u5b89\u5168\uff0c\u4ed6\u4eec\u8fde\u52a8\u90fd\u4e0d\u80fd\u52a8\u3002 \u3000\u3000\u67f3\u65e0\u7709\u638c\u5fc3\u4e0d\u89c9\u5df2\u6c81\u51fa\u4e86\u51b7\u6c57\u3002 \u3000\u3000\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\u695a\u7559\u9999\u5ffd\u7136\u5411\u90a3\u67af\u7626\u77ee\u5c0f\u7684\u9ed1\u8863\u8001\u4eba\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5728\u4e0b\u4e8c\u5341\u5e74\u524d\u4fbf \u5df2\u4e45\u4ef0\u51cc\u8001\u524d\u8f88\u201c\u51fa\u624b\u53cc\u7edd\uff0c\u9e33\u9e2f\u795e\u5251\u201d\u72ec\u6b65\u5929\u4e0b\uff0c\u4e0d\u60f3\u4eca\u65e5\u7adf\u80fd\u548c\u51cc\u8001\u524d\u8f88\u5171\u5904\u4e00\u5802\uff0c\u5b9e \u662f\u4e0d\u80dc\u8363\u5ba0\u4e4b\u81f3\u3002\u201d \u3000\u3000\u90a3\u9ed1\u8863\u8001\u4eba\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u83ab\u975e\u65e9\u5df2\u8ba4\u51fa\u4e86\u6211\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u5fae\u7b11\u9053\uff1a\u201c\u5728\u4e0b\u65b9\u624d\u89c1\u5230\u4e94\u4f4d\u524d\u8f88\u65f6\uff0c\u53ea\u4e0d\u8fc7\u8ba4\u51fa\u4e86\u4e00\u4e2a\u4eba\u6765\uff0c\u4f46\u5374\u5e76\u975e\u662f\u51cc\u8001 \u524d\u8f88\u3002\u201d \u3000\u3000\u90a3\u9ed1\u8863\u8001\u4eba\u9053\uff1a\u201c\u662f\u8c01\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u76ee\u5149\u8f6c\u5411\u90a3\u624b\u6301\u6728\u5251\u7684\u9ed1\u8863\u4eba\uff0c\u9053\uff1a\u201c\u5728\u4e0b\u90a3\u65f6\u53ea\u8ba4\u51fa\u8fd9\u4f4d\u524d\u8f88\u5fc5\u662f\u201d\u7389\u5251\u201c\u8427 \u77f3\u8427\u5927\u4fa0\u3002\u201d\u4ed6\u542b\u7b11\u6309\u7740\u9053\uff1a\u201c\u8427\u6c0f\u7389\u5251\uff0c\u4e43\u6b66\u6797\u4e2d\u72ec\u4e00\u65e0\u4e8c\u7684\u540d\u5251\uff0c\u8427\u5927\u4fa0\u4e5f\u662f\u6c5f\u6e56\u4e2d\u5c48 \u6307\u53ef\u6570\u7684\u540d\u5251\u5ba2\uff0c\u8427\u5927\u4fa0\uff0c\u65e2\u4e0d\u613f\u4ee5\u771f\u9762\u76ee\u793a\u4eba\uff0c\u4e5f\u552f\u6050\u5728\u4e0b\u81ea\u5175\u5203\u4e0a\u770b\u51fa\u8427\u5927\u4fa0\u7684\u8eab\u4efd\u6765 \u5386\uff0c\u662f\u4ee5\u624d\u524a\u6728\u7bc7\u5251\uff0c\u907f\u4eba\u773c\u76ee\u3002\u201d \u3000\u3000\u201c\u7389\u5251\u201d\u8427\u77f3\u9ed8\u7136\u534a\u664c\uff0c\u5f90\u5f90\u6380\u5f00\u8986\u9762\u9ed1\u5dfe\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u6b63\u662f\u8427\u77f3\uff0c\u4f60\u65e2\u7136\u77e5\u9053\u6211 \u7684\u6765\u5386\uff0c\u4fbf\u5df2\u8be5\u77e5\u9053\u6211\u548c\u89c2\u9c7c\u8001\u4eba\u7684\u4ea4\u60c5\uff0c\u522b\u7684\u8bdd\u6211\u4e5f\u4e0d\u5fc5\u8bf4\u4e86\u3002\u201d \u3000\u3000\u53ea\u89c1\u4ed6\u9762\u5982\u51a0\u738b\uff0c\u65e5\u5982\u671f\u661f\uff0c\u9b13\u53d1\u867d\u5df2\u82b1\u767d\uff0c\u7709\u68a2\u773c\u89d2\u4e5f\u6709\u4e86\u4e9b\u76b1\u7eb9\uff0c\u4f46\u4f9d\u7a00\u72b9\u53ef\u60f3\u89c1 \u5f53\u5e74\u4e4b\u624b\u91c7\uff0c\u53ea\u4e0d\u8fc7\u4e2d\u5e74\u4ee5\u5f8c\u5df2\u53d1\u798f\u4e86\u3002 \u3000\u3000\u695a\u7559\u9999\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u5c31\u56e0\u4e3a\u5728\u4e0b\u6df1\u77e5\u4e94\u4f4d\u548c\u674e\u8001\u5e84\u4e3b\u7684\u4ea4\u60c5\uff0c\u662f\u4ee5\u65b9\u624d\u5728\u4e0b\u4fbf\u4e5f\u5df2\u731c \u5230\uff0c\u4e94\u4f4d\u4e2d\u5fc5\u6709\u4e00\u4f4d\u662f\u548c\u674e\u8001\u524d\u8f88\u6709\u90ce\u8205\u4e4b\u4eb2\u7684\u201c\u53cc\u5251\u65e0\u654c\u9547\u5173\u4e1c\u201d\u51cc\u98de\u9601\u8001\u524d\u8f88\uff0c\u53ea\u4e0d\u8fc7 \u4e00\u65f6\u95f4\u8ba4\u4e0d\u51fa\u662f\u90a3\u4e00\u4f4d\u800c\u5df2\u3002\u201d \u3000\u3000\u51cc\u98de\u9601\u9053\uff1a\u201c\u4f60\u662f\u4ec0\u9ebd\u65f6\u95f4\u8ba4\u51fa\u6211\u6765\u7684\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u524d\u8f88\u51fa\u624b\u6570\u62db\u4e4b\u5f8c\uff0c\u5728\u4e0b\u4fbf\u5df2\u8ba4\u51fa\u6765\u4e86\u3002\u201d \u3000\u3000\u51cc\u98de\u9601\u9053\uff1a\u201c\u6211\u7528\u7684\u5e76\u975e\u672c\u95e8\u5251\u6cd5\uff0c\u4f60\u5374\u662f\u4ece\u90a3\u70b9\u770b\u51fa\u6765\u7684\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u524d\u8f88\u7528\u7684\u867d\u975e\u672c\u95e8\u5251\u6cd5\uff0c\u5374\u4ecd\u6709\u8e2a\u8ff9\u53ef\u5bfb\uff0c\u53ea\u56e0\u524d\u8f88\u4e00\u5411\u60ef\u7528\u9e33\u9e2f\u53cc\u5251\uff0c \u9aa4\u7136\u4f7f\u7528\u5355\u5251\uff0c\u4fbf\u96be\u514d\u6709\u4e9b\u4e0d\u60ef\u3002\u201d\u4ed6\u4e00\u7b11\u63a5\u9053\uff1a\u201c\u65e0\u8bba\u662f\u4ec0\u9ebd\u4eba\uff0c\u4ed6\u6570\u5341\u5e74\u6765\u6839\u6df1\u8482\u56fa\u7684 \u4e60\u60ef\uff0c\u4e00\u65f6\u4e4b\u95f4\u662f\u4e07\u4e07\u65e0\u6cd5\u5b8c\u5168\u6539\u8fc7\u6765\u7684\uff0c\u524d\u8f88\u7684\u5de6\u624b\u867d\u4e5f\u634f\u7740\u5251\u8bc0\uff0c\u4f46\u4e00\u5230\u7d27\u5f20\u65f6\uff0c\u624b\u638c \u5c31\u7d27\u7d27\u63e1\u8d77\uff0c\u597d\u50cf\u63e1\u7740\u4e00\u67c4\u770b\u4e0d\u89c1\u7684\u65e0\u5f62\u4e4b\u5251\u4f3c\u7684\u3002\u201d \u3000\u3000\u51cc\u98de\u9601\u4e5f\u6c89\u9ed8\u4e86\u534a\u664c\uff0c\u9053\uff1a\u201c\u4f60\u4e00\u76f4\u7528\u624b\u634f\u7740\u5251\u5c16\uff0c\u83ab\u975e\u65e9\u5df2\u51c6\u5907\u8981\u5c06\u5251\u67c4\u585e\u5165\u6211\u624b \u91cc\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5728\u4e0b\u77e5\u9053\u82e5\u5c06\u5251\u67c4\u9012\u5230\u51cc\u8001\u524d\u8f88\u624b\u8fb9\uff0c\u524d\u8f88\u4e00\u5b9a\u4f1a\u5728\u4e0d \u77e5\u4e0d\u89c9\u4e2d\u63a5\u8fc7\u53bb\uff0c\u53ea\u56e0\u524d\u8f88\u8fd9\u65f6\u5df2\u5c06\u6240\u6709\u7cbe\u795e\u5168\u90fd\u8d2f\u6ce8\u638c\u4e2d\u5251\u4e0a\uff0c\u5c01\u522b\u7684\u4e8b\u5c31\u96be\u514d\u6709\u6240\u758f \u5ffd\uff0c\u6240\u4ee5\u8fd9\u65f6\u524d\u8f88\u5c31\u96be\u514d\u8981\u88ab\u201c\u4e60\u60ef\u201d\u6240\u652f\u914d\u3002\u201d \u3000\u3000\u8fd9\u9053\u7406\u6b63\u5982\u4e00\u4e2a\u5438\u70df\u7684\u4eba\uff0c\u82e5\u662f\u4e0b\u5b9a\u51b3\u5fc3\u6210\u4e86\u70df\uff0c\u4f46\u7b49\u5230\u4ed6\u795e\u7ecf\u7d27\u5f20\uff0c\u5168\u795e\u8d2f\u6ce8\u5728\u67d0\u4e00 \u4ef6\u4e8b\u65f6\uff0c\u624b\u8fb9\u53c8\u6070\u5de7\u6709\u70df\uff0c\u4ed6\u5c31\u4f1a\u5728\u4e0d\u77e5\u4e0d\u89c9\u4e2d\u5c06\u9999\u70df\u62ff\u8d77\u6765\u4e86\uff0c\u53ea\u56e0\u8fd9\u65f6\u4ed6\u7684\u884c\u52a8\u5df2\u7531 \u201c\u4e0b\u610f\u8bc6\u201d\u6240\u652f\u914d\u3002 \u3000\u3000\u695a\u7559\u9999\u90a3\u65f6\u81ea\u7136\u8fd8\u4e0d\u61c2\u5f97\u4ec0\u9ebd\u201c\u4e0b\u610f\u8bc6\u201d\uff0c\u4ed6\u53ea\u77e5\u9053\u201c\u4e60\u60ef\u6210\u81ea\u7136\u201d\uff0c\u8fd9\u9053\u7406\u603b\u662f\u4e0d\u9519 \u7684\u3002 \u3000\u3000\u51cc\u98de\u9601\u957f\u957f\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5b9e\u4e0d\u76f8\u7792\uff0c\u6211\u63a5\u8fc7\u8fd9\u67c4\u5251\u5f8c\uff0c\u8fd8\u4e0d\u77e5\u9053\u8fd9\u67c4\u5251\u662f\u600e\u4f1a\u5230 \u6211\u624b\u4e2d\u7684\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4f46\u524d\u8f88\u60f3\u5fc5\u4e5f\u77e5\u9053\uff0c\u8fd9\u5251\u9635\u65e2\u5c11\u4e0d\u5f97\u4e00\u67c4\u5251\uff0c\u4e5f\u591a\u4e0d\u5f97\u4e00\u67c4\u5251\uff0c\u5426\u5219\u9635\u6cd5 \u7684\u63a8\u52a8\uff0c\u5c31\u7acb\u523b\u6709\u4e86\u963b\u622a\u3002\u201d \u3000\u3000\u51cc\u98de\u9601\u4f3c\u4e4e\u5fc3\u60c5\u6c89\u91cd\uff0c\u8fde\u8bdd\u90fd\u4e0d\u613f\u8bf4\u4e86\u3002 \u3000\u3000\u695a\u7559\u9999\u6309\u7740\u9053\uff1a\u201c\u5251\u9635\u7684\u63a8\u52a8\u6709\u4e86\u963b\u622a\uff0c\u9635\u5f0f\u5c31\u7acb\u523b\u6709\u4e86\u7834\u7efd\uff0c\u4f46\u4ee5\u524d\u8f88\u4eec\u7684\u529f\u529b\uff0c\u5728 \u4e00\u77ac\u95f4\u5c31\u53ef\u4ee5\u5c06\u8fd9\u7834\u7efd\u5f25\u8865\u8fc7\u6765\u3002\u201d \u3000\u3000\u90a3\u9880\u957f\u8001\u4eba\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u5c31\u4e58\u8fd9\u4e00\u77ac\u95f4\uff0c\u5148\u5236\u4f4f\u4e86\u674e\u4e16\u5144\uff0c\u8981\u6211\u4eec\u6295\u9f20\u5fcc\u5668\uff0c\u4e0d\u80fd\u518d\u51fa \u624b\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5728\u4e0b\u6b64\u4e3e\uff0c\u5b9e\u662f\u60c5\u975e\u5f97\u5df2\uff0c\u5728\u4e0b\u5e76\u65e0\u4e1d\u6beb\u4f24\u5bb3\u674e\u5144\u4e4b\u610f\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u5ffd\u7136\u51b2\u8fc7\u53bb\uff0c\u5927\u58f0\u9053\uff1a\u201c\u90a3\u9ebd\u4f60\u4e3a\u4f55\u4e0d\u653e\u4e86\u4ed6\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u5728\u4e0b\u82e5\u653e\u4e86\u4ed6\uff0c\u5404\u4f4d\u662f\u5426\u4e5f\u80af\u653e\u4e86\u6211\u5462\uff1f\u201d \u3000\u3000\u67f3\u65e0\u7709\u54ac\u4e86\u54ac\u7259\uff0c\u9053\uff1a\u201c\u53ea\u8981\u4f60\u4e0d\u4f24\u5bb3\u4ed6\uff0c\u6211\u5c31\u7b54\u5e94\u4f60\u2026\u2026\u2026\u201d \u3000\u3000\u674e\u7389\u51fd\u4e00\u76f4\u5782\u7740\u5934\u9eef\u7136\u65e0\u8bed\uff0c\u6b64\u523b\u5ffd\u7136\u5927\u559d\u9053\uff1a\u201c\u4f60\u4e5f\u7edd\u4e0d\u80fd\u7b54\u5e94\u4efb\u4f55\u4e8b\uff0c\u4f60\u96be\u9053\u5fd8 \u4e86\u2026\u2026\u2026\u201d \u3000\u3000\u67f3\u65e0\u7709\u8dfa\u4e86\u8dfa\u811a\uff0c\u9053\uff1a\u201c\u6211\u5e76\u6ca1\u6709\u5fd8\u8bb0\uff0c\u53ef\u662f\u4f60\u2026\u2026\u2026\uff0c\u6211\u53c8\u600e\u9ebd\u80fd\u8ba9\u4ed6\u4f24\u4e86\u4f60\uff1f\u201d \u3000\u3000\u674e\u7389\u51fd\u55c4\u58f0\u9053\uff1a\u201c\u6211\u6ca1\u5173\u7cfb\uff0c\u4f60\u5c31\u7b97\u6740\u4e86\u6211\uff0c\u4e5f\u4e0d\u80fd\u653e\u4ed6\u8d70\u7684\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u76ee\u4e2d\u7adf\u4f46\u8981\u6d41\u4e0b\u6cea\u6765\uff0c\u51c4\u7136\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u4e3a\u4e86\u6211\u4e0d\u60dc\u2026\u2026\u2026\u201d \u3000\u3000\u5979\u8bdd\u672a\u5b8c\uff0c\u674e\u7389\u51fd\u5ffd\u7136\u72c2\u543c\u4e00\u58f0\uff0c\u5934\u649e\u5411\u695a\u7559\u9999\u7684\u80f8\u819b\uff0c\u53cc\u8db3\u4e5f\u8fde\u73af\u8e22\u51fa\uff0c\u76f4\u53d6\u695a\u7559\u9999 \u7684\u4e0b\u8179\u3002 \u3000\u3000\u8fd9\u4e00\u6765\u8fde\u51cc\u98de\u9601\u7684\u8138\u8272\u90fd\u53d8\u4e86\uff0c\u56e0\u4e3a\u5927\u5bb6\u90fd\u77e5\u9053\uff0c\u53ea\u8981\u695a\u7559\u9999\u7684\u638c\u529b\u4e00\u5410\uff0c\u674e\u7389\u51fd\u7684\u8151 \u810f\u5fc3\u80ba\u5c31\u7acb\u523b\u8981\u88ab\u9707\u788e\u3002 \u3000\u3000\u53ea\u542c\u201c\u7830\u7830\u201d\u51e0\u54cd\uff0c\u674e\u7389\u51fd\u8e09\u8dc4\u5f8c\u9000\uff0c\u638c\u4e2d\u5251\u8131\u624b\u98de\u51fa\uff0c\u4f46\u4ed6\u7684\u8eab\u5f62\u5374\u5e76\u6ca1\u6709\u5012\u4e0b\u3002 \u3000\u3000\u695a\u7559\u9999\u53cd\u800c\u88ab\u4ed6\u4e00\u811a\u8e22\u5012\u3002 \u3000\u3000\u5728\u90a3\u95f4\u4e0d\u5bb9\u53d1\u7684\u4e00\u5239\u90a3\u95f4\uff0c\u695a\u7559\u9999\u7adf\u6ca1\u6709\u4f7f\u7528\u638c\u4e0a\u7684\u4e70\u529b\uff0c\u5728\u81ea\u5df1\u7684\u6027\u547d\u5df2\u5c06\u4e0d\u4fdd\u7684\u65f6 \u5019\uff0c\u4ed6\u7adf\u8fd8\u662f\u4e0d\u80af\u4f24\u522b\u4eba\u7684\u6027\u547d\u3002 \u3000\u3000\u674e\u7389\u51fd\u8e09\u8dc4\u5f8c\u9000\uff0c\u695a\u7559\u9999\u8eab\u5b50\u5012\u4e0b\uff0c\u4e00\u9053\u5251\u5149\u76f4\u98de\u800c\u51fa\u2026\u2026\u2026\u4e5f\u5c31\u5728\u8fd9\u65f6\uff0c\u67f3\u65e0\u7709\u8eab\u5f62 \u5df2\u98de\u63a0\u800c\u8d77\u3002 \u3000\u3000\u5979\u8eab\u5f62\u5982\u6d41\u661f\u8ffd\u6708\uff0c\u53cd\u624b\u6284\u4f4f\u4e86\u90a3\u67c4\u81ea\u674e\u7389\u51fd\u638c\u4e2d\u98de\u51fa\u6765\u7684\u5251\uff0c\u5251\u5149\u56de\u65cb\uff0c\u8fd0\u4eba\u5e26\u5251\u5411 \u695a\u7559\u9999\u523a\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u695a\u7559\u9999\u4e0d\u5fcd\u4f24\u4eba\uff0c\u4f46\u81ea\u5df1\u5374\u88ab\u8e22\u5f97\u4e0d\u8f7b\uff0c\u8138\u4e0a\u5df2\u6ca1\u6709\u4e00\u4e1d\u8840\u8272\uff0c\u5374\u6709\u4e00\u7c92\u7c92\u51b7\u6c57\u5728\u5f80\u5916 \u5192\u3002 \u3000\u3000\u4ed6\u773c\u89c1\u67f3\u65e0\u7709\u8fd9\u4e00\u5251\u523a\u6765\uff0c\u7adf\u4e5f\u65e0\u6cd5\u95ea\u907f\uff0c\u773c\u89c1\u67f3\u65e0\u7709\u8fd9\u4e00\u5251\u5c31\u8981\u5c06\u4ed6\u6d3b\u6d3b\u9489\u6b7b\u5728\u5730 \u4e0a\u3002 \u3000\u3000\u7a81\u7136\u201c\u545b\u201d\u7684\u4e00\u58f0\uff0c\u58f0\u5982\u9f99\u541f\uff0c\u706b\u661f\u56db\u6e85\u3002 \u3000\u3000\u51cc\u98de\u9601\u7b49\u4e94\u4eba\u638c\u4e2d\u7684\u516d\u67c4\u5251\u5149\u540c\u65f6\u6325\u51fa\uff0c\u4ea4\u7ec7\u6210\u4e00\u7247\u5251\u7f51\uff0c\u7adf\u5c06\u67f3\u65e0\u7709\u8fd9\u4e00\u5251\u51cc\u7a7a\u6258\u4f4f \u4e86\u3002 \u3000\u3000\u67f3\u65e0\u7709\u88ab\u9707\u5f97\u51cc\u7a7a\u7ffb\u4e86\u4e2a\u8eab\uff0c\u624d\u843d\u5230\u5730\u4e0a\uff0c\u4e00\u53ea\u624b\u867d\u5df2\u88ab\u9707\u5f97\u53d1\u9ebb\uff0c\u4f46\u8fd8\u662f\u7d27\u63e1\u7740\u5251\u4e0d \u653e\uff0c\u98a4\u58f0\u9053\uff1a\u201c\u524d\u2026\u2026\u2026\u524d\u8f88\u4eec\u4e3a\u4f55\u8981\u6551\u4ed6\uff1f\u201d \u3000\u3000\u8427\u77f3\u5389\u58f0\u9053\uff1a\u201c\u4ed6\u4e0d\u5fcd\u4f24\u4f60\u592b\u5a7f\u6027\u547d\uff0c\u624d\u4f1a\u88ab\u8e22\u5012\uff0c\u4f60\u600e\u9ebd\u80fd\u5728\u8fd9\u79cd\u65f6\u5019\u6765\u6740\u4ed6\uff0c\u864e\u4e18 \u674e\u5bb6\u7684\u5b50\u5f1f\uff0c\u600e\u80fd\u505a\u5982\u6b64\u4e0d\u4ec1\u4e0d\u4e49\u7684\u4e8b\uff1f\u201d \u3000\u3000\u67f3\u65e0\u7709\u5782\u4e0b\u4e86\u5934\uff0c\u4f3c\u4e4e\u4e5f\u8bf4\u4e0d\u51fa\u8bdd\u6765\u4e86\u3002 \u3000\u3000\u674e\u7389\u51fd\u5374\u5ffd\u7136\u8dea\u4e86\u4e0b\u6765\uff0c\u5782\u9996\u9053\uff1a\u201c\u4ed6\u638c\u4e0b\u7559\u60c5\uff0c\u665a\u8f88\u81ea\u4e5f\u77e5\u9053\uff0c\u8fd9\u4e0d\u6740\u4e4b\u6069\uff0c\u665a\u8f88\u66f4 \u4e0d\u6562\u5fd8\u8bb0\uff0c\u65e0\u8bba\u8981\u665a\u8f88\u5982\u4f55\u62a5\u7b54\uff0c\u665a\u8f88\u90fd\u5728\u6240\u4e0d\u8f9e\u3002\u201d \u3000\u3000\u8427\u77f3\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8fd9\u624d\u50cf\u8bdd\uff0c\u6211\u8f88\u6b66\u4fa0\u4e2d\u4eba\uff0c\u8bb2\u7a76\u7684\u5c31\u662f\u6069\u6028\u5206\u660e\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u9053\uff1a\u201c\u4ed6\u5bf9\u665a\u8f88\u7684\u6069\u60c5\uff0c\u665a\u8f88\u56fa\u7136\u8981\u62a5\u7b54\uff0c\u4f46\u4eca\u65e5\u665a\u8f88\u5374\u65e0\u8bba\u5982\u4f55\u4e5f\u4e0d\u80fd\u653e\u4ed6\u8d70 \u7684\u3002\u201d \u3000\u3000\u8427\u77f3\u53f1\u9053\uff1a\u201c\u8fd9\u662f\u4ec0\u9ebd\u8bdd\uff1f\u201d \u3000\u3000\u674e\u7389\u51fd\u5934\u91cd\u5f97\u66f4\u4f4e\uff0c\u9053\uff1a\u201c\u53ea\u56e0\u4ed6\u5bf9\u665a\u8f88\u7684\u6069\u60c5\u65e0\u8bba\u591a\u9ebd\u539a\uff0c\u603b\u4e5f\u4e0d\u5982\u7236\u601d\u6df1\u91cd\uff0c\u665a\u8f88 \u4eca\u65e5\u82e5\u5ff5\u79c1\u60c5\u653e\u4e86\u4ed6\uff0c\u5bb6\u7236\u53ea\u6015\u4fbf\u8981\u62b1\u61be\u7ec8\u751f\u4e86\uff0c\u5b5d\u4e49\u4e8c\u5b57\u65e2\u96be\u4e24\u5168\uff0c\u665a\u8f88\u53ea\u6709\u4ee5\u5b5d\u9053\u4e3a \u5148\uff0c\u524d\u8f88\u4eec\u603b\u4e0d\u80fd\u8981\u665a\u8f88\u505a\u4e2a\u4e0d\u5b5d\u7684\u4eba\u5427\uff1f\u201d \u3000\u3000\u8427\u77f3\u9ed8\u7136\u826f\u4e45\uff0c\u76ee\u5149\u7f13\u7f13\u8f6c\u5411\u674e\u89c2\u9c7c\u3002 \u3000\u3000\u53ea\u89c1\u8fd9\u8001\u4eba\u4e00\u5f20\u82cd\u767d\u9ebb\u6728\u7684\u8138\uff0c\u6b64\u523b\u7adf\u5df2\u6da8\u7ea2\uff0c\u5634\u89d2\u7684\u808c\u8089\u4e5f\u5df2\u5f00\u59cb\u53d1\u6296\uff0c\u90a3\u53cc\u7a7a\u865a\u7684 \u773c\u775b\u91cc\uff0c\u66f4\u5145\u6ee1\u4e86\u60b2\u6124\u4e4b\u8272\uff0c\u7adf\u4f3c\u4e4e\u6709\u4e00\u79cd\u795e\u79d8\u7684\u706b\u70ac\uff0c\u5c06\u4ed6\u5df2\u5feb\u51b7\u900f\u4e86\u7684\u751f\u547d\u53c8\u71c3\u70e7\u4e86\u8d77 \u6765\u3002 \u3000\u3000\u8427\u77f3\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u76ee\u5149\u56db\u8f6c\uff0c\u9053\uff1a\u201c\u5404\u4f4d\u7684\u610f\u89c1\u5982\u4f55\uff1f\u201d \u3000\u3000\u51cc\u98de\u9601\u7b49\u56db\u4eba\u50cf\u662f\u4e5f\u89c9\u5f97\u5f88\u4e3a\u96be\uff0c\u7adf\u6ca1\u6709\u4e00\u4e2a\u4eba\u56de\u7b54\u4ed6\u8fd9\u53e5\u8bdd\uff0c\u674e\u7389\u51fd\u7528\u773c\u89d2\u779f\u4e86\u4ed6 \u4eec\uff0c\u53c8\u9053\uff1a\u201c\u665a\u8f88\u4e5f\u77e5\u9053\u4ee5\u524d\u8f88\u4eec\u7684\u8eab\u4efd\u5a01\u671b\uff0c\u662f\u7edd\u4e0d\u80af\u4e58\u4eba\u4e4b\u5371\uff0c\u53d6\u4eba\u6027\u547d\u7684\uff0c\u4f46\u4ee5\u524d\u8f88 \u4eec\u548c\u5bb6\u7236\u7684\u4ea4\u60c5\uff0c\u603b\u4e5f\u4e0d\u81f3\u65bc\u773c\u770b\u7740\u4ed6\u5982\u6b64\u75db\u82e6\u5427\uff1f\u201d\u4ed6\u62ac\u8d77\u5934\u6765\uff0c\u7f13\u7f13\u6309\u7740\u9053\uff1a\u201c\u5bb6\u7236\u81ea \u4ece\u4e03\u5e74\u524d\u82e6\u7ec3\u5251\u6c14\u65f6\uff0c\u4e0d\u614e\u8d70\u706b\u5165\u9b54\uff0c\u8fd9\u4e03\u5e74\u6765\u5b9e\u662f\u751f\u4e0d\u5982\u6b7b\uff0c\u524d\u8f88\u4eec\u53c8\u600e\u5fcd\u5fc3\u2026\u2026\u2026\u201d \u3000\u3000\u8427\u77f3\u5ffd\u7136\u5927\u559d\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u4e0d\u5fc5\u8bf4\u4e86\uff0c\u6211\u53ea\u95ee\u4f60\u4e00\u53e5\u8bdd\uff0c\u6b64\u523b\u6211\u4eec\u5c31\u7b97\u6740\u4e86\u695a\u7559\u9999\uff0c \u5bf9\u4f60\u7236\u4eb2\u53c8\u80fd\u6709\u4ec0\u9ebd\u597d\u5904\uff1f\u201d \u3000\u3000\u674e\u7389\u51fd\u9053\uff1a\u201c\u665a\u8f88\u4e5f\u4e0d\u77e5\u5bb6\u7236\u662f\u4e3a\u4e86\u4ec0\u9ebd\u4e8b\u5b9a\u8981\u53d6\u6b64\u4eba\u7684\u6027\u547d\uff0c\u53ea\u77e5\u9053\u7236\u547d\u4e0d\u53ef\u8fdd\uff0c\u524d \u8f88\u4eec\u82e5\u8fd8\u672a\u5fd8\u8bb0\u5bb6\u7236\u6614\u65e5\u5bf9\u524d\u8f88\u4eec\u7684\u2026\u2026\u2026\u201d \u3000\u3000\u8427\u77f3\u53c8\u6253\u65ad\u4e86\u5b83\u7684\u8bdd\uff0c\u5927\u58f0\u9053\uff1a\u201c\u4f60\u7528\u4e0d\u7740\u63d0\u9192\u6211\uff0c\u674e\u89c2\u9c7c\u6614\u65e5\u7684\u786e\u5bf9\u6211\u4e0d\u9519\uff0c\u6211\u5c31\u7b97 \u80fd\u5bf9\u4e0d\u8d77\u5929\u4e0b\u7684\u4eba\uff0c\u4e5f\u4e0d\u80fd\u5bf9\u4e0d\u8d77\u4ed6\u3002\u201d \u3000\u3000\u4ed6\u5634\u91cc\u8bf4\u7740\u8bdd\uff0c\u5df2\u5c06\u638c\u4e2d\u7684\u5251\u64a4\u4e86\u56de\u53bb\uff0c\u9053\uff1a\u201c\u6211\u7684\u5fc3\u610f\u5df2\u51b3\uff0c\u4e0d\u77e5\u9053\u4f60\u4eec\u600e\u4eec\u8bf4\uff1f\u201d \u3000\u3000\u90a3\u9880\u957f\u8001\u4eba\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u77f3\u8001\u65e2\u7136\u5982\u6b64\uff0c\u8001\u673d\u66f4\u65e0\u8bdd\u53ef\u8bf4\u3002\u201d \u3000\u3000\u51cc\u98de\u9601\u9053\uff1a\u201c\u6211\u4e0e\u89c2\u9c7c\u5144\u4e0d\u4f46\u662f\u81f3\u4ea4\uff0c\u8fd8\u662f\u81f3\u4eb2\uff0c\u6211\u7684\u5904\u5883\u5b9e\u5728\u6bd4\u5404\u4f4d\u66f4\u96be\u8bf4\u8bdd\uff0c\u6240 \u4ee5\u2026\u2026\u2026\u6240\u4ee5\u2026\u2026\u2026\u201d \u3000\u3000\u4ed6\u970d\u7136\u8f6c\u8fc7\u8eab\uff0c\u9053\uff1a\u201c\u4eca\u65e5\u65e0\u8bba\u5404\u4f4d\u662f\u6740\u4e86\u8fd9\u695a\u7559\u9999\uff0c\u8fd8\u662f\u653e\u4e86\u4ed6\uff0c\u6211\u53ea\u6709\u4e0d\u95fb\u4e0d\u95ee\uff0c \u5404\u4f4d\u6700\u597d\u5c31\u53ea\u5f53\u6211\u4e0d\u5728\u8fd9\u91cc\u5427\uff01\u201d \u3000\u3000\u73b0\u5728\uff0c\u5df2\u6709\u56db\u67c4\u5251\u64a4\u4e86\u56de\u53bb\u3002 \u3000\u3000\u90a3\u770b\u6765\u6700\u5e73\u51e1\u7684\u9ed1\u8863\u4eba\u5df2\u6c89\u9ed8\u4e86\u8bb8\u4e45\uff0c\u6b64\u523b\u624d\u6c89\u58f0\u9053\uff1a\u201c\u6211\u7684\u610f\u601d\u548c\u98de\u8001\u4e00\u6837\u3002\u201d \u3000\u3000\u8fd9\u4eba\u4f3c\u4e4e\u4e0d\u559c\u6b22\u8bf4\u8bdd\uff0c\u53ea\u8bf4\u4e86\u51e0\u4e2a\u5b57\uff0c\u5c31\u4e5f\u8f6c\u8fc7\u8eab\u53bb\u3002 \u3000\u3000\u65bc\u662f\u5269\u4e0b\u6765\u7684\u5c31\u53ea\u6709\u90a3\u9ad8\u5927\u8001\u4eba\u7684\u4e00\u67c4\u5251\u4e86\uff0c\u4ed6\u867d\u7136\u7d27\u7d27\u63e1\u7740\u5251\u67c4\uff0c\u4f46\u5251\u5c16\u5374\u4f3c\u5df2\u5728\u98a4 \u6296\u3002 \u3000\u3000\u8427\u77f3\u76b1\u7709\u9053\uff1a\u201c\u6211\u77e5\u9053\u674e\u89c2\u9c7c\u548c\u4f60\u7684\u4ea4\u60c5\u6700\u6df1\uff0c\u4f60\u4e3a\u4f55\u4e0d\u8bf4\u8bdd\uff1f\u201d \u3000\u3000\u90a3\u9ed1\u8863\u8001\u4eba\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u89c2\u9c7c\u5144\u4e0d\u4f46\u4e0e\u6211\u4ea4\u60c5\u6df1\u539a\uff0c\u800c\u4e14\u8fd8\u5bf9\u6211\u6709\u6551\u547d\u4e4b\u6069\uff0c \u82e5\u53ea\u4e3a\u6211\u4e00\u4e2a\u4eba\u7684\u5173\u7cfb\uff0c\u53eb\u6211\u4eb2\u624b\u6740\u4e86\u695a\u7559\u9999\u90fd\u6ca1\u5173\u7cfb\uff0c\u53ea\u53ef\u60dc\u2026\u2026\u2026\u201d \u3000\u3000\u8427\u77f3\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u9ed1\u8863\u8001\u4eba\uff1a\u201c\u77f3\u8001\u603b\u8be5\u77e5\u9053\uff0c\u6b64\u523b\u6211\u4e00\u8a00\u4e00\u884c\uff0c\u90fd\u8db3\u4ee5\u5f71\u54cd\u6b66\u5f53\u5c71\u4e0a\u4e0a\u4e0b\u4e0b\u6570\u5343\u5f1f\u5b50\uff0c \u6211\u600e\u9ebd\u80fd\u2026\u2026\u2026\u6211\u600e\u9ebd\u80fd\u2026\u2026\u2026\u201d \u3000\u3000\u4ed6\u8bed\u58f0\u7adf \u3000\u3000\u98a4\u6296\u8d77\u6765\uff0c\u663e\u89c1\u5fc3\u91cc\u5145\u6ee1\u4e86\u77db\u76fe\u75db\u82e6\u3002 \u3000\u3000\u8427\u77f3\u5374\u5389\u58f0\u9053\uff1a\u201c\u539f\u6765\u4f60\u662f\u5728\u987e\u5fcc\u4f60\u6b66\u5f53\u5927\u62a4\u6cd5\u7684\u8eab\u4efd\uff0c\u4f46\u82e5\u975e\u674e\u89c2\u9c7c\u6551\u4f60\uff0c\u4f60\u80fd\u6d3b\u5230 \u73b0\u5728\u9ebd\uff1f\u4f60\u4e3a\u4f55\u4e0d\u80fd\u4e3a\u4e86\u4ed6\u8f9e\u53bb\u8fd9\u62a4\u6cd5\u4e4b\u4f4d\uff1f\u201d \u3000\u3000\u8fd9\u9ed1\u8863\u8001\u4eba\u8d6b\u7136\u7adf\u662f\u6b66\u5f53\u5c71\u5f53\u4eca\u7b2c\u4e00\u62a4\u6cd5\u94c1\u5c71\u9053\u957f\uff0c\u695a\u7559\u9999\u4e0d\u7981\u6697\u4e2d\u53f9\u4e86\u53e3\u6c14\uff0c\u53ea\u542c\u8427 \u77f3\u53c8\u9053\uff1a\u201c\u8001\u5b9e\u544a\u8bc9\u4f60\uff0c\u4eca\u65e5\u6211\u62a5\u4e86\u674e\u89c2\u9c7c\u4e4b\u6069\u5f8c\uff0c\u6211\u4e5f\u89c9\u5f97\u65e0\u6cd5\u518d\u7ba1\u675f\u53f7\u4ee4\u7389\u5251\u95e8\u4e0b\u4e86\uff0c \u4e5f\u53ea\u6709\u4ece\u6b64\u9000\u9690\u6df1\u5c71\uff0c\u4f60\u82e5\u80af\u6765\u505a\u6211\u7684\u4f19\u4f34\uff0c\u6211\u5012\u6b22\u8fce\u5f97\u5f88\u3002\u201d \u3000\u3000\u94c1\u5c71\u9053\u957f\u80f8\u819b\u8d77\u4f0f\uff0c\u6c57\u73e0\u5df2\u6dcc\u843d\u5728\u8863\u8896\u4e0a\u3002 \u3000\u3000\u695a\u7559\u9999\u5ffd\u7136\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u6211\u770b\u9053\u957f\u4e5f\u4e0d\u5fc5\u518d\u4e3a\u96be\u4e86\uff0c\u4e0d\u5982\u4e5f\u548c\u8fd9\u51e0\u4f4d\u5927\u4fa0\u4eec\u4e00\u6837\uff0c\u4e5f \u62ff\u6211\u6765\u505a\u4eba\u60c5\u5427\uff01\u6240\u8c13\u201c\u6c5f\u6e56\u9053\u4e49\u201d\uff0c\u672c\u6765\u5c31\u53ef\u4ee5\u6709\u5f88\u591a\u79cd\u89e3\u91ca\uff0c\u4f60\u4eca\u5929\u6740\u4e86\u6211\uff0c\u522b\u4eba\u975e\u4f46 \u4e0d\u4f1a\u8bf4\u4f60\u4e0d\u4ec1\u4e0d\u4e49\uff0c\u53cd\u800c\u4f1a\u8bf4\u4f60\u662f\u4e2a\u6069\u6028\u5206\u660e\uff0c\u5982\u6069\u5fc5\u62a5\u7684\u5927\u4e08\u592b\uff0c\u4eca\u65e5\u4f60\u82e5\u653e\u4e86\u6211\uff0c\u4ee5\u5f8c \u53cd\u800c\u65e0\u6cd5\u505a\u4eba\u4e86\u3002\u201d \u3000\u3000\u94c1\u5c71\u9053\u957f\u8dfa\u4e86\u8dfa\u811a\uff0c\u5ffd\u7136\u4e3e\u8d77\u5de6\u638c\uff0c\u53cd\u624b\u4e00\u638c\u5411\u81ea\u5df1\u53f3\u80a9\u4e0a\u5207\u4e86\u4e0b\u53bb\uff0c\u53ea\u542c\u201c\u5361\u5693\u201d\u4e00 \u58f0\uff0c\u9aa8\u9abc\u5982\u6298\u7af9\u3002 \u7b2c\u5341\u4e94\u7ae0\u3000\u6b7b\u4ea1\u6ecb\u5473>> \u53e4\u9f99\u300a\u695a\u7559\u9999\u7cfb\u5217\xb7\u753b\u7709\u9e1f\u300b \u7b2c\u5341\u4e94\u7ae0\u3000\u6b7b\u4ea1\u6ecb\u5473 \u3000\u3000\u8427\u77f3\u5931\u58f0\u9053\uff1a\u201c\u4f60\u8fd9\u662f\u4e3a\u4e86\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u94c1\u5c71\u9053\u957f\u8e09\u8dc4\u5f8c\u9000\uff0c\u5636\u58f0\u60e8\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u90fd\u77a7\u89c1\u4e86\uff0c\u695a\u7559\u9999\uff0c\u4f60\u4e5f\u77a7\u7740\uff0c\u6211\u5e76\u975e\u4e0d\u613f\u963b \u6321\u4ed6\u4eec\u6740\u4eba\uff0c\u6211\u5b9e\u5728\u5df2\u662f\u65e0\u529b\u963b\u6321\u4e86\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u4ea6\u662f\u8138\u65e0\u8840\u8272\uff0c\u7adf\u4ee5\u5df2\u88ab\u9a87\u5446\u3002 \u3000\u3000\u94c1\u5c71\u9053\u957f\u5636\u58f0\u9053\uff1a\u201c\u4f60\u73b0\u5728\u4e3a\u4f55\u8fd8\u4e0d\u6740\u4e86\u4ed6\uff1f\u4f60\u8fd8\u7b49\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u67f3\u65e0\u7709\u4e5f\u62dc\u5012\u5728\u5730--\u548c\u674e\u7389\u51fd\u4e24\u4eba\u540c\u65f6\u62dc\u9053\uff1a\u201c\u591a\u8c22\u524d\u8f88\uff0c\u524d\u8f88\u4eec\u7684\u5927\u6069\uff0c\u5f1f\u5b50\u6ca1\u9f7f\u4e0d \u5fd8\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u8f7b\u8f7b\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u6709\u94c1\u5c71\u9053\u957f\u8fd9\u6837\u7684\u4eba\u5982\u6b64\u5bf9\u6211\uff0c\u53ef\u89c1\u201c\u6c5f\u6e56\u9053\u4e49\u201d \u8fd9\u5339\u5b57\u5e76\u975e\u5168\u662f\u9a97\u4eba\u7684\uff0c\u6211\u603b\u7b97\u6b7b\u5f97\u4e0d\u51a4\u4e86\uff0c\u53ea\u4e0d\u8fc7\u6211\u8fd8\u662f\u4e0d\u61c2\uff0c\u4f60\u4eec\u4e3a\u4f55\u4e00\u5b9a\u8981\u6740\u6211\uff0c\u6211 \u4e5f\u77e5\u9053\u4f60\u4eec\u73b0\u5728\u7edd\u4e0d\u80af\u544a\u8bc9\u6211\u7684\uff0c\u770b\u6765\u6211\u53ea\u6709\u505a\u4e2a\u7cca\u6d82\u9b3c\u4e86\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u7684\u5251\u950b\u7ec8\u65bc\u523a\u5165\u4e86\u4ed6\u7684\u80f8\u819b\u3002 \u3000\u3000\u695a\u7559\u9999\u5df2\u80fd\u611f\u89c9\u5230\u5251\u950b\u523a\u5165\u4ed6\u7684\u8840\u8089\uff0c\u5947\u602a\u7684\u662f\uff0c\u5230\u4e86\u8fd9\u65f6\uff0c\u4ed6\u53cd\u800c\u4e0d\u89c9\u5f97\u6050\u60e7\uff0c\u751a\u81f3 \u8fde\u75db\u82e6\u90fd\u611f\u89c9\u4e0d\u5230\u3002 \u3000\u3000\u4ed6\u53ea\u89c9\u5251\u950b\u51b7\u5f97\u50cf\u51b0\u4e00\u6837\u3002 \u3000\u3000\u4e5f\u4e0d\u77e5\u4e3a\u4e86\u4ec0\u9ebd\uff0c\u5728\u8fd9\u4e00\u5239\u90a3\u95f4\uff0c\u4ed6\u795e\u601d\u7adf\u5ffd\u7136\u98d8\u5230\u4e86\u8fdc\u65b9\uff0c\u98d8\u5230\u9065\u8fdc\u7684\u5317\u56fd\uff0c\u90a3\u4e00\u7247 \u51b0\u5929\u96ea\u5730\u91cc\u3002 \u3000\u3000\u4ed6\u60f3\u8d77\u81ea\u5df1\u5f88\u5c0f\u5f88\u5c0f\u7684\u65f6\u5019\uff0c\u548c\u80e1\u94c1\u82b1\u4e00\u9f50\u5728\u90a3\u53ef\u7231\u7684\u96ea\u63a8\u4e0a\u6253\u7740\u6eda\uff0c\u80e1\u94c1\u82b1\u6084\u6084\u5c06\u4e00 \u5757\u51b0\u585e\u8fdb\u4ed6\u7684\u8116\u5b50\u3002 \u3000\u3000\u51b0\u96ea\u76f4\u6d41\u4e0b\u4ed6\u7684\u80f8\u819b\uff0c\u90a3\u611f\u89c9\u5c31\u548c\u73b0\u5728\u4e00\u6837\u3002 \u3000\u3000\u522b\u4eba\u8981\u62ff\u51b0\u585e\u5165\u4f60\u8116\u5b50\u65f6\uff0c\u4f60\u4f1a\u89c9\u5f97\u5f88\u5bb3\u6015\uff0c\u4f46\u7b49\u5230\u51b0\u96ea\u5df2\u6d41\u5728\u4f60\u7684\u8eab\u4e0a\uff0c\u4f60\u53cd\u800c\u4f1a\u89c9 \u5f97\u6709\u4e00\u79cd\u6b8b\u9177\u7684\u6109\u5feb\u4e4b\u611f\uff0c\u5f77\u4f5b\u5f97\u5230\u4e86\u4e00\u79cd\u89e3\u8131\uff0c\u56e0\u4e3a\u4f60\u6240\u5bb3\u6015\u7684\u4e8b\uff0c\u7ec8\u65bc\u5df2\u7ecf\u8fc7\u53bb\u4e86\u3002 \u3000\u3000\u53ea\u56e0\u4eba\u4eec\u6240\u771f\u6b63\u60e7\u6015\u7684\uff0c\u901a\u5e38\u90fd\u4e0d\u662f\u4e8b\u7269\u7684\u672c\u8eab\uff0c\u800c\u53ea\u4e0d\u8fc7\u662f\u4ed6\u5bf9\u90a3\u4ef6\u4e8b\u7684\u60f3\u50cf\u800c\u5df2\u3002 \u3000\u3000\u4eba\u4eec\u754f\u60e7\u6b7b\u4ea1\uff0c\u4e5f\u53ea\u662f\u56e0\u4e3a\u6ca1\u6709\u4eba\u4e86\u89e3\u6b7b\u4e4b\u795e\u79d8\uff0c\u6240\u4ee5\u624d\u4f1a\u5bf9\u201c\u6b7b\u4ea1\u201d\u8fd9\u4ef6\u4e8b\u751f\u51fa\u8bb8\u591a \u53ef\u6015\u7684\u60f3\u50cf\u3002 \u3000\u3000\u73b0\u5728\uff0c\u6b7b\u4ea1\u5df2\u5230\u4e86\u695a\u7559\u9999\u773c\u524d\u3002 \u3000\u3000\u5728\u8fd9\u591a\u59ff\u591a\u5f69\uff0c\u5145\u6ee1\u4e86\u4f20\u5947\u6027\u7684\u4e00\u751f\u4e2d\uff0c\u4ed6\u5df2\u4e0d\u77e5\u6709\u591a\u5c11\u6b21\u66fe\u7ecf\u9762\u5bf9\u6b7b\u4ea1\uff0c\u4f46\u5374\u4ece\u6765\u6ca1 \u6709\u4e27\u5931\u8fc7\u81ea\u4fe1\u3002 \u3000\u3000\u53ea\u6709\u8fd9\u4e00\u6b21\uff0c\u4ed6\u81ea\u5df1\u5df2\u5b8c\u5168\u65e0\u80fd\u4e3a\u529b\u4e86\uff0c\u4ed6\u4e5f\u77e5\u9053\u6b64\u65f6\u6b64\u523b\uff0c\u7edd\u4e0d\u53ef\u80fd\u518d\u6709\u4efb\u4f55\u4eba\u4f1a\u6765 \u6551\u4ed6\u3002 \u3000\u3000\u4ed6\u53ea\u89c9\u81ea\u5df1\u4ece\u6765\u4e5f\u6ca1\u6709\u8ddd\u79bb\u6b7b\u4ea1\u8fd9\u9ebd\u8fd1\uff0c\u751a\u81f3\u5df2\u8fd1\u5f97\u4ed6\u80fd\u770b\u900f\u6b7b\u4ea1\u4e4b\u795e\u79d8\uff0c\u4f7f\u4ed6\u89c9\u5f97 \u201c\u6b7b\u201d\u8fd9\u4ef6\u4e8b\uff0c\u4e5f\u4e0d\u8fc7\u5982\u6b64\u800c\u5df2\uff0c\u5e76\u6ca1\u6709\u4ec0\u9ebd\u53ef\u6015\u7684\uff0c\u4ed6\u89c9\u5f97\u90a3\u4e9b\u6015\u6b7b\u7684\u4eba\u4e0d\u4f46\u5f88\u53ef\u601c\uff0c\u4e5f \u5b9e\u5728\u5f88\u53ef\u7b11\u3002 \u3000\u3000\u6b64\u523b\u4ed6\u552f\u4e00\u7684\u5e0c\u671b\uff0c\u53ea\u662f\u5e0c\u671b\u80e1\u94c1\u82b1\u5df2\u5e26\u7740\u82cf\u84c9\u84c9\u5979\u4eec\u9003\u8d70\u4e86--\u4ed6\u82e5\u77e5\u9053\u80e1\u94c1\u82b1\u73b0\u5728\u4e5f \u5df2\u843d\u4eba\u4e86\u522b\u4eba\u7684\u638c\u63e1\u4e2d\uff0c\u90a3\u9ebd\u4ed6\u4e34\u6b7b\u524d\u7684\u5fc3\u60c5\u5c31\u4e0d\u4f1a\u6709\u5982\u6b64\u5e73\u9759\u3002 \u3000\u3000\u5728\u8fd9\u4e00\u5239\u90a3\u95f4\uff0c\u4ed6\u7adf\u60f3\u8d77\u4e86\u8bb8\u591a\u4e8b\uff0c\u4ed6\u4e5f\u4e0d\u77e5\u9053\u81ea\u5df1\u600e\u80fd\u5728\u8fd9\u77ed\u77ed\u7684\u4e00\u77ac\u95f4\u60f3\u8d77\u8fd9\u9ebd\u591a \u4e8b\u6765\u3002 \u3000\u3000\u4ed6\u89c9\u5f97\u90a3\u51b0\u51b7\u7684\u5251\u950b\uff0c\u8fd8\u505c\u7559\u5728\u4ed6\u80f8\u819b\u4e0a\u3002 \u3000\u3000\u5251\u5cf0\u7adf\u4f3c\u5df2\u505c\u987f\u3002 \u3000\u3000\u65bc\u662f\u4ed6\u5fcd\u4e0d\u4f4f\u8981\u53bb\u770b\u67f3\u65e0\u7709\u6700\u5f8c\u4e00\u773c\u3002 \u3000\u3000\u4ed6\u53d1\u89c9\u67f3\u65e0\u7709\u7adf\u4e5f\u5728\u77aa\u7740\u4ed6\uff0c\u5979\u90a3\u82cd\u767d\u800c\u7f8e\u4e3d\u7684\u8ddf\u4e0a\uff0c\u7adf\u5f77\u4f5b\u5e26\u7740\u79cd\u51c4\u51c9\u548c\u60cb\u60dc\u4e4b\u610f\u3002 \u3000\u3000\u53ea\u542c\u674e\u7389\u51fd\u8f7b\u8f7b\u54b3\u55fd\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u695a\u5144\uff0c\u6211\u4eec\u5b9e\u5728\u4e5f\u89c9\u5f97\u6709\u4e9b\u5bf9\u4e0d\u8d77\u4f60\uff0c\u5e0c\u671b\u4f60\u539f\u8c05 \u6211\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u51e0\u4e4e\u5fcd\u4e0d\u4f4f\u8981\u7b11\u4e86\u51fa\u6765\u6740\u4eba\u7684\u4eba\uff0c\u5c45\u7136\u8981\u88ab\u6740\u7684\u4eba\u539f\u8c05\u4ed6\uff0c\u695a\u7559\u9999\u53ea\u89c9\u4ed6\u8fd9\u53e5\u8bdd \u5b9e\u5728\u8bf4\u5f97\u5f88\u5999\u3002 \u3000\u3000\u67f3\u65e0\u7709\u7adf\u4e5f\u5e7d\u5e7d\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6211\u4eec\u5e76\u4e0d\u60f3\u6740\u4f60\u7684\uff0c\u8fd9\u5b9e\u5728\u662f\u4ef6\u4e0d\u5f97\u5df2\u7684\u4e8b\u3002\u201d\u5979 \u53c8\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u8d77\u4e86\u773c\u775b\u3002 \u3000\u3000\u695a\u7559\u9999\u77e5\u9053\u5979\u4e00\u95ed\u8d77\u773c\u775b\uff0c\u5251\u950b\u5c31\u8981\u523a\u4e0b\u6765\u4e86\u3002 \u3000\u3000\u8c01\u77e5\u5c31\u5728\u8fd9\u65f6\uff0c\u53ea\u542c\u201c\u54d7\u5566\u5566\u201d\u4e00\u7247\u54cd\u52a8\uff0c\u4f3c\u4e4e\u6709\u4e2a\u8336\u51e0\u7ffb\u5012\uff0c\u513f\u4e0a\u7684\u8336\u76cf\u5168\u90fd\u8dcc\u5f97\u7c89 \u788e\u3002 \u3000\u3000\u6309\u7740\uff0c\u7adf\u6709\u4e00\u4eba\u55c4\u58f0\u9053\uff1a\u201c\u4f4f\u2026\u2026\u2026\u4f4f\u624b\u3002\u201d \u3000\u3000\u6b64\u65f6\u6b64\u523b\uff0c\u695a\u7559\u9999\u5b9e\u5728\u60f3\u4e0d\u5230\u4f1a\u6709\u4eba\u6765\u6551\u4ed6\u3002 \u3000\u3000\u4ed6\u66f4\u8fde\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\u6551\u4ed6\u7684\u8fd9\u4eba\u662f\u8c01\u3002 \u3000\u3000\u8fd9\u662f\u95f4\u5f88\u7cbe\u81f4\u7684\u5c4b\u5b50\uff0c\u78a7\u5999\u7a97\u4e0a\uff0c\u82b1\u5f71\u6d6e\u52a8\uff0c\u7d2b\u7f57\u5e10\u5b50\uff0c\u9999\u6c14\u6c24\u6c32\uff0c\u5f77\u4f5b\u662f\u95f4\u5c11\u5973\u7684\u95fa \u623f\u3002 \u3000\u3000\u4f46\u5728\u80e1\u94c1\u82b1\u773c\u4e2d\uff0c\u8fd9\u53ea\u4e0d\u8fc7\u662f\u95f4\u7262\u623f\u800c\u5df2\u3002 \u3000\u3000\u5e73\u59d1\u5a18\u4e0d\u505c\u5728\u5c4b\u5b50\u91cc\u8d70\u52a8\u7740\uff0c\u5979\u90a3\u7ea4\u7ec6\u7684\u8170\u80a2\uff0c\u626d\u52a8\u5f97\u4ecd\u662f\u90a3\u9ebd\u7f8e\uff0c\u90a3\u4e30\u6ee1\u7684\u80f4\u4f53\uff0c\u4f3c \u4e4e\u5df2\u5feb\u5c06\u8584\u8584\u7684\u7ef8\u886b\u6da8\u7834\uff0c\u751a\u81f3\u8fde\u5185\u8863\u7684\u82b1\u7eb9\u90fd\u53ef\u4ee5\u770b\u5f97\u51fa\u6765\uff0c\u6709\u8fd9\u9ebd\u6837\u4e00\u4e2a\u5c11\u5973\u5728\u9762\u524d\u8d70 \u6765\u8d70\u53bb\uff0c\u5b9e\u5728\u662f\u7537\u4eba\u7684\u773c\u798f\u3002 \u3000\u3000\u4f46\u73b0\u5728\u80e1\u94c1\u82b1\u5374\u4e00\u70b9\u4e5f\u4e0d\u89c9\u5f97\u6109\u5feb\u4e86\uff0c\u4ed6\u672c\u6765\u60f3\u5728\u8fd9\u4e30\u6ee1\u7684\u80f4\u4f53\u4e0a\u634f\u4e00\u628a\uff0c\u73b0\u5728\u5374\u53ea\u60f3 \u5728\u5979\u8138\u4e0a\u6253\u4e00\u62f3\u3002 \u3000\u3000\u91cd\u91cd\u6253\u4e00\u62f3\uff0c\u6700\u597d\u5c06\u5979\u7259\u9f7f\u90fd\u6253\u6389\uff0c\u53eb\u5979\u518d\u4e5f\u4e0d\u80fd\u8bf4\u8c0e\u9a97\u4eba\uff0c\u53ea\u53ef\u60dc\u73b0\u5728\u4ed6\u5df2\u88ab\u56f0\u5f97\u50cf \u662f\u53ea\u7cbd\u5b50\u3002 \u3000\u3000\u4ed6\u53ea\u89c9\u5e73\u59d1\u5a18\u8d8a\u626d\u8d8a\u5389\u5bb3\uff0c\u626d\u5f97\u4ed6\u773c\u775b\u53d1\u82b1\uff0c\u5fcd\u4e0d\u4f4f\u5927\u58f0\u9053\uff1a\u201c\u4f60\u5c41\u80a1\u4e0a\u957f\u4e86\u75d4\u75ae\u9ebd\uff1f \u4e3a\u4ec0\u9ebd\u4e0d\u80fd\u5750\u4e0b\u6765\uff1f\u201d \u3000\u3000\u5e73\u59d1\u5a18\u7adf\u771f\u7684\u8d70\u5230\u4ed6\u9762\u524d\uff0c\u5750\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u5012\u672a\u60f3\u5230\u5979\u5982\u6b64\u542c\u8bdd\uff0c\u6014\u4e86\u534a\u664c\uff0c\u5927\u58f0\u53c8\u9053\uff1a\u201c\u6211\u53c8\u4e0d\u662f\u4f60\u8001\u5b50\uff0c\u4f60\u4e3a\u4ec0\u9ebd\u8fd9\u6837 \u542c\u8bdd\uff1f\u201d \u3000\u3000\u5e73\u59d1\u5a18\u975e\u4f46\u8fd8\u662f\u4e0d\u751f\u6c14\uff0c\u53cd\u800c\u5ae3\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u8ba4\u4e3a\u81ea\u5df1\u5df2\u7ecf\u5feb\u6b7b\u4e86\uff0c\u6240\u4ee5\u706b \u6c14\u624d\u8fd9\u9ebd\u5927\uff0c\u5176\u5b9e\u4f60\u6839\u672c\u7528\u4e0d\u7740\u53d1\u813e\u6c14\uff0c\u56e0\u4e3a\u6211\u4eec\u6839\u672c\u4e0d\u4f1a\u6740\u4f60\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u773c\u73e0\u5b50\u4e00\u8f6c\uff0c\u9053\uff1a\u201c\u65e2\u7136\u4e0d\u6740\u6211\uff0c\u4e3a\u4f55\u4e0d\u653e\u4e86\u6211\uff1f\u201d \u3000\u3000\u5e73\u59d1\u5a18\u9053\uff1a\u201c\u53ea\u8981\u90a3\u4f4d\u695a\u7559\u9999\u4e00\u6b7b\uff0c\u6211\u4eec\u7acb\u523b\u5c31\u653e\u4e86\u4f60\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u76b1\u4e86\u76b1\u7709\uff0c\u5e73\u59d1\u5a18\u5df2\u5fae\u7b11\u63a5\u9053\uff1a\u201c\u975e\u4f46\u8981\u653e\u4e86\u4f60\uff0c\u5c31\u8fde\u90a3\u56db\u4f4d\u59d1\u5a18\uff0c\u4e5f\u4f1a\u4e00\u9f50\u653e \u4e86\u7684\uff0c\u6240\u4ee5\u4f60\u73b0\u5728\u6700\u597d\u6c42\u8001\u5929\u8ba9\u695a\u7559\u9999\u5feb\u4e9b\u6b7b\uff0c\u4ed6\u6b7b\u5f97\u8d8a\u65e9\uff0c\u5bf9\u4f60\u4eec\u8d8a\u597d\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u51b7\u7b11\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u6211\u53ea\u6015\u8981\u7559\u5728\u8fd9\u91cc\u5403\u4f60\u4eec\u4e00\u8f88\u5b50\u4e86\u3002\u201d \u3000\u3000\u5e73\u59d1\u5a18\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u77aa\u7740\u5979\uff0c\u4eba\u5b54\u9053\uff1a\u201c\u544a\u8bc9\u597d\uff0c\u695a\u7559\u9999\u662f\u6c38\u8fdc\u6b7b\u4e0d\u4e86\u7684\uff0c\u73b0\u5728\u4f60\u8d76\u7d27\u5c06\u6211\u653e\u4e0b\uff0c\u7b97 \u4f60\u806a\u660e\uff0c\u5426\u5219\u7b49\u4ed6\u6765\u4e86\uff0c\u563f\u563f\u2026\u2026\u2026\u201d \u3000\u3000\u5e73\u59d1\u5a18\u5403\u5403\u7b11\u9053\uff1a\u201c\u54ce\u7ea6\uff1a\u6211\u597d\u5bb3\u6015\u5440\uff1a\u4f60\u53ea\u8981\u4e00\u5413\u6211\uff0c\u6211\u5c31\u6015\u6b7b\u4e86\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u4e5f\u9f87\u7259\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u73b0\u5728\u5f53\u7136\u4e0d\u6015\uff0c\u4f46\u7b49\u4ed6\u6765\u7684\u65f6\u5019\u2026\u2026\u2026\u201d \u3000\u3000\u7a81\u542c\u95e8\u5916\u4e00\u4eba\u8f7b\u8f7b\u5524\u9053\uff1a\u201c\u5e73\u59d1\u5a18\u3002\u201d \u3000\u3000\u5e73\u59d1\u5a18\u9053\uff1a\u201c\u8fdb\u6765\u2026\u2026\u2026\u4f60\u5df2\u53bb\u56de\u7980\u5c11\u5e84\u4e3b\u592b\u4eba\u4e86\u9ebd\uff1f\u5c11\u592b\u4eba\u8bf4\u4e86\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u8fdb\u6765\u7684\u6b63\u662f\u90a3\u9752\u8863\u5782\u9aeb\u7ae5\u5b50\uff0c\u8eac\u8eab\u9053\uff1a\u201c\u5c11\u5e84\u4e3b\u592b\u4eba\u53ea\u7b11\u4e86\u7b11\uff0c\u4ec0\u9ebd\u8bdd\u4e5f\u6ca1\u6709\u8bf4\u3002\u201d \u3000\u3000\u5e73\u59d1\u5a18\u779f\u4e86\u80e1\u94c1\u82b1\u4e00\u773c\uff0c\u53c8\u95ee\u9053\uff1a\u201c\u4f60\u53ef\u89c1\u5230\u4e86\u90a3\u4f4d\u695a\u9999\u5e05\uff1f\u201d \u3000\u3000\u90a3\u9752\u8863\u7ae5\u5b50\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u89c1\u5230\u4e86\uff0c\u4ed6\u679c\u7136\u662f\u4e2a\u5f88\u4f53\u9762\u7684\u7537\u4eba\uff0c\u81f3\u5c11\u6bd4\u8fd9\u4e00\u4f4d\u597d\u770b\u591a \u4e86\uff0c\u4e5f\u806a\u660e\u591a\u4e86\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u5fcd\u4e0d\u4f4f\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5c0f\u5b69\u5b50\u61c2\u5f97\u4e2a\u5c41\u3002\u201d \u3000\u3000\u5e73\u59d1\u5a18\u5927\u7b11\u9053\uff1a\u201c\u5c31\u56e0\u4e3a\u5c0f\u5b69\u5b50\u4e0d\u61c2\u4e8b\uff0c\u6240\u4ee5\u4ed6\u4eec\u8bf4\u7684\u624d\u662f\u771f\u8bdd\u3002\u201d \u3000\u3000\u90a3\u9752\u8863\u7ae5\u5b50\u5ffd\u53c8\u7b11\u9053\uff1a\u201c\u6211\u5e38\u542c\u522b\u4eba\u8bf4\u8fd9\u4f4d\u695a\u9999\u5e05\u5982\u4f55\u5982\u4f55\u4e86\u4e0d\u8d77\uff0c\u4f9d\u6211\u770b\u6765\uff0c\u4ed6\u9664\u4e86 \u957f\u5f97\u6bd4\u522b\u4eba\u597d\u770b\u4e00\u4e9b\u5916\uff0c\u522b\u7684\u4e5f\u7a00\u677e\u5e73\u5e38\u5f97\u5f88\uff0c\u6211\u65b9\u624d\u521a\u8d70\u8fdb\u53bb\u6ca1\u591a\u4e45\uff0c\u4ed6\u5c31\u88ab\u5c11\u5e84\u4e3b\u8e22\u4e86 \u4e00\u811a\uff0c\u8eba\u5728\u5730\u4e0a\uff0c\u8fde\u52a8\u90fd\u4e0d\u80fd\u52a8\u4e86\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u6012\u9053\uff1a\u201c\u4f60\u53ea\u6015\u662f\u6d3b\u89c1\u4e86\u9b3c\u3002\u201d \u3000\u3000\u90a3\u9752\u8863\u7ae5\u5b50\u7b11\u563b\u563b\u9053\uff1a\u201c\u4f60\u82e5\u8ba4\u4e3a\u6211\u5728\u9a97\u4eba\uff0c\u83ab\u8981\u76f8\u4fe1\u5c31\u662f\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u54ac\u7740\u7259\u5446\u4e86\u534a\u664c\uff0c\u7ec8\u65bc\u8fd8\u662f\u5fcd\u4e0d\u4f4f\u9053\uff1a\u201c\u6211\u867d\u7136\u4e0d\u76f8\u4fe1\uff0c\u4f60\u8bf4\u6765\u542c\u542c\u4e5f\u6ca1\u5173\u7cfb\uff0c \u53cd\u6b63\u6211\u6b63\u95f2\u5f97\u65e0\u804a\u3002\u201d \u3000\u3000\u90a3\u7ae5\u5b50\u7b11\u9053\uff1a\u201c\u4f60\u95f2\u5f97\u65e0\u804a\uff0c\u6211\u5374\u5fd9\u5f97\u5f88\uff0c\u8fd8\u6ca1\u529f\u592b\u8bf4\u7ed9\u4f60\u542c\u54e9\uff01\u201d\u4ed6\u5634\u91cc\u8bf4\u7740\u8bdd\uff0c\u7adf \u5df2\u8f6c\u8eab\u626c\u957f\u800c\u53bb\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u53c8\u6025\u53c8\u6c14\uff0c\u8fde\u8116\u5b50\u90fd\u7c97\u4e86\uff0c\u5374\u4e5f\u65e0\u6cd5\u53ef\u65bd\u3002 \u3000\u3000\u8c01\u77e5\u8fc7\u4e86\u534a\u664c\uff0c\u90a3\u7ae5\u5b50\u5ffd\u53c8\u63a2\u5934\u8fdb\u6765\uff0c\u671b\u7740\u4ed6\u7b11\uff1a\u201c\u4f60\u82e5\u771f\u7684\u60f3\u77e5\u9053\u4f60\u90a3\u670b\u53cb\u600e\u9ebd\u6837 \u4e86\uff0c\u6211\u5012\u6709\u4e2a\u6cd5\u5b50\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u8131\u53e3\u9053\uff1a\u201c\u4ec0\u9ebd\u6cd5\u5b50\uff1f\u201d \u3000\u3000\u90a3\u7ae5\u5b50\u60a0\u7136\u9053\uff1a\u201c\u4f60\u82e5\u80af\u9001\u70b9\u4e1c\u897f\u7ed9\u6211\uff0c\u6211\u5fc3\u91cc\u4e00\u9ad8\u5174\uff0c\u8bf4\u4e0d\u5b9a\u5c31\u4f1a\u8bf4\u7ed9\u4f60\u542c\u4e86\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u4f60\u8981\u6211\u9001\u4f60\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u90a3\u7ae5\u5b50\u773c\u73e0\u5b50\u4e00\u8f6c\uff0c\u9053\uff1a\u201c\u522b\u7684\u6211\u4e5f\u4e0d\u8981\uff0c\u6211\u53ea\u8981\u4f60\u8eab\u4e0a\u90a3\u4e2a\u94f6\u5323\u5b50\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u51b7\u7b11\u9053\uff1a\u201c\u67f3\u65e0\u7709\u679c\u7136\u6ca1\u6709\u5fd8\u8bb0\u6b64\u7269\uff0c\u5979\u5982\u4f55\u4e0d\u81ea\u5df1\u6765\u62ff\uff1f\u201d \u3000\u3000\u90a3\u7ae5\u5b50\u9053\uff1a\u201c\u5c11\u5976\u5976\u4f55\u5fc5\u4eb2\u81ea\u51fa\u9a6c\uff0c\u5c31\u7b97\u6211\uff0c\u73b0\u5728\u83ab\u8bf4\u53ea\u8981\u4f60\u8fd9\u6837\u4e1c\u897f\uff0c\u6211\u5c31\u7b97\u60f3\u8981\u4f60 \u8863\u670d\u88e4\u5b50\u5168\u90fd\u5265\u4e0b\u6765\uff0c\u4f60\u4e5f\u53ea\u6709\u4e7e\u77aa\u773c\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u7684\u773c\u775b\u679c\u7136\u77aa\u5f97\u6bd4\u9e21\u86cb\u8fdc\u5927\uff0c\u6012\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u6562\u3002\u201d \u3000\u3000\u90a3\u7ae5\u5b50\u7b11\u9053\uff1a\u201c\u6211\u53c8\u6709\u4ec0\u9ebd\u4e0d\u6562\uff0c\u53ea\u4e0d\u8fc7\u6211\u4eec\u674e\u5bb6\u7684\u4eba\uff0c\u4e00\u5411\u5f88\u6709\u89c4\u77e9\uff0c\u7edd\u4e0d\u80af\u968f\u4fbf\u8981 \u4eba\u5bb6\u4e1c\u897f\uff0c\u9664\u975e\u4f60\u5fc3\u7518\u60c5\u613f\u8981\u9001\u7ed9\u6211\u3002\u201d \u3000\u3000\u5e73\u59d1\u5a18\u5ae3\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u653e\u5fc3\uff0c\u80e1\u5927\u4fa0\u4e00\u5411\u6700\u6177\u6168\u4e0d\u8fc7\uff0c\u7edd\u4e0d\u4f1a\u820d\u4e0d\u5f97\u8fd9\u6837\u4e1c\u897f\u7684\uff0c \u4f55\u51b5\uff0c\u4ed6\u5634\u91cc\u867d\u4e0d\u8bf4\uff0c\u5fc3\u91cc\u5374\u5df2\u6025\u5f97\u8981\u547d\uff0c\u4f60\u82e5\u8fd8\u4e0d\u80af\u8bf4\u51fa\u90a3\u4f4d\u695a\u9999\u5e05\u73b0\u5728\u7684\u60c5\u51b5\uff0c\u4ed6\u8bf4\u4e0d \u5b9a\u771f\u4f1a\u88ab\u4f60\u6d3b\u6d3b\u6025\u6b7b\u4e86\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u867d\u7136\u706b\u5192\u53c1\u4e08\uff0c\u4f46\u4ed6\u786e\u5b9e\u6025\u7740\u60f3\u77e5\u9053\u695a\u7559\u9999\u7684\u6d88\u606f\uff0c\u8fd9\u6d88\u606f\u7eb5\u7136\u4e0d\u53ef\u9760\uff0c\u603b\u4e5f\u6bd4 \u6ca1\u6709\u6d88\u606f\u7684\u597d\u3002\u4ed6\u53ea\u6709\u6697\u4e2d\u53f9\u4e86\u53e3\u6c14\uff0c\u5634\u91cc\u5374\u5927\u58f0\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u80e1\u5927\u7237\u4e00\u5411\u5927\u65b9\u5f97\u5f88\uff0c\u4f60\u82e5 \u8981\uff0c\u5c31\u62ff\u53bb\u5427\uff01\u201d \u3000\u3000\u90a3\u7ae5\u5b50\u7acb\u523b\u4ece\u4ed6\u8eab\u4e0a\u638f\u51fa\u4e86\u90a3\u66b4\u96e8\u68a8\u82b1\u9489\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u662f\u4f60\u5fc3\u7518\u60c5\u613f\u8981\u9001\u7ed9\u6211\u7684\uff0c\u6211\u53ef \u6ca1\u6709\u5f3a\u8feb\u4f60\uff0c\u5bf9\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u5583\u5583\u9053\uff1a\u201c\u8fd9\u5c31\u53eb\u53c1\u5341\u5c81\u8001\u5a18\u5012\u7ef7\u6838\u513f\uff0c\u7b97\u6211\u6d3b\u8be5\u5012\u9709\u3002\u201d \u3000\u3000\u90a3\u7ae5\u5b50\u7b11\u9053\uff1a\u201c\u4f60\u8fd8\u7b97\u5012\u9709\u9ebd\uff1f\u548c\u4f60\u90a3\u670b\u53cb\u4e00\u6bd4\uff0c\u4f60\u7b80\u76f4\u8d70\u4e86\u5927\u8fd0\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u6025\u6025\u9053\uff1a\u201c\u4ed6\u2026\u2026\u2026\u4ed6\u7a76\u7adf\u600e\u9ebd\u6837\u4e86\uff1f\u201d \u3000\u3000\u90a3\u7ae5\u5b50\u9053\uff1a\u201c\u4ed6\u88ab\u5c11\u5e84\u4e3b\u4e00\u811a\u8e22\u5012\u5f8c\uff0c\u5c11\u5976\u5976\u7acb\u523b\u7a9c\u8fc7\u53bb\u4e00\u5251\u524c\u51fa\uff0c\u90a3\u4f4d\u9f0e\u9f0e\u5927\u540d\u7684\u695a \u9999\u5e05\uff0c\u7adf\u8fde\u95ea\u907f\u90fd\u4e0d\u80fd\u95ea\u907f\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u7eb5\u7136\u4e0d\u76f8\u4fe1\uff0c\u542c\u5230\u8fd9\u91cc\uff0c\u4e5f\u4e0d\u7981\u5931\u58f0\u60ca\u547c\u51fa\u6765\uff0c\u90a3\u7ae5\u5b50\u5374\u7b11\u4e86\u7b11\uff0c\u7f13\u7f13\u6309\u7740\u53c8 \u9053\uff1a\u201c\u4f46\u90a3\u4e94\u4f4d\u524d\u8f88\u5374\u8ba4\u4e3a\u5c11\u5976\u5976\u4e0d\u8be5\u6740\u4ed6\uff0c\u7adf\u4e00\u9f50\u51fa\u624b\u6321\u4f4f\u4e86\u5c11\u5976\u5976\u7684\u5251\u2026\u2026\u2026\u201d \u3000\u3000\u542c\u5230\u8fd9\u91cc\uff0c\u80e1\u94c1\u82b1\u53c8\u4e0d\u89c9\u957f\u957f\u677e\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u770b\u6765\u90a3\u4e94\u4eba\u679c\u7136\u4e0d\u5931\u4e3a\u540d\u5bb6\u98ce\u8303\u3002\u201d \u3000\u3000\u90a3\u7ae5\u5b50\u9053\uff1a\u201c\u4f60\u6b64\u523b\u5df2\u76f8\u4fe1\u6211\u8bf4\u7684\u4e0d\u5047\u9ebd\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u8fd8\u672a\u8bf4\u8bdd\uff0c\u5e73\u59d1\u5a18\u5df2\u7b11\u9053\uff1a\u201c\u4ed6\u5f53\u7136\u76f8\u4fe1\u4e86\uff0c\u56e0\u4e3a\u4e00\u4e2a\u4eba\u5bf9\u65bc\u597d\u6d88\u606f\uff0c\u603b\u662f\u6bd4\u8f83 \u5bb9\u6613\u76f8\u4fe1\u7684\u3002\u201d \u3000\u3000\u90a3\u7ae5\u5b50\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u6211\u518d\u8bf4\u4e0b\u53bb\uff0c\u4ed6\u53ea\u6015\u8fde\u4e00\u4e2a\u5b57\u4e5f\u4e0d\u4f1a\u76f8\u4fe1\u4e86\u3002\u201d \u3000\u3000\u5e73\u59d1\u5a18\u7728\u4e86\u7728\u773c\uff0c\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u90a3\u7ae5\u5b50\u9053\uff1a\u201c\u53ea\u56e0\u6211\u73b0\u5728\u518d\u8bf4\u4e0b\u53bb\uff0c\u5c31\u6ca1\u6709\u4e00\u53e5\u662f\u597d\u6d88\u606f\u4e86\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u55c4\u58f0\u9053\uff1a\u201c\u96be\u9053\u2026\u2026\u2026\u96be\u9053\u90a3\u4e94\u4eba\u53c8\u6539\u53d8\u4e86\u4e3b\u610f\uff1f\u201d \u3000\u3000\u90a3\u7ae5\u5b50\u9053\uff1a\u201c\u4ed6\u4eec\u4e94\u4eba\u867d\u6709\u4e9b\u5047\u9053\u5b66\uff0c\u4f46\u7a76\u7adf\u4e0d\u662f\u8001\u7cca\u6d82\uff0c\u8fd8\u5206\u5f97\u51fa\u4e8b\u60c5\u7684\u8f7b\u91cd\uff0c\u5c11\u5e84 \u4e3b\u5bf9\u4ed6\u4eec\u6653\u4ee5\u5927\u4e49\u5f8c\uff0c\u4ed6\u4eec\u4e94\u4eba\u7ec8\u65bc\u4e00\u4e2a\u4e2a\u90fd\u64a4\u4e86\u624b\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u867d\u7136\u60f3\u4e0d\u4fe1\u4ed6\u7684\u8bdd\uff0c\u5374\u53c8\u4e0d\u80fd\u4e0d\u4fe1\uff0c\u5fcd\u4e0d\u4f4f\u9053\uff1a\u201c\u5f8c\u6765\u5462\uff1f\u201d \u3000\u3000\u90a3\u7ae5\u5b50\u9053\uff1a\u201c\u5f8c\u6765\u6211\u5c31\u8d70\u4e86\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u5927\u53eb\u8d77\u6765\uff0c\u901a\uff1a\u201c\u4f60\u8d70\u4e86\uff1f\u4f60\u4e3a\u4f55\u8981\u8d70\uff1f\u201d \u3000\u3000\u90a3\u7ae5\u5b50\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u6700\u6015\u770b\u5230\u6b7b\u4eba\uff0c\u6211\u770b\u5230\u5c11\u5976\u5976\u7684\u5251\u5df2\u523a\u5165\u4ed6\u7684\u80f8\u819b\uff0c\u5c31\u7acb \u523b\u6084\u6084\u6e9c\u4e86\u51fa\u6765\uff0c\u8fd9\u662f\u574f\u6d88\u606f\uff0c\u6211\u77e5\u9053\u4f60\u4e0d\u4f1a\u76f8\u4fe1\u7684\uff0c\u4f46\u4f60\u8fdf\u65e9\u603b\u6709\u76f8\u4fe1\u7684\u65f6\u5019\uff0c\u6211\u4e5f\u6839\u672c \u4e0d\u5fc5\u8bf4\u8c0e\u8bdd\u6765\u5413\u4f60\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u53ea\u89c9\u5168\u8eab\u90fd\u53d1\u4e86\u9ebb\uff0c\u6ee1\u5934\u5927\u6c57\uff0c\u5982\u96e8\u800c\u843d\u3002 \u3000\u3000\u90a3\u7ae5\u5b50\u7b11\u9053\uff1a\u201c\u53ea\u4e0d\u8fc7\u6211\u8d70\u4e86\u4e4b\u5f8c\uff0c\u4e5f\u8bb8\u4f1a\u7a81\u7136\u94bb\u51fa\u6765\u4e00\u4e2a\u4eba\u53bb\u6551\u4ed6\uff0c\u6211\u65e9\u5df2\u542c\u8bf4\u8fc7\u695a \u7559\u9999\u7684\u670b\u53cb\u4e0d\u5c11\uff0c\u662f\u9ebd\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u5927\u58f0\u9053\uff1a\u201c\u5f53\u7136\u4f1a\u6709\u4eba\u53bb\u6551\u4ed6\u7684\uff0c\u5f53\u7136\u4f1a\u6709\u4eba\u53bb\u6551\u4ed6\u7684\uff0c\u5f53\u7136\u2026\u2026\u2026\u201d\u4ed6\u5c06\u8fd9\u53e5 \u8bdd\u4e00\u8fde\u8bf4\u4e86\u597d\u51e0\u904d\uff0c\u53ea\u56e0\u4ed6\u6015\u8fde\u81ea\u5df1\u90fd\u4e0d\u4f1a\u76f8\u4fe1\uff0c\u6240\u4ee5\u5c31\u591a\u8bf4\u51e0\u904d\uff0c\u6765\u52a0\u5f3a\u4fe1\u5fc3\u3002 \u3000\u3000\u600e\u5948\u4ed6\u8bf4\u4e86\u4e03\u516b\u904d\u5f8c\uff0c\u81ea\u5df1\u8fd8\u662f\u65e0\u6cd5\u76f8\u4fe1\u3002 \u3000\u3000\u90a3\u7ae5\u5b50\u60a0\u7136\u9053\uff1a\u201c\u4f60\u60f3\uff0c\u6709\u4ec0\u9ebd\u4eba\u4f1a\u6765\u6551\u4ed6\u5462\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u6551\u4ed6\u7684\u4eba\u591a\u5f97\u5f88\uff0c\u7b80\u76f4\u592a\u591a\u4e86\u3002\u201d \u3000\u3000\u90a3\u7ae5\u5b50\u9053\uff1a\u201c\u54e6\uff01\u4f60\u4e14\u8bf4\u4e24\u4e2a\u6765\u542c\u542c\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u8b6c\u5982\u8bf4\uff0c\u8b6c\u5982\u8bf4\uff0c\u4e2d\u539f\u4e00\u70b9\u7ea2\uff0c\u201c\u4e07\u91cc\u72ec\u884c\u201d\u6234\u8001\u524d\u8f88\uff0c\u5c11\u6797\u5357\u5b97\u638c\u95e8\u5929 \u5cf0\u5927\u5e08\uff0c\u8fd8\u6709\u6211\u4eec\u7684\u8001\u670b\u53cb\u59ec\u51b0\u96c1\uff0c\u54c8\u54c8\uff01\u4f60\u603b\u8be5\u542c\u8bf4\u8fc7\u8fd9\u4e9b\u4eba\u7684\u540d\u5b57\uff1f\u201d \u3000\u3000\u4ed6\u867d\u7136\u62da\u547d\u5728\u81ea\u5df1\u5b89\u6170\u81ea\u5df1\uff0c\u4f46\u4e5f\u77e5\u9053\u8fd9\u4e9b\u4eba\u7edd\u4e0d\u53ef\u80fd\u4f1a\u5ffd\u7136\u8d76\u6765\u7684\uff0c\u4f55\u51b5\u4ed6\u4eec\u5c31\u7b97\u6765 \u4e86\uff0c\u4e5f\u672a\u5fc5\u80fd\u6551\u5f97\u4e86\u695a\u7559\u9999\u3002 \u3000\u3000\u90a3\u7ae5\u5b50\u773c\u73e0\u5b50\u53c8\u4e00\u8f6c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u597d\u50cf\u770b\u5230\u6765\u4e86\u4f4d\u8001\u548c\u5c1a\uff0c\u597d\u50cf\u5c31\u662f\u5929\u5cf0\u5927\u5e08\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u5927\u559c\u9053\uff1a\u201c\u4f60\u771f\u7684\u77a7\u89c1\u4e86\u9ebd\uff1f\u201d \u3000\u3000\u90a3\u7ae5\u5b50\u9053\uff1a\u201c\u55ef\uff01\u53ef\u662f\u6211\u5f8c\u6765\u4ed4\u7ec6\u4e00\u770b\uff0c\u624d\u77e5\u9053\u90a3\u4e0d\u662f\u4e2a\u548c\u5c1a\uff0c\u53ea\u4e0d\u8fc7\u662f\u4e2a\u79c3\u5b50\u800c \u5df2\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u7b80\u76f4\u5feb\u6c14\u75af\u4e86\uff0c\u4e5f\u5feb\u6025\u75af\u4e86\u3002 \u3000\u3000\u90a3\u7ae5\u5b50\u7b11\u563b\u563b\u9053\uff1a\u201c\u4f60\u83ab\u751f\u6c14\uff0c\u6211\u4e5f\u4e0d\u662f\u5b58\u5fc3\u60f3\u6c14\u4f60\uff0c\u53ea\u4e0d\u8fc7\u4f60\u65e2\u7136\u559c\u6b22\u81ea\u5df1\u9a97\u81ea\u5df1\uff0c \u6211\u4e5f\u53ea\u597d\u5e2e\u4f60\u7684\u5fd9\u6765\u9a97\u9a97\u4f60\u4e86\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u5927\u543c\u9053\uff1a\u201c\u4f60\u8ba4\u4e3a\u5f88\u5f97\u610f\u9ebd\uff0c\u544a\u8bc9\u4f60\uff0c\u4f60\u4eec\u82e5\u771f\u6740\u4e86\u695a\u7559\u9999\uff0c\u4e0d\u51fa\u534a\u4e2a\u6708\uff0c\u8fd9\u62e5 \u7fe0\u5c71\u5e84\u5c31\u8981\u88ab\u4eba\u5937\u4e3a\u5e73\u5730\u3002\u201d \u3000\u3000\u4ed6\u7684\u8bdd\u8fd8\u6ca1\u6709\u8bf4\u5b8c\uff0c\u7a81\u542c\u5c4b\u5b50\u91cc\u54cd\u8d77\u4e86\u4e00\u7247\u201c\u683c\u90ce\u683c\u90ce\u201d\u7684\u58f0\u97f3\uff0c\u542c\u6765\u5f77\u4f5b\u662f\u94c1\u5668\u6572 \u6253\u3002 \u3000\u3000\u4ed4\u7ec6\u518d\u4e00\u542c\uff0c\u8fd9\u58f0\u97f3\u7adf\u4f3c\u81ea\u5730\u4e0b\u9762\u4f20\u4e0a\u6765\u7684\u3002 \u3000\u3000\u90a3\u7ae5\u5b50\u671b\u7740\u5e73\u59d1\u5a18\u7b11\u9053\uff1a\u201c\u83ab\u975e\u662f\u90a3\u53ea\u6bcd\u8001\u864e\u53c8\u5728\u53d1\u5a01\u4e86\u9ebd\uff1f\u201d \u3000\u3000\u5e73\u59d1\u5a18\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5979\u8fd9\u662f\u5728\u53eb\u4eba\uff0c\u6211\u82e5\u4e0d\u4e0b\u53bb\uff0c\u5979\u5c31\u8981\u4e00\u76f4\u6572\u4e2a\u4e0d\u505c\uff0c\u76f4\u5230\u628a\u4eba \u90fd\u5435\u6b7b\u4e3a\u6b62\u3002\u201d\u90a3\u7ae5\u5b50\u7b11\u9053\uff1a\u201c\u4f60\u7ed9\u5979\u70b9\u989c\u8272\u770b\uff0c\u5979\u5c31\u4f1a\u4e56\u4e56\u7684\u542c\u8bdd\u4e86\u201d\u5e73\u59d1\u5a18\u9053\uff1a\u201c\u6211\u65e9 \u5c31\u60f3\u7ed9\u5979\u4eec\u770b\u4e86\uff0c\u5c11\u5976\u5976\u5374\u504f\u504f\u8981\u6211\u5ba2\u6c14\u6c14\u7684\u5bf9\u5979\u4eec\uff0c\u5e78\u597d\u73b0\u5728\u90a3\u59d3\u695a\u7684\u5df2\u5b8c\u4e86\uff0c\u6211\u603b\u7b97\u4e5f \u53ef\u4ee5\u8131\u79bb\u82e6\u6d77\u4e86\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u773c\u775b\u53c8\u77aa\u4e86\u8d77\u6765\uff0c\u5927\u58f0\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u53ef\u662f\u82cf\u59d1\u5a18\u5979\u4eec\uff1f\u201d \u3000\u3000\u5e73\u59d1\u5a18\u773c\u6ce2\u6d41\u52a8\uff0c\u5ffd\u7136\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u60f3\u77a7\u77a7\u5979\u4eec\u9ebd\uff1f\u597d\uff0c\u6211\u73b0\u5728\u5c31\u5e26\u4f60\u53bb\uff0c\u6211\u770b\u4f60\u548c \u90a3\u53ea\u6bcd\u8001\u864e\u5012\u4e70\u53ef\u4ee5\u7b97\u662f\u5929\u751f\u7684\u4e00\u5bf9\u3002\u201d \u3000\u3000\u5979\u5c06\u5899\u4e0a\u6302\u7740\u7684\u663c\u8f74\u4e00\u6273\uff0c\u663c\u5c31\u5377\u4e86\u8d77\u6765\uff0c\u9732\u51fa\u6761\u5730\u9053\uff0c\u8d70\u4e0b\u51e0\u7ea7\u77f3\u9636\uff0c\u5c31\u662f\u95f4\u88c5\u7740\u94c1 \u6805\u7684\u5730\u5ba4\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u4e00\u5230\u4e86\u4e0b\u9762\uff0c\u5c31\u77a7\u89c1\u53c1\u53ea\u4e4c\u9f9f\u3002 \u3000\u3000\u8fd9\u53c1\u53ea\u4e4c\u9f9f\u662f\u7528\u5927\u7b14\u6d53\u58a8\u753b\u5728\u8fce\u9762\u7684\u5899\u4e0a\u7684\uff0c\u6700\u5927\u7684\u4e00\u53ea\uff0c\u7adf\u663c\u5f97\u6bd4\u5706\u684c\u5b50\u8fd8\u5927\u4e9b\u3002 \u3000\u3000\u6700\u5999\u7684\u662f\uff0c\u8fd9\u4e4c\u9f9f\u5934\u4e0a\u8fd8\u6709\u80e1\u5b50\u3002 \u3000\u3000\u53e6\u5916\u4e24\u53ea\u5c31\u753b\u5f97\u6bd4\u8f83\u5c0f\u4e9b\uff0c\u65c1\u8fb9\u5206\u522b\u5199\u7740\uff1a\u201c\u674e\u89c2\u9c7c\u8096\u50cf\u3001\u674e\u7389\u51fd\u8096\u50cf\uff0c\u67f3\u65e0\u7709\u8096\u50cf\u3002 \u5cad\u5357\u5b8b\u751c\u513f\u620f\u58a8\u3002\u4e2d\u539f\u674e\u7ea2\u8896\u9898\u5b57\u3002\u201d\u8fd9\u5e45\u5927\u4e2d\u5802\u65c1\u8fb9\uff0c\u8fd8\u6709\u526f\u201c\u5bf9\u8054\u201d\u201c\u513f\u5b50\u662f\u8870\u4ed4\uff0c\u8001 \u5b50\u662f\u8870\u516c\u3002\u5ab3\u5987\u662f\u8870\u5a46\uff0c\u4e00\u95e8\u53c1\u8870\u4eba\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u6b64\u523b\u82e5\u4e0d\u662f\u5fc3\u4e8b\u91cd\u91cd\uff0c\u53ea\u6015\u65e9\u5df2\u770b\u5f97\u7b11\u51fa\u58f0\u6765\u3002\u7136\u5f8c\uff0c\u4ed6\u624d\u77a7\u89c1\u56db\u4eba\u3002 \u3000\u3000\u56db\u4e2a\u90fd\u662f\u5e74\u8f7b\u6f02\u4eae\u7684\u7edd\u8272\u7f8e\u4eba\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u6700\u5148\u770b\u5230\u7684\u4e00\u4e2a\u68b3\u7740\u4e24\u6761\u5927\u8fab\u5b50\uff0c\u6de1\u8910\u8272\u7684\u74dc\u5b50\u8138\u4e0a\uff0c\u914d\u7740\u53cc\u53c8\u7075\u6d3b\u3001\u53c8\u4fcf\u76ae\u7684 \u5927\u773c\u775b\u3002 \u3000\u3000\u6b64\u523b\u5979\u4e0a\u7528\u4e00\u4e2a\u94dc\u8138\u76c6\u5728\u94c1\u6805\u4e0a\u6572\u4e2a\u4e0d\u505c\u3002 \u3000\u3000\u5979\u65c1\u8fb9\u7684\u4e00\u4e2a\u7a7f\u7740\u4ef6\u70c8\u706b\u822c\u7684\u9c9c\u7ea2\u8863\u88f3\uff0c\u76ae\u80a4\u5374\u81ea\u5f97\u50cf\u662f\u767d\u7389\uff0c\u53c8\u5ae9\u5f97\u50cf\u662f\u53ef\u4ee5\u5439\u5f39\u5f97 \u7834\u3002 \u3000\u3000\u53e6\u5916\u7ea6\u4e24\u4e2a\u4eba\uff0c\u5374\u5728\u90a3\u8fb9\u4e0b\u68cb\uff0c\u8fd9\u8fb9\u867d\u7136\u5df2\u5435\u7ffb\u4e86\u5929\uff0c\u90a3\u8fb9\u5374\u50cf\u662f\u4e00\u70b9\u58f0\u97f3\u4e5f\u6ca1\u6709\u542c \u89c1\u3002 \u3000\u3000\u5de6\u9762\u7684\u4e00\u4eba\u53c8\u6e29\u67d4\uff0c\u53c8\u6587\u9759\uff0c\u677e\u677e\u7684\u53d1\u9afb\uff0c\u6e05\u6f88\u7684\u773c\u6ce2\uff0c\u770b\u6765\u5c31\u50cf\u662f\u5929\u4e0a\u7684\u4ed9\u5b50\uff0c\u4eba\u5df2 \u4e0d\u98df\u4eba\u95f4\u70df\u706b\u3002 \u3000\u3000\u53f3\u9762\u7684\u4e00\u4eba\u5982\u6625\u5929\u7684\u6843\u82b1\uff0c\u5374\u51b7\u5982\u51ac\u5929\u7684\u575a\u51b0\uff0c\u60e8\u767d\u7684\u8138\u4e0a\uff0c\u6709\u4e00\u53cc\u5982\u6d77\u6c34\u822c\u6df1\u9083\u7684\u773c \u775b\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u5583\u5583\u9053\uff1a\u201c\u6211\u603b\u7b97\u89c1\u5230\u5979\u4eec\u4e86\uff0c\u53ea\u53ef\u60dc\u5df2\u7ecf\u592a\u8fdf\u4e86\u4e9b\u3002\u201d \u3000\u3000\u90a3\u4f4d\u5927\u8fab\u5b50\u7684\u5c11\u5973\u89c1\u5230\u5e73\u59d1\u5a18\uff0c\u5c31\u5927\u7b11\u9053\uff1a\u201c\u8870\u5973\uff0c\u4f60\u6761\u817f\u65ad\u4f50\u4e5c\u4e5f\uff1f\u70b9\u89e3\u5230\u4e1c\u5bb6\u81f3\u843d \u6765\u3002\u201d \u3000\u3000\u5e73\u59d1\u5a18\u5fae\u7b11\u9053\uff1a\u201c\u6211\u4e0d\u662f\u8870\u5973\uff0c\u4f60\u7684\u8bdd\u6211\u4e5f\u542c\u4e0d\u61c2\u3002\u201d \u3000\u3000\u90a3\u5c11\u5973\u5927\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u61c2\uff1f\u4f60\u4e0d\u61c2\u70b9\u77e5\u6211\u53eb\u4f60\u505a\u8870\u5973\uff1f\u201d\u5979\u8138\u4e0a\u7684\u8868\u60c5\u5b9a\u5b50\u53d8\u4e07\u5316\uff0c\u4e30 \u5bcc\u5f97\u5f88\uff0c\u660e\u660e\u8fd8\u5728\u7b11\u7740\uff0c\u5ffd\u7136\u95f4\u5c31\u677f\u8d77\u4e86\u8138\uff0c\u5389\u58f0\u9053\uff1a\u201c\u6211\u95ee\u4f60\uff0c\u4f60\u4eec\u5bb6\u7684\u4e3b\u4eba\u90fd\u6b7b\u5149\u4e86 \u9ebd\uff1f\u70b9\u89e3\u5230\u73b0\u5728\u8fd8\u4e0d\u6765\u8ddf\u6211\u4eec\u8bf4\u8bdd\u3002\u201d \u3000\u3000\u5979\u8bf4\u7684\u201c\u5b98\u8bdd\u201d\u4e2d\u867d\u5939\u7740\u4e00\u4e24\u53e5\u5e7f\u4e1c\u8154\uff0c\u5e73\u59d1\u5a18\u603b\u7b97\u80fd\u542c\u61c2\u4e86\uff0c\u53ef\u662f\u5979\u8fd8\u672a\u8bf4\u8bdd\uff0c\u90a3\u7ea2 \u8863\u5c11\u5973\u5ffd\u7136\u77aa\u5927\u4e86\u773c\u775b\uff0c\u5931\u58f0\u9053\uff1a\u201c\u80e1\u2026\u2026\u2026\u80e1\u2026\u2026\u2026\u4f60\u662f\u4e0d\u662f\u59d3\u80e1\uff1f\u201d \u7b2c\u5341\u516d\u7ae0\u3000\u9519\u7efc\u590d\u6742>> \u53e4\u9f99\u300a\u695a\u7559\u9999\u7cfb\u5217\xb7\u753b\u7709\u9e1f\u300b \u7b2c\u5341\u516d\u7ae0\u3000\u9519\u7efc\u590d\u6742 \u3000\u3000\u80e1\u94c1\u82b1\u82e6\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u6b63\u662f\u80e1\u94c1\u82b1\uff0c\u60f3\u4e0d\u5230\u4f60\u5c45\u7136\u8fd8\u8ba4\u5f97\u6211\u3002\u201d \u3000\u3000\u4ed6\u521a\u8bf4\u51fa\u81ea\u5df1\u7684\u540d\u5b57\uff0c\u90a3\u7ea4\u5f31\u7684\u5c11\u5973\u4e5f\u7acb\u523b\u653e\u4e0b\u68cb\u5b50\uff0c\u970d\u7136\u7ad9\u4e86\u8d77\u6765\uff0c\u53c1\u4e2a\u4eba\u4e00\u9f50\u77aa\u7740 \u4ed6\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6211\u4e5f\u77e5\u9053\u4f60\u662f\u82cf\u84c9\u84c9\uff0c\u4f60\u662f\u674e\u7ea2\u8896\uff0c\u4f60\u662f\u5b8b\u751c\u513f\uff0c\u3001\u4ee5\u524d\u770b\u5230 \u4f60\u4eec\u65f6\uff0c\u4f60\u4eec\u8fd8\u662f\u5c0f\u5b69\u5b50\uff0c\u60f3\u4e0d\u5230\u73b0\u5728\u5df2\u957f\u5f97\u8fd9\u9ebd\u5927\u4e86\u3002\u201d \u3000\u3000\u674e\u7ea2\u8896\u5ae3\u7136\u9053\uff1a\u201c\u6bcf\u4e2a\u4eba\u90fd\u4f1a\u957f\u5927\u7684\uff0c\u662f\u9ebd\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u53f9\u9053\uff1a\u201c\u6211\u65e9\u5c31\u60f3\u770b\u770b\u4f60\u4eec\u4e86\uff0c\u53ea\u53ef\u60dc\u73b0\u5728\u8fd9\u65f6\u5019\u771f\u4e0d\u597d\uff0c\u8fd9\u5730\u65b9\u66f4\u574f\u3002\u201d \u3000\u3000\u5e73\u59d1\u5a18\u5c06\u4ed6\u653e\u5728\u94c1\u6805\u524d\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u8001\u670b\u53cb\u89c1\u9762\uff0c\u591a\u804a\u804a\u5427\uff01\u201d \u3000\u3000\u5979\u5634\u91cc\u8bf4\u7740\u8bdd\uff0c\u7528\u811a\u5c16\u5728\u5730\u4e0a\u8f7b\u8f7b\u4e00\u70b9\uff0c\u90a3\u5757\u77f3\u677f\u5c31\u5ffd\u7136\u50cf\u7fd8\u7fd8\u677f\u4f3c\u7684\u4e00\u7ffb\uff0c\u80e1\u94c1\u82b1\u7684 \u4eba\u5c31\u50cf\u7403\u4e00\u822c\u6ed1\u5230\u94c1\u6805\u91cc\u53bb\u3002 \u3000\u3000\u674e\u7ea2\u8896\u548c\u5b8b\u751c\u513f\u62a2\u7740\u5c06\u4ed6\u6276\u4e86\u8d77\u6765\uff0c\u4e3a\u4ed6\u89e3\u5f00\u8eab\u4e0a\u56f0\u7740\u7684\u725b\u7b4b\uff0c\u4e24\u4eba\u4e00\u5431\u5431\u55b3\u55b3\u7684\u95ee \u9053\uff1a\u201c\u4f60\u600e\u4f1a\u4e5f\u5230\u8fd9\u91cc\u6765\u4e86\u5462\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u82e6\u7b11\u9053\uff1a\u201c\u6211\u4e5f\u6b63\u5728\u60f3\u95ee\u4f60\u4eec\u662f\u600e\u4f1a\u5230\u8fd9\u91cc\u6765\u7684\uff1f\u201d \u3000\u3000\u5b8b\u751c\u513f\u62a2\u7740\u9053\uff1a\u201c\u6211\u68af\u53bb\u4f50\u6c99\u6f20\uff0c\u7747\u89c1\u5404\u5339\u9a6c\u7ffb\u9ece\uff0c\u4ee5\u4e3a\u2026\u2026\u2026\u201d \u3000\u3000\u5979\u5431\u5431\u55b3\u55b3\u8bf4\u4e86\u4e00\u5927\u5806\uff0c\u5ffd\u7136\u505c\u4f4f\uff0c\u53f9\u9053\uff1a\u201c\u6211\u8bf4\u8bdd\u4f60\u6050\u6015\u4e5f\u4e0d\u61c2\uff0c\u8fd8\u662f\u8ba9\u5979\u4eec\u8bf4 \u5427\uff01\u201d \u3000\u3000\u674e\u7ea2\u8896\u9053\uff1a\u201c\u957f\u8bdd\u77ed\u8bf4\uff0c\u603b\u4e4b\u6211\u4eec\u5230\u6c99\u6f20\u53bb\u73a9\u4e86\u4e00\u8d75\uff0c\u5c31\u56de\u6765\u627e\u695a\u7559\u9999\uff0c\u5374\u5728\u534a\u8def\u4e0a\u9047 \u89c1\u674e\u7389\u51fd\u3001\u67f3\u65e0\u7709\u592b\u59bb\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u4f60\u4eec\u53ef\u8ba4\u5f97\u8fd9\u592b\u59bb\u4e24\u4eba\u3002\u201d \u3000\u3000\u674e\u7ea2\u8896\u53f9\u9053\uff1a\u201c\u8c01\u8ba4\u5f97\u4ed6\u4eec\u5440\uff01\u53ea\u4e0d\u8fc7\u90a3\u5929\u6211\u4eec\u5230\u5feb\u610f\u697c\u53bb\u627e\u5c0f\u5b5f\u5c1d\u6253\u542c\u6d88\u606f\u7684\u65f6\u5019\uff0c \u4ed6\u4eec\u6070\u5de7\u4e5f\u5728\u90a3\u91cc\u3002\u201d \u3000\u3000\u80e1\u94c1\u5316\u6697\u6697\u53f9\u9053\uff1a\u201c\u4ed6\u4eec\u53ea\u6015\u5e76\u4e0d\u662f\u6070\u5de7\u5728\u90a3\u91cc\uff0c\u800c\u662f\u6545\u610f\u5728\u90a3\u91cc\u7b49\u7740\u4f60\u4eec\u7684\u3002\u201d \u3000\u3000\u674e\u7ea2\u8896\u9053\uff1a\u201c\u6211\u4eec\u672c\u6765\u5c31\u89c9\u5f97\u8fd9\u592b\u59bb\u4e24\u4eba\u770b\u6765\u4eba\u6ee1\u4e0d\u9519\u7684\u6837\u5b50\uff0c\u53c8\u542c\u8bf4\u4ed6\u4eec\u662f\u5f88\u6709\u540d\u7684 \u4e16\u5bb6\u5b50\u5f1f\uff0c\u81ea\u7136\u5c31\u4e0d\u4f1a\u5bf9\u4ed6\u4eec\u6709\u63d0\u9632\u4e4b\u5fc3\u3002\u201d\u5979\u5ffd\u7136\u505c\u4e0b\u6765\uff0c\u671b\u7740\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u4f60\u82e5\u4e0d\u77e5\u9053 \u4ed6\u4eec\u7684\u4e3a\u4eba\uff0c\u4f60\u4f1a\u5bf9\u4ed6\u4eec\u6709\u63d0\u9632\u4e4b\u5fc3\u9ebd\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u82e6\u7b11\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u4f1a\uff0c\u53ea\u56e0\u4e3a\u6211\u4eec\u90fd\u6ca1\u6709\u695a\u7559\u9999\u806a\u660e\uff0c\u4e5f\u6ca1\u6709\u4ed6\u90a3\u9ebd\u4ed4\u7ec6\u3002\u201d \u3000\u3000\u674e\u7ea2\u8896\u9053\uff1a\u201c\u5c31\u56e0\u4e3a\u8fd9\u7f18\u6545\uff0c\u6240\u4ee5\u4ed6\u8981\u548c\u6211\u4eec\u4e00\u8def\u540c\u884c\uff0c\u6211\u4eec\u5c31\u7b54\u5e94\u4e86\uff0c\u8c01\u77e5\u4ed6\u4eec\u7adf\u5728 \u6211\u4eec\u8336\u58f6\u91cc\u5077\u5077\u653e\u4e86\u8ff7\u836f\uff0c\u7b49\u6211\u4eec\u9192\u6765\u65f6\uff0c\u5df2\u88ab\u9001\u5230\u8fd9\u91cc\uff0c\u6211\u5b9e\u5728\u60f3\u4e0d\u5230\u50cf\u4ed6\u4eec\u8fd9\u6837\u7684\u4e16\u5bb6 \u5b50\u5f1f\uff0c\u4e5f\u4f1a\u8981\u5982\u6b64\u5351\u9119\u65e0\u803b\u7684\u624b\u6bb5\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u53f9\u9053\uff1a\u201c\u82e5\u6362\u4e86\u6211\uff0c\u6211\u4e5f\u60f3\u4e0d\u5230\u7684\u3002\u201d \u3000\u3000\u674e\u7ea2\u8896\u9053\uff1a\u201c\u6700\u5947\u602a\u7684\u662f\uff0c\u6211\u4eec\u5230\u73b0\u5728\u4e3a\u4e0a\uff0c\u8fd8\u4e0d\u77e5\u9053\u4ed6\u4eec\u7a76\u7adf\u5b58\u7684\u662f\u4ec0\u9ebd\u5fc3\uff0c\u53ea\u56e0\u6211 \u4eec\u88ab\u5173\u5728\u8fd9\u91cc\u4e4b\u5f8c\uff0c\u7adf\u4e00\u76f4\u90fd\u6ca1\u6709\u89c1\u8fc7\u4ed6\u4eec\u3002\u201d\u5979\u6307\u7740\u5b8b\u751c\u513f\u53c8\u9053\uff1a\u201c\u8fd9\u5c0f\u8868\u867d\u7136\u5929\u5929\u9a82\uff0c \u5929\u5929\u5435\uff0c\u4f46\u65e0\u8bba\u5979\u600e\u9ebd\u9a82\uff0c\u674e\u5bb6\u7684\u4eba\u7adf\u597d\u50cf\u5168\u90fd\u6b7b\u5149\u4e86\uff0c\u8fde\u4e00\u4e2a\u90fd\u4e0d\u80af\u9732\u9762\uff0c\u6211\u4eec\u6c14\u6781\u4e86\uff0c \u5c31\u5728\u5899\u4e0a\u663c\u4e86\u53c1\u4e2a\u5927\u4e4c\u9f9f\uff0c\u8c01\u77e5\u4ed6\u4eec\u7adf\u50cf\u662f\u771f\u7684\u53d8\u6210\u4e86\u7f29\u5934\u4e4c\u9f9f\uff0c\u8eb2\u7740\u4e0d\u6562\u89c1\u4eba\u3002\u201d\u5979\u4e5f\u53f9 \u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u60f3\uff0c\u4ed6\u4eec\u8fd9\u7a76\u7adf\u662f\u5728\u6253\u4ec0\u9ebd\u4e3b\u610f\u5462\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u6ee1\u8179\u82e6\u6c34\uff0c\u6b63\u4e0d\u77e5\u8be5\u5982\u4f55\u56de\u7b54\u3002 \u3000\u3000\u82cf\u84c9\u84c9\u5ffd\u7136\u9053\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u5df2\u89c1\u8fc7\u695a\u7559\u9999\u4e86\uff1f\u201d \u3000\u3000\u5979\u77ac\u4e5f\u4e0d\u77ac\u7684\u77aa\u7740\u4ed6\uff0c\u80e1\u94c1\u82b1\u53ea\u89c9\u5979\u90a3\u53cc\u6e29\u67d4\u7684\u773c\u6ce2\uff0c\u5ffd\u7136\u53d8\u5f97\u65e0\u6bd4\u660e\u4eae\uff0c\u7adf\u4f7f\u4eba\u4e0d\u80fd \u5728\u5979\u9762\u524d\u8bf4\u8c0e\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u53ea\u6709\u70b9\u4e86\u70b9\u5934\uff0c\u9053\uff1a\u201c\u6211\u5df2\u89c1\u8fc7\u4e86\u4ed6\u3002\u201d \u3000\u3000\u82cf\u84c9\u84c9\u9053\uff1a\u201c\u4ed6\u73b0\u5728\u7a76\u7adf\u5728\u4ec0\u9ebd\u5730\u65b9\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u5782\u4e0b\u5934\uff0c\u907f\u5f00\u5979\u7684\u76ee\u5149\uff0c\u8bb7\u8bb7\u9053\uff1a\u201c\u6211\u2026\u2026\u2026\u6211\u4e5f\u4e0d\u5927\u6e05\u695a\u3002\u201d \u3000\u3000\u82cf\u84c9\u84c9\u8d70\u5230\u4ed6\u9762\u524d\uff0c\u4e00\u5b57\u5b57\u9053\uff1a\u201c\u4f60\u4e00\u5b9a\u77e5\u9053\u7684\uff0c\u6211\u5e0c\u671b\u4f60\u83ab\u8981\u7792\u7740\u6211\u4eec\uff0c\u65e0\u8bba\u4ed6\u53d1\u751f \u4e86\u4ec0\u9ebd\u4e8b\uff0c\u90fd\u5e0c\u671b\u4f60\u544a\u8bc9\u6211\u4eec\uff0c\u56e0\u4e3a\u6211\u4eec\u6709\u6743\u77e5\u9053\u3002\u201d\u5979\u8bed\u58f0\u867d\u4ecd\u662f\u90a3\u9ebd\u6e29\u67d4\uff0c\u4f46\u8bf4\u5230\u5f8c \u6765\uff0c\u53d8\u5f97\u7126\u6025\u5c16\u9510\u4e86\uff0c\u5979\u4f3c\u4e4e\u4e5f\u5df2\u611f\u89c9\u5230\u4ec0\u9ebd\u4e0d\u7965\u7684\u9884\u5146\u3002 \u3000\u3000\u53ef\u662f\u80e1\u94c1\u82b1\u53c8\u600e\u5fcd\u5728\u5979\u4eec\u9762\u524d\u5c06\u695a\u7559\u9999\u7684\u51f6\u8baf\u8bf4\u51fa\u6765\u3002 \u3000\u3000\u53ea\u53ef\u60dc\u4ed6\u5e76\u4e0d\u662f\u4e00\u4e2a\u5584\u65bc\u9690\u85cf\u81ea\u5df1\u60c5\u611f\u7684\u4eba\uff0c\u4ed6\u867d\u7136\u4e00\u4e2a\u5b57\u90fd\u6ca1\u6709\u8bf4\uff0c\u82cf\u84c9\u84c9\u8138\u8272\u5374\u5df2 \u6e10\u6e10\u53d8\u4e86\u3002 \u3000\u3000\u5979\u4f3c\u4e4e\u5ffd\u7136\u5931\u53bb\u4e86\u91cd\u5fc3\uff0c\u8eab\u5b50\u518d\u4e5f\u7ad9\u4e0d\u4f4f\u4e86\uff0c\u5ffd\u7136\u5c31\u5012\u4e86\u4e0b\u53bb\uff0c\u5b8b\u751c\u513f\u548c\u674e\u7ea2\u8896\u60ca\u547c\u7740 \u6276\u8d77\u4e86\u5979\u3002 \u3000\u3000\u53ea\u542c\u4e00\u58f0\u8f7b\u53f1\uff0c\u9ed1\u73cd\u73e0\u5ffd\u7136\u8d70\u8fc7\u6765\u6293\u4f4f\u4e86\u80e1\u94c1\u82b1\u7684\u54bd\u5589\uff0c\u5979\u82cd\u767d\u7684\u8138\u4e0a\u5df2\u5168\u65e0\u4e00\u4e1d\u8840 \u8272\uff0c\u77aa\u770b\u7740\u80e1\u94c1\u82b1\u54fc\u58f0\u9053\uff1a\u201c\u4ed6\u7a76\u7adf\u51fa\u4e86\u4ec0\u9ebd\u4e8b\uff1f\u4f60\u4e0d\u8bf4\u6211\u5c31\u5148\u6740\u4e86\u4f60\u3002\u201d \u3000\u3000\u82cf\u84c9\u84c9\u6323\u624e\u7740\u722c\u8d77\u6765\uff0c\u98a4\u58f0\u9053\uff1a\u201c\u653e\u5f00\u4ed6\uff0c\u653e\u5f00\u4ed6\u2026\u2026\u2026\u4ed6\u7edd\u6ca1\u6709\u6076\u610f\u3002\u201d \u3000\u3000\u9ed1\u73cd\u73e0\u9053\uff1a\u201c\u4f46\u4ed6\u4e3a\u4ec0\u9ebd\u4e0d\u80af\u8bf4\uff1f\u4ed6\u7a76\u7adf\u60f3\u9690\u7792\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u82cf\u84c9\u84c9\u76ee\u4e2d\u5df2\u6d41\u4e0b\u6cea\u6765\uff0c\u9eef\u7136\u9053\uff1a\u201c\u6211\u77e5\u9053\u4ed6\u4e0d\u80af\u8bf4\uff0c\u53ea\u4e0d\u8fc7\u662f\u6015\u6211\u4eec\u4f24\u5fc3\u800c\u5df2\u3002\u201d \u3000\u3000\u5979\u8bdd\u672a\u8bf4\u5b8c\uff0c\u5df2\u662f\u4f4d\u4e0d\u6210\u58f0\uff0c\u674e\u7ea2\u8896\u3001\u5b8b\u751c\u513f\u3001\u9ed1\u73cd\u73e0\u53c1\u4e2a\u4eba\u4e5f\u4f3c\u5168\u90fd\u7ad9\u4e0d\u4f4f\u4e86\u53c1\u4e2a\u4eba \u4e00\u77aa\u7740\u80e1\u94c1\u82b1\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u77a7\u89c1\u5979\u4eec\u90a3\u79cd\u773c\u8272\uff0c\u5fc3\u91cc\u5c31\u597d\u50cf\u88ab\u9488\u5728\u523a\u7740\u4f3c\u7684\uff0c\u4ed6\u76f4\u5230\u6b64\u523b\uff0c\u624d\u61c2\u5f97\u4f24\u5fc3\u7684\u6ecb \u5473\u3002 \u3000\u3000\u7a81\u7136\u95f4\uff0c\u4e00\u4e2a\u4eba\u98de\u4e5f\u4f3c\u7684\u51b2\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u8fd9\u4eba\u8d6b\u7136\u6b63\u662f\u674e\u7389\u51fd\u3002 \u3000\u3000\u4e00\u89c1\u5230\u4ed6\uff0c\u674e\u7ea2\u8896\u5979\u4eec\u7684\u773c\u775b\u91cc\u5c31\u4f3c\u5c06\u5192\u51fa\u706b\u6765\u3002 \u3000\u3000\u674e\u7ea2\u8896\u9ad8\u58f0\u9053\uff1a\u201c\u4f60\u8fd9\u6076\u8d3c\uff0c\u4f60\u5c45\u7136\u8fd8\u6562\u6765\u89c1\u6211\u4eec\uff1f\u201d \u3000\u3000\u5b8b\u751c\u513f\u98a4\u58f0\u9053\uff1a\u201c\u4f60\u628a\u6211\u4eec\u7684\u695a\u7559\u9999\u600e\u9ebd\u6837\u4e86\uff1f\u201d \u3000\u3000\u9ed1\u73cd\u73e0\u5389\u58f0\u9053\uff1a\u201c\u4f60\u6700\u597d\u5feb\u5feb\u6740\u4e86\u6211\uff0c\u5426\u5219\u6211\u8fdf\u65e9\u603b\u6709\u4e00\u5929\u8981\u6740\u4e86\u4f60\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u6012\u559d\u9053\uff1a\u201c\u6076\u8d3c\uff0c\u4f60\u6562\u548c\u6211\u4e00\u51b3\u751f\u6b7b\u9ebd\uff1f\u201d \u3000\u3000\u56db\u4e2a\u4eba\u4e00\u7834\u53e3\u5927\u9a82\uff0c\u674e\u7389\u51fd\u7adf\u4ecd\u5b8c\u5168\u6ca1\u6709\u542c\u5230\u3002 \u3000\u3000\u53ea\u89c1\u4ed6\u7684\u8138\u8272\u7adf\u6bd4\u674e\u7ea2\u8896\u4ed6\u4eec\u66f4\u60b2\u4f24\uff0c\u66f4\u53ef\u6015\uff0c\u4ed6\u773c\u775b\u91cc\u5e03\u6ee1\u4e86\u8840\u4e1d\uff0c\u5168\u8eab\u90fd\u5728\u4e0d\u505c\u7684 \u53d1\u6765\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u4ed6\u4eec\u53cd\u800c\u4e0d\u7981\u89c9\u5f97\u5f88\u5947\u602a\u4e86\uff0c\u6b63\u731c\u4e0d\u51fa\u4ed6\u600e\u4f1a\u53d8\u5f97\u5982\u6b64\u6a21\u6837\uff0c\u67f3\u65e0\u7709\u5ffd\u7136\u4e5f\u51b2\u4e86 \u8fdb\u6296\u3002 \u3000\u3000\u5979\u795e\u60c5\u4e0d\u4f46\u5f88\u60b2\u4f24\uff0c\u800c\u4e14\u50cf\u662f\u5f88\u60ca\u60f6\u3002 \u3000\u3000\u5979\u7adf\u51b2\u5230\u674e\u7389\u51fd\u9762\u524d\uff0c\u7d27\u7d27\u62b1\u4f4f\u4e86\u4ed6\uff0c\u98a4\u58f0\u9053\uff1a\u201c\u90fd\u662f\u6211\u5bb3\u4e86\u4f60\uff0c\u90fd\u662f\u6211\u5bb3\u4e86\u4f60\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u6ca1\u6709\u8bf4\u8bdd\uff0c\u53ea\u662f\u8f7b\u8f7b\u5730\u53bb\u629a\u6478\u5979\u51cc\u4e71\u7684\u5934\u53d1\uff0c\u4ed6\u76ee\u5149\u4e2d\u5145\u6ee1\u4e86\u60b2\u75db\uff0c\u4e5f\u5145\u6ee1\u4e86\u601c \u60dc\u3002 \u3000\u3000\u67f3\u65e0\u7709\u5ffd\u7136\u653e\u5f00\u4ed6\uff0c\u81ea\u8896\u4e2d\u62d4\u51fa\u4e00\u67c4\u96ea\u4eae\u7684\u5315\u9996\u3002 \u3000\u3000\u5979\u7adf\u4e00\u5200\u5411\u81ea\u5df1\u7684\u5fc3\u7a9d\u523a\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u674e\u7389\u51fd\u53d1\u4e86\u75af\u4f3c\u7684\u62b1\u4f4f\u5979\uff0c\u54fc\u58f0\u9053\uff1a\u201c\u4f60\u600e\u9ebd\u80fd\u8fd9\u6837\u505a\uff0c\u5feb\u4f4f\u624b\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u5df2\u662f\u6cea\u6d41\u6ee1\u9762\uff0c\u9053\uff1a\u201c\u6211\u5df2\u62d6\u7d2f\u4f60\u8fd9\u9ebd\u4e45\uff0c\u6c42\u6c42\u4f60\uff0c\u8ba9\u6211\u6b7b\u5427\uff0c\u6211\u6b7b\u4e86\u4e4b\u5f8c\uff0c\u522b \u4eba\u5c31\u4f1a\u539f\u8c05\u4f60\u7684\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u8dfa\u811a\u9053\uff1a\u201c\u4f60\u6b7b\u4e86\u4e4b\u5f8c\uff0c\u6211\u8fd8\u80fd\u6d3b\u4e0b\u53bb\u9ebd\uff1f\u201d \u3000\u3000\u67f3\u65e0\u7709\u8eab\u5b50\u4e00\u9635\u98a4\u6296\uff0c\u624b\u4e2d\u7684\u5315\u9996\u201c\u5f53\u201d\u7684\u843d\u5728\u77f3\u677f\u4e0a\uff0c\u5979\u4e5f\u7d27\u7d27\u62b1\u4f4f\u4e86\u674e\u7389\u51fd\uff0c\u653e\u58f0 \u5927\u54ed\u8d77\u6765\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u4ed6\u4eec\u5168\u90fd\u77a7\u5f97\u6014\u4f4f\u4e86\uff0c\u8c01\u4e5f\u731c\u4e0d\u51fa\u8fd9\u592b\u59bb\u4e24\u4eba\u7a76\u7adf\u4e3a\u4e86\u4ec0\u9ebd\u53d8\u6210\u5982\u6b64\u6a21\u6837\uff1f\u8fd9\u83ab \u975e\u53c8\u662f\u5728\u505a\u620f\uff1f\u53ea\u542c\u67f3\u65e0\u7709\u75db\u54ed\u7740\u9053\uff1a\u201c\u5176\u5b9e\u6211\u53c8\u600e\u9ebd\u820d\u5f97\u79bb\u5f00\u4f60\uff0c\u53ea\u4e0d\u8fc7\uff0c\u6211\u89c9\u5f97\u4f60\u5df2\u4e3a \u6211\u727a\u7272\u5f97\u592a\u591a\u4e86\uff0c\u6211\u600e\u5fcd\u518d\u8ba9\u4f60\u966a\u7740\u6211\u53d7\u82e6\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u67d4\u58f0\u9053\uff1a\u201c\u81ea\u4ece\u4f60\u6765\u4e86\uff0c\u6211\u6bcf\u4e00\u5929\uff0c\u6bcf\u4e00\u4e2a\u65f6\u8fb0\u90fd\u662f\u5feb\u4e50\u7684\uff0c\u600e\u9ebd\u80fd\u8bf4\u662f\u53d7 \u82e6\uff1f\u201d \u3000\u3000\u67f3\u65e0\u7709\u9053\uff1a\u201c\u90a3\u9ebd\uff0c\u6211\u4eec\u4e0d\u5982\u8d70\u5427\uff01\u53bb\u627e\u4e2a\u5730\u65b9\uff0c\u5b89\u5b89\u9759\u9759\u7684\u4f4f\u4e0b\u6765\uff0c\u4ec0\u9ebd\u4eba\u90fd\u4e0d \u89c1\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u9053\uff1a\u201c\u53ef\u662f\u4f60\u2026\u2026\u2026\u201d \u3000\u3000\u67f3\u65e0\u7709\u51c4\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u4e5f\u8bb8\u8fd8\u80fd\u6d3b\u51e0\u4e2a\u6708\uff0c\u7b49\u8fd9\u51e0\u4e2a\u6708\u2026\u2026\u2026\u201d \u3000\u3000\u674e\u7389\u51fd\u5ffd\u7136\u6253\u65ad\u4e86\u5b83\u7684\u8bdd\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u6211\u65e0\u8bba\u5982\u4f55\u4e5f\u4e0d\u80fd\u8ba9\u4f60\u6b7b\uff0c\u6211\u8981\u4f60\u6c38\u8fdc\u6d3b\u4e0b \u53bb\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u9053\uff1a\u201c\u53ef\u662f\u73b0\u5728\u2026\u2026\u2026\u201d \u3000\u3000\u674e\u7389\u51fd\u9053\uff1a\u201c\u73b0\u5728\u6211\u4eec\u5e76\u6ca1\u6709\u7edd\u671b\uff0c\u6211\u4eec\u81f3\u5c11\u8fd8\u6709\u8fd9\u4e94\u4e2a\u4eba\u5728\u624b\u91cc\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u4ed6\u4eec\u8d8a\u542c\u8d8a\u4e0d\u660e\u767d\uff0c\u8d8a\u542c\u8d8a\u5947\u602a\u3002 \u3000\u3000\u67f3\u65e0\u7709\u4e3a\u4f55\u8981\u6b7b\uff1f\u4ed6\u4eec\u4e3a\u4ec0\u9ebd\u2026\u2026\u2026 \u3000\u3000\u7a81\u542c\u674e\u7389\u51fd\u4e00\u58f0\u5927\u559d\uff0c\u9053\uff1a\u201c\u7ad9\u4f4f\uff0c\u4f60\u82e5\u6562\u518d\u5f80\u524d\u8d70\uff0c\u6211\u5c31\u8981\u4ed6\u4eec\u7684\u547d\u3002\u201d \u3000\u3000\u4ed6\u4e0d\u77e5\u4f55\u65f6\u5df2\u5c06\u90a3\u66b4\u96e8\u68a8\u82b1\u9489\u5bf9\u51c6\u4e86\u80e1\u94c1\u82b1\u4ed6\u4eec\u7684\u8eab\u5b50\uff0c\u53e6\u4e00\u53ea\u624b\u7d27\u7d27\u62c9\u4f4f\u67f3\u65e0\u7709\uff0c\u50cf \u662f\u751f\u6015\u5931\u843d\u4e86\u5979\u3002 \u3000\u3000\u77f3\u9636\u4e0a\u6709\u4eba\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5230\u4e86\u73b0\u5728\u4f60\u8fd8\u4e0d\u80af\u653e\u624b\u9ebd\uff1f\u4f60\u8fd9\u662f\u4f55\u82e6\uff1f\u201d \u3000\u3000\u8fd9\u58f0\u97f3\u7adf\u8d6b\u7136\u6b63\u662f\u695a\u7559\u9999\u7684\u3002 \u3000\u3000\u695a\u7559\u9999\u7adf\u6ca1\u6709\u6b7b\u3002 \u3000\u3000\u662f\u8c01\u6551\u4e86\u4ed6\uff1f \u3000\u3000\u80e1\u94c1\u82b1\u4ed6\u4eec\u53c8\u60ca\u53c8\u559c\uff0c\u5931\u58f0\u547c\u9053\uff1a\u201c\u695a\u7559\u9999\u662f\u4f60\u9ebd\uff1f\u201d \u3000\u3000\u4ed6\u4eec\u5df2\u7528\u4e0d\u7740\u56de\u7b54\uff0c\u53ea\u56e0\u4e3a\u4ed6\u4eec\u7ec8\u65bc\u53c8\u89c1\u5230\u4e86\u695a\u7559\u9999\u3002 \u3000\u3000\u695a\u7559\u9999\u6b63\u7ad9\u5728\u6700\u4e0b\u9762\u4e00\u7ea7\u77f3\u9636\u4e0a\uff0c\u679c\u7136\u4e0d\u6562\u518d\u5f80\u4e0b\u9762\u8d70\u4e00\u6b65\uff0c\u53ea\u56e0\u4ed6\u6df1\u6df1\u77e5\u9053\u66b4\u96e8\u68a8\u82b1 \u9489\u7684\u5a01\u529b\u3002 \u3000\u3000\u73b0\u5728\uff0c\u80e1\u94c1\u82b1\u4ed6\u4eec\u4e94\u4e2a\u4eba\u6324\u5728\u4e00\u95f4\u5e76\u4e0d\u5927\u7684\u77f3\u5ba4\u4e2d\uff0c\u6bcf\u4e2a\u4eba\u90fd\u5728\u66b4\u96e8\u68a8\u82b1\u9489\u7684\u5a01\u529b\u63a7\u5236 \u4e4b\u4e0b\u3002 \u3000\u3000\u4ed6\u4eec\u6839\u672c\u6ca1\u6709\u95ea\u907f\u7684\u9980\u5730\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u8df3\u4e86\u8d77\u6765\uff0c\u5927\u7b11\u9053\uff1a\u201c\u8001\u81ed\u866b\uff0c\u4f60\u679c\u7136\u6ca1\u6709\u6b7b\uff0c\u6211\u5c31\u77e5\u9053\u4f60\u6b7b\u4e0d\u4e86\u7684\uff0c\u5929\u4e0b\u6709\u8c01 \u80fd\u8981\u4f60\u7684\u547d\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u867d\u7136\u5728\u5fae\u7b11\uff0c\u8fd8\u662f\u5fcd\u4e0d\u4f4f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f46\u8fd9\u6b21\u82e5\u975e\u6709\u4eba\u6551\u6211\uff0c\u6211\u7684\u547d\u5c31\u5df2\u7ecf \u88ab\u4eba\u8981\u53bb\u4e86\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u771f\u6709\u4eba\u6765\u6551\u4e86\u4f60\uff1f\u662f\u8c01\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4f60\u731c\u4e0d\u51fa\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u6211\u5b9e\u5728\u731c\u4e0d\u51fa\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u53f9\u9053\uff1a\u201c\u4f60\u81ea\u7136\u731c\u4e0d\u51fa\uff0c\u53ea\u56e0\u6211\u81ea\u5df1\u4e5f\u60f3\u4e0d\u5230\u6551\u6211\u7684\u4eba\u7adf\u662f\u674e\u89c2\u9c7c\u674e\u8001\u524d\u8f88\u201d \u3000\u3000\u80e1\u94c1\u82b1\u53c8\u6014\u4f4f\u4e86\uff0c\u5931\u58f0\u9053\uff1a\u201c\u513f\u5b50\u60f3\u8981\u4f60\u7684\u547d\uff0c\u8001\u5b50\u600e\u4f1a\u53bb\u6551\u4f60\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u82e6\u7b11\u9053\uff1a\u201c\u4ed6\u6839\u672c\u4e0d\u77e5\u9053\u8fd9\u4ef6\u4e8b\uff0c\u66f4\u6ca1\u6709\u8981\u6211\u547d\u7684\u610f\u601d\uff0c\u6240\u6709\u7684\u4e8b\uff0c\u5168\u90fd\u662f\u8fd9\u4f4d \u674e\u516c\u5b50\u8d24\u4f09\u4fea\u4e24\u4eba\u5b89\u6392\u51fa\u6765\u7684\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u53ef\u662f\uff0c\u5e05\u4e00\u5e06\u90a3\u4e9b\u4eba\uff0c\u5c82\u975e\u5168\u662f\u53d7\u4e86\u674e\u89c2\u9c7c\u6240\u6258\u800c\u6765\u7684\u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u8fd9\u53ea\u4e0d\u8fc7\u662f\u674e\u516c\u5b50\u5728\u5047\u4f20\u5723\u65e8\u800c\u5df2\uff0c\u513f\u5b50\u66ff\u8001\u5b50\u8bf4\u8bdd\uff0c\u522b\u4eba\u81ea\u7136\u4e0d\u4f1a\u6000 \u7591\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u90a3\u9ebd\u674e\u89c2\u9c7c\u4e3a\u4f55\u4e0d\u5426\u8ba4\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u53ea\u56e0\u674e\u8001\u524d\u8f88\u4e03\u5e74\u524d\u7ec3\u529f\u5c94\u4e86\u6c14\uff0c\u5168\u8eab\u90fd\u5df2\u50f5\u6728\uff0c\u8fde\u8bdd\u90fd\u8bf4\u4e0d\u51fa\u4e86\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u8d8a\u542c\u8d8a\u5947\u602a\u4e86\uff0c\u9053\uff1a\u201c\u4ed6\u65e2\u5df2\u5168\u8eab\u50f5\u6728\uff0c\u53c8\u600e\u80fd\u51fa\u624b\u6551\u4f60\u5462\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u53f9\u9053\uff1a\u201c\u674e\u8001\u524d\u8f88\u4e00\u751f\u6b63\u76f4\uff0c\u6700\u91cd\u6c5f\u6e56\u9053\u4e49\uff0c\u4ed6\u773c\u770b\u7740\u4e0d\u5e73\u7684\u4e8b\u5728\u4ed6\u773c\u524d\u53d1\u751f\uff0c \u800c\u4e14\u8fd8\u5047\u501f\u4ed6\u7684\u7684\u540d\u5b57\uff0c\u5fc3\u91cc\u7684\u96be\u53d7\u548c\u6c14\u6124\uff0c\u4f60\u6211\u53ea\u6015\u662f\u60f3\u50cf\u4e0d\u5230\u7684\uff0c\u4f46\u4ed6\u5374\u53c8\u504f\u504f\u53ea\u80fd\u773c \u7741\u7741\u5730\u671b\u7740\uff0c\u8fde\u52a8\u90fd\u4e0d\u80fd\u52a8\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u4e00\u62cd\u5df4\u638c\uff0c\u9053\uff1a\u201c\u83ab\u975e\u662f\u4ed6\u6c14\u6781\u4e4b\u4e0b\uff0c\u90a3\u4e00\u80a1\u4e45\u5df2\u88ab\u618b\u6b7b\u7684\u771f\u6c14\uff0c\u7adf\u53c8\u88ab\u6012\u706b\u6240 \u6fc0\u52a8\u4e86\u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u9053\uff1a\u201c\u6b63\u662f\u5982\u6b64\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u7b11\u9053\uff1a\u201c\u5f8c\u9762\u7684\u4e8b\uff0c\u4f60\u4e0d\u8bf4\u6211\u4e5f\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u54e6\uff01\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u67f3\u65e0\u7709\u6b63\u8981\u6740\u4f60\u65f6\uff0c\u77a7\u89c1\u674e\u8001\u524d\u8f88\u5ffd\u7136\u53c8\u80fd\u8d70\u52a8\u8bf4\u8bdd\u4e86\uff0c\u81ea\u7136\u8981\u5927\u5403\u4e00 \u60ca\uff0c\u4e00\u4e2a\u4eba\u773c\u89c1\u81ea\u5df1\u7684\u9634\u8c0b\u5c31\u8981\u88ab\u63ed\u7a7f\uff0c\u8c01\u90fd\u4f1a\u5bb3\u6015\u7684\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u7b49\u5979\u518d\u60f3\u6740\u4f60\u65f6\uff0c\u90a3\u4e94\u4e2a\u8001\u5934\u5b50\u81ea\u7136\u5c31\u4e0d\u4f1a\u518d\u8ba9\u5979\u4e0b\u624b\uff0c\u90a3\u65f6\u674e\u7389\u51fd\u53ea\u6015 \u66f4\u5413\u5f97\u8fde\u9b42\u90fd\u6ca1\u6709\u4e86\uff0c\u6240\u4ee5\u7acb\u523b\u5c31\u8ffd\u5230\u8fd9\u91cc\u6765\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u5fae\u7b11\u9053\uff1a\u201c\u5341\u6210\u4e2d\u4f60\u5c45\u7136\u731c\u4e2d\u4e86\u4e5d\u6210\uff0c\u8fd9\u5012\u771f\u96be\u5f97\u7684\u5f88\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u4f46\u4f60\u65e2\u7136\u80fd\u627e\u5230\u8fd9\u91cc\uff0c\u4e3a\u4ec0\u9ebd\u4e0d\u5c06\u90a3\u51e0\u4e2a\u4e0a\u4e86\u5f53\u7684\u8001\u5934\u5b50\u4e5f\u5e26\u6765\u5462\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u7f13\u7f13\u9053\uff1a\u201c\u6211\u81ea\u5df1\u7684\u4e8b\uff0c\u81ea\u7136\u8fd8\u662f\u5e94\u8be5\u7531\u6211\u81ea\u5df1\u6765\u89e3\u51b3\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u4f60\u80fd\u89e3\u51b3\u5f97\u4e86\u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u6de1\u6de1\u7b11\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u77e5\u9053\u4e16\u4e0a\u662f\u5426\u771f\u7684\u6709\u89e3\u51b3\u4e0d\u4e86\u7684\u4e8b\uff0c\u81f3\u5c11\u6211\u5230\u76ee\u524d\u8fd8\u672a\u9047\u89c1 \u8fc7\u3002\u201d \u3000\u3000\u8fd9\u4ef6\u4e8b\u53ef\u7559\u5230\u4ee5\u5f8c\u6162\u6162\u518d\u8bf4\uff0c\u4f46\u4ed6\u4eec\u4e24\u4eba\u90fd\u4e00\u76f4\u5728\u8bf4\u4e2a\u4e0d\u505c\uff0c\u7adf\u4f3c\u5fd8\u4e86\u8fd9\u662f\u4ec0\u9ebd\u65f6\u5019\uff0c \u8fd9\u662f\u4ec0\u9ebd\u5730\u65b9\uff0c\u66f4\u597d\u50cf\u5168\u672a\u77a7\u89c1\u674e\u7389\u51fd\u548c\u67f3\u65e0\u7709\u8fd8\u5728\u4e00\u65c1\u864e\u89c6\u7708\u7708\uff0c\u5b8b\u751c\u513f\u5979\u4eec\u4e00\u65c1\u5b9e\u5728\u77a7 \u5f97\u5947\u602a\u5f97\u5f88\u3002 \u3000\u3000\u6700\u4ee4\u5979\u4eec\u4f24\u5fc3\u7684\u662f\uff0c\u695a\u7559\u9999\u975e\u4f46\u6ca1\u6709\u5bf9\u5979\u4eec\u8bf4\u8bdd\uff0c\u751a\u81f3\u8fde\u770b\u90fd\u6ca1\u6709\u770b\u5979\u4eec\u4e00\u773c\uff0c\u53cd\u800c\u548c \u80e1\u94c1\u82b1\u8bf4\u4e2a\u4e0d\u505c\u3002 \u3000\u3000\u8fd9\u5176\u4e2d\u53ea\u6709\u82cf\u84c9\u84c9\u77e5\u9053\u695a\u7559\u9999\u7684\u5fc3\u610f\uff0c\u5979\u77e5\u9053\u4ed6\u4eec\u6b64\u523b\u6b63\u662f\u60f3\u7528\u8fd9\u4e9b\u8bdd\u6765\u5206\u6563\u674e\u7389\u51fd\u7684 \u6ce8\u610f\u529b\u3002 \u3000\u3000\u53ea\u8981\u674e\u7389\u51fd\u7a0d\u6709\u758f\u5ffd\uff0c\u695a\u7559\u9999\u7acb\u523b\u5c31\u53ef\u4ee5\u593a\u4e0b\u4ed6\u624b\u91cc\u7684\u201c\u66b4\u96e8\u68a8\u82b1\u9489\u201d\uff0c\u695a\u7559\u9999\u51fa\u624b\u4e4b \u5feb\uff0c\u82cf\u84c9\u84c9\u66f4\u6e05\u695a\u5f97\u5f88\u3002 \u3000\u3000\u600e\u5948\u674e\u7389\u51fd\u7684\u773c\u775b\u8fd8\u662f\u77ac\u4e5f\u4e0d\u77ac\u7684\u77aa\u7740\u695a\u7559\u9999\uff0c\u4ed6\u7684\u624b\u8fd8\u662f\u7d27\u7d27\u6263\u4f4f\u90a3\u4e00\u5323\u201c\u66b4\u96e8\u68a8\u82b1 \u9489\u201d\u3002 \u3000\u3000\u695a\u7559\u9999\u65e0\u8bba\u8bf4\u4ec0\u9ebd\uff0c\u4ed6\u7adf\u4f3c\u5168\u90fd\u542c\u4e0d\u89c1\uff0c\u4f46\u53ea\u8981\u695a\u7559\u9999\u7684\u624b\u4e00\u52a8\uff0c\u4ed6\u7684\u66b4\u96e8\u68a8\u82b1\u9489\uff0c\u7acb \u523b\u5c31\u4f1a\u53d1\u5c04\u51fa\u6765\u3002 \u3000\u3000\u82cf\u84c9\u84c9\u4e0d\u7981\u5728\u6697\u4e2d\u53f9\u4e86\u53e3\u6c14\uff0c\u56e0\u4e3a\u5979\u5df2\u770b\u51fa\u695a\u7559\u9999\u5728\u60f3\u593a\u4e0b\u674e\u7389\u51fd\u624b\u91cc\u7684\u68a8\u82b1\u9489\uff0c\u53ea\u6015 \u6bd4\u5728\u864e\u53e3\u4e2d\u62d4\u7259\u8fd8\u8981\u56f0\u96be\u3002 \u3000\u3000\u7a81\u542c\u674e\u7389\u51fd\u5389\u58f0\u9053\uff1a\u201c\u4f60\u4eec\u8bf4\u5b8c\u4e86\u9ebd\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u4f60\u96be\u9053\u4e5f\u60f3\u8bf4\u8bdd\uff1f\u597d\uff0c\u90a3\u9ebd\u6211\u5148\u95ee\u4f60\uff0c\u695a\u7559\u9999\u7a76\u7adf\u548c\u4f60\u6709\u4ec0\u9ebd\u4ec7\u6068\uff1f\u4f60\u4e3a \u4ec0\u9ebd\u8981\u5982\u6b64\u5bb3\u4ed6\uff1f\u201d \u3000\u3000\u674e\u7389\u51fd\u5c45\u7136\u4e5f\u957f\u53f9\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4ed6\u548c\u6211\u5f80\u65e5\u65e0\u51a4\uff0c\u8fd1\u65e5\u65e0\u4ec7\uff0c\u6211\u8981\u6740\u4ed6\uff0c\u5b9e\u5728\u60c5\u4e0d\u5f97 \u5df2\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u6012\u9053\uff1a\u201c\u4f60\u8fd9\u662f\u5728\u8bf4\u8bdd\uff0c\u8fd8\u662f\u5728\u653e\u5c41\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u4e5f\u4e0d\u751f\u6c14\uff0c\u7adf\u53c8\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6709\u8bb8\u591a\u4e8b\uff0c\u6211\u4e5f\u77e5\u9053\u4f60\u7edd\u4e0d\u4f1a\u61c2\u7684\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u6709\u8bb8\u591a\u4e8b\u6211\u672c\u6765\u7684\u786e\u767e\u601d\u4e0d\u5f97\u5176\u89e3\uff0c\u53ef\u662f\u73b0\u5728\u5374\u6e10\u6e10\u60f3\u901a\u4e86\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u9053\uff1a\u201c\u54e6\uff01\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u6700\u4ee4\u6211\u60f3\u4e0d\u901a\u7684\u5c31\u662f\uff0c\u4f60\u4eec\u65e2\u7136\u653e\u8fc7\u6211\uff0c\u4e3a\u4ec0\u9ebd\u53c8\u8981\u6740\u6211\u5462\uff1f\u201d \u3000\u3000\u4ed6\u53c8\u7b11\u4e86\u7b11\uff0c\u6309\u7740\u9053\uff1a\u201c\u5f8c\u6765\u6211\u624d\u60f3\u901a\u8fd9\u9053\u7406\uff0c\u56e0\u4e3a\u6211\u5df2\u53d1\u73b0\u4f60\u4eec\u6839\u672c\u6ca1\u6709\u6551\u8fc7\u6211\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u9053\uff1a\u201c\u4f60\u2026\u2026\u2026\u4f60\u96be\u9053\u5fd8\u4e86\u90a3\u5929\u5728\u77f3\u89c2\u97f3\u7684\u79d8\u8c37\u4e2d\u2026\u2026\u2026\u201d \u3000\u3000\u695a\u7559\u9999\u542c\u5230\u4e86\u5979\u7684\u8bdd\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u90a3\u5929\u4f60\u7684\u786e\u6740\u4e86\u4e0d\u5c11\u4eba\uff0c\u4f46\u5374\u5e76\u4e0d\u662f\u4e3a\u4e86\u6551\u6211\uff0c\u53ea \u56e0\u90a3\u65f6\u6211\u5df2\u7ecf\u9003\u51fa\u6765\u4e86\uff0c\u4f60\u4e0d\u6740\u90a3\u4e9b\u4eba\u6211\u4e5f\u53ef\u4ee5\u9003\u5f97\u51fa\u53bb\u7684\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u65e2\u4e0d\u80af\u627f\u6211\u7684\u60c5\uff0c\u6211\u4e5f\u6ca1\u6cd5\u5b50\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4f60\u867d\u672a\u6551\u8fc7\u6211\uff0c\u6211\u8fd8\u662f\u5f88\u611f\u6fc0\u4f60\uff0c\u53ea\u56e0\u82e5\u975e\u4f60\u51fa\u624b\u76f8\u6551\uff0c\u9f9f\u5179\u738b\u7236\u5973\u548c\u80e1 \u94c1\u82b1\u4ed6\u4eec\u53ea\u6015\u5df2\u6b7b\u65bc\u77f3\u89c2\u97f3\u7684\u6bd2\u9152\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u9053\uff1a\u201c\u4f60\u5c45\u7136\u8fd8\u672a\u5fd8\u8bb0\u8fd9\u4ef6\u4e8b\uff0c\u5012\u4e5f\u96be\u5f97\u5f97\u5f88\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u6211\u81ea\u7136\u4e0d\u4f1a\u5fd8\u8bb0\uff0c\u56e0\u4e3a\u6211\u4e00\u76f4\u5728\u5947\u602a\uff0c\u4f60\u4eec\u662f\u89c1\u5230\u82cf\u84c9\u84c9\u5979\u4eec\u4e4b\u5f8c\u5230\u6c99\u6f20 \u53bb\u7684\uff0c\u600e\u4f1a\u4e00\u5230\u6c99\u6f20\uff0c\u5c31\u80fd\u627e\u5f97\u5230\u77f3\u89c2\u97f3\u90a3\u79d8\u8c37\u91cc\uff1f\u90a3\u79d8\u8c37\u4e0d\u4f46\u5730\u52bf\u504f\u50fb\uff0c\u4eba\u8ff9\u7f55\u81f3\uff0c\u800c\u4e14 \u8c37\u4e2d\u9053\u8def\u9519\u7efc\u590d\u6742\uff0c\u4f46\u4f60\u4eec\u5374\u50cf\u662f\u8f7b\u8f66\u719f\u8def\uff0c\u6765\u53bb\u81ea\u5982\uff0c\u8fd9\u5c82\u975e\u662f\u4ef6\u602a\u4e8b\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u8038\u7136\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u542c\u4f60\u8fd9\u9ebd\u4e00\u8bf4\uff0c\u6211\u4e5f\u89c9\u5f97\u5947\u602a\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u8fd8\u6709\uff0c\u77f3\u89c2\u97f3\u7528\u6bd2\u7684\u529f\u592b\u6781\u7cbe\uff0c\u5979\u914d\u5236\u51fa\u6765\u7684\u6bd2\u9152\uff0c\u522b\u4eba\u81ea\u7136\u65e0\u6cd5\u5316\u89e3\uff0c \u6240\u4ee5\u5979\u77a7\u89c1\u80e1\u94c1\u82b1\u4ed6\u4eec\u559d\u4e86\u5979\u7684\u6bd2\u9152\u5f8c\uff0c\u5c31\u7acb\u523b\u8d70\u4e86\uff0c\u56e0\u4e3a\u5979\u8ba4\u4e3a\u4e16\u4e0a\u7edd\u6ca1\u6709\u4eba\u80fd\u89e3\u5f97\u4e86\u4ed6 \u4eec\u7684\u6bd2\uff0c\u6240\u4ee5\u624d\u4f1a\u90a3\u9ebd\u653e\u5fc3\u3002\u201d \u3000\u3000\u4ed6\u773c\u775b\u76ef\u7740\u67f3\u65e0\u7709\uff0c\u7f13\u7f13\u6309\u7740\u9053\uff1a\u201c\u4f46\u4f60\u5374\u8f7b\u63cf\u6de1\u5199\u7684\u5c31\u5c06\u4ed6\u4eec\u4e2d\u7684\u6bd2\u89e3\u4e86\uff0c\u8fd9\u5c82\u975e\u53c8 \u662f\u4ef6\u602a\u4e8b\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u62ac\u624b\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5979\u82e5\u4e0d\u77e5\u9053\u77f3\u89c2\u97f3\u90a3\u79cd\u9152\u7684\u6bd2\u6027\uff0c\u600e\u9ebd\u80fd\u4e3a\u6211\u4eec\u89e3\u6bd2\u5462\uff1f\u201d \u3000\u3000\u67f3\u65e0\u7709\u4e00\u53cc\u6625\u82b1\u822c\u7684\u7389\u624b\uff0c\u7d27\u7d27\u634f\u7740\u5979\u81ea\u5df1\u7684\u8863\u895f\uff0c\u9053\uff1a\u201c\u8fd9\u4e24\u4ef6\u4e8b\u4f60\u96be\u9053\u2026\u2026\u2026\u96be\u9053 \u5df2\u7ecf\u60f3\u901a\u4e86\u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u4e24\u4ef6\u4e8b\u867d\u7136\u5f88\u96be\u89e3\u91ca\uff0c\u4f46\u5374\u4e5f\u662f\u4f60\u4eec\u7559\u4e0b\u6765\u7684\u552f\u4e00\u6f0f\u6d1e\uff0c\u82e5 \u975e\u8fd9\u4e24\u4ef6\u4e8b\uff0c\u6211\u53ea\u6015\u6c38\u8fdc\u4e5f\u731c\u4e0d\u51fa\u4f60\u7684\u771f\u5b9e\u8eab\u4efd\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u7684\u6307\u8282\u5df2\u634f\u5f97\u53d1\u81ea\uff0c\u4f46\u4e00\u53cc\u624b\u8fd8\u662f\u5728\u4e0d\u505c\u7684\u53d1\u6296\uff0c\u9053\uff1a\u201c\u4f60\u2026\u2026\u2026\u4f60\u73b0\u5728\u96be\u9053\u5df2 \u731c\u51fa\u4e86\u6211\u7684\u771f\u5b9e\u8eab\u4efd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u6211\u5148\u95ee\u4f60\uff0c\u4e00\u4e2a\u4eba\u82e5\u662f\u6839\u672c\u5c31\u6ca1\u6709\u53bb\u8fc7\u77f3\u89c2\u97f3\u90a3\u79d8\u8c37\uff0c\u4ed6\u80fd\u5728\u8c37\u4e2d\u6765\u53bb\u81ea \u5982\u9ebd\uff1f\u201d \u3000\u3000\u67f3\u65e0\u7709\u54ac\u4e86\u54ac\u5634\u5507\uff0c\u9053\uff1a\u201c\u4e0d\u80fd\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4e00\u4e2a\u4eba\u82e5\u4e0d\u77e5\u9053\u77f3\u89c2\u97f3\u90a3\u676f\u6bd2\u9152\u7684\u6210\u4efd\uff0c\u80fd\u89e3\u5f97\u4e86\u90a3\u676f\u9152\u7684\u6bd2\u9ebd\uff1f\u201d \u3000\u3000\u67f3\u65e0\u7709\u9053\uff1a\u201c\u4e0d\u80fd\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u82e5\u975e\u662f\u77f3\u89c2\u97f3\u5f88\u4eb2\u8fd1\u7684\u4eba\uff0c\u65e2\u65e0\u6cd5\u77e5\u9053\u90a3\u79d8\u8c37\u7684\u51fa\u5165\u9053\u8def\uff0c\u4e5f\u4e0d\u4f1a\u77e5\u9053\u90a3 \u6bd2\u9152\u7684\u6210\u4efd\uff0c\u662f\u9ebd\uff1f\u201d \u3000\u3000\u67f3\u65e0\u7709\u5ffd\u7136\u5927\u7b11\u8d77\u6765\u3002 \u3000\u3000\u5979\u4f3c\u4e4e\u5df2\u4e0d\u80fd\u63a7\u5236\u81ea\u5df1\uff0c\u4e00\u76f4\u795e\u7ecf\u8d28\u7684\u683c\u683c\u7b11\u4e2a\u4e0d\u505c\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u5fcd\u4e0d\u4f4f\u9053\uff1a\u201c\u5979\u2026\u2026\u2026\u5979\u7a76\u7adf\u662f\u4ec0\u9ebd\u4eba\u5462\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u4e00\u5b57\u5b57\u9053\uff1a\u201c\u4f60\u96be\u9053\u8fd8\u60f3\u4e0d\u5230\u5979\u4e5f\u662f\u77f3\u89c2\u97f3\u7684\u95e8\u4e0b\u5f1f\u5b50\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u7adf\u4e5f\u662f\u77f3\u89c2\u97f3\u7684\u95e8\u4e0b\u5f1f\u5b50\u3002 \u3000\u3000\u8fd9\u53e5\u8bdd\u8bf4\u51fa\u6765\uff0c\u8fde\u80e1\u94c1\u82b1\u90fd\u5927\u5403\u4e86\u4e00\u60ca\u3002 \u3000\u3000\u674e\u7389\u51fd\u66f4\u662f\u9762\u8272\u5927\u53d8\uff0c\u5389\u58f0\u9053\uff1a\u201c\u5979\u82e5\u4e5f\u662f\u77f3\u89c2\u97f3\u95e8\u4e0b\u5b50\u5f1f\uff0c\u90a3\u5929\u4e3a\u4f55\u8981\u5c06\u5979\u7684\u540c\u95e8\u5168 \u90fd\u6740\u6b7b\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u51b7\u7b11\u9053\uff1a\u201c\u77f3\u89c2\u97f3\u65e2\u7136\u5df2\u7ecf\u60f3\u5230\u9f9f\u5179\u56fd\u53bb\u79f0\u738b\u79f0\u9738\u4e86\uff0c\u5e26\u7740\u8fd9\u9ebd\u591a\u5f92\u5f1f\uff0c\u5c82\u975e\u53cd \u6210\u4e86\u7d2f\u8d58\uff1f\u201d \u3000\u3000\u674e\u7389\u51fd\u9053\uff1a\u201c\u4f60\u2026\u2026\u2026\u4f60\u8ba4\u4e3a\u90a3\u662f\u77f3\u89c2\u97f3\u8981\u5979\u5c06\u90a3\u4e9b\u4eba\u6740\u6b7b\u7684\u9ebd\uff1f\u201d \u7b2c\u5341\u4e03\u7ae0\u3000\u6b8b\u66b4\u4e4b\u5c24>> \u53e4\u9f99\u300a\u695a\u7559\u9999\u7cfb\u5217\xb7\u753b\u7709\u9e1f\u300b \u7b2c\u5341\u4e03\u7ae0\u3000\u6b8b\u66b4\u4e4b\u5c24 \u3000\u3000\u695a\u7559\u9999\u9053\u2236\u201c\u6b63\u662f\u5982\u6b64\u3002\u201d \u3000\u3000\u4ed6\u7acb\u523b\u63a5\u7740\u53c8\u9053\u2236\u201c\u5c31\u56e0\u4e3a\u90a3\u4e9b\u4eba\u4e5f\u60f3\u4e0d\u5230\u5979\u4f1a\u4e0b\u8fd9\u6bd2\u624b\uff0c\u6240\u4ee5\u624d\u5bf9\u5979\u5168\u6ca1\u6709\u9632\u5907\uff0c\u5426 \u5219\u4ee5\u5979\u4e00\u4eba\u4e4b\u529b\uff0c\u53c8\u600e\u80fd\u5728\u7247\u523b\u4e2d\u6740\u6b7b\u90a3\u9ebd\u591a\u4eba\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u9053\u2236\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u4f60\u8ba4\u4e3a\u5c31\u56e0\u4e3a\u5b83\u662f\u77f3\u89c2\u97f3\u6700\u4eb2\u8fd1\u7684\u4eba\uff0c\u6240\u4ee5\u5979\u624d\u8981\u6740\u4f60\uff0c\u662f \u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\u2236\u201c\u9664\u6b64\u4e4b\u5916\uff0c\u4f3c\u4e4e\u518d\u4e5f\u6ca1\u6709\u522b\u7684\u89e3\u91ca\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u9053\u2236\u201c\u90a3\u9ebd\u6211\u5462\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u53f9\u9053\u2236\u201c\u4f60\u53ea\u6015\u4e5f\u4e0a\u4e86\u5979\u7684\u5f53\uff0c\u88ab\u5979\u5229\u7528\u4e86\u3002\u4e5f\u8bb8\u5979\u6839\u672c\u5c31\u662f\u77f3\u89c2\u97f3\u6d3e\u5230\u6c5f\u5357\u6765 \u5367\u5e95\u7684\u5978\u7ec6\uff0c\u6240\u4ee5\u624d\u5ac1\u7ed9\u4f60\uff0c\u7528\u201c\u62e5\u7fe0\u5c71\u5e84\u201d\u5c11\u5e84\u4e3b\u592b\u4eba\u7684\u540d\u4e49\u6765\u4f5c\u63a9\u62a4\uff0c\u81ea\u7136\u662f\u518d\u597d\u4e5f\u6ca1 \u6709\u7684\u4e86\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u9053\u2236\u201c\u5979\u65e2\u662f\u77f3\u89c2\u97f3\u7684\u6b7b\u515a\uff0c\u4e3a\u4f55\u5374\u53bb\u89e3\u4e86\u80e1\u94c1\u82b1\u4ed6\u4eec\u4e2d\u7684\u6bd2\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\u2236\u201c\u53ea\u56e0\u90a3\u65f6\u6211\u5df2\u6740\u4e86\u77f3\u89c2\u97f3\uff0c\u5979\u89c1\u5230\u5927\u52bf\u5df2\u53bb\uff0c\u6240\u4ee5\u624d\u53bb\u6551\u4e86\u4ed6\u4eec\uff0c\u4e5f\u597d\u4e3a \u65e5\u5f8c\u7559\u4e2a\u9000\u6b65\uff0c\u53cd\u6b63\u80e1\u94c1\u82b1\u4ed6\u4eec\u82e5\u662f\u6b7b\u4e86\uff0c\u5bf9\u5979\u4e5f\u6ca1\u4ec0\u9ebd\u597d\u5904\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u5ffd\u4e5f\u5927\u7b11\u8d77\u6765\u3002 \u3000\u3000\u4ed6\u7b11\u58f0\u4e2d\u7adf\u5145\u6ee1\u4e86\u60b2\u6124\u4e4b\u610f\uff0c\u50cf\u662f\u6709\u6ee1\u5fc3\u6028\u6c14\u3002 \u3000\u3000\u4ed6\u5927\u7b11\u7740\u9053\u2236\u201c\u695a\u7559\u9999\u5440\u695a\u7559\u9999\uff0c\u4f60\u5b9e\u5728\u592a\u806a\u660e\u4e86\uff0c\u53ea\u53ef\u60dc\u806a\u660e\u5f97\u8fc7\u4e86\u5ea6\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\u2236\u201c\u6211\u96be\u9053\u8bf4\u9519\u4e86\u9ebd\uff1f\u201d \u3000\u3000\u674e\u7389\u51fd\u5636\u58f0\u9053\u2236\u201c\u4f60\u81ea\u7136\u6ca1\u6709\u8bf4\u9519\uff0c\u65e0\u6240\u4e0d\u77e5\u7684\u695a\u7559\u9999\u600e\u4f1a\u9519\u5462\uff1f\u73b0\u5728\u65e0\u8bba\u4f60\u600e\u9ebd\u8bf4\uff0c \u53cd\u6b63\u5df2\u5168\u90fd\u6ca1\u4ec0\u9ebd\u5173\u7cfb\u4e86\u3002\u201d \u3000\u3000\u4ed6\u76ee\u4e2d\u4ee5\u5df2\u5192\u51fa\u706b\u6765\uff0c\u4eba\u5b54\u9053\u2236\u201c\u53ea\u56e0\u4f60\u73b0\u5728\u53cd\u6b63\u5df2\u975e\u6b7b\u4e0d\u53ef\uff0c\u5426\u5219\u6211\u5c31\u7acb\u523b\u6740\u4e86\u5979 \u4eec\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u5403\u60ca\u9053\u2236\u201c\u4f60\u75af\u4e86\u9ebd\uff1f\u201d \u3000\u3000\u674e\u7389\u51fd\u543c\u9053\u2236\u201c\u4e0d\u9519\uff0c\u6211\u7684\u786e\u75af\u4e86\uff0c\u4f46\u4f60\u82e5\u6362\u4e86\u6211\uff0c\u4f60\u53ea\u6015\u6bd4\u6211\u75af\u5f97\u66f4\u5389\u5bb3\u3002\u201d \u3000\u3000\u4ed6\u7684\u624b\u98a4\u6296\u7740\uff0c\u968f\u65f6\u90fd\u53ef\u80fd\u5c06\u90a3\u201c\u66b4\u96e8\u68a8\u82b1\u9489\u201d\u7684\u673a\u7c27\u62e8\u52a8\uff0c\u82e5\u6362\u4e86\u522b\u4eba\uff0c\u600e\u9ebd\u6562\u518d\u523a \u6fc0\u4ed6\u3002 \u3000\u3000\u4f46\u80e1\u94c1\u82b1\u5374\u4e0d\u7ba1\u53c1\u4e03\u4e8c\u5341\u4e00\uff0c\u8fd8\u662f\u4eba\u543c\u9053\u2236\u201c\u5230\u73b0\u5728\u4e3a\u6b62\uff0c\u4f60\u8fd8\u8981\u5e87\u62a4\u5979\uff1f\u201d \u3000\u3000\u674e\u7389\u51fd\u4e5f\u5927\u543c\u9053\u2236\u201c\u5f53\u7136\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u7684\u543c\u58f0\u66f4\u5927\uff0c\u6012\u9053\u2236\u201c\u5230\u73b0\u5728\u4e3a\u6b62\uff0c\u4f60\u96be\u9053\u8fd8\u4e0d\u76f8\u4fe1\u5b83\u662f\u77f3\u89c2\u97f3\u90a3\u5973\u9b54\u7684\u95e8 \u4e0b\uff1f\u201d \u3000\u3000\u67f3\u65e0\u7709\u672c\u5df2\u5782\u4e0b\u5934\uff0c\u5ffd\u53c8\u62ac\u8d77\u5934\u6765\uff0c\u5389\u58f0\u9053\u2236\u201c\u4e0d\u9519\uff0c\u6211\u672c\u662f\u77f3\u89c2\u97f3\u95e8\u4e0b\uff0c\u4f46\u6211\u4ece\u6765\u4e5f \u6ca1\u6709\u7792\u7740\u4ed6\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u6014\u4e86\u6014\uff0c\u77aa\u7740\u674e\u7389\u51fd\u9053\u2236\u201c\u4f60\u65e9\u5df2\u77e5\u9053\u5b83\u662f\u77f3\u89c2\u97f3\u6d3e\u5230\u6c5f\u5357\u6765\u5367\u5e95\u7684\u5978\u7ec6\uff0c\u8fd8\u8981 \u5a36\u5979\u4f5c\u8001\u5a46\uff0c\u9664\u4e86\u5979\u4e4b\u5916\uff0c\u5929\u4e0b\u7684\u5973\u4eba\u96be\u9053\u90fd\u6b7b\u5149\u4e86\u4e0d\u6210\uff1f\u201d \u3000\u3000\u67f3\u65e0\u7709\u7d27\u7d27\u63e1\u7740\u674e\u7389\u51fd\u7684\u624b\u81c2\uff0c\u4e0d\u8ba9\u4ed6\u8bf4\u8bdd\u3002 \u3000\u3000\u5979\u81ea\u5df1\u7684\u624b\u4e5f\u5728\u53d1\u6296\uff0c\u98a4\u58f0\u9053\u2236\u201c\u4ec0\u9ebd\u6076\u6bd2\u7684\u8bdd\u90fd\u88ab\u4f60\u4eec\u8bf4\u5c3d\u4e86\uff0c\u80fd\u4e0d\u80fd\u4e5f\u8ba9\u6211\u8bf4\u51e0\u53e5 \u8bdd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u4e86\u7b11\uff0c\u9053\u2236\u201c\u5728\u4e0b\u6b63\u5728\u6d17\u8033\u606d\u542c\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u9053\u2236\u201c\u77f3\u89c2\u97f3\u6240\u6536\u7684\u5f1f\u5b50\uff0c\u53ea\u6709\u6211\u548c\u66f2\u65e0\u5bb9\u662f\u4ece\u5c0f\u5c31\u8ddf\u7740\u5979\u957f\u5927\u7684\uff0c\u6211\u4eec\u4e24\u4eba\u90fd \u662f\u5b64\u513f\uff0c\u751a\u81f3\u8fde\u81ea\u5df1\u7236\u6bcd\u7684\u540d\u59d3\u90fd\u4e0d\u77e5\u9053\uff0c\u5979\u672c\u6765\u66ff\u6211\u53d6\u4e86\u4e2a\u540d\u5b57\uff0c\u6211\u5230\u8fd9\u91cc\u5f8c\uff0c\u624d\u6307\u67f3\u4e3a \u59d3\uff0c\u6613\u540d\u65e0\u7709\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\u2236\u201c\u66f2\u65e0\u5bb9\u7684\u540d\u5b57\uff0c\u83ab\u975e\u4e5f\u662f\u5bb9\u8c8c\u88ab\u6bc1\u4e4b\u5f8c\u624d\u66f4\u6539\u7684\u9ebd\uff1f\u201d \u3000\u3000\u67f3\u65e0\u7709\u9053\u2236\u201c\u4e0d\u9519\uff0c\u5979\u672c\u6765\u53eb\u505a\u65e0\u601d\uff0c\u6211\u672c\u6765\u53eb\u65e0\u5fc6\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u53f9\u4e86\u53e3\u6c14\uff0c\u54fa\u54fa\u9053\u2236\u201c\u65e0\u601d\u3001\u65e0\u4ebf\u3001\u65e0\u82b1\u2026\u2026\u5509\uff01\u201d \u3000\u3000\u67f3\u65e0\u7709\u9053\u2236\u201c\u5979\u867d\u7136\u60f3\u8981\u6211\u4eec\u65e0\u601d\u65e0\u5fc6\uff0c\u600e\u5948\u6211\u4eec\u5374\u4e5f\u662f\u6709\u8840\u6709\u8089\u7684\u4eba\uff0c\u6bcf\u4e2a\u4eba\u957f\u5927\u4e86 \u90fd\u4f1a\u601d\u5ff5\u81ea\u5df1\u7684\u7236\u6bcd\uff0c\u6211\u4eec\u81ea\u7136\u4e5f\u4e0d\u4f8b\u5916\uff0c\u600e\u5948\u5979\u5374\u59cb\u7ec8\u4e0d\u80af\u8bf4\u51fa\u6211\u4eec\u7684\u7236\u6bcd\u662f\u8c01\uff0c\u6211\u4eec\u53ea \u8981\u4e00\u63d0\u8fd9\u4ef6\u4e8b\uff0c\u5979\u5c31\u4f1a\u53d1\u813e\u6c14\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u53f9\u9053\u2236\u201c\u5979\u5bf9\u5979\u95e8\u4e0b\u5b50\u5f1f\u7684\u624b\u6bb5\uff0c\u6211\u662f\u4eb2\u773c\u77a7\u89c1\u8fc7\u7684\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u9053\u2236\u201c\u5979\u53ea\u6709\u5bf9\u6211\u548c\u66f2\u65e0\u5bb9\u4e24\u4eba\u7279\u522b\u597d\u4e9b\uff0c\u4e0d\u8fc7\u66f2\u65e0\u5bb9\u7684\u6027\u60c5\u6bd4\u8f83\u5b64\u50fb\u521a\u5f3a\uff0c\u53c8 \u4e0d\u4f1a\u8bf4\u8ba8\u5979\u6b22\u559c\u7684\u8bdd\uff0c\u6211\u5374\u6bd4\u8f83\u2026\u2026\u201d \u3000\u3000\u80e1\u94c1\u82b1\u51b7\u7b11\u622a\u53e3\u9053\u2236\u201c\u4f60\u5374\u6bd4\u8f83\u4f1a\u62cd\u4eba\u7684\u9a6c\u5c41\uff0c\u8fd9\u6211\u5012\u77e5\u9053\u7684\uff0c\u4f60\u82e5\u60f3\u8ba8\u4eba\u6b22\u559c\u65f6\u6240\u8bf4 \u7684\u8bdd\uff0c\u542c\u5f97\u4eba\u8033\u6735\u90fd\u8981\u6d41\u51fa\u6cb9\u6765\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u6839\u672c\u4e0d\u7406\u4ed6\uff0c\u53ea\u662f\u6309\u7740\u9053\u2236\u201c\u5728\u522b\u4eba\u773c\u4e2d\u770b\u6765\uff0c\u77f3\u89c2\u97f3\u597d\u50cf\u771f\u7684\u662f\u77f3\u5934\u96d5\u6210\u7684\uff0c \u4f46\u5979\u4e5f\u662f\u4e2a\u6709\u8840\u6709\u8089\u7684\u4eba\uff0c\u5979\u4e5f\u6709\u4eba\u7684\u5f31\u70b9\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\u2236\u201c\u54e6\uff01\u201d \u3000\u3000\u67f3\u65e0\u7709\u9053\u2236\u201c\u6709\u65f6\u4faf\uff0c\u5979\u4e5f\u4f1a\u89c9\u5f97\u5fe7\u6101\u70e6\u607c\uff0c\u5bc2\u5bde\u75db\u82e6\uff0c\u5230\u4e86\u8fd9\u79cd\u65f6\u5019\uff0c\u5979\u4e5f\u4f1a\u501f\u9152\u6d47 \u6101\uff0c\u800c\u4e14\u5e38\u4f1a\u559d\u5f97\u5927\u9189\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u5931\u58f0\u9053\u2236\u201c\u60f3\u4e0d\u5230\u77f3\u89c2\u97f3\u8fd8\u6709\u8fd9\u9ebd\u6837\u4e00\u4ef6\u597d\u5904\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u9053\u2236\u201c\u5c31\u56e0\u4e3a\u5979\u5bf9\u6211\u6bd4\u8f83\u4eb2\u8fd1\uff0c\u6240\u4ee5\u5e38\u5e38\u8981\u6211\u966a\u5979\u4e00\u9f50\u559d\u9152\uff0c\u6709\u4e00\u5929\u5979\u53c8\u559d\u9189 \u4e86\uff0c\u7adf\u5bf9\u6211\u8bf4\u51fa\u4e00\u4ef6\u79d8\u5bc6\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\u2236\u201c\u4ec0\u9ebd\u79d8\u5bc6\uff1f\u201d \u3000\u3000\u67f3\u65e0\u7709\u9053\u2236\u201c\u90a3\u5929\u559d\u5230\u5929\u5feb\u4eae\u7684\u65f6\u5019\uff0c\u5979\u5df2\u9189\u5f97\u773c\u775b\u53d1\u76f4\uff0c\u5ffd\u7136\u544a\u8bc9\u6211\uff0c\u66f2\u65e0\u5bb9\u7684\u7236\u6bcd \u5c31\u662f\u88ab\u5979\u6740\u6b7b\u7684\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u52a8\u5bb9\u9053\u2236\u201c\u5979\u96be\u9053\u5c31\u4e3a\u4e86\u8981\u6536\u66f2\u65e0\u5bb9\u505a\u5f92\u5f1f\uff0c\u6240\u4ee5\u5c31\u6740\u6b7b\u5b83\u7684\u7236\u6bcd\uff1f\u201d \u3000\u3000\u67f3\u65e0\u7709\u9053\u2236\u201c\u6b63\u662f\u5982\u6b64\u3002\u201d \u3000\u3000\u5979\u7684\u58f0\u97f3\u5df2\u56e0\u6fc0\u52a8\u800c\u5636\u54d1\uff0c\u6c89\u9ed8\u4e86\u534a\u664c\uff0c\u624d\u63a5\u7740\u9053\u2236\u201c\u6211\u542c\u4e86\u5b83\u7684\u8bdd\uff0c\u53c8\u662f\u5403\u60ca\uff0c\u53c8\u662f \u5bb3\u6015\uff0c\u5f53\u65f6\u6211\u5c31\u60f3\u5230\uff0c\u66f2\u65e0\u5bb9\u7684\u7236\u6bcd\u65e2\u662f\u88ab\u5979\u6740\u6b7b\u7684\uff0c\u90a3\u9ebd\u6211\u7684\u7236\u6bcd\u5462\uff1f\u201d \u3000\u3000\u542c\u5230\u8fd9\u91cc\uff0c\u80e1\u94c1\u82b1\u4e5f\u4e0d\u7981\u4e3a\u4e4b\u52a8\u5bb9\uff0c\u5fcd\u4e0d\u4f4f\u9053\u2236\u201c\u4f60\u4e3a\u4ec0\u9ebd\u4e0d\u8d81\u5979\u559d\u9189\u65f6\u95ee\u95ee\u5979\uff1f\u201d \u3000\u3000\u67f3\u65e0\u7709\u9053\u2236\u201c\u6211\u81ea\u7136\u95ee\u8fc7\u5979\uff0c\u5979\u5374\u8bf4\uff0c\u6211\u7684\u8eab\u4e16\u548c\u66f2\u65e0\u5bb9\u4e0d\u540c\uff0c\u6211\u662f\u522b\u4eba\u7684\u5f03\u5a74\uff0c\u8fde\u5979 \u90fd\u4e0d\u77e5\u9053\u6211\u7684\u7236\u6bcd\u662f\u8c01\uff0c\u6211\u518d\u95ee\u5979\uff0c\u5979\u5c31\u62c9\u6211\u75db\u54ed\u8d77\u6765\uff0c\u8bf4\u5979\u81ea\u5df1\u8fde\u4e00\u4e2a\u4eb2\u4eba\u90fd\u6ca1\u6709\uff0c\u59cb\u7ec8 \u5c06\u6211\u5f53\u505a\u5979\u4eb2\u751f\u7684\u5973\u513f\u4e00\u822c\u770b\u5f85\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u5fcd\u4e0d\u4f4f\u53c8\u9053\u2236\u201c\u5979\u4e00\u54ed\uff0c\u4f60\u5c31\u76f8\u4fe1\u4e86\u9ebd\uff1f\u201d \u3000\u3000\u67f3\u65e0\u7709\u63c9\u4e86\u63c9\u773c\u775b\uff0c\u9053\u2236\u201c\u6211\u867d\u7136\u4e0d\u4fe1\uff0c\u5374\u4e5f\u627e\u4e0d\u51fa\u4ec0\u9ebd\u8bc1\u636e\uff0c\u66f4\u4e0d\u6562\u5c06\u8fd9\u79d8\u5bc6\u544a\u8bc9\u66f2 \u65e0\u5bb9\uff0c\u56e0\u4e3a\u6211\u82e5\u544a\u8bc9\u4e86\u5979\uff0c\u53cd\u800c\u7b49\u65bc\u5bb3\u4e86\u5979\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u54ce\u9053\u2236\u201c\u4e0d\u9519\uff0c\u77f3\u781a\u97f3\u82e5\u77e5\u9053\u66f2\u65e0\u5bb9\u5df2\u53d1\u73b0\u9053\u79d8\u5bc6\uff0c\u5979\u7edd\u4e0d\u4f1a\u518d\u7559\u5979\u6d3b\u5728\u4e16\u4e0a \u7684\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u9053\u2236\u201c\u4ece\u90a3\u5929\u665a\u4e0a\u4e4b\u5f8c\uff0c\u6211\u8868\u9762\u4e0a\u770b\u6765\u867d\u7136\u8fd8\u662f\u548c\u4ee5\u524d\u4e00\u6837\uff0c\u5176\u5b9e\u7684\u5fc3\u91cc\u5df2\u7ecf\u53d8 \u5316\uff0c\u518d\u4e5f\u4e0d\u80fd\u50cf\u4ee5\u524d\u90a3\u9ebd\u6837\u548c\u5979\u4eb2\u8fd1\u4e86\u3002\u201d \u3000\u3000\u5730\u51fa\u51fa\u7684\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u6309\u7740\u9053\u2236\u201c\u66f2\u65e0\u5bb9\u7684\u53d8\u5316\u5374\u6bd4\u627e\u66f4\u5927\uff0c\u5979\u5e74\u7eaa\u8d8a\u957f\uff0c\u5bf9\u77f3\u89c2\u97f3\u5c31 \u8d8a\u758f\u8fdc\uff0c\u5979\u5c31\u597d\u50cf\u662f\u4e00\u6735\u5728\u7a7a\u8c37\u4e2d\u7684\u5e7d\u5170\uff0c\u770b\u6765\u603b\u662f\u90a3\u9ebd\u51b7\u6f20\uff0c\u90a3\u9ebd\u9ad8\u8d35\uff0c\u90a3\u9ebd\u5fe7\u90c1\uff0c\u90a3\u9ebd \u7f8e\uff0c\u6211\u867d\u7136\u662f\u4e2a\u5973\u4eba\uff0c\u4f46\u8fde\u6211\u90fd\u89c5\u5f97\u5979\u5b9e\u5728\u662f\u771f\u7f8e\uff0c\u7f8e\u5f97\u4ee4\u4eba\u4e0d\u6562\u53bb\u6cbe\u67d3\u5979\uff0c\u66f4\u4e0d\u6562\u53bb\u6500\u6298 \u5979\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u627c\u8155\u957f\u53f9\u9053\u2236\u201c\u53ea\u53ef\u60dc\u6211\u4eec\u7adf\u65e0\u7f18\u4e00\u7779\u5979\u90a3\u65f6\u7684\u989c\u8272\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u9eef\u7136\u9053\u2236\u201c\u53ea\u53ef\u60dc\u5929\u5992\u7ea2\u989c\uff0c\u6211\u2026\u2026\u6211\u5b9e\u5728\u4e5f\u672b\u60f3\u5230\u77f3\u89c2\u97f3\u7adf\u4f1a\u6bc1\u53bb\u5979\u7684\u5bb9 \u8c8c\u2026\u2026\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\u2236\u201c\u4f60\u4e5f\u77e5\u9053\u90a3\u662f\u77f3\u89c2\u97f3\u4e0b\u7684\u6bd2\u624b\uff1f\u201d \u3000\u3000\u67f3\u65e0\u7709\u9053\u2236\u201c\u6211\u77e5\u9053\u3002\u201d \u3000\u3000\u5979\u54ac\u7740\u7259\u63a5\u9053\u2236\u201c\u6211\u77e5\u9053\u8fd9\u4ef6\u4e8b\u5f8c\uff0c\u66f4\u89c9\u5f97\u65e0\u6cd5\u548c\u77f3\u89c2\u97f3\u76f8\u5904\u4e86\uff0c\u5979\u867d\u7136\u518d\u53c1\u544a\u8bc9\u6211\uff0c \u53eb\u6211\u653e\u5fc3\uff0c\u8bf4\u5979\u7edd\u4e0d\u4f1a\u5411\u6211\u4e0b\u6bd2\u624b\u7684\uff0c\u53ef\u662f\u5728\u6211\u773c\u4e2d\uff0c\u5979\u5df2\u53d8\u6210\u4e86\u4e00\u6761\u6bd2\u86c7\uff0c\u5979\u53ea\u8981\u77a7\u6211\u4e00 \u773c\uff0c\u6211\u90fd\u65e0\u6cd5\u5fcd\u53d7\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u76ee\u5149\u95ea\u52a8\uff0c\u9053\u2236\u201c\u4f60\u96be\u9053\u662f\u9003\u51fa\u6765\u7684\u9ebd\uff1f\u201d \u3000\u3000\u67f3\u65e0\u7709\u9053\u2236\u201c\u6211\u6ca1\u6709\u9003\uff0c\u6211\u82e5\u60f3\u9003\uff0c\u4e5f\u5c31\u6d3b\u4e0d\u5230\u73b0\u5728\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\u2236\u201c\u90a3\u9ebd\u4f60\u2026\u2026\u201d \u3000\u3000\u67f3\u65e0\u7709\u9053\u2236\u201c\u6211\u53ea\u662f\u8bf4\u2236\u6211\u5df2\u662f\u5927\u4eba\u4e86\uff0c\u5df2\u7ecf\u5e94\u8be5\u51fa\u6765\u89c1\u89c1\u4e16\u9762\uff0c\u6211\u4ece\u5c0f\u5c31\u751f\u957f\u5728\u90a3\u8352 \u6f20\u7684\u7a77\u8c37\u4e2d\uff0c\u8fde\u5916\u9762\u7684\u4e16\u754c\u662f\u4ec0\u9ebd\u6837\u5b50\u90fd\u4e0d\u77e5\u9053\uff0c\u6240\u4ee5\u627e\u6c42\u5979\u653e\u6211\u51fa\u6765\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\u2236\u201c\u5979\u600e\u9ebd\u8bf4\uff1f\u201d \u3000\u3000\u67f3\u65e0\u7709\u9053\u2236\u201c\u5979\u4ec0\u9ebd\u90fd\u6ca1\u6709\u8bf4\uff0c\u53ea\u95ee\u6211\uff0c\u4ec0\u9ebd\u65f6\u5019\u8981\u8d70\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\u2236\u201c\u4f60\u600e\u9ebd\u8bf4\uff1f\u201d \u3000\u3000\u67f3\u65e0\u7709\u9053\u2236\u201c\u90a3\u65f6\u6211\u53ea\u89c9\u8fde\u4e00\u5929\u90fd\u803d\u4e0d\u4e0b\u53bb\u4e86\uff0c\u6211\u5c31\u8bf4\u2236\u6700\u597d\u662f\u660e\u5929\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\u2236\u201c\u5979\u96be\u9053\u5c45\u7136\u7b54\u5e94\u4e86\u9ebd\uff1f\u201d \u3000\u3000\u67f3\u65e0\u7709\u9053\u2236\u201c\u5979\u542c\u4e86\u6211\u7684\u8bdd\uff0c\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u5ffd\u7136\u8bf4\u2236\u201c\u597d\uff0c\u6211\u4eca\u5929\u665a\u4e0a\u66ff\u4f60\u996f\u884c\u3002\u201d\u6211 \u4e5f\u60f3\u4e0d\u5230\u5979\u5c45\u7136\u4f1a\u7b54\u5e94\uff0c\u771f\u662f\u5f00\u5fc3\u6781\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\u2236\u201c\u4f60\u5f00\u5fc3\u5f97\u53ea\u6015\u8fd8\u592a\u65e9\u4e86\u4e9b\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u9053\u2236\u201c\u5f53\u5929\u665a\u4e0a\uff0c\u5979\u679c\u7136\u51c6\u5907\u4e86\u9152\u83dc\u4e3a\u6211\u996f\u884c\uff0c\u6211\u2026\u2026\u6211\u7a76\u7adf\u662f\u5979\u517b\u5927\u7684\uff0c\u60f3\u5230 \u660e\u5929\u5c31\u8981\u548c\u5979\u5206\u522b\uff0c\u5fc3\u91cc\u4e5f\u4e0d\u514d\u6709\u4e9b\u4f24\u611f\uff0c\u60f3\u5230\u5979\u7adf\u5982\u6b64\u723d\u5feb\u7684\u8ba9\u6211\u8d70\uff0c\u53c8\u4e0d\u514d\u5bf9\u5979\u6068\u611f \u6fc0\uff0c\u6240\u4ee5\u90a3\u5929\u665a\u4e0a\uff0c\u6211\u53c8\u966a\u5979\u559d\u4e86\u4e00\u591c\u3002\u201d \u3000\u3000\u542c\u5230\u8fd9\u91cc\uff0c\u80e1\u94c1\u82b1\u4f3c\u4e5f\u9690\u9690\u89c9\u51fa\u5979\u8bdd\u4e2d\u7684\u6076\u610f\uff0c\u5fc3\u91cc\u7adf\u4e5f\u6709\u4e9b\u4e3a\u5979\u7d27\u5f20\u8d77\u6765\uff0c\u5fcd\u4e0d\u4f4f\u95ee \u9053\u2236\u201c\u7b2c\u4e8c\u5929\u5462\uff1f\u201d \u3000\u3000\u67f3\u65e0\u7709\u9762\u4e0a\u5374\u6728\u65e0\u8868\u60c5\uff0c\u6de1\u6de1\u9053\u2236\u201c\u7b2c\u4e8c\u5929\u65e9\u4e0a\uff0c\u5979\u5c31\u9001\u6211\u51fa\u8c37\uff0c\u653e\u6211\u8d70\u4e86\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u6014\u4e86\u6014\uff0c\u9053\u2236\u201c\u5979\u5c31\u8fd9\u6837\u653e\u4f60\u8d70\u4e86\u9ebd\uff1f\u201d \u3000\u3000\u67f3\u65e0\u7709\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u9762\u4e0a\u867d\u7136\u4ec0\u9ebd\u8868\u60c5\u90fd\u6ca1\u6709\uff0c\u8138\u8272\u5374\u82cd\u767d\u5f97\u53ef\u6015\uff0c\u773c\u775b\u91cc\u66f4\u5145\u6ee1\u4e86\u6028 \u6bd2\u4e4b\u8272\u3002 \u3000\u3000\u5979\u4e00\u5b57\u5b57\u7f13\u7f13\u8bf4\u9053\u2236\u201c\u5979\u5c31\u8fd9\u6837\u653e\u6211\u8d70\u4e86\uff0c\u56e0\u4e3a\u5979\u7b97\u51c6\u6211\u4e00\u5b9a\u4f1a\u56de\u53bb\u7684\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\u2236\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u67f3\u65e0\u7709\u9053\u2236\u201c\u6211\u8fd8\u672a\u8d70\u51fa\u4e94\u767e\u91cc\uff0c\u5c31\u89c9\u5f97\u8179\u75db\u5982\u7ede\uff0c\u5c31\u597d\u50cf\u6709\u6761\u6781\u5c0f\u7684\u6bd2\u86c7\u5728\u6211\u7684\u80a0\u5b50 \u91cc\u8815\u52a8\u7740\uff0c\u7528\u6bd2\u624d\u5728\u54ac\u7740\u6211\u7684\u5fc3\u809d\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u542c\u5f97\u5168\u8eab\u5bd2\u6bdb\u90fd\u7ad6\u4e86\u8d77\u6765\uff0c\u9053\u2236\u201c\u9152\u2026\u2026\u9152\u4e2d\u6709\u6bd2\uff1f\u201d \u3000\u3000\u67f3\u65e0\u7709\u54ac\u7740\u7259\uff0c\u9053\u2236\u201c\u4e0d\u9519\uff0c\u9152\u4e2d\u6709\u6bd2\uff0c\u6240\u4ee5\u5979\u7b97\u51c6\u6211\u4e00\u5b9a\u4f1a\u722c\u7740\u56de\u53bb\u6c42\u5979\uff0c\u5426\u5219\u6211\u5c31 \u8981\u6b7b\u5728\u6c99\u6f20\u91cc\uff0c\u8fde\u6536\u5c38\u7684\u4eba\u90fd\u6ca1\u6709\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u6012\u9053\u2236\u201c\u5979\u65e2\u5df2\u7b54\u5e94\u653e\u4f60\u662f\uff0c\u4e3a\u4ec0\u9ebd\u53c8\u8981\u5728\u9152\u4e2d\u4e0b\u6bd2\uff1f\u201d \u3000\u3000\u67f3\u65e0\u7709\u5636\u58f0\u9053\u2236\u201c\u56e0\u4e3a\u5979\u8981\u6211\u77e5\u9053\u5b83\u7684\u5389\u5bb3\uff0c\u8981\u6211\u6c38\u8fdc\u4e0d\u6562\u80cc\u53db\u5979\uff0c\u8981\u6211\u8dea\u5728\u5730\u4e0a\u6c42 \u5979\u2026\u2026\u5979\u559c\u6b22\u770b\u522b\u4eba\u54c0\u6c42\u5b83\u7684\u6837\u5b50\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u5583\u5583\u9053\u2236\u201c\u5e78\u597d\u8fd9\u4eba\u73b0\u5728\u5df2\u7ecf\u6b7b\u4e86\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u5df2\u63a5\u7740\u9053\u2236\u201c\u5979\u867d\u7136\u9634\u9669\u6bd2\u8fa3\uff0c\u5374\u8fd8\u662f\u5fd8\u8bb0\u4e86\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\u2236\u201c\u4ec0\u9ebd\u4e8b\uff1f\u201d \u3000\u3000\u67f3\u65e0\u7709\u9053\u2236\u201c\u5979\u5fd8\u4e86\u81ea\u5df1\u559d\u9189\u9152\u7684\u65f6\u5019\uff0c\u66fe\u7ecf\u544a\u8bc9\u6211\u8bb8\u591a\u79d8\u5bc6\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\u2236\u201c\u5979\u96be\u9053\u5c06\u89e3\u6bd2\u7684\u79d8\u5bc6\u4e5f\u544a\u8bc9\u4e86\u4f60\uff1f\u201d \u3000\u3000\u67f3\u65e0\u7709\u51b7\u51b7\u9053\u2236\u201c\u6211\u662f\u5b83\u7684\u95e8\u4e0b\uff0c\u81ea\u7136\u4e5f\u5b66\u4f1a\u4e0d\u5c11\u4e0b\u6bd2\u89e3\u6bd2\u7684\u672c\u4e8b\uff0c\u5426\u5219\u9601\u4e0b\u53ea\u6015\u4e5f\u6d3b \u4e0d\u5230\u73b0\u5728\u4e86\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u53ea\u6709\u63c9\u9f3b\u5b50\uff0c\u65e0\u8bdd\u53ef\u8bf4\u3002 \u3000\u3000\u695a\u7559\u9999\u9053\u2236\u201c\u4f46\u5979\u5bf9\u4f60\u4e0b\u7684\u6bd2\uff0c\u5374\u5fc5\u5b9a\u662f\u5979\u672a\u66fe\u6559\u7ed9\u4f60\u89e3\u6cd5\u7684\uff0c\u4f60\u751a\u81f3\u6839\u672c\u5c31\u4e0d\u77e5\u9053\u5979 \u4e0b\u7684\u662f\u4ec0\u9ebd\u6bd2\uff0c\u53c8\u5982\u4f55\u53bb\u89e3\uff1f\u201d \u3000\u3000\u67f3\u65e0\u7709\u9053\u2236\u201c\u8fd9\u9053\u7406\u6211\u81ea\u7136\u660e\u767d\uff0c\u53ef\u662f\u5979\u5374\u544a\u8bc9\u8fc7\u6211\uff0c\u7f42\u7c9f\u867d\u80fd\u4f7f\u4eba\u6c89\u6ca6\uff0c\u4f46\u6709\u65f6\u5374\u4e5f \u662f\u6b62\u75db\u89e3\u6bd2\u7684\u826f\u836f\uff0c\u56e0\u4e3a\u5b83\u80fd\u4f7f\u4eba\u5b8c\u5168\u9ebb\u9189\uff0c\u6839\u672c\u5fd8\u8bb0\u4e86\u75db\u82e6\uff0c\u6240\u4ee5\u6211\u65e9\u5c31\u5077\u5077\u85cf\u4e86\u4e00\u5323\u81ea \u7f42\u7c9f\u63d0\u70bc\u51fa\u7684\u767d\u8272\u7c89\u672b\uff0c\u56e0\u4e3a\u6211\u968f\u65f6\u90fd\u5728\u63d0\u9632\u7740\u5979\u4e0b\u6bd2\u624b\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\u2236\u201c\u4e00\u65e6\u4e00\u4e2a\u4eba\u82e5\u662f\u7ec8\u65e5\u5728\u9ebb\u9189\u4e2d\uff0c\u53c8\u4e0e\u6b7b\u4f55\u5f02\uff1f\u201d \u3000\u3000\u67f3\u65e0\u7709\u9053\u2236\u201c\u6211\u81ea\u7136\u4e5f\u77e5\u9053\u82e5\u4ee5\u7f42\u7c9f\u6765\u6b62\u75db\uff0c\u5b9e\u5728\u65e0\u5f02\u996e\u9e29\u6b62\u6e34\uff0c\u4f46\u662f\u90a3\u65f6\u6211\u5b9e\u5728\u5df2\u75db \u5f97\u65e0\u6cd5\u5fcd\u53d7\uff0c\u800c\u4e14\u6211\u5b81\u6b7b\u4e5f\u4e0d\u613f\u518d\u56de\u53bb\u6c42\u5979\uff0c\u6c38\u8fdc\u505a\u5b83\u7684\u5974\u96b6\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u957f\u53f9\u9053\u2236\u201c\u65bc\u662f\u4f60\u5c31\u505a\u4e86\u7f42\u7c9f\u7684\u5974\u96b6\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u9eef\u7136\u5782\u4e0b\u4e86\u5934\uff0c\u5979\u4e0d\u613f\u7834\u4eba\u770b\u5230\u5979\u7684\u8138\u3002 \u3000\u3000\u5979\u7684\u8138\u5df2\u56e0\u75db\u82e6\u800c\u626d\u66f2\u3002 \u3000\u3000\u53e3\u53e3\u53e3 \u3000\u3000\u80e1\u94c1\u82b1\u6014\u4e86\u6014\uff0c\u9053\u2236\u201c\u6211\u660e\u660e\u542c\u5230\u4f60\u5728\u5c4b\u91cc\u7a77\u53eb\uff0c\u53c8\u600e\u80fd\u51fa\u6765\u6697\u7b97\u4eba\u5462\uff1f\u4f60\u2026\u2026\u65bc\u4f1a\u5206 \u8eab\u672f\u5427\uff1f\u201d \u3000\u3000\u67f3\u65e0\u7709\u9053\u2236\u201c\u7f42\u7c9f\u6b62\u75db\u867d\u5df2\u4e0d\u5982\u4ece\u524d\u6709\u6548\uff0c\u4f46\u4e5f\u7528\u4e0d\u7740\u90a3\u9ebd\u591a\u65f6\u5019\uff0c\u6211\u542c\u5f97\u4f60\u4eec\u5df2\u8d70\u51fa \u9662\u5b50\uff0c\u5c31\u8981\u4e00\u4e2a\u5c0f\u4e2b\u5934\u88c5\u51fa\u6211\u7684\u547b\u541f\u58f0\uff0c\u6bcf\u4e2a\u4eba\u75db\u82e6\u65f6\u58f0\u97f3\u90fd\u4f1a\u53d8\u6837\u5b50\u7684\uff0c\u6240\u4ee5\u4f60\u4eec\u5c31\u7b97\u89c9 \u5f97\u58f0\u97f3\u6709\u5f02\uff0c\u4e5f\u4e0d\u4f1a\u6000\u7591\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\u2236\u201c\u4f60\u5c06\u66b4\u96e8\u68a8\u82b1\u9489\u629b\u5728\u6811\u6797\u91cc\uff0c\u81ea\u7136\u4e5f\u662f\u4e3a\u4e86\u6015\u88ab\u6211\u4eec\u53d1\u73b0\u4e86\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u9053\u2236\u201c\u55ef\uff01\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\u2236\u201c\u4f60\u4eec\u6839\u672c\u6ca1\u6709\u53bb\u627e\u90a3\u4e03\u6839\u6307\u5934\u7684\u8001\u524d\u8f88\uff0c\u56e0\u4e3a\u4e16\u754c\u4e0a\u6839\u672c\u5c31\u6ca1\u6709\u90a3\u9ebd\u6837\u4e00 \u4e2a\u4eba\uff0c\u662f\u9ebd\uff1f\u201d \u3000\u3000\u67f3\u65e0\u7709\u7b11\u4e86\u7b11\uff0c\u9053\u2236\u201c\u975e\u4f46\u6ca1\u6709\u4ed6\u8fd9\u4e2a\u4eba\uff0c\u5c31\u8fde\u201c\u718a\u8001\u4f2f\u201d\u4e5f\u662f\u675c\u64b0\u7684\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\u2236\u201c\u4f60\u4eec\u6545\u610f\u8bf4\u662f\u8981\u53bb\u627e\u4eba\uff0c\u5c31\u56e0\u4e3a\u4f60\u4eec\u5df2\u82b1\u4e86\u5eff\u4e07\u4e24\u94f6\u5b50\u4e70\u4e86\u4e2a\u51f6\u624b\uff0c\u4ed6\u884c \u523a\u7684\u65f6\u5019\uff0c\u4f60\u4eec\u4e00\u5b9a\u4e0d\u80fd\u5728\u573a\uff0c\u5426\u5219\u4f60\u4eec\u5c31\u4e0d\u5fc5\u627e\u4ed6\u6765\u4e86\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u9053\u2236\u201c\u6b63\u662f\u5982\u6b64\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\u2236\u201c\u8c01\u77e5\u4ed6\u5374\u88ab\u695a\u7559\u9999\u6349\u4f4f\u4e86\uff0c\u4f60\u4eec\u6015\u6cc4\u6f0f\u673a\u5bc6\uff0c\u5c31\u53ea\u6709\u5c06\u4ed6\u6740\u4e86\u706d\u53e3\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u9053\u2236\u201c\u4e00\u70b9\u4e5f\u4e0d\u9519\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u77a7\u7740\u695a\u7559\u9999\uff0c\u82e6\u7b11\u9053\u2236\u201c\u6211\u73b0\u5728\u624d\u77e5\u9053\u4f60\u771f\u662f\u4e2a\u6d3b\u8bf8\u845b\uff0c\u7b80\u76f4\u6599\u4e8b\u5982\u795e\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u9762\u4e0a\u4e5f\u4e0d\u7981\u9732\u51fa\u60ca\u8bb6\u4e4b\u8272\uff0c\u9053\u2236\u201c\u8fd9\u4e9b\u4e8b\uff0c\u4f60\u96be\u9053\u65e9\u5df2\u731c\u5230\u4e86\u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u53f9\u9053\u2236\u201c\u4f46\u6211\u5b9e\u5728\u731c\u4e0d\u51fa\u4f60\u4e3a\u4f55\u8981\u6740\u6211\uff1f\u4f60\u65e2\u975e\u4e3a\u77f3\u89c2\u97f3\u62a5\u4ec7\uff0c\u5374\u662f\u4e3a\u4e86\u4ec0\u9ebd \u5462\uff1f\u201d \u3000\u3000\u67f3\u65e0\u7709\u53c8\u6c89\u9ed8\u4e86\u8bb8\u4e45\uff0c\u7f13\u7f13\u9053\u2236\u201c\u662f\u4e3a\u4e86\u6211\u81ea\u5df1\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u8bb6\u7136\u9053\u2236\u201c\u4f60\u81ea\u5df1\uff1f\u4f60\u81ea\u5df1\u96be\u9053\u548c\u6211\u6709\u4ec0\u9ebd\u4ec7\u6068\uff1f\u201d \u3000\u3000\u67f3\u65e0\u7709\u9053\u2236\u201c\u6211\u548c\u4f60\u5e76\u6ca1\u6709\u4ec7\u6068\uff0c\u4f46\u662f\u4f60\u4e0d\u6b7b\uff0c\u6211\u5c31\u5f97\u6b7b\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u66f4\u60ca\u8bb6\uff0c\u9053\u2236\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u67f3\u65e0\u7709\u9eef\u7136\u9053\u2236\u201c\u8fd1\u5e74\u6765\uff0c\u6211\u6bd2\u53d1\u7684\u6b21\u6570\u8d8a\u6765\u8d8a\u5bc6\uff0c\u9700\u8981\u7684\u7f42\u7c9f\u4e5f\u8d8a\u6765\u8d8a\u591a\uff0c\u6211\u5e26\u51fa\u6765 \u7684\u90a3\u4e00\u5323\u65e9\u5df2\u7528\u5b8c\u4e86\uff0c\u8981\u5230\u6c5f\u6e56\u4e0a\u53bb\u641c\u8d2d\uff0c\u66f4\u4e0d\u77e5\u6709\u591a\u9ebd\u56f0\u96be\uff0c\u6211\u4e5f\u77e5\u9053\u50cf\u8fd9\u6837\u5b50\u4e0b\u53bb\uff0c\u6211 \u7eb5\u4e0d\u6b7b\u65bc\u77f3\u89c2\u97f3\u4e4b\u6bd2\uff0c\u4e5f\u8981\u6b7b\u65bc\u7f42\u7c9f\u4e4b\u6bd2\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\u2236\u201c\u786e\u662f\u5982\u6b64\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u9053\u2236\u201c\u6211\u81ea\u5df1\u53d7\u82e6\u5012\u6ca1\u4ec0\u9ebd\uff0c\u4f46\u2026\u2026\u4f46\u6211\u5b9e\u5728\u4e0d\u5fcd\u62d6\u7d2f\u4e86\u4ed6\uff0c\u4ed6\u4e3a\u4e86\u6211\u8fd9\u75c5\uff0c\u4e3a \u4e86\u53bb\u627e\u7f42\u7c9f\uff0c\u5df2\u4e0d\u77e5\u82b1\u4e86\u591a\u5c11\u94b1\uff0c\u53d7\u4e86\u591a\u5c11\u82e6\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u9762\u8272\u60e8\u767d\uff0c\u54ac\u7259\u9053\u2236\u201c\u8fd9\u4ef6\u4e8b\u4f60\u4e0d\u5fc5\u8bf4\u7684\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u51c4\u7136\u9053\u2236\u201c\u4e8b\u5df2\u81f3\u6b64\uff0c\u6211\u4e00\u5b9a\u8981\u5c06\u6240\u6709\u7684\u4e8b\u5168\u90fd\u8bf4\u51fa\u6765\u2026\u2026\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\u2236\u201c\u4f60\u65e9\u5c31\u8be5\u8bf4\u51fa\u6765\u4e86\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u9053\u2236\u201c\u636e\u6211\u6240\u77e5\uff0c\u77f3\u89c2\u97f3\u5e73\u751f\u53ea\u6015\u4e00\u4e2a\u4eba\uff0c\u5979\u66fe\u8bf4\u8fc7\uff0c\u8fd9\u4eba\u7b80\u76f4\u662f\u5b83\u7684\u514b\u661f\uff0c\u5979 \u6240\u6709\u7684\u672c\u4e8b\uff0c\u82e5\u7528\u5230\u8fd9\u4eba\u9762\u524d\uff0c\u5c31\u53d8\u5f97\u4e0d\u503c\u4e00\u6587\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u5931\u58f0\u9053\u2236\u201c\u54e6\uff01\u4e16\u4e0a\u8fd8\u6709\u8fd9\u9ebd\u6837\u4e00\u4e2a\u4eba\u9ebd\uff1f\u662f\u8c01\uff1f\u201d \u3000\u3000\u67f3\u65e0\u7709\u5e76\u4e0d\u56de\u7b54\uff0c\u53ea\u662f\u6309\u7740\u9053\u2236\u201c\u6240\u4ee5\u627e\u5c31\u60f3\uff0c\u8fd9\u4eba\u53ea\u6015\u80fd\u89e3\u4e86\u77f3\u89c2\u97f3\u7684\u6bd2\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\u2236\u201c\u4f60\u53d1\u89c9\u81ea\u5df1\u4e2d\u6bd2\u7684\u65f6\u5019\uff0c\u5c31\u8be5\u53bb\u627e\u8fd9\u4eba\u4e86\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u9053\u2236\u201c\u6211\u867d\u7136\u65e9\u5df2\u60f3\u53bb\u627e\u4ed6\uff0c\u53ef\u662f\u53c8\u4e00\u76f4\u4e0d\u6562\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\u2236\u201c\u4f60\u6015\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u67f3\u65e0\u7709\u9053\u2236\u201c\u53ea\u56e0\u4ed6\u4e0d\u4f46\u662f\u4e16\u4e0a\u6b66\u529f\u6700\u9ad8\u7684\u4eba\uff0c\u4e5f\u662f\u4e16\u4e0a\u6700\u53ef\u6015\u7684\u4eba\uff0c\u4ed6\u7684\u6027\u683c\u6839\u672c\u65e0 \u6cd5\u6349\u6478\uff0c\u813e\u6c14\u66f4\u662f\u559c\u6012\u65e0\u5e38\uff0c\u65e2\u4e0d\u660e\u662f\u975e\uff0c\u4e5f\u4e0d\u8fa8\u5584\u6076\uff0c\u53ea\u8981\u4ed6\u9ad8\u5174\uff0c\u4ed6\u4ec0\u9ebd\u4e8b\u90fd\u505a\u5f97\u51fa\uff0c \u6740\u6b7b\u4e2a\u628a\u4eba\uff0c\u5728\u4ed6\u8bf4\u6765\u7b80\u5355\u6bd4\u634f\u6b7b\u53ea\u8682\u8681\u8fd8\u5bb9\u6613\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u51b7\u51b7\u9053\u2236\u201c\u8fd9\u6837\u7684\u4eba\uff0c\u6211\u5012\u60f3\u548c\u4ed6\u6597\u6597\u3002\u201d \u7b2c\u5341\u516b\u7ae0\u3000\u4f60\u6b7b\u6211\u6d3b>> \u53e4\u9f99\u300a\u695a\u7559\u9999\u7cfb\u5217\xb7\u753b\u7709\u9e1f\u300b \u7b2c\u5341\u516b\u7ae0\u3000\u4f60\u6b7b\u6211\u6d3b \u3000\u3000\u67f3\u65e0\u7709\u779f\u4e86\u4ed6\u4e00\u773c\uff0c\u76ee\u4e2d\u6ee1\u662f\u8f7b\u8511\u4e4b\u8272\uff0c\u597d\u50cf\u5728\u8bf4\u2236\u201c\u5c31\u51ed\u4f60\u80e1\u94c1\u82b1\uff0c\u4e00\u4e07\u4e2a\u4e5f\u6597\u4e0d\u8fc7 \u4eba\u5bb6\u4e00\u4e2a\u3002\u201d \u3000\u3000\u4f46\u8fd9\u8bdd\u5979\u5e76\u6ca1\u6709\u8bf4\u51fa\u6765\uff0c\u5979\u53ea\u662f\u53f9\u7740\u6c14\u9053\u2236\u201c\u6211\u867d\u7136\u4e0d\u6562\u53bb\u89c1\u4ed6\uff0c\u60c5\u52bf\u5374\u903c\u5f97\u6211\u975e\u53bb\u89c1 \u4ed6\u4e0d\u53ef\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u5fcd\u4e0d\u4f4f\u95ee\u9053\u2236\u201c\u4f60\u5230\u5e95\u89c1\u5230\u4e86\u4ed6\u6ca1\u6709\uff1f\u201d \u3000\u3000\u67f3\u65e0\u7709\u9053\u2236\u201c\u89c1\u7740\u4e86\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\u2236\u201c\u4ed6\u662f\u4e0d\u662f\u80fd\u89e3\u4f60\u7684\u6bd2\u5462\uff1f\u201d \u3000\u3000\u67f3\u65e0\u7709\u9053\u2236\u201c\u4ed6\u81ea\u7136\u80fd\u89e3\uff0c\u4f46\u4ed6\u5374\u6709\u4e2a\u6761\u4ef6\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\u2236\u201c\u4ec0\u9ebd\u6761\u4ef6\uff1f\u201d \u3000\u3000\u67f3\u65e0\u7709\u53f9\u9053\u2236\u201c\u4ed6\u4e5f\u6ca1\u4ec0\u9ebd\u522b\u7684\u6761\u4ef6\uff0c\u53ea\u4e0d\u8fc7\u95ee\u6211\u8981\u4e00\u4ef6\u4e1c\u897f\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u5df2\u7d27\u5f20\u8d77\u6765\uff0c\u5df2\u9690\u7ea6\u731c\u51fa\u90a3\u4eba\u8981\u7684\u662f\u4ec0\u9ebd\u3002 \u3000\u3000\u4f46\u4ed6\u8fd8\u662f\u5fcd\u4e0d\u4f4f\u95ee\u9053\u2236\u201c\u4ed6\u8981\u7684\u662f\u4ec0\u9ebd\u4e1c\u897f\uff1f\u201d \u3000\u3000\u67f3\u65e0\u7709\u4e00\u5b57\u5b57\u9053\u2236\u201c\u4ed6\u8981\u7684\u662f\u695a\u7559\u9999\u7684\u4eba\u5934\u3002\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u8bf4\u51fa\u6765\uff0c\u5927\u5bb6\u5168\u90fd\u6014\u4f4f\u3002 \u3000\u3000\u997c\u4e86\u5f88\u4e45\uff0c\u80e1\u94c1\u82b1\u624d\u77a7\u7740\u695a\u7559\u9999\u7b11\u9053\u2236\u201c\u4f60\u8111\u888b\u91cc\u7a76\u7adf\u6709\u4ec0\u9ebd\u5b9d\u8d1d\uff0c\u4e3a\u4ec0\u9ebd\u60f3\u8981\u4f60\u8111\u888b \u7684\u4eba\u7adf\u6709\u90a3\u9ebd\u591a\uff1f\u201d \u3000\u3000\u67f3\u65e0\u7709\u5782\u4e0b\u4e86\u5934\uff0c\u7f13\u7f13\u9053\u2236\u201c\u6211\u548c\u4f60\u65e0\u51a4\u65e0\u4ec7\uff0c\u672c\u4e0d\u5fcd\u4e3a\u4e86\u8fd9\u79cd\u4e8b\u6740\u4f60\u7684\uff0c\u4f46\u90a3\u4eba\u5374 \u8bf4\uff0c\u6211\u4e2d\u6bd2\u5df2\u6df1\uff0c\u6700\u591a\u53ea\u6709\u4e24\u53c1\u4e2a\u6708\u7684\u5bff\u547d\u4e86\uff0c\u5728\u8fd9\u4e24\u53c1\u4e2a\u6708\u91cc\uff0c\u6211\u82e5\u4e0d\u80fd\u63d0\u7740\u4f60\u7684\u8111\u888b\u53bb \u89c1\u4ed6\uff0c\u5c31\u53ea\u6709\u8d76\u5feb\u51c6\u5907\u5f8c\u4e8b\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u60c5\u4e0d\u81ea\u7981\u63c9\u4e86\u63c9\u9f3b\u5b50\uff0c\u9053\u2236\u201c\u73b0\u5728\u5df2\u7ecf\u8fc7\u4e86\u591a\u4e45\uff1f\u201d \u3000\u3000\u67f3\u65e0\u7709\u9053\u2236\u201c\u5df2\u4e24\u4e2a\u6708\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\u2236\u201c\u90a3\u4eba\u8bf4\u7684\u8bdd\u9760\u5f97\u4f4f\u9ebd\uff1f\u201d \u3000\u3000\u67f3\u65e0\u7709\u9053\u2236\u201c\u4f60\u82e5\u77e5\u9053\u4ed6\u662f\u8c01\uff0c\u5c31\u7edd\u4e0d\u4f1a\u6000\u7591\u4ed6\u7684\u8bdd\u4e86\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u51b7\u7b11\u9053\u2236\u201c\u6211\u5012\u672b\u60f3\u5230\u4f60\u7adf\u662f\u5982\u6b64\u6015\u6b7b\u7684\u4eba\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u76ee\u4e2d\u6d41\u4e0b\u6cea\u6765\uff0c\u98a4\u58f0\u9053\u2236\u201c\u6211\u5e76\u4e0d\u662f\u6015\u6b7b\uff0c\u6211\u53ea\u4e0d\u8fc7\u662f\u2026\u2026\u662f\u2026\u2026\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\u2236\u201c\u662f\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u674e\u7389\u51fd\u5ffd\u7136\u5636\u58f0\u9053\u2236\u201c\u5979\u53ea\u662f\u4e3a\u4e86\u6211\uff0c\u5979\u4e0d\u5fcd\u629b\u4e0b\u6211\u4e00\u4e2a\u4eba\u53bb\u6b7b\uff0c\u73b0\u5728\u4f60\u603b\u8be5\u660e\u767d\u4e86 \u5427\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\u2236\u201c\u660e\u767d\u4e86\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u6012\u543c\u9053\u2236\u201c\u4f60\u73b0\u5728\u603b\u8be5\u77e5\u9053\uff0c\u5979\u5e76\u4e0d\u662f\u77f3\u89c2\u97f3\u7684\u5978\u7ec6\uff0c\u66f4\u4e0d\u662f\u4efb\u4f55\u4eba\u7684\u5978\u7ec6\uff0c\u5730 \u60f3\u8981\u4f60\u7684\u547d\uff0c\u53ea\u4e0d\u8fc7\u662f\u60f3\u4fdd\u5168\u81ea\u5df1\u7684\u6027\u547d\u800c\u5df2\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\u2236\u201c\u4eba\u4e0d\u4e3a\u5df1\uff0c\u5929\u8bdb\u5730\u706d\uff0c\u8fd9\u70b9\u6211\u7edd\u4e0d\u602a\u5979\uff0c\u5979\u8fd9\u6837\u505a\u672c\u662f\u5e94\u8be5\u7684\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u4f3c\u4e5f\u60f3\u4e0d\u5230\u4ed6\u53cd\u5012\u5e2e\u67f3\u65e0\u7709\u8bf4\u8d77\u8bdd\u6765\uff0c\u6014\u4e86\u534a\u664c\uff0c\u55c4\u58f0\u9053\u2236\u201c\u65e2\u7136\u5982\u6b64\uff0c\u4f60\u5c31\u7d22 \u6027\u6210\u5168\u4e86\u5979\u5427\uff01\u201d \u3000\u3000\u695a\u7559\u9999\u60a0\u7136\u9053\u2236\u201c\u6211\u65b9\u624d\u5df2\u8bf4\u8fc7\uff0c\u4eba\u4e0d\u4e3a\u5df1\uff0c\u5929\u8bdb\u5730\u706d\uff0c\u6240\u4ee5\u627e\u867d\u7136\u5f88\u60f3\u5e2e\u5b83\u7684\u5fd9\uff0c\u4f46 \u81f3\u5c11\u4e5f\u5f97\u5148\u4e3a\u81ea\u5df1\u6253\u7b97\u6253\u7b97\u3002\u201d \u3000\u3000\u4ed6\u51dd\u6ce8\u7740\u674e\u7389\u51fd\u5fae\u7b11\u9053\u2236\u201c\u82e5\u8981\u4f60\u780d\u4e0b\u81ea\u5df1\u7684\u8111\u888b\u53bb\u5e2e\u522b\u4eba\u7684\u5fd9\uff0c\u4f60\u80af\u4e0d\u80af\uff1f\u201d \u3000\u3000\u674e\u7389\u51fd\u82cd\u767d\u7684\u8138\u5df2\u6da8\u7ea2\u4e86\uff0c\u6012\u9053\u2236\u201c\u8fd9\u4e2a\u5fd9\u4f60\u4e0d\u5e2e\u4ed6\u4e0d\u884c\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\u2236\u201c\u54e6\uff1f\u201d \u3000\u3000\u674e\u7389\u51fd\u9053\u2236\u201c\u4f60\u82e5\u4e0d\u80af\u6b7b\uff0c\u6211\u5c31\u8981\u4ed6\u4eec\u4e94\u4e2a\u4eba\u7684\u547d\uff0c\u4f60\u603b\u4e0d\u5fcd\u773c\u89c1\u770b\u7740\u4ed6\u4eec\u4e94\u4e2a\u4eba\u4e3a\u4f60 \u800c\u6b7b\u5427\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\u2236\u201c\u4f60\u82e5\u6740\u6b7b\u4e86\u4ed6\u4eec\uff0c\u4f60\u4eec\u592b\u59bb\u2026\u2026\u201d \u3000\u3000\u6797\u7389\u51fd\u5927\u543c\u9053\u2236\u201c\u6211\u4eec\u592b\u59bb\u53cd\u6b63\u4e0d\u60f3\u6d3b\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\u2236\u201c\u770b\u6765\u4f60\u4eec\u7684\u786e\u662f\u4e2a\u75f4\u60c5\u7684\u4eba\uff0c\u4e3a\u4e86\u81ea\u5df1\u7684\u8001\u5a46\uff0c\u7adf\u4e0d\u60dc\u505a\u8fd9\u79cd \u4e8b\u2026\u2026\u4f46\u4f60\u4e3a\u4ec0\u9ebd\u4e0d\u76f4\u63a5\u7528\u8fd9\u66b4\u96e8\u68a8\u82b1\u9489\u5939\u6740\u6211\u5462\uff1f\u201d \u3000\u3000\u674e\u7389\u51fd\u54ac\u7740\u7259\uff0c\u5636\u58f0\u9053\u2236\u201c\u6211\u6ca1\u6709\u6740\u6b7b\u4f60\u7684\u628a\u63e1\uff0c\u8fd9\u5df2\u662f\u6211\u6700\u5f8c\u4e00\u6ce8\uff0c\u6211\u7edd\u4e0d\u80fd\u5192 \u9669\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u5fae\u7b11\u9053\u2236\u201c\u81f3\u5c11\u4f60\u8fd9\u53e5\u8bf4\u7684\u5012\u662f\u8001\u5b9e\u8bdd\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u9053\u2236\u201c\u73b0\u5728\u8bdd\u5df2\u8bf4\u5c3d\uff0c\u4f60\u518d\u62d6\u4e0b\u53bb\u4e5f\u6ca1\u6709\u7528\u4e86\uff0c\u6211\u518d\u7ed9\u4f60\u7247\u523b\u8003\u8651\uff0c\u7b49\u6211\u6570\u5230\u4e94 \u5b57\uff0c\u4f60\u4e0d\u6b7b\uff0c\u4ed6\u4eec\u5c31\u5f97\u6b7b\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u53f9\u4e86\u53e3\u6c14\uff0c\u5583\u5583\u9053\u2236\u201c\u6570\u5230\u4e94\uff1f\u2026\u2026\u4f60\u4e3a\u4f55\u4e0d\u53ea\u80af\u6570\u5230\u53c1\u5462\uff1f\u90a3\u6837\u5c82\u975e\u66f4\u7d27\u5f20\u523a \u6fc0\u5f97\u591a\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u94c1\u9752\u7740\u7751\uff0c\u9053\u2236\u201c\u4e00\u3002\u201d \u3000\u3000\u4ed6\u8fde\u58f0\u97f3\u90fd\u5df2\u7d27\u5f20\u5f97\u5636\u54d1\uff0c\u8bf4\u4e86\u4e24\u6b21\uff0c\u624d\u8bf4\u51fa\u8fd9\u4e2a\u201c\u4e00\u201d\u5b57\u6765\uff0c\u53ea\u56e0\u4ed6\u77e5\u9053\u695a\u7559\u9999\u82e5\u4e0d \u80af\u6b7b\uff0c\u90a3\u9ebd\u975e\u4f46\u80e1\u94c1\u82b1\u3001\u82cf\u84c9\u84c9\u3001\u674e\u7ea2\u8896\u3001\u5b8b\u751c\u513f\u3001\u9ed1\u73cd\u73e0\u90fd\u5f97\u6b7b\uff0c\u4ed6\u4eec\u592b\u59bb\u4e24\u4eba\u4e5f\u4f11\u60f3\u518d \u6d3b\u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u695a\u7559\u9999\u73b0\u5728\u5374\u4e0d\u50cf\u60f3\u6b7b\u7684\u6837\u5b50\u3002 \u3000\u3000\u674e\u7389\u51fd\u55c4\u58f0\u9053\u2236\u201c\u4e8c\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u5c45\u7136\u5fae\u7b11\u4e86\u8d77\u6765\u3002 \u3000\u3000\u674e\u7389\u51fd\u5b9e\u5728\u4e0d\u613f\u770b\u5230\u8fd9\u5fae\u7b11\uff0c\u53ea\u6709\u77aa\u7740\u82cf\u84c9\u84c9\u5979\u4eec\uff0c\u4ed6\u81ea\u7136\u77e5\u9053\u5979\u4eec\u7edd\u6ca1\u6709\u4e00\u4e2a\u4eba\u4f1a \u8bf4\u2236\u201c\u695a\u7559\u9999\uff0c\u4f60\u6b7b\u5427\uff01\u8ba9\u6211\u4eec\u6d3b\u4e0b\u53bb\uff0c\u6211\u4eec\u90fd\u662f\u4f60\u6700\u4eb2\u8fd1\u7684\u4eba\uff0c\u4f60\u82e5\u4e3a\u6211\u4eec\u6b7b\u4e86\uff0c\u5929\u4e0b\u7684 \u4eba\u90fd\u4f1a\u8d5e\u626c\u4f60\u3002\u201d \u3000\u3000\u4ed6\u5e76\u4e0d\u5e0c\u671b\u5979\u4eec\u4f1a\u8bf4\u51fa\u8fd9\u6837\u7684\u8bdd\uff0c\u4ed6\u5e0c\u671b\u5979\u4eec\u8bf4\u2236\u201c\u695a\u7559\u9999\uff0c\u4f60\u5343\u4e07\u4e0d\u80fd\u6b7b\uff01\u8ba9\u6211\u4eec\u6b7b \u5427\uff01\u6211\u4eec\u90fd\u662f\u65e0\u8db3\u8f7b\u91cd\u7684\u4eba\uff0c\u6b7b\u4e86\u4e5f\u6ca1\u4ec0\u9ebd\u5173\u7cfb\u3002\u201d \u3000\u3000\u4ed6\u66f4\u5e0c\u671b\u5979\u4eec\u4f1a\u8bf4\u2236\u201c\u6211\u4eec\u80fd\u4e3a\u4f60\u800c\u6b7b\uff0c\u6b7b\u4e5f\u7791\u76ee\u4e86\uff0c\u4f46\u613f\u4f60\u83ab\u8981\u5fd8\u8bb0\u6211\u4eec\uff0c\u6bcf\u5230\u6625\u79cb \u5fcc\u65e5\uff0c\u4f60\u80fd\u5728\u6211\u4eec\u575f\u524d\u71c3\u4e00\u70b7\u9999\uff0c\u6211\u4eec\u4e5f\u5c31\u5fc3\u6ee1\u610f\u8db3\u4e86\u3002\u201d \u3000\u3000\u56e0\u4e3a\u4ed6\u77e5\u9053\u5979\u4eec\u82e5\u8bf4\u51fa\u8fd9\u4e9b\u8bdd\uff0c\u5c31\u5fc5\u5b9a\u4f1a\u9020\u6210\u4e00\u79cd\u58ee\u70c8\u7684\u3001\u60b2\u75db\u7684\u3001\u9500\u9b42\u7684\u60c5\u7eea\u548c\u6c14 \u6c1b\u3002 \u3000\u3000\u4ed6\u4e5f\u77e5\u9053\u695a\u7559\u9999\u662f\u4e2a\u5f88\u591a\u60c5\u7684\u4eba\uff0c\u4e00\u5b9a\u4f1a\u88ab\u8fd9\u79cd\u8bdd\u6253\u52a8\uff0c\u751a\u81f3\u4f1a\u70ed\u8840\u5954\u817e\uff0c\u4e0d\u80fd\u81ea\u5236\u3002 \u3000\u3000\u5230\u4e86\u90a3\u65f6\uff0c\u4ed6\u5c31\u7b97\u4e0d\u60f3\u6b7b\uff0c\u4e5f\u4f1a\u6b7b\u4e86\u3002 \u3000\u3000\u4f46\u82cf\u84c9\u84c9\u5979\u4eec\u5374\u4ec0\u9ebd\u8bdd\u4e5f\u6ca1\u6709\u8bf4\uff0c\u5979\u4eec\u53ea\u662f\u9759\u9759\u7684\u7ad9\u5728\u90a3\u91cc\uff0c\u9759\u9759\u7684\u7b49\u7740\uff0c\u65e2\u4e0d\u60b2\u4f24\uff0c \u4e5f\u4e0d\u6fc0\u52a8\u3002 \u3000\u3000\u674e\u7389\u51fd\u65e2\u662f\u60ca\u5947\uff0c\u53c8\u662f\u5931\u671b\uff0c\u8fd9\u4e9b\u4eba\u7adf\u8fde\u4e00\u4e1d\u4f24\u611f\u7684\u60c5\u7eea\u90fd\u6ca1\u6709\uff0c\u4ed6\u4eec\u96be\u9053\u90fd\u4e0d\u662f\u6709\u8840 \u6709\u8089\u7684\u4eba\u9ebd\uff1f \u3000\u3000\u674e\u7389\u51fd\u7d27\u5f20\u5f97\u8fde\u201c\u53c1\u201d\u5b57\u90fd\u8bf4\u4e0d\u51fa\u6765\u4e86\u3002 \u3000\u3000\u695a\u7559\u9999\u5ffd\u7136\u5fae\u7b11\u7740\u9053\u2236\u201c\u6211\u73b0\u5728\u624d\u660e\u767d\u4e86\u4e24\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u8131\u53e3\u95ee\u9053\u2236\u201c\u4ec0\u9ebd\u4e8b\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\u2236\u201c\u6211\u73b0\u5728\u624d\u77e5\u9053\u201c\u62e5\u7fe0\u5c71\u5e84\u201d\u7684\u5b50\u5f1f\u7684\u786e\u90fd\u4e0d\u4f1a\u505a\u574f\u4e8b\u7684\uff0c\u56e0\u4e3a\u4f60\u975e\u4f46\u4e0d\u61c2 \u5f97\u8be5\u5982\u4f55\u53bb\u505a\u574f\u4e8b\uff0c\u751a\u81f3\u8fde\u8be5\u5982\u4f55\u5413\u4eba\u90fd\u4e0d\u61c2\u3002\u201d \u3000\u3000\u4ed6\u5fae\u7b11\u7740\u63a5\u9053\u2236\u201c\u4f60\u82e5\u60f3\u53eb\u522b\u4eba\u6015\u4f60\uff0c\u4f60\u81ea\u5df1\u5c31\u5343\u4e07\u4e0d\u80fd\u5bb3\u6015\uff0c\u4f60\u81ea\u5df1\u82e5\u5148\u5bb3\u6015\u8d77\u6765\uff0c \u522b\u4eba\u53c8\u600e\u9ebd\u4f1a\u6015\u4f60\u5462\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u5927\u7b11\u9053\u2236\u201c\u4e0d\u9519\uff0c\u8fd9\u5c31\u6b63\u5982\u61c2\u5f97\u8bf4\u7b11\u8bdd\u7684\u4eba\uff0c\u81ea\u5df1\u7edd\u5bf9\u4e0d\u4f1a\u7b11\uff0c\u4ed6\u81ea\u5df1\u82e5\u5148\u5927\u7b11 \u8d77\u6765\uff0c\u90a3\u9ebd\u65e0\u8bba\u4ed6\u8bf4\u7684\u7b11\u8bdd\u591a\u6709\u8da3\uff0c\u522b\u4eba\u4e5f\u4e0d\u4f1a\u89c9\u5f97\u597d\u7b11\u7684\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u6012\u9053\u2236\u201c\u4f60\u4eec\u4ee5\u4e3a\u2026\u2026\u201d \u3000\u3000\u695a\u7559\u9999\u6839\u672c\u4e0d\u8ba9\u4ed6\u8bf4\u8bdd\uff0c\u622a\u53e3\u9053\u2236\u201c\u50cf\u4f60\u4eec\u8fd9\u6837\u7684\u4e16\u5bb6\u5b50\u5f1f\uff0c\u8fd8\u6709\u4e2a\u6700\u5927\u7684\u6bdb\u75c5\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u51e0\u4e4e\u53c8\u5fcd\u4e0d\u4f4f\u8981\u95ee\u2236\u201c\u4ec0\u9ebd\u6bdb\u75c5\uff1f\u201d \u3000\u3000\u4f46\u4ed6\u603b\u7b97\u6ca1\u6709\u95ee\u51fa\u6765\uff0c\u5374\u5927\u543c\u9053\u2236\u201c\u56db\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u6839\u672c\u4e0d\u7406\u4ed6\uff0c\u60a0\u7136\u9053\u2236\u201c\u4f60\u4eec\u6700\u5927\u7684\u6bdb\u75c5\u5c31\u662f\u6c5f\u6e56\u5386\u7ec3\u592a\u5c11\uff0c\u56e0\u4e3a\u4f60\u4eec\u6839\u672c\u7528\u4e0d \u7740\u81ea\u5df1\u5230\u6c5f\u6e56\u4e2d\u53bb\u6323\u624e\uff0c\u53bb\u594b\u6597\uff0c\u4f60\u4eec\u7684\u5730\u4f4d\u4e00\u751f\u51fa\u6765\u5c31\u6ce8\u5b9a\u8981\u6bd4\u522b\u4eba\u9ad8\u4e9b\uff0c\u6240\u4ee5\u4f60\u4eec\u5927\u90fd \u514d\u4e0d\u4e86\u6709\u4e9b\u76ee\u7a7a\u4e00\u5207\uff0c\u6240\u4ee5\u4e5f\u5c31\u96be\u514d\u4f1a\u7c97\u5fc3\u5927\u610f\u3002\u201d \u3000\u3000\u4ed6\u7a81\u7136\u6307\u7740\u674e\u7389\u51fd\u624b\u91cc\u7684\u201c\u66b4\u96e8\u68a8\u82b1\u9489\u201d\u9053\u2236\u201c\u8b6c\u5982\u8bf4\uff0c\u8fd9\u201c\u66b4\u96e8\u68a8\u82b1\u9489\u201d\u73b0\u5728\u6b63\u662f\u4f60 \u7684\u6551\u547d\u62a4\u8eab\u7b26\uff0c\u4f60\u592b\u59bb\u4e24\u4eba\u73b0\u5728\u4ec0\u9ebd\u4e8b\u5168\u90fd\u8981\u9760\u5b83\u4e86\uff0c\u4f46\u4f60\u4e8b\u5148\u6709\u6ca1\u6709\u5c06\u5b83\u68c0\u67e5\u4e00\u904d\uff0c\u770b\u770b \u5b83\u7684\u673a\u7c27\u662f\u4e0d\u662f\u6709\u4e86\u6bdb\u75c5\uff1f\u770b\u770b\u8fd9\u5323\u5b50\u662f\u4e0d\u662f\u7a7a\u7684\uff1f\u201d \u3000\u3000\u674e\u7389\u51fd\u50cf\u662f\u5ffd\u7136\u6328\u4e86\u4e00\u97ad\u5b50\uff0c\u55c4\u58f0\u9053\u2236\u201c\u66b4\u96e8\u68a8\u82b1\u9489\u4ece\u6765\u4e07\u65e0\u4e00\u5931\u2026\u2026\u201d \u3000\u3000\u695a\u7559\u9999\u9053\u2236\u201c\u4e16\u754c\u4e0a\u5e76\u6ca1\u6709\u4e00\u4ef6\u4e8b\u662f\u6c38\u8fdc\u4e0d\u4f1a\u51fa\u9519\u7684\uff0c\u8fde\u592a\u9633\u90fd\u6709\u88ab\u5929\u72d7\u661f\u541e\u6ca1\u7684\u65f6 \u5019\uff0c\u8fd9\u66b4\u96e8\u68a8\u82b1\u9489\u53c8\u600e\u4f1a\u7edd\u5bf9\u4e07\u65e0\u4e00\u5931\uff0c\u4e5f\u8bb8\u5b83\u91cc\u9762\u5ffd\u7136\u751f\u9508\u4e86\u5462\uff1f\u4e5f\u8bb8\u5ffd\u7136\u6709\u51e0\u4e2a\u5c0f\u866b\u94bb \u8fdb\u53bb\uff0c\u585e\u4f4f\u4e86\u5b83\u7684\u9489\u5b54\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u8fde\u9f3b\u5b50\u4e0a\u90fd\u6c81\u51fa\u4e86\u6c57\uff0c\u624b\u4e5f\u6296\u5f97\u66f4\u5389\u5bb3\u3002 \u3000\u3000\u695a\u7559\u9999\u6de1\u6de1\u9053\u2236\u201c\u4f55\u51b5\uff0c\u5b83\u5c31\u7b97\u771f\u7684\u662f\u4e07\u65e0\u4e00\u5931\u4e5f\u6ca1\u6709\u7528\uff0c\u56e0\u4e3a\u5b83\u6839\u672c\u5c31\u662f\u7a7a\u7684\uff0c\u6628\u5929 \u665a\u4e0a\u6211\u4eec\u5bf9\u4ed8\u201c\u5929\u7f57\u5730\u67cf\u201d\u592b\u59bb\u65f6\uff0c\u5df2\u5c06\u91cc\u9762\u7684\u68a8\u82b1\u9489\u5168\u5c04\u4e86\u51fa\u53bb\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u5ffd\u7136\u5927\u7b11\u4e86\u8d77\u6765\u3002 \u3000\u3000\u4ed6\u72c2\u7b11\u7740\u9053\u2236\u201c\u4f60\u4ee5\u4e3a\u6211\u662f\u4e8c\u5c81\u7684\u5c0f\u5b69\u5b50\uff0c\u5c31\u51ed\u4f60\u8fd9\u51e0\u53e5\u8bdd\u5c31\u53ef\u4ee5\u5c06\u6211\u5440\u5012\uff1f\u8001\u5b9e\u544a\u8bc9 \u4f60\uff0c\u4f60\u8bf4\u7684\u8bdd\uff0c\u6211\u8fde\u4e00\u4e2a\u5b57\u4e5f\u4e0d\u76f8\u4fe1\u3002\u201d \u3000\u3000\u4ed6\u5634\u91cc\u867d\u8bf4\u5f97\u5982\u6b64\u575a\u5b9a\uff0c\u5176\u5b9e\u5fc3\u91cc\u5374\u5df2\u52a8\u6447\uff0c\u56e0\u4e3a\u6709\u4fe1\u5fc3\u7684\u4eba\u7edd\u4e0d\u4f1a\u8fd9\u9ebd\u6837\u7b11\u7684\uff0c\u8fd9\u79cd \u7b11\u4e00\u5b9a\u662f\u5728\u63a9\u9970\u5fc3\u91cc\u7684\u4e0d\u5b89\u3002 \u3000\u3000\u695a\u7559\u9999\u60a0\u7136\u9053\u2236\u201c\u4f60\u82e5\u4e0d\u4fe1\uff0c\u4e3a\u4f55\u4e0d\u81ea\u5df1\u77a7\u77a7\uff1f\u201d \u3000\u3000\u674e\u7389\u51fd\u543c\u9053\u2236\u201c\u6211\u7528\u4e0d\u7740\u77a7\uff0c\u6839\u672c\u7528\u4e0d\u7740\u3002\u201d \u3000\u3000\u4ed6\u5634\u91cc\u5728\u8bf4\u201c\u5468\u4e0d\u7740\u201d\u7684\u65f6\u5019\uff0c\u773c\u775b\u5df2\u5fcd\u4e0d\u4f4f\u5411\u90a3\u53ea\u53d1\u5149\u7684\u94f6\u5323\u4e0a\u53bb\u77a7\u4e86\uff0c\u624b\u4e5f\u5fcd\u4e0d\u4f4f \u5728\u4e0a\u9762\u6478\u7d22\u3002 \u3000\u3000\u5176\u5b9e\uff0c\u8fd9\u5323\u5b50\u662f\u4e0d\u662f\u7a7a\u7684\uff0c\u4ed6\u6839\u672c\u5c31\u77a7\u4e0d\u51fa\uff0c\u66f4\u6478\u4e0d\u51fa\u6765\uff0c\u4ed6\u53ea\u662f\u795e\u7ecf\u7d27\u5f20\uff0c\u5df2\u65e0\u6cd5\u63a7 \u5236\u81ea\u5df1\u3002 \u3000\u3000\u5c31\u5728\u4ed6\u773c\u775b\u548c\u624b\u5f00\u59cb\u79fb\u52a8\u7684\u8fd9\u4e00\u5239\u90a3\u95f4\uff0c\u695a\u7559\u9999\u5c31\u50cf\u662f\u4e00\u679d\u7bad\uff0c\u4e00\u9053\u95ea\u7535\uff0c\u5ffd\u7136\u7a9c\u4e86\u8fc7 \u53bb\u3002 \u3000\u3000\u674e\u7389\u51fd\u53c8\u60ca\u53c8\u6012\uff0c\u4f46\u5df2\u95ea\u907f\u4e0d\u53ca\u4e86\u3002\u4ed6\u7684\u53cd\u5e94\u548c\u52a8\u4f5c\u56fa\u7136\u4e5f\u5f88\u5feb\uff0c\u4f46\u5374\u6ca1\u6709\u4efb\u4f55\u4eba\u7684\u52a8 \u4f5c\u80fd\u6bd4\u695a\u7559\u9999\u66f4\u5feb\u3002 \u3000\u3000\u7b49\u4ed6\u53d1\u73b0\u81ea\u5df1\u5df2\u4e0a\u5f53\u7684\u65f6\u5019\uff0c\u695a\u7559\u9999\u5df2\u62ac\u8d77\u4e86\u4ed6\u7684\u624b\uff0c\u6323\u624e\u4e2d\uff0c\u4e5f\u4e0d\u77e5\u662f\u8c01\u7684\u624b\u89e6\u52a8\u4e86 \u68a8\u82b1\u9489\u7684\u673a\u7c27\u3002 \u3000\u3000\u53ea\u542c\u201c\u84ec\u201d\u7684\u4e00\u58f0\u94f6\u5149\u5982\u7535\uff0c\u66b4\u5c04\u800c\u51fa\u3002 \u3000\u3000\u6309\u7740\uff0c\u53c8\u662f\u4e00\u8fde\u4e32\u201c\u7b03\u7b03\u201d\u4e4b\u58f0\uff0c\u4e8c\u5341\u4e03\u679a\u68a8\u82b1\u9489\u5df2\u5168\u90fd\u5c09\u5728\u5c4b\u9876\u4e0a\uff0c\u7adf\u5168\u90fd\u9489\u5165\u77f3\u5934 \u91cc\u3002 \u3000\u3000\u674e\u7389\u51fd\u5168\u8eab\u7684\u7cbe\u795e\u529b\u6c14\uff0c\u4e5f\u5f77\u4f5b\u5168\u90fd\u968f\u540c\u8fd9\u66b4\u96e8\u68a8\u82b1\u9489\u5c04\u4e86\u51fa\u53bb\uff0c\u4ed6\u6574\u4e2a\u5165\u4f3c\u4e4e\u5ffd\u7136\u865a \u8131\u3002 \u3000\u3000\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u68a8\u82b1\u9489\u5323\u4e5f\u8dcc\u5728\u5730\u4e0a\u3002 \u3000\u3000\u8fd9\u4ef6\u4e8b\u5168\u90fd\u53d1\u751f\u5728\u5239\u90a3\u95f4\uff0c\u68a8\u82b1\u9489\u5c04\u51fa\u65f6\u7684\u58f0\u97f3\uff0c\u9489\u5165\u77f3\u5934\u65f6\u7684\u58f0\u97f3\uff0c\u9489\u5323\u843d\u5730\u65f6\u7684\u58f0 \u97f3\uff0c\u51e0\u4e4e\u662f\u540c\u65f6\u53d1\u51fa\u6765\u7684\u3002 \u3000\u3000\u7136\u5f8c\uff0c\u5c31\u662f\u6b7b\u4e00\u822c\u9759\u5bc2\u3002 \u3000\u3000\u53ea\u89c1\u695a\u7559\u9999\u5de6\u624b\u6258\u4f4f\u674e\u7389\u51fd\u7684\u53f3\u624b\uff0c\u53f3\u8098\u62b5\u5728\u674e\u7389\u51fd\u7684\u5de6\u80c1\u95f4\uff0c\u674e\u7389\u51fd\u5374\u50cf\u662f\u5df2\u5931\u53bb\u4e86 \u9b42\u9b44\uff0c\u773c\u775b\u65e2\u672a\u671b\u7740\u695a\u7559\u9999\uff0c\u4e5f\u672b\u770b\u522b\u4eba\uff0c\u53ea\u662f\u75f4\u75f4\u7684\u62df\u6ce8\u7740\u90a3\u4e8c\u5341\u4e03\u679a\u5df2\u9489\u5728\u77f3\u5934\u91cc\u7684\u68a8 \u82b1\u9489\u3002 \u3000\u3000\u7136\u5f8c\uff0c\u4e24\u884c\u6cea\u73e0\u7f13\u7f13\u81ea\u4ed6\u773c\u89d2\u6d41\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u67f3\u65e0\u7709\u672c\u4f3c\u60f3\u51b2\u5411\u695a\u7559\u9999\u7684\uff0c\u4f46\u811a\u6b65\u521a\u8e0f\u51fa\uff0c\u5374\u987f\u4f4f\u3002 \u3000\u3000\u5979\u4e5f\u6ca1\u6709\u77a7\u695a\u7559\u9999\u4e00\u773c\uff0c\u53ea\u662f\u75f4\u75f4\u7684\u671b\u7740\u674e\u7389\u51fd\uff0c\u90a3\u53ea\u7f8e\u4e3d\u7684\u773c\u775b\u91cc\uff0c\u5145\u6ee1\u4e86\u611f\u4f24\uff0c\u5145 \u6ee1\u4e86\u60b2\u75db\uff0c\u4e5f\u5145\u6ee1\u4e86\u8bf4\u4e0d\u5c3d\u7684\u60c5\u610f\u3002 \u3000\u3000\u5979\u6ca1\u6709\u6d41\u6cea\uff0c\u4f46\u90a3\u773c\u8272\u5374\u6bd4\u6d41\u6cea\u66f4\u60b2\u54c0\u3002 \u3000\u3000\u695a\u7559\u9999\u4e00\u51fb\u5f97\u624b\uff0c\u80e1\u94c1\u82b1\u3001\u82cf\u84c9\u84c9\u5979\u4eec\u81ea\u7136\u559c\u6781\uff0c\u4f46\u5374\u6ca1\u6709\u4e00\u4e2a\u6b22\u547c\u51fa\u58f0\u6765\uff0c\u751a\u81f3\u6ca1\u6709 \u4eba\u8bf4\u8bdd\u3002 \u3000\u3000\u6bcf\u4e2a\u4eba\u4f3c\u4e4e\u90e8\u88ab\u592b\u59bb\u4e24\u4eba\u7684\u6df1\u60c5\u6240\u611f\u52a8\uff0c\u4e0d\u5fcd\u518d\u523a\u6fc0\u4ed6\u4eec\u4e86\uff0c\u56e0\u4e3a\u4ed6\u4eec\u505a\u51fa\u6765\u7684\u4e8b\u56fa\u7136 \u53ef\u6068\uff0c\u4f46\u4ed6\u4eec\u7684\u906d\u9047\u5374\u5b9e\u5728\u53ef\u601c\u3002 \u3000\u3000\u5b8b\u751c\u513f\u8499\u7740\u773c\u775b\uff0c\u5ffd\u7136\u8f7b\u8f7b\u6ce3\u4e86\u8d77\u6765\u3002 \u3000\u3000\u6c38\u8fdc\u6ca1\u6709\u4eba\u80fd\u9884\u6d4b\u5c11\u5973\u4eec\u4f1a\u5728\u4ec0\u9ebd\u65f6\u5019\u6d41\u6cea\uff0c\u56e0\u4e3a\u5979\u4eec\u968f\u65f6\u968f\u5730\uff0c\u90fd\u53ef\u80fd\u4e3a\u4e86\u4efb\u4f55\u4e8b\u800c \u6d41\u6cea\u3002 \u3000\u3000\u5979\u4eec\u4f1a\u4e3a\u7231\u4e24\u6d41\u6cea\uff0c\u4e5f\u4f1a\u4e3a\u6068\u800c\u6d41\u6cea\uff0c\u5979\u4eec\u4f1a\u4e3a\u4e00\u4e9b\u7f8e\u4e3d\u7684\u4e8b\u7269\u800c\u6d41\u6cea\uff0c\u4e5f\u4f1a\u4e3a\u4e86\u4e00\u4e9b \u4e11\u6076\u7684\u4e8b\u7269\u4e24\u6d41\u6cea\u3002 \u3000\u3000\u5979\u4eec\u4f1a\u4e3a\u60b2\u4f24\u800c\u6d41\u6cea\uff0c\u4e5f\u4f1a\u4e3a\u5feb\u4e50\u800c\u6d41\u6cea\u3002 \u3000\u3000\u5979\u4eec\u751a\u81f3\u53ef\u80fd\u4e0d\u4e3a\u4ec0\u9ebd\u4e8b\u5c31\u6d41\u4e0b\u6cea\u6765\u3002 \u3000\u3000\u4f46\u5b8b\u751c\u513f\u8fd9\u773c\u6cea\u5374\u663e\u7136\u662f\u5f88\u771f\u631a\u7684\uff0c\u5979\u4f3c\u4e4e\u5df2\u5fd8\u4e86\u8fd9\u592b\u59bb\u4e24\u4eba\u5c31\u5728\u7247\u523b\u524d\u8fd8\u662f\u5b83\u7684\u4ec7 \u654c\uff0c\u8fd8\u8981\u6740\u5979\u3002 \u3000\u3000\u5979\u54ed\u5f97\u90a3\u9ebd\u4f24\u5fc3\uff0c\u4f7f\u4eba\u5fcd\u4e0d\u4f4f\u8981\u4ee5\u4e3a\u5979\u5b81\u613f\u5272\u4e0b\u695a\u7559\u9999\u7684\u5934\u9885\uff0c\u6765\u6551\u8fd9\u592b\u59bb\u4e24\u4eba\u7684\u6027 \u547d\u3002 \u3000\u3000\u674e\u7ea2\u8896\u3001\u82cf\u84c9\u84c9\uff0c\u548c\u9ed1\u73cd\u73e0\u7684\u773c\u775b\u7adf\u4e5f\u6e10\u6e7f\u4e86\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u53f9\u4e86\u53e3\u6c14\uff0c\u5583\u5583\u9053\u2236\u201c\u5973\u4eba\u3001\u5973\u4eba\u2026\u2026\u5973\u4eba\u771f\u662f\u5999\u5f97\u5f88\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u82e6\u7b11\u9053\u2236\u201c\u88ab\u5979\u4eec\u8fd9\u9ebd\u6837\u4e00\u54ed\uff0c\u8fde\u6211\u90fd\u5fcd\u4e0d\u4f4f\u4ee5\u4e3a\u8be5\u6b7b\u7684\u662f\u695a\u7559\u9999\u6211\u4e86\u3002\u201d \u3000\u3000\u674e\u7ea2\u8896\u5ffd\u7136\u9053\u2236\u201c\u4f60\u2026\u2026\u4f60\u51c6\u5907\u5c06\u4ed6\u4eec\u600e\u9ebd\u6837\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u6c89\u541f\u7740\uff0c\u7f13\u7f13\u9053\u2236\u201c\u4ed6\u4eec\u5df2\u7ecf\u6709\u4e03\u6b21\u8981\u6740\u6211\u3002\u201d \u3000\u3000\u674e\u7ea2\u8896\u9053\u2236\u201c\u4f46\u4ee5\u5f8c\u4ed6\u4eec\u7edd\u4e0d\u4f1a\u518d\u5bb3\u4f60\u4e86\u3002\u201d \u3000\u3000\u82cf\u84c9\u84c9\u67d4\u58f0\u9053\u2236\u201c\u6211\u65b9\u624d\u542c\u4ed6\u4eec\u8bf4\uff0c\u4ed6\u4eec\u53ea\u60f3\u627e\u4e2a\u6ca1\u6709\u4eba\u7684\u5730\u65b9\uff0c\u5b89\u5b89\u9759\u9759\u7684\u8fc7\u51e0\u4e2a \u6708\uff0c\u4f60\u2026\u2026\u4f60\u5c31\u6210\u5168\u4e86\u4ed6\u4eec\u5427\uff01\u201d \u3000\u3000\u9ed1\u73cd\u73e0\u9053\u2236\u201c\u4e0d\u9519\uff0c\u4f60\u653e\u4e86\u4ed6\u4eec\u5427\uff01\u201d \u3000\u3000\u695a\u7559\u9999\u671b\u7740\u80e1\u94c1\u82b1\uff0c\u9053\u2236\u201c\u4f60\u7684\u610f\u601d\u5462\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\u2236\u201c\u4e0d\u80fd\u653e\u2026\u2026\u201d \u3000\u3000\u4ed6\u8bdd\u672b\u8bf4\u5b8c\uff0c\u5b8b\u751c\u513f\u8df3\u4e86\u8d77\u6765\uff0c\u8dfa\u7740\u811a\u9053\u2236\u201c\u4e3a\u4ec0\u9ebd\u4e0d\u80fd\u653e\uff1f\u201d \u3000\u3000\u674e\u7ea2\u8896\u9053\u2236\u201c\u4f60\u8fd9\u4eba\u4e3a\u4ec0\u9ebd\u8fd9\u6837\u72e0\u5fc3\u5462\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u957f\u53f9\u4e86\u4e00\u58f0\uff0c\u9053\u2236\u201c\u73b0\u5728\u6211\u4eec\u82e5\u8ba9\u4ed6\u4eec\u8d70\uff0c\u5b9e\u5728\u7b49\u65bc\u6740\u6b7b\u4ed6\u4eec\u4e00\u6837\uff0c\u56e0\u4e3a\u67f3\u65e0\u7709 \u5df2\u6d3b\u4e0d\u957f\u4e86\uff0c\u5979\u4e00\u6b7b\uff0c\u674e\u7389\u51fd\u8fd8\u80fd\u6d3b\u4e0b\u53bb\u9ebd\uff1f\u201d \u3000\u3000\u82cf\u84c9\u84c9\u5979\u4eec\u5168\u90fd\u6014\u4f4f\u4e86\u3002 \u3000\u3000\u674e\u7ea2\u8896\u9053\u2236\u201c\u4f60\u2026\u2026\u4f60\u96be\u9053\u60f3\u6551\u4ed6\u4eec\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u53f9\u9053\u2236\u201c\u4ed6\u4eec\u82e5\u6740\u4e86\u695a\u7559\u9999\uff0c\u56fa\u7136\u662f\u6211\u7684\u4ec7\u4eba\uff0c\u4f46\u73b0\u5728\u4ed6\u4eec\u5e76\u6ca1\u6709\u6740\u6b7b\u695a\u7559\u9999\uff0c \u5374\u6551\u8fc7\u6211\u7684\u547d\uff0c\u6240\u4ee5\u4ed6\u4eec\u4e0d\u4f46\u662f\u6211\u7684\u670b\u53cb\uff0c\u8fd8\u662f\u6211\u7684\u6069\u4eba\u3002\u201d \u3000\u3000\u4ed6\u633a\u8d77\u80f8\uff0c\u5927\u58f0\u9053\u2236\u201c\u6211\u80e1\u94c1\u82b1\u96be\u9053\u8fd8\u4f1a\u773c\u770b\u7740\u6551\u547d\u6069\u4eba\u4e2d\u6bd2\u800c\u6b7b\u9ebd\uff1f\u201d \u3000\u3000\u5b8b\u751c\u513f\u5ffd\u7136\u62b1\u4f4f\u4e86\u4ed6\uff0c\u7834\u6d95\u4e3a\u7b11\uff0c\u5a07\u7b11\u7740\u9053\u2236\u201c\u4f60\u771f\u662f\u4e2a\u597d\u4eba\u3002\u201d \u3000\u3000\u5979\u751c\u7b11\u7684\u7b11\u9765\u8ddd\u79bb\u80e1\u94c1\u82b1\u7684\u8138\u5df2\u4e0d\u5230\u4e00\u5bf8\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u547b\u541f\u7740\u9053\u2236\u201c\u4f60\u82e5\u518d\u62b1\u4f4f\u6211\u4e0d\u653e\uff0c\u6211\u5c31\u8981\u53d8\u6210\u574f\u4eba\u4e86\u3002\u201d \u3000\u3000\u5b8b\u751c\u513f\u653e\u5f00\u624b\uff0c\u8138\u5df2\u6709\u4e9b\u7ea2\u4e86\uff0c\u9762\u988a\u4e0a\u7684\u6cea\u73e0\u5374\u8fd8\u672a\u4e7e\uff0c\u770b\u6765\u5c31\u50cf\u662f\u4e00\u53ea\u8fd8\u5e26\u7740\u9732\u73e0\u7684 \u7ea2\u82f9\u679c\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u5927\u7b11\u7740\u8d70\u5230\u94c1\u6805\u524d\uff0c\u9053\u2236\u201c\u4f60\u53ea\u8981\u8bf4\u51fa\u80fd\u6551\u4f60\u7684\u90a3\u4e2a\u4eba\u662f\u8c01\uff0c\u6211\u4eec\u5c31\u53ef\u4ee5\u5e2e\u4f60\u53bb \u5411\u4ed6\u8981\u89e3\u836f\uff0c\u4ed6\u82e5\u4e0d\u80af\u7ed9\uff0c\u563f\u563f\u2026\u2026\u6211\u4e0d\u6253\u6241\u4ed6\u7684\u5934\u624d\u602a\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u4ecd\u7136\u75f4\u75f4\u7684\u671b\u7740\u90a3\u94f6\u661f\u822c\u5d4c\u5728\u5c4b\u9876\u4e0a\u7684\u94f6\u9489\u3002 \u3000\u3000\u67f3\u65e0\u7709\u4ecd\u7136\u75f4\u75f4\u7684\u671b\u7740\u674e\u7389\u51fd\u3002 \u3000\u3000\u592b\u59bb\u4e24\u4eba\u7adf\u50cf\u662f\u5168\u90fd\u6ca1\u6709\u542c\u5230\u4ed6\u5728\u8bf4\u4ec0\u9ebd\u3002 \u3000\u3000\u82cf\u84c9\u84c9\u67d4\u58f0\u9053\u2236\u201c\u4f60\u8bf4\u5427\uff0c\u53ea\u8981\u4f60\u8bf4\u51fa\u6765\uff0c\u4ed6\u4eec\u4e00\u5b9a\u6709\u6cd5\u5b50\u80fd\u5c06\u89e3\u836f\u8981\u56de\u6765\u7684\u3002\u201d \u3000\u3000\u674e\u7ea2\u8896\u4f38\u624b\u81ea\u94c1\u6805\u95f4\u62c9\u4f4f\u67f3\u65e0\u7709\u7684\u624b\uff0c\u9053\u2236\u201c\u65e0\u8bba\u591a\u9ebd\u56f0\u96be\u7684\u4e8b\uff0c\u695a\u7559\u9999\u90fd\u6709\u6cd5\u5b50\u529e\u5f97 \u5230\u7684\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u76ee\u4e2d\u7ec8\u65bc\u6d41\u4e0b\u6cea\u6765\uff0c\u9eef\u7136\u9053\u2236\u201c\u4f60\u4eec\u5b9e\u5728\u592a\u597d\u4e86\uff0c\u4f60\u4eec\u5bf9\u6211\u7684\u597d\u610f\uff0c\u6211\u4eca\u751f\u53ea\u6015 \u518d\u4e5f\u65e0\u6cd5\u62a5\u7b54\u3002\u201d \u3000\u3000\u674e\u7ea2\u8896\u7b11\u7740\u9053\u2236\u201c\u4f60\u8bf4\u51fa\u6765\uff0c\u5c31\u7b97\u62a5\u7b54\u4e86\u6211\u4eec\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u5ffd\u7136\u7529\u8131\u5b83\u7684\u624b\uff0c\u55c4\u58f0\u9053\u2236\u201c\u6211\u4e0d\u80fd\u8bf4\u3002\u201d \u3000\u3000\u674e\u7ea2\u8896\u9053\u2236\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u67f3\u65e0\u7709\u6d41\u6cea\u9053\u2236\u201c\u56e0\u4e3a\u6211\u82e5\u8bf4\u51fa\u6765\uff0c\u975e\u4f46\u6ca1\u6709\u7528\uff0c\u53cd\u800c\u5bb3\u4e86\u4f60\u4eec\uff0c\u6211\u73b0\u5728\u2026\u2026\u73b0\u5728\u5b9e\u5728 \u4e0d\u5fcd\u518d\u5bb3\u4f60\u4eec\u4e86\u3002\u201d \u3000\u3000\u674e\u7ea2\u8896\u9053\u2236\u201c\u4f60\u96be\u9053\u6015\u4ed6\u4eec\u53bb\u4e3a\u4f60\u8981\u89e3\u836f\u65f6\u88ab\u90a3\u4eba\u6740\u4e86\u9ebd\uff1f\u201d \u3000\u3000\u67f3\u65e0\u7709\u9053\u2236\u201c\u55ef\uff01\u201d \u3000\u3000\u674e\u7ea2\u8896\u7b11\u9053\u2236\u201c\u4f60\u672a\u514d\u592a\u770b\u8f7b\u4ed6\u4eec\u4e86\u3002\u201d \u3000\u3000\u5b8b\u751c\u513f\u8dfa\u811a\u9053\u2236\u201c\u5230\u73b0\u5728\u4f60\u96be\u9053\u8fd8\u4e0d\u76f8\u4fe1\u4ed6\u4eec\u6709\u591a\u5927\u672c\u4e8b\uff1f\u201d \u3000\u3000\u67f3\u65e0\u7709\u51c4\u7136\u4e00\u7b11\uff0c\u9053\u2236\u201c\u82e5\u6709\u4eba\u80fd\u4ece\u90a3\u4eba\u624b\u4e0a\u5c06\u89e3\u836f\u593a\u6765\uff0c\u6211\u4e5f\u4e0d\u81f3\u65bc\u82e6\u82e6\u8981\u6740\u695a\u7559\u9999 \u4e86\uff0c\u4f60\u60f3\uff0c\u6211\u65e2\u7136\u80fd\u8981\u5e05\u4e00\u5e06\u3001\u8427\u7389\u5251\u3001\u5929\u7f57\u5730\u7f51\u8fd9\u4e9b\u4eba\u6765\u5bb3\u695a\u7559\u9999\uff0c\u81ea\u7136\u4e5f\u5c31\u80fd\u8981\u4ed6\u4eec\u53bb \u4e3a\u6211\u6c42\u89e3\u836f\uff0c\u6211\u4e3a\u4ec0\u9ebd\u4e0d\u8fd9\u6837\u505a\u5462\uff1f\u201d \u3000\u3000\u80e1\u94c1\u5316\u94c1\u7709\u9053\u2236\u201c\u51ed\u90a3\u4e9b\u6b66\u6797\u524d\u8f88\uff0c\u96be\u9053\u90fd\u4e0d\u80fd\u4ece\u90a3\u4eba\u624b\u4e0a\u5c06\u89e3\u836f\u8981\u56de\u6765\uff1f\u201d \u3000\u3000\u67f3\u65e0\u7709\u9eef\u7136\u9053\u2236\u201c\u4ed6\u4eec\u5c31\u7b97\u4e00\u9f50\u53bb\uff0c\u4e5f\u8981\u4e00\u9f50\u6b7b\u5728\u90a3\u4eba\u7684\u624b\u4e0a\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u771f\u7684\u5413\u4e86\u4e00\u8df3\uff0c\u52a8\u5bb9\u9053\u2236\u201c\u4f60\u8bf4\u90a3\u4eba\u7adf\u80fd\u5c06\u5e05\u4e00\u5e06\u3001\u8427\u7389\u5251\u3001\u5929\u7f57\u5730\u7f51\u8fd9\u4e9b\u4eba\u5168 \u90fd\u6740\u6b7b\uff1f\u201d \u3000\u3000\u67f3\u65e0\u7709\u9053\u2236\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u6014\u4e86\u534a\u664c\uff0c\u5583\u5583\u9053\u2236\u201c\u4e16\u4e0a\u771f\u6709\u8fd9\u6837\u7684\u4eba\u9ebd\uff0c\u6211\u5b9e\u5728\u6709\u4e9b\u4e0d\u4fe1\u3002\u201d \u7b2c\u5341\u4e5d\u7ae0\u3000\u4ed9\u5883\u4e0e\u5730\u72f1>> \u53e4\u9f99\u300a\u695a\u7559\u9999\u7cfb\u5217\xb7\u753b\u7709\u9e1f\u300b \u7b2c\u5341\u4e5d\u7ae0\u3000\u4ed9\u5883\u4e0e\u5730\u72f1 \u3000\u3000\u695a\u7559\u9999\u5ffd\u7136\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\u2236\u201c\u5979\u4e0d\u8bf4\u6211\u73b0\u5728\u4e5f\u5df2\u731c\u51fa\u90a3\u4eba\u662f\u8c01\u4e86\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\u2236\u201c\u662f\u8c01\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\u2236\u201c\u6c34\u6bcd\u9634\u59ec\u3002\u201d \u3000\u3000\u201c\u6c34\u6bcd\u9634\u59ec\u201d\u8fd9\u56db\u4e2a\u5b57\u4e00\u8bf4\u51fa\u6765\uff0c\u80e1\u94c1\u82b1\u8138\u4e0a\u5c31\u597d\u50cf\u88ab\u6d82\u4e86\u4e00\u5c42\u6b7b\u7070\u8272\u7684\u6cb9\u6f06\uff0c\u8fde\u773c\u775b \u91cc\u90fd\u6ca1\u6709\u5149\u4e86\u3002 \u3000\u3000\u522b\u7684\u4eba\u66f4\u662f\u8038\u7136\u5931\u8272\uff0c\u5c31\u597d\u50cf\u8fd9\u540d\u5b57\u7684\u672c\u8eab\u5c31\u6709\u4e00\u79cd\u795e\u79d8\u7684\u9b54\u529b\uff0c\u4eba\u4eec\u53ea\u8981\u542c\u5230\u8fd9\u540d \u5b57\uff0c\u5c31\u4f1a\u9047\u89c1\u4e00\u4e9b\u4e0d\u7965\u7684\u4e8b\u3002 \u3000\u3000\u53ea\u6709\u4e45\u5c45\u5927\u6f20\u7684\u9ed1\u73cd\u73e0\uff0c\u4f3c\u4e4e\u8fd8\u5bf9\u8fd9\u540d\u5b57\u4e0d\u5927\u719f\u6089\u3002 \u3000\u3000\u5979\u5fcd\u4e0d\u4f4f\u95ee\u9053\u2236\u201c\u8fd9\u201c\u6c34\u6bcd\u9634\u59ec\u201d\u7684\u540d\u5b57\u6211\u597d\u50cf\u542c\u8fc7\uff0c\u5374\u60f3\u4e0d\u8d77\u662f\u8c01\u4e86\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\u2236\u201c\u6c34\u6bcd\u9634\u59ec\u5c31\u662f\u795e\u6c34\u5a18\u5a18\uff0c\u4e5f\u5c31\u662f\u795e\u6c34\u5bab\u7684\u4e3b\u4eba\u3002\u201d \u3000\u3000\u73b0\u5728\uff0c\u9ed1\u73cd\u73e0\u7684\u9762\u4e0a\u4e5f\u53d8\u4e86\u989c\u8272\u3002 \u3000\u3000\u695a\u7559\u9999\u77a7\u7740\u67f3\u65e0\u7709\u9053\u2236\u201c\u6211\u6ca1\u6709\u731c\u9519\u5427\uff1f\u201d \u3000\u3000\u67f3\u65e0\u7709\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u624d\u70b9\u4e86\u70b9\u5934\uff0c\u957f\u53f9\u9053\u2236\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u9ed1\u73cd\u73e0\u9053\u2236\u201c\u6211\u867d\u7136\u5f88\u5c11\u5165\u5173\uff0c\u4f46\u4e5f\u542c\u8bf4\u8fd9\u201c\u6c34\u6bcd\u9634\u59ec\u201d\u4e43\u662f\u6b66\u6797\u4e2d\u7b2c\u4e00\u4e2a\u602a\u4eba\uff0c\u636e\u8bf4 \u5979\u7684\u813e\u6c14\u8fd8\u6709\u51e0\u5206\u548c\u77f3\u89c2\u97f3\u76f8\u4f3c\uff0c\u5e73\u751f\u6700\u6068\u7537\u4eba\uff0c\u65e0\u8bba\u4efb\u4f55\u7537\u4eba\uff0c\u53ea\u8981\u77a7\u4e86\u5979\u4e00\u773c\uff0c\u5979\u5c31\u7edd \u4e0d\u4f1a\u8ba9\u4ed6\u518d\u6d3b\u4e0b\u53bb\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u63c9\u4e86\u63c9\u9f3b\u5b50\uff0c\u82e6\u7b11\u9053\u2236\u201c\u4f60\u5f04\u9519\u4e86\uff0c\u5979\u7684\u813e\u6c14\u548c\u77f3\u89c2\u97f3\u4e00\u70b9\u4e5f\u4e0d\u76f8\u4f3c\uff0c\u77f3\u89c2\u97f3\u975e \u4f46\u4e0d\u6068\u7537\u4eba\uff0c\u800c\u4e14\u8fb9\u5f88\u559c\u6b22\u7537\u4eba\uff0c\u5c24\u5176\u662f\u6f02\u4eae\u7684\u7537\u4eba\uff0c\u5979\u7684\u6bdb\u75c5\u53ea\u4e0d\u8fc7\u662f\u5bf9\u7537\u4eba\u7684\u80c3\u53e3\u592a\u5927 \u4e86\u800c\u5df2\uff0c\u6240\u4ee5\u603b\u662f\u60f3\u6362\u4e2a\u65b0\u9c9c\u7684\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u53f9\u9053\u2236\u201c\u4f46\u201c\u6c34\u6bcd\u9634\u59ec\u201d\u5374\u662f\u771f\u7684\u6068\u7537\u4eba\uff0c\u636e\u6211\u6240\u77e5\uff0c\u666e\u5929\u4e4b\u4e0b\u7edd\u6ca1\u6709\u4e00\u4e2a\u7537\u4eba \u548c\u5979\u63a5\u8fd1\u8fc7\uff0c\u795e\u6c34\u5bab\u4e2d\u66f4\u770b\u4e0d\u5230\u4e00\u4e2a\u7537\u4eba\u3002\u201d \u3000\u3000\u9ed1\u73cd\u73e0\u9053\u2236\u201c\u53ef\u662f\u6211\u4e5f\u77e5\u9053\u8fd9\u4eba\u867d\u7136\u559c\u6012\u65e0\u5e38\uff0c\u867d\u7136\u5f88\u6068\u7537\u4eba\uff0c\u4f46\u5979\u5374\u5e76\u4e0d\u662f\u4e2a\u574f\u4eba\uff0c \u4e5f\u4e0d\u50cf\u77f3\u89c2\u97f3\u90a3\u9ebd\u6837\uff0c\u60f3\u53bb\u5bb3\u522b\u4eba\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\u2236\u201c\u4e0d\u9519\uff0c\u53ea\u8981\u522b\u4eba\u4e0d\u53bb\u716e\u5979\uff0c\u5979\u4e5f\u7edd\u4e0d\u60f9\u522b\u4eba\u3002\u201d \u3000\u3000\u9ed1\u73cd\u73e0\u9053\u2236\u201c\u90a3\u9ebd\uff0c\u5979\u4e3a\u4ec0\u9ebd\u8981\u6740\u4f60\u5462\uff1f\u4f60\u96be\u9053\u60f9\u4e86\u5979\u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u82e6\u7b11\u9053\u2236\u201c\u6211\u6b63\u662f\u60f9\u4e86\u5979\u4e86\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u53f9\u9053\u2236\u201c\u4f60\u4eec\u7a76\u7adf\u6709\u4ec0\u9ebd\u4ec7\u6068\uff1f\u6211\u4e5f\u4e0d\u77e5\u9053\uff0c\u56e0\u4e3a\u6211\u4e0d\u6562\u95ee\u5979\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u53f9\u9053\u2236\u201c\u53c1\u56db\u4e2a\u6708\u4ee5\u524d\uff0c\u795e\u6c34\u5bab\u4e2d\u5ffd\u7136\u5931\u7a83\uff0c\u4e22\u4e86\u4e00\u74f6\u201c\u5929\u4e00\u795e\u6c34\u201d\uff0c\u795e\u6c34\u5bab\u7684 \u4eba\u7adf\u6000\u7591\u662f\u6211\u5077\u7684\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u9053\u2236\u201c\u7a76\u7adf\u662f\u4e0d\u662f\u4f60\u5462\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u82e6\u7b11\u9053\u2236\u201c\u81ea\u7136\u4e0d\u662f\u6211\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\u2236\u201c\u6211\u4e5f\u76f8\u4fe1\u7edd\u4e0d\u662f\u4ed6\uff0c\u82e5\u662f\u201c\u5929\u4e00\u795e\u9152\u201d\uff0c\u4ed6\u4e5f\u8bb8\u8fd8\u4f1a\u5077\u6765\u559d\u559d\uff0c\u300e\u5929\u4e00\u795e \u6c34\u300f\u4ed6\u5077\u6765\u53c8\u6709\u4ec0\u9ebd\u7528\uff1f\u201d \u3000\u3000\u5b8b\u751c\u513f\u5ffd\u7136\u201c\u5657\u54e7\u201d\u4e00\u7b11\uff0c\u9053\u2236\u201c\u82e5\u662f\u201c\u5929\u4e00\u795e\u918b\u201d\uff0c\u6211\u5c31\u77e5\u9053\u662f\u8c01\u5077\u7684\u4e86\u3002\u201d \u3000\u3000\u674e\u7ea2\u8896\u72e0\u72e0\u77aa\u4e86\u5979\u4e00\u773c\uff0c\u54ac\u7740\u5634\u5507\u6084\u58f0\u9053\u2236\u201c\u5c0f\u8868\uff0c\u4f60\u624d\u662f\u4e2a\u918b\u575b\u5b50\u54e9\uff01\u201d \u3000\u3000\u5979\u4eec\u548c\u695a\u7559\u9999\u751f\u6d3b\u4e86\u90a3\u9ebd\u591a\u5e74\uff0c\u53c8\u751f\u6d3b\u5728\u6d77\u4e0a\uff0c\u6240\u4ee5\u5979\u4eec\u7684\u5fc3\u80f8\u90fd\u5f88\u5f00\u6717\uff0c\u968f\u65f6\u90fd\u4e0d\u4f1a \u5fd8\u8bb0\u7b11\u7b11\u3002 \u3000\u3000\u4f46\u695a\u7559\u9999\u73b0\u5728\u5374\u771f\u6709\u4e9b\u7b11\u4e0d\u51fa\u4e86\u3002 \u3000\u3000\u4ed6\u76b1\u7740\u7709\u9053\u2236\u201c\u5929\u4e00\u795e\u6c34\u6211\u867d\u8fde\u89c1\u90fd\u6ca1\u6709\u89c1\u8fc7\uff0c\u4f46\u795e\u6c34\u5bab\u7684\u4eba\u5374\u4e0d\u80af\u653e\u8fc7\u6211\uff0c\u7adf\u903c\u7740\u8981 \u6211\u5728\u4e00\u4e2a\u6708\u4e2d\u5c06\u5077\u6c34\u7684\u90a3\u4eba\u627e\u51fa\u6765\uff0c\u5426\u5219\u5979\u4eec\u5c31\u8981\u6765\u627e\u6211\u7b97\u8d26\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u9053\u2236\u201c\u4f60\u627e\u51fa\u4e86\u90a3\u4eba\u662f\u8c01\u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u82e6\u7b11\u9053\u2236\u201c\u6211\u627e\u51fa\u6765\u4e86\uff0c\u53ea\u53ef\u60dc\u90a3\u4e00\u9635\u5b50\u53d1\u751f\u7684\u4e8b\u592a\u591a\uff0c\u6211\u7adf\u5fd8\u4e86\u795e\u6c34\u5bab\u7ed9\u6211\u7684 \u9650\u671f\uff0c\u4e5f\u6ca1\u6709\u53bb\u5411\u5979\u4eec\u4ea4\u4ee3\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u6447\u7740\u5934\u9053\u2236\u201c\u4e00\u4e2a\u6709\u6559\u517b\u7684\u7537\u4eba\uff0c\u600e\u9ebd\u80fd\u5fd8\u8bb0\u4ed6\u548c\u5973\u4eba\u7684\u7ea6\u4f1a\u5462\uff1f\u8fd9\u5c31\u96be\u602a\u522b\u4eba \u8981\u6765\u627e\u4f60\u7684\u9ebb\u70e6\u4e86\uff0c\u6211\u5012\u4e0d\u602a\u5979\u4eec\u3002\u201d \u3000\u3000\u674e\u7ea2\u8896\u561f\u7740\u5634\u9053\u2236\u201c\u4ed6\u6839\u672c\u5c31\u4e0d\u8be5\u548c\u5979\u4eec\u7ea6\u5b9a\u7684\uff0c\u90a3\u65f6\u4ed6\u6839\u6728\u8fde\u4e00\u70b9\u628a\u63e1\u4e5f\u6ca1\u6709\uff0c\u8fd9\u4ef6 \u4e8b\u4e5f\u6839\u672c\u548c\u4ed6\u65e0\u5173\uff0c\u4f46\u4ed6\u4e00\u77a7\u89c1\u90a3\u4f4d\u773c\u6ce2\u6bd4\u6d77\u6c34\u8fd8\u6e29\u67d4\u7684\u5973\u5b69\u5b50\uff0c\u4ed6\u5934\u5c31\u6655\u4e86\uff0c\u5c31\u7cca\u91cc\u7cca\u6d82 \u7684\u7b54\u5e94\u4e86\u4eba\u5bb6\uff0c\u73b0\u5728\u795e\u6c34\u5bab\u2026\u2026\u201d \u3000\u3000\u5b8b\u751c\u513f\u5ffd\u53c8\u5657\u54e7\u4e00\u7b11\uff0c\u9053\u2236\u201c\u795e\u6c34\u5bab\u4e5f\u6ca1\u6709\u4ec0\u9ebd\u4e86\u4e0d\u8d77\uff0c\u5979\u4eec\u82e5\u6765\u4e86\uff0c\u6211\u4eec\u8fd9\u91cc\u53cd\u6b63\u6709 \u201c\u795e\u918b\u5bab]\u7684\u638c\u95e8\u4eba\u5bf9\u4ed8\u5979\u3002\u201d \u3000\u3000\u5176\u5b9e\u674e\u7ea2\u8896\u548c\u5b8b\u751c\u513f\u4e5f\u77e5\u9053\u73b0\u5728\u5e76\u4e0d\u662f\u9002\u65bc\u5f00\u73a9\u7b11\u7684\u65f6\u5019\uff0c\u5979\u4eec\u53ea\u4e0d\u8fc7\u662f\u89c9\u5f97\u8fd9\u5730\u65b9\u7684 \u773c\u6cea\u5df2\u592a\u591a\u4e86\uff0c\u6240\u4ee5\u5979\u4eec\u5c31\u8981\u5236\u9020\u4e9b\u6b22\u7b11\u3002\u56e0\u4e3a\u5979\u4eec\u8ba4\u4e3a\u4eba\u4eec\u5728\u906d\u9047\u5230\u56f0\u96be\u548c\u4e0d\u5e78\u7684\u65f6\u5019\uff0c \u773c\u6cea\u5e76\u4e0d\u542c\u5230\u8fd9\u91cc\uff0c\u82cf\u84c9\u84c9\u3001\u5b8b\u751c\u513f\u3001\u674e\u7ea2\u8896\u76ee\u4e2d\u7adf\u90fd\u5df2\u4e0d\u7981\u6d41\u4e0b\u4e86\u773c\u6cea\uff0c\u9ed1\u73cd\u73e0\u8138\u4e0a\u4e5f\u4e0d \u7981\u9732\u51fa\u60b2\u75db\u4e4b\u8272\u3002 \u3000\u3000\u5973\u4eba\u4e0e\u5973\u4eba\u4e4b\u95f4\uff0c\u867d\u7136\u5f88\u96be\u4ea4\u670b\u53cb\uff0c\u4f46\u5973\u4eba\u5374\u603b\u662f\u540c\u60c5\u5973\u4eba\uff0c\u56e0\u4e3a\u5979\u4eec\u89c9\u5f97\u53ea\u8981\u662f\u5973 \u4eba\uff0c\u5c31\u503c\u5f97\u540c\u60c5\u3002 \u3000\u3000\u82cf\u84c9\u84c9\u5e7d\u5e7d\u53f9\u9053\u2236\u201c\u8fd9\u4e9b\u5e74\u6765\uff0c\u4f60\u7684\u65e5\u5b50\u4e00\u5b9a\u5f88\u4e0d\u597d\u8fc7\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\u2236\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u90a3\u5929\u534a\u591c\u4f60\u5728\u90a3\u5ba2\u6808\u4e2d\u547b\u541f\u547c\u53f7\uff0c\u4e5f\u662f\u56e0\u4e3a\u75c5\u6bd2\u53d1\u4f5c\uff0c\u5e76\u4e0d\u662f \u5047\u88c5\u7684\u4e86\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u9053\u2236\u201c\u4e0d\u9519\uff0c\u4ee5\u524d\u6211\u6bd2\u53d1\u65f6\u53ea\u8981\u4e00\u670d\u7f42\u7c9f\uff0c\u75db\u82e6\u7acb\u6b63\uff0c\u4f46\u6700\u8fd1\u8fd9\u4e9b\u65e5\u5b50\uff0c\u5c31\u7b97\u7528 \u6bd4\u4ee5\u524d\u591a\u4e24\u500d\u7684\u7f42\u7c9f\u6765\u6b62\u75db\uff0c\u4e5f\u4e0d\u5982\u4ee5\u524d\u90a3\u9ebd\u6709\u6548\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u53f9\u9053\u2236\u201c\u8fd9\u5e76\u4e0d\u662f\u56e0\u4e3a\u7f42\u7c9f\u5df2\u5931\u53bb\u6b62\u75db\u4e4b\u529b\uff0c\u800c\u662f\u56e0\u4e3a\u4f60\u6574\u4e2a\u4eba\u90fd\u5df2\u6e10\u6e10\u88ab\u5b83\u9ebb \u6728\uff0c\u5c31\u6b63\u5982\u4e0a\u4e86\u9152\u763e\u7684\u4eba\uff0c\u9152\u5fc5\u5b9a\u8d8a\u559d\u8d8a\u591a\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u62a2\u7740\u9053\u2236\u201c\u4e00\u70b9\u4e5f\u4e0d\u9519\uff0c\u4ee5\u524d\u6211\u559d\u9152\u65f6\uff0c\u53ea\u8981\u559d\u4e0a\u4e2a\u53c1\u4e94\u676f\uff0c\u5c31\u4f1a\u89c9\u5f97\u98d8\u98d8\u6b32 \u4ed9\uff0c\u5fd8\u5374\u4e86\u6240\u6709\u70e6\u607c\uff0c\u4f46\u73b0\u5728\u6211\u5c31\u7b97\u559d\u4e0a\u53c1\u4e94\u65a4\u70e7\u5200\u5b50\uff0c\u8fd8\u662f\u597d\u50cf\u6ca1\u559d\u4e00\u6837\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u5fcd\u4e0d\u4f4f\u7b11\u4e86\u7b11\uff0c\u4ed6\u77e5\u9053\u4e00\u4e2a\u559d\u9152\u7684\u4eba\uff0c\u968f\u65f6\u90fd\u4f1a\u627e\u673a\u4f1a\u5439\u5618\u5439\u5618\u81ea\u5df1\u7684\u9152\u91cf\u3002 \u3000\u3000\u53ea\u542c\u80e1\u94c1\u82b1\u53c8\u9053\u2236\u201c\u90a3\u5929\u4f60\u65e2\u7136\u662f\u771f\u7684\u6709\u6bdb\u75c5\uff0c\u7528\u66b4\u96e8\u68a8\u82b1\u9489\u6765\u6697\u7b97\u6211\u4eec\u7684\u4eba\u53c8\u662f\u8c01 \u5462\uff1f\u201d \u3000\u3000\u67f3\u65e0\u7709\u6c89\u9ed8\u4e86\u534a\u664c\uff0c\u6de1\u6de1\u9053\u2236\u201c\u4e5f\u662f\u6211\u3002\u201d \u3000\u3000\u80fd\u89e3\u51b3\u4efb\u4f55\u95ee\u9898\u3002 \u3000\u3000\u53ea\u6709\u7b11\u58f0\u624d\u662f\u5bf9\u4ed8\u56f0\u96be\u548c\u4e0d\u5e78\u7684\u6700\u597d\u6b66\u5668\u3002 \u3000\u3000\u53ef\u662f\u5979\u4eec\u5df2\u6e10\u6e10\u53d1\u73b0\u5979\u4eec\u7684\u7b11\u58f0\u975e\u4f46\u6ca1\u6709\u51b2\u6de1\u522b\u4eba\u7684\u60b2\u54c0\uff0c\u53cd\u800c\u5c01\u522b\u4eba\u662f\u79cd\u523a\u6fc0\u3002 \u3000\u3000\u770b\u89c1\u5979\u4eec\u7b11\u5f97\u90a3\u9ebd\u5f00\u5fc3\uff0c\u67f3\u65e0\u7709\u7684\u795e\u60c5\u5c31\u663e\u5f97\u66f4\u60e8\u6de1\uff0c\u56e0\u4e3a\u5979\u89c9\u5f97\u6bcf\u4e2a\u4eba\u90fd\u5f88\u5e78\u798f\uff0c\u53ea \u6709\u5b83\u7684\u4e00\u751f\u5145\u6ee1\u4e0d\u5e78\u3002 \u3000\u3000\u674e\u7ea2\u8896\u548c\u5b8b\u751c\u513f\u4e5f\u6e10\u6e10\u7b11\u4e0d\u51fa\u4e86\u3002 \u3000\u3000\u8fd9\u65f6\u67f3\u65e0\u7709\u624d\u60f3\u8d77\u5979\u4eec\u8fd8\u88ab\u56da\u5728\u7262\u72f1\u91cc\uff0c\u65bc\u662f\u5979\u7684\u624b\u5728\u77f3\u58c1\u4e0a\u8f7b\u8f7b\u4e00\u89e6\uff0c\u94c1\u6805\u4fbf\u7f13\u7f13\u6ed1 \u5f00\uff0c\u6ca1\u5165\u77f3\u58c1\u91cc\u3002 \u3000\u3000\u7136\u5f8c\u5979\u5c31\u8f6c\u8fc7\u8eab\uff0c\u5411\u695a\u7559\u9999\u76c8\u76c8\u4e00\u62dc\uff0c\u9eef\u7136\u9053\u2236\u201c\u6211\u592b\u59bb\u8499\u9999\u5e05\u5f00\u6069\u4e0d\u6740\uff0c\u5df2\u662f\u611f\u6fc0\u4e0d \u5c3d\uff0c\u5b9e\u5728\u4e0d\u80fd\uff0c\u4e5f\u4e0d\u6562\u518d\u6c42\u9999\u5e05\u51fa\u624b\u76f8\u6551\u4e86\uff0c\u6b64\u5f8c\u4f46\u671b\u2026\u2026\u201d \u3000\u3000\u695a\u7559\u9999\u6253\u65af\u4e86\u5b83\u7684\u8bdd\uff0c\u9053\u2236\u201c\u4f60\u4e0d\u5fc5\u8ba4\u4e3a\u6211\u662f\u8981\u5192\u9669\u53bb\u6551\u4f60\uff0c\u53cd\u6b63\u6211\u662f\u975e\u5230\u795e\u6c34\u5bab\u53bb\u8d70 \u4e00\u8d9f\u4e0d\u53ef\u7684\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u957f\u53f9\u4e86\u4e00\u58f0\uff0c\u9053\u2236\u201c\u90a3\u79cd\u5730\u65b9\uff0c\u9999\u5e05\u4f60\u4e0d\u53bb\u4e5f\u7f62\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u9053\u2236\u201c\u6211\u600e\u9ebd\u80fd\u4e0d\u53bb\uff0c\u6211\u82e5\u4e0d\u53bb\uff0c\u4ee5\u5f8c\u7684\u9ebb\u70e6\u53ea\u6015\u66f4\u5927\u4e86\uff0c\u90a3\u4f4d\u201c\u6c34\u6bcd\u9634\u59ec\u201d \u65e2\u7136\u80fd\u8981\u4f60\u6765\u6740\u6211\uff0c\u4e5f\u80fd\u8981\u522b\u4eba\u6765\u6740\u6211\uff0c\u6211\u96be\u9053\u8fd8\u80fd\u63d0\u9632\u5979\u4e00\u8f88\u5b50\u9ebd\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u7acb\u523b\u6309\u7740\u9053\u2236\u201c\u4e0d\u9519\uff0c\u4ed6\u65e2\u7136\u5df2\u5931\u4e86\u7ea6\uff0c\u5c31\u8be5\u53bb\u548c\u4eba\u5bb6\u8bb2\u4e2a\u660e\u767d\uff0c\u6211\u60f3\u90a3\u201c\u6c34\u6bcd \u9634\u59ec\u201d\u603b\u4e0d\u4f1a\u662f\u4e2a\u86ee\u4e0d\u8bb2\u7406\u7684\u4eba\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u53f9\u9053\u2236\u201c\u4f60\u4ee5\u4e3a\u5979\u662f\u4e2a\u5f88\u8bb2\u7406\u7684\u4eba\u9ebd\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u6012\u9053\u2236\u201c\u5979\u82e5\u771f\u7684\u4e0d\u8bb2\u7406\uff0c\u6211\u4eec\u4e5f\u6709\u4e0d\u8bb2\u7406\u7684\u6cd5\u5b50\u5bf9\u4ed8\u5979\uff0c\u90a3\u795e\u6c34\u5bab\u5c31\u662f\u5200\u5c71\u706b \u6d77\uff0c\u9f99\u6f6d\u864e\u7a74\uff0c\u6211\u80e1\u67d0\u4eba\u4e5f\u8981\u53bb\u95ef\u4e00\u95ef\u3002\u201d \u3000\u3000\u82cf\u84c9\u84c9\u5ffd\u7136\u9053\u2236\u201c\u795e\u6c34\u5bab\u65e2\u6ca1\u6709\u5200\u5c71\u706b\u6d77\uff0c\u4e5f\u4e0d\u662f\u9f99\u6f6d\u864e\u7a74\uff0c\u53cd\u800c\u662f\u4e2a\u98ce\u666f\u975e\u5e38\u4f18\u7f8e\uff0c \u6709\u5982\u4ed9\u5883\u7684\u5730\u65b9\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\u2236\u201c\u5bf9\u4e86\uff0c\u53ea\u6709\u4f60\u662f\u5230\u795e\u6c34\u5bab\u53bb\u8fc7\u7684\uff0c\u4f60\u89c9\u90a3\u5730\u65b9\u662f\u4e0d\u662f\u771f\u7684\u5f88\u53ef\u6015\uff1f\u201d \u3000\u3000\u82cf\u84c9\u84c9\u9053\u2236\u201c\u5728\u6211\u8bf4\u6765\uff0c\u90a3\u5730\u65b9\u5b9e\u5728\u4e00\u70b9\u4e5f\u4e0d\u53ef\u6015\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\u2236\u201c\u54e6\uff1f\u201d \u3000\u3000\u82cf\u84c9\u84c9\u9053\u2236\u201c\u4f60\u53ef\u542c\u5230\u8fc7\u4f20\u8bf4\u4e2d\u7684\u6843\u82b1\u6e90\u9ebd\uff1f\u795e\u6c34\u5bab\u5c31\u548c\u6843\u82b1\u6e90\u4e00\u6837\uff0c\u7b80\u76f4\u53ef\u8bf4\u662f\u4eba\u95f4 \u7684\u4ed9\u5883\uff0c\u6211\u5230\u4e86\u90a3\u91cc\u4e4b\u5f8c\uff0c\u8fd8\u65e0\u6cd5\u76f8\u4fe1\u90a3\u5c31\u662f\u540d\u9707\u5929\u4e0b\u7684\u795e\u6c34\u5bab\uff0c\u56e0\u90a3\u91cc\u975e\u4f46\u6ca1\u6709\u6740\u6c14\uff0c\u8fde \u4e00\u70b9\u70df\u706b\u6c14\u90fd\u6ca1\u6709\u3002\u201d \u3000\u3000\u5979\u773c\u6ce2\u770b\u6765\u66f4\u6e29\u67d4\uff0c\u7f13\u7f13\u6309\u7740\u9053\u2236\u201c\u90a3\u65f6\u5019\u6b63\u662f\u521d\u590f\uff0c\u6211\u5750\u7740\u6761\u5c0f\u8239\uff0c\u6cbf\u6eaa\u800c\u4e0a\uff0c\u8d70\u4e86 \u5f88\u4e45\u4e4b\u5f8c\uff0c\u5c31\u53d1\u89c9\u6709\u4e00\u74e3\u74e3\u6843\u82b1\u6cbf\u7740\u6eaa\u6c34\u6d41\u4e0b\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u5fcd\u4e0d\u4f4f\u95ee\u9053\u2236\u201c\u662f\u4e0d\u662f\u8fd8\u6709\u80e1\u9ebb\u996d\uff1f\u201d \u3000\u3000\u82cf\u84c9\u84c9\u5ae3\u7136\u4e00\u7b11\uff0c\u9053\u2236\u201c\u82b1\u74e3\u4e2d\u7684\u786e\u8fd8\u6709\u5f88\u9999\u7684\u80e1\u9ebb\u996d\uff0c\u5fae\u98ce\u4e2d\u82b1\u9999\u66f4\u9189\u4eba\uff0c\u6211\u5750\u5728\u8239 \u4e0a\uff0c\u975e\u4f46\u597d\u50cf\u5df2\u8d70\u5165\u4e86\u56fe\u753b\uff0c\u7b80\u76f4\u597d\u50cf\u5df2\u8d70\u5165\u4e86\u795e\u8bdd\u3002\u201d \u3000\u3000\u5979\u8bf4\u5f97\u90a3\u9ebd\u7f8e\uff0c\u8fde\u80e1\u94c1\u82b1\u90fd\u4e0d\u89c9\u542c\u5f97\u75f4\u4e86\u3002 \u3000\u3000\u82cf\u84c9\u84c9\u5df2\u63a5\u7740\u9053\u2236\u201c\u6211\u5982\u75f4\u5982\u9189\uff0c\u4e5f\u4e0d\u77e5\u8239\u884c\u4e86\u591a\u4e45\uff0c\u6e10\u6e10\u8d70\u5165\u4e00\u6761\u5c71\u9699\u91cc\uff0c\u4e24\u65c1\u90fd\u751f \u7740\u5f88\u6d53\u5bc6\u7684\u6c34\u8349\uff0c\u6697\u5f97\u4f38\u624b\u4e0d\u89c1\u4e94\u6307\u3002\u6211\u7528\u6868\u62e8\u7740\u6c34\u8349\uff0c\u53c8\u8d70\u4e86\u5f88\u4e45\uff0c\u773c\u524d\u8c41\u7136\u5f00\u6717\uff0c\u53ea\u89c1 \u773c\u524d\u767e\u82b1\u5982\u9526\uff0c\u662f\u4e00\u7247\u9526\u7ee3\u5c71\u8c37\uff0c\u53f3\u9762\u4e00\u9053\u7011\u5e03\u81ea\u5c71\u5dc5\u98de\u6302\u800c\u4e0b\uff0c\u9e23\u73e0\u6e85\u7389\uff0c\u6c81\u4eba\u5fc3\u80ba\uff0c\u82b1 \u4e1b\u95f4\u9690\u9690\u53ef\u4ee5\u89c1\u5230\u4e00\u4e9b\u4ead\u53f0\u8305\u820d\uff0c\u8fd8\u6709\u51e0\u5341\u51e0\u4e07\u53ea\u4e0d\u77e5\u540d\u7684\u9e1f\u5728\u98de\u6765\u98de\u53bb\uff0c\u89c1\u4e86\u4eba\u4e5f\u4e0d\u5bb3 \u6015\uff0c\u7adf\u6709\u51e0\u53ea\u98de\u5230\u6211\u7684\u80a9\u5934\uff0c\u50cf\u662f\u8981\u548c\u6211\u8bf4\u8bdd\u3002\u201d \u3000\u3000\u8fd9\u5982\u8bd7\u5982\u753b\u7684\u7f8e\u666f\u88ab\u5979\u7528\u90a3\u6e29\u67d4\u7684\u8bed\u58f0\u5a13\u5a13\u8bf4\u6765\uff0c\u66f4\u4ee4\u4eba\u5176\u610f\u4e5f\u6d88\uff0c\u674e\u7ea2\u8896\u8f7b\u8f7b\u53f9\u4e86\u53e3 \u6c14\uff0c\u9053\u2236\u201c\u65e9\u77e5\u795e\u6c34\u5bab\u662f\u8fd9\u9ebd\u6837\u7684\u4ed9\u5883\uff0c\u6211\u4e5f\u8be5\u966a\u4f60\u53bb\u7684\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u5ffd\u7136\u95ee\u9053\u2236\u201c\u4f46\u59d1\u5a18\u4f60\u53c8\u600e\u4f1a\u77e5\u9053\u90a3\u6761\u5c0f\u6eaa\u5c31\u662f\u5165\u5c71\u7684\u9014\u5f84\u5462\uff1f\u201d \u3000\u3000\u82cf\u84c9\u84c9\u9053\u2236\u201c\u6211\u6709\u4e2a\u59d1\u59d1\uff0c\u662f\u795e\u6c34\u5bab\u7684\u95e8\u4e0b\uff0c\u5979\u66fe\u7ecf\u544a\u8bc9\u8fc7\u6211\uff0c\u8981\u53bb\u627e\u5979\u7684\u65f6\u5019\u5e94\u8be5\u600e \u9ebd\u6837\u53bb\uff0c\u5979\u81ea\u7136\u4e0d\u51c6\u6211\u5c06\u8fd9\u79d8\u5bc6\u8bf4\u7ed9\u522b\u4eba\u77e5\u9053\u3002\u201d \u3000\u3000\u5b8b\u751c\u513f\u7728\u7740\u773c\u9053\u2236\u201c\u4f60\u59d1\u59d1\u4e5f\u4f4f\u5728\u82b1\u4e1b\u95f4\u90a3\u4e9b\u5c4b\u5b50\u91cc\u9ebd\uff1f\u201d \u3000\u3000\u82cf\u84c9\u84c9\u9053\u2236\u201c\u5f8c\u6765\u6211\u624d\u77e5\u9053\uff0c\u82b1\u6811\u4e1b\u4e2d\u90a3\u4e9b\u4ead\u53f0\u8305\u820d\uff0c\u5c31\u662f\u795e\u6c34\u5bab\u95e8\u4e0b\u7684\u5c45\u5904\uff0c\u56e0\u4e3a\u6bcf \u4e2a\u4eba\u7684\u559c\u7231\u4e0d\u540c\uff0c\u662f\u4ee5\u5979\u4eec\u4f4f\u7684\u5c4b\u5b50\u5f0f\u6837\u4e5f\u4e0d\u540c\u3002\u201d \u3000\u3000\u674e\u7ea2\u8896\u9053\u2236\u201c\u4f60\u59d1\u59d1\u4f4f\u7684\u5730\u65b9\u662f\u4ec0\u9ebd\u6837\u4e8e\u5462\uff1f\u201d \u3000\u3000\u82cf\u84c9\u5df7\u9053\u2236\u201c\u5979\u4f4f\u7684\u662f\u4e24\u95f4\u5f88\u7cbe\u81f4\u7684\u8305\u820d\uff0c\u5916\u9762\u6709\u7af9\u7bf1\uff0c\u9662\u5b50\u91cc\u8fd8\u79cd\u7740\u83ca\u82b1\uff0c\u90a3\u65f6\u83ca\u82b1 \u867d\u7136\u8fd8\u6ca1\u6709\u5f00\u653e\uff0c\u4f46\u6211\u4e00\u5230\u4e86\u90a3\u91cc\uff0c\u5c31\u4e0d\u7981\u60f3\u8d77\u9676\u6e0a\u660e\u7ea6\u4e24\u53e5\u8bd7\u3002\u201d \u3000\u3000\u674e\u7ea2\u8896\u6f2b\u58f0\u9053\u2236\u201c\u91c7\u83ca\u4e1c\u7bf1\u4e0b\uff0c\u60a0\u7136\u89c1\u5357\u5c71\u2026\u2026\u201d \u3000\u3000\u80e1\u94c1\u82b1\u53f9\u4e86\u53e3\u6c14\uff0c\u5583\u5583\u9053\u2236\u201c\u770b\u6765\u513f\u8fd9\u201c\u6c34\u6bcd\u9634\u59ec\u201d\u5bf9\u5979\u7684\u5f92\u5f1f\uff0c\u5b9e\u5728\u6bd4\u77f3\u89c2\u97f3\u597d\u5f97\u591a \u4e86\u3002\u201d \u3000\u3000\u82cf\u84c9\u84c9\u9053\u2236\u201c\u53ea\u53ef\u60dc\u6211\u5230\u4e86\u90a3\u91cc\u4e4b\u5f8c\uff0c\u5e76\u4e0d\u80fd\u56db\u4e0b\u6e38\u901b\uff0c\u53ea\u80fd\u5f85\u5728\u6211\u59d1\u59d1\u7684\u5c4b\u5b50\u91cc\uff0c\u56e0 \u4e3a\u5979\u8b66\u544a\u8fc7\u6211\uff0c\u6211\u82e5\u5230\u5904\u4e71\u8dd1\uff0c\u7acb\u523b\u5c31\u4f1a\u6709\u5f88\u5927\u7684\u707e\u7978\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\u2236\u201c\u4ec0\u9ebd\u707e\u7978\uff01\u201d \u3000\u3000\u82cf\u84c9\u84c9\u9053\u2236\u201c\u5979\u4e5f\u6ca1\u6709\u8bf4\u51fa\u662f\u4ec0\u9ebd\u707e\u7978\uff0c\u53ea\u662f\u5c06\u6211\u5173\u5728\u5c4b\u5b50\u91cc\uff0c\u4e0d\u8ba9\u6211\u89c1\u4eba\uff0c\u6240\u4ee5\u627e\u8fde \u90a3\u4f4d\u5bab\u5357\u71d5\u59d1\u5a18\u90fd\u6ca1\u6709\u89c1\u5230\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\u2236\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u4f60\u4e5f\u6ca1\u6709\u89c1\u5230\u201c\u6c34\u6bcd\u9634\u59ec\u201d\u4e86\uff1f\u201d \u3000\u3000\u82cf\u84c9\u84c9\u9053\u2236\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\u2236\u201c\u4f60\u4e5f\u4e0d\u77e5\u9053\u5979\u4f4f\u5728\u4ec0\u9ebd\u5730\u65b9\uff1f\u201d \u3000\u3000\u82cf\u84c9\u84c9\u9053\u2236\u201c\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u5979\u53f9\u4e86\u53e3\u6c14\uff0c\u63a5\u7740\u9053\u2236\u201c\u6211\u5b9e\u5728\u5f88\u60f3\u89c1\u89c1\u8fd9\u4f4d\u6b66\u6797\u4e2d\u7684\u4f20\u5947\u4eba\u7269\uff0c\u4f46\u6211\u59d1\u59d1\u5374\u518d\u53c1\u8b66\u544a \u6211\uff0c\u4e0d\u8ba9\u6211\u89c1\u5979\uff0c\u53ef\u662f\u6211\u77e5\u9053\u5979\u7684\u786e\u4e5f\u4f4f\u5728\u90a3\u7247\u5c71\u8c37\u91cc\uff0c\u4e5f\u8bb8\u5c31\u5728\u6211\u59d1\u59d1\u8305\u820d\u5bf9\u9762\u90a3\u7247\u6843\u82b1 \u676f\u4e2d\uff0c\u4e5f\u8bb8\u5c31\u5728\u5c71\u5761\u524d\u90a3\u5c0f\u5c0f\u7684\u5c3c\u5eb5\u91cc\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\u2236\u201c\u5c3c\u5eb5\uff1f\u795e\u6c34\u5bab\u4e2d\u96be\u9053\u4e5f\u6709\u5c3c\u59d1\u9ebd\uff1f\u201d \u3000\u3000\u82cf\u84c9\u84c9\u9053\u2236\u201c\u636e\u8bf4\u201c\u6c34\u6bcd\u9634\u59ec\u201d\u662f\u4f4d\u5f88\u8654\u8bda\u7684\u5c45\u58eb\uff0c\u6240\u4ee5\u5979\u624d\u4f1a\u8ba9\u201c\u5999\u50e7\u201d\u65e0\u82b1\u5165\u8c37\u53bb \u89e3\u8bf4\u4f5b\u7ecf\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u6c89\u541f\u7740\u9053\u2236\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u5b83\u7684\u786e\u5f88\u53ef\u80fd\u5c31\u4f4f\u5728\u90a3\u5c3c\u5eb5\u91cc\u7684\u3002\u201d \u3000\u3000\u82cf\u84c9\u84c9\u9053\u2236\u201c\u4f46\u636e\u6211\u6240\u77e5\uff0c\u65e0\u82b1\u4e5f\u5e76\u6ca1\u6709\u89c1\u8fc7\u5979\uff0c\u65e0\u82b1\u5165\u8c37\u5f8c\uff0c\u6bcf\u5929\u90fd\u8981\u5750\u5728\u7011\u5e03\u524d\u7684 \u5927\u77f3\u4e0a\u8bb2\u4e24\u4e2a\u65f6\u8fb0\u4f5b\u7ecf\uff0c\u4ed6\u4e5f\u77e5\u9053\u201c\u6c34\u6bcd\u9634\u59ec\u201d\u6bcf\u5929\u90fd\u5728\u542c\u4ed6\u8bb2\u7ecf\uff0c\u5374\u59cb\u7ec8\u6ca1\u6709\u89c1\u5230\u5979\u7684\u4eba \u7a76\u7adf\u5728\u90a3\u91cc\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u82e6\u82e6\u7b11\u9053\u2236\u201c\u8fd9\u5b9e\u5728\u662f\u4e2a\u5f88\u795e\u79d8\u7684\u4eba\u7269\uff0c\u6bd4\u6211\u60f3\u50cf\u4e2d\u8fd8\u8981\u795e\u79d8\u5f97\u591a\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u7b11\u9053\u2236\u201c\u4f46\u8fd9\u795e\u6c34\u5bab\u5374\u6ca1\u6709\u6211\u60f3\u50cf\u4e2d\u795e\u79d8\uff0c\u6211\u672c\u6765\u4ee5\u4e3a\u90a3\u5730\u65b9\u4e00\u5b9a\u5f88\u9634\u68ee\u53ef\u6015\uff0c \u8c01\u77e5\u5374\u6bd4\u4e16\u4e0a\u5927\u591a\u6570\u5730\u65b9\u90fd\u53ef\u7231\u5f97\u591a\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u5ffd\u7136\u9053\u2236\u201c\u5404\u4f4d\u83ab\u8981\u5fd8\u4e86\uff0c\u6211\u4e5f\u5230\u795e\u6c34\u5bab\u53bb\u8fc7\u7684\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\u2236\u201c\u4f60\u81ea\u7136\u53bb\u8fc7\u7684\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u9053\u2236\u201c\u636e\u6211\u6240\u77e5\uff0c\u795e\u6c34\u5bab\u5e76\u4e0d\u662f\u82cf\u59d1\u5a18\u6240\u8bf4\u7684\u90a3\u79cd\u5730\u65b9\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u8bb6\u7136\u9053\u2236\u201c\u54e6\uff1f\u4f60\u89c1\u5230\u7684\u795e\u6c34\u5bab\u96be\u9053\u6709\u4ec0\u9ebd\u4e0d\u540c\u9ebd\uff1f\u201d \u3000\u3000\u67f3\u65e0\u7709\u9053\u2236\u201c\u6709\u5f88\u5927\u7684\u4e0d\u540c\u3002\u201d\u5979\u4e00\u5b57\u5b57\u6309\u7740\u9053\u2236\u201c\u82cf\u59d1\u5a18\u89c1\u5230\u7684\u795e\u6c34\u5bab\uff0c\u662f\u4eba\u95f4\u4ed9 \u5883\uff0c\u6211\u89c1\u5230\u7684\u795e\u6c34\u5bab\uff0c\u5374\u662f\u4eba\u95f4\u5730\u72f1\u3002\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u8bf4\u51fa\u6765\uff0c\u5927\u5bb6\u53c8\u5168\u90fd\u6014\u4f4f\u3002 \u3000\u3000\u67f3\u65e0\u7709\u9053\u2236\u201c\u6211\u6ca1\u6709\u59d1\u59d1\u6307\u70b9\u6211\u5165\u5c71\u7684\u9014\u5f84\uff0c\u6240\u4ee5\u8d39\u4e86\u8bb8\u591a\u529f\u592b\uff0c\u624d\u6253\u542c\u51fa\u60f3\u5230\u795e\u6c34\u5bab \u53bb\u7684\u4eba\uff0c\u4e00\u5b9a\u8981\u5148\u7ecf\u8fc7\u83e9\u63d0\u5eb5\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u76b1\u7709\u9053\u2236\u201c\u8fd9\u83e9\u63d0\u5eb5\u65e2\u548c\u795e\u6c34\u5bab\u5173\u7cfb\u5982\u6b64\u5bc6\u5207\uff0c\u81ea\u7136\u4e5f\u5fc5\u5b9a\u662f\u4e2a\u5f88\u6709\u540d\u7684\u5730\u65b9\uff0c \u6211\u600e\u5730\u4ece\u672a\u542c\u8fc7\u8fd9\u540d\u5b57\uff1f\u201d \u3000\u3000\u67f3\u65e0\u7709\u9053\u2236\u201c\u8fd9\u83e9\u63d0\u5eb5\u53ea\u4e0d\u8fc7\u662f\u95f4\u5f88\u7834\u70c2\u7684\u5c0f\u5e99\uff0c\u5eb5\u91cc\u4e5f\u53ea\u6709\u4e00\u4e2a\u5c3c\u59d1\uff0c\u8fd9\u5c3c\u59d1\u770b\u6765\u81f3 \u5c11\u5df2\u7ecf\u6709\u4e03\u516b\u5341\u5c81\u4e86\uff0c\u800c\u4e14\u4f3c\u4e4e\u53c8\u804b\u53c8\u54d1\uff0c\u4f46\u65e0\u8bba\u4ec0\u9ebd\u4eba\uff0c\u8981\u60f3\u5230\u795e\u6c34\u5bab\u53bb\uff0c\u5c31\u5f97\u5c06\u81ea\u5df1\u4e3a \u4ec0\u9ebd\u8981\u53bb\u7684\u539f\u56e0\uff0c\u544a\u8bc9\u8fd9\u8001\u5c3c\u59d1\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\u2236\u201c\u8fd9\u5c3c\u59d1\u65e2\u7136\u53c8\u804b\u53c8\u54d1\uff0c\u600e\u9ebd\u80fd\u542c\u5230\u522b\u4eba\u8bf4\u8bdd\uff1f\u201d \u3000\u3000\u67f3\u65e0\u7709\u9053\u2236\u201c\u5979\u82e5\u4e0d\u80af\u8ba9\u4f60\u5230\u795e\u6c34\u5bab\u53bb\uff0c\u5979\u5c31\u53c8\u804b\u53c8\u54d1\uff0c\u4f60\u65e0\u8bba\u600e\u9ebd\u6c42\u5979\uff0c\u5979\u90fd\u542c\u4e0d \u89c1\uff0c\u4f46\u5979\u82e5\u80af\u8ba9\u4f60\u53bb\uff0c\u4f60\u8bf4\u7684\u6bcf\u4e00\u4e2a\u5b57\uff0c\u5979\u90fd\u542c\u5f97\u6e05\u6e05\u695a\u695a\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\u2236\u201c\u8fd9\u6cd5\u5b50\u5012\u771f\u4e0d\u9519\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u9053\u2236\u201c\u6211\u5bf9\u5979\u8bf4\u51fa\u6211\u60f3\u5230\u795e\u6c34\u5bab\u53bb\u7684\u7406\u7531\u4e4b\u5f8c\uff0c\u5979\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u5ffd\u7136\u5012\u4e86\u676f\u8336\uff0c \u8981\u6211\u559d\u4e0b\u53bb\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\u2236\u201c\u4f60\u559d\u4e0b\u53bb\u4e86\u9ebd\uff1f\u201d \u3000\u3000\u67f3\u65e0\u7709\u53f9\u9053\u2236\u201c\u6211\u600e\u9ebd\u80fd\u4e0d\u559d\u5462\uff1f\u201d \u3000\u3000\u5979\u82e6\u7b11\u7740\u63a5\u9053\u2236\u201c\u6211\u81ea\u7136\u4e5f\u77e5\u9053\u8fd9\u676f\u8336\u4e0d\u662f\u597d\u559d\u7684\uff0c\u559d\u4e0b\u53bb\u4e4b\u5f8c\uff0c\u6211\u679c\u7136\u7acb\u523b\u660f\u8ff7\u4e86\u8fc7 \u53bb\uff0c\u7b49\u6211\u9192\u6765\u7684\u65f6\u5019\uff0c\u6211\u53d1\u73b0\u7adf\u5df2\u88ab\u5173\u5728\u4e00\u53ea\u85e4\u7bb1\u4e8e\u91cc\uff0c\u7bb1\u5b50\u6c34\u6dcb\u6dcb\u7684\uff0c\u50cf\u662f\u5728\u6c34\u91cc\u6ce1\u8fc7\uff0c \u6211\u8eab\u4e0a\u4e5f\u5168\u90fd\u6e7f\u900f\u4e86\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u4e00\u76f4\u5931\u9b42\u843d\u9b44\u7684\u672c\u7acb\u5728\u90a3\u91cc\uff0c\u6b64\u523b\u624d\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u671b\u7740\u4ed6\u7684\u59bb\u5b50\uff0c\u76ee\u4e2d\u6ee1\u662f\u60cb \u60dc\u4e4b\u610f\u3002 \u3000\u3000\u67f3\u65e0\u7709\u9053\u2236\u201c\u5e78\u597d\u8fd9\u7bb1\u5b50\u662f\u7528\u85e4\u6761\u7f16\u7684\uff0c\u800c\u4e14\u5916\u9762\u6ca1\u6709\u4e0a\u9501\uff0c\u65bc\u662f\u6211\u5c31\u4ece\u7bb1\u5b50\u91cc\u722c\u4e86\u51fa \u6765\uff0c\u624d\u53d1\u73b0\u90a3\u91cc\u662f\u6761\u5f88\u9634\u6e7f\u7684\u5730\u9053\uff0c\u8fde\u4e00\u70b9\u5149\u4e5f\u6ca1\u6709\uff0c\u53ea\u6709\u4e00\u9635\u9635\u6d41\u6c34\u7684\u58f0\u97f3\u54cd\u4e2a\u4e0d\u505c\uff0c\u53ef \u662f\u6211\u4e5f\u8fa8\u4e0d\u51fa\u6c34\u58f0\u662f\u4ece\u90a3\u91cc\u4f20\u6765\u7684\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\u2236\u201c\u795e\u6c34\u5bab\u5fc5\u5b9a\u6709\u5904\u6c34\u6e90\uff0c\u81f3\u5c11\u8fd9\u4e00\u70b9\u662f\u6ca1\u6709\u7591\u95ee\u7684\u4e86\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u77aa\u773c\u9053\u2236\u201c\u795e\u6c34\u5bab\u6ca1\u6709\u6c34\uff0c\u96be\u9053\u8fd8\u6709\u9152\u5417\uff1f\u201d \u3000\u3000\u67f3\u65e0\u7709\u9053\u2236\u201c\u6211\u4ec0\u9ebd\u4e5f\u77a7\u4e0d\u89c1\uff0c\u53ea\u6709\u6478\u7740\u5f80\u524d\u8d70\uff0c\u65e2\u4e0d\u77e5\u8fd9\u6761\u5730\u9053\u7a76\u7adf\u6709\u591a\u957f\uff0c\u4e5f\u4e0d\u77e5 \u9053\u8fd9\u5730\u9053\u662f\u901a\u5411\u90a3\u91cc\u7684\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\u2236\u201c\u4f46\u4f60\u81f3\u5c11\u53ef\u4ee5\u786e\u5b9a\uff0c\u8fd9\u6761\u5730\u9053\u91cc\u7edd\u4e0d\u4f1a\u6709\u4eba\u6765\u6697\u7b97\u4f60\uff0c\u56e0\u4e3a\u201c\u6c34\u6bcd\u9634\u59ec\u201d \u81f3\u5c11\u4e0d\u4f1a\u662f\u4e2a\u6697\u7b97\u522b\u4eba\u7684\u4eba\u3002\u201d \u3000\u3000\u4ed6\u8fd9\u53e5\u8bdd\u672c\u662f\u597d\u610f\uff0c\u8c01\u77e5\u5374\u523a\u7740\u67f3\u65e0\u7709\u7684\u9690\u75db\uff0c\u5979\u82cd\u767d\u7684\u8138\u4e5f\u4e0d\u7981\u7ea2\u4e86\uff0c\u5782\u4e0b\u5934\u9053\u2236 \u201c\u90a3\u65f6\u6211\u773c\u775b\u548c\u8033\u6735\u867d\u7136\u90fd\u6ca1\u6709\u7528\u4e86\uff0c\u4f46\u9f3b\u5b50\u5374\u8fd8\u6709\u7528\uff0c\u56e0\u4e3a\u90a3\u5730\u9053\u4e2d\u7adf\u6709\u5404\u5f0f\u5404\u6837\u4e0d\u540c\u7684 \u6c14\u5473\u3002\u201d \u3000\u3000\u5b8b\u751c\u5144\u9053\u2236 \u3000\u3000\u201c\u4ec0\u2026\u2026\u4ec0\u9ebd\u6c14\u5473\uff1f\u201d \u3000\u3000\u67f3\u65e0\u7709\u9053\u2236\u201c\u8d77\u5148\u662f\u4e00\u9635\u9635\u6f6e\u6e7f\u7684\u6c14\u5473\uff0c\u6309\u7740\u53c8\u6709\u4e00\u9635\u9635\u706b\u70e7\u7684\u6c14\u5473\uff0c\u50cf\u662f\u6709\u4e1c\u897f\u88ab\u70e7 \u7126\u4e86\uff0c\u5f8c\u6765\u53c8\u6709\u8840\u8165\u6c14\u3001\u94c1\u9508\u6c14\u3001\u6ce5\u571f\u6c14\u3001\u6728\u5934\u6c14\u2026\u2026\u201d \u3000\u3000\u5979\u9762\u4e0a\u7adf\u9732\u51fa\u4e86\u6050\u60e7\u4e4b\u8272\uff0c\u55c4\u58f0\u9053\u2236\u201c\u5728\u90a3\u5730\u9053\u4e2d\uff0c\u867d\u7136\u6ca1\u6709\u4efb\u4f55\u4eba\u6765\u6697\u7b97\u8fc7\u6211\uff0c\u4e5f\u6ca1 \u6709\u4efb\u4f55\u9677\u9631\uff0c\u4f46\u5c31\u53ea\u8fd9\u9ebd\u4e0d\u540c\u7684\u6c14\u5473\uff0c\u5df2\u903c\u5f97\u6211\u5feb\u53d1\u75af\u4e86\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u8fd8\u662f\u5fcd\u4e0d\u4f4f\u95ee\u9053\u2236\u201c\u8fd9\u4e9b\u6c14\u5473\u53c8\u4e0d\u80fd\u4f24\u4eba\uff0c\u6709\u4ec0\u9ebd\u53ef\u6015\u7684\u5462\uff1f\u201d \u3000\u3000\u67f3\u65e0\u7709\u53f9\u9053\u2236\u201c\u6211\u672c\u6765\u4e5f\u60f3\u4e0d\u5230\u6c14\u5473\u4f1a\u6709\u4ec0\u9ebd\u53ef\u6015\u7684\uff0c\u4f46\u5230\u4e86\u90a3\u65f6\uff0c\u6211\u624d\u77e5\u9053\u4e16\u4e0a\u6ca1\u6709 \u4efb\u4f55\u4e8b\u6bd4\u8fd9\u4e9b\u6c14\u5473\u66f4\u53ef\u6015\u7684\u4e86\u3002\u201d \u3000\u3000\u5979\u8fde\u58f0\u97f3\u90fd\u5df2\u6709\u4e9b\u5636\u54d1\uff0c\u98a4\u58f0\u9053\u2236\u201c\u6211\u95fb\u5230\u706b\u70e7\u6c14\u7684\u65f6\u5019\uff0c\u5f00\u59cb\u8fd8\u4e0d\u89c9\u5f97\u600e\u6837\uff0c\u5f8c\u6765\u53ea \u89c9\u5f97\u6211\u5f77\u4f5b\u662f\u572d\u5728\u4e00\u4e2a\u5f88\u5927\u7684\u706b\u7089\u91cc\uff0c\u5728\u88ab\u4eba\u711a\u70e7\u7740\u3002\u201d \u3000\u3000\u5b8b\u751c\u513f\u7f29\u4e86\u7f29\u80a9\u8180\uff0c\u4eba\u9760\u5230\u674e\u7ea2\u8896\u8eab\u4e0a\u53bb\u3002 \u3000\u3000\u67f3\u65e0\u7709\u9053\u2236\u201c\u6211\u95fb\u5230\u8840\u8165\u6c14\u548c\u94c1\u9508\u6c14\u7684\u65f6\u5019\uff0c\u53ea\u89c9\u56db\u9762\u90fd\u662f\u6b7b\u5c38\uff0c\u597d\u50cf\u6709\u6210\u5343\u4e0a\u4e07\u4e2a\u6b7b \u5c38\uff0c\u8eb2\u5728\u9ed1\u6697\u4e2d\uff0c\u6211\u8fd0\u8def\u90fd\u4e0d\u6562\u8d70\u4e86\uff0c\u53ea\u56e0\u6211\u89c9\u5f97\u518d\u8d70\u4e00\u6b65\uff0c\u8bf4\u4e0d\u5b9a\u5c31\u4f1a\u8e29\u5728\u4e00\u5177\u6b7b\u5c38\u4e0a\uff0c \u800c\u4e14\u8bf4\u4e0d\u5b9a\u5c31\u662f\u6211\u670b\u53cb\u7684\u6b7b\u5c38\u3002\u201d \u3000\u3000\u674e\u7ea2\u8896\u7684\u8eab\u5b50\u4e5f\u6709\u4e9b\u53d1\u51b7\u4e86\uff0c\u53ea\u5f80\u82cf\u84c9\u84c9\u8eab\u4e0a\u9760\u3002 \u3000\u3000\u67f3\u65e0\u7709\u9053\u2236\u201c\u7b49\u6211\u95fb\u5230\u6ce5\u571f\u6c14\u548c\u6728\u5934\u6c14\u7684\u65f6\u5019\uff0c\u6211\u81ea\u5df1\u50cf\u662f\u4e5f\u5df2\u53d8\u6210\u4e86\u4e00\u5177\u6b7b\u5c38\uff0c\u5df2\u88ab \u653e\u5728\u68fa\u6750\u91cc\uff0c\u57cb\u5728\u5730\u4e0b\u3002\u201d \u3000\u3000\u5979\u957f\u53f9\u7740\u63a5\u9053\u2236\u201c\u6211\u672c\u6765\u4ee5\u4e3a\u4e00\u4e2a\u4eba\u53ea\u4f1a\u4e3a\u4e86\u773c\u775b\u89c1\u5230\u7684\u4e8b\u800c\u5bb3\u6015\uff0c\u4e3a\u4e86\u8033\u6735\u542c\u5230\u7684\u58f0 \u97f3\u800c\u5bb3\u6015\uff0c\u5230\u4e86\u90a3\u65f6\uff0c\u6211\u624d\u77e5\u9053\u9f3b\u5b50\u55c5\u5230\u7684\u6c14\u5473\uff0c\u624d\u662f\u6700\u4ee4\u4eba\u5bb3\u6015\u7684\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u53f9\u9053\u2236\u201c\u8fd9\u53ea\u6015\u662f\u56e0\u4e3a\u773c\u775b\u6240\u89c1\u7684\uff0c\u548c\u8033\u6735\u6240\u542c\u7684\u90fd\u6bd4\u8f83\u5b9e\u5728\u4e9b\uff0c\u800c\u9f3b\u5b50\u6240\u55c5\u5230 \u7684\uff0c\u5374\u865a\u65e0\u7f25\u7f08\uff0c\u4e0d\u53ef\u6349\u6478\uff0c\u4f60\u53ea\u6709\u7528\u5e7b\u60f3\u53bb\u731c\u6d4b\uff0c\u8d8a\u60f3\u5c31\u8d8a\u53ef\u6015\u3002\u201d \u3000\u3000\u201c\u2014\u2014\u6211\u65e9\u5df2\u8bf4\u8fc7\uff0c\u4eba\u4eec\u6240\u754f\u60e7\u7684\uff0c\u5e76\u4e0d\u662f\u4e8b\u7269\u7684\u672c\u8eab\uff0c\u800c\u662f\u4ed6\u5bf9\u8fd9\u4ef6\u4e8b\u7269\u751f\u51fa\u6765\u7684\u60f3 \u50cf\u3002\u201d\u3002 \u3000\u3000\u67f3\u65e0\u7709\u9053\u2236\u201c\u6240\u4ee5\u5728\u90a3\u5730\u9053\u4e2d\uff0c\u6211\u867d\u7136\u4ec0\u9ebd\u4e5f\u6ca1\u6709\u77a7\u89c1\uff0c\u4ec0\u9ebd\u4e5f\u6ca1\u6709\u542c\u89c1\uff0c\u5c31\u5df2\u88ab\u6298\u78e8 \u5f97\u8fde\u4e00\u4e1d\u529b\u6c14\u90fd\u6ca1\u6709\uff0c\u8fd0\u8d70\u90fd\u8d70\u4e0d\u52a8\u4e86\u3002\u201d \u3000\u3000\u5b8b\u751c\u513f\u6574\u4e2a\u4eba\u90fd\u7f29\u5728\u674e\u7ea2\u8896\u6000\u91cc\uff0c\u5374\u8fd8\u662f\u8981\u95ee\u9053\u2236\u201c\u5f8c\u2026\u2026\u5f8c\u6765\u5462\uff1f\u201d \u3000\u3000\u5973\u5b69\u5b50\u5927\u591a\u6709\u79cd\u6bdb\u75c5\uff0c\u8d8a\u662f\u5bb3\u6015\u7684\u4e8b\uff0c\u8d8a\u662f\u8981\u542c\u3002 \u3000\u3000\u67f3\u65e0\u7709\u9053\u2236\u201c\u5c31\u5728\u90a3\u65f6\uff0c\u5730\u9053\u4e2d\u5ffd\u7136\u54cd\u8d77\u4e86\u4e00\u4e2a\u4eba\u7684\u58f0\u97f3\uff0c\u90a3\u58f0\u97f3\u542c\u6765\u867d\u7136\u5f88\u67d4\u7f8e\uff0c\u4f46 \u6211\u90a3\u65f6\u5374\u53ea\u89c9\u5979\u9634\u51c4\u51c4\u7684\uff0c\u7adf\u4e0d\u50cf\u662f\u4eba\u7684\u58f0\u97f3\u3002\u201d \u3000\u3000\u5b8b\u751c\u5144\u9053\u2236\u201c\u5979\u2026\u2026\u5979\u2026\u2026\u5979\u8bf4\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u67f3\u65e0\u7709\u9053\u2236\u201c\u5979\u8bf4\uff0c\u5979\u5df2\u770b\u8fc7\u6211\u7684\u75c5\u52bf\uff0c\u4e5f\u77e5\u9053\u6211\u4e2d\u7684\u662f\u4ec0\u9ebd\u6bd2\u4e86\uff0c\u4f46\u6211\u82e5\u60f3\u5979\u51fa\u624b\u6765 \u6551\u6211\uff0c\u5c31\u8981\u2026\u2026\u5c31\u8981\u2026\u2026\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u4e86\u7b11\uff0c\u9053\u2236\u201c\u5c31\u8981\u5c06\u6211\u7684\u5934\u62ff\u53bb\u7ed9\u5979\uff0c\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u67f3\u65e0\u7709\u5782\u4e0b\u5934\uff0c\u9053\u2236\u201c\u6211\u867d\u7136\u518d\u53c1\u54c0\u6c42\u5979\uff0c\u95ee\u5979\u8fd8\u6709\u6ca1\u6709\u522b\u7684\u8def\u53ef\u8d70\uff0c\u4f46\u5979\u5374\u518d\u4e5f\u4e0d\u7406 \u6211\u4e86\uff0c\u6211\u8bf4\u5f97\u58f0\u97f3\u90fd\u5df2\u5636\u54d1\uff0c\u5979\u5374\u50cf\u662f\u6839\u672c\u4e00\u4e2a\u5b57\u90fd\u6ca1\u6709\u542c\u5230\u3002\u201d \u7b2c\u4e8c\u5341\u7ae0\u3000\u524d\u8f88\u98ce\u8303>> \u53e4\u9f99\u300a\u695a\u7559\u9999\u7cfb\u5217\xb7\u753b\u7709\u9e1f\u300b \u7b2c\u4e8c\u5341\u7ae0\u3000\u524d\u8f88\u98ce\u8303 \u3000\u3000\u695a\u7559\u9999\u6c89\u541f\u7740\u9053\u2236\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u4f60\u4e5f\u5e76\u6ca1\u6709\u89c1\u5230\u201c\u6c34\u6bcd\u9634\u59ec\u201d\u4e86\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u53f9\u9053\u2236\u201c\u6211\u975e\u4f46\u6ca1\u6709\u89c1\u5230\u5979\uff0c\u8fde\u5979\u7684\u95e8\u4e0b\u90fd\u6ca1\u6709\u89c1\u5230\u4e00\u4e2a\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\u2236\u201c\u4f60\u662f\u600e\u9ebd\u6837\u56de\u6765\u7684\uff1f\u201d \u3000\u3000\u67f3\u65e0\u7709\u9053\u2236\u201c\u6211\u4e5f\u4e0d\u77e5\u54c0\u6c42\u4e86\u591a\u4e45\uff0c\u9f3b\u5b50\u91cc\u5ffd\u53c8\u55c5\u5230\u53e6\u4e00\u79cd\u6c14\u5473\uff0c\u8fd9\u6b21\u6211\u55c5\u5230\u7684\u7adf\u662f\u9999 \u6c14\uff0c\u5f77\u4f5b\u662f\u665a\u4e0a\u4ece\u7a97\u5916\u5439\u8fdb\u6765\u7684\u6625\u98ce\uff0c\u53c8\u5f77\u4f5b\u662f\u6bcd\u4eb2\u6000\u4e2d\u7684\u4e73\u9999\uff0c\u6211\u55c5\u5230\u8fd9\u9999\u6c14\uff0c\u5c31\u4e0d\u77e5\u4e0d \u89c9\u7684\u7761\u7740\u4e86\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\u2236\u201c\u7b49\u4f60\u9192\u6765\u5f8c\uff0c\u4f60\u5df2\u56de\u5230\u90a3\u83e9\u63d0\u5eb5\uff1f\u201d \u3000\u3000\u67f3\u65e0\u7709\u9053\u2236\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u5979\u53f9\u606f\u4e00\u58f0\uff0c\u63a5\u7740\u9053\u2236\u201c\u6211\u9192\u6765\u7684\u65f6\u5019\uff0c\u8eab\u4e0a\u7684\u8863\u670d\u5df2\u4e7e\u4e86\uff0c\u90a3\u8001\u5c3c\u59d1\u6b63\u5750\u5728\u6211\u5bf9\u9762\uff0c \u624b\u91cc\u8fd8\u62ff\u7740\u6211\u521a\u559d\u8fc7\u7684\u90a3\u53ea\u8336\u7897\uff0c\u5c31\u50cf\u4ec0\u9ebd\u4e8b\u90fd\u6ca1\u6709\u53d1\u751f\u8fc7\uff0c\u6211\u518d\u95ee\u4ed6\uff0c\u518d\u6c42\u5979\uff0c\u5979\u5c31\u8fde\u4e00 \u4e2a\u5b57\u90fd\u542c\u4e0d\u89c1\u4e86\u3002\u201d \u3000\u3000\u5b8b\u751c\u513f\u53ea\u89c9\u624b\u811a\u51b0\u51c9\uff0c\u98a4\u58f0\u9053\u2236\u201c\u4f60\u2026\u2026\u4f60\u5c31\u597d\u50cf\u505a\u4e86\u4e2a\u68a6\uff1f\u201d \u3000\u3000\u67f3\u65e0\u7709\u9eef\u7136\u9053\u2236\u201c\u4e0d\u9519\uff0c\u6709\u65f6\u8fde\u6211\u81ea\u5df1\u90fd\u5206\u4e0d\u51fa\uff0c\u90a3\u7a76\u7adf\u662f\u771f\uff1f\u8fd8\u662f\u68a6\uff1f\u201d \u3000\u3000\u674e\u7ea2\u8896\u4e5f\u957f\u53f9\u4e86\u4e00\u58f0\uff0c\u82e6\u7b11\u9053\u2236\u201c\u542c\u4f60\u8fd9\u9ebd\u4e00\u58f0\uff0c\u6211\u53c8\u4e0d\u60f3\u5230\u90a3\u795e\u6c34\u5bab\u53bb\u4e86\u3002\u201d \u3000\u3000\u5b8b\u751c\u513f\u671b\u7740\u82cf\u84c9\u84c9\uff0c\u9053\u2236\u201c\u795e\u6c34\u5bab\u2026\u2026\u795e\u6c34\u5bab\uff1f\u90a3\u7a76\u7adf\u662f\u4e2a\u600e\u9ebd\u6837\u7684\u5730\u65b9\u5462\uff1f\u201d \u3000\u3000\u5979\u8fd9\u8bdd\u867d\u662f\u95ee\u82cf\u84c9\u84c9\u7684\uff0c\u4f46\u4e0d\u5e0c\u671b\u82cf\u84c9\u84c9\u7b54\u590d\u3002 \u3000\u3000\u56e0\u4e3a\u5979\u77e5\u9053\u82cf\u84c9\u84c9\u4e5f\u4e00\u5b9a\u56de\u7b54\u4e0d\u51fa\u3002 \u3000\u3000\u5927\u5bb6\u53c8\u6c89\u9ed8\u4e86\u4e0b\u6765\uff0c\u5fc3\u91cc\u90fd\u6709\u4e2a\u95ee\u9898\u3002 \u3000\u3000\u795e\u6c34\u5bab\u771f\u662f\u50cf\u82cf\u84c9\u84c9\u6240\u8bf4\u7684\u90a3\u6837\uff0c\u662f\u4e2a\u4e16\u5916\u6843\u6e90\uff0c\u4eba\u95f4\u4ed9\u5883\u5462\uff1f\u8fd8\u662f\u50cf\u67f3\u65e0\u7709\u6240\u8bf4\u7684\u90a3 \u6837\uff0c\u662f\u4e2a\u5145\u6ee1\u4e86\u795e\u79d8\u548c\u6050\u6016\u7684\u4eba\u95f4\u5730\u72f1\uff1f \u3000\u3000\u80e1\u94c1\u82b1\u53c8\u5728\u6478\u9f3b\u5b50\u4e86\uff0c\u5583\u5583\u9053\u2236\u201c\u4e5f\u8bb8\u4f60\u4eec\u4e24\u4eba\u53bb\u7684\u4e0d\u662f\u540c\u4e00\u4e2a\u5730\u65b9\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u9053\u2236\u201c\u5929\u4e0b\u53ea\u6709\u4e00\u4e2a\u795e\u6c34\u5bab\u2236\u7edd\u6ca1\u6709\u7b2c\u4e8c\u4e2a\u3002\u201d \u3000\u3000\u82cf\u84c9\u84c9\u9053\u2236\u201c\u6211\u53bb\u7684\u90a3\u5730\u65b9\u5c31\u662f\u795e\u6c34\u5bab\uff0c\u7edd\u4e0d\u4f1a\u9519\u3002\u201d \u3000\u3000\u5979\u4eec\u7684\u8bed\u6c14\u90fd\u662f\u540c\u6837\u80af\u5b9a\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\u2236\u201c\u82e5\u6362\u4e86\u522b\u4eba\u8bf4\u7edd\u4e0d\u4f1a\u5f04\u9519\uff0c\u6211\u4e5f\u8bb8\u8fd8\u4e0d\u76f8\u4fe1\uff0c\u4f46\u4f60\u4eec\u4e24\u4f4d\u59d1\u5a18 \u65e2\u7136\u8bf4\u7edd\u4e0d\u4f1a\u5f04\u9519\uff0c\u90a3\u53ea\u6015\u5c31\u2026\u2026\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u987f\u4f4f\u8bed\u58f0\uff0c\u77aa\u7740\u67f3\u65e0\u7709\u9053\u2236\u201c\u4f60\u5230\u4e86\u90a3\u5730\u65b9\uff0c\u8fde\u4e00\u4e2a\u4eba\u90fd\u6ca1\u77a7\u89c1\uff0c\u600e\u77e5\u9053\u90a3\u5730\u65b9 \u5c31\u662f\u795e\u6c34\u5bab\u5462\uff1f\u201d \u3000\u3000\u674e\u7ea2\u8896\u4e5f\u7acb\u523b\u6309\u7740\u9053\u2236\u201c\u662f\u5440\uff1a\u4f60\u600e\u77e5\u83e9\u63d0\u5eb5\u91cc\u90a3\u8001\u5c3c\u59d1\uff0c\u4e00\u5b9a\u4f1a\u5c06\u4f60\u9001\u5230\u795e\u6c34\u5bab \u53bb\uff1f\u201d \u3000\u3000\u82cf\u84c9\u84c9\u773c\u775b\u91cc\u53d1\u51fa\u4e86\u5149\uff0c\u4e5f\u62a2\u7740\u9053\u2236\u201c\u662f\u8c01\u544a\u8bc9\u4f60\uff0c\u8981\u5230\u795e\u6c34\u5bab\u53bb\uff0c\u4e00\u5b9a\u8981\u5148\u7ecf\u8fc7\u90a3\u83e9 \u63d0\u5eb5\u7684\uff1f\u8fd9\u4ef6\u4e8b\u8bf4\u4e0d\u5b9a\u6839\u672c\u5c31\u662f\u90a3\u4eba\u505a\u51fa\u6765\u7684\u5708\u5957\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u9053\u2236\u201c\u5708\u5957\uff1f\u201d \u3000\u3000\u82cf\u84c9\u84c9\u9053\u2236\u201c\u4e0d\u9519\uff0c\u5708\u5957\u3002\u201d \u3000\u3000\u5979\u6309\u7740\u9053\u2236\u201c\u90a3\u4eba\u8bf4\u4e0d\u5b9a\u548c\u4ed6\u2026\u2026\u548c\u695a\u7559\u9999\u6709\u4ec7\uff0c\u6240\u4ee5\u6545\u610f\u8bbe\u51fa\u8fd9\u5708\u5957\u6765\u9a97\u4f60\uff0c\u83e9\u63d0\u5eb5 \u90a3\u8001\u5c3c\u59d1\u81ea\u7136\u4e5f\u662f\u548c\u4ed6\u4e32\u901a\u7684\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u62cd\u624b\u9053\u2236\u201c\u4e00\u70b9\u4e5f\u4e0d\u9519\uff0c\u4ed6\u4eec\u8fd9\u6837\u505a\uff0c\u5c31\u4e3a\u7684\u662f\u8981\u4f60\u6740\u695a\u7559\u9999\uff0c\u5176\u5b9e\u4ed6\u4eec\u6839\u672c\u6ca1 \u6709\u5c06\u4f60\u9001\u5230\u795e\u6c34\u5bab\u53bb\u3002\u201d \u3000\u3000\u674e\u7ea2\u8896\u9053\u2236\u201c\u4f60\u559d\u4e86\u90a3\u676f\u8336\u5f8c\uff0c\u5df2\u7ecf\u8ff7\u8ff7\u7cca\u7cca\u7684\u4e86\uff0c\u5979\u4eec\u968f\u4fbf\u5c06\u4f60\u9001\u5230\u90a3\u91cc\u53bb\uff0c\u4f60\u53cd\u6b63 \u90fd\u4e0d\u4f1a\u77e5\u9053\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u6c89\u541f\u7740\uff0c\u7f13\u7f13\u9053\u2236\u201c\u59d1\u5a18\u4eec\u8bf4\u7684\u8bdd\uff0c\u4e5f\u672a\u5c1d\u6ca1\u6709\u9053\u7406\u3002\u201d \u3000\u3000\u674e\u7ea2\u8896\u9053\u2236\u201c\u8fd9\u81ea\u7136\u5f88\u6709\u9053\u7406\uff0c\u4f60\u53bb\u90a3\u5730\u6d1e\uff0c\u8bf4\u4e0d\u5b9a\u5c31\u5728\u83e9\u63d0\u5eb5\u7684\u4e0b\u9762\uff0c\u4f60\u542c\u5230\u7684\u90a3\u58f0 \u97f3\uff0c\u8bf4\u4e0d\u5b9a\u5c31\u662f\u90a3\u8001\u5c3c\u59d1\u5728\u8bf4\u8bdd\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\u2236\u201c\u53ef\u662f\uff0c\u8fd9\u4ef6\u4e8b\u65e2\u7136\u6709\u5173\u6211\u7684\u751f\u6b7b\uff0c\u6211\u53c8\u600e\u9ebd\u4f1a\u968f\u4fbf\u542c\u4fe1\u522b\u4eba\u7684 \u8bdd\u5462\uff1f\u6307\u70b9\u6211\u8fd9\u6761\u8def\u5f84\u7684\u4eba\uff0c\u6211\u81ea\u7136\u5f88\u80fd\u4fe1\u4efb\u4ed6\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u563f\u563f\u7b11\u9053\u2236\u201c\u592a\u4fe1\u4efb\u522b\u4eba\u7684\u4eba\uff0c\u90fd\u8981\u5012\u6963\u7684\uff0c\u8fd9\u9053\u7406\u4f60\u5e94\u8be5\u6bd4\u522b\u4eba\u90fd\u660e\u767d\u624d \u662f\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u7ea2\u7740\u8138\u5782\u4e0b\u4e86\u5934\uff0c\u9053\u2236\u201c\u53ef\u662f\u8fd9\u4e2a\u4eba\u2026\u2026\u8fd9\u4e2a\u4eba\u7edd\u4e0d\u4f1a\u8bf4\u5047\u8bdd\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\u2236\u201c\u54e6\uff1f\u6211\u5012\u5df2\u6709\u5f88\u4e45\u672a\u66fe\u89c1\u5230\u4e0d\u8bf4\u8c0e\u7684\u4eba\u4e86\uff0c\u6211\u5012\u60f3\u77a7\u77a7\u8fd9\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u67f3\u65e0\u7709\u9053\u2236\u201c\u4ed6\u8001\u4eba\u5bb6\u4fbf\u662f\u6b66\u6797\u4e2d\u4eba\u79f0\u201c\u541b\u5b50\u5251\u201d\u7684\u9ec4\u9c81\u76f4\u9ec4\u8001\u5251\u5ba2\uff0c\u6211\u60f3\u5404\u4f4d\u591a\u591a\u5c11 \u5c11\u603b\u8be5\u542c\u8bf4\u8fc7\u4e00\u4e9b\u4ed6\u8001\u4eba\u5bb6\u7684\u4e8b\u8ff9\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u7acb\u523b\u8bf4\u4e0d\u51fa\u8bdd\u4e86\uff0c\u53ea\u56e0\u4e3a\u4ed6\u4e5f\u77e5\u9053\uff0c\u5929\u4e0b\u82e5\u6709\u4e00\u4e2a\u4e0d\u8bf4\u8c0e\u7684\u4eba\uff0c\u90a3\u4eba\u5fc5\u5b9a\u5c31\u662f\u8fd9 \u4f4d\u201c\u541b\u5b50\u5251\u201d\u9ec4\u9c81\u76f4\u3002 \u3000\u3000\u674e\u7ea2\u8896\u5fcd\u4e0d\u4f4f\u9053\u2236\u201c\u5979\u8bf4\u7684\u4e0d\u9519\uff0c\u8fd9\u4f4d\u9ec4\u8001\u5251\u5ba2\u5012\u7684\u786e\u4e0d\u6127\u4e3a\u8bda\u5b9e\u7684\u541b\u5b50\uff0c\u751f\u5e73\u4ece\u6765\u4e5f \u6ca1\u6709\u8bf4\u8fc7\u4e00\u53e5\u8c0e\u8bdd\uff0c\u6700\u96be\u5f97\u7684\u662f\uff0c\u4ed6\u4e0d\u4f46\u5bf9\u670b\u53cb\u4ee5\u8bda\u76f8\u5f85\uff0c\u5c31\u7b97\u5bf9\u4ed6\u7684\u4ec7\u654c\uff0c\u4e5f\u4e00\u5411\u662f\u5b9e\u8bdd \u5b9e\u8bf4\uff0c\u4ece\u6765\u4e0d\u80af\u8bf4\u8c0e\u7684\u3002\u201d \u3000\u3000\u5b8b\u751c\u513f\u62cd\u624b\u7b11\u9053\u2236\u201c\u6211\u4eec\u7684\u674e\u59d1\u5a18\u53c8\u60f3\u5c06\u5979\u809a\u5b50\u91cc\u7684\u5b66\u95ee\u5356\u5f04\u5356\u5f04\u4e86\uff0c\u5979\u5012\u7684\u786e\u88c5\u4e86\u4e00 \u809a\u5b50\u7684\u638c\u6545\uff0c\u8bf4\u8d77\u6765\u771f\u80fd\u4ee4\u4eba\u542c\u51fa\u8033\u6cb9\u3002\u201d \u3000\u3000\u5979\u8bf4\u7684\u867d\u662f\u201c\u5b98\u8bdd\u201d\uff0c\u4f46\u5374\u534a\u751f\u4e0d\u719f\uff0c\u7b80\u76f4\u6bd4\u5e7f\u4e1c\u8bdd\u8fd8\u96be\u61c2\uff0c\u67f3\u65e0\u7709\u4e5f\u542c\u4e0d\u61c2\u5979\u5728\u8bf4\u4ec0 \u9ebd\uff0c\u53ea\u4e0d\u8fc7\u5fc3\u91cc\u6709\u4e9b\u5947\u602a\u2236\u201c\u8fd9\u4f4d\u674e\u59d1\u5a18\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u201c\u541b\u5b50\u5251\u201d\u95ef\u8361\u6c5f\u6e56\u7684\u65f6\u5019\uff0c\u5979\u53ea\u6015\u8fd8 \u672a\u51fa\u4e16\u54e9\uff0c\u4f46\u542c\u5979\u7684\u53e3\u6c14\uff0c\u5bf9\u201c\u541b\u5b50\u5251\u201d\u7684\u5f80\u4e8b\u5979\u5374\u50cf\u77e5\u9053\u5f97\u5f88\u591a\u3002\u201d \u3000\u3000\u5374\u4e0d\u77e5\u674e\u7ea2\u8896\u975e\u4f46\u5bf9\u201c\u541b\u5b50\u5251\u201d\u7684\u5f80\u4e8b\u77e5\u9053\u5f97\u4e0d\u5c11\uff0c\u6c5f\u6e56\u4e2d\u6210\u540d\u4eba\u7269\u7684\u4e8b\u8ff9\uff0c\u5979\u4e5f\u5f88\u5c11 \u6709\u4e0d\u77e5\u9053\u7684\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u4e5f\u5fcd\u4e0d\u4f4f\u95ee\u9053\u2236\u201c\u4f60\u8bf4\u9ec4\u8001\u5251\u5ba2\u5bf9\u4ec7\u654c\u4e5f\u4e0d\u80af\u8bf4\u8c0e\uff0c\u8fd9\u6211\u5012\u6709\u4e9b\u4e0d\u61c2\u4e86\u3002\u201d \u3000\u3000\u674e\u7ea2\u8896\u9053\u2236\u201c\u4f60\u548c\u4eba\u52a8\u624b\u65f6\uff0c\u5bf9\u65b9\u82e5\u95ee\u2236\u201c\u4f60\u6700\u62ff\u624b\u7684\u662f\u4ec0\u9ebd\u529f\u592b\uff1f\u6700\u5389\u5bb3\u7684\u662f\u90a3\u51e0 \u62db\uff1f\u51fa\u624b\u65f6\u51c6\u5907\u7528\u4ec0\u9ebd\u62db\u5f0f\uff1f\u201d\u4f60\u80af\u4e0d\u80af\u544a\u8bc9\u4ed6\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u5927\u7b11\u9053\u2236\u201c\u5175\u4e0d\u538c\u8bc8\uff0c\u548c\u4eba\u4ea4\u624b\u65f6\uff0c\u8bb2\u7a76\u7684\u5c31\u662f\u865a\u865a\u5b9e\u5b9e\uff0c\u624d\u80fd\u4ee4\u5bf9\u65b9\u65e0\u6cd5\u62db \u67b6\uff0c\u81ea\u5df1\u82e5\u5148\u5c06\u81ea\u5df1\u7684\u5e95\u7ec6\u90fd\u6296\u9732\u51fa\u6765\uff0c\u8fd8\u548c\u4eba\u6253\u4ec0\u9ebd\u67b6\u3002\u201d \u3000\u3000\u674e\u7ea2\u8896\u9053\u2236\u201c\u522b\u4eba\u82e5\u95ee\u4f60\u8fd9\u4e9b\u8bdd\uff0c\u4f60\u7edd\u4e0d\u80af\u544a\u8bc9\u4ed6\u5427\uff01\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\u2236\u201c\u90a3\u4eba\u82e5\u662f\u6211\u7684\u5bf9\u5934\uff0c\u6211\u81ea\u7136\u4e0d\u80af\u544a\u8bc9\u4ed6\uff0c\u53ef\u662f\u6211\u7684\u5bf9\u5934\u4e5f\u7edd\u4e0d\u95ee\u6211\u8fd9\u4e9b \u8bdd\uff0c\u56e0\u4e3a\u4ed6\u77e5\u9053\u6211\u6ca1\u6709\u53d1\u75af\uff0c\u6211\u5c31\u7b97\u8bf4\u4e86\uff0c\u4e5f\u7edd\u4e0d\u4f1a\u662f\u771f\u7684\u3002\u201d \u3000\u3000\u674e\u7ea2\u8896\u7b11\u4e86\u7b11\uff0c\u9053\u2236\u201c\u6211\u4e5f\u77e5\u9053\u4f60\u7edd\u4e0d\u80af\u8bf4\u7684\uff0c\u5c31\u7b97\u8bf4\u4e86\uff0c\u5bf9\u65b9\u65e2\u4e0d\u4f1a\u76f8\u4fe1\uff0c\u4e5f\u4e0d\u6562\u76f8 \u4fe1\uff0c\u53ef\u662f\u9ec4\u8001\u5251\u5ba2\u548c\u4eba\u52a8\u624b\u65f6\uff0c\u522b\u4eba\u65e0\u8bba\u95ee\u4ed6\u4ec0\u9ebd\uff0c\u4ed6\u6709\u4e00\u53e5\u5c31\u8bf4\u4e00\u53e5\uff0c\u800c\u4e14\u8bf4\u51fa\u6765\u7edd\u4e0d\u66f4 \u6539\uff0c\u4ed6\u82e5\u8bf4\u6700\u5f8c\u662f\u51c6\u5907\u4ee5\u4e00\u62db\u201c\u98de\u9e1f\u6295\u6797\u201d\u53bb\u524a\u5bf9\u65b9\u7684\u5934\u5dfe\uff0c\u5c31\u7edd\u4e0d\u4f1a\u7528\u4e00\u62db\u201c\u7389\u5973\u7a7f\u68ad\u201d \u53bb\u523a\u522b\u4eba\u7684\u80f8\u819b\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u6014\u4e86\u6014\uff0c\u9053\u2236\u201c\u8fd9\u6837\u548c\u4eba\u4ea4\u624b\uff0c\u5c82\u975e\u5fc5\u5b9a\u8981\u5403\u5927\u4e8f\u9ebd\uff1f\u201d \u3000\u3000\u674e\u7ea2\u8896\u9053\u2236\u201c\u4e0d\u9519\uff0c\u9ec4\u8001\u5251\u5ba2\u5c31\u56e0\u4e3a\u8fd9\u7f18\u6545\uff0c\u5e73\u751f\u4e5f\u4e0d\u77e5\u5403\u8fc7\u591a\u5c11\u6b21\u4e8f\u4e86\uff0c\u53ea\u56e0\u522b\u4eba\u77e5 \u9053\u4ed6\u8fd9\u813e\u6c14\u5f8c\uff0c\u8981\u548c\u4ed6\u4ea4\u624b\u65f6\uff0c\u5c31\u4e00\u5b9a\u8981\u5148\u95ee\u6e05\u695a\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\u2236\u201c\u9ec4\u8001\u5251\u5ba2\u56fa\u7136\u662f\u529f\u529b\u6df1\u539a\uff0c\u522b\u4eba\u5c31\u7b97\u77e5\u9053\u4ed6\u8981\u7528\u4ec0\u9ebd\u62db\u5f0f\uff0c\u4e5f\u65e0\u6cd5\u62db\u67b6\u62b5 \u6321\uff0c\u4f46\u82e5\u9047\u5230\u548c\u4ed6\u529f\u529b\u76f8\u82e5\u7684\u4eba\uff0c\u5c82\u975e\u7b49\u65bc\u5df2\u4e0d\u6218\u800c\u8d25\uff1f\u201d \u3000\u3000\u674e\u7ea2\u8896\u53f9\u9053\u2236\u201c\u6b63\u662f\u5982\u6b64\uff0c\u6240\u4ee5\u6709\u51e0\u6b21\u6218\u5f79\uff0c\u9ec4\u8001\u5251\u5ba2\u660e\u660e\u5e94\u8be5\u80dc\u7684\uff0c\u5374\u53cd\u800c\u8d25\u4e86\u3002\uff0c \u4f46\u4e5f\u5c31\u56e0\u4e3a\u4ed6\u662f\u4f4d\u8bda\u8d38\u541b\u5b50\uff0c\u6240\u4ee5\u522b\u4eba\u7eb5\u7136\u80dc\u4e86\u4ed6\uff0c\u4e5f\u4e0d\u5fcd\u4f24\u4ed6\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u63a5\u7740\u9053\u2236\u201c\u4f55\u51b5\uff0c\u9ec4\u8001\u524d\u8f88\u4ee5\u8bda\u5f85\u4eba\uff0c\u6240\u4ee5\u597d\u670b\u53cb\u6781\u591a\uff0c\u6c5f\u6e56\u4e2d\u8001\u4e00\u8f88\u7684\u82f1\u4fa0\uff0c \u5dee\u4e0d\u591a\u5168\u662f\u4ed6\u8001\u4eba\u5bb6\u7684\u77e5\u4ea4\uff0c\u6240\u4ee5\u5c31\u7b97\u662f\u4ed6\u7684\u5bf9\u5934\uff0c\u4e5f\u4e0d\u6562\u4f24\u4ed6\u3002\u201d \u3000\u3000\u5979\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\u2236\u201c\u5404\u4f4d\u8bf7\u60f3\u60f3\uff0c\u8fd9\u79cd\u4eba\u8bf4\u51fa\u6765\u7684\u8bdd\uff0c\u6211\u80fd\u4e0d\u76f8\u4fe1\u9ebd\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u82e6\u7b11\u9053\u2236\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u4f60\u53bb\u7684\u90a3\u5730\u65b9\u4e5f\u5fc5\u5b9a\u662f\u795e\u6c34\u5bab\uff0c\u7edd\u4e0d\u4f1a\u9519\u4e86\u3002\u201d \u3000\u3000\u82cf\u84c9\u84c9\u9ed8\u7136\u534a\u664c\uff0c\u9053\u2236\u201c\u53ea\u53ef\u60dc\u9ec4\u8001\u5251\u5ba2\u4e0d\u77e5\u5728\u90a3\u91cc\uff0c\u5426\u5219\u6211\u5012\u771f\u60f3\u5411\u4ed6\u8bf7\u6559\u51e0\u4ef6 \u4e8b\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u4e00\u76f4\u5728\u9759\u9759\u7684\u542c\u7740\uff0c\u6b64\u523b\u5ffd\u7136\u7b11\u4e86\u7b11\uff0c\u9053\u2236\u201c\u4f60\u60f3\u8bf7\u6559\u4ec0\u9ebd\u4e8b\uff0c\u4e0d\u59a8\u8bf4\u51fa\u6765\uff0c\u9ec4 \u8001\u5251\u5ba2\u4e5f\u8bb8\u80fd\u542c\u5f97\u5230\u4e5f\u672b\u53ef\u77e5\u3002\u201d \u3000\u3000\u82cf\u84c9\u84c9\u77aa\u5927\u773c\u775b\uff0c\u9053\u2236\u201c\u4ed6\u96be\u9053\u5c31\u5728\u9644\u8fd1\u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u53c8\u7b11\u4e86\u7b11\uff0c\u5374\u4e0d\u8bf4\u8bdd\u4e86\u3002 \u3000\u3000\u53ea\u542c\u5730\u9053\u7684\u77f3\u7ea7\u4e0a\u6709\u4eba\u8f7b\u8f7b\u54b3\u55fd\u4e86\u96e8\u58f0\u3002 \u3000\u3000\u6309\u7740\uff0c\u5c31\u6709\u53c1\u4e2a\u4eba\u7f13\u7f13\u8d70\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u8fd9\u53c1\u4eba\u8eab\u4e0a\u90fd\u7a7f\u7740\u9ed1\u8272\u7684\u888d\u5b50\uff0c\u8170\u7554\u90fd\u60ac\u7740\u5251\u80e1\u94c1\u82b1\u7acb\u523b\u5c31\u8ba4\u51fa\u4ed6\u4eec\u6b63\u90fd\u662f\u65b9\u624d\u548c\u695a\u7559 \u9999\u52a8\u624b\u7684\u4eba\u3002 \u3000\u3000\u53ea\u4e0d\u8fc7\u73b0\u5728\u4ed6\u4eec\u5df2\u5c06\u8499\u9762\u7684\u4e1d\u5dfe\u53d6\u4e86\u4e0b\u6765\uff0c\u53c1\u4e2a\u4eba\u6c14\u5ea6\u867d\u540c\u6837\u7684\u6c89\u7a33\uff0c\u4f46\u5f62\u8c8c\u5374\u5927\u4e0d\u76f8 \u540c\u3002 \u3000\u3000\u5f53\u5148\u800c\u884c\u7684\uff0c\u662f\u4f4d\u7709\u6e05\u76ee\u79c0\uff0c\u9762\u5982\u94f6\u76c6\u7684\u8001\u4eba\uff0c\u73b0\u5728\u867d\u7136\u5df2\u53d1\u798f\u4e86\uff0c\u60f3\u5f53\u5e74\u5374\u5fc5\u5b9a\u662f\u4f4d \u98ce\u5ea6\u7fe9\u7fe9\u7684\u7f8e\u7537\u5b50\u3002 \u3000\u3000\u4ed6\u7709\u95f4\u72b9\u5e26\u7740\u6012\u8272\uff0c\u4f3c\u4e4e\u9980\u6012\u672a\u6d88\uff0c\u813e\u6c14\u53c8\u663e\u7136\u5f88\u521a\u70c8\uff0c\u8fd9\u4eba\u4e0d\u95ee\u53ef\u77e5\uff0c\u5c31\u662f\u540d\u6ee1\u5929\u4e0b \u7684\u201c\u7389\u5251\u201d\u8427\u77f3\u4e86\u3002 \u3000\u3000\u4ed6\u8eab\u65c1\u4e00\u4eba\u8eab\u6750\u9880\u957f\uff0c\u9762\u5bb9\u6e05\u77bf\uff0c\u51e0\u4e4e\u6bd4\u4ed6\u6574\u9ad8\u4e86\u4e00\u4e2a\u5934\uff0c\u795e\u6c14\u770b\u62e3\u62a5\u4e25\u8083\uff0c\u4f46\u76ee\u5149\u5374 \u5f88\u6148\u548c\u3002 \u3000\u3000\u6b64\u523b\u4ed6\u53cc\u7709\u5fae\u76b1\uff0c\u5f77\u4f5b\u6709\u4e9b\u5fc3\u4e8b\u3002 \u3000\u3000\u5f8c\u9762\u8fd8\u8ddf\u7740\u4e00\u4eba\uff0c\u8eab\u6750\u65e2\u4e0d\u592a\u9ad8\uff0c\u4e5f\u4e0d\u7b97\u77ee\uff0c\u5bb9\u8c8c\u5f88\u5e73\u51e1\uff0c\u5f88\u5e73\u548c\uff0c\u57fa\u81f3\u8fde\u4e00\u4e1d\u8868\u60c5\u90fd \u6ca1\u6709\u3002 \u3000\u3000\u8fd9\u53c1\u4eba\u4e2d\uff0c\u53ea\u6709\u4ed6\u770b\u6765\u6ca1\u6709\u90a3\u79cd\u540d\u5251\u5ba2\u6151\u4eba\u7684\u624b\u91c7\uff0c\u4f46\u4e5f\u53ea\u6709\u4ed6\u795e\u60c5\u6700\u51b7\u6f20\uff0c\u4ee4\u4eba\u4e0d\u6562 \u4eb2\u8fd1\u3002 \u3000\u3000\u674e\u7389\u51fd\u592b\u5987\u4e00\u89c1\u5230\u8fd9\u53c1\u4e2a\u4eba\uff0c\u53c8\u500f\u5730\u8dea\u4e86\u4e0b\u53bb\uff0c\u8fde\u5934\u90fd\u4e0d\u6562\u62ac\u8d77\uff0c\u90a3\u4eba\u4e5f\u672a\u77a7\u4ed6\u4eec\u4e00 \u773c\uff0c\u5374\u5411\u695a\u7559\u9999\u62b1\u62f3\u4e00\u63d6\u3002 \u3000\u3000\u201c\u7389\u5251\u201d\u8427\u77f3\u957f\u53f9\u9053\u2236\u201c\u8001\u673d\u65b9\u624d\u4e3a\u7ad6\u5b50\u6240\u611a\uff0c\u51e0\u4e4e\u94f8\u4e0b\u5927\u9519\uff0c\u5b9e\u5df2\u65e0\u989c\u518d\u89c1\u9999\u5e05\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u7acb\u523b\u8eac\u8eab\u9053\u2236\u201c\u524d\u8f88\u8a00\u91cd\u4e86\uff0c\u5728\u4e0b\u600e\u62c5\u5f53\u5f97\u8d77\u3002\u201d \u3000\u3000\u90a3\u9880\u957f\u8001\u4eba\u4e5f\u53f9\u9053\u2236\u201c\u8001\u673d\u5e73\u751f\u6708\u4fe1\u8fd8\u672b\u505a\u51fa\u8fc7\u4ec0\u9ebd\u8d1f\u4eba\u4e4b\u4e8b\uff0c\u4f46\u6b64\u756a\u2026\u2026\u5509\uff01\u6b64\u756a\u5b9e \u4ee4\u8001\u592b\u65e0\u5730\u81ea\u5bb9\uff0c\u4f46\u671b\u9999\u5e05\u6055\u7f6a\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u53ea\u6709\u8fde\u58f0\u9053\u2236\u201c\u4e0d\u6562\uff0c\u4e0d\u6562\u2026\u2026\u201d \u3000\u3000\u8427\u77f3\u8dcc\u8db3\u9053\u2236\u201c\u957f\u8bdd\u77ed\u8bf4\uff0c\u8001\u673d\u7b49\u672c\u90fd\u5df2\u6ca1\u6709\u8138\u518d\u89c1\u4eba\u4e86\uff0c\u4f46\u82e5\u5c31\u6b64\u4e00\u8d70\u4e86\u4e4b\uff0c\u66f4\u4e0d\u50cf \u8bdd\uff0c\u662f\u4ee5\u8f6c\u6765\u5411\u9999\u5e05\u8d1f\u8346\u8bf7\u7f6a\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u672c\u6765\u8fd8\u5bf9\u4ed6\u4eec\u5f88\u6c14\u6124\uff0c\u4f46\u6b64\u523b\u89c1\u5230\u4ed6\u4eec\u7adf\u4e0d\u60dc\u79fb\u5c0a\u964d\u8d35\uff0c\u6765\u5411\u4e2a\u5f8c\u751f\u5c0f\u5b50\u8bf7\u7f6a\u8d54 \u793c\uff0c\u53c8\u4e0d\u7981\u6697\u6697\u8d5e\u7f8e\u3002 \u3000\u3000\u201c\u5bf9\u5c31\u662f\u5bf9\uff0c\u9519\u5c31\u662f\u9519\uff0c\u9519\u4e86\u5c31\u8ba4\u9519\uff0c\u7edd\u4e0d\u63a8\u8bff\u2026\u2026\u8fd9\u79cd\u6b66\u6797\u524d\u8f88\u7684\u98ce\u8f88\uff0c\u7684\u786e\u4ee4\u4eba\u4f69 \u670d\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u7684\u795e\u60c5\u4e5f\u5f88\u60f6\u6050\uff0c\u8c26\u8c22\u4e86\u51e0\u53e5\uff0c\u7acb\u523b\u5c31\u95ee\u9053\u2236\u201c\u674e\u8001\u524d\u8f88\u7684\u60c5\u51b5\u5df2\u597d\u4e9b\u4e86\u9ebd\uff1f\u201d \u3000\u3000\u8427\u77f3\u53f9\u9053\u2236\u201c\u89c2\u9c7c\u5144\u6b64\u6b21\u867d\u56e0\u7687\u5929\u6709\u773c\uff0c\u56e0\u7978\u5f97\u798f\uff0c\u4f46\u4ed6\u4e45\u75c5\u4e4b\u5f8c\uff0c\u7cbe\u6c14\u5df2\u865a\uff0c\u6b64\u756a\u53c8 \u52a8\u4e86\u771f\u6012\uff0c\u65e7\u75c5\u867d\u53bb\uff0c\u65b0\u75c5\u53c8\u751f\uff0c\u867d\u7ecf\u6211\u4eec\u51e0\u4e2a\u4eba\u5408\u529b\u5c06\u4ed6\u771f\u6c14\u5f15\u5165\u6b63\u8f68\uff0c\u4f46\u4e00\u65f6\u95f4\u53ea\u6015\u8fd8 \u662f\u96be\u4ee5\u5eb7\u590d\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\u2236\u201c\u94c1\u5c71\u9053\u957f\u5462\uff1f\u201d \u3000\u3000\u8427\u77f3\u9eef\u7136\u9053\u2236\u201c\u8fd9\u4f4d\u9053\u5144\u59dc\u6842\u4e4b\u6027\uff0c\u8001\u800c\u5f25\u8fa3\uff0c\u5374\u672a\u60f3\u5230\u81ea\u5df1\u7a76\u7adf\u5df2\u975e\u5c11\u5e74\u4e86\uff0c\u600e\u7ecf\u5f97 \u8d77\u5982\u6b64\u91cd\u521b\uff0c\u65b9\u624d\u867d\u7136\u8fd8\u80fd\u52c9\u5f3a\u652f\u6301\uff0c\u4f46\u6b64\u523b\u7684\u60c5\u51b5\u5374\u4f3c\u6bd4\u89c2\u9c7c\u5144\u66f4\u4e25\u91cd\uff0c\u5e78\u597d\u51cc\u98de\u8001\u4e43\u662f \u6cbb\u4f24\u7684\u540d\u5bb6\uff0c\u6b64\u523b\u8fd8\u5728\u7167\u6599\u7740\u4ed6\u3002\u201d \u3000\u3000\u542c\u5230\u8fd9\u91cc\uff0c\u674e\u7389\u51fd\u5df2\u662f\u6cea\u6d41\u6ee1\u9762\uff0c\u67f3\u65e0\u7709\u66f4\u65e9\u5df2\u6ce3\u4e0d\u6210\u58f0\uff0c\u592b\u59bb\u4e24\u4eba\u4e00\u9f50\u4ee5\u9996\u987f\u5730\uff0c\u54fd \u58f0\u9053\u2236\u201c\u665a\u8f88\u8be5\u6b7b\uff01\u90fd\u662f\u665a\u8f88\u8be5\u6b7b\uff01\u201d \u3000\u3000\u4ed6\u4eec\u4e0d\u8bf4\u8bdd\u53cd\u5012\u597d\uff0c\u8fd9\u4e00\u8bf4\u8bdd\uff0c\u8427\u77f3\u6012\u6c14\u7acb\u523b\u53c8\u53d1\u4f5c\u4e86\uff0c\u5389\u58f0\u9053\u2236\u201c\u4f60\u4e24\u4eba\u8fd8\u6709\u80c6\u5b50\u6562 \u7559\u5728\u8fd9\u91cc\uff1f\u4f60\u4e24\u4eba\u5c45\u7136\u8fde\u6211\u4eec\u90fd\u9a97\u4e86\uff0c\u96be\u9053\u5c31\u4e0d\u6015\u4f60\u4eec\u674e\u5bb6\u7956\u5b97\u7559\u4e0b\u7684\u5bb6\u6cd5\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u6d41\u6cea\u9053\u2236\u201c\u665a\u8f88\u4e5f\u77e5\u9053\u7f6a\u65e0\u53ef\u8ffd\uff0c\u5e94\u8be5\u4f0f\u6cd5\uff0c\u53ea\u6c42\u524d\u8f88\u9976\u4e86\u5979\u4e00\u547d\uff0c\u5979\u2026\u2026 \u5979\u2026\u2026\u2026\u5979\u672c\u548c\u6b64\u4e8b\u65e0\u5173\u7684\u3002\u201d \u3000\u3000\u8427\u77f3\u6012\u9053\u2236\u201c\u5979\u82e5\u548c\u6b64\u4e8b\u65e0\u5173\uff0c\u8c01\u548c\u6b64\u4e8b\u6709\u5173\uff1f\u201c\u62e5\u7fe0\u5c71\u5e84\u201d\u7684\u58f0\u540d\u5df2\u88ab\u4f60\u4eec\u6bc1\u5c3d\u4e86\uff0c \u96be\u9053\u8fd8\u8981\u7559\u4e0b\u5979\u6765\u4e22\u4eba\u9ebd\uff1f\u201d \u3000\u3000\u67f3\u65e0\u7709\u4f0f\u5730\u75db\u54ed\u9053\u2236\u201c\u4e0d\u9519\uff0c\u6b64\u4e8b\u5168\u56e0\u6211\u800c\u8d77\uff0c\u548c\u4ed6\u65e0\u5173\uff0c\u8bf7\u524d\u8f88\u4eec\u9976\u4e86\u4ed6\u5427\uff01\u201d \u3000\u3000\u82cf\u84c9\u84c9\u5979\u4eec\u542c\u4e86\u8fd9\u51c4\u60e8\u7684\u54ed\u58f0\uff0c\u53c8\u4e0d\u7981\u4e3a\u4e4b\u607b\u7136\uff0c\u6b63\u4e0d\u77e5\u8be5\u5982\u4f55\u4e3a\u8fd9\u4e00\u53cc\u540c\u547d\u7684\u9e33\u9e2f\u6c42 \u60c5\u3002 \u3000\u3000\u8c01\u77e5\u90a3\u9880\u957f\u8001\u4eba\u5374\u53f9\u4e86\u53e3\u6c14\uff0c\u67d4\u58f0\u9053\u2236\u201c\u4f60\u4eec\u4e5f\u4e0d\u5fc5\u96be\u53d7\uff0c\u6211\u4eec\u53d7\u89c2\u9c7c\u5144\u4e4b\u6258\uff0c\u672c\u60f3\u6765 \u4ee5\u5bb6\u6cd5\u5904\u7f6e\u4f60\u4eec\u7684\uff0c\u4f46\u65b9\u624d\u6211\u4eec\u5728\u4e0a\u9762\u5df2\u542c\u4e86\u4f60\u4eec\u7684\u8bdd\uff0c\u4e5f\u89c9\u5f97\u4f60\u4eec\u7684\u906d\u9047\u5f88\u53ef\u601c\uff0c\u5e76\u975e\u6ca1 \u6709\u53ef\u4ee5\u539f\u8c05\u4e4b\u5904\uff0c\u6211\u4eec\u5df2\u51b3\u5b9a\u66ff\u4f60\u4eec\u53bb\u5411\u89c2\u9c7c\u5144\u6c42\u60c5\u4e86\u3002\u201d \u3000\u3000\u8427\u77f3\u8fde\u8fde\u8dfa\u8db3\uff0c\u82e6\u7b11\u9053\u2236\u201c\u6211\u65b9\u624d\u5df2\u8bf4\u8fc7\uff0c\u8981\u591a\u6559\u8bad\u6559\u8bad\u4ed6\u4eec\u7684\uff0c\u4f60\u6b64\u523b\u600e\u5730\u53c8\u5bf9\u4ed6\u4eec \u8bf4\u5b9e\u8bdd\u4e86\u3002\u201d \u3000\u3000\u90a3\u9880\u957f\u8001\u4eba\u53f9\u9053\u2236\u201c\u4ed6\u4eec\u770b\u6765\u5df2\u6709\u75db\u6094\u4e4b\u610f\uff0c\u4f60\u4f55\u5fc5\u518d\u53eb\u4ed6\u4eec\u7740\u6025\u5462\uff1f\u201d \u3000\u3000\u82cf\u84c9\u84c9\u5fcd\u4e0d\u4f4f\u548c\u674e\u7ea2\u8896\u76f8\u89c6\u4e00\u7b11\uff0c\u53ea\u56e0\u542c\u5230\u8fd9\u91cc\uff0c\u5979\u4eec\u5df2\u731c\u51fa\u8fd9\u9880\u957f\u8001\u4eba\u5fc5\u662f\u201c\u541b\u5b50 \u5251\u201d\u65e0\u7591\u4e86\u3002 \u3000\u3000\u53ef\u662f\u83ab\u8bf4\u82cf\u84c9\u84c9\u5979\u4eec\uff0c\u5c31\u8fde\u695a\u7559\u9999\u7adf\u4e5f\u770b\u4e0d\u900f\u90a3\u5bb9\u8c8c\u5e73\u51e1\uff0c\u795e\u6c14\u51b7\u6f20\u7684\u5251\u5ba2\u662f\u4ec0\u9ebd\u6765 \u5386\u3002 \u3000\u3000\u4ed6\u5e74\u7eaa\u770b\u6765\u5f77\u4f5b\u6bd4\u8427\u77f3\u3001\u9ec4\u9c81\u76f4\u4ed6\u4eec\u5e74\u8f7b\u4e9b\uff0c\u4f46\u695a\u7559\u9999\u65b9\u624d\u88ab\u56f0\u5728\u5251\u9635\u4e2d\u65f6\uff0c\u5df2\u89c9\u51fa\u8fd9 \u4eba\u529f\u529b\u4e4b\u6df1\u539a\uff0c\u5251\u6cd5\u7684\u8001\u8fa3\uff0c\u7edd\u4e0d\u5728\u8427\u77f3\u3001\u94c1\u5c71\u9053\u957f\u3001\u51cc\u98de\u9601\u3001\u9ec4\u9c81\u76f4\uff0c\u548c\u5e05\u4e00\u5e06\u8fd9\u4e9b\u524d\u8f88 \u540d\u5251\u5ba2\u4e4b\u4e0b\u3002 \u3000\u3000\u4f55\u51b5\u4ed6\u65e2\u662f\u674e\u89c2\u9c7c\u7684\u597d\u53cb\uff0c\u4e5f\u81ea\u7136\u662f\u6210\u540d\u5df2\u4e45\u7684\u524d\u8f88\uff0c\u4f46\u695a\u7559\u9999\u5374\u504f\u504f\u60f3\u4e0d\u8d77\u524d\u8f88\u540d\u5bb6 \u4e2d\u6709\u8fd9\u6837\u4e00\u4e2a\u4eba\u6765\u3002 \u3000\u3000\u695a\u7559\u9999\u6b63\u8981\u63a2\u95ee\u4ed6\u7684\u540d\u59d3\u6765\u5386\uff0c\u8c01\u77e5\u4ed6\u5374\u5df2\u8f6c\u8fc7\u8eab\u5b50\uff0c\u80cc\u8d1f\u7740\u53cc\u624b\uff0c\u62ac\u7740\u5934\u51fa\u795e\u8d77\u6765\uff0c \u4e5f\u4e0d\u77e5\u5728\u60f3\u4e9b\u4ec0\u9ebd\u3002 \u3000\u3000\u8427\u77f3\u548c\u9ec4\u9c81\u76f4\u5c45\u7136\u4e5f\u6ca1\u6709\u5c06\u695a\u7559\u9999\u5f15\u89c1\u7ed9\u4ed6\uff0c\u4ed6\u4f3c\u4e4e\u662f\u4e2a\u5f88\u795e\u79d8\u7684\u4eba\u7269\u3002\u695a\u7559\u9999\u4e5f\u4e0d\u7981 \u5bf9\u4ed6\u8d8a\u6765\u8d8a\u597d\u5947\u4e86\u3002 \u3000\u3000\u8fd9\u65f6\u201c\u541b\u5b50\u5251\u201d\u5ffd\u7136\u671b\u7740\u82cf\u84c9\u84c9\u9053\u2236\u201c\u8fd9\u4f4d\u59d1\u5a18\u2026\u2026\u201d \u3000\u3000\u82cf\u84c9\u84c9\u7acb\u523b\u68c0\u887d\u4f5c\u793c\u9053\u2236\u201c\u665a\u8f88\u82cf\u84c9\u84c9\uff0c\u6709\u51e0\u4ef6\u4e8b\u6b63\u60f3\u8bf7\u6559\u524d\u8f88\u3002\u201d \u3000\u3000\u9ec4\u9c81\u76f4\u5fae\u7b11\u9053\u2236\u201c\u82cf\u59d1\u5a18\u53ea\u7ba1\u8bf4\u5427\uff01\u201d \u3000\u3000\u82cf\u84c9\u84c9\u6c89\u541f\u4e86\u534a\u664c\uff0c\u9053\u2236\u201c\u524d\u8f88\u786e\u77e5\u90a3\u83e9\u63d0\u5eb5\u4e43\u662f\u795e\u6c34\u5bab\u7684\u63a5\u5f15\u5904\u9ebd\uff1f\u201d \u3000\u3000\u9ec4\u9c81\u76f4\u9053\u2236\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u4ed6\u4e5f\u6c89\u541f\u4e86\u534a\u664c\uff0c\u624d\u63a5\u7740\u9053\u2236\u201c\u65e0\u7709\u95ee\u8d77\u6211\u65f6\uff0c\u8001\u673d\u672c\u4e0d\u77e5\u5979\u4e3a\u4f55\u8981\u5230\u795e\u6c34\u5bab\u53bb\uff0c\u53ea\u5f53 \u5979\u5c11\u5e74\u597d\u5947\uff0c\u662f\u5728\u65e0\u610f\u95f4\u968f\u53e3\u95ee\u51fa\u6765\u7684\u3002\u201d \u3000\u3000\u82cf\u84c9\u84c9\u9053\u2236\u201c\u524d\u8f88\u53ef\u77e5\u9053\u83e9\u63d0\u5eb5\u90a3\u4f4d\u8001\u5e08\u592a\u662f\u4f55\u6765\u5386\u9ebd\uff1f\u201d \u3000\u3000\u9ec4\u9c81\u76f4\u8ffd\u2236\u201c\u90a3\u4f4d\u54d1\u5e08\u592a\u5012\u4e5f\u53ef\u7b97\u662f\u5f53\u4e16\u4e00\u4f4d\u5947\u4eba\uff0c\u4ece\u6765\u6ca1\u6709\u4eba\u77e5\u9053\u5979\u7684\u6765\u5386\uff0c\u4e5f\u4ece\u65e0 \u4eba\u542c\u5979\u8bf4\u8fc7\u4e00\u53e5\u8bdd\u3002\u201d \u3000\u3000\u82cf\u84c9\u84c9\u9053\u2236\u201c\u5979\u662f\u771f\u7684\u6b8b\u5e9f\uff0c\u8fd8\u662f\u88c5\u804b\u4f5c\u54d1\uff1f\u201d \u3000\u3000\u9ec4\u9c81\u76f4\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\u2236\u201c\u4e00\u4e2a\u4eba\u82e5\u80fd\u88c5\u804b\u4f5c\u54d1\u6570\u5341\u5e74\uff0c\u60f3\u5fc5\u6709\u5979\u7684\u4f24\u5fc3\u4e8b\uff0c\u8001\u673d\u53c8\u4f55\u5fc5 \u518d\u53bb\u8ffd\u7a76\u5979\u662f\u771f\u662f\u5047\u5462\uff1f\u201d \u3000\u3000\u82cf\u84c9\u84c9\u8083\u7136\u9053\u2236\u201c\u524d\u8f88\u80f8\u895f\uff0c\u786e\u975e\u665a\u8f88\u4eec\u6240\u80fd\u4f01\u53ca\uff0c\u665a\u8f88\u5b9e\u5728\u60ed\u6127\u5f97\u5f88\u3002\u201d \u3000\u3000\u5979\u5782\u624b\u8083\u7acb\uff0c\u7adf\u4e0d\u518d\u95ee\u4e86\u3002 \u3000\u3000\u997c\u4e86\u534a\u664c\uff0c\u9ec4\u9c81\u76f4\u5374\u5fcd\u4e0d\u4f4f\u95ee\u9053\u2236\u201c\u82cf\u59d1\u5a18\u60f3\u95ee\u7684\u53ea\u6015\u4e0d\u6b62\u8fd9\u4e24\u53e5\u8bdd\u5427\uff1f\u201d \u3000\u3000\u82cf\u84c9\u84c9\u53c8\u6c89\u541f\u4e86\u5f88\u4e45\uff0c\u624d\u606d\u58f0\u9053\u2236\u201c\u665a\u8f88\u7684\u786e\u8fd8\u6709\u4e8b\u8981\u8bf7\u6559\u524d\u8f88\u3002\u201d \u3000\u3000\u9ec4\u9c81\u76f4\u9053\u2236\u201c\u65e2\u662f\u5982\u6b64\uff0c\u59d1\u5a18\u4e3a\u4f55\u4e0d\u95ee\uff1f\u201d \u3000\u3000\u82cf\u84c9\u84c9\u9053\u2236\u201c\u665a\u8f88\u552f\u6050\u6709\u4e9b\u4e8b\u662f\u524d\u8f88\u4e0d\u613f\u5bf9\u5916\u4eba\u9053\u7684\uff0c\u4f46\u665a\u8f88\u82e5\u662f\u95ee\u4e86\uff0c\u524d\u8f88\u53c8\u7edd\u4e0d\u4f1a \u4ee5\u865a\u8a00\u6577\u884d\uff0c\u662f\u4ee5\u665a\u8f88\u4e0d\u6562\u518d\u95ee\u3002\u201d \u3000\u3000\u542c\u5230\u8fd9\u91cc\uff0c\u80e1\u94c1\u82b1\u5fc3\u91cc\u53ea\u89c9\u6697\u6697\u597d\u7b11\u2236\u201c\u96be\u602a\u8001\u81ed\u866b\u8981\u53eb\u8fd9\u4f4d\u59d1\u5a18\u5230\u795e\u6c34\u5bab\u53bb\u6253\u542c\u6d88 \u606f\uff0c\u770b\u6765\u5979\u7684\u786e\u5f88\u61c2\u5f97\u95ee\u8bdd\u7684\u6280\u5de7\uff0c\u5979\u5634\u91cc\u867d\u8bf4\u201c\u4e0d\u6562\u518d\u95ee\u201d\uff0c\u5176\u5b9e\u5374\u65e0\u5f02\u5df2\u7ecf\u5c06\u4ec0\u9ebd\u8bdd\u90fd \u95ee\u4e86\u51fa\u6765\uff0c\u800c\u4e14\u8fd8\u8981\u4eba\u5bb6\u975e\u8bf4\u4e0d\u53ef\u3002\u201d \u3000\u3000\u9ec4\u9c81\u76f4\u679c\u7136\u7b11\u4e86\u7b11\uff0c\u9053\u2236\u201c\u59d1\u5a18\u662f\u5426\u60f3\u95ee\u8001\u673d\u662f\u600e\u4f1a\u77e5\u9053\u8fd9\u4ef6\u4e8b\u7684\uff1f\u201d \u3000\u3000\u82cf\u84c9\u84c9\u5fae\u7b11\u4e0d\u8bed\u3002 \u3000\u3000\u9ec4\u9c81\u76f4\u9053\u2236\u201c\u5176\u5b9e\u8fd9\u4ef6\u4e8b\u8001\u673d\u4e5f\u662f\u542c\u522b\u4eba\u8bf4\u7684\u3002\u201d \u3000\u3000\u4ed6\u6709\u610f\u65e0\u610f\u95f4\u779f\u4e86\u90a3\u5e73\u51e1\u7684\u9ed1\u886b\u5251\u5ba2\u4e00\u773c\uff0c\u53c8\u6309\u7740\u9053\u2236\u201c\u8001\u673d\u4e5f\u76f8\u4fe1\u8fd9\u4eba\u6240\u8bf4\u7684\u8bdd\u5fc5\u771f \u65e0\u5047\uff0c\u53ea\u56e0\u4ed6\u5e73\u751f\u4ece\u672a\u5728\u8001\u673d\u9762\u524d\u9690\u7792\u8fc7\u4efb\u4f55\u4e8b\uff0c\u66f4\u672b\u5bf9\u8001\u673d\u8bf4\u8fc7\u4e00\u53e5\u5047\u8bdd\u3002\u201d \u3000\u3000\u82cf\u84c9\u84c9\u76ee\u5149\u95ea\u52a8\uff0c\u5ffd\u7136\u7b11\u9053\u2236\u201c\u8fd9\u4eba\u60f3\u5fc5\u662f\u524d\u8f88\u7684\u7ea2\u7c89\u77e5\u5df1\u2026\u2026\u201d \u3000\u3000\u5979\u6545\u610f\u5c06\u201c\u7ea2\u7c89\u77e5\u5df1\u201d\u56db\u4e2a\u5b57\u58f0\u97f3\u62d6\u5f97\u957f\u957f\u7684\u3002 \u3000\u3000\u9ec4\u9c81\u76f4\u679c\u7136\u5fcd\u4e0d\u4f4f\u9053\u2236\u201c\u59d1\u5a18\u8bf4\u7b11\u4e86\uff0c\u8001\u673d\u751f\u5e73\u4e0d\u4e8c\u8272\uff0c\u90a3\u6709\u4ec0\u9ebd\u7ea2\u7c89\u77e5\u5df1\u3002\u201d \u3000\u3000\u82cf\u84c9\u84c9\u773c\u775b\u4e00\u4eae\uff0c\u9053\u2236\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u5bf9\u524d\u8f88\u8bf4\u8d77\u8fd9\u4ef6\u4e8b\u6765\u7684\uff0c\u96be\u9053\u7adf\u662f\u4f4d\u7537\u58eb\u9ebd\uff1f\u201d \u3000\u3000\u9ec4\u9c81\u76f4\u9053\u201c\u55ef\uff01\u201d \u3000\u3000\u82cf\u84c9\u84c9\u7acb\u523b\u8ffd\u95ee\u9053\u2236\u201c\u636e\u665a\u8f88\u6240\u77e5\uff0c\u5929\u4e0b\u4ece\u6ca1\u6709\u4e00\u4e2a\u7537\u4eba\u80fd\u77e5\u201c\u795e\u6c34\u5bab\u201d\u7684\u79d8\u5bc6\uff0c\u524d\u8f88 \u8fd9\u4f4d\u670b\u53cb\u53c8\u662f\u600e\u9ebd\u4f1a\u77e5\u9053\u8fd9\u4ef6\u4e8b\u7684\u5462\uff1f\u201d \u3000\u3000\u9ec4\u9c81\u76f4\u6c89\u541f\u4e86\u534a\u664c\uff0c\u82e6\u7b11\u9053\u2236\u201c\u59d1\u5a18\u82e5\u95ee\u53ca\u8001\u673d\u81ea\u5df1\u7684\u4e8b\uff0c\u8001\u673d\u662f\u77e5\u65e0\u4e0d\u8a00\uff0c\u8a00\u65e0\u4e0d \u5c3d\uff0c\u4f46\u8fd9\u4ef6\u4e8b\u5374\u6709\u5173\u522b\u4eba\u7684\u79d8\u5bc6\uff0c\u6055\u8001\u673d\u4e0d\u80fd\u591a\u8bf4\u4e86\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u8bdd\u7684\u65f6\u95f4\uff0c\u53c8\u779f\u4e86\u90a3\u9ed1\u886b\u5251\u5ba2\u4e00\u773c\uff0c\u5ffd\u7136\u62b1\u62f3\u9053\u2236\u201c\u8001\u673d\u8a00\u5c3d\u65bc\u6b64\uff0c\u5c31\u6b64\u544a\u8f9e\u3002\u201d \u3000\u3000\u90a3\u9ed1\u886b\u5251\u5ba2\u5df2\u8f6c\u8fc7\u8eab\uff0c\u5411\u695a\u7559\u9999\u5306\u5306\u4e00\u63d6\uff0c\u5c31\u8d70\u4e86\u51fa\u53bb\uff0c\u4e24\u4eba\u90fd\u4f3c\u4e4e\u518d\u4e5f\u4e0d\u613f\u518d\u5728\u8fd9\u91cc \u505c\u7559\u534a\u523b\u3002 \u3000\u3000\u8427\u77f3\u76b1\u4e86\u76b1\u7709\uff0c\u5927\u58f0\u9053\u2236\u201c\u9c81\u516c\uff0c\u8fd9\u91cc\u7684\u4e8b\uff0c\u4f60\u4e0d\u7ba1\u4e86\u9ebd\uff1f\u201d \u3000\u3000\u53ea\u542c\u9ec4\u9c81\u76f4\u5728\u77f3\u9636\u4e0a\u7b11\u9053\u2236\u201c\u6e05\u5b98\u96be\u65ad\u5bb6\u52a1\u4e8b\uff0c\u4ed6\u4eec\u7236\u5b50\u95f4\u7684\u7ea0\u7eb7\uff0c\u522b\u4eba\u60f3\u7ba1\u4e5f\u7ba1\u4e0d\u4e86 \u7684\uff0c\u89c2\u9c7c\u5144\u73b0\u5728\u867d\u7136\u6012\u6c14\u51b2\u5929\uff0c\u4f46\u53ea\u8981\u8fc7\u4e86\u53c1\u4e94\u5929\uff0c\u4e5f\u5c31\u597d\u4e86\u3002\u201d \u3000\u3000\u8bf4\u5230\u6700\u5f8c\u4e24\u53e5\u8bdd\uff0c\u4ed6\u5df2\u8d70\u5f97\u5f88\u8fdc\uff0c\u8427\u77f3\u8dfa\u4e86\u8dfa\u811a\uff0c\u4e5f\u8ffd\u4e86\u51fa\u53bb\uff0c\u5ffd\u53c8\u56de\u8fc7\u5934\u6765\u77aa\u7740\u674e\u7389 \u51fd\u9053\u2236\u201c\u4f60\u8fd9\u4e24\u5929\u6700\u597d\u83ab\u8981\u53bb\u89c1\u4f60\u7684\u8001\u5934\u5b50\uff0c\u514d\u5f97\u4ed6\u53c8\u88ab\u4f60\u6c14\u5f97\u8d70\u706b\u5165\u9b54\uff0c\u4f60\u6700\u597d\u8fdc\u8fdc\u7684\u907f \u5f00\uff0c\u7b49\u4ed6\u7684\u75db\u597d\u4e86\u518d\u56de\u6765\uff0c\u90a3\u65f6\u4ed6\u6709\u4e86\u529b\u6c14\uff0c\u63cd\u4f60\u7684\u65f6\u5019\u4e5f\u53ef\u4ee5\u63cd\u5f97\u91cd\u4e9b\u3002\u201d \u3000\u3000\u677e\u9e64\u697c\u7684\u83dc\u672c\u5c31\u5f88\u6709\u540d\uff0c\u4f55\u51b5\u5927\u5bb6\u53c8\u5168\u90fd\u997f\u4e86\uff0c\u80e1\u94c1\u82b1\u56fa\u7136\u662f\u5f00\u6000\u7545\u996e\uff0c\u5c31\u8fde\u82cf\u84c9\u84c9\u4e5f \u559d\u4e86\u51e0\u676f\u3002 \u3000\u3000\u5176\u4e2d\u5c31\u53ea\u6709\u9ed1\u73cd\u73e0\u5f77\u4f5b\u6709\u4e9b\u5fc3\u4e8b\uff0c\u674e\u7389\u51fd\u592b\u5987\u81ea\u7136\u66f4\u98df\u4e0d\u4e0b\u54bd\uff0c\u4ed6\u4eec\u672c\u65e0\u989c\u8ddf\u7740\u5927\u5bb6\u4e00 \u8d77\u6765\u7684\u3002 \u3000\u3000\u4f46\u674e\u7ea2\u8896\u5374\u8bf4\u2236\u201c\u4f60\u4eec\u600e\u80fd\u5230\u522b\u7684\u5730\u65b9\u53bb\u5462\uff1f\u6211\u4eec\u53c8\u4e0d\u8ba4\u5f97\u90a3\u83e9\u63d0\u5eb5\u5728\u90a3\u91cc\uff0c\u8fd8\u8981\u8bf7\u4f60 \u5e26\u8def\u54e9\uff0c\u96be\u9053\u4f60\u4e0d\u80af\u5e2e\u5fd9\uff1f\u201d \u3000\u3000\u5b8b\u751c\u513f\u4e5f\u5e2e\u7740\u674e\u7ea2\u8896\u62c9\u4ed6\u4eec\uff0c\u5979\u8bf4\u2236\u201c\u695a\u7559\u9999\u53cd\u6b63\u4e00\u5b9a\u8981\u5230\u795e\u6c34\u5bab\u53bb\u7684\uff0c\u53ea\u8981\u4ed6\u4e00\u5230\u795e \u6c34\u5bab\uff0c\u5c31\u80fd\u5c06\u89e3\u836f\u66ff\u4f60\u8981\u51fa\u6765\uff0c\u4f60\u653e\u5fc3\u597d\u4e86\u3002\u201d \u3000\u3000\u522b\u7684\u4eba\u867d\u7136\u77e5\u9053\u8fd9\u4ef6\u4e8b\u7edd\u4e0d\u4f1a\u5982\u6b64\u5bb9\u6613\uff0c\u4f46\u4e5f\u5e76\u6ca1\u6709\u62c5\u5fc3\uff0c\u56e0\u4e3a\u65e0\u8bba\u591a\u9ebd\u5927\u7684\u5371\u9669\u695a\u7559 \u9999\u90fd\u95ef\u8fc7\u4e86\uff0c\u4ed6\u4eec\u8ba4\u4e3a\u201c\u6c34\u6bcd\u9634\u59ec\u201d\u5c31\u7b97\u518d\u5389\u5bb3\uff0c\u4e5f\u53ea\u4e0d\u8fc7\u662f\u4e2a\u4eba\u800c\u5df2\uff0c\u96be\u9053\u8fd8\u80fd\u5c06\u695a\u7559\u9999 \u541e\u4e0b\u53bb\u4e0d\u6210\u3002 \u3000\u3000\u771f\u6b63\u62c5\u5fc3\u7684\u5012\u662f\u695a\u7559\u9999\u81ea\u5df1\u3002 \u7b2c\u4e8c\u5341\u4e00\u7ae0\u3000\u4eba\u76ae\u9762\u5177>> \u53e4\u9f99\u300a\u695a\u7559\u9999\u7cfb\u5217\xb7\u753b\u7709\u9e1f\u300b \u7b2c\u4e8c\u5341\u4e00\u7ae0\u3000\u4eba\u76ae\u9762\u5177 \u3000\u3000\u56e0\u4e3a\u53ea\u6709\u4ed6\u89c1\u8bc6\u8fc7\u77f3\u89c2\u97f3\u7684\u6b66\u529f\uff0c\u800c\u53f3\u89c2\u97f3\u5e73\u751f\u6700\u754f\u60e7\u7684\u5374\u662f\u201c\u6c34\u6bcd\u9634\u59ec\u201d\uff0c\u9634\u59ec\u7684\u6b66 \u529f\u7a76\u7adf\u9ad8\u660e\u5230\u4ec0\u9ebd\u7a0b\u5ea6\uff0c\u4ed6\u7b80\u76f4\u8fde\u60f3\u90fd\u4e0d\u6562\u53bb\u60f3\uff0c\u4f55\u51b5\u5979\u90a3\u201c\u795e\u6c34\u5bab\u201d\u7684\u79d8\u5bc6\u66f4\u4e0d\u53ef\u601d\u8bae\u3002 \u3000\u3000\u7a81\u542c\u80e1\u94c1\u82b1\u9053\u2236\u201c\u51cc\u98de\u9601\u3001\u8427\u77f3\u3001\u94c1\u5c71\u9053\u957f\u3001\u9ec4\u9c81\u76f4\uff0c\u8fd9\u56db\u4f4d\u6211\u7684\u786e\u662f\u4eba\u5df2\u95fb\u540d\u7684\u4e86\uff0c \u4f46\u90a3\u4f4d\u6709\u4e9b\u9634\u9633\u602a\u6c14\u7684\u662f\u4f55\u8bb8\u4eba\u4e5f\uff1f\u201d \u3000\u3000\u674e\u7ea2\u8896\u9053\u2236\u201c\u4f60\u8bf4\u7684\u53ef\u662f\u90a3\u4ece\u6765\u4e0d\u7b11\uff0c\u4e5f\u4ece\u6765\u4e0d\u8bf4\u8bdd\u7684\u4eba\u9ebd\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\u2236\u201c\u5c31\u662f\u4ed6\u3002\u201d \u3000\u3000\u674e\u7ea2\u8896\u9053\u2236\u201c\u6211\u89c1\u5230\u8fd9\u4eba\uff0c\u4e5f\u89c9\u5f97\u6709\u4e9b\u5947\u602a\uff0c\u624d\u60f3\u95ee\u95ee\u4ed6\u6765\u5386\u7684\uff0c\u8c01\u77e5\u4ed6\u4eec\u5ffd\u7136\u95f4\u5c31\u8d70 \u4e86\u3002\u201d \u3000\u3000\u82cf\u84c9\u84c9\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\u2236\u201c\u4ed6\u4eec\u8d70\u5f97\u90a3\u9ebd\u5feb\uff0c\u4e5f\u8bb8\u5c31\u662f\u6015\u6211\u4eec\u95ee\u4ed6\u7684\u6765\u5386\u3002\u201d \u3000\u3000\u674e\u7ea2\u8896\u9053\u2236\u201c\u53ef\u662f\u2026\u2026\u674e\u516c\u5b50\uff0c\u4f60\u96be\u9053\u4e5f\u4e0d\u77e5\u90a3\u4eba\u662f\u8c01\u9ebd\uff1f\u201d \u3000\u3000\u674e\u7389\u51fd\u6447\u4e86\u6447\u5934\uff0c\u9053\u2236\u201c\u90a3\u4f4d\u524d\u8f88\u4e43\u662f\u9ec4\u8001\u524d\u8f88\u8bf7\u6765\u7684\u5e2e\u624b\uff0c\u9ec4\u8001\u524d\u8f88\u53ea\u8bf4\u4ed6\u5251\u6cd5\u4e4b \u9ad8\uff0c\u5f53\u4e16\u5c11\u6709\u4eba\u53ca\uff0c\u7edd\u4e0d\u4f1a\u8bef\u4e8b\uff0c\u5374\u4e0d\u80af\u8bf4\u51fa\u4ed6\u7684\u59d3\u540d\u6765\u5386\u3002\u201d \u3000\u3000\u674e\u7ea2\u8896\u76b1\u7709\u9053\u2236\u201c\u8fd9\u662f\u4e3a\u4e86\u4ec0\u9ebd\u5462\uff1f\u201d \u3000\u3000\u674e\u7389\u51fd\u9053\u2236\u201c\u5f53\u65f6\u6211\u4eec\u4e5f\u89c9\u5f97\u5f88\u5947\u602a\uff0c\u5374\u4e0d\u6562\u591a\u95ee\uff0c\u53ea\u9053\u8427\u8001\u524d\u8f88\u4ed6\u4eec\u6765\u4e86\u4e4b\u5f8c\uff0c\u4e00\u5b9a \u4f1a\u8ba4\u51fa\u4ed6\u6765\u7684\u3002\u201d \u3000\u3000\u674e\u7ea2\u8896\u9053\u2236\u201c\u4e0d\u9519\uff0c\u8427\u5927\u4fa0\u7684\u786e\u662f\u4ea4\u6e38\u5e7f\u9614\uff0c\u6b66\u6797\u4e2d\u8001\u4e00\u8f88\u7684\u6210\u540d\u82f1\u96c4\uff0c\u591a\u591a\u5c11\u5c11\u90fd\u548c \u201c\u7389\u5251\u95e8\u201d\u6709\u4e9b\u5173\u7cfb\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u9053\u2236\u201c\u4f46\u8427\u8001\u524d\u8f88\u975e\u4f46\u4e0d\u8ba4\u5f97\u4ed6\uff0c\u8fde\u4ed6\u7684\u4eba\u90fd\u4ece\u6765\u672b\u89c1\u8fc7\uff0c\u6b66\u6797\u4e2d\u6210\u540d\u7684\u5251\u5ba2\uff0c \u4e5f\u7edd\u6ca1\u6709\u4e00\u4e2a\u4eba\u957f\u5f97\u548c\u4ed6\u76f8\u4f3c\u7684\u3002\u201d \u3000\u3000\u82cf\u84c9\u84c9\u5ffd\u53c8\u4e00\u7b11\uff0c\u60a0\u7136\u9053\u2236\u201c\u6211\u65e9\u5df2\u77e5\u9053\u4e16\u4e0a\u7edd\u4e0d\u4f1a\u6709\u4e00\u4e2a\u4eba\u8ba4\u5f97\u4ed6\u3002\u201d \u3000\u3000\u674e\u7ea2\u8896\u9053\u2236\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u82cf\u84c9\u84c9\u9053\u2236\u201c\u90a3\u5730\u5ba4\u4e2d\u5149\u7ebf\u5f88\u6697\uff0c\u4e5f\u96be\u602a\u4f60\u4eec\u770b\u4e0d\u51fa\u6765\u3002\u201d \u3000\u3000\u674e\u7ea2\u8896\u5931\u58f0\u9053\u2236\u201c\u96be\u9053\u4ed6\u90a3\u5f20\u8138\u4e0d\u662f\u771f\u7684\u9ebd\uff1f\u201d \u3000\u3000\u82cf\u84c9\u84c9\u7b11\u4e86\u7b11\uff0c\u671b\u7740\u695a\u7559\u9999\u9053\u2236\u201c\u6b64\u4eba\u4e0d\u4f46\u6613\u5bb9\u672f\u975e\u5e38\u9ad8\u660e\uff0c\u6234\u7684\u4eba\u76ae\u9762\u5177\u66f4\u5341\u5206\u7cbe \u5de7\uff0c\u6240\u4ee5\u624d\u80fd\u7792\u8fc7\u4f60\u4eec\u8fd9\u4e9b\u5927\u884c\u5bb6\u7684\u773c\u775b\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u4e5f\u7b11\u4e86\u7b11\uff0c\u5374\u6ca1\u6709\u8bf4\u4ec0\u9ebd\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u9053\u2236\u201c\u4f60\u4eec\u770b\u4ed6\u7b11\u5f97\u8fd9\u526f\u602a\u6837\u5b50\uff0c\u5c31\u597d\u50cf\u4ec0\u9ebd\u4e8b\u90fd\u7792\u4e0d\u8fc7\u4ed6\u4f3c\u7684\uff0c\u5176\u5b9e\u4ed6\u8fd9\u4eba\u6700 \u5927\u7684\u672c\u4e8b\u5c31\u662f\u7b11\uff0c\u7b11\u5f97\u8ba9\u522b\u4eba\u4e5f\u731c\u4e0d\u900f\u4ed6\u7a76\u7adf\u77e5\u9053\u4e86\u591a\u5c11\u3002\u201d \u3000\u3000\u674e\u7ea2\u8896\u5ae3\u7136\u9053\u2236\u201c\u4f60\u7a76\u7adf\u4e0d\u6127\u662f\u4ed6\u7684\u77e5\u5df1\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\u2236\u201c\u90a3\u4eba\u7684\u4e00\u5f20\u8138\u6b7b\u6b7b\u677f\u677f\uff0c\u5168\u65e0\u8868\u60c5\uff0c\u6211\u4e5f\u65e9\u5c31\u6000\u7591\u4ed6\u8138\u4e0a\u6709\u82b1\u6837\u4e86\uff0c\u53ef\u662f \u5374\u504f\u504f\u77a7\u4e0d\u51fa\u4e1d\u6beb\u7834\u7efd\u6765\u3002\u201d \u3000\u3000\u82cf\u84c9\u84c9\u9053\u2236\u201c\u8fd9\u53ea\u56e0\u4ed6\u6234\u7684\u90a3\u5f20\u4eba\u76ae\u9762\u5177\uff0c\u548c\u6c5f\u6e56\u5e38\u89c1\u7684\u4e0d\u540c\uff0c\u90a3\u786e\u662f\u9876\u5c16\u7684\u597d\u624b\u5236\u9020 \u51fa\u6765\u7684\uff0c\u53ef\u79f0\u5f97\u4e0a\u662f\u6b64\u4e2d\u795e\u54c1\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\u2236\u201c\u6c5f\u6e56\u4e2d\u80fd\u5236\u9020\u8fd9\u79cd\u4eba\u76ae\u9762\u5177\u7684\u4eba\u4e00\u5411\u4e0d\u591a\uff0c\u8fd1\u4e94\u5341\u5e74\u6765\uff0c\u7cbe\u65bc\u6b64\u9053\u7684\u4eba\u4e00 \u5171\u4e5f\u4e0d\u8d85\u8fc7\u5341\u4e2a\uff0c\u5374\u53ea\u6709\u53c1\u4e2a\u80fd\u79f0\u5f97\u4e0a\u662f\u597d\u624b\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u5ffd\u7136\u95ee\u9053\u2236\u201c\u4f60\u53ef\u77e5\u9053\u662f\u90a3\u53c1\u4e2a\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\u2236\u201c\u7b2c\u4e00\u4eba\u53eb\u201c\u5c0f\u795e\u7ae5\u201d\uff0c\u53ea\u56e0\u4ed6\u4e03\u516b\u5c81\u65f6\u5c31\u5f88\u6709\u540d\uff0c\u4f46\u6d3b\u4e0d\u5230\u4e8c\u5341\u51e0\u5c81\u5c31\u6b7b \u4e86\uff0c\u80fd\u505a\u4eba\u76ae\u9762\u5177\u7684\u4eba\uff0c\u53ef\u8bf4\u6ca1\u6709\u4e00\u4e2a\u597d\u4e1c\u56db\uff0c\u53ea\u6709\u4ed6\u8fd8\u4e0d\u7b97\u592a\u574f\u3002\u201d \u3000\u3000\u4ed6\u621b\u7136\u987f\u4f4f\u8bed\u58f0\uff0c\u53ea\u56e0\u4ed6\u53d1\u73b0\u82cf\u84c9\u84c9\u7684\u8138\u4e0a\u7adf\u5ffd\u7136\u9732\u51fa\u4e86\u60b2\u4f24\u4e4b\u8272\uff0c\u8fde\u773c\u5708\u513f\u90fd\u6709\u4e9b\u7ea2 \u4e86\u3002 \u3000\u3000\u674e\u7ea2\u8896\u773c\u73e0\u4e8e\u4e00\u8f6c\uff0c\u62a2\u7740\u9053\u2236\u201c\u7b2c\u4e8c\u4e2a\u4eba\u53eb\u201c\u5343\u9762\u4eba\u9b54\u201d\uff0c\u591a\u5e74\u524d\u5c31\u88ab\u201c\u94c1\u8840\u5927\u65d7\u95e8\u201d \u7684\u94c1\u4e2d\u68e0\u94c1\u5927\u4fa0\u6740\u4e86\uff0c\u800c\u4e14\u8fd8\u5c06\u4ed6\u8d39\u4e86\u4e00\u751f\u5fc3\u8840\u5efa\u9020\u7684\u201c\u4e07\u5999\u5bab\u201d\uff0c\u70e7\u6210\u4e00\u7247\u74e6\u783e\uff0c\u4ed6\u4eec\u5236 \u4f5c\u7684\u4eba\u76ae\u9762\u5177\uff0c\u4e5f\u6ca1\u6709\u4e00\u5f20\u7559\u4e0b\u6765\u7684\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u9053\u2236\u201c\u8fd8\u6709\u4e00\u4e2a\u4eba\u5462\uff1f\u201d \u3000\u3000\u674e\u7ea2\u8896\u54ac\u7740\u5634\u5507\uff0c\u9053\u2236\u201c\u8fd9\u4eba\u7684\u540d\u5b57\u6211\u4e00\u60f3\u8d77\u6765\u5c31\u6076\u5fc3\uff0c\u8fd8\u662f\u4e0d\u8981\u8bf4\u7684\u597d\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u9053\u2236\u201c\u4ed6\u96be\u9053\u6bd4\u201c\u5343\u9762\u4eba\u9b54\u201d\u8fd8\u8981\u6076\u6bd2\uff1f\u201d \u3000\u3000\u674e\u7ea2\u8896\u9053\u2236\u201c\u5343\u9762\u4eba\u9b54\u6700\u591a\u4e5f\u53ea\u4e0d\u8fc7\u662f\u5fc3\u72e0\u624b\u8fa3\uff0c\u6b8b\u5fcd\u6076\u6bd2\u800c\u5df2\uff0c\u4f46\u8fd9\u4eba\u5374\u662f\u65e2\u5351\u9119\uff0c \u53c8\u65e0\u803b\uff0c\u4ec0\u9ebd\u4e0d\u8981\u8138\u7684\u4e8b\u90fd\u505a\u5f97\u51fa\u6765\uff0c\u7b80\u76f4\u5c31\u4e0d\u662f\u4e2a\u4eba\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u9ed8\u7136\u534a\u664c\uff0c\u52a8\u5bb9\u9053\u2236\u201c\u4f60\u8bf4\u7684\u83ab\u975e\u662f\u90a3\u4e0d\u7537\u4e0d\u5973\u7684\u4eba\u5996\u201c\u96c4\u5a18\u5b50\u201d\u9ebd\uff1f\u201d \u3000\u3000\u674e\u7ea2\u8896\u6068\u6068\u9053\u2236\u201c\u5c31\u662f\u4ed6\uff0c\u6c5f\u6e56\u4e2d\u65e0\u8bba\u9ed1\u767d\u4e24\u9053\uff0c\u6bcf\u4e2a\u4eba\u90fd\u6068\u4e0d\u5f97\u5c06\u4ed6\u788e\u5c38\u4e07\u6bb5\uff0c\u53e4\u5f80 \u4eca\u6765\uff0c\u53ea\u6015\u4ece\u6765\u4e5f\u6ca1\u6709\u4e00\u4e2a\u4eba\u7ed3\u4ec7\u6bd4\u4ed6\u66f4\u591a\u7684\uff0c\u6240\u4ee5\u4ed6\u7ec8\u5e74\u4e1c\u8eb2\u897f\u85cf\uff0c\u5c31\u9760\u4ed6\u5236\u4f5c\u7684\u4eba\u76ae\u9762 \u5177\u6765\u9003\u907f\u4ec7\u5bb6\u7684\u8ffd\u8e2a\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u9053\u2236\u201c\u548c\u9ec4\u8001\u524d\u8f88\u4e00\u9f50\u6765\u7684\u4eba\uff0c\u96be\u9053\u5c31\u662f\u4ed6\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u5fae\u7b11\u9053\u2236\u201c\u9ec4\u8001\u524d\u8f88\u4e00\u751f\u6b63\u76f4\uff0c\u600e\u4f1a\u548c\u8fd9\u79cd\u4eba\u4e3a\u4f0d\uff0c\u4f55\u51b5\uff0c\u90a3\u96c4\u5a18\u5b50\u867d\u7136\u72e1\u733e\u5584 \u53d8\uff0c\u8f7b\u529f\u5251\u6cd5\u4e5f\u7b97\u4e0d\u5f31\uff0c\u4f46\u5341\u51e0\u5e74\u524d\u4fbf\u5df2\u6076\u8d2f\u6ee1\u76c8\u4e86\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u53f9\u9053\u2236\u201c\u6211\u4ece\u5c0f\u5728\u6c99\u6f20\u91cc\uff0c\u5bf9\u4e2d\u539f\u6b66\u6797\u7684\u638c\u6545\uff0c\u672c\u5c31\u5f88\u964c\u751f\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u5fae\u7b11\u7740\u63a5\u9053\u2236\u201c\u62e5\u7fe0\u5c71\u5e84\u4e00\u5411\u5bb6\u98ce\u4e25\u6b63\uff0c\u81ea\u7136\u66f4\u7edd\u4e0d\u4f1a\u63d0\u8d77\u8fd9\u79cd\u6deb\u8d3c\u7684\u540d\u5b57\uff0c\u4f46 \u96c4\u5a18\u5b50\u4f0f\u8bdb\uff0c\u5728\u5f53\u65f6\u5374\u7684\u786e\u662f\u4ef6\u8f70\u52a8\u4e00\u65f6\u7684\u5927\u4e8b\uff0c\u6709\u5f88\u591a\u4eba\u751a\u81f3\u4e0d\u60dc\u5343\u91cc\u8fe2\u8fe2\u7684\u8d76\u53bb\u770b\u4ed6\u7684 \u5c38\u4f53\uff0c\u4e3a\u7684\u53ea\u662f\u8981\u4ece\u4ed6\u5c38\u8eab\u4e0a\u5272\u4e0b\u4e00\u5757\u8089\u6765\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u9053\u2236\u201c\u6c5f\u6e56\u4e2d\u6ca1\u6709\u4eba\u80fd\u89c1\u5230\u4ed6\u7684\u771f\u9762\u76ee\uff0c\u53c8\u600e\u77e5\u90a3\u5c38\u4f53\u5c31\u662f\u4ed6\u5462\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\u2236\u201c\u53ea\u56e0\u6740\u4ed6\u7684\u4eba\u4e0d\u4f46\u5c06\u4ed6\u7684\u5c38\u8eab\u9ad8\u9ad8\u540a\u8d77\uff0c\u8fd8\u5728\u4e0a\u9762\u7528\u6731\u7b14\u5199\u4e86\u51e0\u884c\u5b57\uff0c\u5927 \u610f\u662f\u8bf4\u2236\u8fd9\u4eba\u4fbf\u662f\u91c7\u82b1\u6deb\u8d3c\u96c4\u5a18\u5b50\uff0c\u6240\u4ee5\u795e\u6c34\u5bab\u624d\u5c06\u4e4b\u9664\u53bb\uff0c\u4e3a\u5929\u4e0b\u7684\u5973\u4eba\u9664\u5bb3\u3002\u201d \u3000\u3000\u67f3\u65e0\u7709\u5931\u58f0\u9053\u2236\u201c\u795e\u6c34\u5bab\uff1f\u8fd9\u96c4\u5a18\u5b50\u96be\u9053\u4e5f\u662f\u6b7b\u5728\u201c\u6c34\u6bcd\u9634\u59ec\u201d\u624b\u4e0a\u7684\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\u2236\u201c\u4e0d\u9519\uff0c\u5c31\u56e0\u4e3a\u6740\u4ed6\u7684\u4eba\u4e43\u662f\u795e\u6c34\u5bab\u4e3b\uff0c\u6240\u4ee5\u6c5f\u6e56\u4e2d\u4eba\u624d\u786e\u4fe1\u90a3\u5c38\u8eab\u5fc5\u662f\u96c4 \u5a18\u5b50\u65e0\u7591\uff0c\u56e0\u4e3a\u795e\u6c34\u5bab\u4e3b\u7edd\u4e0d\u4f1a\u5f04\u9519\u7684\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u4e00\u76f4\u5728\u671b\u7740\u82cf\u84c9\u84c9\uff0c\u6b64\u523b\u5ffd\u7136\u9053\u2236\u201c\u8fd9\u96c4\u5a18\u5b50\u7684\u4eba\u867d\u6b7b\u4e86\uff0c\u4ed6\u505a\u7684\u4eba\u76ae\u9762\u5177\u8bf4\u4e0d \u5b9a\u8fd8\u6709\u51e0\u5f20\u7559\u4e0b\u6765\uff0c\u90a3\u9ed1\u886b\u5251\u5ba2\u5934\u4e0a\u6234\u7684\u9762\u5177\uff0c\u8bf4\u4e0d\u5b9a\u5c31\u662f\u4ed6\u7684\u3002\u201d \u3000\u3000\u674e\u7ea2\u8896\u9053\u2236\u201c\u7edd\u4e0d\u4f1a\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u5931\u7b11\u9053\u2236\u201c\u90a3\u9762\u5177\u4e0a\u53c8\u6ca1\u6709\u5199\u4e0a\u62db\u724c\uff0c\u4f60\u600e\u80fd\u5982\u6b64\u80af\u5b9a\uff1f\u201d \u3000\u3000\u674e\u7ea2\u8896\u77aa\u4e86\u4ed6\u4e00\u773c\uff0c\u9053\u2236\u201c\u56e0\u4e3a\u8fd9\u96c4\u5a18\u5b50\u957f\u5f97\u672c\u5c31\u6709\u4e9b\u5a18\u5a18\u8154\uff0c\u5374\u81ea\u8d1f\u4e3a\u5929\u4e0b\u7b2c\u4e00\u4e2a\u7f8e \u7537\u4e8e\uff0c\u6240\u4ee5\u4ed6\u4f5c\u7684\u9762\u5177\uff0c\u4e5f\u90fd\u662f\u7f8e\u7537\u5b50\u7684\u6a21\u6837\uff0c\u7edd\u4e0d\u4f1a\u50cf\u90a3\u4eba\u6234\u7684\u9762\u5177\u90a3\u9ebd\u5446\u677f\u5e73\u51e1\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\u2236\u201c\u55ef\uff01\u6709\u9053\u7406\u3002\u201d \u3000\u3000\u674e\u7ea2\u8896\u9053\u2236\u201c\u5c31\u56e0\u4e3a\u4ed6\u5236\u4f5c\u7684\u9762\u5177\u5f88\u7cbe\u5de7\uff0c\u6240\u4ee5\u4ed6\u4e00\u76f4\u5c06\u4e4b\u73cd\u5982\u62f1\u74a7\uff0c\u5c0f\u795e\u7ae5\u548c\u5343\u9762\u4eba \u9b54\u5236\u7684\u9762\u5177\uff0c\u6c5f\u6e56\u4e2d\u8fd8\u5076\u6709\u7559\u4f20\uff0c\u4f46\u4ed6\u5236\u7684\u9762\u5177\uff0c\u5374\u4ece\u6765\u6ca1\u6709\u4eba\u89c1\u5230\u8fc7\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u62a2\u7740\u9053\u2236\u201c\u4f55\u51b5\uff0c\u4ed6\u65e2\u7136\u662f\u6b7b\u5728\u795e\u6c34\u5bab\u4e3b\u624b\u91cc\u7684\uff0c\u4ed6\u7eb5\u6709\u9762\u5177\u7559\u4e0b\uff0c\u4e5f\u5fc5\u5b9a\u90fd\u5728 \u9634\u59ec\u624b\u4e0a\uff0c\u7edd\u4e0d\u4f1a\u4f20\u5230\u5916\u9762\u6765\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u779f\u4e86\u82cf\u84c9\u84c9\u4e00\u773c\uff0c\u9053\u2236\u201c\u5343\u9762\u4eba\u9b54\u548c\u96c4\u5a18\u5b50\u65e2\u7136\u90fd\u6ca1\u6709\u9762\u5177\u7559\u4e0b\u6765\uff0c\u90a3\u9ebd\u4ed6\u6234\u7ea6 \u9762\u5177\u5c31\u5fc5\u5b9a\u662f\u5c0f\u795e\u7ae5\u7559\u4e0b\u6765\u7684\u4e86\u3002\u201d \u3000\u3000\u82cf\u84c9\u84c9\u5ffd\u7136\u9053\u2236\u201c\u7edd\u4e0d\u4f1a\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u65e9\u5c31\u89c9\u5f97\u5979\u4e00\u542c\u5230\u201c\u5c0f\u795e\u7ae5\u201d\u8fd9\u540d\u5b57\uff0c\u795e\u60c5\u5c31\u53d8\u5f97\u6709\u4e9b\u5f02\u6837\uff0c\u6240\u4ee5\u6b64\u523b\u4e5f\u4e0d\u518d\u8ffd \u95ee\uff0c\u53ea\u8ba9\u5979\u81ea\u5df1\u8bf4\u4e0b\u53bb\u3002 \u3000\u3000\u82cf\u84c9\u84c9\u679c\u7136\u6309\u7740\u9053\u2236\u201c\u201c\u5c0f\u795e\u7ae5\u201d\u7684\u9762\u5177\uff0c\u4e5f\u7edd\u6ca1\u6709\u6d41\u4f20\u5230\u6c5f\u6e56\u4e2d\u53bb\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u3002\u201c\u54e6\uff1f\u3002\u201d \u3000\u3000\u82cf\u84c9\u84c9\u773c\u5708\u53c8\u7ea2\u4e86\uff0c\u5782\u82dc\u9053\u2236\u201c\u56e0\u4e3a\u4ed6\u7ea6\u9762\u5177\u5168\u90fd\u7559\u7ed9\u6211\u4e86\uff0c\u56e0\u4e3a\u6211\u2026\u2026\u6211\u5c31\u662f\u5b83\u7684\u59b9 \u59b9\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u6014\u4e86\u6014\uff0c\u4ec0\u9ebd\u8bdd\u90fd\u4e0d\u80fd\u8bf4\u4e86\u3002 \u3000\u3000\u4ed6\u65e9\u5df2\u542c\u695a\u7559\u9999\u8bf4\u8fc7\uff0c\u674e\u7ea2\u8896\u3001\u5b8b\u751c\u513f\u548c\u82cf\u84c9\u84c9\u8fd9\u53c1\u4e2a\u5973\u5b69\u5b50\u7684\u8eab\u4e16\u90fd\u5f88\u60b2\u60e8\uff0c\u90fd\u662f\u5b64 \u513f\u3002 \u3000\u3000\u4f46\u4ed6\u5374\u60f3\u4e0d\u5230\u82cf\u84c9\u84c9\u548c\u5c0f\u795e\u7ae5\u4e4b\u95f4\u7adf\u6709\u8fd9\u9ebd\u6df1\u7684\u5173\u7cfb\uff0c\u4ed6\u7684\u5634\u867d\u95ed\u7740\uff0c\u773c\u775b\u5374\u77aa\u7740\u695a\u7559 \u9999\uff0c\u50cf\u662f\u5728\u8bf4\u2236\u201c\u96be\u602a\u522b\u4eba\u90fd\u8bf4\u695a\u7559\u9999\u5316\u8eab\u5343\u4e07\uff0c\u539f\u6765\u5168\u90fd\u662f\u5c0f\u795e\u7ae5\u7684\u6770\u4f5c\uff0c\u4f60\u8fd9\u8001\u81ed\u866b\u4e3a \u4ec0\u9ebd\u4e0d\u65e9\u544a\u8bc9\u6211\u5462\uff0c\u96be\u9053\u8fd8\u60f3\u7792\u7740\u6211\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u4e86\u7b11\uff0c\u9053\u2236\u201c\u4eba\u5bb6\u4e0d\u613f\u610f\u4ee5\u771f\u9762\u76ee\u89c1\u4eba\uff0c\u90a3\u4e5f\u662f\u4eba\u5bb6\u7684\u81ea\u7531\uff0c\u6211\u4eec\u4e5f\u4e0d\u5fc5\u8ffd\u6839 \u7a76\u67e2\u53bb\u95ee\u4eba\u5bb6\u7684\u9762\u5177\u662f\u4ece\u90a3\u91cc\u6765\u7684\uff0c\u53cd\u6b63\u4eba\u5bb6\u5bf9\u6211\u4eec\u5e76\u6ca1\u6709\u6076\u610f\u3002\u201d \u3000\u3000\u4ed6\u4e0d\u8ba9\u522b\u4eba\u8bf4\u8bdd\uff0c\u63a5\u7740\u53c8\u9053\u2236\u201c\u6211\u65b9\u624d\u53bb\u5411\u674e\u8001\u524d\u8f88\u9053\u522b\u548c\u9053\u8c22\u7684\u65f6\u5019\uff0c\u4ed6\u4eec\u8fd8\u5728\u90a3 \u91cc\uff0c\u597d\u50cf\u5728\u7b49\u7740\u6211\u4f3c\u7684\uff0c\u9ec4\u8001\u5251\u5ba2\u89c1\u5230\u6211\uff0c\u5c31\u5c06\u6211\u62c9\u5230\u4e00\u8fb9\uff0c\u5bf9\u6211\u8bf4\u2236\u4ed6\u8fd9\u670b\u53cb\u662f\u4e2a\u5f88\u53ef\u601c \u7684\u4eba\uff0c\u6709\u5f88\u591a\u96be\u8a00\u7684\u82e6\u8877\uff0c\u5e0c\u671b\u6211\u4eec\u539f\u8c05\u4ed6\u3002\u201d \u3000\u3000\u674e\u7ea2\u8896\u9053\u2236\u201c\u539f\u8c05\u4ed6\u4ec0\u9ebd\uff1f\u9ec4\u9c81\u76f4\u4e3a\u4f55\u4f1a\u5ffd\u7136\u5bf9\u4f60\u8bf4\u8fd9\u4e9b\u8bdd\u5462\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\u2236\u201c\u8fd9\u2026\u2026\u4e5f\u8bb8\u56e0\u4e3a\u4ed6\u5c31\u662f\u5bf9\u9ec4\u8001\u5251\u5ba2\u8bf4\u51fa\u795e\u6c34\u5bab\u3001\u83e9\u63d0\u5eb5\u79d8\u5bc6\u7684\u4eba\uff0c\u6240\u4ee5\u9ec4 \u8001\u5251\u5ba2\u5e0c\u671b\u6211\u4eec\u4e0d\u8981\u518d\u6765\u8ffd\u7a76\u8fd9\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\u2236\u201c\u6240\u4ee5\u4f60\u4e5f\u5c31\u4e0d\u51c6\u5907\u518d\u8ffd\u7a76\u4e86\uff0c\u662f\u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\u2236\u201c\u6211\u76f8\u4fe1\u9ec4\u8001\u5251\u5ba2\u7edd\u4e0d\u4f1a\u9a97\u6211\uff0c\u66f4\u4e0d\u4f1a\u9677\u5bb3\u6211\uff0c\u6211\u65e2\u7136\u7b54\u5e94\u4e86\u4ed6\uff0c\u4e5f\u5c31\u7edd\u4e0d \u80fd\u5bf9\u4ed6\u98df\u8a00\u3002\u201d \u3000\u3000\u4ed6\u9762\u8272\u5ffd\u7136\u53d8\u5f97\u5f88\u4e25\u8083\uff0c\u6c89\u58f0\u9053\u2236\u201c\u6bcf\u4e2a\u4eba\u90fd\u6709\u6743\u4fdd\u7559\u4ed6\u79c1\u4eba\u7684\u79d8\u5bc6\uff0c\u53ea\u8981\u4ed6\u6ca1\u6709\u4f24\u5bb3 \u5230\u522b\u4eba\u5c31\u6ca1\u6709\u6743\u53bb\u8ffd\u95ee\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u5927\u58f0\u9053\u2236\u201c\u4e0d\u9519\uff0c\u559c\u6b22\u7aa5\u63a2\u522b\u4eba\u9690\u79c1\u7684\uff0c\u5c31\u5fc5\u5b9a\u662f\u5978\u6076\u7684\u5c0f\u4eba\u3002\u201d \u3000\u3000\u9ed1\u73cd\u73e0\u4e00\u76f4\u5728\u56de\u907f\u7740\u695a\u7559\u9999\u7684\u76ee\u5149\uff0c\u4e0d\u6562\u77a7\u4ed6\u3002 \u3000\u3000\u5979\u90a3\u53cc\u6df1\u6c88\u51b7\u6f20\u7684\u5927\u773c\u775b\u91cc\uff0c\u5145\u6ee1\u4e86\u5fe7\u90c1\u4e4b\u8272\uff0c\u5c31\u50cf\u662f\u6f84\u6e05\u7684\u6e56\u6c34\u4e0a\uff0c\u5df2\u7b3c\u7f69\u7740\u4e00\u5c42\u51c4 \u8ff7\u7684\u96fe\u3002 \u3000\u3000\u6b64\u523b\u5979\u5374\u5ffd\u7136\u7ad9\u4e86\u8d77\u6765\uff0c\u5782\u7740\u5934\u9053\u2236\u201c\u6211\u2026\u2026\u6211\u5b9e\u5728\u89c9\u5f97\u5f88\u5bf9\u4e0d\u8d77\u4f60\u4eec\uff0c\u53ef\u662f\u2026\u2026\u73b0\u5728 \u4f60\u4eec\u65e2\u5df2\u53c8\u56e2\u805a\u5728\u4e00\u8d77\uff0c\u6211\u7684\u7f6a\u5b7d\u4e5f\u53ef\u4ee5\u51cf\u8f7b\u4e9b\u3002\u201d \u3000\u3000\u674e\u7ea2\u8896\u5f20\u5927\u773c\u775b\uff0c\u9053\u2236\u201c\u5927\u59d0\uff0c\u4f60\u4e3a\u4ec0\u9ebd\u8981\u8bf4\u8fd9\u6837\u7684\u8bdd\u5462\uff1f\u201d \u3000\u3000\u9ed1\u73cd\u73e0\u4e00\u7b11\uff0c\u9053\u2236\u201c\u53ea\u56e0\u6211\u8981\u8d70\u4e86\uff0c\u6240\u4ee5\u6211\u89c9\u5f97\u8fd8\u662f\u5c06\u8bdd\u8bf4\u51fa\u6765\u7684\u597d\uff0c\u6211\u2026\u2026\u201d \u3000\u3000\u5979\u8bdd\u8fd8\u6ca1\u6709\u8bf4\u5b8c\uff0c\u5b8b\u751c\u513f\u548c\u674e\u7ea2\u8896\u5df2\u62c9\u4f4f\u4e86\u5b83\u7684\u624b\u3002 \u3000\u3000\u5b8b\u751c\u513f\u7740\u6025\u9053\u2236\u201c\u6211\u4eec\u65e2\u5df2\u7ed3\u62dc\u6210\u59d0\u59b9\uff0c\u4f60\u600e\u9ebd\u80fd\u629b\u4e0b\u6211\u4eec\u4e00\u4e2a\u4eba\u8d70\u3002\u201d \u3000\u3000\u9ed1\u73cd\u73e0\u9053\u2236\u201c\u6c99\u6f20\u867d\u7136\u4e0d\u662f\u597d\u5730\u65b9\uff0c\u4f46\u2026\u2026\u4f46\u5374\u662f\u6211\u7684\u5bb6\u2026\u2026\u201d \u3000\u3000\u5979\u4f3c\u4e5f\u60f3\u8d77\u81ea\u5df1\u5e76\u6ca1\u6709\u5bb6\u4e86\uff0c\u8bed\u58f0\u5df2\u54fd\u54bd\u8d77\u6765\u3002 \u3000\u3000\u674e\u7ea2\u8896\u4e5f\u7740\u6025\u9053\u2236\u201c\u6211\u4eec\u7684\u5bb6\uff0c\u5c31\u662f\u4f60\u7684\u5bb6\uff0c\u4f60\u2026\u2026\u4f60\u2026\u2026\u201d \u3000\u3000\u82cf\u84c9\u84c9\u540c\u58f0\u9053\u2236\u201c\u4e0d\u9519\uff0c\u6211\u4eec\u5927\u5bb6\u5728\u4e00\u8d77\uff0c\u5c31\u548c\u4eb2\u751f\u7684\u5144\u5f1f\u59d0\u59b9\u4e00\u6837\u3002\u201d \u3000\u3000\u5b8b\u751c\u513f\u5927\u58f0\u9053\u2236\u201c\u4f60\u82e5\u8981\u8d70\uff0c\u6211\u4e5f\u8ddf\u4f60\u4e00\u9f50\u8d70\u3002\u201d \u3000\u3000\u5979\u4eec\u8bf4\u7684\u662f\u90a3\u9ebd\u8bda\u6073\uff0c\u90a3\u9ebd\u8ba4\u771f\u3002 \u3000\u3000\u9ed1\u73cd\u73e0\u76ee\u4e2d\u7684\u8ff7\u96fe\u5df2\u53d8\u4e3a\u96e8\u70b9\uff0c\u5979\u52c9\u5f3a\u5fcd\u4f4f\u4e0d\u8ba9\u773c\u6cea\u6d41\u4e0b\u6765\uff0c\u4f46\u5374\u5fcd\u4e0d\u4f4f\u779f\u4e86\u695a\u7559\u9999\u4e00 \u773c\uff0c\u50cf\u662f\u5728\u8bf4\u2236\u201c\u5979\u4eec\u90fd\u4e0d\u8ba9\u6211\u8d70\uff0c\u4f60\u5462\uff1f\u201d\u3002 \u3000\u3000\u695a\u7559\u9999\u5fae\u7b11\u9053\u2236\u201c\u6211\u4eec\u867d\u6ca1\u6709\u7ed3\u62dc\u6210\u5144\u59b9\uff0c\u4f46\u5374\u662f\u670b\u53cb\uff0c\u73b0\u5728\u670b\u53cb\u6709\u56f0\u96be\uff0c\u4f60\u600e\u9ebd\u80fd\u629b \u4e0b\u670b\u53cb\u4e00\u8d70\u4e86\u4e4b\u5462\uff1f\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u679c\u7136\u5f88\u6709\u6548\uff0c\u9ed1\u73cd\u73e0\u5e7d\u51fa\u7684\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\u2236\u201c\u4f60\u2026\u2026\u201d \u3000\u3000\u695a\u7559\u9999\u9053\u2236\u201c\u6211\u5e0c\u671b\u4f60\u80fd\u966a\u7ea2\u8896\u548c\u751c\u513f\u5230\u90a3\u83e9\u63d0\u5eb5\u53bb\uff0c\u5979\u4eec\u90fd\u662f\u5b69\u5b50\uff0c\u4e00\u70b9\u6c5f\u6e56\u5386\u7ec3\u90fd \u6ca1\u6709\uff0c\u4f60\u5e94\u8be5\u7167\u987e\u5979\u4eec\u624d\u662f\u3002\u201d \u3000\u3000\u9ed1\u73cd\u73e0\u6c88\u9ed8\u7740\uff0c\u7ec8\u65bc\u7f13\u7f13\u751f\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u5b8b\u751c\u513f\u5c55\u5934\u7b11\u9053\u2236\u201c\u6211\u4eec\u4e00\u5b9a\u542c\u5979\u7684\u8bdd\uff0c\u7edd\u4e0d\u8c03\u76ae\u6363\u86cb\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u201c\u5657\u54e7\u201d\u4e00\u7b11\uff0c\u9053\u2236\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u4f60\u672c\u6765\u662f\u5f88\u8c03\u76ae\u6363\u86cb\u7684\u4e86\u3002\u201d \u3000\u3000\u5b8b\u751c\u513f\u77aa\u4e86\u4ed6\u4e00\u773c\uff0c\u5374\u54ac\u7740\u5634\u5507\u7b11\u4e86\u3002 \u3000\u3000\u674e\u7ea2\u8896\u9053\u2236\u201c\u4f60\u5462\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\u2236\u201c\u4f46\u4f60\u4eec\u90fd\u4e0d\u77e5\u9053\u83e9\u63d0\u5eb5\u5728\u90a3\u91cc\uff0c\u6240\u4ee5\u8fd8\u8981\u8bf7\u674e\u516c\u5b50\u4e3a\u4f60\u4eec\u5e26\u8def\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\u2236\u201c\u6211\u548c\u5c0f\u80e1\u4e00\u9053\u8d70\uff0c\u4ece\u53e6\u4e00\u6761\u8def\u8fdb\u795e\u6c34\u5bab\uff0c\u7531\u84c9\u513f\u5e26\u8def\uff0c\u4eca\u5929\u662f\u521d\u4e5d\uff0c\u5047\u5982 \u8fd0\u6c14\u597d\uff0c\u6708\u5706\u4e4b\u591c\uff0c\u6211\u4eec\u5c31\u53ef\u4ee5\u5728\u795e\u6c34\u5bab\u91cc\u78b0\u5934\u4e86\u3002\u201d \u3000\u3000\u674e\u7ea2\u8896\u9053\u2236\u201c\u6211\u4eec\u90fd\u662f\u5973\u4eba\uff0c\u6240\u4ee5\u6700\u591a\u53ea\u4e0d\u8fc7\u662f\u8fdb\u4e0d\u53bb\u795e\u6c34\u5bab\u53bb\uff0c\u7edd\u4e0d\u4f1a\u6709\u4ec0\u9ebd\u5371\u9669 \u7684\uff0c\u4f46\u662f\u4f60\u2026\u2026\u201d \u3000\u3000\u80e1\u94c1\u82b1\u5927\u7b11\u9053\u2236\u201c\u4f60\u653e\u5fc3\uff0c\u90a3\u201c\u6c34\u6bcd\u9634\u59ec\u201d\u65e2\u7136\u662f\u5973\u4eba\uff0c\u5979\u5c31\u7edd\u4e0d\u5fcd\u6740\u6b7b\u8fd9\u8001\u81ed\u866b\u7684\u3002 \u3000\u3000\u695a\u7559\u9999\u6545\u610f\u677f\u7740\u8138\u9053\u2236\u201c\u4e0d\u9519\uff0c\u5979\u6700\u591a\u53ea\u4e0d\u8fc7\u6740\u6b7b\u4f60\u800c\u5df2\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u4e5f\u677f\u8d77\u4e86\u8138\uff0c\u9053\u2236\u201c\u6211\u5012\u4e0d\u6015\u5979\u6740\u6211\uff0c\u5979\u82e5\u8981\u5ac1\u7ed9\u6211\uff0c\u90a3\u5012\u771f\u9ebb\u70e6\u4e86\u3002\u201d \u3000\u3000\u674e\u7ea2\u8896\u3001\u5b8b\u751c\u513f\u65e9\u5df2\u7b11\u5f97\u5f2f\u4e0b\u4e86\u8170\u3002 \u3000\u3000\u5b8b\u751c\u513f\u5403\u5403\u7b11\u9053\u2236\u201c\u5979\u82e5\u5ac1\u7ed9\u4f60\uff0c\u3002\u795e\u6c34\u5bab\u571f\u8981\u6539\u4e3a\u201c\u795e\u9152\u5bab\u201d\u4e86\u3002\u201d \u3000\u3000\u8fd9\u662f\u4e2a\u5c0f\u5c0f\u7684\u5c71\u57ce\uff0c\u518d\u8fdb\u53bb\u5c31\u662f\u7ef5\u4e98\u767e\u91cc\u7684\u5c71\u533a\u3002\u695a\u7559\u9999\u3001\u80e1\u94c1\u5316\u548c\u82cf\u84c9\u84c9\u5230\u8fd9\u91cc\u7684\u65f6 \u5019\uff0c\u5df2\u7136\u508d\u665a\u4e86\u3002 \u3000\u3000\u65e0\u8bba\u5230\u4e86\u4efb\u4f55\u5730\u65b9\uff0c\u80e1\u94c1\u82b1\u7b2c\u4e00\u4ef6\u5927\u4e8b\u5c31\u662f\u8981\u5148\u627e\u4e00\u5bb6\u9152\u94fa\uff0c\u4ed6\u53ef\u4ee5\u4e0d\u5403\u996d\uff0c\u4e0d\u7761\u89c9\uff0c \u9152\u5374\u975e\u559d\u4e0d\u53ef\u3002 \u3000\u3000\u5b81\u9759\u7684\u5c71\u57ce\uff0c\u8857\u9053\u4e0a\u884c\u4eba\u5e76\u4e0d\u591a\uff0c\u8fd9\u65f6\u524d\u9762\u5ffd\u7136\u8d70\u8fc7\u6765\u51e0\u4e2a\u4eba\uff0c\u695a\u7559\u9999\u4e00\u770b\u4ed6\u4eec\u7684\u88c5 \u675f\uff0c\u5c31\u77e5\u9053\u4ed6\u4eec\u5fc5\u662f\u6c5f\u6e56\u5ba2\uff0c\u80e1\u94c1\u82b1\u4e00\u770b\u4ed6\u4eec\u7684\u773c\u795e\uff0c\u5c31\u77e5\u9053\u4ed6\u4eec\u5fc5\u662f\u9152\u9b3c\uff0c\u56e0\u4e3a\u559d\u8fc7\u9152\u7684 \u4eba\u773c\u775b\u90fd\u4f1a\u53d8\u5f97\u548c\u6b7b\u9c7c\u5dee\u4e0d\u591a\u7684\u3002 \u3000\u3000\u559d\u8fc7\u9152\u7684\u4eba\uff0c\u8bf4\u8bdd\u7684\u58f0\u97f3\u4e5f\u7279\u522b\u5927\uff0c\u4ed6\u4eec\u81ea\u5df1\u4ee5\u4e3a\u662f\u5728\u538b\u7740\u55d3\u5b50\u8bf4\u8bdd\uff0c\u4f46\u522b\u4eba\u5df2\u88ab\u4ed6\u4eec \u5435\u6b7b\u4e86\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u6b63\u60f3\u53bb\u5411\u4ed6\u4eec\u6253\u542c\u6253\u542c\u2236\u201c\u5356\u9152\u7684\u5730\u65b9\u5728\u90a3\u91cc\uff1f\u201d \u3000\u3000\u90a3\u51e0\u4e2a\u4eba\u8bf4\u8bdd\u7684\u58f0\u97f3\u5df2\u4f20\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u53ea\u542c\u4e00\u4eba\u9053\u2236\u201c\u54b1\u4eec\u6b63\u5728\u559d\u5f97\u8fc7\u763e\uff0c\u4f60\u4e3a\u4ec0\u9ebd\u8981\u5c06\u6211\u5e26\u8d70\uff1f\u201d \u3000\u3000\u53e6\u4e00\u4eba\u9053\u2236\u201c\u65b9\u624d\u8d70\u8fdb\u201c\u592a\u767d\u697c\u201d\u7684\u90a3\u4e24\u4e2a\u8001\u5934\u5b50\uff0c\u4f60\u53ef\u77e5\u9053\u662f\u8c01\uff1f\u201d \u3000\u3000\u90a3\u4eba\u77aa\u773c\u9053\u2236\u201c\u662f\u8c01\uff1f\u96be\u9053\u662f\u4f60\u8001\u7684\u4e08\u4eba\u4e0d\u6210\uff1f\u201d \u3000\u3000\u53e6\u4e00\u4eba\u51b7\u7b11\u9053\u2236\u201c\u4ed6\u82e5\u771f\u662f\u6211\u8001\u4e08\u4eba\uff0c\u6211\u5c31\u9732\u8138\u4e86\u2026\u2026\u544a\u8bc9\u4f60\uff0c\u4ed6\u5c31\u662f\u6614\u5e74\u5c06\u74e6\u5d34\u5be8\u5341 \u516b\u5bb6\u5934\u513f\u90fd\u6311\u4e86\u7684\u201c\u541b\u5b50\u5251\u201d\u9ec4\u9c81\u76f4\uff0c\u4f60\u603b\u8be5\u542c\u8bf4\u4ed6\u7684\u4e07\u513f\u5427\uff1f\u201d \u3000\u3000\u90a3\u4eba\u6014\u4e86\u534a\u664c\uff0c\u679c\u7136\u4e0d\u6562\u518d\u54cd\u3002 \u3000\u3000\u7b2c\u53c1\u4eba\u5374\u7b11\u9053\u2236\u201c\u542c\u8bf4\u8fd9\u8001\u5934\u5b50\u548c\u4eba\u52a8\u624b\u7684\u65f6\u5019\uff0c\u5148\u5c31\u544a\u8bc9\u4f60\u4ed6\u8981\u7528\u4ec0\u9ebd\u62db\u5f0f\uff0c\u8fd9\u8bdd\u53ef \u662f\u771f\u7684\u9ebd\uff1f\u201d \u3000\u3000\u90a3\u4eba\u9053\u2236\u201c\u4f60\u5c31\u7b97\u77e5\u9053\u4ed6\u8981\u7528\u4ec0\u9ebd\u62db\u5f0f\uff0c\u8fd8\u662f\u4e00\u6837\u6321\u4e0d\u4f4f\u4ed6\u7684\uff0c\u54b1\u4eec\u8981\u559d\u9152\uff0c\u591a\u7684\u662f\u5730 \u65b9\uff0c\u4f55\u5fc5\u8ddf\u4ed6\u5728\u4e00\u8d77\u60f9\u9ebb\u70e6\u3002\u201d \u3000\u3000\u4ed6\u4eec\u4e00\u9762\u8bf4\u7740\u8bdd\uff0c\u4e00\u9762\u5df2\u81ea\u695a\u7559\u9999\u8eab\u65c1\u8d70\u8fc7\uff0c\u5176\u4e2d\u6709\u4e2a\u4eba\u8fd8\u77aa\u4e86\u82cf\u84c9\u84c9\u4e00\u773c\uff0c\u4f3c\u4e4e\u8981\u5403 \u5403\u8c46\u8150\uff0c\u63e9\u63e9\u6cb9\u3002 \u3000\u3000\u4f46\u4e00\u60f3\u201c\u541b\u5b50\u5251\u201d\u5c31\u5728\u9644\u8fd1\uff0c\u4ed6\u4e5f\u5c31\u8001\u5b9e\u4e86\u3002 \u3000\u3000\u7b49\u4ed6\u4eec\u8d70\u8fdc\uff0c\u80e1\u94c1\u82b1\u624d\u7b11\u7740\u9053\u2236\u201c\u60f3\u4e0d\u5230\u9ec4\u9c81\u76f4\u4e5f\u5230\u8fd9\u91cc\u6765\u4e86\uff0c\u5012\u771f\u662f\uff0c\u4eba\u751f\u4f55\u5904\u4e0d\u76f8 \u9022\uff0c\u5374\u4e0d\u77e5\u4ed6\u9152\u91cf\u5982\u4f55\uff0c\u6211\u53bb\u627e\u4ed6\u559d\u4e24\u676f\u5427\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u6c88\u541f\u9053\u2236\u201c\u4e5f\u8bb8\u4ed6\u5e76\u4e0d\u60f3\u89c1\u6211\u4eec\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\u2236\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u4ed6\u773c\u73e0\u5b50\u4e00\u8f6c\uff0c\u53c8\u604d\u7136\u9053\u2236\u201c\u90a3\u4e9b\u4eba\u8bf4\u4ed6\u4eec\u6709\u4e24\u4e2a\u4eba\uff0c\u53e6\u4e00\u4e2a\u5fc5\u5b9a\u5c31\u662f\u90a3\u6234\u9762\u5177\u7684\u4eba\uff0c \u4ed6\u4eec\u8bf4\u4e0d\u5b9a\u4e5f\u662f\u8981\u5230\u795e\u6c34\u5bab\u53bb\u7684\uff0c\u5426\u5219\u600e\u4f1a\u8dd1\u5230\u8fd9\u79cd\u9b3c\u5730\u65b9\u6765\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u4f3c\u4e4e\u5728\u6c88\u601d\u7740\uff0c\u5e76\u6ca1\u6709\u56de\u7b54\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u773c\u775b\u4e5f\u4eae\u4e86\uff0c\u9053\u2236\u201c\u4f60\u731c\u5f97\u4e00\u5b9a\u4e0d\u9519\uff0c\u90a3\u4eba\u4e00\u5b9a\u548c\u201c\u795e\u6c34\u5bab\u201d\u6709\u5f88\u6df1\u7684\u5173\u7cfb\uff0c\u5426 \u5219\u4ed6\u4e00\u4e2a\u5927\u7537\u4eba\uff0c\u600e\u9ebd\u4f1a\u5bf9\u201c\u795e\u6c34\u5bab\u201d\u7684\u4e8b\u77e5\u9053\u5f97\u90a3\u9ebd\u6e05\u695a\uff1f\u201d \u3000\u3000\u82cf\u84c9\u84c9\u4e00\u76f4\u9759\u9759\u7684\u542c\u7740\uff0c\u53ea\u6709\u5979\u8fd9\u79cd\u806a\u660e\u7684\u5973\u5b50\uff0c\u624d\u61c2\u5f97\u7537\u4eba\u5728\u8bf4\u8bdd\u7684\u65f6\u5019\uff0c\u5979\u5e94\u8be5\u95ed \u8d77\u81ea\u5df1\u7684\u5634\u3002 \u3000\u3000\u695a\u7559\u9999\u8003\u8651\u4e86\u5f88\u4e45\uff0c\u5ffd\u7136\u4e00\u7b11\uff0c\u9053\u2236\u201c\u4ed6\u4eec\u65e2\u6709\u96be\u8a00\u7684\u82e6\u8877\uff0c\u6211\u4eec\u5c31\u4e0d\u5fc5\u53bb\u4ee4\u4eba\u96be\u582a\uff0c \u4f46\u65b9\u624d\u90a3\u51e0\u4e2a\u6c5f\u6e56\u5ba2\uff0c\u5374\u663e\u89c1\u7edd\u975e\u5584\u7c7b\uff0c\u6211\u4eec\u5012\u8be5\u7559\u610f\u7559\u610f\u4ed6\u4eec\u624d\u662f\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\u2236\u201c\u5bf9\uff0c\u6211\u8d5e\u6210\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u9053\u2236\u201c\u6211\u4e5f\u77e5\u9053\u4f60\u4e0d\u4f1a\u53cd\u5bf9\uff0c\u56e0\u4e3a\u8ddf\u7740\u4ed6\u4eec\u8d70\uff0c\u975e\u4f46\u6709\u95f2\u4e8b\u53ef\u7ba1\uff0c\u800c\u4e14\u8fd8\u4e00\u5b9a \u6709\u9152\u53ef\u559d\uff0c\u8fd9\u4e24\u6837\u6b63\u90fd\u662f\u4f60\u6700\u559c\u6b22\u7684\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u5927\u7b11\u9053\u2236\u201c\u8001\u81ed\u866b\u679c\u7136\u4e0d\u6127\u6211\u80e1\u94c1\u82b1\u7684\u77e5\u5df1\u3002\u201d \u3000\u3000\u90a3\u51e0\u4e2a\u6c5f\u6e56\u5ba2\u53bb\u7684\u5730\u65b9\u679c\u7136\u6709\u9152\uff0c\u4f46\u5374\u5e76\u6ca1\u6709\u95f2\u4e8b\u53ef\u7ba1\uff0c\u56e0\u4e3a\u8fd9\u51e0\u4eba\u5c45\u7136\u90fd\u5f88\u8001\u5b9e\uff0c\u751a \u81f3\u6ca1\u6709\u4e00\u4e2a\u53d1\u9152\u75af\u7684\u3002 \u3000\u3000\u559d\u5b8c\u4e86\u9152\uff0c\u4ed6\u4eec\u5c45\u7136\u5c31\u627e\u4e86\u5bb6\u5ba2\u6808\uff0c\u5173\u8d77\u623f\u95e8\u6765\u7761\u89c9\uff0c\u8fc7\u4e86\u534a\u664c\uff0c\u53ea\u542c\u9f3e\u58f0\u5982\u96f7\uff0c\u5c45\u7136 \u771f\u7761\u7740\u4e86\u3002 \u3000\u3000\u695a\u7559\u9999\u4e5f\u89c9\u5f97\u5f88\u610f\u5916\uff0c\u80e1\u94c1\u82b1\u53ea\u8981\u6709\u9152\u559d\uff0c\u8fd8\u6ca1\u6709\u559d\u9189\uff0c\u4ed6\u4e5f\u5c31\u5e76\u4e0d\u60f3\u591a\u4e8b\uff0c\u4ed6\u4eec\u81ea\u7136 \u4e0d\u613f\u5728\u665a\u4e0a\u5165\u5c71\uff0c\u65bc\u662f\u4e5f\u5728\u90a3\u5bb6\u5ba2\u6808\u6b47\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u8fd8\u662f\u8001\u6bdb\u75c5\uff1b\u4e0d\u80af\u56de\u623f\u53bb\u7761\u89c9\u3002 \u3000\u3000\u997c\u4e86\u53c1\u66f4\uff0c\u695a\u7559\u9999\u624d\u6253\u7740\u5475\u6b20\u9053\u2236\u201c\u660e\u5929\u54b1\u4eec\u5c31\u8981\u53bb\u627e\u795e\u6c34\u5bab\uff0c\u4f60\u96be\u9053\u4e0d\u60f3\u517b\u8db3\u7cbe\u795e\u505a \u6b63\u4e8b\u9ebd\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u53d1\u7b11\u9053\u2236\u201c\u6211\u4e00\u7761\u591a\u4e86\u5c31\u5934\u6655\uff0c\u8fd8\u662f\u2026\u2026\u201d \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u7a81\u542c\u7a97\u5916\uff0c\u201c\u55e4\u201d\u7684\u4e00\u54cd\u98e8\u3002 \u3000\u3000\u4e00\u4eba\u6c89\u7740\u58f0\u97f3\u9053\u2236\u201c\u695a\u7559\u9999\uff0c\u51fa\u6765\u3002\u201d \u3000\u3000\u8fd9\u4e94\u4e2a\u5b57\u8fd8\u672b\u8bf4\u5b8c\uff0c\u80e1\u94c1\u82b1\u5df2\u7a9c\u51fa\u4e86\u7a97\u5b50\uff0c\u4ed6\u662f\u4ece\u6765\u4e5f\u4e0d\u6015\u522b\u4eba\u6697\u7b97\u7684\uff0c\u695a\u7559\u9999\u4e5f\u53ea\u6709 \u8ddf\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u53ea\u89c1\u4e00\u6761\u9ed1\u5f71\u5728\u524d\u9762\u7684\u5c4b\u80cc\u4e0a\u4e00\u95ea\uff0c\u8fd8\u4f3c\u4e4e\u5411\u695a\u7559\u9999\u62db\u4e86\u62db\u624b\uff0c\u4e00\u7728\u773c\u7684\u529f\u592b\uff0c\u5c31\u5df2\u63a0 \u51fa\u4e86\u4e03\u516b\u4e08\u3002 \u7b2c\u4e8c\u5341\u4e8c\u7ae0\u3000\u4eba\u4e3a\u8d22\u6b7b>> \u53e4\u9f99\u300a\u695a\u7559\u9999\u7cfb\u5217\xb7\u753b\u7709\u9e1f\u300b \u7b2c\u4e8c\u5341\u4e8c\u7ae0\u3000\u4eba\u4e3a\u8d22\u6b7b \u3000\u3000\u8fd9\u4eba\u7684\u8f7b\u529f\u4e4b\u9ad8\uff0c\u5b9e\u4ee4\u695a\u7559\u9999\u90fd\u5403\u4e86\u4e00\u60ca\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u6c89\u58f0\u9053\u2236\u201c\u60f3\u4e0d\u5230\u6211\u4eec\u6ca1\u6709\u627e\u4ed6\u7684\u9ebb\u70e6\uff0c\u4ed6\u5374\u6765\u627e\u6211\u4eec\u7684\u9ebb\u70e6\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u77e5\u9053\u4ed6\u8bf4\u7684\u201c\u4ed6\u201d\uff0c\u5c31\u662f\u6307\u90a3\u4e0d\u613f\u4ee5\u771f\u9762\u76ee\u793a\u4eba\u7684\u9ed1\u886b\u5251\u5ba2\uff0c\u4f46\u695a\u7559\u9999\u5374\u6709\u4e9b\u6000 \u7591\uff0c\u9053\u2236\u201c\u6211\u770b\u8fd9\u4eba\u7edd\u4e0d\u4f1a\u662f\u4ed6\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\u2236\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\u2236\u201c\u4ed6\u9690\u85cf\u81ea\u5df1\u7684\u8eab\u4efd\u72b9\u6050\u4e0d\u53ca\uff0c\u600e\u4f1a\u6765\u627e\u6211\u4eec\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\u2236\u201c\u4e0d\u662f\u4ed6\u662f\u8c01\uff1f\u4f60\u83ab\u5fd8\u8bb0\uff0c\u8fd9\u6837\u7684\u9ad8\u624b\uff0c\u5929\u4e0b\u5e76\u6ca1\u6709\u51e0\u4e2a\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\u2236\u201c\u4f60\u4e5f\u83ab\u8981\u5fd8\u8bb0\uff0c\u8fd9\u91cc\u5df2\u5230\u4e86\u795e\u6c34\u5bab\u7684\u7981\u533a\u4e4b\u5185\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u7b11\u4e86\u7b11\uff0c\u9053\u2236\u201c\u4f46\u8fd9\u4eba\u5374\u662f\u4e2a\u7537\u7684\uff0c\u7edd\u4e0d\u662f\u795e\u6c34\u5bab\u95e8\u4e0b\uff0c\u4f60\u96be\u9053\u8fde\u4ed6\u662f\u7537\u662f\u5973\u90fd \u5206\u4e0d\u51fa\u9ebd\uff1f\u201d \u3000\u3000\u4ed6\u4eec\u4e00\u5f00\u53e3\u8bf4\u8bdd\uff0c\u8eab\u6cd5\u5c31\u6162\u4e86\u4e0b\u6765\uff0c\u8ddd\u79bb\u90a3\u4eba\u5f71\u4e5f\u5c31\u66f4\u8fdc\u4e86\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u76b1\u7709\u9053\u2236\u201c\u5feb\u8ffd\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\u2236\u201c\u4ed6\u65e2\u7136\u6765\u627e\u6211\u4eec\uff0c\u5c31\u4e00\u5b9a\u4f1a\u7b49\u7740\u6211\u4eec\uff0c\u6211\u4eec\u4f55\u5fc5\u7740\u6025\u3002\u201d \u3000\u3000\u53ea\u89c1\u524d\u9762\u90a3\u4eba\u5f71\u8eab\u6cd5\u679c\u7136\u4e5f\u6162\u4e86\u4e0b\u6765\uff0c\u7adf\u505c\u5728\u4e00\u4e2a\u77ee\u5c0f\u7684\u5c4b\u810a\u4e0a\uff0c\u9891\u9891\u5411\u4ed6\u4eec\u4e24\u4eba\u62db \u624b\u3002\u695a\u7559\u9999\u5ffd\u7136\u9053\u2236\u201c\u4f60\u56de\u53bb\u7167\u987e\u84c9\u513f\u5427\uff1a\u83ab\u8981\u53c8\u4e2d\u4e86\u522b\u4eba\u8c03\u864e\u79bb\u5c71\u4e4b\u8ba1\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u4e00\u5fc3\u60f3\u8981\u77a7\u77a7\u8fd9\u8eab\u6000\u7edd\u6280\u7684\u591c\u884c\u4eba\u662f\u8c01\uff0c\u662f\u4e3a\u4ec0\u9ebd\u6765\u627e\u4ed6\u4eec\u7684\uff0c\u4ed6\u5b9e\u5728\u820d\u4e0d\u5f97\u56de \u53bb\u3002 \u3000\u3000\u4f46\u8fd9\u65f6\u695a\u7559\u9999\u5df2\u63a0\u51fa\u5f88\u8fdc\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u53ea\u6709\u53f9\u606f\u7740\u56de\u8f6c\u8eab\uff0c\u5583\u5583\u9053\u2236\u201c\u8ddf\u8001\u81ed\u866b\u5728\u4e00\u8d77\uff0c\u597d\u4e8b\u603b\u8f6e\u4e0d\u5230\u6211\u7684\u3002\u201d \u3000\u3000\u591c\u6df1\u4eba\u9759\uff0c\u5ba2\u6808\u91cc\u706f\u706b\u591a\u5df2\u7184\u706d\uff0c\u53ea\u6709\u4e24\u95f4\u623f\u5b50\u8fd8\u4eae\u7740\u706f\uff0c\u4e00\u95f4\u662f\u4f19\u8ba1\u4eec\u7761\u7684\uff0c\u53e6\u4e00\u95f4 \u5c31\u662f\u695a\u7559\u9999\u7684\u5c4b\u5b50\u3002 \u3000\u3000\u82cf\u84c9\u84c9\u81ea\u7136\u5c31\u4f4f\u5728\u695a\u7559\u9999\u9694\u58c1\u3002 \u3000\u3000\u65c1\u8fb9\u9662\u5b50\u91cc\u7684\u53c1\u95f4\u623f\uff0c\u5c31\u662f\u90a3\u4e9b\u6c5f\u6e56\u4eba\u7761\u7684\uff0c\u4ed6\u4eec\u5c4b\u5b50\u91cc\u7684\u706f\u65e9\u5df2\u7184\u706d\u4e86\uff0c\u9664\u4e86\u9f3e\u58f0\u5916 \u5c31\u542c\u4e0d\u5230\u522b\u7684\u52a8\u9759\u3002 \u3000\u3000\u4f46\u80e1\u94c1\u82b1\u56de\u5230\u5ba2\u6808\u7684\u65f6\u5019\uff0c\u8fd9\u53c1\u95f4\u623f\u7684\u706f\u706b\u5ffd\u7136\u4eae\u4e86\u8d77\u6765\uff0c\u7a97\u7eb8\u4e0a\u5df2\u73b0\u51fa\u5e62\u5e62\u7684\u4eba\u5f71\u3002 \u3000\u3000\u8fd9\u4e9b\u4eba\u6df1\u66f4\u534a\u591c\u91cc\u5ffd\u7136\u722c\u8d77\u6765\u5e72\u4ec0\u9ebd\uff1f \u3000\u3000\u82cf\u84c9\u84c9\u5c4b\u5b50\u91cc\u5e76\u6ca1\u6709\u4ec0\u9ebd\u5f02\u6837\u7684\u52a8\u9759\uff0c\u80e1\u94c1\u82b1\u6c88\u541f\u4e86\u534a\u664c\uff0c\u7d22\u6027\u5728\u5c4b\u810a\u5f8c\u85cf\u4e86\u8d77\u6765\uff0c\u6697 \u4e2d\u7aa5\u63a2\u7740\u90a3\u53c1\u95f4\u5c4b\u5b50\u3002\u3002 \u3000\u3000\u4ed6\u65e9\u5df2\u89c9\u5f97\u90a3\u4e9b\u4eba\u4e0d\u662f\u597d\u8def\u9053\uff0c\u4f46\u82e5\u662f\u4ed6\u4eec\u534a\u591c\u91cc\u8d77\u6765\u662f\u4e3a\u4e86\u8981\u505a\u6848\uff0c\u8fd9\u5c71\u57ce\u4e2d\u5374\u5e76\u6ca1 \u6709\u503c\u5f97\u4ed6\u4eec\u4e0b\u624b\u7684\u5bf9\u8c61\u3002 \u3000\u3000\u4ed6\u4eec\u843d\u811a\u5728\u8fd9\u91cc\uff0c\u663e\u7136\u53e6\u6709\u76ee\u7684\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u773c\u775b\u77aa\u5f97\u5927\u5927\u7684\uff0c\u6697\u9053\u2236\u201c\u4e0d\u7ba1\u4f60\u4eec\u60f3\u5e72\u4ec0\u9ebd\uff0c\u4eca\u5929\u65e2\u7136\u649e\u89c1\u6211\uff0c\u5c31\u6d3b\u8be5\u4f60\u4eec\u5012 \u6963\u3002\u201d \u3000\u3000\u997c\u4e86\u534a\u664c\uff0c\u5de6\u9762\u5c4b\u5b50\u91cc\u7684\u706f\u5ffd\u53c8\u7184\u4e86\uff0c\u4e24\u6761\u4eba\u5f71\u6084\u6084\u63a0\u4e86\u51fa\u6765\uff0c\u7528\u624b\u6307\u5728\u4e2d\u95f4\u90a3\u95f4\u5c4b\u5b50 \u7684\u7a97\u4e0a\u5f39\u4e86\u5f39\uff0c\u9053\u2236\u201c\u53c1\u66f4\u4e86\u3002\u201d \u3000\u3000\u5c4b\u5b50\u7684\u4eba\u5e26\u7740\u7b11\u9053\u2236\u201c\u6211\u4eec\u65e9\u5df2\u51c6\u5907\u597d\u4e86\uff0c\u6b63\u5728\u7b49\u7740\u4f60\u4eec\u54e9\uff01\u201d \u3000\u3000\u8bf4\u8bdd\u95f4\uff0c\u4e5f\u6709\u4e24\u4e2a\u4eba\u63d0\u7740\u5927\u5305\u88b1\u8d70\u51fa\u6765\uff0c\u9053\u2236\u201c\u4f60\u4eec\u5148\u63d0\u7740\u8fd9\u5305\u88b1\uff0c\u6211\u4eec\u53bb\u89e3\u624b\u3002\u201d \u3000\u3000\u5916\u9762\u4e24\u4eba\u7b11\u9a82\u9053\u2236\u201c\u4f60\u4eec\u771f\u662f\u4e61\u4e0b\u4f6c\uff0c\u4e0d\u805a\u8d22\uff0c\u559d\u4e86\u9152\uff0c\u5c3f\u5c31\u6765\u3002\u201d \u3000\u3000\u4ed6\u4eec\u7b11\u9a82\u7740\u521a\u6309\u7740\u5305\u88b1\uff0c\u5c4b\u91cc\u51fa\u6765\u7ea6\u4e24\u4e2a\u4eba\u8896\u5e95\u5ffd\u7136\u5404\u7ffb\u51fa\u4e00\u67c4\u89e3\u8155\u5c16\u5200\uff0c\u201c\u55e4\u201d\u7684\u4e00 \u58f0\uff0c\u524c\u5165\u4e86\u5916\u9762\u4e24\u4eba\u7684\u8116\u5b50\u3002 \u3000\u3000\u4ed6\u4eec\u4e24\u4eba\u95f7\u54fc\u4e00\u58f0\uff0c\u7acb\u523b\u5c31\u5012\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u53e6\u4e24\u4eba\u53f3\u624b\u62bd\u51fa\u5c16\u5200\uff0c\u5de6\u624b\u5df2\u585e\u4e86\u56e2\u68c9\u5e03\u5728\u4ed6\u4eec\u5200\u53e3\u91cc\uff0c\u8fde\u4e00\u6ef4\u8840\u90fd\u6ca1\u6709\u6e85\u51fa\u6765\uff0c\u624b\u6cd5 \u5f53\u771f\u662f\u53c8\u4e7e\u51c0\uff0c\u53c8\u4fd0\u843d\uff0c\u663e\u89c1\u662f\u6740\u4eba\u7684\u8001\u624b\u3002 \u3000\u3000\u8fd9\u53d8\u5316\u59d4\u5b9e\u5927\u51fa\u80e1\u94c1\u82b1\u610f\u6599\u4e4b\u5916\uff0c\u4ed6\u5b9e\u672b\u60f3\u5230\u8fd9\u4e9b\u4eba\u65e2\u672b\u53bb\u6740\u4eba\uff0c\u4e5f\u672b\u53bb\u505a\u6848\uff0c\u53cd\u800c\u5148 \u81ea\u76f8\u6b8b\u6740\u8d77\u6765\u3002 \u3000\u3000\u8fd9\u65f6\u53f3\u9762\u5c4b\u5b50\u4e5f\u63a0\u51fa\u4e24\u4e2a\u4eba\uff0c\u77a7\u89c1\u5916\u9762\u7684\u60c5\u51b5\uff0c\u663e\u7136\u4e5f\u5403\u4e86\u4e00\u60ca\uff0c\u4e24\u4eba\u5012\u9000\u4e00\u6b65\uff0c\u53cd\u624b \u63e1\u4f4f\u5200\u67c4\uff0c\u5389\u58f0\u9053\u2236\u201c\u96f7\u8001\u4e8c\uff0c\u4f60\u60f3\u5e72\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u90a3\u96f7\u8001\u4e8c\u5728\u978b\u5e95\u4e0a\u64e6\u4e7e\u4e86\u5200\u4e0a\u7684\u8840\uff0c\u7b11\u563b\u563b\u9053\u2236\u201c\u6211\u4ec0\u9ebd\u90fd\u4e0d\u60f3\u5e72\uff0c\u53ea\u4e0d\u8fc7\u89c9\u5f97\u4e00\u6837\u4e1c \u897f\u82e5\u662f\u56db\u4e2a\u4eba\u5206\uff0c\u5c31\u8981\u6bd4\u516d\u4e2a\u4eba\u5206\u597d\u5f97\u591a\u3002\u201d \u3000\u3000\u90a3\u4e24\u4eba\u5bf9\u671b\u4e00\u773c\uff0c\u5168\u90fd\u7b11\u4e86\u3002 \u3000\u3000\u96f7\u8001\u4e8c\u9053\u2236\u201c\u54b1\u4eec\u867d\u7136\u5c06\u90a3\u6279\u9e70\u722a\u5b59\u5168\u7529\u8131\u4e86\uff0c\u4f46\u77a7\u8fd9\u6279\u8d27\u773c\u719f\u7684\u4eba\u8fd8\u5927\u6709\u4eba\u5728\uff0c\u8bf4\u4e0d \u5b9a\u5f8c\u9762\u8fd8\u4f1a\u6709\u4eba\u8ddf\u6765\uff0c\u54b1\u4eec\u8fd8\u662f\u5feb\u8d70\u5427\uff01\u201d \u3000\u3000\u80e1\u94c1\u82b1\u8fd9\u624d\u77e5\u9053\u4ed6\u4eec\u679c\u7136\u90fd\u662f\u6c5f\u6d0b\u5927\u76d7\uff0c\u800c\u4e14\u521a\u505a\u4e86\u4e00\u7968\u597d\u4e70\u5356\uff0c\u662f\u4e3a\u4e86\u9003\u907f\u522b\u4eba\u7684\u8ffd \u8e2a\uff0c\u624d\u5230\u8fd9\u5c71\u57ce\u6765\u7684\u3002 \u3000\u3000\u90a3\u5927\u5305\u88b1\u9f13\u9f13\u56ca\u56ca\u7684\uff0c\u4e5f\u4e0d\u77e5\u88c5\u7684\u662f\u4ec0\u9ebd\uff0c\u4f46\u770b\u4ed6\u4eec\u4e00\u7adf\u4e0d\u60dc\u4e3a\u4e86\u8fd9\u7968\u8d27\u81ea\u76f8\u6b8b\u6740\uff0c\u5305 \u88b1\u91cc\u663e\u7136\u7edd\u4e0d\u4f1a\u662f\u5e73\u51e1\u4e4b\u7269\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u7684\u5fc3\u5df2\u75d2\u4e86\uff0c\u624b\u4e5f\u75d2\u4e86\uff0c\u6697\u9053\u2236\u201c\u6211\u82e5\u4e0d\u770b\u770b\u8fd9\u5305\u88b1\u91cc\u88c5\u7684\u662f\u4ec0\u9ebd\uff0c\u4eca\u5929\u665a\u4e0a\u4f11 \u60f3\u7761\u5f97\u7740\u3002\u201d \u3000\u3000\u5176\u5b9e\u4ed6\u5f53\u7136\u4e0d\u4ec5\u662f\u60f3\u770b\u770b\u800c\u5df2\uff0c\u8fd9\u56db\u4eba\u5c31\u50cf\u9001\u4e0a\u95e8\u6765\u7684\u80a5\u732a\uff0c\u4ed6\u82e5\u5c06\u4ed6\u4eec\u63a8\u56de\u53bb\uff0c\u5b9e\u5728 \u5bf9\u4e0d\u8d77\u81ea\u5df1\u3002 \u3000\u3000\u8fd9\u65f6\u96f7\u8001\u4e8c\u5df2\u5c06\u5305\u88b1\u63d0\u4e86\u8d77\u6765\uff0c\u80e1\u94c1\u82b1\u521a\u60f3\u63a0\u4e0b\u53bb\uff0c\u7a81\u89c1\u4e00\u6761\u767d\u5f71\uff0c\u5c31\u50cf\u662f\u4e00\u7247\u96ea\u82b1\u822c \u98d8\u8fc7\u6765\u3002 \u3000\u3000\u96f7\u8001\u4e8c\u4e00\u4ed6\u4eec\u597d\u50cf\u8fd8\u6ca1\u6709\u77a7\u89c1\uff0c\u76f4\u5230\u8fd9\u767d\u8272\u7684\u4eba\u5f71\u98d8\u98d8\u7684\u843d\u5728\u4ed6\u4eec\u9762\u524d\uff0c\u4ed6\u4eec\u624d\u5403\u4e86\u4e00 \u60ca\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u4e5f\u5403\u4e86\u4e00\u7a9f\uff0c\u56e0\u4e3a\u8fd9\u767d\u8272\u7684\u4eba\u5f71\uff0c\u8f7b\u529f\u5b9e\u5728\u9ad8\u660e\uff0c\u4ed6\u731c\u4e0d\u900f\u8fd9\u5c0f\u5c0f\u7684\u5c71\u57ce\u7adf\u4f1a\u6765 \u4e86\u8fd9\u9ebd\u591a\u7edd\u9876\u7684\u6b66\u529f\u9ad8\u624b\u3002 \u3000\u3000\u4ed6\u4e5f\u770b\u4e0d\u6e05\u8fd9\u4eba\u7684\u8138\uff0c\u53ea\u77a7\u89c1\u5b83\u7684\u8eab\u6750\u5f88\u8f7b\u76c8\uff0c\u5934\u53d1\u5f88\u957f\uff0c\u597d\u50cf\u662f\u4e2a\u5f88\u5e74\u8f7b\uff0c\u5f88\u6f02\u4eae\u7684 \u5973\u5b50\u3002 \u3000\u3000\u56e0\u4e3a\u96f7\u8001\u4e8c\u4ed6\u4eec\u8138\u4e0a\u7684\u5403\u60ca\u4e4b\u6001\u867d\u8fd8\u672b\u6d88\u5931\uff0c\u773c\u775b\u5374\u5df2\u772f\u4e86\u8d77\u6765\uff0c\u8272\u8ff7\u8ff7\u7684\u77a7\u7740\u8fd9\u767d\u8863 \u5973\u5b50\u3002 \u3000\u3000\u82e5\u80fd\u4ee4\u7537\u4eba\u7684\u773c\u775b\u772f\u8d77\u6765\uff0c\u8fd9\u5973\u5b50\u5c31\u4e00\u5b9a\u4e0d\u4f1a\u4e11\u7684\uff0c\u80e1\u94c1\u82b1\u5bf9\u8fd9\u79cd\u4e8b\uff0c\u4e00\u5411\u5f88\u6709\u7ecf\u9a8c\u3002 \u3000\u3000\u53ea\u542c\u90a3\u767d\u8863\u5973\u5b50\u9053\u2236\u201c\u5730\u4e0a\u7684\u8fd9\u4e24\u4e2a\u4eba\uff0c\u662f\u4f60\u4eec\u6740\u7684\u9ebd\uff1f\u201d \u3000\u3000\u5979\u8bf4\u8bdd\u7684\u58f0\u97f3\u81ea\u7136\u4e5f\u5f88\u597d\u542c\uff0c\u53ea\u662f\u6709\u4e9b\u51b7\u51b0\u51b0\u7684\u3002 \u3000\u3000\u96f7\u8001\u53c1\u5374\u7b11\u4e86\uff0c\u9053\u2236\u201c\u8fd9\u4e24\u4eba\u662f\u4e0d\u662f\u6211\u4eec\u6740\u7684\uff0c\u4e0e\u59d1\u5a18\u53c8\u6709\u4ec0\u9ebd\u5173\u7cfb\uff0c\u50cf\u59d1\u5a18\u8fd9\u6837\u7684\u7f8e \u4eba\u513f\uff0c\u96be\u9053\u8fd8\u4f1a\u5728\u8859\u95e8\u91cc\u5403\u7cae\u5f53\u5dee\u4e0d\u6210\u3002\u201d \u3000\u3000\u90a3\u767d\u8863\u5973\u5b50\u7f13\u7f13\u9053\u2236\u201c\u4f60\u82e5\u5728\u522b\u7684\u5730\u65b9\u6740\u4eba\uff0c\u83ab\u8bf4\u6740\u4e24\u4e2a\uff0c\u5c31\u7b97\u6740\u4e24\u767e\u4e2a\u4e5f\u548c\u6211\u6ca1\u5173 \u7cfb\uff0c\u4f46\u5728\u8fd9\u91cc\u2026\u2026\u201d \u3000\u3000\u96f7\u8001\u4e8c\u9053\u2236\u201c\u8fd9\u5730\u65b9\u96be\u9053\u6709\u4ec0\u9ebd\u4e0d\u540c\uff1f\u201d \u3000\u3000\u767d\u8863\u5973\u5b50\u9053\u2236\u201c\u8fd9\u5730\u65b9\u4e0d\u80fd\u6740\u4eba\u7684\u3002\u201d \u3000\u3000\u96f7\u8001\u4e8c\u4e00\u7b11\u9053\u2236\u201c\u4f46\u73b0\u5728\u6211\u5df2\u7ecf\u6740\u4eba\uff0c\u59d1\u5a18\u4f60\u8bf4\u5e94\u8be5\u600e\u9ebd\u529e\u5462\uff1f\u201d \u3000\u3000\u4ed6\u5bf9\u8fd9\u5973\u5b50\u672c\u6765\u8fd8\u6709\u754f\u60e7\u4e4b\u5fc3\uff0c\u56e0\u4e3a\u4ed6\u4e5f\u5df2\u770b\u51fa\u8fd9\u5973\u5b50\u7684\u8f7b\u529f\u5f88\u9ad8\u660e\uff0c\u4f46\u73b0\u5728\u4ed6\u4f3c\u4e4e\u5df2 \u88ab\u8fd9\u5973\u5b50\u7684\u7f8e\u8c8c\u5f04\u5f97\u6709\u4e9b\u795e\u9b42\u98a0\u5012\uff0c\u80c6\u5b50\u4e5f\u6e10\u6e10\u5927\u4e86\u8d77\u6765\u3002\u56e0\u4e3a\u7537\u4eba\u5bf9\u7f8e\u4e3d\u5973\u4eba\u7684\u63d0\u9632\u4e4b\u5fc3 \u603b\u662f\u7279\u522b\u5c0f\u7684\u3002 \u3000\u3000\u6240\u4ee5\u7f8e\u4e3d\u7684\u5973\u4eba\u65f6\u5e38\u90fd\u80fd\u4ee4\u7537\u4eba\u4e0a\u5f53\u3002 \u3000\u3000\u90a3\u767d\u8863\u5973\u5b50\u9053\u2236\u201c\u4f60\u65e2\u7136\u5df2\u6740\u4e86\u4eba\uff0c\u5c31\u53ea\u6709\u4e24\u4e2a\u6cd5\u5b50\u4e86\u3002\u201d \u3000\u3000\u96f7\u8001\u4e8c\u4e00\u9053\u2236\u201c\u4ec0\u9ebd\u6cd5\u5b50\uff1f\u201d \u3000\u3000\u767d\u8863\u5973\u5b50\u9053\u2236\u201c\u7b2c\u4e00\u4e2a\u6cd5\u5b50\uff0c\u5c31\u662f\u4f60\u5c06\u8fd9\u4e24\u4eba\u7684\u6b7b\u5c38\u5403\u4e0b\u53bb\uff0c\u800c\u4e14\u8981\u7528\u820c\u5934\u5c06\u5730\u4e0a\u7684\u8840 \u8ff9\u8214\u5f97\u4e7e\u4e7e\u51c0\u51c0\u3002\u201d \u3000\u3000\u96f7\u8001\u4e8c\u5927\u7b11\u9053\u2236\u201c\u6211\u8fd9\u4eba\u4ec0\u9ebd\u90fd\u5403\uff0c\u53ea\u6709\u5927\u8364\u4e0d\u5403\u6b7b\u4eba\uff0c\u5c0f\u8364\u4e0d\u5403\u82cd\u8747\u2026\u2026\u201d \u3000\u3000\u4ed6\u7b11\u58f0\u5ffd\u7136\u505c\u987f\uff0c\u5f77\u4f5b\u5df2\u89c9\u51fa\u8fd9\u5973\u5b50\u5e76\u4e0d\u662f\u5728\u5f00\u73a9\u7b11\u3002\u80e1\u94c1\u82b1\u867d\u770b\u4e0d\u89c1\u5979\u7684\u8138\uff0c\u5374\u4e5f\u77e5 \u9053\u5979\u8138\u8272\u4e00\u5b9a\u53d8\u4e86\u3002 \u3000\u3000\u90a3\u5973\u5b50\u5df2\u7f13\u7f13\u6309\u7740\u9053\u2236\u201c\u4f60\u82e5\u4e0d\u60f3\u5403\u6b7b\u4eba\uff0c\u4e5f\u6ca1\u6709\u5173\u7cfb\uff0c\u53cd\u6b63\u8fd8\u6709\u7b2c\u4e8c\u4e2a\u6cd5\u5b50\u3002\u201d \u3000\u3000\u96f7\u8001\u4e8c\u9053\u2236\u201c\u4ec0\u2026\u2026\u4ec0\u9ebd\u6cd5\u5b50\uff1f\u201d \u3000\u3000\u767d\u8863\u5973\u5b50\u9053\u2236\u201c\u8fd9\u7b2c\u4e8c\u4e2a\u6cd5\u5b50\u5c31\u5bb9\u6613\u591a\u4e86\uff0c\u4f60\u8ddf\u7740\u6211\u6765\u5427\uff01\u201d \u3000\u3000\u5979\u8f7b\u76c8\u7684\u8f6c\u8fc7\u8eab\uff0c\u4eba\u5df2\u63a0\u4e0a\u5899\u5934\u3002 \u3000\u3000\u591c\u51c9\u5982\u6c34\uff0c\u81ea\u5c71\u7684\u90a3\u4e00\u8fb9\u5439\u8fc7\u6765\u7684\u665a\u98ce\uff0c\u8f7b\u67d4\u5f97\u5c31\u5982\u540c\u5929\u9e45\u7684\u7fbd\u6bdb\uff0c\u6de1\u6de1\u7684\u661f\u5149\u7167\u5728\u5979 \u8eab\u4e0a\u3002\u5979\u8f7b\u76c8\u7684\u8eab\u5b50\u5f77\u4f5b\u6eb6\u65bc\u8fd9\u6e29\u67d4\u7684\u79cb\u591c\u4e2d\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u4e00\u5239\u90a3\u95f4\uff0c\u80e1\u94c1\u82b1\u7ec8\u65bc\u77a7\u89c1\u4e86\u5979\u7684\u8138\u3002 \u3000\u3000\u5979\u4e5f\u8bb8\u5e76\u4e0d\u5341\u5206\u7f8e\uff0c\u4f46\u5728\u5982\u6b64\u5e7d\u9759\u7684\u591c\u8272\u91cc\uff0c\u5982\u6b64\u6726\u80e7\u7684\u661f\u5149\u4e0b\uff0c\u5979\u770b\u6765\u5b9e\u5728\u6709\u79cd\u4ee4\u4eba \u4e0d\u53ef\u6297\u62d2\u7684\u5438\u5f15\u529b\u3002 \u3000\u3000\u96f7\u8001\u4e8c\u548c\u4ed6\u7684\u53c1\u4e2a\u4f19\u4f34\uff0c\u4f3c\u4e4e\u53c8\u5df2\u5fd8\u8bb0\u4e86\u4e00\u5207\uff0c\u56db\u4e2a\u4eba\u53ea\u4e0d\u8fc7\u8fdf\u7591\u4e86\u7247\u523b\uff0c\u5c31\u4e00\u9f50\u8ddf\u7740 \u5979\u63a0\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u82cf\u84c9\u84c9\u90a3\u95f4\u5c4b\u5b50\u91cc\u4ecd\u7136\u6ca1\u6709\u4e1d\u6beb\u52a8\u9759\uff0c\u5979\u4ee5\u5df2\u7761\u5f97\u5f88\u719f\uff0c\u80e1\u94c1\u82b1\u53d7\u8fc7\u4e0a\u6b21\u7684\u6559\u8bad\u4e4b\u5f8c\uff0c \u73b0\u5728\u5df2\u4e0d\u6562\u5927\u610f\u3002 \u3000\u3000\u4ed6\u4e5f\u77e5\u9053\u81ea\u5df1\u5e94\u8be5\u770b\u5b88\u5728\u8fd9\u91cc\uff0c\u82cf\u84c9\u84c9\u82e5\u53c8\u4e2d\u4e86\u522b\u4eba\u7684\u6697\u7b97\uff0c\u4ed6\u4e0d\u4f46\u6ca1\u6709\u8138\u89c1\u695a\u7559\u9999\uff0c \u7b80\u76f4\u6ca1\u6709\u8138\u505a\u4eba\u4e86\u3002 \u3000\u3000\u4f46\u90a3\u767d\u8863\u5973\u5b50\u5b9e\u5728\u592a\u7f8e\uff0c\u4eba\u795e\u79d8\uff0c\u5979\u53eb\u90a3\u56db\u4e2a\u6c5f\u6e56\u4eba\u8ddf\u7740\u5979\u8d70\uff0c\u7a76\u7adf\u662f\u4e3a\u4e86\u4ec0\u9ebd\u5462\uff1f\u5979 \u8981\u5e26\u4ed6\u4eec\u5230\u90a3\u91cc\u53bb\uff1f \u3000\u3000\u90a3\u5927\u5305\u88b1\u91cc\u7a76\u7adf\u662f\u4ec0\u9ebd\u4e1c\u897f\uff1f \u3000\u3000\u80e1\u94c1\u82b1\u7684\u597d\u5947\u5fc3\u7b80\u76f4\u5df2\u5feb\u7206\u70b8\u4e86\uff0c\u4ed6\u82e5\u4e0d\u7acb\u523b\u8ddf\u7740\u53bb\u770b\u4e2a\u660e\u767d\uff0c\u8bf4\u4e0d\u5b9a\u7acb\u523b\u5c31\u4f1a\u53d1\u75af \u7684\u3002 \u3000\u3000\u4ed6\u62da\u547d\u7684\u63c9\u7740\u9f3b\u5b50\uff0c\u6b63\u4e0d\u77e5\u8be5\u5982\u4f55\u662f\u597d\uff0c\u8c01\u77e5\u5c31\u5728\u8fd9\u65f6\uff0c\u82cf\u84c9\u84c9\u5ffd\u7136\u81ea\u7a97\u5b50\u91cc\u63a2\u51fa\u5934 \u6765\uff0c\u5411\u4ed6\u62db\u4e86\u62db\u624b\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u4e00\u7eb5\u8eab\u5c31\u51c9\u4e86\u8fc7\u53bb\uff0c\u9053\u2236\u201c\u539f\u6765\u4f60\u8fd8\u6ca1\u6709\u7761\u3002\u201d \u3000\u3000\u82cf\u84c9\u84c9\u62bf\u5634\u7b11\u9053\u2236\u201c\u4f60\u4eec\u559d\u4e86\u9152\u4e4b\u5f8c\u8bf4\u8bdd\u7684\u58f0\u97f3\u8fde\u804b\u5b50\u90fd\u4f1a\u88ab\u5435\u9192\uff0c\u6211\u600e\u9ebd\u7761\u5f97\u7740\u5462\uff1f \u4f55\u51b5\uff0c\u4eca\u5929\u665a\u4e0a\u8fd9\u9662\u5b50\u91cc\u53c8\u8fd9\u9ebd\u70ed\u95f9\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\u2236\u201c\u539f\u6765\u4f60\u90fd\u77a7\u89c1\u4e86\u3002\u201d \u3000\u3000\u82cf\u84c9\u84c9\u9053\u2236\u201c\u6211\u770b\u89c1\u4f60\u4eec\u8ffd\u4e00\u4e2a\u4eba\u51fa\u53bb\uff0c\u7136\u5f8c\u4f60\u53c8\u4e00\u4e2a\u4eba\u56de\u6765\u4e86\u3002\u201d \u3000\u3000\u82e5\u5728\u5e73\u65f6\uff0c\u80e1\u94c1\u82b1\u4e00\u5b9a\u4f1a\u4e58\u673a\u5f00\u5f00\u5979\u548c\u695a\u7559\u9999\u7684\u73a9\u7b11\uff0c\u8ba9\u5979\u7ea2\u4e00\u7ea2\u8138\uff0c\u6216\u8005\u8ba9\u5979\u4e3a\u695a\u7559 \u9999\u7740\u7740\u6025\u3002 \u3000\u3000\u4f46\u73b0\u5728\uff0c\u4ed6\u7684\u5174\u8da3\u5e76\u4e0d\u5728\u8fd9\u4e0a\u9762\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u7acb\u523b\u95ee\u9053\u2236\u201c\u65b9\u624d\u9694\u58c1\u9662\u5b50\u91cc\u53d1\u751f\u7684\u4e8b\uff0c\u4f60\u4e5f\u77a7\u89c1\u4e86\u9ebd\uff1f\u201d \u3000\u3000\u82cf\u84c9\u84c9\u773c\u6ce2\u6d41\u52a8\uff0c\u9053\u2236\uff0c\u201c\u4f60\u662f\u4e0d\u662f\u60f3\u8ddf\u7740\u53bb\u770b\u770b\u4ed6\u4eec\u7684\u4e0b\u843d\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u773c\u775b\u4eae\u4e86\uff0c\u5927\u559c\u9053\u2236\u201c\u4f60\u4e5f\u60f3\u53bb\uff1f\u6211\u4eec\u4e00\u540c\u53bb\u77a7\u77a7\u597d\u4e0d\u597d\uff1f\u201d \u3000\u3000\u82cf\u84c9\u84c9\u9053\u2236\u201c\u6211\u4e0d\u80fd\u53bb\uff0c\u56e0\u4e3a\u90a3\u5973\u5b50\u4e07\u4e00\u5979\u4e5f\u77a7\u89c1\u6211\uff0c\u8bf4\u4e0d\u5b9a\u5c31\u4f1a\u6709\u9ebb\u70e6\u7684\uff0c\u4f46\u4f60\u5374\u6ca1 \u5173\u7cfb\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\u2236\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u82cf\u84c9\u84c9\u9053\u2236\u201c\u56e0\u4e3a\u5979\u8ba4\u5f97\u6211\uff0c\u5374\u4e0d\u8ba4\u5f97\u4f60\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u7acb\u523b\u8ffd\u95ee\u9053\u2236\u201c\u5979\u8ba4\u5f97\u4f60\uff1f\u4f60\u4e5f\u8ba4\u5f97\u5979\u9ebd\uff1f\u5b83\u662f\u4ec0\u9ebd\u4eba\uff1f\u201d \u3000\u3000\u82cf\u84c9\u84c9\u9053\u2236\u201c\u5979\u5c31\u662f\u795e\u6c34\u5b98\u6d3e\u53bb\u627e\u695a\u7559\u9999\u7684\u4eba\uff0c\u53eb\u5bab\u5357\u71d5\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u4e00\u5e97\uff0c\u6014\u4f4f\u4e86\uff0c\u5583\u5583\u9053\u2236\u201c\u96be\u602a\u5979\u529f\u592b\u4e0d\u5f31\uff0c\u539f\u6765\u662f\u201c\u6c34\u6bcd\u201d\u9634\u59ec\u7684\u5f92\u5f1f\u3002\u201d \u3000\u3000\u82cf\u84c9\u84c9\u9053\u2236\u201c\u4f60\u66f4\u60f3\u53bb\u77a7\u77a7\u4e86\uff0c\u662f\u9ebd\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u53c8\u6478\u4e86\u6478\u514b\u5b50\uff0c\u9053\u2236\u201c\u53ef\u662f\u4f60\u2026\u2026\u201d \u3000\u3000\u82cf\u84c9\u84c9\u5ae3\u7136\u9053\u2236\u201c\u4f60\u5c3d\u907f\u53bb\u5427\uff0c\u6211\u53c8\u4e0d\u662f\u5c0f\u5b69\u5b50\uff0c\u96be\u9053\u8fd8\u4e0d\u80fd\u7167\u987e\u81ea\u5df1\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u5927\u559c\u9053\u2236\u201c\u4f60\u771f\u662f\u4e2a\u597d\u59d1\u5a18\uff0c\u96be\u602a\u90a3\u8001\u81ed\u866b\u6068\u4e0d\u5f97\u65f6\u65f6\u523b\u523b\u90fd\u5c06\u4f60\u8854\u5728\u5634\u91cc\uff0c\u8fd8 \u6015\u4e00\u4e0d\u5c0f\u5fc3\u4f1a\u5c06\u4f60\u541e\u4e86\u4e0b\u53bb\u3002\u201d \u3000\u3000\u4ed6\u7ec8\u65bc\u8fd8\u662f\u5c06\u82cf\u84c9\u84c9\u7684\u8138\u8bf4\u7ea2\u4e86\uff0c\u7b49\u4ed6\u63a0\u51fa\u5899\u5916\u5f8c\uff0c\u4ed6\u8fd8\u662f\u89c9\u5f97\u5f88\u5f00\u5fc3\uff0c\u56e0\u4e3a\u4ed6\u5f88\u559c\u6b22 \u770b\u7f8e\u4e3d\u7684\u5c11\u5973\u4eec\u8138\u7ea2\u7684\u6837\u5b50\u3002\u4ed6\u559c\u6b22\u770b\u5230\u5e74\u8f7b\u7684\u7537\u5973\u4eec\u4e24\u60c5\u76f8\u60a6\uff0c\u4ed6\u603b\u89c9\u5f97\u8fd9\u662f\u4e16\u4e0a\u6700\u7f8e\u4e3d \u7684\u4e8b\u3002 \u3000\u3000\u4ed6\u4e5f\u5f88\u66ff\u695a\u7559\u9999\u6b22\u559c\uff0c\u56e0\u4e3a\u4ed6\u89c9\u5f97\u82cf\u84c9\u84c9\u5b9e\u5728\u4e0d\u9519\u3002 \u3000\u3000\u4ed6\u957f\u957f\u547c\u5438\u4e86\u53e3\u6c14\uff0c\u5583\u5583\u9053\u2236\u201c\u90a3\u8001\u81ed\u866b\u5b9e\u5728\u6bd4\u6211\u8d70\u8fd0\u3002\u201d \u3000\u3000\u53ef\u662f\u73b0\u5728\u4e5f\u6709\u4ee4\u80e1\u94c1\u82b1\u70e6\u607c\u7684\u4e8b\uff0c\u5c31\u5728\u8fd9\u51e0\u53e5\u8bdd\u7684\u529f\u592b\u91cc\uff0c\u90a3\u767d\u8863\u5973\u5b50\u548c\u96f7\u8001\u4e8c\u4ed6\u4eec\u5df2 \u8fde\u5f71\u5b50\u770b\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u4ed6\u4e5f\u77e5\u9053\u5bab\u5357\u71d5\u7684\u811a\u7a0b\u4e0d\u4f1a\u6bd4\u4ed6\u6162\u5f88\u591a\uff0c\u4f46\u5c31\u51ed\u96f7\u8001\u4e8c\u4ed6\u4eec\u56db\u4e2a\u4eba\uff0c\u4ed6\u81ea\u4fe1\u5c31\u7b97\u53ea\u7528\u4e00 \u6761\u817f\u8df3\u4e5f\u80fd\u8ffd\u5f97\u4e0a\u4ed6\u4eec\u7684\u3002 \u3000\u3000\u73b0\u5728\u7684\u95ee\u9898\u53ea\u662f\uff0c\u4ed6\u4eec\u662f\u5f80\u90a3\u4e2a\u65b9\u5411\u8d70\u7684\uff1f\u5de6\u9762\u7684\u8def\u901a\u5411\u5e02\u8857\uff0c\u53f3\u9762\u901a\u5411\u5b98\u9053\uff0c\u524d\u9762\u5c31 \u662f\u4ed6\u65b9\u548c\u695a\u7559\u9999\u8ffd\u8e2a\u90a3\u795e\u79d8\u591c\u884c\u4eba\u7684\u65b9\u5411\u3002 \u3000\u3000\u65bc\u662f\u4ed6\u5c31\u7b14\u76f4\u5411\u524d\u9762\u63a0\u51fa\uff0c\u56e0\u4e3a\u4ed6\u8d70\u8fd9\u6761\u8def\uff0c\u5c31\u7b97\u627e\u4e0d\u5230\u5bab\u5357\u71d5\uff0c\u6700\u5c11\u4e5f\u80fd\u9047\u7740\u695a\u7559\u9999 \u7684\u3002 \u3000\u3000\u524d\u9762\u5e76\u6ca1\u6709\u8def\uff0c\u53ea\u662f\u4e00\u91cd\u91cd\u5c4b\u810a\u3002 \u3000\u3000\u4ed6\u8bb0\u5f97\u529b\u624d\u63a0\u8fc7\u8fd9\u4e9b\u5c4b\u810a\u65f6\uff0c\u4e0b\u9762\u7684\u706f\u706b\u90fd\u5df2\u7184\u4e86\uff0c\u5c71\u57ce\u4e2d\u7684\u4eba\u90fd\u77e5\u9053\u5c0f\u5fc3\u706b\u70db\uff0c\u5f88\u5c11 \u6709\u4eba\u70b9\u7740\u706f\u7761\u89c9\u7684\u3002 \u3000\u3000\u4f46\u73b0\u5728\uff0c\u4ed6\u5ffd\u7136\u53d1\u73b0\u524d\u9762\u6709\u5bb6\u4eba\u7684\u706f\u5149\u5f88\u4eae\uff0c\u800c\u4e14\u8fd8\u6709\u4e00\u9635\u9635\u53ee\u549a\u6572\u6253\u4e4b\u58f0\uff0c\u4ece\u9662\u5b50\u91cc \u4f20\u51fa\u3002 \u3000\u3000\u8fd9\u5bb6\u4eba\u7684\u9662\u5b50\u91cc\u5806\u7740\u5f88\u591a\u6728\u5934\uff0c\u5c4b\u6a90\u4e0b\u60ac\u7740\u706f\u7b3c\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u672c\u60f3\u5f80\u65c1\u8fb9\u7ed5\u8fc7\u53bb\uff0c\u4f46\u773c\u89d2\u5374\u5df2\u77a5\u89c1\u9662\u5b50\u91cc\u6709\u4e24\u4e2a\u4eba\u5728\u6572\u7740\u7684\u7adf\u662f\u53e3\u68fa\u6750\u3002 \u3000\u3000\u8fd9\u5bb6\u7adf\u662f\u68fa\u6750\u5e97\u3002 \u3000\u3000\u65e0\u8bba\u591a\u9ebd\u5c0f\u7684\u57ce\u9547\uff0c\u90fd\u4f1a\u6709\u5bb6\u68fa\u6750\u5e97\u7684\uff0c\u56e0\u4e3a\u6bcf\u4e2a\u5730\u65b9\u90fd\u6709\u4eba\uff0c\u6bcf\u4e2a\u4eba\u90fd\u6709\u6b7b\u7684\u4e00\u5929\u3002 \u3000\u3000\u8fd9\u5e76\u4e0d\u5947\u602a\u3002 \u3000\u3000\u5f0a\u6750\u5e97\u91cc\u7684\u4eba\u81ea\u7136\u8981\u9489\u68fa\u6750\uff0c\u68fa\u6750\u91cc\u4e00\u5b9a\u6709\u6b7b\u4eba\u3002 \u3000\u3000\u8fd9\u4e5f\u4e0d\u5947\u602a\u3002 \u3000\u3000\u5947\u602a\u7684\u662f\uff0c\u8fd9\u4e24\u4eba\u4e3a\u4f55\u53c1\u66f4\u534a\u591c\u7684\u5ffd\u7136\u722c\u8d77\u6765\u9489\u68fa\u6750\uff0c\u96be\u9053\u8fd9\u9644\u8fd1\u5ffd\u7136\u6709\u4eba\u534a\u591c\u66b4\u6bd9 \u9ebd\uff1f \u3000\u3000\u7eb5\u7136\u5982\u6b64\uff0c\u4e5f\u53ef\u4ee5\u7b49\u5230\u660e\u5929\u518d\u9489\u5440\uff01\u6b7b\u4eba\u662f\u7edd\u4e0d\u4f1a\u7740\u6025\u7684\u2026\u2026\u6d3b\u4eba\uff0c\u81ea\u7136\u66f4\u4e0d\u4f1a\u6025\u7740\u8fdb \u68fa\u6750\u4e86\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u53c8\u4e0d\u7981\u52a8\u4e86\u597d\u5947\u4e4b\u5fc3\uff0c\u4ed6\u5fcd\u4e0d\u4f4f\u987f\u4f4f\u8eab\u5f62\uff0c\u65bc\u662f\u4ed6\u7acb\u523b\u5c31\u53d1\u89c9\u9662\u5b50\u91cc\u7adf\u6709\u56db\u53e3\u68fa \u6750\u3002 \u3000\u3000\u56db\u65e5\u68fa\u6750\u6709\u53c1\u65e5\u8fd8\u6ca1\u6709\u9489\u4e0a\u68fa\u76d6\u3002 \u3000\u3000\u53c1\u65e5\u68fa\u6750\u91cc\u90fd\u88c5\u7740\u6b7b\u4eba\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u518d\u4e0d\u8fdf\u7591\uff0c\u98de\u8eab\u8dc3\u4e0b\u9662\u5b50\uff0c\u90a3\u6b63\u5728\u9489\u68fa\u6750\u7684\u4e24\u4e2a\u4eba\u5403\u4e86\u4e00\u60ca\uff0c\u8fde\u624b\u91cc\u7684\u9489\u9524\u90fd\u9a87 \u5f97\u98de\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u4e5f\u4e0d\u7406\u4ed6\u4eec\uff0c\u53ea\u662f\u6025\u7740\u53bb\u770b\u90a3\u53c1\u53e3\u68fa\u6750\u91cc\u7684\u6b7b\u4eba\uff0c\u4ed6\u53ea\u77a7\u4e86\u4e00\u773c\uff0c\u8138\u8272\u5df2\u53d8\u4e86\uff0c \u5931\u58f0\u9e6b\u547c\u9053\u2236\u201c\u539f\u6765\u662f\u4ed6\u4eec\u3002\u201d \u3000\u3000\u8fd9\u68fa\u6750\u91cc\u7684\u6b7b\u4eba\uff0c\u7adf\u662f\u96f7\u8001\u4e8c\u548c\u4ed6\u7684\u670b\u53cb\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u7247\u523b\u4e4b\u524d\u8fd8\u4eb2\u773c\u89c1\u5230\u4ed6\u4eec\u9c9c\u8e66\u6d3b\u8df3\u7684\uff0c\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\u8fd9\u56db\u4eba\u73b0\u5728\u5df2\u8eba\u5728\u68fa\u6750\u91cc\u3002 \u3000\u3000\u90a3\u4e24\u4eba\u5df2\u8dea\u4e86\u4e0b\u6765\uff0c\u60ca\u547c\u9053\u2236\u201c\u5927\u7237\u9976\u547d\uff0c\u8fd9\u4e0d\u5173\u5c0f\u4eba\u4eec\u7684\u4e8b\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u89c1\u5230\u4ed6\u4eec\u5df2\u9762\u65e0\u4eba\u8272\uff0c\u77e5\u9053\u4ed6\u4eec\u5fc5\u5b9a\u5df2\u5c06\u4ed6\u8ba4\u505a\u662f\u96f7\u8001\u4e8c\u7684\u670b\u53cb\u4e86\uff0c\u4ed6\u53ea\u6709\u52c9\u5f3a \u7b11\u7740\u9053\u2236\u201c\u6211\u4e5f\u77e5\u9053\u8fd9\u4e0d\u5173\u4f60\u4eec\u7684\u4e8b\uff0c\u4f46\u8fd9\u662f\u600e\u9ebd\u56de\u4e8b\u5462\uff1f\u201d \u3000\u3000\u4e24\u4eba\u5e74\u7eaa\u4e2d\u8f83\u5927\u7684\uff0c\u4f3c\u662f\u68fa\u6750\u5e97\u7684\u8001\u677f\uff0c\u58ee\u8d77\u80c6\u5b50\u9053\u2236\u201c\u5c0f\u4eba\u4eec\u672c\u5df2\u7761\u7740\u4e86\uff0c\u5ffd\u7136\u6709\u4f4d \u4ed9\u5973\u822c\u7684\u59d1\u5a18\uff0c\u5c06\u5c0f\u4eba\u4eec\u53eb\u9192\uff0c\u53eb\u5c0f\u4eba\u51c6\u5907\u56db\u53e3\u68fa\u6750\uff0c\u5728\u9662\u5b50\u91cc\u7b49\u7740\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\u2236\u201c\u662f\u4e2a\u7a7f\u767d\u8863\u670d\u7684\u59d1\u5a18\u9ebd\uff1f\u201d \u3000\u3000\u5f0a\u6750\u5e97\u8001\u677f\u9053\u2236\u201c\u4e0d\u9519\uff0c\u5c0f\u4eba\u4eec\u867d\u89c9\u5947\u602a\uff0c\u4f46\u8fd9\u91cc\u65f6\u5e38\u90fd\u4f20\u8bf4\u6709\u4ed9\u5973\u663e\u7075\u7684\u4e8b\uff0c\u636e\u8bf4\u8fd9 \u5c71\u91cc\u7684\u4ed9\u5973\u5f88\u591a\uff0c\u6240\u4ee5\u5c0f\u4eba\u4eec\u4e5f\u4e0d\u6562\u4e0d\u4ece\u547d\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u51b7\u7b11\u9053\u2236\u201c\u90a3\u4e9b\u4e0d\u662f\u4ed9\u5973\uff0c\u662f\u6c34\u9b3c\u3002\u201d \u3000\u3000\u5f0a\u6750\u5e97\u8001\u677f\u5012\u62bd\u4e86\u53e3\u51c9\u6c14\uff0c\u5934\u58f0\u9053\u2236\u201c\u90a3\u4f4d\u4ed9\u2026\u2026\u6c34\u2026\u2026\u59d1\u5a18\u8fc7\u4e86\u534a\u664c\uff0c\u5c31\u5e26\u4e86\u56db\u2026\u2026 \u56db\u4f4d\u597d\u6c49\u56de\u6765\u4e86\uff0c\u770b\u5979\u5bf9\u4ed6\u4eec\u7684\u6837\u5b50\uff0c\u4e5f\u5e76\u4e0d\u51f6\u72e0\uff0c\u53ea\u662f\u8981\u5176\u4e2d\u4e00\u4e2a\u4eba\u5148\u4ed8\u7ed9\u6211\u4e8c\u5341\u4e24\u94f6 \u5b50\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\u2236\u201c\u90a3\u4eba\u600e\u9ebd\u8bf4\uff1f\u201d \u3000\u3000\u5f0a\u6750\u5e97\u8001\u677f\u9053\u2236\u201c\u90a3\u2026\u2026\u4f4d\u597d\u6c49\u8fd8\u50cf\u662f\u5f88\u6b22\u559c\uff0c\u8bf4\u2236\u201c\u6211\u548c\u4ed6\u4eec\u672c\u5c31\u662f\u670b\u53cb\uff0c\u66ff\u4ed6\u4eec\u4e70 \u53e3\u68fa\u6750\uff0c\u672c\u662f\u5e94\u8be5\u7684\u3002\u201d\u5c0f\u4eba\u542c\u4e86\u8fd9\u8bdd\uff0c\u4e5f\u677e\u4e86\u53e3\u6c14\uff0c\u4ee5\u4e3a\u4ed6\u4eec\u6709\u670b\u53cb\u6b7b\u4e86\uff0c\u6240\u4ee5\u90a3\u4f4d\u59d1\u5a18 \u5c31\u5e26\u4ed6\u4eec\u6765\u4e70\u68fa\u6750\uff0c\u8fd9\u662f\u7167\u987e\u5c0f\u4eba\u7684\u751f\u610f\uff0c\u5c0f\u4eba\u8fd9\u91cc\u8fd8\u5f88\u5c11\u6709\u4e00\u5929\u80fd\u5356\u51fa\u56db\u53e3\u68fa\u6750\u7684\uff0c\u8c01 \u77e5\u2026\u2026\u201d \u3000\u3000\u4ed6\u7259\u9f7f\u6253\u6218\uff0c\u8fde\u8bdd\u90fd\u8bf4\u4e0d\u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u671b\u7740\u68fa\u6750\u91cc\u7684\u96f7\u8001\u4e8c\uff0c\u5fc3\u91cc\u4e5f\u6709\u4e9b\u54ed\u7b11\u4e0d\u5f97\u3002 \u3000\u3000\u96f7\u8001\u4e8c\u53d1\u73b0\u81ea\u5df1\u4ed8\u94b1\u539f\u6765\u662f\u5728\u66ff\u81ea\u5df1\u4e70\u68fa\u6750\u7684\u65f6\u5019\uff0c\u4ed6\u5fc3\u91cc\u53c8\u662f\u4ec0\u9ebd\u6ecb\u5473\u5462\uff1f\u8fd9\u79cd\u6ecb\u5473 \u53ea\u6015\u5f88\u5c11\u6709\u4eba\u80fd\u60f3\u50cf\u5f97\u5230\u3002 \u3000\u3000\u997c\u4e86\u534a\u664c\uff0c\u90a3\u68fa\u6750\u5e97\u8001\u677f\u624d\u6309\u7740\u9053\u2236\u201c\u8c01\u77e5\u9053\u7b49\u5230\u4ed6\u4eec\u4ed8\u8fc7\u94f6\u5b50\u4e4b\u5f8c\uff0c\u90a3\u4f4d\u59d1\u5a18\u5ffd\u7136 \u9053\u2236\u201c\u7b2c\u4e8c\u4e2a\u6cd5\u5b50\u53ea\u4e0d\u8fc7\u8981\u4f60\u4eec\u7684\u547d\uff0c\u90a3\u5b9e\u5728\u5bb9\u6613\u6781\u4e86\u3002\u201d\u5c0f\u4eba\u4eec\u521a\u5927\u5403\u4e00\u60ca\uff0c\u8fd8\u6ca1\u6709\u770b\u6e05 \u662f\u600e\u9ebd\u56de\u4e8b\uff0c\u8fd9\u56db\u4f4d\u597d\u6c49\u5df2\u4e00\u4e2a\u4e2a\u5168\u90fd\u5012\u4e86\u4e0b\u53bb\u3002\u201d \u3000\u3000\u4ed6\u5168\u8eab\u90fd\u5728\u53d1\u6296\uff0c\u5934\u58f0\u9053\u2236\u201c\u5c0f\u4eba\u5e73\u751f\u8fd8\u4ece\u672a\u89c1\u8fc7\u6709\u4eba\u6b7b\u5f97\u8fd9\u9ebd\u5feb\u7684\uff0c\u56db\u4e2a\u6d3b\u751f\u751f\u7684 \u4eba\uff0c\u4e0d\u77e5\u600e\u5730\u4e00\u6765\uff0c\u5c31\u5168\u90fd\u53d8\u6210\u4e86\u6b7b\u5c38\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u4e5f\u542c\u5f97\u5446\u4f4f\u4e86\uff0c\u9053\u2236\u201c\u7136\u5f8c\u5462\uff1f\u201d \u3000\u3000\u5f0a\u6750\u5e97\u8001\u677f\u9053\u2236\u201c\u7136\u5f8c\u2026\u2026\u7136\u5f8c\u90a3\u4f4d\u59d1\u5a18\u5c31\u5ffd\u7136\u4e0d\u89c1\u4e86\u3002\u201d \u3000\u3000\u4ed6\u82e6\u7740\u8138\u63a5\u9053\u2236\u201c\u8fd9\u79cd\u4e8b\u60c5\u8bf4\u522b\u4eba\u542c\uff0c\u522b\u4eba\u4e5f\u4e00\u5b9a\u4e0d\u4f1a\u76f8\u4fe1\u7684\uff0c\u6240\u4ee5\u5c0f\u4eba\u4eec\u53ea\u6709\u8fde\u591c\u5c06 \u68fa\u6750\u9489\u597d\u9001\u8d70\uff0c\u624d\u5927\u7237\u4f60\u2026\u2026\u4f60\u2026\u2026\u201d \u3000\u3000\u80e1\u94c1\u82b1\u4e00\u7b11\u9053\u2236\u201c\u4f60\u653e\u5fc3\uff0c\u6211\u9a6c\u4e0a\u4e5f\u4f1a\u5ffd\u7136\u4e0d\u89c1\u7684\uff0c\u603b\u4e0d\u4f1a\u7ba1\u4f60\u7684\u4e8b\uff0c\u53ef\u662f\uff0c\u8fd9\u56db\u4eba\u672c \u6765\u63d0\u7740\u4e2a\u5927\u5305\u88b1\uff0c\u4f60\u77a7\u89c1\u6ca1\u6709\uff1f\u201d \u3000\u3000\u5f0a\u6750\u5e97\u8001\u677f\u9053\u2236\u201c\u597d\u50cf\u662f\u2026\u2026\u662f\u90a3\u4f4d\u59d1\u5a18\u63d0\u8d70\u4e86\uff0c\u5c0f\u4eba\u90a3\u65f6\u5df2\u5413\u5f97\u773c\u775b\u90fd\u53d1\u4e86\u82b1\uff0c\u5b9e\u5728 \u5e76\u6ca1\u6709\u77a7\u6e05\u695a\u2026\u2026\u201d \u3000\u3000\u4ed6\u8bdd\u672b\u8bf4\u5b8c\uff0c\u80e1\u94c1\u82b1\u679c\u7136\u4e5f\u5ffd\u7136\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u4ee5\u5f8c\u8fd9\u68fa\u6750\u5e97\u8001\u677f\u4e00\u8fde\u75c5\u4e86\u4e03\u5929\uff0c\u82e5\u6709\u4eba\u95ee\u4ed6\u4e03\u5929\u524d\u665a\u4e0a\u5728\u5e72\u4ec0\u9ebd\uff0c\u4ed6\u5c31\u53d1\u8a93\u8bf4\u4ec0\u9ebd\u4e5f \u6ca1\u6709\u505a\uff0c\u53ea\u4e0d\u8fc7\u505a\u4e86\u573a\u5669\u68a6\u3002 \u3000\u3000\u5c0f\u5c0f\u7684\u571f\u5730\u5e99\u65c1\uff0c\u662f\u95f4\u5e73\u623f\uff0c\u91cc\u9762\u6709\u5f88\u591a\u684c\u6905\uff0c\u539f\u6765\u662f\u95f4\u79c1\u587e\u5b66\u5802\uff0c\u4f46\u8001\u5e08\u5e76\u4e0d\u4f4f\u5728\u91cc \u9762\uff0c\u5b66\u751f\u81ea\u7136\u4e5f\u65e9\u5df2\u653e\u5b66\u4e86\uff0c\u91cc\u9762\u4e00\u4e2a\u4eba\u4e5f\u6ca1\u6709\uff0c\u5374\u70b9\u7740\u6839\u8721\u70db\uff0c\u706b\u5149\u95ea\u70c1\uff0c\u770b\u8d77\u6765\u6709\u4e9b\u9634 \u68ee\u68ee\u7684\u3002 \u3000\u3000\u695a\u7559\u9999\u8ffd\u5230\u8fd9\u91cc\uff0c\u524d\u9762\u90a3\u4eba\u5f71\u5ffd\u7136\u505c\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u8fd9\u4eba\u7adf\u662f\u4e2a\u5f88\u4e7e\u5f88\u7626\u7684\u8001\u5934\u5b50\uff0c\u5934\u53d1\u80e1\u5b50\u5168\u90fd\u767d\u4e86\uff0c\u4f46\u8eab\u5b50\u5374\u4ecd\u5f88\u786c\u6717\uff0c\u7ad9\u5728\u90a3\u91cc\u5c31\u50cf \u662f\u4e00\u6746\u67aa\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u56de\u8fc7\u8eab\u5411\u695a\u7559\u9999\u7b11\u4e86\u7b11\uff0c\u9053\u2236\u201c\u695a\u9999\u5e05\u7684\u8f7b\u529f\u679c\u7136\u540d\u4e0d\u865a\u4f20\uff0c\u5929\u4e0b\u65e0\u53cc\uff0c\u5f53\u771f\u4ee4 \u8001\u673d\u5f00\u4e86\u773c\u754c\u3002\u201d \u3000\u3000\u695a\u56fd\u9999\u62b1\u62f3\u9053\u2236\u201c\u524d\u8f88\u8fc7\u5956\u4e86\u3002\u201d \u3000\u3000\u4ed6\u5df2\u8d81\u8bf4\u8bdd\u7684\u65f6\u5019\uff0c\u5c06\u8fd9\u8001\u4eba\u4ed4\u7ec6\u89c2\u5bdf\u4e86\u4e00\u904d\uff0c\u6b64\u523b\u5ffd\u53c8\u7b11\u9053\u2236\u201c\u666e\u5929\u4e4b\u4e0b\uff0c\u82e5\u8fd8\u6709\u5728 \u4e0b\u8ffd\u4e0d\u4e0a\u7684\u4eba\uff0c\u90a3\u5fc5\u5b9a\u5c31\u662f\u201c\u4e07\u91cc\u72ec\u884c\u201d\u6234\u8001\u524d\u8f88\uff0c\u524d\u8f88\u624d\u771f\u4ee4\u665a\u8f88\u5f00\u4e86\u773c\u754c\u3002\u201d \u3000\u3000\u90a3\u8001\u4eba\u6717\u58f0\u5927\u7b11\u9053\u2236\u201c\u542c\u9999\u5e05\u8fd9\u9ebd\u6837\u4e00\u8bf4\uff0c\u8001\u673d\u53cd\u800c\u663e\u5f97\u5c0f\u5bb6\u6c14\u4e86\uff0c\u5176\u5b9e\u8001\u673d\u5e76\u4e0d\u662f\u6545 \u610f\u60f3\u5356\u5f04\u8fd9\u8eab\u89c1\u4e0d\u5f97\u4eba\u7684\u529f\u592b\uff0c\u8001\u673d\u5c06\u9999\u5e05\u5f15\u5230\u8fd9\u91cc\u6765\uff0c\u53ea\u4e0d\u8fc7\u662f\u56e0\u4e3a\u9999\u5e05\u6240\u4f4f\u7684\u90a3\u5bb6\u5ba2\u6808 \u91cc\u6709\u51e0\u4e2a\u4eba\u8ba8\u538c\uff0c\u6240\u4ee5\u8bf4\u8bdd\u6709\u4e9b\u4e0d\u4fbf\u3002\u201d \u3000\u3000\u5f88\u591a\u4eba\u90fd\u4ee5\u4e3a\u5e74\u7eaa\u8d8a\u5927\u7684\u4eba\u8d8a\u8c26\u865a\uff0c\u5176\u5b9e\u4e00\u4e2a\u4eba\u5e74\u7eaa\u8d8a\u5927\uff0c\u5c31\u8d8a\u4e0d\u80af\u670d\u8f93\uff0c\u8d8a\u559c\u6b22\u542c\u522b \u4eba\u5949\u627f\u81ea\u5df1\u3002 \u3000\u3000\u5949\u627f\u8bdd\u82e5\u7531\u4e00\u4e2a\u548c\u81ea\u5df1\u672c\u4e8b\u5dee\u4e0d\u591a\u7684\u540c\u884c\u5634\u91cc\u8bf4\u51fa\u6765\uff0c\u90a3\u66f4\u662f\u8fc7\u763e\u65e0\u6bd4\uff0c\u5929\u4e0b\u6ca1\u6709\u4eba\u4e0d \u559c\u6b22\u542c\u7684\u3002 \u3000\u3000\u6234\u72ec\u884c\u82e5\u4e0d\u60f3\u8981\u695a\u7559\u9999\u77a7\u77a7\u4ed6\u7684\u529f\u592b\uff0c\u4ed6\u4e3a\u4f55\u4e0d\u8d70\u6162\u4e9b\u5462\uff1f \u7b2c\u4e8c\u5341\u4e09\u7ae0\u3000\u72ec\u884c\u5176\u662f>> \u53e4\u9f99\u300a\u695a\u7559\u9999\u7cfb\u5217\xb7\u753b\u7709\u9e1f\u300b \u7b2c\u4e8c\u5341\u4e09\u7ae0\u3000\u72ec\u884c\u5176\u662f \u3000\u3000\u695a\u7559\u9999\u7b11\u4e86\uff0c\u4f46\u77ac\u5373\u76b1\u7709\u9053\u2236\u201c\u524d\u8f88\u6240\u8bf4\u7684\u90a3\u51e0\u4e2a\u8ba8\u538c\u7684\u4eba\uff0c\u83ab\u975e\u662f\u2026\u2026\u201d \u3000\u3000\u6234\u72ec\u884c\u9053\u2236\u201c\u5c31\u662f\u4f4f\u5728\u4f60\u9694\u58c1\u9662\u5b50\u91cc\u7684\u90a3\u51e0\u4e2a\u4eba\uff0c\u8001\u673d\u672c\u662f\u4e3a\u4e86\u8ffd\u8e2a\u4ed6\u4eec\u624d\u5230\u8fd9\u91cc\u6765 \u7684\uff0c\u5374\u60f3\u4e0d\u5230\u5728\u8fd9\u91cc\u9047\u89c1\u9999\u5e05\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u9053\u2236\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u665a\u8f88\u5012\u8be5\u611f\u6fc0\u4ed6\u4eec\u624d\u662f\u4e86\uff0c\u5374\u4e0d\u77e5\u4ed6\u4eec\u7a76\u7adf\u505a\u4e86\u4ec0\u9ebd\u4e8b\uff0c\u7adf \u80fd\u52b3\u52a8\u524d\u8f88\u7684\u5927\u9a7e\uff1f\u201d \u3000\u3000\u6234\u72ec\u884c\u7b11\u4e86\u7b11\uff0c\u9053\u2236\u201c\u8001\u5934\u5b50\u6700\u6015\u5bc2\u5bde\uff0c\u56e0\u4e3a\u4ed6\u4eec\u603b\u6015\u960e\u738b\u4f1a\u8d81\u6ca1\u6709\u4eba\u7684\u65f6\u5019\u5c06\u4ed6\u6293 \u53bb\uff0c\u627e\u8fd9\u8001\u5934\u5b50\u4e5f\u4e0d\u4f8b\u5916\uff0c\u6240\u4ee5\u5c31\u6574\u5929\u7ba1\u522b\u4eba\u7684\u95f2\u4e8b\u3002\u201d \u3000\u3000\u4ed6\u6c89\u4e0b\u4e86\u8138\uff0c\u63a5\u7740\u9053\u2236\u201c\u90a3\u51e0\u4eba\u867d\u662f\u6c5f\u6e56\u4e2d\u7684\u65e0\u540d\u5c0f\u5352\uff0c\u4f46\u6700\u8fd1\u5374\u505a\u4e86\u4ef6\u5f88\u53ef\u6068\u7684\u4e8b\uff0c \u6211\u8001\u5934\u5b50\u5df2\u53d1\u8a93\u8981\u4ed6\u4eec\u7684\u547d\u3002\u201d \u3000\u3000\u4ed6\u65e2\u672b\u8bf4\u51fa\u90a3\u4ef6\u5f88\u53ef\u6068\u7684\u4e8b\u7a76\u7adf\u662f\u4ec0\u9ebd\u4e8b\uff1f\u695a\u7559\u9999\u4e5f\u5c31\u7edd\u4e0d\u591a\u95ee\uff0c\u695a\u7559\u9999\u4ece\u6765\u4e0d\u559c\u6b22\u591a \u5634\u7684\u3002 \u3000\u3000\u6234\u72ec\u884c\u9053\u2236\u201c\u73b0\u5728\u8001\u673d\u65e2\u5df2\u627e\u7740\u4ed6\u4eec\uff0c\u5374\u8fd8\u662f\u6ca1\u6709\u4e0b\u624b\uff0c\u9999\u5e05\u53ea\u6015\u89c9\u5f97\u6709\u4e9b\u5947\u602a\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\u2236\u201c\u6b63\u662f\u3002\u201d \u3000\u3000\u6234\u72ec\u884c\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\u2236\u201c\u8fd9\u53ea\u56e0\u4ed6\u4eec\u50cf\u662f\u88ab\u9b3c\u8ff7\u4e86\u5fc3\u7a8d\uff0c\u4ec0\u9ebd\u5730\u65b9\u4e0d\u9003\uff0c\u7adf\u9003\u5230\u8fd9\u91cc \u6765\uff0c\u4f60\u603b\u8be5\u77e5\u9053\u5728\u8fd9\u9644\u8fd1\u662f\u4e0d\u4fbf\u6740\u4eba\u7684\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u52a8\u5bb9\u9053\u2236\u201c\u4e0d\u9519\uff0c\u665a\u8f88\u4e5f\u542c\u8bf4\u8fc7\uff0c\u201c\u6c34\u6bcd\u201d\u9634\u59ec\u7edd\u4e0d\u8bb8\u522b\u4eba\u5728\u201c\u795e\u6c34\u5bab\u201d\u5468\u56f4\u767e \u91cc\u4e4b\u5185\u52a8\u624b\u6740\u4eba\uff0c\u8c01\u82e5\u72af\u4e86\u5979\u7684\u7981\u4ee4\uff0c\u5979\u5c31\u8981\u8c01\u7684\u547d\u3002\u3002\u201d \u3000\u3000\u6234\u72ec\u884c\u53c8\u7b11\u4e86\u7b11\uff0c\u9053\u2236\u201c\u8001\u673d\u5012\u4e5f\u4e0d\u662f\u6015\u5979\uff0c\u53ea\u662f\u597d\u7537\u4e0d\u8ddf\u5973\u6597\uff0c\u6211\u6d3b\u4e86\u8fd9\u9ebd\u5927\u628a\u5e74 \u7eaa\uff0c\u4f55\u5fc5\u518d\u6765\u8ddf\u5973\u4eba\u6597\u6c14\u5462\uff1f\u201d \u3000\u3000\u8fd9\u8001\u4eba\u679c\u7136\u662f\u59dc\u6842\u4e4b\u6027\uff0c\u8001\u800c\u5f25\u8fa3\uff0c\u7edd\u4e0d\u80af\u5411\u4efb\u4f55\u4eba\u4f4e\u5934\uff0c\u66f4\u4e0d\u80af\u5728\u522b\u4eba\u9762\u524d\u8f93\u4e86\u5634\u3002 \u3000\u3000\u695a\u7559\u9999\u5fc3\u91cc\u867d\u89c9\u5f97\u6709\u4e9b\u597d\u7b11\uff0c\u5374\u53ea\u6709\u9644\u548c\u7740\u9053\u2236\u201c\u524d\u8f88\u8bf4\u7684\u662f\uff0c\u548c\u5973\u4eba\u6597\u6c14\uff0c\u5012\u6963\u7684\u603b \u662f\u7537\u4eba\u3002\u201d \u3000\u3000\u6234\u72ec\u884c\u7b11\u9053\u2236\u201c\u8001\u673d\u65e9\u5c31\u60f3\u548c\u9999\u5e05\u559d\u4e24\u676f\u4e86\uff0c\u53ea\u53ef\u60dc\u53eb\u5316\u5b50\u767b\u4e0d\u4e0a\u5927\u96c5\u4e4b\u5802\uff0c\u6240\u4ee5\u53ea\u597d \u6682\u501f\u8fd9\u5730\u65b9\u7528\u7528\uff0c\u53ea\u671b\u660e\u5929\u90a3\u4f4d\u51ac\u70d8\u5148\u751f\u6765\u7684\u65f6\u5019\uff0c\u83ab\u8981\u88ab\u6211\u4eec\u7559\u4e0b\u6765\u7684\u9152\u6c14\u91ba\u9189\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u5fcd\u4f4f\u7b11\u9053\u2236\u201c\u4e0d\u77e5\u524d\u8f88\u53ef\u51c6\u5907\u4e86\u72d7\u8089\u9ebd\uff1f\u665a\u8f88\u4e0d\u5403\u72d7\u8089\u7684\u3002\u201d \u3000\u3000\u6234\u72ec\u884c\u62cd\u7740\u4ed6\u7684\u80a9\u5934\uff0c\u5927\u7b11\u9053\u2236\u201c\u6211\u770b\u4f60\u53ea\u6015\u4e5f\u4e2d\u4e86\u90a3\u4e9b\u8bf4\u4e66\u5f39\u8bcd\u4eba\u7684\u6bd2\uff0c\u90a3\u4e9b\u4eba\u4e00\u8bf4 \u8d77\u53eb\u5316\u5b50\u5403\u996d\uff0c\u65c1\u8fb9\u4e00\u5b9a\u7168\u7740\u4e00\u9505\u72d7\u8089\uff0c\u5176\u5b9e\u53eb\u5316\u5b50\u4e5f\u5e76\u975e\u4eba\u4eba\u90fd\u5403\u72d7\u8089\u7684\u3002\u201d \u3000\u3000\u70b9\u7740\u7684\u8721\u70db\u5df2\u70e7\u4e86\u4e00\u534a\uff0c\u684c\u5b50\u4e0b\u7684\u9152\u575b\u5b50\u5df2\u5f00\u5c01\u4e86\uff0c\u684c\u4e0a\u8fd8\u6709\u4e00\u5305\u5305\u7528\u6cb9\u7eb8\u5305\u7740\u7684\u5364\u83dc \u6234\u72ec\u884c\u679c\u7136\u662f\u65e9\u5df2\u51c6\u5907\u597d\u8981\u8bf7\u5ba2\u7684\u6837\u5b50\u3002 \u3000\u3000\u4f46\u5c31\u5728\u51e0\u5929\u524d\u4ed6\u8fd8\u4e0d\u613f\u548c\u695a\u7559\u9999\u89c1\u9762\uff0c\u8fd9\u6b21\u4e3a\u4f55\u5ffd\u7136\u6539\u53d8\u4e86\u5462\uff1f\u8fd9\u51e0\u5929\u4e4b\u5185\u662f\u4ec0\u9ebd\u4e8b\u4ee4 \u4ed6\u6539\u53d8\u4e86\u4e3b\u610f\uff1f \u3000\u3000\u695a\u7559\u9999\u5ffd\u7136\u53d1\u73b0\u4ed6\u7edd\u4e0d\u662f\u5076\u7136\u9047\u89c1\u81ea\u5df1\u7684\uff0c\u4ed6\u4e00\u5b9a\u6709\u4e8b\u8981\u627e\u695a\u7559\u9999\uff0c\u800c\u4e14\u770b\u6765\u8fd8\u662f\u4ef6\u5f88 \u91cd\u8981\u7684\u4e8b\u3002 \u3000\u3000\u559d\u4e86\u51e0\u676f\u4e4b\u5f8c\uff0c\u695a\u7559\u9999\u5ffd\u7136\u7b11\u9053\u2236\u201c\u524d\u8f88\u662f\u5426\u65e9\u5df2\u77e5\u9053\u201c\u795e\u6c34\u5bab\u201d\u8981\u627e\u665a\u8f88\u7684\u9ebb\u70e6\uff0c\u7b97 \u51c6\u665a\u8f88\u5fc5\u5b9a\u4f1a\u5230\u8fd9\u91cc\u6765\uff0c\u6240\u4ee5\u65e9\u5c31\u5728\u8fd9\u91cc\u7b49\u7740\uff0c\u51c6\u5907\u52a9\u665a\u8f88\u4e00\u81c2\u4e4b\u529b\u4e86\uff1f\u201d \u3000\u3000\u6234\u72ec\u884c\u6014\u4e86\u6014\uff0c\u4e3e\u676f\u5927\u7b11\u9053\u2236\u201c\u8001\u673d\u5e38\u542c\u522b\u4eba\u8bf4\u2236\u695a\u7559\u9999\u662f\u94c1\u94f8\u7684\u80c6\u5b50\uff0c\u5374\u662f\u6c34\u6676\u5fc3 \u809d\uff0c\u8fd9\u8bdd\u679c\u7136\u4e0d\u9519\uff0c\u679c\u7136\u4ec0\u9ebd\u4e8b\u90fd\u4f11\u60f3\u7792\u5f97\u8fc7\u4f60\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\u2236\u201c\u8d35\u5e2e\u7684\u6d88\u606f\u679c\u7136\u7075\u901a\uff0c\u524d\u8f88\u7684\u4ed7\u4e49\u66f4\u4ee4\u4eba\u611f\u6fc0\uff0c\u4f46\u8fd9\u4ef6\u4e8b\u2026\u2026\u201d \u3000\u3000\u6234\u72ec\u884c\u62a2\u7740\u9053\u2236\u201c\u8001\u673d\u4e5f\u77e5\u9053\u8fd9\u4ef6\u4e8b\u662f\u522b\u4eba\u4e0d\u80fd\u7ba1\uff0c\u4e5f\u7ba1\u4e0d\u4e86\u7684\uff0c\u8fd9\u6b21\u53ea\u4e0d\u8fc7\u662f\u60f3\u6765\u5411 \u9999\u5e05\u62a5\u544a\u4e00\u4ef6\u6d88\u606f\uff0c\u804a\u62a5\u9999\u5e05\u5bf9\u655d\u5e2e\u7684\u6069\u60c5\u65bc\u4e07\u4e00\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u706b\u8eab\u9053\u2236\u201c\u524d\u8f88\u8a00\u91cd\u4e86\u3002\u201d \u3000\u3000\u6234\u72ec\u884c\u9053\u2236\u201c\u8001\u673d\u8981\u8bf4\u7684\u8fd9\u4ef6\u4e8b\uff0c\u4e5f\u6b63\u548c\u655d\u5e2e\u90a3\u4e0d\u8096\u5b7d\u5f92\u5357\u5bab\u7075\u6709\u5173\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\u2236\u201c\u65e0\u82b1\uff1f\u201d \u3000\u3000\u6234\u72ec\u884c\u5c06\u9152\u676f\u91cd\u91cd\u6401\u5230\u684c\u4e0a\uff0c\u957f\u53f9\u9053\u2236\u201c\u4e0d\u9519\uff0c\u65e0\u82b1\uff0c\u6b64\u4eba\u8eab\u5728\u65b9\u5916\uff0c\u5374\u4e0d\u5b88\u6e05\u89c4\uff0c\u7adf \u5c06\u201c\u795e\u6c34\u5bab\u201d\u91cc\u4e00\u4f4d\u7389\u6d01\u51b0\u6e05\u7684\u5c0f\u6cf5\u5a18\u5f15\u8bf1\u6210\u5978\uff0c\u800c\u9677\u4eba\u65bc\u6b7b\uff0c\u8fd9\u4ef6\u4e8b\u9999\u5e05\u60f3\u5fc5\u662f\u77e5\u9053 \u7684\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\u2236\u201c\u4f46\u665a\u8f88\u4ece\u672a\u5c06\u8fd9\u4ef6\u4e8b\u4f20\u626c\u51fa\u53bb\uff0c\u5374\u4e0d\u77e5\u524d\u8f88\u662f\u600e\u4f1a\u77e5\u9053\u7684\uff1f\u201d \u3000\u3000\u6234\u72ec\u884c\u53f9\u9053\u2236\u201c\u9999\u5e05\u9690\u6076\u626c\u5584\uff0c\u4e0d\u613f\u63ed\u4eba\u9690\u79c1\uff0c\u8fd9\u79cd\u5fb7\u884c\u56fa\u7136\u53ef\u656c\uff1b\u600e\u5948\u5929\u7f51\u6062\u6062\uff0c\u758f \u800c\u4e0d\u6f0f\uff0c\u4e00\u4e2a\u4eba\u505a\u7684\u4e8b\u65e0\u8bba\u591a\u9ebd\u79d8\u5bc6\uff0c\u8fdf\u65e9\u8fd8\u662f\u8981\u88ab\u522b\u4eba\u77e5\u9053\u7684\u3002\u201d \u3000\u3000\u4ed6\u53f9\u606f\u7740\u63a5\u9053\u2236\u201c\u5357\u5bab\u7075\u867d\u7136\u7f6a\u5927\u6076\u6781\uff0c\u4f46\u4eba\u6b7b\u4e4b\u5f8c\uff0c\u4e5f\u5c31\u4e00\u4e86\u767e\u4e86\uff0c\u655d\u5e2e\u7684\u51e0\u4f4d\u957f\u8001 \u51b3\u8bae\u4e4b\u4e0b\uff0c\u8fd8\u662f\u51c6\u5907\u5c06\u4ed6\u7684\u9057\u4f53\u4ee5\u5e2e\u4e3b\u4e4b\u793c\u5b89\u846c\uff0c\u8fd9\u2026\u2026\u8fd9\u81ea\u7136\u4e5f\u662f\u56e0\u4e3a\u4ed6\u4eec\u89c9\u5f97\u5bb6\u4e11\u4e0d\u53ef \u5916\u626c\uff0c\u6b64\u4e2d\u82e6\u8877\uff0c\u9999\u5e05\u60f3\u5fc5\u4e5f\u80fd\u4e86\u89e3\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\u2236\u201c\u662f\u3002\u201d \u3000\u3000\u6234\u72ec\u884c\u9053\u2236\u201c\u672c\u5e2e\u5f1f\u5b50\u68c0\u70b9\u5357\u5bab\u7075\u751f\u524d\u7684\u9057\u7269\uff0c\u51c6\u5907\u5c06\u4e4b\u6b89\u846c\u65f6\uff0c\u5374\u53d1\u73b0\u4ed6\u9057\u7269\u4e2d\u6709\u4e2a \u5236\u4f5c\u5f88\u53e4\u96c5\u7684\u6728\u9c7c\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u5fae\u5fae\u76b1\u4e86\u76b1\u7709\uff0c\u9053\u2236\u201c\u6728\u9c7c\uff1f\u201d \u3000\u3000\u6234\u72ec\u884c\u9053\u2236\u201c\u5c31\u662f\u51fa\u5bb6\u4eba\u8bf5\u7ecf\u65f6\u7528\u7684\u6728\u9c7c\uff0c\u655d\u5e2e\u5b50\u5f1f\u65e2\u4e0d\u62dc\u4f5b\uff0c\u4e5f\u4e0d\u5ff5\u7ecf\uff0c\u600e\u4f1a\u6709\u6728\u9c7c \u7559\u4e0b\u6765\u5462\uff1f\u65bc\u662f\u5927\u5bb6\u90fd\u60f3\u5230\u8fd9\u6728\u9c7c\u5fc5\u5b9a\u662f\u65e0\u82b1\u5bc4\u5b58\u5728\u90a3\u91cc\u7684\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u70b9\u7740\u5934\u9053\u2236\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u6234\u72ec\u884c\u9053\u2236\u201c\u5927\u5bb6\u60f3\u5230\u5357\u5bab\u7075\u7684\u4e00\u751f\uff0c\u90fd\u662f\u88ab\u8fd9\u6076\u50e7\u65e0\u82b1\u6240\u5bb3\uff0c\u90fd\u4e0d\u514d\u8d77\u4e86\u60b2\u6124\u4e4b \u5fc3\u2026\u2026\u201d \u3000\u3000\u4ed6\u9eef\u7136\u6309\u7740\u9053\u2236\u201c\u8981\u77e5\u9053\u5357\u5bab\u7075\u5c0f\u65f6\u5019\u6728\u662f\u4e2a\u5584\u4f53\u4eba\u610f\u7684\u4e56\u5b69\u5b50\uff0c\u655d\u5e2e\u7684\u957f\u8001\u4eec\u90fd\u5bf9\u4ed6 \u6709\u6781\u6df1\u539a\u7684\u611f\u60c5\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u53f9\u7740\u6c14\u70b9\u4e86\u70b9\u5934\uff0c\u5fc3\u91cc\u6697\u6697\u5fd6\u9053\u2236\u201c\u81ea\u5df1\u7684\u5b69\u5b50\u505a\u4e86\u9519\u4e8b\uff0c\u7236\u6bcd\u4e00\u5b9a\u8981\u8ba4\u4e3a\u662f\u522b \u4eba\u5e26\u574f\u7684\uff0c\u8fd9\u672c\u662f\u4eba\u4e4b\u5e38\u60c5\u3002\u201d \u3000\u3000\u53ea\u542c\u6234\u72ec\u884c\u63a5\u7740\u9053\uff0c\u201c\u5176\u4e2d\u5c24\u5176\u4ee5\u738b\u957f\u8001\u7684\u5fc5\u60c5\u6700\u6fc0\u52a8\uff0c\u7adf\u5fcd\u4e0d\u4f4f\u5c06\u8fd9\u6728\u9c7c\u593a\u8fc7\u6765\uff0c\u91cd \u91cd\u6454\u5728\u5730\u4e0a\uff0c\u8c01\u77e5\u6728\u9c7c\u6454\u788e\u4e4b\u5f8c\uff0c\u91cc\u9762\u9713\u73b0\u51fa\u4e86\u4e00\u672c\u7eb8\u7c3f\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u52a8\u5bb9\u9053\u2236\u201c\u7eb8\u7c3f\uff1f\u4e0a\u9762\u8bb0\u7740\u7684\u662f\u4ec0\u9ebd\u4e8b\uff1f\u201d \u3000\u3000\u6234\u72ec\u884c\u9053\u2236\u201c\u8fd9\u7eb8\u7c3f\u88ab\u6536\u85cf\u5f97\u8fd9\u6837\u9690\u79d8\uff0c\u4e0a\u9762\u8bb0\u8f7d\u7684\u7eb5\u975e\u6b66\u529f\u5fc3\u6cd5\uff0c\u4e5f\u4e00\u5b9a\u662f\u6781\u5927\u7684\u79d8 \u5bc6\uff0c\u8001\u673d\u7b49\u4e5f\u5e76\u975e\u559c\u6b22\u63ed\u4eba\u9690\u79c1\u7684\u4eba\uff0c\u672c\u6765\u51c6\u5907\u5c06\u5b83\u70e7\u4e86\u7684\uff0c\u4f46\u738b\u957f\u8001\u5374\u8ba4\u4e3a\u8fd9\u5176\u4e2d\u7684\u79d8\u5bc6 \u8bf4\u4e0d\u5b9a\u4e0e\u4e10\u5e2e\u6709\u5173\uff0c\u6240\u4ee5\u575a\u6301\u8981\u77a7\u77a7\u3002\u201d \u3000\u3000\u8981\u77e5\u4e10\u5e2e\u5b50\u5f1f\u7d20\u6765\u4ee5\u6b63\u9053\u81ea\u5c45\uff0c\u800c\u7aa5\u770b\u522b\u4eba\u7684\u79d8\u51fd\u79c1\u8bb0\uff0c\u5374\u662f\u4ef6\u5f88\u4e0d\u5149\u660e\u78ca\u843d\u7684\u4e8b\u3002 \u3000\u3000\u6240\u4ee5\u6234\u72ec\u884c\u624d\u8bf4\u4e86\u5f88\u591a\u8bdd\u89e3\u91ca\uff0c\u695a\u7559\u9999\u81ea\u7136\u4e5f\u53ea\u6709\u552f\u552f\u79f0\u662f\u3002 \u3000\u3000\u6234\u72ec\u884c\u559d\u4e86\u676f\u9152\uff0c\u53c8\u6309\u7740\u9053\u2236\u201c\u8fd9\u6728\u7eb8\u7c3f\u4e0a\u8bb0\u8f7d\u7684\u679c\u7136\u662f\u65e0\u82b1\u4e00\u751f\u7684\u79d8\u5bc6\uff0c\u8001\u673d\u5b9e\u5728\u60f3 \u4e0d\u901a\u4ed6\u4e3a\u4f55\u8981\u5c06\u8fd9\u4e9b\u4e22\u4eba\u7684\u4e8b\u8bb0\u8f7d\u4e0b\u6765\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u9053\u2236\u201c\u8fd9\u4e9b\u4e8b\u524d\u8f88\u867d\u89c9\u5f97\u5f88\u4e22\u4eba\uff0c\u65e0\u82b1\u5374\u8bf4\u4e0d\u5b9a\u53cd\u800c\u89c9\u5f97\u662f\u81ea\u5df1\u7684\u5f97\u610f\u4e4b\u4e8b\uff0c \u4ed6\u65e2\u4e0d\u80fd\u5c06\u8fd9\u4ef6\u4e8b\u8bf4\u51fa\u6765\uff0c\u53ea\u6709\u9010\u6761\u8bb0\u4e0b\uff0c\u804a\u4ee5\u81ea\u6170\u4e86\u3002\u201d \u3000\u3000\u6234\u72ec\u884c\u4e5f\u7b11\u4e86\u7b11\uff0c\u9053\u2236\u201c\u9999\u5e05\u5bf9\u8fd9\u4e9b\u6076\u4eba\u7684\u5fc3\u7406\uff0c\u7684\u786e\u7814\u7a76\u5f97\u5f88\u900f\u5f7b\uff0c\u96be\u602a\u65e0\u8bba\u591a\u9ebd\u72e1 \u733e\u7684\u4eba\uff0c\u4e00\u9047\u7740\u9999\u5e05\uff0c\u8f68\u65e0\u6cd5\u9041\u5f62\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u53ea\u5f97\u53c8\u6b20\u8eab\u8c26\u8c22\uff0c\u5374\u95ee\u9053\u2236\u201c\u65e0\u82b1\u8bb0\u8f7d\u7684\u90a3\u4e9b\u79d8\u5bc6\u4e2d\uff0c\u83ab\u975e\u6709\u5173\u201c\u795e\u6c34\u5bab\u201d \u7684\uff1f\u201d \u3000\u3000\u6234\u72ec\u884c\u9053\u2236\u201c\u6b63\u56e0\u5982\u6b64\uff0c\u662f\u4ee5\u8001\u673d\u624d\u4e13\u7a0b\u524d\u6765\u62a5\u544a\u7ed9\u9999\u5e05\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\u2236\u201c\u4e0d\u6562\u2026\u2026\u201d \u3000\u3000\u4ed6\u6c88\u541f\u7740\u53c8\u9053\u2236\u201c\u524d\u8f88\u7684\u610f\u601d\uff0c\u662f\u5426\u8981\u5c06\u4ed6\u90a3\u672c\u79d8\u8bb0\u501f\u7ed9\u665a\u8f88\u4e00\u9605\uff1f\u201d \u3000\u3000\u6234\u72ec\u884c\u4e5f\u6c89\u541f\u7740\uff0c\u7f13\u7f13\u9053\u2236\u201c\u8001\u673d\u672c\u6709\u6b64\u610f\uff0c\u4f46\u2026\u2026\u4f46\u65e0\u82b1\u53f7\u79f0\u201c\u5999\u50e7\u201d\uff0c\u6c5f\u6e56\u4e2d\u4e00\u4e9b \u540d\u95e8\u4e16\u5bb6\uff0c\u90fd\u4ee5\u80fd\u8bf7\u5230\u4ed6\u505a\u5ba2\u4e3a\u8363\uff0c\u6240\u4ee5\u2026\u2026\u6240\u4ee5\u4ed6\u90a3\u672c\u79d8\u8bb0\u4e0a\uff0c\u8fd8\u8bb0\u7740\u4e0d\u5c11\u522b\u4eba\u5bb6\u95fa\u9601\u5343 \u91d1\u7684\u9690\u79c1\uff0c\u82e5\u662f\u6cc4\u9732\u51fa\u4e00\u4e9b\uff0c\u6c5f\u6e56\u5c31\u4e0d\u77e5\u6709\u591a\u5c11\u4eba\u7684\u597d\u5bb6\u5ead\u8981\u88ab\u62c6\u6563\uff0c\u591a\u5c11\u4f4d\u597d\u5973\u4e8e\u8981\u542b\u7f9e \u800c\u6b7b\uff0c\u6240\u4ee5\uff0c\u8001\u673d\u5df2\u5c06\u90a3\u672c\u810f\u4e1c\u897f\u70e7\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\u2236\u201c\u70e7\u5f97\u597d\u3002\u201d \u3000\u3000\u6234\u72ec\u884c\u9053\u2236\u201c\u4f46\u90a3\u4e0a\u9762\u6240\u8bb0\u8f7d\u4e0b\u6709\u5173\u201c\u795e\u6c34\u5bab\u201d\u7684\u4e8b\uff0c\u8001\u673d\u5374\u5df2\u94ed\u8bb0\u5728\u5fc3\uff0c\u53ea\u56e0\u4ed6\u4e5f\u8bb8 \u5c31\u662f\u552f\u4e00\u8fdb\u8fc7\u795e\u6c34\u5bab\u7684\u7537\u4eba\uff0c\u4ed6\u7684\u8bb0\u8f7d\u81ea\u7136\u5f25\u8db3\u73cd\u8d35\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\u2236\u201c\u665a\u8f88\u613f\u95fb\u5176\u8be6\u3002\u201d \u3000\u3000\u6234\u72ec\u884c\u53f9\u9053\u2236\u201c\u4ed6\u7684\u786e\u662f\u4e2a\u806a\u660e\u7edd\u9876\u7684\u4eba\uff0c\u4e0d\u4f46\u5999\u89e3\u97f3\u5f8b\u4e66\u753b\uff0c\u800c\u4e14\u5999\u65bc\u8bf4\u6cd5\uff0c\u8fde\u795e\u6c34 \u5bab\u9634\u5bab\u4e3b\u90fd\u95fb\u5f97\u4ed6\u7684\u5927\u540d\uff0c\u800c\u9634\u5bab\u4e3b\u5374\u662f\u4f4d\u793c\u4f5b\u751a\u8bda\u7684\u4eba\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\u2236\u201c\u8fd9\u4e00\u70b9\u665a\u8f88\u4e5f\u66fe\u542c\u4eba\u8bf4\u8d77\u8fc7\u3002\u201d \u3000\u3000\u6234\u72ec\u884c\u9053\u2236\u201c\u795e\u6c34\u5bab\u4e3b\u53ec\u4ed6\u8bf4\u6cd5\uff0c\u65e0\u82b1\u975e\u4f46\u89c9\u5f97\u5f88\u8363\u5e78\uff0c\u800c\u4e14\u6b63\u4e2d\u4e0b\u6000\uff0c\u53ea\u56e0\u4ed6\u65e9\u5c31\u5728 \u52a8\u90a3\u201c\u5929\u4e00\u795e\u6c34\u201d\u7684\u4e3b\u610f\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\u2236\u201c\u8981\u60f3\u81f4\u4eba\u65bc\u6b7b\uff0c\u800c\u6b7b\u5f8c\u5374\u77a7\u4e0d\u51fa\u4e2d\u6bd2\u4e4b\u8c61\u6765\uff0c\u4e16\u4e0a\u9664\u4e86\u201c\u5929\u4e00\u795e\u6c34\u201d\u5916\uff0c \u5b9e\u65e0\u4ed6\u7269\u3002\u201d \u3000\u3000\u6234\u72ec\u884c\u9053\u2236\u201c\u4f46\u4ed6\u867d\u7136\u8fdb\u4e86\u795e\u6c34\u5bab\uff0c\u5374\u8fd8\u662f\u65e0\u673a\u53ef\u4e58\uff0c\u53ea\u56e0\u9634\u5bab\u4e3b\u5bf9\u95e8\u4e0b\u5b50\u5f1f\u7684\u7ea6\u675f\u6781 \u4e25\uff0c\u4ed6\u6839\u672c\u6ca1\u6709\u548c\u90a3\u4e9b\u59d1\u5a18\u8bf4\u8bdd\u7684\u673a\u4f1a\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\u2236\u201c\u54e6\uff01\u201d \u3000\u3000\u6234\u72ec\u884c\u9053\u2236\u201c\u800c\u4e14\u9634\u5bab\u4e3b\u5e76\u6ca1\u6709\u7559\u4ed6\u4f4f\u5728\u795e\u6c34\u5bab\u91cc\uff0c\u53ea\u4e0d\u8fc7\u6bcf\u65e5\u7531\u5348\u65f6\u5f00\u59cb\uff0c\u8bf7\u4ed6\u6765\u8bf4 \u6cd5\u4e00\u4e2a\u65f6\u8fb0\uff0c\u8bf4\u5b8c\u4e86\u7acb\u523b\u5c31\u6709\u4eba\u9001\u4ed6\u51fa\u8c37\uff0c\u60f3\u591a\u505c\u7559\u4e00\u523b\u90fd\u529e\u4e0d\u5230\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u6c88\u541f\u7740\u9053\u2236\u201c\u63a5\u9001\u4ed6\u7684\uff0c\u90fd\u662f\u4e9b\u4ec0\u9ebd\u4eba\u5462\uff1f\u201d \u3000\u3000\u6234\u72ec\u884c\u9053\u2236\u201c\u63a5\u9001\u4ed6\u7684\u662f\u56db\u4f4d\u795e\u6c34\u5bab\u7684\u5973\u5f1f\u5b50\uff0c\u56db\u4e2a\u4eba\u4e92\u76f8\u76d1\u89c6\uff0c\u672c\u6765\u5b9e\u5728\u53ef\u8bf4\u662f\u6beb\u65e0 \u53ef\u4e58\u7684\u673a\u4f1a\uff0c\u751a\u81f3\u8fde\u4ed6\u81ea\u5df1\u90fd\u5df2\u8ba4\u4e3a\u7edd\u671b\u4e86\uff0c\u8c01\u77e5\u6709\u4e00\u5929\uff0c\u4ed6\u5ffd\u7136\u53d1\u73b0\u8fd9\u56db\u4f4d\u59d1\u5a18\u4e2d\uff0c\u7adf\u6709 \u4e00\u4f4d\u5728\u5bf9\u4ed6\u5077\u5077\u7684\u7b11\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u53f9\u9053\u2236\u201c\u8fd9\u4f4d\u59d1\u5a18\u60f3\u5fc5\u5c31\u662f\u53f8\u5f92\u9759\u4e86\u3002\u201d \u3000\u3000\u6234\u70db\u884c\u9053\u2236\u201c\u4e0d\u9519\uff0c\u4f46\u90a3\u65f6\u4ed6\u5e76\u4e0d\u77e5\u9053\u53f8\u5f92\u9759\u8fd9\u540d\u5b57\uff0c\u4ed6\u53ea\u89c9\u5f97\u8fd9\u4f4d\u59d1\u5a18\u773c\u6ce2\u4e2d\u4f3c\u4e4e\u8109 \u8109\u542b\u60c5\uff0c\u5f77\u4f5b\u5bf9\u4ed6\u6709\u610f\uff0c\u53ea\u4e0d\u8fc7\u4e24\u4eba\u95f4\u8fd8\u662f\u6ca1\u6709\u8bf4\u8bdd\u7684\u673a\u4f1a\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u82e6\u7b11\u9053\u2236\u201c\u50cf\u65e0\u82b1\u8fd9\u79cd\u4eba\uff0c\u8981\u8c03\u60c5\u662f\u7528\u4e0d\u7740\u8bf4\u8bdd\u7684\u3002\u201d \u3000\u3000\u6234\u70db\u884c\u9053\u2236\u201c\u4f46\u6ca1\u6709\u673a\u4f1a\uff0c\u4ed6\u8fd8\u662f\u65e0\u6cd5\u4e0b\u624b\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\u2236\u201c\u50cf\u4ed6\u8fd9\u79cd\u4eba\uff0c\u81ea\u7136\u4f1a\u81ea\u5df1\u5236\u9020\u673a\u4f1a\u3002\u201d \u3000\u3000\u6234\u72ec\u884c\u6068\u6068\u9053\u2236\u201c\u6b63\u662f\u5982\u6b64\u3002\u201d \u3000\u3000\u4ed6\u6309\u7740\u9053\u2236\u201c\u636e\u4ed6\u7684\u8bb0\u8f7d\uff0c\u795e\u6c34\u5bab\u4e43\u662f\u4e00\u5ea7\u5c71\u8c37\uff0c\u8c37\u4e2d\u7e41\u82b1\u5982\u9526\uff0c\u5b9b\u5982\u6843\u6e90\uff0c\u6797\u6728\u63a9\u6620 \u95f4\uff0c\u70b9\u7f00\u7740\u8bb8\u591a\u4ead\u53f0\u697c\u9601\uff0c\u5c31\u662f\u795e\u6c34\u5bab\u5973\u5f1f\u5b50\u4eec\u7684\u5c45\u5904\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u6697\u9053\u2236\u201c\u84c9\u513f\u679c\u7136\u6ca1\u6709\u8bf4\u9519\uff0c\u4f46\u67f3\u65e0\u7709\u6240\u8bf4\u7684\uff0c\u90a3\u53c8\u662f\u600e\u9ebd\u56de\u4e8b\u5462\uff1f\u3002\u201d \u3000\u3000\u6234\u72ec\u884c\u9053\u2236\u201c\u5c71\u8c37\u4e2d\u8fd8\u6709\u4e00\u9053\u7011\u5e03\uff0c\u52bf\u5982\u98de\u9f99\uff0c\u7011\u5e03\u4e0b\u6709\u6f6d\u5982\u955c\uff0c\u6f6d\u4e2d\u6709\u4e00\u5757\u5927\u77f3\u5934\uff0c \u90a3\u4e5f\u5c31\u662f\u65e0\u82b1\u7684\u8bf4\u6cd5\u4e4b\u5904\u3002\u201d \u3000\u3000\u4ed6\u6309\u7740\u9053\u2236\u201c\u65e0\u82b1\u4e00\u5165\u8c37\u5c31\u5750\u5230\u8fd9\u5757\u5927\u77f3\u5934\u4e0a\u6765\u8bf4\u6cd5\uff0c\u8bf4\u5b8c\u4e86\u5c31\u8d70\uff0c\u4ed6\u82e6\u5fc3\u7b79\u5212\u4e4b\u4e0b\uff0c \u89c9\u5f97\u53ea\u6709\u5728\u8fd9\u5757\u5927\u77f3\u5934\u4e0a\u505a\u624b\u811a\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u5fcd\u4e0d\u4f4f\u95ee\u9053\u2236\u201c\u505a\u4ec0\u9ebd\u624b\u811a\uff1f\u201d \u3000\u3000\u6597\u72ec\u884c\u9053\u2236\u201c\u8fd9\u5757\u5927\u77f3\u5757\u672c\u5c31\u5e73\u6ed1\u5982\u955c\uff0c\u6709\u5929\u4ed6\u5165\u8c37\u5f8c\u53c8\u6545\u610f\u8e0f\u4e86\u811a\u9752\u82d4\u6ce5\u6cde\uff0c\u4e00\u8e0f\u4e0a \u77f3\u5934\uff0c\u5c31\u6ed1\u4e86\u4e0b\u53bb\u3002\u201d\u3002 \u3000\u3000\u4ed6\u6068\u6068\u63a5\u7740\u9053\u2236\u201c\u4eba\u4eba\u90fd\u77e5\u65e0\u82b1\u4e43\u5c11\u6797\u9ad8\u8db3\uff0c\u82e5\u8bf4\u4ed6\u8fd0\u7ad9\u90fd\u7ad9\u4e0d\u7a33\uff0c\u522b\u4eba\u81ea\u7136\u4e0d\u4fe1\uff0c\u4f46 \u978b\u5e95\u6709\u4e86\u9752\u82d4\u6ce5\u6cde\uff0c\u5c31\u96be\u8bf4\u4e86\uff0c\u4f55\u51b5\u4ed6\u8fd8\u6545\u610f\u8fde\u53d8\u51e0\u79cd\u8eab\u6cd5\uff0c\u624d\u8dcc\u5165\u6c34\u4e2d\uff0c\u6b64\u4eba\u505a\u4f5c\u4e4b\u9ad8 \u660e\uff0c\u8fde\u9634\u5bab\u4e3b\u90fd\u88ab\u7792\u8fc7\u4e86\u3002\u201d \u3000\u3000\u3001\u695a\u7559\u9999\u82e6\u7b11\u6697\u5fd6\u9053\u2236\u201c\u6211\u53c8\u4f55\u5c1d\u4e0d\u662f\u88ab\u4ed6\u7792\u8fc7\u8bb8\u591a\u6b21\uff1f\u4e00\u4e2a\u4eba\u82e5\u80fd\u9a97\u5f97\u8fc7\u6211\uff0c\u53ea\u6015\u5c31 \u5f88\u5c11\u6709\u9a97\u4e0d\u8fc7\u7684\u4eba\u4e86\u3002\u201d\u3002 \u3000\u3000\u53ea\u542c\u6234\u72ec\u884c\u63a5\u7740\u9053\u2236\u201c\u4ed6\u5168\u8eab\u6e7f\u900f\u4e4b\u5f8c\uff0c\u81ea\u7136\u96be\u4ee5\u5b89\u5fc3\u8bf4\u6cd5\uff0c\u81ea\u7136\u8981\u5148\u5c06\u8863\u670d\u70d8\u4e7e\uff0c\u8fd9 \u8981\u6c42\u8c01\u4e5f\u4e0d\u80fd\u8bf4\u4e0d\u5408\u7406\uff0c\u8fde\u9634\u5bab\u4e3b\u4e5f\u65e0\u6cd5\u62d2\u7edd\uff0c\u6240\u4ee5\u5c31\u53eb\u4eba\u5e26\u4ed6\u5230\u5c71\u811a\u4e0b\u7684\u4e00\u5ea7\u5c0f\u5e99\u91cc\uff0c\u8fd8 \u4e3a\u4ed6\u751f\u8d77\u5806\u706b\u70e4\u8863\u670d\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\u2236\u201c\u8981\u5c06\u8863\u670d\u70e4\u4e7e\uff0c\u81f3\u5c11\u8981\u534a\u4e2a\u65f6\u8fb0\uff0c\u6709\u534a\u4e2a\u65f6\u8fb0\u5df2\u53ef\u505a\u8bb8\u591a\u4e8b\u4e86\u3002\u201d \u3000\u3000\u6234\u72ec\u884c\u9053\u2236\u201c\u4ed6\u4ee5\u4e3a\u90a3\u5bf9\u4ed6\u5fae\u7b11\u7684\u59d1\u5a18\u53f8\u5f92\u9759\u4e5f\u4e00\u5b9a\u4f1a\u8d81\u6b64\u673a\u4f1a\uff0c\u548c\u4ed6\u5355\u72ec\u76f8\u5904\u7684\uff0c\u8c01 \u77e5\u5374\u662f\u53e6\u4e24\u4f4d\u59d1\u5a18\u5c06\u4ed6\u5e26\u5230\u5e99\u91cc\u6765\uff0c\u800c\u4e14\u751f\u8d77\u706b\u4e4b\u5f8c\uff0c\u7acb\u523b\u5c31\u9000\u51fa\u53bb\u4e86\uff0c\u8fd8\u5c06\u90a3\u5ea7\u5c0f\u5e99\u7684\u95e8 \u7a97\u5168\u90fd\u5173\u5f97\u7d27\u7d27\u7684\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u4e5f\u89c9\u5f97\u6709\u4e9b\u8be7\u5f02\uff0c\u9053\u2236\u201c\u8fd9\u9ebd\u4e00\u6765\uff0c\u65e0\u82b1\u5c82\u975e\u4e5f\u65e0\u6cd5\u53ef\u65bd\u4e86\u9ebd\uff1f\u201d \u3000\u3000\u6234\u72ec\u884c\u9053\u2236\u201c\u4ed6\u6b63\u5728\u53d1\u6101\u7684\u65f6\u5019\uff0c\u90a3\u4f4d\u53f8\u5f92\u59d1\u5a18\u7adf\u5ffd\u7136\u81ea\u795e\u5e54\u5f8c\u8d70\u4e86\u51fa\u6765\uff0c\u800c\u4e14\u81ea\u613f\u732e \u8eab\u65bc\u4ed6\uff0c\u8fd9\u4e00\u53d8\u5316\uff0c\u636e\u8bb0\u8f7d\u8fde\u4ed6\u81ea\u5df1\u90fd\u89c9\u5f97\u5f88\u610f\u5916\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u4e5f\u4e3a\u4e4b\u52a8\u5bb9\uff0c\u5583\u5583\u9053\u2236\u201c\u90a3\u4f4d\u53f8\u5f92\u59d1\u5a18\u662f\u81ea\u795e\u5e54\u5f8c\u8d70\u51fa\u6765\u7684\uff1f\u5982\u6b64\u8bf4\u6765\uff0c\u90a3\u5c0f\u5e99 \u91cc\u5fc5\u5b9a\u6709\u6761\u79d8\u9053\u4e86\u2026\u2026\u795e\u6c34\u5bab\u91cc\u6bcf\u680b\u623f\u5b50\u662f\u4e0d\u662f\u90fd\u6709\u79d8\u9053\u5462\uff1f\u662f\u4e0d\u662f\u6bcf\u6761\u79d8\u9053\u90fd\u901a\u5411\u201c\u6c34 \u6bcd\u201d\u9634\u59ec\u7684\u5c45\u5904\uff1f\u751a\u81f3\u8fd8\u6709\u79d8\u9053\u8fdc\u8fbe\u67f3\u65e0\u7709\u6240\u5728\u7684\u90a3\u83e9\u63d0\u5eb5\uff1f\u201d \u3000\u3000\u6234\u72ec\u884c\u867d\u7136\u5e76\u6ca1\u6709\u542c\u61c2\u4ed6\u5728\u8bf4\u4ec0\u9ebd\uff0c\u5374\u4e5f\u6ca1\u6709\u95ee\uff0c\u53ea\u662f\u63a5\u7740\u9053\u2236\u201c\u636e\u4ed6\u8bf4\uff0c\u90a3\u53f8\u5f92\u9759\u539f \u6765\u662f\u9634\u5bab\u4e3b\u6700\u4eb2\u4fe1\u7684\u5f1f\u5b50\u4e4b\u4e00\uff0c\u548c\u4ed6\u7f20\u7ef5\u4e00\u5ea6\u4e4b\u5f8c\uff0c\u5c31\u5bf9\u4ed6\u6b7b\u5fc3\u584c\u5730\uff0c\u4ed6\u53ea\u4e0d\u8fc7\u8bf4\u60f3\u89c1\u8bc6\u89c1 \u8bc6\u201c\u5929\u4e00\u795e\u6c34\u201d\uff0c\u53f8\u5f92\u9759\u5c31\u7acb\u523b\u4e3a\u4ed6\u5077\u4e86\u4e00\u74f6\u51fa\u6765\uff0c\u4e24\u5929\u5f8c\u5728\u4ed6\u5c71\u8c37\u7684\u65f6\u5019\u5c31\u5077\u5077\u4ea4\u7ed9\u4e86 \u4ed6\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u8bb6\u7136\u9053\u2236\u201c\u7adf\u6709\u5982\u6b64\u5bb9\u6613\uff1f\u201d \u3000\u3000\u6234\u72ec\u884c\u9053\u2236\u201c\u4ed6\u81ea\u5df1\u5b9e\u4e5f\u672b\u60f3\u5230\u8fd9\u4ef6\u4e8b\u529e\u5f97\u6709\u5982\u6b64\u5bb9\u6613\uff0c\u56e0\u4e3a\u201c\u795e\u6c34\u5bab\u201d\u7684\u95e8\u4e0b\u867d\u8273\u5982 \u6843\u674e\uff0c\u5374\u51b7\u82e5\u51b0\u971c\uff0c\u4ed6\u518d\u4e5f\u672b\u60f3\u5230\u53f8\u5f92\u9759\u7adf\u4f1a\u81ea\u613f\u732e\u8eab\uff0c\u7adf\u4f3c\u6bd4\u8361\u5987\u6deb\u5a03\u8fd8\u8981\u8f7b\u4f7b\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\u2236\u201c\u800c\u4e14\u5979\u5728\u4e00\u4e24\u5929\u5185\u5c31\u80fd\u5c06\u6574\u74f6\u7684\u201c\u5929\u4e00\u795e\u6c34\u201d\u5077\u51fa\u6765\uff0c\u81ea\u7136\u662f\u300e\u6c34\u6bcd\u300f\u9634 \u59ec\u5ba0\u4fe1\u7684\u5f1f\u5b50\uff0c\u5979\u80fd\u5f97\u5230\u6c34\u6bcd\u7684\u5ba0\u4fe1\uff0c\u5e73\u65e5\u81ea\u7136\u4e0d\u662f\u4e2a\u8f7b\u4f7b\u6deb\u8361\u7684\u4eba\uff0c\u53c8\u600e\u4f1a\u4e00\u89c1\u5230\u65e0\u82b1\uff0c \u5c31\u5b8c\u5168\u53d8\u4e86\uff1f\u201d \u3000\u3000\u6234\u72ec\u884c\u53f9\u9053\u2236\u201c\u8fd9\u53ea\u6015\u5c31\u662f\u4f5b\u95e8\u6240\u8bf4\u7684\u5b7d\u7f18\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\u2236\u201c\u4ee5\u5f1f\u5b50\u770b\u6765\uff0c\u8fd9\u5176\u4e2d\u53ea\u6015\u8fd8\u53e6\u6709\u9690\u60c5\u3002\u201d \u3000\u3000\u6234\u72ec\u884c\u9053\u2236\u201c\u65e0\u8bba\u5176\u4e2d\u662f\u5426\u53e6\u6709\u9690\u60c5\uff0c\u8fd9\u4ef6\u4e8b\u603b\u7b97\u5df2\u6210\u8fc7\u53bb\uff0c\u8001\u673d\u4eca\u65e5\u91cd\u63d0\u65e7\u4e8b\uff0c\u53ea\u4e0d \u8fc7\u60f3\u8ba9\u9999\u5e05\u5bf9\u201c\u795e\u6c34\u5bab\u201d\u7684\u60c5\u51b5\u7565\u6709\u4e86\u89e3\u800c\u5df2\u3002\u201d \u3000\u3000\u4ed6\u7b11\u4e86\u7b11\uff0c\u53c8\u9053\u2236\u201c\u90a3\u672c\u79c1\u8bb0\u65e2\u662f\u65e0\u82b1\u5199\u7ed9\u81ea\u5df1\u770b\u7684\uff0c\u6240\u8bb0\u8f7d\u7684\u60f3\u5fc5\u5b9a\u662f\u5b9e\u60c5\uff0c\u6240\u4ee5\uff0c \u4f9d\u8001\u673d\u63a8\u6d4b\uff0c\u9634\u5bab\u4e3b\u7684\u5c45\u5904\u53ea\u6015\u662f\u5728\u5c71\u8170\u5730\u5e95\uff0c\u800c\u4e14\u5fc5\u5b9a\u5c31\u5728\u90a3\u6c34\u6f6d\u9644\u8fd1\uff0c\u6240\u4ee5\u65e0\u82b1\u5728\u8bb2\u7ecf \u7684\u65f6\u5019\uff0c\u5979\u624d\u80fd\u542c\u5f97\u5230\u3002\u201d \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u4e24\u4eba\u5ffd\u7136\u5168\u90fd\u7ad9\u4e86\u8d77\u6765\uff0c\u5916\u9762\u8863\u8882\u98ce\u5cb1\uff0c\u4e00\u4eba\u7b11\u7740\u9053\u2236\u201c\u6709\u9152\u6709\u83dc\uff0c\u5374\u4e0d\u627e \u6211\u6765\u3002\u6234\u8001\u524d\u51fb\u672a\u514d\u539a\u6b64\u800c\u8584\u5f7c\u5427\uff1f\u201d \u3000\u3000\u5728\u7b11\u58f0\u4e2d\u95ef\u8fdb\u6765\u7684\uff0c\u81ea\u7136\u5c31\u662f\u80e1\u94c1\u82b1\uff0c\u4f46\u4ed6\u4e5f\u611f\u514d\u5230\u73b0\u5728\u5e76\u4e0d\u662f\u559d\u9152\u7684\u65f6\u5019\uff0c\u56e0\u4e3a\u4ed6\u73b0 \u5728\u6025\u7740\u8981\u8bf4\u8bdd\u3002 \u3000\u3000\u695a\u7559\u9999\u542c\u4ed6\u8bf4\u51fa\u4e86\u65b9\u624d\u7684\u7ecf\u8fc7\uff0c\u53c8\u4e0d\u7981\u5f00\u59cb\u53bb\u6478\u9f3b\u5b50\u4e86\uff0c\u4ed6\u89c9\u5f97\u5f88\u6109\u5feb\u6216\u8005\u5f88\u4e0d\u6109\u5feb\u7684 \u65f6\u5019\uff0c\u5c31\u5fcd\u4e0d\u4f4f\u8981\u6478\u9f3b\u5b50\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u9053\u2236\u201c\u4f60\u7528\u4e0d\u7740\u6478\u9f3b\u5b50\uff0c\u4e5f\u7528\u4e0d\u7740\u66ff\u84c9\u84c9\u62c5\u5fc3\uff0c\u5979\u6bd4\u4f60\u60f3\u50cf\u4e2d\u8981\u80fd\u5e72\u5f97\u591a\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u6c88\u541f\u9053\u2236\u201c\u542c\u4f60\u8fd9\u9ebd\u8bf4\uff0c\u6b7b\u7684\u90a3\u516d\u4eba\u5e76\u4e0d\u80fd\u7b97\u662f\u6c5f\u6e56\u4e2d\u7b2c\u4e00\u6d41\u7684\u89d2\u8272\uff0c\u53ea\u4e0d\u8fc7\u5076 \u5c14\u505a\u4e86\u4e00\u7968\u5927\u4e70\u5356\u800c\u5df2\u3002\u201d \u3000\u3000\u6234\u72ec\u884c\u62a2\u7740\u9053\u2236\u201c\u4e0d\u9519\uff0c\u90a3\u516d\u4eba\u5e76\u4e0d\u662f\u4ec0\u9ebd\u4e00\u6d41\u9ad8\u624b\uff0c\u8001\u673d\u4e5f\u5e76\u4e0d\u662f\u7279\u5730\u8ddf\u7740\u4ed6\u4eec\u6765 \u7684\uff0c\u53ea\u4e0d\u8fc7\u5728\u8fd9\u91cc\u649e\u89c1\u4e86\u4ed6\u4eec\u800c\u5df2\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u7b11\u9053\u2236\u201c\u90a3\u6837\u7684\u89d2\u8272\uff0c\u81ea\u7136\u4e0d\u503c\u5f97\u52b3\u52a8\u524d\u8f88\u5927\u9a7e\uff0c\u524d\u8f88\u7528\u4e0d\u7740\u89e3\u91ca\uff0c\u6211\u4eec\u4e5f\u770b\u5f97 \u51fa\u6765\u7684\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\u2236\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u5bab\u5357\u71d5\u6b64\u756a\u51fa\u8c37\uff0c\u4e5f\u7edd\u4e0d\u662f\u4e3a\u4e86\u5bf9\u4ed8\u4ed6\u4eec\u7684\uff0c\u53ea\u4e0d\u8fc7\u662f\u90a3\u516d\u4eba \u65f6\u8fd0\u4e0d\u6d4e\uff0c\u624d\u51d1\u5de7\u9047\u89c1\u4e86\u5979\u3002\u201d \u3000\u3000\u6234\u72ec\u884c\u9053\u2236\u201c\u4f55\u4ee5\u89c1\u5f97\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u5927\u7b11\u9053\u2236\u201c\u95fb\u5f26\u6b4c\u800c\u77e5\u96c5\u610f\uff0c\u524d\u8f88\u96be\u9053\u8fd8\u542c\u4e0d\u51fa\u4ed6\u7684\u5f26\u5916\u4e4b\u97f3\u9ebd\uff1f\u201d \u3000\u3000\u6234\u72ec\u884c\u5fae\u7b11\u7740\uff0c\u80e1\u94c1\u82b1\u5c31\u63a5\u7740\u9053\u2236\u201c\u5bab\u5357\u71d5\u5c31\u662f\u4e0a\u6b21\u53bb\u627e\u695a\u7559\u9999\u7684\u4eba\uff0c\u9634\u59ec\u65e2\u7136\u6d3e\u5979\u53bb \u627e\u5802\u5802\u7684\u695a\u9999\u5e05\uff0c\u53ef\u89c1\u5979\u5fc5\u662f\u201c\u795e\u6c34\u5bab\u201d\u95e8\u4e0b\u6570\u4e00\u6570\u4e8c\u7684\u89d2\u8272\uff0c\u4f46\u90a3\u516d\u4e2a\u4eba\u5374\u53ea\u4e0d\u8fc7\u662f\u6c5f\u6e56 \u4e2d\u7684\u65e0\u540d\u5c0f\u5352\u800c\u5df2\uff0c\u4e5f\u4e0d\u503c\u5f97\u52b3\u52a8\u5979\u5927\u9a7e\u7684\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u77aa\u4e86\u4ed6\u4e00\u773c\uff0c\u9053\u2236\u201c\u6211\u53d1\u89c9\u4f60\u4eca\u5929\u8bdd\u8bf4\u5f97\u592a\u591a\uff0c\u9152\u5374\u559d\u5f97\u592a\u5c11\u4e86\u3002\u201d \u3000\u3000\u6234\u72ec\u884c\u9053\u2236\u201c\u4f46\u8fd9\u8bdd\u5e76\u6ca1\u6709\u8bf4\u9519\uff0c\u201c\u795e\u6c34\u5bab\u201d\u6d3e\u51fa\u6765\u627e\u695a\u9999\u5e05\u7684\u4eba\uff0c\u5728\u5bab\u4e2d\u7684\u8eab\u4efd\u5fc5\u5b9a \u5f88\u9ad8\uff0c\u7edd\u4e0d\u4f1a\u4e13\u7a0b\u4e3a\u4e86\u90a3\u516d\u4eba\u5c71\u8c37\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\u2236\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u5bab\u5357\u71d5\u6b64\u756a\u51fa\u8c37\uff0c\u9e21\u9053\u662f\u4e3a\u4e86\u5bf9\u4ed8\u695a\u7559\u9999\u7684\u9ebd\uff1f\u4f46\u5979\u4eec\u600e\u9ebd\u4f1a \u77e5\u9053\u695a\u7559\u9999\u5df2\u5230\u4e86\u8fd9\u91cc\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u6c88\u541f\u7740\uff0c\u6234\u72ec\u884c\u5374\u5df2\u5c06\u684c\u4e0a\u7684\u9152\u83dc\u5168\u90fd\u88c5\u5728\u4e00\u53ea\u9ebb\u888b\u91cc\uff0c\u53c8\u717d\u7184\u4e86\u70db\u706b\uff0c\u6c88\u58f0 \u9053\u2236\u201c\u9ed1\u591c\u5b64\u706f\uff0c\u59d4\u5b9e\u592a\u5f15\u4eba\u6ce8\u76ee\uff0c\u80e1\u5144\u65e2\u80fd\u627e\u5230\u8fd9\u91cc\uff0c\u522b\u4eba\u4e5f\u80fd\u627e\u5f97\u5230\uff0c\u54b1\u4eec\u8fd8\u662f\u6362\u4e2a\u5730 \u65b9\u559d\u9152\u53bb\u5427\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u521a\u8f6c\u8eab\u8d70\u5230\u95e8\u53e3\uff0c\u5ffd\u7136\u53c8\u505c\u4f4f\u4e86\u811a\uff0c\u7ad9\u5728\u7a97\u5b50\u65c1\u7684\u80e1\u94c1\u82b1\u5374\u8fc7\u4e86\u534a\u664c\u4e4b\u5f8c\uff0c\u624d\u770b \u51fa\u591c\u8272\u4e2d\u53c8\u63a0\u6765\u4e24\u6761\u4eba\u5f71\u3002\u8fd9\u4e24\u4eba\u8eab\u5f62\u90fd\u51fa\u5947\u7684\u8f7b\u5feb\uff0c\u5c24\u5176\u662f\u5de6\u9762\u8eab\u6750\u8f83\u77ee\u7684\u4e00\u4eba\uff0c\u695a\u7559\u9999 \u548c\u6234\u72ec\u884c\u90fd\u662f\u4e00\u7b49\u4e00\u7684\u5927\u884c\u5bb6\uff0c\u4e00\u773c\u5c31\u77a7\u51fa\u8fd9\u4eba\uff0c\u4e0d\u4f46\u8f7b\u529f\u6781\u9ad8\uff0c\u800c\u4e14\u59cb\u7ec8\u90fd\u80fd\u4fdd\u6301\u7740\u4e00\u79cd \u4f18\u96c5\u4ece\u5bb9\u7684\u59ff\u6001\uff0c\u5c31\u5f77\u4f5b\u5728\u968f\u7740\u665a\u98ce\u4e2d\u65e0\u58f0\u7684\u8282\u594f\u5728\u98d8\u7136\u800c\u821e\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u77a7\u4e86\u77a7\u6234\u72ec\u884c\uff0c\u53c8\u77a7\u4e86\u77a7\u695a\u7559\u9999\uff0c\u5fcd\u4e0d\u4f4f\u53f9\u4e86\u53e3\u6c14\uff0c\u4ed6\u5e73\u65e5\u5bf9\u81ea\u5df1\u7684\u8f7b\u529f\u4e5f\u5f88\u81ea \u8d1f\uff0c\u4f46\u4eca\u5929\u665a\u4e0a\uff0c\u4ed6\u6240\u89c1\u5230\u7684\u6bcf\u4e00\u4e2a\u4eba\uff0c\u8f7b\u529f\u90fd\u662f\u8981\u6bd4\u4ed6\u9ad8\u51fa\u8bb8\u591a\uff0c\u5c31\u597d\u50cf\u5929\u4e0b\u6240\u6709\u7684\u8f7b\u529f \u9ad8\u624b\u5168\u90fd\u6d8c\u5230\u8fd9\u5c0f\u57ce\u6765\u4e86\u3002 \u3000\u3000\u94c1\u72ec\u884c\u6084\u6084\u6253\u4e86\u4e2a\u624b\u52bf\uff0c\u53c1\u4e2a\u4eba\u5df2\u5168\u90fd\u81ea\u53e6\u4e00\u8fb9\u7684\u7a97\u6237\u91cc\u9000\u4e86\u51fa\u53bb\uff0c\u7a97\u5916\u5c31\u662f\u4e2a\u8349\u6728\u5f88 \u5bc6\u7684\u5c71\u5751\u3002\u4ed6\u4eec\u5e76\u6ca1\u6709\u8d70\u8fdc\uff0c\u53ea\u662f\u9690\u8eab\u5728\u8349\u6728\u9634\u5f71\u91cc\uff0c\u53c1\u4e2a\u4eba\u5fc3\u91cc\u90fd\u5728\u6697\u6697\u731c\u6d4b\u2236\u8fd9\u4e24\u4eba\u662f \u8c01\uff1f\u662f\u4e3a\u4f55\u800c\u6765\u7684\uff1f\u4ed6\u4eec\u51b3\u5fc3\u8981\u7b49\u7740\u77a7\u4e2a\u6c34\u843d\u77f3\u51fa\u3002 \u3000\u3000\u53e3\u53e3\u53e3 \u3000\u3000\u90a3\u4e24\u4eba\u4e0d\u4f46\u76f4\u5954\u8fd9\u5b66\u5802\u800c\u6765\uff0c\u800c\u4e14\u8fd8\u4f3c\u4e4e\u6765\u8fc7\u4e0d\u6b62\u4e00\u6b21\u4e86\uff0c\u5bf9\u8fd9\u9644\u8fd1\u4e00\u5e26\u7684\u5730\u52bf\u90fd\u719f\u6089 \u5f97\u5f88\u3002\u4ed6\u4eec\u5728\u5916\u9762\u7565\u4e00\u9021\u5de1\uff0c\u5c31\u8d70\u8fdb\u4e86\u8fd9\u5b66\u5802\uff0c\u8eab\u6750\u8f83\u77ee\u7684\u4e00\u4eba\u521a\u8de8\u8fdb\u95e8\u69db\uff0c\u5c31\u505c\u4f4f\u4e86\u811a \u6b65\uff0c\u6c89\u58f0\u9053\u2236\u201c\u8fd9\u95e8\u600e\u5730\u6ca1\u6709\u5173\u4e0a\uff1f\u201d \u3000\u3000\u53e6\u4e00\u4eba\u5fae\u7b11\u9053\u2236\u201c\u5c0f\u5b69\u5b50\u4eec\u5df4\u4e0d\u5f97\u65e9\u4e9b\u653e\u5b66\u56de\u5bb6\uff0c\u90a3\u91cc\u8fd8\u4f1a\u8bb0\u5f97\u5173\u95e8\uff1f\u201d \u3000\u3000\u90a3\u4eba\u6c89\u541f\u7740\uff0c\u9053\u2236\u201c\u4f46\u5728\u8fd9\u91cc\u6559\u5b66\u7684\u8fd8\u662f\u90a3\u4f4d\u738b\u5148\u751f\uff0c\u6211\u77e5\u9053\u6b64\u4eba\u662f\u4e2a\u4e00\u4e1d\u4e0d\u82df\u7684\u8001\u53e4 \u677f\uff0c\u505a\u4e8b\u7d20\u6765\u8c28\u614e\u5f97\u5f88\uff0c\u600e\u4f1a\u2026\u2026\u201d \u3000\u3000\u8eab\u6750\u8f83\u9ad8\u7684\u4e00\u4eba\u7b11\u7740\u6253\u65ad\u4e86\u4ed6\u7684\u8bdd\uff0c\u9053\u2236\u201c\u4ed6\u53ea\u6015\u4e5f\u88ab\u5b69\u5b50\u4eec\u5435\u660f\u4e86\u5934\uff0c\u4f55\u51b5\uff0c\u5173\u4e0d\u5173 \u95e8\u53c8\u6709\u4f55\u59a8\uff0c\u53cd\u6b63\u8fd9\u91cc\u4e5f\u6ca1\u6709\u4ec0\u9ebd\u4e1c\u897f\u503c\u5f97\u52b3\u52a8\u6881\u4e0a\u541b\u5b50\u7684\u5927\u9a7e\u3002\u201d \u7b2c\u4e8c\u5341\u56db\u7ae0\u3000\u751f\u6b7b\u4e4b\u4ea4>> \u53e4\u9f99\u300a\u695a\u7559\u9999\u7cfb\u5217\xb7\u753b\u7709\u9e1f\u300b \u7b2c\u4e8c\u5341\u56db\u7ae0\u3000\u751f\u6b7b\u4e4b\u4ea4 \u3000\u3000\u8fd9\u4eba\u7684\u58f0\u97f3\uff0c\u548c\u7f13\u800c\u82cd\u8001\uff0c\u542c\u6765\u7adf\u719f\u6089\u5f97\u5f88\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u548c\u695a\u7559\u9999\u4e00\u65f6\u95f4\u6b63\u60f3\u4e0d\u8d77\u4ed6\u662f\u8c01\uff0c\u8eab\u6750\u8f83\u77ee\u7684\u90a3\u4eba\u5df2\u8d70\u5230\u7a97\u53e3\uff0c\u4ed6\u4eec\u65b9\u9000\u51fa\u53bb\u7684 \u65f6\u5019\uff0c\u4e5f\u5fd8\u8bb0\u5c06\u8fd9\u6247\u7a97\u5b50\u5173\u4e0a\u4e86\u3002 \u3000\u3000\u5c71\u5761\u6321\u4f4f\u4e86\u661f\u5149\uff0c\u4f46\u4f9d\u7a00\u4ecd\u53ef\u8fa8\u51fa\u8fd9\u4eba\u7684\u9762\u76ee\uff0c\u80e1\u94c1\u5316\u548c\u695a\u7559\u9999\u5fc3\u91cc\u90fd\u4e0d\u7981\u6709\u4e9b\u60ca\u8bb6\u3002 \u3000\u3000\u8fd9\u4eba\u5c45\u7136\u662f\u4ed6\u4eec\u5728\u201c\u62e5\u7fe0\u5c71\u5e84\u201d\u6240\u89c1\u5230\u7684\u90a3\u795e\u79d8\u7684\u9ed1\u8863\u5251\u5ba2\uff0c\u53e6\u4e00\u4eba\u65e0\u7591\u5c31\u662f\u201c\u541b\u5b50 \u5251\u201d\u9ec4\u9c81\u76f4\u4e86\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u53c1\u66f4\u534a\u591c\u7684\u5230\u8fd9\u91cc\u6765\uff0c\u800c\u4e14\u884c\u8e2a\u53c8\u5982\u6b64\u9690\u79d8\uff0c\u597d\u50cf\u751f\u6015\u88ab\u522b\u4eba\u53d1\u89c9\uff0c\u8fd9\u53c8\u4e3a\u7684\u662f \u4ec0\u9ebd\u5462\uff1f \u3000\u3000\u80e1\u94c1\u5316\u548c\u695a\u7559\u9999\u81ea\u7136\u96be\u514d\u8981\u89c9\u5f97\u5f88\u5947\u602a\u3002 \u3000\u3000\u6726\u80e7\u7684\u591c\u8272\u4e2d\uff0c\u8fd9\u9ed1\u8863\u4eba\u7684\u9762\u8272\u770b\u6765\u4f3c\u4e4e\u5f88\u6c88\u91cd\uff0c\u4f46\u76ee\u4e2d\u5374\u95ea\u52a8\u7740\u4e00\u79cd\u5947\u5f02\u7684\u5149\u8292\uff0c\u770b \u6765\u53c8\u5f77\u4f5b\u5f88\u5174\u594b\uff0c\u5f88\u6fc0\u52a8\u3002 \u3000\u3000\u4ed6\u671b\u7740\u7a97\u5916\u7684\u591c\u8272\u5446\u5446\u7684\u51fa\u4e86\u4f1a\u795e\uff0c\u624d\u957f\u53f9\u4e86\u4e00\u58f0\uff1a\u201c\u6211\u8fd9\u4e9b\u5e74\u6765\u603b\u662f\u7591\u795e\u7591\u9b3c\uff0c\u4f60\u4e5f \u8bb8\u4f1a\u2026\u2026\u201d \u3000\u3000\u9ec4\u9c81\u76f4\u8d70\u6765\u62cd\u5f15\u62cd\u4ed6\u7684\u80a9\u5934\uff0c\u9053\uff1a\u201c\u6211\u4e0d\u602a\u4f60\uff0c\u5728\u4f60\u8fd9\u79cd\u73af\u5883\u4e0b\uff0c\u8c28\u614e\u5c0f\u5fc3\u4e9b\u672c\u662f\u5e94\u8be5 \u7684\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u5782\u4e0b\u4e86\u5934\uff0c\u9eef\u7136\u9053\uff1a\u201c\u666e\u5929\u4e4b\u4e0b\uff0c\u4eba\u4eba\u60f3\u5c06\u6211\u7f6e\u4e4b\u65bc\u6b7b\u5730\uff0c\u53ea\u6709\u4f60\u2026\u2026\u4f60\u5bf9\u6211\u5374 \u59cb\u7ec8\u4e0d\u5f03\uff0c\u800c\u6211\u975e\u4f46\u65e0\u6cd5\u62a5\u7b54\u4f60\uff0c\u53cd\u800c\u603b\u662f\u8981\u8fde\u7d2f\u4f60\u3002\u201d \u3000\u3000\u9ec4\u9c81\u76f4\u9053\uff1a\u201c\u4ea4\u53cb\u8d35\u4e4e\u76f8\u77e5\uff0c\u65e0\u8bba\u4f60\u5c01\u522b\u4eba\u600e\u6837\uff0c\u4f46\u5bf9\u6211\uff0c\u5374\u59cb\u7ec8\u5fe0\u8bda\u5982\u4e00\uff0c\u4f3c\u4e4e\u5728\u6211 \u773c\u4e2d\uff0c\u4f60\u5728\u4e16\u4e0a\u6bd4\u4efb\u4f55\u4eba\u90fd\u53ef\u9760\u5f97\u591a\u3002\u201d \u3000\u3000\u4ed6\u5fae\u7b11\u7740\u63a5\u9053\uff1a\u201c\u8fd9\u5e74\u5934\u670b\u53cb\u8d8a\u6765\u8d8a\u96be\u4ea4\uff0c\u50cf\u4f60\u8fd9\u6837\u7684\u670b\u53cb\uff0c\u6211\u8fd9\u4e00\u8f88\u5b50\u6015\u518d\u4e5f\u627e\u4e0d\u51fa \u7b2c\u4e8c\u4e2a\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u76ee\u4e2d\u5145\u6ee1\u4e86\u611f\u6fc0\u4e4b\u610f\uff0c\u4e5f\u5fae\u7b11\u7740\u9053\uff1a\u201c\u8fd9\u53e5\u8bdd\u672c\u8be5\u6211\u8bf4\u7684\uff0c\u6c5f\u6e56\u4e2d\u4eba\u82e5\u77e5\u9053\u201c\u541b \u5b50\u5251\u201d\u7adf\u548c\u6211\u7ed3\u4e3a\u751f\u6b7b\u4e4b\u4ea4\uff0c\u6015\u6bd4\u542c\u5230\u5929\u5cf0\u5927\u5e08\u8fd8\u4fd7\u5a36\u4e86\u8001\u5a46\u8fd8\u8981\u5947\u602a\u3002\u201d \u3000\u3000\u4ed6\u8bed\u58f0\u4e2d\u867d\u6709\u4e86\u7b11\u5bb9\uff0c\u4f46\u9762\u4e0a\u5374\u4ecd\u7136\u6b7b\u677f\u677f\u7684\u3002 \u3000\u3000\u4f46\u8fd9\u4eba\u7a76\u7adf\u662f\u8c01\u5462\uff1f \u3000\u3000\u80e1\u94c1\u5316\u548c\u695a\u7559\u9999\u5bf9\u671b\u4e86\u4e00\u773c\uff0c\u5fc3\u91cc\u4e0d\u7ea6\u800c\u540c\u6697\u6697\u5fd6\u9053\uff1a\u201c\u8fd9\u4eba\u8138\u4e0a\u679c\u7136\u6234\u7740\u9762\u5177\u3002\u201d \u3000\u3000\u4e3a\u4ec0\u9ebd\u6bcf\u4e2a\u4eba\u90fd\u60f3\u5c06\u4ed6\u7f6e\u4e4b\u65bc\u6b7b\u5730\uff1f \u3000\u3000\u4ed6\u534a\u591c\u91cc\u8dd1\u5230\u8fd9\u65e0\u4eba\u7684\u5b66\u5802\u6765\uff0c\u7a76\u7adf\u5b58\u7740\u4ec0\u9ebd\u5c45\u5fc3\uff1f \u3000\u3000\u80e1\u94c1\u82b1\u7b80\u76f4\u5fcd\u4e0d\u4f4f\u8981\u51b2\u51fa\u53bb\uff0c\u5c06\u8fd9\u4eba\u5934\u4e0a\u7684\u4eba\u76ae\u9762\u5177\u5265\u4e0b\u6765\uff0c\u77a7\u4e2a\u6e05\u695a\uff0c\u95ee\u4e2a\u660e\u767d\u3002 \u3000\u3000\u997c\u4e86\u534a\u664c\uff0c\u53ea\u542c\u9ec4\u9c81\u76f4\u9053\uff1a\u201c\u4eca\u5929\u665a\u4e0a\uff0c\u6211\u672c\u6765\u4e0d\u8be5\u6765\u7684\u2026\u2026\u201d \u3000\u3000\u9ed1\u8863\u4eba\u62a2\u7740\u9053\uff1a\u201c\u6211\u4e00\u5b9a\u8981\u4f60\u6765\uff0c\u53ea\u56e0\u6211\u4e00\u5b9a\u8981\u4f60\u77a7\u77a7\u5979\u3002\u201d \u3000\u3000\u4ed6\u76ee\u5149\u4e2d\u53c8\u5145\u6ee1\u4e86\u5174\u594b\u4e4b\u610f\uff0c\u7adf\u5fcd\u4e0d\u4f4f\u7b11\u4e86\u51fa\u6765\uff0c\u9053\uff1a\u201c\u4f60\u6015\u5e73\u751f\u4e5f\u6ca1\u6709\u89c1\u8fc7\u50cf\u5979\u90a3\u9ebd \u7f8e\u4e3d\u7684\u5973\u5b69\u5b50\u3002\u201d \u3000\u3000\u9ec4\u9c81\u76f4\u4e5f\u5fae\u7b11\u7740\u9053\uff1a\u201c\u6211\u4e0d\u5fc5\u770b\uff0c\u4e5f\u77e5\u9053\u5979\u5fc5\u5b9a\u53c8\u806a\u660e\uff0c\u53c8\u7f8e\u4e3d\uff0c\u53ea\u4e0d\u8fc7\u2026\u2026\u6050\u6015\u591a\u4e86 \u4e00\u4e2a\u4eba\u5728\u65c1\u8fb9\uff0c\u4f60\u4eec\u8bf4\u8bdd\u4f1a\u6709\u4e9b\u4e0d\u4fbf\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u6709\u4ec0\u9ebd\u4e0d\u4fbf\uff0c\u5979\u65e9\u5c31\u542c\u6211\u8bf4\u8fc7\u4f60\u4e86\uff0c\u4eca\u5929\u80fd\u89c1\u5230\u4f60\uff0c\u5979\u4e5f\u4e00\u5b9a\u4f1a\u89c9\u5f97\u5f88\u6b22 \u559c\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u53c8\u7b11\u9053\uff1a\u201c\u4eca\u5929\u6211\u4eec\u4e00\u5b9a\u8981\u75db\u75db\u5feb\u5feb\u7684\u559d\u4e24\u676f\uff0c\u6211\u5df2\u7ecf\u6709\u5f88\u4e45\u6ca1\u6709\u8fd9\u9ebd\u6837\u5f00\u5fc3\u8fc7 \u4e86\uff0c\u4ee5\u5f8c\u6015\u4e5f\u4e0d\u4f1a\u518d\u6709\u2026\u2026\u201d \u3000\u3000\u9ec4\u9c81\u76f4\u53c8\u6253\u65ad\u4e86\u4ed6\u7684\u8bdd\uff0c\u9053\uff1a\u201c\u5f00\u5fc3\u7684\u65e5\u5b50\uff0c\u5c31\u4e0d\u8981\u8bf4\u4e27\u6c14\u8bdd\uff0c\u73b0\u5728\u65f6\u5019\u5df2\u7ecf\u5feb\u5230\u4e86\uff0c \u4f60\u8fd8\u662f\u5feb\u5c06\u9152\u83dc\u6446\u51fa\u6765\u5427\uff01\u201d \u3000\u3000\u8fd9\u4e24\u4e2a\u679c\u7136\u662f\u6765\u7b49\u4eba\u7684\uff0c\u800c\u4e14\u8fd8\u8981\u559d\u4e24\u676f\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u5fc3\u91cc\u6697\u6697\u7684\u7b11\uff1a\u201c\u60f3\u4e0d\u5230\u8fd9\u5b66\u5802\u4eca\u591c\u53d8\u6210\u9152\u5e97\u4e86\uff0c\u800c\u4e14\u751f\u610f\u8fd8\u771f\u4e0d\u9519\uff0c\u6bcf\u4e2a\u4eba\u90fd \u8981\u6765\u559d\u4e24\u676f\u3002\u201d \u3000\u3000\u695a\u56fd\u9999\u5374\u66f4\u5947\u602a\uff0c\u542c\u4ed6\u4eec\u7684\u8bf4\u6cd5\uff0c\u8fd9\u9ed1\u8863\u4eba\u5728\u7b49\u7684\u7adf\u4f3c\u4e4e\u662f\u4ed6\u7684\u60c5\u4eba\uff0c\u4f46\u4ed6\u4e3a\u4f55\u8981\u7ea6\u4f1a \u5230\u8fd9\u79cd\u5730\u65b9\u89c1\u9762\u5462\uff1f \u3000\u3000\u90a3\u5973\u5b69\u5b50\u96be\u9053\u4e5f\u548c\u4ed6\u4e00\u6837\u89c1\u4e0d\u5f97\u4eba\u9ebd\uff1f \u3000\u3000\u53ea\u89c1\u9ed1\u8863\u4eba\u679c\u7136\u5e26\u6765\u4e86\u4e00\u5927\u888b\u4e1c\u897f\uff0c\u4ed6\u4e00\u6837\u6837\u7684\u62ff\u51fa\u6765\u6446\u5230\u684c\u5b50\u4e0a\uff0c\u8fd8\u5e26\u7740\u7b11\u9053\uff1a\u201c\u7092 \u8695\u8c46\u548c\u82b1\u751f\u7c73\u867d\u7136\u90fd\u662f\u6700\u5e73\u5e38\u7684\u4e1c\u897f\uff0c\u4f46\u5979\u5374\u89c9\u5f97\u6bd4\u4ec0\u9ebd\u5c71\u73cd\u6d77\u5473\u90fd\u597d\u5403\uff0c\u4e0a\u6b21\u5979\u4e00\u4e2a\u4eba\u5c31 \u51e0\u4e4e\u5403\u4e86\u4e24\u65a4\u3002\u201d \u3000\u3000\u9ec4\u9c81\u76f4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8d8a\u662f\u5e73\u5e38\u7684\u4e1c\u897f\uff0c\u6709\u4e9b\u4eba\u8d8a\u662f\u89c9\u5f97\u73cd\u8d35\uff0c\u8fd9\u6015\u4e5f\u5c31\u662f\u90a3\u4e9b\u5929\u6f62\u8d35\u80c4 \u4eec\u7684\u60b2\u54c0\uff0c\u56e0\u4e3a\u4ed6\u4eec\u867d\u7136\u4eab\u5c3d\u4eba\u95f4\u7684\u8363\u534e\u5bcc\u8d35\uff0c\u4f46\u4e00\u4e9b\u5e73\u5e38\u4eba\u90fd\u80fd\u4eab\u53d7\u7684\u4e50\u8da3\uff0c\u4ed6\u4eec\u53cd\u800c\u6c38 \u8fdc\u4e5f\u4eab\u53d7\u4e0d\u5230\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9ed8\u7136\u534a\u664c\uff0c\u5ffd\u7136\u8f6c\u8fc7\u8eab\uff0c\u5583\u5583\u9053\uff1a\u201c\u6211\u5b9e\u5728\u5bf9\u4e0d\u8d77\u5979\uff0c\u6211\u672c\u8be5\u5e26\u5979\u8d70\u7684\uff0c\u4f46\u6211\u5374 \u662f\u4e2a\u61e6\u592b\uff0c\u7adf\u773c\u770b\u7740\u5979\u53bb\u5fcd\u53d7\u90a3\u79cd\u8981\u547d\u7684\u5bc2\u5bde\u3002\u201d \u3000\u3000\u4ed6\u4ee5\u80cc\u5bf9\u7740\u9ec4\u9c81\u76f4\uff0c\u4e5f\u4e0d\u613f\u88ab\u9ec4\u9c81\u76f4\u770b\u5230\u4ed6\u5728\u6084\u6084\u7684\u62ed\u6cea\uff0c\u5374\u4e0d\u77e5\u7a97\u5916\u9ed1\u6697\u4e2d\u6709\u53c1\u4e2a\u4eba \u6b63\u770b\u5f97\u6e05\u6e05\u695a\u695a\u3002 \u3000\u3000\u8fd9\u65f6\u9ec4\u9c81\u76f4\u5df2\u71c3\u8d77\u4e86\u4e00\u6839\u8721\u70db\uff0c\u5c4b\u5b50\u91cc\u867d\u7136\u5149\u4eae\u4e86\uff0c\u4f46\u5374\u9aa4\u7136\u6c88\u5bc2\u4e86\u4e0b\u6765\uff0c\u4eae\u5149\u5e76\u4e0d\u80fd \u4ee4\u8fd9\u6c88\u5bc2\u53d8\u5f97\u597d\u53d7\u4e9b\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u4eec\u6b63\u5728\u7b49\u5f85\uff0c\u4e16\u4e0a\u6839\u672c\u5c31\u6ca1\u6709\u4efb\u4f55\u4e8b\u4f1a\u6bd4\u7b49\u5f85\u66f4\u96be\u53d7\u7684\uff0c\u7adf\u9c81\u76f4\u5df2\u6e10\u6e10\u6709\u4e9b\u4e0d \u5b89\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u8d70\u5230\u7a97\u53e3\uff0c\u51fa\u795e\u7684\u671b\u7740\u8fdc\u65b9\u3002 \u3000\u3000\u8fdc\u65b9\u7684\u9ed1\u6697\u540f\u6d53\uff0c\u4ed6\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u5583\u5583\u9053\uff1a\u201c\u73b0\u5728\u6015\u65e9\u5df2\u8fc7\u4e86\u53c1\u66f4\u3002\u201d \u3000\u3000\u9ec4\u9c81\u76f4\u9053\uff1a\u201c\u8fd8\u6ca1\u6709\u90a3\u9ebd\u665a\u5427\uff1f\u201d \u3000\u3000\u9ed1\u8863\u4eba\u53c8\u6447\u4e86\u6447\u5934\uff0c\u9053\uff1a\u201c\u4f60\u60f3\uff0c\u4eca\u5929\u665a\u4e0a\u5979\u4f1a\u4e0d\u4f1a\u6765\uff1f\u201d \u3000\u3000\u9ec4\u9c81\u76f4\u52c9\u5f3a\u7b11\u9053\uff1a\u201c\u7edd\u4e0d\u4f1a\u4e0d\u6765\u7684\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u8f6c\u8fc7\u8eab\uff0c\u9eef\u7136\u9053\uff1a\u201c\u5176\u5b9e\uff0c\u5979\u4e0d\u6765\u4e5f\u597d\uff0c\u6211\u82e5\u662f\u5979\uff0c\u4e5f\u672a\u5fc5\u4f1a\u6765\u7684\uff0c\u6211\u2026\u2026\u201d \u3000\u3000\u7a81\u542c\u95e8\u5916\u201c\u7b03\u201d\u7684\u4e00\u4f1a\uff0c\u9ed1\u8863\u4eba\u548c\u9ec4\u9c81\u76f4\u970d\u7136\u8f6c\u8fc7\u8eab\uff0c\u5c31\u53d1\u73b0\u4e00\u6761\u74e2\u9038\u800c\u82d7\u6761\u7684\u767d\u8863\u4eba \u5f71\uff0c\u5df2\u7ad9\u5728\u95e8\u53e3\u3002 \u3000\u3000\u95e8\u5916\u8fd8\u662f\u5f88\u9ed1\u6697\uff0c\u80e1\u94c1\u82b1\u5e76\u6ca1\u6709\u770b\u6e05\u8fd9\u767d\u8863\u4eba\u5f71\uff0c\u5374\u53d1\u73b0\u695a\u7559\u9999\u7684\u5634\u5ffd\u7136\u5f20\u5f00\u4e86\uff0c\u5c31\u597d \u50cf\u5ffd\u7136\u7834\u4eba\u8e29\u4e86\u4e00\u811a\u3002 \u3000\u3000\u53ea\u56e0\u4ed6\u5df2\u770b\u6e05\u95e8\u5916\u8fd9\u4ed9\u5b50\u822c\u7684\u767d\u8863\u4eba\u5f71\uff0c\u4ed6\u5df2\u770b\u5230\u5979\u90a3\u7f8e\u4e3d\u800c\u51b7\u6f20\u7684\u773c\u775b\uff0c\u8fd9\u4eba\u8d6b\u7136\u7adf \u662f\u5bab\u5357\u71d5\u3002 \u3000\u3000\u4ed6\u518d\u4e5f\u60f3\u4e0d\u5230\u9ed1\u8863\u4eba\u5728\u8fd9\u91cc\u7b49\u7684\u7adf\u662f\u5bab\u5357\u71d5\uff0c\u7adf\u60f3\u4e0d\u5230\u51b7\u82e5\u51b0\u971c\u7684\u5bab\u5357\u71d5\uff0c\u7adf\u662f\u8fd9\u9ed1\u8863 \u4eba\u9b42\u7275\u68a6\u8426\u7684\u60c5\u4eba\u3002 \u3000\u3000\u4ed6\u4e00\u76f4\u8ba4\u4e3a\u5bab\u5357\u71d5\u662f\u4e16\u4e0a\u6700\u5723\u6d01\uff0c\u6700\u4e0d\u53ef\u5192\u6e0e\u7684\u5973\u5b50\uff0c\u8c01\u77e5\u9053\u5979\u5c45\u7136\u4e5f\u6709\u4e2a\u5730\u4e0b\u7684\u60c5 \u90ce\u3002 \u3000\u3000\u695a\u7559\u9999\u6697\u4e2d\u53f9\u4e86\u53e3\u6c14\uff0c\u597d\u50cf\u89c9\u5f97\u81ea\u5df1\u4e0a\u4e86\u522b\u4eba\u7684\u5f53\u2014\u2014\u5916\u9762\u5c31\u7b97\u662f\u4ed6\u8001\u5a46\uff0c\u4ed6\u6015\u90fd\u4e0d\u4f1a \u6bd4\u6b64\u523b\u66f4\u60ca\u8bb6\u3002 \u3000\u3000\u56e0\u4e3a\u4ee4\u7537\u4eba\u4eec\u6700\u751f\u6c14\u7684\u4e8b\uff0c\u5c31\u662f\u4ed6\u4e0d\u80fd\u5f97\u5230\u7684\u5973\u4eba\uff0c\u522b\u4eba\u53cd\u800c\u5f97\u5230\u4e86\uff0c\u8fd9\u662f\u4efb\u4f55\u7537\u4eba\u90fd \u65e0\u6cd5\u5fcd\u53d7\u7684\u3002 \u3000\u3000\u53ea\u89c1\u9ed1\u8863\u4eba\u6b22\u559c\u7684\u8fce\u4e86\u4e0a\u53bb\uff0c\u5374\u53c8\u9aa4\u7136\u505c\u4e0b\u811a\u6b65\uff0c\u5931\u58f0\u9053\uff1a\u201c\u5bab\u59d1\u5a18\uff0c\u662f\u4f60\u3002\u201d \u3000\u3000\u7206\u5357\u71d5\u8f7b\u76c8\u7684\u8d70\u4e86\u8fdb\u6765\uff0c\u6de1\u6de1\u9053\uff1a\u201c\u6211\u5ffd\u7136\u6709\u4e9b\u79c1\u4e8b\uff0c\u6240\u4ee5\u6765\u8fdf\uff0c\u62b1\u6b49\u5f97\u5f88\u3002\u201d \u3000\u3000\u5979\u5634\u91cc\u867d\u5728\u8bf4\u62b1\u6b49\uff0c\u4f46\u8bed\u6c14\u51b7\u6f20\uff0c\u8c01\u90fd\u53ef\u4ee5\u542c\u51fa\u5979\u8fde\u4e00\u5206\u62b1\u6b49\u7684\u610f\u601d\u90fd\u6ca1\u6709\uff0c\u695a\u7559\u9999\u6697 \u4e2d\u5ffd\u53c8\u677e\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u5df2\u770b\u51fa\u5bab\u5357\u71d5\u548c\u8fd9\u9ed1\u8863\u4eba\u7edd\u6ca1\u6709\u4ec0\u9ebd\u4eb2\u871c\u7684\u5173\u7cfb\uff0c\u90a3\u9ebd\uff0c\u9ed1\u8863\u4eba\u7b49\u7684\u96be\u9053\u5e76\u4e0d\u662f \u5979\u9ebd\uff1f \u3000\u3000\u65e2\u7136\u4e0d\u662f\u5979\uff0c\u5979\u4e3a\u4f55\u8981\u6765\u5462\uff1f \u3000\u3000\u9ed1\u8863\u4eba\u6014\u4e86\u534a\u664c\uff0c\u5782\u4e0b\u4e86\u5934\uff0c\u9053\uff1a\u201c\u5c0f\u9759\u5979\u2026\u2026\u5979\u4e0d\u80fd\u6765\u4e86\uff0c\u662f\u9ebd\uff1f\u201d \u3000\u3000\u7206\u5357\u71d5\u9053\uff1a\u201c\u5979\u82e5\u80fd\u6765\uff0c\u6211\u5c31\u4e0d\u4f1a\u6765\u4e86\uff0c\u662f\u5417\uff1f\u201d \u3000\u3000\u9ed1\u8863\u4eba\u832b\u7136\u70b9\u7740\u5934\uff0c\u5583\u5583\u9053\uff1a\u201c\u4e0d\u6765\u4e5f\u597d\uff0c\u6211\u65e9\u5c31\u8bf4\u8fc7\uff0c\u5979\u4e0d\u6765\u4e5f\u597d\u3002\u201d \u3000\u3000\u9ec4\u9c81\u76f4\u5ffd\u7136\u9053\uff1a\u201c\u662f\u4e0d\u662f\u6539\u671f\u4e86\uff1f\u201d \u3000\u3000\u4ed6\u6ee1\u6518\u7740\u5e0c\u671b\uff0c\u671b\u7740\u5bab\u5357\u71d5\uff0c\u5bab\u5357\u71d5\u5374\u77a7\u90fd\u4e0d\u77a7\u4ed6\u4e00\u773c\uff0c\u6de1\u6de1\u9053\uff1a\u201c\u5979\u4ee5\u5f8c\u4e5f\u4e0d\u4f1a\u6765 \u4e86\uff0c\u6c38\u8fdc\u4e0d\u4f1a\u6765\u4e86\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u7684\u4e00\u53cc\u624b\u5ffd\u7136\u62bd\u631b\u7740\u7d27\u63e1\u4e86\u8d77\u6765\uff0c\u55c4\u58f0\u9053\uff1a\u201c\u5979\u6709\u6ca1\u6709\u2026\u2026\uff1a\u6709\u6ca1\u6709\u4ec0\u9ebd\u4fe1\u5e26\u7ed9 \u6211\uff1f\u201d \u3000\u3000\u7206\u5357\u71d5\u9053\uff1a\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u8eab\u5b50\u98a4\u6296\u7740\uff0c\u5ffd\u7136\u72c2\u543c\u9053\uff1a\u201c\u4e3a\u4ec0\u9ebd\uff1f\u4f60\u5e08\u5085\u660e\u660e\u7b54\u5e94\u8fc7\u6211\uff0c\u6bcf\u9694\u4e94\u5e74\u8ba9\u6211\u89c1\u5979 \u4e00\u9762\u7684\uff0c\u73b0\u5728\u4e3a\u4ec0\u9ebd\u53cd\u6094\u4e86\uff0c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u7206\u5357\u71d5\u51b7\u51b7\u9053\uff1a\u201c\u6211\u5e08\u5085\u5e76\u6ca1\u6709\u53cd\u6094\uff0c\u5979\u8001\u4eba\u5bb6\u8bf4\u51fa\u6765\u7684\u8bdd\uff0c\u6c38\u65e0\u66f4\u6539\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u90a3\u9ebd\u5979\u4e3a\u4f55\u4e0d\u6765\u89c1\u6211\uff1f\u6211\u7edd\u4e0d\u76f8\u4fe1\u5979\u4f1a\u4e0d\u613f\u89c1\u6211\u3002\u201d \u3000\u3000\u7206\u5357\u71d5\u9053\uff1a\u201c\u5979\u4e5f\u4e0d\u662f\u4e0d\u613f\u89c1\u4f60\uff0c\u800c\u662f\u5df2\u4e0d\u80fd\u89c1\u4f60\u4e86\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u8eab\u5b50\u9aa4\u7136\u4e00\u9707\uff0c\u5c31\u5f77\u4f5b\u6709\u4e00\u9053\u65e0\u5f62\u7684\u95ea\u7535\u51fb\u5728\u4ed6\u8eab\u4e0a\uff0c\u4ed6\u4e00\u6b65\u6b65\u5f80\u5f8c\u9000\uff0c\u98a4\u58f0 \u9053\uff1a\u201c\u5979\u96be\u9053\u2026\u2026\u96be\u9053\u5df2\u2026\u2026\u201d \u3000\u3000\u7206\u5357\u71d5\u5c45\u7136\u4e5f\u8f7b\u8f7b\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5979\u5df2\u6c38\u8fdc\u4e0d\u5fc5\u518d\u5fcd\u53d7\u4eba\u4e16\u95f4\u7684\u75db\u82e6\u4e86\uff0c\u5979\u5b9e\u5728\u6bd4 \u4f60\u6211\u90fd\u5e78\u8fd0\u5f97\u591a\u3002\u201d \u3000\u3000\u5979\u8bdd\u672b\u8bf4\u5b8c\uff0c\u9ed1\u8863\u4eba\u5df2\u8f6f\u8f6f\u7684\u5012\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u9ec4\u9c81\u76f4\u62a2\u8fc7\u53bb\u6276\u4f4f\u4ed6\uff0c\u55c4\u58f0\u9053\uff1a\u201c\u4e0d\u77e5\u9053\u59d1\u5a18\u80fd\u4e0d\u80fd\u544a\u8bc9\u6211\u4eec\uff0c\u5979\u662f\u600e\u6837\u6b7b\u7684\uff1f\u201d \u3000\u3000\u7206\u5357\u71d5\u9ed8\u7136\u534a\u664c\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u6211\u53ea\u80fd\u544a\u8bc9\u4f60\uff0c\u5979\u662f\u4e3a\u4e86\u7ef4\u62a4\u201c\u795e\u6c34\u5bab\u201d\u7684\u5149\u8363\u800c\u6b7b\u7684\uff0c \u53ea\u56e0\u5979\u662f\u4e2a\u5f88\u6709\u9aa8\u6c14\u7684\u5973\u5b69\u5b50\uff0c\u6211\u4eec\u90fd\u4e3a\u5979\u9a84\u50b2\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u832b\u7136\u70b9\u7740\u5934\uff0c\u5583\u5583\u9053\uff1a\u201c\u591a\u8c22\u4f60\u544a\u8bc9\u6211\uff0c\u6211\u2026\u2026\u6211\u6068\u9ad8\u5174\u2026\u2026\u201d \u3000\u3000\u8bf4\u5230\u201c\u9ad8\u5174\u201d\u4e24\u5b57\uff0c\u4ed6\u76ee\u4e2d\u5df2\u6d41\u4e0b\u6cea\u6765\u3002 \u3000\u3000\u7206\u5357\u71d5\u53c8\u6c88\u9ed8\u4e86\u534a\u664c\uff0c\u4e00\u5b57\u5b57\u9053\uff1a\u201c\u4f60\u6709\u8fd9\u9ebd\u6837\u4e00\u4e2a\u5973\u513f\uff0c\u5b9e\u5728\u662f\u4f60\u7684\u8fd0\u6c14\uff0c\u56e0\u4e3a\u4f60\u5b9e \u5728\u4e0d\u914d\u7684\u3002\u201d \u3000\u3000\u542c\u5230\u8fd9\u91cc\uff0c\u695a\u7559\u9999\u5fc3\u91cc\u53c8\u662f\u60ed\u6127\uff0c\u53c8\u662f\u96be\u53d7\u3002 \u3000\u3000\u4ed6\u8fd9\u624d\u77e5\u9053\u81ea\u5df1\u65b9\u5168\u90fd\u60f3\u9519\u4e86\uff0c\u8fd9\u9ed1\u8863\u4eba\u7b49\u7684\u5e76\u4e0d\u662f\u4ed6\u7684\u60c5\u4eba\uff0c\u800c\u662f\u4ed6\u7684\u5973\u513f\u3002 \u3000\u3000\u53ea\u542c\u5bab\u5357\u71d5\u51b7\u51b7\u63a5\u9053\uff1a\u201c\u73b0\u5728\u5979\u5df2\u6b7b\u4e86\uff0c\u4f60\u548c\u201c\u795e\u6c34\u5bab\u201d\u5c31\u518d\u4e5f\u6ca1\u6709\u4e1d\u6beb\u5173\u7cfb\uff0c\u6240\u4ee5\uff0c \u5bb6\u5e08\u5e0c\u671b\u4f60\u4ee5\u5f8c\u6700\u597d\u83ab\u5230\u8fd9\u9644\u8fd1\u6765\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u4f46\u2026\u2026\u4f46\u5979\u7684\u5c38\u9aa8\u2026\u2026\uff1a\u201c\u5bab\u5357\u71d5\u9053\uff1a\u201c\u5979\u7684\u5c38\u9aa8\uff0c\u6211\u4eec\u5df2\u5b89\u846c\u4e86\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u6211\u80fd\u4e0d\u80fd\u5230\u5979\u5893\u524d\u53bb\u77a7\u77a7\uff1f\u201d \u3000\u3000\u7206\u5357\u71d5\u9053\uff1a\u201c\u4e0d\u80fd\u3002\u201d \u3000\u3000\u5979\u4f3c\u5df2\u51b3\u5fc3\u4e0d\u518d\u542c\u9ed1\u8863\u4eba\u8bf4\u8bdd\uff0c\u8f6c\u8eab\u8d70\u4e86\u51fa\u53bb\u3002\u4f46\u8d70\u5230\u95e8\u53e3\uff0c\u5979\u5ffd\u53c8\u8f6c\u56de\u5934\uff0c\u60a0\u7136\u9053\uff1a \u201c\u4f60\u53ef\u77e5\u9053\u6c5f\u6e56\u4e0a\u6709\u4e2a\u53eb\u695a\u7559\u9999\u7684\u4eba\uff1f\u201d \u3000\u3000\u9ed1\u8863\u4eba\u53ea\u662f\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u7206\u5357\u71d5\u9053\uff1a\u201c\u5f88\u597d\uff0c\u4f60\u82e5\u89c1\u5230\u4ed6\uff0c\u6700\u597d\u6740\u4e86\u4ed6\uff0c\u56e0\u4e3a\u53f8\u5f92\u9759\u5c31\u662f\u6b7b\u5728\u4ed6\u624b\u4e0a\u7684\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u8138\u90fd\u6c14\u767d\u4e86\uff0c\u4ed6\u5b9e\u5728\u60f3\u4e0d\u5230\u8fd9\u4f4d\u201c\u5723\u6d01\u201d\u7684\u5bab\u5357\u71d5\u59d1\u5a18\uff0c\u8bf4\u8d77\u8c0e\u8bdd\u6765\u5c31\u50cf\u5403\u767d\u83dc \u4f3c\u7684\uff0c\u800c\u4e14\u8fd8\u4e00\u5b9a\u60f3\u8981\u4ed6\u7684\u547d\u3002 \u3000\u3000\u9664\u6b64\u4e4b\u5916\uff0c\u4ed6\u4e5f\u5f88\u60ca\u8bb6\uff0c\u56e0\u4e3a\u4ed6\u66f4\u60f3\u4e0d\u5230\u8fd9\u9ed1\u8863\u4eba\u7684\u5973\u513f\uff0c\u7adf\u662f\u4e3a\u65e0\u82b1\u6b89\u60c5\u800c\u6b7b\u7684\u53f8\u5f92 \u9759\u3002 \u3000\u3000\u53ea\u542c\u201c\u7830\u201d\u7684\u4e00\u58f0\uff0c\u4e00\u5f20\u684c\u5b50\u5df2\u88ab\u9ed1\u8863\u4eba\u62cd\u788e\u3002 \u3000\u3000\u4ed6\u7d27\u63e1\u7740\u53cc\u62f3\uff0c\u54fc\u58f0\u9053\uff1a\u201c\u695a\u7559\u9999\uff0c\u695a\u7559\u9999\uff0c\u6211\u2026\u2026\u6211\u90a3\u5929\u4e3a\u4ec0\u9ebd\u4e0d\u6740\u6b7b\u4ed6\u3002\u201d \u3000\u3000\u9ec4\u9c81\u76f4\u6014\u4e86\u534a\u664c\uff0c\u53ea\u662f\u4e0d\u4f4f\u5583\u5583\u81ea\u8bed\u9053\uff1a\u201c\u6709\u8fd9\u79cd\u4e8b\uff1f\u4e16\u4e0a\u771f\u4f1a\u6709\u8fd9\u79cd\u4e8b\uff1f\u201d \u3000\u3000\u9ed1\u8863\u4eba\u970d\u7136\u7ad9\u8d77\uff0c\u53c8\u201c\u5657\u201d\u5730\u751f\u4e86\u4e0b\u53bb\uff0c\u4f46\u5168\u8eab\u4f3c\u4e4e\u5df2\u5448\u865a\u8131\uff0c\u7d27\u63e1\u7740\u7684\u53cc\u624b\u4e5f\u677e\u5f00 \u4e86\u3002 \u3000\u3000\u997c\u4e86\u534a\u664c\uff0c\u4ed6\u7adf\u7eb5\u58f0\u72c2\u7b11\u8d77\u6765\u3002 \u3000\u3000\u9ec4\u9c81\u76f4\u53d8\u8272\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u2026\u2026\u201d \u3000\u3000\u9ed1\u8863\u4eba\u72c2\u7b11\u9053\uff1a\u201c\u6211\u6ca1\u6709\u600e\u6837\uff0c\u53ea\u4e0d\u8fc7\u662f\u5728\u7b11\u6211\u81ea\u5df1\u800c\u5df2\uff0c\u6211\u201c\u96c4\u5a18\u5b50\u201d\u4e00\u751f\u4e2d\u4e5f\u4e0d\u77e5 \u6bc1\u4e86\u591a\u5c11\u4eba\u7684\u5973\u513f\uff0c\u73b0\u5728\u522b\u4eba\u53ea\u4e0d\u8fc7\u6740\u6211\u4e00\u4e2a\u5973\u513f\uff0c\u6211\u4e3a\u4f55\u8981\u6068\u4ed6\uff0c\u8fd9\u4e5f\u8bb8\u5c31\u662f\u62a5\u5e94\uff0c\u8fd9\u662f \u8001\u5929\u7ed9\u6211\u7684\u62a5\u5e94\u3002\u201d \u3000\u3000\u8bf4\u5230\u5f8c\u6765\uff0c\u4ed6\u7684\u72c2\u7b11\u5df2\u53d8\u4e3a\u75db\u54ed\u3002 \u3000\u3000\u4f46\u6234\u72ec\u884c\u3001\u80e1\u94c1\u5316\u548c\u695a\u7559\u9999\uff0c\u5374\u5df2\u5403\u60ca\u5f97\u8bf4\u4e0d\u51fa\u8bdd\u6765\uff0c\u4ed6\u4eec\u4eca\u5929\u665a\u4e0a\u4e5f\u9047\u89c1\u4e86\u5f88\u591a\u610f\u5916 \u7684\u4e8b\u3002 \u3000\u3000\u53ef\u662f\uff0c\u4efb\u4f55\u4e8b\u4e5f\u4e0d\u4f1a\u6bd4\u8fd9\u4ef6\u4e8b\u66f4\u4ee4\u4ed6\u4eec\u5403\u60ca\u4e86\u3002 \u3000\u3000\u8fd9\u795e\u79d8\u7684\u9ed1\u8863\u4eba\uff0c\u539f\u6765\u5c31\u662f\u201c\u96c4\u5a18\u5b50\u201d\u3002 \u3000\u3000\u96be\u602a\u4ed6\u8bf4\uff1a\u201c\u5929\u4e0b\u7684\u4eba\u90fd\u8981\u5c06\u4ed6\u6740\u4e4b\u800c\u5f8c\u5feb\u3002\u201d \u3000\u3000\u96be\u602a\u4ed6\u8138\u4e0a\u7684\u9762\u5177\u5982\u6b64\u7cbe\u5de7\uff0c\u884c\u8e2a\u5982\u6b64\u8be1\u79d8\u3002 \u3000\u3000\u8f7b\u529f\u53c8\u5982\u6b64\u9ad8\u5999\u3002 \u3000\u3000\u96be\u602a\u4ed6\u8bf4\uff1a\u201c\u4efb\u4f55\u4eba\u90fd\u4e0d\u4f1a\u76f8\u4fe1\u541b\u5b50\u5251\u4f1a\u548c\u4ed6\u4ea4\u670b\u53cb\u3002\u201d \u3000\u3000\u6b66\u6797\u4e2d\u7b2c\u4e00\u4e2a\u541b\u5b50\u4eba\uff0c\u7adf\u4f1a\u548c\u91c7\u82b1\u6deb\u8d3c\u4ea4\u670b\u53cb\uff0c\u539f\u662f\u4efb\u4f55\u4eba\u90fd\u68a6\u60f3\u4e0d\u5230\u7684\u4e8b\uff0c\u96be\u602a\u4ed6\u8981 \u548c\u9ec4\u9c81\u76f4\u5f62\u5f71\u4e0d\u79bb\uff0c\u539f\u6765\u4ed6\u5c31\u662f\u8981\u4ee5\u9ec4\u9c81\u76f4\u7684\u8eab\u4efd\u6765\u63a9\u62a4\u81ea\u5df1\u3002 \u3000\u3000\u96be\u602a\u9ec4\u9c81\u76f4\u518d\u53c1\u8bf4\uff1a\u201c\u4ed6\u6709\u4e0d\u5f97\u5df2\u7684\u82e6\u8877\uff0c\u5e0c\u671b\u695a\u7559\u9999\u4e0d\u8981\u8ffd\u7a76\u3002\u201d\u539f\u6765\u4ed6\u5c31\u662f\u6015\u695a\u7559 \u9999\u53d1\u73b0\u4ed6\u7684\u79d8\u5bc6\u3002 \u3000\u3000\u8fd9\u4e9b\u4ee4\u4eba\u60f3\u4e0d\u901a\u7684\u4e8b\uff0c\u73b0\u5728\u4ed6\u4eec\u603b\u7b97\u90fd\u5df2\u60f3\u901a\u4e86\u3002 \u3000\u3000\u53ef\u662f\uff0c\u201c\u96c4\u5a18\u5b50\u201d\u4e0d\u662f\u660e\u660e\u5df2\u7ecf\u6b7b\u4e86\u9ebd\uff1f\u6c5f\u6e56\u4e2d\u4eba\u4eba\u90fd\u77e5\u9053\u4ed6\u5df2\u6b7b\u5728\u201c\u795e\u6c34\u5bab\u201d\u4e3b\u4eba\u7684 \u624b\u4e0a\u3002 \u3000\u3000\u4ed6\u4e3a\u4ec0\u9ebd\u504f\u504f\u8fdc\u6d3b\u7740\u5462\uff1f \u3000\u3000\u8bf4\u8bdd\u6c38\u65e0\u66f4\u6539\u7684\u795e\u6c34\u5bab\u4e3b\uff0c\u4e3a\u4ec0\u9ebd\u8981\u4e3a\u4ed6\u6492\u8c0e\uff1f\u4e00\u751f\u6700\u6068\u7537\u4eba\u7684\u795e\u6c34\u5bab\u4e3b\uff0c\u600e\u4f1a\u504f\u504f\u4e3a \u8fd9\u6700\u65e0\u803b\u7684\u7537\u4eba\u6492\u8c0e\uff1f \u3000\u3000\u8fd9\u4ef6\u4e8b\uff0c\u5374\u4ee4\u695a\u7559\u9999\u4ed6\u4eec\u66f4\u60f3\u4e0d\u901a\u4e86\u3002 \u3000\u3000\u695a\u7559\u9999\u548c\u80e1\u94c1\u82b1\u6b63\u5728\u60ca\u8bb6\u7740\uff0c\u7a81\u542c\u201c\u54fc\u201d\u7684\u4e00\u58f0\uff0c\u94c1\u72ec\u884c\u5df2\u81ea\u4ed6\u4eec\u8eab\u65c1\u7bad\u4e00\u822c\u7a9c\u4e86\u51fa \u53bb\u3002 \u3000\u3000\u4ed6\u7684\u4eba\u8fd8\u672a\u63a0\u5165\u7a97\u6237\uff0c\u5df2\u5389\u58f0\u9053\uff1a\u201c\u96c4\u5a18\u5b50\uff0c\u4f60\u8ba4\u5f97\u6211\u6234\u72ec\u884c\u9ebd\uff1f\u4e8c\u5341\u5e74\u524d\uff0c\u6211\u5df2\u51b3\u5fc3 \u4e3a\u6c5f\u6e56\u9664\u53bb\u4f60\u8fd9\u7978\u5bb3\uff0c\u4eca\u65e5\u4f60\u8fd8\u6709\u4ec0\u9ebd\u8bdd\u8bf4\uff1f\u201d \u3000\u3000\u96c4\u5a18\u5b50\u75f4\u75f4\u7684\u5750\u5728\u90a3\u91cc\uff0c\u51fa\u795e\u7684\u5446\u671b\u7740\u9762\u524d\u95ea\u52a8\u7684\u70db\u5149\uff0c\u4f3c\u4e4e\u6839\u672c\u6ca1\u6709\u542c\u5230\u4ed6\u7684\u6012\u9a82\u3002 \u3000\u3000\u9ec4\u9c81\u76f4\u5374\u5df2\u62a2\u5148\u4e00\u6b65\uff0c\u8fce\u4e0a\u4e86\u6234\u72ec\u884c\uff0c\u6c88\u58f0\u9053\uff1a\u201c\u4ed6\u4e0d\u662f\u96c4\u5a18\u5b50\uff0c\u96c4\u5a18\u5b50\u65e9\u5df2\u6b7b\u4e86\u3002\u201d \u3000\u3000\u6234\u72ec\u884c\u72c2\u7b11\u9053\uff1a\u201c\u4e45\u95fb\u201c\u541b\u5b50\u5251\u201d\u4e00\u751f\u4e0d\u8bf4\u8c0e\u8bdd\uff0c\u8c01\u77e5\u5374\u662f\u4e2a\u5927\u8a00\u6b3a\u4eba\uff0c\u6b3a\u4e16\u76d7\u540d\u4e4b \u8f88\uff0c\u5230\u4e86\u6b64\u65f6\uff0c\u5c45\u7136\u8fd8\u8981\u8bf4\u8c0e\u3002\u201d \u3000\u3000\u9ec4\u9c81\u76f4\u795e\u8272\u4e0d\u53d8\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u8001\u673d\u5e76\u672a\u8bf4\u8c0e\uff0c\u65e0\u6076\u4e0d\u505a\u7684\u96c4\u5a18\u5b50\u65e9\u5df2\u6b7b\u4e86\uff0c\u73b0\u5728\u5750\u5728\u8fd9 \u91cc\u7684\uff0c\u53ea\u662f\u4e2a\u5df2\u82e6\u5fc3\u5fcf\u6094\u4e86\u4e8c\u5341\u5e74\u7684\u53ef\u601c\u4eba\uff0c\u5df2\u53d7\u4e86\u4e8c\u5341\u5e74\u75db\u82e6\u78e8\u6298\uff0c\u4ece\u65e0\u4e00\u65e5\u80fd\u5b89\u7761\u7684\u53ef \u601c\u4eba\uff0c\u4e00\u4e2a\u521a\u77e5\u9053\u5973\u513f\u88ab\u4eba\u6740\u5bb3\u7684\u7236\u4eb2\u3002\u201d \u3000\u3000\u6234\u72ec\u884c\u51b7\u7b11\u9053\uff1a\u201c\u53ef\u601c\uff1f\u90a3\u4e9b\u6b7b\u5728\u4ed6\u624b\u4e0a\u7684\u597d\u5973\u5b50\u96be\u9053\u5c31\u4e0d\u53ef\u601c\uff1f\u4ed6\u8fd9\u4e00\u751f\u6240\u9020\u4e0b\u7684\u7f6a \u5b7d\uff0c\u96be\u9053\u5c31\u6b64\u4fbf\u80fd\u6d17\u6e05\uff1f\u201d \u3000\u3000\u9ec4\u9c81\u76f4\u9053\uff1a\u201c\u5c31\u7b97\u4ed6\u6240\u53d7\u7684\u6298\u78e8\u8fd8\u4e0d\u8db3\u5f25\u8865\u4ed6\u7684\u7f6a\u5b7d\uff0c\u4f46\u4ed6\u65e9\u5df2\u75db\u81ea\u6094\u6539\uff0c\u5df2\u53d8\u6210\u6211\u5e73 \u751f\u6240\u89c1\u5230\u7684\u6700\u5584\u826f\uff0c\u6700\u89c4\u77e9\u7684\u4eba\uff0c\u6240\u4ee5\u4f60\u73b0\u5728\u5982\u679c\u6740\u4e86\u4ed6\uff0c\u5e76\u4e0d\u662f\u6740\u6b7b\u4e2a\u6deb\u8d3c\uff0c\u800c\u662f\u6740\u6b7b\u4e86 \u4e00\u4e2a\u5584\u826f\u7684\u597d\u4eba\u3002\u201d \u3000\u3000\u4ed6\u957f\u957f\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u60f3\u901a\u4e86\u8fd9\u70b9\u4e4b\u5f8c\uff0c\u82e5\u8fd8\u8981\u6740\u4ed6\uff0c\u5c31\u8bf7\u52a8\u624b\u5427\uff01\u4ed6\u65e2\u4e0d\u4f1a\u53cd \u6297\uff0c\u6211\u4e5f\u7edd\u4e0d\u4f1a\u62e6\u963b\uff0c\u53ea\u4e0d\u8fc7\u2026\u2026\u201d \u3000\u3000\u6234\u72ec\u884c\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u53ea\u4e0d\u8fc7\u600e\u6837\uff1f\u201d \u3000\u3000\u9ec4\u9c81\u76f4\u4e00\u5b57\u5b57\u9053\uff1a\u201c\u53ea\u4e0d\u8fc7\u6211\u82e5\u89c1\u7740\u751f\u5e73\u597d\u53cb\u6b7b\u5728\u9762\u524d\uff0c\u4e5f\u7edd\u4e0d\u5fcd\u72ec\u751f\u3002\u201d \u3000\u3000\u6234\u72ec\u884c\u6014\u4e86\u6014\uff0c\u779f\u4e86\u7a97\u5916\u4e00\u773c\uff0c\u4f3c\u4e4e\u60f3\u8981\u695a\u7559\u9999\u6765\u4e3a\u4ed6\u505a\u4e2a\u4e3b\u610f\uff0c\u4f46\u695a\u7559\u9999\u73b0\u5728\u5374\u4e0d\u613f \u73b0\u8eab\u3002 \u3000\u3000\u4ed6\u81ea\u7136\u4e0d\u613f\u62c5\u8d77\u5c06\u53f8\u5f92\u9759\u6740\u6b7b\u7684\u7f6a\uff0c\u4ed6\u5df2\u77e5\u9053\u8fd9\u4ef6\u4e8b\u5728\u8fd9\u79cd\u65f6\u5019\uff0c\u65e0\u8bba\u8c01\u4e5f\u65e0\u6cd5\u89e3\u91ca\u5f97 \u6e05\u3002 \u3000\u3000\u53ea\u89c1\u9ec4\u9c81\u76f4\u795e\u8272\u5df2\u6e10\u6e10\u5b89\u8be6\uff0c\u76ee\u5149\u4e5f\u6e10\u6e10\u575a\u5b9a\uff0c\u4efb\u4f55\u4eba\u90fd\u53ef\u4ee5\u770b\u5f97\u51fa\u8fd9\u79cd\u4eba\u7684\u786e\u662f\u4e0d\u4f1a \u8bf4\u8c0e\u7684\u3002 \u3000\u3000\u6234\u72ec\u884c\u53f9\u4e86\u53e3\u6c14\uff0c\u5583\u5583\u9053\uff1a\u201c\u96c4\u5a18\u5b50\u80fd\u4ea4\u5230\u4f60\u8fd9\u79cd\u670b\u53cb\uff0c\u5b9e\u5728\u662f\u8fd0\u6c14\uff0c\u5947\u602a\u7684\u662f\uff0c\u4ed6\u8fd9 \u79cd\u4eba\u600e\u9ebd\u4f1a\u548c\u4f60\u8fd9\u79cd\u4eba\u4ea4\u4e0a\u670b\u53cb\u7684\u5462\uff1f\u201d \u3000\u3000\u4ed6\u4e0d\u8ba9\u9ec4\u9c81\u76f4\u8bf4\u8bdd\uff0c\u63a5\u7740\u53c8\u9053\uff1a\u201c\u5176\u5b9e\u6211\u4e5f\u5df2\u60f3\u5230\uff0c\u4e00\u4e2a\u51f6\u6deb\u6076\u6bd2\u7684\u4eba\uff0c\u662f\u7edd\u4e0d\u4f1a\u5bf9\u81ea \u5df1\u7684\u5973\u513f\u50cf\u4ed6\u90a3\u9ebd\u6837\u75bc\u7231\u7684\u2026\u2026\u201d \u3000\u3000\u695a\u7559\u9999\u53d1\u89c9\u4ed6\u8bf4\u8bdd\u7684\u58f0\u97f3\u5ffd\u7136\u6709\u4e86\u53d8\u5316\uff0c\u7adf\u53d8\u5f97\u6709\u4e9b\u542b\u7cca\u4e0d\u6e05\u4e86\uff0c\u800c\u4e14\u8d8a\u8bf4\u8d8a\u7f13\u6162\u3002 \u3000\u3000\u4ed6\u81ea\u5df1\u5374\u50cf\u662f\u5e76\u6ca1\u6709\u53d1\u89c9\uff0c\u8fd8\u5728\u63a5\u7740\u9053\uff1a\u201c\u96c4\u5a18\u5b50\u7adf\u4f1a\u5bf9\u81ea\u5df1\u7684\u5973\u513f\u6709\u5982\u6b64\u6df1\u60c5\uff0c\u8fd9\u5b9e \u5728\u4e5f\u662f\u4ee4\u4eba\u96be\u4fe1\u7684\u4e8b\uff0c\u5c31\u51ed\u8fd9\u4e00\u70b9\uff0c\u6211\u5c31\u8be5\u653e\u4e86\u4ed6\u3002\u201d \u3000\u3000\u6700\u5f8c\u4e00\u53e5\u8bdd\u8fd8\u6ca1\u6709\u8bf4\u5b8c\u7684\u65f6\u5019\uff0c\u4ed6\u8138\u8272\u5df2\u53d8\u4e86\uff0c\u8bf4\u5230\u201c\u653e\u4e86\u4ed6\u201d\u53c1\u4e2a\u5b57\u65f6\uff0c\u4ed6\u5df2\u51b2\u5230\u96c4 \u5a18\u5b50\u9762\u4e00\uff0c\u4e00\u62f3\u51fb\u51fa\u3002 \u3000\u3000\u96c4\u5a18\u5b50\u5e76\u6ca1\u6709\u95ea\u907f\uff0c\u6210\u540d\u6c5f\u6e56\u5782\u516d\u5341\u5e74\u7684\u201c\u5343\u91cc\u72ec\u884c\u4fa0\u201d\u8fd9\u4e00\u62f3\u51fb\u51fa\uff0c\u7adf\u53d8\u5f97\u5168\u65e0\u4e1d\u6beb \u529b\u6cf5\u3002 \u3000\u3000\u9ec4\u9c81\u76f4\u8138\u8272\u4e5f\u5df2\u5927\u53d8\uff0c\u77aa\u7740\u96c4\u5a18\u5b50\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u4e3a\u4f55\u2026\u2026\u201d \u3000\u3000\u6234\u72ec\u884c\u5636\u58f0\u9053\uff1a\u201c\u4f60\u8fd8\u4f1a\u4ec0\u9ebd\uff0c\u4f60\u6211\u4e24\u4eba\u5168\u90fd\u778e\u4e86\u773c\uff0c\u770b\u9519\u4e86\u4eba\u3002\u201d \u3000\u3000\u8fd9\u65f6\u80e1\u94c1\u82b1\u4e5f\u5df2\u770b\u51fa\u96c4\u5a18\u5b50\u7adf\u5728\u6697\u4e2d\u65bd\u653e\u4e86\u4e00\u79cd\u6781\u6076\u6bd2\u7684\u8ff7\u836f\uff0c\u5c06\u6234\u72ec\u884c\u548c\u4ed6\u7684\u6069\u53cb\u9ec4 \u9c81\u76f4\u8ff7\u5012\u3002 \u3000\u3000\u522b\u4eba\u8fd9\u9ebd\u6837\u5bf9\u4ed6\uff0c\u4ed6\u5374\u505a\u51fa\u8fd9\u79cd\u4e8b\u6765\uff0c\u201c\u96c4\u5a18\u5b50\u201d\u679c\u7136\u540d\u4e0d\u865a\u4f20\uff0c\u662f\u4e16\u4e0a\u6700\u5351\u9119\u6076\u6bd2\u7684 \u4eba\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u53ea\u89c9\u6012\u6124\u586b\u81ba\uff0c\u7acb\u523b\u5c31\u8981\u51b2\u51fa\u53bb\uff0c\u8c01\u77e5\u695a\u7559\u9999\u7adf\u53c8\u62c9\u4f4f\u4e86\u4ed6\uff0c\u800c\u4e14\u8fd8\u4e0d\u8ba9\u4ed6\u8bf4 \u8bdd\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u96c4\u5a18\u4e8e\u5df2\u7ad9\u4e86\u8d77\u6765\uff0c\u4ed6\u76ee\u4e2d\u5df2\u662f\u70ed\u6cea\u76c8\u7736\uff0c\u5374\u66f4\u886c\u5f97\u4ed6\u90a3\u5f20\u51b7\u6f20\u7684\u7751\u770b\u6765\u4efd \u5916\u8be1\u79d8\u3002 \u3000\u3000\u53ea\u89c1\u4ed6\u5411\u6234\u72ec\u884c\u6df1\u6df1\u4e00\u63d6\uff0c\u55c4\u58f0\u9053\uff1a\u201c\u6234\u5148\u751f\u7684\u4e0d\u6740\u4e4b\u6069\uff0c\u5728\u4e0b\u6c38\u751f\u96be\u4ee5\u5fd8\u8bb0\uff0c\u4f46\u6234\u5148 \u751f\u4e5f\u53ef\u4ee5\u653e\u5fc3\uff0c\u5728\u4e0b\u7edd\u4e0d\u4f1a\u8ba9\u4f60\u5f8c\u6094\u6ca1\u6709\u6740\u6211\u7684\u3002\u201d \u3000\u3000\u4ed6\u8f6c\u8fc7\u8eab\u671b\u7740\u9ec4\u9c81\u76f4\uff0c\u53c8\u5782\u4e0b\u5934\u9053\uff1a\u201c\u81f3\u65bc\u4f60\uff0c\u6211\u2026\u2026\u6211\u5b9e\u5728\u6ca1\u6709\u4ec0\u9ebd\u8bdd\u597d\u8bf4\uff0c\u4f60\u2026\u2026 \u4f60\u2026\u2026\u201d \u3000\u3000\u8bf4\u5230\u8fd9\u91cc\uff0c\u4ed6\u5589\u5934\u5df2\u585e\u4f4f\uff0c\u518d\u4e5f\u8bf4\u4e0d\u4e0b\u53bb\uff0c\u800c\u8fd9\u65f6\u6234\u72ec\u884c\u548c\u9ec4\u9c81\u76f4\u4e5f\u542c\u4e0d\u5230\u4ec0\u9ebd\u4e86\uff0c\u4ed6 \u4eec\u90fd\u5df2\u5012\u4e86\u4e0b\u53bb\u3002"}),showOnHomepage:!0},{name:"\u7b2c\u4e8c\u5341\u4e94\u7ae0\u3000\u6709\u5973\u6000\u6625",content:(0,s.jsx)(s.Fragment,{children:"\u9ec4\u9c81\u76f4\u5012\u5728\u5730\u4e0a\uff0c\u8fd8\u8bf4\u4e86\u6700\u5f8c\u4e00\u53e5\u8bdd\u3002 \u3000\u3000\u4ed6\u8bf4\u7684\u58f0\u97f3\u867d\u8f7b\u5fae\uff0c\u4f46\u6bcf\u4e2a\u5b57\u90fd\u80fd\u542c\u5f97\u5f88\u6e05\u695a\u3002 \u3000\u3000\u53ea\u542c\u4ed6\u4e00\u5b57\u5b57\u9053\uff1a\u201c\u6211\u7edd\u4e0d\u4f1a\u770b\u9519\u4f60\u3002\u201d \u3000\u3000\u96c4\u5a18\u5b50\u76ee\u4e2d\u7684\u773c\u6cea\uff0c\u518d\u4e5f\u5fcd\u4e0d\u4f4f\u593a\u7736\u800c\u51fa\u3002 \u3000\u3000\u4ed6\u75f4\u75f4\u7684\u671b\u7740\u5730\u4e0a\u5df2\u660f\u8ff7\u4e86\u7684\u9ec4\u9c81\u76f4\uff0c\u5ffd\u7136\u8dea\u4e86\u4e0b\u53bb\uff0c\u78d5\u4e86\u53c1\u4e2a\u5934\uff0c\u8131\u4e0b\u8eab\u4e0a\u7684\u957f\u886b\uff0c \u76d6\u5728\u9ec4\u9c81\u76f4\u8eab\u4e0a\u3002 \u3000\u3000\u4ed6\u7684\u624b\u5728\u98a4\u6296\u7740\uff0c\u98a4\u58f0\u9053\uff1a\u201c\u6211\u5bf9\u4e0d\u8d77\u4f60\u3002\u201d \u3000\u3000\u8fd9\u7b80\u7b80\u5355\u5355\u4e94\u4e2a\u5b57\u91cc\uff0c\u4e5f\u4e0d\u77e5\u542b\u8574\u7740\u591a\u5c11\u8f9b\u9178\uff1f\u591a\u5c11\u8840\u6cea\uff1f\u591a\u5c11\u53cb\u60c5\uff1f\u5f53\u771f\u4ee4\u95fb\u8005\u9f3b \u9178\u3002 \u3000\u3000\u7136\u5f8c\uff0c\u4ed6\u5c31\u8f6c\u8eab\u72c2\u5954\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u63c9\u7740\u9f3b\u5b50\uff0c\u9053\uff1a\u201c\u4ed6\u2026\u2026\u4ed6\u8fd9\u662f\u4ec0\u9ebd\u610f\u601d\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u53f9\u9053\uff1a\u201c\u4ed6\u8fd9\u53ea\u4e0d\u8fc7\u662f\u60f3\u5165\u795e\u6c34\u5bab\uff0c\u56e0\u4e3a\u65e0\u8bba\u4ed6\u5973\u513f\u662f\u751f\u662f\u6b7b\uff0c\u4e5f\u8981\u89c1\u5979\u6700\u5f8c\u4e00 \u9762\uff0c\u4f46\u4ed6\u4e5f\u77e5\u9053\u9ec4\u9c81\u76f4\u7edd\u4e0d\u4f1a\u8ba9\u4ed6\u53bb\u7684\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u56e0\u4e3a\u4ed6\u6b64\u53bb\u5fc5\u6b7b\u65e0\u7591\uff0c\u7adf\u9c81\u76f4\u4e0d\u5fcd\u773c\u770b\u4ed6\u53bb\u9001\u6b7b\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9eef\u7136\u9053\uff1a\u201c\u6b63\u662f\u5982\u6b64\uff0c\u6240\u4ee5\u6211\u4e00\u5b9a\u8981\u8ddf\u4ed6\u4e00\u9f50\u5165\u5bab\uff0c\u6234\u8001\u524d\u8f88\u548c\u9ec4\u8001\u5251\u5ba2\uff0c\u5c31\u5168 \u90fd\u4ea4\u7ed9\u4f60\u4e86\u3002\u201d \u3000\u3000\u4ed6\u8f7b\u8f7b\u4e00\u63a0\uff0c\u4fbf\u5df2\u63a0\u8fc7\u5c4b\u820d\u3002 \u3000\u3000\u53ea\u542c\u4ed6\u8bed\u58f0\u8fdc\u8fdc\u4f20\u6765\uff0c\u9053\uff1a\u201c\u83ab\u5fd8\u4e86\uff0c\u8fd8\u6709\u84c9\u513f\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u4e5f\u4e0d\u77e5\u662f\u5426\u542c\u5230\u4e86\u4ed6\u7684\u8bdd\uff0c\u53ea\u662f\u5583\u5583\u81ea\u8bed\u9053\uff1a\u201c\u539f\u6765\u96c4\u5a18\u5b50\u771f\u7684\u5df2\u6539\u8fc7\u81ea\u65b0\uff0c\u539f \u6765\u4ed6\u5bf9\u9ec4\u9c81\u76f4\u548c\u6234\u72ec\u884c\u5e76\u6ca1\u6709\u6076\u610f\uff0c\u4f46\u6211\u65b9\u82e5\u662f\u5fcd\u4e0d\u4f4f\u51b2\u4e86\u51fa\u53bb\uff0c\u82e5\u662f\u5931\u624b\u6740\u6b7b\u4e86\u4ed6\uff0c\u8fd8\u4e0d \u8ba9\u4ed6\u89e3\u91ca\uff0c\u90a3\u9ebd\u4ed6\u5c82\u975e\u6c38\u8fdc\u8981\u542b\u51a4\u4e5d\u6cc9\uff0c\u800c\u6211\u4e5f\u8bb8\u8fd8\u5728\u81ea\u9e23\u5f97\u610f\u3002\u201d \u3000\u3000\u4ed6\u4e0d\u6562\u518d\u60f3\u4e0b\u53bb\u3002 \u3000\u3000\u4ed6\u5df2\u6c57\u51fa\u5982\u96e8\uff0c\u6e7f\u900f\u91cd\u8863\u3002 \u3000\u3000\u8981\u8ddf\u8e2a\u96c4\u5a18\u5b50\u5e76\u4e0d\u662f\u4ef6\u5bb9\u6613\u4e8b\uff0c\u4ed6\u4e0d\u4f46\u8eab\u6cd5\u8fc5\u6025\uff0c\u800c\u4e14\u884c\u52a8\u7279\u522b\u673a\u8b66\u5c0f\u5fc3\uff0c\u8fd9\u4e9b\u90fd\u662f\u4ed6 \u5728\u957f\u5e74\u7684\u9003\u4ea1\u751f\u6daf\u4e2d\u953b\u70bc\u51fa\u6765\u7684\uff0c\u8981\u5728\u6697\u4e2d\u76ef\u7740\u4ed6\u800c\u4e0d\u88ab\u4ed6\u53d1\u89c9\uff0c\u4e16\u4e0a\u9664\u4e86\u695a\u7559\u9999\u5916\uff0c\u6015\u518d \u96be\u627e\u5f97\u51fa\u7b2c\u4e8c\u4e2a\u3002 \u3000\u3000\u56e0\u4e3a\u695a\u7559\u9999\u9664\u4e86\u8f7b\u529f\u8d85\u4eba\u4e4b\u5916\uff0c\u8fd8\u6709\u4e00\u53cc\u4efd\u5916\u9510\u5229\u7684\u773c\u775b\uff0c\u6240\u4ee5\u5e76\u4e0d\u9700\u8981\u8ffd\u5f97\u505a\u592a\u7d27\u3002 \u3000\u3000\u4ee4\u695a\u7559\u9999\u5947\u602a\u7684\u662f\uff0c\u4ed6\u5e76\u6ca1\u6709\u5954\u5411\u5c71\u533a\uff0c\u53cd\u800c\u63a0\u56de\u4e86\u90a3\u5c71\u57ce\u4e2d\u4e00\u5bb6\u5ba2\u6808\u91cc\uff0c\u96be\u9053\u4ed6\u5e76\u4e0d \u60f3\u5230\u795e\u6c34\u5bab\u53bb\u4e86\uff1f \u3000\u3000\u695a\u7559\u9999\u51e0\u4e4e\u8981\u4ee5\u4e3a\u81ea\u5df1\u731c\u9519\u4e86\u3002 \u3000\u3000\u4ed6\u4f4f\u7684\u5ba2\u6808\u5e76\u4e0d\u8fdc\uff0c\u4ed6\u5b9e\u5728\u60f3\u56de\u53bb\u770b\u770b\u82cf\u84c9\u84c9\uff0c\u53ef\u662f\u4ed6\u5374\u53c8\u4e0d\u613f\u9519\u8fc7\u96c4\u5a18\u5b50\uff0c\u56e0\u4e3a\u4ed6\u5df2 \u611f\u89c9\u5230\u96c4\u5a18\u5b50\u548c\u795e\u6c34\u5bab\u7684\u5173\u7cfb\u4f3c\u4e4e\u5f88\u6df1\uff0c\u800c\u4e14\u5f88\u4e0d\u5bfb\u5e38\uff0c\u4ed6\u60f3\u4ee5\u96c4\u5a18\u5b50\u4e3a\u6865\u6881\uff0c\u4ed6\u8ba4\u4e3a\u8fd9\u4e5f \u8bb8\u662f\u552f\u4e00\u7684\u6377\u5f84\u3002 \u3000\u3000\u73b0\u5728\u8ddd\u79bb\u5929\u4eae\u8fd8\u6709\u6bb5\u65f6\u5019\uff0c\u5c71\u57ce\u5728\u591c\u8272\u4e2d\u770b\u6765\u662f\u90a3\u9ebd\u5b89\u7965\u800c\u5b81\u9759\uff0c\u6708\u5149\u9759\u9759\u7684\u7167\u5728\u5c4b\u9876 \u4e0a\uff0c\u5c4b\u9876\u4e0b\u7684\u4eba\u4eec\u90fd\u5728\u6c88\u7761\uff0c\u4ed6\u4eec\u7684\u751f\u6d3b\u867d\u7136\u5e73\u51e1\u800c\u5355\u8c03\uff0c\u4f46\u5e73\u51e1\u5c82\u975e\u4e5f\u6b63\u662f\u8bb8\u591a\u79cd\u5e78\u798f\u4e4b \u4e00\u3002 \u3000\u3000\u695a\u7559\u9999\u51e0\u4e4e\u5df2\u5fd8\u8bb0\u5728\u5c4b\u9876\u4e0b\u5b89\u7761\u662f\u4ec0\u9ebd\u6ecb\u5473\u4e86\u3002 \u3000\u3000\u591c\u8272\u867d\u7136\u5f88\u7f8e\uff0c\u4f46\u53c1\u66f4\u534a\u591c\u7684\u8eb2\u5728\u5c4b\u9876\u4e0a\u7aa5\u63a2\u7740\u522b\u4eba\u7684\u79d8\u5bc6\uff0c\u65e0\u8bba\u5982\u4f55\u90fd\u4e0d\u662f\u4ef6\u4ee4\u4eba\u6109 \u5feb\u7684\u4e8b\u3002 \u3000\u3000\u5e78\u597d\u8fd9\u65f6\u96c4\u5a18\u5b50\u5df2\u51c9\u4e86\u51fa\u6765\uff0c\u4ed6\u53d1\u4eae\u7684\u773c\u775b\u5728\u9ed1\u6697\u4e2d\u4e00\u95ea\uff0c\u5c31\u50cf\u53ea\u732b\u4f3c\u7684\uff0c\u53c8\u6ca1\u5165\u9ed1\u6697 \u4e2d\u3002 \u3000\u3000\u695a\u7559\u9999\u53d1\u73b0\u4ed6\u624b\u4e0a\u5df2\u591a\u4e86\u4e2a\u9ed1\u8272\u7684\u76ae\u56ca\uff0c\u4ed6\u7279\u610f\u56de\u5230\u8fd9\u5ba2\u6808\u4e00\u6b21\uff0c\u663e\u7136\u5c31\u4e3a\u7684\u662f\u6765\u53d6\u8fd9 \u76ae\u56ca\u7684\u3002 \u3000\u3000\u56ca\u4e2d\u88c5\u7684\u662f\u4ec0\u9ebd\uff1f\u4ed6\u4e3a\u4f55\u8981\u5982\u6b64\u91cd\u89c6\uff1f \u3000\u3000\u8fd9\u6b21\u96c4\u5a18\u5b50\u624d\u76f4\u5954\u5c71\u533a\uff0c\u534a\u4e2a\u65f6\u8fb0\u5f8c\uff0c\u4ed6\u5df2\u5230\u4e86\u5c71\u9e93\uff0c\u4f46\u5374\u5e76\u6ca1\u6709\u4e0a\u5c71\uff0c\u53ea\u662f\u6cbf\u7740\u5c71\u811a \u98de\u63a0\u4e86\u4e00\u6bb5\u8def\u9014\u3002 \u3000\u3000\u4ed6\u7ecf\u8fc7\u7684\u5730\u65b9\u8d8a\u6765\u8d8a\u8352\u50fb\uff0c\u6709\u65f6\u8981\u8d8a\u8fc7\u5c71\u6cc9\uff0c\u6709\u65f6\u8981\u8d8a\u8fc7\u4e00\u5806\u5806\u7684\u8346\u68d8\uff0c\u6709\u65f6\u8fd8\u8981\u7a7f\u8fc7 \u4e00\u4e9b\u5f88\u7a84\u7684\u5c71\u9699\u3002 \u3000\u3000\u695a\u7559\u9999\u867d\u7136\u5f88\u7559\u610f\uff0c\u4f46\u4e0b\u6b21\u82e5\u8981\u4ed6\u518d\u6765\uff0c\u4ed6\u4e5f\u672b\u5fc5\u80fd\u627e\u5f97\u5230\u8fd9\u6761\u8def\uff0c\u96c4\u5a18\u5b50\u5374\u4f3c\u5bf9\u8fd9\u5c71 \u533a\u7684\u4e00\u8349\u4e00\u6728\u90fd\u5f88\u719f\u6089\u3002 \u3000\u3000\u4ed6\u751a\u81f3\u4ece\u6765\u4e5f\u6ca1\u6709\u505c\u4e0b\u6765\u8fa8\u8ba4\u65b9\u5411\uff0c\u8fd9\u6761\u8def\u4ed6\u4f3c\u4e4e\u5df2\u4e0d\u77e5\u9053\u6765\u8fc7\u591a\u5c11\u6b21\u4e86\uff0c\u5c31\u7b97\u95ed\u7740\u773c \u775b\u4e5f\u627e\u5f97\u5230\u3002 \u3000\u3000\u53ef\u662f\u8fdb\u5165\u5c71\u533a\u5f8c\uff0c\u4ed6\u7684\u884c\u52a8\u5c31\u66f4\u8c28\u614e\uff0c\u98de\u63a0\u5728\u7a7a\u4e2d\u65f6\uff0c\u90fd\u4f1a\u5ffd\u7136\u56de\u5934\u89c2\u671b\uff0c\u695a\u7559\u9999\u8ddf\u8e2a \u5f97\u4e5f\u5c31\u66f4\u5403\u529b\u3002 \u3000\u3000\u800c\u4e14\u8fd9\u65f6\u5929\u5df2\u7ecf\u6e10\u6e10\u4eae\u4e86\uff0c\u5c71\u5dc5\u5f8c\u5df2\u9732\u51fa\u4e86\u9576\u7740\u91d1\u8fb9\u7684\u4e91\u5f69\uff0c\u6728\u53f6\u4e0a\u7684\u9732\u73e0\u4e5f\u6e10\u6e10\u53d1\u51fa \u4e86\u95ea\u5149\u3002 \u3000\u3000\u5929\u82e5\u4e00\u4eae\uff0c\u695a\u7559\u9999\u5c31\u7edd\u5bf9\u65e0\u6cd5\u518d\u8ddf\u8e2a\u4ed6\u3002 \u3000\u3000\u8fd9\u65f6\u4e73\u767d\u8272\u7684\u6668\u96fe\u4e5f\u5df2\u5189\u5189\u5347\u8d77\uff0c\u4f3c\u4e4e\u5728\u8fd9\u5bc2\u5bde\u82cd\u51c9\u7684\u5c71\u8c37\u95f4\uff0c\u7b3c\u8d77\u4e86\u4e00\u5c42\u8f7b\u7eb1\uff0c\u4f7f\u666f \u8272\u770b\u6765\u66f4\u51c4\u8ff7\u5e7d\u8273\u3002 \u3000\u3000\u4f46\u695a\u7559\u9999\u5374\u66f4\u62c5\u5fc3\uff0c\u56e0\u4e3a\u96fe\u82e5\u592a\u6d53\uff0c\u4ed6\u4e0d\u4f46\u7acb\u523b\u5c31\u4f1a\u5931\u53bb\u96c4\u5a18\u5b50\u7684\u884c\u8e2a\uff0c\u751a\u81f3\u8fd8\u4f1a\u5931\u53bb \u65b9\u5411\u3002 \u3000\u3000\u82e5\u5728\u8fd9\u79cd\u5730\u65b9\u8ff7\u4e86\u8def\uff0c\u90a3\u66f4\u662f\u4ef6\u53ef\u6015\u7684\u4e8b\u3002 \u3000\u3000\u6653\u98ce\u4e2d\u9690\u9690\u4f20\u6765\u4e86\u4e00\u9635\u9635\u6d41\u6c34\u58f0\uff0c\u5999\u97f5\u5929\u7136\uff0c\u5982\u4ed9\u5b50\u9e23\u7434\uff0c\u5728\u8fd9\u65e0\u8fb9\u5bc2\u9759\u4e2d\u542c\u6765\uff0c\u4ee4\u4eba \u5fc3\u795e\u7686\u9189\u3002 \u3000\u3000\u695a\u7559\u9999\u60f3\u5230\u82cf\u84c9\u84c9\u53d9\u8ff0\u8fc7\u5979\u5165\u5c71\u65f6\u7684\u60c5\u51b5\uff0c\u5fc3\u91cc\u4e00\u559c\uff0c\u6697\u9053\uff1a\u201c\u8fd9\u91cc\u83ab\u975e\u5df2\u5230\u4e86\u795e\u6c34\u5bab \u7684\u5165\u53e3\u5904\u4e86\u9ebd\uff1f\u201d \u3000\u3000\u53ef\u662f\u96c4\u5a18\u5b50\u5230\u4e86\u8fd9\u91cc\uff0c\u53cd\u800c\u505c\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u4ed6\u56db\u9762\u671b\u4e86\u4e00\u773c\uff0c\u7acb\u523b\u5411\u53f3\u8fb9\u4e00\u7247\u5c71\u5d16\u63a0\u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u8fd9\u5ea7\u5c71\u5761\u5f62\u52bf\u7edd\u9669\uff0c\u4e0b\u9762\u5341\u4e08\u7b14\u7acb\u5982\u524a\uff0c\u4e0a\u9762\u602a\u77f3\u5ce5\u5d58\uff0c\u4e2d\u95f4\u5374\u51f8\u51fa\u4e00\u7247\u5e73\u53f0\u4f3c\u7684\u5c71 \u5d16\u3002 \u3000\u3000\u96c4\u5a18\u5b50\u5230\u4e86\u8fd9\u7247\u5c71\u5d16\u5f8c\uff0c\u5c31\u5ffd\u7136\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u539f\u6765\u5c71\u5d16\u7adf\u6709\u4e2a\u6d1e\u7a74\uff0c\u5374\u88ab\u4e0a\u4e0b\u51e0\u5757\u5982\u72ac\u7259\u4ea4\u9519\u7684\u77f3\u5934\u63a9\u76d6\uff0c\u6240\u4ee5\u7531\u4e0b\u9762\u671b\u4e0a\u53bb\uff0c\u5f88\u4e0d \u5bb9\u6613\u53d1\u73b0\u3002 \u3000\u3000\u8fd9\u6d1e\u7a74\u83ab\u975e\u5c31\u662f\u76f4\u8fbe\u795e\u6c34\u5bab\u7684\u79d8\u5f84\uff1f \u3000\u3000\u695a\u7559\u9999\u8fd8\u662f\u6ca1\u6709\u76f4\u63a0\u4e0a\u53bb\uff0c\u4ed6\u4e0d\u6562\u6709\u4e1d\u6beb\u5927\u610f\uff0c\u56e0\u4e3a\u8fd9\u91cc\u7684\u5730\u52bf\u5b9e\u5728\u592a\u9669\uff0c\u4ed6\u53ea\u8981\u7a0d\u6709 \u4e0d\u614e\uff0c\u4e0d\u4f46\u7acb\u523b\u5c31\u8981\u88ab\u5bf9\u65b9\u53d1\u89c9\uff0c\u800c\u4e14\u8fb9\u7f6e\u8eab\u5728\u5371\u9669\u4e4b\u5730\uff0c\u5bf9\u65b9\u82e5\u662f\u65bd\u5c55\u6740\u624b\uff0c\u4ed6\u6839\u672c\u8fde\u9000 \u8def\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u4ed6\u58c1\u864e\u822c\u8d34\u7740\u5c71\u58c1\u7ed5\u4e86\u8fc7\u53bb\uff0c\u9690\u8eab\u5728\u90a3\u4e00\u7247\u5e73\u53f0\u822c\u7684\u5c71\u5d16\u4e0b\uff0c\u53c8\u5c06\u8033\u6735\u8d34\u5728\u5c71\u58c1\u4e0a\uff0c\u9759 \u9759\u7684\u503e\u542c\u4e86\u534a\u664c\u3002 \u3000\u3000\u53ea\u542c\u4e0a\u9762\u6d1e\u7a74\u4e2d\u4f20\u6765\u4e86\u6781\u8f7b\u5fae\u7684\u742e\u5ce5\u58f0\uff0c\u5b9b\u5982\u91d1\u94c1\u76f8\u51fb\uff0c\u53c8\u50cf\u662f\u96c4\u5a18\u5b50\u5728\u5c06\u4e00\u4ef6\u4ef6\u5f88\u5c0f \u7684\u94c1\u5668\u6401\u5728\u77f3\u5934\u4e0a\u65f6\u6240\u53d1\u51fa\u7684\u58f0\u97f3\u3002 \u3000\u3000\u96c4\u5a18\u5b50\u663e\u7136\u8fd8\u7559\u5728\u8fd9\u6d1e\u7a74\u4e2d\u6ca1\u6709\u8d70\u3002 \u3000\u3000\u997c\u4e86\u534a\u664c\uff0c\u695a\u7559\u9999\u53c8\u542c\u5230\u4ed6\u7684\u555c\u6c34\u58f0\uff0c\u5480\u56bc\u58f0\uff0c\u5076\u5c14\u8fd8\u6709\u6c88\u91cd\u7684\u53f9\u606f\u58f0\uff0c\u811a\u6b65\u8d70\u52a8\u58f0\u3002 \u3000\u3000\u695a\u7559\u9999\u672c\u6765\u8fd8\u731c\u4e0d\u5230\u4ed6\u7559\u5728\u8fd9\u6d1e\u7a74\u4e2d\u5e72\u4ec0\u9ebd\uff0c\u73b0\u5728\u53d1\u73b0\u4ed6\u7adf\u4f3c\u8fd8\u8981\u5728\u91cc\u9762\u9017\u7559\u4e00\u6bb5\u5f88\u4e45 \u7684\u65f6\u5019\uff0c\u624d\u60f3\u5230\u4ed6\u4e5f\u8bb8\u662f\u8981\u5728\u8fd9\u91cc\u7b49\u5230\u5929\u9ed1\u3002 \u3000\u3000\u4ed6\u4e5f\u4e0d\u6562\u5728\u5149\u5929\u5316\u65e5\u4e0b\u8fdb\u5165\u795e\u6c34\u5bab\u3002 \u3000\u3000\u695a\u7559\u9999\u6697\u4e2d\u53f9\u4e86\u53e3\u6c14\uff0c\u4e5f\u53ea\u6709\u5728\u5916\u9762\u7b49\u7740\uff0c\u96c4\u5a18\u5b50\u81f3\u5c11\u8fd8\u5e26\u6765\u98df\u7269\u548c\u6c34\uff0c\u4ed6\u5374\u53ea\u6709\u5728\u5916 \u9762\u4e7e\u7b49\u3002 \u3000\u3000\u73b0\u5728\u8ddd\u79bb\u5929\u9ed1\u81f3\u5c11\u8fd8\u6709\u4e94\u516d\u4e2a\u65f6\u8fb0\uff0c\u8fd9\u4e94\u516d\u4e2a\u65f6\u8fb0\u5b9e\u5728\u5f88\u96be\u6371\uff0c\u4ed6\u5728\u5c71\u58c1\u65c1\u627e\u4e86\u4e2a\u9690\u50fb \u5904\u8eba\u4e0b\u6765\uff0c\u4f46\u5374\u4e0d\u6562\u95ed\u4e0a\u773c\u775b\u3002 \u3000\u3000\u56e0\u4e3a\u96c4\u5a18\u5b50\u82e5\u662f\u4e07\u4e00\u4e0d\u5230\u5929\u9ed1\u5c31\u51fa\u6765\u4e86\uff0c\u4ed6\u5c31\u53c8\u9519\u8fc7\u4e86\u673a\u4f1a\uff0c\u695a\u7559\u9999\u867d\u7136\u5f88\u559c\u6b22\u5192\u9669\uff0c \u4f46\u5374\u4e0d\u559c\u6b22\u5192\u8fd9\u79cd\u9669\u3002 \u3000\u3000\u7b49\u4eba\u672c\u5df2\u7ecf\u591f\u96be\u53d7\u7684\u4e86\uff0c\u997f\u7740\u809a\u5b50\u7b49\u4eba\u66f4\u4e0d\u662f\u6ecb\u5473\u3002 \u3000\u3000\u50cf\u695a\u7559\u9999\u8fd9\u6837\u7684\u4eba\uff0c\u5c31\u7b97\u997f\u4e0a\u4e2a\u53c1\u4e94\u5929\uff0c\u4e5f\u4e0d\u4f1a\u5012\u4e0b\u53bb\u7684\uff0c\u4f46\u201c\u9965\u997f\u201d\u5e76\u4e0d\u7eaf\u7cb9\u662f\u8089\u4f53 \u4e0a\u7684\u95ee\u9898\u3002 \u3000\u3000\u56e0\u4e3a\u9965\u997f\u5f80\u5f80\u8fd8\u4f1a\u5e26\u7ed9\u4eba\u4e00\u79cd\u7cbe\u795e\u4e0a\u7684\u7a7a\u865a\uff0c\u6240\u4ee5\u695a\u7559\u9999\u53ea\u6709\u52aa\u529b\u53bb\u60f3\u4e9b\u522b\u7684\u4e8b\uff0c\u5e78\u597d \u4ed6\u80fd\u60f3\u7684\u5b9e\u5728\u592a\u591a\u4e86\u3002 \u3000\u3000\u4ed6\u8fd9\u4e00\u751f\u4e2d\u5b9e\u5728\u5145\u6ee1\u4e86\u5404\u5f0f\u5404\u6837\u7684\u56de\u4ebf\uff0c\u867d\u7136\u6709\u4e9b\u4e5f\u66fe\u4ee4\u4ed6\u75db\u82e6\uff0c\u4f46\u5927\u591a\u6570\u90fd\u80fd\u5e26\u7ed9\u4ed6 \u4e00\u70b9\u5b89\u6170\u548c\u6e29\u99a8\u3002 \u3000\u3000\u4ed6\u60f3\u8d77\u4e86\u81ea\u5df1\u7684\u7ae5\u5e74\uff0c\u90a3\u771f\u662f\u4e00\u6bb5\u9ec4\u91d1\u822c\u7684\u65e5\u5b50\u3002 \u3000\u3000\u5927\u591a\u6570\u6210\u540d\u7684\u82f1\u96c4\uff0c\u7ec3\u6b66\u65f6\u90fd\u5fcd\u53d7\u8fc7\u522b\u4eba\u6240\u65e0\u6cd5\u5fcd\u53d7\u7684\u8270\u8f9b\u548c\u75db\u82e6\uff0c\u4f46\u695a\u7559\u9999\u5374\u5e76\u6ca1\u6709 \u8fd9\u79cd\u611f\u89c9\u3002 \u3000\u3000\u867d\u7136\u4ed6\u4e5f\u66fe\u4e0d\u7720\u4e0d\u4f11\uff0c\u4e5f\u66fe\u5728\u51b0\u96ea\u5bd2\u98ce\u4e2d\u5954\u9a70\u65bc\u5d0e\u5c96\u7684\u5c71\u9053\u4e0a\uff0c\u6765\u953b\u70bc\u8f7b\u529f\u548c\u4f53\u529b\uff0c\u4e5f \u66fe\u5728\u70c8\u65e5\u9177\u70ed\u4e0b\u6d41\u6c57\uff0c\u751a\u81f3\u6d41\u8840\uff0c\u4f46\u4ed6\u5e76\u4e0d\u8ba4\u4e3a\u8fd9\u662f\u75db\u82e6\uff0c\u56e0\u4e3a\u8fd9\u5c31\u662f\u4ed6\u7684\u5174\u8da3\uff0c\u6240\u4ee5\u4ed6\u603b \u80fd\u627e\u5f97\u5230\u4e50\u8da3\u3002 \u3000\u3000\u4ed6\u53c8\u60f3\u8d77\u4e86\u90a3\u4e9b\u7ae5\u5e74\u7684\u597d\u53cb\uff0c\u59ec\u51b0\u96c1\u3001\u80e1\u94c1\u82b1\u2026\u2026 \u3000\u3000\u4e00\u60f3\u5230\u80e1\u94c1\u82b1\uff0c\u4ed6\u5c31\u5fcd\u4e0d\u4f4f\u7b11\u4e86\uff0c\u4ed6\u4e00\u76f4\u8ba4\u4e3a\u80e1\u94c1\u82b1\u5e76\u4e0d\u662f\u771f\u7684\u7231\u559d\u9152\uff0c\u53ea\u4e0d\u8fc7\u559c\u6b22\u559d \u9152\u65f6\u90a3\u79cd\u60c5\u8c03\u800c\u5df2\u3002 \u3000\u3000\u56e0\u4e3a\u9152\u603b\u662f\u80fd\u5e26\u7ed9\u4eba\u4eec\u70ed\u95f9\u548c\u6b22\u4e50\u3002 \u3000\u3000\u4ed6\u6709\u5404\u5f0f\u5404\u6837\u7684\u670b\u53cb\uff0c\u4ed6\u89c9\u5f97\u8fd9\u4e9b\u670b\u53cb\u90fd\u5bf9\u4ed6\u4e0d\u9519\uff0c\u6240\u4ee5\u4ed6\u5fc3\u4e2d\u5145\u6ee1\u53cb\u60c5\u7684\u6e29\u6696\uff0c\u8fd9\u4ee4 \u4ed6\u5f88\u8212\u670d\u3002 \u3000\u3000\u65bc\u662f\u4ed6\u53c8\u60f3\u8d77\u4e86\u4e00\u70b9\u7ea2\uff0c\u60f3\u8d77\u4e86\u66f2\u65e0\u5bb9\uff0c\u8fd9\u4e24\u4eba\u5916\u8868\u90fd\u51b7\u5f97\u50cf\u51b0\u5c71\u4e00\u6837\uff0c\u5fc3\u91cc\u90fd\u5145\u6ee1\u4e86 \u70ed\u706b\u3002 \u3000\u3000\u4ed6\u4e0d\u77e5\u9053\u8fd9\u4e24\u4eba\u73b0\u5728\u5230\u90a3\u91cc\u53bb\u4e86\uff0c\u4e5f\u4e0d\u77e5\u9053\u4e00\u70b9\u7ea2\u662f\u4e0d\u662f\u8fd8\u5728\u7ee7\u7eed\u9003\u907f\u90a3\u523a\u5ba2\u96c6\u56e2\u7684\u8ffd \u8e2a\u3002 \u3000\u3000\u4ed6\u53ea\u6709\u5728\u6697\u4e2d\u795d\u798f\u3002 \u3000\u3000\u8fd9\u65f6\u7a7a\u5c71\u4e2d\u5df2\u6709\u4e86\u5404\u79cd\u58f0\u97f3\uff0c\u6709\u6d41\u6c34\u58f0\uff0c\u6709\u9e1f\u8bed\u866b\u9e23\uff0c\u98ce\u5439\u6728\u53f6\uff0c\u6ee1\u5c71\u677e\u6d9b\uff0c\u8fdc\u5904\u8fd8\u5076 \u7136\u4f1a\u4f20\u6765\u4e00\u4e24\u58f0\u91ce\u517d\u7684\u4f4e\u5578\u3002 \u3000\u3000\u695a\u7559\u9999\u62ac\u8d77\u5934\uff0c\u5ffd\u7136\u53d1\u73b0\u65e5\u8272\u5df2\u6e10\u504f\u897f\u3002 \u3000\u3000\u4eba\u5728\u56de\u4ebf\u4e2d\uff0c\u65f6\u95f4\u5f80\u5f80\u4f1a\u8fc7\u5f97\u5f88\u5feb\u7684\uff0c\u6240\u4ee5\u6709\u4e9b\u5b64\u72ec\u7684\u8001\u4eba\u53ea\u6709\u751f\u6d3b\u5728\u56de\u4ebf\u91cc\uff0c\u624d\u80fd\u5ea6 \u8fc7\u6f2b\u957f\u5bc2\u5bde\u7684\u665a\u5e74\u3002 \u3000\u3000\u4f46\u73b0\u5728\u8ddd\u79bb\u5929\u9ed1\u6700\u5c11\u8fd8\u6709\u4e00\u4e24\u4e2a\u65f6\u8fb0\uff0c\u695a\u7559\u9999\u4f38\u4e86\u4e2a\u61d2\u8170\uff0c\u521a\u60f3\u7ad9\u8d77\u6765\u6d3b\u52a8\u6d3b\u52a8\u7b4b\u9aa8\u3002 \u3000\u3000\u8c01\u77e5\u5c31\u5728\u8fd9\u65f6\uff0c\u4e0a\u9762\u7684\u6d1e\u7a74\u4e2d\u5df2\u94bb\u51fa\u4e86\u4e00\u4e2a\u4eba\u6765\u3002 \u3000\u3000\u8fd9\u4eba\u5e76\u4e0d\u662f\u96c4\u5a18\u5b50\u3002 \u3000\u3000\u9664\u4e86\u96c4\u5a18\u5b50\u5916\uff0c\u8fd9\u6d1e\u4e2d\u5c45\u7136\u8fd8\u6709\u522b\u7684\u4eba\uff0c\u96be\u9053\u5979\u65e9\u5df2\u5728\u6d1e\u4e2d\u7b49\u7740\u96c4\u5a18\u5b50\u9ebd\uff1f \u3000\u3000\u5979\u662f\u4e2a\u5f88\u7f8e\u4e3d\u7684\u5c11\u5973\uff0c\u7a7f\u7740\u96ea\u767d\u7684\u8863\u670d\uff0c\u7ad9\u5728\u51f8\u51fa\u7684\u5c71\u5d16\u4e0a\uff0c\u6ee1\u5934\u9ed1\u53d1\u548c\u96ea\u767d\u7684\u8863\u8882\u540c \u65f6\u5728\u98ce\u4e2d\u98d8\u626c\uff0c\u770b\u6765\u662f\u90a3\u9ebd\u8d85\u7fa4\u7edd\u4fd7\u3002 \u3000\u3000\u662f\u5bab\u5357\u71d5\u3002 \u3000\u3000\u7206\u5357\u71d5\u600e\u4f1a\u5728\u8fd9\u91cc\uff1f\u96c4\u5a18\u5b50\u5230\u90a3\u91cc\u53bb\u4e86\uff1f \u3000\u3000\u695a\u7559\u9999\u7684\u5fc3\u8df3\u4e86\u8d77\u6765\uff0c\u4f46\u53c8\u4ed4\u7ec6\u77a7\u4e86\u4e00\u773c\u5f8c\uff0c\u4ed6\u624d\u53d1\u89c9\u8fd9\u5973\u5b50\u5e76\u4e0d\u662f\u5bab\u5357\u71d5\uff0c\u53ea\u4e0d\u8fc7\u548c \u5bab\u5357\u71d5\u5f88\u76f8\u4f3c\u3002 \u3000\u3000\u5979\u7684\u795e\u60c5\u3001\u8863\u88f3\u3001\u88c5\u675f\uff0c\u548c\u8170\u7554\u90a3\u6839\u5e26\u5b50\uff0c\u90fd\u544a\u8bc9\u4eba\u5979\u4e5f\u662f\u540d\u9707\u5929\u4e0b\u7684\u201c\u795e\u6c34\u5bab\u201d\u95e8 \u4e0b\u3002 \u3000\u3000\u90a3\u9ebd\uff0c\u5979\u600e\u4f1a\u5728\u8fd9\u6d1e\u7a74\u4e2d\u5462\uff1f\u96be\u9053\u8fd9\u6d1e\u7a74\u771f\u662f\u795e\u6c34\u5bab\u7684\u79d8\u5f84\uff1f\u96be\u9053\u96c4\u5a18\u5b50\u65e9\u5df2\u5230\u4e86\u795e\u6c34 \u5bab\uff1f \u3000\u3000\u695a\u7559\u9999\u4e5f\u6709\u4e9b\u7740\u6025\u4e86\uff0c\u53ea\u89c1\u8fd9\u5c11\u5973\u98d8\u98d8\u81ea\u5c71\u5d16\u4e0a\u63a0\u4e86\u4e0b\u6765\uff0c\u5979\u7684\u8f7b\u529f\u662f\u90a3\u9ebd\u9ad8\u5999\uff0c\u59ff\u6001 \u662f\u90a3\u9ebd\u4f18\u7f8e\u3002 \u3000\u3000\u5979\u624b\u91cc\u8fd8\u63d0\u7740\u4e2a\u9ed1\u8272\u7684\u53cd\u56ca\u3002 \u3000\u3000\u539f\u6765\u8fd9\u5c11\u5973\u5c31\u662f\u96c4\u5a18\u5b50\u3002 \u3000\u3000\u695a\u7559\u9999\u5fcd\u4e0d\u4f4f\u5728\u6697\u4e2d\u82e6\u7b11\uff0c\u96c4\u5a18\u5b50\u679c\u7136\u540d\u4e0d\u865a\u4f20\uff0c\u6613\u5bb9\u7684\u672c\u4e8b\u679c\u7136\u7cbe\u5999\uff0c\u7adf\u51e0\u4e4e\u8fde\u695a\u7559 \u9999\u90fd\u9a97\u8fc7\u4e86\u3002 \u3000\u3000\u6700\u5999\u7684\u662f\uff0c\u4ed6\u5316\u88c5\u6210\u5973\u4eba\u5f8c\uff0c\u5168\u8eab\u4e0a\u4e0b\uff0c\u518d\u4e5f\u6ca1\u6709\u4e00\u5206\u4e00\u6beb\u7537\u4eba\u7684\u5473\u9053\uff0c\u4e00\u8f6c\u773c\uff0c\u4e00\u4e3e \u624b\uff0c\u4e00\u6295\u8db3\uff0c\u90fd\u6d3b\u50cf\u662f\u4e2a\u5973\u4eba\uff0c\u695a\u7559\u9999\u867d\u7136\u4e5f\u80fd\u88c5\u9f99\u50cf\u9f99\uff0c\u88c5\u864e\u50cf\u864e\uff0c\u4f46\u8fd9\u79cd\u5973\u4eba\u7684\u5473\u9053\uff0c \u4ed6\u5374\u4e00\u8f88\u5b50\u4e5f\u88c5\u4e0d\u51fa\u7684\u3002 \u3000\u3000\u96c4\u5a18\u5b50\u5728\u5c71\u5d16\u4e0b\u89c2\u671b\u7740\uff0c\u5e76\u6ca1\u6709\u7acb\u523b\u5c55\u52a8\u8eab\u5f62\u3002 \u3000\u3000\u695a\u7559\u9999\u5ffd\u7136\u53d1\u89c9\u4ed6\u7709\u68a2\u773c\u89d2\uff0c\u6709\u5f88\u591a\u76b1\u7eb9\uff0c\u4ed6\u8fdc\u770b\u867d\u8fd8\u662f\u4e2a\u5c11\u5973\uff0c\u4f46\u5e74\u7eaa\u663e\u7136\u5df2\u4e0d\u5c0f \u4e86\u3002 \u3000\u3000\u8fd9\u5c31\u662f\u96c4\u5a18\u5b50\u672c\u6765\u7684\u9762\u76ee\u9ebd\uff1f \u3000\u3000\u695a\u7559\u9999\u6697\u6697\u53f9\u606f\uff0c\u96be\u602a\u96c4\u5a18\u5b50\u5bf9\u81ea\u5df1\u5bb9\u8c8c\u90a3\u9ebd\u81ea\u8d1f\uff0c\u4ed6\u5b9e\u5728\u53ef\u8bf4\u662f\u4e2a\u7edd\u4e16\u7684\u7f8e\u7537\u5b50\u3002 \u3000\u3000\u4ed6\u867d\u7136\u5e74\u534e\u5df2\u8001\uff0c\u4f46\u8fd8\u662f\u6bd4\u5927\u591a\u6570\u5973\u4eba\u90fd\u7f8e\u5f97\u591a\uff0c\u4e00\u4e2a\u7537\u4eba\u7adf\u6b64\u5973\u4eba\u8fd8\u7f8e\uff0c\u6bd4\u5973\u4eba\u8fd8\u50cf \u5973\u4eba\uff0c\u8fd9\u5b9e\u5728\u4e0d\u53ef\u601d\u8bae\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u65e2\u5df2\u6539\u626e\u6210\u5973\u4eba\uff0c\u4e3a\u4ec0\u9ebd\u8fd8\u8981\u7528\u81ea\u5df1\u672c\u6765\u7684\u9762\u76ee\u5462\uff1f\u8fd9\u70b9\u53c8\u4ee4\u695a\u7559\u9999\u60f3\u4e0d\u901a\u4e86\u3002 \u3000\u3000\u4ed6\u4e5f\u60f3\u4e0d\u5230\u96c4\u5a18\u5b50\u7adf\u548c\u5bab\u5357\u71d5\u5982\u6b64\u76f8\u4f3c\u3002 \u3000\u3000\u90a3\u9ebd\uff0c\u96c4\u5a18\u5b50\u548c\u5bab\u5357\u71d5\u4e4b\u95f4\uff0c\u662f\u4e0d\u662f\u4e5f\u6709\u67d0\u79cd\u5947\u5999\u7684\u5173\u7cfb\uff1f \u3000\u3000\u6709\u4eba\u4e5f\u8bb8\u8981\u95ee\uff1a\u201c\u96c4\u5a18\u5b50\u65e2\u7136\u8981\u626e\u6210\u201c\u795e\u6c34\u5bab\u201d\u5f1f\u5b50\u7684\u6a21\u6837\u6765\u6df7\u5165\u795e\u6c34\u5bab\uff0c\u90a3\u9ebd\u4ed6\u4e3a\u4f55 \u4e0d\u7d22\u6027\u626e\u6210\u5bab\u5357\u71d5\u5462\uff1f\u201d \u3000\u3000\u4f46\u695a\u7559\u9999\u5374\u77e5\u9053\u8fd9\u95ee\u9898\u5f88\u611a\u8822\u3002 \u3000\u3000\u56e0\u4e3a\u6613\u5bb9\u672f\u5e76\u4e0d\u662f\u9b54\u6cd5\uff0c\u7cbe\u65bc\u6613\u5bb9\u672f\u7684\u4eba\uff0c\u56fa\u7136\u80fd\u6539\u53d8\u81ea\u5df1\u7684\u5bb9\u8c8c\uff0c\u4ee4\u522b\u4eba\u96be\u4ee5\u53d1\u89c9\uff0c \u4f46\u5374\u7edd\u4e0d\u53ef\u80fd\u4ee3\u66ff\u53e6\u4e00\u4e2a\u4eba\u2014\u2014\u695a\u7559\u9999\u56fa\u7136\u53ef\u4ee5\u6539\u626e\u6210\u5f20\u5578\u6797\uff0c\u90a3\u53ea\u4e0d\u8fc7\u662f\u56e0\u4e3a\u6ca1\u6709\u4eba\u8ba4\u5f97 \u5f20\u5578\u6797\u800c\u5df2\u3002 \u3000\u3000\u6240\u4ee5\uff0c\u82e5\u8bf4\u96c4\u5a18\u5b50\u80fd\u5728\u7247\u523b\u95f4\u5c31\u626e\u6210\u5bab\u5357\u71d5\uff0c\u6df7\u5165\u795e\u6c34\u5bab\uff0c\u795e\u6c34\u5bab\u4e2d\u7684\u4eba\u4e5f\u5168\u6ca1\u6709\u53d1 \u89c9\uff0c\u90a3\u5c31\u4e0d\u662f\u6545\u4e8b\uff0c\u800c\u662f\u795e\u8bdd\u4e86\u3002 \u3000\u3000\u82e5\u662f\u6709\u4e00\u6bb5\u5f88\u957f\u7684\u65f6\u95f4\uff0c\u8ba9\u96c4\u5a18\u5b50\u80fd\u5145\u4efd\u7684\u51c6\u5907\uff0c\u5c3d\u91cf\u6a21\u4eff\u5bab\u5357\u71d5\u7684\u795e\u60c5\u548c\u52a8\u4f5c\uff0c\u90a3\u4e5f \u8bb8\u8fd8\u6709\u53ef\u80fd\u3002 \u3000\u3000\u7136\u5f8c\u96c4\u5a18\u5b50\u5ffd\u7136\u5728\u5730\u4e0a\u6316\u4e86\u4e2a\u6d1e\uff0c\u5c06\u90a3\u9ed1\u8272\u76ae\u56ca\u4e2d\u7684\u4e1c\u897f\u90fd\u57cb\u4e86\u4e0b\u53bb\uff0c\u8fd9\u76ae\u56ca\u4e2d\u88c5\u7684\u81ea \u7136\u662f\u4ed6\u6613\u5bb9\u4e4b\u7269\u3002 \u3000\u3000\u4f46\u4ed6\u8fd8\u662f\u5c06\u81f3\u76ae\u8944\u63d0\u5728\u624b\u91cc\u3002 \u3000\u3000\u7a7a\u7684\u76ae\u56ca\u8fd8\u6709\u4ec0\u9ebd\u7528\u5462\uff1f\u695a\u7559\u9999\u53c8\u89c9\u5f97\u5f88\u5947\u602a\u3002 \u3000\u3000\u8fd9\u65f6\u65e5\u8272\u867d\u5df2\u897f\u659c\uff0c\u9633\u5149\u5374\u4ecd\u666e\u7167\u7740\u5927\u5730\uff0c\u96c4\u5a18\u5b50\u62ac\u5934\u671b\u4e86\u671b\u5929\u8272\uff0c\u6162\u6162\u7684\u5411\u524d\u8d70\u4e86\u51fa \u53bb\u3002 \u3000\u3000\u4ed6\u4f3c\u4e4e\u6bd4\u695a\u7559\u9999\u66f4\u7740\u6025\uff0c\u4e5f\u7b49\u4e0d\u5230\u5929\u9ed1\u4e86\u3002 \u3000\u3000\u695a\u7559\u9999\u76f4\u7b49\u4ed6\u8f6c\u8fc7\u4e00\u7247\u5c71\u5773\uff0c\u624d\u6562\u8ffd\u8fc7\u53bb\uff0c\u8c01\u77e5\u7b49\u4ed6\u4e5f\u8f6c\u8fc7\u90a3\u5c71\u5773\u65f6\uff0c\u7adf\u53c8\u5931\u53bb\u4e86\u96c4\u5a18 \u5b50\u7684\u8e2a\u8ff9\u3002 \u3000\u3000\u8fd9\u5c71\u5773\u5f8c\u7adf\u662f\u7edd\u8def\uff0c\u4e24\u65c1\u5c71\u7acb\u5982\u58c1\uff0c\u4e2d\u95f4\u4e00\u7247\u5c71\u58c1\u8fd1\u9762\u800c\u8d77\uff0c\u5c31\u50cf\u662f\u4e00\u53ea\u7f3a\u4e86\u8fb9\u7684\u5323 \u4e8e\u3002 \u3000\u3000\u96c4\u5a18\u5b50\u65e2\u5df2\u8d70\u5165\u8fd9\u5323\u5b50\u91cc\uff0c\u600e\u4f1a\u53c8\u5ffd\u7136\u4e0d\u89c1\u4e86\u5462\uff1f \u3000\u3000\u96be\u9053\u4ed6\u5df2\u53d1\u73b0\u8eab\u5f8c\u6709\u4eba\u5728\u8ffd\u8e2a\uff1f\u53ef\u662f\u8fd9\u91cc\u53c1\u9762\u5c71\u58c1\uff0c\u63d2\u7fc5\u4e5f\u96be\u98de\u6e21\uff0c\u4ed6\u96be\u9053\u8fd8\u80fd\u94bb\u5165\u5730 \u4e0b\u4e0d\u6210\uff1f \u3000\u3000\u8fd9\u7684\u786e\u662f\u4ef6\u4ee4\u4eba\u60ca\u5f02\u7684\u4e8b\uff0c\u4f46\u695a\u7559\u9999\u7684\u60ca\u5f02\u5f88\u5feb\u5c31\u5df2\u8fc7\u53bb\uff0c\u4ed6\u5c0f\u5fc3\u7684\u641c\u7d22\u4e86\u534a\u664c\uff0c\u5c31\u53d1 \u73b0\u4e2d\u95f4\u7684\u5c71\u58c1\u548c\u5de6\u9762\u7684\u5c71\u58c1\u95f4\uff0c\u6709\u4e00\u7ebf\u7a7a\u9699\u3002 \u3000\u3000\u8fd9\u7a7a\u9699\u5bbd\u4ec5\u5c3a\u9980\uff0c\u800c\u4e14\u957f\u6ee1\u4e86\u6742\u8349\u548c\u85e4\u841d\uff0c\u695a\u7559\u9999\u82e5\u975e\u4eb2\u773c\u89c1\u5230\u96c4\u5a18\u5b50\u5728\u6b64\u95f4\u5931\u8e2a\uff0c\u7b97 \u51c6\u4e86\u8fd9\u91cc\u5fc5\u5b9a\u8fd8\u6709\u9000\u8def\uff0c\u90a3\u9ebd\u4ed6\u5c31\u7b97\u641c\u7d22\u5f97\u518d\u4ed4\u7ec6\uff0c\u4e5f\u7edd\u4e0d\u4f1a\u53d1\u73b0\u8fd9\u4e24\u9762\u5de8\u5927\u7684\u5c71\u58c1\u95f4\uff0c\u8fd8 \u6709\u8fd9\u9ebd\u6837\u4e00\u6761\u79d8\u5f84\u3002 \u3000\u3000\u7a7f\u8fc7\u8fd9\u6761\u79d8\u5f84\uff0c\u90a3\u82e5\u6709\u82e5\u65e0\u7684\u6d41\u6c34\u58f0\uff0c\u5c31\u5ffd\u7136\u53d8\u5f97\u6e05\u695a\u54cd\u4eae\u8d77\u6765\uff0c\u6c34\u58f0\u6f7a\u6f7a\uff0c\u5982\u5728\u8033 \u7554\u3002\u6668\u96fe\u51c4\u8ff7\uff0c\u5f25\u6162\u4e86\u8fd9\u4e98\u53e4\u4ee5\u6765\u4fbf\u5c11\u6709\u4eba\u8e2a\u7684\u5c71\u8c37\u3002 \u3000\u3000\u695a\u7559\u9999\u4f0f\u4e0b\u8eab\u5b50\uff0c\u5c0f\u5fc3\u7ffc\u7ffc\u7684\u5faa\u7740\u6d41\u6c34\u58f0\u8d70\u8fc7\u53bb\uff0c\u4ed6\u77e5\u9053\u81ea\u5df1\u6bcf\u8d70\u4e00\u6b65\uff0c\u5c31\u8ddd\u79bb\u79d8\u5bc6\u8fd1 \u4e86\u4e00\u6b65\u3002 \u3000\u3000\u5374\u4e5f\u8ddd\u79bb\u5371\u9669\u66f4\u8fd1\u4e86\u4e00\u6b65\u3002 \u3000\u3000\u7a81\u7136\u95f4\uff0c\u6709\u79cd\u5947\u5f02\u7684\u201c\u5636\u5636\u201d\u58f0\u4f20\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u695a\u7559\u9999\u7acb\u523b\u505c\u4e0b\u811a\u6b65\uff0c\u5168\u8eab\u4f0f\u5728\u5730\u4e0a\uff0c\u86c7\u4e00\u822c\u5411\u524d\u6ed1\u52a8\u4e86\u4e24\u53c1\u5c3a\uff0c\u4ed6\u5c31\u770b\u5230\u96c4\u5a18\u5b50\u3002 \u3000\u3000\u90a3\u795e\u79d8\u7684\u6d41\u6c34\uff0c\u5c31\u5728\u96c4\u5a18\u5b50\u811a\u7554\uff0c\u6b64\u523b\u4ed6\u53cc\u624b\u6367\u7740\u90a3\u9ed1\u8272\u7684\u76ae\u56ca\uff0c\u6b63\u5728\u7528\u529b\u7684\u5411\u76ae\u56ca\u4e2d \u5439\u7740\u6c14\u3002 \u3000\u3000\u90a3\u76ae\u56ca\u8fc5\u901f\u7684\u81a8\u80c0\u4e86\u8d77\u6765\uff0c\u5927\u52a0\u8f66\u8f6e\u3002 \u3000\u3000\u695a\u7559\u9999\u8fd9\u624d\u604d\u7136\u5927\u609f\uff0c\u6697\u9053\uff1a\u201c\u539f\u6765\u4ed6\u662f\u8981\u7528\u8fd9\u76ae\u56ca\u4f5c\u76ae\u7b4f\uff0c\u7136\u5f8c\u518d\u4e58\u7740\u76ae\u7b4f\u987a\u6d41\u800c \u4e0b\uff0c\u76f4\u5165\u795e\u6c34\u5bab\u3002\u201d \u3000\u3000\u53ea\u89c1\u96c4\u5a18\u5b50\u679c\u7136\u5df2\u5c06\u76ae\u7b4f\u5728\u6c34\u4e2d\u653e\u4e0b\uff0c\u53c8\u4f38\u51fa\u4e00\u53ea\u811a\u53bb\u8bd5\u63a2\u76ae\u7b4f\u7684\u8f7d\u91cd\u91cf\uff0c\u7136\u5f8c\u5c31\u8f7b\u8f7b \u7684\u5750\u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u76ae\u7b4f\u773c\u770b\u5c31\u8981\u987a\u6d41\u800c\u4e0b\uff0c\u695a\u7559\u9999\u6b63\u5728\u53d1\u6101\uff0c\u4e0d\u77e5\u8be5\u5982\u4f55\u8ffd\u4e0b\u53bb\uff0c\u8c01\u77e5\u5c31\u5728\u8fd9\u65f6\uff0c\u7a81\u542c \u201c\u5636\u201d\u7684\u4e00\u58f0\u3002 \u3000\u3000\u96c4\u5a18\u5b50\u5ffd\u7136\u81ea\u76ae\u7b4f\u4e0a\u7a9c\u4e86\u8d77\u6765\uff0c\u96ea\u767d\u7684\u8f7b\u8863\u56db\u6563\u98de\u8d77\uff0c\u5c31\u50cf\u662f\u5df2\u548c\u51c4\u8ff7\u7684\u6d53\u96fe\u6eb6\u4e3a\u4e00 \u4f53\u3002 \u3000\u3000\u90a3\u76ae\u7b4f\u5728\u6c34\u4e2d\u98ce\u8f66\u822c\u4e0d\u505c\u7684\u65cb\u8f6c\uff0c\u8d8a\u8f6c\u8d8a\u5c0f\uff0c\u8f6c\u8fc7\u5341\u4e03\u516b\u6b21\u4e4b\u5f8c\uff0c\u201c\u54e7\u201d\u7684\u98de\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u6697\u4e2d\u663e\u7136\u6709\u4eba\u5c06\u76ae\u7b4f\u51fb\u7834\u4e86\uff0c\u76ae\u7b4f\u6cc4\u6c14\uff0c\u624d\u4f1a\u65cb\u8f6c\u4e0d\u505c\u3002 \u3000\u3000\u96c4\u5a18\u5b50\u5df2\u843d\u5728\u5cb8\u8fb9\uff0c\u76ee\u5149\u4e2d\u5145\u6ee1\u4e86\u60ca\u9a87\u4e4b\u610f\uff0c\u987f\u4e86\u987f\u8db3\uff0c\u521a\u60f3\u8f6c\u8eab\u98de\u5954\uff0c\u8ff7\u96fe\u4e2d\u5ffd\u7136\u4f20 \u6765\u4e00\u9635\u8f7b\u7b11\u3002 \u3000\u3000\u4e00\u4e2a\u5a07\u5a9a\u7684\u8bed\u58f0\u5e26\u7740\u7b11\u9053\uff1a\u201c\u4f60\u65e2\u5df2\u6765\u4e86\uff0c\u4f55\u5fc5\u8d70\u5462\uff1f\u201d \u3000\u3000\u53ea\u542c\u6c34\u58f0\u8f6f\u4e43\uff0c\u5df2\u6709\u4e00\u53f6\u8f7b\u821f\uff0c\u51b2\u7834\u8ff7\u96fe\uff0c\u7f13\u7f13\u8361\u51fa\uff0c\u8239\u5934\u4e0a\u7ad9\u7740\u4e2a\u82d7\u6761\u7684\u767d\u8863\u4eba\u5f71\uff0c \u638c\u4e2d\u957f\u7bd9\u4e00\u70b9\uff0c\u8f7b\u821f\u5df2\u71d5\u5b50\u822c\u98d8\u5230\u5cb8\u8fb9\u3002 \u3000\u3000\u96c4\u5a18\u5b50\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u539f\u6765\u662f\u4f60\u3002\u201d \u3000\u3000\u767d\u8863\u5973\u5a07\u7b11\u7740\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u662f\u6211\uff0c\u4f60\u60f3\u4e0d\u5230\u5427\uff01\u4f46\u6211\u65e9\u5df2\u77e5\u9053\u4f60\u4f1a\u6765\u7684\uff0c\u65e9\u5c31\u5728\u8fd9\u91cc\u7b49 \u7740\u4f60\u4e86\u3002\u201d \u3000\u3000\u5e7d\u79d8\u7684\u7edd\u8c37\u3001\u6d53\u96fe\u3001\u6d41\u6c34\uff0c\u4f3c\u5973\u5b9e\u7537\uff0c\u6b7b\u800c\u590d\u6d3b\u7684\u6c5f\u6e56\u5de8\u76d7\uff0c\u8fd9\u4e00\u5207\u672c\u5c31\u5145\u6ee1\u4e86\u795e\u79d8\u4e0e \u8be1\u5f02\u3002 \u3000\u3000\u73b0\u5728\uff0c\u6d53\u96fe\u4e2d\u7adf\u53c8\u5ffd\u7136\u51fa\u73b0\u4e86\u8fd9\u71d5\u5b50\u822c\u7684\u8f7b\u821f\uff0c\u5e7d\u7075\u822c\u7684\u7f8e\u5973\uff0c\u5c31\u8fde\u695a\u7559\u9999\u4e5f\u4e0d\u7981\u89c9\u5f97 \u624b\u5728\u53d1\u51b7\u3002 \u3000\u3000\u8fd9\u4e00\u5207\u4e8b\u7a76\u7adf\u662f\u771f\uff1f\u662f\u5e7b\uff1f\u8fde\u4ed6\u90fd\u6709\u4e9b\u5206\u4e0d\u6e05\u4e86\u3002 \u3000\u3000\u4ed6\u53ea\u89c9\u8fd9\u767d\u8863\u5973\u4e8e\u98ce\u59ff\u7ef0\u7ea6\uff0c\u5f77\u4f5b\u7edd\u7f8e\uff0c\u4f46\u5728\u8fd9\u6d53\u5bc6\u7684\u96fe\u4e2d\uff0c\u4ed6\u4e5f\u77a7\u4e0d\u6e05\u5979\u7684\u9762\u76ee\u5bb9 \u8c8c\u3002 \u3000\u3000\u96c4\u5a18\u5b50\u6c88\u9ed8\u4e86\u5f88\u4e45\uff0c\u624d\u53f9\u606f\u7740\u9053\uff1a\u201c\u6211\u672c\u6765\u4e5f\u4e0d\u60f3\u6765\u7684\uff0c\u53ef\u662f\uff0c\u6211\u975e\u6765\u4e00\u8d9f\u4e0d\u53ef\u3002\u201d \u3000\u3000\u90a3\u767d\u8863\u5973\u621b\u7136\u987f\u4f4f\u4e86\u7b11\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u96be\u9053\u5df2\u5fd8\u8bb0\u4e86\u4f60\u6614\u65e5\u7acb\u4e0b\u7684\u6bd2\u8a93\u9ebd\uff1f\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u8bf4\u51fa\u6765\uff0c\u695a\u7559\u9999\u5ffd\u7136\u53d1\u73b0\u5979\u7684\u58f0\u97f3\u5f88\u719f\u6089\u3002 \u3000\u3000\u6309\u7740\uff0c\u4ed6\u53c8\u53d1\u73b0\u8fd9\u767d\u8863\u5973\u548c\u96c4\u5a18\u5b50\u7ad9\u5728\u4e00\u8d77\uff0c\u65e0\u8bba\u88c5\u675f\u3001\u59ff\u6001\u548c\u4e30\u91c7\uff0c\u7adf\u90fd\u6709\u51e0\u5206\u76f8 \u4f3c\u3002 \u3000\u3000\u96c4\u5a18\u5b50\u9eef\u7136\u9053\uff1a\u201c\u6211\u6ca1\u6709\u5fd8\u8bb0\uff0c\u6211\u53ea\u4e0d\u8fc7\u60f3\u770b\u770b\u6211\u5973\u513f\u7684\u575f\u5893\u3002\u201d \u3000\u3000\u767d\u8863\u5973\u9053\uff1a\u201c\u90a3\u4e5f\u53ea\u4e0d\u8fc7\u662f\u4e00\u576f\u9ec4\u571f\u800c\u5df2\uff0c\u6709\u4ec0\u9ebd\u597d\u770b\u7684\uff0c\u4f60\u82e5\u60f3\u770b\uff0c\u53bb\u770b\u770b\u90a3\u4e9b\u88ab\u4f60 \u5bb3\u6b7b\u7684\u4eba\u7684\u575f\u5893\u4e5f\u4e00\u6837\uff0c\u5929\u4e0b\u6240\u6709\u7684\u575f\u5893\u90fd\u5dee\u4e0d\u591a\u3002\u201d \u3000\u3000\u5979\u8fd9\u53e5\u8bdd\u8bf4\u5f97\u5ffd\u7136\u5c16\u523b\u8d77\u6765\uff0c\u695a\u7559\u9999\u542c\u4e86\u8fd9\u53e5\u8bdd\uff0c\u624d\u60f3\u8d77\u81ea\u7136\u5206\u8fa8\u4e0d\u51fa\uff0c\u56e0\u4e3a\u695a\u7559\u9999\u60f3 \u4e0d\u5230\u50cf\u5979\u5982\u6b64\u51b7\u6f20\u7684\u5973\u5b50\uff0c\u5c45\u7136\u4e5f\u6709\u7b11\u7684\u65f6\u5019\u3002 \u3000\u3000\u8c01\u77e5\u8fd9\u65f6\u5bab\u5357\u71d5\u7adf\u53c8\u5a07\u7b11\u4e86\u8d77\u6765\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u5bf9\u4e0d\u8d77\uff0c\u6211\u5e76\u4e0d\u662f\u6709\u610f\u8981\u8bf4\u90a3\u4e9b\u8bdd\u6765\u4f24\u5bb3 \u4f60\u7684\uff0c\u4f60\u83ab\u8981\u751f\u6211\u7684\u6c14\u597d\u5417\uff1f\u6211\u2026\u2026\u6211\u4e0b\u6b21\u4e00\u5b9a\u4e0d\u8bf4\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u51e0\u4e4e\u53c8\u6000\u7591\u81ea\u5df1\u542c\u9519\u4e86\u3002 \u3000\u3000\u4ed6\u7edd\u4e0d\u76f8\u4fe1\u5bab\u5357\u71d5\u7adf\u4f1a\u8bf4\u51fa\u8fd9\u79cd\u8bdd\u6765\u3002 \u3000\u3000\u4f46\u8fd9\u5973\u5b50\u7684\u786e\u662f\u5bab\u5357\u71d5\uff0c\u5979\u8f7b\u76c8\u7684\u4e0b\u4e86\u8239\uff0c\u8d70\u5230\u96c4\u5a18\u5b50\u9762\u524d\uff0c\u96c4\u5a18\u5b50\u53ea\u662f\u6728\u7acb\u5728\u90a3\u91cc\uff0c \u4e5f\u4e0d\u77e5\u5728\u60f3\u4e9b\u4ec0\u9ebd\uff1f \u3000\u3000\u7206\u5357\u71d5\u5ae3\u7136\u7b11\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u4f60\u672c\u6765\u9762\u76ee\u9ebd\uff1f\u96be\u602a\u5979\u603b\u662f\u8bf4\u6211\u957f\u5f97\u5f88\u50cf\u4f60\uff0c\u751a\u81f3\u6bd4\u4f60\u7684\u5973 \u513f\u8fd8\u50cf\u4f60\u2026\u2026\u201d \u3000\u3000\u96c4\u5a18\u5b50\u5ffd\u7136\u62ac\u8d77\u5934\uff0c\u9053\uff1a\u201c\u5979\u2026\u2026\u5979\u65f6\u5e38\u5728\u4f60\u9762\u524d\u8bf4\u8d77\u6211\uff1f\u201d \u3000\u3000\u7206\u5357\u71d5\u9053\uff1a\u201c\u55ef\uff01\u201d \u3000\u3000\u5979\u56f4\u7740\u96c4\u5a18\u5b50\u8d70\u4e86\u4e00\u5708\uff0c\u53c8\u5728\u4ed6\u9762\u524d\u505c\u4e86\u4e0b\u6765\uff0c\u4e00\u53cc\u6df1\u9083\u7684\u773c\u775b\uff0c\u77ac\u4e5f\u4e0d\u77ac\u5730\u51dd\u6ce8\u7740 \u4ed6\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u4f60\u4e5f\u65f6\u5e38\u60f3\u8d77\u5979\u9ebd\uff1f\u201d \u3000\u3000\u96c4\u5a18\u5b50\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8fd9\u4e9b\u5e74\u6765\uff0c\u6211\u65e9\u5df2\u5c06\u4ec0\u9ebd\u4eba\u90fd\u5fd8\u4e86\u3002\u201d \u3000\u3000\u7206\u5357\u71d5\u5403\u5403\u7b11\u9053\uff1a\u201c\u597d\u4e2a\u8584\u60c5\u7684\u4eba\uff0c\u522b\u4eba\u4e3a\u4e86\u4f60\u6b7b\u53bb\u6d3b\u6765\uff0c\u4f60\u5374\u5c06\u522b\u4eba\u5fd8\u5f97\u4e7e\u4e7e\u51c0\u51c0\uff0c \u4e16\u4e0a\u96be\u9053\u5c31\u771f\u7684\u6ca1\u6709\u4e00\u4e2a\u4eba\u80fd\u4ee4\u4f60\u52a8\u5fc3\u7684\u9ebd\uff1f\u201d \u3000\u3000\u96c4\u5a18\u5b50\u9053\uff1a\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u4ed6\u8f7b\u8f7b\u54ac\u7740\u5634\u5507\uff0c\u5c31\u50cf\u662f\u4e2a\u5a07\u7f9e\u7684\u5c11\u5973\u3002 \u3000\u3000\u7206\u5357\u71d5\u9053\uff1a\u201c\u6211\u73b0\u5728\u624d\u77e5\u9053\u4f60\u5b9e\u5728\u662f\u4e2a\u8ff7\u6b7b\u4eba\u7684\u5996\u7cbe\uff0c\u4e5f\u96be\u602a\u90a3\u9ebd\u591a\u5973\u5b69\u5b50\u5fc3\u7518\u60c5\u613f\u7ea6 \u4e3a\u4f60\u6b7b\uff0c\u5c31\u8fde\u6211\u2026\u2026\u6211\u4e5f\u2026\u2026\u201d \u3000\u3000\u5979\u7684\u7751\u4f3c\u4e4e\u7ea2\u4e86\uff0c\u5782\u5934\u53bb\u5f04\u7740\u5973\u89d2\u3002 \u3000\u3000\u96c4\u5a18\u5b50\u773c\u775b\u91cc\u95ea\u8fc7\u4e86\u4e00\u4e1d\u5149\u8292\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u4f60\u4e5f\u600e\u9ebd\u6837\uff1f\u201d \u3000\u3000\u7206\u5357\u71d5\u5934\u5782\u5f97\u66f4\u4f4e\uff0c\u9053\uff1a\u201c\u522b\u4eba\u90fd\u8bf4\u4f60\u6700\u4e86\u89e3\u5973\u4eba\uff0c\u4f60\u96be\u9053\u5c31\u4e0d\u4e86\u89e3\u6211\uff1f\u96be\u9053\u8fd8\u4e0d\u660e\u767d \u6211\u7684\u5fc3\u610f\uff1f\u201d \u3000\u3000\u96c4\u5a18\u5b50\u8f7b\u8f7b\u62c9\u8d77\u4e86\u5979\u7684\u624b\uff0c\u5ffd\u53c8\u653e\u5f00\uff0c\u957f\u53f9\u9053\uff1a\u201c\u6211\u8fd8\u662f\u4e0d\u660e\u767d\u597d\u4e9b\u3002\u201d \u3000\u3000\u7206\u5357\u71d5\u9053\uff1a\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u96c4\u5a18\u5b50\u67d4\u58f0\u9053\uff1a\u201c\u56e0\u4e3a\u4f60\u548c\u522b\u7684\u5973\u5b69\u5b50\u4e0d\u540c\uff0c\u6211\u4e0d\u80fd\u2026\u2026\u4e0d\u80fd\u5bb3\u4e86\u4f60\u3002\u201d \u3000\u3000\u7206\u5357\u71d5\u9053\uff1a\u201c\u6211\u4e5f\u662f\u4e2a\u5973\u4eba\uff0c\u6211\u4e5f\u8981\u2026\u2026\u4e5f\u8981\u2026\u2026\u201d \u3000\u3000\u96c4\u5a18\u5b50\u53f9\u9053\uff1a\u201c\u5728\u6211\u773c\u4e2d\uff0c\u4f60\u6c38\u8fdc\u662f\u90a3\u9ebd\u6e29\u67d4\uff0c\u90a3\u9ebd\u7eaf\u6d01\uff0c\u90a3\u9ebd\u53ef\u7231\uff0c\u53ea\u8981\u80fd\u8fdc\u8fdc\u7684\u671b \u7740\u4f60\uff0c\u6211\u5df2\u5fc3\u6ee1\u610f\u8db3\u4e86\u3002\u201d \u3000\u3000\u4ed6\u6e29\u67d4\u7684\u53d9\u8bf4\u7740\uff0c\u695a\u7559\u9999\u5728\u6697\u4e2d\u542c\u5f97\u53ea\u6709\u53f9\u606f\u3002 \u3000\u3000\u8fd9\u4e9b\u8bdd\uff0c\u6bcf\u4e00\u53e5\u90fd\u662f\u5973\u5b69\u5b50\u6700\u7231\u542c\u7684\uff0c\u6bcf\u4e2a\u5973\u5b69\u5b50\u90fd\u5e0c\u671b\u5979\u5728\u7537\u4eba\u5fc3\u76ee\u4e2d\u548c\u522b\u4eba\u4e0d\u540c\uff0c \u90fd\u5e0c\u671b\u7537\u4eba\u5d07\u62dc\u5979\u3002 \u3000\u3000\u4e00\u4e2a\u5973\u5b69\u5b50\u542c\u5230\u8fd9\u4e9b\u8bdd\u5f8c\uff0c\u82e5\u8fd8\u80fd\u62d2\u7edd\u4ed6\uff0c\u90a3\u624d\u771f\u662f\u602a\u4e8b\uff0c\u695a\u7559\u9999\u552f\u4e00\u89c9\u5f97\u5e86\u5e78\u7684\u662f\uff0c \u5e78\u597d\u8fd9\u91cc\u6ca1\u6709\u8272\u72fc\u5728\u5077\u542c\u3002 \u3000\u3000\u8fd9\u4e9b\u8bdd\u82e5\u88ab\u8272\u72fc\u4eec\u5b66\u4f1a\uff0c\u4e16\u4e0a\u66f4\u4e0d\u77e5\u6709\u591a\u5c11\u5973\u5b69\u5b50\u8981\u906d\u6b83\u4e86\u3002 \u3000\u3000\u4f46\u8f6c\u5ff5\u4e00\u60f3\uff0c\u695a\u7559\u9999\u53c8\u4e0d\u7981\u82e6\u7b11\uff0c\u6697\u9053\uff1a\u201c\u4e00\u4e2a\u7537\u4eba\u82e5\u5df2\u6709\u8d44\u683c\u88ab\u79f0\u4e3a\u201c\u8272\u72fc\u201d\uff0c\u8fd9\u4e9b \u8bdd\u4ed6\u5fc5\u5b9a\u65e9\u5df2\u8bf4\u5f97\u6eda\u74dc\u70c2\u719f\u4e86\uff0c\u53c8\u4f55\u5fc5\u518d\u6765\u5b66\u5462\uff1f\u201d \u7b2c\u4e8c\u5341\u516d\u7ae0\u3000\u864e\u7a74\u9f99\u6f6d>> \u53e4\u9f99\u300a\u695a\u7559\u9999\u7cfb\u5217\xb7\u753b\u7709\u9e1f\u300b \u7b2c\u4e8c\u5341\u516d\u7ae0\u3000\u864e\u7a74\u9f99\u6f6d \u3000\u3000\u661f\u5149\u5df2\u5347\u8d77\uff0c\u5728\u5982\u6b64\u6e29\u67d4\u7684\u661f\u5149\u4e0b\uff0c\u6700\u575a\u5f3a\u7684\u5973\u5b50\u4e5f\u4f1a\u53d8\u5f97\u8f6f\u5f31\u8d77\u6765\u7684\uff0c\u5bab\u5357\u71d5\u5df2\u504e\u5165 \u96c4\u5a18\u5b50\u6000\u91cc\u3002 \u3000\u3000\u96c4\u5a18\u5b50\u8f7b\u629a\u7740\u5979\u7684\u67d4\u53d1\uff0c\u8f7b\u8f7b\u9053\uff1a\u201c\u4f60\u603b\u8be5\u77e5\u9053\uff0c\u6211\u4eec\u7edd\u4e0d\u53ef\u80fd\u6c38\u8fdc\u5b88\u5728\u4e00\u8d77\u7684\u3002\u201d \u3000\u3000\u7206\u5357\u71d5\u9053\uff1a\u201c\u6211\u77e5\u9053\u3002\u201d \u3000\u3000\u96c4\u5a18\u5b50\u9053\uff1a\u201c\u4f60\u4e0d\u5f8c\u6094\uff1f\u201d \u3000\u3000\u7206\u5357\u71d5\u9053\uff1a\u201c\u6211\u7edd\u4e0d\u5f8c\u6094\uff0c\u53ea\u8981\u80fd\u6709\u4e00\u6b21\uff0c\u8ba9\u6211\u4ee5\u5f8c\u80fd\u6709\u4e2a\u751c\u871c\u7684\u56de\u4ebf\uff0c\u5c31\u7b97\u8981\u6211\u6b7b\uff0c \u6211\u4e5f\u5fc3\u7518\u60c5\u613f\u4e86\u3002\u201d \u3000\u3000\u96c4\u5a18\u5b50\u4e0d\u518d\u8bf4\u8bdd\uff0c\u4ed6\u7684\u624b\u6ed1\u8fdb\u4e86\u5979\u4e86\u5979\u7684\u8863\u670d\u2026\u2026 \u3000\u3000\u695a\u7559\u9999\u867d\u7136\u4e0d\u662f\u541b\u5b50\uff0c\u4e5f\u4e0d\u80fd\u518d\u770b\u4e0b\u53bb\u4e86\uff0c\u4ed6\u6084\u6084\u7ffb\u4e86\u4e2a\u8eab\uff0c\u4ef0\u671b\u7740\u5929\u4e0a\u7684\u661f\u5149\uff0c\u661f\u661f \u4f3c\u4e4e\u5728\u5411\u4ed6\u7728\u773c\u3002 \u3000\u3000\u7206\u5357\u71d5\u7adf\u662f\u8fd9\u9ebd\u6837\u4e00\u4e2a\u5973\u5b69\u5b50\uff0c\u4ed6\u5b9e\u5728\u60f3\u4e0d\u5230\u3002 \u3000\u3000\u53ef\u662f\uff0c\u5973\u5b69\u5b50\u5230\u4e86\u5979\u8fd9\u79cd\u5e74\u7eaa\uff0c\u53ef\u6709\u8c01\u4e0d\u6000\u6625\u5462\uff1f \u3000\u3000\u695a\u7559\u9999\u6697\u6697\u53f9\u606f\uff0c\u6697\u6697\u82e6\u7b11\u3002 \u3000\u3000\u4ed6\u4f3c\u4e4e\u6709\u4e9b\u5f8c\u6094\uff0c\u81ea\u5df1\u4e3a\u4ec0\u9ebd\u9519\u8fc7\u4e86\u673a\u4f1a\u3002 \u3000\u3000\u7a81\u542c\u5bab\u5357\u71d5\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u8981\u5230\u90a3\u91cc\u53bb\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u5fcd\u4e0d\u4f4f\u626d\u5934\u77a7\u4e86\u4e00\u773c\uff0c\u53ea\u89c1\u96c4\u5a18\u5b50\u5ffd\u7136\u81ea\u90a3\u5c0f\u8239\u91cc\u5750\u4e86\u8d77\u6765\uff0c\u8f7b\u8f7b\u7684\u53f9\u606f\u7740\u9053\uff1a \u201c\u6211\u4e5f\u820d\u4e0d\u5f97\u8d70\uff0c\u53ef\u662f\u65f6\u5019\u5df2\u4e0d\u65e9\u4e86\uff0c\u6211\u4e00\u5b9a\u8981\u53bb\u2026\u2026\u201d \u3000\u3000\u7206\u5357\u71d5\u9053\uff1a\u201c\u4f60\u8981\u53bb\u627e\u5c0f\u9759\u7684\u2026\u2026\u201d \u3000\u3000\u96c4\u5a18\u5b50\u53f9\u9053\uff1a\u201c\u65e0\u8bba\u5982\u4f55\uff0c\u6211\u603b\u662f\u5979\u7236\u4eb2\uff0c\u603b\u8be5\u53bb\u770b\u770b\u5979\u6700\u5f8c\u7684\u5f52\u5bbf\u3002\u201d \u3000\u3000\u7206\u5357\u71d5\u9053\uff1a\u201c\u4f60\u4e0d\u5fc5\u7740\u6025\uff0c\u6211\u4f1a\u5e26\u4f60\u53bb\u7684\uff0c\u73b0\u5728\u2026\u2026\u201d \u3000\u3000\u4e00\u53ea\u7c89\u5149\u81f4\u81f4\u7684\u624b\u81c2\u81ea\u5c0f\u821f\u4e2d\u4f38\u51fa\u6765\uff0c\u5c06\u96c4\u5a18\u5b50\u53c8\u62c9\u4e86\u4e0b\u53bb\u2014\u2014\u4ed6\u65e9\u5c31\u5728\u7b49\u5bab\u5357\u71d5\u8bf4\u8fd9 \u53e5\u8bdd\u4e86\u3002 \u3000\u3000\u695a\u7559\u9999\u81ea\u7136\u4e5f\u77e5\u9053\u96c4\u5a18\u5b50\u8fd9\u662f\u5728\u5229\u7528\u5979\uff0c\u53ef\u662f\u4ed6\u65e2\u4e0d\u80fd\u8bf4\u7834\uff0c\u4e5f\u4e0d\u80fd\u963b\u6b62\uff0c\u56e0\u4e3a\u8fd9\u662f\u5bab \u5357\u71d5\u5fc3\u7518\u60c5\u613f\u7684\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u5f53\u4e00\u4e2a\u5973\u4eba\uff0c\u51b3\u5fc3\u8981\u505a\u8fd9\u4ef6\u4e8b\u7684\u65f6\u5019\uff0c\u4efb\u4f55\u4eba\u90fd\u4e0d\u80fd\u53bb\u963b\u6b62\uff0c\u5426\u5219\u5979\u5c31\u7b97\u4e0d\u6740 \u4f60\uff0c\u4e5f\u8981\u6068\u4f60\u4e00\u8f88\u5b50\u3002 \u3000\u3000\u8f7b\u821f\u5ffd\u7136\u5267\u70c8\u7684\u52a8\u8361\u8d77\u6765\uff0c\u98ce\u4e2d\u4f20\u6765\u4e86\u9500\u9b42\u7684\u547b\u541f\u3002 \u3000\u3000\u661f\u5149\u66f4\u6726\u80e7\u3002 \u3000\u3000\u695a\u7559\u9999\u53ea\u6709\u95ed\u4e0a\u773c\u775b\u3002 \u3000\u3000\u4f46\u4ed6\u5374\u4e0d\u80fd\u585e\u4f4f\u8033\u6735\uff0c\u8fc7\u4e86\u534a\u664c\uff0c\u53ea\u542c\u5bab\u5357\u71d5\u68a6\u5453\u822c\u4f4e\u8bed\u9053\uff1a\u201c\u4f60\u771f\u2026\u2026\u771f\u7684\uff0c\u96be\u602a\u90a3 \u4e9b\u5973\u4eba\u60c5\u613f\u4e3a\u4f60\u6b7b\uff0c\u96be\u602a\u5979\u6c38\u8fdc\u5fd8\u4e0d\u4e86\u4f60\uff0c\u6015\u5230\u6b7b\u4e5f\u5fd8\u4e0d\u4e86\u4f60\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u53c8\u4e0d\u7981\u5947\u602a\u3002 \u3000\u3000\u7206\u5357\u71d5\u8bf4\u7684\u201c\u5979\u201d\u662f\u8c01\u5462\uff1f\u662f\u96c4\u5a18\u5b50\u7684\u60c5\u4eba\uff1f \u3000\u3000\u96c4\u5a18\u5b50\u5728\u4f4e\u4f4e\u7684\u5598\u606f\uff0c\u9053\uff1a\u201c\u4f60\u4e5f\u5f88\u597d\u3002\u201d \u3000\u3000\u7206\u5357\u71d5\u817b\u58f0\u9053\uff1a\u201c\u6211\u96be\u9053\u6bd4\u5979\u8fd8\u597d\uff1f\u201d \u3000\u3000\u96c4\u5a18\u5b50\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u9ebd\u603b\u662f\u8981\u63d0\u8d77\u5979\uff0c\u96be\u9053\u4f60\u548c\u5979\u4e5f\u2026\u2026\u201d \u3000\u3000\u7206\u5357\u71d5\u5ffd\u7136\u5403\u5403\u7684\u7b11\u4e86\u8d77\u6765\uff0c\u901a\uff1a\u201c\u4f60\u53ef\u77e5\u9053\u6211\u4e3a\u4ec0\u9ebd\u8981\u8ddf\u4f60\u597d\uff1f\u201d \u3000\u3000\u96c4\u5a18\u5b50\u4f3c\u4e4e\u6014\u4e86\u6014\uff0c\u9053\uff1a\u201c\u4f60\u96be\u9053\u662f\u56e0\u4e3a\u5979\u2026\u2026\u201d \u3000\u3000\u7206\u5357\u71d5\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5c31\u56e0\u4e3a\u5979\u5f97\u5230\u4e86\u4f60\uff0c\u6240\u4ee5\u627e\u4e5f\u4e00\u5b9a\u8981\u5f97\u5230\u4f60\u3002\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u521a\u8bf4\u5b8c\uff0c\u96c4\u5a18\u5b50\u5ffd\u7136\u53d1\u51fa\u4e00\u58f0\u51c4\u60e8\u7684\u547c\u58f0\u3002 \u3000\u3000\u695a\u7559\u9999\u5403\u4e86\u4e00\u60ca\uff0c\u626d\u5934\u53bb\u770b\uff0c\u53ea\u89c1\u96c4\u5a18\u5b50\u5df2\u8d64\u88f8\u7740\u81ea\u5c0f\u821f\u91cc\u7ad9\u4e86\u8d77\u6765\uff0c\u98a4\u6296\u7740\u7ad9\u5728\u8239 \u5934\u3002 \u3000\u3000\u661f\u5149\u4e0b\uff0c\u8ff7\u96fe\u4e2d\uff0c\u4ed6\u82cd\u767d\u7684\u80f8\u819b\u4e0a\u9c9c\u8840\u4e0d\u65ad\u7684\u5f80\u5916\u5192\u3002 \u3000\u3000\u53ea\u542c\u5bab\u5357\u71d5\u5403\u5403\u7b11\u9053\uff1a\u201c\u4f60\u4f55\u5fc5\u5403\u60ca\uff0c\u6211\u53ea\u4e0d\u8fc7\u60f3\u5c06\u4f60\u7684\u5fc3\uff0c\u6316\u51fa\u6765\u77a7\u77a7\u800c\u5df2\u3002\u201d \u3000\u3000\u96c4\u5a18\u4e8e\u53cc\u624b\u7d27\u7d27\u6309\u5728\u80f8\u524d\u7684\u521b\u53e3\uff0c\u98a4\u58f0\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u4e3a\u4ec0\u9ebd\u8981\u8fd9\u6837\u505a\uff1f\u201d \u3000\u3000\u7206\u5357\u71d5\u9053\uff1a\u201c\u4f60\u8fd8\u4e0d\u77e5\u9053\uff1f\u4f60\u8fd8\u4ee5\u4e3a\u6211\u662f\u771f\u7684\u559c\u6b22\u4f60\uff1f\u201d \u3000\u3000\u5979\u4e0d\u505c\u7684\u7b11\u7740\uff0c\u5ffd\u7136\u4e5f\u7ad9\u4e86\u8d77\u6765\uff0c\u5728\u4f4e\u8ff7\u7684\u661f\u5149\u4e0b\uff0c\u5979\u6210\u719f\u7684\u5c11\u5973\u80f4\u4f53\uff0c\u770b\u6765\u6676\u83b9\u5982 \u7389\u3002 \u3000\u3000\u4f46\u5979\u7684\u8138\u4e0a\u5374\u5e26\u7740\u6076\u9b54\u822c\u7684\u5996\u6c14\uff0c\u7f8e\u4e3d\u7684\u773c\u775b\u91cc\uff0c\u66f4\u5145\u6ee1\u4e86\u6028\u6bd2\u548c\u6740\u673a\uff0c\u5979\u77aa\u7740\u96c4\u5a18\u5b50 \u9053\uff1a\u201c\u8001\u5b9e\u544a\u8bc9\u4f60\uff0c\u6211\u65e9\u5c31\u60f3\u6740\u4f60\u4e86\uff0c\u6211\u4e0d\u80fd\u5fcd\u53d7\u5979\u5728\u6211\u9762\u524d\u63d0\u8d77\u4f60\uff0c\u8bf4\u6211\u591a\u9ebd\u50cf\u4f60\uff0c\u53ea\u8981 \u4e00\u63d0\u8d77\u4f60\uff0c\u6211\u5c31\u96be\u53d7\u5f97\u8981\u53d1\u75af\u3002\u201d \u3000\u3000\u96c4\u5a18\u5b50\u55c4\u58f0\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u5728\u5403\u918b\uff1f\u96be\u9053\u4f60\u7adf\u4f1a\u7231\u4e0a\u5979\u4e0d\u6210\uff1f\u201d \u3000\u3000\u7206\u5357\u71d5\u5927\u58f0\u9053\uff1a\u201c\u6211\u4e3a\u4ec0\u9ebd\u4e0d\u80fd\u7231\u4e0a\u5979\uff1f\u4e3a\u4ec0\u9ebd\u4e0d\u80fd\uff1f\u201d \u3000\u3000\u96c4\u5a18\u5b50\u5403\u60ca\u7684\u77a7\u7740\u5979\uff0c\u4eba\u5374\u5df2\u5012\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u73b0\u5728\uff0c\u695a\u7559\u9999\u53c8\u4e0d\u77e5\u9053\u5bab\u5357\u71d5\u6240\u8bf4\u7684\u201c\u5979\u201d\u7a76\u7adf\u662f\u7537\uff0c\u8fd8\u662f\u5973\u4e86\uff0c\u201c\u5979\u201d\u82e5\u662f\u7537\u7684\uff0c\u600e \u4f1a\u662f\u96c4\u5a18\u5b50\u7684\u60c5\u4eba\u3002 \u3000\u3000\u201c\u5979\u201d\u82e5\u662f\u5973\u7684\uff0c\u5bab\u5357\u71d5\u53c8\u600e\u4f1a\u7231\u4e0a\u5979\uff1f \u3000\u3000\u695a\u7559\u9999\u5b9e\u5728\u731c\u4e0d\u5230\u5979\u4eec\u8fd9\u53c1\u4e2a\u4eba\u4e4b\u95f4\uff0c\u7a76\u7adf\u662f\u4ec0\u9ebd\u5173\u7cfb\u2014\u2014\u8fd9\u53c1\u4e2a\u4eba\u4e4b\u95f4\u7684\u5173\u7cfb\u592a\u590d\u6742 \u4e86\u3002 \u3000\u3000\u53ea\u542c\u201c\u5657\u901a\u201d\u4e00\u58f0\uff0c\u96c4\u5a18\u5b50\u5df2\u8dcc\u5165\u6d41\u6c34\uff0c\u4e8c\u5341\u5e74\u7684\u82e6\u884c\u5fcf\u6094\uff0c\u7ec8\u65bc\u8fd8\u662f\u4e0d\u80fd\u6d17\u6e05\u4ed6\u7684\u7f6a \u5b7d\u3002 \u3000\u3000\u4ed6\u6bd5\u7adf\u8fd8\u662f\u6b7b\u5728\u5973\u4eba\u624b\u91cc\u3002 \u3000\u3000\u7206\u5357\u71d5\u7ad9\u5728\u8239\u5934\uff0c\u75f4\u75f4\u7684\u671b\u7740\u661f\u5149\u4e0b\u7684\u6d41\u6c34\u3002 \u3000\u3000\u7136\u5f8c\u5979\u4e5f\u8dc3\u5165\u6c34\u91cc\uff0c\u5c06\u8eab\u4e0a\u6bcf\u4e00\u5206\uff0c\u6bcf\u4e00\u5bf8\u5730\u65b9\u90fd\u6d17\u5f97\u4e7e\u4e7e\u51c0\u51c0\uff0c\u7b49\u5979\u7a7f\u597d\u8863\u670d\u65f6\uff0c\u5979 \u770b\u6765\u53c8\u662f\u90a3\u9ebd\u5723\u6d01\u4e86\u3002 \u3000\u3000\u591c\u8272\u5df2\u6d53\uff0c\u6d53\u96fe\u53cd\u800c\u6de1\u4e86\u4e9b\u3002 \u3000\u3000\u4e00\u58f0\u8f6f\u4e43\uff0c\u8f7b\u821f\u53c8\u8361\u5165\u6d53\u6d53\u7684\u591c\u8272\u4e2d\u3002 \u3000\u3000\u695a\u7559\u9999\u8fde\u8003\u8651\u90fd\u6ca1\u6709\u8003\u8651\uff0c\u4e5f\u6f5c\u5165\u6c34\u4e2d\uff0c\u522b\u4eba\u90fd\u8bf4\u4ed6\u8f7b\u529f\u7b2c\u4e00\uff0c\u4ed6\u81ea\u5df1\u5374\u8ba4\u4e3a\u6c34\u6027\u6bd4\u8f7b \u529f\u8fd8\u597d\u5f97\u591a\u3002 \u3000\u3000\u5c31\u7b97\u9c7c\u8dc3\u5165\u6c34\u91cc\uff0c\u4e5f\u7edd\u4e0d\u4f1a\u6709\u4ed6\u8fd9\u9ebd\u7075\u6d3b\u3002 \u3000\u3000\u8f7b\u821f\u5728\u524d\u9762\u8d70\uff0c\u4ed6\u6f5c\u4f0f\u5728\u6c34\u4e0b\uff0c\u6697\u6697\u8ffd\u8e2a\uff0c\u4ed6\u76f8\u4fe1\u5bab\u5357\u71d5\u5728\u6b64\u65f6\u6b64\u523b\uff0c\u7edd\u4e0d\u4f1a\u53d1\u89c9\u5230\u5f8c \u9762\u6709\u4eba\u8ffd\u8e2a\u7684\u3002 \u3000\u3000\u65e0\u8bba\u4efb\u4f55\u4eba\u5728\u505a\u8fc7\u8fd9\u79cd\u4e8b\u5f8c\uff0c\u611f\u89c9\u90fd\u4f1a\u53d8\u5f97\u8fdf\u949d\u4e9b\u3002 \u3000\u3000\u5c0f\u6eaa\u65c1\u7684\u98ce\u7269\u5728\u6709\u661f\u6709\u96fe\u7684\u665a\u4e0a\u5fc5\u5b9a\u751a\u7f8e\uff0c\u695a\u7559\u9999\u867d\u770b\u4e0d\u5230\uff0c\u5374\u53ef\u4ee5\u60f3\u50cf\uff0c\u60f3\u50cf\u6c38\u8fdc\u6bd4 \u5b9e\u9645\u66f4\u7f8e\u5f97\u591a\u7684\u3002 \u3000\u3000\u4e5f\u4e0d\u77e5\u8d70\u4e86\u591a\u4e45\uff0c\u4ed6\u53d1\u89c9\u5c0f\u821f\u4ee5\u5df2\u8361\u5165\u4e86\u4e00\u6761\u5c71\u9699\u91cc\uff0c\u6c34\u5e95\u7684\u6c34\u8349\u5f88\u591a\uff0c\u800c\u4e14\u5e26\u7740\u79cd\u9634 \u68ee\u68ee\u7684\u6c14\u606f\u3002 \u3000\u3000\u4ed6\u4e5f\u60f3\u4f38\u51fa\u5934\u6765\u77a7\u77a7\uff0c\u4f46\u662f\u4ed6\u5e76\u6ca1\u6709\u8fd9\u9ebd\u6837\u505a\uff0c\u53c8\u8fc7\u4e86\u534a\u664c\uff0c\u4ed6\u5c31\u542c\u5230\u5c0f\u821f\u9760\u5cb8\u7684\u58f0 \u97f3\u3002 \u3000\u3000\u4ed6\u8fd8\u662f\u6ca1\u6709\u4f38\u51fa\u5934\u6765\uff0c\u4ed6\u81ea\u5df1\u4ece\u6765\u6ca1\u6709\u8bd5\u8fc7\u81ea\u5df1\u7a76\u7adf\u80fd\u5728\u6c34\u5e95\u6f5c\u4f0f\u591a\u4e45\uff0c\u5b8b\u751c\u513f\u603b\u8ba4\u4e3a \u4ed6\u53ef\u4ee5\u5728\u6c34\u4e0b\u7761\u89c9\u3002 \u3000\u3000\u6c34\u5e95\u7684\u4e16\u754c\uff0c\u6bd4\u6c34\u4e0a\u5b89\u9759\u5f97\u591a\u3002 \u3000\u3000\u4ed6\u53c8\u7b49\u4e86\u5f88\u4e45\uff0c\u8fd8\u662f\u542c\u4e0d\u5230\u4efb\u4f55\u58f0\u97f3\uff0c\u65bc\u662f\u4ed6\u5c31\u7528\u4e00\u5806\u6c34\u8349\u76d6\u7740\u5934\uff0c\u81ea\u6c34\u9762\u4e0b\u6084\u6084\u9732\u51fa \u4e86\u773c\u775b\u3002 \u3000\u3000\u4ed6\u7ec8\u65bc\u770b\u5230\u4e86\u795e\u6c34\u5bab\u3002 \u3000\u3000\u8fd9\u90a3\u91cc\u662f\u4eba\u95f4\u7684\u5c71\u8c37\uff0c\u7b80\u76f4\u662f\u4e00\u5e45\u7edd\u5999\u7684\u56fe\u753b\u3002 \u3000\u3000\u695a\u7559\u9999\u60f3\u8d77\u82cf\u84c9\u84c9\u66fe\u7ecf\u8bf4\u8fc7\uff0c\u5c71\u8c37\u91cc\u672c\u6709\u5343\u767e\u53ea\u5404\u5f0f\u5404\u6837\u7684\u9e1f\uff0c\u73b0\u5728\u9e1f\u5df2\u6c88\u7761\u4e86\uff0c\u4eba\u5374 \u4f3c\u8fd8\u6ca1\u6709\u7761\u3002 \u3000\u3000\u56fe\u753b\u822c\u7684\u5c71\u6797\u95f4\uff0c\u8fd8\u4eae\u7740\u4e00\u70b9\u70b9\u706f\u5149\uff0c\u6620\u7740\u90a3\u4e00\u61a7\u649e\u4ead\u53f0\u697c\u9601\uff0c\u7af9\u85e4\u8305\u820d\uff0c\u4e5f\u6620\u7740\u90a3\u4e00 \u9053\u7011\u5e03\u3002 \u3000\u3000\u7011\u5e03\u4ece\u5929\u800c\u964d\uff0c\u98de\u73e0\u6e85\u7389\uff0c\u707f\u70c2\u5982\u94f6\uff0c\u5947\u602a\u7684\u662f\uff0c\u8fd9\u9ebd\u5927\u7684\u7011\u5e03\u81ea\u534a\u7a7a\u4e2d\u5012\u6302\u800c\u4e0b\uff0c\u6cc4 \u5165\u6e56\u4e2d\uff0c\u6c34\u58f0\u5e76\u4e0d\u9707\u8033\uff0c\u53cd\u800c\u5982\u9e23\u7434\u594f\u7389\uff0c\u542c\u6765\u4f46\u89c9\u795e\u6e05\u6c14\u723d\uff0c\u663e\u7136\u6c34\u529b\u5df2\u88ab\u5de7\u5999\u7684\u5ba3\u6cc4\u4e86 \u5f88\u591a\u3002 \u3000\u3000\u98ce\u58f0\u4e2d\u4f3c\u4e4e\u9690\u9690\u6709\u4e1d\u7af9\u58f0\u4f20\u6765\uff0c\u886c\u7740\u7476\u78a7\u822c\u7684\u6d41\u6c34\u58f0\uff0c\u4f7f\u8fd9\u56fe\u753b\u822c\u7684\u5c71\u8c37\uff0c\u770b\u6765\u66f4\u5e73\u548c \u800c\u5b89\u8be6\u3002 \u3000\u3000\u4f46\u695a\u7559\u9999\u5374\u53c8\u60f3\u8d77\u82cf\u84c9\u84c9\u7684\u59d1\u59d1\u66fe\u7ecf\u8b66\u6212\u8fc7\u5979\uff1a\u201c\u82e5\u5728\u5c71\u8c37\u4e2d\u968f\u610f\u8d70\u52a8\uff0c\u7acb\u523b\u5c31\u4f1a\u6709\u53ef \u6015\u7684\u707e\u7978\u3002\u201d \u3000\u3000\u5728\u5982\u6b64\u5e73\u548c\u5b89\u8be6\u7684\u5730\u65b9\uff0c\u53c8\u600e\u4f1a\u6709\u53ef\u6015\u7684\u707e\u7978\u5462\uff1f \u3000\u3000\u695a\u7559\u9999\u5df2\u53d1\u73b0\u8fd9\u5730\u65b9\u5e76\u4e0d\u662f\u8868\u9762\u770b\u6765\u90a3\u9ebd\u5e73\u9759\uff0c\u201c\u795e\u6c34\u5bab\u201d\u4e5f\u5e76\u4e0d\u662f\u4f20\u8bf4\u4e2d\u90a3\u9ebd\u5723\u6d01\u7684 \u5730\u65b9\u3002 \u3000\u3000\u8fd9\u91cc\u5fc5\u5b9a\u9690\u85cf\u7740\u8bb8\u591a\u60ca\u4eba\u53ef\u6015\u7684\u79d8\u5bc6\u3002 \u3000\u3000\u4ed6\u73b0\u5728\u5df2\u4e0d\u4f46\u8981\u5411\u201c\u6c34\u6bcd\u201d\u9634\u59ec\u89e3\u91ca\u8bef\u4f1a\uff0c\u8fd8\u51b3\u5fc3\u8981\u67e5\u63a2\u6b64\u95f4\u7684\u79d8\u5bc6\uff0c\u6240\u4ee5\u4ed6\u884c\u52a8\u66f4\u5f97 \u4efd\u5916\u5c0f\u5fc3\u3002 \u3000\u3000\u5c0f\u821f\u8fd8\u505c\u7559\u5728\u5cb8\u8fb9\uff0c\u5bab\u5357\u71d5\u5374\u5df2\u77a7\u4e0d\u89c1\u4e86\u3002\u5c71\u8c37\u4e2d\u9759\u6084\u6084\u7684\u6ca1\u6709\u4eba\u8e2a\uff0c\u695a\u7559\u9999\u5b9e\u5728\u4e0d\u77e5 \u8be5\u4ece\u4f55\u5904\u4e0b\u624b\u3002 \u3000\u3000\u4ed6\u8003\u8651\u4e86\u534a\u664c\uff0c\u5ffd\u7136\u60f3\u8d77\u4e86\u65e0\u82b1\u7684\u906d\u9047\u2014\u2014\u8fd9\u6240\u6709\u7684\u4e00\u5207\u4e8b\uff0c\u90fd\u662f\u4ece\u4e00\u4e2a\u5c0f\u5c0f\u7684\u5c3c\u5eb5\u4e2d \u5f00\u59cb\u7684\u3002 \u3000\u3000\u6781\u76ee\u671b\u53bb\uff0c\u5c71\u811a\u65c1\u679c\u7136\u6709\u5ea7\u5c3c\u5eb5\u3002\u201c\u6c34\u6bcd\u201d\u9634\u59ec\u662f\u5426\u5c31\u5728\u8fd9\u5c3c\u5eb5\u4e2d\u5462\uff1f\u695a\u7559\u9999\u4e0b\u5b9a\u51b3 \u5fc3\uff0c\u65e0\u8bba\u5982\u4f55\uff0c\u90fd\u8981\u5148\u5230\u8fd9\u5c3c\u5eb5\u4e2d\u77a7\u4e2a\u7a76\u7adf\u3002 \u3000\u3000\u5c3c\u5eb5\u4e2d\u706f\u5149\u9edd\u6697\uff0c\u83b9\u83b9\u5982\u9b3c\u706b\u3002 \u3000\u3000\u695a\u7559\u9999\u51e0\u4e4e\u82b1\u4e86\u534a\u4e2a\u65f6\u8fb0\uff0c\u624d\u7531\u5cb8\u8fb9\u6f5c\u5230\u8fd9\u91cc\uff0c\u4ed6\u786e\u4fe1\u81ea\u5df1\u7edd\u6ca1\u6709\u53d1\u51fa\u6bd4\u868a\u5b50\u66f4\u5927\u7684\u58f0 \u97f3\u3002 \u3000\u3000\u8fd9\u6bb5\u8def\u9014\u867d\u975e\u9065\u8fdc\uff0c\u4f46\u666e\u5929\u4e4b\u4e0b\uff0c\u9664\u4e86\u695a\u7559\u9999\u5916\uff0c\u6015\u518d\u4e5f\u6ca1\u6709\u7b2c\u4e8c\u4e2a\u4eba\u80fd\u8d70\u5f97\u5230\u4e86\u3002 \u3000\u3000\u5c3c\u5eb5\u4e2d\u9759\u6084\u65e0\u4eba\uff0c\u4e00\u5c18\u4e0d\u67d3\uff0c\u4f46\u5eb5\u524d\u7684\u51e0\u5341\u7ea7\u77f3\u9636\uff0c\u4e5f\u90fd\u5e73\u6ed1\u6e05\u51c0\u5f97\u50cf\u955c\u5b50\u4e00\u6837\uff0c\u5149\u53ef \u9274\u4f4e\u5782\u7684\u795e\u5e54\u524d\uff0c\u4e00\u706f\u5982\u8c46\uff0c\u695a\u7559\u9999\u5728\u56db\u9762\u67e5\u63a2\u4e86\u5f88\u4e45\uff0c\u65ad\u5b9a\u8fd9\u91cc\u7edd\u6ca1\u6709\u4eba\u65f6\uff0c\u624d\u98de\u8eab\u800c \u5165\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u8fd9\u5c3c\u5eb5\u4e2d\u6709\u6761\u79d8\u9053\uff0c\u8bf4\u4e0d\u5b9a\u5c31\u662f\u901a\u5411\u201c\u6c34\u6bcd\u201d\u9634\u59ec\u4f4f\u5904\u7684\uff0c\u53ef\u662f\uff0c\u79d8\u9053\u7a76\u7adf\u5728\u90a3 \u91cc\u5462\uff1f \u3000\u3000\u795e\u6848\u524d\u6709\u53c1\u53ea\u84b2\u56e2\uff0c\u79d8\u5bc6\u662f\u5426\u5c31\u5728\u84b2\u56e2\u4e0b\uff1f \u3000\u3000\u695a\u7559\u9999\u5c06\u53c1\u53ea\u84b2\u56e2\u90fd\u79fb\u5f00\u4e86\uff0c\u84b2\u56e2\u4e0b\u4e5f\u662f\u5e73\u6574\u7684\u77f3\u5730\uff0c\u4ed6\u5931\u671b\u7684\u53f9\u4e86\u53e3\u6c14\uff0c\u76ee\u5149\u79fb\u5230\u795e \u5e54\u4e0a\u3002 \u3000\u3000\u4ed6\u5fcd\u4e0d\u4f4f\u8981\u4f38\u624b\u53bb\u6380\u795e\u5e54\u3002 \u3000\u3000\u53ef\u662f\uff0c\u5c31\u5728\u8fd9\u65f6\uff0c\u4ed6\u5ffd\u7136\u542c\u5230\u4e00\u58f0\u53f9\u606f\u3002 \u3000\u3000\u53f9\u606f\u58f0\u662f\u90a3\u9ebd\u8f7b\uff0c\u4f46\u5728\u695a\u7559\u9999\u6b64\u523b\u542c\u6765\uff0c\u5374\u65e0\u5f02\u9752\u5929\u4e4b\u9739\u96f3\uff0c\u4ed6\u60f3\u9000\uff0c\u4f46\u77e5\u9053\u9000\u5df2\u6765\u4e0d \u53ca\u4e86\u3002 \u3000\u3000\u8868\u706b\u822c\u7684\u706f\u5149\u4e0b\uff0c\u4ed6\u5df2\u770b\u5230\u4e00\u6761\u767d\u8863\u4eba\u5f71\uff0c\u5979\u5c31\u50cf\u5e7d\u7075\u822c\u5ffd\u7136\u81ea\u5730\u5e95\u51fa\u73b0\uff0c\u6b63\u9759\u9759\u7684\u77a7 \u7740\u695a\u7559\u9999\u3002 \u3000\u3000\u53ea\u542c\u5979\u53f9\u606f\u7740\u9053\uff1a\u201c\u8fd9\u91cc\u5df2\u6709\u4e8c\u5341\u5e74\u672a\u66fe\u6d41\u8840\u4e86\uff0c\u4f60\u4f55\u5fc5\u4e00\u5b9a\u8981\u6b7b\u5728\u8fd9\u91cc\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u82e6\u7b11\u7740\u63c9\u4e86\u63c9\u9f3b\u5b50\uff0c\u9053\uff1a\u201c\u8001\u5b9e\u8bf4\uff0c\u6211\u5e76\u4e0d\u60f3\u6b7b\u7684\u3002\u201d \u3000\u3000\u4ed6\u53d1\u73b0\u8fd9\u662f\u4e2a\u975e\u5e38\u7f8e\u4e3d\u7684\u5973\u4eba\uff0c\u53ea\u4e0d\u8fc7\u65e0\u60c5\u7684\u5c81\u6708\u5df2\u5728\u5979\u8138\u4e0a\u7559\u4e0b\u4e86\u4e00\u4e9b\u6b8b\u9177\u7684\u75d5\u8ff9\u3002 \u3000\u3000\u5979\u7684\u76ee\u5149\u867d\u4e5f\u5341\u5206\u51b7\u6f20\uff0c\u4f46\u5374\u5e76\u6ca1\u6709\u4ec0\u9ebd\u6740\u673a\u3002 \u3000\u3000\u8fd9\u96be\u9053\u5c31\u662f\u5982\u4eca\u5929\u4e0b\u4eba\u754f\u4e4b\u5982\u864e\u7684\u201c\u6c34\u6bcd\u201d\u9634\u59ec\uff1f \u3000\u3000\u767d\u8863\u5982\u4e91\u7684\u4e2d\u5e74\u7f8e\u5987\u4eba\u4ecd\u7136\u5728\u9759\u9759\u7684\u77a7\u7740\u4ed6\u3002 \u3000\u3000\u695a\u7559\u9999\u52c9\u5f3a\u4e00\u7b11\uff0c\u6309\u7740\u9053\uff1a\u201c\u665a\u8f88\u6b64\u6765\uff0c\u53ea\u4e0d\u8fc7\u662f\u60f3\u62dc\u89c1\u5bab\u4e3b\u4e00\u9762\u2026\u2026\u201d \u3000\u3000\u767d\u8863\u7f8e\u5987\u6447\u4e86\u6447\u5934\uff0c\u9053\uff1a\u201c\u6211\u5e76\u4e0d\u662f\u4f60\u4eec\u60f3\u89c1\u7684\u4eba\uff0c\u5426\u5219\u4f60\u73b0\u5728\u8fd8\u60f3\u6d3b\u7740\u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u76ee\u5149\u95ea\u52a8\uff0c\u9053\uff1a\u201c\u90a3\u9ebd\u524d\u8f88\u662f\u2026\u2026\u201d \u3000\u3000\u767d\u8863\u7f8e\u5987\u9053\uff1a\u201c\u5c06\u6b7b\u4eba\uff0c\u4f55\u5fc5\u8fd8\u8981\u95ee\u522b\u4eba\u7684\u540d\u5b57\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u524d\u8f88\u82e5\u8981\u6740\u6211\uff0c\u4e3a\u4f55\u8fd8\u4e0d\u52a8\u624b\u5462\uff1f\u201d \u3000\u3000\u767d\u8863\u7f8e\u5987\u9eef\u7136\u9053\uff1a\u201c\u6211\u4e0d\u80fd\u52a8\u624b\uff0c\u5728\u8fd9\u4e16\u4e0a\uff0c\u6211\u5df2\u53ea\u6709\u4e00\u4e2a\u4eb2\u4eba\uff0c\u6211\u600e\u9ebd\u80fd\u6740\u6b7b\u5979\u7684\u5fc3 \u4e0a\u4eba\u5462\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u52a8\u5bb9\u9053\uff1a\u201c\u524d\u8f88\u77e5\u9053\u6211\u662f\u2026\u2026\u201d \u3000\u3000\u3002\u767d\u8863\u7f8e\u5987\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e16\u4e0a\u9664\u4e86\u695a\u7559\u9999\u5916\uff0c\u8fd8\u6709\u8c01\u80fd\u8d70\u5f97\u5230\u8fd9\u91cc\uff1f\u8fd8\u6709\u8c01\u6709\u8fd9\u9ebd \u5927\u7684\u80c6\u5b50\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u6df1\u6df1\u4e00\u793c\uff0c\u9053\uff1a\u201c\u665a\u8f88\u65e9\u5df2\u542c\u84c9\u513f\u8bf4\u8d77\u8fc7\u4f60\u8001\u4eba\u5bb6\u4e86\uff0c\u4eca\u65e5\u80fd\u89c1\u5230\u4f60\u8001\u4eba\u5bb6\uff0c\u5b9e \u5728\u662f\u665a\u8f88\u5929\u5927\u7684\u8fd0\u6c14\u3002\u201d \u3000\u3000\u767d\u8863\u7f8e\u5987\u9053\uff1a\u201c\u6211\u4e5f\u542c\u84c9\u513f\u8bf4\u8d77\u8fc7\u4f60\uff0c\u82e5\u4e0d\u662f\u4f60\uff0c\u84c9\u513f\u5df2\u4e0d\u77e5\u8981\u6d41\u843d\u5230\u4ec0\u9ebd\u5730\u6b65\u4e86\uff0c\u5c31 \u4e3a\u4e86\u62a5\u7b54\u4f60\u6b64\u756a\u6069\u60c5\uff0c\u6211\u4e5f\u4e0d\u80fd\u96be\u4e3a\u4f60\u3002\u201d \u3000\u3000\u5979\u56db\u4e0b\u671b\u4e86\u4e00\u773c\uff0c\u6309\u7740\u9053\uff1a\u201c\u5e78\u800c\u4eca\u5929\u662f\u6211\u5f53\u503c\uff0c\u522b\u4eba\u4e0d\u4f1a\u5230\u8fd9\u91cc\u6765\uff0c\u4f60\u5feb\u8d70\u5427\uff01\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u665a\u8f88\u65e2\u5df2\u5230\u4e86\u8fd9\u91cc\uff0c\u597d\u6b79\u4e5f\u8981\u89c1\u9634\u5bab\u4e3b\u4e00\u9762\u3002\u201d \u3000\u3000\u767d\u8863\u7f8e\u5987\u6c88\u4e0b\u4e86\u8138\uff0c\u5389\u58f0\u9053\uff1a\u201c\u4f60\u6c38\u8fdc\u4e5f\u89c1\u4e0d\u7740\u5979\u7684\uff0c\u9664\u975e\u4f60\u5b9a\u8981\u6b7b\u5728\u8fd9\u91cc\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u8eac\u8eab\u9053\uff1a\u201c\u53ea\u6c42\u4f60\u8001\u4eba\u5bb6\u6307\u70b9\u4e00\u6761\u660e\u8def\uff0c\u665a\u8f88\u5c31\u5df2\u611f\u6fc0\u4e0d\u5c3d\uff0c\u522b\u7684\u4e8b\uff0c\u665a\u8f88\u518d\u4e5f \u4e0d\u6562\u6765\u9ebb\u70e6\u4f60\u8001\u4eba\u5bb6\u4e86\u3002\u201d \u3000\u3000\u767d\u8863\u7f8e\u5987\u6839\u672c\u4e0d\u7406\u4ed6\uff0c\u53ea\u662f\u6325\u624b\u9053\uff1a\u201c\u5feb\u8d70\uff0c\u518d\u8fdf\u5c31\u6765\u4e0d\u53ca\u4e86\uff0c\u5feb\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u4e5f\u597d\u50cf\u542c\u4e0d\u61c2\u5979\u7684\u8bdd\uff0c\u8fd8\u662f\u8eac\u8eab\u9053\uff1a\u201c\u665a\u8f88\u77e5\u9053\u8fd9\u91cc\u6709\u4e00\u6761\u79d8\u9053\u2026\u2026\u201d \u3000\u3000\u767d\u8863\u7f8e\u5987\u53d8\u8272\u9053\uff1a\u201c\u79d8\u9053\uff1f\u4ec0\u9ebd\u79d8\u9053\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u89c1\u5979\u4e00\u542c\u5230\u201c\u79d8\u9053\u201d\u4e24\u5b57\uff0c\u795e\u60c5\u5c31\u7acb\u523b\u4e3a\u4e4b\u5927\u53d8\uff0c\u7531\u6b64\u53ef\u89c1\uff0c\u8fd9\u79d8\u9053\u7684\u5173\u7cfb\u5fc5\u5b9a \u5f88\u5927\u3002 \u3000\u3000\u4ed6\u66f4\u4e0d\u80af\u8d70\u4e86\uff0c\u966a\u7740\u7b11\u9053\uff1a\u201c\u6b64\u95f4\u82e5\u65e0\u79d8\u9053\uff0c\u4f60\u8001\u4eba\u5bb6\u662f\u4ece\u90a3\u91cc\u8d70\u51fa\u6765\u7684\u5462\uff1f\u201d \u3000\u3000\u767d\u8863\u7f8e\u5987\u6012\u9053\uff1a\u201c\u4f60\u96be\u9053\u771f\u6d3b\u5f97\u4e0d\u8010\u70e6\u4e86\u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u8001\u4eba\u5bb6\u82e5\u4e0d\u80af\u8bf4\uff0c\u665a\u8f88\u5c31\u53ea\u597d\u6b7b\u5728\u8fd9\u91cc\u4e86\u3002\u201d \u3000\u3000\u767d\u8863\u7f8e\u5987\u77aa\u7740\u4ed6\uff0c\u5979\u5b9e\u5728\u8fd8\u6ca1\u6709\u89c1\u8fc7\u8fd9\u6837\u7684\u7537\u4eba\u3002\u66f4\u60f3\u4e0d\u5230\u4e16\u4e0a\u7adf\u6709\u4eba\u5728\u8fd9\u79cd\u65f6\u5019\u8fd8\u80fd \u7b11\u5f97\u51fa\u6765\u3002 \u3000\u3000\u695a\u7559\u9999\u4e5f\u771f\u6c88\u5f97\u4f4f\u6c14\uff0c\u5979\u4e0d\u8bf4\u8bdd\uff0c\u4ed6\u5c31\u9759\u9759\u7684\u7b49\u7740\uff0c\u5c31\u5728\u8fd9\u65f6\uff0c\u90a3\u4f3c\u6709\u4f3c\u65e0\u7684\u60a0\u626c\u4e50\u58f0 \u5ffd\u7136\u53d8\u6025\uff0c\u5982\u96e8\u6253\u82ad\u8549\uff0c\u73e0\u843d\u7389\u76d8\uff0c\u94ee\u9535\u4e0d\u7edd\u3002 \u3000\u3000\u767d\u8863\u7f8e\u5987\u7684\u9762\u8272\u4e5f\u5ffd\u7136\u53d8\u4e86\uff0c\u6c88\u58f0\u9053\uff1a\u201c\u8fd8\u6709\u8c01\u548c\u4f60\u4e00\u9f50\u6765\u7684\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u5c31\u53ea\u665a\u8f88\u4e00\u4eba\uff0c\u5e76\u65e0\u2026\u2026\u201d \u3000\u3000\u767d\u8863\u7f8e\u5987\u9762\u5e26\u60ca\u60f6\u4e4b\u8272\uff0c\u622a\u53e3\u9053\uff1a\u201c\u4e50\u58f0\u793a\u8b66\uff0c\u5df2\u53c8\u6709\u5916\u4eba\u5165\u8c37\u800c\u6765\uff0c\u82e5\u975e\u4f60\u7684\u540c\u4f34\uff0c \u4f1a\u662f\u4ec0\u9ebd\u4eba\u5462\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u6697\u4e2d\u4e5f\u5403\u4e86\u4e00\u60ca\uff0c\u4ed6\u8fd9\u624d\u77e5\u9053\u795e\u6c34\u5bab\u679c\u7136\u662f\u8b66\u6212\u68ee\u4e25\uff0c\u7adf\u8fde\u90a3\u4ed9\u97f5\u822c\u7684\u4e50\u58f0\uff0c\u90fd \u662f\u5979\u4eec\u7684\u4f20\u8b66\u4e4b\u6cd5\u3002 \u3000\u3000\u767d\u8863\u7f8e\u5987\u4e00\u6b65\u63a0\u5230\u95e8\u53e3\uff0c\u56db\u4e0b\u77a7\u4e86\u4e00\u773c\uff0c\u53c8\u9000\u4e86\u56de\u6765\uff0c\u5389\u58f0\u9053\uff1a\u201c\u6b64\u523b\u4eba\u867d\u8fd8\u672a\u5230\uff0c\u4f46 \u8b66\u4e50\u4e00\u8d77\uff0c\u8c37\u4e2d\u5f1f\u5b50\u4fbf\u5df2\u5404\u5c31\u65b9\u4f4d\uff0c\u65e0\u8bba\u8c01\u53ea\u8981\u5165\u8c37\u4e00\u6b65\uff0c\u4fbf\u662f\u6709\u53bb\u65e0\u56de\u7684\u4e86\uff0c\u4f60\u4e3a\u4f55\u8fd8\u4e0d \u5feb\u8d70\uff0c\u8fd8\u7559\u5728\u8fd9\u91cc\uff0c\u96be\u9053\u5b9a\u8981\u8fde\u7d2f\u6211\u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u53f9\u9053\uff1a\u201c\u6b64\u8c37\u65e2\u5df2\u53d8\u4e3a\u6b7b\u8c37\uff0c\u6015\u8fde\u9e1f\u96c0\u4e5f\u96be\u98de\u6e21\uff0c\u5374\u53eb\u665a\u8f88\u8fc8\u5411\u4f55\u5904\u5462\uff1f\u201d \u3000\u3000\u767d\u8863\u7f8e\u5987\u53d8\u8272\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u4e0d\u59a8\u627e\u4e2a\u5730\u65b9\u5148\u8eb2\u4e00\u8eb2\uff0c\u7b49\u4e8b\u8fc7\u4e4b\u5f8c\uff0c\u6211\u518d\u8bbe\u6cd5\u5e26\u4f60\u51fa \u53bb\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u773c\u73e0\u5b50\u4e00\u8f6c\uff0c\u63c9\u7740\u9f3b\u5b50\u9053\uff1a\u201c\u665a\u8f88\u82e5\u662f\u968f\u610f\u4e71\u8d70\uff0c\u53ef\u80fd\u6b65\u6b65\u4ff1\u662f\u5371\u673a\uff0c\u665a\u8f88\u4e5f\u4e0d \u77e5\u8be5\u8eb2\u5230\u90a3\u91cc\uff0c\u9664\u975e\u524d\u8f88\u5c06\u90a3\u6761\u79d8\u9053\u793a\u77e5\uff0c\u8ba9\u665a\u8f88\u8eb2\u8fdb\u53bb\u3002\u201d \u3000\u3000\u767d\u8863\u7f8e\u5987\u987f\u811a\u9053\uff1a\u201c\u79d8\u9053\uff0c\u79d8\u9053\uff0c\u4f60\u5c31\u77e5\u9053\u8fd9\u91cc\u6709\u6761\u79d8\u9053\uff0c\u4f46\u4f60\u4e0d\u77e5\u9053\uff0c\u8fd9\u79d8\u9053\u7684\u67a2\u7ebd \u5c31\u5728\u5bab\u4e3b\u5bdd\u5ba4\u4e2d\uff0c\u53ea\u80fd\u7531\u91cc\u9762\u51fa\u6765\uff0c\u5916\u9762\u7684\u4eba\u6839\u672c\u65e0\u6cd5\u8fdb\u53bb\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u6014\u4e86\u6014\uff0c\u4e00\u9897\u5fc3\u5df2\u4e0d\u7981\u5f80\u4e0b\u6c88\u3002 \u3000\u3000\u8fd9\u65f6\u6025\u9aa4\u7684\u4e50\u58f0\u53c8\u5df2\u7f13\u6162\u4e0b\u6765\uff0c\u4f46\u695a\u7559\u9999\u5df2\u77e5\u9053\u8fd9\u7f13\u6162\u7684\u8282\u594f\u4e2d\uff0c\u6bcf\u4e00\u62cd\u90fd\u6f5c\u4f0f\u7740\u6740 \u673a\u3002 \u3000\u3000\u4ed6\u4e5f\u77e5\u9053\u8fd9\u767d\u8863\u7f8e\u5987\u7684\u60ca\u60f6\u7edd\u4e0d\u662f\u5047\u88c5\u51fa\u6765\u7684\uff0c\u795e\u6c34\u5bab\u4e3b\u82e5\u662f\u77e5\u9053\u5979\u5faa\u79c1\u901a\u654c\uff0c\u90a3\u5f8c\u679c \u5b9e\u662f\u4e0d\u582a\u8bbe\u60f3\u3002 \u3000\u3000\u65bc\u662f\u695a\u7559\u9999\u518d\u4e5f\u4e0d\u8bf4\u4ec0\u9ebd\uff0c\u53ea\u662f\u8eac\u8eab\u4e00\u63d6\uff0c\u9053\uff1a\u201c\u591a\u8c22\u524d\u8f88\u6307\u6559\u3002\u201d \u3000\u3000\u8bdd\u672a\u8bf4\u5b8c\uff0c\u4ed6\u5df2\u8f6c\u8eab\u63a0\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u767d\u8863\u7f8e\u5987\u4f3c\u4e4e\u8981\u8ffd\u51fa\u53bb\uff0c\u4f46\u53c8\u505c\u4f4f\u4e86\u811a\u6b65\uff0c\u5979\u7f8e\u4e3d\u773c\u775b\u91cc\uff0c\u5145\u6ee1\u4e86\u75db\u82e6\u4e4b\u8272\uff0c\u9eef\u7136\u9053\uff1a \u201c\u84c9\u513f\uff0c\u83ab\u8981\u602a\u6211\uff0c\u4e0d\u662f\u6211\u4e0d\u60f3\u6551\u4ed6\uff0c\u6211\u5b9e\u5728\u4e5f\u65e0\u80fd\u4e3a\u529b\u3002\u201d \u3000\u3000\u5979\u77e5\u9053\u695a\u7559\u9999\u6b64\u756a\u4e00\u51fa\u4e86\u8fd9\u5c3c\u5eb5\uff0c\u5c31\u5df2\u6b65\u5165\u6b7b\u4ea1\u4e86\u3002 \u3000\u3000\u591c\u8272\u5f88\u6df1\uff0c\u6bcf\u4e00\u4e2a\u5730\u65b9\u770b\u6765\u90fd\u5f77\u4f5b\u662f\u7edd\u597d\u7684\u85cf\u8eab\u4e4b\u5904\uff0c\u4f46\u695a\u7559\u9999\u5374\u77e5\u9053\u9ed1\u6697\u4e2d\u5230\u5904\u90fd\u53ef \u80fd\u9690\u85cf\u7740\u6740\u673a\uff0c\u6bcf\u4e00\u4e2a\u770b\u6765\u5f88\u79d8\u5bc6\u7684\u85cf\u8eab\u5904\uff0c\u90fd\u53ef\u80fd\u662f\u8bf1\u4eba\u7684\u9677\u9631\uff0c\u53ea\u8981\u4ed6\u5984\u8d70\u4e00\u6b65\uff0c\u5c31\u53ef \u80fd\u6b7b\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u4e5f\u7edd\u4e0d\u80fd\u5c31\u8fd9\u6837\u7ad9\u7740\u4e0d\u52a8\uff0c\u8fd9\u7f8e\u4e3d\u800c\u5e7d\u9759\u7684\u5c71\u8c37\uff0c\u7b80\u76f4\u5df2\u6ca1\u6709\u4ed6\u7acb\u8db3\u5bb9\u8eab\u4e4b\u5730\u3002 \u3000\u3000\u98ce\u5439\u6728\u53f6\uff0c\u4f3c\u4e4e\u6709\u8863\u8882\u5e26\u98ce\u58f0\u968f\u98ce\u800c\u6765\uff0c\u695a\u7559\u9999\u5ffd\u7136\u53d1\u89c9\u8fdc\u5904\u767d\u5f71\u4e00\u95ea\uff0c\u6b63\u662f\u63a0\u5230\u8fd9\u8fb9 \u6765\u7684\u3002 \u3000\u3000\u4ed6\u53ea\u8981\u518d\u7a0d\u6709\u8fdf\u7591\uff0c\u5c31\u7acb\u523b\u8981\u88ab\u4eba\u53d1\u73b0\u4e86\u3002 \u3000\u3000\u5728\u661f\u5149\u4e0b\u770b\u6765\uff0c\u5e73\u9759\u7684\u6e56\u6c34\u707f\u70c2\u5982\u94f6\u3002 \u3000\u3000\u695a\u7559\u9999\u5ffd\u7136\u5411\u6e56\u6c34\u4e2d\u6ed1\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u5e73\u9759\u7684\u6e56\u6c34\u53ea\u4e0d\u8fc7\u88ab\u6fc0\u8d77\u4e86\u4e2a\u5c0f\u5c0f\u7684\u6f29\u6da1\uff0c\u6f29\u6da1\u8fd8\u672b\u6d88\u5931\uff0c\u5df2\u6709\u4e00\u6761\u767d\u8863\u4eba\u5f71\u63a0\u4e86\u8fc7 \u6765\u3002 \u3000\u3000\u5979\u51e0\u4e4e\u548c\u5bab\u5357\u71d5\u540c\u6837\u7f8e\u4e3d\uff0c\u98de\u63a0\u7684\u59ff\u6001\u4e5f\u90a3\u9ebd\u52a8\u4eba\uff0c\u660e\u4eae\u7684\u773c\u6ce2\u56db\u4e0b\u4e00\u8f6c\uff0c\u76b1\u4e86\u76b1\u7709\uff0c \u8f7b\u5524\u9053\uff1a\u201c\u53c1\u59d0\u3002\u201d \u3000\u3000\u90a3\u767d\u8863\u7f8e\u5987\u7acb\u523b\u81ea\u5c3c\u5eb5\u4e2d\u8fce\u51fa\uff0c\u9053\uff1a\u201c\u4ec0\u9ebd\u4e8b\uff1f\u201d \u3000\u3000\u5c11\u5973\u9053\uff1a\u201c\u6211\u65b9\u89c1\u5230\u8fd9\u91cc\u597d\u50cf\u6709\u6761\u4eba\u7684\u5f71\u5b50\uff0c\u53c1\u59d0\u53ef\u66fe\u542c\u5230\u4ec0\u9ebd\u52a8\u9759\uff1f\u201d \u3000\u3000\u767d\u8863\u7f8e\u5987\u9053\uff1a\u201c\u6ca1\u6709\u5440\uff01\u201d \u3000\u3000\u5979\u7b11\u4e86\u7b11\uff0c\u53c8\u9053\uff1a\u201c\u8b66\u4e50\u65b9\u8d77\uff0c\u4eba\u5fc5\u5b9a\u8fd8\u672b\u5165\u8c37\uff0c\u600e\u4f1a\u5230\u4e86\u8fd9\u91cc\uff1f\u201d \u3000\u3000\u5c11\u5973\u76ee\u5149\u95ea\u52a8\uff0c\u5583\u5583\u9053\uff1a\u201c\u96be\u9053\u6211\u8fd8\u4f1a\u770b\u9519\u9ebd\uff1f\u8fd9\u5012\u602a\u4e86\u3002\u201d \u3000\u3000\u767d\u8863\u7f8e\u5987\u51b7\u7b11\u9053\uff1a\u201c\u4e5d\u59b9\u4f60\u7684\u4e00\u53cc\u591c\u773c\u867d\u7136\u5389\u5bb3\uff0c\u4f46\u6211\u4e5f\u4e0d\u662f\u778e\u5b50\u804b\u5b50\uff0c\u8fd9\u91cc\u82e5\u662f\u6709 \u4eba\uff0c\u6211\u600e\u9ebd\u4f1a\u4e00\u70b9\u52a8\u9759\u90fd\u4e0d\u77e5\u9053\uff1f\u201d \u3000\u3000\u5c11\u5973\u966a\u7b11\u9053\uff1a\u201c\u53c1\u59d0\u4f55\u5fc5\u52a8\u6c14\uff0c\u6211\u53ea\u4e0d\u8fc7\u662f\u968f\u4fbf\u95ee\u95ee\u800c\u5df2\u3002\u201d \u3000\u3000\u767d\u8863\u7f8e\u5987\u8fd9\u624d\u5c55\u989c\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5c0f\u5fc3\u4e9b\u603b\u662f\u597d\u7684\uff0c\u53ea\u4e0d\u8fc7\uff0c\u8fd9\u91cc\u65b9\u82e5\u771f\u6709\u4eba\uff0c\u73b0\u5728\u5230\u90a3 \u91cc\u53bb\u4e86\u5462\uff1f\u96be\u9053\u4ed6\u8fd8\u4f1a\u9690\u8eab\u6cd5\u4e0d\u6210\uff1f\u201d \u3000\u3000\u5c11\u5973\u7b11\u9053\uff1a\u201c\u662f\u5440\uff1a\u4ed6\u9664\u975e\u8dc3\u5165\u795e\u6e56\uff0c\u5426\u5219\u65e0\u8bba\u8eb2\u5230\u90a3\u91cc\u90fd\u8981\u89e6\u52a8\u8b66\u8baf\uff0c\u53ef\u662f\uff0c\u4ed6\u82e5\u771f \u7684\u6562\u8dc3\u5165\u795e\u6e56\uff0c\u4e5f\u96be\u514d\u8981\u53d1\u51fa\u4e9b\u58f0\u97f3\uff0c\u9664\u975e\u4ed6\u662f\u6761\u9ca4\u9c7c\u7cbe\u3002\u201d \u3000\u3000\u5979\u7b11\u7740\u5411\u90a3\u767d\u8863\u7f8e\u5987\u6446\u4e86\u6446\u624b\uff0c\u53c8\u9053\uff1a\u201c\u5ba2\u4eba\u6015\u5df2\u5feb\u5230\u4e86\uff0c\u6211\u4eec\u518d\u5230\u522b\u5904\u53bb\u770b\u770b\uff0c\u53c1\u59d0 \u4f60\u4e5f\u5f00\u59cb\u51c6\u5907\u5427\uff01\u4eba\u5bb6\u65e2\u7136\u6562\u5230\u8fd9\u91cc\u6765\uff0c\u6211\u4eec\u603b\u4e0d\u80fd\u8ba9\u4eba\u5bb6\u5931\u671b\u3002\u201d \u3000\u3000\u53ea\u89c1\u5979\u98de\u4ed9\u822c\u81ea\u94f6\u6e56\u4e0a\u63a0\u8fc7\uff0c\u8f6c\u77ac\u4fbf\u5df2\u4e0d\u89c1\u3002 \u3000\u3000\u767d\u8863\u7f8e\u5987\u671b\u7740\u6e56\u6c34\u5446\u679c\u7684\u51fa\u4e86\u534a\u664c\u795e\uff0c\u5583\u5583\u9053\uff1a\u201c\u6b7b\u91cc\u9003\u751f\uff0c\u7b97\u4f60\u8d70\u8fd0\uff0c\u5371\u673a\u72b9\u5728\uff0c\u5c0f \u5fc3\u5c0f\u5fc3\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u6f5c\u5165\u6c34\u5e95\uff0c\u5fc3\u8fd8\u662f\u8df3\u5f97\u5f88\u5389\u5bb3\u3002 \u3000\u3000\u5728\u65b9\u90a3\u4e00\u77ac\u4e4b\u95f4\uff0c\u4ed6\u7684\u751f\u4e0e\u6b7b\u5c31\u51e0\u4e4e\u5df2\u6ca1\u6709\u8ddd\u79bb\uff0c\u4f46\u73b0\u5728\u5df2\u5b89\u5168\u4e86\uff0c\u81f3\u5c11\u6682\u65f6\u662f\u5b89\u5168 \u7684\u3002 \u3000\u3000\u6e56\u6c34\u51fa\u5947\u7684\u6e05\u6f88\uff0c\u5c31\u50cf\u662f\u4e00\u5927\u5757\u900f\u660e\u7684\u6c34\u6676\uff0c\u5929\u4e0a\u7684\u661f\u5149\u6708\u8272\uff0c\u51e0\u4e4e\u53ef\u4ee5\u7b14\u76f4\u7167\u5165\u6e56 \u5e95\u3002 \u3000\u3000\u6e56\u5e95\u94fa\u7740\u96ea\u767d\u7684\u6c99\u5b50\uff0c\u4e5f\u5728\u95ea\u95ea\u53d1\u5149\u3002 \u3000\u3000\u695a\u7559\u9999\u5728\u6c34\u5e95\uff0c\u7b80\u76f4\u5c31\u548c\u5728\u7a7a\u6c14\u4e2d\u4e00\u6837\u81ea\u7531\u3002 \u3000\u3000\u6d77\u6d0b\u3001\u6c5f\u6cb3\u3001\u6e56\u6cca\u3001\u6c60\u5858\uff0c\u751a\u81f3\u9752\u6d77\u7684\u76d0\u6c34\u6e56\u3001\u6c5f\u5357\u7684\u6d4a\u6c34\u6eaa\uff0c\u5bf9\u6bcf\u4e00\u79cd\u6c34\u6027\uff0c\u4ed6\u90fd\u719f \u6089\u5f97\u5982\u89c2\u638c\u6307\u3002 \u3000\u3000\u6c34\u5e95\u4e0b\u7684\u5947\u5999\u4e16\u754c\uff0c\u6b63\u662f\u4ed6\u8877\u5fc3\u70ed\u7231\u7684\u3002 \u3000\u3000\u6c34\u4e0b\u6bcf\u4e00\u79cd\u751f\u7269\uff0c\u90fd\u50cf\u662f\u4ed6\u7684\u597d\u670b\u53cb\uff0c\u4ed6\u53ef\u4ee5\u968f\u65f6\u5524\u51fa\u5979\u4eec\u7684\u540d\u5b57\u3002 \u3000\u3000\u4f46\u6b64\u523b\uff0c\u4ed6\u5fc3\u91cc\u5374\u6709\u79cd\u4e0d\u5b89\u7684\u611f\u89c9\u3002 \u3000\u3000\u8fd9\u7f8e\u4e3d\u7684\u5c0f\u6e56\uff0c\u7adf\u662f\u4e2a\u6b7b\u6e56\uff0c\u6c34\u9762\u4e0b\u7adf\u6ca1\u6709\u4efb\u4f55\u751f\u7269\uff0c\u6ca1\u6709\u9c7c\u867e\uff0c\u6ca1\u6709\u868c\u86e4\uff0c\u751a\u81f3\u8fde\u6c34 \u8349\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u695a\u7559\u9999\u89c9\u5f97\u81ea\u5df1\u5c31\u5f77\u4f5b\u5230\u4e86\u4e00\u4e2a\u5b8c\u5168\u964c\u751f\u7684\u57ce\u5e02\uff0c\u8fd9\u57ce\u5e02\u867d\u7136\u6574\u9f50\u800c\u6d01\u51c0\uff0c\u5374\u8fde\u4e00\u4e2a\u4eba \u4e5f\u6ca1\u6709\u3002 \u3000\u3000\u5c0f\u6e56\u7684\u56db\u5468\uff0c\u90fd\u5806\u780c\u7740\u5de8\u5927\u800c\u7f8e\u4e3d\u7684\u9752\u767d\u77f3\u5757\uff0c\u7011\u5e03\u843d\u5728\u6c34\u9762\uff0c\u5728\u6c34\u5e95\u6fc0\u51fa\u4e86\u4e00\u4e32\u4e32\u73cd \u73e0\u7684\u6ce1\u6cab\u3002 \u7b2c\u4e8c\u5341\u4e03\u7ae0\u3000\u6c34\u6bcd\u9634\u59ec>> \u53e4\u9f99\u300a\u695a\u7559\u9999\u7cfb\u5217\xb7\u753b\u7709\u9e1f\u300b \u7b2c\u4e8c\u5341\u4e03\u7ae0\u3000\u6c34\u6bcd\u9634\u59ec \u3000\u3000\u82e5\u662f\u6362\u4e86\u522b\u4eba\uff0c\u6f5c\u4f0f\u5728\u5982\u6b64\u7f8e\u4e3d\u800c\u5e73\u9759\u7684\u6e56\u6c34\u4e2d\uff0c\u4e00\u5b9a\u8981\u4ee5\u4e3a\u81ea\u5df1\u662f\u7edd\u5bf9\u5b89\u5168\u7684\u4e86\u3002 \u3000\u3000\u4f46\u695a\u7559\u9999\u603b\u89c9\u5f97\u8fd9\u5730\u65b9\u6709\u70b9\u4e0d\u5bf9\uff0c\u76f4\u5230\u4ed6\u5728\u5de8\u5927\u7684\u77f3\u5757\u4e0e\u77f3\u5757\u95f4\uff0c\u627e\u5230\u4e86\u4e00\u4e2a\u5f88\u9690\u5bc6\u7684 \u85cf\u8eab\u5904\uff0c\u4ed6\u7684\u5fc3\u624d\u7b97\u8d70\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u7136\u5f8c\uff0c\u4ed6\u5c31\u7acb\u523b\u60f3\u8d77\u4e86\u4e24\u4ef6\u5947\u602a\u7684\u4e8b\u3002 \u3000\u3000\u8fd9\u91cc\u7684\u79d8\u9053\u65e2\u7136\u53ea\u80fd\u51fa\uff0c\u4e0d\u80fd\u5165\uff0c\u90a3\u9ebd\u201c\u6c34\u6bcd\u201d\u9634\u59ec\u5efa\u9020\u8fd9\u4e9b\u79d8\u9053\uff0c\u7a76\u7adf\u662f\u4e3a\u4e86\u4ec0\u9ebd\uff1f \u3000\u3000\u73b0\u5728\u53c8\u6709\u4eba\u4fb5\u5165\u4e86\u795e\u6c34\u5bab\uff0c\u6765\u7684\u4eba\u4f1a\u662f\u8c01\u5462\uff1f \u3000\u3000\u4ed6\u7684\u8eab\u5b50\u521a\u597d\u5d4c\u5728\u4e24\u5757\u5de8\u77f3\u95f4\uff0c\u8fd9\u4e24\u5757\u5de8\u77f3\u90fd\u6709\u4e00\u622a\u9732\u51fa\u6c34\u9762\uff0c\u695a\u7559\u9999\u5fcd\u4e0d\u4f4f\u4e5f\u4f38\u51fa\u5934 \u53bb\u3002 \u3000\u3000\u4ed6\u6b6a\u7740\u5934\uff0c\u53ea\u9732\u51fa\u4e00\u53ea\u773c\u775b\uff0c\u4e24\u5757\u5de8\u77f3\u7684\u9634\u5f71\u6070\u5de7\u63a9\u62a4\u7740\u4ed6\uff0c\u4ed6\u89c9\u5f97\u8fd9\u5730\u52bf\u5f88\u597d\uff0c\u7edd\u4e0d \u4f1a\u88ab\u4eba\u53d1\u73b0\u3002 \u3000\u3000\u4ed6\u5b9e\u5728\u60f3\u770b\u770b\u8fd9\u6709\u52c7\u6c14\u5192\u9669\u4fb5\u5165\u795e\u6c34\u5bab\u7684\u4eba\u662f\u8c01\u3002 \u3000\u3000\u5c71\u8c37\u4e2d\u8fd8\u662f\u5f88\u5e73\u9759\uff0c\u4ece\u6c34\u5e95\u4e0b\u9732\u51fa\u534a\u8fb9\u8138\u6765\u770b\u8fd9\u5c71\u8c37\uff0c\u90a3\u611f\u89c9\u53c8\u548c\u81ea\u5df1\u7f6e\u8eab\u5728\u8c37\u4e2d\u65f6\u4e0d \u540c\u4e86\u3002 \u3000\u3000\u6240\u6709\u7684\u666f\u7269\u90fd\u50cf\u662f\u66f4\u9065\u8fdc\uff0c\u66f4\u6726\u80e7\uff0c\u5b8c\u5168\u4e0d\u50cf\u662f\u771f\u5b9e\u7684\uff0c\u53ea\u50cf\u662f\u4e00\u5e45\u56fe\u753b\uff0c\u4e00\u4e2a\u68a6\u2026\u2026 \u3000\u3000\u4f46\u695a\u7559\u9999\u5e76\u6ca1\u6709\u5fc3\u60c5\u6765\u6b23\u8d4f\u8fd9\u68a6\u822c\u6726\u80e7\u7684\u7f8e\u666f\uff0c\u4ed6\u53ea\u662f\u7559\u610f\u7740\u9ed1\u6697\u4e2d\u90a3\u4e9b\u6700\u5e7d\u79d8\u7684\u5730 \u65b9\u3002 \u3000\u3000\u4ed6\u8fd8\u662f\u77a7\u4e0d\u89c1\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u4ed6\u53d1\u73b0\u53c1\u6761\u4eba\u5f71\u7bad\u4e00\u822c\u81ea\u8fdc\u65b9\u5c71\u8c37\u7684\u5165\u53e3\u5904\u7a9c\u4e86\u51fa\u6765\uff0c\u53c1\u4e2a\u4eba\u7684\u8f7b\u529f\u90fd\u662f\u7b2c \u4e00\u6d41\u7684\u8eab\u624b\u3002 \u3000\u3000\u8fd9\u53c1\u4eba\u4f3c\u4e4e\u5e76\u4e0d\u60f3\u9690\u85cf\u81ea\u5df1\u7684\u8eab\u5f62\uff0c\u4eba\u8c37\u4e4b\u5f8c\uff0c\u7acb\u523b\u5c31\u5c55\u52a8\u8eab\u6cd5\uff0c\u5411\u7011\u5e03\u8fd9\u8fb9\u6251\u4e86\u8fc7 \u53bb\u3002 \u3000\u3000\u661f\u5149\u4e0b\u77a7\u7740\u4ed6\u4eec\u7684\u8eab\u5f62\uff0c\u4ed6\u4eec\u7684\u8138\u5728\u6708\u8272\u4e2d\u4e00\u95ea\uff0c\u695a\u7559\u9999\u9aa4\u7136\u5403\u4e86\u4e00\u60ca\uff0c\u51e0\u4e4e\u5c06\u4e00\u53e3\u6e56 \u6c34\u90fd\u541e\u4e0b\u809a\u53bb\u3002 \u3000\u3000\u8fd9\u53c1\u4eba\u7adf\u662f\u9ec4\u9c81\u76f4\u3001\u80e1\u94c1\u5316\u548c\u6234\u72ec\u884c\u3002 \u3000\u3000\u4e5f\u5c31\u5728\u8fd9\u65f6\uff0c\u56db\u9762\u5ffd\u7136\u51fa\u73b0\u4e86\u5341\u9980\u6761\u767d\u8863\u4eba\u5f71\uff0c\u6709\u7684\u7ad9\u5728\u6811\u68a2\uff0c\u6709\u7684\u968f\u98ce\u98d8\u8361\uff0c\u5c31\u50cf\u662f \u4e00\u7fa4\u9ed1\u591c\u7684\u5e7d\u7075\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u3001\u9ec4\u9c81\u76f4\u548c\u6234\u72ec\u884c\u4e5f\u4f3c\u5403\u4e86\u4e00\u60ca\uff0c\u8eab\u5f62\u6025\u907d\u7684\u81ea\u534a\u7a7a\u4e2d\u4e0b\u964d\uff0c\u540c\u65f6\u843d\u5728\u6e56\u7554\u7684\u4e00 \u5757\u77f3\u5757\u4e0a\u3002 \u3000\u3000\u53c1\u4e2a\u4eba\u80cc\u5bf9\u7740\u80cc\uff0c\u51dd\u795e\u5f85\u654c\u3002 \u3000\u3000\u4f46\u90a3\u4e9b\u767d\u8863\u4eba\u5e76\u6ca1\u5411\u4ed6\u4eec\u6251\u8fc7\u6765\uff0c\u53ea\u662f\u8fdc\u8fdc\u7684\u7ad9\u7740\uff0c\u9759\u9759\u7684\u671b\u7740\u4ed6\u4eec\uff0c\u5f02\u6837\u7684\u6c88\u9759\uff0c\u4ee4 \u4eba\u7a92\u606f\u3002 \u3000\u3000\u5230\u5f8c\u6765\u8fd8\u662f\u80e1\u94c1\u82b1\u618b\u4e0d\u4f4f\u4e86\uff0c\u5927\u58f0\u9053\uff1a\u201c\u8fd9\u5730\u65b9\u5c31\u662f\u795e\u6c34\u5bab\uff1f\u201d \u3000\u3000\u8fdc\u5904\u4e5f\u4e0d\u77e5\u662f\u8c01\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u4eec\u65e2\u7136\u6765\u4e86\uff0c\u8fd8\u4f1a\u4e0d\u77e5\u9053\u8fd9\u662f\u4ec0\u9ebd\u5730\u65b9\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u6253\u4e86\u4e2a\u54c8\u54c8\uff0c\u9053\uff1a\u201c\u521d\u6b21\u4e0a\u95e8\u7684\u4eba\uff0c\u81ea\u7136\u8981\u5148\u95ee\u95ee\u662f\u5426\u627e\u5bf9\u4e86\u5730\u65b9\u3002\u201d \u3000\u3000\u4e00\u4eba\u9053\uff1a\u201c\u4f60\u627e\u5bf9\u4e86\u3002\u201d \u3000\u3000\u53e6\u4e00\u4eba\u9053\uff1a\u201c\u53c1\u662f\u4ece\u90a3\u91cc\u6765\u7684\uff1f\u6709\u4f55\u89c1\u6559\uff1f\u201d \u3000\u3000\u8fd9\u4eba\u7684\u58f0\u97f3\u6bd4\u8f83\u6e29\u548c\uff0c\u4e5f\u6bd4\u8f83\u6709\u793c\uff0c\u695a\u7559\u9999\u5df2\u542c\u51fa\u5979\u5c31\u662f\u65b9\u5728\u5c3c\u5eb5\u4e2d\u63a9\u62a4\u8fc7\u4ed6\u7684\u767d\u8863\u7f8e \u5987\u4eba\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u4f3c\u4e4e\u8fd8\u5728\u72b9\u7591\uff0c\u9ec4\u9c81\u76f4\u5df2\u6717\u58f0\u9053\uff1a\u201c\u5728\u4e0b\u67f3\u5dde\u9ec4\u9c81\u76f4\uff0c\u8fd9\u4f4d\u662f\u4e10\u5e2e\u7684\u524d\u8f88\u6234\u72ec\u884c \u6234\u8001\u7237\u5b50\uff0c\u8fd8\u6709\u4e00\u4f4d\u5c31\u662f\u540d\u6ee1\u5929\u4e0b\u7684\u80e1\u94c1\u82b1\u3002\u201d \u3000\u3000\u4ed6\u4e00\u9762\u8bf4\uff0c\u695a\u7559\u9999\u4e00\u9762\u5728\u6697\u4e2d\u82e6\u7b11\uff1a\u201c\u6b64\u4eba\u679c\u7136\u4e0d\u6127\u4e3a\u541b\u5b50\uff0c\u53e5\u53e5\u90fd\u662f\u8bf4\u7684\u8001\u5b9e\u8bdd\u3002\u201d \u3000\u3000\u9ec4\u9c81\u76f4\u3001\u6234\u72ec\u884c\u3001\u80e1\u94c1\u82b1\uff0c\u8fd9\u53c1\u4eba\u53ef\u8bf4\u90fd\u662f\u53f1\u5412\u98ce\u4e91\uff0c\u540d\u9707\u6b66\u6797\u7684\u5927\u4eba\u7269\uff0c\u53ef\u8bf4\u662f\u201c\u8dfa \u8dfa\u811a\u56db\u57ce\u4e71\u5934\u201d\u7684\u8c6a\u6770\u3002 \u3000\u3000\u4f46\u795e\u6c34\u5bab\u7684\u5f1f\u5b50\u542c\u5230\u4ed6\u4eec\u7684\u540d\u5b57\uff0c\u5374\u8fde\u4e00\u70b9\u53cd\u5e94\u4e5f\u6ca1\u6709\uff0c\u90a3\u767d\u8863\u7f8e\u5987\u53ea\u662f\u6c88\u6c88\u201c\u54e6\u201d\u4e86 \u4e00\u58f0\u9053\uff1a\u201c\u5f88\u597d\uff0c\u53c1\u4f4d\u5c31\u8bf7\u629b\u4e0b\u5175\u5203\uff0c\u542c\u5019\u53d1\u843d\u5427\uff01\u201d \u3000\u3000\u80e1\u94c1\u82b1\u4ef0\u5929\u5927\u7b11\u4e86\u8d77\u6765\uff0c\u9053\uff1a\u201c\u629b\u4e0b\u5175\u5203\uff0c\u542c\u5019\u53d1\u843d\uff1f\u4f60\u8bf4\u7684\u662f\u4ec0\u9ebd\u8bdd\uff1f\u6211\u5b9e\u5728\u542c\u4e0d \u61c2\u3002\u201d \u3000\u3000\u767d\u8863\u7f8e\u5987\u76b1\u4e86\u76b1\u7709\uff0c\u8f7b\u53f9\u9053\uff1a\u201c\u877c\u8681\u5c1a\u4e14\u751f\uff0c\u4f60\u4eec\u4f55\u5fc5\u4e00\u5fc3\u6c42\u6b7b\uff1f\u201d \u3000\u3000\u9ec4\u9c81\u76f4\u50cf\u662f\u751f\u6015\u80e1\u94c1\u82b1\u53c8\u51fa\u8a00\u4e0d\u900a\uff0c\u8d76\u7d27\u62b1\u62f3\u9053\uff1a\u201c\u5728\u4e0b\u7b49\u6765\u6b64\u5e76\u65e0\u6076\u610f\uff0c\u53ea\u4e0d\u8fc7\u6765\u627e \u4e24\u4e2a\u670b\u53cb\u3002\u201d \u3000\u3000\u767d\u8863\u7f8e\u5987\u5389\u58f0\u9053\uff1a\u201c\u670b\u53cb\uff1f\u4f60\u53ef\u77e5\u9053\u8fd9\u91cc\u662f\u4ec0\u9ebd\u5730\u65b9\uff1f\u90a3\u91cc\u6709\u4f60\u4eec\u7684\u670b\u53cb\uff1f\u201d \u3000\u3000\u9ec4\u9c81\u76f4\u9053\uff1a\u201c\u4ed6\u4eec\u81ea\u7136\u4e0d\u662f\u8d35\u5bab\u5f1f\u5b50\uff0c\u53ea\u4e0d\u8fc7\u662f\u2026\u2026\u201d \u3000\u3000\u767d\u8863\u7f8e\u5987\u9762\u8272\u53c8\u53d8\u4e86\u53d8\uff0c\u622a\u53e3\u9053\uff1a\u201c\u8fd9\u91cc\u7edd\u6ca1\u6709\u5916\u6765\u7684\u4eba\uff0c\u666e\u5929\u4e4b\u4e0b\uff0c\u8c01\u4e5f\u6ca1\u6709\u4f60\u4eec\u8fd9 \u9ebd\u5927\u7684\u80c6\u5b50\uff0c\u6562\u8d81\u591c\u95f4\u5165\u795e\u6c34\u5bab\u3002\u201d \u3000\u3000\u9ec4\u9c81\u76f4\u548c\u80e1\u94c1\u82b1\u5bf9\u671b\u4e86\u4e00\u773c\uff0c\u8138\u8272\u90fd\u5f88\u6c88\u91cd\u3002 \u3000\u3000\u9ec4\u9c81\u76f4\u6c88\u58f0\u9053\uff1a\u201c\u4ed6\u4eec\u4e5f\u8bb8\u5e76\u6ca1\u6709\u6765\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u4ee5\u4e3a\u4ed6\u4eec\u4e5f\u548c\u4f60\u4e00\u6837\u90fd\u662f\u541b\u5b50\uff0c\u8bf4\u7684\u90fd\u662f\u8001\u5b9e\u8bdd\uff1f\u201d \u3000\u3000\u65b9\u5728\u6e56\u8fb9\u5de1\u5f0b\u7684\u5c11\u5973\u5ffd\u7136\u4e00\u63a0\u800c\u51fa\uff0c\u5389\u58f0\u9053\uff1a\u201c\u4f60\u4eec\u5df2\u662f\u5c06\u6b7b\u7684\u4eba\u4e86\uff0c\u6211\u4eec\u6839\u672c\u7528\u4e0d\u7740 \u518d\u8ddf\u4f60\u4eec\u8bf4\u8bdd\u3002\u201d \u3000\u3000\u9ec4\u9c81\u76f4\u8fd8\u672b\u5f00\u53e3\uff0c\u6234\u72ec\u884c\u5df2\u6012\u559d\u9053\u2026\u201c\u6211\u8001\u4eba\u5bb6\u4e5f\u6839\u672c\u61d2\u5f97\u8ddf\u4f60\u4eec\u8bf4\u8bdd\uff0c\u5feb\u53bb\u53eb\u201c\u6c34 \u6bcd\u201d\u9634\u59ec\u51fa\u6765\u5427\uff01\u201d \u3000\u3000\u90a3\u5c11\u5973\u51b7\u51b7\u9053\uff1a\u201c\u597d\uff0c\u4f60\u4eec\u4e00\u6b7b\uff0c\u6211\u5c31\u5e26\u4f60\u4eec\u53bb\u89c1\u5979\u8001\u4eba\u5bb6\u3002\u201d \u3000\u3000\u5979\u8bdd\u8fd8\u672b\u8bf4\u5b8c\uff0c\u695a\u7559\u9999\u5df2\u77e5\u9053\u662f\u975e\u6253\u8d77\u6765\u4e0d\u53ef\u7684\u4e86\uff0c\u56e0\u4e3a\u522b\u4eba\u4e5f\u8bb8\u4f1a\u53d7\u201c\u795e\u6c34\u5bab\u201d\u7684 \u6c14\uff0c\u4f46\u80e1\u94c1\u82b1\u5374\u662f\u8c01\u7684\u6c14\u4e5f\u4e0d\u53d7\u7684\u3002 \u3000\u3000\u4e19\u7136\u5979\u7684\u8bdd\u521a\u8bf4\u5b8c\uff0c\u5df2\u54cd\u8d77\u4e24\u58f0\u6012\u53f1\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u548c\u6234\u72ec\u884c\u7bad\u4e00\u822c\u76f4\u7a9c\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u6234\u72ec\u884c\u638c\u4e2d\u5175\u5203\u53ea\u4e0d\u8fc7\u662f\u6761\u9ed1\u9edd\u9edd\u7684\u77ed\u68d2\uff0c\u4e10\u5e2e\u5f1f\u5b50\u884c\u8d70\u6c5f\u6e56\u65f6\uff0c\u9664\u4e86\u8fd9\u6761\u6253\u72d7\u68d2\u5916\uff0c \u7edd\u4e0d\u8bb8\u518d\u5e26\u5176\u4ed6\u5175\u5203\u3002 \u3000\u3000\u8fd9\u662f\u4e10\u5e2e\u5386\u4ee3\u76f8\u4f20\u7684\u5e2e\u89c4\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u81ea\u547d\u53cc\u638c\u65e0\u654c\uff0c\u5bf9\u654c\u65f6\u5e73\u751f\u4ece\u4e0d\u7528\u5175\u5203\uff0c\u4f46\u6b64\u523b\u5374\u4e0d\u77e5\u4ece\u90a3\u91cc\u5f04\u6765\u4e86\u4e00\u67c4\u647a\u94c1 \u5200\u3002 \u3000\u3000\u8fd9\u67c4\u5200\u4ed6\u4e00\u76f4\u9690\u5728\u8098\u5f8c\uff0c\u6b64\u523b\u5200\u5149\u4e00\u95ea\uff0c\u201c\u516b\u65b9\u98ce\u96e8\u201d\u7adf\u662f\u864e\u864e\u751f\u5a01\uff0c\u7edd\u4e0d\u5728\u6b66\u6797\u4efb\u4f55 \u4e00\u4f4d\u4f7f\u5200\u7684\u540d\u5bb6\u4e4b\u4e0b\u3002 \u3000\u3000\u695a\u7559\u9999\u77e5\u9053\u4ed6\u8fd9\u662f\u5b58\u5fc3\u4ee5\u5a01\u70c8\u521a\u731b\u7684\u5200\u6cd5\uff0c\u6765\u514b\u5236\u201c\u795e\u6c34\u5bab\u201d\u5982\u884c\u4e91\u6d41\u6c34\u822c\u4ee5\u9634\u67d4\u89c1\u957f \u7684\u6b66\u529f\u3002 \u3000\u3000\u767d\u8863\u7f8e\u5987\u6012\u559d\u9053\uff1a\u201c\u4e8c\u5341\u5e74\u6765\uff0c\u4ece\u6765\u4e5f\u6ca1\u6709\u4eba\u6562\u5728\u6b64\u5730\u52a8\u6b66\uff0c\u4f60\u4eec\u7684\u80c6\u5b50\u5012\u771f\u4e0d\u5c0f\u3002\u201d \u3000\u3000\u559d\u58f0\u4e2d\uff0c\u5df2\u6709\u4e03\u516b\u4e2a\u767d\u8863\u5973\u5206\u522b\u5411\u80e1\u94c1\u5316\u548c\u6234\u72ec\u884c\u8fce\u4e86\u4e0a\u53bb\uff0c\u5979\u4eec\u7684\u8eab\u6cd5\u679c\u7136\u65e0\u4e00\u4e0d\u662f \u8f7b\u67d4\u66fc\u5999\uff0c\u8d85\u7fa4\u7edd\u4fd7\u3002 \u3000\u3000\u9ec4\u9c81\u76f4\u5927\u53eb\u9053\uff1a\u201c\u6709\u8bdd\u597d\u8bf4\uff0c\u4f55\u5fc5\u52a8\u624b\u3002\u201d \u3000\u3000\u4f46\u4ed6\u7684\u8bdd\u8fd8\u672b\u8bf4\u5b8c\uff0c\u5df2\u6709\u53c1\u56db\u4eba\u5c06\u4ed6\u56f4\u4f4f\uff0c\u638c\u5f71\u5982\u8774\u8776\u7ffb\u98de\uff0c\u56db\u9762\u516b\u65b9\u7684\u5411\u4ed6\u62cd\u4e86\u8fc7 \u6765\u3002 \u3000\u3000\u9ec4\u9c81\u76f4\u53f9\u4e86\u53e3\u6c14\uff0c\u53cd\u624b\u4e00\u64a4\uff0c\u201c\u545b\u201d\u9f99\u541f\uff0c\u4e00\u67c4\u7cbe\u5149\u8000\u76ee\u7684\u957f\u5251\u51fa\u9798\uff0c\u5316\u4f5c\u4e86\u4e00\u9053\u98de \u8679\u3002 \u3000\u3000\u4ed6\u5251\u6cd5\u867d\u6c88\u7a33\u539a\u91cd\uff0c\u4e0d\u5931\u201c\u541b\u5b50\u201d\u4e4b\u98ce\uff0c\u4f46\u62db\u5f0f\u4e4b\u82e5\u8fa3\uff0c\u529f\u529b\u4e4b\u6df1\u539a\uff0c\u679c\u7136\u4e0d\u6127\u4e3a\u4e00\u4ee3 \u5251\u6cd5\u5b97\u5320\u3002 \u3000\u3000\u8fdc\u5904\u7684\u4e50\u58f0\u53c8\u8f6c\u6025\uff0c\u4f3c\u5df2\u89c9\u51fa\u6765\u7684\u8fd9\u53c1\u4eba\u4e0d\u597d\u5bf9\u4ed8\uff0c\u6025\u9aa4\u7684\u4e50\u58f0\u4e2d\uff0c\u5251\u6c14\u5200\u5149\u5df2\u5f25\u6f2b\u4e86 \u6574\u4e2a\u5c71\u8c37\u3002 \u3000\u3000\u5bf9\u4ed8\u80e1\u94c1\u82b1\u7684\u56db\u4eba\u663e\u7136\u6700\u5403\u529b\uff0c\u56e0\u4e3a\u9ec4\u9c81\u76f4\u548c\u6234\u89e6\u884c\u81ea\u6043\u5e74\u7eaa\u548c\u8eab\u4efd\uff0c\u8fd8\u4e0d\u80af\u51fa\u624b\u592a \u72e0\u3002 \u3000\u3000\u4f46\u80e1\u94c1\u82b1\u5fc3\u91cc\u60e6\u8bb0\u7740\u695a\u7559\u9999\u7684\u5b89\u5371\uff0c\u4e00\u5fc3\u60f3\u5c06\u795e\u6c34\u5bab\u7684\u5f1f\u5b50\u5168\u90fd\u6253\u5012\uff0c\u624b\u4e0b\u90a3\u91cc\u8fd8\u80af\u7559 \u60c5\u3002 \u3000\u3000\u53ea\u89c1\u4ed6\u51fa\u5200\u5982\u9f99\u98de\uff0c\u6536\u5200\u5982\u864e\u8e1e\uff0c\u201c\u795e\u6c34\u5bab\u201d\u95e8\u4e0b\u7684\u638c\u6cd5\u867d\u7136\u53d8\u5316\u4e07\u5343\uff0c\u8be1\u79d8\u96be\u6d4b\uff0c\u5374 \u4e5f\u4e1d\u6beb\u5360\u4e0d\u4e86\u4e0a\u98ce\u3002 \u3000\u3000\u8981\u77e5\u9053\u8fd9\u4e9b\u767d\u8863\u5973\u5b50\u7eb5\u6709\u72ec\u6b65\u5929\u4e0b\u7684\u201c\u6c34\u6bcd\u201d\u9634\u59ec\u4e4b\u5fc3\u6cd5\u4f20\u6388\uff0c\u600e\u5948\u4e34\u654c\u4ea4\u624b\u7684\u7ecf\u9a8c\u5374 \u5acc\u4e0d\u8db3\u3002 \u3000\u3000\u662f\u4ee5\u5979\u4eec\u5f80\u5f80\u4f1a\u9519\u8fc7\u5148\u673a\u3002 \u3000\u3000\u4f46\u80e1\u94c1\u82b1\u3001\u6234\u72ec\u884c\uff0c\u5374\u65e0\u4e00\u4e0d\u662f\u8eab\u7ecf\u767e\u6218\u7684\u6c99\u573a\u8001\u5c06\uff0c\u975e\u4f46\u7edd\u4e0d\u4f1a\u9519\u8fc7\u4efb\u4f55\u673a\u4f1a\uff0c\u800c\u4e14 \u6bcf\u4e00\u62db\u51fa\u624b\u4e4b\u5224\u65ad\u90fd\u6b63\u786e\u65e0\u8bef\uff0c\u6bcf\u4e00\u4eba\u90fd\u77e5\u9053\u8be5\u5728\u4ec0\u9ebd\u65f6\u5019\u4f7f\u51fa\u4ec0\u9ebd\u6837\u7684\u62db\u5f0f\uff0c\u653b\u5411\u5bf9\u65b9\u6700 \u5f31\u4e4b\u4e00\u73af\u3002 \u3000\u3000\u4ee5\u6b64\u523b\u7684\u6218\u5c40\u800c\u8bba\uff0c\u4ed6\u4eec\u4f3c\u5df2\u7a33\u7a33\u5360\u4e86\u4e0a\u98ce\u3002 \u3000\u3000\u53ef\u662f\uff0c\u4ed6\u4eec\u7eb5\u7136\u80fd\u5360\u80dc\uff0c\u53c8\u6709\u4ec0\u9ebd\u7528\u5462\uff1f \u3000\u3000\u201c\u6c34\u6bcd\u201d\u9634\u59ec\u8fd8\u6ca1\u6709\u73b0\u8eab\uff0c\u767d\u8863\u7f8e\u5987\u3001\u5bab\u5357\u71d5\uff0c\u8fd9\u4e9b\u795e\u6c34\u5bab\u7684\u4e3b\u529b\u6b64\u523b\u4e5f\u90fd\u8fd8\u6ca1\u6709\u51fa \u624b\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u4ed6\u4eec\u8fdf\u65e9\u8fd8\u662f\u5fc5\u8d25\u65e0\u7591\u3002 \u3000\u3000\u695a\u7559\u9999\u7d27\u5f20\u5f97\u51e0\u4e4e\u5c06\u534a\u4e2a\u8eab\u5b50\u90fd\u91c7\u51fa\u6c34\u9762\u4e86\uff0c\u4ed6\u6b64\u523b\u624d\u77e5\u9053\u770b\u522b\u4eba\u52a8\u624b\uff0c\u5b9e\u5728\u6bd4\u81ea\u5df1\u51fa \u624b\u8fd8\u8981\u7d27\u5f20\u5f97\u591a\u3002 \u3000\u3000\u4ed6\u6068\u4e0d\u5f97\u4e5f\u51b2\u51fa\u53bb\uff0c\u52a0\u5165\u6218\u56f4\uff0c\u4f46\u4ed6\u4e5f\u77e5\u9053\u81ea\u5df1\u82e5\u662f\u8fd9\u9ebd\u6837\u505a\uff0c\u90a3\u9ebd\u4ed6\u4eec\u56db\u4eba\u4e5f\u8bb8\u90fd\u4e0d \u514d\u8981\u846c\u8eab\u5728\u8fd9\u91cc\u3002 \u3000\u3000\u201c\u633d\u5f13\u5f53\u633d\u5f3a\uff0c\u64d2\u8d3c\u5148\u64d2\u738b\u201d\uff0c\u4ed6\u552f\u4e00\u7684\u5e0c\u671b\uff0c\u5c31\u662f\u5148\u627e\u51fa\u201c\u6c34\u6bcd\u201d\u9634\u59ec\u7684\u5f31\u70b9\uff0c\u7136\u5f8c \u518d\u4e00\u4e0b\u5b50\u5c06\u5979\u7684\u4e03\u5bf8\u5236\u4f4f\u3002 \u3000\u3000\u4ed6\u7b97\u51c6\u201c\u6c34\u6bcd\u201d\u9634\u59ec\u8fdf\u65e9\u90fd\u8981\u73b0\u8eab\u7684\u3002 \u3000\u3000\u53ea\u8981\u5979\u9732\u9762\uff0c\u4ed6\u5c31\u6709\u673a\u4f1a\u3002 \u3000\u3000\u695a\u7559\u9999\u5fc3\u91cc\u867d\u7136\u7126\u6025\uff0c\u795e\u6c34\u5bab\u5f1f\u5b50\u5374\u66f4\u7126\u6025\u3002 \u3000\u3000\u5979\u4eec\u81ea\u89c6\u6781\u9ad8\uff0c\u4ece\u6765\u4e5f\u672a\u5c06\u522b\u4eba\u770b\u5728\u773c\u91cc\uff0c\u603b\u8ba4\u4e3a\u53ea\u8981\u81ea\u5df1\u4e00\u51fa\u624b\uff0c\u7acb\u523b\u5c31\u80fd\u5c06\u5bf9\u65b9\u624b \u5230\u64d2\u6765\u3002 \u3000\u3000\u5374\u4e0d\u77e5\u5bf9\u65b9\u8fd9\u53c1\u4eba\u7adf\u90fd\u662f\u5f53\u4eca\u5929\u4e0b\u9876\u5c16\u513f\u7684\u9ad8\u624b\uff0c\u9519\u975e\u662f\u795e\u6c34\u5bab\uff0c\u82e5\u662f\u6362\u4e86\u522b\u7684\u5730\u65b9\uff0c \u65e0\u8bba\u4ec0\u9ebd\u5730\u65b9\uff0c\u90fd\u65e9\u5df2\u88ab\u4ed6\u4eec\u4e00\u811a\u5e73\u4e86\u3002\u8fd9\u53c1\u4eba\u8054\u624b\u4f5c\u6218\uff0c\u5929\u4e0b\u6015\u8fd8\u627e\u4e0d\u51fa\u66f4\u5f3a\u7684\u9635\u5bb9\u3002 \u3000\u3000\u7a81\u542c\u4e00\u58f0\u5a07\u547c\uff0c\u5df2\u6709\u4e00\u4e2a\u767d\u8863\u5973\u51cc\u7a7a\u5012\u51c9\u4e86\u51fa\u53bb\uff0c\u5979\u5de6\u624b\u6342\u7740\u53f3\u81c2\uff0c\u9c9c\u8840\u5df2\u81ea\u6307\u7f1d\u91cc\u5411 \u5916\u6c81\u51fa\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u72c2\u7b11\u9053\uff1a\u201c\u82e5\u975e\u770b\u5728\u4f60\u662f\u4e2a\u5973\u4eba\uff0c\u8fd9\u4e00\u5200\u5c31\u8981\u4f60\u7684\u547d\u4e86\u3002\u201d \u3000\u3000\u90a3\u5c11\u5973\u201c\u4e5d\u59b9\u201d\u51b7\u7b11\u9053\uff1a\u201c\u5200\u731b\u800c\u65e0\u52b2\uff0c\u6c14\u8e81\u800c\u4e0d\u51dd\uff0c\u8fd9\u6837\u7684\u6b66\u529f\uff0c\u4e5f\u6562\u6765\u5356\u72c2\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u7b11\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u4f60\u6b66\u529f\u5fc5\u5b9a\u6ee1\u4e0d\u9519\u7684\u4e86\uff0c\u6211\u5012\u60f3\u77a7\u77a7\u3002\u201d \u3000\u3000\u4e5d\u59b9\u53f1\u9053\uff1a\u201c\u6b63\u662f\u8981\u4f60\u77a7\u77a7\u3002\u201d \u3000\u3000\u5403\u58f0\u4e2d\uff0c\u5979\u4e5f\u6251\u5165\u4e86\u6218\u5708\uff0c\u53e6\u53c1\u4e00\u4e2a\u767d\u8863\u5973\u672c\u6765\u62db\u5f0f\u5df2\u9012\u51fa\uff0c\u4f46\u5979\u4e00\u53cc\u7ea4\u7ec7\u7389\u624b\u5374\u5148\u5230 \u4e86\u80e1\u94c1\u82b1\u773c\u524d\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u5200\u80cc\u4e00\u7acb\uff0c\u5200\u5203\u5ffd\u7136\u5411\u5916\u4e00\u7ffb\uff0c\u4e5d\u59b9\u8fd9\u4e00\u62db\u82e5\u662f\u4e0d\u64a4\uff0c\u4e00\u53ea\u6625\u8471\u7684\u7389\u624b\u5c31\u8981\u6bc1\u5728 \u5200\u950b\u4e0a\u4e86\u3002 \u3000\u3000\u4f46\u5979\u53d8\u62db\u5b9e\u5728\u5feb\uff0c\u624b\u8155\u4e00\u53cd\uff0c\u76f4\u53d6\u80e1\u94c1\u82b1\u5de6\u989a\u3002 \u3000\u3000\u8fd9\u4e00\u62db\u53d8\u5316\u81ea\u7136\uff0c\u4e1d\u6beb\u4e0d\u5e26\u70df\u706b\u6c14\uff0c\u4f46\u4e5f\u5c31\u56e0\u4e3a\u5979\u8fd9\u53d8\u5316\u592a\u987a\u7406\u6210\u7ae0\uff0c\u662f\u4ee5\u4e45\u7ecf\u5927\u654c\u7684 \u80e1\u94c1\u82b1\uff0c\u65e9\u5df2\u7b97\u51c6\u4e86\u5979\u7684\u51fa\u624b\u3002 \u3000\u3000\u4ed6\u7684\u5200\u950b\u65e9\u5df2\u5148\u5728\u90a3\u91cc\u7b49\u7740\u5979\u4e86\u3002 \u3000\u3000\u4e5d\u59b9\u4e0d\u77e5\u9053\u8fd9\u662f\u81ea\u5df1\u7684\u7ecf\u9a8c\u592a\u5c11\uff0c\u51fa\u624b\u7684\u5224\u65ad\u4e0d\u6b63\u786e\uff0c\u53ea\u9053\u5bf9\u65b9\u5df2\u5c06\u81ea\u5df1\u4f7f\u51fa\u7684\u6b66\u529f\u62db \u5f0f\u6478\u900f\u4e86\u3002 \u3000\u3000\u5979\u5fc3\u91cc\u6697\u6697\u5403\u60ca\uff0c\u53d8\u62db\u66f4\u4e0d\u5982\u65b9\u51cc\u5389\u6d41\u52a8\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u5927\u7b11\u9053\uff1a\u201c\u62db\u5feb\u800c\u65e0\u529b\uff0c\u6c14\u602f\u800c\u4e0d\u52c7\uff0c\u8fd9\u6837\u7684\u6b66\u529f\uff0c\u4e5f\u6562\u5728\u6211\u9762\u524d\u5356\u72c2\uff0c\u82e5\u975e\u6211 \u601c\u9999\u60dc\u7389\uff0c\u4f60\u8fd9\u53ea\u6625\u8471\u822c\u7684\u5c0f\u624b\uff0c\u65e9\u5c31\u53d8\u6210\u8471\u82b1\u4e86\u3002\u201d \u3000\u3000\u4ed6\u8fd9\u201c\u8471\u82b1\u201d\u4e24\u5b57\u5f53\u771f\u7528\u5f97\u5999\u6781\uff0c\u695a\u56fd\u9999\u542c\u5f97\u51e0\u4e4e\u5fcd\u4e0d\u4f4f\u8981\u7b11\u51fa\u58f0\u6765\uff0c\u4f46\u4ed6\u4e5f\u77e5\u9053\u80e1\u94c1 \u82b1\u8fd9\u5e76\u4e0d\u662f\u5728\u5403\u8c46\u8150\u6216\u5f00\u73a9\u7b11\uff0c\u800c\u662f\u5728\u6545\u610f\u6fc0\u6012\u5bf9\u65b9\uff0c\u8fd9\u201c\u653b\u5fc3\u4e4b\u6218\u201d\u6b63\u662f\u8001\u6c5f\u6e56\u4eec\u5e38\u7528\u7684 \u624b\u6bb5\u3002 \u3000\u3000\u4e5d\u59b9\u6c5f\u6e56\u4e0d\u8001\uff0c\u81ea\u7136\u96be\u514d\u4e0a\u5f53\uff0c\u8138\u90fd\u6c14\u7ea2\u4e86\uff0c\u5979\u6c42\u80dc\u4e4b\u5fc3\u4e00\u5207\uff0c\u51fa\u624b\u5c31\u66f4\u96be\u4fdd\u6301\u51b7\u9759\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u4ee5\u4e00\u5bf9\u56db\uff0c\u5200\u5149\u5982\u4e91\u70bc\uff0c\u5c45\u7136\u53c8\u5360\u4e86\u4e0a\u98ce\u3002 \u3000\u3000\u5ffd\u7136\u95f4\uff0c\u53c8\u662f\u4e00\u58f0\u60ca\u547c\uff0c\u53c8\u6709\u4e00\u4eba\u9000\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u6234\u72ec\u884c\u4e5f\u5927\u7b11\u9053\uff1a\u201c\u5c0f\u5fc3\u4e9b\uff0c\u82e5\u975e\u8001\u592b\u4e0d\u613f\u4ee5\u5927\u538b\u5c0f\uff0c\u4f60\u8fd9\u53ea\u6625\u8471\u822c\u7684\u5c0f\u624b\uff0c\u5c31\u8981\u53d8\u6210 \u8471\u6cb9\u997c\u4e86\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u7b11\u9053\uff1a\u201c\u5999\u6781\uff01\u5999\u6781\uff01\u5200\u65a9\u8471\u82b1\uff0c\u68cd\u6253\u8471\u6cb9\u997c\uff0c\u53ea\u5dee\u9ec4\u8001\u7237\u5b50\u7684\u5251\u6311\u83ca\u6cb9\u9e21 \u4e86\u3002\u201d \u3000\u3000\u9ec4\u9c81\u76f4\u5374\u6c88\u58f0\u9053\uff1a\u201c\u4f60\u4eec\u5e74\u7eaa\u592a\u8f7b\uff0c\u4e34\u654c\u7ecf\u9a8c\u4e0d\u8db3\uff0c\u5fc3\u6d6e\u6c14\u8e81\uff0c\u518d\u6253\u4e0b\u53bb\uff0c\u5fc5\u6709\u4f24\u4ea1\uff0c \u8fd8\u662f\u5feb\u8bf7\u4f60\u4eec\u7684\u5bab\u4e3b\u51fa\u6765\u5427\uff01\u201d \u3000\u3000\u695a\u7559\u9999\u6697\u53f9\u9053\uff1a\u201c\u6b64\u4eba\u679c\u7136\u662f\u6e29\u826f\u541b\u5b50\uff0c\u8bda\u5b9e\u4e0d\u6b3a\uff0c\u770b\u6765\u8fd9\u201c\u541b\u5b50\u5251\u201d\u53c1\u5b57\uff0c\u5012\u7684\u786e\u662f \u540d\u5b9e\u76f8\u526f\u7684\u3002\u201d \u3000\u3000\u4ed6\u5fc3\u91cc\u66f4\u7126\u6025\uff0c\u56e0\u4e3a\u4ed6\u77e5\u9053\u201c\u795e\u6c34\u5bab\u201d\u96c4\u5c45\u5929\u4e0b\uff0c\u5fc5\u975e\u5f92\u5177\u865a\u540d\uff0c\u8fd9\u4e9b\u5f1f\u5b50\u7684\u6b66\u529f\u5df2\u7b97 \u4e00\u6d41\u8eab\u624b\uff0c\u201c\u6c34\u6bcd\u201d\u9634\u59ec\u5fc5\u5b9a\u66f4\u6709\u60ca\u4eba\u7684\u7edd\u827a\uff0c\u5979\u4e00\u73b0\u8eab\uff0c\u5c40\u9762\u5fc5\u5b9a\u8981\u5927\u4e3a\u6539\u89c2\uff0c\u6015\u662f\u51f6\u591a \u5409\u5c11\u3002 \u3000\u3000\u4f46\u201c\u6c34\u6bcd\u201d\u9634\u59ec\u4e3a\u4ec0\u9ebd\u5230\u73b0\u5728\u8fd8\u4e0d\u73b0\u8eab\u5462\uff1f \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u695a\u7559\u9999\u5ffd\u7136\u611f\u89c9\u5230\u5e73\u9759\u7684\u6e56\u6c34\u4e2d\u4f3c\u4e4e\u6709\u4e86\u6e4d\u6fc0\u7684\u6c34\u6d41\uff0c\u4ed6\u7ea6\u4e24\u6761\u817f\u5df2\u9690\u9690\u611f \u89c9\u5230\u4e00\u79cd\u538b\u529b\u3002 \u3000\u3000\u8fd9\u79cd\u611f\u89c9\u6781\u8f7b\u5fae\uff0c\u6362\u4e86\u4efb\u4f55\u4eba\u90fd\u4e0d\u4f1a\u89c9\u5bdf\uff0c\u4f46\u695a\u7559\u9999\u8eab\u4f53\u6bdb\u5b54\u4ff1\u53ef\u547c\u5438\uff0c\u611f\u89c9\u4e4b\u654f\u9510\uff0c \u975e\u4efb\u4f55\u4eba\u53ef\u6bd4\u3002 \u3000\u3000\u4ed6\u8eab\u5b50\u7acb\u523b\u6f5c\u5165\u6c34\u4e2d\uff0c\u5411\u5de6\u9762\u4e00\u5757\u5de8\u77f3\u5f8c\u7684\u7a7a\u9699\u6324\u4e86\u8fdb\u53bb\uff0c\u5168\u8eab\u7f29\u9aa8\uff0c\u6bd4\u4ed6\u5e73\u5e38\u7684\u4f53\u79ef \u81f3\u5c11\u5c0f\u4e86\u53c1\u5206\u4e4b\u4e00\u3002 \u3000\u3000\u4ed6\u51fa\u751f\u5165\u6b7b\uff0c\u8fd9\u4e00\u751f\u4e2d\u6240\u5192\u7684\u9669\uff0c\u6bd4\u5e73\u5e38\u4e00\u767e\u4e2a\u4eba\u52a0\u8d77\u6765\u90fd\u591a\uff0c\u82e5\u975e\u4ed6\u53cd\u5e94\u5feb\uff0c\u5e94\u53d8\u66f4 \u5feb\uff0c\u65e9\u5df2\u4e0d\u77e5\u6b7b\u8fc7\u591a\u5c11\u6b21\u4e86\u3002 \u3000\u3000\u8fd9\u4e00\u6b21\uff0c\u4ed6\u8fd9\u79cd\u8d85\u4eba\u7684\u5e94\u53d8\u80fd\u529b\u53c8\u6551\u4e86\u4ed6\u3002 \u3000\u3000\u4ed6\u53d1\u73b0\u5c31\u5728\u4ed6\u53f3\u9762\u7684\u90a3\u5757\u5de8\u77f3\u5df2\u5728\u79fb\u52a8\uff0c\u4ed6\u817f\u4e0a\u611f\u5230\u7684\u538b\u529b\uff0c\u5c31\u662f\u8fd9\u5757\u5de8\u77f3\u79fb\u52a8\u65f6\u63a8\u52a8 \u6c34\u6d41\u6240\u9020\u6210\u7684\u3002 \u3000\u3000\u4ed6\u82e5\u8fd8\u6ca1\u6709\u8eb2\u5165\u8fd9\u7a7a\u9699\u91cc\u3002\uff0c\u4e24\u8fb9\u7684\u5de8\u77f3\u5c31\u8981\u5c06\u4ed6\u5939\u4f4f\u3002 \u3000\u3000\u5de8\u77f3\u65e2\u5b58\u79fb\u52a8\uff0c\u6e56\u5e95\u663e\u7136\u4e5f\u6709\u79d8\u9053\uff0c\u201c\u6c34\u6bcd\u201d\u9634\u59ec\u7684\u79d8\u5bc6\uff0c\u663e\u7136\u5c31\u5728\u6e56\u5e95\uff0c\u695a\u7559\u9999\u8fd9\u65f6 \u7684\u5174\u594b\uff0c\u5b9e\u5728\u96be\u4ee5\u5f62\u5bb9\u3002 \u3000\u3000\u4e24\u5757\u5de8\u77f3\u5e76\u6ca1\u6709\u5b8c\u5168\u5408\u62e2\uff0c\u4e2d\u95f4\u8fd8\u6709\u4e00\u7ebf\u7a7a\u9699\u3002 \u3000\u3000\u695a\u7559\u9999\u4fa7\u7740\u5934\uff0c\u4ece\u8fd9\u6761\u7a7a\u9699\u4e2d\u671b\u51fa\u53bb\uff0c\u53ea\u89c1\u4e00\u8fde\u4e32\u6c34\u6ce1\u81ea\u77f3\u5f8c\u51b2\u6d41\u4e86\u51fa\u6765\uff0c\u6309\u7740\uff0c\u5374\u51fa \u73b0\u4e86\u4e24\u4e2a\u4eba\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u90fd\u7a7f\u7740\u767d\u8272\u7684\u957f\u888d\uff0c\u867d\u7136\u5728\u6c34\u4e2d\uff0c\u4f46\u957f\u888d\u5e76\u6ca1\u6709\u6e7f\u8d34\u5728\u5979\u4eec\u8eab\u4e0a\uff0c\u53cd\u6709\u5982\u5728\u98ce\u4e2d \u4e00\u822c\u98d8\u52a8\u3002 \u3000\u3000\u695a\u7559\u9999\u5df2\u8ba4\u51fa\u5176\u4e2d\u4e00\u4eba\u6b63\u662f\u5bab\u5357\u71d5\uff0c\u5979\u7684\u773c\u775b\u5728\u6c34\u4e2d\u770b\u6765\uff0c\u663e\u5f97\u66f4\u6726\u80e7\uff0c\u66f4\u6df1\u9083\uff0c\u4e5f\u66f4 \u7f8e\u4e3d\u3002 \u3000\u3000\u5979\u62c9\u7740\u53e6\u4e00\u4eba\u7684\u624b\u7f13\u7f13\u8d70\u4e86\u51fa\u6765\uff0c\u5979\u4eec\u5728\u6c34\u4e2d\u884c\u52a8\uff0c\u51e0\u4e4e\u5c31\u548c\u5728\u9646\u5730\u4e0a\u540c\u6837\u5b89\u7965\u800c\u81ea \u7136\u3002 \u3000\u3000\u695a\u7559\u9999\u770b\u4e0d\u5230\u53e6\u4e00\u4eba\u7684\u9762\u8c8c\uff0c\u53ea\u89c9\u5f97\u5b83\u662f\u4e2a\u5f88\u9ad8\u5927\u7684\u5973\u5b50\uff0c\u51e0\u4e4e\u6bd4\u5bab\u5357\u71d5\u9ad8\u51fa\u4e86\u6574\u6574\u4e00 \u4e2a\u5934\u3002 \u3000\u3000\u8fd9\u4eba\u96be\u9053\u5c31\u662f\u90a3\u795e\u79d8\u800c\u53ef\u6015\u7684\u201c\u6c34\u6bcd\u201d\u9634\u59ec\u9ebd\uff1f \u3000\u3000\u53ea\u89c1\u5bab\u5357\u71d5\u7275\u7740\u5979\uff0c\u5ffd\u7136\u5c06\u5979\u7684\u624b\u653e\u5728\u9762\u988a\u4e0a\u7528\u529b\u78e8\u64e6\u7740\uff0c\u76ee\u4e2d\u6d41\u9732\u51fa\u4e00\u79cd\u5f3a\u70c8\u7684\u7231 \u6b32\u3002 \u3000\u3000\u8fd9\u4eba\u7528\u53e6\u4e00\u53ea\u624b\u53bb\u629a\u6478\u5979\u7684\u5934\u53d1\uff0c\u770b\u6765\u5c31\u50cf\u662f\u4e00\u53cc\u5f88\u6069\u7231\u7684\u60c5\u4fa3\uff0c\u7edd\u4e0d\u50cf\u662f\u5e08\u5f92\u95f4\u5e94\u6709 \u7684\u4e3e\u52a8\u3002 \u3000\u3000\u8fd9\u4eba\u96be\u9053\u5e76\u4e0d\u662f\u9634\u59ec\uff0c\u800c\u662f\u4e2a\u7537\u7684\uff1f \u3000\u3000\u695a\u7559\u9999\u53c8\u770b\u7cca\u6d82\u4e86\uff0c\u8fd9\u65f6\u5bab\u5357\u71d5\u7ec8\u65bc\u5df2\u653e\u5f00\u624b\uff0c\u4f46\u4e00\u53cc\u5145\u6ee1\u4e86\u7231\u6b32\u7684\u76ee\u5149\u5374\u8fd8\u662f\u51dd\u4f4f\u5728 \u8fd9\u4eba\u8138\u4e0a\u3002 \u3000\u3000\u8fd9\u4eba\u5374\u5df2\u8f6c\u8fc7\u8eab\uff0c\u695a\u7559\u9999\u7ec8\u65bc\u770b\u5230\u4e86\u5979\u7684\u8138\u3002 \u3000\u3000\u5979\u6709\u4e00\u53cc\u5f88\u5927\u7684\u773c\u775b\uff0c\u5f88\u6d53\u7684\u80a9\uff0c\u9f3b\u4e8e\u66f4\u575a\u633a\u800c\u7855\u5927\uff0c\u8584\u8584\u7684\u5634\u7d27\u7d27\u95ed\u7740\uff0c\u663e\u793a\u51fa\u5979\u662f \u4e2a\u5f88\u6709\u6bc5\u529b\u548c\u51b3\u5fc3\u7684\u4eba\u3002 \u3000\u3000\u8fd9\u662f\u5f20\u5f88\u4e0d\u5e73\u51e1\u7684\u8138\uff0c\u90a3\u575a\u633a\u7684\u9f3b\u5b50\u4f7f\u5979\u770b\u4e0a\u53bb\u6709\u4e00\u79cd\u6151\u4eba\u7684\u5a01\u4e25\uff0c\u5979\u7684\u795e\u60c5\u66f4\u663e\u51fa\u5979 \u4e00\u5411\u662f\u552f\u6211\u72ec\u5c0a\uff0c\u4ece\u6765\u4e5f\u6ca1\u6709\u4eba\u6562\u53cd\u6297\u5979\uff0c\u9664\u4e86\u795e\u6c34\u5bab\u4e3b\u201c\u6c34\u6bcd\u201d\u9634\u59ec\u5916\uff0c\u522b\u4eba\u7edd\u4e0d\u914d\u6709\u8fd9 \u9ebd\u6837\u4e00\u5f20\u8138\u3002 \u3000\u3000\u4f46\u8fd9\u5374\u5e76\u4e0d\u50cf\u662f\u4e00\u5f20\u5973\u4eba\u7684\u7751\uff0c\u82e5\u975e\u5979\u7684\u8eab\u6750\u5f88\u660e\u663e\u662f\u5973\u4eba\u7684\uff0c\u695a\u7559\u9999\u51e0\u4e4e\u8981\u8ba4\u4e3a\u201c\u6c34 \u6bcd\u201d\u9634\u59ec\u662f\u4e2a\u7537\u4eba\u3002 \u3000\u3000\u5947\u602a\u7684\u662f\uff0c\u5979\u5e76\u6ca1\u6709\u5347\u51fa\u6e56\u9762\uff0c\u53cd\u800c\u7f13\u7f13\u8d70\u5230\u6e56\u5fc3\uff0c\u695a\u7559\u9999\u8fd9\u624d\u53d1\u73b0\u6e56\u5fc3\u6709\u5757\u767d\u77f3\uff0c\u5979 \u5c31\u5728\u767d\u77f3\u4e0a\u76d8\u819d\u5750\u4e0b\u3002 \u3000\u3000\u5979\u8fd9\u662f\u4ec0\u9ebd\u610f\u601d\uff1f \u3000\u3000\u4e0a\u9762\u5df2\u95f9\u5f97\u5929\u7ffb\u5730\u590d\uff0c\u5979\u4e3a\u4ec0\u9ebd\u8fd8\u5750\u5728\u8fd9\u91cc\uff1f \u3000\u3000\u695a\u7559\u9999\u6b63\u89c9\u5f97\u5947\u602a\u201c\u6c34\u6bcd\u201d\u9634\u59ec\u5df2\u5411\u5bab\u5357\u71d5\u6446\u4e86\u6446\u624b\uff0c\u5bab\u5357\u71d5\u4e5f\u5411\u77f3\u5934\u8fd9\u8fb9\u6253\u4e86\u4e2a\u624b \u5f0f\u3002 \u3000\u3000\u5239\u90a3\u95f4\uff0c\u4f46\u89c1\u4e00\u80a1\u5f3a\u70c8\u7684\u6fc0\u6d41\uff0c\u81ea\u6e56\u5fc3\u90a3\u5757\u767d\u77f3\u4e0b\u51b2\u8d77\uff0c\u5f62\u6210\u4e86\u4e00\u6761\u6c34\u67f1\uff0c\u5c06\u9634\u59ec\u76f4\u6258 \u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u5e73\u9759\u7684\u6e56\u9762\u4e0a\uff0c\u5ffd\u7136\u6709\u4e00\u6761\u6c34\u67f1\u51b2\u5929\u800c\u8d77\uff0c\u5347\u8d77\u53c1\u4e08\u5f8c\uff0c\u624d\u56db\u4e0b\u6e85\u51fa\uff0c\u5c31\u5728\u8fd9\u6c34\u67f1\u7684\u9876 \u7aef\uff0c\u7adf\u76d8\u819d\u7aef\u5750\u7740\u4e2a\u767d\u8863\u4eba\u3002 \u3000\u3000\u661f\u5149\u707f\u70c2\uff0c\u6c34\u67f1\u4e5f\u95ea\u95ea\u7684\u53d1\u7740\u5149\u3002 \u3000\u3000\u8fdc\u8fdc\u770b\u6765\uff0c\u5c31\u5f77\u4f5b\u767d\u8863\u89c2\u97f3\u81ea\u6e56\u5e95\u98de\u5347\uff0c\u7aef\u5750\u5728\u4e00\u5ea7\u4e03\u5b9d\u7409\u7483\u83b2\u53f0\u4e0a\uff0c\u6cd5\u76f8\u5e84\u4e25\uff0c\u4ee4\u4eba \u4e0d\u6562\u4ef0\u89c6\u3002 \u3000\u3000\u8fdc\u5904\u7684\u4e50\u58f0\u5df2\u53d8\u5f97\u67d4\u548c\u800c\u5e84\u4e25\u3002 \u3000\u3000\u6240\u6709\u7684\u767d\u8863\u5973\u90fd\u9000\u4e86\u4e0b\u53bb\uff0c\u5929\u5730\u95f4\u5f77\u4f5b\u53ea\u5269\u4e0b\u4e86\u8fd9\u5982\u955c\u7684\u94f6\u6e56\uff0c\u6e56\u4e0a\u7684\u83b2\u5ea7\uff0c\u5ea7\u4e0a\u7684\u6cd5 \u76f8\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u3001\u9ec4\u9c81\u76f4\u3001\u6234\u72ec\u884c\uff0c\u4ef0\u9762\u800c\u671b\uff0c\u4ed6\u4eec\u867d\u7136\u7ecf\u591a\u89c1\u5e7f\uff0c\u6b64\u523b\u4e5f\u4e0d\u7981\u4e3a\u4e4b\u5c4f\u606f\u80a1\u6817\uff0c \u795e\u9b42\u98de\u8d8a\u3002 \u3000\u3000\u8fd9\u65f6\u5bab\u5357\u71d5\u4e5f\u81ea\u6e56\u5fc3\u5982\u98de\u4ed9\u822c\u51c9\u5230\u6e56\u5cb8\uff0c\u65e5\u5982\u95ea\u7535\uff0c\u9762\u7f69\u79cb\u971c\uff0c\u95ea\u7535\u822c\u7684\u76ee\u5149\u4e00\u626b\uff0c\u51b7 \u51b7\u9053\uff1a\u201c\u5bab\u4e3b\u6cd5\u8eab\u5df2\u73b0\uff0c\u4f60\u4eec\u8fd8\u4e0d\u8dea\u5012\u53c1\u62dc\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u5ffd\u7136\u7b11\u4e86\u3002 \u3000\u3000\u4ed6\u5728\u8fd9\u79cd\u65f6\u5019\u5c45\u7136\u8fd8\u6562\u7b11\uff0c\u80c6\u5b50\u5b9e\u5728\u4e0d\u5c0f\uff0c\u8fde\u5bab\u5357\u71d5\u76ee\u4e2d\u90fd\u4e0d\u7981\u9732\u51fa\u4e86\u4e00\u4e1d\u60ca\u5947\u4e4b\u8272\u3002 \u3000\u3000\u53ea\u542c\u80e1\u94c1\u82b1\u5927\u7b11\u9053\uff1a\u201c\u6cd5\u8eab\uff1f\u53c1\u62dc\uff1f\u4f60\u96be\u9053\u771f\u4ee5\u4e3a\u81ea\u5df1\u662f\u795e\u4ed9\u9ebd\uff1f\u201d \u3000\u3000\u7206\u5357\u71d5\u76b1\u4e86\u76b1\u7709\uff0c\u9053\uff1a\u201c\u8fd9\u72c2\u5f92\u662f\u8c01\uff1f\u201d \u3000\u3000\u4e5d\u59b9\u62a2\u5148\u62dc\u5012\uff0c\u9053\uff1a\u201c\u6b64\u4eba\u81ea\u79f0\u80e1\u94c1\u82b1\uff0c\u548c\u4ed6\u540c\u6765\u7684\u662f\u201c\u541b\u5b50\u5251\u201d\u9ec4\u9c81\u76f4\uff0c\u4e10\u5e2e\u6234\u72ec \u884c\u3002\u201d \u3000\u3000\u7206\u5357\u71d5\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u53c1\u4eba\u662f\u5426\u81ea\u89c9\u6b66\u529f\u4e0d\u5f31\uff0c\u7adf\u6562\u95ef\u5230\u8fd9\u91cc\u6765\uff1f\u201d \u3000\u3000\u6234\u70db\u884c\u4ef0\u5929\u72c2\u7b11\u9053\uff1a\u201c\u5728\u4e0b\u7b49\u529f\u592b\u867d\u4e0d\u60ca\u4eba\uff0c\u5374\u4e5f\u8fd8\u8fc7\u5f97\u53bb\u3002\u201d \u3000\u3000\u201c\u6c34\u6bcd\u201d\u9634\u59ec\u5ffd\u7136\u9053\uff1a\u201c\u6b64\u4eba\u662f\u8c01\u7684\u95e8\u4e0b\uff1f\u201d \u3000\u3000\u5979\u8fd9\u53e5\u8bdd\u4e0d\u95ee\u6234\u72ec\u884c\u81ea\u5df1\uff0c\u53cd\u800c\u95ee\u5bab\u5357\u71d5\uff0c\u5f77\u4f5b\u5979\u6839\u672c\u4e0d\u613f\u548c\u7537\u4eba\u8bf4\u8bdd\uff0c\u6234\u72ec\u884c\u4e0d\u7981\u53c8 \u7b11\u9053\uff1a\u201c\u6211\u8001\u4eba\u5bb6\u51fa\u9053\u7684\u65f6\u5019\uff0c\u5979\u8fd8\u4e0d\u77e5\u5728\u90a3\u91cc\u5462\uff1f\u4f60\u95ee\u5979\uff0c\u5979\u53c8\u600e\u4f1a\u77e5\u9053\u6211\u8001\u4eba\u5bb6\u7684\u6765 \u5386\u3002\u201d \u3000\u3000\u7206\u5357\u71d5\u7b49\u4ed6\u7b11\u5b8c\u4e86\uff0c\u624d\u51b7\u51b7\u9053\uff1a\u201c\u6b64\u4eba\u672c\u662f\u6a2a\u884c\u4e24\u6cb3\u7684\u72ec\u884c\u76d7\uff0c\u53c1\u5341\u5c81\u5f8c\uff0c\u624d\u6539\u90aa\u5f52 \u6b63\uff0c\u6295\u5165\u4e10\u5e2e\uff0c\u660e\u867d\u662f\u5f53\u65f6\u5e2e\u4e3b\u5415\u5357\u7684\u5f1f\u5b50\uff0c\u5176\u5b9e\u5374\u662f\u5415\u5357\u9996\u5f92\u6731\u660e\u4ee3\u5e08\u4f20\u827a\uff0c\u4f20\u6388\u6b66\u529f\u7ed9 \u4ed6\u7684\uff0c\u662f\u4ee5\u4ed6\u5165\u95e8\u867d\u665a\uff0c\u5728\u5e2e\u4e2d\u8f88\u4efd\u5374\u5f88\u9ad8\u3002\u201d \u3000\u3000\u201c\u6c34\u6bcd\u201d\u9634\u59ec\u9053\uff1a\u201c\u4ed6\u6b66\u529f\u662f\u5426\u5df2\u5f97\u4e86\u6731\u660e\u771f\u4f20\uff1f\u201d \u3000\u3000\u7206\u5357\u71d5\u9053\uff1a\u201c\u6731\u660e\u53f7\u79f0\u94a2\u62f3\u94c1\u638c\uff0c\u5185\u529b\u4e4b\u5f3a\uff0c\u638c\u529b\u4e4b\u539a\uff0c\u5728\u4e10\u5e2e\u4e2d\u53ef\u79f0\u7a7a\u524d\u7edd\u540e\uff0c\u4ed6\u600e \u9ebd\u6bd4\u5f97\u4e0a\uff0c\u53ea\u4e0d\u8fc7\u4ed6\u672c\u662f\u72ec\u884c\u76d7\u51fa\u8eab\uff0c\u662f\u4ee5\u8f7b\u529f\u4f3c\u4e4e\u6bd4\u6731\u660e\u8fd8\u80dc\u4e00\u7b79\uff0c\u53c8\u56e0\u4e3a\u4ed6\u672c\u4f7f\u7684\u662f \u5251\uff0c\u6240\u4ee5\u4ed6\u7684\u68cd\u6cd5\u4e2d\u63c9\u5408\u4e86\u201c\u4e03\u4e03\u56db\u5341\u4e5d\u624b\u56de\u98ce\u821e\u67f3\u5251\u201d\u7684\u53d8\u5316\uff0c\u5728\u5f53\u4eca\u4e10\u5e2e\u4e2d\uff0c\u53ef\u7b97\u662f\u7b2c \u4e00\u4eba\u4e86\u3002\u201d \u3000\u3000\u5979\u5c45\u7136\u5c06\u6234\u72ec\u884c\u7684\u6765\u5386\u548c\u6b66\u529f\u5982\u6570\u5bb6\u73cd\u822c\u8bf4\u4e86\u51fa\u6765\uff0c\u8fd9\u4e0b\u5b50\u6234\u89e6\u884c\u53ef\u7b11\u4e0d\u51fa\u4e86\uff0c\u6697\u6697\u5fd6 \u9053\uff1a\u201c\u795e\u6c34\u5bab\u5f1f\u5b50\u7d20\u6765\u4e0d\u548c\u5916\u4eba\u6765\u5f80\uff0c\u8c01\u77e5\u5979\u4eec\u79c0\u624d\u4e0d\u51fa\u95e8\uff0c\u7adf\u80fd\u77e5\u5929\u4e0b\u4e8b\uff0c\u770b\u6765\u795e\u6c34\u5bab\u5012 \u7684\u786e\u6709\u4e9b\u540d\u5802\u3002\u201d \u3000\u3000\u53ea\u542c\u201c\u6c34\u6bcd\u201d\u9634\u59ec\u51b7\u7b11\u9053\uff1a\u201c\u5c31\u8fde\u6731\u660e\uff0c\u5e73\u751f\u4e5f\u4e0d\u6562\u5984\u5165\u672c\u5bab\u4e00\u6b65\uff0c\u60f3\u4e0d\u5230\u6b64\u4eba\u7684\u80c6\u5b50 \u7adf\u6bd4\u6731\u660e\u8fd8\u5927\u3002\u201d \u7b2c\u4e8c\u5341\u516b\u7ae0\u3000\u751f\u6b7b\u4e4b\u640f>> \u53e4\u9f99\u300a\u695a\u7559\u9999\u7cfb\u5217\xb7\u753b\u7709\u9e1f\u300b \u7b2c\u4e8c\u5341\u516b\u7ae0\u3000\u751f\u6b7b\u4e4b\u640f \u3000\u3000\u6c34\u6bcd\u9634\u59ec\u968f\u624b\u5411\u80e1\u94c1\u82b1\u4e00\u6307\uff0c\u9053\uff1a\u201c\u8fd9\u4eba\u5462\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u77aa\u7740\u5bab\u5357\u71d5\uff0c\u5fc3\u91cc\u6697\u6697\u5f97\u610f\uff1a\u201c\u4f60\u82e5\u8fde\u6211\u7684\u6b66\u529f\u6765\u5386\u90fd\u77e5\u9053\uff0c\u90a3\u6211\u624d\u7b97\u4f69\u670d\u4f60 \u4e86\u3002\u201d \u3000\u3000\u7206\u5357\u71d5\u679c\u7136\u6c88\u541f\u4e86\u534a\u664c\uff0c\u624d\u7f13\u7f13\u9053\uff1a\u201c\u6b64\u4eba\u548c\u695a\u7559\u9999\u4e00\u6837\uff0c\u6c5f\u6e56\u4e2d\u51e0\u4e4e\u6ca1\u6709\u4eba\u77e5\u9053\u4ed6\u4eec \u7684\u6b66\u529f\u6765\u5386\uff0c\u53ea\u77e5\u4ed6\u4eec\u672c\u90fd\u662f\u4e16\u5bb6\u5b50\uff0c\u800c\u4e14\u81ea\u5e7c\u597d\u6b66\uff0c\u662f\u4ee5\u5bb6\u91cc\u4e3a\u4ed6\u4eec\u8bf7\u4e86\u4e0d\u5c11\u6b66\u5e08\uff0c\u4f46\u4ed6 \u4eec\u7684\u6b66\u529f\u5374\u7edd\u4e0d\u662f\u8fd9\u4e9b\u6b66\u5e08\u80fd\u6559\u51fa\u6765\u7684\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u70b9\u7740\u5934\uff0c\u5fae\u7b11\u9053\uff1a\u201c\u4e00\u70b9\u4e5f\u4e0d\u9519\u3002\u201d \u3000\u3000\u7206\u5357\u71d5\u9053\uff1a\u201c\u6240\u4ee5\u5f53\u65f6\u6709\u8bb8\u591a\u4eba\u6000\u7591\uff0c\u4ed6\u4eec\u5bb6\u91cc\u4e00\u5b9a\u6709\u4f4d\u9690\u8ff9\u6c5f\u6e56\u7684\u98ce\u5c18\u5f02\u4eba\uff0c\u5728\u6697\u4e2d \u5077\u5077\u4f20\u6388\u7ed9\u4ed6\u4eec\u6b66\u529f\uff0c\u4e5f\u6709\u4eba\u6000\u7591\u4ed6\u4eec\u51d1\u5de7\u5f97\u5230\u4e86\u4e00\u672c\u524d\u8f88\u9ad8\u4eba\u7559\u4e0b\u6765\u7684\u6b66\u529f\u79d8\u7b08\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u7b11\u9053\uff1a\u201c\u4f60\u80fd\u77e5\u9053\u8fd9\u9ebd\u591a\uff0c\u5df2\u7b97\u4e0d\u5bb9\u6613\u4e86\u3002\u201d \u3000\u3000\u7206\u5357\u71d5\u4e5f\u4e0d\u7406\u4ed6\uff0c\u63a5\u7740\u53c8\u9053\uff1a\u201c\u53ef\u662f\uff0c\u4ed6\u548c\u695a\u7559\u9999\u867d\u662f\u4e00\u9f50\u957f\u5927\u7684\uff0c\u6b66\u529f\u7684\u8def\u6570\uff0c\u5374\u7edd \u4e0d\u76f8\u540c\uff0c\u4ed6\u6b66\u529f\u8d70\u7684\u662f\u521a\u731b\u4e00\u8def\uff0c\u4f3c\u4e4e\u548c\u6614\u5e74\u201c\u94c1\u8840\u5927\u65d7\u95e8\u201d\u7684\u6b66\u529f\u6709\u4e9b\u76f8\u4f3c\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u5ffd\u7136\u7b11\u4e0d\u51fa\u6765\u4e86\uff0c\u9762\u4e0a\u5df2\u4e0d\u7981\u9732\u51fa\u60ca\u8bb6\u4e4b\u8272\u3002 \u3000\u3000\u7206\u5357\u71d5\u8fde\u770b\u90fd\u4e0d\u770b\u4ed6\u4e00\u773c\uff0c\u7f13\u7f13\u63a5\u7740\u9053\uff1a\u201c\u6614\u5e74\u94c1\u4e2d\u68e0\u91cd\u632f\u94c1\u8840\u5927\u65d7\u95e8\u5f8c\uff0c\u201d\u591c\u5e1d\u201d\u7236 \u5b50\u5c31\u548c\u5927\u65d7\u95e8\u4e2d\u4e00\u4f4d\u53eb\u8d64\u8db3\u6c49\u7684\u524d\u8f88\uff0c\u8fdc\u6e38\u6d77\u5916\uff0c\u4ed6\u4eec\u66fe\u7ecf\u7ecf\u8fc7\u6b64\u4eba\u7684\u6545\u4e61\uff0c\u4ee5\u5f1f\u5b50\u63a8\u6d4b\uff0c \u695a\u7559\u9999\u7684\u6b66\u529f\u4e5f\u8bb8\u662f\u591c\u5e1d\u7684\u4f20\u6388\uff0c\u8d64\u8db3\u6c49\u5374\u6536\u4e86\u6b64\u4eba\u505a\u5f92\u5f1f\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u53f9\u4e86\u53e3\u6c14\uff0c\u5583\u5583\u9053\uff1a\u201c\u8fd9\u6b21\u4f60\u731c\u7684\u867d\u4e0d\u4e2d\u65b9\u4e0d\u8fdc\u77e3\uff0c\u96be\u602a\u6c5f\u6e56\u4e2d\u4eba\u4eba\u90fd\u6015\u4f60\u4eec\uff0c \u770b\u6765\u4f60\u4eec\u679c\u7136\u771f\u6709\u4e24\u4e0b\u5b50\u3002\u201d \u3000\u3000\u542c\u5230\u201c\u591c\u5e1d\u201d\u548c\u201c\u94c1\u8840\u5927\u65d7\u95e8\u201d\u7684\u540d\u5b57\uff0c\u8fde\u201c\u6c34\u6bcd\u201d\u9634\u59ec\u4e5f\u4e0d\u7981\u4e3a\u4e4b\u58f0\u7136\u52a8\u5bb9\uff0c\u6c88\u541f\u534a \u664c\uff0c\u9053\uff1a\u201c\u8fd9\u53c1\u4eba\u662f\u4e3a\u4f55\u800c\u6765\u7684\uff1f\u201d \u3000\u3000\u4e5d\u59b9\u8eac\u8eab\u9053\uff1a\u201c\u4ed6\u4eec\u8bf4\u662f\u6765\u627e\u4eba\u7684\u3002\u201d \u3000\u3000\u90a3\u767d\u8863\u7f8e\u5987\u4e5f\u8eac\u8eab\u9053\uff1a\u201c\u5f1f\u5b50\u65e9\u5df2\u544a\u8bc9\u4ed6\u4eec\uff0c\u672c\u8c37\u7edd\u65e0\u5916\u4eba\u51fa\u5165\uff0c\u4ed6\u4eec\u5c45\u7136\u8fd8\u4e0d\u76f8 \u4fe1\u3002\u201d \u3000\u3000\u201c\u6c34\u6bcd\u201d\u9634\u59ec\u51b7\u7b11\u9053\uff1a\u201c\u4ed6\u4eec\u60f3\u600e\u6837\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u62a2\u7740\u9053\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u8981\u6211\u4eec\u8bf4\u8001\u5b9e\u8bdd\uff1f\u201d \u3000\u3000\u7206\u5357\u71d5\u9053\uff1a\u201c\u8bf4\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u6211\u4eec\u672c\u662f\u6765\u627e\u4eba\u7684\uff0c\u4eba\u65e2\u4e0d\u5728\u8fd9\u91cc\uff0c\u6211\u4eec\u73b0\u5728\u5df2\u7ecf\u60f3\u8d70\u4e86\u3002\u201d \u3000\u3000\u7206\u5357\u71d5\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u5012\u662f\u4e2a\u806a\u660e\u4eba\uff0c\u53ea\u53ef\u60dc\u672c\u5bab\u4e00\u5411\u662f\u6765\u5f97\u8d70\u4e0d\u5f97\u7684\u3002\u4f60\u60f3\u8fdb\u6765\uff0c\u7edd\u6ca1 \u6709\u4eba\u62e6\u963b\uff0c\u4f60\u82e5\u60f3\u51fa\u53bb\uff0c\u5c31\u96be\u5982\u767b\u5929\u4e86\u3002\u201d \u3000\u3000\u6c34\u6bcd\u5ffd\u53c8\u9053\uff1a\u201c\u544a\u8bc9\u4ed6\u4eec\uff0c\u65e0\u8bba\u4ed6\u4eec\u7528\u4ec0\u9ebd\u6cd5\u5b50\uff0c\u53ea\u8981\u4ed6\u4eec\u80fd\u5c06\u672c\u5bab\u81ea\u8fd9\u5723\u6c34\u83b2\u53f0\u4e0a\u63a8 \u4e0b\u53bb\uff0c\u672c\u5bab\u5c31\u653e\u4ed6\u4eec\u8d70\u3002\u201d \u3000\u3000\u7206\u5357\u71d5\u9053\uff1a\u201c\u4f60\u4eec\u53ea\u8981\u2026\u2026\u201d \u3000\u3000\u80e1\u94c1\u82b1\u5927\u7b11\u9053\uff1a\u201c\u6211\u4eec\u53c8\u4e0d\u662f\u804b\u5b50\uff0c\u5979\u8bf4\u7684\u8bdd\u6211\u4eec\u5df2\u542c\u89c1\u4e86\uff0c\u7528\u4e0d\u7740\u4f60\u518d\u8bf4\u4e00\u6b21\u3002\u201d \u3000\u3000\u6234\u72ec\u884c\u9053\uff1a\u201c\u5374\u4e0d\u77e5\u5979\u8bf4\u7684\u8bdd\u7b97\u4e0d\u7b97\u6570\uff1f\u201d \u3000\u3000\u7206\u5357\u71d5\u6c88\u7740\u8138\u9053\uff1a\u201c\u5bab\u4e3b\u4ee4\u51fa\u5982\u5c71\uff01\u6c38\u65e0\u66f4\u6539\u3002\u201d \u3000\u3000\u80e1\u94c1\u5316\u548c\u6234\u72ec\u884c\u5bf9\u671b\u4e00\u773c\uff0c\u9762\u4e0a\u90fd\u4e0d\u7981\u9732\u51fa\u559c\u8272\u3002 \u3000\u3000\u4ed6\u4eec\u89c1\u5230\u8fd9\u201c\u6c34\u6bcd\u201d\u9634\u59ec\u5750\u5728\u6fc0\u6d8c\u7684\u6c34\u82b1\u4e0a\uff0c\u7adf\u5b89\u5982\u6cf0\u5c71\uff0c\u5df2\u77e5\u9053\u6b64\u4eba\u975e\u4f46\u8f7b\u529f\u5df2\u767b\u5cf0 \u9020\u6781\uff0c\u6c14\u529f\u4ea6\u6df1\u4e0d\u53ef\u6d4b\uff0c\u4ed6\u4eec\u7684\u786e\u672a\u5fc5\u662f\u5979\u7684\u654c\u624b\uff0c\u5979\u82e5\u627e\u4ed6\u4eec\u6311\u6218\uff0c\u4ee5\u4ed6\u4eec\u7684\u8eab\u4efd\uff0c\u65e2\u4e0d \u80fd\u62d2\u7edd\uff0c\u4e5f\u4e0d\u80fd\u53c1\u4e2a\u6253\u4e00\u4e2a\uff0c\u90a3\u9ebd\u4eca\u5929\u4ed6\u4eec\u6015\u662f\u7684\u786e\u5f88\u96be\u6d3b\u7740\u8d70\u51fa\u8fd9\u795e\u6c34\u5bab\u4e86\u3002\u53ef\u662f\u73b0\u5728\u9634 \u59ec\u65e2\u7136\u5982\u6b64\u6258\u5927\uff0c\u60c5\u51b5\u5c31\u5927\u4e0d\u76f8\u540c\u4e86\u3002 \u3000\u3000\u51ed\u4ed6\u4eec\u53c1\u4e2a\u6b66\u6797\u4e2d\u4e00\u7b49\u4e00\u7684\u9ad8\u624b\uff0c\u82e5\u8fd8\u4e0d\u80fd\u5c06\u5979\u81ea\u8fd9\u6839\u672c\u5750\u4e0d\u7a33\u7684\u6c34\u67f1\u4e0a\u903c\u4e0b\u6765\uff0c\u90a3\u624d \u771f\u662f\u602a\u4e8b\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u751f\u6015\u5979\u53c8\u6539\u53d8\u4e3b\u610f\uff0c\u6545\u610f\u51b7\u7b11\u9053\uff1a\u201c\u4eba\u5bb6\u65e2\u7136\u4e00\u5b9a\u8981\u8fd9\u9ebd\u6837\u505a\uff0c\u6211\u4eec\u4e5f\u6ca1\u6cd5\u5b50\uff0c \u662f\u9ebd\uff1f\u201d \u3000\u3000\u6234\u72ec\u884c\u9053\uff1a\u201c\u8fd9\u5c31\u53eb\u5ba2\u968f\u4e3b\u4f7f\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u773c\u73e0\u5b50\u4e00\u8f6c\uff0c\u9053\uff1a\u201c\u4f46\u6211\u4eec\u5374\u8fd8\u8981\u5546\u91cf\u5546\u91cf\uff0c\u4e0d\u77e5\u884c\u4e0d\u884c\uff1f\u201d \u3000\u3000\u6c34\u6bcd\u53ea\u6325\u4e86\u6325\u624b\uff0c\u5bab\u5357\u71d5\u5c31\u51b7\u51b7\u9053\uff1a\u201c\u53cd\u6b63\u4f60\u4eec\u5546\u91cf\u4e5f\u65e0\u7528\u7684\uff0c\u53bb\u5427\uff01\u201d \u3000\u3000\u80e1\u94c1\u82b1\u5c06\u9ec4\u9c81\u76f4\u548c\u6234\u72ec\u884c\u62c9\u5230\u4e00\u8fb9\uff0c\u5fcd\u4e0d\u4f4f\u7b11\u9053\uff1a\u201c\u770b\u6765\u8fd9\u6b21\u201c\u6c34\u6bcd\u201d\u9634\u59ec\u7684\u659b\u6597\u662f\u88c1 \u5b9a\u7684\u4e86\u201d\u9ec4\u9c81\u76f4\u5374\u76b1\u7709\u9053\uff1a\u201c\u53ef\u662f\uff0c\u5979\u65e2\u6562\u8fd9\u9ebd\u6837\u505a\uff0c\u8bf4\u4e0d\u5b9a\u6211\u5fc3\u6709\u81f4\u80dc\u7684\u628a\u63e1\u3002\u201d \u3000\u3000\u6234\u72ec\u884c\u7b11\u9053\uff1a\u201c\u4f60\u4e5f\u4e0d\u5fc5\u592a\u957f\u4ed6\u4eba\u7684\u5fd7\u6c14\uff0c\u706d\u81ea\u5df1\u7684\u5a01\u98ce\uff0c\u51ed\u6211\u4eec\u53c1\u4eba\u4e4b\u529b\uff0c\u4e00\u51b2\u800c \u4e0a\uff0c\u5c31\u7b97\u5979\u8fde\u4eba\u5e26\u67f1\u5b50\u90fd\u662f\u94c1\u94f8\u7684\uff0c\u4e5f\u96be\u514d\u8981\u88ab\u6211\u4eec\u51b2\u5012\u3002\u201d \u3000\u3000\u9ec4\u9c81\u76f4\u60f3\u6765\u60f3\u53bb\uff0c\u4e5f\u5b9e\u5728\u60f3\u4e0d\u51fa\u9634\u59ec\u80fd\u6709\u4ec0\u9ebd\u7a33\u64cd\u80dc\u7b97\u7684\u6cd5\u5b50\uff0c\u4f46\u4ed6\u4e3a\u4eba\u8c28\u614e\uff0c\u8fd8\u6709\u4e9b \u4e0d\u653e\u5fc3\uff0c\u9053\uff1a\u201c\u94c1\u4eba\u662f\u6b7b\u7684\uff0c\u5979\u5374\u662f\u6d3b\u7684\uff0c\u6211\u4eec\u53c1\u4eba\u4e00\u9f50\u5168\u529b\u51b2\u8fc7\u53bb\uff0c\u82e5\u662f\u88ab\u5979\u95ea\u5f00\uff0c\u90a3\u65f6 \u4f60\u4eec\u4e0a\u65e0\u53ef\u501f\u4e4b\u529b\uff0c\u4e0b\u65e0\u7acb\u8db3\u4e4b\u5730\uff0c\u6015\u5c31\u96be\u514d\u8981\u8dcc\u5165\u6e56\u4e2d\uff0c\u7eb5\u7136\u4e0d\u88ab\u5979\u4eec\u6240\u64d2\uff0c\u4e5f\u65e0\u989c\u518d\u8bd5 \u7b2c\u4e8c\u6b21\u4e86\u3002\u201d \u3000\u3000\u6234\u72ec\u884c\u4e5f\u4e0d\u7981\u76b1\u4e86\u76b1\u7709\uff0c\u9053\uff1a\u201c\u8fd9\u4e5f\u6709\u9053\u7406\u3002\u201d \u3000\u3000\u9ec4\u9c81\u76f4\u9053\uff1a\u201c\u662f\u4ee5\uff0c\u4ee5\u5728\u4e0b\u611a\u89c1\uff0c\u6211\u4eec\u53c1\u4e2a\u4eba\u7edd\u4e0d\u80fd\u540c\u65f6\u51fa\u624b\uff0c\u53ea\u56e0\u53c1\u4eba\u540c\u4e0a\uff0c\u867d\u7136\u529b \u91cf\u5927\u4e9b\uff0c\u4f46\u4e00\u51fb\u4e0d\u4e2d\uff0c\u5f8c\u65b9\u4fbf\u4e0d\u7ee7\u2026\u2026\u201d \u3000\u3000\u6234\u72ec\u884c\u9053\uff1a\u201c\u4f46\u6211\u4eec\u53c1\u4eba\u82e5\u662f\u5206\u5f00\u51fa\u624b\uff0c\u529b\u91cf\u5c82\u975e\u66f4\u4e0d\u591f\u4e86\u9ebd\uff1f\u201d \u3000\u3000\u9ec4\u6ed4\u76f4\u9053\uff1a\u201c\u6211\u5148\u4ee5\u957f\u8679\u8d2f\u65e5\u7684\u8eab\u6cd5\uff0c\u5411\u5979\u51b2\u8fc7\u53bb\uff0c\u770b\u5979\u5982\u4f55\u62db\u67b6\u95ea\u907f\uff0c\u80e1\u5144\u7d27\u968f\u5728\u6211 \u5f8c\u95e8\uff0c\u7b49\u6211\u4e00\u51fb\u4e0d\u4e2d\uff0c\u80e1\u5144\u518d\u5411\u5979\u8fdb\u653b\uff0c\u8fd9\u6b21\u5979\u8eab\u6cd5\u5df2\u53d8\u4e86\u4e00\u6b21\uff0c\u6c14\u529b\u5fc5\u5df2\u6d88\u8017\uff0c\u53d8\u5316\u5fc5\u5df2 \u7a0d\u7f13\uff0c\u5c31\u7b97\u80e1\u5144\u8fd9\u4e00\u51fb\u4ecd\u4e0d\u4e2d\uff0c\u7b49\u5230\u6234\u8001\u7237\u5b50\u4f5c\u7b2c\u53c1\u51fb\u65f6\uff0c\u5979\u5fc5\u5df2\u6210\u4e86\u5f3a\u5f29\u4e4b\u672b\uff0c\u6234\u8001\u7237\u5b50 \u5c31\u4e0d\u96be\u4e00\u51fb\u594f\u529f\u4e86\u3002\u201d \u3000\u3000\u6234\u72ec\u884c\u62d8\u638c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u6cd5\u5b50\u679c\u7136\u59a5\u5f53\u5f97\u591a\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u5374\u6447\u4e86\u6447\u5934\uff0c\u9053\uff1a\u201c\u8fd9\u6cd5\u5b50\u4e5f\u4e0d\u597d\u3002\u201d \u3000\u3000\u6234\u72ec\u884c\u9053\uff1a\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u5979\u771f\u529b\u663e\u7136\u5728\u6211\u4eec\u4e4b\u4e0a\uff0c\u800c\u4e14\u6211\u4eec\u5411\u5979\u8fdb\u653b\u65f6\uff0c\u8eab\u5b50\u51cc\u7a7a\uff0c\u5168\u65e0\u7740\u529b\u4e4b \u5904\uff0c\u5979\u5750\u5728\u6c34\u67f1\u4e0a\uff0c\u65e0\u8bba\u5982\u4f55\u603b\u6bd4\u6211\u4eec\u7a33\u4e9b\uff0c\u662f\u4ee5\u6211\u4eec\u82e5\u662f\u5206\u53c1\u6b21\u51fa\u624b\uff0c\u5f88\u53ef\u80fd\u90fd\u88ab\u5979\u4ee5\u638c \u65b9\u9707\u5f97\u4e00\u4e2a\u4e2a\u7684\u8dcc\u4e0b\u6765\u3002\u201d \u3000\u3000\u9ec4\u9c81\u76f4\u5931\u8272\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5979\u7684\u8eab\u6cd5\u6839\u672c\u4e0d\u5fc5\u53d8\u5316\uff0c\u53ea\u8981\u5b89\u5750\u5728\u4e0a\u9762\uff0c\u4ee5\u5148\u5929\u638c\u529b\u5411\u6211\u4eec \u51fb\u51fa\uff0c\u6211\u4eec\u662f\u4e07\u4e07\u62b5\u6321\u4e0d\u4f4f\u7684\u3002\u201d \u3000\u3000\u6234\u70db\u884c\u5374\u671b\u7740\u80e1\u94c1\u82b1\u7b11\u9053\uff1a\u201c\u4f60\u65e2\u7136\u8fd9\u9ebd\u6837\u8bf4\uff0c\u60f3\u5fc5\u5df2\u6709\u597d\u4e3b\u610f\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u538b\u4f4e\u58f0\u97f3\u9053\uff1a\u201c\u6700\u597d\u7684\u6cd5\u5b50\uff0c\u8fd8\u662f\u7531\u6211\u4eec\u53c1\u4e2a\u4eba\u4e00\u9f50\u51b2\u8fc7\u53bb\uff0c\u4f46\u6211\u5374\u5e76\u4e0d\u5411\u5979\u8fdb \u653b\uff0c\u8eab\u5b50\u51cc\u7a7a\u5f8c\uff0c\u6211\u5c31\u6539\u53d8\u65b9\u5411\uff0c\u53bb\u65a9\u5979\u5ea7\u4e0b\u7684\u6c34\u67f1\uff0c\u4f60\u4eec\u4e24\u4eba\u4e0d\u59a8\u865a\u5f20\u58f0\u5a01\uff0c\u6765\u63a9\u62a4\u6211\uff0c \u4e5f\u4e0d\u5fc5\u771f\u7684\u548c\u5979\u529b\u62da\u3002\u201d \u3000\u3000\u4ed6\u7b11\u4e86\u7b11\uff0c\u6309\u7740\u9053\uff1a\u201c\u53ea\u8981\u6c34\u67f1\u88ab\u51b2\u6563\uff0c\u5979\u8fd8\u80fd\u5728\u4e0a\u9762\u5750\u5f97\u4f4f\u9ebd\uff1f\u201d \u3000\u3000\u8fd9\u6cd5\u5b50\u8bf4\u51fa\u6765\uff0c\u8fde\u9ec4\u9c81\u76f4\u90fd\u4e0d\u7981\u559c\u52a8\u989c\u8272\u3002 \u3000\u3000\u6234\u72ec\u884c\u62c9\u4f4f\u80e1\u94c1\u82b1\u7684\u624b\uff0c\u7b11\u9053\uff1a\u201c\u6211\u95ef\u4e86\u51e0\u5341\u5e74\u6c5f\u6e56\uff0c\u60f3\u4e0d\u5230\u7adf\u4e0d\u5982\u4f60\u8fd9\u5e74\u7eaa\u8f7b\u8f7b\u7684\u5c0f \u4f19\u5b50\u3002\u201d \u3000\u3000\u9ec4\u9c81\u76f4\u9053\uff1a\u201c\u80e1\u5144\u679c\u7136\u662f\u667a\u52c7\u53cc\u5168\uff0c\u975e\u4eba\u80fd\u53ca\u3002\u201d \u3000\u3000\u6234\u72ec\u884c\u9053\uff1a\u201c\u8fd9\u5c31\u53eb\u505a\uff1a\u5c04\u4eba\u5148\u5c04\u9a6c\uff0c\u9a6c\u82e5\u5012\u4e86\uff0c\u4eba\u8fd8\u80fd\u5750\u5f97\u4f4f\u9ebd\uff1f\u201d \u3000\u3000\u4ed6\u4eec\u8d8a\u60f3\u8d8a\u89c9\u5f97\u8fd9\u6cd5\u5b50\u5b9e\u662f\u65e0\u61c8\u53ef\u51fb\uff0c\u5999\u4e0d\u53ef\u8a00\uff0c\u201c\u6c34\u6bcd\u201d\u9634\u59ec\u5c31\u7b97\u6709\u901a\u5929\u7684\u672c\u4e8b\uff0c\u6b64 \u756a\u4e5f\u5fc5\u8d25\u65e0\u7591\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u7b11\u9053\uff1a\u201c\u8fd9\u4e9b\u574f\u4e3b\u610f\uff0c\u6211\u672c\u6765\u662f\u60f3\u4e0d\u51fa\u6765\u7684\uff0c\u53ea\u4e0d\u8fc7\u8fd9\u4e24\u4e2a\u6708\u6765\u5929\u5929\u548c\u90a3\u8001\u81ed\u866b \u5728\u4e00\u8d77\u5df2\u6e10\u6e10\u88ab\u4ed6\u6559\u574f\u4e86\u3002\u201d \u3000\u3000\u9ec4\u9c81\u76f4\u6014\u4e86\u6014\uff0c\u9053\uff1a\u201c\u8001\u81ed\u866b\u662f\u8c01\uff1f\u201d \u3000\u3000\u6234\u72ec\u884c\u5931\u7b11\u9053\uff1a\u201c\u6b64\u4eba\u83ab\u975e\u81ed\u5f97\u5f88\uff0c\u624d\u4f1a\u6709\u8fd9\u9ebd\u6837\u4e00\u4e2a\u5916\u53f7\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u7b11\u9053\uff1a\u201c\u522b\u7684\u81ed\u866b\u90fd\u5f88\u5177\uff0c\u8fd9\u53ea\u8001\u81ed\u866b\u5374\u662f\u9999\u7684\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u7b49\u5230\u5bab\u5357\u71d5\u4e5f\u63a0\u4e0a\u6e56\u9762\uff0c\u53c8\u7b49\u4e86\u5f88\u4e45\uff0c\u624d\u7f13\u7f13\u5c06\u53f3\u8fb9\u90a3\u5757\u77f3\u5934\u63a8\u5f00\u4e00\u70b9\uff0c\u63a2\u51fa\u4e86 \u534a\u4e2a\u8eab\u5b50\u3002 \u3000\u3000\u53ea\u89c1\u77f3\u5f8c\u679c\u7136\u6709\u6761\u79d8\u5bc6\u7684\u6c34\u9053\uff0c\u79d8\u9053\u4e2d\u7684\u6d41\u6c34\u4e0e\u6e56\u6c34\u76f8\u901a\uff0c\u4ea6\u7136\u6e05\u6f88\u5982\u955c\uff0c\u6781\u76ee\u671b\u592b\uff0c \u4e0d\u89c1\u4eba\u5f71\u3002 \u3000\u3000\u695a\u7559\u9999\u867d\u7136\u6781\u62c5\u5fc3\u80e1\u94c1\u82b1\u4ed6\u4eec\u7684\u5b89\u5371\uff0c\u4f46\u8fd9\u673a\u4f1a\u5374\u7edd\u4e0d\u53ef\u5931\uff0c\u53ea\u8981\u4ed6\u9f99\u627e\u51fa\u9634\u59ec\u7684\u79d8 \u5bc6\uff0c\u5c31\u80fd\u6551\u5f97\u4e86\u4ed6\u4eec\u3002 \u3000\u3000\u5426\u5219\uff0c\u4ed6\u51fa\u53bb\u4e5f\u6ca1\u6709\u7528\u3002 \u3000\u3000\u6c34\u9053\u4e24\u65c1\u90fd\u94fa\u7740\u767d\u7389\u822c\u7684\u5927\u7406\u77f3\u677f\uff0c\u6d41\u6c34\u4e5f\u4f3c\u5728\u95ea\u95ea\u53d1\u5149\uff0c\u695a\u7559\u9999\u6e38\u9c7c\u822c\u6ed1\u4e86\u8fdb\u53bb\uff0c\u7acb \u523b\u5c31\u77e5\u9053\u4e0d\u5999\u3002 \u3000\u3000\u4ed6\u8bb0\u5f97\u5bab\u5357\u71d5\u65b9\u5411\u8fd9\u8fb9\u6446\u4e86\u6446\u624b\uff0c\u7136\u5f8c\u5730\u4e0b\u7684\u6cc9\u6c34\u624d\u55b7\u6fc0\u800c\u51fa\uff0c\u90a3\u9ebd\uff0c\u8fd9\u6c34\u9053\u7684\u95e8\u6237 \u5f8c\uff0c\u663e\u7136\u5fc5\u5b9a\u6709\u4eba\u5728\u64cd\u7eb5\u55b7\u6cc9\u7684\u67a2\u7ebd\u3002 \u3000\u3000\u695a\u7559\u9999\u60f3\u5230\u8fd9\u70b9\u65f6\uff0c\u5df2\u7ecf\u592a\u8fdf\u4e86\u3002 \u3000\u3000\u4e00\u67c4\u5206\u6c34\u523a\u5df2\u5411\u4ed6\u523a\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u8fd9\u4e00\u51fb\u81ea\u7136\u672b\u5fc5\u80fd\u4f24\u5f97\u4e86\u4ed6\uff0c\u4f46\u7cdf\u7cd5\u7684\u662f\uff0c\u53ea\u8981\u4ed6\u884c\u8e2a\u4e00\u88ab\u795e\u6c34\u5bab\u4e2d\u7684\u4eba\u53d1\u89c9\uff0c\u4e0d\u4f46\u4ed6 \u81ea\u5df1\u6240\u6709\u7684\u8ba1\u5230\u5168\u65e0\u6cd5\u5b9e\u73b0\uff0c\u90a3\u767d\u8863\u5987\u4eba\u4e5f\u8981\u88ab\u8fde\u7d2f\u4e86\uff0c\u4ed6\u5c31\u7b97\u80fd\u5c06\u51fa\u624b\u7684\u8fd9\u4eba\u6740\u6b7b\uff0c\u4f46\u884c \u8e2a\u8fd8\u662f\u96be\u514d\u88ab\u6cc4\u9732\u3002 \u3000\u3000\u4ed6\u884c\u52a8\u4e00\u76f4\u90fd\u5f88\u5c0f\u5fc3\uff0c\u4e0d\u60f3\u5728\u6700\u5f8c\u5df2\u63a5\u8fd1\u6210\u529f\u65f6\uff0c\u5374\u8fd8\u662f\u72af\u4e86\u4e00\u6b21\u9519\u8bef\u2014\u2014\u4e00\u6b21\u81f4\u547d\u7684 \u9519\u8bef\u3002 \u3000\u3000\u201c\u6c34\u6bcd\u201d\u9634\u59ec\u4ecd\u7136\u7aef\u5750\u5728\u6c34\u67f1\u4e0a\u52a8\u4e5f\u4e0d\u52a8\uff0c\u5f77\u4f5b\u5c31\u7b97\u8981\u5979\u5728\u4e0a\u9762\u5750\u4e0a\u4e2a\u53c1\u5929\u4e94\u5929\uff0c\u5979\u7167 \u6837\u8fd8\u662f\u7a33\u5982\u6cf0\u5c71\u7684\u3002 \u3000\u3000\u7206\u5357\u71d5\u5374\u6709\u4e9b\u4e0d\u8010\u70e6\u4e86\uff0c\u76b1\u7709\u9053\uff1a\u201c\u4f60\u4eec\u5546\u91cf\u5b8c\u4e86\u5417\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u5b8c\u4e86\u3002\u201d \u3000\u3000\u7206\u5357\u71d5\u76ee\u5149\u95ea\u52a8\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u5c31\u51ed\u4f60\u4eec\u53c1\u4eba\uff0c\u96be\u9053\u8fd8\u80fd\u5546\u91cf\u51fa\u4ec0\u9ebd\u5999\u8ba1\u4e0d\u6210\uff1f\u201d \u3000\u3000\u5979\u8fd9\u8bdd\u662f\u671b\u7740\u9ec4\u9c81\u76f4\u8bf4\u7684\u3002 \u3000\u3000\u9ec4\u9c81\u76f4\u679c\u7136\u9053\uff1a\u201c\u5728\u4e0b\u7b49\u5546\u91cf\u7684\u2026\u2026\u201d \u3000\u3000\u4ed6\u5c45\u7136\u50cf\u662f\u53c8\u8981\u8bf4\u8001\u5b9e\u8bdd\u4e86\uff0c\u6234\u72ec\u884c\u548c\u80e1\u94c1\u82b1\u4e0d\u7ea6\u800c\u540c\uff0c\u5927\u58f0\u9053\uff1a\u201c\u6211\u4eec\u8bdd\u5df2\u8bf4\u591f\uff0c\u52a8 \u624b\u5427\uff01\u201d \u3000\u3000\u4ed6\u4eec\u65e9\u5df2\u7ea6\u597d\u4e86\u624b\u52bf\uff0c\u6b64\u523b\u80e1\u94c1\u82b1\u4e00\u6325\u624b\uff0c\u53c1\u4e2a\u4eba\u5c31\u7acb\u523b\u5e76\u80a9\u63a0\u8d77\uff0c\u5200\u5149\u5251\u5f71\u5df2\u5316\u505a\u98de \u8679\uff0c\u6a2a\u8d2f\u4e86\u6e56\u9762\u3002 \u3000\u3000\u8981\u77e5\u201c\u6c34\u6bcd\u201d\u9634\u59ec\u5ea7\u4e0b\u7684\u6c34\u67f1\u5df2\u9ad8\u6709\u53c1\u4e08\uff0c\u6c34\u67f1\u5728\u6e56\u5fc3\uff0c\u8ddd\u79bb\u6e56\u5cb8\u4fbf\u4e0d\u6b62\u516d\u4e08\uff0c\u6234\u72ec\u884c \u4ed6\u4eec\u8f7b\u529f\u5c31\u7b97\u9ad8\u6781\uff0c\u4e5f\u96be\u4e00\u63a0\u516d\u4e08\u3002 \u3000\u3000\u4f46\u4ed6\u4eec\u5374\u662f\u81ea\u6e56\u7554\u7684\u4e00\u5757\u5de8\u77f3\u4e0a\u63a0\u8fc7\u53bb\u7684\uff0c\u8fd9\u5de8\u77f3\u7a81\u5165\u6e56\u6c34\u4e2d\uff0c\u8ddd\u79bb\u201c\u6c34\u6bcd\u201d\u5df2\u53ea\u6709\u53c1 \u4e08\u5de6\u53f3\u4e86\u3002 \u3000\u3000\u8981\u4ed6\u4eec\u4e00\u63a0\u53c1\u4e08\uff0c\u5e76\u975e\u96be\u4e8b\u3002 \u3000\u3000\u8fd9\u65f6\u4ed6\u4eec\u80dc\u7b97\u5728\u63e1\uff0c\u66f4\u662f\u7cbe\u795e\u767e\u500d\uff0c\u6bcf\u4e2a\u4eba\u90fd\u5c06\u81ea\u5df1\u7684\u6b66\u529f\u53d1\u6325\u5230\u6781\u81f4\uff0c\u8fdc\u8fdc\u671b\u592b\uff0c\u53ea \u89c1\u53c1\u4eba\u5982\u94f6\u6c49\u53c1\u4ed9\uff0c\u5e26\u7740\u957f\u8679\u98de\u5929\u800c\u8d77\uff0c\u5c31\u8fde\u795e\u6c34\u5bab\u7684\u95e8\u4e0b\u5f1f\u5b50\u89c1\u4e86\uff0c\u4e5f\u4e0d\u7981\u4e3a\u4e4b\u76ee\u52a8\u795e \u79fb\u3002 \u3000\u3000\u6c34\u6bcd\u4ecd\u7aef\u5750\u672b\u52a8\u3002\u773c\u89c1\u53c1\u4eba\u8ddd\u79bb\u5979\u5df2\u4e0d\u53ca\u516b\u5c3a\uff0c\u80e1\u94c1\u82b1\u5ffd\u7136\u957f\u5578\u4e00\u58f0\uff0c\u8eab\u5f62\u9aa4\u53d8\uff0c\u6325\u5200 \u5411\u201c\u6c34\u6bcd\u201d\u5ea7\u4e0b\u6c34\u67f1\u51b2\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u4e5f\u5c31\u5728\u8fd9\u65f6\uff0c\u6c34\u6bcd\u7684\u8eab\u5b50\u5ffd\u7136\u5411\u4e0b\u4e00\u6c88\uff0c\u53cc\u624b\u5728\u6c34\u67f1\u4e0a\u6309\u4e86\u6309\uff0c\u6c34\u67f1\u4e0a\u7acb\u523b\u5206\u51fa\u53c1\u9053\u5206 \u6cc9\uff0c\u76f4\u5c04\u800c\u51fa\u3002 \u3000\u3000\u55b7\u6cc9\u7684\u6c34\u529b\u672c\u5df2\u6781\u5f3a\uff0c\u6b64\u523b\u518d\u52a0\u4e0a\u6c34\u6bcd\u60ca\u4eba\u7684\u638c\u529b\uff0c\u6c34\u7bad\u98de\u51fa\uff0c\u5176\u901f\u5ea6\u548c\u529b\u91cf\u7eb5\u7136\u96f7\u9706 \u95ea\u7535\u4e5f\u4e0d\u53ef\u6bd4\u62df\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u4ed6\u4eec\u7684\u8eab\u5f62\u672c\u5728\u5168\u529b\u524d\u6251\uff0c\u8981\u95ea\u907f\u90a3\u91cc\u8fd8\u6765\u5f97\u53ca\uff0c\u53ea\u89c1\u4e00\u7247\u94f6\u5149\u8fce\u9762\u800c\u6765\uff0c\u80f8\u53e3 \u7acb\u523b\u611f\u89c9\u5230\u4e00\u79cd\u7a7a\u524d\u672a\u6709\uff0c\u65e0\u53ef\u6bd4\u62df\u7684\u649e\u51fb\u4e4b\u529b\uff0c\u5f77\u4f5b\u56db\u9762\u7684\u5c71\u5cf0\uff0c\u5168\u90fd\u5411\u4ed6\u4eec\u538b\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u4ed6\u4eec\u53ea\u89c9\u5589\u5934\u4e00\u751c\uff0c\u773c\u524d\u4e00\u9ed1\uff0c\u5df2\u6655\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u695a\u7559\u9999\u7684\u8eab\u5b50\u5728\u6c34\u4e2d\u6bd4\u5728\u9646\u5730\u4e0a\u66f4\u7075\u6d3b\uff0c\u53ea\u8f7b\u8f7b\u4e00\u6ed1\uff0c\u5df2\u907f\u5f00\u4e86\u90a3\u67c4\u6765\u52bf\u5e76\u4e0d\u6162\u7684\u5206\u6c34 \u523a\u3002 \u3000\u3000\u90a3\u5c11\u5973\u8eab\u624b\u4e5f\u4e0d\u5f31\uff0c\u795e\u6c34\u5bab\u95e8\u4e0b\u7684\u5f1f\u5b50\uff0c\u90fd\u7ec3\u6709\u4e00\u79cd\u5728\u6c34\u91cc\u52a8\u624b\u7684\u72ec\u95e8\u62db\u5f0f\uff0c\u5206\u6c34\u523a\u4e5f \u662f\u5728\u6c34\u4e2d\u52a8\u624b\u7684\u72ec\u95e8\u6b66\u5668\u3002 \u3000\u3000\u5979\u7684\u624b\u8155\u53ea\u4e00\u6c88\uff0c\u5206\u6c34\u523a\u5df2\u5947\u5999\u7684\u6539\u53d8\u4e86\u65b9\u5411\u3002 \u3000\u3000\u4f46\u8fd9\u6b21\u5979\u4e00\u62db\u8fd8\u672b\u524c\u51fa\uff0c\u5df2\u89c9\u5f97\u4e00\u9635\u9ebb\u75f9\u4e4b\u611f\u7531\u5979\u8098\u95ee\u7684\u201c\u66f2\u6c60\u201d\u7a74\u4f20\u9694\u4e86\u5979\u5168\u8eab\u3002 \u3000\u3000\u5979\u7edd\u672b\u60f3\u5230\u5bf9\u65b9\u5728\u6c34\u4e2d\u70b9\u7a74\uff0c\u800c\u4e14\u624b\u52a8\u8fd8\u80fd\u5982\u6b64\u5f3a\uff0c\u5927\u60ca\u4e4b\u4e0b\uff0c\u5931\u58f0\u60ca\u547c\uff0c\u4f46\u5634\u521a\u5f20 \u5f00\uff0c\u4e00\u53e3\u6c34\u5df2\u704c\u4e86\u8fdb\u53bb\u3002 \u3000\u3000\u695a\u7559\u9999\u7528\u4e24\u53ea\u624b\u6258\u7740\u5979\u7684\u8eab\u5b50\uff0c\u53cc\u8db3\u5212\u6c34\uff0c\u5411\u6c34\u9053\u4e2d\u6e38\u6e38\u4e86\u8fdb\u53bb\uff0c\u8fd9\u5c11\u5973\u5ffd\u7136\u5931\u8e2a\uff0c \u201c\u6c34\u6bcd\u201d\u9634\u59ec\u56de\u6765\u65f6\u5fc5\u5b9a\u4f1a\u53d1\u73b0\u7684\uff0c\u5979\u7acb\u523b\u5c31\u4f1a\u60f3\u5230\u7981\u5bab\u4e2d\u5df2\u6f5c\u5165\u654c\u4eba\uff0c\u695a\u7559\u9999\u7684\u884c\u8e2a\u7acb\u523b \u5c31\u4f1a\u88ab\u53d1\u73b0\u3002 \u3000\u3000\u53ef\u662f\u695a\u7559\u9999\u7eb5\u7136\u660e\u77e5\u5982\u6b64\uff0c\u4e5f\u53ea\u6709\u5192\u9669\uff0c\u8fd9\u673a\u4f1a\u4ed6\u7edd\u4e0d\u80fd\u9519\u8fc7\uff0c\u4f55\u51b5\uff0c\u4ed6\u6839\u672c\u4e5f\u5df2\u6ca1\u6709 \u9009\u62e9\u7684\u9980\u5730\u3002 \u3000\u3000\u4ed6\u4e00\u5b9a\u8981\u5728\u201c\u6c34\u6bcd\u201d\u9634\u59ec\u56de\u6765\u4e4b\u524d\uff0c\u627e\u51fa\u5979\u7684\u79d8\u5bc6\u548c\u5f31\u70b9\uff0c\u4ed6\u4e5f\u53ea\u5e0c\u671b\u80e1\u94c1\u82b1\u4ed6\u4eec\u80fd\u591a \u62d6\u4f4f\u5979\u7247\u523b\u3002 \u3000\u3000\u5728\u8fd9\u79cd\u60c5\u51b5\u4e0b\uff0c\u5f53\u771f\u662f\u4e1d\u6beb\u65f6\u95f4\u4e5f\u4e0d\u80fd\u6d6a\u8d39\u3002 \u3000\u3000\u6c34\u9053\u867d\u4e0d\u77ed\uff0c\u4f46\u695a\u7559\u9999\u5f88\u5feb\u7684\u5c31\u8f6c\u4e86\u53c1\u4e2a\u5f2f\uff0c\u5230\u8fbe\u5c3d\u5934\uff0c\u6c34\u9762\u4e0a\u9690\u9690\u5df2\u53ef\u770b\u5230\u706f\u5149\u95ea \u52a8\u3002 \u3000\u3000\u695a\u7559\u9999\u7b97\u51c6\u4e0a\u9762\u5fc5\u5b9a\u8fd8\u6709\u4eba\u7559\u5b88\uff0c\u4ed6\u5e76\u6ca1\u6709\u8003\u8651\u591a\u4e45\uff0c\u5c31\u5c06\u638c\u4e2d\u8fd9\u5c11\u5973\u7684\u8eab\u5b50\u6258\u4e0a\u4e86\u6c34 \u9762\u3002 \u3000\u3000\u6c5f\u6e56\u4e2d\u4eba\u5bf9\u6c34\u6bcd\u7684\u7981\u5bab\u66fe\u7ecf\u6709\u8fc7\u8bb8\u591a\u79cd\u60f3\u50cf\uff0c\u56e0\u4e3a\u6839\u672c\u4ece\u65e0\u4e00\u4eba\u5230\u8fc7\u8fd9\u5730\u65b9\uff0c\u662f\u4ee5\u5c31\u89c9 \u5f97\u66f4\u795e\u79d8\u3002 \u3000\u3000\u6709\u4eba\u751a\u81f3\u5c06\u8fd9\u5730\u65b9\u60f3\u50cf\u6210\u5929\u5bab\u4e00\u6837\uff0c\u5176\u5b9e\uff0c\u8fd9\u4e5f\u53ea\u4e0d\u8fc7\u662f\u95f4\u4ee5\u5927\u7406\u77f3\u780c\u6210\u7684\u5730\u5ba4\uff0c\u5e76\u6ca1 \u6709\u4ec0\u9ebd\u5341\u5206\u534e\u4e3d\u7684\u9648\u8bbe\u3002 \u3000\u3000\u201c\u6c34\u6bcd\u201d\u9634\u59ec\u663e\u7136\u5e76\u4e0d\u662f\u4e2a\u6ce8\u610f\u4eab\u53d7\u7684\u4eba\uff0c\u5979\u53ea\u662f\u5c06\u8fd9\u5730\u65b9\u4fdd\u6301\u7edd\u5bf9\u6d01\u51c0\uff0c\u4efb\u4f55\u5730\u65b9\u90fd \u627e\u4e0d\u51fa\u4e00\u7c92\u7070\u5c18\u3002 \u3000\u3000\u662f\u4ee5\u56db\u9762\u7684\u5927\u7406\u77f3\u770b\u6765\u5c31\u50cf\u767d\u7389\u822c\u6676\u83b9\u751f\u5149\u3002 \u3000\u3000\u6c34\u9053\u7684\u51fa\u53e3\uff0c\u662f\u4e2a\u77f3\u780c\u7684\u5c0f\u6c60\uff0c\u6c60\u7554\u7684\u77f3\u5934\u4e5f\u5e76\u6ca1\u6709\u4ec0\u9ebd\u5938\u5f20\u7684\u96d5\u523b\uff0c\u7b80\u5355\u7684\u7ebf\u6761\u770b\u6765 \u53cd\u800c\u4efd\u5916\u660e\u6717\u60a6\u76ee\u3002 \u3000\u3000\u8fd9\u65f6\u6c60\u7554\u6b63\u6709\u4e24\u4e2a\u4e5f\u5f88\u7f8e\u4e3d\u7684\u5c11\u5973\u5728\u6574\u7406\u7740\u841d\u4e1d\uff0c\u770b\u6765\u65e2\u4e0d\u50cf\u8695\u4e1d\uff0c\u4e5f\u4e0d\u50cf\u94f6\u4e1d\uff0c\u8d28\u5730 \u8f7b\u67d4\u800c\u575a\u97e7\uff0c\u6b63\u662f\u5979\u4eec\u505a\u8863\u670d\u7684\u8d28\u6599\u3002 \u3000\u3000\u5979\u4eec\u53d1\u73b0\u540c\u95e8\u7684\u8eab\u5b50\u5ffd\u7136\u81ea\u6c34\u6c60\u4e2d\u6d6e\u51fa\u6765\u65f6\uff0c\u9762\u4e0a\u90fd\u9732\u51fa\u60ca\u5f02\u4e4b\u8272\uff0c\u7acb\u523b\u8dc3\u4e0b\u53bb\u5c06\u5979\u62c9 \u8d77\u6765\u3002 \u3000\u3000\u5979\u4eec\u8fc7\u60ef\u4e86\u5355\u8c03\u3001\u5bc2\u5bde\uff0c\u800c\u4e14\u5e73\u9759\u7684\u751f\u6d3b\uff0c\u5bf9\u4efb\u4f55\u610f\u5916\u7684\u4e8b\u90fd\u4e0d\u77e5\u8be5\u5982\u4f55\u5e94\u4ed8\uff0c\u66f4\u672b\u60f3 \u5230\u6c34\u4e0b\u9762\u8fd8\u6709\u4eba\u3002 \u3000\u3000\u695a\u7559\u9999\u5f88\u5bb9\u6613\u7684\u53c8\u70b9\u4e86\u5979\u4eec\u7684\u7a74\u9053\uff0c\u7136\u5f8c\u5c06\u5979\u4eec\u90fd\u62ac\u51fa\u6c34\u6c60\uff0c\u770b\u5230\u5979\u4eec\u53c1\u5f20\u7f8e\u4e3d\u7684\u8138\u4e0a \u72b9\u51dd\u7ed3\u7740\u60ca\u60b8\u4e4b\u8272\u3002 \u3000\u3000\u695a\u7559\u9999\u4e0d\u89c9\u5bf9\u5979\u4eec\u62b1\u6b49\u7684\u4e00\u7b11\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u6211\u7edd\u6ca1\u6709\u4f24\u5bb3\u4f60\u4eec\u7684\u610f\u601d\uff0c\u4f60\u4eec\u53ea\u8981\u4e56\u4e56\u7684 \u4f11\u606f\u4e00\u4f1a\u513f\u5c31\u597d\u4e86\u3002\u201d \u3000\u3000\u4ed6\u7684\u5fae\u7b11\u662f\u90a3\u9ebd\u4eb2\u5207\u800c\u6e29\u67d4\uff0c\u82e5\u8bf4\u4e16\u4e0a\u53ea\u6709\u4e00\u4e2a\u4eba\u7684\u5fae\u7b11\u80fd\u4ee4\u53d7\u4e86\u60ca\u7684\u5973\u5b69\u5b50\u5b89\u4e0b\u5fc3 \u6765\uff0c\u90a3\u4eba\u5c31\u662f\u695a\u7559\u9999\u4e86\u3002 \u3000\u3000\u5c11\u5973\u4eec\u7684\u8138\u8272\u867d\u4ecd\u662f\u82cd\u767d\u7684\uff0c\u4f46\u76ee\u5149\u5df2\u6e10\u6e10\u5e73\u9759\u4e0b\u6765\uff0c\u5979\u4eec\u867d\u4e0d\u77e5\u9053\u8fd9\u82f1\u4fca\u7684\u7537\u4eba\u662f \u8c01\uff0c\u5374\u89c9\u5f97\u4ed6\u8bf4\u51fa\u7684\u6bcf\u53e5\u8bdd\uff0c\u90fd\u53ef\u4ee5\u4fe1\u4efb\u2014\u2014\u695a\u7559\u9999\u6709\u79cd\u5947\u5f02\u7684\u9b45\u529b\uff0c\u603b\u80fd\u4ee4\u5973\u5b69\u5b50\u89c9\u5f97\u4ed6 \u662f\u4e2a\u5f88\u53ef\u4fe1\u4efb\u7684\u7537\u4eba\u3002 \u3000\u3000\u4ed6\u4e5f\u4ece\u6765\u6ca1\u6709\u8ba9\u5979\u4eec\u5931\u671b\u3002 \u3000\u3000\u77f3\u5ba4\u4e2d\u53ea\u6709\u4e00\u5e8a\u4e00\u51e0\uff0c\u4e00\u4e2a\u5e76\u4e0d\u592a\u5927\u7684\u8863\u67dc\uff0c\u548c\u4e00\u4e9b\u94fa\u5728\u5730\u4e0a\u7684\u5750\u57ab\uff0c\u9664\u4e86\u8fd9\u4e9b\u751f\u6d3b\u4e0a \u6700\u4f4e\u9650\u5ea6\u7684\u5fc5\u9700\u4e4b\u7269\u5916\uff0c\u8fd9\u5c4b\u5b50\u91cc\u7b80\u76f4\u6ca1\u6709\u4e00\u6837\u4e1c\u897f\u662f\u591a\u9980\u7684\uff0c\u53ef\u89c1\u201c\u6c34\u6bcd\u201d\u9634\u59ec\u975e\u4f46\u6d01\u7656 \u5f88\u6df1\uff0c\u800c\u4e14\u751f\u6d3b\u7b80\u5355\uff0c\u81ea\u5f8b\u6781\u4e25\u3002\u548c\u6c5f\u6e56\u4e2d\u4eba\u60f3\u50cf\u4e2d\u7684\u201c\u6c34\u6bcd\u201d\u9634\u59ec\u5b8c\u5168\u4e0d\u540c\u3002 \u3000\u3000\u8fd9\u6837\u7684\u4eba\uff0c\u600e\u4f1a\u6709\u4ec0\u9ebd\u79d8\u5bc6\u548c\u5f31\u70b9\uff1f \u3000\u3000\u695a\u7559\u9999\u4e5f\u627e\u4e0d\u5230\u53ef\u5c06\u8fd9\u53c1\u4e2a\u5c11\u5973\u85cf\u8d77\u6765\u7684\u5730\u65b9\uff0c\u4ed6\u6c88\u541f\u4e86\u534a\u664c\uff0c\u5ffd\u7136\u89e3\u5f00\u4e00\u4e2a\u5c11\u5973\u7684\u7a74 \u9053\uff0c\u5fae\u7b11\u7740\u9053\uff1a\u201c\u4f60\u77e5\u9053\u53ef\u6709\u4ec0\u9ebd\u5730\u65b9\u53ef\u4ee5\u8ba9\u6211\u5c06\u4f60\u4eec\u85cf\u8d77\u6765\u9ebd\uff1f\u201d \u3000\u3000\u82e5\u662f\u6362\u4e86\u522b\u4eba\u95ee\u8fd9\u53e5\u8bdd\uff0c\u8fd9\u5c11\u5973\u6b7b\u4e5f\u4e0d\u80af\u8bf4\u7684\u3002 \u3000\u3000\u4f46\u695a\u7559\u9999\u7684\u6001\u5ea6\u5374\u5982\u6b64\u8bda\u6073\uff0c\u5982\u6b64\u4eb2\u5207\uff0c\u4ee4\u5979\u89c9\u5f97\u5c31\u5f77\u4f5b\u662f\u4e00\u4e2a\u8001\u670b\u53cb\u5411\u5979\u5618\u5bd2\u95ee\u6696\u3002 \u3000\u3000\u4ee4\u5979\u89c9\u5f97\u4ed6\u95ee\u8fd9\u53e5\u8bdd\u53ea\u4e0d\u8fc7\u662f\u4e3a\u4e86\u5173\u5fc3\u5979\uff0c\u662f\u4e3a\u4e86\u5979\u597d\uff0c\u8fd9\u5b9e\u5728\u662f\u4efb\u4f55\u5973\u5b69\u5b50\u90fd\u65e0\u6cd5\u62d2 \u7edd\u7684\u3002 \u3000\u3000\u5979\u671b\u7740\u4ed6\u7684\u5fae\u7b11\uff0c\u4e0d\u7531\u81ea\u4e3b\u7684\u5c31\u7b54\u9053\uff1a\u201c\u4f60\u770b\u5230\u4e86\u5bf9\u9762\u5899\u4e0a\u7684\u90a3\u76cf\u706f\u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u662f\u4e0d\u662f\u8863\u67dc\u65c1\u7684\u90a3\u76cf\uff1f\u201d \u3000\u3000\u5c11\u5973\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u53ea\u8981\u5c06\u90a3\u76cf\u706f\u5411\u5de6\u8fb9\u4e00\u6273\uff0c\u5c31\u4f1a\u73b0\u51fa\u4e00\u6247\u95e8\uff0c\u4f60\u5c06\u6211\u4eec\u85cf\u5230\u90a3\u91cc\u9762 \u53bb\u5c31\u4e0d\u4f1a\u6709\u4eba\u53d1\u73b0\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u6c88\u541f\u7740\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u4e0d\u77e5\u90a3\u5730\u65b9\u662f\u5426\u5b89\u5168\uff1f\u201d \u3000\u3000\u5c11\u5973\u9053\uff1a\u201c\u5f88\u5c11\u6709\u4eba\u4f1a\u5230\u90a3\u91cc\u53bb\u7684\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u8c22\u8c22\u4f60\uff0c\u4ee5\u5f8c\u4f60\u82e5\u79bb\u5f00\u795e\u6c34\u5bab\uff0c\u4e0d\u59a8\u53bb\u627e\u6211\uff0c\u6211\u4e00\u5b9a\u4f1a\u5e26\u4f60\u5230\u5f88 \u591a\u597d\u73a9\u7684\u5730\u65b9\u53bb\u3002\u201d \u3000\u3000\u90a3\u5c11\u5973\u5fcd\u4e0d\u4f4f\u5c55\u989c\u4e00\u7b11\uff0c\u7ea2\u7740\u7751\u9053\uff1a\u201c\u8c22\u8c22\u4f60\u3002\u201d \u3000\u3000\u5979\u521a\u8bf4\u8fc7\u4e86\u201c\u8c22\u8c22\u201d\uff0c\u7a74\u9053\u5c31\u53c8\u88ab\u70b9\u4f4f\u4e86\u3002 \u3000\u3000\u695a\u7559\u9999\u679c\u7136\u627e\u5230\u4e86\u90a3\u6247\u95e8\uff0c\u5c06\u5979\u4eec\u85cf\u4e86\u8fdb\u53bb\u3002 \u3000\u3000\u4ed6\u672c\u53ef\u518d\u95ee\u5979\u4eec\u8bb8\u591a\u8bdd\u7684\uff0c\u4f46\u4ed6\u77e5\u9053\u5979\u4eec\u82e5\u8bf4\u5f97\u592a\u591a\uff0c\u82e5\u662f\u4e07\u4e00\u88ab\u6c34\u6bcd\u77e5\u9053\uff0c\u90a3\u5f8c\u679c\u5c31 \u4e0d\u582a\u8bbe\u60f3\u3002 \u3000\u3000\u4ed6\u4ece\u4e0d\u5fcd\u4f24\u5bb3\u4e00\u4e2a\u5bf9\u4ed6\u5982\u6b64\u4fe1\u4efb\u7684\u4eba\u3002 \u3000\u3000\u4f55\u51b5\uff0c\u4ed6\u4e5f\u77e5\u9053\uff0c\u81ea\u5df1\u82e5\u662f\u95ee\u5f97\u5927\u591a\uff0c\u5979\u4eec\u5c31\u96be\u514d\u4f1a\u63d0\u9ad8\u8b66\u89c9\uff0c\u4e0d\u518d\u5bf9\u4ed6\u5982\u6b64\u4fe1\u4efb\u4e86\u3002 \u3000\u3000\u4ed6\u4e5f\u4ece\u6765\u4e0d\u613f\u7834\u574f\u4e00\u4e2a\u5c11\u5973\u5bf9\u4ed6\u7684\u597d\u5370\u8c61\u3002 \u3000\u3000\u7ebf\u6761\u7b80\u5355\u7684\u77ed\u51e0\uff0c\u53ea\u6709\u4e00\u53ea\u767d\u7389\u8336\u76cf\uff0c\u5ea7\u57ab\u662f\u7528\u767d\u8272\u7684\u9a6c\u5c3e\u8349\u7f16\u6210\u7684\uff0c\u867d\u7136\u6709\u5f88\u591a\u5973\u4eba \u90fd\u559c\u6b22\u5c06\u4e00\u4e9b\u8d34\u8eab\u7684\u79d8\u5bc6\u85cf\u5728\u6795\u5934\u4e0b\uff0c\u5e8a\u8925\u91cc\uff0c\u4f46\u201c\u6c34\u6bcd\u201d\u9634\u59ec\u5374\u65e0\u8bba\u5982\u4f55\u4e5f\u4e0d\u50cf\u662f\u8fd9\u79cd\u5973 \u4eba\uff0c\u5979\u7684\u5e8a\u5355\u8fde\u4e00\u6761\u7ec9\u7eb9\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u6240\u4ee5\u8fd9\u5c4b\u91cc\u552f\u4e00\u53ef\u4ee5\u6536\u85cf\u79d8\u5bc6\u7684\u5730\u65b9\uff0c\u5c31\u662f\u90a3\u8863\u67dc\u3002 \u3000\u3000\u695a\u7559\u9999\u5583\u5583\u9053\uff1a\u201c\u62b1\u6b49\u5f97\u5f88\uff0c\u6211\u5e76\u4e0d\u662f\u60f3\u523a\u63a2\u4f60\u7684\u79d8\u5bc6\uff0c\u53ea\u4e0d\u8fc7\u60f3\u6551\u81ea\u5df1\u7684\u547d\u800c\u5df2\uff0c\u53ea \u671b\u4f60\u8863\u67dc\u91cc\u6ca1\u6709\u8ba9\u6211\u770b\u4e86\u4f1a\u8138\u7ea2\u7684\u4e1c\u897f\u3002\u201d \u3000\u3000\u8863\u6846\u91cc\u6240\u6709\u7684\u4e1c\u897f\u7b80\u5355\u5f97\u53ef\u4ee5\u516c\u5f00\u5230\u9a6c\u8def\u4e0a\u53bb\u3002 \u3000\u3000\u9664\u4e86\u4e00\u4e9b\u7b80\u5355\u7684\u8863\u670d\u5916\uff0c\u91cc\u9762\u4ec0\u9ebd\u90fd\u6ca1\u6709\uff0c\u5947\u602a\u7684\u53ea\u662f\uff0c\u5176\u4e2d\u7adf\u6709\u4e00\u4ef6\u662f\u7537\u4eba\u7684\u8863\u670d\u3002 \u3000\u3000\u695a\u7559\u9999\u63d0\u8d77\u4e00\u4ef6\u9ebb\u5e03\u7684\u77ed\u88e4\uff0c\u4ed6\u600e\u9ebd\u4e5f\u770b\u4e0d\u51fa\u4e16\u4e0a\u4f1a\u6709\u5973\u4eba\u7a7f\u8fd9\u79cd\u77ed\u88e4\uff0c\u8fd9\u77ed\u88e4\u548c\u4ed6\u7a7f \u7684\u51e0\u4e4e\u5b8c\u5168\u4e00\u6837\u3002 \u3000\u3000\u795e\u6c34\u5bab\u91cc\u96be\u9053\u7adf\u85cf\u7740\u4e2a\u7537\u4eba\uff1f \u3000\u3000\u8fd9\u96be\u9053\u662f\u201c\u6c34\u6bcd\u201d\u9634\u59ec\u7684\u79d8\u5bc6\uff1f \u3000\u3000\u695a\u7559\u9999\u5b9e\u5728\u4e0d\u6562\u76f8\u4fe1\uff0c\u5374\u53c8\u4e0d\u5f97\u4e0d\u76f8\u4fe1\u3002 \u3000\u3000\u4f46\u8fd9\u7537\u4eba\u662f\u8c01\u5462\uff1f\u5728\u90a3\u91cc\uff1f \u3000\u3000\u695a\u7559\u9999\u6b63\u5728\u60ca\u7591\uff0c\u5ffd\u7136\u89c1\u5230\u90a3\u8fb9\u7684\u6c60\u6c34\u8d77\u4e86\u4e00\u9635\u6d9f\u6f2a\uff0c\u65e0\u8bba\u5728\u4efb\u4f55\u60c5\u51b5\u4e0b\uff0c\u4ed6\u90fd\u7edd\u4e0d\u4f1a \u9519\u8fc7\u53d1\u751f\u5728\u4ed6\u5468\u56f4\u7684\u4efb\u4f55\u4e8b\u3002 \u3000\u3000\u4ed6\u7acb\u523b\u65ad\u5b9a\u8fd9\u5fc5\u5b9a\u662f\u201c\u6c34\u6bcd\u201d\u9634\u59ec\u56de\u6765\u4e86\uff0c\u8fd9\u65f6\u5df2\u6ca1\u6709\u522b\u7684\u85cf\u8eab\u4e4b\u5904\uff0c\u4ed6\u53ea\u6709\u95ea\u8eab\u8eb2\u5165 \u4e86\u8863\u67dc\u3002 \u3000\u3000\u4f46\u4ed6\u5df2\u6765\u4e0d\u53ca\u5c06\u8863\u67dc\u5173\u7d27\u4e86\u3002 \u3000\u3000\u201c\u6c34\u6bcd\u201d\u9634\u59ec\u5df2\u81ea\u6c60\u6c34\u4e2d\u51fa\u73b0\uff0c\u5979\u811a\u4e0b\u5f77\u4f5b\u6709\u4eba\u6258\u7740\u4f3c\u7684\uff0c\u7f13\u7f13\u81ea\u6c60\u6c34\u4e2d\u5347\u8d77\uff0c\u8fd9\u79cd\u529f \u529b\uff0c\u8fde\u695a\u7559\u9999\u89c1\u4e86\u90fd\u5f88\u5403\u3002\u3002 \u3000\u3000\u5c31\u51ed\u8fd9\u4e00\u70b9\uff0c\u695a\u7559\u9999\u5df2\u77e5\u9053\u201c\u6c34\u6bcd\u201d\u9634\u59ec\u7684\u6b66\u529f\u679c\u7136\u8fd8\u5728\u77f3\u89c2\u97f3\u4e4b\u4e0a\uff0c\u4ed6\u81ea\u5df1\u66f4\u7edd\u4e0d\u662f \u5979\u7684\u654c\u624b\u3002 \u3000\u3000\u6b64\u523b\u53ea\u8981\u5979\u53d1\u73b0\u8fd9\u91cc\u6709\u53c1\u4e2a\u4eba\u5931\u4e86\u8e2a\uff0c\u4e00\u5b9a\u4f1a\u7acb\u523b\u5f00\u59cb\u641c\u7d22\uff0c\u65e0\u8bba\u5982\u4f55\uff0c\u5979\u90fd\u4e0d\u4f1a\u9519\u8fc7 \u8fd9\u8863\u67dc\u7684\u3002 \u3000\u3000\u56e0\u4e3a\u8fd9\u5730\u65b9\u6839\u672c\u6ca1\u6709\u522b\u7684\u85cf\u8eab\u5904\u3002 \u3000\u3000\u53ea\u8981\u5979\u4e00\u53d1\u73b0\u695a\u7559\u9999\uff0c\u90a3\u9ebd\u695a\u7559\u9999\u5c31\u5fc5\u6b7b\u65e0\u7591\uff0c\u56e0\u4e3a\u695a\u7559\u9999\u80fd\u6218\u80dc\u7684\u673a\u4f1a\u6015\u8fde\u4e07\u5206\u4e4b\u4e00 \u90fd\u4e0d\u5230\u3002 \u3000\u3000\u695a\u7559\u9999\u51e0\u4e4e\u8fde\u5fc3\u8df3\u90fd\u505c\u6b62\u4e86\u3002 \u3000\u3000\u8c01\u77e5\u201c\u6c34\u6bcd\u201d\u9634\u59ec\u7adf\u5b8c\u5168\u6ca1\u6709\u7559\u610f\u8fd9\u5730\u65b9\u5c11\u4e86\u53c1\u4e2a\u4eba\uff0c\u5979\u5f77\u4f5b\u6709\u7740\u5f88\u91cd\u7684\u5fc3\u4e8b\uff0c\u5168\u6ca1\u6709 \u7559\u610f\u5230\u522b\u7684\u3002 \u3000\u3000\u4ece\u6ca1\u6709\u5173\u7d27\u7684\u8863\u67dc\u95e8\u7f1d\u671b\u51fa\u53bb\uff0c\u53ea\u89c1\u5979\u53cc\u7709\u7d27\u7d27\u5a25\u7740\uff0c\u8138\u4e0a\u5e26\u7740\u6012\u5bb9\uff0c\u76ee\u5149\u770b\u6765\u5374\u6709\u4e9b \u90c1\u3002 \u3000\u3000\u3001 \u3000\u3000\u4e00\u8d70\u8fdb\u5c4b\u4e8e\uff0c\u5979\u5c31\u8eba\u5230\u5e8a\u4e0a\uff0c\u773c\u775b\u76f4\u52fe\u52fe\u7684\u770b\u7740\u5c4b\u9876\uff0c\u4e5f\u4e0d\u77e5\u5728\u60f3\u4e9b\u4ec0\u9ebd\uff0c\u6839\u672c\u6ca1\u6709\u5f80 \u8863\u6846\u8fd9\u8fb9\u77a7\u4e00\u773c\u3002 \u7b2c\u4e8c\u5341\u4e5d\u7ae0\u3000\u53d8\u6001\u5fc3\u7406>> \u53e4\u9f99\u300a\u695a\u7559\u9999\u7cfb\u5217\xb7\u753b\u7709\u9e1f\u300b \u7b2c\u4e8c\u5341\u4e5d\u7ae0\u3000\u53d8\u6001\u5fc3\u7406 \u3000\u3000\u695a\u7559\u9999\u8fd9\u4e00\u6b21\u5371\u673a\u786e\u5df2\u8fc7\u53bb\uff0c\u4f46\u4ed6\u60f3\u5230\u80e1\u94c1\u82b1\u4ed6\u4eec\u73b0\u5728\u7684\u5904\u5883\uff0c\u5fc3\u91cc\u4e0d\u7981\u66f4\u96be\u53d7\u66f4\u7740 \u6025\u3002 \u3000\u3000\u6c34\u6bcd\u65e2\u5df2\u56de\u6765\u4e86\uff0c\u80e1\u94c1\u82b1\u4ed6\u4eec\u5f88\u53ef\u80fd\u5df2\u906d\u4e86\u6bd2\u624b\u3002 \u3000\u3000\u695a\u7559\u9999\u81ea\u5df1\u4e5f\u79bb\u6b7b\u4e0d\u8fdc\u4e86\uff0c\u4ed6\u8eb2\u5728\u8fd9\u8863\u67dc\u91cc\uff0c\u65e2\u4e0d\u80fd\u8fdb\uff0c\u4e5f\u4e0d\u80fd\u9000\uff0c\u8fdf\u65e9\u8fd8\u662f\u8981\u88ab\u4eba\u53d1 \u73b0\u7684\u3002 \u3000\u3000\u82e5\u662f\u6362\u4e86\u522b\u4eba\uff0c\u6015\u65e9\u5df2\u6025\u5f97\u53d1\u75af\u3002 \u3000\u3000\u4f46\u5230\u4e86\u8fd9\u5730\u6b65\uff0c\u695a\u7559\u9999\u53cd\u800c\u4e0d\u7740\u6025\u4e86\uff0c\u56e0\u4e3a\u4ed6\u77e5\u9053\u7740\u6025\u53cd\u800c\u6ca1\u6709\u7528\uff0c\u53cd\u800c\u4f1a\u4f7f\u4ed6\u5931\u53bb\u51b7 \u9759\u3002 \u3000\u3000\u4ed6\u73b0\u5728\u4e00\u5b9a\u8981\u51b7\u9759\uff0c\u51b7\u9759\u7684\u7b49\u5f85\u673a\u4f1a\u3002 \u3000\u3000\u53ea\u53ef\u60dc\u8fd9\u673a\u4f1a\u5b9e\u5728\u6e3a\u832b\u5f97\u5f88\u3002 \u3000\u3000\u997c\u4e86\u534a\u664c\uff0c\u5bab\u5357\u71d5\u4e5f\u56de\u6765\u4e86\u3002 \u3000\u3000\u5929\u4e0b\u6240\u6709\u7684\u5f1f\u5b50\u8d70\u5165\u5e08\u957f\u5bdd\u5ba4\u4e2d\uff0c\u4e00\u5b9a\u90fd\u4f1a\u5148\u7980\u62a5\uff0c\u518d\u95ee\u5b89\uff0c\u6b66\u6797\u4e2d\u4eba\u867d\u4e0d\u62d8\u5c0f\u8282\uff0c\u4f46 \u5e08\u5f92\u4e4b\u793c\u8fd8\u662f\u4e0d\u53ef\u5931\u7684\u3002 \u3000\u3000\u4f55\u51b5\u795e\u6c34\u5bab\u89c4\u77e9\u4e4b\u4e25\uff0c\u66f4\u662f\u5929\u4e0b\u7686\u77e5\u3002 \u3000\u3000\u5947\u602a\u7684\u662f\uff0c\u5bab\u5357\u71d5\u5374\u968f\u968f\u4fbf\u4fbf\u7684\u5c31\u8d70\u4e86\u8fdb\u6765\uff0c\u5c31\u50cf\u662f\u59bb\u5b50\u8d70\u5165\u81ea\u5df1\u4e08\u592b\u7684\u5bdd\u5ba4\u4f3c\u7684\uff0c\u800c \u4e14\u5c45\u7136\u5750\u5230\u5e8a\u4e0a\u53bb\u4e86\u3002 \u3000\u3000\u9634\u59ec\u662f\u8eba\u5728\u90a3\u91cc\uff0c\u8fde\u4e00\u70b9\u53cd\u5e94\u90fd\u6ca1\u6709\uff0c\u5979\u5f92\u5f1f\u5750\u5230\u5979\u5e8a\u4e0a\uff0c\u5979\u8fd9\u751f\u5177\u6d01\u7656\u7684\u4eba\uff0c\u5374\u4e00\u70b9 \u4e5f\u4e0d\u5728\u610f\u3002 \u3000\u3000\u53ea\u542c\u5bab\u5357\u71d5\u9053\uff1a\u201c\u90a3\u53c1\u4eba\u5df2\u5173\u4e86\u8d77\u6765\uff0c\u7b49\u4ed6\u4eec\u9192\u8fc7\u6765\u5f8c\uff0c\u53c1\u59d0\u5c31\u4f1a\u76d8\u95ee\u4ed6\u4eec\u7684\u53e3\u4f9b\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u4e0d\u7981\u6697\u4e2d\u677e\u4e86\u53e3\u6c14\uff0c\u80e1\u94c1\u82b1\u4ed6\u4eec\u7684\u5904\u5883\u867d\u5371\u9669\uff0c\u4f46\u81f3\u5c11\u8fd8\u6ca1\u6709\u6b7b\uff0c\u53ea\u8981\u8fd8\u6ca1\u6709 \u6b7b\uff0c\u5c31\u6709\u673a\u4f1a\u3002 \u3000\u3000\u7206\u5357\u71d5\u53c8\u9053\uff1a\u201c\u4f46\u4e5d\u59b9\u5374\u8ba4\u4e3a\u8981\u53c1\u59d0\u53bb\u76d8\u95ee\u4ed6\u4eec\u6709\u4e9b\u4e0d\u59a5\u3002\u201d \u3000\u3000\u201c\u6c34\u6bcd\u201d\u9634\u59ec\u9053\uff1a\u201c\u4e0d\u59a5\uff1f\u201d \u3000\u3000\u7206\u5357\u71d5\u9053\uff1a\u201c\u5979\u8ba4\u4e3a\u4ed6\u4eec\u8bf4\u7684\u8bdd\u5e76\u4e0d\u5047\uff0c\u7684\u786e\u662f\u6765\u627e\u4eba\u7684\uff0c\u56e0\u4e3a\u8fd9\u91cc\u7684\u786e\u5df2\u6709\u4eba\u8fdb\u6765 \u4e86\u3002\u201d \u3000\u3000\u6c34\u6bcd\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u7206\u5357\u71d5\u9053\uff1a\u201c\u5979\u8bf4\u5979\u65b9\u7684\u786e\u66fe\u7ecf\u53d1\u73b0\u4f5b\u5802\u524d\u6709\u4eba\u8e2a\uff0c\u4f46\u5b88\u5728\u4f5b\u5802\u91cc\u7684\u53c1\u59d0\u5374\u786c\u8bf4\u6ca1\u6709\uff0c \u6240\u4ee5\u5979\u8ba4\u4e3a\u8fd9\u5176\u4e2d\u9887\u6709\u8e4a\u8df7\u3002\u201d \u3000\u3000\u9634\u59ec\u53ea\u51b7\u7b11\u4e86\u4e00\u58f0\uff0c\u5e76\u6ca1\u6709\u8bf4\u8bdd\u3002 \u3000\u3000\u695a\u7559\u9999\u66f4\u662f\u62c5\u5fc3\uff0c\u9634\u59ec\u82e5\u662f\u53d1\u73b0\u90a3\u201c\u53c1\u59d0\u201d\u6709\u5faa\u79c1\u7eb5\u654c\u4e4b\u5acc\uff0c\u5979\u7684\u5904\u5883\u7740\u5b9e\u582a\u8651\uff0c\u695a\u7559 \u9999\u5b9e\u5728\u4e0d\u5fcd\u8ba9\u5979\u4e3a\u81ea\u5df1\u53d7\u7d2f\u3002 \u3000\u3000\u997c\u4e86\u534a\u664c\uff0c\u9634\u59ec\u5ffd\u7136\u9053\uff1a\u201c\u4f60\u8ba4\u4e3a\u4ed6\u4eec\u6765\u627e\u7684\u4eba\u4f1a\u662f\u8c01\u5462\uff1f\u201d \u3000\u3000\u7206\u5357\u71d5\u6c88\u9ed8\u4e86\u4e00\u4f1a\u513f\uff0c\u9053\uff1a\u201c\u4ed6\u4eec\u4e45\u8d70\u6c5f\u6e56\uff0c\u670b\u53cb\u4e00\u5b9a\u5f88\u591a\uff0c\u6211\u600e\u77e5\u9053\u4ed6\u4eec\u627e\u7684\u662f \u8c01\uff1f\u201d \u3000\u3000\u9634\u59ec\u9053\uff1a\u201c\u4f60\u4e0d\u8ba4\u5f97\u90a3\u9ec4\u9c81\u76f4\uff1f\u201d \u3000\u3000\u7206\u5357\u71d5\u9053\uff1a\u201c\u6211\u600e\u9ebd\u4f1a\u8ba4\u5f97\u4ed6\uff1f\u201d \u3000\u3000\u9634\u59ec\u9053\uff1a\u201c\u4f46\u4ed6\u5374\u597d\u50cf\u8ba4\u5f97\u4f60\u3002\u201d \u3000\u3000\u7206\u5357\u71d5\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u9634\u59ec\u9053\uff1a\u201c\u4f60\u96be\u9053\u4e0d\u77e5\u9053\u9ec4\u9c81\u76f4\u662f\u201c\u4ed6\u201d\u751f\u5e73\u6700\u597d\u7684\u670b\u53cb\uff0c\u4e5f\u662f\u201c\u4ed6\u201d\u552f\u4e00\u7684\u670b\u53cb\u3002\u201d \u3000\u3000\u7206\u5357\u71d5\u54ac\u7740\u5634\u5507\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u6211\u600e\u9ebd\u4f1a\u77e5\u9053\uff0c\u201c\u4ed6\u201d\u53c8\u4e0d\u662f\u6211\u7684\u60c5\u4eba\uff0c\u600e\u9ebd\u4f1a\u5c06\u8fd9\u4e9b\u4e8b \u544a\u8bc9\u6211\u3002\u201d \u3000\u3000\u9634\u59ec\u5ffd\u7136\u7ffb\u8eab\u751f\u4e86\u8d77\u6765\uff0c\u4e00\u628a\u63ea\u4f4f\u5979\u7684\u5934\u53d1\uff0c\u5389\u58f0\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u4e00\u5b9a\u6709\u5f88\u591a\u4e8b\u5728\u7792\u7740 \u6211\uff0c\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u7206\u5357\u71d5\u7528\u529b\u54ac\u7740\u5634\u5507\uff0c\u4e0d\u8bf4\u8bdd\u3002 \u3000\u3000\u9634\u59ec\u9053\uff1a\u201c\u6628\u5929\u665a\u4e0a\u4f60\u89c1\u5230\u201c\u4ed6\u201d\u4e4b\u5f8c\uff0c\u7a76\u7adf\u53d1\u751f\u4e86\u4ec0\u9ebd\u4e8b\uff1f\u4e3a\u4ec0\u9ebd\u4e00\u76f4\u7b49\u5230\u4eca\u5929\u65e9\u4e0a \u4f60\u624d\u56de\u6765\uff1f\u201d \u3000\u3000\u5979\u7684\u624b\u8f6c\u52a8\uff0c\u5c06\u5979\u7684\u5934\u53d1\u7f20\u5728\u624b\u4e0a\uff0c\u5bab\u5357\u71d5\u75db\u5f97\u51e0\u4e4e\u8981\u6d41\u51fa\u773c\u6cea\uff0c\u4f46\u5634\u89d2\u5374\u6cdb\u8d77\u4e86\u5fae \u7b11\uff0c\u9053\uff1a\u201c\u4f60\u5728\u5403\u918b\uff1f\u201d \u3000\u3000\u9634\u59ec\u9053\uff1a\u201c\u6211\u5403\u4ec0\u9ebd\u918b\uff1f\u201d \u3000\u3000\u7206\u5357\u71d5\u4e0d\u6000\u597d\u610f\u7684\u7b11\u7740\uff0c\u9053\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u6015\u6211\u548c\u4ed6\u6709\u4e86\u4ec0\u9ebd\u5173\u7cfb\uff0c\u6240\u4ee5\u624d\u5403\u918b\u3002\u201d \u3000\u3000\u9634\u59ec\u8299\u4e86\uff0c\u7b11\u5f97\u5374\u6709\u4e9b\u4e0d\u5b89\u3002 \u3000\u3000\u5979\u7b11\u7740\u9053\uff1a\u201c\u4f60\u548c\u4ed6\u600e\u4f1a\u6709\u4ec0\u9ebd\u5173\u7cfb\uff1f\u201d \u3000\u3000\u7206\u5357\u71d5\u7728\u7740\u773c\u9053\uff1a\u201c\u4e3a\u4ec0\u9ebd\u4e0d\u4f1a\uff1f\u4ed6\u662f\u7537\u4eba\uff0c\u6211\u662f\u5973\u4eba\uff0c\u7537\u4eba\u548c\u5973\u4eba\u5728\u4e00\u8d77\uff0c\u5c82\u975e\u672c\u5c31 \u662f\u5f88\u6b63\u5e38\u7684\u4e8b\u9ebd\uff1f\u201d \u3000\u3000\u9634\u59ec\u7684\u624b\u5ffd\u7136\u5934\u6296\u4e86\u8d77\u6765\uff0c\u653e\u677e\u4e86\u5979\u7684\u5934\u53d1\uff0c\u55c4\u58f0\u9053\uff1a\u201c\u4f46\u4f60\u7edd\u4e0d\u4f1a\u505a\u8fd9\u79cd\u4e8b\u7684\uff0c\u662f \u5417\uff1f\u201d \u3000\u3000\u7206\u5357\u71d5\u5c06\u5934\u53d1\u7528\u5230\u9762\u524d\uff0c\u8f7b\u8f7b\u7684\u629a\u6478\u7740\uff0c\u5583\u5583\u9053\uff1a\u201c\u4ed6\u5b9e\u5728\u662f\u4e2a\u5f88\u6709\u8da3\u7684\u7537\u4eba\uff0c\u96be\u602a\u4f60 \u4e00\u76f4\u5fd8\u4e0d\u4e86\u4ed6\u3002\u201d \u3000\u3000\u5979\u8138\u4e0a\u6e10\u6e10\u6cdb\u8d77\u4e00\u9635\u7ea2\u6f6e\uff0c\u50cf\u662f\u5df2\u6709\u4e00\u80a1\u70ed\u6d41\u81ea\u5fc3\u5e95\u5347\u8d77\u3002 \u3000\u3000\u9634\u59ec\u5403\u60ca\u7684\u671b\u7740\u5979\uff0c\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u96be\u9053\u771f\u7684\u2026\u2026\u201d \u3000\u3000\u7206\u5357\u71d5\u661f\u7738\u8499\u80e7\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u5947\u602a\u7684\u662f\uff0c\u4ed6\u5bf9\u6211\u7684\u52a8\u4f5c\uff0c\u7adf\u5b8c\u5168\u548c\u4f60\u5bf9\u6211\u505a\u7684\u4e00\u6837\uff0c\u5f53 \u4ed6\u7684\u624b\u5728\u629a\u6478\u6211\u7684\u65f6\u5019\uff0c\u6211\u8fd8\u4ee5\u4e3a\u662f\u4f60\uff0c\u4f46\u4ed6\u5374\u6bd4\u4f60\u2026\u2026\u201d \u3000\u3000\u201c\u53ed\u201d\u7684\u4e00\u58f0\uff0c\u3002\u9634\u59ec\u7684\u624b\u5df2\u63b4\u5728\u5979\u8138\u4e0a\uff0c\u6012\u9053\uff1a\u201c\u4e0d\u8bb8\u4f60\u518d\u8bf4\u4e0b\u53bb\u3002\u201d \u3000\u3000\u7206\u5357\u71d5\u624b\u629a\u7740\u8138\uff0c\u5ffd\u53c8\u5403\u5403\u7684\u7b11\u4e86\u8d77\u6765\uff0c\u9053\uff1a\u201c\u4f60\u5728\u5403\u918b\uff0c\u6211\u5c31\u77e5\u9053\u4f60\u5728\u5403\u918b\u3002\u201d \u3000\u3000\u5979\u7684\u624b\u73af\u62b1\u8d77\u9634\u59ec\u7684\u8116\u5b50\uff0c\u7528\u7259\u9f7f\u8f7b\u5543\u7740\u5b83\u7684\u8033\u6735\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u6211\u559c\u6b22\u770b\u5230\u4f60\u5403\u918b\uff0c\u53ea \u8981\u4f60\u4e5f\u80af\u4e3a\u6211\u5403\u918b\uff0c\u6211\u5c31\u7b97\u7acb\u523b\u4e3a\u4f60\u6b7b\u4e86\uff0c\u4e5f\u6ca1\u6709\u4ec0\u9ebd\u5173\u7cfb\u3002\u201d \u3000\u3000\u9634\u59ec\u6728\u7136\u5750\u7740\uff0c\u773c\u775b\u4f3c\u4e5f\u6709\u4e9b\u6f6e\u6e7f\u4e86\uff0c\u5583\u5583\u9053\uff1a\u201c\u4e3a\u4ec0\u9ebd\u8981\u8fd9\u6837\u505a\uff1f\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u7206\u5357\u71d5\u9053\uff1a\u201c\u53ea\u56e0\u6211\u53d7\u4e0d\u4e86\uff0c\u6211\u5df2\u7ecf\u5feb\u53d1\u75af\u4e86\uff0c\u6211\u8981\u62a5\u4ec7\u3002\u201d \u3000\u3000\u9634\u59ec\u9053\uff1a\u201c\u62a5\u4ec7\uff1f\u201d \u3000\u3000\u7206\u5357\u71d5\u9053\uff1a\u201c\u6bcf\u56de\u4f60\u548c\u6211\u597d\u7684\u65f6\u5019\uff0c\u6211\u5c31\u4f1a\u60f3\uff0c\u662f\u4e0d\u662f\u56e0\u4e3a\u6211\u50cf\u4ed6\uff0c\u4f60\u624d\u548c\u6211\u597d\uff1f\u6bcf\u5f53 \u4f60\u62b1\u7740\u6211\u7684\u65f6\u5019\uff0c\u6211\u5c31\u4f1a\u60f3\uff0c\u662f\u4e0d\u662f\u4ed6\u4e5f\u7528\u8fd9\u79cd\u6cd5\u5b50\u62b1\u8fc7\u4f60\uff0c\u4f60\u624d\u7528\u8fd9\u79cd\u6cd5\u5b50\u62b1\u6211\uff1f\u4f60\u62b1\u6211 \u7684\u65f6\u5019\uff0c\u5fc3\u91cc\u662f\u4e0d\u662f\u8fd8\u5728\u60f3\u7740\u4ed6\uff1f\u201d \u3000\u3000\u9634\u59ec\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u60f3\u5f97\u5927\u591a\u4e86\u3002\u201d \u3000\u3000\u7206\u5357\u71d5\u9053\uff1a\u201c\u6211\u4e0d\u4f46\u4e3a\u81ea\u5df1\u62a5\u4ec7\uff0c\u4e5f\u8981\u4e3a\u4f60\u62a5\u4ec7\u3002\u201d \u3000\u3000\u9634\u59ec\u58f0\u97f3\u5df2\u98a4\u6296\uff0c\u9053\uff1a\u201c\u4e3a\u6211\uff1f\u201d \u3000\u3000\u7206\u5357\u71d5\u9053\uff1a\u201c\u56e0\u4e3a\u4ed6\u629b\u5f03\u4e86\u4f60\uff0c\u4f46\u4f60\u5374\u4e00\u76f4\u5fd8\u4e0d\u4e86\u4ed6\uff0c\u4f60\u7231\u4ed6\uff0c\u4ed6\u5374\u53cd\u800c\u4ee5\u6b64\u6765\u8981\u80c1 \u4f60\uff0c\u903c\u7740\u4f60\u53ea\u597d\u8ba9\u4ed6\u79bb\u5f00\u8fd9\u91cc\u2026\u2026\u201d \u3000\u3000\u9634\u59ec\u6ca1\u6709\u8bf4\u8bdd\uff0c\u773c\u6cea\u5374\u5df2\u6d41\u4e0b\u9762\u988a\u3002 \u3000\u3000\u695a\u7559\u9999\u5b9e\u5728\u60f3\u4e0d\u5230\u72ec\u6b65\u6b66\u6797\uff0c\u4e0d\u53ef\u4e00\u4e16\u7684\u201c\u6c34\u6bcd\u201d\u9634\u59ec\u4e5f\u662f\u88ab\u60c5\u6240\u56f0\uff0c\u4e3a\u60c5\u98a0\u5012\uff0c\u66f4\u60f3 \u4e0d\u5230\u5979\u7684\u60c5\u611f\u7adf\u5982\u6b64\u4e0d\u6b63\u5e38\u3002 \u3000\u3000\u695a\u7559\u9999\u603b\u7b97\u5df2\u660e\u767d\u8fd9\u7a76\u7adf\u662f\u600e\u9ebd\u56de\u4e8b\u4e86\u3002 \u3000\u3000\u9634\u59ec\u672c\u6765\u5c31\u662f\u4e2a\u4e0d\u6b63\u5e38\u7684\u5973\u4eba\uff0c\u5979\u7684\u60c5\u6b32\u662f\u7578\u5f62\u7684\uff0c\u5979\u8ba8\u538c\u7537\u4eba\uff0c\u5374\u5c06\u60c5\u6b32\u5728\u5973\u4eba\u8eab\u4e0a \u53d1\u6cc4\u3002 \u3000\u3000\u6240\u4ee5\u5979\u6536\u4e86\u5f88\u591a\u7f8e\u4e3d\u7684\u5973\u5f1f\u5b50\uff0c\u800c\u4e14\u5efa\u9020\u4e86\u5f88\u591a\u79d8\u9053\uff0c\u53ef\u4ee5\u76f4\u8fbe\u5979\u6240\u6709\u5973\u5f1f\u5b50\u7684\u5bdd\u5ba4\u3002 \u3000\u3000\u90a3\u767d\u8863\u7f8e\u5987\u66fe\u7ecf\u8b66\u544a\u82cf\u84c9\u84c9\uff0c\u4e0d\u8bb8\u5979\u968f\u610f\u8d70\u52a8\uff0c\u5c31\u662f\u6015\u201c\u6c34\u6bcd\u201d\u9634\u59ec\u770b\u5230\u5979\uff0c\u4e5f\u5bf9\u5979\u751f \u51fa\u7578\u5f62\u7684\u7231\u6b32\u3002 \u3000\u3000\u90a3\u5b9e\u5728\u662f\u79cd\u201c\u60f3\u4e0d\u5230\u7684\u53ef\u6015\u707e\u7978\u201d\u3002 \u3000\u3000\u6614\u5e74\u201c\u96c4\u5a18\u5b50\u201d\u5230\u4e86\u795e\u6c34\u5bab\uff0c\u4e5f\u548c\u9634\u59ec\u6709\u4e86\u4e0d\u6b63\u5e38\u7684\u5173\u7cfb\uff0c\u7b49\u5230\u9634\u59ec\u53d1\u73b0\u4ed6\u5e76\u975e\u5973\u4eba \u65f6\uff0c\u5df2\u7ecf\u8fdf\u4e86\u3002 \u3000\u3000\u4f46\u201c\u96c4\u5a18\u5b50\u201d\u4e00\u8eab\u517c\u6709\u5973\u6027\u7684\u6e29\u67d4\uff0c\u548c\u7537\u6027\u7684\u9b45\u529b\uff0c\u201c\u6c34\u6bcd\u201d\u9634\u59ec\u7ec8\u65bc\u4e5f\u7231\u4e0a\u4e86\u4ed6\uff0c\u800c \u4e0d\u80fd\u81ea\u62d4\u3002 \u3000\u3000\u65bc\u662f\uff0c\u4ed6\u4eec\u751f\u4e0b\u4e86\u53f8\u5f92\u9759\u3002 \u3000\u3000\u53ef\u662f\u201c\u96c4\u5a18\u5b50\u201d\u5374\u4e0d\u7518\u6c38\u8fdc\u201c\u96cc\u4f0f\u201d\u5728\u9634\u59ec\u7684\u88d9\u4e0b\uff0c\u4ed6\u4e00\u5fc3\u60f3\u79bb\u5f00\u8fd9\u91cc\uff0c\u9634\u59ec\u867d\u4e0d\u653e\u4ed6 \u8d70\uff0c\u4f46\u96c4\u5a18\u5b50\u5374\u4ee5\u6b64\u79d8\u5bc6\u8981\u80c1\u5979\u3002 \u3000\u3000\u201c\u6c34\u6bcd\u201d\u9634\u59ec\u81ea\u7136\u4e0d\u613f\u88ab\u522b\u4eba\u77e5\u9053\u5979\u662f\u4e2a\u53d8\u6001\u7684\u5973\u4eba\uff0c\u6700\u5f8c\u53ea\u597d\u653e\u4ed6\u8d70\u4e86\uff0c\u800c\u4e14\u6c38\u8fdc\u4e0d \u8bb8\u4ed6\u518d\u56de\u6765\u3002 \u3000\u3000\u4f46\u5979\u8fd8\u662f\u5fd8\u4e0d\u4e86\uff0c\u56e0\u4e3a\u50cf\u201c\u96c4\u5a18\u5b50\u201d\u8fd9\u79cd\u4e00\u8eab\u800c\u517c\u5177\u7537\u5973\u4e24\u6027\u4e4b\u4f18\u70b9\u7684\u4eba\uff0c\u4e16\u4e0a\u6015\u8fd8\u6ca1 \u6709\u7b2c\u4e8c\u4e2a\u3002 \u3000\u3000\u6240\u4ee5\u9634\u59ec\u5c31\u9009\u4e2d\u4e86\u548c\u201c\u96c4\u5a18\u5b50\u201d\u957f\u5f97\u5f88\u50cf\u7684\u5bab\u5357\u71d5\uff0c\u6765\u4f5c\u81ea\u5df1\u7684\u7231\u5ba0\uff0c\u4ee5\u586b\u8865\u81ea\u5df1\u5fc3\u7075 \u4e0a\u7684\u7a7a\u865a\u3002 \u3000\u3000\u5c31\u56e0\u4e3a\u8fd9\u79cd\u4e0d\u6b63\u5e38\u7684\u60c5\u611f\uff0c\u624d\u4f1a\u5f15\u8d77\u8fd9\u8bb8\u591a\u4e0d\u6b63\u5e38\u7684\u4e8b\u3002 \u3000\u3000\u73b0\u5728\uff0c\u695a\u7559\u9999\u7ec8\u65bc\u53d1\u73b0\u4e86\u9634\u59ec\u7684\u79d8\u5bc6\u3002 \u3000\u3000\u53ef\u662f\uff0c\u4ed6\u53c8\u80fd\u600e\u9ebd\u6837\u5462\uff1f \u3000\u3000\u4ed6\u65e2\u4e0d\u662f\u201c\u96c4\u5a18\u5b50\u201d\uff0c\u66f4\u4e0d\u80fd\u50cf\u96c4\u5a18\u5b50\u90a3\u6837\u4ee5\u8fd9\u79cd\u79d8\u5bc6\u6765\u8981\u80c1\u9634\u59ec\uff0c\u4ed6\u7684\u5904\u5883\u8fd8\u662f\u548c\u4ee5 \u524d\u4e00\u6837\u3002 \u3000\u3000\u4ed6\u80fd\u6d3b\u4e0b\u53bb\u7684\u5e0c\u671b\uff0c\u6015\u8fd8\u4e0d\u5230\u767e\u5206\u4e4b\u4e00\u3002 \u3000\u3000\u7206\u5357\u71d5\u7528\u820c\u5934\u8f7b\u8f7b\u8214\u7740\u9634\u59ec\u9762\u4e0a\u7684\u773c\u6cea\uff0c\u7528\u80f8\u819b\u78e8\u64e6\u7740\u5979\u7684\u80f8\u819b\uff0c\u5589\u5499\u91cc\u53d1\u51fa\u4e86\u4e00\u79cd\u547b \u541f\u822c\u7684\u5598\u606f\u58f0\u3002 \u3000\u3000\u4f46\u9634\u59ec\u5374\u63a8\u5f00\u4e86\u5979\u9053\uff1a\u201c\u6211\u9759\u9759\u7684\u6b47\u4e00\u6b47\uff0c\u4f60\u8d70\u5427\uff01\u201d \u3000\u3000\u7206\u5357\u71d5\u54ac\u7740\u5634\u5507\uff0c\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u4e0d\u8981\u2026\u2026\u201d \u3000\u3000\u9634\u59ec\u9053\uff1a\u201c\u73b0\u5728\u6211\u7684\u5fc3\u60c5\u4e0d\u597d\uff0c\u4ec0\u9ebd\u90fd\u4e0d\u60f3\u3002\u201d \u3000\u3000\u7206\u5357\u71d5\u6c88\u9ed8\u4e86\u534a\u664c\uff0c\u5ffd\u7136\u51b2\u8fc7\u53bb\u8dc3\u5165\u4e86\u6c34\u6c60\u3002 \u3000\u3000\u9634\u59ec\u7b49\u5230\u6c60\u4e0a\u7684\u6d9f\u6f2a\u6d88\u5931\uff0c\u5ffd\u7136\u4e0b\u4e86\u5e8a\uff0c\u8d70\u5411\u90a3\u8863\u67dc\uff0c\u5979\u4f3c\u4e4e\u8981\u6362\u4ef6\u8863\u670d\u5f8c\u518d\u7761\u4e0b\u3002 \u3000\u3000\u695a\u7559\u9999\u8fde\u547c\u5438\u90fd\u51e0\u4e4e\u505c\u987f\u4e86\u3002 \u3000\u3000\u4f46\u9634\u59ec\u8d70\u5230\u8863\u67dc\u5374\u6ca1\u6709\u62c9\u95e8\u3002 \u3000\u3000\u5979\u5446\u5446\u7684\u7ad9\u5728\u90a3\u91cc\uff0c\u5fc3\u91cc\u4e0d\u77e5\u5728\u60f3\u7740\u4ec0\u9ebd\uff0c\u8fc7\u4e86\u5f88\u4e45\u4e4b\u5f8c\uff0c\u5ffd\u7136\u5c06\u8863\u67dc\u5173\u4e0a\uff0c\u81ea\u5916\u9762\u9501 \u4e86\u8d77\u6765\u3002 \u3000\u3000\u8fd9\u67dc\u4e5f\u662f\u7528\u5f88\u539a\u7684\u5927\u7406\u77f3\u5236\u6210\u7684\uff0c\u65e0\u8bba\u8c01\u88ab\u5173\u5728\u91cc\u9762\u4e4b\u5f8c\uff0c\u90fd\u4f11\u60f3\u80fd\u7834\u58c1\u800c\u51fa\uff0c\u695a\u7559\u9999 \u4e00\u9897\u5fc3\u7acb\u523b\u6c88\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u5979\u96be\u9053\u53d1\u73b0\u4e86\u8863\u67dc\u91cc\u6709\u4eba\uff1f \u3000\u3000\u90a3\u9ebd\u5979\u4e3a\u4f55\u4e0d\u4ee4\u4ed6\u51fa\u6765\uff0c\u53cd\u800c\u5c06\u4ed6\u5173\u5728\u8863\u67dc\u91cc\uff1f \u3000\u3000\u5e78\u597d\u8863\u67dc\u7684\u4e0a\u7aef\u8fd8\u6709\u4e9b\u96d5\u7a7a\u7684\u82b1\u7eb9\uff0c\u4eba\u5173\u5728\u91cc\u9762\uff0c\u8fd8\u4e0d\u81f3\u65bc\u7a92\u606f\uff0c\u4f46\u8fd9\u79cd\u6ecb\u5473\u4e5f\u4e0d\u662f\u597d \u53d7\u7684\u3002 \u3000\u3000\u9634\u59ec\u82e5\u4e0d\u62ff\u8863\u670d\uff0c\u695a\u7559\u9999\u5c31\u8981\u6c38\u8fdc\u88ab\u5173\u5728\u8fd9\u77f3\u7262\u822c\u7684\u8863\u67dc\u91cc\uff0c\u9634\u59ec\u82e5\u6765\u62ff\u8863\u670d\uff0c\u7acb\u523b\u5c31 \u8981\u53d1\u89c9\u4ed6\u3002 \u3000\u3000\u695a\u7559\u9999\u6b63\u4e0d\u77e5\u8be5\u5982\u4f55\u662f\u597d\uff0c\u7a81\u542c\u9634\u59ec\u9053\uff1a\u201c\u4f60\u65e2\u5df2\u53d1\u8a93\u6c38\u4e0d\u518d\u5165\u795e\u6c34\u5bab\uff0c\u73b0\u5728\u4e3a\u4f55\u53c8\u6765 \u4e86\uff1f\u201d \u3000\u3000\u5979\u8bed\u58f0\u4e2d\u5145\u6ee1\u4e86\u6028\u6bd2\uff0c\u695a\u7559\u9999\u5148\u5403\u4e86\u4e00\u60ca\uff0c\u77ac\u95f4\u604d\u7136\u5927\u609f\uff1a\u539f\u6765\u5979\u4ee5\u4e3a\u9501\u5728\u8863\u67dc\u91cc\u7684\u662f \u96c4\u5a18\u5b50\uff0c\u5979\u5e76\u4e0d\u77e5\u9053\u91cc\u9762\u4e0d\u662f\u96c4\u5a18\u5b50\uff0c\u5979\u8ba4\u4e3a\u9664\u4e86\u96c4\u5a18\u5b50\u5916\uff0c\u4e16\u4e0a\u7edd\u6ca1\u6709\u7b2c\u4e8c\u4e2a\u4eba\u80fd\u6f5c\u5165\u5979 \u5bdd\u5ba4\u4e2d\u7684\u3002 \u3000\u3000\u695a\u7559\u9999\u4e5f\u4e0d\u77e5\u662f\u5426\u8be5\u63ed\u7834\uff0c\u4e00\u65f6\u95f4\u53ea\u6709\u95ed\u7740\u5634\u3002 \u3000\u3000\u9634\u59ec\u9053\uff1a\u201c\u4f60\u603b\u8be5\u77e5\u9053\uff0c\u6211\u662f\u518d\u4e5f\u4e0d\u613f\u89c1\u5230\u4f60\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u6697\u9053\uff1a\u201c\u96be\u602a\u5979\u53d1\u89c9\u67dc\u4e2d\u6709\u4eba\u5f8c\uff0c\u5374\u5c06\u8863\u67dc\u53cd\u9501\u8d77\u6765\uff0c\u539f\u6765\u5979\u662f\u56e0\u4e3a\u4e0d\u613f\u518d\u89c1\u96c4 \u5a18\u5b50\u4e4b\u9762\u3002\u201d \u3000\u3000\u9634\u59ec\u53c8\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\u6211\u4e3a\u4ec0\u9ebd\u8981\u5357\u71d5\u8d70\u9ebd\uff1f\u201d \u3000\u3000\u5979\u6068\u6068\u63a5\u7740\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u4e5f\u4e0d\u613f\u8ba9\u5357\u71d5\u518d\u89c1\u5230\u4f60\uff0c\u5979\u8fd8\u662f\u4e2a\u5b69\u5b50\uff0c\u4f60\u4e3a\u4ec0\u9ebd\u8981\u7cdf\u8e4b\u5979\uff1f \u96be\u9053\u4f60\u53ea\u662f\u4e3a\u4e86\u8981\u4f24\u5bb3\u6211\uff1f\u96be\u9053\u4f60\u5bb3\u5f97\u6211\u8fd8\u4e0d\u591f\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u4e0d\u6562\u8bf4\u8bdd\uff0c\u5374\u53ca\u65f6\u53f9\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u9634\u59ec\u9053\uff1a\u201c\u4f60\u7528\u4e0d\u7740\u53f9\u6c14\uff0c\u4e5f\u7528\u4e0d\u7740\u518d\u7528\u82b1\u8a00\u5de7\u8bed\u6765\u6b3a\u9a97\u6211\uff0c\u6211\u662f\u6c38\u8fdc\u518d\u4e5f\u4e0d\u4f1a\u539f\u8c05\u4f60 \u7684\u4e86\uff0c\u4f60\u4e5f\u603b\u8be5\u77e5\u9053\u3002\u201d \u3000\u3000\u5979\u5389\u58f0\u63a5\u7740\u9053\uff1a\u201c\u4f60\u5df2\u8fdd\u80cc\u4e86\u6614\u65e5\u7684\u8a93\u8a00\uff0c\u6562\u518d\u5230\u8fd9\u91cc\u6765\uff0c\u6211\u4e5f\u4e0d\u5fc5\u518d\u987e\u5ff5\u6614\u65e5\u7684\u60c5 \u4efd\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u4e00\u76f4\u5728\u56de\u5fc6\u7740\u96c4\u5a18\u5b50\u8bf4\u8bdd\u7684\u58f0\u8c03\uff0c\u6b64\u523b\u5ffd\u7136\u9053\uff1a\u201c\u4f60\u4e00\u5b9a\u8981\u6211\u6b7b\u5728\u8fd9\u91cc\uff1f\u201d \u3000\u3000\u4ed6\u4e5f\u77e5\u9053\u81ea\u5df1\u5b66\u5f97\u5e76\u4e0d\u592a\u50cf\uff0c\u4f46\u9634\u59ec\u548c\u96c4\u5a18\u5b50\u5df2\u6709\u591a\u5e74\u672a\u89c1\uff0c\u4e00\u4e2a\u4eba\u8bf4\u8bdd\u7684\u58f0\u97f3\u4e5f\u591a\u5c11 \u4f1a\u968f\u7740\u5e74\u9f84\u6539\u53d8\u7684\u3002 \u3000\u3000\u4ed6\u53ea\u671b\u9634\u59ec\u5206\u8fa8\u4e0d\u51fa\u3002 \u3000\u3000\u9664\u59ec\u679c\u7136\u6ca1\u6709\u542c\u51fa\u6765\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u96be\u9053\u4ee5\u4e3a\u6211\u8fd8\u4f1a\u50cf\u4e0a\u6b21\u4e00\u6837\uff0c\u53c8\u653e\u4f60\u8d70\u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4f46\u2026\u2026\u4f46\u4f60\u603b\u8be5\u8ba9\u6211\u518d\u89c1\u4f60\u6700\u5f8c\u4e00\u9762\u3002\u201d \u3000\u3000\u9634\u59ec\u6c88\u9ed8\u4e86\u5f88\u4e45\uff0c\u624d\u55c4\u58f0\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u9ebd\u8fd8\u8981\u89c1\u6211\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u2026\u2026\u201d \u3000\u3000\u9634\u59ec\u53c8\u5389\u58f0\u9053\uff1a\u201c\u4f60\u4e0d\u8981\u8bf4\u4e86\uff0c\u65e0\u8bba\u4f60\u518d\u8bf4\u4ec0\u9ebd\uff0c\u6211\u90fd\u7edd\u4e0d\u4f1a\u76f8\u4fe1\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u6015\u89c1\u5230\u6211\u4e4b\u5f8c\uff0c\u5c31\u4e0d\u5fcd\u518d\u6740\u6211\u4e86\uff1f\u201d \u3000\u3000\u4ed6\u8bf4\u7684\u6bcf\u4e00\u53e5\u8bdd\uff0c\u90fd\u7ecf\u8fc7\u4e86\u518d\u53c1\u8003\u8651\uff0c\u7edd\u4e0d\u6562\u8bf4\u9519\u4e00\u4e2a\u5b57\uff0c\u4ed6\u77e5\u9053\u8d8a\u662f\u8981\u9634\u59ec\u89c1 \u201c\u4ed6\u201d\uff0c\u9634\u59ec\u5c31\u8d8a\u4e0d\u4f1a\u89c1\u4ed6\u7684\u3002 \u3000\u3000\u9634\u59ec\u679c\u7136\u9053\uff1a\u201c\u65e0\u8bba\u4f60\u600e\u9ebd\u8bf4\uff0c\u6211\u90fd\u4e0d\u4f1a\u518d\u89c1\u4f60\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4f46\u4f60\u81f3\u5c11\u5148\u8be5\u544a\u8bc9\u6211\uff0c\u9759\u513f\u7a76\u7adf\u662f\u600e\u9ebd\u6b7b\u7684\uff1f\u201d \u3000\u3000\u9634\u59ec\u53c8\u6c88\u9ed8\u4e86\u5f88\u4e45\uff0c\u9eef\u7136\u9053\uff1a\u201c\u5979\u4e00\u76f4\u90fd\u4e0d\u77e5\u9053\u6211\u662f\u5979\u7684\u6bcd\u4eb2\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4f60\u81ea\u7136\u4e0d\u4f1a\u8bf4\u7684\uff0c\u56e0\u4e3a\u4f60\u662f\u4e2a\u201c\u5723\u5973\u201d\uff0c\u600e\u9ebd\u80fd\u751f\u5b69\u5b50\u5462\uff1f\u800c\u6211\u4e3a\u4e86\u9075\u5b88 \u6614\u65e5\u7684\u8a93\u8a00\uff0c\u4e5f\u53ea\u597d\u6b3a\u9a97\u5979\uff0c\u8bf4\u5979\u7684\u6bcd\u4eb2\u65e9\u5df2\u6b7b\u4e86\u3002\u201d \u3000\u3000\u9634\u59ec\u9053\uff1a\u201c\u5c31\u56e0\u4e3a\u6211\u4eec\u7684\u6001\u5ea6\u592a\u66a7\u6627\uff0c\u6240\u4ee5\u5979\u5c31\u8ba4\u4e3a\u5979\u7684\u6bcd\u4eb2\u5c31\u662f\u88ab\u6211\u5bb3\u6b7b\u7684\uff0c\u4e00\u76f4\u60f3 \u590d\u4ec7\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u53f9\u9053\uff1a\u201c\u53ef\u601c\u7684\u5b69\u5b50\uff0c\u5979\u96be\u9053\u4e0d\u660e\u767d\u6c38\u8fdc\u6ca1\u6709\u673a\u4f1a\u7684\u9ebd\uff1f\u201d \u3000\u3000\u9634\u59ec\u9053\uff1a\u201c\u6240\u4ee5\u5979\u5c31\u627e\u673a\u4f1a\uff0c\u76f4\u5230\u90a3\u6076\u50e7\u65e0\u82b1\u6765\u4e86\uff0c\u5979\u77e5\u9053\u65e0\u82b1\u662f\u5c11\u6797\u7684\u5f1f\u5b50\uff0c\u5728\u6c5f\u6e56 \u4e2d\u4eba\u7f18\u53c8\u5f88\u597d\uff0c\u5979\u60f3\u501f\u65e0\u82b1\u7684\u529b\u91cf\u6765\u5bf9\u4ed8\u6211\uff0c\u6240\u4ee5\u7adf\u4e0d\u60dc\u4ee5\u8272\u76f8\u6765\u8bf1\u60d1\u65e0\u82b1\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u8fd9\u624d\u604d\u7136\u5927\u609f\u3002 \u3000\u3000\u4ed6\u672c\u6765\u5c31\u5728\u5947\u602a\uff0c\u53f8\u5f92\u9759\u53ea\u4e0d\u8fc7\u662f\u4e2a\u5c11\u5973\uff0c\u7eb5\u7136\u6000\u6625\uff0c\u4e5f\u4e0d\u81f3\u65bc\u5982\u6b64\u6deb\u8361\uff0c\u7adf\u4e3b\u52a8\u7684\u5411 \u65e0\u82b1\u6295\u6000\u9001\u62b1\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u624d\u77e5\u9053\uff0c\u539f\u6765\u53f8\u5f92\u9759\u5bf9\u65e0\u82b1\u4e5f\u6709\u76ee\u7684\uff0c\u4e24\u4eba\u6b63\u662f\u5c14\u865e\u6211\u8bc8\uff0c\u90fd\u6ca1\u6709\u5b58\u7740\u597d\u5fc3\u3002 \u3000\u3000\u9634\u59ec\u53c8\u9053\uff1a\u201c\u8c01\u77e5\u9053\u65e0\u82b1\u4e5f\u60f3\u5229\u7528\u5979\u6765\u5077\u5929\u4e00\u795e\u6c34\uff0c\u5f97\u624b\u4e4b\u5f8c\uff0c\u7acb\u523b\u5c31\u5c06\u5979\u5f03\u4e4b\u5982\u9057\uff0c \u5979\u90a3\u65f6\u809a\u91cc\u5df2\u6709\u4e86\u8eab\u5b55\uff0c\u6015\u6211\u4ee5\u95e8\u89c4\u5904\u7f6e\uff0c\u7adf\u542b\u6068\u81ea\u6740\u4e86\u3002\u201d \u3000\u3000\u8bf4\u5230\u8fd9\u91cc\uff0c\u5979\u8bed\u58f0\u4e5f\u5df2\u54fd\u54bd\uff0c\u60e8\u7136\u9053\uff1a\u201c\u5979\u5374\u4e0d\u77e5\u9053\u6211\u65e0\u8bba\u5982\u4f55\u90fd\u4e0d\u4f1a\u6740\u5979\u7684\uff0c\u76f4\u5230\u6b7b \u7684\u65f6\u5019\uff0c\u5979\u2026\u2026\u5979\u8fd8\u662f\u4e0d\u77e5\u9053\u6211\u5c31\u662f\u5979\u7684\u4eb2\u751f\u6bcd\u4eb2\u3002\u201d \u3000\u3000\u8fd9\u6bb5\u66f2\u6298\u800c\u60b2\u60e8\u7684\u516c\u6848\uff0c\u76f4\u5230\u73b0\u5728\uff0c\u624d\u5b8c\u5168\u6c34\u843d\u77f3\u51fa\u3002 \u3000\u3000\u695a\u7559\u9999\u957f\u53f9\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u4f60\u662f\u65e9\u5c31\u77e5\u9053\u6b64\u4e2d\u5185\u60c5\u7684\u4e86\u3002\u201d \u3000\u3000\u9634\u59ec\u9053\uff1a\u201c\u6211\u81ea\u7136\u77e5\u9053\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u90a3\u4f60\u4e3a\u4ec0\u9ebd\u8fd8\u6000\u7591\u662f\u522b\u4eba\u5077\u76d7\u4e86\u5929\u4e00\u795e\u6c34\u5462\uff1f\u201d \u3000\u3000\u9634\u59ec\u9053\uff1a\u201c\u6211\u6839\u672c\u4ece\u6765\u4e5f\u6ca1\u6709\u6000\u7591\u8fc7\u522b\u4eba\uff0c\u53ea\u4e0d\u8fc7\uff0c\u8fd9\u4ef6\u4e8b\u7684\u79d8\u5bc6\u7edd\u4e0d\u80fd\u8ba9\u522b\u4eba\u77e5\u9053\uff0c \u6240\u4ee5\u627e\u4e00\u5b9a\u8981\u627e\u4e2a\u66ff\u7f6a\u7f94\u7f8a\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u6545\u610f\u95ee\u9053\uff1a\u201c\u4f60\u627e\u7684\u662f\u8c01\uff1f\u201d \u3000\u3000\u9634\u59ec\u9053\uff1a\u201c\u695a\u7559\u9999\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u82e6\u7b11\u9053\uff1a\u201c\u4f60\u603b\u7b97\u627e\u5bf9\u4eba\u4e86\u3002\u201d \u3000\u3000\u9634\u59ec\u9053\uff1a\u201c\u6211\u53ea\u6709\u627e\u4ed6\uff0c\u56e0\u4e3a\u53ea\u6709\u4ed6\u624d\u80fd\u505a\u5f97\u51fa\u8fd9\u4e9b\u4e8b\u6765\uff0c\u6211\u53bb\u627e\u522b\u4eba\uff0c\u6c5f\u6e56\u4e2d\u4eba\u53c8\u600e \u4f1a\u76f8\u4fe1\u5462\uff1f\u201d \u3000\u3000\u5979\u8bed\u6c14\u4e2d\u5c45\u7136\u6ca1\u6709\u4e00\u70b9\u62b1\u6b49\u7684\u610f\u601d\uff0c\u53cd\u4f3c\u89c9\u5f97\u5f88\u5f97\u610f\u3002 \u3000\u3000\u695a\u7559\u9999\u5fcd\u4e0d\u4f4f\u9053\uff1a\u201c\u4f60\u4e3a\u4e86\u4fdd\u5168\u795e\u6c34\u5bab\u7684\u540d\u8a89\uff0c\u7adf\u4e0d\u60dc\u727a\u7272\u4e00\u4e2a\u65e0\u8f9c\u8005\u7684\u6027\u547d\u9ebd\uff1f\u201d \u3000\u3000\u9634\u59ec\u5389\u7b11\u9053\uff1a\u201c\u4e3a\u4e86\u4fdd\u5168\u795e\u6c34\u5bab\u7684\u540d\u8a89\uff0c\u6211\u4e0d\u60dc\u505a\u4efb\u4f55\u4e8b\u3002\u201d \u3000\u3000\u5979\u8bed\u58f0\u987f\u4e86\u534a\u664c\uff0c\u5ffd\u7136\u5e7d\u5e7d\u7684\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f55\u51b5\uff0c\u9664\u4e86\u4f60\u4e4b\u5916\uff0c\u522b\u7684\u7537\u4eba\uff0c\u5728\u6211 \u773c\u4e2d\u5b9e\u4e0d\u5982\u520d\u72d7\uff0c\u83ab\u8bf4\u6b7b\u4e00\u4e2a\u695a\u7559\u9999\uff0c\u5c31\u7b97\u6b7b\u4e00\u5343\u4e2a\uff0c\u4e00\u4e07\u4e2a\u53c8\u6709\u4f55\u59a8\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u6697\u4e2d\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u4f60\u5e76\u4e0d\u662f\u4e3a\u4e86\u4ed6\u5931\u7ea6\u624d\u8981\u6740\u4ed6\u7684\u3002\u201d \u3000\u3000\u9634\u59ec\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4ed6\u4e0d\u6765\u56fa\u7136\u8981\u6b7b\uff0c\u6765\u4e86\u66f4\u662f\u975e\u6b7b\u4e0d\u53ef\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u6c88\u9ed8\u4e86\u5f88\u4e45\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u4f60\u8fd8\u8bb0\u5f97\u6709\u4e2a\u4eba\u53eb\u67f3\u65e0\u7709\u55c4\uff1f\u201d \u3000\u3000\u9634\u59ec\u9053\uff1a\u201c\u6211\u5f53\u7136\u8bb0\u5f97\uff0c\u5979\u662f\u77f3\u89c2\u97f3\u7684\u5f1f\u5b50\u3002\u201d \u3000\u3000\u5979\u8bed\u58f0\u5ffd\u7136\u6fc0\u52a8\u8d77\u6765\uff0c\u5927\u58f0\u9053\uff1a\u201c\u4f60\u600e\u4f1a\u8ba4\u5f97\u5979\u7684\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u7528\u4e0d\u7740\u5403\u918b\uff0c\u6211\u5e76\u4e0d\u8ba4\u8bc6\u5979\uff0c\u53ea\u4e0d\u8fc7\u56e0\u4e3a\u5979\u6700\u8fd1\u505a\u4e86\u4ef6\u5f88\u8f70\u52a8 \u7684\u4e8b\uff0c\u6240\u4ee5\u627e\u624d\u77e5\u9053\u5979\u7684\u540d\u5b57\u3002\u201d \u3000\u3000\u9634\u59ec\u9053\uff1a\u201c\u5f88\u8f70\u52a8\u7684\u4e8b\uff1f\u662f\u4ec0\u9ebd\u4e8b\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u5979\u4e3a\u4e86\u8981\u6c42\u4f60\u4e3a\u5979\u89e3\u6bd2\uff0c\u6240\u4ee5\u5bb3\u6b7b\u4e86\u695a\u7559\u9999\u3002\u201d \u3000\u3000\u9634\u59ec\u9053\uff1a\u201c\u89e3\u5979\u7684\u6bd2\uff1f\u5979\u4e2d\u4e86\u4ec0\u9ebd\u6bd2\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u8bb6\u7136\u9053\uff1a\u201c\u4f60\u4e0d\u77e5\u9053\uff1f\u201d \u3000\u3000\u9634\u59ec\u9053\uff1a\u201c\u6211\u53ea\u77e5\u9053\u5979\u6839\u672c\u6ca1\u6709\u4e2d\u6bd2\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u8fd9\u624d\u771f\u7684\u6014\u4f4f\u4e86\u3002 \u3000\u3000\u539f\u6765\u8fd9\u53c8\u662f\u67f3\u65e0\u7709\u505a\u7684\u5708\u5957\uff0c\u8981\u4ed6\u6765\u81ea\u6295\u7f57\u7f51\uff0c\u539f\u6765\u4ed6\u6bd5\u7adf\u6ca1\u6709\u731c\u9519\uff0c\u5979\u679c\u7136\u771f\u7684\u662f\u77f3 \u89c2\u97f3\u6d3e\u5230\u4e2d\u539f\u5367\u5e95\u7684\u5978\u7ec6\u3002 \u3000\u3000\u695a\u7559\u9999\u6c14\u5f97\u51e0\u4e4e\u8fde\u8840\u90fd\u5410\u4e86\u51fa\u6765\uff0c\u4ed6\u672c\u6765\u4ee5\u4e3a\u81ea\u5df1\u6c38\u8fdc\u4e0d\u4f1a\u4e0a\u5973\u4eba\u7684\u5f53\uff0c\u8c01\u77e5\u5230\u5e95\u8fd8\u662f \u4e0a\u4e86\u4e00\u6b21\u3002 \u3000\u3000\u4ed6\u8fd9\u6b21\u5f53\u4e0a\u5f97\u53ef\u5b9e\u5728\u4e0d\u5c0f\u3002 \u3000\u3000\u9634\u59ec\u5ffd\u7136\u53c8\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\u6211\u8981\u600e\u9ebd\u6837\u5bf9\u4ed8\u4f60\u5417\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u82e6\u7b11\u9053\uff1a\u201c\u6211\u53ea\u5e0c\u671b\u4f60\u83ab\u8981\u5c06\u8fd9\u8863\u67dc\u6c88\u5728\u6e56\u5e95\u3002\u201d \u3000\u3000\u9634\u59ec\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u5b9e\u5728\u662f\u4e2a\u806a\u660e\u4eba\uff0c\u53ea\u53ef\u60dc\u806a\u660e\u4eba\u65f6\u5e38\u603b\u4f1a\u505a\u51fa\u4e00\u4e9b\u5f88\u7b28\u7684\u4e8b \u6765\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u5634\u91cc\u53d1\u82e6\uff0c\u55c4\u58f0\u9053\uff1a\u201c\u4f60\u96be\u9053\u771f\u7684\u4e0d\u613f\u8ba9\u6211\u89c1\u4f60\u6700\u5f8c\u4e00\u9762\uff1f\u201d \u3000\u3000\u9634\u59ec\u53c8\u6c88\u9ed8\u4e86\u5f88\u4e45\uff0c\u7a81\u7136\u51b7\u7b11\u9053\uff1a\u201c\u695a\u7559\u9999\uff0c\u4f60\u7528\u4e0d\u7740\u518d\u73a9\u82b1\u6837\u4e86\uff0c\u4f60\u65e2\u7136\u77e5\u9053\u4e86\u6211\u8fd9 \u9ebd\u591a\u79d8\u5bc6\uff0c\u4f60\u60f3\u6211\u8fd8\u4f1a\u8ba9\u4f60\u518d\u6d3b\u7740\u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u5168\u8eab\u90fd\u51c9\u4e86\uff0c\u80c3\u91cc\u76f4\u5192\u9178\u6c34\uff0c\u957f\u53f9\u9053\uff1a\u201c\u539f\u6765\u4f60\u5df2\u7ecf\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u9634\u59ec\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u672c\u6765\u7684\u786e\u5df2\u9a97\u8fc7\u4e86\u6211\uff0c\u4f46\u4f60\u5374\u4e0d\u8be5\u8bf4\u695a\u7559\u9999\u5df2\u88ab\u67f3\u65e0\u7709\u5bb3\u6b7b\u4e86\uff0c\u5c31\u7b97 \u67f3\u65e0\u7709\u771f\u5bb3\u6b7b\u4e86\u695a\u7559\u9999\uff0c\u4e5f\u7edd\u4e0d\u6562\u88ab\u522b\u4eba\u77e5\u9053\u7684\uff0c\u695a\u7559\u9999\u867d\u4e0d\u662f\u597d\u4eba\uff0c\u4f46\u670b\u53cb\u5374\u4e0d\u5c11\uff0c\u5979\u96be \u9053\u4e0d\u6015\u522b\u4eba\u627e\u5979\u62a5\u4ec7\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u53f9\u9053\uff1a\u201c\u6211\u5b9e\u5728\u4f4e\u4f30\u4e86\u4f60\uff0c\u4f60\u6bd4\u6211\u60f3\u50cf\u4e2d\u8fd8\u8981\u7cbe\u660e\u5f97\u591a\u3002\u201d \u3000\u3000\u9634\u59ec\u9053\uff1a\u201c\u4f46\u6211\u5374\u6ca1\u6709\u4f4e\u4f30\u4f60\uff0c\u6211\u77e5\u9053\u5c31\u51ed\u67f3\u65e0\u7709\uff0c\u662f\u4e07\u4e07\u5bb3\u4e0d\u6b7b\u4f60\u7684\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u5ffd\u7136\u5927\u7b11\u9053\uff1a\u201c\u8fd9\u4e5f\u5c31\u96be\u602a\u4f60\u4e0d\u6562\u653e\u6211\u51fa\u53bb\uff0c\u548c\u6211\u4e00\u51b3\u751f\u6b7b\u4e86\u3002\u201d \u7b2c\u4e09\u5341\u7ae0\u3000\u6c34\u5e95\u5927\u6218>> \u53e4\u9f99\u300a\u695a\u7559\u9999\u7cfb\u5217\xb7\u753b\u7709\u9e1f\u300b \u7b2c\u4e09\u5341\u7ae0\u3000\u6c34\u5e95\u5927\u6218 \u3000\u3000\u9634\u59ec\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u6fc0\u5c06\u4e5f\u6ca1\u6709\u7528\uff0c\u6211\u8981\u6740\u4f60\uff0c\u53ea\u4e0d\u8fc7\u662f\u4e3e\u624b\u4e4b\u52b3\u800c\u5df2\uff0c\u53ef\u662f\u6211\u53c8\u4f55\u82e6\u810f \u4e86\u81ea\u5df1\u7684\u624b\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4f46\u4f60\u82e5\u4e0d\u8ba9\u6211\u51fa\u6765\uff0c\u6709\u4ef6\u4e8b\u4f60\u5c31\u6c38\u8fdc\u4e0d\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u9634\u59ec\u679c\u7136\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u751a\u9ebd\u4e8b\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u60a0\u7136\u9053\uff1a\u201c\u96c4\u5a18\u5b50\u65e2\u7136\u5e76\u4e0d\u5728\u8863\u67dc\u91cc\uff0c\u90a3\u9ebd\u4ed6\u5728\u90a3\u91cc\u5462\uff1f\u8fd9\u79d8\u5bc6\u9664\u4e86\u6211\u4e4b\u5916\uff0c\u4e16 \u4e0a\u53ea\u6015\u518d\u4e5f\u6ca1\u6709\u7b2c\u4e8c\u4e2a\u4eba\u80fd\u544a\u8bc9\u4f60\u3002\u201d \u3000\u3000\u4ed6\u53e3\u6c14\u542c\u6765\u867d\u4f3c\u5f88\u60a0\u7136\uff0c\u5176\u5b9e\u6697\u4e2d\u5374\u634f\u7740\u628a\u51b7\u6c57\u3002 \u3000\u3000\u8fd9\u4e5f\u662f\u4ed6\u6700\u5f8c\u7684\u4e00\u4e2a\u673a\u4f1a\u4e86\uff0c\u4ed6\u53ea\u5e0c\u671b\u9634\u59ec\u4e5f\u548c\u522b\u7684\u5973\u4eba\u4e00\u6837\uff0c\u4e5f\u6709\u597d\u5947\u5fc3\uff0c\u4e00\u5b9a\u8981\u903c \u4ed6\u8bf4\u51fa\u8fd9\u79d8\u5bc6\u3002 \u3000\u3000\u53ea\u8981\u9634\u59ec\u80af\u653e\u4ed6\u51fa\u53bb\uff0c\u4ed6\u81f3\u5c11\u8fd8\u6709\u4e07\u4e00\u7684\u5e0c\u671b\uff0c\u5426\u5219\u4ed6\u5c31\u8981\u88ab\u6d3b\u6d3b\u56f0\u6b7b\u5728\u8fd9\u8863\u67dc\u91cc\uff0c\u6c38 \u8fdc\u518d\u4e5f\u89c1\u4e0d\u7740\u5929\u65e5\u3002 \u3000\u3000\u8c01\u77e5\u9053\u9634\u59ec\u975e\u4f46\u6ca1\u6709\u95ee\uff0c\u8fde\u8bdd\u90fd\u4e0d\u8bf4\u4e86\u3002 \u3000\u3000\u997c\u4e86\u534a\u664c\uff0c\u695a\u7559\u9999\u53ea\u542c\u5230\u673a\u7c27\u54cd\u52a8\u58f0\uff0c\u9634\u59ec\u5f77\u4f5b\u5728\u5f00\u542f\u4e00\u4e2a\u79d8\u5bc6\u7684\u95e8\u6237\uff0c\u6309\u7740\uff0c\u5c31\u542c\u5f97 \u5979\u6c88\u58f0\u9053\uff1a\u201c\u5feb\u5c06\u8fd9\u8863\u67dc\u62ac\u51fa\u53bb\uff0c\u6c88\u5728\u6e56\u5e95\u3002\u201d \u3000\u3000\u8fd9\u5b9e\u5728\u662f\u4e00\u4e2a\u5f88\u5947\u7279\u7684\u547d\u4ee4\uff1a\u201c\u5979\u4e3a\u4ec0\u9ebd\u8981\u5c06\u81ea\u5df1\u653e\u8863\u670d\u7684\u67dc\u5b50\u6c88\u5230\u6c34\u4e2d\u53bb\u5462\uff1f\u201d\u4f46\u5979 \u7684\u5f1f\u5b50\u5fc3\u91cc\u7eb5\u7136\u6000\u7591\uff0c\u5634\u91cc\u4e5f\u4e0d\u6562\u95ee\u51fa\u6765\u3002 \u3000\u3000\u5979\u4eec\u53ea\u662f\u606d\u58f0\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u9634\u59ec\u53c8\u9053\uff1a\u201c\u65e0\u8bba\u8863\u67dc\u91cc\u53d1\u751f\u4ec0\u9ebd\u58f0\u97f3\uff0c\u4f60\u4eec\u90fd\u5f53\u6ca1\u6709\u542c\u5230\uff0c\u77e5\u9053\u9ebd\uff1f\u201d \u3000\u3000\u5979\u7684\u5f1f\u5b50\u53c8\u606d\u58f0\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u7d22\u6027\u4ec0\u9ebd\u8bdd\u90fd\u4e0d\u8bf4\u4e86\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u77e5\u9053\u6c34\u6bcd\u4ee4\u51fa\u5fc5\u884c\uff0c\u4ed6\u65e0\u8bba\u8bf4\u4ec0\u9ebd\u90fd\u5df2\u6ca1\u6709\u7528\u4e86\uff0c\u4ed6\u53ea\u6068\u81ea\u5df1\u7684\u8fd0\u6c14\u5b9e\u5728\u592a\u574f\u3002 \u3000\u3000\u8fd9\u4e16\u4e0a\u6ca1\u6709\u597d\u5947\u5fc3\u7684\u5973\u4eba\u5e76\u4e0d\u591a\uff0c\u6709\u4e9b\u7537\u4eba\u5c31\u7b97\u627e\u4e00\u8f88\u5b50\u4e5f\u672a\u5fc5\u627e\u5f97\u5230\uff0c\u6b64\u756a\u5c45\u7136\u7adf\u88ab \u4ed6\u9047\u89c1\u4e86\u4e00\u4e2a\u3002 \u3000\u3000\u8863\u67dc\u5df2\u88ab\u62ac\u4e86\u8d77\u6765\u3002 \u3000\u3000\u6ca1\u6709\u8fc7\u591a\u4e45\uff0c\u5c31\u6709\u6c34\u6d41\u5165\u4e86\u8863\u67dc\u3002 \u3000\u3000\u695a\u7559\u9999\u6574\u4e2a\u4eba\u53c8\u88ab\u6ce1\u5728\u6c34\u91cc\u4e86\u3002 \u3000\u3000\u4f46\u8fd9\u6b21\uff0c\u6c34\u5e76\u6ca1\u6709\u50cf\u4ee5\u524d\u90a3\u9ebd\u6837\u5e26\u7ed9\u4ed6\u4e00\u79cd\u6e05\u51c9\u9002\u610f\u7684\u611f\u89c9\uff0c\u56e0\u4e3a\u4ed6\u5df2\u77e5\u9053\u8fd9\u6c34\u8fc7\u4e0d\u4e86 \u591a\u4e45\uff0c\u5c31\u5c06\u8981\u6eb6\u5316\u4ed6\u7684\u751f\u547d\uff0c\u8150\u70c2\u4ed6\u7684\u9aa8\u8089\uff0c\u90a3\u65f6\u695a\u7559\u9999\u8fd9\u4e2a\u4eba\u5c31\u5c06\u5b8c\u5b8c\u5168\u5168\u6d88\u5931\u5728\u6c34\u91cc\u3002 \u3000\u3000\u4ed6\u5fcd\u4e0d\u4f4f\u6697\u4e2d\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6c34\u5144\u6c34\u5144\uff0c\u6211\u4e00\u5411\u90fd\u6ca1\u6709\u5bf9\u4e0d\u8d77\u4f60\uff0c\u4f60\u4e3a\u4ec0\u9ebd\u5374\u8981\u5bf9\u4e0d \u8d77\u6211\u5462\uff1f\u201d \u3000\u3000\u76f4\u5230\u73b0\u5728\u4e3a\u6b62\uff0c\u4ed6\u4ece\u4e0d\u77e5\u9053\u7edd\u671b\u662f\u4ec0\u9ebd\u6ecb\u5473\u3002 \u3000\u3000\u73b0\u5728\uff0c\u4ed6\u603b\u7b97\u77e5\u9053\u4e86\u3002 \u3000\u3000\u6c34\u7684\u538b\u529b\u5df2\u8d8a\u6765\u8d8a\u91cd\uff0c\u695a\u7559\u9999\u4ec0\u9ebd\u90fd\u770b\u4e0d\u5230\uff0c\u4f46\u4e5f\u77e5\u9053\u77f3\u67dc\u5df2\u5c06\u8981\u88ab\u62ac\u81f3\u6e56\u5fc3\u3002 \u3000\u3000\u4f46\u5ffd\u7136\u95f4\uff0c\u6c34\u7684\u538b\u529b\u53c8\u6e10\u6e10\u51cf\u8f7b\u4e86\uff0c\u6309\u7740\uff0c\u6c34\u53c8\u6e10\u6e10\u81ea\u77f3\u67dc\u4e2d\u6f0f\u4e86\u51fa\u53bb\uff0c\u7adf\u53c8\u88ab\u62ac\u56de\u6c34 \u6bcd\u7684\u5bdd\u5ba4\u3002 \u3000\u3000\u53ea\u542c\u6c34\u6bcd\u9053\uff1a\u201c\u5c31\u653e\u5728\u8fd9\u91cc\uff0c\u51fa\u53bb\u3002\u201d \u3000\u3000\u201c\u7830\u201d\u7684\u4e00\u58f0\uff0c\u77f3\u67dc\u53c8\u63a5\u89e6\u5230\u77f3\u5730\uff0c\u695a\u7559\u9999\u8eab\u5b50\u4e00\u9707\uff0c\u5c31\u7a33\u5b9a\u4e0b\u6765\uff0c\u4ed6\u7b2c\u4e00\u6b21\u53d1\u89c9\u811a\u8e0f \u5b9e\u5730\u539f\u6765\u7adf\u662f\u5982\u6b64\u6109\u5feb\u7684\u4e8b\u3002 \u3000\u3000\u795e\u6c34\u5bab\u5f1f\u5b50\u79bb\u5f00\u4e4b\u5f8c\uff0c\u77f3\u67dc\u5916\u5c31\u53c8\u6c88\u5bc2\u4e86\u4e0b\u6765\uff0c\u4ed6\u53ea\u80fd\u542c\u5230\u6c34\u6bcd\u7684\u547c\u5438\u58f0\u8d8a\u6765\u8d8a\u6025\u4fc3\uff0c \u663e\u89c1\u5979\u7684\u5fc3\u60c5\u5df2\u6e10\u6e10\u6fc0\u52a8\u3002 \u3000\u3000\u695a\u7559\u9999\u7b11\u4e86\uff0c\u5927\u58f0\u9053\uff1a\u201c\u6211\u65e9\u5c31\u77e5\u9053\u4f60\u4f1a\u6539\u53d8\u4e3b\u610f\u7684\uff0c\u6211\u82e5\u88ab\u6df9\u6b7b\uff0c\u4f60\u5c31\u6c38\u8fdc\u518d\u4e5f\u4e0d\u77e5 \u9053\u96c4\u5a18\u5b50\u7a76\u7adf\u5728\u90a3\u91cc\u4e86\u3002\u201d \u3000\u3000\u9634\u59ec\u679c\u7136\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u4ed6\u5728\u90a3\u91cc\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u60a0\u7136\u9053\uff1a\u201c\u4ed6\u4e5f\u8bb8\u5df2\u7ecf\u6b7b\u4e86\uff0c\u4e5f\u8bb8\u8fd8\u6d3b\u7740\uff0c\u4e5f\u8bb8\u8fdc\u5728\u5929\u8fb9\uff0c\u4e5f\u8bb8\u5c31\u8fd1\u5728\u773c\u524d\uff0c\u4f60 \u82e5\u60f3\u6211\u544a\u8bc9\u4f60\uff0c\u53ea\u6709\u4e00\u4e2a\u6cd5\u5b50\u3002\u201d \u3000\u3000\u9634\u59ec\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u96be\u9053\u60f3\u6211\u653e\u4e86\u4f60\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u6211\u867d\u7136\u4e0d\u662f\u4e2a\u751f\u610f\u4eba\uff0c\u53ef\u662f\u4e5f\u77e5\u9053\u505a\u4e70\u5356\u4e00\u5b9a\u8981\u516c\u9053\uff0c\u8fd9\u6d88\u606f\u867d\u7136\u5f88\u73cd \u8d35\uff0c\u5374\u8fd8\u662f\u6362\u4e0d\u4e86\u695a\u7559\u9999\u7684\u4e00\u6761\u547d\uff0c\u6211\u7edd\u4e0d\u6f2b\u5929\u8981\u4ef7\uff0c\u4e5f\u514d\u5f97\u4f60\u5c31\u5730\u8fd8\u94b1\u3002\u201d \u3000\u3000\u9634\u59ec\u9053\uff1a\u201c\u4f60\u65e2\u7136\u77e5\u9053\uff0c\u8fd8\u60f3\u600e\u6837\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u6211\u53ea\u8981\u4f60\u653e\u6211\u51fa\u6765\uff0c\u8ba9\u6211\u548c\u4f60\u4f5c\u4e00\u573a\u9c8d\u5e73\u7684\u51b3\u6597\u3002\u201d \u3000\u3000\u9634\u59ec\u9053\uff1a\u201c\u90a3\u9ebd\u4f60\u8fd8\u662f\u5fc5\u6b7b\u65e0\u7591\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u5927\u7b11\u9053\uff1a\u201c\u4f60\u4ee5\u4e3a\u6211\u5f88\u6015\u6b7b\u5417\uff1f\u6211\u53ea\u4e0d\u8fc7\u89c9\u5f97\u8fd9\u9ebd\u6837\u6b7b\uff0c\u672a\u514d\u592a\u7a9d\u56ca\u800c\u5df2\uff0c\u6211\u6d3b \u5f97\u5feb\u5feb\u4e50\u4e50\uff0c\u6b7b\u4e5f\u8981\u6b7b\u5f97\u8f70\u8f70\u70c8\u70c8\u3002\u201d \u3000\u3000\u9634\u59ec\u5f88\u4e45\u6ca1\u6709\u8bf4\u8bdd\u3002 \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4f46\u4f60\u82e5\u771f\u7684\u4e0d\u6562\u548c\u6211\u52a8\u624b\uff0c\u6211\u4e5f\u7edd\u4e0d\u52c9\u5f3a\u4f60\uff0c\u6211\u82e5\u662f\u4f60\uff0c\u6015\u4e5f\u4e0d\u80af\u5c06\u695a\u7559 \u9999\u653e\u51fa\u6765\u7684\u3002\u201d \u3000\u3000\u9634\u59ec\u8fd8\u662f\u6ca1\u6709\u8bf4\u8bdd\uff0c\u4f46\u77f3\u67dc\u5374\u4f20\u6765\u201c\u683c\u201d\u7684\u4e00\u54cd\u3002 \u3000\u3000\u7136\u5f8c\uff0c\u624d\u542c\u5f97\u9634\u59ec\u51b7\u51b7\u9053\uff1a\u201c\u67dc\u5df2\u5f00\u4e86\uff0c\u4f60\u51fa\u6765\u5427\uff0c\u53ea\u4e0d\u8fc7\u4f60\u6700\u597d\u8bb0\u4f4f\uff0c\u4f60\u51fa\u6765\u4e4b\u5f8c\uff0c \u975e\u4f46\u6b7b\u5f97\u66f4\u5feb\uff0c\u800c\u4e14\u4e00\u5b9a\u6b7b\u5f97\u66f4\u60e8\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u957f\u957f\u5410\u51fa\u53e3\u6c14\uff0c\u5583\u5583\u9053\uff1a\u201c\u8c22\u5929\u8c22\u5730\uff0c\u4f60\u603b\u7b97\u662f\u4e2a\u5973\u4eba\uff0c\u8fd8\u4e0d\u81f3\u65bc\u4e00\u70b9\u597d\u5947\u4e4b\u5fc3 \u4e5f\u6ca1\u6709\uff0c\u4e00\u4e2a\u5973\u4eba\u82e5\u8fde\u5979\u7684\u60c5\u4eba\u7684\u4e0b\u843d\u90fd\u4e0d\u60f3\u77e5\u9053\uff0c\u90a3\u9ebd\u5929\u4e0b\u6015\u8981\u5927\u4e71\u4e86\u3002\u201d \u3000\u3000\u9634\u59ec\u5389\u58f0\u9053\uff1a\u201c\u4ed6\u7a76\u7adf\u662f\u6b7b\u662f\u6d3b\uff1f\u7a76\u7adf\u5728\u90a3\u91cc\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4f60\u662f\u5e0c\u671b\u4ed6\u5df2\u6b7b\u4e86\uff1f\u8fd8\u662f\u5e0c\u671b\u4ed6\u4f9d\u65e7\u6d3b\u7740\uff1f\u4f60\u2026\u2026\u201d \u3000\u3000\u4ed6\u4e00\u9762\u8bf4\u8bdd\uff0c\u4e00\u9762\u5df2\u63a8\u5f00\u4e86\u77f3\u67dc\u7684\u95e8\u8d70\u4e86\u51fa\u6765\u3002 \u3000\u3000\u8bf4\u5230\u8fd9\u91cc\u65f6\uff0c\u4ed6\u5ffd\u7136\u6014\u4f4f\uff0c\u56e0\u4e3a\u4ed6\u53d1\u89c9\u7ad9\u5728\u4ed6\u9762\u524d\u7684\u9634\u59ec\uff0c\u7adf\u5df2\u4e0d\u518d\u662f\u65b9\u4ed6\u89c1\u5230\u7684\u9634\u59ec \u4e86\u3002 \u3000\u3000\u65b9\u7684\u9634\u59ec\u8fd8\u662f\u72ec\u6b65\u5929\u4e0b\u7684\u795e\u6c34\u5bab\u4e3b\uff0c\u4e00\u4e3e\u4e00\u52a8\u4e2d\u90fd\u5145\u6ee1\u4e86\u5a01\u4e25\u548c\u81ea\u4fe1\uff0c\u4ee4\u4eba\u4e0d\u6562\u4e0d\u5bf9\u5979 \u5c0a\u656c\u3002 \u3000\u3000\u4f46\u73b0\u5728\u7684\u9634\u59ec\u5374\u5df2\u53d8\u6210\u4e86\u4e00\u4e2a\u5e73\u51e1\u7684\u5973\u4eba\uff0c\u4e00\u53cc\u6e05\u6f88\u660e\u9510\u7684\u773c\u775b\u91cc\uff0c\u5df2\u5145\u6ee1\u4e86\u7eb7\u4e71\u7684\u60c5 \u6b32\uff0c\u5a01\u4e25\u9547\u5b9a\u7684\u9762\u5bb9\u4e5f\u53d8\u5f97\u7126\u6025\u800c\u6fc0\u52a8\uff0c\u5e73\u6574\u7684\u8863\u886b\u4e5f\u8d77\u4e86\u7ec9\u7eb9\uff0c\u751a\u81f3\u8fde\u4e00\u53cc\u624b\u90e8\u5f00\u59cb\u6709\u4e9b \u53d1\u6296\u3002 \u3000\u3000\u695a\u7559\u9999\u518d\u4e5f\u60f3\u4e0d\u5230\u4e00\u4e2a\u5973\u4eba\u4f1a\u5728\u7247\u523b\u4e4b\u95f4\u53d1\u751f\u8fd9\u9ebd\u5927\u7684\u53d8\u5316\uff0c\u4e0d\u53ef\u4e00\u4e16\u7684\u795e\u6c34\u5bab\u4e3b\uff0c\u5ffd \u7136\u95f4\u5c31\u53d8\u6210\u4e86\u4e00\u4e2a\u5e73\u51e1\u7684\u5973\u4eba\u3002 \u3000\u3000\u8fd9\u6539\u53d8\u5b9e\u5728\u592a\u5927\uff0c\u5b9e\u5728\u4ee4\u4eba\u4e0d\u53ef\u601d\u8bae\uff0c\u5979\u5728\u8fd9\u6bb5\u65f6\u95f4\u91cc\u6240\u5fcd\u53d7\u7684\u75db\u82e6\u548c\u6298\u78e8\uff0c\u6015\u4e5f\u4e0d\u662f \u522b\u4eba\u6240\u80fd\u60f3\u50cf\u7684\u3002 \u3000\u3000\u695a\u7559\u9999\u53cd\u800c\u6709\u4e9b\u4e0d\u5fcd\uff0c\u957f\u53f9\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u4f60\u5bf9\u4ed6\u5c45\u7136\u771f\u7684\u662f\u4e00\u5f80\u60c5\u6df1\uff0c\u4ed6\u82e5\u80fd\u65e9\u4e9b\u77e5 \u9053\uff0c\u6240\u6709\u7684\u4e8b\u4e5f\u8bb8\u90fd\u4f1a\u53d8\u5f97\u597d\u4e9b\u7684\uff0c\u53ea\u53ef\u60dc\u4ed6\u6c38\u8fdc\u4e5f\u4e0d\u4f1a\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u9634\u59ec\u7d27\u63e1\u8d77\u53cc\u624b\uff0c\u55c4\u58f0\u9053\uff1a\u201c\u4ed6\u2026\u2026\u4ed6\u5df2\u6c38\u8fdc\u2026\u2026\u201d \u3000\u3000\u695a\u7559\u9999\u53f9\u9053\uff1a\u201c\u4ed6\u82e5\u77e5\u9053\u4e16\u4e0a\u8fd8\u6709\u4e2a\u4eba\u5728\u6b7b\u5fc3\u584c\u5730\u7684\u7231\u7740\u4ed6\uff0c\u4e5f\u8bb8\u8fd8\u4e0d\u4f1a\u6b7b\uff0c\u53ea\u4e0d\u8fc7\uff0c \u4e00\u4e2a\u7537\u4eba\u82e5\u80fd\u5f97\u5230\u4f60\u5bf9\u4ed6\u8fd9\u6837\u7684\u771f\u60c5\uff0c\u6b7b\u53c8\u4f55\u59a8\u3002\u201d \u3000\u3000\u9634\u59ec\u8eab\u5b50\u5934\u6296\u7740\uff0c\u5ffd\u7136\u51b7\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u60f3\u4ee5\u6b64\u6765\u6270\u4e71\u6211\u7684\u5fc3\u795e\uff0c\u4f7f\u6211\u65e0\u6cd5\u548c\u4f60\u4ea4 \u624b\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u6211\u672c\u6765\u7684\u786e\u6709\u8fd9\u6253\u7b97\uff0c\u600e\u5948\u6211\u4ece\u6765\u4e5f\u4e0d\u5fcd\u5fc3\u9a97\u4e00\u4e2a\u4f24\u5fc3\u7684\u5973 \u4eba\u3002\u201d \u3000\u3000\u9634\u59ec\u5389\u559d\u9053\uff1a\u201c\u662f\u4e0d\u662f\u4f60\u6740\u4e86\u4ed6\u7684\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u7a76\u7adf\u662f\u8c01\u6740\u4e86\u4ed6\uff1f\u5230\u73b0\u5728\u4f60\u8fd8\u731c\u4e0d\u51fa\u9ebd\uff1f\u201d \u3000\u3000\u9634\u59ec\u8eab\u5b50\u4e00\u9707\uff0c\u4f3c\u4e4e\u8fd0\u7ad9\u90fd\u7ad9\u4e0d\u7a33\u4e86\u3002 \u3000\u3000\u300e\u695a\u7559\u9999\u4f20\u5947\u300f\u53775\u753b\u7709\u9e1f(p111-\u5b8c) \u3000\u3000\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u5979\u5f77\u4f5b\u53c8\u82cd\u8001\u4e86\u8bb8\u591a\uff0c\u9eef\u7136\u81ea\u8bed\u9053\uff1a\u201c\u50bb\u5b69\u5b50\uff0c\u4f60\u4e3a\u4ec0\u9ebd\u8981\u8fd9\u6837\u505a\uff1f\u201d \u3000\u3000\u695a\u7559\u6773\u4e00\u5b57\u5b57\u9053\uff1a\u201c\u5979\u4e3a\u4ec0\u9ebd\u8981\u8fd9\u6837\u505a\uff0c\u4f60\u4e5f\u8be5\u77e5\u9053\u7684\u3002\u201d \u3000\u3000\u9634\u59ec\u7684\u624b\u98a4\u6296\u7740\uff0c\u5979\u662f\u60f3\u627e\u4e00\u4e2a\u53ef\u4ee5\u652f\u6301\u8eab\u4f53\u7684\u5730\u65b9\uff0c\u9664\u4e86\u201c\u60c5\u611f\u201d\u4e4b\u5916\uff0c\u4e16\u4e0a\u8fd8\u6709\u4ec0 \u9ebd\u4e8b\u80fd\u7ed9\u5979\u5982\u6b64\u5de8\u5927\u7684\u6253\u51fb\uff1f \u3000\u3000\u5979\u7684\u906d\u9047\u5b9e\u5728\u503c\u5f97\u5411\u60c5\uff0c\u4f46\u5979\u7684\u201c\u60c5\u611f\u201d\u5374\u53c8\u5b9e\u5728\u592a\u8352\u5510\uff0c\u695a\u7559\u6773\u4e5f\u4e0d\u77e5\u5979\u7a76\u7adf\u662f\u53ef \u601c\uff1f\u662f\u53ef\u6068\uff0c\u8fd8\u662f\u53ef\u7b11\uff1f \u3000\u3000\u695a\u7559\u6773\u53f9\u9053\uff1a\u201c\u6211\u672c\u4e0d\u60f3\u6270\u4e71\u4f60\u7684\u5fc3\u795e\uff0c\u53ef\u662f\u4f60\u73b0\u5728\u7684\u786e\u4e0d\u9002\u65bc\u548c\u4eba\u52a8\u624b\uff0c\u6211\u4e5f\u4e0d\u613f\u8d81 \u4eba\u4e4b\u5371\u3002\u201d \u3000\u3000\u9634\u59ec\u7684\u8eab\u5b50\u5ffd\u53c8\u67aa\u4e00\u822c\u633a\u7acb\u4e86\u8d77\u6765\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u6740\u4eba\u7528\u4e0d\u7740\u7b49\u5230\u5fc3\u60c5\u597d\u7684\u65f6\u5019\uff0c\u4f60\u53ea\u7ba1 \u5148\u51fa\u624b\u5427\uff01\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4f60\u73b0\u5728\u771f\u7684\u80fd\u51fa\u624b\uff1f\u201d \u3000\u3000\u9634\u59ec\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u7528\u4e0d\u7740\u4e3a\u6211\u62c5\u5fc3\uff0c\u8fd8\u662f\u5148\u4e3a\u4f60\u81ea\u5df1\u62c5\u5fc3\u5427\uff01\u4e14\u8981\u4f60\u80fd\u6321\u5f97\u8fc7\u6211\u5341\u62db\uff0c \u4e5f\u5c31\u4e0d\u6789\u4f60\u5b66\u6b66\u4e00\u4e16\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u6773\u7b11\u9053\uff1a\u201c\u4f60\u53e3\u6c14\u5012\u771f\u4e0d\u5c0f\u3002\u201d \u3000\u3000\u201c\u5c0f\u201d\u5b57\u51fa\u53e3\uff0c\u4ed6\u5df2\u7bad\u4e00\u822c\u5411\u9634\u59ec\u51b2\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u81ea\u5df1\u552f\u4e00\u80fd\u80dc\u8fc7\u5bf9\u65b9\u4e4b\u5904\uff0c\u5c31\u662f\u4e2a\u201c\u5feb\u201d\u5b57\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u5c3d\u91cf\u5229\u7528\u8fd9\u201c\u5feb\u201d\u5b57\uff0c\u53ea\u8981\u4ed6\u80fd\u62a2\u5f97\u4e00\u5239\u90a3\u95f4\u7684\u5148\u673a\uff0c\u4ed6\u5c31\u6216\u8bb8\u8fd8\u6709\u6218\u80dc\u7684\u5e0c \u671b\u3002 \u3000\u3000\u4ed6\u51fa\u624b\u5b9e\u5728\u5feb\uff0c\u5feb\u5982\u6025\u98ce\uff0c\u4f7f\u5982\u95ea\u7535\u3002 \u3000\u3000\u8c01\u77e5\u4ed6\u521a\u4e00\u51fa\u624b\uff0c\u9634\u59ec\u7684\u624b\u638c\u4e00\u6325\uff0c\u5c31\u7acb\u523b\u6709\u4e00\u80a1\u5947\u5f02\u7684\u529b\u91cf\u963b\u4f4f\u4e86\u4ed6\u7684\u53bb\u8def\uff0c\u8fd9\u80a1\u529b \u91cf\u5982\u6d6a\u6f6e\u521d\u8d77\uff0c\u6f8e\u6e43\u4e0d\u7edd\u3002 \u3000\u3000\u695a\u7559\u9999\u83ab\u8bf4\u6839\u672c\u65e0\u6cd5\u62a2\u5f97\u5148\u673a\uff0c\u6839\u672c\u5c31\u8fd1\u4e0d\u4e86\u5979\u7684\u8eab\u3002 \u3000\u3000\u4ed6\u672c\u4ee5\u4e3a\u201c\u6c34\u6bcd\u201d\u9634\u59ec\u4e5f\u548c\u77f3\u89c2\u97f3\u4e00\u6837\uff0c\u662f\u4ee5\u5947\u8be1\u7684\u8eab\u5f62\u548c\u62db\u5f0f\u89c1\u957f\uff0c\u6240\u4ee5\u4ed6\u8ba4\u4e3a\u81ea\u5df1 \u6216\u8bb8\u8fd8\u80fd\u4ee5\u5e94\u53d8\u548c\u6025\u667a\u6765\u5236\u654c\u673a\u5148\u3002 \u3000\u3000\u4ed6\u548c\u77f3\u89c2\u97f3\u90a3\u4e00\u621f\uff0c\u4e5f\u6b63\u662f\u5982\u6b64\u3002 \u3000\u3000\u5374\u4e0d\u77e5\u201c\u6c34\u6bcd\u201d\u9634\u59ec\u7684\u6b66\u529f\u7adf\u548c\u5929\u4e0b\u5404\u95e8\u5404\u6d3e\u7684\u6b66\u529f\u90fd\u4e0d\u76f8\u5411\uff0c\u5979\u7684\u6b66\u529f\u7adf\u662f\u81ea\u201c\u6c34\u201d \u4e2d\u7ec3\u51fa\u6765\u7684\u3002 \u3000\u3000\u5979\u7684\u529b\u91cf\u4e5f\u6b63\u548c\u201c\u6c34\u201d\u4e00\u6837\uff0c\u770b\u6765\u867d\u67d4\u548c\u5e73\u9759\uff0c\u5176\u5b9e\u5374\u662f\u65e0\u671b\u4e0d\u6467\uff0c\u65e0\u7269\u53ef\u6321\u7684\uff0c\u6ef4\u6c34 \u5df2\u80fd\u7a7f\u9636\uff0c\u6d2a\u6c34\u66f4\u80fd\u5c71\u5cb3\u79fb\u5f62\uff0c\u57ce\u5e02\u6bc1\u706d\uff0c\u81ea\u53e4\u4ee5\u6765\uff0c\u5929\u4e0b\u5c31\u4ece\u6765\u6ca1\u6709\u4efb\u4f55\u4e00\u79cd\u4e1c\u897f\u80fd\u62b5\u6297 \u6c34\u7684\u529b\u91cf\u3002 \u3000\u3000\u695a\u7559\u9999\u8fd9\u624d\u53d1\u73b0\u4e16\u4e0a\u6700\u53ef\u6015\u7684\u539f\u6765\u5c31\u662f\u6c34\u3002 \u3000\u3000\u65e0\u60c5\u7684\u6c34\u3002 \u3000\u3000\u201c\u6c34\u6bcd\u201d\u7684\u51fa\u624b\u66f4\u65e0\u60c5\uff0c\u5979\u7684\u8eab\u5f62\u8fd8\u672a\u6539\u53d8\uff0c\u90a3\u79cd\u6f8e\u6e43\u5982\u6f6e\u7684\u638c\u529b\u5df2\u5c06\u695a\u7559\u9999\u538b\u5f97\u900f\u4e0d \u8fc7\u6c14\u6765\u3002 \u3000\u3000\u4ed6\u8fde\u53d8\u51e0\u79cd\u8eab\u6cd5\uff0c\u4f46\u53ea\u8981\u9634\u59ec\u4e00\u6325\u624b\uff0c\u4ed6\u7684\u653b\u52bf\u5c31\u88ab\u963b\u904f\uff0c\u4ed6\u6839\u672c\u65e0\u6cd5\u7ed9\u9634\u59ec\u4e1d\u6beb\u5a01 \u80c1\u3002 \u3000\u3000\u695a\u7559\u9999\u53f9\u4e86\u53e3\u6c14\uff1a\u9053\uff1a\u201c\u96be\u602a\u6c5f\u6e56\u4e2d\u4eba\u4eba\u6015\u4f60\uff0c\u65e0\u8bba\u4efb\u4f55\u4eba\u548c\u4f60\u52a8\u624b\uff0c\u7684\u786e\u6ca1\u6709\u6218\u80dc\u7684 \u5e0c\u671b\u3002\u201d \u3000\u3000\u4ed6\u5634\u91cc\u8bf4\u7740\u8bdd\uff0c\u53c8\u6539\u53d8\u4e86\u4e03\u516b\u79cd\u8eab\u6cd5\u3002 \u3000\u3000\u867d\u7136\u660e\u77e5\u65e0\u8bba\u4f7f\u51fa\u4efb\u4f55\u62db\u5f0f\u6765\u90fd\u662f\u65e0\u7528\u7684\uff0c\u4f46\u4ed6\u7684\u8eab\u5f62\u8fd8\u662f\u8981\u77ac\u606f\u4e0d\u505c\u7684\u6539\u53d8\uff0c\u56e0\u4e3a\u53ea \u8981\u4ed6\u8eab\u5f62\u4e00\u505c\u987f\uff0c\u5c31\u7acb\u523b\u8981\u88ab\u90a3\u80a1\u5de8\u5927\u7684\u529b\u91cf\u538b\u6241\u3002 \u3000\u3000\u5c3a\u542c\u6c34\u6bcd\u51b7\u51b7\u9053\uff1a\u201c\u6211\u5df2\u8ba9\u4e86\u4f60\u56db\u5341\u4e03\u62db\uff0c\u4f60\u8ba4\u4e3a\u591f\u4e86\u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u9053\uff1a\u201c\u591f\u4e86\u591f\u4e86\uff0c\u4f60\u8fd8\u624b\u5427\uff01\u201d \u3000\u3000\u6c34\u6bcd\u9053\uff1a\u201c\u4f60\u80fd\u6321\u5f97\u4f4f\u6211\u51e0\u62db\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u90a3\u5012\u8bf4\u4e0d\u5b9a\uff0c\u4e5f\u8bb8\u8fde\u4e00\u62db\u90fd\u6321\u4e0d\u4e86\uff0c\u4e5f\u8bb8\u53ef\u4ee5\u6321\u4e0a\u4e2a\u4e03\u516b\u767e\u62db\u3002\u201d \u3000\u3000\u6c34\u6bcd\u51b7\u7b11\u9053\uff1a\u201c\u4ee5\u4f60\u7684\u6b66\u529f\uff0c\u53ea\u8981\u80fd\u6321\u5f97\u4e86\u6211\u4e03\u516b\u62db\uff0c\u6211\u5c31\u8ba9\u4f60\u8d70\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u5f8c\u6094\uff1f\u201d \u3000\u3000\u6c34\u6bcd\u5389\u53f1\u9053\uff1a\u201c\u72c2\u5f92\uff0c\u5148\u63a5\u6211\u4e00\u62db\u518d\u8bf4\u3002\u201d \u3000\u3000\u5403\u58f0\u4e2d\uff0c\u5979\u5df2\u8fce\u9762\u4e00\u638c\u5411\u695a\u7559\u9999\u62cd\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u5979\u8fd9\u79cd\u638c\u529b\u6700\u5389\u5bb3\u4e4b\u5904\uff0c\u5c31\u662f\u4ee4\u5bf9\u65b9\u975e\u4f46\u4e0d\u80fd\u62db\u67b6\uff0c\u4e5f\u4e0d\u80fd\u9000\uff0c\u6b63\u50cf\u662f\u5df2\u6295\u8eab\u6d2a\u6d41\u4e4b\u4e2d \u7684\u4eba\uff0c\u53ea\u6709\u594b\u529b\u9006\u6d41\u800c\u4e0a\uff0c\u4e5f\u8bb8\u8fd8\u6709\u4e00\u7ebf\u751f\u673a\uff0c\u5149\u662f\u60f3\u9000\u4e0b\u53bb\u7f13\u53e3\u6c14\uff0c\u90a3\u9ebd\u5c31\u7acb\u523b\u8981\u88ab\u6d2a\u6c34 \u5377\u8d70\uff0c\u6b7b\u65e0\u846c\u8eab\u4e4b\u5730\u4e86\u3002 \u3000\u3000\u695a\u7559\u9999\u7cbe\u65bc\u6c34\u4f50\uff0c\u81ea\u7136\u5f88\u660e\u767d\u8fd9\u4e2a\u9053\u7406\u3002 \u3000\u3000\u53ef\u662f\u6c34\u6bcd\u8fd9\u4e00\u638c\u62bd\u51fa\uff0c\u4ed6\u5c45\u7136\u8fd8\u662f\u518d\u5411\u5f8c\u9000\u4e86\u3002 \u3000\u3000\u4ed6\u4f3c\u5df2\u5fc3\u7070\u610f\u51b7\uff0c\u653e\u5f03\u4e86\u62b5\u6297\u3002\u518d\u4e5f\u6ca1\u6709\u5728\u9006\u6d41\u4e2d\u594b\u6597\u6c42\u751f\u7684\u52c7\u6c14\uff0c\u5728\u8fd9\u79cd\u60c5\u51b5\u4e0b\uff0c\u53ea \u6709\u201c\u6b7b\u201d\u624d\u662f\u89e3\u8131\u3002 \u3000\u3000\u4ed6\u7684\u8eab\u5b50\u7acb\u523b\u88ab\u6c34\u6bcd\u7684\u638c\u529b\u9707\u5f97\u65ad\u7ebf\u7eb8\u9e22\u822c\u98de\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u6c34\u6bcd\u4e5f\u89c9\u5f97\u5f88\u610f\u5916\u3002 \u3000\u3000\u6b66\u529f\u5230\u4e86\u5979\u8fd9\u79cd\u706b\u5019\u7684\u4eba\uff0c\u6b63\u5982\u9ad8\u624b\u5955\u68cb\uff0c\u53ea\u8981\u5bf9\u65b9\u4e0b\u4e00\u7740\u68cb\uff0c\u5979\u5df2\u53ef\u5148\u7b97\u51fa\u5bf9\u529b\u5f8c\u9762 \u4e03\u516b\u7740\u7684\u68cb\u8def\u3002 \u3000\u3000\u695a\u7559\u9999\u4e00\u51fa\u624b\uff0c\u9634\u59ec\u5df2\u5bf9\u4ed6\u6b66\u529f\u7684\u6df1\u6d45\u4e86\u5982\u6307\u638c\u3002 \u3000\u3000\u5979\u7b97\u51c6\u695a\u7559\u9999\u6700\u5c11\u8fd8\u53ef\u62b5\u6321\u5979\u4e03\u62db\uff0c\u8c01\u77e5\u4e00\u62db\u51fa\u624b\uff0c\u695a\u7559\u9999\u5df2\u88ab\u971e\u98de\uff0c\u5979\u65e9\u5df2\u7b97\u51c6\u4e86\u7684 \u5f8c\u7740\uff0c\u7adf\u65e0\u6cd5\u4f7f\u51fa\u6765\u4e86\uff0c\u8fd9\u4e0d\u4f46\u4ee4\u5979\u89c9\u5f97\u5f88\u610f\u5916\uff0c\u751a\u81f3\u4ee4\u5979\u6709\u4e9b\u5931\u671b\uff0c\u5730\u60f3\u4e0d\u51fa\u81ea\u5df1\u7684\u5224\u65ad \u600e\u4f1a\u6709\u4e86\u9519\u8bef\uff1f \u3000\u3000\u53ef\u662f\u5979\u5fc3\u795e\u867d\u5206\uff0c\u638c\u529b\u5374\u672a\u7aed\uff0c\u82e5\u662f\u6362\u4e86\u522b\u4eba\uff0c\u5df2\u6295\u5165\u5979\u8fd9\u79cd\u638c\u529b\u4e4b\u4e2d\uff0c\u662f\u518d\u4e5f\u65e0\u6cd5\u8131 \u8eab\u7684\u4e86\u3002 \u3000\u3000\u53ea\u4e0d\u8fc7\u695a\u7559\u9999\u7684\u8f7b\u529f\u4e4b\u9ad8\uff0c\u4e5f\u662f\u5979\u672a\u66fe\u60f3\u5230\u7684\u3002 \u3000\u3000\u4f46\u542c\u201c\u5657\u901a\u201d\u4e00\u58f0\uff0c\u695a\u7559\u9999\u7adf\u5df2\u6323\u8131\u4e86\u5979\u7684\u638c\u529b\uff0c\u843d\u5165\u6c60\u6c34\u4e2d\uff0c\u8eab\u5f62\u5982\u6e38\u9c7c\u4e00\u7ffb\uff0c\u4fbf\u5df2 \u6d88\u5931\u4e0d\u89c1\u3002 \u3000\u3000\u9634\u59ec\u51b7\u7b11\u4e00\u58f0\uff0c\u4e00\u95ea\u8eab\uff0c\u4e5f\u8dc3\u5165\u6c34\u91cc\u3002 \u3000\u3000\u53ea\u89c1\u695a\u7559\u9999\u7684\u8eab\u6cd5\u5728\u6c34\u4e2d\u4f3c\u4e4e\u6bd4\u5728\u7a7a\u4e2d\u66f4\u5feb\uff0c\u4f46\u9634\u59ec\u53f7\u79f0\u201c\u6c34\u6bcd\u201d\uff0c\u6c34\u6027\u4e4b\u60c5\u5999\uff0c\u81ea\u7136 \u66f4\u975e\u4ed6\u4eba\u80fd\u53ca\u3002 \u3000\u3000\u4f55\u51b5\uff0c\u5728\u6c34\u4e2d\u6e38\u52a8\u65f6\uff0c\u5168\u8eab\u6bcf\u4e00\u5904\u90fd\u8981\u914d\u5408\u65e0\u95f4\uff0c\u4e24\u53ea\u811a\u7684\u6446\u52a8\u5c24\u5176\u91cd\u8981\uff0c\u5149\u662f\u7a7f\u7740\u978b \u5b50\uff0c\u5c31\u52bf\u5fc5\u8981\u5f71\u82e5\u662f\u5728\u9c7c\u5c3e\u4e0a\u52a0\u4e2a\u5957\u5b50\uff0c\u90a3\u9ebd\u5c31\u7b97\u9c7c\u4e5f\u6e38\u4e0d\u5feb\u7684\u3002 \u3000\u3000\u695a\u7559\u9999\u53ea\u89c9\u811a\u4e0a\u4e00\u53cc\u978b\u5b50\uff0c\u5f77\u4f5b\u6709\u5343\u94a7\u4e4b\u91cd\uff0c\u800c\u4e14\u8d8a\u6765\u8d8a\u91cd\uff0c\u4f46\u4ed6\u5e76\u6ca1\u6709\u60ca\u60f6\u5931\u63aa\uff0c\u56e0 \u4e3a\u4ed6\u65e9\u5c31\u77e5\u9053\u9003\u4e0d\u4e86\u7684\u3002 \u3000\u3000\u4ed6\u6839\u672c\u4e0d\u60f3\u8d70\uff0c\u60f3\u5728\u6c34\u4e2d\u4e0e\u9634\u59ec\u4e00\u6218\u3002 \u3000\u3000\u5728\u9646\u4e0a\uff0c\u4ed6\u7edd\u4e0d\u662f\u9634\u59ec\u7684\u5bf9\u624b\uff0c\u53ef\u662f\u5728\u6c34\u4e2d\uff0c\u9634\u59ec\u7684\u638c\u529b\u7eb5\u7136\u8fd8\u80fd\u53d1\u6325\uff0c\u4e5f\u52bf\u5fc5\u8981\u6253\u4e2a \u6298\u6263\u3002 \u3000\u3000\u4e16\u4e0a\u4e5f\u53ea\u6709\u201c\u6c34\u201d\u624d\u80fd\u6d88\u706d\u201c\u6c34\u201d\u7684\u529b\u91cf\u3002 \u3000\u3000\u5e73\u9759\u7684\u6e56\u9762\u4e0a\uff0c\u5ffd\u7136\u8d77\u4e86\u6c79\u6d8c\u7684\u6d6a\u6d9b\uff0c\u5c31\u5f77\u4f5b\u98ce\u548c\u65e5\u4e3d\u7684\u6d77\u5cb8\uff0c\u9aa4\u8d77\u66b4\u98ce\uff0c\u98ce\u5728\u547c\u5578\uff0c \u6d77\u4e5f\u5728\u547c\u5578\u3002 \u3000\u3000\u53c8\u5f77\u4f5b\u6e56\u5e95\u6765\u4e86\u4e24\u6761\u4e0a\u53e4\u6d2a\u8352\u65f6\u7684\u86df\u9f99\uff0c\u6b63\u5728\u6d77\u4e2d\u4f5c\u5750\u6b7b\u7684\u640f\u6597\u3002 \u3000\u3000\u795e\u6c34\u5bab\u7684\u5f1f\u5b50\u90fd\u5403\u60ca\u7684\u8dd1\u4e86\u51fa\u6765\uff0c\u8fd9\u4e00\u6f6d\u6f84\u6e05\u7684\u6e56\u6c34\uff0c\u672c\u662f\u5979\u4eec\u5fc3\u76ee\u4e2d\u7684\u201c\u795e\u6e56\u201d\uff0c\u5982 \u4eca\u600e\u4f1a\u53d8\u6210\u4e86\u201c\u9b54\u6e56\u201d\u3002 \u3000\u3000\u4e14\u89c1\u6e56\u6c34\u5ffd\u7136\u58c1\u4e0a\u800c\u8d77\uff0c\u5728\u521d\u5347\u7684\u9633\u5149\u4e2d\u770b\u6765\uff0c\u5c31\u5b9b\u5982\u4e00\u9053\u78a7\u7eff\u7684\u6c34\u6676\u5899\uff0c\u707f\u70c2\u4e3b\u5149\uff0c \u4e0d\u53ef\u65b9\u7269\u3002 \u3000\u3000\u5239\u90a3\u95f4\uff0c\u8fd9\u6c34\u6676\u5899\u5ffd\u53c8\u6d88\u5931\uff0c\u6c34\u9762\u4e0a\u63a5\u7740\u6cdb\u8d77\u4e86\u4e00\u8fde\u4e32\u7684\u6d9f\u6f2a\u548c\u6c34\u6ce1\uff0c\u53c8\u5b9b\u5982\u6709\u4e2a\u591a\u4e8b \u7684\u5996\u795e\uff0c\u5728\u6e56\u5e95\u5347\u8d77\u4e86\u4e00\u7089\u9b54\u706b\uff0c\u5c06\u6574\u4e2a\u6e56\u7684\u6c34\u90fd\u716e\u6cb8\uff0c\u7136\u5f8c\u518d\u5c06\u5929\u5730\u4e3b\u7075\u4e00\u9f50\u6295\u5165\uff0c\u4f9b\u4ed6 \u5480\u56bc\u3002 \u3000\u3000\u8fd9\u666f\u8c61\u58ee\u4e3d\u5947\u5e7b\uff0c\u5374\u53c8\u5e26\u7740\u79cd\u4e0d\u53ef\u5f62\u5bb9\u7684\u5996\u6c14\uff0c\u4ee4\u4eba\u89c1\u4e86\u4e0d\u4f46\u76ee\u7729\u7837\u593a\uff0c\u800c\u4e14\u6bdb\u9aa8\u609a \u7136\u3002 \u3000\u3000\u795e\u6c34\u5bab\u5f1f\u5b50\u5927\u90fd\u662f\u81ea\u5e7c\u5c31\u5165\u5bab\u6765\u7684\uff0c\u5728\u8fd9\u79cd\u73af\u5883\u4e2d\u751f\u957f\uff0c\u4f7f\u5979\u4eec\u6bcf\u4e2a\u4eba\u90fd\u89c9\u5f97\u81ea\u5df1\u9ad8\u9ad8 \u5728\u4e0a\u3001\u548c\u51e1\u4fd7\u4e2d\u7684\u4eba\u4e0d\u5411\uff0c\u4e5f\u4e0d\u8be5\u6709\u51e1\u4fd7\u4e2d\u4eba\u90a3\u4e9b\u51e1\u4fd7\u7684\u611f\u60c5\uff0c\u6240\u4ee5\u5979\u4eec\u4ece\u4e0d\u77e5\u9053\u201c\u7231\u201d\u662f \u4f55\u7269\uff1f \u3000\u3000\u4e5f\u4ece\u4e0d\u77e5\u9053\u201c\u6068\u201d\u662f\u4f55\u7269\uff1f\u201c\u6050\u60e7\u201d\u8fd9\u4e24\u4e2a\u5b57\uff0c\u5979\u4eec\u66f4\u89c9\u5f97\u662f\u5341\u5206\u53ef\u7b11\u7684\u3002 \u3000\u3000\u53ef\u662f\u73b0\u5728\uff0c\u5979\u4eec\u5fc3\u91cc\u5374\u8d77\u4e86\u4e00\u79cd\u83ab\u540d\u7684\u9707\u98a4\uff0c\u5f77\u4f5b\u89c9\u5f97\u5df2\u6709\u79cd\u4e0d\u53ef\u6297\u62d2\u7684\u707e\u7978\u5c06\u8981\u964d\u4e34 \u5230\u5979\u4eec\u8eab\u4e0a\u3002 \u3000\u3000\u6709\u4e9b\u4eba\u751a\u81f3\u89c9\u5f97\u5979\u4eec\u751f\u5b58\u7684\u5929\u5730\u5df2\u5c06\u6bc1\u706d\u3002 \u3000\u3000\u7206\u5357\u71d5\u4e5f\u5954\u4e86\u51fa\u6765\uff0c\u76ee\u4e2d\u72b9\u767d\u5e26\u7740\u6cea\u5149\uff0c\u4f46\u89c1\u5230\u6e56\u9762\u4e0a\u60ca\u4eba\u7684\u666f\u8c61\u5f8c\uff0c\u5979\u7684\u60b2\u54c0\u4e5f\u77ac\u5373 \u88ab\u60ca\u9a87\u6240\u66ff\u4ee3\u3002 \u3000\u3000\u5927\u5bb6\u89c1\u5230\u5979\uff0c\u5c31\u4e00\u9f50\u56f4\u4e86\u4e0a\u53bb\uff0c\u62a2\u7740\u95ee\u9053\uff1a\u201c\u8fd9\u662f\u600e\u9ebd\u56de\u4e8b\uff1f\u2026\u2026\u3002\u8fd9\u662f\u600e\u9ebd\u56de\u4e8b\uff1f\u201d \u3000\u3000\u7206\u5357\u71d5\u5fc3\u91cc\u867d\u4e5f\u548c\u5979\u4eec\u5411\u6837\u60ca\u9a87\uff0c\u4f46\u89c1\u5979\u4eec\u7684\u60ca\u9a87\u4e4b\u8272\uff0c\u5979\u53ea\u6709\u52c9\u5f3a\u4f5c\u51fa\u9547\u5b9a\u4e4b\u7b06\uff0c\u53cd \u800c\u5b89\u6170\u5979\u4eec\u9053\uff1a\u201c\u4e0d\u8981\u7d27\u7684\uff0c\u8fd9\u4e5f\u8bb8\u662f\u98ce\u2026\u2026\u201d \u3000\u3000\u201c\u4f46\u73b0\u5728\u5e76\u6ca1\u6709\u98ce\u5440\uff01\u201d \u3000\u3000\u6709\u4eba\u54c0\u6c42\u7740\u9053\uff1a\u201c\u56db\u59d0\uff0c\u4f60\u53bb\u77a7\u77a7\u5427\uff0c\u6700\u597d\u662f\u53bb\u95ee\u95ee\u5e08\u5085\u3002\u201d \u3000\u3000\u7206\u5357\u71d5\u8fdf\u7591\u7740\uff1a\u201c\u53c1\u59d0\u5462\uff1f\u201d \u3000\u3000\u6709\u4eba\u5e94\u9053\uff1a\u201c\u53c1\u59d0\u548c\u4e5d\u59b9\u90fd\u8fd8\u5728\u903c\u95ee\u90a3\u53c1\u4e2a\u4eba\u53e3\u4f9b\u3002\u201d \u3000\u3000\u7206\u5357\u71d5\u54ac\u7740\u5634\u5507\uff0c\u7ec8\u65bc\u4e0b\u4e86\u51b3\u5fc3\uff0c\u98de\u8eab\u4e00\u63a0\uff0c\u63a0\u5230\u6e56\u6c34\u7554\uff0c\u4f46\u5979\u8fd8\u6ca1\u6709\u8df3\u4e0b\u53bb\uff0c\u7a81\u6709\u4e00 \u9635\u6d6a\u6d9b\u5377\u6765\u3002 \u3000\u3000\u5979\u8fde\u7ad9\u90fd\u7ad9\u4e0d\u7a33\u4e86\uff0c\u88ab\u6d6a\u5934\u6253\u5f97\u8e09\u8dc4\u5f8c\u9000\u3002 \u3000\u3000\u5979\u5403\u60ca\u7684\u5446\u4e86\u534a\u664c\uff0c\u5ffd\u7136\u626d\u5934\u5954\u56de\u5979\u81ea\u5df1\u7684\u5c0f\u697c\uff0c\u552f\u6709\u5979\u7684\u5c45\u5904\uff0c\u662f\u53ef\u4ee5\u4ece\u5916\u9762\u76f4\u5165\u6c34 \u5bab\u5bdd\u5ba4\u7684\u3002 \u3000\u3000\u6c34\u5bab\u5bdd\u5ba4\u4e2d\u7684\u56db\u4e2a\u5c11\u5973\u5df2\u5413\u5f97\u5634\u5507\u53d1\u81ea\u3002 \u3000\u3000\u5728\u8fd9\u91cc\uff0c\u5979\u4eec\u867d\u770b\u4e0d\u5230\u6e56\u6c34\u7684\u5947\u5f02\u53d8\u5316\uff0c\u4f46\u6c34\u52bf\u649e\u6fc0\u7740\u5c71\u58c1\uff0c\u6574\u4e2a\u5bdd\u5ba4\u90fd\u5f77\u4f5b\u53d8\u6210\u4e86\u4e00 \u53ea\u88ab\u56f0\u5728\u60ca\u4fe6\u9a87\u6d6a\u4e2d\u7684\u5c0f\u821f\uff0c\u90fd\u4e00\u9635\u9635\u60ca\u5929\u52a8\u5730\u7684\u58f0\u54cd\uff0c\u66f4\u662f\u6151\u4eba\u9b42\u9b44\uff0c\u4ee4\u4eba\u89c9\u5f97\u5929\u5730\u90fd\u5df2 \u5c06\u5d29\u88c2\u3002 \u3000\u3000\u7206\u5357\u71d5\u5954\u4e86\u8fdb\u6765\uff0c\u5389\u58f0\u9053\uff1a\u201c\u5e08\u5085\u5462\uff1f\u201d \u3000\u3000\u5c11\u5973\u4eec\u6447\u4e86\u6447\u5934\uff0c\u98a4\u58f0\u9053\uff1a\u201c\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u7206\u5357\u71d5\u6012\u9053\uff1a\u201c\u4f60\u4eec\u4e00\u76f4\u5728\u8fd9\u91cc\u7684\uff0c\u600e\u4f1a\u4e0d\u77e5\u9053\uff1f\u201d \u3000\u3000\u5c11\u5973\u9053\uff1a\u201c\u5979\u8001\u4eba\u5bb6\u672c\u8981\u6211\u4eec\u5c06\u8fd9\u8863\u67dc\u62ac\u5230\u6e56\u4e2d\u53bb\uff0c\u5f8c\u6765\u5ffd\u53c8\u53eb\u6211\u4eec\u62ac\u56de\u6765\uff0c\u7136\u5f8c\u5c31\u53eb \u6211\u4eec\u51fa\u53bb\uff0c\u7b49\u6211\u4eec\u542c\u5230\u8fd9\u58f0\u6614\u65f6\u518d\u8fdb\u6765\uff0c\u5979\u8001\u4eba\u5bb6\u5df2\u4e0d\u89c1\u4e86\u3002\u201d \u3000\u3000\u7206\u5357\u71d5\u76b1\u7740\u7709\uff0c\u6c88\u601d\u4e86\u534a\u664c\uff0c\u53c8\u95ee\u9053\uff1a\u201c\u8fd9\u5730\u65b9\u53ef\u6709\u522b\u4eba\u8fdb\u6765\u9ebd\uff1f\u201d \u3000\u3000\u5c11\u5973\u9053\uff1a\u201c\u6ca1\u2026\u2026\u6ca1\u6709\u3002\u201d \u3000\u3000\u5176\u5b9e\u5979\u5c31\u662f\u88ab\u695a\u7559\u9999\u6240\u5236\u7684\u90a3\u4e8c\u4e00\u4e2a\u5c11\u5973\u5176\u4e2d\u4e4b\u4e00\uff0c\u5979\u7684\u7a74\u9053\u8fd8\u662f\u9634\u59ec\u81ea\u5df1\u66ff\u5979\u89e3\u5f00 \u7684\u3002 \u3000\u3000\u4f46\u5230\u4e86\u8fd9\u79cd\u65f6\u5019\uff0c\u5979\u600e\u6562\u518d\u591a\u5634\u3002 \u3000\u3000\u7206\u5357\u71d5\u8dfa\u4e86\u8dfa\u811a\uff0c\u7eb5\u8eab\u8dc3\u5165\u90a3\u5c0f\u6c60\u3002 \u3000\u3000\u6c34\u9053\u4e2d\u7684\u54cd\u58f0\u66f4\u60ca\u4eba\uff0c\u5c3a\u56e0\u4e24\u58c1\u5df2\u8d77\u4e86\u5171\u9e23\u3002 \u3000\u3000\u7206\u5357\u71d5\u8fd8\u672a\u6e38\u51fa\u6c34\u9053\uff0c\u5df2\u77a7\u89c1\u4e24\u4eba\u6b63\u5982\u4e24\u6761\u86df\u9f99\u6295\u5728\u6c34\u4e2d\u6fc0\u6597\uff0c\u4e24\u4eba\u7684\u8eab\u5f62\u4e4b\u5feb\uff0c\u90fd\u7edd \u975e\u8a00\u8bed\u6240\u80fd\u5f62\u5bb9\u3002 \u3000\u3000\u6e56\u9614\u6570\u5341\u4e08\uff0c\u4ed6\u4eec\u4e24\u4eba\u5374\u4f3c\u5df2\u5c06\u6574\u4e2a\u6e56\u5e95\u5168\u90fd\u5360\u636e\uff0c\u5bab\u5357\u71d5\u7b2c\u4e00\u773c\u89c1\u5230\u4ed6\u4eec\u65f6\uff0c\u4ed6\u4eec\u8fd8 \u5728\u6e56\u7684\u53f3\u8fb9\u3002 \u3000\u3000\u4f46\u4e00\u7728\u773c\u4e4b\u5f8c\uff0c\u4ed6\u4eec\u5df2\u5230\u4e86\u6e56\u7684\u5de6\u8fb9\u3002 \u3000\u3000\u5c31\u56e0\u4e3a\u4ed6\u4eec\u7684\u8eab\u5f62\u90fd\u592a\u5feb\uff0c\u6240\u4ee5\u8eab\u6cd5\u770b\u6765\u53cd\u5012\u6ca1\u6709\u4ec0\u9ebd\u7cbe\u5999\u7684\u53d8\u5316\uff0c\u6e56\u6c34\u7684\u6fc0\u8361\uff0c\u4e5f\u5e76 \u975e\u5168\u56e0\u4e3a\u4ed6\u4eec\u62db\u5f0f\u53d8\u5316\u95f4\u6240\u53d1\u51fa\u7684\u771f\u6c14\uff0c\u800c\u591a\u534a\u662f\u56e0\u4e3a\u4ed6\u4eec\u8eab\u5f62\u51b2\u7834\u6e56\u6c34\u65f6\u7684\u901f\u5ea6\uff0c\u901f\u5ea6\u8d8a \u5feb\uff0c\u529b\u91cf\u8d8a\u5927\u3002 \u3000\u3000\u4ed6\u4eec\u82e5\u5728\u9646\u4e0a\u640f\u6597\uff0c\u58f0\u5a01\u5c31\u4e0d\u4f1a\u5982\u6b64\u60ca\u4eba\uff0c\u56e0\u4e3a\u649e\u51fb\u4e86\u6c34\uff0c\u6c34\u53c8\u649e\u51fb\u7740\u6c34\uff0c\u4e00\u5206\u529b\u91cf\uff0c \u5c31\u53d8\u6210\u4e86\u5341\u5206\u3002 \u3000\u3000\u5c31\u56e0\u4e3a\u6c34\u5728\u4e0d\u505c\u7684\u52a8\uff0c\u6240\u4ee5\u624d\u4f1a\u5c06\u4ed6\u4eec\u7684\u8eab\u5f62\u63a8\u52a8\u5f97\u66f4\u5feb\uff0c\u5728\u8fd9\u79cd\u60c5\u51b5\u4e0b\u52a8\u624b\uff0c\u4e0d\u4f46\u8981 \u5229\u7528\u81ea\u5df1\u7684\u6bcf\u4e00\u5206\u529b\u91cf\uff0c\u4e5f\u8981\u5229\u7528\u6c34\u7684\u52a8\u529b\uff0c\u6709\u65f6\u4eba\u88ab\u6c34\u529b\u5e26\u52a8\uff0c\u62db\u5f0f\u5df2\u6839\u672c\u65e0\u6cd5\u7531\u81ea\u5df1\u63a7 \u5236\u4e86\u3002 \u7b2c\u4e09\u5341\u4e00\u7ae0\u3000\u6b7b\u4ea1\u4e4b\u543b>> \u53e4\u9f99\u300a\u695a\u7559\u9999\u7cfb\u5217\xb7\u753b\u7709\u9e1f\u300b \u7b2c\u4e09\u5341\u4e00\u7ae0\u3000\u6b7b\u4ea1\u4e4b\u543b \u3000\u3000\u8fd9\u4e0d\u4f46\u662f\u4e00\u573a\u7a7a\u524d\u7edd\u540e\u7684\u6076\u6218\uff0c\u4e5f\u662f\u4e00\u573a\u5999\u7edd\u4eba\u5bf0\u7684\u5927\u6218\uff0c\u5176\u4e2d\u53d8\u5316\u4e4b\u5947\u5999\uff0c\u9664\u4e86\u5f53\u5c40 \u8005\u53ea\u6015\u8c01\u4e5f\u65e0\u6cd5\u4f53\u4f1a\u3002 \u3000\u3000\u7206\u5357\u71d5\u5df2\u77a7\u5f97\u76ee\u5b9a\u53e3\u5446\uff0c\u6e56\u6c34\u5df2\u545b\u5165\u5979\u7684\u54bd\u5589\uff0c\u5979\u5374\u51e0\u4e4e\u5b8c\u5168\u6ca1\u6709\u89c9\u5bdf\uff0c\u5979\u5b9e\u5728\u60f3\u4e0d\u5230 \u4e16\u4e0a\u6709\u8c01\u80fd\u548c\u201c\u6c34\u6bcd\u201d\u9634\u59ec\u4ea4\u624b\u3002 \u3000\u3000\u5979\u66f4\u60f3\u4e0d\u5230\u8fd9\u4eba\u7adf\u4f3c\u5e76\u672a\u843d\u5728\u4e0b\u98ce\u3002 \u3000\u3000\u5728\u65cb\u52a8\u7684\u6c34\u6d41\u4e2d\uff0c\u5979\u6839\u672c\u8fa8\u4e0d\u51fa\u695a\u7559\u9999\u7684\u8eab\u5f62\u548c\u9762\u8c8c\uff0c\u4f46\u5728\u5979\u5fc3\u91cc\u5374\u5df2\u9690\u7ea6\u60f3\u8d77\u4e86\u695a\u7559 \u9999\u8fd9\u4e2a\u4eba\u3002\u3002 \u3000\u3000\u60f3\u8d77\u4e86\u4ed6\u90a3\u8ff7\u4eba\u7684\u5fae\u7b11\uff0c\u61d2\u6563\u7684\u795e\u6001\u3002\u201c\u695a\u7559\u9999\uff0c\u8fd9\u4e00\u5b9a\u662f\u695a\u7559\u9999\u3002\u201d \u3000\u3000\u9664\u4e86\u695a\u7559\u9999\u5916\uff0c\u4e16\u4e0a\u8fd8\u6709\u8c01\u80fd\u548c\u201c\u6c34\u6bcd\u201d\u4e00\u8f83\u8eab\u624b\uff1f \u3000\u3000\u5176\u5b9e\u695a\u7559\u9999\u6b64\u65f6\u5df2\u662f\u82e6\u4e0d\u582a\u8a00\uff0c\u82e5\u975e\u4ed6\u90a3\u79cd\u5e94\u53d8\u7684\u6025\u667a\uff0c\u4f7f\u4ed6\u80fd\u5145\u4efd\u5229\u7528\u4e86\u6c34\u7684\u52a8\u529b\uff0c \u4ed6\u53ea\u6015\u65e9\u5df2\u846c\u8eab\u5728\u6c34\u5e95\u3002 \u3000\u3000\u4ed6\u53ea\u89c9\u8eab\u4e0a\u8d1f\u62c5\u7684\u538b\u529b\u5df2\u8d8a\u6765\u8d8a\u5927\uff0c\u5168\u8eab\u7684\u8840\u7ba1\u90fd\u4f3c\u5df2\u5c06\u7206\u88c2\uff0c\u9f3b\u5b50\u91cc\u4e5f\u5df2\u5c06\u545b\u51fa\u9762 \u6765\u3002 \u3000\u3000\u5982\u4eca\u4ed6\u624d\u77e5\u9053\uff0c\u5728\u6c34\u4e2d\u52a8\u624b\uff0c\u4ed6\u4e5f\u662f\u5411\u6837\u7684\u5168\u65e0\u751f\u8def\u3002 \u3000\u3000\u6c34\u6bcd\u7684\u638c\u529b\u672c\u5c31\u662f\u5728\u6c34\u4e2d\u7ec3\u6210\u7684\uff0c\u522b\u4eba\u7684\u638c\u529b\u5728\u6c34\u4e2d\u53d1\u6325\u4e0d\u51fa\uff0c\u4f46\u5979\u7684\u638c\u529b\u5374\u53ea\u4e0d\u8fc7\u6253 \u4e86\u4e2a\u6298\u6263\u800c\u5df2\u3002 \u3000\u3000\u695a\u7559\u9999\u53ea\u89c9\u5f97\u56db\u9762\u7684\u6c34\u4f3c\u5df2\u8d8a\u6765\u8d8a\u6d53\u5bc6\uff0c\u6d53\u5f97\u5c31\u50cf\u9762\u4e00\u6837\uff0c\u4ed6\u7684\u8eab\u5f62\u5df2\u6e10\u6e10\u88ab\u6ede\u4f4f\uff0c\u6e10 \u6e10\u4e0d\u80fd\u79fb\u52a8\u3002 \u3000\u3000\u4ed6\u81ea\u77e5\u5df2\u5230\u4e86\u6b7b\u4ea1\u7684\u8fb9\u7f18\u3002 \u3000\u3000\u8c01\u77e5\u201c\u6c34\u6bcd\u201d\u9634\u59ec\u7684\u8eab\u6cd5\u7adf\u5df2\u6162\u4e86\u4e0b\u53bb\uff0c\u4e3e\u624b\u6295\u8db3\u95f4\uff0c\u4e5f\u6e10\u6e10\u6709\u4e86\u79cd\u529b\u4e0d\u4ece\u5fc3\u7684\u73b0\u8c61\u3002 \u3000\u3000\u695a\u7559\u9999\u53c8\u60ca\u53c8\u559c\uff0c\u4ed6\u672c\u731c\u4e0d\u900f\u6c34\u6bcd\u90a3\u9ebd\u5145\u6c9b\u7684\u5185\u529b\u600e\u4f1a\u6d88\u8017\u5f97\u5982\u6b64\u5feb\uff0c\u4f46\u7acb\u523b\u5c31\u604d\u7136\u5927 \u9634\u59ec\u5e76\u975e\u5df2\u529b\u7aed\uff0c\u800c\u662f\u5df2\u6c14\u7aed\u4e86\u3002 \u3000\u3000\u695a\u7559\u9999\u5df2\u7ec3\u6210\u4e86\u4e00\u79cd\u795e\u79d8\u7684\u547c\u5438\u65b9\u6cd5\uff0c\u4ed6\u5728\u6c34\u4e2d\u547c\u5438\u51e0\u4e4e\u548c\u9646\u5730\u4e0a\u5411\u6837\u81ea\u7531\uff0c\u4f46\u522b\u4eba\u5374 \u4e0d\u540c\u3002 \u3000\u3000\u800c\u4e14\u4e00\u4e2a\u4eba\u5728\u6fc0\u70c8\u7684\u640f\u6597\u65f6\uff0c\u66f4\u9700\u8981\u5145\u4efd\u7684\u201c\u6c14\u201d\uff0c\u8fd9\u4e5f\u662f\u80dc\u8d1f\u6210\u8d25\u7684\u91cd\u8981\u5173\u952e\u4e4b\u4e00\u3002 \u3000\u3000\u9634\u59ec\u4f53\u5185\u7684\u201c\u6c14\u201d\u5728\u6025\u907d\u7684\u6d88\u8017\u7740\uff0c\u6b64\u523b\u5df2\u5feb\u6d88\u8017\u5149\u4e86\uff0c\u5979\u8eab\u4f53\u4e2d\u5df2\u8d77\u4e86\u4e00\u79cd\u4e0d\u53ef\u6297\u62d2 \u7684\u75b2\u5026\u4e4b\u611f\uff0c\u4f3c\u5df2\u6655\u6655\u6b32\u7761\u3002 \u3000\u3000\u695a\u7559\u9999\u77e5\u9053\u53ea\u8981\u8ba9\u4ed6\u51fa\u6c34\u53bb\u6362\u4e00\u6b21\u6c14\uff0c\u81ea\u5df1\u5c31\u5fc5\u8d25\u65e0\u7591\uff0c\u56e0\u4e3a\u201c\u6c14\u201d\u53ef\u4ee5\u6362\uff0c\u201c\u529b\u201d\u5374 \u65e0\u6cd5\u6362\u3002 \u3000\u3000\u4ed6\u65e0\u8bba\u5982\u4f55\u4e5f\u4e0d\u80fd\u8ba9\u5979\u6362\u6c14\u3002 \u3000\u3000\u53ea\u89c1\u9634\u59ec\u8eab\u5b50\u5ffd\u7136\u4e00\u7ffb\uff0c\u4e0a\u8eab\u5f8c\u4ef0\uff0c\u811a\u80cc\u633a\u76f4\uff0c\u5728\u4e00\u5239\u90a3\u95f4\u4fbf\u5df2\u8e22\u51fa\u4e86\u4e5d\u811a\uff0c\u8fd9\u4e5d\u811a\u867d \u7136\u8e22\u4e0d\u5230\u695a\u7559\u9999\uff0c\u4f46\u5374\u8e22\u51fa\u4e86\u4e00\u8fde\u4e32\u6c34\u6ce1\uff0c\u6bcf\u4e2a\u6c34\u6ce1\u4e2d\u90fd\u5e26\u7740\u4e00\u80a1\u5f3a\u52b2\u7684\u771f\u6c14\uff0c\u94c1\u5f39\u822c\u51fb\u5411 \u695a\u7559\u9999\u3002 \u3000\u3000\u695a\u7559\u9999\u8981\u95ea\u907f\u672c\u4e0d\u56f0\u96be\uff0c\u4f46\u4ed6\u53ea\u8981\u5f80\u5f8c\u4e00\u9001\uff0c\u9634\u59ec\u7684\u8eab\u5b50\u5c31\u4f1a\u501f\u7740\u8fd9\u8e22\u6c34\u7684\u529b\u91cf\u51b2\u51fa\u6c34 \u9762\u3002 \u3000\u3000\u6c34\u6ce1\u4e00\u8fde\u4e32\u51fb\u51fa\uff0c\u5979\u7684\u4eba\u5df2\u5982\u706b\u7bad\u822c\u5411\u4e0a\u5347\u8d77\u3002 \u3000\u3000\u773c\u89c1\u695a\u7559\u9999\u5df2\u65e0\u6cd5\u5c06\u5979\u62e6\u963b\uff0c\u4ed6\u60c5\u6025\u4e4b\u4e0b\uff0c\u7adf\u4e0d\u987e\u4e00\u5207\u7684\u6251\u4e86\u4e0a\u53bb\uff0c\u7d27\u7d27\u62b1\u4f4f\u4e86\u5979\u7684\u53cc \u817f\u3002 \u3000\u3000\u9634\u59ec\u518d\u4e5f\u60f3\u4e0d\u5230\u695a\u7559\u9999\u4f1a\u4f7f\u51fa\u5982\u6b64\u5192\u9669\uff0c\u5982\u6b64\u65e0\u8d56\u7684\u62db\u5f0f\uff0c\u6025\u5207\u95f4\u4e5f\u4e0d\u77e5\u8be5\u5982\u4f55\u89e3\u6551\uff0c \u8eab\u5b50\u5df2\u88ab\u695a\u7559\u9999\u62d6\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u5979\u53c8\u60ca\u53c8\u6012\uff0c\u4e00\u638c\u62cd\u5411\u695a\u7559\u9999\u7684\u5934\u9876\u3002 \u3000\u3000\u695a\u7559\u9999\u53cc\u624b\u62b1\u4f4f\u4e86\u5b83\u7684\u817f\uff0c\u65e2\u4e0d\u80fd\u62db\u67b6\uff0c\u4e5f\u4e0d\u6562\u653e\u5f00\uff0c\u56e0\u4e3a\u53ea\u8981\u4ed6\u7684\u624b\u4e00\u677e\uff0c\u9634\u59ec\u7684\u817f \u5c31\u4f1a\u8e22\u4e2d\u4ed6\u8981\u5bb3\u3002 \u3000\u3000\u4ed6\u53ea\u6709\u7528\u5934\u5728\u9634\u59ec\u7684\u809a\u5b50\u4e0a\u4e00\u9876\uff0c\u9634\u59ec\u7684\u8eab\u5b50\u88ab\u9876\u5f97\u5411\u5f8c\u4e00\u5012\uff0c\u8fd9\u4e00\u638c\u4e5f\u5c31\u62cd\u4e0d\u4e0b\u53bb \u4e86\u3002 \u3000\u3000\u8fd9\u79cd\u62db\u5f0f\u7528\u5f97\u66f4\u8352\u5510\uff0c\u9634\u59ec\u53ea\u89c9\u5168\u8eab\u90fd\u5df2\u6c14\u5f97\u53d1\u9ebb\u3002 \u3000\u3000\u9664\u4e86\u96c4\u5a18\u5b50\u5916\uff0c\u5979\u5e73\u751f\u51e0\u66fe\u88ab\u7537\u4eba\u5982\u6b64\u6402\u62b1\u8fc7\uff0c\u4e5f\u4e0d\u77e5\u662f\u5426\u56e0\u4e3a\u6c14\u5df2\u5c06\u7aed\uff0c\u5979\u5168\u8eab\u7adf\u8f6f \u7ef5\u7ef5\u7684\u4fbf\u4e0d\u51fa\u534a\u5206\u6c14\u529b\u6765\u3002 \u3000\u3000\u695a\u7559\u9999\u81ea\u5df1\u53c8\u4f55\u5c1d\u4e0d\u89c9\u5f97\u8fd9\u79cd\u62db\u5f0f\u7528\u5f97\u672a\u514d\u6709\u4e9b\u89c1\u4e0d\u5f97\u4eba\uff0c\u4f46\u4e00\u4e2a\u4eba\u5728\u6323\u624e\u6c42\u751f\u65f6\uff0c\u90a3 \u91cc\u8fd8\u987e\u5f97\u4e86\u90a3\u9ebd\u591a\u3002 \u3000\u3000\u4ed6\u4e58\u7740\u9634\u59ec\u8eab\u5b50\u5411\u5f8c\u4e00\u4ef0\u7684\u65f6\u5019\uff0c\u5df2\u7a9c\u4e0a\u53bb\u5c06\u5979\u7684\u53cc\u624b\u8fde\u4eba\u4e00\u9f50\u7d27\u7d27\u62b1\u4f4f\uff0c\u53c8\u7528\u4e24\u6761\u817f \u76d8\u4f4f\u4e86\u5979\u7684\u817f\u3002 \u3000\u3000\u4ed6\u5c31\u50cf\u4e2a\u516b\u722a\u9c7c\u4f3c\u7684\uff0c\u5c06\u9634\u59ec\u7f20\u5f97\u8fde\u52a8\u90fd\u52a8\u4e0d\u4e86\u3002 \u3000\u3000\u53ea\u89c1\u9634\u59ec\u773c\u775b\u5df2\u6e10\u6e10\u7ffb\u767d\uff0c\u5634\u89d2\u5df2\u5728\u5f80\u5916\u5192\u6c14\u6ce1\uff0c\u7528\u4e0d\u4e86\u591a\u4e45\uff0c\u5979\u5c31\u96be\u514d\u8981\u7a92\u606f\u800c\u6b7b\u3002 \u3000\u3000\u695a\u7559\u9999\u773c\u89c1\u53c8\u5c06\u6218\u80dc\u4e86\uff0c\u8fd9\u4e00\u6b21\u80dc\u5229\u867d\u7136\u5e76\u4e0d\u5341\u5206\u5149\u5f69\uff0c\u4f46\u80dc\u5229\u6bd5\u7adf\u662f\u80dc\u5229\uff0c\u65e0\u8bba\u90a3\u79cd \u80dc\u5229\uff0c\u81f3\u5c11\u90fd\u6bd4\u5931\u8d25\u597d\u5f97\u591a\u3002 \u3000\u3000\u8c01\u77e5\u5c31\u5728\u8fd9\u65f6\uff0c\u695a\u7559\u9999\u5ffd\u7136\u89c9\u5f97\u4e00\u80a1\u5f3a\u52b2\u7684\u529b\u91cf\u81ea\u8eab\u5b50\u4e0b\u51b2\u4e0a\u6765\uff0c\u5c06\u4ed6\u4eec\u4e24\u4e2a\u4eba\u90fd\u51b2\u5f97 \u5411\u4e0a\u5347\u8d77\u6765\u3002 \u3000\u3000\u539f\u6765\u4ed6\u4eec\u4e0d\u77e5\u4e0d\u89c9\u95f4\uff0c\u5df2\u5230\u4e86\u6e56\u5fc3\u90a3\u77f3\u677f\u4e0a\u7ad9\u7acb\u7684\u6c34\u9053\u53e3\uff0c\u5bab\u5357\u71d5\u4e00\u63a5\u67a2\u7ebd\uff0c\u6e56\u5fc3\u7684\u55b7 \u6cc9\u5c31\u53c8\u7bad\u4e00\u822c\u5411\u4e0a\u3001\u8d77\u3002 \u3000\u3000\u5239\u90a3\u4e4b\u95f4\uff0c\u695a\u7559\u9999\u548c\u9634\u59ec\u90fd\u5df2\u88ab\u51b2\u4e0a\u4e86\u6c34\u9762\u3002 \u3000\u3000\u695a\u7559\u9999\u77e5\u9053\u6e05\u53ea\u8981\u8ba9\u9634\u59ec\u5598\u4e00\u53e3\u6c14\uff0c\u4ed6\u5c31\u518d\u4e5f\u62b1\u4e0d\u4f4f\u5979\u4e86\uff0c\u4f46\u8fd9\u65f6\u4ed6\u7684\u624b\u53ef\u4e07\u4e07\u4e0d\u80fd\u653e \u5f00\u3002 \u3000\u3000\u53ea\u89c1\u773c\u524d\u4e00\u4eae\uff0c\u4ed6\u4eec\u5df2\u51b2\u51fa\u4e86\u6e56\u6c34\u3002 \u3000\u3000\u695a\u7559\u9999\u518d\u4e5f\u987e\u4e0d\u5f97\u522b\u7684\uff0c\u5ffd\u7136\u5c06\u5934\u51d1\u4e86\u4e0a\u53bb\uff0c\u7528\u5634\u7d27\u7d27\u76d6\u4f4f\u4e86\u9634\u59ec\u7684\u5634\uff0c\u7528\u9f3b\u5b50\u7d27\u7d27\u538b \u4f4f\u4e86\u9634\u59ec\u7684\u9f3b\u5b50\u3002 \u3000\u3000\u4ed6\u65e0\u8bba\u5982\u4f55\uff0c\u4e5f\u4e0d\u80fd\u8ba9\u9634\u59ec\u547c\u5438\u3002 \u3000\u3000\u795e\u6c34\u5bab\u7684\u5f1f\u5b50\u672c\u662f\u5206\u6563\u5728\u5404\u5904\u7684\uff0c\u6709\u7684\u5728\u6811\u4e0b\uff0c\u6709\u7684\u5728\u6e56\u7554\uff0c\u4f46\u73b0\u5728\u5979\u4eec\u5df2\u6e10\u6e10\u805a\u5728\u4e00 \u8d77\u3002 \u3000\u3000\u8fd9\u4e9b\u5b64\u72ec\u7684\u5c11\u5973\u4eec\uff0c\u53ea\u6709\u5728\u60ca\u60e7\u7684\u65f6\u5019\uff0c\u624d\u4f1a\u89c9\u5f97\u9700\u8981\u522b\u4eba\uff0c\u6050\u60e7\u539f\u6765\u5c31\u6bd4\u5feb\u4e50\u66f4\u80fd\u4ee4 \u4eba\u5408\u7fa4\u3002 \u3000\u3000\u8fd9\u6015\u4e5f\u5c31\u662f\u4eba\u7c7b\u5927\u591a\u90fd\u89c9\u5f97\u4e0d\u5feb\u4e50\u7684\u539f\u56e0\u3002 \u3000\u3000\u5979\u4eec\u53d1\u73b0\u6e56\u6c34\u5df2\u6e10\u6e10\u5e73\u9759\u4e0b\u6765\u7684\u65f6\u5019\uff0c\u5c31\u53c8\u5728\u4e0d\u77e5\u4e0d\u89c9\u95f4\u6e10\u6e10\u6563\u5f00\u4e86\uff0c\u6709\u7684\u4eba\u5df2\u5728\u6697\u4e2d \u5e86\u5e78\uff0c\u5371\u9669\u5df2\u8fc7\u53bb\u3002 \u3000\u3000\u8c01\u77e5\u5c31\u5728\u8fd9\u65f6\uff0c\u6e56\u5fc3\u7684\u6c34\u67f1\u5ffd\u53c8\u51b2\u5929\u800c\u8d77\u3002 \u3000\u3000\u8fd9\u55b7\u6cc9\u6c34\u67f1\u672c\u662f\u201c\u6c34\u6bcd\u201d\u9634\u59ec\u73b0\u8eab\u65f6\u624d\u4f1a\u51fa\u73b0\u7684\uff0c\u5979\u4eec\u518d\u4e5f\u60f3\u4e0d\u5230\u8fd9\u6b21\u6c34\u67f1\u4e0a\u7adf\u6709\u4e24\u4e2a \u4eba\u3002 \u3000\u3000\u9664\u4e86\u6c34\u6bcd\u5916\uff0c\u7adf\u8fd8\u6709\u4e2a\u7537\u4eba\u3002 \u3000\u3000\u8fd9\u7537\u4eba\u7adf\u548c\u6c34\u6bcd\u7d27\u7d27\u62e5\u62b1\u5728\u4e00\u8d77\uff0c\u871c\u871c\u7684\u63a5\u7740\u543b\u3002 \u3000\u3000\u795e\u6c34\u5bab\u7684\u5f1f\u5b50\u5168\u90fd\u60ca\u8bb6\u5f97\u5446\u4f4f\u4e86\uff0c\u5c31\u7b97\u5929\u5d29\u5730\u88c2\uff0c\u5c71\u6cb3\u53d8\u8272\uff0c\u4e5f\u7edd\u4e0d\u80fd\u4ee4\u5979\u4eec\u5982\u6b64\u5403 \u60ca\u3002 \u3000\u3000\u5bf9\u7537\u4eba\u6df1\u75db\u6076\u7edd\uff0c\u4e00\u5411\u795e\u5723\u4e0d\u53ef\u4fb5\u72af\u7684\u201c\u6c34\u6bcd\u201d\uff0c\u600e\u4f1a\u548c\u7537\u4eba\u5982\u6b64\u4eb2\u871c\uff1f\u8fd9\u7537\u4eba\u662f\u8c01 \u5462\uff1f \u3000\u3000\u5979\u4eec\u7684\u773c\u775b\u90fd\u5df2\u53d1\u76f4\u3002 \u3000\u3000\u543b\uff0c\u672c\u662f\u751c\u871c\u7684\u3002 \u3000\u3000\u4f46\u5728\u51e0\u5341\u53cc\u773c\u775b\u4e4b\u4e0b\u63a5\u543b\uff0c\u5c31\u4e0d\u662f\u4ef6\u4ee4\u4eba\u6109\u5feb\u7684\u4e8b\u4e86\uff0c\u4f55\u51b5\u8fd9\u4e00\u543b\u4e2d\u6839\u672c\u5c31\u6ca1\u6709\u4e1d\u6beb\u751c \u871c\u4e4b\u610f\u3002 \u3000\u3000\u8fd9\u4e00\u543b\u662f\u6b7b\u4ea1\u4e4b\u543b\u3002 \u3000\u3000\u53e6\u6709\u4e00\u79cd\u6b8b\u9177\u7684\u7f8e\u3002 \u3000\u3000\u6b8b\u9177\u7684\u9b45\u529b\u3002 \u3000\u3000\u82e5\u975e\u8eab\u5386\u5176\u5883\u7684\u4eba\uff0c\u8c01\u4e5f\u9886\u7565\u4e0d\u51fa\u8fd9\u5176\u4e2d\u7684\u75db\u82e6\u6ecb\u5473\uff0c\u4f46\u4ebf\u4e07\u4eba\u4e2d\uff0c\u53c8\u6709\u51e0\u4eba\u80fd\u8eab\u5386\u5176 \u5883\uff1f \u3000\u3000\u695a\u7559\u9999\u672c\u662f\u4e3a\u4e86\u6323\u624e\u6c42\u751f\u4e86\u8fd9\u9ebd\u505a\u7684\uff0c\u4f46\u6b64\u523b\uff0c\u4e5f\u4e0d\u77e5\u600e\u5730\uff0c\u4ed6\u5fc3\u91cc\u7adf\u8d77\u4e86\u4e00\u79cd\u65e0\u6cd5\u63cf \u53d9\u7684\u5f02\u6837\u611f\u89c9\u3002 \u3000\u3000\u6c34\u52bf\u5728\u4ed6\u8eab\u5b50\u4e0b\u51b2\u6fc0\u7740\uff0c\u5c31\u50cf\u662f\u706b\u7130\u3002 \u3000\u3000\u9634\u59ec\u7684\u8eab\u5b50\u5df2\u6e10\u6e10\u8f6f\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u5979\u7684\u8138\u672c\u5df2\u6da8\u5f97\u901a\u7ea2\uff0c\u6b64\u523b\u53c8\u6e10\u6e10\u82cd\u767d\u3002 \u3000\u3000\u695a\u7559\u9999\u4e0d\u6562\u95ed\u8d77\u773c\u775b\uff0c\u5979\u8138\u4e0a\u6bcf\u4e00\u6839\u808c\u8089\u7684\u98a4\u52a8\uff0c\u695a\u7559\u9999\u90fd\u53ef\u4ee5\u770b\u5f97\u6e05\u6e05\u695a\u695a\uff1b\u5979\u7684\u5fc3 \u6bcf\u4e00\u6b21\u8df3\u52a8\uff0c\u695a\u7559\u9999\u4e5f\u90fd\u53ef\u542c\u5f97\u6e05\u6e05\u695a\u695a\u3002 \u3000\u3000\u695a\u7559\u9999\u672c\u89c9\u5f97\u5979\u662f\u4e2a\u575a\u5f3a\u3001\u51b3\u65ad\uff0c\u80fd\u81ea\u5236\u7684\u5973\u4eba\u3002 \u3000\u3000\u4f46\u73b0\u5728\uff0c\u4ed6\u548c\u5979\u8ddd\u79bb\u5f97\u8fd9\u9ebd\u8fd1\uff0c\u4ed6\u5ffd\u7136\u89c9\u5f97\u5979\u5df2\u53d8\u5f97\u5341\u5206\u8f6f\u5f31\u800c\u53ef\u601c\uff0c\u548c\u522b\u7684\u5973\u4eba\u5e76\u6ca1 \u6709\u4ec0\u9ebd\u4e0d\u5411\u3002 \u3000\u3000\u65e0\u8bba\u591a\u4f1f\u5927\u7684\u5973\u4eba\uff0c\u5728\u7537\u4eba\u6000\u62b1\u4e2d\u90fd\u4f1a\u53d8\u5f97\u6e3a\u5c0f\u7684\u3002 \u3000\u3000\u8fd9\u662f\u5343\u53e4\u4e0d\u6613\u7684\u9053\u7406\uff0c\u4e5f\u662f\u4ef6\u5f88\u6709\u8da3\u7684\u4e8b\uff0c\u82e5\u975e\u5982\u6b64\uff0c\u8fd9\u4e16\u754c\u4e5f\u8bb8\u5c31\u4e0d\u4f1a\u7531\u7537\u4eba\u6765\u7edf\u6cbb \u4e86\u3002 \u3000\u3000\u695a\u7559\u9999\u5b9e\u5728\u4e0d\u5fcd\u8ba9\u5979\u6b7b\u5728\u81ea\u5df1\u7684\u6000\u62b1\u91cc\u3002 \u3000\u3000\u4f46\u4ed6\u53ea\u8981\u4e00\u653e\u624b\uff0c\u81ea\u5df1\u5c31\u5f97\u6b7b\u3002 \u3000\u3000\u9634\u59ec\u8e59\u4f4f\u7684\u4e00\u53e3\u6c14\u82e5\u662f\u7a81\u7136\u53d1\u6563\uff0c\u90a3\u529b\u91cf\u7684\u5f3a\u5927\uff0c\u5c31\u7edd\u4e0d\u662f\u695a\u7559\u9999\u6240\u80fd\u62b5\u5fa1\u7684\uff0c\u4ed6\u6015\u7acb \u523b\u5c31\u8981\u88ab\u9707\u5f97\u56db\u5206\u4e94\u88c2\u3002 \u3000\u3000\u4ed6\u4eec\u7684\u751f\u4e0e\u6b7b\u4e4b\u95f4\uff0c\u51e0\u4e4e\u5df2\u6ca1\u6709\u8ddd\u79bb\u3002 \u3000\u3000\u9634\u59ec\u4e5f\u5728\u77aa\u7740\u695a\u7559\u9999\u3002 \u3000\u3000\u5979\u76ee\u4e2d\u672c\u6765\u5145\u6ee1\u4e86\u6124\u6012\u548c\u6028\u6bd2\u4e4b\u610f\uff0c\u4f46\u6b7b\u4ea1\u7684\u611f\u89c9\u5df2\u6e10\u6e10\u5c06\u5979\u5f81\u670d\uff0c\u5979\u8fde\u201c\u6068\u201d\u90fd\u65e0\u529b \u518d\u6068\u4e86\u3002 \u3000\u3000\u5979\u773c\u775b\u91cc\u5df2\u4e0d\u7531\u81ea\u4e3b\u6d41\u9732\u51fa\u4e00\u79cd\u60b2\u54c0\u4e5e\u601c\u4e4b\u610f\u695a\u7559\u9999\u5ffd\u7136\u53d1\u73b0\u4e00\u6ef4\u6676\u83b9\u7684\u6cea\u73e0\uff0c\u81ea\u5979\u773c \u775b\u91cc\u6d41\u4e86\u51fa\u6765\u3002 \u3000\u3000\u6cea\u73e0\u6d6e\u6e38\u5728\u5979\u82cd\u767d\u7684\u9762\u9765\u4e0a\u6eda\u52a8\u7740\u3002 \u3000\u3000\u6b7b\u4ea1\uff0c\u662f\u516c\u5e73\u7684\uff0c\u5728\u6b7b\u4ea1\u9762\u524d\uff0c\u6700\u4f1f\u5927\u7684\u4eba\u4e5f\u53d8\u5f97\u5f88\u5e73\u51e1\u3002 \u3000\u3000\u695a\u7559\u9999\u7684\u624b\u6e10\u6e10\u677e\u4e86\u3002 \u3000\u3000\u4ed6\u6b64\u523b\u672c\u6765\u5df2\u53ef\u4ee5\u91cd\u624b\u53bb\u6740\u6b7b\u5979\uff0c\u6216\u8005\u81f3\u5c11\u5148\u70b9\u4f4f\u5979\u7684\u7a74\u9053\uff0c\u56e0\u4e3a\u9634\u59ec\u5df2\u5b8c\u5168\u5931\u53bb\u4e86\u62b5 \u6297\u7684\u529b\u91cf\u3002 \u3000\u3000\u4f46\u4ed6\u5e76\u6ca1\u6709\u8fd9\u6837\u505a\uff0c\u4ed6\u5b9e\u5728\u65e0\u6cd5\u4f24\u5bb3\u4e00\u4e2a\u6b63\u5728\u6d41\u6cea\u7684\u5973\u5b50\uff0c\u4ed6\u8fd9\u4e00\u4e3b\u4e2d\uff0c\u4ece\u6765\u6ca1\u6709\u505a\u8fc7 \u8fd9\u4e8b\u3002 \u3000\u3000\u695a\u7559\u9999\u5e76\u4e0d\u662f\u4e00\u4e2a\u50cf\u4f20\u8bf4\u4e2d\u90a3\u9ebd\u51b7\u6f20\u65e0\u60c5\u7684\u4eba\uff0c\u4e5f\u5e76\u4e0d\u50cf\u4f20\u8bf4\u4e2d\u90a3\u9ebd\u806a\u660e\uff0c\u6709\u65f6\u751a\u81f3\u4f1a \u505a\u51fa\u4e00\u4e9b\u611a\u8822\u7684\u4e8b\u3002 \u3000\u3000\u4f46\u5c31\u5728\u8fd9\u65f6\uff0c\u6258\u4f4f\u4ed6\u4eec\u7684\u6c34\u67f1\u5ffd\u7136\u6d88\u5931\u4e86\u3002\u695a\u7559\u9999\u548c\u9634\u59ec\u7acb\u523b\u5e73\u7a7a\u843d\u4e86\u4e0b\u53bb\uff0c\u201c\u5657\u201d\u4e00 \u58f0\u6c34\u4e2d\u3002 \u3000\u3000\u4ed6\u4f3c\u5df2\u5b8c\u5168\u5fd8\u8bb0\u4e86\u81ea\u5df1\u7f6e\u8eab\u4f55\u5904\uff0c\u5b8c\u5168\u6ca1\u6709\u9632\u5907\uff0c\u7adf\u51e0\u4e4e\u88ab\u9707\u5f97\u6655\u4e86\u8fc7\u53bb\uff0c\u6000\u4e2d\u7684\u9634\u59ec \u4e5f\u88ab\u9707\u98de\u3002 \u3000\u3000\u5979\u53ea\u89c9\u4e00\u53ea\u624b\u81ea\u6c34\u4e0b\u4f38\u51fa\uff0c\u70b9\u4f4f\u4e86\u4ed6\u7684\u5929\u9053\u3002\u5728\u8fd9\u4e00\u5239\u90a3\u95f4\uff0c\u4ed6\u5ffd\u7136\u60f3\u8d77\u4e86\u4e00\u53e5\u8bdd\uff0c\u8fd9 \u53e5\u8bdd\u4ed6\u5df2\u5fd8\u8bb0\u662f\u8c01\u8bf4\u7684\uff0c\u4f46\u6bcf\u4e2a\u5b57\u4ed6\u90fd\u8bb0\u5f97\u6e05\u695a\u3002 \u3000\u3000\u201c\u5973\u4eba\u7684\u773c\u6cea\uff0c\u6c38\u8fdc\u662f\u5bf9\u4ed8\u7537\u4eba\u7684\u6700\u6709\u6548\u7684\u6b66\u5668\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u5f20\u5f00\u773c\u775b\u65f6\uff0c\u5bab\u5357\u71d5\u6b63\u671b\u7740\u4ed6\u51b7\u7b11\u3002 \u3000\u3000\u4ed6\u53c8\u5df2\u56de\u5230\u6c34\u6bcd\u7684\u5bdd\u5bab\uff0c\u9634\u59ec\u4e5f\u76d8\u819d\u5750\u5728\u4ed6\u5bf9\u9762\uff0c\u5979\u8138\u4e0a\u7edd\u6ca1\u6709\u4efb\u4f55\u8868\u60c5\uff0c\u4f3c\u5df2\u6062\u590d\u4e86 \u5979\u7684\u51b7\u9177\u4e0e\u575a\u5f3a\u3002 \u3000\u3000\u7206\u5357\u71d5\u51b7\u51b7\u9053\uff1a\u201c\u6211\u65e9\u5c31\u8bf4\u8fc7\uff0c\u4ece\u6ca1\u6709\u4eba\u80fd\u5728\u795e\u6c34\u5bab\u5360\u5f97\u4e86\u4fbf\u5b9c\u7684\uff0c\u5c31\u8fde\u6218\u65e0\u4e0d\u80dc\u7684\u695a \u7559\u9999\u4e5f\u4e0d\u80fd\u4f8b\u5916\u3002\u201d \u3000\u3000\u5979\u77aa\u7740\u695a\u7559\u9999\uff0c\u4e00\u5b57\u5b57\u63a5\u7740\u9053\uff1a\u201c\u73b0\u5728\uff0c\u4f60\u5df2\u627f\u8ba4\u81ea\u5df1\u6218\u8d25\u4e86\u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u770b\u6765\u6211\u5df2\u975e\u627f\u8ba4\u4e0d\u53ef\u3002\u201d \u3000\u3000\u7206\u5357\u71d5\u9053\uff1a\u201c\u4f60\u8fd8\u6709\u4ec0\u9ebd\u8bdd\u8bf4\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u82e6\u7b11\u9053\uff1a\u201c\u6211\u5df2\u6ca1\u6709\u4ec0\u9ebd\u8bdd\u597d\u8bf4\u4e86\u3002\u201d \u3000\u3000\u7206\u5357\u71d5\u50b2\u7136\u4e00\u7b11\uff0c\u8f6c\u5934\u671b\u7740\u9634\u59ec\uff0c\u9053\uff1a\u201c\u4f60\u8bf4\uff0c\u6211\u4eec\u5e94\u8be5\u5982\u4f55\u5904\u7f6e\u4ed6\uff1f\u201d \u3000\u3000\u9634\u59ec\u9ed8\u7136\u534a\u664c\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u8fd9\u4eba\u88ab\u4f60\u6240\u4fd8\uff0c\u5e94\u8be5\u7531\u4f60\u4f5c\u4e3b\u3002\u201d \u3000\u3000\u7206\u5357\u71d5\u76ee\u4e2d\u9732\u51fa\u4e00\u4e1d\u6076\u6bd2\u7684\u7b11\u610f\uff0c\u9053\uff1a\u201c\u4e5f\u597d\uff0c\u5c31\u5c06\u4ed6\u4ea4\u7ed9\u6211\u5427\uff01\u201d \u3000\u3000\u5979\u521a\u8d70\u5230\u695a\u7559\u9999\u9762\u524d\uff0c\u9634\u59ec\u5ffd\u7136\u53c8\u9053\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u4e5f\u60f3\u50cf\u5bf9\u4ed8\u96c4\u5a18\u5b50\u90a3\u6837\u5bf9\u4ed8\u4ed6\uff1f\u201d \u3000\u3000\u7206\u5357\u71d5\u6014\u4e86\u6014\uff0c\u8138\u8272\u6e10\u6e10\u53d8\u4e86\uff0c\u957f\u957f\u5410\u51fa\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8fd9\u662f\u4ed6\u544a\u8bc9\u4f60\u7684\uff1f\u201d \u3000\u3000\u9634\u59ec\u9053\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u6ca1\u6709\u60f3\u5230\u4ed6\u4f1a\u770b\u5230\u4f60\u7684\u79d8\u5bc6\uff1f\u201d \u3000\u3000\u7206\u5357\u71d5\u6ca1\u6709\u56de\u7b54\uff0c\u695a\u7559\u9999\u53ef\u4ee5\u5f88\u6e05\u695a\u7684\u770b\u5230\u5979\u624b\u6307\u6e10\u6e10\u53d1\u6296\uff0c\u53c8\u6e10\u6e10\u634f\u7d27\uff0c\u6307\u8282\u90fd\u5df2\u634f \u5f97\u53d1\u767d\u3002 \u3000\u3000\u997c\u4e86\u534a\u664c\uff0c\u5979\u5ffd\u7136\u5389\u58f0\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u662f\u6211\u6740\u4e86\u90a3\u4e2a\u4eba\uff0c\u6211\u82e5\u6740\u9519\u4e86\uff0c\u66ff\u4ed6\u507f\u547d\u4e5f\u65e0\u59a8\uff0c \u4f46\u5077\u770b\u522b\u4eba\u79d8\u5bc6\u7684\u4eba\uff0c\u4e5f\u5f97\u6b7b\u3002\u201d \u3000\u3000\u5979\u624b\u6307\u7a81\u53c8\u4f38\u76f4\uff0c\u5200\u4e00\u822c\u5411\u695a\u7559\u9999\u5288\u4e0b\u3002 \u3000\u3000\u4f46\u8fd9\u53ea\u624b\u8fd8\u672a\u89e6\u53ca\u695a\u7559\u9999\u7684\u54bd\u5589\uff0c\u5979\u7684\u4eba\u5df2\u98de\u4e86\u51fa\u53bb\uff0c\u9634\u59ec\u4e0d\u77e5\u4f55\u65f6\u5df2\u8dc3\u8d77\uff0c\u9762\u4e0a\u4ecd\u7136 \u6728\u65e0\u8868\u60c5\u3002 \u3000\u3000\u7206\u5357\u71d5\u201c\u7830\u201d\u7684\u649e\u4e0a\u77f3\u58c1\uff0c\u518d\u6ed1\u5230\u5730\u4e0a\uff0c\u5403\u60ca\u7684\u77aa\u7740\u9634\u59ec\u3002\u76ee\u4e2d\u5145\u6ee1\u4e86\u60ca\u7591\u4e0d\u4fe1\u4e4b\u8272\uff0c \u98a4\u58f0\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\uff1f\u201d \u3000\u3000\u9634\u59ec\u9053\uff1a\u201c\u6211\u2026\u2026\u201d \u3000\u3000\u7206\u5357\u71d5\u76ee\u4e2d\u5ffd\u7136\u6d41\u4e0b\u6cea\u6765\uff0c\u9053\uff1a\u201c\u4f60\u600e\u9ebd\u2026\u2026\u2026\u600e\u9ebd\u5fcd\u5fc3\u5bf9\u6211\u4e0b\u624b\uff1f\u201d \u3000\u3000\u9634\u59ec\u9053\uff1a\u201c\u4f60\u600e\u9ebd\u5fcd\u5fc3\u5bf9\u4ed6\u4e0b\u624b\uff1f\u201d \u3000\u3000\u7206\u5357\u71d5\u55c4\u58f0\u9053\uff1a\u201c\u4ed6\uff1f\u8c01\uff1f\u662f\u695a\u7559\u9999\uff0c\u8fd8\u662f\u96c4\u5a18\u5b50\uff1f\u201d \u3000\u3000\u9634\u59ec\u6c88\u9ed8\u7740\uff0c\u695a\u7559\u9999\u53d1\u73b0\u5979\u7684\u624b\u4e5f\u5df2\u5f00\u59cb\u53d1\u6296\u3002 \u3000\u3000\u7206\u5357\u71d5\u543c\u9053\uff1a\u201c\u539f\u6765\u4f60\u8fd8\u662f\u7231\u4ed6\uff1f\u539f\u6765\u6211\u53ea\u4e0d\u8fc7\u662f\u4ed6\u7684\u4ee3\u7528\u54c1\uff0c\u4f60\u7adf\u4e0d\u60dc\u6740\u4e86\u6211\u66ff\u4ed6\u62a5 \u4ec7\uff0c\u4f46\u4f60\u53ef\u77e5\u9053\u6211\u6740\u4ed6\u662f\u4e3a\u4e86\u4f60\u9ebd\uff1f\u201d \u3000\u3000\u9634\u59ec\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6211\u77e5\u9053\u3002\u201d \u3000\u3000\u7206\u5357\u71d5\u9053\uff1a\u201c\u90a3\u9ebd\u4f60\u4e3a\u4ec0\u9ebd\u8fd8\u8981\u2026\u2026\u8fd8\u8981\u2026\u2026\u201d \u3000\u3000\u9634\u59ec\u9053\uff1a\u201c\u4f60\u4e0d\u6740\u4ed6\uff0c\u6211\u4e5f\u8bb8\u4f1a\u6740\u4ed6\uff0c\u4f46\u4f60\u6740\u4e86\u4ed6\uff0c\u6211\u5c31\u8981\u4e3a\u4ed6\u62a5\u4ec7\uff0c\u65e0\u8bba\u8c01\u6740\u4e86\u4ed6\uff0c \u6211\u90fd\u8981\u4e3a\u4ed6\u62a5\u4ec7\u3002\u201d \u3000\u3000\u7206\u5357\u71d5\u6c88\u9ed8\u4e86\u534a\u664c\uff0c\u9eef\u7136\u9053\uff1a\u201c\u4f60\u7684\u610f\u601d\uff0c\u6211\u5df2\u7ecf\u61c2\u4e86\u3002\u201d \u3000\u3000\u8fd9\u610f\u601d\u5176\u5b9e\u5e76\u4e0d\u96be\u61c2\uff0c\u6b63\u5982\u4e00\u4e2a\u5b69\u5b50\u505a\u4e86\u574f\u4e8b\uff0c\u7236\u6bcd\u56fa\u7136\u8981\u6253\u4ed6\u7f5a\u4ed6\uff0c\u4f46\u522b\u4eba\u82e5\u6253\u4e86 \u4ed6\uff0c\u505a\u7236\u6bcd\u7684\u975e\u4f46\u5fc3\u75db\uff0c\u8bf4\u4e0d\u5b9a\u8fd8\u4f1a\u53bb\u627e\u90a3\u4eba\u62da\u547d\uff0c\u8fd9\u5c31\u662f\u201c\u7231\u201d\uff0c\u6c38\u8fdc\u4ee4\u4eba\u4e0d\u53ef\u6349\u6478\uff0c\u4f46 \u8c01\u90fd\u4e0d\u80fd\u5426\u8ba4\u5b83\u7684\u5b58\u5728\u3002 \u3000\u3000\u9634\u59ec\u53f9\u606f\u7740\u9053\uff1a\u201c\u4f60\u61c2\u4e86\u6700\u597d\uff0c\u6211\u4e5f\u5e0c\u671b\u4f60\u80fd\u61c2\u3002\u201d \u3000\u3000\u7206\u5357\u71d5\u9053\uff1a\u201c\u4f46\u4f60\u83ab\u5fd8\u4e86\uff0c\u82e5\u4e0d\u662f\u6211\uff0c\u4f60\u2026\u2026\u201d \u3000\u3000\u9634\u59ec\u9053\uff1a\u201c\u6211\u4e5f\u77e5\u9053\u4f60\u6551\u4e86\u6211\uff0c\u4f46\u90a3\u662f\u53e6\u5916\u4e00\u56de\u4e8b\uff0c\u6211\u4f1a\u597d\u597d\u5b89\u846c\u4f60\u7684\u3002\u201d \u3000\u3000\u7206\u5357\u71d5\u53c8\u6c88\u9ed8\u4e86\u5f88\u4e45\uff0c\u51c4\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u73b0\u5728\u624d\u771f\u7684\u660e\u767d\u4f60\u662f\u4e3a\u4e86\u4ec0\u9ebd\u6740\u6211\u7684\u3002\u201d \u3000\u3000\u9634\u59ec\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u7206\u5357\u71d5\u9053\uff1a\u201c\u4f60\u6740\u6211\uff0c\u53ea\u56e0\u6211\u6551\u4e86\u4f60\u3002\u201d \u3000\u3000\u9634\u59ec\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u7206\u5357\u71d5\u9053\uff1a\u201c\u6211\u6b7b\u4e86\u4e4b\u5f8c\uff0c\u5c31\u6c38\u8fdc\u6ca1\u6709\u4eba\u77e5\u9053\u4f60\u66fe\u7ecf\u8d25\u5728\u695a\u7559\u9999\u624b\u4e0a\uff0c\u4e5f\u4e0d\u4f1a\u6709\u4eba\u77e5\u9053 \u6211\u66fe\u7ecf\u6551\u8fc7\u4f60\uff0c\u4f60\u4ece\u6765\u4e0d\u80fd\u5fcd\u53d7\u5931\u8d25\u7684\u803b\u8fb1\uff0c\u6240\u4ee5\u975e\u6740\u6211\u4e0d\u53ef\u3002\u201d \u3000\u3000\u9634\u59ec\u957f\u957f\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u4e00\u5411\u90fd\u5f88\u806a\u660e\uff0c\u4e5f\u8bb8\u592a\u806a\u660e\u4e86\u3002\u201d \u3000\u3000\u7206\u5357\u71d5\u6014\u4e86\u6014\uff0c\u5583\u5583\u9053\uff1a\u201c\u6211\u7a76\u7adf\u662f\u806a\u660e\u8fd8\u662f\u7b28\uff0c\u8fde\u6211\u81ea\u5df1\u90fd\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u5979\u95ed\u4e0a\u773c\u775b\uff0c\u4e0d\u518d\u8bf4\u8bdd\uff0c\u4e5f\u8bf4\u4e0d\u51fa\u4e86\u3002 \u3000\u3000\u6c88\u9ed8\uff0c\u6c88\u9ed8\u5f97\u4ee4\u4eba\u7a92\u606f\u3002 \u3000\u3000\u8fde\u695a\u7559\u9999\u90fd\u4e0d\u613f\u6253\u7834\u8fd9\u6c88\u9ed8\uff0c\u4ed6\u4e5f\u8bb8\u662f\u4e0d\u6562\u3002 \u3000\u3000\u997c\u4e86\u5f88\u4e45\uff0c\u9634\u59ec\u5ffd\u7136\u8f6c\u8eab\u77aa\u7740\u695a\u7559\u9999\uff0c\u9053\uff1a\u201c\u4f60\u8ba4\u4e3a\u6211\u771f\u662f\u4e3a\u4e86\u5979\u6551\u4e86\u6211\u624d\u695a\u7559\u9999\u6c88\u541f \u7740\uff0c\u9053\uff1a\u201c\u6211\u60f3\u4f60\u5e76\u4e0d\u662f\u8fd9\u79cd\u4eba\u3002\u201d \u3000\u3000\u9634\u59ec\u9053\uff1a\u201c\u5979\u4e86\u89e3\u6211\u96be\u9053\u8fd8\u6ca1\u6709\u4f60\u6e05\u695a\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u90a3\u53ea\u56e0\u5979\u81ea\u5df1\u662f\u8fd9\u79cd\u4eba\uff0c\u6240\u4ee5\u624d\u4f1a\u5c06\u4f60\u770b\u5f97\u548c\u5979\u4e00\u6837\u3002\u201d \u3000\u3000\u9634\u59ec\u76ee\u5149\u7a7a\u865a\u7684\u51dd\u6ce8\u7740\u8fdc\u65b9\uff0c\u5583\u5583\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5c31\u56e0\u4e3a\u4f60\u5e76\u4e0d\u662f\u8fd9\u79cd\u4eba\uff0c\u624d\u4f1a\u8bf4\u6211\u4e5f\u4e0d \u662f\uff0c\u4f60\u82e5\u975e\u8fd9\u79cd\u4eba\uff0c\u5979\u4e5f\u8bb8\u5c31\u6839\u672c\u6ca1\u6709\u673a\u4f1a\u6551\u6211\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u82e5\u662f\u5982\u6b64\u6bd2\u8fa3\u7684\u4eba\uff0c\u5979\u6015\u65e9\u5df2\u6b7b\u5728\u695a\u7559\u9999\u624b\u4e0a\uff0c\u53ef\u662f\u695a\u7559\u9999\u5374\u672a\u60f3\u5230\u5979\u81ea\u5df1\u5c45\u7136 \u4e5f\u77e5\u9053\u3002 \u3000\u3000\u4ed6\u81ea\u7136\u4e5f\u5e0c\u671b\u9634\u59ec\u4e0d\u662f\u8fd9\u79cd\u4eba\uff0c\u56e0\u4e3a\u9634\u59ec\u82e5\u771f\u548c\u5bab\u5357\u71d5\u8bf4\u7684\u4e00\u6837\uff0c\u5c31\u4e00\u5b9a\u4e5f\u8981\u6740\u6b7b\u4ed6\u706d \u53e3\u3002 \u3000\u3000\u4f46\u9634\u59ec\u7a76\u7adf\u662f\u4e0d\u662f\u8fd9\u79cd\u4eba\u5462\uff1f\u695a\u7559\u9999\u5e76\u4e0d\u77e5\u9053\uff0c\u4ed6\u53ea\u77e5\u9053\u81ea\u5df1\u7684\u6027\u547d\u5df2\u634f\u5728\u9634\u59ec\u624b\u4e0a\u3002 \u3000\u3000\u4ed6\u5df2\u5c1d\u5230\u4e86\u81ea\u5df1\u51b7\u6c57\u7684\u54b8\u5473\u3002 \u3000\u3000\u997c\u4e86\u534a\u664c\uff0c\u9634\u59ec\u5ffd\u53c8\u95ee\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\u4f60\u8fd9\u6b21\u4e3a\u4f55\u4f1a\u5931\u8d25\u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u82e6\u7b11\u9053\uff1a\u201c\u6211\u77e5\u4e0d\u77e5\u9053\u53c8\u6709\u4f55\u5206\u522b\uff1f\u201d \u3000\u3000\u9634\u59ec\u9053\uff1a\u201c\u4f60\u5e94\u8be5\u77e5\u9053\u7684\uff0c\u4f60\u8fd9\u6b21\u5931\u8d25\uff0c\u53ea\u56e0\u4e3a\u4f60\u7684\u5fc3\u592a\u8f6f\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4f60\u5462\uff1f\u4f60\u7684\u5fc3\u96be\u9053\u4ece\u6765\u4e0d\u8f6f\uff1f\u201d \u3000\u3000\u9634\u59ec\u6c88\u9ed8\u4e86\u5f88\u4e45\uff0c\u5ffd\u7136\u51b7\u51b7\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u7684\u5fc3\uff1f\u4f60\u4ee5\u4e3a\u6211\u8fd8\u6709\u5fc3\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u4e00\u9897\u5fc3\u5df2\u6c88\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u4ed6\u4ee5\u4e3a\u81ea\u5df1\u8fd9\u6b21\u5df2\u771f\u7684\u6ca1\u6709\u5e0c\u671b\u4e86\u3002 \u3000\u3000\u8c01\u77e5\u9634\u59ec\u5374\u5df2\u9eef\u7136\u63a5\u7740\u9053\uff1a\u201c\u5c31\u56e0\u4e3a\u6211\u5df2\u4ec0\u9ebd\u90fd\u6ca1\u6709\u4e86\uff0c\u6240\u4ee5\uff0c\u4f60\u7684\u751f\u6b7b\u5bf9\u6211\u4e5f\u5df2\u65e0\u5173 \u7d27\u8981\uff0c\u6211\u751a\u81f3\u5df2\u61d2\u5f97\u6740\u4f60\u3002\u201d \u3000\u3000\u5979\u5ffd\u7136\u53cd\u624b\u4e00\u638c\uff0c\u62cd\u5f00\u4e86\u695a\u7559\u9999\u7684\u7a74\u9053\u3002 \u3000\u3000\u695a\u7559\u9999\u6014\u4e86\u534a\u664c\uff0c\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u96be\u9053\u5df2\u7ecf\u60f3\u2026\u2026\u201d \u3000\u3000\u9634\u59ec\u5ffd\u53c8\u5389\u58f0\u9053\uff1a\u201c\u6211\u600e\u9ebd\u60f3\uff0c\u4e5f\u4e0e\u4f60\u65e0\u5173\uff0c\u4f60\u5feb\u8d70\uff0c\u83ab\u7b49\u6211\u6539\u53d8\u4e86\u4e3b\u610f\u3002\u201d \u3000\u3000\u5979\u5524\u5165\u4e86\u4e00\u4e2a\u60ca\u614c\u7684\u5f1f\u5b50\uff0c\u9053\uff1a\u201c\u5e26\u8fd9\u4eba\u53bb\u627e\u4f60\u53c1\u5e08\u59d0\uff0c\u695a\u7559\u9999\u6574\u4e86\u6574\u8863\u886b\uff0c\u8eac\u8eab\u9053\uff1a \u201c\u591a\u8c22\u5bab\u4e3b\u3002\u201d \u3000\u3000\u8fd9\u65f6\u9634\u59ec\u5374\u5df2\u5982\u8001\u618e\u5165\u5b9a\uff0c\u5f77\u4f5b\u6c38\u8fdc\u518d\u4e5f\u4e0d\u613f\u9192\u6765\u3002 \u3000\u3000\u77f3\u95e8\u6e10\u6e10\u9616\u8d77\uff0c\u6e10\u6e10\u6321\u4f4f\u4e86\u695a\u7559\u9999\u7684\u89c6\u7ebf\uff0c\u5c06\u201c\u6c34\u6bcd\u201d\u9634\u59ec\u9694\u7edd\u5728\u95e8\u91cc\uff0c\u975e\u4f46\u9694\u7edd\u4e86\u6574 \u4e2a\u4e16\u754c\uff0c\u4e5f\u9694\u7edd\u4e86\u5979\u7684\u4e3b\u547d\u3002 \u3000\u3000\u8fd9\u95e8\uff0c\u5374\u662f\u5979\u81ea\u5df1\u9020\u6210\u7684\u3002 \u3000\u3000\u695a\u7559\u9999\u53f9\u4e86\u53e3\u6c14\uff0c\u77e5\u9053\u4eca\u5f8c\u6015\u4efb\u4f55\u4eba\u518d\u4e5f\u89c1\u4e0d\u7740\u5979\u4e86\uff0c\u4ed6\u82e5\u4ece\u6765\u4e5f\u6ca1\u6709\u89c1\u8fc7\u9634\u59ec\uff0c\u4ed6\u7edd \u4e0d\u4f1a\u89c9\u5f97\u6709\u4e1d\u6beb\u9057\u61be\u3002 \u3000\u3000\u4f46\u73b0\u5728\uff0c\u4e5f\u4e0d\u77e5\u600e\u5730\uff0c\u4ed6\u5fc3\u91cc\u7adf\u89c9\u5f97\u6709\u4e9b\u4f24\u611f\u3002 \u3000\u3000\u90a3\u795e\u6c34\u5bab\u7684\u5f1f\u5b50\u5b88\u5019\u5728\u4e00\u65c1\uff0c\u770b\u6765\u53c8\u662f\u60ca\u8bb6\uff0c\u53c8\u662f\u597d\u5947\uff0c\u5979\u663e\u7136\u8fd8\u672a\u5f04\u6e05\u8fd9\u82f1\u4fca\u7684\u7537\u4eba \u548c\u5979\u5e08\u5085\u95ee\u7684\u5173\u7cfb\u3002 \u3000\u3000\u695a\u7559\u9999\u53f9\u606f\u7740\u8f6c\u8fc7\u8eab\uff0c\u9053\uff1a\u201c\u6211\u4eec\u8d70\u5427\uff01\u201d \u3000\u3000\u4ed6\u518d\u4e5f\u60f3\u4e0d\u5230\u81ea\u5df1\u8fd9\u53e5\u8bdd\u8fd8\u672a\u8bf4\u5b8c\uff0c\u5c31\u5df2\u77a7\u89c1\u4e86\u80e1\u94c1\u82b1\u3002\u80e1\u94c1\u82b1\u7adf\u5df2\u548c\u9ec4\u9c81\u76f4\u3001\u6234\u72ec\u884c \u4e00\u9f50\u5306\u5306\u8d76\u6765\u3002 \u3000\u3000\u4ed6\u4eec\u89c1\u5230\u695a\u7559\u9999\uff0c\u663e\u7136\u4e5f\u5403\u4e86\u4e00\u60ca\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u5931\u58f0\u9053\uff1a\u201c\u8001\u81ed\u866b\uff0c\u4f60\u600e\u9ebd\u8dd1\u51fa\u6765\u7684\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u4e5f\u5931\u58f0\u9053\uff1a\u201c\u4f60\u4eec\u600e\u9ebd\u8dd1\u51fa\u6765\u7684\uff1f\u201d \u3000\u3000\u4e24\u4eba\u51e0\u4e4e\u5728\u5411\u65f6\u95f4\u51fa\u4e86\u5411\u6837\u4e00\u53e5\u8bdd\uff0c\u90fd\u5fcd\u4e0d\u4f4f\u7b11\u4e86\uff0c\u65e0\u8bba\u5982\u4f55\uff0c\u4ed6\u4eec\u80fd\u518d\u76f8\u89c1\uff0c\u603b\u662f\u6b22 \u559c\u591a\u65bc\u60ca\u5f02\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u7b11\u9053\uff1a\u201c\u8fd8\u662f\u4f60\u5148\u8bf4\u5427\uff0c\u4f60\u9047\u7740\u7684\u4e8b\u4e00\u5b9a\u6bd4\u6211\u4eec\u7cbe\u91c7\uff0c\u6211\u4eec\u7684\u6545\u4e8b\u5b9e\u5728\u6709\u70b9\u6cc4 \u6c14\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u9053\uff1a\u201c\u8fd8\u662f\u4f60\u5148\u8bf4\u5427\uff0c\u6211\u8fd9\u6545\u4e8b\u8bf4\u6765\u8bdd\u957f\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u77a7\u4e86\u6234\u72ec\u884c\u548c\u9ec4\u6625\u76f4\u4e00\u773c\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u8bf4\u6765\u771f\u4e22\u4eba\uff0c\u6211\u4eec\u53c1\u4e2a\u4eba\u7adf\u5168\u4e0d\u662f\u6c34\u6bcd\u7684 \u5bf9\u624b\uff0c\u82e5\u4e0d\u662f\u84c9\u513f\u7684\u59d1\u5988\uff0c\u6211\u4eec\u6015\u5df2\u89c1\u4e0d\u7740\u4f60\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u52a8\u5bb9\u9053\uff1a\u201c\u5979\u653e\u4e86\u4f60\u4eec\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u53f9\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5979\u548c\u4e00\u4e2a\u53eb\u201d\u4e5d\u59b9\u201d\u7684\u4e00\u9f50\u6765\u76d8\u95ee\u6211\u4eec\uff0c\u6211\u4eec\u81ea\u7136\u4ec0\u9ebd\u4e5f\u4e0d\u80af \u8bf4\uff0c\u4f46\u90a3\u53eb\u201c\u4e5d\u59b9\u201d\u7684\u5c0f\u4e2b\u5934\u5012\u771f\u5155\u5f97\u5f88\uff0c\u5c45\u7136\u8981\u7528\u82e6\u5211\u6765\u7cdf\u8e4b\u6211\u4eec\uff0c\u5e78\u597d\u84c9\u513f\u7684\u59d1\u5988\u8bf4\u6211 \u4eec\u90fd\u662f\u6709\u8eab\u4efd\u7684\u4eba\uff0c\u5e94\u8be5\u4ee5\u793c\u76f8\u5f85\uff0c\u8c01\u77e5\u90a3\u5c0f\u4e2b\u5934\u7ffb\u4e86\u7751\uff0c\u786c\u8bf4\u84c9\u513f\u7684\u59d1\u5988\u4e00\u5b9a\u65e9\u5df2\u548c\u6211\u4eec \u4e32\u901a\u4e86\u3002\u201d \u3000\u3000\u4ed6\u6068\u6068\u7684\u63a5\u7740\u9053\uff1a\u201c\u90a3\u5c0f\u4e2b\u5934\u4eba\u5155\u5634\u4e5f\u5155\uff0c\u8fd8\u8bf4\u4e86\u8bb8\u591a\u5f88\u96be\u542c\u7684\u8bdd\uff0c\u84c9\u513f\u7684\u59d1\u5988\u5fcd\u65e0\u53ef \u5fcd\uff0c\u5ffd\u7136\u95f4\u51fa\u624b\u70b9\u4e86\u5979\u7684\u7a74\u9053\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u8038\u7136\u9053\uff1a\u201c\u5979\u2026\u2026\u5979\u600e\u80fd\u5982\u6b64\u5192\u9669\uff1f\u201d \u7b2c\u4e09\u5341\u4e8c\u7ae0\u3000\u51fa\u5bab\u7ef4\u8270>> \u53e4\u9f99\u300a\u695a\u7559\u9999\u7cfb\u5217\xb7\u753b\u7709\u9e1f\u300b \u7b2c\u4e09\u5341\u4e8c\u7ae0\u3000\u51fa\u5bab\u7ef4\u8270 \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u5979\u8fd9\u9ebd\u6837\u505a\uff0c\u8fde\u6211\u4eec\u90fd\u5403\u4e86\u4e00\u60ca\uff0c\u56e0\u4e3a\u201d\u795e\u6c34\u5bab\u201d\u95e8\u89c4\u4e4b\u4e25\uff0c\u662f\u6c5f\u6e56\u4e2d\u4eba \u4eba\u90fd\u77e5\u9053\u7684\uff0c\u5979\u8fd9\u9ebd\u505a\u65e0\u5f02\u5df2\u627f\u8ba4\u548c\u6211\u4eec\u4e32\u901a\uff0c\u6b3a\u5e08\u901a\u654c\uff0c\u90a3\u7f6a\u540d\u53ef\u4e0d\u8f7b\uff0c\u4f46\u5979\u51fa\u624b\u5f8c\u53cd\u800c \u9547\u5b9a\u4e0b\u6765\uff0c\u53ea\u662f\u53eb\u6211\u4eec\u5feb\u6765\u627e\u4f60\uff0c\u5979\u8bf4\u4f60\u4e5f\u8bb8\u4e5f\u843d\u5165\u4e86\u6c34\u6bcd\u7684\u638c\u63e1\u4e2d\uff0c\u4e5f\u8bb8\u2026\u2026\u4e5f\u8bb8\u5df2\u906d\u4e86 \u6bd2\u624b\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u7740\u6025\u9053\uff1a\u201c\u5979\u81ea\u5df1\u5462\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u5979\u2026\u2026\u5979\u4f3c\u5df2\u4e0b\u4e86\u51b3\u5fc3\uff0c\u5df2\u5c06\u751f\u547d\u7f6e\u4e4b\u5ea6\u5916\uff0c\u53ea\u4e0d\u8fc7\u544a\u8bc9\u6211\u4eec\uff0c\u90a3\u83e9\u63d0\u5eb5 \u4e2d\u7684\u804b\u54d1\u5c3c\u672c\u662f\u5979\u7684\u5927\u5e08\u59d0\uff0c\u56e0\u4e3a\u72af\u4e86\u95e8\u89c4\uff0c\u624d\u843d\u5230\u5982\u6b64\u5730\u6b65\u7684\uff0c\u5979\u5e0c\u671b\u6211\u4eec\u6709\u673a\u4f1a\u65f6\u597d\u597d \u7167\u987e\u5979\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u8dfa\u811a\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u5979\u663e\u7136\u4e5f\u6015\u548c\u5979\u7684\u5927\u5e08\u59d0\u843d\u5165\u5411\u6837\u60b2\u60e8\u7684\u906d\u9047\uff0c\u6240\u4ee5\u51c6\u5907 \u4e00\u6b7b\u4e86\u4e4b\u2026\u2026\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9eef\u7136\u9053\uff1a\u201c\u770b\u6765\u6015\u662f\u5982\u6b64\uff0c\u6211\u4eec\u8d70\u7684\u65f6\u5019\uff0c\u5979\u5c31\u5c06\u90a3\u77f3\u7262\u81ea\u91cc\u9762\u5c01\u95ed\uff0c\u5c06\u5979\u81ea\u5df1 \u5173\u5728\u90a3\u77f3\u7262\u91cc\uff0c\u6211\u4eec\u53d1\u73b0\u4e0d\u5999\uff0c\u518d\u6c42\u5979\u5f00\u53e3\u65f6\uff0c\u5c31\u5df2\u65e0\u8bba\u600e\u9ebd\u6837\u90fd\u53eb\u4e0d\u5f00\u95e8\u4e86\uff0c\u5979\u6839\u672c\u5df2\u62d2 \u7edd\u56de\u7b54\u6211\u4eec\u7684\u8bdd\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u63d2\u5634\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u201d\u6c34\u6bcd\u201d\u9634\u59ec\u548c\u5979\u7684\u5f92\u5f1f\u7adf\u662f\u5411\u6837\u7684\u9a84\u50b2\uff0c\u751a\u81f3\u4e0d\u613f\u8ba9\u522b\u4eba\u89c1 \u5230\u5979\u4eec\u6b7b\uff0c\u96be\u9053\u5979\u4eec\u8981\u6c38\u8fdc\u6d3b\u5728\u522b\u4eba\u5fc3\u91cc\uff0c\u201d\u80e1\u94c1\u82b1\u5e76\u672a\u5b8c\u5168\u542c\u61c2\u8fd9\u53e5\u8bdd\u4e2d\u7684\u610f\u601d\uff0c\u56e0\u4e3a\u5979 \u5b9e\u672a\u60f3\u5230\u201c\u6c34\u6bcd\u201d\u9634\u59ec\u7684\u6b7b\u6cd5\u4e5f\u662f\u5b8c\u5168\u4e00\u6837\u4ed6\u53ea\u662f\u60e8\u7136\u9053\uff1a\u201c\u65e0\u8bba\u5982\u4f55\uff0c\u6211\u4eec\u603b\u662f\u611f\u6fc0\u5979 \u7684\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u550f\u5618\u826f\u4e45\uff0c\u624d\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u662f\u600e\u4f1a\u6765\u7684\uff1f\u662f\u4e0d\u662f\u84c9\u513f\u7ec8\u65bc\u8fd8\u662f\u5c06\u5165\u5bab\u7684\u9014\u5f84\u544a\u8bc9 \u4e86\u4f60\u4eec\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u82e6\u7b11\u9053\uff1a\u201c\u4f60\u8d70\u4e86\u4e4b\u5f8c\uff0c\u6211\u4eec\u5c31\u6c42\u5979\u8bf4\u51fa\u6765\uff0c\u5979\u672c\u6765\u4e0d\u80af\uff0c\u4f46\u8fc7\u4e86\u4e00\u5929\u5f8c\uff0c\u5979\u4e5f \u5f00\u59cb\u4e3a\u4f60\u62c5\u5fc3\u8d77\u6765\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u6025\u7740\u95ee\u8feb\uff1a\u201c\u5979\u81ea\u5df1\u6709\u6ca1\u6709\u8ddf\u4f60\u4e00\u9f50\u6765\uff1f\u201d \u3000\u3000\u80e1\u732e\u82b1\u9053\uff1a\u201c\u5979\u6015\u8ddf\u6211\u4eec\u4e00\u9f50\u6765\u6709\u6240\u4e0d\u4fbf\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u66f4\u7740\u6025\uff0c\u9053\uff1a\u201c\u90a3\u9ebd\u5979\u7684\u4eba\u5462\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u5979\u8bf4\uff0c\u5979\u8981\u8d76\u5230\u90a3\u83e9\u63d0\u5eb5\u53bb\u548c\u751c\u513f\u5979\u4eec\u4f1a\u5408\uff0c\u7136\u5f8c\u518d\u770b\u770b\u662f\u5426\u80fd\u5230\u8fd9\u91cc \u6765\uff0c\u6211\u6b63\u60f3\u529d\u5979\u83ab\u8981\u7740\u6025\uff0c\u5979\u53cd\u800c\u5148\u6765\u5b89\u6170\u6211\u4e86\u3002\u201d \u3000\u3000\u4ed6\u7b11\u4e86\u7b11\uff0c\u63a5\u7740\u9053\uff1a\u201c\u5979\u5bf9\u4f60\u751a\u6709\u4fe1\u5fc3\uff0c\u8bf4\u4f60\u65e0\u8bba\u9047\u7740\u4ec0\u9ebd\u5371\u9669\uff0c\u90fd\u4e00\u5b9a\u6709\u6cd5\u5b50\u8131\u8eab \u7684\u3002\u201d \u3000\u3000\u6234\u72ec\u884c\u82e6\u7b11\u7740\u9053\uff1a\u201c\u770b\u6765\u5979\u53ea\u4e0d\u8fc7\u6709\u70b9\u4e3a\u6211\u4eec\u62c5\u5fc3\uff0c\u518d\u53c1\u529d\u6211\u4eec\u83ab\u8981\u51fa\u624b\uff0c\u53ef\u662f\u6211\u4eec\u4e00 \u5230\u8fd9\u91cc\uff0c\u5c31\u5c06\u5979\u7684\u8bdd\u5168\u90fd\u5fd8\u4e86\u3002\u201d \u3000\u3000\u9ec4\u9c81\u76f4\u8fd9\u65f6\u4e5f\u8d70\u4e86\u8fc7\u6765\uff0c\u8bb7\u6728\u9053\uff1a\u201c\u655d\u53cb\u662f\u8c01\uff0c\u9999\u5e05\u73b0\u5728\u60f3\u5fc5\u65e9\u5df2\u77e5\u9053\u4e86\uff0c\u4ed6\u65e9\u5e74\u6240\u505a \u6240\u4e3a\uff0c\u867d\u4ee4\u4eba\u65e0\u6cd5\u5411\u60c5\uff0c\u4f46\u8fd1\u5e74\u6765\u4ed6\u5df2\u6539\u8fc7\u81ea\u65b0\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u53f9\u9053\uff1a\u201c\u4ed6\u7684\u4e8b\u6211\u90fd\u5df2\u77e5\u9053\uff0c\u4e5f\u5f88\u5411\u60c5\u4ed6\uff0c\u53ea\u53ef\u60dc\u4ed6\u2026\u2026\u201d \u3000\u3000\u9ec4\u6625\u76f4\u9762\u8272\u60e8\u53d8\uff0c\u9053\uff1a\u201c\u4ed6\u2026\u2026\u4ed6\u83ab\u975e\u5df2\u906d\u4e86\u6bd2\u624b\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u957f\u53f9\u4e0d\u8bed\u3002 \u3000\u3000\u9ec4\u9c81\u76f4\u55c4\u58f0\u9053\uff1a\u201c\u8bba\u8d77\u4ed6\u6614\u5e74\u4e4b\u884c\u4e8b\uff0c\u4e5f\u7684\u786e\u6b7b\u6709\u9980\u8f9c\uff0c\u53ef\u662f\u2026\u2026\u53ef\u662f\u2026\u2026\u5728\u4e0b\u4ecd\u60f3\u77e5 \u9053\uff0c\u7a76\u7adf\u662f\u8c01\u6740\u4e86\u4ed6\u7684\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u63d2\u5634\u9053\uff1a\u201c\u6740\u4ed6\u7684\u4eba\u4e5f\u5df2\u88ab\u4eba\u6740\u4e86\uff0c\u800c\u4e14\u662f\u795e\u6c34\u5bab\u4e3b\u4e3a\u4ed6\u62a5\u7684\u4ec7\uff0c\u5982\u4eca\u4ed6\u4eec\u4e00\u5bb6 \u53c1\u53e3\uff0c\u60f3\u5fc5\u5df2\u5728\u5929\u4e0a\u56e2\u5706\uff0c\u524d\u8f88\u53c8\u4f55\u5fc5\u518d\u4e3a\u4ed6\u4f24\u5fc3\uff1f\u201d \u3000\u3000\u9ec4\u6625\u76f4\u9eef\u7136\u5782\u82dc\uff0c\u54fa\u54fa\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4ee5\u4ed6\u7684\u7f6a\u5b7d\uff0c\u843d\u5f97\u8fd9\u6837\u7684\u4e0b\u573a\uff0c\u8001\u5929\u4e5f\u4e0d\u80fd\u7b97\u662f\u4e8f \u5f85\u4ed6\u4e86\uff01\u201d \u3000\u3000\u8bdd\u867d\u5982\u6b64\uff0c\u4ed6\u76ee\u4e2d\u8fd8\u662f\u96be\u514d\u70ed\u6cea\u8574\u76d4\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u62cd\u4e86\u62cd\u695a\u7559\u9999\u7684\u80a9\u8180\uff0c\u9053\uff1a\u201c\u4f60\u5462\uff1f\u4f60\u662f\u600e\u9ebd\u4ece\u6c34\u6bcd\u638c\u63e1\u4e2d\u9003\u51fa\u6765\u7684\uff1f\u96be\u9053\u4f60 \u53c8\u2026\u2026\u201d \u3000\u3000\u4ed6\u795e\u79d8\u7684\u4e00\u7b11\uff0c\u4f4f\u53e3\u4e0d\u8bed\u3002 \u3000\u3000\u695a\u7559\u9999\u77aa\u4e86\u4ed6\u4e00\u773c\uff0c\u9053\uff1a\u201c\u6211\u65e2\u5df2\u9003\u51fa\u6765\uff0c\u4e5f\u7528\u4e0d\u7740\u4f60\u62c5\u5fc3\u4e86\uff0c\u5012\u662f\u84c9\u513f\u5979\u4eec\uff0c\u5230\u73b0\u5728 \u4e3a\u4ec0\u9ebd\u8fd8\u6ca1\u6765\u5462\uff1f\u96be\u9053\u53c8\u51fa\u4e86\u4ec0\u9ebd\u4e8b\uff1f\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u8f6c\u8eab\u671b\u7740\u90a3\u795e\u6c34\u5bab\u7684\u5f1f\u5b50\uff0c\u542b\u7b11\u9053\uff1a\u201c\u59d1\u5a18\u7684\u82b3\u540d\u80fd\u544a\u8bc9\u6211\u5417\uff1f\u201d \u3000\u3000\u8fd9\u5c11\u5973\u672c\u5df2\u542c\u5f97\u773c\u775b\u53d1\u76f4\uff0c\u8d70\u4e5f\u4e0d\u6562\u8d70\uff0c\u6b64\u523b\u53c8\u5403\u4e86\u4e00\u60ca\uff0c\u5403\u5403\u9053\uff1a\u201c\u6211\u53eb\u5357\u82f9\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u67d4\u58f0\u9053\uff1a\u201c\u6211\u4eec\u60f3\u5230\u5916\u9762\u7684\u83e9\u63d0\u5eb5\u53bb\u627e\u4eba\uff0c\u4e0d\u77e5\u5357\u82f9\u59d1\u5a18\u4f60\u80fd\u5e26\u8def\u5417\uff1f\u201d \u3000\u3000\u5357\u82f9\u671b\u4e86\u671b\u90a3\u9053\u5df2\u5173\u5f97\u7d27\u7d27\u7684\u95e8\uff0c\u9053\uff1a\u201c\u5e08\u5085\u5e76\u6ca1\u6709\u8981\u6211\u5e26\u4f60\u4eec\u53bb\uff0c\u6211\u81ea\u5df1\u4e5f\u4e0d\u6562\u4f5c \u4e3b\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u9053\uff1a\u201c\u59d1\u5a18\u53ea\u7ba1\u653e\u5fc3\uff0c\u4f60\u5e26\u6211\u4eec\u53bb\uff0c\u5979\u7edd\u4e0d\u4f1a\u602a\u4f60\u7684\u3002\u201d \u3000\u3000\u5357\u82f9\u54ac\u7259\u95ed\u5507\uff0c\u4f3c\u5df2\u4e0d\u77e5\u8be5\u5982\u4f55\u662f\u597d\u3002 \u3000\u3000\u695a\u7559\u9999\u8f7b\u8f7b\u62c9\u4f4f\u4e86\u5979\u7684\u624b\uff0c\u9053\uff1a\u201c\u6211\u4eec\u8d70\u5427\uff01\u201d \u3000\u3000\u5357\u82f9\u7684\u8138\u4e5f\u7ea2\u4e86\uff0c\u60f3\u6323\u8131\u4ed6\u7684\u624b\uff0c\u5374\u5782\u4e0b\u4e86\u5934\uff0c\u60f3\u8bf4\u4ec0\u9ebd\uff0c\u53c8\u8bf4\u4e0d\u51fa\uff0c\u5c45\u7136\u75f4\u75f4\u7684\u8ddf\u4ed6 \u8d70\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u53f9\u4e86\u53e3\u6c14\uff0c\u6447\u7740\u5934\u9053\uff1a\u201c\u65e0\u8bba\u591a\u5155\u7684\u5973\u5b69\u5b50\uff0c\u4e00\u89c1\u5230\u8fd9\u8001\u81ed\u866b\uff0c\u597d\u50cf\u5c31\u53d8\u5f97\u4e00\u70b9 \u6cd5\u5b50\u4e5f\u6ca1\u6709\u4e86\uff0c\u6211\u771f\u4e0d\u660e\u767d\u8fd9\u662f\u4e3a\u4e86\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u6234\u72ec\u884c\u4e5f\u7b11\u4e86\uff0c\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u5982\u6b64\u7b80\u5355\u7684\u9053\u7406\uff0c\u4f60\u90fd\u4e0d\u660e\u767d\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u96be\u9053\u4ed6\u5bf9\u5973\u5b69\u5b50\u771f\u6709\u4ec0\u9ebd\u9b54\u529b\uff0c\u6211\u600e\u5730\u8fde\u4e00\u70b9\u4e5f\u770b\u4e0d\u51fa\uff1f\u201d \u3000\u3000\u6234\u72ec\u884c\u7b11\u9053\uff1a\u201c\u4f60\u82e5\u4e5f\u770b\u5f97\u51fa\uff0c\u90a3\u5c31\u7cdf\u4e86\u3002\u201d \u3000\u3000\u7011\u5e03\u6cfb\u5165\u6e56\u4e2d\uff0c\u6e56\u6c34\u53c8\u81ea\u5730\u4e0b\u6d41\u51fa\uff0c\u7011\u5e03\u4e0d\u7aed\uff0c\u6e56\u6c34\u4e0d\u6ea2\uff0c\u5750\u5750\u4e0d\u606f\uff0c\u6c38\u65e0\u65ad\u7edd\uff0c\u8fd9\u6b63 \u662f\u5927\u81ea\u7136\u7684\u7384\u5999\u3002 \u3000\u3000\u695a\u7559\u9999\u4ed6\u4eec\u6cbf\u7740\u4e00\u9053\u5730\u4e0b\u7684\u6c34\u6d41\u5f80\u524d\u8d70\uff0c\u53ea\u89c9\u5730\u52bf\u6e10\u9ad8\uff0c\u5c3d\u5934\u5904\u53c8\u6709\u5341\u9980\u77f3\u7ea7\uff0c\u77f3\u7ea7\u4e0a \u5c31\u662f\u51fa\u53e3\u3002 \u3000\u3000\u5357\u82f9\u9053\uff1a\u201c\u8fd9\u4e0a\u9762\u5c31\u662f\u83e9\u63d0\u5eb5\uff0c\u4e5f\u662f\u672c\u5bab\u7684\u95e8\u6237\u4e4b\u4e00\uff0c\u82e5\u6709\u4eba\u60f3\u8981\u5165\u5bab\uff0c\u8fd9\u662f\u6700\u65b9\u4fbf\u7684 \u6cd5\u5b50\uff0c\u56e0\u4e3a\u5927\u5e08\u59d0\u770b\u6765\u867d\u5155\uff0c\u5176\u5b9e\u5fc3\u80a0\u5374\u5f88\u8f6f\uff0c\u522b\u4eba\u82e5\u662f\u5bf9\u5979\u82e6\u82e6\u54c0\u6c42\uff0c\u5979\u5f88\u5c11\u4f1a\u72e0\u5f97\u4e0b\u5fc3 \u6765\u62d2\u7edd\u7684\u3002\u201d\uff0c\u8d70\u8fc7\u8fd9\u4e00\u6bb5\u8def\u5f8c\uff0c\u5979\u4f3c\u4e4e\u5df2\u548c\u695a\u7559\u9999\u53d8\u5f97\u5f88\u719f\u4e86\uff0c\u975e\u4f46\u518d\u4e5f\u4e0d\u5bb3\u6015\uff0c\u4e00\u53ea\u7ea4 \u624b\u4e5f\u59cb\u7ec8\u8ba9\u695a\u7559\u9999\u62c9\u7740\uff0c\u6ca1\u6709\u6323\u8131\u3002 \u3000\u3000\u4f46\u695a\u7559\u9999\u5fc3\u91cc\u5374\u5728\u6697\u6697\u7740\u6025\uff0c\u5979\u4eec\u7684\u5927\u5e08\u59d0\u65e2\u7136\u5fc3\u5f88\u8f6f\uff0c\u90a3\u9ebd\u674e\u7ea2\u8896\u5979\u4eec\u4e3a\u4ec0\u9ebd\u76f4\u5230\u6b64 \u523b\u8fd8\u672a\u6765\u5462\uff1f \u3000\u3000\u53ea\u542c\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u542c\u8bf4\u4ece\u8fd9\u91cc\u5165\u5bab\u7684\u4eba\uff0c\u90fd\u662f\u88ab\u88c5\u5728\u7bb1\u5b50\u91cc\u9001\u8fdb\u6765\u7684\uff0c\u662f\u5417\uff1f\u201d \u3000\u3000\u5357\u82f9\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u56e0\u4e3a\u5927\u5e08\u59d0\u4e0d\u80fd\u79bb\u5f00\u83e9\u63d0\u5eb5\uff0c\u6240\u4ee5\u53ea\u6709\u5c06\u4eba\u653e\u5728\u7af9\u7bb1\u91cc\uff0c\u8ba9\u7bb1\u5b50\u6d6e\u5728 \u6c34\u9762\u4e0a\u987a\u6d41\u800c\u4e0b\u3002\u201d \u3000\u3000\u80e1\u732e\u82b1\u671b\u4e86\u695a\u7559\u9999\u4e00\u773c\uff0c\u9053\uff1a\u201c\u770b\u6765\u67f3\u65e0\u7709\u8fd9\u6b21\u5012\u6ca1\u6709\u8bf4\u8c0e\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u53ea\u6709\u82e6\u7b11\u3002 \u3000\u3000\u4ed6\u5df2\u53d1\u73b0\u67f3\u65e0\u7709\u5b9e\u5728\u662f\u5f88\u61c2\u5f97\u8bf4\u8c0e\u7684\u4eba\uff0c\u56e0\u4e3a\u53ea\u6709\u8fd9\u79cd\u4eba\u624d\u61c2\u5f97\u82e5\u5728\u8c0e\u8bdd\u4e2d\u52a0\u51e0\u6210\u771f \u8bdd\uff0c\u5c31\u6700\u5bb9\u6613\u4ee4\u4eba\u76f8\u4fe1\u3002 \u3000\u3000\u5357\u82f9\u9053\uff1a\u201c\u8fd9\u51fa\u53e3\u5c31\u5728\u5927\u5e08\u59d0\u6240\u5750\u7684\u84b2\u56e2\u4e0b\uff0c\u6211\u4eec\u5e73\u65f6\u5f88\u5c11\u5230\u8fd9\u91cc\u6765\uff0c\u56e0\u4e3a\u81ea\u4ece\u5927\u5e08\u59d0 \u83b7\u7f6a\u4e4b\u5f8c\uff0c\u5e08\u5085\u5c31\u4e0d\u51c6\u59d0\u59b9\u4eec\u548c\u5979\u6765\u5f80\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u5979\u7a76\u7adf\u72af\u4e86\u4ec0\u9ebd\u7f6a\uff1f\u201d \u3000\u3000\u5357\u82f9\u9053\uff1a\u201c\u8fd9\u2026\u2026\u6211\u4e0d\u5927\u6e05\u695a\u4e86\u3002\u201d \u3000\u3000\u5979\u663e\u7136\u4e0d\u613f\u518d\u8bf4\u8d77\u8fd9\u4ef6\u4e8b\uff0c\u5306\u5306\u8d70\u4e0a\u77f3\u9636\uff0c\u5c06\u58c1\u4e0a\u7684\u94c1\u73af\u8f7b\u8f7b\u6572\u4e86\u6572\uff0c\u53ea\u542c\u53ee\u5f53\u4e4b\u58f0\uff0c \u5b9b\u5982\u9f99\u541f\uff0c\u56db\u9762\u77f3\u58c1\u90fd\u8d77\u4e86\u56de\u5e94\u3002 \u3000\u3000\u5357\u82f9\u9053\uff1a\u201c\u56e0\u4e3a\u5927\u5e08\u59d0\u7ec8\u65e5\u5750\u5728\u84b2\u56e2\u4e0a\uff0c\u6781\u5c11\u8d70\u52a8\uff0c\u6240\u4ee5\u53ea\u8981\u94c1\u73af\u4e00\u6572\uff0c\u5979\u7acb\u523b\u5c31\u77e5 \u9053\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u4e0d\u8bf4\u8bdd\u4e86\uff0c\u4ed6\u5fc3\u91cc\u4e5f\u96be\u514d\u6709\u4e9b\u7d27\u5f20\uff0c\u5e0c\u671b\u8fd9\u79d8\u9053\u7684\u95e8\u6237\u5feb\u4e9b\u51fa\u73b0\uff0c\u597d\u53bb\u77a7\u77a7\u5b8b\u751c \u513f\u5979\u4eec\u7a76\u7adf\u51fa\u4e86\u4ec0\u9ebd\u4e8b\uff1f \u3000\u3000\u8c01\u77e5\u8fc7\u4e86\u534a\u664c\uff0c\u5730\u9053\u4e0a\u4ecd\u662f\u6beb\u65e0\u52a8\u9759\u3002 \u3000\u3000\u5357\u82f9\u76b1\u7709\u9053\uff1a\u201c\u5947\u602a\uff0c\u5927\u5e08\u59d0\u73b0\u5728\u96be\u9053\u51d1\u5de7\u4e0d\u5728\u4e0a\u9762\u5e7f\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u5fc3\u91cc\u867d\u6025\uff0c\u53cd\u800c\u5b89\u6170\u7740\u5979\u9053\uff1a\u201c\u4e5f\u8bb8\u5979\u5076\u7136\u51fa\u53bb\u8d70\u52a8\u8d70\u52a8\u8fd9\u662f\u662f\u4eba\u4e4b\u5e38\u60c5\u3002\u201d \u3000\u3000\u5357\u82f9\u9053\uff1a\u201c\u5979\u7edd\u4e0d\u4f1a\u79bb\u5f00\u83e9\u63d0\u5eb5\uff0c\u4e0a\u9762\u7684\u5730\u65b9\u5e76\u4e0d\u5927\uff0c\u5979\u65e0\u8bba\u5728\u90a3\u91cc\uff0c\u53ea\u8981\u73af\u58f0\u4e00\u54cd\uff0c \u5979\u672c\u6765\u90fd\u5e94\u8be5\u542c\u5f97\u5230\u7684\uff0c\u9664\u975e\u4e0a\u9762\u4e5f\u51fa\u4e86\u4e8b\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u81ea\u7136\u6bd4\u5979\u66f4\u7740\u6025\uff0c\u56e0\u4e3a\u4ed6\u2026\u2026 \u3000\u3000\u67f3\u65e0\u7709\u65e2\u7136\u77e5\u9053\u53ea\u8981\u5979\u4eec\u4e00\u5165\u5bab\uff0c\u5c31\u7acb\u523b\u4f1a\u63ed\u7834\u5979\u7684\u8c0e\u8bdd\uff0c\u81ea\u7136\u8981\u5343\u65b9\u767e\u8ba1\u7684\u963b\u6270\u5979 \u4eec\u3002 \u3000\u3000\u674e\u7ea2\u8896\u867d\u7136\u535a\u95fb\u5f3a\u8bb0\uff0c\u4f46\u5374\u5e76\u6ca1\u6709\u4ec0\u9ebd\u5fc3\u673a\uff0c\u5b8b\u751c\u513f\u66f4\u662f\u5929\u771f\u70c2\u6f2b\uff0c\u5168\u4e0d\u61c2\u4e16\u8feb\u4eba\u5fc3\u7684 \u5978\u8bc8\u3002 \u3000\u3000\u4f55\u51b5\u5979\u4eec\u4e24\u4eba\u53c8\u5168\u90fd\u5bf9\u67f3\u65e0\u7709\u751f\u51fa\u4e86\u5411\u60c5\u4e4b\u5fc3\uff0c\u6240\u4ee5\u67f3\u65e0\u7709\u5982\u8981\u5bb3\u5979\u4eec\uff0c\u5b9e\u5728\u662f\u6613\u5982\u53cd \u638c\u3002 \u3000\u3000\u53ea\u542c\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u4e0a\u9762\u4e0d\u5f00\u95e8\uff0c\u6211\u4eec\u5c31\u96be\u9053\u6ca1\u6cd5\u5b50\u8fdb\u53bb\u5417\uff1f\u201d \u3000\u3000\u5357\u82f9\u9053\uff1a\u201c\u6ca1\u6cd5\u5b50\uff0c\u8fd9\u5730\u9053\u7684\u51fa\u53e3\u53ea\u6709\u5728\u4e0a\u9762\u624d\u80fd\u5f00\u542f\uff0c\u56e0\u4e3a\u5e08\u5085\u6015\u6211\u4eec\u6109\u6109\u6e9c\u51fa\u53bb \u73a9\u2026\u2026\u201d \u3000\u3000\u80e1\u94c1\u82b1\u5ffd\u7136\u4e00\u62cd\u5df4\u638c\uff0c\u5931\u7b11\u9053\uff1a\u201c\u6211\u5fd8\u4e86\u4e00\u4ef6\u4e8b\uff0c\u60f3\u4e0d\u5230\u8fde\u4f60\u4e5f\u5fd8\u4e86\u3002\u201d \u3000\u3000\u5357\u82f9\u6014\u4e86\u6014\uff0c\u9053\uff1a\u201c\u6211\u5fd8\u4e86\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u4f60\u5927\u5e08\u59d0\u53c8\u804b\u53c8\u54d1\uff0c\u53ea\u6709\u5750\u5728\u84b2\u56e2\u4e0a\uff0c\u624d\u80fd\u611f\u89c9\u5230\u4f60\u5728\u4e0b\u9762\u6572\u51fb\u94c1\u73af\uff0c\u82e5 \u662f\u8d70\u5230\u522b\u7684\u5730\u65b9\u4e86\uff0c\u90a3\u91cc\u8fd8\u80fd\u542c\u5f97\u5230\u58f0\u97f3\u3002\u201d \u3000\u3000\u5357\u82f9\u5ae3\u7136\u9053\uff1a\u201c\u5979\u80fd\u542c\u5f97\u5230\u7684\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u4e3a\u4ec0\u9ebd\uff1f\u96be\u9053\u5979\u65e2\u4e0d\u804b\uff0c\u4e5f\u4e0d\u54d1\uff0c\u53ea\u662f\u6545\u610f\u8888\u51fa\u6765\u7684\uff1f\u201d \u3000\u3000\u8c01\u77e5\u5357\u82f9\u8fd8\u662f\u6447\u7740\u5934\u9053\uff1a\u201c\u5979\u7684\u786e\u53c8\u58f0\u53c8\u54d1\uff0c\u4e00\u70b9\u4e5f\u4e0d\u5047\u3002\u201d \u3000\u3000\u8fd9\u6b21\u80e1\u94c1\u82b1\u4e5f\u6014\u4f4f\u4e86\uff0c\u9053\uff1a\u201c\u65e2\u7136\u771f\u7684\u53c8\u804b\u53c8\u54d1\uff0c\u53c8\u600e\u80fd\u542c\u5f97\u5230\u58f0\u97f3\u5462\uff1f\u201d \u3000\u3000\u5357\u82f9\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u539f\u56e0\u4f60\u89c1\u5230\u5979\u4e4b\u5f8c\uff0c\u4e5f\u8bb8\u5c31\u660e\u767d\u4e86\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u6014\u4e86\u534a\u664c\uff0c\u604d\u7136\u9053\uff1a\u201c\u6211\u73b0\u5728\u5df2\u660e\u767d\u4e86\u3002\u201d \u3000\u3000\u5357\u82f9\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u80e1\u7eed\u82b1\u9053\uff1a\u201c\u6709\u4eba\u53ea\u8981\u770b\u522b\u4eba\u5634\u5507\u7684\u52a8\u4f5c\uff0c\u5c31\u80fd\u731c\u51fa\u4ed6\u5728\u8bf4\u4ec0\u9ebd\u8bdd\uff0c\u4f60\u5e08\u59d0\u60f3\u5fc5\u4e5f\u6709\u8fd9 \u79cd\u672c\u9886\u3002\u201d \u3000\u3000\u5357\u82f9\u957f\u957f\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5979\u4e0d\u4f46\u53c8\u804b\u53c8\u54d1\uff0c\u800c\u4e14\u2026\u2026\u800c\u4e14\u773c\u775b\u4e5f\u4e0d\u884c\u4e86\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u53c8\u6014\u4f4f\uff0c\u5403\u60ca\u9053\uff1a\u201c\u5979\u96be\u9053\u8fd8\u662f\u4e2a\u778e\u5b50\uff1f\u201d \u3000\u3000\u5357\u82f9\u9053\uff1a\u201c\u55ef\uff01\u201d \u3000\u3000\u80e1\u620f\u82b1\u6025\u5f97\u76f4\u63c9\u9f3b\u5b50\uff0c\u82e6\u7b11\u7740\u5583\u5583\u9053\uff1a\u201c\u4e00\u4e2a\u4eba\u53c8\u804b\u53c8\u54d1\u53c8\u778e\uff0c\u5374\u80fd\u542c\u5f97\u522b\u4eba\u5411\u5979\u82e6\u82e6 \u54c0\u6c42\uff0c\u800c\u4e14\u8ff8\u80fd\u542c\u5230\u6572\u95e8\u7684\u58f0\u8a00\uff0c\u8001\u81ed\u866b\uff0c\u4f60\u4e00\u5411\u5f88\u806a\u660e\uff0c\u8fd9\u6b21\u6015\u4e5f\u88ab\u5f04\u7cca\u6d82\u4e86\u5427\uff1f\u201d \u3000\u3000\u53ea\u542c\u6572\u73af\u4e4b\u58f0\u53c8\u54cd\u4e86\u8d77\u6765\u3002 \u3000\u3000\u8fd9\u6b21\u5357\u82f9\u6572\u7684\u58f0\u97f3\u66f4\u5927\u3002 \u3000\u3000\u4f46\u8fc7\u4e86\u534a\u664c\uff0c\u4e0a\u9762\u4ecd\u7136\u6beb\u65e0\u56de\u5e94\u3002 \u3000\u3000\u695a\u7559\u9999\u5fcd\u4e0d\u4f4f\u4e5f\u8d70\u4e86\u4e0a\u53bb\uff0c\u5c06\u8033\u6735\u8d34\u4f4f\u4e0a\u9762\u7684\u77f3\u58c1\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u6025\u7740\u95ee\u9053\uff1a\u201c\u4f60\u542c\u89c1\u4e86\u4ec0\u9ebd\u58f0\u97f3\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u76b1\u7740\u7709\uff0c\u9053\uff1a\u201c\u542c\u4e0d\u5927\u6e05\u695a\uff0c\u597d\u50cf\u4ec0\u9ebd\u58f0\u97f3\u90fd\u6ca1\u6709\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u8dfa\u811a\u9053\uff1a\u201c\u4f60\u9f3b\u5b50\u4e0d\u5bee\uff0c\u8033\u6735\u96be\u9053\u4e5f\u4e0d\u7075\u4e86\u9ebd\uff1f\u201d \u3000\u3000\u6234\u72ec\u884c\u5ffd\u7136\u81ea\u8170\u7554\u7684\u9ebb\u5e03\u888b\u91cc\u53d6\u51fa\u4e2a\u94c1\u7897\uff0c\u9053\uff1a\u201c\u7528\u94c1\u7897\u6263\u5728\u77f3\u58c1\u4e0a\uff0c\u5c31\u4f1a\u542c\u5f97\u6e05\u695a \u4e9b\u3002\u201d \u3000\u3000\u90a3\u65f6\u80e1\u94c1\u82b1\u81ea\u7136\u4e0d\u4f1a\u660e\u767d\u58f0\u6ce2\u7684\u539f\u7406\uff0c\u8be7\u58f0\u9053\uff1a\u201c\u771f\u7684\uff1f\u201d \u3000\u3000\u6234\u72ec\u884c\u7b11\u9053\uff1a\u201c\u6c5f\u6e56\u4e2d\u4eba\u4eba\u90fd\u77e5\u9053\u4e10\u5e2e\u5b50\u5f1f\u5077\u9e21\u6478\u72d7\u7684\u672c\u4e8b\u51a0\u7edd\u5929\u4e0b\uff0c\u4f60\u96be\u9053\u8fd8\u672a\u542c\u8bf4 \u8fc7\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u542b\u7b11\u63a5\u8fc7\u4e86\u94c1\u7897\uff0c\u6263\u5728\u77f3\u58c1\u4e0a\uff0c\u518d\u5c06\u8033\u6735\u8d34\u4f4f\u94c1\u7897\uff0c\u4ed6\u773c\u775b\u6e10\u6e10\u4eae\u4e86\uff0c\u53cc\u80a9\u5374\u76b1 \u5f97\u66f4\u7d27\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u6709\u58f0\u97f3\u4e86\u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u55ef\uff01\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u4ec0\u9ebd\u58f0\u97f3\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u597d\u50cf\u6709\u4eba\u5728\u8bf4\u8bdd\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u6478\u7740\u9f3b\u5b50\uff0c\u5931\u7b11\u9053\uff1a\u201c\u54d1\u5df4\u96be\u9053\u4e5f\u80fd\u8bf4\u8bdd\u5417\uff1f\u201d \u3000\u3000\u5357\u82f9\u60f3\u7b11\uff0c\u5374\u6ca1\u6709\u7b11\u51fa\u6765\uff0c\u76b1\u7709\u9053\uff1a\u201c\u8fd9\u7edd\u4e0d\u662f\u6211\u5927\u5e08\u59d0\u8bf4\u8bdd\u7684\u58f0\u97f3\uff0c\u5979\u4e0d\u4f1a\u8bf4\u8bdd\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u4e5f\u8bb8\u662f\u751c\u513f\u5979\u4eec\u8fd8\u5728\u6c42\u5979\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u6c88\u541f\u7740\u9053\uff1a\u201c\u4e0d\u662f\u2026\u2026\u8fd9\u662f\u7537\u4eba\u7684\u58f0\u97f3\uff0c\u4f46\u55d3\u5b50\u5f88\u7c97\uff0c\u53c8\u4e0d\u50cf\u674e\u7389\u51fd\u3002\u201d \u3000\u3000\u5357\u82f9\u5403\u60ca\u9053\uff1a\u201c\u7537\u4eba\uff1f\u7537\u4eba\u5728\u8bf4\u8bdd\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u5931\u7b11\u9053\uff1a\u201c\u7537\u4eba\u4e5f\u662f\u4e00\u79cd\u4eba\uff0c\u6709\u65f6\u4e5f\u548c\u5973\u4eba\u4e00\u6837\u4f1a\u8bf4\u8bdd\u7684\uff0c\u59d1\u5a18\u4f55\u5fc5\u5982\u6b64\u5403 \u60ca\uff1f\u201d \u3000\u3000\u5357\u82f9\u9053\uff1a\u201c\u4f46\u591a\u5e74\u4ee5\u6765\uff0c\u4ece\u6765\u4e5f\u6ca1\u6709\u4eba\u6562\u5230\u83e9\u63d0\u5eb5\u53bb\u6253\u6270\u7684\uff0c\u6c5f\u6e56\u4e2d\u6839\u672c\u5c31\u6ca1\u6709\u51e0\u4e2a\u4eba \u77e5\u9053\u83e9\u63d0\u5eb5\u8fd9\u5730\u65b9\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u8fde\u795e\u6c34\u5bab\u73b0\u5728\u90fd\u6709\u7537\u4eba\u8fdb\u6765\u4e86\uff0c\u4f55\u51b5\u83e9\u63d0\u5eb5\uff1f\u201d \u3000\u3000\u5357\u82f9\u8138\u7b06\u53c8\u53d8\u4e86\u53d8\uff0c\u9053\uff1a\u201c\u53ef\u662f\u5230\u795e\u6c34\u5bab\u6765\u7684\u4eba\u4e00\u5b9a\u90fd\u6709\u5f88\u8feb\u5207\u7684\u7406\u7531\uff0c\u6240\u4ee5\u624d\u4e0d\u60dc\u5192 \u9669\uff0c\u83e9\u63d0\u5eb5\u5374\u53ea\u4e0d\u8fc7\u662f\u4e2a\u8352\u51c9\u7684\u5bfa\u5e99\uff0c\u65e2\u6ca1\u6709\u4e1d\u6beb\u5438\u5f15\u4eba\u7684\u5730\u65b9\uff0c\u5927\u5e08\u59d0\u4e5f\u7edd\u4e0d\u4f1a\u548c\u4efb\u4f55\u4eba \u7ed3\u6028\uff0c\u4ed6\u4eec\u5230\u90a3\u91cc\u53bb\u7684\u76ee\u7684\u4f55\u5728\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u8fd9\u4e5f\u8bb8\u662f\u56e0\u4e3a\u4ed6\u4eec\u60f3\u4ece\u90a3\u91cc\u79d8\u5bc6\u6f5c\u5165\u795e\u6c34\u5bab\u6765\u3002\u201d \u3000\u3000\u5357\u82f9\u9053\uff1a\u201c\u4f9d\u6211\u770b\uff0c\u4ed6\u4eec\u4e5f\u8bb8\u662f\u4e3a\u4e86\u4f60\u4eec\u7684\u670b\u53cb\u624d\u6765\u7684\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u76b1\u4e86\u76b1\u7709\uff0c\u4e5f\u5c06\u8033\u6735\u51d1\u5230\u94c1\u7897\u4e0a\uff0c\u4e00\u9762\u95ee\u9053\uff1a\u201c\u4f60\u542c\u4e0d\u542c\u5f97\u5230\u4ed6\u4eec\u5728\u8bf4\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u82e6\u7b11\u9053\uff1a\u201c\u201d\u542c\u4e0d\u5230\uff0c\u4ed6\u4eec\u73b0\u5728\u5df2\u7ecf\u4e0d\u8bf4\u8bdd\u4e86\u3002\u201d \u3000\u3000\u6c88\u9ed8\uff0c\u6709\u65f6\u56fa\u7136\u6bd4\u4efb\u4f55\u8bed\u8a00\u90fd\u503c\u5f97\u73cd\u60dc\uff0c\u9759\u5bc2\uff0c\u6709\u65f6\u4e5f\u6bd4\u4efb\u4f55\u58f0\u97f3\u90fd\u53ef\u6015\uff0c\u83e9\u63d0\u5eb5\u4e2d\u6b64 \u523b\u6b63\u662f\u6b7b\u4e00\u822c\u7684\u9759\u5bc2\uff0c\u8fde\u4e00\u70b9\u58f0\u97f3\u4e5f\u6ca1\u6709\uff0c\u4e0a\u9762\u7684\u4eba\u96be\u9053\u5728\u4e00\u77ac\u95f4\u5168\u6b7b\u5149\u4e86\u9ebd\uff1f\u5426\u5219\u4e3a\u4f55\u4f1a \u5ffd\u7136\u6c88\u5bc2\u4e0b\u6765\uff1f \u3000\u3000\u695a\u7559\u9999\u638c\u5fc3\u5df2\u4e0d\u89c9\u6c81\u51fa\u4e86\u51b7\u6c57\u3002 \u3000\u3000\u6bcf\u4e2a\u4eba\u90fd\u5728\u7d27\u5f20\u5730\u7b49\u7740\uff0c\u8fc7\u4e86\u5f88\u4e45\u4e4b\u5f8c\uff0c\u80e1\u94c1\u82b1\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u8fd8\u6ca1\u6709\u58f0\u6614\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u53f9\u9053\uff1a\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u5357\u82f9\u9053\uff1a\u201c\u4e5f\u8bb8\u2026\u2026\u4e5f\u8bb8\u5927\u5e08\u59d0\u5df2\u5c06\u6765\u7684\u4eba\u5168\u51fb\u9000\u4e86\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u90a3\u5979\u4e3a\u4ec0\u9ebd\u8fd8\u4e0d\u5f00\u95e8\u5462\uff1f\u201d \u3000\u3000\u5357\u82f9\u6014\u4e86\u6014\uff0c\u9f3b\u5c16\u4e5f\u6c81\u51fa\u4e86\u51b7\u6c57\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u7740\u6025\u9053\uff1a\u201c\u6211\u770b\u7ea2\u8896\u548c\u751c\u513f\u4e00\u5b9a\u51fa\u4e86\u4e8b\uff0c\u5426\u5219\u5979\u4eec\u7edd\u4e0d\u4f1a\u8fd9\u9ebd\u4eba\u90fd\u4e0d\u5f00\u8154\u7684\uff0c\u5c24 \u5176\u662f\u751c\u513f\uff0c\u8981\u5979\u95ed\u7740\u5634\u5b9e\u5728\u4e0d\u5bb9\u6613\u3002\u201d \u3000\u3000\u6234\u72ec\u884c\u8f7b\u54b3\u4e86\u4e24\u58f0\uff0c\u9053\uff1a\u201c\u4e5f\u8bb8\u5979\u4eec\u8fd8\u6ca1\u6709\u5230\u8fd9\u91cc\u6765\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u5ffd\u7136\u9053\u2026\u201c\u6211\u4eec\u73b0\u5728\u9000\u51fa\u53bb\uff0c\u7531\u5916\u9762\u8d76\u5230\u83e9\u63d0\u5eb5\u8981\u8d70\u591a\u4e45\uff1f\u201d \u3000\u3000\u5357\u82f9\u9053\uff1a\u201c\u90a3\u8981\u7ed5\u4e2a\u5927\u5708\u5b50\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u591a\u5927\u7684\u5708\u5b50\uff1f\u201d \u3000\u3000\u5357\u82f9\u9053\uff1a\u201c\u5f88\u5927\uff0c\u8f7b\u529f\u6700\u597d\u7684\u4eba\uff0c\u81f3\u5c11\u4e5f\u8981\u8d70\u53c1\u56db\u4e2a\u65f6\u8fb0\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u8dfa\u811a\u9053\uff1a\uff0c\u201c\u8fd9\u600e\u9ebd\u529e\u5462\uff1f\u7b80\u76f4\u628a\u4eba\u5feb\u6025\u75af\u4e86\uff0c\u8001\u81ed\u866b\uff0c\u4f60\u600e\u5730\u4e5f\u60f3\u4e0d\u51fa\u6cd5\u5b50\u6765 \u4e86\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u6c88\u541f\u7740\uff0c\u5ffd\u53c8\u95ee\u9053\uff1a\u201c\u4f60\u5927\u5e08\u59d0\u82e5\u662f\u7b54\u5e94\u5c06\u4eba\u9001\u5165\u795e\u6c34\u5bab\uff0c\u662f\u4e0d\u662f\u4f1a\u5148\u7ed9\u5979\u4eec\u559d \u4e00\u676f\u6709\u8ff7\u836f\u7684\u8336\uff0c\u514d\u5f97\u88ab\u4ed6\u4eec\u770b\u5230\u5165\u5bab\u7684\u9014\u5f84\u3002\u201d \u3000\u3000\u5357\u82f9\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u751c\u513f\u5979\u4eec\u4e5f\u77e5\u9053\u8fd9\u56de\u4e8b\uff0c\u6240\u4ee5\u5979\u4eec\u660e\u77e5\u8336\u91cc\u6709\u8ff7\u836f\uff0c\u4e5f\u4f1a\u9ad8\u9ad8\u5174\u5174\u7684\u559d\u4e0b \u53bb\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5979\u4eec\u65e2\u7136\u77e5\u9053\u4e00\u559d\u4e0b\u8fd9\u676f\u8336\u5c31\u5230\u4e86\u795e\u6c34\u5bab\uff0c\u81ea\u7136\u975e\u559d\u4e0d\u53ef\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u5979\u4eec\u559d\u4e0b\u53bb\u5f8c\uff0c\u5c31\u88ab\u8ff7\u5012\uff0c\u81ea\u7136\u5c31\u4e0d\u80fd\u8bf4\u8bdd\u4e86\uff0c\u6240\u4ee5\u6211\u4eec\u624d\u542c\u4e0d\u5230\u5979\u4eec\u8bf4 \u8bdd\u7684\u58f0\u97f3\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u62cd\u624b\u9053\uff1a\u201c\u6709\u9053\u7406\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4f46\u8fd9\u4f4d\u5927\u5e08\u59d0\u8fd8\u6ca1\u6709\u5c06\u5979\u4eec\u9001\u4e0b\u6765\uff0c\u83e9\u63d0\u5eb5\u4e2d\u5c31\u6765\u4e86\u5916\u654c\uff0c\u8fd9\u4e9b\u4eba\u4e5f\u8bb8\u771f \u662f\u4e3a\u4e86\u751c\u513f\u5979\u4eec\u6765\u7684\uff0c\u5c31\u8981\u5927\u5e08\u59d0\u5c06\u5979\u4eec\u4ea4\u51fa\u6765\u3002\u201d \u3000\u3000\u5357\u82f9\u62a2\u7740\u9053\uff1a\u201c\u5927\u5e08\u59d0\u7edd\u4e0d\u80af\u7684\uff0c\u5979\u4eec\u5230\u4e86\u83e9\u63d0\u5eb5\uff0c\u5c31\u662f\u5927\u5e08\u59d0\u7684\u5ba2\u4eba\uff0c\u5927\u5e08\u59d0\u65e0\u8bba\u5982 \u4f55\u4e5f\u4e0d\u4f1a\u5c06\u5979\u4eec\u4ea4\u7ed9\u522b\u4eba\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u6240\u4ee5\u90a3\u4e9b\u4eba\u5c31\u8981\u548c\u4f60\u5927\u5e08\u59d0\u8c08\u5224\uff0c\u4e0d\u5230\u8c08\u5224\u51b3\u88c2\u65f6\uff0c\u4ed6\u4eec\u4e5f\u4e0d\u613f\u5411\u795e\u6c34\u5bab \u7684\u95e8\u4e0b\u51fa\u624b\u7684\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u8fd9\u4e5f\u6709\u9053\u7406\uff0c\u53ef\u662f\u4ed6\u4eec\u73b0\u5728\u4e3a\u4ec0\u9ebd\u4e0d\u8c08\u4e86\u5462\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u8fd9\u4e5f\u8bb8\u662f\u4e3a\u4e86\u4ed6\u4eec\u7ed9\u4e86\u5927\u5e08\u59d0\u4e00\u4e2a\u9650\u671f\uff0c\u8981\u5979\u8003\u8651\u8003\u8651\uff0c\u7136\u5f8c\u518d\u7b54\u590d\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u65e2\u662f\u5982\u6b64\uff0c\u5979\u6b64\u523b\u5fc5\u5df1\u8eab\u5c45\u9669\u5883\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6765\u7684\u4eba\u82e5\u975e\u5979\u7684\u654c\u624b\uff0c\u4e5f\u5c31\u7528\u4e0d\u7740\u8c08\u5224\u4e86\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u7740\u6025\u9053\uff1a\u201c\u90a3\u9ebd\u5979\u4e3a\u4ec0\u9ebd\u8fd8\u4e0d\u8d76\u5feb\u6253\u5f00\u8fd9\u9053\u95e8\uff0c\u8ba9\u6211\u4eec\u8fdb\u53bb\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u53f9\u9053\uff1a\u201c\u5979\u8eab\u5728\u5f3a\u654c\u73af\u4f3a\u4e4b\u4e2d\uff0c\u53c8\u600e\u6562\u5c06\u795e\u6c34\u5bab\u7684\u5165\u53e3\u663e\u9732\u51fa\u6765\u5462\uff1f\u201d \u3000\u3000\u5357\u82f9\u671b\u7740\u4ed6\uff0c\u76ee\u5149\u4e2d\u5145\u6ee1\u4e86\u8d5e\u8d4f\u4e4b\u610f\u3002\u5979\u867d\u7136\u4ec0\u9ebd\u8bdd\u90fd\u6ca1\u6709\u8bf4\uff0c\u4f46\u4e00\u4e2a\u5973\u4eba\u82e5\u7528\u773c\u775b\u6765 \u8d5e\u7f8e\u7537\u4eba\uff0c\u90a3\u5b9e\u5728\u6bd4\u8bf4\u4efb\u4f55\u8bdd\u90fd\u8981\u4ee4\u4eba\u559c\u6b22\u3002 \u3000\u3000\u695a\u7559\u9999\u6478\u4e86\u6478\u9f3b\u5b50\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u8fd9\u53ea\u4e0d\u8fc7\u662f\u6211\u7684\u731c\u6d4b\u800c\u5df2\uff0c\u5b9e\u5728\u7684\u60c5\u51b5\u662f\u5426\u5982\u6b64\uff0c\u8c01\u4e5f \u4e0d\u6562\u65ad\u5b9a\u3002\u201d \u3000\u3000\u5357\u82f9\u67d4\u58f0\u9053\uff1a\u201c\u4f46\u6211\u5374\u53ef\u65ad\u5b9a\u4f60\u731c\u7684\u4e00\u5b9a\u4e0d\u9519\uff0c\u56e0\u4e3a\u9664\u6b64\u4e4b\u5916\uff0c\u6839\u672c\u5c31\u4e0d\u53ef\u80fd\u6709\u522b\u7684\u60c5 \u51b5\u53d1\u751f\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u53f9\u9053\uff1a\u201c\u4f46\u6211\u5374\u5e0c\u671b\u4ed6\u731c\u9519\u4e86\uff0c\u5426\u5219\u751c\u513f\u5979\u4eec\u65e2\u660f\u8ff7\u4e0d\u9192\uff0c\u4f60\u5927\u5e08\u59d0\u53c8\u4e0d\u6562\u5f00 \u95e8\uff0c\u6211\u4eec\u66f4\u65e0\u6cd5\u53ca\u65f6\u8d76\u53bb\u2026\u2026\u2026\u8fd9\u79cd\u60c5\u51b5\u53ef\u5458\u7684\u7cdf\u900f\u4e86\u3002\u201d \u3000\u3000\u5927\u5bb6\u60f3\u5230\u5979\u4eec\u5904\u5883\u4e4b\u5371\u9669\uff0c\u4e5f\u90fd\u4e0d\u7981\u5f62\u65bc\u7b06\u3002\u4f46\u9664\u4e86\u5728\u8fd9\u91cc\u4e7e\u7740\u6025\u4e4b\u5916\uff0c\u8c01\u4e5f\u60f3\u4e0d\u51fa\u522b \u7684\u6cd5\u5b50\u3002 \u7b2c\u4e09\u5341\u4e09\u7ae0\u3000\u5b9d\u5251\u65e0\u7f6a>> \u53e4\u9f99\u300a\u695a\u7559\u9999\u7cfb\u5217\xb7\u753b\u7709\u9e1f\u300b \u7b2c\u4e09\u5341\u4e09\u7ae0\u3000\u5b9d\u5251\u65e0\u7f6a \u3000\u3000\u5357\u82f9\u5ffd\u53c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5176\u5b9e\u5404\u4f4d\u4e5f\u7528\u4e0d\u7740\u592a\u62c5\u5fc3\uff0c\u5927\u5e08\u59d0\u672c\u662f\u6211\u4eec\u672c\u95e8\u59d0\u59b9\u4e2d\u6b66\u529f\u6700\u9ad8 \u7684\u4e00\u4eba\uff0c\u5979\u5982\u4eca\u867d\u5df2\u6b8b\u5e9f\uff0c\u6b66\u529f\u5e76\u672a\u5931\u53bb\uff0c\u4e00\u5b9a\u80fd\u5c06\u90a3\u4e9b\u4eba\u51fb\u9000\u7684\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u6447\u7740\u5934\u9053\uff1a\u201c\u5979\u82e5\u6709\u628a\u63e1\u80fd\u5c06\u90a3\u4e9b\u4eba\u51fb\u9000\uff0c\u4e00\u5b9a\u65e9\u5c31\u52a8\u624b\u4e86\uff0c\u53c8\u600e\u4f1a\u7b49\u5230\u73b0 \u5728\u3002\u201d \u3000\u3000\u5357\u82f9\u9053\uff1a\u201c\u53ef\u662f\u2026\u2026\u53ef\u662f\u6211\u5e08\u5085\u5e38\u8bf4\uff0c\u5927\u5e08\u59d0\u7684\u6b66\u529f\u5df2\u7edd\u4e0d\u5728\u5f53\u4eca\u6b66\u6797\u6700\u8d1f\u76db\u540d\u5341\u5927\u9ad8 \u624b\u4e4b\u4e0b\uff0c\u90a3\u4e9b\u4eba\u7684\u6b66\u529f\u96be\u9053\u8fd8\u80fd\u6bd4\u5979\u66f4\u9ad8\u9ebd\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u82e6\u7b11\u9053\uff1a\u201c\u6562\u548c\u695a\u7559\u9999\u4f5c\u5bf9\u7684\u4eba\uff0c\u81ea\u7136\u4e00\u5b9a\u6709\u4e24\u4e0b\u5b50\u3002\u201d \u3000\u3000\u6234\u72ec\u884c\u9053\uff1a\u201c\u9999\u5e05\u80fd\u60f3\u5f97\u51fa\u90a3\u4e9b\u4eba\u662f\u8c01\u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u82e6\u7b11\u9053\uff1a\u201c\u6211\u7eb5\u80fd\u731c\u5f97\u51fa\u90a3\u4e9b\u4eba\u662f\u8c01\uff0c\u65bc\u4e8b\u53c8\u6709\u4f55\u8865\uff1f\u201d \u3000\u3000\u5176\u5b9e\u4ed6\u5df2\u731c\u51fa\u90a3\u4e9b\u4eba\u516b\u6210\u662f\u67f3\u65e0\u7709\u52fe\u5f15\u6765\u7684\uff0c\u5979\u8fd9\u9ebd\u505a\u4e0d\u4f46\u53ef\u4ee5\u622a\u65ad\u695a\u7559\u9999\u7684\u9053\u8def\uff0c\u800c \u4e14\u8fd8\u53ef\u4ee5\u5c06\u751c\u513f\u5979\u4eec\u64d2\u4e3a\u4eba\u8d28\uff0c\u7528\u6765\u8981\u80c1\u695a\u7559\u9999\uff0c\u5373\u4f7f\u4e8b\u5f8c\u80fd\u4fa5\u5e78\u9003\u51fa\uff0c\u4e5f\u65e0\u6cd5\u518d\u6cc4\u9732\u5979\u7684 \u79d8\u5bc6\u3002 \u3000\u3000\u695a\u7559\u9999\u5df2\u8ba4\u5b9a\u4e86\u8fd9\u5fc5\u5b9a\u53c8\u662f\u67f3\u65e0\u7709\u7684\u8fde\u73af\u6bd2\u8ba1\u3002 \u3000\u3000\u4ed6\u957f\u957f\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6211\u73b0\u5728\u53ea\u5e0c\u671b\u4f60\u4eec\u7684\u5927\u5e08\u59d0\u5df2\u770b\u51fa\u81ea\u5df1\u7684\u6b66\u529f\u7edd\u975e\u5bf9\u65b9\u7684\u654c \u624b\u3002\u201d \u3000\u3000\u5357\u82f9\u76b1\u7709\u9053\uff1a\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u53ea\u56e0\u5979\u82e5\u88ab\u903c\u5f97\u65e0\u6cd5\u53ef\u65bd\u65f6\uff0c\u4e5f\u8bb8\u5c31\u4f1a\u4e0d\u987e\u4e00\u5207\uff0c\u5c06\u8fd9\u9053\u95e8\u6253\u5f00\u4e86\u3002\u201d \u3000\u3000\u6234\u72ec\u884c\u9644\u638c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u5c31\u53eb\u7f6e\u4e4b\u6b7b\u5730\u800c\u5f8c\u751f\u3002\u201d \u3000\u3000\u5357\u82f9\u9053\uff1a\u201c\u82e5\u662f\u6362\u4e86\u522b\u4eba\uff0c\u5230\u4e86\u7edd\u5883\u65f6\uff0c\u4e5f\u8bb8\u4f1a\u8fd9\u9ebd\u505a\u7684\uff0c\u4f46\u6211\u5927\u5e08\u59d0\u5b81\u6b7b\u4e5f\u4e0d\u4f1a\u3002\u201d \u3000\u3000\u6234\u72ec\u884c\u76b1\u7709\u9053\uff1a\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u5357\u82f9\u53f9\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u5927\u5e08\u59d0\u5c31\u56e0\u4e3a\u65e0\u5fc3\u6cc4\u9732\u4e86\u672c\u5bab\u7684\u51fa\u5165\u9053\u8def\uff0c\u6240\u4ee5\u624d\u53d7\u5230\u91cd\u8d23\uff0c\u5979\u8fd9 \u6b21\u53c8\u600e\u6562\u518d\u91cd\u8e48\u590d\u64a4\u3002\u201d \u3000\u3000\u8fd9\u4f3c\u4e4e\u5df2\u662f\u6700\u5f8c\u4e00\u4e2a\u5e0c\u671b\uff0c\u6b64\u523b\u5e0c\u671b\u53c8\u544a\u65ad\u7edd\uff0c\u5927\u5bb6\u90fd\u4e0d\u7981\u4e3a\u4e4b\u5931\u8272\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u5374\u773c\u775b\u4e00\u4eae\uff0c\u5ffd\u7136\u51b2\u4e0a\u53bb\uff0c\u7528\u624b\u6572\u51fb\u7740\u77f3\u58c1\u4e0a\u7684\u94c1\u73af\uff0c\u56db\u58c1\u90fd\u8d77\u4e86\u56de\u58f0\uff0c\u9707\u5f97\u4eba \u8033\u6735\u90fd\u9ebb\u4e86\u3002 \u3000\u3000\u5357\u82f9\u5931\u58f0\u9053\uff1a\u201c\u4f60\u8fd9\u662f\u5e72\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u7b11\u9053\uff1a\u201c\u8fd9\u5c31\u53eb\u7f6e\u4e4b\u6b7b\u5730\u800c\u5f8c\u751f\u3002\u201d \u3000\u3000\u6234\u72ec\u884c\u62cd\u624b\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u90a3\u4e9b\u4eba\u542c\u5230\u5730\u4e0b\u6709\u58f0\u97f3\u4f20\u51fa\uff0c\u5c31\u5fc5\u5b9a\u5df2\u80fd\u731c\u5230\u795e\u6c34\u5bab\u7684\u5165\u53e3\u662f \u5728\u4ec0\u9ebd\u5730\u65b9\u4e86\uff0c\u4ed6\u4eec\u82e5\u5df2\u77e5\u8fdb\u795e\u6c34\u5bab\u7684\u5165\u53e3\u5728\u4f55\u5904\uff0c\u90a3\u4f4d\u5927\u5e08\u59d0\u4e5f\u5c31\u6ca1\u6709\u4ec0\u9ebd\u9690\u7792\u53ef\u8a00\u4e86\uff0c \u5979\u82e5\u5df2\u6ca1\u6709\u987e\u5fcc\uff0c\u4e5f\u8bb8\u5c31\u4f1a\u5c06\u8fd9\u9053\u95e8\u6253\u5f00\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u7b11\u9053\uff1a\u201c\u6211\u662f\u4e2a\u7b28\u4eba\uff0c\u53ea\u80fd\u60f3\u5f97\u51fa\u8fd9\u79cd\u7b28\u6cd5\u5b50\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u4e5f\u5df2\u559c\u52a8\u989c\u8272\uff0c\u9053\uff1a\u201c\u5230\u4e86\u806a\u660e\u4eba\u90fd\u65e0\u6cd5\u53ef\u65bd\u65f6\uff0c\u7b28\u4eba\u60f3\u51fa\u6765\u7684\u6cd5\u5b50\u4e00\u5b9a\u6700\u6709 \u7528\u3002\u201d \u3000\u3000\u201c\u6709\u7528\u201d\u4e24\u4e2a\u5b57\u521a\u8bf4\u5b8c\uff0c\u5df2\u6709\u4e00\u7ebf\u5929\u5149\u7167\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u5eb5\u5802\u7684\u5149\u7ebf\u4e5f\u4e0d\u4eae\uff0c\u65e5\u8272\u88ab\u6d53\u836b\u6240\u63a9\uff0c\u5f77\u4f5b\u81ea\u53e4\u4ee5\u6765\u5c31\u7167\u4e0d\u5230\u8fd9\u91cc\uff0c\u4f7f\u5f97\u8fd9\u53e4\u8001\u7684\u4f5b \u5802\uff0c\u5e73\u6dfb\u4e86\u51e0\u5206\u51c4\u51c9\u4e4b\u610f\u3002 \u3000\u3000\u9ec4\u5e54\u4f4e\u5782\uff0c\u4e5f\u770b\u4e0d\u51fa\u795e\u9f9b\u91cc\u4f9b\u7684\u662f\u4ec0\u9ebd\u4f5b\u50cf\uff0c\u6848\u4e0a\u94fa\u7740\u548c\u795e\u5e54\u540c\u6837\u9648\u65e7\u7684\u9ec4\u7f0e\uff0c\u4f4e\u5782\u5230 \u5730\u3002 \u3000\u3000\u4e00\u4e2a\u7626\u524a\u82cd\u8001\u7684\u9752\u8863\u5c3c\uff0c\u5782\u7709\u655b\u76ee\uff0c\u76d8\u819d\u5750\u5728\u795e\u6848\u524d\u7684\u84b2\u56e2\u4e0a\uff0c\u867d\u7136\u662f\u5750\u7740\uff0c\u72b9\u53ef\u770b\u51fa \u5b83\u7684\u8eab\u6750\u5f88\u9ad8\u5927\u3002 \u3000\u3000\u5979\u67af\u9ec4\u7684\u8138\u4e0a\u5df2\u7626\u5f97\u6ca1\u6709\u4e00\u4e1d\u8089\u4e86\uff0c\u9732\u51fa\u4e86\u9ad8\u8038\u7684\u98a7\u9aa8\uff0c\u4f7f\u5f97\u5979\u770b\u6765\u66f4\u6194\u60b4\u82cd\u8001\uff0c\u4e5f\u66f4 \u4e25\u8083\u51b7\u9177\u3002 \u3000\u3000\u5728\u5979\u9762\u524d\u5de6\u53f3\u4e24\u4fa7\uff0c\u8fd8\u6709\u51e0\u5177\u84b2\u56e2\uff0c\u5de6\u9762\u84b2\u56e2\u4e5f\u76d8\u819d\u5750\u7740\u4e24\u4e2a\u5f88\u7f8e\u4e3d\u7684\u5c11\u5973\uff0c\u5934\u5782\u5728\u80f8 \u524d\uff0c\u4f3c\u5df2\u6c88\u7761\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u6b63\u662f\u674e\u7ea2\u8896\u548c\u5b8b\u751c\u513f\u3002 \u3000\u3000\u53f3\u9762\u84b2\u56e2\u4e0a\uff0c\u5750\u7740\u4e00\u7537\u4e00\u5973\uff0c\u4f46\u5374\u4e0d\u662f\u674e\u7389\u51fd\u592b\u5987\uff0c\u7537\u7684\u9762\u8272\u82cd\u767d\uff0c\u4f3c\u4e4e\u5e26\u7740\u4e2a\u9762\u5177\uff0c \u4f46\u9752\u8863\u4e0a\u8840\u8ff9\u6591\u6591\uff0c\u53c8\u4f3c\u53d7\u4e86\u91cd\u4f24\u3002\u4ed6\u7d27\u54ac\u7259\u5173\uff0c\u7d27\u95ed\u7740\u773c\u775b\uff0c\u4f3c\u5728\u5fcd\u53d7\u7740\u6781\u5927\u7684\u75db\u82e6\uff0c\u51e0 \u4e4e\u8fde\u5750\u90fd\u5750\u4e0d\u7a33\u4e86\u3002 \u3000\u3000\u90a3\u5973\u7684\u9762\u4e0a\u8499\u7740\u4e00\u65b9\u4e1d\u5dfe\uff0c\u53ea\u9732\u51fa\u4e00\u53cc\u5f88\u52a8\u4eba\u7684\u773c\u775b\uff0c\u53ea\u4e0d\u8fc7\u76ee\u5149\u4e2d\u4e5f\u5145\u6ee1\u4e86\u60ca\u60e7\u548c\u60b2 \u6124\u4e4b\u8272\u3002 \u3000\u3000\u4f5b\u5802\u4e2d\u672c\u6765\u6fc0\u8361\u7740\u4e00\u9635\u9635\u9707\u8033\u7684\u91d1\u94c1\u4ea4\u51fb\u58f0\uff0c\u58f0\u97f3\u663e\u7136\u662f\u6765\u81ea\u5730\u4e0b\uff0c\u5230\u4e86\u8fd9\u65f6\uff0c\u624d\u5ffd\u7136 \u505c\u987f\u3002 \u3000\u3000\u8fd9\u65f6\u90a3\u9752\u8863\u5c3c\u5750\u4e0b\u7684\u84b2\u56e2\u5df2\u5728\u7f13\u7f13\u79fb\u52a8\uff0c\u84b2\u56e2\u4e2d\u9732\u51fa\u4e86\u4e2a\u6d1e\u7a74\uff0c\u7136\u5f8c\uff0c\u5c31\u6709\u4e24\u4e2a\u4eba\u72e1\u5154 \u822c\u7a9c\u4e86\u51fa\u6765\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u4e0d\u95ee\u53ef\u77e5\uff0c\u81ea\u7136\u5c31\u662f\u80e1\u94c1\u82b1\u548c\u695a\u7559\u9999\u3002 \u3000\u3000\u8499\u9762\u7684\u5973\u5b50\u77a7\u89c1\u4e86\u4ed6\u4eec\uff0c\u76ee\u4e2d\u84e6\u7136\u73b0\u51fa\u4e86\u60ca\u559c\u4e4b\u8272\uff0c\u4f46\u9752\u8863\u5c3c\u90a3\u53cc\u51b7\u9177\u7684\u7738\u5b50\u91cc\uff0c\u5374\u5c04 \u51fa\u4e86\u6bd4\u5200\u8fdc\u9510\u5229\u7684\u5149\u8292\u3002 \u3000\u3000\u5979\u957f\u8896\u4e00\u5c55\uff0c\u4f46\u89c1\u4e4c\u5149\u95ea\u52a8\uff0c\u5e26\u7740\u4e00\u80a1\u5f3a\u52b2\u7edd\u4f26\u7684\u98ce\u58f0\uff0c\u547c\u5578\u7740\u5411\u695a\u7559\u9999\u4ed6\u4eec\u5377\u4e86\u8fc7 \u53bb\u3002 \u3000\u3000\u5355\u53ea\u8fd9\u4e00\u80a1\u52b2\u6c14\u5f3a\u98ce\u5df2\u4ee4\u4eba\u96be\u4ee5\u62b5\u5fa1\uff0c\u4f55\u51b5\u52b2\u98ce\u4e2d\u8fd8\u5e26\u7740\u201c\u795e\u6c34\u5bab\u201d\u89c1\u8840\u5c01\u5589\u7684\u72ec\u95e8\u6697 \u5668\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u53ea\u89c9\u5bd2\u98ce\u6251\u9762\uff0c\u9aa4\u7136\u95f4\u7adf\u88ab\u903c\u5f97\u900f\u4e0d\u8fc7\u6c14\u6765\u3002 \u3000\u3000\u4ed6\u5927\u60ca\u4e4b\u4e0b\uff0c\u8eab\u5b50\u4e00\u7f29\uff0c\u51cc\u7a7a\u5012\u7ffb\u4e86\u51fa\u53bb\uff0c\u201c\u7830\u201d\u7684\uff0c\u649e\u6563\u4e86\u7a97\u6237\uff0c\u98de\u51fa\u7a97\u5916\uff0c\u53ea\u89c9\u978b \u5b50\u4e0a\u5fae\u5fae\u4e00\u9707\uff0c\u4ee5\u4ed6\u5e94\u53d8\u4e4b\u901f\uff0c\u7adf\u8fd8\u662f\u96be\u514d\u88ab\u6697\u5668\u51fb\u4e2d\uff0c\u5e78\u597d\u4ed6\u5165\u5173\u5f8c\u8fd8\u672a\u6362\u8fc7\u978b\u5b50\uff0c\u7a7f\u7684 \u4ecd\u662f\u59ec\u51b0\u96c1\u4e3a\u4ed6\u51c6\u5907\u7684\u725b\u76ae\u9774\uff0c\u90a3\u6697\u5668\u7684\u529b\u9053\u867d\u5f3a\u52b2\uff0c\u4e5f\u7a7f\u4e0d\u900f\u8fd9\u79cd\u8001\u725b\u76ae\u3002 \u3000\u3000\u5426\u5219\u4ed6\u5c31\u7b97\u4e0d\u6b7b\uff0c\u8fd9\u6761\u817f\u4e5f\u7b97\u5e9f\u4e86\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u8eab\u5b50\u8fd8\u5728\u7a7a\u4e2d\uff0c\u5df2\u88ab\u60ca\u51fa\u4e00\u8eab\u51b7\u6c57\u3002 \u3000\u3000\u7a97\u5916\u7684\u53e4\u6811\u6d53\u836b\uff0c\u6728\u53f6\u751a\u5bc6\uff0c\u4ed6\u6b63\u60f3\u5148\u51c9\u5230\u6811\u4e0a\u518d\u8bf4\uff0c\u8c01\u77e5\u5c31\u5728\u8fd9\u65f6\uff0c\u5ffd\u542c\u201c\u54e7\u201d\u7684\u4e00 \u54cd\u3002 \u3000\u3000\u5bd2\u5149\u95ea\u52a8\u95f4\uff0c\u5df2\u6709\u4e00\u67c4\u5251\u6bd2\u86c7\u822c\u81ea\u6728\u53f6\u6d53\u836b\u95f4\u523a\u4e86\u51fa\u6765\uff0c\u6765\u52bf\u4e4b\u5feb\uff0c\u51fa\u624b\u4e4b\u6bd2\uff0c\u7adf\u4e0d\u5728 \u9752\u8863\u5c3c\u7684\u6697\u5668\u4e4b\u4e0b\u3002 \u3000\u3000\u8fd9\u4e00\u5251\u6765\u5f97\u66f4\u5927\u51fa\u4ed6\u610f\u5916\uff0c\u4ed6\u4e00\u53e3\u771f\u6c14\u5df2\u7528\u5c3d\uff0c\u8eab\u5b50\u8fd8\u5728\u7a7a\u4e2d\uff0c\u5c31\u7b97\u6709\u5929\u5927\u7684\u672c\u4e8b\uff0c\u4e5f \u8eb2\u4e0d\u5f00\u8fd9\u4e00\u5251\u4e86\u3002 \u3000\u3000\u4ed6\u5634\u91cc\u521a\u5192\u51fa\u53e3\u82e6\u6c34\uff0c\u51c6\u5907\u62da\u7740\u6328\u4e00\u5251\u4e86\uff0c\u7a81\u89c1\u9ed1\u5ffd\u5ffd\u4e00\u56e2\u9ed1\u5f71\u81ea\u7a97\u5b50\u98de\u51fa\uff0c\u8fce\u7740\u5251\u5149 \u98de\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u53ea\u542c\u53c8\u662f\u201c\u54e7\u201d\u7684\u4e00\u58f0\uff0c\u5251\u5149\u5df2\u7a7f\u900f\u4e86\u8fd9\u56e2\u9ed1\u5f71\uff0c\u7adf\u662f\u53ea\u84b2\u56e2\uff0c\u4f46\u80e1\u94c1\u82b1\u5e76\u6ca1\u6709\u770b\u5230\u8fd9 \u662f\u4ec0\u9ebd\u3002 \u3000\u3000\u4ed6\u811a\u5c16\u4e00\u6cbe\u5730\uff0c\u5df2\u53c8\u7a9c\u5165\u4e86\u7a97\u6237\u3002 \u3000\u3000\u53ea\u89c1\u695a\u7559\u9999\u4ecd\u7ad9\u5728\u90a3\u91cc\uff0c\u5f77\u4f5b\u6839\u672c\u6ca1\u6709\u52a8\u8fc7\uff0c\u65b9\u7684\u52b2\u98ce\u548c\u6697\u5668\uff0c\u4e5f\u4e0d\u77e5\u4ed6\u662f\u600e\u9ebd\u8eb2\u8fc7\u53bb \u7684\u3002 \u3000\u3000\u518d\u770b\u5357\u82f9\u4e5f\u5df2\u8dc3\u4e86\u571f\u6765\uff0c\u6b63\u62c9\u7740\u90a3\u9752\u8863\u5c3c\u7684\u624b\u5728\u8bf4\u8bdd\uff0c\u663e\u7136\u6b63\u5728\u4e3a\u695a\u7559\u9999\u4ed6\u4eec\u6c42\u60c5\uff0c\u4e3a \u4ed6\u4eec\u89e3\u91ca\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u62b9\u4e86\u62b9\u6c57\uff0c\u901a\uff1a\u201c\u8001\u81ed\u866b\uff0c\u770b\u6765\u6211\u53c8\u6b20\u4f60\u4e00\u6b21\u60c5\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u6b21\u6551\u4f60\u547d\u7684\u5012\u4e0d\u662f\u6211\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u8bb6\u7136\u9053\uff1a\u201c\u662f\u8c01\uff1f\u201d \u3000\u3000\u4ed6\u5634\u8bf4\u7740\u8bdd\uff0c\u5934\u5df2\u8f6c\u8fc7\u53bb\uff0c\u8fd9\u624d\u53d1\u73b0\u65b9\u5750\u5728\u5730\u4e0a\u7684\u8499\u9762\u5973\u5b50\u5df2\u7ad9\u4e86\u8d77\u6765\uff0c\u5ea7\u4e0b\u7684\u84b2\u56e2\u5df2 \u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u6478\u4e86\u6478\u9f3b\u5b50\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u59d1\u5a18\u6551\u4e86\u6211\u7684\u547d\uff0c\u6211\u5374\u53bb\u8c22\u522b\u4eba\uff0c\u5b9e\u5728\u4e0d\u597d\u610f\u601d\u5f97\u5f88\uff0c \u4f46\u59d1\u5a18\u4e5f\u83ab\u8981\u89c1\u602a\uff0c\u6211\u8fd9\u4eba\u867d\u7b28\uff0c\u5012\u4e5f\u77e5\u9053\u597d\u6b79\uff0c\u4ee5\u5f8c\u59d1\u5a18\u65e0\u8bba\u8981\u6211\u505a\u4ec0\u9ebd\uff0c\u8981\u6211\u6c34\u91cc\u53bb\u627e \u5c31\u6c34\u91cc\u53bb\uff0c\u8981\u6211\u706b\u91cc\u53bb\u627e\u5c31\u706b\u91cc\u53bb\u3002\u201d \u3000\u3000\u8499\u9762\u5973\u4e8e\u76ee\u5149\u95ea\u52a8\uff0c\u4f3c\u4e4e\u60f3\u8bf4\u4ec0\u9ebd\u3002 \u3000\u3000\u4f46\u8fd9\u65f6\u5357\u82f9\u5df2\u7ad9\u4e86\u8d77\u6765\uff0c\u5927\u58f0\u9053\uff1a\u201c\u6211\u5927\u5e08\u59d0\u60f3\u95ee\u95ee\u3002\u4f60\u4eec\u7684\u6765\u5386\uff0c\u548c\u672c\u5bab\u6709\u4ec0\u9ebd\u6e0a \u6e90\u3002\u201d \u3000\u3000\u5979\u662f\u80cc\u5bf9\u7740\u90a3\u9752\u8863\u5c3c\u59d1\uff0c\u6b64\u523b\u5ffd\u7136\u5411\u695a\u7559\u9999\u7728\u4e86\u7728\u773c\u775b\uff0c\u624d\u63a5\u7740\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u4eec\u548c\u672c \u5bab\u5fc5\u6709\u5f88\u6df1\u7684\u6e0a\u6e90\uff0c\u5426\u5219\u5e08\u5085\u5979\u8001\u4eba\u5bb6\u5c31\u4e0d\u4f1a\u53eb\u4f60\u4eec\u6765\u8fd9\u91cc\u4e86\uff0c\u6240\u4ee5\u4f60\u4eec\u8fd8\u662f\u5411\u5927\u5e08\u59d0\u8bf4\u660e \u7684\u597d\u3002\u201d \u3000\u3000\u5176\u5b9e\u5979\u7528\u4e0d\u7740\u7728\u773c\u775b\uff0c\u695a\u7559\u9999\u4e5f\u660e\u767d\u5979\u7684\u610f\u601d\u2014\u2014\u5979\u867d\u7136\u5c06\u4ed6\u4eec\u5e26\u6765\u8fd9\u91cc\uff0c\u5fc3\u91cc\u8fd8\u662f\u5bb3 \u6015\u5f97\u5f88\u3002 \u3000\u3000\u695a\u7559\u9999\u81ea\u7136\u4e5f\u4e0d\u4f1a\u8981\u5979\u6765\u627f\u62c5\u8fd9\u8d23\u4efb\uff0c\u6c88\u541f\u7740\u9053\uff1a\u201c\u6b64\u4e2d\u8be6\u60c5\uff0c\u4e00\u65f6\u95f4\u4e5f\u4e0d\u80fd\u8be6\u8bf4\uff0c\u7b49 \u59d1\u5a18\u89c1\u5230\u4ee4\u5e08\u65f6\uff0c\u81ea\u7136\u4f1a\u660e\u767d\u7684\uff0c\u6b64\u523b\u8fd8\u662f\u5148\u5e94\u4ed8\u8fd9\u91cc\u7684\u4e8b\u8981\u7d27\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u62a2\u7740\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u60f3\u77e5\u9053\u9b3c\u9b3c\u795f\u795f\u8eb2\u5728\u5916\u9762\u6697\u7b97\u4eba\u7684\u90a3\u4e9b\u5c0f\u5b50\u7a76\u7adf\u662f\u8c01\uff1f\u6211\u597d \u6b79\u4e5f\u8981\u7ed9\u4ed6\u4eec\u4e2a\u6559\u8bad\u3002\u201d \u3000\u3000\u9752\u8863\u5c3c\u76ee\u5149\u867d\u5728\u95ea\u52a8\u7740\uff0c\u4f46\u9762\u4e0a\u5374\u6728\u65e0\u8868\u60c5\u3002 \u3000\u3000\u5979\u7684\u773c\u775b\u51e0\u4e4e\u5168\u662f\u7070\u8272\u7684\uff0c\u5c31\u5f77\u4f5b\u6b7b\u6c34\u4e2d\u7684\u5bd2\u51b0\uff0c\u800c\u5979\u7684\u8138\u5c31\u50cf\u662f\u4e00\u6e56\u6b7b\u6c34\uff0c\u51b7\u9177\u4e2d\u53c8 \u5e26\u7740\u51fa\u5947\u7684\u5b81\u9759\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u5fcd\u4e0d\u4f4f\u53c8\u8981\u53bb\u6478\u9f3b\u5b50\uff0c\u82e6\u7b11\u7740\u9053\uff1a\u201c\u4f60\u2026\u2026\u5927\u5e08\u771f\u7684\u4e0d\u80fd\u8bf4\u8bdd\uff1f\u201d \u3000\u3000\u9752\u8863\u5c3c\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u4f46\u5927\u5e08\u5374\u80fd\u542c\u5f97\u5230\u6211\u4eec\u8bf4\u8bdd\uff1f\u201d \u3000\u3000\u9752\u8863\u5c3c\u7adf\u6447\u4e86\u6447\u5934\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u6014\u4e86\u6014\uff0c\u9053\uff1a\u201c\u4f60\u660e\u660e\u542c\u5f97\u89c1\uff0c\u4e3a\u4f55\u504f\u504f\u8981\u8bf4\u542c\u4e0d\u89c1\u5462\uff1f\u201d \u3000\u3000\u5357\u82f9\u9053\uff1a\u201c\u6211\u5927\u5e08\u59d0\u771f\u7684\u542c\u4e0d\u89c1\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u82e5\u662f\u542c\u4e0d\u89c1\uff0c\u5979\u600e\u4f1a\u70b9\u5934\u6447\u5934\uff1f\u201d \u3000\u3000\u5357\u82f9\u77a7\u4e86\u90a3\u9752\u8863\u5c3c\u4e00\u773c\uff0c\u6b32\u8a00\u53c8\u6b62\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u82e6\u7b11\u9053\uff1a\u201c\u6c42\u6c42\u4f60\u4eec\u5feb\u8bf4\u51fa\u6765\u5427\uff0c\u83ab\u8981\u518d\u6253\u54d1\u8c1c\u4e86\uff0c\u6211\u7b80\u76f4\u5df2\u5feb\u88ab\u6025\u5f97\u53d1\u75af\u3002\u201d \u3000\u3000\u770b\u6765\u695a\u7559\u9999\u731c\u7684\u5e76\u4e0d\u9519\uff0c\u674e\u7389\u51fd\u592b\u5987\u65e2\u7136\u4e0d\u5728\u8fd9\u91cc\uff0c\u5916\u9762\u7684\u4eba\u60f3\u5fc5\u662f\u4ed6\u4eec\u627e\u6765\u5bf9\u4ed8\u674e\u7ea2 \u8896\u548c\u5b8b\u751c\u513f\u7684\u3002 \u3000\u3000\u4f46\u8fd9\u4e9b\u4eba\u7a76\u7adf\u662f\u8c01\u5462\uff1f\u770b\u90a3\u4e00\u5251\u6765\u52bf\u4e4b\u72e0\u6bd2\u8f9b\u8fa3\uff0c\u4ed6\u4eec\u7684\u5251\u6cd5\u4e4b\u9ad8\uff0c\u5e76\u4e0d\u6bd4\u9ec4\u9c81\u76f4\u5dee\u591a \u5c11\u3002 \u3000\u3000\u67f3\u65e0\u7709\u53c8\u4ece\u90a3\u91cc\u627e\u6765\u8fd9\u8bb8\u591a\u9ad8\u624b\uff1f \u3000\u3000\u8fd8\u6709\uff0c\u8fd9\u8499\u7740\u9762\u7684\u4e00\u7537\u4e00\u5973\u662f\u8c01\u5462\uff1f\u4e3a\u4ec0\u9ebd\u8981\u5982\u6b64\u795e\u79d8\uff1f \u3000\u3000\u80e1\u94c1\u82b1\u5fc3\u91cc\u5c38\u662f\u7591\u56e2\u91cd\u91cd\uff0c\u5374\u504f\u504f\u9047\u4e0a\u4e00\u4e2a\u54d1\u5427\uff0c\u518d\u52a0\u4e0a\u5b63\u7ea2\u8896\u548c\u5b8b\u751c\u513f\u53c8\u660f\u8ff7\u4e0d\u9192\u3002 \u3000\u3000\u65e0\u8bba\u8c01\u9047\u82e5\u8fd9\u79cd\u4e8b\uff0c\u4e0d\u6025\u5f97\u53d1\u75af\u624d\u602a\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u7a81\u542c\u7a97\u5916\u4e00\u4eba\u5389\u58f0\u9053\uff1a\u201c\u6b64\u4e8b\u548c\u5404\u4f4d\u5168\u65e0\u5173\u7cfb\uff0c\u65b9\u90a3\u4e00\u5251\u4e5f\u53ea\u4e0d\u8fc7\u662f\u804a\u4ee5\u793a \u8b66\u800c\u5df2\uff0c\u5e76\u65e0\u4f24\u4eba\u4e4b\u610f\uff0c\u53ea\u8981\u5404\u4f4d\u5c06\u672c\u95e8\u7684\u53db\u5f92\u4ea4\u51fa\u6765\uff0c\u6211\u4eec\u7acb\u523b\u5c31\u8d70\uff0c\u79cb\u6beb\u65e0\u72af\uff1b\u4f46\u5404\u4f4d \u82e5\u662f\u5b9a\u8981\u6765\u77a0\u8fd9\u8d9f\u6d51\u6c34\uff0c\u6015\u5c31\u96be\u514d\u8981\u7389\u77f3\u4ff1\u711a\u4e86\u3002\u201d \u3000\u3000\u542c\u4ed6\u4eec\u7684\u53e3\u6c14\uff0c\u7adf\u4f3c\u5e76\u975e\u6765\u627e\u674e\u7ea2\u8896\u548c\u5b8b\u751c\u513f\u7684\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u5a25\u7709\u9053\uff1a\u201c\u4f60\u4eec\u7a76\u7adf\u662f\u4ec0\u9ebd\u4eba\uff1f\u8c01\u662f\u4f60\u4eec\u7684\u53db\u5f92\uff1f\u201d \u3000\u3000\u7a97\u5916\u8fd8\u672a\u7b54\u8bdd\uff0c\u90a3\u8eab\u8d1f\u91cd\u4f24\u7684\u8499\u9762\u5ba2\u5ffd\u7136\u8df3\u4e86\u8d77\u6765\uff0c\u6323\u624e\u7740\u5411\u5916\u51b2\u51fa\uff0c\u80e1\u94c1\u82b1\u521a\u6014\u4e86 \u6014\uff0c\u53ea\u542c\u201c\u53ee\u201d\u7684\u4e00\u54cd\uff0c\u90a3\u9752\u8863\u5c3c\u548c\u8499\u9762\u5973\u5b50\u5df2\u53cc\u53cc\u6321\u4f4f\u4e86\u8499\u9762\u5ba2\u7684\u53bb\u8def\uff0c\u8499\u9762\u5973\u5b50\u98a4\u58f0 \u9053\uff1a\u201c\u6211\u4eec\u65e2\u5df2\u5230\u4e86\u8fd9\u91cc\uff0c\u4e00\u5207\u4e8b\u5c31\u8be5\u542c\u51ed\u5927\u5e08\u4f5c\u4e3b\uff0c\u4f60\u6b64\u523b\u82e5\u662f\u51b2\u4e86\u51fa\u53bb\uff0c\u5c82\u975e\u8f9c\u8d1f\u4e86\u5979 \u8001\u4eba\u5bb6\u7684\u4e00\u756a\u597d\u610f\u3002\u201d \u3000\u3000\u9752\u8863\u5c3c\u76ee\u5149\u707c\u707c\uff0c\u77aa\u7740\u90a3\u8499\u9762\u5ba2\uff0c\u7f13\u7f13\u70b9\u7740\u5934\uff0c\u90a3\u8499\u9762\u5973\u5b50\u77ed\u8bf4\u4e00\u53e5\u8bdd\uff0c\u9752\u8863\u5c3c\u7684\u811a\u4e0b \u5c31\u6709\u4e00\u9635\u8f7b\u94c3\u822c\u7684\u58f0\u97f3\u54cd\u8d77\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u5ffd\u7136\u53d1\u73b0\u5979\u811a\u4e0b\u7adf\u7cfb\u7740\u4e00\u6761\u6781\u7ec6\u7684\u94c1\u7ec3\uff0c\u800c\u94c1\u7ec3\u7684\u53e6\u4e00\u7aef\uff0c\u5374\u88ab\u63a9\u76d6\u5728\u9ec4\u5e54\u4f4e\u5782 \u7684\u795e\u6848\u4e0b\u3002 \u3000\u3000\u8499\u9762\u5973\u5b50\u8bf4\u4e00\u53e5\u8bdd\uff0c\u8fd9\u6761\u94c1\u70bc\u5c31\u52a8\u4e00\u52a8\uff0c\u94c1\u70bc\u5728\u9752\u77f3\u677f\u4e0a\u9707\u52a8\u7740\uff0c\u5c31\u53d1\u51fa\u4e00\u9635\u9635\u8f7b\u5fae\u7684 \u201c\u53ee\u5f53\u201d\u58f0\u54cd\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u8fd9\u624d\u660e\u767d\u804b\u5b50\u662f\u600e\u4f1a\u542c\u89c1\u522b\u4eba\u8bf4\u8bdd\u7684\u4e86\uff0c\u4ed6\u5b9e\u5728\u5fcd\u4e0d\u4f4f\u60f3\u8fc7\u53bb\u77a7\u77a7\u7a76\u7adf\u662f\u4ec0\u9ebd\u4eba \u8eb2\u5728\u90a3\u795e\u6848\u5e95\u4e0b\uff1f\u4e3a\u4f55\u4e5f\u5982\u6b64\u795e\u79d8\uff1f\u4f46\u4ed6\u8fd8\u6ca1\u6709\u8d70\u8fc7\u53bb\uff0c\u695a\u7559\u9999\u5df2\u7528\u773c\u8272\u963b\u6b62\u4e86\u4ed6\u3002 \u3000\u3000\u53ea\u542c\u7a97\u5916\u90a3\u4eba\u51b7\u7b11\u9053\uff1a\u201c\u5927\u4e08\u592b\u505a\u4e8b\u6562\u4f5c\u6562\u5f53\uff0c\u5802\u5802\u7537\u5b50\u6c49\u5374\u9003\u5230\u8fd9\u91cc\u6765\u6c42\u5987\u4eba\u5973\u5b50\u7684 \u5e87\u62a4\uff0c\u7b97\u5f97\u4e86\u4ec0\u9ebd\u82f1\u96c4\u597d\u6c49\uff1f\u7b80\u76f4\u8fde\u6211\u4eec\u7684\u4eba\u90fd\u88ab\u4f60\u53bb\u5149\u4e86\u3002\u201d \u3000\u3000\u90a3\u8499\u9762\u5ba2\u8eab\u5b50\u98a4\u6296\uff0c\u5ffd\u7136\u4e00\u95ea\u8eab\uff0c\u81ea\u9752\u8863\u5c3c\u548c\u8499\u9762\u5973\u5b50\u4e4b\u95f4\u7a9c\u4e86\u8fc7\u53bb\uff0c\u4ed6\u8eab\u6cd5\u4e4b\u5feb\uff0c\u7adf \u8d85\u51fa\u80e1\u94c1\u82b1\u610f\u6599\u4e4b\u5916\u3002 \u3000\u3000\u90a3\u9752\u8863\u5c3c\u8fd9\u6b21\u4e5f\u6ca1\u6709\u62e6\u4f4f\u4ed6\uff0c\u53ea\u89c1\u4ed6\u8eab\u62ab\u7684\u5bbd\u888d\u968f\u98ce\u626c\u8d77\uff0c\u5de6\u9762\u7684\u4e00\u53ea\u8863\u8896\uff0c\u7adf\u5f77\u4f5b\u662f \u7a7a\u8361\u8361\u7684\u3002 \u3000\u3000\u3001 \u3000\u3000\u773c\u89c1\u4ed6\u5df2\u5c06\u51b2\u51fa\u95e8\uff0c\u5916\u9762\u98ce\u5439\u6728\u53f6\uff0c\u6c99\u6c99\u4f5c\u54cd\uff0c\u663e\u89c1\u4ed6\u53ea\u8981\u4e00\u811a\u8de8\u51fa\u8fd9\u83e9\u63d0\u5eb5\u95e8\u69db\uff0c\u5c31 \u4e0d\u77e5\u6709\u591a\u5c11\u9053\u5251\u5149\u8981\u5411\u4ed6\u51fb\u4e0b\u3002 \u3000\u3000\u4f46\u5c31\u5728\u8fd9\u65f6\uff0c\u53c8\u6709\u4eba\u5f71\u4e00\u95ea\uff0c\u6321\u4e86\u4ed6\u7684\u53bb\u8def\u3002 \u3000\u3000\u8fd9\u4eba\u5f8c\u53d1\u5148\u81f3\uff0c\u8eab\u6cd5\u7adf\u6bd4\u4ed6\u8fd8\u8981\u5feb\u5f97\u591a\uff0c\u4e0d\u95ee\u53ef\u77e5\uff0c\u81ea\u7136\u5c31\u662f\u201c\u8f7b\u529f\u5929\u4e0b\u7b2c\u4e00\u201d\u7684\u695a\u7559 \u9999\u4e86\u3002 \u3000\u3000\u8499\u9762\u5ba2\u5389\u58f0\u9053\uff1a\u201c\u6b64\u4e8b\u4e0e\u4f60\u65e0\u5173\uff0c\u95ea\u5f00\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u5fae\u7b11\u9053\uff1a\u201c\u4f60\u7684\u4e8b\u5c31\u662f\u6211\u7684\u4e8b\uff0c\u600e\u4f1a\u548c\u6211\u65e0\u5173\u5462\uff1f\u201d \u3000\u3000\u8499\u9762\u5ba2\u8eab\u5b50\u4e00\u9707\uff0c\u55c4\u58f0\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u662f\u8c01\uff1f\u6211\u4e0d\u8ba4\u5f97\u4f60\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u53f9\u9053\uff1a\u201c\u5c31\u7b97\u4f60\u4e0d\u8ba4\u5f97\u6211\uff0c\u6211\u8fd8\u662f\u8ba4\u5f97\u4f60\u3002\u201d \u3000\u3000\u8499\u9762\u5ba2\u5ffd\u7136\u53cd\u624b\u4e00\u638c\uff0c\u5207\u5411\u695a\u7559\u9999\u7684\u54bd\u5589\u3002 \u3000\u3000\u4f46\u695a\u7559\u9999\u65e2\u4e0d\u62db\u67b6\uff0c\u4e5f\u4e0d\u95ea\u907f\uff0c\u8499\u9762\u5ba2\u8fd9\u4e00\u638c\u679c\u7136\u5230\u4e86\u534a\u9014\u5c31\u786c\u751f\u751f\u987f\u4f4f\uff0c\u695a\u7559\u9999\u51dd\u6ce8 \u7740\u4ed6\u3002 \u3000\u3000\u4ed6\u957f\u957f\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u9eef\u7136\u9053\uff1a\u201c\u7ea2\u5144\uff0c\u6211\u77e5\u9053\u4f60\u5fc3\u9ad8\u6c14\u50b2\uff0c\u7d20\u6765\u4e0d\u80af\u6c42\u4eba\uff0c\u4f46\u5230\u4e86\u73b0\u5728 \u4f60\u82e5\u8fd8\u8981\u9690\u7792\uff0c\u5c31\u672a\u514d\u592a\u5c06\u6211\u770b\u5f97\u4e0d\u591f\u670b\u53cb\u4e86\u5427\uff1f\u201d \u3000\u3000\u8499\u9762\u5ba2\u970d\u7136\u8f6c\u8fc7\u8eab\uff0c\u80a9\u5934\u5934\u52a8\uff0c\u663e\u89c1\u5f97\u5fc3\u91cc\u5b9e\u662f\u6fc0\u52a8\u5df2\u6781\uff0c\u90a3\u8499\u9762\u5973\u5b50\u8d70\u8fc7\u53bb\u62c9\u4f4f\u4ed6\u7684 \u624b\uff0c\u76ee\u4e2d\u5df2\u6d41\u4e0b\u6cea\u6765\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u76ee\u5b9a\u53e3\u5446\uff0c\u6014\u4e86\u534a\u664c\uff0c\u8bb7\u8bb7\u9053\uff1a\u201c\u7ea2\u5144\uff0c\u66f2\u59d1\u5a18\u2026\u2026\u5509\u8872\uff01\u6211\u771f\u8be5\u6b7b\uff0c\u7adf\u6ca1\u6709\u8ba4 \u51fa\u662f\u4f60\u4eec\u3002\u201d \u3000\u3000\u90a3\u8499\u9762\u5973\u5b50\u6b63\u662f\u66f2\u65e0\u5bb9\uff0c\u51c4\u7136\u9053\uff1a\u201c\u6211\u4e0d\u80fd\u597d\u597d\u7167\u987e\u4ed6\uff0c\u53cd\u800c\u8981\u6765\u6c42\u2026\u2026\u6c42\u4eba\uff0c\u6211\u5b9e\u5728 \u89c9\u5f97\u65e0\u989c\u518d\u89c1\u4f60\u4eec\u4e4b\u9762\u4e86\uff0c\u53ef\u662f\u2026\u2026\u53ef\u662f\u2026\u2026\u201d \u3000\u3000\u80e1\u94c1\u82b1\u8df3\u4e86\u8d77\u6765\uff0c\u5927\u58f0\u9053\uff1a\u201c\u8fd9\u4e5f\u662f\u6211\u8be5\u6b7b\uff0c\u7ea2\u5144\u82e5\u975e\u88ab\u6211\u8fd9\u778e\u4e86\u773c\u7684\u6df7\u86cb\u8bef\u4f24\u6210\u6b8b \u5e9f\uff0c\u73b0\u5728\u53c8\u600e\u4f1a\u53d7\u4eba\u6b3a\u8d1f\uff0c\u4f55\u51b5\uff0c\u66f2\u59d1\u5a18\u4f60\u4eca\u5929\u53c8\u6551\u4e86\u6211\u4e00\u547d\uff0c\u6211\u2026\u2026\u6211\u2026\u2026\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u51b2\u4e86\u51fa\u53bb\uff0c\u72c2\u543c\u9053\uff1a\u201c\u8c01\u8981\u6765\u627e\u4e00\u70b9\u7ea2\u7684\u9ebb\u70e6\uff0c\u5c31\u5148\u6765\u627e\u6211\u80e1\u94c1\u82b1\u5427\uff01\u201d \u3000\u3000\u543c\u58f0\u4e2d\uff0c\u5df2\u6709\u4e24\u9053\u9752\u5149\u81ea\u6728\u53f6\u4e1b\u4e2d\u95ea\u7535\u822c\u51fb\u4e0b\u3002 \u3000\u3000\u8fd9\u65f6\u9ec4\u9c81\u76f4\u548c\u6234\u72ec\u884c\u624d\u81ea\u5730\u9053\u4e0b\u8dc3\u51fa\uff0c\u4e24\u4eba\u4e00\u5de6\u4e00\u53f3\uff0c\u4e5f\u81ea\u7a97\u5916\u4e2d\u51c9\u4e86\u51fa\u53bb\uff0c\u53ea\u542c\u6234\u72ec \u884c\u7b11\u9a82\u9053\uff1a\u201c\u597d\u7334\u513f\u5d3d\u5b50\uff0c\u771f\u4e0b\u6bd2\u624b\u5440\uff01\u201d \u3000\u3000\u53c8\u542c\u5f97\u9ec4\u9c81\u76f4\u6c88\u58f0\u9053\uff1a\u201c\u8fd9\u4e9b\u4eba\u5251\u6cd5\u8f9b\u8fa3\u72e0\u6bd2\uff0c\u81ea\u6210\u4e00\u5bb6\uff0c\u4f60\u4eec\u5c0f\u5fc3\u4e86\u3002\u201d \u3000\u3000\u4e00\u70b9\u7ea2\u53cd\u624b\u7529\u4e0b\u4e86\u8138\u4e0a\u7684\u9762\u5177\uff0c\u9732\u51fa\u4e86\u4ed6\u82cd\u767d\u800c\u6194\u60b4\u7684\u8138\uff0c\u4f46\u4ed6\u7684\u773c\u775b\u5374\u4ecd\u662f\u90a3\u9ebd\u51b7\u9177 \u5014\u5f3a\uff0c\u8dfa\u811a\u9053\uff1a\u201c\u8fd9\u662f\u6211\u7684\u4e8b\uff0c\u4f60\u4eec\u4f55\u5fc5\u63d2\u624b\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u5c0f\u80e1\u5bf9\u4f60\u81ea\u89c9\u65bc\u5c0f\u6709\u6127\uff0c\u4f60\u82e5\u4e0d\u8ba9\u4ed6\u51fa\u53bb\u6253\u4e00\u67b6\uff0c\u4ed6\u6015\u771f\u7684\u8981\u6025\u75af\u4e86\u3002\u201d \u3000\u3000\u4e00\u70b9\u7ea2\u54ac\u4e86\u54ac\u7259\uff0c\u9053\uff1a\u201c\u4f46\u8fd9\u4ef6\u4e8b\u5374\u662f\u65e0\u8bba\u8c01\u4e5f\u7ba1\u4e0d\u4e86\u7684\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u4e00\u70b9\u7ea2\u795e\u60c5\u663e\u5f97\u66f4\u7126\u8e81\uff0c\u4fbf\u58f0\u9053\uff1a\u201c\u4f60\u4e5f\u7528\u4e0d\u7740\u591a\u95ee\uff0c\u4f60\u82e5\u771f\u662f\u6211\u7684\u670b\u53cb\uff0c\u5c31\u5e26\u7740\u4ed6\u4eec \u5feb\u8d70\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u53f9\u9053\uff1a\u201c\u4ee5\u4f60\u6211\u7684\u4ea4\u60c5\uff0c\u4f60\u8fd8\u6709\u4ec0\u9ebd\u4e8b\u4e0d\u80fd\u5bf9\u6211\u8bf4\u7684\u5417\uff1f\u201d \u3000\u3000\u4e00\u70b9\u7ea2\u53ea\u662f\u6325\u624b\u9053\uff1a\u201c\u5feb\u8d70\uff01\u5feb\u8d70\uff01\u4f60\u82e5\u518d\u4e0d\u8d70\uff0c\u83ab\u602a\u6211\u8ddf\u4f60\u7ffb\u8138\u3002\u201d \u3000\u3000\u66f2\u65e0\u5bb9\u9eef\u7136\u9053\uff1a\u201c\u4ed6\u5b9e\u5728\u6709\u96be\u8a00\u7684\u82e6\u8877\u2026\u2026\u201d \u3000\u3000\u695a\u7559\u9999\u6253\u65ad\u4e86\u5b83\u7684\u8bdd\uff0c\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u4f60\u770b\u89c1\u5916\u9762\u90a3\u68f5\u6811\u4e86\u5417\uff1f\u201d \u3000\u3000\u66f2\u65e0\u5bb9\u6014\u4e86\u6014\uff0c\u867d\u7136\u4e0d\u660e\u767d\u4ed6\u4e3a\u4f55\u8981\u95ee\u8fd9\u53e5\u8bdd\uff0c\u8fd8\u662f\u70b9\u4e86\u70b9\u5934\uff0c\u9053\uff1a\u201c\u770b\u89c1\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4e00\u68f5\u6811\u4ece\u5730\u4e0a\u957f\u51fa\u6765\uff0c\u4e5f\u548c\u4eba\u4e00\u6837\uff0c\u662f\u4e3a\u4e86\u8981\u6210\u957f\u3001\u7ed3\u5b9e\u3001\u4f20\u5b97\u63a5\u4ee3\uff0c\u4f46 \u73b0\u5728\u5b83\u5374\u88ab\u8fd9\u4e9b\u4eba\u7684\u5251\u5149\u780d\u5f97\u4e71\u4e03\u516b\u7cdf\uff0c\u8fd9\u662f\u4e0d\u662f\u5f88\u53ef\u60dc\uff1f\u201d \u3000\u3000\u66f2\u65e0\u5bb9\u6014\u4e86\u6014\uff0c\u671b\u7740\u7a97\u5916\u7eb5\u6a2a\u98de\u821e\u7684\u5251\u6c14\uff0c\u4e5f\u4e0d\u77e5\u8be5\u8bf4\u4ec0\u9ebd\uff0c\u56e0\u4e3a\u5979\u8fd8\u662f\u4e0d\u660e\u767d\u695a\u7559\u9999 \u7684\u610f\u601d\u3002 \u3000\u3000\u695a\u7559\u9999\u5df2\u63a5\u7740\u9053\uff1a\u201c\u65e0\u8bba\u662f\u4eba\u7684\u751f\u547d\u4e5f\u597d\uff0c\u6811\u7684\u751f\u547d\u4e5f\u597d\uff0c\u5b83\u82e5\u8fd8\u672a\u6210\u957f\u5c31\u88ab\u6467\u6bc1\u4e86\uff0c \u603b\u662f\u4ef6\u53ef\u6068\u7684\u4e8b\uff0c\u4f46\u4f60\u80fd\u8bf4\u8fd9\u662f\u5251\u7684\u9519\u5417\uff1f\u201d \u3000\u3000\u66f2\u65e0\u5bb9\u9053\uff1a\u201c\u8fd9\u2026\u2026\u8fd9\u6211\u4e5f\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u51dd\u6ce8\u7740\u5979\uff0c\u4e00\u5b57\u5b57\u9053\uff1a\u201c\u5251\u672c\u8eab\u5e76\u6ca1\u6709\u9519\uff0c\u9519\u7684\u53ea\u662f\u90a3\u53ea\u63e1\u5251\u7684\u624b\u3002\u201d \u3000\u3000\u66f2\u65e0\u5bb9\u52a8\u5bb9\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u5df2\u77e5\u9053\u4ed6\u7684\u4e8b\u4e86\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u53f9\u4e86\u53e3\u6c14\uff0c\u81ea\u6000\u4e2d\u53d6\u51fa\u4e86\u90a3\u9762\u94dc\u724c\u2014\u2014\u94dc\u724c\u4e0a\u6709\u5341\u53c1\u67c4\u72ed\u957f\u7684\u5251\uff0c\u56f4\u7ed5\u7740\u4e00\u53ea \u624b\u3002 \u3000\u3000\u4e00\u70b9\u7ea2\u9aa4\u7136\u5931\u8272\uff0c\u5389\u58f0\u9053\uff1a\u201c\u8fd9\u662f\u90a3\u91cc\u6765\u7684\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u6ca1\u6709\u56de\u7b54\u4ed6\uff0c\u5374\u957f\u53f9\u9053\uff1a\u201c\u8fd9\u53ea\u624b\uff0c\u6015\u5c31\u662f\u4e16\u4e0a\u6700\u795e\u79d8\u3001\u6700\u90aa\u6076\u3001\u4e5f\u6700\u6709\u6743\u529b\u7684 \u4e00\u53ea\u624b\u4e86\uff0c\u56e0\u4e3a\u4ed6\u4e0d\u4f46\u5728\u6697\u4e2d\u638c\u63e1\u7740\u65e0\u6570\u4eba\u7684\u751f\u6b7b\uff0c\u800c\u4e14\u8fd8\u4ee4\u4eba\u6b7b\u5f97\u7cca\u91cc\u7cca\u6d82\uff0c\u4e0d\u660e\u4e0d\u767d\uff0c \u76f4\u5230\u6b7b\u5f8c\u8fd8\u4e0d\u77e5\u4e16\u4e0a\u6709\u8fd9\u53ea\u624b\u5b58\u5728\u3002\u201d \u3000\u3000\u4ed6\u77aa\u7740\u4e00\u70b9\u7ea2\uff0c\u6c88\u58f0\u9053\uff1a\u201c\u4e16\u4e0a\u53ea\u8981\u6709\u4e00\u53ea\u8fd9\u6837\u7684\u624b\u5b58\u5728\uff0c\u81f3\u5c11\u5c31\u6709\u4e00\u4e24\u4eba\u96be\u514d\u751f\u65bc\u6050 \u60e7\uff0c\u800c\u6b7b\u65bc\u9ed1\u6697\uff0c\u82e5\u5c06\u8fd9\u53ea\u624b\u6d88\u706d\u4e86\uff0c\u5927\u5bb6\u7684\u65e5\u5b50\u90fd\u4f1a\u8fc7\u5f97\u592a\u5e73\u5f97\u591a\uff0c\u662f\u5417\uff1f\u201d \u3000\u3000\u4e00\u70b9\u7ea2\u7528\u529b\u54ac\u7740\u7259\uff0c\u5634\u89d2\u7684\u808c\u8089\u5374\u8fd8\u662f\u5728\u4e0d\u4f4f\u62bd\u52a8\uff0c\u4fbf\u58f0\u9053\uff1a\u201c\u4f60\u60f3\u6d88\u706d\u4ed6\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u5389\u58f0\u9053\uff1a\u201c\u4f60\u7eb5\u7136\u4e0d\u60f3\u6d88\u706d\u4ed6\uff0c\u4ed6\u4e5f\u8981\u6d88\u706d\u4f60\u7684\u3002\u201d \u3000\u3000\u4e00\u70b9\u7ea2\u6025\u4fc3\u7684\u5598\u606f\u7740\uff0c\u5ffd\u7136\u75af\u72c2\u822c\u5927\u7b11\u8d77\u6765\u3002 \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u6211\u77e5\u9053\u4ed6\u4e00\u5b9a\u662f\u5f88\u53ef\u6015\u7684\u4eba\uff0c\u4f46\u65e0\u8bba\u591a\u53ef\u6015\u7684\u4eba\u6211\u90fd\u89c1\u8fc7\u4e86\u3002\u201d \u3000\u3000\u4e00\u70b9\u7ea2\u9aa4\u7136\u987f\u4f4f\u4e86\u7b11\u58f0\uff0c\u901a\uff1a\u201c\u6211\u77e5\u9053\u4f60\u5bf9\u4efb\u4f55\u4eba\u90fd\u65e0\u6240\u754f\u60e7\uff0c\u53ef\u662f\u4ed6\u2026\u2026\u201d \u3000\u3000\u4ed6\u4e00\u53cc\u7738\u5b50\u5ffd\u7136\u53d8\u5f97\u66f4\u9ed1\uff0c\u66f4\u6df1\uff0c\u770b\u6765\u5c31\u50cf\u662f\u4e2a\u65e0\u5e95\u7684\u6df1\u6d1e\uff0c\u5145\u6ee1\u4e86\u65e0\u8fb9\u7684\u6050\u60e7\uff0c\u65e0\u5e95 \u7684\u75db\u82e6\u3002 \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u5230\u4e86\u73b0\u5728\uff0c\u4f60\u96be\u9053\u8fd8\u4e0d\u613f\u52a9\u6211\u4e00\u81c2\u4e4b\u529b\uff1f\u201d \u3000\u3000\u4e00\u70b9\u7ea2\u5634\u89d2\u62bd\u52a8\u7740\uff0c\u55c4\u58f0\u9053\uff1a\u201c\u4f60\u83ab\u5fd8\u4e86\uff0c\u6211\u662f\u4ed6\u517b\u5927\u7684\uff0c\u6211\u7684\u6b66\u529f\u4e5f\u662f\u4ed6\u4f20\u6388\u7684\uff0c\u4ed6 \u7eb5\u7136\u8981\u6740\u6211\uff0c\u6211\u4e5f\u4e0d\u80fd\u51fa\u5356\u4ed6\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9ed8\u7136\u534a\u664c\uff0c\u957f\u53f9\u9053\uff1a\u201c\u8fd9\u662f\u4f60\u7684\u4e49\u6c14\uff0c\u6211\u7edd\u4e0d\u52c9\u5f3a\u4f60\u2026\u2026\u6211\u53ea\u95ee\u4f60\uff0c\u4ed6\u4eca\u5929\u6765\u4e86 \u6ca1\u6709\uff1f\u201d \u3000\u3000\u4e00\u70b9\u7ea2\u671b\u7740\u7a97\u5916\u7684\u5251\u5149\uff0c\u6c88\u9ed8\u4e86\u534a\u664c\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u4ed6\u4eca\u65e5\u82e5\u6765\u4e86\uff0c\u5916\u9762\u6015\u65e9\u5df2\u4f4f\u624b \u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u7b2c\u4e09\u5341\u56db\u7ae0\u3000\u94c1\u8840\u4f20\u5947>> \u53e4\u9f99\u300a\u695a\u7559\u9999\u7cfb\u5217\xb7\u753b\u7709\u9e1f\u300b \u7b2c\u4e09\u5341\u56db\u7ae0\u3000\u94c1\u8840\u4f20\u5947 \u3000\u3000\u695a\u7559\u9999\u76ee\u5149\u95ea\u52a8\uff0c\u8bd5\u63a2\u7740\u9053\uff1a\u201c\u90a3\u9ebd\uff0c\u859b\u8863\u4eba\u5462\u3002\u201d \u3000\u3000\u4e00\u70b9\u7ea2\u53c8\u6c88\uff0c\u4e86\u534a\u664c\uff0c\u9053\uff1a\u201c\u859b\u8863\u4eba\u7684\u5251\u6cd5\uff0c\u5728\u4ed6\u773c\u4e2d\uff0c\u53ea\u4e0d\u8fc7\u662f\u6839\u7ee3\u82b1\u9488\u800c\u5df2\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u7ee3\u82b1\u9488\uff1f\u201d \u3000\u3000\u4e00\u70b9\u7ea2\u9053\uff1a\u201c\u7ee3\u82b1\u9488\u53ea\u80fd\u7ee3\u82b1\uff0c\u82e5\u7528\u6765\u7f1d\u8863\u8872\u88ab\uff0c\u5c31\u8981\u65ad\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u6b64\u8bdd\u600e\u8bb2\uff1f\u201d \u3000\u3000\u4e00\u70b9\u7ea2\u9053\uff1a\u201c\u859b\u8863\u4eba\u7684\u5251\u6cd5\u597d\u770b\uff0c\u4ed6\u7684\u5251\u6cd5\u5b9e\u7528\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u60f3\u5230\u4e00\u70b9\u7ea2\u5251\u6cd5\u4e4b\u8f9b\u8fa3\u6709\u6548\uff0c\u4e0d\u7981\u82e6\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u597d\u770b\u7684\u5251\u6cd5\u672b\u5fc5\u80fd\u4f24\u4eba\uff0c\u6740 \u4eba\u7684\u5251\u6cd5\u672a\u5fc5\u597d\u770b\u3002\u201d \u3000\u3000\u4e00\u70b9\u7ea2\u9053\uff1a\u201c\u6b63\u662f\u5982\u6b64\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u542c\u4f60\u8fd9\u9ebd\u4e00\u8bf4\uff0c\u6211\u5012\u66f4\u60f3\u89c1\u4ed6\u4e00\u9762\u4e86\u3002\u201d \u3000\u3000\u4e00\u70b9\u7ea2\u4f3c\u4e5f\u53f9\u4e86\u4e00\u58f0\uff0c\u5583\u5583\u9053\uff1a\u201c\u4f60\u8fd8\u662f\u4e0d\u89c1\u7684\u597d\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u4e86\u7b11\uff0c\u6539\u53e3\u95ee\u9053\uff1a\u201c\u4eca\u5929\u4ed6\u4eec\u6765\u4e86\u51e0\u4e2a\u4eba\uff1f\u201d \u3000\u3000\u66f2\u65e0\u5bb9\u9053\uff1a\u201c\u516b\u4e2a\u3002\u201d \u3000\u3000\u5979\u54ac\u4e86\u54ac\u5507\uff0c\u9053\uff1a\u201c\u672c\u6765\u662f\u5341\u4e2a\u7684\uff0c\u4f46\u5728\u6d4e\u5357\u57ce\u5916\uff0c\u5df2\u88ab\u6211\u4eec\u9664\u53bb\u4e86\u4e00\u4e2a\uff0c\u8fd8\u6709\u4e00\u4e2a\u4e0d \u77e5\u4e3a\u4f55\u5ffd\u7136\u8d70\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u76b1\u7709\u9053\uff1a\u201c\u4ed6\u4eec\u5728\u6d4e\u5357\u57ce\u5df2\u76ef\u4e0a\u4e86\u4f60\u4eec\uff1f\u201d \u3000\u3000\u66f2\u65e0\u5bb9\u77a7\u4e86\u4e00\u70b9\u7ea2\u4e00\u773c\uff0c\u9eef\u7136\u9053\uff1a\u201c\u4ed6\u2026\u2026\u4ed6\u672c\u6765\u8fd8\u4e0d\u4fe1\u90a3\u4e9b\u4eba\u4f1a\u771f\u7684\u5bf9\u4ed6\u4e0b\u6bd2\u624b\uff0c\u76f4 \u5230\u4ed6\u53d7\u4e86\u91cd\u4f24\u2026\u2026\u82e5\u975e\u4ed6\u53d7\u4e86\u91cd\u4f24\uff0c\u6211\u4eec\u4e5f\u4e0d\u4f1a\u9003\u5230\u8fd9\u91cc\u6765\u4e86\u3002\u201d \u3000\u3000\u5979\u53f9\u4e86\u53e3\u6c14\uff0c\u6309\u7740\u53c8\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u5e08\u5085\u4ee5\u524d\u5bf9\u6211\u8bf4\u8fc7\uff0c\u4ee5\u5f8c\u6211\u65e0\u8bba\u9047\u7740\u4ec0\u9ebd\u5371\u96be\uff0c\u90fd\u53ef \u4ee5\u5230\u8fd9\u91cc\u6765\u6c42\u5927\u5e08\u5e87\u62a4\u2026\u2026\u90a3\u65f6\u5979\u5b9e\u5728\u5bf9\u6211\u4e0d\u9519\u3002\u201d\u3002 \u3000\u3000\u8bf4\u7740\u8bf4\u7740\uff0c\u5979\u773c\u5708\u5df2\u6e10\u6e10\u7ea2\u4e86\uff0c\u4f3c\u5df2\u60f3\u8d77\u4e86\u77f3\u89c2\u97f3\u6614\u5e74\u5bf9\u5979\u7684\u6069\u60c5\uff0c\u800c\u5fd8\u5374\u4e86\u5979\u7684\u4ec7 \u6068\u3002 \u3000\u3000\u695a\u7559\u9999\u5ffd\u7136\u53d1\u73b0\u8fd9\u51b7\u6f20\u5014\u5f3a\u7684\u5973\u5b50\uff0c\u5728\u8fd9\u4e00\u4e2a\u591a\u6708\u91cc\uff0c\u5df2\u53d8\u5f97\u6e29\u67d4\u5f97\u591a\uff0c\u4e5f\u53d8\u5f97\u66f4\u591a\u6101 \u5584\u611f\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u552f\u6709\u201c\u7231\u60c5\u201d\u7684\u529b\u91cf\u624d\u80fd\u4ee4\u5979\u8f6c\u53d8\u5f97\u8fd9\u9ebd\u5feb\uff0c\u8fd9\u9ebd\u591a\uff0c\u4ed6\u4e0d\u7981\u6697\u6697\u66ff\u4e00\u70b9\u7ea2\u9ad8 \u5174\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u77e5\u9053\u4e00\u70b9\u7ea2\u8fdf\u65e9\u4e5f\u4f1a\u88ab\u8fd9\u79cd\u529b\u91cf\u8f6f\u5316\u7684\uff0c\u8fd9\u5b64\u72ec\u7684\u5c11\u5e74\u5c31\u50cf\u662f\u4e00\u68f5\u751f\u957f\u5728\u5371\u5ca9\u4e0a \u7684\u6811\uff0c\u5b9e\u5728\u592a\u9700\u8981\u611f\u60c5\u7684\u6ecb\u6da6\u4e86\u3002 \u3000\u3000\u4ed6\u5374\u672a\u53d1\u73b0\u90a3\u9752\u8863\u5c3c\u542c\u4e86\u66f2\u65e0\u5bb9\u7684\u8bdd\uff0c\u8138\u8272\u5ffd\u7136\u5927\u53d8\uff0c\u7070\u767d\u7684\u7738\u5b50\u91cc\uff0c\u4e5f\u71c3\u70e7\u8d77\u4e00\u80a1\u706b \u7130\u3002 \u3000\u3000\u66f2\u65e0\u5bb9\u671b\u7740\u4ed6\u624b\u91cc\u7684\u94dc\u724c\uff0c\u9053\uff1a\u201c\u4ed6\u4eec\u5341\u4e2a\u4eba\u4e4b\u4e2d\u6709\u4e2a\u4eba\u5ffd\u7136\u5931\u8e2a\u4e86\uff0c\u83ab\u975e\u662f\u4f60\u2026\u2026\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u6211\u5e76\u6ca1\u6709\u6740\u4ed6\uff0c\u4f46\u4ed6\u5012\u7684\u786e\u662f\u6765\u6740\u6211\u7684\u3002\u201d \u3000\u3000\u66f2\u65e0\u5bb9\u9053\uff1a\u201c\u6211\u4eec\u8fd9\u4e00\u8def\u4e0a\uff0c\u548c\u4ed6\u4eec\u4ea4\u624b\u4e0d\u4e0b\u4e03\u6b21\uff0c\u636e\u6211\u6240\u77e5\uff0c\u5931\u8e2a\u7684\u90a3\u4eba\u4e43\u662f\u5176\u4e2d\u6b66 \u529f\u6700\u5dee\u7684\u4e00\u4e2a\uff0c\u4ed6\u4eec\u600e\u4f1a\u8981\u4ed6\u53bb\u5bf9\u4ed8\u4f60\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u56e0\u4e3a\u90a3\u65f6\u4ed6\u4eec\u5e76\u4e0d\u77e5\u9053\u523a\u6740\u7684\u5bf9\u8c61\u662f\u695a\u7559\u9999\uff0c\u81ea\u7136\u8981\u7559\u4e0b\u4e3b\u529b\u6765\u5bf9\u4ed8\u4f60 \u4eec\uff0c\u6d3e\u6700\u5dee\u7684\u4e00\u4e2a\u53bb\u4e0b\u624b\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u53c8\u95ee\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u5269\u4e0b\u7684\u8fd9\u516b\u4e2a\u4eba\uff0c\u6b66\u529f\u96be\u9053\u90fd\u6bd4\u4ed6\u9ad8\uff1f\u201d \u3000\u3000\u66f2\u65e0\u5bb9\u53f9\u9053\uff1a\u201c\u6211\u4eec\u548c\u4ed6\u4eec\u4ea4\u624b\u6709\u4e03\u6b21\uff0c\u6bcf\u6b21\u867d\u7136\u90fd\u80fd\u6b7b\u91cc\u9003\u751f\uff0c\u4f46\u4e5f\u5b9e\u5728\u662f\u4fa5\u5e78\uff0c\u6709 \u4e24\u6b21\u8fde\u6211\u81ea\u5df1\u90fd\u8ba4\u4e3a\u662f\u96be\u9003\u6bd2\u624b\u7684\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u4e5f\u77a7\u4e86\u7a97\u5916\u7684\u5251\u6c14\u4e00\u773c\uff0c\u76b1\u7709\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u5c0f\u80e1\u4ed6\u4eec\u4ee5\u4e00\u654c\u4e8c\uff0c\u6015\u8fd8\u2026\u2026\u201d \u3000\u3000\u7a81\u542c\u94c1\u70bc\u51fb\u5730\uff0c\u53ee\u5f53\u4e0d\u7edd\u3002 \u3000\u3000\u9752\u8863\u5c3c\u6ee1\u9762\u6012\u5bb9\uff0c\u77aa\u7740\u90a3\u9ec4\u5e54\u5782\u5b83\u7684\u795e\u6848\uff0c\u5979\u8db3\u8e1d\u4e0a\u7f1a\u7740\u7684\u94c1\u70bc\uff0c\u4e5f\u5728\u4e0d\u505c\u7684\u7275\u52a8\u7740\u3002 \u3000\u3000\u5357\u82f9\u66f4\u662f\u6ee1\u8138\u60ca\u60f6\u7126\u6025\u4e4b\u81f3\uff0c\u4f3c\u5df2\u4e0d\u77e5\u8be5\u5982\u4f55\u662f\u597d\u3002 \u3000\u3000\u8fd9\u65f6\u7a97\u5916\u5251\u5149\u867d\u5f3a\uff0c\u5374\u8fd8\u5e76\u672a\u5c06\u90a3\u9053\u7eb5\u6a2a\u5f00\u9614\u7684\u5200\u98ce\u548c\u90a3\u7247\u77eb\u5982\u9f99\u7684\u68cd\u5f71\u5b8c\u5168\u538b\u5012\u3002 \u3000\u3000\u695a\u7559\u9999\u5411\u5357\u82f9\u62db\u4e86\u62db\u624b\uff0c\u6084\u58f0\u95ee\u9053\uff1a\u201c\u4f60\u5927\u5e08\u59d0\u4e3a\u4ec0\u9ebd\u53d1\u813e\u6c14\uff1f\u201d \u3000\u3000\u5357\u82f9\u7691\u4e86\u66f2\u65e0\u5bb9\u4e00\u773c\uff0c\u9053\u3002\u201c\u201d\u8fd9\u4f4d\u59d1\u5a18\u65b9\u597d\u50cf\u5728\u8bf4\u6211\u5927\u5e08\u59d0\u65e0\u529b\u4fdd\u62a4\u8fd9\u5730\u529b\u7684\u5165\uff0c\u6211 \u5927\u5e08\u59d0\u542c\u4e86\u5f88\u96be\u53d7\uff0c\u60f3\u8981\u51fa\u53bb\u548c\u90a3\u4e9b\u4eba\u4e00\u8f83\u9ad8\u4e0b\uff0c\u53ef\u662f\u2026\u2026\u201d \u3000\u3000\u7a81\u89c1\u9752\u8863\u5c3c\u8dfa\u4e86\u8dfa\u811a\uff0c\u8f6c\u8eab\u98de\u63a0\u800c\u53bb\uff0c\u4f46\u521a\u5230\u95e8\u53e3\uff0c\u5979\u8db3\u4e0b\u7684\u94c1\u70bc\u5df2\u88ab\u7ef7\u5f97\u7b14\u76f4\uff0c\u518d\u4e5f \u65e0\u6cd5\u524d\u8fdb\u534a\u6b65\u3002\u3002 \u3000\u3000\u5357\u82f9\u53f9\u53e3\u6c14\uff0c\u9eef\u7136\u9053\uff1a\u201c\u53ef\u662f\u5979\u5374\u6c38\u8fdc\u65e0\u6cd5\u8d70\u51fa\u53bb\u3002\u201d \u3000\u3000\u53ea\u89c1\u9752\u8863\u5c3c\u6ee1\u9762\u6012\u5bb9\uff0c\u9752\u7b4b\u4e00\u6839\u6839\u66b4\u8d77\uff0c\u663e\u7136\u5df2\u7528\u4e86\u5168\u529b\uff0c\u695a\u7559\u9999\u65b9\u63a5\u8fc7\u5979\u4e00\u638c\uff0c\u81ea\u7136 \u77e5\u9053\u8fd9\u8001\u5c3c\u5185\u529b\u4e4b\u60ca\u4eba\u3002 \u3000\u3000\u4f46\u5979\u7eb5\u7136\u7528\u5c3d\u5168\u529b\uff0c\u5374\u4ecd\u65e0\u6cd5\u5c06\u90a3\u6839\u7ec6\u7ec6\u7684\u4e00\u6839\u94c1\u70bc\u6323\u65ad\uff0c\u5357\u82f9\u671b\u7740\u8fd9\u5df2\u5982\u7434\u5f26\u822c\u7ef7\u7d27 \u4e86\u7684\u94c1\u70bc\uff0c\u53f9\u9053\uff1a\u201c\u636e\u8bf4\u8fd9\u94c1\u70bc\u4e43\u662f\u5bd2\u94c1\u7cbe\u82f1\u6240\u94f8\uff0c\u7eb5\u662f\u524a\u94c1\u5982\u6ce5\u7684\u5b9d\u5200\u5229\u5203\uff0c\u4e5f\u96be\u5c06\u5b83\u780d \u65ad\uff0c\u4f55\u51b5\u4eba\u529b\u5462\uff1f\u201d \u3000\u3000\u53ea\u89c1\u94c1\u70bc\u8d8a\u7ef7\u8d8a\u7d27\uff0c\u90a3\u795e\u6848\u4e5f\u6447\u52a8\u8d77\u6765\uff0c\u7adf\u5e54\u4e2d\u54cd\u8d77\u4e86\u4e00\u79cd\u6781\u8f7b\u7ec6\u7684\u5598\u606f\u58f0\uff0c\u4f3c\u4e4e\u795e\u6848 \u4e0b\u4e5f\u6709\u4e2a\u4eba\u5728\u7528\u529b\u62c9\u7740\u94c1\u70bc\u3002 \u3000\u3000\u695a\u7559\u9999\u76ee\u5149\u95ea\u52a8\uff0c\u9053\uff1a\u201c\u94c1\u70bc\u7684\u53e6\u4e00\u7aef\uff0c\u4e0d\u77e5\u662f\u7f1a\u5728\u4ec0\u9ebd\u5730\u65b9\u7684\uff1f\u201d \u3000\u3000\u5357\u82f9\u5782\u4e0b\u4e86\u5934\uff0c\u9053\uff1a\u201c\u4f60\u65e2\u5df2\u770b\u51fa\u6765\u4e86\uff0c\u4f55\u5fc5\u8fd8\u8981\u95ee\u6211\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u96be\u9053\u94c1\u70bc\u7684\u53e6\u4e00\u7aef\u4e5f\u7f1a\u5728\u4e00\u4e2a\u4eba\u7684\u811a\u4e0a\uff0c\u4ed6\u5374\u85cf\u5728\u795e\u6848\u4e0b\uff0c\u4e0d\u80af\u73b0\u8eab\uff0c\u53ea \u662f\u62c9\u52a8\u7740\u94c1\u70bc\uff0c\u548c\u4f60\u5927\u5e08\u59d0\u6765\u901a\u6d88\u606f\u3002\u201d \u3000\u3000\u5357\u82f9\u53f9\u9053\uff1a\u201c\u5426\u5219\u6211\u5927\u5e08\u59d0\u53c8\u600e\u80fd\u542c\u5f97\u5230\u522b\u4eba\u8bf4\u8bdd\u5462\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4f46\u8fd9\u4eba\u662f\u8c01\u5462\uff1f\u4e3a\u4ec0\u9ebd\u4e0d\u80af\u8ba9\u4f60\u5927\u5e08\u59d0\u51fa\u53bb\uff1f\u4e3a\u4ec0\u9ebd\u6c38\u8fdc\u8eb2\u5728\u795e\u6848\u4e0b\u4e0d\u80af \u89c1\u4eba\uff1f\u201d \u3000\u3000\u5357\u82f9\u6c88\u9ed8\u4e86\u534a\u664c\uff0c\u8f7b\u8f7b\u9053\uff1a\u201c\u8fd9\u4e5f\u662f\u4e2a\u79d8\u5bc6\uff0c\u8fde\u6211\u4eec\u90fd\u4ece\u672a\u89c1\u8fc7\u4ed6\u2026\u2026\u201d \u3000\u3000\u5ffd\u7136\u95f4\uff0c\u53ea\u542c\u201c\u84ec\u201d\u7684\u4e00\u58f0\u9707\u52a8\uff0c\u90a3\u673d\u8150\u7684\u795e\u6848\u7ecf\u4e0d\u8d77\u771f\u6c14\u7684\u51b2\u6fc0\uff0c\u7adf\u88ab\u9707\u6563\uff0c\u6728\u5c51\u7eb7 \u98de\u4e2d\uff0c\u4e00\u6761\u4eba\u5f71\u5e26\u7740\u51c4\u5389\u7684\u5578\u58f0\u51b2\u4e86\u51fa\u53bb\uff0c\u5374\u7528\u90a3\u590d\u6848\u7684\u9ec4\u5e54\u5c06\u9762\u76ee\u56db\u80a2\u4e00\u9f50\u88f9\u4f4f\uff0c\u8fd8\u662f\u6ca1 \u6709\u4eba\u80fd\u770b\u5230\u4ed6\u7684\u8eab\u5f62\u9762\u8c8c\u3002 \u3000\u3000\u695a\u7559\u9999\u63a0\u8fc7\u53bb\u62cd\u4e86\u62cd\u4e00\u70b9\u7ea2\uff0c\u9053\uff1a\u201c\u7ea2\u8896\u548c\u751c\u513f\u90fd\u4ea4\u7ed9\u4f60\u4e86\u3002\u201d \u3000\u3000\u4ed6\u6839\u672c\u4e0d\u8ba9\u4e00\u70b9\u7ea2\u62d2\u7edd\uff0c\u4eba\u5df2\u968f\u7740\u8bed\u58f0\u51b2\u51fa\u3002 \u3000\u3000\u53ea\u89c1\u4e00\u9053\u5251\u5149\u5982\u5339\u7ec3\u822c\u81ea\u6728\u53f6\u4e1b\u4e2d\u98de\u6765\uff0c\u95ea\u7535\u822c\u523a\u5411\u90a3\u521a\u4ece\u795e\u6848\u4e0b\u51b2\u51fa\u53bb\u7684\u201c\u602a\u4eba\u201d\u3002 \u3000\u3000\u4ed6\u8fde\u5934\u5e26\u8138\u90fd\u88ab\u8499\u5728\u9ec4\u5e54\u91cc\uff0c\u6839\u672c\u4ec0\u9ebd\u90fd\u77a7\u4e0d\u89c1\uff0c\u4efb\u4f55\u4eba\u90fd\u4ee5\u4e3a\u4ed6\u662f\u4e07\u4e07\u8eb2\u4e0d\u5f00\u8fd9\u4e00\u5251 \u7684\u3002 \u3000\u3000\u8c01\u77e5\u5251\u5149\u523a\u4e0b\uff0c\u4ed6\u8eab\u5f62\u5ffd\u7136\u4e00\u95ea\uff0c\u5df2\u6e38\u9c7c\u822c\u81ea\u90a3\u9ed1\u8863\u52a8\u88c5\u7684\u957f\u5251\u523a\u5ba2\u9762\u524d\u6ed1\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u90a3\u9752\u8863\u5c3c\u8eab\u5f71\u4e5f\u4e00\u95ea\uff0c\u81ea\u9ed1\u8863\u523a\u5ba2\u8eab\u5f8c\u63a0\u8fc7\uff0c\u4ed6\u4eec\u4e24\u4eba\u7684\u94c1\u70bc\u5c31\u7ed5\u5728\u9ed1\u8863\u523a \u5ba2\u8eab\u4e0a\u3002 \u3000\u3000\u53ea\u542c\u201c\u55e4\u201d\u7684\u4e00\u58f0\uff0c\u90a3\u9ed1\u8863\u523a\u5ba2\u8fde\u60e8\u53eb\u4e4b\u58f0\u90fd\u6ca1\u6709\u53d1\u51fa\uff0c\u8f68\u5df2\u88ab\u8fd9\u94c1\u70bc\u751f\u751f\u52d2\u6210\u4e24\u6bb5\u3002 \u3000\u3000\u9c9c\u8840\u65d7\u82b1\u822c\u98de\u51fa\uff0c\u94c1\u70bc\u53c8\u5df2\u7ef7\u5f97\u7b14\u76f4\uff0c\u9752\u8863\u5c3c\u548c\u90a3\u8eab\u62ab\u9ec4\u5e54\u7684\u602a\u4eba\u5df2\u5411\u53e6\u4e00\u4e2a\u9ed1\u8863\u523a\u5ba2 \u63a0\u8fc7\u53bb\u3002 \u3000\u3000\u4ed6\u4eec\u8fd9\u79cd\u6740\u4eba\u7684\u65b9\u6cd5\u5b9e\u5728\u532a\u5937\u6240\u601d\uff0c\u8eab\u6cd5\u602a\u5f02\uff0c\u51fa\u624b\u4e4b\u8f9b\u8fa3\uff0c\u8fde\u695a\u7559\u9999\u89c1\u4e86\u90fd\u4e0d\u7981\u4e3a\u4e4b \u58f0\u7136\u52a8\u5bb9\u3002 \u3000\u3000\u90a3\u8fb9\u6b63\u6709\u516d\u4e03\u4e2a\u9ed1\u8863\u523a\u5ba2\u5728\u6728\u4e1b\u4e2d\u548c\u80e1\u94c1\u82b1\u3001\u9ec4\u9c81\u76f4\u6234\u72ec\u884c\u7b49\u53c1\u4eba\u7f20\u6597\u3002 \u3000\u3000\u6d53\u5bc6\u7684\u679d\u53f6\u88ab\u5251\u6c14\u6240\u6467\uff0c\u96e8\u70b9\u822c\u56db\u9762\u7eb7\u98de\uff0c\u5341\u51e0\u682a\u6d53\u836b\u52a0\u76d6\u7684\u8001\u6811\uff0c\u51e0\u4e4e\u90fd\u5df2\u53ea\u5269\u4e0b\u4e86 \u4e00\u622a\u5149\u79c3\u79c3\u7684\u6811\u5e72\u3002 \u3000\u3000\u90a3\u770b\u6765\u5c31\u50cf\u662f\u4e00\u4e9b\u88ab\u8131\u5149\u4e86\u8863\u670d\u7684\u8001\u5934\u5b50\uff0c\u9732\u7740\u82cd\u767d\u3001\u5b71\u5f31\u3001\u751f\u6ee1\u4e86\u76b1\u7eb9\u7684\u76ae\u80a4\uff0c\u5728\u897f \u98ce\u4e2d\u98a4\u6296\u7740\u3002 \u3000\u3000\u9ed1\u8863\u5251\u5ba2\u638c\u4e2d\u7684\u5251\u4e5f\u6b63\u548c\u4e00\u70b9\u7ea2\u6614\u65e5\u6240\u4f7f\u7528\u7684\u4e00\u6837\uff0c\u957f\u800c\u72ed\u7a84\uff0c\u800c\u4e14\u4efd\u91cf\u6bd4\u4e00\u822c\u5251\u90fd\u8981 \u8f7b\u5f97\u591a\u3002 \u3000\u3000\u4ed6\u4eec\u7684\u5251\u6cd5\u81ea\u7136\u4e5f\u548c\u4e00\u70b9\u7ea2\u540c\u6837\u8f9b\u8fa3\u800c\u72e0\u6bd2\uff0c\u7edd\u6ca1\u6709\u4ec0\u9ebd\u82b1\u4fcf\u7684\u62db\u5f0f\uff0c\u4e00\u51fa\u624b\u5c31\u8981\u4eba\u7684 \u547d\u3002 \u3000\u3000\u800c\u4e14\u8fd9\u4e9b\u4eba\u4ea4\u624b\u7684\u7ecf\u9a8c\u90fd\u4e30\u5bcc\u5df2\u6781\uff0c\u663e\u7136\u770b\u51fa\u80e1\u94c1\u82b1\u3001\u9ec4\u9c81\u76f4\uff0c\u548c\u6234\u72ec\u884c\u8fd9\u53c1\u4eba\u90fd\u4e0d\u662f \u597d\u60f9\u7684\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u4eec\u7edd\u4e0d\u548c\u80e1\u94c1\u82b1\u4ed6\u4eec\u6b63\u9762\u4f5c\u6218\uff0c\u7b2c\u4e00\u4eba\u957f\u5251\u524c\u51fa\u5f8c\uff0c\u8eab\u5f62\u5c31\u7acb\u523b\u95ea\u5230\u6811\u5f8c\uff0c\u7b2c\u4e8c \u4eba\u957f\u5251\u5df2\u81ea\u53e6\u4e00\u4e2a\u65b9\u5411\u524c\u51fa\u3002 \u3000\u3000\u51e0\u4eba\u5251\u5149\u7f2d\u7ed5\uff0c\u914d\u5408\u5f97\u70b9\u6ef4\u4e0d\u6f0f\uff0c\u6b63\u662f\uff1a\u201c\u77bb\u4e4b\u5728\u524d\uff0c\u5ffd\u7109\u5728\u5f8c\uff0c\u77bb\u4e4b\u5728\u5de6\uff0c\u5ffd\u7109\u5728 \u53f3\u3002\u201d \u3000\u3000\u5230\u5f8c\u6765\u80e1\u94c1\u82b1\u6839\u672c\u4efd\u4e0d\u6e05\u5bf9\u81ea\u5df1\u523a\u6765\u7684\u4e00\u5251\u7a76\u7adf\u662f\u8c01\u524c\u51fa\u7684\u4e86\uff0c\u4ed6\u4eec\u4ee5\u53c1\u654c\u516d\uff0c\u672c\u6765\u4ee5 \u4e3a\u81ea\u5df1\u53ea\u8981\u5bf9\u4ed8\u4e24\u4eba\u5c31\u5df2\u8db3\u591f\u3002 \u3000\u3000\u8c01\u77e5\u4ed6\u4eec\u6bcf\u4e2a\u4eba\u90fd\u8981\u5bf9\u4ed8\u516d\u4e2a\uff0c\u8fd9\u516d\u4eba\u8f66\u8f6e\u822c\u8f6c\u52a8\u4e0d\u6b47\uff0c\u7adf\u4f7f\u5f97\u80e1\u94c1\u82b1\u4ed6\u4eec\u7684\u529b\u91cf\u65e0\u6cd5 \u96c6\u4e2d\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u663e\u7136\u5df2\u52a8\u4e86\u771f\u706b\uff0c\u4f46\u4ed6\u638c\u4e2d\u7684\u4e00\u67c4\u5200\u7eb5\u6709\u964d\u9f99\u4f0f\u864e\u7684\u5a01\u529b\uff0c\u5374\u8fd8\u662f\u4f24\u4e0d\u4e86\u5bf9\u65b9\u4e00 \u7247\u8863\u89d2\u3002 \u3000\u3000\u695a\u7559\u9999\u4e00\u773c\u77a7\u8fc7\uff0c\u5df2\u77e5\u9053\u66f2\u65e0\u5bb9\u754f\u60e7\u7684\u5e76\u975e\u6ca1\u6709\u7406\u7531\uff0c\u8fd9\u4e9b\u9ed1\u8863\u523a\u5ba2\u7684\u786e\u90fd\u662f\u4e45\u7ecf\u8bad\u7ec3 \u7684\u51f6\u624b\u3002 \u3000\u3000\u7167\u8fd9\u6837\u6253\u4e0b\u53bb\uff0c\u80e1\u94c1\u82b1\u4ed6\u4eec\u975e\u6d41\u8840\u4e0d\u53ef\u3002 \u3000\u3000\u4f46\u8fd9\u65f6\uff0c\u9752\u8863\u5c3c\u548c\u90a3\u8eab\u88ab\u9ec4\u5e54\u7684\u602a\u4eba\u5df2\u98de\u63a0\u8fc7\u53bb\uff0c\u4e24\u4eba\u5de6\u53f3\u5305\u6284\uff0c\u4e2d\u95f4\u7684\u94c1\u70bc\u957f\u8fbe\u4e24\u4e08 \u5f00\u5916\uff0c\u4f3c\u4e4e\u60f3\u5c06\u80e1\u94c1\u82b1\u3001\u6234\u72ec\u884c\u3001\u9ec4\u9c81\u76f4\uff0c\u548c\u90a3\u516d\u4e2a\u9ed1\u8863\u5251\u5ba2\uff0c\u4e00\u9f50\u7528\u94c1\u70bc\u6346\u4f4f\uff0c\u518d\u52d2\u6b7b\u3002 \u3000\u3000\u8fd9\u94c1\u70bc\u6b64\u523b\u7adf\u53d8\u6210\u4e86\u4e00\u79cd\u6700\u5947\u7279\uff0c\u6700\u6709\u6548\u7684\u6b66\u5668\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u4ed6\u4eec\u4e00\u65f6\u95f4\u663e\u7136\u90fd\u4e0d\u77e5\u9053\u5982\u4f55\u5e94\u4ed8\u8fd9\u79cd\u6b66\u5668\uff0c\u4ed6\u4eec\u53ea\u6709\u5411\u5f8c\u9000\uff0c\u9ed1\u8863\u523a\u5ba2\u4e2d\u6709\u4e00 \u4eba\u53cd\u624b\u4e00\u5251\uff0c\u5411\u90a3\u94c1\u70bc\u5241\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u53ea\u542c\u201c\u94ee\u201d\u7684\u4e00\u58f0\uff0c\u706b\u661f\u56db\u6e85\uff0c\u8fd9\u9ed1\u8863\u523a\u5ba2\u638c\u4e2d\u7684\u5251\u7adf\u88ab\u9707\u5f97\u8131\u624b\u98de\u51fa\uff0c\u94c1\u70bc\u4ecd\u7eb9\u98ce\u4e0d \u52a8\u3002 \u3000\u3000\u9ed1\u8863\u523a\u5ba2\u4e00\u60ca\uff0c\u518d\u60f3\u9000\uff0c\u5df2\u6765\u4e0d\u53ca\u4e86\u3002 \u3000\u3000\u4f46\u89c1\u4eba\u5f71\u4e00\u95ea\uff0c\u4f46\u95fb\u201c\u5580\u201d\u7684\u4e00\u58f0\uff0c\u9c9c\u8840\u65d7\u82b1\u822c\u98de\u6fc0\u800c\u8d77\uff0c\u9ed1\u8863\u523a\u5ba2\u7684\u8eab\u5b50\u5df2\u65ad\u6210\u4e86\u4e24 \u622a\u3002 \u3000\u3000\u90a3\u94c1\u70bc\u8fd8\u662f\u7ef7\u5f97\u7b14\u76f4\uff0c\u53ea\u4e0d\u8fc7\u9752\u8863\u5c3c\u548c\u90a3\u602a\u4eba\u5df2\u6362\u4e86\u4e2a\u8fb9\u800c\u5df2\u3002 \u3000\u3000\u9ed1\u8863\u523a\u5ba2\u4eec\u5927\u9a87\u4e4b\u4e0b\uff0c\u7eb7\u7eb7\u5411\u5f8c\u9000\uff0c\u4f46\u80e1\u94c1\u82b1\u3001\u9ec4\u9c81\u76f4\uff0c\u548c\u6234\u72ec\u884c\u5374\u6b63\u5728\u5f8c\u9762\u7b49\u7740\u4ed6 \u4eec\u3002 \u3000\u3000\u4ed6\u4eec\u957f\u5251\u4e00\u5c55\uff0c\u5206\u6210\u4e94\u4e2a\u65b9\u5411\u95ea\u5165\u6811\u5f8c\u3002 \u3000\u3000\u53ea\u89c1\u4eba\u5f71\u4e00\u95ea\uff0c\u5176\u4e2d\u53c8\u6709\u4e00\u4eba\u88ab\u94c1\u70bc\u7f1a\u5728\u6811\u4e0a\u2026\u2026 \u3000\u3000\u53ea\u4e0d\u8fc7\u5728\u5239\u90a3\u4e4b\u95f4\uff0c\u4ed6\u4eec\u5df2\u6d3b\u6d3b\u7684\u52d2\u6b7b\u4e86\u53c1\u4e2a\u4eba\uff0c\u695a\u7559\u9999\u53d1\u73b0\u8fd9\u53c1\u6b21\u653b\u52bf\uff0c\u90fd\u662f\u90a3\u602a\u4eba \u53d1\u52a8\u7684\u3002 \u3000\u3000\u4ed6\u8eab\u6cd5\u4f3c\u4e4e\u6bd4\u9752\u8863\u5c3c\u66f4\u5feb\uff0c\u695a\u7559\u9999\u5b9e\u5728\u60f3\u770b\u770b\u4ed6\u7a76\u7adf\u662f\u600e\u9ebd\u6837\u7684\u4e00\u4e2a\u4eba\uff0c\u4f46\u90a3\u9ec4\u5e54\u5374\u8fde \u4ed6\u7684\u8db3\u8e1d\u4e5f\u4e00\u8d77\u76d6\u4f4f\u4e86\u3002 \u3000\u3000\u4ed6\u6839\u672c\u4ec0\u9ebd\u4e5f\u77a7\u4e0d\u89c1\uff0c\u4f46\u5374\u4f3c\u6709\u79cd\u8759\u8760\u822c\u7684\u89e6\u89c9\uff0c\u6839\u672c\u4e0d\u5fc5\u7528\u773c\u775b\uff0c\u4e5f\u80fd\u201c\u770b\u201d\u5f97\u89c1\u3002 \u3000\u3000\u695a\u7559\u9999\u77e5\u9053\u552f\u6709\u778e\u5b50\u624d\u4f1a\u6709\u8fd9\u79cd\u5947\u5f02\u7684\u89e6\u89c9\u3002 \u3000\u3000\u4e00\u4e2a\u778e\u5b50\u548c\u4e00\u4e2a\u53c8\u804b\u53c8\u54d1\u7684\u4eba\u914d\u5408\u5728\u4e00\u8d77\uff0c\u7adf\u80fd\u53d1\u6325\u8fd9\u9ebd\u5927\u7684\u5a01\u529b\uff0c\u695a\u7559\u9999\u9664\u4e86\u53ef\u601c\u4ed6 \u4eec\u4e4b\u5916\uff0c\u53c8\u4e0d\u7981\u5f88\u4f69\u670d\u3002 \u3000\u3000\u4f46\u8fd9\u778e\u5b50\u7a76\u7adf\u4e3a\u4e86\u4ec0\u9ebd\u4e8b\u4e0d\u6562\u89c1\u4eba\u5462\uff1f \u3000\u3000\u4ed6\u548c\u90a3\u9752\u8863\u5c3c\u4e4b\u95f4\u7a76\u7adf\u6709\u4ec0\u9ebd\u5173\u7cfb\uff1f\u201c\u6c34\u6bcd\u201d\u9634\u59ec\u7a76\u7adf\u4e3a\u4e86\u4ec0\u9ebd\u624d\u5c06\u8fd9\u4e24\u4e2a\u4eba\u7981\u9522\u5728\u4e00 \u8d77\uff1f \u3000\u3000\u8fd9\u65f6\u9ed1\u8863\u523a\u5ba2\u53ea\u5269\u4e0b\u4e94\u4e2a\u4eba\u4e86\uff0c\u8fd9\u4e94\u4eba\u4f3c\u5df2\u4e0d\u6562\u518d\u51fa\u624b\uff0c\u53ea\u662f\u5728\u6811\u5e72\u4e4b\u95f4\u6765\u53bb\uff0c\u4f46\u4ed6\u4eec \u4e5f\u4e0d\u6562\u9000\u8d70\u3002 \u3000\u3000\u90a3\u53ea\u201c\u624b\u201d\u91cc\u663e\u7136\u8fd8\u63e1\u7740\u6839\u97ad\u5b50\uff0c\u4ed6\u4eec\u82e5\u662f\u6ca1\u6709\u8fbe\u6210\u4efb\u52a1\u5c31\u9000\u8d70\uff0c\u6240\u906d\u53d7\u7684\u5fc5\u5b9a\u66f4\u60e8\u3002 \u3000\u3000\u4ed6\u4eec\u7684\u5251\u4e0b\u867d\u7136\u4e0d\u77e5\u6740\u8fc7\u591a\u5c11\u4eba\uff0c\u4f46\u4ed6\u4eec\u81ea\u5df1\u7684\u547d\u8fd0\uff0c\u4e5f\u8bb8\u6bd4\u4ed6\u4eec\u6240\u6740\u6b7b\u7684\u4eba\u66f4\u60b2\u60e8\u3002 \u3000\u3000\u695a\u7559\u9999\u53f9\u4e86\u53e3\u6c14\uff0c\u7eb5\u8eab\u63a0\u4e86\u8fc7\u53bb\uff0c\u53ea\u89c1\u4e00\u4e2a\u9ed1\u8863\u523a\u5ba2\u521a\u4ece\u80e1\u94c1\u82b1\u7684\u5200\u5149\u4e0b\u7a9c\u51fa\u6765\uff0c\u9752\u8863 \u5c3c\u548c\u90a3\u602a\u5ba2\u5df2\u5ffd\u7136\u81ea\u4ed6\u8eab\u65c1\u7684\u4e24\u68f5\u6811\u5f8c\u95e8\u51fa\uff0c\u90a3\u81f4\u547d\u7684\u94c1\u70bc\uff0c\u5df2\u627c\u65ad\u4e86\u4ed6\u7684\u53bb\u8def\uff0c\u4e5f\u627c\u65ad\u4e86 \u4ed6\u7684\u751f\u673a\u3002 \u3000\u3000\u9ed1\u8863\u523a\u5ba2\u72c2\u543c\u4e00\u58f0\uff0c\u957f\u5251\u6bd2\u86c7\u822c\u524c\u51fa\uff0c\u4f46\u90a3\u602a\u4eba\u811a\u6b65\u4e00\u6ed1\uff0c\u5df2\u81ea\u5251\u5149\u4e2d\u6ed1\u4e86\u51fa\u53bb\uff0c\u94c1\u70bc \u5df2\u7ed5\u4f4f\u4e86\u4ed6\u7684\u8eab\u5b50\u3002 \u3000\u3000\u773c\u89c1\u4ed6\u54bd\u5589\u53c8\u5c06\u88ab\u627c\u6210\u4e24\u622a\uff0c\u4f46\u5c31\u5728\u7728\u773c\u4e4b\u95f4\uff0c\u695a\u7559\u9999\u7684\u624b\u638c\u5df2\u6293\u4f4f\u4e86\u94c1\u70bc\uff0c\u9053\uff1a\u201c\u4ed6 \u4eec\u4e5f\u662f\u53ef\u601c\u4eba\uff0c\u9976\u4e86\u4ed6\u4e00\u547d\u5427\uff01\u201d \u3000\u3000\u9752\u8863\u5c3c\u77aa\u7740\u695a\u7559\u9999\uff0c\u5f77\u4f5b\u53c8\u60ca\u53c8\u6012\u2014\u2014\u94c1\u70bc\u5df2\u88ab\u695a\u7559\u9999\u6293\u5f97\u7d27\u7d27\u7684\uff0c\u5979\u81ea\u7136\u65e0\u6cd5\u201c\u542c\u201d \u5230\u695a\u7559\u9999\u5728\u8bf4\u4ec0\u9ebd\u3002 \u3000\u3000\u90a3\u9ed1\u8863\u523a\u5ba2\u9762\u4e0a\u867d\u8499\u7740\u5934\u5dfe\uff0c\u4f46\u770b\u5b83\u7684\u773c\u775b\uff0c\u4e5f\u662f\u60ca\u7591\u591a\u65bc\u6050\u60e7\uff0c\u4ed6\u66f4\u731c\u4e0d\u900f\u695a\u7559\u9999\u4e3a \u4f55\u8981\u6551\u4ed6\uff1f \u3000\u3000\u695a\u7559\u9999\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u653e\u5fc3\uff0c\u6211\u4e0d\u4f1a\u903c\u4f60\u8bf4\u4efb\u4f55\u4e8b\u7684\uff0c\u56e0\u4e3a\u6211\u77e5\u9053\u4f60\u5b81\u6b7b\u4e5f\u4e0d\u4f1a\u8bf4\uff0c \u73b0\u5728\u6211\u60f3\u548c\u4f60\u4eec\u505a\u4e2a\u4ea4\u6613\u3002\u201d \u3000\u3000\u9ed1\u8863\u523a\u5ba2\u76ee\u5149\u95ea\u7f29\u7740\u56db\u9762\u671b\u4e86\u4e00\u773c\uff0c\u8fd9\u65f6\u80e1\u94c1\u82b1\u4ed6\u4eec\u5df2\u505c\u4e0b\u624b\u6765\uff0c\u53e6\u56db\u4e2a\u9ed1\u8863\u523a\u5ba2\u867d\u4ecd \u5728\u6e38\u52a8\uff0c\u8eab\u5f62\u4e5f\u5df2\u6e10\u7f13\u3002 \u3000\u3000\u51e0\u4e2a\u4eba\u7684\u773c\u775b\u90fd\u5728\u77aa\u7740\u695a\u7559\u9999\uff0c\u7ec8\u65bc\u6709\u4e00\u4eba\u95ee\u9053\uff1a\u201c\u4ec0\u9ebd\u4ea4\u6613\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u53ea\u8981\u4f60\u4eec\u6562\u8d70\uff0c\u8fd9\u6b21\u5c31\u653e\u4f60\u4eec\u8d70\uff0c\u5e76\u6ca1\u6709\u4efb\u4f55\u6761\u4ef6\u3002\u201d \u3000\u3000\u9ed1\u8863\u523a\u5ba2\u4eec\u5168\u90fd\u6014\u4f4f\u3002 \u3000\u3000\u8fd9\u201c\u4ea4\u6613\u201d\u5b9e\u5728\u592a\u5408\u7b97\uff0c\u4ed6\u4eec\u53cd\u5012\u4e0d\u77e5\u8be5\u5982\u4f55\u662f\u597d\u4e86\u3002 \u3000\u3000\u695a\u7559\u9999\u60a0\u7136\u9053\uff1a\u201c\u5404\u4f4d\u6015\u8981\u4ee5\u4e3a\u5929\u4e0b\u7edd\u6ca1\u6709\u8fd9\u79cd\u4fbf\u5b9c\u7684\uff0c\u662f\u5417\uff1f\u5176\u5b9e\u4f60\u4eec\u8fd9\u6b21\u6765\u4e5f\u5e76\u6ca1 \u6709\u5360\u5230\u4ec0\u9ebd\u4fbf\u5b9c\uff0c\u662f\u5417\uff1f\u201d \u3000\u3000\u4ed6\u62cd\u4e86\u62cd\u9ed1\u8863\u523a\u5ba2\u7684\u80a9\u5934\uff0c\u5fae\u7b11\u9053\uff1a\u201c\u6211\u65e2\u5df2\u7b54\u5e94\u4e86\u4f60\u4eec\uff0c\u4f60\u4eec\u5c31\u53ea\u7ba1\u653e\u5fc3\u8d70\u5427\uff01\u201d \u3000\u3000\u8fd9\u9ed1\u8863\u523a\u5ba2\u5fd6\u4e86\u534a\u664c\uff0c\u7eb5\u8eab\u4e00\u63a0\uff0c\u81ea\u94c1\u70bc\u4e2d\u98de\u8d77\u3002 \u3000\u3000\u695a\u7559\u9999\u53c8\u9053\uff1a\u201c\u4e00\u4e2a\u4eba\u53ea\u8981\u6d3b\u7740\uff0c\u4ee5\u5f8c\u603b\u8fd8\u6709\u673a\u4f1a\uff0c\u6b7b\u4eba\u5c31\u6c38\u8fdc\u6ca1\u6cd5\u5b50\u529e\u4e8b\u4e86\u3002\u201d \u3000\u3000\u4ed6\u4f3c\u4e4e\u5728\u5583\u5583\u81ea\u8bed\uff0c\u4f46\u542c\u4e86\u8fd9\u53e5\u8bdd\uff0c\u9ed1\u8863\u523a\u5ba2\u4eec\u624d\u5ffd\u7136\u4e0b\u5b9a\u51b3\u5fc3\uff0c\u98de\u63a0\u800c\u53bb\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u7acb\u523b\u8df3\u4e86\u8d77\u6765\uff0c\u9053\uff1a\u201c\u8001\u81ed\u866b\uff0c\u4f60\u96be\u9053\u60f3\u505a\u548c\u5c1a\u4e86\u9ebd\uff1f\u4f46\u548c\u5c1a\u4e5f\u4e0d\u4f1a\u50cf\u4f60\u8fd9\u6837\u4e71 \u53d1\u6148\u60b2\u7684\uff0c\u5c45\u7136\u5e73\u767d\u5c31\u5c06\u8fd9\u4e9b\u51f6\u624b\u653e\u8d70\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u53f9\u9053\uff1a\u201c\u8fd9\u4e9b\u4eba\u5e76\u4e0d\u80fd\u7b97\u662f\u51f6\u624b\uff0c\u53ea\u80fd\u7b97\u5080\u5121\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u76b1\u7709\u9053\uff1a\u201c\u5080\u5121\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5080\u5121\uff0c\u4ed6\u4eec\u6bcf\u4e2a\u4eba\u8eab\u4e0a\u90fd\u7cfb\u7740\u6839\u7ef3\u5b50\uff0c\u7ef3\u5934\u5c31\u5728\u90a3\u53ea\u201d\u624b\u201d\u4e0a\uff0c\u4f60 \u5c31\u7b97\u5c06\u4ed6\u4eec\u5168\u6740\u6b7b\u4e86\u4e5f\u6ca1\u6709\u7528\uff0c\u90a3\u53ea\u201c\u624b\u201d\u5f88\u5feb\u5c31\u4f1a\u518d\u627e\u5341\u53c1\u4e2a\u5080\u5121\u6765\u6740\u4eba\u7684\uff0c\u800c\u4e14\u8fd9\u6b21\u4f60 \u6740\u4e86\u4ed6\u5341\u53c1\u4e2a\uff0c\u4e0b\u6b21\u4ed6\u8bf4\u4e0d\u5b9a\u5c31\u4f1a\u627e\u4e8c\u5341\u516d\u4e2a\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u6478\u4e86\u6478\u9f3b\u5b50\uff0c\u9053\uff1a\u201c\u4f46\u2026\u2026\u2026\u4f46\u4f60\u5c31\u8fd9\u6837\u5c06\u4ed6\u4eec\u653e\u4e86\uff0c\u603b\u4e0d\u662f\u751f\u610f\u7ecf\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u5c31\u4e0d\u61c2\u4e86\uff0c\u505a\u751f\u610f\u8bb2\u7a76\u7684\u5c31\u662f\u653e\u957f\u7ebf\uff0c\u9493\u5927\u9c7c\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u773c\u775b\u4e00\u4eae\uff0c\u9053\uff1a\u201c\u6211\u660e\u767d\u4e86\uff0c\u4f60\u653e\u4ed6\u4eec\u8d70\uff0c\u5c31\u662f\u4e3a\u4e86\u8981\u4ed6\u4eec\u5e26\u4f60\u53bb\u627e\u90a3\u53ea\u201d \u624b\u201d\uff0c\u53ef\u662f\uff0c\u4f60\u7684\u201c\u7ebf\u201d\u53c8\u5728\u90a3\u91cc\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4f60\u7684\u9f3b\u5b50\u6bd4\u6211\u7075\uff0c\u96be\u9053\u8fd8\u6ca1\u6709\u55c5\u51fa\u6765\u9ebd\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u95ed\u8d77\u773c\u775b\u957f\u957f\u5438\u4e86\u53e3\u6c14\uff0c\u53ea\u89c9\u5fae\u98ce\u4e2d\u7f25\u7f08\u4f20\u6765\u4e00\u9635\u9635\u6de1\u6de1\u7684\u201c\u90c1\u91d1\u9999\u201d\u7684\u5e7d\u9999\u3002 \u3000\u3000\u8fd9\u6b63\u662f\u695a\u9999\u5e05\u72ec\u6709\u7684\u9999\u6c14\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u5931\u7b11\u9053\uff1a\u201c\u539f\u6765\u4f60\u8fd9\u8001\u81ed\u866b\u65b9\u4f38\u624b\u5728\u4eba\u5bb6\u80a9\u4e0a\u4e00\u62cd\uff0c\u5df2\u5c06\u81ed\u6c14\u67d3\u5230\u4ed6\u8eab\u4e0a\u53bb \u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u73b0\u5728\u53ea\u8981\u505a\u4e00\u6b21\u9010\u81ed\u4e4b\u592b\uff0c\u5c31\u53ef\u4ee5\u8ffd\u5230\u90a3\u6761\u5927\u9c7c\u3002\u201d \u3000\u3000\u4ed6\u8bdd\u521a\u8bf4\u5b8c\uff0c\u53ea\u542c\u94c1\u70bc\u201c\u53ee\u201d\u7684\u4e00\u54cd\uff0c\u9752\u8863\u5c3c\u548c\u90a3\u602a\u4eba\u5df2\u98de\u4e00\u822c\u63a0\u4e86\u51fa\u53bb\uff0c\u695a\u7559\u9999\u975e\u4f46 \u6ca1\u6709\u62e6\u963b\uff0c\u76ee\u4e2d\u53cd\u800c\u9732\u51fa\u6b23\u6170\u4e4b\u8272\uff0c\u6c88\u58f0\u9053\uff1a\u201c\u4f60\u548c\u9ec4\u8001\u5148\u751f\uff0c\u6234\u8001\u524d\u8f88\u7559\u5728\u8fd9\u91cc\u7167\u987e\uff0c \u6211\u2026\u2026\u201d \u3000\u3000\u80e1\u94c1\u82b1\u5927\u53eb\u9053\uff1a\u201c\u4e0d\u884c\uff0c\u8fd9\u6b21\u8bf4\u4ec0\u9ebd\u6211\u90fd\u975e\u53bb\u4e0d\u53ef\u3002\u201d \u3000\u3000\u4e00\u53e5\u8bdd\u672b\u8bf4\u5b8c\uff0c\u4ed6\u7684\u4eba\u5df2\u8fdc\u5728\u6570\u4e08\u5916\u3002 \u3000\u3000\u695a\u7559\u9999\u5f97\u5411\u9ec4\u9c81\u76f4\u548c\u6234\u72ec\u884c\u62b1\u4e86\u62b1\u62f3\uff0c\u53c8\u6307\u4e86\u6307\u83e9\u63d0\u5eb5\u7684\u95e8\uff0c\u9053\uff1a\u201c\u8fd9\u91cc\u7684\u4e8b\uff0c\u5c31\u504f\u52b3 \u4e24\u4f4d\u524d\u8f88\u591a\u8d39\u795e\u4e86\uff0c\u8fd8\u6709\u84c9\u513f\uff0c\u5979\u82e5\u6765\u4e86\u2026\u2026\u201d \u3000\u3000\u6234\u72ec\u884c\u7b11\u9053\uff1a\u201c\u4f60\u53ea\u7ba1\u653e\u5fc3\u53bb\u5427\uff0c\u82cf\u59d1\u5a18\u6765\u65f6\uff0c\u6211\u4e5f\u4f1a\u544a\u8bc9\u5979\u7684\u3002\u201d \u3000\u3000\u7b49\u695a\u7559\u9999\u8d70\u5f8c\uff0c\u4ed6\u624d\u53f9\u4e86\u53e3\u6c14\uff0c\u82e6\u7b11\u7740\u5411\u9ec4\u9c81\u76f4\u9053\uff1a\u201c\u5982\u6b64\u770b\u6765\uff0c\u8fd8\u662f\u6211\u4eec\u4e24\u4e2a\u8001\u5934\u5b50 \u8f7b\u677e\u81ea\u5728\u3002\u201d \u3000\u3000\u9ec4\u9c81\u76f4\u4e5f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4e00\u4e2a\u7537\u4eba\u8eab\u4e0a\u82e5\u80cc\u4e86\u4e2a\u5305\u88b1\uff0c\u5df2\u662f\u4ef6\u82e6\u4e8b\uff0c\u4f55\u51b5\u4ed6\u8eab \u4e0a\u7684\u5305\u88b1\u7adf\u6709\u53c1\u4e2a\u4e4b\u591a\u5462\uff01\u201d \u3000\u3000\u6234\u72ec\u884c\u5374\u53c8\u7b11\u4e86\uff0c\u9053\uff1a\u201c\u5728\u6211\u4eec\u8001\u5934\u5b50\u770b\u6765\uff0c\u8fd9\u56fa\u7136\u662f\u4ef6\u82e6\u5dee\u4e8b\uff0c\u4f46\u5728\u90a3\u4e9b\u5c0f\u4f19\u5b50\u7684\u773c \u4e2d\u770b\u6765\uff0c\u4e5f\u8bb8\u7fa1\u6155\u8fd8\u6765\u4e0d\u53ca\u54e9\uff01\u201d \u3000\u3000\u695a\u7559\u9999\u6ca1\u6709\u591a\u4e45\u5c31\u8ffd\u4e0a\u4e86\u80e1\u94c1\u82b1\uff0c\u53ea\u89c1\u80e1\u94c1\u82b1\u8fdc\u8fdc\u8ddf\u7740\u9752\u8863\u5c3c\u548c\u90a3\u602a\u4eba\uff0c\u770b\u6765\u4f3c\u4e4e\u6709\u4e9b \u5fc3\u795e\u4e0d\u5b9a\u3002 \u3000\u3000\u4ed6\u89c1\u5230\u695a\u7559\u9999\u8d76\u6765\u4e86\uff0c\u5ffd\u7136\u9053\uff1a\u201c\u770b\u6765\u6211\u4eec\u4ee5\u5f8c\u5e94\u8be5\u517b\u6761\u72d7\u624d\u662f\u3002\u201d \u3000\u3000\u695a\u56fd\u9999\u9053\uff1a\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u73b0\u5728\u6211\u4eec\u82e5\u662f\u6709\u6761\u72d7\uff0c\u5c31\u4e00\u5b9a\u4e0d\u4f1a\u8ffd\u9519\u65b9\u5411\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u671b\u7740\u524d\u9762\u4e24\u4e2a\u4eba\u9053\uff1a\u201c\u4ed6\u4eec\u4e5f\u7edd\u4e0d\u4f1a\u8ffd\u9519\u65b9\u5411\u7684\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u4e0d\u89c1\u5f97\u5427\uff0c\u73b0\u5728\u6211\u5df2\u55c5\u4e0d\u5230\u4f60\u90a3\u81ed\u6c14\u4e86\uff0c\u4ed6\u4eec\u2026\u2026\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u8fd9\u602a\u4f60\u7684\u9f3b\u5b50\u4e0d\u7075\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u6211\u7684\u9f3b\u5b50\u867d\u6bd4\u4e0d\u4e0a\u72d7\uff0c\u4f46\u6bd4\u4f60\u603b\u5f3a\u4e9b\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u9053\uff1a\u201c\u4f9d\u6211\u770b\u6765\uff0c\u4f60\u7684\u9f3b\u5b50\u548c\u72d7\u9f3b\u4e8e\u4e5f\u5dee\u4e0d\u591a\u4e86\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u77aa\u773c\u9053\uff1a\u201c\u6211\u7684\u9f3b\u5b50\u82e5\u771f\u662f\u72d7\u9f3b\u5b50\uff0c\u90a3\u9ebd\u6211\u5df2\u55c5\u4e0d\u5230\u4e86\uff0c\u4ed6\u4eec\u600e\u9ebd\u80fd\u55c5\u5f97\u5230\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u6211\u7684\u773c\u775b\u548c\u8033\u6735\u662f\u4e0d\u662f\u7279\u522b\u7075\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u54fc\uff01\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\u90a3\u662f\u4e3a\u4e86\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u4e5f\u8bb8\u56e0\u4e3a\u4f60\u662f\u5c5e\u5154\u5b50\u7684\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4f60\u7528\u4e0d\u7740\u773c\u7ea2\uff0c\u90a3\u53ea\u662f\u56e0\u4e3a\u6211\u7684\u9f3b\u5b50\u592a\u4e0d\u7ba1\u7528\uff0c\u6240\u4ee5\u8001\u5929\u7279\u522b\u7ed9\u6211\u7684\u8865 \u507f\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u773c\u775b\u4e00\u4eae\uff0c\u9053\uff1a\u201c\u4f60\u7684\u610f\u601d\u662f\u8bf4\uff0c\u5c31\u56e0\u4e3a\u4ed6\u4eec\u7684\u773c\u775b\u548c\u8033\u6735\u90fd\u4e0d\u884c\uff0c\u6240\u4ee5\u9f3b\u5b50\u7279 \u522b\u7075\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u4f60\u603b\u7b97\u660e\u767d\u4e86\uff0c\u5012\u771f\u4e0d\u5bb9\u6613\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u773c\u775b\u4e00\u8f6c\uff0c\u7b11\u9053\uff1a\u201c\u5c31\u56e0\u4e3a\u6211\u8111\u7b4b\u8fdf\u949d\uff0c\u6240\u4ee5\u8001\u5929\u4e5f\u7ed9\u4e86\u6211\u7279\u522b\u7684\u8865\u507f\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u54e6\uff1a\u4ec0\u9ebd\u8865\u507f\uff1f\u6211\u5012\u771f\u8fd8\u6ca1\u6709\u770b\u51fa\u6765\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u5927\u7b11\u9053\uff1a\u201c\u4f60\u82e5\u770b\u5f97\u51fa\u6765\uff0c\u90a3\u5c31\u7cdf\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u5927\u7b11\u9053\uff1a\u201c\u4f60\u5c11\u5f97\u610f\uff0c\u4f9d\u6211\u770b\uff0c\u4f60\u90a3\u4ef6\u4e8b\u4e5f\u4e0d\u89c1\u5f97\u2026\u2026\u201d \u3000\u3000\u4ed6\u8bed\u58f0\u9aa4\u7136\u987f\u4f4f\uff0c\u8138\u8272\u4e5f\u9aa4\u7136\u53d8\u4e86\u3002 \u3000\u3000\u524d\u9762\u7684\u5bc6\u6797\u4e2d\uff0c\u5ffd\u7136\u4f20\u51fa\u4e86\u4e00\u58f0\u60e8\u547c\u3002 \u3000\u3000\u547c\u58f0\u51c4\u5389\uff0c\u4ed4\u7ec6\u4e00\u542c\uff0c\u7adf\u662f\u4e94\u4e2a\u4eba\u53d1\u51fa\u6765\u7684\uff0c\u800c\u4e14\u5e76\u975e\u540c\u65f6\u53d1\u51fa\uff0c\u53ea\u4e0d\u8fc7\u4e94\u4eba\u53d1\u51fa\u60e8\u547c \u65f6\u867d\u6709\u5148\u5f8c\uff0c\u76f8\u5dee\u5374\u6781\u5fae\uff0c\u662f\u4ee5\u542c\u6765\u5b9b\u5982\u4e00\u58f0\uff0c\u800c\u4e14\u5341\u5206\u77ed\u4fc3\uff0c\u663e\u7136\u4ed6\u4eec\u60e8\u58f0\u521a\u53d1\u51fa\uff0c\u4fbf\u5df2 \u6c14\u7edd\u3002 \u3000\u3000\u9752\u8863\u5c3c\u548c\u90a3\u602a\u4eba\u5df2\u62a2\u5165\u5bc6\u6797\u3002 \u3000\u3000\u53ea\u89c1\u4e94\u4e2a\u9ed1\u8863\u523a\u5ba2\u5df2\u6a2a\u5c38\u5c31\u5730\uff0c\u5589\u5499\u95ee\u7684\u9c9c\u8840\u4ecd\u5728\u5411\u5916\u6d8c\uff0c\u4e00\u4e2a\u53c8\u7626\u53c8\u957f\u7684\u9ed1\u8863\u4eba\uff0c\u6b63 \u4fef\u671b\u7740\u4ed6\u4eec\u54bd\u5589\u95ee\u7684\u8840\u82b1\uff0c\u76ee\u4e2d\u5e26\u7740\u5f88\u6ee1\u610f\u3001\u5f88\u6fc0\u8d4f\u7684\u795e\u8272\uff0c\u5c31\u50cf\u662f\u4e00\u4e2a\u753b\u5bb6\u6b63\u5728\u6b23\u8d3a\u81ea\u5df1 \u521a\u5b8c\u6210\u7684\u6770\u4f5c\u3002 \u3000\u3000\u4ed6\u7a7f\u7740\u4ef6\u957f\u53ef\u53ca\u5730\u7684\u9ed1\u888d\uff0c\u8138\u4e0a\u6234\u7740\u4e2a\u7d2b\u6a80\u6728\u96d5\u6210\u7684\u9762\u5177\uff0c\u53ea\u9732\u51fa\u4e00\u53cc\u51e0\u4e4e\u5b8c\u5168\u662f\u6b7b\u7070 \u8272\u7684\u773c\u775b\u3002 \u7b2c\u4e09\u5341\u4e94\u7ae0\u3000\u77e5\u5df1\u77e5\u5f7c>> \u53e4\u9f99\u300a\u695a\u7559\u9999\u7cfb\u5217\xb7\u753b\u7709\u9e1f\u300b \u7b2c\u4e09\u5341\u4e94\u7ae0\u3000\u77e5\u5df1\u77e5\u5f7c \u3000\u3000\u9762\u5177\u663e\u7136\u662f\u9ad8\u624b\u96d5\u6210\u7684\uff0c\u4e94\u5b98\u6829\u6829\u5982\u751f\uff0c\u5634\u89d2\u5f77\u4f5b\u8fd8\u5e26\u7740\u4e00\u4e1d\u7b11\u5bb9\uff0c\u51e0\u4e4e\u8fde\u4e00\u6839\u6839\u7709\u6bdb \u90fd\u6570\u5f97\u51fa\uff0c\u4f46\u989c\u8272\u5374\u662f\u7ea2\u4e2d\u9732\u7d2b\uff0c\u7d2b\u91cc\u53d1\u80b2\uff0c\u518d\u52a0\u4e0a\u90a3\u53cc\u6b7b\u7070\u8272\u7684\u773c\u775b\uff0c\u770b\u6765\u66f4\u662f\u8bf4\u4e0d\u51fa\u7684 \u8be1\u79d8\u53ef\u6016\u3002 \u3000\u3000\u4ed6\u624b\u91cc\u63d0\u7740\u67c4\u72ed\u957f\u7684\u5251\uff0c\u5251\u5c16\u8fd8\u5728\u6ef4\u7740\u9c9c\u8840\u3002 \u3000\u3000\u90a3\u4e94\u4e2a\u9ed1\u8863\u523a\u5ba2\u5251\u6cd5\u90fd\u4e0d\u5f31\uff0c\u8f7b\u529f\u4e5f\u5f88\u9ad8\uff0c\u4f46\u7adf\u5728\u4e00\u5239\u90a3\u4e4b\u95f4\uff0c\u5c31\u5168\u90e8\u906d\u4e86\u8fd9\u4eba\u7684\u6bd2 \u624b\u3002 \u3000\u3000\u8fd9\u4eba\u624b\u6bb5\u4e4b\u8fa3\uff0c\u5251\u6cd5\u4e4b\u5feb\uff0c\u5b9e\u662f\u9a87\u4eba\u542c\u95fb\u3002 \u3000\u3000\u9752\u8863\u5c3c\u76ee\u4e2d\u9732\u51fa\u6124\u6012\u4e4b\u8272\uff0c\u548c\u90a3\u602a\u4eba\u5de6\u53f3\u5305\u6284\u8fc7\u53bb\u3002 \u3000\u3000\u9ed1\u888d\u5ba2\u4f3c\u4e4e\u5168\u672b\u89c9\u5bdf\uff0c\u8fde\u773c\u76ae\u90fd\u672b\u62ac\u8d77\u3002 \u3000\u3000\u9752\u8863\u5c3c\u548c\u90a3\u602a\u4eba\u95ea\u7535\u7684\u6284\u5411\u4ed6\u8eab\u5f8c\uff0c\u94c1\u70bc\u5df2\u7ed5\u4f4f\u4e86\u4ed6\u524d\u80f8\uff0c\u4e24\u4eba\u8eab\u5f62\u53ea\u8981\u4e00\u9519\uff0c\u4ed6\u8eab\u4e8e \u5c31\u8981\u65ad\u6210\u4e24\u622a\u3002 \u3000\u3000\u8c01\u77e5\u5c31\u5728\u4ed6\u4eec\u8eab\u5f62\u4ea4\u9519\u7684\u5239\u90a3\u4e4b\u95f4\uff0c\u9ed1\u888d\u5ba2\u638c\u4e2d\u7684\u5251\u5ffd\u7136\u6bd2\u86c7\u822c\u53cd\u624b\u81ea\u4e60\u4e0b\u524c\u51fa\uff0c \u201c\u54e7\u201d\u7684\u5229\u5165\u4e86\u9ec4\u5e54\u3002 \u3000\u3000\u957f\u5251\u62d4\u51fa\u65f6\uff0c\u9c9c\u8840\u4e5f\u968f\u7740\u7bad\u4e00\u822c\u5c04\u4e86\u51fa\u6765\u3002 \u3000\u3000\u9ed1\u888d\u5ba2\u6839\u672c\u6ca1\u6709\u56de\u5934\u770b\u4e00\u773c\uff0c\u4f3c\u4e4e\u65e9\u5df2\u7b97\u51c6\u4e86\u8fd9\u4e00\u5251\u7edd\u4e0d\u4f1a\u843d\u7a7a\u3002 \u3000\u3000\u8fd9\u4e00\u5251\u5176\u5b9e\u5e76\u6ca1\u6709\u4ec0\u9ebd\u795e\u5947\u4e4b\u5904\uff0c\u4f46\u4ed6\u51fa\u624b\u5b9e\u5728\u592a\u5feb\uff0c\u65f6\u95f4\u5b9e\u5728\u7b97\u5f97\u592a\u51c6\uff0c\u51fa\u624b\u7684\u90e8\u4f4d \u66f4\u5927\u51fa\u5bf9\u65b9\u610f\u5916\u3002 \u3000\u3000\u770b\u6765\u8fd9\u7b80\u76f4\u4e0d\u662f\u5251\u5728\u523a\u4eba\uff0c\u800c\u50cf\u662f\u81ea\u5df1\u5f80\u5251\u5c16\u4e0a\u9001\u8fc7\u53bb\u4e00\u822c\uff0c\u6700\u5999\u7684\u662f\uff0c\u8fd9\u67c4\u5251\u524c\u7530\u65f6 \u82e5\u6709\u4e1d\u6beb\u504f\u5dee\uff0c\u82e5\u662f\u6162\u4e86\u534a\u6b65\uff0c\u56fa\u7136\u4e0d\u53ef\u80fd\u5f97\u624b\uff0c\u8fd9\u67c4\u5251\u524c\u5c71\u65f6\u82e5\u662f\u5feb\u4e86\u534a\u6b65\uff0c\u4e5f\u662f\u4e07\u4e07\u65e0 \u6cd5\u5f97\u624b\u7684\u3002 \u3000\u3000\u4ed6\u7b97\u51c6\u4e86\u5bf9\u65b9\u4e24\u4eba\u8eab\u5f62\u4ea4\u9519\u65f6\uff0c\u624d\u662f\u4ed6\u4eec\u9632\u5b88\u6700\u758f\u5ffd\u7684\u4e00\u5239\u90a3\uff0c\u53ea\u56e0\u4ed6\u4eec\u773c\u89c1\u81ea\u5df1\u5373\u5c06 \u5f97\u624b\uff0c\u6b22\u559c\u4e4b\u5fc3\u4e00\u751f\uff0c\u8b66\u6212\u4e4b\u5fc3\u5c31\u5f31\u4e86\u3002 \u3000\u3000\u4f55\u51b5\u4ed6\u4eec\u4e24\u4eba\u8054\u624b\uff0c\u4e2d\u95f4\u53c8\u6709\u94c1\u70bc\u76f8\u8fde\uff0c\u53ef\u8bf4\u6d51\u5982\u4e00\u4f53\uff0c\u8fd9\u4e00\u5251\u65e0\u8bba\u5411\u8c01\u524c\u51fa\uff0c\u53e6\u4e00\u4eba \u90fd\u53ef\u51fa\u624b\u63f4\u6551\u3002 \u3000\u3000\u53ea\u6709\u5728\u4e24\u4eba\u8eab\u4f53\u4ea4\u9519\u7684\u8fd9\u5239\u90a3\u95f4\uff0c\u9752\u8863\u5c3c\u88ab\u6321\u5728\u90a3\u602a\u4eba\u8eab\u5f8c\uff0c\u9ed1\u888d\u5ba2\u4e00\u5251\u524c\u51fa\uff0c\u5979\u6839\u672c \u770b\u4e0d\u5230\u3002 \u3000\u3000\u8fd9\u6b63\u662f\u4ed6\u4eec\u9632\u5b88\u4e0a\u7684\u552f\u4e00\u5f31\u70b9\uff0c\u4f46\u8981\u770b\u51fa\u8fd9\u5f31\u70b9\u6765\uff0c\u5374\u8c08\u4f55\u5bb9\u6613\uff0c\u4f55\u51b5\u8fd9\u4e00\u523b\u6b63\u5982\u767d\u9a79 \u8fc7\u9699\uff0c\u7728\u773c\u5373\u8fc7\uff0c\u8981\u628a\u63e1\u8fd9\u4e00\u5239\u90a3\u51fa\u624b\uff0c\u66f4\u662f\u96be\u4e0a\u52a0\u96be\u4e86\u3002 \u3000\u3000\u53ea\u89c1\u9ec4\u5e54\u4e00\u9635\u98a4\u52a8\uff0c\u91cc\u9762\u7684\u4eba\u5df2\u5012\u4e0b\u3002 \u3000\u3000\u9752\u8863\u5c3c\u8eab\u5b50\u51b2\u51fa\uff0c\u9aa4\u7136\u56de\u5934\uff0c\u51b7\u6f20\u7684\u9762\u5bb9\u5982\u906d\u96f7\u6b9b\uff0c\u773c\u9f3b\u4e94\u5b98\u90fd\u5df2\u6536\u7f29\u5230\u4e00\u5904\uff0c\u53d1\u4e86\u72c2 \u4f3c\u7684\u6251\u5230\u90a3\u5806\u9ec4\u5e54\u4e0a\uff0c\u7adf\u4ee5\u5df2\u5fd8\u4e86\u90a3\u67c4\u6740\u4eba\u7684\u5251\u8ddd\u79bb\u5979\u5df2\u4e0d\u53ca\u4e00\u5c3a\u3002 \u3000\u3000\u9ed1\u888d\u5ba2\u8f6c\u8eab\u671b\u7740\u5979\uff0c\u76ee\u4e2d\u9732\u51fa\u4e00\u4e1d\u8f7b\u8511\u4e4b\u8272\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u611f\u60c5\u5982\u6b64\u8106\u5f31\uff0c\u6839\u672c\u5c31\u4e0d\u914d \u7ec3\u6b66\u7684\uff0c\u6211\u7d22\u6027\u6210\u5168\u4e86\u4f60\u5427\uff01\u201d \u3000\u3000\u9752\u8863\u5c3c\u6839\u672c\u542c\u4e0d\u5230\uff0c\u957f\u5251\u5df2\u7f13\u7f13\u523a\u4e0b\u3002 \u3000\u3000\u7a81\u542c\u4e00\u4eba\u5927\u559d\u9053\uff1a\u201c\u4f4f\u624b\uff01\u201d \u3000\u3000\u9ed1\u888d\u5ba2\u5c45\u7136\u771f\u505c\u4f4f\u4e86\u624b\uff0c\u5374\u672b\u56de\u5934\uff0c\u53ea\u662f\u6de1\u6de1\u9053\uff1a\u201c\u695a\u9999\u5e05\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u4e5f\u672b\u6251\u4e0a\u6765\uff0c\u53ea\u56e0\u4ed6\u77e5\u9053\u9ed1\u888d\u5ba2\u638c\u4e2d\u7684\u5251\u968f\u65f6\u53ef\u523a\u4e0b\uff0c\u4ed6\u8eab\u6cd5\u518d\u5feb\uff0c\u6251\u8fc7\u53bb\u4e5f\u6765 \u4e0d\u53ca\u4e86\u3002 \u3000\u3000\u4ed6\u8eab\u5f62\u5728\u4e00\u4e08\u5916\u5c31\u505c\u4e0b\uff0c\u76ee\u5149\u707c\u707c\uff0c\u77aa\u7740\u90a3\u53ea\u62ff\u7740\u5251\u7684\u624b\uff0c\u6c88\u58f0\u9053\uff1a\u201c\u5728\u4e0b\u6b63\u662f\u695a\u7559 \u9999\u3002\u201d \u3000\u3000\u9ed1\u888d\u5ba2\u53d1\u51fa\u4e86\u4e00\u58f0\u4e7e\u6da9\u7684\u7b11\uff0c\u9053\uff1a\u201c\u5f88\u597d\uff0c\u6211\u65e9\u5c31\u77e5\u9053\u4f60\u6211\u4e24\u4eba\u7ec8\u6709\u4e00\u65e5\u4f1a\u89c1\u9762\u7684\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u9601\u4e0b\u5c31\u662f\u90a3\u53ea\u624b\uff1f\u201d \u3000\u3000\u9ed1\u888d\u5ba2\u4f3c\u4e4e\u6014\u4e86\u6014\uff0c\u9053\uff1a\u201c\u624b\uff1f\u201d \u3000\u3000\u4f46\u4ed6\u77ac\u5373\u604d\u7136\uff0c\u9634\u68ee\u68ee\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u5c31\u662f\u90a3\u53ea\u624b\uff0c\u4e16\u4e0a\u5927\u591a\u6570\u4eba\u7684\u751f\u6740\u4e4b\u6743\uff0c\u5c31\u64cd \u5728\u6211\u624b\u4e0a\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u4ee5\u773c\u8272\u62e6\u4f4f\u4e86\u80e1\u94c1\u82b1\uff0c\u4e0d\u8ba9\u4ed6\u8f7b\u4e3e\u5984\u52a8\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u8fd8\u662f\u5fcd\u4e0d\u4f4f\u559d\u9053\uff1a\u201c\u4f46\u73b0\u5728\u4f60\u7684\u751f\u6740\u4e4b\u6743\uff0c\u5374\u64cd\u5728\u6211\u4eec\u624b\u4e0a\u3002\u201d \u3000\u3000\u9ed1\u888d\u5ba2\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u4ed6\u51b7\u6f20\u7684\u76ee\u5149\u4e2d\uff0c\u5145\u6ee1\u4e86\u8f7b\u8511\u4e4b\u610f\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u6012\u9053\uff1a\u201c\u4f60\u4e0d\u4fe1\u6211\u4eec\u80fd\u6740\u4f60\uff1f\u201d \u3000\u3000\u9ed1\u888d\u5ba2\u4ece\u5934\u5230\u811a\u5c06\u4ed6\u6253\u91cf\u4e86\u4e00\u904d\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u5c31\u53ea\u4f60\u4eec\u4e24\u4f4d\u9ebd\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u5927\u6012\u9053\uff1a\u201c\u4f60\u8fd8\u5acc\u5c11\u4e0d\u6210\uff1f\u201d \u3000\u3000\u9ed1\u888d\u5ba2\u9053\uff1a\u201c\u4e24\u4f4d\u662f\u60f3\u5355\u6253\u72ec\u6597\uff1f\uff1f\u8fd8\u662f\u60f3\u4e00\u9f50\u52a8\u624b\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u77a7\u4e86\u77a7\u695a\u7559\u9999\u4e00\u773c\uff0c\u5389\u58f0\u9053\uff1a\u201c\u5bf9\u4ed8\u4f60\u8fd9\u79cd\u6076\u5f92\uff0c\u6839\u672c\u4e0d\u5fc5\u8bb2\u4ec0\u9ebd\u6c5f\u6e56\u9053\u4e49\u3002\u201d \u3000\u3000\u9ed1\u888d\u5ba2\u5ffd\u7136\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u53ef\u60dc\u53ef\u60dc\u2026\u2026\u201d \u3000\u3000\u80e1\u94c1\u82b1\u77aa\u773c\u9053\uff1a\u201c\u53ef\u60dc\uff1f\u201d \u3000\u3000\u9ed1\u888d\u5ba2\u9053\uff1a\u201c\u82e5\u662f\u6362\u4e86\u5e73\u65f6\uff0c\u4e24\u4f4d\u5148\u658b\u6212\u53c1\u65e5\uff0c\u5c06\u7cbe\u795e\u4f53\u529b\u90fd\u57f9\u517b\u5230\u6700\u4f73\u72b6\u6001\uff0c\u518d\u9001\u4e24 \u6837\u987a\u624b\u7684\u5175\u5203\u6765\u548c\u6211\u4ea4\u624b\uff0c\u4e5f\u8bb8\u8fd8\u80fd\u63a5\u4e0a\u6211\u4e94\u767e\u62db\uff0c\u4f46\u4eca\u5929\u2026\u2026\u201d \u3000\u3000\u80e1\u94c1\u82b1\u5fcd\u4e0d\u4f4f\u559d\u9053\uff1a\u201c\u4eca\u5929\u53c8\u600e\u6837\uff1f\u201d \u3000\u3000\u9ed1\u888d\u5ba2\u9053\uff1a\u201c\u4eca\u65e5\u4e24\u4f4d\u53cc\u76ee\u5931\u795e\uff0c\u811a\u4e0b\u865a\u6d6e\uff0c\u663e\u7136\u5df2\u5c06\u529b\u6c14\u6d88\u8017\u4e86\u5927\u534a\uff0c\u800c\u4e14\u4e5f\u7761\u7720\u4e0d \u8db3\uff0c\u8179\u5185\u66f4\u7a7a\u865a\uff0c\u5341\u6210\u6b66\u529f\uff0c\u6700\u591a\u4e5f\u4e0d\u8fc7\u53ea\u5269\u4e0b\u56db\u6210\u4e86\u3002\u201d \u3000\u3000\u4ed6\u6447\u4e86\u6447\u5934\uff0c\u53f9\u606f\u7740\u9053\uff1a\u201c\u4e24\u4f4d\u5728\u8fd9\u79cd\u60c5\u51b5\u4e0b\u548c\u6211\u52a8\u624b\uff0c\u5b9e\u5728\u662f\u4e0d\u667a\u4e4b\u4e3e\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u77aa\u7740\u4ed6\uff0c\u5ffd\u7136\u5927\u7b11\u8d77\u6765\uff0c\u9053\uff1a\u201c\u4f60\u60f3\u5413\u6211\u4eec\uff1f\u4f60\u4ee5\u4e3a\u6211\u4eec\u5f88\u5bb3\u6015\uff1f\u201d \u3000\u3000\u9ed1\u888d\u5ba2\u9053\uff1a\u201c\u4e24\u4f4d\u867d\u4e0d\u6015\uff0c\u6211\u5374\u6709\u4e9b\u5931\u671b\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u5931\u671b\uff1f\u201d \u3000\u3000\u9ed1\u888d\u5ba2\u76ee\u5149\u51dd\u6ce8\u7740\u638c\u4e2d\u7684\u5251\u5c16\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u5341\u5e74\u524d\uff0c\u6211\u8fdc\u6e38\u5173\u5916\uff0c\u66fe\u7ecf\u9047\u7740\u4e2a\u65e0\u540d\u5251 \u5ba2\uff0c\u5728\u957f\u767d\u5c71\u5dc5\u7684\u5929\u6c60\u4e4b\u7554\u548c\u6211\u5927\u6218\u4e86\u4e24\u65e5\u4e24\u591c\u2026\u2026\u201d \u3000\u3000\u4ed6\u6b7b\u7070\u8272\u7684\u773c\u775b\u91cc\u5df2\u9732\u51fa\u4e00\u79cd\u70bd\u70ed\u7684\u706b\u7130\uff0c\u6309\u7740\u9053\uff1a\u201c\u90a3\u4e00\u6218\u5b9e\u662f\u75db\u5feb\u6dcb\u6f13\uff0c\u4ee4\u6211\u7ec8\u751f \u96be\u5fd8\uff0c\u53ea\u53ef\u60dc\u90a3\u4e00\u6218\u4e4b\u5f8c\uff0c\u6211\u5c31\u518d\u4e5f\u9047\u4e0d\u7740\u90a3\u822c\u79f0\u5fc3\u5982\u610f\u7684\u5bf9\u624b\u4e86\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u51b7\u7b11\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u4f60\u96be\u9053\u5df2\u662f\u5929\u4e0b\u65e0\u654c\u4e86\u9ebd\uff1f\u201d \u3000\u3000\u9ed1\u888d\u5ba2\u4e5f\u4e0d\u7406\u4ed6\uff0c\u6309\u7740\u53c8\u9053\uff1a\u201c\u5251\u58eb\u800c\u65e0\u5bf9\u624b\uff0c\u5176\u5fc3\u60c5\u4e4b\u5bc2\u5bde\u82e6\u95f7\uff0c\u4e24\u4f4d\u6015\u5f88\u96be\u60f3\u50cf\uff0c \u8fd9\u5341\u5e74\u4ee5\u6765\uff0c\u6211\u65f6\u65f6\u523b\u523b\u90fd\u5bfb\u4e00\u5bf9\u624b\u800c\u4e0d\u53ef\u5f97\u2026\u2026\u201d \u3000\u3000\u4ed6\u76ee\u5149\u5ffd\u7136\u51dd\u6ce8\u5230\u695a\u7559\u9999\u9762\u4e0a\uff0c\u9053\uff1a\u201c\u76f4\u5230\u6211\u542c\u4eba\u8bf4\u8d77\u4f60\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u9601\u4e0b\u83ab\u975e\u6709\u5fc3\u4ee5\u627e\u4e3a\u5bf9\u624b\u9ebd\uff1f\u201d \u3000\u3000\u9ed1\u888d\u5ba2\u9053\uff1a\u201c\u6211\u542c\u5230\u6709\u5173\u4f60\u7684\u4f20\u8bf4\u5df2\u5f88\u4e45\u4e86\uff0c\u672c\u8fd8\u4ee5\u4e3a\u90a3\u53ea\u4e0d\u8fc7\u662f\u6c5f\u6e56\u4e2d\u4eba\u7684\u5938\u5f20\uff0c\u4f46 \u4eca\u65e5\u6211\u89c1\u5230\u4f60\uff0c\u624d\u77e5\u9053\u679c\u7136\u662f\u5929\u751f\u4e0b\u6765\u5c31\u8be5\u5b66\u5f0f\u7684\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u8fc7\u5956\u3002\u201d \u3000\u3000\u9ed1\u888d\u5ba2\u9053\uff1a\u201c\u6211\u7b2c\u4e00\u773c\u770b\u5230\u4f60\uff0c\u5c31\u53d1\u73b0\u4f60\u7684\u667a\u6167\u4e0e\u51b7\u9759\uff0c\u4ff1\u975e\u4ed6\u4eba\u53ef\u6bd4\uff0c\u80fd\u548c\u4f60\u8fd9\u6837\u7684 \u4eba\u5927\u6218\u4e00\u573a\uff0c\u5012\u4e5f\u662f\u4e00\u5927\u5feb\u4e8b\uff0c\u53ea\u53ef\u60dc\u73b0\u5728\u2026\u2026\u201d \u3000\u3000\u695a\u7559\u9999\u5fae\u7b11\u9053\uff1a\u201c\u73b0\u5728\u53c8\u5982\u4f55\uff1f\u201d \u3000\u3000\u9ed1\u888d\u5ba2\u9053\uff1a\u201c\u4ee5\u4f60\u6b64\u523b\u7684\u60c5\u51b5\uff0c\u82e5\u662f\u5355\u72ec\u548c\u6211\u52a8\u624b\uff0c\u4e5f\u8bb8\u8fd8\u80fd\u63a5\u4e0a\u6211\u4e24\u767e\u62db\uff0c\u4f46\u662f\u52a0\u4e0a \u4ed6\uff0c\u6211\u767e\u62db\u4e4b\u5185\u5c31\u53ef\u8981\u4f60\u7684\u547d\u3002\u201d \u3000\u3000\u80e1\u94c1\u82b1\u8df3\u4e86\u8d77\u6765\uff0c\u4eba\u543c\u9053\uff1a\u201c\u6211\u4e00\u4e2a\u4eba\u4e5f\u80fd\u8981\u4f60\u7684\u547d\u3002\u201d \u3000\u3000\u9ed1\u888d\u5ba2\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u7684\u6b66\u529f\uff0c\u5728\u6c5f\u6e56\u4e2d\u4e5f\u53ef\u7b97\u5f97\u4e0a\u662f\u4e00\u6d41\u8eab\u624b\uff0c\u53ef\u662f\u4eca\u65e5\u4f60\u4e24\u4eba\u7cbe\u795e\u4f53 \u529b\u4ff1\u5df2\u5c06\u5d29\u6e83\uff0c\u4e24\u4eba\u8054\u624b\uff0c\u975e\u4f46\u4e0d\u80fd\u6536\u4e92\u52a9\u4e4b\u6548\uff0c\u53cd\u800c\u4f1a\u4ee4\u5f7c\u6b64\u5206\u5fc3\uff0c\u4e0d\u89c1\u5176\u5229\uff0c\u53cd\u89c1\u5176 \u5f0a\u2026\u2026\u201d \u3000\u3000\u80e1\u94c1\u82b1\u5927\u7b11\u9053\uff1a\u201c\u65e0\u8bba\u4f60\u600e\u9ebd\u8bf4\uff0c\u4eca\u5929\u6211\u4eec\u4e5f\u662f\u8981\u4e24\u4e2a\u6253\u4f60\u4e00\u4e2a\u7684\uff0c\u5c31\u7b97\u4f60\u8bf4\u7834\u820c\u5934\uff0c \u4e5f\u4f11\u60f3\u6211\u4e0a\u4f60\u7684\u5f53\u3002\u201d \u3000\u3000\u9ed1\u888d\u5ba2\u53c8\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5343\u91d1\u6613\u5f97\uff0c\u826f\u5c06\u96be\u6c42\uff0c\u695a\u7559\u9999\u5440\u695a\u7559\u9999\uff0c\u6211\u8fd9\u6837\u6740\u4e86\u4f60\uff0c \u5b9e\u5728\u662f\u7cdf\u8e4b\u4e86\u4f60\uff0c\u53ef\u60dc\u53ef\u60dc\uff01\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u9053\uff1a\u201c\u65e2\u662f\u5982\u6b64\uff0c\u9601\u4e0b\u96be\u9053\u4e0d\u80fd\u4e0d\u6740\u6211\u9ebd\uff1f\u201d \u3000\u3000\u9ed1\u888d\u5ba2\u9053\uff1a\u201c\u82e5\u8ba9\u4f60\u8fd9\u79cd\u4eba\u6d3b\u5728\u4e16\u4e0a\uff0c\u6211\u4e5f\u662f\u5bdd\u98df\u96be\u5b89\u2026\u2026\u201d \u3000\u3000\u4ed6\u76ee\u4e2d\u5ffd\u7136\u5c04\u51fa\u4e00\u80a1\u6740\u673a\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u4f46\u4eca\u65e5\u4f60\u4eec\u5982\u80fd\u63a5\u5f97\u4f4f\u6211\u4e00\u767e\u4e94\u5341\u62db\uff0c\u6211\u5c31\u4e0d\u6740 \u4f60\u3002\u201d \u3000\u3000\u4e00\u9635\u98ce\u5439\u8fc7\uff0c\u4ed6\u638c\u4e2d\u5251\u5c16\u5df2\u6311\u8d77\u3002 \u3000\u3000\u6740\u673a\u672c\u6765\u53ea\u5728\u4ed6\u773c\u775b\u91cc\uff0c\u4f46\u4ed6\u5251\u5f0f\u4e00\u8d77\uff0c\u5929\u5730\u95f4\u7acb\u523b\u5145\u6ee1\u4e86\u6740\u6c14\u3002 \u3000\u3000\u201c\u5982\u80fd\u63a5\u5f97\u4f4f\u6211\u4e00\u767e\u4e94\u5341\u62db\uff0c\u6211\u5c31\u4e0d\u6740\u4f60\u3002\u201d \u3000\u3000\u542c\u4e86\u8fd9\u53e5\u8bdd\uff0c\u80e1\u94c1\u82b1\u51e0\u4e4e\u5fcd\u4e0d\u4f4f\u8981\u5927\u7b11\u8d77\u6765\u3002 \u3000\u3000\u4ed6\u4ece\u6765\u4e5f\u672a\u60f3\u5230\u4e16\u4e0a\u6709\u8fd9\u9ebd\u72c2\u5984\u7684\u4eba\u3002 \u3000\u3000\u4f46\u73b0\u5728\uff0c\u4ed6\u7b11\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u4e5f\u4e0d\u77e5\u4e3a\u4e86\u4ec0\u9ebd\uff0c\u4ed6\u4e5f\u5728\u4e0d\u77e5\u4e0d\u89c9\u4e2d\u88ab\u8fd9\u79cd\u6740\u6c14\u6240\u9707\u6151\uff0c\u53ea\u89c9\u5fc3\u4e2d\u7adf\u4e0d\u7531\u81ea\u4e3b\u7684\u6c81\u51fa\u4e86 \u51b7\u6c57\u3002 \u3000\u3000\u5e05\u4e00\u5e06\u7684\u5251\u6c14\u51cc\u5389\uff0c\u5374\u4e5f\u672b\u4ee4\u4ed6\u5982\u6b64\u5fc3\u60ca\uff0c\u53ea\u56e0\u5e05\u4e00\u5e06\u7684\u5251\u6c14\u662f\u6b7b\u7684\uff0c\u53ea\u80fd\u6151\u4eba\u4e4b\u5fc3\uff0c \u4e0d\u80fd\u4f24\u4eba\u4e4b\u8eab\u3002\u800c\u8fd9\u9ed1\u888d\u5ba2\u5374\u5df2\u5c06\u672c\u8eab\u7684\u6740\u673a\u4e0e\u5251\u6c14\u5408\u800c\u4e3a\u4e00\u3002 \u3000\u3000\u8fd9\u6740\u6c14\u7adf\u4f3c\u6d3b\u7684\u3002 \u3000\u3000\u4ed6\u7684\u5251\u867d\u672b\u52a8\uff0c\u4f46\u8fd9\u80a1\u6740\u6c14\u5374\u5df2\u5728\u6d41\u7a9c\u7740\uff0c\u65e0\u5b54\u4e0d\u5165\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u53ea\u89c9\u8fd9\u80a1\u6740\u6c14\u5df2\u7a9c\u5165\u4e86\u4ed6\u7684\u773c\u775b\u3002\u7a9c\u5165\u4e86\u4ed6\u7684\u8033\u6735\uff0c\u7a9c\u5165\u4e86\u4ed6\u7684\u9f3b\u5b54\uff0c\u7a9c\u5165\u4e86\u4ed6 \u7684\u8863\u8896\u2026\u2026 \u3000\u3000\u4ed6\u6574\u4e2a\u4eba\u5f77\u4f5b\u5df2\u8d64\u88f8\u88f8\u7684\u88ab\u8fd9\u80a1\u6740\u6c14\u5305\u56f4\uff0c\u4e0d\u5fc5\u51fa\u624b\uff0c\u5df2\u843d\u4e86\u4e0b\u98ce\uff0c\u4f55\u51b5\u4ed6\u5b9e\u5728\u4e0d\u77e5\u8be5 \u5982\u4f55\u51fa\u624b\u3002 \u3000\u3000\u9ed1\u888d\u5ba2\u7684\u5251\u5c16\u4e0b\u5782\uff0c\u65e2\u975e\u653b\u52bf\uff0c\u4e5f\u975e\u5b88\u52bf\uff0c\u5168\u8eab\u4e0a\u4e0b\uff0c\u53ef\u8bf4\u65e0\u4e00\u5904\u4e0d\u662f\u7a7a\u95e8\u5927\u9732\uff0c\u7834\u7efd \u767e\u51fa\u3002 \u3000\u3000\u5c31\u56e0\u4e3a\u5982\u6b64\uff0c\u662f\u4ee5\u80e1\u94c1\u82b1\u66f4\u4e0d\u77e5\u8be5\u5982\u4f55\u51fa\u624b\uff0c\u53ea\u56e0\u4ed6\u6839\u672c\u65e0\u6cd5\u63e3\u6d4b\u8fd9\u9ed1\u888d\u5ba2\u638c\u4e2d\u5251\u4e0b\u4e00 \u6b65\u7684\u53d8\u5316\u3002 \u3000\u3000\u7a81\u542c\u695a\u7559\u9999\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u53ef\u60dc\u53ef\u60dc\u2026\u2026\u201d \u3000\u3000\u9ed1\u888d\u5ba2\u51b7\u51b7\u7684\u770b\u7740\u4ed6\u3002 \u3000\u3000\u695a\u7559\u9999\u7b11\u9053\uff1a\u201c\u9601\u4e0b\u4e5f\u4ee4\u6211\u5931\u671b\u5f97\u5f88\u3002\u201d \u3000\u3000\u9ed1\u888d\u5ba2\u7ec8\u65bc\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u5931\u671b\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u6211\u672c\u4ee5\u4e3a\u9601\u4e0b\u5251\u6cd5\u5982\u4f55\u9ad8\u660e\uff0c\u73b0\u5728\u4e00\u770b\uff0c\u9601\u4e0b\u7684\u8eab\u6cd5\u5b9e\u5728\u662f\u7834\u7efd\u767e\u51fa\uff0c\u8352 \u5510\u53ef\u7b11\u2026\u2026\u201d \u3000\u3000\u9ed1\u888d\u5ba2\u51b7\u51b7\u9053\uff1a\u201c\u65e2\u662f\u5982\u6b64\uff0c\u4f60\u4e3a\u4f55\u8fd8\u4e0d\u51fa\u624b\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u5728\u4e0b\u5b9e\u5728\u6709\u4e9b\u4e0d\u5fcd\u51fa\u624b\u3002\u201d \u3000\u3000\u9ed1\u888d\u5ba2\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u6015\u662f\u56e0\u4e3a\u6211\u8fd9\u4e00\u62db\u7834\u7efd\u592a\u591a\u4e86\uff0c\u53cd\u800c\u4e0d\u77e5\u8be5\u5982\u4f55\u4e0b\u624b\u5427\uff01\u201d \u3000\u3000\u4ed6\u51b7\u51b7\u63a5\u7740\u9053\uff1a\u201c\u82e5\u662f\u4f60\u5355\u72ec\u548c\u6211\u52a8\u624b\uff0c\u8fd8\u53ef\u51ed\u4f60\u8fd9\u4eba\u7684\u8f7b\u529f\u6765\u8bd5\u63a2\u6211\u7684\u5251\u8def\uff0c\u4f46\u6b64\u523b \u4f60\u5374\u8981\u987e\u5fcc\u4f60\u7684\u540c\u4f34\uff0c\u56e0\u4e3a\u82e5\u4f60\u4e00\u62db\u5931\u624b\uff0c\u6211\u7684\u5251\u5c31\u5df2\u523a\u7a7f\u4ed6\u7684\u54bd\u5589\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u53c8\u4f55\u5c1d\u4e0d\u77e5\u8fd9\u9053\u7406\uff0c\u53ea\u4e0d\u8fc7\u4ed6\u53d1\u73b0\u80e1\u94c1\u82b1\u795e\u8272\u6709\u4e9b\u5931\u5e38\uff0c\u6240\u4ee5\u8981\u60f3\u6cd5\u5b50\u4f7f\u4ed6\u9547\u5b9a \u4e9b\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u8bf4\u8bdd\u5e38\u5e38\u80fd\u4f7f\u4e00\u4e2a\u4eba\u9547\u5b9a\u4e0b\u6765\u3002 \u3000\u3000\u9ed1\u888d\u5ba2\u76ee\u5149\u5982\u7535\uff0c\u51b7\u7b11\u7740\u53c8\u9053\uff1a\u201c\u4f60\u7684\u5fc3\u610f\uff0c\u6211\u4e5f\u77e5\u9053\uff0c\u82e5\u6362\u4e86\u5e73\u65e5\uff0c\u4ed6\u4e5f\u4e0d\u81f3\u5982\u6b64\uff0c \u4f46\u6b64\u523b\u4ed6\u5fc3\u529b\u4ea4\u7601\uff0c\u7cbe\u795e\u8089\u4f53\u90fd\u8106\u5f31\u4e0d\u582a\uff0c\u6240\u4ee5\u624d\u88ab\u6211\u5251\u6c14\u4e58\u865a\u800c\u5165\uff0c\u6b64\u523b\u4ed6\u4f53\u5185\u867d\u65e0\u4f24 \u635f\uff0c\u4f46\u7cbe\u795e\u5df2\u88ab\u6211\u5251\u6c14\u6240\u6467\uff0c\u5df2\u548c\u6b7b\u4eba\u65e0\u5dee\u4e86\u3002\u201d \u3000\u3000\u53ea\u89c1\u80e1\u94c1\u82b1\u773c\u775b\u53d1\u76f4\uff0c\u6ee1\u5934\u51b7\u6c57\uff0c\u638c\u4e2d\u7684\u5200\u4f3c\u4e4e\u5df2\u53d8\u5f97\u91cd\u903e\u5343\u65a4\uff0c\u4ed6\u7eb5\u7136\u7528\u5c3d\u5168\u529b\uff0c\u5374 \u8fde\u5200\u5c16\u4e5f\u4e3e\u4e0d\u8d77\u6765\u3002 \u3000\u3000\u8eab\u7ecf\u767e\u6218\u7684\u80e1\u94c1\u82b1\u600e\u9ebd\u4f1a\u53d8\u6210\u5982\u6b64\u6a21\u6837\u3002 \u3000\u3000\u695a\u7559\u9999\u9aa4\u7136\u89c9\u5f97\u4ed6\u9762\u5bf9\u7684\u4e0d\u662f\u4e00\u4e2a\u4eba\uff0c\u4e00\u67c4\u5251\uff0c\u800c\u662f\u4e00\u56e2\u6df7\u6c8c\u7684\uff0c\u5947\u7279\u7684\uff0c\u5996\u5f02\u7684\u6740 \u6c14\uff01 \u3000\u3000\u8fd9\u56e2\u6740\u6c14\u662f\u4e00\u4e2a\u5947\u4eba\u548c\u4e00\u67c4\u9b54\u5251\u6df7\u5408\u51dd\u7ed3\u6210\u7684\uff0c\u4eba\u548c\u5251\u5df2\u51dd\u4e3a\u4e00\u4f53\uff0c\u51e0\u4e4e\u5df2\u65e0\u575a\u4e0d\u6467\uff0c \u65e0\u61c8\u53ef\u51fb\u3002 \u3000\u3000\u8fd9\u4eba\u5df2\u6210\u4e86\u5251\u4e4b\u9b3c\uff0c\u5251\u5df2\u6210\u4e86\u4eba\u7684\u9b42\u9b44\u3002 \u3000\u3000\u695a\u7559\u9999\u6697\u4e2d\u53f9\u4e86\u53e3\u6c14\uff0c\u4ed6\u77e5\u9053\u4ee5\u81ea\u5df1\u73b0\u5728\u7684\u60c5\u51b5\u6765\u9762\u5bf9\u8fd9\u5251\u4e2d\u4e4b\u9b54\uff0c\u975e\u4f46\u4e0d\u667a\uff0c\u800c\u4e14\u4e0d \u5e78\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u5728\u9965\u997f\u3001\u75b2\u5026\u65f6\uff0c\u8089\u4f53\u4e0d\u652f\uff0c\u7cbe\u795e\u66f4\u8106\u5f31\uff0c\u5185\u8d3c\u5df2\u5c06\u751f\uff0c\u5916\u8d3c\u81ea\u7136\u66f4\u5bb9\u6613\u4e58\u865a\u800c \u5165\u3002 \u3000\u3000\u548c\u6c34\u6bcd\u90a3\u4e00\u6218\u5df2\u51e0\u4e4e\u5c06\u4ed6\u7684\u771f\u529b\u635f\u8017\u6b86\u5c3d\uff0c\u6b64\u523b\u4ed6\u5b9e\u5728\u5df2\u65e0\u529b\u51fb\u7834\u8fd9\u56e2\u6740\u6c14\u3002 \u3000\u3000\u9ed1\u888d\u5ba2\u6b7b\u7070\u7684\u773c\u775b\u91cc\uff0c\u5ffd\u7136\u5c04\u51fa\u4e86\u4e00\u80a1\u9752\u5149\uff0c\u6b63\u5982\u706b\u7130\u5df2\u70e7\u6210\u767d\u70ed\uff0c\u695a\u7559\u9999\u7eb5\u7136\u662f\u94a2 \u94c1\uff0c\u4e5f\u96be\u514d\u8981\u88ab\u878d\u5316\u3002 \u3000\u3000\u4ed6\u53ea\u671b\u90a3\u9752\u8863\u5c3c\u9f99\u9aa4\u7136\u594b\u8d77\uff0c\u90a3\u9ebd\u4e24\u4eba\u524d\u5f8c\u5939\u51fb\uff0c\u4e5f\u8bb8\u8fd8\u6709\u80dc\u671b\uff0c\u600e\u5948\u9752\u8863\u5c3c\uff0c\u4e5f\u5df2\u5b8c \u5168\u5d29\u6e83\u4e86\uff0c\u4f0f\u5728\u90a3\u5c38\u8eab\u4e0a\uff0c\u5f77\u4f5b\u8fd0\u7ad9\u90fd\u65e0\u6cd5\u7ad9\u8d77\u3002 \u3000\u3000\u7a81\u7136\u95f4\uff0c\u5251\u5c16\u6311\u8d77\uff0c\u5212\u4e86\u4e2a\u5706\u5f27\u3002 \u3000\u3000\u9ed1\u888d\u5ba2\u51b7\u51b7\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u4f60\u4eec\u6bd4\u6211\u60f3\u50cf\u4e2d\u8fd8\u8981\u4e0d\u6d4e\uff0c\u770b\u6765\u6211\u4e3e\u624b\u95f4\u5df2\u53ef\u5c06\u4f60\u7f6e\u4e4b\u65bc\u6b7b \u5730\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u51dd\u6ce8\u7740\u4ed6\u638c\u4e2d\u7684\u5251\u5c16\uff0c\u6b63\u51c6\u5907\u98de\u8eab\u800c\u8d77\uff0c\u4f46\u9ed1\u888d\u5ba2\u957f\u5251\u7a81\u7136\u5316\u4e3a\u4e00\u7247\u5149\u5e55\u65ad\u7edd\u4e86 \u4ed6\u6240\u6709\u53bb\u8def\u3002 \u3000\u3000\u5251\u5c16\u7834\u98ce\uff0c\u5c16\u9510\u5982\u54e8\u3002 \u3000\u3000\u695a\u7559\u9999\u5c31\u7b97\u80fd\u7834\u4e86\u8fd9\u4e00\u5251\uff0c\u600e\u5948\u6b64\u523b\u5df2\u662f\u529b\u4e0d\u4ece\u5fc3\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u5374\u542c\u4e00\u4eba\u5927\u559d\u9053\uff1a\u201c\u4f4f\u624b\uff01\u201d \u3000\u3000\u559d\u58f0\u54cd\u8d77\uff0c\u547c\u5578\u7684\u5251\u98ce\uff0c\u7a81\u7136\u5bc2\u7edd\uff0c\u90a3\u5996\u86c7\u822c\u7684\u957f\u5251\u4e5f\u9aa4\u7136\u987f\u4f4f\uff0c\u5251\u5c16\u9065\u6307\u7740\u695a\u7559\u9999\u7684 \u7709\u5fc3\u3002 \u3000\u3000\u9ed1\u888d\u5ba2\u51b7\u51b7\u9053\uff1a\u201c\u6211\u53ea\u4e0d\u8fc7\u662f\u60f3\u770b\u770b\u8c01\u8981\u6211\u4f4f\u624b\u800c\u5df2\uff0c\u5e76\u6ca1\u6709\u522b\u7684\u610f\u601d\uff0c\u4f60\u5e94\u8be5\u77e5\u9053\u6211 \u8fd9\u4e00\u5251\u968f\u65f6\u90fd\u53ef\u53d6\u4f60\u7684\u6027\u547d\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u5e76\u6ca1\u6709\u542c\u5230\u5b83\u7684\u8bdd\uff0c\u53ea\u662f\u671b\u7740\u4ed6\u8eab\u5f8c\uff0c\u53ea\u542c\u4ed6\u8eab\u5f8c\u4e00\u4eba\u9053\uff1a\u201c\u4f60\u770b\u4e0d\u5230\u6211\u7684\uff0c\u56e0 \u4e3a\u4f60\u53ea\u8981\u4e00\u52a8\uff0c\u6211\u5c31\u8981\u4f60\u7684\u547d\u3002\u201d \u3000\u3000\u8fd9\u58f0\u97f3\u867d\u7136\u5a07\u8106\u67d4\u7f8e\uff0c\u4f46\u5374\u4e5f\u5e26\u7740\u79cd\u51cc\u5389\u7684\u6740\u6c14\uff0c\u4ee4\u4eba\u4e0d\u5f97\u4e0d\u76f8\u4fe1\u5b83\u7684\u8bdd\uff0c\u4e5f\u4e0d\u6562\u4e0d \u4fe1\u3002 \u3000\u3000\u9ed1\u888d\u5ba2\u77aa\u7740\u695a\u7559\u9999\uff0c\u53ea\u89c1\u695a\u7559\u9999\u8138\u8272\u65e2\u662f\u60ca\u5947\uff0c\u53c8\u662f\u6b22\u559c\uff0c\u5fae\u7b11\u9053\uff1a\u201c\u4f60\u6700\u597d\u76f8\u4fe1\u5979\u7684 \u8bdd\uff0c\u6211\u53ef\u4ee5\u4fdd\u8bc1\u5979\u7edd\u4e0d\u662f\u8bf4\u7b11\u7684\u3002\u201d \u3000\u3000\u9ed1\u888d\u5ba2\u51b7\u7b11\u9053\uff1a\u201c\u6211\u82e5\u4e0d\u4fe1\u5462\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u60a0\u7136\u9053\uff1a\u201c\u4f60\u82e5\u770b\u5230\u5979\u624b\u91cc\u62ff\u7740\u7684\u662f\u4ec0\u9ebd\uff0c\u5c31\u4e0d\u4f1a\u4e0d\u4fe1\u4e86\u3002\u201d \u3000\u3000\u9ed1\u888d\u5ba2\u76ee\u5149\u987f\u65f6\u5df2\u53d8\u6210\u6b7b\u7070\uff0c\u4e00\u5b57\u5b57\u9053\uff1a\u201c\u65e0\u8bba\u5979\u624b\u91cc\u62ff\u7740\u7684\u662f\u4ec0\u9ebd\uff0c\u6211\u8fd8\u662f\u968f\u65f6\u90fd\u53ef \u8981\u4f60\u7684\u547d\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4f60\u4e3a\u4f55\u4e0d\u5148\u770b\u770b\u5979\u624b\u91cc\u62ff\u7740\u7684\u662f\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u8981\u77e5\u9ed1\u888d\u5ba2\u6b64\u523b\u5168\u8eab\u52b2\u6c14\u5168\u90fd\u51dd\u805a\u5728\u5251\u4e0a\uff0c\u53ea\u8981\u4e00\u56de\u5934\uff0c\u5251\u6c14\u4fbf\u677e\u6cc4\uff0c\u695a\u7559\u9999\u5c31\u6709\u4e86\u751f \u673a\u3002 \u3000\u3000\u8c01\u77e5\u9ed1\u888d\u5ba2\u7adf\u4e5f\u770b\u900f\u4e86\u4ed6\u7684\u5fc3\u610f\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u60f3\u8981\u6211\u56de\u5934\uff0c\u6015\u8fd8\u4e0d\u5927\u5bb9\u6613\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4f60\u4e0d\u6562\u56de\u5934\uff1f\u201d \u3000\u3000\u9ed1\u888d\u5ba2\u9053\uff1a\u201c\u6b64\u523b\u4f60\u5168\u8eab\u90fd\u5df2\u5728\u6211\u5251\u6c14\u7b3c\u7f69\u4e4b\u4e0b\uff0c\u5df2\u5982\u74ee\u4e2d\u4e4b\u9cd6\uff0c\u7f51\u4e2d\u4e4b\u9c7c\uff0c\u6211\u82e5\u4e0d\u56de \u5934\uff0c\u4f60\u5c31\u6c38\u65e0\u751f\u673a\uff0c\u7eb5\u7136\u5979\u638c\u4e2d\u4e8c\u5341\u4e03\u6538\u66b4\u96e8\u68a8\u82b1\u9489\u5168\u90fd\u6253\u5728\u6211\u8eab\u4e0a\uff0c\u6211\u8fd9\u4e00\u5251\u8fd8\u662f\u53ef\u4ee5\u7f6e \u4f60\u65bc\u6b7b\u5730\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u539f\u6765\u4f60\u5df2\u731c\u51fa\u5979\u624b\u91cc\u62ff\u7740\u7684\u662f\u4ec0\u9ebd\u4e86\u3002\u201d \u3000\u3000\u9ed1\u888d\u5ba2\u51b7\u7b11\u9053\uff1a\u201c\u5979\u624b\u91cc\u82e5\u65e0\u66b4\u96e8\u68a8\u82b1\u9489\uff0c\u53c8\u600e\u6562\u5982\u6b64\u5927\u8a00\u8981\u80c1\u65bc\u6211\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u5ffd\u7136\u7b11\u4e86\u7b11\u9053\uff1a\u201c\u4f46\u5979\u624b\u4e0a\u82e5\u662f\u7a7a\u7684\uff0c\u53ea\u4e0d\u8fc7\u662f\u5413\u5413\u4f60\u7684\uff0c\u4f60\u8fd9\u5f53\u4e0a\u5f97\u5c82\u975e\u51a4 \u4efb\uff1f\u201d \u3000\u3000\u9ed1\u888d\u5ba2\u8138\u8272\u53d8\u4e86\u53d8\uff0c\u9053\uff1a\u201c\u5979\u624b\u4e0a\u662f\u5426\u7a7a\u7684\uff0c\u6211\u4e0d\u5fc5\u56de\u5934\u770b\u4e5f\u53ef\u77e5\u9053\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u54e6\uff1f\u4f60\u80cc\u5f8c\u4e5f\u6709\u773c\u775b\uff1f\u201d \u3000\u3000\u9ed1\u888d\u5ba2\u5389\u58f0\u9053\uff1a\u201c\u6211\u8fd9\u4e00\u5251\u524c\u51fa\uff0c\u5c31\u53ef\u8bd5\u51fa\u5979\u624b\u4e0a\u662f\u5426\u7a7a\u7684\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u9053\uff1a\u201c\u5979\u624b\u4e0a\u82e5\u662f\u771f\u6709\u66b4\u96e8\u68a8\u82b1\u9489\uff0c\u4f60\u8fd9\u4e00\u5251\u524c\u51fa\uff0c\u5c82\u975e\u5c31\u69fd\u4e86\uff1f\u4e8c\u5341\u4e03\u679a\u66b4 \u96e8\u68a8\u82b1\u9489\u81ea\u4f60\u80cc\u5f8c\u51fb\u51fa\uff0c\u4f60\u80fd\u8eb2\u5f97\u5f00\u5417\uff1f\u201d \u3000\u3000\u9ed1\u888d\u5ba2\u51b7\u51b7\u9053\uff1a\u201c\u80fd\u548c\u695a\u9999\u5e05\u540c\u5f52\u65bc\u5c3d\uff0c\u5012\u4e5f\u5e76\u4e0d\u662f\u4ec0\u9ebd\u592a\u8680\u672c\u7684\u751f\u610f\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u5f88\u597d\uff0c\u4f60\u51fa\u624b\u5427\uff01\u6015\u4f60\u8fd9\u4e00\u5251\u672b\u5fc5\u80fd\u6740\u5f97\u6b7b\u6211\uff0c\u90a3\u65f6\u4f60\u53ef\u5c31\u8680\u4e86 \u5927\u672c\u4e86\u3002\u201d \u3000\u3000\u9ed1\u888d\u5ba2\u8138\u8272\u53c8\u53d8\u4e86\u53d8\uff0c\u9053\uff1a\u201c\u6211\u82e5\u4e0d\u60f3\u51fa\u624b\u5462\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u51fa\u624b\uff0c\u5979\u6015\u4e5f\u4e0d\u4f1a\u51fa\u624b\u7684\uff0c\u4f60\u82e5\u60f3\u8d70\uff0c\u53ea\u7ba1\u8bf7\u4fbf\uff0c\u5e76\u6ca1\u6709\u4eba\u62c9\u4f4f \u4f60\u3002\u201d \u3000\u3000\u9ed1\u888d\u5ba2\u76ee\u5149\u95ea\u52a8\uff0c\u9053\uff1a\u201c\u6211\u600e\u77e5\u5979\u2026\u2026\u201d \u3000\u3000\u695a\u7559\u9999\u622a\u53e3\u9053\uff1a\u201c\u53ea\u8981\u4f60\u8d70\uff0c\u6211\u4fdd\u8bc1\u5979\u51b3\u4e0d\u5411\u4f60\u51fa\u624b\u3002\u201d \u3000\u3000\u9ed1\u888d\u5ba2\u9053\uff1a\u201c\u4f60\u7528\u4ec0\u9ebd\u4fdd\u8bc1\uff1f\u6211\u51ed\u4ec0\u9ebd\u8981\u4fe1\u4efb\u4f60\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u82e5\u4e0d\u4fe1\u4efb\u6211\uff0c\u5c31\u53ea\u6709\u51fa\u624b\uff0c\u4f60\u82e5\u4e0d\u60f3\u51fa\u624b\uff0c\u5c31\u53ea\u6709\u4fe1\u4efb\u6211\uff0c \u8fd9\u5176\u95f4\u96be\u9053\u8fd8\u6709\u4ec0\u9ebd\u9009\u62e9\u7684\u9980\u5730\uff1f\u201d \u3000\u3000\u9ed1\u888d\u5ba2\u76ee\u5149\u707c\u707c\uff0c\u77aa\u4e86\u4ed6\u534a\u664c\uff0c\u5ffd\u7136\u5927\u7b11\u9053\uff1a\u201c\u6211\u82e5\u8fde\u695a\u9999\u5e08\u90fd\u4e0d\u4fe1\u4efb\uff0c\u8fd9\u4e16\u4e0a\u90a3\u91cc\u8fd8 \u6709\u6211\u53ef\u4ee5\u4fe1\u4efb\u7684\u4eba\u2026\u2026\u597d\uff0c\u4eca\u65e5\u5c31\u6b64\u522b\u8fc7\uff0c\u6765\u65e5\u5f8c\u4f1a\u6709\u671f\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4e0b\u6b21\u4f60\u6211\u518d\u89c1\u65f6\uff0c\u4f60\u6700\u597d\u60f3\u6cd5\u5b50\u5728\u80cc\u5f8c\u88c5\u4e0a\u5bf9\u773c\u775b\u3002\u201d \u3000\u3000\u9ed1\u888d\u5ba2\u9053\uff1a\u201c\u53ea\u671b\u9601\u4e0b\u4e5f\u597d\u751f\u4fdd\u91cd\u8eab\u4f53\uff0c\u517b\u7cbe\u84c4\u9510\uff0c\u5728\u8fd9\u53c1\u4e2a\u6708\u91cc\uff0c\u5207\u83ab\u6709\u4ec0\u9ebd\u75c5\u75db\uff1b \u5426\u5219\u5c31\u592a\u4ee4\u6211\u5931\u671b\u4e86\u3002\u201d \u3000\u3000\u4ed6\u5634\u91cc\u8bf4\u7740\u8bdd\uff0c\u4eba\u5df2\u5927\u6b65\u8d70\u4e86\u51fa\u6765\uff0c\u7adf\u59cb\u7ec8\u6ca1\u6709\u56de\u5934\u53bb\u770b\u4e00\u773c\uff0c\u53ea\u89c1\u4ed6\u9ed1\u886b\u968f\u98ce\u98d8\u52a8\uff0c \u7728\u773c\u95f4\u5c31\u8d70\u5f97\u77a7\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u4ed6\u521a\u8d70\uff0c\u672c\u6765\u7ad9\u5728\u4ed6\u8eab\u5f8c\u7684\u82cf\u84c9\u84c9\u7acb\u523b\u5c31\u5012\u4e86\u4e0b\u53bb\uff0c\u5979\u8138\u4e0a\u5df2\u770b\u4e0d\u5230\u4e00\u4e1d\u8840\u8272\uff0c\u51b7\u6c57\u65e9 \u5df2\u6e7f\u900f\u91cd\u8863\u3002 \u3000\u3000\u5979\u7684\u624b\u662f\u7a7a\u7684\uff0c\u90a3\u6709\u4ec0\u9ebd\u66b4\u96e8\u68a8\u82b1\u9489\u3002 \u3000\u3000\u695a\u7559\u9999\u8d76\u8fc7\u53bb\u6276\u4f4f\u4e86\u5979\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u4f60\u6765\u5f97\u6b63\u597d\uff0c\u5b9e\u5728\u592a\u597d\u4e86\u3002\u201d \u3000\u3000\u82cf\u84c9\u84c9\u5634\u5507\u8fd8\u5728\u53d1\u6296\uff0c\u8fde\u8bdd\u90fd\u8bf4\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u695a\u7559\u9999\u7b11\u9053\uff1a\u201c\u5176\u5b9e\u4f60\u7528\u4e0d\u7740\u5bb3\u6015\u7684\u3002\u201d \u3000\u3000\u82cf\u84c9\u84c9\u52c9\u5f3a\u7b11\u9053\uff1a\u201c\u6211\u522b\u7684\u90fd\u4e0d\u6015\uff0c\u5c31\u6015\u4ed6\u56de\u5934\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u56e0\u4e3a\u53ea\u8981\u4f60\u6765\u4e86\uff0c\u624b\u4e0a\u662f\u5426\u6709\u66b4\u96e8\u68a8\u82b1\u9489\u90fd\u5b8c\u5168\u4e00\u6837\u3002\u201d \u3000\u3000\u82cf\u84c9\u84c9\u9053\uff1a\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u9053\uff1a\u201c\u4ed6\u65b9\u5e76\u4e0d\u662f\u5728\u5439\u725b\uff0c\u5c31\u7b97\u4f60\u624b\u4e0a\u6709\u66b4\u96e8\u68a8\u82b1\u9489\uff0c\u53ea\u8981\u4ed6\u6562\u51fa\u624b\uff0c\u8fd8\u662f\u53ef \u4ee5\u6740\u6211\uff0c\u6211\u90a3\u65f6\u7684\u786e\u5df2\u5728\u4ed6\u5251\u6c14\u7b3c\u7f69\u4e4b\u4e0b\uff0c\u4f46\u6211\u4e5f\u7b97\u51c6\u4ed6\u7edd\u4e0d\u6562\u51fa\u624b\uff0c\u4e5f\u4e0d\u6562\u56de\u5934\u7684\uff0c\u56e0\u4e3a \u8fd9\u79cd\u4eba\u4e00\u5b9a\u5c06\u81ea\u5df1\u7684\u6027\u547d\u770b\u5f97\u6bd4\u4ec0\u9ebd\u90fd\u91cd\uff0c\u7edd\u4e0d\u6562\u4ee5\u81ea\u5df1\u7684\u6027\u547d\u4f5c\u8d4c\u6ce8\u3002\u201d \u3000\u3000\u82cf\u84c9\u84c9\u9053\uff1a\u201c\u53ef\u662f\uff0c\u4ed6\u4e3a\u4ec0\u9ebd\u4e0d\u6562\u56de\u5934\u5462\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u9053\uff1a\u201c\u4ed6\u4e0d\u6562\u56de\u5934\uff0c\u5c31\u662f\u6015\u53d1\u73b0\u81ea\u5df1\u4e0a\u5f53\uff0c\u4ed6\u8fd9\u79cd\u4eba\u82e5\u53d1\u73b0\u81ea\u5df1\u4e0a\u4e86\u522b\u4eba\u7684 \u5f53\uff0c\u53ea\u6015\u5c31\u8981\u6c14\u5f97\u53d1\u75af\u3002\u201d \u3000\u3000\u82cf\u84c9\u84c9\u9053\uff1a\u201c\u4ed6\u5148\u56de\u5934\u770b\u770b\u518d\u52a8\u624b\u4e5f\u4e0d\u8fdf\u5440w\u201d\u695a\u7559\u9999\u9053\uff1a\u201c\u4ed6\u53ea\u8981\u56de\u5934\u4e00\u770b\uff0c\u5c31\u65e0\u6cd5 \u52a8\u624b\u4e86\u3002\u201d \u3000\u3000\u82cf\u84c9\u84c9\u9053\uff1a\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4f60\u624b\u4e0a\u82e5\u6709\u66b4\u96e8\u2502\u82b1\u9489\uff0c\u4ed6\u4e00\u56de\u5934\uff0c\u4f60\u5c31\u53ef\u4e58\u673a\u5236\u4ed6\u65bc\u6b7b\u3002\u201d \u3000\u3000\u82cf\u84c9\u84c9\u9053\uff1a\u201c\u53ef\u662f\u6211\u2026\u2026\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4f60\u624b\u4e0a\u662f\u7a7a\u7684\uff0c\u4ed6\u4e00\u56de\u5934\uff0c\u5c31\u4f1a\u53d1\u73b0\u81ea\u5df1\u4e0a\u4e86\u5927\u5f53\uff0c\u518d\u60f3\u5c06\u5251\u6c14\u51dd\u805a\uff0c\u5c31 \u96be\u5982\u767b\u5929\u4e86\u3002\u201d \u7b2c\u4e09\u5341\u516d\u7ae0\u3000\u767e\u6218\u767e\u80dc>> \u53e4\u9f99\u300a\u695a\u7559\u9999\u7cfb\u5217\xb7\u753b\u7709\u9e1f\u300b \u7b2c\u4e09\u5341\u516d\u7ae0\u3000\u767e\u6218\u767e\u80dc \u3000\u3000\u82cf\u84c9\u84c9\u53c8\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u4ed6\u77e5\u9053\u6211\u5df2\u662f\u5f3a\u5f29\u4e4b\u672b\uff0c\u81ea\u4fe1\u6709\u5fc5\u80dc\u7684\u628a\u63e1\uff0c\u6240\u4ee5\u624d\u80fd\u4ee5\u6c14\u52bf\u538b\u5012\u6211\uff0c\u4f46 \u4ed6\u82e5\u53d1\u73b0\u81ea\u5df1\u4e0a\u4e86\u5f53\u5f8c\uff0c\u8fd9\u80a1\u6c14\u5c31\u5f31\u4e86\uff0c\u6211\u7684\u6c14\u52bf\u5c31\u53ef\u4ee5\u538b\u5012\u4ed6\uff0c\u90a3\u65f6\u80dc\u8d1f\u4e4b\u6570\u5c31\u96be\u4ee5\u9884 \u535c\uff0c\u8fd9\u79cd\u4eba\u600e\u80af\u6253\u6ca1\u6709\u628a\u63e1\u7684\u4ed7\uff1f\u662f\u4ee5\u627e\u7b97\u51c6\u4ed6\u5b81\u53ef\u4e00\u8d70\u4e86\u4e4b\uff0c\u4e5f\u4e0d\u613f\u56de\u5934\u7684\u3002\u201d \u3000\u3000\u4ed6\u5fae\u7b11\u7740\u63a5\u9053\uff1a\u201c\u9ad8\u624b\u76f8\u4e89\uff0c\u6b63\u5982\u4e24\u519b\u4ea4\u950b\uff0c\u6c14\u52bf\u4e07\u4e0d\u53ef\u8870\uff0c\u6218\u56fd\u65f6\u9c81\u5927\u5c06\u66f9\u5251\u8bf4\u5f97 \u597d\uff1a\u201c\u592b\u6218\uff0c\u52c7\u6c14\u4e5f\uff0c\u4e00\u9f13\u4f5c\u6c14\uff0c\u518d\u800c\u8870\uff0c\u53c1\u800c\u7aed\uff0c\u5f7c\u7aed\u6211\u76c8\uff0c\u6545\u514b\u4e4b\u3002\u201d\u5c31\u56e0\u4e3a\u4ed6\u660e\u767d\u8fd9 \u9053\u7406\uff0c\u6240\u4ee5\u80fd\u4ee5\u5be1\u51fb\u4f17\uff0c\u6218\u65e0\u4e0d\u80dc\u3002\u201d \u3000\u3000\u82cf\u84c9\u84c9\u5a9a\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5c31\u56e0\u4e3a\u695a\u9999\u5e05\u4f60\u4e5f\u660e\u767d\u8fd9\u9053\u7406\uff0c\u6240\u4ee5\u6bcf\u6b21\u90fd\u80fd\u4ee5\u5f31\u51fb\u5f3a\uff0c\u9022\u51f6 \u5316\u5409\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u7b11\u9053\uff1a\u201c\u8fc7\u5956\u8fc7\u5956\uff0c\u4f46\u82e5\u975e\u4f60\u53ca\u65f6\u8d76\u6765\uff0c\u6211\u8fd8\u662f\u6ca1\u5492\u53ef\u5ff5\u7684\u3002\u201d \u3000\u3000\u82cf\u84c9\u84c9\u9053\uff1a\u201c\u4f46\u4f60\u5b9e\u5728\u4e5f\u771f\u80fd\u6c88\u5f97\u4f4f\u6c14\uff0c\u770b\u5230\u4f60\u65b9\u90a3\u9ebd\u8f7b\u677e\u6109\u5feb\u7684\u6837\u5b50\uff0c\u8fde\u6211\u51e0\u4e4e\u90fd\u8981 \u4ee5\u4e3a\u6211\u624b\u4e0a\u771f\u6709\u66b4\u96e8\u68a8\u82b1\u9489\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u770b\u6211\u5f88\u8f7b\u677e\u6109\u5feb\uff0c\u5176\u5b9e\u6211\u5fc3\u91cc\u53c8\u4f55\u5c1d\u4e0d\u7d27\u5f20\u5f97\u8981\u547d\uff0c\u4ee5\u6211\u4eca \u5929\u7684\u4f53\u529b\u7cbe\u795e\u548c\u4ed6\u4ea4\u624b\uff0c\u5b9e\u5728\u8fde\u4e00\u5206\u628a\u63e1\u90fd\u6ca1\u6709\u3002\u201d \u3000\u3000\u82cf\u84c9\u84c9\u51dd\u6ce8\u7740\u4ed6\uff0c\u76ee\u4e2d\u53c8\u9732\u51fa\u4e00\u4e1d\u5fe7\u90c1\u4e4b\u8272\uff0c\u9053\uff1a\u201c\u4f60\u5e73\u65f6\u548c\u4ed6\u4ea4\u624b\uff0c\u53c8\u80fd\u6709\u51e0\u5206\u628a \u63e1\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9ed8\u7136\u534a\u664c\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u548c\u77f3\u89c2\u97f3\u4ea4\u624b\uff0c\u4e5f\u6ca1\u6709\u4ec0\u9ebd\u628a\u63e1\uff0c\u4f46\u6211\u8fd8\u662f\u6218\u80dc \u4e86\u5979\u3002\u201d \u3000\u3000\u8fd9\u65f6\u9752\u8863\u5c3c\u624d\u7f13\u7f13\u81ea\u90a3\u9ec4\u5e54\u590d\u7740\u7684\u5c38\u8eab\u4e0a\u7ad9\u4e86\u8d77\u6765\uff0c\u695a\u7559\u9999\u4e00\u76f4\u90fd\u5728\u7559\u610f\u7740\u5979\uff0c\u53ea\u4e0d\u8fc7 \u4ed6\u77e5\u9053\u4e00\u4e2a\u5973\u4eba\u5728\u771f\u6b63\u60b2\u75db\u65f6\u7edd\u4e0d\u4f1a\u613f\u610f\u6709\u4eba\u6765\u6253\u6270\uff0c\u662f\u4ee5\u624d\u4e00\u76f4\u6ca1\u6709\u5bf9\u5979\u8bf4\u8bdd\uff0c\u597d\u8ba9\u5979\u5b89 \u5b89\u9759\u9759\u7684\u54ed\u4e2a\u591f\u3002 \u3000\u3000\u5973\u4eba\u5728\u75db\u54ed\u65f6\u82e5\u6709\u4eba\u53bb\u529d\u963b\uff0c\u90a3\u9ebd\u5979\u5c31\u6c38\u8fdc\u4e5f\u54ed\u4e0d\u5b8c\u4e86\u3002 \u3000\u3000\u9752\u8863\u5c3c\u5df2\u6b62\u4f4f\u4e86\u54ed\u58f0\uff0c\u82cd\u767d\u7684\u8138\u770b\u6765\u5df2\u6709\u4e9b\u6d6e\u80bf\uff0c\u5979\u8f6c\u8eab\u9762\u5bf9\u7740\u695a\u7559\u9999\uff0c\u5ffd\u7136\u563f\u58f0\u9053\uff1a \u201c\u6211\u60f3\u6c42\u4f60\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u8bf7\u5429\u5490\u3002\u201d \u3000\u3000\u9752\u8863\u5c3c\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u4eec\u4e00\u5b9a\u90fd\u5f88\u5947\u602a\uff0c\u731c\u4e0d\u51fa\u201d\u4ed6\u201d\u7a76\u7adf\u662f\u8c01\uff1f\u4e3a\u4ec0\u9ebd\u4e00\u76f4\u8eb2\u7740\u4e0d\u613f \u89c1\u4eba\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u6bcf\u4e2a\u4eba\u90fd\u6709\u81ea\u5df1\u7684\u79d8\u5bc6\uff0c\u8c01\u4e5f\u65e0\u6743\u5e72\u6270\u3002\u201d \u3000\u3000\u9752\u8863\u5c3c\u7f13\u7f13\u70b9\u4e86\u70b9\u5934\uff0c\u9053\uff1a\u201c\u73b0\u5728\u6211\u53ea\u6c42\u4f60\uff0c\u6c38\u8fdc\u83ab\u8981\u63a2\u7a76\u8fd9\u79d8\u5bc6\uff0c\u6c38\u8fdc\u83ab\u8981\u63ed\u5f00\u8fd9\u9ec4 \u5e54\uff0c\u6c38\u8fdc\u83ab\u8981\u8ba9\u4efb\u4f55\u4eba\u770b\u5230\u4ed6\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u60f3\u4e5f\u4e0d\u60f3\uff0c\u7acb\u523b\u9053\uff1a\u201c\u5728\u4e0b\u53ef\u4ee5\u4fdd\u8bc1\uff0c\u6211\u7684\u670b\u53cb\u4e2d\u7edd\u6ca1\u6709\u4e00\u4e2a\u559c\u6b22\u7aa5\u4eba\u9690\u79c1\u7684 \u4eba\u3002\u201d \u3000\u3000\u9752\u8863\u5c3c\u957f\u957f\u5410\u51fa\u53e3\u6c14\uff0c\u4ef0\u89c6\u7740\u82cd\u7a79\uff0c\u75f4\u75f4\u7684\u51fa\u4e86\u534a\u664c\u795e\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u4f60\u662f\u4e2a\u541b\u5b50\uff0c\u6211\u53ef \u4ee5\u4fe1\u6258\u4f60\uff0c\u6211\u6b7b\u4e86\u4e4b\u5f8c\uff0c\u5e0c\u671b\u4f60\u7acb\u523b\u5c06\u6211\u4eec\u4e24\u4eba\u706b\u5316\uff0c\u7136\u5f8c\u518d\u628a\u6211\u4eec\u7684\u9aa8\u7070\u6492\u5165\u90a3\u6761\u6d41\u5411\u795e \u6c34\u5bab\u7684\u6eaa\u6c34\u4e2d\u3002\u201d \u3000\u3000\u5979\u5634\u89d2\u5ffd\u7136\u9732\u51fa\u4e00\u4e1d\u5fae\u7b11\uff0c\u6309\u7740\u9053\uff1a\u201c\u8fd9\u6837\uff0c\u6211\u4eec\u6d3b\u7740\u867d\u4e0d\u80fd\u91cd\u56de\u795e\u6c34\u5bab\uff0c\u6b7b\u5f8c\u603b\u80fd\u56de \u53bb\u4e86\u3002\u201d \u3000\u3000\u5979\u51b7\u9177\u3001\u6d6e\u80bf\u3001\u5145\u6ee1\u4e86\u75db\u82e6\u7684\u8138\u4e0a\uff0c\u5c45\u7136\u9732\u51fa\u4e86\u4e00\u4e1d\u5fae\u7b11\uff0c\u8fd9\u7b11\u5bb9\u770b\u6765\u5b9e\u5728\u53c8\u5947\u7279\uff0c\u53c8 \u8be1\u79d8\uff0c\u53c8\u53ef\u6015\u3002 \u3000\u3000\u695a\u7559\u9999\u4e5f\u5fcd\u4e0d\u4f4f\u6253\u4e86\u4e2a\u5bd2\u5664\uff0c\u52a8\u5bb9\u9053\uff1a\u201c\u5927\u5e08\u4f60\u96be\u9053\u60f3\u2026\u2026\u201d \u3000\u3000\u9752\u8863\u5c3c\u6325\u624b\u6253\u65ad\u4e86\u5b83\u7684\u8bdd\uff0c\u9eef\u7136\u9053\uff1a\u201c\u6211\u4e0e\u4f60\u7d20\u6627\u5e73\u751f\uff0c\u521d\u6b21\u76f8\u89c1\u5c31\u5c06\u8fd9\u79cd\u4e8b\u4ea4\u6258\u65bc \u4f60\uff0c\u53ea\u56e0\u6211\u76f8\u4fe1\u4f60\u662f\u4f4d\u8bda\u5b9e\u7684\u541b\u5b50\uff0c\u4eca\u751f\u6211\u867d\u65e0\u6cd5\u62a5\u7b54\u4f60\u4e86\uff0c\u4f46\u6211\u5fc5\u5b9a\u5728\u51a5\u51a5\u4e2d\u4fdd\u4f51\u4f60\u7684\u5b89 \u5eb7\u3002\u201d \u3000\u3000\u8fd9\u79cd\u8bdd\u5728\u522b\u4eba\u8bf4\u6765\uff0c\u4e5f\u8bb8\u53ea\u662f\u7a7a\u8c08\uff0c\u4f46\u81ea\u5979\u53e3\u4e2d\u8bf4\u51fa\u6765\uff0c\u5374\u81ea\u6709\u4e00\u79cd\u795e\u79d8\u7684\u529b\u91cf\uff0c\u4ee4\u4eba \u89c9\u5f97\u81ea\u5df1\u5f77\u4f5b\u6b63\u5728\u548c\u4e00\u4e2a\u5e7d\u7075\u505a\u7740\u4ea4\u6613\u3002 \u3000\u3000\u695a\u7559\u9999\u4e0d\u518d\u8bf4\u8bdd\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u77e5\u9053\u5979\u7684\u51b3\u5fc3\u662f\u8c01\u4e5f\u65e0\u6cd5\u66f4\u6539\u7684\u4e86\u3002 \u3000\u3000\u9752\u8863\u5c3c\u53cc\u624b\u5408\u4ec0\uff0c\u8eac\u8eab\u4e00\u793c\uff0c\u53e3\u5ba3\u4f5b\u53f7\uff0c\u7f13\u7f13\u8f6c\u8eab\u3002 \u3000\u3000\u695a\u7559\u9999\u5e76\u6ca1\u6709\u770b\u5230\u5979\u6709\u4efb\u4f55\u52a8\u4f5c\uff0c\u5979\u7684\u4eba\u5df2\u5012\u4e0b\u3002 \u3000\u3000\u5012\u5728\u90a3\u9ec4\u5e54\u590d\u76d6\u7684\u5c38\u8eab\u4e0a\u3002 \u3000\u3000\u695a\u7559\u9999\u957f\u957f\u53f9\u606f\uff0c\u8eac\u8eab\u884c\u793c\u3002 \u3000\u3000\u82cf\u84c9\u84c9\u5374\u5df2\u70ed\u6cea\u76c8\u7736\uff0c\u63c9\u7740\u773c\u775b\u9053\uff1a\u201c\u770b\u6765\u8fd9\u4f4d\u5927\u5e08\u4e5f\u662f\u4e2a\u591a\u60c5\u4eba\u3002\u201d \u3000\u3000\u7a81\u542c\u80e1\u94c1\u82b1\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u5931\u58f0\u9053\uff1a\u201c\u54a6\uff1a\u4f60\u51e0\u65f6\u6765\u7684\uff1f\u4ed6\u5462\uff1f\u201d \u3000\u3000\u4ed6\u8bf4\u7684\u201c\u4f60\u201d\u81ea\u7136\u662f\u82cf\u84c9\u84c9\uff0c\u201c\u4ed6\u201d\u5c31\u662f\u90a3\u9ed1\u888d\u5ba2\u3002 \u3000\u3000\u82cf\u84c9\u84c9\u6115\u7136\u9053\uff1a\u201c\u4f60\u6ca1\u6709\u77a7\u89c1\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u832b\u7136\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u2026\u2026\u201d \u3000\u3000\u4ed6\u5934\u4e0a\u53c8\u5192\u51fa\u51b7\u6c57\uff0c\u55c4\u58f0\u9053\uff1a\u201c\u8fd9\u662f\u600e\u9ebd\u56de\u4e8b\uff1f\u6211\u600e\u5730\u5ffd\u7136\u505a\u4e86\u68a6\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u7f13\u7f13\u9053\uff1a\u201c\u5c31\u56e0\u4e3a\u4f60\u5728\u505a\u68a6\uff0c\u6240\u4ee5\u627e\u4e00\u76f4\u4e0d\u6562\u60ca\u52a8\u4f60\uff0c\u73b0\u5728\u4f60\u7684\u68a6\u65e2\u5df2\u9192\u4e86\uff0c\u5c31 \u5c06\u68a6\u4e2d\u7684\u5fd8\u4e86\u5427\uff01\u201d \u3000\u3000\u8981\u77e5\u80e1\u94c1\u82b1\u65b9\u5fc3\u795e\u88ab\u6151\uff0c\u51e0\u4e4e\u5df2\u53ea\u662f\u4e00\u5177\u7a7a\u7684\u8eaf\u58f3\uff0c\u5269\u4e0b\u7684\u4e5f\u5c31\u4e0d\u591a\u4e86\uff0c\u82e5\u88ab\u60ca\u52a8\uff0c\u771f \u6c14\u4e00\u5c94\uff0c\u4fbf\u96be\u514d\u8d70\u706b\u5165\u9b54\u3002 \u3000\u3000\u4ed6\u82e5\u4e0d\u5c06\u8fd9\u4ef6\u4e8b\u5fd8\u8bb0\uff0c\u4ee5\u5f8c\u4e0e\u4eba\u52a8\u624b\uff0c\u4fbf\u96be\u514d\u5931\u53bb\u81ea\u4fe1\uff0c\u4f7f\u6b66\u7684\u4eba\u82e5\u662f\u5931\u53bb\u81ea\u4fe1\uff0c\u5269\u4e0b \u7684\u5c31\u4e0d\u591a\u4e86\u3002\u80e1\u94c1\u82b1\u53c8\u4f55\u5c1d\u4e0d\u660e\u767d\u8fd9\u9053\u7406\uff0c\u6ee1\u5934\u51b7\u6c57\u53c8\u4e0d\u7981\u6d94\u6d94\u800c\u843d\u3002 \u3000\u3000\u695a\u7559\u9999\u51dd\u6ce8\u7740\u4ed6\uff0c\u8fc7\u4e86\u534a\u664c\uff0c\u624d\u67d4\u58f0\u9053\uff1a\u201c\u73b0\u5728\u4f60\u5df2\u5fd8\u4e86\u9ebd\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u53c8\u6c88\u9ed8\u4e86\u5f88\u4e45\uff0c\u5ffd\u7136\u4ef0\u5929\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u5fd8\u4e86\u3002\u201d \u3000\u3000\u4ee5\u67af\u679d\u548c\u6728\u53f6\u5c06\u5c38\u8eab\u63a9\u76d6\uff0c\u695a\u7559\u9999\u71c3\u8d77\u4e86\u706b\u3002 \u3000\u3000\u6240\u6709\u7684\u79d8\u5bc6\uff0c\u7acb\u523b\u5c31\u8981\u968f\u7740\u706b\u5149\u6d88\u901d\u4e86\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u671b\u7740\u90a3\u59cb\u7ec8\u88ab\u9ec4\u5e54\u63a9\u76d6\u7740\u7684\u5c38\u8eab\uff0c\u5fcd\u4e0d\u4f4f\u5583\u5583\u9053\uff1a\u201c\u8fd9\u4eba\u7a76\u7adf\u662f\u8c01\u5462\uff1f\u662f\u8fd9\u4f4d\u9752 \u8863\u5c3c\u7684\u5e08\u59b9\uff1f\u8fd8\u662f\u5979\u7684\u60c5\u4eba\uff1f\u53ea\u56e0\u4ed6\u5bb9\u8c8c\u88ab\u6bc1\uff0c\u6240\u4ee5\u624d\u8eb2\u7740\u4e0d\u6562\u89c1\u4eba\uff1f\u201d \u3000\u3000\u82cf\u84c9\u84c9\u60f3\u8bf4\u53e5\u4ec0\u9ebd\uff0c\u5374\u6ca1\u6709\u8bf4\u51fa\u53e3\u3002 \u3000\u3000\u65b9\u9ec4\u5e54\u88ab\u98ce\u5439\u8d77\u4e00\u89d2\uff0c\u5979\u5f77\u4f5b\u770b\u5230\u4e86\u8fd9\u4eba\u7684\u624b\u3002 \u3000\u3000\u770b\u6765\u90a3\u7adf\u4e0d\u50cf\u662f\u53ea\u4eba\u7684\u624b\uff0c\u800c\u50cf\u662f\u53ea\u91ce\u517d\u7684\u722a\u5b50\uff0c\u4e0a\u9762\u5f77\u4f5b\u957f\u7740\u5f88\u957f\u7684\u6307\u7532\uff0c\u8fd8\u5e26\u7740\u4e9b \u9ed1\u6bdb\u3002 \u3000\u3000\u96be\u9053\u9752\u8863\u5c3c\u5982\u6b64\u7737\u604b\u7684\u53ea\u4e0d\u8fc7\u662f\u53ea\u901a\u7075\u7684\u91ce\u517d\uff1f \u3000\u3000\u201c\u60c5\u201d\u4e0e\u201c\u5b7d\u201d\u4e4b\u95f4\uff0c\u6709\u65f6\u76f8\u9694\u672c\u5c31\u53ea\u4e0d\u8fc7\u4e00\u7ebf\u800c\u5df2\u3002 \u3000\u3000\u4f46\u82cf\u84c9\u84c9\u975e\u4f46\u4e0d\u6562\u8bf4\uff0c\u751a\u81f3\u8fde\u60f3\u90fd\u4e0d\u6562\u60f3\u3002 \u3000\u3000\u4f55\u51b5\uff0c\u4eba\u7684\u624b\u4e0a\uff0c\u6709\u65f6\u4e5f\u4f1a\u957f\u51fa\u9ed1\u6bdb\u6765\u7684\u3002 \u3000\u3000\u706b\uff0c\u5f00\u59cb\u71c3\u70e7\u3002 \u3000\u3000\u8fd9\u79d8\u5bc6\u5df2\u5728\u706b\u4e2d\u6d88\u901d\u4e86\uff0c\u6c38\u8fdc\u6d88\u901d\u4e86\u3002 \u3000\u3000\u82cf\u84c9\u84c9\u5fc3\u91cc\u5374\u6c38\u8fdc\u7559\u4e0b\u4e2a\u8c1c\u3002 \u3000\u3000\u4e00\u70b9\u7ea2\u548c\u66f2\u65e0\u5bb9\u53c8\u8d70\u4e86\u3002\u6ca1\u6709\u4eba\u80fd\u7559\u5f97\u4f4f\u4ed6\u4eec\uff0c\u56e0\u4e3a\u4ed6\u4eec\u5728\u5b64\u72ec\u4e2d\u751f\uff0c\u5728\u5b64\u72ec\u4e2d\u957f\u3002 \u3000\u3000\u53ea\u6709\u5b64\u72ec\u7684\u751f\u6d3b\uff0c\u624d\u662f\u4ed6\u4eec\u559c\u7231\u7684\u3002 \u3000\u3000\u552f\u4e00\u4ee4\u695a\u7559\u9999\u6b23\u6170\u7684\u662f\uff0c\u8fd9\u4e24\u4e2a\u5b64\u72ec\u7684\u4eba\u5df2\u7ed3\u5408\u5230\u4e00\u8d77\u3002 \u3000\u3000\u6234\u72ec\u884c\u575a\u6301\u8981\u9001\u4ed6\u4eec\u4e00\u7a0b\uff0c\u56e0\u4e3a\u6234\u72ec\u884c\u8fd9\u4e00\u751f\u4e5f\u662f\u5b64\u72ec\u7684\uff0c\u53ea\u6709\u4ed6\u624d\u80fd\u4e86\u89e3\u5b64\u72ec\u7684\u4eba\u5f80 \u5f80\u4e5f\u4f1a\u6709\u4e00\u9897\u706b\u70ed\u7684\u5fc3\u3002 \u3000\u3000\u9ec4\u9c81\u76f4\u5462\uff1f\u4ed6\u51b3\u5fc3\u8981\u5728\u90a3\u6761\u6de1\u6c34\u4e2d\u627e\u5230\u96c4\u5a18\u5b50\u7684\u4f53\uff0c\u4ed6\u4eec\u7684\u53cb\u60c5\u60a3\u96be\u4e0d\u79fb\uff0c\u751f\u6b7b\u4e0d\u6613\u3002 \u3000\u3000\u695a\u7559\u9999\u5c06\u9752\u8863\u5c3c\u7684\u9aa8\u7070\u4ea4\u7ed9\u4e86\u4ed6\uff0c\u56e0\u4e3a\u4ed6\u4e5f\u662f\u4e2a\u53ef\u4ee5\u4fe1\u6258\u7684\u4eba\uff0c\u65e0\u8bba\u8c01\u4ea4\u5230\u9ec4\u9c81\u76f4\u8fd9\u6837 \u7684\u670b\u53cb\uff0c\u90fd\u662f\u4ef6\u5f88\u5e78\u8fd0\u7684\u4e8b\u3002 \u3000\u3000\u5b8b\u751c\u513f\u4e00\u76f4\u561f\u7740\u5634\uff0c\u57cb\u6028\u7740\uff0c\u5979\u6655\u7761\u4e86\u4e00\u573a\uff0c\u9519\u8fc7\u4e86\u8bb8\u591a\u201c\u70ed\u95f9\u201d\uff0c\u4e00\u76f4\u89c9\u5f97\u5f88\u4e0d\u5f00 \u5fc3\u3002 \u3000\u3000\u82cf\u84c9\u84c9\u5c31\u5b89\u6170\u5979\uff1a\u201c\u4f60\u867d\u7136\u9519\u8fc7\u4e86\u8bb8\u591a\u4e8b\uff0c\u4f46\u6709\u4e9b\u4e8b\u770b\u4e0d\u5230\u53cd\u800c\u597d\u3002\u201d \u3000\u3000\u674e\u7ea2\u8896\u5374\u5728\u5411\u695a\u7559\u9999\u53d9\u8bf4\u6b64\u884c\u7684\u7ecf\u8fc7\uff1a\u201c\u534a\u9014\u4e2d\u67f3\u65e0\u7709\u7684\u6bd2\u5ffd\u53c8\u53d1\u4f5c\uff0c\u65e0\u6cd5\u6210\u884c\uff0c\u6240\u4ee5 \u674e\u7389\u51fd\u5c31\u7559\u4e0b\u6765\u966a\u5979\uff0c\u4ed6\u4eec\u5728\u4e00\u4e2a\u6a35\u592b\u7684\u8305\u820d\u4e2d\u517b\u75c5\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u81ea\u7136\u77e5\u9053\u67f3\u65e0\u7709\u5e76\u4e0d\u662f\u201c\u75c5\u201d\uff0c\u800c\u662f\u201c\u6015\u201d\uff0c\u5979\u77e5\u9053\u81ea\u5df1\u7684\u79d8\u5bc6\u5df2\u5c06\u88ab\u63ed\u7a7f\uff0c\u90a3 \u91cc\u8fd8\u6562\u6765\u89c1\u695a\u7559\u9999\u3002 \u3000\u3000\u674e\u7ea2\u8896\u52a8\u5bb9\u9053\uff1a\u201c\u4f60\u662f\u8bf4\uff0c\u67f3\u65e0\u7709\u6839\u672c\u6ca1\u6709\u4e2d\u6bd2\uff0c\u5979\u5c06\u4f60\u8bf1\u5230\u795e\u6c34\u5bab\u6765\uff0c\u53ea\u662f\u4e3a\u4e86\u8981\u66ff \u77f3\u89c2\u97f3\u590d\u4ec7\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u9053\uff1a\u201c\u6b63\u662f\u5982\u6b64\u3002\u201d \u3000\u3000\u674e\u7ea2\u8896\u9053\uff1a\u201c\u8fd9\u9ebd\u6837\u8bf4\u6765\uff0c\u5979\u4e5f\u7edd\u4e0d\u6562\u518d\u7559\u5728\u90a3\u6a35\u592b\u5bb6\u91cc\u4e86\uff0c\u6211\u4eec\u4f55\u5fc5\u518d\u7a7a\u8dd1\u4e00\u8d9f\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u53f9\u9053\uff1a\u201c\u53d7\u9a97\u7684\u5e76\u4e0d\u6b62\u6211\u4eec\uff0c\u8fd8\u6709\u674e\u7389\u51fd\uff0c\u6211\u597d\u6b79\u4e5f\u8981\u627e\u5230\u4ed6\u3002\u201d \u3000\u3000\u4ed6\u4eec\u5f88\u5feb\u5c31\u5230\u4e86\u90a3\u91cc\uff0c\u53ea\u89c1\u4e1b\u6797\u65c1\u7684\u5c71\u811a\u4e0b\u6709\u4e24\u95f4\u5c0f\u5c0f\u7684\u6728\u5c4b\uff0c\u4e00\u4e2a\u5e74\u7eaa\u867d\u5df2\u4e0d\u5c0f\uff0c\u7b4b \u9aa8\u5374\u5f88\u58ee\u7684\u6a35\u592b\u6b63\u7cbe\u8d64\u7740\u4e0a\u8eab\u5728\u5c4b\u5916\u7684\u91ce\u5730\u4e0a\u5288\u67f4\uff0c\u4ed6\u867d\u7136\u4e0d\u61c2\u6b66\u529f\uff0c\u4f46\u6bcf\u4e00\u65a7\u5288\u4e0b\uff0c\u90fd\u5e26 \u7740\u79cd\u5f88\u67d4\u7f8e\u7684\u97f5\u5f8b\uff0c\u4e00\u6839\u6839\u5de8\u5927\u7684\u6728\u67f4\u5e94\u65a7\u800c\u88c2\u3002 \u3000\u3000\u695a\u7559\u9999\u671b\u7740\u4ed6\u7075\u5de7\u7684\u8fd0\u7528\u7740\u65a7\u5934\uff0c\u60f3\u8d77\u4e86\u201c\u517b\u7531\u57fa\u548c\u5356\u6cb9\u7fc1\u201d\u7684\u6545\u4e8b\uff0c\u5fc3\u91cc\u4e0d\u7981\u53c8\u6709\u8bb8 \u591a\u611f\u6168\u3002 \u3000\u3000\u201c\u6b66\u529f\u867d\u7136\u7ec3\u5230\u5929\u4e0b\u7b2c\u4e00\uff0c\u53c8\u6709\u4ec0\u9ebd\u503c\u5f97\u9a84\u50b2\u7684\uff0c\u5f53\u4eca\u5929\u4e0b\u4f7f\u65a7\u7684\u7b2c\u4e00\u540d\u5bb6\u53c8\u80fd\u6bd4\u8fd9\u6a35 \u592b\u5f3a\u80dc\u591a\u5c11\uff1f\u201d \u3000\u3000\u674e\u7ea2\u8896\u8d70\u8fc7\u53bb\uff0c\u542b\u7b11\u9053\uff1a\u201c\u501f\u95ee\u5927\u54e5\uff0c\u6211\u4eec\u90a3\u4e24\u4f4d\u670b\u53cb\u8fd8\u5728\u8fd9\u91cc\u9ebd\uff1f\u201d \u3000\u3000\u6a35\u592b\u9762\u4e0a\u6beb\u65e0\u8868\u60c5\uff0c\u751a\u81f3\u8fde\u770b\u90fd\u6ca1\u6709\u770b\u5979\u4e00\u773c\uff0c\u53ea\u662f\u70b9\u4e86\u70b9\u5934\uff0c\u4e00\u65a7\u5288\u4e0b\uff0c\u53c8\u4e00\u6839\u6728\u67f4 \u5e94\u65a7\u800c\u88c2\u3002 \u3000\u3000\u674e\u7ea2\u8896\u9053\u8fc7\u591a\u8c22\uff0c\u548c\u695a\u7559\u9999\u6253\u4e86\u4e2a\u773c\u8272\uff0c\u4e24\u4eba\u63a0\u5230\u95e8\u53e3\uff0c\u5c31\u89c1\u5230\u4e86\u674e\u7389\u51fd\u3002 \u3000\u3000\u9648\u8bbe\u7b80\u964b\u7684\u6728\u5c4b\u4e2d\uff0c\u6709\u5f20\u767d\u6728\u65b9\u684c\uff0c\u674e\u7389\u51fd\u6b63\u4e00\u4e2a\u4eba\u5750\u5728\u90a3\u91cc\u559d\u9152\uff0c\u4ed6\u8138\u8272\u82cd\u767d\uff0c\u770b\u6765 \u6709\u4e9b\u7761\u7720\u4e0d\u8db3\uff0c\u4f46\u5374\u4e00\u676f\u63a5\u7740\u4e00\u676f\uff0c\u4e0d\u505c\u7684\u559d\u7740\uff0c\u5c4b\u91cc\u7684\u5149\u7ebf\u5f88\u6697\uff0c\u867d\u7136\u662f\u767d\u5929\uff0c\u5374\u5f77\u4f5b\u9759 \u5bc2\u822c\u8427\u7d22\u3002 \u3000\u3000\u4ed6\u4eec\u8d70\u8fdb\u53bb\uff0c\u674e\u7389\u51fd\u53ea\u4e0d\u8fc7\u62ac\u8d77\u5934\u77a7\u4e86\u4ed6\u4eec\u4e00\u773c\uff0c\u7acb\u523b\u53c8\u81ea\u987e\u81ea\u7684\u559d\u8d77\u9152\u6765\uff0c\u50cf\u662f\u5df2\u5ffd \u7136\u53d8\u6210\u4e86\u4e2a\u964c\u751f\u4eba\u3002\u695a\u7559\u9999\u5728\u4ed6\u5bf9\u9762\u5750\u4e0b\uff0c\u8fc7\u4e86\u5f88\u4e45\uff0c\u624d\u95ee\u9053\uff1a\u201c\u5ac2\u592b\u4eba\u5462\uff1f\u201d \u3000\u3000\u674e\u7389\u51fd\u4f3c\u4e4e\u8fc7\u4e86\u5f88\u4e45\u624d\u542c\u61c2\u4ed6\u8fd9\u53e5\u8bdd\uff0c\u5ffd\u7136\u4e00\u7b11\uff0c\u6084\u58f0\u9053\uff1a\u201c\u5979\u7761\u7740\u4e86\uff0c\u4f60\u4eec\u83ab\u8981\u5435\u9192 \u5979\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u8fd9\u624d\u53d1\u73b0\u91cc\u9762\u7684\u5c4b\u89d2\u4e2d\u6709\u5f20\u5e8a\uff0c\u5e8a\u4e0a\u679c\u7136\u7761\u7740\u4e2a\u4eba\uff0c\u53ea\u4e0d\u8fc7\u5168\u8eab\u90fd\u88ab\u68c9\u88ab\u76d6\u7740\uff0c \u6839\u672c\u77a7\u4e0d\u89c1\u9762\u76ee\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u4e00\u8d70\u8fdb\u6765\uff0c\u5c31\u5fcd\u4e0d\u4f4f\u62ff\u8d77\u9152\u74f6\u3002 \u3000\u3000\u8c01\u77e5\u674e\u7389\u51fd\u5374\u4e00\u628a\u62a2\u4e86\u8fc7\u53bb\uff0c\u9053\uff1a\u201c\u9152\u4e0d\u591a\u4e86\uff0c\u6211\u81ea\u5df1\u8981\u559d\uff0c\u4f60\u8981\u559d\uff0c\u4e3a\u4f55\u4e0d\u81ea\u5df1\u53bb \u4e70\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u6014\u4f4f\u4e86\uff0c\u51e0\u4e4e\u8fd8\u65e0\u6cd5\u76f8\u4fe1\u8fd9\u4eba\u5c31\u662f\u6614\u65e5\u90a3\u6177\u6168\u597d\u53cb\u7684\u674e\u7389\u51fd\uff0c\u4f46\u674e\u7389\u51fd\u5374\u4ecd\u65c1\u82e5 \u65e0\u4eba\uff0c\u81ea\u987e\u81ea\u659f\u81ea\u996e\uff0c\u522b\u4eba\u65e0\u8bba\u5c06\u4ed6\u5f53\u505a\u90a3\u79cd\u4eba\uff0c\u4ed6\u4f3c\u4e4e\u5168\u90fd\u5df2\u4e0d\u653e\u5728\u5fc3\u4e0a\u3002 \u3000\u3000\u997c\u4e86\u534a\u664c\uff0c\u695a\u7559\u9999\u624d\u7f13\u7f13\u9053\uff1a\u201c\u62b1\u6b49\u5f97\u5f88\uff0c\u6211\u4eec\u5e76\u6ca1\u6709\u4e3a\u5ac2\u592b\u4eba\u5c06\u89e3\u836f\u62ff\u56de\u6765\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u6c88\u58f0\u9053\uff1a\u201c\u56e0\u4e3a\u5ac2\u592b\u4eba\u6839\u672c\u5c31\u6ca1\u6709\u4e2d\u6bd2\uff0c\u6c34\u6bcd\u4eb2\u81ea\u544a\u8bc9\u4e86\u6211\u3002\u201d \u3000\u3000\u4ed6\u4ee5\u4e3a\u674e\u7389\u51fd\u542c\u4e86\u8fd9\u8bdd\u5fc5\u5b9a\u8981\u5927\u5403\u4e00\u60ca\uff0c\u8c01\u77e5\u674e\u7389\u51fd\u8138\u4e0a\u8fde\u4e00\u70b9\u8868\u60c5\u4e5f\u6ca1\u6709\uff0c\u8fc7\u4e86\u534a \u664c\uff0c\u5ffd\u53c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5979\u6709\u75c5\uff1f\u90a3\u5b9e\u5728\u592a\u597d\u4e86\uff0c\u592a\u597d\u4e86\u2026\u2026\u201d \u3000\u3000\u695a\u7559\u9999\u5ffd\u7136\u53d1\u73b0\u4ed6\u7b11\u5f97\u751a\u662f\u5947\u7279\uff0c\u8bf4\u662f\u5728\u7b11\uff0c\u5012\u4e0d\u5982\u8bf4\u662f\u5728\u54ed\uff0c\u4e00\u65f6\u95f4\u4ed6\u4e5f\u731c\u4e0d\u900f\u674e\u7389 \u51fd\u7a76\u7adf\u662f\u4f55\u5fc3\u610f\uff0c\u4e5f\u4e0d\u77e5\u662f\u8be5\u4e25\u8bcd\u76f8\u8bd8\uff0c\u7ffb\u8138\u52a8\u624b\uff0c\u8fd8\u662f\u5c06\u8fd9\u4ef6\u4e8b\u8f7b\u8f7b\u5e26\u8fc7\uff0c\u5c31\u6b64\u4e0d\u63d0\u4e86\u3002 \u3000\u3000\u695a\u7559\u9999\u7d20\u6765\u5fc3\u80f8\u5bbd\u5927\uff0c\u53d7\u4eba\u6069\u60e0\uff0c\u56fa\u7136\u70b9\u6c34\u5fc5\u62a5\uff0c\u4f46\u5374\u4ece\u6765\u4e0d\u613f\u8bb0\u4ec7\uff0c\u4f55\u51b5\u4ed6\u5fc3\u4e8b\u5df2 \u4e86\uff0c\u53c8\u65e0\u4f24\u635f\uff0c\u77f3\u89c2\u97f3\u4e00\u95e8\u66f4\u5df2\u7531\u6b64\u4e2d\u65ad\uff0c\u4ed6\u53c8\u4f55\u82e6\u518d\u82e6\u82e6\u8ffd\u903c\u4e00\u4e2a\u5f31\u5973\u5b50\uff0c\u5fc3\u601d\u8f6c\u52a8\u95f4\uff0c \u4eba\u5df2\u7ad9\u4e86\u8d77\u6765\uff0c\u7b11\u7740\u9053\uff1a\u201c\u5728\u4e0b\u4efb\u52a1\u5df2\u4e86\uff0c\u5c31\u6b64\u544a\u8f9e\u5427\uff1a\u6b64\u5f8c\u2026\u2026\u201d \u3000\u3000\u4ed6\u8bdd\u8fd8\u672b\u8bf4\u5b8c\uff0c\u5b8b\u751c\u513f\u5df2\u5927\u58f0\u9053\uff1a\u201c\u5514\u5f97\uff0c\u6211\u70b9\u9ebd\u4e5f\u8981\u95ee\u4e2a\u6e05\u695a\uff0c\u5979\u5634\u91cc\u8bf4\u7740\u8bdd\uff0c\u4eba\u5df2 \u51b2\u8fc7\u53bb\uff0c\u6380\u8d77\u4e86\u5e8a\u4e0a\u7684\u88ab\uff0c\u8bf4\u5230\u8fd9\u91cc\uff0c\u5979\u8bed\u58f0\u5ffd\u7136\u987f\u4f4f\uff0c\u671b\u7740\u5e8a\u4e0a\u7684\u4eba\uff0c\u7adf\u5413\u5446\u4e86\u3002\u67f3\u65e0\u7709 \u7684\u786e\u7761\u5728\u5e8a\u4e0a\uff0c\u4f46\u9762\u5982\u91d1\u7eb8\uff0c\u53cc\u76ee\u7d27\u95ed\uff0c\u8138\u4e0a\u7684\u8089\u5df2\u5168\u90fd\u6d88\u5931\u65e0\u5f71\uff0c\u53ea\u5269\u4e0b\u76ae\u5305\u9aa8\u5934\u3002\u8fd9\u7edd \u8272\u7684\u4e3d\u4eba\uff0c\u7adf\u5df2\u53d8\u5f97\u6709\u5982\u9ab7\u9ac5\uff0c\u800c\u4e14\u751f\u6c14\u5168\u65e0\uff0c\u5374\u6709\u4e24\u53c1\u53ea\u8682\u8681\u5728\u5979\u8033\u9f3b\u4e2d\u722c\u8fdb\u722c\u51fa\u3002\u5b8b\u751c \u513f\u201d\u54c7\u201d\u7684\u4e00\u58f0\uff0c\u5410\u4e86\u51fa\u6765\uff0c\u82cf\u84c9\u84c9\u7b49\u4eba\u4e5f\u4e0d\u7981\u8f6c\u8fc7\u5934\u53bb\uff0c\u4e0d\u5fcd\u518d\u770b\uff0c\u80e1\u94c1\u82b1\u5931\u8272\u9053\uff0c \u201c\u5979\u2026\u2026\u5979\u5df2\u6b7b\u4e86\u3002\u201d \u3000\u3000\u674e\u7389\u51fd\u5374\u6447\u4e86\u6447\u5934\uff0c\u6084\u58f0\u7b11\u9053\uff1a\u201c\u5979\u6ca1\u6709\u6b7b\uff0c\u53ea\u4e0d\u8fc7\u7761\u5f97\u5f88\u719f\u800c\u5df2\uff0c\u4f60\u4eec\u5343\u4e07\u83ab\u8981\u5435\u9192 \u5979\u3002\u201d\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u7eb5\u7136\u9c81\u83bd\uff0c\u4e5f\u77e5\u9053\u6b64\u4eba\u5b9e\u5728\u7528\u60c5\u592a\u6df1\uff0c\u662f\u4ee5\u7adf\u62d2\u7edd\u76f8\u4fe1\u4ed6\u7684\u7231\u59bb\u5df2\u6b7b\uff0c\u53ea\u56e0\u4ed6\u6839 \u672c\u4e0d\u80fd\u627f\u53d7\u8fd9\u5de8\u5927\u7684\u4f24\u75db\u3002 \u3000\u3000\u671b\u7740\u4ed6\u8138\u4e0a\u7684\u7b11\u5bb9\uff0c\u80e1\u94c1\u82b1\u70ed\u6cea\u4e5f\u4e0d\u7981\u5c06\u8981\u593a\u7736\u800c\u51fa\u2026\u2026 \u3000\u3000\u706f\u5149\u5f88\u6697\uff0c\u56e0\u4e3a\u8fd9\u672c\u5c31\u53ea\u662f\u4e2a\u5f88\u7b80\u964b\u7684\u5c0f\u9152\u94fa\u3002 \u3000\u3000\u4ed6\u4eec\u867d\u7136\u90fd\u5df2\u5f88\u997f\u4e86\uff0c\u4f46\u7ecf\u8fc7\u8fd9\u4ef6\u4e8b\u5f8c\uff0c\u8fd8\u6709\u8c01\u80fd\u5403\u5f97\u4e0b\uff1f \u3000\u3000\u674e\u7ea2\u8896\u773c\u775b\u4e5f\u6709\u4e9b\u53d1\u7ea2\uff0c\u5583\u5583\u9053\uff1a\u201c\u6211\u60f3\u4e0d\u5230\u5979\u7adf\u4f1a\u81ea\u6740\uff0c\u6211\u5b9e\u5728\u60f3\u4e0d\u5230\u2026\u2026\u201d \u3000\u3000\u82cf\u84c9\u84c9\u53f9\u9053\uff1a\u201c\u4e5f\u8bb8\u5979\u5e76\u4e0d\u662f\u81ea\u6740\uff0c\u800c\u662f\u771f\u7684\u4e2d\u6bd2\u65e0\u6551\u4e86\u3002\u201d \u3000\u3000\u674e\u7ea2\u8896\u9053\uff1a\u201c\u4f46\u6211\u76f8\u4fe1\u6c34\u6bcd\u4e5f\u7edd\u4e0d\u4f1a\u8bf4\u8c0e\u7684\uff0c\u56e0\u4e3a\u5979\u4e5f\u62b1\u5b9a\u4e86\u5fc5\u6b7b\u4e4b\u5fc3\uff0c\u53c8\u4f55\u5fc5\u518d\u9a97\u4eba \u5462\uff1f\u201d \u3000\u3000\u82cf\u84c9\u84c9\u9eef\u7136\u9053\uff1a\u201c\u8fd9\u4e5f\u8bb8\u662f\u56e0\u4e3a\u67f3\u65e0\u7709\u4e00\u76f4\u4ee5\u4e3a\u81ea\u5df1\u4e2d\u4e86\u6bd2\uff0c\u6240\u4ee5\u8eab\u5fc3\u4e00\u76f4\u53d7\u7740\u6298\u78e8\uff0c \u7591\u5fc3\u672c\u5c31\u53ef\u4ee5\u6740\u5f97\u6b7b\u4eba\u7684\u3002\u201d \u3000\u3000\u674e\u7ea2\u8896\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u65e0\u8bba\u600e\u9ebd\u8bf4\uff0c\u67f3\u65e0\u7709\u5e76\u6ca1\u6709\u9a97\u6211\u4eec\u2026\u2026\u201d \u3000\u3000\u5b8b\u751c\u5144\u9053\uff1a\u201c\u4f60\u4eec\u60f3\uff0c\u674e\u7389\u51fd\u662f\u4e0d\u662f\u771f\u7684\u4f1a\u4e00\u76f4\u5728\u90a3\u91cc\u7b49\u7740\u5979\u9192\u6765\u5462\uff1f\u4ed6\u2026\u2026\u4ed6\u672b\u514d\u592a \u53ef\u601c\u4e86\u3002\u201d \u3000\u3000\u8bf4\u7740\u8bf4\u7740\uff0c\u5979\u76ee\u4e2d\u53c8\u6d41\u4e0b\u6cea\u6765\u3002 \u3000\u3000\u82cf\u84c9\u84c9\u9053\uff1a\u201c\u65e0\u8bba\u591a\u9ebd\u6df1\u7684\u4f24\u75db\uff0c\u65e5\u5b50\u4e45\u4e86\uff0c\u4e5f\u4f1a\u6e10\u6e10\u6de1\u5fd8\u7684\uff0c\u5426\u5219\u8fd9\u4e16\u4e0a\u6015\u6709\u4e00\u534a\u4eba \u8981\u6d3b\u4e0d\u4e0b\u53bb\u4e86\u3002\u201d \u3000\u3000\u5979\u8bf4\u7684\u4e0d\u9519\uff0c\u65e0\u8bba\u591a\u9ebd\u6df1\u7684\u60b2\u54c0\u548c\u75db\u82e6\uff0c\u65e5\u4e45\u4e5f\u4f1a\u6de1\u5fd8\u7684\uff0c\u201c\u5fd8\u8bb0\u201d\uff0c\u672c\u5c31\u662f\u4eba\u7c7b\u6240\u4ee5 \u80fd\u751f\u5b58\u7684\u672c\u80fd\u4e4b\u4e00\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u5ffd\u7136\u7528\u529b\u4e00\u62cd\u695a\u7559\u9999\u7684\u80a9\u5934\uff0c\u9053\uff1a\u201c\u4f60\u7684\u5fc3\u4e8b\u5df2\u4e86\uff0c\u53c8\u80dc\u4e86\u5929\u4e0b\u7b2c\u4e00\u7684\u795e\u6c34\u5bab \u4e3b\uff0c\u4f60\u8fd8\u6709\u4ec0\u9ebd\u4e0d\u5f00\u5fc3\u7684\uff1f\u4e3a\u4f55\u603b\u662f\u95f7\u95f7\u4e0d\u4e50\u7684\u5750\u5728\u90a3\u91cc\uff0c\u8fde\u9152\u90fd\u4e0d\u559d\uff1f\u201d \u3000\u3000\u695a\u7559\u9999\u82e6\u7b11\u7740\uff0c\u6ca1\u6709\u8bf4\u8bdd\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u662f\u89c9\u5f97\u9519\u602a\u4e86\u67f3\u65e0\u7709\uff0c\u6240\u4ee5\u5fc3\u91cc\u5f88\u96be\u53d7\uff0c\u53ef\u662f\uff0c\u8fd9\u4e5f\u4e0d\u80fd\u602a\u4f60\uff0c \u65e0\u8bba\u5982\u4f55\uff0c\u5979\u603b\u4e0d\u662f\u56e0\u4f60\u800c\u6b7b\u7684\u3002\u201d \u3000\u3000\u3002\u695a\u7559\u9999\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u65e0\u8bba\u5982\u4f55\uff0c\u6211\u4eec\u6b64\u884c\u90fd\u7b97\u76f8\u5f53\u987a\u5229\u7684\uff0c\u552f\u4e00\u9057\u61be\u53ea\u662f\u9ed1 \u5927\u59d0\uff0c\u6211\u5bc5\u672b\u60f3\u5230\u5979\u7684\u813e\u6c14\u7adf\u90a3\u9ebd\u62d7\uff0c\u8fd8\u662f\u4e0d\u8f9e\u800c\u522b\u4e86\u3002\u201d \u3000\u3000\u695a\u7559\u9999\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u4e3e\u676f\u4e00\u996e\u800c\u5c3d\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u5c55\u989c\u7b11\u9053\uff1a\u201c\u65e0\u8bba\u5982\u4f55\uff0c\u4e0d\u5f00\u5fc3\u7684\u4e8b\u603b\u7b97\u90fd\u5df2\u8fc7\u53bb\uff0c\u73b0\u5728\u6211\u4eec\u603b\u5e94\u8be5\u60f3\u671b\u5f00\u5fc3\u7684 \u4e8b\uff0c\u505a\u4e9b\u5f00\u5fc3\u7684\u4e8b\u4e86\u5427\uff0c\u6211\u2026\u2026\u201d \u3000\u3000\u4ed6\u8bed\u58f0\u5ffd\u7136\u987f\u4f4f\uff0c\u773c\u775b\u4e5f\u53d1\u4e86\u76f4\u3002 \u3000\u3000\u4e00\u4e2a\u9752\u8863\u5c11\u5973\u6258\u7740\u4e2a\u5927\u6728\u76d8\u76c8\u76c8\u8d70\u4e86\u8fc7\u6765\uff0c\u5979\u957f\u5f97\u867d\u7136\u4e0d\u4e11\uff0c\u4f46\u4e5f\u7edd\u4e0d\u80fd\u7b97\u592a\u7f8e\uff0c\u53ea\u4e0d \u8fc7\u8138\u4e0a\u5374\u59cb\u7ec8\u5e26\u7740\u79cd\u795e\u5723\u4e0d\u53ef\u4fb5\u72af\u7684\u6a21\u6837\uff0c\u201c\u7830\u201d\u7684\uff0c\u5c06\u6728\u76d8\u4e0a\u7684\u9152\u58f6\u91cd\u91cd\u6401\u5728\u80e1\u94c1\u82b1\u9762 \u524d\uff0c\u4e00\u626d\u5934\u5c31\u8d70\u4e86\u56de\u53bb\uff0c\u8fde\u773c\u89d2\u90fd\u6ca1\u6709\u779f\u80e1\u94c1\u82b1\u4e00\u773c\u3002 \u3000\u3000\u695a\u7559\u9999\u89c1\u5230\u80e1\u94c1\u82b1\u5931\u9b42\u843d\u9b44\u7684\u6a21\u6837\uff0c\u4e5f\u4e0d\u7981\u7b11\u4e86\uff0c\u9053\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u53c8\u60f3\u5728\u8fd9\u91cc\u4f4f\u4e0b\u6765 \u4e86\uff1f\u201d \u3000\u3000\u80e1\u94c1\u82b1\u6478\u7740\u9f3b\u5b50\uff0c\u53c8\u5446\u4e86\u5f88\u4e45\uff0c\u5ffd\u7136\u53d1\u73b0\u672a\u78b0\u89c1\u7684\u4e00\u53cc\u5927\u773c\u775b\u6b63\u5728\u77ac\u4e5f\u4e0d\u77ac\u5730\u671b\u7740\u4ed6\u3002 \u3000\u3000\u80e1\u94c1\u82b1\u4ef0\u9762\u5927\u7b11\u9053\uff1a\u201c\u611a\u6211\u4e00\u6b21\uff0c\u5176\u9519\u5728\u4eba\uff0c\u82e5\u662f\u80fd\u540c\u6837\u9a97\u6211\u4e24\u6b21\uff0c\u5c31\u662f\u6211\u81ea\u5df1\u7684\u9519 \u4e86\uff0c\u4f60\u60f3\u6211\u600e\u9ebd\u4f1a\u518d\u4e0a\u8fd9\u79cd\u5f53\uff1f\u201d"}),showOnHomepage:!0}]}}]);