"use strict";(self.webpackChunkwebsite=self.webpackChunkwebsite||[]).push([[43323],{91328:(e,n,t)=>{t.r(n),t.d(n,{default:()=>a});var s=t(74848);const a=[{name:"\u9ed1\u706b",content:(0,s.jsx)(s.Fragment,{children:"\u7b2c\u4e00\u7ae0\u3000\u767d\u8272\u7684\u5973\u4eba \u3000 \u3000\u3000\uff11\u3001\u9ed1\u8272\u7684\u706b \u3000\u3000\u6e05\u6668\u4e09\u65f6\u534a\uff0c\u963f\u8482\u4fbf\u4e0d\u60c5\u4e0d\u613f\u7684\u8d77\u5e8a\uff0c\u7761\u773c\u60fa\u677e\u7684\u6293\u4e86\u6bdb\u3001\u7259\u5237\u3001\u6f31\u53e3\u676f\uff0c\u4e00\u8fb9\u5237\u7259\u4e00\u8fb9\u6d17\u6fa1\u3002 \u3000\u3000\u5979\u4e5f\u4e0d\u60f3\u90a3\u4e48\u65e9\u4fbf\u8d77\u5e8a\u3002\u5979\u7684\u4eba\u7f18\u4e00\u5411\u5f88\u597d\uff0c\u4eca\u665a\u5979\u7684\u59ca\u59b9\u963f\u82b3\u8981\u51fa\u5ac1\u4e86\uff0c\u6628\u5929\u5979\u8ddf\u51e0\u4e2a\u59ca\u59b9\u8bf4\u662f\u966a\u5ac1\u3001\u5176\u5b9e\u662f\u51d1\u70ed\u95f9\u3001\u641e\u6270\u81f3\u6df1\u5bb5\u3002 \u3000\u3000\u53ef\u662f\u5979\u4eca\u5929\u8fd8\u662f\u5f97\u8981\u5de5\u4f5c\u3002 \u3000\u3000\u5979\u7684\u5de5\u4f5c\u5fc5\u987b\u8981\u5728\u4e00\u5927\u6e05\u65e9\uff0c\u5929\u8fd8\u6ca1\u4eae\u524d\u505a\u597d\u3002 \u3000\u3000\u56e0\u4e3a\u5979\u662f\u4e2a\u80f6\u5de5\u3002 \u3000\u3000\u4e00\u4e2a\u975e\u5e38\u6f02\u4eae\u7684\u5973\u80f6\u5de5\u3002 \u3000\u3000\u963f\u8482\u7684\u786e\u975e\u5e38\u597d\u770b\uff0c\u4e4c\u6e9c\u6e9c\u7684\u957f\u53d1\uff0c\u767d\u91cc\u900f\u7ea2\u7684\u76ae\u80a4\uff0c\u5979\u5728\u770b\u4eba\u7684\u65f6\u5019\uff0c\u773c\u775b\u6df1\u6df1\u7684\uff0c\u8868\u60c5\u4e5f\u5f88\u8da3\u81f4\uff0c\u88ab\u5979\u770b\u7684\u4eba\u4e5f\u611f\u5230\u98d8\u98d8\u7136\u7684\u3002\u7531\u4e8e\u5979\u7684\u80a4\u8272\u5728\u5f53\u5730\u70ed\u5e26\u6c14\u5019\u91cc\u7b97\u662f\u5341\u5206\u96be\u5f97\u4e00\u89c1\u7684\u7c89\u7ea2\u767d\u7699\uff0c\u800c\u5979\u773c\u775b\u91cc\u7684\u795e\u97f5\u53c8\u5f88\u5947\u7279\uff0c\u5f88\u591a\u4eba\u90fd\u8c03\u4f83\u8d5e\u7fa1\u8bf4\u5979\u662f\u4e2a\u6df7\u8840\u513f\u3002 \u3000\u3000\u5979\u603b\u662f\u7b11\u9a82\u90a3\u4e9b\u4eba\u65e0\u804a\uff0c\u5176\u5b9e\u5fc3\u91cc\u6709\u4e00\u79cd\u865a\u8363\u7684\u559c\u6b22\u3002 \u3000\u3000\u8c03\u7b11\u5979\u7684\u4eba\u5927\u90fd\u662f\u8ffd\u5979\u7684\u4eba\u3002 \u3000\u3000\u8ffd\u5979\u7684\u4eba\u771f\u662f\u5404\u8272\u4eba\u7b49\u90fd\u6709\uff0c\u4ece\u9694\u7bf1\u90bb\u820d\uff0c\u5230\u540c\u5b66\u540c\u4e8b\uff0c\u7b97\u4e00\u4e0b\u7adf\u6709\uff1a\u534e\u4eba\u3001\u9a6c\u6765\u4eba\u3001\u5370\u5ea6\u4eba\u3001\u5b5f\u52a0\u91cc\u4eba\uff0c\u8fd8\u6709\u4e00\u4e2a\u9521\u514b\u4eba\uff01 \u3000\u3000\u201c\u53ef\u80fd\u662f\u56e0\u4e3a\u5979\u6837\u5b50\u957f\u5f97\u7279\u522b\uff0c\u4e0d\u5206\u5730\u57df\uff0c\u4ed6\u4eec\u624d\u5bf9\u5979\u4e5f\u7279\u522b\u6709\u5174\u8da3\u5427\uff01 \u3000\u3000\u53cd\u6b63\u963f\u8482\u4e0d\u6025\u3002 \u3000\u3000\u5979\u8981\u6162\u6162\u9009\u62e9\u3002 \u3000\u3000\u5979\u7684\u5bb6\u5883\u4e0d\u597d\uff0c\u5426\u5219\u4e5f\u4e0d\u5fc5\u5929\u5929\u4e00\u5927\u6e05\u65e9\u5c31\u8981\u53bb\u5272\u80f6\u5e2e\u8865\u5bb6\u8ba1\u4e86\uff0c\u4ed6\u9664\u4e86\u8981\u9009\u4e00\u4e2a\u9ad8\u5927\u82f1\u4fca\u548c\u7231\u81ea\u5df1\u7684\u4e08\u592b\u4e4b\u5916\uff0c\u672a\u6765\u592b\u6c64\u8fd8\u5fc5\u987b\u662f\u4e2a\u6709\u94b1\u4eba\u3002 \u3000\u3000\u2014\u2014\u8fd9\u6837\uff0c\u5979\u5c31\u53ef\u4ee5\u4e0d\u5fc5\u518d\u51fa\u6765\u5de5\u4f5c\uff0c\u53ef\u4ee5\u5728\u5bb6\u505a\u4ed6\u7684\u5c11\u5976\u5976\uff0c\u5728\u5e97\u91cc\u5f53\u5979\u7684\u201c\u5934\u5bb6\u5a18\u201d\uff0c\u597d\u8ba9\u5bb6\u91cc\u7684\u4eba\u4eab\u4eab\u798f\u3002 \u3000\u3000\u60f3\u5230\u4ee5\u540e\uff0d\uff0d\u5c31\u5728\u4e0d\u4e45\u4ee5\u540e\u2014\u2014\u5c31\u4e0d\u5fc5\u5230\u80f6\u56ed\u53bb\u53d7\u98ce\u62b5\u5bd2\u5582\u868a\u5b50\uff0c\u5979\u7684\u5fc3\u60c5\u5c31\u7279\u522b\u6109\u5feb\u8d77\u6765\u3002 \u3000\u3000\u5979\u4e00\u8fb9\u6d17\u6fa1\u3001\u63e9\u4e0a\u80a5\u7682\u3001\u51b2\u6c34\u62b9\u8eab\uff0c\u4e00\u8fb9\u81ea\u90a3\u4e00\u9762\u5df2\u88ab\u6c34\u6e0d\u8680\u5f97\u82b1\u6591\u6591\u7684\u65b9\u955c\u91cc\uff0c\u6b23\u8d4f\u81ea\u5df1\u5c11\u5973\u5b8c\u597d\u7684\u55e3\u4f53\u3002 \u3000\u3000\u2014\u2014\u563f\uff0c\u8eab\u88c1\u771f\u597d\u2026\u2026\u8ff7\u6b7b\u90a3\u4e9b\u7537\u4eba\u4e86\u2026\u2026 \u3000\u3000\u60f3\u5230\u8fd9\u91cc\uff0c\u963f\u8482\u5c31\u5fcd\u4e0d\u4f4f\u54ac\u7740\u4e0b\u5507\u6697\u7b11\uff1a\u96be\u602a\u90a3\u4e2a\u68ee\u7f8e\uff0c\u4e00\u89c1\u7740\u5979\u5c31\u8bdd\u90fd\u8bf4\u4e0d\u51fa\u6765\uff0c\u963f\u534e\u7684\u4e00\u53cc\u8d3c\u773c\u8001\u5f80\u5979\u8eab\u4e0a\u6e9c\u4e86\u3002 \u3000\u3000\u2014\u2014\u8870\u4eba\uff01 \u3000\u3000\u8981\u4e0d\u662f\u6e05\u6668\u7684\u7a7a\u6c14\u5f88\u6709\u70b9\u51b7\uff0c\u5979\u8fd8\u4f1a\u201c\u81ea\u6211\u6b23\u8d4f\u201d\u4e0b\u53bb\u3002 \u3000\u3000\u6b63\u5728\u5979\u8981\u64e6\u6d17\u8eab\u4e0a\u7682\u6cab\u7684\u65f6\u5019\uff0c\u7a81\u7136\uff0c\u65e0\u7531\u5730\u3001\u4e0d\u53ef\u6069\u8bae\u5730\u3001\u6beb\u65e0\u5fc3\u7406\u51c6\u5907\u5730\u751f\u8d77\u4e86\u4e00\u4e2a\u611f\u89c9\uff1a \u3000\u3000\u2014\u2014\u6709\u4eba\u5728\u770b\u5979\uff01 \u3000\u3000\u2014\u2014\u6709\u4e00\u53cc\u773c\u775b\u5728\u671b\u7740\u5979\uff01 \u3000\u3000\u8c01\uff1f \u3000\u3000\u4e0d\u53ef\u80fd\u7684\u3002\u5988\u5988\u8eab\u4f53\u4e0d\u597d\uff0c\u8fd8\u5728\u7761\u89c9\uff0c\u54e5\u54e5\u5ac2\u5ac2\u5df2\u53bb\u4e86\u201c\u5df4\u5239\u201d\u6a31\u644a\u6863\uff0c\u59b9\u59b9\u548c\u5f1f\u5f1f\u5e74\u7eaa\u53c8\u5927\u5c0f\uff0c\u5bb6\u91cc\u5df2\u6ca1\u6709\u5176\u4ed6\u7684\u4eba\u4e86\u3002 \u3000\u3000\u5979\u8fde\u5fd9\u5b9a\u775b\u518d\u770b\u3002 \u3000\u3000\u2014\u2014\u51b2\u51c9\u623f\u81ea\u94c1\u95e8\u5c01\u5f97\u5bc6\u3002\u5bc6\u5b9e\u5b9e\u5730\u4e00\u6ca1\u6709\u4eba\u554a\u3002 \u3000\u3000\u96be\u9053\u662f\u773c\u82b1\uff1f\u963f\u8482\u5fc3\u4e2d\u72d0\u7591\uff0c\u53ef\u662f\u90a3\u79cd\u607f\u89c9\u53c8\u90a3\u4e48\u7684\u771f\u5b9e\uff0c\u771f\u5b9e\u5f97\u597d\u50cf\u521a\u624d\u5728\u51b2\u51c9\u623f\u91cc\u8fd8\u6709\u4e00\u4e2a\u4eba\u5c31\u7ad9\u5728\u8fd9\u91cc\uff01 \u3000\u3000\u5979\u7684\u5fc3\u6709\u70b9\u53d1\u6bdb\uff0c\u8d76\u5fd9\u60f3\u62b9\u5e72\u8eab\u4e0a\u7684\u80a5\u7682\u6c34\u8d70\u51fa\u6765\uff0c\u4e0d\u6599\u8fd9\u4e00\u60ca\u614c\uff0c\u7ed9\u51e0\u6ef4\u80a5\u7682\u6c34\u73e0\u6e85\u5165\u4e86\u773c\u775b\uff0c\u53c8\u75db\u53c8\u75d2\u3002 \u3000\u3000\u5979\u53ea\u597d\u4e0d\u4f4f\u7684\u7528\u6e05\u6c34\u51b2\u6d17\u62b9\u773c\u775b\u3002 \u3000\u3000\u2014\u2014\u5e78\u597d\uff0c\u90a3\u79cd\u88ab\u201c\u7aa5\u6d74\u201d\u7684\u611f\u89c9\u4e0d\u518d\u51fa\u73b0\u4e86\u3002 \u3000\u3000\u963f\u8482\u4e5f\u4e0d\u7406\u8fd9\u8bb8\u591a\u4e86\u3002\u65f6\u4faf\u5df2\u4e0d\u65e9\u4e86\uff0c\u5272\u80f6\u7684\u5de5\u4f5c\u662f\u5ef6\u8fdf\u4e0d\u5f97\u7684\u3002\u5979\u5fc3\u91cc\u53ea\u9a82\u5012\u9709\uff0c\u4e5f\u6ca1\u7ee7\u7eed\u56de\u60f3\u521a\u624d\u7684\u60c5\u5f62\uff0c\u4fbf\u7a7f\u4e0a\u5de5\u4f5c\u670d\uff0c\u7ed1\u4e0a\u5934\u5dfe\u7cfb\u4e0a\u6c14\u706f\uff0c\u9a91\u4e0a\u90a3\u67b6\u53c8\u9ad8\u53c8\u5927\u7684\u811a\u8e0f\u8f66\u53bb\u3001\u4e0a\u5979\u51cc\u6668\u7684\u73ed\u4e86\uff0c \u3000\u3000\u5272\u6811\u80f6\u8fd9\u4e00\u884c\u5de5\u4f5c\uff0c\u4e3b\u8981\u662f\u7528\u4e00\u79cd\u5f88\u7279\u522b\u7684\u94a7\u5200\uff0c\u6253\u659c\u522e\u7834\u6a61\u6811\u7684\u8868\u76ae\uff0c\u8ba9\u5b83\u6e17\u51fa\u4e86\u6811\u8102\u3002\u8fd9\u4e9b\u6811\u8102\uff0c\u6d41\u5165\u4e00\u4e2a\u9676\u676f\u91cc\uff0c\u4fbf\u662f\u6240\u8c13\u7684\u80f6\u6c41\uff1a\u80f6\u6c41\u6536\u96c6\u4e4b\u540e\uff0c\u518d\u9001\u5230\u5230\u5de5\u5382\u52a0\u5de5\uff0c\u6210\u4e86\u80f6\u7247\uff0c\u5356\u7ed9\u5382\u5546\uff0c\u7528\u9014\u6781\u591a\uff0c\u4ece\u8f6e\u80ce\u3001\u5851\u80f6\u5230\u5bb6\u5ead\u7528\u5177\u3001\u62d6\u978b\u7403\u978b\uff0c\u751a\u81f3\u907f\u5b55\u5957\u90fd\u662f\u6765\u81ea\u6a61\u80f6\u6811\u7684\u8102\u6c41\u3002 \u3000\u3000\u7531\u4e8e\u6a61\u6811\u662f\u8fd9\u884c\u4e1a\u91cc\u7684\u4e00\u5207\u4f9d\u51ed\uff0c\u6240\u4ee5\u5f97\u8981\u597d\u597d\u7684\u7ef4\u62a4\uff0c\u82e5\u5728\u592a\u9633\u51fa\u6765\u4e4b\u540e\u518d\u522e\u7834\u6a61\u6811\u7684\u5e9f\u8102\uff0c\u4f1a\u5bf9\u50cf\u6811\u9020\u6210\u4f24\u5bb3\uff0c\u6240\u4ee5\u201c\u5272\u80f6\u201d\u7684\u5de5\u4f5c\u591a\u5728\u51cc\u6668\u5230\u5929\u4eae\u8fd9\u4e00\u6bb5\u65f6\u95f4\u5b8c\u6210\u3002 \u3000\u3000\u4e00\u4e2a\u80f6\u5de5\u8981\u5272\u7684\u6811\u6570\u767e\u68f5\u5230\u5343\u6570\u68f5\u4e0d\u7b49\uff0c\u81ea\u7136\u8981\u8d77\u4e2a\u5927\u65e9\uff0c\u6478\u9ed1\u51fa\u53d1\uff0c\u5230\u6a61\u6797\u5b50\u91cc\uff0c\u9010\u68f5\u6811\u9010\u88f8\u6811\u7684\u5272\u53d6\u80f6\u6c41\u3002 \u3000\u3000\u6a61\u6811\u6797\u5360\u5730\u901a\u5e38\u90fd\u6781\u5e7f\uff0c\u4e14\u90fd\u5728\u8352\u90ca\uff0c\u6709\u7684\u662f\u690d\u5728\u5c71\u5761\u4e0a\uff0c\u5730\u50fb\u4eba\u7a00\uff0c\u534a\u591c\u5929\u6ca1\u4eae\u5c31\u8981\u5230\u56ed\u5b50\u91cc\u5de5\u4f5c\uff0c\u86c7\u866b\u9f20\u8681\u81ea\u662f\u53f8\u7a7a\u89c1\u60ef\uff0c\u4ee4\u4eba\u53d1\u6307\u7684\u4e8b\u4ef6\u4e5f\u4e0d\u7edd\u5982\u7f15\uff0c\u8981\u5e73\u5b89\u65e0\u4e8b\u5219\u8981\u9760\u571f\u5730\u62ff\u7763\u4fdd\u4f51\u4e86\u3002 \u3000\u3000\u901a\u5e38\uff0c\u540c\u4e00\u4e2a\u56ed\u4e18\u91cc\u7684\u80f6\u5de5\u90fd\u662f\u5148\u805a\u4e8e\u4e00\u5730\uff0c\u7136\u540e\u4e00\u9f50\u9a91\u811a\u8e0f\u8f66\u51fa\u53d1\u7684\u3002 \u3000\u3000\u5728\u90a3\u6837\u7684\u5c71\u8def\u91cc\uff0c\u552f\u6709\u811a\u8e0f\u8f66\u624d\u662f\u6700\u4e3a\u4fbf\u5229\u7684\u4ea4\u901a\u5de5\u5177\u3002 \u3000\u3000\u56e0\u6015\u868a\u866b\u54ac\u4f24\uff1b\u80f6\u5de5\u5927\u90fd\u6234\u80f6\u624b\u5957\uff0c\u7a7f\u80f6\u978b\uff0c\u5168\u8eab\u5957\u84dd\u8272\u7c97\u5e03\u8863\u3001\u88f9\u5934\u5dfe\u3001\u8fd8\u6234\u4e0a\u6697\u591c\u4f5c\u7167\u660e\u7528\u7684\u706b\u6c34\u706f\uff0c\u7136\u7136\u518d\u4ee5\u80f6\u5200\u8ddf\u6a61\u6811\u970d\u970d\u53ae\u78e8\u6574\u4e2a\u6f2b\u6f2b\u957f\u591c\u3002 \u3000\u3000\u4eca\u5929\uff0c\u963f\u8482\u4e5f\u4e0d\u4f8b\u5916\u3002 \u3000\u3000\u4e00\u7fa4\u5973\u80f6\u5de5\u6d69\u6d69\u8361\u8361\u7684\u51fa\u53d1\uff0c\u4e00\u5f00\u59cb\u65f6\u6574\u6761\u8def\u5c31\u662f\u5979\u4eec\u4e00\u5b57\u6a2a\u6392\u7684\u811a\u8e0f\u8f66\u3001\u6574\u4e2a\u591c\u91cc\u53ea\u5145\u65a5\u7740\u5979\u4eec\u5927\u58f0\u8c08\u7b11\u7684\u58f0\u97f3\u3002 \u3000\u3000\u7b49\u5230\u4e00\u4e0a\u4e86\u5927\u9a6c\u8def\uff0c\u56e0\u6015\u534a\u591c\u98de\u9a70\u800c\u8fc7\u7684\u8f66\u5b50\uff0c\u811a\u8e0f\u8f66\u53d8\u6210\u4e86\u4e00\u5b57\u76f4\u6392\uff0c\u4ecd\u7136\u9996\u5c3e\u547c\u5e94\uff1b\u6709\u65f6\u5019\u6700\u524d\u9762\u7684\u4eba\u5927\u58f0\u56b7\u4e00\u53e5\u8bdd\uff0c\u540e\u9762\u7684\u8f66\u5b50\u4e00\u4eba\u4f20\u4e00\u4eba\uff0c\u4e00\u76f4\u4f20\u7ed9\u7b2c\u4e8c\u5341\u4e94\u4eba\u542c\u3002 \u3000\u3000\u5230\u4e86\u56ed\u4e18\u4e4b\u540e\uff0c\u5404\u4eba\u5206\u522b\u628a\u8f66\u5934\u4e00\u8f6c\uff0c\u4e00\u58f0\u62dc\u62dc\u5c31\u5f80\u81ea\u5df1\u7684\u5de5\u4f5c\u5730\u8e29\u53bb\uff0c\u4e8e\u662f\u4eba\u6e10\u6e10\u53d8\u5f97\u5c11\u4e86\uff0c\u5269\u4e0b\u7684\u8111\u8e0f\u8f66\u7684\u8f66\u706f\u548c\u5979\u4eec\u5934\u4e0a\u7684\u706b\u6c34\u706f\u3001\u51e0\u70b9\u6655\u9ec4\uff0c\u7a7f\u63d2\u5728\u5bc6\u5bc6\u9ebb\u9ebb\u9759\u9759\u5bc2\u5bc2\u7684\u6a61\u6811\u6797\u95f4\u3002 \u3000\u3000\u963f\u8482\u5de5\u4f5c\u7684\u5730\u70b9\u662f\u5728\u6797\u5b50\u91cc\u6bd4\u8f83\u6df1\u8fdc\u7684\u5730\u65b9\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u53ea\u5269\u4e0b\u4e24\u540d\u5973\u5de5\u548c\u5979\u4e00\u8d77\u3002 \u3000\u3000\u5c71\u8def\u5d0e\u5c96\u3001\u8fd8\u6709\u4e0d\u5c11\u6811\u6839\u6d6e\u5728\u8def\u9762\u4e0a\uff0c\u8f66\u5b50\u4e00\u5dc5\u4e00\u98a0\u7684\uff0c\u5f88\u4e0d\u597d\u53d7\uff0c\u4e0d\u8fc7\u5979\u4eec\u56e0\u5e38\u5e74\u5de5\u4f5c\uff0c\u4e5f\u4e60\u4ee5\u4e3a\u5e38\u3002 \u3000\u3000\u5979\u4eec\u6b63\u5728\u7b11\u8c08\u7740\u963f\u82b3\u548c\u963f\u65fa\u7684\u5a5a\u4e8b\u3002 \u3000\u3000\u5728\u8fd9\u5c0f\u5c0f\u7684\u57ce\u9547\uff0c\u670b\u53cb\u51e0\u4e4e\u90fd\u662f\u5171\u540c\u7684\uff0c\u5f53\u7136\u4e5f\u6709\u7684\u662f\u5171\u540c\u7684\u8bdd\u9898\u3002 \u3000\u3000\u963f\u8482\u672c\u4e5f\u8bf4\u7740\u7b11\u7740\uff0c\u5ffd\u7136\uff0c\u5979\u89c9\u5f97\u524d\u9762\u7684\u6797\u5b50\u91cc\u597d\u50cf\u6709\u4ec0\u4e48\u4e1c\u897f\u4e00\u6643\u800c\u8fc7\u3002 \u3000\u3000\u8fd9\u65f6\u5019\u5927\u6982\u662f\u5b50\u591c\u4e09\u70b9\u591a\u56db\u65f6\u5427\uff0c\u5979\u4eec\u7684\u811a\u8e0f\u8f66\u706f\u6781\u5176\u5fae\u5f31\uff0c\u5145\u5176\u91cf\u53ea\u80fd\u7167\u89c1\u4e94\u5c3a\u4ee5\u5185\u7684\u8def\u9762\u60c5\u51b5\uff0c\u800c\u5979\u4eec\u4eec\u5934\u4e0a\u6234\u7684\u6c14\u706f\uff0c\u4e5f\u4ec5\u53ef\u7528\u4f5c\u7528\u660e\u624b\u8fb9\u7684\u5de5\u4f5c\u2014\u2014\u90a3\u4e8b\u7269\u81f3\u5c11\u5728\u8ddd\u79bb\u5979\u4e09\u53e3\u5341\u5c3a\u5916\u98d8\u8fc7\uff0c\u5979\u662f\u7edd\u5bf9\u6ca1\u6709\u7406\u7531\u770b\u6e05\u695a\u7684\u3002 \u3000\u3000\u8fd9\u4e0d\u7531\u5f97\u4f7f\u5979\u6014\u4e86\u4e00\u6014\u3002 \u3000\u3000\u2014\u2014\u5206\u660e\u770b\u89c1\u4e86\uff01 \u3000\u3000\u2014\u2014\u90a3\u8fd8\u662f\u4e2a\u767d\u8272\u7684\u5f71\u5b50\uff01 \u3000\u3000\u770b\u6765\u8fd8\u662f\u4e2a\u5973\u4eba\u7684\u5f71\u5b50\uff01 \u3000\u3000\u8fd9\u662f\u6beb\u65e0\u9053\u7406\u7684\uff01\u5979\u600e\u4e48\u770b\u5f97\u89c1\u5462\uff01\u534a\u591c\u4e09\u66f4\u7684\uff0c\u600e\u4f1a\u6709\u4e2a\u5973\u4eba\u5728\u8fd9\u5bc6\u6797\u91cc\u5462\uff01 \u3000\u3000\u5979\u60f3\u518d\u770b\uff0c\u5df2\u4ec0\u4e48\u90fd\u770b\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u6a61\u6797\u53c8\u56de\u590d\u4e00\u7247\u9edd\u6697\u6f06\u9ed1\uff0c\uff0c\u53ea\u6709\u5934\u4e0a\u7684\u6c14\u706f\u52c9\u529b\u63a8\u5f00\u6570\u5c3a\u9ed1\u5e55\uff0c\u4f59\u53c8\u544a\u4e4f\u529b\u5f92\u7136\u3002 \u3000\u3000\u201c\u600e\u4e48\u4e86\uff1f\u201d\u540c\u4f34\u73e0\u73e0\u53d1\u73b0\u5979\u6709\u4e9b\u602a\u5f02\u3002 \u3000\u3000\u201c\u4f60\u4eec\u6709\u6ca1\u6709\u770b\u89c1\uff1f\u201d \u3000\u3000\u201c\u770b\u89c1\u4ec0\u4e48\uff1f\uff1a\u4e24\u4e2a\u540c\u4f34\u90fd\u4e0d\u660e\u6240\u6307\u3002 \u3000\u3000\u201c\u4e00\u4e2a\u767d\u8272\u7684\u5f71\u5b50\u2026\u2026\u201d\u5979\u8bf4\uff0c\u7528\u624b\u6307\u7740\u90a3\u4e00\u7247\u5bc6\u6797\uff0c\u201c\u4e00\u4e2a\u767d\u8272\u7684\u5973\u4eba\uff01\u201d\u3000\u3000\u4e00\u201c\u8f66\uff01\u201d\u4e24\u4e2a\u5973\u4f34\u4e00\u9f50\u53eb\u4e86\u8d77\u6765\uff0c\u201c\u4f60\u53d1\u795e\u7ecf\u54ea\uff01\u201d \u3000\u3000\u5979\u4eec\u867d\u7136\u4ec0\u4e48\u4e5f\u6ca1\u770b\u89c1\uff0c\u3001\u4e0d\u8fc7\u603b\u6709\u4e9b\u5fc3\u5bd2\uff0c\u5728\u5357\u6d0b\u4e00\u5e26\u7684\u4f20\u8bf4\u91cc\uff0c\u534a\u519c\u4e09\u66f4\u89c1\u7740\u7a7f\u767d\u8863\u7684\u5973\u4eba\uff0c\u4e0d\u662f\u4ef6\u597d\u4e8b\uff0c\u8bb8\u591a\u610f\u5916\u90fd\u56e0\u6b64\u800c\u751f\uff0c\u6240\u4ee5\u5973\u4f34\u90fd\u53eb\u4e86\u4e00\u58f0\uff1a\u5927\u5409\u5229\u662f\uff01 \u3000\u3000\u963f\u8482\u81ea\u5df1\u4e5f\u83ab\u540d\u5176\u5999\uff1a\u201c\u4eca\u5929\u6211\u7684\u773c\u775b\u662f\u600e\u4e48\u4e86\uff1f\u8001\u662f\u2026\u2026\u201d \u3000\u3000\u5979\u7684\u5de5\u4f5c\u5730\u70b9\u5df2\u5230\u3002 \u3000\u3000\u4e24\u4e2a\u5973\u4f34\u8c03\u7b11\u5979\u8bf4\uff1a\u201c\u4f60\u89c1\u963f\u82b3\u51fa\u5ac1\u3001\u4e5f\u60f3\u5ac1\u60f3\u5f97\u53d1\u70e7\u5566\uff01\u201d\u201c\u522b\u80e1\u601d\u4e71\u60f3\u4e86\uff0c\u627e\u4e2a\u7537\u4eba\u5ac1\u4e86\u4ed6\u5427\uff01\u201d \u3000\u3000\u5979\u4eec\u5728\u9759\u591c\u7684\u5bc2\u6797\u91cc\u6beb\u65e0\u5fcc\u60ee\u7684\u6b22\u7b11\u7740\uff0c\u8e0f\u8f66\u800c\u53bb\u3002 \u3000\u3000\u963f\u8482\u89c1\u90a3\u4e24\u8f86\u811a\u8e0f\u8f66\u540e\u7684\u4e24\u70b9\u6697\u7ea2\u7684\u706f\uff0c\u8fd8\u6709\u56e0\u98a0\u7c38\u800c\u53d1\u51fa\u7684\u78b0\u649e\u58f0\uff0c\u6e10\u6e10\u8fdc\u53bb\uff0c\u76f4\u81f3\u4e3a\u9ed1\u6697\u6240\u541e\u6ca1\uff0c\u4ec0\u4e48\u4e5f\u770b\u4e0d\u89c1\uff0c\u4ec0\u4e48\u4e5f\u542c\u4e0d\u5230\u4e86\u3002 \u3000\u3000\u4e00\u5929\u7684\u5de5\u4f5c\u53c8\u8981\u5f00\u59cb\u4e86 \u3000\u3000\u5979\u4fef\u8eab\u6c99\u6c99\u7684\u5272\u7740\u6811\u76ae\uff0c\u628a\u4ee5\u524d\u6811\u5e72\u4e0a\u90a3\u4e00\u9053\u5012v\u5b57\u7684\u76ae\u6c9f\u624d\u8f7b\u8f7b\u522e\u53bb\u4e00\u4e9b\uff0c\u80f6\u6c41\u5c31\u4f1a\u4e00\u70b9\u70b9\u5192\u4e0a\u6765\uff0c\u6d41\u6ce8\u5230\u80f6\u676f\u91cc\u4e86\u3002 \u3000\u3000\u6c99\u6c99\u3001\u6c99\u6c99\u2026\u2026 \u3000\u3000\u53ea\u6709\u5934\u4e0a\u90a3\u4e00\u70b9\u706b\u6c34\u706f\u7684\u6697\u706b\uff0c\u8fd8\u6709\u8fd9\u4e00\u79cd\u5355\u8c03\u7684\u6c99\u6c99\u4e4b\u58f0\uff0c\u4ee5\u53ca\u51e0\u58f0\u53e4\u602a\u7684\u866b\u9e23\u4e4b\u5916\uff0c\u5176\u4f59\u7684\uff0c\u6574\u4e2a\u6811\u6797\u5c31\u50cf\u4e00\u53e3\u5927\u5e03\u888b\uff0c\u8c01\u4e5f\u4e0d\u77e5\u8fd8\u6709\u4ec0\u4e48\u3001\u6ca1\u6709\u4ec0\u4e48\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u963f\u8482\u4e5f\u4e60\u60ef\u4e86\u3002 \u3000\u3000\u53c8\u5272\u597d\u4e00\u68f5\u6811\u4e86\u2026\u2026\u5979\u8d77\u6765\uff0c\u8212\u8212\u8eab\u5b50\uff0c\u6b63\u8981\u8d70\u5411\u53e6\u4e00\u68f5\u6811\uff0c\u5ffd\u7136\u95f4\uff0c\u5934\u4e0a\u4e00\u9eef\u3002 \u3000\u3000\u706f\u6697\u4e86\u3002 \u3000\u3000\u4e00\u6ca1\u7406\u7531\u7684\uff01 \u3000\u3000\u5979\u8bb0\u5f97\u6dfb\u6ee1\u4e86\u706b\u9152\u624d\u51fa\u95e8\u6765\u7684\u3002 \u3000\u3000\u5979\u4e0b\u610f\u8bc6\u7684\u7528\u624b\u53bb\u6276\u6b63\u4e00\u4e0b\u5934\u4e0a\u7684\u706f\uff0c\u5ffd\u7136\uff0c\u706f\u5168\u6697\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u4e00\u79cd\u65e0\u53ef\u633d\u6551\u7684\u6697\u6de1\u3002 \u3000\u3000\u76f4\u81f3\u5168\u9ed1\u3002\u3000\u3000\u3001 \u3000\u3000\u8fd9\u4e00\u4e0b\u5b50\uff0c\u963f\u8482\u72b9\u8dcc\u843d\u5728\u9ed1\u6697\u91cc\uff0c\u5b8c\u5168\u8ddf\u9ed1\u6697\u878d\u4e3a\u4e00\u4f53\uff0c\u800c\u9ed1\u6697\u5c31\u4f3c\u662f\u51dd\u56fa\u4e86\u4f3c\u7684\u3002 \u3000\u3000\u5e78\u4e8f\u963f\u8482\u4e5f\u4e0d\u662f\u6ca1\u6709\u7ecf\u5386\u8fc7\u8fd9\u79cd\u60c5\u5883\u3002 \u3000\u3000\u5979\u6709\u7ecf\u9a8c\uff0c\u6240\u4ee5\u5e76\u4e0d\u592a\u614c\u5f20\u3002 \u3000\u3000\u5979\u53d6\u51fa\u4e86\u6253\u706b\u673a\u3002 \u3000\u3000\u201c\u556a\u201d\u7684\u4e00\u58f0\u3002 \u3000\u3000\u4e0d\u4eae\u3002 \u3000\u3000\u5979\u518d\u6253\u6253\u706b\u673a\u3002 \u3000\u3000\u53c8\u662f\u201c\u556a\u201d\u7684\u4e00\u58f0\u3002 \u3000\u3000\u4ecd\u662f\u4e0d\u4eae\u3002 \u3000\u3000\u5979\u8fde\u6253\u51e0\u6b21\uff0c\u90e8\u4e0d\u7740\u706b\uff0c\u5fc3\u4e2d\u5927\u5947\uff0c\u4e0d\u89c9\u7528\u624b\u4e00\u6478\u3002 \u3000\u3000\u6781\u75db\u7684\u611f\u89c9\uff1a\u3001\u4ee4\u5979\u98de\u7094\u7684\u7f29\u624b\u3002 \u3000\u3000\uff0d\uff0d\uff0d\uff0d\uff0d\u4e3a\u4ec0\u4e48\u4f1a\u8fd9\u6837\u5b50\uff1f \u3000\u3000\u5979\u7684\u6307\u5c16\u4f20\u6765\u7684\u75db\u7684\u611f\u89c9\u3002 \u3000\u3000\u96be\u9053\u706b\u5df2\u7ecf\u7740\u4e86\uff1f\u963f\u8482\u4e0d\u7531\u5f97\u614c\u5f20\u8d77\u6765\uff1a\u53ea\u662f\u6211\u770b\u4e0d\u89c1\u800c\u5df2\uff1f \u3000\u3000\u2014\u2014\u96be\u9053\u6211\u5df2\u778e\u4e86\uff01 \u3000\u3000\u201c\u6c99\u6c99\u3001\u6c99\u6c99\u3002\u3002\u3002\u201d \u3000\u3000\u2014\u2014\u8fd9\u662f\u4ec0\u4e48\u58f0\u97f3\uff1f \u3000\u3000\u8fd9\u8ddf\u5272\u80f6\u7684\u58f0\u97f3\u5341\u5206\u8fd1\u4f3c\u3002 \u3000\u3000\u53ea\u662f\u66f4\u731b\u70c8\u3001\u66f4\u6d69\u5927\u3002\u5982\u679c\u963f\u8482\u5272\u80f6\u7684\u58f0\u97f3\u6bd4\u4f5c\u662f\u4e00\u53ea\u8682\u8681\uff0c\u8fd9\u58f0\u97f3\u5374\u8fd1\u4f3c\u96c4\u5175\u3002 \u3000\u3000\u53ef\u662f\u963f\u8482\u5e76\u6ca1\u6709\u5272\u80f6\u6c34\uff01 \u3000\u3000\u5929\uff01\u96be\u9053\u8fd9\u9ed1\u6697\u7684\u80f6\u56ed\u91cc\uff0c\u6b63\u5728\u5e03\u6ee1\u7740\u4eba\u5272\u80f6\uff01 \u3000\u3000\u963f\u8482\u6050\u60e7\u5f97\u60f3\u53eb\u558a\uff0c\u4f46\u56e0\u592a\u8fc7\u60ca\u6050\u53cd\u800c\u53eb\u4e0d\u51fa\u58f0\u6765\uff0c\u3002 \u3000\u3000\u6c99\u6c99\u4e4b\u58f0\u66f4\u903c\u8fd1\u4e86\u3002 \u3000\u3000\u5979\u611f\u89c9\u5230\u70ed\u3002 \u3000\u3000\u706b\u7684\u611f\u89c9\u3002 \u3000\u3000\u963f\u8482\u60f3\u9003\u3002 \u3000\u3000\u53ef\u662f\u5728\u614c\u4e71\u95f4\uff0c\u5979\u4ec0\u4e48\u90fd\u770b\u4e0d\u89c1\u3002 \u3000\u3000\u5979\u4e5f\u627e\u4e0d\u5230\u5979\u7684\u811a\u8e0f\u8f66\u3002 \u3000\u3000\u7136\u540e\uff0c\u5979\u89c9\u5f97\u201c\u6c99\u6c99\u201d\u4e4b\u58f0\u5df2\u201c\u722c\u201d\u4e0a\u5979\u7684\u8863\u89d2\u3002 \u3000\u3000\u5979\u611f\u89c9\u5230\u9510\u70c8\u7684\u7684\u75db\uff0c\u8fd9\u4f7f\u5979\u7ec8\u4e8e\u80fd\u5c16\u547c\u51fa\u58f0\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u90a3\u6c99\u6c99\u4e4b\u58f0\u4e5f\u53d8\u6210\u4e86\u9192\u9192\u6050\u6050\u4e4b\u58f0\u5df2\u7ecf\u5ef6\u53ca\u4e86\u5979\u7684\u8eab\u4f53\uff0c\u71c3\u4e0a\u4e86\u5979\u7684\u8138\u90e8\u2014\u2014 \u3000\u3000\u6b7b\u4ea1\uff0c\u5982\u9ed1\u6697\u5979\u884c\u8fd1\uff0c\u5e26\u7740\u9707\u6016\u4e0e\u7684\u707c\u75db\u3002 \u3000\u3000\u9644\u8fd1\u7684\u5973\u5de5\u90fd\u542c\u5230\u90a3\u4f7f\u5979\u4eec\u7ec8\u751f\u96be\u5fd8\u7684\u4fee\u547c\u58f0\u3002 \u3000\u3000\u5f53\u5979\u4eec\u805a\u62e2\u8d76\u53bb\u7684\u65f6\u5019\uff0c\u53ea\u770b\u5230\u4e00\u5177\u70e7\u7126\u4e86\u7684\u8eab\u4f53\uff0c\u9644\u8fd1\u8fd8\u6709\u51e0\u68f5\u70e7\u574f\u4e86\u7684\u704c\u6728\u3002 \u3000\u3000\u521a\u624d\u8fd8\u5343\u5a07\u767e\u5a9a\u8a00\u7b11\u664f\u664f\u7684\u963f\u8482\uff0c\u4e00\u4e0b\u5b50\u88ab\u70e7\u6210\u4e86\u4e00\u5177\u60e8\u4e0d\u5fcd\u7779\u7684\u7126\u5c38\uff0c\u8fd9\u4e5f\u662f\u5979\u4eec\u6bd5\u751f\u96be\u5fd8\u7684\u60c5\u666f\u3002 \u3000\u3000\u8ddd\u963f\u8482\u88ab\u201c\u602a\u706b\u201d\u70e7\u6b7b\u7684\u4e8b\u4ef6\u540e\u5341\u4e8c\u5929\u3002\u5728\u9644\u8fd1\u5c71\u57ce\u91cc\u7684\u5fb7\u53d4\uff0c\u559d\u4e86\u4e00\u70b9\u6930\u82b1\u9152\uff0c\u4e00\u6447\u4e09\u6643\u7684\u8d70\u53bb\u201c\u4e92\u52a9\u56e2\u201d\u770b\u66f4\u3002 \u3000\u3000\u4ed6\u559c\u6b22\u559d\u6930\u82b1\u9152\uff0c\u9664\u4e86\u56e0\u4e3a\u7279\u522b\u4fbf\u5b9c\uff0c\u8fd8\u56e0\u4e3a\u90a3\u4e00\u80a1\u515c\u820c\u7684\u9178\u5473\u3002 \u3000\u3000\u800c\u4e14\uff0c\u559d\u6930\u82b1\u9152\u53ef\u4ee5\u8ba9\u4ed6\u60f3\u8d77\uff0c\u5f53\u65e5\u5728\u5c71\u6797\u91cc\u8ddf\u65e5\u672c\u4ed4\u6253\u6e38\u51fb\u7684\u65f6\u5019\uff0c\u4ed6\u6bcf\u5230\u4e00\u4e2a\u5370\u5ea6\u4eba\u7684\u5c0f\u6751\u843d\uff0c\u6751\u4eba\u90fd\u89c6\u4ed6\u4e3a\u82f1\u96c4\uff0c\u4ed6\u5c31\u662f\u4e00\u9762\u559d\u7740\u6930\u82b1\u9152\uff0c\u66fe\u8bd5\u8fc7\u4e00\u665a\u62e5\u62b1\u8fc7\u4e09\u4e2a\u5973\u4eba\u3002 \u3000\u3000\u8fc7\u53bb\u98ce\u5149\u4e0d\u518d\u3002 \u3000\u3000\u5728\u5fb7\u53d4\u5fc3\u76ee\u4e2d\uff0c\u5f80\u65e5\u90fd\u662f\u7f8e\u597d\u7684\u53e3\u5fc6\u3002 \u3000\u3000\u53ea\u6709\u73b0\u5728\u4e0d\u597d\u3002 \u3000\u3000\u6253\u5b8c\u4ed7\u4e86\uff0c\u8fd9\u5730\u65b9\u7e41\u8363\u4e86\uff0c\u81ea\u5df1\u5374\u4f3c\u9000\u5316\u4e86\u3001\u843d\u4f0d\u4e86\u3002 \u3000\u3000\u2014\u2014\u4e24\u4e2a\u9ec4\u8138\u5a46\uff0c\u516b\u4e2a\u5b50\u5973\u3002 \u3000\u3000\u2014\u2014\u6709\u4ec0\u4e48\u4e8b\uff0c\u6bd4\u4e00\u4e2a\u4e0d\u597d\u770b\u7684\u8001\u5a46\u66f4\u65e0\u763e\uff1f \u3000\u3000\u2014\u2014\u5f53\u7136\u662f\u4e24\u4e2a\u4e11\u8001\u5a46\uff01 \u3000\u3000\u4fd7\u8bed\u8bf4\uff1a\u201c\u4e00\u4e2a\u5f2f\u8170\uff0c\u4e24\u4e2a\u9a7c\u80cc\uff0c\u4e09\u4e2a\u65ad\u62c5\u6311\u201d\uff0c\u5fb7\u53d4\u81ea\u7136\u4e0d\u4f1a\u63a8\u5c61\u5230\u53ef\u4ee5\u4e71\u6027\u7684\u6930\u82b1\u9152\u4e0a\uff0c\u4ed6\u6709\u516b\u4e2a\u5b69\u5b50\uff0c\u4f7f\u4ed6\u4e0d\u5f97\u4e0d\u5728\u81ea\u5929\u66ff\u4eba\u8865\u978b\u4e4b\u4f59\u3002\u665a\u4e0a\u66f4\u517c\u4e86\u8fd9\u4e00\u4efd\u201c\u4e92\u52a9\u56e2\u201d\u7684\u5b88\u671b\u5de5\u4f5c\u3002 \u3000\u3000\u56e0\u4e3a\u8fd9\u4e00\u5e26\u5730\u533a\u4e0d\u5927\u5e73\u9756\uff0c\u5e73\u65f6\u5e38\u6709\u52ab\u532a\u51fa\u6765\u6d3b\u52a8\uff0c\u5c71\u533a\u91cc\u53ef\u80fd\u8fd8\u6709\u4e9b\u672a\u88ab\u527f\u706d\u7684\u6e38\u51fb\u961f\u6f5c\u4f0f\uff0c\u8fd1\u6d77\u53c8\u6709\u6765\u81ea\u5370\u5c3c\u7684\u975e\u6cd5\u79fb\u6c11\uff0c\u4e8e\u662f\u5f53\u5730\u653f\u5e9c\uff0c\u6210\u7acb\u7ec4\u5408\u4e86\u201c\u4e92\u52a9\u56e2\u201d\uff0c\u5b97\u65e8\u662f\uff1a \u3000\u3000\u5b88\u91cc\u76f8\u52a9\uff0c\u4ee5\u9632\u4e00\u65e6\u6709\u4e2a\u4ec0\u4e48\uff0c\u53ca\u65e9\u793a\u8b66\u3002 \u3000\u3000\u5fb7\u53d4\u662f\u4e92\u52a9\u56e2\u7684\u770b\u66f4\u4e4b\u4e00\uff0c\u8fd9\u662f\u6c11\u95f4\u56e2\u4f53\uff0c\u6ca1\u6709\u67aa\uff0c\u53ea\u6709\u54e8\u68cd\u3002 \u3000\u3000\u8fd9\u4e00\u4e2a\u4e86\u671b\u5ba4\u5c31\u8bbe\u5728\u68d5\u6cb9\u6811\u6797\u4e4b\u524d\uff0c\u5728\u665a\u4e0a\u53ea\u6709\u5b64\u96f6\u96f6\u7684\u4e00\u53ea\u65e5\u5149\u706f\u4eae\u7740\uff0c\u663e\u5f97\u4efd\u5916\u8352\u51c9\u3002 \u3000\u3000\u5fb7\u53d4\u4e0d\u7ba1\u3002 \u3000\u3000\u53cd\u6b63\u4ed6\u65e0\u6240\u8c13\u3002 \u3000\u3000\u4eca\u665a\u5fb7\u53d4\u662f\u65e9\u5230\u4e86\u4e9b\uff0c\u624b\u4e0a\u8fd8\u62ff\u4e86\u74f6\u6930\u82b1\u9152\uff0c\u7ecf\u8fc7\u8857\u4e0a\u7684\u65f6\u5019\uff0c\u4e0d\u77e5\u600e\u7684\uff0c\u624b\u8098\u7ed9\u649e\u4e86\u4e00\u4e0b\uff0c\u9152\u6ea2\u51fa\uff0c\u6e85\u53ca\u4e86\u773c\u775b\u3002 \u3000\u3000\u5fb7\u53d4\u4e00\u9762\u64e6\u773c\u4e00\u9762\u5927\u9a82\uff1a\u201c\u6b7b\u592d\u5bff\uff0c\u8d70\u8def\u4e0d\u957f\u773c\u775b\u2026\u2026\u201d\u3002 \u3000\u3000\u5176\u5b9e\u4ed6\u662f\u4e60\u60ef\u8bf4\u51e0\u53e5\u7c97\u53e3\uff0c\u4e5f\u4e0d\u662f\u771f\u7684\u60f3\u9a82\u4eba\uff0c\u53cd\u6b63\u773c\u4e5f\u4e0d\u5927\u75db\uff0c\u5f53\u7136\u4e5f\u6839\u672c\u4e0d\u4f1a\u6709\u4eba\u6545\u610f\u3002 \u3000\u3000\u4ed6\u60f3\u65e9\u4e9b\u95f4\u5230\u201c\u4e92\u52a9\u56e2\u201d\u7684\u5b88\u671b\u5ba4\u91cc\uff0c\u65e9\u4e9b\u628a\u9152\u559d\u5149\uff0c\u4e0d\u7136\uff0c\u5496\u5561\u660e\u548c\u7403\u4ed4\u6765\u65f6\uff0c\u4ed6\u4eec\u5c31\u4e0d\u8bb8\u4ed6\u5728\u5de5\u4f5c\u65f6\u559d\u9152\u4e86\u3002 \u3000\u3000\u2014\u2014\u6211\u73b0\u5728\u559d\uff0c\u4f60\u4eec\u6765\u65f6\uff0c\u95fb\u5230\u6211\u4e00\u53e3\u9152\u6c14\uff0c\u4f46\u5c31\u662f\u5948\u4e0d\u4e86\u6211\u4f55\uff01 \u3000\u3000\u60f3\u5230\u8fd9\u70b9\uff0c\u5fb7\u53d4\u5c31\u5f97\u610f\u5730\u7b11\u4e86\u3002 \u3000\u3000\u50cf\u4ed6\u8fd9\u79cd\u4eba\uff0c\u8981\u6d3b\u4e0b\u53bb\uff0c\u81ea\u7136\u5f97\u8981\u61c2\u5f97\u968f\u65f6\u968f\u5730\u627e\u5f00\u5fc3\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\u5019\uff0c\u4ed6\u4e0d\u7ecf\u610f\u5730\u5411\u767e\u53f6\u7a97\u53e3\u4e00\u671b\uff0c\u77a5\u89c1\u5728\u68d5\u6401\u56ed\u4e18\u7684\u6c99\u8def\u4e0a\uff0c\u6709\u4e00\u4e2a\u3002 \u3000\u3000\u4e00\u4e2a\u5973\u4eba\u3002 \u3000\u3000\u4e00\u4e2a\u7a7f\u767d\u8863\u670d\u7684\u5973\u4eba\u3002 \u3000\u3000\uff12\u3001\u9ed1\u8272\u7684\u73e0\u5b9d \u3000\u3000\u4e09\u66f4\u534a\u591c\u7684\uff0c\u600e\u4f1a\u6709\u4e2a\u5973\u4eba\u4ec1\u7acb\u5728\u90a3\u513f\u5462\uff1f \u3000\u3000\u5fb7\u53d4\u89c9\u5f97\u5f88\u72d0\u7591 \u3000\u3000\u4e07\u7c41\u4ff1\u5bc2\uff0c\u8352\u90ca\u91ce\u9053\uff0c\u4e00\u4e2a\u5973\u5b69\u5b50\u5728\u8fd9\u91cc\u5e72\u5417\uff1f \u3000\u3000\u83ab\u4e0d\u662f\u6765\u81ea\u5bfb\u77ed\u89c1\u7684\uff1f\u4e00\u4e2a\u5355\u8eab\u5973\u5b50\u6df1\u591c\u6765\u5230\u8fd9\u79cd\u5730\u65b9\uff0c\u5c31\u7b97\u4e0d\u6015\u52ab\u532a\u4e5f\u8be5\u5c0f\u5fc3\u8272\u72fc\u5440\u2026\u2026\u60f3\u5230\u8fd9\u91cc\uff0c\u559d\u8fc7\u9152\u540e\u7684\u5fb7\u53d4\uff0c\u5c31\u88ab\u4e24\u79cd\u60f3\u6cd5\u70e6\u7f20\u7740\uff0c\u4e00\u662f\u90a3\u5973\u5b50\u53ef\u80fd\u8981\u81ea\u6740\uff0c\u4ed6\u5f88\u5e94\u8be5\u53bb\u963b\u6b62\uff0c\u8fd9\u662f\u5584\u5fc3\u7684\uff1b\u4e00\u662f\u5f53\u4ed6\u60f3\u8d77\u5728\u8fd9\u534a\u591c\u65e0\u4eba\u7684\u5149\u666f\u91cc\u8fd9\u5973\u5b50\u8fd8\u6562\u51fa\u73b0\u5728\u8fd9\u91cc\uff0c\u516b\u6210\u4e0d\u662f\u4ec0\u4e48\u6b63\u7ecf\u4eba\uff0c\u4ed6\u60f3\u5230\u201c\u8272\u72fc\u201d\u7684\u65f6\u5019\u81ea\u5df1\u540c\u65f6\u4e5f\u8272\u5fc3\u5927\u8d77\u3002 \u3000\u3000\u4ed6\u51b3\u5b9a\u8981\u8fc7\u53bb\u770b\u770b\u3002 \u3000\u3000\u6b63\u8d70\u51fa\u90a3\u4e86\u671b\u5c97\u7684\u65f6\u5019\uff0c\u5496\u5561\u660e\u548c\u7403\u4ed4\u521a\u597d\u8fc7\u6765\u503c\u591c\uff0c\u8fce\u9762\u770b\u89c1\u5fb7\u53d4\u8d70\u51fa\u6765\uff0c\u5c31\u95fb\u5230\u4ed6\u51b2\u9f3b\u7684\u9152\u5473\u3002 \u3000\u3000\u201c\u53c8\u559d\u9152\u4e86\uff01\u201d\u7403\u4ed4\u6ca1\u597d\u6c14\u3002 \u3000\u3000\u201c\u600e\u6837\uff1f\u5b88\u591c\u5566\uff0c\u8fd8\u8981\u53bb\u54ea\u91cc\uff1f\u201d\u5496\u5561\u660e\u89c1\u5fb7\u53d4\u62ff\u7740\u54e8\u68cd\uff0c\u4e00\u6447\u4e09\u6643\u7684\u8d70\u51fa\u6765\uff0c\u987a\u53e3\u95ee\u4e86\u4e00\u53e5\u3002 \u3000\u3000\u201c\u6211\uff1f\u201d\u5fb7\u53d4\u6307\u7740\u81ea\u5df1\u7684\u9152\u7cdf\u9f3b\uff0c\u9732\u51fa\u4e86\u6ee1\u53e3\u9ec4\u7259\uff1a\u201c\u6211\u5c31\u662f\u8981\u53bb\u6267\u884c\u5b88\u591c\u7684\u4efb\u52a1\u3002\u4f60\u6ca1\u770b\u89c1\u5417\uff1f\u6211\u53bb\u627e\u90a3\u4e2a\u767d\u8272\u7684\u5973\u4eba\uff1f\u201d \u3000\u3000\u201d \u3000\u3000\u7403\u4ed4\u6b63\u63e9\u7740\u773c\u775b\uff0c\u6ca1\u5728\u610f\uff0c\u6545\u800c\u4e00\u7b11\u7f6e\u4e4b\uff0c\u4ee5\u4e3a\u5fb7\u53d4\u53c8\u559d\u591a\u4e86\u9152\uff0c\u80e1\u8a00\u4e71\u8bed\u3002 \u3000\u3000\u5496\u5561\u660e\u5947\u9053\uff1a\u201c\u767d\u8272\u7684\u5973\u4eba\uff1f\u201d\u4fbf\u89c1\u5fb7\u53d4\u5f80\u6c99\u539f\u4e0a\u8d70\u53bb\u3002\u8fdc\u5904\u4f3c\u4e4e\u771f\u6709\u4e2a\u767d\u8272\u7684\u5f71\u5b50\uff0c\u770b\u53bb\u4f3c\u5728\u865a\u65e0\u98d8\u6e3a\u95f4\uff0c\u6709\u70b9\u7eb3\u95f7\uff0c\u4ee5\u4e3a\u662f\u5fb7\u53d4\u7684\u5bb6\u4eba\u670b\u53cb\uff0c\u4e5f\u6ca1\u591a\u52a0\u6ce8\u610f\u3002 \u3000\u3000\u7403\u4ed4\u6b63\u5f00\u7740\u4e86\u6536\u97f3\u673a\uff0c\u4e00\u9762\u542c\u4e00\u9762\u6253\u7740\u62cd\u5b50\u3002 \u3000\u3000\u5496\u5561\u660e\u53bb\u627e\u6c34\u559d\uff0c\u53d1\u73b0\u90fd\u662f\u9694\u591c\u7684\u8336\uff0c\u5478\u4e86\u4e00\u58f0\uff1a\u201c\u6b7b\u5fb7\u53d4\uff0c\u5148\u6765\u4e5f\u4e0d\u70e7\u5f00\u6c34\uff0c\u6e34\u6b7b\u4e86\uff01\u201d \u3000\u3000\u7403\u4ed4\u5fc3\u4e0d\u5728\u7109\u7684\u6f2b\u58f0\u8bf4\uff1a\u201c\u4ed6\u4e0d\u70e7\u4f60\u70e7\u5440\u2026\u2026\u6696\uff0c\u6700\u597d\u6ce1\u58f6\u5496\u5561\uff0c\u6ce1\u5496\u5561\u4f60\u5496\u5561\u660e\u662f\u6700\u62ff\u624b\u7684\u5566\uff01\u201d \u3000\u3000\u5496\u5561\u660e\u5550\u9053\uff1a\u201c\u5c11\u7237\uff0c\u8981\u51b2\u5496\u5561\u4f60\u81ea\u5df1\u4e0d\u4f1a\u51b2\uff0c\u4f60\u5c31\u53ea\u4f1a\u542c\u97f3\u4e50\u5440\uff01\u201d \u3000\u3000\u201c\u957f\u591c\u6f2b\u6f2b\u5440\uff0c\u201d\u7403\u4ed4\u5b66\u7740\u767d\u5149\u7684\u58f0\u97f3\uff0c\u62b1\u7740\u6536\u97f3\u673a\u50cf\u62b1\u7740\u4e00\u4e2a\u6d3b\u8272\u751f\u9999\u7684\u7f8e\u5973\uff0c\u602a\u58f0\u602a\u6c14\u7684\u5531\uff1a\u201c\u5982\u679c\u6ca1\u6709\u4f60\uff0c\u65e5\u5b50\u600e\u4e48\u8fc7\u2026\u2026\u201d \u3000\u3000\u5496\u5561\u660e\u4e00\u8fb9\u6b63\u51c6\u5907\u8981\u70e7\u5f00\u6c34\uff0c\u53ef\u662f\u53c8\u627e\u4e0d\u5230\u706b\u67f4\u6765\u70b9\u71c3\u7164\u6c14\uff0c\u4e00\u8fb9\u6f2b\u4e0d\u7ecf\u5fc3\u7684\u95ee\uff1a\u201c\u8981\u4e0d\u8981\u4e5f\u66ff\u5fb7\u53d4\u6ce1\u4e00\u676f\uff1f\u201d \u3000\u3000\u7403\u4ed4\u628a\u811a\u653e\u5230\u684c\u9762\u4e0a\uff0c\u51f3\u5b50\u524d\u811a\u4e5f\u7fd8\u4e86\u8d77\u6765\uff0c\u60a0\u54c9\u6e38\u54c9\u7684\u8bf4\uff1a\u201c\u4ed6\u5440\uff0c\u6709\u9152\u5c31\u5f97\u4e86\uff0c\u8fd8\u559d\u4ec0\u4e48\u2026\u2026\u201d \u3000\u3000\u4e00\u9762\u8bf4\u7740\uff0c\u4e00\u9762\u4e0d\u7ecf\u610f\u7684\u671b\u5411\u7a97\u5916\u3002 \u3000\u3000\u4ed6\u5c31\u770b\u89c1\u4e00\u4e2a\u4ed6\u4e00\u8f88\u5b50\u90fd\u5fd8\u4e0d\u4e86\u7684\u666f\u8c61 \u3000\u3000\u5fb7\u53d4\u5728\u6c99\u539f\u4e0a\uff0c\u4f3c\u4e4e\u6b63\u5728\u8ddf\u4e00\u4e2a\u865a\u6643\u6643\u7684\u767d\u8272\u5f71\u5b50\u8bf4\u8bdd\u3002 \u3000\u3000\u7136\u540e\u5fb7\u53d4\u5ffd\u7136\u56de\u5934\uff0c\u5f80\u8fd9\u8fb9\u72c2\u5954\u3002 \u3000\u3000\u7531\u4e8e\u76f8\u9694\u5f97\u6781\u8fdc\uff0c\u53ef\u662f\u72b9\u53ef\u4ee5\u611f\u89c9\u5230\uff0c\u5fb7\u53d4\u56e0\u592a\u8fc7\u60ca\u5fc3\u52a8\u9b44\uff0c\u4ee5\u81f4\u6574\u5f20\u8138\u5b54\uff0c\u90fd\u5df2\u6b6a\u66f2\u53d8\u5f62\u3002 \u3000\u3000\u7a76\u7adf\u5fb7\u53d4\u770b\u89c1\u4e86\u4ec0\u4e48\uff0c\u624d\u4f1a\u8fd9\u822c\u6050\u60e7\u5462\uff1f\u7403\u4ed4\u4e0d\u77e5\u9053\uff0c\u53ef\u662f\uff0c\u63a5\u4e0b\u6765\uff0c\u5fb7\u53d4\u6574\u4e2a\u4eba\uff0c\u90fd\u4f3c\u878d\u5316\u5728\u9ed1\u591c\u91cc\uff0c\u4ed6\u75af\u72c2\u7684\u6251\u6253\u7740\uff0c\u72b9\u5982\u4e00\u5927\u7fa4\u864e\u8702\u6b63\u5728\u566c\u5411\u4ed6\uff0c\u51c4\u53eb\u58f0\u53ea\u6015\u5728\u4e03\u91cc\u5916\u7684\u4eba\u90fd\u53ef\u4ee5\u542c\u5f97\u5230\u3002 \u3000\u3000\u5fb7\u53d4\u7684\u8eab\u4f53\uff0c\u4e5f\u4f3c\u5728\u7d2f\u591c\u4e2d\u88ab\u5543\u8680\u7740\uff0c\u591c\u4e4c\u4e4c\u7684\u4f3c\u7f20\u7ed5\u7740\u51e0\u6761\u5de8\u87d2\uff0c\u65e0\u8bba\u5fb7\u53d4\u518d\u600e\u4e48\u6251\u6253\u6323\u624e\uff0c\u90fd\u65e0\u6cd5\u6323\u8bf4\u90a3\u53ef\u6016\u7684\u7ea0\u7f20\u3002 \u3000\u3000\u7403\u4ed4\u6574\u4e2a\u4eba\u6014\u4f4f\u4e86\u3002 \u3000\u3000\u4e5f\u6574\u4e2a\u4eba\u50f5\u4f4f\u4e86\u3002 \u3000\u3000\u8fd9\u666f\u8c61\u90a3\u4e48\u7684\u6050\u6016\uff0c\u81f4\u4f7f\u4ed6\u6401\u5728\u684c\u4e0a\u7684\u53cc\u817f\u5b8c\u5168\u50f5\u786c\uff0c\u5934\u76ae\u53d1\u70b8\uff0c\u4e00\u65f6\u7adf\u5fd8\u4e86\u53bb\u7559\u610f\u90a3\u4e2a\u767d\u8272\u7684\u4eba\uff0c\u8fd8\u5728\u4e0d\u5728\u73b0\u573a\u3002 \u3000\u3000\u5fb7\u53d4\u54c0\u53f7\u7740\uff0c\u5411\u54e8\u5c97\u5954\u6765\u3002 \u3000\u3000\u900f\u8fc7\u5c97\u54e8\u7684\u65e5\u5149\u706f\u4e00\u7167\uff0c\u7403\u4ed4\u53ef\u4ee5\u9690\u7ea6\u5730\u770b\u89c1\uff0c\u5fb7\u53d4\u8eab\u4e0a\u50cf\u957f\u4e86\u6570\u5341\u84ec\u8349\u3002 \u3000\u3000\u6d77\u85fb\u822c\u4f3c\u7684\u8349\u3002 \u3000\u3000\u9ed1\u8272\u7684\u8349\uff01 \u3000\u3000\u8fd9\u65f6\uff0c\u5496\u5561\u660e\u4e5f\u542c\u5230\u6709\u5f02\u58f0\uff0c\u5fd9\u4ece\u5185\u5954\u51fa\u6765\uff0c\u6025\u95ee\uff1a\u201c\u53d1\u751f\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u7531\u4e8e\u7403\u4ed4\u5df2\u592a\u8fc7\u60ca\u6115\uff0c\u5496\u5561\u660e\u7684\u58f0\u97f3\u7a81\u7136\u81ea\u540e\u54cd\u8d77\uff0c\u5413\u4e86\u4ed6\u4e00\u5927\u8df3\uff0c\u4ee5\u81f4\u53cc\u811a\u4e00\u98a4\uff0c\u5931\u5374\u5e73\u8861\uff0c\u8fde\u4eba\u5e26\u6905\u5f80\u540e\u8dcc\u4e86\u4e2a\u4ef0\u516b\u53c9\uff01 \u3000\u3000\u8fd9\u53ef\u628a\u5496\u5561\u660e\u4e5f\u5413\u4e86\u4e00\u5927\u8df3\uff0c\u5fd9\u628a\u7403\u4ed4\u6276\u8d77\uff0c\u7403\u4ed4\u53ea\u6307\u7740\u7a97\u5916\uff0c\u8bf4\u4e86\u5341\u51e0\u4e2a\u5b57\u90fd\u62fc\u51d1\u4e0d\u6210\u4e00\u53e5\uff0c\u201c\u5916\u2026\u2026\u9762\u2026\u2026\u5929\u2026\u2026\u5fb7\u2026\u2026\u53d4\u2026\u2026\u4ed6\u2026\u2026\u4ed6\u2026\u2026\u4e0d\u77e5\u2026\u2026\u505a\u2026\u2026\u4ec0\u4e48\u2026\u2026\u201d \u3000\u3000\u5496\u5561\u660e\u4e5f\u770b\u5230\u90a3\u53ef\u6016\u7684\u60c5\u666f\u4e86\u3002 \u3000\u3000\u5fb7\u53d4\u5df2\u5954\u8fd1\uff0c\u8138\u5bb9\u5df2\u75db\u82e6\u5230\u6781\u70b9\uff0c\u773c\u770b\u8981\u63a5\u8fd1\u54e8\u5c97\u7684\u65f6\u4faf\uff0c\u7ec8\u4e8e\u652f\u6301\u4e0d\u4f4f\uff0c\u8fde\u540c\u7f20\u5728\u4ed6\u8eab\u4e0a\u9ed1\u591c\u822c\u7684\u201c\u6d77\u85fb\u201d\uff0c\u4e00\u8d77\u5012\u4e86\u4e0b\u6765\uff0c\u5f84\u81ea\u5728\u5730\u4e0a\u60e8\u53f7\u7740\uff0c\u7ffb\u6eda\u7740\u3001\u6323\u624e\u7740\u3002 \u3000\u3000\u5496\u5561\u660e\u6bd4\u7403\u4ed4\u9547\u5b9a\uff0c\u6284\u8d77\u4e00\u67c4\u5df4\u51b7\u5200\u5c31\u51b2\u4e86\u51fa\u53bb\uff0c\u53ef\u662f\uff0c\u5230\u4e86\u63a5\u8fd1\u7684\u65f6\u5019\uff0c\u4e5f\u53ea\u6709\u5446\u7acb\u5f53\u5802\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u53d1\u73b0\u5728\u5fb7\u53d4\u8eab\u4e0a\u7f20\u7ed5\u7684\u201c\u4e8b\u7269\u201d\uff0c\u662f\u4f1a\u95ea\u6643\u3001\u6447\u52a8\u3001\u541e\u5410\u7684\uff0c\u4f3c\u6709\u82e5\u65e0\uff0c\u7ecf\u706f\u5149\u4e00\u7167\uff0c\u5496\u5561\u660e\u5df2\u7ecf\u53ef\u4ee5\u80af\u5b9a\u4e86\u4e00\u4ef6\u4e8b\uff1a \u3000\u3000\u2014\u2014\u90a3\u662f\u706b\u3002 \u3000\u3000\u9ed1\u8272\u7684\u706b\u3002 \u3000\u3000\u5496\u5561\u660e\u751a\u81f3\u53ef\u4ee5\u611f\u53d7\u5f97\u5230\uff0c\u90a3\u706b\u7684\u70ed\u529b\u3002 \u3000\u3000\u5e7d\u5f02\u7684\u70ed\u529b\uff01 \u3000\u3000\u7403\u4ed4\u548c\u5496\u5561\u660e\u4eb2\u773c\u770b\u89c1\u4e86\u201c\u9ed1\u8272\u7684\u706b\u201d\u3002 \u3000\u3000\u4ed6\u4eec\u4e5f\u4eb2\u773c\u770b\u89c1\u201c\u9ed1\u706b\u201d\u70e7\u6b7b\u4e86\u5fb7\u53d4\u3002 \u3000\u3000\u2014\u2014\u6d3b\u751f\u751f\u70e7\u6b7b\u4e86\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u963f\u8482\u6b7b\u7684\u65f6\u5019\uff0c\u5973\u80f6\u5de5\u4eec\u90fd\u770b\u4e0d\u5230\u706b\u5149\uff0c\u53ef\u662f\u963f\u8482\u662f\u7ed9\u9976\u6b7b\u7684\u3002 \u3000\u3000\u4e5f\u66fe\u6709\u4eba\u60f3\u8fc7\uff0c\u90a3\u706b\u4f1a\u4e0d\u4f1a\u662f\u65e0\u8272\u7684\uff0c\u53ef\u662f\u8fd9\u60f3\u6cd5\u592a\u8352\u8c2c\uff0c\u6839\u672c\u6ca1\u6709\u4eba\u6562\u76f8\u4fe1\u4f1a\u6709\u8fd9\u79cd\u706b\u3002 \u3000\u3000\u6709\u4eba\u751a\u81f3\u63a8\u6d4b\u963f\u8482\u662f\u7740\u4e86\u96f7\u5288\u2014\u2014\u53ef\u662f\u90a3\u4e00\u665a\uff0c\u8c01\u4e5f\u6ca1\u89c9\u5bdf\u66fe\u6709\u8fc7\u95ea\u7535\u3002 \u3000\u3000\u73b0\u5728\u5fb7\u53d4\u7684\u6b7b\uff0c\u5374\u6709\u4e24\u4e2a\u4eba\u4eb2\u773c\u76ee\u7779\u3002 \u3000\u3000\u771f\u7684\u6709\u706b\uff01 \u3000\u3000\u9ed1\u706b\uff01 \u3000\u3000\u2014\u2014\u8fd9\u706b\u4eff\u4f5b\u662f\u5730\u72f1\u91cc\u7684\u5996\u706b\uff0c\u6ca1\u6709\u5149\uff0c\u8fde\u70ed\u529b\u4e5f\u5e26\u7740\u68ee\u51b7\u3002 \u3000\u3000\u6d88\u606f\u5f88\u7094\u7684\u5c31\u4f20\u5f00\u4e86\u3002 \u3000\u3000\u9ed1\u8272\u7684\u706b\u662f\u4e00\u79cd\u5996\u90aa\u3002 \u3000\u3000\u2014\u2014\u800c\u9664\u4e86\u9ed1\u706b\u4e4b\u5916\uff0c\u5927\u5bb6\u6ca1\u6709\u6f0f\u4e86\u53e6\u4e00\u4e2a\u8be1\u5f02\u7684\u73b0\u8c61\uff1a\u5728\u9ed1\u706b\u53d1\u751f\u4e4b\u524d\uff0c\u8fd8\u6709\u4e00\u6761\u81ea\u8272\u7684\u5f71\u5b50\u3002 \u3000\u3000\u767d\u8272\u7684\u5973\u4eba\uff01 \u3000\u3000\u5fb7\u53d4\u548c\u963f\u8482\u79bb\u5947\u60e8\u6b7b\u4e4b\u540e\uff0c\u8fd9\u56db\u4e2a\u6708\u6765\uff0c\u9646\u9646\u7eed\u7eed\u6bc1\u5728\u201c\u9ed1\u706b\u201d\u4e4b\u4e0b\u7684\u4eba\uff0c\u81f3\u5c11\u6709\u4e5d\u4e2a\u4eba\u3002 \u3000\u3000\u9ed1\u706b\u90fd\u5728\u9eef\u591c\u91cc\u53d1\u751f\u3002 \u3000\u3000\u4eba\u4eec\u4e89\u76f8\u8d70\u62a5\uff0c\u95fb\u98ce\u8272\u53d8\uff0c\u5728\u8fd9\u4e00\u5e26\u5341\u51e0\u4e2a\u5e02\u9547\u91cc\u3001\u6562\u6df1\u591c\u51fa\u6765\u7684\u4eba\u5c31\u66f4\u5c11\u4e86\uff0c\u4ee5\u81f4\u672c\u6765\u5c31\u6709\u4e9b\u8427\u6761\u51c4\u51c9\u7684\u591c\u8857\u4e0a\uff0c\u66f4\u52a0\u51c4\u51c9\u8427\u6761\u3002 \u3000\u3000\u5f53\u7136\uff0c\u50cf\u4e00\u4e9b\u5728\u665a\u95f4\u5de5\u4f5c\u7684\u4eba\uff0c\u5c31\u907f\u65e0\u53ef\u907f\uff0c\u53ea\u597d\u6c42\u795e\u62dc\u4f5b\u4e4b\u540e\uff0c\u786c\u7740\u5934\u76ae\u53bb\u9762\u5bf9\u3002 \u3000\u3000\u50cf\u5f00\u591c\u8f66\u7684\u201c\u5477\u54e9\u8f66\u201d\uff08\u5373\u5927\u5361\u8f66\uff09\u53f8\u673a\uff0c\u56e0\u5fc5\u987b\u628a\u8f66\u4e0a\u7684\u8d27\u7269\u5357\u4e0a\u5317\u4e0b\u5f7b\u591c\u8d76\u65f6\u95f4\u8fd0\u9001\uff0c\u5c31\u53ea\u597d\u8c41\u4e86\u51fa\u53bb\uff0c\u53ea\u671b\u4e0d\u8981\u9047\u4e0a\u201c\u9ed1\u706b\u201d\u6216\u8005\u201c\u767d\u8272\u7684\u5973\u4eba\uff0c\uff0c\u628a\u8f66\u5728\u9a6c\u8def\u4e0a\u5f00\u5f97\u66f4\u52a0\u98ce\u9a70\u7535\u63a3\u3002 \u3000\u3000\u8fd9\u4e00\u6765\uff0c\u56e0\u4ff1\u9ed1\u706b\u800c\u4f24\u4ea1\u7684\u4eba\u5c31\u66f4\u591a\u4e86\uff0c\u5f53\u7136\u5305\u62ec\u4e86\u610f\u5916\u548c\u8f66\u7978\u3002 \u3000\u3000\u53ef\u662f\u8521\u56db\u5e78\u534a\u591c\u9a7e\u9a76\uff0c\u5374\u4e0d\u662f\u4e3a\u4e86\u7cca\u53e3\u3001\u5de5\u4f5c\u3002 \u3000\u3000\u4ed6\u4e0d\u6015\u9ed1\u4eba\u3002 \u3000\u3000\u2014\u2014\u4e0d\u7ba1\u518d\u600e\u4e48\u9ed1\u3001\u4ec0\u4e48\u706b\uff0c\u4ed6\u90fd\u4e0d\u6015\u3002 \u3000\u3000\u8521\u56db\u5e78\u7684\u751f\u547d\u91cc\uff0c\u4ece\u6765\u6ca1\u6709\u201c\u6015\u201d\u5b57\u3002 \u3000\u3000\u4ed6\u5728\u5e74\u7eaa\u5f88\u5c0f\u7684\u65f6\u5019\uff0c\u88ab\u4ed6\u54e5\u54e5\u5e26\u53bb\u53c2\u52a0\u73ed\u4e0a\u7684\u9732\u8425\u3002\u5f53\u5929\u534a\u591c\uff0c\u8425\u5916\u5ffd\u7136\u9634\u98ce\u9635\u9635\uff0c\u4e00\u9635\u602a\u98ce\u5439\u7184\u4e86\u672c\u6765\u70e7\u5f97\u6b63\u65fa\u7684\u8425\u706b\uff0c\u5269\u4e0b\u4e00\u70b9\u706b\u82d7\u4e5f\u8f6c\u5448\u9eef\u7eff\u8272\uff0c\u53ea\u542c\u4e00\u79cd\u7684\u5fae\u54cd\uff0c\u8150\u81ed\u4e4b\u5473\u88ad\u4eba\u9f3b\u5598\uff0c\u900f\u8fc7\u6a21\u7cca\u7684\u6708\u8272\uff0c\u9690\u7ea6\u53ef\u89c1\u6709\u5341\u51e0\u6761\u8be1\u5f02\u7684\u4eba\u5f71\u5728\u8df3\u52a8\u7740\uff0c\u6709\u4eba\u5fc3\u6c34\u6e05\uff0c\u7ea6\u7565\u4e00\u6570\uff0c\u5171\u6709\u5341\u4e8c\u9053\u5f71\u5b50\u3002 \u3000\u3000\u4e00\u540c\u51fa\u6765\u9732\u8425\u7684\u540c\u6211\u65e9\u5df2\u5413\u5f97\u8138\u65e0\u4eba\u8272\u3001\u6296\u4f88\u7684\u6296\u54c6\uff0c\u6342\u8138\u7684\u6342\u8138\uff0c\u8fde\u8521\u56db\u5e78\u7684\u54e5\u54e5\u8521\u4e09\u62e9\u4e5f\u5413\u5f97\u8138\u76ae\u62bd\u7b4b\uff0c\u98a4\u4e0d\u58f0\u97f3\uff1a\u201c\u662f\u2026\u2026\u5341\u2026\u2026\u4e8c\u2026\u884c\u2026\u2026\u5c38\u3002\u201d \u3000\u3000\u5f53\u65f6\uff0c\u5728\u90a3\u4e00\u5e26\u6709\u5f88\u591a\u8352\u575f\uff0c\u5f53\u5730\u4fd7\u79f0\u4e3a\u201c\u5927\u4f2f\u516c\u5c71\u201d\uff0c\u4f20\u8bf4\u5e38\u89c1\u5341\u4e8c\u9053\u98de\u5c38\uff0c\u6ca1\u60f3\u5230\u5374\u7ed9\u4ed6\u4eec\u9047\u4e0a\u3002\u4e0d\u6599\uff0c\u8fd8\u4e0d\u5230\u5341\u4e09\u5c81\u7684\u8521\u56db\u5e78\u5374\u6602\u7136\u7acb\u8d77\uff0c\u5c31\u6307\u90a3\u4e9b\u8dc3\u52a8\u7684\u7070\u5f71\u6717\u58f0\u8bf4\uff1a \u3000\u3000\u201c\u4f60\u8981\u662f\u4eba\uff0c\u522b\u5413\u4eba\uff01\u4f60\u8981\u662f\u9b3c\uff0c\u90fd\u5df2\u7ecf\u6b7b\u4e86\uff0c\u8fd8\u6562\u5413\u4eba\uff1a\u6eda\u56de\u53bb\u5427\uff01\u201d \u3000\u3000\u4ed6\u8fd9\u53e5\u8bdd\u4e00\u8bf4\uff0c\u706b\u7130\u53c8\u4ece\u6697\u9752\u56de\u5230\u660e\u4eae\uff0c\u5c38\u81ed\u5c3d\u53bb\uff0c\u8fde\u9b3c\u5f71\u5b50\u4e5f\u4e0d\u89c1\u4e86\u3002\u4e8e\u662f\uff0c\u8521\u56db\u5e78\u201c\u80c6\u5927\u5305\u5929\uff0c\u4eba\u65fa\u9b3c\u6015\u7684\u540d\u58f0\uff0c\u5c31\u6cb8\u6cb8\u626c\u626c\u7684\u4f20\u4e86\u5f00\u53bb\uff0c\u751a\u81f3\u6709\u4eba\u8bf4\u4ed6\u5fc3\u6b63\u4eba\u5584\u9633\u6c14\u76db\uff0c\u5bb6\u91cc\u82e5\u5177\u6709\u4ec0\u4e48\u4eba\u72af\u4e86\u90aa\uff0c\u7ed9\u6c61\u79fd\u6cbe\u4e86\u8eab\uff0c\u4e5f\u8981\u4ed6\u53bb\u6536\u60ca\u9000\u90aa\u3002 \u3000\u3000\u8521\u56db\u5e78\u4f55\u6b62\u5927\u80c6\uff0c\u4e14\u8fd8\u662f\u4e00\u8eab\u5947\u9022\u3002\u5341\u4e94\u5c81\u90a3\u5e74\uff0c\u4ed6\u4e3a\u4e86\uff0c\u8ffd\u7d22\u4e00\u6761\u7ea2\u6c34\u6cb3\u6d41\u7684\u6c34\u6e90\uff0c\u8054\u540c\u51e0\u4f4d\u7ed3\u62dc\u5144\u5f1f\u4e0a\u5c71\u7d22\u6e90\uff0c\u51e0\u5ea6\u7ed9\u6df1\u5c71\u91cc\u7684\u571f\u4eba\u201c\u6c99\u76d6\u201d\u8d8b\u6740\uff0c\u4e5f\u9047\u4e0a\u5c71\u732b\u548c\u91ce\u732a\uff0c\u9669\u6b7b\u8fd8\u751f\uff0c\u4f46\u4ed6\u8fd8\u662f\u4e00\u6837\u627e\u51fa\u4e86\u6c34\u6e90\uff0c\u7ed8\u5236\u6210\u5730\u56fe\uff0c\u5e74\u7eaa\u5c0f\u5c0f\u5c31\u5bf9\u56fd\u5bb6\u5730\u7406\u7f16\u52d8\u4f5c\u51fa\u4e86\u8d21\u732e\u3002 \u3000\u3000\u957f\u5927\u4e4b\u540e\uff0c\u81ea\u7136\u66f4\u4e0d\u5f97\u4e86\uff0c\u82f1\u52c7\u4e8b\u8ff9\u591a\u5f97\u6570\u4e0d\u6e05\u3002\u6709\u4e09\u4ef6\u4e8b\u8fc4\u4eca\u8fd8\u4e3a\u4eba\u6d25\u6d25\u4e50\u9053\uff1a\u5373\u662f\u4ed6\u8ddf\u8e2a\u51e0\u540d\u81ea\u5077\u6e21\u5165\u5883\u7684\u5370\u5c3c\u4eba\uff0c\u679c\u89c1\u4ed6\u4eec\u4eba\u5c4b\u884c\u52ab\uff0c\u8fd8\u6325\u5200\u65a9\u6740\u4e8b\u4e3b\uff0c\u4ed6\u4e00\u6012\u4e4b\u4e0b\uff0c\u4ee5\u4e00\u654c\u4e94\uff0c\u8d64\u624b\u7a7a\u62f3\uff0c\u7adf\u5236\u4f0f\u4e86\u4e94\u540d\u62ff\u7740\u5229\u5668\u7684\u532a\u5f92\uff0c\u987f\u65f6\u4f7f\u4ed6\u6210\u4e3a\u62a5\u7ae0\u4e0a\u7684\u77a9\u76ee\u4eba\u7269\u3002 \u3000\u3000\u53e6\u541f\u6b21\u662f\u4ed6\u60f3\u589e\u8fdb\u751f\u6d3b\u7ecf\u9a8c\uff0c\u968f\u540c\u6e14\u8239\u51fa\u6d77\u6355\u9c7c\uff0c\u521a\u597d\u9047\u4e0a\u516c\u6d77\u5730\u5e26\u7684\u83f2\u5f8b\u5bbe\u6d77\u76d7\u3001\u4ed6\u4e0d\u7518\u53d7\u52ab\uff0c\u8054\u540c\u6e14\u592b\u4e0e\u6d77\u76d7\u786c\u62fc\uff0c\u4ee5\u5be1\u654c\u4f17\uff0c\u5c45\u7136\u6218\u80dc\uff0c\u4e5f\u4f7f\u4ed6\u58f0\u540d\u5927\u566a\u3002 \u3000\u3000\u8fd8\u6709\u4e00\u6b21\uff0c\u5f53\u5730\u4e24\u4e2a\u534e\u4eba\u96c6\u56e2\u56e0\u4e0d\u540c\u610f\u4e00\u7b14\u6b3e\u5b50\u7684\u8fd0\u7528\u65b9\u6cd5\u800c\u53d1\u751f\u8d8a\u8d84\uff0c\u51e0\u81f3\u6b66\u6597\u3002\u9274\u6b64\uff0c\u8521\u56db\u5e78\u591a\u65b9\u5954\u8d70\uff0c\u805a\u5408\u5f53\u5e74\u6709\u540d\u671b\u3001\u6709\u5f71\u54cd\u529b\u7684\u4e61\u7ec5\uff0c\u53bb\u8bf4\u670d\u4e86\u4e24\u5e2e\u515a\u9b41\uff0c\u628a\u8fd9\u7b14\u6b3e\u5b50\u7528\u4f5c\u7b79\u529e\u534e\u6587\u72ec\u7acb\u4e2d\u5b66\u7684\u57fa\u91d1\uff0c\u7ed3\u679c\u7686\u5927\u6b22\u559c\u3002 \u3000\u3000\u8521\u56db\u5e78\u5728\u5f53\u5730\u4e0d\u53ea\u662f\u4e2a\u6e38\u4fa0\u5f0f\u7684\u4eba\u7269\uff0c\u800c\u4e14\u8fd8\u52a0\u5165\u4e86\u4e16\u754c\u6027\u7684\u201c\u4e0d\u5e73\u793e\u201d\u3002 \u3000\u3000\u201c\u4e0d\u5e73\u793e\u201d\u4fbf\u662f\u4e00\u4e2a\u4e13\u66ff\u4eba\u6253\u62b1\u4e0d\u5e73\uff0c\u66ff\u53d7\u6b3a\u8d1f\u7684\u8d2b\u5f31\u8005\u51fa\u5934\uff0c\u4e3a\u53d7\u51a4\u5c48\u8005\u4f38\u51a4\uff0c\u8fd0\u7528\u793e\u5458\u7684\u80fd\u529b\uff0c\u4e3b\u6301\u6b63\u4e49\uff0c\u5bf9\u6cd5\u5f8b\u4e0d\u80fd\u5236\u88c1\u7684\u6076\u4eba\u65bd\u52a0\u6253\u51fb\uff0c\u800c\u4e14\u8fd8\u4e50\u4e8e\u4e3a\u65e0\u8f9c\u800c\u6ca1\u6709\u53cd\u6297\u80fd\u529b\u7684\u53d7\u5bb3\u8005\u5411\u4ed6\u4eec\u5bf9\u5934\u62a5\u590d\u3002 \u3000\u3000\u8521\u56db\u5e78\u662f\u201c\u4e0d\u5e73\u793e\u201d\u7684\u4e00\u5458\u3002 \u3000\u3000\u4ed6\u4e3a\u8fd9\u4e00\u70b9\u800c\u6cbe\u6cbe\u81ea\u559c\u3002 \u3000\u3000\u542c\u8bf4\uff0c\u5728\u6b64\u5730\u603b\u5171\u53ea\u6709\u4e09\u4e2a\u4eba\u88ab\u9009\u5165\u201c\u4e0d\u5e73\u793e\u201d\u91cc\uff0c\u6240\u4ee5\u80fd\u88ab\u9009\u5165\uff0c\u4e00\u5b9a\u5728\u667a\u6167\u3001\u624d\u80fd\uff0c\u8eab\u624b\u548c\u8868\u73b0\uff0c\u8d21\u732e\u4e0a\u90fd\u5fc5\u7136\u6709\u8fc7\u4eba\u51fa\u8272\u5904\u3002\u8fd9\u662f\u4e00\u79cd\u6b8a\u8363\u3002 \u3000\u3000\u8eab\u4e3a\u4e0d\u4e0d\u5e73\u793e\u7684\u4e00\u5458\uff0c\u8521\u56db\u5e78\u5f53\u7136\u611f\u5230\u9a84\u50b2\u5149\u8363\u3002 \u3000\u3000\u4ed6\u4eca\u665a\u4e0d\u53ea\u662f\u5149\u8363\uff0c\u8fd8\u89c9\u5f97\u5174\u594b\u548c\u751c\u871c\u3002 \u3000\u3000\u5174\u594b\u662f\u56e0\u4e3a\u660e\u5929\u8981\u89c1\u7684\u4eba\u3002 \u3000\u3000\u751c\u871c\u662f\u56e0\u4e3a\u4ed6\u8eab\u8fb9\u6709\u4e00\u4e2a\u751c\u871c\u7684\u5973\u5b69\u5b50\u3002 \u3000\u3000\u4e00\u4e2a\u751c\u871c\u5f97\u4ee4\u4eba\u770b\u4e00\u773c\u5fc3\u90fd\u8f6f\u4e86\uff0c\u7f8e\u5f97\u4ee4\u4eba\u8ddf\u5979\u8bf4\u4e00\u53e5\u8bdd\u5c31\u9165\u4e86\uff0c\u4f46\u4e00\u53cc\u773c\u5374\u5e38\u5b55\u80b2\u7740\u5fae\u6101\u7684\u5973\u5b69\u5b50\u3002 \u3000\u3000\u5979\u539f\u540d\u53eb\u5f20\u5c0f\u79c0\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u559c\u6b22\u53eb\u5979\u505a\u5f20\u5c0f\u6101\u3002 \u3000\u3000\u4ed6\u8fd8\u628a\u201c\u5f20\u5c0f\u6101\u201d\u8fd9\u540d\u5b57\u53eb\u5f00\u4e86\uff0c\u5927\u5bb6\u90fd\u4e60\u60ef\u628a\u5979\u53eb\u505a\u5f20\u5c0f\u6101\u3002 \u3000\u3000\u56e0\u4e3a\u5979\u6709\u4e00\u5bf9\u5fe7\u6101\u7684\u773c\u3002 \u3000\u3000\u5c31\u7b97\u5728\u5979\u7b11\u7684\u65f6\u5019\uff0c\u4e00\u53cc\u773c\u775b\u4e5f\u662f\u5fe7\u90c1\u7684\uff0c\u201c\u5b81\u54c0\u77dc\u800c\u52ff\u559c\u201d\uff0c\u5927\u6982\u5c31\u662f\u5979\u773c\u795e\u91cc\u6d41\u9732\u7684\u610f\u601d\u3002 \u3000\u3000\u4ed6\u559c\u6b22\u8fd9\u4e00\u5bf9\u773c\u775b\u3002 \u3000\u3000\u4ed6\u7231\u4e0a\u4e86\u8fd9\u4e2a\u5973\u5b69\u5b50\uff0c\u7231\u5f97\u5f88\u6df1\u3002 \u3000\u3000\u201c\u5f20\u5c0f\u6101\uff0c\u201d\u4ed6\u5e38\u8fd9\u6837\u547c\u5524\u5979\uff0c\u201c\u96e8\u540e\u4e5f\u4f1a\u5929\u6674\uff0c\u53ef\u662f\u4f60\u773c\u91cc\u603b\u662f\u8f7d\u4e0d\u53bb\u8bb8\u591a\u6101\u3002\u201d\u4ed6\u66fe\u8fd9\u6837\u5730\u8c03\u7b11\u5979\u3002 \u3000\u3000\u201c\u600e\u4e48\u5566\uff1f\u201d\u5f20\u5c0f\u6101\u5750\u5728\u9a7e\u9a76\u5ea7\u65c1\uff0c\u89c1\u8521\u56db\u5e78\u5174\u594b\u5f97\u8001\u662f\u65f6\u624b\u5728\u65b9\u5411\u76d8\u4e0a\u6253\u62cd\u5b50\uff0c\u8fd8\u6291\u4e0d\u4f4f\u5507\u89d2\u8fb9\u7684\u7b11\u610f\uff0c\u4fbf\u5fae\u55d4\u5730\u95ee\uff1a \u3000\u3000\u201c\u770b\u5174\u594b\u5f97\u4f60\u5566\uff01\u660e\u5929\u6765\u7684\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u201c\u6211\u7684\u5144\u5f1f\uff0c\u201d\u8521\u56db\u5e78\u60f3\u5230\u660e\u5929\u5c31\u8981\u89c1\u5230\u7684\u4eba\uff0c\u4fbf\u751f\u8d77\u4e00\u79cd\u610f\u5174\u98de\u626c\u7684\u611f\u89c9\uff0c\u201c\u6211\u6700\u4f69\u670d\u7684\u5144\u5f1f\u3002\u201d \u3000\u3000\u201c\u4f60\u7684\u5144\u5f1f\uff1f\u4f60\u54e5\u54e5\u2026\u2026\u201d \u3000\u3000\u624d\u4e0d\u662f\u4ed6\u5462\uff01\u90a3\u4e2a\u80c6\u5c0f\u9b3c\uff01\u201d\u8521\u56db\u5e78\u60f3\u8d77\u4ed6\u90a3\u4e2a\u80c6\u5c0f\u6015\u4e8b\u3001\u864e\u5934\u86c7\u5c3e\u7684\u54e5\u54e5\u5c31\u5fc3\u91cc\u6709\u6c14\uff0c\u201c\u6211\u8fd9\u51e0\u4e2a\u5144\u5f1f\uff0c\u5176\u4e2d\u4e24\u4f4d\uff0c\u6211\u4e5f\u53ea\u89c1\u8fc7\u4e24\u6b21\u3002\u201d \u3000\u3000\u201c\u54e6\uff0c\u539f\u6765\u662f\u7ed3\u62dc\u5144\u5f1f\u3002\u201d \u3000\u3000\u201c\u53ea\u8981\u6295\u5951\uff0c\u90a3\u53ef\u6bd4\u540c\u80de\u5144\u5f1f\u66f4\u77e5\u5fc3\u5440\uff01\u201d\u8521\u56db\u5e78\u8c08\u8d77\u4ed6\u4eec\u5c31\u89c9\u5f97\u4e0e\u6709\u8363\u7109\uff0c\u201c\u4ed6\u4eec\u90fd\u662f\u4e00\u4e9b\u5f88\u4e86\u4e0d\u8d77\u7684\u4eba\u3002\u201d \u3000\u3000\u201c\u5c31\u50cf\u4f60\u4e00\u6837\uff1f\u201d\u5f20\u5c0f\u6101\u8877\u5fc3\u7684\u8bf4\u3002\u5979\u662f\u8861\u5dde\u4eba\uff0c\u7ca4\u8bed\u8bf4\u5f97\u4e0d\u5927\u7075\u5149\uff1b\u8521\u56db\u5e78\u662f\u5e7f\u897f\u4eba\uff0c\u4e0d\u4f1a\u8bf4\u6109\u5dde\u8bdd\uff0c\u6240\u4ee5\u4ed6\u4eec\u53ea\u597d\u7528\u534e\u8bed\u4ea4\u8c08\u3002\u5f20\u5c0f\u6101\u7684\u58f0\u97f3\u672c\u5c31\u8f6f\u7cef\u61e6\u7684\uff0c\u8bf4\u8d77\u534e\u8bed\u6765\u66f4\u6709\u4e00\u79cd\u8106\u7a23\u7a23\u7684\u8154\u8c03\uff0c\u65f6\u800c\u5939\u6742\u7740\u5979\u81ea\u5df1\u7279\u6709\u7684\u5c3e\u97f3\uff0c\u5f88\u662f\u597d\u542c\u3002 \u3000\u3000\u201c\u4ed6\u4eec\u53ef\u6bd4\u6211\u66f4\u68d2\uff0c\u4ed6\u4eec\u7684\u4e8b\u8ff9\u2026\u2026\u201d\u8521\u56db\u5e78\u4e00\u9762\u9a7e\u7740\u8f66\u5b50\u5728\u9ed1\u9053\u4e0a\u98de\u9a70\uff0c\u4e24\u65c1\u98de\u63a0\u800c\u8fc7\u4e00\u6392\u6392\u7684\u50cf\u6811\u6797\u3002\u4ed6\u5f88\u4e3a\u521a\u624d\u5f20\u5c0f\u6101\u90a3\u4e00\u53e5\u95f4\u63a5\u8d5e\u7f8e\u4ed6\u7684\u8bdd\u800c\u9676\u9676\u7136\uff0c\u4f46\u5728\u9676\u7136\u7d2f\u8fd8\u662f\u4e0d\u5fd8\u4ed6\u51e0\u4e2a\u5ff5\u4e4b\u4ea6\u4e3a\u795e\u5f80\u7684\u8fdc\u65b9\u670b\u53cb\uff1a\u201c\u4ed6\u4eec\u7684\u6545\u4e8b\uff0c\u6211\u8ddf\u4f60\u8bf4\u5341\u5929\u5341\u591c\u90fd\u8bf4\u4e0d\u5b8c\uff0c\u660e\u5929\u4f60\u89c1\u7740\u4ed6\u4eec\u5c31\u77e5\u9053\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u4ed6\u4eec\u2026\u2026\u201d\u5f20\u5c0f\u6101\u504f\u7740\u5934\u95ee\uff1a\u201c\u4ed6\u4eec\u662f\u5e72\u4ec0\u4e48\u7684\u5440\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4eec\u90fd\u6709\u6b63\u5f53\u804c\u4e1a\uff0c\u6709\u7684\u662f\u4f5c\u5bb6\uff0c\u6709\u7684\u662f\u6559\u6388\uff0c\u6709\u7684\u662f\u5546\u4e1a\u5de8\u5b50\uff0c\u4f01\u4e1a\u5bb6\uff0c\u6709\u7684\u662f\u9ad8\u7ea7\u8b66\u52a1\u4eba\u5458\uff0c\u6709\u7684\u662f\u2026\u2026\u4e0d\u8fc7\uff0c\u90a3\u53ea\u662f\u4ed6\u4eec\u7684\u804c\u4e1a\uff0c\u4ed6\u4eec\u7684\u4e8b\u4e1a\uff0c\u5219\u8ddf\u6211\u4e00\u6837\u2026\u2026\u201d \u3000\u3000\u8521\u56db\u5e78\u65e0\u9650\u5149\u8363\u4ed6\u8bf4\uff1a\u201c\u4e3a\u5f31\u8005\u6253\u62b1\u4e0d\u5e73\uff0c\u4f38\u5f20\u6b63\u4e49\u3002\u201d \u3000\u3000\u5f20\u5c0f\u6101\u8be7\u9053\uff1a\u201c\u8fd9\u8fd9\u4e0d\u662f\u53e4\u4ee3\u7684\u6b66\u4fa0\u5c0f\u8bf4\u91cc\u624d\u4f1a\u53d1\u751f\u7684\u4e8b\u5417\uff1f\u201d\u5979\u8be7\u5f02\u7684\u65f6\u5019\u773c\u775b\u4ecd\u4e0d\u6539\u6101\u8272\u3002 \u3000\u3000\u201c\u4f60\u522b\u4ee5\u4e3a\u73b0\u5728\u6ca1\u6709\uff0c\u201d\u8521\u56da\u5e78\u5495\u54dd\u5730\u9053\uff1a\u201c\u5c31\u662f\u56e0\u4e3a\u6709\uff0c\u6240\u4ee5\u4eba\u7c7b\u624d\u80fd\u751f\u5b58\u5230\u73b0\u5728\u3002\u201d \u3000\u3000\u201c\u4f60\u4e5f\u662f\u5176\u4e2d\u4e00\u5458\u5440\uff1f\u201d \u3000\u3000\u201c\u5bf9\u3002\u201d \u3000\u3000\u201c\u4ed6\u4eec\u2026\u2026\u662f\u54ea\u91cc\u4eba\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4eec\u5206\u5e03\u5728\u5168\u4e16\u754c\u5404\u5730\uff0c\u660e\u5929\u6765\u7684\u4e09\u4eba\uff0c\u4e00\u4f4d\u662f\u53f0\u6e7e\u4eba\uff0c\u4e00\u4f4d\u662f\u9999\u6e2f\u4eba\uff0c\u53e6\u5916\u4e00\u4f4d\u662f\u8fd9\u7ec4\u7ec7\u91cc\u6838\u5fc3\u4efd\u5b50\uff0c\u2018\u516d\u4eba\u5e2e\u2019\u7684\u8001\u5927\uff0c\u6211\u4e5f\u4e0d\u5927\u6e05\u695a\u4ed6\u5230\u5e95\u662f\u54ea\u91cc\u4eba\u2026\u2026\u201d \u3000\u3000\u7a81\u7136\uff0c\u4ed6\u6025\u901f\u8f6c\u5411\u3001\u731b\u7136\u5239\u63a3\uff0c\u707f\u4eae\u7684\u706f\u9aa4\u8fd1\u800c\u8fc7\uff0c\u5dee\u70b9\u6ca1\u773c\u524d\u9762\u7684\u8f66\u5b50\u76f8\u649e\u3002\u3000\u3000\u3002 \u3000\u3000\u201c\u4f60\u5440\uff0c\u8bf4\u5f97\u592a\u5174\u594b\u4e86\u5462\u2026\u2026\u201d\u5f20\u5c0f\u6101\u60ca\u9b42\u672a\u5b9a\u7684\u8bf4\u3002\u201c\u8981\u662f\u51fa\u4e86\u4e8b\uff0c\u660e\u5929\u5c31\u4ec0\u4e48\u4eba\u4e5f\u89c1\u4e0d\u5230\u4e86\u3002\u201d \u3000\u3000\u201c\u5bf9\u4e0d\u8d77\u5bf9\u4e0d\u8d77\uff0c\u201d\u8521\u56db\u5e78\u60f3\u518d\u5f00\u8f66\uff0c\u4f46\u8bd5\u4e86\u51e0\u6b21\uff0c\u8f66\u5b50\u7684\u5f15\u64ce\u90fd\u65e0\u6cd5\u5f00\u52a8\u3002\u201c\u6211\u4e0b\u8f66\u770b\u770b\u3002\u201d \u3000\u3000\u8f66\u5b50\u505c\u5728\u90ca\u9053\u5bc6\u6797\u65c1\uff0c\u4fee\u4e86\u8001\u534a\u5929\uff0c\u4ecd\u662f\u5f00\u4e0d\u52a8\u3002\u4ed6\u56de\u5230\u8f66\u4e2d\uff0c\u5f20\u5c0f\u6101\u638f\u51fa\u624b\u5e15\u66ff\u4ed6\u63e9\u53bb\u8138\u4e0a\u7684\u6c57\uff0c\u8521\u56da\u5e78\u65e0\u5948\u5730\u8038\u8038\u80a9\uff1a\u201c\u770b\u6765\uff0c\u4eca\u5929\u662f\u8fd9\u90e8\u8f66\u5b50\u7684\u751f\u65e5\uff0c\u5b83\u5927\u6982\u8981\u4f11\u5047\u4e00\u5929\u4ee5\u793a\u5e86\u795d\u5427\u3002\u201d \u3000\u3000\u5728\u8fd9\u6df1\u591c\u7684\u90ca\u9053\u4e0a\uff0c\u8fde\u63a0\u8fc7\u7684\u8f66\u8f86\u4e5f\u4e0d\u6613\u89c1\u3002\u8def\u9762\u5de6\u8fb9\u662f\u5bc6\u6797\uff0c\u53f3\u8fb9\u662f\u77ff\u6e56\uff0c\u91ce\u8349\u4e1b\u751f\uff0c\u8fdc\u5904\u4f3c\u6709\u5ea7\u5c0f\u5c0f\u7684\u795e\u9f9b\u3002 \u3000\u3000\u201c\u600e\u4e48\u529e\uff1f\u201d\u5f20\u5c0f\u6101\u62c5\u5fc3\u5730\u8bf4\uff1f\u201d \u3000\u3000\u201c\u600e\u4e48\u529e\uff1f\u201d\u8521\u56db\u5e78\u4eae\u7740\u8f66\u91cc\u7684\u706f\uff0c\u770b\u89c1\u5f20\u5c0f\u6101\u5fe7\u6101\u7684\u6837\u5b50\uff0c\u5fc3\u4e2d\u63a0\u8fc7\u6d53\u70c8\u7684\u871c\u610f\u8f7b\u601c\uff0c\u8f7b\u8f7b\u7684\u62e5\u7740\u5979\uff1a\u201c\u4f60\u4e0d\u6015\u6211\uff1f\u201d \u3000\u3000\u5f20\u5c0f\u6101\u5947\u9053\uff1a\u201c\u6015\u4f60\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u6015\u6211\u3002\u3002\u3002\u201d\u8521\u56fd\u5e78\u6545\u610f\u88c5\u4e86\u4e2a\u5341\u5206\u72de\u72f0\u7684\u6837\u5b50\u3002\u5f20\u5c0f\u6101\u5ffd\u7136\u5c16\u53eb\u4e86\u4e00\u58f0\u3002 \u3000\u3000\u8521\u56db\u5e78\u5012\u662f\u8ba9\u5979\u5413\u4e86\u4e00\u5927\u8df3\uff0c\u5fd9\u629a\u6170\u9053\uff1a\u201c\u522b\u6015\uff0c\u522b\u6015\uff0c\u6211\u53ea\u662f\u5413\u5413\u4f60\u7684\uff0c\u6211\u600e\u4f1a\u2026\u201d \u3000\u3000\u5f20\u5c0f\u6101\u62bf\u7740\u5634\u7b11\u4e86\uff1a\u201c\u6211\u4e5f\u662f\u5413\u5413\u4f60\u7f62\u4e86\u3002\u201d \u3000\u3000\u8521\u56db\u5e78\u8fd9\u624d\u604d\u609f\uff0c\u6307\u7740\u5979\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u4f60\u6bd4\u6211\u66f4\u2026\u201d \u3000\u3000\u5f20\u5c0f\u6101\u67d4\u67d4\u5730\u7b11\u9053\uff1a\u201c\u6211\u4e0d\u6015\u4f60\u561b\uff0c\u6211\u77e5\u9053\u4f60\u624d\u4e0d\u662f\u90a3\u79cd\u4eba\u6765\u7684\u5440\u3002\u201d \u3000\u3000\u7136\u540e\u5979\u56db\u5468\u671b\u671b\uff0c\u8fd8\u662f\u6709\u70b9\u5fc3\u5fe7\u7684\u9053\uff1a\u201c\u6211\u4eec\u8fd8\u662f\u60f3\u529e\u6cd5\u5148\u56de\u5230\u5e02\u533a\u5427\u3002\u201d \u3000\u3000\u201c\u6211\u771f\u5e78\u798f\u3002\u201d\u8521\u56db\u5e78\u5374\u5e72\u8106\u8eba\u9760\u5728\u5ea7\u4f4d\u4e0a\u3002 \u3000\u3000\u201c\u2026\u2026\uff1f\u201d\u5f20\u5c0f\u6101\u50bb\u4e4e\u4e4e\u7684\u770b\u7740\u4ed6\uff0c\u4e0d\u660e\u4ed6\u6240\u6307\u3002 \u3000\u3000\u8521\u56db\u5e78\u5ffd\u4f38\u624b\uff0c\u629a\u7740\u5979\u7684\u79c0\u53d1\uff0c\u5f88\u73cd\u60dc\u7684\u770b\u7740\u5979\u8bf4\uff1a\u201c\u77e5\u9053\u5417\uff1f\u5728\u591c\u8272\u91cc\u4f60\u66f4\u7f8e\u3002\u539f\u6765\u4f60\u7684\u773c\u8272\u5728\u9ed1\u591c\u5c31\u50cf\u9ed1\u8272\u7684\u73e0\u5b9d\u2026\u2026\u6211\u4eca\u5929\u624d\u7b2c\u4e00\u6b21\u53d1\u73b0\u3002\u201d \u3000\u3000\u5f20\u5c0f\u6101\u7f9e\u6da9\u5730\u55d4\u7b11\uff0c\u201c\u4f60\u8fd9\u4eba\uff0c\u8001\u6ca1\u6b63\u7ecf\uff0c\u534a\u591c\u4e09\u66f4\u8352\u5c71\u91ce\u5cad\uff0c\u8f66\u5b50\u6b7b\u4e86\u706b\uff0c\u4f60\u8fd8\u8bf4\u81ea\u5df1\u5e78\u798f\uff01\u201d \u3000\u3000\u201c\u6211\u4e0d\u5e78\u798f\u5417\uff1f\u4f60\u77e5\u9053\u6211\u53eb\u8521\u56db\u5e78\uff0c\u54ea\u6765\u201c\u56db\u5e78\u201d\u4e00\u5e78\u662f\u6211\u5929\u751f\u6709\u8fc7\u4eba\u7684\u5934\u8111\uff0c\u4e8c\u5e78\u662f\u6211\u6709\u8fc7\u4eba\u4f53\u529b\uff0c\u4e09\u5e78\u662f\u6211\u6709\u8fc7\u4eba\u7684\u610f\u5fd7\uff0c\u56db\u5e78\u662f\u6211\u6709\u4f60\u2026\u2026\u8521\u56db\u5e78\u65e0\u9650\u6ee1\u8db3\u60a0\u7136\u81ea\u5f97\u7684\u8bf4\uff1a \u3000\u3000\u201c\u4f60\u770b\uff0c\u5c31\u7b97\u662f\u534a\u591c\u5352\u4e8e\u629b\u951a\uff0c\u4e5f\u8fd8\u6709\u4e2a\u7f8e\u4e3d\u6e29\u67d4\u7684\u5973\u5b50\u4e0e\u6211\u5e76\u5ea6\uff0c\u6211\u4e0d\u5e78\u798f\u8c01\u5e78\u798f\uff1f\u201d \u3000\u3000\u201c\u597d\u5566\u597d\u5566\uff0c\u4e16\u4e0a\u6240\u6709\u7684\u5e78\u798f\u90fd\u7ed9\u4f60\u4e00\u4e2a\u4eba\u5360\u53bb\u4e86\u3002\u201d\u5f20\u5c0f\u6101\u7b11\u4ed6\uff0c\u4f46\u53c8\u62c5\u5fc3\u4ed6\uff1a\u201c\u2026 \u3000\u3000\u90a3\u4e2a\u4ec0\u4e48\u4e0d\u2026\u4e0d\u5e73\u793e\uff0c\u8fd9\u513f\u5c31\u53ea\u4f60\u4e00\u4e2a\u4eba\u52a0\u5165\u5440\uff1f\u8fd9\u4e0d\u662f\u5f88\u5371\u9669\u5417\uff1f\u201d \u3000\u3000\u201c\u6211\u8fd8\u6015\u5371\u9669\u5417\uff01\u201d\u8521\u56de\u5e78\u9a84\u50b2\u7684\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u8fd9\u5730\u533a\u4e5f\u4e0d\u53ea\u6211\u4e00\u4e2a\u4eba\uff0c\u8fd8\u6709\u4e00\u4f4d\uff1a \u3000\u3000\u201c\u5927\u7ea2\u82b1\u201d\u4ed6\u662f\u6211\u6700\u597d\u7684\u6218\u53cb\uff0c\u4e5f\u662f\u6700\u4f73\u52b2\u654c\u3002\u4ed6\u540c\u6837\u662f\u2018\u4e0d\u5e73\u793e\u2019\u7684\u6210\u5458\uff0c\u4e0d\u8fc7\u4e00\u5411\u7531\u6211\u6765\u8d1f\u8d23\u8054\u7cfb\u2026\u2026\u8fd8\u6709\u90a3\u4e2a\u6e29\u6587\uff0c\u4ed6\u4e5f\u6210\u5929\u592e\u7740\u6211\u8981\u52a0\u5165\u5462\uff01\u201d \u3000\u3000\u201c\u6e29\u6587\uff1f\u201d\u5f20\u5c0f\u6101\u4e00\u6349\u5230\u8fd9\u4e2a\u540d\u5b57\u5c31\u597d\u7b11\uff1a\u201c\u4ed6\uff1f\u4ed6\u600e\u884c\uff1f\u201d\u53c8\u95ee\uff0c\u201c\u5927\u7ea2\u82b1\uff1f\u8c01\u662f\u5927\u7ea2\u82b1\uff1f\u201d \u3000\u3000\u201c\u7ea2\u5927\u82b1\u5440\u2026\u2026\u201d \u3000\u3000\u5f20\u5c0f\u6101\u5ffd\u7136\u201c\u5514\u201d\u4e86\u4e00\u58f0\u3002 \u3000\u3000\u8521\u56db\u5b63\u95ee\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u201c\u597d\u50cf\u6709\u4ec0\u4e48\u4eba\u5728\u5916\u9762\u7ecf\u8fc7\u2026\u2026\u201d\u5f20\u5c0f\u6101\u773c\u795e\u5f88\u6709\u4e9b\u8ff7\u832b\u3002\u3000\u3000\u201d \u3000\u3000\u90a3\u4e48\u6676\u83b9\u7684\u7738\u5b50\uff0c\u50cf\u73e0\u5b9d\u4e00\u822c\uff0c\u4f46\u73e0\u5b9d\u662f\u5149\u91c7\u593a\u76ee\u7684\uff0c\u8fd9\u5bf9\u9ed1\u8272\u7684\u73e0\u5ba4\u5374\u662f\u4f24\u611f\u7684\u3002\u8521\u56db\u5e78\u968f\u610f\u7684\u5411\u5916\u9762\u770b\u770b\uff0c\u9ed1\u6f06\u6f06\u7684\uff0c\u82cd\u7a79\u5c3d\u5904\uff0c\u6709\u4e00\u8f6e\u9752\u8272\u7684\u6b8b\u6708\u3002\u4ed6\u5fc3\u91cc\u5145\u6ee1\u7740\u871c\u610f\u8f7b\u601c\uff0c\u5374\u542c\u5f20\u5c0f\u6101\u6293\u7740\u7709\u8fb9\u6028\u9053\uff1a\u201c\u8fd8\u4e0d\u5feb\u60f3\u529e\u6cd5\u56de\u5e02\u533a\u53bb\uff0c\u4e0d\u7136\uff0c\u5988\u53c8\u8981\u5520\u53e8\u7684\u4e86\uff0c\u4f60\u770b\uff0c\u4eba\u5bb6\u8fd9\u513f\u8fd8\u8ba9\u5c71\u868a\u53ee\u4e86\u4e00\u53e3\u5462\u2026\u2026\u201d \u3000\u3000\u8521\u56db\u5e78\u51d1\u8fc7\u8138\u53bb\uff0c\u5475\u62a4\u7684\u8bf4\uff1a\u201c\u6b7b\u868a\u5b50\uff01\u8ba9\u6211\u77a7\u77a7\u2026\u2026\u201d \u3000\u3000\u4ed6\u7684\u8138\u51d1\u8fd1\u5f20\u5c0f\u6101\u7684\u7389\u9765\uff0c\u89c1\u4f0a\u67d4\u4e3d\u5f97\u50cf\u4e00\u573a\u9759\u4f0f\u5728\u7528\u591c\u91cc\u7684\u7eee\u68a6\uff0c\u8138\u4e0a\u7b11\u610f\u76c8\u76c8\uff0c\u773c\u91cc\u8f7b\u6101\u70b9\u70b9\uff0c\u4e3e\u6b62\u95f4\u72b9\u4f3c\u53f6\u5760\u73ca\u73ca\uff0c\u5343\u79cd\u98ce\u60c5\uff0c\u90fd\u4e0d\u5982\u4ece\u4f55\u5f00\u59cb\uff0c\u5982\u4f55\u7ed3\u675f\uff0c\u5fcd\u4e0d\u4f4f\u60f3\u4eb2\u543b\u5979\u4e00\u4e0b\u2026\u2026\u201d \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u5f20\u5c0f\u6101\u500f\u5730\u53d1\u51fa\u4e00\u58f0\u9a87\u7edd\u7684\u60ca\u547c\uff01 \u3000\u3000\u7531\u4e8e\u8fd9\u4e00\u58f0\u60ca\u547c\u5341\u5206\u7a81\u5140\uff0c\u975e\u5e38\u51c4\u5389\uff0c\u8521\u56db\u5e78\u5012\u771f\u7684\u7ed9\u5413\u4e86\u4e00\u8df3\uff0c\u4f46\u4ed6\u968f\u5373\u4e86\u7136\uff0c\u7b11\u62e7\u5f20\u5c0f\u6101\u7389\u988a\uff0c\u5254\u7740\u7709\u7231\u60dc\u5730\u7b11\u9a82\uff1a\u201c\u4f60\u5440\uff0c\u91cd\u65bd\u6545\u6280\uff0c\u5c31\u662f\u4e0d\u8ba9\u6211\u543b\uff0c\u6211\u770b\u4f60\u8fd9\u6b21\u8fd8\u9a97\u2026\u2026\u201d \u3000\u3000\u53ef\u662f\u4ed6\u7a81\u7136\u53d1\u73b0\uff0c\u5f20\u5c0f\u6101\u672c\u6765\u4e00\u5bf9\u591a\u6101\u5584\u611f\u7684\u7738\u5b50\u91cc\uff0c\u800c\u4eca\u5168\u6ce8\u6ee1\u4e86\u9707\u6016\u4e0e\u60ca\u60e7\uff0c\u53ea\u76f4\u52fe\u52fe\u7684\u770b\u7740\u524d\u9762\u2014\u2014\u5373\u4ed6\u7684\u80cc\u540e\uff1a\u90a3\u9762\u6321\u98ce\u73bb\u7483\u5916\u3002 \u3000\u3000\u8fd9\u4f7f\u8521\u56db\u674e\u987f\u89c9\u6709\u5f02\uff0c\u75be\u8f6c\u8eab\u4e00\u770b\u3002 \u3000\u3000\u6321\u98ce\u73bb\u7483\u5916\uff0c\u767d\u5f71\u4e00\u95ea\u3002\u3000\u3000\u3001 \u3000\u3000\u5f20\u5c0f\u6101\u9a87\u7136\uff1a\u201c\u90a3\u5973\u4eba\u2014\u2014\u90a3\u5973\u4eba\u2026\u2026\u201d\u3002 \u3000\u3000\u8fd9\u65f6\u5019\u8f66\u5b50\u7a00\u5c11\uff0c\u4eba\u8e2a\u7f55\u89c1\uff0c\u66f4\u4f55\u51b5\u662f\u4e2a\u5973\u4eba\uff01 \u3000\u3000\u8521\u56db\u5e78\u62cd\u62cd\u5f20\u5c0f\u6101\u7684\u80a9\uff0c\u8f7b\u58f0\u4f46\u6709\u529b\u5730\u9053\uff1a\u201c\u6ca1\u4ec0\u4e48\u7684\uff0c\u6211\u4e0b\u8f66\u770b\u770b\u53bb\u3002\u201d \u3000\u3000\u5f20\u5c0f\u6101\u60f3\u6293\u4f4f\u8521\u56db\u5e78\u7684\u624b\uff0c\u4e0d\u8ba9\u4ed6\u4e0b\u8f66\uff0c\u53ef\u662f\u8521\u56db\u5e78\u5df2\u5f00\u4e86\u8f66\u95e8\u884c\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u8521\u56db\u5e78\u4e34\u79bb\u5f00\u524d\u7684\u90a3\u53e5\u8bdd\uff0c\u4f7f\u5f20\u5c0f\u6101\u611f\u89c9\u5230\u5b89\u5168\u4e0e\u5b89\u5b9a\u3002 \u3000\u3000\u4e8e\u662f\u5f20\u5c0f\u6101\u5728\u9ed1\u6697\u4e2d\u7b49\u8521\u56db\u5e78\u56de\u6765\u3002 \u3000\u3000\u2014\u2014\u8521\u56db\u5e78\u600e\u4e48\u8fd8\u4e0d\u56de\u6765\uff1f \u3000\u3000\u65f6\u95f4\u4e00\u5206\u4e00\u79d2\u7684\u8fc7\u53bb\uff0c\u5728\u5f20\u5c0f\u6101\u5fc3\u4e2d\u800c\u8a00\uff0c\u5c82\u6b62\u662f\u6e21\u65e5\u5982\u5e74\uff0c\u6e21\u65e5\u5982\u5e74\u53ea\u662f\u5bc2\u5bde\uff0c\u800c\u4eca\u8fd9\u5341\u6570\u5206\u949f\u95f4\u5374\u5145\u6ee1\u4e86\u672a\u77e5\u4e5f\u4e0d\u53ef\u77e5\u7684\u9ed1\u3001\u65e0\u8fb9\u548c\u65e0\u9650\u7684\u6050\u60e7\u3002 \u3000\u3000\u5f20\u5c0f\u6101\u552f\u4e00\u66fe\u5728\u773c\u5e18\u91cc\u63a0\u8fc7\u7684\u662f\uff0c\u8521\u56db\u5e78\u4f3c\u4e4e\u8ddf\u4e00\u4e2a\u4eba\u201d\u2014\u2014\u4e00\u4e2a\u767d\u8272\u7684\u5f71\u4e8e\u2014\u2014\u8d70\u4eba\u6df1\u9eef\u7684\u5bc6\u6797\u91cc\u53bb\u3002 \u3000\u3000\u2014\u2014\u201c\u5979\u201d\u662f\u8c01\uff1f \u3000\u3000\u2014\u2014\u4e3a\u4ec0\u4e48\u8521\u56db\u5e78\u8981\u9650\u201c\u5979\u201d\u53bb\uff1f \u3000\u3000\u5f20\u5c0f\u6101\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u9ed1\u6697\u4eff\u4f5b\u53d8\u6210\u4e86\u5f20\u7259\u821e\u722a\u7684\u751f\u7269\uff0c\u5728\u6240\u6709\u7684\u7a7a\u95f4\u91cc\u5f20\u626c\u6d41\u7a9c\uff0c\u7136\u800c\u53c8\u662f\u6b7b\u5bc2\u7684\u3002\u9759\u5bc2\u5f97\u50cf\u4e00\u573a\u6bc1\u706d\uff0c\u6b63\u5728\u65e0\u58f0\u5730\u8fdb\u884c\u7740\u3002 \u3000\u3000\u56db\u5e78\uff0c\u56db\u5e78\u2026\u2026\u5728\u5c0f\u6101\u5fc3\u91cc\uff0c\u90a3\u4e48\u54c0\u5f31\u65e0\u529b\u5730\u547c\u558a\u7740\uff0c\u76f4\u81f3\u4e24\u9053\u5f3a\u70c8\u7684\u5149\u7ebf\uff0c\u6025\u5267\u63a5\u8fd1\uff0c\u50cf\u731b\u517d\u4e00\u822c\u523a\u4eba\u4e86\u5979\u9ed1\u77b3\u91cc\u3002\u3002\u3002 \u3000\u3000\u7b2c\u4e8c\u5929\u4ee5\u5934\u6761\u520a\u8f7d\u4ee4\u4eba\u89e6\u76ee\u60ca\u5fc3\u7684\u65b0\u95fb\uff1a \u3000\u3000\u201c\u9752\u5e74\u6280\u51fb\u5bb6\u997c\u5e97\u5c11\u4e1c\u8521\u56db\u5e78\u60e8\u6b7b \u3000\u3000\u6df1\u591c\u9ed1\u706b\u711a\u8eab\u5973\u53cb\u76ee\u7779\u75db\u4e0d\u6b32\u751f\u201d \u3000\u3000\u4eba\u4eec\u8bae\u8bba\u7eb7\u7eb7\uff1a\u4e00\u5411\u9a81\u52c7\u5584\u6218\u3001\u65e0\u754f\u8270\u9669\u3001\u8eab\u6000\u7edd\u6280\u7684\u8521\u56db\u5e78\uff0c\u5230\u5e95\u662f\u600e\u4e48\u6b7b\u7684\uff1f \u3000\u3000\u90a3\u5929\u665a\u4e0a\uff0c\u8521\u56db\u5e78\u548c\u5f20\u5c0f\u6101\u4e3a\u4f55\u5728\u90a3\u79cd\u8352\u50fb\u7684\u5730\u65b9\u9017\u7559\uff1f \u3000\u3000\u2014\u2014\u5f20\u5c0f\u6101\u5230\u5e95\u89c1\u7740\u4e86\u4e9b\u4ec0\u4e48\uff1f \u3000\u3000\u5927\u5bb6\u5404\u81ea\u63e3\u607b\u3001\u5f20\u5c0f\u6101\u5728\u53d1\u751f\u4e8b\u60c5\u4e4b\u540e\u3001\u4e00\u76f4\u4e0d\u63a5\u53d7\u8bb0\u8005\u7684\u8bbf\u95ee\uff0c\u4e5f\u4ece\u4e0d\u80af\u4eae\u76f8\u3002 \u3000\u3000\u8c01\u77e5\u9053\u90a3\u4e00\u665a\u7a76\u7adf\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\uff1f \u3000\u3000\u8c01\u8981\u77e5\u9053\u90fd\u5fc5\u987b\u7b49\u3002 \u3000\u3000\u2014\u2014\u81f3\u5c11\u8981\u7b49\u5230\u201c\u4e0d\u5e73\u793e\u201d\u7684\u90a3\u51e0\u4e2a\u8fdc\u65b9\u6765\u5ba2\u6765\u5230\u6b64\u5730\u4ee5\u540e\u2026\u2026 \u3000 \u3000 \u7b2c\u4e8c\u7ae0\u3000\u98de\u673a\u91cc\u7684\u5371\u673a \u3000 \u3000\u3000\uff11\u3001\u673a\u5bc6 \u3000\u3000\u201c\u4e0d\u5e73\u793e\u201d\u7684\u201c\u5185\u90e8\u8d44\u6599\u201d\u91cc\uff0c\u5206\u4e24\u79cd\u201c\u8d44\u6599\u201d\uff0c\u4e00\u79cd\u662f\u201c\u4e00\u822c\u7b80\u4ecb\u201d\uff0c\u4e00\u79cd\u662f\u201c\u673a\u5bc6\u7ec6\u8282\u201d\u3002 \u3000\u3000\u5728\u201c\u4e0d\u5e73\u793e\u201d\u7684\u6838\u5fc3\u201c\u534e\u4eba\u5c0f\u7ec4\u201d\u91cc\uff0c\u6709\u201c\u516d\u4eba\u5e2e\u201d\u7684\u4ee3\u53f7\uff0c\u8fd9\u662f\u5176\u4e2d\u56fd\u4eba\u7684\u201c\u4e00\u822c\u7b80\u4ecb\u201d\u3002\u59d3\u540d\uff1a\u9648\u5251\u8c01\u3002\u6027\u522b\uff1a\u7537\u3002\u5e74\u5c81\uff1a\u4e09\u5341\u4e09\u3002\u9ad8\u5ea6\uff1a\u4e94\u5c3a\u4e09\u5bf8\u534a\u3002\u5b66\u5386\uff1a\u5ff5\u8fc7\u56db\u95f4\u4e0d\u540c\u5927\u5b66\u4e0d\u540c\u7684\u5b66\u7cfb\uff0c\u5168\u672a\u6bd5\u4e1a\u5373\u4f11\u5b66\u3001\u8fdf \u3000\u3000\u5b66\u3002\u5bb6\u4e16\uff1a\u89c1\u201c\u673a\u5bc6\u201d\u4e03\u53f7\u3002\u6210\u529f\uff1a\u6d41\u6d3e\u590d\u6742\uff0c\u81f3\u5c11\u7cbe\u901a\u516d\u5341\u4e03\u79cd\u4e2d\u56fd\u6b66\u672f\uff0c\u8fde\u9488\u7078\u3001\u70b9 \u3000\u3000\u7a74\u3001\u6c14\u529f\u3001\u8f7b\u529f\u4e5f\u6709\u6781\u9ad8\u7684\u9020\u8be3\u3002\u7231\u597d\uff1a\u53e4\u602a\u5174\u8da3\u6781\u591a\uff0c\u5bf9\u4e00\u5207\u5174\u8da3\u5747\u6709\u4e13\u4e1a\u8ba4\u8bc6\uff0c\u8be6\u89c1\u201c\u673a\u5bc6\u201d\u4e2a\u6027\uff1a\u5916\u8868\u575a\u5f3a\uff0c\u5185\u5fc3\u8106\u5f31\uff0c\u4e00\u65e6\u52a8\u60c5\uff0c\u4e0d\u53ef\u81ea\u62d4\u3002\u8be6\u89c1\u673a\u5bc6\u7279\u6027\uff1a\u4e00\u3001\u80fd\u5728\u5371\u9669\u6765\u4e34\u524d\u4e00\u523b\u9884\u611f\u9632\u8303\uff0c\u4e8c\uff0c\u5bf9\u4efb\u4f55\u6b66\u529f\u548c \u3000\u3000\u654c\u624b\uff0c\u90fd\u80fd\u5728\u5bf9\u654c\u4e4b\u9645\u5373\u65f6\u800c\u53c8\u53ca\u65f6\u5730\u60f3\u51fa\u4e00\u5957\u7834\u89e3\u4e4b\u6cd5\uff1b\u4e09\u3001\u5fc3\u80a0\u5927\u8f6f\uff0c\u6709\u65f6\u5987\u4eba\u4e4b\u4ec1\uff0c\u4e3a\u654c\u6240\u8d81\uff1b\u56db\u3002\u53ef\u4ee5\u540c\u65f6\u770b\u4e09\u90e8\u7535\u89c6\u3001\u540c\u65f6\u9605\u8bfb\u4e24\u672c\u4e66\u3001\u6216\u4e00\u9762\u770b\u4e66\u4e00\u9762\u4e0e\u4eba\u4ea4\u8c08\u3001\u751a\u81f3\u4e00\u9762\u770b\u620f\u4e00\u9762\u4e0e\u4eba\u5bf9\u6253\uff0c\u800c\u5bf9\u60c5\u8282\u5185\u5bb9\u4f9d\u7136\u53ef\u89c2\u5bdf\u5f97\u5de8\u7ec6\u65e0\u9057\uff0c\u8fc7\u76ee\u4e0d\u5fd8\u3002\u4e94\u3001\u5bf9\u624b\u8d8a\u5f3a\uff0c\u5904\u5883\u6108\u662f\u6076\u52a3\uff0c\u6108\u80fd\u53d1\u6325\u4ed6\u575a\u6bc5\u6027\u60c5\u8eab\u4efd\uff1a\u4e0d\u5e73\u793e\uff0c\u4e4b\u201c\u516d\u4eba\u5e2e\u201d\u9f99\u5934\u3002\u52a0\u5165\u6027\u8d28\uff1a\u521b\u529e\u4eba\u4e4b\u4e00\u3002\u6ce8\uff1a\u7279\u6b8a\u4eba\u7269\uff0c\u5fc5\u987b\u53c2\u8003\u201c\u5bc6\u4ef6\u5341\u516b\uff0c\u4e94\u5341\u4e03\uff0c\u516b\u5341\u4e00\u3001\u4e00\u96f6\u4e09\u3001\u4e00\u4e00\u4e94\u7b49\u201d\u3002\u59d3\u540d\uff1a\u725b\u4e3d\u751f\u3002 \u3000\u3000\uff1d\u201c\u201d\u5f13\u6027\u522b\uff1a\u7537\u3002\u5e74\u9f84\uff1a\u5341\u4e5d\u3002 \u3000\u3000\u201c\u3000\u3000l\u201dD\u9ad8\u5ea6\uff1a\u516d\u5c3a\u56db\u5bf8\u534a\u3002\u5b66\u5386\uff1a\u5c0f\u5b66\u672a\u6bd5\u4e1a\u3002\u5bb6\u4e16\uff1a\u4e2d\u56fd\u5927\u9646\u79fb\u6c11\uff0c\u519c\u6c11\u4e4b\u5b50\u3002 \u3000\u3000\u6b66\u529f\uff1a\u4e00\u5207\u786c\u95e8\u6b66\u529f\uff0c\u5305\u62ec\u7f55\u89c1\u751a\u6216\u5df1\u5931\u4f20\u7684\uff0c\u201c\u5341\u4e09\u592a\u4fdd\u6a2a\u7ec3\u201d\u3001\u201c\u91d1\u949f\u7f69\u201d\u3001\u201c\u94c1\u5934\u529f\u201d\u3001\u201c\u7ae5\u5b50\u529f\u201d\u3001\u201c\u94c1\u5e03\u886b\u201d\u3001\u201c\u4e5d\u725b\u4e8c\u864e\u795e\u529f\u201d\u3001\u201c\u91d1\u521a\u4e0d\u574f\u4e4b\u8eab\u201d\u7b49\u3002\u7231\u597d\uff1a\u89c1\u673a\u5bc6\u56db\u5341\u516b\u3002\u6ce8\uff1a\u6b64\u4eba\u7279\u522b\u8d2a\u7761\uff01\u4e2a\u6027\uff1a \u3000\u3000\u5584\u6076\u65e0\u5b9a\uff0c\u55dc\u6218\u82e5\u72c2\u3002\u7279\u6027\uff1a\u5929\u751f\u795e\u529b\uff0c\u9a81\u52c7\u5584\u6218\uff0c\u4f46\u667a\u6167\u4f4e\uff0c\u4e14\u6b7b\u5fc3\u773c\uff0c\u5929\u6027\u5341\u5206\u5b5d\u987a\uff0c\u89c1\u5973\u6027\u6613\u5bb3\u71e5\uff0c\u6015\u5b64\u72ec\uff0c\u6613\u95ef\u7978\u3002\u8eab\u4efd\uff1a\u201c\u4e0d\u5e73\u793e\u201d\u4e4b\u201c\u516d\u4eba\u5e2e\u201d\u8001\u5e7a\u3002\u52a0\u5165\u6027\u8d28\uff1a\u7f18\u3002\u9648\u5251\u8c01\u5f15\u8350\u3002\u59d3\u540d\uff1a\u9a86\u94c3\u3002\u6027\u522b\uff1a\u5973\u3002\u5e74\u9f84\uff1a\u4e8c\u5341\u56db\u3002\u9ad8\u5ea6\uff1a\u4e94\u5c3a\u4e94\u5bf8\u3002\u5b66\u5386\uff1a\u9999\u6e2f\u67d0\u5927\u5b66\u8086\u4e1a\uff0c\u82f1\u56fd\u4e00\u5927\u5b66\u7855\u58eb\u3002\u5ff5\u4e66\u65f6\u66fe\u6709\u5947\u9022\u3002\u5b66\u79d1\u65b9\u9762\u8be6\u89c1\u673a\u5bc6\u4e8c\u4e09\u4e09\u3002\u5bb6\u4e16\uff1a\u5bcc\u5bb6\u5973\uff0c\u8be6\u89c1\u673a\u5bc6\u4e8c\u4e09\u4e09\u3002\u6b66\u529f\uff1a\u67d4\u9053\u3001\u5408\u6c14\u9053\u3001\u897f\u6d0b\u5251\u3001\u4e2d\u56fd\u5251\uff0c\u9664\u5584\u4e8e\u7528\u836f\u4e4b\u5916\uff0c\u4f59\u7686\u4e0d\u751a\u9ad8\u660e\u3002\u6ce8\uff1a\u9a86\u94c3\u672c\u4eba\u4e3a\u4e86\u8981\u5220\u9664\u4e0a\u53e5\u672a\u4e4b\u8bc4\u8bed\uff0c\u66fe\u4e03\u5ea6\u626c\u8a00\u8981\u9000\u51fa\u201c\u4e0d\u5e73\u793e\u201d\uff0c\u5f97\u9648\u5251\u8c01\u5927\u529b\u529d\u89e3\u624d\u6253\u6d88\u6b64\u5ff5\u3002\u7231\u597d\uff1a\u73a9\uff0c\u6d6a\u6f2b\uff0c\u505a\u5927\u4e8b\u3002\u4e2a\u6027\uff1a\u5c0f\u59d0\u813e\u6c14\u3002\uff0a\u5220\u4e2d\u95f4\u4e8c\u5b57\u4ea6\u53ef\uff0a\u7279\u6027\uff0c\u81ea\u5c0a\u5fc3\u5f3a\u3001\u81ea\u4fe1\u5fc3\u5f3a\uff0c\u53ef\u60dc\u81ea\u7acb\u81ea\u6d4e\u80fd\u529b\u4e0d\u9ad8\uff0c\u6ce8\uff1a\u540e\u9762\u8bc4\u8bed\u5728\u9a86\u94c3\u540c\u5fd7\u8981\u6c42\u4e0b\u5220\u53bb\u3002\u8eab\u4efd\uff1a\u201c\u4e0d\u5e73\u793e\u201d\u4e4b\u201c\u516d\u4eba\u5e2e\u201d\u7684\u201c\u5c0f\u56db\u59b9\u59b9\u201d\u3002\u52a0\u5165\u6027\u8d28\uff1a\u56db\u5341\u4e94\u6b21\u7533\u8bf7\u59cb\u7ecf\u6279\u51c6\u3002\u59d3\u540d\uff1a\u53f2\u6d41\u82b3\u3002\u5b66\u5386\uff1a\u53f0\u6e7e\u5927\u5b66\u7855\u58eb\uff0c\u7f8e\u56fd\u67d0\u5927\u5b66\u535a\u58eb\u3002\u5bb6\u4e16\uff1a\u4e66\u9999\u4e16\u4ee3\uff0c\u516c\u52a1\u5458\u4e4b\u5b50\u3002\u6b66\u529f\uff1a\u51e0\u4e4e\u4e00\u5207\u5206\u6bb5\u7ea7\u8003\u5e26\u7684\u6b66\u529f\u4ed6\u90fd\u6709\u5934\u8854\uff0c\u5408\u79f0\u4e09\u5341\u4e03\u6bb5\uff0c\u4e14\u6709\u7279\u6b8a\u201c\u8d85\u80fd\u529b\u201d\uff0c\u8be6\u89c1\u201c\u673a\u5bc6\u201d\u4e09\u5341\u4e03\u3002\u7231\u597d\uff1a\u559c\u6b22\u5f53\u5b66\u8005\uff0c\u5e0c\u671b\u6210\u4fa0\u8005\u3002 \u3000\u3000\u4e2a\u6027\uff1a\u77db\u76fe\u3002\u7279\u6027\uff1a\u7231\u5439\u725b\u3001\u5145\u4e13\u5bb6\uff0c\u597d\u8272\u800c\u4e0d\u627f\u8ba4\uff0c\u81ea\u547d\u98ce\u6d41\u5374\u4e0d\u98ce\u6d41\u7231\u5927\u8c08\u5b66\u95ee\uff0c\u662f\u4e2a\u7406\u60f3\u4e3b\u4e49\u8005\u3002\u8eab\u4efd\uff1a\u201c\u4e0d\u5e73\u793e\u201d\u4e4b\u201c\u516d\u4eba\u5e2e\u201d\u7684\u4e09\u5f53\u5bb6\u3002\u52a0\u5165\u6027\u8d28\uff1a \u3000\u3000\u732e\u8eab\u7406\u60f3\u3002 \u3000\u3000\u6e9c\u201c \u3000\u3000\u8fd9\u53ea\u662f\u201c\u4e0d\u5e73\u793e\u201d\u7684\u5185\u90e8\u4e00\u822c\u8d44\u6599\u3002\u636e\u8bf4\uff0c\u5728\u201c\u673a\u5bc6\u6587\u4ef6\u91cc\uff0c\u4e00\u5207\u6709\u5173\u8fd9\u51e0\u4eba\u7684\u91cd\u8981\u8d44\u6599\uff0c\u90fd\u6536\u5165\u6863\u6848\u91cc\uff0c\u5176\u7ec6\u5fae\u7684\u7a0b\u5ea6\uff0c\u751a\u81f3\u8fd8\u5305\u62ec\u4e86\uff1a\u9648\u5251\u8c01\u8ddf\u5973\u4eba\u9020\u7231\u7684\u65f6\u5019\uff0c\u559c\u7528\u820c\u5c16\u8f7b\u8214\u5979\u4eec\u7684\u4e73\u5c16\uff1a\u53f2\u6d41\u82b3\u7231\u4e0a\u6d17\u624b\u95f4\uff0c\u4e00\u8fb9\u51fa\u606d\u4e00\u8fb9\u770b\u4e66\uff1b\u9a86\u94c3\u559c\u6b22\u996d\u540e\u5403\u751c\u54c1\uff0c\u8fde\u5507\u818f\u90fd\u9009\u62e9\u6709\u751c\u5473\u7684\uff1b\u725b\u4e3d\u751f\u6700\u559c\u6b22\u653e\u5c41\uff0c\u4e0d\u7ba1\u662f\u8c01\u4e5f\u4e0d\u53ef\u4ee5\u6709\u4e00\u8bed\u8fb1\u53ca\u4ed6\u7684\u6bcd\u4eb2\u7b49\u7b49\uff0c\u8fde\u8fd9\u4e9b\u751f\u6d3b\u7684\u7ec6\u8282\u4e0e\u4e60\u6027\u90fd\u7eaa\u5f55\u5f97\u5982\u6b64\u5de8\u7ec6\u65e0\u9057\uff0c\u4ee5\u81f4\u8c01\u770b\u5230\u8fd9\u4e9b\u8d44\u6599\uff0c\u8c01\u5c31\u5927\u53ef\u4ee5\u628a\u4ed6\u4eec\u201c\u63a7\u5236\u201d\u5728\u624b\u91cc\u3002 \u3000\u3000\u201c\u516d\u4eba\u5e2e\u201d\u5f53\u7136\u6709\u516d\u4e2a\u4eba\u3002 \u3000\u3000\u4e0a\u9762\u53ea\u6709\u4e94\u4e2a\u4eba\u7684\u8d44\u6599\u3002 \u3000\u3000\u5f53\u7136\u7f3a\u4e86\u4e00\u4e2a\u3002 \u3000\u3000\u2014\u2014\u8001\u4e8c\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u8fd9\u4e00\u6b21\uff0c\u201c\u8001\u4e8c\u201d\u5e76\u6ca1\u6709\u5357\u884c\uff0c\u6240\u4ee5\u5e76\u4e0d\u6025\u7740\u4ecb\u7ecd\u4ed6\u7684\u8d44\u6599\u3002 \u3000\u3000\u56e0\u4e3a\u8fd9\u4e9b\u8d44\u6599\u592a\u91cd\u8981\u4e86\uff0c\u6240\u4ee5\u4ea4\u7ed9\u4e00\u4e2a\u7edd\u5bf9\u53ef\u4ee5\u4fe1\u8d56\u7684\u4eba\u6536\u85cf\u3002 \u3000\u3000\u4ed6\u6700\u91cd\u5927\u7684\u4efb\u52a1\u4fbf\u662f\u6536\u85cf\u8fd9\u4e9b\u8d44\u6599\u3002 \u3000\u3000\u4ed6\u5b81\u53ef\u628a\u8fd9\u4e9b\u8d44\u6599\u70b9\u71c3\u6216\u541e\u5230\u809a\u5b50\u91cc\u53bb\uff0c\u4e5f\u4e0d\u4f1a\u8ba9\u5b83\u843d\u5728\u522b\u4eba\u7684\u624b\u4e0a\u3002 \u3000\u3000\u670b\u53cb\u4fe1\u4efb\u4ed6\uff0c\u4ed6\u5c31\u8ba9\u670b\u53cb\u4fe1\u4efb\u3002 \u3000\u3000\u5728\u6574\u4e2a\u201c\u4e0d\u5e73\u793e\u201d\u91cc\uff0c\u5c31\u53ea\u6709\u4ed6\u81ea\u5df1\u6ca1\u6709\u8d44\u6599\u6863\u6848\u3002 \u3000\u3000\u81f3\u4e8e\u4ed6\u662f\u8c01\uff0c\u5c31\u53ea\u6709\u201c\u4e0d\u5e73\u793e\u201d\u7684\u4e09\u4e2a\u201c\u5934\u5934\u513f\u201d\u77e5\u9053\uff0c\u5176\u4e2d\u5f53\u7136\u5305\u62ec\u4e86\u9648\u5251\u8c01\u3002 \u3000\u3000\uff12\u3001\u52ab\u673a\u8005\u7684\u52a8\u673a \u3000\u3000\u9648\u5251\u8c01\u3001\u9a86\u94c3\u3001\u53f2\u6d41\u82b3\u3001\u725b\u4e3d\u751f\u5728\u8c6a\u534e\u5ba2\u673a\u4e0a\uff0c\u518d\u8fc7\u4e00\u5c0f\u65f6\u4e8c\u5341\u4e94\u5206\uff0c\u5c31\u8981\u5230\u8fbe\u4ed6\u4eec\u7684\u76ee\u7684\u5730\u3002 \u3000\u3000\u53f2\u6d41\u82b3\u5fd8\u4e86\u628a\u4e66\u5e26\u8fdb\u624b\u63d0\u884c\u674e\u91cc\uff0c\u6240\u4ee5\u4eba\u5728\u5ea7\u4f4d\u4e0a\uff0c\u6d51\u8eab\u7684\u4e0d\u81ea\u5728\uff0c\u628a\u98de\u673a\u4e0a\u7684\u65c5\u6e38\u6307\u5357\uff0c\u76ee\u7684\u5730\u4ecb\u7ecd\u3001\u6551\u751f\u6307\u793a\u751a\u81f3\u8fde\u536b\u751f\u888b\u7528\u6cd5\uff0c\u5168\u90e8\u4e00\u4e00\u8bfb\u904d\u4e86\u3002 \u3000\u3000\u5e78\u597d\uff0c\u4ed6\u6b63\u767e\u822c\u65e0\u804a\u7684\u65f6\u5019\uff0c\u627e\u5230\u4e86\u4e00\u6837\u5341\u5206\u53ef\u8bfb\u7684\u201c\u4e1c\u897f\u201d\u3002 \u3000\u3000\u7a7a\u4e2d\u5c0f\u59d0\u9001\u9910\u540e\u9152\u6765\u3002 \u3000\u3000\u4ed6\u968f\u624b\u63a5\u8fc7\uff0c\u7a81\u7136\u53d1\u73b0\uff0c\u90a3\u9a6c\u6765\u7a7a\u4e2d\u5c0f\u59d0\u773c\u775b\u6df1\u6df1\u3001\u7b11\u610f\u751c\u751c\uff0c\u662f\u4e2a\u6807\u81f4\u7684\u7f8e\u4e3d\u5973\u5b50\uff01 \u3000\u3000\u7a7a\u4e2d\u5c0f\u59d0\u628a\u996e\u54c1\u9012\u4e86\u7ed9\u4ed6\uff0c\u5c31\u8f6c\u8eab\u626d\u599e\u7740\u8170\u80a2\uff0c\u5230\u524d\u9762\u7684\u8d70\u9053\u4e0a\u53bb\u4e86\u3002 \u3000\u3000\u7559\u7ed9\u4ed6\u6ee1\u773c\u7684\u66f2\u7ebf\u3002 \u3000\u3000\u53f2\u6d41\u82b3\u624b\u91cc\u62ff\u7740\u751c\u9152\uff0c\u8fd8\u672a\u559d\uff0c\u5fc3\u5df2\u751c\u5f97\u6ee1\u6ee1\u7684\u3002 \u3000\u3000\u4e5f\u8bb8\u662f\u53f2\u6d41\u82b3\u770b\u5f97\u5927\u4e13\u6ce8\uff0c\u592a\u7528\u795e\u4e4b\u6545\u5427\uff0c\u90a3\u7a7a\u4e2d\u5c0f\u59d0\u4eff\u4f5b\u80cc\u4e0a\u611f\u89c9\u5230\u90a3\u707c\u70ed\u7684\u7535\u6ce2\uff0c\u5ffd\u56de\u8fc7\u5934\u6765\uff0c\u773c\u795e\u641c\u7d22\u4e00\u4e0b\uff0c\u5c31\u53d1\u73b0\u90a3\u4e2a\u62ff\u53f0\u6e7e\u62a4\u7167\u7684\u82f1\u4fca\u7537\u5b50\uff0c\u6b63\u900f\u8fc7\u5de6\u53f3\u52a0\u8d77\u6765\u81f3\u5c11\u4e00\u5343\u56db\u767e\u5ea6\u7684\u9ed1\u6846\u773c\u955c\u7247\u6014\u4e4e\u4e4e\u7684\u671b\u7740\u5979\u3002 \u3000\u3000\u5979\u4e0d\u89c9\u4e00\u7b11\u3002 \u3000\u3000\u6bd4\u7cd6\u8863\u8fd8\u751c\u3002 \u3000\u3000\u7136\u540e\u5c31\u8d70\u4e86\u3002 \u3000\u3000\u5979\u5fd9\u7740\u5462\u3002\u3000\u3000\u3002 \u3000\u3000\u201c\u5929\uff0c\u201d\u53f2\u6d41\u82b3\u5583\u5583\u5730\u9053\u3002\u4ed6\u5df2\u653e\u4e0b\u4ed6\u624b\u4e0a\u7684\u822a\u7ebf\u56fe\u4e86\uff1a\u201c\u6bd5\u7ade\uff0c\u5c31\u7b97\u4e66\u4e2d\u6709\u9ec4\u91d1\u5c4b\uff0c\u4e5f\u7edd\u6ca1\u6709\u989c\u5982\u7389\uff0c\u201c\u5929\uff01\u201d \u3000\u3000\u7136\u540e\u4ed6\u5174\u81f4\u52c3\u52c3\u7684\u56de\u8fc7\u5934\u6765\uff0c\u5411\u9664\u5251\u8c01\u9053\uff1a\u201c\u5929\uff01\u4f60\u77e5\u9053\u6211\u770b\u89c1\u4ec0\u4e48\u4e86\uff1f\u201d \u3000\u3000\u9648\u5251\u8c01\u6b63\u60f3\u7740\u4e8b\u60c5\uff0c\u53f2\u6d41\u82b3\u7684\u5174\u5934\u4e00\u6765\uff0c\u4ed6\u77e5\u9053\u81ea\u5df1\u65e0\u8bba\u5982\u4f55\u90fd\u60f3\u4e0d\u4e0b\u53bb\u4e86\uff0c\u53ea\u597d\u8bf4\uff1a \u3000\u3000\u201c\u5929\u6b63\u5728\u7a97\u5916\u3002\u201d \u3000\u3000\u201c\u554a\uff0c\u4e0a\u5e1d\uff01\u201d\u53f2\u6d41\u82b3\u8bf4\uff1a\u6211\u4ece\u6765\u6ca1\u6709\u770b\u8fc7\u90a3\u4e48\u751c\u7684\u7b11\u5bb9\uff0c\u4e0a\u5e1d\uff01\u751c\u5f97\u5c31\u50cf\u4e00\u7c92\u6930\u5b50\u7cd6\u3002\u201d \u3000\u3000\u9648\u5251\u8c01\u53f9\u4e86\u4e00\u58f0\uff0c\u5341\u6307\u5408\u5728\u8179\u4e0a\uff0c\u5b89\u8be6\u4ed6\u8bf4\uff1a\u201c\u4e0a\u5e1d\u4ed6\u8001\u4eba\u5bb6\u771f\u5fd9\uff01\u201d\u3000\u3000\u3002\u3001\uff0f\u2019\uff0f\u3001 \u3000\u3000\u53f2\u6d41\u82b3\u4ecd\u5583\u5583\u81ea\u8bed\uff0c\u201c\u8fd8\u6709\u90a3\u81c0\u90e8\u2026\u2026\u6211\u4ece\u6765\u5c31\u6ca1\u770b\u8fc7\u4e48\u597d\u770b\u7684\u81c0\u90e8\u2026\u2026\u201d \u3000\u3000\u7761\u773c\u6726\u80e7\u7684\u725b\u4e3d\u751f\u6a21\u7cca\u95f4\u542c\u5230\u4e86\u4e00\u70b9\u4ec0\u4e48\uff0c\u52c9\u5f3a\u7741\u5f00\u4e00\u7ebf\u7f1d\u7684\u773c\u775b\uff1a\u201c\u4ec0\u4e48\u7535\u2026\u2026\u98de\u673a\u4e0a\u54ea\u6709\u5e97\u9986\uff01\u201d \u3000\u3000\u53f2\u6d41\u82b3\u767b\u65f6\u6ca1\u597d\u6c14\uff1a\u201c\u81c0\u90e8\uff0c\u6211\u8bf4\u7684\u662f\u4eba\u7684\u80cc\u540e\u90a3\u2026\u2026\u90a3\u4e00\u5904\u7684\u81c0\u90e8\uff0c\u4ec0\u4e48\u5e97\u94fa\uff01\u201d \u3000\u3000\u201c\u54e6\uff0c\u201d\u725b\u4e3d\u751f\u723d\u5feb\u7684\u8bf4\uff1a\u201c\u4f60\u8bf4\u7684\u662f\u5c41\u822c\uff01\u201d\u4fa7\u4e00\u4e2a\u8eab\uff0c\u53c8\u547c\u547c\u5730\u7761\u7740\u4e86\u3002 \u3000\u3000\u201c\u4f60\uff01\u201d\u53f2\u6d41\u82b3\u7528\u624b\u6258\u4e86\u6258\u773c\u955c\uff0c\u53ea\u597d\u65e0\u5948\u5730\u9053\uff1a\u201c\u4f60\u2026\u4f60\u8fd9\u4e2a\u7c97\u4eba\uff01\u201d \u3000\u3000\u725b\u4e3d\u751f\u6ca1\u542c\u89c1\u3002 \u3000\u3000\u4ed6\u65e9\u5df2\u795e\u6e38\u5230\u4e5d\u9704\u4e91\u5916\u60ca\u4ed6\u7684\u9b42\u53bb\u4e86\u3002 \u3000\u3000\u4ed6\u4f53\u79ef\u5e9e\u5927\uff0c\u5728\u8fd9\u9760\u53f3\u8fb9\u7684\u53cc\u4eba\u5ea7\u4f4d\u4e0a\uff0c\u4ed6\u5f80\u7a97\u8fb9\u90a3\u4e48\u4e00\u5750\uff0c\u808c\u8089\u90e8\u8d85\u8fc7\u4e2d\u95f4\u7684\u6276\u624b\uff0c\u6324\u5230\u9664\u5251\u8c01\u90a3\u513f\u53bb\u4e86\u3002 \u3000\u3000\u9648\u5251\u8c01\u53ea\u597d\u9053\uff1a\u201c\u6211\u5230\u90a3\u513f\u5750\u5750\u3002\u201d\u53f3\u624b\u8fb9\u8fd8\u6709\u51e0\u4e2a\u7a7a\u4f4d\uff0c\u6709\u4e00\u4e2a\u83f2\u6d25\u5bbe\u5987\u4eba\u5750\u5728\u90a3\u513f\uff0c\u73e0\u5149\u5b9d\u6c14\uff0c\u4e00\u526f\u8d35\u592b\u4eba\u7684\u6c14\u6d3e\u3002 \u3000\u3000\u53f2\u6d41\u82b3\u5e78\u707e\u4e50\u7978\uff1a\u201c\u4f60\u4e0d\u4f46\u8981\u4e89\u53d6\u7a7a\u95f4\uff0c\u8fd8\u8981\u4e89\u53d6\u65f6\u95f4\u54e9\uff0c\u54b1\u4eec\u4e0d\u4e45\u5c31\u8981\u5230\u4e86\uff01\u201d \u3000\u3000\u201c\u4e0d\u662f\uff0c\u201d\u9648\u5251\u8c01\u6f84\u6e05\uff0c\u201c\u6211\u662f\u8981\u5438\u6536\u65b0\u9c9c\u7a7a\u6c14\uff0c\u963f\u725b\u8fde\u7761\u7740\u4e86\u4e5f\u4e0d\u5fd8\u6c61\u67d3\u7a7a\u6c14\u3002\u201d \u3000\u3000\u4e00\u542c\u201c\u6c61\u67d3\u7a7a\u6c14\u201d\uff0c\u53f2\u6d41\u82b3\u4e5f\u9a6c\u4e0a\u95fb\u5230\u5f02\u5473\u4e86\uff1a\u201c\u7a7a\u6c14\u6c61\u67d3\u201d\u662f\u4ed6\u4eec\u7684\u7528\u8bcd\uff0c\u610f\u6307\u201c\u653e\u5c41\u201d\uff0c\u800c\u725b\u4e3d\u751f\u5c31\u662f\u6700\u7231\u653e\u5c41\u7684\u3002 \u3000\u3000\u53f2\u6d41\u82b3\u8d70\u907f\u4e0d\u8fed\uff0c\u8d81\u673a\u53bb\u8ddf\u90a3\u7a7a\u4e2d\u5c0f\u59d0\u642d\u6c55\u3002\u4ed6\u5750\u5728\u7a97\u8fb9\u4f4d\u5b50\uff0c\u8981\u8d70\u51fa\u53bb\uff0c\u53ea\u597d\u6324\u8fc7\u5750\u5728\u9760\u8d70\u9053\u4f4d\u7f6e\u9a86\u94c3\u7684\u8eab\u524d\u3002 \u3000\u3000\u9a86\u94c3\u767d\u4e86\u4ed6\u4e00\u773c\uff1a\u201c\u8ba8\u538c\uff0c\u8001\u662f\u5750\u7acb\u4e0d\u5b89\u7684\uff0c\u96be\u538b\u805a\u4e0d\u4e86\u8d22\uff01\u201d\u5374\u8fd8\u662f\u5750\u76f4\u4e86\u8eab\u5b50\uff0c\u8ba9\u53f2\u6d41\u82b3\u884c\u51fa\u6765\u3002 \u3000\u3000\u53f2\u6d41\u82b3\u6258\u4e86\u6258\u773c\u955c\u6846\uff0c\u4f4e\u58f0\u7528\u534e\u8bed\u8ddf\u5979\u7b11\u8bf4\uff1a\u201c\u6211\u53ea\u662f\u5750\u7acb\u4e0d\u5b89\uff0c\u4f60\u662f\u4e0d\u5b89\u4e8e\u5ba4\u4e86\u3002\u201d \u3000\u3000\u539f\u6765\u9a86\u94c3\u6b63\u8ddf\u9694\u7740\u8d70\u9053\u5de6\u8fb9\u4e00\u540d\u6d0b\u4eba\u8c08\u5929\u3002\u90a3\u6d0b\u4eba\u957f\u5f97\u9ad8\u5927\u9b41\u68a7\uff0c\u82f1\u4f1f\u975e\u51e1\uff0c\u4e0a\u673a\u540e\u4e00\u89c1\u9a86\u94c3\uff0c\u4fbf\u5931\u4e86\u9b42\u4f3c\u7684\uff0c\u4e09\u756a\u56db\u6b21\u3001\u4e94\u56de\u516d\u906d\u7684\u8ddf\u9a86\u94c3\u642d\u6c55\uff0c\u4ed6\u6b63\u8bf4\u5230\u534e\u4eba\u5f88\u52e4\u52b3\uff0c\u4e0d\u8fc7\u5374\u8001\u662f\u4eff\u5236\u4ed6\u4eec\u7684\u4ea7\u7269\uff0c\u201c\u4f8b\u5982\u624b\u8868\u3002\u201d\u4ed6\u8bf4\uff0c\u7528\u82f1\u8bed\u3002\u201c\u5b9e\u5728\u5927\u6ca1\u521b\u610f\u4e86\u3002\u201d \u3000\u3000\u9a86\u94c3\u4e00\u5411\u6765\u8005\u4e0d\u62d2\uff0c\u89c9\u5f97\u8fd9\u6d0b\u4eba\u4e5f\u4e0d\u8ba8\u4eba\u538c\uff0c\u4e5f\u7b54\u7406\u4e86\u4ed6\u51e0\u53e5\uff0c\u624d\u77e5\u9053\u4ed6\u662f\u745e\u58eb\u4eba\u3002 \u3000\u3000\u8fd9\u745e\u58eb\u4eba\u6b63\u5343\u65b9\u767e\u8ba1\u7684\u60f3\u7ea6\u9a86\u94c3\u4eca\u665a\u8d74\u4ed6\u4e4b\u7ea6\u3002 \u3000\u3000\u53f2\u6d41\u82b3\u201c\u6324\u201d\u51fa\u53bb\u540e\uff0c\u8ddf\u90a3\u745e\u58eb\u4eba\u70b9\u4e86\u70b9\u5934\uff0c\u53c8\u5411\u9a86\u94c3\u6324\u7709\u5f04\u773c\u7684\u8bf4\uff1a\u201c\u5e08\u59b9\uff0c\u8d76\u5feb\u522b\u5750\u90a3\u4e48\u76f4\u3002\u201d \u3000\u3000\u9a86\u94c3\u5947\u9053\uff1a\u201c\u55ef\uff1f\u201d \u3000\u3000\u53f2\u6d41\u82b3\u5fcd\u7b11\u8bf4\uff1a\u201c\u4f60\u80f8\u524d\u5e76\u4e0d\u4f1f\u5927\uff0c\u5750\u592a\u76f4\u7531\u4fa7\u9762\u770b\uff0c\u66b4\u9732\u5f31\u70b9\u5566\u3002\u2019\u4e00\u8bf4\u5b8c\uff0c\u8fb9\u7b11\u8fb9\u8d70\u4e86\u5f00\u53bb\u3002 \u3000\u3000\u9a86\u94c3\u987f\u65f6\u6c14\u7ea2\u4e86\u8138\uff0c\u5f85\u610f\u4f1a\u5230\u4ed6\u8bdd\u91cc\u610f\u601d\u65f6\u53f2\u6d41\u82b3\u65e9\u5c31\u8d70\u8fdc\u4e86\u3002 \u3000\u3000\u90a3\u745e\u58eb\u4eba\u5f88\u6709\u793c\u8c8c\u7684\u95ee\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u9a86\u94c3\u5fd9\u8f6c\u8fc7\u8eab\u6765\uff0c\u8170\u90e8\u6ed1\u4e0b\u6905\u9760\u8db3\u6709\u56db\u5bf8\u4f59\u3001\u624d\u5c55\u989c\u7b11\u7b54\uff1a\u201c\u6ca1\u4ec0\u4e48\u3002\u201d \u3000\u3000\u745e\u58eb\u4eba\u72d0\u7591\u5730\u9053\uff1a\u201c\u4ed6\u662f\u2026\u2026\u201d \u3000\u3000\u9a86\u94c3\u7b54\uff1a\u201c\u6211\u7684\u4ec6\u4eba\u3002\u201d \u3000\u3000\u201c\u4ec6\u4eba\u2026\u2026\u201d\u745e\u58eb\u4eba\u771f\u65e0\u6cd5\u7f6e\u4fe1\uff0c\u4e0d\u8fc7\u4ed6\u603b\u7b97\u542c\u8bf4\u8fc7\u4e0d\u5c11\u7684\u4e2d\u56fd\u4eba\u4f20\u5947\uff0c\u4e5f\u4e0d\u6562\u591a\u95ee\u3002 \u3000\u3000\u7a81\u7136\uff0c\u673a\u4e0a\u53d1\u751f\u4e86\u6781\u5927\u7684\u53d8\u52a8\u3002 \u3000\u3000\u673a\u4e0a\u6709\u4e24\u4e2a\u76ae\u80a4\u9ed1\u9ec4\u7684\u9752\u5e74\uff0c\u75be\u7ad9\u4e86\u8d77\u6765\uff0c\u4e00\u4e2a\u4eba\u624b\u4e0a\u6709\u4e00\u67c4\u5c0f\u624b\u67aa\uff0c\u53e6\u4e00\u4eba\u624b\u4e0a\u7ade\u8fd8\u6709\u9897\u624b\u69b4\u5f39\u3002 \u3000\u3000\u4ed6\u4eec\u8fc5\u901f\u5730\u5404\u5360\u8d70\u9053\u4e00\u8fb9\uff0c\u7528\u82f1\u8bed\u5927\u58f0\u559d\u4ee4\uff1a\u201c\u4e0d\u8bb8\u52a8\uff0c\u52a8\u6211\u5c31\u5f15\u7206\u624b\u69b4\u5f39\u3002\u201d\u673a\u8231\u91cc\u7684\u4eba\u60ca\u547c\u3001\u60f6\u4e71\uff0c\u53e6\u4e00\u4eba\u5219\u62d8\u51fa\u4e00\u4e2a\u5c0f\u578b\u7684\u5587\u53ed\uff0c\u626c\u58f0\u63a7\u5236\u573a\u9762\uff0c\u201c\u4f60\u4eec\u9047\u4e0a\u52ab\u673a\u4e86\u3002\u6700\u597d\u5c31\u662f\u4e56\u4e58\u7684\u542c\u8bdd\uff0c\u6211\u4eec\u65e2\u6562\u52ab\u673a\uff0c\u4fbf\u5df2\u51c6\u5907\u597d\u4e00\u5207\u884c\u52a8\uff0c\u5305\u62ec\u6740\u4e86\u4e0d\u542c\u8bdd\u7684\u4eba\uff0c\u5fc5\u8981\u65f6\u8fde\u81ea\u5df1\u4e5f\u6740\u4e86\u3002\u201d \u3000\u3000\u7136\u540e\u4ed6\u7830\u4e00\u58f0\uff0c\u8e22\u5f00\u6d17\u624b\u95f4\u7684\u95e8\uff0c\u628a\u4e00\u540d\u6b63\u5728\u91cc\u9762\u89e3\u624b\u7684\u7626\u5b50\uff0c\u62d6\u51fa\u6765\u626f\u5230\u5ea7\u4f4d\u4e0a\u3002\u90a3\u7537\u5b50\u5413\u5f97\u88e4\u88c6\u5168\u6e7f\u4e86\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u53f2\u6d41\u82b3\u548c\u90a3\u9a6c\u6765\u7a7a\u59d0\u6b63\u597d\u5728\u8d70\u9053\u4e0a\uff0c\u90a3\u9a6c\u6765\u7a7a\u59d0\u5413\u5f97\u4f4e\u547c\u4e86\u4e00\u58f0\uff0c\u8fd9\u4eba\u4e00\u5df4\u638c\u628a\u90a3\u7a7a\u59d0\u63b4\u5012\u5728\u5730\uff0c\u53f2\u6d41\u82b3\u53f1\u9053\uff1a\u201c\u4f60\u600e\u4e48\u5bf9\u5973\u58eb\u90a3\u4e48\u7c97\u66b4\uff01\u201d\u90a3\u83f2\u7c4d\u7537\u5b50\u8fce\u9762\u5c31\u7ed9\u4ed6\u4e00\u811a\uff0c\u5598\u5728\u4ed6\u809a\u5b50\u4e0a\uff0c\u53f2\u6d41\u82b3\u987f\u65f6\u5f2f\u4e86\u8170\uff0c\u90a3\u6c49\u5b50\u7684\u67aa\u5634\u6b63\u5411\u7740\u4ed6\u3002\u53f2\u6d41\u82b3\u4e0d\u6562\u5984\u52a8\uff0c\u53ea\u8fc7\u53bb\u6276\u8d77\u90a3\u7a7a\u4e2d\u5c0f\u59d0\uff0c\u7a7a\u59d0\u7684\u5634\u89d2\u6dcc\u8840\uff0c\u60ca\u6050\u4e07\u5206\u3002 \u3000\u3000\u5728\u9648\u5251\u8c01\u8eab\u65c1\u7684\u90a3\u540d\u8d35\u592b\u4eba\uff0c\u4e5f\u5ffd\u5c14\u7ad9\u4e86\u8d77\u6765\u3002 \u3000\u3000\u9648\u5251\u8c01\u5750\u5728\u5979\u8eab\u65c1\uff0c\u6240\u4ee5\u80fd\u591f\u6e05\u695a\u5730\u770b\u89c1\uff1a\u90a3\u83f2\u5f8b\u5bbe\u5973\u4eba\u633d\u5728\u624b\u4e0a\u7684\u624b\u63d0\u888b\uff0c\u53ea\u5728\u51e0\u4e0b\u62c6\u6563\u626e\u51d1\u95f4\uff0c\u7adf\u5408\u6210\u4e86\u4e00\u633a\u5c0f\u578b\u7684\u673a\u67aa\u3002\u3000\u3000\uff1a\u201c\u3000\u3000\u201d\uff5e\u3001\u9648\u5251\u8c01\u4f3c\u4e4e\u6709\u6240\u52a8\u4f5c\uff0c\u4f46\u90a3\u83f2\u5f8b\u5bbe\u6253\u4eba\u5341\u5206\u8b66\u89c9\uff0c\u5df2\u8fc5\u5373\u8ddf\u4ed6\u4fdd\u6301\u8ddd\u79bb\uff0c\u540c\u65f6\uff0c\u4e00\u628a\u624b\u67aa\u5df2\u5bf9\u51c6\u4e86\u4ed6\u7684\u592a\u9633\u7a74\u3002 \u3000\u3000\u9648\u5251\u8c01\u4f5c\u4e86\u4e00\u4e2a\u8868\u793a\u8981\u5bf9\u65b9\u5b89\u5fc3\u7684\u624b\u52bf\u3001\u5750\u56de\u539f\u4f4d\u3002 \u3000\u3000\u539f\u6765\u8fd8\u6709\u4e00\u4e2a\u6ee1\u8138\u80e1\u5b50\u7684\u83f2\u5f8b\u5bbe\u4e2d\u5e74\u7537\u5b50\uff0c\u624b\u4e0a\u6709\u4e00\u67c4\u957f\u4e0d\u903e\u4e00\u6307\u7684\u8ff7\u4f60\u6cb9\u73cd\u578b\u624b\u67aa\uff0c\u5927\u6982\u53ea\u6709\u4e00\u53d1\u4e8e\u5f39\u7684\u90a3\u79cd\u7c7b\u578b\u3002 \u3000\u3000\u90a3\u5927\u80e1\u5b50\u4f3c\u662f\u9886\u8896\uff0c\u9547\u5b9a\u5730\u5429\u5490\uff1a\u201c\u7f57\u8fde\u5a1c\uff0c\u4f60\u5b88\u5728\u8fd9\u91cc\uff0c\u8c01\u52a8\u5c31\u5bb3\u4e86\u5927\u5bb6\u3002\u5a01\u5c14\u68ee\uff0c\u4f60\u8d1f\u8d23\u524d\u9762\uff0c\u8bb0\u4f4f\u4f60\u7684\u624b\u69b4\u5f39\uff0c\u975e\u5230\u5fc5\u8981\u65f6\u4e0d\u53ef\u7528\u4f46\u5fc5\u8981\u65f6\u52ff\u5fd8\u4e86\u7528\u3002\u5085\u83b1\u5179\uff0c\u4f60\u53bb\u5bf9\u4ed8\u9a7e\u9a76\u5ba4\u3002\u201d \u3000\u3000\u90a3\u4e8c\u7537\u4e00\u5973\u5e94\u9053\uff1a\u201c\u662f\uff0c\u5c06\u519b\u3002\u201d \u3000\u3000\u6301\u67aa\u7684\u6c49\u5b50\u5085\u83b1\u5179\u5373\u8fd4\u8eab\u5f80\u9a7e\u9a76\u8231\u63a9\u53bb\u3002 \u3000\u3000\u62ff\u624b\u69b4\u5f39\u7684\u6c49\u4e88\u5a01\u5c14\u68ee\u4e00\u811a\u8e39\u5728\u53f2\u6d41\u82b3\u7684\u5c41\u80a1\u4e0a\uff0c\u65a5\u559d\uff1a\u201c\u56de\u4f60\u7684\u5ea7\u4f4d\u4e0a\u53bb\u3002\u201d \u3000\u3000\u9648\u5251\u8c01\u7528\u773c\u89d2\u4e00\u77a5\uff0c\u5374\u89c1\u725b\u4e3d\u751f\u4ecd\u5728\u547c\u547c\u5927\u7761\u3002 \u3000\u3000\u53f2\u6d41\u82b3\u8e09\u8e09\u545b\u8dc4\u7684\u8dcc\u61a7\u56de\u6765\uff0c\u7ecf\u8fc7\u9a86\u94c3\u5904\uff0c\u987f\u4e86\u4e00\u987f\uff0c\u5374\u5728\u9648\u5251\u8c01\u8eab\u8fb9\u5750\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u90a3\u4e9b\u4eba\u4e5f\u8bb0\u4e0d\u6e05\u695a\u8fd9\u534e\u4eba\u539f\u6765\u7684\u4f4d\u7f6e\uff0c\u4fbf\u4e0d\u53bb\u7ba1\u4ed6\u4e86\u3002 \u3000\u3000\u672a\u51e0\uff0c\u53ea\u542c\u5e7f\u64ad\u4e2d\u4f20\u6765\uff1a\u201c\u8fd9\u662f\u673a\u957f\u534e\u7279\u5411\u5927\u5bb6\u8bf4\u8bdd\uff1a\u7531\u4e8e\u673a\u4e0a\u53d1\u751f\u4e86\u4e00\u4e9b\u4e8b\u6545\uff0c\u987b\u8981\u6539\u53d8\u822a\u9053\uff0c\u6211\u4eec\u4e00\u5b9a\u4f1a\u7aed\u5c3d\u6240\u80fd\uff0c\u4ee5\u4fdd\u5b89\u5168\uff0c\u8bf7\u5404\u4f4d\u65c5\u5ba2\u4e0d\u5fc5\u6050\u614c\u3002\u201d \u3000\u3000\u90a3\u201c\u5c06\u519b\u201d\u70b9\u70b9\u5934\u626c\u58f0\u8bf4\uff1a\u201c\u5404\u4f4d\uff0c\u6211\u4eec\u73b0\u5728\u663e\u7136\u5df2\u63a7\u5236\u4e86\u4e00\u5207\uff0c\u4f60\u4eec\u8981\u662f\u90fd\u60f3\u6d3b\u4e0b\u53bb\uff0c\u4e0d\u59a8\u5c31\u9001\u6211\u4eec\u4e00\u7a0b\uff0c\u5230\u6211\u4eec\u8981\u53bb\u7684\u5730\u65b9\u3002\u201d \u3000\u3000\u90a3\u745e\u58eb\u4eba\u7981\u4e0d\u4f4f\u95ee\uff1a\u201c\u4f60\u60f3\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u90a3\u540d\u6301\u624b\u69b4\u5f39\u7684\u83f2\u7c4d\u6c49\u5b50\uff0c\u626c\u624b\u5c31\u7ed9\u4ed6\u4e00\u8bb0\u8033\u5149\uff1a\u201c\u4f60\u7ade\u6562\u8fd9\u6837\u8ddf\u5c06\u519b\u8bf4\u8bdd\uff01\u201d \u3000\u3000\u90a3\u83f2\u7c4d\u201c\u5c06\u519b\u201d\u987f\u4e86\u4e00\u987f\uff0c\u8f7b\u63cf\u6de1\u5199\u7684\u8bf4\uff1a\u201c\u6211\u4eec\u521a\u7ecf\u8fc7\u4e00\u6b21\u6d41\u4ea7\u653f\u53d8\uff0c\u53ef\u662f\u6211\u4eec\u4e3a\u4e86\u56fd\u5bb6\u7684\u5e0c\u671b\u5e76\u6ca1\u6709\u653e\u5f03\u3002\u6211\u4eec\u8981\u501f\u8fd9\u67b6\u98de\u673a\u53bb\u6307\u5b9a\u7684\u5730\u65b9\u3002\u5982\u679c\u6ca1\u6709\u4f60\u4eec\uff0c\u6211\u5f88\u53ef\u80fd\u4f1a\u88ab\u519b\u65b9\u6253\u4e0b\u6765\uff0c\u6240\u4ee5\uff0c\u4f60\u4eec\u5f97\u8981\u4e3a\u6211\u6790\u7977\u3002\u201d \u3000\u3000\u90a3\u6301\u624b\u69b4\u5f39\u7684\u6c49\u5b50\uff0c\u56e0\u6b63\u8d70\u5230\u745e\u58eb\u4eba\u8eab\u8fb9\uff0c\u800c\u53d1\u73b0\u9a86\u94c3\u51fa\u4f17\u7684\u8273\u8272\uff0c\u5fcd\u4e0d\u4f4f\u6478\u4e86\u6478\u5979\u7684\u4e0b\u5df4\uff0c\u53c8\u5728\u5979\u7684\u80f8\u90e8\u78b0\u4e86\u78b0\u3002 \u3000\u3000\u90a3\u745e\u58eb\u4eba\u6012\u53f1\uff1a\u201c\u6742\u79cd\uff0c\u4f60\u60f3\u5e72\u4ec0\u4e48\uff01\u201d \u3000\u3000\u8fd9\u4e00\u53e5\u8bdd\u53c8\u4f7f\u4ed6\u81ea\u8ba8\u82e6\u5403\u3002\u90a3\u6c49\u5b50\u53c8\u8fce\u9762\u7ed9\u4ed6\u4e00\u62f3\u3002 \u3000\u3000\u90a3\u201c\u5c06\u519b\u201d\u8bf4\u8bdd\u4e86\uff1a\u201c\u5a01\u5c14\u68ee\uff0c\u4f4f\u624b\uff01\u201d \u3000\u3000\u90a3\u83f2\u7c4d\u6c49\u5b50\u8fd9\u624d\u4e0d\u6562\u6709\u8fdd\uff0c\u4f46\u4e00\u53cc\u8272\u8ff7\u8ff7\u7684\u773c\u4ecd\u4e0d\u65f6\u6e9c\u5230\u9a86\u94c3\u7684\u8eab\u4e0a\uff0c\u8c01\u90fd\u77e5\u9053\u4ed6\u8981\u4ec0\u4e48\u548c\u60f3\u5e72\u4ec0\u4e48\u3002 \u3000\u3000\u53f2\u6d41\u82b3\u4f4e\u58f0\u9053\uff1a\u201c\u554a\u54c8\uff0c\u9a86\u94c3\u8fd9\u56de\u53ef\u8981\u5403\u82e6\u5934\u4e86\u3002\u201d \u3000\u3000\u90a3\u6301\u673a\u67aa\u7684\u8d35\u5987\u6b63\u6307\u4ee4\u7a7a\u59d0\u628a\u673a\u4e0a\u7684\u7a7a\u4e2d\u5c0f\u59d0\u5168\u53cd\u7ed1\u5728\u5ea7\u6905\u4e0a\u3002 \u3000\u3000\u9648\u5251\u8c01\u4e5f\u5c0f\u58f0\u9053\uff1a\u201c\u4f60\u7684\u610f\u4e2d\u4eba\u4e5f\u5403\u82e6\u4e86\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u4e0d\u628a\u6211\u548c\u5979\u6346\u5728\u4e00\u8d77\uff01\u201d\u53f2\u6d41\u82b3\u8bf4\uff0c\u5ffd\u7136\u770b\u89c1\u90a3\u9a6c\u6765\u5973\u5b50\uff0c\u816e\u8fb9\u8fd8\u9690\u7136\u7ea2\u80bf\u4e86\u4e00\u5927\u5757\uff0c\u5fc3\u4e2d\u5c31\u96be\u53d7\uff0c\u201c\u738b\u516b\u86cb\uff01\u4f60\u770b\u4ed6\u4eec\u662f\u4ec0\u4e48\u6765\u8def\uff1f\u201d \u3000\u3000\u9648\u5251\u8c01\u6ca1\u6709\u8bf4\u8bdd\u3002 \u3000\u3000\u53f2\u6d41\u82b3\u5927\u5947\uff0c\u7528\u65f6\u90fd\u78b0\u78b0\u4ed6\uff1a\u201c\u5582\uff0c\u6211\u5728\u95ee\u4f60\u5440\uff1f\u201d \u3000\u3000\u201c\u4f60\u5728\u9a82\u5de5\u516b\u86cb\uff0c\u8c01\u53bb\u7b54\u4f60\uff0c\u5c31\u6210\u4e86\u738b\u516b\u86cb\u3002\u201d\u9648\u5251\u8c01\u60a0\u95f2\u7684\u8bf4\uff0c\u53c8\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u201c\u4f60\u770b\uff0c\u8001\u4e48\u8fd8\u5728\u7761\u3002\u201d \u3000\u3000\u53f2\u6d41\u82b3\u8fd9\u624d\u7701\u609f\uff1a\u201c\u522b\u7ba1\u90a3\u53ea\u732a\u4e86\u3001\u6211\u662f\u95ee\u4f60\u5440\uff0c\u4ed6\u4eec\u4e3a\u4f55\u8981\u52a8\u673a\uff1f\u201d \u3000\u3000\u201c\u8fd1\u65e5\u6709\u4e00\u4e2a\u4e9a\u6d32\u56fd\u5bb6\u53d1\u751f\u4e86\u5175\u53d8\uff0c\u4f46\u65cb\u5373\u53d7\u63a7\u5236\uff0c\u8fd9\u4e9b\u53db\u519b\u662f\u53d7\u4e00\u4e2a\u6d41\u4ea1\u5728\u5916\u7684\u72ec\u88c1\u9886\u8896\u6240\u9a71\u4f7f\uff0c\u8bd5\u56fe\u4ee5\u6b66\u529b\u63a8\u7ffb\u653f\u5e9c\u593a\u6743\u7684\uff0c\u53db\u519b\u9886\u8896\u5728\u4e8b\u8d25\u540e\u5404\u81ea\u9003\u4ea1\uff0c\u8fd8\u626c\u8a00\u8981\u5377\u571f\u91cd\u6765\uff0c\u8fd9\u51e0\u4eba\u2026\u2026\u53ef\u80fd\u5c31\u662f\u2026\u2026\u201d \u3000\u3000\u201c\u95ed\u8d77\u4f60\u7684\u5634\uff01\u201d\u90a3\u83f2\u7c4d\u5987\u4eba\u628a\u673a\u67aa\u4e00\u633a\uff0c\u8fc7\u6765\u5c31\u5728\u9648\u5251\u8c01\u9888\u80a9\u91cd\u91cd\u4e00\u51fb\uff1a\u201c\u518d\u8bf4\u8bdd\u6211\u5c31\u5728\u4f60\u989d\u5934\u5f00\u4e00\u4e2a\u6d1e\uff01\u201d \u3000\u3000\u9648\u5251\u8c01\u53cc\u5389\u4e00\u6c89\uff0c\u518d\u5c55\uff0c\u4e00\u8fde\u626c\u4e86\u4e09\u6b21\u7709\u6bdb\u3002 \u3000\u3000\u53f2\u6d41\u82b3\u4f4e\u58f0\u8bf4\uff1a\u201c\u6211\u771f\u60f3\u770b\u770b\u5979\u3002\u201d \u3000\u3000\u9648\u5251\u8c01\u75be\u9053\uff1a\u201c\u770b\u5427\u3002\u201d \u3000\u3000\u90a3\u540d\u6301\u624b\u69b4\u5f39\u7684\u5a01\u5c14\u68ee\uff0c\u6c14\u51b2\u51b2\u7684\u5927\u6b65\u8d70\u4e86\u8fc7\u6765\uff0c\u6ee1\u8138\u715e\u6c14\uff1a\u201c\u4f60\u4eec\u4e24\u4e2a\uff0c\u8fd8\u5728\u8bf4\u8bdd\uff0c\u4e0d\u8981\u6d3b\u4e86\uff01\u201d \u3000\u3000\u5c06\u519b\u201d\u9053\uff1a\u201c\u770b\u6765\u8981\u6740\u6389\u4e00\u4e24\u4e2a\uff0c\u624d\u80fd\u6536\u6548\u3002\u201d \u3000\u3000\u5a01\u5c14\u68ee\u5927\u58f0\u5e94\uff1a\u201c\u662f\uff01\u201d \u3000\u3000\u4ed6\u7684\u610f\u601d\u662f\u8981\u6740\u53f2\u6d41\u82b3\u3002 \u3000\u3000\u4ed6\u4e00\u65e9\u5c31\u770b\u8fd9\u4eba\u4e0d\u987a\u773c\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u53ea\u6709\u4e00\u679a\u624b\u69b4\u5f39\uff0c\u5e76\u6ca1\u6709\u67aa\u3002 \u3000\u3000\u4e8e\u662f\u4ee5\u4ed6\u62d4\u51fa\u4e86\u5200\u3002 \u3000\u3000\u519b\u5200\u3002 \u3000\u3000\uff13\u3001\u65f6\u673a\uff0c\u751f\u673a\uff0c\u5951\u673a\uff0c\u5148\u673a \u3000\u3000\u4ed6\u8981\u5272\u8fd9\u77a7\u7740\u4e0d\u987a\u773c\u7684\u5c0f\u5b50\u7684\u8116\u5b50\u3002 \u3000\u3000\u2014\u2014\u7531\u4e8e\u770b\u8fd9\u53ae\u4e0d\u987a\u773c\uff0c\u6240\u4ee5\u66f4\u7279\u522b\u8981\u770b\u770b\u4ed6\u6b7b\u524d\u6050\u60e7\u7684\u6837\u5b50\u3002 \u3000\u3000\u4ed6\u4e00\u770b\uff0c\u5c31\u770b\u5230\u5bf9\u65b9\u7684\u773c\u775b\u3002 \u3000\u3000\u90a3\u4e00\u53cc\u5728\u539a\u73bb\u7483\u7247\u5e95\u4e0b\u70e7\u70ed\u7684\u773c\u775b\u3002 \u3000\u3000\u4ed6\u53ea\u671b\u4e00\u773c\uff0c\u5c31\u8981\u4e0b\u624b\u3002 \u3000\u3000\u53ef\u662f\uff0c\u4ed6\u7684\u89c6\u7ebf\u5374\u6536\u4e0d\u56de\u6765\u4e86\u3002 \u3000\u3000\u2014\u2014\u8fd9\u662f\u600e\u4e48\u641e\u7684\uff1f \u3000\u3000\u4ed6\u60f3\u4e00\u5200\u523a\u53bb\u3002 \u3000\u3000\u53ef\u662f\uff0c\u90a3\u53ea\u624b\u7ade\u4e5f\u4e0d\u542c\u4f7f\u5524\u4e86\u3002 \u3000\u3000\u4ed6\u7aed\u529b\u60f3\u8f6c\u79fb\u89c6\u7ebf\uff0c\u53ef\u662f\u529e\u4e0d\u5230\u3002 \u3000\u3000\u90a3\u53cc\u773c\u775b\u4f3c\u662f\u65e0\u5e95\u6df1\u6f6d\uff0c\u4ed6\u4eff\u4f3c\u6389\u8fdb\u4e86\u6cbc\u6cfd\uff0c\u6108\u6323\u624e\u5c31\u6c89\u5f97\u6108\u5feb\u3002 \u3000\u3000\u201c\u5c06\u519b\u201d\u89c1\u4ed6\u8fd8\u6ca1\u6709\u4e0b\u624b\uff0c\u53eb\u4e86\u4e00\u58f0\uff1a\u201c\u5e72\u5427\u3002\u201d \u3000\u3000\u53ef\u5a01\u5c14\u68ee\u4ecd\u5446\u7ad9\u5728\u90a3\u91cc\u3002 \u3000\u3000\u201c\u5c06\u519b\u201d\u559d\u4e86\u4e00\u58f0\uff1a\u201c\u5a01\u5c14\u68ee\uff01\u201d \u3000\u3000\u53f2\u6d41\u82b3\u5374\u8fc5\u901f\u4f38\u624b\uff0c\u5df2\u6263\u4f4f\u4e86\u5a01\u5c14\u68ee\u624b\u4e0a\u7684\u624b\u69b4\u5f39\uff0c\u4f46\u773c\u795e\u4ecd\u7d27\u76ef\u8fdb\u5a01\u5c14\u68ee\u7684\u773c\u775b\u91cc\u3002 \u3000\u3000\u201c\u5c06\u519b\u201d\u53cd\u5e94\u6781\u5feb\uff0c\u67aa\u53e3\u5df2\u75be\u5bf9\u5411\u53f2\u6d41\u82b3\uff0c\u5c31\u8981\u62a0\u52a8\u6273\u673a\u3002 \u3000\u3000\u9648\u5251\u8c01\u6b63\u7b49\u4ed6\u6709\u6240\u884c\u52a8\u3002 \u3000\u3000\u4ed6\u4e00\u52a8\uff0c\u9648\u5251\u8c01\u4e5f\u52a8\u4e86\u3002 \u3000\u3000\u4ed6\u7684\u624b\u8155\u624d\u4e00\u632f\uff0c\u67aa\u53e3\u4e00\u79fb\uff0c\u9648\u5251\u8c01\u7684\u624b\u6307\u5df2\u4f3c\u94c1\u7b8d\u4e00\u822c\u6263\u5728\u4ed6\u7684\u8109\u95e8\u4e0a\u3002 \u3000\u3000\u4e5f\u4e0d\u77e5\u600e\u7684\uff0c\u201c\u5c06\u519b\u201d\u53ea\u89c9\u624b\u8155\u4e00\u9ebb\uff0c\u67aa\u5c31\u518d\u4e5f\u63e1\u4e0d\u4f4f\u3002 \u3000\u3000\u5148\u673a\u539f\u672c\u638c\u63e1\u5728\u52ab\u673a\u8005\u624b\u91cc\u3002 \u3000\u3000\u53ef\u662f\u5728\u4e00\u5239\u90a3\u95f4\uff0c\u5c40\u52bf\u53d8\u4e86\u3002 \u3000\u3000\u9648\u5251\u8c01\u3001\u53f2\u6d41\u52b3\u5df2\u638c\u63e1\u4f4f\u53cd\u51fb\u7684\u5951\u673a\u3002 \u3000\u3000\u53ef\u662f\u6709\u4e86\u4f5c\u6218\u7684\u673a\u4f1a\uff0c\u5e76\u4e0d\u4ee3\u8868\u5c31\u662f\u80dc\u5229\u3002 \u3000\u3000\u9648\u5251\u8c01\u53f3\u624b\u6263\u4f4f\u201c\u5c06\u519b\u201d\u7684\u8109\u95e8\uff0c\u9f99\u4e89\u593a\u67aa\uff0c\u67aa\u624d\u5230\u624b\uff0c\u8d35\u592b\u4eba\u7684\u673a\u67aa\u5df2\u9876\u4f4f\u4e86\u4ed6\u7684\u540e\u8111\u3002 \u3000\u3000\u4f46\u4ed6\u5e76\u6ca1\u6709\u505c\u6b62\u4ed6\u7684\u52a8\u4f5c\u3002 \u3000\u3000\u751a\u81f3\u6ca1\u6709\u56e0\u800c\u51cf\u7f13\u3002 \u3000\u3000\u4ed6\u4e00\u8098\u5c31\u628a\u201c\u5c06\u519b\u201d\u649e\u98de\u51fa\u53bb\u3002 \u3000\u3000\u7136\u540e\u731b\u7136\u8fd4\u8eab\u3002 \u3000\u3000\u8d35\u5987\u4eba\u5df2\u4e0d\u80fd\u9009\u62e9\u3002 \u3000\u3000\u5979\u53ea\u6709\u5f00\u67aa\u3002 \u3000\u3000\u5411\u4ed6\u8138\u4e0a\u5f00\u67aa\u3002 \u3000\u3000\u673a\u67aa\u662f\u6273\u52a8\u4e86\uff0c\u4f46\u6ca1\u6709\u67aa\u58f0\u3002 \u3000\u3000\u4e5f\u6ca1\u6709\u5b50\u5f39\u3002 \u3000\u3000\u9648\u5251\u8c01\u7684\u5c0f\u624b\u67aa\u5374\u5df2\u5d4c\u5728\u5979\u53cc\u4e73\u4e4b\u95f4\u3002 \u3000\u3000\u201c\u867d\u7136\uff0c\u6211\u53ea\u6709\u4e00\u7c92\u5b50\u5f39\uff0c\uff0c\u9664\u5251\u8c01\u4ece\u5bb9\u4e0d\u8feb\u7684\u8bf4\u201c\u5bf9\u53ea\u5c04\u4e00\u6b21\u7684\u4e1c\u897f\u6211\u4e00\u5411\u73cd\u60dc\uff0c\u9664\u975e\u4f60\u903c\u6211\uff0c\u5426\u5219\u6211\u8fd8\u771f\u4e0d\u60f3\u5c04\u5728\u4f60\u8eab\u4e0a\u3002\u201d \u3000\u3000\u8d35\u592b\u4eba\u77a0\u76ee\u3001\u5f03\u67aa\u3002 \u3000\u3000\u9648\u5251\u8c01\u7684\u624b\u81ea\u897f\u88c5\u53e3\u888b\u91cc\u4f38\u51fa\u6765\u3002 \u3000\u3000\u638c\u5fc3\u6709\u516d\u4e03\u53d1\u5b50\u5f39\u3002 \u3000\u3000\u8d35\u5987\u4eba\u8fd9\u624d\u77e5\u9053\uff0c\u5728\u521a\u624d\u9648\u5251\u8c01\u5750\u8fd1\u5979\u7684\u65f6\u5019\uff0c\u5c31\u5df2\u770b\u51fa\u5979\u624b\u63d0\u5305\u7684\u7384\u865a\uff0c\u65e9\u5df2\u795e\u4e0d\u77e5\u3001\u9b3c\u4e0d\u89c9\u7684\u53d6\u51fa\u4e86\u5979\u7684\u5b50\u5f39\u3002 \u3000\u3000\u5c40\u9762\u4f3c\u4e4e\u5df2\u53d7\u5230\u521d\u6b65\u7684\u63a7\u5236\u3002 \u3000\u3000\u65f6\u673a\u4f3c\u5df2\u638c\u63e1\u5728\u9648\u5251\u8c01\u90a3\u4e00\u8fb9\u3002 \u3000\u3000\u638c\u63e1\u65f6\u673a\u5c31\u662f\u53d6\u5f97\u4e86\u751f\u673a\u3002 \u3000\u3000\u53f2\u6d41\u82b3\u6b63\u62df\u6325\u62f3\u3002 \u3000\u3000\u5411\u5a01\u5c14\u68ee\u7684\u9f3b\u5b50\u3002 \u3000\u3000\u4ed6\u5bf9\u8fd9\u79cd\u9e70\u94a9\u9f3b\u4e00\u5411\u6ca1\u6709\u597d\u611f\u3002 \u3000\u3000\u66f4\u4f55\u51b5\u8fd9\u6837\u4e00\u53ea\u4e0d\u4ee4\u4eba\u597d\u611f\u7684\u9f3b\u5b50\u662f\u957f\u5728\u90a3\u4e48\u4e00\u4e2a\u4ee4\u4eba\u538c\u6076\u7684\u8138\u4e0a\u3002 \u3000\u3000\u4f46\u4ed6\u7684\u62f3\u8fd8\u6ca1\u6709\u6325\u51fa\uff0c\u4e00\u4eba\u5df2\u8df3\u4e86\u8d77\u6765\u3002 \u3000\u3000\u8fd9\u4eba\u624b\u4e0a\u4e5f\u6709\u67aa\u3002 \u3000\u3000\u8fd9\u4eba\u6b63\u662f\u521a\u624d\u5728\u6d17\u624b\u95f4\u91cc\u88ab\u626f\u51fa\u6765\u7684\u90a3\u7626\u5b50\u3002 \u3000\u3000\u4ed6\u62d4\u67aa\uff0c\u4eba\u5728\u9648\u5251\u8c01\u548c\u53f2\u6d41\u82b3\u7684\u540e\u9762\u3002 \u3000\u3000\u4ed6\u752b\u52a8\uff0c\u80cc\u540e\u5c31\u7ed9\u4e00\u7269\u9876\u4f4f\u3002 \u3000\u3000\u201c\u653e\u4e0b\u67aa\uff0c\u201d\u4e00\u4e2a\u5e26\u4e86\u70b9\u5174\u594b\u6e05\u8106\u7684\u5973\u97f3\u9053\uff1a\u201c\u5426\u5219\u6211\u5c31\u5148\u5728\u4f60\u80cc\u4e0a\u5f00\u4e2a\u7a9f\u7abf\u3002\u201d \u3000\u3000\u4eba \u3000\u3000\u201c\u4f60\u4e0d\u6562\u5f00\u67aa\u7684\uff0c\u201d\u8fd9\u7626\u5b50\u5f3a\u4f5c\u9547\u5b9a\uff0c\u6211\u4eec\u662f\u5728\u98de\u673a\u4e0a\u3002\u201d \u3000\u3000\u5ffd\u542c\u673a\u4e0a\u4e00\u4e2a\u5c0f\u5b69\u5b50\u95ee\uff1a\u201c\u5988\u54aa\uff0c\u90a3\u4f4d\u59d0\u59d0\u4e3a\u4f55\u7528\u4e00\u652f\u6c64\u5319\u6307\u7740\u53d4\u53d4\u7684\u80cc\uff1f\u201d \u3000\u3000\u7626\u5b50\u4e00\u542c\uff0c\u5fc3\u77e5\u6709\u5f02\u4e00\u970d\u7136\u8f6c\u8eab\u2026\u2026 \u3000\u3000\u201c\u4f60\u4eec\u90a6\u6805\u5f00\u67aa\uff0c\u201d\u8f66\u628a\u5251\u8c01\u4e00\u8f6c\u8eab\uff0c\u7626\u5b50\u624b\u4e0a\u7684\u67aa\uff0c\u4e0d\u77e5\u600e\u7684\u5df2\u843d\u5728\u4ed6\u624b\u91cc\u4e86\uff0c\u201c\u6240\u4ee5\u67aa\u662f\u6211\u7684\u3002\u201d \u3000\u3000\u7626\u5b50\u60ca\u9a87\u4e0d\u5df2\u3002\u5bf9\u65b9\u8f6c\u8eab\uff0c\u4ed6\u662f\u770b\u89c1\u7684\uff0c\u53ef\u662f\u600e\u4e48\u51fa\u624b\uff0c\u4ed6\u7ad9\u5728\u5bf9\u65b9\u9762\u524d\u7adf\u4e5f\u6ca1\u770b\u6e05\u695a\uff0c\u53ea\u89c9\u624b\u8155\u4e00\u9ebb\uff0c\u4e94\u6307\u4e00\u677e\uff0c\u67aa\u4fbf\u6613\u4e86\u4e3b\u4eba\u4e86\u3002 \u3000\u3000\u2014\u2014\u8fd9\u51e0\u4e2a\u4eba\uff0c\u5230\u5e95\u662f\u4ec0\u4e48\u4eba\uff1f \u3000\u3000\u2014\u2014\u8fd9\u7a76\u7adf\u662f\u4ec0\u4e48\u9b54\u6cd5\uff01 \u3000\u3000\u2014\u2014\u4eca\u65e5\u4ed6\u4eec\u6765\u52ab\u8fd9\u67b6\u98de\u673a\uff0c\u4f46\u4e0a\u8fd9\u4e9b\u715e\u661f\uff0c\u5b9e\u5728\u662f\u5012\u5c3d\u4e86\u9709\u3002 \u3000\u3000\u201c\u901a\u5e38\uff0c\u9ad8\u660e\u7684\u52a8\u673a\u8005\uff0c\u90fd\u4f1a\u7559\u4e0b\u4e00\u4e24\u4e2a\u4eba\u624b\uff0c\u5145\u4f5c\u666e\u901a\u64ad\u5ba2\uff0c\u4ee5\u9632\u5c40\u9762\u6709\u53d8\u65f6\uff0c\u53ef\u4ee5\u6709\u4eba\u53ca\u65f6\u51fa\u624b\u6273\u56de\u3002\u201d\u9648\u5251\u8c01\u89e3\u91ca\u9053\uff1a\u201c\u6211\u4eec\u8fd9\u4f4d\u5973\u58eb\u4fbf\u662f\u7559\u7740\u6765\u8d1f\u8d23\u5bf9\u4ed8\u8fd9\u79cd\u6700\u540e\u5173\u5934\u624d\u51fa\u73b0\u7684\u4eba\u3002\u201d \u3000\u3000\u201c\u6211\u624d\u4e0d\u4f1a\u5f00\u67aa\u3001\u201d\u9a86\u94c3\u6109\u5feb\u5730\u8bf4\uff0c\u5979\u628a\u624b\u4e0a\u4e00\u652f\u94f6\u5319\u9001\u5230\u7626\u5b50\u9762\u524d\uff0c\u201c\u6211\u4e5f\u6ca1\u6709\u67aa\u53ef\u5f00\u3002\u201d\u5979\u7528\u624b\u62e7\u4e86\u8eab\u8fb9\u4e00\u4e2a\u6ee1\u8138\u96c0\u6591\u7684\u5c0f\u5b69\u8138\u4e0a\u4e00\u628a\uff0c\u8bf4\uff0c\u201c\u4f60\u5dee\u70b9\u513f\u5c31\u5bb3\u82e6\u4e86\u59d0\u59d0\u3002\u201d \u3000\u3000\u90a3\u745e\u58eb\u4eba\u5bf9\u9a86\u94c3\u50bb\u4e86\u773c\uff1a\u201c\u5c0f\u59d0\u539f\u6765\u4f60\u662f\u4e2a\u2026\u2026\u5973\u8d85\u4eba\uff01\u201d \u3000\u3000\u9a86\u94c3\u5ae3\u7136\u4e00\u7b11\uff0c\u8bf4\uff0c\u201c\u6211\u4eec\u534e\u4eba\u5c82\u6b62\u4e8e\u521b\u610f\uff0c\u8fd8\u5e38\u4ee4\u4e16\u4eba\u60ca\u5947\u5462\uff01\u201d \u3000\u3000\u201c\u597d\uff0c\u5bf9\u4e0d\u8d77\uff0c\u5c06\u519b\uff0c\u4f60\u4eec\u7684\u52ab\u673a\u884c\u52a8\u5b8c\u7ed3\u4e86\u3002\u201d\u9648\u5251\u8c01\u628a\u67aa\u4ea4\u7ed9\u53f2\u6d41\u82b3\u548c\u9a86\u94c3\uff0c\u624b\u91cc\u53ea\u7559\u4e0b\u4e00\u9897\u624b\u69b4\u5f39\uff0c\u725b\u4e3d\u751f\u72b9\u5728\u547c\u547c\u5927\u7761\uff0c\u538b\u6839\u513f\u4e0d\u77e5\u9053\u662f\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\u60c5\u3002\u201c\u8be5\u6211\u53bb\u673a\u957f\u5ba4\u628a\u5269\u4e0b\u7684\u4e00\u4f4d\u4e5f\u8bf7\u8fc7\u6765\u5427\u3002\u201d \u3000\u3000\u4ed6\u77e5\u9053\u9a86\u94c3\u548c\u53f2\u6d41\u82b3\u4f1a\u201c\u6599\u7406\u201d\u73b0\u573a\u7684\u3002 \u3000\u3000\u2014\u2014\u8981\u5bf9\u4ed8\u5269\u4e0b\u90a3\u540d\u52ab\u673a\u8005\uff0c\u51ed\u4ed6\u7684\u8eab\u624b\uff0c\u53ef\u4ee5\u8bf4\u6839\u672c\u5c31\u4e0d\u5f53\u6210\u4e00\u56de\u4e8b\u3002 \u3000\u3000\u4e0d\u8fc7\u4e0d\u77e5\u600e\u7684\uff0c\u5f53\u4ed6\u7ad9\u8d77\u6765\u8d70\u5411\u9a7e\u9a76\u5ba4\u4e4b\u65f6\uff0c\u5ffd\u7136\u89c9\u5f97\u6709\u4e00\u70b9\u4e0d\u59a5\u3002 \u3000\u3000\u2014\u2014\u6709\u4ec0\u4e48\u4e0d\u59a5\u5462\uff1f \u3000\u3000\u4ed6\u5374\u53c8\u8bf4\u4e0d\u4e0a\u6765\u3002 \u3000\u3000\uff14\u3001\u201c\u6625\u5929\u91cc\u201d \u3000\u3000\u4ed6\u89e3\u51b3\u5269\u4e0b\u90a3\u540d\u52ab\u673a\u8005\u5085\u83b1\u5179\uff0c\u65b9\u6cd5\u975e\u5e38\u7b80\u5355\uff0c\u4e5f\u5341\u5206\u719f\u7ec3\u3002 \u3000\u3000\u4ed6\u6572\u95e8\u3002 \u3000\u3000\u91cc\u9762\u7684\u4eba\u679c\u7136\u975e\u5e38\u7d27\u5f20\uff1a\u201c\u8c01\uff01\u201d \u3000\u3000\u9648\u5251\u8c01\u5b66\u7740\u201c\u5c06\u519b\u201d\u83f2\u5f8b\u5bbe\u8154\u7684\u82f1\u8bed\u5306\u4fc3\u7684\u8bf4\uff1a\u201c\u662f\u6211\uff0c\u5feb\u5f00\u95e8\uff01\u201d \u3000\u3000\u91cc\u9762\u5374\u8fdf\u7591\u4e86\u4e00\u9635\uff0c\u558a\u9053\uff1a\u201c\u4f60\u7684\u6572\u95e8\u6697\u53f7\u4e0d\u5bf9\uff01\u201d \u3000\u3000\u201c\u8be5\u6b7b\uff01\uff0c\u9648\u5251\u8c01\u4ecd\u5b66\u7740\u201c\u5c06\u519b\u201d\u7684\u8bed\u97f3\u6012\u9a82\u9053\uff1a\u201c\u6211\u53d7\u4f24\u4e86\uff01\u5c40\u9762\u5feb\u63a7\u5236\u4e0d\u4f4f\u4e86\uff0c\u518d\u4e0d\u8ba9\u6211\u8fdb\u6765\u6211\u5c31\u5feb\u6b7b\u4e86\u3002 \u3000\u3000\u8fd9\u4e00\u4e0b\u679c\u7136\u594f\u6548\u3002 \u3000\u3000\u5f00\u95e8\u4e86\u3002 \u3000\u3000\u4e0d\u8fc7\u95ee\u9898\u5e76\u6ca1\u6709\u89e3\u51b3\u3002 \u3000\u3000\u5085\u83b1\u5179\u5f88\u806a\u660e\u3002 \u3000\u3000\u4ed6\u65e9\u6709\u63d0\u9632\u3002 \u3000\u3000\u4ed6\u7684\u624b\u67aa\u6307\u7740\u673a\u5e08\u534e\u7279\u7684\u53f3\u592a\u9633\u7a74\u3002 \u3000\u3000\u201c\u4f60\u662f\u8c01\uff01\u201d\u5085\u83b1\u5179\u5927\u543c\uff0c\u201c\u4e0d\u7ba1\u4f60\u662f\u8c01\uff0c\u4f60\u4e00\u8fdb\u6765\u6211\u5c31\u8f70\u6389\u4ed6\u7684\u5934\uff01\u201d \u3000\u3000\u201c\u4f60\u8f70\u5427\uff0c\u201d\u9648\u5251\u9648\u8138\u9732\u60b2\u54c0\u7684\u8bf4\uff0c\u201c\u4f60\u4e0d\u8f70\uff0c\u6211\u66ff\u4f60\u8f70\uff0c\u6211\u4eec\u5927\u5bb6\u4e00\u9f50\u8f70\u5427\u3002\u201d \u3000\u3000\u8bf4\u7f62\u4ed6\u5c31\u629b\u51fa\u4e86\u4e00\u4ef6\u201c\u4e1c\u897f\u201d\u3002 \u3000\u3000\u5f85\u5085\u83b1\u5179\u770b\u6e05\u695a\u90a3\u6454\u5728\u5730\u4e0a\u7684\u201c\u4e1c\u897f\u201d\u7684\u65f6\u5019\uff0c\u624d\u77e5\u9053\u90a3\u662f\u4e00\u679a\u624b\u69b4\u5f39\uff01 \u3000\u3000\u4ed6\u5927\u53eb\u3002 \u3000\u3000\u4ed6\u4e0d\u60f3\u6b7b\u3002 \u3000\u3000\u4ed6\u53ef\u8981\u6d3b\u547d\u3002 \u3000\u3000\u4ed6\u65e2\u60f3\u62fe\u8d77\u90a3\u679a\u624b\u69b4\u5f39\uff0c\u628a\u5b83\u6254\u51fa\u53bb\uff0c\u4f46\u6700\u540e\u8fd8\u662f\u9009\u62e9\u51b2\u51fa\u673a\u8231\u3002 \u3000\u3000\u7ed3\u679c\u4ed6\u7ed9\u9664\u5251\u8c01\u7eca\u5012\u5728\u5730\uff0c\u67aa\u4e5f\u83ab\u540d\u5176\u5999\u7684\u843d\u5165\u4e86\u5bf9\u65b9\u7684\u624b\u4e0a\u3002 \u3000\u3000\u90a3\u4e9b\u673a\u5e08\u4e5f\u90fd\u5927\u53eb\u7740\uff0c\u6709\u7684\u7ad9\u5230\u4e86\u6905\u5b50\u4e0a\u3002 \u3000\u3000\u201c\u522b\u6015\uff01\u201d\u9648\u5251\u8c01\u8fde\u5fd9\u8fc7\u53bb\u62fe\u8d77\u4e86\u624b\u69b4\u5f39\uff0c\u89e3\u91ca\u9053\uff1a\u201c\u4fdd\u9669\u63a3\u548c\u649e\u9488\u90fd\u6ca1\u62d4\u5f00\uff0c\u4f60\u4eec\u8bf7\u7ee7\u7eed\u9a7e\u98de\u673a\u5427\uff0c\u53ea\u8981\u8fd9\u67b6\u5b9d\u8d1d\u4e0d\u649e\u5c71\uff0c\u6211\u770b\u6211\u4eec\u662f\u53ef\u4ee5\u4e0d\u5fc5\u6539\u9053\u5c31\u53ef\u4ee5\u5b89\u5168\u62b5\u8fbe\u76ee\u7684\u5730\u7684\u3002\u201d \u3000\u3000\u91ca\u7591\u8fc7\u540e\uff0c\u4ed6\u5c31\u62bc\u7740\u5085\u83b1\u5179\u51fa\u53bb\u3002 \u3000\u3000\u4e00\u56de\u5230\u5ba2\u8231\uff0c\u4e0d\u77e5\u600e\u7684\uff0c\u4ed6\u5c31\u53c8\u89c9\u5f97\u6709\u70b9\u4e0d\u5bf9\u52b2\u3002 \u3000\u3000\u5f88\u4e0d\u5bf9\u52b2\u3002 \u3000\u3000\u9a86\u94c3\u5728\u8fd9\u91cc\uff0c\u53f2\u6d41\u82b3\u4e5f\u5728\u8fd9\u91cc\uff0c\u201c\u5c06\u519b\u201d\u5728\u3001\u5a01\u5c14\u68ee\u5728\u3001\u8d35\u592b\u4eba\u5728\u3001\u7626\u4e2a\u5b50\u5728\uff0c\u751a\u81f3\u8fde\u745e\u58eb\u4f6c\u3001\u9a6c\u6765\u7a7a\u59d0\u90fd\u597d\u597d\u7684\u5728\u8fd9\u91cc\uff0c\u53ef\u662f\u4e0d\u77e5\u600e\u7684\uff0c\u4ed6\u5c31\u89c9\u5f97\u6709\u70b9\u4e0d\u5bf9\u52b2\u3002 \u3000\u3000\u53f2\u6d41\u82b3\u5750\u5728\u90a3\u513f\uff0c\u626c\u626c\u7709\u6bdb\u3002 \u3000\u3000\u9a86\u94c3\u5750\u5728\u90a3\u513f\u4f3c\u7b11\u975e\u7b11\uff0c\u5de6\u8138\u9152\u6da1\uff0c\u6df1\u5f97\u50cf\u4e00\u4e2a\u751c\u68a6\u3002 \u3000\u3000\u9648\u5251\u8c01\u7b11\u4e86\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u4f3c\u201c\u8f7b\u677e\u201d\u4e86\u8d77\u6765\u3002 \u3000\u3000\u4ed6\u5439\u7740\u53e3\u54e8\u3002 \u3000\u3000\u90a3\u662f\u4e00\u9996\u6b4c\u3002 \u3000\u3000\u53e4\u8001\u7684\u6b4c\u3002 \u3000\u3000\u2014\u2014\u6625\u5929\u91cc\uff0c\u767e\u82b1\u9999\uff0c\u7405\u54e9\u683c\u7405\u54e9\u683c\u7405\u54e9\u683c\u7405\uff0c\u6e29\u6696\u7684\u548c\u98ce\u8fce\u9762\u5439\uff0c\u5439\u52a8\u4e86\u6211\u7684\u7834\u8863\u88f3\uff0c\u7405\u54e9\u683c\u7405\uff0c\u4e3a\u4e86\u5403\u4e5f\u4e3a\u4e86\u7a7f\uff0c\u663c\u591c\u90fd\u8981\u5fd9\u2026\u2026 \u3000\u3000\u4ed6\u5439\u4e86\u51e0\u4e0b\u53e3\u54e8\uff0c\u505c\u4e86\u505c\uff0c\u53c8\u5531\u4e86\u8d77\u6765\u3002\u3002 \u3000\u3000\u8fd9\u662f\u9996\u8d2b\u7a77\u800c\u4e0d\u5fd8\u5176\u4e50\u7684\u6b4c\uff0c\u4e50\u89c2\u8c41\u8fbe\uff0c\u6b4c\u662f\u8001\u7684\uff0c\u4f46\u4ece\u66f2\u5230\u8bcd\u5531\u53bb\u90fd\u662f\u5e74\u8f7b\u7684\uff0c\u516d\u4e03\u5341\u5c81\u4ee5\u4e0a\u7684\u534e\u4eba\u5927\u90e8\u5728\u56fd\u5bb6\u79bb\u4e71\u7684\u65f6\u5019\u54fc\u8fc7\u8fd9\u9996\u6b4c\uff0c\u56db\u4e94\u5341\u5c81\u7684\u534e\u4eba\u5927\u6982\u90fd\u542c\u8fc7\u8fd9\u9996\u4e00\u542c\u96be\u5fd8\u767e\u542c\u4e0d\u538c\u7684\u6b4c\u66f2\uff0c\u4e8c\u4e09\u5341\u5c81\u53ea\u8981\u5bf9\u4e2d\u534e\u6587\u5316\u4e0d\u5927\u8131\u8282\u534e\u4eba\uff0c\u4e5f\u6709\u4e0d\u5c11\u4eba\u4f1a\u5531\u6216\u542c\u8fc7\u8fd9\u9996\u4e50\u4ee5\u5fd8\u5fe7\u7684\u6b4c\u66f2\u3002 \u3000\u3000\u4ed6\u4e00\u9762\u5531\uff0c\u4e00\u9762\u626c\u7740\u7709\u6bdb\u3002 \u3000\u3000\u4e00\u626c\uff0c\u5373\u6c89\uff0c\u518d\u626c\uff0c\u4e00\u8fde\u5c55\u52a8\u4e86\u4e09\u6b21\u3002 \u3000\u3000\u53f3\u7709\u3002 \u3000\u3000\u7136\u540e\u4ed6\u7b11\u7740\u8bf4\uff1a\u201c\u73b0\u5728\uff0c\u4eba\u4e0d\u662f\u90fd\u5230\u9f50\u4e86\u5417\uff1f\u201d \u3000\u3000\u8fd9\u65f6\u5019\uff0c\u4ed6\u62bc\u7740\u5085\u83b1\u5179\uff0c\u5df2\u8d70\u8fc7\u51e0\u884c\u5934\u7b49\u8231\u7684\u5ea7\u4f4d\uff0c\u7a81\u7136\u95f4\uff0c\u9648\u5251\u8c01\u7684\u80cc\u540e\u9760\u8fd1\u8d70\u5eca\u7684\u4e00\u5f20\u5ea7\u4f4d\u4e0a\uff0c\u4e00\u4eba\u84e6\u5730\u53cd\u8fc7\u8eab\u6765\u3002 \u3000\u3000\u7531\u4e8e\u8fd9\u4eba\u8eab\u88c1\u77ee\u5c0f\uff0c\u6240\u4ee5\u5c31\u7b97\u662f\u53cc\u819d\u8dea\u5728\u6905\u57ab\u4e0a\u501f\u529b\uff0c\u4f46\u4e5f\u4e0d\u8fc7\u53ea\u6709\u5934\u90e8\u9732\u51fa\u6905\u9760\u4e4b\u4e0a\u3002 \u3000\u3000\u8fd9\u4eba\u867d\u7136\u77ee\u5c0f\uff0c\u4f46\u5934\u7279\u522b\u5927\u3002 \u3000\u3000\u4ed6\u7adf\u662f\u4e00\u540d\u5c0f\u5b69\uff01 \u3000\u3000\u2014\u2014\u90a3\u540d\u66fe\u51fa\u8bed\u201c\u9053\u7834\u201d\u9a86\u94c3\u53ea\u7528\u4e00\u652f\u7fb9\u52fa\u6307\u7740\u201c\u7626\u5b50\u201d\u80cc\u90e8\u7684\u5c0f\u5b69\u3002 \u3000\u3000\u4ed6\u4e00\u201c\u5192\u51fa\u5934\u6765\u201d\uff0c\u624b\u4e0a\u4f7f\u6709\u4e00\u652f\u67aa\u3002 \u3000\u3000\u67aa\u53e3\u81ea\u7136\u662f\u5bf9\u51c6\u9648\u5251\u8c01\u7684\u80cc\u4e0a\u3002 \u3000\u3000\u9648\u5251\u8c01\u662f\u80cc\u5411\u7740\u4ed6\u7684\u3002 \u3000\u3000\u6309\u7167\u9053\u7406\uff0c\u9648\u5251\u8c01\u662f\u7edd\u4e0d\u77e5\u9053\u540e\u5934\u6709\u8fd9\u6837\u7684\u4e00\u4e2a\u4eba\uff0c\u6b63\u5728\u5411\u4ed6\u72d9\u51fb\u7684\u3002 \u3000\u3000\u53ef\u662f\uff0c\u5c31\u5728\u90a3\u201c\u5c0f\u5b69\u5b50\u201d\u5192\u8d77\u6765\u7684\u4e00\u5239\u90a3\uff0c\u9648\u5251\u8c01\u5ffd\u7136\u3001\u7a81\u7136\u3001\u5140\u7136\u3001\u5f92\u7136\u800c\u500f\u7136\u5730\uff0c\u731b\u7136\u8fd4\u8eab\uff0c\u53f3\u624b\u4e00\u843d\uff0c\u4e00\u652f\u673a\u4e0a\u5370\u7684\u94c1\u53c9\u5df2\u63d2\u5165\u201c\u5c0f\u5b69\u5b50\u201d\u7684\u624b\u8155\u91cc\u3002 \u3000\u3000\u201c\u5c0f\u5b69\u5b50\u201d\u60e8\u547c\u4e00\u58f0\uff0c\u60f3\u8981\u62a0\u52a8\u677f\u673a\uff0c\u4f46\u67aa\u5df2\u7ed9\u9664\u5251\u8c01\u5288\u624b\u593a\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u540c\u65f6\uff0c\u5728\u9648\u5251\u8c01\u8fd8\u672a\u8fd4\u8eab\u5411\u201c\u5c0f\u5b69\u5b50\u201d\u51fa\u624b\u4e4b\u524d\uff0c\u4ed6\u5df2\u98de\u8d77\u4e00\u811a\uff0c\u628a\u5085\u83b1\u5179\u8e22\u5f97\u76f4\u8dcc\u51fa\u53bb\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u673a\u4e0a\u7684\u4eba\u77e5\u9053\u53c8\u52a8\u624b\u4e86\uff0c\u80c6\u5c0f\u7684\u5973\u58eb\u53d1\u51fa\u5c16\u547c\u3002 \u3000\u3000\u5085\u83b1\u5179\u7ed9\u8fd9\u72e0\u72e0\u7684\u4e00\u811a\uff0c\u8e22\u5f97\u76f4\u8dcc\u51fa\u53bb\u3002 \u3000\u3000\u8fd9\u4e00\u5239\u90a3\u95f4\uff0c\u6709\u4e09\u4e2a\u4eba\u540c\u65f6\u60f3\u52a8\u624b\u3002 \u3000\u3000\u4e00\u4e2a\u662f\u201c\u8d35\u5987\u4eba\u201d\u3002 \u3000\u3000\u5979\u624b\u4e0a\u4e5f\u6709\u67aa\u3002 \u3000\u3000\u5979\u5750\u5728\u9a86\u94c3\u7684\u8eab\u4fa7\uff0c\u67aa\u53e3\u6b63\u5bf9\u51c6\u9a86\u94c3\u7684\u5de6\u808b\u3002 \u3000\u3000\u6240\u4ee5\u9a86\u94c3\u4e0d\u80fd\u52a8\u3002 \u3000\u3000\u5979\u4e5f\u4e0d\u6562\u4e71\u52a8\u3002 \u3000\u3000\u4e00\u52a8\uff0c\u201c\u8d35\u592b\u4eba\u201d\u5c31\u4f1a\u4e0d\u987e\u4e00\u5207\u7684\u5f00\u67aa\u3002 \u3000\u3000\u5979\u81ea\u5df1\u80fd\u4e0d\u80fd\u8eb2\u5f00\u8fd9\u4e00\u67aa\u8fd8\u5728\u5176\u6b21\uff0c\u4f46\u5728\u4e00\u4e07\u4e00\u5343\u82f1\u5c3a\u9ad8\u7a7a\u4e0a\u7684\u98de\u673a\u662f\u4e0d\u662f\u53ef\u4ee5\u7981\u53d7\u5f97\u8d77\u5f00\u4e00\u4e2a\u6d1e\uff0c\u5219\u662f\u4e2a\u8c01\u90fd\u5192\u4e0d\u8d77\u7684\u9669\u3002 \u3000\u3000\u53e6\u5916\u4e00\u4e2a\u60f3\u52a8\u624b\u7684\u4eba\u662f\u201c\u5c06\u519b\u201d\u3002 \u3000\u3000\u4ed6\u624b\u4e0a\u4e5f\u6709\u4e00\u628a\u5200\u3002 \u3000\u3000\u521a\u624d\u5a01\u5c14\u68ee\u60f3\u7528\u672a\u6740\u53f2\u6d41\u52b3\u7684\u5c0f\u578b\u952f\u9f7f\u519b\u5200\u3002 \u3000\u3000\u4ed6\u5c31\u5750\u5728\u53f2\u6d41\u82b3\u7684\u8eab\u4fa7\u3002\u770b\u6765\uff0c\u662f\u53f2\u6d41\u82b3\u80c1\u6301\u7740\u4ed6\uff0c\u5b9e\u9645\u4e0a\uff0c\u5374\u662f\u4ed6\u7528\u5200\u9876\u7740\u53f2\u6d41\u82b3\u3002 \u3000\u3000\u53e6\u5916\u4e00\u4e2a\u4eba\u4e5f\u60f3\u52a8\u624b\u3002 \u3000\u3000\u8fd9\u662f\u90a3\u540d\u201c\u7626\u5b50\u201d\u3002 \u3000\u3000\u7626\u5b50\u624b\u4e0a\u6709\u67aa\u3002 \u3000\u3000\u4ed6\u539f\u5148\u662f\u51c6\u5907\u5f85\u90a3\u201c\u5c0f\u5b69\u5b50\u201d\u52a8\u624b\uff0c\u4ed6\u5c31\u7acb\u5373\u6251\u8fc7\u53bb\u62a2\u6551\u5085\u83b1\u5179\uff0c\u523a\u6b7b\u90a3\u4e2a\u51e0\u4e4e\u7834\u574f\u4e86\u4ed6\u4e00\u5207\u7684\u4eba\u3002 \u3000\u3000\u53ef\u662f\u5c40\u9762\u53d8\u751f\u9aa4\u7136\u3002 \u3000\u3000\u5c40\u9762\u53d8\u5f97\u5b8c\u5168\u975e\u4ed6\u60f3\u50cf\u3002 \u3000\u3000\u751a\u81f3\u975e\u4ed6\u6240\u80fd\u5e94\u4ed8\u3002 \u3000\u3000\u4e5f\u51b3\u975e\u4ed6\u6240\u80fd\u63a7\u5236\u3002 \u3000\u3000\u5c0f\u5b69\u5b50\uff0c\u4e00\u52a8\u624b\uff0c\u7acb\u5373\u88ab\u9648\u5251\u8c01\u91cd\u521b\u800c\u4e14\u5236\u4f4f\u4e86\u7626\u5b50\uff0c\u4ecd\u7136\u6251\u4e86\u51fa\u53bb\uff0c\u67aa\u53e3\u5411\u9648\u5251\u8c01\u7784\u51c6\u4e4b\u9645\u2014\u2014\u5085\u83b1\u5179\u5df2\u201c\u7a81\u7136\u201d\u6251\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u4ed6\u95ea\u5f00\u3002 \u3000\u3000\u518d\u7784\u51c6\u3002 \u3000\u3000\u5ffd\u89c1\u773c\u524d\u94f6\u5149\u4e00\u95ea\u3002 \u3000\u3000\u4ed6\u7684\u98df\u6307\u5df2\u88ab\u5207\u65ad\u3002 \u3000\u3000\u98df\u6307\u4e00\u65ad\uff0c\u8840\u6d41\u5982\u6ce8\uff0c\u4ed6\u4e5f\u62a0\u4e0d\u4e86\u67aa\u673a\u3001 \u3000\u3000\u201c\u53ee\u201d\u7684\u4e00\u58f0\uff0c\u90a3\u4e8b\u7269\u65ad\u6307\u4e4b\u540e\u8fd8\u61a7\u5728\u67aa\u628a\u4e0a\uff0c\u539f\u6765\u662f\u4e00\u628a\u5200\u3002 \u3000\u3000\u4e00\u628a\u673a\u4e0a\u9910\u7528\u7684\u94f6\u5200\u3002 \u3000\u3000\u2014\u2014\u8fd9\u79cd\u5200\u662f\u949d\u53e3\u7684\uff0c\u6740\u4f24\u529b\u4e0d\u5927\uff0c\u53ef\u662f\u5728\u9648\u5251\u8c01\u624b\u4e0a\u4f7f\u6765\uff0c\u7adf\u53d1\u51fa\u4e86\u6781\u53ef\u6015\u7684\u6740\u529b\u3002 \u3000\u3000\u201c\u7626\u5b50\u201d\u75db\u6781\uff0c\u8fd8\u60f3\u6323\u624e\uff0c\u53ef\u662f\u9648\u5251\u8c01\u5df2\u5230\u4e86\u4ed6\u7684\u8eab\u524d\uff0c\u2014\u2014\u4ed6\u53ea\u6765\u5f97\u53ca\u770b\u89c1\u90a3\u4e0d\u7b97\u9ad8\u5927\u7684\u534e\u4eba\u5df2\u8fd4\u8fd1\u4ed6\u9762\u524d\uff0c\u7a81\u7136\uff0c\u8179\u90e8\u5df2\u906d\u91cd\u51fb\uff0c\u4f7f\u4ed6\u6574\u4e2a\u4eba\u5f2f\u4e86\u4e0b\u53bb\uff0c\u63a5\u7740\uff0c\u9888\u90e8\u7684\u5927\u52a8\u8109\u5df2\u4e0d\u77e5\u7ed9\u4ec0\u4e48\u5288\u4e2d\uff0c\u4ed6\u773c\u524d\u4e00\u9ed1\u3001\u53cc\u817f\u4e00\u8f6f\uff0c\u5df2\u683d\u5012\u4e0b\u53bb\u4e86\u2026\u2026 \u3000\u3000\u4ee5\u81f4\u4ed6\u5bf9\u540e\u91c7\u53d1\u751f\u7684\u4e8b\uff0c\u5168\u4e0d\u77e5\u6653\uff0c\u9192\u6765\u7684\u65f6\u5019\u5df2\u6263\u62bc\u5728\u6212\u5907\u68ee\u4e25\u7684\u673a\u573a\u7f81\u7559\u5ba4\u91cc\u3002 \u3000\u3000\u53e6\u5916\u4e24\u4e2a\u60f3\u201c\u52a8\u624b\u201d\u4f2a\u4eba\u4e5f\u52a8\u4e0d\u4e86\u624b\u3002 \u3000\u3000\u539f\u56e0\u662f\u4ed6\u4eec\u63e1\u6709\u5229\u5668\u7684\u624b\u5df2\u4e0d\u542c\u4ed6\u4eec\u7684\u4f7f\u5524\u3002 \u3000\u3000\u56fa\u4e3a\u4e00\u53ea\u5927\u624b\u5df2\u7b8d\u4f4f\u4e86\u4ed6\u4eec\u62ff\u6b66\u5668\u7684\u624b\u81c2\uff0c\u5c31\u8fd9\u4e48\u4e00\u626d\uff0c\u4ed6\u4eec\u7684\u80a9\u81c2\u95f4\u5173\u8282\u4fbf\u7ed9\u62e7\u65ad\u4e86\u3002 \u3000\u3000\u2014\u2014\u8fd9\u6837\u53ef\u6015\u7684\u4e00\u822c\u5927\u529b\uff0c\u4f7f\u4ed6\u4eec\u611f\u5230\u4e0d\u6b62\u662f\u81c2\u78c5\u5378\u4e86\u4e0b\u6765\uff0c\u800c\u662f\u6574\u53ea\u624b\u81c2\u79bb\u5f00\u4e86\u4ed6\u4eec\u7684\u8eaf\u4f53\uff0c\u81f4\u4f7f\u4ed6\u4eec\u5b8c\u5168\u6ca1\u6709\u529e\u6cd5\u5f00\u67aa\u3001\u51fa\u5200\u3002 \u3000\u3000\u66f4\u53ef\u6015\u7684\u662f\u3001\u8fd9\u4e00\u4f38\u624b\u95f4\u4fbf\u5378\u4e0b\u4e24\u4eba\u7684\u80ef\u5b50\u7684\u53ea\u662f\u4e00\u4e2a\u4eba\u505a\u7684\u4e8b\u3002 \u3000\u3000\u4ed6\u5728\u505a\u8fd9\u4ef6\u4e8b\u7684\u65f6\u5019\uff0c\u53f2\u6d41\u82b3\u548c\u9a86\u94c3\u5df2\u8df3\u51fa\u53bb\uff0c\u5236\u4f0f\u6b63\u8981\u8fd8\u51fb\u7684\u5085\u83b1\u5179\u548c\u521a\u8981\u6e05\u9192\u8fc7\u6765\u7684\u5a01\u5c14\u68ee\u3002 \u3000\u3000\u201c\u5c06\u519b\u201d\u548c\u201c\u8d35\u5987\u4eba\u201d\u90fd\u662f\u5750\u5728\u673a\u8231\u4e2d\u6392\u4f4d\u5b50\u56db\u6905\u8fde\u4f4d\u7684\u4e2d\u95f4\u4e24\u5f20\u3002 \u3000\u3000\u539f\u672c\u53f2\u6d41\u7a77\u548c\u9a86\u94c3\u5df2\u5236\u4f4f\u4e86\u4ed6\u4eec\uff0c\u53ef\u662f\uff0c\u4e00\u5f85\u9648\u5251\u8c01\u51b2\u5165\u9a7e\u9a76\u5ba4\uff0c\u90a3\u4e2a\u5916\u5b69\u5b50\u7a81\u7136\u7528\u67aa\u6307\u7740\u4ed6\u4eec\uff0c\u5f62\u52bf\u6025\u8f6c\u76f4\u4e0b\u3002\u53f2\u6d41\u82b3\u548c\u9a86\u94c3\u518d\u827a\u9ad8\u80c6\u5927\uff0c\u4e5f\u4e0d\u6562\u53bb\u8bd5\u6387\u8fd9\u4e9b\u4ea1\u547d\u4e4b\u5f92\u662f\u4e0d\u662f\u771f\u7684\u6562\u5728\u98de\u673a\u4e0a\u5f00\u67aa\uff0c\u53ea\u597d\u6295\u964d\u3002\u5728\u201c\u5c0f\u5b69\u5b50\uff0c\u548c\u201c\u5c06\u519b\u201d\u7684\u8fc5\u901f\u6307\u6325\u4e0b\uff0c\u4e00\u5207\u90fd\u6062\u590d\u539f\u72b6\uff1a \u3000\u3000\u201c\u8d35\u592b\u4eba\u201d\u548c\u201c\u5c06\u519b\u201d\u4ecd\u7136\u5750\u5728\u5ea7\u4f4d\u4e2d\u95f4\u7684\u4e24\u5f20\uff0c\u4fdd\u6301\u539f\u72b6\uff0c\u4e0d\u8ba9\u9648\u5251\u8c01\u751f\u7591\uff0c\u53ea\u4e0d\u8fc7\uff0c\u539f\u7531\u53f2\u6d41\u82b3\u548c\u9a86\u94c3\u4ee5\u5200\u67aa\u5236\u4f4f\u4e8c\u4eba\uff0c\u73b0\u5df2\u53d8\u6210\u53d7\u5236\u8005\u3002 \u3000\u3000\u7136\u540e\uff0c\u90a3\u201c\u5c0f\u5b69\u5b50\u201d\u5c31\u57cb\u4f0f\u5728\u5934\u7b49\u8231\u524d\u7684\u5ea7\u6905\u4e0a\u3002\u53ea\u5f85\u9648\u5251\u8c01\u51fa\u6765\uff0c\u5c31\u548c\u201c\u7626\u5b50\u201d\u5bf9\u4ed6\u6765\u4e2a\u80cc\u8179\u5939\u653b\u3002 \u3000\u3000\u4ed6\u4eec\u5df2\u5931\u53bb\u4e86\u4e00\u4e2a\u4eba\u624b\uff0c\u90a3\u5c31\u662f\u5a01\u5c14\u68ee\u3002 \u3000\u3000\u5a01\u5c14\u68ee\u4ecd\u76ee\u5149\u75f4\u6ede\uff0c\u795e\u667a\u4f3c\u4e00\u65f6\u95f4\u8fd8\u672a\u56e0\u590d\u8fc7\u6765\u3002 \u3000\u3000\u201c\u5c06\u519b\u201d\u3001\u201c\u5c0f\u5b69\u5b50\u201d\u3001\u201c\u8d35\u5987\u4eba\u201d\u548c\u201c\u7626\u5b50\u201d\u5df2\u6765\u4e0d\u53ca\u8bbe\u6cd5\u4f7f\u5a01\u5c14\u68ee\u6062\u590d\u6e05\u9192\u3002 \u3000\u3000\u4ed6\u4eec\u7684\u201c\u76ee\u6807\u201d\u662f\u9648\u5251\u8c01\u3002 \u3000\u3000\u4ed6\u4eec\u5728\u6781\u77ed\u7684\u65f6\u95f4\u5185\uff08\u53ea\u4e0d\u8fc7\u662f\u9648\u5251\u8c01\u51b2\u5165\u9a7e\u9a8c\u5ba4\u5236\u670d\u5085\u83b1\u5179\u7684\u77ed\u77ed\u9877\u523b\u91cc\uff09\uff0c\u5df2\u5e03\u597d\u4e86\u5c40\uff0c\u673a\u8231\u91cc\u7684\u4eba\u90fd\u4e0d\u6562\u58f0\u5f20\uff0c\u53f2\u6d41\u82b3\u548c\u9a86\u94c3\u5df2\u88ab\u631f\u5236\uff0c\u201c\u5c06\u519b\u201d\u5df2\u4e0b\u4ee4\uff0c\u673a\u4e0a\u7684\u4eba\u8981\u662f\u5984\u52a8\uff0c\u4ed6\u7acb\u5373\u4e71\u67aa\u626b\u5c04\uff0c\u4e0d\u53ef\u60dc\u673a\u5760\u4eba\u4ea1\uff0c\u540c\u5f52\u4e8e\u5c3d\u3002 \u3000\u3000\u53ef\u662f\uff0c\u6ca1\u6599\u5230\u9648\u5251\u8c01\u5374\u4f3c\u6d1e\u6089\u4e86\u4e00\u5207\u3002 \u3000\u3000\u4ed6\u5728\u201c\u5c0f\u5b69\u5b50\u201d\u8981\u4e0b\u624b\u7684\u524d\u4e00\u5239\u90a3\u51fb\u6bc1\u4e86\u4ed6\uff0c\u800c\u53c8\u5148\u4e00\u6b65\u51fb\u5012\u4e86\u201c\u7626\u5b50\u201d\u3002 \u3000\u3000\u4fdf\u201c\u5c06\u519b\u201d\u548c\u201c\u8d35\u5987\u4eba\u201d\u8981\u53d1\u52a8\u7684\u65f6\u5019\uff0c\u5374\u7ed9\u4eba\u81ea\u540e\u5236\u4f4f\u4e86\u3002 \u3000\u3000\u540e\u9762\u7684\u4eba\uff0c\u7adf\u7136\u5c31\u662f\u90a3\u4e2a\u4e00\u76f4\u4ee5\u6765\u90fd\u547c\u547c\u5927\u7761\u3001\u4ece\u672a\u6e05\u9192\u8fc7\u7684\u5f6a\u578b\u5927\u6c49\u3002 \u3000\u3000\u725b\u4e3d\u751f\u3002 \u3000\u3000\u4ed6\u662f\u5728\u4ec0\u4e48\u65f6\u5019\u9192\u6765\u7684\u5462\uff1f \u3000\u3000\u4ed6\u662f\u5728\u4ec0\u4e48\u65f6\u5019\u95ea\u5230\u4e86\u52ab\u673a\u8005\u7684\u80cc\u540e\u5462\uff1f \u3000\u3000\u4ed6\u662f\u7528\u4ec0\u4e48\u624b\u6cd5\uff0c\u80fd\u591f\u5728\u5239\u90a3\u95f4\u4f7f\u4e24\u4eba\u7684\u624b\u81c2\uff0c\u90fd\u4e0d\u5c5e\u4e8e\u4ed6\u4eec\u81ea\u5df1\u7684\u4e86\uff1f\u800c\u53c8\u4e00\u6ef4\u8840\u90fd\u4e0d\u5fc5\u6d41\uff1f \u3000\u3000\u56e0\u4e3a\u9648\u5251\u8c01\u7684\u9002\u65f6\u53cd\u51fb\uff0c\u8fd8\u6709\u725b\u4e3d\u751f\u7684\u53ca\u65f6\u51fa\u624b\uff0c\u4f7f\u8fd9\u5e72\u52ab\u673a\u8005\u201c\u516d\u4eba\u7ec4\u201d\uff1a\u5c06\u519b\u3001\u5c0f\u5b69\u5b50\u3001\u8d35\u5987\u4eba\u3001\u7626\u5b50\u3001\u5085\u83b1\u5179\u3001\u5a01\u5c14\u68ee\u5b8c\u5168\u88ab\u51fb\u6e83\u4e86\u3002 \u3000\u3000\u2014\u2014\u53ef\u662f\u9648\u5251\u8c01\u600e\u4e48\u77e5\u9053\u673a\u8231\u5185\u7684\u5c40\u9762\u5df2\u53d7\u52ab\u673a\u8005\u63a7\u5236\uff1f \u3000\u3000\u2014\u2014\u4ed6\u80cc\u540e\u53c8\u6ca1\u957f\u773c\u775b\uff0c\u5982\u4f55\u77e5\u9053\u72d9\u51fb\u8005\u600e\u6837\u4e0b\u624b\uff1f\u4e07\u4e00\u5224\u65ad\u9519\u8bef\uff0c\u53ea\u8981\u7ed9\u5bf9\u65b9\u5f00\u51fa\u4e00\u67aa\uff0c\u60c5\u51b5\u5c82\u4e0d\u662f\u6076\u52a3\u5f97\u4e0d\u53ef\u6536\u62fe\uff1f \u3000\u3000\u2014\u2014\u4ed6\u53c8\u4ece\u4f55\u5f97\u77e5\u90a3\u201c\u5c0f\u5b69\u5b50\u201d\u4e5f\u662f\u4e00\u540d\u52ab\u673a\u8005\uff1f \u3000\u3000\u2014\u2014\u4e3a\u4f55\u53f2\u6d41\u82b3\u3001\u9a86\u73b2\u3001\u725b\u4e3d\u751f\u548c\u9648\u5251\u8c01\u56db\u4eba\uff0c\u884c\u52a8\u80fd\u914d\u5408\u5f97\u5982\u6b64\u5929\u8863\u65e0\u7f1d\u3002\u8981\u662f\u5176\u4e2d\u6709\u4e00\u4e2a\u4eba\u3001\u4e00\u4e2a\u884c\u52a8\u4e0d\u5408\u62cd\uff0c\u5c40\u9762\u53ef\u4e0d\u582a\u60f3\u50cf\uff01 \u3000\u3000\u8fd9\u90fd\u662f\u4e3a\u4ec0\u4e48\uff1f \u3000\u3000\u8fd9\u6b63\u662f\u90a3\u540d\u53eb\u6b27\u5170\u8bfa\u7684\u745e\u58eb\u4eba\uff0c\u7ec8\u4e8e\u5fcd\u4e0d\u4f4f\uff0c\u5411\u90a3\u4f4d\u672c\u6765\u4f7f\u4ed6\u4ef0\u6155\u73b0\u5728\u4ee4\u4ed6\u656c\u4ef0\u7684\u201c\u795e\u5947\u5973\u4fa0\u201d\u2014\u2014\u9a86\u94c3\u4e00\u7cbe\u6559\u7684\u95ee\u9898\u3002\u3000\u3000\u2026 \u3000\u3000\u201c\u6211\u4eec\u662f\u4e00\u7fa4\u597d\u670b\u53cb\uff0c\u9ed8\u5951\u5df2\u5230\u4e86\u4e0d\u8a00\u800c\u55bb\uff0c\u610f\u901a\u795e\u4f1a\u7684\u5730\u6b65\u4e86\u3002\u201d \u3000\u3000\u201c\u6211\u4eec\u6709\u5f88\u591a\u7279\u6b8a\u7684\u6697\u53f7\uff1a\u8b6c\u5982\u626c\u7709\u3001\u626c\u5de6\u7709\u6216\u53f3\u7709\uff0c\u4e00\u5171\u591a\u5c11\u4e0b\uff0c\u90fd\u662f\u6697\u53f7\u3002\u6211\u4eec\u4e00\u770b\u80a5\u9e2d\u7684\u8868\u60c5\uff0c\u4ee5\u53ca\u4ed6\u8bf4\u90a3\u53e5\u770b\u6765\u6ca1\u6709\u5185\u5bb9\u7684\u8bdd\uff0c\u4f7f\u77e5\u9053\u4ed6\u8981\u53d1\u52a8\u4e86\uff0c\u6211\u4eec\u81ea\u5f53\u51fa\u624b\u914d\u5408\u884c\u52a8\u3002 \u3000\u3000\u201c\u80a5\u9e2d\u201d\u6709\u4e00\u79cd\u7279\u6b8a\u7684\u672c\u9886\uff0c\u4ed6\u80fd\u9884\u611f\u5230\u7978\u60a3\u7684\u53d1\u751f\uff0c\u6240\u4ee5\u80fd\u6bd4\u4eba\u65e9\u4e00\u6b65\u9192\u89c9\uff0c\u65e9\u4e00\u4e9b\u9632\u8303\u2026\u2026\u4ed6\u5927\u6982\u76f4\u89c9\u77e5\u9053\u5c40\u9762\u6709\u53d8\uff0c\u4e5f\u611f\u89c9\u5230\u90a3\u4e2a\u5176\u5b9e\u5df2\u7ecf\u4e8c\u5341\u4e5d\u5c81\u4e86\u7684\u4f8f\u5112\u7edd\u5bf9\u4e0d\u662f\u4e2a\u5929\u771f\u7684\u5c0f\u5b69\u3002\u201c\u6211\u4eec\u4e2d\u56fd\u4eba\u6709\u4e00\u79cd\u6b66\u529f\uff0c\u53eb\u505a\u2018\u542c\u58f0\u8fa8\u4f4d\u2019\uff0c\u4ec5\u51ed\u79fb\u52a8\u7684\u58f0\u54cd\uff0c\u5149\u7ebf\u7684\u8f6c\u6362\u751a\u81f3\u547c\u5438\u548c\u5fc3\u8df3\u7684\u9891\u7387\u548c\u7f13\u901f\uff0c\u80fd\u4e0d\u770b\u4f60\u4fbf\u53ef\u5224\u522b\u51fa\u4f60\u7684\u4f4d\u7f6e\u3001\u51fa\u624b\u548c\u5728\u505a\u4ec0\u4e48\uff0c\u6211\u4eec\u7684\u8001\u5927\u2018\u80a5\u9e2d\u2019\u6b63\u662f\u7cbe\u901a\u8fd9\u79cd\u6b66\u529f\u7684\u5947\u4eba\u2026\u2026\u5982\u679c\u8981\u4ece\u80cc\u540e\u6697\u7b97\u9648\u5251\u8c01\uff0c\u4e0d\u5982\u5f53\u9762\u4e0e\u4e4b\u5bf9\u51b3\u3002\u201d \u3000\u3000\u201c\u53e6\u5916\uff0c\u4e2d\u56fd\u8fd8\u6709\u4e00\u79cd\u5f88\u666e\u904d\u7684\u6b66\u529f\uff0c\u53eb\u505a\u2018\u64d2\u62ff\u624b\u2019\uff0c\u5206\u7b4b\u9519\u7a74\uff0c\u5389\u5bb3\u975e\u51e1\uff0c\u53ea\u8981\u7ed9\u4ed6\u53cc\u624b\u5341\u6307\u6cbe\u4e0a\u4e86\uff0c\u4fbf\u80fd\u8ba9\u654c\u624b\u6ca1\u6709\u4e00\u6839\u9aa8\u8089\u662f\u53ef\u4ee5\u8fd0\u4f5c\u7684\uff0c\u76f4\u81f3\u6218\u5fd7\u5b8c\u5168\u4e27\u5931\u4e3a\u6b62\u3002\u6211\u4eec\u53c8\u6709\u53e6\u4e00\u95e8\u786c\u529f\uff0c\u53eb\u505a\u201c\u5341\u4e09\u592a\u4fdd\u6a2a\u7ec3\u2019\uff0c\u80fd\u7ec3\u5c31\u4e00\u8eab\u94dc\u76ae\u94c1\u9aa8\uff0c\u5e73\u65f6\u5200\u5251\u8fd8\u4e0d\u4e00\u5b9a\u80fd\u523a\u5f97\u8fdb\u53bb\u2026\u2026\u8981\u662f\u8fd9\u4e24\u79cd\u6b66\u529f\u5408\u5728\u4e00\u8d77\u8fd0\u7528\uff0c\u8fd9\u4fbf\u5f88\u53ef\u80fd\u662f\u4f60\u521a\u624d\u770b\u5230\u7684\u53d1\u751f\u7684\u53c8\u4e0d\u6562\u7f6e\u4fe1\u7684\u6548\u679c\u4e86\u3002\u201d \u3000\u3000\u8fd9\u662f\u9a86\u94c3\u7684\u53e3\u7b54\u3002 \u3000\u3000\u5979\u628a\u8fd9\u516d\u540d\u52ab\u673a\u8005\u5168\u5236\u4f0f\u4e86\u540e\uff0c\u81ea\u7136\u6709\u4e9b\u5f97\u610f\u6d0b\u6d0b\uff0c\u56de\u7b54\u5f97\u4e5f\u6bd4\u8f83\u8be6\u7ec6\u4e00\u4e9b\u3002 \u3000\u3000\u53ef\u662f\u6b27\u5170\u8bfa\u4ecd\u7136\u8981\u95ee\uff1a\u201c\u6211\u4e0d\u76f8\u4fe1\u9648\u5148\u751f\u5355\u51ed\u76f4\u89c9\u5c31\u53ef\u4ee5\u628a\u884c\u52a8\u8ba1\u7b97\u5f97\u5982\u6b64\u7cbe\u5bc6\u2014\u2014\u7b80\u76f4\u8981\u6bd4\u6211\u4eec\u51fa\u4ea7\u7684\u624b\u8868\u8fd8\u7cbe\u786e\u65e0\u8bef\u3002\u8fd8\u6709\uff0c\u90a3\u4f4d\u725b\u5148\u751f\u662f\u600e\u4e48\u9192\u6765\u7684\u5462\uff1f\u6211\u770b\u4ed6\u7761\u5f97\u5c31\u8c61\u2026\u2026\u201d \u3000\u3000\u201c\u732a\u4e00\u6837\u3002\u201d\u9a86\u94c3\u723d\u5feb\u7684\u66ff\u4ed6\u8bf4\u4e86\u4e0b\u53bb\uff0c\u7136\u540e\u53c8\u723d\u843d\u5730\u9053\uff0c\u4f60\u8fd8\u8981\u77e5\u9053\u4ec0\u4e48\uff0c\u4f55\u4e0d\u76f4\u63a5\u53bb\u95ee\u6211\u4eec\u7684\u8001\u5927\uff1f\u201d \u3000\u3000\u6b27\u5170\u8bfa\u672c\u6765\u60f3\u5148\u53bb\u95ee\u95ee\u90a3\u4e2a\u5927\u5757\u5934\uff0c\u4e0d\u6599\u8f6c\u5934\u4e00\u770b\uff0c\u90a3\u5927\u4e2a\u5b50\u5728\u51fa\u624b\u4e4b\u540e\uff0c\u65cb\u5373\u53c8\u5012\u5934\u547c\u547c\u5927\u7761\uff0c\u5bf9\u673a\u4e0a\u7684\u4e8b\u65e9\u5df2\u6492\u624b\u4e0d\u7406\u4e86\u3002 \u3000\u3000\u6b27\u5170\u8bfa\u53ea\u597d\u53bb\u95ee\u9648\u5251\u8c01\u3002 \u3000\u3000\u9648\u5251\u8c01\u6b63\u5631\u53f2\u6d41\u82b3\u628a\u90a3\u516d\u540d\u52a8\u673a\u8005\u6346\u7ed1\u8d77\u6765\uff0c\u5f53\u7136\u8fd8\u6709\u5176\u4ed6\u673a\u4e0a\u4eba\u5458\u7684\u5e2e\u5fd9\u3002 \u3000\u3000\u4ed6\u8d81\u673a\u957f\u5411\u4ed6\u9053\u8c22\u7684\u65f6\u5019\u4e00\u518d\u8868\u660e\uff0c\u4ed6\u53ea\u662f\u9002\u9022\u5176\u4f1a\u7684\u505a\u4e86\u70b9\u81ea\u6551\u3001\u6551\u4eba\u7684\u4e8b\uff0c\u5e0c\u671b\u5728\u62b5\u8fbe\u673a\u573a\u63a5\u53d7\u8b66\u65b9\u8c03\u67e5\u52ab\u673a\u4e8b\u4ef6\u7ecf\u8fc7\u7684\u65f6\u5019\uff0c\u4e0d\u5fc5\u592a\u5f3a\u8c03\u548c\u5938\u5f20\u4ed6\u548c\u53f2\u6d41\u82b3\u3001\u725b\u4e3d\u751f\u7b49\u4eba\u7684\u201c\u529f\u80fd\u201d\u3002 \u3000\u3000\u201c\u6211\u4eec\u8fd9\u8d9f\u7eaf\u7cb9\u662f\u6765\u89c1\u670b\u53cb\u548c\u6e38\u73a9\uff0c\u6211\u4eec\u53ea\u60f3\u60a0\u95f2\u81ea\u5728\u7684\u6765\uff0c\u5f00\u5f00\u5fc3\u5fc3\u7684\u79bb\u5f00\uff0c\u4e0d\u60f3\u88ab\u4eba\u5f53\u6210\u602a\u7269\u6765\u529e\u3002\u201d\u9648\u5251\u8c01\u8868\u660e\u4e86\u81ea\u5df1\u7684\u6001\u5ea6\uff0c\u201c\u6211\u4eec\u6240\u4f5c\u7684\u4e8b\uff0c\u90fd\u662f\u8be5\u4f5c\u7684\uff0c\u5982\u679c\u4f60\u4eec\u771f\u7684\u8981\u8c22\u6211\u4eec\uff0c\u90a3\u4e48\uff0c\u4e0d\u5f20\u626c\u662f\u4f7f\u6211\u4eec\u6b64\u884c\u5f97\u5230\u6700\u5927\u6109\u5feb\u7684\u6700\u597d\u65b9\u5f0f\u3002\u201d \u3000\u3000\u673a\u4e0a\u4e00\u4f17\u673a\u5458\u548c\u4e58\u5ba2\uff0c\u5bf9\u9648\u5251\u8c01\u7b49\u4eba\u81ea\u7136\u53c8\u662f\u611f\u6fc0\u53c8\u662f\u656c\u7fa1\u3002 \u3000\u3000\u6240\u4ee5\u6b27\u5170\u8bfa\u597d\u4e0d\u5bb9\u6613\u624d\u4ece\u4f17\u4eba\u7684\u201c\u5305\u56f4\u201d\u4e2d\u4e89\u53d6\u5230\u5411\u9648\u5251\u8c01\u53d1\u95ee\u7684\u673a\u4f1a\u3002 \u3000\u3000\u201c\u7b54\u6848\u5176\u5b9e\u5f88\u7b80\u5355\uff0c\u90fd\u662f\u56e0\u4e3a\u4e00\u9996\u6b4c\uff0c\u201d\u9648\u5251\u8c01\u4e50\u4e8e\u7b54\u590d\u4ed6\u95ee\u9898\uff0c\u6211\u90a3\u4f4d\u5927\u5757\u5934\u7684\u670b\u53cb\uff0c\u65e0\u8bba\u7761\u5f97\u591a\u6c89\uff0c\u53ea\u8981\u8981\u4e00\u542c\u5230\u8fd9\u6b4c\uff0c\u5c31\u4f1a\u9192\u6765\u3002\u53ea\u8981\u4ed6\u9192\u4e86\u8fc7\u6765\uff0c\u80fd\u5236\u4f0f\u4ed6\u7684\u4eba\u5c31\u4e0d\u4f1a\u592a\u591a\u4e86\u3002\u201d \u3000\u3000\u4ed6\u7b11\u7740\u8865\u5145\u8bf4\uff1a\u201c\u8fd9\u662f\u4e00\u9996\u8fbe\u89c2\u5f00\u6717\u7684\u597d\u6b4c\u66f2\uff0c\u5145\u4efd\u7684\u8868\u73b0\u535c\u534e\u4eba\u5b89\u8d2b\u4e50\u9053\u7684\u7cbe\u795e\u3001\u5982\u679c\u9a86\u94c3\u80af\u6559\u4f60\uff0c\u4f60\u4e5f\u4e0d\u59a8\u597d\u597d\u5b66\u4e00\u5b66\u3002\u8fd9\u9996\u6b4c\u5c31\u53eb\u505a\u201c\u6625\u5929\u91cc\u201d\u3002 \u3000\u3000\u5982\u679c\u9a86\u5c0f\u59d0\u80af\u6559\uff0c\u4ed6\u8981\u5b66\u7684\u8fd8\u591a\u7740\u5462\u3002 \u3000\u3000\u81f3\u5c11\uff0c\u4ed6\u8981\u5b66\u5982\u4f55\u624d\u53ef\u4e0d\u5f00\u4e00\u67aa\u5c31\u628a\u51e0\u4e2a\u624b\u6301\u67aa\u68b0\uff0c\u51f6\u6b8b\u7684\u52ab\u673a\u8005\u6cbb\u5f97\u8c61\u5c0f\u7ef5\u7f8a\u4e00\u6837\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u8fd9\u745e\u58eb\u4eba\u7684\u8981\u6c42\u4f3c\u5e76\u4e0d\u8ba8\u597d\u3002 \u3000\u3000\u9a86\u94c3\u5728\u6b66\u529f\u4e0a\u80fd\u4fdd\u62a4\u4e00\u4e2a\u7537\u4eba\uff1b\u8fd9\u70b9\u5979\u5fc3\u91cc\u6709\u6570\uff0c\u81ea\u4e0d\u51fa\u5947\u3002\u53ef\u662f\u4e00\u4e2a\u5927\u7537\u4eba\u641e\u5230\u8981\u62dc\u5979\u4e3a\u5e08\uff0c\u522b\u7684\u4e0d\u8bf4\uff0c\u8fd9\u81f3\u5c11\u4f7f\u5979\u5bf9\u8fd9\u7537\u4eba\u5df2\u5931\u53bb\u4e86\u5973\u4eba\u5bf9\u7537\u4eba\u7684\u5174\u8da3\u3002\u3000\u3000\uff0c\u3000\u3000y\uff3c\u201c\u53ef\u4ee5\uff0c\u4e0d\u8fc7\u7b49\u6709\u7f18\u7684\u65f6\u5019\u518d\u8bf4\u5427\u3002\u201d \u3000\u3000\u6b27\u5170\u8bfa\u542c\u4e0d\u61c2\u4ec0\u4e48\u53eb\u505a\u201c\u6709\u7f18\u201d\u3002 \u3000\u3000\u201c\u6709\u7f18\u5c31\u662f\u4f60\u6070\u597d\u3001\u6211\u521a\u597d\uff0c\u201d\u9a86\u73b2\u542c\u5230\u4e8b\u4e8b\u90fd\u8981\u89e3\u91ca\uff0c\u5e86\u5e78\u81ea\u5df1\u6ca1\u4e00\u65f6\u5fc3\u8f6f\u7b54\u5e94\u6559\u5bfc\u8fd9\u4e2a\u597d\u5b66\u4e0d\u5026\u7684\u5f1f\u5b50\u3002\u4f46\u5979\u53ef\u5026\u4e86\uff0c\u201c\u5c31\u662f\u4f60OK\uff0c\u6211OK\u7684\u65f6\u5019\u3002\u201d \u3000\u3000\u201c\u73b0\u5728\u5462\uff1f\u201d\u6b27\u5170\u8bfa\u4e0d\u6b7b\u5fc3\uff0c\u201c\u4f60OK\u5417\uff1f\u201d \u3000\u3000\u201cSorry\uff01\u201d"}),showOnHomepage:!0},{name:"\u7b2c\u4e09\u7ae0\u3000\u7981\u5fcc",content:(0,s.jsx)(s.Fragment,{children:"\uff11\u3001\u4e00\u6735\u82b1\u662f\u4e00\u6b21\u4f24\u5fc3 \u3000\u3000\u7b49\u5230\u9648\u5251\u8c01\u3001\u53f2\u6d41\u82b3\u3001\u9a86\u94c3\u3001\u725b\u4e3d\u751f\u7b49\u56db\u4eba\u771f\u6b63\u6b65\u51fa\u673a\u573a\u7684\u65f6\u5019\uff0c\u5df2\u662f\u5f53\u665a\u534a\u591c\u7684\u4e8b\u4e86\u3002 \u3000\u3000\u9648\u5251\u8c01\u4e00\u518d\u91cd\u7533\uff1a\u4ed6\u65e2\u4e0d\u60f3\u9886\u4ec0\u4e48\u5956\u7ae0\uff0c\u4e5f\u4e0d\u60f3\u516c\u5f00\u4eae\u76f8\u7ed9\u8bb0\u8005\u91c7\u8bbf\uff0c\u53ea\u60f3\u8ddf\u4e00\u822c\u6e38\u5ba2\u4e00\u6837\uff0c\u5e73\u5b89\u987a\u5229\u7684\u8fdb\u4eba\u56fd\u5883\uff0c\u6240\u4ed6\u4eec\u4e0d\u60f3\u6709\u4efb\u4f55\u4eba\u7684\u62db\u5f85\u548c\u62a4\u9001\u3002 \u3000\u3000\u4e8e\u4ed6\u4eec\u6d88\u6d31\u4e86\u4e00\u573a\u201c\u52ab\u673a\u4e8b\u4ef6\u201d\uff0c\u5c45\u529f\u81f3\u4f1f\uff0c\u5982\u6b64\u201c\u631f\u6069\u6c42\u62a5\u201d\uff0c\u53ea\u6c42\u4e0d\u5f20\u626c\uff0c\u5b9e\u5728\u662f\u5f53\u5730\u8b66\u65b9\u4e0d\u53ef\u80fd\u62d2\u7edd\u7684\u4e8b\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u56e0\u8fd9\u201c\u52ab\u673a\u201d\u4e8b\u4ef6\u975e\u540c\u5c0f\u53ef\uff0c\u4ed6\u4eec\u56db\u4eba\u4e5f\u7559\u4e86\u4e0b\u6765\uff0c\u4f5c\u5404\u79cd\u8c03\u67e5\u3001\u6307\u8ba4\uff0c\u4e00\u4f4d\u53eb\u4f46\u8bdb\u6c40\u7684\u9a6c\u6765\u8b66\u5b98\u4e00\u518d\u5e0c\u6c42\u9648\u5251\u8c01\u56db\u4eba\u80fd\u4e0e\u8b66\u65b9\u4fdd\u6301\u8054\u7edc\u3002 \u3000\u3000\u8fd9\u70b9\u5bf9\u4ed6\u4eec\u800c\u8a00\uff0c\u4e5f\u662f\u4e0d\u96be\u7b54\u5141\u7684\u4e8b\u3002 \u3000\u3000\u4ed6\u4eec\u539f\u7ea6\u4e86\u8521\u56db\u5e78\u6765\u63a5\u673a\uff0c\u8b66\u65b9\u8868\u793a\u5df2\u8054\u7edc\u4e86\u63a5\u673a\u7684\u4eba\uff0c\u5b89\u6392\u5b9a\u5728\u6b21\u6668\u4e8c\u65f6\u4e09\u5341\u5206\uff0c\u518d\u5230\u673a\u573a\u6765\u63a5\u4ed6\u4eec\u3002 \u3000\u3000\u6240\u4ee5\uff0c\u4ed6\u4eec\u6ee1\u4ee5\u4e3a\u4e00\u51fa\u673a\u573a\u5c31\u4f1a\u89c1\u5230\u8521\u56db\u5e78\u7684\u3002 \u3000\u3000\u5bf9\u725b\u4e3d\u751f\u3001\u9a86\u94c3\u3001\u53f2\u6d41\u82b3\u800c\u8a00\uff0c\u8fd9\u91cc\u65e2\u662f\u4eba\u751f\u8def\u4e0d\u719f\uff0c\u8fde\u8bed\u8a00\u4e5f\u4e0d\u5927\u6c9f\u901a\u5f97\u4e86\uff0c\u4e0d\u8fc7\uff0c\u4ed6\u4eec\u4ecd\u575a\u6301\u4e0d\u5f53\u201c\u7279\u6743\u4eba\u7269\u201d\uff0c\u56f5\u4e3a\u4e00\u65e6\u5f53\u4e86\u201c\u7279\u6743\u201d\uff0c\u65e0\u8bba\u5728\u4f55\u65f6\u4f55\u5730\uff0c\u90fd\u770b\u4e0d\u5230\u771f\u6b63\u7684\u539f\u8c8c\u4e0e\u7279\u8272\u4e86\u3002 \u3000\u3000\u2014\u2014\u5bf9\u4e00\u4e2a\u559c\u6b22\u51cc\u9a7e\u4e8e\u4eba\u7684\u4eba\u800c\u8a00\uff0c\u201c\u7279\u6743\u201d\u662f\u4e00\u79cd\u4eab\u53d7\uff1a\u4f46\u5bf9\u559c\u6b22\u5c0a\u91cd\u4eba\u7684\u4eba\u6765\u8bf4\uff0c\u201c\u7279\u6743\u201d\u662f\u4e00\u79cd\u4fae\u8fb1\u3002 \u3000\u3000\u4e3a\u4e86\u8981\u8bf4\u670d\u8b66\u65b9\u201c\u4e88\u4ee5\u653e\u884c\u201d\uff0c\u4f46\u53c8\u4e0d\u201c\u7279\u522b\u793c\u5f85\u201d\uff0c\u9648\u5251\u8c01\u5230\u6700\u540e\u8fd8\u662f\u4e0d\u5f97\u4e0d\u52a8\u7528\u4e86\u4ed6\u7684\u201c\u7279\u6743\u201d\u3002 \u3000\u3000\u4ed6\u4eae\u51fa\u4e86\u4ed6\u53e6\u4e00\u4e2a\u201c\u8eab\u4efd\u201d\u3002 \u3000\u3000\u2014\u2014\u8fd9\u8ddf\u56fd\u9645\u5211\u8b66\u6709\u7279\u6b8a\u5173\u8054\u7684\u804c\u8854\uff0c\u4f7f\u8b66\u65b9\u66f4\u522e\u76ee\u76f8\u770b\u3002 \u3000\u3000\u4ed6\u4eec\u624d\u5f97\u4ee5\u201c\u706b\u901f\u901a\u5173\u201d\u3002 \u3000\u3000\u201c\u600e\u4e48\u6837\uff1f\u201d\uff0c\u53f2\u6d41\u82b3\u8c03\u4f83\u7684\u95ee\u9a86\u73b2\uff1a\u201c\u4f60\u4e0d\u7b49\u4f60\u90a3\u4f4d\u5f92\u5f1f\uff1f\u201d \u3000\u3000\u201c\u6211\u7684\u5f92\u5f1f\uff1f\u201d\u9a86\u94c3\u4e00\u65f6\u4e0d\u77e5\u4ed6\u4f55\u6240\u6307\uff0c\u4f46\u56e0\u4e3a\u8ddf\u53f2\u6d41\u82b3\u592a\u8fc7\u719f\u6089\uff0c\u77e5\u9053\u4ed6\u5fcd\u7b11\u7684\u65f6\u5019\u81ea\u5634\u91cc\u5410\u51fa\u6765\u7684\u591a\u534a\u4e0d\u662f\u4ec0\u4e48\u606d\u7ef4\u7684\u8bdd\u3002 \u3000\u3000\u201c\u4f60\u90a3\u4f4d\uff0c\u745e\u58eb\u8868\u2019\u5440\uff01\u201d \u3000\u3000\u201c\u4ed6\u5440\uff0c\u201d\u9a86\u94c3\u6487\u4e86\u6487\u5634\uff0c\u201c\u5316\u5916\u4e4b\u6c11\uff01\u201d \u3000\u3000\u201c\u4f60\u5403\u725b\u6392\uff0c\u4e0d\u662f\u559c\u6b22\u4e09\u5206\u719f\u7684\u5417\uff1f\u201d\u53f2\u6d41\u82b3\u8bf4\uff0c\u201c\u4ed6\u521a\u597d\uff0c\u6e05\u8eab\u957f\u6bdb\uff0c\u7ea2\u53d1\u7eff\u773c\uff0c\u539f\u6c41\u539f\u5473\uff0c\u4e0d\u6b63\u6295\u4f60\u6240\u597d\uff1f\u201d \u3000\u3000\u201c\u4f60\u53bb\u6b7b\u5427\uff01\u201d\u9a86\u94c3\u9a7e\u4ed6\u3002 \u3000\u3000\u70ed\u5e26\u6c14\u5019\uff0c\u540d\u4e0d\u865a\u4f20\u3002\u4ed6\u4eec\u63d0\u7740\u884c\u674e\u8fc7\u4e86\u5173\u5361\uff0c\u4e0d\u6d88\u4e00\u4f1a\u513f\uff0c\u4fbf\u5df2\u70ed\u5f97\u5927\u6c57\u6dcb\u6f13\u3002 \u3000\u3000\u9648\u5251\u8c01\u5012\u6ca1\u4ec0\u4e48\uff0c\u4ed6\u624b\u4e0a\u7684\u884c\u674e\u672c\u5c31\u4e0d\u591a\u3002\u53f2\u6d41\u82b3\u76ae\u7bb1\u91cc\u7684\u8863\u7269\u4e5f\u4e0d\u7b97\u591a\uff0c\u4f46\u5e26\u6765\u7684\u4e66\u7bb1\u5374\u76f8\u5f53\u53ef\u89c2\u3002\u9a86\u94c3\u5c31\u60e8\u4e86\uff0c\u5979\u5c31\u7b97\u662f\u53ea\u51fa\u95e8\u51e0\u5929\uff0c\u4f46\u65b0\u88c5\u3001\u5316\u5986\u54c1\u7edf\u7edf\u51fa\u7b3c\uff0c\u8db3\u6709\u4e09\u4e2a\u65c5\u884c\u888b\u3001\u4e24\u4e2a\u76ae\u7bb1\u8fd8\u6709\u4e00\u4e2a\u624b\u63d0\u7bb1\uff01 \u3000\u3000\u725b\u4e3d\u751f\u672c\u6765\u662f\u6700\u8f7b\u677e\u81ea\u5982\uff0c\u56e0\u4e3a\u4ed6\u624b\u4e0a\u884c\u8f66\u672c\u5c31\u6700\u5c11\u3001\u5ff5\u7b97\u518d\u591a\u4e5f\u96be\u4ed6\u4e0d\u5012\uff0c\u53ef\u662f\u4ed6\u5f0a\u5728\u7a7f\u5f97\u897f\u88c5\u7b14\u633a\uff0c\u957f\u8896\u886c\u8863\u6253\u9886\u5e26\uff0c\u5c31\u7b97\u628a\u897f\u88c5\u8131\u4e86\u4e0b\u6765\u642d\u5728\u624b\u4e0a\uff0c\u4e5f\u70ed\u5f97\u84b8\u7b3c\u4f3c\u7684\uff0c\u504f\u662f\u4ed6\u6c57\u817a\u53d1\u8fbe\uff0c\u65e9\u5df2\u6c57\u6e7f\u900f\u8863\u3002 \u3000\u3000\u53f2\u6d41\u82b3\u8fd8\u8c03\u4f83\u4ed6\uff1a\u201c\u54c7\uff01\u597d\u6027\u611f\u3002\u201d \u3000\u3000\u725b\u4e3d\u751f\u65e0\u610f\u8bc6\u7684\u5f20\u5f00\u4e86\u5927\u5634\uff0c\u50bb\u7b11\u3002 \u3000\u3000\u9a86\u94c3\u5495\u54dd\uff1a\u201c\u4e5f\u6ca1\u89c1\u8fc7\u8fd9\u4e48\u5927\u70ed\u7684\u5929\uff0c\u2019\u5230\u534a\u591c\u9010\u70ed\u5f97\u50cf\u84b8\u7b3c\u4e0a\u6837\u3002\u201d \u3000\u3000\u201c\u5927\u6982\u5feb\u4e0b\u96e8\u4e86\uff0c\u201d\u9648\u5251\u8c01\u8bf4\uff1a\u201c\u4e0b\u96e8\u524d\u3001\u624d\u7279\u522b\u7684\u70ed\uff1b\u5176\u5b9e\uff0c\u5728\u8fd9\u51e0\u5012\u5e38\u6709\u51c9\u98ce\u9001\u723d\u5462\uff0c\u6bd4\u8bf8\u4e8e\u9999\u6e2f\u7684\u590f\u5929\uff0c\u90fd\u662f\u4eba\u6324\u4eba\u673a\u5668\u6324\u673a\u5668\u94a2\u9aa8\u6c34\u6ce5\u6324\u94a2\u9aa8\u6c34\u6ce5\u7684\u5c40\u67ec\u5473\uff0c\u8fd8\u6709\u53f0\u5317\u590f\u5929\u8fde\u4e91\u90fd\u662f\u94c5\u9ed1\u8272\u7684\u76f4\u6c89\u538b\u5230\u5934\u9876\uff0c\u8fd9\u513f\u8fd8\u7b97\u662f\u7a7a\u6c14\u65b0\u9c9c\u5462\uff01\u201d \u3000\u3000\u201c\u53ef\u5c31\u662f\u70ed\uff01\u201d\u9a86\u94c3\u56b7\u3002\u5979\u4e00\u9762\u57cb\u6028\uff0c\u4e00\u9762\u628a\u4e00\u9897\u9888\u5589\u4e0b\u7684\u94ae\u6263\u89e3\u5f00\uff0c\u4f46\u968f\u5373\u53c8\u89c9\u5f97\u5f00\u4e86\u4e00\u7c92\u8fd8\u4e0d\u591f\uff0c\u53c8\u5f00\u4e00\u9897\u3002 \u3000\u3000\u5979\u7684\u9888\u767d\u5f97\u50cf\u7389\u7814\u4e00\u822c\uff0c\u4e0d\u4f46\u6027\u611f\uff0c\u800c\u4e14\u611f\u6027\uff0c\u8fd8\u6709\u4e00\u9897\u4fcf\u5de7\u7684\u5c0f\u9ed1\u75e3\uff0c\u957f\u5728\u90a3\u91cc\u50cf\u4e00\u7c92\u5c0f\u5c0f\u7684\u9ed1\u94bb\u3002 \u3000\u3000\u673a\u573a\u6709\u4eba\u5439\u4e86\u4e00\u58f0\u53e3\u54e8\u3002 \u3000\u3000\u51e0\u4e2a\u7ecf\u8fc7\u7684\u65c5\u5ba2\uff0c\u90fd\u8272\u54aa\u772f\u7684\u5f80\u9a86\u94c3\u655d\u5f00\u7684\u591c\u886b\u91cc\u671b\u4e0b\u53bb\u3002 \u3000\u3000\u201c\u6709\u8fb1\u56fd\u4f53\uff0c\u6210\u4f55\u4f53\u7edf\uff01\u201d\u53f2\u6d41\u82b3\u53c8\u6765\u627e\u5979\u7684\u9ebb\u70e6\uff1a\u201c\u4f60\u4eec\u5973\u5b69\u5b50\u5c31\u559c\u6b22\u5728\u8863\u670d\u4e0a\u526a\u51e0\u4e2a\u7834\u6d1e\uff0c\u626f\u6389\u51e0\u7c92\u7ebd\u6263\uff0c\u6211\u7684\u4e0d\u8fc7\u662f\u4e00\u9971\u7537\u4eba\u7684\u773c\u798f\u3002\u201d \u3000\u3000\u201c\u4f60\u7ba1\u6211\uff01\u6211\u9ad8\u5174\uff01\u201d\u9a86\u94c3\u6028\u624b\u4e0a\u7684\u884c\u674e\u4f7f\u5979\u5bf8\u6b65\u96be\u884c\uff1a\u201c\u592a\u91cd\uff01\u201d \u3000\u3000\u201c\u597d\uff01\u6211\u4e0d\u7ba1\u4f60\uff01\u201d\u53f2\u6d41\u82b3\u6700\u559c\u6b22\u548c\u4eba\u6597\u5634\uff0c\u53ea\u751f\u6050\u627e\u4e0d\u5230\u5bf9\u624b\uff1a\u201c\u4f60\u5e72\u8106\u628a\u8863\u94ae\u90fd\u89e3\u6389\u7b97\u4e86\uff0c\u5c82\u4e0d\u51c9\u5feb\uff01\u201d \u3000\u3000\u9a86\u94c3\u6012\u53f1\uff1a\u201c\u53f2\u6d41\u82b3\uff01\u201d \u3000\u3000\u5979\u8fd9\u6837\u4e00\u8bb0\u5c16\u58f0\u5389\u53f1\uff0c\u4f7f\u673a\u573a\u95e8\u524d\u7684\u4eba\u90fd\u5411\u4ed6\u4eec\u8fd9\u8fb9\u671b\u6765\u3002\u5348\u591c\u7684\u673a\u573a\u672c\u5c31\u6ca1\u4ec0\u4e48\u4eba\uff0c\u9a86\u94c3\u7684\u6e05\u53f1\u66f4\u52a0\u4ee4\u4eba\u4ee5\u4e3a\u6709\u4ec0\u4e48\u610f\u5916\u6b63\u5728\u53d1\u751f\u3002 \u3000\u3000\u9648\u5251\u8c01\u76b1\u4e86\u7709\uff0c\u5429\u5490\uff1a\u201c\u91d1\u94c3\u5b50\u624b\u4e0a\u7684\u884c\u674e\u592a\u591a\u4e86\uff0c\u4f60\u4eec\u5e2e\u5979\u8001\u4eba\u5bb6\u62ce\u62ce\u5427\uff01\u201d \u3000\u3000\u725b\u4e3d\u751f\u7b11\u563b\u563b\u7684\u8981\u5e2e\u5979\u63d0\uff0c\u9a86\u94c3\u628a\u4ed6\u63a8\u5f00\uff1a\u201c\u6211\u4e0d\u8981\u4f60\u6765\u62ff\uff0c\u7c97\u624b\u7c97\u811a\u7684\uff0c\u7ed9\u4f60\u6cbe\u4e0a\u7684\u51c6\u4f1a\u5f04\u574f\u3002\u201d \u3000\u3000\u5979\u8a00\u4e0b\u4e4b\u610f\u662f\u6307\u660e\u8981\u53f2\u6d41\u82b3\u66ff\u5979\u62ff\u3002 \u3000\u3000\u201c\u6211\u624b\u4e0a\u7684\u884c\u674e\u4e5f\u5f88\u91cd\u5440\uff01\u201d\u53f2\u6d41\u82b3\u9a6c\u4e0a\u63a8\u5378\u3002 \u3000\u3000\u9648\u5251\u8c01\u7b11\u8bf4\uff1a\u201c\u963f\u725b\uff0c\u4f60\u66ff\u8001\u53f2\u63d0\u4ed6\u624b\u4e0a\u7684\u884c\u674e\uff0c\u90a3\u4e48\uff0c\u8001\u53f2\u5c31\u53ef\u4ee5\u5e2e\u91d1\u94c3\u5b50\u62ff\u4e1c\u897f\u4e86\u3002\u201d \u3000\u3000\u725b\u4e3d\u751f\u4f9d\u8a00\u7167\u505a\uff0c\u53f2\u6d41\u82b3\u4e07\u5206\u4e0d\u5f97\u5df2\uff0c\u53ea\u597d\u66ff\u9a86\u94c3\u63d0\u884c\u674e\uff0c\u884c\u674e\u624d\u5230\u4ed6\u624b\u5c31\u5927\u547c\u5c0f\u53eb\uff1a \u3000\u3000\u201c\u5509\uff0c\u54ea\u6709\u4eba\u8fde\u5ac1\u5986\u90fd\u5e26\u51fa\u95e8\u7684\uff01\u91cd\u6b7b\u4e86\uff01\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u6108\u53d1\u611f\u5230\u70ed\u6c14\u4e0a\u5347\uff0c\u504f\u662f\u673a\u573a\u5185\u5916\u6e29\u5ea6\u5dee\u5f02\u5341\u5206\u4e4b\u5927\uff0c\u53f2\u6d41\u82b3\u773c\u955c\u7247\u4e5f\u5192\u4e0a\u4e00\u5c42\u6c34\u6c14\uff0c\u4ee5\u81f4\u89c6\u91ce\u4e00\u7247\u6a21\u7cca\u3002 \u3000\u3000\u4ed6\u9a82\u4e86\u4e00\u58f0\uff1a\u201c\u8be5\u6b7b\uff01\u201d \u3000\u3000\u9a86\u94c3\u767b\u65f6\u53d8\u8138\uff1a\u201c\u62ff\u51e0\u5305\u4e1c\u897f\uff0c\u4e5f\u4e0d\u7528\u9a82\u4eba\u7684\uff01\u4e00\u4e2a\u5927\u7537\u4eba\uff0c\u66ff\u5973\u5b69\u5b50\u62ff\u513f\u4ef6\u884c\u674e\u4e5f\u8fd9\u822c\u6ca1\u98ce\u5ea6\u3002\u96be\u602a\u4ea4\u4e0d\u5230\u5973\u670b\u53cb\uff01\u201d \u3000\u3000\u53f2\u6d41\u82b3\u5f53\u65f6\u80c0\u7ea2\u4e86\u8138\uff1a\u201c\u6211\u9a82\u4f60\uff1f\u8c01\u9a7e\u4f60\uff01\u201d \u3000\u3000\u9a86\u94c3\u53c9\u8170\u504f\u9996\uff0c\u4e00\u526f\u201c\u4f60\u51f6\u6211\u4e0d\u6015\u4f60\u201d\uff0c\u751a\u81f3\u662f\u201c\u4f60\u6076\u6211\u6bd4\u4f60\u66f4\u6076\u201d\u7684\u6837\u5b50\uff0c\u201c\u4f60\u4e0d\u662f\u9a7e\u6211\u8fd8\u5199\u8c01\uff01\u201d \u3000\u3000\u53f2\u6d41\u82b3\u5012\u662f\u4e00\u6014\uff1a\u201c\u6211\u9a82\u4f60\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u4f60\u9a82\u4e86\u81ea\u5df1\u4e0d\u77e5\u9053\u5417\uff1f\u201d\u9a86\u94c3\u51b7\u7b11\uff1a\u201c\u4f60\u9a82\u6211\u8be5\u6b7b\uff01\u201d\u5979\u4ecd\u53c9\u7740\u8170\u8bf4\u8bdd\uff0c\u9887\u5f15\u4eba\u6ce8\u76ee\u3002\u534a\u591c\u673a\u573a\u5f80\u6765\u7684\u73ed\u673a\u5e76\u4e0d\u591a\uff0c\u6545\u800c\u642d\u5ba2\u4e5f\u5c11\u5f97\u53ef\u601c\uff0c\u591a\u534a\u90fd\u662f\u4e00\u4e9b\u56e0\u4e8b\u6ede\u7559\u673a\u573a\u7684\u4eba\uff0c\u4f46\u51e1\u5728\u573a\u7684\u7537\u6027\uff0c\u83ab\u4e0d\u628a\u6ce8\u610f\u529b\u5168\u96c6\u4e2d\u5728\u8fd9\u4e2a\u53c9\u8170\u7684\u5999\u9f84\u5c11\u5973\u8eab\u4e0a\u2014\u2014\u5927\u6982\u662f\u56e0\u4e3a\u9a86\u94c3\u662f\u5728\u5927\u90fd\u4f1a\u51fa\u8eab\u7684\u5973\u5b50\u4e4b\u6545\uff0c\u8170\u8eab\u6563\u53d1\u51fa\u4e00\u822c\u5973\u6027\u7684\u52c9\u529b\uff0c\u8ff7\u4eba\u5f97\u5e76\u4e0d\u5b8c\u5168\u662f\u67d4\uff0c\u53cd\u800c\u6709\u4e00\u79cd\u82f1\u6c14\u7684\u5438\u5f15\u529b\u3002\u5979\u4e5f\u8ba9\u4eba\u95fb\u5230\u4e00\u822c\u9999\u5473\uff0c\u53ef\u662f\u4e0d\u662f\u9c9c\u82b1\u7684\u9999\u6c14\uff0c\u800c\u662f\u9999\u6c34\u822c\u7684\u82ac\u82b3\uff0c\u53ef\u662f\u90fd\u4e00\u6837\u7684\u6e05\u9999\u3002\u54ea\u6709\u5973\u4eba\u53c9\u7740\u8170\u9a82\u4eba\u4e5f\u4e0d\u4e88\u4eba\u6076\u611f\u7684\uff1f\u5982\u6709\uff0c\u9a86\u94c3\u5c31\u662f\u4e00\u4e2a\u3002\u5979\u4e0d\u5355\u662f\u808c\u80a4\u76f4\u4f3c\u5439\u5f39\u5f97\u7834\uff0c\u8fde\u8eab\u88c1\u4e5f\u5439\u5f39\u5f97\u7834\u3002 \u3000\u3000\u201c\u8be5\u6b7b\uff01\u201d\u53f2\u6d41\u82b3\u5012\u662f\u8ddf\u5979\u9a82\u60ef\u4e86\uff0c\u9a82\u8d77\u6765\u53ef\u6ca1\u628a\u5979\u5f53\u5973\u5b69\u5b50\u529e\uff0c\u201c\u6211\u7684\u8be5\u6b7b\u662f\u9a82\u6211\u7684\u773c\u955c\uff01\u201d \u3000\u3000\u201c\u4ec0\u4e48\uff1f\u201d\u9a86\u94c3\u542c\u4e0d\u660e\u767d\uff1a\u201c\u9a82\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u6211\u662f\u8bf4\u773c\u955c\u2026\u2026\u201d\u53f2\u6d41\u82b3\u6c14\u6781\u4e86\uff0c\u201c\u6211\u662f\u9a82\u5929\u6c14\u2026\u2026\u4e0d\u662f\u9a82\u4f60\uff01\u201d \u3000\u3000\u201c\u8fd9\u7b97\u4ec0\u4e48\uff1f\u201d\u9a86\u94c3\u6652\u7b11\uff1a\u201c\u4e00\u4f1a\u513f\u8bf4\u773c\u955c\u4e00\u4f1a\u513f\u8bf4\u9a82\u7a7a\u6c14\uff0c\u9a82\u4e86\u4eba\u8fd8\u4e0d\u6562\u8ba4\u8d26\uff0c\u7b97\u4ec0\u4e48\u597d\u6c49\uff01\u201d \u3000\u3000\u201c\u6211\u662f\u9a82\u6211\u7684\u773c\u955c\uff01\u56e0\u4e3a\u5b83\u4e00\u63a5\u89e6\u70ed\u7a7a\u6c14\uff0c\u5c31\u5e03\u6ee1\u4e86\u6c34\u6c14\uff0c\u4f7f\u6211\u770b\u4e0d\u6e05\u695a\uff0c\u73b0\u5728\u4f60\u660e\u767d\u4e86\u6ca1\u6709\uff01\u201d\u53f2\u6d41\u82b3\u8fd9\u56de\u771f\u7684\u5149\u706b\u4e86\uff0c\u201c\u4f60\u8fd9\u7b97\u4ec0\u4e48\uff1f\u9a82\u6211\u4ea4\u4e0d\u5230\u5973\u53cb\uff0c\u9a82\u6211\u4e0d\u662f\u597d\u6c49\u2026\u2026\u201d \u3000\u3000\u9a86\u94c3\u8fd9\u624d\u5f04\u6e05\u695a\uff0c\u53f2\u6d41\u82b3\u539f\u6765\u4e0d\u662f\u9a82\u5979\uff0c\u5fd9\u8bf4\uff1a\u201c\u5bf9\u4e0d\u8d77\uff0c\u6211\u9519\u4e86\uff0c\u53f2\u6d41\u82b3\u554a\uff0c\u4f60\u82f1\u4fca\u6f47\u6d12\u3001\u535a\u5b66\u591a\u624d\uff0c\u6025\u516c\u597d\u4e49\u3001\u723d\u6717\u53ef\u4eb2\uff0c\u600e\u4f1a\u6ca1\u5973\u670b\u53cb\u5462\uff01\u662f\u4f60\u81ea\u5df1\u4e0d\u80af\u6ee5\u4ea4\u7f62\u4e86\u2026\u2026\u201d\u5979\u8fd9\u4e9b\u6d3b\u4e00\u8bf4\u4e0b\u6765\uff0c\u53f2\u6d41\u82b3\u5012\u8fd8\u771f\u4e0d\u597d\u610f\u601d\u53d1\u4f5c\u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u5c82\u6599\u9a86\u94c3\u8f6c\u79fb\u9635\u5730\uff0c\u53bb\u5632\u7b11\u725b\u4e3d\u751f\uff1a\u201c\u4f60\u5440\uff0c\u534a\u591c\u4e09\u66f4\u7684\u5916\u56fd\u673a\u573a\uff0c\u8c01\u8981\u770b\u4f60\u561b\uff0c\u504f\u7a7f\u5f97\u8fd9\u822c\u9686\u91cd\uff0c\u6211\u770b\u7b97\u4e86\u5427\u2026\u2026\u201d\u9a86\u94c3\u4e00\u8fb9\u8bf4\u4e00\u8fb9\u7b11\u4e0d\u53ef\u652f\uff1a\u201c\u53ea\u8981\u4f60\u7167\u7167\u955c\u5b50\uff0c\u5c31\u77e5\u9053\u81ea\u5df1\u4e0d\u5982\u8fd8\u662f\u6c57\u886b\u77ed\u88e4\u597d\u5566\u2026\u2026\u4f60\u8fd9\u79cd\u4eba\u54ea\uff0c\u7a7f\u8d77\u9f99\u888d\u4e5f\u4e0d\u50cf\u592a\u5b50\u554a\uff01\u201d \u3000\u3000\u725b\u4e3d\u751f\u4e00\u542c\uff0c\u7c97\u4e86\u8116\u5b50\uff0c\u7ed3\u7ed3\u5df4\u5df4\u7684\u8bf4\uff1a\u201c\u4f60\u2026\uff0c\u6211\u2026\u2026\u6211\u7231\u600e\u4e48\u7a7f\u2026\u5173\u2026\u2026\u5173\u4f60\u4ec0\u4e48\u4e8b\uff01\u201d \u3000\u3000\u9a86\u94c3\u7b11\u5f97\u660e\u7738\u7693\u9f7f\u90e8\u5728\u94f6\u706f\u4e0b\u53d1\u4eae\uff0c\u5c11\u5973\u4e30\u8174\u800c\u5145\u6ee1\u6d3b\u529b\u7684\u80f4\u4f53\uff0c\u5728\u7ef7\u7d27\u7684\u8863\u88d9\u91cc\u6bcf\u4e00\u6b21\u7b11\u90fd\u5373\u7b11\u6210\u4e00\u9053\u66f2\u7ebf\uff1a\u201c\u90fd\u53eb\u4f60\u4e0d\u5fc5\u6253\u9886\u5e26\u4e86\u2026\u53ef\u4e0d\u662f\u5417\uff1f\u73b0\u5728\u50cf\u7ed9\u4eba\u52d2\u7d27\u5589\u7ba1\u4f3c\u7684\uff0c\u8fde\u8bdd\u90fd\u8bf4\u4e0d\u51fa\u6765\uff01\u201d \u3000\u3000\u725b\u4e3d\u751f\u4e00\u65e6\u751f\u6c14\uff0c\u66f4\u662f\u597d\u4e0d\u5bb9\u6613\u624d\u628a\u8bdd\u81ea\u5589\u5499\u4f3c\u820d\u5229\u5b50\u4e00\u822c\u5730\u4e00\u9897\u9897\u7684\u6324\u51fa\u6765\uff1a \u3000\u3000\u201c\u4f60\u2026\u2026\u6211\u2026\u7a7f\u2026\u2026\u4ec0\u2026\u2026\u4e48\u2026\u4f60\u2026\u7ba1\u4e0d\u2026\u2026\u7740\uff01\u201d \u3000\u3000\u201c\u5bf9\uff01\u201d\u9a86\u94c3\u94f6\u94c3\u822c\u5730\u7b11\u4e86\u8d77\u6765\u3002\u201c\u6211\u53c8\u4e0d\u662f\u4f60\u5988\uff0c\u6211\u7ba1\u4e0d\u7740\uff01\u201d \u3000\u3000\u725b\u4e3d\u751f\u7684\u8138\u8272\u5ffd\u7136\u53d8\u4e86\u3002 \u3000\u3000\u4e00\u6761\u6811\u6839\u822c\u7684\u9752\u7b4b\u659c\u5728\u4ed6\u7684\u989d\u4e0a\uff0c\u624b\u6307\u7adf\u4f3c\u4e94\u6761\u86c7\u822c\u5730\u6296\u52a8\u4e86\u8d77\u6765\uff1a\u201c\u4f60\u8bf4\u4ec0\u4e48\uff01\u201d\u8fd9\u65f6\u7ade\u4e5f\u4e0d\u53e3\u5403\u4e86\uff0c\u4e00\u53ea\u624b\uff0c\u8fc5\u75be\u5730\u642d\u5230\u4e86\u9a86\u94c3\u80a9\u4e0a\u3002 \u3000\u3000\u9a86\u94c3\u672c\u80fd\u5730\u95ea\u4e86\u4e00\u95ea\uff0c\u9648\u5251\u8c01\u9a6c\u4e0a\u95ea\u8eab\u5230\u4ed6\u4eec\u4e4b\u95f4\uff0c\u6c89\u7740\u8138\u8bf4\uff1a\u201c\u963f\u725b\uff0c\u9a86\u94c3\u662f\u65e0\u610f\u7684\u3002\u201d \u3000\u3000\u725b\u4e3d\u751f\u957f\u5438\u4e86\u4e00\u53e3\u6c14\uff0c\u624b\u6307\u662f\u4e0d\u6296\u4e86\uff0c\u4f46\u989d\u4e0a\u9752\u7b4b\u4ecd\u6a2a\u5728\u90a3\u91cc\uff1a\u201c\u5979\u2026\u2026\u5979\u8bf4\u6211\u6bcd\u4eb2\u2026\u2026\u201d \u3000\u3000\u8fd9\u53e3\u662f\u53f2\u6d41\u82b3\u4e25\u8083\u7684\u8bf4\uff1a\u201c\u4e0d\u662f\u7684\uff0c\u9a86\u94c3\u5979\u8ddf\u4f60\u5f00\u5f00\u73a9\u7b11\u800c\u5df2\u3002\u201d \u3000\u3000\u725b\u4e3d\u751f\u989d\u4e0a\u7684\u9752\u7b4b\u8fd9\u624d\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u9664\u5251\u8c01\u5411\u9a86\u94c3\u4e25\u5389\u7684\u671b\u4e86\u4e00\u773c\uff1a\u201d\u4f60\u77e5\u9053\u963f\u725b\u7684\u813e\u6c14\uff0c\u4ee5\u79cd\u73a9\u7b11\u4ee5\u540e\u8fd8\u662f\u5c11\u5f00\u7684\u597d\u3002\u201d \u3000\u3000\u201c\u662f\u3002\u201d\u9a86\u94c3\u59d4\u5c48\u5730\u5e94\u4e86\u4e00\u58f0\uff0c\u53c8\u5c0f\u58f0\u7684\u81ea\u8bf4\u81ea\u8bdd\uff0c\u201d\u4eba\u5bb6\u4e5f\u53ea\u4e0d\u8fc7\u662f\u5f00\u5f00\u73a9\u7b11\u800c\u5df2\u2026\u2026\u201d \u3000\u3000\u522b\u770b\u5c31\u4e09\u56db\u4e2a\u4eba\uff0c\u53ef\u5c31\u662f\u6027\u60c5\u548c\u7981\u5fcc\uff0c\u4eba\u4eba\u90fd\u4e0d\u4e00\u6837\u3002\u9a86\u94c3\u5343\u91d1\u5c0f\u59d0\u813e\u6c14\uff0c\u5c31\u7b97\u662f\u51fa\u6765\u95fb\u6c5f\u7322\uff0c\u4e5f\u603b\u7231\u6349\u5f04\u4eba\u3001\u8981\u4eba\u8fc1\u5c31\u5979\u3002\u725b\u4e3d\u751f\u4efb\u52b3\u4efb\u6028\uff0c\u56e0\u5b66\u5386\u4e0d\u591f\uff0c\u597d\u751f\u81ea\u5351\uff0c\u6545\u7231\u523b\u610f\u88c5\u626e\uff0c\u4f46\u5c31\u662f\u522b\u6709\u4e00\u53e5\u8fb1\u53ca\u4ed6\u6bcd\u4eb2\u3002\u81f3\u4e8e\u53f2\u6d41\u82b3\uff0c\u5bf9\u4ed6\u800c\u8a00\uff0c\u7b80\u76f4\u662f\u4e00\u6735\u82b1\u4e00\u6b21\u4f24\u5fc3\uff0c\u6bcf\u4e00\u6b21\u8feb\u5973\u5b69\u5b50\u7684\u7ed3\u679c\uff0c\u4e0d\u662f\u4ee4\u4ed6\u5931\u671b\u7684\u7a7a\u7684\u4fe1\u7bb1\uff0c\u5c31\u662f\u5fc3\u788e\u7684\u7535\u8bdd\uff0c\u5230\u5934\u6765\u4ed6\u603b\u662f\u8981\u5ba3\u79f0\uff1a\u5fc3\u91cc\u6839\u672c\u90fd\u6ca1\u6709\u5979\u3002 \u3000\u3000\u6240\u4ee5\u5728\u4ed6\u9762\u524d\u4e5f\u6700\u597d\u4e0d\u8981\u63d0\u4ed6\u6ca1\u6709\u5973\u670b\u53cb\u7684\u4e8b\u3002 \u3000\u3000\u5728\u8fd9\u4e16\u754c\u4e0a\uff0c\u6bcf\u4e00\u4e2a\u4eba\u548c\u6bcf\u4e2a\u5730\u65b9\uff0c\u90fd\u6709\u4ed6\uff08\u5b83\uff09\u7684\u7981\u5fcc\uff0c\u5c31\u4f3c\u4e07\u52ff\u8bf7\u56de\u6559\u5f92\u5403\u732a\u8089\uff0c\u4e0d\u8981\u8bf8\u5370\u5ea6\u4eba\u5403\u725b\u8089\u4e00\u6837\uff0c\u522b\u5bf9\u79c3\u5934\u8bf4\u5934\u53d1\uff0c\u522b\u5bf9\u4e11\u5987\u63d0\u955c\u5b50\u3002 \u3000\u3000\u8fd9\u4e16\u754c\u4e0a\uff0c\u6709\u7684\u662f\u7981\u5fcc\u3002 \u3000\u3000\u751a\u81f3\u53ef\u4ee5\u8bf4\uff0c\u4f60\u8981\u8ba8\u4e00\u4e2a\u4eba\u7684\u6b22\u5fc3\uff0c\u9996\u5148\u5404\u8981\u4e86\u89e3\u4ed6\u7684\u7981\u5fcc\u3002 \u3000\u3000\u2014\u2014\u6653\u5f97\u5982\u4f55\u9082\u5f00\u4ed6\u7684\u7981\u5fcc\u5c31\u53ef\u4ee5\u4f7f\u4ed6\u5bf9\u4f60\u6ca1\u6709\u987e\u5fcc\u3002 \u3000\u3000\uff12\u3001\u4e00\u6b21\u610f\u5916\u4e00\u4e2a\u6559\u8bad \u3000\u3000\u673a\u573a\u4e5f\u6709\u5b83\u7684\u201c\u7981\u5fcc\u201d\u3002 \u3000\u3000\u6bd4\u65b9\u8bf4\uff1a\u4e0d\u80fd\u5e26\u67aa\u68b0\u3001\u6bd2\u54c1\u3001\u8fdd\u7981\u54c1\u7b49\uff0c\u5c31\u662f\u5b83\u7684\u7981\u5fcc\u3002\u53ea\u8981\u89e6\u72af\u4e86\u8fd9\u7981\u5fcc\uff0c\u6709\u7684\u4eba\u4f1a\u88ab\u9001\u7262\uff0c\u6709\u7684\u4eba\u751a\u81f3\u8fd8\u4f1a\u88ab\u67aa\u6bd9\u3002 \u3000\u3000\u6bcf\u4e2a\u56fd\u5bb6\u90fd\u6709\u5b83\u7684\u7981\u5fcc\u3002 \u3000\u3000\u6709\u7684\u5730\u65b9\u4e0d\u53ef\u4ee5\u516c\u5f00\u6279\u8bc4\u653f\u5e9c\uff0c\u6709\u7684\u5730\u65b9\u4e0d\u53ef\u4ee5\u6253\u730e\uff0c\u6709\u7684\u5730\u65b9\u4e0d\u53ef\u4ee5\u516c\u5f00\u5c0f\u4fbf\u3002\u5982\u679c\u89e6\u72af\u4e86\u8fd9\u4e9b\u7981\u5fcc\uff0c\u5f88\u53ef\u80fd\u5c31\u4ece\u6b64\u4e0d\u89c1\u5929\u65e5\uff0c\u751a\u81f3\u7ed9\u4eba\u5207\u4e0b\u4e86\u547d\u6839\u5b50\u653e\u4eba\u4f60\u7684\u53e3\u888b\u91cc\u3002 \u3000\u3000\u6bcf\u4e2a\u56fd\u5bb6\u7684\u4eba\u6c11\u4e5f\u6709\u4ed6\u7684\u7981\u5fcc\u3002 \u3000\u3000\u6709\u4e9b\u5730\u65b9\u7684\u4eba\u4e0d\u80fd\u78b0\u4ed6\u7684\u5934\u3001\u6709\u4e9b\u5730\u65b9\u7684\u4eba\u5728\u8fc7\u5e74\u7684\u65f6\u5019\u4e0d\u80fd\u8bf4\u4e0d\u5409\u5229\u7684\u8bdd\uff0c\u6709\u4e9b\u5730\u65b9\u7684\u4eba\u5728\u5ff5\u7ecf\u7684\u65f6\u5019\u4f60\u4e0d\u80fd\u6253\u5475\u6b20\uff0c\u5426\u5219\uff0c\u4f60\u5f88\u53ef\u80fd\u5c31\u4f1a\u88ab\u4eba\u64b5\u51fa\u6765\uff0c\u751a\u6216\u662f\u7ed9\u4eba\u5728\u820c\u6839\u7a7f\u4e86\u4e00\u652f\u9488\uff0c\u6a2a\u8d2f\u4e86\u5de6\u53f3\u4e24\u816e\u3002 \u3000\u3000\u540c\u662f\u673a\u573a\uff0c\u4f46\u6bcf\u4e2a\u673a\u573a\u7684\u201c\u7981\u5fcc\u201d\u53ef\u80fd\u5e76\u4e0d\u4e00\u6837\u3002 \u3000\u3000\u50cf\u5728\u8fd9\u91cc\uff0c\u5982\u679c\u4f60\u534a\u591c\u4e09\u66f4\u6b65\u51fa\u673a\u573a\uff0c\u800c\u53c8\u4e0d\u662f\u521a\u5de7\u6709\u73ed\u673a\u964d\u843d\u7684\u65f6\u5019\uff0c\u4f60\u4fbf\u5f88\u53ef\u80fd\u4f1a\u9047\u5230\u4e00\u79cd\u60c5\u51b5\uff1a \u3000\u3000\u6ca1\u6709\u8ba1\u7a0b\u8f66\u3002 \u3000\u3000\u5f53\u7136\uff0c\u4e5f\u6ca1\u6709\u51fa\u79df\u6c7d\u8f66\u3002 \u3000\u3000\u8fd9\u513f\u79bb\u5e02\u533a\u6709\u5341\u4e5d\u82f1\u91cc\u8fdc\uff0c\u800c\u4e14\u4f4d\u4e8e\u90ca\u533a\uff0c\u6cbf\u8def\u9edd\u6697\uff0c\u63d0\u7740\u884c\u674e\uff0c\u6ca1\u6709\u8f66\u5b50\uff0c\u90a3\u5c31\u5bf8\u6b65\u96be\u884c\u3002 \u3000\u3000\u53ef\u80fd\u56e0\u4e3a\u5927\u70ed\uff0c\u53f2\u6d41\u82b3\u5df2\u663e\u5f97\u7126\u71e5\u4e86\uff1a\u201c\u4f60\u4e0d\u662f\u8bf4\u8521\u56db\u5e78\u4f1a\u6765\u63a5\u54b1\u4eec\u5417\uff1f\u201d \u3000\u3000\u8fd9\u4e9b\u4eba\u91cc\uff0c\u53ea\u6709\u9648\u5251\u8c01\u8ddf\u8521\u56db\u5e78\u662f\u4f1a\u8fc7\u9762\u7684\uff0c\u6240\u4ee5\u628a\u95ee\u9898\u90fd\u7559\u7ed9\u9648\u5251\u8c01\u3002 \u3000\u3000\u201c\u55ef\u3002\u201d\u9648\u5251\u8c01\u786e\u77e5\u8521\u56db\u5e78\u8fd8\u6ca1\u6709\u6765\u3002 \u3000\u3000\u4ed6\u7684\u8bb0\u5fc6\u529b\u4e00\u5411\u90fd\u597d\uff0c\u89c1\u8fc7\u7684\u4eba\uff0c\u90fd\u4e0d\u4f1a\u5fd8\u6389\uff1a\u66f4\u4f55\u51b5\u8521\u56db\u5e78\u4e88\u4ed6\u6781\u6df1\u523b\u7684\u597d\u5370\u8c61\u3002 \u3000\u3000\u201c\u8fd9\u6837\u7684\u4e00\u4e2a\u4e49\u5f1f\uff0c\u201d\u9a86\u94c3\u5937\u7136\u7684\u8bf4\uff1a\u201c\u539f\u6765\u8fd9\u4e48\u4e0d\u5b88\u4fe1\u7528\u3002\u201d \u3000\u3000\u201c\u4e0d\u4f1a\u7684\uff0c\u201d\u9648\u5251\u8c01\u8bf4\uff0c\u201c\u4ed6\u4e00\u5411\u90fd\u5f88\u5b88\u4fe1\u7528\uff0c\u60f3\u5fc5\u662f\u8def\u4e0a\u6709\u4e8b\u803d\u6401\u4e86\u3002\u201d \u3000\u3000\u201c\u6211\u6700\u4e0d\u559c\u6b22\u4e0d\u5b88\u4fe1\u8bfa\u7684\u4eba\uff0c\u6709\u65f6\u5019\uff0c\u4e0d\u5b88\u4fe1\u8981\u6bd4\u76f4\u63a5\u7528\u5229\u5668\u53bb\u4f24\u5bb3\u4e00\u4e2a\u4eba\u66f4\u751a\uff0c\u201d\u9648\u5251\u8c01\u8bb0\u5f97\u8521\u56db\u5e78\u66fe\u5bf9\u4ed6\u8bf4\u8fc7\u8fd9\u6837\u7684\u4e00\u6bb5\u6d3b\uff1a\u201c\u7528\u5200\u53bb\u4f24\u5bb3\u4e00\u4e2a\u4eba\uff0c\u4f24\u5c31\u662f\u4f24\u3001\u6b7b\u5c31\u662f\u6b7b\uff0c\u8981\u662f\u907f\u5f97\u8fc7\uff0c\u4f60\u8fd8\u53ef\u4ee5\u53cd\u51fb\uff0c\u8981\u662f\u8eab\u624b\u8fc7\u4eba\uff0c\u4e5f\u53ef\u4fdd\u4e0d\u6b7b\u4e0d\u4f24\u3002\u6545\u7528\u5200\u53bb\u4f24\u5bb3\u83ab\u5982\u6587\u5b57\u3002\u81ea\u7eb8\u9ed1\u5b57\uff0c\u5370\u51fa\u6765\u516c\u8bf8\u4e8e\u4e16\uff0c\u5c31\u7b97\u4e0d\u662f\u7684\u4e5f\u96be\u4e00\u4e00\u6f84\u6e05\uff0c\u6240\u9020\u6210\u7684\u4f24\u5bb3\uff0c\u65e2\u6df1\u4e14\u8fdc\uff0c\u5c24\u751a\u4e8e\u5229\u5668\u3002\u4f46\u4ee5\u6587\u5b57\u4f24\u4eba\u53c8\u4e0d\u53ca\u8bed\u8a00\u3002\u80cc\u540e\u4e2d\u4f24\u3001\u9020\u8c23\u751f\u8bfd\uff0c\u66f4\u9632\u4e0d\u80dc\u9632\uff0c\u8fde\u7a7a\u7a74\u6765\u98ce\u7684\u7a74\u4e5f\u65e0\u8ff9\u53ef\u5bfb\uff0c\u771f\u662f\u6740\u4eba\u4e0d\u6cbe\u8840\uff0c\u4f46\u6700\u751a\u7684\u83ab\u5982\u4e0d\u5b88\u4fe1\u8bfa\u2026\u2026\u201d \u3000\u3000\u201c\u597d\u6c49\u90fd\u662f\u91cd\u7136\u8bfa\u7684\u3002\u53e4\u4eba\u4e00\u8bed\u6258\u5b64\u3001\u4e00\u8bfa\u5343\u91d1\uff0c\u5c31\u662f\u8fd9\u4e2a\u610f\u601d\u3002\u522b\u4eba\u4fe1\u4efb\u4f60\uff0c\u53ef\u80fd\u8eab\u5bb6\u6027\u547d\u90fd\u4ea4\u4e86\u7ed9\u4f60\uff0c\u4ee5\u4e3a\u4f60\u662f\u53ef\u4fe1\u7684\u3002\u4f46\u8981\u662f\u4f60\u7a81\u7136\u7ffb\u8138\u5931\u4fe1\u3001\u4e0d\u8d1f\u8d23\u4efb\uff0c\u4ee4\u5bf9\u65b9\u53d7\u5bb3\u4e4b\u5927\uff0c\u6709\u65f6\u53cd\u4e0d\u5982\u523a\u4ed6\u4e00\u5200\u2026\u2026\u201d\u4ed6\u8bb0\u5f97\u8521\u56db\u5e78\u8fd8\u8fd9\u6837\u4ed6\u8bf4\u8fc7\uff0c\u201c\u4f60\u8981\u4f24\u4e00\u4e2a\u4eba\u7684\u8eab\u4f53\uff0c\u53ef\u4ee5\u80cc\u540e\u523a\u4ed6\u4e00\u5200\u3002\u4f60\u8981\u4f24\u5bb3\u4e00\u4e2a\u4eba\u7684\u540d\u8a89\uff0c\u7528\u8bed\u8a00\u6587\u5b57\u5c31\u53ef\u4ee5\u7acb\u7aff\u89c1\u5f71\uff0c\u4f46\u82e5\u4f60\u8981\u4f24\u4e00\u4e2a\u4eba\u7684\u5fc3\uff0c\u83ab\u8fc7\u4e8e\u5728\u4ed6\u4fe1\u91cd\u4e8e\u4f60\u4e4b\u9645\uff0c\u751a\u6216\u662f\u5168\u529b\u5e94\u4ed8\u5371\u96be\u4e4b\u65f6\uff0c\u4f60\u820d\u5f03\u4e86\u4ed6\uff0c\u4e14\u628a\u4e00\u5207\u5982\u6cf0\u5c71\u4e4b\u6258\u89c6\u5982\u9e45\u6bdb\u4e4b\u8f7b\u4e00\u2026\u201d \u3000\u3000\u201c\u6211\u9047\u8fc7\u8fd9\u79cd\u4eba\uff0c\u4ed6\u4eec\u4ee4\u6211\u75db\u5fc3\u75be\u9996\uff0c\u51e0\u4e4e\u5bf9\u4eba\u6027\u5931\u53bb\u4e86\u4fe1\u5ff5\uff0c\u592a\u6b8b\u5fcd\u4e86\u3002\u201d \u3000\u3000\u201c\u5e78\u4e8f\u6211\u9047\u89c1\u4e86\u4f60\u3002\u201d \u3000\u3000\u201c\u8fd8\u6709\u5f20\u5c0f\u6101\u3002\u201d \u3000\u3000\u9648\u5251\u6dee\u8bb0\u5f97\u8fd9\u4e9b\u8bdd\u3002 \u3000\u3000\u4e00\u4e2a\u5e74\u8f7b\u4eba\uff0c\u7528\u4ed6\u7684\u81ea\u4fe1\u548c\u7231\u3001\u5149\u548c\u70ed\u903c\u51fa\u6765\u7684\u8bdd\u3002 \u3000\u3000\u9648\u5251\u8c01\u77e5\u9053\u8521\u56db\u5e78\u4e0d\u4f1a\u4e0d\u6765\u7684\u3002 \u3000\u3000\u2014\u2014\u201c\u4e0d\u5e73\u793e\u201d\u91cc\u7684\u201c\u516d\u4eba\u5e2e\u201d\uff0c\u5357\u4e0b\u4e0e\u4ed6\u548c\u201c\u5927\u7ea2\u82b1\u201d\u4f1a\u96c6\uff0c\u8fd9\u662f\u4ef6\u201c\u5927\u4e8b\u201d\uff0c\u4ed6\u600e\u4e48\u53ef\u80fd\u4e0d\u6765\uff0c\u600e\u4e48\u53ef\u4ee5\u4e0d\u6765\uff01 \u3000\u3000\u2014\u2014\u4e00\u4e2a\u6700\u6068\u4e0d\u5b88\u4fe1\u7ea6\u7684\u4eba\uff0c\u51b3\u4e0d\u4f1a\u65e0\u6545\u5931\u7ea6\u3002 \u3000\u3000\u5c31\u7b97\u4ed6\u6709\u4e8b\u4e0d\u80fd\u6765\uff0c\u4e5f\u4f1a\u53eb\u4eba\u6765\u5440\uff01\u9648\u5251\u8c01\u60f3\u3002\u4ed6\u4e0d\u662f\u6709\u4e2a\u5973\u670b\u53cb\u53eb\u5f20\u5c0f\u6101\u7684\u5417\uff1f\u542c\u8bf4\u5df2\u5feb\u8981\u7ed3\u5a5a\u4e86\u2026\u2026 \u3000\u3000\u4e0d\u8fc7\u76ee\u524d\u8981\u9762\u5bf9\u7684\u662f\uff1a\u8981\u662f\u8521\u56db\u5e78\u771f\u7684\u4e0d\u6765\uff0c\u4ed6\u4eec\u7684\u60c5\u5f62\u53ef\u6709\u70b9\u201c\u68d8\u624b\u201d\u3002 \u3000\u3000\u4ed6\u51b3\u5b9a\u6253\u7535\u8bdd\u7ed9\u8521\u56db\u5e78\u3002 \u3000\u3000\u7531\u4e8e\u4ed6\u4eec\u6240\u62b5\u6295\u7684\u673a\u573a\u548c\u8521\u56db\u5e78\u6240\u4f4f\u7684\u5730\u65b9\u662f\u5728\u4e0d\u540c\u7684\u5dde\u57df\u91cc\uff0c\u9700\u8981\u62e8\u4e0d\u540c\u7684\u5b57\u53f7\uff0c\u800c\u9648\u5251\u8c01\u624b\u4e0a\u7684\u786c\u5e01\u5e76\u4e0d\u8db3\u591f\uff0c\u52a0\u4e0a\u63a5\u7ebf\u751f\u8bed\u97f3\u4e0a\u7684\u8bef\u4f1a\uff0c\u63a5\u4e86\u597d\u51e0\u6b21\uff0c\u90fd\u6ca1\u6709\u63a5\u901a\u3002 \u3000\u3000\u9a86\u94c3\u66f4\u4e0d\u8010\u70e6\u4e86\uff0c\u201c\u600e\u4e48\u641e\u7684\u3002\u201d\u5979\u672c\u6765\u643d\u4e86\u4e00\u70b9\u6e05\u6de1\u7684\u9999\u6c34\uff0c\u53ef\u662f\u8fd9\u4e00\u9635\u5b50\u6dcc\u4e86\u70b9\u6c57\uff0c\u82b3\u9999\u66f4\u6d53\u4e86\u3002 \u3000\u3000\u53f2\u6d41\u82b3\u5efa\u8bae\uff1a\u201c\u90a3\u6211\u4eec\u53eb\u90e8\u8ba1\u7a0b\u8f66\u5148\u5230\u5e02\u533a\u627e\u5bb6\u9152\u5e97\u518d\u8bf4\u5427\u3002\u201d \u3000\u3000\u201c\u90a3\u4e5f\u597d\uff0c\u201d\u9664\u5251\u8c01\u987a\u4fbf\u9644\u5e26\u4e86\u4e00\u53e5\uff1a\u201c\u6700\u597d\u53eb\u534e\u4eba\u9a7e\u7684\u8f66\u5b50\uff0c\u6bd4\u8f83\u65b9\u4fbf\u95ee\u4ed6\u4e00\u4e9b\u95ee\u9898\u3002\u201d \u3000\u3000\u6ca1\u60f3\u5230\uff0c\u9648\u5251\u8c01\u5429\u5490\u4e86\u53f2\u6d41\u82b3\u8fd9\u4e00\u53e5\u8bdd\uff0c\u51e0\u4e4e\u4f7f\u4ed6\u201c\u5927\u95f9\u673a\u573a\u3002\u201d \u3000\u3000\u53f2\u6d41\u82b3\u628a\u884c\u674e\u653e\u5728\u4e00\u8fb9\uff0c\u53bb\u53eb\u8ba1\u7a0b\u8f66\uff0c\u5341\u51e0\u5206\u949f\u90fd\u6ca1\u56de\u6765\u3002\u9648\u5251\u8c01\u4fbf\u53eb\u725b\u4e3d\u751f\u4e5f\u53bb\u770b\u770b\u3002 \u3000\u3000\u2014\u2014\u56e0\u4e3a\u884c\u674e\u53ef\u771f\u4e0d\u5c11\uff0c\u800c\u4e14\u6b63\u503c\u591c\u6df1\uff0c\u8981\u9a86\u94c3\u4e5f\u51fa\u53bb\u201c\u629b\u5934\u9732\u8138\u201d\u7684\uff0c\u603b\u4e0d\u592a\u597d\u3002 \u3000\u3000\u5c82\u6599\u725b\u4e3d\u751f\u8fd9\u4e00\u53bb\uff0c\u72b9\u5982\u5728\u706b\u4e0a\u6cfc\u6cb9\u3002 \u3000\u3000\u539f\u6765\u53f2\u6d41\u82b3\u5230\u5916\u9762\u53bb\u53eb\u8ba1\u7a0b\u8f66\uff0c\u4e0d\u719f\u8def\uff0c\u89c1\u673a\u573a\u5916\u7684\u8d70\u9053\u4e0a\u4e00\u5217\u6392\u7740\u4e0d\u5c11\u8ba1\u7a0b\u8f66\uff0c\u4fbf\u627e\u4e86\u4e00\u90e8\u6bd4\u8f83\u65b0\u548c\u5bbd\u8212\u7684\u201c\u9a6c\u8d5b\u5730\u201d\uff0c\u53eb\u9192\u4e86\u90a3\u4e2a\u6b63\u5728\u606c\u7761\u4e2d\u7684\u53f8\u673a\uff0c\u95ee\u4ed6\u4ef7\u94b1\u3002 \u3000\u3000\u201c\u53bb\u54ea\u91cc\uff1f\u201d \u3000\u3000\u53f2\u6d41\u82b3\u8bf4\u4e86\u5730\u70b9\uff0c\u95ee\u4ed6\uff1a\u201c\u591a\u5c11\u94b1\uff1f\u201d \u3000\u3000\u201c\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u591a\u5c11\u94b1\uff1f\u201d \u3000\u3000\u4ed6\u7528\u7684\u662f\u7ca4\u8bed\uff0c\u5bf9\u65b9\u4e5f\u662f\u7528\u5e7f\u4e1c\u6d3b\uff0c\u4e0d\u8fc7\u4ed6\u662f\u53f0\u6e7e\u6765\u7684\uff0c\u7ca4\u8bed\u8bf4\u5f97\u8352\u8154\u8d70\u677f\uff0c\u4f46\u5728\u9999\u6e2f\u90fd\u8fd8\u884c\u5f97\u901a\uff0c\u7ed3\u679c\u6765\u5230\u6b64\u5730\uff0c\u90a3\u53f8\u673a\u542c\u6765\u542c\u53bb\u90fd\u4e0d\u660e\u767d\u3002 \u3000\u3000\u542c\u4e86\u8001\u534a\u5929\uff0c\u90a3\u53f8\u673a\u624d\u602a\u773c\u4e00\u7ffb\uff0c\u604d\u7136\u7684\u8bf4\uff1a\u201c\u4f60\u8bf4\u7684\u662f\u51e0\u591a\u956d\uff01\u201d\u7136\u540e\u6279\u8bc4\u4ed6\uff1a\u201c\u4f60\u7684\u5e7f\u4e1c\u8bdd\u771f\u96be\u542c\uff01\u201d \u3000\u3000\u201c\u4ec0\u4e48\u2018\u956d\u2019\uff01\u201d\u53f2\u6d41\u82b3\u5f53\u7136\u4e0d\u670d\u6c14\uff0c\u4ed6\u7684\u5e7f\u4e1c\u8bdd\u867d\u7136\u6b20\u4f73\uff0c\u4f46\u8fd9\u53e5\u8bdd\u4e3a\u53d1\u97f3\u81ea\u4fe1\u8fd8\u628a\u63e1\u5f97\u51c6\uff1a\u201c\u94b1\u5c31\u662f\u94b1\uff01\u201d \u3000\u3000\u201c\u5165\u4e61\u968f\u4fd7\uff0c\u770b\u4f60\u7684\u6837\u5b50\u662f\u8bfb\u4e66\u4eba\uff0c\u8fde\u8fd9\u70b9\u9053\u7406\u90fd\u4e0d\u77e5\u9053\uff0c\u201d\u90a3\u53f8\u673a\u51b7\u7b11\uff1a\u201c\u6211\u4eec\u8fd9\u513f\u53eb\u94b1\u505a\u2018\u956d\u2019\uff01\u201d \u3000\u3000\u8fd9\u4e00\u9635\u4e89\u6267\uff0c\u53cc\u65b9\u90fd\u6ca1\u4e86\u597d\u5370\u8c61\u3002 \u3000\u3000\u4e8e\u662f\u53f8\u673a\u6f2b\u5929\u5f00\u4ef7\uff0c\u53f2\u6d41\u82b3\u5fc3\u91cc\u4e00\u7b97\uff0c\u89c9\u5f97\u592a\u5212\u4e0d\u6765\uff0c\u4fbf\u843d\u5730\u8fd8\u94b1\u3002 \u3000\u3000\u201c\u592a\u8d35\u4e86\uff01\u201d \u3000\u3000\u201c\u8d35\uff1a\u5927\u4f6c\uff0c\u6211\u5df2\u51c6\u5907\u4f11\u606f\u7684\u4e86\uff0c\u4f60\u60f3\uff0c\u4e09\u66f4\u534a\u591c\u7684\uff0c\u8981\u8f7d\u4f60\u8d70\u516b\u5341\u591a\u91cc\uff0c\u6211\u8fd8\u5f97\u7a7a\u8f66\u56de\u6765\uff0c\u4e00\u8def\u4e0a\u8fd8\u6ca1\u89c9\u53ef\u7761\uff0c\u591a\u8f9b\u82e6\uff0c\u6211\u8fd8\u5acc\u5c11\u5462\uff01\u201d \u3000\u3000\u201c\u8fd8\u7761\u89c9\uff1f\u7231\u7761\u89c9\u53c8\u4f55\u5fc5\u51fa\u6765\u9a7e\u8f66\uff01\u201d \u3000\u3000\u201c\u2018\u8001\u53cb\uff0c\u4f60\u597d\u547d\uff01\u4f46\u4e0d\u5fc5\u591a\u8bf4\u4e86\uff0c\u956d\uff0c\u6211\u662f\u6536\u8fd9\u4e48\u591a\u7684\u4e86\uff0c\u6765\u56de\u8fd9\u767e\u6765\u91cc\u8def\uff0c\u53ef\u4e0d\u597d\u8d5a\u54ea\uff01\u201d \u3000\u3000\u201c\u8fd9\u513f\u6ca1\u6709\u516c\u4ef7\u5417\uff1f\u201d \u3000\u3000\u201c\u516c\u4ef7\uff1f\u4f60\u4ee5\u4e3a\u8fd9\u662f\u4e2d\u56fd\u5927\u9646\uff01\u6211\u8fd9\u5c31\u662f\u516c\u4ef7\uff01\u534a\u591c\u5f00\u8f66\uff0c\u7b97\u8d35\u4e00\u4e9b\u4e5f\u7406\u6240\u5f53\u7136\uff01\u201d \u3000\u3000\u201c\u4e00\u70b9\u6298\u6263\u90fd\u4e0d\u6253\uff1f\u201d \u3000\u3000\u201c\u4f60\u4ee5\u4e3a\u6211\u4eec\u5f00\u8f66\u7684\u662f\u5728\u5927\u51cf\u4ef7\uff1f\u201d \u3000\u3000\u201c\u90a3\u4e0d\u5982\u7167\u8868\u7b97\u597d\u4e86\u3002\u201d \u3000\u3000\u201c\u8868\uff1f\u6211\u4eec\u8fd9\u513f\u4e0d\u5174\u7b97\u8868\u7684\uff01\u201d\u90a3\u540d\u80a4\u8272\u9752\u767d\u7684\u53f8\u673a\u6253\u91cf\u7740\u4ed6\uff0c\u5634\u89d2\u634e\u4e86\u51e0\u5206\u8ba5\u523a\uff0c\u201c\u5148\u751f\uff0c\u4f60\u8981\u662f\u6ca1\u94b1\uff0c\u5c31\u4e0d\u8981\u6765\u6e38\u57e0\u5427\uff1f\u201d \u3000\u3000\u201c\u8c01\u8bf4\u6211\u6ca1\u94b1\uff0c\u4f60\u8fd9\u662f\u72d7\u773c\u770b\u4eba\u4f4e\u561b\uff01\u201d\u53f2\u6d41\u82b3\u5fff\u5fff\u4ed6\u8bf4\uff0c\u201c\u8ba1\u7a0b\u8f66\u4e0d\u7b97\u8868\uff0c\u90a3\u600e\u4e48\u8ba1\u7a0b\uff1f\u201d \u3000\u3000\u90a3\u8138\u8272\u9752\u767d\u7684\u53f8\u673a\u731b\u5730\u63a8\u5f00\u4e86\u95e8\uff0c\u6c14\u864e\u864e\u7684\u903c\u8fd1\u53f2\u6d41\u82b3\u8bf4\uff1a\u201c\u4e8f\u4f60\u8fd8\u662f\u4e2a\u8bfb\u4e66\u4eba\uff0c\u51fa\u53e3\u4f24\u4eba\uff01\u201d \u3000\u3000\u8fd9\u65f6\u5df2\u6709\u5176\u4ed6\u7684\u53f8\u673a\u8dd1\u8fc7\u6765\u770b\u53d1\u751f\u4ec0\u4e48\u4e8b\uff0c\u5404\u79cd\u5404\u8272\u7684\u4eba\u90fd\u6709\uff0c\u6709\u7684\u4e0d\u540c\u79cd\u65cf\u7684\u4eba\u7528\u9a6c\u6765\u8bed\u6216\u82f1\u8bed\u8be2\u95ee\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\u60c5\uff0c\u540c\u662f\u534e\u4eba\u4e5f\u7528\u4e0d\u540c\u7684\u65b9\u8a00\u8bf8\u5982\u6f6e\u5dde\u8bdd\u3001\u798f\u5efa\u8bdd\u3001\u5ba2\u5bb6\u8bdd\u3001\u5e7f\u4e1c\u8bdd\u5728\u4e92\u76f8\u4ea4\u8c08\uff0c\u6d3b\u4f3c\u65b9\u8a00\u5927\u5c55\u3002 \u3000\u3000\u6709\u4e2a\u77ee\u80d6\u5b50\u53f8\u673a\u521a\u5230\uff0c\u4fbf\u51d1\u8da3\u7684\u95ee\u4e86\u4e00\u53e5\uff0c\u201c\u4f55\u767e\u660e\uff0c\u4ed6\u9a82\u4f60\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u90a3\u9752\u767d\u6c49\u5b50\u6c14\u6124\u672a\u5e73\uff1a\u201c\u4ed6\u9a82\u6211\u662f\u72d7\uff01\u201d \u3000\u3000\u201c\u6696\uff0c\u8bf4\u6765\u5012\u4e5f\u5f88\u50cf\uff01\u201d\u90a3\u77ee\u80d6\u5b50\u5c45\u7136\u5927\u6709\u540c\u611f\u3002 \u3000\u3000\u201c\u6b7b\u2018\u75f0\u6876\u2019\uff0c\u4f60\u624d\u662f\u72d7\uff0c\u61d2\u76ae\u72d7\uff01\u201d\u4f55\u767e\u660e\u7b80\u76f4\u662f\u706b\u4e0a\u52a0\u4e86\u6cb9\uff0c\u201c\u4f60\u8fd9\u662f\u5e2e\u5916\u4eba\u561b\uff01\u201d \u3000\u3000\u201c\u6211\u4eec\u8fd9\u662f\u5728\u7406\u8bba\uff0c\u4ec0\u4e48\u5916\u4eba\u4e0d\u5916\u4eba\u7684\uff01\u201d\u53f2\u6d41\u82b3\u9a6c\u4e0a\u53cd\u9a73\uff0c\u201c\u4f60\u4eec\u8fd9\u624d\u662f\u6b3a\u8d1f\u5916\u4eba\uff01\u201d \u3000\u3000\u6709\u4e00\u4e2a\u4eba\u7acb\u5373\u68ee\u7136\u7684\u8bf4\uff1a\u201c\u6b3a\u8d1f\u4f60\u53c8\u600e\u6837\uff1f\u56db\u773c\u4ed4\uff0c\u6765\u5230\u6211\u4eec\u5730\u5934\uff0c\u8fd8\u8fd9\u4e48\u62db\u79ef\uff1f\u201d \u3000\u3000\u53f2\u6d41\u82b3\u4e00\u5411\u597d\u5f3a\uff0c\u5faa\u58f0\u97f3\u8fc7\u5934\u53bb\uff1a\u201c\u4f60\u4eec\u60f3\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u8fd9\u4e00\u6765\uff0c\u66f4\u72af\u4e86\u4f17\u6012\uff0c\u6709\u4e00\u4e2a\u6bd4\u8f83\u6301\u91cd\u7684\u6c49\u5b50\u8bf4\uff1a\u201c\u5e74\u8f7b\u4eba\uff0c\u8fd9\u5c31\u662f\u4f60\u7684\u4e0d\u662f\u4e86\uff0c\u6211\u4eec\u8fd9\u513f\u4e0d\u53eb\u8ba1\u7a0b\u8f66\uff0c\u53eb\u7684\u58eb\uff0c\u662f\u4e0d\u8ba1\u7a0b\u7684\u3002\u201d \u3000\u3000\u201c\u7684\u58eb\u548c\u8ba1\u7a0b\u8f66\u4e0d\u4e5f\u5c31\u662f\u4e00\u6837\u7684\u672c\u5b50\u5417\uff1f\u201d\u53f2\u6d41\u82b3\u6b7b\u4e0d\u8ba4\u8f93\uff1a\u201c\u4ed6\u7b97\u5f97\u592a\u8d35\u4e86\uff0c\u7b80\u76f4\u662f\u5403\u5b9a\u4e86\u6e38\u5ba2\u561b\uff01\u201d \u3000\u3000\u90a3\u6301\u91cd\u7684\u4e2d\u5e74\u6c49\u5b50\u95ee\u4e86\u94b1\u6570\uff0c\u8bf4\uff1a\u201c\u4e0d\u8d35\u4e86\uff0c\u534a\u591c\u884c\u8f66\uff0c\u662f\u8981\u8fd9\u4e2a\u4ef7\u94b1\u7684\u3002\u4f55\u51b5\uff0c\u662f\u4f60\u627e\u4e0a\u4ed6\u7684\uff0c\u4ed6\u5f00\u9ad8\u4e00\u70b9\u513f\u4ef7\u94b1\uff0c\u4e5f\u7406\u6240\u5f53\u7136\u3002\u201d \u3000\u3000\u53f2\u6d41\u82b3\u8bf4\uff1a\u201c\u90a3\u6211\u5c31\u4e0d\u5750\u4ed6\u7684\u8f66\u5b50\u4e86\u3002\u201d \u3000\u3000\u90a3\u9752\u767d\u6c49\u5b50\u8bf4\uff1a\u201c\u73b0\u5728\uff0c\u4f60\u51fa\u4e24\u500d\u7684\u4ef7\u94b1\uff0c\u6211\u4e5f\u4e0d\u8981\u8f7d\u4f60\u4e86\u3002\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u725b\u4e3d\u751f\u4e5f\u8d76\u6765\u4e86\u3002 \u3000\u3000\u7531\u4e8e\u725b\u4e3d\u751f\u957f\u5f97\u9b41\u68a7\u8fc7\u4eba\uff0c\u90a3\u5e72\u4eba\u4e00\u770b\uff0c\u4f7f\u77e5\u4e0d\u597d\u60f9\uff0c\u6240\u4ee5\u4e5f\u4e0d\u518d\u5f00\u53e3\u3002 \u3000\u3000\u725b\u4e3d\u751f\u95ee\uff1a\u201c\u600e\u4e48\u4e86\uff1f\u201d \u3000\u3000\u53f2\u6d41\u82b3\u9010\u4e00\u95ee\u90a3\u4e9b\u53f8\u673a\uff1a\u201c\u4f60\u8981\u4e0d\u8981\u8f7d\uff1f\u201d\u7684\u58eb\u53f8\u673a\u540c\u884c\u95f4\u4e5f\u6709\u4ed6\u4eec\u7684\u9053\u4e49\uff0c\u5f88\u591a\u4eba\u4e0d\u60f3\u8ba9\u90a3\u4f55\u767e\u660e\u96be\u53d7\uff0c\u4e5f\u5c31\u4e0d\u80af\u63a5\u8fd9\u5355\u751f\u610f\u3002 \u3000\u3000\u4f46\u6709\u4e00\u4e2a\u957f\u8138\u9ad8\u7626\u7684\u6c49\u5b50\u8bf4\uff1a\u201c\u6211\u8f7d\uff0c\u201d \u3000\u3000\u4f55\u767e\u660e\u9a82\u9053\uff1a\u201c\u7af9\u7b52\u996d\uff0c\u4f60\u4e0d\u8bb2\u4e49\u6c14\uff01\u201d \u3000\u3000\u201c\u660e\u54e5\uff0c\u201d\u7af9\u7b52\u996d\u8bf4\uff1a\u201c\u4f60\u4e0d\u662f\u4e0d\u77e5\u9053\uff0c\u6211\u6253\u4ece\u6628\u5929\u534a\u591c\u628a\u8f66\u5f00\u8fdb\u6765\u4e4b\u540e\uff0c\u8f6e\u5230\u73b0\u5728\u8fd8\u6ca1\u8f6e\u8fc7\u4e00\u73ed\uff0c\u6211\u518d\u4e0d\u53d1\u5e02\uff0c\u56de\u5bb6\u600e\u4e48\u5f00\u996d\uff1f\u201d \u3000\u3000\u4f55\u767e\u660e\u6c14\u5f97\u4e0d\u776c\u4ed6\uff0c\u4e0d\u6599\uff0c\u53e6\u4e00\u540d\u9ebb\u76ae\u6c49\u5b50\u4e0d\u7518\u4e0d\u5fff\u5730\u8bf4\uff1a\u201d\u8fd9\u8d9f\u8f66\uff0c\u4f60\u62ff\u4e0d\u5982\u6211\u62ff\uff0c\u8f6e\u4e5f\u8be5\u5148\u8f6e\u5230\u6211\uff01\u201d \u3000\u3000\u7af9\u7b52\u996d\u8bf4\uff1a\u201c\u718a\u732b\uff0c\u4f60\u5c31\u522b\u8ddf\u6211\u62a2\u8fd9\u56de\u996d\u597d\u4e0d\u597d\uff1f\u201d \u3000\u3000\u4e24\u4eba\u53c8\u4e89\u6267\u8d77\u6765\uff0c\u4e0d\u610f\uff0c\u6709\u51e0\u6765\u9a6c\u6765\u4eba\u4e0a\u524d\u6765\uff0c\u8ddf\u534e\u4eba\u53f8\u673a\u4eec\u7528\u5deb\u8bed\u5927\u58f0\u5bf9\u8bdd\uff0c\u8d8a\u8bf4\u8d8a\u54cd\uff0c\u4f3c\u4e4e\u53c8\u542f\u4e86\u53e6\u4e00\u4e89\u7aef\u3002 \u3000\u3000\u8fd9\u56de\u5012\u662f\u53f2\u6d41\u82b3\u5947\u800c\u95ee\u9053\uff1a\u201c\u5e72\u4ec0\u4e48\uff1f\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\u60c5\uff1f\u201d \u3000\u3000\u4e89\u6267\u4f3c\u4e4e\u8d8a\u6765\u8d8a\u5267\u70c8\uff0c\u53cc\u65b9\u90fd\u662f\u5411\u53f2\u6d41\u82b3\u6307\u624b\u5212\u811a\uff0c\u7136\u540e\u4e89\u6301\u4e0d\u4f11\u3002\u8fd8\u6709\u4e24\u4e2a\u9a6c\u6765\u4eba\u8fc7\u6765\u62c9\u8d70\u53f2\u6d41\u82b3\uff0c\u4f46\u53c8\u6709\u534e\u4eba\u628a\u4ed6\u4eec\u63a8\u5f00\uff0c\u53f2\u6d41\u82b3\u66f4\u83ab\u540d\u5176\u5999\uff0c\u6025\u95ee\uff1a\u201c\u4ed6\u4eec\u8981\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u90a3\u6301\u91cd\u8001\u6210\u7684\u6c49\u5b50\u89e3\u91ca\u9053\uff1a\u201c\u8fd9\u513f\u673a\u573a\u7684\u7684\u58eb\u662f\u8f6e\u7740\u6765\u7684\uff0c\u8c01\u5148\u63d2\u53f7\u8c01\u5c31\u5148\u8f7d\u5ba2\uff0c\u6709\u7684\u672c\u5b50\u5df2\u6765\u4faf\u4e86\u4e24\u4e09\u5929\u4e86\u8fd8\u6ca1\u8f6e\u5230\u4e00\u8f66\u7684\u751f\u610f\uff0c\u4f60\u4e00\u6765\u5c31\u627e\u4e0a\u4f55\u767e\u660e\u90a3\u4e00\u90e8\uff0c\u7136\u540e\u53c8\u60f3\u5750\u66f4\u540e\u9762\u7af9\u7b52\u996d\u90a3\u4e00\u90e8\uff0c\u8fd9\u51e0\u4e2a\u5148\u6765\u7684\u81ea\u7136\u4e0d\u5fff\u6c14\u4e86\u3002\u201d \u3000\u3000\u201c\u6211\u4eec\u8981\u627e\u7684\u662f\u534e\u4eba\u53f8\u673a\u5440\uff01\u201d\u53f2\u6d41\u82b3\u5fd9\u7528\u82f1\u6587\u8ddf\u4ed6\u4eec\u8bf4\u4e86\u3002 \u3000\u3000\u53ea\u4e0d\u8fc7\uff0c\u6709\u7684\u53f8\u673a\uff0c\u4e0d\u5927\u542c\u5f97\u4eec\u82f1\u8bed\uff0c\u6709\u7684\u6839\u672c\u4e0d\u7406\u4ed6\uff0c\u53ea\u8bf4\uff1a\u201c\u54ea\u6709\u8fd9\u79cd\u4e8b\uff01\u5149\u627e\u534e\u4eba\u53f8\u673a\uff0c\u90a3\u5176\u4ed6\u79cd\u65cf\u7684\u53f8\u673a\u4e0d\u5fc5\u5f00\u8f66\u4e86\uff01\u201d \u3000\u3000\u53f2\u6d41\u82b3\u89c9\u5f97\u5bf9\u65b9\u86ee\u4e0d\u8bb2\u7406\uff1a\u201c\u6211\u4eec\u662f\u5916\u5730\u6e38\u5ba2\uff0c\u81ea\u7136\u60f3\u6211\u8bed\u8a00\u4e0a\u6bd4\u8f83\u65b9\u4fbf\uff0c\u4e5f\u6bd4\u8f83\u53ef\u4ee5\u6c9f\u901a\u7684\u53f8\u673a\u4f5c\u5411\u5bfc\u5440\uff01\u201d \u3000\u3000\u51e0\u4e2a\u9a6c\u6765\u53f8\u673a\u542c\u5f97\u4f3c\u61c2\u975e\u61c2\uff0c\u4f9d\u7136\u5341\u5206\u7684\u4e0d\u53cb\u597d\u3002\u4f55\u767e\u660e\u3001\u201c\u75f0\u6876\u201d\u7b49\u4eba\u672c\u6765\u5f88\u4e0d\u803b\u201c\u7af9\u7b52\u996d\u201d\u3001\u201c\u718a\u732b\u201d\u7b49\u62c9\u5ba2\u7684\u6240\u4e3a\uff0c\u4f46\u73b0\u5728\u4e5f\u5e2e\u56de\u201c\u81ea\u5df1\u4eba\u201d\uff0c\u7528\u5bf9\u65b9\u4e89\u8bba\u3002\u5176\u4e2d\u6709\u4eba\u63a8\u4e86\u5bf9\u65b9\u4e00\u4e0b\uff0c\u51e0\u4e4e\u5c31\u8981\u52a8\u8d77\u624b\u6765\u3002 \u3000\u3000\u5176\u4e2d\u4e00\u4e2a\u9a6c\u6765\u53f8\u673a\u82f1\u8bed\u8bf4\u5f97\u5f88\u6d41\u5229\uff0c\u4ed6\u56de\u7b54\u4e86\u53f2\u6d41\u82b3\u7684\u6d3b\uff1a\u201c\u4e0d\u53ef\u4ee5\u7684\uff0c\u8981\u662f\u4eba\u4eba\u90fd\u9009\u53f8\u673a\uff0c\u90a3\u4e48\uff0c\u6211\u4eec\u8fd8\u6392\u961f\u6765\u5e72\u4ec0\u4e48\uff1f\u8981\u662f\u9a6c\u6765\u6e38\u5ba2\u53ea\u9009\u9a6c\u6765\u540c\u80de\u4f5c\u53f8\u673a\uff0c\u4f60\u4eec\u53c8\u4f1a\u600e\u4e48\u60f3\uff1f\u201d \u3000\u3000\u5c82\u6599\u4ed6\u7684\u6d3b\u8fd8\u672a\u8bf4\u5b8c\uff0c\u6709\u4e2a\u534e\u4eba\u53f8\u673a\u5c31\u56b7\u9053\uff1a\u201c\u4e0a\u6b21\u4f60\u4e0d\u662f\u5c31\u62a2\u4e86\u6211\u7684\u5ba2\u5417\uff1f\u201d\u8bf4\u7740\u63a8\u4e86\u4ed6\u4e00\u628a\uff0c\u90a3\u9a6c\u6765\u4eba\u4e00\u4e2a\u4e0d\u7559\u795e\uff0c\u5c31\u649e\u5230\u53e6\u4e00\u540d\u540c\u4f34\u8eab\u4e0a\u3002 \u3000\u3000\u90a3\u540c\u4f34\u5927\u6982\u4e5f\u662f\u706b\u7206\u813e\u6c14\uff0c\u7d20\u6027\u4e0d\u8bb2\u7406\uff0c\u626f\u4f4f\u6e38\u5ba2\u4e2d\u7684\u4e00\u540d\u5c31\u8dd1\u3002 \u3000\u3000\u4ed6\u7684\u7528\u610f\u662f\u8981\u628a\u8fd9\u5ba2\u4eba\u5f80\u81ea\u5df1\u7684\u8f66\u4e0a\u62c9\uff0c\u8fde\u9053\u7406\u90fd\u7701\u4e0b\u4e86\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u62c9\u7684\u662f\u725b\u4e3d\u751f\u3002 \u3000\u3000\u725b\u4e3d\u751f\u4e0d\u8bb2\u82f1\u8bed\u3002 \u3000\u3000\u4ed6\u66f4\u4e0d\u61c2\u9a6c\u6765\u8bed\u3002 \u3000\u3000\u90a3\u9a6c\u6765\u4eba\u8fd9\u4e00\u62c9\u626f\uff0c\u4ed6\u66f4\u4e0d\u77e5\u662f\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\uff0c\u4ee5\u4e3a\u662f\u5411\u4ed6\u52a8\u624b\uff0c\u6240\u4ee5\u4e00\u632f\u81c2\uff0c\u65bd\u51fa\u4e86\u64d2\u62ff\u624b\uff0c\u628a\u90a3\u4eba\u7684\u624b\u81c2\u7ede\u4f4f\u4e86\u3002 \u3000\u3000\u90a3\u9a6c\u6765\u4eba\u54ea\u662f\u725b\u4e3d\u751f\u7684\u5bf9\u4e8e\uff0c\u767b\u65f6\u75db\u5f97\u568e\u53eb\u8d77\u6765\u3002 \u3000\u3000\u8fd9\u4e00\u6765\uff0c\u6c14\u6c1b\u7acb\u5373\u7d27\u5f20\u4e86\u8d77\u6765\u3002 \u3000\u3000\u90a3\u7fa4\u9a6c\u6765\u4eba\u7acb\u5373\u6012\u76ee\u76f8\u5411\uff0c\u51c6\u5907\u52a8\u624b\uff0c\u534e\u4eba\u53f8\u673a\u4e5f\u5927\u4e3a\u8bb6\u5f02\uff0c\u65a5\u53f1\uff1a\u201c\u4f60\u600e\u4e48\u6253\u4eba\uff01\u201d \u3000\u3000\u4ed6\u4eec\u611f\u5230\u6709\u201c\u5916\u4eba\u201d\u201c\u6b3a\u4fae\u201d\u540c\u884c\uff0c\u4fbf\u751f\u4e86\u56e2\u7ed3\u4e4b\u5fc3\uff0c\u4e00\u81f4\u5bf9\u5916\u3002 \u3000\u3000\u53f2\u6d41\u82b3\u60f3\u559d\u6b62\u725b\u4e3d\u751f\uff0c\u4e5f\u5df2\u6765\u4e0d\u53ca\u4e86\u3002 \u3000\u3000\u4e8b\u5230\u4e34\u5934\uff0c\u53ea\u6709\u786c\u63a5\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u9648\u5251\u8c01\u6765\u4e86\u3002 \u3000\u3000\u9648\u5251\u8c01\u8981\u662f\u4e0d\u53ca\u65f6\u8d76\u5230\uff0c\u5c40\u9762\u4f1a\u95f9\u5f97\u600e\u4e48\u51f6\uff0c\u8fd8\u771f\u662f\u4e0d\u53ef\u60f3\u50cf\u3002 \u3000\u3000\u9648\u5251\u8c01\u4e00\u6765\uff0c\u9996\u5148\u505a\u4e86\u4e00\u4ef6\u4e8b\u3002 \u3000\u3000\u4ed6\u8ddf\u5927\u5bb6\u9053\u6b49\u3002 \u3000\u3000\u4ed6\u8868\u660e\u56e0\u4e3a\u53f2\u6d41\u82b3\u521d\u6765\u6b64\u5730\uff0c\u624d\u8bef\u6253\u8bef\u649e\uff0c\u574f\u4e86\u89c4\u77e9\uff0c\u540e\u53c8\u6307\u51fa\u725b\u4e3d\u751f\u4e0d\u61c2\u82f1\u8bed\uff0c\u8bef\u4f1a\u6709\u4eba\u88ad\u51fb\u4ed6\uff0c\u624d\u6709\u8bef\u89e3\u3002 \u3000\u3000\u4ed6\u8fd8\u5411\u90a3\u81c2\u8098\u88ab\u626d\u7684\u9a6c\u672a\u4eba\u8d54\u507f\u4e86\u4e00\u4e9b\u201c\u5fc3\u610f\u201d\u3002 \u3000\u3000\u90a3\u5e72\u53f8\u673a\u7ed9\u60f9\u8d77\u4e86\u706b\u5934\uff0c\u81ea\u7136\u4e5f\u4e0d\u662f\u4e09\u8a00\u4e24\u8bed\u5c31\u53ef\u4ee5\u5e73\u606f\u4e0b\u53bb\u7684\u3002\u4f46\u9648\u5251\u8c01\u8bf4\u8bdd\u5f97\u4f53\u3001\u6001\u5ea6\u8bda\u631a\uff0c\u4e14\u9a6c\u6765\u8bed\u3001\u534e\u8bed\u3001\uff01\u201d\u4e1c\u8bdd\u3001\u82f1\u8bed\u5939\u6742\u5e76\u7528\uff0c\u5728\u573a\u7684\u4eba\u90fd\u542c\u5f97\u660e\u767d\uff0c\u52a0\u4e0a\u9648\u5251\u8c01\u201c\u660e\u767d\u4e8b\u7406\u201d\u5730\u201c\u585e\u201d\u4e86\u4e00\u4e9b\u94b1\u8fc7\u53bb\uff0c\u800c\u8fd9\u4ef6\u4e8b\u5486\u56b7\u4e86\u597d\u4e00\u9635\u5b50\uff0c\u6b63\u6709\u5de1\u8b66\u6ce8\u610f\u5230\u8fd9\u8fb9\u6765\u4e86\uff0c\u4e8e\u662f\u5927\u5bb6\u4fbf\u4e2a\u751a\u4e86\u4e86\u3002 \u3000\u3000\u4e4b\u540e\uff0c\u5404\u4eba\u53cd\u5e94\u4e0d\u4e00\u6837\u3002 \u3000\u3000\u725b\u4e3d\u751f\u54d7\u7136\u3002 \u3000\u3000\u201c\u54d7\uff0c\u539f\u6765\u4f60\u8fd8\u4f1a\u9a6c\u6765\u8bdd\u7684\uff01\u201d \u3000\u3000\u9648\u5251\u8c01\u53ea\u4e00\u7b11\uff0c\u4e0d\u4f5c\u6b63\u9762\u7b54\u590d\u3002 \u3000\u3000\u53f2\u6d41\u82b3\u5219\u4ecd\u5fc3\u6709\u4e0d\u7518\u3002 \u3000\u3000\u201c\u6211\u4eec\u53c8\u6ca1\u505a\u9519\uff0c\u6015\u4ed6\u4eec\u5e72\u5417\uff1f\u201d \u3000\u3000\u8fd9\u56de\u9648\u5251\u8c01\u5c31\u4e88\u4ee5\u4e25\u65a5\u4e86\u3002 \u3000\u3000\u201c\u4e0d\uff0c\u662f\u6211\u4eec\u505a\u9519\u4e86\u3002\u8981\u53eb\u8f66\uff0c\u4f7f\u5f97\u5230\u673a\u573a\u552e\u7968\u5904\u53bb\u4e70\u7968\uff0c\u7136\u540e\u5bf9\u53f7\u53eb\u8f66\uff0c\u5982\u679c\u8981\u8bf7\u534e\u4eba\u53f8\u673a\uff0c\u4e5f\u8be5\u5bf9\u670d\u52a1\u5458\u8bf4\u660e\uff0c\u7531\u4ed6\u6765\u5b89\u6392\u2026\u2026\u6211\u4e00\u65f6\u5fd8\u4e86\u4ea4\u4ee3\u8fd9\u4e9b\u7ec6\u8282\uff0c\u6ca1\u60f3\u5230\u4f60\u4f1a\u771f\u7684\u53bb\u81ea\u884c\u53eb\u8f66\uff0c\u5dee\u70b9\u574f\u4e86\u4ed6\u4eec\u7684\u884c\u89c4\uff0c\u8fd8\u95f9\u4e86\u4e8b\u2026\u2026\u201d \u3000\u3000\u725b\u4e3d\u751f\u5230\u73b0\u5728\u8fd8\u4e0d\u5927\u6e05\u695a\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\u3002 \u3000\u3000\u201c\u90a3\u4e2a\u4eba\u2026\u2026\u90a3\u4e2a\u5370\u5ea6\u4eba\u2026\u2026\u201d\u4ed6\u4ecd\u5206\u4e0d\u6e05\u695a\u9a6c\u6765\u4eba\u548c\u5370\u5ea6\u4eba\u7684\u5dee\u5f02\uff0c\u201c\u4ed6\u4e3a\u4ec0\u4e48\u6253\u6211\uff1f\u201d \u3000\u3000\u9648\u5251\u8c01\u53ea\u597d\u8bf4\uff1a\u201c\u4ed6\u6ca1\u6709\u6253\u4f60\uff0c\u662f\u4f60\u6253\u4ed6\u3002\u6211\u4eec\u8981\u662f\u95f9\u4e86\u4e8b\uff0c\u7ed3\u679c\u8fd8\u662f\u8981\u673a\u573a\u8b66\u5bdf\u672a\u6392\u89e3\uff0c\u4e0d\u662f\u8ba9\u4eba\u7b11\u5927\u4e86\u53e3\uff1f\u521a\u624d\u54b1\u4eec\u8fd8\u53eb\u4eba\u4e0d\u5fc5\u62a4\u9001\u6211\u4eec\u79bb\u5f00\u7684\u5462\uff01\u201d \u3000\u3000\u9a86\u94c3\u603b\u662f\u80fd\u53ca\u65f6\u627e\u5230\u53ef\u6028\u7684\u4eba\uff1a\u201c\u4e00\u5929\u90fd\u662f\u90a3\u4e2a\u8521\u56db\u5e78\uff0c\u4ed6\u4e0d\u6765\u63a5\u6211\u4eec\uff0c\u624d\u4f1a\u641e\u5230\u8fd9\u6837\u5b50\u2026\u2026\u201d \u3000\u3000\u5979\u60bb\u60bb\u7136\u7684\u8bf4\uff1a\u201c\u8fd9\u4e2a\u7ed3\u62dc\u8001\u5f1f\uff0c\u6211\u672a\u89c1\u5230\u4ed6\uff0c\u5bf9\u4ed6\u7684\u5370\u8c61\u5df2\u7ecf\u574f\u900f\u4e86\u3002\u201d \u3000\u3000\u201d\u73b0\u5728\u6211\u4eec\u8be5\u600e\u4e48\u529e\uff1f\u201d\u53f2\u6d41\u82b3\u95ee\u5f97\u6709\u70b9\u6c55\u6c55\u7136\uff0c\u73b0\u5728\u4ed6\u4e5f\u53cd\u7701\u5230\u521a\u624d\u81ea\u5df1\u7684\u4e0d\u662f\u4e86\u3002 \u3000\u3000\u201c\u5148\u53bb\u4e70\u7968\u5427\uff0c\u201d\u9648\u5251\u8c01\u8bf4\uff0c\u201c\u8fd9\u6b21\u662f\u610f\u5916\uff0c\u4e0d\u8fc7\u6240\u6709\u7684\u610f\u5916\u5927\u90fd\u662f\u53e6\u4e00\u79cd\u5f62\u5f0f\u7684\u6559\u8bad\u3002\u8bb0\u4f4f\u8fd9\u6559\u8bad\u5c31\u597d\u3002\u201d \u3000\u3000\u201c\u4e0d\u8981\u4ee5\u81ea\u5df1\u7684\u4e60\u60ef\u6765\u8981\u6c42\u522b\u4eba\uff0c\u56e0\u4e3a\u81ea\u5df1\u7684\u4e60\u60ef\u53ef\u80fd\u5c31\u662f\u5bf9\u65b9\u7684\u4e0d\u4e60\u60ef\uff1b\u201d\u4ed6\u8bed\u91cd\u5fc3\u957f\u7684\u8bf4\uff0c\u201c\u540c\u6837\u7684\uff0c\u4ed6\u6240\u9002\u5e94\u7684\u65b9\u5f0f\u4e0d\u4e00\u5b9a\u4f60\u5c31\u80fd\u9002\u5e94\u3002\u201d \u3000\u3000\u8bf4\u5230\u8fd9\u53e5\u540e\u7684\u65f6\u5019\uff0c\u673a\u573a\u5374\u8fdb\u6765\u4e86\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u4e00\u4e2a\u7709\u6e05\u76ee\u79c0\u3001\u6e29\u6587\u723d\u6717\u7684\u4eba\u3002 \u3000\u3000\u2014\u2014\u8fd9\u4eba\u8ba9\u4f60\u4e00\u773c\u770b\u53bb\uff0c\u5c31\u77e5\u9053\u4ed6\u662f\u4e2a\u8010\u4e0d\u4f4f\u5bc2\u5bde\u4f46\u53c8\u5411\u5f80\u5bc2\u5bde\u5883\u754c\u7684\u90a3\u7c7b\u4eba\u3002 \u3000\u3000\uff13\u3001\u4e00\u6b21\u7cca\u6d82\u4e00\u81ea\u5728 \u3000\u3000\u8fd9\u4eba\u65bd\u65bd\u7136\u8d70\u4e86\u8fdb\u6765\uff0c\u6e38\u76ee\u56db\u987e\uff0c\u7ec8\u4e8e\u53d1\u73b0\u4e86\u8fd9\u56db\u540d\u4efd\u5916\u663e\u773c\u7684\u4eba\u3002 \u3000\u3000\u4ed6\u5174\u9ad8\u91c7\u70c8\u7684\u4e09\u6b65\u5e76\u4f5c\u4e24\u6b65\uff0c\u8dd1\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u4ed6\u70ed\u70c8\u5f97\u50cf\u4e00\u4e2a\u5c0f\u5b69\u5b50\u4e00\u822c\u3002 \u3000\u3000\u4ed6\u95ee\uff1a\u201c\u2026\u2026\u4f60\u4eec\u5c31\u662f\u2026\u2026\u201d\u7136\u540e\u795e\u795e\u79d8\u79d8\u7684\u538b\u4f4e\u4e86\u8bed\u97f3\uff1a\u201c\u516d\u4eba\u5e2e\u2019\uff1f\u201d \u3000\u3000\u9a86\u94c3\u201c\u563f\u201d\u4e86\u4e00\u58f0\uff1a\u201c\u4f60\u73b0\u5728\u624d\u6765\u5440\uff01\u201d \u3000\u3000\u90a3\u4fca\u79c0\u7684\u4eba\u8bf4\uff1a\u201c\u521a\u6765\u3002\u6211\u8fd8\u5728\u8def\u8fb9\u644a\u5403\u4e86\u987f\u6d88\u591c\uff0c\u90a3\u8def\u8fb9\u7684\u4e1d\u67d1\uff0c\u53c8\u53c8\u9c9c\u53c8\u591a\u8840\uff0c\u54d7\u55f2\uff0c\u597d\u5473\u9053\uff0c\u9876\u522e\u522e\u3002\u201d \u3000\u3000\u9a86\u94c3\u4e00\u542c\u66f4\u6c14\uff0c\u5979\u4e5f\u997f\u4e86\u3002\u521a\u624d\u5728\u673a\u4e0a\u7684\u98df\u7269\u96be\u4ee5\u4e0b\u54bd\uff0c\u518d\u52a0\u4e0a\u4e00\u8f6e\u6076\u6597\uff0c\u540e\u6765\u7684\u4e0b\u5348\u8336\u4e5f\u5c31\u6b20\u5949\u4e86\uff0c\u5230\u4e86\u673a\u573a\uff0c\u8fde\u756a\u201c\u76d8\u95ee\u201d\uff0c\u9664\u4e86\u4e24\u4ef6\u897f\u591a\u58eb\u548c\u4e00\u676f\u5496\u5561\uff0c\u4e5f\u771f\u6ca1\u4ec0\u4e48\u4e1c\u897f\u80fd\u4e0b\u809a\uff0c\u90a3\u5bb6\u4f19\u8fd9\u4e48\u4e00\u63d0\uff0c\u9a86\u94c3\u7684\u80c3\u8fd8\u771f\u7684\u6e38\u884c\u6297\u8bae\u8d77\u6765\u4e86\u3002 \u3000\u3000\u5979\u53c9\u7740\u8170\u3001\u772f\u7740\u773c\u3001\u76b1\u7740\u9f3b\u5b50\uff0c\u8bf4\uff1a\u201c\u4f60\u53ef\u6765\u4e86\uff1f\u201d \u3000\u3000\u90a3\u4eba\u5ffd\u89c1\u90a3\u4e48\u4fcf\u4e3d\u800c\u53c8\u65f6\u9ae6\u7684\u4e00\u4e2a\u5973\u5b50\uff0c\u4e5f\u770b\u5f97\u772f\u7740\u773c\uff0c\u62b1\u7740\u8098\uff0c\u76b1\u7740\u9f3b\u5b50\u3002 \u3000\u3000\u9a86\u94c3\u4e0d\u6000\u597d\u610f\uff1a\u201c\u4f60\u77e5\u9053\u4f60\u8fdf\u4e86\u591a\u4e45\uff1f\u201d \u3000\u3000\u201c\u4e00\u4e2a\u5c0f\u65f6\u53c8\u56db\u5341\u4e94\u5206\u3002\u201d\u90a3\u4eba\u5012\u633a\u8001\u5b9e\u3002 \u3000\u3000\u201c\u4f60\u8fdf\u5230\u8fd8\u5148\u53bb\u5403\u6d88\u591c\uff1f\u201d \u3000\u3000\u201c\u5176\u5b9e\u90a3\u662f\u6211\u7684\u5348\u9910\u3002\u201d\u90a3\u4eba\u5012\u4e00\u70b9\u5185\u759a\u7684\u610f\u601d\u4e5f\u6ca1\u6709\uff0c\u201c\u901a\u5e38\u6211\u662f\u4e0b\u5348\u624d\u8d77\u5e8a\uff0c\u628a\u665a\u9910\u5f53\u6210\u65e9\u9910\u3002\u201d \u3000\u3000\u201c\u4f60\u4e0d\u77e5\u9053\u4eca\u5929\u6211\u4eec\u4f1a\u6765\u5417\uff1f\u201d \u3000\u3000\u201c\u77e5\u9053\u3002\u201d\u90a3\u4eba\u5f88\u723d\u5feb\u3002 \u3000\u3000\u201c\u4f60\u4e0d\u77e5\u9053\u6211\u4eec\u662f\u6253\u8001\u8fdc\u6765\u7684\u5417\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u77e5\u9053\u3002\u201d\u90a3\u4eba\u4e0d\u4f46\u8001\u5b9e\uff0c\u7b80\u76f4\u5929\u771f\uff0c\u5982\u679c\u8fd9\u4e5f\u7b97\u662f\u5929\u771f\u7684\u8bdd\uff0c\u5df2\u201c\u5929\u771f\u201d\u5230\u4e86\u5e7c\u7a1a\u7684\u5730\u6b65\uff1b\u4e00\u4e2a\u4eba\u5982\u679c\u5929\u771f\u5f97\u53ea\u4f1a\u5229\u5df1\u4f24\u4eba\uff0c\u90a3\u5df2\u662f\u53ef\u6076\uff0c\u4e0d\u662f\u5929\u771f\u4e86\u3002 \u3000\u3000\u201c\u90a3\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u7b49\u6211\u4eec\u4e00\u9f50\u6765\u5403\u6d88\u591c\uff1f\u4f60\u6ca1\u6253\u7b97\u7ed9\u6211\u4eec\u63a5\u98ce\u5417\uff1f\u96be\u9053\u8fd9\u4e5f\u662f\u4f60\u4eec\u8fd9\u513f\u7684\u98ce\u4fd7\u6c11\u60c5\uff1f\u4f60\u8fd9\u7b97\u4ec0\u4e48\u610f\u601d\uff1f\u201d\u9a86\u94c3\u53d1\u4f5c\u4e86\uff1a\u201c\u4f60\u6574\u6574\u8fdf\u5230\u4e86\u4e00\u4e2a\u5c0f\u65f6\u53c8\u56db\u5341\u4e94\u5206\u949f\uff0c\u8fd9\u7b97\u4ec0\u4e48\u5f85\u5ba2\u4e4b\u9053\uff01\u201d \u3000\u3000\u201c\u54ce\u5440\uff01\u201d\u90a3\u4eba\u53eb\u4e86\u8d77\u6765\uff0c\u201c\u6211\u600e\u4e48\u6ca1\u60f3\u5230\uff01\u4f60\u4eec\u8fd8\u672a\u7528\u9910\u5440\uff1f\u8fd9\u98de\u673a\u600e\u4e48\u641e\u7684\uff01\u4f60\u4eec\u90a3\u73ed\u673a\u7684\u670d\u52a1\u6001\u5ea6\u7740\u5b9e\u662f\u5927\u5dee\u52b2\u4e86\uff01\u201d \u3000\u3000\u9a86\u94c3\u4e00\u6115\u3002 \u3000\u3000\u90a3\u4eba\u53c8\u95ee\uff0c\u201c\u56db\u5e78\u5462\uff1f\u4ed6\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u9a86\u94c3\u8f6c\u9996\u770b\u9648\u5251\u8c01\u3002 \u3000\u3000\u9648\u5251\u8c01\u795e\u8272\u4e0d\u53d8\uff0c\u53ea\u6447\u4e86\u6447\u5934\uff0c\u8bf4\u4e86\u4e00\u65ec\uff1a\u201c\u6211\u4e5f\u4e0d\u77e5\u9053\u4ed6\u662f\u8c01\u3002\u201d \u3000\u3000\u90a3\u4eba\u51e0\u4e4e\u53c8\u53eb\u4e86\u8d77\u6765\uff1a\u201c\u4f60\u4eec\u4e0d\u662f\u8981\u544a\u8bc9\u6211\uff1a\u5c0f\u8521\u8fd8\u6ca1\u6765\u5427\uff1f\u201d \u3000\u3000\u9a86\u94c3\u4e5f\u50bb\u4e86\u773c\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u201d \u3000\u3000\u90a3\u4eba\u4e00\u8dfa\u811a\uff1a\u201c\u560e\uff01\u4ed6\u771f\u7684\u6ca1\u6765\uff01\u201d \u3000\u3000\u53f2\u6d41\u82b3\u95ee\uff0c\u201c\u4f60\u5230\u5e95\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u6211\uff1f\u201d\u90a3\u4eba\u6307\u4e86\u6307\u81ea\u5df1\u7684\u9f3b\u5b50\uff0c\u51fa\u73b0\u4e86\u4e00\u79cd\u5f88\u8471\u76f4\u53ef\u7231\u7684\u8868\u60c5\uff0c\u70ed\u70c8\u5730\u7b11\u8bf4\uff1a\u201c\u5bf9\uff0c\u6211\u8fd8\u6ca1\u81ea\u6211\u4ecb\u7ecd\uff1a\u6211\u59d3\u6e29\uff0c\u540d\u6587\uff0c\u6211\u662f\u4e2a\u5f88\u6709\u540d\u7684\u8bd7\u4eba\uff0c\u63a8\u5e7f\u672c\u5730\u534e\u4eba\u6587\u5316\u827a\u672f\uff0c\u4e0d\u9057\u4f59\u529b\uff0c\u4f60\u4eec\u5728\u8fd9\u91cc\u591a\u4f4f\u51e0\u5929\u4fbf\u4f1a\u542c\u5230\u6211\u7684\u82f1\u96c4\u4e8b\u8ff9\uff0c\u8fd8\u6709\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u4e00\u5411\u4e50\u5584\u597d\u65bd\u3001\u5bcc\u5192\u9669\u7cbe\u795e\u3001\u53cd\u5e94\u673a\u654f\u3001\u8fd8\u662f\u4e2a\u798f\u5c06\u2026\u2026\u201d\u4ed6\u50cf\u544a\u8bc9\u4e86\u522b\u4eba\u4e00\u4e2a\u5927\u79d8\u5bc6\uff0c\u800c\u80af\u5b9a\u5bf9\u65b9\u542c\u4e86\u4e5f\u4f1a\u6709\u4e2a\u6781\u5927\u7684\u60ca\u559c\u4ed6\u8bf4\uff0c\u201c\u6211\u5f88\u559c\u6b22\u80fd\u52a0\u5165\u4f60\u4eec\uff0c\u4f60\u4eec\u7684\u4e8b\u8ff9\u6211\u542c\u8fc7\u4e0d\u5c11\uff0c\u4f46\u5982\u679c\u6709\u6211\u5728\uff0c\u4e00\u5b9a\u4f1a\u66f4\u7cbe\u5f69\u4e30\u5bcc\u7684\u3002\u201d \u3000\u3000\u7136\u540e\u4ed6\u624d\u8865\u5145\u4e86\u4e00\u53e5\uff1a\u201c\u6211\u662f\u8521\u56db\u5e78\u7684\u670b\u53cb\uff0c\u597d\u670b\u53cb\u3002\u201d \u3000\u3000\u773c\u524d\u8fd9\u4e2a\u6e29\u6587\uff0c\u8bf4\u4e86\u4e00\u5927\u756a\u8bdd\uff0c\u5230\u6700\u540e\u4e00\u53e5\u8bdd\u624d\u7b97\u662f\u4e2a\u56de\u7b54\u3002 \u3000\u3000\u53f2\u6d41\u82b3\u6ca1\u597d\u6c14\u7684\u6a2a\u4e86\u4ed6\u4e00\u773c\uff1a\u201c\u4f60\u5012\u5f88\u4f1a\u63a8\u5e7f\u3002\u201d \u3000\u3000\u6e29\u6587\u6ca1\u610f\u4f1a\u8fc7\u6765\uff1a\u201c\u63a8\u5e7f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u53f2\u6d41\u82b3\u6545\u610f\u6df1\u6df1\u6c89\u6c89\u7684\u8bf4\uff1a\u201c\u63a8\u5e7f\u4f60\u81ea\u5df1\u554a\uff01\u201d \u3000\u3000\u9a86\u94c3\u5f88\u4e0d\u540c\u610f\uff1a\u201c\u8fd9\u8fd8\u53eb\u63a8\u5e7f\uff1f\u8fd9\u662f\u63a8\u9500\u561b\uff01\u201d \u3000\u3000\u6e29\u6587\u4e5f\u597d\u50cf\u5728\u8ba8\u8bba\u4e00\u4e2a\u8ddf\u4ed6\u65e0\u5173\u7684\u4eba\u7269\u4f3c\u7684\uff0c\u6301\u5e73\u7684\u8bf4\uff1a\u201c\u8fd9\u4e5f\u4e0d\u771f\u63a8\u9500\uff0c\u4e00\u4e2a\u4eba\u6b63\u5982\u4e00\u4ef6\u8d27\u54c1\uff0c\u6709\u597d\u5904\u5374\u6ca1\u4eba\u4e70\uff0c\u53c8\u6709\u4f55\u7528\uff1f\u597d\u5904\u5f53\u7136\u8981\u8ba9\u4eba\u77e5\u9053\uff0c\u4e5f\u4e0d\u59a8\u6253\u6253\u5e7f\u544a\u2014\u2014\u5176\u5b9e\u662f\u81ea\u6211\u4ecb\u7ecd\uff0c\u4e0d\u7b97\u662f\u81ea\u884c\u63a8\u9500\uff1a\u6211\u53c8\u6ca1\u6253\u7b97\u3002\u5356\u8eab\u2019\u7ed9\u4f60\u4eec\u3002\u201d \u3000\u3000\u53f2\u6d41\u82b3\u5fcd\u4e0d\u4f4f\u8f7b\u53eb\u4e86\u4e00\u58f0\uff1a\u201c\u5929\uff01\u201d \u3000\u3000\u6e29\u6587\u5373\u884c\u4f1a\u610f\uff0c\u772f\u4e86\u772f\u773c\u775b\uff1a\u201c\u5730\uff01\u201d \u3000\u3000\u53f2\u6d41\u82b3\u5012\u76f4\u4e86\u773c\uff1a\u201c\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u4f60\u4eec\u90fd\u662f\u6709\u672c\u9886\u7684\u4eba\uff0c\u521a\u624d\u4e00\u5b9a\u662f\u5728\u558a\u4e00\u4e2a\u6697\u53f7\u3002\u201d\u6e29\u6587\u7684\u6837\u5b50\uff0c\u7740\u53bb\u806a\u660e\u5f97\u4f3c\u5934\u4e0a\u6709\u4e09\u675f\u5149\u5708\uff1a\u201c\u6240\u4ee5\u4f60\u53eb\u5929\uff0c\u6211\u5373\u558a\u5730\uff0c\u4e0d\u77e5\u7b54\u5bf9\u4e86\u6ca1\u6709\uff1f\u6211\u770b\u516b\u4e5d\u4e0d\u79bb\u5341\u4e86\u5427\uff1f\u201d \u3000\u3000\u8fd9\u6b21\u53f2\u6d41\u82b3\u5fcd\u4e0d\u4f4f\u547b\u541f\u4e86\u4e00\u58f0\uff1a\u201c\u6211\u7684\u5988\u5440\uff01\u201d \u3000\u3000\u201c\u771f\u602a\uff0c\u201d\u6e29\u6587\u5583\u5583\u81ea\u8bed\u5730\u9053\uff1a\u201c\u96be\u9053\u8fd9\u56de\u8981\u5bf9\uff1a\u2018\u4f60\u7684\u7238\u5440\u2019\u4e0d\u6210\uff1f\u201d \u3000\u3000\u9648\u5251\u8c01\u8bf4\u8bdd\u4e86\uff1a\u201c\u6e29\u5148\u751f\uff1f\u201d \u3000\u3000\u201c\u5728\uff01\u201d\u6e29\u6587\u673a\u8b66\u7684\u8bf4\uff1a\u201c\u6211\u77e5\u9053\uff0c\u6211\u731c\u4f60\u5c31\u662f\u8001\u5927\u9648\u5251\u8c01\u3002\u5426\u5219\uff0c\u6709\u8c01\u80fd\u4e2a\u5b50\u8fd9\u822c\u4e0d\u9ad8\uff0c\u4f46\u7ad9\u5f97\u6e0a\u505c\u5cb3\u5cd9\uff0c\u597d\u4e00\u4ed8\u6c14\u6d3e\uff01\u201d \u3000\u3000\u9a86\u94c3\u563f\u58f0\u9053\uff1a\u201c\u53e3\u751c\u820c\u6ed1\uff0c\u6ca1\u8bda\u610f\uff01\u201d \u3000\u3000\u201c\u8c22\u8c22\u3002\u201d\u9648\u5251\u8c01\u7684\u773c\u775b\u5b8c\u5168\u4e0d\u81ea\u4ed6\u8138\u4e0a\u79fb\u5f00\uff0c\u201c\u662f\u8521\u56db\u5e78\u53eb\u4f60\u6765\u7684\uff1f\u201d \u3000\u3000\u201c\u662f\u3002\u201d \u3000\u3000\u201c\u4ed6\u6ca1\u548c\u4f60\u4e00\u8d77\u6765\uff1f\u201d \u3000\u3000\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4f4f\u5728\u9739\u96f3\u5dde\uff0c\u6211\u4f4f\u5728\u9996\u90fd\uff0c\u672c\u6765\u5c31\u76f8\u9694\u8fd1\u767e\u82f1\u91cc\uff0c\u4ed6\u6709\u672c\u5b50\uff0c\u6211\u4e5f\u6709\u8f66\u5b50\uff0c\u81ea\u7136\u662f\u4ed6\u6765\u4ed6\u7684\uff0c\u6211\u6765\u6211\u7684\u3002\u201d \u3000\u3000\u201c\u662f\u4ed6\u544a\u8bc9\u4f60\u6211\u4eec\u6765\u4e86\uff1f\u201d \u3000\u3000\u201c\u4ed6\u5e38\u5e38\u8ddf\u6211\u63d0\u8d77\u4f60\u4eec\u7684\u4e8b\uff0c\u5e76\u5f15\u4ee5\u4e3a\u8363\u3002\u201d \u3000\u3000\u201c\u4ed6\u4ec0\u4e48\u65f6\u5019\u901a\u77e5\u4f60\u6211\u4eec\u6765\u7684\u4e8b\uff1f\u201d \u3000\u3000\u201c\u4e09\u5929\u524d\u3002\u201d \u3000\u3000\u201c\u4ed6\u8bf4\u4ed6\u4e5f\u4f1a\u6765\u63a5\u673a\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u5f53\u7136\u3002\u4ed6\u8fd8\u8bf4\u8981\u6211\u8fdf\u4e00\u4e2a\u534a\u5c0f\u65f6\u624d\u5230\uff0c\u6211\u731c\u4ed6\u662f\u8981\u5148\u8ddf\u4f60\u4eec\u8c08\u8c08\uff0c\u6211\u592e\u4ed6\u4ecb\u7ecd\u4f60\u4eec\u7ed9\u6211\u8ba4\u8bc6\uff0c\u5f53\u7136\u8981\u901a\u6c14\u77e5\u8da3\u2026\u2026\u201d\u8bf4\u5230\u8fd9\u91cc\uff0c\u6e29\u6587\u624d\u60f3\u8d77\u4ec0\u4e48\u4f3c\u7684\u8ddf\u9a86\u94c3\u8bf4\uff1a\u201c\u9a86\u5c0f\u59d0\uff0c\u4f60\u4e0d\u8981\u592a\u751f\u6c14\uff0c\u5176\u5b9e\u7b97\u8d77\u6765\u6211\u4e5f\u53ea\u4e0d\u8fc7\u662f\u8fdf\u5230\u4e86\u5341\u4e94\u5206\u949f\u3002\u201d \u3000\u3000\u9a86\u94c3\u672c\u6765\u4e5f\u6709\u70b9\u4e0d\u597d\u610f\u601d\uff0c\u4f46\u53c8\u5f88\u6709\u70b9\u8be7\u5f02\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\u6211\u59d3\u9a86\uff1f\u201d \u3000\u3000\u201c\u5c0f\u8521\u544a\u8bc9\u6211\u8bf4\uff0c\u2018\u516d\u4eba\u5e2e\u2019\u91cc\u53ea\u6709\u4e00\u4e2a\u5927\u7684\uff0c\u800c\u4e14\u5f88\u51f6\u3001\u4e0d\u5927\u8bb2\u7406\u2026\u2026\u201d\u6e29\u6587\u5012\u662f\u4f46\u767d\uff0c\u201c\u4e0d\u662f\u4f60\u8fd8\u6709\u8c01\uff01\u4f55\u51b5\uff0c\u4ed6\u4e5f\u8bf4\u8fc7\u9a86\u5c0f\u59d0\u6bd4\u5973\u660e\u661f\u8fd8\u6f02\u4eae\uff0c\u5bf9\u5440\uff0c\u73b0\u5728\u7684\u6f14\u5458\u5168\u662f\u9694\u90bb\u73e0\u5973\u4e09\u697c\u7965\u4ed4\uff0c\u54ea\u6709\u8fd9\u6837\u6f02\u4eae\u7684\u5973\u660e\u661f\uff01\u201d \u3000\u3000\u9a86\u94c3\u5012\u662f\u53c8\u55d4\u53c9\u559c\uff0c\u53cd\u800c\u6709\u70b9\u7a98\u8d77\u6765\uff0c\u547b\u9053\uff1a\u201c\u89c1\u9b3c\u4e86\uff01\u8521\u56db\u5e78\u53c8\u6ca1\u89c1\u8fc7\u6211\uff0c\u600e\u77e5\u9053\u2026\u2026\u201d\u5ffd\u7136\u604d\u7136\uff0c\u4fbf\u6ca1\u8bf4\u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u2014\u2014\u5f53\u7136\u662f\u8001\u5927\u8bf4\u7684\u3002 \u3000\u3000\u2014\u2014\u5305\u62ec\u79f0\u8a89\u548c\u574f\u8bc4\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u901a\u5e38\u4e00\u4e2a\u5973\u5b69\u5b50\uff0c\u4f60\u53ea\u8981\u80af\u5b9a\u4e86\u5979\u7684\u7f8e\u8c8c\uff0c\u5c31\u7b97\u4e0d\u5927\u91cd\u89c6\u5979\u7684\u667a\u6167\uff0c\u5979\u4e5f\u4e0d\u4f1a\u592a\u4ecb\u6000\u7684\u3002 \u3000\u3000\u9a86\u94c3\u4e5f\u4e0d\u4f8b\u5916\u3002 \u3000\u3000\u201c\u4ed6\u540e\u6765\u6709\u6ca1\u6709\u8054\u7edc\u8fc7\u4f60\uff1f\u201d\u9648\u5251\u8c01\u5012\u662f\u4e00\u70b9\u4e5f\u4e0d\u653e\u677e\u3002 \u3000\u3000\u201c\u6ca1\u6709\u4e86\u3002\u201d \u3000\u3000\u201c\u6240\u4ee5\u4f60\u4ee5\u4e3a\u4ed6\u6765\u4e86\u3002\u201d \u3000\u3000\u201c\u539f\u6765\u4ed6\u771f\u7684\u662f\u8fd8\u6ca1\u6709\u6765\u3002\u201d \u3000\u3000\u201c\u8521\u56db\u5e78\u2026\u2026\u5e38\u5931\u7ea6\u5417\uff1f\u201d\u53f2\u6d41\u82b3\u5fcd\u4e0d\u4f4f\u95ee\u4e86\u8fd9\u6837\u4e00\u53e5\u201c\u4ed6\uff1f\u522b\u4eba\u8fdf\u5230\u4e00\u5206\u949f\u4ed6\u8fd8\u8981\u76b1\u7709\u5934\u5462\uff01\u4ed6\u8bf4\u8fc7\uff0c\u5c31\u7b97\u4ed6\u8ffd\u7684\u5973\u670b\u53cb\u8fdf\u5230\u534a\u5c0f\u65f6\u2014\u2014\u53ea\u8981\u662f\u6545\u610f\u548c\u5e38\u5e38\u8fdf\u5230\u7684\u8bdd\uff0c\u4ed6\u5b81\u613f\u8ffd\u4e0d\u5230\u4e5f\u4e0d\u613f\u7b49\u4e0b\u53bb\u3002\u201d \u3000\u3000\u201c\u90a3\u4e48\u2026\u2026\u770b\u6765\u4ed6\u662f\u6709\u70b9\u610f\u5916\u4e86\u3002\u201d\u9648\u5251\u8c01\u8bf4\uff0c\u4e0d\u77e5\u600e\u7684\uff0c\u4ed6\u4e5f\u611f\u89c9\u5230\u6709\u70b9\u5f00\u6717\u4e0d\u8d77\u6765\u3002 \u3000\u3000\u201c\u4e0d\u8981\u7d27\uff0c\u6709\u610f\u5916\u624d\u6709\u60ca\u559c\u5440\uff01\u201d\u6e29\u6587\u5012\u662f\u4e00\u70b9\u4e5f\u4e0d\u4ee5\u4e3a\u6012\uff1a\u201c\u6709\u6211\u5728\uff0c\u6211\u5e26\u4f60\u4eec\u79bb\u5f00\u673a\u573a\uff0c\u4f60\u4eec\u8981\u5230\u5e02\u533a\u5148\u53bb\u4f4f\u4e0a\u4e00\u5bbf\u4e5f\u53ef\u4ee5\uff0c\u6211\u73b0\u5728\u8f7d\u4f60\u4eec\u5357\u4e0b\u53bb\u627e\u5c0f\u8521\u4e5f\u65e0\u59a8\u2026\u2026\u603b\u4e4b\uff0c\u4efb\u541b\u9009\u62e9\uff0c\u5728\u4e0b\u53ef\u6548\u72ac\u9a6c\u4e4b\u52b3\u3002\u201d \u3000\u3000\u201c\u54d7\uff0c\u53c8\u4f1a\u90af\u4e25\u91cd\u3002\u201d\u9a86\u94c3\u7528\u5e7f\u4e1c\u8bdd\u8bf4\u4e86\u4e00\u53e5\u3002 \u3000\u3000\u201c\u4eba\u751f\u5728\u4e16\uff0c\u54c8\u5938\u5f20\u5450\u70b9\u4f1a\u597d\u73a9\u54d7\uff01\u201d\u6e29\u6587\u4e5f\u7528\u7eaf\u6b63\u7684\u5e7f\u4e1c\u8bdd\u56de\u4e86\u4e00\u53e5\u3002 \u3000\u3000\u5927\u5bb6\u90fd\u719f\u7edc\u4e86\u8d77\u6765\u3002 \u3000\u3000\u201c\u4e0d\u5982\u518d\u53bb\u6253\u4e2a\u7535\u8bdd\u7ed9\u8521\u56db\u5e78\u3002\u201d\u9648\u5251\u8c01\u5efa\u8bae\u3002 \u3000\u3000\u201c\u4f60\u4eec\u521a\u624d\u6ca1\u6253\u8fc7\u5417\uff1f\u201d \u3000\u3000\u201c\u6253\u8fc7\u4e86\uff0c\u201d\u9648\u5251\u8c01\u8bf4\uff0c\u201c\u4f46\u6ca1\u63a5\u901a\u3002\u201d \u3000\u3000\u201c\u4e00\u5b9a\u4f1a\u901a\u7684\u2026\u2026\u8bb8\u662f\u4f60\u4eec\u4e0d\u5927\u719f\u6089\u5427\uff01\u201d\u6e29\u6587\u5012\u4e00\u76f4\u90fd\u5174\u81f4\u52c3\u52c3\uff0c\u201c\u6211\u53bb\u6253\u6253\u770b\u3002\u201d \u3000\u3000\u672a\u5f97\u5927\u5bb6\u540c\u610f\uff0c\u5df2\u98de\u6b65\u5230\u7535\u8bdd\u4ead\u6253\u957f\u9014\u7535\u8bdd\u53bb\u4e86\u3002 \u3000\u3000\u9a86\u94c3\u7b11\u4ed6\uff1a\u201c\u771f\u662f\u4e2a\u602a\u4eba\u3002\u201d \u3000\u3000\u53f2\u6d41\u82b3\u4e5f\u8bf4\uff1a\u201c\u8fd9\u4e48\u7b28\uff0c\u8fd8\u60f3\u52a0\u5165\u6211\u4eec\uff0c\u771f\u4e8f\u5f97\u4ed6\u3002\u201d \u3000\u3000\u9648\u5251\u8c01\u7b11\u8bf4\uff1a\u201c\u4f60\u770b\u4eba\u5bb6\u7b28\uff0c\u4eba\u5bb6\u8fd8\u7b11\u4f60\u8822\u5462\uff01\u201d \u3000\u3000\u201c\u4ed6\uff1f\u201d\u53f2\u6d41\u82b3\u4e0d\u5c51\u5730\u8981\u8bf4\u4e0b\u53bb\u3002\uff0c\u4f46\u6e29\u6587\u5df2\u56de\u6765\u4e86\u3002 \u3000\u3000\u201c\u600e\u6837\uff1f\u201d \u3000\u3000\u201c\u6ca1\u4eba\u63a5\u7535\u8bdd\u3002\u201d\u6e29\u6587\u6709\u70b9\u6cc4\u6c14\u3002 \u3000\u3000\u201c\u2026\u2026\u662f\u4e0d\u662f\u6709\u70b9\u4e0d\u5bf9\u52b2\uff1f\u201d\u53f2\u6d41\u82b3\u8f6c\u5934\u95ee\u9648\u5251\u8c01\uff0c\u53d1\u73b0\u4ed6\u4eec\u7684\u5927\u54e5\u5f88\u6709\u70b9\u5fe7\u8651\u7684\u6837\u5b50\u3002\u4ed6\u4eec\u4e00\u5411\u90fd\u77e5\u9053\u9648\u5251\u8c01\u7684\u76f4\u89c9\u5f02\u5e38\u7075\u654f\u3002 \u3000\u3000\u201c\u73b0\u5728\u5df2\u7ecf\u51cc\u6668\u56db\u70b9\u949f\u4e86\uff0c\u5c0f\u8521\u7684\u5bb6\u4eba\u4e0d\u63a5\u7535\u8bdd\uff0c\u4e5f\u4e0d\u7a00\u5947\u5440\uff0c\u201d\u6e29\u6587\u8131\u53e3\u800c\u51fa\uff0c\u201c\u54ce\uff0c\u4f60\u53ef\u771f\u7b28\u554a\uff01\u201d \u3000\u3000\u53f2\u6d41\u82b3\u7ed9\u9a82\u5f97\u6014\u4e86\u4e00\u6014\u3002 \u3000\u3000\u9a86\u94c3\u201c\u5495\u201d\u7684\u4e00\u58f0\u7b11\u4e86\u51fa\u6765\u3002 \u3000\u3000\u201c\u5929\uff01\u89c1\u9b3c\u4e86\uff01\u201d\u53f2\u6d41\u82b3\u5495\u54dd\u7740\uff0c\u201c\u4eca\u5929\u8001\u662f\u8ddf\u9b3c\u9a82\u67b6\u3002\u201d \u3000\u3000\u201c\u4f60\u8bf4\u4ec0\u4e48\uff1f\uff0c\u6e29\u6587\u968f\u53e3\u95ee\uff0c\u4ed6\u7684\u5174\u81f4\u53c8\u6765\u4e86\uff1a\u201c\u6765\uff0c\u6211\u5148\u8f7d\u4f60\u4eec\u79bb\u5f00\u673a\u573a\u518d\u8bf4\u3002\u201d \u3000\u3000\u4ed6\u5174\u594b\u8d77\u6765\u7684\u65f6\u5019\uff0c\u50cf\u8db3\u7403\u5927\u8d5b\u7684\u90a3\u4e00\u7c92\u7403\uff0c\u6eda\u5230\u54ea\u91cc\u90fd\u5438\u4f4f\u4eba\u7684\u89c6\u7ebf\u548c\u5f15\u8d77\u54d7\u7136\u3002 \u3000\u3000\u4ed6\u628a\u8f66\u5b50\u5f00\u5230\u673a\u573a\u5927\u53a6\u95e8\u524d\uff0c\u8ba9\u9648\u5251\u8c01\u7b49\u4eba\u4e0a\u4e86\u8f66\uff0c\u4fbf\u79bb\u5f00\u4e86\u673a\u573a\u3002 \u3000\u3000\u53f2\u6d41\u82b3\u53d1\u73b0\u4ed6\u628a\u8f66\u5b50\u5f00\u5f97\u5f88\u6162\uff0c\u5927\u6982\u6bcf\u5c0f\u65f6\u4e0d\u8db3\u4e94\u5341\u91cc\uff0c\u800c\u53f2\u6d41\u82b3\u662f\u5f00\u60ef\u5feb\u8f66\u7684\u3002\u504f\u504f\u6e29\u6587\u5f00\u7684\u662f\u4fdd\u65f6\u6377\u3002 \u3000\u3000\u2014\u2014\u9a7e\u7740\u8fd9\u6837\u4e00\u90e8\u8dd1\u8f66\uff0c\u5728\u6df1\u591c\u7684\u9ad8\u901f\u516c\u8def\u4e0a\uff0c\u5c45\u7136\u53ea\u5f00\u65f6\u901f\u56db\u5341\u91cc\uff0c\u5c31\u50cf\u7a7f\u4e86\u9053\u888d\u5230\u4e86\u9053\u573a\u4e0a\u5374\u53ea\u51c6\u770b\u4e66\u4e0d\u8bb8\u7ec3\u6b66\u4e00\u822c\uff0c\u4f7f\u53f2\u6d41\u82b3\u81ea\u811a\u8dbe\u75d2\u5230\u4e86\u624b\u6307\u5934\u3002 \u3000\u3000\u201c\u4f60\u5f00\u5feb\u4e00\u4e9b\u597d\u4e0d\u597d\uff1f\u201d \u3000\u3000\u201c\u4e0d\u597d\u3002\u201d\u6e29\u6587\u6c14\u5b9a\u795e\u95f2\uff0c\u201c\u5c0f\u5fc3\u9a76\u5f97\u4e07\u5e74\u8f66\u3002\u201d \u3000\u3000\u540e\u9762\u4e00\u90e8\u8001\u7237\u8f66\u5c45\u7136\u8d85\u8d8a\u4e86\u4ed6\u4eec\u7684\u8f66\u5b50\uff0c\u8f66\u91cc\u7684\u4eba\u5c51\u7136\u8fd8\u53d1\u51fa\u5632\u5f04\u7684\u5c16\u5578\u3002 \u3000\u3000\u201c\u5feb\uff0c\u8d85\u8f66\uff01\u201d\u9a86\u94c3\u4e5f\u618b\u4e0d\u4f4f\u4e86\u3002 \u3000\u3000\u201c\u4e0d\uff0c\u201d\u6e29\u6587\u6162\u6761\u65af\u7406\u7684\u8bf4\uff0c\u201c\u4fdd\u6301\u8ddd\u79bb\uff0c\u4ee5\u7b56\u5b89\u5168\uff0c\u201c\u5168\u4f60\u4e2a\u5934\uff01\u201d\u9a86\u94c3\u5492\u9a82\uff0c\u201c\u6211\u6e34\u6b7b\u4e86\u3002\u201d \u3000\u3000\u6e29\u6587\u597d\u50cf\u6ca1\u542c\u89c1\u3002 \u3000\u3000\u9a86\u94c3\u89c1\u8fd9\u4e00\u5e26\u591a\u89c1\u6811\u6728\u5c11\u89c1\u4eba\uff0c\u6570\u5341\u7801\u624d\u6709\u4e00\u76cf\u6f84\u9ec4\u7684\u8def\u706f\uff0c\u5341\u5206\u51c4\u51c9\uff0c\u4e0d\u50cf\u9999\u6e2f\u7684\u4e0d\u591c\u5929\uff0c\u5230\u5904\u6b4c\u821e\u5347\u5e73\u3001\u901a\u5bb5\u8425\u4e1a\uff0c\u4e0d\u7981\u57cb\u6028\uff1a\u201c\u6765\u5230\u8fd9\u4e2a\u9b3c\u5730\u65b9\uff0c\u5509\uff01\u201d \u3000\u3000\u8fd9\u56de\u6e29\u6587\u53ef\u4e0d\u6c89\u9ed8\u4e86\uff1a\u201c\u4ec0\u4e48\u9b3c\u5730\u65b9\uff1f\u201d \u3000\u3000\u201c\u4e0d\u662f\u9b3c\u5730\u65b9\uff0c\u201d\u9a86\u94c3\u8bf4\uff0c\u201c\u8fd9\u513f\u8fde\u9b3c\u4e5f\u6ca1\u6709\u4e00\u53ea\u3002\u201d \u3000\u3000\u201c\u8c08\u8d77\u9b3c\uff0c\u8fd9\u513f\u6700\u8fd1\u5012\u662f\u5e38\u5e38\u95f9\u9b3c\u3002\u201d\u6e29\u6587\u8bf4\uff0c\u201c\u4e0d\u77e5\u9053\u4f60\u4eec\u6709\u6ca1\u6709\u542c\u8bf4\u8fc7\uff1f\u201d \u3000\u3000\u201c\u9b3c\uff1f\u201d\u9a86\u94c3\u548c\u53f2\u6d41\u82b3\u90fd\u6765\u4e86\u5174\u81f4\u3002 \u3000\u3000\u201c\u4f60\u4eec\u4fe1\u4e0d\u4fe1\uff1f\u201d \u3000\u3000\u201c\u4fe1\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u4f60\u4eec\u76f8\u4fe1\u8fd9\u4e16\u4e0a\u2026\u2026\u771f\u7684\u6709\u9b3c\u8fd9\u56de\u4e8b\u5417\uff1f\u201d\u6e29\u6587\u8bd5\u63a2\u7684\u95ee\u3002 \u3000\u3000\u201c\u9b3c\uff0c\u2026\u2026\u6709\u4eba\u8bf4\u662f\u5f88\u5e73\u5e38\u7684\u4e1c\u897f\uff0c\u6b63\u5982\u4eba\u5b58\u5728\u4e8e\u4e16\u95f4\u4e00\u822c\uff0c\u4e5f\u6709\u9b3c\u7684\u5b58\u5728\uff0c\u53ea\u4e0d\u8fc7\u4eba\u9b3c\u4e4b\u95f4\uff0c\u7f3a\u4e4f\u6c9f\u901a\u7684\u65b9\u5f0f\uff0c\u4e00\u822c\u6765\u8bf4\uff0c\u4f60\u8d70\u4f60\u7684\u9ec4\u6cc9\u9053\uff0c\u6211\u884c\u6211\u7684\u5948\u4f55\u6865\u5c31\u662f\u4e86\u3002\u201d\u53f2\u6d41\u82b3\u6258\u4e86\u4e00\u6258\u773c\u955c\uff0c\u8bf4\uff0c\u201c\u6211\u6709\u4e2a\u597d\u670b\u53cb\uff0c\u4ed6\u8ba4\u4e3a\u9b3c\u9b42\u662f\u4e00\u675f\u7535\u6ce2\uff0c\u4f46\u901a\u5e38\u4e00\u822c\u4eba\u8c03\u4e0d\u5230\u6536\u542c\u5b83\u7684\u9891\u5f8b\uff0c\u6240\u4ee5\u5c31\u89c1\u4e0d\u5230\u9b3c\uff0c\u53ef\u662f\u5728\u7279\u6b8a\u73af\u5883\u4e4b\u4e0b\uff0c\u4f8b\u5982\u559d\u4e86\u8fc7\u91cf\u9152\u3001\u5728\u4e00\u4e2a\u9634\u6c14\u7279\u522b\u91cd\u7684\u5730\u65b9\u3001\u6216\u7cbe\u795e\u5931\u5e38\u7684\u60c5\u5f62\u91cc\uff0c\u8111\u7535\u6ce2\u6709\u4e86\u4e0d\u540c\u7684\u9707\u8361\uff0c\u5c31\u53ef\u4ee5\u649e\u89c1\u4e86\u3002\u201d \u3000\u3000\u201c\u90a3\u662f\u522b\u4eba\u7684\u610f\u89c1\uff0c\u201d\u6e29\u6587\u8bf4\uff0c\u201c\u4f60\u81ea\u5df1\u7684\u5462\uff1f\u201d \u3000\u3000\u201c\u9b3c\u53ea\u4e0d\u8fc7\u662f\u7b2c\u4e8c\u79cd\u4eba\uff0c\u795e\u4e5f\u662f\u53e6\u4e00\u79cd\u4eba\u3002\u6ca1\u6709\u4eba\uff0c\u4fbf\u6ca1\u6709\u9b3c\uff0c\u4e5f\u6ca1\u6709\u795e\u4e86\u3002\u6362\u53e5\u8bdd\u8bf4\uff0c\u6211\u4eec\u5728\u8682\u8681\u7684\u773c\u91cc\uff0c\u4e5f\u662f\u795e\u3002\u5b83\u4eec\u4e00\u53ea\u53ea\u6392\u7740\u961f\u5f80\u524d\u884c\uff0c\u6211\u4eec\u5ffd\u7136\u62ce\u8d77\u5b83\u4eec\u5176\u4e2d\u4e00\u53ea\uff0c\u5b83\u4eec\u4e5f\u65ad\u4e0d\u4f1a\u77e5\u9053\u4e3a\u4ec0\u4e48\u5ffd\u7136\u6709\u4e00\u53ea\u540c\u4f34\u4f1a\u4e0d\u89c1\u4e86\uff0c\u5982\u679c\u5b83\u4eec\u8ddf\u4eba\u4e00\u6837\u4f1a\u601d\u60f3\uff0c\u4e5f\u4e00\u5b9a\u4ee5\u4e3a\u6709\u795e\u660e\u4f5c\u5d07\u3002\u800c\u4eba\u5c31\u662f\u795e\u3002\u6362\u53e5\u8bdd\u8bf4\uff0c\u6211\u4eec\u53ef\u80fd\u4e5f\u662f\u2018\u8682\u8681\u2019\uff0c\u6709\u8bb8\u591a\u4eba\u672a\u77e5\u7684\u529b\u91cf\uff0c\u6216\u8005\u7b2c\u56db\u5ea6\u3001\u7b2c\u4e94\u5ea6\u7a7a\u95f4\uff0c\u6211\u4eec\u4eba\u7c7b\u6839\u672c\u6ca1\u529e\u6cd5\u7a81\u7834\uff0c\u800c\u53e6\u4e00\u79cd\u4eba\uff0c\u53ef\u4ee5\u63a7\u5236\u65f6\u95f4\uff0c\u98de\u8dc3\u7a7a\u95f4\uff0c\u751a\u81f3\u53ef\u4ee5\u76f4\u63a5\u8fdb\u5165\u53e6\u4e00\u751f\u547d\uff0c\u8111\u6ce2\u3001\u611f\u60c5\u91cc\uff0c\u90a3\u5b83\u5c31\u5177\u5907\u4e86\u2018\u6cd5\u529b\u2019\uff0c\u662f\u6211\u4eec\u7684\u2018\u9b3c\u2019\u6216\u8005\u2018\u795e\u2019\u4e86\u3002\u53f2\u6d41\u82b3\u8d8a\u8bf4\u8d8a\u8d77\u52b2\uff0c\u201c\u6240\u4ee5\u6ca1\u6709\u4eba\u3002\u5c31\u6ca1\u6709\u9b3c\u548c\u795e\u3002\u4eba\u7c7b\u6700\u5b9d\u8d35\u7684\u5c31\u662f\u7ecf\u9a8c\u548c\u77e5\u8bc6\uff0c\u6240\u4ee5\u521d\u6c11\u7684\u795e\u8bdd\u6700\u4e3a\u4e30\u5bcc\uff0c\u5c71\u6709\u795e\uff0c\u6d77\u6709\u795e\uff0c\u65e5\u6708\u6c34\u706b\u65e0\u4e0d\u6709\u795e\u3002\u4eba\u5bf9\u65e0\u77e5\u6216\u672a\u77e5\u7684\uff0c\u4fbf\u89e3\u91ca\u4e3a\u795e\u79d8\u7684\u529b\u91cf\uff0c\u8fd9\u529b\u91cf\u901a\u5e38\u90fd\u4ee5\u9b3c\u795e\u79f0\u4e4b\u3002\u8981\u662f\u6211\u4eec\u5230\u73b0\u5728\u8fd8\u4e0d\u77e5\u706b\u5c71\u7206\u53d1\u7684\u539f\u56e0\uff0c\u5f53\u7136\u4f1a\u4ee5\u4e3a\u662f\u5c71\u795e\u5728\u53d1\u6012\uff1b\u8981\u662f\u4eba\u7c7b\u81f3\u4eca\u8fd8\u672a\u4e86\u89e3\u65e5\u8680\u7684\u539f\u56e0\uff0c\u4e5f\u4f1a\u89e3\u91ca\u4e3a\u5929\u72d7\u98df\u6708\u2026\u2026\u201d \u3000\u3000\u201c\u7f57\u54e9\u516b\u55e6\uff01\u201d\u9a86\u94c3\u6ca1\u5fc3\u60c5\u542c\u53f2\u6d41\u82b3\u7684\u5b8f\u8bba\uff0c\u201c\u4f60\u8bf4\u8fd9\u513f\u6700\u8fd1\u95f9\u9b3c\uff1f\u201d \u3000\u3000\u201c\u662f\u3002\u201d \u3000\u3000\u201c\u95f9\u4ec0\u4e48\u9b3c\uff1f\u201d \u3000\u3000\u201c\u6700\u8fd1\u5f88\u591a\u4eba\u90fd\u89c1\u8fc7\u4e00\u4e2a\u9ed1\u591c\u624d\u51fa\u73b0\u7684\u767d\u8272\u5973\u9b3c\uff0c\u7136\u540e\u90fd\u9047\u4e86\u7978\uff0c\u7ed9\u4e00\u79cd\u5947\u602a\u7684\u706b\u6d3b\u6d3b\u70e7\u6b7b\u2026\u2026\u201d \u3000\u3000\u201c\u767d\u8272\u7684\u5973\u9b3c\uff1f\u5947\u602a\u7684\u706b\uff1f\u201d\u9648\u5251\u8c01\u5ffd\u7136\u95ee\uff0c\u201c\u53ef\u5426\u8be6\u7ec6\u8bf4\u660e\u3002\u201d \u3000\u3000\u201c\u6211\u4e5f\u4e0d\u660e\uff0c\u56e0\u4e3a\u6211\u6ca1\u89c1\u8fc7\u3002\u201d\u6e29\u6587\u52aa\u529b\u641c\u7d22\u4ed6\u8111\u91cc\u6709\u5173\u8fd9\u65b9\u9762\u7684\u4f20\u8bf4\uff0c\u201c\u542c\u8bf4\u90a3\u662f\u4e00\u4e2a\u767d\u8272\u7684\u5973\u4eba\uff0c\u89c1\u4e86\u5979\u4e4b\u540e\uff0c\u5c31\u4f1a\u6709\u4e00\u79cd\u5996\u706b\uff0c\u90a3\u706b\u53c8\u79f0\u4f5c\u2018\u5730\u72f1\u4e4b\u706b\u2019\uff0c\u662f\u9ed1\u8272\u7684\uff0c\u53c8\u6709\u4eba\u8bf4\uff0c\u662f\u6ca1\u6709\u989c\u8272\u7684\uff0c\u4e00\u65e6\u6cbe\u4e0a\u4e86\uff0c\u4e0d\u628a\u4eba\u70e7\u6210\u7126\u70ad\u51b3\u4e0d\u7184\u706d\u2026\u2026\u201d \u3000\u3000\u4ed6\u4ece\u5012\u540e\u955c\u91cc\u770b\u89c1\u9648\u5251\u8c01\u5728\u540e\u5ea7\u4e0a\u9677\u5165\u6c89\u601d\u3002 \u3000\u3000\u725b\u4e3d\u751f\u5374\u53c8\u5728\u547c\u547c\u5927\u7761\uff0c\u9f3b\u9f3e\u58f0\u6bd4\u4ed6\u4eec\u7684\u5bf9\u8bdd\u66f4\u54cd\u3002 \u3000\u3000\u201c\u4f60\u4eec\u8fd9\u4e48\u2018\u677f\u5446\u2019\uff0c\u6b66\u529f\u9ad8\u5f3a\uff0c\u201d\u6e29\u6587\u5ffd\u7136\u5174\u81f4\u9ad8\u6602\uff0c\u5fc3\u8840\u6765\u6f6e\u7684\u8bf4\uff0c\u201c\u53ef\u66fe\u8ddf\u9b3c\u4ea4\u8fc7\u624b\uff1f\u201d \u3000\u3000\u7565\u94c3\u8e69\u7740\u79c0\u7709\uff0c\u201c\u4ec0\u4e48\u2018\u677f\u5446\u2019\uff0c\u4f60\u624d\u5446\uff01\u201d \u3000\u3000\u201c\u677f\u5446\u2019\u662f\u9a6c\u6765\u6d3b\uff0c\u610f\u6307\u806a\u660e\uff0c\u7b49\u4e8e\u662f\u5e7f\u4e1c\u4eba\u8bf4\u4eba\u2018\u543b\u2019\u7684\u610f\u601d\uff1a\u201d\u6e29\u6587\u89e3\u91ca\uff0c\u201c\u6211\u7684\u610f\u601d\u662f\u8bf4\uff1a\u4f60\u4eec\u90af\u543b\uff0c\u8bf4\u4e0d\u5b9a\u8fd9\u4e00\u6b21\u6765\uff0c\u8ddf\u6211\u4eec\u8fd9\u513f\u7684\u2018\u5996\u9b54\u9b3c\u602a\u2019\u3001\u4ec0\u4e48\u767d\u9b3c\u9ed1\u706b\u7684\u4ea4\u4e00\u4ea4\u624b\uff0c\u90a3\u53ef\u6709\u620f\u770b\u4e86\uff01\u201d \u3000\u3000\u201c\u4f60\u7231\u770b\u620f\uff0c\u5230\u620f\u9662\u53bb\uff01\u201d\u53f2\u6d41\u52b3\u60f3\u8d77\u8ddf\u6b64\u5730\u7684\u4eba\u8bed\u8a00\u6b20\u901a\uff0c\u521a\u624d\u8fd8\u4e3a\u4e86\u201c\u956d\u201d\u548c\u201c\u94b1\u201d\u7684\u53d1\u97f3\u4e89\u6301\u4e86\u597d\u4e45\uff0c\u8d8a\u60f3\u8d8a\u662f\u5fff\u5fff\uff0c\u201c\u543b\u5c31\u662f\u543b\uff0c\u4ec0\u4e48\u2018\u677f\u5446\u2019\u4e0d\uff0c\u677f\u5446\u2019\u7684\uff01\u201d \u3000\u3000\u201c\u8bdd\u4e0d\u662f\u90a3\u4e48\u8bf4\u7684\u3002\u4e00\u4e2a\u5730\u65b9\u6709\u4e00\u4e2a\u5730\u65b9\u7684\u751f\u6d3b\u4e60\u60ef\uff0c\u4e5f\u6709\u90a3\u5730\u65b9\u7684\u65b9\u8a00\u571f\u8bdd\uff0c\u201d\u6e29\u6587\u7b11\u8bf4\uff1a\u201c\u4eba\u4e61\u968f\u4fd7\u561b\uff01\u201d \u3000\u3000\u201c\u4f55\u6b62\u4fd7\uff0c\u7b80\u76f4\u571f\uff01\u201d\u9a86\u94c3\u4eba\u5728\u524d\u5ea7\uff0c\u5373\u65f6\u8868\u793a\u4e0d\u5c51\uff0c\u201c\u543b\u5c31\u662f\u543b\uff0c\u4ec0\u4e48\u677f\u5446\u4e0d\u677f\u5446\u7684\uff0c\u571f\u4eba\u58eb\u8bdd\uff01\u4e0d\u5080\u662f\u9053\u5730\u7684\u571f\u4eba\uff01\u201d \u3000\u3000\u8bf4\u7f62\u8fd8\u7b11\u5f97\u82b1\u679d\u6447\u66f3\u3002 \u3000\u3000\u6e29\u6587\u6a2a\u779f\u4e86\u5979\u4e00\u773c\uff0c\u8fd9\u56de\u662f\u6b63\u8272\u7684\u8bf4\uff0c\u201c\u4f60\u8fd9\u4e48\u8bf4\u5c31\u4e0d\u5bf9\u4e86\u3002\u8c01\u53c8\u662f\uff0c\u7eaf\u79cd\u2019\u7684\u4e2d\u56fd\u4eba\uff1f\u8499\u53e4\u3001\u5973\u771f\u3001\u5951\u4e39\u3001\u85cf\u65cf\u3001\u56de\u65cf\u3001\u5357\u86ee\u2026\u2026\u5982\u679c\u7ec6\u5206\u6761\u6790\u8d77\u6765\uff0c\u6015\u4e5f\u6709\u6570\u767e\u5343\u79cd\u65cf\u5427\uff1f \u3000\u3000\u56fd\u5bb6\u4e5f\u66fe\u6570\u5ea6\u6613\u4e3b\uff0c\u8840\u7edf\u65e9\u5df2\u662f\u5927\u6df7\u548c\u4e86\u3002\u53ea\u8981\u662f\u4eba\u624d\uff0c\u54ea\u91cc\u90fd\u80fd\u7ad9\u5f97\u8d77\u6765\uff0c\u53ea\u8981\u662f\u4e2a\u4eba\u7269\uff0c\u5728\u54ea\u91cc\u957f\u5927\u90fd\u57cb\u6ca1\u4e0d\u4e86\u4ed6\uff0c\u5f53\u5e74\u4e2d\u56fd\u641e\u9769\u547d\uff0c\u534e\u4fa8\u8fd8\u662f\u9769\u547d\u4e4b\u6bcd\u5462\uff01\u8bf4\u53e5\u4e0d\u9002\u5f53\u7684\u5b9e\u4f8b\uff0c\u6700\u8fd1\u66fe\u88ab\u6000\u7591\u53ef\u80fd\u662f\u9999\u6e2f\u6709\u53f2\u4ee5\u6765\u6700\u5927\u5b97\u7684\u2018\u5546\u4e1a\u9a97\u6848\u2019\uff0c\u8fd8\u4e0d\u662f\u6211\u4eec\u65b0\u9a6c\u534e\u4eba\u53ea\u624b\u6380\u7684\u98ce\u96e8\uff1f\u4f60\u4eec\u806a\u660e\u6211\u4eec\u571f\uff0c\u4f46\u7ed3\u679c\u8c01\u9a97\u4e86\u8c01\uff1f\u800c\u4e14\u8fd8\u6839\u672c\u544a\u4e0d\u4e86\u4ed6\u5462\uff01\u8fd9\u5f53\u7136\u4e0d\u503c\u5f97\u5f15\u4ee5\u4e3a\u8363\uff0c\u4f46\u4f60\u4eec\u4e5f\u522b\u4ee5\u5730\u57df\u4e0d\u540c\u4e3a\u50b2\u3002\u9999\u6e2f\u88ab\u82f1\u56fd\u653f\u5e9c\u7edf\u6cbb\u4e86\u767e\u591a\u5e74\uff0c\u53f0\u6e7e\u4e5f\u66fe\u88ab\u65e5\u672c\u5360\u9886\u4e86\u4e94\u5341\u591a\u5e74\uff0c\u4e2d\u56fd\u5927\u9646\u4ea6\u4e3a\u5916\u56fd\u4eba\u7684\u601d\u60f3\u5360\u636e\u4e86\u8fd1\u56db\u5341\u5e74\uff0c\u8c01\u624d\u662f\u9053\u5730\u7684\u534e\u4eba\uff1f\u8c01\u624d\u662f\u771f\u6b63\u7684\u571f\u4eba\uff1f\u9999\u6e2f\u4eba\u7684\u7ca4\u8bed\u9053\u5730\u5417\uff1f\u4ec0\u4e48\u2018\u6c99\u5c55\u2019\u3001\u2018\u6469\u767b\u2019\u3001\u2018\u83cf\u6253\u2019\u3001\uff0c\u591a\u58eb\u2019\u3001\u2018\u58eb\u591a\u2019\u2018\u57fa\u2019\uff0c\u2018\u5d29\u2019\uff0c\u2026\u2026\u83ab\u4e0d\u662f\u4ece\u82f1\u8bed\u7ffb\u6210\u7ca4\u4fd7\u7684\uff0c\u8fd9\u4e5f\u4e0d\u7b97\u7eaf\u5e7f\u4e1c\u8bdd\u5427\uff1f\u6bcf\u4e00\u4e2a\u5730\u65b9\u7684\u8bed\u8a00\u90fd\u6709\u5b83\u7684\u7279\u8272\uff0c\u6240\u4ee5\u6211\u4eec\u4e5f\u6709\u2018\u5148\u51b7\u2019\u3001\u2018\u677f\u5446\u2019\uff0c\u2018\u82cf\u683c\u2019\uff0c\u2018\u5ac1\u62db\u2019\u2026\u2026\u90a3\u5206\u522b\u662f\u2018\u8f7b\u677e\u2019\u3001\u2018\u806a\u660e\u3001\u80fd\u5e72\u2019\u3001\u201c\u559c\u6b22\u2019\u3001\u2018\u9a9a\u6270\u2019\u7684\u610f\u601d\uff0c\u8bed\u97f3\u6d3b\u6cfc\uff0c\u540c\u6837\u662f\u4e30\u5bcc\u4e86\u4e2d\u6587\u7684\u8bed\u5f55\u3002\u73b0\u5728\u53f0\u6e7e\u4e0d\u662f\u5174\u7528\u53f0\u6e7e\u8bdd\u6765\u5199\u5c0f\u8bf4\u5417\uff1f\u5728\u4ed6\u4eec\u800c\u8a00\uff0c\u662f\u7406\u6240\u5f53\u7136\u7684\u4e8b\uff0c\u53ef\u662f\u5bf9\u9999\u6e2f\u4eba\u6765\u8bf4\uff0c\u53ef\u80fd\u4f1a\u662f\u4e0d\u77e5\u6240\u8c13\u4e86\u3002\u540c\u6837\u7684\uff0c\u9999\u6e2f\u4eba\u82e5\u7528\u5e7f\u4e1c\u8bdd\u6765\u5199\u4f5c\uff0c\u53e3\u8bed\u5316\u662f\u53e3\u8bed\u5316\u4e86\uff0c\u5bf9\u9999\u6e2f\u65e0\u7591\u5341\u5206\u6709\u4eb2\u5207\u611f\uff0c\u53ef\u662f\u5bf9\u522b\u7684\u7701\u4efd\u7684\u8bfb\u8005\u800c\u8a00\uff0c\u53c8\u662f\u4e0d\u80fd\u6c9f\u901a\u7684\u4e86\u3002\u4e0d\u8fc7\u4e2d\u6587\u4e5f\u662f\u4ece\u5404\u79cd\u4e0d\u540c\u7684\u5730\u65b9\u8bed\u8a00\uff0c\u751a\u81f3\u5916\u56fd\u8bed\u8a00\u878d\u4f1a\u8fc7\u6765\u7684\uff0c\u73b0\u5728\u54ea\u6709\u7eaf\u4e2d\u6587\u53ef\u8a00\uff1f\u53cd\u6b63\u53bb\u829c\u5b58\u7cbe\uff0c\u6f5c\u79fb\u9ed8\u5316\uff0c\u5230\u5934\u6765\uff0c\u4e2d\u534e\u6587\u5316\u4e00\u5411\u6709\u5bb9\u4e43\u5927\uff0c\u6240\u4ee5\u624d\u4e0d\u6015\u6539\u671d\u6362\u4ee3\uff0c\u5c31\u7b97\u5f02\u65cf\u7edf\u6cbb\uff0c\u4e00\u6837\u80fd\u5c06\u4e4b\u540c\u5316\uff0c\u53cd\u6b63\u4f60\u80fd\u5bb9\u6211\uff0c\u6211\u80fd\u5bb9\u4f60\u5c31\u662f\u6700\u597d\u7684\u6001\u5ea6\uff0c\u522b\u56e0\u4e3a\u522b\u4eba\u51e0\u53e5\u8bdd\u8bf4\u5f97\u4e0d\u592a\u4e00\u6837\uff0c\u81ea\u5df1\u611f\u5230\u4e0d\u4e60\u60ef\u5c31\u8bf4\u4eba\u58eb\uff0c\u771f\u8981\u8bf4\u8d77\u6765\uff0c\u8fd8\u4e0d\u77e5\u9053\u8c01\u6d0b\u8c01\u571f\u5462\uff01\u201d \u3000\u3000\u6e29\u6587\u610f\u72b9\u672a\u5c3d\uff0c\u53c8\u8865\u4e86\u4e00\u53e5\u8bf4\uff1a\u201c\u6b63\u5982\u4f60\u4eec\u628a\u795e\u7ecf\u75c5\u7684\u4eba\u8bf4\u662f\u2018\u9752\u5c71\u51fa\u6765\u7684\u2019\u4e00\u6837\uff0c\u8fd9\u513f\u5374\u53eb\u505a\u2018\u7ea2\u6bdb\u4e39\u51fa\u6765\u7684\u2019\uff0c\u4e00\u9752\u4e00\u7ea2\uff0c\u5404\u6709\u6240\u5178\uff0c\u6cb3\u6c34\u4e0d\u72af\u4e95\u6c34\uff0c\u4f55\u4e0d\u4ee5\u6301\u5e73\u4e4b\u5fc3\uff0c\u7167\u5355\u5168\u6536\uff0c\u591a\u5b66\u4f1a\u4e00\u79cd\u8bed\u8a00\uff0c\u6709\u76ca\u65e0\u5bb3\u3002\u201d \u3000\u3000\u9a86\u94c3\u8010\u5fc3\u5730\u7b49\u4ed6\u8bf4\u5f97\u544a\u4e00\u6bb5\u843d\uff0c\u624d\u95ee\uff1a\u201c\u771f\u4e8f\u4f60\uff01\u201d \u3000\u3000\u6e29\u6587\u4e0d\u89e3\uff1a\u201c\u55ef\uff1f\u201d \u3000\u3000\u201c\u4f60\u4e00\u9762\u9a7e\u8f66\uff0c\u4e00\u9762\u8bf4\u8bdd\uff0c\u201d\u9a86\u94c3\u5ffd\u7136\u6e29\u67d4\u4e86\u8d77\u6765\uff0c\u4f7f\u6e29\u6587\u5f88\u6709\u4e9b\u53d7\u5ba0\u82e5\u60ca\u8d77\u6765\uff0c\u201c\u4f60\u4e0d\u7d2f\u5440\uff1f\u201d \u3000\u3000\u201c\u4e0d\u7d2f\uff0c\u4e0d\u7d2f\u3002\u201d \u3000\u3000\u201c\u90a3\u4f60\u8bf4\u90a3\u4e48\u591a\uff0c\u4e0d\u53e3\u6e34\u5440\uff1f\u201d \u3000\u3000\u201c\u4e0d\u6e34\uff0c\u563b\u563b\uff0c\u4e0d\u6e34\u3002\u201d \u3000\u3000\u201c\u6211\u53ef\u6e34\u6b7b\u4e86\uff01\u201d\u9a86\u94c3\u8fd9\u4e00\u53e5\u8bdd\u624d\u7b97\u56fe\u7a77\u5315\u73b0\u3002 \u3000\u3000\u201c\u4e00\u70b9\u4e5f\u4e0d\u6e34\uff0c\u563b\uff0c\u201d\u6e29\u6587\u7b11\u563b\u563b\u7684\u8bf4\uff1a\u201c\u5947\u602a\uff0c\u8bf4\u7684\u4e0d\u6e34\uff0c\u542c\u7684\u6e34\u3002\u201d \u3000\u3000\u201c\u8fd9\u513f\u9ed1\u51c4\u51c4\u3001\u9b3c\u5f71\u5e62\u51f6\u7684\uff0c\u96be\u602a\u4f1a\u6709\u9b3c\u4e86\uff0c\u201d\u9a86\u94c3\u6028\u8f7d\u8fde\u5929\u7684\u8bf4\uff0c\u201c\u4f60\u770b\uff0c\u4e00\u8def\u5f00\u8fc7\u53bb\uff0c\u8fde\u95f4\u50cf\u6837\u7684\u591c\u5e97\u90fd\u6ca1\u6709\uff0c\u96be\u602a\u4f1a\u65f6\u5e38\u95f9\u9b3c\u4e86\uff01\u201d \u3000\u3000\u9648\u5251\u8c01\u5728\u540e\u5ea7\u5ffd\u7136\u8bf4\uff1a\u201c\u91d1\u94c3\u5b50\u3002\u201d \u3000\u3000\u9a86\u94c3\u542c\u8fd9\u4e48\u4e00\u58f0\u53eb\uff0c\u5fc3\u91cc\u8dcc\u4e86\u8dcc\uff0c\u8bf4\u771f\u7684\uff0c\u5e73\u65f6\u4ed6\u4eec\u51e0\u4eba\u6709\u8bf4\u6709\u7b11\uff0c\u8fd8\u620f\u79f0\u9648\u5251\u8c01\u4e3a\u201c\u80a5\u9e2d\u201d\uff0c\u5176\u5b9e\uff0c\u5fc3\u5e95\u91cc\u8fd8\u662f\u5bf9\u8fd9\u4e2a\u8001\u5927\u53c8\u656c\u53c8\u754f\u7684\u3002 \u3000\u3000\u800c\u4eca\u542c\u9648\u5251\u8c01\u4e25\u8083\u7684\u53eb\u4e86\u90a3\u4e48\u4e00\u58f0\uff0c\u5fc3\u4e0b\u5148\u602f\u4e86\u4e09\u5206\uff0c\u767b\u65f6\u4e0d\u6562\u4e71\u8bf4\u8bdd\u4e86\u3002 \u3000\u3000\u201c\u4f60\u4eec\u4e0d\u80fd\u8001\u662f\u4ee5\u7f8e\u56fd\u3001\u9999\u6e2f\u3001\u53f0\u6e7e\u6765\u8981\u6c42\u8fd9\u513f\uff0c\u57fa\u672c\u4e0a\uff0c\u8fd9\u513f\u8ddf\u9999\u6e2f\u5730\u7406\u73af\u5883\u4e5f\u5f88\u4e0d\u4e00\u6837\u3002\u9999\u6e2f\u662f\u5f39\u4e38\u4e4b\u5730\uff0c\u662f\u5546\u8d38\u91d1\u878d\u4e2d\u5fc3\uff0c\u5bf8\u4f59\u5c3a\u4e0a\uff0c\u5bc6\u96c6\u53d1\u5c55\uff0c\u8fd9\u513f\u5219\u662f\u5f00\u53d1\u4e2d\u56fd\u5bb6\uff0c\u800c\u4e14\u5730\u5927\u4eba\u7a00\uff0c\u5206\u5341\u51e0\u5dde\uff0c\u6bcf\u5dde\u53c8\u6709\u6570\u5341\u767e\u5343\u5e02\u9547\u6216\u57e0\u533a\uff0c\u53d1\u5c55\u7684\u6a21\u5f0f\u662f\u5b8c\u5168\u4e0d\u4e00\u6837\u7684\u3002\u4f60\u770b\u4eba\u5bb6\u4e4c\u706f\u9ed1\u706b\u7684\uff1b\u662f\u56e0\u4e3a\u8fd8\u5728\u90ca\u9053\u4e0a\uff0c\u6ca1\u8fdb\u5165\u5e02\u533a\u3002\u4f60\u5acc\u8fd9\u91cc\u8bbe\u5907\u4e0d\u591f\u73b0\u4ee3\u5316\uff0c\u8d77\u5c45\u996e\u98df\u4e0d\u5982\u9999\u6e2f\u65b9\u4fbf\uff0c\u4f46\u8981\u662f\u522b\u4eba\u4ee5\u4ed6\u4eec\u7684\u773c\u5149\u6765\u770b\u9999\u6e2f\uff0c\u4e5f\u4e00\u5b9a\u9887\u4e0d\u4ee5\u4e3a\u7136\u3002\u4f60\u4ee5\u9999\u9999\u6e2f\u5927\u90fd\u4f1a\u4e3a\u8363\uff0c\u522b\u4eba\u53ef\u80fd\u4ee5\u4ed6\u4eec\u7684\u81ea\u7136\u73af\u5883\u4e3a\u50b2\u3002\u50cf\u9999\u6e2f\u4e2d\u4e0b\u5c42\u7684\u5c0f\u5bb6\u5ead\uff0c\u4f4f\u5343\u591a\u5e73\u65b9\u5c3a\u623f\u5b50\u5df2\u5c5e\u7f55\u6709\uff0c\u4f46\u8fd9\u513f\u4e2d\u4e0b\u9636\u5c42\u6216\u4e70\u6216\u79df\u4e0b\u4e00\u680b\u5343\u591a\u5e73\u65b9\u5c3a\u7684\u5c45\u5904\u662f\u5e38\u4e8b\uff0c\u5c82\u4e0d\u662f\u4e00\u6837\u4f1a\u5acc\u6211\u4eec\u4f4f\u7684\u50cf\u9e3d\u5b50\u7a9d\uff1f\u201d \u3000\u3000\u4ed6\u987f\u4e86\u4e00\u987f\uff0c\u518d\u52a0\u5f3a\u4ed6\u8bed\u8c03\uff1a\u201c\u6c38\u8fdc\uff0c\u6c38\u8fdc\u4e5f\u4e0d\u8981\u628a\u81ea\u5df1\u7684\u89c2\u5ff5\u5f3a\u52a0\u5728\u522b\u4eba\u8eab\u4e0a\uff0c\u8981\u628a\u81ea\u5df1\u7684\u4e60\u60ef\u4e0e\u8981\u6c42\u5f3a\u4f5c\u522b\u4eba\u7684\u4e60\u60ef\u4e0e\u8981\u6c42\uff0c\u53c8\u4e0d\u80fd\u8bbe\u8eab\u5904\u5730\u4e3a\u4ed6\u4eba\u7740\u60f3\uff0c\u5c31\u4e00\u5b9a\u4f1a\u9020\u6210\u8bef\u89e3\u4e0e\u9694\u9602\u3002\u3002\u3002\u201d \u3000\u3000\u201c\u8fd9\u539f\u672c\u90fd\u53ef\u4ee5\u907f\u514d\u7684\u3002\u201d\u4ed6\u4f3c\u4e4e\u5f88\u6709\u4e9b\u611f\u89e6\u3002 \u3000\u3000\u201c\u53ef\u662f\u2026\u2026\u201d\u9a86\u94c3\u6b32\u8a00\u53c8\u6b62\u3002 \u3000\u3000\u201c\u53ef\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u53ef\u662f\uff0c\u4f60\u628a\u4f60\u7684\u610f\u89c1\u5168\u544a\u8bc9\u4e86\u6211\uff0c\u800c\u6211\u53c8\u5168\u542c\u4ece\u4e86\u4f60\u7684\u610f\u89c1\uff0c\u8fd9\u6837\u2026\u2026\u201d\u9a86\u94c3\u6709\u70b9\u541e\u541e\u5410\u5410\uff0c\u201c\u8fd9\u6837\u5c82\u4e0d\u662f\u2026\u2026\u4f60\u4e5f\u628a\u4f60\u81ea\u5df1\u7684\u89c2\u5ff5\u5f3a\u52a0\u5728\u6211\u7684\u5934\u4e0a\u5417\uff1f\u201d \u3000\u3000\u9648\u5251\u8c01\u6123\u4e86\u6123\uff0c\u968f\u5373\u54c8\u54c8\u5927\u7b11\u8d77\u6765\u3002 \u3000\u3000\u201c\u4f60\u5012\u662f\u6d3b\u5b66\u6d3b\u7528\uff0c\u73b0\u7092\u73b0\u5356\uff1b\u201d\u4ed6\u7b11\u7740\u8bf4\uff0c\u201c\u6211\u53ea\u662f\u5728\u529d\u4f60\uff0c\u4f60\u53ef\u4ee5\u4e0d\u542c\uff0c\u4e5f\u53ef\u4ee5\u4e0d\u7406\uff0c\u4f46\u8981\u662f\u5f97\u7f6a\u4e86\u4eba\uff0c\u5f04\u5f97\u522b\u4eba\u4e0d\u6109\u5feb\uff0c\u81ea\u5df1\u4e0d\u5f00\u5fc3\uff0c\u90a3\u4e5f\u6028\u4e0d\u5f97\u4eba\u54e6\u3002\u201d \u3000\u3000\u9a86\u94c3\u4f38\u4e86\u4f38\u820c\u5934\u3002 \u3000\u3000\u201c\u60a8\u7684\u8bdd\u8c01\u6562\u4e0d\u542c\u561b\u3002\u201d \u3000\u3000\u201c\u6211\u6709\u90a3\u4e48\u9738\u9053\u5417\uff1f\u201d\u9648\u5251\u8c01\u7b11\u8bf4\u3002 \u3000\u3000\u201c\u8bf4\u8d77\u9b3c\uff0c\u6211\u4eec\u8fd9\u513f\u5012\u6709\u4e2a\u771f\u5b9e\u7684\u7b11\u8bdd\u2026\u2026\u201d \u3000\u3000\u9a86\u94c3\u4e0d\u60f3\u81ea\u8ba8\u6ca1\u8da3\uff0c\u6b63\u60f3\u8f6c\u79fb\u8bdd\u9898\uff0c\u542c\u6e29\u6587\u53c8\u626f\u5230\u522b\u5904\u53bb\uff0c\u6b63\u4e2d\u4e0b\u6000\uff0c\u8fde\u5fd9\u51d1\u8da3\uff1a\u201c\u9b3c\u4e5f\u6709\u7b11\u8bdd\uff1f\u8bf4\u6765\u542c\u542c\u3002\u201d \u3000\u3000\u201c\u4f4f\u5728\u8fd9\u513f\u6709\u56db\u5927\u6c11\u65cf\uff0c\u5206\u522b\u662f\u9a6c\u6765\u4eba\u3001\u534e\u4eba\u3001\u5370\u5ea6\u4eba\u548c\u5b5f\u52a0\u91cc\u4eba\uff0c\u5f53\u7136\uff0c\u8fd8\u6709\u4e00\u4e9b\u5c11\u6570\u6c11\u65cf\uff0c\u4f8b\u5982\u9521\u514b\u4eba\u3001\u6d0b\u4eba\u3001\u6c99\u76d6\u4eba\u3001\u5370\u5c3c\u4eba\u7b49\u3002\u4ee5\u524d\uff0c\u6211\u4eec\u534e\u4eba\u5e38\u4ee5\u5927\u4e2d\u534e\u6c11\u65cf\u4e3a\u672c\u4f4d\uff0c\u89c1\u4ed6\u4eec\u591a\u76ae\u80a4\u9edd\u9ed1\uff0c\u4fbf\u628a\u4ed6\u4eec\u53eb\u505a\u2018\u9a6c\u6765\u9b3c\u2019\uff0c\u5409\u7075\u9b3c\u2019\uff0c\u2018\u5b5f\u52a0\u62c9\u9b3c\u2019\u7b49\u7b49\uff0c\u540e\u6765\uff0c\u4ed6\u4eec\u4e5f\u4e00\u6837\u7167\u677f\u716e\u7897\uff0c\u79f0\u6211\u4eec\u4e3a\u2018\u652f\u90a3\u9b3c\u2019\u3002\u6211\u8fd9\u624d\u60f3\u5230\uff0c\u5bf9\u5440\uff0c\u6211\u4eec\u79f0\u4ed6\u4eec\u4e3a\u201c\u9b3c\u201d\u4ed6\u4eec\u4e5f\u4e00\u6837\u53ef\u4ee5\u4e0d\u628a\u6211\u4eec\u5f53\u4eba\u6765\u529e\u3002\u6211\u4eec\u81ea\u6043\u8138\u8272\u767d\uff0c\u4f46\u4ed6\u4eec\u4e5f\u53ef\u80fd\u81ea\u4ee5\u4e3a\u9ed1\u5f97\u6f02\u4eae\uff01\u8bd5\u770b\u7f8e\u56fd\u7684\u767d\u4eba\uff0c\u79f0\u9ed1\u4eba\u4e3a\u9ed1\u9b3c\uff0c\u8ba4\u4e3a\u9ed1\u8272\u662f\u80ae\u810f\u7684\u80a4\u8272\uff0c\u4f46\u5728\u9ed1\u4eba\u5fc3\u76ee\u4e2d\uff0c\u5374\u662f\u8d8a\u9ed1\u8d8a\u6f02\u4eae\uff0c\u4ed6\u4eec\u8ba4\u4e3a\u767d\u80a4\u8272\u624d\u662f\u80ae\u810f\u7684\u5462\uff01\u201d \u3000\u3000\u4ed6\u7b11\u7b11\u53c8\u8bf4\uff1a\u201c\u5982\u679c\u8fd9\u6837\u63a8\u8bba\u4e0b\u53bb\uff0c\u9ec4\u76ae\u80a4\u7684\u9a82\u9ed1\u76ae\u80a4\u7684\u662f\u9b3c\uff0c\u767d\u76ae\u80a4\u7684\u9a82\u9ec4\u76ae\u80a4\u7684\u662f\u9b3c\uff0c\u9ed1\u76ae\u80a4\u7684\u4e5f\u9a82\u767d\u76ae\u80a4\u7684\u662f\u9b3c\uff1b\u518d\u7ec6\u5206\u7c7b\uff1a\u8bed\u8a00\u4e0a\u7684\u4e0d\u540c\u4e5f\u53ef\u4ee5\u5f7c\u6b64\u770b\u4e0d\u8d77\u3002\u4f8b\u548c\u69df\u57ce\u4f4f\u7684\u591a\u662f\u798f\u5efa\u4eba\uff0c\u6021\u4fdd\u591a\u4f4f\u7684\u662f\u5e7f\u4e1c\u4eba\uff0c\u65b0\u52a0\u5761\u4f4f\u7684\u591a\u662f\u6f6e\u5dde\u4eba\uff0c\u5927\u5bb6\u4f60\u6307\u6211\u662f\u9b3c\uff0c\u6211\u8bf4\u4f60\u662f\u9b3c\uff0c\u5230\u5934\u6765\uff0c\u53ea\u6015\u8c01\u90fd\u4e0d\u662f\u4eba\u4e86\u3002\u201d \u3000\u3000\u9a86\u94c3\u542c\u4e86\uff0c\u5fc3\u4e2d\u5492\u9a82\uff0c\u77e5\u9053\u6e29\u6587\u662f\u7ed5\u4e86\u4e00\u4e2a\u4eba\u5708\u5b50\uff0c\u4f9d\u7136\u662f\u5bf9\u5979\u51b7\u8bbd\u70ed\u5632\uff0c\u5fc3\u4e2d\u8fde\u9a82\uff1a \u3000\u3000\u89c1\u9b3c\u4e86\uff01\u4f46\u788d\u7740\u8001\u5927\u66ff\u8fd9\u5bb6\u4f19\u201c\u6491\u201c\u8170\u201d\uff0c\u4e0d\u597d\u5f53\u9762\u53d1\u4f5c\uff0c\u53ea\u5728\u5fc3\u91cc\u76d8\u7b97\uff1a\u563f\uff0c\u5f85\u6709\u673a\u4f1a\uff0c\u770b\u672c\u5c0f\u59d0\u4e0d\u597d\u597d\u6536\u62fe\u4f60\uff01 \u3000\u3000\u5f53\u4e0b\u5c4f\u4f4f\u4e86\u6c14\uff0c\u522b\u8fc7\u8138\u53bb\uff0c\u4e0d\u53bb\u7b54\u7406\u6e29\u6587\u3002 \u3000\u3000\u9648\u5251\u8c01\u7b11\u8bf4\uff1a\u201c\u6e29\u5144\u3002\u201d \u3000\u3000\u6e29\u6587\u5bf9\u9648\u5251\u8c01\u5f88\u5c0a\u656c\uff0c\u8fde\u5fd9\u95ee\uff1a\u201c\u4f55\u4e8b\uff1f\u201d \u3000\u3000\u201c\u4e0d\u5982\u627e\u4e2a\u5730\u65b9\uff0c\u5403\u70b9\u4e1c\u897f\u559d\u70b9\u6c34\u3002\u201d\u9648\u5251\u8c01\u522b\u6709\u7528\u610f\u7684\u8bf4\uff0c\u201c\u6709\u7684\u4eba\u5927\u6982\u5df2\u6e34\u5f97\u5728\u559d\u53e3\u6c34\u4e86\u3002\u201d \u3000\u3000\u201c\u4e00\u5b9a\u4f1a\u53bb\uff0c\u5305\u7ba1\u6709\u5403\u6709\u559d\u7684\u3002\u201d\u6e29\u6587\u7b11\u7740\u8bf4\uff0c\u201c\u5176\u5b9e\u5df2\u5feb\u5230\u5e02\u533a\u4e86\uff0c\u8fd1\u90ca\u90a3\u513f\u6709\u51e0\u6863\u8def\u8fb9\u8336\u5e97\uff0c\u5473\u9053\u4e00\u6d41\uff0c\u6211\u6b63\u51c6\u5907\u53bb\u90a3\u513f\uff0c\u7edd\u4e0d\u80fd\u8ba9\u4f60\u4eec\u8fdc\u9053\u6765\u5ba2\u7b2c\u4e00\u987f\u5c31\u7559\u4e0b\u4e0d\u597d\u5370\u8c61\u561b\uff01\u201d \u3000\u3000\u201c\u8336\u5e97\uff01\u201d\u725b\u4e3d\u751f\u53eb\u9053\uff1a\u201c\u53ea\u559d\u8336\uff1f\u6211\u4e5f\u997f\u4e86\uff01\u4e00\u542c\u5403\u7684\uff0c\u4ed6\u5c31\u4e0d\u77e5\u5728\u4f55\u65f6\u5df2\u9192\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u201c\u8fd9\u513f\u4e00\u822c\u7684\u8def\u8fb9\u644a\u5e97\u90fd\u53eb\u8336\u5e97\uff0c\u5176\u5b9e\u4e0d\u6b62\u996e\u8336\uff0c\u4ece\u7cef\u7c73\u9e21\u9a6c\u62c9\u7cd5\u867e\u997a\u70e7\u5356\u5230\u7092\u7c89\u7092\u83dc\u9c7c\u867e\u9e21\u9e2d\uff0c\u90fd\u5e94\u6709\u5c3d\u6709\uff0c\u4e14\u90fd\u4ef7\u5ec9\u7f8e\u5473\u3002\u201d\u9648\u5251\u8c01\u8bf4\uff0c\u201c\u4f60\u53c8\u5fd8\u4e86\uff0c\u522b\u4ee5\u53f0\u6e7e\u7684\u2018\u8336\u9986\u2019\u2018\u8336\u5e97\u2019\u6765\u770b\u8fd9\u513f\u7684\u8336\u5e97\u3002\u201d \u3000\u3000\u201c\u5bf9\uff0c\u6709\u4e9b\u540d\u8f9e\u4e00\u6837\uff0c\u53ef\u662f\u5185\u91cc\u4e0d\u540c\uff0c\u6b63\u5982\u201c\u7406\u53d1\u5385\u2019\uff0c\u53f0\u6e7e\u548c\u9999\u6e2f\u5c31\u662f\u4e24\u56de\u4e8b\u4e86\u3002\u201d \u3000\u3000\u53f2\u6d41\u82b3\u4e5f\u52a0\u5165\u4e86\u201c\u9648\u5251\u8c01\u9635\u5bb9\u201d\u3002 \u3000\u3000\u6e29\u6587\u597d\u5947\u7684\u95ee\uff1a\u201c\u7406\u53d1\u5385\uff1f\u4e0d\u662f\u7406\u53d1\u7684\uff1f\u201d \u3000\u3000\u201c\u5bf9\uff01\u201d\u53f2\u6d41\u82b3\u8c03\u4f83\u7684\u8bf4\uff0c\u201c\u5728\u53f0\u6e7e\uff0c\u53ef\u4ee5\u628a\u4f60\u4fee\u7406\u5f97\u65e0\u6cd5\u65e0\u5929\uff01\u201d \u3000\u3000\u6e29\u6587\u4f9d\u7136\u6ca1\u6709\u542c\u61c2\uff0c\u770b\u53f2\u6d41\u82b3\u66a7\u6627\u7684\u7b11\u7740\uff0c\u4fbf\u6253\u7b97\u79c1\u4e0b\u518d\u95ee\u4e2a\u6e05\u695a\uff0c\u53ea\u8bf4\uff1a\u201c\u6211\u4eec\u5148\u53bb\u6d88\u591c\uff0c\u5230\u5e02\u533a\u6211\u5bb6\u65c5\u793e\u4f4f\u4e00\u5bbf\uff0c\u660e\u5929\u518d\u5357\u4e0b\u53bb\u627e\u5c0f\u8521\u5427\u3002\u201d \u3000\u3000\u201c\u65c5\u793e\uff1f\u201d\u9a86\u94c3\u5947\u9053\uff1a\u201c\u6211\u4eec\u627e\u65c5\u884c\u793e\u5e72\u5417\uff1f\u201d \u3000\u3000\u201c\u65c5\u793e\u5c31\u662f\u9999\u6e2f\u6240\u79f0\u7684\u9152\u5e97\uff0c\u4e5f\u5c31\u662f\u53f0\u6e7e\u7684\u996d\u5e97\uff0c\u4e2d\u56fd\u5927\u9646\u7684\u5bbe\u9986\u3002\u201d\u9648\u5251\u8c01\u8bf4\uff0c\u201c\u4f60\u770b\uff0c\u5149\u662fHotel\u5c31\u6709\u8fd9\u4e48\u591a\u4e0d\u540c\u7684\u8bd1\u6cd5\u3002\u201d \u3000\u3000\u201c\u4e0d\u8fc7\uff0c\u8fd9\u513f\u6bd4\u8f83\u5927\u7684Hotel\u4e5f\u591a\u7528\u2018\u9152\u5e97\u2019\u4e86\u3002\u201d\u6e29\u6587\u8bf4\u3002 \u3000\u3000\u725b\u4e3d\u751f\u4e5f\u8bf4\uff1a\u201c\u5927\u9646\u4e5f\u5f00\u59cb\u7528\u9152\u5e97\u4e86\u3002\u201d \u3000\u3000\u201c\u5176\u5b9e\u6b20\u901a\uff0c\u201d\u9648\u5251\u8c01\u7b11\u9053\uff1a\u201c\u9152\u5e97\u4e0d\u5356\u9152\uff0c\u53ea\u79df\u623f\u95f4\u3002\u201d \u3000\u3000\u201c\u996d\u5e97\u66f4\u4e0d\u901a\uff0c\u201d\u53f2\u6d41\u82b3\u7b11\u8bf4\uff0c\u201c\u996d\u5e97\u4e0d\u5403\u996d\uff0c\u53ea\u7761\u89c9\u3002\u201d \u3000\u3000\u4f17\u4eba\u6709\u8bf4\u6709\u7b11\uff0c\u5728\u8f66\u5b50\u8fd8\u6ca1\u6709\u62b5\u8fbe\u5403\u4e1c\u897f\u7684\u5730\u65b9\u4e4b\u524d\uff0c\u5927\u5bb6\u90fd\u5bf9\u6e29\u6587\u8fd9\u4e2a\u201c\u964c\u751f\u670b\u53cb\u201d \u3000\u3000\u70ed\u7edc\u4e86\u8d77\u6765\u3002 \u3000\u3000\u6240\u4ee5\u4fdf\u6e29\u6587\u53d1\u73b0\u4ed6\u8d70\u9519\u4e86\u8def\u65f6\uff0c\u5927\u5bb6\u5bf9\u4ed6\u90fd\u53c8\u6028\u53c8\u9a82\uff0c\u6beb\u4e0d\u5ba2\u6c14\u3002 \u3000\u3000\u6e29\u6587\u4e5f\u4e0d\u6e29\u4e0d\u706b\uff0c\u4f9d\u65e7\u7b11\u563b\u563b\u7684\uff0c\u628a\u8f66\u5b50\u5f00\u5230\u53ef\u4ee5\u6389\u5934U\u8f6c\u4e4b\u5904\uff0c\u91cd\u65b0\u8d76\u8def\uff0c\u4f46\u4f9d\u7136\u662f\u65f6\u901f\u56db\u5341\u4e94\u91cc\u3002 \u3000\u3000\u53f2\u6d41\u82b3\u770b\u4e0d\u8fc7\u773c\u3002\u201c\u8fd9\u4e48\u6162\uff0c\u4e0d\u5982\u6211\u66ff\u4f60\u5f00\u5427\uff01\u201d \u3000\u3000\u201c\u4f60\u4e0d\u719f\u8fd9\u513f\u7684\u8def\uff0c\u201d\u6e29\u6587\u4e0d\u614c\u4e0d\u5fd9\u7684\u8bf4\uff0c\u201c\u96be\u4fdd\u4e0d\u5f00\u5230\u77ff\u6e56\u91cc\u53bb\u3002\u201d \u3000\u3000\u201c\u4f60\u8fd9\u4e48\u80e1\u6d82\uff0c\u201d\u9a86\u94c3\u8d81\u673a\u5e2e\u53f2\u6d41\u82b3\u8fd9\u4e00\u8fb9\uff0c\u201c\u8bf4\u4e0d\u5b9a\u53c8\u4f1a\u628a\u8f66\u5b50\u5f00\u56de\u673a\u573a\u53bb\u3002\u201d \u3000\u3000\u6e29\u6587\u7b11\u4e86\u4e00\u7b11\uff1a\u201c\u6211\u4e00\u5411\u80e1\u6d82\u3002\u201d \u3000\u3000\u201c\u4f60\u770b\u6765\u80e1\u6d82\uff0c\u201d\u9648\u5251\u8c01\u597d\u4e00\u4f1a\u513f\u4e0d\u8bf4\u8bdd\u4e86\uff0c\u73b0\u5728\u5ffd\u7136\u63d2\u53e3\uff1a\u201c\u4f46\u521a\u624d\u8bf4\u4e86\u597d\u4e9b\u5e76\u4e0d\u7cca\u6d82\u7684\u8bdd\u3002\u201d \u3000\u3000\u201c\u4e00\u6b21\u80e1\u6d82\u4e00\u6b21\u81ea\u5728\uff0c\u201d\u6e29\u6587\u53f9\u4e86\u53e3\u6c14\uff0c\u201c\u6709\u65f6\uff0c\u6211\u5012\u5e0c\u671b\u81ea\u5df1\u5e38\u5e38\u80e1\u6d82\u3002\u201d \u3000 \u3000 \u7b2c\u56db\u7ae0\u3000\u4e00\u6b21\u6323\u624e\u4e00\u6b21\u6253\u51fb \u3000 \u3000\u3000\uff11\u3001\u6389\u8fdb\u731b\u706b\u91cc \u3000\u3000\u80e1\u6d82\u662f\u798f\uff0c\u6240\u4ee5\u96be\u5f97\u80e1\u6d82\u3002 \u3000\u3000\u2014\u2014\u5982\u679c\u9648\u5251\u8c01\u80fd\u50cf\u6e29\u6587\u4e00\u822c\u80e1\u6d82\uff0c\u90a3\u4e48\u5c31\u4fdd\u51c6\u53ef\u4ee5\u5403\u5230\u4e00\u987f\u597d\u6d88\u591c\u4e86\u3002 \u3000\u3000\u2014\u2014\u5982\u679c\u6709\u4eba\u8ddf\u4f60\u8bf4\uff1a\u6709\u4e24\u4e2a\u6d88\u606f\uff0c\u4e00\u597d\u4e00\u574f\uff0c\u4f60\u8981\u5148\u542c\u54ea\u4e00\u4e2a\uff1f \u3000\u3000\u8bb8\u591a\u4eba\u90fd\u4f1a\u662f\u9009\u201c\u574f\u201d\u7684\uff0c\u90a3\u662f\u5e0c\u671b\u201c\u82e6\u5c3d\u7518\u6765\u201d\uff0c\u201c\u5148\u82e6\u540e\u4e50\u201d\u5df1\u662f\u4e00\u79cd\u7f8e\u5fb7\uff1b\u5f53\u7136\u4e5f\u6709\u4eba\u5148\u9009\u201c\u597d\u201d\u7684\u542c\uff0c\u56e0\u4e3a\u65e2\u6709\u82e6\u7684\uff0c\u4e0d\u5982\u5148\u4e50\u4e86\u518d\u8bf4\uff0c\u8981\u4e0d\u7136\uff0c\u542c\u4e86\u201c\u574f\u201d\u7684\u4e4b\u540e\uff0c\u5fc3\u60c5\u4e5f\u201c\u574f\u201d\uff0c\u542c\u201c\u597d\u201d\u7684\u4e5f\u201c\u597d\u201d\u4e0d\u8d77\u6765\u3002 \u3000\u3000\u2014\u2014\u2014\u5176\u5b9e\u8fd8\u6709\u4e00\u79cd\u65b9\u5f0f\u3002 \u3000\u3000\u90a3\u5c31\u662f\u4e24\u8005\u90fd\u4e0d\u9009\u3002 \u3000\u3000\u53cd\u6b63\u597d\u7684\u574f\u7684\u6d88\u606f\uff0c\u8fdf\u65e9\u90fd\u4f1a\u6765\u7684\uff0c\u7b49\u6765\u65f6\u518d\u8bf4\u5427\u3002\u4e0d\u5982\u73b0\u5728\u4e0d\u542c\uff0c\u514d\u5f97\u5f71\u54cd\u5fc3\u60c5\u3002 \u3000\u3000\u6e29\u6587\u4f3c\u4e4e\u662f\u8fd9\u79cd\u4eba\u3002 \u3000\u3000\u9648\u5251\u8c01\u6ce8\u610f\u5230\u4ed6\u770b\u62a5\u7eb8\u53ea\u770b\u526f\u520a\uff0c\u5927\u6982\u5c31\u662f\u56e0\u4e3a\u5176\u4ed6\u7248\u9762\u90fd\u520a\u8f7d\u7740\u592a\u591a\u60b2\u82e6\u4e8b\u4e4b\u6545\u5427\u3002 \u3000\u3000\u4e00\u4e2a\u7cbe\u660e\u7684\u4eba\uff0c\u5c31\u505a\u4e0d\u5230\u8fd9\u70b9\u3002 \u3000\u3000\u2014\u2014\u7cbe\u660e\u7684\u4eba\u8bb2\u6c42\u5148\u673a\uff0c\u201c\u6599\u654c\u673a\u5148\u201d\u4f7f\u662f\u5728\u90a3\u4e2a\u201c\u5148\u201d\u5b57\u4e0a\uff0c\u5bf9\u4e00\u5207\u6709\u7528\u7684\u8d44\u8baf\uff0c\u4ed6\u4eec\u4e00\u5b9a\u8981\u6bd4\u522b\u4eba\u5148\u638c\u63e1\uff0c\u624d\u80fd\u5728\u7ade\u4e89\u4e2d\u5360\u4f18\u52bf\u3002 \u3000\u3000\u81f3\u4e8e\u53f2\u6d41\u82b3\u548c\u9a86\u94c3\uff0c\u53c8\u662f\u5178\u578b\u7684\u4e24\u79cd\u4eba\u3002 \u3000\u3000\u5982\u679c\u53eb\u4e00\u7897\u56db\u5b9d\u7c89\uff0c\u9a86\u94c3\u4e00\u5b9a\u4f1a\u628a\u201c\u56db\u5b9d\u201d\u5148\u884c\u4e00\u53e3\u6c14\u5403\u5b8c\uff0c\u7136\u540e\u624d\u6765\u5403\u7c89\uff0c\u53f2\u6d41\u82b3\u5219\u6070\u597d\u80e1\u53cd\uff0c\u5148\u5403\u5b8c\u7c89\uff0c\u518d\u6765\u597d\u597d\u4eab\u53d7\u90a3\u4e9b\u9c7c\u4e38\u3001\u725b\u8089\u4e38\u3001\u9c7c\u76ae\u997a\u3001\u58a8\u9c7c\u4e38\u4ec0\u4e48\u7684\u3002 \u3000\u3000\u2014\u2014\u800c\u725b\u4e3d\u751f\u5462\uff1f\u5219\u7b80\u5355\u5f97\u5f88\u3002\u4ed6\u53ef\u662f\u8fd9\u56db\u5b9d\u5e26\u7c89\uff0c\u4e00\u9f50\u9aa8\u788c\u788c\u72c2\u98ce\u626b\u843d\u53f6\u957f\u9cb8\u6c72\u6eaa\u6c34\u4f3c\u7684\uff0c\u5168\u541e\u5230\u809a\u5b50\u91cc\u53bb\uff0c\u53ea\u8981\u8fd8\u7559\u7740\u7897\u789f\u7b77\u5b50\u5df2\u7b97\u662f\u5ba2\u6c14\u7684\uff0c\u4e8b\u540e\u4f60\u95ee\u4ed6\u54ea\u6837\u597d\u5403\uff0c\u4ed6\u53ef\u80fd\u8fd8\u4f1a\u53cd\u95ee\u4f60\uff1a\u201c\u56db\u5b9d\uff1f\u4ec0\u4e48\u56db\u5b9d\uff1f\u6211\u521a\u624d\u5403\u4e86\u4ec0\u4e48\uff1f\u6211\u5403\u8fc7\u8fd9\u4e1c\u897f\u5417\uff1f\u201d \u3000\u3000\u4e00\u5c31\u7b97\u662f\u5403\u4e00\u7897\u9762\uff0c\u56db\u4e2a\u4eba\u90fd\u5403\u51fa\u4e0d\u540c\u7684\u4e2a\u6027\u6765\u3002 \u3000\u3000\u53ef\u662f\u8fd9\u6b21\u8c01\u90fd\u4e00\u6837\u3002 \u3000\u3000\u2014\u2014\u5306\u5306\u7684\u628a\u7897\u91cc\u7684\u98df\u7269\u585e\u5165\u809a\u5b50\u91cc\uff0c\u9a6c\u4e0a\u5c31\u52a8\u8eab\u4e86\u3002 \u3000\u3000\u800c\u9648\u5251\u8c01\u6839\u672c\u4e0d\u60f3\u5403\u3002 \u3000\u3000\u56e0\u4e3a\u5931\u53bb\u4e86\u80c3\u53e3\u3002 \u3000\u3000\u539f\u56e0\u51fa\u5728\u90a3\u4e00\u4efd\u62a5\u7eb8\u4e0a\u3002 \u3000\u3000\u8336\u5e97\u4e2d\u6709\u5c0f\u7ae5\u515c\u9500\u62a5\u7eb8\uff0c\u6e29\u6587\u968f\u4fbf\u4e70\u4e86\u4e00\u4efd\uff0c\u6b63\u5728\u9605\u8bfb\u526f\u520a\u3002 \u3000\u3000\u9648\u5251\u8c01\u7a81\u7136\u53d1\u73b0\uff0c\u6e29\u6587\u624b\u4e0a\u62ff\u7684\u62a5\u7eb8\u7684\u80cc\u9875\uff0c\u6b63\u6709\u8fd9\u51e0\u4e2a\u5b57\uff1a\u201c\u56db\u5e78\u60e8\u6b7b\uff0f\u75db\u4e0d\u6b32\u751f\u201d\uff0c\u8fd9\u4e24\u884c\u5b57\u4e0a\u9762\u80af\u5b9a\u8fd8\u6709\u53e5\u5b50\u7684\uff0c\u4f46\u7ed9\u6e29\u6587\u6298\u5230\u53e6\u4e00\u8fb9\u53bb\u4e86\u3002 \u3000\u3000\u9664\u5251\u8c01\u7acb\u5373\u4f38\u624b\u593a\u8fc7\u62a5\u7eb8\uff0c\u624d\u628a\u6807\u9898\u770b\u4e2a\u5206\u660e\uff1a\u201c\u9752\u5e74\u6280\u51fb\u5bb6\u997c\u5e97\u5c11\u4e1c\u8521\u56db\u5e78\u60e8\u6b7b\uff0f\u6df1\u591c\u9ed1\u706b\u711a\u8eab\u5973\u53cb\u65e5\u7779\u75db\u4e0d\u6b32\u751f\u201d\uff0c\u4e0b\u9762\u8fd8\u6709\u51e0\u4e2a\u5b57\u7684\u526f\u9898\uff1a \u3000\u3000\u201c\u5996\u706b\u518d\u5ea6\u8086\u51f6 \u3000\u3000\u5a5a\u4e8b\u53d8\u4f5c\u4e27\u5352\u201d \u3000\u3000\u9648\u5251\u8c01\u76b1\u7740\u7709\u3001\u8fc5\u901f\u5730\uff0c\u800c\u4e14\u4ed4\u7ec6\u5730\u770b\u62a5\u4e0a\u7684\u5185\u5bb9\uff0c\u77e5\u9053\u90a3\u662f\u4e24\u5929\u524d\u7684\u4e8b\uff1b\u4f46\u56e0\u4e3a\u592a\u8038\u4eba\u542c\u95fb\uff0c\u6240\u4ee5\u65b0\u95fb\u7ee7\u7eed\u8ffd\u8e2a\u8fd9\u4ef6\u4e8b\u3002\u8fd9\u65f6\uff0c\u65c1\u8fb9\u7684\u4eba\u4e5f\u90fd\u770b\u5230\u4e86\u6807\u9898\uff1a\u90fd\u9707\u6115\u4f4f\u4e86\u3002 \u3000\u3000\u53f2\u6d41\u82b3\u547b\u541f\u4e86\u4e00\u58f0\uff1a\u201c\u5929\uff01\u201d \u3000\u3000\u9a86\u94c3\u5931\u58f0\u9053\uff1a\u201c\u4ed6\u2026\uff0c\u4ed6\u7adf\u6b7b\u4e86\uff01\u201d \u3000\u3000\u53ea\u6709\u6e29\u6587\u5728\u9648\u5251\u8c01\u5bf9\u9762\uff0c\u8fd8\u83ab\u540d\u5176\u5999\u5bf9\u65b9\u4e3a\u4f55\u4e00\u628a\u5c31\u62a2\u53bb\u4e86\u4ed6\u624b\u4e2d\u7684\u62a5\u7eb8\uff0c\u4e5f\u53ea\u6709\u4ed6\u770b\u4e0d\u89c1\u9648\u5251\u8c01\u6240\u6ce8\u89c6\u7684\u5185\u5bb9\uff0c\u5947\u9053\uff1a\u201c\u6b7b\u4e86\uff1f\u8c01\u6b7b\u4e86\uff1f\u201d \u3000\u3000\u7136\u540e\u4ed6\u4f38\u8fc7\u5934\u53bb\u3002 \u3000\u3000\u4e00\u4e2a\u660e\u4e86\u771f\u76f8\u7684\u4eba\u5c31\u4f1a\u5931\u53bb\u7cca\u6d82\u7684\u798f\u6c14\u3002 \u3000\u3000\u4ed6\u4e5f\u53d1\u73b0\u4e86\u662f\u4ec0\u4e48\u4e8b\u3002 \u3000\u3000\u4ed6\u5df2\u77e5\u9053\u6b7b\u7684\u662f\u4ec0\u4e48\u4eba\u3002 \u3000\u3000\u4ed6\u60ca\u6115\u83ab\u5df2\u7684\u8bf4\u4e86\u4e00\u53e5\uff1a\u201c\u600e\u4e48\u4f1a\u2026\u2026\u600e\u4e48\u4f1a\u2026\u2026\u201d \u3000\u3000\u7136\u540e\u4ed6\u4e00\u76f4\u54ed\u3002 \u3000\u3000\u4e00\u76f4\u6d41\u6cea\u3002 \u3000\u3000\u9a86\u94c3\u4ece\u6765\u6ca1\u89c1\u8fc7\u4e00\u4e2a\u7537\u4eba\u4f1a\u54ed\u6210\u8fd9\u6837\u5b50\u3002 \u3000\u3000\u76f4\u5230\u4ed6\u54ed\u5f97\u544a\u4e00\u6bb5\u843d\uff0c\u7136\u540e\u6cea\u6d41\u6ee1\u8138\u7684\u53bb\u4e70\u5355\uff0c\u5413\u7684\u90a3\u5e97\u4e1c\u4e0d\u77e5\u9053\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\u3002 \u3000\u3000\u7136\u540e\u4ed6\u5374\u975e\u5e38\u9547\u5b9a\uff0c\u53e3\u5230\u684c\u524d\uff0c\u8bf4\uff1a\u201c\u6211\u4eec\u8d70\uff01\u201d \u3000\u3000\u201c\u53bb\u54ea\u91cc\uff1f\u201d\u9a86\u94c3\u95ee\u3002 \u3000\u3000\u201c\u6211\u4eec\u73b0\u5728\u5c31\u5357\u4e0b\u3002\u201d\u6e29\u6587\u60b2\u75db\u7684\u8bf4\uff0c\u201c\u53bb\u67e5\u660e\u771f\u76f8\uff0c\u66ff\u56db\u5e78\u62a5\u4ec7\uff01\u201d \u3000\u3000\u9648\u5251\u8c01\u5f3a\u6291\u96be\u8fc7\uff0c\u6545\u610f\u95ee\uff1a\u201c\u4f60\u4e0d\u76f8\u4fe1\u56db\u5e78\u662f\u6b7b\u4e8e\u610f\u5916\uff1f\u201d \u3000\u3000\u201c\u4e0d\u53ef\u80fd\uff0c\u7edd\u4e0d\u53ef\u80fd\u3002\u4ee5\u5c0f\u8521\u7684\u8eab\u624b\uff0c\u54ea\u6709\u706b\u6cbe\u5f97\u4e0a\u4ed6\u7684\u8eab\u5b50\uff1f\u6211\u62c5\u4fdd\u8fde\u7535\u90fd\u7535\u4e0d\u90a3\u4ed6\uff01\u4e00\u5b9a\u662f\u6709\u4eba\u5bb3\u4ed6\u7684\uff01\u201d\u6e29\u6587\u5927\u58f0\u5730\u8bf4\uff0c\u201c\u6211\u662f\u4ed6\u7684\u670b\u53cb\uff0c\u6211\u4e0d\u80fd\u8ba9\u4ed6\u6b7b\u5f97\u4e0d\u660e\u4e0d\u81ea\uff01\u201d \u3000\u3000\u201c\u4f60\u4e0d\u76f8\u4fe1\u90a3\u9ed1\u706b\u5c31\u662f\u9b3c\u706b\uff1f\u201d\u9648\u5251\u8c01\u7d27\u8feb\u76ef\u4eba\u7684\u95ee\uff0c\u201c\u5c31\u7b97\u662f\u4eba\u653e\u7684\u706b\uff0c\u80fd\u70e7\u6b7b\u5f97\u4e86\u8521\u56db\u5e78\u7684\uff0c\u5b9a\u975e\u540c\u51e1\u54cd\uff0c\u4f60\u4e5f\u8981\u53bb\u60f9\u4ed6\uff1f\u201d \u3000\u3000\u201c\u5982\u679c\u4ed6\u662f\u4eba\uff0c\u4ed6\u6562\u6740\u6211\u7684\u670b\u53cb\uff0c\u6211\u5c31\u8981\u4ed6\u6740\u80ce\u547d\uff01\u201d\u6e29\u6587\u7406\u76f4\u6c14\u58ee\u7684\u8bf4\uff0c\u201c\u5982\u679c\u4ed6\u662f\u9b3c\uff0c\u6211\u5c31\u8981\u628a\u8fd9\u9b54\u9b3c\u63ea\u51fa\u6765\uff0c\u95ee\u95ee\u4ed6\u4e3a\u4ec0\u4e48\u8981\u5bb3\u4eba\uff01\u201d \u3000\u3000\u7136\u540e\u4ed6\u6c14\u51b2\u51b2\u7684\u8bf4\uff1a\u201c\u4f60\u4eec\u8981\u662f\u4e0d\u6562\u53bb\uff0c\u53ef\u4ee5\u4e0d\u53bb\uff01\u201d \u3000\u3000\u725b\u4e3d\u751f\u6012\u9053\uff1a\u201c\u4f60\u8bf4\u4ec0\u4e48\uff01\u201d \u3000\u3000\u53f2\u6d41\u82b3\u51b7\u7b11\uff1a\u201c\u6211\u4eec\u4f1a\u4e0d\u6562\u53bb\uff1f\u201d \u3000\u3000\u201c\u5c31\u8fd9\u4e48\u529e\uff01\u201d\u9648\u5251\u8c01\u8bf4\uff0c\u201c\u54b1\u4eec\u4e0a\u7a77\u78a7\u843d\u4e0b\u9ec4\u6cc9\uff0c\u4e5f\u8981\u627e\u51fa\u6740\u6b7b\u56db\u5e78\u7684\u539f\u56e0\uff0c\u66ff\u5c0f\u8521\u62a5\u4ec7\uff01\u201d \u3000\u3000\u4ed6\u4eec\u654c\u5ffe\u540c\u4ec7\uff0c\u5306\u5306\u628a\u98df\u7269\u626b\u5165\u80c3\u91cc\uff0c\u5c31\u654f\u6377\u7684\u4e0a\u4e86\u8f66\uff0c\u8f66\u95e8\u51e0\u4e4e\u662f\u540c\u4e00\u65f6\u95f4\u5173\u4e0a\u7684\u3002 \u3000\u3000\u53f2\u6d41\u82b3\u89c1\u6e29\u6587\u4ecd\u6cea\u6d41\u4e0d\u6b62\uff0c\u60c5\u7eea\u6fc0\u52a8\uff0c\u4fbf\u95ee\uff1a\u201c\u4f60\u8981\u4e0d\u8981\u6682\u7531\u6211\u6765\u9a7e\u8f66\uff1f\u201d \u3000\u3000\u201c\u4e0d\u5fc5\u3002\u201d\u6e29\u6587\u65a9\u9489\u622a\u94c1\u7684\u8bf4\uff1a\u201c\u56db\u5e78\u7684\u6b7b\u903c\u4f7f\u5927\u5bb6\u8df3\u8ff8\u731b\u706b\u91cc\uff0c\u73b0\u5728\u8c01\u90fd\u4e0d\u80fd\u6b7b\u3002\u6211\u81ea\u4f1a\u7559\u7740\u81ea\u5df1\u548c\u5927\u5bb6\u7684\u6027\u547d\uff0c\u4e0d\u7136\u8c01\u66ff\u5c0f\u8521\u62a5\u4ec7\uff01\u201d\u4ed6\u4e00\u9762\u8bf4\u4e00\u9762\u5012\u8f66\uff0c\u8f66\u540e\u5934\u78b0\u7684\u4e00\u4e0b\u50cf\u662f\u649e\u4e0a\u4e86\u4ec0\u4e48\u7269\u4f53\u3002 \u3000\u3000\u53f2\u6d41\u82b3\u4ecd\u662f\u6709\u70b9\u4e0d\u653e\u5fc3\u3002 \u3000\u3000\u201c\u4e0d\u8981\u7d27\u7684\uff0c\u201d\u9648\u5251\u8c01\u8bf4\uff0c\u201c\u4ed6\u521a\u624d\u5728\u731d\u95fb\u5669\u8017\u65f6\u4ecd\u80fd\u5148\u53bb\u66ff\u6211\u4eec\u7ed3\u8d26\u624d\u884c\u52a8\uff0c\u4ed6\u8fd8\u662f\u6e05\u9192\u5f97\u5f88\u3002\u201d \u3000\u3000\u8f66\u5b50\u5728\u9ed1\u591c\u7684\u67cf\u6cb9\u9053\u4e0a\u72c2\u541e\u7740\u8def\u7891\u3002 \u3000\u3000\u8f66\u706f\u5728\u5343\u969c\u4e07\u91cd\u7684\u9ed1\u5e55\u4e2d\u8361\u51fa\u4e24\u7ebf\u9ec4\u8292\u3002 \u3000\u3000\u201c\u4e5f\u8bb8\uff0c\u4f60\u5f97\u8981\u5728\u4e00\u9762\u9a7e\u8f66\uff0c\u201c\u9648\u5251\u8c01\u51dd\u91cd\u7684\u8bf4\uff0c\u201c\u4e00\u9762\u544a\u8bc9\u6211\u4e00\u4e9b\u6709\u5173\u8521\u56db\u5e78\u548c\u53ef\u80fd\u4e0e\u2018\u9ed1\u706b\u2019\u6709\u5173\u7684\u4e8b\u3002\u201d \u3000\u3000\u201c\u53ef\u4ee5\u3002\u201d\u6e29\u6587\u575a\u5b9a\u4ed6\u8bf4\uff0c\u201c\u8fd9\u513f\u79bb\u76ee\u7684\u5730\u8fd8\u6709\u4e5d\u5341\u591a\u82f1\u91cc\uff0c\u81f3\u5c11\u8981\u4e09\u56db\u4e2a\u5c0f\u65f6\u624d\u80fd\u62b5\u8fbe\uff0c\u6211\u4e00\u8def\u4e0a\u4f1a\u628a\u6240\u77e5\u7684\u544a\u8bc9\u4f60\u4eec\u3002\u201d \u3000\u3000\u4ed6\u4eec\u672c\u6765\u5174\u9ad8\u91c7\u70c8\u7684\u6765\uff0c\u53ef\u662f\u56e0\u4e3a\u4e00\u4e2a\u670b\u53cb\u7684\u6b7b\u8baf\uff0c\u4f7f\u4ed6\u4eec\u90fd\u9677\u8fdb\u4e86\u6c34\u6df1\u706b\u70ed\u91cc\u3002 \u3000\u3000\u62b5\u8fbe\u201c\u8d1d\u591a\u201d\u57e0\u7684\u65f6\u5019\uff0c\u5df2\u662f\u7b2c\u4e8c\u5929\u4e0a\u5348\u7684\u4e8b\uff0c\u4ed6\u4eec\u81ea\u7136\u90fd\u65e0\u5fc3\u7761\u7720\uff0c\u4e00\u8def\u4e0a\u8fd8\u4e70\u4e86\u56db\u4e94\u4efd\u62a5\u7eb8\uff0c\u4e00\u62b5\u57e0\u5c31\u60f3\u5148\u5230\u8521\u56db\u5e78\u7684\u5bb6\u91cc\u53bb\u6253\u63a2\u60c5\u5f62\u3002 \u3000\u3000\u9648\u5251\u51c6\u5219\u8ba4\u4e3a\uff1a\u201c\u5148\u627e\u4e2a\u5730\u65b9\u6b47\u4e00\u6b47\uff0c\u6d17\u628a\u8138\u518d\u53bb\u3002\u201d \u3000\u3000\u725b\u4e3d\u751f\u3001\u9a86\u94c3\u3001\u53f2\u6d41\u52b3\u90fd\u5f88\u4e0d\u4ee5\u4e3a\u7136\u3002 \u3000\u3000\u201c\u53cd\u6b63\u8521\u56db\u5e78\u90fd\u5df2\u7ecf\u6b7b\u4e86\uff0c\u4eba\u6b7b\u4e0d\u80fd\u590d\u751f\uff0c\u201d\u9648\u5251\u8c01\u53ea\u597d\u8fdb\u4e00\u6b65\u89e3\u91ca\uff0c\u201c\u4eba\u5bb6\u6b63\u5728\u4f24\u5fc3\u96be\u8fc7\uff0c\u54b1\u4eec\u8981\u662f\u53bb\u5f97\u592a\u8fc7\u8d38\u7136\uff0c\u4e5f\u672a\u514d\u592a\u4ee4\u56db\u5e78\u7684\u5bb6\u4eba\u9519\u6115\u4e86\u3002\u201d \u3000\u3000\u542c\u4e86\u8fd9\u8bdd\uff0c\u53f2\u3001\u9a86\u3001\u725b\u4e09\u4eba\u624d\u4e0d\u60c5\u4e0d\u613f\u7684\uff0c\u5728\u6e29\u6587\u5e26\u9886\u4e4b\u4e0b\uff0c\u968f\u4fbf\u627e\u4e86\u5bb6\u65c5\u793e\u843d\u811a\u3002 \u3000\u3000\u6e29\u6587\u4e5f\u89c2\u5bdf\u7740\u4ed6\u4eec\uff0c\u95ee\u4eae\u7740\u773c\u775b\uff1a\u201c\u542c\u8bf4\uff0c\u4f60\u4eec\u4e4b\u4e2d\u53ea\u6709\u5251\u8c01\u5144\u89c1\u8fc7\u56db\u5e78\uff0c\u4f60\u4eec\u90fd\u672a\u89c1\u8fc7\u4ed6\uff0c\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u53f2\u6d41\u82b3\u5f88\u6709\u4e9b\u9eef\u7136\uff1a\u201c\u5509\uff0c\u7f18\u60ad\u4e00\u89c1\u3002\u201d \u3000\u3000\u6e29\u6587\u8bf4\uff1a\u201c\u96be\u5f97\uff0c\u96be\u5f97\u3002\u201d \u3000\u3000\u53f2\u6d41\u82b3\u201c\u54e6\u201d\u4e86\u4e00\u58f0\uff0c\u4e0d\u660e\u767d\u4e3a\u4ec0\u4e48\u201c\u96be\u5f97\u201d\u3002 \u3000\u3000\u201c\u8981\u662f\u4f60\u4eec\u8ddf\u56db\u5e78\u76f8\u77e5\u76f8\u719f\uff0c\u4e3a\u4ed6\u62a5\u4ec7\u81ea\u662f\u7406\u6240\u5f53\u7136\u7684\u3002\u201d\u6e29\u6587\u611f\u53f9\u7684\u8bf4\uff0c\u201c\u53ef\u662f\u4f60\u4eec\u7d20\u4e0d\u76f8\u8bc6\uff0c\u5374\u4e3a\u4ed6\u7684\u4e8b\u8fd9\u822c\u7126\u5207\u60ac\u5fc3\uff0c\u5b9e\u5728\u96be\u5f97\u3002\u201d \u3000\u3000\u201c\u8521\u5148\u751f\u8ddf\u6211\u4eec\u7684\u8001\u5927\u80a5\u9e2d\u662f\u7ed3\u4e49\u5144\u5f1f\uff0c\u6211\u4eec\u8ddf\u8001\u5927\u4e5f\u662f\u7ed3\u62dc\u5144\u5f1f\uff0c\u201d\u53f2\u6d41\u82b3\u65ad\u7136\u7684\u8bf4\uff0c\u201c\u90a3\u5c31\u7b49\u4e8e\u6211\u4eec\u8ddf\u56db\u5e78\u4e5f\u662f\u7ed3\u4e49\u5144\u5f1f\uff0c\u7ed3\u4e49\u4e49\u5144\u5f1f\u5c31\u7b97\u8bbe\u89c1\u8fc7\uff0c\u4e5f\u662f\u5144\u5f1f\uff0c\u4ed6\u7ed9\u4eba\u5bb3\u4e86\uff0c\u54b1\u4eec\u51b3\u4e0d\u80fd\u5750\u89c6\u4e0d\u7406\u7684\u3001\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u4ed6\u987f\u4e86\u987f\uff0c\u53c8\u8bf4\uff1a\u201c\u6b63\u5982\u4f60\u7684\u4e00\u4e2a\u597d\u53cb\uff1b\u66fe\u7ecf\u8ddf\u4f60\u6709\u8fc7\u4e00\u6bb5\u6b22\u4e50\u65f6\u5149\uff0c\u53ef\u662f\u4ed6\u540e\u6765\u79bb\u5f00\u4f60\u4e86\uff0c\u4e0d\u518d\u8ddf\u4f60\u5728\u4e00\u8d77\u4e86\uff0c\u53ef\u662f\u4ed6\u82e5\u9047\u7978\uff0c\u6216\u8005\u906d\u52ab\uff0c\u4f60\u603b\u4e0d\u4f1a\u8896\u624b\u65c1\u89c2\uff0c\u76f8\u5e94\u4e0d\u7406\u7684\u5427\uff1f\u201d \u3000\u3000\u9a86\u94c3\u521a\u6d17\u8fc7\u4e86\u8138\uff0c\u4e00\u5f20\u8138\u50cf\u51fa\u6c34\u7684\u8299\u84c9\u4f3c\u7684\uff0c\u53ea\u662f\u4e00\u665a\u6ca1\u597d\u597d\u7761\u8fc7\u3001\u5f88\u4e0b\u5448\u73b0\u4e86\u4e24\u62b9\u86fe\u7ffc\u4f3c\u7684\u9ed1\u773c\u888b\u3002\u5979\u5ae3\u7136\u4e00\u7b11\u8bf4\uff1a\u201c\u4eba\uff0c\u4e0d\u4e00\u5b9a\u8981\u76f8\u719f\u624d\u80fd\u76f8\u77e5\u7684\uff0c\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u6e29\u6587\u671b\u5411\u725b\u4e3d\u751f\u3002 \u3000\u3000\u81ea\u4ece\u542c\u8bf4\u8521\u56db\u5e78\u4e27\u547d\u4e4b\u540e\uff0c\u8fd9\u4e2a\u4e00\u5411\u8d2a\u7761\u7684\u5f6a\u5f62\u5927\u6c49\uff0c\u8fc4\u4eca\u4e00\u76f4\u672a\u66fe\u5408\u8fc7\u773c\u76ae\u3002 \u3000\u3000\u4ed6\u77aa\u7740\u725b\u4e00\u822c\u7684\u706b\u773c\uff0c\u53ea\u7b49\u5f85\u51fa\u53d1\u3002 \u3000\u3000\u9648\u5251\u8c01\u8f7b\u54b3\u4e86\u4e00\u58f0\uff1a\u4ed6\u7684\u62f3\u8f7b\u8f7b\u63e1\u7740\uff0c\u653e\u5728\u5507\u8fb9\u3002 \u3000\u3000\u5927\u5bb6\u90fd\u77e5\u9053\u4ed6\u8981\u8bf4\u8bdd\uff0c\u7acb\u5373\u805a\u62e2\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u201c\u6e29\u6587\u4e5f\u9760\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u201c\u54b1\u4eec\u6765\u5230\u6b64\u5730\u662f\u5ba2\uff0c\u975e\u5230\u4e07\u4e0d\u5f97\u5df2\u7684\u65f6\u5019\uff0c\u4e07\u52ff\u641e\u60f9\u662f\u975e\uff0c\u5207\u52ff\u8f7b\u6613\u51fa\u624b\u3002\u201d\u4ed6\u8bf4\u5f97\u5f88\u6162\uff0c\u53ef\u662f\u4e5f\u5f88\u6709\u529b\uff0c\u201c\u9ed1\u8272\u7684\u706b\u767d\u8272\u7684\u5973\u4eba\uff0c\u81ea\u7136\u90fd\u6709\u53e4\u602a\u3002\u6211\u4eec\u662f\u4eba\u751f\u5730\u4e0d\u719f\uff0c\u800c\u4e16\u95f4\u4e8b\u53c8\u65e0\u5947\u4e0d\u6709\uff0c\u8981\u67e5\u4e2a\u6c34\u843d\u77f3\u51fa\uff0c\u81ea\u7136\u8981\u80c6\u5927\u5fc3\u7ec6\uff0c\u8c28\u614e\u4ece\u4e8b\uff0c\u4e0d\u53ef\u6253\u8349\u60ca\u86c7\u3002\u201d \u3000\u3000\u6e29\u6587\u95ee\uff1a\u201c\u60a8\u8ba4\u4e3a\u8be5\u4ece\u4f55\u7740\u624b\u5462\uff1f\u201d \u3000\u3000\u201c\u5f20\u5c0f\u6101\u3002\u201d\u9648\u5251\u8c01\u56de\u7b54\u3002 \u3000\u3000\u201c\u4e00\uff0c\u5979\u662f\u8521\u56db\u5e78\u6b7b\u65f6\u552f\u4e00\u76ee\u51fb\u8bc1\u4eba\uff0c\u4e8c\uff0c\u5979\u662f\u56db\u5e78\u7684\u4eb2\u5bc6\u5973\u53cb\uff0c\u5979\u4e86\u89e3\u5c0f\u8521\u6bd4\u54b1\u4eec\u90fd\u591a\uff0c\u8c03\u67e5\u5e94\u4ece\u5979\u90a3\u513f\u7740\u624b\u3002\u201d\u9648\u5251\u8c01\u660e\u6670\u7684\u8bf4\uff0c\u201c\u8fd8\u6709\uff0c\u56db\u5e78\u6b7b\u540e\uff1f\u636e\u62a5\u8f7d\u5f20\u5c0f\u6101\u4e00\u76f4\u4e0d\u80af\u5411\u8bb0\u8005\u63d0\u4f9b\u4efb\u4f55\u6d88\u606f\uff0c\u542c\u8bf4\u8b66\u65b9\u4e5f\u675f\u624b\u624b\u65e0\u7b56\uff0c\u7a76\u7adf\u5979\u662f\u4e0d\u662f\u53d7\u5230\u6781\u5927\u7684\u60ca\u5413\uff0c\u8fd8\u662f\u6709\u4ec0\u4e48\u5979\u4e0d\u4fbf\u8bf4\u3001\u4e0d\u80fd\u8bf4\u7684\uff1f\u201d \u3000\u3000\u201c\u6211\u89c1\u8fc7\u5979\uff0c\u201d\u6e29\u6587\u540c\u610f\uff0c\u4f46\u4ed6\u4e5f\u5f88\u540c\u60c5\uff1a\u201c\u5c0f\u6101\u662f\u4e2a\u597d\u5973\u5b69\u3002\u201d \u3000\u3000\u201c\u6240\u4ee5\u6211\u4eec\u66f4\u5e94\u4ece\u5979\u90a3\u513f\u7740\u624b\u3002\u201d\u8fd9\u5c31\u662f\u9648\u5251\u8c01\u7684\u7ed3\u8bba\u3002 \u3000\u3000\uff12\u3001\u6253\u8349\u60ca\u868a \u3000\u3000\u5230\u4e86\u8d1d\u591a\u57e0\u8521\u5bb6\uff0c\u6b63\u597d\u8d76\u4e0a\u8521\u56db\u5e78\u7684\u5927\u6b93\u4e4b\u65e5\u3002 \u3000\u3000\u8521\u56db\u5e78\u751f\u524d\u4ea4\u6e38\u5e7f\u9614\uff0c\u4f46\u846c\u793c\u6765\u7684\u4eba\u5e76\u4e0d\u591a\uff0c\u4e3b\u8981\u7684\u539f\u56e0\uff0c\u662f\u56e0\u4e3a\u5f53\u5730\u7684\u4eba\u8ff7\u4fe1\uff1a\u7ed9\u9ed1\u706b\u70e7\u6b7b\u7684\u4eba\uff0c\u8bf4\u4e0d\u5b9a\u906d\u5929\u8c34\uff0c\u81f3\u5c11\u4e5f\u662f\u649e\u4e86\u90aa\uff0c\u8fd9\u79cd\u9709\u6c14\u8fd8\u662f\u4e0d\u8981\u53bb\u60f9\u7684\u597d\u3002 \u3000\u3000\u6240\u4ee5\u7075\u5802\u751a\u4e3a\u51b7\u6e05\u3002 \u3000\u3000\u5927\u5385\u5f53\u4e2d\uff0c\u7f6e\u653e\u4e86\u4e00\u4ed8\u68fa\u6728\uff0c\u5bff\u6728\u524d\u4e00\u5bf9\u8721\u70db\uff0c\u4e00\u4e2a\u94c1\u76ae\u6876\u662f\u62ff\u6765\u70e7\u51a5\u7eb8\u7684\uff0c\u60f3\u5230\u8fd9\u672c\u6765\u662f\u4e2a\u5e74\u8f7b\u6709\u4e3a\u7684\u5c0f\u4f19\u5b50\uff0c\u800c\u4eca\u5728\u68fa\u6728\u91cc\u4e5f\u53ea\u662f\u5177\u70e7\u7126\u7684\u5c38\u4f53\uff0c\u5c31\u4ee4\u4eba\u611f\u6168\u4e0d\u5df2\u3002 \u3000\u3000\u4ed6\u4eec\u4e00\u8e0f\u8fdb\u53bb\uff0c\u6e29\u6587\u7387\u5148\u5728\u7075\u67e9\u524d\u4e09\u97a0\u8eac\uff0c\u7136\u540e\u8f6c\u5411\u4e00\u540d\u8eab\u6750\u798f\u798f\u6cf0\u6cf0\u3001\u8138\u5b54\u5706\u5706\u6ee1\u6ee1\uff0c\u8fde\u773c\u775b\u3001\u773c\u955c\uff0c\u9f3b\u5934\u3001\u8138\u80da\u3001\u751a\u81f3\u8fde\u95e8\u7259\u3001\u51e0\u7c92\u8138\u4e0a\u7684\u9752\u6625\u75d8\u90fd\u662f\u5706\u5f62\u7684\u6c49\u5b50\uff0c\u8bf4\u4e86\u51e0\u53e5\u8bdd\u3002 \u3000\u3000\u90a3\u6c49\u5b50\u4e0d\u4f4f\u70b9\u5934\uff0c\u5411\u4ed6\u4eec\u8fd9\u8fb9\u671b\u6765\uff0c\u7136\u540e\u53c8\u53bb\u8ddf\u4e00\u5bf9\u8001\u592b\u5987\u8bf4\u4e86\u51e0\u53e5\u3002 \u3000\u3000\u9648\u5251\u8c01\u4e00\u8fdb\u5927\u95e8\uff0c\u5c31\u5728\u5385\u5185\u5dee\u4e0d\u591a\u4e8c\u5341\u4eba\u91cc\uff0c\u53d1\u73b0\u4e86\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u4e00\u4e2a\u4ee4\u4eba\u4e00\u8ddf\u770b\u53bb\uff0c\u5c31\u77e5\u9053\u4ed6\u662f\u4e2a\u5145\u6ee1\u53db\u9006\u6027\u7684\u5e74\u8f7b\u4eba\u3002 \u3000\u3000\u8fd9\u4eba\u5168\u8eab\u90fd\u5145\u6ee1\u4e86\u52b2\u548c\u529b\uff0c\u800c\u4e14\u51e0\u4e4e\u8981\u6ea2\u51fa\u6765\u4e86\uff0c\u8fd9\u8fd8\u4e0d\u80fd\u5b8c\u5168\u6292\u53d1\u4ed6\u7684\u6d3b\u529b\uff0c\u6240\u4ee5\u5c31\u8fde\u4e00\u4e2a\u773c\u795e\uff0c\u4e5f\u4eff\u4f3c\u662f\u4e00\u8bb0\u51fa\u51fb\uff0c\u5145\u65a5\u7740\u65e0\u6bd4\u7684\u52b2\u548c\u529b\u3002 \u3000\u3000\u4ed6\u6311\u8845\u7684\u671b\u8fc7\u8fd9\u8fb9\uff0c\u53ef\u662f\u53ea\u8981\u4ed4\u7ec6\u73b0\u5bdf\uff0c\u53d1\u73b0\u4ed6\u773c\u91cc\u6709\u6291\u5236\u4e0d\u4f4f\u7684\u597d\u5947\uff0c\u8fd9\u79cd\u4ee4\u4eba\u611f\u5230\u6709\u4ed6\u5728\u7684\u5730\u65b9\uff0c\u5e38\u4e8b\u4e5f\u4f1a\u53d8\u6210\u602a\u4e8b\u3002 \u3000\u3000\u9648\u89c1\u8c01\u5219\u521a\u597d\u76f8\u53cd\u3002 \u3000\u3000\u4ed6\u5411\u6765\u90fd\u662f\u4e2a\u628a\u602a\u4e8b\u5f53\u4f5c\u5e38\u4e8b\u7684\u4eba\u3002 \u3000\u3000\u4ed6\u6c14\u5b9a\u795e\u95f2\uff0c\u8d8b\u524d\u4e0a\u9999\uff0c\u97a0\u8eac\uff0c\u7136\u540e\u5782\u624b\u800c\u7acb\uff0c\u5fc3\u91cc\u53ea\u5411\u68fa\u6728\u91cc\u91cd\u590d\u7684\u5ff5\u4e00\u53e5\u8bdd\uff1a\u201c\u56db\u5949\uff0c\u6211\u4eec\u6765\u8fdf\u4e86\u4e00\u6b65\uff0c\u4f60\u5b89\u606f\u5427\uff0c\u6211\u4eec\u4e00\u5b9a\u4f1a\u4e3a\u4f60\u62a5\u4ec7\uff01\u201d \u3000\u3000\u8521\u56db\u5e78\u7684\u6bcd\u4eb2\u56e0\u4e3a\u4e27\u5b50\u4e4b\u75db\uff0c\u6ca1\u6709\u5fc3\u60c5\u8bf4\u8bdd\uff0c\u53ea\u8868\u5fc3\u60c5\u8c22\u8fc7\u8fd9\u56db\u4f4d\u8fdc\u65b9\u6765\u5ba2\u7684\u540a\u5501\u4e4b\u60c5\u3002 \u3000\u3000\u6e29\u6587\u5219\u628a\u8521\u4e09\u62e9\u62c9\u5230\u4e00\u8fb9\uff0c\u95ee\u4ed6\u8be6\u60c5\u3002 \u3000\u3000\u201c\u56db\u5e78\u662f\u600e\u4e48\u6b7b\u7684\uff1f\u201d \u3000\u3000\u201c\u4ed6\u7ed9\u706b\u70e7\u6b7b\u7684\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u706b\uff1f\u201d \u3000\u3000\u201c\u3002\u3002\u3002\u5996\u706b\u3002\u201d \u3000\u3000\u201c\u4ed6\u597d\u7aef\u7aef\u7684\uff0c\u4e3a\u4f55\u4f1a\u7ed9\u70e7\u6b7b\uff1f\u201d \u3000\u3000\u201c\u4ed6\u5f00\u8f66\u8f7d\u5f20\u5c0f\u59d0\u51fa\u53bb\uff0c\u8f66\u5b50\u5f15\u64ce\u574f\u4e86\uff0c\u505c\u5728\u90ca\u5916\uff0c\u4ed6\u4e0b\u8f66\u53bb\u4fee\u7406\uff0c\u7ed3\u679c\u2026\u2026\u201d \u3000\u3000\u201c\u5f20\u5c0f\u6101\u5462\uff1f\u201d \u3000\u3000\u201c\u3002\u3002\u3002\u3002\u3002\u3002\u201d \u3000\u3000\u201c\u5979\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u201c\u5979\u2026\u201d \u3000\u3000\u201c\u5e72\u4ec0\u4e48\u541e\u541e\u5410\u5410\u7684\uff01\u201d \u3000\u3000\u201c\u5979\u2026\u2026\u6ca1\u6709\u6765\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\uff1f\u56db\u5e78\u662f\u5979\u7684\u672a\u5a5a\u592b\u2026\u2026\u5927\u6b93\u4e4b\u65e5\u5979\u90fd\u4e0d\u6765\uff01\u201d \u3000\u3000\u201c\u4e5f\u8bb8\uff0c\u201d\u8521\u4e09\u62e9\u8f6f\u5f31\u7684\u8bf4\uff0c\u201c\u4e5f\u8bb8\u5979\u662f\u6709\u82e6\u8877\u7684\u5427\u3002\u201d \u3000\u3000\u201c\u5979\u5927\u8fc7\u4efd\u4e86\uff01\u201d\u6e29\u6587\u5fff\u5fff\u7684\u8bf4\uff0c\u201c\u4e0d\u884c\uff0c\u6211\u4eec\u8981\u53bb\u95ee\u95ee\u5979\u3002\u201d \u3000\u3000\u201c\u7b97\u4e86\u5427\uff0c\u522b\u60f9\u4e8b\u4e86\u3002\u201d\u8521\u4e09\u62e9\u8fde\u5fd9\u529d\u963b\u3002 \u3000\u3000\u201c\u5979\u4f4f\u5728\u54ea\u91cc\uff1f\u201d\u6e29\u6587\u4e00\u70b9\u4e5f\u4e0d\u6e29\u6587\u4e86\uff0c\u800c\u4e14\u8fd8\u5f88\u7d27\u8feb\u903c\u4eba\u3002 \u3000\u3000\u3002 \u3000\u3000\u201c\u5979\u2026\u2026\u201d\u8521\u4e09\u62e9\u513f\u4e4e\u662f\u5728\u6073\u6c42\uff0c\u201c\u5979\u6709\u4e2a\u5f88\u51f6\u7684\u54e5\u54e5\u5440\uff0c\u4f60\u8fd8\u662f\u522b\u751f\u4e8b\u597d\u5417\uff01\u201d \u3000\u3000\u53f2\u6d41\u82b3\u53eb\u4e86\u8d77\u6765\uff1a\u201c\u4f60\u4e5f\u662f\u8521\u56db\u5e78\u7684\u54e5\u54e5\u5440\uff01\u201d \u3000\u3000\u201c\u53ef\u662f\u2026\u2026\u201d\u8521\u4e09\u62e9\u82e6\u7740\u8138\u8bf4\uff1a\u201c\u56db\u5e78\u7684\u4e0d\u5e78\uff0c\u8ddf\u9ed1\u706b\u6709\u5173\uff0c\u542c\u8bf4\uff0c\u6709\u5996\u5b7d\u7f20\u8eab\uff0c\u624d\u4f1a\u6cbe\u4e0a\u9ed1\u706b\u2026\u2026\u6211\u4eec\u5c31\u66f4\u4e0d\u80fd\u5f97\u7f6a\u2018\u7ea2\u6bdb\u62ff\u7763\u2019\u7684\u4eba\u554a\uff01\u201d \u3000\u3000\u201c\u201c\u7ea2\u6bdb\u62ff\u7763\u201d\u53f2\u6d41\u82b3\u83ab\u540d\u5176\u5999\uff0c\u201c\u4ec0\u4e48\u7ea2\u6bdb\u62ff\u7763\uff1f\u201d \u3000\u3000\u8521\u4e09\u62e9\u663e\u7136\u4e0d\u60f3\u591a\u8bf4\u3002 \u3000\u3000\u521a\u5de7\u54fa\u4f6c\u53c8\u8981\u5ff5\u7ecf\u8d85\u6e21\uff0c\u8521\u4e09\u62e9\u8fde\u5fd9\u8d76\u56de\u53bb\u52a0\u5165\u884c\u5217\u3002 \u3000\u3000\u9a86\u94c3\u5374\u628a\u4ed6\u62e6\u4e86\u4e00\u62e6\uff0c\u707f\u4eae\u4eae\u7684\u4e00\u7b11\uff0c\u201c\u8521\u5148\u751f\uff0c\u6211\u6709\u4e00\u53e5\u820c\u8981\u544a\u8bc9\u4f60\u3002\u201d \u3000\u3000\u2014\u2014\u773c\u524d\u662f\u90a3\u4e48\u6f02\u4eae\u7684\u5973\u5b69\u5b50\uff0c\u8c01\u4e5f\u4e0d\u4f1a\u4ecb\u610f\u542c\u53e5\u8bdd\u7684\u3002 \u3000\u3000\u201c\u6211\u73b0\u5728\u77e5\u9053\u4f60\u4e3a\u4ec0\u4e48\u8fde\u6697\u75ae\u90fd\u662f\u5706\u7684\u4e86\uff0c\u201d\u9a86\u94c3\u8bf4\uff0c\u201c\u56e0\u4e3a\u4f60\u7684\u4eba\u592a\u5706\u4e86\u2014\u2014\u6bd4\u5728\u8349\u573a\u4e0a\u7ed9\u4eba\u8e22\u7684\u7403\u8fd8\u5706\uff01\u201d \u3000\u3000\u201c\u4f60\u4e0d\u544a\u8bc9\u6211\u4eec\u5f20\u5c0f\u59d0\u7684\u4f4f\u5740\uff0c\u201d\u9a86\u94c3\u626c\u957f\u800c\u53bb\uff0c\u201c\u6211\u4eec\u8fd8\u662f\u4e00\u6837\u4f1a\u627e\u5f97\u5230\u3002\u201d \u3000\u3000\u4ed6\u4eec\u679c\u7136\u627e\u5230\u4e86\u3002 \u3000\u3000\u2014\u2014\u8fd9\u5c71\u57ce\u672c\u6765\u5c31\u4e0d\u5927\uff0c\u53ea\u6709\u4e0d\u8fc7\u51e0\u5343\u6237\u4eba\u5bb6\uff0c\u5f20\u5c0f\u6101\u53c8\u662f\u8fd9\u513f\u6781\u4e3a\u51fa\u8272\u7684\u5973\u5b50\uff0c\u4e00\u6253\u63a2\u4fbf\u77e5\u9053\u4e86\u3002 \u3000\u3000\u5f20\u5c0f\u6101\u5f80\u7684\u5730\u65b9\uff0c\u662f\u81ea\u5927\u8857\u8f6c\u5165\u9ec4\u6ce5\u9053\u4e0a\u3002\u9053\u65c1\u5c3d\u662f\u78a7\u8349\uff0c\u4f46\u56e0\u4e3a\u5e38\u6709\u4eba\u884c\uff0c\u4e5f\u4e0d\u7b97\u9ad8\uff0c\u6709\u51e0\u4e2a\u6811\u5934\uff0c\u663e\u7136\u662f\u88ab\u780d\u8fc7\u7684\uff0c\u4e5f\u6709\u4e00\u6bb5\u65f6\u65f6\u65e5\u4e86\u3002\u6811\u5934\u90fd\u6210\u4e86\u6e7f\u9ed1\u8272\uff0c\u5176\u4e2d\u4e5f\u6709\u4e00\u4e24\u68f5\u957f\u51fa\u4e86\u5ae9\u7eff\u7684\u65b0\u82bd\uff0c\u6709\u51e0\u68f5\u767d\u5170\u82b1\u6811\uff0c\u9e64\u7acb\u9e21\u7fa4\u7684\u5c79\u7acb\u7740\uff0c\u98ce\u4e00\u6765\u65f6\uff0c\u82b1\u5927\u5982\u624b\uff0c\u65cb\u5440\u8f6c\u5440\u7684\u98d8\u843d\u4e0b\u6765\uff0c\u6709\u4e00\u6735\u8fd8\u6253\u5728\u6e29\u6587\u7684\u5934\u4e0a\uff0c\u6e29\u6587\u5927\u53eb\u4e86\u4e00\u58f0\uff0c\u201c\u597d\u75db\uff01\u201d \u3000\u3000\u9a86\u94c3\u8bf4\u5634\uff1a\u201c\u6ca1\u60c5\u8da3\uff01\u201d \u3000\u3000\u4ed6\u4eec\u624b\u4e0a\u7684\u5730\u5740\u662f\u201cLOT\u5341\u4e03\u4e00\u4e09\u53f7\u201d\u3002\u4ed6\u4eec\u627e\u5230\u4e86\u201c\u5341\u4e03\u4e00\u4e00\u53f7\u201d\uff0c\u4e5f\u5bfb\u5230\u4e86\u4e00\u5341\u516d\u4e00\u4e8c\u53f7\u201d\uff0c\u518d\u627e\u4e0b\u53bb\uff0c\u4f7f\u662f\u201c\u5341\u516d\u4e00\u4e94\u53f7\u201d\uff0c\u5c31\u662f\u6ca1\u6709\u4e09\u53f7\u3002 \u3000\u3000\u4ed6\u4eec\u5927\u5947\u3002 \u3000\u3000\u201c\u5988\u7684\uff0c\u6211\u5c31\u4e0d\u4fe1\u627e\u4e0d\u5230\uff0c\u201d\u53f2\u6d41\u82b3\u548c\u6e29\u6587\u4e24\u4eba\u90fd\u4e0d\u4fe1\u90aa\uff0c\u4f46\u518d\u6765\u7684\u95e8\u724c\u53f7\u7801\uff0c\u5df2\u5230\u4e86\u5341\u516b\uff0c\u5341\u4e5d\u53f7\u4e86\u3002 \u3000\u3000\u725b\u4e3d\u751f\u5ffd\u201c\u554a\u201d\u7684\u53eb\u4e86\u4e00\u58f0\uff0c\u4f38\u624b\u4e00\u6307\uff0c\u4f17\u4eba\u671b\u53bb\uff0c\u53ea\u89c1\u4e00\u9635\u7729\u76ee\uff0c\u6b63\u5bf9\u7740\u81ea\u5c4b\u6a90\u540e\u4f0f\u7740\u7684\u65e5\u5149\uff0c\u707f\u4eae\u5f97\u4ee4\u4ed6\u4eec\u4e00\u65f6\u7741\u4e0d\u5f00\u773c\u6765\u3002 \u3000\u3000\u539f\u6765\u773c\u524d\u662f\u4e00\u5927\u680b\u5e99\u5b87\u3002\u4e5f\u4e0d\u7b97\u5927\uff0c\u4f46\u70df\u96fe\u8ff7\u6f2b\uff0c\u770b\u6765\u9999\u706b\u5f88\u76db\u3002 \u3000\u3000\u5728\u9633\u5149\u4e0b\uff0c\u5ffd\u89c1\u4e00\u95f4\u534a\u4ee5\u9752\u74e6\u7ea7\u7816\u3001\u534a\u4ee5\u6c34\u6ce5\u6d0b\u7070\u3001\u534a\u4e2d\u4e0d\u897f\u7684\u5e99\u5b87\u7acb\u5728\u90a3\u513f\uff0c\u4e0d\u514d\u6709\u4e9b\u7a81\u5140\u7684\u611f\u89c9\u3002 \u3000\u3000\u5728\u5e99\u5b87\u9644\u8fd1\uff0c\u7070\u70ec\u56db\u98d8\uff0c\u9999\u5ba2\u6781\u591a\uff0c\u800c\u4e14\u5f88\u6709\u70b9\u4e0d\u5bfb\u5e38\u3002 \u3000\u3000\u4e00\u5bb6\u5e99\u5b87\u5982\u679c\u76f8\u4f20\u7075\u9a8c\uff0c\u4eba\u4eec\u7ade\u76f8\u8d70\u544a\uff0c\u6765\u8fdb\u9999\u7684\u5584\u7537\u4fe1\u5973\u5fc5\u7136\u66f4\u591a\uff0c\u672c\u6765\u5c31\u662f\u5e38\u4e8b\uff0c\u5c31\u50cf\u9999\u6e2f\u7684\u8f66\u516c\u5e99\u3001\u9ec4\u5927\u4ed9\u7960\u3001\u5929\u540e\u5e99\uff0c\u6bcf\u5f53\u795e\u8bde\u8fc7\u8282\uff0c\u671d\u62dc\u7684\u9999\u5ba2\u5f97\u8981\u8fdc\u4ece\u5e99\u95e8\u6392\u5230\u8857\u4e0a\u53bb\uff0c\u7edd\u4e0d\u7a00\u5947\u3002\u81f3\u4e8e\u4e2d\u56fd\u5927\u9646\u7684\u4e00\u4e9b\u540d\u5bfa\u53e4\u5239\uff0c\u9999\u706b\u9f0e\u76db\u66f4\u4e0d\u5728\u8bdd\u4e0b\u3002\u5947\u7684\u662f\u8fd9\u95f4\u5bfa\u5e99\u5ffd\u751f\u5750\u843d\u5728\u8fd9\u5bfb\u5e38\u767e\u59d3\u5df7\u5f04\u91cc\uff0c\u9999\u5ba2\u76f4\u4ece\u540e\u95e8\u9c7c\u8d2f\u6392\u5217\u51fa\u4e00\u6761\u957f\u86c7\u9635\u6765\uff0c\u8db3\u6709\u4e09\u5341\u7801\u957f\uff0c\u76f4\u6392\u5230\u8352\u5730\u8349\u4e1b\u7684\u7a84\u9053\u4e0a\u53bb\uff0c\u81f3\u5c11\u4e5f\u6709\u4e8c\u4e09\u767e\u4eba\u5728\u8f6e\u6d41\u7740\uff0c\u4f46\u66f4\u4e3a\u5f02\u5e38\u7684\u662f\uff0c\u5e73\u5e38\u6700\u5435\u95f9\u4e0d\u4f11\u7684\u9999\u5ba2\uff0c\u5230\u4e86\u8fd9\u91cc\uff0c\u7adf\u4e1d\u6beb\u4e0d\u6562\u55a7\u56a3\uff0c\u6bd4\u5728\u654c\u519b\u7684\u523a\u5200\u4e0b\u5217\u961f\u641c\u8eab\u7684\u65f6\u5019\u8fd8\u8981\u8083\u9759\u3002 \u3000\u3000\u6240\u4ee5\uff0c\u51e0\u767e\u4eba\u7ad9\u5728\u90a3\u91cc\uff0c\u7adf\u9759\u5f97\u50cf\u4e00\u4e2a\u4eba\u5728\u90a3\u513f\u6253\u9968\u513f\u4e00\u6837\uff0c\u4e5f\u8bb8\u4e0d\u540c\u7684\u53ea\u662f\uff1a\u8fde\u6253\u5428\u7684\u58f0\u97f3\u4e5f\u4e0d\u53ef\u95fb\u3002 \u3000\u3000\u9a86\u94c3\u4e00\u5411\u53e3\u6ca1\u906e\u62e6\uff0c\u51b2\u53e3\u4fbf\u8bf4\uff0c\u201c\u54ce\u3001\u600e\u4e48\u7adf\u6709\u4e00\u5927\u5806\u4eba\u5728\u8fd9\u91cc\uff0c\u5413\u4e86\u6211\u4e00\u8df3\u3002\u201d \u3000\u3000\u53f2\u6d41\u82b3\u597d\u5947\u4ed6\u8bf4\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\u5e99\uff1f\u600e\u4e48\u9759\u5f97\u8fd9\u6837\u5b50\uff0c\u4e0d\u662f\u7ed9\u96c6\u4f53\u50ac\u7720\u4e86\u5427\uff1f\u201d \u3000\u3000\u9648\u5251\u8c01\u6ca1\u6709\u8bf4\u8bdd\uff0c\u53cc\u7709\u7d27\u76b1\uff0c\u81f4\u4f7f\u989d\u4e0a\u7ad6\u8d77\u4e00\u9053\u60ac\u9488\u4f3c\u7684\u76f4\u7eb9\uff0c\u50cf\u81ea\u5370\u5802\u95f4\u653e\u4e00\u628a\u98de\u5251\u5230\u989d\u4e0a\u53bb\u4e86\u3002 \u3000\u3000\u4ed6\u7528\u624b\u6307\u4e86\u6307\u3002 \u3000\u3000\u5927\u5bb6\u90fd\u770b\u5230\u5e99\u95e8\u4e0a\u7684\u6a2a\u533e\u3002 \u3000\u3000\u201c\u7ea2\u6bdb\u62ff\u7763\u2026\u2026\u201d\u9a86\u94c3\u8fb9\u5ff5\u8fb9\u8bf4\uff0c\u201c\u54ea\u6709\u8fd9\u822c\u53e4\u602a\u7684\u5bfa\u540d\u2026\u2026\u201d \u3000\u3000\u9648\u5251\u8c01\u6e05\u4e86\u6e05\u5589\u5499\uff0c\u8bf4\uff1a\u201c\u8fd9\u662f\u4e00\u5ea7\u5e99\u3002\u201d \u3000\u3000\u53f2\u6d41\u82b3\u7b11\u7740\u8bf4\uff0c\u201c\u8fd9\u5f53\u7136\u4e0d\u662f\u5496\u5561\u5e97\u3002\u201d \u3000\u3000\u9648\u5251\u8c01\u5e76\u4e0d\u6b23\u8d4f\u4ed6\u7684\u5e7d\u9ed8\uff0c\u201c\u800c\u4e14\u8fd9\u5ea7\u5e99\u7684\u95e8\u724c\u5c31\u662f\u5341\u516d\u4e00\u4e09\u53f7\u3002\u201d \u3000\u3000\u5927\u5bb6\u90fd\u201c\u554a\u201d\u4e86\u4e00\u58f0\uff0c\u7559\u610f\u4e4b\u4e0b\uff0c\u8fd9\u624d\u53d1\u73b0\u5728\u95e8\u4e0a\u786e\u6302\u4e86\u4e00\u5757\u88ab\u9999\u706b\u718f\u5f97\u7070\u7070\u9ed1\u9ed1\u7684\u95e8\u724c\u53f7\u7801\u3002 \u3000\u3000\u5e99\u4e5f\u6709\u95e8\u724c\u53f7\u7801\uff0c\u5c31\u50cf\u603b\u7edf\u4e5f\u6709\u8eab\u4efd\u8bc1\u3001\u6709\u7fc5\u8180\u7684\u8759\u8760\u4e5f\u662f\u9f20\u7c7b\u7684\u9053\u7406\u4e00\u6837\u3002 \u3000\u3000\u9648\u5251\u8c01\u95ee\uff1a\u201c\u521a\u624d\u662f\u8c01\u95ee\u7684\u8def\uff1f\u201d \u3000\u3000\u6e29\u6587\u8bf4\uff1a\u201c\u6211\uff01\u201d \u3000\u3000\u8fd9\u51e0\u4eba\u91cc\uff0c\u8981\u7b97\u4ed6\u662f\u201c\u5730\u5934\u201d\uff0c\u6253\u63a2\u95ee\u8def\u7684\u4e8b\uff1a\u81ea\u7136\u8be5\u4ed6\u8d1f\u8d23\u3002 \u3000\u3000\u9648\u5251\u8c01\u95ee\uff1a\u201c\u4f60\u662f\u5411\u8c01\u95ee\u7684\u8def\uff1f\u201d \u3000\u3000\u6e29\u6587\u8bf4\uff1a\u201c\u5176\u5b9e\u6211\u5e76\u6ca1\u6709\u95ee\u4eba\uff0c\u662f\u4eba\u95ee\u6211\u7684\u3002\u201d \u3000\u3000\u201c\u600e\u4e48\u8bf4\uff1f\u201d \u3000\u3000\u201c\u521a\u624d\u5728\u8521\u5bb6\u7075\u5802\u524d\uff0c\u8521\u4e09\u62e9\u4e0d\u80af\u5410\u9732\uff0c\u5927\u5bb6\u60bb\u60bb\u79bb\u5f00\u7684\u65f6\u5019\uff0c\u5c31\u6709\u4e00\u4e2a\u9752\u5e74\u8fc7\u6765\u95ee\u6211\uff0c\u662f\u4e0d\u662f\u8981\u627e\u5f20\u5c0f\u6101\u7684\u5730\u5740\uff1f\u6211\u8bf4\u662f\uff0c\u4ed6\u8bf4\u8fd9\u4e0d\u96be\u627e\u5440\uff0c\u5c31\u7ed9\u4e86\u6211\u8fd9\u4e2a\u5730\u5740\u3002\u201d \u3000\u3000\u53f2\u6d41\u82b3\u563f\u58f0\u8bf4\uff1a\u201c\u6211\u8fd8\u4ee5\u4e3a\u662f\u4f60\u6253\u63a2\u51fa\u6765\u7684\u5462\uff0c\u539f\u6765\u662f\u4eba\u4e3b\u52a8\u63d0\u4f9b\u7684\uff0c\u54b1\u4eec\u8fd9\u56de\u53ef\u7ed9\u4eba\u5bb6\u6dae\u4e86\uff0c\u81ea\u8dd1\u4e86\u4e00\u6545\u51a4\u6789\u8def\u3002\u201d \u3000\u3000\u9648\u5251\u8c01\u8bf4\uff1a\u201c\u4e0d\u662f\u51a4\u6789\u8def\u3002\u201d \u3000\u3000\u53f2\u6d41\u82b3\u8bf4\uff1a\u201c\u96be\u9053\u5f20\u5c0f\u6101\u4f4f\u5728\u5e99\u91cc\u4e0d\u6210\uff1f\u201d \u3000\u3000\u9a86\u94c3\u7684\u8054\u60f3\u529b\u53ef\u66f4\u5feb\u4e00\u6b65\uff1a\u201c\u83ab\u975e\u5f20\u5c0f\u6101\u60b2\u4f24\u8fc7\u5ea6\uff0c\u770b\u7834\u7ea2\u5c18\uff0c\u6765\u8fd9\u513f\u51fa\u5bb6\u5f53\u5c3c\u59d1\u4e86\uff1f\u201d \u3000\u3000\u201c\u8fd9\u90fd\u6709\u53ef\u80fd\uff0c\u4e0d\u8fc7\uff0c\u4e0d\u4f1a\u662f\u51a4\u6789\u8def\u3002\u201d\u9648\u5251\u8c01\u8bf4\uff1a\u201c\u90a3\u4eba\u8981\u7684\uff0c\u4fbf\u662f\u8981\u4f60\u5e26\u5927\u5bb6\u6765\u8fd9\u513f\u3002\u201d \u3000\u3000\u201c\u6765\u8fd9\u91cc\uff1f\u201d\u9a86\u94c3\u4ecd\u662f\u4e0d\u89e3\uff0c\u201c\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u770b\uff01\u201d\u9648\u5251\u8c01\u8bf4\uff1a\u201c\u770b\u8fd9\u5ea7\u5e99\u3002\u201d \u3000\u3000\u201c\u7ea2\u6bdb\u62ff\u7763\u3002\u201d \u3000\u3000\u5927\u5bb6\u671b\u7740\u8fd9\u5728\u9633\u5149\u4e2d\u6d6e\u5347\u7740\u70df\u96fe\u7684\u5e99\u5b87\uff0c\u4eff\u4f5b\u7f69\u4e0a\u4e86\u4e00\u5c42\u7eb1\u5e55\uff0c\u66f4\u52a0\u795e\u79d8\u3002 \u3000\u3000\u6e29\u6587\u6709\u70b9\u5fff\u5fff\uff1a\u201c\u4ed6\u5e72\u5565\u8981\u54c4\u54b1\u4eec\u6765\u8fd9\u91cc\uff1f\u201d \u3000\u3000\u9664\u5251\u8c01\u95ee\uff1a\u201c\u521a\u624d\u628a\u5730\u5740\u544a\u8bc9\u4f60\u7684\u4eba\uff0c\u662f\u4e0d\u662f\u4e00\u4e2a\u957f\u6709\u4e00\u53cc\u6d53\u7709\u3001\u5f88\u6587\u6c14\u3001\u5f88\u6c89\u7740\u3001\u98a7\u4e0a\u6709\u4e00\u9897\u7070\u75e3\u3001\u75e3\u4e0a\u8fd8\u957f\u4e86\u4e00\u4e1b\u6bdb\u7684\u9752\u5e74\u4eba\uff1f\u201d \u3000\u3000\u201c\u5bf9\u5440\uff0c\u8981\u4e0d\u662f\u4ed6\u957f\u5f97\u8fd8\u50cf\u5f88\u7a33\u91cd\u7684\u6837\u5b50\uff0c\u6211\u53c8\u600e\u4f1a\u76f8\u4fe1\u4ed6\u7684\u8bdd\u5462\uff1f\uff0c\u6e29\u6587\u8bf4\u4e86\u4e00\u534a\uff0c\u5ffd\u7136\u60f3\u8d77\uff0c\u201c\u6696\u3001\u4f60\u600e\u4e48\u90a3\u4e48\u6e05\u695a\u4ed6\u7684\u957f\u76f8\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u4ed6\u6765\u4e86\u3002\u201d\u9648\u5251\u8c01\u8bf4\uff1a\u201c\u4f60\u8981\u95ee\u7684\u8bdd\uff0c\u4f55\u4e0d\u95ee\u4ed6\u53bb\uff01\u201d \u3000\u3000\u6765\u7684\u4eba\u53ef\u4ee5\u7528\u201c\u7cbe\u660e\u5f3a\u5e72\uff0c\u8c26\u606d\u6709\u793c\u201d\u516b\u4e2a\u5b57\u5f62\u5bb9\u3002 \u3000\u3000\u8fd9\u4eba\u4e00\u770b\u5c31\u77e5\u9053\u4ed6\u806a\u660e\uff0c\u800c\u4e14\u6709\u89c1\u8bc6\uff0c\u5e76\u4e14\u5728\u793e\u4f1a\u4e0a\u7684\u5730\u4f4d\u4e00\u5b9a\u4e0d\u4f4e\u3002 \u3000\u3000\u2014\u4e2a\u4eba\u7684\u624d\u5b66\u6c14\u5668\uff0c\u53ea\u8981\u8c08\u4e0a\u51e0\u53e5\u8bdd\uff0c\u5c31\u4e00\u5b9a\u53ef\u4ee5\u5206\u8fa8\u5f97\u51fa\u6765\u3002 \u3000\u3000\u96be\u5f97\u7684\u662f\u8fd9\u4eba\u5e74\u8f7b\uff0c\u5b8c\u5168\u6ca1\u6709\u9a84\u6c14\u3002 \u3000\u3000\u53ea\u6709\u4e00\u70b9\u70b9\u9634\u6c14\u3002 \u3000\u3000\u2014\u2014\u8fd9\u4e48\u4e00\u4e2a\u82f1\u4f1f\u9752\u5e74\uff0c\u5374\u4e0d\u662f\u7ed9\u4eba\u201c\u4f1f\u7537\u5b50\u201d\u7684\u611f\u89c9\uff0c\u53cd\u800c\u4ee4\u4eba\u89c9\u5f97\u6709\u201c\u5a18\u5a18\u8154\u201d\u7684\u5370\u8c61\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u8fd9\u4eba\u7684\u4e3e\u6b62\u867d\u6709\u70b9\u201c\u5973\u6027\u5316\u201c\uff0c\u4f46\u8bf4\u8bdd\u5374\u5f88\u76f4\u63a5\uff0c\u201c\u6211\u59d3\u6bdb\uff0c\u540d\u5ff5\u884c\uff0c\u5bf9\u4e0d\u8d77\uff0c\u662f\u6211\u628a\u4f60\u4eec\u5f15\u6765\u8fd9\u513f\u7684\u3002\u201d \u3000\u3000\u6e29\u6587\u6e29\u6587\u5730\u8bf4\uff1a\u201c\u4e0d\u8981\u7d27\uff0c\u4e0d\u8981\u7d27\u3002\u201d \u3000\u3000\u9648\u5251\u8c01\u5411\u725b\u4e3d\u751f\u626c\u4e86\u626c\u5de6\u7709\u3002 \u3000\u3000\u725b\u4e3d\u751f\u5ffd\u7136\u5927\u6012\uff0c\u731b\u5411\u524d\u9677\u4e86\u4e00\u6b65\uff0c\u201c\u4f60\u5230\u5e95\u6709\u4ec0\u4e48\u4f01\u56fe\uff1f\u201d \u3000\u3000\u6bdb\u5ff5\u884c\u4e86\u65e0\u60e7\u8272\uff0c\u53ea\u6de1\u5b9a\u7684\u8bf4\uff1a\u201c\u6211\u60f3\u8981\u4f60\u4eec\u770b\u770b\u8fd9\u5ea7\u5e99\u3002\u201d \u3000\u3000\u5927\u5bb6\u90fd\u7981\u4e0d\u4f4f\u5411\u9648\u5251\u8c01\u671b\u53bb\u3002 \u3000\u3000\u9648\u5251\u8c01\u4e5f\u6162\u6761\u65af\u7406\u7684\u8bf4\uff1a\u201c\u6211\u4eec\u770b\u4e86\uff0c\u4f60\u8981\u6211\u4eec\u8fdb\u53bb\u770b\u5417\uff1f\u201d \u3000\u3000\u6bdb\u5ff5\u884c\u7b11\u8bf4\uff0c\u201c\u5404\u4f4d\u8bf7\u968f\u610f\u3002\u201d \u3000\u3000\u53f2\u6d41\u82b3\u63d2\u4e86\u4e00\u53e5\uff1a\u201c\u6211\u4eec\u662f\u6765\u627e\u5f20\u5c0f\u6101\uff0c\u4e0d\u662f\u6765\u62dc\u795e\u3002\u201d \u3000\u3000\u6bdb\u5ff5\u884c\u8bf4\uff1a\u201c\u5404\u4f4d\u4e3a\u4f55\u8981\u627e\u5f20\u5c0f\u6101\uff1f\u201d\u8bf4\u5b8c\uff0c\u6d41\u76ee\u671b\u5411\u6bcf\u4e00\u4e2a\u4eba\uff0c\u773c\u8272\u90fd\u5728\u5404\u4eba\u9762\u4e0a\u6253\u4e86\u4e00\u4e2a\u79cb\u6ce2\u3002 \u3000\u3000\u53f2\u6d41\u82b3\u5f3a\u786c\u5730\u9053\uff1a\u201c\u8fd9\u4e0d\u5173\u4f60\u4e8b\u3002\u201d \u3000\u3000\u6bdb\u5ff5\u884c\u795e\u8272\u4e0d\u53d8\uff1a\u201c\u6211\u77e5\u9053\u5404\u4f4d\u4e3a\u5565\u627e\u5f20\u5c0f\u6101\u3002\u201d \u3000\u3000\u9648\u5251\u8c01\u8bf4\uff1a\u201c\u4f60\u8bf4\u8bf4\u770b\u3002\u201d \u3000\u3000\u201c\u662f\u4e3a\u4e86\u8521\u56db\u5e78\u60e8\u6b7b\u7684\u4e8b\uff0c\u201d\u6bdb\u5ff5\u884c\u72e1\u8bc8\u5730\u8bf4\u3002 \u3000\u3000\u53f2\u6d41\u82b3\u770b\u4e0d\u60ef\u5bf9\u65b9\u7684\u795e\u8272\uff1a\u201c\u90a3\u4e5f\u6ca1\u4f60\u7684\u4e8b\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u8521\u56db\u5e78\u7684\u6b7b\u8ddf\u9ed1\u706b\u6709\u5173\u3002\u201d\u6bdb\u5ff5\u884c\u7acb\u5373\u9053\u3002 \u3000\u3000\u9648\u5251\u8c01\u7d27\u63a5\u7740\u95ee\uff1a\u201c\u90a3\u4e48\u8bf4\uff1a\u4f60\u4e5f\u8ddf\u9ed1\u706b\u6709\u5173\u4e86\uff1f\u201d \u3000\u3000\u201c\u4e0d\uff0c\u4e0d\u662f\u6211\u3002\u201d\u6bdb\u5ff5\u884c\u773c\u8272\u5411\u5e99\u90a3\u513f\u793a\u610f\u5730\u8f6c\u4e86\u4e00\u4e0b\uff1a\u201c\u8fd9\u5ea7\u5e99\u3002\u201d \u3000\u3000\u53f2\u6d41\u82b3\u5947\u9053\uff1a\u201c\u8fd9\u5ea7\u5e99\u8ddf\u9ed1\u706b\u6709\u4ec0\u4e48\u5173\u7cfb\uff1f\u201d \u3000\u3000\u9648\u5251\u8c01\u8c08\u7136\u8bf4\uff0c\u201c\u4f60\u65e2\u7136\u5f15\u6211\u4eec\u6765\u4e86\uff0c\u5c31\u4e00\u5b9a\u4f1a\u544a\u8bc9\u6211\u4eec\u4e00\u4e9b\u4e8b\u7684\uff0c\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u6bdb\u5ff5\u884c\u7b11\u8bf4\uff1a\u201c\u8bf8\u4f4d\u4e0d\u89c9\u5f97\u8fd9\u5ea7\u5e99\u5f88\u6709\u70b9\u2026\u90a3\u4e2a\u5417\uff1f\u201d \u3000\u3000\u9a86\u94c3\u542c\u4e0d\u60ef\u8fd9\u79cd\u201c\u6666\u6da9\u201d\u8bed\u8a00\uff1a\u201c\u90a3\u4e2a\u662f\u54ea\u4e2a\uff1f\u201d \u3000\u3000\u6bdb\u5ff5\u884c\u5e72\u7b11\u4e86\u4e24\u58f0\uff0c\u201c\u4e0d\u662f\u6709\u70b9\u2018\u602a\u5f02\u2019\u5417\uff1f\u201d \u3000\u3000\u201c\u5bf9\u5440\uff01\u201d\u53f2\u6d41\u82b3\u7acb\u5373\u8868\u793a\u4ed6\u65e9\u5c31\u89c9\u5bdf\u51fa\u6765\u4e86\u201d\u521a\u624d\u6211\u5c31\u8bf4\u4e86\uff0c\u8fd9\u5e99\u6709\u70b9\u602a\u602a\u7684\uff0c\u4e0a\u9999\u671d\u62dc\u7684\u4eba\u90fd\u4f3c\u88ab\u96c6\u4f53\u50ac\u7720\u4e86\u3002\u201d \u3000\u3000\u6bdb\u5ff5\u884c\u8bf4\uff1a\u201c\u597d\u89c2\u5bdf\u529b\u5440\uff01\u201d \u3000\u3000\u53f2\u6d41\u82b3\u7acb\u65f6\u9ad8\u5174\u4e86\u8d77\u6765\uff0c\u5bf9\u773c\u524d\u7684\u4eba\u5370\u8c61\u4e5f\u5927\u4e3a\u6539\u89c2\u3002 \u3000\u3000\u201c\u9999\u5ba2\u81ea\u7136\u90fd\u662f\u4fe1\u5949\u624d\u6765\uff0c\u4e5f\u6ca1\u4ec0\u4e48\u53ef\u602a\u7684\u3002\u201d\u9648\u5251\u8c01\u8bf4\uff1a\u201c\u4e0d\u77e5\u9ed1\u706b\u8ddf\u8fd9\u513f\u6709\u4ec0\u4e48\u5173\u7cfb\uff1f\u201d \u3000\u3000\u6bdb\u5ff5\u884c\u538b\u4f4e\u58f0\u97f3\uff0c\u795e\u795e\u79d8\u79d8\u4ed6\u8bf4\uff1a\u201c\u8bf8\u4f4d\u53ef\u77e5\u9053\u8fd9\u4e9b\u4eba\u90fd\u662f\u6765\u6c42\u62dc\u4e9b\u4ec0\u4e48\u7684\uff1f\u201d \u3000\u3000\u9648\u5251\u8c01\u5e72\u8106\u76f4\u622a\u5730\u95ee\uff1a\u201c\u6c42\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u6c42\u7b26\u3002\u201d \u3000\u3000\u201c\u7b26\uff1f\u201d \u3000\u3000\u201c\u7834\u89e3\u9ed1\u706b\u7684\u7b26\u5492\uff01\u201d \u3000\u3000\u201c\u54e6\uff1f\u201d \u3000\u3000\u201c\u4f60\u4eec\u60f3\u77e5\u9053\u4ed4\u7ec6\uff0c\u8fd9\u513f\u8bf4\u8bdd\u4e0d\u65b9\u4fbf\uff0c\u201d\u6bdb\u5ff5\u884c\u56db\u987e\u8bf4\uff1a\u201c\u8bf7\u8ddf\u6211\u6765\u3002\u201d \u3000\u3000\u9648\u5251\u8c01\u5728\u5927\u5bb6\u542f\u6b65\u8ddf\u6bdb\u5ff5\u884c\u800c\u8d70\u4e4b\u9645\uff0c\u5728\u53f2\u6d41\u82b3\u8033\u8fb9\u4f4e\u58f0\u8bf4\uff1a\u201c\u711a\u5316\u5854\u65c1\u7684\u90a3\u4e2a\u4eba\uff0c\u662f\u4e0d\u662f\u5728\u56db\u5e78\u7075\u5802\u524d\u90a3\u4e2a\u50cf\u8c79\u5b50\u4e00\u822c\u7684\u5e74\u8f7b\u4eba\uff1f\u201d \u3000\u3000\u53f2\u6d41\u82b3\u671b\u53bb\u7684\u65f6\u5019\uff0c\u90a3\u711a\u70e7\u94c5\u5b9d\u7528\u7684\u77f3\u5854\u65c1\uff0c\u53ea\u6709\u7eb8\u7070\u98d8\u98de\uff0c\u5854\u540e\u5df1\u4e0d\u89c1\u4e86\u521a\u624d\u4e00\u76f4\u90fd\u5728\u90a3\u513f\u7684\u7070\u8272\u5f71\u5b50\u3002 \u3000\u3000\u4ed6\u4eec\u5728\u8857\u53e3\u7684\u4e00\u5ea7\u5496\u5561\u644a\u5750\u4e0b\u6765\u3002 \u3000\u3000\u8fd9\u513f\u7684\u5496\u5561\u5e97\u5f53\u7136\u6ca1\u4ec0\u4e48\u96c5\u5ea7\uff0c\u4e00\u5207\u90fd\u4ee5\u7ecf\u6d4e\u5ec9\u5b9c\u4e3a\u51c6\u5219\uff0c\u5341\u5206\u7b80\u964b\uff0c\u94c1\u950c\u5c4b\u9876\u3001\u94c1\u76ae\u51f3\u5b50\uff0c\u4f46\u89c6\u91ce\u5341\u5206\u5bbd\u5e7f\uff0c\u5750\u5728\u8fd9\u513f\u4e58\u51c9\u804a\u5929\u559d\u5496\u5561\uff0c\u4e5f\u522b\u6709\u610f\u8da3\u3002 \u3000\u3000\u4ed6\u4eec\u5404\u53eb\u4e86\u4e00\u676f\u5496\u5561\u3002 \u3000\u3000\u53f2\u6d41\u82b3\u5477\u4e86\u4e00\u53e3\uff0c\u5c31\u56b7\u9053\uff1a\u201c\u592a\u8f9b\u82e6\u4e86\u3002\u201d \u3000\u3000\u9a86\u94c3\u4e00\u542c\uff0c\u5c31\u731b\u5f80\u676f\u91cc\u4e0b\u7cd6\u3002 \u3000\u3000\u6e29\u6587\u6ef4\u5495\u4ed6\u8bf4\uff1a\u201c\u5496\u5561\u672c\u6765\u8981\u8fd9\u6837\u624d\u591f\u539f\u5473\u5440\uff01\u201d \u3000\u3000\u725b\u4e3d\u751f\u559d\u5496\u5561\uff0c\u4e00\u4ef0\u9888\u5b50\u559d\u4e00\u676f\uff0c\u7136\u540e\u53c8\u53eb\u4e86\u4e00\u676f\u3002\u53c8\u662f\u4e00\u53e3\u6c14\u5e72\u5b8c\uff0c\u518d\u53eb\u4e00\u676f\uff0c\u770b\u4ed6\u7684\u6837\u5b50\uff0c\u559d\u5496\u5561\u8ddf\u559d\u9152\u3001\u559d\u6c34\u6ca1\u4e48\u5dee\u522b\u3002 \u3000\u3000\u6bdb\u5ff5\u884c\u4e3a\u4e4b\u77a0\u76ee\uff1a\u201c\u8fd9\u4f4d\u2026\u5f88\u559c\u6b22\u559d\u5496\u5561\u5427\uff1f\u201d \u3000\u3000\u9a86\u94c3\u6ca1\u597d\u6c14\u7684\u8bf4\uff1a\u201c\u522b\u7ba1\u4ed6\uff0c\u4ed6\u559d\u4ec0\u4e48\u90fd\u4e00\u6837\uff0c\u4e00\u559d\u5c31\u662f\u5927\u6876\u7684\u3002\u201d \u3000\u3000\u9648\u5251\u8c01\u8bf4\uff0c\u201c\u4f60\u7684\u8bdd\uff0c\u5728\u8fd9\u91cc\u5e94\u8be5\u6ca1\u4ec0\u4e48\u4e0d\u65b9\u4fbf\u8bf4\u4e86\u5427\uff1f\u201d \u3000\u3000\u201c\u5b9e\u4e0d\u76f8\u7792\uff0c\u5728\u8fd9\u4e00\u5e26\u51e0\u4e2a\u5e02\u9547\u91cc\uff0c\u65e0\u8bba\u8bf4\u4ec0\u4e48\u8bdd\uff0c\u53ea\u8981\u5173\u7cfb\u5230\u2018\u7ea2\u6bdb\u62ff\u7763\u2019\uff0c\u90fd\u662f\u4e0d\u4fbf\u8bf4\u7684\u3002\u201d\u6bdb\u5ff5\u884c\u611f\u6168\u7684\u9053\uff1a\u201c\u4f46\u6211\u5df2\u7ecf\u8c41\u51fa\u53bb\u4e86\u3002\u201d \u3000\u3000\u5927\u5bb6\u89c1\u4ed6\u8bed\u6c14\u51dd\u91cd\uff0c\u90fd\u9759\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u201c\u6211\u662f\u8521\u56db\u5e78\u7684\u597d\u670b\u53cb\uff0c\u6211\u542c\u4ed6\u63d0\u8fc7\u4f60\u4eec\uff0c\u5bf9\u4f60\u4eec\u7684\u5404\u79cd\u4e8b\u8ff9\uff0c\u90fd\u5f88\u656c\u4ef0\u2026\u201d \u3000\u3000\u9648\u5251\u8c01\u989d\u4e0a\u53c8\u60ac\u8d77\u4e86\u4e00\u652f\u9488\uff1a\u201c\u5ba2\u6c14\u8bdd\u90fd\u53ef\u4ee5\u4e0d\u5fc5\u8bf4\u4e86\u3002\u201d \u3000\u3000\u4ed6\u8865\u5145\u4e86\u4e00\u53e5\uff1a\u201c\u5982\u679c\u56db\u5e78\u6b7b\u5f97\u51a4\uff0c\u6211\u4eec\u662f\u4ed6\u7684\u670b\u53cb\uff0c\u5e94\u8be5\u4e3a\u4ed6\u62a5\u4ec7\u624d\u5bf9\u3002\u201d \u3000\u3000\u201c\u5bf9\u3002\u201d\u6bdb\u5ff5\u884c\u4e00\u62cd\u684c\u5b50\uff0c\u201c\u6240\u4ee5\u6211\u8bf7\u4f60\u4eec\u6765\u3002\u201d \u3000\u3000\u201c\u4e0d\u662f\u8bf7\uff0c\u201d\u6e29\u6587\u4ecd\u6709\u4f59\u5fff\uff0c\u201c\u800c\u662f\u9a97\u3002\u201d \u3000\u3000\u201c\u597d\u4e86\uff0c\u201d\u9648\u5251\u8c01\u963b\u6b62\u9053\uff1a\u201c\u90a3\u4e9b\u4e8b\u5c31\u522b\u63d0\u4e86\uff0c\u9ed1\u706b\u548c\u7ea2\u6bdb\u62ff\u7763\u6709\u4ec0\u4e48\u5173\u7cfb\uff1f\u201d \u3000\u3000\u201c\u4f60\u4eec\u53ef\u77e5\u9053\u8fd9\u513f\u53d1\u751f\u4e86\u9ed1\u706b\u4e8b\u4ef6\uff0c\u8c01\u6700\u83b7\u5229\uff1f\u201d \u3000\u3000\u6bdb\u5ff5\u884c\u5148\u95ee\u7136\u540e\u81ea\u884c\u8bf4\u51fa\u7b54\u6848\uff1a \u3000\u3000\u201c\u7ea2\u6bdb\u62ff\u7763\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u53f2\u6d41\u82b3\u548c\u6e29\u6587\u90fd\u95ee\u3002 \u3000\u3000\u201c\u56e0\u4e3a\u636e\u8bf4\u53ea\u6709\u7ea2\u6bdb\u62ff\u7763\u7684\u7075\u7b26\u3001\u9e92\u9e9f\u3001\u846b\u82a6\u548c\u795e\u50cf\uff0c\u624d\u53ef\u4ee5\u8f9f\u9547\u9ed1\u706b\u7684\u5996\u90aa\uff0c\u53ea\u6709\u5230\u7ea2\u6bdb\u62ff\u7763\u5e99\u91cc\u4e0a\u8fc7\u9999\uff0c\u624d\u53ef\u4ee5\u4e0d\u4e3a\u90a3\u767d\u8272\u7684\u5973\u9b3c\u6240\u60d1\uff0c\u6b65\u4e0a\u5669\u8fd0\u3002\u6240\u4ee5\u4eba\u4eba\u90fd\u5230\u7ea2\u6bdb\u62ff\u7763\u90a3\u513f\uff0c\u4e0d\u60dc\u9ad8\u4ef7\u6c42\u7075\u7b26\u3001\u795e\u724c\u7684\u4fdd\u4f51\uff0c\u542c\u8bf4\u62ff\u7763\u795e\u4e0d\u559c\u6b22\u4eba\u58f0\u55a7\u54c4\uff0c\u6765\u62dc\u795e\u6c42\u7b26\u7684\u4eba\u4fbf\u5927\u6c14\u90fd\u4e0d\u6562\u5410\uff0c\u7b49\u4e09\u5929\u4e24\u591c\u4e5f\u8981\u5728\u8fd9\u91cc\u9886\u4e00\u9053\u7b26\u3001\u62dc\u4e00\u62dc\u795e\u624d\u80fd\u653e\u5fc3\u79bb\u53bb\u3002\u201d \u3000\u3000\u201c\u6709\u8fd9\u79cd\u4e8b\uff01\u201d\u53f2\u6d41\u82b3\u6709\u70b9\u4e0d\u6562\u7f6e\u4fe1\u3002 \u3000\u3000\u201c\u8ff7\u4fe1\uff01\u8fd9\u662f\u9a86\u94c3\u7684\u76f4\u63a5\u53cd\u5e94\u3002 \u3000\u3000\u201c\u5f53\u7136\u662f\u8ff7\u4fe1\uff0c\u4f46\u7edd\u5bf9\u662f\u6709\u8fd9\u79cd\u4e8b\uff01\u201d\u6bdb\u5ff5\u884c\u5206\u8fa8\u8bf4\uff1a\u201c\u6709\u51e0\u4e2a\u4eba\u66fe\u78b0\u4e0a\u9ed1\u706b\u800c\u52ab\u540e\u4f59\u751f\u7684\uff0c\u4ed6\u4eec\u90fd\u6709\u7ea2\u6bdb\u62ff\u7763\u5e99\u6c42\u5f97\u7684\u7075\u7b26\uff0c\u81f3\u5c11\uff0c\u4e5f\u662f\u5728\u6700\u8fd1\u53bb\u5e99\u91cc\u62dc\u8fc7\u795e\u4e0a\u8fc7\u9999\u7684\uff0c\u8fd9\u6837\u4e00\u6765\uff0c\u53ea\u8981\u5165\u5e99\u5c31\u6709\u53ef\u80fd\u5f97\u514d\u4e8e\u52ab\uff0c\u8c01\u4e0d\u552f\u6050\u540e\u4eba\u4e00\u6b65\uff0c\u5927\u5bb6\u90fd\u53bb\u4e86\uff0c\u8c01\u6562\u4e0d\u53bb\uff1f\u201d \u3000\u3000\u9648\u5251\u8c01\u4e00\u76f4\u5728\u542c\uff0c\u6ca1\u6709\u4f5c\u58f0\uff0c\u7136\u540e\u624d\u95ee\uff1a\u201c\u5f20\u5c0f\u6101\u8eab\u4e0a\u4e5f\u6709\u7075\u7b26\uff1f\u201d \u3000\u3000\u6bdb\u5ff5\u884c\u8bf4\uff1a\u201c\u662f\u5440\uff01\u201d \u3000\u3000\u201c\u6240\u4ee5\uff0c\u4f60\u6000\u7591\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u4e0d\u6562\u6000\u7591\uff0c\u6000\u7591\u4e24\u4e2a\u5b57\uff0c\u4e5f\u4eb5\u8bfb\u795e\u660e\u3002\u201d\u4ed6\u89e3\u5f00\u80f8\u524d\u7b2c\u4e00\u7c92\u9492\u6263\uff0c\u638f\u51fa\u4e00\u6761\u5df2\u892a\u4e86\u8272\u7684\u94f6\u94fe\uff0c\u94fe\u7aef\u7cfb\u7740\u4e00\u5ea7\u4f5b\u724c\uff0c\u201c\u4f60\u770b\uff0c\u8fde\u6211\u4e5f\u514d\u4e0d\u4e86\uff0c\u6234\u7740\u603b\u662f\u5fc3\u5b89\u3002\u201d \u3000\u3000\u201c\u90a3\u4e48\uff0c\u4f60\u53ea\u662f\u63d0\u793a\u6211\u4eec\uff1a\u51e1\u662f\u7ea2\u6bdb\u62ff\u7763\u5e99\u7684\u4fe1\u5f92\uff0c\u5c31\u80fd\u514d\u5374\u9ed1\u706b\u7684\u7978\u5bb3\uff0c\u56e0\u800c\uff0c\u9ed1\u706b\u4e8b\u4ef6\u7684\u6700\u5927\u5f97\u5229\u8005\uff0c\u4fbf\u662f\u7ea2\u6bdb\u62ff\u7763\u5e99\uff1f\u201d\u9648\u5251\u8c01\u4ecd\u7d27\u76ef\u4e0d\u653e\u3002 \u3000\u3000\u201c\u53ef\u4ee5\u2026\u2026\u8fd9\u6837\u8bf4\u3002\u201d\u6bdb\u5ff5\u884c\u6709\u70b9\u72b9\u8c6b\uff0c\u63a5\u7740\u4ed6\u53c8\u63d0\u4f9b\u4e86\u4e00\u4e2a\u4e8b\u5b9e\uff0c\u201c\u5728\u9ed1\u706b\u8086\u5a01\u4e4b\u524d\uff0c\u7ea2\u6bdb\u62ff\u7763\u9999\u706b\u7a00\u5c11\uff0c\u95e8\u5ead\u51b7\u843d\u3002\u201d \u3000\u3000\u201c\u6211\u660e\u767d\u4e86\u3002\u201d\u9648\u5251\u8c01\u7684\u773c\u795e\uff0c\u50cf\u624b\u7535\u7b52\u4e00\u822c\u7684\u5b9a\u5728\u6bdb\u5ff5\u884c\u7684\u8138\u4e0a\uff1a\u201c\u591a\u8c22\u4f60\u63d0\u4f9b\u7684\u6d88\u606f\u3002\u7ea2\u6bdb\u62ff\u7763\u4e00\u5b9a\u6709\u4e0d\u5c11\u4fe1\u5f92\uff0c\u4f60\u6765\u544a\u8bc9\u6211\u4eec\u8fd9\u4e9b\u4e5f\u771f\u8981\u5192\u4e0a\u70b9\u5371\u9669\u3002\u201d \u3000\u3000\u7136\u540e\u8bf4\uff0c\u201c\u6211\u4eec\u8fd8\u8981\u4f60\u63d0\u4f9b\u4e00\u4e2a\u6d88\u606f\u3002\u201d \u3000\u3000\u6bdb\u5ff5\u884c\u8bf4\uff1a\u201c\u5f53\u5c3d\u6240\u80fd\u3002\u201d \u3000\u3000\u201c\u5f20\u5c0f\u6101\u4f4f\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u201c\u8fd9\u91cc\u3002\u201d \u3000\u3000\u201c\u8fd9\u91cc\uff1f\u201d \u3000\u3000\u201c\u5176\u5b9e\u5c31\u5728\u5e99\u7684\u9694\u58c1\uff0c\u5341\u516d\u4e00\u4e09\u53f7\u3002\u201d \u3000\u3000\u201c\u8c22\u8c22\u3002\u201d \u3000\u3000\u201c\u8981\u6211\u5148\u901a\u77e5\u5f20\u5c0f\u59d0\uff1f\u201d\u6bdb\u5ff5\u884c\u81ea\u544a\u594b\u52c7\u7684\u8bf4\uff0c\u201d\u6211\u662f\u5979\u7684\u597d\u670b\u53cb\uff0c\u5979\u5f53\u6211\u662f\u5979\u7684\u5144\u957f\u4e00\u6837\u3002\u201d \u3000\u3000\u201c\u6211\u4eec\u4e0d\u60f3\u6253\u8349\u60ca\u86c7\u3002\u201d\u9648\u5251\u8c01\u65ad\u7136\u8bf4\uff1a\u201c\u4f60\u662f\u672c\u5730\u4eba\uff0c\u592a\u9732\u8138\u5bb9\u6613\u7275\u8fde\u8fdb\u53bb\u3002\u201d \u3000\u3000\u201c\u6211\u4e0d\u6015\u7275\u8fde\uff0c\u4e0d\u8fc7\u8bf4\u5b9e\u5728\u7684\uff0c\u6211\u4e5f\u76f8\u5f53\u76f8\u4fe1\u7ea2\u6bdb\u62ff\u7763\u7684\u795e\u9a8c\u3002\u201d\u6bdb\u5ff5\u884c\u5ffd\u7136\u60f3\u8d77\u4ec0\u4e48\u4f3c\u7684\u8bf4\uff0c\u201c\u8fd8\u6709\uff0c\u6211\u5f97\u63d0\u9192\u4f60\u4eec\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u201c\u8bf7\u8bf4\u3002\u201d \u3000\u3000\u201c\u5c0f\u5fc3\u987e\u5f71\u3002\u201d \u3000\u3000\u201c\u987e\u2026\u2026\u201d\u725b\u4e3d\u4e3b\u6293\u4e0d\u8c01\u4e0b\u4e00\u4e2a\u5b57\u97f3\u3002 \u3000\u3000\u201c\u5f71\uff1f\u201d\u53f2\u6d41\u82b3\u662f\u542c\u5230\u4e86\uff0c\u4f46\u4e5f\u5f88\u6000\u7591\u3002 \u3000\u3000\u201c\u4f60\u8bf4\u7684\u662f\u90a3\u4e2a\u8bd7\u4e0e\u529f\u592b\u5408\u4e00\u7684\u987e\u5f71\uff1f\u201d\u6e29\u6587\u53eb\u4e86\u8d77\u6765\uff0c\u201c\u4ed6\u5728\u8fd9\u91cc\uff1f\u201d \u3000\u3000\u6bdb\u5ff5\u884c\u70b9\u5934\u3002 \u3000\u3000\u6e29\u6587\u4ea2\u594b\u5730\u8bf4\uff1a\u201c\u4ed6\u600e\u4e48\u4e86\uff1f\u201d \u3000\u3000\u201c\u4ed6\u662f\u4e00\u76f4\u90fd\u8ffd\u6c42\u5f20\u5c0f\u6101\u7684\u4eba\u3002\u201d\u6bdb\u5ff5\u884c\u8bf4\uff0c\u201d\u800c\u4e14\uff0c\u4ed6\u5c31\u662f\u7ea2\u6bdb\u62ff\u7763\u7684\u5c11\u4e3b\u4eba\uff0c\u4ed6\u8001\u7238\u987e\u6b65\u5c31\u662f\u5e99\u91cc\u7684\u4e3b\u6301\uff0c\u4e00\u5207\u7075\u7b26\u3001\u795e\u724c\u90fd\u662f\u7531\u4ed6\u8001\u7238\u53d1\u51fa\u6765\u7684\u3002\u201d \u3000\u3000\u53f2\u6d41\u82b3\u8865\u5145\u8bf4\uff1a\u201c\u5f53\u7136\uff0c\u4f60\u7684\u610f\u601d\u662f\uff1a\u949e\u7968\u4e5f\u662f\u4ed6\u6536\u7684\u4e86\uff1f\u201d \u3000\u3000\u6bdb\u5ff5\u884c\u8038\u80a9\u8bf4\uff0c\u201c\u987e\u5f71\u6b66\u529f\u9ad8\u5f3a\uff0c\u4eba\u591a\u52bf\u4f17\uff0c\u5e74\u8f7b\u4eba\u90fd\u542c\u4ed6\u7684\u6307\u6325\u3002\u4ed6\u5f53\u5f20\u5c0f\u59d0\u662f\u5b9d\uff0c\u4e0d\u8bb8\u522b\u4eba\u63a5\u8fd1\uff0c\u7ed3\u679c\u7ed9\u56db\u5e78\u8d62\u5f97\u4e86\u82b3\u5fc3\uff0c\u6211\u77e5\u9053\u4ed6\u5bf9\u8fd9\u4ef6\u4e8b\u5f88\u662f\u4e0d\u5fff\u3002\u201d \u3000\u3000\u201c\u6211\u4eec\u73b0\u5728\u90fd\u660e\u81ea\u4f60\u7684\u610f\u601d\u4e86\u3002\u201d\u9648\u5251\u8c01\u8bf4\uff0c\u201c\u6211\u4eec\u4f1a\u5c0f\u5fc3\u7684\u3002\u201d \u3000\u3000\u201c\u5982\u679c\u6ca1\u6709\u5fc5\u8981\uff0c\u6700\u597d\u4e0d\u8981\u5f97\u7f6a\u987e\u5f71\u3002\u201d\u6bdb\u5ff5\u884c\u4ecd\u4e0d\u653e\u5fc3\uff0c\u201c\u4ed6\u5f88\u5389\u5bb3\u3002\u5f53\u7136\uff0c\u4e0d\u78b0\u987e\u5f71\uff0c\u53ea\u8981\u4e0d\u6cbe\u5f20\u5c0f\u6101\u5c31\u5f97\u4e86\u3002\u201d \u3000\u3000\u9648\u5251\u8c01\u7b11\u4e86\uff0c\u201c\u8981\u662f\u8fd9\u6837\uff0c\u6211\u4eec\u8fd8\u6765\u8fd9\u91cc\u4e8e\u4ec0\u4e48\uff1f\u62dc\u796d\u8fc7\u4e86\u56db\u5e78\uff0c\u5c31\u8be5\u56de\u53bb\u4e86\u3002\u201d \u3000\u3000\u5927\u5bb6\u90fd\u660e\u767d\u9648\u5251\u8c01\u7684\u610f\u601d\u3002 \u3000\u3000\u6bdb\u5ff5\u884c\u7559\u4e0b\u4e86\u8054\u7edc\u7535\u8bdd\uff0c\u8d70\u4e86\u3002 \u3000\u3000\u9648\u5251\u8c01\u95ee\u6e29\u6587\uff1a\u201c\u987e\u5f71\u662f\u4f60\u7684\u670b\u53cb\uff1f\u201d \u3000\u3000\u201c\u4e0d\u662f\uff0c\u4ed6\u7684\u8bd7\u5728\u8fd9\u513f\u5f88\u6709\u540d\uff0c\u4ed6\u5199\u5f97\u5f88\u6709\u7985\u5473\uff0c\u6211\u6709\u65f6\u90fd\u770b\u4e0d\u61c2\u3002\u201d\u6e29\u6587\u8bf4\uff0c\u201c\u542c\u8bf4\u4ed6\u7684\u6b66\u529f\u4e5f\u5f88\u597d\uff0c\u5341\u516d\u4e03\u5c81\u94c1\u5f53\u4e0a\u526f\u6559\u5934\u4e86\uff0c\u73b0\u5728\u66f4\u4e0d\u5f97\u4e86\uff0c\u51e0\u5c4a\u56fd\u672f\u6bd4\u8d5b\u4ed6\u90fd\u8fdb\u5165\u4e86\u4e09\u7532\uff0c\u76f4\u5230\u53bb\u5e74\u4e3a\u6b62\u3002\u201d \u3000\u3000\u201c\u54c8\uff01\u5c0f\u65f6\u4e86\u4e86\uff01\u201d\u9a86\u94c3\u7b11\u8bf4\uff0c\u201c\u73b0\u5728\u53ef\u88ab\u6dd8\u6c70\u51fa\u5c40\u4e86\u5427\uff01\u201d \u3000\u3000\u201c\u4e0d\uff0c\u201d\u6e29\u6587\u8bf4\uff0c\u53bb\u5e74\u5f00\u59cb\uff0c\u4ed6\u5f53\u4e86\u8bc4\u5ba1\u3002\u201d \u3000\u3000\u4ed6\u4f38\u4e86\u4f38\u820c\u5934\uff0c\u597d\u5389\u5bb3\uff0c\u624d\u4e0d\u8fc7\u4e8c\u5341\u51e0\u5c81\uff0c\u5c31\u5f53\u4e86\u6559\u5934\u548c\u8bc4\u5ba1\u3002\u201d \u3000\u3000\u9a86\u94c3\u767b\u65f6\u6c89\u4e86\u8138\uff1a\u201c\u4ed6\u5f88\u5389\u5bb3\u5417\uff1f\u6709\u7a7a\uff0c\u6211\u5012\u8981\u4f1a\u4f1a\u3002\u201d \u3000\u3000\u201c\u7b97\u4e86\uff0c\u6211\u4eec\u4e0d\u662f\u6765\u62f3\u6253\u752b\u5317\u82f1\u96c4\u5c3d\u60f9\u4e8b\u7684\u3002\u201d\u9648\u5251\u8c01\u8bf4\uff0c\u201c\u6211\u4eec\u662f\u6765\u5f04\u6e05\u695a\u56db\u5e78\u662f\u600e\u4e48\u6b7b\u7684\u3002\u201d \u3000\u3000\u201c\u53e6\u5916\uff0c\u521a\u624d\u5728\u711a\u5316\u5854\u65c1\u6709\u4eba\u4e00\u76f4\u5728\u6ce8\u89c6\u6211\u4eec\uff0c\u6211\u8ba4\u5f97\u51fa\u6765\uff0c\u4ed6\u5c31\u662f\u521a\u624d\u5728\u7075\u5802\u524d\u50cf\u4e00\u5934\u8c79\u5b50\u4f3c\u7684\u5e74\u8f7b\u4eba\u3002\u201d\u9648\u5251\u8c01\u8865\u5145\u9053\uff1a\u201c\u4ed6\u5927\u6982\u5c31\u662f\u987e\u5f71\u3002\u201d \u3000\u3000\u201c\u597d\u5f00\u5fc3\uff0c\u6211\u7ec8\u4e8e\u8ddf\u987e\u5f71\u78b0\u9762\u4e86\u3002\u201d\u6e29\u6587\u4ecd\u5728\u5174\u594b\u4e2d\uff0c\u201c\u672c\u5730\u4e24\u5927\u56fd\u9645\u6587\u8c6a\u7ec8\u4e8e\u4f1a\u9762\u4e86\u3002\u201d \u3000\u3000\u201c\u56fd\u9645\u6587\u8c6a\uff1f\u201d\u9a86\u94c3\u8001\u5b9e\u4e0d\u5ba2\u6c14\u7684\u8bf4\uff0c\u201c\u6211\u5728\u5916\u5730\u8fd8\u5f79\u542c\u8bf4\u8fc7\u6709\u4f60\u8fd9\u4e00\u53f7\u4eba\u7269\u5462\uff01\u201d \u3000\u3000\u4ed6\u4eec\u53bb\u62dc\u8bbf\u5f20\u5c0f\u6101\u3002 \u3000\u3000\u4ed6\u4eec\u51e0\u4e4e\u8fdb\u4e0d\u53bb\u3002 \u3000\u3000\u56e0\u4e3a\u5f20\u5c0f\u6101\u7684\u54e5\u54e5\u4e0d\u8bb8\u4ed6\u4eec\u8fdb\u53bb\u3002 \u3000\u3000\u4ed6\u4ee5\u4e3a\u4ed6\u4eec\u662f\u8bb0\u8005\uff0c \u3000\u3000\u5f53\u53f2\u6d41\u82b3\u7b49\u4eba\u8868\u660e\u4e86\u8eab\u4efd\u3001\u5f20\u5c0f\u6101\u7684\u54e5\u54e5\u66f4\u4e0d\u8bb8\u4ed6\u4eec\u8fdb\u5165\u3002 \u3000\u3000\u201c\u6211\u6ca1\u542c\u8521\u56db\u5e78\u63d0\u8fc7\u4f60\u4eec\uff0c\u600e\u77e5\u9053\u4f60\u4eec\u4e0d\u662f\u767d\u649e\u7684\uff1f\u201d\u5f20\u5c0f\u6101\u7684\u54e5\u54e5\u5f20\u8bde\u6267\u610f\u4e0d\u80af\uff0c\u4e14\u81ea\u4ee5\u4e3a\u806a\u660e\uff1a\u201c\u6211\u59b9\u59b9\u8fd1\u65e5\u5fc3\u60c5\u5f88\u574f\uff0c\u8c01\u90fd\u4e0d\u89c1\u3002\u201d \u3000\u3000\u8fd9\u65f6\u5019\u6e29\u6587\u7ad9\u51fa\u6765\u4e86\u3002 \u3000\u3000\u8fd9\u65f6\u5019\uff0c\u4ed6\u4eec\u624d\u4e86\u89e3\u6e29\u6587\u7684\u672c\u9886\u3002 \u3000\u3000\u6e29\u6587\u81ea\u63a5\u673a\u5f00\u59cb\uff0c\u65e0\u8bba\u9a7e\u8f66\uff0c\u5b89\u6392\u4f4f\u5bbf\u3001\u81b3\u98df\uff0c\u627e\u4eba\uff0c\u5747\u4e0d\u89c1\u7cbe\u660e\uff0c\u4f46\u4fdf\u4ed6\u5411\u5f20\u8bde\u5c55\u52a8\u4e09\u5bf8\u4e0d\u70c2\u4e4b\u820c\uff0c\u5343\u65b9\u8bf4\u670d\u3001\u767e\u8ba1\u8bf4\u52a8\u3001\u8f6f\u786c\u517c\u65bd\u4e4b\u9645\uff0c\u65f6\u626e\u5c0f\u4e11\uff0c\u65f6\u4f5c\u89e3\u4eba\uff0c\u65f6\u4f6f\u6012\u72b6\uff0c\u771f\u662f\u5343\u53d8\u4e07\u5316\uff0c\u597d\u4eba\u6076\u4eba\u574f\u4eba\u5584\u4eba\u5c0f\u4eba\u751a\u81f3\u8fde\u4e0d\u662f\u4eba\u4ed6\u90fd\u4e00\u624b\u5305\u529e\uff0c\u4f7f\u5f97\u9648\u5251\u6dee\u7b49\u4eba\u53f9\u4e3a\u89c2\u6b62\uff0c\u4e3a\u4e4b\u77a0\u76ee\u3002 \u3000\u3000\u5f20\u8bde\u7ec8\u4e8e\u88ab\u8f6f\u5316\u3002 \u3000\u3000\u201c\u53ea\u4e00\u4f1a\u513f\u3002\u201d\u5f20\u8bde\u72b9\u8c6b\u3002 \u3000\u3000\u201c\u4e00\u4f1a\u513f\u5c31\u597d\u4e86\u3002\u201d \u3000\u3000\u201c\u4e0d\u4f1a\u89c1\u62a5\uff1f\u201d \u3000\u3000\u201c\u6211\u4eec\u53c8\u4e0d\u662f\u8bb0\u8005\uff0c\u89c1\u62a5\u5e72\u5417\uff1f\u201d \u3000\u3000\u201c\u90a3\u4e48\u2026\u2026\u201d \u3000\u3000\u201c\u8c22\u8c22\u3002\u201d \u3000\u3000\u201c\u4e0d\uff0c\u201d\u5f20\u8bde\u8bf4\uff0c\u201c\u6211\u53bb\u95ee\u59b9\u59b9\u5979\u613f\u4e0d\u613f\u89c1\u4f60\u4eec\u518d\u8bf4\u3002\u201d \u3000\u3000\u5f20\u8bde\u8f6c\u5934\u8d70\u4e86\u8fdb\u53bb\u4e86\uff0c\u623f\u95e8\u5e03\u5e18\u4e00\u9635\u8f7b\u6447\u3002 \u3000\u3000\u9648\u5251\u8c01\u6e38\u76ee\u56db\u987e\uff0c\u76ee\u5149\u843d\u5728\u6728\u677f\u5899\u4e0a\u63a5\u7740\u7684\u4e00\u5e45\u5de8\u578b\u6d77\u62a5\u6708\u5386\u4e0a\uff0c\u6708\u5386\u5973\u90ce\u662f\u8fd1\u5341\u5e74\u6765\u4e00\u76f4\u90fd\u7ea2\u5f97\u53d1\u7d2b\u7684\u5973\u661f\uff0c\u5f88\u82f1\u723d\u5730\u5fae\u7b11\u7740\uff0c\u6709\u51e0\u5206\u4fca\u6c14\u5e05\u6c14\uff0c\u96be\u602a\u5979\u4f1a\u7ea2\u5f97\u90a3\u4e48\u4e45\u4e86\uff0c\u539f\u6765\u7f8e\u5230\u4e86\u6781\u81f4\uff0c\u4fbf\u6210\u4e86\u4e2d\u6027\uff0c\u9633\u6027\u7684\u82f1\u6717\u548c\u5973\u6027\u7684\u67d4\u4e3d\u90fd\u517c\u800c\u6709\u4e4b\u3002\u9648\u5251\u8c01\u60f3\u3002 \u3000\u3000\u7136\u540e\u4ed6\u53d1\u73b0\u90a3\u6708\u5386\u4e0a\uff0c\u5176\u4e2d\u4e00\u4e2a\u65e5\u671f\uff0c\u88ab\u7ea2\u7b14\u5708\u4e86\u4e00\u5708\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u623f\u95e8\u7684\u5e03\u5e18\u53c8\u4e00\u9635\u6447\u6643\u3002 \u3000\u3000\u51fa\u6765\u7684\u4e0d\u6b62\u662f\u5f20\u8bde\u3002 \u3000\u3000\u8fd8\u6709\u5f20\u8bde\u7684\u7236\u6bcd\u3002 \u3000\u3000\u552f\u72ec\u6ca1\u6709\u5f20\u5c0f\u6101\u3002 \u3000\u3000\u5f20\u5c0f\u6101\u7236\u6bcd\u5e74\u7eaa\u90fd\u5f88\u5927\u4e86\uff0c\u6574\u5f20\u8138\u5c31\u50cf\u662f\u706b\u8e81\u813e\u6c14\u4f46\u504f\u53c8\u5199\u4e0d\u51fa\u4e1c\u897f\u6765\u7684\u4f5c\u5bb6\u6240\u4e22\u5f03\u7684\u7a3f\u7eb8\uff0c\u5e03\u6ee1\u4e86\u7eb5\u6a2a\u4ea4\u9519\u7684\u8def\u8f68\u822c\u7684\u76b1\u7eb9\u3002\u76b1\u7eb9\u5728\u4e24\u5f20\u8001\u4eba\u7684\u8138\u4e0a\uff0c\u5404\u5e03\u5947\u5175\uff0c\u4f46\u8109\u7edc\u5404\u4e0d\u76f8\u8fd1\uff0c\u53ea\u6709\u4e00\u70b9\u662f\u76f8\u540c\u7684\uff0c\u4ed6\u4eec\u7684\u5507\u89d2\u90fd\u5f80\u4e0b\u62d7\u3002 \u3000\u3000\u8fd9\u4e00\u70b9\uff0c\u663e\u793a\u4e86\u4ed6\u4eec\u63a5\u8fd1\u5b64\u72ec\u7684\u5014\u5f3a\u3002 \u3000\u3000\u6e29\u6587\u54a7\u5f00\u4e86\u4e00\u4e2a\u7b11\u5bb9\uff0c\u6bd4\u6708\u997c\u9985\u8fd8\u751c\u7684\uff0c\u8bf4\uff1a\u201c\u4f60\u4eec\u597d\uff0c\u6211\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u597d\u3002\u201d\u8001\u4eba\u7b54\uff1a\u201c\u8c01\u90fd\u4e0d\u53ef\u4ee5\u9a9a\u6270\u5c0f\u6101\u3002\u201d \u3000\u3000\u8001\u5a46\u5a46\u7acb\u5373\u652f\u6301\u8001\u516c\uff1a\u201c\u8c01\u6765\u6253\u6270\u5c0f\u6101\u90fd\u4e0d\u597d\u3002\u201d \u3000\u3000\u2014\u2014\u770b\u6765\uff0c\u6e29\u6587\u7684\u90a3\u4e00\u5f20\u53e3\u5230\u6b64\u5df2\u82f1\u96c4\u65e0\u7528\u6b66\u4e4b\u5730\u4e86\u3002 \u3000\u3000\u5374\u5728\u8fd9\u65f6\u5019\uff0c\u4ece\u5916\u9762\u8d70\u8fdb\u6765\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u5979\u7ad9\u5728\u95e8\u53e3\uff0c\u4e00\u6643\u8eab\u8d70\u4e86\u8fdb\u6765\uff0c\u95e8\u5916\u7684\u9633\u5149\u6697\u4e86\u4e00\u6697\uff0c\u7136\u540e\u5ffd\u7136\u5728\u5c4b\u91cc\u4eae\u4e86\u8d77\u6765\uff0c\u53ef\u80fd\u7531\u4e8e\u5c4b\u91cc\u672c\u6765\u662f\u6697\u51c9\u7684\u539f\u6545\u5427\uff0c\u4ea6\u4eae\u4e3d\u4e5f\u662f\u67d4\u548c\u7684\uff0c\u4ee4\u4eba\u4ee5\u4e3a\u5979\u628a\u9633\u5149\u4e5f\u5e26\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u6e29\u6587\u559c\u547c\uff1a\u201c\u5c0f\u6101\u3002\u201d \u3000\u3000\u5f20\u5c0f\u6101\u4e00\u6b65\u9677\u5165\u5c4b\u5b50\u91cc\uff0c\u4e4d\u89c1\u90a3\u4e48\u591a\u4eba\uff0c\u5413\u4e86\u4e00\u8df3\uff0c\u53cc\u624b\u81ea\u7136\u653e\u5230\u80f8\u53e3\u4e0a\uff0c\u4e00\u65f6\u95f4\u5979\u4e5f\u5206\u4e0d\u6e05\u8c01\u662f\u8c01\uff0c\u4e5f\u6ca1\u610f\u4f1a\u5230\u548c\u6e29\u6587\u76f8\u70bd\u8fd8\u662f\u672a\u8bc6\u3002 \u3000\u3000\u201c\u4f60\u662f\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u662f\u6e29\u6587\u3002\u201d \u3000\u3000\u201c\u6e29\u2026\u2026\u6587\u2026\u2026\u201d\u5f20\u5c0f\u6101\u663e\u7136\u8981\u4ee5\u8bfb\u97f3\u6765\u5524\u9192\u5bf9\u8fd9\u4e2a\u540d\u5b57\u7684\u8bb0\u5fc6\uff0c\u8fd9\u6837\u4e00\u4e2a\u5973\u5b69\u5b50\uff0c\u8ba9\u4eba\u4e00\u770b\uff0c\u4e0d\u77e5\u600e\u7684\uff0c\u5c31\u4f1a\u600e\u8d77\u7f8e\u4e3d\u4e0e\u54c0\u6101\u7684\u7ed3\u5408\u3002\u53ea\u8981\u770b\u4e0a\u4e00\u773c\uff0c\u5c31\u4f1a\u88ab\u90a3\u5b8c\u5168\u6ca1\u6709\u4fb5\u7565\u6027\u7684\u7f8e\u4e3d\u6240\u611f\u52a8\uff0c\u800c\u4e14\u5347\u8d77\u4e86\u4e00\u9635\u604d\u4f6c\u7684\u6e29\u67d4\u3002 \u3000\u3000\u201c\u6211\u2026\u2026\u662f\u2026\u2026\u725b\u2026\u2026\u4e3d\u2026\u2026\u751f\u2026\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\uff0c\u8bf4\u5f97\u871c\u610f\u8f7b\u601c\uff0c\u7ed3\u7ed3\u5df4\u5df4\u7740\u4ed6\u7684\u67d4\u60c5\u4e07\u79cd\uff0c\u53ef\u662f\u8fd9\u53e5\u8bdd\u4e00\u51fa\u53e3\uff0c\u5927\u5bb6\u90fd\u5413\u4e86\u4e00\u8df3\u3002 \u3000\u3000\u56e0\u4e3a\u8bf4\u8bdd\u7684\u4eba\u662f\u725b\u4e3d\u751f\u3002 \u3000\u3000\u4e00\u5411\u7c97\u8c6a\u5f97\u63a5\u8fd1\u7c97\u9c81\u7684\u725b\u4e3d\u751f\uff01 \u3000\u3000\u7d20\u6765\u864e\u864e\u751f\u98ce\u89c6\u6b7b\u5982\u5f52\u7684\u725b\u4e3d\u751f\uff0c\u7537\u513f\u672c\u8272\u53d8\u5316\u4f5c\u6b32\u65ad\u67d4\u80a0\u7684\u591a\u60c5\u79cd\uff0c\u5728\u5411\u5f20\u5c0f\u6101\u4f5c\u81ea\u6211\u4ecb\u7ecd\u3002 \u3000\u3000\u4e0d\u7531\u5f97\u5927\u5bb6\u4e0d\u557c\u7b11\u7686\u975e\u3002 \u3000\u3000\u201c\u662f\u4f60\u2026\u2026\u201d\u5f20\u5c0f\u6101\u7528\u4e00\u53ea\u7ea4\u7ec6\u7684\u98df\u6307\uff0c\u6307\u7740\u6e29\u6587\u3002 \u3000\u3000\u5979\u8bb0\u8d77\u6765\u4e86\u3002 \u3000\u3000\u5979\u80cc\u7740\u9633\u5149\uff0c\u9633\u5149\u81ea\u5979\u7684\u8eab\u540e\u7167\u8fdb\u6765\uff0c\u8863\u7740\u3001\u8033\u5ed3\u90fd\u6620\u6f3e\u51fa\u900f\u4eae\u7684\u989c\u8272\uff0c\u5916\u9762\u660e\u660e\u6709\u5c0f\u5b69\u5b50\u5b09\u95f9\u7684\u58f0\u97f3\uff0c\u5c4b\u5ba4\u8fb9\u4e5f\u90fd\u662f\u4eba\u58f0\uff0c\u53ef\u662f\u8fd9\u5973\u5b50\u4e00\u51fa\u73b0\uff0c\u4e00\u5207\u4fbf\u90fd\u5b81\u9759\u4e86\u4e0b\u6765\u3002\u5c31\u50cf\u5728\u5fd9\u788c\u7684\u529e\u516c\u684c\u4e0a\u7684\u73bb\u7483\u74f6\u91cc\u7684\u4e00\u6735\u81ea\u83b2\u82b1\u3001\u5c3d\u7ba1\u5468\u56f4\u7684\u4eba\u4f9d\u7136\u5728\u6253\u5b57\u673a\u548c\u8ba1\u7b97\u673a\u6309\u94ae\u4e0a\u5bfb\u751f\u8ba1\uff0c\u4f46\u82b1\u4f9d\u7136\u662f\u82b1\uff0c\u9759\u5b83\u8d85\u7136\u7684\u9759\u3002 \u3000\u3000\u201c\u662f\u6211\u5440\u3002\u201d\u6e29\u6587\u89c1\u5979\u8fd8\u8bb0\u5f97\u81ea\u5df1\uff0c\u4e5f\u5f88\u9ad8\u5174\u3002 \u3000\u3000\u201c\u4ed6\u4eec\u662f\u2026\u2026\u201d\u5f20\u5c0f\u6101\u7684\u8ddf\u91cc\u8fc5\u5feb\u7684\u62b9\u8fc7\u4e86\u4e24\u6735\u60ca\u60f6\u3002 \u3000\u3000\u201c\u4ed6\u4eec\u662f\u56db\u5e78\u7684\u597d\u670b\u53cb\uff0c\u6253\u4ece\u9999\u6e2f\u6765\u7684\uff0c\u4e0d\u77e5\u9053\u4f60\u6709\u6ca1\u6709\u6240\u56db\u5e78\u8bf4\u8fc7\u2026\u2026\u201d\u6e29\u6587\u70ed\u5207\u5f97\u4e0d\u5141\u5bf9\u65b9\u6709\u4efb\u4f55\u673a\u4f1a\u6253\u56de\u7968\uff0c\u201c\u4ed6\u4eec\u5c31\u662f\u3001\u516d\u4eba\u5e2e\u2019\uff0c\u4e00\u5411\u884c\u4fa0\u4ed7\u4e49\u2026\u2026\u201d \u3000\u3000\u6ca1\u6599\u5f20\u5c0f\u6101\u5931\u60ca\u65e0\u795e\u7684\u9661\u4e00\u70b9\u5934\uff1a\u201c\u6211\u77e5\u9053\u3002\u201d \u3000\u3000\u7136\u540e\u5979\u53c8\u52a0\u5f3a\u4e86\u4e00\u53e5\uff1a\u201c\u6211\u542c\u8fc7\u3002\u201d \u3000\u3000\u4e4b\u540e\u5979\u62fe\u8d77\u67d4\u5f31\u800c\u53c8\u654f\u611f\u5f97\u4f3c\u4e0d\u6562\u8ddf\u4efb\u4f55\u6709\u751f\u547d\u529b\u7684\u76ee\u5149\u5bf9\u89c6\u7684\u7f8e\u7738\uff0c\u695a\u695a\u800c\u575a\u5b9a\u7684\u95ee\uff1a\u201c\u4f60\u4eec\u60f3\u77e5\u9053\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4f17\u4eba\u6ca1\u6599\u5230\u8fd9\u67d4\u5f31\u5973\u5b50\u4f1a\u8fd9\u4e48\u723d\u5feb\uff0c\u4e00\u65f6\u9762\u9762\u76f8\u89d1\u3002 \u3000\u3000\u9648\u5251\u8c01\u8bf4\uff0c\u201c\u6709\u5173\u56db\u5e78\u7684\u6b7b\uff0c\u4f60\u80fd\u544a\u8bc9\u6211\u4eec\u4ec0\u4e48\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5f20\u5c0f\u6101\u7684\u773c\u795e\u91cc\u6709\u5343\u8a00\u4e07\u8bed\uff0c\u6bcf\u4e00\u4e2a\u773c\u8272\u90fd\u6709\u6101\u6709\u6028\uff0c\u4f46\u5c31\u50cf\u7164\u6cb9\u706f\u7130\u820c\u4e0a\u90a3\u4e00\u9053\u84dd\u706b\u5149\u5f71\uff0c\u4e0d\u53ef\u6349\u6a21\uff0c\u4f46\u53c8\u6bd4\u706b\u66f4\u67d4\u51c4\u7f8e\u7edd\u3002 \u3000\u3000\u5979\u53ea\u4e00\u7b11\u3002 \u3000\u3000\u7b11\u6bd4\u4e0d\u7b11\u66f4\u6101\u3002 \u3000\u3000\u5f20\u8bde\u6709\u963b\u6b62\u4e4b\u610f\uff1a\u201c\u5c0f\u6101\u2026\u2026\u201d \u3000\u3000\u5c0f\u6101\u4e00\u6447\u9996\uff0c\u7136\u540e\u54ac\u7740\u4e0b\u5507\uff0c\u575a\u5b9a\u7684\u5411\u5979\u7684\u54e5\u54e5\u3001\u7236\u4eb2\u3001\u6bcd\u4eb2\uff0c\u7528\u529b\u4e00\u70b9\u5934\u3002 \u3000\u3000\u201c\u597d\u5427\uff0c\u201d\u5f20\u8001\u5934\u513f\u53f9\u4e86\u4e00\u58f0\uff0c\u7ec8\u4e8e\u6781\u4e0d\u653e\u5fc3\u4e5f\u53ea\u597d\u653e\u5fc3\u7684\u8bf4\uff0c\u201c\u53cd\u6b63\uff0c\u4f60\u5df2\u957f\u5927\u4e86\uff0c\u4ec0\u4e48\u662f\u8be5\u8bf4\u7684\u548c\u4e0d\u8be5\u8bf4\u7684\uff0c\u4f60\u81ea\u5df1\u90fd\u6653\u5f97\u7684\u4e86\u3002\u201c\u201c\u7238\uff0c\u4f60\u653e\u5fc3\u5427\uff0c\u8be5\u8bf4\u7684\uff0c\u603b\u662f\u8981\u8bf4\u7684\uff0c\u4e0d\u8be5\u8bf4\u7684\uff0c\u53c8\u6709\u4ec0\u4e48\u597d\u8bf4\u5462\u3002\u201d\u5f20\u5c0f\u6101\u52c7\u6562\u7684\u5411\u53ea\u6709\u4e00\u4e2a\u76f8\u8bc6\u800c\u5176\u4ed6\u90fd\u662f\u521d\u89c1\u7684\u964c\u751f\u4eba\u8bf4\uff0c\u201c\u4f60\u4eec\u8981\u5230\u54ea\u91cc\u53bb\u8c08\uff1f\u201d \u3000\u3000\u5176\u5b9e\uff0c\u8fd9\u6837\u9762\u5bf9\u95ee\u9898\u7684\u6001\u5ea6\uff0c\u4e5f\u8c08\u4e0d\u4e0a\u4ec0\u4e48\u201c\u52c7\u6562\u201d\uff0c\u53ea\u662f\u53d1\u751f\u5728\u5f20\u5c0f\u6101\u8eab\u4e0a\uff0c\u5c31\u8ba9\u4eba\u89c9\u5f97\u5979\u52c7\u6562\uff0c\u5f88\u60f3\u9f13\u821e\u5979\u90a3\u4e48\u4eae\u4e3d\u7684\u52c7\u6562\u4e0b\u53bb\u3002 \u3000\u3000\u201c\u5c31\u8fd9\u91cc\uff0c\u597d\u5417\uff1f\u201d \u3000\u3000\u9648\u5251\u51c6\u6c89\u7740\u4ed6\u8bf4\u3002 \u3000\u3000\u81f3\u5c11\uff0c\u4ed6\u8ba4\u4e3a\uff0c\u8fd9\u662f\u4e00\u4e2a\u53ef\u4ee5\u6559\u5f20\u5c0f\u6101\u672c\u4eba\u548c\u5979\u5bb6\u4eba\u90fd\u6bd4\u8f83\u653e\u5fc3\u7684\u5730\u65b9\u3002 \u3000\u3000\u201c\u6211\u770b\u8fd9\u56de\u4e0d\u4f1a\u662f\u6253\u8349\u60ca\u86c7\u4e86\u3002\u201d \u3000\u3000\u201c\u600e\u4e48\u8bf4\uff1f\u201d \u3000\u3000\u53f2\u6d41\u82b3\u548c\u9a86\u94c3\u5728\u7ec6\u8bed\u3002 \u3000\u3000\u725b\u4e3d\u751f\u5728\u50bb\u7b11\u3002 \u3000\u3000\u201c\u50cf\u5f20\u5c0f\u6101\u8fd9\u6837\u7684\u5973\u5b69\u5b50\u600e\u4f1a\u50cf\u6761\u86c7\uff1f\u201d\u53f2\u6d41\u82b3\u5927\u60ca\u5c0f\u602a\u7684\u8bf4\uff1a\u201c\u54ea\u6709\u8fd9\u4e48\u4f18\u7f8e\u3001\u6e29\u9a6f\u3001\u8f7b\u67d4\u7684\u86c7\uff01\u201d \u3000\u3000\u201c\u5bf9\uff0c\u201d\u6e29\u6587\u4f38\u8fc7\u5934\u6765\u51d1\u4e00\u628a\u5634\u8bf4\uff0c\u201c\u81f3\u591a\uff0c\u90a3\u53ea\u662f\u6253\u8349\u60ca\u868a\u800c\u5df2\u3002\u201d \u3000\u3000\uff13\u3001\u5931\u53bb\u7684\u91d1\u94c3\u5b50 \u3000\u3000\u201c\u4f60\u4eec\u60f3\u77e5\u9053\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u90a3\u5929\u665a\u4e0a\u7684\u4e8b\u3002\u201d \u3000\u3000\u201c\u90a3\u5929\u665a\u4e0a\u2026\u2026\u201d \u3000\u3000\u201c\u56db\u5e78\u662f\u600e\u4e48\u6b7b\u7684\uff1f\u201d\u9648\u5251\u8c01\u8bda\u631a\u4ed6\u8bf4\uff0c\u201c\u6211\u4eec\u90fd\u5f88\u60f3\u77e5\u9053\u3002\u201d \u3000\u3000\u5f20\u5c0f\u6101\u4f3c\u662f\u5760\u5165\u4e86\u672c\u662f\u8981\u523b\u610f\u9057\u5fd8\u7684\u56de\u5fc6\u4e2d\u3002 \u3000\u3000\u5979\u7684\u773c\u8272\u60ca\u60e7\u3002 \u3000\u3000\u5979\u7684\u795e\u8272\u614c\u60f6\u3002 \u3000\u3000\u2014\u2014\u5c31\u50cf\u662f\u8dcc\u4eba\u5669\u68a6\u91cc\uff0c\u5979\u4e0d\u6b32\u68a6\u4e0b\u53bb\u53ea\u6709\u9192\u6765\uff0c\u53c8\u50cf\u89e6\u53ca\u4e86\u706b\uff0c\u5982\u679c\u4e0d\u88ab\u70e7\u4f24\uff0c\u552f\u6709\u8fdc\u79bb\u90a3\u706b\u7130\u3002 \u3000\u3000\u8981\u5979\u8bf4\u51fa\u90a3\u665a\u7684\u4e8b\uff0c\u5c31\u7b49\u4e8e\u8981\u5979\u56de\u5230\u8fc7\u53bb\u7684\u5fe7\u60e7\u91cc\uff0c\u5bf9\u8fd9\u6837\u4e00\u4e2a\u955c\u6e56\u6e05\u6708\u4f3c\u7684\u5973\u5b69\u800c\u8a00\uff0c\u975e\u8981\u6709\u6781\u5927\u7684\u6b8b\u5fcd\u624d\u72e0\u5f97\u4e86\u5fc3\u53bb\u60ca\u6270\u3002 \u3000\u3000\u9648\u5251\u8c01\u5374\u575a\u6301\u8981\u5f20\u5c0f\u6101\u8bf4\u4e0b\u53bb\u3002 \u3000\u3000\u5f20\u5c0f\u6101\u5927\u6982\u662f\u60f3\u8bf4\uff0c\u8981\u8bf4\u3001\u4f1a\u8bf4\u7684\uff0c\u53ef\u662f\uff0c\u5c4b\u524d\u7684\u72ac\uff0c\u7a81\u7136\u5267\u70c8\u7684\u5420\u4e86\u8d77\u6765\u3002 \u3000\u3000\u7136\u540e\u662f\u4e09\u4e24\u58f0\u4f4e\u6c89\u7684\u53f1\u559d\u3002 \u3000\u3000\u5927\u5420\u58f0\u7acb\u5373\u9759\u6b62\u3002 \u3000\u3000\u6709\u7684\u5420\u58f0\u8fd8\u53d8\u6210\u545c\u545c\u7684\u4f4e\u9e23\uff0c\u5c31\u50cf\u72d7\u513f\u89c1\u5230\u4e86\u4e3b\u4eba\u7684\u6837\u5b50\u3002 \u3000\u3000\u90a3\u4e24\u4e09\u58f0\u4f4e\u53f1\uff0c\u6765\u52bf\u6781\u5feb\uff0c\u521d\u65f6\u8fd8\u660e\u660e\u53ea\u5728\u8def\u53e3\uff0c\u4f46\u4e00\u4e0b\u5b50\u5df2\u5230\u4e86\u95e8\u53e3\u3002 \u3000\u3000\u90a3\u4eba\u5954\u8d70\u65f6\u6240\u5e26\u8d77\u7684\u98ce\u58f0\uff0c\u6781\u5176\u5389\u70c8\uff0c\u4f46\u811a\u4e0b\u8e0f\u6b65\u4e4b\u8f7b\uff0c\u5b9b\u4f3c\u65e0\u58f0\uff0c\u82e5\u4e0d\u662f\u6709\u72d7\u5420\uff0c\u8fd8\u9519\u89c9\u53ea\u662f\u5728\u95e8\u5916\u522e\u8fc7\u4e86\u4e00\u9635\u98ce\u3002 \u3000\u3000\u4f46\u90a3\u9635\u98ce\u5df2\u5230\u4e86\u95e8\u524d\u3002 \u3000\u3000\u51e0\u5934\u5927\u5927\u5c0f\u5c0f\u7684\u72d7\uff0c\u90fd\u8ddf\u5728\u4ed6\u8eab\u8fb9\u3002 \u3000\u3000\u2014\u2014\u90a3\u4e9b\u72d7\u4e4d\u89c1\u6709\u4eba\u98de\u5954\uff0c\u6ca1\u8ba4\u6e05\u662f\u8c01\u4fbf\u5420\u4e86\u8d77\u6765\uff0c\u53ef\u662f\u4e00\u65e6\u77e5\u9053\u4e86\u539f\u6765\u662f\u8fd9\u4eba\uff0c\u90fd\u6b7b\u5fc3\u584c\u5730\u7684\u56f4\u62e2\u8fc7\u6765\u3002 \u3000\u3000\u8fd9\u4eba\u5728\u95e8\u524d\u4e00\u7ad9\uff0c\u9633\u5149\u906e\u53bb\u4e86\u4e00\u5927\u534a\u3002 \u3000\u3000\u5f20\u5c0f\u6101\u7684\u4f4d\u5b50\u662f\u9762\u5bf9\u95e8\u53e3\u3002 \u3000\u3000\u9648\u5251\u8c01\u6ca1\u6709\u56de\u8eab\u3002 \u3000\u3000\u4ed6\u751a\u81f3\u4e0d\u66fe\u56de\u5934\u3002 \u3000\u3000\u5927\u5bb6\u90fd\u8bc1\u4e86\u6014\uff0c\u6e29\u6587\u5e72\u54b3\u4e86\u4e00\u58f0\u3002\u7528\u7f13\u548c\u7684\u8bed\u97f3\u8bf4\uff1a\u201c\u670b\u53cb\uff0c\u6211\u770b\u4f60\u8bef\u4f1a\u4e86\uff0c\u6211\u4eec\u53ea\u662f\u6765\u62dc\u8bbf\u5f20\u5c0f\u59d0\u7684\u3002\u201d \u3000\u3000\u201c\u80e1\u8bf4\uff01\u201d\u90a3\u4eba\u51b7\u7b11\uff0c\u201c\u5c0f\u6101\u6839\u672c\u4e0d\u5165\u8bc6\u4f60\u4eec\uff01\u201d \u3000\u3000\u201c\u4e0d\u662f\u7684\u3002\u201d\u6e29\u6587\u4f9d\u7136\u597d\u6c14\u5730\u89e3\u91ca\uff1a\u201c\u5f20\u5c0f\u59d0\u548c\u6211\u662f\u7d20\u8bc6\uff0c\u8fd8\u662f\u56db\u5e78\u5144\u7ed9\u6211\u4eec\u5f15\u4ecb\u7684\u5462\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u4ed6\u4eec\u5462\uff1f\u201d\u90a3\u4eba\u5145\u6ee1\u654c\u610f\u7684\u8bf4\uff0c\u201c\u8fd9\u4e9b\u5916\u6765\u4eba\uff01\u201d \u3000\u3000\u201c\u5916\u6765\u4eba\u53c8\u600e\u6837\uff01\u5916\u6765\u4eba\u4e5f\u4e0d\u662f\u5916\u661f\u4eba\uff0c\u4f60\u6ca1\u89c1\u8fc7\u4ece\u5916\u56fd\u6765\u7684\u5ba2\u4eba\u4e48\uff01\u201d\u53f2\u6d41\u82b3\u53ef\u6ca1\u597d\u6c14\uff0c\u201c\u6211\u4eec\u627e\u7684\u662f\u5f20\u5c0f\u6101\u3001\u5173\u4f60\u4ec0\u4e48\u4e8b\uff01\u201d \u3000\u3000\u7136\u5219\u4ed6\u5176\u5b9e\u6bd4\u725b\u4e3d\u751f\u77ee\u5c0f\u3002 \u3000\u3000\u81f3\u5c11\u77ee\u4e0a\u4e00\u5c3a\u4f59\uff0c\u5c11\u4e86\u4e94\u5341\u78c5\u3002 \u3000\u3000\u725b\u4e3d\u751f\u65e5\u9732\u51f6\u5149\uff0c\u9732\u51fa\u767d\u68ee\u68ee\u7684\u7259\u9f7f\u3002 \u3000\u3000\u90a3\u51e0\u5934\u91ce\u72d7\uff0c\u672c\u6765\u5c31\u91ce\u6027\u672a\u9a6f\uff0c\u5bf9\u5c4b\u91cc\u8fd9\u51e0\u4e2a\u4e0d\u901f\u4e4b\u5ba2\u6b63\u80e1\u80e1\u5730\u4f4e\u543c\u7740\uff0c\u4f46\u725b\u4e3d\u751f\u8fd9\u4e00\u7ad9\u8d77\u6765\uff0c\u90a3\u4e9b\u72ac\u53ea\u5168\u90fd\u5f80\u540e\u9000\uff0c\u5939\u7740\u5c3e\u5df4\uff0c\u8fde\u53eb\u4e5f\u6ca1\u6562\u53eb\u4e0a\u534a\u58f0\u3002 \u3000\u3000\u725b\u4e3d\u751f\u68ee\u7136\u9053\uff1a\u201c\u4f60\u3001\u521a\u624d\u8bf4\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201d\u6211\u73b0\u5728\u8bf4\uff0c\u201d\u90a3\u4eba\u51b7\u7136\u9053\uff1a\u201c\u6eda\u51fa\u53bb\u3002\u201d \u3000\u3000\u725b\u4e3d\u751f\u63a8\u5f00\u4e86\u51f3\u5b50\u3002 \u3000\u3000\u5f20\u5c0f\u6101\u5ffd\u7136\u8bf4\u8bdd\u4e86\u3002 \u3000\u3000\u201c\u4f60\u8981\u5e72\u4ec0\u4e48\uff01\u201d\u4ed6\u4eec\u90fd\u6ca1\u6599\u5230\u8fd9\u6837\u4e00\u4e2a\u8877\u54c0\u6101\u6101\u7684\u5f20\u5c0f\u6101\uff0c\u51f6\u8d77\u6765\u4f1a\u90a3\u4e48\u51f6\uff0c\u201d\u8fd9\u513f\u5f79\u4f60\u7684\u4e8b\uff01\u201d \u3000\u3000\u90a3\u4eba\u575a\u5b9a\u3001\u575a\u51b3\u3001\u575a\u6bc5\u7684\u8138\u4e0a\u7acb\u5373\u73b0\u51fa\u4e86\u4e3a\u96be\u4e4b\u6781\u8272\uff1a\u201c\u6211\u2026\u2026\u6211\u6015\u4ed6\u4eec\u2026\u2026\u5bf9\u4f60\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u7684\u4e8b\u4e0d\u5173\u4f60\u4e8b\uff01\u201d\u5f20\u5c0f\u6101\u75be\u8a00\u5389\u8272\uff0c\u201c\u5173\u4f60\u4ec0\u4e48\u4e8b\uff1f\u4f60\u8d70\uff01\u201d \u3000\u3000\u90a3\u4eba\u8138\u4e0a\u9732\u51fa\u9eef\u7136\u4e4b\u8272\u3002 \u3000\u3000\u7136\u540e\u53c8\u6709\u4e00\u79cd\u96be\u8a00\u7684\u6028\u6124\u3002 \u3000\u3000\u4ed6\u6068\u548c\u5fff\u90fd\u5728\u773c\u8272\u91cc\u6d41\u9732\u65e0\u9057\u3002 \u3000\u3000\u4f46\u4ed6\u4f3c\u4e0d\u6562\u8fdd\u6297\u5f20\u5c0f\u6101\u7684\u8bdd\u3002 \u3000\u3000\u201c\u6211\u2026\u2026\u201d \u3000\u3000\u4ed6\u7684\u8138\u808c\u62bd\u6410\u7740\u3002 \u3000\u3000\u201c\u4f60\u8fd8\u4e0d\u8d70\uff01\u201d \u3000\u3000\u4ed6\u63e1\u7d27\u4e86\u62f3\u5934\u3002 \u3000\u3000\u201c\u597d\uff01\u6211\u8d70\uff01\u201d\u4ed6\u51b3\u610f\u5f97\u4f3c\u4e00\u5200\u65ad\u81c2\uff0c\u201c\u53ef\u662f\u4f60\u8981\u5c0f\u5fc3\uff0c\u8fd9\u73ed\u4eba\u4e0d\u662f\u597d\u4eba\u3002\u4f60\u88ab\u4eba\u9a97\u8fc7\uff0c\u96be\u9053\u8fd8\u8981\u7ed9\u4eba\u6b3a\u9a97\u4e0b\u53bb\u5417\uff1f\u201d \u3000\u3000\u6e29\u6587\u53f9\u9053\uff1a\u201c\u5509\u5440\uff0c\u4f60\u600e\u4e48\u53ef\u4ee5\u8fd9\u6837\u8bf4\u5462\uff01\u201d \u3000\u3000\u5f20\u5c0f\u6101\u6c14\u5f97\u773c\u91cc\u6d41\u7740\u6cea\u82b1\uff0c\u8fd9\u4f7f\u5f97\u5979\u6e05\u6f88\u7684\u7738\u5b50\u66f4\u6e05\u6f88\u3002\u4e00\u5982\u628a\u96e8\u82b1\u77f3\u653e\u5728\u6e05\u6c34\u91cc\u66f4\u89c1\u6709\u96e8\u6709\u82b1\uff1a\u201c\u4f60\u8fd8\u8bf4\uff0c\u4f60\u8fd8\u8bf4\uff01\u201d \u3000\u3000\u90a3\u4eba\u9eef\u7136\u4f46\u5fff\u5fff\u7684\u8bf4\uff1a\u201c\u597d\uff0c\u6211\u8d70\uff01\u201d \u3000\u3000\u201c\u987e\u5148\u751f\u3002\u201d\u4e00\u76f4\u672a\u66fe\u56de\u5934\u7684\u9648\u5251\u8c01\u7a81\u7136\u53d1\u8bdd\u3002 \u3000\u3000\u90a3\u4eba\u731b\u7136\u6b62\u6b65\uff0c\u5168\u8eab\u808c\u9aa8\uff0c\u4ecd\u4fdd\u6301\u7740\u8ddf\u6b63\u8981\u8de8\u6b65\u800c\u884c\u7684\u65f6\u5019\u5b8c\u5168\u4e00\u6837\u7684\u59ff\u52bf\uff0c\u53ef\u662f\u4ed6\u7684\u7cbe\u795e\u4e0e\u6218\u5fd7\uff0c\u5df2\u5168\u96c6\u4e2d\u5230\u4e00\u4e2a\u7126\u70b9\u4e0a\u3002 \u3000\u3000\u4ed6\u7684\u201c\u7126\u70b9\u201d\u4fbf\u662f\u9648\u5251\u8c01\u3002 \u3000\u3000\u201c\u4f60\u600e\u4e48\u77e5\u9053\u6211\u59d3\u987e\uff1f\u201d\u4ed6\u5145\u6ee1\u6545\u610f\u5730\u95ee\u3002 \u3000\u3000\u201c\u4f60\u5c31\u662f\u987e\u5f71\u3002\u201d\u9648\u5251\u8c01\u5e76\u4e0d\u56de\u8eab\uff0c\u53ea\u6de1\u6de1\u4ed6\u8bf4\uff1a\u201c\u4f60\u7684\u4e0b\u76d8\u529f\u592b\u7ec3\u5f97\u5b9e\u5728\u597d\uff0c\u5927\u6982\u5728\u6d6e\u6c99\u4e0a\u4e5f\u73b0\u4e0d\u51fa\u811a\u5370\u5427\uff1f\u8fd9\u79cd\u2018\u767b\u840d\u6e21\u6c34\u2019\u7684\u529f\u592b\uff0c\u6ca1\u60f3\u5230\u8fd9\u6b21\u5230\u6d77\u5916\u6765\u624d\u5f97\u4ee5\u5927\u5f00\u773c\u754c\u3002\u201d \u3000\u3000\u987e\u5f71\u7684\u773c\u795e\u4f9d\u7136\u9510\u70c8\u3002 \u3000\u3000\u66f3\u6d41\u82b3\u5728\u51dd\u89c6\u4e00\u4e2a\u4eba\u7684\u65f6\u5019\uff0c\u8db3\u4ee5\u628a\u5bf9\u65b9\u7684\u7cbe\u795e\u610f\u5fd7\u4ee5\u63a7\u5236\uff0c\u53ef\u4ee5\u8bf4\u662f\u4e00\u79cd\u593a\u53bb\u4ed6\u4eba\u5fd7\u9b44\u7684\u76ee\u529b\u3002 \u3000\u3000\u4f46\u4ed6\u4e0e\u987e\u5f71\u5bf9\u89c6\u7684\u65f6\u5019\uff0c\u5e99\u89c9\u5230\u5bf9\u65b9\u7684\u773c\u795e\u53cd\u5c04\u81ea\u5df1\u7684\u773c\u529b\uff0c\u5c31\u7b97\u81ea\u5df1\u5c3d\u5c55\u6240\u80fd\uff0c\u4e5f\u672a\u5fc5\u5c31\u80fd\u9a7e\u9a6d\u5f97\u4e86\u8fd9\u8c79\u5b50\u4e00\u822c\u7cbe\u608d\u7684\u4eba\u3002 \u3000\u3000\u53ef\u5c31\u5728\u8fd9\u4e00\u5239\u90a3\u95f4\uff0c\u987e\u5f71\u7684\u773c\u795e\u7a0d\u5fae\u655b\u4e86\u4e00\u655b\u3002 \u3000\u3000\u5982\u679c\u53f2\u6d41\u82b3\u8981\u4ee5\u773c\u795e\u652b\u53bb\u5bf9\u65b9\u7684\u9b42\u9b44\u7684\u8bdd\uff0c\u6b64\u9645\u4fbf\u662f\u4e0a\u4f73\u65f6\u673a\uff0c\u4e5f\u5c31\u662f\u8bf4\uff0c\u597d\u6bd4\u9ad8\u624b\u7684\u5bf9\u51b3\uff0c\u5728\u8fd9\u7a0d\u7eb5\u5373\u901d\u7684\u65f6\u673a\u91cc\u5bf9\u65b9\u7adf\u7136\u51fa\u73b0\u4e86\u7834\u7efd\uff0c\u6b63\u53ef\u8d81\u865a\u800c\u5165\uff0c\u4e00\u51fb\u81f4\u547d\u3002 \u3000\u3000\u987e\u5f71\u7684\u773c\u795e\u4e4b\u6240\u4ee5\u602f\u4e86\u4e00\u602f\uff0c\u662f\u56e0\u4e3a\u90a3\u4e2a\u4ece\u80cc\u5f71\u770b\u53bb\u5df2\u6709\u4e00\u80a1\u903c\u4eba\u6c14\u52bf\u7684\u6c49\u5b50\uff0c\u7adf\u7136\u4e0d\u56de\u8eab\u5c31\u770b\u5230\u4e86\u4ed6\u7684\u6b66\u529f\u5bb6\u6570\u3002 \u3000\u3000\u201c\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u201d \u3000\u3000\u4ed6\u6ca1\u95ee\u4e0b\u53bb\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u5982\u679c\u95ee\u4e0b\u53bb\uff0c\u6c14\u52bf\u5c31\u5f31\u4e86\u3002 \u3000\u3000\u2014\u2014\u5bf9\u65b9\u53ea\u8bf4\u4e86\u4e24\u53e5\u6d3b\uff0c\u5c31\u6296\u51fa\u4e86\u4ed6\u4e24\u4e2a\u201c\u5e95\u7ec6\u201d\uff0c\u8981\u662f\u4ed6\u8fd8\u8981\u5012\u8fc7\u53bb\u95ee\u4eba\u5bb6\u5230\u5e95\u662f\u600e\u4e48\u77e5\u9053\u7684\uff0c\u90a3\u4e48\u5c31\u4f1a\u65e0\u6c14\u52bf\u53ef\u8a00\u4e86\u3002 \u3000\u3000\u4ed6\u7701\u8d77\u4e86\u8fd9\u70b9\uff0c\u6240\u4ee5\u4e0d\u95ee\u4e0b\u53bb\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u5df2\u95ee\u4e86\u4e00\u534a\u3002 \u3000\u3000\u95ee\u4e86\u4e00\u534a\u4e0d\u95ee\uff0c\u4e0d\u7ba1\u662f\u6ca1\u6709\u4fe1\u5fc3\u8fd8\u662f\u7701\u89c9\u81ea\u5df1\u95ee\u9519\u4e86\uff0c\u5728\u6c14\u52bf\u4e0a\uff0c\u4ecd\u662f\u77ed\u4e86\u4e00\u5927\u622a\u3002 \u3000\u3000\u9648\u5251\u8c01\u4e5f\u4e0d\u56de\u7b54\u4ed6\u7684\u8bdd\uff0c\u53ea\u8bf4\uff1a\u201c\u542c\u8bf4\u4f60\u4eec\u6709\u9547\u5236\u9ed1\u706b\u7684\u6cd5\u529b\uff0c\u5341\u5206\u5e0c\u671b\u80fd\u5411\u4f60\u8bf7\u6559\u3002\u201d \u3000\u3000\u201c\u4f60\u4e5f\u4f2f\u9ed1\u706b\u4e48\uff1f\u201d\u987e\u5f71\u88c5\u505a\u4e0d\u5c51\u7684\u6837\u5b50\uff0c\u201c\u90a3\u4f60\u6c42\u6c42\u6211\u7238\u7238\u5427\uff01\u201d \u3000\u3000\u201c\u6709\u673a\u4f1a\uff0c\u201d\u9648\u5251\u8c01\u5e73\u9759\u7684\u8bf4\uff0c\u201c\u5012\u771f\u7684\u8981\u62dc\u4f1a\u4ed6\u8001\u4eba\u5bb6\u3002\u201d \u3000\u3000\u201c\u4f60\u6765\u5427\uff0c\u201d\u987e\u5f71\u72e0\u72e0\u7684\u8bf4\uff1a\u201c\u4f60\u4e0d\u6765\u6211\u4e5f\u4f1a\u53bb\u627e\u4f60\u4eec\u7684\u3002\u201d \u3000\u3000\u8bf4\u7f62\uff0c\u7528\u4e00\u79cd\u201c\u7b49\u7740\u77a7\u201d\u7684\u8868\u60c5\uff0c\u6124\u6124\u7136\u7684\u79bb\u53bb\u4e86\u3002 \u3000\u3000\u9a86\u94c3\u626c\u58f0\u53eb\u9053\uff1a\u201c\u4e0d\u7528\u7b49\u4ee5\u540e\uff0c\u73b0\u5728\u5c31\u6765\u5427\u3002\u201d \u3000\u3000\u987e\u5f71\u6839\u672c\u6ca1\u628a\u5979\u77a7\u5728\u773c\u91cc\uff0c\u8fde\u773c\u89d2\u4e5f\u4e0d\u8138\u5979\u4e00\u773c\uff0c\u5927\u6b65\u800c\u53bb\u3002 \u3000\u3000\u9a86\u94c3\u6c14\u7684\u7c89\u816e\u9080\u7ea2\uff0c\u4e00\u9762\u8ffd\u51fa\u53bb\u4e00\u9762\u9a7e\u9053\uff1a\u201c\u4f60\u8fd9\u4eba\uff0c\u6211\u5728\u8ddf\u4f60\u8bf4\u8bdd\u5440\uff0c\u600e\u4e48\u90a3\u4e48\u6ca1\u793c\u8c8c\uff01\u201d \u3000\u3000\u9648\u5251\u8c01\u4f4e\u58f0\u559d\u6b62\uff1a\u201c\u91d1\u94c3\u5b50\uff01\u201d \u3000\u3000\u4f46\u8fdc\u5904\u4f20\u6765\u9880\u5f71\u4e0d\u5c51\u7684\u8bed\u97f3\uff1a\u201c\u5973\u5b69\u5b50\u6700\u597d\u8fd8\u662f\u56de\u5bb6\u62b1\u5a03\u5a03\uff0c\u5b66\u4eba\u591a\u7ba1\u95f2\u4e8b\uff0c\u5f85\u4f1a\u513f\u53ef\u522b\u54ed\u7740\u56de\u5bb6\u53eb\u5988\u5988\uff01\u201d \u3000\u3000\u9a86\u94c3\u4e00\u542c\uff0c\u5fcd\u65e0\u53ef\u5fcd\uff0c\u4e00\u6b65\u5c31\u8d8a\u8fc7\u90a3\u5f20\u684c\u5b50\uff0c\u8df3\u5230\u95e8\u5916\u53bb\uff0c\u629b\u4e0b\u4e00\u53e5\u8bdd\uff1a\u201c\u4e0d\u884c\uff0c\u65e0\u8bba\u5982\u4f55\uff0c\u6211\u5f97\u8981\u6559\u8bad\u6559\u8bad\u4ed6\uff01\u201d \u3000\u3000\u9648\u5251\u8c01\u5411\u725b\u4e3d\u751f\u75be\u9053\uff1a\u201c\u963f\u725b\uff0c\u4f60\u4e5f\u4f1a\u770b\u770b\u3002\u201d \u3000\u3000\u4ed6\u4e00\u5411\u77e5\u9053\u9a86\u94c3\u7684\u672c\u9886\uff0c\u4f46\u4e5f\u6ca1\u770b\u8f7b\u987e\u5f71\uff0c\u4ed6\u4e00\u5411\u75bc\u9a86\u94c3\uff0c\u6240\u4ee5\u66f4\u4e0d\u60f3\u5931\u53bb\u8fd9\u7f8e\u4e3d\u806a\u654f\u7684\u91d1\u94c3\u5b50\u3002 \u3000\u3000\u725b\u4e3d\u4e3b\u7528\u529b\u4e00\u70b9\u5934\uff0c\u9888\u90e8\u53d1\u51fa\u201c\u5f97\u201d\u7684\u4e00\u58f0\u95f7\u54cd\uff1a\u201c\u597d\u3002\u201d\u4e00\u6b65\u5c31\u8de8\u51fa\u4e86\u95e8\u69db\u3002 \u3000\u3000\u5f20\u8bde\u89c1\u8fd9\u4e9b\u4eba\u7684\u8eab\u624b\u90fd\u90a3\u4e48\u597d\uff0c\u4e0d\u7531\u5f97\u50bb\u4e86\u773c\uff0c\u9694\u4e86\u597d\u4e00\u4f1a\u513f\u624d\u80fd\u8bf4\uff1a\u201c\u987e\u5f71\u8fd9\u4eba\u2026\u2026 \u3000\u3000\u5f88\u6709\u672c\u9886\uff0c\u5c31\u662f\u2026\u2026\u5f88\u9a84\u50b2\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u7ba1\u4ed6\u591a\u6709\u672c\u9886\u3001\u201c\u53f2\u6d41\u82b3\u54fc\u54fc\u5730\u9053\uff1a\u201c\u6709\u91d1\u94c3\u5b50\u548c\u963f\u725b\uff0c\u4ed6\u6709\u591a\u5927\u7684\u672c\u9886\u90fd\u6ca1\u672c\u4e8b\u53bb\u9886\u4e86\u3002\u201d \u3000\u3000\u9648\u5251\u8c01\u6ca1\u8bf4\u4ec0\u4e48\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u5fc3\u5e95\u91cc\u4e5f\u540c\u610f\u53f2\u6d41\u82b3\u7684\u6d3b\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u76f8\u4f20\u8fd9\u4e24\u4e2a\u7eea\u62dc\u5f1f\u59b9\u7684\u672c\u9886\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u5411\u5f20\u5c0f\u6101\u8bf4\uff1a\u201c\u90a3\u665a\u7684\u4e8b\u2026\u2026\u201d \u3000\u3000\u4ed6\u8981\u95ee\u4e0b\u53bb\u3002 \u3000\u3000\u4ed6\u4e00\u5b9a\u8981\u77e5\u9053\u4e2a\u7a76\u7adf\uff0c\u5c31\u7b97\u6709\u4eba\u6545\u610f\u6765\u6253\u6270\u3001\u963b\u6b62\uff0c\u5728\u77e5\u9053\u524d\u56e0\u540e\u679c\u524d\uff0c\u4ed6\u662f\u51b3\u4e0d\u4f1a\u88ab\u5f15\u79bb\uff0c\u4e00\u5b9a\u4f1a\u5b88\u5728\u5f20\u5c0f\u6101\u8fb9\uff0c\u76f4\u81f3\u542c\u5979\u8bf4\u51fa\u771f\u76f8\u4e3a\u6b62\u3002 \u3000\u3000\u9664\u975e\u662f\u5f20\u5c0f\u6101\u81ea\u5df1\u4e0d\u613f\u610f\u8bf4\u3002 \u3000\u3000\uff14\u3001\u5f53\u5929\u665a\u4e0a \u3000\u3000\u5f20\u5c0f\u6101\u51b3\u4e0d\u613f\u518d\u5fc6\u8d77\u90a3\u5929\u665a\u4e0a\u7684\u4e8b\u3002 \u3000\u3000\u90a3\u53ef\u6015\u7684\u665a\u4e0a\u3002 \u3000\u3000\u90a3\u7f9e\u803b\u7684\u665a\u4e0a\u3002 \u3000\u3000\u90a3\u4f24\u5fc3\u7684\u665a\u4e0a\u3002 \u3000\u3000\u90a3\u5982\u5669\u68a6\u4e00\u822c\u4ee4\u4eba\u754f\u60e7\u7684\u665a\u4e0a\u3002 \u3000\u3000\u53ef\u662f\uff0c\u4ed6\u4eec\u8981\u4ed6\u8bf4\u51fa\u90a3\u665a\u7684\u7ecf\u8fc7\u3002 \u3000\u3000\u8fd9\u4f7f\u5979\u4e0d\u5f97\u4e0d\u53c8\u5815\u4eba\u4e86\u90a3\u50cf\u6076\u9b54\u7f16\u7ed3\u7684\u86db\u7f51\u4e00\u822c\u7684\u56de\u5fc6\u4e4b\u4e2d\u3002 \u3000\u3000\u5bf9\u5f20\u5c0f\u6101\u800c\u8a00\uff0c\u5728\u5979\u7f8e\u4e3d\u7684\u5f62\u8c8c\u6210\u957f\u7684\u5c81\u6708\u91cc\uff0c\u6709\u7684\u662f\u65e0\u5c3d\u7684\u8f9b\u9178\u548c\u60b2\u51c9\uff0c\u8bf4\u4e0d\u51fa\u7684\u5bc2\u5bde\uff0c\u800c\u4e14\u8fd8\u662f\u6bcf\u4e00\u6b21\u6323\u624e\u90fd\u6362\u6765\u518d\u4e00\u6b21\u6253\u51fb\u3002 \u3000\u3000\u6323\u624e\u5f97\u8f9b\u8f9b\u82e6\u82e6\uff0c\u6253\u51fb\u5f97\u6c89\u6c89\u91cd\u91cd\u3002 \u3000\u3000\u8fd9\u4e9b\u6253\u51fb\uff0c\u6709\u65f6\u5019\uff1b\u6c89\u91cd\u5f97\u4ee4\u5f20\u5c0f\u6101\u51e0\u4e4e\u4e0d\u613f\u518d\u7ad9\u8d77\u6765\u3002 \u3000\u3000\u2014\u2014\u6c38\u8fdc\u4e0d\u6323\u624e\u4e5f\u662f\u4e00\u79cd\u5b89\u9759\u548c\u5e78\u798f\u3002 \u3000\u3000\u6323\u624e\u672c\u8eab\u5c31\u6709\u7740\u4e0d\u5f97\u4e0d\u6323\u624e\u7684\u75db\u82e6\u3002 \u3000\u3000\u5979\u7684\u6837\u5b50\u5f88\u6709\u4e00\u79cd\u5973\u6027\u67d4\u5a9a\u7684\u9b45\u529b\u3002 \u3000\u3000\u4f46\u5979\u7684\u95ee\u9898\u4e5f\u51fa\u5728\u8fd9\u91cc\u3002 \u3000\u3000\u4e00\u76f4\u4ee5\u6765\uff0c\u5c31\u662f\u56e0\u4e3a\u5979\u957f\u5f97\u6f02\u4eae\uff0c\u6240\u4ee5\u9ebb\u70e6\u4e5f\u7279\u522b\u591a\u3002 \u3000\u3000\u8fd8\u5728\u5ff5\u5c0f\u5b66\u7684\u65f6\u5019\uff0c\u56e0\u4e3a\u5979\u7684\u7f8e\u8c8c\u4e0e\u53ef\u7231\uff0c\u7adf\u5f15\u8d77\u4e86\u4e00\u4e2a\u4eba\u9762\u517d\u5fc3\u7684\u8001\u5e08\u52a8\u4e86\u8272\u5fc3\uff0c\u8bd5\u56fe\u4ee5\u4e49\u52a1\u6559\u5979\u8865\u4e60\u7684\u540d\u4e49\uff0c\u5e38\u5728\u6709\u610f\u65e0\u610f\u95f4\u5411\u5979\u4f5c\u6deb\u4eb5\u7684\u4e3e\u6b62\u3002 \u3000\u3000\u90a3\u65f6\u5f20\u5c0f\u6101\u5e74\u7eaa\u8fd8\u592a\u5c0f\uff0c\u8fd8\u4e0d\u77e5\u9053\u90a3\u662f\u600e\u4e48\u56de\u4e8b\u3002\u6709\u4e00\u6b21\u90a3\u6559\u5458\u517d\u6027\u5927\u53d1\u60f3\u8981\u5f3a\u66b4\u5979\u7684\u65f6\u5019\uff0c\u6b63\u597d\u7ed9\u90a3\u6559\u5458\u7684\u592a\u5927\u649e\u7834\uff0c\u5979\u60ca\u547c\u7740\u8ddf\u6559\u5458\u626d\u6253\uff0c\u90a3\u6559\u5458\u8001\u7f9e\u6210\u6012\u7adf\u8981\u52d2\u6b7b\u59bb\u5b50\uff0c\u5f3a\u66b4\u5c0f\u6101\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u547c\u53eb\u58f0\u5374\u5f15\u8d77\u987e\u5f71\u7684\u7236\u4eb2\uff0c\u987e\u6b65\u7684\u6ce8\u610f\uff0c\u4ed6\u53ca\u65f6\u5236\u4f4f\u4e86\u90a3\u6559\u5458\uff0c\u4e5f\u5236\u6b62\u4e86\u8fd9\u4ef6\u66b4\u884c\u3002 \u3000\u3000\u4ece\u6b64\u4ee5\u540e\uff0c\u5728\u5f20\u5c0f\u6101\u7684\u5e7c\u5c0f\u5fc3\u7075\u91cc\u3001\u53d7\u5230\u4e86\u6781\u5927\u7684\u4f24\u5bb3\uff0c\u5bf9\u7537\u5973\u95f4\u7684\u4e8b\u611f\u5230\u754f\u60e7\u548c\u7591\u8651\uff0c\u5bf9\u201c\u6027\u201d\u4e5f\u5b8c\u5168\u6709\u626d\u66f2\u7684\u89c2\u5ff5\uff1a\u5979\u53ea\u89c9\u5f97\u90a3\u662f\u4e00\u79cd\u4e0d\u6d01\u7684\u884c\u4e3a\u3002 \u3000\u3000\u5f20\u5c0f\u6101\u8fd8\u56e0\u800c\u8f6c\u4e86\u6821\u3002 \u3000\u3000\u4ed6\u7684\u5c0f\u5b66\u4e94\u5e74\u7ea7\uff0c\u4e5f\u56e0\u6b64\u5ff5\u4e86\u4e24\u5e74\u3002 \u3000\u3000\u8981\u662f\u8fd8\u7559\u5728\u90a3\u95f4\u5b66\u6821\u91cc\uff09\u4ece\u540c\u5b66\u5230\u6559\u5e08\u3001\u5b66\u957f\uff0c\u96be\u514d\u90fd\u4f1a\u5bf9\u5979\u6307\u6307\u70b9\u70b9\uff0c\u5c31\u7b97\u662f\u540c\u60c5\u5979\u7684\u906d\u9047\u7684\uff0c\u4e5f\u90fd\u4f1a\u4e3a\u5979\u611f\u53f9\uff1a\u5e74\u7eaa\u5c0f\u5c0f\u5c31\u201c\u5f15\u4eba\u72af\u7f6a\u3001\u957f\u5927\u4e86\u8fd8\u5f97\u4e86\uff1f\u7ea2\u989c\u7978\u6c34\u554a\uff01 \u3000\u3000\u7978\u4e0d\u5355\u884c\u3002\u5f20\u5c0f\u6101\u5728\u5ff5\u521d\u4e2d\u7684\u65f6\u5019\uff0c\u5728\u5f52\u5bb6\u7684\u8def\u4e0a\uff0c\u8fd9\u662f\u5dee\u70b9\u5c31\u7ed9\u90bb\u6751\u7684\u66b4\u5f92\u5f3a\u66b4\u4e86\u3002 \u3000\u3000\u8fd9\u6b21\u662f\u987e\u5f71\u548c\u6bdb\u5ff5\u884c\u53ca\u65f6\u8d76\u5230\uff0c\u628a\u66b4\u5f92\u6253\u8dd1\u3002 \u3000\u3000\u90a3\u65f6\u5019\u5f20\u5c0f\u6101\u8863\u886b\u4e0d\u5179\uff0c\u8fd8\u662f\u987e\u5f71\u628a\u5979\u62b1\u56de\u6765\u7684\u3002 \u3000\u3000\u8fd9\u4ef6\u4e8b\u5bf9\u5f20\u5c0f\u6101\u7684\u4f24\u5bb3\u66f4\u5927\u3002 \u3000\u3000\u987e\u5f71\u548c\u6bdb\u5ff5\u884c\u548c\u5979\u53ef\u8bf4\u662f\u81ea\u5c0f\u73a9\u5230\u5927\uff0c\u90a3\u4e00\u6bb5\u65f6\u5019\uff0c\u5979\u751a\u81f3\u4e0d\u60f3\u89c1\u8fd9\u4e24\u4e2a\u8001\u53cb\u3002 \u3000\u3000\u9002\u9022\u90a3\u6bb5\u65f6\u5019\uff0c\u5979\u5bb6\u91cc\u4e5f\u6709\u5927\u53d8\uff0c\u5bb6\u9053\u4e2d\u843d\uff0c\u51e0\u4e4e\u4e0d\u80fd\u7ef4\u6301\u5979\u90a3\u4e48\u4e00\u70b9\u70b9\u7684\u5b66\u8d39\u3002 \u3000\u3000\u81ea\u90a3\u65f6\u5019\u5f00\u59cb\uff0c\u5f20\u5c0f\u6101\u5c31\u51fa\u6765\u8ddf\u7236\u4eb2\u7684\u51b0\u679c\u644a\u5e2e\u624b\uff0c\u534a\u5de5\u534a\u8bfb\uff0c\u5e2e\u8865\u5bb6\u8ba1\u3002 \u3000\u3000\u5982\u679c\u4e0d\u662f\u4ed6\u54e5\u54e5\u5f20\u8bde\u5f53\u65f6\u529b\u4e3b\u5979\u5ff5\u4e0b\u53bb\uff0c\u6050\u6015\u5979\u591a\u534a\u5df2\u7ecf\u8f8d\u5b66\u4e86\u3002 \u3000\u3000\u4e0d\u8fc7\u5f20\u8bde\u81ea\u5df1\u5374\u4ece\u90a3\u65f6\u8d77\u4fbf\u5931\u5b66\u4e86\u3002 \u3000\u3000\u5230\u4e86\u9ad8\u4e2d\uff0c\u5f20\u5c0f\u6101\u5fc3\u65e0\u65c1\u9a9b\uff0c\u4e13\u5fc3\u5ff5\u4e66\u3001\u4e0d\u8c08\u604b\u7231\uff0c\u4f46\u5979\u7684\u7f8e\u8c8c\uff0c\u5f15\u52a8\u4e86\u8bb8\u591a\u7537\u751f\u7684\u975e\u5206\u4e4b\u60f3\uff0c\u867d\u7136\u5979\u6beb\u4e0d\u5047\u8272\uff0c\u4f46\u4e5f\u4e0d\u60f3\u592a\u62d2\u4eba\u4e8e\u5343\u91cc\u4e4b\u5916\uff0c\u592a\u8fc7\u4f24\u4eba\u3002\u8fd9\u4e00\u6765\uff0c\u5374\u4f7f\u4e00\u4e2a\u7537\u751f\uff0c\u7ade\u4e3a\u5f97\u4e0d\u5230\u5979\u7684\u9752\u7750\u800c\u81ea\u6740\u3002 \u3000\u3000\u8fd9\u4e00\u4e0b\uff0c\u628a\u4e8b\u60c5\u95f9\u5927\u4e86\u3002 \u3000\u3000\u90a3\u7537\u751f\u6b7b\u4e0d\u6210\uff0c\u4f46\u90a3\u7537\u751f\u7684\u5bb6\u957f\u5230\u5b66\u6821\u6765\uff0c\u5f53\u4f17\u6012\u65a5\u5f20\u5c0f\u6101\u7684\u4e0d\u662f\uff0c\u8bf4\u5979\u662f\u201c\u5c0f\u5996\u60c5\uff01\u201d \u3000\u3000\u5f20\u5c0f\u6101\u65e0\u6cd5\u627f\u8ba4\u8fd9\u79cd\u65e0\u8f9c\u7684\u4fae\u8fb1\u3002 \u3000\u3000\u5979\u771f\u60f3\uff1a\u201c\u6b7b\u4e86\u7b97\u4e86\uff01\u201d \u3000\u3000\u90a3\u4e00\u5e74\uff0c\u5979\u5fc3\u60c5\u5927\u4e71\uff0c\u4e00\u5411\u6210\u7ee9\u4f18\u79c0\u7684\u5979\u3001\u7ade\u4e5f\u4f1a\u8003\u843d\u699c\u3002\u5728\u8fd9\u4e2a\u5730\u65b9\u548c\u8fd9\u4e2a\u5e74\u5934\uff0c\u8003\u4e0d\u4e0a\u5bf9\u4e00\u4e2a\u5e74\u8f7b\u5973\u4e88\u6765\u8bf4\u662f\u524d\u666f\u582a\u865e\u7684\uff0c\u9664\u975e\u662f\u627e\u4e2a\u597d\u5a46\u5bb6\u5ac1\u4e86\u7b97\u6570\u3002 \u3000\u3000\u53ef\u662f\u5f20\u5c0f\u6101\u53c8\u4e0d\u7518\u4e8e\u6b64\u3002 \u3000\u3000\u5982\u6b64\u4e00\u6765\uff0c\u8fd9\u6b21\u6253\u51fb\u63a5\u8e35\u800c\u6765\uff0c\u628a\u5f20\u5c0f\u6101\u539f\u5148\u7684\u7f8e\u68a6\u90fd\u6253\u788e\u4e86\u3002 \u3000\u3000\u5f20\u5c0f\u6101\u51fa\u6765\u627e\u5de5\u4f5c\u7684\u7b2c\u4e00\u5929\uff0c\u504f\u53c8\u5728\u5149\u5929\u5316\u65e5\u7684\u8857\u5934\uff0c\u9047\u4e0a\u4e86\u4e00\u4e2a\u534a\u75af\u4e0d\u766b\u7684\u9732\u4f53\u72c2\uff0c\u5411\u5979\u4f5c\u51fa\u4e0d\u96c5\u7684\u4e3e\u63aa\u3002 \u3000\u3000\u90a3\u4eba\u867d\u88ab\u626d\u9001\u5230\u8b66\u5c40\u53bb\uff0c\u4f46\u5bf9\u5f20\u5c0f\u6101\u800c\u8a00\uff0c\u662f\u4e00\u6b21\u6323\u624e\u4fbf\u6765\u4e00\u6b21\u6253\u51fb\uff0c\u7531\u4e8e\u6253\u51fb\u592a\u5927\u3001\u592a\u591a\uff0c\u4f7f\u5979\u611f\u89c9\u5230\uff1a\u53ea\u8981\u4e0d\u6323\u624e\u4fbf\u6d3b\u4e0d\u4e0b\u53bb\u3002 \u3000\u3000\u5982\u6b64\uff0c\u6323\u624e\u4fbf\u4e0d\u662f\u52c9\u529b\u800c\u4e3a\u7684\u4e8b\u4e86\uff0c\u800c\u662f\u751f\u5b58\u7684\u5fc5\u9700\u3002 \u3000\u3000\u7ecf\u8fc7\u8fd9\u4e00\u4ef6\u4ead\u4e4b\u540e\uff0c\u5f20\u5c0f\u6101\u5bf9\u7537\u5973\u4e4b\u95f4\u7684\u4e8b\u53cd\u800c\u80fd\u591f\u6b63\u89c6\uff0c\u4ee5\u5f80\u90a3\u4e00\u79cd\u201c\u80ae\u810f\u201d\u201c\u4e0d\u6d01\u201d\u7684\u611f\u89c9\uff0c\u5012\u662f\u9042\u6e10\u5730\u6d88\u5931\u4e86\u3002 \u3000\u3000\u6709\u4e9b\u4eba\u9047\u5230\u4e00\u70b9\u5c0f\u632b\uff0c\u53ef\u80fd\u4f1a\u5f88\u6cae\u4e27\uff1b\u53d8\u5f97\u9000\u7f29\uff1b\u4f46\u5f53\u9047\u4e0a\u91cd\u5927\u6253\u51fb\uff0c\u800c\u4e14\u53ea\u8981\u4e0d\u63a5\u53d7\u8fd9\u6b21\u6253\u51fb\u4fbf\u65e0\u6cd5\u751f\u5b58\u4e0b\u53bb\u7684\u65f6\u5019\uff0c\u53cd\u800c\u80fd\u591f\u53bb\u9762\u5bf9\u8fd9\u4e9b\u6253\u51fb\uff0c\u5e76\u4e88\u4ee5\u53cd\u51fb\u3002 \u3000\u3000\u5f20\u5c0f\u6101\u5c31\u662f\u8fd9\u79cd\u4eba\u3002 \u3000\u3000\u79c1\u5e95\u4e0b\uff0c\u5979\u8ba4\u4e3a\u8521\u56db\u5e78\u4e5f\u662f\u8fd9\u79cd\u4eba\u3002 \u3000\u3000\u4e0e\u8521\u56db\u5e78\u76f8\u8bc6\u4e4b\u540e\uff0c\u90a3\u662f\u5979\u6700\u5feb\u4e50\u7684\u65e5\u5b50\u3002 \u3000\u3000\u2014\u2014\u8521\u56db\u5e78\u723d\u6717\u3001\u81ea\u4fe1\u3001\u80fd\u5e72\uff0c\u800c\u4e14\u7231\u5979\u3002 \u3000\u3000\u201c\u7231\u5979\u201d\u5e76\u4e14\u201c\u5c0a\u91cd\u5979\u201d\u3002 \u3000\u3000\u8fd9\u5bf9\u5f20\u5c0f\u6101\u800c\u8a00\uff0c\u662f\u6700\u91cd\u8981\u7684\u3002 \u3000\u3000\u4f46\u5f20\u5c0f\u6101\u5e76\u4e0d\u8ba4\u4e3a\u8521\u56db\u5e78\u4e00\u751f\u90fd\u662f\u5e78\u8fd0\u548c\u5e78\u798f\u7684\u3002 \u3000\u3000\u5979\u89c9\u5f97\u8521\u56db\u5e78\u4e5f\u58ee\u5fd7\u672a\u916c\uff0c\u5fe7\u90c1\u96be\u4f38\u3002 \u3000\u3000\u2014\u2014\u8521\u56db\u5e78\u5176\u5b9e\u80f8\u6000\u5927\u5fd7\uff0c\u5f88\u60f3\u4e3a\u6c11\u65cf\u6587\u5316\u505a\u70b9\u4e8b\uff0c\u4f46\u5904\u8eab\u5728\u8fd9\u6837\u7684\u4e00\u4e2a\u7f3a\u4e4f\u5929\u65f6\u5730\u5229\u4eba\u548c\u7684\u73af\u5883\u91cc\uff0c\u4ed6\u53c8\u80fd\u505a\u4e9b\u4ec0\u4e48\uff1f \u3000\u3000\u2014\u2014\u8521\u56db\u5e78\u5176\u5b9e\u4e5f\u5f88\u6709\u6587\u624d\uff0c\u53ef\u662f\u4ed6\u6027\u5b50\u5927\u8fc7\u62d7\u6267\uff0c\u800c\u4e14\u624d\u534e\u8fc7\u4e8e\u7729\u76ee\uff0c\u81f4\u4f7f\u6587\u575b\u524d\u8f88\u90fd\u4e0d\u80af\u683d\u57f9\u4ed6\uff0c\u800c\u5e74\u8f7b\u4e00\u8f88\u53c8\u6a21\u4eff\u4e86\u4ed6\u8f9b\u8f9b\u82e6\u6614\u5efa\u7acb\u7684\u6587\u4f53\uff0c\u52a0\u4ee5\u53d1\u6325\uff0c\u6bd4\u4ed6\u8fd8\u53d7\u7f16\u8005\u548c\u8bfb\u8005\u7684\u6367\u573a\uff0c\u8fd9\u4e00\u6765\uff0c\u4ed6\u8fd9\u4e2a\u201c\u539f\u521b\u8005\u201d\u4fbf\u88ab\u57cb\u6ca1\u4e86\u3002\u65e2\u7136\u4ece\u6587\u4e0d\u53d7\u6ce8\u610f\uff0c\u8521\u56db\u5e78\u6539\u800c\u4e60\u6b66\u3002 \u3000\u3000\u6ca1\u60f3\u5230\uff0c\u5728\u6b66\u827a\u4e0a\uff0c\u4ed6\u5374\u4e00\u5e06\u98ce\u987a\uff0c\u6210\u540d\u6781\u65e9\u3002\u8fd9\u4e5f\u8bb8\u5728\u53e4\u4eba\u6765\u8bf4\u4f46\u662f\u201c\u519b\u529f\u663e\u8361\u201d\u7684\u90a3\u4e00\u7c7b\u4eba\u5427\uff0c\u6216\u8bb8\u5728\u516b\u5b57\u4e0a\u662f\u5b9c\u6b66\u4e0d\u9002\u6587\u5427\uff0c\u8521\u56db\u5e78\u4e3a\u4eba\u6240\u77e5\uff0c\u53cd\u800c\u662f\u4ed6\u7684\u5192\u9669\u4e8b\u4e1a\u3002 \u3000\u3000\u5bf9\u8521\u56db\u5e78\u800c\u8a00\uff0c\u4ed6\u6bcf\u6b21\u60f3\u53bb\u53d1\u6325\u5fd7\u6c14\u4e0a\u7684\u62b1\u8d1f\u548c\u6587\u5b66\u4e0a\u7684\u624d\u534e\uff0c\u4e5f\u53ea\u662f\u518d\u591a\u9047\u4e0a\u4e00\u6b21\u6323\u624e\u4e00\u6b21\u6253\u51fb\u7684\u547d\u8fd0\u3002 \u3000\u3000\u53ea\u4e0d\u8fc7\u8521\u56db\u5e78\u4e00\u5411\u90e8\u5f80\u597d\u5904\u60f3\uff0c\u81ea\u89c9\u5e78\u798f\u7f62\u4e86\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u53ea\u8981\u65f6\u65f6\u8ba4\u4e3a\u4ed6\u81ea\u5df1\u662f\u5e78\u798f\u7684\uff0c\u522b\u4eba\u4e5f\u5c31\u5bb9\u6613\u8ba4\u540c\u4ed6\u4e5f\u662f\u5e78\u798f\u7684\u4e86\u3002 \u3000\u3000\u81f3\u5c11\uff0c\u4e00\u4e2a\u5e38\u81ea\u89c9\u201c\u4eba\u5728\u798f\u4e2d\u201d\u7684\u4eba\uff0c\u51b3\u4e0d\u4f1a\u592a\u8fc7\u4e0d\u5e78\u3002 \u3000\u3000\u5f20\u5c0f\u6101\u4e5f\u53ea\u662f\u5fc3\u5e95\u91cc\u89c9\u5f97\u8521\u56db\u5e78\u5e76\u4e0d\u80fd\u7b97\u662f\u4e00\u4e2a\u201c\u5e78\u798f\u7684\u4eba\u201d\uff0c\u5979\u4e5f\u4ece\u672a\u5bf9\u4ed6\u63d0\u8d77\u8fc7\u5979\u7684\u60f3\u6cd5\uff0c\u5979\u751f\u6015\u8fd9\u4e9b\u60b2\u89c2\u3001\u6d88\u6781\u7684\u60f3\u6cd5\u4f1a\u5f71\u54cd\u8521\u56db\u5e78\u751f\u547d\u91cc\u7684\u79ef\u6781\u53d6\u5411\u3002 \u3000\u3000\u76f4\u81f3\u90a3\u665a\u2026\u2026 \u3000\u3000\u5f20\u5c0f\u6101\u624d\u77e5\u9053\u8521\u56db\u5e78\u6709\u591a\u4e48\u7684\u4e0d\u5e78\u3002 \u3000\u3000\u2026\u2026\u90a3\u5929\u665a\u4e0a\uff0c\u8f66\u5b50\u505c\u5728\u8352\u90ca\u3002 \u3000\u3000\u767d\u8272\u7684\u5973\u4eba\u5728\u9ed1\u6697\u91cc\u63a0\u8fc7\u3002 \u3000\u3000\u5f20\u5c0f\u6101\u60ca\u89c9\u3002 \u3000\u3000\u8521\u56db\u5e78\u5374\u6ca1\u770b\u89c1\u3002 \u3000\u3000\u4ed6\u51fa\u53bb\u67e5\u770b\u3002 \u3000\u3000\u5f20\u5c0f\u6101\u5728\u8f66\u91cc\uff0c\u7b49\u4e86\u8bb8\u4e45\u3002 \u3000\u3000\u8bb8\u4e45\u2026\u2026 \u3000\u3000\u8bb8\u4e45\u90fd\u672a\u89c1\u8521\u56db\u5e78\u56de\u6765\u3002 \u3000\u3000\u8f66\u5b50\u91cc\u7684\u4eea\u5668\u8fd8\u4eae\u7740\u4e00\u76cf\u6697\u7ea2\u7684\u706f\u3002 \u3000\u3000\u2014\u2014\u4eae\u7740\u4e00\u70b9\u60ca\u5fc3\u7684\u5bc2\u5bde\u3002 \u3000\u3000\u8f66\u5916\u7684\u706f\uff0c\u8fd8\u628a\u5149\u4eae\u63a8\u5f00\u4e86\u4e94\u516d\u5c3a\u3002 \u3000\u3000\u4e94\u516d\u5c3a\u4ee5\u5916\u662f\u6a21\u7cca\u98d8\u5ffd\u7684\u4e16\u754c\uff0c\u8c61\u9634\u5206\u9633\u6653\u4e4b\u95f4\u7684\u4e00\u70b9\u660f\u7791\u3002 \u3000\u3000\u90a3\u4e00\u70b9\u660f\u7791\u540e\u662f\u4e00\u7247\u9ed1\u6697\u3002 \u3000\u3000\u65e0\u5c3d\u7684\u9ed1\u6697\u50cf\u8fdb\u5165\u4e86\u9f13\u7684\u5fc3\u810f\u3002 \u3000\u3000\u5fc3\u8df3\u58f0\u64c2\u5728\u81ea\u5df1\u7684\u542c\u89c9\u91cc\uff0c\u624d\u77e5\u9053\u9664\u4e86\u7591\u4ff1\uff0c\u8fd8\u6709\u88ab\u56f0\u7684\u60ca\u6050\u3002 \u3000\u3000\u90a3\u611f\u89c9\u548c\u5979\u51e0\u6b21\u53d7\u8fb1\uff0c\u547c\u5929\u4e0d\u5e94\uff0c\u5524\u5730\u65e0\u95e8\u65f6\u7684\u611f\u53d7\uff0c\u7adf\u7136\u975e\u5e38\u7684\u8fd1\u4f3c\u3002 \u3000\u3000\u2014\u2014\u600e\u4e48\u529e\u2026 \u3000\u3000\u4e3a\u4ec0\u4e48\u56db\u5e78\u8fd8\u4e0d\u56de\u6765\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\u5019\uff0c\u5979\u5ffd\u7136\u89c9\u5bdf\u5728\u9065\u8fdc\u7684\u9ed1\u6697\u91cc\u3001\u6709\u4e24\u53ea\u767d\u706f\u7b3c\u51dd\u5728\u90a3\u91cc\u3002 \u3000\u3000\u7b49\u5979\u6ce8\u610f\u5230\u60f3\u770b\u6e05\u695a\u7684\u65f6\u5019\uff0c\u706f\u5149\u5df2\u8fc5\u901f\u5730\u903c\u8fd1\u3001\u6269\u5927\uff01\u788e\u7136\u76f4\u523a\u5165\u5979\u7684\u773c\u5e18\uff01 \u3000\u3000\u8bf4\u5230\u8fd9\u91cc\uff0c\u5f20\u5c0f\u6101\u4e00\u53ea\u624b\u629a\u7740\u80f8\u53e3\uff0c\u4e00\u53ea\u624b\u7d27\u7d27\u7684\u6293\u4f4f\u6905\u89d2\uff0c\u8bf4\u4e0d\u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u201c\u540e\u6765\u600e\u4e48\u4e86\uff1f\u201d\u53f2\u6d41\u82b3\u6025\u7740\u95ee\u3002 \u3000\u3000\u201c\u540e\u6765\u600e\u6837\u4e86\uff1f\u201d\u6e29\u6587\u6e29\u548c\u7684\u95ee\u3002 \u3000\u3000\u201c\u540e\u6765\u2026\u2026\uff0c\u5f20\u5c0f\u6101\u9694\u4e86\u597d\u4e00\u4f1a\u513f\uff0c\u624d\u63a5\u5f97\u4e0b\u53bb\uff1a\u201c\u2026\u540e\u6765\u6211\u770b\u89c1\u2026\u2026\u201d \u3000\u3000\u5979\u770b\u89c1\u7684\u4eba\uff0c\u8fde\u5979\u81ea\u5df1\u4e5f\u4e0d\u76f8\u4fe1\u3002 \u3000\u3000\u522b\u4eba\u5f53\u7136\u66f4\u4e0d\u80fd\u7f6e\u4fe1\u3002 \u3000\u3000\u2014\u2014\u8fd9\u5927\u6982\u5c31\u662f\u5979\u4e0d\u6562\u516c\u5f00\u7684\u539f\u56e0\u5427\u3002 \u3000\u3000\u4e00\u65e6\u516c\u5f00\uff0c\u53ea\u6709\u9020\u6210\u4e09\u79cd\u540e\u679c\uff1a\u4e00\u3001\u522b\u4eba\u538b\u6839\u513f\u4e0d\u4f1a\u76f8\u4fe1\uff1b\u4e8c\u3001\u5979\u7684\u8bf7\u4f1a\u4f7f\u76f8\u4fe1\u7684\u4eba\u9020\u6210\u6050\u614c\uff0c\u4e09\u3001\u5979\u5c31\u7b97\u628a\u8bdd\u8bf4\u51fa\u6765\uff0c\u5bf9\u5979\u548c\u5c01\u67e5\u8521\u56db\u5e78\u60e8\u6b7b\u7684\u6848\u4ef6\uff0c\u4e5f\u65ad\u4e0d\u4f1a\u6709\u4ec0\u4e48\u52a9\u76ca\u3002 \u3000\u3000\u6240\u4ee5\u5979\u624d\u4e0d\u613f\u610f\u8bf4\u51fa\u6765\u3002 \u3000\u3000\u5979\u89c1\u5230\u7684\u7adf\u7136\u662f\uff1a \u3000\u3000\u963f\u8482\u548c\u5fb7\u53d4\uff01 \u3000\u3000\u90a3\u4e24\u76cf\u5f3a\u5149\uff0c\u9661\u7136\u5230\u4e86\u5f20\u5c0f\u6101\u8fd1\u524d\u4e4b\u540e\uff0c\u5f20\u5c0f\u6101\u7684\u53cc\u76ee\u88ab\u7167\u5f97\u51e0\u4e4e\u7741\u4e0d\u5f00\u6765\uff0c\u53ef\u662f\uff0c\u5979\u5374\u4f9d\u7136\u770b\u89c1\uff0c\u81ea\u5979\u6240\u5750\u7684\u8f66\u5b50\u6240\u53d1\u51fa\u6765\u80a0\u706f\u5149\u548c\u90a3\u4e24\u9053\u5f3a\u5149\u4e4b\u95f4\u5e7f\u6709\u4e9b\u201c\u7269\u4f53\u2019\u7ecf\u8fc7\u3002 \u3000\u3000\u4e00\u662f\u963f\u8482\u3002 \u3000\u3000\u4e00\u662f\u5fb7\u53d4\u3002 \u3000\u3000\u4e24\u4e2a\u5df2\u88ab\u201c\u9ed1\u706b\u201d\u70e7\u6b7b\u7684\u201c\u4eba\u201d\u3002 \u3000\u3000\u4e00\u5206\u660e\u662f\u4ed6\u4eec\uff01 \u3000\u3000\u201c\u600e\u4e48\u4f1a\uff1f\u201d\u6e29\u6587\u548c\u53f2\u6d41\u82b3\u90fd\u53eb\u4e86\u8d77\u6765\u3002 \u3000\u3000\u6e29\u6587\u52a0\u4e86\u4e00\u53e5\uff1a\u201c\u4f60\u89c1\u9b3c\u4e86\u4e0d\u6210\uff1f\u201d \u3000\u3000\u5f20\u5c0f\u6101\u5c45\u7136\u70b9\u5934\u3002 \u3000\u3000\u6e29\u6587\u628a\u4e0b\u9762\u8981\u8bf4\u7684\u8bdd\u90fd\u201c\u541e\u56de\u201d\u809a\u91cc\uff0c\u4ed6\u770b\u51fa\u5f20\u5c0f\u6101\u662f\u771f\u7684\u5341\u5206\u6050\u614c\uff0c\u800c\u4e14\u662f\u5728\u8bf4\u771f\u8bdd\uff0c\u8f6c\u8ff0\u771f\u7684\u53d1\u751f\u8fc7\u7684\u4e8b\u3002 \u3000\u3000\u53f2\u6d41\u82b3\u5374\u89c9\u5f97\u5f20\u5c0f\u6101\u5728\u6050\u60e7\u4e4b\u4f59\uff0c\u8fd8\u6709\u54c0\u6101\u3002 \u3000\u3000\u8fd9\u8868\u60c5\u4ee4\u4ed6\u5fc3\u52a8\uff0c\u540c\u65f6\uff0c\u4e5f\u4ee4\u4ed6\u51e0\u4e4e\u8981\u8bf4\u51fa\u53e3\u7684\u8bbd\u5632\u5168\u6d88\u89e3\u4e8e\u5589\u95f4\u3002 \u3000\u3000\u4ed6\u53ea\u80fd\u91cd\u590d\u7684\u95ee\u8fd9\u4e00\u53e5\uff1a\u201c\u600e\u4e48\u4f1a\uff1f\u201d \u3000\u3000\u5f20\u5c0f\u6101\u626c\u8d77\u4e86\u8138\uff0c\u4f46\u4ecd\u5728\u70b9\u5934\u3002 \u3000\u3000\u5979\u80af\u5b9a\u5979\u81ea\u5df1\u6240\u770b\u5230\u7684\uff0c\u867d\u7136\u5979\u81ea\u5df1\u4f3c\u4e4e\u4e5f\u5e76\u4e0d\u5341\u5206\u76f8\u4fe1\u3002\u3000\u3000\u4e2d\u201c\u4f60\u771f\u7684\u770b\u89c1\u5fb7\u53d4\u548c\u963f\u8482\uff1f\u201d \u3000\u3000\u9648\u5251\u8c01\u95ee\u3002\u4ed6\u4eec\u5728\u8d76\u6765\u8fd9\u513f\u7684\u9014\u4e2d\uff0c\u5df2\u542c\u6e29\u6587\u7565\u8ff0\u8fc7\u201c\u9ed1\u706b\u201d\u8086\u54b8\uff0c\u70e7\u6b7b\u5973\u80f6\u5de5\u963f\u8482\u548c\u770b\u66f4\u5fb7\u53d4\u7684\u4e8b\u3002 \u3000\u3000\u5f20\u5c0f\u6101\u653e\u5f00\u4e86\u624b\uff0c\u575a\u51b3\u5730\u70b9\u5934\u3002 \u3000\u3000\u201c\u4ed6\u4eec\u6709\u4ec0\u4e48\u4e0d\u4e00\u6837\u5417\uff1f\u201d \u3000\u3000\u5f20\u5c0f\u6101\u5148\u6447\u5934\uff0c\u7136\u540e\u773c\u795e\u4e00\u4eae\uff0c\u70b9\u5934\u3002 \u3000\u3000\u4ec0\u4e48\u4e0d\u4e00\u6837\uff1f\u201d\u9648\u5251\u8c01\u4ed4\u7ec6\u5730\u95ee\u3002 \u3000\u3000\u201c\u90a3\u65f6\u5019\uff0c\u6211\u7684\u8f66\u706f\u7684\u7535\u529b\u5df2\u7ecf\u7528\u5b8c\uff0c\u9010\u6e10\u6697\u6de1\u4e0b\u53bb\uff0c\u76f4\u81f3\u5168\u7184\u4e86\u3002\u201d\u5f20\u5c0f\u6101\u8bf4\uff0c\u201c\u53ef\u662f\u6211\u8fd8\u662f\u770b\u89c1\u4e86\u4ed6\u4eec\u3002\u201d \u3000\u3000\u201c\u4ed6\u4eec\u6bd4\u4ee5\u524d\u9ed1\u2026\u2026\u201d\u5f20\u5c0f\u6101\u72b9\u6709\u4f59\u60b8\u7684\u8bf4\uff1a\u201c\u2026\u2026\u5c31\u50cf\u662f\u70e7\u7126\u4e86\u7684\u90a3\u79cd\u9ed1\u3002\u201d \u3000\u3000\u201c\u4ed6\u4eec\u5bf9\u4f60\u505a\u4e86\u4e9b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\u2026\u2026\u4ed6\u4eec\u53ea\u662f\u5f88\u75db\u82e6\u2026\u2026\u201d \u3000\u3000\u201c\u4f60\u600e\u4e48\u77e5\u9053\u4ed6\u4eec\u5f88\u75db\u82e6\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4eec\u7684\u4e94\u5b98\u90fd\u5728\u6dcc\u7740\u8840\uff0c\u5f00\u7740\u53e3\uff0c\u6ea2\u7740\u8840\uff0c\u5728\u8bf4\u8bdd\uff0c\u53ef\u662f\u6211\u5728\u8f66\u5185\uff0c\u6211\u542c\u4e0d\u89c1\u4ed6\u4eec\u5728\u8bf4\u4ec0\u4e48\u2026\u2026\u201d \u3000\u3000\u201c\u4f60\u662f\u600e\u4e48\u8ba4\u8bc6\u963f\u8482\u548c\u5fb7\u53d4\u7684\uff1f\u201d \u3000\u3000\u201c\u5fb7\u53d4\u66fe\u8ddf\u6211\u8205\u8205\u4e00\u8d77\u780d\u8fc7\u67f4\uff0c\u6765\u8fc7\u5bb6\u91cc\u51e0\u6b21\uff0c\u4ed6\u8ba4\u8bc6\u7238\u7238\uff1b\u963f\u8482\u5728\u5c0f\u5b66\u7684\u65f6\u5019\uff0c\u662f\u6211\u7684\u540c\u5b66\u3002\u201d \u3000\u3000\u201c\u54e6\u3002\u201d\u9648\u5251\u8c01\u9677\u5165\u4e86\u6c89\u601d\u3002 \u3000\u3000\u7136\u540e\u95ee\uff1a\u201c\u800c\u540e\u5462\uff1f\u201d \u3000\u3000\u201c\u800c\u540e\u2026\u2026\u201d\u5f20\u5c0f\u6101\u50cf\u4e0b\u4e86\u6781\u5927\u7684\u51b3\u5fc3\u8bf4\uff0c\u201c\u4ed6\u4eec\u5c31\u6d88\u5931\u4e86\uff0c\u4ed6\u5c31\u51fa\u73b0\u4e86\u3002\u201d \u3000\u3000\u201c\u4ed6\uff1f\u201d \u3000\u3000\u201c\u56db\u5e78\u3002\u201d\u5f20\u5c0f\u6101\u75db\u5fc3\u7684\u8bf4\uff1a\u201c\u4ed6\u5168\u8eab\u88ab\u4e00\u79cd\u9ed1\u8272\u7684\u706b\u7f20\u70e7\u7740\uff0c\u4ed6\u5728\u8fdc\u5904\u60e8\u547c\uff0c\u53eb\u6211\u5feb\u8d70\uff0c\u6211\u5f00\u8f66\u95e8\u51fa\u53bb\u7684\u65f6\u5019\uff0c\u4ed6\u5df2\u5012\u5728\u5730\u4e0a\u2026\u2026\u90a3\u4e9b\u9ed1\u8272\u7684\u706b\uff0c\u76f4\u628a\u4ed6\u70e7\u5f97\u4e00\u52a8\u4e5f\u4e0d\u80fd\u52a8\u7684\u65f6\u5019\uff0c\u624d\u544a\u6d88\u5931\u2026\u2026\u201d \u3000\u3000\u5927\u5bb6\u90fd\u6ca1\u8bf4\u8bdd\u3002 \u3000\u3000\u90fd\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u201c\u53ef\u662f\uff0c\u201d\u5f20\u5c0f\u6101\u5ffd\u7136\u8bf4\uff1a\u201c\u706b\u7184\u4e86\uff0c\u56db\u5e78\u8fd8\u662f\u52a8\u4e86\u4e00\u52a8\u3002\u201d \u3000\u3000\u201c\u554a\u3002\u201d\u542c\u7684\u4eba\u90fd\u5413\u4e86\u4e00\u8df3\uff0c\u5f88\u60f3\u77e5\u9053\u4e0b\u6587\u3002 \u3000\u3000\u201c\u53ea\u542c\u556a\u7684\u4e00\u58f0\uff0c\u539f\u6765\u662f\u56db\u5e78\u88ab\u70e7\u7126\u4e86\u7684\u5c38\u4f53\uff0c\u8098\u90e8\u4f3c\u88ab\u4ec0\u4e48\u6401\u4f4f\u4e86\u8fd8\u5f39\u4e86\u4e00\u5f39\uff0c\u624d\u843d\u5b9e\u5728\u5730\u4e0a\uff0c\u201d\u5f20\u5c0f\u6101\u5ffd\u7136\u63a9\u7740\u8138\uff0c\u5df2\u6ce3\u4e0d\u6210\u58f0\uff1a\u201c\u592d\u554a\uff0c\u90a3\u662f\u4ec0\u4e48\u706b\uff0c\u7adf\u5982\u8bb8\u6076\u6bd2\u2026\u2026\u201d \u3000\u3000\u9664\u5251\u8c01\u6c89\u58f0\u8bf4\uff1a\u201c\u5f20\u5c0f\u59d0\uff0c\u4f60\u4e0d\u8981\u96be\u8fc7\uff0c\u6211\u4eec\u4f1a\u7aed\u5c3d\u6240\u80fd\uff0c\u67e5\u4e2a\u6c34\u843d\u77f3\u51fa\u7684\u2026\u2026\u201d \u3000\u3000\u7136\u540e\u4f4e\u58f0\u5429\u5490\u53f2\u6d41\u82b3\uff0c\u201c\u4f60\u548c\u6e29\u6587\u7559\u5728\u8fd9\u91cc\uff0c\u597d\u597d\u770b\u987e\u5f20\u5c0f\u59d0\u2026\u2026\u201d \u3000\u3000\u53f2\u6d41\u82b3\u5947\u9053\uff1a\u201c\u4f60\u8981\u53bb\u54ea\u91cc\uff1f\u201d \u3000\u3000\u201c\u9a86\u94c3\u548c\u725b\u4e3d\u751f\u5230\u73b0\u5728\u8fd8\u6ca1\u6709\u56de\u6765\uff0c\u6050\u6015\u5df2\u7ecf\u662f\u9047\u4e8b\u4e86\uff0c\u9648\u5251\u8c01\u8bf4\uff0c\u201c\u6211\u53bb\u770b\u770b\u3002\u5f20\u5c0f\u59d0\u66fe\u5728\u73b0\u573a\u76ee\u51fb\uff0c\u73b0\u53c8\u91cd\u63d0\u8fd9\u4ef6\u53ef\u6015\u7684\u4e8b\uff0c\u60c5\u7eea\u81ea\u7136\u4e0d\u5927\u7a33\u5b9a\uff0c\u5b9c\u6709\u4eba\u5728\u8fd9\u91cc\u770b\u987e\u2026\u2026\u201d \u3000\u3000\u6e29\u6587\u548c\u53f2\u6d41\u82b3\u4e00\u9f50\u62a2\u7740\u8bf4\uff1a\u201c\u6211\u6765\u770b\u987e\u5979\u597d\u4e86\u3002\u201d\u8bf4\u5b8c\uff0c\u800c\u5165\u90fd\u6b62\u4e86\u58f0\uff0c\u77aa\u4e86\u5bf9\u65b9\u4e00\u773c\u3002 \u3000 \u3000 \u7b2c\u4e94\u7ae0 \u3000 \u3000\u3000\uff11\u3001\u201c\u521a\u51fb\u9053\u201d\u91cc\u6ca1\u6709\u201c\u9053\u7406\u201d \u3000\u3000\u5e78\u4e8f\u9648\u5251\u8c01\u8d76\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u2014\u2014\u8981\u4e0d\u7136\uff0c\u8fd9\u6837\u7684\u573a\u9762\uff0c\u771f\u4e0d\u77e5\u8be5\u5982\u4f55\u6536\u62fe\u3002 \u3000\u3000\u725b\u4e3d\u751f\u548c\u9a86\u94c3\u8ffd\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u53ef\u662f\u8ffd\u4e0d\u7740\u3002 \u3000\u3000\u725b\u4e3d\u751f\u957f\u5f97\u9ad8\u5927\u7c97\u58ee\uff0c\u96be\u514d\u7b28\u91cd\u4e86\u4e9b\u3002\u53ef\u662f\u53ea\u8981\u4ed6\u8fc8\u5f00\u5927\u6b65\uff0c\u8c01\u4e5f\u4e0d\u53ca\u4ed6\u6b65\u5b50\u5bbd\uff0c\u4ed6\u8de8\u4e09\u6b65\u7b49\u4e8e\u4eba\u8dc3\u4e94\u6b65\uff0c\u80fd\u8dd1\u8d62\u4ed6\u7684\u4eba\u672c\u5c31\u4e0d\u591a\u3002 \u3000\u3000\u9a86\u94c3\u7684\u8f7b\u8eab\u529f\u592b\u6781\u597d\u3002 \u3000\u3000\u5979\u4e00\u5411\u90fd\u8ba4\u4e3a\uff0c\u8f7b\u8eab\u529f\u592b\u80fd\u4f7f\u81ea\u5df1\u8eab\u6750\u7f8e\u597d\u8eab\u4f53\u68d2\uff0c\u65e2\u7136\u6709\u8fd9\u4e48\u591a\u597d\u5904\uff0c\u90a3\u53c8\u4f55\u4e0d\u591a\u7ec3\u7ec3\u5462\uff1f \u3000\u3000\u53ef\u662f\u9a86\u94c3\u61d2\u3002 \u3000\u3000\u5979\u7231\u7761\u3001\u8d2a\u73a9\u3001\u597d\u5403\uff0c\u6709\u70b9\u5927\u5fd7\uff0c\u5374\u65e0\u91ce\u5fc3\u3002 \u3000\u3000\u5979\u559c\u6b22\u7ec3\u8f7b\u529f\uff0c\u4f46\u5bf9\u811a\u8e1d\u7ed1\u7740\u94c5\u94c1\u5c71\u4e0b\u8dd1\u4e0a\u5c71\u518d\u7531\u5c71\u4e0a\u8dd1\u4e0b\u6765\u7684\u7ec3\u6cd5\u5168\u65e0\u8010\u5fc3\uff0c\u56e0\u6b64\uff0c\u5979\u7684\u57fa\u7840\u4e5f\u624e\u5f97\u4e0d\u591f\u597d\u3002 \u3000\u3000\u4e0d\u8fc7\u4e00\u5411\u90fd\u6bd4\u522b\u4eba\u7075\u6d3b\u8f7b\u5feb\u3002 \u3000\u3000\u5979\u8dd1\u8d77\u6765\u5c31\u8ddf\u5979\u7684\u51fa\u624b\u4e00\u822c\u8f7b\u5feb\u3002 \u3000\u3000\u6ca1\u6599\u5230\u7684\u662f\uff1a \u3000\u3000\u4ed6\u4eec\u4e24\u4eba\u90fd\u8feb\u4e0d\u4e0a\u987e\u5f71\u3002 \u3000\u3000\u2014\u2014\u751a\u81f3\u8fde\u987e\u5f71\u7684\u5f71\u5b50\u90fd\u8feb\u4e0d\u4e0a\u3002 \u3000\u3000\u5728\u65e5\u5f71\u4e0b\uff0c\u987e\u5f71\u8fde\u5f71\u5b50\u90fd\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u9a86\u94c3\u89c9\u5f97\u5f88\u751f\u6c14\uff0c\u4e5f\u5f88\u4e22\u8138\u3002 \u3000\u3000\u5979\u66f4\u89c9\u5f97\u81ea\u5df1\u4e22\u4e0d\u8d77\u8fd9\u4e2a\u8138\u3002 \u3000\u3000\u725b\u4e3d\u751f\u5012\u65e0\u6240\u8c13\uff0c\u4ed6\u8fd8\u8bf4\uff1a\u201c\u5509\uff01\u90a3\u5bb6\u4f19\uff0c\u8dd1\u5f97\u5012\u662f\u633a\u5feb\u7684\u3002\u201d \u3000\u3000\u201c\u5feb\uff1f\u4ed6\u5feb\uff1f\u201d\u9a86\u94c3\u5fff\u5fff\u4e0d\u5e73\u7684\u8bf4\uff0c\u201c\u89c1\u9b3c\u4e86\uff01\u201d \u3000\u3000\u201c\u6211\u5012\u77e5\u9053\u5728\u54ea\u91cc\u53ef\u4ee5\u628a\u4ed6\u6252\u51fa\u6765\u3002\u201d\u9a86\u94c3\u8bf4\uff0c\u5e15\u4eec\u53bb\u2018\u7ea2\u6bdb\u62ff\u7763\u5e99\u2019\u3002\u201d \u3000\u3000\u725b\u4e3d\u751f\u50bb\u547c\u547c\u7684\u8bf4\uff1a\u201c\u53bb\u90a3\u91cc\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u90a3\u4eba\u9b3c\u9b3c\u795f\u795f\uff0c\u51b3\u4e0d\u662f\u4ec0\u4e48\u5584\u7c7b\uff01\u201d\u9a86\u96f6\u632f\u632f\u6709\u8f9e\uff0c\u201c\u4f60\u770b\uff0c\u9ed1\u706b\u95f9\u5f97\u4e2a\u6ee1\u57ce\u98ce\u96e8\uff0c\u4f46\u53ea\u8981\u5316\u94b1\u62dc\u4e86\u7ea2\u6bdb\u62ff\u7763\u5c31\u6ca1\u4e8b\uff0c\u8fd9\u4e0d\u662f\u6446\u660e\u4e86\u5185\u91cc\u6709\u6587\u7ae0\u5417\uff01\u641e\u4e0d\u597d\uff0c\u9ed1\u706b\u5c31\u662f\u4ed6\u4eec\u7236\u5b50\u4fe9\u4e00\u624b\u70ae\u5236\u51fa\u6765\u7684\u3002\u54b1\u4eec\u53bb\u95f9\u4e00\u95f9\uff0c\u95f9\u4ed6\u4e2a\u9e21\u72ac\u4e0d\u5b81\uff0c\u770b\u4ed6\u8fd8\u7075\u4e0d\u7075\uff01\u201d \u3000\u3000\u725b\u4e3d\u751f\u6123\u6123\u5730\u9053\uff1a\u201c\u8fd9\u2026\u2026\u201d \u3000\u3000\u9a86\u94c3\u6fc0\u4ed6\uff1a\u201c\u8fd9\u4ec0\u4e48\uff1f\u4f60\u6015\u5440\uff1f\u201d \u3000\u3000\u725b\u4e3d\u58f0\u77aa\u5706\u4e86\u773c\uff0c\u6307\u7740\u81ea\u5df1\u7684\u9f3b\u5b50\uff0c\u8eac\u7740\u8eab\u5b50\uff0c\u6c14\u547c\u547c\u7684\u8bf4\uff0c\u201c\u6211\uff1f\u6015\uff1f\u201d \u3000\u3000\u9a86\u94c3\u94f6\u94c3\u4f3c\u7684\u4eae\u7b11\u7740\uff1a\u201c\u4e0d\u6015\u5c31\u4f1a\u561b\u3002\u201d \u3000\u3000\u725b\u4e3d\u751f\u4ecd\u662f\u6709\u4e9b\u72b9\u4fca\uff1a\u201c\u4e0d\u5148\u95ee\u95ee\u8001\u5927\u4e48\uff1f\u201d \u3000\u3000\u9a86\u94c3\u628a\u7f8e\u4e3d\u7684\u773c\u775b\u4e00\u77aa\uff1a\u201c\u95ee\u4e86\u4ed6\u8fd8\u6709\u5f97\u73a9\u4e48\uff01\u201d \u3000\u3000\u725b\u4e3d\u751f\u4e00\u60f3\u4e5f\u662f\uff0c\u4e24\u4eba\u5954\u5230\u8def\u53e3\uff0c\u5374\u89c1\u90a3\u5496\u5561\u5e97\u4ecd\u5750\u7740\u90a3\u4e2a\u6bdb\u5ff5\u884c\u3002 \u3000\u3000\u6bdb\u5ff5\u884c\u751f\u5f97\u7279\u522b\u767d\u7699\uff0c\u9887\u4e0d\u4f3c\u5f53\u5730\u4eba\u5927\u90fd\u662f\u76ae\u80a4\u6bd4\u8f83\u9edd\u9ed1\uff0c\u6240\u4ee5\u8fdc\u8fdc\u770b\u53bb\u5c31\u77e5\u9053\u662f\u4ed6\uff0c\u4f55\u51b5\u4ed6\u8fd8\u957f\u5f97\u7279\u522b\u79c0\u6c14\u3002 \u3000\u3000\u6bdb\u5ff5\u884c\u4e4d\u89c1\u4ed6\u4eec\uff0c\u6709\u70b9\u8be7\u5f02\uff0c\u4f46\u4ecd\u70ed\u70c8\u5730\u62db\u547c\u3002 \u3000\u3000\u201c\u4f60\u4eec\u4fe9\u5306\u5306\u5fd9\u5fd9\u7684\uff0c\u8981\u5230\u54ea\u513f\u53bb\u5440\uff1f\u201d \u3000\u3000\u201c\u6211\u4eec\u53bb\u627e\u4eba\u7b97\u8d26\uff01\u201d\u9a86\u94c3\u6068\u5f97\u7259\u5636\u5636\u7684\uff0c\u201c\u8981\u770b\u70ed\u95f9\u7684\u5c31\u4e00\u8d77\u6765\u3002\u201d \u3000\u3000\u6bdb\u5ff5\u884c\u66f4\u52a0\u8bb6\u5f02\u3002 \u3000\u3000\u4ed6\u5fd9\u8ffd\u4e0a\u53bb\uff0c\u641e\u4e86\u8001\u534a\u5929\uff0c\u624d\u77e5\u9053\u9a86\u94c3\u5728\u751f\u8c01\u7684\u6c14\uff0c\u4e3a\u4ec0\u4e48\u4f1a\u90a3\u4e48\u751f\u6c14\u3002 \u3000\u3000\u201c\u4f60\u4eec\u5c31\u522b\u53bb\u7ea2\u6bdb\u62ff\u7763\u4e86\uff0c\u201d\u6bdb\u5ff5\u884c\u529d\u6b62\uff0c\u201c\u5c0f\u56de\u7684\u7238\u7238\u6b66\u529f\u6cd5\u529b\uff0c\u90fd\u9ad8\u5f3a\u5f97\u5f88\uff0c\u4f60\u5c31\u522b\u60f9\u4ed6\u4e86\u3002\u201d \u3000\u3000\u201c\u6b66\u529f\uff1f\u6cd5\u529b\uff1f\u201d\u9a86\u94c3\u4e0d\u5c51\u5730\u9053\uff0c\u8fd9\u79cd\u4eba\uff0c\u786e\u504f\u8981\u60f9\u4e00\u60f9\u3002\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\u5c0f\u987e\uff0c\u4ed6\u73b0\u5728\u6b63\u5728\u6559\u6b66\uff0c\u4e0d\u4f1a\u5728\u7ea2\u6bdb\u62ff\u7763\u5019\u60a8\u7684\u5927\u9a7e\u7684\uff0c\u201d\u6bdb\u5ff5\u884c\u8bf4\uff0c\u201c\u6211\u8ddf\u5c0f\u987e\u662f\u5341\u6570\u5e74\u7684\u670b\u53cb\uff0c\u4ed6\u7684\u8d77\u5c45\u4f5c\u606f\uff0c\u6211\u6700\u6e05\u695a\u4e0d\u8fc7\u3002\u201d \u3000\u3000\u9a86\u94c3\u505c\u4e86\u4e0b\u6765\uff0c\u95ee\uff1a\u201c\u4ed6\u73b0\u5728\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u5979\u4e0d\u7b49\u6bdb\u5ff5\u884c\u56de\u7b54\uff0c\u4fbf\u7f8e\u4e3d\u7740\u8138\u5bb9\u4f46\u6b63\u7ecf\u516b\u677f\u7684\u8bf4\uff1a\u201c\u4f60\u6700\u597d\u4e0d\u8981\u9a97\u6211\u4e5f\u4e0d\u8981\u8bf4\u4f60\u4e0d\u77e5\u9053\uff0c\u56e0\u4e3a\u6211\u73b0\u5728\u813e\u6c14\u4e0d\u597d\uff0c\u800c\u4e14\u6b63\u8981\u627e\u4eba\u53d1\u813e\u6c14\uff01\u201d \u3000\u3000\u5979\u5f88\u65e0\u7406\u7684\u8bf4\u4e86\u4ee5\u4e0b\u4e00\u53e5\uff1a\u201c\u6211\u4e0d\u5e0c\u671b\u6211\u4e0a\u4f60\u3002\u201d \u3000\u3000\u201c\u5927\u4f1a\u5802\u3002\u201d \u3000\u3000\u6bdb\u5ff5\u884c\u53ea\u597d\u8fd9\u6837\u56de\u7b54\u3002 \u3000\u3000\u5927\u4f1a\u5802\u91cc\uff0c\u5145\u6ee1\u4e86\u558a\u6740\u4e4b\u58f0\u3002 \u3000\u3000\u4e00\u7fa4\u4eba\u7a7f\u4e0a\u9053\u888d\uff0c\u6b63\u5728\u6740\u6c14\u9686\u80fa\u7684\u7ec3\u6b66\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u6b63\u597d\u662f\u4e09\u6b65\u5f0f\u7684\u81ea\u7531\u5bf9\u62c6\u3002\u5176\u4e2d\u4e00\u540d\u5b66\u5458\uff0c\u4e0d\u5c0f\u5fc3\u7528\u529b\u8fc7\u731b\uff0c\u8e22\u4e2d\u5bf9\u624b\u7684\u8179\u9645\uff0c\u90a3\u4eba\u75db\u5f97\u8138\u8272\u53d1\u767d\uff0c\u5f2f\u4e0b\u4e86\u8170\uff0c\u5b66\u5458\u8fde\u5fd9\u8bf4\u5bf9\u4e0d\u8d77\u8d54\u4e0d\u662f\u3002 \u3000\u3000\u201c\u521a\u51fb\u9053\u91cc\u6ca1\u6709\u5bf9\u4e0d\u8d77\u3002\u201d\u987e\u5f71\u6c89\u58f0\u559d\u9053\u201c\u5728\u8fd9\u91cc\u8fc7\u62db\uff0c\u751f\u662f\u751f\uff0c\u6b7b\u662f\u6b7b\uff0c\u8d25\u4e86\u80dc\u4e86\u90fd\u662f\u5e38\u4e8b\uff0c\u5a46\u5a46\u5988\u5988\u5bf9\u4e0d\u8d77\uff0c\u8fd9\u91cc\u90fd\u7528\u4e0d\u4e0a\uff0c\u6015\u75db\u7684\u5c31\u4e0d\u8981\u7ed9\u4eba\u51fb\u4e2d\uff0c\u6015\u4f24\u7684\u5c31\u4e0d\u8981\u4e0a\u9635\u6765\u3002\u201d \u3000\u3000\u6570\u540d\u5b66\u5458\u90fd\u75be\u58f0\u5e94\u9053\uff1a\u201c\u662f\uff01\u201d \u3000\u3000\u5374\u542c\u5916\u9762\u6709\u4eba\u634f\u7740\u55d3\u5b50\u602a\u58f0\u602a\u6c14\u7684\u53eb\u4e86\u8d77\u6765\uff1a\u201c\u8981\u662f\u6015\u6b7b\u5462\uff1f\u201d \u3000\u3000\u987e\u5f71\u76b1\u4e86\u76b1\u7709\uff0c\u5e76\u6ca1\u6709\u52a0\u4ee5\u7406\u4f1a\u3002 \u3000\u3000\u4ed6\u4ecd\u7136\u53d1\u51fa\u545b\u559d\uff0c\u6bcf\u559d\u4e00\u58f0\uff0c\u90a3\u4e09\u56de\u5341\u540d\u5b66\u5458\u5c31\u6253\u51fa\u4e00\u4e3e\u3001\u8e22\u51fa\u4e00\u811a\uff1b\u6216\u9000\u6216\u8fdb\u3001\u67b6\u52bf\u5341\u8db3\u3002 \u3000\u3000\u5916\u9762\u53c8\u6709\u4eba\u9634\u9633\u602a\u6c14\u7684\u558a\uff1a\u201c\u5506\uff0c\u597d\u50cf\u771f\u6709\u8fd9\u4e48\u4e24\u4e0b\u5b50\u54e9\uff01\u201d \u3000\u3000\u53e6\u4e00\u4e2a\u563f\u58f0\u9053\uff1a\u201c\u8bf4\u4ec0\u4e48\u90a3\u4e5f\u662f\u65e5\u672c\u4eba\u7684\u8d70\u72d7\uff01\u201d \u3000\u3000\u8fd9\u4e9b\u4eba\u4e00\u5531\u4e00\u642d\uff0c\u4f7f\u5916\u9762\u56f4\u89c2\u5802\u91cc\u7ec3\u6b66\u7684\u7fa4\u4f17\uff0c\u4e9b\u5fae\u9a9a\u52a8\u8d77\u6765\u3002 \u3000\u3000\u5927\u5802\u91cc\u4e00\u4f4d\u9edd\u9ed1\u7ed3\u5b9e\uff0c\u7709\u7c97\u773c\u7ec6\u7684\u5854\u578b\u5927\u6c49\uff0c\u5fcd\u4e0d\u4f4f\u5c31\u8981\u52a8\u6012\uff0c\u970d\u7136\u56de\u9996\uff0c\u987e\u5f71\u75be\u95ee\uff1a \u3000\u3000\u201c\u5df4\u95ed\uff0c\u4f60\u8bb0\u4e0d\u8bb0\u5f97\u521a\u51fb\u9053\u7b2c\u516b\u548c\u5341\u56db\u6761\u95e8\u89c4\uff1f\u201d \u3000\u3000\u201c\u662f\u3002\u201d\u5927\u6c49\u7acb\u5373\u63e1\u7d27\u4e86\u62f3\u5934\u3001\u5782\u4e0b\u4e86\u5934\uff0c\u5927\u58f0\u7684\u80cc\u800c\uff1a\u201c\u2018\u4e0d\u53ef\u597d\u52c7\u6597\u72e0\uff0c\u4e0d\u51c6\u79c1\u6597\u901e\u80fd\u2019\u3001\u201c\u80fd\u5fcd\u4eba\u6240\u4e0d\u80fd\u5fcd\uff0c\u4e3a\u4eba\u6240\u4e0d\u6562\u5c0f\u3002\u201d \u3000\u3000\u53ea\u542c\u5916\u9762\u7684\u8bed\u97f3\u602a\u7b11\u8d77\u6765\uff1a\u201c\u5fcd\u4eba\u4e4b\u6240\u4e0d\u80fd\u5fcd\uff1f\u6015\u662f\u4fbf\u79d8\u5427\u3002\u201d \u3000\u3000\u201c\u4e3a\u4eba\u6240\u4e0d\u6562\u4e3a\uff1f\uff0c\u53e6\u4e00\u4e2a\u63a5\u9053\uff1a\u201c\u5077\u9e21\u6478\u72d7\u3001\u5916\u52a0\u8bb9\u795e\u9a97\u9b3c\u2026\u2026\u201d \u3000\u3000\u987e\u5f71\u5ffd\u7136\u671d\u5916\u559d\u4e86\u4e00\u58f0\uff1a\u201c\u4ec0\u4e48\u4eba\uff1f\u8fdb\u6765\u8bf4\u8bdd\uff01\u201d \u3000\u3000\u5916\u9762\u4e00\u65f6\u9759\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u987e\u5f71\u53ea\u7b49\u4e86\u7247\u523b\uff0c\u4fbf\u8f6c\u8eab\u5411\u5b66\u5458\u4eec\u6717\u58f0\u9053\uff1a\u201c\u6211\u4eec\u8fd9\u513f\u662f\u9053\u9986\uff0c\u5df2\u5411\u5730\u65b9\u653f\u5e9c\u548c\u8b66\u5bdf\u5355\u4f4d\u6ce8\u518c\uff0c\u6211\u4eec\u7ec3\u6211\u4eec\u7684\u6b66\uff0c\u600e\u4e48\u7ec3\u662f\u6211\u4eec\u7684\u4e8b\uff0c\u53cd\u6b63\u6211\u4eec\u6ca1\u72af\u7740\u522b\u4eba\uff0c\u8981\u662f\u771f\u6709\u4eba\u60f9\u6211\u4eec\uff0c\u54b1\u4eec\u4e5f\u4e00\u5b9a\u5e94\u63a5\u7740\u3002\u4e0d\u8fc7\u5f97\u8981\u8bf4\u660e\uff1a\u662f\u6709\u4eba\u6765\u7838\u54b1\u4eec\u7684\u76d8\u5b50\uff0c\u628a\u4ed6\u7ed9\u62fe\u51fa\u53bb\u54b1\u4eec\u53ef\u4e0d\u8d42\u4f24\u836f\u8d39\u3002\u201d \u3000\u3000\u4e00\u4f17\u5b66\u5458\u4eec\u6218\u5fd7\u9ad8\u6602\uff0c\u9f50\u58f0\u9053\uff1a\u201c\u662f\uff01\u201d \u3000\u3000\u53ea\u542c\u5916\u9762\u53c8\u6709\u4eba\u5c16\u58f0\u9053\uff1a\u201c\u65e5\u672c\u8d70\u72d7\uff0c\u88c5\u8154\u4f5c\u6837\uff0c\u6709\u672c\u4e8b\u8ddf\u6211\u4eec\u2018\u640f\u51fb\u6d3e\u2019\u7684\u4eba\u5355\u6311\u53ea\u5bf9\uff01\u201d \u3000\u3000\u201c\u597d\uff0c\u90a3\u4f60\u8fdb\u6765\uff0c\u54b1\u4eec\u8981\u662f\u6709\u4e09\u53ea\u624b\u6b3a\u8d1f\u4e24\u53ea\u624b\u7684\uff0c\u7acb\u5373\u5173\u4e86\u94fa\u5b50\u5c31\u8d70\uff01\u201d\u987e\u5f71\u53c9\u7740\u8170\uff0c\u626c\u58f0\u9053\uff1a\u201c\u662f\u597d\u6c49\u7684\u5c31\u4e0d\u8981\u8eb2\u5728\u9ed1\u6697\u5904\u8bf4\u9634\u8bdd\uff01\u201d \u3000\u3000\u5916\u9762\u4e00\u9635\u9a9a\u7136\u3002 \u3000\u3000\u62c9\u62c9\u626f\u626f\u95f4\uff0c\u7ec8\u4e8e\u8fd8\u662f\u6709\u4e09\u56db\u540d\u770b\u5f97\u51fa\u6765\u662f\u8840\u6c14\u65b9\u521a\u3001\u4f46\u5f88\u6709\u4e24\u4e0b\u5b50\u7684\u9752\u5e74\u5927\u6c49\uff0c\u5145\u6ee1\u6545\u610f\u7684\u8d70\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u5df4\u95ed\u8ddf\u4e24\u4e09\u540d\u5b66\u5458\uff0c\u8d70\u4e0a\u524d\u53bb\uff0c\u95ee\uff1a\u201c\u521a\u624d\u662f\u4f60\u4eec\u5728\u5916\u5934\u5927\u547c\u5c0f\u53eb\u7684\u5427\uff1f\u201d \u3000\u3000\u8d70\u8fdb\u6765\u7684\u56db\u4e2a\u4eba\uff0c\u9759\u4e86\u4e00\u4f1a\u513f\uff0c\u4f60\u770b\u6211\uff0c\u6211\u770b\u4f60\uff0c\u7a81\u7136\u90fd\u6311\u8845\u7684\u7206\u7b11\u8d77\u6765\u3002 \u3000\u3000\u4e00\u4e2a\u8bf4\uff1a\u201c\u662f\u5440\uff0c\u4f60\u80fd\u600e\u6837\uff1f\u201d \u3000\u3000\u4e00\u4e2a\u8bf4\uff1a\u201c\u4f60\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u4f60\u4e0d\u5fc5\u8bf4\uff0c\u6211\u77e5\u9053\u4e86\uff0c\u662f\uff1a\u5df4\u4e00\u95ed\u2014\u2014\uff01\u201d \u3000\u3000\u7136\u540e\u56db\u4eba\u53c8\u602a\u7b11\u8d77\u6765\uff0c\u4e00\u9f50\u558a\uff0c \u3000\u3000\u201c\u5df4\uff0d\uff0d\u95ed\uff0d\uff0d\u732a\uff01\u201d \u3000\u3000\u201c\u5df4\u95ed\u201d\u8fd9\u4e24\u4e2a\u5b57\u7684\u53d1\u97f3\uff0c\u8ddf\u9a6c\u6765\u8bdd\u7684\u732a\uff0c\u662f\u4e00\u6837\u7684\u3002\u53ef\u662f\u8fd9\u4f4d\u201c\u521a\u51fb\u9053\u201d\u526f\u6559\u7ec3\u7684\u786e\u59d3\u5df4\u540d\u95ed\uff0c\u4ed6\u81ea\u5c0f\u4e5f\u7ed9\u4eba\u8c03\u7b11\u60ef\u4e86\uff0c\u53ef\u662f\u8fd9\u51e0\u4eba\u516c\u7136\u5728\u4ed6\u5b66\u751f\u9762\u524d\u6298\u8fb1\u4ed6\uff0c\u6559\u4ed6\u8fd9\u53e3\u6c14\u600e\u4e48\u5fcd\u5f97\u4e0b\u6765\uff0c\u6012\u53f1\uff1a\u201c\u4f60\u4eec\u2026\u2026\u592a\u8fc7\u4efd\u4e86\uff01\u201d \u3000\u3000\u4e00\u4e2a\u6ee1\u8138\u5c3d\u662f\u4e0d\u6000\u597d\u610f\u7b11\u5bb9\u7684\u6c49\u5b50\u6b63\u4e2d\u4e0b\u6000\u4f3c\u7684\u5e94\u6218\uff0c\u7f6e\uff1a\u201c\u4f60\u4eec\u8981\u600e\u6837\uff1f\u6253\u4eba\u554a\uff1f\u201d \u3000\u3000\u7136\u540e\u4ed6\u6d8e\u7740\u8138\u5b89\u8fd1\u5df4\u95ed\uff0c\u7aef\u8be6\u4e86\u4ed6\u597d\u4e00\u4f1a\u513f\uff0c\u624d\u53d1\u73b0\u65b0\u5927\u9646\u4f3c\u7684\u53eb\u9053\uff1a\u201c\u54ce\u54df\uff0c\u6211\u4eec\u7684\u5df4\u2014\u2014\u95ed\u4e00\u53ef\u52a8\u6012\u4e86\uff0c\u4f60\u5c31\u522b\u751f\u90a3\u4e48\u5927\u6c14\u4e86\uff0c\u4f60\u770b\u4f60\u770b\uff0c\u813e\u6c14\u592a\u5927\uff0c\u9752\u6625\u75d8\u2026\uff0c\u4e0d\uff0c\u8001\u6625\u75d8\u53ef\u957f\u5f97\u4e00\u8138\u90fd\u662f\u5462\uff01\u201d \u3000\u3000\u5176\u4f59\u4e3b\u4eba\u90fd\u9644\u548c\u7684\u602a\u7b11\u8d77\u6765\uff0c\u53c8\u4e00\u8d77\u558a\uff0c\u201c\u2014\u2014\u5df4\u4e00\u95ed\u2014\u2014\u732a\uff01\u201d \u3000\u3000\u8fd9\u79cd\u60c5\u5f62\uff0c\u5df2\u662f\u6446\u660e\u8f66\u9a6c\uff1a\u662f\u6765\u8e29\u76d8\u5b50\u3001\u7838\u573a\u7684\u4e86\u3002 \u3000\u3000\u5df4\u95ed\u6c14\u6781\u4e86\u3002 \u3000\u3000\u4ed6\u4e00\u6c14\uff0c\u5c31\u6709\u70b9\u513f\u53e3\u5403\u3002 \u3000\u3000\u201c\u4f60\u2026\u2026\u4f60\u4eec\u201d\u5df4\u95ed\u80c0\u7ea2\u4e86\u8138\uff0c\u201c\u4f60\u4eec\u5927\u592a\u592a\u2026\u2026\u8fc7\u2026\u2026\u201d \u3000\u3000\u90a3\u4e00\u809a\u5b50\u574f\u6c34\u90fd\u5199\u5728\u8138\u4e0a\u7684\u6c49\u5b50\u53c8\u7b11\u4e86\u8d77\u6765\uff1a\u201c\u6211\u4eec\u54ea\u6709\u592a\u592a\uff1f\u4f60\u7684\u592a\u592a\u4e0d\u5c31\u90fd\u662f\u6211\u4eec\u7684\u592a\u5927\u5417\uff1f\u201d \u3000\u3000\u5df4\u95ed\u8eab\u8fb9\u90a3\u4e09\u540d\u5b66\u5458\uff0c\u90fd\u6309\u637a\u4e0d\u4f4f\u4e86\uff0c\u8d70\u4e0a\u524d\u53bb\uff0c\u8981\u628a\u8fd9g\u4e2a\u5bfb\u8845\u7684\u4eba\u63a8\u51fa\u53bb\uff0c\u4e0d\u6599\uff0c\u90a3\u574f\u5f97\u4e03\u60c5\u4e0a\u8138\u7684\u6c49\u5b50\u559d\u4e86\u4e00\u58f0\uff0c\u4ed6\u8eab\u540e\u7684\u4e09\u4eba\u7a81\u7136\u62a2\u4e0a\u524d\u53bb\uff0c\u51fa\u624b\u3002 \u3000\u3000\u51fa\u624b\u6781\u5feb\u3002 \u3000\u3000\u4e14\u51c6\u3002 \u3000\u3000\u66f4\u72e0\u3002 \u3000\u3000\u5df4\u95ed\u8eab\u8fb9\u7684\u4eba\u539f\u5148\u5e76\u6ca1\u6709\u9632\u8303\u3002 \u3000\u3000\u4ed6\u4eec\u539f\u5148\u53ea\u60f3\u628a\u8fd9\u51e0\u540d\u4e0d\u901f\u4e4b\u5ba2\u63a8\u51fa\u53bb\u3002 \u3000\u3000\u6ca1\u60f3\u5230\u90a3\u4eba\u62a2\u5148\u52a8\u624b\uff0c\u800c\u5b9e\u9645\u4e0a\uff0c\u4e00\u4e2a\u7ec3\u8fc7\u6b66\u7684\u4eba\uff0c\u8c01\u5148\u62a2\u5f97\u4e86\u5148\u624b\u800c\u8c01\u4e00\u65f6\u758f\u5ffd\uff0c\u5f80\u5f80\u5c31\u8db3\u4ee5\u81f4\u547d\u843d\u8d25\u3002\u522b\u8bf4\u529f\u529b\u76f8\u4eff\uff0c\u5c31\u7b97\u662f\u6b66\u529f\u60ac\u6b8a\uff0c\u4f5c\u4e3a\u4e00\u4e2a\u73b0\u5b9e\u91cc\u7684\u6b66\u8005\uff0c\u53ea\u8981\u4e00\u65f6\u4e0d\u5bdf\uff0c\u8ba9\u5bf9\u65b9\u593a\u5f97\u5148\u673a\uff0c\u53cd\u800c\u60e8\u8d25\u5728\u4e00\u4e2a\u6b66\u529f\u8fdc\u900a\u81ea\u5df1\u7684\u4eba\u624b\u91cc\uff0c\u7edd\u5bf9\u4e0d\u662f\u4ef6\u53ef\u4f84\u7684\u4e8b\u3002 \u3000\u3000\u8fd9\u4e09\u540d\u5b66\u5458\u5c31\u5403\u4e8f\u5728\u8fd9\u91cc\u3002 \u3000\u3000\u662f\u90a3\u4e09\u4e2a\u5bfb\u8845\u8005\u5148\u52a8\u7684\u624b\u3002 \u3000\u3000\u4ed6\u4eec\u90fd\u6ca1\u6709\u60f3\u5230\u5bf9\u65b9\u4f1a\u7a81\u7136\u52a8\u624b\u3001\u6240\u4ee5\u90fd\u88ab\u51fb\u5012\u5728\u5730\u3002 \u3000\u3000\u51e1\u662f\u7ec3\u8fc7\u6b66\u7684\u4eba\uff0c\u8981\u628a\u5bf9\u65b9\u5728\u4e00\u62db\u4e4b\u95f4\u51fb\u5012\uff0c\u4f7f\u5bf9\u65b9\u6218\u529b\u5168\u5931\uff0c\u51b3\u4e0d\u662f\u4ef6\u96be\u4e8b\u3002 \u3000\u3000\u8fd9\u4e09\u540d\u5b66\u5458\u7684\u60c5\u5f62\u5c31\u662f\u8fd9\u6837\u3002 \u3000\u3000\u4e00\u5239\u90a3\u95f4\uff0c\u4e09\u4e2a\u4eba\u90fd\u5012\u4e86\u4e0b\u53bb\uff0c\u75db\u82e6\u5f97\u4e94\u5b98\u90fd\u76b1\u5728\u4e00\u8d77\uff0c\u4e00\u65f6\u90fd\u722c\u4e0d\u8d77\u6765\u3002 \u3000\u3000\u5df4\u95ed\u6012\u53f1\uff1a\u201c\u4f60\u4eec\u2026\u2026\u6253\u4eba\uff01\u201d \u3000\u3000\u4ed6\u7684\u8bdd\u4ecd\u7136\u8bf4\u5f97\u7ed3\u7ed3\u5df4\u5df4\u3002 \u3000\u3000\u4f46\u4ed6\u7684\u51fa\u624b\uff0c\u5374\u5e72\u51c0\u5229\u843d\u3002 \u3000\u3000\u56e0\u4e3a\u90a3\u4e09\u4e2a\u4eba\u653e\u5012\u4e86\u4e09\u540d\u5b66\u5458\u540e\uff0c\u7acb\u5373\u5206\u6210\u4e09\u4e2a\u4e0d\u540c\u7684\u65b9\u5411\uff0c\u4e00\u9f50\u5411\u4ed6\u56f4\u653b\u3002 \u3000\u3000\u5728\u5b9e\u6218\u91cc\uff0c\u5176\u5b9e\u6700\u6015\u7684\u5c31\u662f\u9047\u5230\u80cc\u8179\u53d7\u654c\u7684\u56f4\u653b\u3002 \u3000\u3000\u56e0\u4e3a\u8c01\u90e8\u4e0d\u662f\u6b66\u4fa0\u5c0f\u8bf4\u91cc\u7684\u4eba\u7269\uff0c\u8c01\u7684\u80cc\u540e\u90fd\u6ca1\u957f\u773c\u775b\uff0c\u53cc\u62f3\u96be\u654c\u56db\u624b\uff0c\u53ea\u8981\u6709\u4eba\u5de6\u53f3\u524d\u540e\u5939\u51fb\uff0c\u6b66\u529f\u518d\u9ad8\u7684\u4eba\u90fd\u8981\u5403\u5927\u4e8f\u3002 \u3000\u3000\u4e0d\u8fc7\u8fd9\u79cd\u60c5\u5f62\uff0c\u53d1\u751f\u5728\u5df4\u95ed\u8eab\u4e0a\u4f3c\u662f\u4e2a\u4f8b\u5916\u3002 \u3000\u3000\u4e09\u4e2a\u4eba\u4e00\u9f50\u5411\u4ed6\u51fa\u624b\u3002 \u3000\u3000\u4ed6\u53ea\u6765\u5f97\u53ca\u5411\u4e00\u4eba\u8fd8\u51fb\u3002 \u3000\u3000\u4ed6\u751a\u81f3\u6765\u4e0d\u53ca\u95ea\u8eb2\u3002 \u3000\u3000\u8fd9\u4e9b\u4f3c\u4e4e\u5e76\u6ca1\u6709\u4ec0\u4e48\u201c\u4f8b\u5916\u201d\u3002 \u3000\u3000\u4f8b\u5916\u7684\u662f\uff1a\u4e09\u4e2a\u4eba\u81f3\u5c11\u6709\u56db\u62f3\u4e24\u811a\u540c\u65f6\u51fb\u4e2d\u4ed6\u3002 \u3000\u3000\u800c\u4e14\u662f\u8981\u5bb3\u3002 \u3000\u3000\u4ed6\u7adf\u633a\u4f4f\u4e86\u3002 \u3000\u3000\u6ca1\u6709\u4e8b\u3002 \u3000\u3000\u4ed6\u53ea\u53cd\u51fb\u4e00\u62f3\u3002 \u3000\u3000\u4e00\u62f3\u3002 \u3000\u3000\u4e00\u62f3\u5bf9\u65b9\u5c31\u5012\u4e86\u4e00\u540d\u3002 \u3000\u3000\u7136\u540e\u5269\u4e0b\u7684\u4e24\u4eba\uff0c\u53c8\u5728\u8fd9\u77ac\u95f4\u51fb\u4e2d\u4ed6\u4e09\u62f3\u4e00\u811a\uff0c\u5176\u4e2d\u6709\u4e00\u4e2a\u4e00\u819d\u5934\u8fd8\u9876\u5165\u4ed6\u7684\u9f20\u6eaa\u7a74\u3002 \u3000\u3000\u4ed6\u53c8\u8fd8\u624b\u4e00\u51fb\u3002 \u3000\u3000\u4e00\u51fb\uff0c\u5c31\u5012\u4e0b\u4e00\u4eba\u3002 \u3000\u3000\u4e00\u5012\uff0c\u5c31\u722c\u4e0d\u8d77\u6765\u3002 \u3000\u3000\u800c\u4ed6\uff0c\u8fde\u53d7\u591a\u6b21\u91cd\u51fb\uff0c\u5374\u50cf\u4e2a\u6ca1\u4e8b\u7684\u4eba\u4f3c\u7684\u3002 \u3000\u3000\u4eff\u4f5b\u8fde\u88ab\u51fb\u7684\u611f\u89c9\u4e5f\u6ca1\u6709\u3002 \u3000\u3000\u4ed6\u7684\u5bf9\u624b\u53ea\u5269\u4e0b\u4e86\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u8fd9\u4eba\u505a\u4e86\u4e00\u4ef6\u4e8b\u3002 \u3000\u3000\u4ed6\u62d4\u51fa\u4e86\u5200\u5b50\u3002 \u3000\u3000\u5f39\u7c27\u5200\u3002 \u3000\u3000\u4e00\u5200\u5c31\u5411\u5df4\u95ed\u7684\u53f3\u808b\u626c\u53bb\u3002 \u3000\u3000\uff12\u3001\u7528\u62f3\u5934\u8bf4\u51fa\u6765\u7684\u9053\u7406 \u3000\u3000\u7136\u540e\u662f\u7ed3\u679c\u3002 \u3000\u3000\u7ed3\u679c\u5b8c\u5168\u548c\u524d\u56de\u4e24\u4eba\u4e00\u6837\u3002 \u3000\u3000\u4e00\u6a21\u4e00\u6837\u3002 \u3000\u3000\u4e00\u4eba\u5012\u4e86\u4e0b\u53bb\uff0c\u4e00\u4eba\u4ecd\u7136\u5c79\u7acb\u7740\u3002 \u3000\u3000\u5012\u4e0b\u53bb\u7684\u662f\u62ff\u5200\u5b50\u7684\u3002 \u3000\u3000\u5c79\u7acb\u7684\u4ecd\u7136\u662f\u5df4\u95ed\u3002 \u3000\u3000\u8fd9\u4e2a\u65f6\u5019\u7684\u5df4\u95ed\uff0c\u7684\u786e\u5f88\u201c\u5df4\u95ed\u201d\u3002 \u3000\u3000\u2014\u2014\u201c\u5df4\u95ed\u201d\u7684\u53d1\u97f3\uff0c\u5728\u9a6c\u6765\u8bdd\u662f\u201c\u732a\u201d\u7684\u610f\u601d\uff0c\u4f46\u5728\u5f53\u5730\u201c\u5df4\u95ed\u201d\u7684\u7ca4\u97f3\uff0c\u4e5f\u6709\u7740\u201c\u9a84\u50b2\u201d\u3001\u201c\u4e0d\u53ef\u4e00\u4e16\u201d\u7684\u610f\u601d\u3002 \u3000\u3000\u4e00\u5df4\u95ed\u73b0\u5728\u4fbf\u5f88\u6709\u8fd9\u4e2a\u610f\u601d\u3002 \u3000\u3000\u8ddf\u4ed6\u5bf9\u624b\u7684\u4e09\u4e2a\u4eba\uff0c\u90fd\u5012\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u5bf9\u624b\u53ea\u5269\u4e0b\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u90a3\u4e2a\u4e00\u8138\u597d\u76f8\u7684\u4eba\u3002 \u3000\u3000\u8fd9\u4eba\u5e76\u6ca1\u6709\u8d81\u673a\u8e6d\u8d70\u3002 \u3000\u3000\u53cd\u800c\u52a8\u624b\u3002 \u3000\u3000\u4ed6\u52a8\u7684\u4e5f\u662f\u62f3\u5934\u3002 \u3000\u3000\u8fd9\u4e00\u62f3\u4e0d\u5e26\u58f0\u606f\u3002 \u3000\u3000\u51e1\u662f\u7ec3\u8fc7\u6b66\u7684\u4eba\u90fd\u77e5\u9053\uff0c\u8981\u628a\u62f3\u811a\u7ec3\u5f97\u96c4\u52b2\u6709\u529b\u3001\u864e\u864e\u751f\u98ce\uff0c\u7edd\u5bf9\u4e0d\u662f\u4ef6\u96be\u4e8b\uff0c\u96be\u7684\u662f\u51fa\u624b\u91cd\u82e5\u5343\u94a9\u4f46\u53c8\u62f3\u91cd\u82e5\u8f7b\u3001\u4e0d\u5e26\u98ce\u58f0\uff0c\u8fd9\u624d\u80fd\u4f24\u4eba\u4e8e\u4e0d\u5907\u3001\u6740\u4eba\u4e8e\u65e0\u5f62\u3002 \u3000\u3000\u8fd9\u4eba\u89c1\u4e09\u4e2a\u540c\u4f34\u5168\u5012\u4e86\u4e0b\u53bb\uff0c\u4f9d\u7136\u4e0d\u9000\uff0c\u53cd\u800c\u51fa\u88ad\uff0c\u6b66\u529f\u5982\u4f55\u5012\u5728\u5176\u6b21\uff0c\u81f3\u5c11\u5728\u6b66\u80c6\u4e0a\u4ea4\u4ee3\u5f97\u8fc7\u53bb\u3002 \u3000\u3000\u5df4\u95ed\u987f\u65f6\u751f\u4e86\u201c\u8bc6\u82f1\u96c4\u8005\u91cd\u82f1\u96c4\u201d\u4e4b\u610f\u3002 \u3000\u3000\u4ed6\u662f\u4e00\u4e2a\u6b66\u8005\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u656c\u91cd\u771f\u6b63\u7684\u6b66\u8005\u3002 \u3000\u3000\u4ed6\u66fe\u8ddf\u987e\u5f71\u4e94\u6b21\u4ea4\u624b\uff0c\u4e94\u6b21\u60e8\u8d25\uff0c\u987e\u5f71\u867d\u7136\u6bd4\u4ed6\u5c0f\u4e03\u5c81\uff0c\u5374\u4f7f\u4ed6\u670d\u5f97\u4e94\u4f53\u201c\u63b7\u201d\u5730\uff0c\u6539\u62dc\u987e\u5f71\u4e3a\u5e08\u3002\u987e\u5f71\u4ee5\u5927\u80c6\u521b\u65b0\u3001\u5bfb\u627e\u4e2a\u4eba\u98ce\u683c\u795e\u91c7\u7684\u65b9\u5f0f\u6765\u70b9\u5316\u4ed6\uff0c\u4f7f\u4ed6\u7684\u6b66\u529f\uff0c\u7a81\u98de\u731b\u8fdb\uff0c\u8fd9\u4f7f\u4ed6\u8dc3\u5347\u5230\u4e00\u4e2a\u4ed6\u524d\u6240\u672a\u6709\u7684\u5883\u5730\u3002 \u3000\u3000\u7531\u662f\uff0c\u4ed6\u6781\u611f\u8c22\u987e\u5f71\u3002 \u3000\u3000\u4ed6\u662f\u4e2a\u6b66\u75f4\u3002 \u3000\u3000\u4ed6\u559c\u6b22\u6240\u6709\u7684\u540c\u597d\u8005\u3002 \u3000\u3000\u4ed6\u4e00\u89c1\u90a3\u4eba\u4e0d\u9000\uff0c\u800c\u4e14\u51fa\u62f3\u9ad8\u660e\uff0c\u5c31\u6253\u4ece\u5fc3\u91cc\u559c\u6b22\u3002\u4e00\u4e2a\u9ad8\u5c1a\u7684\u6b66\u8005\uff0c\u4eba\u518d\u574f\u4e5f\u574f\u4e0d\u5230\u54ea\u91cc\u53bb\u3002\u4ed6\u4e00\u5411\u90fd\u8ba4\u4e3a\u8fd9\u662f\u4e2a\u771f\u7406\u3002 \u3000\u3000\u53ef\u60dc\u201c\u771f\u7406\u201d\u6709\u65f6\u5019\u4e5f\u6709\u5f88\u591a\u79cd\u3001\u8bb8\u591a\u9762\u3002 \u3000\u3000\u6709\u65f6\uff0c\u4f60\u8ba4\u4e3a\u7684\u201c\u771f\u7406\u201d\uff0c\u522b\u4eba\u4e0d\u89c9\u5f97\u662f\u771f\u7406\u3001\u522b\u4eba\u6240\u6267\u6301\u7684\u771f\u7406\u3001\u4f60\u4e5f\u53ea\u5f53\u662f\u6b6a\u7406\u3002 \u3000\u3000\u6b63\u5982\u6709\u7684\u4eba\u8ba4\u4e3a\u6b66\u529f\u4e0d\u4e89\u80dc\u8d25\u53ea\u4e89\u9ad8\u4f4e\uff0c\u4e5f\u6709\u4eba\u8ba4\u4e3a\u597d\u7684\u6b66\u529f\u5c31\u662f\u8981\u628a\u654c\u4eba\u6253\u5012\uff0c\u4f60\u8bf4\u54ea\u6837\u624d\u662f\u771f\u7406\u3002 \u3000\u3000\u5df4\u95ed\u9047\u4e0a\u7684\u4e0d\u662f\u771f\u7406\u3002 \u3000\u3000\u81f3\u5c11\u4e0d\u662f\u4ed6\u4fe1\u5ff5\u91cc\u7684\u201c\u771f\u7406\u201d\u3002 \u3000\u3000\u90a3\u4e00\u8138\u72e1\u8bc8\u7684\u6c49\u5b50\u4e00\u62f3\u6253\u6765\uff0c\u5df4\u95ed\u4e00\u638c\u63a5\u8fc7\u3002 \u3000\u3000\u4ed6\u7684\u624b\u5927\u3002 \u3000\u3000\u5927\u5982\u6d77\u7897\u3002 \u3000\u3000\u4ed6\u4e00\u5411\u90fd\u662f\u4ee5\u624b\u53bb\u63a5\u5bf9\u65b9\u7684\u62f3\uff0c\u518d\u51f6\u731b\u7684\u62f3\u5934\uff0c\u53ea\u8981\u7ed9\u4ed6\u5728\u638c\u4e2d\u8f7b\u8f7b\u4e00\u627c\uff0c\u5bf9\u65b9\u7684\u624b\u8155\u4e0d\u65ad\u4e5f\u5f97\u8131\u81fc\u3002 \u3000\u3000\u5bf9\u4e8e\u8fd9\u70b9\uff0c\u4ed6\u4e00\u5411\u90fd\u5f88\u6709\u4fe1\u5fc3\u3002 \u3000\u3000\u8fd9\u6b21\u4e5f\u6ca1\u6709\u4f8b\u5916\u3002 \u3000\u3000\u4ed6\u4e00\u4f38\u624b\uff0c\u5c31\u63a5\u4f4f\u4e86\u5bf9\u65b9\u7684\u62f3\u5934\u3002 \u3000\u3000\u6ca1\u6599\u5230\u7684\u662f\uff1a\u638c\u5fc3\u4e00\u75db\uff0c\u8ddf\u7740\u4e00\u9ebb\u3002 \u3000\u3000\u8fd9\u4e00\u75db\u4e00\u9ebb\uff0c\u4f7f\u4ed6\u6240\u6709\u7684\u63a5\u9547\u800c\u6765\u7684\u53cd\u5e94\uff0c\u90fd\u65e0\u6cd5\u6b63\u5e38\u8fd0\u4f5c\uff0c\u800c\u90a3\u5978\u733e\u6c49\u5b50\u7684\u5de6\u62f3\uff0c\u5df2\u75db\u51fb\u4ed6\u7684\u53f3\u592a\u9633\u7a74\u3002 \u3000\u3000\u2014\u2014\u51fb\u4e2d\u4e4b\u540e\u7684\u60c5\u5f62\u5982\u4f55\uff0c\u7b80\u76f4\u96be\u4ee5\u60f3\u50cf\u3002 \u3000\u3000\u5e78\u597d\u90a3\u4e00\u62f3\u5e76\u6ca1\u6709\u51fb\u4e2d\u3002 \u3000\u3000\u2014\u2014\u4e0d\u662f\u51fb\u4e0d\u4e2d\uff0c\u800c\u662f\u56e0\u4e3a\u90a3\u4e00\u62f3\u5728\u534a\u9014\u7ed9\u7262\u7262\u7684\u6263\u4f4f\uff0c\u7d27\u7d27\u7684\u6293\u7740\u3002 \u3000\u3000\u987e\u5f71\u3002 \u3000\u3000\u987e\u5f71\u4f38\u51fa\u4e86\u53cc\u624b\u3002 \u3000\u3000\u4ed6\u4e0d\u662f\u201c\u63e1\u624b\u201d\u3002 \u3000\u3000\u63e1\u624b\u901a\u5e38\u662f\u8868\u793a\u53cb\u5584\u548c\u4eb2\u5462\u3002 \u3000\u3000\u4ed6\u662f\u63e1\u4f4f\u4e86\u5bf9\u65b9\u7684\u62f3\u8155\u3002 \u3000\u3000\u6325\u62f3\u901a\u5e38\u662f\u4ee3\u8868\u5c3d\u529b\u548c\u6253\u51fb\u3002 \u3000\u3000\u987e\u5f71\u63e1\u4f4f\u4e86\u5bf9\u65b9\u6325\u62f3\u7684\u8155\uff0c\u65e0\u7591\u7b49\u4e8e\u662f\u627c\u6740\u4e86\u5bf9\u65b9\u6253\u51fb\u7684\u529b\u91cf\u3002 \u3000\u3000\u4e00\u5207\u90fd\u9759\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u90a3\u72e1\u6ed1\u6c49\u5b50\u7684\u62f3\u5934\u63e1\u5728\u987e\u5f71\u624b\u91cc\uff0c\u4e00\u5982\u5200\u5728\u94c1\u7827\u4e0a\uff0c\u4efb\u4ed6\u600e\u4e48\u62bd\u62d4\uff0c\u4e5f\u6536\u4e0d\u56de\u539f\u662f\u5c5e\u4e8e\u4ed6\u81ea\u5df1\u7684\u4e00\u53cc\u624b\u3001\u4e00\u5bf9\u62f3\u5934\u3002 \u3000\u3000\u7136\u540e\uff0c\u987e\u5f71\u7f13\u7f13\u7684\u8bf4\uff1a \u3000\u3000\u201c\u8fd9\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4ed6\u95ee\u90a3\u540d\u5978\u6ed1\u6c49\u5b50\u3002 \u3000\u3000\u62f3\u5934\u3002 \u3000\u3000\u4eba\u7684\u62f3\u5934\u5c31\u662f\u62f3\u5934\u3002\u65e0\u8bba\u5927\u5c0f\u62f3\u5934\uff0c\u9038\u662f\u62f3\u5934\u3002\u5c31\u7b97\u6709\u7684\u4eba\u5929\u751f\u6709\u516d\u53ea\u624b\u6307\uff0c\u6216\u4e0d\u5c0f\u5fc3\u53ea\u641e\u5f97\u5269\u4e0b\u56db\u53ea\u624b\u6307\uff0c\u4f46\u63e1\u8d77\u624b\u6307\u6536\u5165\u638c\u5fc3\u8fd8\u662f\u62f3\u5934\u4e00\u4e2a\u3002 \u3000\u3000\u53ea\u4e0d\u8fc7\uff0c\u90a3\u4eba\u7684\u62f3\u5934\uff0c\u6bd4\u8f83\u7279\u522b\u4e86\u4e00\u4e9b\u3002 \u3000\u3000\u4ed6\u6234\u4e86\u6212\u6307\u3002 \u3000\u3000\u6234\u6212\u6307\u4e5f\u6ca1\u4ec0\u4e48\u7279\u522b\u3002 \u3000\u3000\u4f60\u5728\u8857\u4e0a\u8d70\uff0c \u3000\u3000\u5341\u4e2a\u4eba\u91cc\u603b\u6709\u4e09\u4e2a\u4eba\u662f\u6234\u7740\u6212\u6307\u7684\u3002 \u3000\u3000\u53ea\u4e0d\u8fc7\u90a3\u4eba\u6234\u7684\u662f\u9abc\u9ac5\u5934\u6212\u6307\u3002 \u3000\u3000\u5de6\u53f3\u4e2d\u6307\u90fd\u6709\u4e00\u53ea\u3002 \u3000\u3000\u800c\u4e14\u4ed4\u7ec6\u770b\u53bb\uff0c \u3000\u3000\u9876\u4e0a\u90fd\u6709\u4e00\u53e3\u5c16\u9488\u3002 \u3000\u3000\u5df4\u95ed\u7684\u4e00\u9ebb\u4e00\u75db\uff0c\u5c31\u662f\u8fd9\u53e3\u9488\u523a\u5728\u4ed6\u638c\u5fc3\u7684\u7ed3\u679c\u3002 \u3000\u3000\u5982\u679c\u4ed6\u7684\u592a\u9633\u7a74\u7ed9\u8fd9\u6837\u7684\u4e00\u652f\u9488\u523a\u8fc7\u53bb\uff0c\u60c5\u5f62\u5982\u4f55\uff0c\u8fd8\u771f\u4e0d\u582a\u60f3\u50cf\u3002 \u3000\u3000\u6240\u4ee5\u987e\u5f71\u634f\u4f4f\u4e86\u90a3\u4eba\u7684\u62f3\u5934\uff0c\u5c31\u50cf\u6293\u4f4f\u4e86\u6bd2\u86c7\u7684\u4e03\u5bf8\uff0c\u7136\u540e\u624d\u4ee5\u4e00\u79cd\u6c89\u7740\u3001\u6c89\u51b7\u3001\u6c89\u91cd\u7684\u8bed\u8c03\uff0c\u8fd9\u6837\u7684\u95ee\u4ed6\u3002 \u3000\u3000\u90a3\u4eba\u4e0d\u77e5\u600e\u4e48\u56de\u7b54\u597d\u3002 \u3000\u3000\u987e\u5f71\u7684\u773c\u795e\u51b7\u5f97\u50cf\u51b0\u9547\u8fc7\u7684\u6708\u8272\uff1a\u201c\u4f60\u662f\u90b9\u5347\u7684\u4eba\uff1f\u201d \u3000\u3000\u90a3\u4eba\u7528\u529b\u6323\u4e0d\u8131\uff0c\u60f3\u8981\u8d77\u811a\uff0c\u987e\u5f71\u8d34\u8fd1\uff0c\u811a\u4e00\u6a2a\uff0c\u5df2\u622a\u4f4f\u4e86\u4ed6\u4e0b\u76c6\u4efb\u4f55\u53ef\u4ee5\u53cd\u51fb\u7684\u52a8\u4f5c\u3002 \u3000\u3000\u201c\u6211\u77e5\u4f60\u5c31\u662f\u2018\u9c7c\u751f\u2019\u201d\u987e\u5f71\u4e00\u5b57\u4e00\u5b57\u5730\u95ee\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u8981\u5411\u6211\u4eec\u4e0b\u6bd2\u624b\uff1f\u201d \u3000\u3000\u9c7c\u751f\u6323\u8131\u4e0d\u4e86\uff0c\u6025\u7ea2\u4e86\u8138\uff0c\u5927\u53eb\uff1a\u201c\u5173\u4f60\u5c41\u4e8b\uff01\u201d \u3000\u3000\u201c\u683c\u201d\u7684\u4e00\u58f0\u3002 \u3000\u3000\u201c\u9c7c\u751f\u201d\u60e8\u53eb\u5f97\u50cf\u4e00\u652f\u5200\u624e\u8fdb\u4ed6\u7684\u80a0\u80c3\u91cc\u3002 \u3000\u3000\u4ed6\u7684\u624b\u8155\u5df2\u7ed9\u987e\u5f71\u62c9\u8131\u4e86\u4e00\u53ea\uff0c\u60e8\u547c\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u597d\u72e0\u2026\u2026\u201d \u3000\u3000\u987e\u5f71\u53ea\u6de1\u8c08\u7684\u8bf4\uff1a\u201c\u90a3\u662f\u4f60\u7684\u4e8b\u5173\u6211\u5c41\u4e8b\u3002\u201d \u3000\u3000\u5c31\u5728\u8fd9\u65f6\u5019\uff0c\u5ffd\u542c\u5916\u9762\u6709\u4e00\u4e2a\u90c1\u96f7\u4e5f\u4f3c\u7684\u58f0\u97f3\u5927\u53f1\u9053\uff1a\u201c\u8981\u5e72\u4ec0\u4e48\uff01\u5f00\u9053\u573a\u5b50\u6765\u6b3a\u8d1f\u4eba\u54a7\uff01\u201d \u3000\u3000\u90a3\u4e2a\u201c\u9c7c\u751f\u201d\u8f6c\u8fc7\u5934\u53bb\uff0c\u4e00\u89c1\u6765\u4eba\uff0c\u559c\u5982\u7687\u6069\u5927\u8d66\uff0c\u5927\u53eb\uff1a\u201c\u8001\u5927\uff0c\u8001\u5927\uff0c\u8fd9\u738b\u516b\u86cb\u5728\u6b3a\u8d1f\u6211\u4eec\u5144\u5f1f\uff0c\u4e0d\u7ed9\u60a8\u9762\u5b50\uff0c\u5feb\u6765\u6551\u6211\uff01\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u4e00\u8bf4\uff0c\u4e24\u8fb9\u90fd\u695e\u4e86\u4e00\u6123\u3001\u5446\u4e86\u4e00\u5446\u3002 \u3000\u3000\u6765\u4eba\u662f\u725b\u4e3d\u751f\u3002 \u3000\u3000\u4ed6\u4e00\u8fc7\u6765\uff0c\u5c31\u770b\u89c1\u9053\u573a\u91cc\u5012\u4e0b\u4e86\u56db\u4e94\u4e2a\u4eba\uff0c\u54fc\u54fc\u537f\u537f\u7684\u4e00\u65f6\u722c\u4e0d\u8d77\u6765\uff0c\u800c\u987e\u5f71\u6b63\u5728\u627c\u65ad\u4e86\u4e00\u4e2a\u4eba\u7684\u8155\u5b50\u8fd8\u8bf4\u201c\u5173\u6211\u5c41\u4e8b\u201d\uff0c\u4ed6\u4e00\u65f6\u770b\u4e0d\u8fc7\u53bb\uff0c\u4fbf\u633a\u8eab\u8d70\u4e86\u8fc7\u53bb\uff0c\u626c\u58f0\u8bf4\u4e86\u90a3\u4e00\u53e5\u8bdd\u3002 \u3000\u3000\u6ca1\u60f3\u90a3\u6c49\u5b50\u7ade\u53eb\u4ed6\u4e3a\u201c\u8001\u5927\u201d\u3002 \u3000\u3000\u2014\u2014\u4ed6\u51e0\u65f6\u5f53\u8d77\u201c\u8001\u5927\u201d\u6765\u4e86\uff1f \u3000\u3000\u2014\u2014\u81ea\u4ece\u4ed6\u53eb\u9648\u5251\u8c01\u505a\u201c\u8001\u5927\u201d\u540e\uff0c\u4ed6\u5df2\u597d\u4e45\u6ca1\u505a\u8fc7\u201c\u8001\u5927\u201d\u4e86\u3002 \u3000\u3000\u4ed6\u66fe\u542c\u201c\u8001\u5927\u201d\u8bf4\u8fc7\uff1a\u201c\u7b28\u4eba\u624d\u8981\u5f53\u8001\u5927\u3002\u5f53\u8001\u5927\uff0c\u8981\u6bd4\u522b\u4eba\u591a\u8d1f\u8d23\u4efb\u3002\u8981\u5f53\u597d\u8001\u5927\uff0c\u8fd8\u4e0d\u80fd\u6bd4\u4eba\u591a\u4eab\u53d7\u6743\u76ca\u3002\u5f53\u8001\u5927\u771f\u4e0d\u5bb9\u6613\u554a\uff0c\u4f60\u5e72\u5f97\u597d\u522b\u4eba\u4ee5\u4e3a\u662f\u5e94\u8be5\u7684\uff0c\u4f60\u4e8e\u4e0d\u597d\u522b\u4eba\u8fd8\u89c9\u5f97\u4f60\u6cbb\u8be5\u3002\u73b0\u5728\u5e02\u573a\u4e0a\u6d41\u884c\u4f60\u53eb\u6211\u4e00\u65ec\u2018\u5927\u54e5\u2019\u6211\u79f0\u4f60\u4e00\u53e5\u2018\u8001\u5927\u2019\uff0c\u751a\u81f3\u53eb\u5728\u5634\u91cc\u9a82\u5728\u5fc3\u91cc\uff0c\u5230\u5e95\u8fd8\u6709\u8c01\u662f\u5f53\u771f\u7684\uff1f\u73b0\u5728\u6c5f\u6e56\u9053\u4e0a\u7684\u6c49\u5b50\uff0c\u90fd\u662f\u4e0d\u8bb2\u4e49\u6c14\u7684\u4e86\u3002\u81f3\u4e8e\u5bf9\u8f88\u4efd\u5206\u9645\uff0c\u6709\u6743\u6709\u52bf\u4fbf\u662f\u552f\u547d\u662f\u4ece\uff0c\u6ca1\u5976\u7684\u4e48\uff1f\u7ba1\u5b83\u7239\u5a18\uff01\u4f60\u60f3\uff0c\u4e07\u4e00\u4e0d\u614e\uff0c\u5f53\u4e0a\u8fd9\u79cd\u4eba\u7684\u8001\u5927\uff0c\u4f60\u8bf4\u662f\u4e0d\u662f\u6b7b\u65e0\u846c\u8eab\u4e4b\u5730\uff1f\u201d \u3000\u3000\u73b0\u5728\u4ed6\u5ffd\u7136\u88ab\u79f0\u4e3a\u201c\u8001\u5927\u201d\uff0c\u4e00\u65f6\u8fd8\u672a\u4f1a\u8fc7\u610f\u6765\uff0c\u53ea\u89c9\u5f97\u6709\u4e9b\u9676\u9676\u7136\u3002 \u3000\u3000\u987e\u5f71\u5219\u6709\u4e9b\u6115\u7136\u3002 \u3000\u3000\u4ed6\u8ba4\u5f97\u8fd9\u4e2a\u5927\u5757\u5934\u5c31\u662f\u6253\u4ece\u5916\u56fd\u6765\u7684\u4e0d\u901f\u4e4b\u5ba2\u3001\u540c\u65f6\u4e5f\u662f\u9a9a\u62e2\u5f20\u5c0f\u6101\u7684\u6076\u5ba2\u4e4b\u4e00\u3002 \u3000\u3000\u2014\u2014\u4ed6\u53ea\u662f\u6ca1\u60f3\u5230\u201c\u9c7c\u751f\u201d\u8fd9\u51e0\u4e2a\u4e0b\u4e09\u8934\u7684\u8d25\u7c7b\uff0c\u5c45\u7136\u4e5f\u662f\u5e94\u8fd9\u4e8e\u4eba\u4e4b\u547d\u800c\u6765\u7684\u3002 \u3000\u3000\u201c\u539f\u6765\u662f\u4f60\uff01\u201d\u987e\u5f71\u51b7\u54fc\u9053\uff1a\u201c\u679c\u7136\u4e0d\u662f\u4ec0\u4e48\u597d\u4e1c\u897f\uff01\u201d \u3000\u3000\u201c\u4f60\u2026\u2026\u4f60\u4e0d\u662f\u4e1c\u897f\uff01\u201d\u725b\u4e3d\u751f\u672c\u6765\u5c31\u4e0d\u5584\u8f9e\u4ee4\uff0c\u4e00\u6c14\u8d77\u6765\u5c31\u66f4\u4e0d\u4f1a\u8bf4\u8bdd\uff0c\u4f46\u800c\u4eca\u56de\u4e86\u4e00\u65ec\u5634\uff0c\u5374\u8bef\u6253\u8bef\u7740\uff0c\u4e00\u9488\u89c1\u8840\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u5ffd\u95fb\u4e00\u58f0\u5486\u54ee\u3002 \u3000\u3000\u5df4\u95ed\u638c\u5fc3\u90a3\u4e00\u9635\u9ebb\u75d2\u5df2\u7ecf\u8fc7\u53bb\uff0c\u4ed6\u4e5f\u5df2\u53d1\u73b0\u4e86\u201c\u9c7c\u751f\u201d\u5bf9\u4ed6\u65bd\u6697\u7b97\uff0c\u6124\u6012\u4e4b\u4e0b\uff0c\u60f3\u8981\u628a\u4ed6\u652b\u4f4f\u6458\u53bb\u4ed6\u624b\u4e0a\u7684\u6bd2\u9488\u6307\u73af\u3002 \u3000\u3000\u5df4\u95ed\u5ffd\u5730\u6251\u4e86\u8fc7\u6765\u3002\u770b\u5230\u5df4\u95ed\u6c14\u5f97\u9752\u7b4b\u7a81\u73b0\u7684\u6837\u5b50\uff0c\u53ef\u628a\u201c\u9c7c\u751f\u201d\u7ed9\u5413\u50bb\u4e86\u3002 \u3000\u3000\u4ed6\u4ecd\u53d7\u987e\u5f71\u6240\u5236\uff0c\u65e0\u6cd5\u8131\u8eab\uff0c\u89c1\u5df4\u95ed\u542b\u6012\u51b2\u4e86\u8fc7\u6765\uff0c\u771f\u4e2a\u5413\u5f97\u4e22\u4e86\u4e09\u9b42\u53bb\u4e86\u4e03\u9b44\uff0c\u5927\u53eb\uff1a\u201c\u6551\u547d\u2026\u2026\u201d \u3000\u3000\u725b\u4e3d\u751f\u4e0d\u77e5\u5c31\u91cc\uff0c\u957f\u8eab\u4e00\u62e6\u3002 \u3000\u3000\u8fd9\u4e00\u62e6\u4e4b\u52bf\uff0c\u5c31\u50cf\u4e00\u9053\u77f3\u5899\u5ffd\u7136\u6a2a\u5728\u5df4\u95ed\u7684\u773c\u524d\u3002 \u3000\u3000\u5df4\u95ed\u7684\u51b2\u52bf\uff0c\u5f62\u540c\u4e00\u5934\u6012\u864e\u3002 \u3000\u3000\u2014\u2014\u5982\u679c\u524d\u9762\u6709\u5899\uff0c\u4ed6\u4f1a\u8dc3\u8fc7\u8fd9\u4e00\u9053\u5899\u3002 \u3000\u3000\u2014\u2014\u5982\u679c\u524d\u9762\u6709\u4eba\uff0c\u4ed6\u4f1a\u201c\u5403\u201d\u6389\u8fd9\u4e2a\u4eba\u3002 \u3000\u3000\u4f46\u4ed6\u7684\u524d\u9762\u662f\u725b\u4e3d\u751f\u3002 \u3000\u3000\u5df4\u95ed\u9ad8\u5927\u8c6a\u58ee\u5f97\u5c31\u50cf\u4e00\u68f5\u98ce\u96e8\u4e2d\u6012\u8301\u7684\u53e4\u6811\u3002 \u3000\u3000\u725b\u4e3d\u751f\u5374\u4f3c\u4e00\u5ea7\u5c71\u3002 \u3000\u3000\u6ca1\u6709\u4eba\u80fd\u5c06\u4e4b\u94f2\u5e73\u7684\u5c71\u3002 \u3000\u3000\u4ed6\u73b0\u5728\u4e5f\u4e0d\u52a8\u5982\u5c71\u3002 \u3000\u3000\u5df4\u95ed\u5389\u559d\uff1a\u201c\u6eda\u5f00\uff01\u201d\u4e00\u638c\u63a8\u53bb\u3002 \u3000\u3000\u725b\u4e3d\u751f\u54a7\u5634\u4e00\u7b11\uff0c\u53cd\u624b\u4e00\u683c\u3002 \u3000\u3000\u4e24\u53ea\u624b\u81c2\uff0c\u7ec8\u4e8e\u4e0d\u6253\u4e0d\u76f8\u8bc6\u7684\u683c\u5728\u4e00\u8d77\u3002 \u3000\u3000\u5df4\u95ed\u539f\u672c\u8fd9\u4e00\u63a8\uff0c\u4ee5\u4e3a\u50cf\u63a8\u5012\u4e00\u526f\u9ebb\u5c06\u724c\u4f3c\u7684\uff0c\u4ed6\u7528\u7684\u529b\u9053\u4e5f\u4e0d\u7b97\u592a\u5927\u3002 \u3000\u3000\u725b\u4e3d\u751f\u8fd9\u4e00\u683c\u4e4b\u529b\uff0c\u4e5f\u5dee\u4e0d\u591a\u8ddf\u63a8\u5f00\u4e00\u6247\u5f39\u7c27\u95e8\u4f3c\u7684\uff0c\u529b\u9053\u4ea6\u4e0d\u5916\u5982\u662f\u3002 \u3000\u3000\u4f46\u5f53\u4ed6\u4eec\u4e24\u53ea\u624b\u81c2\u98ce\u7b4b\u8d21\u7a81\u4e4b\u5904\u78b0\u61a7\u5728\u4e00\u8d77\u7684\u65f6\u4faf\uff0c\u7a81\u7136\u800c\u6025\u8fd9\u7684\uff0c\u4e24\u4eba\u90fd\u540c\u65f6\u9aa4\u589e\u4e86\u5185\u52b2\u3002 \u3000\u3000\u800c\u4e14\u5728\u77ed\u77ed\u7684\u78b0\u683c\u8fc7\u7a0b\u4e2d\uff0c\u5404\u9042\u589e\u4e86\u4e09\u6ce2\u7684\u529b\u9053\u3002 \u3000\u3000\u7136\u540e\u4e24\u4eba\u4ee5\u8098\u5c16\u4e3a\u8f74\uff0c\u4e24\u53ea\u62f3\u5934\u5341\u53ea\u624b\u6307\u7d27\u7d27\u63e1\u5728\u4e00\u8d77\uff0c\u5927\u5bb6\u8f83\u4e0a\u4e86\u52b2\u3002 \u3000\u3000\u90a3\u662f\u771f\u529b\u3002 \u3000\u3000\u4e24\u53ea\u624b\u81c2\u90fd\u58ee\u5982\u5927\u817f\u3001\u7c97\u5982\u6811\u6839\u3002 \u3000\u3000\u4e24\u5165\u8fd9\u4e48\u4e00\u63e1\uff0c\u5df4\u95ed\u8138\u4e0a\u7acb\u523b\u7d2b\u80c0\uff0c\u989d\u4e0a\u5192\u51fa\u4e86\u6266\uff0c\u4ed6\u95f7\u54fc\u4e00\u58f0\uff0c\u53e6\u4e00\u53ea\u6d77\u7897\u5927\u7684\u62f3\u5934\uff0c\u5df2\u540c\u65f6\u51fb\u5728\u725b\u4e3d\u751f\u7684\u80f8\u819b\u4e0a\u3002 \u3000\u3000\u725b\u4e3d\u751f\u54a7\u5634\u4e00\u7b11\uff0c\u8475\u6247\u822c\u5927\u7684\u624b\u638c\uff0c\u4e5f\u6258\u51fb\u5728\u5df4\u95ed\u7684\u4e0b\u9894\u3002 \u3000\u3000\u5df4\u95ed\u4e00\u4ef0\u9996\uff0c\u4f46\u5e76\u6ca1\u6709\u4ef0\u8dcc\u51fa\u53bb\u3002 \u3000\u3000\u725b\u4e3d\u751f\u5148\u7740\u4e86\u5df4\u95ed\u4e00\u62f3\uff0c\u4ed6\u7684\u53cd\u5e94\u5c45\u7136\u662f\uff1a \u3000\u3000\u7b11\u4e86\u4e00\u7b11\u3002 \u3000\u3000\u2014\u2014\u8fd8\u7b11\u5f97\u86ee\u6709\u600e\u529b\u54e9\u3002 \u3000\u3000\u7136\u540e\u4ed6\u7684\u80f8\u819b\u4f3c\u7f29\u4e86\u4e00\u7f29\uff0c\u90a3\u60c5\u5f62\u5c31\u50cf\u662f\uff1a\u4e00\u4e2a\u5439\u80c0\u4e86\u6c14\u7684\u6c7d\u7403\uff0c\u53ea\u8981\u7528\u624b\u6307\u4e00\u538b\uff0c\u7acb\u5373\u5c31\u762a\u4e0b\u53bb\u4e00\u5c0f\u5757\uff0c\u4f46\u4f60\u4e00\u653e\u624b\uff0c\u5b83\u7acb\u5373\u53c8\u56de\u590d\u539f\u6765\u9971\u9971\u6ee1\u6ee1\u7684\u72b6\u6001\u3002 \u3000\u3000\u7d27\u63a5\u7740\u4e0b\u6765\uff0c\u4ed6\u4eec\u662f\u5728\u6bd4\u529b\u3002 \u3000\u3000\u6bd4\u771f\u529b\u3002 \u3000\u3000\u5df4\u95ed\u7a7f\u7740\u77ed\u8896\u7684\u9053\u888d\uff0c\u662f\u4ee5\u4ed6\u7684\u5c0f\u81c2\uff0c\u5b8c\u5168\u88f8\u9732\uff0c\u8ddf\u725b\u4e3d\u751f\u7684\u624b\u81c2\u7f20\u7b8d\u5728\u4e00\u8d77\uff0c\u90a3\u7b4b\u8089\u5c31\u50cf\u6f2b\u753b\u6216\u8fde\u73af\u56fe\u91cc\u6240\u7ed8\u7684\u90a3\u4e9b\u5929\u751f\u795e\u529b\u7684\u58ee\u6c49\uff0c\u5938\u5f20\u5f97\u4ee4\u4eba\u5012\u541e\u4e00\u53e3\u6c14\uff0c\u53c8\u50cf\u793e\u4f1a\u4e3b\u4e49\u56fd\u5bb6\u91cc\u7a81\u663e\u52b3\u52a8\u9636\u7ea7\u7684\u5065\u5eb7\u5f0f\u8c6a\u58ee\u7684\u94dc\u96d5\uff0c\u770b\u7740\u4e5f\u4f1a\u6709\u4e00\u79cd\u5145\u6ee1\u6253\u51fb\u529b\u7684\u9707\u64bc\u3002 \u3000\u3000\u4e24\u4eba\u76f8\u6301\u4e0d\u4e0b\u3002 \u3000\u3000\u573a\u4e2d\u5df2\u5b8c\u5168\u9759\u4e86\u4e0b\u6765\uff0c\u5c4f\u606f\u4ee5\u5f85\u3002 \u3000\u3000\u573a\u4e2d\u9664\u4e86\u6d41\u6c57\u7684\u58f0\u97f3\uff0c\u8fd8\u6709\u4e00\u79cd\u58f0\u97f3\uff0c\u76f8\u5f53\u523a\u8033\u3002 \u3000\u3000\u88c2\u5e1b\u7684\u58f0\u97f3\u3002 \u3000\u3000\u725b\u4e3d\u751f\u539f\u672c\u662f\u7a7f\u7740\u897f\u88c5\u548c\u767d\u957f\u8896\u886c\u8863\u7684\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u7684\u8863\u886b\u88c2\u5f00\u3002 \u3000\u3000\u897f\u88c5\u4e5f\u88c2\u5f00\u3002 \u3000\u3000\u8863\u886b\u53d1\u51fa\u5429\u5490\u5ac1\u568e\u5636\u5636\u78c1\u54e6\u7684\u58f0\u97f3\uff0c\u4e00\u4e0b\u5b50\uff0c\u725b\u4e3d\u751f\u4e0a\u8eab\u8863\u886b\u7247\u7247\u788e\u88c2\uff0c\u4e0a\u8eab\u51e0\u4e4e\u5b8c\u5168\u8d64\u88f8\u3002 \u3000\u3000\u4e00\u597d\u597d\u7684\u7a7f\u5728\u8eab\u4e0a\u7684\u8863\u670d\uff0c\u4e3a\u4f55\u4f1a\u7247\u7247\u788e\u88c2\uff1f \u3000\u3000\u90a3\u662f\u56e0\u4e3a\u725b\u4e3d\u751f\u6574\u4e2a\u8eab\u5b50\uff0c\u7a81\u7136\u81a8\u80c0\u4e86\u8d77\u6765\uff0c\u7c97\u5927\u4e86\u8d77\u6765\uff0c\u4ee5\u4e00\u79cd\u201c\u7206\u70b8\u201d\u7684\u901f\u5ea6\u548c\u5a01\u529b\uff0c\u5148\u884c\u7ef7\u788e\u4e86\u81ea\u5df1\u8eab\u4e0a\u7684\u670d\u9970\u3002 \u3000\u3000\u540c\u65f6\uff0c\u4ed6\u624b\u81c2\u4e5f\u9732\u4e86\u51fa\u6765\u3002 \u3000\u3000\u4ed6\u624b\u81c2\u4e0a\u6bcf\u4e00\u5757\u7b4b\u8089\uff0c\u90fd\u50cf\u94c1\u954c\u7684\u5e9e\u9b3c\uff0c\u5c31\u50cf\u897f\u90e8\u7247\u91cc\u626e\u6f14\u4ec0\u4e48\u795e\u5251\u6b66\u58eb\u7684\u5927\u529b\u58eb\u4e00\u822c\uff0c\u6ca1\u6709\u4e00\u5757\u7b4b\u9aa8\u548c\u8840\u8089\uff0c\u4f1a\u6709\u4e00\u70b9\u513f\u59a5\u534f\u7684\u53ef\u80fd\u3002 \u3000\u3000\u4ed6\u7684\u624b\u5c31\u50cf\u4e00\u68f5\u5343\u5e74\u7720\u6708\u795e\u6728\uff0c\u6b63\u4f38\u51fa\u4e86\u4ed6\u7ad9\u7acb\u4e0d\u5012\u7684\u4e3b\u6839\u3002 \u3000\u3000\u4e24\u6761\u53f3\u81c2\u4ecd\u7f20\u5728\u4e00\u8d77\u3002 \u3000\u3000\u4f46\u53ef\u4e00\u70b9\u4e5f\u4e0d\u7f20\u7ef5\u3002 \u3000\u3000\u800c\u662f\u7f20\u6218\u3002 \u3000\u3000\u88c2\u5e1b\u58f0\u4ecd\u6709\u3002 \u3000\u3000\u2014\u2014\u8fd9\u53e3\u662f\u5df4\u95ed\u8eab\u4e0a\u53d1\u51fa\u7684\u3002 \u3000\u3000\u4e0d\u662f\u4e0a\u8eab\uff0c\u800c\u662f\u4e0b\u8eab\u3002 \u3000\u3000\u58f0\u97f3\u662f\u4ece\u5df4\u95ed\u7684\u88e4\u5b50\u91cc\u53d1\u51fa\u6765\u7684\u3002 \u3000\u3000\u8fd9\u6837\u4e00\u6765\uff0c\u725b\u4e3d\u751f\u4e0e\u5df4\u95ed\u6bd4\u6324\u5185\u529b\uff0c\u8eab\u4e0a\u7684\u8863\u9970\uff0c\u90e8\u4e3a\u4e4b\u7ef7\u88c2\u3002 \u3000\u3000\u6240\u4e0d\u540c\u7684\u662f\uff1a\u725b\u4e3d\u751f\u7ef7\u88c2\u7684\u662f\u4e0a\u519c\u3002 \u3000\u3000\u5df4\u95ed\u88c2\u7684\u662f\u88e4\u5b50\u3002 \u3000\u3000\u800c\u4e14\u662f\u88e4\u88c6\u3002 \u3000\u3000\u8fd9\u4e0b\u201c\u4e8b\u6001\u4e25\u91cd\u201d\uff0c\u5df4\u95ed\u8fde\u5fd9\u7528\u53e6\u4e00\u53ea\u624b\u906e\u4f4f\u88e4\u88c6\u3002 \u3000\u3000\u53ef\u662f\u4e00\u53ea\u624b\u63a9\u906e\u4e0d\u4f4f\u3002 \u3000\u3000\u4ed6\u6da8\u7ea2\u4e86\u8138\u3002 \u3000\u3000\u4ed6\u7684\u53e6\u4e00\u53ea\u624b\u4e0d\u80fd\u62bd\u56de\u3002 \u3000\u3000\u56e0\u4e3a\u90a3\u53ea\u624b\u6b63\u5728\u505a\u4e00\u4ef6\u4e8b\uff1a \u3000\u3000\u4ee5\u529b\u91cf\u6765\u8bc1\u660e\u8c01\u624d\u662f\u6709\u7406\u3002 \u3000\u3000\u2014\u2014\u4e16\u4e0a\u6709\u4e9b\u771f\u7406\uff0c\u65e2\u4e0d\u662f\u7528\u5634\u5df4\u8bf4\u7684\uff0c\u4e5f\u4e0d\u662f\u7528\u884c\u52a8\u8bf4\u7684\uff0c\u800c\u662f\u8981\u7528\u62f3\u5934\u6765\u8bf4\u7684\u3002 \u3000\u3000\u7528\u6b66\u529b\u6765\u8bf4\u7684\u9053\u7406\uff0c\u6709\u65f6\u5019\u8981\u6bd4\u6709\u9053\u6709\u7406\u7684\u9053\u7406\u66f4\u7ba1\u7528\u3002 \u3000\u3000\u53ea\u4e0d\u8fc7\u5728\u66b4\u529b\u4e0b\u7684\u9053\u7406\uff0c\u8c01\u843d\u8d25\u4e86\u5c31\u65e0\u7406\u3002 \u3000\u3000\u5df4\u95ed\u4e00\u65e6\u7f20\u4e0a\u725b\u4e3d\u751f\u7684\u624b\uff0c\u7b49\u4e8e\u5728\u8bf4\u4e00\u573a\u529b\u91cf\u7684\u7406\u3002 \u3000\u3000\u4ed6\u4eec\u4ee5\u624b\u6765\u4e89\u8fa9\u3002 \u3000\u3000\u4ee5\u62f3\u5934\u6765\u8bc1\u660e\u3002 \u3000\u3000\u4ee5\u529b\u91cf\u6765\u5224\u65ad\u8c01\u662f\u8c01\u975e\u3002 \u3000\u3000\u5df4\u95ed\u4e00\u5411\u4e0d\u591a\u8a00\u3002 \u3000\u3000\u4ed6\u4e00\u5411\u90fd\u5f88\u6709\u7406\u3002 \u3000\u3000\u4ed6\u7684\u7406\u662f\u7528\u62f3\u5934\u8bf4\u7684\u3002 \u3000\u3000\u2014\u2014\u4efb\u4f55\u4eba\u7528\u66b4\u529b\u5728\u4ed6\u9762\u524d\u8bf4\u7406\u7684\u65f6\u5019\uff0c\u4ed6\u5c31\u4f1a\u4ee5\u62f3\u5934\u6765\u8ba9\u5bf9\u65b9\u6210\u4e86\u6709\u7406\u8bf4\u4e0d\u51fa\u3002 \u3000\u3000\u6bcf\u4e00\u6b21\uff0c\u5f53\u4e0d\u8bb2\u7406\u7684\u4eba\u8ddf\u4ed6\u8bb2\u62f3\u5934\u7684\u9053\u7406\u7684\u65f6\u5019\uff0c\u4ed6\u8ddf\u89c1\u81ea\u5df1\u7684\u62f3\u5934\u7ec8\u7a76\u8fd8\u662f\u8bf4\u670d\u4e86\u5bf9\u65b9\u3002 \u3000\u3000\u53ef\u662f\u8fd9\u6b21\u4e0d\u4e00\u6837\u3002 \u3000\u3000\u5f53\u4ed6\u7684\u624b\u89e6\u7740\u4e86\u725b\u4e3d\u751f\u624b\u81c2\u7684\u65f6\u5019\u3002 \u3000\u3000\u4ed6\u89c9\u5f97\u7406\u4e0d\u76f4\u3002 \u3000\u3000\u6c14\u4e0d\u58ee\u3002 \u3000\u3000\u4f46\u4ed6\u53c8\u4e0d\u80fd\u7f29\u624b\u3002 \u3000\u3000\u2014\u2014\u4e16\u4e0a\u6709\u4e9b\u4e8b\u60c5\uff0c\u4e00\u65e6\u63d2\u4e0a\u4e86\u624b\uff0c\u534a\u9014\u518d\u6765\u7eb3\u624b\uff0c\u65e0\u8bba\u600e\u4e48\u8bf4\u90fd\u4f1a\u53d8\u6210\u6b6a\u7406\u4e86\u3002 \u3000\u3000\u4ed6\u6ca1\u60f3\u5230\u7684\u662f\uff0c \u3000\u3000\u4ed6\u4e0d\u7f29\u624b\u53ef\u662f\u725b\u4e3d\u751f\u5374\u7f29\u4e86\u624b\u3001 \u3000\u3000\u800c\u4e14\u8bf4\u6536\u5c31\u6536\u3002 \u3000\u3000\u725b\u4e3d\u751f\u4e00\u6536\u624b\uff0c\u5df4\u95ed\u624d\u5446\u4e86\u4e00\u6761\uff0c\u7acb\u523b\u53cc\u624b\u63a9\u4f4f\u4e86\u88e4\u88c6\u3002 \u3000\u3000\u4e00\u540d\u7f8e\u4e3d\u5973\u5b50\u6b63\u79bb\u4ed6\u4eec\u5341\u5206\u4e4b\u8fd1\u3002 \u3000\u3000\u5341\u5206\u964c\u751f\u7684\u7f8e\u4e3d\u5973\u5b50\u3002 \u3000\u3000\u6240\u4ee5\u5df4\u95ed\u5f88\u7a98\u3002 \u3000\u3000\u4ed6\u7684\u624b\u5ffd\u7136\u4e00\u7a7a\uff0c\u91cd\u5fc3\u5168\u5931\uff0c\u5f53\u4ed6\u77e5\u9053\u654c\u624b\u5df2\u7ecf\u6536\u624b\u7684\u65f6\u5019\uff0c\u4ed6\u4e5f\u60f3\u7acb\u523b\u6536\u624b\u3002 \u3000\u3000\u53ef\u662f\u90a3\u53ea\u624b\uff0c\u5df2\u597d\u50cf\u5b8c\u5168\u4e0d\u5c5e\u4e8e\u4ed6\u4f3c\u7684\u3002 \u3000\u3000\u4e5f\u5c31\u662f\u8bf4\uff0c\u624b\uff0c\u4ecd\u5728\u90a3\u513f\uff0c\u4f46\u4ed6\u51e0\u4e4e\u6307\u6325\u4e0d\u4e86\u90a3\u53ea\u624b\u7684\u52a8\u4f5c\u3002 \u3000\u3000\u4e0d\u8fc7\u8fd9\u53ea\u662f\u4e00\u4e0b\u5b50\u7684\u4e8b\u3002 \u3000\u3000\u8fd9\u65f6\u5019\uff0c\u4e03\u516b\u540d\u201c\u521a\u51fb\u9053\u201d\u7684\u5b66\u751f\uff0c\u6b63\u8981\u4e00\u6d8c\u800c\u4e0a\u3002 \u3000\u3000\u6709\u4e00\u540d\u5b66\u5458\u5df2\u5411\u725b\u4e3d\u751f\u6325\u62f3\u3002 \u3000\u3000\u4e00\u4e2a\u6b63\u610f\u56fe\u6253\u4ece\u540e\u9762\u7b8d\u4f4f\u4ed6\u3002 \u3000\u3000\u4ed6\u4eec\u90fd\u6ca1\u6709\u5f97\u624b\u3002 \u3000\u3000\u53cd\u800c\u5931\u4e86\u624b\u3002 \u3000\u3000\u4ed6\u4eec\u90fd\u201c\u98de\u201d\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u5f53\u4ed6\u4eec\u8eab\u5b50\u79bb\u5730\uff0c\u8fd8\u4e0d\u77e5\u9053\u81ea\u5df1\u5c06\u201c\u98de\u201d\u5728\u4ec0\u4e48\u65b9\u5411\u4ec0\u4e48\u5730\u65b9\u4ec0\u4e48\u90e8\u4f4d\u5148\u89e6\u5730\u4e4b\u9645\uff0c\u624d\u542c\u89c1\u90a3\u7f8e\u4e3d\u800c\u964c\u751f\u800c\u65f6\u9ae6\u800c\u4ee4\u4eba\u5fc3\u733f\u610f\u9a6c\u7684\u5973\u5b50\u7684\u4e00\u58f0\u6e05\u53f1\uff1a \u3000\u3000\u201c\u60f3\u4eba\u591a\u6b3a\u4eba\u5c11\u554a\uff01\u201d \u3000\u3000\u7136\u540e\u4ed6\u4eec\u5c31\u201c\u98de\u8dcc\u201d\u51fa\u53bb\u3002 \u3000\u3000\u5f53\u4ed6\u4eec\u843d\u5730\u7684\u65f6\u5019\uff0c\u624d\u542c\u89c1\u81ea\u5df1\u7684\u9aa8\u5934\u7684\u54c0\u9e23\u548c\u5df4\u95ed\u6559\u7ec3\u7684\u6012\u543c\uff1a\u201c\u4e0d\u8bb8\u52a8\u624b\uff01\u201d \u3000\u3000\u4ed6\u4eec\u672c\u6765\u4e5f\u4e0d\u60f3\u52a8\u624b\uff0c\u53ef\u662f\u5148\u524d\u773c\u89c1\u51e0\u540d\u5e08\u5144\u5f1f\u906d\u4eba\u6697\u7b97\uff0c\u800c\u5df4\u6559\u7ec3\u8ddf\u90a3\u4e00\u5ea7\u5c71\u7ea7\u7684\u5927\u6c49\u654c\u5bf9\uff0c\u4f3c\u4e4e\u4e5f\u6ca1\u8ba8\u7740\u4e86\u4fbf\u5b9c\uff0c\u4fbf\u6253\u7b97\u786c\u7740\u5934\u76ae\u5148\u884c\u8ddf\u4ed6\u8017\u8017\u518d\u8bf4\u3002\u5176\u5b9e\uff0c\u8c01\u4e5f\u8bbe\u610f\u601d\u8981\u60f9\u770b\u53bb\u96be\u60f9\u81f3\u6781\u5f97\u50cf\u4e00\u5ea7\u8d70\u52a8\u7684\u94dc\u50cf\u2014\u2014\u6ca1\u60f3\u5230\u8fde\u90a3\u94dc\u50cf\u8eab\u65c1\u7684\u90a3\u6735\u5a07\u4e3d\u7684\u82b1\u4e5f\u5982\u6b64\u7684\u4e0d\u597d\u60f9\u3002 \u3000\u3000\u201c\u4e0d\u8bb8\u52a8\u624b\u201d\u662f\u5df4\u95ed\u558a\u51fa\u6765\u7684\u3002 \u3000\u3000\u4ed6\u4ecd\u662f\u80c0\u7ea2\u4e86\u8138\u3002 \u3000\u3000\u725b\u4e3d\u751f\u9732\u51fa\u81ea\u68ee\u68ee\u4e5f\u767d\u751f\u751f\u7684\u7259\u9f7f\uff0c\u7b11\u4e86\u3002 \u3000\u3000\u4ed6\u4f38\u51fa\u4e86\u62c7\u6307\uff1a \u3000\u3000\u201c\u52b2\uff01\u201d \u3000\u3000\u4ed6\u53ea\u8bf4\u4e00\u4e2a\u5b57\u3002 \u3000\u3000\u5df4\u95ed\u7ea2\u7740\u8138\uff0c\u4e0d\u8bf4\u4ec0\u4e48\uff0c\u6479\u7136\u5378\u4e0b\u4e86\u4e0a\u8eab\u7684\u9053\u888d\uff0c\u7ed1\u7d27\u5728\u8170\u95f4\u3002 \u3000\u3000\u725b\u4e3d\u4e3b\u53ea\u629a\u629a\u53f3\u80f8\uff0c\u795e\u8272\u4e0d\u53d8\u7684\u8bf4\uff1a\u201c\u4f60\u90a3\u4e00\u62f3\uff0c\u65ad\u4e86\u6211\u4e00\u6761\u808b\u9aa8\u3002\u201d\u7136\u540e\u5f88\u6709\u70b9\u611f\u6168\uff1a\u201c\u6211\u7684\u808b\u9aa8\u597d\u4e45\u6ca1\u65ad\u8fc7\u4e86\uff0c\u4e0a\u6b21\u2018\u66b4\u8d70\u65cf\u2019\u7684\u5c0f\u5154\u5d3d\u5b50\u7528\u94c1\u7ba1\u548c\u94a2\u64ac\u6572\u4e86\u534a\u5929\uff0c\u6211\u8fde\u5c0f\u6307\u5934\u4e5f\u6ca1\u6298\u534a\u6839\u3002\u201d \u3000\u3000\u4ed6\u53c8\u7528\u529b\u5730\u70b9\u4e86\u70b9\u5934\uff1a\u201c\u4f60\u4e00\u62f3\u5c31\u65ad\u4e86\u6211\u4e00\u6761\u808b\u9aa8\uff0c\u9ad8\uff01\u201d \u3000\u3000\u4ed6\u8bf4\u5f97\u4eff\u4f5b\u662f\u5730\u4e0a\u7684\u4e00\u6839\u7ed9\u72d7\u5403\u7684\u8089\u9aa8\u5934\uff0c\u800c\u4e0d\u662f\u957f\u5728\u4ed6\u8eab\u4f53\u4e0a\u7684\u8840\u8089\u76f8\u8fde\u6b63\u4fdd\u62a4\u7740\u5185\u810f\u7684\u808b\u9aa8\u3002 \u3000\u3000\u201c\u8c22\u8c22\u3002\u201d\u5df4\u95ed\u7d2b\u80c0\u7740\u8138\uff0c\u8f8d\u5685\u5730\u9053\uff1a\u201c\u8c22\u3002\u201d \u3000\u3000\u201c\u4fd7\u3002\u201d\u725b\u4e3d\u751f\u8bf4\uff0c\u201c\u8d5e\u4f60\u53c8\u4e0d\u662f\u8981\u8ba8\u597d\u4f60\u3001\u8c22\u4ec0\u4e48\u8c22\u3002\u201d \u3000\u3000\u201c\u4e0d\u662f\u8c22\u4f60\u8d5e\u6211\uff0c\u800c\u662f\u8c22\u4f60\u521a\u624d\u6258\u6211\u4e0b\u5df4\u90a3\u4e00\u8bb0\uff0c\u8981\u662f\u7528\u4e0a\u4e86\u529b\uff0c\u6211\u7684\u9888\u9aa8\u65e9\u5c31\u65ad\u4e86\uff1b\u201d\u5df4\u95ed\u6c89\u7740\u8138\u8bf4\uff1a\u201c\u800c\u4e14\u4f60\u521a\u624d\u8981\u662f\u4e0d\u6536\u624b\uff0c\u6211\u7684\u624b\u5c31\u4e0d\u662f\u6211\u7684\u4e86\u3002\u201d \u3000\u3000\u201c\u6240\u4ee5\u6211\u624d\u8c22\u4f60\u4e24\u6b21\u3002\u201d\u4ed6\u8ba4\u771f\u7684\u8bf4\u3002 \u3000\u3000\uff13\u3001\u65e0\u7406\u4e5f\u662f\u4e00\u79cd\u9053\u7406 \u3000\u3000\u201c\u8c22\u8c22\u3002\u201d \u3000\u3000\u201c\u4e0d\u5ba2\u6c14\u3002\u201d \u3000\u3000\u201c\u75db\u5417\uff1f\u201d \u3000\u3000\u201c\u65ad\u4e86\u4e00\u6761\u808b\u9aa8\u54ea\u6709\u4e0d\u75db\u7684\uff1f\u201d\u725b\u4e3d\u751f\u7b11\u7740\uff0c\u5c45\u7136\u8fd8\u95ed\u4e86\u95ed\u773c\u775b\uff0c\u9676\u9189\u5730\u8bf4\uff1a\u201c\u4e0d\u8fc7\uff0c\u8bf4\u5b9e\u5728\u7684\uff0c\u6709\u65f6\u75db\u4e00\u75db\u4e5f\u633a\u8212\u670d\u7684\u3002\u201d \u3000\u3000\u201c\u5bf9\u4e0d\u8d77\u3002\u201d\u5df4\u95ed\u5f88\u662f\u6709\u70b9\u6b49\u759a\u7684\u6837\u5b50\u3002 \u3000\u3000\u201c\u6ca1\u6709\u4ec0\u4e48\u5bf9\u4e0d\u8d77\u7684\u3002\u201d\u725b\u4e3d\u751f\u4e5f\u8ba4\u771f\u7684\u8bf4\uff1a\u201c\u6211\u4e5f\u5bf9\u4e0d\u8d77\u4f60\u4e00\u6b21\u3002\u201d \u3000\u3000\u5df4\u95ed\u542c\u4e0d\u660e\u767d\u3002 \u3000\u3000\u201c\u56e0\u4e3a\u6211\u8981\u62ff\u4f60\u6362\u4e00\u6837\u4e1c\u897f\uff0c\u201d\u725b\u4e3d\u751f\u53ea\u597d\u89e3\u91ca\u3002 \u3000\u3000\u201c\u4e1c\u897f\uff1f\u201d\u5df4\u95ed\u4ecd\u662f\u542c\u4e0d\u61c2\u3002 \u3000\u3000\u201c\u4e0d\u662f\u4e1c\u897f\uff0c\u201d\u725b\u4e3d\u751f\u6f84\u6e05\uff1a\u201c\u662f\u4eba\u3002\u201d \u3000\u3000\u8bdd\u4e00\u8bf4\u5b8c\uff0c\u5df4\u95ed\u5c31\u8dcc\u5012\uff0c \u3000\u3000\u5012\u5411\u987e\u5f71\u3002 \u3000\u3000\u5df4\u95ed\u7684\u4e0b\u76d8\u529f\u592b\u6781\u597d\uff0c\u81ea\u7136\u4e0d\u4f1a\u65e0\u7f18\u65e0\u6545\u7684\u8dcc\u5012\u3002 \u3000\u3000\u4ed6\u662f\u5ffd\u5c14\u906d\u4eba\u7eca\u5012\u7684\u3002 \u3000\u3000\u7eca\u5012\u4ed6\u7684\u662f\u90a3\u4e2a\u6f02\u4eae\u7684\u964c\u751f\u5973\u5b50\u3002 \u3000\u3000\u5979\u8ba9\u4eba\u6454\u5012\uff0c\u5c31\u50cf\u968f\u624b\u6253\u7ffb\u4e00\u676f\u5f00\u6c34\u4e00\u822c\u8f7b\u800c\u6613\u4e3e\u3002 \u3000\u3000\u5df4\u95ed\u8dcc\u5411\u987e\u5f71\u3002 \u3000\u3000\u5df4\u95ed\u662f\u4e2a\u7ed3\u5b9e\u7684\u5927\u5757\u5934\uff0c\u5c31\u50cf\u4e00\u68f5\u4f1a\u8d70\u52a8\u7684\u5927\u6811\u3002 \u3000\u3000\u987e\u5f71\u4e3a\u4e86\u4e0d\u88ab\u649e\u5f97\u4e5f\u8e87\u5730\u4e0d\u8d77\uff0c\u53ea\u6709\u4f38\u624b\u53bb\u6276\u4ed6\u3002 \u3000\u3000\u8fd9\u4e00\u6276\uff0c\u725b\u4e3d\u751f\u5df2\u8db3\u591f\u65f6\u95f4\u628a\u201c\u9c7c\u751f\u201d\u62a2\u4e86\u8fc7\u53bb\uff0c\u751a\u81f3\u5df2\u8db3\u53ef\u628a\u654c\u4eba\u51fb\u5012\u5728\u5730\u5341\u6b21\u4e86\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u6709\u4e00\u4ef6\u4e8b\uff0c\u725b\u4e3d\u751f\u548c\u9a86\u94c3\u90fd\u6ca1\u6709\u6599\u5230\u3002 \u3000\u3000\u5c31\u5728\u9a86\u94c3\u628a\u5df4\u95ed\u7eca\u86db\u5411\u987e\u5f71\u7684\u5239\u90a3\u95f4\uff0c\u987e\u5f71\u5df2\u5148\u4e00\u6b65\u628a\u624b\u4e0a\u7684\u201c\u9c7c\u751f\u201d\u63a8\u649e\u5411\u9a86\u94c3\u3002 \u3000\u3000\u9a86\u94c3\u662f\u4e2a\u7f8e\u4e3d\u800c\u5bf9\u9ae6\u7684\u5973\u5b50\u3002 \u3000\u3000\u7f8e\u4e3d\u7684\u5973\u5b50\u5f53\u7136\u6709\u6d01\u7656\uff01 \u3000\u3000\u5979\u5f53\u7136\u4e0d\u60f3\u7ed9\u4e00\u4e2a\u770b\u53bb\u8fd9\u822c\u6c61\u7cdf\u908b\u9062\u7684\u7537\u5b50\u201c\u649e\u4e2a\u6ee1\u6000\u201d\u3002 \u3000\u3000\u5979\u539f\u672c\u7684\u8ba1\u5212\u662f\uff1a\u628a\u5df4\u95ed\u63a8\u5411\u987e\u5f71\uff0c\u7136\u540e\u5979\u548c\u725b\u4e3d\u751f\u540c\u65f6\u51fa\u624b\uff0c\u64d2\u4e0b\u4e24\u4eba\uff0c\u903c\u95ee\u4ed6\u201c\u9ed1\u706b\u201d\u5230\u5e95\u662f\u600e\u4e48\u53e3\u4e8b\uff1f \u3000\u3000\u53ef\u662f\uff0c\u5f53\u5730\u628a\u5df4\u95ed\u63a8\u8fc7\u53bb\u7684\u65f6\u5019\uff0c\u201c\u9c7c\u4e3b\u201d\u4e5f\u7ed9\u63a8\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u5979\u5728\u5343\u9493\u4e00\u53d1\u95f4\u95ea\u5f00\u3002 \u3000\u3000\u4f46\u5979\u8fd8\u662f\u5f88\u6709\u4fe1\u5fc3\u3002 \u3000\u3000\u5979\u6709\u4fe1\u5fc3\u725b\u4e3d\u751f\u4f1a\u628a\u63e1\u8fd9\u4e00\u5239\u90a3\u95f4\u7684\u673a\u4f1a\uff0c\u51fb\u5012\u987e\u5f71\u3002 \u3000\u3000\u201c\u4e0d\u5e73\u793e\u201d\u4e2d\u7684\u4eba\uff0c\u81ea\u6709\u7d27\u5bc6\u800c\u5916\u4eba\u6240\u65e0\u6cd5\u53c2\u900f\u7684\u8054\u7edc\u65b9\u5f0f\uff0c\u6240\u4ee5\u5979\u4e00\u53d1\u52a8\uff0c\u5c31\u77e5\u9053\u725b\u4e3d\u751f\u5fc5\u4f1a\u914d\u5408\u51fa\u624b\u3002 \u3000\u3000\u5979\u4e00\u5411\u5f88\u9a84\u50b2\u3002 \u3000\u3000\u5979\u81ea\u50b2\u7684\u662f\uff1a\u806a\u660e\u548c\u6f02\u4eae\u3002 \u3000\u3000\u81f3\u4e8e\u6b66\u529f\uff0c\u5979\u6709\u81ea\u77e5\u4e4b\u660e\u3002 \u3000\u3000\u4e00\u4e00\u725b\u4e3d\u751f\u770b\u6765\u9c81\u949d\uff0c\u4f46\u82e5\u8bba\u6b66\u529f\uff0c\u9a86\u94c3\u77e5\u9053\u81ea\u5df1\u81f3\u5c11\u8fd8\u5dee\u4e0a\u4e00\u5927\u622a\u3002 \u3000\u3000\u725b\u4e3d\u751f\u679c\u4e0d\u8d1f\u5979\u6240\u671b\u3002 \u3000\u3000\u5728\u6b66\u529f\u4e0a\uff0c\u725b\u4e3d\u751f\u4e5f\u4ece\u4e0d\u8d1f\u4efb\u4f55\u4eba\u6240\u671b\u3002 \u3000\u3000\u4ed6\u5df2\u628a\u63e1\u4f4f\u51fa\u624b\u7684\u65f6\u673a\u3002 \u3000\u3000\u987e\u5f71\u867d\u7136\u63a8\u5f00\u4e86\u201c\u9c7c\u751f\u201d\uff0c\u4f46\u4ecd\u63a5\u4e0b\u4e86\u5df4\u95ed\u3002 \u3000\u3000\u4ee5\u4e00\u53ea\u624b\u3002 \u3000\u3000\u4e00\u2014\u2014\u4e2a\u670b\u53cb\u5931\u8db3\u5728\u4f60\u9762\u524d\uff0c\u4f60\u80fd\u4e0d\u51fa\u624b\u6276\u4ed6\u4e00\u6276\uff1f \u3000\u3000\u800c\u5df4\u95ed\u6b63\u662f\u4ed6\u7684\u670b\u53cb\uff0c\u540c\u65f6\u4e5f\u662f\u6218\u53cb\u3002 \u3000\u3000\u5982\u679c\u8bf4\u5df4\u95ed\u4e00\u5982\u4e00\u67b6\u5e9e\u5927\u7684\u673a\u5668\uff0c\u987e\u5f71\u7684\u624b\u5c31\u50cf\u627e\u5230\u4e86\u90d1\u67b6\u673a\u5668\u7535\u6e90\u5f00\u5173\uff0c\u4e00\u4f38\u624b\u4fbf\u4f7f\u4e00\u5207\u9759\u6b62\u3002 \u3000\u3000\u53ef\u662f\u725b\u4e3d\u751f\u5e76\u4e0d\u9759\u6b62\u3002 \u3000\u3000\u4ed6\u50cf\u4e00\u5ea7\u8d70\u52a8\u7684\u5927\u5c71\u3002 \u3000\u3000\u4ed6\u7684\u529b\u4e0d\u6b62\u662f\u52b2\u9053\uff0c\u800c\u662f\u4e00\u79cd\u63a5\u8fd1\u7206\u70b8\u7684\u5de8\u529b\u3002 \u3000\u3000\u4ed6\u53cc\u62f3\u4e00\u5408\uff0c\u5f80\u987e\u5f71\u5934\u4e0a\u5288\u843d\u3002 \u3000\u3000\u4e00\u4f46\u5728\u62f3\u4fa7\u79bb\u987e\u5f71\u5934\u9876\u7ea6\u83ab\u534a\u5c3a\u4e4b\u9065\uff0c\u9661\u7136\u6b62\u4f4f\uff0c\u4e0d\u5f97\u5bf8\u8fdb\u3002 \u3000\u3000\u7136\u540e\u725b\u4e3d\u751f\u8138\u4e0a\u51fa\u73b0\u4e86\u4e00\u79cd\u795e\u8272\u3002 \u3000\u3000\u75db\u82e6\u4e4b\u8272\u3002 \u3000\u3000\u5728\u725b\u4e3d\u751f\u8138\u4e0a\u6700\u5bb9\u6613\u51fa\u73b0\u7684\u795e\u60c5\uff0c\u5f53\u7136\u5c31\u662f\u66b4\u71e5\u548c\u50bb\u7b11\u3002 \u3000\u3000\u6700\u96be\u5f97\u4e00\u89c1\u7684\uff0c\u5c31\u662f\u8fd9\u79cd\u75db\u695a\u800c\u75db\u82e6\u5f97\u626d\u66f2\u4e86\u4e94\u5b98\u7684\u795e\u8272\u3002 \u3000\u3000\u4ed6\u73b0\u5728\u5c31\u662f\u8fd9\u79cd\u795e\u60c5\u3002 \u3000\u3000\u56e0\u4e3a\u987e\u5f71\u4e00\u6761\u80f3\u818a\u62e6\u4f4f\u5df4\u95ed\u7684\u8eab\u5b50\uff0c\u534a\u8e72\u7740\u8eab\u5b50\uff0c\u524d\u5c48\u7740\u819d\u76d6\uff0c\u5de6\u624b\u7684\u4e2d\u6307\uff0c\u7a81\u4e86\u51fa\u6765\uff0c\u523a\u4e2d\u725b\u4e3d\u751f\u7684\u53f3\u80f8\u3002\u53ea\u662f\u4e00\u53ea\u624b\u6307\u3002 \u3000\u3000\u725b\u4e3d\u751f\u7ec3\u7684\u662f\u786c\u95e8\u6b66\u529f\uff0c\u5176\u4e2d\u5305\u62ec\u4e86\uff0c\u201c\u91d1\u949f\u7f69\u201d\u3001\u201c\u94c1\u5e03\u886b\u201d\u3001\u201c\u94dc\u5934\u529f\u201d\u3001\u201c\u5341\u4e09\u5927\u4fdd\u6a2a\u7ec3\u201d\u3001\u201c\u94c1\u95e8\u95e9\u201d\u3001\u201c\u6df7\u5143\u4e00\u6c14\u201d\u751a\u81f3\u6709\u4eba\u6000\u7591\u4ed6\u8fd8\u5728\u7ec3\u201c\u7ae5\u5b50\u529f\u201d\u3002 \u3000\u3000\u50cf\u4ed6\u4e00\u4e2a\u8fd9\u6837\u7684\u4eba\uff0c\u5e38\u4eba\u5c31\u7b97\u7528\u6728\u6869\u53bb\u64c2\u4ed6\uff0c\u4ed6\u4e5f\u53ea\u5f53\u662f\u66ff\u4ed6\u6414\u75d2\u4e00\u6837\u3002 \u3000\u3000\u5df4\u95ed\u5929\u751f\u795e\u529b\uff0c\u80fd\u4e00\u62f3\u51fb\u65ad\u4ed6\u4e00\u6761\u80c1\u9aa8\uff0c\u5df2\u7ecf\u662f\u4e00\u4e2a\u610f\u5916\u3002 \u3000\u3000\u2014\u2014\u201c\u516d\u4eba\u5e2e\u201d\u4e2d\u7684\u201c\u5927\u80a5\u9e2d\u201d\u9648\u5251\u8c01\u5c31\u8bf4\u8fc7\uff1a\u666e\u5929\u4e4b\u4e0b\uff0c\u6253\u65ad\u725b\u4e3d\u751f\u8eab\u4e0a\u9aa8\u5934\u8005\u80fd\u6709\u51e0\u4eba\uff1f \u3000\u3000\u53ef\u662f\uff0c\u725b\u4e3d\u751f\u73b0\u5728\u7adf\u7136\u4e3a\u75db\u695a\u800c\u5168\u8eab\u75c9\u5b6a\u8d77\u6765\uff0c\u800c\u4e14\u53ea\u662f\u56e0\u4e3a\u4e00\u6839\u624b\u6307\uff01 \u3000\u3000\u2014\u2014\u8fd9\u662f\u4ec0\u4e48\u6837\u7684\u624b\u6307\uff1f \u3000\u3000\u96be\u9053\u6240\u53d1\u51fa\u6765\u7684\u52b2\u9053\uff0c\u8981\u6bd4\u5b50\u5f39\u8fd8\u5177\u6740\u4f24\u529b\u4e0d\u6210\uff01 \u3000\u3000\u90a3\u662f\u987e\u5f71\u7684\u624b\u6307\u3002 \u3000\u3000\u4e2d\u6307\u3002 \u3000\u3000\u8fd9\u6839\u4e2d\u6307\u4e5f\u6ca1\u4ec0\u4e48\u7279\u522b\u3002 \u3000\u3000\u4e5f\u8bb8\u7279\u522b\u7684\u53ea\u662f\uff1a\u5b83\u6b63\u622e\u5728\u725b\u4e3d\u751f\u7684\u65ad\u9aa8\u91cc\u3002 \u3000\u3000\u6df1\u6df1\u5730\u622e\u4e86\u8fdb\u53bb\u3002 \u3000\u3000\u2014\u2014\u65ad\u4e86\u7684\u808b\u9aa8\u91cc\u5f53\u7136\u662f\u5fc3\u810f\u3002 \u3000\u3000\u5fc3\u810f\u662f\u4e00\u4e2a\u8981\u547d\u7684\u8981\u5bb3\uff0c\u65e0\u8bba\u5bf9\u8c01\u90fd\u662f\u4e00\u6837\uff1b\u4e00\u4e2a\u4eba\u53ef\u7ec3\u5f97\u94c1\u9aa8\u94dc\u76ae\uff0c\u4f46\u4e0d\u53ef\u80fd\u7ec3\u5c31\u94a2\u5fc3\u810f\u3002 \u3000\u3000\u725b\u4e3d\u751f\u4f3c\u88ab\u6467\u6bc1\u4e86\u3002 \u3000\u3000\u9a86\u94c3\u770b\u5230\u8fd9\u79cd\u60c5\u5f62\uff0c\u5fc3\u5c31\u4e71\u4e86\u3002 \u3000\u3000\u5979\u4e00\u5411\u559c\u6b22\u95ef\u7978\u3002\u5979\u4e00\u5411\u8ba4\u4e3a\uff0c\u4e00\u4e2a\u5973\u5b50\uff0c\u65e2\u7136\u8fd8\u62e5\u6709\u9752\u6625\u548c\u7f8e\u4e3d\uff0c\u4e0d\u95ef\u95ef\u7978\u5b9e\u5728\u8bf4\u4e0d\u8fc7\u53bb\u3002\u8fd9\u201c\u95ef\u7978\u201d\u7684\u610f\u601d\u5176\u5b9e\u5c31\u662f\u201c\u95f9\u4e8b\u201d\uff0c\u8ddf\u201c\u80e1\u95f9\u201d\u7684\u610f\u601d\u4e5f\u76f8\u53bb\u4e0d\u8fdc\uff0c\u4f46\u51b3\u4e0d\u7b49\u4e8e\u53bb\u62db\u60f9\u90a3\u79cd\u201c\u5927\u7978\u4e34\u5934\u201d\u7684\u201c\u7978\u201d\u3002 \u3000\u3000\u5979\u89c1\u987e\u5f71\u50b2\u6162\uff0c\u6b63\u773c\u4e5f\u4e0d\u7784\u5979\u4e00\u773c\uff0c\u4fbf\u60f3\u8fc7\u53bb\u6311\u8845\uff0c\u60f9\u6012\u4ed6\u4e00\u4e0b\uff0c\u8bf4\u5230\u5934\u6765\uff0c\u4e5f\u4e0d\u8fc7\u662f\u8981\u5bf9\u65b9\u6b63\u89c6\u3001\u6ce8\u610f\u5979\u3002 \u3000\u3000\u5979\u8feb\u4e0d\u5230\u987e\u5f71\uff0c\u725b\u4e3d\u751f\u5374\u8ffd\u4e86\u4e0a\u6765\uff0c\u4f7f\u5979\u89c9\u5f97\u66f4\u6709\u5b89\u5168\u611f\uff1a\u5c31\u7b97\u8fd9\u5730\u65b9\u4eba\u751f\u8def\u4e0d\u719f\uff0c\u6709\u8fd9\u4e2a\u5929\u751f\u795e\u52c7\u7684\u5927\u5757\u5934\u5728\uff0c\u8fd8\u6015\u6709\u7f69\u4e0d\u4f4f\u7684\u4e8b\u4e48\uff01 \u3000\u3000\u7136\u540e\u56f0\u4e3a\u6bdb\u5ff5\u884c\u7684\u6307\u5f15\uff0c\u4ed6\u4eec\u5230\u4e86\u5927\u4f1a\u5802\uff0c\u770b\u89c1\u987e\u5f71\u5fff\u5fff\u5730\u6559\u5b66\u5458\u4eec\u5fd8\u547d\u5730\u7ec3\u6b66\uff0c\u8fd9\u5df2\u4e0d\u4f3c\u662f\u6559\u6b66\u3002\u4e60\u6b66\uff0c\u800c\u662f\u4e00\u79cd\u53d1\u6cc4\u3001\u6cc4\u6012\u4e86\u3002 \u3000\u3000\u5f53\u90a3\u51e0\u4e2a\u6d41\u6c13\u8fdb\u53bb\u60f9\u4e8b\u4e4b\u65f6\uff0c\u5979\u4e5f\u77a7\u8fd9\u51e0\u540d\u5730\u75de\u6d41\u6c13\u56a3\u5f20\u8dcb\u6248\uff0c\u5341\u5206\u4e0d\u987a\u773c\uff0c\u5df2\u5f88\u60f3\u51fa\u6765\u6559\u8bad\u4ed6\u4eec\u4e00\u987f\uff0c\u53ef\u662f\u8fd9\u5e72\u4eba\u5230\u9053\u573a\u91cc\u95f9\u4e8b\uff0c\u9a86\u94c3\u53cd\u800c\u89c9\u5f97\u9ad8\u5174\u3002 \u3000\u3000\u2014\u2014\u597d\u54c7\uff0c\u770b\u90a3\u59d3\u987e\u7684\u5c0f\u5b50\u8fd8\u80fd\u901e\u4ec0\u4e48\u5a01\u98ce\uff01 \u3000\u3000\u7ed3\u679c\u90a3\u51e0\u540d\u5c0f\u6d41\u6c13\u592a\u4e0d\u6d4e\u4e8b\uff0c\u51e0\u4e4e\u7ed9\u4e00\u4e2a\u5df4\u95ed\u5c31\u4e86\u4e8b\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u5979\u5df2\u793a\u610f\u725b\u4e3d\u751f\u53bb\u79e4\u79e4\u987e\u5f71\u548c\u5df4\u95ed\u7684\u65a4\u4e24\u3002 \u3000\u3000\u725b\u4e3d\u751f\u50bb\u547c\u547c\u5730\u7b11\u7740\uff0c\u8868\u793a\u4e0d\u60f3\u60f9\u4e8b\u3002 \u3000\u3000\u201c\u6211\u77e5\u9053\uff0c\u4f60\u4e00\u5b9a\u6015\u4e86\u3002\u201d \u3000\u3000\u201c\u6015\u4e86\uff1f\u201d\u725b\u4e3d\u751f\u5bf9\u9a86\u94c3\u8fd9\u53e5\u8bdd\u7684\u53cd\u5e94\u662f\u201c\u4e0d\u6562\u7f6e\u4fe1\u201d\u3002 \u3000\u3000\u201c\u8fd8\u4e0d\u627f\u8ba4\uff0c\u6211\u770b\u4e00\u4e2a\u59d3\u5df4\u7684\uff0c\u5c31\u8981\u6bd4\u4f60\u5f3a\u3002\u201d\u9a86\u94c3\u8038\u8038\u80a9\uff0c\u5341\u5206\u201c\u540c\u60c5\u201d\u4ed6\u8bf4\uff0c\u201c\u66f4\u4f55\u51b5\u4f60\u6015\u7684\u662f\u90a3\u59d3\u987e\u7684\u2018\u5f71\u2019\u5b50\u3002\u201d \u3000\u3000\u201c\u4ed6\uff1f\u201d\u725b\u4e3d\u751f\u51e0\u4e4e\u6ca1\u8df3\u8d77\u6765\uff0c\u201c\u6211\u4f1a\u6015\u4ed6\uff1f\u201d \u3000\u3000\u201c\u5bf9\uff0c\u201d\u9a86\u94c3\u5c31\u662f\u8981\u628a\u4ed6\u6c14\u51fa\u771f\u706b\u6765\uff0c\u201c\u5927\u8c61\u5c31\u6015\u8001\u9f20\u3001\u6c34\u725b\u5948\u4f55\u4e0d\u4e86\u82cd\u8747\u2014\u2014\u73b0\u5728\u5c31\u4e0d\u77e5\u8c01\u662f\u5927\u8c61\u3001\u8c01\u662f\u82cd\u8747\u3002\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u90a3\u53eb\u201c\u9c7c\u751f\u201d\u7684\u6697\u7b97\u4e0d\u7740\uff0c\u5df2\u7ed9\u987e\u5f71\u5236\u4f4f\u4e86\u3002 \u3000\u3000\u201c\u4f60\u8fd8\u4e0d\u53bb\u6551\u4eba\u7684\u8bdd\uff0c\u201d\u9a86\u94c3\u8d81\u673a\u717d\u98ce\u62e8\u706b\uff1a\u201c\u90a3\u4e0d\u662f\u6015\uff0c\u800c\u662f\u6050\u60e7\u4e86\u3002\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u4f7f\u725b\u4e3d\u751f\u4e0d\u987e\u4e00\u5207\uff0c\u8d70\u8fdb\u9053\u573a\u3002 \u3000\u3000\u63a5\u4e0b\u6765\u7684\u53d1\u5c55\uff0c\u201c\u9c7c\u751f\u201d\u558a\u725b\u4e3d\u751f\u4e3a\u201c\u8001\u5927\u201d\uff0c\u4ea6\u9887\u51fa\u4e4e\u9a86\u94c3\u610f\u6599\u4e4b\u5916\u3002 \u3000\u3000\u7136\u540e\u662f\u725b\u4e3d\u751f\u6bd4\u62fc\u5df4\u95ed\u3002\u725b\u4e3d\u751f\u7559\u624b\u4e0d\u4f24\u4eba\uff0c\u9a86\u94c3\u8d81\u673a\u5077\u88ad\u987e\u5f71\uff0c\u53cd\u800c\u9020\u6210\u987e\u5f71\u786c\u6324\u725b\u4e3d\u751f\u7684\u5c40\u9762\u3002 \u3000\u3000\u786c\u62fc\u7684\u7ed3\u679c\u7adf\u662f\uff1a \u3000\u3000\u725b\u4e3d\u751f\u5931\u53bb\u4e86\u6218\u6597\u529b\u3002 \u3000\u3000\u8fd9\u6837\u7684\u201c\u7ed3\u679c\u201d\uff0c\u7edd\u5bf9\u662f\u9a86\u94c3\u59cb\u6599\u672a\u53ca\u7684\u3002 \u3000\u3000\u5979\u77e5\u9053\u8fd9\u7cbe\u608d\u7684\u6c49\u5b50\u6709\u4e24\u4e0b\u5b50\uff0c\u5374\u4e0d\u77e5\u9053\uff0c\u4ed6\u4e0d\u6b62\u6709\u4e24\u4e0b\u5b50\u3002 \u3000\u3000\u73b0\u5728\u725b\u4e3d\u751f\u8d1f\u4f24\u540e\uff0c\u53f2\u6d41\u82b3\u3001\u9648\u5251\u8c01\u7b49\u90fd\u4e0d\u5728\u8eab\u8fb9\uff0c\u773c\u524d\u6709\u4e00\u5927\u5806\u201c\u654c\u4eba\u201d\uff0c\u8fd8\u6709\u4e00\u4e2a\u4e00\u62db\u5373\u628a\u725b\u4e3d\u4e3b\u201c\u6253\u5012\u201d\u4e86\u7684\u4eba\uff01 \u3000\u3000\u5979\u662f\u4e0d\u662f\u80fd\u5e94\u4ed8\uff1f \u3000\u3000\u5979\u662f\u4e0d\u662f\u53ef\u4ee5\u5e94\u4ed8\u5f97\u6765\uff1f \u3000\u3000\u8fd9\u7b54\u6848\u8fde\u5979\u4e5f\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u5979\u4e5f\u4e0d\u60f3\u77e5\u9053\u3002 \u3000\u3000\u5979\u53ea\u77e5\u9053\u5728\u8fd9\u79cd\u65f6\u5019\uff0c\u4e00\u5b9a\u8981\u5f3a\u4f5c\u9547\u5b9a\uff0c\u4e00\u5b9a\u8981\u6bd4\u8fd9\u4e9b\u6076\u4eba\u66f4\u6076\uff01 \u3000\u3000\u51b5\u4e14\uff0c\u6bd5\u7adf\u5979\u4e0d\u662f\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u2014\u2014\u8fd8\u6709\u90a3\u51e0\u540d\u5730\u75de\u6d41\u6c13\u3002 \u3000\u3000\u5979\u4e00\u5ff5\u53ca\u6b64\uff0c\u5ffd\u7136\u53d1\u73b0\u4e00\u4e2a\u4e8b\u5b9e\uff1a \u3000\u3000\u9664\u4e86\u75db\u5f97\u8fd8\u7ad9\u4e0d\u76f4\u8eab\u5b50\u7684\u725b\u4e3d\u751f\u4e4b\u5916\uff0c\u8fd9\u8fb9\u5c31\u771f\u7684\u53ea\u5269\u4e0b\u5979\u4e00\u4e2a\u4eba\u4e86\u3002 \u3000\u3000\u56fa\u4e3a\u90a3\u51e0\u540d\u5c0f\u6d41\u6c13\u2014\u2014\u5305\u62ec\u90a3\u201c\u9c7c\u751f\u201d\u5728\u5185\u5168\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u2014\u2014\u6211\u4eec\u4e3a\u4ed6\u4eec\u51fa\u5934\uff0c\u8fd9\u51e0\u4e2a\u5c0f\u5154\u5d3d\u5b50\u7ade\u4e0d\u542d\u4e00\u58f0\u7684\u6e9c\u5149\u4e86\u3002 \u3000\u3000\u9a86\u94c3\u6c14\u5f97\u5dee\u70b9\u7709\u6bdb\u6389\u6210\u4e86\u80e1\u5b50\u3002 \u3000\u3000\u53ef\u662f\u518d\u6c14\u4e5f\u6ca1\u6709\u7528\u3002 \u3000\u3000\u773c\u524d\u7684\u56f0\u5883\u8981\u5e94\u4ed8\u3002 \u3000\u3000\u201c\u4f60\u4eec\u8fd9\u7b97\u6b3a\u8d1f\u4eba\u54ea\uff01\u201d \u3000\u3000\u987e\u5f71\u6df1\u6df1\u5438\u4e86\u4e00\u53e3\u6c14\uff0c\u94c1\u9752\u7740\u8138\uff0c\u4e00\u4e2a\u5b57\u4e00\u4e2a\u5b57\u7684\u8bf4\uff1a\u201c\u662f\u4f60\u4eec\u8fdb\u6765\u8e29\u76d8\u5b50\uff0c\u65bd\u6697\u7b97\u7684\u3002\u201d \u3000\u3000\u201c\u6211\u4eec\u2026\u2026\u201d\u53ef\u662f\u90a3\u51e0\u4e2a\u5c0f\u6d41\u6c13\u5df2\u6e9c\u4e4b\u5927\u5409\uff0c\u9a86\u94c3\u77e5\u9053\u518d\u600e\u4e48\u8bf4\u4e5f\u65e0\u7528\uff0c\u53ea\u597d\u5f3a\u8f9e\u7684\u8bf4\uff0c\u201c\u4f60\u4eec\u6b3a\u8d1f\u6211\u4eec\u662f\u5916\u6765\u4eba\u3002\u201d \u3000\u3000\u201c\u8c01\u7ba1\u4f60\u662f\u54ea\u91cc\u6765\u7684\u4eba\u3002\u201d\u987e\u5f71\u51b7\u51b7\u4ed6\u8bf4\uff0c\u201c\u8c01\u8fdb\u6765\u95f9\u4e8b\u5c31\u662f\u654c\u4eba\u3002\u201d\u91cc\u5916\u7684\u4eba\u4e00\u9f50\u54c4\u7b11\uff0c\u770b\u6765\u56f4\u89c2\u7684\u6c11\u4f17\u90fd\u76f8\u5f53\u652f\u6301\u987e\u5f71\uff0c\u5bf9\u95f9\u4e8b\u7684\u9a86\u94c3\u7b49\u4eba\u9887\u4e0d\u4ee5\u4e3a\u7136\u3002 \u3000\u3000\u201c\u4f60\u4eec\u2026\u2026\u201d\u9a86\u94c3\u7d20\u6027\u6492\u8d56\u5230\u5e95\uff1a\u201c\u4f60\u4eec\u6b3a\u8d1f\u6211\u662f\u5973\u4eba\u3002\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u4e00\u51fa\u53e3\uff0c\u6548\u679c\u66f4\u7cdf\u3002 \u3000\u3000\u5916\u9762\u5927\u547c\u5c0f\u53eb\u5439\u53e3\u54e8\uff0c\u5c45\u7136\u8fd8\u6709\u4eba\u634f\u7740\u55d3\u5b50\u53eb\uff1a\u201c\u987e\u6559\u7ec3\uff0c\u6211\u770b\u4f60\u5c31\u522b\u6b3a\u8d1f\u5979\uff0c\u5e72\u8106\u628a\u5979\u5f3a\u5978\u6389\u7b97\u4e86\uff01\u201d \u3000\u3000\u9a86\u94c3\u6c14\u5f97\u7c89\u8138\u901a\u7ea2\u3002 \u3000\u3000\u8fde\u987e\u5f71\u770b\u4e86\uff0c\u5fc3\u91cc\u4e5f\u5fcd\u4e0d\u4f4f\u6697\u53f9\uff1a\u597d\u4e00\u5f20\u6c14\u8d77\u6765\u66f4\u8273\u4e3d\u7684\u7f8e\u8138\uff01 \u3000\u3000\u201c\u4e0d\u8981\u8138\uff01\u201d\u9a86\u94c3\u9a7e\u9053\uff1a\u201c\u4f60\u4eec\u8fd9\u4e9b\u91ce\u4eba\uff0c\u4e0d\u77e5\u793c\u4eea\u5ec9\u803b\uff01\u201d \u3000\u3000\u8fd9\u4e00\u53e5\u4f7f\u5f97\u9053\u573a\u91cc\u5916\uff0c\u6709\u4eba\u5618\u58f0\u6709\u4eba\u54d7\u7136\u6709\u5165\u7d22\u6027\u9a82\u7c97\u8bdd\u3002 \u3000\u3000\u201c\u65e0\u7406\u5c31\u662f\u53e6\u4e00\u79cd\u65e0\u793c\u3002\u201d\u987e\u5f71\u53c8\u6df1\u6df1\u5438\u4e86\u4e00\u53e3\u6c14\uff0c\u5f90\u5f90\u5730\u5410\u4e86\u51fa\u6765\uff0c\u7709\u5934\u7d27\u9501\uff0c\u5e73\u9759\u7684\u8bf4\uff1a\u201c\u662f\u4f60\u4eec\u4e0d\u8bb2\u7406\u5728\u5148\u3002\u201d \u3000\u3000\u201c\u6211\u4eec\u4e3a\u4ec0\u4e48\u8981\u5bf9\u65e0\u793c\u7684\u4eba\u8bb2\u7406\uff1f\u201d\u9a86\u94c3\u6446\u51fa\u67b6\u5f0f\uff0c\u4f60\u4eec\u60f3\u8981\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u201c\u6709\u65f6\u5019\uff0c\u6ca1\u6709\u9053\u7406\u4e5f\u662f\u4e00\u79cd\u9053\u7406\uff0c\u201d\u987e\u5f71\u4e00\u5b9a\u5728\u53e3\u91cc\u54ac\u7740\u7259\uff0c\u8981\u4e0d\u7136\uff0c\u4ed6\u7684\u987b\u8fb9\u4e0d\u4f1a\u7a81\u9732\u4e86\u4e24\u68f1\u9752\u7b4b\uff0c\u201c\u4f60\u73b0\u5728\u597d\u50cf\u5728\u8bb2\u9053\u7406\uff0c\u4f46\u5176\u5b9e\u662f\u65e2\u65e0\u793c\u53c8\u65e0\u7406\u3002\u201d \u3000\u3000\u201c\u4f60\u4eec\u8d70\u5427\u3002\u6211\u4eec\u6839\u672c\u4e0d\u60f3\u8981\u600e\u6837\u3002\u201d\u5395\u5f71\u6325\u6325\u624b\uff0c\u5c45\u7136\u6709\u70b9\u75b2\u5026\u4ed6\u8bf4\uff0c\u201c\u4f60\u7684\u670b\u53cb\u4f24\u53d7\u5f97\u4e0d\u8f7b\uff0c\u5f97\u8981\u8d76\u5feb\u6276\u4ed6\u56de\u53bb\u6b47\u6b47\u3002\u201d \u3000\u3000\u8fd9\u7b54\u6848\u548c\u6001\u5ea6\u8f6c\u53d8\u5012\u662f\u4ee4\u9a86\u94c3\u4e00\u6014\uff0c\u4e5f\u4f7f\u5979\u7684\u6597\u5fd7\u65e0\u4ece\u53ef\u6cc4\u3002 \u3000\u3000\u201c\u732b\u54ed\u8017\u5b50\u2026\u2026\u201d\u5979\u4e0d\u4fe1\u81ea\u5df1\u53ef\u4ee5\u53bb\u7684\u201c\u5982\u6b64\u5bb9\u6613\u201d\u3002 \u3000\u3000\u201c\u6211\u4e0d\u662f\u732b\u3002\u201d\u987e\u5f71\u8138\u4e0a\u4f3c\u7b11\u975e\u7b11\uff0c\u8fd9\u65f6\u624d\u6df1\u6df1\u7684\u671b\u4e86\u9a86\u94c3\u4e00\u773c\uff0c\u201c\u4f60\u548c\u4f60\u7684\u670b\u53cb\u4e5f\u4e0d\u662f\u8001\u9f20\u3002\u201d \u3000\u3000\u4f17\u4eba\u90fd\u7b11\u5f00\u4e86\u3002\u9a86\u94c3\u53ea\u89c9\u8138\u4e0a\u5bf8\u70ed\uff0c\u4e5f\u4e0d\u77e5\u662f\u4e3a\u4e86\u81ea\u5df1\u8bf4\u9519\u4e86\u8bdd\uff0c\u8fd8\u662f\u56e0\u4e3a\u987e\u5f71\u770b\u5979\u7684\u773c\u795e\u3002 \u3000\u3000\u2014\u2014\u4ed6\u603b\u7b97\u671b\u4e86\u6211\u4e00\u773c\u3002 \u3000\u3000\u8fd9\u773c\u795e\u6ca1\u6709\u654c\u610f\u3002 \u3000\u3000\u2014\u2014\u8fd9\u773c\u795e\u597d\u719f\u6089\u2026\u2026 \u3000\u3000\u201c\u5144\u5f1f\u4eec\uff0c\u201d\u987e\u5f71\u5429\u5490\u5927\u5bb6\uff0c\u201c\u54b1\u4eec\u4eca\u5929\u5c31\u4e0d\u7ec3\u4e86\u3001\u65e9\u4e9b\u56de\u5bb6\u5427\u3002\u201d \u3000\u3000\u4ed6\u4eec\u5c45\u7136\u6362\u8863\u7684\u6362\u8863\uff0c\u6536\u62fe\u7684\u6536\u62fe\uff0c\u8fd8\u6709\u4eba\u9010\u76cf\u706f\u7684\u637b\u7184\u3001\u7adf\u6ca1\u5f53\u9a86\u94c3\u5c31\u5728\u9053\u9986\u91cc\u3002 \u3000\u3000\u9a86\u94c3\u6ca1\u6cd5\u5b50\u3002 \u3000\u3000\u5979\u4e0d\u80fd\u4e0d\u7167\u987e\u53d7\u4e86\u4f24\u7684\u725b\u4e3d\u751f\u3002 \u3000\u3000\uff5e\u5979\u603b\u4e0d\u80fd\u773c\u7741\u7741\u7b49\u8fd9\u4e00\u5e72\u4eba\u5148\u5979\u800c\u53bb\uff0c\u628a\u5979\u7559\u5728\u7a7a\u8361\u8361\u9ed1\u7cca\u7cca\u7684\u5927\u4f1a\u5802\u9053\u573a\u91cc\u3002 \u3000\u3000\u6240\u4ee5\u5979\u4e5f\u53ea\u6709\u8d70\u4e86\u3002 \u3000\u3000\u6276\u7740\u725b\u4e3d\u751f\u79bb\u53bb\u3002 \u3000\u3000\u5979\u8fd9\u65f6\u624d\u53d1\u89c9\uff1a \u3000\u3000\u725b\u4e3d\u751f\u597d\u91cd\u3002 \u3000\u3000\u6bd4\u5979\u5e73\u65f6\u60f3\u50cf\u5f97\u8fd8\u91cd\uff01 \u3000\u3000\u5979\u73b0\u5728\u662f\u5b81\u53ef\u63d0\u81ea\u5df1\u7684\u884c\u674e\uff0c\u4e5f\u4e0d\u7528\u53bb\u6276\u7740\u8fd9\u6837\u4e2a\u4eba\u3002\u8bb8\u662f\u603b\u53eb\u4eba\u66ff\u5979\u62ff\u884c\u674e\u5427\uff0c\u8fd9\u56de\u53ef\u662f\u8981\u73b0\u4e16\u62a5\u4e86\uff0c\u4e00\u62ff\u5c31\u201c\u62ff\u201d\u8fd9\u4e48\u4e2a\u8d85\u91cd\u7684\u884c\u674e\uff01 \u3000\u3000\u5979\u6276\u7740\u725b\u4e3d\u751f\u6162\u6162\u8d70\u51fa\u9053\u9986\u7684\u65f6\u5019\uff0c\u5fc3\u91cc\u6709\u4e00\u79cd\u201c\u5fcd\u8fb1\u8d1f\u91cd\u201d\u7684\u611f\u89c9\u3002 \u3000\u3000\u597d\uff0c\u4e0b\u56de\u6211\u53eb\u8001\u5927\u201c\u80a5\u9e2d\u201d\u6765\uff0c\u5c31\u6709\u4f60\u4eec\u597d\u770b\u7684\uff01 \u3000\u3000\u6709\u5c0f\u90e8\u5206\u4eba\u7fa4\u8fd8\u672a\u6563\u53bb\uff0c\u5728\u95e8\u53e3\u602a\u53eb\u3002 \u3000\u3000\u53ea\u542c\u9986\u91cc\u7684\u987e\u5f71\u6709\u6c14\u65e0\u529b\u7684\u53eb\u4e86\u4e00\u58f0\uff1a\u201c\u5404\u4f4d\uff0c\u4ed6\u4eec\u662f\u5916\u5730\u6765\u7684\uff0c\u653e\u4ed6\u4eec\u4e00\u9a6c\u5427\u3002\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u4e00\u51fa\uff0c\u8fd9\u4e9b\u56f4\u89c2\u7684\u4eba\u624d\u6ca1\u7ee7\u7eed\u4e3a\u96be\u5979\u3002 \u3000\u3000\u89c1\u9b3c\u4e86\uff01\u5979\u5fc3\u91cc\u60f3\uff1a\u90a3\u5bb6\u4f19\u8bf4\u8bdd\u8c61\u65ad\u4e86\u4e09\u5e74\u6c14\u4f3c\u7684\uff0c\u521a\u624d\u4e0d\u8fd8\u662f\u751f\u9f99\u6d3b\u864e\u780d\u4e09\u5341\u516d\u5200\u8fd8\u9f99\u7cbe\u864e\u731b\u7684\u6a21\u6837\u513f\u5417\uff1f\u600e\u4e48\u8fd9\u4e1c\u897f\u5728\u8fd9\u5730\u65b9\u6709\u8fd9\u4e48\u5927\u7684\u5f71\u54cd\u529b\uff0c\u4e00\u53e5\u6709\u6c14\u65e0\u529b\u6709\u795e\u65e0\u6c14\u7684\u8bdd\u5c31\u8db3\u4ee5\u9a71\u6563\u4eba\u7fa4\uff1f \u3000\u3000\u8fd9\u65f6\uff0c\u4e00\u4e2a\u4eba\uff0c\u5ffd\u95ea\u5230\u773c\u524d\uff0c\u62ff\u4e86\u4e00\u4ef6\u4e1c\u897f\uff0c\u5411\u5979\u548c\u725b\u4e3d\u751f\uff0c\u626c\u4e86\u4e00\u626c\u3002 \u3000\u3000\u5979\u9f3b\u91cc\u95fb\u5230\u6709\u70b9\u9178\u5473\uff0c\u6b63\u60f3\u95ea\u8eb2\uff0c\u5374\u89c1\u53ea\u662f\u4e00\u4e2a\u67af\u7626\u7684\u8001\u592a\u5a46\u3002 \u3000\u3000\u4e00\u4e2a\u5370\u5ea6\u8001\u5a46\u5a46\u3002 \u3000\u3000\u5979\u624b\u91cc\u62ff\u7684\u662f\u7eb1\u5e03\u3002 \u3000\u3000\u4e00\u4e2a\u8721\u67d3\u56fe\u6848\u7684\u7eb1\u5e03\u3002 \u3000\u3000\u90a3\u8001\u592a\u5a46\u5634\u91cc\u5ff5\u5ff5\u6709\u8bcd\uff0c\u53cd\u6b63\u5979\u4e5f\u542c\u4e0d\u4f46\u662f\u4ec0\u4e48\uff0c\u800c\u4e14\u5bf9\u7740\u4e00\u4e2a\u8001\u592a\u5a46\uff0c\u800c\u4e14\u4e5f\u6ca1\u6709\u5411\u5730\u6cfc\u4e9b\u4ec0\u4e48\uff0c\u6240\u4ee5\u4e5f\u4e0d\u80fd\u53d1\u4f5c\u3002 \u3000\u3000\u201c\u89c1\u9b3c\u4e86\uff01\u201d\u5979\u5fc3\u91cc\u5492\u9a82\uff0c\u53ea\u5e0c\u671b\u80fd\u628a\u201c\u5343\u94a7\u91cd\u62c5\u2019\u7684\u725b\u4e3d\u751f\u9001\u5230\u5f20\u5c0f\u6101\u5bb6\u91cc\uff0c\u7ed9\u8001\u5927\u597d\u597d\u7684\u6cbb\u6d3d\u3002\u53ef\u662f\u4e00\u60f3\u5230\u79bb\u5f20\u5c0f\u6101\u5bb6\u8fd8\u90a3\u4e48\u8fdc\uff0c\u90a3\u4e48\u9ed1\uff08\u5929\u8272\u5df2\u5168\u9ed1\u4e0b\u6765\uff09\uff0c\u90a3\u4e48\u8352\u50fb\uff0c\u5979\u7684\u5934\u7acb\u523b\u4f3c\u6709\u516d\u9897\u5927\uff0c\u8fd9\u5730\u65b9\u65e2\u6ca1\u6709\u7684\u58eb\uff0c\u53c8\u4eba\u751f\u8def\u4e0d\u719f\uff0c\u52a0\u4e0a\u5979\u4e0d\u77e5\u9053\uff08\u4e5f\u5fd8\u4e86\u95ee\uff09\u5f20\u5c0f\u6101\u5bb6\u91cc\u7684\u7535\u8bdd\u53f7\u7801\uff0c\u6b64\u523b\u7684\u5904\u5883\uff0c\u53ea\u6709\u786c\u6328\u3001\u786c\u633a\u3001\u786c\u71ac\u4e86\u3002 \u3000\u3000\u9a86\u94c3\u4e0d\u77e5\u9053\u4eba\u751f\u91cc\u6709\u591a\u5c11\u6b21\u9700\u8981\u201c\u9876\u786c\u4e0a\u201d\u7684\u5c40\u9762\uff0c\u5979\u53ea\u5e0c\u671b\u81ea\u5df1\u6c38\u8fdc\u4e0d\u8981\u518d\u9047\u4e0a\u8fd9\u79cd\u60c5\u5883\u3002 \u3000\u3000\u5979\u5f53\u7136\u6ca1\u60f3\u5230\u5176\u5b9e\u8fd9\u624d\u7b97\u662f\u4e2a\u5f00\u59cb\u3002 \u3000 \u3000 \u7b2c\u516d\u7ae0\u3000\u5982\u5200\u7684\u5f80\u4e8b \u3000 \u3000\u30001\u3001\u5f53\u6211\u4eec\u5c0f\u7684\u65f6\u5019 \u3000\u3000\u9648\u5251\u8c01\u79bb\u5f00\u4e86\u5f20\u5c0f\u6101\u7684\u5bb6\uff0c\u5e76\u6ca1\u6709\u76f4\u63a5\u53bb\u8ffd\u9a86\u94c3\u548c\u725b\u4e3d\u751f\u3002 \u3000\u3000\u4ed6\u81ea\u5927\u95e8\u51fa\u53bb\uff0c\u5374\u8f7b\u8f7b\u7684\u6263\u54cd\u4e86\u540e\u95e8\u3002 \u3000\u3000\u5f20\u8bde\u8fc7\u6765\u5e94\u95e8\u7684\u65f6\u5019\uff0c\u770b\u662f\u9648\u5251\u8c01\uff0c\u5634\u5df4\u7acb\u523b\u5f20\u6210\u4e86O\u578b\u3002 \u3000\u3000\u4ed6\u770b\u770b\u540e\u9662\u8db3\u6709\u5341\u4e00\u5c3a\u9ad8\u7684\u56f4\u5899\uff0c\u770b\u770b\u8eab\u4e0a\u70b9\u5c18\u4e0d\u67d3\u7684\u9648\u5251\u8c01\uff0c\u597d\u4e0d\u5bb9\u6613\u624d\u51dd\u51fa\u4e00\u53e5\u8bdd\u8981\u53d1\u95ee\uff0c\u9648\u5251\u8c01\u5df2\u4e0d\u614c\u4e0d\u5fd9\u7684\u8bf4\uff1a\u201c\u4e3a\u4e86\u4f60\u59b9\u59b9\uff0c\u8bf7\u4f60\u4e0d\u8981\u58f0\u5f20\u3002\u201d \u3000\u3000\u5f20\u8bde\u89c9\u5f97\u5341\u5206\u602a\u8bde\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u4f60\u59b9\u59b9\u5e76\u6ca1\u6709\u628a\u5b9e\u60c5\u548c\u76d8\u6258\u51fa\uff0c\u201d\u9648\u5251\u8c01\u80af\u5b9a\u800c\u575a\u5b9a\u5730\u9053\uff1a\u201c\u5979\u4e00\u5b9a\u8fd8\u6709\u4e8b\u60c5\u7792\u7740\u5927\u5bb6\uff0c\u53ef\u80fd\u8fd8\u5305\u62ec\u4e86\u4f60\u4eec\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\uff0c\u201d\u4ed6\u8865\u5145\u9053\uff1a\u201c\u90a3\u53ef\u80fd\u4fbf\u662f\u6848\u60c5\u7684\u8981\u70b9\u3002\u201d \u3000\u3000\u201c\u5c0f\u6101\u5979\u4ece\u4e0d\u505a\u4e8f\u5fc3\u4e8b\uff0c\u5e72\u5417\u8981\u7792\u4eba\uff01\u201d\u5f20\u8bde\u5bf9\u9648\u5251\u8c01\u7684\u6d3b\u76f8\u5f53\u62d2\u6297\uff0c\u201c\u4f60\u4e0d\u8981\u51a4\u6789\u597d\u4eba\uff0c\u6211\u59b9\u59b9\u662f\u4e2a\u5584\u826f\u7684\u597d\u5973\u5b69\u3002\u201d \u3000\u3000\u201c\u5c31\u662f\u56e0\u4e3a\u5979\u592a\u5584\u826f\u3001\u4eba\u592a\u597d\uff0c\u6240\u4ee5\u624d\u4e0d\u5f97\u5df2\u8981\u7792\u4f4f\u4e00\u4e9b\u4e8b\u60c5\u7684\u3002\u201d\u9648\u5251\u8c01\u6280\u5de7\u5730\u53cd\u95ee\uff0c\u201c\u4f60\u96be\u9053\u5728\u5148\u524d\u66fe\u542c\u8fc7\u963f\u8482\u548c\u5fb7\u53d4\u6b7b\u540e\u53c8\u51fa\u73b0\u7684\u4e8b\u5417\uff1f\u201d \u3000\u3000\u8fd9\u95ee\u9898\u4f7f\u5f97\u5f20\u8bde\u4e0d\u5f97\u4e0d\u540c\u610f\u3002 \u3000\u3000\u5728\u4eca\u5929\u4e4b\u524d\uff0c\u4ed6\u7684\u786e\u6ca1\u542c\u5c0f\u6101\u63d0\u8d77\u8fc7\u3002 \u3000\u3000\u201c\u5979\u662f\u6015\u5f15\u8d77\u5927\u5bb6\u7684\u60ca\u5413\uff0c\u4e0d\u60f3\u65b0\u95fb\u754c\u628a\u5b83\u5938\u5927\u6e32\u67d3\uff0c\u624d\u4e0d\u8bf4\u7684\u3002\u201d\u5f20\u8bde\u8fd8\u662f\u5728\u4e3a\u4ed6\u59b9\u59b9\u8fa9\u62a4\uff0c\u201c\u5979\u4e0d\u8bf4\u662f\u5bf9\u7684\uff01\u201d \u3000\u3000\u201c\u6211\u4e5f\u4e0d\u8ba4\u4e3a\u5979\u7684\u51fa\u53d1\u70b9\u662f\u9519\u7684\u3002\u201d\u9648\u5251\u8c01\u53cd\u95ee\uff1a\u201c\u53ef\u662f\uff0c\u8c61\u8fd9\u6837\u91cd\u8981\u7684\u4e8b\uff0c\u4f60\u80fd\u4fdd\u8bc1\u5979\u518d\u4e5f\u6ca1\u6709\u56e0\u4e3a\u826f\u5584\u7684\u7406\u7531\u800c\u4e0d\u544a\u8bc9\u60a8\u5417\uff1f\u201d \u3000\u3000\u5f20\u8bde\u4e00\u65f6\u4e3a\u4e4b\u8bed\u585e\uff1a\u201c\u4f60\u662f\u8bf4\uff0c\u5979\u8fd8\u6709\u3002\u3002\u3002\u201d \u3000\u3000\u201c\u81f3\u5c11\uff0c\u5979\u8fd8\u6709\u5f88\u591a\u5fc3\u4e8b\u3001\u6211\u770b\u5f97\u51fa\u6765\uff0c\u5979\u4e0d\u8bf4\u5176\u5b9e\u662f\u5bb3\u4e86\u5979\u3002\u201d \u3000\u3000\u201c\u5979\u3002\u3002\u3002\u54ce\uff0c\u8fd9\u50bb\u5973\u5b69\uff01\u201d\u5f20\u8bde\u5fc3\u75db\u7684\u8bf4\uff0c\uff1a\u201c\u9648\u5148\u751f\uff0c\u4f60\u8bf4\uff0c\u6211\u8be5\u600e\u4e48\u529e\uff1f\u600e\u4e48\u624d\u80fd\u8ba9\u5979\u8bf4\u51fa\u6765\uff1f\u201d \u3000\u3000\u201c\u5979\u4e0d\u80af\u8bf4\uff0c\u6211\u4e5f\u6ca1\u529e\u6cd5\u3002\u6211\u79bb\u5f00\u7684\u65f6\u5019\uff0c\u5176\u5b9e\u5df2\u6697\u793a\u53e6\u4e00\u4f4d\u670b\u53cb\uff1b\u8981\u8bbe\u6cd5\u4f7f\u5979\u628a\u6897\u5728\u5fc3\u91cc\u7684\u8bdd\u5410\u51fa\u6765\uff0c\u9664\u5251\u8c01\u5a49\u8f6c\u7684\u8bf4\uff0c\u201c\u53ef\u662f\uff0c\u9664\u4e86\u5e0c\u671b\u4f60\u59b9\u59b9\u7167\u5b9e\u5410\u9732\u4e4b\u5916\uff0c\u6211\u4eec\u8fd8\u53ef\u4ee5\u505a\u4e00\u4e9b\u4e8b\u3002\u201d \u3000\u3000\u201c\u505a\u4e00\u4e9b\u4e8b\uff1f\u201d \u3000\u3000\u201c\u505a\u8fd9\u4ef6\u4e8b\u8981\u4f60\u5e2e\u5fd9\u3002\u201d \u3000\u3000\u201c\u6211\uff1f\u201c\u5f20\u8bde\u6709\u70b9\u4e0d\u6562\u76f8\u4fe1\uff0c\u6211\u4e5f\u80fd\u5e2e\u5f97\u4e0a\u5fd9\uff1f\u201d \u3000\u3000\u201c\u80fd\u3002\u201d\u9648\u5251\u8c01\u65a9\u9489\u622a\u94c1\u7684\u8bf4\uff0c\u201c\u4f60\u63d0\u4f9b\u4e24\u4e2a\u4eba\u7684\u4f4f\u5904\u7ed9\u6211\u77e5\u9053\uff0c\u6700\u597d\uff0c\u4e3a\u4e86\u4fbf\u4e8e\u6211\u8fdb\u884c\u67e5\u95ee\u3002\u4f60\u5c31\u81ea\u540e\u95e8\u8ddf\u6211\u8d70\u4e00\u6b21\u3002\u201d \u3000\u3000\u201c\u4e24\u4e2a\u4eba\u2026\u2026\u201d \u3000\u3000\u201c\u963f\u5e1d\uff0c\u90a3\u4e2a\u5973\u80f6\u5de5\u3002\u201d\u9648\u5251\u8c01\u4ee5\u4e00\u79cd\u4ee4\u4eba\u6298\u670d\u7684\u5e72\u7ec3\u6c89\u7740\u7684\u9053\uff1a\u201c\u8fd8\u6709\u90a3\u4e2a\u8001\u770b\u66f4\uff0c\u5fb7\u53d4\u7684\u4f4f\u5904\u3002\u201d \u3000\u3000\u5f20\u8bde\u6709\u529e\u6cd5\u3002 \u3000\u3000\u4ed6\u51b3\u5fc3\u5e26\u9648\u5251\u8c01\u53bb\u3002 \u3000\u3000\u4ed6\u867d\u7136\u8fd8\u4e0d\u600e\u4e48\u4e86\u89e3\u4e5f\u4e0d\u5982\u4f55\u4fe1\u4efb\u8fd9\u51e0\u4e2a\u5916\u6765\u7684\u4eba\uff0c\u4f46\u53ea\u8981\u4e3a\u4e86\u4ed6\u59b9\u59b9\u597d\uff0c\u4ed6\u4ec0\u4e48\u90fd\u80af\u5e72\u3002 \u3000\u3000\u4ed6\u5df2\u5e74\u8fc7\u4e09\u5341\u4e94\u4e86\uff0c\u4e00\u76f4\u6ca1\u6709\u5a36\u8001\u5a46\u3002 \u3000\u3000\u4e5f\u8bb8\uff0c\u5728\u4ed6\u7684\u5fc3\u574e\u6df1\u5904\uff0c\u5df2\u628a\u8fd9\u7f8e\u4e3d\u800c\u5fe7\u6101\u7684\u59b9\u59b9\u5f53\u4f5c\u662f\u59bb\u5b50\u4e86\u3002 \u3000\u3000\u867d\u7136\u4ed6\u81ea\u5df1\u5e76\u6ca1\u6709\u81ea\u89c9\u5230\u8fd9\u70b9\u3002 \u3000\u3000\u9648\u5251\u8c01\u7ad9\u5728\u540e\u9662\uff0c\u7b49\u5f20\u8bde\u51fa\u6765\u3002 \u3000\u3000\u9662\u5b50\u91cc\u6709\u4e00\u4e1d\u5fae\u98ce\uff0c\u53f6\u5b50\u7b5b\u7740\u9633\u5149\uff0c\u5df2\u7ecf\u4e0d\u7099\u4eba\u4e86\u3002\u8fdc\u5904\u64ad\u7740\u4e00\u9996\u201c\u5f53\u6211\u4eec\u5c0f\u7684\u65f6\u5019\u201d\uff0c\u9662\u5b50\u91cc\u6709\u4e00\u53e3\u6c34\u4e95\uff0c\u4e24\u4e09\u53ea\u6bcd\u9e21\u548c\u4e00\u7fa4\u5c0f\u9e21\uff0c\u6b63\u5728\u5544\u98df\u58f3\u7c73\uff0c\u4e00\u53ea\u72d7\u628a\u809a\u5b50\u8db4\u8d34\u5728\u51b7\u6d0b\u7070\u5730\u4e0a\u7761\u5927\u89c9\uff0c\u9648\u5251\u8c01\u5ffd\u7136\u60f3\u5230\uff0c\u5f20\u5c0f\u6101\u7684\u7ae5\u5e74\uff0c\u5c31\u5728\u8fd9\u91cc\u8fc7\u7684\u5417\uff1f\u4ed6\u53ef\u4ee5\u60f3\u50cf\u4e00\u4e2a\u7f8e\u4e3d\u7684\u5c0f\u5973\u5b69\uff0c\u6258\u7740\u816e\uff0c\u7728\u7740\u65e0\u6687\u7684\u773c\u775b\u5728\u542c\u6b4c\u770b\u82b1\u770b\u843d\u53f6\uff0c\u957f\u5927\u540e\u4fbf\u6210\u4e3a\u5a49\u7ea6\u800c\u8f7b\u6cbe\u7740\u5fae\u6101\u7684\u5f20\u5c0f\u6101\u3002 \u3000\u3000\u81ea\u6211\u4fe9\u5c0f\u7684\u65f6\u5019 \u3000\u3000\u65f6\u5e38\u624b\u633d\u7740\u624b \u3000\u3000\u5802\u4e0a\u6816\u606f\u5802\u4e0b\u8d70 \u3000\u3000\u4e0d\u77e5\u9053\u4ec0\u4e48\u662f\u5fe7\u6101\u2026\u2026 \u3000\u3000\u90a3\u9996\u6b4c\u8fdc\u8fdc\u5730\u98d8\u6765\uff0c\u50cf\u4e00\u4e2a\u8fd1\u8fd1\u7684\u7ae5\u5e74\u3002 \u3000\u3000\u9648\u5251\u8c01\u4e0d\u7981\u4e5f\u4f4e\u54fc\u7740\uff0c\u76f4\u81f3\u4ed6\u542c\u5230\u53e6\u4e00\u79cd\u58f0\u97f3\uff0c\u597d\u50cf\u662f\u68b5\u5531\uff0c\u53c8\u4f3c\u662f\u5ff5\u7ecf\uff0c\u53ef\u662f\u518d\u4ed4\u7ec6\u542c\u53bb\uff0c\u53c8\u50cf\u6570\u5341\u4e2a\u4eba\u628a\u4e16\u95f4\u6700\u6076\u6bd2\u7684\u5492\u9a82\u8bed\u8a00\u5012\u8fc7\u6765\u5ff5\u3002 \u3000\u3000\u58f0\u97f3\u5728\u4e0d\u8fdc\u5904\u4e00\u4e2a\u70df\u96fe\u8ff7\u6f2b\u7684\u5730\u65b9\u4f20\u6765\u3002 \u3000\u3000\u9648\u5251\u8c01\u77e5\u9053\u90a3\u662f\u4ec0\u4e48\u5730\u65b9\u3002 \u3000\u3000\u4ed6\u5fc3\u91cc\u5ffd\u7136\u751f\u8d77\u4e00\u79cd\u611f\u89c9\uff1a \u3000\u3000\u4ed6\u4e00\u5b9a\u4f1a\u53bb\u90a3\u5ea7\u5e99\uff0c\u89c1\u8bc6\u4e00\u4e0b\u201c\u7ea2\u6bdb\u62ff\u7763\u201d\uff0c\u4f1a\u4e00\u4f1a\u4e3b\u6301\u90a3\u795e\u79d8\u4eea\u5f0f\u7684\u8001\u4eba\u3002 \u3000\u3000\u90a3\u4e2a\u53eb\u987e\u6b65\u7684\u8001\u4eba\u3002 \u3000\u3000\u8fd9\u4e00\u5929\u4e00\u5b9a\u4e0d\u4f1a\u8fdc\u3002 \u3000\u3000\u800c\u4e14\u5df2\u7ecf\u5f88\u8fd1\u5f88\u8fd1\u4e86\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u5f20\u8bde\u51fa\u6765\u4e86\u3002 \u3000\u3000\u4ed6\u4eec\u5315\u56fe\u4e0d\u60ca\u7684\u79bb\u5f00\u4e86\u5f20\u5bb6\u3002 \u3000\u3000\u5f20\u5bb6\u5927\u5385\u91cc\u6e29\u6587\u548c\u53f2\u6d41\u82b3\u6b63\u5728\u9017\u5f20\u5c0f\u6101\u8bf4\u8bdd\uff0c\u6d51\u4e0d\u77e5\u4e24\u4eba\u7684\u6765\u3001\u4e24\u4eba\u7684\u53bb\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u9648\u5251\u8c01\u4e5f\u4e0d\u77e5\u9053\uff0c\u8fd9\u65f6\u5019\uff0c\u53f2\u6d41\u82b3\u548c\u6e29\u6587\u5728\u4e92\u76f8\u4e89\u6267\u4e2d\u5374\u4e0d\u610f\u628a\u95ee\u9898\u8ffd\u5230\u4e86\u8981\u5bb3\u3002 \u3000\u3000\u201c\u5f20\u5c0f\u59d0\u2026\u2026\u54ce\uff0c\u8fd9\u79f0\u547c\u591a\u89c1\u5916\u5440\uff0c\u54b1\u4eec\u4e00\u89c1\u6295\u7f18\uff0c\u4f60\u4e5f\u5c31\u522b\u53eb\u6211\u5148\u751f\u4e86\u2026\u2026\u5f20\u5c0f\u6101\uff0c\u4f60\u522b\u6015\uff0c\u8fd9\u4e16\u4e0a\u6ca1\u6709\u9b3c\u8fd9\u56de\u4e8b\u7684\uff0c\u8981\u771f\u7684\u6709\u9b3c\uff0c\u6b7b\u53bb\u7684\u4eba\u90a3\u4e48\u591a\uff0c\u4eba\u4eba\u90fd\u662f\u9b3c\uff0c\u8fd9\u4e16\u754c\u65e9\u7ed9\u9b3c\u5360\u6ee1\u4e86\uff0c\u4eba\u600e\u4f4f\u5f97\u4e0b\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u5e74\u7eaa\u6bd4\u4f60\u7a0d\u957f\uff0c\u6211\u5c31\u4e0d\u5ba2\u6c14\u53eb\u4f60\u5c0f\u6101\u4e86\u3002\u5c0f\u6101\uff0c\u4f60\u4e0d\u8981\u6050\u60e7\uff0c\u90a3\u665a\u4f60\u6240\u89c1\u7684\uff0c\u4e00\u5b9a\u90fd\u662f\u5e7b\u8c61\u2026\u2026\u5e7b\u8c61\u662f\u6ca1\u4ec0\u4e48\u53ef\u6015\u7684\u2014\u2014\u6709\u4eba\u5e7b\u60f3\u81ea\u5df1\u53d8\u6210\u4e86\u4e00\u53ea\u8001\u9f20\uff0c\u53ef\u662f\u4ed6\u4ecd\u662f\u4eba\uff0c\u4e0d\u662f\u8001\u9f20\uff0c\u5728\u5e7b\u89c9\u7684\u4e16\u754c\u91cc\u65e0\u8bba\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\uff0c\u4f60\u90fd\u4e0d\u8981\u5f53\u771f\u3001\u4e0d\u5fc5\u5f53\u771f\u2026\u2026\u201d \u3000\u3000\u4e0d\u6599\uff0c\u53f2\u6d41\u82b3\u7684\u8bdd\u8fd8\u6ca1\u8bf4\u5b8c\uff0c\u5f20\u5c0f\u6101\u4e0d\u4f46\u4e0d\u611f\u5230\u5b89\u6170\uff0c\u53cd\u800c\u54ed\u4e86\u51fa\u6765\uff0c\u4e00\u53cc\u7ea4\u624b\u6342\u7740\u8138\u8bf4\uff1a\u201c\u4e0d\uff01\u4e0d\uff01\u90a3\u662f\u771f\u7684\uff0c\u90a3\u662f\u771f\u7684\uff01\u201d \u3000\u3000\u8fd9\u4e00\u54ed\uff0c\u628a\u5f20\u8001\u7238\u548c\u5f20\u8001\u592a\u5927\u90e8\u7ed9\u54ed\u4e86\u51fa\u6765\uff0c\u602a\u8d23\u5730\u77aa\u7740\u773c\u524d\u4e24\u540d\u201c\u4e0d\u53d7\u6b22\u8fce\u201d\u3001\u201c\u6b7b\u8d56\u7740\u4e0d\u8d70\u201d\u7684\u201c\u5ba2\u4eba\u201d\uff0c\u4ee5\u773c\u8272\u4e0b\u201c\u9010\u5ba2\u4ee4\u201d\u3002 \u3000\u3000\u6e29\u6587\u5fd9\u8214\u4e86\u8214\u5e72\u5507\uff0c\u4f5c\u72b6\u9a82\u53f2\u6d41\u82b3\uff1a\u201c\u662f\u4e0d\u662f\uff0c\u90fd\u53eb\u4f60\u4e0d\u8981\u6c14\u54ed\u5f20\u5c0f\u59d0\u7684\u4e86\uff0c\u4e56\u4e56\uff0c\u662f\u771f\u7684\u5c31\u662f\u771f\u7684\u5427\uff0c\u4f46\u90fd\u8fc7\u53bb\u4e86\uff0c\u6709\u4ec0\u4e48\u597d\u54ed\u7684\uff1f\u201d \u3000\u3000\u4e0d\u610f\u5f20\u5c0f\u6101\u8fd9\u56de\u54ed\u5f97\u5f88\u5389\u5bb3\uff0c\u5979\u62bd\u6410\u7740\u3001\u98a4\u6296\u7740\uff0c\u4f46\u5c31\u662f\u6ca1\u6709\u771f\u7684\u653e\u58f0\u54ed\u51fa\u6765\u3002 \u3000\u3000\u5927\u5bb6\u90fd\u77e5\u9053\u8fd9\u79cd\u201c\u54ed\u201d\u8981\u6bd4\u771f\u7684\u201c\u54ed\u201d\u66f4\u60b2\u75db\u3002 \u3000\u3000\u8fd9\u624d\u662f\u771f\u201c\u54ed\u201d\u2014\u2014\u54ed\u4e0d\u51fa\u6765\u7684\u54ed\u3002 \u3000\u3000\u201c\u4e0d\uff01\u4e0d\u662f\u771f\u7684\uff01\u201d\u5f20\u5c0f\u6101\u773c\u6cea\u7c0c\u7c0c\u5730\u6d41\u4e0b\u6765\u3001\u4ee4\u4eba\u770b\u4e86\u4e5f\u8981\u60cb\u60dc\uff0c\u8fd9\u4e48\u7f8e\u597d\u7684\u6cea\u600e\u4e48\u80fd\u6d41\u8fc7\u8fd9\u6837\u4e00\u5f20\u7f8e\u4e3d\u7684\u8138\u4e0a\uff0c\u800c\u4e14\uff0c\u600e\u80fd\u7adf\u8fd8\u8ba9\u5b83\u7ee7\u7eed\u6d41\u4e0b\u53bb\u5462\uff01\u201c\u4ed6\u5bf9\u6211\u505a\u51fa\u90a3\u6837\u7684\u4e8b\u2026\u2026\u90a3\u4e0d\u662f\u771f\u7684\uff01\u6211\u90fd\u4e0d\u8981\u6d3b\u4e86\uff01\u201d \u3000\u3000\u8fd9\u4e00\u6765\uff0c\u5f20\u5c0f\u6101\u51e0\u8fd1\u5d29\u6e83\u3002 \u3000\u3000\u53f2\u6d41\u82b3\u548c\u6e29\u6587\u53ef\u614c\u4e86\u624b\u811a\uff0c\u4e00\u4e2a\u5411\u5f20\u5c0f\u6101\u8bf4\u5c3d\u597d\u6d3b\u3001\u51fa\u5c3d\u6cd5\u5b9d\uff0c\u4e0d\u6562\u6c42\u80fd\u54c4\u5f97\u5979\u7834\u6d95\u4e3a\u7b11\uff0c\u53ea\u6c42\u8ba9\u5979\u522b\u4f24\u5fc3\u4e0b\u53bb\u5c31\u662f\u4e07\u5e78\u4e86\uff0c\u4e00\u4e2a\u5219\u5728\u8ddf\u5f20\u8001\u7239\u3001\u5f20\u8001\u592a\u5927\u767e\u822c\u5206\u8bf4\uff0c\u8bf4\u597d\u8bf4\u6b79\uff0c\u624d\u4e0d\u81f4\u88ab\u64b5\u51fa\u95e8\u53bb\u3002\u81f3\u4e8e\u4ec0\u4e48\u8bf1\u5411\u8c03\u67e5\uff0c\u5f20\u5c0f\u6101\u4e00\u4e2a\u68a8\u82b1\u5e26\u96e8\uff0c\u4e24\u5165\u65e9\u5c31\u5fc3\u4e71\u5982\u9ebb\u3002\u8fd9\u56de\u7edd\u5bf9\u4e0d\u662f\u53eb\u4ec0\u4e48\u95ee\u4e2a\u6c34\u843d\u77f3\u51fa\uff0c\u800c\u662f\u95ee\u5230\u4e5d\u9704\u4e91\u5916\u53bb\u4e86\u3002 \u3000\u3000\uff12\u3001\u7ea2\u70e7\u725b\u8089 \u3000\u3000\u9648\u5251\u8c01\u6b65\u4e0a\u65e5\u7a0b\u7684\u65f6\u5019\uff0c\u5929\u8272\u5df2\u7ecf\u9ed1\u4e86\u3002 \u3000\u3000\u4ed6\u548c\u5f20\u8bde\u7ecf\u8fc7\u5e02\u533a\uff0c\u89c1\u5230\u8def\u4eba\u8bae\u8bba\u7eb7\u7eb7\uff0c\u6253\u542c\u4e4b\u4e0b\uff1b\u624d\u77e5\u9053\u201c\u5927\u4f1a\u5802\u201d\u91cc\u95f9\u4e86\u4e8b\uff1a\u4e24\u4e2a\u201c\u5916\u6765\u4eba\u201d\u2014\u2014\u2014\u7537\u4e00\u5973\u3001\u52c7\u7684\u9ad8\u58ee\u5973\u7684\u7f8e\u4e3d\u2014\u2014\u7adf\u5e26\u4e86\u4e00\u7fa4\u672c\u5730\u51fa\u540d\u5201\u6076\u7684\u6d41\u6c13\u53bb\u201c\u521a\u51fb\u9053\u201d\u7684\u9053\u573a\u8e29\u76d8\u5b50\uff0c\u4e00\u756a\u683c\u6597\u4e0b\uff0c\u95f9\u4e8b\u7684\u4eba\u8fd8\u52a8\u4e86\u5200\u5b50\u3001\u65bd\u4e86\u6697\u7b97\uff0c\u4f46\u4ecd\u7ed9\u6253\u5f97\u62b1\u5934\u9f20\u7a9c\uff0c\u90a3\u201c\u9ad8\u5927\u7684\u6076\u6c49\u201d\u88ab\u6253\u5f97\u201c\u7ad9\u4e0d\u76f4\u8eab\u5b50\u201d\uff0c\u7531\u90a3\u7f8e\u4e3d\u65f6\u9ae6\u5973\u5b50\u6276\u8d70\uff0c\u4f46\u521a\u51fb\u9053\u603b\u6559\u7ec3\u987e\u5f71\u4e5f\u53d7\u4e86\u4f24\uff0c\u636e\u8bf4\u8fd8\u6d41\u4e86\u4e00\u5730\u7684\u8840\u2026\u2026 \u3000\u3000\u8fd9\u4f20\u8bf4\u5927\u81f4\u662f\u4e3a\u987e\u5f71\u62b1\u4e0d\u5e73\uff0c\u5bf9\u201c\u5916\u6765\u4eba\u201d\u5341\u5206\u9119\u8584\u3002 \u3000\u3000\u5728\u4e00\u4e2a\u8ddf\u5916\u9762\u4e16\u754c\u5e76\u672a\u5145\u4efd\u4ea4\u6d41\u7684\u4e61\u9547\u91cc\u3001\u8fd9\u79cd\u5fc3\u6001\u7edd\u5bf9\u662f\u6b63\u5e38\u7684\u3002 \u3000\u3000\u53ea\u6709\u9648\u5251\u8c01\u611f\u89c9\u5230\u4e0d\u5bfb\u5e38\u3002 \u3000\u3000\u2014\u2014\u51fa\u4e8b\u7684\u4eba\u60f3\u5fc5\u662f\u9a86\u94c3\u548c\u725b\u4e3d\u751f\u3002 \u3000\u3000\u2014\u2014\u542c\u6765\u725b\u4e3d\u751f\u8fd8\u53d7\u4e86\u4f24\uff01 \u3000\u3000\u2014\u2014\u6ca1\u60f3\u5230\u987e\u5f71\u7684\u6b66\u529f\u8fd8\u8981\u6bd4\u81ea\u5df1\u60f3\u50cf\u4e2d\u66f4\u9ad8\uff0c\u8fde\u725b\u4e3d\u751f\u90fd\u5e94\u4ed8\u4e0d\u4e86\u4ed6\uff01 \u3000\u3000\u2014\u2014\u770b\u6765\u8fd9\u5730\u65b9\u7684\u4eba\u8fd8\u5341\u5206\u7231\u6234\u987e\u5f71\uff01 \u3000\u3000\u9648\u5251\u8c01\u5fc3\u5fe7\u725b\u4e3d\u751f\u548c\u9a86\u94c3\u7684\u60c5\u5f62\uff0c\u6240\u4ee5\u4ed6\u5feb\u6b65\u75be\u8d70\uff0c\u60f3\u5148\u8d76\u53e3\u53bb\u770b\u770b\u3002 \u3000\u3000\u5f20\u8bde\u8ddf\u9648\u5251\u8c01\u719f\u7edc\u4e86\uff0c\u4e5f\u9010\u6e10\u6d88\u9664\u4e86\u654c\u610f\u3002 \u3000\u3000\u4ed6\u539f\u672c\u60f3\u8bf7\u9648\u5251\u8c01\u5403\u70b9\u4e1c\u897f\uff0c\u4f46\u9648\u5251\u8c01\u5df2\u65e0\u5fc3\u9017\u7559\u3002 \u3000\u3000\u65e2\u7136\u9648\u5251\u8c01\u8981\u8d76\u7740\u56de\u53bb\uff0c\u4ed6\u4fbf\u7d22\u6027\u518d\u5f85\u4e00\u4f1a\u513f\uff0c\u4e70\u70b9\u7092\u7c89\u56de\u53bb\u7ed9\u5927\u5bb6\u4f5c\u6d88\u591c\u3002 \u3000\u3000\u2014\u2014\u5c0f\u6101\u548c\u7238\u5988\u4e00\u5411\u90fd\u559c\u6b22\u7092\u7c89\u3002 \u3000\u3000\u4e00\u5c24\u5176\u662f\u559c\u6b22\u5403\u5927\u98df\u5802\u524d\u90a3\u6f6e\u5dde\u4f6c\u963f\u5e2d\u7684\u7092\u6c99\u6cb3\u7c89\u3002 \u3000\u3000\u4e8e\u662f\u5f20\u8bde\u4fbf\u7559\u4e0b\u6765\u4e70\u7092\u7c89\uff0c\u9664\u5251\u8c01\u4e00\u4e2a\u4eba\u5148\u884c\u8d76\u8fd4\u3002 \u3000\u3000\u5e78\u597d\u9648\u5251\u8c01\u662f\u8d76\u7740\u56de\u53bb\u3002 \u3000\u3000\u518d\u56de\u8fdf\u4e00\u6b65\uff0c\u4ed6\u89c1\u5230\u7684\u725b\u4e3d\u751f\u548c\u9a86\u94c3\u53ef\u80fd\u5c31\u4e0d\u518d\u751f\u9f99\u6d3b\u864e\u548c\u7f8e\u4e3d\u52a8\u4eba\u4e86\u3002 \u3000\u3000\u800c\u662f\u4e24\u5757\u70ad\u3002\u2026 \u3000\u3000\u70e7\u7126\u4e86\u7684\u70ad\u3002 \u3000\u3000\u9648\u5251\u8c01\u5faa\u7740\u9ec4\u6ce5\u548c\u8305\u8349\u4e1b\u8d70\u53bb\uff0c\u5ffd\u5c14\uff0c\u89c9\u5f97\u65ad\u6811\u5934\u90a3\u513f\u6709\u4eba\u5f71\u4e00\u95ea\u3002 \u3000\u3000\u9648\u5251\u8c01\u53ea\u5e73\u5e73\u9759\u9759\u7684\u95ee\u4e86\u4e00\u53e5\uff1a\u201c\u8c01\uff1f\u201d \u3000\u3000\u53ea\u542c\u4e00\u58f0\u547b\u541f\uff0c\u4e00\u540d\u8001\u5927\u5a46\u884c\u4e86\u51fa\u674e\uff0c\u867d\u7136\u56e0\u8857\u706f\u5728\u8fdc\u800c\u6709\u8f6c\u6298\u7684\u5730\u65b9\u7167\u6765\uff0c\u5341\u5206\u5fae\u5f31\uff0c\u6837\u5b50\u770b\u4e0d\u6e05\u695a\uff0c\u4f46\u9648\u5251\u8c01\u53ef\u4ee5\u611f\u89c9\u5f97\u5230\u90a3\u8001\u592a\u5a46\u5341\u5206\u80ae\u810f\uff0c\u800c\u4e14\u4eff\u4f5b\u8fd8\u5f88\u75db\u82e6\uff0c\u9690\u9690\u7ea6\u7ea6\u8fd8\u5e26\u4e86\u4e00\u80a1\u9178\u5473\u3002 \u3000\u3000\u9648\u5251\u8c01\u5fc3\u60f3\uff1a\u201c\u8fd9\u8001\u5987\u4eba\u5927\u6982\u662f\u5728\u8349\u5806\u91cc\u5927\u4fbf\u5427\uff1f\u201d \u3000\u3000\u4ed6\u4e0d\u60f3\u751f\u4e8b\uff0c\u4fbf\u7ee7\u7eed\u8d70\u4ed6\u7684\u8def\u3002 \u3000\u3000\u53ef\u662f\u90a3\u8001\u592a\u5a46\u4e00\u9635\u54b3\u55fd\uff0c\u6447\u6447\u6b32\u5760\u7684\u6837\u5b50\uff0c\u9648\u5251\u8c01\u8fde\u5fd9\u4f38\u624b\u8fc7\u53bb\u6276\u5979\u4e00\u628a\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\u5019\uff0c\u5728\u9648\u5251\u8c01\u5fc3\u91cc\uff0c\u751f\u51fa\u4e00\u79cd\u611f\u89c9\u3002 \u3000\u3000\u4e00\u79cd\u7279\u5f02\u7684\u611f\u89c9\u3002 \u3000\u3000\u751a\u81f3\u662f\u602a\u5f02\u7684\u611f\u89c9\u3002 \u3000\u3000\u8fd9\u662f\u4ed6\u5929\u751f\u7684\u7980\u8d4b\u3002 \u3000\u3000\u2014\u2014\u2014\u65e6\u6709\u5371\u673a\u53d1\u751f\u7684\u65f6\u5019\uff0c\u4ed6\u591a\u80fd\u5728\u5371\u673a\u51fa\u73b0\u524d\u4e00\u5239\u90a3\u9884\u611f\u5f97\u5230\u3002 \u3000\u3000\u8fd9\u4e00\u5239\u90a3\u5b9e\u5728\u662f\u592a\u91cd\u8981\u4e86\u3002 \u3000\u3000\u201c\u6599\u654c\u673a\u5148\u201d\u8fd9\u56db\u4e2a\u5b57\uff0c\u4e5f\u4e0d\u8fc7\u662f\u638c\u63e1\u4e86\u4e00\u5239\u90a3\u4f18\u52bf\u7684\u610f\u601d\u3002 \u3000\u3000\u5c24\u5176\u5728\u9ad8\u624b\u6bd4\u6b66\u8fc7\u62db\u91cc\uff0c\u4e00\u5239\u90a3\u4e0d\u4ec5\u53ef\u4ee5\u5206\u80dc\u8d1f\uff0c\u5224\u8f93\u8d62\uff0c\u8fd8\u53ef\u4ee5\u5b9a\u751f\u6b7b\u3001\u89c1\u8363\u8fb1\u3002 \u3000\u3000\u9648\u5251\u8c01\u5c31\u5728\u5371\u673a\u6765\u4e34\u7684\u524d\u4e00\u5239\u90a3\u611f\u89c9\u5230\u4e86\u3002 \u3000\u3000\u63a5\u4e0b\u6765\u7684\u53cd\u5e94\uff0c\u5219\u8981\u770b\u4ed6\u81ea\u5df1\u7684\u4e86\u3002 \u3000\u3000\u4ed6\u7b2c\u4e00\u4e2a\u53cd\u5e94\u6781\u4e3a\u8be1\u5f02\u3002 \u3000\u3000\u4ed6\u7acb\u5373\u95ed\u4e0a\u4e86\u53cc\u76ee\u3002 \u3000\u3000\u2014\u2014\u81f3\u4e8e\u4ed6\u4e3a\u4ec0\u4e48\u4f1a\u95ed\u4e0a\u53cc\u773c\uff0c\u4e3b\u8981\u662f\u56e0\u4e3a\u5728\u4e0b\u5348\u4ed6\u5230\u5fb7\u53d4\u5bb6\u4e3a\u8bbf\u8c08\u91cc\uff0c\u4f7f\u4ed6\u8b66\u89c9\u5230\uff1a \u3000\u3000\u5408\u4e0a\u53cc\u773c\uff0c\u662f\u73b0\u5728\u7b2c\u4e00\u4ef6\u8981\u505a\u7684\u4e8b\u3002 \u3000\u3000\u7136\u540e\u4ed6\u611f\u89c9\u5f97\u5230\u90a3\u8001\u592a\u5a46\u6b63\u5411\u4ed6\u6b3a\u8fd1\u3002 \u3000\u3000\u540c\u4e00\u65f6\u95f4\u4ed6\u95fb\u5230\u4e00\u822c\u523a\u9f3b\u7684\u9178\u5473\u3002 \u3000\u3000\u90a3\u8001\u592a\u5a46\u903c\u8fd1\u8eab\u5f62\u4e4b\u8be1\u79d8\u3001\u8fc5\u75be\uff0c\u4e0d\u4f46\u5b8c\u5168\u4e0d\u50cf\u662f\u4e00\u4e2a\u4e0a\u4e86\u5e74\u7eaa\u7684\u5973\u4eba\uff0c\u751a\u81f3\u4e25\u683c\u6765\u8bf4\uff0c\u50cf\u4e00\u6761\u86c7\u3002 \u3000\u3000\u50cf\u4e00\u6761\u5728\u8349\u4e1b\u91cc\u6e38\u8d70\u81ea\u5982\u7684\u6bd2\u86c7\u3002 \u3000\u3000\u800c\u4eca\u9648\u5251\u8c01\u7684\u4eba\u5c31\u5728\u8349\u4e1b\u91cc\u3002 \u3000\u3000\u2014\u2014\u5728\u8349\u4e1b\u91cc\u62b5\u6297\u6bd2\u86c7\u5c31\u8ddf\u5728\u6cb3\u91cc\u5e94\u4ed8\u6c34\u86c7\u4e00\u822c\u4e0d\u597d\u5bf9\u4ed8\u3002 \u3000\u3000\u4f55\u51b5\u4ed6\u8fd8\u95ed\u7740\u53cc\u773c\u3002 \u3000\u3000\u6218\u6597\u4e00\u5f00\u59cb\u5c31\u975e\u5e38\u5267\u70c8\u3002 \u3000\u3000\u4f46\u4e5f\u7ed3\u675f\u5f97\u5f88\u5feb\u3002 \u3000\u3000\u8001\u5a46\u5a46\u8fc5\u75be\u5730\u63a5\u8fd1\u9648\u5251\u8c01\uff0c\u9e70\u722a\u4e00\u822c\u7684\u53cc\u624b\uff0c\u5bf9\u9664\u5251\u8c01\u53d1\u52a8\u4e86\u72e0\u547d\u7684\u653b\u88ad\u3002 \u3000\u3000\u51e1\u662f\u5979\u6240\u6263\u9501\u4e4b\u5904\uff0c\u65e0\u4e0d\u662f\u54bd\u5589\u3001\u5589\u6838\u3001\u592a\u9633\u7a74\u3001\u9888\u4fa7\u3001\u6512\u5fc3\u7a74\u3001\u814b\u7a9d\u3001\u9f20\u6eaa\u7b49\u6b7b\u7a74\u8981\u5bb3\u3002 \u3000\u3000\u9648\u5251\u8c01\u867d\u7136\u95ed\u7740\u773c\uff0c\u4f46\u4ed6\u8d34\u6b65\u968f\u8eab\uff0c\u9047\u62db\u62c6\u62db\uff0c\u4e0d\u4f46\u8fde\u6d88\u5e26\u6253\uff0c\u8fd8\u8fde\u6253\u5e26\u6d88\uff0c\u5bc6\u6b65\u63c9\u8eab\uff0c\u660e\u9000\u6697\u8fdb\uff0c\u4f3c\u5f31\u8fd8\u5f3a\uff0c\u6253\u5230\u540e\u6765\uff0c\u9648\u5251\u8c01\u7d27\u7d27\u7684\u7c98\u624b\u8d34\u8ff8\u3001\u8001\u592a\u5a46\u53ea\u6709\u5403\u529b\u5e94\u4ed8\uff0c\u6323\u529b\u6c42\u9000\u7684\u4efd\uff01 \u3000\u3000\u201c \u3000\u3000\u8001\u592a\u5a46\u5492\u9a82\u7740\uff0c\u7528\u4e00\u79cd\u9648\u5251\u8c01\u5b8c\u5168\u542c\u4e0d\u61c2\u7684\u8bed\u8a00\uff0c\u76f4\u81f3\u8fd9\u5492\u9a82\u53d8\u6210\u4e86\u9ad8\u58f0\u5c16\u53eb\uff0c\u7136\u540e\u9648\u5251\u8c01\u53ea\u89c9\u8eab\u524d\u4e00\u70ed\u3002 \u3000\u3000\u4ed6\u8fde\u5fd9\u95ea\u5f00\uff0c\u4e00\u7741\u773c\uff0c\u5c31\u770b\u89c1\u4e00\u652f\u706b\u628a\u5411\u4ed6\u6254\u6765\u3002 \u3000\u3000\u4ed6\u4f38\u624b\u63a5\u4f4f\u3002 \u3000\u3000\u706b\u718a\u718a\u7684\u71c3\u70e7\u7740\u3002 \u3000\u3000\u90a3\u8001\u592a\u5a46\u5df2\u8fc5\u901f\u7684\u6ca1\u5165\u8349\u4e1b\u91cc\u3002 \u3000\u3000\u2014\u2014\u7136\u800c\u90a3\u706b\u628a\u662f\u4ece\u4e0d\u540c\u7684\u65b9\u5411\u6254\u6765\u7684\u3002 \u3000\u3000\u9648\u5251\u8c01\u7684\u76ee\u5149\u7acb\u5373\u75be\u641c\u5411\u90a3\u201c\u7ea2\u6bdb\u62ff\u7763\u201d\u7684\u65b9\u5411\u3002 \u3000\u3000\u8fd9\u4e00\u77a5\u95f4\uff0c\u4ed6\u5c31\u770b\u5230\u4e00\u4ef6\u4e8b\u7269\u95ea\u8fc7\uff1a \u3000\u3000\u4e00\u6761\u767d\u8272\u7684\u5f71\u5b50\u3002 \u3000\u3000\u2014\u2014\u597d\u50cf\u8fd8\u662f\u4e2a\u5973\u4eba\u3002 \u3000\u3000\u9648\u5251\u8c01\u4e00\u751f\u5192\u9669\uff0c\u66fe\u906d\u53d7\u8fc7\u65e0\u6570\u7684\u4f0f\u51fb\uff0c\u4eca\u665a\u7684\u5077\u88ad\uff0c\u5bf9\u4ed6\u800c\u8a00\uff0c\u4f9d\u6837\u662f\u6709\u60ca\u65e0\u9669\uff0c\u5e76\u4e0d\u7b97\u7279\u522b\u3002 \u3000\u3000\u7279\u522b\u7684\u662f\uff0c\u5728\u653b\u88ad\u4e4b\u65f6\uff0c\u90a3\u4e00\u80a1\u9178\u5473\u3002 \u3000\u3000\u8fd8\u6709\u90a3\u8001\u592a\u5a46\u7684\u8bed\u8a00\u3002 \u3000\u3000\u9648\u5251\u8c01\u7adf\u7136\u542c\u4e0d\u4f46\u5979\u8bf4\u7684\u662f\u4ec0\u4e48\u610f\u601d\uff0c\u53ef\u662f\u8fd8\u53ef\u4ee5\u5206\u8fa8\u3002\u51fa\u6765\uff0c\u90a3\u5e94\u8be5\u662f\u5370\u5ea6\u8bed\u3002 \u3000\u3000\u2014\u4e2a\u7d20\u6627\u5e73\u4e3b\u7684\u5370\u5ea6\u5973\u4eba\u4e3a\u4f55\u4f1a\u5bf9\u4ed6\u4f5c\u51fa\u4ea1\u547d\u7684\u4f0f\u51fb\uff1f \u3000\u3000\u4e00\u4e2a\u5370\u5ea6\u5973\u4eba\u6240\u4f7f\u7684\u7ade\u662f\u8fd1\u4e4e\u4e2d\u56fd\u201c\u756a\u5b50\u9e70\u722a\u529f\u201d\u7684\u51b7\u95e8\u6b66\u529f\uff01 \u3000\u3000\u9648\u5251\u8c01\u6ca1\u673a\u4f1a\u5bfb\u601d\u4e0b\u53bb\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u542c\u5230\u4e00\u79cd\u58f0\u97f3\u3002 \u3000\u3000\u4e0d\u662f\u68b5\u5531\u3002 \u3000\u3000\u5f53\u7136\u4e5f\u4e0d\u518d\u662f\u5d14\u840d\u5531\u7684\u201c\u5f53\u6211\u4eec\u5c0f\u7684\u65f6\u5019\u201d \u3000\u3000\u800c\u662f\u547c\u53eb\u58f0\u3002 \u3000\u3000\u201c\u6551\u547d\u3002\u201d \u3000\u3000\u4e24\u4e2a\u4eba\u7684\u547c\u558a\u58f0\u3002 \u3000\u3000\u4f46\u53eb\u201c\u6551\u547d\u201d\u7684\u662f\u5973\u58f0\uff0c\u90a3\u4e2a\u7537\u7684\uff0c\u53ea\u662f\u5927\u53eb\uff0c\u50cf\u4e00\u5934\u56f0\u517d\u4e00\u822c\u6124\u6012\uff0c\u4f46\u5374\u62b5\u6b7b\u4e0d\u80af\u558a\u51fa\u201c\u6551\u547d\u201d\u4e24\u4e2a\u5b57\u6765\u3002 \u3000\u3000\u9648\u5251\u8c01\u51e0\u4e4e\u4e0d\u7528\u5206\u8fa8\u4fbf\u77e5\u9053\u4ed6\u4eec\u662f\u8c01\u3002 \u3000\u3000\u7136\u540e\u4ed6\u4fbf\u770b\u89c1\u9ed1\u591c\u91cc\u4e00\u79cd\u8fc7\u4efd\u7684\u5149\u8292\u3002 \u3000\u3000\u706b\u5149\u3002 \u3000\u3000\u6108\u70e7\u6108\u70c8\u7684\u706b\u5149\uff0c \u3000\u3000\u5f53\u9648\u5251\u8c01\u51b2\u8fc7\u53bb\u628a\u9a86\u94c3\u548c\u725b\u4e3d\u751f\u6551\u51fa\u6765\u7684\u65f6\u5019\uff0c\u5f88\u9707\u8bb6\u4e24\u540d\u201c\u7231\u5c06\u201d\u7adf\u6210\u4e86\u201c\u778e\u773c\u82cd\u8747\u201d\uff0c\u5728\u706b\u7130\u56db\u9762\u5305\u56f4\u4f46\u672a\u5408\u62e2\u4e71\u6253\u4e71\u61c2\uff0c\u8fde\u8863\u670d\u90e8\u6cbe\u4e86\u51e0\u5904\u706b\u5934\uff0c\u5374\u4e0d\u77e5\u9053\u5982\u4f55\u6253\u7184\u8eab\u4e0a\u7684\u706b\u7130\uff0c\u4e5f\u4e0d\u77e5\u5982\u4f55\u8eb2\u5f00\u706b\u52bf\uff0c\u95ef\u4e00\u6761\u51fa\u8def\u3002 \u3000\u3000\u540c\u6837\u7684\uff0c\u5f53\u6b63\u503c\u60ca\u5f13\u4e4b\u9e1f\u7684\u4e24\u4e2a\u4eba\u7684\u751f\u6b7b\u5b58\u4ea1\u4e4b\u9645\uff0c\u5ffd\u7136\u542c\u5230\u201c\u8001\u5927\u201d\u7684\u58f0\u97f3\uff0c\u800c\u4e14\u4e88\u4ed6\u4eec\u9003\u751f\u7684\u6307\u793a\uff0c\u5c31\u8c61\u5cb8\u4e0a\u7684\u9c7c\u53c8\u91cd\u65b0\u56de\u5230\u6c34\u91cc\uff0c\u65e2\u7136\u201c\u5927\u80a5\u9e2d\u201d\u53ca\u65f6\u8d76\u5230\uff0c\u4ed6\u4eec\u4e00\u5b9a\u6b7b\u4e0d\u6210\u4e86\u3002 \u3000\u3000\u8fd9\u662f\u201c\u4e0d\u5e73\u793e\u201d\u91cc\u7684\u6210\u5458\uff0c\u5bf9\u201c\u5927\u80a5\u9e2d\u201d\u4e00\u8d2f\u4ee5\u6765\u7684\u4fe1\u4efb\u3002 \u3000\u3000\u4e24\u4eba\u7684\u786e\u662f\u201c\u6b7b\u4e0d\u6210\u201d\u3002 \u3000\u3000\u706b\u4ecd\u5728\u70e7\u3002 \u3000\u3000\u2014\u2014\u663e\u7136\u662f\u6709\u4eba\u5728\u56db\u5468\u7684\u5b50\u8305\u8349\u548c\u6854\u6811\u95f4\u70b9\u71c3\u4e86\u706b\u5934\uff0c\u8981\u628a\u9a86\u94c3\u548c\u725b\u4e3d\u751f\u6d3b\u751f\u751f\u70e7\u6b7b\u3002 \u3000\u3000\u725b\u4e3d\u4e3b\u548c\u9a86\u94c3\u5fc3\u5934\u91cc\u7684\u706b\u4e5f\u5728\u70e7\u3002 \u3000\u3000\u2014\u2014\u5982\u8bb8\u6076\u6bd2\uff0c\u8981\u7684\u662f\u4ed6\u4eec\u7684\u547d1 \u3000\u3000\u4ed6\u4eec\u4ecd\u770b\u4e0d\u89c1\u201c\u706b\u201d\u3002 \u3000\u3000\u77a7\u4e0d\u89c1\u706b\u5149\u3002 \u3000\u3000\u201c\u706b\u70e7\u5230\u8fd9\u6837\u5b50\uff0c\u201d\u9648\u5251\u8c01\u5583\u5583\u4ed6\u8bf4\uff1a\u201c\u7ea2\u6bdb\u62ff\u7763\u7684\u4eba\u6ca1\u6709\u7406\u7531\u770b\u4e0d\u5230\uff0c\u770b\u5230\u4e86\u6ca1\u6709\u7406\u7531\u4e0d\u51fa\u6765\u6551\u4eba\uff0c\u5426\u5219\u5f88\u5bb9\u6613\u4f1a\u6b83\u53ca\u6c60\u9c7c\u2026\u2026\u9664\u975e\u4ed6\u4eec\u90fd\u662f\u778e\u5b50\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u2026\u2026\u201d\u9a86\u94c3\u8fd9\u56de\u6297\u58f0\u9053\uff1a\u201c\u6211\u4eec\u90fd\u4e0d\u662f\u778e\u5b50\uff0c\u4e3a\u5565\u6211\u548c\u725b\u4e3d\u751f\u4e5f\u770b\u4e0d\u89c1\u706b\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u4f60\u4eec\u5931\u53bb\u4e86\u77a7\u89c1\u706b\u7684\u80fd\u529b\u3002\u201d\u9664\u5251\u8c01\u8865\u5145\uff0c\u201c\u6211\u6000\u7591\u4f60\u4eec\u73b0\u5728\u4e5f\u6682\u65f6\u5931\u53bb\u5206\u8fa8\u5149\u7684\u89c6\u529b\u3002\u201d \u3000\u3000\u201c\u600e\u4e48\u4f1a\u8fd9\u6837\u5b50\uff1f\u201d \u3000\u3000\u9648\u5251\u8c01\u6ca1\u6709\u56de\u7b54\u3002 \u3000\u3000\u6709\u4e00\u70b9\u9664\u5251\u8c01\u4e09\u4eba\u662f\u5fc3\u77e5\u809a\u660e\u7684\uff1a\u8981\u662f\u7b49\u4ed6\u4eec\u73b0\u5728\u624d\u6765\u6551\u706b\uff0c\u201c\u6211\u4eec\u65e9\u5c31\u53d8\u6210\u4e86\u7ea2\u70e7\u725b\u8089\u4e86\u201d\uff0c\u725b\u4e3d\u751f\u5fff\u5fff\u4ed6\u8bf4\uff0c\u201c\u800c\u4e14\u4e00\u5b9a\u8fd8\u4f1a\u70e7\u5f97\u5f88\u7126\u3002\u201d \u3000\u3000\u201c\u4f60\u624d\u662f\u7ea2\u70e7\u725b\u8089\uff01\u201d\u9a86\u94c3\u5c45\u7136\u8fd8\u6709\u5fc3\u60c5\u4e3a\u8c01\u662f\u725b\u8089\u7684\u4e8b\u201c\u6f84\u6e05\u201d\uff1a\u201c\u6211\u7684\u8089\u4e00\u5b9a\u5f88\u9999\u3002\u201d \u3000\u3000\u201c\u5f88\u9999\u7684\u8089\u5c31\u662f\u9999\u8089\uff0c\u201d\u4e00\u4eba\u642d\u8154\uff0c\u8fd8\u53cd\u95ee\uff0c\u201c\u4f60\u77e5\u9053\u4ec0\u4e48\u662f\u9999\u8089\uff1f\u201d \u3000\u3000\u539f\u6765\u53f2\u6d41\u82b3\u89c1\u51b2\u5929\u706b\u5149\u3001\u4e5f\u8dd1\u51fa\u6765\u6551\u706b\u3002 \u3000\u3000\u201c\u9999\u8089\uff1f\u201d\u9a86\u94c3\u4ee5\u4e3a\u662f\u597d\u4e1c\u897f\uff1a\u201c\u4ec0\u4e48\u9999\u8089\uff1f\u201d \u3000\u3000\u201c\u9999\u8089\u5c31\u662f\u72d7\u8089\u3002\u201d\u53f2\u6d41\u82b3\u7b11\u563b\u563b\u7684\u56de\u7b54\u3002 \u3000\u3000\u8d81\u9a86\u94c3\u8fd8\u672a\u53d1\u4f5c\uff0c\u9648\u5251\u8c01\u9a6c\u4e0a\u95ee\u4e00\u4ef6\u4e8b\uff1a\u201c\u6e29\u6587\u5462\uff1f\u201d \u3000\u3000\u201c\u627e\u7559\u4e0b\u4ed6\u6765\u966a\u5f20\u5c0f\u6101\uff1a\u201d\u53f2\u6d41\u82b3\u6258\u4e86\u6258\u539a\u6846\u773c\u955c\uff0c\u6c89\u5b9e\u91cc\u5c45\u7136\u8fd8\u7ed9\u706b\u5149\u95ea\u6620\u51fa\u4e00\u4e1d\u72e1\u733e\uff0c\u201c\u6211\u600e\u4e48\u77e5\u9053\u8fd9\u628a\u706b\u4f1a\u4e0d\u4f1a\u662f\u58f0\u4e1c\u51fb\u897f\uff0c\u5fd7\u5728\u5c0f\u6101\uff1f\u201d \u3000\u3000\u9648\u5251\u8c01\u89c9\u5f97\u8fd9\u4e2a\u201c\u6210\u5458\u201d\u5df2\u7ecf\u201c\u6210\u719f\u201d\u4e86\u3002 \u3000\u3000\u4ed6\u4e0d\u662f\u89c9\u5f97\u53f2\u6d41\u82b3\u7559\u4e0b\u4eba\u6765\u201c\u4fdd\u62a4\u201d\u5f20\u5c0f\u6101\u6709\u4ec0\u4e48\u7279\u522b\u9ad8\u660e\u4e4b\u5904\uff0c\u800c\u662f\u89c9\u5f97\u4ee5\u53f2\u6d41\u82b3\u5bf9\u5f20\u5c0f\u6101\u767e\u822c\u732e\u6bb7\u52e4\u3001\u5c45\u7136\u80fd\u5728\u91cd\u8981\u5173\u5934\u8981\u4ed6\u7684\u201c\u5bf9\u624b\u201d\u6e29\u6587\u7559\u4e0b\u6765\u770b\u987e\uff0c\u8fd9\u81f3\u5c11\u8981\u6709\u70b9\u201c\u98ce\u5ea6\u201d\u624d\u80fd\u505a\u5230\u3002 \u3000\u3000\u76f4\u81f3\u9648\u5251\u8c01\u5e26\u9a86\u94c3\u548c\u725b\u4e3d\u751f\u65e5\u5230\u5f20\u5bb6\u7684\u65f6\u5019\uff0c\u624d\u660e\u767d\u8fc7\u6765\uff1a\u53f2\u6d41\u82b3\u4e3a\u5565\u4f1a\u90a3\u4e48\u201c\u5927\u65b9\u201d\u3002 \u3000\u3000\u56fa\u4e3a\u5f20\u5bb6\u8001\u7239\u548c\u8001\u5927\u592a\uff0c\u5168\u201c\u5750\u9547\u201d\u5927\u5385\uff0c\u6015\u8fd9\u4e24\u4e2a\u201c\u5916\u6765\u5ba2\u201d\u518d\u628a\u4ed6\u4eec\u7684\u5b9d\u8d1d\u5973\u513f\u552c\u54ed\u4e86\uff0c\u8fd8\u751f\u6015\u4e00\u4e0d\u5c0f\u5fc3\uff0c\u8fd9\u4e9b\u4eba\u8fd8\u4f1a\u628a\u4ed6\u4eec\u4e56\u5973\u513f\u201c\u5543\u4e86\u201d\u3002 \u3000\u3000\u9648\u5251\u8c01\u5631\u725b\u4e3d\u751f\u548c\u9a86\u94c3\u8d76\u5feb\u53bb\u6d17\u8138\u3002 \u3000\u3000\u5c24\u5176\u8981\u6d17\u4e00\u6d17\u773c\u3002 \u3000\u3000\u5f20\u8001\u5148\u751f\u6b63\u5728\u95ee\u53f2\u6d41\u82b3\uff1a\u201c\u706b\u5728\u54ea\u91cc\u70e7\u8d77\u6765\u7684\uff1f\u201d\u53f2\u6d41\u82b3\u8fde\u5fd9\u6bd4\u624b\u5212\u811a\u6f14\u8db3\u5341\u516d\u5927\u672c\u7684\u9648\u8bf4\u4e00\u756a\uff0c\u5f20\u8001\u5148\u751f\u5583\u5583\u5730\u9053\uff1a\u201c\u771f\u90aa\u95e8\uff01\u201d \u3000\u3000\u9648\u5251\u8c01\u89c1\u725b\u4e3d\u751f\u548c\u9a86\u94c3\u62ed\u7740\u773c\u775b\u51fa\u6765\uff0c\u4fbf\u95ee\uff1a\u201c\u521a\u624d\u4f60\u4eec\u770b\u5230\u5c4b\u91cc\u7684\u706f\u706b\u662f\u600e\u6837\u7684\uff1f\u201d \u3000\u3000\u201c\u5f88\u602a\u554a\uff01\u201d\u9a86\u94c3\u504f\u7740\u5934\u8bf4\uff0c\u201c\u9eef\u9eef\u7684\uff0c\u7eff\u7eff\u7684\u3002\u201d \u3000\u3000\u725b\u4e3d\u751f\u629a\u7740\u5fc3\u53e3\uff0c\u8bf4\uff1a\u201c\u597d\u5389\u5bb3\u3002\u201d \u3000\u3000\u9648\u5251\u8c01\u95ee\uff1a\u201c\u4ec0\u4e48\u597d\u5389\u5bb3\uff1f\u201d \u3000\u3000\u201c\u4ed6\u7740\u4e86\u90a3\u59d3\u987e\u7684\u4e00\u6307\uff0c\u4eba\u5bb6\u4e00\u6307\u5c31\u53ef\u628a\u4ed6\u7ed9\u637a\u5012\u4e86\uff0c\u771f\u5dee\u52b2\u3002\u201d\u9a86\u94c3\u8bf4\u8d77\u201c\u65b0\u4ec7\u65e7\u6068\u201d\uff0c\u201c\u8fd8\u8981\u5f85\u672c\u5c0f\u59d0\u53bb\u6276\u8fd9\u53ea\u5927\u7b28\u725b\u56de\u6765\u3002\u201d \u3000\u3000\u201c\u4ed6\u4e5f\u6ca1\u8ba8\u7740\u4fbf\u5b9c\u3002\u201d\u725b\u4e3d\u751f\u4e0d\u7518\u53d7\u5c48\uff0c\u4ed6\u4e5f\u7740\u4e86\u6211\u4e00\u8bb0\u201c\u8001\u725b\u8f70\u62f3\u2019\u3002\u201d \u3000\u3000\u201c\u4f60\u90a3\u4e00\u62f3\u51fb\u4e2d\u4ed6\u4ec0\u4e48\u90e8\u4f4d\uff1f\u201d\u9648\u5251\u8c01\u6df1\u77e5\u725b\u4e3d\u751f\u7684\u62f3\u52b2\uff0c\u6709\u4e9b\u9707\u8bb6\uff0c\u201c\u4ed6\u600e\u4e48\u4e86\uff1f\u201d \u3000\u3000\u201c\u6253\u5728\u8111\u95e8\u4e0a\uff0c\u201d\u725b\u4e3d\u751f\u8bf4\uff0c\u201c\u4e0d\u8fc7\u4ed6\u7adf\u633a\u4e0b\u6765\u4e86\uff0c\u597d\u5389\u5bb3\u3002\u201d \u3000\u3000\u9648\u5251\u8c01\u8fd9\u624d\u6709\u4e9b\u653e\u5fc3\uff0c\u7ee7\u7eed\u4ed6\u521a\u624d\u7684\u95ee\u9898\uff1a\u201c\u600e\u4e48\uff1f\u4f60\u521a\u624d\u8fdb\u5c4b\u91cc\u7684\u65f6\u5019\u770b\u89c1\u706f\u706b\u662f\u4ec0\u4e48\u989c\u8272\u7684\uff1f\u201d \u3000\u3000\u201c\u8ddf\u6811\u53f6\u4e00\u6837\u3002\u201d\u725b\u4e3d\u751f\u50bb\u547c\u547c\u7684\u8bf4\uff0c\u201c\u7eff\u7684\uff01\u201d \u3000\u3000\u201c\u73b0\u5728\u5462\uff1f\u201d \u3000\u3000\u201c\u6696\uff0c\u597d\u50cf\u6bd4\u8f83\u6b63\u5e38\u4e86\u554a\u3002\u201d \u3000\u3000\u201c\u5149\u7684\uff0c\u4eae\u7684\uff0c\u201d\u9a86\u94c3\u5f62\u5bb9\uff0c\u201c\u521a\u624d\u662f\u6ca1\u6709\u4eae\u5149\u7684\u3001\u5e7d\u5f02\u7684\uff01\u201d\u7136\u540e\u5979\u53d1\u73b0\u4e86\u4ec0\u4e48\u4f3c\u7684\u53eb\u4e86\u8d77\u6765\uff0c\u201c\u5947\u602a\uff0c\u4e3a\u4ec0\u4e48\u4f1a\u8fd9\u6837\u5b50\u7684\u5462\uff1f\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u5f20\u8bde\u62ce\u7740\u51e0\u5305\u7092\u7c89\uff0c\u4e00\u8fdb\u95e8\u5c31\u53eb\uff1a\u201c\u597d\u5927\u7684\u706b\uff01\u201d \u3000\u3000\u53f2\u6d41\u82b3\u95ee\uff1a\u201c\u5df2\u6251\u706d\u4e86\u6ca1\u6709\uff1f\u201d \u3000\u3000\u201c\u8fd8\u4e0d\u6e05\u695a\uff0c\u6211\u62c5\u5fc3\u5bb6\u91cc\u6709\u4e8b\uff0c\u5c31\u5148\u8d76\u56de\u6765\u770b\u770b\u3002\u201d\u5f20\u8bde\u624b\u91cc\u7684\u7092\u7c89\u6643\u5440\u6643\u7684\uff0c\u53d1\u51fa\u6d53\u90c1\u7684\u9999\u5473\uff0c\u201c\u4e0d\u8fc7\uff0c\u7ea2\u6bdb\u62ff\u7763\u7684\u4eba\u90fd\u51fa\u6765\u6551\u706b\uff0c\u6709\u4ed6\u4eec\u5728\uff0c\u6ca1\u6709\u6551\u4e0d\u7184\u7684\u706b\u3002\u201d \u3000\u3000\u53f2\u6d41\u82b3\u548c\u9a86\u94c3\u90fd\u5404\u81ea\u53d1\u51fa\u4e00\u58f0\u51b7\u7b11\u3002 \u3000\u3000\u9648\u5251\u8c01\u628a\u5f20\u8bde\u62c9\u5230\u4e00\u65c1\u53bb\uff0c\u5631\u4ed6\uff1a\u201c\u4f60\u53ef\u4e0d\u53ef\u4ee5\u8bf7\u4f2f\u7236\u3001\u4f2f\u6bcd\u4ed6\u4eec\u4e24\u4f4d\u5148\u79bb\u5f00\u4e00\u4e0b\u3002\u201d \u3000\u3000\u5f20\u8bde\u4e00\u6123\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u6211\u6709\u4e8b\u8981\u95ee\u5c0f\u6101\u3002\u5982\u65e0\u610f\u5916\uff0c\u2018\u9ed1\u706b\u4e8b\u4ef6\u2019\u6211\u5df2\u638c\u63e1\u5230\u7ebf\u7d22\u4e86\u2014\u2014\u73b0\u5728\u5c31\u7b49\u4f60\u59b9\u59b9\u518d\u900f\u9732\u4e00\u4e9b\u5b9e\u60c5\uff1a\u201d\u9648\u5251\u8c01\u80f8\u6709\u6210\u7af9\u7684\u8bf4\uff0c\u201c\u53ef\u662f\uff0c\u6709\u4e24\u8001\u5728\uff0c\u6050\u6015\u5bf9\u4f60\u59b9\u59b9\u2026\u2026\u4e0d\u5927\u65b9\u4fbf\u3002\u201d \u3000\u3000\u201c\u597d\uff0c\u6211\u53ef\u4ee5\u8bf7\u5f00\u4e24\u8001\u3002\u201d\u5f20\u8bde\u8bf4\uff0c\u201c\u4e0d\u8fc7\uff0c\u6211\u6709\u4e2a\u6761\u4ef6\u3002\u201d \u3000\u3000\u9648\u5251\u8c01\u6ca1\u6599\u4ed6\u4f1a\u53cd\u63d0\u51fa\u8981\u6c42\u3002 \u3000\u3000\u201c\u6211\u8981\u7559\u5728\u8fd9\u91cc\uff0c\u201d\u5f20\u8bde\u5e0c\u6c42\u7684\u8bf4\uff1a\u201c\u6211\u4e5f\u8981\u542c\u3002\u201d \u3000\u3000\u2014\u2014\u539f\u6765\u4e5f\u662f\u4e2a\u597d\u5947\u7684\u4eba\u3002 \u3000\u3000\u201c\u597d\u3002\u201d\u9648\u5251\u8c01\u7b11\u4e86\uff0c\u201c\u4f60\u2026\u2026\u7528\u4ec0\u4e48\u529e\u6cd5\u5f15\u5f00\u4e24\u4f4d\u8001\u4eba\u5bb6\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u3002\u201d\u5f20\u8bde\u628a\u624b\u91cc\u63d0\u7740\u7684\u51e0\u4e2a\u82ad\u8549\u53f6\u5377\u7740\u7684\u4e09\u89d2\u5305\u5b50\u626c\u4e86\u626c\uff0c\u5c0f\u58f0\u5730\u9053\uff1a\u201c\u4ed6\u4eec\u90fd\u7231\u5403\u3002\u201d\u4ed6\u8bf4\u5f97\u6709\u4e9b\u5f97\u610f\u6d0b\u6d0b\u3002\u201c\u5e74\u7eaa\u5927\u4e86\uff0c\u5c31\u662f\u4ecd\u7231\u5403\u3002\u201d \u3000\u3000\u2014\u2014\u4e70\u51e0\u5305\u7092\u7c89\u56de\u5bb6\u6d88\u591c\uff0c\u4e00\u5bb6\u4eba\u56e2\u56e2\u5750\u70ed\u547c\u547c\u7684\u5403\uff0c\u4e5f\u662f\u5c0f\u4e61\u9547\u91cc\u7684\u5929\u4f26\u4e4b\u4e50\u5427\uff1f \u3000\u3000\u9648\u5251\u8c01\u5ffd\u4e5f\u751f\u8d77\u4e86\u4e00\u79cd\u91cd\u62fe\u5f80\u4e8b\u7684\u60c5\u6000\u3002 \u3000\u3000\uff13\u3001\u5f80\u4e8b\u662f\u4e00\u628a\u4f24\u5fc3\u7684\u5200 \u3000\u3000\u6b64\u9645\uff0c\u9648\u5251\u8c01\u3001\u725b\u4e3d\u751f\u3001\u53f2\u6d41\u82b3\u3001\u9a86\u94c3\u90fd\u805a\u62e2\u5728\u4e00\u8d77\uff0c\u56f4\u62e2\u7740\u5f20\u8bde\u3001\u5f20\u5c0f\u6101\u5144\u59b9\u5750\u7740\u3002 \u3000\u3000\u3001 \u3000\u3000\u9648\u5251\u8c01\u66ff\u725b\u4e3d\u4e3b\u6b21\u524d\u80cc\u540e\u63c9\u4e86\u597d\u4e00\u9635\u5b50\uff0c\u63a8\u5bab\u51fa\u8840\uff0c\u539f\u62df\u66ff\u4ed6\u63a5\u9a6d\u4eec\u9aa8\uff0c\u7ec6\u5bdf\u4e4b\u4e0b\uff0c\u624d\u77e5\u725b\u4e3d\u751f\u7684\u80c1\u9aa8\u5e76\u65e0\u6298\u65ad\uff0c\u53ea\u662f\u5176\u4e2d\u4e00\u6839\u80f8\u9aa8\u51f9\u632b\u4e86\u4e00\u5c0f\u5757\uff0c\u53d7\u5012\u7684\u4f24\u5bb3\u4e5f\u51b3\u4e0d\u7b97\u8f7b\u3002 \u3000\u3000\u5f20\u5c0f\u6101\u4e5f\u66ff\u725b\u4e3d\u751f\u5305\u624e\u4f24\u53e3\uff0c\u5979\u5782\u7740\u5934\uff0c\u89c2\u5bdf\u725b\u4e3d\u751f\u7684\u4f24\u52bf\uff0c\u79c0\u7709\u8e59\u7740\u4e0d\u5fcd\u4e0e\u5173\u6ce8\uff0c\u4eff\u4f5b\u5728\u7737\u725b\u4e3d\u751f\u4ee3\u53d7\u82e6\u695a\u3002 \u3000\u3000\u5012\u662f\u725b\u4e3d\u751f\uff0c\u4fef\u89c6\u5f20\u5c0f\u6101\u7684\u79c0\u80a9\u4e91\u53d1\uff0c\u53ea\u5475\u5475\u5730\u50bb\u7b11\u7740\u3002\u3000\u3000\uff08\u9648\u5251\u8c01\u5ffd\u7136\u8bf4\uff1a\u201c\u5f20\u5c0f\u59d0\uff0c\u4f60\u662f\u4eb2\u773c\u89c1\u8fc7\u9ed1\u706b\u7684\u3002\u201d \u3000\u3000\u5f20\u5c0f\u6101\u54c0\u6c42\u4ed6\u8bf4\uff1a\u201c\u4e0d\u8981\u518d\u63d0\u9ed1\u4eba\u4e86\uff0c\u597d\u5417\uff1f\u201d \u3000\u3000\u9648\u5251\u8c01\u6beb\u4e0d\u52a8\u5bb9\uff1a\u201c\u4e0d\u63d0\u4e0d\u53ef\u4ee5\uff0c\u56e0\u4e3a\u521a\u624d\u70e7\u8d77\u6765\u7684\u6b63\u662f\u9ed1\u706b\uff0c\u8fd8\u5dee\u70b9\u8981\u4e86\u8fd9\u4f4d\u725b\u5148\u751f\u548c\u8fd9\u4f4d\u9a86\u5c0f\u59d0\u7684\u547d\u3002\u201d \u3000\u3000\u5f20\u5c0f\u6101\u84e6\u7136\u4e00\u9707\uff1a\u201c\u4ec0\u4e48\u2026\u2026\u53c8\u662f\u9ed1\u4eba\uff1f\u201d \u3000\u3000\u201c\u6b63\u662f\u9ed1\u4eba\u3002\u8fd9\u9ed1\u706b\u7684\u5a01\u80c1\uff0c\u5e76\u6ca1\u6709\u8fc7\u53bb\uff0c\u800c\u65e6\u8fd8\u8981\u903c\u8fd1\u6765\uff0c\u6240\u4ee5\u6211\u4eec\u4e0d\u4f46\u8981\u63d0\u5b83\uff0c\u800c\u4e14\u8fd8\u8981\u9762\u961f\u5b83\u3001\u89e3\u51b3\u5b83\uff0c\u6251\u706d\u5b83\uff0c\u4e0d\u7136\uff0c\u8fd8\u4e0d\u77e5\u6709\u591a\u5c11\u4eba\u4f1a\u88ab\u5b83\u6240\u5bb3\u3002\u201d \u3000\u3000\u5f20\u5c0f\u6101\u5ffd\u7136\u60f3\u5230\u4e86\u4e00\u4ef6\u4e8b\uff1a\u201c\u53ef\u662f\u2026\u2026\u521a\u624d\u6211\u770b\u5230\u706b\u5149\u2026\u2026\u90a3\u706b\u4e0d\u662f\u9ed1\u8272\u7684\u3002\u201d \u3000\u3000\u201c\u4f60\u90a3\u665a\u770b\u5230\u7684\u9ed1\u706b\uff0c\u81ea\u7136\u662f\u9ed1\u8272\u7684\uff0c\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u5f20\u5c0f\u6101\u70b9\u5934\u3002 \u3000\u3000\u201c\u5176\u5b9e\u9ed1\u4eba\u5c31\u662f\u666e\u901a\u7684\u706b\uff0c\u5b83\u8ddf\u5e73\u5e38\u7684\u706b\u662f\u4e00\u6837\u8272\u6cfd\u7684\u3002\u201d\u9648\u5251\u8c01\u7f13\u7f13\u7684\u8bf4\uff0c\u201c\u9ed1\u706b\u672c\u6765\u4e0d\u662f\u9ed1\u8272\u7684\uff0c\u53ea\u662f\u56e0\u4e3a\u89c1\u5230\u5b83\u7684\u4eba\u5df2\u5931\u53bb\u8fa8\u522b\u5149\u8272\u7684\u80fd\u529b\uff0c\u624d\u4f1a\u88ab\u5b83\u6d3b\u6d3b\u70e7\u6b7b\u3002\u201d \u3000\u3000\u5f20\u5c0f\u6101\u3001\u5f20\u8bde\u3001\u53f2\u6d41\u82b3\u3001\u6e29\u6587\u90fd\u5f88\u5403\u60ca\uff1a\u201c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u201d\u6e29\u6587\u8fd8\u5495\u54e9\u5495\u565c\u7684\u52a0\u4e86\u4e00\u53e5\uff1a \u3000\u3000\u201c\u9ed1\u706b\u4e0d\u9ed1\uff0c\u96be\u9053\u8fd8\u662f\u767d\u8272\u7684\u4e0d\u6210\u3002\u201d \u3000\u3000\u5012\u662f\u725b\u4e3d\u751f\u548c\u9a86\u94c3\uff0c\u56e0\u4e3a\u4eb2\u5386\u8fc7\u8fa8\u522b\u4e0d\u51fa\u706b\u8272\u7684\u60c5\u5f62\uff0c\u4e00\u65f6\u4f3c\u61c2\u975e\u61c2\u3002 \u3000\u3000\u201c\u5f20\u8bde\u5e26\u6211\u4f1a\u89c1\u8fc7\u5fb7\u53d4\u7684\u5bb6\u4eba\uff0c\u6070\u597d\u5f53\u665a\u5fb7\u53d4\u88ab\u70e7\u6b7b\u7684\u65f6\u5019\uff0c\u5496\u5561\u660e\u548c\u7403\u4ed4\u90fd\u5728\u73b0\u573a\u76ee\u7779\uff0c\u6211\u7ec6\u95ee\u4e4b\u4e0b\uff0c\u8bc1\u5b9e\u4e86\u51e0\u4ef6\u4e8b\u3002\u5728\u5496\u5561\u660e\u548c\u7403\u4ed4\u4eb2\u773c\u770b\u89c1\u5fb7\u53d4\u88ab\u4e00\u79cd\u2018\u9ed1\u8272\u7684\u5996\u706b\u2019\u70e7\u6b7b\u4e4b\u524d\uff0c\u5496\u5561\u660e\u66fe\u7528\u4e00\u6761\u8336\u697c\u7684\u6e7f\u6bdb\u5dfe\u62b9\u8fc7\u8138\uff0c\u4e4b\u540e\u773c\u775b\u4e00\u76f4\u611f\u89c9\u4e0d\u5927\u8212\u670d\u3002\u7403\u4ed4\u5219\u5728\u53bb\u4e92\u52a9\u56e2\u5b88\u671b\u5ba4\u7684\u8def\u4e0a\uff0c\u7ed9\u91ce\u5b69\u5b50\u4e00\u4e2a\u98de\u7403\u6253\u4e2d\u8138\u989d\uff0c\u773c\u775b\u4e5f\u523a\u75db\u4e86\u597d\u4e00\u9635\u5b50\u2026\u2026\u800c\u6211\u4eec\u4e5f\u4ece\u4e00\u4e9b\u8857\u574a\u8def\u4eba\u53e3\u91cc\u5f97\u6089\uff1a\u540c\u5728\u4e00\u5929\uff0c\u5fb7\u53d4\u5728\u8def\u4e0a\u4e5f\u662f\u63c9\u7740\u773c\u8d70\u8fc7\uff0c\u5927\u5bb6\u90fd\u8bf4\u4ed6\u4e0d\u662f\u9152\u559d\u591a\u4e86\uff0c\u5c31\u662f\u7ed9\u6c99\u5b50\u5439\u8fdb\u4e86\u773c\u91cc\u3002\u201d \u3000\u3000\u201c\u6211\u5230\u73b0\u5728\u8fd8\u6ca1\u6709\u53bb\u8fc7\u963f\u8482\u7684\u5bb6\uff0c\u4e0d\u77e5\u9053\u771f\u5b9e\u7684\u60c5\u5f62\u662f\u600e\u4e48\u4e2a\u6837\u5b50\u2026\u4e0d\u8fc7\uff0c\u4eca\u5929\u4e0b\u5348\u5728\u5fb7\u53d4\u5bb6\u91cc\u6709\u4e00\u4e2a\u5973\u80f6\u5de5\u6765\u540a\u5501\uff0c\u5979\u4e5f\u662f\u521a\u4ece\u963f\u8482\u5bb6\u6170\u95ee\u8fc7\u6765\u7684\uff0c\u4e5f\u63d0\u5230\u963f\u8482\u5728\u51fa\u4e8b\u524d\u51e0\u5929\uff0c\u8001\u662f\u5411\u5979\u63d0\u5230\uff0c\u89c9\u5f97\u597d\u50cf\u6709\u4eba\u5728\u76d1\u89c6\u3001\u7aa5\u4f3a\u5979\u4e00\u5305\u62ec\u6d17\u6fa1\u7684\u65f6\u5019\u3002\u5979\u8fd8\u63d0\u5230\uff0c\u90a3\u5929\u65e9\u4e0a\uff0c\u5979\u662f\u8ddf\u963f\u8482\u4e00\u8d77\u9a91\u811a\u8e0f\u8f66\u53bb\u80f6\u56ed\u7684\uff0c\u5979\u89c9\u5f97\u963f\u8482\u53cc\u773c\u7ea2\u80bf\uff0c\u597d\u50cf\u54ed\u8fc7\u4f3c\u7684\uff0c\u4e0d\u8fc7\u56e0\u4e3a\u5929\u8272\u672a\u4eae\uff0c\u770b\u4e0d\u6e05\u695a\uff0c\u5979\u4e5f\u4e0d\u597d\u610f\u601d\u95ee\u963f\u8482\u3002\u201d \u3000\u3000\u201c\u53e6\u5916\uff0c\u6211\u4eec\u5728\u8c03\u67e5\u8fd9\u4ef6\u6848\u5b50\u7684\u65f6\u5019\uff0c\u4e00\u76f4\u53d7\u5230\u8bef\u5bfc\uff1a\u663e\u7136\u6709\u4eba\u4e0d\u5e0c\u671b\u6211\u4eec\u518d\u8c03\u67e5\u4e0b\u53bb\u3002\u5c31\u5728\u521a\u624d\uff0c\u4ed6\u4eec\u5bf9\u6211\u4eec\u4e24\u4f4d\u540c\u4f34\u4f5c\u51fa\u653b\u51fb\uff0c\u51e0\u4e4e\u5c31\u8981\u4e86\u4ed6\u4eec\u7684\u547d\uff0c\u6240\u7528\u7684\u6280\u4fe9\u4e5f\u5c31\u662f\u9ed1\u706b\uff0c\u90a3\u5176\u5b9e\u53ea\u662f\u4e00\u628a\u666e\u901a\u7684\u706b\uff0c\u53ea\u662f\u706b\u4e00\u65e6\u8089\u773c\u770b\u4e0d\u89c1\uff0c\u90a3\u5c31\u53d8\u6210\u5f88\u5177\u6740\u4f24\u529b\u7684\u4e1c\u897f\u4e86\uff1b\u201d \u3000\u3000\u9664\u5251\u8c01\u7528\u4e00\u79cd\u80af\u5b9a\u800c\u6709\u529b\u7684\u8bed\u8c03\uff0c\u51b3\u610f\u4e0d\u4f46\u8981\u603b\u7ed3\u81ea\u5df1\u7684\u4fa6\u5bdf\u548c\u63a8\u7406\u7684\u6210\u679c\uff0c\u8fd8\u8981\u535e\u963f\u6210\u7684\u74e6\u89e3\u5f20\u5c0f\u6101\u7684\u81ea\u536b\u9632\u7ebf\uff0c\u6240\u4ee5\u4ed6\u4e0d\u5f90\u4e0d\u75be\u7684\u8bdd\u5c31\u4f3c\u5dee\u7fd8\u5343\u4e07\u96c4\u5175\u5175\u4e34\u57ce\u4e0b\u7684\u8981\u5bf9\u65b9\u6ca1\u6709\u4e00\u4e1d\u6297\u4e89\u7684\u4f59\u5730\uff0c\u201c\u8c61\u5c71\u5d16\uff0c\u5b83\u672c\u8eab\u662f\u4e0d\u4f1a\u52a8\u7684\uff0c\u6ca1\u6709\u4fb5\u7565\u6027\u7684\uff0c\u4f46\u5982\u679c\u4f60\u770b\u4e0d\u89c1\u5b83\u7684\u5b58\u5728\uff0c\u4fbf\u5f80\u5f80\u8dcc\u5f97\u7c89\u8eab\u788e\u9aa8\u800c\u4e0d\u660e\u6240\u4ee5\u4e86\u3002\u53c8\u5982\u4f60\u5728\u8fc7\u9a6c\u8def\u7684\u65f6\u5019\u77a7\u4e0d\u89c1\u8f66\u5b50\uff0c\u90a3\u4e48\uff0c\u5728\u90a3\u65f6\u5019\uff0c\u8f66\u5b50\u5c31\u8981\u53d8\u6210\u6740\u4eba\u7684\u6b66\u5668\u4e86\u3002\u6211\u6000\u7591\u64cd\u7eb5\u9ed1\u706b\u7684\u4eba\uff0c\u6000\u6709\u4e00\u79cd\u6781\u5927\u7684\u76ee\u7684\u4e0e\u91ce\u5fc3\uff0c\u624d\u6765\u8fdb\u884c\u6740\u4eba\u4e8b\u4ef6\u3002\u8981\u628a\u4e00\u4e2a\u4eba\u6d3b\u6d3b\u70e7\u6b7b\uff0c\u800c\u53d8\u6210\u90aa\u6076\u8087\u4e34\u7684\u4f20\u8bf4\uff0c\u60f3\u540c\u65f6\u8fbe\u5230\u8fd9\u4e24\u6837\u6548\u679c\uff1b\u4ed6\u4eec\u53ea\u8981\u4f7f\u53d7\u5bb3\u8005\u4e00\u65f6\u770b\u4e0d\u5230\u6216\u5206\u8fa8\u4e0d\u51fa\uff1a\u4ec0\u4e48\u662f\u706b\uff01\u53ea\u8981\u89c1\u4e0d\u5230\u706b\u5149\uff0c\u5c31\u4e0d\u6613\u9001\u5f00\u706b\u7684\u88ad\u51fb\u3002\u5fb7\u53d4\u3001\u7403\u4ed4\u3001\u5496\u5561\u660e\uff0c\u8fd8\u6709\u963f\u8482\uff0c\u5728\u9047\u7978\u524d\u773c\u775b\u5747\u6709\u4e0d\u9002\uff0c\u4ee4\u6211\u751f\u7591\u3002\u800c\u4e14\uff0c\u521a\u624d\u6709\u4eba\u5411\u6211\u548c\u9a86\u94c3\u3001\u963f\u725b\u4f5c\u8fc7\u72d9\u51fb\uff0c\u6211\u4eec\u9f3b\u91cc\u90fd\u95fb\u8fc7\u4e00\u9635\u9178\u5473\uff0c\u5c0f\u9a86\u548c\u963f\u725b\u56e0\u4e0d\u50cf\u6211\u65e9\u6709\u6240\u95fb\uff0c\u65e9\u6709\u9632\u5907\uff0c\u6240\u4ee5\u4e4b\u540e\u773c\u775b\u4fbf\u611f\u5230\u6da9\u6da9\u7684\uff0c\u89c6\u529b\u4e5f\u4e0d\u6b63\u5e38\u8d77\u6765\u4e86\u3002\u6211\u60f3\uff0c\u8fd9\u4fbf\u662f\u9ed1\u706b\u5e55\u540e\u64cd\u7eb5\u8005\u7684\u969c\u773c\u6cd5\u3002\u201d \u3000\u3000\u4ed6\u201c\u56fe\u7a77\u5315\u73b0\u201d\u4e5f\u4e49\u6b63\u8f9e\u4e25\u7684\u5bf9\u5f20\u5c0f\u6101\u8bf4\uff1a\u201c\u6240\u4ee5\uff0c\u5047\u5982\u4f60\u8fd8\u6709\u771f\u76f8\u6ca1\u544a\u8bc9\u6211\u4eec\uff0c\u73b0\u5728\uff0c\u4f60\u4e00\u5b9a\u8981\u8bf4\uff0c\u800c\u4e14\uff0c\u4e5f\u5230\u4e86\u4e0d\u80fd\u4e0d\u8bf4\u7684\u65f6\u5019\u4e86\u3002\u6211\u4eec\u521d\u5230\u8d35\u5883\uff0c\u4e0e\u4eba\u65e0\u5ffd\u65e0\u4ec7\uff0c\u4e5f\u906d\u72d9\u51fb\uff0c\u4ed6\u4eec\u76ee\u7684\u4e0d\u4ec5\u662f\u4e88\u6211\u4eec\u8b66\u8bda\u4e00\u751a\u81f3\u6740\u4eba\u706d\u53e3\uff0c\u4ed6\u4eec\u4e3a\u4ec0\u4e48\u8981\u8fd9\u6837\u505a\uff1f\u5f88\u7b80\u5355\uff0c\u56e0\u4e3a\u77e5\u9053\u6211\u4eec\u5df2\u627e\u4e0a\u4e86\u4f60\u3001\u4ed6\u4eec\u8981\u5bf9\u4ed8\u6211\u4eec\u96be\u9053\u5c31\u4f1a\u653e\u8fc7\u4f60\u5417\uff1f\u8fd9\u4e2a\u95ee\u9898\u5df2\u662f\u591a\u4f59\u7684\u4e86\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u5b8c\u4e86\u8fd9\u756a\u8bdd\uff0c\u5c31\u9759\u9759\u7684\u7b49\u5f20\u5c0f\u6101\u5f00\u53e3\u3002\u4ed6\u4e0d\u518d\u8bf4\u8bdd\uff0c\u4e5f\u5e76\u4e0d\u7126\u6025\uff0c\u4eff\u4f5b\u4ed6\u77e5\u9053\u5f20\u5c0f\u6101\u4e00\u5b9a\u4f1a\u8bf4\u8bdd\uff0c\u800c\u4e14\u4e00\u5b9a\u4f1a\u8bf4\u51fa\u4ed6\u60f3\u542c\u7684\u8bdd\u3002 \u3000\u3000\u201c\u6211\u60f3\u8d77\u6765\u4e86\u3002\u201d\u5f20\u5c0f\u6101\u50cf\u5728\u81ea\u5df1\u8111\u6d77\u91cc\u6293\u4f4f\u4e86\u4e00\u6761\u7ebf\uff0c\u7136\u540e\u6e10\u6e10\u53d1\u73b0\u8fd9\u6761\u7ebf\u80fd\u4f7f\u4ed6\u6500\u4e0a\u5929\u7684\u5c3d\u5934\uff1a\u201c\u6211\u66fe\u5728\u8f66\u5b50\u7184\u706b\u7684\u65f6\u5019\uff0c\u66fe\u7528\u624b\u5e15\u66ff\u56db\u5e78\u63e9\u6c57\uff0c\u2026\u2026\u4e4b\u540e\u4ed6\u7684\u773c\u775b\u5c31\u6709\u70b9\u53d1\u7ea2\uff0c\u4e00\u76f4\u5728\u7728\uff0c\u53ef\u662f\u4ed6\u81ea\u5df1\u4f3c\u4e0d\u81ea\u77e5\u3002\u201d \u3000\u3000\u201c\u54e6\u3002\u8fd8\u6709\u5462\u2026\u3002\u201d \u3000\u3000\u201c\u8fd8\u6709\u2026\u2026\u201d\u5f20\u5c0f\u6101\u5ffd\u7136\u628a\u5fc3\u4e00\u6a2a\uff0c\u90fd\u8c41\u51fa\u53bb\u4e86\uff0c\u867d\u7136\u8bb0\u5fc6\u4f3c\u662f\u4e00\u628a\u4f24\u4eba\u7684\u5200\uff0c\u4f46\u5979\u5df2\u88ab\u5b83\u4f24\u591f\u4e86\uff0c\u5979\u51b3\u5b9a\u8981\u53cd\u8fc7\u6765\u53bb\u9762\u5bf9\u8fd9\u4e9b\u4f24\uff0c\u751a\u81f3\u7529\u5c3d\u5e73\u751f\u4e4b\u529b\u53bb\u6298\u65ad\u8fd9\u4e00\u628a\u6cbe\u8840\u7684\u5200\uff0c\u201c\u597d\uff0c\u6211\u544a\u8bc9\u4f60\u4eec\uff0c\u90a3\u5929\u665a\u4e0a\u3001\u56db\u5e78\u4ed6\u5f3a\u66b4\u4e86\u6211\uff01\u201d \u3000\u3000\u201c\u4ec0\u4e48\uff01\u201d\u8fde\u5f20\u8bde\u4e5f\u53eb\u4e86\u8d77\u6765\uff1a\u201c\u8fd9\u755c\u751f\uff01\u201d \u3000\u3000\u201c\u5012\u5e95\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d\u9648\u5251\u8c01\u7d27\u63a5\u7740\u95ee\u3002\u53f2\u6d41\u82b3\u548c\u6e29\u6587\u90fd\u5728\u9762\u9762\u76f8\u89d1\u7740\u6012\u6124\u4e0e\u7591\u60d1\u3002 \u3000\u3000\u201c\u662f\u7684\uff0c\u201d\u5f20\u5c0f\u6101\u7684\u773c\u7738\u660e\u4eae\u5982\u9ed1\u8272\u7684\u73e0\u5b9d\uff0c\u66f4\u663e\u5f97\u4e00\u5f20\u7f8e\u8138\u82cd\u767d\u5982\u7389\uff0c\u201c\u90a3\u767d\u8272\u7684\u5973\u4eba\u5189\u5189\u98d8\u8fc7\u53bb\u4e4b\u540e\uff0c\u56db\u5e78\u4fbf\u8ffd\u4e86\u8fc7\u53bb\u2026\u2026\u6211\u4e00\u76f4\u90fd\u5728\u7b49\u4ed6\uff0c\u591c\u5f88\u9ed1\uff0c\u4ed6\u3001\u76f4\u90fd\u6ca1\u6709\u56de\u6765\u2026\uff0c\u7136\u540e\u662f\u4e24\u9053\u5f3a\u5149\uff0c\u523a\u4eba\u6211\u7684\u773c\u5e18\u2026\u2026\u201d \u3000\u3000\u201c\u5f3a\u5149\uff1f\u201d\u9648\u5251\u8c01\u8c61\u6293\u4f4f\u4e86\u4e00\u4e2a\u5267\u76d7\u4e00\u822c\u7684\u95ee\uff1a\u201c\u4ec0\u4e48\u5f3a\u5149\uff1f\u662f\u4ec0\u4e48\u4e1c\u897f\u53d1\u51fa\u6765\u7684\u5f3a\u5149\uff1f\u201d \u3000\u3000\u201c\u2026\u2026\u6211\u4e5f\u4e0d\u77e5\u9053\uff0c\u8fd9\u5149\u7ebf\u5b9e\u5728\u592a\u523a\u76ee\uff0c\u4ee4\u6211\u7528\u624b\u906e\u4f4f\u4e86\u773c\u775b\u3002\u5f85\u518d\u79fb\u5f00\u53cc\u624b\u7684\u65f6\u5019\uff0c\u6211\u5c31\u770b\u89c1\u2026\u2026\u201d \u3000\u3000\u201c\u5fb7\u53d4\u548c\u963f\u8482\uff1f\u201d\u6e7f\u6587\u5fcd\u4e0d\u4f4f\u53eb\u7834\u3002 \u3000\u3000\u201c\u4e0d\u662f\u4ed6\u4eec\uff01\u201d\u5f20\u5c0f\u6101\u63a9\u7740\u8138\u53eb\u4e86\u8d77\u6765\uff0c\u62bd\u6ce3\u7740\uff0c\u7136\u540e\u5728\u6307\u7f1d\u91cc\u8bf4\uff1a\u201c\u56db\u5e78\u5df2\u7ecf\u56de\u6765\uff0c\u4ed6\u5c31\u50cf\u4e00\u53ea\u91ce\u517d\uff0c\u62ab\u5934\u6563\u53d1\uff0c\u628a\u6211\u62d6\u51fa\u8f66\u5916\uff0c\u4ed6\u5b8c\u5168\u4e0d\u7406\u6211\u7684\u62d2\u6297\uff0c\u53ea\u75af\u72c2\u7684\u2026\u2026\u4ed6\u5f3a\u66b4\u4e86\u6211\u2026\u2026\u7136\u540e\uff0c\u4ed6\u7a81\u7136\u7ad9\u8d77\u6765\uff0c\u72c2\u7b11\u7740\u3001\u72c2\u5954\u7740\uff0c\u5c31\u50cf\u4e00\u4e2a\u5403\u4e86\u70b8\u836f\u7684\u91ce\u4eba\u4f3c\u7684\uff0c\u8dd1\u8fdb\u9ed1\u6697\u91cc\uff0c\u624d\u90a3\u4e48\u4e00\u4e0b\u5b50\uff0c\u53c8\u568e\u53eb\u7740\u6325\u52a8\u7740\u6323\u624e\u70ae\u4e86\u51fa\u6765\u2026\u2026\u4ed6\u6b63\u7ed9\u90a3\u2018\u9ed1\u706b\u2019\u7f20\u70e7\u7740\uff0c\u800c\u5fb7\u53d4\u548c\u963f\u8482\u7684\u9b3c\u9b42\uff0c\u4e5f\u5728\u90a3\u65f6\u5019\u51fa\u73b0\uff0c\u5c31\u5728\u540e\u5934\u8ffd\u9010\u7740\u4ed6\uff0c\u6211\u4e5f\u662f\u5728\u90a3\u65f6\u5019\uff0c\u624d\u6655\u4e86\u8fc7\u53bb\u3002\u3002\u3002\u201d \u3000\u3000\u201c\u5929\uff01\u201d\u9a86\u94c3\u7981\u4e0d\u4f4f\u601c\u501f\u5730\u629a\u6170\u7740\u4f0f\u5728\u684c\u4e0a\u75db\u54ed\u7684\u5f20\u5c0f\u6101\u3002\u5979\u4e00\u65f6\u5b8c\u5168\u627e\u4e0d\u5230\u9002\u5f53\u7684\u8a00\u8bcd\uff0c\u53ea\u80fd\u4e00\u518d\u91cd\u590d\u8fd9\u4e00\u53e5\uff1a\u201c\u5929\uff01\u201d \u3000\u3000\u53f2\u6d41\u82b3\u548c\u6e29\u6587\u90fd\u8138\u8272\u94c1\u9752\u3002 \u3000\u3000\u201c\u56db\u5e78\u4ed6\u51b3\u4e0d\u662f\u8fd9\u79cd\u4eba\uff01\u201d\u9648\u5251\u8c01\u80cc\u8d1f\u53cc\u624b\uff0c\u6162\u6162\u8e31\u5230\u9636\u524d\uff0c\u6c89\u91cd\u5f97\u50cf\u80cc\u8d1f\u4e86\u6574\u4e2a\u591c\u8272\uff0c\u4f46\u4ecd\u7136\u6bd4\u706f\u5149\u8fd8\u6e05\u695a\u660e\u4eae\u7684\u8bf4\uff0c\u201c\u4f60\u4eec\u90fd\u66fe\u56e0\u63e9\u6c57\u3001\u906e\u53bb\u5f3a\u5149\u800c\u7528\u624b\u5e15\u6216\u62ff\u8fc7\u624b\u5e15\u7684\u624b\u53bb\u63a5\u89e6\u8fc7\u773c\u775b\u2026\u2026\u65e2\u7136\u9ed1\u706b\u662f\u5047\u7684\uff0c\u53d1\u751f\u5728\u56db\u5e78\u548c\u4f60\u8eab\u4e0a\u7684\u4e8b\uff0c\u4e5f\u4e0d\u4e00\u5b9a\u662f\u771f\u7684\uff0c\u6709\u4eba\u8981\u5728\u6740\u5bb3\u56db\u5e78\u524d\uff0c\u8fd8\u8981\u6bc1\u6389\u4ed6\u5728\u4f60\u5fc3\u76ee\u4e2d\u7684\u5f62\u8c61\u4e0e\u597d\u611f\uff0c\u8fd9\u70b9\u662f\u663e\u89c1\u7684\u3002\u201d \u3000\u3000\u5f20\u5c0f\u6101\u60b2\u621a\u5730\u6d41\u7740\u6cea\u3002\u56e0\u4e3a\u788d\u4e8e\u5979\u7684\u7236\u6bcd\u4ecd\u5728\u996d\u5385\uff0c\u5979\u5f3a\u5fcd\u4f4f\u54ed\u58f0\uff0c\u53cd\u800c\u6210\u4e3a\u4e00\u79cd\u6b32\u54ed\u4e0d\u80fd\u7684\u60b2\u6ce3\u3002\u4f46\u9648\u5251\u8c01\u7684\u5206\u6790\u66f4\u5e26\u5979\u8fdb\u5165\u4e86\u4e00\u4e2a\u5979\u66f4\u4e0d\u80fd\u63a5\u53d7\u548c\u5fcd\u53d7\u7684\u4e16\u754c\u4e0e\u4e8b\u5b9e\u91cc\u3002 \u3000\u3000\u201c\u65e2\u7136\u6709\u8fd9\u6837\u5b50\u53ef\u6015\u7684\u4e8b\uff0c\u8fd9\u6837\u90aa\u5f02\u7684\u624b\u6bb5\uff0c\u8fd9\u6837\u6b79\u6bd2\u7684\u4eba\u2026\u2026\u201d\u9648\u5251\u8c01\u671b\u7740\u6d53\u5f97\u5316\u4e0d\u5f00\u50cf\u56fa\u4f53\u4e00\u822c\u6709\u529b\u800c\u53c8\u4f3c\u6db2\u4f53\u4e00\u822c\u6d41\u7a9c\u7684\u591c\u8272\uff0c\u4e00\u5b57\u4e00\u53e5\u7684\u8bf4\uff1a\u201c\u6211\u4eec\u5c31\u4e14\u8ddf\u4ed6\u4eec\u6765\u6bd4\u4e2a\u4e0d\u6b7b\u4e0d\u6563\u5427\uff01\u201d"}),showOnHomepage:!0}]}}]);