"use strict";(self.webpackChunkwebsite=self.webpackChunkwebsite||[]).push([[11023],{41888:(e,n,s)=>{s.r(n),s.d(n,{default:()=>a});var t=s(74848);const a=[{name:"\u585e\u5916\u5947\u4fa0\u4f20",content:(0,t.jsx)(t.Fragment,{children:"\u7b2c\u4e00\u56de\u3000\u5e08\u5144\u5f1f\u6c99\u6f20\u5947\u9022 \u3000\u3000\u73ab\u7470\u82b1\u5f00\u8c61\u4e91\u971e\uff0c \u3000\u3000\u679c\u5b50\u6bd4\u7897\u8fd8\u8981\u5927\uff0c \u3000\u3000\u55fd\u5566\u2014\u2014 \u3000\u3000\u5ba2\u4eba\u5440\uff0c\u4f60\u7684\u53e3\u513f\u5e72\u4e86\u5427\uff1f \u3000\u3000\u8bf7\u4e0b\u4f60\u7684\u9a6c\uff0c\u8fd9\u91cc\u6709\u751c\u751c\u7684\u54c8\u871c\u74dc \u3000\u3000\u6b4c\u58f0\u6742\u7740\u9a7c\u94c3\uff0c\u98d8\u8361\u5728\u9ec4\u6c99\u6f20\u6f20\u7684\u7a7a\u9645\u3002\u51e0\u5339\u9a86\u9a7c\uff0c\u62d6\u7740\u6c89\u91cd\u7684\u6b65\u4f10\uff0c\u5728\u5854\u514b\u62c9\u9a6c\u5e72\u7684 \u5927\u6c99\u6f20\u4e0a\u884c\u8d70\u3002\u4e00\u4e2a\u54c8\u8428\u514b\u9752\u5e74\u5f15\u542d\u9ad8\u6b4c\uff0c\u6b4c\u58f0\u65b9\u6b47\uff0c\u9a7c\u80cc\u4e0a\u53e6\u4e00\u4e2a\u9752\u5e74\u7b11\u7740\u9a82\u9053\uff1a\u201c\u4f0a\u58eb \u8fbe\uff0c\u6ca1\u6709\u628a\u4f60\u6e34\u6b7b\u5440\uff1f\u5531\u8fd9\u6837\u7684\u6b4c\uff0c\u6211\u7ed9\u4f60\u5531\u5f97\u5589\u5499\u90fd\u7126\u5566\uff01\u201d \u3000\u3000\u4f0a\u58eb\u8fbe\u4e5f\u7b11\u7740\u7b54\u9053\uff0c\u201c\u4e8f\u4f60\u548c\u6211\u4eec\u4f4f\u4e86\u8fd9\u4e48\u591a\u5e74\uff0c\u8fd8\u4e0d\u61c2\u5f97\u54c8\u8428\u514b\u4eba\uff0c\u6211\u4eec\u54c8\u8428\u514b\u4eba \u5440\uff0c\u5728\u6700\u82e6\u7684\u65f6\u5019\uff0c\u4e5f\u7b11\u5f97\u51fa\u6765\uff01\u201d \u3000\u3000\u53e6\u4e00\u4e2a\u54c8\u8428\u514b\u9752\u5e74\u63d2\u53e3\u8bf4\u9053\uff1a\u201c\u4f0a\u58eb\u8fbe\uff0c\u4f60\u8bf4\u5f97\u597d\u3002\u53ea\u662f\uff0c\u4f60\u5531\u7684\u6b4c\u672a\u514d\u592a\u4e0d\u5bf9\u666f\u5566\uff01 \u4f60\u770b\u524d\u9762\u5c3d\u662f\u5927\u5927\u5c0f\u5c0f\u7684\u6c99\u4e18\uff0c\u627e\u4e00\u70b9\u6c34\u90fd\u96be\uff0c\u4f60\u5440\uff0c\u5728\u8fd9\u4e2a\u9b3c\u5730\u65b9\uff0c\u5374\u63d0\u8d77\u4ec0\u4e48\u54c8\u871c\u74dc\uff0c \u4f60\u8fd9\u4e0d\u662f\u6210\u5fc3\u5455\u4eba\u5417\uff1f\u201d \u3000\u3000\u4f0a\u58eb\u8fbe\u5ffd\u7136\u88c5\u51fa\u751f\u6c14\u7684\u6837\u5b50\uff0c\u9a82\u4ed6\u9053\uff1a\u201c\u9ea6\u76d6\u63d0\u5440\uff0c\u4f60\u5c45\u7136\u8bf4\u6211\u4eec\u7684\u5730\u65b9\u662f\u9b3c\u5730\u65b9\uff1f\u4f60 \u5728\u8349\u539f\u4e0a\u51fa\u751f\uff0c\u5728\u8349\u539f\u4e0a\u957f\u5927\uff0c\u8db3\u8ff9\u8e0f\u904d\u5929\u5c71\u5357\u5317\uff0c\u96be\u9053\u6211\u8fd8\u4e0d\u77e5\u9053\u6211\u4eec\u8349\u539f\u4e0a\u6709\u591a\u597d\u591a\u7f8e \u7684\u4e1c\u897f\u3002\u8fd9\u91cc\u662f\u6c99\u6f20\uff0c\u6211\u7ed9\u4f60\u6570\u6570\u770b\uff1a\u90a3\u50cf\u5b54\u96c0\u4e00\u6837\u7fe0\u84dd\u7684\u5b54\u96c0\u679c\uff0c\u6cb3\u8fb9\u4e24\u5cb8\u5bb6\u5bb6\u6237\u6237\u68a8\u56ed \u91cc\u538b\u5f2f\u4e86\u6811\u679d\u7684\u68a8\u5b50\uff1b\u751c\u5f97\u50cf\u9a6c\u5976\u6837\u7684\u5410\u9c81\u756a\u8461\u8404\uff1b\u963f\u514b\u82cf\u3001\u5580\u4ec0\u7684\u6843\u548c\u674f\uff1b\u8fd8\u6709\u4e00\u63d0\u8d77\u5c31 \u8ba9\u4f60\u6d41\u6d8e\u7684\u54c8\u871c\u74dc\uff0c\u54ea\u4e00\u6837\u4e0d\u662f\u597d\u4e1c\u897f\uff1f\u54fc\uff0c\u74dc\u679c\u8fd8\u7b97\u4e0d\u4e86\u4ec0\u4e48\u5462\uff0c\u6211\u4eec\u8fd8\u6709\u767d\u4e91\u4f3c\u7684\u7f8a \u7fa4\uff0c\u62d6\u7740\u957f\u8fab\u5b50\u7684\u5927\u5730\u4e0a\u6700\u7f8e\u7684\u59d1\u5a18\u3002\u554a\uff01\u9ea6\u76d6\u63d0\uff0c\u8d70\u8fc7\u8fd9\u4e2a\u6c99\u6f20\uff0c\u6211\u966a\u4f60\u53bb\u627e\u4f60\u90a3\u7f8e\u4e3d\u53ef \u7231\u7684\u7267\u7f8a\u59d1\u5a18\u3002\u201d \u3000\u3000\u9ea6\u76d6\u63d0\u6602\u5934\u8bf4\u9053\uff1a\u201c\u4f60\u522b\u6570\u5566\uff0c\u8981\u6570\u6211\u4eec\u7684\u597d\u4e1c\u897f\u5440\uff0c\u4e00\u5929\u4e5f\u6570\u4e0d\u5b8c\uff1b\u6211\u4eec\u8fd8\u6709\u963f\u5c14\u6cf0 \u5c71\u5728\u9633\u5149\u95ea\u8000\u4e0b\u7684\u91d1\u5b50\uff1b\u6606\u4ed1\u5c71\u6d41\u4e0b\u7684\u7389\u6cb3\uff0c\u5728\u5ca9\u77f3\u4e0a\u5c31\u9576\u7740\u77f3\u69b4\u4e00\u6837\u7ea2\u548c\u767e\u5408\u82b1\u4e00\u6837\u767d\u7684 \u5b9d\u77f3\uff0c\u4f7f\u6d41\u6c34\u90fd\u53d8\u5f97\u6591\u70c2\u3002\u53ea\u662f\u8fd9\u4e9b\u4e1c\u897f\u90fd\u5feb\u8981\u7ed9\u6ee1\u6d32\u631e\u5b50\u62ff\u53bb\u5566\uff01\u201d \u3000\u3000\u5f00\u5934\u8d23\u5907\u4f0a\u58eb\u8fbe\u7684\u6c49\u65cf\u9752\u5e74\u63a5\u58f0\u8bf4\u9053\uff1a\u201c\u6240\u4ee5\u6211\u4eec\u8981\u628a\u4ed6\u62ff\u56de\u6765\u3002\u9ea6\u76d6\u63d0\uff0c\u4f60\u522b\u7b11\u6211\u60f3 \u5f97\u592a\u602a\uff0c\u6211\u8fd8\u60f3\u603b\u6709\u4e00\u5929\uff0c\u6211\u4eec\u4f1a\u628a\u5929\u5c71\u7684\u96ea\u6c34\u5f15\u5230\u8fd9\u4e2a\u6c99\u6f20\uff0c\u90a3\u65f6\u5440\uff0c\u6211\u4eec\u4e0d\u4f46\u4fdd\u4f4f\u6240\u6709 \u7684\u597d\u4e1c\u897f\uff0c\u6211\u4eec\u8fd8\u4f1a\u6dfb\u51fa\u8bb8\u591a\u65b0\u7684\u597d\u4e1c\u897f\u6765\uff01\u4f60\u7684\u7267\u7f8a\u59d1\u5a18\u518d\u4e5f\u4e0d\u6015\u9ec4\u6c99\u541e\u4e0b\u5979\u7684\u7f8a\u7fa4\uff0c\u4e00 \u5b9a\u4f1a\u7b11\u5f97\u66f4\u7f8e\u4e3d\u66f4\u53ef\u7231\uff01\u201d \u3000\u3000\u4f0a\u58eb\u8fbe\u4e00\u4e0b\u8df3\u5230\u90a3\u4e2a\u6c49\u65cf\u9752\u5e74\u7684\u9a86\u9a7c\u4e0a\uff0c\u62b1\u7740\u4ed6\u9053\uff1a\u201c\u6768\u5927\u4fa0\uff0c\u4f60\u7684\u5fc3\u6bd4\u6211\u4eec\u6700\u597d\u7684\u5b9d \u77f3\u8fd8\u8981\u597d\u4e0a\u4e07\u500d\uff0c\u4f60\u662f\u6c49\u4eba\uff0c\u53ef\u5c31\u50cf\u6211\u4eec\u54c8\u8428\u514b\u65cf\u7684\u5144\u5f1f\u4e00\u6837\uff0c\u4e0d\uff0c\u7b80\u76f4\u8981\u6bd4\u5144\u5f1f\u8fd8\u8981\u4eb2\uff01 \u4f60\u5e2e\u6211\u4eec\u6253\u4e86\u8fd9\u4e48\u591a\u5e74\u4ed7\uff0c\u73b0\u5728\u8fd8\u7d2f\u4f60\u966a\u6211\u4eec\u8d70\u8fd9\u4e2a\u5927\u6c99\u6f20\u3002\u5440\uff0c\u6211\u771f\u613f\u610f\u4eb2\u4eb2\u4f60\u3002\u201d \u3000\u3000\u88ab\u79f0\u505a\u6768\u5927\u4fa0\u7684\u5e26\u7b11\u65a5\u8d23\u4ed6\u9053\uff1a\u201c\u522b\u80e1\u95f9\uff01\u6211\u662f\u9886\u961f\uff0c\u6211\u8981\u4e0b\u547d\u4ee4\u5566\uff0c\u5927\u5bb6\u4e0d\u51c6\u591a\u8bf4 \u8bdd\u3002\u73b0\u5728\u8d8a\u6765\u8d8a\u70ed\uff0c\u6211\u4eec\u6c34\u56ca\u91cc\u7684\u6c34\u4e0d\u591a\u5566\u3002\u8bf4\u5f97\u53e3\u5e72\u4e86\uff0c\u53c8\u8981\u591a\u559d\u6c34\uff0c\u90a3\u53ef\u4e0d\u6210\u5440\uff01\u201d\u4f0a \u58eb\u8fbe\u4f38\u4f38\u820c\u5934\uff0c\u8df3\u56de\u81ea\u5df1\u7684\u9a86\u9a7c\uff0c\u54cd\u52a8\u76ae\u97ad\uff0c\u50cf\u4e00\u4e2a\u987d\u76ae\u7684\u5b69\u5b50\u4f3c\u7684\u4f0f\u5728\u9a7c\u5cf0\u4e0a\u505a\u9b3c\u8138\u3002 \u3000\u3000\u8fd9\u4f4d\u88ab\u54c8\u8428\u514b\u65cf\u4eba\u79f0\u4e3a\u6768\u5927\u4fa0\u7684\uff0c\u540d\u53eb\u6768\u4e91\u806a\uff0c\u662f\u5929\u5c71\u4e0a\u6666\u660e\u63b8\u5e08\u7684\u5927\u5f1f\u5b50\u3002\u6666\u660e\u7985 \u5e08\uff0c\u4e0d\u77e5\u662f\u4ec0\u4e48\u65f6\u5019\u4ece\u4e2d\u571f\u6765\u7684\uff0c\u4ed6\u9690\u5c45\u5929\u5c71\u4e4b\u5dc5\uff0c\u7cbe\u7814\u5251\u6cd5\uff0c\u91c7\u96c6\u4e86\u5404\u5bb6\u5404\u6d3e\u4e4b\u957f\uff0c\u72ec\u521b \u4e86\u4e00\u767e\u56db\u5341\u516b\u624b\u5929\u5c71\u5251\u6cd5\uff0c\u5f8a\u73af\u8fde\u7528\uff0c\u5947\u5999\u65e0\u7a77\u3002\u6768\u4e91\u806a\u7236\u4eb2\u662f\u660e\u4ee3\u5fe0\u81e3\u4e4b\u540e\uff0c\u4e3a\u907f\u201c\u9609 \u7978\u201d\uff08\u660e\u80b2\u5b97\u65f6\uff0c\u592a\u76d1\u9b4f\u5fe0\u8d24\u5b88\u653f\uff0c\u79f0\u4e3a\u9609\u7978\u3002\uff09\u9003\u5230\u65b0\u7586\uff0c\u5f97\u4eba\u6307\u70b9\uff0c\u5c06\u513f\u5b50\u9001\u7ed9\u6666\u660e\u7985 \u5e08\u4e3a\u5f92\u3002\u4ece\u516b\u5c81\u4eba\u5c81\uff0c\u4e00\u5171\u5b66\u4e86\u5341\u5e74\uff0c\u5df2\u5c3d\u5f97\u5929\u5c71\u5251\u6cd5\u7cbe\u9ad3\u3002 \u3000\u3000\u5341\u516b\u5c81\u90a3\u5e74\uff0c\u6768\u4e91\u806a\u5f00\u59cb\u4e0b\u5c71\uff0c\u5728\u5929\u5c71\u5357\u5317\u8def\uff0c\u6e38\u4fa0\u4ed7\u4e49\uff0c\u9504\u5f3a\u6276\u5f31\uff0c\u548c\u7267\u6c11\u4eec\u6210\u4e3a\u597d \u53cb\uff0c\u90a3\u65f6\u6b63\u662f\u987a\u6cbb\u5165\u5173\u540e\u7684\u7b2c\u4e03\u5e74\uff0c\u5927\u5c40\u5df2\u5b9a\uff0c\u6e05\u5ef7\u5f00\u59cb\u4fb5\u7565\u897f\u5317\uff0c\u65b0\u7586\u5404\u65cf\uff0c\u7eb7\u8d77\u4f5c\u6218\u3002 \u6768\u4e91\u806a\u8fdb\u4eba\u54c8\u8428\u514b\u519b\u4e2d\uff0c\u5e2e\u52a9\u4ed6\u4eec\u62b5\u6297\u6e05\u5175\u3002\u6253\u4e86\u516d\u5e74\uff0c\u7ec8\u56e0\u5be1\u4e0d\u654c\u4f17\uff0c\u81ea\u65b0\u7586\u4e2d\u90e8\u4e00\u76f4\u9000 \u81f3\u5357\u7586\uff0c\u88ab\u8feb\u8fdb\u4e86\u5854\u514b\u62c9\u9a6c\u5e72\u5927\u6c99\u6f20\u3002\u5404\u90e8\u5206\u6210\u4e86\u96f6\u661f\u5c0f\u80a1\uff0c\u56db\u5904\u9003\u6563\u3002\u6768\u4e91\u806a\u8fd9\u4e00\u80a1\u53ea\u6709 \u516b\u4e2a\u4eba\uff0c\u5408\u4e58\u56db\u5339\u9a86\u9a7c\u3002\u4f0a\u58eb\u8fbe\u548c\u9ea6\u76d6\u63d0\u662f\u54c8\u8428\u514b\u65cf\u4e24\u4e2a\u51fa\u540d\u7684\u5e74\u8f7b\u6b66\u58eb\uff0c\u4e5f\u5728\u8fd9\u5c0f\u80a1\u4e4b \u4e2d\u3002\u8fd9\u4e24\u4e2a\u4eba\u5929\u6027\u4e50\u89c2\uff0c\u867d\u5728\u5371\u96be\u4e4b\u4e2d\uff0c\u5374\u575a\u4fe1\u54c8\u8428\u514b\u65cf\u4e00\u5b9a\u4e0d\u4f1a\u957f\u4e45\u53d7\u4eba\u6b3a\u8d1f\u3002\u4ed6\u4eec\u867d\u7231 \u8bf4\u7b11\uff0c\u53ef\u4e5f\u6fc0\u8d77\u540c\u884c\u8005\u75b2\u4e4f\u7684\u7cbe\u795e\u3002 \u3000\u3000\u6f20\u6f20\u9ec4\u6c99\uff0c\u65e0\u8fb9\u65e0\u9645\u3002\u4ed6\u4eec\u5728\u5927\u6c99\u6f20\u91cc\u884c\u4e86\u591a\u5929\uff0c\u8fd8\u662f\u672a\u5230\u4eba\u5bb6\uff0c\u6c34\u56ca\u91cc\u7684\u6c34\u4e5f\u8d8a\u6765\u8d8a \u5c11\u3002\u9633\u5149\u5c04\u5728\u9ec4\u6c99\u4e0a\uff0c\u70eb\u5f97\u9a86\u9a7c\u4e5f\u76f4\u5598\u6c14\u3002\u5e78\u597d\u5230\u4e86\u508d\u665a\uff0c\u5929\u6c14\u5c31\u6e10\u6e10\u51c9\u5feb\u4e0b\u6765\u3002\u6768\u4e91\u806a\u627e \u4e86\u9053\u5c0f\u6c9f\uff0c\u6c9f\u5e95\u5df2\u7ecf\u9f9f\u88c2\u3002\u6768\u4e91\u806a\u7528\u624b\u5f80\u4e0b\u63d2\u4e86\u51e0\u63d2\uff0c\u62e8\u5f00\u6ce5\u571f\uff0c\u6293\u8d77\u4e00\u628a\u6ce5\u6c99\uff0c\u770b\u4e86\u4e00 \u770b\uff0c\u8bf4\u9053\uff1a\u201c\u4eca\u665a\u6211\u4eec\u5c31\u5bbf\u8425\u5728\u8fd9\u5730\u65b9\u3002\u201d \u3000\u3000\u67b6\u597d\u5e10\u5e55\u4e4b\u540e\uff0c\u5927\u5bb6\u559d\u4e86\u51e0\u53e3\u6c34\uff0c\u9001\u4e0b\u5e72\u7cae\uff0c\u6768\u4e91\u806a\u9053\uff1a\u201c\u8fd9\u5c0f\u6c9f\u7684\u6ce5\u571f\u867d\u7136\u5e72\u71e5\uff0c\u4f46 \u5374\u53ef\u80fd\u662f\u4e2a\u6c34\u6e90\uff0c\u4f0a\u58eb\u8fbe\u548c\u9ea6\u76d6\u63d0\uff0c\u8f9b\u82e6\u4f60\u4eec\u4e00\u8d9f\uff0c\u4ece\u8fd9\u6761\u5c0f\u6c9f\u8d70\u4e0b\u53bb\uff0c\u627e\u627e\u90a3\u91cc\u6709\u6ca1\u6709\u6c34 \u6e90\u3002\u201d \u3000\u3000\u5728\u6c99\u6f20\u91cc\u627e\u6c34\u6e90\uff0c\u53ef\u5f97\u6709\u5f88\u4e30\u5bcc\u7684\u7ecf\u9a8c\uff0c\u8981\u4e0d\u7136\uff0c\u5230\u5904\u4e71\u6398\u627e\u6c34\uff0c\u90a3\u53ef\u662f\u767d\u8d39\u529b\u6c14\u3002\u4f0a \u58eb\u8fbe\u548c\u9ea6\u76d6\u63d0\u719f\u6089\u6c99\u6f20\uff0c\u5c31\u5982\u719f\u6089\u5f97\u5728\u81ea\u5df1\u7684\u5bb6\u4e00\u6837\u3002\u53eb\u4ed6\u4eec\u53bb\u627e\u6c34\uff0c\u6768\u4e91\u806a\u81ea\u7136\u53ef\u4ee5\u653e\u5fc3 \u4e86\u3002 \u3000\u3000\u6c99\u6f20\u6c14\u5019\u53d8\u5316\u5f88\u5927\uff0c\u4e2d\u5348\u9177\u70ed\uff0c\u665a\u4e0a\u5374\u5bd2\u51b7\u8d77\u6765\u3002\u6768\u4e91\u806a\u8bb8\u4e45\uff0c\u5c1a\u672a\u89c1\u4e8c\u4eba\u56de\u6765\uff0c\u731b\u7136 \u60f3\u8d77\uff0c\u8fd9\u4e24\u4e2a\u4eba\u5306\u5306\u51fa\u53bb\uff0c\u8eab\u4e0a\u8fd8\u662f\u7a7f\u7740\u5355\u886b\uff0c\u867d\u7136\u4ed6\u4eec\u6709\u4e00\u8eab\u6b66\u529f\uff0c\u4e5f\u6015\u4ed6\u4eec\u62b5\u5fa1\u4e0d\u4f4f\u3002 \u6768\u4e91\u806a\u62ff\u8d77\u4e24\u4ef6\u8001\u7f8a\u76ae\u8884\uff0c\u6b65\u51fa\u5e10\u5e55\uff0c\u6b63\u60f3\u53eb\u5524\uff0c\u5ffd\u7136\u542c\u5f97\u4f0a\u58eb\u8fbe\u53e3\u54e8\u4e4b\u58f0\uff0c\u6025\u5fd9\u8d76\u53bb\uff0c\u53ea \u89c1\u5bd2\u661f\u51b7\u6708\u4e4b\u4e0b\uff0c\u4ed6\u4eec\u548c\u4e00\u4e2a\u6c49\u65cf\u9752\u5e74\u6253\u7684\uff0b\u5206\u6fc0\u70c8\u3002\u4e24\u4eba\u8fde\u8fde\u9000\u540e\uff0c\u663e\u89c1\u4e0d\u652f\u3002\u800c\u90a3\u4e2a\u6c49 \u65cf\u9752\u5e74\u80cc\u540e\u5f71\u5f71\u7ef0\u7ef0\u7684\u597d\u50cf\u8fd8\u6709\u5341\u6765\u4e2a\u4eba\u3002 \u3000\u3000\u6768\u4e91\u806a\u5927\u5403\u4e00\u60ca\uff0c\u8fd9\u4e24\u4e2a\u4eba\u6b66\u529f\uff0c\u5728\u54c8\u8428\u514b\u65cf\u4e2d\u6570\u4e00\u6570\u4e8c\uff0c\u90a3\u548c\u4ed6\u5bf9\u654c\u7684\u4e00\u5b9a\u662f\u6b66\u6797\u9ad8 \u624b\u4e86\u3002\u4ed6\u672a\u5e26\u5175\u5668\uff0c\u4e00\u8dc3\u800c\u4e0a\uff0c\u4e24\u624b\u62a1\u5f00\u4e24\u4ef6\u8001\u7f8a\u76ae\u8884\uff0c\u5411\u90a3\u4eba\u5f53\u5934\u7f69\u4e0b\uff0c\u90a3\u4eba\u5251\u6cd5\u597d\u4e0d\u8fc5 \u6377\uff0c\u4e00\u4e2a\u56de\u8eab\u62d7\u6b65\uff0c\u5251\u950b\u5df2\u907f\u8fc7\u6768\u4e91\u806a\u7684\u201c\u94c1\u5e03\u886b\u201d\u62db\u6570\uff0c\u76f4\u523a\u8fc7\u6765\u3002\u6768\u4e91\u806a\u201c\u566b\u201d\u4e86\u4e00 \u58f0\uff0c\u4e24\u4ef6\u76ae\u8884\u5de6\u53f3\u4e00\u5377\uff0c\u75be\u4f3c\u98d8\u98ce\uff0c\u53ea\u542c\u5f97\u201c\u55e4\u201d\u7684\u4e00\u58f0\uff0c\u76ae\u8884\u7ed9\u6495\u7834\u4e00\u5757\uff0c\u800c\u90a3\u4eba\u7684\u5251\u4e5f \u7ed9\u593a\u4e86\u51fa\u624b\u3002\u6768\u4e91\u806a\u53eb\u9053\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u695a\u662d\u5357\u5e08\u5f1f\uff1f\u201d\u90a3\u4eba\u6ee1\u9762\u901a\u7ea2\uff0c\u5728\u5730\u4e0a\u62fe\u8d77\u5b9d\u5251\uff0c\u8fc8 \u524d\u4e00\u6b65\uff0c\u770b\u6e05\u695a\u540e\uff0c\u6025\u5fd9\u884c\u793c\uff0c\u8bf4\u9053\uff1a\u201c\u554a\uff0c\u600e\u4e48\u6768\u5e08\u5144\u6765\u5230\u6b64\u5730\uff01\u201d \u3000\u3000\u695a\u662d\u5357\u662f\u4e00\u4e2a\u5b64\u513f\uff0c\u540e\u6768\u4e91\u806a\u4e09\u5e74\u4e0a\u5c71\uff0c\u662f\u6666\u660e\u7985\u5e08\u7684\u7b2c\u4e8c\u4e2a\u5f92\u5f1f\u3002\u6768\u4e91\u806a\u4e0b\u5c71\u4e4b\u540e\u7684 \u7b2c\u4e09\u5e74\uff0c\u4ed6\u4e5f\u5b66\u6ee1\u4e86\u5341\u5e74\uff0c\u4e0b\u5c71\u884c\u4fa0\uff0c\u5230\u73b0\u5728\u4e5f\u6709\u4e09\u5e74\u4e86\u3002 \u3000\u3000\u6768\u4e91\u806a\u516d\u5e74\u672a\u89c1\u5e08\u5f1f\uff0c\u6b64\u9645\u5ffd\u5728\u6c99\u6f20\u76f8\u9022\uff0c\u5fc3\u4e2d\u5927\u559c\uff0c\u4e00\u628a\u62c9\u7740\u695a\u662d\u5357\u9053\uff1a\u201c\u5e08\u5f1f\uff0c\u4f60 \u51e0\u65f6\u4e0b\u5c71\u7684\uff1f\u4e5f\u4e0d\u544a\u8bc9\u6211\u4e00\u58f0\u3002\u5e08\u5f1f\uff0c\u51e0\u5e74\u4e0d\u89c1\uff0c\u4f60\u7684\u6b66\u529f\u5927\u8fdb\u4e86\u3002\u5c45\u7136\u80fd\u628a\u6211\u7684\u8001\u7f8a\u76ae\u8884 \u4e5f\u6495\u7834\u4e00\u5757\u3002\u54c8\uff0c\u54c8\uff01\u201d\u4ed6\u5374\u4e0d\u77e5\u695a\u662d\u5357\u4f7f\u7684\u662f\u4e00\u628a\u5b9d\u5251\uff0c\u540d\u5524\u6e38\u9f99\u5251\uff0c\u548c\u81ea\u5df1\u6240\u4f7f\u7684\u65ad\u7389 \u5251\u4e00\u6837\uff0c\u540c\u662f\u6666\u660e\u7985\u5e08\u6240\u4f20\u7684\u5b9d\u7269\uff0c\u695a\u662d\u5357\u624b\u4f7f\u5b9d\u5251\uff0c\u53ea\u4e24\u62db\u5c31\u88ab\u5e08\u5144\u593a\u51fa\u4e86\u624b\uff0c\u975e\u5e38\u5c34 \u5c2c\u3002\u6768\u4e91\u806a\u70ed\u70c8\u62db\u547c\uff0c\u4ed6\u5374\u662f\u6709\u4e00\u53e5\u6ca1\u4e00\u53e5\u3002\u6768\u4e91\u806a\u9053\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u548c\u90a3\u4e9b\u4eba\u4e00\u540c\u6765\u7684\uff0c\u4eca \u665a\u548c\u6211\u4eec\u4f4f\u5728\u4e00\u8d77\u7f62\u3002\u201d\u695a\u662d\u5357\u9053\uff1a\u201c\u6211\u4eec\u6709\u8981\u7d27\u4e8b\uff0c\u8981\u8fde\u591c\u8d76\u8def\uff0c\u5f80\u5317\u8fb9\u53bb\uff0c\u6211\u4eec\u53ea\u662f\u60f3 \u8981\u4e00\u70b9\u6c34\u3002\u201d\u6768\u4e91\u806a\u9053\uff1a\u201c\u4f60\u4eec\u6ca1\u6c34\u5566\uff1f\u201d\u695a\u662d\u5357\u70b9\u4e86\u70b9\u5934\u3002\u4f0a\u58eb\u8fbe\u4e0a\u524d\u62c9\u7740\u6768\u4e91\u806a\uff0c\u7528\u54c8 \u8428\u514b\u8bdd\u8bf4\u9053\uff1a\u201c\u4f60\u8fd9\u5e08\u5f1f\u597d\u6ca1\u9053\u7406\uff0c\u6211\u4eec\u8f9b\u8f9b\u82e6\u82e6\u6398\u51fa\u4e86\u6c34\u6e90\uff0c\u4ed6\u8dd1\u8fc7\u6765\u8981\u72ec\u5360\u3002\u770b\u4f60\u7684\u9762 \u4e0a\uff0c\u8981\u4e0d\u7136\u6211\u4eec\u771f\u4e0d\u7ed9\u4ed6\uff01\u201d\u6768\u4e91\u806a\u542c\u540e\uff0c\u5f88\u4e0d\u81ea\u7136\uff0c\u770b\u4e86\u695a\u662d\u5357\u4e00\u773c\uff0c\u5fc3\u60f3\uff1a\u201c\u600e\u7684\u4ed6\u53d8 \u6210\u8fd9\u6837\u7684\u4eba\uff1f\u201d\u672c\u60f3\u8bad\u4ed6\u4e00\u987f\uff0c\u53ea\u662f\u4e45\u522b\u91cd\u9022\uff0c\u53c8\u517c\u548c\u4ed6\u6765\u7684\u4eba\u4e5f\u5df2\u77e5\u9053\uff0c\u4e0d\u60f3\u4ee4\u4ed6\u5f53\u4f17\u4e22 \u8138\uff0c\u8bf4\u9053\uff1a\u201c\u65e2\u7136\u6398\u51fa\u4e86\u6c34\u6e90\uff0c\u5c31\u5927\u5bb6\u5206\u4eab\u5427\u3002\u201d\u95ee\u4f0a\u58eb\u8fbe\u9053\uff1a\u201c\u6c34\u6e90\u5728\u54ea\u91cc\uff1f\u201d\u4f0a\u58eb\u8fbe\u4e00 \u6307\uff0c\u53ea\u89c1\u5728\u6c9f\u8fb9\uff0c\u6c34\u4e00\u6ef4\u4e00\u6ef4\u7684\u6d41\u4e0b\u6765\u3002\u9ea6\u76d6\u63d0\u8fd9\u65f6\u6b63\u62ff\u7740\u4e00\u4e2a\u5927\u76ae\u888b\u5728\u76db\u6c34\u3002 \u3000\u3000\u6768\u4e91\u806a\u8fc7\u53bb\uff0c\u5e76\u6307\u4e00\u6233\uff0c\u7528\u201c\u94c1\u6307\u7985\u201d\u529f\u592b\u628a\u5ca9\u77f3\u63d2\u5f00\uff0c\u6c34\u6d41\u4f3c\u6cc9\u822c\u7684\u5c04\u51fa\u6765\u3002\u5373\u662f\u8fd9 \u6837\uff0c\u4e5f\u5b88\u5230\u534a\u591c\u624d\u88c5\u6ee1\u516d\u4e2a\u76ae\u888b\u3002\u8fd9\u65f6\uff0c\u6c34\u5df2\u6ca1\u6709\u4e86\u3002\u5728\u88c5\u6c34\u65f6\uff0c\u5e10\u5e55\u4e2d\u5176\u4ed6\u4e94\u4e2a\u4eba\u4e5f\u90fd\u51fa \u6765\u95ee\u957f\u95ee\u77ed\u3002\u6768\u4e91\u806a\u5728\u8fd9\u65f6\u95f4\u4e2d\uff0c\u7adf\u6ca1\u6709\u4ec0\u4e48\u673a\u4f1a\u548c\u5e08\u5f1f\u8bf4\u8bdd\uff0c\u5c31\u662f\u548c\u4ed6\u8bf4\uff0c\u4ed6\u4e5f\u662f\u652f\u543e\u4ee5 \u5bf9\uff0c\u95ee\u4e0d\u51fa\u4ec0\u4e48\u6765\u3002\u4ed6\u53ea\u662f\u8bf4\u5230\u4e86\u4e00\u4e9b\u65f6\u5019\uff0c\u60f3\u627e\u5e08\u5144\uff0c\u53ef\u6ca1\u6709\u627e\u7740\u3002\u5012\u662f\u6768\u4e91\u806a\u5f88\u8be6\u7ec6\u5730 \u544a\u8bc9\u4e86\u4ed6\u8fd9\u51e0\u5e74\u7684\u7ecf\u5386\u3002\u695a\u662d\u5357\u975e\u5e38\u7528\u5fc3\u5730\u542c\uff0c\u800c\u4e14\u8fd8\u4e0d\u65f6\u53d1\u95ee\u3002 \u3000\u3000\u6768\u4e91\u806a\u4e00\u770b\u6c34\u6e90\u5df2\u6db8\uff0c\u5fae\u5fae\u7b11\u9053\uff0c\u201c\u603b\u7b97\u4e0d\u9519\uff0c\u5c45\u7136\u6709\u516d\u888b\uff0c\u597d\uff0c\u5e08\u5f1f\uff0c\u4f60\u4eec\u90a3\u8fb9\u6709\u5341 \u4e8c\u4e2a\u4eba\uff0c\u4f46\u4f60\u4eec\u5317\u53bb\uff0c\u8def\u7a0b\u4e5f\u8fdc\uff0c\u5c31\u5206\u7ed9\u4f60\u56db\u888b\u5427\uff0c\u4f60\u770b\u516c\u4e0d\u516c\u5e73\u3002\u201d\u695a\u662d\u5357\u8fde\u58f0\u9053\u8c22\uff0c\u53eb \u4eba\u62ff\u8d77\u6c34\u888b\uff0c\u56de\u5230\u4ed6\u4eec\u7684\u5e10\u5e55\uff0c\u88c5\u4e0a\u9a86\u9a7c\uff0c\u8fde\u591c\u4fbf\u8d70\u3002\u6768\u4e91\u806a\u95ee\u4ed6\u6709\u4ec0\u4e48\u8981\u7d27\u4e8b\uff0c\u4ed6\u603b\u4e0d\u80af \u8bf4\u3002\u6768\u4e91\u806a\u4ee5\u4e3a\u4ed6\u7684\u4e8b\u548c\u4ed6\u540c\u884c\u7684\u4eba\u6709\u5173\uff0c\u4e5f\u4e0d\u4fbf\u518d\u95ee\u3002 \u3000\u3000\u6768\u4e91\u806a\u522b\u8fc7\u695a\u662d\u5357\u540e\uff0c\u53c8\u8d70\u4e86\u4e09\u592d\uff0c\u5c1a\u672a\u8d70\u51fa\u6c99\u6f20\u3002\u4f0a\u58eb\u8fbe\u9053\uff1a\u201c\u5e78\u597d\u8fd9\u4e48\u591a\u5929\u6765\u90fd\u6ca1 \u6709\u522e\u5927\u98ce\uff0c\u8981\u4e0d\u7136\u4e00\u573a\u5927\u98ce\uff0c\u5c31\u7b97\u6ca1\u4e8b\uff0c\u4f46\u6c99\u4e18\u6539\u5f62\uff0c\u4e5f\u4f1a\u8ff7\u8def\u3002\u201d\u8bdd\u8fd8\u672a\u4e86\uff0c\u5ffd\u7136\u4e00\u9635\u9635 \u98ce\u5439\u6765\uff0c\u9ec4\u7070\u8272\u7684\u6c99\u96fe\u5411\u4e1c\u65b9\u98d8\u53bb\u3002\u6768\u4e91\u806a\u9053\uff1a\u201c\u5e78\u597d\u662f\u5fae\u98ce\u3002\u201d\u4f0a\u58eb\u8fbe\u9053\uff1a\u201c\u4e5f\u4e0d\u80fd\u4e0d\u9632 \u5907\u3002\u201d\u6768\u4e91\u806a\u6b63\u60f3\u627e\u5730\u65b9\u9489\u597d\u5e10\u5e55\uff0c\u4e00\u62ac\u5934\uff0c\u5ffd\u7136\u8fdc\u5904\u9a7c\u94c3\u53ee\u5f53\uff0c\u8fd8\u6709\u9a6c\u5636\u4e4b\u58f0\uff0c\u6768\u4e91\u806a \u9053\uff1a\u201c\u5947\u602a\uff0c\u597d\u50cf\u6709\u51e0\u5341\u4e2a\u4eba\uff0c\u53c8\u4e0d\u662f\u4e70\u5356\u5b63\u8282\uff0c\u54ea\u91cc\u6765\u7684\u8fd9\u4e48\u591a\u4eba\uff1f\u201d\u7b49\u4e86\u4e00\u4f1a\uff0c\u90a3\u7fa4\u9a86 \u9a7c\u961f\u5df2\u8d70\u4e86\u8fd1\u6765\uff0c\u524d\u9762\u8fd8\u6709\u4e24\u5339\u8499\u53e4\u9a7c\u7fa4\u3002\u9a6c\u4e0a\u7684\u4eba\u4e00\u4e2a\u7adf\u662f\u81ea\u5df1\u7684\u5e08\u5f1f\u695a\u662d\u5357\uff0c\u53e6\u4e00\u4e2a\u5374 \u662f\u4e2a\u52b2\u88c5\u88c5\u675f\u7684\u6c49\u5b50\u3002\u9a7c\u80cc\u4e0a\u90a3\u4e9b\u4eba\u8fd9\u65f6\u4e5f\u90fd\u8df3\u4e86\u4e0b\u6765\uff0c\u6c49\u4eba\u6ee1\u4eba\u90fd\u6709\uff0c\u4e2a\u4e2a\u624b\u91cc\u62ff\u7740\u5175\u5668\u3002 \u3000\u3000\u6768\u4e91\u806a\u66ae\u7136\u4e00\u60ca\uff0c\u4e0a\u524d\u559d\u9053\uff1a\u201c\u5e08\u5f1f\uff0c\u4f60\u53c8\u8d70\u56de\u6765\u5e72\u5417\uff1f\u201d\u695a\u662d\u5357\u9762\u8272\u4e00\u6c89\uff0c\u6307\u7740\u6768\u4e91 \u806a\u5bf9\u90a3\u4e2a\u6ee1\u4eba\u8bf4\u9053\uff1a\u201c\u4ed6\u5c31\u662f\u9886\u7740\u54c8\u8428\u514b\u53db\u4e71\u7684\u6768\u4e91\u806a\uff01\u201d\u90a3\u6ee1\u4eba\u628a\u624b\u4e00\u62db\uff0c\u51e0\u5341\u4e2a\u7cbe\u58ee\u6c49 \u5b50\u500f\u5730\u51b2\u4e86\u8fc7\u6765\uff0c\u628a\u6768\u4e91\u806a\u7b49\u516b\u4e2a\u4eba\u56f4\u5728\u6838\u5fc3\u3002 \u3000\u3000------------------ \u3000\u3000 \u626c\u5251\u8f69\u5c45\u58eb \u626b\u63cf\u6821\u5bf9  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u585e\u5916\u5947\u4fa0\u4f20\u300b\u2014\u2014\u7b2c\u4e8c\u56de\u3000\u52ab\u540e\u5ffd\u9022\u5947\u5973\u5b50 \u6881\u7fbd\u751f\u300a\u585e\u5916\u5947\u4fa0\u4f20\u300b \u7b2c\u4e8c\u56de\u3000\u52ab\u540e\u5ffd\u9022\u5947\u5973\u5b50 \u3000\u3000\u8fd9\u5239\u90a3\u95f4\uff0c\u6768\u4e91\u806a\u53c8\u60ca\u53c8\u6012\u3002\u4ed6\u60ca\u6050\u7684\u4e0d\u662f\u81ea\u5df1\u751f\u547d\u7684\u5371\u9669\uff0c\u800c\u662f\u5173\u5fc3\u540c\u884c\u7684\u54c8\u8428\u514b \u4eba\u3002\u4ed6\u81ea\u4fe1\u4ee5\u4ed6\u7cbe\u5999\u7684\u5251\u672f\uff0c\u95ef\u51fa\u8fd9\u5341\u4eba\u7684\u5305\u56f4\uff0c\u5c1a\u975e\u96be\u4e8b\u3002\u4f55\u51b5\u4ed6\u51e0\u5e74\u6765\u51fa\u751f\u5165\u6b7b\uff0c\u65e9\u5df2 \u5c06\u751f\u547d\u7f6e\u4e4b\u5ea6\u5916\u4e86\u3002\u53ef\u662f\u4ed6\u5374\u4e0d\u80fd\u4e0d\u4e3a\u540c\u884c\u7684\u4f19\u4f34\u62c5\u7740\u5fc3\uff0c\u4ed6\u4eec\u90fd\u662f\u54c8\u8428\u514b\u65cf\u6700\u4f18\u79c0\u7684\u9752 \u5e74\uff0c\u654c\u4f17\u6211\u5be1\uff0c\u82e5\u7136\u6298\u635f\u5728\u8fd9\u4e00\u671b\u65e0\u9645\u7684\u5927\u6c99\u6f20\u4e2d\uff0c\u90a3\u53ef\u6bd4\u635f\u5931\u4e00\u767e\u4e2a\u7f8a\u7fa4\u8fd8\u60e8\u91cd\u3002\u4ed6\u60ca \u6050\uff0c\u4ed6\u6124\u6012\uff0c\u4ed6\u6124\u6012\u7684\u662f\u81ea\u5df1\u5e08\u5f1f\u695a\u662d\u5357\uff0c\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u6b63\u662f\u6709\u4e3a\u4e4b\u65f6\uff0c\u5fc3\u7075\u5374\u50cf\u8150\u70c2\u7684\u82f9 \u679c\uff0c\u4ed6\u5c45\u7136\u53d8\u8282\u6295\u964d\uff0c\u7ed9\u654c\u4eba\u5e26\u8def\uff0c\u8981\u7528\u81ea\u5df1\u7684\u9c9c\u8840\u67d3\u7ea2\u4ed6\u7684\u9876\u5b50\u3002 \u3000\u3000\u7136\u800c\u8fd9\u4e5f\u53ea\u662f\u4e00\u5239\u90a3\u95f4\u4e4b\u4e8b\uff0c\u60ca\u6050\u4e0e\u6124\u6012\u7684\u60c5\u7eea\uff0c\u7535\u5149\u77f3\u706b\u822c\u5728\u4ed6\u8111\u5b50\u91cc\u4e00\u95ea\u800c\u8fc7\u3002\u65f6 \u95f4\u4e0d\u5bb9\u8bb8\u4ed6\u601d\u60f3\uff0c\u654c\u4eba\u7684\u5175\u5203\u5df2\u523a\u6765\u4e86\u3002\u5c31\u5728\u8fd9\u5239\u90a3\u95f4\uff0c\u4ed6\u5927\u543c\u4e00\u58f0\uff0c\u4e00\u67c4\u77ed\u5251\u6479\u7136\u51fa\u624b\uff0c \u201c\u9752\u51e4\u626b\u5c18\u201d\uff0c\u5c55\u51fa\u5929\u5c71\u5251\u6cd5\u4e2d\u7684\u7cbe\u5999\u62db\u6570\uff0c\u56db\u9762\u4e00\u8361\uff0c\u767b\u65f6\u6709\u51e0\u4e2a\u654c\u4eba\u7684\u5175\u5203\uff0c\u7ed9\u626b\u51fa\u4e86 \u624b\u3002 \u3000\u3000\u6768\u4e91\u806a\u731b\u5982\u6012\u72ee\uff0c\u4e00\u53e3\u77ed\u5251\uff0c\u7cbe\u8292\u7535\u95ea\uff0c\u5728\u654c\u4eba\u7684\u5305\u56f4\u5708\u4e2d\u5de6\u51b2\u53f3\u7a81\uff0c\u4e0d\u4e00\u4f1a\u5c31\u78b0\u7740\u4e86 \u81ea\u5df1\u7684\u5e08\u5f1f\u695a\u662d\u5357\u3002\u695a\u662d\u5357\u53eb\u9053\uff1a\u201c\u5e08\u5144\uff0c\u4f60\u8fc7\u6211\u4eec\u8fd9\u8fb9\u6765\u5427\uff0c\u4f55\u82e6\u53bb\u5e2e\u90a3\u4e9b\u54c8\u8428\u514b\u4eba\uff01\u201d \u6768\u4e91\u806a\u4e00\u5251\u5288\u53bb\uff0c\u559d\u9053\uff1a\u201c\u6211\u6ca1\u6709\u4f60\u8fd9\u6837\u7684\u5e08\u5f1f\uff01\u201d\u695a\u662d\u5357\u8fde\u9000\u4e09\u4e0b\uff0c\u9053\uff1a\u201c\u5929\u547d\u5df2\u5b9a\uff0c\u6ee1 \u6e05\u5df2\u5728\u5317\u4eac\u5750\u7a33\u9f99\u5ef7\uff0c\u4e2d\u539f\u767e\u4e07\u660e\u519b\u90fd\u5df2\u7ecf\u74e6\u89e3\uff0c\u56de\u7586\u53db\u4e71\uff0c\u4e5f\u5feb\u626b\u5e73\u3002\u4f60\u5e26\u7740\u51e0\u4e2a\u4eba\uff0c\u5954 \u9a70\u5927\u6f20\uff0c\u53c8\u80fd\u6210\u4e86\u4ec0\u4e48\u4e8b\uff01\u201d\u6768\u4e91\u806a\u54ac\u7740\u7259\u9f7f\uff0c\u5237\uff01\u5237\uff01\u5237\uff01\u8fde\u523a\u4e09\u5251\uff0c\u9a82\u9053\uff1a\u201c\u65e0\u803b\u4e4b \u5f92\uff0c\u4e3a\u864e\u4f5c\u6005\uff01\u201d\u4e00\u5251\u7d27\u4f3c\u4e00\u5251\uff0c\u628a\u695a\u662d\u5357\u6740\u5f97\u624b\u5fd9\u811a\u4e71\u3002 \u3000\u3000\u695a\u662d\u5357\u5728\u62fc\u547d\u62db\u67b6\u4e2d\uff0c\u5ffd\u5730\u4e00\u58f0\u957f\u5578\uff0c\u5728\u65c1\u52a9\u6218\u7684\u6e05\u5175\uff0c\u50cf\u9000\u6f6e\u822c\u4e24\u8fb9\u5206\u4e0b\u3002\u6768\u4e91\u806a\u6b63 \u5728\u5947\u602a\uff0c\u53ea\u89c1\u4e00\u4e2a\u6ee1\u6d32\u519b\u5b98\uff0c\u7b56\u9a6c\u4e0a\u6765\uff0c\u79bb\u5f00\u4ed6\u4eec\u8fd8\u6709\u4e03\u516b\u4e08\u7684\u5149\u666f\uff0c\u6479\u7136\u5728\u9a6c\u80cc\u4e0a\u817e\u7a7a\u63a0 \u8d77\uff0c\u624b\u6301\u7740\u4e00\u628a\u5947\u5f62\u602a\u72b6\u7684\u77ed\u5175\u5668\uff0c\u5f53\u5934\u63d2\u4e0b\u5c31\u8c61\u82cd\u9e70\u4e00\u822c\uff0c\u6768\u4e91\u806a\u5927\u6012\uff0c\u53cc\u8db3\u4e00\u987f\uff0c\u4e5f\u5e73 \u5730\u62d4\u8d77\u77ed\u5251\uff0c\u201c\u4e3e\u706b\u71ce\u5929\u201d\uff0c\u5f80\u90a3\u4eba\u7684\u5175\u5203\u4e0a\u4e00\u642d\u64a9\uff0c\u53ea\u542c\u5f97\u5f53\u7684\u4e00\u58f0\uff0c\u90a3\u4eba\u7684\u5175\u5203\uff0c\u5df2\u7ed9 \u9707\u51fa\u4e86\u624b\u3002\u5c31\u5728\u6b64\u9645\uff0c\u6768\u4e91\u806a\u8eab\u5b50\u60ac\u7a7a\uff0c\u731b\u89c1\u4e00\u80a1\u5bd2\u98ce\uff0c\u76f4\u5c04\u4e0a\u6765\uff0c\u4ed6\u987e\u4e0d\u4e86\u4f24\u5bb3\u654c\u4eba\uff0c\u4ee5 \u7edd\u9876\u8f7b\u529f\u201c\u7ec6\u80f8\u5de7\u7ffb\u4e91\u201d\u4e4b\u6280\uff0c\u5012\u8e66\u51fa\u53bb\uff0c\u8f7b\u98d8\u98d8\u843d\u5728\u5730\u4e0a\u3002\u56de\u5934\u4e00\u770b\u3001\u53ea\u89c1\u695a\u662d\u5357\u4e5f\u521a\u843d \u5728\u5730\u4e0a\uff0c\u6a2a\u5251\u56db\u987e\u3002\u521a\u624d\u4e58\u865a\u8fdb\u88ad\uff0c\u6551\u51fa\u90a3\u5bb6\u4f19\u7684\u6b63\u662f\u81ea\u5df1\u7684\u5e08\u5f1f\u3002 \u3000\u3000\u6768\u4e91\u806a\u76ee\u95ea\u7cbe\u5149\uff0c\u91cd\u51dd\u6d69\u6c14\uff0c\u77ed\u5251\u500f\u7ffb\uff0c\u8981\u548c\u4e24\u4e2a\u4eba\u6253\u5728\u4e00\u8d77\u3002\u90a3\u6ee1\u6d32\u519b\u5b98\u540d\u53eb\u7ebd\u67af \u5e90\uff0c\u4e43\u662f\u957f\u767d\u5c71\u6d3e\u98ce\u96f7\u5251\u9f50\u771f\u541b\u7684\u95e8\u4e0b\uff0c\u624b\u4f7f\u4e00\u628a\u4e27\u95e8\u632b\uff0c\u80fd\u5f53\u4e94\u884c\u5251\u4f7f\uff0c\u53c8\u53ef\u4f5c\u70b9\u7a74\u62e5 \u7528\uff0c\u5728\u516b\u65d7\u5175\u4e2d\uff0c\u6b66\u529f\u6570\u4e00\u6570\u4e8c\uff0c\u6ee1\u6e05\u7684\u5b97\u5ba4\u5e74\u9752\u7684\u5c06\u9886\u591a\u94ce\uff0c\u8bba\u8d77\u8f88\u4efd\uff0c\u8fd8\u662f\u4ed6\u7684\u5e08\u4f84\u3002 \u4ed6\u81ea\u5165\u5173\u4ee5\u6765\uff0c\u7f55\u9022\u654c\u4eba\uff0c\u6700\u8fd1\u624d\u7ed9\u8c03\u5230\u65b0\u7586\uff0c\u5e2e\u52a9\u4f0a\u7281\u5c06\u519b\u7eb3\u5170\u79c0\u5409\uff0c\u5e73\u5b9a\u56de\u90e8\u3002\u4ed6\u4e5f\u662f \u56e0\u81ea\u6043\u8fc7\u751a\uff0c\u4e0d\u77e5\u6768\u4e91\u806a\u5929\u5c71\u5251\u6cd5\u7684\u795e\u5999\uff0c\u6240\u4ee5\u4e00\u89c1\u9762\u5c31\u51cc\u7a7a\u4e0b\u51fb\uff0c\u60f3\u663e\u4e00\u624b\u7ed9\u695a\u662d\u5357\u770b\uff0c \u54ea\u6599\u8f7b\u529f\u8df3\u8dc3\u4e4b\u672f\uff0c\u6b63\u662f\u6768\u4e91\u806a\u6240\u957f\uff0c\u65b9\u4e00\u4ea4\u950b\uff0c\u5c31\u51e0\u4e4e\u6b7b\u5728\u6768\u4e91\u806a\u5251\u4e0b\uff0c\u4ed6\u4e0d\u7531\u5f97\u6c14\u7130\u5168 \u6d88\uff0c\u9a84\u6c14\u5c3d\u655b\uff0c\u6267\u8d77\u201c\u4e27\u95e8\u632b\u201d\uff0c\u6253\u70b9\u7cbe\u795e\uff0c\u65bd\u5c55\u5e73\u751f\u6240\u5b66\uff0c\u518d\u548c\u6768\u4e91\u806a\u7f20\u6597\u3002 \u3000\u3000\u8fd9\u6837\u4e00\u6765\uff0c\u6768\u4e91\u806a\u5012\u4e0d\u5bb9\u6613\u5f97\u624b\u4e86\u3002\u7ebd\u67af\u5e90\u7684\u4e27\u95e8\u632b\uff0c\u98d8\u6765\u6643\u53bb\uff0c\u65f6\u800c\u5f53\u5200\u5251\u5288\u4e0b\uff0c\u65f6 \u800c\u5f53\u5224\u5b98\u7b14\u6307\u6765\u3002\u6240\u6307\u7684\u5168\u662f\u4eba\u8eab\u4e09\u5341\u516d\u9053\u5927\u7a74\u3002\u66f4\u52a0\u4e0a\u7cbe\u901a\u5929\u5c71\u5251\u6cd5\u7684\u695a\u662d\u5357\uff0c\u4e00\u9762\u5728\u65c1 \u7275\u5236\uff0c\u4e00\u53e3\u957f\u5251\u7d27\u7d27\u8ddf\u5b9a\u6768\u4e91\u806a\uff0c\u4e00\u9762\u968f\u65f6\u63d0\u9192\u7ebd\u67af\u5e90\uff0c\u53eb\u4ed6\u5982\u4f55\u5e94\u4ed8\uff0c\u5c31\u597d\u50cf\u6559\u7ec3\u4e00\u822c\u3002 \u695a\u662d\u5357\u7684\u529f\u529b\u867d\u6d45\uff0c\u8fdc\u4e0d\u5982\u6768\u4e91\u806a\uff0c\u4f46\u56e0\u4ed6\u719f\u6089\u672c\u95e8\u5251\u6cd5\uff0c\u505a\u6559\u7ec3\u6307\u6325\u7ebd\u67af\u5e90\u534f\u540c\u4f5c\u6218\uff0c\u5374 \u662f\u751a\u4e3a\u9ed8\u5951\uff0c\u4e24\u4eba\u8fd9\u4e00\u914d\u5408\u7f20\u6597\uff0c\u5012\u628a\u6768\u4e91\u806a\u7eca\u5f97\u5f88\u7d27\uff0c\u4e0d\u8ba9\u4ed6\u8131\u51fa\u8eab\u53bb\u63f4\u6551\u5176\u4ed6\u7684\u54c8\u8428\u514b \u4eba\uff1b\uff1a \u3000\u3000\u8fd9\u65f6\u5927\u6f20\u4e0a\u5df2\u9677\u4e8e\u6df7\u6218\u4e4b\u4e2d\uff0c\u6768\u4e91\u806a\u53ea\u542c\u5f97\u4f0a\u58eb\u8fbe\u548c\u9ea6\u76d6\u63d0\u4e24\u4e2a\u54c8\u8428\u514b\u52c7\u58eb\u547c\u558a\u53f1\u54a4\u4e4b \u58f0\uff0c\u6562\u60c5\u5df2\u662f\u6253\u5f97\u5341\u5206\u6fc0\u70c8\u3002\u4ed6\u5fc3\u4e2d\u5927\u6012\uff0c\u5251\u6cd5\u4e00\u53d8\uff0c\u51cc\u5389\u65e0\u524d\uff0c\u5251\u5149\u95ea\u95ea\uff0c\u7f24\u7eb7\u98de\u821e\uff0c\u76d8 \u65cb\u8d77\u843d\u53d8\u5316\uff0c\u4e0d\u53ef\u540d\u72b6\uff0c\u4e0d\u53ef\u6349\u6478\u3002\u695a\u662d\u752b\u867d\u7136\u77e5\u9053\u8fd9\u662f\u5929\u5c71\u5251\u6cd5\u4e2d\u7684\u56de\u65cb\u8fde\u73af\u5251\u6cd5\uff0c\u4f46\u56e0 \u4e3a\u6768\u4e91\u806a\u8d8a\u5c55\u8d8a\u5feb\uff0c\u8fc5\u901f\u4e4b\u6781\uff0c\u800c\u4e14\u662f\u628a\u62db\u6570\u6298\u6563\u6765\u7528\uff0c\u4ee4\u4ed6\u76ee\u4e0d\u6687\u7ed9\uff0c\u81ea\u987e\u4e0d\u6687\uff0c\u54ea\u91cc\u8fd8 \u80fd\u63d0\u9192\u7ebd\u67af\u5e90\u3002 \u3000\u3000\u6768\u4e91\u806a\u8d8a\u6218\u8d8a\u52c7\uff0c\u5ffd\u5730\u695a\u662d\u5357\u4f7f\u4e86\u4e00\u62db\u201c\u6781\u76ee\u6ca7\u6ce2\u201d\uff0c\u5251\u5c16\u76f4\u523a\uff0c\u6768\u4e91\u806a\u8f7b\u8f7b\u4e00\u95ea\u3002\u77ed \u5251\u5df2\u4e58\u865a\u76f4\u53d6\u4e2d\u8def\uff0c\u695a\u662d\u5357\u56de\u6551\u4e0d\u53ca\uff0c\u672c\u6765\u4e07\u96be\u9003\u8131\u3002\u4e0d\u6599\u6768\u4e91\u806a\u4e0b\u624b\u4e4b\u9645\uff0c\u5ffd\u89c1\u695a\u662d\u5357\u6ee1 \u9762\u6050\u60e7\u4e4b\u5bb9\uff0c\u5fc3\u4e2d\u4e00\u8f6f\uff0c\u5251\u5c16\u5728\u4ed6\u80f8\u524d\u8f7b\u8f7b\u4e00\u70b9\uff0c\u53ea\u5272\u7834\u4ed6\u7684\u8863\u670d\uff0c\u6ca1\u6709\u5212\u4f24\u4ed6\u7684\u76ae\u8089\u3002\u77ed \u5251\u8fc5\u53c8\u6536\u53e3\uff0c\u53eb\u9053\uff1a\u201c\u5e08\u5f1f\uff0c\u4f60\u8fd8\u4e0d\u6094\u609f\u8fc7\u6765\uff01\u201d\u6768\u4e91\u806a\u5fc3\u5730\u7eaf\u539a\uff0c\u4ed6\u60f3\u8d77\u540c\u5728\u5929\u5c71\u4e4b\u9645\uff0c \u695a\u662d\u5357\u5728\u6280\u827a\u4e0a\u4e0d\u660e\u4e4b\u5904\uff0c\u5e38\u5411\u81ea\u5df1\u8bf7\u6559\u3002\u5e08\u5144\u5f1f\u611f\u60c5\u672c\u6765\u5c31\u597d\u3002\u800c\u4e14\u4ed6\u53c8\u662f\u5b64\u513f\uff0c\u5148\u662f\u4e3a \u6666\u660e\u7985\u5e08\u4e00\u4e2a\u4fd7\u5bb6\u5e08\u5f1f\u6536\u517b\uff0c\u540e\u6765\u624d\u9001\u4e0a\u5c71\u6765\u3002\u6768\u4e91\u806a\u89c1\u4ed6\u53ef\u601c\uff0c\u4e5f\u5c31\u7279\u522b\u7167\u7ba1\u4ed6\u3002\u4e0d\u6599\u4ed6 \u4e0b\u5c71\u4e09\u5e74\uff0c\u5374\u53d8\u6210\u8fd9\u4e2a\u6837\u5b50\uff0c\u6768\u4e91\u806a\u60f3\uff1a\u4ed6\u5b9a\u662f\u5e74\u5c11\u65e0\u77e5\uff0c\u7ed9\u574f\u4eba\u8bf1\u53db\uff0c\u56e0\u6b64\u4e0b\u7559\u60c5\uff0c\u4ecd\u60f3 \u529d\u4ed6\u6539\u8fc7\u3002 \u3000\u3000\u4e0d\u6599\u8fd9\u6837\u7f13\u5f97\u4e00\u7f13\u695a\u662d\u5357\u5206\u5916\u7559\u795e\uff0c\u5251\u6cd5\u4e58\u52bf\u53cd\u51fb\uff0c\u66f4\u4e3a\u6bd2\u8fa3\u3002\u800c\u7ebd\u67af\u5e90\u7684\u4e27\u95e8\u632b\uff0c\u6240 \u4f7f\u7684\u4e5f\u5c3d\u662f\u6bd2\u62db\u3002\u4e24\u4eba\u53c8\u8fde\u5439\u80e1\u54e8\uff0c\u53eb\u6765\u4e86\u5341\u591a\u4e2a\u6e05\u5175\u518d\u628a\u6768\u4e91\u806a\u56f4\u5728\u6838\u5fc3\uff0c\u8fd9\u65f6\u8fd1\u5904\u53c8\u4f20 \u6765\u54c8\u8428\u514b\u60e8\u53eb\u4e4b\u58f0\uff0c\u60f3\u662f\u5df2\u6709\u4f24\u4ea1\u3002\u6768\u4e91\u806a\u9b13\u7709\u5012\u7ad6\uff0c\u6012\u6781\u6c14\u9876\uff0c\u5929\u5c71\u5251\u6cd5\u4e00\u7d27\uff0c\u500f\u524d\u500f \u540e\uff0c\u7acb\u65f6\u5251\u5149\u6325\u970d\uff0c\u9163\u6218\u4e2d\u597d\u51e0\u4e2a\u4eba\u4e2d\u5251\u5012\u4e0b\u3002\u7ebd\u67af\u5e90\u548c\u695a\u662d\u5357\u4e8c\u4eba\uff0c\u4e5f\u5c61\u9047\u9669\u62db\uff0c\u53ea\u89c9\u5bd2 \u98ce\u5c31\u4f3c\u5728\u9762\u524d\u5212\u6765\u5212\u53bb\uff01 \u3000\u3000\u6b63\u6253\u5f97\u5341\u5206\u706b\u70ed\uff0c\u6781\u5ea6\u7d27\u5f20\u4e4b\u9645\u3002\u5ffd\u7136\u95f4\uff0c\u5927\u6f20\u4e0a\u9ec4\u6c99\u56da\u8d77\uff0c\u6709\u4eba\u5927\u53eb\u201c\u72c2\u98ce\u6765\u4e86\uff01\u201d \u6768\u4e91\u806a\u5403\u4e86\u4e00\u60ca\uff0c\u7ebd\u62c8\u5e90\u548c\u695a\u662d\u5357\u5df1\u6536\u8d77\u5175\u5203\uff0c\u8df3\u51fa\u5708\u5916\u3002\u970e\u90a3\u95f4\uff0c\u72c2\u98ce\u522e\u5730\u800c\u6765\uff0c\u4e00\u671b\u65e0 \u9645\u7684\u5927\u6c99\u6f20\u4e0a\uff0c\u5c3d\u662f\u9ec4\u7070\u8272\u7684\u6c99\u96fe\uff0c\u50cf\u6570\u5341\u767e\u91cc\u91cd\u539a\u539a\u7684\u9ec4\u5e55\uff0c\u906e\u5929\u853d\u5730\uff0c\u767d\u65e5\u9752\u5929\uff0c\u987f\u6210 \u9ed1\u591c\uff0c\u6c99\u96fe\u4e2d\u53ea\u89c1\u4eba\u5f71\u5e62\u5e62\uff0c\u56db\u5904\u5954\u9003\u3002\u5404\u81ea\u53bb\u62a2\u9a86\u9a7c\uff0c\u627e\u5e10\u5e55\uff0c\u6216\u5bfb\u89c5\u853d\u63a9\u4e4b\u5730\u3002 \u3000\u3000\u6768\u4e91\u806a\u9ad8\u58f0\u5927\u53eb\uff1a\u201c\u4f0a\u58eb\u8fbe\uff01\u9ea6\u76d6\u63d0\uff0c\u4f60\u4eec\u5728\u54ea\u91cc\uff1f\u201d\u4f46\u5728\u72c2\u98ce\u547c\u5578\u4e2d\uff0c\u4ed6\u7684\u58f0\u97f3\u6b63\u5982 \u5b64\u821f\u4e4b\u6df9\u6ca1\u4e8e\u6d77\u6d0b\uff0c\u54ea\u91cc\u6709\u4eba\u7b54\u5e94\u3002\u5c31\u5728\u6b64\u9645\uff0c\u6768\u4e91\u806a\u53c8\u89c9\u80cc\u540e\u88ab\u6c99\u77f3\u731b\u51fb\uff0c\u4ed6\u8fd9\u4e00\u60ca\u975e\u540c \u5c0f\u53ef\uff0c\u82e5\u662f\u6c99\u6f20\u4e0a\u7684\u6c99\u4e18\u88ab\u98ce\u79fb\u52a8\uff0c\u4efb\u6b66\u529f\u518d\u9ad8\uff0c\u4e5f\u4f1a\u88ab\u6d3b\u57cb\u4e27\u751f\u3002 \u3000\u3000\u5371\u6025\u4e2d\u4ed6\u907f\u8fc7\u98ce\u5934\uff0c\u53d1\u8db3\u72c2\u5954\u3002\u4ed6\u867d\u5728\u65b0\u7586\u591a\u5e74\uff0c\u5374\u672a\u66fe\u5728\u6c99\u6f20\u4e2d\u8fc7\u8fc7\u65e5\u5b50\u3002\u672c\u6765\u82e5\u78b0 \u5230\u8fd9\u6837\u5927\u7684\u98ce\uff0c\u6700\u597d\u662f\u6398\u5730\u6210\u6c9f\uff0c\u8eb2\u5728\u5176\u4e2d\u3002\u5047\u5982\u521a\u597d\u78b0\u7740\u6c99\u4e18\u843d\u4e0a\uff0c\u90a3\u5f53\u7136\u6ca1\u547d\u3002\u4f46\u82e5\u4e0d \u662f\u8fd9\u6837\u51d1\u5de7\uff0c\u6c99\u77f3\u5728\u4e0a\u9762\u522e\u8fc7\uff0c\u5374\u662f\u65e0\u4f24\u3002\u800c\u4e14\u7eb5\u7b97\u6c99\u571f\u79ef\u6709\u51e0\u5c3a\u539a\uff0c\u98ce\u8fc7\u540e\u4e5f\u53ef\u4ee5\u6316\u51fa \u6765\u3002\u6768\u4e91\u806a\u5374\u6ca1\u6709\u62b5\u5fa1\u98ce\u6c99\u7684\u7ecf\u9a8c\uff0c\u53ea\u662f\u72c2\u5954\u3002\u4ed6\u7684\u8f7b\u529f\u867d\u7136\u8d85\u5353\u7edd\u4f26\uff0c\u5374\u600e\u6837\u4e5f\u4e0d\u53ca\u72c2\u98ce \u7684\u8fc5\u75be\u3002\u8dd1\u4e86\u8bb8\u4e45\uff0c\u8fd8\u662f\u5728\u72c2\u98ce\u5a01\u80c1\u4e4b\u4e0b\uff0c\u8863\u88f3\u5df2\u88ab\u6c99\u77f3\u522e\u7834\uff0c\u795e\u5fd7\u4e5f\u6e10\u8ff7\u7cca\u3002\u8fd9\u65f6\u5ffd\u95fb\u6709 \u6c34\u58f0\u7011\u6f0f\uff0c\u6768\u4e91\u806a\u7cbe\u795e\u4e00\u632f\uff0c\u5fc3\u60f3\uff1a\u83ab\u975e\u662f\u627e\u5230\u4e86\u6c99\u6f20\u4e2d\u7f55\u6709\u7684\u6e56\u6cca\uff0c\u4ed6\u5faa\u7740\u6c34\u58f0\uff0c\u594b\u529b\u8dd1 \u53bb\u3002\u731b\u7136\u95f4\uff0c\u98ce\u52bf\u9aa4\u5927\uff0c\u72c2\u98ce\u631f\u7740\u5927\u91cf\u7684\u9ec4\u6c99\uff0c\u4f3c\u5343\u519b\u4e07\u9a6c\uff0c\u75be\u6d8c\u800c\u6765\uff0c\u4e2d\u95f4\u8fd8\u6709\u7740\u51e0\u5757\u5927 \u77f3\u5934\uff0c\u843d\u4e0b\u65f6\u6b63\u51fb\u4e2d\u4e86\u4ed6\u3002\u6768\u4e91\u806a\u7b4b\u75b2\u529b\u5026\uff0c\u8111\u888b\u6b32\u88c2\uff0c\u5927\u53eb\u4e00\u58f0\uff1a\u201c\u6211\u547d\u4f11\u77e3\uff01\u201d\u6323\u6700\u540e \u4e00\u53e3\u6c14\uff0c\u594b\u529b\u4e00\u8dc3\uff0c\u53ea\u89c9\u843d\u8db3\u5904\u8f6f\u7ef5\u7ef5\u4e00\u7247\uff0c\u4eba\u4e5f\u7acb\u65f6\u660f\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u6768\u4e91\u806a\u624d\u60a0\u60a0\u9192\u8f6c\u3002\u795e\u5fd7\u521d\u590d\uff0c\u4fbf\u89c9\u5e7d\u9999\u7f15\u7f15\uff0c\u6c81\u4eba\u5fc3\u80ba\u3002\u6768\u4e91\u806a\u7741 \u773c\u4e00\u770b\uff0c\u53d1\u89c9\u81ea\u5df1\u7adf\u662f\u8eb2\u5728\u4e00\u4e2a\u5e10\u5e55\u4e4b\u4e2d\uff0c\u5e10\u5e55\u56db\u5468\u5806\u7740\u9c9c\u82b1\uff0c\u4e2d\u95f4\u7adf\u662f\u4e00\u4f4d\u7a7f\u7740\u730e\u88c5\u7684\u5c11 \u5973\uff0c\u80cc\u5411\u7740\u81ea\u5df1\uff0c\u6367\u7740\u4e00\u5377\u4e66\u5728\u9605\u8bfb\u3002 \u3000\u3000\u7591\u5047\u7591\u771f\uff0c\u5982\u68a6\u5982\u5e7b\u3002\u6768\u4e91\u806a\u51e0\u4e4e\u8981\u53eb\u4e86\u51fa\u6765\uff0c\u4f46\u4ed6\u4e45\u7ecf\u6218\u573a\uff0c\u5904\u5904\u5c0f\u5fc3\u3002\u4ed6\u53cc\u773c\u4e00 \u78d5\uff0c\u5047\u88c5\u672a\u9192\uff0c\u7ec6\u5bdf\u52a8\u9759\u3002 \u3000\u3000\u90a3\u5c11\u5973\u4e0d\u77e5\u4ed6\u5df2\u9192\u8f6c\uff0c\u4ecd\u5728\u4f4e\u58f0\u541f\u54e6\u3002\u6768\u4e91\u806a\u7ec6\u542c\uff0c\u90a3\u5c11\u5728\u5ff5\u4e00\u9996\u8bcd\u3002\u8bcd\u9053\uff1a\u201c\u695a\u6c5f\u7a7a \u665a\uff0c\u6068\u79bb\u7fa4\u4e07\u91cc\uff0c\u604d\u7136\u60ca\u6563\uff0c\u81ea\u987e\u5f71\u6b32\u4e0b\u5bd2\u5858\uff0c\u6b63\u9769\u67af\uff0c\u6c34\u5e73\u5929\u8fdc\u3002\u5199\u4e0d\u6210\u4e66\uff0c\u53ea\u5bc4\u5f97\u76f8\u601d \u4e00\u70b9\u3002\u6599\u56e0\u5faa\u8bef\u6b8b\u6be1\u62e5\u96ea\uff0c\u6545\u4eba\u5fc3\u773c\u3002 \u3000\u3000\u63a8\u601c\u65c5\u6101\u832c\u518d\uff0c\u6f2b\u957f\u95e8\u591c\u6084\uff0c\u9526\u7b5d\u5f39\u6028\u3002\u60f3\u4f34\u4fa3\u72b9\u5bbf\u82a6\u82b1\uff0c\u5ff5\u6625\u524d\uff0c\u53bb\u7a0b\u5e94\u8f6c\u3002\u66ae\u96e8\u76f8 \u547c\uff0c\u6015\u6479\u5730\u7389\u95e8\u5173\u91cd\u89c1\u3002\u672a\u7f9e\u5eb6\u5f52\u6765\uff0c\u753b\u5e18\u534a\u5377\uff01\u201d \u3000\u3000\u6768\u4e91\u806a\u662f\u5fe0\u81e3\u4e4b\u540e\uff0c\u5e7c\u8bfb\u8bd7\u4e66\u3002\u5728\u5929\u5c71\u5b66\u827a\uff0c\u4e5f\u672a\u66fe\u4e22\u8352\u5e9f\u3002\u4e00\u542c\u5c31\u77e5\u662f\u5357\u5b8b\u8bcd\u4eba\u5f20\u708e \u548f\u5b64\u96c1\u7684\u4e00\u9996\u8bcd\u3002\u4ed6\u60f3\uff1a\u8fd9\u5c11\u5973\u5728\u585e\u5916\uff0c\u60f3\u662f\u5bc2\u5bde\u6781\u4e86\uff0c\u5b64\u72ec\u6781\u4e86\uff0c\u6240\u4ee5\u624d\u5ff5\u8fd9\u4e00\u9996\u8bcd\uff01 \u3000\u3000\u6b63\u601d\u60f3\u95f4\uff0c\u5e10\u5e55\u5916\u53c8\u8d70\u8fdb\u4e00\u4e2a\u5c11\u5973\uff0c\u5411\u730e\u88c5\u5c11\u5973\u95ee\u9053\uff1a\u201c\u5c0f\u59d0\uff0c\u90a3\u4eba\u9192\u4e86\u6ca1\u6709\uff1f\u4f60\u6709\u4ec0 \u4e48\u5429\u5490\u5417\uff1f\u201d\u730e\u88c5\u5c11\u5973\u63a9\u5377\u8bf4\u9053\uff1a\u201c\u6ca1\u6709\u9192\u5417\uff1f\u4f60\u53bb\u770b\u770b\uff0c\u4ed6\u8fd8\u6709\u6ca1\u6709\u51fa\u51b7\u6c57\uff1f\u5934\u4e0a\u7684\u70ed\u9000 \u4e86\u6ca1\u6709\uff1f\u82e5\u6709\u51b7\u6c57\uff0c\u4f60\u5c31\u7ed9\u4ed6\u6362\u8863\u3002\u201d\u90a3\u8fdb\u6765\u7684\u5c11\u5973\u201c\u54df\uff01\u201d\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u5c0f\u59d0\uff0c\u4f60\u4e13\u5dee\u9063 \u6211\u53bb\u670d\u4f8d\u8fd9\u4e2a\u81ed\u7537\u4eba\uff0c\u6211\u53ef\u4e0d\u5e72\u3002\u201d\u6768\u4e91\u806a\u5fc3\u60f3\uff1a\u201c\u8fd9\u8d70\u8fdb\u6765\u7684\u5c11\u5973\u5927\u7ea6\u662f\u4e2a\u4e2b\u9b1f\uff0c\u730e\u88c5\u5c11 \u5973\u5b9a\u5fc5\u662f\u5bcc\u8c6a\u6216\u5b98\u5bb6\u5c0f\u59d0\uff0c\u8981\u4e0d\u7136\u5c31\u662f\u90e8\u843d\u914b\u957f\u7684\u5973\u513f\u3002\u201d \u3000\u3000\u730e\u88c5\u5c11\u5973\u201c\u5478\u201d\u4e86\u4e00\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u51e0\u65f6\u5b66\u8d77\u6c49\u65cf\u5c0f\u59d0\u7684\u6d3e\u5934\u6765\u4e86\uff0c\u6211\u4eec\u6ee1\u6d32\u5973\u513f\uff0c\u4ece\u4e0d \u7814\u7a76\u4ec0\u4e48\u7537\u5973\u6388\u53d7\u4e0d\u4eb2\u8fd9\u4e00\u5957\uff0c\u4f60\u522b\u559c\u6b22\u8bfb\u6c49\u4eba\u8bd7\u4e66\uff0c\u6211\u53ef\u4e0d\u559c\u6b22\u4ed6\u4eec\u90a3\u4e9b\u865a\u6587\u4fd7\u793c\u3002\u518d \u8bf4\uff0c\u4f60\u95fb\u8fc7\u4ed6\u8eab\u4e0a\u7684\u6c14\u5473\u5417\uff1f\u600e\u8bf4\u4ed6\u662f\u4e2a\u81ed\u7537\u5b50\u3002\u201d\u90a3\u4e2a\u4e2b\u73af\u63a9\u5634\u7b11\u9053\uff1a\u201c\u5c0f\u59d0\u7684\u53e3\u8d8a\u6765\u8d8a \u5389\u5bb3\u4e86\uff0c\u4e13\u62ff\u6211\u4eec\u505a\u4e0b\u4eba\u7684\u6765\u6253\u8da3\u3002\u662f\uff0c\u4ed6\u4e00\u70b9\u4e5f\u4e0d\u81ed\uff0c\u8fd8\u662f\u4e2a\u7f8e\u7537\u5b50\u5462\uff01\u201d\u730e\u88c5\u5c11\u5973\u677f\u7740 \u8138\u9053\uff1a\u201c\u4f60\u522b\u778e\u8bf4\uff0c\u6211\u662f\u89c1\u4ed6\u4f69\u7684\u77ed\u5251\u4e43\u662f\u5b9d\u7269\uff0c\u60f3\u4ed6\u5b9a\u6709\u6765\u5386\uff0c\u8fd9\u624d\u6551\u4ed6\u4f60\u77e5\u9053\u4ec0\u4e48\uff1f\u201d \u90a3\u4e2a\u4e2b\u9b1f\u53c8\u9053\uff1a\u201c\u662f\u5440\uff0c\u6211\u4ec0\u4e48\u4e5f\u4e0d\u77e5\u9053\uff0c\u53ea\u77e5\u9053\u5c0f\u59d0\u8fd8\u6ca1\u6709\u5982\u610f\u90ce\u541b\uff01\u201d\u730e\u88c5\u5c11\u5973\u7ed9\u5979\u9017 \u5f97\u7b11\u4e86\u51fa\u6765\uff0c\u7b11\u9a82\u9053\uff1a\u201c\u4f60\u518d\u80e1\u8bf4\uff0c\u770b\u6211\u4e0d\u6495\u7834\u4f60\u7684\u5634\u3002\u201d \u3000\u3000\u90a3\u4e2a\u4e2b\u73af\u5411\u6768\u4e91\u806a\u7f13\u7f13\u884c\u6765\uff0c\u90a3\u4e2a\u730e\u88c5\u5c11\u5973\u4e5f\u8f6c\u8fc7\u4e86\u9762\u3002\u6768\u4e91\u806a\u5fae\u542f\u773c\u76ae\uff0c\u5077\u5077\u4e00\u770b\uff0c \u53ea\u89c1\u5979\u7f8e\u8273\u7edd\u4f26\uff0c\u8fde\u90a3\u4e2b\u73af\u4e5f\u662f\u59ff\u8272\u4e0d\u4fd7\u3002\u90a3\u4e2a\u4e2b\u9b1f\u5ffd\u7136\u62cd\u638c\u7b11\u9053\uff1a\u201c\u5c0f\u59d0\uff0c\u4ed6\u9192\u6765\u4e86\uff0c\u5077 \u5077\u5728\u770b\u4f60\u5462\uff01\u201d \u3000\u3000------------------ \u3000\u3000 \u626c\u5251\u8f69\u5c45\u58eb \u626b\u63cf\u6821\u5bf9  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u585e\u5916\u5947\u4fa0\u4f20\u300b\u2014\u2014\u7b2c\u4e09\u56de\u3000\u4ec7\u4eba\u7684\u5973\u513f \u6881\u7fbd\u751f\u300a\u585e\u5916\u5947\u4fa0\u4f20\u300b \u7b2c\u4e09\u56de\u3000\u4ec7\u4eba\u7684\u5973\u513f \u3000\u3000\u90a3\u730e\u88c5\u5c11\u5973\u5657\u55e4\u4e00\u7b11\uff0c\u8d70\u8fd1\u524d\u6765\u3002\u6768\u4e91\u806a\u7ed9\u4e2b\u73af\u9053\u7834\uff0c\u53ea\u597d\u7741\u5f00\u773c\u775b\uff0c\u6b20\u8eab\u6b32\u8d77\u3002\u4e0d\u6599 \u65b9\u4e00\u8f6c\u52a8\uff0c\u53ea\u89c9\u767e\u9ab8\u6b32\u6563\uff0c\u7b4b\u9aa8\u9178\u75db\u3002\u8fd9\u624d\u77e5\u9053\u90a3\u4e00\u573a\u5927\u98ce\u6c99\uff0c\u7adf\u4f7f\u81ea\u5df1\u53d7\u521b\u751a\u91cd\u3002\u6025\u8c03\u597d \u8109\u606f\uff0c\u4e0d\u6562\u4e71\u52a8\u3002\u730e\u88c5\u5c11\u5973\u76c8\u76c8\u7b11\u9053\uff1a\u201c\u4f60\u5df2\u7ecf\u7761\u4e86\u4e00\u5929\u96f6\u4e00\u591c\u4e86\uff0c\u600e\u4e48\u6837\uff0c\u5f88\u4e0d\u8212\u670d\u662f \u5417\uff1f\u201d \u3000\u3000\u6768\u4e91\u806a\u4f4e\u58f0\u9053\u8c22\u8bf4\u9053\uff1a\u201c\u591a\u8499\u5c0f\u59d0\u76f8\u6551\uff0c\u8bf7\u95ee\u8fd9\u91cc\u662f\u4ec0\u4e48\u5730\u65b9\uff1f\u5c0f\u59d0\u53c8\u662f\u54ea\u91cc\u7684\u4eba\uff1f\u201d \u730e\u88c5\u5c11\u5973\u9053\uff1a\u201c\u8fd9\u91cc\u662f\u624e\u6728\u53f0\uff0c\u79bb\u4f0a\u7281\u4e0d\u8fc7\u56db\u767e\u591a\u91cc\u3002\u4f60\u4e0d\u5fc5\u7ba1\u6211\u662f\u4ec0\u4e48\u4eba\uff0c\u53ea\u987e\u5728\u6211\u8fd9\u513f \u9759\u517b\u597d\u4e86\uff0c\u4f60\u5462\uff1f\u4f60\u53c8\u662f\u4ece\u54ea\u91cc\u6765\u7684\uff1f\u4e3a\u4ec0\u4e48\u4e00\u4e2a\u4eba\u5728\u6c99\u6f20\u91cc\u4e71\u95ef\uff1f\u201d \u3000\u3000\u6768\u4e91\u806a\u5927\u5403\u4e00\u60ca\uff0c\u81ea\u5df1\u4ece\u65b0\u7586\u5317\u90e8\u8d70\u5165\u6208\u58c1\uff0c\u539f\u62df\u901a\u8fc7\u6c99\u6f20\u8d70\u5165\u5357\u7586\uff0c\u4e0d\u6599\u5374\u8d70\u5230\u897f\u90e8 \u6765\u3002\u8fd9\u91cc\u79bb\u4f0a\u7281\u65e2\u8fd1\uff0c\u800c\u4f0a\u7281\u53c8\u662f\u6e05\u519b\u96c6\u7ed3\u4e4b\u5730\uff0c\u5012\u4e0d\u80fd\u4e0d\u5206\u5916\u5c0f\u5fc3\u3002\u90a3\u4e2b\u9b1f\u89c1\u4ed6\u6014\u6014\u7684\u671b \u7740\u5c0f\u59d0\uff0c\u6ca1\u6709\u56de\u7b54\uff0c\u53c8\u7b11\u7740\u9053\uff1a\u201c\u5c0f\u4f19\u5b50\uff0c\u5c3d\u671b\u6211\u4eec\u7684\u5c0f\u59d0\u505a\u4ec0\u4e48\uff0c\u4f60\u9053\u5979\u662f\u8c01\uff1f\u54fc\uff0c\u8bf4\u51fa \u6765\u8981\u5413\u4f60\u4e00\u8df3\uff0c\u5979\u53eb\u2026\u2026\u201d \u3000\u3000\u8bdd\u672a\u8bf4\u5b8c\uff0c\u730e\u88c5\u5c11\u5973\u6025\u622a\u7740\u8bf4\u9053\uff1a\u201c\u522b\u591a\u53e3\uff0c\u6211\u53eb\u660e\u6167\uff0c\u524d\u5929\u5e26\u4eba\u5230\u8fd9\u91cc\u6253\u730e\uff0c\u521a\u521a\u8e0f \u8fdb\u6c99\u6f20\uff0c\u4e0d\u6599\u5c31\u9047\u5230\u5f25\u5929\u5377\u5730\u7684\u98ce\u6c99\uff0c\u5e78\u597d\u8fd9\u91cc\u6709\u4e00\u5ea7\u5c71\u5cf0\uff0c\u6321\u4f4f\u98ce\u6c99\u7684\u6765\u52bf\uff0c\u6211\u4eec\u7684\u5e10\u5e55 \u53c8\u90fd\u575a\u56fa\uff0c\u8fd9\u624d\u4fa5\u5e78\u8eb2\u8fc7\u3002\u201d \u3000\u3000\u5c0f\u4e2b\u73af\u53c8\u9053\uff1a\u201c\u524d\u5929\u9ec4\u660f\u65f6\u5019\uff0c\u98ce\u52bf\u8f6c\u7f13\uff0c\u6211\u4eec\u5230\u5e03\u817e\u6e56\u53bb\u6253\u730e\uff0c\u731b\u7136\u95f4\u98ce\u6c99\u53c8\u5927\u8d77 \u6765\uff0c\u6211\u4eec\u770b\u89c1\u4f60\u6ca1\u547d\u98de\u5954\uff0c\u597d\u8c61\u548c\u98ce\u6c99\u8d5b\u8dd1\u4e00\u6837\uff0c\u8dd1\u5230\u6e56\u8fb9\uff0c\u4f60\u4e5f\u4e0d\u77e5\u9053\u3002\u6211\u4eec\u53ea\u89c1\u4f60\u4f3c\u7f9a \u7f8a\u9047\u5230\u72ee\u5b50\u4e00\u6837\uff0c\u7a81\u7136\u8dc3\u8d77\uff0c\u6251\u901a\u4e00\u58f0\uff0c\u5c31\u9677\u5165\u6e56\u8fb9\u7684\u6ce5\u6cbc\u53bb\u4e86\u3002\u5c0f\u59d0\u548c\u6211\u628a\u4f60\u62c9\u51fa\u6765\uff0c \u54fc\uff01\u4f60\u6ee1\u8eab\u90fd\u662f\u6c61\u6ce5\uff0c\u6211\u4eec\u53eb\u9a6c\u592b\u7ed9\u4f60\u6d17\u4e86\u534a\u4e2a\u65f6\u8fb0\uff0c\u624d\u5f04\u5e72\u51c0\u3002\u800c\u4f60\u5c31\u8c61\u6b7b\u4eba\u4e00\u6837\uff0c\u4ec0\u4e48 \u4e5f\u4e0d\u77e5\u9053\uff01\u201d \u3000\u3000\u6768\u4e91\u806a\u53c8\u662f\u611f\u6fc0\uff0c\u53c8\u662f\u7f9e\u60ed\uff0c\u4f46\u84e6\u7136\u60f3\u8d77\uff0c\u8fd9\u4e2a\u53eb\u505a\u660e\u6167\u7684\u5c11\u5973\uff0c\u65e2\u4e0d\u80af\u544a\u8bc9\u81ea\u5df1\u7684\u540d \u5b57\u548c\u8eab\u4e16\uff0c\u800c\u770b\u5979\u7684\u6c14\u6d3e\uff0c\u6709\u4e2b\u9b1f\u3001\u6709\u9a6c\u592b\uff0c\u8fd8\u4eb2\u81ea\u5e26\u4eba\u5230\u8fd9\u91cc\u6253\u730e\uff0c\u8fd9\u53ef\u4e0d\u662f\u666e\u901a\u4eba\u5bb6\u7684 \u5973\u513f\u3002\u5979\u5230\u5e95\u662f\u4ec0\u4e48\u8eab\u4efd\uff0c\u6768\u4e91\u806a\u600e\u4e48\u4e5f\u731c\u4e0d\u900f\u3002 \u3000\u3000\u5c0f\u4e2b\u9b1f\u53c8\u9053\uff1a\u201c\u6211\u4eec\u5df2\u7ecf\u544a\u8bc9\u4f60\u4e86\uff0c\u4f60\u672a\u56de\u7b54\u6211\u4eec\u5c0f\u59d0\u7684\u95ee\u8bdd\u5462\uff01\u201d\u6768\u4e91\u806a\u9053\uff1a\u201c\u6211\u672c \u6765\u662f\u548c\u4e00\u5927\u7fa4\u9a7c\u9a6c\u5ba2\u5546\uff0c\u4ece\u5317\u7586\u6765\u7684\uff0c\u8d70\u4e86\u7ea6\u5341\u6765\u5929\uff0c\u534a\u8def\u78b0\u4e0a\u5927\u98ce\u6c99\uff0c\u4e00\u4e2a\u4eba\u5c31\u95ef\u8fd9\u513f\u6765 \u4e86\u3002\u8fd9\u5e76\u6ca1\u4ec0\u4e48\u5947\u602a\u5440\u3002\u201d \u3000\u3000\u5c0f\u4e2b\u9b1f\u62bf\u5634\u7b11\u9053\uff1a\u201c\u8fd9\u624d\u771f\u5947\u602a\u5462\uff01\u4ece\u5317\u7586\u8d70\u4e86\u5341\u6765\u5929\uff0c\u5e94\u8be5\u5230\u4e86\u6c99\u6f20\u4e2d\u90e8\uff0c\u4ece\u4e2d\u90e8\u8d70 \u5230\u8fd9\u91cc\uff0c\u5c11\u8bf4\u4e5f\u6709\u4e94\u516d\u767e\u91cc\uff0c\u770b\u6765\u4f60\u7684\u811a\u7a0b\u771f\u53ef\u4ee5\u548c\u7f9a\u7f8a\u6bd4\u8d5b\u4e86\u3002\u201d \u3000\u3000\u660e\u6167\u5c0f\u59d0\u5fae\u5fae\u4e00\u7b11\uff0c\u4ece\u8863\u5e95\u62bd\u51fa\u4e00\u628a\u7cbe\u8292\u593a\u76ee\u7684\u77ed\u5251\uff0e\u8bf4\u9053\uff1a\u201c\u5c0f\u4e2b\u5934\u89c1\u8bc6\u592a\u5c11\uff0c\u4e0d\u5fc5 \u7406\u5979\u3002\u770b\u4f60\u6709\u8fd9\u6837\u4e00\u628a\u5b9d\u5251\uff0c\u4e00\u5929\u8dd1\u51e0\u767e\u91cc\u4e5f\u5f53\u4e0d\u662f\u96be\u4e8b\u3002\u6211\u770b\u4f60\u7684\u6b66\u529f\u4e00\u5b9a\u5f88\u597d\uff0c\u5f85\u4f60\u6c14 \u529b\u6062\u590d\u4e4b\u540e\uff0c\u6559\u51e0\u624b\u7ed9\u6211\u597d\u5417\uff1f\u201d\u5c0f\u4e2b\u9b1f\u63d2\u53e3\u9053\uff1a\u201c\u2018\u662f\u5440\uff0c\u6211\u4eec\u7684\u5c0f\u59d0\u9876\u7231\u6b66\u827a\uff0c\u8bb8\u591a\u6559 \u5934\u90fd\u4e0d\u591f\u5979\u6253\u5462\uff01\u201d\u6768\u4e91\u806a\u542c\u5f97\u201c\u6559\u5934\u201d\u4e8c\u5b57\u53c8\u662f\u76b1\u4e86\u76b1\u7709\u5934\u3002 \u3000\u3000\u8fd9\u65f6\u5916\u9762\u53c8\u8fdb\u6765\u4e86\u4e24\u4e2a\u5a62\u5973\uff0c\u6367\u8fdb\u4e00\u5927\u74e2\u9178\u9a6c\u5976\u7ed9\u6768\u4e91\u806a\u559d\uff0c\u6768\u4e91\u806a\u6b63\u9965\u997f\uff0c\u4e5f\u4e0d\u5ba2\u6c14 \u7684\u559d\u4e86\u3002\u730e\u88c5\u5c11\u5973\u9053\uff1a\u201c\u4f60\u521a\u521a\u9192\u8f6c\uff0c\u8fd8\u4e0d\u80fd\u8bf4\u5927\u591a\u8bdd\uff0c\u518d\u9759\u517b\u4e24\u5929\u5427\uff0c\u5f85\u4f60\u597d\u540e\uff0c\u6211\u548c\u4f60 \u53bb\u73a9\u3002\u201d \u3000\u3000\u6768\u4e91\u806a\u9759\u517b\u4e24\u5929\uff0c\u679c\u7136\u6c14\u529b\u5b8c\u5168\u6062\u590d\u3002\u5728\u8fd9\u4e24\u5929\u4e2d\uff0c\u660e\u6167\u548c\u90a3\u4e2a\u5c0f\u4e2b\u9b1f\u966a\u5728\u4ed6\u7684\u8eab\u65c1\uff0c \u4e0e\u4ed6\u804a\u5929\u89e3\u95f7\u3002\u660e\u6167\u65e2\u901a\u6b66\u529f\uff0c\u4ea6\u89e3\u6587\u4e8b\u3002\u6768\u4e91\u806a\u4e0e\u5979\u8c08\u5f97\u5f88\u662f\u6295\u673a\u3002\u53ea\u662f\u4e00\u78b0\u5230\u8c08\u53ca\u4e24\u4eba \u7684\u6765\u5386\u65f6\u3002\u5927\u5bb6\u90fd\u628a\u8bdd\u5934\u7ed5\u4e86\u5f00\u53bb\u3002 \u3000\u3000\u7b2c\u4e09\u5929\uff0c\u6768\u4e91\u806a\u5df2\u80fd\u8d70\u52a8\u5982\u5e38\u4e86\u3002\u660e\u6167\u5c0f\u59d0\u5e26\u4ed6\u6b65\u51fa\u5e10\u5e55\u3002\u6768\u4e91\u806a\u53ea\u89c1\u5e10\u5e55\u9644\u8fd1\u679c\u7136\u6709 \u4e00\u4e2a\u6e56\u6cca\uff0c\u60f3\u6765\u5c31\u662f\u5979\u4eec\u6240\u8bf4\u7684\u5e03\u817e\u5141\u6e56\u7684\u4e1c\u9762\uff0c\u6709\u4e00\u5ea7\u5c71\u5cf0\uff0c\u592a\u9633\u900f\u8fc7\u4e73\u767d\u8272\u7684\u4e91\uff0c\u7167\u5728 \u5c71\u4e0a\uff0c\u5012\u5f71\u6cdb\u5728\u78a7\u6ce2\u8361\u6f3e\u7684\u6e56\u4e2d\uff0c\u771f\u662f\u65e5\u4e3d\u98ce\u548c\uff0c\u4e00\u70b9\u4e0d\u50cf\u522e\u98ce\u6c99\u7684\u6837\u5b50\u3002\u6e56\u4e0a\u6709\u6210\u7fa4\u7684\u91ce \u9e2d\u548c\u6c34\u9e1f\u5728\u60a0\u95f2\u7684\u6e38\u6765\u6e38\u53bb\uff0c\u53d1\u51fa\u60a6\u8033\u7684\u9e23\u58f0\u3002\u4e91\u56e2\u822c\u7684\u7f8a\u7fa4\u5728\u8349\u5730\u4e0a\u5403\u7740\u8349\u3002\u6e56\u8fb9\u6709\u591a\u4e2a \u730e\u88c5\u7537\u5973\uff0c\u6325\u7740\u76ae\u97ad\u9ad8\u5531\u7267\u6b4c\u3002\u4ed6\u4eec\u89c1\u660e\u6167\u5c0f\u59d0\u51fa\u6765\uff0c\u90fd\u606d\u606d\u656c\u656c\u5730\u884c\u793c\uff0c\u5bf9\u6768\u4e91\u806a\u66f4\u662f\u5341 \u5206\u6ce8\u89c6\u3002 \u3000\u3000\u6768\u4e91\u806a\u5fae\u5fae\u4e00\u9707\uff0c\u95ee\u9053\uff1a\u201c\u8fd9\u4e9b\u90fd\u662f\u4f60\u5e26\u6765\u7684\u4eba\u5417\uff1f\u201d\u660e\u6167\u70b9\u4e86\u70b9\u5934\uff0c\u628a\u8bdd\u5934\u7ed5\u5f00\u53bb \u9053\uff1a\u201c\u4f60\u770b\u8fd9\u91cc\u771f\u662f\u6c99\u6f20\u4e2d\u7684\u7eff\u6d32\uff0c\u8fd9\u6837\u597d\u7684\u98ce\u666f\uff01\u201d\u6768\u4e91\u806a\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u8fd9\u5730\u65b9\u8fd9\u79cd\u5bc2\u9759 \u5b89\u8be6\u7684\u6c14\u6c1b\uff0c\u771f\u50cf\u4e16\u5916\u6843\u6e90\u4e00\u6837\uff0c\u8981\u662f\u6ca1\u6709\u5175\u6208\u591a\u597d\uff01\u201d\u660e\u6167\u9053\uff1a\u201c\u4f60\u53c8\u5728\u53d1\u4ec0\u4e48\u611f\u6168\u4e86\uff1f \u4f60\u4e0d\u613f\u610f\u6709\u6218\u706b\u5175\u6208\uff0c\u4e3a\u4ec0\u4e48\u4f69\u7740\u5b9d\u5251\uff0c\u8fd8\u7ec3\u4e86\u90a3\u4e48\u4e00\u8eab\u6b66\u827a\uff1f\u201d\u6768\u4e91\u806a\u9053\uff1a\u201c\u5047\u5982\u6ca1\u6709\u6218 \u706b\u5e26\u5230\u65b0\u7586\uff0c\u6211\u4eec\u4e5f\u4e0d\u4f1a\u62ff\u5200\u5f04\u5251\uff01\u201d\u660e\u6167\u5c0f\u59d0\u7f8e\u76ee\u6d41\u76fc\uff0c\u76ef\u7740\u6768\u4e91\u806a\u9053\uff1a\u201c\u4f60\u662f\u54c8\u8428\u514b\u65cf \u8fd8\u662f\u7ef4\u543e\u5c14\u65cf\uff1f\u6211\u770b\u4f60\u597d\u50cf\u662f\u519b\u4e2d\u7684\uff1f\u201d\u6768\u4e91\u806a\u9762\u8272\u5ffd\u53d8\uff0c\u95ee\u9053\uff1a\u201c\u5047\u5982\u6211\u662f\u4f60\u7684\u654c\u4eba\uff0c\u4f60 \u540e\u6094\u6551\u4e86\u6211\u5417\uff1f\u201d\u660e\u6167\u7b11\u9053\uff1a\u201c\u6211\u548c\u4f60\u4e00\u6837\uff0c\u4e5f\u4e0d\u613f\u610f\u6253\u4ed7\uff0c\u4f60\u53ef\u80fd\u662f\u6211\u4eec\u4e00\u65cf\u7684\u654c\u4eba\uff0c\u4f46 \u4e0d\u4f1a\u662f\u6211\u7684\u654c\u4eba\uff01\u201d \u3000\u3000\u6b63\u8bf4\u8bdd\u95f4\uff0c\u5ffd\u7136\u5c71\u7684\u90a3\u8fb9\uff0c\u4f20\u6765\u7684\u9a6c\u94c3\u9a7c\u94c3\u4e4b\u58f0\uff0c\u660e\u6167\u5c0f\u59d0\u9053\uff1a\u201c\u5982\u679c\u6709\u4eba\u6765\u5230\uff0c\u95ee\u8d77 \u4f60\u65f6\uff0c\u4f60\u5c31\u8bf4\u662f\u8ff7\u4e86\u8def\u7684\u7267\u4eba\uff0c\u7ed9\u6211\u4eec\u6551\u8d77\u6765\u7684\uff0c\u8bb0\u5f97\u5417\uff1f\u201d\u6768\u4e91\u806a\u4e00\u770b\u81ea\u5df1\u8eab\u4e0a\uff0c\u7a7f\u7684\u662f \u4e00\u5957\u7267\u6c11\u7684\u670d\u88c5\uff0c\u77e5\u9053\u662f\u660e\u6167\u5c0f\u59d0\u7ed9\u4ed6\u6362\u7684\uff0c\u6697\u8d5e\u5979\u60f3\u5f97\u5468\u5230\uff0c\u70b9\u4e86\u70b9\u5934\u3002\u53c8\u628a\u77ed\u5251\u9012\u7ed9\u4ed6 \u9053\uff1a\u201c\u8fd9\u628a\u5251\u8fd8\u7ed9\u4f60\uff0c\u60f3\u4f60\u4e0d\u4f1a\u62ff\u6765\u4e0e\u6211\u4e3a\u654c\u3002\u201d\u6768\u4e91\u806a\u4f4e\u4f4e\u8bf4\u9053\uff1a\u201c\u6211\u6c38\u4e0d\u4f1a\u4f24\u5bb3\u4f60\uff01\u201d \u3000\u3000\u8fd9\u65f6\u5c71\u62d7\u5904\u8f6c\u6765\u4e00\u5f6a\u4eba\u9a6c\uff0c\u4e3a\u9996\u7684\u8de8\u7740\u9ad8\u5934\u5927\u9a6c\uff0c\u5a01\u98ce\u51db\u51db\uff0c\u7adf\u662f\u4e00\u4f4d\u6ee1\u6d32\u5c06\u519b\u3002\u6768\u4e91 \u806a\u4e00\u89c1\uff0c\u51e0\u4e4e\u53eb\u51fa\u58f0\u6765\uff0c\u6b64\u4eba\u975e\u662f\u522b\u4eba\uff0c\u6b63\u662f\u4f0a\u7281\u5c06\u519b\u7eb3\u5170\u79c0\u5409\uff0c\u4ed6\u662f\u5e26\u6e05\u5175\u4fb5\u5165\u65b0\u7586\u7684\u5c06 \u9886\u4e4b\u4e00\uff0c\u5728\u7387\u9886\u54c8\u8428\u514b\u4eba\u62b5\u6297\u6e05\u5175\u7684\u6218\u6597\u4e2d\uff0c\u66fe\u548c\u4ed6\u4ea4\u8fc7\u624b\u3002\u6768\u4e91\u806a\u4f4e\u4e0b\u5934\u6765\uff0c\u773c\u671b\u522b\u5904\u3002 \u53ea\u542c\u5f97\u7eb3\u5170\u79c0\u5409\u53eb\u9053\uff1a\u201c\u660e\u6167\uff0c\u4f60\u7238\u7238\u6253\u4e86\u80dc\u4ed7\u56de\u6765\u54af\uff01\u8def\u8fc7\u8fd9\u91cc\uff0c\u542c\u8bf4\u4f60\u5728\u8fd9\u91cc\u6253\u730e\uff0c\u600e \u4e48\u6837\uff0c\u730e\u5f97\u4ec0\u4e48\u4e1c\u897f\u9001\u7ed9\u7238\u7238\uff1f\u201d \u3000\u3000\u6768\u4e91\u806a\u8fd9\u4e00\u60ca\u66f4\u662f\u975e\u540c\u5c0f\u53ef\uff0c\u60f3\u4e0d\u5230\u8fd9\u4f4d\u6551\u81ea\u5df1\u6027\u547d\u7684\u5c11\u5973\uff0c\u7adf\u7136\u662f\u7eb3\u5170\u79c0\u5409\u7684\u5973\u513f\u3002 \u731b\u7136\u95f4\uff01\u4ed6\u597d\u50cf\u89c9\u5f97\u975e\u5e38\u7a7a\u865a\u53c8\u975e\u5e38\u5931\u671b\uff01\u4f46\u968f\u5373\u53e6\u4e00\u4e2a\u5ff5\u5934\u5347\u4e86\u4e0a\u6765\uff1a\u81ea\u5df1\u8d1f\u7740\u91cd\u5927\u7684\u4f7f \u547d\uff0c\u8981\u91cd\u65b0\u805a\u96c6\u54c8\u8428\u514b\u4eba\uff0c\u6218\u6597\u518d\u6218\u6597\uff01\u81ea\u5df1\u4e0d\u80fd\u7ed9\u4ed6\u53d1\u73b0\uff0c\u5047\u5982\u88ab\u53d1\u73b0\u4e86\uff0c\u7acb\u523b\u5c31\u5f97\u60f3\u6cd5 \u9003\u8dd1\u3002\u4ed6\u8bd5\u8bd5\u6d3b\u52a8\u81ea\u5df1\u7684\u7b4b\u9aa8\uff0c\u89c9\u5f97\u529b\u6c14\u5145\u6c9b\uff0c\u4ed6\u629a\u7740\u77ed\u5251\uff0c\u5145\u6ee1\u4e86\u52c7\u6c14\uff01 \u3000\u3000\u8fd9\u65f6\u7eb3\u5170\u79c0\u5409\u5df2\u5e26\u9886\u4eba\u9a6c\uff0c\u8d70\u5230\u6e56\u8fb9\u996e\u6c34\uff0c\u660e\u6167\u7684\u4ece\u4eba\u8df3\u7740\u7b11\u7740\uff0c\u5531\u7740\u6ee1\u6d32\u7684\u6218\u6b4c\u8fce\u63a5 \u4ed6\u4eec\u3002\u6768\u4e91\u806a\u54ac\u7d27\u7259\u9f7f\uff0c\u4f46\u7acb\u5373\u60f3\u5230\uff1a\u201c\u4f55\u5fc5\u6068\u8fd9\u4e9b\u4eba\uff0c\u4ed6\u4eec\u4e5f\u90fd\u662f\u53d7\u6b3a\u9a97\u800c\u88ab\u9a97\u6765\u7684 \u554a\uff01\u201d\u4ed6\u6df7\u4eba\u4e86\u4eba\u7fa4\u4e4b\u4e2d\uff0c\u4e5f\u5047\u4f5c\u5531\u6b4c\u821e\u8e48\uff0c\u5e0c\u671b\u907f\u8fc7\u4ed6\u4eec\u7684\u6ce8\u89c6\u3002 \u3000\u3000\u6b63\u5728\u8fd9\u4e2a\u65f6\u5019\uff0c\u5ffd\u7136\u6709\u4e24\u4e2a\u6e05\u519b\u519b\u5b98\uff0c\u50cf\u559d\u9189\u9152\u4e00\u6837\uff0c\u62e5\u821e\u8fc7\u6765\uff0c\u5728\u6768\u4e91\u806a\u80a9\u5934\u91cd\u91cd\u7684 \u649e\u4e86\u4e00\u4e0b\uff0c\u6768\u4e91\u806a\u672c\u80fd\u7684\u8fd0\u8d77\u5185\u529b\u5f80\u5916\u4e00\u8feb\uff0c\u90a3\u4e24\u4e2a\u519b\u5b98\u8dcc\u8dcc\u649e\u649e\u7684\u76f4\u7ed9\u78b0\u51fa\u4e08\u8bb8\uff0c\u624d\u6536\u5f97 \u4f4f\u811a\u6b65\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u4ed6\u662f\u8c01\uff1f\u201d\u539f\u6765\u8fd9\u4e24\u4e2a\u519b\u5b98\u770b\u89c1\u4ed6\u7267\u4eba\u6253\u626e\uff0c\u6742\u5728\u660e\u6167\u5c0f\u59d0\u7684\u4ece\u4eba \u4e2d\uff0c\u89c9\u5f97\u6709\u70b9\u7279\u522b\uff0c\u6545\u610f\u6765\u8bd5\u4ed6\u3002 \u3000\u3000\u660e\u6167\u5c0f\u59d0\u6025\u5fd9\u62e6\u4e0a\u53bb\u9053\uff1a\u201c\u4ed6\u662f\u7ef4\u543e\u5c14\u65cf\u7684\u7267\u6c11\uff0c\u4f60\u4eec\u4e0d\u8981\u96be\u4e3a\u4ed6\uff01\u201d\u8fd9\u65f6\u7eb3\u5170\u79c0\u5409\u7684 \u58eb\u5175\u548c\u660e\u6167\u5c0f\u59d0\u7684\u4ece\u4eba\u90fd\u5df2\u9759\u4e86\u4e0b\u6765\uff0c\u6ce8\u89c6\u7740\u8fd9\u7a81\u7136\u7684\u4e8b\u53d8\uff01 \u3000\u3000\u6768\u4e91\u806a\u9547\u9759\u5f97\u5f88\uff0c\u8fce\u63a5\u7740\u4e24\u4e2a\u519b\u5b98\u7684\u6ce8\u89c6\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u662f\u4ece\u5e93\u5c14\u7f55\u6765\u7684\u7267\u6c11\uff0c\u6211\u7684 \u7f8a\u7fa4\u548c\u540c\u4f34\uff0c\u90fd\u7ed9\u524d\u51e0\u5929\u522e\u7684\u5927\u98ce\u6c99\u6253\u6563\u4e86\u3002\u6211\u662f\u4f60\u4eec\u7684\u683c\u683c\uff08\u6ee1\u6d32\u8bdd\u5c0a\u8d35\u7684\u59d1\u5a18\uff09\u6551 \u7684\u3002\u201d\u660e\u6167\u5e94\u58f0\u7ed9\u8bc1\u5b9e\uff0c\u4e24\u4e2a\u519b\u5b98\u5140\u662f\u5c06\u4fe1\u5c06\u7591\u3002 \u3000\u3000\u7eb3\u5170\u79c0\u5409\u76ee\u4e0d\u8f6c\u775b\u5730\u76ef\u7740\u6768\u4e91\u806a\uff0c\u5ffd\u7136\u53f3\u624b\u4e00\u626c\uff0c\u4e00\u652f\u8896\u7bad\u5411\u4ed6\u5c04\u6765\uff0c\u6768\u4e91\u806a\u7565\u4fa7\u8eab\u8eaf \u5c31\u907f\u8fc7\u4e86\u3002\u7eb3\u5170\u79c0\u5409\u5927\u53eb\uff1a\u201c\u8fd9\u662f\u5978\u7ec6\uff0c\u8d76\u5feb\u62ff\u4e0b\uff01\u201d\u4ed6\u8eab\u8fb9\u7684\u51e0\u4e2a\u6ee1\u6d32\u6b66\u58eb\uff0c\u7acb\u523b\u56db\u9762\u8dc3 \u51fa\uff0c\u51c6\u5907\u5408\u56f4\uff0c\u4f5c\u52bf\u64d2\u62ff\uff0c\u539f\u6765\u7eb3\u5170\u79c0\u5409\u548c\u6768\u4e91\u806a\u7684\u961f\u4f0d\u4f5c\u6218\u8fc7\uff0c\u5728\u6218\u573a\u89c1\u8fc7\u4e00\u9762\u3002\u6b64\u65f6\u89c1 \u4ed6\u7267\u6c11\u6253\u626e\uff0c\u89c9\u5f97\u6709\u70b9\u9762\u719f\uff0c\u4f46\u53c8\u8bb0\u4e0d\u8d77\u6765\uff0c\u540e\u6765\u8bd5\u4ed6\u4e00\u652f\u8896\u5251\uff0c\u89c1\u4ed6\u907f\u6697\u5668\u7684\u8eab\u6cd5\uff0c\u6781\u4e3a \u8f7b\u5de7\uff0c\u7edd\u4e0d\u662f\u4e2a\u666e\u901a\u7267\u6c11\u53ef\u6bd4\uff0c\u56e0\u6b64\u9a6c\u4e0a\u9192\u8d77\uff0c\u7acb\u523b\u4e0b\u4ee4\u628a\u4ed6\u751f\u64d2\u3002 \u3000\u3000\u6768\u4e91\u806a\u9661\u7136\u5927\u559d\u4e00\u58f0\uff0c\u8fce\u7740\u4e00\u4e2a\u6251\u4e0a\u6765\u7684\u6ee1\u6d32\u6b66\u58eb\uff0c\u4e00\u4e2a\u7167\u9762\uff0c\u5580\u5693\u4e00\u58f0\uff0c\u628a\u90a3\u4e2a\u6b66\u58eb \u7684\u624b\u8155\u786c\u751f\u751f\u7684\u6298\u65ad\u4e0b\u6765\uff0c\u90a3\u4e2a\u6b66\u58eb\u75db\u5f97\u6740\u732a\u822c\u7684\u5927\u58f0\u53f7\u53eb\uff0c\u6768\u4e91\u806a\u7406\u4e5f\u4e0d\u7406\uff0c\u201c\u556a\u5854\u201d\u4e00 \u58f0\uff0c\u6254\u5728\u5730\u4e0a\uff0c\u8f6c\u4e86\u534a\u4e2a\u5706\u5708\uff1b\u53c8\u63a5\u7740\u7b2c\u4e8c\u4e2a\u6b66\u58eb\u653b\u6765\u7684\u62f3\u5934\uff0c\u4e00\u626f\u4e4b\u4e0b\uff0c\u628a\u4ed6\u6d3b\u6349\u8fc7\u6765\uff0c \u53c8\u662f\u5927\u559d\u4e00\u58f0\uff0c\u5c06\u4ed6\u62a1\u4e86\u8d77\u6765\uff0c\u4e00\u9635\u6025\u98ce\u6025\u821e\uff0c\u628a\u90a3\u6b66\u58eb\u80d6\u5927\u7684\u8eab\u8eaf\uff0c\u76f4\u5411\u6e56\u5fc3\u63b7\u53bb\uff0c\u53ea\u542c \u5f97\u201c\u6251\u901a\u201d\u4e00\u58f0\uff0c\u6fc0\u8d77\u4e86\u4e00\u80a1\u6d6a\u82b1\uff0c\u5413\u5f97\u7eb3\u5170\u79c0\u5409\u76ee\u77aa\u53e3\u5446\u3002 \u3000\u3000\u8fd9\u65f6\u6e05\u519b\u6b66\u58eb\uff0c\u5df2\u7eb7\u7eb7\u6251\u4e86\u4e0a\u6765\uff0c\u6768\u4e91\u806a\u8eab\u624b\u4f55\u7b49\u654f\u6377\uff0c\u770b\u60c5\u5f62\u4e0d\u5bf9\uff0c\u77ed\u5251\u6323\u7136\u51fa\u624b\uff0c \u4e00\u63a0\u6570\u4e08\uff0c\u53cd\u5411\u7eb3\u5170\u79c0\u5409\u6251\u53bb\uff0c\u51e0\u4e2a\u4e0a\u6765\u62e6\u963b\u7684\u519b\u5b98\uff0c\u5728\u4ed6\u4e3e\u624b\u6295\u8db3\u4e4b\u95f4\uff0c\u6216\u53d7\u77ed\u5251\u6240\u4f24\uff0c \u6216\u88ab\u4e86\u7a74\u9053\uff0c\u54ea\u91cc\u62e6\u963b\u5f97\u4f4f\uff1f\u970e\u773c\u4e4b\u95f4\uff0c\u4ed6\u4fbf\u6251\u5230\u7eb3\u5170\u79c0\u5409\u9762\u524d\uff0c\u7eb3\u5170\u79c0\u5409\u6b66\u529f\uff0c\u4e5f\u7740\u5b9e\u6765 \u5f97\uff0c\u8fce\u9762\u5c31\u662f\u4e00\u62f3\uff0c\u6768\u4e91\u806a\u8116\u5b50\u4e00\u95ea\uff0c\u4ed6\u8d81\u52bf\u5c31\u6765\u593a\u6768\u4e91\u806a\u7684\u77ed\u5251\uff0c\u6768\u4e91\u806a\u4f55\u7b49\u5389\u5bb3\uff0c\u624b\u8155 \u4e00\u7ffb\uff0c\u77ed\u5251\u76f4\u523a\u51fa\u53bb\u3002\u8fd9\u65f6\uff0c\u8033\u9645\u5ffd\u542c\u5f97\u7eb3\u5170\u5c0f\u59d0\u7684\u558a\u58f0\uff1a\u201c\u7238\u7238\uff0c\u7238\u7238\uff01\u201d\u6768\u4e91\u806a\u5fc3\u4e2d\u4e00 \u8f6f\uff0c\u7565\u8f6c\u624b\u8155\uff0c\u5251\u950b\u5728\u7eb3\u5170\u79c0\u5409\u9888\u8fb9\u659c\u523a\u800c\u8fc7\u3002\u7eb3\u5170\u79c0\u5409\u867d\u7136\u8eab\u7ecf\u767e\u6218\uff0c\u4f46\u8fd9\u65f6\u53ea\u89c9\u9888\u9879\u51c9 \u98d3\u98d3\u7684\uff0c\u51b7\u98ce\u6c81\u808c\uff0c\u4e5f\u5413\u5f97\u5931\u4e86\u4e09\u9b42\u4e03\u9b44\uff0c\u624b\u811a\u9178\u8f6f\u3002\u6768\u4e91\u806a\u5de6\u624b\u624b\u6307\u5982\u5728\u4ed6\u8170\u9645\u201c\u6d8c\u6cc9 \u7a74\u201d\u4e00\u70b9\uff0c\u7acb\u523b\u628a\u4ed6\u631f\u4e86\u8d77\u6765\uff0c\u5927\u58f0\u559d\u9053\uff1a\u82e5\u8981\u6027\u547d\uff0c\u8d76\u5feb\u8ba9\u6211\u51fa\u53bb\uff01\u201d\u6e05\u519b\u58eb\u5352\uff0c\u89c1\u4e3b\u5c06 \u88ab\u64d2\uff0c\u54ea\u6562\u4e71\u52a8\uff0c\u6768\u4e91\u806a\u4e00\u58f0\u957f\u5578\uff0c\u98de\u5954\u800c\u51fa\uff0c\u89d1\u51c6\u4e00\u5934\u9a8f\u9a6c\uff0c\u731b\u7136\u98de\u63a0\u4e0a\u53bb\uff0c\u5de6\u624b\u624b\u8098\u4e00 \u649e\uff0c\u5c31\u628a\u9a6c\u4e0a\u519b\u5b98\u649e\u8dcc\u4e0b\u53bb\uff0c\u53f3\u624b\u4ecd\u7136\u631f\u7d27\u7eb3\u5170\u79c0\u5409\uff0c\u7b56\u9a6c\u5954\u9a70\uff0c\u6e05\u5175\u6295\u9f20\u5fcc\u5668\uff0c\u4e0d\u6562\u653e \u7bad\uff0c\u53ea\u5f97\u4e5f\u7528\u5feb\u9a6c\u8ffd\u8d76\uff01 \u3000\u3000\u6768\u4e91\u806a\u9a6c\u8dd1\u5f97\u5feb\uff0c\u8f6c\u77ac\u5df2\u628a\u6e05\u5175\u629b\u5728\u540e\u9762\uff0c\u53ea\u6709\u4e00\u9a91\u9a6c\u7d27\u7d27\u8ddf\u7740\u540e\u9762\u3002\u6768\u4e91\u806a\u56de\u5934\u4e00 \u770b\uff0c\u53ea\u542c\u5f97\u6e05\u8106\u5982\u94f6\u94c3\u7684\u5973\u58f0\u53eb\u9053\uff1a\u201c\u4f60\u5df2\u9003\u5f97\u6027\u547d\uff0c\u8fd8\u631f\u6301\u6211\u7684\u7238\u7238\u505a\u4ec0\u4e48\uff1f\u201d\u8fd9\u5973\u7684\u6b63 \u662f\u524d\u51e0\u5929\u6551\u51fa\u81ea\u5df1\u6027\u547d\u7684\u7eb3\u5170\u660e\u6167\u5c0f\u59d0\uff01\u201d \u3000\u3000------------------ \u3000\u3000 \u626c\u5251\u8f69\u5c45\u58eb \u626b\u63cf\u6821\u5bf9  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u585e\u5916\u5947\u4fa0\u4f20\u300b\u2014\u2014\u7b2c\u56db\u56de\u3000\u5973\u4fa0\u98de\u7ea2\u5dfe \u6881\u7fbd\u751f\u300a\u585e\u5916\u5947\u4fa0\u4f20\u300b \u7b2c\u56db\u56de\u3000\u5973\u4fa0\u98de\u7ea2\u5dfe \u3000\u3000\u6768\u4e91\u806a\u6014\u4e86\u4e00\u6014\uff0c\u770b\u7740\u7eb3\u5170\u660e\u6167\u7b56\u9a6c\u98de\u9a70\u800c\u6765\uff0c\u58f0\u97f3\u98a4\u6296\uff0c\u795e\u8272\u51c4\u60f6\uff0c\u987f\u65f6\u5931\u6389\u4e86\u4e3b \u610f\u3002\u8fd9\u4e2a\u6562\u5728\u5341\u4e07\u519b\u4e2d\u6765\u53bb\u81ea\u5982\uff0c\u52c7\u6562\u679c\u51b3\u7684\u5947\u7537\u5b50\uff0c\u5982\u4eca\u7ed9\u4e00\u4e2a\u5c11\u5973\u54c0\u601c\u7684\u76ee\u5149\u6240\u60ca\u4f4f\uff0c \u601d\u60f3\u50cf\u4e00\u80a1\u6d6a\u6f6e\u91cd\u51fb\u7740\u53e6\u4e00\u80a1\u6d6a\u6f6e\uff0c\u4ed6\u60f3\u8d77\u88ab\u65e0\u8f9c\u6b3a\u8d1f\u51cc\u8650\u7684\u54c8\u8428\u514b\u4eba\uff0c\u800c\u81ea\u5df1\u6240\u631f\u4f4f\u7684\u6b63 \u662f\u54c8\u8428\u514b\u4eba\u7684\u5927\u5bf9\u5934\uff1b\u4ed6\u53c8\u60f3\u8d77\u5728\u5e10\u5e55\u4e2d\u6e29\u99a8\u7684\u51e0\u4e2a\u665a\u4e0a\uff0c\u60f3\u8d77\u81ea\u5df1\u7684\u6027\u547d\uff0c\u5c31\u662f\u8fd9\u4e2a\u5f02\u65cf \u5c11\u5973\u6551\u7684\u3002\u4ed6\u7a81\u7136\u52d2\u4f4f\u4e86\u9a6c\uff0c\u56de\u8fc7\u5934\u6765\uff0c\u4e00\u4f38\u624b\uff0c\u89e3\u5f00\u7eb3\u5170\u79c0\u5409\u7684\u7a74\u9053\uff0c\u5c06\u4ed6\u63b7\u5728\u5730\u4e0a\uff0c\u8fce \u7740\u7eb3\u5170\u660e\u6167\u8bf4\u9053\uff1a\u201c\u5c0f\u59d0\uff0c\u4f60\u7684\u7236\u4eb2\u5728\u8fd9\u91cc\uff0c\u4ed6\u4e1d\u6beb\u6ca1\u6709\u53d7\u4f24\uff0c\u4f60\u53ef\u653e\u5fc3\u4e86\u5427\uff01\u201d \u3000\u3000\u7eb3\u5170\u79c0\u5409\u5401\u5598\u7740\u6c14\uff0c\u671b\u7740\u5973\u513f\uff0c\u4e0d\u77e5\u9053\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\u3002\u7eb3\u5170\u660e\u6167\u5c06\u7236\u4eb2\u6276\u4e0a\u4e86\u9a6c\uff0c\u51b2\u7740 \u6768\u4e91\u806a\u8bf4\u9053\uff1a\u201c\u8c22\u8c22\u4f60\u3002\u201d\u6768\u4e91\u806a\u9053\uff1a\u201c\u7528\u4e0d\u7740\u8c22\uff01\u4f60\u6551\u4e86\u6211\u7684\u6027\u547d\uff0c\u6211\u8fd8\u4f60\u7684\u7236\u4eb2\uff0c\u6211\u4eec \u8c01\u4e5f\u4e0d\u6b20\u8c01\u7684\u6069\u60c5\uff01\u201d\u4e24\u817f\u7528\u529b\u4e00\u5939\uff0c\u9a8f\u9a6c\u5636\u9e23\uff0c\u5934\u4e5f\u4e0d\u56de\uff0c\u75be\u9a70\u53bb\u4e86\uff01 \u3000\u3000\u6768\u4e91\u806a\u53e3\u4e2d\u8bf4\u5f97\u90a3\u4e48\u65a9\u9489\u622a\u94c1\uff0c\u5fc3\u91cc\u5374\u662f\u5145\u6ee1\u6005\u60c6\u3002\u4ed6\u611f\u5230\u751f\u547d\u7684\u5145\u5b9e\uff0c\u53c8\u611f\u5230\u611f\u60c5\u7684 \u7a7a\u865a\uff01\u4ed6\u662f\u4e00\u4e2a\u82f1\u96c4\uff0c\u4f46\u5374\u4e0d\u662f\u4e00\u4e2a\u8d85\u4eba\uff0c\u4ed6\u9a71\u9010\u4e0d\u5f00\u5fc3\u5934\u7684\u5029\u5f71\uff0c\u4ed6\u4e0d\u6562\u60f3\u8d77\u5979\u662f\u201c\u4ec7\u4eba \u7684\u5973\u513f\u201d\uff0c\u7136\u800c\u8fd9\u5374\u662f\u4e00\u4e2a\u6b8b\u9177\u7684\u4e8b\u5b9e\uff1b\u90a3\u6837\u4e00\u4e2a\u6e29\u67d4\u660e\u7406\u7684\u5973\u5b50\uff0c\u5374\u6709\u4e00\u4e2a\u53cc\u624b\u6cbe\u6ee1\u8840\u8165 \u7684\u7236\u4eb2\u3002 \u3000\u3000\u6768\u4e91\u806a\u8ff7\u8ff7\u60d8\u60af\u7684\u7b56\u9a6c\u98de\u5954\uff0c\u5411\u5357\u7586\u9a70\u53bb\uff0c\u706b\u7ea2\u7684\u65e5\u5934\u6e10\u5411\u897f\u79fb\uff0c\u5929\u8fb9\u4e00\u62b9\u665a\u971e\uff0c\u6620\u7167 \u7740\u5927\u8349\u539f\uff0c\u53d1\u51fa\u971e\u8f89\u4e3d\u5f69\u3002\u6768\u4e91\u806a\u5583\u5583\u81ea\u8bed\u9053\uff1a\u201c\u767d\u5929\u5c31\u5feb\u8fc7\u53bb\uff0c\u9ed1\u591c\u53c8\u8981\u6765\u4e86\uff01\u201d\u84e6\u7136\u95f4 \u4ed6\u89c9\u5f97\u53c8\u5026\u53c8\u997f\uff0c\u4ed6\u4eca\u65e9\u5728\u5e03\u817e\u6cb3\u7554\uff0c\u593a\u547d\u4e4b\u65f6\uff0c\u62a2\u4e86\u4e00\u4e2a\u519b\u5b98\u7684\u9a6c\uff0c\u5374\u6ca1\u6709\u62a2\u4ed6\u7684\u5e72\u7cae\u3002 \u5728\u5fc3\u91cc\u6240\u601d\uff0c\u8ff7\u60d8\u7b56\u9a6c\u4e4b\u9645\uff0c\u9965\u997f\uff0c\u50cf\u4e00\u4e2a\u9690\u853d\u591a\u5e74\u7684\u654c\u4eba\uff0c\u6ca1\u6709\u51fa\u6765\u88ad\u51fb\uff1b\u73b0\u5728\u7ea2\u65e5\u897f \u79fb\uff0c\u201c\u9690\u853d\u7684\u654c\u4eba\u201d\u51fa\u6765\u4e86\uff01\u4ed6\u611f\u5230\u9965\u997f\u7684\u88ad\u51fb\u4e86\uff01 \u3000\u3000\u4e00\u9635\u665a\u98ce\u5439\u6765\uff0c\u6768\u4e91\u806a\u4f9d\u7a00\u542c\u5f97\u524d\u9762\u6709\u9a6c\u94c3\u4e4b\u58f0\uff0c\u5fc3\u60f3\uff1a\u82e5\u78b0\u5230\u5ba2\u5546\u5c31\u597d\u4e86\uff0c\u4ed6\u4f0f\u5728\u9a6c \u80cc\u4e0a\uff0c\u8f7b\u62cd\u5b83\u7684\u9888\u9879\uff0c\u90a3\u9a6c\u9aa4\u7684\u653e\u5f00\u56db\u8e44\uff0c\u98ce\u9a70\u7535\u63a3\u822c\u8ffd\u4e0a\u53bb\uff0c\u8ffd\u4e86\u4e00\u4f1a\uff0c\u89c1\u7740\u524d\u9762\u6709\u4e24\u5339 \u767d\u9a6c\uff0c\u9a6c\u4e0a\u4eba\u9a91\u672f\u7cbe\u7edd\uff0c\u6768\u4e91\u806a\u4eba\u5026\u9a6c\u4e4f\uff0c\u867d\u7136\u62fc\u547d\u51b2\u53bb\uff0c\u5374\u603b\u662f\u8ffd\u4e0d\u4e0a\u4ed6\u4eec\u3002 \u3000\u3000\u6768\u4e91\u806a\u6b63\u5728\u5927\u611f\u5931\u671b\uff0c\u5ffd\u7136\u524d\u9762\u90a3\u4e24\u9a91\u9a6c\u653e\u6162\u4e86\u811a\u6b65\uff0c\u5e76\u8f94\u800c\u884c\uff0c\u6768\u4e91\u806a\u5927\u559c\uff0c\u50ac\u9a6c\u8d76 \u4e0a\uff0c\u53ea\u89c1\u4e00\u9a91\u9a6c\u4e0a\uff0c\u662f\u4e00\u4e2a\u4fca\u4fcf\u7684\u59d1\u5a18\uff0c\u5934\u4e0a\u5305\u7740\u4e00\u6761\u7ea2\u4e2d\uff0c\u8fce\u98ce\u98d8\u8361\uff1b\u53e6\u4e00\u9a91\u9a6c\u4e0a\uff0c\u5219\u662f \u4e00\u4e2a\u5e74\u9752\u7684\u5c0f\u4f19\u5b50\u3002\u6768\u4e91\u806a\u6b63\u5f85\u5f00\u58f0\u76f8\u5524\uff0c\u5ffd\u542c\u5f97\u665a\u98ce\u4e2d\u65ad\u65ad\u7eed\u7eed\u98d8\u6765\u7684\u8bdd\u8bed\uff1a \u3000\u3000\u201c\u98de\u7ea2\u5dfe\uff0c\u4f60\u4e3a\u4ec0\u4e48\u8981\u50ac\u7740\u9a6c\u513f\u8d76\u8def\u5462\uff1f\u8ba9\u6211\u591a\u6d3b\u4e00\u523b\uff0c\u2026\u2026\u4f60\u4e0d\u4e5f\u662f\u6ca1\u6709\u5e78\u798f \u5417\uff1f\u2026\u2026\u54ce\uff0c\u98de\u7ea2\u5dfe\uff0c\u4f60\u771f\u7684\u8fd9\u6837\u5fcd\u5fc3\u5417\uff1f\u201d \u3000\u3000\u524d\u9762\u98d8\u6765\u4e86\u4e00\u58f0\u53f9\u606f\uff0c\u5145\u6ee1\u7740\u5973\u6027\u7684\u6e29\u67d4\uff0c\u4e24\u5339\u9a6c\u66f4\u6162\u4e0b\u6765\u4e86\u3002 \u3000\u3000\u6768\u4e91\u806a\u5fc3\u5934\u4e00\u9707\uff0c\u201c\u98de\u7ea2\u5dfe\uff1f\u96be\u9053\u524d\u9762\u7684\u5c11\u5973\uff0c\u5c31\u662f\u8349\u539f\u4e0a\u9a70\u540d\u7684\u5973\u82f1\u96c4\uff1f\u98de\u7ea2\u5dfe\u662f\u7f57 \u5e03\u65cf\u8001\u82f1\u96c4\u5510\u52aa\u7684\u5973\u513f\uff0c\u771f\u540d\u53eb\u505a\u54c8\u739b\u96c5\uff0c\u5979\u9a91\u6728\u5251\u672f\u4e24\u4ff1\u7cbe\u5999\uff0c\u5e38\u9a70\u8058\u4e8e\u5929\u5c71\u5357\u5317\uff0c\u50cf\u6768 \u4e91\u806a\u4e00\u6837\uff0c\u4e5f\u662f\u585e\u5916\u7684\u4f20\u5947\u4eba\u7269\uff0c\u56e0\u5979\u559c\u6b22\u62ab\u7740\u7ea2\u5dfe\uff0c\u5728\u9a6c\u80cc\u5954\u9a70\uff0c\u56e0\u6b64\u5f97\u4e86\u98de\u7ea2\u5dfe\u8fd9\u4e2a\u7ef0 \u53f7\u3002\u6768\u4e91\u806a\u4e45\u95fb\u5979\u7684\u58f0\u540d\uff0c\u53ef\u662f\u519b\u65c5\u5306\u5306\uff0c\u4ece\u672a\u4e0e\u5979\u89c1\u8fc7\u9762\u3002 \u3000\u3000\u6768\u4e91\u806a\u867d\u7136\u9965\u997f\uff0c\u4f46\u4e5f\u6682\u65f6\u5fcd\u4f4f\uff0c\u653e\u677e\u4e86\u9a6c\uff0c\u542c\u542c\u4ed6\u4eec\u5728\u8bf4\u4ec0\u4e48\u3002\u8fc7\u4e86\u4e00\u4f1a\uff0c\u53ea\u89c1\u98de\u7ea2 \u5dfe\u5c06\u76ae\u97ad\u4e00\u6325\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u518d\u7ed9\u6211\u5531\u4e00\u9996\u6b4c\uff01\u201d \u3000\u3000\u90a3\u5e74\u9752\u7684\u5c0f\u4f19\u5b50\u5439\u7740\u4e00\u6839\u82a6\u7b19\uff0c\u58f0\u97f3\u975e\u5e38\u51c4\u695a\uff0c\u53c8\u597d\u50cf\u5145\u6ee1\u60e7\u6015\u548c\u5931\u671b\uff0c\u5439\u4e86\u4e00\u9635\uff0c\u5531 \u8d77\u6765\u9053\uff1a \u3000\u3000\u201c\u59d1\u5a18\u5440\uff01 \u3000\u3000\u8bb0\u5f97\u5728\u90a3\u5feb\u4e50\u7684\u65f6\u8fb0\uff0c \u3000\u3000\u4f60\u8bf4\u4f60\u7684\u7231\u60c5\u2014\u2014\u6bd4\u6d77\u8fd8\u8981\u6df1\uff01 \u3000\u3000\u4f60\u600e\u80fd\u8fd9\u4e48\u5fcd\u5fc3\uff1f \u3000\u3000\u8981\u4f24\u5bb3\u4f60\u7684\u7231\u4eba\uff1f \u3000\u3000\u4f60\u79f0\u8d5e\u6211\u7684\u6b4c\u58f0\uff0c \u3000\u3000\u8bf4\u662f\u8349\u539f\u4e0a\u7684\u591c\u83ba\uff0c \u3000\u3000\u5b83\u6b4c\u9882\u4f60\u7684\u7f8e\u4e3d\u548c\u806a\u660e\uff0c \u3000\u3000\u8fd9\u7f8e\u5999\u7684\u6b4c\u58f0\uff0c \u3000\u3000\u4f60\u5f80\u54ea\u91cc\u5bfb\uff1f \u3000\u3000\u4f60\u600e\u80fd\u8fd9\u6837\u5fcd\u5fc3\uff1f \u3000\u3000\u628a\u6211\u8d76\u4e0a\u6b7b\u4ea1\u7684\u65c5\u7a0b\uff01\u201d \u3000\u3000\u6768\u4e91\u806a\u611f\u5230\u4e00\u9635\u98a4\u6817\uff0c\u4ed6\u7a81\u7136\u60f3\u8d77\u4e86\u7eb3\u5170\u660e\u6167\uff0c\u4ed6\u60f3\uff1a\u96be\u5230\u98de\u7ea2\u5dfe\u548c\u8fd9\u4e2a\u5e74\u9752\u5c0f\u4f19\u5b50\uff0c \u4e5f\u50cf\u4ed6\u548c\u7eb3\u5170\u660e\u6167\u4e00\u6837\uff0c\u662f\u7231\u4eba\u548c\u654c\u4eba\uff1f\u4f46\u770b\u6765\u53c8\u4e0d\u4f3c\u5440\uff1f\u6b63\u601d\u7591\u95f4\uff0c\u90a3\u5c11\u5e74\u4e58\u7740\u98de\u7ea2\u5dfe\u5982 \u9189\u5982\u75f4\u4e4b\u9645\uff0c\u7a81\u7136\u4e00\u4e2a\u62c9\u9a6c\uff0c\u7eb5\u9a6c\u98de\u9a70\uff0c\u98de\u7ea2\u5dfe\u67f3\u7709\u5012\u7ad6\uff0c\u957f\u97ad\u4e00\u6325\uff0c\u53eb\u58f0\uff1a\u201c\u62bc\u4e0d\u5e90\uff0c\u4f60 \u627e\u6b7b\uff01\u201d\u5c11\u5e74\u7684\u9a6c\u521a\u4e00\u56de\u5934\uff0c\u98de\u7ea2\u5dfe\u957f\u97ad\u4e00\u5377\uff0c\u5c31\u628a\u4ed6\u5377\u4e86\u56de\u6765\u3002\u6768\u4e91\u806a\u201c\u554a\u5440\u201d\u4e00\u58f0\uff0c\u53eb \u4e86\u8d77\u6765\uff0c\u98de\u7ea2\u5dfe\u56de\u5934\u4e00\u671b\u95ee\u9053\uff0c\u201c\u4f60\u662f\u8c01\uff1f\u201d\u6768\u4e91\u806a\u9053\uff1a\u201c\u6211\u662f\u4e00\u4e2a\u8ff7\u8def\u7684\u65c5\u4eba\u3002\u201d\u98de\u7ea2\u5dfe \u9053\uff1a\u201c\u65e2\u7136\u8fd9\u6837\uff0c\u4f60\u8d76\u4f60\u7684\u8def\u5427\uff0c\u522b\u591a\u7ba1\u95f2\u4e8b\uff01\u201d\u6768\u4e91\u806a\u7eb5\u9a6c\u4e0a\u524d\uff0c\u62b1\u62f3\u8bf4\u9053\uff1a\u201c\u5973\u82f1\u96c4\uff0c \u6055\u6211\u7c97\u9c81\u5766\u7387\uff0c\u6211\u7684\u4e8e\u7cae\u548c\u6c34\u90fd\u6ca1\u6709\u5566\uff01\u4f60\u82e5\u6709\u591a\u7684\u8bdd\uff0c\u80fd\u4e0d\u80fd\u7ed9\u6211\u4e00\u70b9\uff1f\u201d\u98de\u7ea2\u5dfe\u671b\u4e86\u6768 \u4e91\u806a\u4e00\u773c\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u4e2a\u6c49\u4eba\u5f88\u597d\uff0c\u4e0d\u4f1a\u505a\u4f5c\u3002\u201d\u968f\u5373\u4ece\u76ae\u888b\u91cc\u53d6\u51fa\u4e00\u5305\u5e72\u7cae\uff0c\u8fde\u540c\u6c34\u58f6\u629b \u8fc7\u53bb\u9053\uff1a\u201c\u8fd9\u5305\u5e72\u7cae\u7ed9\u4f60\uff0c\u6c34\u53ef\u4e0d\u80fd\u559d\u5b8c\u3002\u201d\u6768\u4e91\u806a\u559d\u4e86\u51e0\u53e3\u6c34\uff0c\u9001\u4e0b\u5e72\u7cae\uff0c\u5c06\u6c34\u58f6\u629b\u4e86\u8fc7 \u53bb\u9053\uff1a\u201c\u8c22\u8c22\u59d1\u5a18\uff01\u201d\u98de\u7ea2\u5dfe\u9053\uff1a\u201c\u597d\uff0c\u4f60\u8d70\u5427\uff01\u6211\u4e0d\u8981\u548c\u4f60\u4e00\u8def\u3002\u201d\u6768\u4e91\u806a\u5e94\u4e86\u4e00\u58f0\uff0c\u7b56 \u9a6c\u659c\u523a\u51b2\u51fa\uff0c\u8fc7\u4e86\u4e00\u4f1a\u53ea\u89c1\u98de\u7ea2\u5dfe\u548c\u90a3\u5c11\u5e74\uff0c\u53c8\u7b56\u9a6c\u98de\u9a70\uff0c\u970e\u5ffd\u8d76\u8fc7\u4ed6\u7684\u524d\u5934\uff0c\u98de\u7ea2\u5dfe\u4e0d\u65ad \u6325\u97ad\uff0c\u4f3c\u4e4e\u5728\u5a01\u80c1\u90a3\u4e2a\u9752\u5e74\u5feb\u8d70\uff01 \u3000\u3000\u6768\u4e91\u806a\u6ee1\u8179\u72d0\u7591\uff0c\u5341\u5206\u4e0d\u89e3\u3002\u5fc3\u60f3\uff1a\u8fd9\u98de\u7ea2\u5dfe\u5728\u5357\u7586\u5927\u5927\u6709\u540d\uff0c\u4e0d\u7ba1\u5979\u662f\u600e\u4e48\u56de\u4e8b\uff0c\u6211 \u90fd\u8981\u63a2\u4e2a\u7a76\u7adf\u3002\u8981\u662f\u5f97\u5979\u5408\u4f5c\uff0c\u62b5\u6297\u6e05\u5175\uff0c\u4e5f\u591a\u4e00\u81c2\u4e4b\u529b\u3002\u6768\u4e91\u806a\u4e5f\u662f\u9a91\u672f\u6781\u7cbe\uff0c\u6674\u6697\u8ddf\u5728 \u98de\u7ea2\u5dfe\u540e\u9762\uff0c\u4fdd\u6301\u7740\u5188\uff01\u770b\u5f97\u89c1\u7684\u8ddd\u79bb\uff0c\u8d70\u4e86\u4e0d\u4e45\uff0c\u5929\u8272\u6e10\u9ed1\uff0c\u98de\u7ea2\u5dfe\u4f3c\u4e4e\u5f88\u719f\u9053\u8def\uff0c\u5f84\u81ea \u7b56\u9a6c\u8d70\u5230\u4e00\u4e2a\u53e4\u5821\u5792\u524d\u9762\uff0c\u5c06\u9a6c\u7cfb\u5728\u8def\u65c1\u5d16\u77f3\u4e0a\uff0c\u548c\u90a3\u5c11\u5e74\u643a\u624b\u8fdb\u5165\u5821\u5792\u53bb\u4e86\u3002 \u3000\u3000\u6768\u4e91\u806a\u5728\u5916\u9762\u515c\u4e86\u4e00\u4e2a\u5708\u5b50\uff0c\u5176\u5730\u5df2\u8131\u51fa\u6c99\u6f20\uff0c\u8349\u539f\u4e0a\u6c34\u6cca\u5e76\u4e0d\u7a00\u5c11\uff0c\u6768\u4e91\u806a\u627e\u5230\u4e86 \u6c34\uff1b\u8ba9\u9a6c\u9971\u559d\u4e86\u4e00\u987f\uff0c\u81ea\u5df1\u4e5f\u996e\u4e86\u51e0\u53e3\u6c34\uff0c\u9001\u4e0b\u5269\u4f59\u7684\u5e72\u7cae\u3002\u517b\u4e86\u4e00\u4f1a\u795e\uff0c\u5c06\u9a6c\u7cfb\u5728\u6c34\u6cca\u4e4b \u6ee8\uff0c\u65bd\u5c55\u8f7b\u529f\uff0c\u591c\u63a2\u53e4\u5821\u3002 \u3000\u3000\u5176\u65f6\u5df2\u662f\u4e00\u94a9\u65b0\u6708\u6e10\u8fd1\u4e2d\u5929\uff0c\u6768\u4e91\u806a\u501f\u7740\u6708\u5149\uff0c\u770b\u90a3\u53e4\u5821\u4e0a\u9762\uff0c\u523b\u6709\u201c\u70fd\u706b\u53f0\u201d\u4e09\u5b57\uff0c \u6768\u4e91\u806a\u901a\u6653\u5386\u53f2\uff0c\u77e5\u9053\u8fd9\u662f\u4e2d\u56fd\u53e4\u4ee3\u884c\u519b\u6240\u7b51\uff0c\u7528\u6728\u548c\u91c9\u571f\u5efa\u6210\u9ad8\u9ad8\u7684\u91d1\u5b57\u5854\u5f62\u7684\u4e1c\u897f\uff0c\u8349 \u539f\u6c99\u6f20\uff0c\u9053\u8def\u6613\u8ff7\uff0c\u53e4\u65f6\u7684\u519b\u961f\u5c31\u7b51\u6b64\u6765\u8868\u793a\u5404\u5730\u7684\u8ddd\u79bb\uff0c\u517c\u4f5c\u201c\u6307\u8def\u6807\u201d\u548c\u201c\u4f11\u606f\u6240\u201d\u4e4b \u7528\uff0c\u6709\u4e8b\u4e4b\u65f6\uff0c\u5728\u4e0a\u9762\u7684\u620d\u5352\uff0c\u71c3\u8d77\u70fd\u706b\uff0c\u53c8\u53ef\u4e92\u76f8\u6551\u5e94\u3002\u65b0\u7586\u7684\u70fd\u706b\u53f0\u591a\u5efa\u7b51\u4e8e\u5510\u65f6\uff0c\u5317 \u7586\u751a\u5c11\uff0c\u5357\u7586\u8f83\u591a\uff0c\u52a0\u4ee5\u65e5\u4e45\u5e74\u6df1\uff0c\u5927\u534a\u574d\u584c\uff0c\u82e5\u975e\u719f\u6089\u9053\u8def\u7684\u4eba\uff0c\u5f88\u96be\u7b97\u51c6\u5bbf\u5934\uff0c\u5229\u7528 \u201c\u70fd\u706b\u53f0\u201d\u6b47\u606f\u3002 \u3000\u3000\u6768\u4e91\u806a\u53cc\u8db3\u4e00\u70b9\uff0c\u8c61\u5927\u96c1\u822c\u63a0\u4e0a\u5821\u5792\uff0c\u8fd9\u5821\u5792\u5171\u6709\u4e24\u5c42\uff0c\u4e0a\u5c42\u9732\u5929\uff0c\u53ef\u4f9b\u620c\u5352\u773a\u671b\uff0c\u4e0b \u5c42\u65b9\u662f\u4eba\u9a6c\u5b89\u6b47\u4e4b\u5904\u3002\u6768\u4e91\u806a\u5230\u4e86\u4e0a\u5c42\uff0c\u8e72\u4e86\u4e0b\u6765\uff0c\u77ed\u5251\u8f7b\u8f7b\u4e00\u63d2\uff0c\u7a7f\u4e86\u4e00\u4e2a\u5c0f\u6d1e\uff0c\u4f0f\u4e0b\u5077 \u770b\uff0c\u53ea\u89c1\u98de\u7ea2\u5dfe\u548c\u90a3\u5c11\u5e74\u6b63\u5728\u4e0b\u9762\uff0c\u4ed6\u4eec\u53d6\u5e72\u8349\u70b9\u8d77\u4e86\u4e00\u5806\u706b\uff0c\u4f3c\u662f\u8c08\u5174\u5f88\u6d53\u3002 \u3000\u3000\u98de\u7ea2\u5dfe\u89c1\u4e0a\u9762\u6709\u4e9b\u6ce5\u571f\u5f7c\u5f7c\u843d\u4e0b\uff0c\u77a7\u4e86\u4e00\u77a7\uff0c\u5e76\u6ca1\u53d1\u73b0\u4ec0\u4e48\uff0c\u9053\uff1a\u201c\u8fd9\u5821\u5792\u4e5f\u592a\u53e4\u8001 \u4e86\uff0c\u98ce\u4e00\u5439\u6ce5\u4e0a\u5c31\u5265\u843d\u4e0b\u6765\u3002\u201d\u4f46\u5979\u8fd8\u4e0d\u653e\u5fc3\uff0c\u968f\u624b\u4e00\u6325\uff0c\u6768\u4e91\u806a\u6025\u95ea\u8fc7\u4e00\u8fb9\uff0c\u7528\u638c\u98ce\u4e00 \u9707\uff0c\u53ea\u89c1\u51e0\u6839\u94f6\u9488\u8dcc\u5728\u9732\u53f0\u4e4b\u4e0a\u3002\u5fc3\u60f3\uff1a\u201c\u90a3\u98de\u7ea2\u5dfe\u597d\u5389\u5bb3\uff01\u5979\u4e5f\u63d0\u9632\u4e0a\u9762\u7a7f\u6709\u5c0f\u5b54\uff0c\u6709\u4eba \u5077\u770b\uff0c\u6240\u4ee5\u653e\u51fa\u98de\u9488\u3002\u82e5\u662f\u6211\u4e0d\u907f\u5f00\uff0c\u5c31\u778e\u4e86\u53cc\u76ee\u3002\u201d \u3000\u3000\u6768\u4e91\u806a\u9707\u843d\u94f6\u9488\uff0c\u518d\u4f0f\u4e0b\u6765\u3002\u98de\u7ea2\u5dfe\u89c1\u6beb\u65e0\u52a8\u9759\uff0c\u4e5f\u4e0d\u518d\u6ce8\u610f\u3002\u6768\u4e91\u806a\u53ea\u542c\u5f97\u98de\u7ea2\u5dfe\u559d \u9053\uff1a\u201c\u62bc\u4e0d\u5e90\uff0c\u4f60\u8fd8\u6709\u4ec0\u4e48\u8bdd\u8bf4\uff1f\u201d\u90a3\u5524\u505a\u62bc\u4e0d\u5e90\u7684\u5c11\u5e74\u9053\uff1a\u201c\u98de\u7ea2\u5dfe\uff0c\u4f60\u600e\u51c0\u542c\u522b\u4eba\u7684\u8bf4 \u8bdd\uff0c\u6211\u7684\u8bf4\u8bdd\uff1f\u4f60\u662f\u6211\u6700\u7231\u6700\u7231\u7684\u4eba\uff0c\u6211\u600e\u80fd\u6697\u5bb3\u4f60\u7684\u7236\u4eb2\uff1f\u8001\u82f1\u96c4\u5728\u963f\u514b\u82cf\u8349\u539f\uff0c\u9aa4\u9047\u6e05 \u5175\uff0c\u53d7\u4e86\u5305\u56f4\uff0c\u6fc0\u6218\u4e09\u5929\u4e09\u591c\u6211\uff1b\u90fd\u966a\u7740\u4ed6\u8001\u4eba\u5bb6\uff0c\u540e\u6765\u6e05\u5175\u4eba\u591a\uff0c\u7834\u4e86\u6211\u4eec\u7684\u9635\u5f62\uff0c\u51b2\u8fdb \u8001\u82f1\u96c4\u7684\u5e10\u7bf7\uff0c\u628a\u4ed6\u6740\u6b7b\uff0c\u6211\u75db\u5fc3\u4e4b\u6781\uff01\u4f60\u600e\u80fd\u602a\u6211\uff1f\u201d \u3000\u3000\u98de\u7ea2\u5dfe\u9053\uff1a\u201c\u80e1\u8bf4\uff0c\u6211\u7684\u7236\u4eb2\u4f55\u7b49\u82f1\u96c4\uff0c\u5c82\u6709\u540c\u4e00\u5e10\u7bf7\uff0c\u4f60\u80fd\u9003\u4ed6\u5374\u4e0d\u80fd\u9003\u51fa\uff1f\u800c\u4e14\u6211 \u542c\u5f97\u957f\u8001\u8bf4\uff0c\u4ed6\u6709\u51ed\u6709\u8bc1\uff0c\u8bc1\u5b9e\u662f\u4f60\u5e26\u9886\u5175\u591c\u88ad\uff0c\u5e76\u5c06\u4ed6\u6697\u5bb3\u7684\uff01\u518d\u8bf4\uff0c\u5982\u4f60\u4e0d\u662f\u505a\u8d3c\u5fc3 \u865a\uff0c\u4e3a\u4ec0\u4e48\u8fdc\u8fdc\u9003\u907f\uff0c\u4e0d\u6562\u56de\u5230\u90e8\u843d\uff1f\u201d \u3000\u3000\u62bc\u4e0d\u5e90\u5ffd\u7136\u62bd\u564e\u8d77\u6765\uff0c\u5e26\u7740\u54ed\u58f0\u8bf4\u9053\uff1a\u201c\u98de\u7ea2\u5dfe\u5440\uff0c\u4f60\u600e\u80fd\u4e0d\u4fe1\u6211\uff0c\u4f60\u662f\u660e\u7406\u7684\u4eba\uff0c\u4f60 \u60f3\u60f3\u770b\uff0c\u4f60\u7236\u4eb2\u662f\u6211\u4eec\u4e00\u65cf\u7684\u5934\u9886\uff0c\u6e05\u5175\u591c\u88ad\uff0c\u5f53\u7136\u5148\u8981\u6349\u4ed6\u3002\u6211\u4e0d\u548c\u4ed6\u4e00\u9053\u6b7b\uff0c\u662f\u6211\u4e0d \u5bf9\uff0c\u6211\u505a\u61e6\u592b\uff0c\u6211\u4e0d\u53cd\u6297\u3002\u4f46\u4f60\u8981\u8bf4\u6211\u6697\u5bb3\u4ed6\uff0c\u90a3\u5374\u662f\u592a\u51a4\u6789\u6211\u4e86\u3002\u4f60\u77e5\u9053\u65cf\u91cc\u7684\u51e0\u4e2a\u957f\u8001 \u90fd\u548c\u6211\u4e0d\u548c\uff0c\u4ed6\u4eec\u9677\u5bb3\u6211\uff0c\u6240\u4ee5\u6211\u4e0d\u6562\u56de\u6765\u3002\u4f46\u4f60\u6765\u6349\u6211\uff0c\u6211\u4e0d\u662f\u4eb2\u81ea\u6765\u89c1\u4f60\u4e86\u5417\uff1f\u98de\u7ea2 \u5dfe\uff0c\u4f60\u662f\u8ba9\u6211\u53bb\u9001\u6b7b\u5440\uff01\u201d \u3000\u3000\u8fd9\u65f6\u98de\u7ea2\u5dfe\u4f3c\u4e4e\u6709\u70b9\u610f\u52a8\u4e86\uff0c\u58f0\u8c03\u4e5f\u7f13\u548c\u4e86\u8bb8\u591a\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u62bc\u4e0d\u5e90\uff0c\u957f\u8001\u8bf4\uff0c\u4ed6\u4eec \u6709\u51ed\u6709\u8bc1\u5462\uff01\u4f60\u548c\u6211\u56de\u5230\u90e8\u843d\u53bb\u5427\u3002\u5982\u679c\u4ed6\u4eec\u8bef\u4f1a\u7684\u8bdd\uff0c\u6211\u8bf7\u4ed6\u4eec\u9976\u4f60\u4fbf\u662f\u3002\u201d\u62bc\u4e0d\u5e90\u9053\uff1a \u201c\u957f\u8001\u6709\u4ec0\u4e48\u51ed\u8bc1\uff0c\u8bf4\u6211\u6697\u5bb3\u65cf\u957f\uff1f\u201d\u98de\u7ea2\u5dfe\u9053\uff1a\u201c\u4f60\u4eec\u53d7\u5305\u56f4\u65f6\uff0c\u6211\u6b63\u53bb\u7f57\u5e03\u6cca\u53bb\u8054\u7edc\uff0c \u6211\u8fd8\u672a\u56de\u5230\u90e8\u843d\uff0c\u5c31\u5f97\u5230\u957f\u8001\u62a5\u4fe1\uff0c\u8981\u6211\u5148\u6349\u4f60\u4e86\u3002\u201d\u62bc\u4e0d\u5e90\u9053\uff1a\u201c\u90a3\u4f60\u4e5f\u8fd8\u672a\u89c1\u5230\u4ec0\u4e48\u51ed \u8bc1\uff0c\u600e\u80fd\u8f7b\u4fe1\u3002\u98de\u7ea2\u5dfe\u5440\uff0c\u4f60\u653e\u6211\u8d70\u5427\uff01\u8981\u4e0d\u7136\u6211\u548c\u4f60\u4e00\u9053\u5230\u8349\u539f\u98d8\u6cca\u53bb\uff0c\u5929\u5929\u665a\u4e0a\uff0c\u7ed9\u4f60 \u5531\u6b4c\uff01\u201d\u98de\u7ea2\u5dfe\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u7684\u957f\u8001\u662f\u6b63\u76f4\u7684\u4eba\u3002\u8bf4\u4ec0\u4e48\u4f60\u4e5f\u8981\u56de\u53bb\u548c\u4ed6\u4eec\u5bf9\u8d28\uff01\u201d\u5979\u8bdd\u867d \u5982\u6b64\uff0c\u53ef\u662f\u58f0\u8c03\u5df2\u66f4\u67d4\u548c\u3002\u62bc\u4e0d\u5e90\u53c8\u53d6\u51fa\u82a6\u7b19\u5439\u4e86\u8d77\u6765\uff0c\u5439\u5b8c\u4e00\u66f2\uff0c\u8f7b\u8f7b\u8bf4\u9053\uff1a\u201c\u98de\u7ea2\u5dfe\uff0c \u4f60\u8fd8\u7231\u6211\u5417\uff1f\u201d \u3000\u3000\u6768\u4e91\u806a\u6b63\u542c\u5f97\u51fa\u795e\uff0c\u5ffd\u7136\u5821\u5792\u5916\u597d\u50cf\u6709\u811a\u6b65\u4e4b\u58f0\u3002\u6768\u4e91\u806a\u8033\u76ee\u4f55\u7b49\u806a\u654f\uff0c\u987e\u4e0d\u5f97\u518d\u542c\uff0c \u7ad9\u4e86\u8d77\u6765\u5f80\u5916\u4e00\u77a7\uff0c\u53ea\u89c1\u56db\u6761\u4eba\u5f71\uff0c\u5df2\u8feb\u8fd1\u5821\u5792\u3002\u5c31\u5728\u6b64\u9645\uff0c\u4e0b\u9762\u98de\u7ea2\u5dfe\u4e00\u58f0\u51b7\u7b11\uff0c\u559d\u9053\uff0c \u201c\u6291\u4e0d\u5e90\uff0c\u4f60\u4e0d\u8bb8\u52a8\u3002\u6211\u770b\u662f\u4ec0\u4e48\u4eba\u6562\u6765\u88ad\u51fb\u59d1\u5a18\uff01\u201d \u3000\u3000------------------ \u3000\u3000 \u626c\u5251\u8f69\u5c45\u58eb \u626b\u63cf\u6821\u5bf9  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u585e\u5916\u5947\u4fa0\u4f20\u300b\u2014\u2014\u7b2c\u4e94\u56de\u3000\u53e4\u5821\u591c\u6218 \u6881\u7fbd\u751f\u300a\u585e\u5916\u5947\u4fa0\u4f20\u300b \u7b2c\u4e94\u56de\u3000\u53e4\u5821\u591c\u6218 \u3000\u3000\u90a3\u56db\u4e2a\u591c\u884c\u4eba\u6b63\u884c\u8fd1\u5821\u5792\uff0c\u5ffd\u89c1\u5821\u95e8\u500f\u5730\u6253\u5f00\u3002\u591c\u8272\u6c89\u51a5\uff0c\u4e00\u6761\u7ea2\u4e2d\u8fce\u98ce\u98d8\u62c2\uff0c\u663e\u5f97\u7279 \u522b\u9c9c\u8273\u593a\u76ee\u3002\u98de\u7ea2\u5dfe\u5de6\u624b\u6301\u7740\u4e00\u6761\u8f6f\u97ad\uff0c\u53f3\u624b\u62ff\u7740\u4e00\u67c4\u5b9d\u5251\uff0c\u4e00\u58f0\u4e0d\u54cd\uff0c\u7ad9\u5728\u95e8\u7684\u6b63\u4e2d\uff0c\u5c31 \u5982\u53e4\u4ee3\u4e00\u4e2a\u5973\u795e\u7684\u77f3\u818f\u96d5\u50cf\u3002\u5927\u6f20\u4e4b\u591c\uff0c\u5bd2\u661f\u95ea\u95ea\uff0c\u886c\u7740\u8fd9\u4e2a\u5c11\u5973\u51b7\u8273\u7684\u5bb9\u989c\uff0c\u4ee4\u4eba\u4e0d\u671f\u7136 \u7684\u611f\u5230\u4e00\u80a1\u5bd2\u610f\u3002\u6768\u4e91\u806a\u4f0f\u5728\u5821\u5792\u4e0a\u5c42\uff0c\u5411\u4e0b\u89c2\u671b\uff0c\u5fc3\u60f3\uff1a\u5148\u770b\u770b\u98de\u7ea2\u5dfe\u7684\u6280\u827a\u5982\u4f55\u3002 \u3000\u3000\u90a3\u56db\u4e2a\u591c\u884c\u4eba\u9aa4\u89c1\u98de\u7ea2\u5dfe\u4ed7\u5251\u73b0\u8eab\uff0c\u53cd\u7ed9\u6151\u4f4f\u4e86\uff0c\u4e00\u65f6\u4e0d\u77e5\u52a8\u624b\u3002\u98de\u7ea2\u5dfe\u7a81\u7684\u51b7\u7b11\u4e00 \u58f0\uff0c\u5de6\u624b\u9a6c\u97ad\u5237\u7684\u4e00\u54cd\uff0c\u4e00\u4e2a\u591c\u884c\u4eba\u7adf\u7ed9\u5377\u4e86\u8fc7\u53bb\uff0c\u98de\u7ea2\u5dfe\u5411\u5916\u4e00\u6325\uff0c\u628a\u90a3\u4e2a\u4eba\u629b\u51fa\u6570\u4e08\u5f00 \u5916\uff0c\u5934\u7834\u8840\u6d41\uff0c\u8fd9\u624d\u9a82\u9053\uff1a\u201c\u5148\u628a\u4f60\u8fd9\u5978\u7ec6\u51fb\u6389\uff01\u201d\u539f\u6765\u8fd9\u4eba\u662f\u7f57\u5e03\u65cf\u7684\u4eba\uff0c\u90a3\u53e6\u5916\u4e09\u4eba\u5219 \u662f\u6e05\u519b\u6b66\u58eb\uff0c\u98de\u7ea2\u5dfe\u4e00\u89c1\u5c31\u77e5\u4ed6\u5e26\u9886\u6e05\u519b\u6b66\u58eb\u6765\u6349\u62ff\u81ea\u5df1\u7684\u3002 \u3000\u3000\u98de\u7ea2\u5dfe\u51fa\u624b\u5982\u7535\uff0c\u90a3\u4e09\u4e2a\u4eba\u5168\u5413\u4e86\u4e00\u8df3\uff0c\u5175\u5203\u6025\u6025\u51fa\u624b\uff0c\u56f4\u4e86\u4e0a\u6765\u3002\u98de\u7ea2\u5dfe\u51b7\u7b11\u58f0\u4e2d\uff0c \u5de6\u97ad\u53f3\u5251\uff0c\u76d8\u65cb\u98de\u821e\uff0c\u72ec\u6218\u4e09\u540d\u6b66\u58eb\uff0c\u6beb\u65e0\u60e7\u8272\u3002 \u3000\u3000\u8fd9\u4e09\u540d\u6b66\u58eb\u529f\u592b\u59d4\u5b9e\u4e0d\u9519\uff0c\u4e00\u4e2a\u4f7f\u5355\u5200\uff0c\u4e00\u4e2a\u4f7f\u94c1\u62d0\uff0c\u53e6\u4e00\u4e2a\u4f7f\u7684\u66f4\u662f\u5947\u95e8\u5175\u5203\u864e\u5934 \u94a9\uff0c\u65bd\u5c55\u5f00\u6765\uff0c\u5206\u8fdb\u5408\u51fb\uff0c\u52bf\u4e5f\u5f88\u60ca\u4eba\u3002\u53ef\u662f\u98de\u7ea2\u5dfe\u6bd4\u4ed6\u4eec\u66f4\u5389\u5bb3\uff0c\u8fd1\u7528\u5251\u6311\uff0c\u8fdc\u7528\u97ad\u51fb\uff0c \u5de6\u97ad\u53f3\u5251\uff0e\u5168\u662f\u8fdb\u624b\u7684\u62db\u6570\u3002\u6768\u4e91\u806a\u770b\u5f97\u5567\u5567\u79f0\u5947\u3002\u8fde\u8fde\u8d5e\u53f9\u3002\u98de\u7ea2\u5dfe\u679c\u771f\u7684\u662f\u540d\u4e2a\u865a\u4f20\u3002 \u79f0\u5f97\u4e0a\u5927\u6f20\u4e2d\u7edd\u65e0\u4ec5\u6709\u7684\u5947\u5973\u5b50\uff01 \u3000\u3000\u98de\u7ea2\u5dfe\u6b63\u5728\u5360\u5c3d\u4e0a\u98ce\u4e4b\u9645\uff0c\u5ffd\u7136\u7ea2\u5dfe\u4e00\u62c2\uff0c\u626d\u5934\u53eb\u9053\uff1a\u201c\u4f60\u51fa\u6765\u4f5c\u4ec0\u4e48\uff1f\u201d\u539f\u6765\u662f\u5979\u540c \u884c\u7684\u90a3\u4e2a\u5c11\u5e74\u62bc\u4e0d\u5e90\uff0c\u8c61\u5c0f\u5077\u4f3c\u7684\u9759\u6084\u6084\u7684\u6e9c\u4e86\u51fa\u6765\u3002\u98de\u7ea2\u5dfe\u4e00\u4e2a\u65cb\u8eab\u7ed5\u6b65\uff0c\u957f\u97ad\u500f\u5730\u6536 \u56de\uff0c\u56de\u8eab\u53cd\u624b\u6253\u51fa\uff0c\u53ea\u542c\u5f97\u201c\u54ce\u54df\u201d\u4e00\u58f0\uff0c\u62bc\u4e0d\u5e90\u5df2\u7ed9\u97ad\u68a2\u626b\u4e2d\u817f\u5f2f\uff0c\u8dcc\u5012\u5730\u4e0a\uff0c\u8fd9\u8fd8\u662f\u98de \u7ea2\u5dfe\u624b\u4e0b\u7559\u60c5\uff0c\u53ea\u7528\u4e00\u4e8c\u6210\u529b\uff0c\u53ea\u7528\u97ad\u68a2\u8f7b\u8f7b\u626b\u4ed6\u4e00\u4e0b\uff0c\u8981\u4e0d\u7136\u4ed6\u7109\u718a\u6d3b\u547d\uff1f\uff1a \u3000\u3000\u98de\u7ea2\u5dfe\u4e00\u97ad\u626b\u51fa\uff0c\u53e3\u4e2d\u56b7\u9053\uff1a\u201c\u4f60\u8d76\u5feb\u81ea\u5df1\u722c\u56de\u53bb\uff0c\u8981\u4e0d\u7136\u6211\u53ef\u8981\u518d\u6253\u4f60\u4e86\uff01\u201d\u62bc\u4e0d\u5e90 \u547b\u541f\u56b7\u9053\uff1a\u201c\u98de\u7ea2\u5dfe\uff0c\u4f60\u597d\u72e0\u554a\uff01\u6211\u662f\u60f3\u51fa\u6765\u5e2e\u4f60\u7684\u5fd9\u554a\uff01\u4f60\u600e\u7684\u628a\u597d\u610f\u5f53\u6210\u574f\u5fc3\uff01\u201d\u98de\u7ea2 \u5dfe\u4e0d\u7406\u4e0d\u776c\uff0c\u5b9d\u5251\u5212\u4e86\u534a\u4e2a\u5f27\u5f62\uff0c\u4e00\u8f6c\u8eab\u53c8\u62e6\u4f4f\u4e86\u4e09\u822c\u5175\u5668\uff01 \u3000\u3000\u5c31\u5728\u98de\u7ea2\u5dfe\u56de\u8eab\u5bf9\u4ed8\u62bc\u4e0d\u5e90\u4e4b\u9645\uff0c\u90a3\u4e09\u540d\u6b66\u58eb\u4ee5\u4e3a\u6709\u673a\u53ef\u4e58\uff0c\u4f7f\u864e\u5934\u94a9\u7684\u4ece\u4fa7\u9762\u4e00\u8dc3\u6251 \u8fdb\uff0c\u4e00\u62db\u201c\u9752\u9f99\u51fa\u6d77\u201d\uff0c\u5c31\u5411\u98de\u7ea2\u4e2d\u80f8\u53e3\u624e\u53bb\uff0c\u98de\u7ea2\u5dfe\u5b9d\u5251\u4e00\u683c\uff0c\u53ea\u542c\u5f97\u201c\u5580\u5693\u201d\u4e00\u58f0\uff0c\u864e \u5934\u94a9\u4e0a\u7684\u6708\u7259\u65ad\u4e86\u4e24\u9f7f\uff01\u90a3\u4f7f\u94c1\u62d0\u7684\u548c\u4f7f\u5212\u5200\u7684\u8fd9\u65f6\u4e5f\u53cc\u53cc\u4ece\u4e2d\u8def\u653b\u5230\u3002\u98de\u7ea2\u5dfe\u5b9d\u5251\u5212\u4e86\u534a \u4e2a\u5f27\u5f62\uff0c\u6321\u8fc7\u864e\u5934\u94a9\uff0c\u4f59\u52bf\u5140\u662f\u672a\u8870\uff0c\u628a\u5355\u5200\u94c1\u62d0\u4e5f\u8361\u4e86\u5f00\u53bb\uff01\u4f7f\u864e\u5934\u94a9\u7684\u4e0d\u77e5\u6b7b\u6d3b\uff0c\u5175\u5203 \u4e00\u6c89\uff0c\u7167\u51c6\u98de\u7ea2\u5dfe\u8170\u808b\u518d\u63d2\uff0c\u98de\u7ea2\u5dfe\u52c3\u7136\u5927\u6012\uff0c\u5de6\u624b\u957f\u97ad\u4e00\u4e2a\u6a2a\u626b\uff0c\u559d\u58f0\uff1a\u201c\u6492\u624b\uff01\u201d\u90a3\u67c4 \u864e\u5934\u94a9\u5df2\u98de\u4e0a\u534a\u7a7a\uff0c\u98de\u7ea2\u5dfe\u731b\u7684\u4e00\u63a0\u800c\u524d\uff0c\u4e00\u5251\u628a\u90a3\u540d\u6b66\u58eb\u62e5\u4e86\u4e2a\u900f\u660e\u7a9f\u7abf\uff0c\u77ed\u5251\u81ea\u524d\u5fc3\u76f4 \u900f\u540e\u5fc3\uff01 \u3000\u3000\u4f7f\u864e\u5934\u94a9\u7684\u6b66\u58eb\uff0c\u5728\u4e09\u4eba\u4e2d\u672c\u9886\u6700\u5f3a\uff0c\u8fd1\u8eab\u53ae\u62fc\uff0c\u4e0d\u4e00\u4e24\u62db\uff0c\u5c31\u9001\u4e86\u547d\uff0c\u5176\u4ed6\u4e24\u4eba\uff0c\u60ca \u5fc3\u52a8\u9b44\uff0c\u54ea\u6562\u4e89\u524d\uff0c\u5e76\u80a9\u4e00\u7acb\uff0c\u94c1\u62d0\u6a2a\u6572\uff0c\u5355\u5200\u4fa7\u51fb\uff0c\u4e14\u6218\u4e14\u9000\uff0c\u8fde\u6253\u80e1\u54e8\uff0c\u4f3c\u4e4e\u662f\u5728\u53ec\u5524 \u6551\u5175\u3002 \u3000\u3000\u6768\u4e91\u806a\u5728\u53e4\u5821\u4e0a\u770b\u5f97\u5206\u660e\uff0c\u53ea\u89c1\u53e4\u5821\u8fdc\u5904\uff0c\u4e24\u6761\u9ed1\u5f71\uff0c\u98de\u9a70\u800c\u6765\uff0c\u4e00\u770b\u7adf\u662f\u516b\u6b65\u8d76\u8749\u7684 \u4e0a\u4e58\u8f7b\u529f\uff0c\u4e0d\u7981\u5927\u8be7\uff01\u600e\u7684\u5927\u6f20\u4e4b\u4e2d\uff0c\u591c\u6df1\u65f6\u5206\uff0c\u8fd8\u6709\u8fd9\u6837\u7684\u9ad8\u624b\u524d\u6765\u3002\u96be\u9053\u4ed6\u4eec\u5c31\u662f\u6e05\u5175 \u7684\u5e2e\u624b\uff1b\u4f46\u4ee5\u81ea\u5df1\u6240\u77e5\uff0c\u5173\u5916\u6b66\u58eb\uff0c\u957f\u4e8e\u51fb\u5251\u9a91\u5c04\uff0c\u8f7b\u529f\u597d\u7684\uff0c\u4e5f\u4e0d\u80fd\u8fbe\u5230\u8fd9\u6837\u7684\u5883\u754c\u3002\u8fd9 \u4efd\u8f7b\u529f\uff0c\u663e\u660e\u662f\u6c49\u4eba\u4e2d\u7684\u5185\u5bb6\u9ad8\u624b\uff0c\u6709\u8fd9\u6837\u529f\u592b\u7684\u4eba\uff0c\u53c8\u5c82\u80af\u4e3a\u864e\u4f5c\u4f25\uff1f \u3000\u3000\u98de\u7ea2\u5dfe\u4e5f\u4f3c\u4e4e\u77a7\u89c1\u8fd9\u4e24\u6761\u4eba\u5f71\u4e86\uff0c\u62db\u6570\u4e00\u7d27\uff0c\u957f\u97ad\u8fde\u6325\uff0c\u628a\u4e24\u4eba\u88f9\u7740\uff0c\u5251\u5149\u97ad\u5f71\u4e2d\uff0c\u53ea \u542c\u5f97\u4e00\u58f0\u6e05\u53f1\uff0c\u98de\u7ea2\u5dfe\u731b\u7684\u8dc3\u8d77\uff0c\u4e00\u4e2a\u201c\u4e4c\u9f99\u6405\u6d77\u201d\uff0c\u90a3\u4f7f\u5355\u5200\u7684\u6b66\u58eb\uff0c\u770b\u4e5f\u672a\u770b\u5f97\u6e05\uff0c\u80f8 \u53e3\u4fbf\u7740\u4e86\u4e00\u5251\uff0c\u6251\u5730\u800c\u6b7b\uff01\u90a3\u4f7f\u94c1\u62d0\u7684\u4e71\u626b\u4e00\u62d0\uff0c\u4fbf\u60f3\u5954\u9003\uff0c\u4f46\u8fd8\u672a\u6765\u5f97\u53ca\u3002\u98de\u7ea2\u5dfe\u957f\u97ad\u4e00 \u5377\uff0c\u53c8\u628a\u4ed6\u7684\u94c1\u62d0\u593a\u4e86\u51fa\u6765\uff0c\u53cd\u624b\u4e00\u97ad\uff0c\u8fd9\u540d\u6b66\u58eb\u7684\u5929\u7075\u76d6\u7acb\u88ab\u6253\u88c2\uff0c\u60e8\u53eb\u4e00\u58f0\uff0c\u8111\u6d46\u6d41\u4e86 \u6ee1\u5730\u3002\u8fd9\u65f6\u90a3\u4e24\u6761\u4eba\u5f71\uff0c\u4e00\u524d\u4e00\u540e\uff0c\u5df2\u8d76\u5230\u6765\u3002\u6768\u4e91\u806a\u5927\u5403\u4e00\u60ca\uff0c\u8fd9\u4eba\u7adf\u662f\u81ea\u5df1\u7684\u5e08\u5f1f\u695a\u662d \u5357\uff0c\u6768\u4e91\u806a\u5fc3\u60f3\uff1a\u539f\u6765\u4ed6\u5728\u6c99\u6f20\u4e4b\u4e2d\uff0c\u9003\u51fa\u4e86\u6027\u547d\uff0c\u53c8\u5230\u8fd9\u91cc\u6253\u4ec0\u4e48\u574f\u4e3b\u610f\u4e86\u3002\u6211\u5012\u8981\u770b\u548c \u770b\u4ed6\u98de\u7ea2\u5dfe\u53c8\u6709\u4ec0\u4e48\u201c\u8fc7\u8282\u201d\u3002\uff08\u5373\u66fe\u7ed3\u8fc7\u4ec0\u4e48\u6028\u4e4b\u610f\uff09\u98de\u7ea2\u5dfe\u6325\u5251\u626c\u97ad\uff0c\u8fde\u6bd9\u4e09\u540d\u6b66\u58eb\u3001 \u4e00\u540d\u53db\u5f92\uff0c\u5feb\u610f\u4e4b\u6781\u3002\u8fd9\u65f6\u89c1\u695a\u662d\u5357\u84e6\u5730\u6765\u5230\uff0c\u9762\u8272\u500f\u53d8\uff0c\u626c\u97ad\u6307\u9053\uff1a\u201c\u695a\u662d\u5357\uff0c\u539f\u6765\u662f \u4f60\uff01\u201d\u695a\u662d\u5357\u9053\uff1a\u201c\u662f\u5440\uff0c\u98de\u7ea2\u5dfe\u59d1\u5a18\uff0c\u54b1\u4eec\u5df2\u5feb\u6709\u4e09\u5e74\u6ca1\u89c1\u9762\u4e86\uff0c\u96be\u4e3a\u4f60\u8fd8\u8bb0\u8d77\u6211\u3002\u201d\u98de \u7ea2\u5dfe\u51b7\u7b11\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u542c\u8bf4\u4f60\u6295\u4e86\u6e05\u5175\uff0c\u5728\u6e05\u519b\u4e2d\uff0c\u5f88\u662f\u5f97\u610f\u3002\u201d\u695a\u662d\u5357\u9762\u4e0a\u4e00\u7ea2\uff0c\u5f3a\u7b11 \u8bf4\u9053\uff1a\u98de\u7ea2\u5dfe\uff0c\u4f60\u4e00\u76f4\u90fd\u4e0d\u77e5\u6211\u7684\u5fc3\u610f\uff0c\u6211\u8fd8\u4e0d\u662f\u4e3a\u4e86\u4f60\uff1f\u201d\u98de\u7ea2\u5dfe\u4e00\u97ad\u6253\u53bb\uff0c\u53f1\u9053\uff1a\u201c\u80e1 \u8bf4\u516b\u9053\uff0c\u4f60\u65e2\u6295\u4e86\u6ee1\u5974\uff0c\u4f60\u5c31\u662f\u6211\u7684\u654c\u4eba\u3002\u201d\u695a\u662d\u5357\u53cd\u8eab\u4e00\u8dc3\uff0c\u907f\u8fc7\u957f\u97ad\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u6240 \u7231\u7684\u90a3\u4e2a\u4eba\uff0c\u6bd4\u6211\u66f4\u4e0d\u5982\uff01\u4ed6\u8981\u6295\u964d\u8fc7\u53bb\uff0c\u4eba\u5bb6\u4e5f\u53ea\u628a\u4ed6\u5f53\u505a\u4e00\u4e2a\u5c0f\u89d2\u8272\uff01\u201d\u98de\u7ea2\u5dfe\u6c14\u5f97\u67f3 \u7709\u5012\u7ad6\uff0c\u559d\u9053\uff1a\u201c\u7518\u5fc3\u4f5c\u8d3c\uff0c\u4f11\u8981\u591a\u8a00\uff01\u201d\u5237\u5237\u957f\u97ad\u76f4\u626b\uff0c\u5b9d\u5251\u6a2a\u6325\u3002 \u3000\u3000\u6768\u4e91\u806a\u542c\u5f97\u5927\u4e3a\u8be7\u5f02\uff1a\u539f\u6765\u695a\u662d\u5357\u7adf\u662f\u548c\u98de\u7ea2\u5dfe\u76f8\u8bc6\u7684\uff0c\u542c\u4ed6\u4eec\u7684\u8bdd\uff0c\u4f3c\u4e4e\u4ed6\u4eec\u4e4b\u95f4\u8fd8 \u6709\u4e00\u6bb5\u6069\u6028\u3002\u5927\u7ea6\u662f\u695a\u662d\u5357\u6709\u610f\u4e8e\u98de\u7ea2\u5dfe\uff0c\u98de\u7ea2\u5dfe\u5374\u7231\u4e0a\u4e86\u90a3\u540d\u6b4c\u624b\u3002\u6768\u4e91\u806a\u4e0d\u7981\u66ff\u98de\u7ea2\u5dfe \u5341\u5206\u4e0d\u503c\uff0c\u4ee5\u8fd9\u6837\u4e00\u4f4d\u5927\u6f20\u5973\u82f1\u96c4\uff0c\u8ffd\u6c42\u5979\u7684\u4eba\u548c\u5979\u6240\u7231\u7684\u4eba\uff0c\u5374\u90fd\u662f\u7075\u9b42\u5351\u52a3\u7684\u4e1c\u897f\u3002 \u3000\u3000\u695a\u662d\u5357\u8fde\u907f\u6570\u62db\uff0c\u98de\u7ea2\u5dfe\u8d8a\u6253\u8d8a\u6025\uff0c\u695a\u662d\u5357\u82e6\u7b11\u4e00\u58f0\uff0c\u6e38\u9f99\u5251\u6323\u7136\u51fa\u624b\uff0c\u53eb\u9053\uff1a\u201c\u98de\u7ea2 \u5dfe\uff0c\u662f\u4f60\u8feb\u5f97\u6211\u52a8\u624b\uff01\u201d\u98de\u7ea2\u5dfe\u4e00\u58f0\u4e0d\u54cd\uff0c\u5237\u7684\u53c8\u662f\u4e00\u97ad\u626b\u53bb\uff0c\u695a\u662d\u5357\u98d8\u8eab\u4e00\u6643\uff0c\u5b9d\u5251\u4e0a \u64a9\uff0c\u97ad\u68a2\u7acb\u523b\u7ed9\u622a\u53bb\u4e00\u6bb5\u3002\u98de\u7ea2\u5dfe\u6012\u9053\uff1a\u201c\u6709\u5b9d\u5251\u4e5f\u4e0d\u6015\u4f60\uff01\u201d\u5de6\u97ad\u53f3\u5251\uff0c\u5c55\u5f00\u4e86\u8f7b\u7075\u7684\u62db \u6570\uff0c\u7adf\u7136\u548c\u695a\u662d\u5357\u6253\u4e86\u4e2a\u5e73\u624b\u3002 \u3000\u3000\u695a\u662d\u5357\u4e00\u58f0\u957f\u5578\uff0c\u5251\u6cd5\u4e00\u53d8\uff0c\u8fc5\u5982\u95ea\u7535\u96f7\u98d8\uff0c\u5728\u5251\u5149\u97ad\u5f71\u4e2d\u6b3a\u8eab\u76f4\u8fdb\u3002\u98de\u7ea2\u5dfe\u4e5f\u5a07\u53f1\u4e00 \u58f0\uff0c\u957f\u97ad\u6325\u821e\uff0c\u77ed\u5251\u76d8\u65cb\uff0c\u4e24\u822c\u5175\u5668\uff0c\u653b\u5b88\u76f8\u8fde\uff0c\u914d\u5408\u5f97\u5999\u5230\u6beb\u5dc5\uff0c\u695a\u662d\u5357\u5929\u5c71\u5251\u6cd5\uff0c\u867d\u7136 \u795e\u5999\u5f02\u5e38\uff0c\u98de\u7ea2\u5dfe\u7684\u62db\u6570\uff0c\u53d8\u5316\u4e5f\u6781\u4e3a\u7e41\u6742\uff0c\u5927\u6218\u6570\u5341\u56de\u5408\uff0c\u90fd\u662f\u672a\u80fd\u5f97\u624b\u3002 \u3000\u3000\u6768\u4e91\u806a\u5728\u4e0a\u9762\u770b\u5f97\u6781\u4e3a\u60ca\u5947\uff0c\u521a\u624d\u89c1\u98de\u7ea2\u5dfe\u6253\u8d25\u4e09\u4e2a\u6b66\u58eb\uff0c\u867d\u7136\u4f69\u670d\u5979\u7684\u6b66\u529f\uff0c\u8fd8\u672a\u89c9 \u5f97\u6709\u4ec0\u4e48\u7279\u522b\u4e4b\u5904\u3002\u5982\u4eca\u89c1\u5979\u5e94\u4ed8\u695a\u662d\u5357\u795e\u5999\u7684\u5251\u6cd5\uff0c\u4ecd\u662f\u6325\u6d12\u81ea\u5982\uff0c\u8fd9\u624d\u77e5\u9053\u5979\u786e\u6709\u72ec\u5230 \u7684\u6280\u827a\u3002\u5979\u80fd\u5de6\u53f3\u4e24\u624b\uff0c\u4f7f\u4e24\u79cd\u4e0d\u540c\u7684\u5175\u5668\uff0c\u4e1d\u6beb\u4e0d\u4e71\uff0c\u53ea\u6b64\u4e00\u70b9\uff0c\u5728\u7b2c\u4e00\u6d41\u597d\u624b\u4e4b\u4e2d\uff0c\u5df2 \u662f\u96be\u627e\uff01\u53ea\u662f\u695a\u662d\u5357\u529f\u529b\u8f83\u5f3a\uff0c\u53c8\u6709\u5b9d\u5251\uff0c\u4e45\u6218\u4e0b\u53bb\uff0c\u98de\u7ea2\u5dfe\u53ea\u6015\u8981\u62b5\u6321\u4e0d\u4f4f\uff01 \u3000\u3000\u98de\u7ea2\u5dfe\u529b\u6218\u695a\u662d\u5357\uff0c\u5168\u795e\u8d2f\u6ce8\uff0c\u65e0\u6687\u65c1\u987e\u3002\u548c\u695a\u662d\u5357\u540c\u6765\u7684\u90a3\u4e2a\u4eba\uff0c\u7adf\u7136\u8d70\u8fdb\u4e86\u53e4\u5821\uff0c \u628a\u62bc\u4e0d\u5e90\u6276\u4e86\u51fa\u6765\u3002\u62bc\u4e0d\u5e90\u53d7\u4e86\u4e00\u97ad\uff0c\u5374\u53ea\u662f\u7a0d\u4f24\u76ae\u8089\uff0c\u5e76\u4e0d\u788d\u4e8b\uff0c\u51fa\u6765\u4e4b\u540e\uff0c\u5c31\u548c\u90a3\u4eba\u6025 \u6025\u5954\u9003\u3002\u98de\u7ea2\u5dfe\u4e00\u89c1\u5927\u6012\uff0c\u5f85\u53bb\u8ffd\u8d76\uff0c\u5374\u53c8\u88ab\u695a\u662d\u5357\u7684\u5251\u5149\u7f69\u4f4f\uff0c\u8131\u8eab\u4e0d\u5f97\u3002\u800c\u4e14\u56e0\u4e3a\u8fd9\u4e00 \u5206\u5fc3\uff0c\u695a\u662d\u5357\u8fd8\u62a2\u4e86\u5148\u624b\uff0c\u5251\u62db\u50ac\u52a8\uff0c\u6709\u5982\u957f\u6c5f\u5927\u6cb3\uff0c\u653b\u52bf\u7ef5\u7ef5\u4e0d\u7edd\uff01\u98de\u7ea2\u5dfe\u8feb\u5f97\u51dd\u795e\u9632 \u5fa1\uff0c\u90a3\u4e24\u4eba\u5df2\u5728\u5979\u7684\u8eab\u8fb9\u4e00\u63a0\u800c\u8fc7\uff01 \u3000\u3000\u6b63\u5f53\u6b64\u9645\uff0c\u53e4\u5821\u4e0a\u4e00\u6761\u9ed1\u5f71\uff0c\u7a81\u7684\u75be\u51b2\u800c\u4e0b\uff0c\u5c31\u5982\u534a\u5929\u98de\u4e0b\u4e00\u5934\u5927\u9e1f\uff01\u62bc\u4e0d\u5e90\u6b63\u5728\u5954 \u9003\uff0c\u84e6\u89c9\u80a9\u5934\u4e00\u7d27\uff0c\u597d\u50cf\u7ed9\u4e94\u652f\u94c1\u94a9\u94a9\u4f4f\u4e00\u6837\uff0c\u75db\u5f7b\u5fc3\u80ba\uff0c\u521a\u53eb\u5f97\u4e00\u58f0\uff0c\u201c\u7f57\u5927\u54e5\uff0c\u5feb\u6765\u6551 \u6211\uff01\u201d\u808b\u4e0b\u5df2\u88ab\u624b\u6307\u4e00\u6233\uff0c\u987f\u65f6\u5168\u8eab\u8f6f\u9ebb\uff0c\u762b\u5728\u5730\u4e0a\u3002 \u3000\u3000\u51b2\u4e0b\u6765\u7684\u6b63\u662f\u6768\u4e91\u806a\uff0c\u4ed6\u628a\u62bc\u4e0d\u5e90\u5236\u670d\u4e4b\u540e\uff0c\u53cc\u638c\u4e00\u6413\uff0c\u5c31\u8fce\u4e0a\u4e86\u695a\u662d\u5357\u7684\u540c\u4f34\u3002\u8fd9\u4eba \u540d\u5524\u7f57\u5927\u6d2a\uff0c\u662f\u5173\u5185\u7684\u72ec\u811a\u5927\u76d7\uff0c\u591a\u5c14\u886e\u5e26\u6e05\u5175\u5165\u5173\uff0c\u6536\u7f57\u6ee1\u6c49\u6b66\u58eb\uff0c\u628a\u4ed6\u6536\u63fd\u4e86\u53bb\uff0c\u7eb3\u5170 \u79c0\u5409\u8fdb\u519b\u65b0\u7586\uff0c\u53c8\u628a\u4ed6\u8981\u53bb\uff0c\u5728\u5e10\u4e0b\u5f53\u4e00\u540d\u7259\u5c06\u3002\u73b0\u5728\u662f\u695a\u662d\u5357\u7684\u526f\u624b\u3002 \u3000\u3000\u7f57\u5927\u6d2a\u6b63\u9886\u7740\u62bc\u4e0d\u5e90\u5954\u9003\uff0c\u5ffd\u542c\u80cc\u540e\u53eb\u58f0\uff0c\u56de\u8fc7\u5934\u65f6\uff0c\u62bc\u4e0d\u5e90\u5df2\u662f\u5012\u5728\u5730\u4e0a\uff0c\u53c8\u60ca\u53c8 \u6012\uff0c\u85e4\u86c7\u68d2\u8fde\u5fd9\u51fa\u624b\uff0c\u6253\u5934\u9876\u4e00\u4e2a\u76d8\u65cb\uff0c\u68d2\u631f\u52b2\u98ce\uff0c\u547c\u7684\u4e00\u58f0\uff0c\u5411\u6768\u4e91\u806a\u62e6\u8170\u626b\u53bb\u3002\u6768\u4e91\u806a \u4e00\u626d\u8eab\uff0c\u85e4\u86c7\u68d2\u8d34\u8eab\u800c\u8fc7\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u7f57\u5927\u6d2a\u68cd\u68d2\u8fd8\u672a\u6536\u56de\uff0c\u6768\u4e91\u806a\u5df2\u6251\u5165\u6000\u4e2d\uff0c\u7f57 \u5927\u6d2a\u6025\u7528\u68d2\u5934\u6572\u51fb\uff0c\u6768\u4e91\u806a\u5927\u559d\u4e00\u58f0\uff0c\u53cc\u624b\u6293\u53bb\uff0c\u4e00\u7167\u9762\u5c31\u7528\u5927\u64d2\u62ff\u624b\u628a\u4ed6\u53cc\u8155\u62ff\u4f4f\uff0c\u624b\u6307 \u7528\u529b\u4e00\u634f\uff0c\u7f57\u5927\u6d2a\u60e8\u53eb\u4e00\u58f0\uff0c\u6d51\u8eab\u65e0\u529b\u3002\u6768\u4e91\u806a\u628a\u4ed6\u6293\u8d77\uff0c\u968f\u624b\u4e00\u629b\uff0c\u4e0d\u518d\u7ba1\u4ed6\u6b7b\u6d3b\uff0c\u5f84\u81ea \u53bb\u6551\u98de\u7ea2\u5dfe\u3002 \u3000\u3000\u98de\u7ea2\u5dfe\u6b63\u5728\u5403\u7d27\uff0c\u542c\u5f97\u53eb\u559d\u58f0\u4e5f\u65e0\u6687\u987e\u671b\u3002\u731b\u7136\u95f4\u695a\u662d\u5357\u6536\u62db\u6025\u9000\uff0c\u98de\u7ea2\u5dfe\u6b63\u81ea\u60ca\u5947\uff0c \u5ffd\u542c\u5f97\u4e00\u58f0\u5927\u559d\uff1a\u201c\u7ad9\u4f4f\uff01\u201d\u7741\u773c\u770b\u65f6\uff0c\u53ea\u89c1\u4e00\u4e2a\u4eba\u75be\u5982\u98de\u9e1f\uff0c\u62e6\u4f4f\u4e86\u695a\u662d\u5357\u7684\u53bb\u8def\u3002 \u3000\u3000\u695a\u662d\u5357\u89c1\u5e08\u5144\u53cc\u624b\u7a7a\u7a7a\uff0c\u5fc3\u91cc\u867d\u7136\u60e7\u6015\uff0c\u8fd8\u5e0c\u5180\u4ed7\u5251\u9003\u751f\uff0c\u4e00\u5251\u72e0\u72e0\u523a\u6765\uff0c\u6768\u4e91\u806a\u6012 \u9053\uff1a\u201c\u4f60\u8fd8\u6562\u4e0e\u6211\u52a8\u624b\uff1f\u201d\u53cc\u638c\u98de\u626c\uff0c\u5728\u5251\u5149\u4e2d\u76f4\u5288\u8fc7\u53bb\uff0c\u970e\u65f6\u4e4b\u95f4\uff0c\u5c31\u62c6\u4e86\u4e8c\u4e09\u5341\u62db\uff0c\u98de \u7ea2\u5dfe\u8d76\u4e86\u8fc7\u6765\uff0c\u770b\u5f97\u60ca\u5f02\u4e0d\u5df2\uff0c\u600e\u7684\u8fd9\u4e2a\u4eba\u7adf\u6562\u7a7a\u62f3\u6765\u6597\u695a\u662d\u5357\u7684\u5b9d\u5251\uff1f\u6b63\u5f85\u51fa\u624b\u76f8\u52a9\uff0c\u53ea \u662f\u8fd9\u4e24\u4eba\u53ae\u6740\u5f97\u6781\u4e3a\u6fc0\u70c8\uff0c\u8eab\u5f62\u8fc5\u75be\u4e4b\u6781\uff0c\u8fde\u5e2e\u624b\u90fd\u63d2\u4e0d\u8fdb\u53bb\uff01 \u3000\u3000\u695a\u662d\u5357\u8bb8\u591a\u529f\u592b\u90fd\u662f\u6768\u4e91\u806a\u4ee3\u5e08\u4f20\u6388\u7684\uff0c\u6768\u4e91\u806a\u5c31\u662f\u95ed\u7740\u773c\u775b\uff0c\u4e5f\u719f\u6089\u4ed6\u7684\u5251\u62db\u53d8\u5316\uff0c \u4ed6\u8fd8\u662f\u4ed7\u7740\u5b9d\u5251\uff0c\u624d\u80fd\u62c6\u5230\u56db\u4e94\u5341\u62db\u3002\u65f6\u5019\u7a0d\u4e45\uff0c\u5c31\u611f\u62b5\u6321\u4e0d\u4f4f\uff0c\u6b63\u60f3\u8bbe\u6cd5\u9003\u547d\u3002\u6768\u4e91\u806a\u624b \u8155\u4e00\u7ffb\uff0c\u5288\u624b\u593a\u4e86\u695a\u662d\u5357\u7684\u6e38\u9f99\u5251\uff0c\u53cc\u6307\u5411\u4e0a\u4e00\u62db\uff0c\u5c31\u70b9\u4e86\u4ed6\u7684\u201c\u6108\u6c14\u7a74\u201d\u3002\u56de\u8eab\u7b11\u9053\uff1a \u201c\u59d1\u5a18\uff0c\u8fd9\u4e2a\u4eba\u4ea4\u7ed9\u4f60\u4e86\uff01\u201d\u98de\u7ea2\u5dfe\u53cc\u76ee\u95ea\u5149\uff0c\u89c1\u6768\u4e91\u806a\u6b63\u662f\u65e5\u95f4\u5411\u81ea\u5df1\u8ba8\u6c34\u559d\u7684\u4eba\uff0c\u7fd8\u8d77 \u62c7\u6307\u9053\u4e86\u4e00\u58f0\uff1a\u201c\u597d\u201d\uff0c\u5c31\u8bf7\u6768\u4e91\u806a\u7275\u7740\u695a\u662d\u5357\uff0c\u5979\u81ea\u5df1\u4e5f\u62c9\u7740\u62bc\u4e0d\u5e90\u540c\u8fdb\u53e4\u5821\u3002 \u3000\u3000\u98de\u7ea2\u5dfe\u7741\u773c\u770b\u7740\u695a\u662d\u5357\uff0c\u559d\u9053\uff1a\u201c\u539f\u6765\u4f60\u8fd9\u53ae\u771f\u662f\u6295\u4e86\u6e05\u519b\uff0c\u73b0\u5728\u8fd8\u6709\u4f55\u8bdd\u53ef\u8bf4\uff1f\u201d\u695a \u662d\u5357\u4e00\u58f0\u4e0d\u54cd\uff0c\u773c\u5149\u76f4\u76ef\u7740\u5979\u3002\u98de\u7ea2\u5dfe\u53cc\u6307\u5411\u524d\u4e00\u4f38\uff0c\u559d\u9053\uff1a\u201c\u5148\u628a\u4f60\u7684\u62db\u5b50\u5e9f\u6389\uff01\u201d\u4f38\u624b \u5c31\u8981\u6316\u695a\u662d\u5357\u53cc\u76ee\u3002 \u3000\u3000------------------ \u3000\u3000 \u626c\u5251\u8f69\u5c45\u58eb \u626b\u63cf\u6821\u5bf9  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u585e\u5916\u5947\u4fa0\u4f20\u300b\u2014\u2014\u7b2c\u516d\u56de\u3000\u5973\u4fa0\u4e0e\u53db\u5f92 \u6881\u7fbd\u751f\u300a\u585e\u5916\u5947\u4fa0\u4f20\u300b \u7b2c\u516d\u56de\u3000\u5973\u4fa0\u4e0e\u53db\u5f92 \u3000\u3000\u98de\u7ea2\u5dfe\u624b\u8155\u4e00\u62ac\uff0c\u4f38\u51fa\u53cc\u6307\uff0c\u6b63\u8981\u6316\u695a\u662d\u5357\u773c\u73e0\uff0c\u5ffd\u89c9\u80f3\u818a\u4e00\u9ebb\uff0c\u6768\u4e91\u806a\u8f7b\u8f7b\u4f38\u624b\uff0c\u5c06 \u5979\u624b\u8155\u6258\u4f4f\uff0c\u98de\u7ea2\u5dfe\u8be7\u5f02\u9053\uff1a\u201c\u4f60\u8fd9\u662f\u5e72\u5417\uff1f\u201d\u6768\u4e91\u806a\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u4ed6\u662f\u6211\u7684\u5e08\u5f1f\uff01\u201d\u98de\u7ea2 \u5dfe\u7741\u5927\u773c\u775b\u95ee\u9053\uff1a\u201c\u4f60\u662f\u54c8\u8428\u514b\u4eba\uff1f\u201d\u6768\u4e91\u806a\u9053\uff1a\u201c\u6211\u53eb\u6768\u4e91\u806a\uff0c\u6211\u5e2e\u54c8\u8428\u514b\u4eba\u6253\u4ed7\uff0c\u60ed\u6127 \u5f97\u5f88\uff0c\u6253\u8d25\u4e86\uff0c\u73b0\u5728\u6211\u8981\u5230\u5357\u7586\u53bb\uff0c\u7ea0\u96c6\u5357\u7586\u7684\u54c8\u8428\u514b\u4eba\uff0c\u518d\u548c\u6e05\u5175\u51b3\u4e2a\u80dc\u8d1f\uff01\u201d\u98de\u7ea2\u5dfe\u8df3 \u4e86\u8d77\u6765\uff0c\u53eb\u9053\uff1a\u201c\u554a\uff01\u539f\u6765\u4f60\u5c31\u662f\u6768\u5927\u4fa0\uff0c\u6211\u7684\u7238\u7238\uff0c\u751f\u524d\u4e00\u76f4\u79f0\u8d5e\u4f60\uff0c\u53ea\u662f\u6ca1\u6709\u673a\u4f1a\u548c\u4f60 \u89c1\u9762\uff01\u201d\u6768\u4e91\u806a\u5fae\u5fae\u4e00\u7b11\uff0c\u6b63\u60f3\u8bf4\u9053\uff1a\u201c\u6211\u4e45\u4ef0\u4f60\u7684\u7684\u5927\u540d\u3002\u201d\u98de\u7ea2\u5dfe\u53c8\u62a2\u7740\u8bf4\u9053\uff1a\u201c\u4f60\u60f3 \u628a\u4ed6\u653e\u4e86\u5417\uff1f\u201d\u8bf4\u7f62\uff0c\u4f38\u624b\u5411\u695a\u662d\u5357\u6307\u4e86\u4e00\u6307\u3002 \u3000\u3000\u6768\u4e91\u806a\u54c8\u54c8\u5927\u7b11\uff0c\u4e5f\u6307\u7740\u62bc\u4e0d\u5e90\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u80af\u628a\u4ed6\u653e\u8d70\u5417\uff1f\u201d\u98de\u7ea2\u5dfe\u6012\u9053\uff1a\u201c\u5f53\u7136 \u4e0d\u80af\uff01\u201d\u6768\u4e91\u806a\u9053\uff1a\u201c\u90a3\u4f60\u8fd8\u95ee\u6211\u5e72\u5417\uff1f\u4f60\u8981\u62bc\u4ed6\u56de\u90e8\u843d\uff0c\u6211\u4e5f\u8981\u62bc\u6211\u8fd9\u4e2a\u4e0d\u6210\u6750\u7684\u5e08\u5f1f\u56de \u5230\u5929\u5c71\u3002\u201d\u98de\u7ea2\u5dfe\u9762\u4e0a\u4e00\u7ea2\uff0c\u77e5\u9053\u81ea\u5df1\u8bf4\u9519\u4e86\u6d3b\uff0c\u6000\u7591\u6768\u4e91\u806a\u4f1a\u6b89\u79c1\u60c5\uff0c\u7ed9\u4ed6\u53cd\u95ee\u56de\u6765\uff0c\u5f53 \u7136\u9ed8\u7136\u4e0d\u8bed\u3002 \u3000\u3000\u6768\u4e91\u806a\u9762\u8272\u4e00\u7aef\uff0c\u53cc\u76ee\u70af\u70af\uff0c\u8feb\u89c6\u7740\u695a\u662d\u5357\uff0c\u8bf4\u9053\uff1a\u201c\u662d\u5357\uff0c\u4f60\u4e0d\u8bb0\u5f97\u5929\u5c71\u5b66\u827a\u7684\u65f6\u5019 \u5417\uff1f\u5e08\u7236\u548c\u6211\u662f\u600e\u6837\u5bf9\u4f60\uff1f\u4f60\u662f\u4e00\u4e2a\u5b64\u513f\uff0c\u6211\u7231\u62a4\u4f60\u5c31\u50cf\u7231\u62a4\u81ea\u5df1\u7684\u5f1f\u5f1f\u4e00\u6837\u3002\u5e08\u7236\u53c8\u662f\u600e \u6837\u6559\u8bad\u4f60\uff0c\u4ed6\u96be\u9053\u6ca1\u6709\u518d\u4e09\u53eb\u4f60\u8bb0\u4f4f\u81ea\u5df1\u662f\u8d2b\u82e6\u4eba\u5bb6\u51fa\u8eab\uff0c\u8981\u4f60\u6280\u6210\u4e4b\u540e\uff0c\u66ff\u8349\u539f\u4e0a\u7684\u7267\u6c11 \u505a\u4e00\u70b9\u4e8b\uff1f\u96be\u9053\u4ed6\u6ca1\u6709\u518d\u4e09\u53eb\u4f60\u8bb0\u7740\uff0c\u5343\u4e07\u4e0d\u8981\u4ed7\u7740\u81ea\u5df1\u7684\u6280\u827a\uff0c\u53bb\u66ff\u5b98\u5e9c\u5f53\u5dee\uff0c\u6b3a\u538b\u7a77\u82e6 \u7684\u4eba\uff1f\u201d\u695a\u662d\u5357\u907f\u5f00\u6768\u4e91\u806a\u8feb\u89c6\u7684\u773c\u5149\uff0c\u9ed8\u7136\u4e0d\u7b54\uff0c\u6768\u4e91\u806a\u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u5e08\u5f1f\uff0c\u6211\u8fd9\u662f\u6700\u540e \u4e00\u58f0\u53eb\u4f60\uff0c\u4f60\u82e5\u518d\u4e0d\u6094\u609f\uff0c\u4f60\u5c31\u662f\u6211\u7684\u654c\u4eba\uff01\u6211\u4e0d\u7528\u628a\u4f60\u62bc\u56de\u5929\u5c71\uff0c\u4e5f\u53ef\u4ee5\u60e9\u7f5a\u4f60\u3002\u4f60\u544a\u8bc9 \u6211\uff0c\u662f\u4f60\u81ea\u5df1\u7518\u5fc3\u6295\u9760\u80e1\u864f\uff0c\u8fd8\u662f\u53d7\u4e86\u522b\u4eba\u7684\u5f15\u8bf1\uff1f\u6295\u9760\u80e1\u864f\uff0c\u6b3a\u51cc\u81ea\u5df1\u7684\u540c\u80de\uff0c\u54fc\uff0c\u8fd9\u6bd4 \u66ff\u5b98\u5e9c\u5f53\u5dee\u66f4\u53ef\u6076\uff01\u201d\u695a\u662d\u5357\u4f4e\u58f0\u7b54\u9053\uff1a\u201c\u4e24\u6837\u90fd\u4e0d\u662f\u3002\u201d\u6768\u4e91\u806a\u6012\u9053\uff1a\u201c\u90a3\u4f60\u662f\u600e\u6837\u8fc7\u53bb \u7684\uff1f\u201d\u695a\u662d\u5357\u5411\u98de\u7ea2\u5dfe\u4e00\u6307\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u95ee\u5979\uff01\u201d\u98de\u7ea2\u5dfe\u52c3\u7136\u5927\u6012\uff0c\u6267\u8d77\u9a6c\u97ad\uff0c\u4e00\u97ad\u626b\u53bb\uff0c \u9a82\u9053\uff1a\u201c\u662f\u6211\u53eb\u4f60\u6295\u964d\u80e1\u864f\u7684\u5417\uff1f\u95ee\u6211\uff1f\u201d\u6768\u4e91\u806a\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u522b\u52a8\u6c14\uff0c\u4f60\u5c31\u544a\u8bc9\u6211\u4ed6\u662f \u600e\u6837\u8ba4\u8bc6\u4f60\u7684\u5427\uff01\u201d \u3000\u3000\u98de\u7ea2\u5dfe\u9053\uff1a\u201c\u4e09\u5e74\u524d\uff0c\u6211\u4eec\u7684\u90e8\u843d\u91cc\u6765\u4e86\u4e00\u4e2a\u5c0f\u4f19\u5b50\uff0c\u4ed6\u8bf4\u662f\u6666\u660e\u7985\u5e08\u7684\u5f92\u5f1f\uff0c\u6211\u4eec\u5c31 \u628a\u4ed6\u6536\u5bb9\u4e0b\u6765\u5566\uff01\u4ed6\u5e38\u5e38\u501f\u6545\u548c\u6211\u4eb2\u8fd1\uff0c\u6211\u4e5f\u628a\u4ed6\u5f53\u6210\u5144\u5f1f\u4e00\u822c\uff0c\u54fc\uff01\u8c01\u77e5\u4ed6\u6ca1\u5b89\u7740\u597d\u5fc3 \u773c\uff01\u201d\u6768\u4e91\u806a\u5fc3\u91cc\u7b11\u9053\uff1a\u201c\u5982\u679c\u4ed6\u53ea\u662f\u60f3\u8ffd\u6c42\u4f60\uff0c\u90a3\u8fd8\u4e0d\u7b97\u574f\u5fc3\u773c\u3002\u201d\u98de\u7ea2\u4e2d\u201c\u54fc\u201d\u4e86\u58f0\uff0c \u7ee7\u7eed\u5f80\u4e0b\u8bf4\u9053\uff1a\u201c\u90a3\u65f6\u6211\u4eec\u6b63\u548c\u6e05\u5175\u6253\u4ed7\uff0c\u5f88\u9700\u8981\u4eba\uff0c\u50cf\u4ed6\u90a3\u6837\u6b66\u827a\u9ad8\u5f3a\u7684\u5c0f\u4f19\u5b50\uff0c\u6211\u4eec\u5c24 \u5176\u770b\u91cd\u3002\u54ea\u6599\u4e0d\u4e45\u6211\u5c31\u770b\u51fa\u6765\u5566\uff0c\u4ed6\u5e76\u4e0d\u662f\u8bda\u5fc3\u5e2e\u52a9\u6211\u4eec\u6253\u4ed7\u6765\u7684\uff01\u201d\u695a\u662d\u5357\u5927\u58f0\u8bf4\u9053\uff1a \u201c\u90a3\u65f6\u5728\u4f60\u4eec\u7684\u90e8\u843d\uff0c\u6211\u6740\u7684\u6e05\u5175\uff0c\u4e0d\u662f\u6bd4\u8c01\u90fd\u591a\u5417\uff1f\u201d\u98de\u7ea2\u5dfe\u51b7\u7b11\u9053\uff1a\u201c\u5982\u679c\u662f\u4f60\u548c\u6211\u5728 \u4e00\u961f\uff0c\u4f60\u5c31\u6bd4\u8c01\u90fd\u52c7\u6562\uff1b\u5982\u679c\u4e0d\u5728\u4e00\u961f\uff0c\u4f60\u5c31\u6ca1\u7cbe\u6253\u91c7\u5566\u3002\u4f60\u6740\u6e05\u5175\u597d\u50cf\u53ea\u662f\u6740\u7ed9\u6211\u770b\u4f3c \u7684\u3002\u201d\u6768\u4e91\u806a\u7709\u5934\u4e00\u76b1\uff0c\u98de\u7ea2\u5dfe\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u4f60\u7684\u5251\u6cd5\u5728\u6211\u4eec\u90e8\u91cc\uff0c\u90a3\u662f\u8c01\u4e5f\u6bd4\u4e0d\u4e0a\u7684\u3002\u53ef \u662f\uff0c\u4e00\u5230\u5371\u9669\u4e4b\u65f6\uff0c\u4f60\u7684\u5251\u6cd5\u5c31\u53ea\u6653\u5f97\u62ff\u6765\u4fdd\u62a4\u81ea\u5df1\u3002\u6768\u5927\u4fa0\uff0c\u4f60\u9886\u54c8\u8428\u514b\u4eba\u6253\u8fc7\u8fd9\u4e48\u591a\u5e74 \u4ed7\uff0c\u4f60\u5f53\u7136\u61c2\u5f97\uff0c\u6253\u4ed7\u7684\u65f6\u5019\uff0c\u4e0d\u662f\u9760\u4e00\u4e8c\u4e2a\u4eba\uff0c\u6253\u8d77\u4ed7\u6765\uff0c\u5168\u519b\u5c31\u662f\u4e00\u4e2a\u6574\u4f53\uff0c\u8981\u914d\u5408\u5f97 \u5341\u5206\u9002\u5f53\uff01\u201d\u6768\u4e91\u806a\u70b9\u70b9\u5934\u9053\uff1a\u201c\u662f\u7684\uff0c\u59d1\u5a18\u4f60\u5f88\u61c2\u5f97\u6253\u4ed7\uff01\u201d\u98de\u7ea2\u5dfe\u53c8\u9053\uff1a\u201c\u53ef\u662f\u4f60\u8fd9\u5e08 \u5f1f\u5462\uff0c\u4ed6\u53ea\u6653\u5f97\u81ea\u5df1\uff01\u53ea\u6653\u5f97\u81ea\u5df1\u901e\u5a01\u98ce\uff0c\u5f88\u5c11\u53bb\u6551\u63f4\u522b\u4eba\uff0c\u6709\u4e00\u5929\uff0c\u4ed6\u548c\u6211\u4e0d\u662f\u7f16\u5728\u4e00 \u961f\uff0c\u800c\u662f\u548c\u6211\u7684\u54e5\u54e5\u540c\u5728\u4e00\u961f\uff0c\u5ffd\u7136\u95ee\u4e2d\u4e86\u6e05\u5175\u7684\u57cb\u4f0f\uff0c\u88ab\u5305\u56f4\u8d77\u6765\u5566\uff0c\u5f62\u52bf\u5341\u5206\u5371\u9669\uff0c\u6c60 \u6025\u8d77\u6765\uff0c\u4e00\u4e2a\u4eba\u633a\u5251\u5c31\u51b2\u51fa\u53bb\uff0c\u4ed7\u7740\u4ed6\u7684\u5251\u6cd5\uff0c\u5c45\u7136\u7ed9\u4ed6\u51b2\u51fa\u91cd\u56f4\uff0c\u53ef\u662f\u6211\u7684\u54e5\u54e5\u5374\u7ed9\u56f4\u4e86 \u4e09\u5929\u4e09\u591c\uff0c\u4e3a\u4e86\u6551\u6b7b\u6276\u4f24\uff0c\u63a9\u62a4\u540c\u4f34\uff0c\u6211\u7684\u54e5\u54e5\u53d7\u4e86\u5341\u5904\u7bad\u4f24\uff0c\u6d74\u8840\u6b7b\u6218\u3002\u540e\u6765\u6211\u4eec\u53ca\u65f6\u8d76 \u5230\uff0c\u7ed9\u4ed6\u89e3\u4e86\u56f4\u3002\u6551\u51fa\u4e86\u8bb8\u591a\u65cf\u4eba\uff0c\u4f46\u6211\u7684\u54e5\u54e5\u5374\u5df2\u6551\u6cbb\u4e0d\u4e86\uff0c\u8fc7\u4e24\u5929\u5c31\u53bb\u4e16\u5566\uff01\u201d\u6768\u4e91\u806a \u5927\u6012\uff0c\u9a82\u9053\uff1a\u201c\u6df7\u86cb\uff01\u201d\u98de\u7ea2\u5dfe\u9053\uff1a\u201c\u6253\u90a3\u4ef6\u4e8b\u4e4b\u540e\uff0c\u6211\u5bf9\u4ed6\u5c31\u8bf4\u4e0d\u51fa\u7684\u8ba8\u538c\u3002\u53ef\u662f\u6211\u7684\u7238 \u7238\u5374\u539f\u8c05\u4e86\u4ed6\uff0c\u8bf4\u4ed6\u5230\u5e95\u662f\u4e2a\u5ba2\u4eba\uff0c\u89c1\u5230\u5371\u9669\uff0c\u81ea\u5df1\u9003\u51fa\u6765\u4e5f\u65e0\u53ef\u975e\u8bae\u3002\u53ea\u8981\u4ed6\u7ee7\u7eed\u5e2e\u6211\u4eec \u6253\u6e05\u5175\uff0c\u6211\u4eec\u4e5f\u5c31\u4e0d\u5fc5\u8d23\u602a\u4ed6\u5566\uff01\u6bd4\u5982\u6ca1\u6709\u4ed6\u6765\u5e2e\u5fd9\u53c8\u600e\u6837\uff1f\u90a3\u6b21\u53d7\u56f4\uff0c\u4f60\u7684\u54e5\u54e5\u8fd8\u4e0d\u662f\u9003 \u4e0d\u4e86\u4e00\u6b7b\u3002\u6211\u7684\u7238\u7238\u5f88\u7231\u6211\u4eec\u5144\u59b9\uff0c\u4ed6\u539f\u8c05\u5566\uff0c\u6211\u4e5f\u5c31\u4e0d\u518d\u8bf4\u4e86\u3002\u53ea\u662f\u6211\u4e00\u8d70\u8fd1\u4ed6\uff0c\u5c31\u597d\u50cf \u95fb\u5230\u2014\u80a1\u81ed\u5473\uff0c\u6211\u53ef\u4ee5\u539f\u8c05\u4ed6\uff0c\u4f46\u5374\u5b9e\u4e0d\u613f\u63a5\u8fd1\u4ed6\u3002\u201d\u6768\u4e91\u806a\u9053\uff1a\u201c\u8fd9\u6837\uff0c\u8fc7\u4e86\u4e0d\u4e45\uff0c\u4ed6\u5c31 \u9003\u8dd1\u5566\uff0c\u662f\u4e0d\u662f\uff1f\u201d\u98de\u7ea2\u5dfe\u70b9\u70b9\u5934\u9053\uff1a\u201c\u6b63\u662f\u8fd9\u6837\uff01\u201d\u6768\u4e91\u806a\u53c8\u6c14\u53c8\u607c\uff0c\u62ac\u5934\u4e00\u770b\uff0c\u89c1\u695a\u662d \u5357\u773c\u4e2d\u8574\u7740\u6cea\u73e0\uff0c\u5fc3\u4e2d\u53c8\u662f\u4e00\u8f6f\u3002\u60f3\u9053\uff1a\u201c\u695a\u662d\u5357\u4eba\u5f88\u806a\u660e\uff0c\u53c8\u662f\u5b64\u513f\u3002\u56e0\u6b64\uff0c\u5f53\u4ed6\u5929\u5c71\u4e4b \u65f6\uff0c\u5e08\u5085\u548c\u81ea\u5df1\u90fd\u5bf9\u4ed6\u7279\u522b\u5ba0\u7231\uff0c\u4e5f\u8bb8\u6b63\u56e0\u5982\u6b64\uff0c\u5c31\u9020\u6210\u4ed6\u7684\u4efb\u6027\u548c\u81ea\u6043\uff0c\u4e0b\u5c71\u4e4b\u540e\uff0c\u66f4\u6ca1 \u4eba\u6559\u5bfc\u4ed6\uff0c\u4ed6\u54c1\u8d28\u4e2d\u574f\u7684\u4e00\u9762\uff0c\u5c31\u6162\u6162\u66b4\u9732\u51fa\u6765\uff0c\u7ec8\u4e8e\u8d70\u4e0a\u4e86\u6b67\u9014\u3002\u8fd9\uff0c\u81ea\u5df1\u4e5f\u5e94\u8be5\u8d1f\u4e00\u90e8 \u5206\u8d23\u4efb\u3002\u81ea\u5df1\u662f\u4ed6\u7684\u5e08\u5144\uff0c\u77e5\u9053\u4ed6\u4e0b\u4e86\u5c71\uff0c\u5374\u4e0d\u6d3e\u4eba\u627e\u4ed6\u3002\u867d\u8bf4\u5f53\u65f6\u519b\u65c5\u5306\u5306\uff0c\u65e0\u6687\u53ca\u6b64\uff0c \u4f46\u7ec8\u662f\u4e00\u4e2a\u9057\u61be\uff0c\u82e5\u4ed6\u5728\u81ea\u5df1\u8eab\u8fb9\uff0c\u4e5f\u8bb8\u4e0d\u4f1a\u8fd9\u6837\u505a\uff0c\u6768\u4e91\u806a\u60f3\u4e86\u4e00\u4f1a\uff0c\u84e6\u7136\u8bf4\u9053\uff1a\u201c\u662d \u5357\uff0c\u6309\u8bf4\u6211\u5e94\u628a\u4f60\u6740\u6389\uff0c\u5ff5\u5728\u4f60\u662f\u6211\u7684\u5e08\u5f1f\uff0c\u6211\u7559\u4e00\u4e2a\u673a\u4f1a\u7ed9\u4f60\uff0c\u4f60\u82e5\u80fd\u6539\u8fc7\u81ea\u65b0\uff0c\u6211\u5c31\u628a \u4f60\u653e\u8d70\uff01\u201d\u98de\u7ea2\u5dfe\u6012\u9053\uff1a\u201c\u53ea\u8bf4\u8bf4\u90a3\u53ef\u4e0d\u884c\uff0c\u8c01\u6562\u62c5\u4fdd\u4ed6\u771f\u80fd\u6539\u8fc7\u81ea\u65b0\uff01\u201d\u6768\u4e91\u806a\u7ee7\u7eed\u8bf4 \u9053\uff1a\u201c\u4f60\u81ea\u5df1\u7ec6\u60f3\u4e00\u4f1a\uff0c\u7136\u540e\u544a\u8bc9\u6211\u4eec\uff0c\u4f60\u9519\u5728\u4ec0\u4e48\u5730\u65b9\uff0c\u6295\u964d\u6e05\u5175\u662f\u4e00\u4e2a\u5927\u9519\uff0c\u4f46\u5728\u8fd9\u4ef6 \u5927\u9519\u4e4b\u95f4\uff0c\u65e9\u5df2\u7ecf\u6709\u8bb8\u591a\u9519\u4e86\uff01\u6bd4\u5982\uff0c\u4f60\u53ea\u662f\u4e3a\u7740\u8fd9\u4e2a\u59d1\u5a18\u800c\u6253\u4ed7\uff0c\u867d\u7136\u4f5c\u6218\u52c7\u6562\uff0c\u4e5f\u662f\u9519 \u8bef\u3002\u201d\u6768\u4e91\u806a\u6c89\u541f\u534a\u664c\uff0c\u518d\u9053\uff1a\u201c\u6211\u4e0d\u63d0\u4f60\u5566\uff0c\u4e00\u4e2a\u4eba\u7684\u9519\u8bef\uff0c\u8981\u4ed6\u81ea\u53bb\u7ec6\u60f3\uff0c\u81ea\u5df1\u53bb\u6316\u6398 \u51fa\u6765\u3002\u6295\u964d\u80e1\u864f\u8fd9\u4e2a\u5927\u9519\uff0c\u662f\u8bb8\u591a\u9519\u8bef\u603b\u56e0\uff0c\u4f60\u8981\u628a\u9519\u8bef\u7684\u6839\u6316\u51fa\u6765\uff01\u201d\u6768\u4e91\u806a\u9762\u8272\u5341\u5206\u5e84 \u4e25\uff0c\u98de\u7ea2\u5dfe\u770b\u7740\u4ed6\u660e\u4eae\u7684\u773c\u5149\uff0c\u542c\u4ed6\u8fd9\u756a\u8bdd\uff0c\u5176\u4e2d\u4f3c\u5927\u6709\u9053\u7406\uff0c\u672c\u60f3\u53cd\u5bf9\uff0c\u4e5f\u8f6c\u53e3\u8bf4\u9053\uff1a \u201c\u5c31\u7531\u4ed6\u53bb\u60f3\u5427\uff01\u201d \u3000\u3000\u8fd9\u970e\u90a3\u95f4\uff0c\u695a\u662d\u5357\u5fc3\u4e2d\u4e00\u9635\u6fc0\u8361\uff0c\u5e08\u5144\u7684\u8bdd\uff0c\u4f3c\u4e4e\u662f\u5728\u4ed6\u7684\u5fc3\u4e2d\u54cd\u8d77\u8b66\u949f\u3002\u731b\u7136\u95f4\uff0c\u524d \u5c18\u5f80\u4e8b\uff0c\u6d8c\u4e0a\u5fc3\u5934\uff0c\u4ed6\u60f3\u8d77\u521a\u4e0b\u5c71\u4e4b\u65f6\uff0c\u4e5f\u66fe\u4ed7\u7740\u672c\u9886\uff0c\u505a\u4e86\u51e0\u4ef6\u4fa0\u4e49\u4e4b\u4e8b\u3002\u540e\u6765\u542c\u8bf4\u98de\u7ea2 \u5dfe\u662f\u5927\u6f20\u4e2d\u7b2c\u4e00\u4e2a\u7f8e\u5973\uff0c\u6b66\u827a\u53c8\u5341\u5206\u9ad8\u5f3a\uff0c\u4e0d\u7981\u8d77\u4e86\u6c42\u5076\u4e4b\u5fc3\uff0c\u5343\u91cc\u8fe2\u8fe2\uff0c\u627e\u5230\u4e86\u5979\u7684\u90e8 \u843d\uff0c\u672c\u4ee5\u4e3a\u4ee5\u81ea\u5df1\u8fd9\u6837\u82f1\u96c4\u5e74\u5c11\uff0c\u548c\u98de\u7ea2\u5dfe\u90a3\u53ef\u771f\u662f\u5929\u4f5c\u4e4b\u5408\u3002\u4e0d\u6599\u98de\u7ea2\u5dfe\u5374\u8d8a\u6765\u8d8a\u758f\u8fdc\u81ea \u5df1\uff0c\u4e0d\u4e45\u53c8\u53d1\u73b0\u5979\u7231\u4e0a\u4e86\u90a3\u4e2a\u6b4c\u624b\uff0c\u90a3\u4e2a\u6f02\u4eae\u7684\u5374\u662f\u5351\u8d31\u7684\u6b4c\u624b\u3002\u4ed6\u60f3\u5230\u8fd9\u91cc\uff0c\u4e0d\u7981\u53c8\u62ac\u8d77 \u5934\u6765\u770b\u770b\u90a3\u62bc\u4e0d\u5e90\uff0c\u62bc\u4e0d\u5e90\u6b63\u5728\u547c\u547c\u7684\u6253\u7740\u9f3e\uff0c\u7761\u5f97\u50cf\u4e2a\u6b7b\u732a\u3002\u695a\u662d\u5357\u8f7b\u8511\u7684\u7b11\u4e86\u4e00\u7b11\uff0c\u5fc3 \u91cc\u8bf4\u9053\uff1a\u201c\u8fd9\u4e2a\u4eba\u6709\u54ea\u70b9\u6bd4\u5f97\u4e0a\u6211\uff0c\u98de\u7ea2\u5dfe\u5374\u7231\u4e0a\u4e86\u4ed6\uff01\u201d\u76f4\u5230\u6b64\u9645\uff0c\u4ed6\u8fd8\u4e0d\u6e05\u695a\u98de\u7ea2\u5dfe\u4e3a \u4ec0\u4e48\u4e0d\u7231\u4ed6\uff0c\u5fc3\u4e2d\u4ecd\u662f\u6709\u7740\u4e00\u80a1\u6124\u6124\u4e0d\u5e73\u4e4b\u6c14\u3002 \u3000\u3000\u73b0\u5728\u90fd\u8fd8\u8fd9\u6837\uff0c\u90a3\u4e2a\u65f6\u5019\uff0c\u66f4\u662f\u53ef\u60f3\u800c\u77e5\uff01\u90a3\u65f6\u4ed6\u6068\u4e0d\u5f97\u628a\u98de\u7ea2\u5dfe\u548c\u62bc\u4e0d\u5e90\u5168\u90fd\u65a9\u6b7b\uff1b \u53ef\u662f\u98de\u7ea2\u5dfe\u7684\u6b66\u827a\u548c\u4ed6\u4e0d\u76f8\u4e0a\u4e0b\uff0c\u62bc\u4e0d\u5e90\u53c8\u7ecf\u5e38\u548c\u5979\u5728\u4e00\u8d77\uff0c\u4ed6\u6ca1\u6709\u4e0b\u624b\u7684\u673a\u4f1a\uff0c\u540c\u65f6\u4ed6\u53c8 \u53d1\u73b0\u5510\u52aa\u8001\u82f1\u96c4\u6e10\u6e10\u5730\u758f\u8fdc\u81ea\u5df1\uff0c\u867d\u7136\u5bf9\u81ea\u5df1\u8fd8\u7b97\u5ba2\u6c14\uff0c\u4f46\u91cd\u8981\u7684\u4efb\u52a1\u90fd\u4e0d\u4ea4\u7ed9\u81ea\u5df1\uff0c\u53ea\u628a \u4ed6\u5f53\u505a\u4e00\u4e2a\u666e\u901a\u7684\u6218\u58eb\u770b\u5f85\u3002\u90a3\u65f6\uff0c\u4ed6\u4e0d\u6b62\u4e00\u6b21\u6028\u9a82\uff01\u54fc\uff0c\u6211\u695a\u662d\u5357\u7684\u5251\u6cd5\uff0c\u8c01\u6bd4\u5f97\u4e0a\uff0c\u4ed6 \u5374\u628a\u6211\u5982\u6b64\u8f7b\u89c6\uff01\u8d77\u521d\u662f\u5728\u5fc3\u4e2d\u6028\u9a82\uff0c\u540e\u6765\u5c31\u6e10\u6e10\u8bf4\u51fa\u58f0\u6765\u3002\u6709\u51e0\u4e2a\u548c\u4ed6\u6c14\u5473\u76f8\u6295\u7684\u201c\u670b \u53cb\u201d\uff0c\u542c\u4e86\u4ed6\u7684\u6028\u9a82\uff0c\u5c31\u529d\u4ed6\u9053\uff1a\u201c\u4ee5\u4f60\u8fd9\u6837\u7684\u82f1\u96c4\uff0c\u4f55\u5fc5\u5728\u8fd9\u91cc\u53d7\u6c14\uff0c\u82e5\u8bf4\u662f\u4e3a\u4e86\u98de\u7ea2 \u5dfe\uff0c\u98de\u7ea2\u5dfe\u8fd9\u4e2a\u5c0f\u72d0\u72f8\u53ef\u53c8\u6709\u4e86\u5fc3\u4e0a\u7684\u4eba\u3002\u4e8e\u662f\u6709\u4e00\u5929\uff0c\u90a3\u51e0\u4e2a\u4eba\u5e26\u4ed6\u53bb\u89c1\u4e00\u4e2a\u4f2a\u88c5\u6210\u9a7c\u9a6c \u5546\u4eba\u7684\u6e05\u519b\u519b\u5b98\uff0c\u4e00\u8bf4\u4e4b\u4e0b\uff0c\u5c31\u628a\u4ed6\u62c9\u8fc7\u53bb\u4e86\u3002\u8fd9\u51e0\u4e2a\u4eba\u539f\u6765\u90fd\u662f\u6e05\u519b\u7684\u5978\u7ec6\u3002\u90a3\u65f6\u695a\u662d\u5357 \u8fd8\u8fd9\u6837\u7684\u60f3\uff1a\u6211\u4e00\u671d\u5f97\u5fd7\uff0c\u8981\u628a\u4f60\u8fd9\u98de\u7ea2\u5dfe\u6c14\u6b7b\u3002\u4ed6\u6ca1\u60f3\u5230\u4ece\u6b64\u5c31\u8d8a\u9677\u8d8a\u6df1\uff0c\u53d8\u6210\u4e86\u66ff\u6e05\u5175 \u5c60\u6740\u8349\u539f\u4e0a\u5584\u826f\u7267\u6c11\u7684\u523d\u5b50\u624b\u3002 \u3000\u3000\u6b64\u9645\uff0c\u695a\u662d\u5357\u8d8a\u60f3\u8d8a\u4e71\uff0c\u5e08\u5144\u5a01\u80c1\u7684\u773c\u5149\u76f4\u8feb\u7740\u4ed6\u3002\u4ed6\u60f3\u5e08\u7236\u5e08\u5144\u5bf9\u81ea\u5df1\u7684\u7231\u62a4\uff0c\u5fc3\u4e2d \u8d77\u4e86\u4e00\u9635\u6094\u610f\u3002\u4f46\u81ea\u5df1\u7684\u9519\u5728\u4e48\u5730\u65b9\u5462\uff1f\u6ee1\u6d32\u4eba\u5df2\u5750\u7a33\u4e86\u6c5f\u5c71\uff0c\u8981\u60f3\u5efa\u529f\u7acb\u4e1a\uff0c\u4e0d\u66ff\u671d\u5ef7\u7684 \u53c8\u66ff\u8c01\u51fa\u529b\u5462\uff1f\u5728\u6e05\u519b\u8fd9\u4e24\u5e74\u4e2d\uff0c\u4ed6\u7ed9\u704c\u8f93\u4e86\u4e00\u5957\u201c\u5b66\u6210\u6587\u6b66\u827a\uff0c\u5356\u4e0e\u5e1d\u738b\u5bb6\u201d\u7684\u601d\u60f3\uff0c\u5e08 \u7236\u5e08\u5144\u7684\u8bdd\u5df2\u6e10\u6e10\u629b\u5728\u8111\u540e\uff0c\u751a\u81f3\u4ed6\u8fd8\u628a\u5f53\u65f6\u8ffd\u968f\u5510\u6012\u8001\u82f1\u96c4\uff0c\u62b5\u6297\u6e05\u5175\u7684\u4e8b\uff0c\u770b\u4f5c\u662f\u5c11\u5e74 \u7684\u51b2\u52a8\u3002 \u3000\u3000\u6768\u4e91\u806a\u89c1\u4ed6\u4e45\u4e45\u4e0d\u8bed\uff0c\u53c8\u8feb\u4ed6\u9053\uff1a\u201c\u662d\u5357\uff01\u4f60\u60f3\u5f97\u901a\u900f\u6ca1\u6709\uff1f\u77e5\u9053\u4e0d\u77e5\u9053\uff0c\u4f60\u7a76\u7adf\u9519\u5728 \u4ec0\u4e48\u5730\u65b9\uff1f\u201d\u695a\u662d\u5357\u672c\u60f3\u6297\u58f0\u8bf4\u9053\uff1a\u6211\u6ca1\u6709\u9519\uff01\u201d\u4f46\u4ed6\u5bb3\u6015\u5e08\u5144\u7684\u76ee\u5149\uff0c\u4e5f\u5bb3\u6015\u98de\u7ea2\u5dfe\u624b\u4e0a \u7684\u957f\u97ad\uff0c\u60f3\uff1a\u201c\u5e08\u5144\u8fd8\u597d\uff0c\u98de\u7ea2\u5dfe\u8fd9\u4e2a\u91ce\u5973\u90ce\uff0c\u813e\u6c14\u53ef\u574f\u900f\u5566\uff0c\u6211\u548c\u4ed6\u4e89\u8fa9\uff0c\u5979\u771f\u4f1a\u628a\u6211\u6253 \u6b7b\uff01\u201d\u4e8e\u662f\u4ed6\u8f6c\u53e3\u8bf4\u9053\uff1a\u201c\u5e08\u5144\uff0c\u5f85\u6211\u518d\u60f3\u4e00\u60f3\uff01\u201d\u6768\u4e91\u806a\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u6211\u7684\u6027\u5b50\u4e5f\u662f\u592a \u6025\uff0c\u4e00\u4e0b\u53eb\u4f60\u901a\u60f3\u900f\uff0c\u90a3\u4e5f\u771f\u96be\u3002\u597d\u5427\uff0c\u6211\u7d22\u6027\u7ed9\u4f60\u4e24\u5929\u529f\u592b\u3002\u6211\u4eec\u5148\u966a\u8fd9\u4f4d\u59d1\u5a18\u56de\u5230\u5979\u7684 \u90e8\u843d\uff0c\u7136\u540e\u6211\u518d\u5e26\u4f60\u8d70\u3002\u90a3\u65f6\u4f60\u8be5\u60f3\u5f97\u6709\u4e9b\u7709\u76ee\u76ee\u4e86\u3002\u201d\u6768\u4e91\u806a\u5fc3\u60f3\uff0c\u695a\u662d\u5357\u66fe\u8ffd\u968f\u8fc7\u5510\u52aa \u8001\u82f1\u96c4\uff0c\u90a3\u8fb9\u6709\u4ed6\u5f53\u5e74\u7684\u6218\u53cb\uff0c\u5e26\u4ed6\u53bb\u90a3\u91cc\uff0c\u8ba9\u4ed6\u89c1\u89c1\u65e7\u65f6\u6218\u53cb\uff0c\u542c\u542c\u5510\u52aa\u8001\u82f1\u96c4\u7684\u58ee\u70c8\u4e8b \u8ff9\uff0c\u53ef\u80fd\u4f1a\u628a\u4ed6\u611f\u52a8\uff0c\u5e2e\u52a9\u4ed6\u53d1\u73b0\u81ea\u5df1\u7684\u9519\u8bef\u3002\u53ef\u56db\u695a\u662d\u5357\u4e00\u542c\u8fd9\u8bdd\uff0c\u5374\u4e0d\u7531\u5f97\u5bb3\u6015\u8d77\u6765\u3002 \u4ed6\u77e5\u9053\u7f57\u5e03\u65cf\u4eba\uff0c\u628a\u6e05\u5175\u6068\u5f97\u523a\u9aa8\u3002\u4ed6\u4eec\u82e5\u77e5\u9053\u81ea\u5df1\u662f\u6e05\u5175\u7684\u519b\u5b98\uff0c\u4e00\u4e2a\u4eba\u4e00\u5757\u77f3\u5c31\u4f1a\u628a\u81ea \u5df1\u6253\u6b7b\uff0c\u4e8e\u662f\u4ed6\u6697\u6697\u76d8\u7b97\u9003\u8d70\u4e4b\u6cd5\u3002 \u3000\u3000\u8fd9\u65f6\u5df2\u8fc7\u4e09\u66f4\uff0c\u53e4\u5821\u5916\u591c\u98ce\u4f4e\u547c\uff0c\u6768\u4e91\u806a\u6574\u65e5\u5954\u9a70\uff0c\u53c8\u6328\u4e86\u5927\u534a\u5929\u7684\u997f\uff0c\u5927\u75c5\u65b0\u6108\uff0c\u4e0d \u89c9\u6253\u4e86\u51e0\u4e2a\u5475\u6b20\uff01\u98de\u7ea2\u5dfe\u9053\uff1a\u201c\u6768\u5927\u4fa0\uff0c\u6211\u548c\u4f60\u8f6e\u6d41\u5b88\u7740\u8fd9\u53ae\u5427\uff0c\u4f60\u5148\u7761\u7247\u523b\uff0c\u5230\u4e94\u66f4\u65f6\u6211 \u5524\u9192\u4f60\u3002\u6211\u518d\u53bb\u7761\u4e00\u4e2a\u65f6\u8fb0\uff0c\u660e\u5929\u665a\u4e00\u70b9\u518d\u8d76\u8def\u3002\u201d\u6768\u4e91\u806a\u9053\uff1a\u201c\u8fd8\u6211\u5148\u8f6e\u503c\u5427\uff0c\u4f60\u53bb \u7761\u3002\u201d\u98de\u7ea2\u5dfe\u9053\uff1a\u201c\u6211\u751f\u957f\u8349\u539f\uff0c\u8dd1\u60ef\u6c99\u6f20\uff0c\u5e76\u4e0d\u89c9\u5f97\u75b2\u5026\u3002\u201d\u6768\u4e91\u806a\u89c1\u5979\u597d\u80dc\uff0c\u7b11\u4e86\u4e00 \u7b11\uff0c\u4f38\u624b\u5728\u695a\u662d\u5357\u201c\u8f6f\u9ebb\u7a74\u201d\u4e0a\u5c31\u91cd\u91cd\u70b9\u4e86\u4e00\u4e0b\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u59a8\u4e8b\u4e86\uff0c\u4f60\u770b\u7740\u4ed6\uff0c\u4e94\u66f4\u65f6\u5206 \u53eb\u9192\u6211\u3002\u201d \u3000\u3000\u5728\u98de\u7ea2\u5dfe\u8f6e\u503c\u7684\u65f6\u5019\uff0c\u695a\u662d\u5357\u60f3\u8ddf\u5979\u8bf4\u8bdd\uff0c\u98de\u7ea2\u5dfe\u603b\u662f\u4e0d\u7406\u4e0d\u776c\uff0c\u6709\u65f6\u8fd8\u6325\u6325\u624b\u4e0a\u7684\u76ae \u97ad\u3002\u695a\u662d\u5357\u5fc3\u91cc\u6c14\u6781\uff0c\u6697\u81ea\u8c03\u597d\u547c\u5438\uff0c\u8fd0\u5185\u529b\u6765\u89e3\u5f00\u81ea\u5df1\u88ab\u5c01\u95ed\u7684\u7a74\u9053\uff0c\u6768\u4e91\u806a\u4e5f\u662f\u8fc7\u4e8e\u5927 \u610f\uff0c\u4ed6\u53ea\u77e5\u9053\u695a\u662d\u5357\u5728\u5929\u5c71\u65f6\u8fd8\u6ca1\u6709\u81ea\u89e3\u7a74\u9053\u7684\u672c\u9886\uff0c\u4e0d\u60f3\u695a\u662d\u5357\u5728\u8fd9\u51e0\u5e74\u4e2d\uff0c\u529f\u592b\u5df2\u7ecf\u5927 \u8fdb\uff0c\u867d\u672a\u6bd4\u5f97\u4e0a\u4ed6\uff0c\u53ef\u662f\u8fd0\u6c14\u89e3\u7a74\uff0c\u5374\u662f\u4e0d\u96be\uff0c\u5927\u7ea6\u8fc7\u4e86\u4e00\u4e2a\u65f6\u8fb0\uff0c\u4ed6\u5df2\u6c14\u8fbe\u56db\u68a2\uff0c\u5fc3\u4e2d\u5927 \u559c\uff0c\u6b63\u60f3\u53d1\u96be\uff0c\u5ffd\u7136\u542c\u5f97\u6768\u4e91\u806a\u5728\u5730\u4e0a\u53eb\u4e86\u4e00\u58f0\uff1a\u201c\u5e08\u5f1f\u5440\uff01\u201d \u3000\u3000------------------ \u3000\u3000 \u626c\u5251\u8f69\u5c45\u58eb \u626b\u63cf\u6821\u5bf9  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u585e\u5916\u5947\u4fa0\u4f20\u300b\u2014\u2014\u7b2c\u4e03\u56de\u3000\u6b4c\u624b\u7684\u6b7b\u4ea1\u65c5\u7a0b \u6881\u7fbd\u751f\u300a\u585e\u5916\u5947\u4fa0\u4f20\u300b \u7b2c\u4e03\u56de\u3000\u6b4c\u624b\u7684\u6b7b\u4ea1\u65c5\u7a0b \u3000\u3000\u695a\u662d\u5357\u731b\u7136\u4e00\u60ca\uff0c\u6768\u4e91\u806a\u53eb\u4e86\u4e00\u58f0\uff0c\u7ffb\u4e86\u4e2a\u8eab\uff0c\u53c8\u7761\u89c9\u4e86\uff0c\u539f\u6765\u662f\u8bf4\u68a6\u8bdd\u3002\u98de\u7ea2\u5dfe\u77aa\u4e86 \u695a\u662d\u5357\u4e00\u773c\uff0c\u6068\u6068\u8bf4\u9053\uff1a\u201c\u4f60\u7684\u5e08\u5144\u68a6\u91cc\u8fd8\u8bb0\u5f97\u4f60\uff0c\u4f60\u5374\u5c3d\u4e0d\u5411\u597d\uff01\u201d\u695a\u662d\u5357\u5664\u58f0\u4e0d\u8bed\uff0c\u6697 \u60f3\uff1a\u600e\u4e48\u8fd9\u6837\u7cca\u6d82\uff0c\u628a\u5e08\u5144\u90fd\u5fd8\u8bb0\u4e86\u3002\u5e78\u597d\u81ea\u5df1\u5c1a\u672a\u53d1\u96be\uff0c\u8981\u4e0d\u7136\u7eb5\u80fd\u8d62\u98de\u7ea2\u5dfe\uff0c\u7ed9\u5979\u4e00 \u558a\uff0c\u5e08\u5144\u4e00\u5b9a\u60ca\u9192\uff0c\u81ea\u5df1\u5373\u7b97\u9003\u5f97\u51fa\u53e4\u5821\uff0c\u4e5f\u4f1a\u7ed9\u4ed6\u64d2\u56de\uff01\u8fd9\u65f6\u4ed6\u7a74\u9053\u5df2\u89e3\uff0c\u4f46\u4ecd\u88c5\u7740\u4e0d\u80fd \u8f6c\u52a8\u81ea\u5982\u6837\u5b50\uff0c\u4f4e\u58f0\u56b7\u9053\uff1a\u201c\u98de\u7ea2\u5dfe\uff0c\u7ed9\u6211\u4e00\u70b9\u6c34\u3002\u201d\u98de\u7ea2\u5dfe\u4e0d\u7406\u4e0d\u776c\uff0c\u695a\u662d\u5357\u53c8\u5927\u58f0\u53eb \u9053\uff1a\u201c\u6e34\u6b7b\u5566\uff01\u7ed9\u6211\u4e00\u70b9\u6c34\uff01\u201d\u98de\u7ea2\u5dfe\u9a82\u9053\uff1a\u201c\u6e34\u6b7b\u6d3b\u8be5\uff01\u4f60\u8fd9\u5c0f\u5b50\uff0c\u6210\u5fc3\u8981\u628a\u4f60\u7684\u5e08\u5144\u5435 \u9192\u3002\u201d\u5237\u5730\u4e00\u97ad\u6a2a\u626b\u8fc7\u6765\uff0c\u695a\u662d\u5357\u6323\u624e\u7740\u8eb2\u907f\uff0c\u201c\u54ce\u5440\u201d\u4e00\u58f0\uff0c\u4f0f\u5728\u5730\u4e0a\uff0c\u8d81\u8fd9\u65f6\u5019\uff0c\u5077\u5077 \u5730\u4ece\u6000\u91cc\u638f\u51fa\u4e00\u5c0f\u5305\u4e1c\u897f\u3002\u98de\u7ea2\u5dfe\u6beb\u4e0d\u6ce8\u610f\uff0c\u76ae\u97ad\u5728\u7a7a\u4e2d\u6325\u52a8\uff0c\u50fb\u556a\u4f5c\u54cd\uff0c\u9a82\u9053\uff1a\u201c\u4f60\u8d56 \u6b7b\uff0c\u8fd8\u4e0d\u8d77\u6765\uff1f\u201d \u3000\u3000\u6768\u4e91\u806a\u7ed9\u4ed6\u4eec\u4e00\u9635\u95f9\uff0c\u679c\u7136\u9192\u4e86\u8fc7\u6765\uff0c\u7761\u773c\u60fa\u677e\uff0c\u5728\u90a3\u91cc\u9053\uff1a\u201c\u98de\u7ea2\u5dfe\uff0c\u51fa\u4e86\u4ec0\u4e48 \u4e8b\uff1f\u201d\u98de\u7ea2\u5dfe\u9053\uff1a\u201c\u6ca1\u6709\u4ec0\u4e48\uff0c\u4f60\u7761\u5427\uff01\u201d\u695a\u662d\u5357\u53c8\u53eb\u9053\uff1a\u201c\u5e08\u5144\uff0c\u6211\u8981\u4e00\u70b9\u6c34\u559d\uff01\u201d\u6768\u4e91 \u806a\u9053\uff1a\u201c\u98de\u7ea2\u5dfe\uff0c\u7ed9\u4ed6\u4e00\u70b9\u5427\u3002\u201d\u98de\u7ea2\u5dfe\u77aa\u4e86\u4e00\u773c\uff1b\u5c06\u6c34\u56ca\u9012\u8fc7\uff0c\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u77a7\u5728\u4f60\u5e08\u5144 \u4efd\u4e0a\uff0c\u7ed9\u4f60\u6c34\u559d\uff01\u201d\u695a\u662d\u5357\u7528\u81c2\u5f2f\u5939\u7740\u6c34\u56ca\uff0c\u4f5c\u4e86\u8f6c\u52a8\u8270\u96be\u7684\u6837\u5b50\uff0c\u4fef\u4e0b\u5934\u6765\uff0c\u201c\u561f\u561f\u201d\u7684 \u559d\u4e86\u51e0\u53e3\u6c34\uff0c\u53f3\u624b\u5374\u5077\u5077\u4e00\u634f\u4e00\u5f39\uff0c\u628a\u90a3\u5c0f\u5305\u4e1c\u897f\u5f39\u8fdb\u4e86\u6c34\u56ca\u3002 \u3000\u3000\u6768\u4e91\u806a\u8fd9\u65f6\u5df2\u7ecf\u9192\u8f6c\uff0c\u7761\u610f\u6d88\u5931\uff0c\u5750\u4e86\u8d77\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u98de\u7ea2\u5dfe\uff0c\u8f6e\u5230\u6211\u5f53\u503c\u4e86\uff01\u201d\u98de\u7ea2 \u5dfe\u9053\uff1a\u201c\u5c1a\u672a\u5230\u4e94\u66f4\u54e9\uff01\u201d\u6768\u4e91\u806a\u9053\uff1a\u201c\u6211\u7761\u4e0d\u7740\u4e86\uff0c\u4f55\u5fc5\u8981\u4e24\u4e2a\u4eba\u90fd\u5b88\u7740\u4ed6\u3002\u201d\u98de\u7ea2\u5dfe\u628a \u76ae\u97ad\u6454\u5728\u5730\u4e0a\uff0c\u9053\uff0c\u201c\u4e5f\u597d\uff0c\u4f60\u53ef\u8981\u5c0f\u5fc3\u70b9\u513f\u3002\u201d\u53d6\u51fa\u4e00\u4ef6\u62ab\u98ce\uff0c\u94fa\u5728\u5730\u4e0a\u4fbf\u7761\u3002\u6768\u4e91\u806a\u5fc3 \u91cc\u7b11\u9053\uff1a\u201c\u771f\u662f\u4e2a\u76f4\u7387\u7684\u59d1\u5a18\u3002\u201d \u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\uff0c\u5730\u4e0a\u8d77\u4e86\u9f3e\u58f0\uff0c\u6768\u4e91\u806a\u6084\u58f0\u8bf4\u9053\uff1a\u201c\u662d\u5357\uff0c\u4f60\u4e0d\u5026\u4e48\uff1f\u4f60\u4e5f\u7761\u597d\u5566\u3002\u201d\u695a\u662d \u5357\u4f4e\u58f0\u7b54\u9053\uff1a\u201c\u6211\u542c\u5e08\u5144\u7684\u6559\u8bad\uff0c\u6b63\u5728\u60f3\u5462\u3002\u201d\u6768\u4e91\u806a\u751a\u4e3a\u6b23\u6170\uff0c\u8bf4\u9053\uff1a\u201c\u4e5f\u597d\uff0c\u4f60\u5c31\u597d\u597d \u60f3\u5427\u3002\u201d\u695a\u662d\u5357\u5782\u5934\u95ed\u76ee\uff0c\u72b6\u5982\u8001\u50e7\u4eba\u5b9a\uff0c\u6768\u4e91\u806a\u6697\u6697\u55df\u53f9\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u6768\u4e91\u806a\u81ea\u5df1\u5df2\u611f\u53e3 \u6e34\uff0c\u62d4\u5f00\u4e86\u6c34\u56ca\u7684\u585e\u5b50\uff0c\u5495\u565c\u5495\u565c\u5730\u559d\u4e86\u51e0\u53e3\u6c34\uff0c\u695a\u662d\u5357\u5077\u5077\u5f00\u773c\u6765\u77a7\uff0c\u53c8\u8fc7\u4e86\u4e00\u4f1a\uff0c\u6768\u4e91 \u806a\u5ffd\u89c9\u773c\u775b\u53d1\u9ed1\uff0c\u8eab\u5b50\u6447\u6447\u6643\u6643\uff0c\u695a\u662d\u5357\u5ffd\u7136\u5927\u53eb\u4e00\u58f0\u201c\u5012\u4e5f\uff01\u201d\u6258\u5730\u8df3\u8d77\uff0c\u95ea\u7535\u822c\u7684\u5c06\u6302 \u5728\u5899\u4e0a\u7684\u6e38\u9f99\u5251\u62a2\u5728\u624b\u4e2d\uff0c\u6768\u4e91\u806a\u9aa4\u51fa\u4e0d\u610f\uff0c\u7741\u773c\u770b\u5f97\u6e05\u65f6\uff0c\u695a\u662d\u5357\u5237\u7684\u4e00\u5251\uff0c\u5206\u5fc3\u523a\u5230\u3002 \u3000\u3000\u539f\u6765\u90a3\u5c0f\u5305\u4e1c\u897f\u4e43\u662f\u9ebb\u9189\u836f\uff0c\u660e\u672b\u6d77\u7981\u521d\u5f00\uff0c\u5df2\u6709\u4e9b\u897f\u6d0b\u836f\u54c1\u8f93\u5165\u4e2d\u56fd\u3002\u5916\u79d1\u7528\u7684\u9ebb\u9189 \u836f\uff0c\u5c24\u4e3a\u5e26\u5175\u7684\u5c06\u5b98\u4eec\u6240\u73cd\u8d35\u3002\u695a\u662d\u5357\u6295\u4e86\u60c5\u519b\u4e4b\u540e\uff0c\u5c61\u5efa\u529f\u52b3\uff0c\u4f0a\u7281\u5c06\u519b\u7eb3\u5170\u79c0\u5409\u89c1\u4ed6\u51fa \u751f\u5165\u6b7b\uff0c\u4e3a\u7b3c\u7edc\u4ed6\uff0c\u7279\u522b\u7ed9\u4e86\u4ed6\u51e0\u5305\u836f\u54c1\uff0c\u544a\u8bc9\u4ed6\u9053\uff1a\u201c\u8fd9\u662f\u9ebb\u9189\u836f\uff0c\u5982\u679c\u4f60\u4e2d\u4e86\u7bad\u4f24\uff0c\u6216 \u4e2d\u4e86\u6709\u6bd2\u7684\u6697\u5668\uff0c\u8981\u522e\u9aa8\u6d88\u6bd2\uff0c\u7528\u8fd9\u4e9b\u836f\u90a3\u662f\u6700\u597d\u4e5f\u4e0d\u8fc7\u4e86\u3002\u4e00\u70b9\u4e5f\u4e0d\u4f1a\u75db\u3002\u201d\u695a\u662d\u5357\u5f53\u65f6 \u8fd8\u7b11\u9053\uff1a\u201c\u6211\u867d\u7136\u6ca1\u6709\u5173\u516c\u7684\u52c7\u6b66\u80c6\u867d\uff0c\u82e5\u771f\u7684\u8981\u522e\u9aa8\u6d88\u6bd2\u65f6\uff0c\u4fdd\u7ba1\u4e0d\u4f1a\u76b1\u7709\u5934\u3002\u201d\u7eb3\u5170\u79c0 \u5409\u9053\uff1a\u201c\u6709\u5907\u65e0\u60a3\uff0c\u5e26\u4e0a\u4e00\u4e24\u5305\u603b\u6709\u597d\u5904\u3002\u201d\u695a\u662d\u5357\u7ec6\u95ee\u7528\u6cd5\uff0c\u77e5\u9053\u8fd9\u79cd\u836f\u54c1\uff0c\u82e5\u7136\u8fdb\u53e3\uff0c \u53ef\u8981\u6bd4\u6c5f\u6e56\u4e0a\u7528\u7684\u8499\u6c57\u836f\u8fd8\u5389\u5bb3\uff0c\u5f53\u65f6\u6697\u6697\u8bb0\u5728\u5fc3\u91cc\u3002 \u3000\u3000\u518d\u8bf4\u6768\u4e91\u806a\u84e6\u89c9\u773c\u524d\u53d1\u9ed1\uff0c\u795e\u5fd7\u660f\u8ff7\uff0c\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u4ed6\u5185\u529f\u6df1\u6e5b\uff0c\u5c61\u7ecf\u5927\u654c\uff0c\u77e5\u9053 \u53d7\u4e86\u695a\u662d\u5357\u6697\u7b97\uff0c\u6025\u5fd9\u4e00\u6444\u5fc3\u795e\uff0c\u521a\u770b\u5f97\u6e05\u65f6\uff0c\u695a\u662d\u5357\u6e38\u9f99\u5251\u5fae\u5e26\u5578\u58f0\uff0c\u5206\u5fc3\u523a\u5230\u3002\u6768\u4e91\u806a \u4e00\u58f0\u5927\u559d\uff0c\u5237\u5730\u817e\u8d77\uff0c\u53cc\u638c\u4e00\u7ffb\uff0c\u5de6\u638c\u76f4\u5288\u695a\u662d\u5357\u7684\u534e\u76d6\u7a74\uff0c\u53f3\u624b\u4e00\u642d\u3002\u4fbf\u6765\u62a2\u4ed6\u7684\u5b9d\u5251\u3002 \u3000\u3000\u695a\u662d\u5357\u6599\u4e0d\u5230\u5e08\u5144\u5403\u4e86\u9ebb\u9189\u836f\u540e\u8fd8\u8fd9\u6837\u8c6a\u731b\uff0c\u4e00\u4e2a\u201c\u76d8\u9f99\u7ed5\u6b65\u201d\uff0c\u907f\u8fc7\u638c\u950b\uff0c\u593a\u8def\u4fbf \u8d70\uff0c\u6768\u4e91\u806a\u773c\u524d\u4e00\u7247\u6a21\u7cca\uff0c\u5f3a\u6444\u5fc3\u795e\u3002\u542c\u98ce\u58f0\uff0c\u8fa8\u65b9\u4f4d\uff0c\u8eab\u5f62\u8d77\u5904\uff0c\u75be\u5982\u95ea\u7535\u822c\u5730\u5c01\u7740\u4e86\u695a \u662d\u5357\u53bb\u8def\uff0c\u53cc\u638c\u7ffb\u7ffb\u6eda\u6eda\uff0c\u786c\u6597\u695a\u662d\u5357\u7684\u5b9d\u5251\uff01\u695a\u662d\u5357\u672a\u66fe\u8bd5\u8fc7\u8fd9\u4e9b\u836f\u54c1\uff0c\u8fd8\u9053\u662f\u836f\u6027\u4e0d \u7075\uff0c\u6697\u6697\u53eb\u9053\uff1a\u201c\u82e6\u4e5f\uff01\u8fd9\u56de\u82e5\u518d\u88ab\u64d2\u62ff\uff0c\u5e08\u5144\u5b9a\u4e0d\u4f1a\u8f7b\u9976\u4e86\u3002 \u3000\u3000\u4e24\u4eba\u970e\u65f6\u4e4b\u95f4\uff0c\u5df2\u62fc\u4e86\u8bb8\u591a\u51f6\u6076\u7684\u9669\u62db\u3002\u98de\u7ea2\u5dfe\u521a\u521a\u4eba\u7761\uff0c\u542c\u95fb\u558a\u58f0\uff0c\u6258\u5730\u8df3\u8d77\uff0c\u4e00\u62b9 \u773c\u775b\uff0c\u89c1\u6768\u4e91\u806a\u548c\u695a\u662d\u5357\u6597\u5f97\u975e\u5e38\u6fc0\u70c8\uff0c\u5927\u5403\u4e00\u60ca\uff0c\u62fe\u8d77\u76ae\u97ad\uff0c\u62d4\u51fa\u4f69\u5251\uff0c\u9a82\u9053\uff1a\u201c\u597d\u5c0f \u5b50\u3002\u5c45\u7136\u6562\u9003\u8dd1\uff01\u201d\u62a2\u4e86\u4e0a\u6765\uff0c\u957f\u97ad\u547c\u5730\u4e00\u54cd\uff0c\u5411\u695a\u662d\u5357\u72e0\u72e0\u62bd\u53bb\uff01\u695a\u662d\u5357\u51b7\u6c57\u6c81\u808c\uff0c\u5e08\u5144 \u4e00\u4eba\u4ed6\u4e5f\u4e0d\u662f\u654c\u624b\uff0c\u66f4\u4f55\u51b5\u52a0\u4e0a\u98de\u7ea2\u5dfe\uff01\u6697\u81ea\u53f9\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u6211\u695a\u662d\u5357\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u5c31\u547d\u4e27\u6b64 \u5904\u3002 \u3000\u3000\u4e0d\u6599\u98de\u7ea2\u5dfe\u4e0d\u52a0\u5165\u8fd8\u597d\uff0c\u4e00\u52a0\u5165\u53cd\u7d2f\u4e86\u6768\u4e91\u806a\u3002\u539f\u6765\u6b64\u65f6\u836f\u529b\u53d1\u4f5c\uff0c\u6768\u4e91\u806a\u53cc\u773c\u5df2\u770b\u4e0d \u6e05\u4e1c\u897f\uff0c\u53ea\u662f\u5f3a\u6444\u5fc3\u795e\uff0c\u8fa8\u58f0\u8fdb\u51fb\u3002\u98de\u7ea2\u5dfe\u7684\u957f\u97ad\u5237\u5237\u4f5c\u54cd\uff0c\u8fd8\u6613\u8fa8\u8ba4\uff0c\u4f69\u5251\u7684\u51fb\u523a\u5288\u6321\uff0c \u53d1\u51fa\u7684\u58f0\u54cd\u548c\u5e26\u8d77\u7684\u98ce\u58f0\u5374\u548c\u695a\u662d\u5357\u7684\u6e38\u9f99\u5251\u4e00\u6837\uff0c\u695a\u662d\u5357\u4e3a\u907f\u5e08\u5144\u7684\u638c\u529b\uff0c\u5df2\u4e2d\u4e86\u98de\u7ea2\u5dfe \u4e00\u97ad\uff0c\u98de\u7ea2\u5dfe\u6b63\u6697\u81ea\u5927\u559c\uff0c\u731b\u7684\u63c9\u8eab\u6025\u8fdb\uff0c\u4e00\u5251\u523a\u53bb\uff0c\u5b9d\u5251\u4ece\u6768\u4e91\u806a\u8eab\u4fa7\u523a\u51fa\uff0c\u6768\u4e91\u806a\u5ffd\u7136 \u5927\u559d\u4e00\u58f0\uff0c\u8eab\u5b50\u4e00\u7ffb\uff0c\u53cc\u6307\u5f80\u5251\u8eab\u4e00\u642d\uff0c\u5288\u624b\u5c31\u593a\u4e86\u98de\u7ea2\u5dfe\u7684\u5b9d\u5251\u3002\u98de\u7ea2\u5dfe\u5927\u53eb\uff1a\u201c\u4f60\u8fd9\u662f \u5e72\u561b\uff1f\u201d\u695a\u662d\u5357\u6478\u4e0d\u7740\u5934\u8111\uff0c\u8fd8\u4ee5\u4e3a\u5e08\u5144\u5ff5\u65e7\u60c5\uff0c\u53c8\u4e00\u6b21\u7684\u6551\u4e86\u81ea\u5df1\u3002\u5fc3\u4e2d\u5927\u559c\uff0c\u8f6c\u8eab\u4fbf\u9003 \u51fa\u53e4\u5821\u3002 \u3000\u3000\u98de\u7ea2\u5dfe\u5927\u6012\uff0c\u6b63\u60f3\u559d\u9a82\u6768\u4e91\u806a\uff0c\u5ffd\u7136\u6768\u4e91\u806a\u2018\u5495\u51ac\u201d\u4e00\u58f0\uff0c\u5012\u5728\u5730\u4e0a\uff0c\u53eb\u9053\uff0c\u201c\u98de\u7ea2 \u5dfe\uff0c\u6211\u53d7\u4e86\u6697\u7b97\u4e86\uff01\u201d\u98de\u7ea2\u5dfe\u5927\u5403\u4e00\u60ca\uff0c\u6025\u5fd9\u770b\u65f6\uff0c\u6768\u4e91\u806a\u5df2\u660f\u8ff7\u4e0d\u9192\u4eba\u4e8b\u3002\u98de\u7ea2\u5dfe\u4e0d\u77e5\u4ed6 \u53d7\u4e86\u4ec0\u4e48\u6697\u7b97\uff0c\u53ea\u9053\u662f\u4e2d\u4e86\u5582\u6bd2\u7684\u6697\u5668\uff0c\u4f46\u7ec6\u7ec6\u68c0\u89c6\uff0c\u8863\u670d\u5e76\u672a\u7834\u70c2\uff0c\u76ae\u8089\u4e5f\u672a\u53d7\u635f\uff0c\u5fc3\u4e2d \u6697\u6697\u7eb3\u95f7\u3002 \u3000\u3000\u8fd9\u65f6\u62bc\u4e0d\u5e90\u4e5f\u5df2\u9192\u6765\uff0c\u89c1\u8fd9\u822c\u60c5\u666f\uff0c\u83ab\u660e\u5176\u5999\uff0c\u62d4\u5f00\u6c34\u56ca\uff0c\u4e5f\u559d\u4e86\u513f\u53e3\u6c34\u3002\u98de\u7ea2\u5dfe\u89c1\u4ed6 \u8d77\u6765\uff0c\u6b63\u60f3\u559d\u4ed6\uff0c\u5ffd\u89c1\u4ed6\u4e5f\u201c\u5495\u549a\u201d\u4e00\u58f0\u5012\u5728\u5730\u4e0a\u3002\u5fc3\u4e2d\u5927\u9a87\uff0c\u77e5\u9053\u90a3\u888b\u6c34\u5df2\u7ed9\u695a\u662d\u5357\u653e\u4e0b \u6bd2\u836f\uff0c\u77ed\u5251\u4e00\u5251\u523a\u53bb\uff0c\u628a\u6c34\u56ca\u523a\u7834\uff0c\u6c34\u6d41\u89e6\u5730\uff0c\u970e\u90a3\u5c31\u7ed9\u5730\u4e0b\u7684\u9ec4\u6c99\u5438\u5f97\u5e72\u5e72\u51c0\u51c0\uff01 \u3000\u3000\u98de\u7ea2\u5dfe\u5148\u6478\u6478\u6768\u4e91\u806a\u7684\u5fc3\u53e3\uff0c\u53c8\u6478\u6478\u62bc\u4e0d\u5e90\u7684\u5fc3\u53e3\uff0c\u53ea\u89c1\u4e24\u4eba\u7684\u5fc3\u90fd\u5728\u8df3\u52a8\uff0c\u9762\u4e0a\u4e5f\u4e0d \u89c1\u6709\u4ec0\u4e48\u9ed1\u6c14\uff0c\u53ea\u662f\u547c\u547c\u7684\u7761\u5f97\u5f88\u751c\uff0c\u677e\u4e86\u53e3\u6c14\uff0c\u7d22\u6027\u6301\u97ad\u4ed7\u5251\uff0c\u5b88\u5728\u4e24\u4eba\u8eab\u8fb9\u3002 \u3000\u3000\u8fd9\u4e00\u5b88\u76f4\u5b88\u5230\u7b2c\u4e8c\u5929\u7684\u4e2d\u5348\uff0c\u6768\u4e91\u806a\u624d\u60a0\u60a0\u8f6c\u9192\uff0c\u7b2c\u4e00\u53e5\u8bdd\u5c31\u95ee\u9053\uff1a\u201c\u695a\u662d\u5357\u8fd9\u53ae\u9003\u8dd1 \u4e86\uff1f\u201d\u98de\u7ea2\u5dfe\u70b9\u4e86\u70b9\u5934\uff0c\u6768\u4e91\u806a\u53eb\u58f0\u201c\u60ed\u6127\uff01\u201d\u8e66\u8d77\u8eab\u6765\uff0c\u6d3b\u52a8\u7b4b\u9aa8\uff0c\u53ea\u89c9\u4e00\u5982\u5e73\u65f6\uff0c\u8bf4 \u9053\uff1a\u201c\u8fd9\u53ae\u4e0d\u77e5\u662f\u4ec0\u4e48\u65f6\u5019\u628a\u8499\u6c57\u836f\u5077\u5077\u653e\u8fdb\u6c34\u91cc\uff0c\u54ce\uff0c\u8fd9\u53ef\u602a\u6211\u5927\u8fc7\u7c97\u5fc3\uff0c\u60f3\u4e0d\u5230\u4ed6\u4f1a\u81ea \u5df1\u89e3\u7a74\uff01\u201d\u98de\u7ea2\u5dfe\u60f3\u4e86\u4e00\u60f3\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u6bd4\u4f60\u66f4\u7c97\u5fc3\uff0c\u4ed6\u559d\u6c34\u65f6\uff0c\u4f0f\u5728\u5730\u4e0a\uff0c\u6562\u60c5\u5c31\u662f\u5728\u90a3 \u4e2a\u65f6\u5019\u505a\u7684\u624b\u811a\u3002\u54fc\uff01\u6211\u4eec\u4e24\u4eba\u90fd\u7c97\u5fc3\uff0c\u56e0\u6b64\u90fd\u4e0d\u8981\u57cb\u6028\u4e86\u3002\u8c05\u4ed6\u4e5f\u9003\u4e0d\u5230\u90a3\u91cc\u53bb\uff01\u201d\u8bf4\u7f62 \u54c8\u54c8\u4e00\u7b11\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\u62bc\u4e0d\u5e90\u4e5f\u9192\u8f6c\u6765\uff0c\u89c1\u98de\u7ea2\u5dfe\u548c\u6768\u4e91\u806a\u8c08\u7b11\u751a\u6b22\uff0c\u53c8\u5992\u53c8\u6068\u53c8\u662f\u5bb3\u6015\u3002\u54c0\u6c42\u9053\uff1a \u201c\u98de\u7ea2\u5dfe\uff0c\u4f60\u653e\u6211\u8d70\u5427\uff01\u201d\u98de\u7ea2\u5dfe\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u8981\u653e\u4f60\u8d70\uff1f\u4f60\u82e5\u6ca1\u6709\u505a\u9519\uff0c\u56de\u5230\u90e8\u843d\u91cc\u53bb\uff0c \u53c8\u6015\u4ec0\u4e48\uff1f\u201d\u62bc\u4e0d\u5e90\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u98de\u7ea2\u5dfe\uff0c\u6211\u4eec\u603b\u7b97\u76f8\u597d\u4e00\u573a\uff0c\u4f60\u82e5\u53e6\u5916\u6709\u4e86\u559c\u6b22\u7684\u4eba\uff0c\u5c31 \u8ba9\u6211\u53bb\u5427\uff0c\u6211\u5728\u5929\u6daf\u6d77\u89d2\uff0c\u4e5f\u4f1a\u7ed9\u4f60\u4eec\u5531\u6b4c\uff0c\u6c42\u771f\u795e\u4fdd\u4f51\u4f60\u4eec\uff01\u201d\u98de\u7ea2\u5dfe\u5927\u6012\uff0c\u4e00\u97ad\u626b\u53bb\uff0c \u559d\u9053\uff1a\u201c\u80e1\u8bf4\uff01\u4f60\u5f53\u6211\u662f\u4ec0\u4e48\u4eba\u6765\u4e86\uff01\u8fd9\u6b21\u56de\u53bb\uff0c\u82e5\u4f60\u65e0\u7f6a\uff0c\u6211\u4f1a\u5411\u4f60\u966a\u7f6a\uff0c\u4f46\u4ee5\u4f60\u8fd9\u6837\u7684 \u4eba\u54c1\uff0c\u6211\u4e0d\u4f1a\u518d\u559c\u6b22\u4f60\uff0c\u82e5\u4f60\u771f\u662f\u8c0b\u6740\u4e86\u6211\u7684\u7236\u4eb2\uff0c\u54fc\uff0c\u90a3\u6211\u53ef\u8981\u4eb2\u624b\u5bb0\u4f60\uff01\u4f60\u82e5\u73b0\u5728\u8981 \u9003\uff0c\u90a3\u53ef\u66f4\u662f\u627e\u6b7b\uff01\u6211\u4f1a\u628a\u4f60\u5272\u788e\uff01\u201d\u62bc\u4e0d\u5e90\u5413\u5f97\u9762\u65e0\u4eba\u8272\uff0c\u6218\u6218\u5162\u5162\uff0c\u54ea\u91cc\u8fd8\u6562\u518d\u8bf4\u534a \u53e5\u3002 \u3000\u3000\u98de\u7ea2\u5dfe\u62bc\u7740\u62bc\u4e0d\u5e90\u4e0a\u9a6c\uff0c\u5bf9\u6768\u4e91\u806a\u9053\uff1a\u201c\u4f60\u4e5f\u5230\u6211\u4eec\u90a3\u91cc\u53bb\u5427\uff0c\u6211\u4eec\u7684\u65cf\u4eba\u4e00\u5b9a\u5f88\u6b22\u8fce \u4f60\uff01\u201d \u3000\u3000\u6768\u4e91\u806a\u9053\u58f0\u201c\u597d\uff01\u201d\u8de8\u4e0a\u9a6c\u80cc\uff0c\u5c31\u4e0e\u4ed6\u4eec\u540c\u884c\u3002 \u3000\u3000\u5feb\u9a6c\u884c\u4e86\u4e24\u5929\uff0c\u7b2c\u4e09\u65e5\u8d70\u8fc7\u5357\u7586\u7684\u201c\u94c1\u95e8\u5173\u201d\uff0c\u53ea\u89c1\u4e00\u6392\u9ad8\u5c71\u4e2d\u95f4\uff0c\u5288\u5f00\u4e00\u6761\u9699\u7f1d\uff0c\u4e00 \u6761\u6025\u6e4d\u7684\u6cb3\u6d41\uff0c\u5c31\u4ece\u8fd9\u9699\u7f1d\u4e2d\u901a\u8fc7\u3002\u98de\u7ea2\u5dfe\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u6211\u4eec\u5357\u7586\u6709\u540d\u7684\u5b54\u96c0\u6cb3\u4e86\u3002\u201d\u62bc\u4e0d \u5e90\u9762\u8272\u82cd\u767d\uff0c\u53c8\u53d6\u51fa\u82a6\u7afa\uff0c\u53c8\u5531\u8d77\u54c0\u4f24\u7684\u6b4c\u513f\u3002\u98de\u7ea2\u5dfe\u5148\u662f\u76b1\u76b1\u7709\u5934\uff0c\u540e\u53c8\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u5531 \u5427\uff1a\u5531\u5427\uff0c\u8ba9\u4f60\u5531\u4e00\u5929\uff0c\u4ee5\u540e\u518d\u4e0d\u542c\u4f60\u5531\u4e86\uff01\u201d\u62bc\u4e0d\u5e90\u53c8\u54c0\u6c42\u9053\uff1a\u201c\u98de\u7ea2\u5dfe\uff0c\u4f60\u4e0d\u662f\u5f88\u7231\u6211 \u7684\u6b4c\u5417\uff1f\u4f60\u613f\u610f\u4ee5\u540e\u6c38\u8fdc\u542c\u4e0d\u5230\u8fd9\u6b4c\u58f0\u5417\uff1f\u201d\u98de\u7ea2\u5dfe\u97ad\u5b50\u5237\u5730\u4e00\u54cd\u4f46\u5374\u5e76\u4e0d\u6253\u4ed6\uff0c\u53ea\u4f5c\u52bf\u8bf4 \u9053\uff1a\u201c\u4f60\u7231\u5531\u5c31\u5531\uff01\u518d\u591a\u8bdd\uff0c\u6211\u5c31\u8981\u6253\u4f60\u4e86\uff01\u201d \u3000\u3000\u8d70\u8fc7\u4e86\u201c\u94c1\u95e8\u5173\u201d\uff0c\u524d\u9762\u662f\u4e00\u5927\u7247\u8349\u539f\uff0c\u5b54\u96c0\u6cb3\u5728\u8349\u5730\u4e0a\u873f\u8712\u5982\u5e26\uff0c\u8fdc\u5904\u96ea\u5c71\u9690\u73b0\uff0c\u4e91 \u5f69\u53d8\u5e7b\uff0c\u4e24\u5cb8\u5782\u6768\u4e1d\u4e1d\u98d8\u62c2\uff0c\u666f\u8272\u96c4\u58ee\u4e4b\u4e2d\uff0c\u5e26\u7740\u65d6\u65ce\uff0c\u6768\u4e91\u806a\u5fc3\u80f8\u5f00\u9614\uff0c\u5f39\u5251\u957f\u5578\u3002\u98de\u7ea2 \u5dfe\u9053\uff1a\u201c\u5230\u4e86\uff01\u201d\u957f\u97ad\u9065\u6307\uff0c\u8fdc\u5904\u5df2\u9690\u9690\u51fa\u73b0\u708a\u70df\u3002\u62bc\u4e0d\u5e90\u6b4c\u58f0\u9aa4\u6b62\uff0c\u9762\u8272\u76ca\u53d1\u82cd\u767d\u3002 \u3000\u3000\u4e09\u9a91\u9a6c\u5728\u8349\u539f\u4e0a\u75be\u9a70\u800c\u8fc7\uff0c\u4e0d\u4e00\u4f1a\uff0c\u53ea\u89c1\u5e10\u5e55\u6797\u7acb\uff0c\u8bb8\u591a\u7267\u6c11\u8fce\u4e86\u51fa\u6765\uff0c\u5987\u5973\u4eec\u5c0f\u5b69\u4eec \u8dd1\u5728\u524d\u5934\uff0c\u53c8\u8df3\u53c8\u7b11\u3002\u53eb\u9053\uff1a\u201c\u6211\u4eec\u7684\u54c8\u739b\u96c5\uff08\u98de\u7ea2\u5dfe\u4e4b\u540d\uff09\u56de\u6765\u5566\uff01\u201d\u6709\u4e00\u961f\u9752\u5e74\u5f39\u8d77\u4e1c \u4e0d\u62c9\u5531\u9053\uff1a \u3000\u3000\u201c\u6211\u4eec\u7684\u5973\u82f1\u96c4\u54c8\u739b\u96c5\uff0c \u3000\u3000\u5979\u5728\u8349\u539f\u4e4b\u4e0a\u58f0\u540d\u5927\uff0c \u3000\u3000\u5b69\u5b50\u4eec\u770b\u89c1\u5979\u7b11\u54c8\u54c8\uff0c \u3000\u3000\u654c\u4eba\u4eec\u770b\u89c1\u5979\u5c31\u5bb3\u6015\uff01 \u3000\u3000\u767d\u624b\u4e2d\u56db\u8fb9\u4e0a\u7ee3\u6ee1\u4e86\u73ab\u7470\u82b1\uff0c \u3000\u3000\u6325\u52a8\u4e2d\u513f\u6b4c\u5531\u6211\u4eec\u7684\u54c8\u739b\u96c5\uff0c \u3000\u3000\u8349\u539f\u4e0a\u7684\u9752\u5e74\u4eba\u4eba\u77e5\u9053\u5979\uff01 \u3000\u3000\u4f9d\u5566\uff0c\u4f60\u770b\u5979\u7684\u9a6c\u513f\u8dd1\u6765\u5566\uff01\u201d \u3000\u3000\u6768\u4e91\u806a\u4f4e\u4f4e\u8bf4\u9053\uff1a\u201c\u98de\u7ea2\u5dfe\uff0c\u8fd9\u8bb8\u591a\u4eba\u7684\u6b4c\u58f0\u6bd4\u4e00\u4e2a\u4eba\u7684\u6b4c\u58f0\u8981\u597d\u542c\u5f97\u591a\u3002\u201d\u98de\u7ea2\u5dfe\u773c \u89d2\u6f6e\u6e7f\uff0c\u4e5f\u4f4e\u4f4e\u8bf4\u9053\uff1a\u201c\u6211\u77e5\u9053\uff01\u201d\u4e00\u4e0b\u9a6c\uff0c\u7275\u7740\u62bc\u4e0d\u5e90\uff0c\u5e26\u7740\u6768\u4e91\u806a\uff0c\u7f13\u7f13\u5730\u8d70\u8fdb\u4e86\u4eba\u7fa4 \u4e4b\u4e2d\u3002\u62bc\u4e0d\u5e90\u8eab\u5b50\u5fae\u5fae\u98a4\u6296\uff0c\u7aed\u529b\u88c5\u51fa\u4e0d\u5728\u4e4e\u7684\u795e\u60c5\u3002 \u3000\u3000\u5e10\u7bf7\u4e2d\u6700\u540e\u8d70\u51fa\u4e09\u4e2a\u8001\u4eba\uff0c\u987b\u53d1\u5982\u94f6\u3002\u5bf9\u98de\u7ea2\u5dfe\u5f2f\u8170\u4f5c\u793c\uff0c\u98de\u7ea2\u5dfe\u8dea\u4e86\u4e0b\u53bb\uff0c\u6d41\u6cea\u8bf4 \u9053\uff1a\u201c\u6211\u6765\u5f97\u8fdf\u4e86\u3002\u201d\u8001\u4eba\u6276\u8d77\u4e86\u5979\uff0c\u95ee\u9053\uff1a\u201c\u62bc\u4e0d\u5e90\u5df2\u7ecf\u56de\u6765\u4e86\uff0c\u8fd9\u4f4d\u53c8\u662f\u8c01\u5462\uff1f\u201d\u98de\u7ea2 \u5dfe\u9053\uff1a\u201c\u8fd9\u4f4d\u5c31\u662f\u6768\u4e91\u806a\u6768\u5927\u4fa0\uff01\u201d \u3000\u3000\u65c1\u8fb9\u7684\u4eba\u4e00\u9635\u6b22\u547c\uff0c\u9752\u5e74\u4eec\u56f4\u62e2\u4e0a\u6765\uff0c\u4e09\u4e2a\u8001\u4eba\u53c8\u5f2f\u8170\u4f5c\u793c\u3002\u6768\u4e91\u806a\u77e5\u9053\u8fd9\u4e09\u4eba\u5b9a\u662f\u65cf \u4e2d\u957f\u8001\uff0c\u6025\u5fd9\u7b54\u793c\u3002\u8001\u4eba\u9053\uff1a\u201c\u6768\u5927\u4fa0\u6765\uff0c\u597d\u6781\u4e86\uff01\u201d\u957f\u8001\u4eec\u628a\u98de\u7ea2\u5dfe\u5f15\u8fdb\u5e10\u4e2d\uff0c\u628a\u62bc\u4e0d\u5e90 \u7f1a\u5728\u5e10\u5916\uff0c\u5e26\u6768\u4e91\u806a\u53bb\u6c90\u6d74\u6b47\u606f\u3002\u5728\u8349\u539f\u4e0a\u4f5c\u5ba2\uff0c\u4e3b\u5bb6\u8bf7\u5ba2\u6c90\u6d74\uff0c\u90a3\u662f\u5bf9\u6700\u5c0a\u8d35\u5ba2\u4eba\u7684\u5f85 \u9047\u3002 \u3000\u3000\u9ec4\u660f\u65e5\u843d\uff0c\u8349\u539f\u4e0a\u65b0\u6708\u5347\u8d77\uff0c\u665a\u996d\u4e4b\u540e\uff0c\u5e10\u5e55\u5916\u7684\u8349\u5730\u4e0a\u70e7\u8d77\u91ce\u706b\uff0c\u7f57\u5e03\u65cf\u7684\u5987\u5973\u4eec\u9752 \u5e74\u4eec\uff0c\u5f39\u7740\u5404\u79cd\u4e50\u5668\uff0c\u56f4\u7740\u91ce\u706b\uff0c\u9ad8\u58f0\u5531\u6b4c\u3002\u6b4c\u58f0\u82cd\u51c9\u60b2\u58ee\uff0c\u4ee4\u4eba\u6fc0\u6124\u3002\u4e00\u4e2a\u957f\u8001\u63ed\u5f00\u5e10\u5e55 \u8fdb\u6765\u8bf7\u9053\uff1a\u201c\u6768\u5927\u4fa0\uff0c\u4eca\u665a\u6211\u4eec\u793c\u796d\u5510\u52aa\u8001\u82f1\u96c4\uff01\u201d\u6768\u4e91\u806a\u8df3\u8d77\u6765\u9053\uff1a\uff1a\u201c\u8bf7\u501f\u4e00\u624e\u9999\uff0c\u6211 \u4e5f\u8981\u5411\u8001\u82f1\u96c4\u81f4\u656c\uff01\u201d\u957f\u8001\u8bf4\uff1a\u201c\u7559\u5f85\u54c8\u739b\u96c5\u796d\u8fc7\u518d\u8bf4\u5427\u3002\u201d\u6768\u4e91\u806a\u8ddf\u4ed6\u8d70\u51fa\u5e10\u5e55\uff0c\u53ea\u89c1\u98de \u7ea2\u5dfe\u548c\u62bc\u4e0d\u5e90\u5df2\u7ad9\u5728\u8349\u5730\u4e0a\uff0c\u98de\u7ea2\u5dfe\u5168\u8eab\u9550\u767d\uff0c\u62bc\u4e0d\u5e90\u9762\u5982\u6b7b\u7070\uff0c\u6c14\u6c1b\u5341\u5206\u6c89\u91cd\u3002 \u3000\u3000------------------ \u3000\u3000 \u626c\u5251\u8f69\u5c45\u58eb \u626b\u63cf\u6821\u5bf9  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u585e\u5916\u5947\u4fa0\u4f20\u300b\u2014\u2014\u7b2c\u516b\u56de\u3000\u8349\u539f\u591c\u796d \u6881\u7fbd\u751f\u300a\u585e\u5916\u5947\u4fa0\u4f20\u300b \u7b2c\u516b\u56de\u3000\u8349\u539f\u591c\u796d \u3000\u3000\u4ef2\u590f\u591c\u7684\u8349\u539f\uff0c\u5929\u7a7a\u7279\u522b\u660e\u51c0\uff0c\u6ee1\u5929\u661f\u6597\uff0c\u50cf\u4e00\u7c92\u7c92\u7684\u5b9d\u77f3\u9576\u5d4c\u5728\u84dd\u7ed2\u5e55\u4e0a\uff0c\u8fdc\u5904\u96ea\u5c71 \u51b0\u5cf0\u77d7\u7acb\u5728\u6df1\u84dd\u8272\u7684\u591c\u7a7a\u4e2d\uff0c\u50cf\u6c34\u6676\u4e00\u6837\u95ea\u95ea\u53d1\u5149\u3002\u8349\u539f\u4e0a\uff0c\u7f57\u5e03\u65cf\u4eba\u56f4\u7740\u91ce\u706b\uff0c\u56f4\u7740\u4ed6\u4eec \u7684\u5973\u82f1\u96c4\u98de\u7ea2\u5dfe\uff0c\u4e5f\u56f4\u7740\u53db\u5f92\u62bc\u4e0d\u5e90\u3002\u8349\u539f\u4e0a\u5df2\u642d\u8d77\u4e00\u5ea7\u9ad8\u53f0\uff0c\u9ad8\u53f0\u4e0a\u653e\u7740\u4e00\u4e2a\u4e09\u5c3a\u6765\u9ad8\u7684 \u74f7\u74f6\uff0c\u4e09\u4e2a\u957f\u8001\u8dea\u5728\u74f7\u74f6\u4e4b\u524d\uff0c\u9ed8\u9ed8\u7948\u7977\u3002\u53f0\u4e0b\u9e26\u96c0\u65e0\u58f0\uff0c\u7a7a\u6c14\u5341\u5206\u8083\u7a46\u3002\u6768\u4e91\u806a\u7528\u773c\u89d2\u5077 \u779f\u98de\u7ea2\u5dfe\uff0c\u53ea\u89c1\u98de\u7ea2\u5dfe\u5782\u4e0b\u4e86\u5934\uff0c\u773c\u89d2\u6709\u6676\u83b9\u7684\u6cea\u5149\u3002\u6768\u4e91\u806a\u4e3a\u5979\u96be\u8fc7\u3002\u5fc3\u4e2d\u6697\u53f9\u5728\u8fd9\u6837\u7f8e \u4e3d\u7684\u8349\u539f\u4e4b\u591c\uff0c\u6f14\u51fa\u7684\u5374\u662f\u8fd9\u6837\u6c89\u91cd\u7684\u60b2\u5267\u3002 \u3000\u3000\u4e09\u4e2a\u957f\u8001\u7948\u7977\u5b8c\u4e86\uff0c\u9ed8\u9ed8\u7684\u7ad9\u4e86\u8d77\u6765\u3002\u98de\u7ea2\u5dfe\u5e26\u62bc\u4e0d\u5e90\u8d70\u4e0a\u9ad8\u53f0\uff0c\u9996\u5ea7\u957f\u8001\u4f38\u5f00\u53cc\u624b\u8bf4 \u9053\uff1a\u201c\u62bc\u4e0d\u5e90\uff0c\u5728\u5510\u52aa\u8001\u82f1\u96c4\u7684\u9aa8\u7070\u4e4b\u524d\uff0c\u4f60\u77e5\u7f6a\u4e48\uff1f\u201d\u62bc\u4e0d\u5e90\u9762\u5982\u6b7b\u7070\uff0c\u9ed8\u4e0d\u4f5c\u58f0\u3002\u957f\u8001 \u624b\u638c\u4e00\u6325\uff0c\u53eb\u9053\uff1a\u201c\u5e26\u90a3\u6e05\u519b\u4fd8\u864f\u6765\uff01\u201d\u53f0\u4e0b\u4e00\u58f0\u5e94\u8bfa\uff0c\u4e24\u540d\u7f57\u5e03\u65cf\u52c7\u58eb\uff0c\u62bc\u7740\u4fd8\u864f\u4e0a\u53f0\uff0c \u957f\u8001\u94f6\u987b\u98d8\u52a8\uff0c\u548c\u989c\u60a6\u8272\u5bf9\u4fd8\u864f\u9053\uff1a\u201c\u4f60\u8bf4\u771f\u8bdd\uff0c\u6211\u4eec\u51b3\u4e0d\u5bb3\u4f60\uff01\u201d\u90a3\u4fd8\u864f\u56de\u8fc7\u8eab\u6765\uff0c\u4e00\u9762 \u5bf9\u7740\u53f0\u4e0b\u4f17\u4eba\uff0c\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u662f\u6e05\u519b\u84dd\u65d7\u90fd\u7edf\u963f\u5df4\u53e4\u7684\u536b\u58eb\uff0c\u4e0a\u6708\u5728\u963f\u514b\u82cf\u8349\u539f\u548c\u4f60\u4eec\u6253 \u4ed7\uff0c\u6fc0\u6218\u4e86\u4e09\u5929\u4e09\u591c\uff0c\u6211\u4eec\u4f24\u4ea1\u5f88\u91cd\uff0c\u8fd8\u6015\u4f60\u4eec\u7ee7\u6709\u63f4\u519b\uff0c\u90fd\u7edf\u672c\u6765\u51c6\u5907\u5728\u7b2c\u4e8c\u65e5\u5c31\u62d4\u5be8\u9000 \u519b\u3002\u90a3\u5929\u665a\u4e0a\uff0c\u4e2d\u519b\u8fdb\u89c1\u90fd\u7edf\uff0c\u8bf4\u5df2\u548c\u4f60\u4eec\u90a3\u8fb9\u7684\u5185\u5e94\u8054\u7edc\u4e0a\u4e86\uff0c\u968f\u5373\u4ea4\u51fa\u4e00\u7247\u7af9\u7b80\uff0c\u7af9\u7b80 \u4e0a\u4e66\u6709\u5730\u56fe\uff0c\u8fd8\u523b\u6709\u2018\u7b2c\u4e09\u5ea7\u5e10\u5e55\uff0c\u63f4\u519b\u96be\u8d76\u6765\u2019\u5341\u4e2a\u5c0f\u5b57\u3002\u90fd\u7edf\u95ee\u4e86\u4e00\u58f0\uff1a\u90a3\u4eba\u53ef\u9760\u5417\uff1f \u4e2d\u519b\u9053\uff1a\u7edd\u5bf9\u53ef\u9760\uff0c\u662f\u62c5\u4fdd\u695a\u662d\u5357\u7684\u3002\u90fd\u7edf\u2018\u6664\u2019\u4e86\u4e00\u58f0\uff0c\u7b2c\u4e8c\u665a\u5c31\u6284\u6377\u5f84\u53bb\u591c\u88ad\u3002\u540e\u6765\u6211 \u624d\u77e5\u9053\uff0c\u7b2c\u4e09\u5ea7\u5e10\u5e55\u5c31\u662f\u4f60\u4eec\u65cf\u957f\u7684\u8d26\u5e55\u3002\u6211\u4eec\u8fdb\u4e86\u5e10\u5e55\uff1b\u5510\u52aa\u8001\u82f1\u96c4\u53ea\u6709\u51e0\u4e2a\u4eb2\u5175\u966a\u7740 \u4ed6\uff0c\u53ef\u662f\u4ed6\u4f5c\u6218\u975e\u5e38\u52c7\u6562\uff0c\u6211\u4eec\u4eec\u7684\u90fd\u7edf\u672c\u60f3\u628a\u4ed6\u751f\u64d2\u7684\uff0c\u7ed9\u4ed6\u4e00\u8fde\u65a9\u6740\u6211\u4eec\u5341\u51e0\u540d\u52c7\u58eb\uff0c \u4ed6\u81ea\u5df1\u4e5f\u662f\u8840\u67d3\u6218\u888d\uff0c\u53d7\u4f24\u5f88\u91cd\u3002\u90fd\u7edf\u89c1\u4ed6\u53d7\u4e86\u91cd\u4f24\uff0c\u8fd8\u662f\u6076\u6218\uff0c\u4eb2\u81ea\u5e26\u9886\u536b\u58eb\u4e0a\u53bb\u56f4\u6349\uff0c \u4e0d\u6599\u4ed6\u864e\u543c\u4e00\u58f0\uff0c\u5ffd\u7136\u6740\u4e86\u51fa\u6765\uff0c\u53c8\u65a9\u4e86\u6211\u4eec\u4e24\u540d\u536b\u58eb\uff0c\u90fd\u7edf\u4e00\u5200\u523a\u8fdb\u4ed6\u7684\u80f8\u53e3\uff0c\u4ed6\u7684\u5175\u5668 \u4e5f\u7ed9\u6211\u4eec\u6253\u6389\u3002\u54ea\u6599\u4ed6\u5168\u8eab\u6251\u4e0a\uff0c\u62b1\u7740\u90fd\u7edf\u4e0d\u653e\u3002\u536b\u58eb\u4eec\u4e00\u9635\u4e71\u5200\u628a\u4ed6\u65a9\u6b7b\uff0c\u62c9\u4e86\u8d77\u6765\uff0c\u4e00 \u770b\uff0c\u6211\u4eec\u7684\u90fd\u7edf\u4e5f\u5df2\u7ed9\u4ed6\u627c\u6b7b\u4e86\uff01\u6211\u8d76\u7d27\u6536\u62fe\u90fd\u7edf\u7684\u9057\u7269\uff0c\u9000\u51fa\u5e10\u5e55\uff0c\u60f3\u53bb\u62a5\u544a\u526f\u7edf\u9886\uff0c\u54ea \u6599\u521a\u51fa\u5e10\u7bf7\uff0c\u5c31\u78b0\u5230\u4f60\u4eec\u4e00\u961f\u52c7\u58eb\uff0c\u62fc\u6b7b\u6765\u6551\u5510\u52aa\u8001\u82f1\u96c4\uff0c\u6211\u4eec\u4e00\u961f\u536b\u58eb\uff0c\u53ea\u6709\u6211\u53d7\u4f24\u88ab \u4fd8\uff0c\u5176\u4f59\u5168\u6218\u6b7b\u4e86\uff01\u201d \u3000\u3000\u90a3\u4fd8\u864f\u8bb2\u5b8c\u4e4b\u540e\uff0c\u53f0\u4e0b\u8d77\u4e86\u4e00\u7247\u555c\u6ce3\u58f0\uff0c\u9996\u5ea7\u957f\u8001\u5408\u638c\u8bf4\u9053\uff1a\u201c\u4ed6\u7684\u540d\u5b57\u662f\u6211\u4eec\u7f57\u5e03\u65cf \u7684\u5149\u8363\uff0c\u4ed6\u7684\u9c9c\u8840\u4fdd\u5b58\u4e86\u6211\u4eec\u7684\u513f\u7ae5\u548c\u5987\u5973\uff0c\u4ed6\u4e0d\u6127\u662f\u771f\u795e\u963f\u62c9\u7684\u513f\u5b50\uff0c\u4ed6\u4e0d\u6127\u662f\u6211\u4eec\u7684\u7236 \u4eb2\u3002\u4ed6\u7684\u540d\u5b57\u6c38\u5782\u4e0d\u673d\uff01\u201d\u53f0\u4e0b\u5de8\u96f7\u822c\u7684\u5e94\u9053\uff1a\u201c\u5510\u52aa\u8001\u82f1\u96c4\u6c38\u5782\u4e0d\u673d\uff01\u201d\u6768\u4e91\u806a\u70ed\u8840\u6cb8 \u817e\uff0c\u5fc3\u9053\uff1a\u6709\u8fd9\u6837\u82f1\u96c4\u7684\u7236\u4eb2\uff0c\u602a\u4e0d\u5f97\u6709\u90a3\u6837\u82f1\u96c4\u7684\u5973\u513f\uff01 \u3000\u3000\u957f\u8001\u9882\u8d5e\u5b8c\u4e86\uff0c\u5f85\u4f17\u4eba\u9759\u4e0b\uff0c\u53c8\u95ee\u90a3\u540d\u4fd8\u864f\u9053\uff1a\u201c\u90fd\u7edf\u7684\u9057\u7269\u662f\u4f60\u6536\u85cf\uff0c\u90a3\u7247\u7af9\u7b80\u53ef\u5728 \u91cc\u9762\u5417\uff1f\u201d\u4fd8\u864f\u70b9\u4e86\u70b9\u5934\uff0c\u4ece\u6000\u4e2d\u638f\u51fa\u4e00\u7247\u7af9\u7b80\uff0c\u957f\u8001\u63a5\u8fc7\u6765\uff0c\u8f6c\u9012\u7ed9\u98de\u7ea2\u5dfe\u9053\uff1a\u201c\u54c8\u739b \u96c5\uff0c\u4f60\u81ea\u5df1\u53bb\u770b\uff01\u201d \u3000\u3000\u98de\u7ea2\u5dfe\u63a5\u8fc7\u7af9\u7b80\uff0c\u4f4e\u5934\u4e00\u770b\uff0c\u9762\u8272\u5927\u53d8\u3002\u4e0a\u9762\u523b\u7740\u7684\u5b57\uff0c\u6b63\u662f\u62bc\u4e0d\u5e90\u7684\u7b14\u8ff9\u3002\u867d\u7136\u5979\u4e00 \u8def\u4e0a\u5df2\u5bf9\u62bc\u4e0d\u5e90\u8d77\u4e86\u5f88\u5927\u7684\u6000\u7591\uff0c\u53ef\u662f\u5fc3\u4e2d\u6709\u65f6\u8fd8\u5e0c\u671b\u90a3\u662f\u5047\u7684\u3002\u8fd9\u5fc3\u60c5\u975e\u5e38\u5fae\u5999\uff0c\u62bc\u4e0d\u5e90 \u5230\u5e95\u662f\u5979\u66fe\u7231\u8fc7\u7684\u4eba\uff0c\u5979\u5b9e\u5728\u4e0d\u6562\u60f3\u8c61\u4ed6\u662f\u90a3\u6837\u5351\u52a3\u7684\u6c49\u5b50\u3002 \u3000\u3000\u9996\u5e2d\u957f\u8001\u89c1\u98de\u7ea2\u5dfe\u6367\u7740\u7af9\u7b80\u7684\u53cc\u624b\u5fae\u5fae\u98a4\u6296\uff0c\u8d70\u4e86\u8fc7\u6765\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u54c8\u739b\u96c5\uff0c\u6211\u4eec\u7684 \u65cf\u4eba\u90fd\u770b\u7740\u4f60\uff01\u4f60\u8bf4\u8be5\u600e\u4e48\u529e\uff01\u201d\u98de\u7ea2\u5dfe\u84e6\u7136\u79c0\u7709\u4e00\u6311\uff0c\u9762\u5bf9\u65cf\u4eba\uff0c\u626c\u7740\u7af9\u7b80\u8bf4\u9053\uff1a\u201c\u771f\u51ed \u5b9e\u636e\u5df2\u5728\u773c\u524d\uff0c\u5bb3\u6b7b\u6211\u7236\u4eb2\u7684\uff0c\u5c31\u662f\u8fd9\u4e2a\u62bc\u4e0d\u5e90\uff01\u201d\u5979\u4e00\u4e2a\u65cb\u8eab\uff0c\u5c06\u7af9\u7b80\u5f80\u62bc\u4e0d\u5e90\u9762\u524d\uff0c\u559d \u5230\uff1a\u201c\u4f60\u6562\u8bf4\u8fd9\u4e2a\u4e0d\u662f\u4f60\u523b\u7684\u5417\uff1f\u201d\u62bc\u4e0d\u5e90\u98a4\u58f0\u8bf4\u9053\uff1a\u201c\u662f\u6211\u523b\u7684\uff01\u201d\u98de\u7ea2\u5dfe\u51c4\u5389\u957f\u7b11\uff0c\u53eb \u9053\uff1a\u201c\u628a\u4ed6\u7ed1\u8d77\u6765\uff0c\u6211\u8981\u53d6\u4ed6\u7684\u5fc3\u809d\u796d\u5960\uff01\u201d \u3000\u3000\u8fd9\u65f6\u523b\u53f0\u4e0b\u9e26\u96c0\u65e0\u58f0\uff0c\u7a7a\u6c14\u6b7b\u5bc2\u3002\u9664\u4e86\u4e09\u4e2a\u957f\u8001\u4e4b\u5916\uff0c\u5176\u4ed6\u7684\u4eba\uff0c\u4e8b\u5148\u4e0d\u77e5\u9053\u62bc\u4e0d\u5e90\u5c31 \u662f\u5978\u7ec6\u3002\u62bc\u4e0d\u5e90\u662f\u8bb8\u591a\u59d1\u5a18\u5fc3\u7231\u7684\u6b4c\u624b\uff0c\u8c01\u90fd\u6ca1\u6709\u6599\u5230\uff0c\u6b4c\u58f0\u5531\u5f97\u90a3\u6837\u7f8e\u5999\u7684\u4eba\uff0c\u5fc3\u5730\u7adf\u662f \u90a3\u4e48\u80ae\u810f\u3002\u9752\u5e74\u4eec\u53c8\u5168\u90fd\u77e5\u9053\u62bc\u4e0d\u5e90\u662f\u98de\u7ea2\u5dfe\u7684\u60c5\u4eba\uff0c\u8fd9\u65f6\u9664\u4e86\u66ff\u98de\u7ea2\u5dfe\u96be\u8fc7\u4e4b\u5916\uff0c\u5168\u90fd\u6000 \u7740\u53c8\u60ca\u5947\u53c8\u6218\u6817\u7684\u5fc3\u60c5\uff0c\u770b\u7740\u98de\u7ea2\u5dfe\u3002\u98de\u7ea2\u5dfe\u62d4\u51fa\u77ed\u5251\uff0c\u8dea\u5728\u88c5\u7740\u7236\u4eb2\u9aa8\u7070\u7684\u74f7\u74f6\u4e0b\u9762\uff0c\u54ed \u9053\uff1a\u201c\u7236\u4eb2\u554a\uff01\u5973\u513f\u66ff\u4f60\u590d\u4ec7\u4e86\uff01\u201d\u5728\u4f17\u4eba\u6ce8\u89c6\u4e0b\uff0c\u98de\u7ea2\u5dfe\u500f\u5730\u8d77\u8eab\uff0c\u64e6\u5e72\u773c\u6cea\uff0c\u77ed\u5251\u5728\u591c \u7a7a\u4e2d\u95ea\u95ea\u53d1\u5149\uff0c\u4e00\u6b65\u4e00\u6b65\uff0c\u8d70\u8fd1\u62bc\u4e0d\u5e90\uff01 \u3000\u3000\u62bc\u4e0d\u5e90\u5ffd\u7136\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u98de\u7ea2\u5dfe\uff0c\u4f60\u51c6\u4e0d\u51c6\u6211\u8bf4\u51e0\u53e5\u8bdd\uff1f\u201d\u957f\u8001\u9053\uff1a\u201c\u82e5\u6709\u51a4\u5c48\uff0c\u5c3d\u53ef \u8fa9\u89e3\uff01\u201d\u98de\u7ea2\u5dfe\u5012\u63d0\u9752\u950b\uff0c\u8feb\u8fd1\u4e00\u6b65\uff0c\u9661\u7136\u505c\u4e0b\uff0c\u559d\u9053\uff1a\u201c\u4f60\u8bf4\uff01\u201d \u3000\u3000\u62bc\u4e0d\u5e90\u54c8\u54c8\u72c2\u7b11\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u98de\u7ea2\u5dfe\uff0c\u4f60\u7684\u76ae\u97ad\u5462\uff1f\u4f60\u628a\u6211\u7528\u5251\u523a\u6b7b\u5427\uff0c\u6211\u518d\u4e0d\u7528\u6015 \u4f60\u7684\u76ae\u97ad\u4e86\uff01\u201d \u3000\u3000\u201c\u6211\u4e0d\u60f3\u8fa9\u89e3\uff0c\u5510\u52aa\u8001\u65cf\u957f\u56e0\u6211\u800c\u6b7b\uff0c\u8fd9\u662f\u6211\u7684\u9519\uff0c\u4f46\uff0c\u98de\u7ea2\u5dfe\uff0c\u96be\u9053\u4f60\u5c31\u6ca1\u6709\u9519\u5417\uff1f \u3000\u3000\u201c\u6211\uff0c\u62bc\u4e0d\u5e90\uff0c\u53eb\u505a\u4f60\u7684\u60c5\u4eba\uff0c\u4f46\u4f60\u52a8\u4e0d\u52a8\u5c31\u7528\u76ae\u97ad\u5a01\u80c1\u6211\uff0c\u4e8b\u65e0\u5927\u5c0f\uff0c\u4e00\u5207\u90fd\u8981\u542c\u4f60 \u7684\u8bdd\uff0c\u6211\u54ea\u91cc\u50cf\u4f60\u7684\u60c5\u4eba\uff0c\u53ea\u662f\u50cf\u4e00\u4e2a\u5351\u5fae\u7684\u4ec6\u4eba\uff0c\u800c\u4f60\u5c31\u662f\u6211\u81f3\u9ad8\u65e0\u4e0a\u7684\u4e3b\u5b50\uff01 \u3000\u3000\u201c\u5c31\u662f\u4f60\u8868\u793a\u7231\u6211\u7684\u65f6\u5019\uff0c\u4e5f\u603b\u662f\u628a\u6211\u5f53\u4f5c\u4e0d\u61c2\u4e8b\u7684\u5c0f\u5b69\u5b50\uff0c\u2018\u62bc\u4e0d\u5e90\uff0c\u4e56\u4e56\u7684\u542c\u8bdd \u554a\uff01\u2019\u2018\u62bc\u4e0d\u5e90\u505a\u8fd9\u6837\u4e0d\u8981\u505a\u90a3\u6837\u554a\uff01\u2019\u2018\u62bc\u4e0d\u5e90\uff0c\u73b0\u5728\u6211\u6709\u70b9\u70e6\u95f7\u5566\uff0c\u4f60\u8d76\u5feb\u7ed9\u6211\u5531\u6b4c \u5427\uff01\u2019\u2018\u62bc\u4e0d\u5e90\uff0c\u5728\u6211\u8eab\u8fb9\uff0c\u4f60\u4e0d\u7528\u5bb3\u6015\u5440\uff01\u2019\u4f60\u77a7\uff0c\u4f60\u54ea\u91cc\u662f\u5c06\u6211\u5f53\u4f5c\u540c\u7b49\u7684\u4eba\u5bf9\u5f85\uff0c\u6211 \u50cf\u662f\u4ec0\u4e48\u672c\u9886\u90fd\u6ca1\u6709\u7684\u4eba\uff0c\u5168\u51ed\u4f60\u7684\u4fdd\u62a4\u3002\u9752\u5e74\u4eec\u53c8\u628a\u6211\u5f53\u6210\u2018\u66b4\u53d1\u6237\u2019\uff0c\u597d\u50cf\u5168\u56e0\u4e3a\u4f60\u98de \u7ea2\u5dfe\u628a\u6211\u770b\u4e0a\uff0c\u6211\u8fd9\u624d\u6296\u8d77\u6765\u5566\u3002\u5728\u6211\u4eec\u7684\u6c11\u6b4c\u91cc\uff0c\u7537\u7684\u6bd4\u505a\u592a\u9633\uff0c\u5973\u7684\u6bd4\u505a\u6708\u4eae\u3002\u4f46\u5728\u6211 \u4eec\u4e4b\u95f4\uff0c\u4f60\u662f\u592a\u9633\uff0c\u6211\u53ea\u662f\u4e00\u9897\u9eef\u6de1\u7684\u661f\u661f\uff01\u597d\u50cf\u6211\u82e5\u662f\u6709\u4e00\u70b9\u70b9\u5149\u8f89\uff0c\u4e5f\u5168\u662f\u6cbe\u4f60\u7684\u6069\u6cfd\uff01 \u3000\u3000\u201c\u4f60\u662f\u503c\u5f97\u9a84\u50b2\u7684\uff0c\u6211\u4eec\u8349\u539f\u4e0a\u7684\u5973\u82f1\u96c4\uff0c\u4f60\u8d70\u5230\u54ea\u91cc\uff0c\u5c0f\u4f19\u5b50\u4eec\u5c31\u50cf\u4f17\u661f\u62f1\u6708\u7684\u56f4\u7ed5 \u7740\u4f60\uff01\u53ef\u662f\u96be\u9053\u6211\u6ca1\u6709\u534a\u4e1d\u9a84\u50b2\uff1f\u96be\u9053\u5f53\u6211\u7684\u6b4c\u58f0\u5728\u5927\u8349\u539f\u98d8\u8361\u7684\u65f6\u5019\uff0c\u5438\u5f15\u4e0d\u7740\u5e74\u9752\u59d1\u5a18 \u7684\u773c\u5149\uff0c \u3000\u3000\u201c\u98de\u7ea2\u5dfe\uff0c\u4f60\u662f\u5973\u82f1\u96c4\uff0c\u53ef\u662f\u6211\u5fcd\u53d7\u4e0d\u4e86\uff01\u8fd9\u4e2a\u65f6\u5019\uff0c\u695a\u662d\u5357\u6697\u5730\u6765\u89c1\u6211\uff0c\u53eb\u6211\u5e2e\u4ed6\u7684 \u5fd9\uff0c\u5c06\u5510\u52aa\u8001\u82f1\u96c4\u6349\u53bb\uff0c\u7136\u540e\u5411\u7f57\u5e03\u65cf\u62db\u964d\u3002\u4ed6\u8bf4\uff1a\u6253\u4e86\u8fd9\u4e48\u591a\u5e74\u7684\u4ed7\uff0c\u4eba\u9a6c\u90fd\u75b2\u5026\u4e86\uff0c\u4e0d \u5982\u6295\u987a\u4e86\u6e05\u519b\uff0c\u597d\u597d\u5730\u8fc7\u65e5\u5b50\u5427\u3002\u4f60\u4eec\u8fd9\u65cf\uff0c\u6700\u575a\u51b3\u8981\u6253\u4ed7\u7684\u662f\u5510\u52aa\u7236\u5973\uff0c\u628a\u8001\u7684\u6349\u4f4f\uff0c\u5c0f \u7684\u5c31\u4e0d\u6562\u5f3a\u786c\u5566\uff01\u6253\u4ed7\u4e0d\u6253\u4ed7\uff0c\u6211\u5012\u4e0d\u5728\u4e4e\uff0c\u4f46\u662f\u6211\u6210\u5fc3\u60f3\u6c14\u6c14\u98de\u7ea2\u5dfe\uff0c\u6211\u8981\u505a\u4e00\u6869\u60ca\u4eba\u7684 \u4e8b\uff0c\u4ee4\u5979\u6709\u4e00\u5929\u4e5f\u8981\u6c42\u6211\u3002\u73b0\u5728\u6211\u77e5\u9053\u9519\u5566\uff0c\u98de\u7ea2\u5dfe\uff0c\u4f46\u6211\u4e5f\u4e0d\u6c42\u4f60\u9976\u6055\u4e86\uff0c\u4f60\u7528\u5251\u5256\u5f00\u6211 \u7684\u80f8\u819b\uff0c\u628a\u4f60\u6240\u7231\u8fc7\u7684\u4eba\u7684\u5fc3\u809d\u62ff\u51fa\u6765\u5427\uff01\u201d \u3000\u3000\u98de\u7ea2\u5dfe\u7684\u624b\u7a81\u7136\u98a4\u6296\u8d77\u6765\uff0c\u5979\u6068\u6781\u62bc\u4e0d\u5e90\uff0c\u5979\u5bf9\u4ed6\u7684\u7231\u5df2\u5b8c\u5168\u6d88\u5931\u4e86\uff0c\u5979\u4e0d\u662f\u4e3e\u4e0d\u8d77\u624b \u6740\u5979\uff0c\u5b8c\u5168\u4e0d\u662f\uff01\u800c\u662f\u62bc\u4e0d\u5e90\u6240\u8bf4\u7684\u8bdd\uff0c\u662f\u5979\u4ee5\u524d\u5b8c\u5168\u6ca1\u6709\u60f3\u8fc7\u7684\uff01 \u3000\u3000\u6709\u4e00\u4e9b\u5e74\u9752\u7684\u59d1\u5a18\u4eec\uff0c\u672c\u6765\u5c31\u559c\u6b22\u62bc\u4e0d\u5e90\u7684\u6b4c\uff0c\u542c\u4e86\u8fd9\u4e00\u756a\u4e34\u6b7b\u524d\u7684\u8bf4\u8bdd\uff0c\u5ffd\u7136\u89c9\u5f97\u8fd9 \u4e2a\u4eba\u867d\u7136\u8be5\u6b7b\uff0c\u4f46\u4e5f\u6709\u4e9b\u53ef\u601c\uff0c\u6709\u4e9b\u59d1\u5a18\u7adf\u4f4e\u4e0b\u5934\u6765\uff0c\u4e0d\u6562\u770b\u53f0\u4e0a\u7684\u666f\u8c61\uff01 \u3000\u3000\u6768\u4e91\u806a\u7ad9\u5728\u53f0\u524d\uff0c\u6e05\u6e05\u695a\u695a\u7684\u770b\u5230\u98de\u7ea2\u5dfe\u7684\u77ed\u5251\u8f7b\u8f7b\u98a4\u52a8\u3002\u4ed6\u4e5f\u770b\u5230\u4e86\u98de\u7ea2\u5dfe\u6027\u683c\u4e0a\u7684 \u4f18\u70b9\u548c\u7f3a\u70b9\u3002\u8fd9\u662f\u4e00\u4e2a\u590d\u6742\u7684\u95ee\u9898\u3002\u9700\u8981\u597d\u597d\u7684\u548c\u98de\u7ea2\u5dfe\u8bb2\u3002 \u3000\u3000\u9752\u5e74\u4eec\u6012\u53eb\u7740\uff0c\u8bb8\u591a\u4eba\u60f3\u4e0a\u53f0\u53bb\u9a73\u65a5\u62bc\u4e0d\u5e90\u3002\u957f\u8001\u4f38\u5f00\u53cc\u624b\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a \u3000\u3000\u201c\u5982\u679c\u4e3a\u4e86\u6211\u4eec\u4e00\u65cf\u7684\u5149\u8363\uff0c\u8981\u4f60\u628a\u725b\u7f8a\u90fd\u62ff\u51fa\u6765\uff0c\u4f60\u5c31\u8bf4\u8fde\u6211\u7684\u6bcd\u9a6c\u4e5f\u62ff\u53bb\u5427\uff01\u5982\u679c \u4e3a\u4e86\u6211\u4eec\u4e00\u65cf\u7684\u5149\u8363\uff0c\u8981\u4f60\u53bb\u6253\u4ed7\uff0c\u4f60\u5c31\u8bf4\u8fde\u6211\u521a\u957f\u6210\u7684\u513f\u5b50\u4e5f\u7b97\u4e0a\u4e00\u4efd\u5427\uff01\u5982\u679c\u4f60\u4e3a\u5927\u5bb6 \u505a\u4e8b\uff0c\u53d7\u4e86\u59d4\u5c48\uff0c\u4e0d\u8981\u5fd9\u7740\u7533\u8fa9\uff0c\u628a\u4e8b\u60c5\u505a\u597d\u4e86\u518d\u8bf4\u5427\uff01 \u3000\u3000\u201c\u8fd9\u662f\u6211\u4eec\u7ecf\u4e66\u4e0a\u8bf4\u7684\u8bdd\uff0c\u5728\u8349\u539f\u4e0a\u6d41\u4f20\u4e86\u8bb8\u591a\u5e74\uff0c\u5927\u5bb6\u90fd\u77e5\u9053\u8fd9\u4e9b\u8bdd\uff0c\u4e0d\u662f\u5417\uff1f\u62bc\u4e0d \u5e90\uff1f \u3000\u3000\u62bc\u4e0d\u5e90\u4f4e\u4e0b\u4e86\u5934\uff0c\u957f\u8001\u58f0\u8c03\u9ad8\u4ea2\uff0c\u8d8a\u8bf4\u8d8a\u5feb\uff0c\u65a5\u9053\uff1a\u201c\u6211\u4eec\u7f57\u5e03\u65cf\u4eba\u90fd\u61c2\u5f97\u8fd9\u4e9b\u6d3b\u7684\u610f \u601d\uff0c\u5728\u771f\u4e3b\u7684\u540d\u4e0b\uff0c\u5728\u6b63\u4e49\u8fd9\u4e00\u8fb9\uff0c\u4e3a\u4e86\u5927\u5bb6\u7684\u4e8b\u60c5\uff0c\u6211\u4eec\u7684\u4e00\u5207\u90fd\u53ef\u4ee5\u5949\u732e\uff0c\u96be\u9053\u4e0d\u662f\u8fd9 \u6837\u5417\uff0c\u62bc\u4e0d\u5e90\uff1f \u3000\u3000\u73b0\u5728\uff0c\u6ee1\u6d32\u7684\u519b\u961f\u4ece\u5173\u5916\u6253\u5230\u5173\u5185\uff0c\u53c8\u6253\u5230\u6211\u4eec\u7684\u65b0\u7586\uff0c\u4ed6\u4eec\u7684\u6218\u9a6c\u5728\u8349\u539f\u4e0a\u8086\u610f\u5954 \u817e\uff0c\u4ed6\u4eec\u7684\u58eb\u5175\u711a\u70e7\u6211\u4eec\u7684\u5e10\u5e55\uff0c\u52ab\u63a0\u6211\u4eec\u7684\u8d22\u7269\u3002\u4ed6\u4eec\u8981\u8349\u539f\u4e0a\u7684\u7267\u6c11\u50cf\u7f94\u7f8a\u4e00\u6837\u9a6f\u670d\uff0c \u505a\u4ed6\u4eec\u7684\u5974\u96b6\uff0c\u53d7\u4ed6\u4eec\u7684\u97ad\u82d4\u3002\u9664\u975e\u662f\u5b8c\u5168\u6ca1\u6709\u9aa8\u5934\u7684\u4eba\uff0c\u5426\u5219\u6ca1\u6709\u4e00\u4e2a\u613f\u610f\u8fd9\u6837\u505a\uff01 \u3000\u3000\u62bc\u4e0d\u5e90\uff0c\u6211\u4eec\u7684\u65cf\u4eba\u5728\u6297\u66b4\uff0c\u5728\u6d41\u8840\uff0c\u4ed6\u4eec\u4e3a\u4e86\u7f57\u5e03\u65cf\u7684\u5149\u8363\uff0c\u4e00\u5207\u90fd\u5949\u732e\u51fa\u6765\u3002\u800c\u4f60 \u5374\u4e00\u70b9\u70b9\u59d4\u5c48\u4e5f\u53d7\u4e0d\u4f4f\uff0c\u800c\u4f60\u5374\u8981\u548c\u4f60\u5fc3\u7231\u7684\u4eba\u6bd4\u8d5b\u9a84\u50b2\uff01 \u3000\u3000\u8981\u6709\u4ec0\u4e48\u9a84\u505a\u5462\uff0c\u5bb3\u6b7b\u6211\u4eec\u5c0a\u656c\u7684\u8001\u82f1\u96c4\uff0c\u5bb3\u6b7b\u4f60\u7684\u5144\u5f1f\u59d0\u59b9\uff0c\u66ff\u654c\u4eba\u505a\u8d70\u72d7\uff0c\u8fd9\u662f\u6700 \u6700\u4e0b\u8d31\u7684\u6ca1\u6709\u9aa8\u5934\u7684\u5974\u624d\uff0c\u4e8f\u4f60\u8fd8\u6562\u8bf4\u98de\u7ea2\u5dfe\uff01 \u3000\u3000\u98de\u7ea2\u5dfe\uff0c\u4f60\u7684\u7236\u4eb2\u5728\u5929\u4e0a\u770b\u7740\u4f60\uff0c\u4f60\u7684\u65cf\u4eba\u5728\u53f0\u4e0b\u770b\u7740\u4f60\uff01\u73b0\u5728\u4f60\u662f\u6211\u4eec\u65cf\u957f\u7684\u7ee7\u4f4d \u4eba\uff0c\u4f60\u53ef\u4ee5\u6309\u7167\u4f60\u7684\u610f\u601d\u53bb\u505a\u3002\u98de\u7ea2\u5dfe\uff0c\u4f60\u8981\u600e\u6837\u53bb\u505a\u5462\uff1f\u201d \u3000\u3000\u98de\u7ea2\u5dfe\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u62ff\u9152\u6765\uff01\u201d\u4e00\u4e2a\u9752\u5e74\u6367\u7740\u4e00\u53cc\u725b\u8033\u5927\u9152\u676f\u8d70\u6765\uff0c\u91cc\u9762\u6709\u534a\u76c5\u70c8\u9152\u3002 \u98de\u7ea2\u5dfe\u5de6\u624b\u63a5\u8fc7\u9152\u76c5\uff0c\u53f3\u624b\u77ed\u5251\u95ea\u7535\u822c\u5730\u63d2\u8fdb\u4e86\u62bc\u4e0d\u5e90\u7684\u80f8\u819b\uff0c\u970e\u65f6\u95f4\uff0c\u62bc\u4e0d\u5e90\u7684\u9c9c\u8840\u98de\u5c04 \u51fa\u6765\uff0c\u98de\u7ea2\u5dfe\u7528\u9152\u76c5\u4e00\u6321\uff0c\u88c5\u6ee1\u4e86\u6ee1\u6ee1\u4e00\u76c5\u8840\u9152\uff01 \u3000\u3000\u98de\u7ea2\u5dfe\u77ed\u5251\u62d4\u51fa\uff0c\u5251\u5c16\u4e0a\u523a\u7740\u4e00\u9897\u9c9c\u8840\u6dcb\u6f13\u7684\u4eba\u5fc3\uff01\u4e00\u58f0\u51c4\u5389\u957f\u7b11\uff0c\u811a\u5c16\u8d77\u5904\uff0c\u62bc\u4e0d\u5e90 \u5c38\u8eab\u6eda\u843d\u53f0\u4e0b\u3002 \u3000\u3000\u98de\u7ea2\u5dfe\u63d0\u7740\u77ed\u5251\uff0c\u6367\u7740\u8840\u9152\uff0c\u56de\u8fc7\u8eab\u6765\uff0c\u7f13\u7f13\u5730\u8d70\u5230\u7236\u4eb2\u7684\u7075\u524d\uff0c\u4e09\u4e2a\u957f\u8001\u8ddf\u5728\u80cc\u540e\uff0c \u8840\u9152\u5012\u5728\u7075\u524d\uff0c\u5fc3\u809d\u9489\u5728\u53f0\u4e0a\u3002\u98de\u7ea2\u4e2d\u5931\u58f0\u75db\u54ed\uff0c\u53eb\u9053\uff1a\u201c\u7236\u4eb2\u554a\uff01\u4f60\u53ef\u4ee5\u7791\u76ee\u4e86\uff01\u201d \u3000\u3000\u5927\u8349\u539f\u4e0a\u6c89\u9ed8\u65e0\u58f0\uff0c\u6240\u6709\u7684\u4eba\u90fd\u4f4e\u4e0b\u4e86\u5934\u53bb\u3002\u5ffd\u7136\u95f4\uff0c\u8fdc\u8fdc\u4f20\u6765\u4e86\u4e00\u9635\u80e1\u7b33\uff0c\u9a6c\u8e44\u58f0\u6e10 \u8fd1\uff0c\u4e1c\u9762\u51b2\u6765\u4e86\u4e00\u5f6a\u4eba\u9a6c\uff0c\u4e3a\u9996\u7684\u6325\u7740\u4e00\u9762\u5927\u65d7\uff0c\u628a\u98ce\u7684\u7f57\u5e03\u65cf\u4eba\u53eb\u9053\uff1a\u201c\u5854\u5c71\u65cf\u914b\u957f \u5230\uff01\u201d\u4e0d\u4e00\u4f1a\uff0c\u897f\u9762\u53c8\u51b2\u6765\u4e86\u4e00\u961f\u9a6c\u5e2e\uff0c\u628a\u98ce\u7684\u53c8\u901a\u62a5\u9053\uff1a\u201c\u838e\u8f66\u4e94\u90e8\u8054\u76df\u4ee3\u8868\u5230\uff01\u201d\u4e0d\u5230 \u534a\u4e2a\u65f6\u8fb0\uff0c\u7adf\u5230\u4e09\u65cf\u914b\u957f\u548c\u5341\u56db\u4e2a\u90e8\u843d\u7684\u5165\u9a6c\uff0c\u79bb\u9ad8\u53f0\u6570\u5341\u6b65\u8fdc\uff0c\u4e00\u5b57\u6392\u5f00\u3002\u9ad8\u53f0\u4e0a\u4e09\u4e2a\u957f \u8001\u8138\u8272\u5927\u53d8\u3002"}),showOnHomepage:!0},{name:"\u7b2c\u4e5d\u56de\u3000\u6bd4\u6b66\u5b9a\u76df ",content:(0,t.jsx)(t.Fragment,{children:"\u8349\u539f\u4e0a\u591c\u98ce\u4f4e\u5578\uff0c\u53f0\u4e0b\u7684\u91ce\u706b\u70e7\u5f97\u6b63\u65fa\uff0c\u98de\u7ea2\u5dfe\u63d0\u7740\u77ed\u5251\uff0c\u8f6c\u8fc7\u8eab\u6765\uff0c\u5bf9\u7740\u4e0b\u9762\u7684\u4eba \u9a6c\uff0c\u832b\u7136\u4e0d\u77e5\u6240\u63aa\u3002\u957f\u8001\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u54c8\u739b\u96c5\uff0c\u5148\u8bf7\u53d4\u4f2f\u4e0a\u6765\u796d\u5960\u3002\u201d\u98de\u7ea2\u5dfe\u628a\u8840\u9152\u4e00\u503e\u800c \u5c3d\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u5404\u4f4d\u53d4\u4f2f\u548c\u670b\u53cb\u4eec\uff0c\u591a\u8c22\u4f60\u4eec\u4ece\u5404\u5904\u8d76\u6765\uff0c\u6211\u7684\u7236\u4eb2\u867d\u6b7b\u72b9\u751f\u3002\u4ed6\u7684\u9c9c\u8840 \u6ef4\u5728\u8349\u539f\u4e0a\uff0c\u53db\u5f92\u7684\u8840\u548c\u654c\u4eba\u7684\u8840\uff0c\u4e5f\u6ef4\u5728\u8349\u539f\u4e0a\u3002\u770b\u5230\u4f60\u4eec\u4e00\u5927\u7fa4\u4e00\u5927\u7fa4\u7684\u5230\u6765\uff0c\u6211\u6562\u76f8 \u4fe1\uff0c\u5982\u679c\u6211\u4eec\u7684\u8840\u5c06\u8981\u6d41\u6210\u5c0f\u6eaa\uff0c\u654c\u4eba\u7684\u8840\u5c31\u8981\u6d41\u6210\u5927\u6cb3\uff0c\u8349\u539f\u57cb\u846c\u6211\u4eec\u4e00\u4e2a\u4eba\uff0c\u5c31\u8981\u57cb\u846c \u4ed6\u4eec\u5341\u4e2a\u4eba\uff01\u6211\u7236\u4eb2\u7684\u9aa8\u7070\u5728\u8fd9\u53f0\u4e0a\uff0c\u6211\u7236\u4eb2\u7684\u7075\u9b42\u5728\u4f60\u4eec\u4e2d\u95f4\uff0c\u4ed6\u8046\u542c\u7740\u4f60\u4eec\u7684\u8bf4\u8bdd\uff0c\u73b0 \u5728\u8bf7\u4f60\u4eec\u4e0a\u53f0\u6765\u796d\u5960\u3002\u201d \u3000\u3000\u6768\u4e91\u806a\u5fc3\u91cc\u8d5e\u53f9\u9053\uff1a\u201c\u597d\u4e00\u4e2a\u98de\u7ea2\u5dfe\uff01\u8fd9\u4e9b\u4eba\u6210\u7fa4\u6765\u5230\uff0c\u770b\u6765\u5e76\u4e0d\u53ea\u662f\u53c2\u52a0\u796d\u5960\u3002\u5982\u679c \u4ed6\u4eec\u6709\u4ec0\u4e48\u4f01\u56fe\uff0c\u98de\u7ea2\u5dfe\u8fd9\u756a\u8bdd\u4f1a\u4ee4\u4ed6\u4eec\u60ed\u6127\uff01\u5bf9\u7740\u5510\u52aa\u8001\u82f1\u96c4\u7684\u9aa8\u7070\uff0c\u8c01\u90fd\u4f1a\u53d1\u8a93\u8981\u6d88\u706d \u654c\u4eba\u7684\uff01\u201d \u3000\u3000\u5404\u65cf\u914b\u957f\u548c\u5404\u90e8\u843d\u7684\u4ee3\u8868\u9c7c\u8d2f\u767b\u53f0\uff0c\u98de\u7ea2\u5dfe\u5411\u6768\u4e91\u806a\u62db\u62db\u624b\uff0c\u6768\u4e91\u806a\u9ed8\u9ed8\u7684\u8ddf\u5728\u540e\u9762\uff0c \u4f17\u4eba\u60ca\u5947\u7684\u770b\u4e86\u4ed6\u4e00\u773c\uff0c\u5e76\u4e0d\u8bf4\u8bdd\uff0c\u4e00\u4e2a\u4e2a\u5728\u5510\u52aa\u8001\u82f1\u96c4\u7684\u7075\u524d\u503e\u4e0b\u8840\u9152\uff0c\u6700\u540e\u8f6e\u5230\u4e86\u6768\u4e91 \u806a\uff0c\u5854\u5c71\u65cf\u7684\u914b\u957f\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u4ed6\u662f\u8c01\uff1f\u201d\u98de\u7ea2\u5dfe\u7b54\u9053\uff1a\u201c\u4ed6\u5c31\u662f\u6768\u4e91\u806a\u592a\u4fa0\uff01\u201d\u4f17\u4eba\u9661\u7136 \u4e00\u60ca\uff0c\u5854\u5c71\u65cf\u914b\u957f\u53c8\u95ee\u9053\uff1a\u201c\u4ed6\u7b97\u662f\u4ee3\u8868\u54c8\u8428\u514b\u4eba\u8fd8\u662f\u4f60\u4eec\u7f57\u5e03\u65cf\u4eba\uff1f\u201d\u6768\u4e91\u806a\u4ea2\u58f0\u8bf4\u9053\uff0c \u201c\u6211\u662f\u6c49\u4eba\uff0c\u6211\u662f\u54c8\u8428\u514b\u7684\u6218\u58eb\u548c\u54c8\u739b\u96c5\u7684\u670b\u53cb\uff01\u6211\u4e0d\u4ee3\u8868\u8c01\uff0c\u8c01\u62b5\u6297\u6e05\u5175\uff0c\u6211\u5c31\u5e2e\u52a9 \u8c01\uff01\u201d\u914b\u957f\u4eec\u548c\u5404\u90e8\u843d\u7684\u4ee3\u8868\u9f50\u58f0\u9053\uff1a\u201c\u597d\uff01\u201d\u5854\u5c71\u65cf\u7684\u914b\u957f\u7f13\u7f13\u7684\u5411\u4e09\u4e2a\u957f\u8001\u95ee\u9053\uff1a\u201c\u5510 \u52aa\u8001\u82f1\u96c4\u6b7b\u4e86\uff0c\u662f\u4e0d\u662f\u4ed6\u7684\u5973\u513f\u627f\u7ee7\u4ed6\u7684\u4f4d\u5b50\uff1f\u201d\u957f\u8001\u53cd\u95ee\u9053\uff1a\u201c\u4f60\u96be\u9053\u4ee5\u4e3a\u6211\u4eec\u7684\u54c8\u739b\u96c5 \u4e0d\u914d\u5417\uff1f\u201d\u5854\u5c71\u65cf\u7684\u914b\u957f\u6b63\u8272\u8bf4\u9053\uff1a\u201c\u98de\u7ea2\u5dfe\u5973\u82f1\u96c4\u5a01\u9707\u5357\u7586\uff0c\u8c01\u6562\u8bf4\u5979\u4e0d\u914d\uff01\u53ef\u662f\u4f60\u522b\u5fd8 \u8bb0\u4e86\uff0c\u5510\u52aa\u8001\u82f1\u96c4\u4e0d\u4f46\u662f\u4f60\u4eec\u7684\u65cf\u957f\uff0c\u4e5f\u662f\u6211\u4eec\u7684\u76df\u4e3b\u3002\u201d\u838e\u8f66\u65cf\u7684\u4ee3\u8868\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u6211\u4eec \u6765\u5960\u796d\u5510\u52aa\u8001\u82f1\u96c4\uff0c\u6211\u4eec\u4e5f\u60f3\u5728\u4eca\u591c\u51b3\u5b9a\uff0c\u8c01\u662f\u65b0\u7684\u76df\u4e3b\u3002\u6211\u4eec\u4e0d\u662f\u4e0d\u670d\u98de\u7ea2\u5dfe\u59d1\u5a18\uff0c\u53ef\u662f \u6309\u7167\u6211\u4eec\u7684\u89c4\u77e9\uff0c\u603b\u5f97\u6bd4\u8bd5\u4e00\u756a\uff0c\u90a3\u624d\u80fd\u63a8\u5b9a\u3002\u201d\u98de\u7ea2\u5dfe\u6025\u5fd9\u8bf4\u9053\uff1a\u201c\u6211\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u76df\u4e3b\u90a3 \u662f\u4e07\u4e0d\u6562\u505a\uff0c\u6211\u4e0d\u53c2\u52a0\u7ade\u9010\u4e86\uff0c\u82e5\u9009\u51fa\u65b0\u7684\u76df\u4e3b\uff0c\u6211\u7b2c\u4e00\u4e2a\u542c\u4ed6\u8c03\u9063\uff01\u201d\u8428\u9a6c\u513f\u65cf\u7684\u914b\u957f\u7b11 \u9053\uff1a\u201c\u90a3\u4e0d\u884c\uff01\u98de\u7ea2\u5dfe\u5973\u82f1\u96c4\u548c\u7f57\u5e03\u65cf\u7684\u52c7\u58eb\u4eec\u600e\u80fd\u4e0d\u53c2\u52a0\u7ade\u9009\uff1f\u6211\u4eec\u5e76\u4e0d\u662f\u4e89\u593a\u76df\u4e3b\uff0c\u800c \u662f\u8981\u516c\u5e73\u9009\u51fa\u4e00\u4e2a\u5927\u5bb6\u5fc3\u670d\u7684\u4eba\uff0c\u5e26\u9886\u6211\u4eec\u548c\u654c\u4eba\u6253\u4ed7\uff01\u201d\u6768\u4e91\u806a\u677e\u4e86\u53e3\u6c14\uff0c\u5fc3\u60f3\uff1a\u539f\u6765\u662f \u8fd9\u6837\uff0c\u53ea\u8981\u80af\u62b5\u6297\u6e05\u5175\u5c31\u6210\uff01\u63d2\u53e3\u8bf4\u9053\uff0c\u201c\u2018\u54c8\u739b\u96c5\uff0c\u5927\u654c\u5f53\u524d\uff0c\u8c01\u4e5f\u4e0d\u8981\u63a8\u8ba9\uff0c\u6309\u7167\u4f60\u4eec \u7684\u89c4\u77ed\u529e\u4e8b\u5427\uff01\u201d\u98de\u7ea2\u5dfe\u770b\u4e86\u6768\u4e91\u806a\u4e00\u773c\uff0c\u70b9\u5934\u7b54\u5e94\u3002 \u3000\u3000\u7ade\u8d5b\u5f00\u59cb\u4e86\u3002\u867d\u7136\u4e0d\u662f\u6027\u547d\u4e4b\u4e89\uff0c\u4f46\u4e5f\u6781\u5176\u60ca\u5fc3\u52a8\u9b44\uff0c\u5934\u4e00\u9635\u662f\u6454\u8de4\uff0c\u838e\u8f66\u65cf\u7684\u628a\u8428\u9a6c \u513f\u65cf\u7684\u52c7\u58eb\u6454\u5012\u5728\u5730\u4e0a\u722c\u4e0d\u8d77\u6765\uff0c\u7f57\u5e03\u65cf\u7684\u52c7\u58eb\u53c8\u628a\u4ed6\u6454\u8131\u4e24\u4e2a\u95e8\u7259\u3002\u5854\u5c71\u65cf\u7684\u52c7\u58eb\u53c8\u628a\u7f57 \u5e03\u65cf\u7684\u6253\u8d25\uff0c\u98de\u7ea2\u5dfe\u662f\u4e2a\u5c11\u5973\uff0c\u4e0d\u4fbf\u53c2\u52a0\uff0c\u6700\u540e\u6ca1\u6709\u6562\u6765\u654c\u5bf9\uff0c\u6b63\u8981\u7b97\u5854\u5c71\u65cf\u5f97\u80dc\uff0c\u6768\u4e91\u806a \u5ffd\u7136\u8d8a\u4f17\u800c\u51fa\uff0c\u8bf4\u9053\uff0c\u201c\u6211\u4e5f\u7b97\u4e00\u4efd\uff0c\u8d62\u4e86\u5c31\u7b97\u662f\u54c8\u739b\u96c5\u59d1\u5a18\u7684\uff01\u201d\u98de\u7ea2\u5dfe\u4f4e\u58f0\u8bf4\u4e86\u53e5\u201c\u8c22 \u8c22\uff01\u201d\u5854\u5c71\u65cf\u7684\u52c7\u58eb\u540d\u53eb\u963f\u76d6\uff0c\u662f\u5357\u7586\u7b2c\u4e00\u540d\u6454\u8de4\u597d\u624b\uff0c\u5fc3\u4e2d\u6012\u9053\uff1a\u201c\u600e\u89c1\u5f97\u5c31\u662f\u4f60\u8d62\uff01\u5f53 \u4e0b\u8dd1\u5230\u6768\u4e91\u806a\u8eab\u65c1\uff0c\u8bf4\u9053\uff1a\u201c\u6768\u5927\u4fa0\uff0c\u8bf7\u8d50\u6559\u5427\uff01\u201d \u3000\u3000\u6768\u4e91\u806a\u5fae\u5fae\u4e00\u7b11\uff0c\u53cc\u624b\u7d27\u8d34\u819d\u65c1\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u5148\u8bf7\u3002\u201d\u6454\u8de4\u662f\u56db\u81c2\u76f8\u4ea4\uff0c\u4e24\u4eba\u4e92\u62b1\uff0c\u4ece \u6ca1\u6709\u53cc\u624b\u4e0b\u5782\uff0c\u53cc\u817f\u633a\u76f4\u7684\u9053\u7406\uff0c\u963f\u76d6\u5947\u9053\uff1a\u201c\u8fd9\u6837\u600e\u80fd\u7b97\u662f\u6454\u8de4\uff1f\u201d\u6768\u4e91\u806a\u7b11\u9053\uff1a\u201c\u4f60\u5c3d \u7ba1\u53d1\u529b\u5c31\u662f\uff0c\u6211\u7ed9\u4f60\u6454\u5012\u4e86\u5c31\u7b97\u6211\u8f93\u3002\u201d\u963f\u76d6\u5927\u6012\uff0c\u4e24\u817f\u5fae\u5f2f\uff0c\u819d\u76d6\u5411\u524d\u4e00\u9876\uff0c\u53cc\u624b\u6273\u7740\u6768 \u4e91\u806a\u7684\u8eab\u8eaf\u4fbf\u6454\uff01\u6768\u4e91\u806a\u559d\u58f0\uff1a\u201c\u53bb\uff01\u201d\u4f46\u4e0d\u89c1\u4ed6\u6251\u624b\uff0c\u963f\u76d6\u5df2\u98de\u95ea\u51fa\u4e00\u4e08\u5f00\u5916\u3002\u4f17\u4eba\u76f8\u987e \u9a87\u7136\u3002\u963f\u76d6\u6253\u4e86\u4e2a\u633a\uff0c\u7ad9\u8d77\u8eab\u6765\uff0c\u7ad9\u7a33\u9a6c\u6b65\uff0c\u53cc\u624b\u4e00\u642d\u6768\u4e91\u806a\u7684\u80a9\u5934\uff0c\u5de6\u624b\u524d\u690e\uff0c\u53f3\u624b\u4e94\u6307 \u5982\u94a9\uff0c\u5411\u4e0b\u4e00\u62c9\uff0c\u8fd9\u662f\u6454\u8de4\u4e2d\u5f88\u5389\u5bb3\u7684\u4e00\u62db\uff0c\u540d\u4e3a\u201c\u63a8\u7a97\u671b\u6708\u201d\u3002\u6768\u4e91\u806a\u53c8\u559d\u4e00\u58f0\uff1a\u201c\u53bb\uff01\u201d \u963f\u76d6\u5e9e\u5927\u7684\u8eab\u8eaf\uff0c\u53c8\u76f4\u98de\u51fa\u53bb\uff0c\u8dcc\u4e86\u4e2a\u56db\u811a\u671d\u5929\uff01\u963f\u76d6\u54c7\u54c7\u5927\u53eb\uff1a\u201c\u5996\u672f\uff0c\u5996\u672f\uff01\u201d \u3000\u3000\u98de\u7ea2\u5dfe\u6b66\u529f\u6df1\u6e5b\uff0c\u4e00\u89c1\u6768\u4e91\u806a\u7eb9\u4e1d\u4e0d\u52a8\uff0c\u4fbf\u5c06\u5bf9\u65b9\u6454\u5012\uff0c\u8131\u53e3\u8d5e\u9053\uff1a\u201c\u597d\u4e2a\u6cbe\u8863\u5341\u516b\u8dcc \u7684\u529f\u592b\uff01\u201d\u8fd9\u79cd\u7edd\u6280\u7684\u5185\u5bb6\u529f\u592b\u5df2\u7ec3\u5230\u7089\u706b\u7eaf\u9752\u4e4b\u5883\uff0c\u6b63\u662f\u201c\u6cbe\u8863\u5341\u516b\u8dcc\u201d\u3002\u5176\u9053\u7406\u6b63\u4e0e\u592a \u6781\u62f3\u7684\u501f\u529b\u6253\u529b\u4e00\u6837\uff0c\u90fd\u662f\u7528\u654c\u4eba\u81ea\u5df1\u53d1\u51fa\u7684\u529b\u91cf\u53bb\u6253\u51fb\u654c\u4eba\u3002\u53ea\u662f\u201c\u6cbe\u8863\u5341\u516b\u8dcc\u201d\u8fd8\u9700\u8981 \u61c2\u5f97\u8fd0\u6c14\u53cd\u51fb\u7684\u529f\u592b\uff0c\u6bd4\u5355\u7eaf\u7684\u501f\u529b\u6253\u529b\u8fd8\u8981\u96be\u4e0a\u8bb8\u591a\u3002 \u3000\u3000\u963f\u76d6\u867d\u7136\u7cbe\u4e8e\u6454\u8de4\uff0c\u5374\u4e0d\u61c2\u5f97\u8fd9\u79cd\u5185\u5bb6\u7684\u4e0a\u4e58\u52a8\u592b\uff0c\u4ecd\u7136\u8bf4\u9053\uff1a\u201c\u4f60\u82e5\u4e0d\u4f7f\u5996\u672f\uff0c\u4f9d\u6b63 \u6454\u8de4\u89c4\u77e9\uff0c\u628a\u6211\u6454\u5012\uff0c\u6211\u5c31\u8f93\u4e86\u3002\u201d\u5854\u5c71\u65cf\u7684\u914b\u957f\u662f\u4e2a\u5185\u5bb6\u9ad8\u624b\uff0c\u7b11\u9a82\u9053\uff1a\u201c\u4f60\u4e0d\u61c2\u5f97\u8fd9\u79cd \u529f\u592b\u5c31\u522b\u80e1\u8bf4\uff01\u201d\u4ed6\u81ea\u5fd6\u4e0d\u662f\u6768\u4e91\u806a\u5bf9\u624b\uff0c\u4e0d\u6562\u51fa\u6765\u66ff\u4e0b\u963f\u76d6\uff0c\u53ea\u662f\u66ff\u963f\u76d6\u8ba4\u8f93\uff0c\u50ac\u4ed6\u56de \u53bb\u3002\u963f\u76d6\u5374\u76f4\u76f4\u633a\u633a\u7684\u7ad9\u5728\u6768\u4e91\u806a\u9762\u524d\uff0c\u6768\u4e91\u806a\u77e5\u4ed6\u4e0d\u670d\uff0c\u53cc\u817f\u5fae\u5f2f\uff0c\u4f38\u51fa\u53cc\u624b\uff0c\u9053\uff0c\u201c\u6765 \u5427\uff01\u201d\u963f\u76d6\u4e00\u628a\u6251\u53bb\uff0c\u6349\u7740\u6768\u4e91\u806a\u7684\u624b\u8155\u4fbf\u626d\uff0c\u6768\u4e91\u806a\u75be\u7684\u624b\u8155\u4e00\u6c89\uff0c\u811a\u4e0b\u4e00\u52fe\uff0c\u963f\u76d6\u529b\u53d1 \u96be\u6536\uff0c\u91cd\u5fc3\u4e0d\u7a33\uff0c\u8eab\u5b50\u524d\u4ec6\uff0c\u7ed9\u6768\u4e91\u806a\u8f7b\u8f7b\u4e00\u52fe\uff0c\u4fbf\u8dcc\u5728\u5730\u4e0a\u3002\u963f\u76d6\u7ad9\u8d77\u6765\uff0c\u7fd8\u8d77\u5927\u62c7\u6307\u8bf4 \u9053\uff1a\u201c\u597d\uff01\u201d\u53c8\u9053\uff1a\u201c\u8fd9\u624d\u662f\u6454\u8dcc\u7edd\u6280\uff0c\u6211\u670d\u4e86\uff01\u201d \u3000\u3000\u6768\u4e91\u806a\u6b63\u60f3\u9000\u4e0b\uff0c\u5854\u5c71\u65cf\u7684\u5927\u529b\u58eb\u5fb7\u5361\u51fa\u6765\u8bf4\u9053\uff1a\u201c\u6768\u5927\u4fa0\uff0c\u6211\u4eec\u6765\u6bd4\u6bd4\u62f3\u3002\u201d\u6b64\u4eba\u8eab \u957f\u516d\u5c3a\u6709\u4f59\uff0c\u4e24\u81c2\u808c\u8089\u594b\u8d77\uff0c\u5c48\u4f38\u4e4b\u95f4\u3002\u9aa8\u9abc\u683c\u683c\u4f5c\u54cd\uff0c\u98de\u7ea2\u5dfe\u7a81\u7136\u4e00\u63a0\u9762\u524d\uff0c\u8bf4\u9053\uff1a\u201c\u6768 \u5927\u4fa0\u662f\u5ba2\u4eba\uff0c\u600e\u597d\u5c3d\u70e6\u6270\u4ed6\uff0c\u6211\u6765\u548c\u4f60\u6bd4\u62f3\uff01\u201d\u98de\u7ea2\u5dfe\u8170\u80a2\u8885\u5a1c\uff0c\u7389\u7acb\u4ead\u4ead\uff0c\u6bd4\u8d77\u90a3\u4e2a\u5de8\u65e0 \u9738\u6765\uff0c\u771f\u662f\u76f8\u6620\u6210\u8da3\u3002 \u3000\u3000\u6768\u4e91\u806a\u4e00\u7b11\u800c\u9000\uff0c\u5fb7\u5361\u9053\uff1a\u201c\u54c8\u739b\u96c5\uff0c\u4f60\u7684\u5251\u672f\u9a70\u540d\u5357\u7586\uff0c\u62f3\u811a\u76f8\u640f\uff0c\u53ef\u662f\u53d6\u5de7\u4e0d\u5f97\uff0c \u6211\u4e00\u62f3\u53ef\u4ee5\u6253\u6b7b\u4e00\u5934\u9a86\u9a7c\uff0c\u78b0\u4f24\u4e86\u4f60\uff0c\u90a3\u53ef\u4e0d\u597d\u610f\u601d\u3002\u201d\u8bf4\u7f62\u968f\u624b\u4e00\u62f3\u6253\u5728\u4e00\u68f5\u5c0f\u6811\u4e0a\uff0c\u90a3 \u68f5\u5c0f\u6811\u679c\u7136\u5e94\u58f0\u5012\u4e0b\uff01\u98de\u7ea2\u5dfe\u77a5\u4e86\u4e00\u773c\uff0c\u82e5\u65e0\u5176\u4e8b\uff0c\u53ea\u662f\u7b11\u9053\uff1a\u201c\u6811\u662f\u6b7b\u7269\uff0c\u5982\u4f55\u6bd4\u5f97\u6e9c\u6ed1 \u7684\u4eba\uff01\u4f60\u53d1\u62f3\u5427\uff0c\u6253\u6b7b\u4e86\u6211\uff0c\u6211\u7684\u65cf\u4eba\u4e5f\u4e0d\u4f1a\u602a\u4f60\u3002\u201d\u5fb7\u5361\u5fc3\u60f3\uff0c\u5979\u518d\u6e9c\u6ed1\uff0c\u53ea\u8981\u88ab\u6211\u62f3\u98ce \u626b\u7740\u4e00\u70b9\uff0c\u5c31\u4f1a\u5012\u5730\u3002\u800c\u4e14\u5c31\u7b97\u6211\u6253\u4e0d\u7740\uff0c\u5979\u4e5f\u4e0d\u4f1a\u6253\u7740\u6211\u3002\u5979\u82e5\u53ea\u662f\u8eb2\u95ea\uff0c\u4e5f\u7b97\u662f\u6211\u4e0a \u98ce\u3002\u5ffd\u5730\u4e00\u62f3\u6253\u51fa\uff0c\u98de\u7ea2\u5dfe\u8f7b\u8f7b\u4e00\u95ea\uff0c\u5df2\u5230\u4e86\u4ed6\u7684\u80cc\u540e\uff0c\u7389\u638c\u4e00\u626c\uff0c\u62cd\u7684\u4e00\u58f0\u6253\u4e2d\u4ed6\u7684\u80cc \u5fc3\u3002\u98de\u7ea2\u5dfe\u7528\u7684\u662f\u5185\u5bb6\u91cd\u624b\u6cd5\uff0c\u5fb7\u5361\u75db\u5f97\u54c7\u54c7\u5927\u53eb\u3002\u4f46\u4ed6\u4e5f\u597d\u751f\u4e86\u5f97\uff0c\u53cd\u624b\u4e00\u635e\uff0c\u84b2\u6247\u822c\u7684 \u5927\u624b\u671d\u98de\u7ea2\u5dfe\u4fbf\u6293\uff0c\u98de\u7ea2\u5dfe\u8f7b\u8f7b\u4e00\u95ea\uff0c\u4f38\u624b\u5728\u4ed6\u80a9\u8180\u8f7b\u8f7b\u4e00\u63a8\uff0c\u5fb7\u5361\u8dc4\u8dc4\u8e09\u8e09\u7684\u540e\u9000\u6570\u6b65\uff0c \u864e\u543c\u4e00\u58f0\uff0c\u4e00\u7a33\u8eab\u5f62\uff0c\u5373\u4fbf\u8dc3\u4e0a\uff0c\u53cc\u62f3\u9f50\u53d1\uff0c\u76f4\u5982\u5de8\u9524\u51fb\u77f3\uff0c\u52c7\u4e0d\u53ef\u6321\uff01\u98de\u7ea2\u5dfe\u8eab\u8eaf\u4e00\u65cb\uff0c \u4f38\u624b\u6293\u7740\u4ed6\u7684\u624b\u8155\u4e00\u5e26\uff0c\u5fb7\u5361\u8fd9\u4e00\u51fb\u4e4b\u529b\uff0c\u4f55\u6b62\u5343\u65a4\uff0c\u800c\u4eca\u7ed9\u5979\u501f\u529b\u4e00\u62c9\uff0c\u53ea\u89c9\u5f97\u8eab\u5b50\u4f3c\u817e \u4e91\u9a7e\u96fe\u4e00\u822c\uff0c\u5411\u524d\u76f4\u98de\u51fa\u53bb\uff0c\u6768\u4e91\u806a\u6025\u5c55\u7edd\u9876\u8f7b\u529f\uff0c\u8eab\u5f62\u8d77\u5904\uff0c\u5982\u7bad\u98de\u5c04\uff0c\u4e00\u628a\u5c06\u5fb7\u5361\u63a5 \u7740\uff0c\u653e\u4e86\u4e0b\u6765\u3002\u8349\u539f\u4e0a\u638c\u58f0\u96f7\u52a8\u3002\u5fb7\u5361\u662f\u4e2a\u76f4\u723d\u6c49\u5b50\uff0c\u5148\u5411\u6768\u4e91\u806a\u62f1\u624b\u8c22\u8fc7\u76f8\u6551\u4e4b\u6069\u60c5\uff0c\u7ee7 \u5bf9\u98de\u7ea2\u5dfe\u4e00\u63d6\u5230\u4ed6\u8bf4\u9053\uff1a\u201c\u59d1\u5a18\u771f\u597d\u6b66\u529f\uff0c\u4ffa\u5fb7\u5361\u662f\u5fc3\u670d\u53e3\u670d\u4e86\uff01\u201d \u3000\u3000\u98de\u7ea2\u5dfe\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\u58f0\uff1a\u201c\u627f\u8ba9\uff01\u201d\u582a\u6070\u90e8\u843d\u961f\u4e2d\u7a81\u7136\u98de\u51fa\u56db\u9a91\u9a8f\u9a6c\uff0c\u9a6c\u4e0a\u4eba\u9a91\u672f\u7cbe \u7edd\uff0c\u5728\u8349\u539f\u4e0a\u8dd1\u4e86\u4e00\u5708\uff0c\u500f\u5730\u505c\u4e0b\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u60f3\u9886\u6559\u7f57\u5e03\u65cf\u52c7\u58eb\u7684\u9a91\u672f\u97ad\u672f\uff01\u201d\u98de\u7ea2\u5dfe \u63a5\u58f0\u53eb\u9053\uff1a\u201c\u7275\u6211\u7684\u767d\u9a6c\u6765\uff01\u201d\u56db\u9a91\u58eb\u9f50\u58f0\u95ee\u9053\uff1a\u201c\u98de\u7ea2\u5dfe\u59d1\u5a18\uff0c\u4f60\u4e5f\u53c2\u52a0\u6bd4\u8d5b\u4e48\uff1f\u90a3\u4e48\u518d \u9009\u4e09\u540d\u9a91\u58eb\u5c31\u591f\u4e86\uff01\u201d\u98de\u7ea2\u5dfe\u7684\u5973\u4f34\u7275\u51fa\u4e00\u9a91\u767d\u9a6c\uff0c\u98de\u7ea2\u5dfe\u98de\u8eab\u9a91\u4e0a\u9a6c\u80cc\uff0c\u62bd\u51fa\u8f6f\u97ad\uff0c\u8fce\u98ce \u626b\u52a8\uff0c\u50fb\u556a\u6709\u58f0\uff0c\u7b11\u7740\u8bf4\u9053\uff1a\u201c\u8ba9\u6211\u4e00\u4eba\u9886\u6559\u4f60\u4eec\u56db\u9a91\u58eb\u7684\u97ad\u672f\u597d\u4e86\uff01\u201d\u53cc\u811a\u4e00\u5939\uff0c\u65e5\u9a6c\u817e \u5f00\u56db\u8e44\uff0c\u5728\u8349\u539f\u4e0a\u98de\u9a70\u800c\u8fc7\u3002\u56db\u9a91\u58eb\u559d\u58f0\u201c\u597d\uff01\u201d\u56db\u5339\u9a6c\u9f50\u5411\u98de\u7ea2\u5dfe\u51b2\u6765\uff0c\u500f\u5730\u5206\u5f00\u5de6\u53f3\u524d \u540e\uff0c\u4e00\u9f50\u622a\u51fb\uff0c\u56db\u6761\u957f\u97ad\uff0c\u773c\u8822\u5c31\u8981\u6253\u5230\u98de\u7ea2\u5dfe\u8eab\u4e0a\u3002\u7f57\u5e03\u65cf\u59d1\u5a18\u5927\u53eb\u8d77\u6765\uff0c\u5ffd\u7136\u9a6c\u80cc\u4e0a\u6ca1 \u4e86\u98de\u7ea2\u5dfe\u4eba\u5f71\uff0c\u98de\u7ea2\u5dfe\u65e9\u5df2\u8eb2\u5728\u9a6c\u809a\u5e95\u4e0b\u4e86\u3002\u90a3\u5339\u767d\u9a6c\u4e2d\u4e86\u4e00\u97ad\uff0c\u957f\u5636\u4e00\u58f0\uff0c\u731b\u5730\u5411\u524d\u51b2 \u53bb\uff0c\u524d\u9762\u90a3\u540d\u9a91\u58eb\uff0c\u63d0\u7f30\u4e00\u95ea\uff0c\u64e6\u8eab\u800c\u8fc7\uff0c\u98de\u7ea2\u5dfe\u95ea\u7535\u822c\u5730\u7ffb\u4e0a\u9a6c\u80cc\uff0c\u957f\u97ad\u4e00\u5377\uff0c\u90a3\u9a91\u58eb\u5c1a \u672a\u770b\u6e05\uff0c\u5df2\u7ed9\u6454\u4e0b\u9a6c\u80cc\u3002\u98de\u7ea2\u5dfe\u66f4\u4e0d\u653e\u677e\uff0c\u53cd\u624b\u4e00\u97ad\uff0c\u628a\u53e6\u4e00\u540d\u9a91\u58eb\u8feb\u843d\u9a6c\u80cc\u3002\u5176\u4ed6\u4e24\u540d\u9a91 \u58eb\u90fd\u53d1\u4e86\u614c\uff0c\u7b56\u9a6c\u8eb2\u95ea\uff0c\u4e0d\u591a\u65f6\u523b\uff0c\u53c8\u7ed9\u98de\u7ea2\u5dfe\u4e00\u4e00\u6454\u5012\u5730\u4e0a\uff01 \u3000\u3000\u5854\u5c71\u65cf\u7684\u914b\u957f\u6309\u637a\u4e0d\u4f4f\uff0c\u98de\u6b65\u800c\u51fa\uff0c\u62e6\u7740\u98de\u7ea2\u5dfe\u7684\u9a6c\u5934\uff0c\u95ee\u9053\uff1a\u201c\u59d1\u5a18\u7d2f\u4e48\uff1f\u201d\u98de\u7ea2\u5dfe \u8dc3\u4e0b\u9a6c\u80cc\uff0c\u62e2\u8896\u4e00\u63d6\uff0c\u8bf4\u9053\uff1a\u201c\u82e5\u5f97\u914b\u957f\u8d50\u6559\uff0c\u5c82\u6562\u63a8\u8f9e\uff01\u201d\u5854\u5c71\u65cf\u914b\u957f\u540d\u53eb\u5df4\u62c9\uff0c\u6df1\u901a\u897f \u85cf\u5929\u9f99\u638c\u6cd5\uff0c\u5f97\u662f\u4e00\u540d\u5185\u5bb6\u9ad8\u624b\u3002\u5f53\u4e0b\u8bf4\u9053\uff1a\u201c\u6211\u60f3\u9886\u6559\u59d1\u5a18\u7684\u638c\u6cd5\u3002\u201d\u98de\u7ea2\u5dfe\u9053\uff1a\u201c\u90a3\u5c31 \u8bf7\u4f60\u8fdb\u62db\u5427\uff01\u201d\u5df4\u62c9\u5237\u5730\u4e00\u7a9c\uff0c\u5feb\u4f3c\u98d8\u98ce\uff0c\u53cc\u81c2\u4e00\u5f20\uff0c\u5411\u5916\u4e00\u5c55\uff0c\u5de6\u638c\u638c\u51fb\u98de\u7ea2\u5dfe\u989d\u95e8\uff0c\u53f3 \u638c\u6253\u51fa\u4e00\u534a\uff0c\u5ffd\u5316\u4e00\u62f3\uff0c\u731b\u51fb\u98de\u7ea2\u5dfe\u524d\u80f8\uff0c\u98de\u7ea2\u5dfe\u6b65\u6cd5\u8f7b\u7075\uff0c\u500f\u7136\u8f6c\u8eab\uff0c\u4e00\u4e2a\u201c\u9739\u96f3\u624b\u201d\u5316 \u89e3\u4e86\u654c\u4eba\u653b\u52bf\uff0c\u53cc\u638c\u8f7b\u98d8\u98d8\u7684\u62cd\u53bb\uff0c\u5df4\u62c9\u80a9\u5934\u4e2d\u4e86\u4e00\u638c\uff0c\u6025\u5fd9\u6c89\u80a9\u7f29\u8155\uff0c\u4ed6\u53f3\u638c\u5411\u4e0b\u4e00\u622a\uff0c \u4e00\u62db\u201c\u91d1\u5203\u5288\u98ce\u201d\uff0c\u731b\u5207\u98de\u7ea2\u5dfe\u8109\u95e8\uff0c\u8fd9\u4e00\u62db\u75be\u5982\u7535\u5149\u77f3\u706b\uff0c\u662f\u5929\u9f99\u638c\u6cd5\u4e2d\u53cd\u5b88\u4e3a\u653b\u7684\u5999 \u62db\uff0c\u54ea\u6599\u98de\u7ea2\u5dfe\u76c8\u76c8\u4e00\u8f6c\uff0c\u8eab\u5b50\u7adf\u4f3c\u968f\u7740\u638c\u98ce\u76f4\u98d8\u51fa\u53bb\uff0c\u76f4\u4f3c\u821e\u8e48\u4e00\u822c\uff0c\u5341\u5206\u7f8e\u5999\u3002\u6768\u4e91\u806a \u5fc3\u5ff5\u4e00\u52a8\uff0c\u201c\u8fd9\u638c\u6cd5\u7adf\u4f3c\u5728\u54ea\u513f\u89c1\u8fc7\u7684\u201d\uff0c\u7ec6\u7ec6\u4e00\u60f3\uff0c\u624d\u60f3\u8d77\u81ea\u5df1\u5728\u5929\u5c71\u5b66\u6280\u7684\u7b2c\u4e09\u5e74\uff0c\u90a3 \u65f6\u695a\u662d\u5357\u624d\u521a\u521a\u4e0a\u5c71\uff0c\u5e08\u7236\u8981\u81ea\u5df1\u4ee3\u4f20\u638c\u6cd5\uff0c\u6709\u4e00\u65e5\u5e08\u5144\u5f1f\u6b63\u5728\u5582\u62db\u5bf9\u638c\uff0c\u5ffd\u7136\u9762\u524d\u73b0\u51fa\u4e00 \u4f4d\u8001\u5a46\u5a46\uff0c\u7b11\u9053\uff1a\u201c\u597d\u638c\u6cd5\uff01\u201d\u53eb\u81ea\u5df1\u5e08\u5144\u5f1f\u540c\u65f6\u5411\u5979\u53d1\u638c\uff0c\u5979\u8f7b\u98d8\u98d8\u5730\u4e1c\u8f6c\u897f\u8f6c\uff0c\u7247\u523b\u4e4b \u95f4\uff0c\u81ea\u5df1\u5c31\u4e2d\u4e86\u5979\u51e0\u638c\u3002\u5e78\u800c\u5979\u6beb\u4e0d\u7528\u529b\uff0c\u5e76\u672a\u89c9\u75db\u3002\u8fd9\u65f6\uff0c\u5e08\u7236\u5df2\u4e0d\u77e5\u4ec0\u4e48\u65f6\u5019\u8d70\u4e86\u51fa \u6765\uff0c\u7b11\u9053\uff1a\u201c\u767d\u592a\u5a46\uff0c\u4f60\u600e\u7684\u6b3a\u4fae\u5c0f\u5b69\u5b50\uff1f\u201d\u8001\u5a46\u5a46\u9053\uff1a\u201c\u6666\u660e\uff0c\u4f60\u603b\u4e0d\u80af\u548c\u6211\u6bd4\u8bd5\uff0c\u6211\u4ee5 \u4e3a\u5929\u5c71\u638c\u6cd5\u5929\u4e0b\u65e0\u53cc\uff0c\u539f\u6765\u4e5f\u4e0d\u8fc7\u5982\u6b64\uff01\u201d\u5e08\u7236\u7ed9\u5979\u6324\u5f97\u6ca1\u6709\u6cd5\u5b50\uff0c\u53ea\u597d\u548c\u5979\u6bd4\u638c\uff0c\u4e24\u4e2a\u5c0f \u5b69\u5b50\u770b\u5f97\u773c\u82b1\u9563\u4e71\uff0c\u8fde\u4eba\u5f71\u4e5f\u5206\u4e0d\u51fa\u6765\uff0c\u5ffd\u7136\u90a3\u4e2a\u8001\u5a46\u5a46\u500f\u5730\u8df3\u51fa\u5708\u5b50\uff0c\u4e00\u58f0\u4e0d\u54cd\uff0c\u98de\u9a70\u800c \u53bb\uff01\u5e08\u7236\u53f9\u9053\uff1a\u201c\u8fd9\u8001\u59aa\u600e\u7684\u5982\u6b64\u597d\u80dc\uff01\u201d\u5e08\u5144\u5f1f\u518d\u4e09\u8bf7\u95ee\uff0c\u624d\u77e5\u662f\u5e08\u7236\u8d62\u4e86\u4e00\u638c\u3002 \u3000\u3000\u6768\u4e91\u806a\u4e00\u770b\u98de\u7ea2\u5dfe\u7684\u638c\u6cd5\uff0c\u84e6\u5730\u60f3\u8d77\u8001\u592a\u5a46\u6765\uff0c\u4e0d\u7528\u518d\u770b\uff0c\u4fbf\u77e5\u98de\u7ea2\u5dfe\u4e00\u5b9a\u83b7\u5f97\u80dc\uff0c\u679c \u7136\u4e0d\u8fc7\u7247\u523b\uff0c\u5ffd\u542c\u5f97\u5df4\u62c9\u5927\u53eb\u4e00\u58f0\uff0c\u8dcc\u51fa\u4e24\u4e09\u4e08\u5916\u3002 \u3000\u3000------------------ \u3000\u3000 \u626c\u5251\u8f69\u5c45\u58eb \u626b\u63cf\u6821\u5bf9  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u585e\u5916\u5947\u4fa0\u4f20\u300b\u2014\u2014\u7b2c\u5341\u56de\u3000\u5185\u5fc3\u7684\u9a84\u50b2 \u6881\u7fbd\u751f\u300a\u585e\u5916\u5947\u4fa0\u4f20\u300b \u7b2c\u5341\u56de\u3000\u5185\u5fc3\u7684\u9a84\u50b2 \u3000\u3000\u98de\u7ea2\u5dfe\u98de\u8eab\u4e00\u63a0\uff0c\u75be\u5982\u5f13\u7bad\uff0c\u4f38\u624b\u6349\u7740\u4e86\u5df4\u62c9\u7684\u76ae\u978b\u540e\u8ddf\u4e00\u62c9\uff0c\u786c\u628a\u5df4\u62c9\u62c9\u4e86\u56de\u6765\u3002\u5df4 \u62c9\u9002\u624d\u4e0e\u98de\u7ea2\u5dfe\u6bd4\u8bd5\u65f6\uff0c\u5929\u9f99\u638c\u6cd5\u7684\u4e00\u767e\u4e8c\u5341\u516d\u5f0f\u62db\u6570\uff0c\u5df2\u7528\u4e86\u4e00\u534a\u4ee5\u4e0a\uff0c\u51e1\u662f\u78b0\u4e0d\u7740\u98de\u7ea2 \u5dfe\uff0c\u751a\u81f3\u8fde\u5979\u968f\u98ce\u98d8\u52a8\u7684\u5f69\u88d9\u4e5f\u635e\u4e0d\u7740\uff0c\u53c8\u6025\u53c8\u60ca\uff0c\u4e00\u4e0b\u5b50\u4f7f\u51fa\u4e86\u5929\u9f99\u638c\u6cd5\u7684\u6cfc\u98ce\u76d8\u6253\u4e09\u7edd \u62db\uff0c\u5934\u4e00\u62db\u201c\u4e4c\u9f99\u7ede\u67f1\u201d\uff0c\u53cc\u638c\u4e4c\u9f99\u822c\u7684\u4e00\u8f6c\u4e00\u7ede\uff0c\u5411\u98de\u7ea2\u5dfe\u5f71\u8eab\u731b\u6251\uff0c\u98de\u7ea2\u5dfe\u8eab\u5b50\u4e00\u4ef0\uff0c \u8f7b\u8f7b\u7684\u968f\u7740\u638c\u98ce\u76f4\u51b2\u51fa\u53bb\uff0c\u5df4\u62c9\u559d\u58f0\u201c\u597d\uff01\u201d\u8eab\u5b50\u4e00\u4f0f\uff0c\u731b\u7684\u5411\u524d\u4e00\u7a9c\uff0c\u7b2c\u4e8c\u62db\u201c\u53cc\u9f99\u51fa \u6d77\u201d\uff0c\u53cc\u638c\u9f50\u53d1\uff0c\u731b\u51fb\u98de\u7ea2\u5dfe\u540e\u5fc3\uff0c\u98de\u7ea2\u5dfe\u5fae\u5fae\u4e00\u95ea\uff0c\u65cb\u8fc7\u8eab\u6765\u3002\u5df4\u62c9\u53cc\u638c\u6253\u5230\u4e2d\u9014\uff0c\u7a81\u7136 \u5316\u638c\u4e3a\u62f3\uff0c\u4e00\u62db\u201c\u767b\u5c71\u8de8\u864e\u201d\uff0c\u5de6\u62f3\u4e00\u5c01\uff0c\u53f3\u62f3\u731b\u51fb\u51fa\u53bb\uff0c\u8fd9\u4e00\u62db\u53d1\u62db\u5947\u5feb\u82e5\u6d41\u661f\uff0c\u4ee5\u4e3a\u98de \u7ea2\u5dfe\u5b9a\u907f\u4e0d\u4e86\uff0c\u90a3\u6599\u98de\u7ea2\u5dfe\u624b\u638c\u4e00\u626c\uff0c\u6258\u4f4f\u4ed6\u7684\u624b\u8098\u4e00\u9001\uff0c\u5a07\u559d\u4e00\u58f0\u201c\u53bb\uff01\u201d\u5df4\u62c9\u6c34\u725b\u822c\u7684 \u8eab\u8eaf\uff0c\u817e\u4e91\u4e00\u822c\u76f4\u63bc\u51fa\u53bb\uff0c\u8349\u539f\u4e0a\u7684\u4eba\u9f50\u58f0\u60ca\u547c\uff0c\u5df4\u62c9\u4e5f\u662f\u7075\u9b42\u513f\u98de\u51fa\u7a8d\u513f\u3002\u4e0d\u6599\u98de\u7ea2\u5dfe\u8eab \u6cd5\u7adf\u662f\u5982\u6b64\u4e4b\u5feb\uff0c\u5c06\u4ed6\u98de\u6454\u51fa\u53bb\u4e4b\u540e\uff0c\u53c8\u628a\u4ed6\u62c9\u4e86\u56de\u53bb\u3002\u5df4\u62c9\u7ad9\u7a33\u4e4b\u540e\uff0c\u62b9\u4e86\u62b9\u6c57\uff0c\u62f1\u624b\u8bf4 \u9053\uff1a\u201c\u59d1\u5a18\u795e\u6280\uff0c\u771f\u662f\u540d\u4e0d\u865a\u4f20\uff01\u6211\u548c\u6211\u7684\u65cf\u4eba\uff0c\u613f\u8bda\u5fc3\u5c0a\u5949\u59d1\u5a18\u505a\u6211\u4eec\u7684\u76df\u4e3b\uff01\u201d\u8349\u539f\u4e0a \u4e00\u9635\u6b22\u547c\uff0c\u838e\u8f66\u65cf\u3001\u8428\u9a6c\u513f\u65cf\u548c\u582a\u6070\u65cf\u7684\u914b\u957f\u4eec\u4e5f\u7eb7\u7eb7\u56f4\u62e5\u4e0a\u6765\uff0c\u540c\u58f0\u9053\u8d3a\u3002 \u3000\u3000\u98de\u7ea2\u5dfe\u8fde\u80dc\u4e09\u9635\uff1a\u51fb\u8d25\u5357\u7586\u8457\u540d\u7684\u529b\u58eb\uff0c\u97ad\u6454\u56db\u540d\u6700\u5f3a\u7684\u9a91\u58eb\uff0c\u53c8\u4ee5\u638c\u6cd5\u8d62\u4e86\u5854\u5c71\u65cf\u7684 \u914b\u957f\u5df4\u62c9\u3002\u6bcf\u4e00\u9635\u90fd\u663e\u51fa\u6df1\u6e5b\u7684\u8d85\u51e1\u7684\u6280\u827a\u3002\u4e09\u65cf\u5341\u56db\u90e8\u843d\uff0c\u6ca1\u4e00\u4e2a\u4e0d\u5fc3\u60a6\u8bda\u670d\uff0c\u6ca1\u4e00\u4eba\u4e0d \u6df1\u5e86\u5f97\u4eba\u3002\u98de\u7ea2\u5dfe\u8fd8\u5f85\u8c26\u8ba9\uff0c\u54ea\u8fd8\u63a8\u8ba9\u5f97\u6389\uff1f\u5f53\u4e0b\u88ab\u5404\u65cf\u914b\u957f\u548c\u5404\u90e8\u843d\u7684\u4ee3\u8868\u62e5\u4e0a\u9ad8\u53f0\uff0c\u6768 \u4e91\u806a\u4e5f\u8ddf\u4e86\u4e0a\u53bb\uff0c\u5728\u5979\u7684\u8033\u8fb9\u4f4e\u4f4e\u8bf4\u9053\uff1a\u201c\u98de\u7ea2\u5dfe\uff0c\u4f60\u5c31\u7b54\u5e94\u4ed6\u4eec\uff0c\u5f53\u4e86\u76df\u4e3b\u5427\uff01\u201d\u98de\u7ea2\u5dfe \u661f\u773c\u6d41\u6ce2\uff0c\u8f7b\u56de\u76fc\u549b\uff0c\u4e5f\u8f7b\u58f0\u7684\u5bf9\u6768\u806a\u9053\uff1a\u201c\u6768\u5927\u4fa0\uff0c\u4f60\u53ef\u5f97\u7559\u5728\u5357\u7586\uff01\u201d\u5404\u65cf\u914b\u957f\u53c8\u56f4\u62e5 \u7740\u6768\u4e91\u806a\u9053\uff1a\u201c\u662f\u5440\uff0c\u6768\u5927\u4fa0\uff0c\u4f60\u521a\u624d\u4ee3\u8868\u7f57\u5e03\u65cf\u51fa\u573a\uff0c\u4f60\u53ef\u5f97\u6276\u52a9\u54b1\u4eec\u7684\u65b0\u76df\u4e3b\u5440\uff01\u201d\u6768 \u4e91\u806a\u7b11\u9053\uff1a\u201c\u51e1\u662f\u62b5\u6297\u6e05\u5175\u7684\u6211\u90fd\u613f\u5e2e\u52a9\u3002\u54c8\u739b\u96c5\u59d1\u5a18\u73b0\u5728\u662f\u5357\u7586\u6297\u6e05\u7684\u4e3b\u5e05\uff0c\u6211\u82e5\u7559\u5728\u5357 \u7586\uff0c\u5f53\u7136\u5728\u5979\u7684\u5e10\u4e0b\u6548\u529b\u3002\u201d\u4f17\u4eba\u53c8\u662f\u4e00\u9635\u6b22\u547c\u3002\u98de\u7ea2\u5dfe\u518d\u796d\u8fc7\u7236\u4eb2\uff0c\u4e8e\u662f\u548c\u5404\u65cf\u914b\u957f\u53ca\u5404 \u90e8\u843d\u4ee3\u8868\uff0c\u6ef4\u8840\u4e3a\u76df\uff0c\u5c31\u4e86\u76df\u4e3b\u4e4b\u4f4d\u3002 \u3000\u3000\u8fd9\u4e00\u6765\uff0c\u5927\u8349\u539f\u4e0a\u7684\u6c14\u6c1b\uff0c\u987f\u4ece\u60b2\u75db\u6fc0\u6124\u53d8\u4e3a\u5174\u9ad8\u91c7\u70c8\uff0c\u9752\u5e74\u7537\u5973\u4eec\u56f4\u7740\u91ce\u706b\uff0c\u53c8\u5531\u8d77 \u90a3\u9996\u98ce\u884c\u8349\u539f\u7684\uff0c\u9882\u8d5e\u98de\u7ea2\u5dfe\u7684\u6b4c\u6765\u3002\u4e00\u65f6\u95f4\uff0c\u201c\u6211\u4eec\u7684\u5973\u82f1\u96c4\u54c8\u739b\u96c5\uff0c\u5979\u5728\u8349\u539f\u4e4b\u4e0a\u58f0\u540d \u5927\u2026\u2026\u201d\u7684\u6b4c\u58f0\u9707\u8361\u4e86\u539f\u91ce\u3002 \u3000\u3000\u7f57\u5e03\u65cf\u7684\u957f\u8001\u4eec\u89c1\u5927\u5bb6\u5174\u81f4\u751a\u9ad8\uff0c\u4e5f\u662f\u5341\u5206\u5f00\u5fc3\u3002\u5f53\u4e0b\u5ba3\u5e03\u4e3a\u4e86\u5e86\u795d\u98de\u7ea2\u5dfe\u5f53\u9009\u65b0\u76df \u4e3b\uff0c\u4eca\u665a\u8981\u5f7b\u591c\u72c2\u6b22\uff0c\u5e76\u8bb8\u53ef\u9752\u5e74\u7537\u5973\u4eec\u73a9\u201c\u5201\u7f8a\u201d\u7684\u6e38\u620f\u3002\u201c\u5201\u7f8a\u201d\u662f\u4e00\u79cd\u628a\u201c\u9a91\u672f\u201d\u548c \u201c\u6c42\u7231\u201d\u8054\u5728\u4e00\u8d77\u7684\u6e38\u620f\uff0c\u9752\u5e74\u7537\u5973\u9a91\u4e0a\u9a8f\u9a6c\uff0c\u5728\u8349\u539f\u4e0a\u8ffd\u9010\uff0c\u7537\u7684\u5728\u524d\uff0c\u5973\u7684\u5728\u540e\uff0c\u82e5\u7537 \u7684\u7ed9\u5973\u7684\u8ffd\u4e0a\u4e86\uff0c\u53ef\u5f97\u4efb\u7531\u59d1\u5a18\u4eec\u97ad\u6253\u3002\u770b\u6765\u662f\u5973\u7684\u5360\u4e86\u201c\u4fbf\u5b9c\u201d\uff0c\u4f46\u6709\u4e9b\u7537\u5b50\uff0c\u60f3\u59d1\u5a18\u4eec \u97ad\u6253\u4e5f\u5f97\u4e0d\u7740\u3002\u539f\u6765\u59d1\u5a18\u4eec\u4e5f\u4e0d\u662f\u80e1\u4e71\u8ffd\u9010\u7537\u5b50\u97ad\u6253\u7684\uff0c\u5979\u4eec\u8ffd\u9010\u7684\u53ea\u662f\u81ea\u5df1\u5fc3\u7231\u7684\u4eba\u3002\u6709 \u4e00\u9996\u8bd7\u9053\uff1a\u201c\u79cb\u591c\u9e23\u82a6\u7ba1\uff0c\u6b4c\u58f0\u904d\u8349\u539f\uff0c\u59d1\u5a18\u9a91\u9a8f\u9a6c\uff0c\u957f\u97ad\u6253\u6240\u6b22\u201d\u5c31\u662f\u6b4c\u5531\u8fd9\u79cd\u98ce\u4fd7\u7684\u3002 \u3000\u3000\u6768\u4e91\u806a\u8fd9\u65f6\u5df2\u9000\u81f3\u53f0\u4e0b\uff0c\u6742\u5728\u4eba\u7fa4\u4e4b\u4e2d\uff0c\u8ddf\u7740\u4ed6\u4eec\u8df3\u821e\u6b4c\u5531\uff0c\u770b\u7740\u4e00\u7fa4\u9752\u5e74\u7537\u5973\uff0c\u9a91\u7740 \u9a8f\u9a6c\uff0c\u5728\u8349\u539f\u4e0a\u4e92\u76f8\u8ffd\u9010\u3002\u73a9\u201c\u5201\u7f8a\u201d\u7684\u6e38\u620f\uff0c\u4e00\u65f6\u95f4\uff0c\u97ad\u58f0\u5288\u556a\uff0c\u7b11\u58f0\u683c\u683c\uff0c\u6574\u4e2a\u8349\u539f\u90fd \u597d\u50cf\u5145\u6ee1\u4e86\u6b22\u4e50\u3002\u6768\u4e91\u806a\u5fc3\u65f7\u795e\u6021\uff0c\u5ffd\u7136\u95f4\u4ed6\u4e24\u65c1\u7684\u4eba\u7fa4\u7eb7\u7eb7\u95ea\u5f00\uff0c\u98de\u7ea2\u5dfe\u4e0d\u77e5\u4ec0\u4e48\u65f6\u5019\u4e0b \u4e86\u9ad8\u53f0\uff0c\u8d70\u4e86\u8fdb\u6765\u3002\u542b\u7b11\u62db\u547c\u4ed6\u9053\uff1a\u201c\u6768\u5927\u4fa0\uff0c\u4f60\u4e0d\u73a9\u2018\u5201\u7f8a\u2019\u7684\u6e38\u620f\uff1f\u201d\u6768\u4e91\u806a\u4e00\u9635\u5fc3 \u8df3\uff0c\u6025\u5fd9\u8bf4\u9053\uff1a\u201c\u6211\u7684\u9a91\u672f\u4e0d\u884c\uff0c\u4e0d\u61c2\u5f97\u73a9\u3002\u201d\u98de\u7ea2\u5dfe\u723d\u6717\u7b11\u9053\uff1a\u201c\u4f60\u522b\u614c\uff0c\u4e0d\u662f\u60f3\u7528\u97ad\u6253 \u4f60\uff0c\u4ed6\u4eec\u5e74\u9752\u5c0f\u4f19\u5b50\u4eec\u501f\u2018\u5201\u7f8a\uff0c\u6765\u6c42\u7231\uff0c\u6211\u4eec\u501f\u2018\u5201\u7f8a\u2019\u6765\u7ec3\u7ec3\u9a91\u672f\u96be\u9053\u4e0d\u884c\u4e48\u3002\u6211\u6700\u559c \u6b22\u591c\u665a\u7684\u8349\u539f\uff0c\u4f60\u966a\u6211\u51fa\u53bb\u73a9\u73a9\u597d\u5417\uff1f\u201d\u6768\u4e91\u806a\u6ee1\u9762\u98de\u7ea2\uff0c\u81ea\u89c9\u591a\u7591\uff0c\u6b63\u60f3\u7b54\u5e94\uff0c\u770b\u770b\u5468\u56f4 \u7684\u9752\u5e74\u7537\u5973\uff0c\u542b\u7b11\u770b\u7740\u81ea\u5df1\uff0c\u5fc3\u5ff5\u4e00\u52a8\uff0c\u8f6c\u53e3\u8bf4\u9053\uff1a\u201c\u90a3\u6211\u4eec\u5c31\u5728\u8349\u539f\u4e0a\u8d70\u8d70\u5427\uff0c\u4e0d\u5fc5\u9a91\u9a6c \u4e86\uff0c\u6211\u4eec\u7684\u811a\u7a0b\u5927\u7ea6\u4e0d\u4f1a\u6162\u8fc7\u5c0f\u4f19\u5b50\u4eec\u7684\u9a8f\u9a6c\u3002\u201d\u4ed6\u5230\u5e95\u8fd8\u662f\u907f\u8fc7\u548c\u98de\u7ea2\u5dfe\u540c\u73a9\u201c\u5201\u7f8a\u201d\u6e38 \u620f\u3002 \u3000\u3000\u6df1\u591c\u7684\u8349\u539f\uff0c\u591c\u98ce\u9001\u6765\u4e00\u9635\u9635\u9752\u8349\u7684\u6c14\u606f\uff0c\u661f\u661f\u8c61\u987d\u76ae\u7684\u5b69\u5b50\uff0c\u7728\u7740\u773c\u775b\uff0c\u597d\u50cf\u5177\u6709\u4e00 \u79cd\u8bf1\u4eba\u7684\u9b45\u529b\u3002\u4e24\u4eba\u8d8a\u884c\u8d8a\u8fdc\uff0c\u4e0d\u77e5\u4e0d\u89c9\u95f4\uff0c\u79bb\u5f00\u4e86\u55a7\u56a3\u7684\u4eba\u7fa4\uff0c\u8dd1\u5230\u4e86\u5f00\u65f7\u7684\u539f\u91ce\u3002\u80cc\u540e \u8bf5\u8d5e\u98de\u7ea2\u5dfe\u7684\u6b4c\u58f0\u8fd8\u9690\u9690\u4f20\u6765\u3002\u98de\u7ea2\u5dfe\u7b11\u8bed\u76c8\u76c8\uff0c\u4f3c\u4e4e\u5f00\u5fc3\u6781\u4e86\uff01\u6768\u4e91\u806a\u66ae\u7136\u60f3\u8d77\u4e86\u62bc\u4e0d\u5e90 \u7684\u8bdd\uff0c\u8f7b\u8f7b\u5730\u62c9\u7740\u98de\u7ea2\u5dfe\u7684\u624b\uff0c\u8bf4\u9053\uff1a\u201c\u54c8\u739b\u96c5\uff0c\u606d\u559c\u4f60\u5f53\u4e86\u76df\u4e3b\uff01\u201d\u98de\u7ea2\u5dfe\u6115\u7136\u8bf4\u9053\uff1a \u201c\u600e\u4e48\u4f60\u4e5f\u8ddf\u6211\u6765\u8fd9\u4e00\u5957\uff0c\u6211\u7684\u672c\u9886\u53ef\u6bd4\u4f60\u5dee\u5f97\u8fdc\u5462\uff01\u201d \u3000\u3000\u6768\u4e91\u806a\u5fae\u5fae\u4e00\u7b11\uff0c\u5ffd\u7136\u8bf4\u9053\uff1a\u201c\u98de\u7ea2\u5dfe\uff0c\u4f60\u65e2\u4e0d\u6b22\u559c\u5ba2\u5957\uff0c\u90a3\u4e48\u6211\u5c31\u5bf9\u4f60\u8bf4\u4e00\u4e9b\u4e0d\u5ba2\u6c14 \u7684\u8bdd\uff0c\u4f60\u4e0d\u602a\u6211\u5417\uff1f\u201d\u98de\u7ea2\u5dfe\u53cc\u773c\u95ea\u95ea\u653e\u5149\uff0c\u9887\u611f\u5947\u602a\uff0c\u8bf4\u9053\uff1a\u201c\u6768\u5927\u4fa0\uff0c\u6211\u6709\u4ec0\u4e48\u4e0d\u5bf9\u7684 \u5730\u65b9\u4f60\u5c3d\u7ba1\u8bf4\u5440\uff0c\u6211\u600e\u4f1a\u602a\u4f60\uff01\u201d \u3000\u3000\u6768\u4e91\u806a\u6c89\u601d\u4e86\u597d\u4e00\u4f1a\uff0c\u8fd9\u624d\u5728\u98de\u7ea2\u5dfe\u6ce8\u89c6\u4e0b\u5f00\u58f0\u9053\uff1a\u201c\u54c8\u739b\u96c5\uff0c\u8349\u539f\u4e0a\u7684\u9752\u5e74\u7537\u5973\u6b4c\u9882 \u4f60\uff0c\u4f60\u7684\u672c\u9886\u4e5f\u7684\u786e\u662f\u4e2d\u5e3c\u7f55\u89c1\u7684\u82f1\u96c4\u3002\u4f46\u4f60\u53ef\u60f3\u5230\uff0c\u8fd9\u4e9b\u6b4c\u9882\u4e5f\u53ef\u80fd\u662f\u65cb\u98ce\u626c\u8d77\u7684\u98ce\u6c99\uff0c \u4f1a\u6389\u8fc7\u5934\u6765\u628a\u4f60\u57cb\u6389\u5417\uff1f\u201d \u3000\u3000\u98de\u7ea2\u5dfe\u561f\u7740\u5c0f\u53e3\u8bf4\u9053\uff1a\u201c\u53c8\u4e0d\u662f\u6211\u53eb\u4ed6\u4eec\u6b4c\u5531\u7684\uff0c\u4f60\u4e0d\u9ad8\u5174\uff0c\u6211\u4ee5\u540e\u5c31\u7981\u6b62\u4ed6\u4eec\u5531\u597d \u4e86\uff01\u201d \u3000\u3000\u6768\u4e91\u806a\u5927\u58f0\u7b11\u9053\uff1a\u201c\u98de\u7ea2\u5dfe\uff0c\u4f60\u4e0d\u61c2\u6211\u7684\u610f\u601d\u3002\u6211\u5f88\u6b22\u559c\u542c\u8fd9\u4e9b\u6b4c\uff0c\u56e0\u4e3a\u6211\u9ad8\u5174\u770b\u5230\uff0c \u4f60\u4eec\u5357\u7586\u7684\u7267\u6c11\uff0c\u6709\u4ed6\u4eec\u8877\u5fc3\u4fe1\u5949\u7684\u82f1\u96c4\u3002\u4ed6\u4eec\u56e2\u7ed3\u5728\u4ed6\u4eec\u6240\u4fe1\u5949\u7684\u4eba\u7684\u5468\u56f4\uff0c\u4f1a\u53d1\u751f\u5f88\u5927 \u7684\u529b\u91cf\u3002\u800c\u4f60\uff0c\u98de\u7ea2\u5dfe\uff0c\u4e5f\u7684\u786e\u503c\u5f97\u4ed6\u4eec\u6b4c\u5531\uff01 \u3000\u3000\u201c\u6211\u8bf4\u7684\u4e0d\u662f\u8fd9\u4e2a\uff0c\u800c\u662f\uff0c\u4f60\u53ef\u66fe\u60f3\u8fc7\uff0c\u8fd9\u4e9b\u6b4c\u9882\uff0c\u4e5f\u53ef\u80fd\u7ed9\u4f60\u5e26\u6765\u4f24\u5bb3\u3002\u98de\u7ea2\u5dfe\uff0c\u4f60 \u8fd8\u8bb0\u5f97\u62bc\u4e0d\u5e90\u4e34\u6b7b\u524d\u7684\u8bdd\u5417\uff1f\u201d \u3000\u3000\u98de\u7ea2\u5dfe\u628a\u624b\u4e00\u6454\uff0c\u773c\u775b\u5145\u6ee1\u7591\u60d1\uff0c\u95ee\u9053\uff1a\u201c\u6768\u5927\u4fa0\uff0c\u96be\u9053\u4f60\u4ee5\u4e3a\u62bc\u4e0d\u5e90\u7684\u8bdd\u5bf9\u5417\uff1f\u201d \u3000\u3000\u6768\u4e91\u806a\u6b63\u8272\u8bf4\u9053\uff1a\u201c\u98de\u7ea2\u5dfe\uff0c\u62bc\u4e0d\u5e90\u662f\u6b7b\u6709\u4f59\u8f9c\uff0c\u4f46\u4ed6\u5bf9\u4f60\u8bf4\u7684\u8bdd\uff0c\u6709\u4e00\u4e9b\u5374\u503c\u5f97\u4f60\u81ea \u5df1\u4ed4\u7ec6\u53bb\u60f3\u3002\u4ed6\u662f\u4f60\u7684\u60c5\u4eba\uff0c\u4e3a\u4ec0\u4e48\u4ed6\u4f1a\u80cc\u53db\u4e86\u4f60\uff0c\u53cd\u800c\u53bb\u52fe\u7ed3\u654c\u4eba\uff1f\u201d \u3000\u3000\u98de\u7ea2\u5dfe\u674f\u8138\u901a\u7ea2\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u4ed6\u81ea\u7518\u4e0b\u8d31\uff01\u201d\u6768\u4e91\u806a\u8bf4\uff1a\u201c\u4e0d\u9519\uff0c\u4ed6\u51c9\u662f\u7075\u9b42\u5351\u52a3\u7684 \u4e1c\u897f\u3002\u4f46\u4ed6\u8fd9\u6837\u5feb\u7684\u80cc\u53db\uff0c\u548c\u4f60\u4e5f\u6709\u5173\u7cfb\uff01\u201d\u98de\u7ea2\u5dfe\u9053\uff1a\u201c\u4f60\u662f\u8bf4\u6211\u4e5f\u6709\u9519\uff1f\u201d \u3000\u3000\u6768\u4e91\u806a\u7d27\u63e1\u98de\u7ea2\u5dfe\u7684\u624b\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u662f\u7684\uff0c\u98de\u7ea2\u5dfe\uff0c\u4f60\u4e5f\u6709\u9519\uff01\u6211\u548c\u4f60\u76f8\u8bc6\u4e0d\u4e45\uff0c\u4f46 \u5728\u8fd9\u51e0\u5929\u91cc\u6211\u89c9\u5bdf\u5f97\u51fa\u6765\u3002\u5927\u5bb6\u90fd\u5728\u6b4c\u9882\u4f60\uff0c\u8bf4\u4f60\u662f\u5973\u82f1\u96c4\uff0c\u2018\u5728\u8349\u539f\u4e4b\u4e0a\u58f0\u540d\u5927\u2019\uff0c\u6211\u89c9 \u5bdf\u5230\uff0c\u5728\u4f60\u5fc3\u91cc\u6ecb\u957f\u7740\u4e00\u79cd\u60c5\u7eea\uff0c\u8fd9\u5c31\u662f\u5185\u5fc3\u7684\u9a84\u50b2\uff01\u201d \u3000\u3000\u98de\u7ea2\u5dfe\u8df3\u8d77\u6765\u9053\uff1a\u201c\u4f60\u8bf4\u6211\u9a84\u50b2\uff1f\u4f60\u95ee\u95ee\u6211\u7684\u65cf\u4eba\u5427\uff0c\u6211\u5bf9\u4ed6\u4eec\u4e0d\u662f\u633a\u548c\u6c14\u5417\uff01\u5c0f\u5b69\u5b50 \u4eec\u4e5f\u613f\u610f\u548c\u6211\u4ea4\u670b\u53cb\uff01\u201d \u3000\u3000\u6768\u4e91\u806a\u7b11\u9053\uff1a\u201c\u4f60\u7684\u9a84\u50b2\u5728\u8868\u9762\u4e0a\u770b\u4e0d\u51fa\u6765\uff0c\u6240\u4ee5\u751a\u81f3\u8fde\u4f60\u81ea\u5df1\u4e5f\u4e0d\u77e5\u9053\u3002\u201c\u5185\u5fc3\u7684\u9a84 \u50b2\uff0c\u5e38\u5e38\u4f1a\u6d41\u9732\u7ed9\u81ea\u5df1\u81f3\u4eb2\u81f3\u8fd1\u7684\u4eba\u77e5\u9053\u3002\u62bc\u4e0d\u5e90\u66fe\u7ecf\u662f\u548c\u4f60\u6240\u4eb2\u8fd1\u7684\u4eba\uff0c\u4ed6\u5c31\u6df1\u6df1\u5730\u611f\u53d7 \u5230\u4f60\u5185\u5fc3\u7684\u9a84\u50b2\uff01\u4f60\u5e76\u6ca1\u6709\u628a\u4ed6\u5f53\u6210\u5e73\u7b49\u7684\u5bf9\u624b\u770b\u5f85\uff0c\u4e0d\u662f\u5417\uff1f\u98de\u7ea2\u5dfe\uff01\u201c\u62bc\u4e0d\u5e90\u7684\u4e8b\u8fd8 \u5c0f\uff0c\u5982\u679c\u4f60\u4efb\u51ed\u81ea\u5df1\u7684\u9a84\u50b2\u5728\u5fc3\u91cc\u6ecb\u957f\uff0c\u751a\u81f3\u5f00\u82b1\u7ed3\u679c\uff0c\u90a3\u4e48\uff0c\u98de\u7ea2\u5dfe\uff0c\u90a3\u4e2a\u679c\u5b50\u5c31\u4f1a\u628a\u4f60 \u7684\u5fc3\u7075\u6bd2\u5bb3\u4e86\u3002\u201c\u98de\u7ea2\u5dfe\uff0c\u4f60\u771f\u5b9e\u7684\u544a\u8bc9\u6211\uff0c\u5f53\u4f60\u542c\u5230\u90a3\u4e9b\u8d5e\u9882\u4f60\u7684\u6b4c\u58f0\u65f6\u3002\u4f60\u7684\u611f\u89c9\u600e \u6837\uff1f\u662f\u5f15\u8d77\u4f60\u7684\u9ad8\u5174\u5462\uff0c\u8fd8\u662f\u5f15\u8d77\u4f60\u7684\u6212\u60e7\uff1f\u6211\u731c\uff0c\u4f60\u662f\u9ad8\u5174\u7684\uff0c\u5c31\u662f\u8868\u9762\u4e0a\u8868\u73b0\u51fa\u4e0d\u9ad8\u5174 \u542c\uff0c\u5176\u5b9e\u5fc3\u91cc\u8fd8\u662f\u9ad8\u5174\u7684\uff0c\u662f\u4e0d\u662f\u8fd9\u6837\u5462\uff1f\u98de\u7ea2\u5dfe\u3002\u201d \u3000\u3000\u98de\u7ea2\u5dfe\u70b9\u70b9\u5934\u9053\uff1a\u201c\u8fd9\u662f\u771f\u7684\uff01\u6768\u5927\u4fa0\uff0c\u6211\u4e0d\u9a97\u4f60\uff0c\u7684\u786e\u662f\u8fd9\u6837\u5b50\u3002\u201d\u5979\u91cd\u590d\u7684\u8bf4\u4e86\u4e4b \u540e\uff0c\u5c31\u6c89\u9ed8\u4e0b\u6765\uff0c\u7275\u7740\u6768\u4e91\u806a\u7684\u624b\uff0c\u7f13\u7f13\u7684\u5728\u8349\u539f\u4e0a\u6f2b\u6b65\uff0c\u8bb8\u4e45\uff0c\u8bb8\u4e45\uff0c\u624d\u5982\u68a6\u521d\u9192\u7684\u5401\u53e3 \u6c14\u9053\uff1a\u201c\u6768\u5927\u4fa0\uff0c\u6211\u8c22\u8c22\u4f60\uff01\u201d \u3000\u3000\u6768\u4e91\u806a\u5fc3\u4e0a\u987f\u611f\u8f7b\u677e\uff0c\u4ef0\u671b\u5929\u7a7a\uff0c\u6708\u4eae\u5df2\u8fc7\u4e2d\u5929\u3002\u4ed6\u89c9\u5f97\u98de\u7ea2\u5dfe\u4e5f\u5982\u8fd9\u6708\u4eae\u4e00\u6837\uff0c\u975e\u5e38 \u768e\u6d01\u3002\u4ed6\u6109\u5feb\u5730\u5439\u7740\u80e1\u54e8\uff0c\u98de\u7ea2\u5dfe\u4e5f\u8fc5\u5373\u4e3a\u4ed6\u6b22\u4e50\u7684\u60c5\u7eea\u6240\u611f\u67d3\uff0c\u4f4e\u58f0\u7684\u5531\u8d77\u8349\u539f\u4e0a\u8ff7\u4eba\u7684 \u7267\u6b4c\u3002\u6768\u4e91\u806a\u7b11\u9053\uff1a\u201c\u6b63\u7ecf\u7684\u4e8b\u8c08\u5b8c\u4e86\uff0c\u73b0\u5728\u8ba9\u6211\u4eec\u597d\u597d\u7684\u73a9\u4e50\u5427\uff01\u201d\u8fd9\u65f6\u6b63\u597d\u6709\u4e00\u53ea\u7f9a\u7f8a \u5728\u8349\u539f\u4e0a\u98de\u5feb\u7684\u5954\u8dd1\uff0c\u4f3c\u4e4e\u662f\u542c\u5230\u4e86\u4eba\u58f0\u5954\u9003\u7684\u3002 \u3000\u3000\u98de\u7ea2\u5dfe\u4e00\u58f0\u5a07\u7b11\uff0c\u6307\u7740\u7f9a\u7f8a\u9053\uff1a\u201c\u6211\u4eec\u53bb\u8ffd\u5b83\uff0c\u6211\u4eec\u6bd4\u8d5b\u4e00\u4e0b\u8f7b\u529f\uff0c\u4f60\u53ef\u4e0d\u8bb8\u8bf4\u6211\u9a84 \u50b2\uff01\u201d\u6768\u4e91\u806a\u7b11\u9053\uff1a\u201c\u8fd9\u4e2a\u4e0e\u9a84\u50b2\u65e0\u5173\uff0c\u4f60\u5148\u8ffd\u5427\uff01\u201d\u8bdd\u521a\u8bf4\u5b8c\uff0c\u98de\u7ea2\u5dfe\u5df2\u5982\u75be\u98ce\u4e00\u6837\u8ffd\u4e0a \u524d\u9762\uff0c\u53c8\u8c61\u4e00\u56e2\u767d\u5f71\u5728\u5927\u8349\u539f\u4e0a\u6eda\u8fc7\uff0c\u6768\u4e91\u806a\u8d5e\u9053\uff1a\u201c\u597d\u8f7b\u529f\uff01\u201d\u5f53\u4e0b\u4e5f\u5c55\u5f00\u8eab\u5f62\uff0c\u75be\u5fd9\u8ffd \u8d76\u3002 \u3000\u3000\u98de\u7ea2\u5dfe\u8dd1\u5f97\u75be\uff0c\u6768\u4e91\u806a\u4e5f\u8d76\u5f97\u6025\uff0c\u4e0d\u4e45\u4e24\u4eba\u90fd\u5df2\u8d76\u8fc7\u4e86\u7f9a\u7f8a\uff0c\u4f46\u5174\u8da3\u6b63\u6d53\uff0c\u8fd8\u662f\u98ce\u9a70\u7535 \u63a3\u822c\u7684\u8ffd\u9010\uff01 \u3000\u3000\u4e24\u4eba\u5404\u5c55\u7edd\u9876\u8f7b\u529f\uff0c\u8d8a\u8dd1\u8d8a\u5feb\uff0c\u6768\u4e91\u806a\u56e0\u4e3a\u5148\u8ba9\u4e86\u98de\u7ea2\u5dfe\u4e00\u6bb5\u8def\uff0c\u76f8\u8ddd\u6709\u4e00\u4e8c\u5341\u6b65\uff0c\u98de \u7ea2\u5dfe\u4e00\u9762\u8dd1\u4e00\u9762\u5a07\u7b11\u9053\uff1a\u201c\u4f60\u8d76\u5f97\u4e0a\u6211\u5417\uff1f\u201d\u6768\u4e91\u806a\u9053\uff1a\u201c\u4f60\u77a7\u7740\uff01\u201d\u4e00\u63d0\u6c14\uff0c\u5c55\u5f00\u201c\u516b\u6b65 \u8d76\u8749\u201d\u7684\u529f\u592b\uff0c\u51e0\u4e2a\u8d77\u843d\uff0c\u62a2\u5230\u4e86\u98de\u7ea2\u5dfe\u524d\u9762\uff0c\u56de\u8fc7\u5934\u6765\uff0c\u53cc\u624b\u4e00\u5f20\uff0c\u7b11\u9053\uff1a\u201c\u98de\u7ea2\u5dfe\uff0c\u4f60 \u7684\u8f7b\u529f\u771f\u597d\uff0c\u6211\u8ffd\u662f\u8ffd\u4e0a\u4e86\uff0c\u989d\u5934\u5374\u4e5f\u5192\u6c57\u4e86\uff01\u201d \u3000\u3000\u98de\u7ea2\u5dfe\u7b11\u9053\uff1a\u201c\u6211\u4e0d\u9ad8\u5174\u542c\u4f60\u6545\u4f5c\u8c26\u865a\u7684\u8bf4\u8bdd\uff0c\u4f60\u4e0d\u5982\u5e72\u8106\u8bf4\uff1a\u2018\u6211\u8d62\u4e86\uff01\u2019\u201d\u4e24\u4eba\u7b11 \u4e86\u4e00\u4f1a\uff0c\u98de\u7ea2\u5dfe\u5ffd\u9053\uff1a\u201c\u4f60\u770b\uff0c\u6211\u4eec\u4e5f\u4e0d\u77e5\u8dd1\u4e86\u591a\u8fdc\uff0c\u524d\u9762\u90a3\u5ea7\u5c71\u53eb\u2018\u9a6c\u8428\u5c14\u5c71\u2019\uff0c\u98ce\u666f\u5f88 \u597d\uff0c\u6211\u4eec\u7684\u4eba\u5e38\u5e38\u5230\u90a3\u91cc\u6e38\u73a9\u548c\u6253\u730e\uff0c\u4ece\u6211\u4eec\u7684\u90e8\u843d\u5230\u90a3\u91cc\u53bb\uff0c\u4ed6\u4eec\u8981\u6574\u6574\u4e00\u5929\uff01\u201d \u3000\u3000\u6768\u4e91\u806a\u4e00\u65f6\u9ad8\u5174\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u5230\u5c71\u4e0a\u73a9\u73a9\uff0c\u597d\u5417\uff1f\u201d\u98de\u7ea2\u5dfe\u62cd\u638c\u7b11\u9053\uff1a\u201c\u597d\u5440\uff0c\u6211\u4eec \u5c31\u73a9\u5230\u5929\u660e\u518d\u56de\u53bb\u3002\u201d\u8bf4\u7f62\uff0c\u53c8\u662f\u4e00\u9635\u98ce\u4f3c\u7684\u8dd1\u5728\u524d\u5934\u3002 \u3000\u3000\u4e24\u4eba\u521a\u8dd1\u4e0a\u5c71\u5c97\uff0c\u98de\u7ea2\u5dfe\u5ffd\u7136\u53e3\u5934\u8bf4\u9053\uff1a\u201c\u4f60\u542c\u542c\uff0c\u90a3\u9762\u597d\u50cf\u6709\u4eba\u58f0\uff01\u201d\u6768\u4e91\u806a\u9053\uff1a \u201c\u6211\u4eec\u6500\u4e0a\u90a3\u68f5\u5927\u6811\u53bb\u770b\u3002\u201d\u4e24\u4eba\u811a\u4e00\u70b9\u5730\uff0c\u540c\u65f6\u7eb5\u4e0a\u4e86\u4e00\u68f5\u5927\u6811\uff0c\u970e\u65f6\u6500\u4e0a\u4e86\u6811\u9876\uff0c\u5411\u4e0b \u4e00\u671b\uff0c\u53ea\u89c1\u5c71\u53e3\u7684\u659c\u5761\u4e4b\u4e0a\uff0c\u6709\u4e24\u4eba\u6253\u5f97\u5f88\u662f\u6fc0\u70c8\u3002\u4e00\u4e2a\u662f\u54c8\u8428\u514b\u4eba\u88c5\u675f\uff0c\u624b\u4f7f\u4e00\u67c4\u957f\u5251\uff0c \u4e00\u4e2a\u5374\u662f\u6ee1\u6d32\u65d7\u4eba\u7684\u6837\u5b50\uff0c\u5de6\u624b\u62a1\u5200\uff0c\u53f3\u624b\u4f7f\u5251\uff0c\u62db\u6570\u975e\u5e38\u53e4\u602a\uff0c\u6768\u4e91\u806a\u89c1\u4e86\uff0c\u51e0\u4e4e\u558a\u51fa\u58f0 \u6765\uff01 \u3000\u3000------------------ \u3000\u3000 \u626c\u5251\u8f69\u5c45\u58eb \u626b\u63cf\u6821\u5bf9  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u585e\u5916\u5947\u4fa0\u4f20\u300b\u2014\u2014\u7b2c\u5341\u4e00\u56de\u3000\u5e7d\u8c37\u6218\u53cc\u51f6 \u6881\u7fbd\u751f\u300a\u585e\u5916\u5947\u4fa0\u4f20\u300b \u7b2c\u5341\u4e00\u56de\u3000\u5e7d\u8c37\u6218\u53cc\u51f6 \u3000\u3000\u4f7f\u957f\u5251\u7684\u4eba\u6b63\u662f\u4f0a\u58eb\u8fbe\uff0c\u4ed6\u662f\u54c8\u8428\u514b\u7684\u52c7\u58eb\uff0c\u53c8\u662f\u6768\u4e91\u806a\u7684\u76df\u5f1f\uff0c\u4e00\u4e2a\u6708\u524d\uff0c\u5728\u6a2a\u6e21\u5927 \u6c99\u6f20\uff0c\u7a81\u9047\u5927\u98ce\u6c99\u65f6\u8d70\u6563\u4e86\u7684\uff0c\u90a3\u4eba\u5de6\u624b\u6301\u5200\uff0c\u53f3\u624b\u4f7f\u5251\uff0c\u62db\u6570\u7e41\u590d\u53e4\u602a\uff0c\u4f0a\u58eb\u8fbe\u867d\u662f\u6709\u540d \u7684\u52c7\u58eb\uff0c\u5374\u5140\u662f\u62b5\u6321\u4e0d\u4f4f\uff0c\u7ed9\u4ed6\u8feb\u5f97\u8fde\u8fde\u9000\u540e\u3002\u4f46\u5c71\u9e93\u4e0a\u8fd8\u6709\u4e00\u4e2a\u5927\u6c49\uff0c\u6296\u7740\u4e00\u679d\u82b1\u67aa\uff0c\u6070 \u6070\u5c01\u4f4f\u4e86\u4f0a\u58eb\u8fbe\u7684\u9000\u8def\u3002\u90a3\u65d7\u4eba\u8fb9\u6218\u8fb9\u558a\uff1a\u201c\u4f60\u8fd9\u56de\u8fd8\u4e0d\u628a\u56fe\u4ea4\u51fa\u6765\uff01\u201d\u4f0a\u58eb\u8fbe\u6012\u9053\uff1a \u201c\u5478\uff01\u4f60\u8981\u6043\u5f3a\u593a\u53d6\uff0c\u6211\u504f\u504f\u4e0d\u7ed9\uff01\u201d\u90a3\u65d7\u4eba\u51b7\u7b11\u4e00\u58f0\uff0c\u5200\u5288\u5251\u6233\uff0c\u8fde\u53d1\u51e0\u62db\u72e0\u62db\uff0c\u4f0a\u58eb\u8fbe \u957f\u5251\u4e00\u683c\uff0c\u5ffd\u7136\u201c\u5440\u201d\u7684\u4e8c\u58f0\uff0c\u53eb\u4e86\u51fa\u6765\uff0c\u90a3\u67c4\u5251\u7ed9\u654c\u4eba\u77ed\u5200\u4e00\u7ede\uff0c\u957f\u5251\u4e00\u64a9\uff0c\u7adf\u81ea\u8131\u624b\u98de \u51fa\uff01\u4f0a\u58eb\u8fbe\u817e\u8eab\u4e00\u8dc3\uff0c\u593a\u8def\u5954\u9003\uff0c\u4e00\u67c4\u82b1\u67aa\uff0c\u53c8\u8fce\u9762\u523a\u5230\u3002 \u3000\u3000\u90a3\u65d7\u4eba\u88c5\u675f\u7684\u540d\u5524\u90b1\u4e1c\u6d1b\uff0c\u662f\u957f\u767d\u5c71\u6d3e\u201c\u98ce\u96f7\u5251\u201d\u9f50\u771f\u541b\u7684\u95e8\u4e0b\uff0c\u6392\u884c\u7b2c\u4e09\uff0c\u90a3\u4f7f\u82b1\u67aa \u7684\u540d\u53eb\u67f3\u897f\u5ca9\uff0c\u662f\u4ed6\u7684\u5e08\u5f1f\uff0c\u6392\u540d\u7b2c\u4e94\u3002\u9f50\u771f\u541b\u662f\u5173\u5916\u7b2c\u4e00\u540d\u6b66\u5e08\uff0c\u6b66\u529f\u9887\u6709\u72ec\u5230\u4e4b\u5904\uff0c\u8def \u6570\u4e0e\u5173\u5185\u4e0d\u540c\uff0c\u90b1\u4e1c\u6d1b\u5728\u4ed6\u4eec\u95e8\u4e0b\u672c\u9886\u6700\u9ad8\uff0c\u4ed6\u5de6\u5200\u53f3\u5251\u7684\u602a\u62db\uff0c\u6768\u4e91\u806a\u770b\u4e86\u4e5f\u6697\u6697\u60ca\u5f02\u3002 \u4e3a\u4e86\u770b\u6e05\u4ed6\u7684\u8def\u6570\uff0c\u56e0\u6b64\u5e76\u4e0d\u7acb\u5373\u51fa\u624b\u3002\u67f3\u897f\u5ca9\u4e00\u67aa\u523a\u51fa\uff0c\u90b1\u4e1c\u6d1b\u53eb\u9053\uff1a\u201c\u5e08\u5f1f\uff0c\u7559\u6d3b \u53e3\uff01\u201d\u8bdd\u58f0\u672a\u4e86\uff0c\u5ffd\u89c9\u5934\u9876\u547c\u7684\u4e00\u58f0\uff0c\u4e00\u6761\u4eba\u5f71\u4f3c\u5927\u9e1f\u822c\u98de\u63a0\u8fc7\u53bb\uff0c\u5c1a\u672a\u770b\u6e05\uff0c\u53c8\u542c\u5f97\u67f3\u897f \u5ca9\u5927\u53eb\u4e00\u58f0\uff0c\u4ed6\u7684\u90a3\u6839\u82b1\u67aa\uff0c\u5df2\u7ed9\u6765\u4eba\u5288\u624b\u593a\u53bb\u3002\u6768\u4e91\u806a\u4f7f\u51fa\u201c\u9e70\u51fb\u957f\u7a7a\u201d\u7684\u8f7b\u529f\u7edd\u6280\uff0c\u4e00 \u624b\u593a\u4e86\u654c\u4eba\u7684\u5175\u5668\uff0c\u4e00\u624b\u62c9\u7740\u4f0a\u58eb\u8fbe\uff0c\u5e76\u80a9\u4e00\u7ad9\uff0c\u559d\u9053\uff1a\u201c\u4f60\u4eec\u4e3a\u4ec0\u4e48\u6b3a\u8d1f\u6211\u7684\u670b\u53cb\uff0c\u8bf4\u5f97 \u6709\u7406\uff0c\u5c31\u653e\u4f60\u8d70\uff0c\u82e5\u7136\u65e0\u7406\uff0c\u54fc\uff0c\u54fc\uff0c\u53ef\u8981\u4f60\u4eec\u7559\u70b9\u8bb0\u53f7\uff01\u201d \u3000\u3000\u90b1\u4e1c\u6d1b\u6b63\u60f3\u7b54\u8bdd\uff0c\u5ffd\u7136\u6811\u9876\u53c8\u662f\u4e00\u58f0\u5a07\u7b11\uff0c\u7ea2\u5dfe\u98d8\u98d8\uff0c\u5982\u4e91\u822c\u843d\u4e0b\u5730\u6765\u3002\u67f3\u897f\u5ca9\u60ca\u53eb \u9053\uff1a\u201c\u98de\u7ea2\u5dfe\uff01\u98de\u7ea2\u5dfe\uff01\u201d\u98de\u7ea2\u5dfe\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u4e5f\u8ba4\u5f97\u6211\uff1f\u201d\u957f\u97ad\u5237\u7684\u51fa\u624b\uff0c\u77ed\u5251\u4e5f\u62d4\u4e86 \u51fa\u6765\uff0c\u5bf9\u90b1\u4e1c\u6d1b\u4e00\u6307\u9053\uff1a\u201c\u539f\u6765\u4f60\u4e5f\u4f1a\u4e24\u624b\u540c\u4f7f\u4e24\u822c\u5175\u5668\uff0c\u597d\uff0c\u54b1\u4eec\u6bd4\u5212\u6bd4\u5212\uff01\u201d\u6768\u4e91\u806a\u53eb \u9053\uff1a\u201c\u54c8\u739b\u96c5\uff0c\u522b\u5fd9\uff0c\u542c\u4ed6\u4eec\u600e\u6837\u8bf4\uff01\u201d\u62c9\u7740\u4f0a\u58eb\u8fbe\u8fc7\u53bb\u548c\u98de\u7ea2\u5dfe\u89c1\u9762\uff0c\u4f0a\u58eb\u8fbe\u4e50\u5f97\u4ec0\u4e48\u4f3c \u7684\uff0c\u62b1\u7740\u6768\u4e91\u806a\u53c8\u53eb\u53c8\u8df3\uff0c\u9891\u9891\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u600e\u4e48\u9003\u51fa\u6765\u7684\uff1f\u8fd8\u7ed3\u8ba4\u4e86\u8fd9\u6837\u4e00\u4f4d\u5973\u82f1\u96c4\uff01\u201d \u3000\u3000\u90b1\u4e1c\u6d1b\u81ea\u51fa\u5e08\u95e8\uff0c\u7f55\u9022\u5bf9\u624b\uff0c\u6768\u4e91\u806a\u9732\u51fa\u90a3\u624b\u60ca\u4eba\u7684\u8f7b\u529f\u867d\u4ee4\u4ed6\u5927\u5403\u4e00\u60ca\uff0c\u53ef\u662f\u4ed6\u8fd8\u4ee5 \u4e3a\u5728\u5200\u5251\u7684\u529f\u592b\u4e0a\u672a\u5fc5\u4f1a\u8f93\u7ed9\u5bf9\u65b9\u3002\u6b64\u523b\u89c1\u5bf9\u65b9\u4e09\u4eba\u7eb5\u58f0\u8c08\u7b11\uff0c\u597d\u50cf\u5168\u4e0d\u628a\u4ed6\u4eec\u653e\u5728\u773c\u91cc\uff0c \u52c3\u7136\u5927\u6012\uff0c\u53eb\u9053\uff1a\u201c\u4f0a\u58eb\u8fbe\uff0c\u4e0d\u6015\u4f60\u6709\u5e2e\u624b\uff0c\u4f60\u4eec\u4e09\u4eba\u4e00\u9f50\u4e0a\u6765\u6211\u4eec\u4e5f\u4e0d\u6015\uff0c\u4f60\u7684\u5b9d\u56fe\u8fd8\u662f \u8981\u4ea4\u51fa\u6765\uff01\u201d \u3000\u3000\u98de\u7ea2\u5dfe\u6325\u52a8\u957f\u97ad\uff0c\u8dc3\u8dc3\u6b32\u51fa\uff0c\u6768\u4e91\u806a\u5c06\u5979\u62c9\u7740\uff0c\u95ee\u4f0a\u58eb\u8fbe\u9053\uff1a\u201c\u4ec0\u4e48\u5b9d\u56fe\uff1f\u662f\u4ed6\u4eec\u7684 \u5417\uff1f\u201d\u4f0a\u58eb\u8fbe\u9053\uff1a\u201c\u8bf4\u6765\u8bdd\u957f\uff0c\u5b9d\u8d1d\u6211\u4e0d\u5e0c\u7f55\uff0c\u4e0d\u8fc7\uff0c\u90a3\u56fe\u4e0d\u662f\u4ed6\u4eec\u7684\uff0c\u4ed6\u4eec\u5374\u4e00\u8def\u6765\u62a2\uff0c \u6211\u6c14\u4e0d\u8fc7\uff0c\u504f\u504f\u4e0d\u7ed9\u4ed6\uff01\u201d \u3000\u3000\u6768\u4e91\u806a\u542c\u4e86\u4e4b\u540e\uff0c\u53eb\u9053\uff1a\u201c\u6210\u4e86\uff01\u6211\u51fa\u53bb\u53d1\u653e\u4ed6\u4eec\uff0c\u98de\u7ea2\u5dfe\u3002\u4f0a\u58eb\u8fbe\uff0c\u4e0d\u51c6\u4f60\u4eec\u5e2e \u624b\uff01\u201d\u98de\u7ea2\u5dfe\u561f\u7740\u5c0f\u5634\uff0c\u5f88\u4e0d\u9ad8\u5174\uff0c\u4f0a\u58eb\u8fbe\u5374\u7b11\u5b5c\u5b5c\u7684\u770b\u7740\u4ed6\u7684\u76df\u5144\u3002 \u3000\u3000\u6768\u4e91\u806a\u4e00\u63a0\u800c\u51fa\uff0c\u53eb\u9053\uff1a\u201c\u597d\uff0c\u4f60\u4eec\u4e24\u4e2a\u9791\u5b50\u4e0d\u670d\u6c14\u4e0d\u662f\uff0c\u90fd\u51b2\u7740\u6211\u6765\uff01\u201d\u90b1\u4e1c\u6d1b\u9053\uff1a \u201c\u54b1\u4eec\u4e00\u5bf9\u6bd4\u6bd4\u5175\u5203\uff0c\u4f60\u62a5\u4e0a\u540d\u6765\u3002\u201d\u6768\u4e91\u806a\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u4e5f\u503c\u5f97\u6211\u4eae\u5175\u5203\uff0c\u62a5\u59d3\u540d\uff0c \u54fc\uff0c\u63a5\u7740\uff01\u201d\u4ed6\u53f3\u624b\u63e1\u7740\u90a3\u6839\u593a\u6765\u7684\u67aa\uff0c\u8f7b\u8f7b\u4e00\u629b\uff0c\u5411\u67f3\u897f\u5ca9\u63b7\u53bb\u3002\u67f3\u897f\u5ca9\u53ea\u89c9\u9510\u98ce\u52b2\u5c04\uff0c \u54ea\u91cc\u6562\u63a5\uff0c\u5f80\u5de6\u4e00\u95ea\uff0c\u90a3\u67c4\u82b1\u67aa\u201c\u5494\u5693\u201d\u4e00\u58f0\u76f4\u6ca1\u5165\u5c71\u77f3\u4e4b\u4e2d\uff0c\u6fc0\u5f97\u706b\u82b1\u56db\u6e85\u3002\u67f3\u897f\u5ca9\u7528\u5c3d \u5403\u5976\u7684\u6c14\u529b\u624d\u62d4\u5f97\u51fa\u6765\uff0c\u8138\u90fd\u9752\u4e86\uff01 \u3000\u3000\u6768\u4e91\u806a\u53cc\u81c2\u4e00\u5c48\u4e00\u4f38\uff0c\u563b\u563b\u51b7\u7b11\uff1a\u201c\u600e\u4e48\u6837\uff1f\u4f60\u4eec\u4e24\u4eba\u6446\u5175\u5203\uff0c\u540c\u4e0a\u6253\u6211\u8fd9\u7a7a\u624b\u7684\u4eba\uff0c \u90fd\u4e0d\u6562\u5427\uff1f\u54fc\uff0c\u771f\u662f\u5e9f\u7269\uff01\u201d\u90b1\u4e1c\u6d1b\u89c1\u6768\u4e91\u806a\u53c8\u663e\u4e86\u4e00\u624b\u201c\u674e\u5e7f\u5c04\u77f3\u201d\uff0c\u63b7\u67aa\u6ca1\u9876\u7684\u529f\u592b\uff0c \u5fc3\u91cc\u4e5f\u5df2\u53d1\u4e86\u6bdb\uff0c\u4f46\u8f6c\u5ff5\u4e00\u60f3\uff0c\u81ea\u5df1\u7684\u98ce\u96f7\u5200\u5251\uff0c\u79f0\u96c4\u5173\u5916\uff0c\u8fd9\u4eba\u529f\u529b\u867d\u9ad8\uff0c\u4f46\u53cc\u624b\u7a7a\u7a7a\uff0c \u6211\u5374\u6015\u4ed6\u4f5c\u751a\u3002\u4ed6\u4e0d\u77e5\u9053\u6768\u4e91\u806a\u662f\u8c01\uff0c\u4f46\u4ed6\u7684\u5e08\u5f1f\u5374\u8ba4\u5f97\u98de\u7ea2\u5dfe\uff0c\u558a\u4e86\u51fa\u6765\u3002\u98de\u7ea2\u5dfe\u7684\u540d\u5934 \u4ffa\u662f\u77e5\u9053\u7684\uff0c\u5fc3\u60f3\uff1a\u90a3\u4eba\u8981\u7528\u8089\u638c\u548c\u4ffa\u6bd4\u8bd5\uff0c\u90a3\u6211\u65e0\u8bba\u5982\u4f55\u4e0d\u4f1a\u843d\u8d25\u3002\u53ea\u662f\u4e45\u95fb\u98de\u7ea2\u5dfe\u4e43\u662f \u5357\u7586\u7b2c\u4e00\u4f4d\u5973\u82f1\u96c4\uff0c\u82e5\u5979\u51fa\u624b\uff0c\u5927\u7ea6\u53ef\u548c\u6211\u6253\u5e73\uff0c\u6211\u5e08\u5f1f\u529f\u592b\u8f83\u5f31\uff0c\u53ea\u6015\u514b\u6b64\u4eba\u4e0d\u4f4f\u3002\u6b63\u6c89 \u541f\u95f4\uff0c\u6768\u4e91\u806a\u53c8\u559d\u9053\uff1a\u201c\u600e\u4e48\u6837\uff1f\u4ed6\u4eec\u82e5\u4e0d\u6562\u6bd4\u5212\uff0c\u5c31\u5f97\u5411\u6211\u7684\u76df\u5f1f\u53e9\u5934\u8d54\u7f6a\uff01\u201d\u90b1\u4e1c\u6d1b\u773c \u73e0\u6ef4\u6e9c\u6e9c\u4e00\u8f6c\uff0c\u8bf4\u9053\uff1a\u201c\u8bdd\u8bf4\u5728\u524d\u5934\uff0c\u54b1\u4eec\u662f\u660e\u5200\u660e\u67aa\uff0c\u65c1\u4eba\u53ef\u4e0d\u51c6\u7528\u6674\u9752\u5b50\uff0c\u82e5\u8981\u7528\u6697\u9752 \u5b50\uff0c\u54b1\u4eec\u4e5f\u53ef\u5212\u51fa\u9053\u6765\uff0c\u53e6\u5916\u6bd4\u8bd5\uff01\u201d\u6768\u4e91\u806a\u559d\u9053\uff1a\u201c\u4f60\u7f57\u55e6\u4f5c\u751a\uff0c\u6211\u7684\u540c\u4f34\u7edd\u4e0d\u51fa\u624b\u5c31 \u662f\uff01\u4f60\u4eec\u4e00\u9f50\u4e0a\u5427\uff0c\u7701\u5f97\u9ebb\u70e6\u3002\u201d \u3000\u3000\u90b1\u4e1c\u6d1b\u7a81\u7136\u4e00\u5200\u523a\u53bb\uff0c\u53eb\u9053\uff1a\u201c\u8fd9\u662f\u4f60\u81ea\u5df1\u5212\u7684\u9053\u513f\uff0c\u6b7b\u522b\u627e\u6211\uff01\u201d\u6768\u4e91\u806a\u5de6\u638c\u4e00\u987f\u4e00 \u642d\uff0c\u8f7b\u62d4\u5200\u628a\uff0c\u53f3\u638c\u75be\u5c55\u5927\u64d2\u62ff\u624b\u4e2d\u7684\u6076\u62db\u201c\u91d1\u96d5\u6293\u5154\u201d\uff0c\u786c\u62a2\u90b1\u4e1c\u6d1b\u53f3\u624b\u7684\u957f\u5251\uff0c\u90b1\u4e1c\u6d1b \u5de6\u5200\u4e00\u6536\uff0c\u53f3\u624b\u957f\u5251\u201c\u4e58\u9f99\u5f15\u51f0\u201d\uff0c\u4e00\u62db\u4e09\u5f0f\uff0c\u523a\u54bd\u5589\uff0c\u6302\u4e24\u80a9\uff0c\u4ee5\u653b\u4e3a\u5b88\uff0c\u597d\u4e0d\u5bb9\u6613\u624d\u5c06 \u6768\u4e91\u806a\u7684\u64d2\u62ff\u624b\u62c6\u5f00\u3002\u53eb\u9053\uff1a\u201c\u897f\u5ca9\uff0c\u4ed6\u8981\u79f0\u91cf\u54b1\u54e5\u4fe9\uff0c\u4f60\u8fd8\u4e0d\u4e0a\u6765\uff01\u201d\u67f3\u897f\u5ca9\u5fc3\u91cc\u53d1\u6bdb\uff0c \u7ed9\u5e08\u5144\u4e00\u559d\uff0c\u53ea\u5f97\u633a\u7740\u67aa\u4ece\u65c1\u95ea\u51fb\u3002 \u3000\u3000\u90b1\u4e1c\u6d1b\u7684\u98ce\u96f7\u5200\u5251\u53e4\u602a\u4e4b\u6781\uff0c\u67f3\u897f\u5ca9\u867d\u7136\u8f83\u5f31\uff0c\u4e5f\u975e\u5eb8\u624b\uff0c\u4e00\u53e3\u5200\uff0c\u4e00\u628a\u5251\uff0c\u4e00\u679d\u67aa\uff0c \u7d27\u7d27\u88f9\u7740\u6768\u4e91\u806a\u3002\u4f0a\u58eb\u8fbe\u5728\u65c1\u8fb9\u770b\u5f97\u60ca\u5fc3\u52a8\u9b44\uff0c\u624b\u5fc3\u6dcc\u6c57\uff0c\u98de\u7ea2\u5dfe\u5374\u5728\u4ed6\u7684\u8033\u8fb9\u8f7b\u8f7b\u7b11\u9053\uff1a \u201c\u4f60\u7684\u76df\u5144\u8981\u8d62\u4e86\u3002\u201d \u3000\u3000\u539f\u6765\u5929\u5c71\u5251\u6cd5\u662f\u6666\u660e\u7985\u5e08\u7efc\u5408\u5404\u6d3e\u4e4b\u957f\uff0c\u72ec\u521b\u51fa\u6765\u7684\u3002\u5173\u5916\u7684\u98ce\u96f7\u5251\u6cd5\uff0c\u6768\u4e91\u806a\u867d\u672a\u89c1 \u8fc7\uff0c\u4f46\u770b\u4e86\u4e00\u9635\u4e4b\u540e\uff0c\u5df2\u63e3\u6469\u51fa\u9053\u7406\u6765\uff0c\u90b1\u4e1c\u6d1b\u7684\u62db\u6570\u867d\u602a\uff0c\u4ecd\u8131\u4e0d\u4e86\u4e00\u653b\u4e00\u5b88\uff0c\u4e92\u76f8\u914d\u5408 \u7684\u9053\u7406\uff0c\u82e5\u4ee5\u77ed\u5200\u653b\u65f6\uff0c\u957f\u5251\u4fbf\u64a4\u56de\u9632\u5b88\uff1b\u82e5\u4ee5\u957f\u5251\u653b\u65f6\uff0c\u77ed\u5200\u4fbf\u64a4\u56de\u9632\u5b88\u3002\u8fd9\u6837\u6218\u6cd5\u6c89\u7a33 \u5f02\u5e38\u3002\u6768\u4e91\u806a\u770b\u6e05\u4e4b\u540e\uff0c\u638c\u6cd5\u4e00\u53d8\uff0c\u7740\u7740\u62a2\u5148\uff0c\u5bf9\u65b9\u5251\u672a\u523a\u51fa\uff0c\u4ed6\u5df2\u7f29\u638c\u95ea\u8eab\uff0c\u5bf9\u65b9\u6536\u56de\uff0c \u4ed6\u53c8\u64d2\u62ff\u6251\u51fb\uff0c\u5341\u6570\u62db\u4e00\u8fc7\uff0c\u90b1\u4e1c\u6d1b\u653b\u5b88\u6b21\u5e8f\u5168\u88ab\u6253\u4e71\u3002\u6768\u4e91\u806a\u957f\u5578\u58f0\u4e2d\uff0c\u638c\u6cd5\u518d\u53d8\uff0c\u7d27\u7d27 \u76ef\u7740\u90b1\u4e1c\u6d1b\uff0c\u5de6\u638c\u6a2a\u626b\u76f4\u5288\uff0c\u4f7f\u7684\u7adf\u662f\u5200\u5251\u8def\u6570\uff0c\u53f3\u624b\u5e76\u6307\u5982\u4e7e\uff0c\u7adf\u5728\u5175\u5203\u7eb7\u98de\u4e2d\uff0c\u4f38\u624b\u53bb \u63a2\u654c\u4eba\u7a74\u9053\u3002\u4ed6\u867d\u7136\u53cc\u624b\u7a7a\u7a7a\uff0c\u5374\u4f3c\u634f\u7740\u4e24\u822c\u5175\u5668\uff0c\u628a\u90b1\u4e1c\u6d1b\u8feb\u5f97\u6ee1\u5934\u5927\u6c57\u3002\u67f3\u897f\u5ca9\u89c1\u5e08\u5144 \u8fde\u8fde\u540e\u9000\uff0c\u8fed\u9047\u9669\u62db\uff0c\u867d\u7136\u5fc3\u91cc\u53d1\u614c\uff0c\u4e5f\u53ea\u597d\u633a\u67aa\u6765\u6551\uff0c\u4ed6\u82b1\u67aa\u6253\u4e86\u4e00\u4e2a\u5708\u5b50\uff0c\u8d70\u504f\u950b\uff0c\u523a \u80a9\u5477\uff0c\u5237\u7684\u4e00\u58f0\u671d\u6768\u4e91\u806a\u80a9\u540e\u523a\u6765\u3002\u6768\u4e91\u806a\u5934\u4e5f\u4e0d\u56de\uff0c\u5ffd\u7136\u53cd\u624b\u4e00\u635e\uff0c\u559d\u58f0\u201c\u53bb\uff01\u201d\u82b1\u67aa\u5df2 \u518d\u88ab\u6768\u4e91\u806a\u593a\u5728\u624b\u4e2d\uff0c\u800c\u67f3\u897f\u5ca9\u4e5f\u8dcc\u51fa\u4e09\u4e08\u5916\u3002\u90b1\u4e1c\u6d1b\u6025\u5fd9\u8df3\u51fa\u5708\u5b50\uff0c\u53eb\u9053\uff1a\u201c\u7b97\u4e86\uff0c\u6211\u4e0d \u662f\u4f60\u7684\u654c\u624b\uff0c\u5b9d\u56fe\u6211\u4e0d\u8981\u4e86\u3002\u597d\u6c49\uff0c\u53ef\u80af\u7559\u4e0b\u540d\u5b57\uff1f\u201d\u6768\u4e91\u806a\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u73b0\u5728\u544a\u8bc9\u4f60\u4e0d \u59a8\uff0c\u6211\u4e5f\u4e0d\u6015\u4f60\u62a5\u4ec7\uff01\u201d\u4f0a\u58eb\u8fbe\u5728\u65c1\u8fb9\u51b7\u7b11\u9053\uff1a\u201c\u8fde\u4ed6\u4f60\u4eec\u4e5f\u4e0d\u8ba4\u8bc6\uff0c\u8fd8\u5145\u54ea\u8def\u597d\u6c49\uff1f\u4ed4\u7ec6 \u542c\u7740\uff0c\u4ed6\u5c31\u662f\u6768\u4e91\u806a\u5927\u4fa0\uff01\u201d\u90b1\u4e1c\u6d1b\u6253\u4e86\u4e2a\u6296\uff0c\u6697\u9053\u602a\u4e0d\u5f97\u8fd9\u6837\u5389\u5bb3\uff0c\u8fd9\u4ec7\u770b\u6765\u4eca\u4e16\u4e5f\u4e0d\u80fd \u62a5\u4e86\u3002\u5f53\u4e0b\u5782\u5934\u4e27\u6c14\uff0c\u62c9\u7740\u67f3\u897f\u5ca9\u5c31\u8981\u8d70\u51fa\u5c71\u8c37\u3002\u6768\u4e91\u806a\u559d\u9053\uff1a\u201c\u4e14\u6162\uff01\u201d\u56de\u8fc7\u5934\u95ee\u4f0a\u58eb\u8fbe \u9053\uff1a\u201c\u4ed6\u4eec\u662f\u4ec0\u4e48\u4eba\uff1f\u201d\u4f0a\u58eb\u8fbe\u9053\uff1a\u201c\u4ed6\u4eec\u81ea\u79f0\u662f\u5173\u5916\u98ce\u96f7\u5251\u9f50\u771f\u541b\u7684\u95e8\u4e0b\uff0c\u5bfb\u5b9d\u6765\u7684\uff0c\u5f3a \u86ee\u5f97\u5f88\u3002\u201d\u6768\u4e91\u806a\u8bb0\u5f97\u5e08\u7236\u8bf4\u8fc7\uff0c\u56db\u5341\u5e74\u524d\uff0c\u5173\u5916\u7684\u9f50\u771f\u541b\u66fe\u6f2b\u6e38\u65b0\u7586\uff0c\u4e0a\u8fc7\u5929\u5c71\u6765\u627e\u4ed6\u3002 \u5e76\u8bf4\u8fd9\u4eba\u5728\u5173\u5916\u8fd8\u7b97\u6b63\u6d3e\uff0c\u6b66\u529f\u4e5f\u6709\u72ec\u5230\u4e4b\u5904\u3002\u5ff5\u5728\u6b66\u6797\u4e00\u8109\uff0c\u6666\u660e\u7985\u5e08\u5f00\u5173\u89c1\u4ed6\u3002\u4e24\u4eba\u5728 \u5929\u5c71\u7edd\u9876\u8c08\u5251\uff0c\u9f50\u771f\u541b\u4e0d\u80af\u4ee5\u540e\u8f88\u81ea\u5c45\uff0c\u9887\u4e3a\u72c2\u50b2\uff0c\u4e24\u4eba\u8bdd\u4e0d\u6295\u673a\uff0c\u4e5f\u5c31\u4f5c\u7f62\uff0c\u6768\u4e91\u806a\u60f3\uff1a \u8fd9\u4e24\u4eba\u5e08\u7236\u65e2\u548c\u81ea\u5df1\u5e08\u7236\u6709\u4e00\u9762\u4e4b\u7f18\uff0c\u867d\u7136\u79cd\u65cf\u4e0d\u540c\u3002\u82e5\u4ed6\u4eec\u4e0d\u662f\u52a9\u6e05\u519b\u4e0e\u5df1\u4e3a\u654c\uff0c\u4e5f\u4e0d\u59a8 \u7f51\u5f00\u4e00\u9762\u3002\u5f53\u4e0b\u53c8\u559d\u9053\uff1a\u201c\u4f60\u4eec\u597d\u597d\u5728\u90a3\u8fb9\u7ad9\u7740\uff0c\u4e0d\u8bb8\u4e71\u52a8\uff0c\u6211\u95ee\u660e\u540e\uff0c\u518d\u8ba9\u4f60\u8d70\u3002\u201d\u8fb9 \u9053\uff1a\u201c\u4f60\u4eec\u82e5\u4e0d\u542c\u8bdd\uff0c\u8bf7\u770b\u8fd9\u7fa4\u98de\u9e1f\u3002\u201d\u90b1\u4e1c\u6d1b\u770b\u8fd9\u624b\u6413\u77f3\u5f39\u7684\u529f\u592b\uff0c\u5fc3\u60f3\uff1a\u8fd9\u624b\u529f\u592b\uff0c\u81ea \u5df1\u5e08\u7236\u867d\u7136\u4e5f\u4f1a\uff0c\u5374\u662f\u6ca1\u6709\u5982\u6b64\u529f\u529b\uff0c\u54ea\u91cc\u8fd8\u6562\u8fdd\u62d7\u3002 \u3000\u3000\u6768\u4e91\u806a\u628a\u4f0a\u58eb\u8fbe\u62c9\u8fc7\u4e00\u8fb9\uff0c\u7ec6\u95ee\u522b\u540e\u7ecf\u8fc7\u3002\u624d\u77e5\u90a3\u65e5\u6c99\u6f20\u522b\u540e\uff0c\u4f0a\u58eb\u8fbe\u4f0f\u5730\u6398\u58d5\uff0c\u85cf\u5728 \u91cc\u9762\uff0c\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u98ce\u66b4\u624d\u53bb\uff0c\u4f0a\u58eb\u8fbe\u62d4\u5f00\u5806\u5728\u8eab\u4e0a\u7684\u6d6e\u6c99\uff0c\u8d77\u6765\u56db\u5904\u5bdf\u770b\uff0c\u540c\u884c\u516b \u4eba\uff0c\u9664\u6768\u4e91\u806a\u548c\u9ea6\u76d6\u63d0\u4e0d\u89c1\u5916\uff0c\u5176\u4ed6\u4e94\u4eba\u548c\u56db\u5339\u9a86\u9a7c\uff0c\u57cb\u5728\u6c99\u5806\u4e4b\u4e2d\uff0c\u6398\u4e86\u51fa\u6765\uff0c\u5df2\u5168\u88ab\u538b \u5f97\u7a92\u606f\u6b7b\u4e86\u3002\u4f0a\u58eb\u8fbe\u5927\u54ed\u4e00\u573a\uff0c\u5728\u6c99\u6f20\u4e0a\u628a\u540c\u4f34\u57cb\u846c\u3002\u5e78\u597d\u6c34\u56ca\u548c\u5e72\u7cae\u888b\u5c1a\u672a\u538b\u574f\u3002\u4f0a\u58eb\u8fbe \u80cc\u4e86\u4e24\u888b\u6c34\u56ca\uff0c\u4e00\u888b\u5e72\u7cae\uff0c\u5207\u4e0b\u4e00\u5927\u5757\u9a7c\u5cf0\uff0c\u89c5\u8def\u5357\u884c\u3002\u53ef\u662f\u6c99\u6f20\u5927\u98ce\u66b4\u4e4b\u540e\uff0c\u5730\u5f62\u5168\u6539\uff0c \u53c8\u6ca1\u6709\u6307\u5357\u9488\uff0c\u6839\u672c\u65e0\u4ece\u8fa8\u522b\u65b9\u5411\uff0c\u4f0a\u58eb\u8fbe\u5728\u6c99\u6f20\u91cc\u8d70\u4e86\u51e0\u5929\uff0c\u5140\u662f\u6f20\u6f20\u9ec4\u6c99\uff0c\u8d70\u4e0d\u51fa\u5927\u6f20 \u800c\u5e72\u7cae\u548c\u6c34\uff0c\u5df2\u5403\u6389\u4e00\u534a\u4e86\u3002 \u3000\u3000\u4e00\u65e5\u9ec4\u660f\uff0c\u4f0a\u58eb\u8fbe\u770b\u7740\u6c99\u6f20\u4e0a\u81ea\u5df1\u7684\u8db3\u5370\uff0c\u8d8a\u770b\u8d8a\u5bb3\u6015\uff0c\u5ffd\u7136\u5728\u6c99\u6f20\u53c8\u53d1\u73b0\u53e6\u4e00\u4e2a\u4eba\u7684 \u8db3\u5370\uff0c\u4e0d\u7981\u72c2\u559c\uff0c\u5ffd\u5fd9\u5faa\u7740\u8db3\u5370\u627e\u53bb\uff0c\u53ea\u89c1\u4e00\u4e2a\u6c99\u5806\u4e4b\u4e0a\uff0c\u8eba\u7740\u4e00\u4e2a\u8001\u4eba\uff0c\u81c2\u6709\u521b\u4f24\uff0c\u4f24\u75d5 \u672a\u590d\uff0c\u4f0a\u58eb\u8fbe\u628a\u6c34\u548c\u5e72\u7cae\u5582\u4ed6\uff0c\u4e45\u4e45\u8001\u4eba\u624d\u8bf4\u5f97\u51fa\u8bdd\u3002\u636e\u4ed6\u8bf4\u4e5f\u662f\u5728\u6c99\u6f20\u9047\u5230\u4ec7\u4eba\uff0c\u4e2d\u5251\u4e4b \u540e\uff0c\u62fc\u547d\u5954\u9003\uff0c\u4e0d\u6599\u53c8\u9047\u5230\u6c99\u6f20\u7684\u5927\u98ce\u6c99\uff0c\u50cf\u81ea\u5df1\u4e00\u6837\uff0c\u8eb2\u8fc7\u707e\u96be\uff0c\u5374\u627e\u4e0d\u5230\u51fa\u8def\u3002 \u3000\u3000\u8fd9\u8001\u4eba\u529b\u7aed\u795e\u67af\uff0c\u867d\u7136\u559d\u6c34\u4e4b\u540e\uff0c\u7cbe\u795e\u7a0d\u632f\uff0c\u81ea\u5fd6\u4ecd\u662f\u8d70\u4e0d\u51fa\u6c99\u6f20\uff0c\u5f53\u4e0b\u5c31\u53eb\u4f0a\u58eb\u8fbe\u4e0d \u5fc5\u7406\u4ed6\uff0c\u81ea\u5df1\u6c42\u751f\u3002\u4f0a\u58eb\u8fbe\u751f\u5c31\u5584\u826f\u5fc3\u80a0\uff0c\u4e0d\u4f46\u4e0d\u8d70\uff0c\u53cd\u7559\u4e0b\u6765\u670d\u4f8d\u4ed6\uff0c\u8001\u4eba\u975e\u5e38\u611f\u6fc0\uff0c\u65ad \u65ad\u7eed\u7eed\u7684\u548c\u4f0a\u58eb\u8fbe\u8c08\u8bdd\uff0c\u4f0a\u58eb\u8fbe\u4e5f\u4e0d\u7792\u4ed6\uff0c\u5c06\u6765\u5386\u8bf4\u4e86\uff0c\u8001\u4eba\u77e5\u9053\u4ed6\u662f\u54c8\u8428\u514b\u6297\u6e05\u7684\u4e49\u58eb\uff0c \u53f9\u53e3\u6c14\u9053\uff1a\u201c\u6211\u662f\u7ef4\u543e\u5c14\u4eba\uff0c\u6e05\u5175\u5165\u5173\uff0c\u6211\u4e00\u70b9\u4e5f\u672a\u5c3d\u529b\uff0c\u771f\u662f\u60ed\u6127\u3002\u53ea\u662f\u6211\u548c\u4f60\u867d\u7136\u9053\u8def \u4e0d\u540c\uff0c\u5374\u662f\u60f3\u8fd9\u8349\u539f\u4e0a\u7684\u4eba\u751f\u6d3b\u8fc7\u5f97\u66f4\u597d\uff0c\u4f60\u4fe1\u6211\u7684\u8bdd\u5417\uff1f\u201d\u4f0a\u58eb\u8fbe\u70b9\u70b9\u5934\uff0c\u8001\u4eba\u53c8\u9053\uff1a \u201c\u6211\u5728\u8349\u539f\u6d41\u6d6a\u4e00\u751f\uff0c\u4e3a\u7684\u662f\u8981\u627e\u51fa\u4e00\u4e2a\u5b9d\u85cf\uff0c\u4e0d\uff0c\u4e0d\u6b62\u4e00\u4e2a\uff0c\u53ef\u80fd\u662f\u51e0\u5341\u4e2a\uff0c\u51e0\u767e\u4e2a\uff01\u5b9d \u85cf\u53d1\u73b0\u4e4b\u540e\uff0c\u8349\u539f\u7684\u4eba\uff0c\u4e2a\u4e2a\u90fd\u6709\u597d\u5904\u3002\u6700\u8fd1\u6211\u5df2\u53d1\u73b0\u4e00\u4e2a\u5927\u5b9d\u85cf\uff0c\u53ea\u8981\u8d70\u5230\u5357\u7586\uff0c\u8fc7\u4e86\u5b54 \u96c0\u6cb3\uff0c\u5c31\u53ef\u4ee5\u627e\u5230\u4e86\u3002\u201d\u8bf4\u7f62\uff0c\u4ed6\u6df1\u6c89\u7684\u770b\u4e86\u4f0a\u58eb\u8fbe\u4e00\u773c\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u77e5\u9053\u6211\u5feb\u8981\u6b7b\u4e86\uff0c\u73b0 \u5728\u5c31\u628a\u5b9d\u56fe\u4ea4\u7ed9\u4f60\u3002\u201d\u8bf4\u7f62\u53d6\u51fa\u4e00\u5c0f\u5757\u7f8a\u76ae\u3002\u4e0a\u9762\u753b\u6709\u5730\u56fe\uff0c\u8fd8\u6709\u6587\u5b57\u3002 \u3000\u3000\u4f0a\u58eb\u8fbe\u8bf4\u5230\u8fd9\u65f6\uff0c\u5c06\u7f8a\u76ae\u53d6\u51fa\u7ed9\u6768\u4e91\u806a\u770b\uff0c\u6768\u4e91\u806a\u9996\u5148\u770b\u5230\u90a3\u6587\u5b57\u662f\uff1a\u201c\u82e5\u7136\u627e\u5230\u9ed1\u6cc9 \u6c34\uff0c\u8349\u539f\u904d\u5730\u653e\u5149\u660e\uff01\u201d\u5947\u9053\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\u610f\u601d\u5462\uff1f\u201d \u3000\u3000------------------ \u3000\u3000 \u626c\u5251\u8f69\u5c45\u58eb \u626b\u63cf\u6821\u5bf9  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u585e\u5916\u5947\u4fa0\u4f20\u300b\u2014\u2014\u7b2c\u5341\u4e8c\u56de\u3000\u9ed1\u6cc9\u6c34\u4e4b\u8c1c \u6881\u7fbd\u751f\u300a\u585e\u5916\u5947\u4fa0\u4f20\u300b \u7b2c\u5341\u4e8c\u56de\u3000\u9ed1\u6cc9\u6c34\u4e4b\u8c1c \u3000\u3000\u201c\u4f0a\u58eb\u8fbe\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u77e5\u9053\u5440\uff01\u201d\u6768\u4e91\u806a\u518d\u770b\u90a3\u5f20\u5730\u56fe\uff0c\u53ea\u89c1\u8349\u539f\u4e0a\u4e00\u5904\u9ad8\u5c71\uff0c\u534a\u5c71\u5904 \u753b\u6709\u66f2\u66f2\u6298\u6298\u7684\u9053\u8def\uff0c\u5c71\u5cf0\u73af\u62b1\u4e2d\uff0c\u6709\u4e00\u5927\u7247\u76c6\u5730\uff0c\u76c6\u5730\u4e0a\u753b\u6709\u8bb8\u591a\u6807\u5fd7\u3002\u6768\u4e91\u806a\u5947\u9053\uff1a \u201c\u8fd9\u4e0a\u9762\u753b\u7684\uff0c\u5c31\u662f\u6211\u4eec\u73b0\u5728\u7684\u8fd9\u5ea7\u5c71\u5417\uff1f\u201d\u4f0a\u58eb\u8fbe\u9053\uff1a\u201c\u662f\u5440\uff01\u8981\u4e0d\uff0c\u6211\u600e\u80fd\u5230\u8fd9\u91cc\u6765 \u5462\uff01\u201d\u6768\u4e91\u806a\u95ee\u9053\uff1a\u201c\u4f60\u662f\u600e\u6837\u78b0\u5230\u90a3\u4e24\u4e2a\u5bb6\u4f19\u7684\uff1f\u201d\u4f0a\u58eb\u8fbe\u9053\uff1a\u201c\u90a3\u8001\u4eba\u5c06\u5730\u56fe\u4ea4\u7ed9\u6211 \u540e\uff0c\u53cc\u817f\u4e00\u4f38\uff0c\u5c31\u6b7b\u53bb\u4e86\uff0c\u6211\u5728\u6c99\u6f20\u8349\u8349\u6316\u4e86\u4e2a\u5751\uff0c\u5c06\u4ed6\u57cb\u846c\uff0c\u5fc3\u91cc\u5145\u6ee1\u4e86\u795e\u79d8\u4e4b\u611f\u3002\u8fd9\u8001 \u4eba\u662f\u4ec0\u4e48\u4eba\u5462\uff1f\u4ed6\u4e00\u751f\u6240\u627e\u5bfb\u7684\u5b9d\u7269\u53c8\u662f\u4ec0\u4e48\u5462\uff1f\u6211\u731c\u60f3\u4e86\u4e00\u6574\u665a\u3002\u7b2c\u4e8c\u5929\u4e00\u65e9\uff0c\u6b63\u60f3\u8d76 \u8def\uff0c\u8fd9\u4e24\u4e2a\u5bb6\u4f19\u6765\u4e86\uff0c\u4e00\u5230\u5c31\u95ee\u6211\u6709\u6ca1\u6709\u89c1\u7740\u8001\u4eba\uff0c\u6211\u5982\u5b9e\u8bf4\u4e86\u3002\u4ed6\u4eec\u62d4\u51fa\u5175\u5203\uff0c\u8981\u6211\u4ea4\u51fa \u8fd9\u5e45\u5730\u56fe\u3002\u6768\u5927\u4fa0\uff0c\u4f60\u77e5\u9053\u6211\u7684\u813e\u6c14\u662f\u5403\u8f6f\u4e0d\u5403\u786c\u7684\uff0c\u4f55\u51b5\u90a3\u8001\u4eba\u4e34\u6b7b\u65f6\u8bf4\u5f97\u8fd9\u6837\u90d1\u91cd\uff0c\u751a \u81f3\u8bf4\u548c\u8349\u539f\u4e0a\u7684\u4eba\u90fd\u6709\u5173\u7cfb\uff0c\u6211\u53c8\u600e\u80af\u8f7b\u6613\u7684\u4ea4\u7ed9\u4ed6\u4eec\u3002\u7ed3\u679c\u81ea\u7136\u662f\u548c\u4ed6\u4eec\u52a8\u624b\uff0c\u8fd9\u4e24\u4e2a\u5bb6 \u4f19\u6b66\u529f\u7740\u5b9e\u4e0d\u9519\uff0c\u6768\u5927\u4fa0\uff0c\u9002\u624d\u4f60\u4e5f\u89c1\u5230\u5566\uff0c\u6211\u7684\u786e\u4e0d\u662f\u4ed6\u4eec\u7684\u5bf9\u624b\u3002\u4f46\u4ed6\u4eec\u6ca1\u6709\u6c34\u56ca\uff0c\u53c8 \u7f3a\u4e4f\u5e72\u7cae\uff0c\u770b\u51c6\u4e86\u4ed6\u4eec\u7684\u5f31\u70b9\uff0c\u6211\u8bf4\uff0c\u4f60\u4eec\u82e5\u518d\u8feb\u6211\uff0c\u6211\u5c31\u628a\u6c34\u56ca\u523a\u7834\uff0c\u628a\u5e72\u7cae\u629b\u6389\uff0c\u5927\u5bb6 \u90fd\u6d3b\u4e0d\u6210\u3002\u4ed6\u4eec\u60f3\u4e86\u4e00\u4f1a\uff0c\u8bf4\u9053\uff1a\u4e5f\u7f62\uff0c\u6211\u4eec\u77e5\u9053\u9053\u8def\uff0c\u4f60\u6709\u98df\u6c34\u5e72\u7cae\uff0c\u6211\u4eec\u5e26\u4f60\u51fa\u6c99\u6f20\uff0c \u4f60\u5c06\u98df\u6c34\u5e72\u7cae\u4f9b\u5e94\u6211\u4eec\u3002\u6211\u7b54\u5e94\u4e86\uff0c\u8d70\u4e86\u4e09\u5929\uff0c\u51fa\u4e86\u6c99\u6f20\uff0c\u8fce\u9762\u5c31\u662f\u8fd9\u4e00\u5ea7\u5c71\u3002\u8fd9\u65f6\uff0c\u4ed6\u4eec \u53ef\u51f6\u5566\uff0c\u53c8\u62ff\u5200\u5f04\u67aa\u7684\u8feb\u6211\u4ea4\u51fa\u5730\u56fe\uff0c\u4e00\u76f4\u8ffd\u5230\u5c71\u4e0a\u3002\u201d \u3000\u3000\u6768\u4e91\u806a\u9053\uff1a\u201c\u597d\uff0c\u6211\u4eec\u8fdb\u53bb\u770b\u770b\uff01\u201d\u62bc\u7740\u90b1\u4e1c\u6d1b\u548c\u67f3\u897f\u5ca9\uff0c\u4e00\u884c\u4e94\u4eba\uff0c\u8d70\u8fc7\u5c71\u8170\uff0c\u679c\u7136 \u534a\u5c71\u4e4b\u4e0a\uff0c\u7fa4\u5cf0\u811a\u4e0b\uff0c\u6709\u4e00\u5927\u7247\u76c6\u5730\uff0c\u76c6\u5730\u4e2d\u6709\u4e00\u4e2a\u5c0f\u6e56\uff0c\u6e56\u8fb9\u6742\u82b1\u751f\u6811\uff0c\u666f\u8272\u9887\u4e3a\u5e7d\u7f8e\u3002 \u98de\u7ea2\u5dfe\u9053\uff1a\u201c\u8fd9\u4e2a\u5730\u65b9\uff0c\u4ee5\u524d\u6211\u4eec\u5e38\u5e38\u6765\u73a9\uff0c\u5728\u6e56\u4e2d\u8fd8\u6d17\u8fc7\u6fa1\uff0c\u96be\u9053\u5b9d\u85cf\u5c31\u5728\u8fd9\u513f\uff1f\u201d\u98de\u7ea2 \u5dfe\u4e00\u9a6c\u5f53\u524d\uff0c\u8dd1\u5230\u6e56\u8fb9\u4e00\u770b\uff0c\u5ffd\u7136\u201c\u54a6\u201d\u7684\u4e00\u58f0\u53eb\u4e86\u51fa\u6765\uff0c\u6768\u4e91\u806a\u8dd1\u4e0a\u524d\u53bb\uff0c\u53ea\u89c1\u6e56\u6c34\u58a8 \u7eff\uff0c\u597d\u50cf\u4e0a\u9762\u94fa\u4e86\u4e00\u5c42\u6cb9\u817b\u817b\u7684\u4e1c\u897f\u3002\u6768\u4e91\u806a\u76b1\u7709\u9053\uff1a\u201c\u8fd9\u6c34\u600e\u8fd9\u4e48\u810f\uff1f\u4e8f\u4f60\u4eec\u8fd8\u6562\u5728\u8fd9\u91cc \u6d17\u6fa1\uff01\u201d\u98de\u7ea2\u5dfe\u9053\uff1a\u201c\u4ee5\u524d\u54ea\u91cc\u662f\u8fd9\u6837\u7684\u3002\uff01\u6c34\u6e05\u89c1\u5e95\uff0c\u660e\u51c0\u6c81\u51c9\uff0c\u6e56\u4e2d\u8fd8\u6709\u83b2\u82b1\u5462\uff01\u201d\u6768 \u4e91\u806a\u5728\u6e56\u8fb9\u8d70\u4e86\u4e00\u5468\uff0c\u89c9\u5f97\u811a\u4e0b\u6ce5\u571f\u677e\u677e\u6563\u6563\uff0c\u6b63\u60ca\u5f02\u95f4\uff0c\u5ffd\u7136\u8ddd\u79bb\u6e56\u6ee8\u4e0d\u8fdc\u4e4b\u5904\uff0c\u5730\u9762\u5ffd \u7136\u55b7\u51fa\u4e00\u80a1\u80a1\u7684\u9ed1\u8272\u6c34\u67f1\u6765\uff01\u201d\u6768\u4e91\u806a\u7b49\u770b\u5f97\u76ee\u77aa\u53e3\u5446\uff0c\u5374\u4e0d\u6653\u5f97\u8fd9\u9ed1\u8272\u6c34\u67f1\u662f\u4ec0\u4e48\u201c\u602a \u5f02\u201d\uff0c\u6768\u4e91\u806a\u60f3\u9053\uff1a\u96be\u9053\u8fd9\u5c31\u662f\u53ef\u4ee5\u4f7f\u8349\u539f\u904d\u5730\u653e\u5149\u660e\u7684\u201c\u9ed1\u6cc9\u6c34\u201d\uff1f \u3000\u3000\u539f\u6765\u65b0\u7586\u77f3\u6cb9\u85cf\u91cf\u6700\u5bcc\uff0c\u53ea\u662f\u51e0\u767e\u5e74\u524d\uff0c\u4eba\u4eec\u4e0d\u61c2\u52d8\u63a2\u5f00\u91c7\uff0c\u5927\u597d\u5bcc\u6e90\uff0c\u57cb\u85cf\u5730\u5e95\u3002\u90a3 \u8001\u4eba\u81ea\u5c0f\u751f\u957f\u6c99\u6f20\uff0c\u904d\u6e38\u65b0\u7586\uff0c\u4e94\u5341\u5e74\u524d\uff0c\u6709\u4e00\u6b21\u4ed6\u5076\u7136\u53d1\u73b0\u4e00\u80a1\u539f\u6cb9\u4ece\u5730\u5e95\u55b7\u4e0a\u6765\uff0c\u5341\u5206 \u60ca\u5f02\u3002\u90a3\u65f6\u6b63\u662f\u6df1\u79cb\u65f6\u5206\uff0c\u5929\u6c14\u5bd2\u51b7\uff0c\u4ed6\u70b9\u706b\u53d6\u6696\uff0c\u706b\u661f\u548c\u539f\u6cb9\u63a5\u89e6\uff0c\u84ec\u7684\u4e00\u58f0\uff0c\u70e7\u4e86\u8d77 \u6765\uff0c\u90a3\u80a1\u539f\u6cb9\u53ea\u6709\u6781\u5c11\u91cf\u55b7\u51fa\u5730\u9762\uff0c\u7247\u523b\u70e7\u5c3d\u3002\u8fd9\u4eba\u540d\u53eb\u963f\u8fdc\u591a\uff0c\u4e5f\u662f\u7cbe\u901a\u6b66\u827a\uff0c\u80f8\u6000\u5927\u5fd7 \u7684\u5f02\u4eba\uff0c\u5f53\u4e0b\u5c31\u7acb\u8a93\u8981\u5728\u65b0\u7586\u5404\u5730\uff0c\u627e\u5bfb\u8fd9\u79cd\u201c\u9ed1\u6cc9\u6c34\u201d\u3002 \u3000\u3000\u4ed6\u627e\u4e86\u51e0\u5341\u5e74\uff0c\u4e5f\u66fe\u53d1\u73b0\u8fc7\u51e0\u5904\u5c0f\u91cf\u7684\u81ea\u5df1\u55b7\u51fa\u6765\u7684\u6cb9\u67f1\u3002\u90a3\u4e2a\u65f6\u5019\uff0c\u4ed6\u5f53\u7136\u4e0d\u61c2\u4ec0\u4e48 \u53eb\u505a\u94bb\u63a2\u548c\u70bc\u6cb9\uff0c\u53ea\u662f\u60f3\u9053\uff1a\u201c\u5047\u82e5\u80fd\u53d1\u73b0\u5927\u91cf\u7684\u2018\u9ed1\u6cc9\u6c34\uff0c\uff0c\u8349\u539f\u4e0a\u4e00\u5b9a\u5927\u653e\u5149\u660e\u3002\u201d\u6709 \u4e86\u51e0\u5341\u5e74\u627e\u5bfb\u201c\u9ed1\u6cc9\u6c34\u201d\u7684\u7ecf\u9a8c\uff0c\u4ed6\u5df2\u6e10\u6e10\u80fd\u591f\u5206\u522b\u51fa\u6709\u6cb9\u77ff\u7684\u5730\u65b9\uff0c\u6ce5\u571f\u4efd\u5916\u4e0d\u540c\uff0c\u4ed6\u627e \u5230\u4e86\u9a6c\u8428\u5c14\u4e24\u5c71\u7684\u76c6\u5730\uff0c\u9690\u9690\u89c9\u5f97\u8fd9\u513f\u7684\u571f\u8d28\uff0c\u597d\u50cf\u548c\u6709\u201c\u9ed1\u6cc9\u6c34\u201d\u7684\u5730\u65b9\u76f8\u540c\uff0c\u5927\u559c\u4e4b \u4e0b\uff0c\u753b\u4e86\u5730\u56fe\uff0c\u60f3\u56de\u53bb\u627e\u65cf\u4eba\u5f00\u6398\uff0c\u770b\u770b\u5730\u5e95\u4e0b\u662f\u5426\u6709\u201c\u9ed1\u6cc9\u6c34\u201d\uff0c\u4e0d\u6599\u5728\u6c99\u6f20\u4e4b\u4e2d\uff0c\u9047\u5230 \u4ec7\u4eba\uff0c\u8eab\u53d7\u91cd\u4f24\uff0c\u53c8\u627e\u4e0d\u5230\u9053\u8def\uff0c\u7adf\u7136\u547d\u4e27\u6c99\u6f20\u4e4b\u4e2d\u3002 \u3000\u3000\u963f\u8fdc\u591a\u731c\u5f97\u4e0d\u9519\uff0c\u8fd9\u76c6\u9003\u679c\u7136\u85cf\u6709\u4e30\u5bcc\u7684\u6cb9\u77ff\uff0c\u53ea\u662f\u6cb9\u5c42\u6df1\u539a\uff0c\u6240\u4ee5\u6ca1\u6709\u55b7\u5c04\u51fa\u6765\u3002\u4e0d \u6599\u4e00\u4e2a\u6708\u524d\uff0c\u6c99\u6f20\u8d77\u5927\u98ce\u66b4\uff08\u4ea6\u662f\u6768\u4e91\u806a\u9047\u5230\u7684\u90a3\u6b21\uff09\uff0c\u8428\u5c14\u5c71\u53d1\u751f\u5730\u9707\uff0c\u5730\u4e0a\u677e\u6563\uff0c\u539f\u6cb9 \u6e10\u6e10\u6d41\u9732\u51fa\u6765\u3002\u800c\u90a3\u6f84\u660e\u5982\u955c\u7684\u5c0f\u6e56\uff0c\u4e5f\u53d8\u4e86\u9ed1\u7eff\u8272\u4e86\uff01 \u3000\u3000\u6b64\u523b\uff0c\u6768\u4e91\u806a\u548c\u98de\u7ea2\u5dfe\u6b63\u5728\u770b\u7740\u201c\u9ed1\u6cc9\u6c34\u201d\u51fa\u795e\uff0c\u6ca1\u6709\u7559\u610f\u90b1\u4e1c\u6d1b\u548c\u67f3\u897f\u5ca9\u9b3c\u9b3c\u795f\u795f\u6b63 \u8981\u9003\u8dd1\u3002\u6768\u4e91\u806a\u559d\u9053\uff1a\u201c\u4f60\u4eec\u6363\u4ec0\u4e48\u9b3c\uff1f\u201d\u8eab\u5f62\u65b9\u52a8\uff0c\u6b63\u60f3\u56de\u8eab\u64d2\u4f4f\u4ed6\u4eec\uff0c\u5ffd\u7136\u6e56\u9762\u201c\u84ec\u201d \u7684\u4e00\u58f0\uff0c\u7a81\u7136\u51b2\u51fa\u4e00\u6761\u706b\u67f1\uff0c\u970e\u65f6\u95f4\u6574\u7247\u6e56\u6c34\u5316\u4e3a\u706b\u6d77\uff0c\u706b\u86c7\u76f4\u5411\u5cb8\u4e0a\u7a9c\u6765\uff0c\u6765\u5f97\u8fc5\u901f\u5f02 \u5e38\uff0c\u6768\u4e91\u806a\u53eb\u9053\uff1a\u201c\u4e0d\u597d\uff01\u201d\u4e00\u624b\u63d0\u8d77\u4f0a\u58eb\u8fbe\uff0c\u8eab\u5982\u5f13\u7bad\u822c\u5012\u7eb5\u51fa\u53bb\uff0c\u7ad9\u7a33\u4e4b\u65f6\uff0c\u53ea\u89c1\u6d53\u70df \u5e03\u7a7a\uff0c\u706b\u5149\u95ea\u95ea\uff0c\u6d53\u70df\u4e2d\u98de\u7ea2\u5dfe\u8ddf\u7740\u8dd1\u51fa\uff0c\u4ec5\u4ec5\u8fdf\u534a\u6b65\uff0c\u624b\u811a\u80f8\u8179\uff0c\u5df2\u53d7\u706b\u7130\u7684\u4f24\uff0c\u6768\u4e91\u806a \u6025\u6025\u6551\u6cbb\uff0c\u54ea\u91cc\u8fd8\u987e\u5f97\u5f97\u4e86\u90b1\u4e1c\u6d1b\u548c\u67f3\u897f\u5ca9\u3002\u8fd9\u4e24\u4eba\u7adf\u5df2\u4e58\u673a\u8dd1\u4e86\u3002 \u3000\u3000\u6768\u4e91\u806a\u968f\u8eab\u643a\u5e26\u6709\u5929\u5c71\u96ea\u83b2\u914d\u6210\u7684\u201c\u78a7\u7075\u4e39\u201d\uff0c\u80fd\u6cbb\u5185\u4f24\uff0c\u6d88\u706b\u6bd2\uff0c\u8d76\u5fd9\u627e\u51fa\u7ed9\u98de\u7ea2\u5dfe \u670d\u4e0b\uff0c\u95ee\u9053\u201c\u59a8\u4e8b\u4e48\uff1f\u201d\u98de\u7ea2\u5dfe\u5f3a\u81ea\u652f\u6491\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u8981\u7d27\uff0c\u8fc7\u4e00\u4f1a\u5c31\u6ca1\u4e8b\u5566\uff01\u201d\u98de\u7ea2\u5dfe\u4e0a \u8863\u5df2\u7834\uff0c\u9732\u51fa\u6676\u83b9\u7684\u808c\u80a4\uff0c\u6768\u4e91\u806a\u4e0d\u6562\u8feb\u89c6\uff0c\u6025\u5fd9\u89e3\u4e0b\u81ea\u5df1\u7684\u4e0a\u8863\uff0c\u7ed9\u5979\u62ab\u4e0a\u3002 \u3000\u3000\u6768\u4e91\u806a\u770b\u7740\u6e56\u4e0a\u706b\u86c7\u98de\u821e\uff0c\u8d5e\u53f9\u9053\uff1a\u201c\u679c\u7136\u662f\u904d\u5730\u5149\u660e\u3002\u201d\u98de\u7ea2\u5dfe\u8eba\u5728\u5730\u4e0a\uff0c\u62ac\u5934\u671b \u5929\uff0c\u7a7a\u4e2d\u5c3d\u662f\u5e26\u7740\u5fae\u81ed\u7684\u6d53\u70df\u3002\u98de\u7ea2\u5dfe\u60ca\u8df3\u8d77\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u7cdf\u4e86\uff01\u7cdf\u4e86\uff01\u201d\u8bf4\u7f62\uff0c\u201c\u54ce\u54df\u201d \u4e00\u58f0\uff0c\u53c8\u6454\u5730\u4e0a\uff0c\u6768\u4e91\u806a\u987e\u4e0d\u5f97\u907f\u5acc\uff0c\u6025\u5fd9\u5c06\u5979\u6276\u8d77\uff0c\u95ee\u9053\uff1a\u201c\u600e\u4e48\u5566\uff1f\u201d\u98de\u7ea2\u5dfe\u9053\uff1a\u201c\u6211 \u4e0d\u59a8\u4e8b\uff0c\u6211\u662f\u62c5\u5fc3\u6211\u7684\u65cf\u4eba\u3002\u201d\u6768\u4e91\u806a\u9053\uff1a\u201c\u4f60\u4eec\u805a\u96c6\u7684\u90a3\u4e2a\u8349\u539f\uff0c\u79bb\u6b64\u8c05\u6709\u767e\u91cc\u5f00\u5916\uff0c\u90a3 \u4f1a\u70e7\u7740\u4ed6\u4eec\uff1f\u201d\u98de\u7ea2\u5dfe\u9053\uff1a\u201c\u4e8f\u4f60\u6253\u4e86\u8fd9\u4e48\u591a\u5e74\u4ed7\uff0c\u8fd8\u60f3\u4e0d\u8d77\u6765\u5417\uff1f\u6e05\u519b\u5728\u5927\u8349\u539f\u4e0a\u5efa\u4e86\u8bb8 \u591a\u70fd\u706b\u53f0\uff0c\u4ee5\u70fd\u706b\u4e3a\u53f7\uff0c\u805a\u96c6\u519b\u961f\u3002\u6211\u4eec\u5357\u7586\u5404\u65cf\u5728\u5371\u6025\u65f6\u4fbf\u70e7\u9a6c\u7caa\uff0c\u7267\u6c11\u4e00\u89c1\u6d53\u70df\u853d\u7a7a\uff0c \u4e5f\u4f1a\u8d76\u6765\u3002\u9a6c\u7caa\u7684\u70df\u5473\u662f\u81ed\u7684\uff0c\u7267\u6c11\u4eec\u4e00\u95fb\u4fbf\u77e5\u9053\uff0c\u4f60\u770b\u8fd9\u91cc\u7684\u706b\u7130\u817e\u7a7a\uff0c\u5e26\u7740\u4e00\u80a1\u81ed\u5473\uff0c \u53ea\u6015\u6e05\u519b\u548c\u6211\u4eec\u7684\u4eba\u90fd\u4f1a\u8d76\u6765\uff0c\u4e24\u65b9\u63a5\u89e6\uff0c\u4fbf\u662f\u4e00\u573a\u5927\u6218\u3002\u6211\u4eec\u8d76\u5feb\u56de\u53bb\uff0c\u8d76\u5feb\u56de\u53bb\uff01\u201d \u3000\u3000\u6768\u4e91\u806a\u6572\u7740\u8111\u888b\u9053\uff1a\u201c\u4f60\u8bf4\u5f97\u5bf9\uff0c\u6211\u771f\u662f\u611a\u949d\uff01\u201d\u5176\u5b9e\u4ed6\u5e76\u975e\u4e0d\u77e5\uff0c\u53ea\u662f\u56e0\u4e3a\u4e13\u66ff\u4e3a\u98de \u7ea2\u5dfe\u6cbb\u4f24\uff0c\u6240\u4ee5\u6ca1\u6709\u60f3\u8d77\u3002 \u3000\u3000\u6768\u4e91\u806a\u68c0\u89c6\u98de\u7ea2\u5dfe\u7684\u4f24\u52bf\uff0c\u77e5\u9053\u4e0d\u662f\u91cd\u4f24\uff0c\u5374\u4e5f\u4e0d\u5b9c\u4e8e\u8fd0\u7528\u8f7b\u529f\uff0c\u8e0c\u8e87\u4e00\u9635\uff0c\u8bf4\u9053\uff1a \u201c\u6211\u80cc\u4f60\u56de\u53bb\u5427\uff01\u201d\u98de\u7ea2\u5dfe\u6beb\u4e0d\u5ff8\u6029\uff0c\u62b1\u7740\u6768\u4e91\u806a\u8116\u5b50\uff0c\u8ba9\u4ed6\u80cc\u51fa\u5c71\u8c37\u3002 \u3000\u3000\u6768\u4e91\u806a\u56e0\u4e3a\u80cc\u7740\u53d7\u4f24\u7684\u4eba\uff0c\u4e0d\u6562\u50cf\u6765\u65f6\u4e00\u6837\uff0c\u548c\u7f9a\u7f8a\u8d5b\u8dd1\uff0c\u811a\u7a0b\u6162\u4e86\u8bb8\u591a\uff0c\u5373\u662f\u8fd9\u6837\uff0c \u8fd8\u8981\u65f6\u65f6\u505c\u4e0b\u6765\u7b49\u4f0a\u58eb\u8fbe\u3002\u51fa\u4e86\u5c71\u8c37\uff0c\u5929\u8272\u5df2\u7ecf\u5927\u4eae\uff0c\u5ffd\u89c1\u8fdc\u5904\u5c18\u7070\u5927\u8d77\uff0c\u4f3c\u662f\u519b\u9a6c\u6740\u6765\u3002 \u6768\u4e91\u806a\u53c8\u7ed9\u98de\u7ea2\u5dfe\u670d\u4e0b\u4e00\u9897\u201c\u78a7\u7075\u4e39\u201d\u8bf4\u9053\uff1a\u201c\u6211\u8981\u8dd1\u5feb\u4e86\uff0c\u4f60\u8981\u5c0f\u5fc3\u70b9\uff01\u201d\u4e00\u624b\u62c9\u7740\u4f0a\u58eb \u8fbe\uff0c\u653e\u5f00\u811a\u6b65\u75be\u8dd1\u3002 \u3000\u3000\u53c8\u8fc7\u4e86\u534a\u4e2a\u65f6\u8fb0\uff0c\u7ea6\u83ab\u5df2\u8d70\u4e86\u4e03\u516b\u5341\u91cc\uff0c\u540e\u9762\u9a6c\u94c3\u53ee\u5f53\uff0c\u6709\u5341\u591a\u9a91\u9a6c\u961f\uff0c\u8ffd\u4e86\u4e0a\u6765\uff0c\u5f29 \u7bad\u7eb7\u98de\uff0c\u6768\u4e91\u806a\u6ca1\u6cd5\uff0c\u5c06\u98de\u7ea2\u5dfe\u653e\u5728\u5730\u4e0a\uff0c\u5429\u5429\u4f0a\u58eb\u8fbe\u9053\uff1a\u201c\u4f60\u5b88\u536b\u5979\uff0c\u5f85\u6211\u628a\u8fd9\u4e9b\u8ffd\u5175\u6740 \u9000\u3002\u201d\u98de\u8eab\u5954\u51fa\uff0c\u53cc\u624b\u8fce\u7740\u5f13\u7bad\u75be\u6293\uff0c\u8fb9\u63a5\u8fb9\u53d1\u3002\u5c06\u5c04\u6765\u7684\u7bad\u53cd\u63b7\u56de\u53bb\uff0c\u970e\u65f6\u4f24\u4e86\u51e0\u4eba\u3002\u90a3 \u5341\u51e0\u9a91\u9a6c\uff0c\u56f4\u4e86\u4e0a\u6765\u3002\u6768\u4e91\u806a\u5c55\u5f00\u6781\u5176\u8fc5\u6377\u7684\u8eab\u6cd5\uff0c\u7eb5\u9ad8\u8dc3\u4f4e\uff0c\u638c\u5288\u5251\u6233\uff0c\u5341\u51e0\u540d\u9a91\u58eb\uff0c\u6ca1 \u591a\u4e45\uff0c\u5168\u88ab\u6740\u6b7b\uff01\u6768\u4e91\u806a\u4e00\u58f0\u957f\u7b11\uff0c\u62a2\u4e86\u4e24\u5f20\u5f13\uff0c\u4e24\u888b\u7bad\uff0c\u7275\u4e86\u4e24\u5339\u9a6c\uff0c\u5927\u6b65\u8d70\u56de\uff0c\u53ef\u662f\u8fd9 \u5341\u9a91\u9a6c\u4e43\u5927\u519b\u7684\u201c\u65a5\u7334\u201d\uff08\u5373\u4fa6\u5bdf\u5175\uff09\uff0c\u6768\u4e91\u806a\u548c\u4ed6\u4eec\u53ae\u6740\u5b8c\u6bd5\uff0c\u53c8\u6709\u767e\u591a\u9a91\u5148\u950b\u90e8\u961f\u56f4\u4e0a \u6765\u4e86\u3002 \u3000\u3000\u6768\u4e91\u806a\u548c\u4f0a\u58eb\u8fbe\u6276\u7740\u98de\u7ea2\u5dfe\u8eb2\u5728\u4e00\u4e2a\u571f\u4e18\u4e4b\u540e\uff0c\u6e05\u5175\u4e00\u8fd1\uff0c\u4fbf\u653e\u51b7\u7bad\uff0c\u7bad\u65e0\u865a\u53d1\uff0c\u8fc7\u6765 \u7684\u5341\u51e0\u9a91\u5175\uff0c\u90fd\u7ed9\u5c04\u4e0b\u9a6c\u6765\uff0c\u6e05\u5175\u53ea\u6562\u8fdc\u8fdc\u56f4\u7740\uff0c\u4e71\u98de\u7fbd\u7bad\u3002\u4ed6\u4eec\u90a3\u6709\u6768\u4e91\u806a\u7684\u795e\u529b\uff0c\u5f13\u7bad \u591a\u534a\u6ca1\u6709\u5c04\u81f3\u4fbf\u843d\u5728\u5730\u4e0a\uff0c\u5c04\u5230\u7684\u4e5f\u5931\u51c6\u5934\uff0c\u6768\u4e91\u806a\u6325\u5251\u53d1\u6253\uff0c\u4e0d\u65f6\u8fd8\u548c\u4f0a\u58eb\u8fbe\u7528\u5f3a\u5f13\u8fd8 \u5c04\uff0c\u867d\u7136\u53ea\u662f\u4e24\u4eba\uff0c\u5374\u548c\u90a3\u961f\u9a91\u5175\uff0c\u76f8\u6301\u4e86\u8bb8\u4e45\u3002 \u3000\u3000\u5148\u950b\u90e8\u961f\u5230\u540e\uff0c\u63a5\u7740\u4fbf\u662f\u5927\u519b\u3002\u6768\u4e91\u806a\u770b\u7740\u4e00\u5927\u961f\u4e00\u5927\u961f\u4eba\u9a6c\uff0c\u81ea\u8fdc\u800c\u8fd1\uff0c\u4e00\u76f4\u51b2\u6765\uff0c \u770b\u60c5\u52bf\u4e07\u96be\u9003\u8131\uff0c\u800c\u80cc\u540e\u53c8\u662f\u6218\u9f13\u9f50\u9e23\uff0c\u6740\u58f0\u9707\u5929\uff0c\u597d\u50cf\u662f\u4e24\u519b\u8ffd\u9010\u3002 \u3000\u3000\u6b63\u5728\u5371\u6025\uff0c\u56db\u9a91\u9a8f\u9a6c\uff0c\u5ffd\u7136\u659c\u523a\u91cc\u51b2\u6765\uff0c\u6768\u4e91\u806a\u53d1\u4e86\u4e24\u7bad\uff0c\u76f4\u5c04\u4e3a\u9996\u7684\u9a91\u58eb\uff0c\u90a3\u4eba\u9a91\u672f \u6781\u7cbe\uff0c\u7adf\u7136\u4e00\u4e2a\u201c\u8e6c\u91cc\u85cf\u8eab\u201d\uff0c\u63a5\u7740\u201c\u659c\u7a7f\u9a6c\u8179\u201d\uff0c\u4e24\u7bad\u90fd\u6ca1\u6709\u5c04\u7740\u3002\u98de\u7ea2\u5dfe\u9053\uff1a\u201c\u81ea\u5df1 \u4eba\uff01\u201d\u6768\u4e91\u806a\u5b9a\u775b\u4e00\u770b\uff0c\u8fd9\u624d\u8ba4\u51fa\u662f\u6628\u665a\u548c\u98de\u7ea2\u5dfe\u6bd4\u8bd5\u7684\u90a3\u582a\u6070\u65cf\u7684\u56db\u9a91\u58eb\u3002 \u3000\u3000\u56db\u9a91\u58eb\u65cb\u98ce\u822c\u7684\u51b2\u5230\uff0c\u53eb\u9053\uff1a\u201c\u6211\u4eec\u7ed9\u654c\u4eba\u7a81\u88ad\uff0c\u4f60\u4eec\u8d76\u5feb\u968f\u6211\u4eec\u7a81\u56f4\u3002\u201d\u4e3a\u9996\u7684\u5c06\u98de \u7ea2\u5dfe\u4e00\u628a\u62c9\u4e0a\u9a6c\u80cc\uff0c\u53c8\u51b2\u51fa\u53bb\u3002\u6768\u4e91\u806a\u548c\u4f0a\u58eb\u8fbe\u8de8\u4e0a\u521a\u624d\u62a2\u6765\u7684\u90a3\u4e24\u5339\u9a6c\uff0c\u8ddf\u7740\u51b2\u51fa\uff0c\u53ef\u662f \u5df2\u7ed9\u6e05\u5175\u9694\u65ad\u4e86\u3002\u6768\u4e91\u806a\u770b\u7740\u90a3\u56db\u9a91\u58eb\u5df2\u8ffd\u4e0a\u4e86\u4ed6\u4eec\u7684\u65cf\u4eba\uff0c\u7ea6\u6709\u4e8c\u767e\u591a\u9a91\uff0c\u867d\u7136\u540e\u6709\u8ffd \u519b\uff0c\u53ef\u662f\u8131\u9669\u6709\u671b\uff0c\u5012\u653e\u4e0b\u4e86\u5fc3\uff0c\u6325\u5251\u75be\u780d\uff0c\u548c\u4f0a\u58eb\u8fbe\u6d74\u8840\u6b7b\u6218\u3002 \u3000\u3000\u6ca1\u6709\u591a\u4e45\uff0c\u4f0a\u58eb\u8fbe\u4e2d\u7bad\u5012\u5730\uff0c\u7ed9\u6e05\u519b\u4fd8\u53bb\uff1b\u6768\u4e91\u806a\u80a9\u4e0a\u4e5f\u53d7\u4e86\u7bad\u4f24\uff0c\u53ea\u542c\u5f97\u56db\u5904\u6740\u58f0\uff0c \u5404\u65cf\u7684\u914b\u957f\uff0c\u4f3c\u4e4e\u90fd\u5df2\u5e26\u5175\u6740\u5230\uff0c\u201c\u54c8\u739b\u96c5\uff0c\u4f60\u5728\u54ea\u91cc\uff1f\u201d\u7684\u547c\u58f0\uff0c\u6b64\u8d77\u5f7c\u843d\uff0c\u60f3\u4ed6\u4eec\u8fd8\u4e0d \u77e5\u9053\u98de\u7ea2\u5dfe\u5df2\u88ab\u56db\u9a91\u5175\u6551\u51fa\uff0c\u4ecd\u5728\u5230\u5904\u5bfb\u627e\uff01\u5854\u5c71\u65cf\u7684\u914b\u957f\u5df4\u62c9\uff0c\u5df2\u8fdc\u8fdc\u671b\u89c1\u6768\u4e91\u806a\uff0c\u53ef\u662f \u5374\u88ab\u6e05\u5175\u9694\u7740\uff0c\u51b2\u6740\u4e0d\u8fdb\uff01 \u3000\u3000\u8fd9\u65f6\u5927\u8349\u539f\u4e0a\u9677\u4e8e\u6df7\u6218\uff0c\u6768\u4e91\u806a\u89c1\u6e05\u519b\u9635\u4e2d\uff0c\u98d8\u6709\u7eb3\u5170\u5c06\u519b\u7684\u5e05\u65d7\uff0c\u5fc3\u60f3\uff1a\u8fd9\u4eba\u4e5f\u6765 \u4e86\uff01\u5fc3\u5ff5\u4e00\u52a8\uff0c\u5de6\u81c2\u53c8\u4e2d\u4e86\u4e00\u5200\uff0c\u6768\u4e91\u806a\u8fd0\u529b\u53cd\u51fb\uff0c\u5355\u638c\u5288\u6b7b\u51e0\u4eba\uff0c\u77ed\u5251\u6325\u6210\u4e00\u9053\u94f6\u5149\uff0c\u62a4 \u7740\u8eab\u4f53\u3002\u6df7\u6218\u8d8a\u70c8\uff0c\u6740\u58f0\u6108\u9ad8\uff0c\u5ffd\u7136\u95f4\u6e05\u5175\u9635\u811a\u5927\u4e71\uff0c\u5343\u519b\u4e07\u9a6c\uff0c\u5982\u6f6e\u6d8c\u8fd1\u3002\u6768\u4e91\u806a\u867d\u662f\u7edd \u9876\u6b66\u529f\uff0c\u4e5f\u6321\u4e0d\u4f4f\u8fd9\u80a1\u72c2\u6f6e\uff0c\u7ed9\u4eba\u7fa4\u9a6c\u961f\u6d8c\u7740\u540e\u9000\u3002\u8fd9\u65f6\u6e05\u519b\u53ea\u987e\u9003\u547d\uff0c\u7adf\u4e0d\u7406\u81ea\u5df1\u961f\u4e2d\u8fd8 \u85cf\u6709\u4e00\u4e2a\u654c\u4eba\uff0e\u8d25\u5175\u50cf\u4e00\u4e2a\u4e2a\u6d6a\u5934\u6253\u8fc7\u6765\uff0c\u53cd\u800c\u6ca1\u4eba\u56f4\u7740\u6768\u4e91\u806a\u653b\u6253\u4e86\u3002\u4e71\u6218\u4e2d\uff0c\u6768\u4e91\u806a\u7684 \u6218\u9a6c\u7ed9\u51b7\u7bad\u5c04\u5012\uff0c\u6768\u4e91\u806a\u594b\u529b\u8dc3\u8d77\uff0c\u7528\u5927\u6454\u624b\uff0c\u53c8\u6454\u98de\u4e86\u51e0\u540d\u6e05\u5175\uff0c\u53ef\u662f\u4ecd\u65e0\u6cd5\u7a81\u56f4\uff0c\u4ecd\u662f \u88ab\u5982\u6f6e\u7684\u8d25\u5175\u62e5\u7740\uff0c\u8eab\u4e0d\u7531\u5df2\u7684\u5954\u9003\uff01 \u3000\u3000------------------ \u3000\u3000 \u626c\u5251\u8f69\u5c45\u58eb \u626b\u63cf\u6821\u5bf9  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u585e\u5916\u5947\u4fa0\u4f20\u300b\u2014\u2014\u7b2c\u5341\u4e09\u56de\u3000\u7231\u6068\u96be\u5206\u8fd8\u5b7d\u503a \u6881\u7fbd\u751f\u300a\u585e\u5916\u5947\u4fa0\u4f20\u300b \u7b2c\u5341\u4e09\u56de\u3000\u7231\u6068\u96be\u5206\u8fd8\u5b7d\u503a \u3000\u3000\u5927\u8349\u539f\u4e0a\u6218\u9a6c\u5954\u817e\uff0c\u4e24\u519b\u8ffd\u9010\uff1b\u6768\u4e91\u806a\u5939\u5728\u6ee1\u6d32\u7684\u8d25\u5175\u7fa4\u4e2d\u3002\u7eb5\u6709\u7edd\u4e16\u6b66\u529f\uff0c\u4e5f\u6321\u4e0d\u4f4f \u6392\u5c71\u5012\u6d77\u822c\u7684\u72c2\u6f6e\uff0c\u7ed9\u8d25\u519b\u6d8c\u8feb\uff0c\u8eab\u4e0d\u7531\u5df1\uff0c\u4e00\u76f4\u9a70\u9003\uff01 \u3000\u3000\u5ffd\u7136\u6ee1\u6e05\u7684\u8d25\u519b\u4e2d\u6709\u4eba\u56db\u5904\u4f20\u547c\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u6211\u4eec\u7684\u63f4\u519b\u5c31\u6765\u4e86\uff0c\u4e0d\u51c6\u540e\u9000\uff0c\u8fdd\u4ee4\u8005 \u65a9\uff01\u201d\u4f46\u54ea\u91cc\u547c\u559d\u5f97\u4f4f\uff0c\u5c31\u662f\u6709\u4e9b\u5175\u58eb\uff0c\u60f3\u505c\u4e0b\u811a\u6b65\u6765\uff0c\u4e5f\u7ed9\u524d\u5934\u9000\u4e0b\u6765\u7684\u8d25\u519b\u62e5\u7740\u5012\u9000\uff0c \u6768\u4e91\u806a\u6697\u53eb\u201c\u82e6\u4e5f\u201d\u6b63\u5954\u9003\u95f4\uff0c\u5ffd\u89c1\u7eb3\u5170\u5c06\u519b\u7684\u5e05\u65d7\u5728\u8eab\u65c1\u98d8\u52a8\uff0c\u6768\u4e91\u806a\u4fa7\u9762\u4e00\u671b\u3001\u53ea\u89c1\u7eb3 \u5170\u79c0\u5409\u8de8\u5728\u4e00\u5934\u9a8f\u9a6c\u4e0a\uff0c\u4e24\u8fb9\u62e5\u7740\u4eb2\u5175\uff1b\u5927\u58f0\u547c\u559d\u3001\u5728\u8d25\u519b\u4e2d\u547c\u53eb\u3002\u4e5f\u4e0d\u77e5\u4ed6\u4eec\u559d\u5f97\u662f\u4ec0 \u4e48\uff1f\u7eb3\u5170\u79c0\u5409\u5ffd\u7136\u770b\u89c1\u6768\u4e91\u806a\u7684\u9762\uff0c\u5927\u5403\u4e00\u60ca\uff0c\u80a5\u9a6c\u4e00\u63d0\uff0c\u75be\u51b2\u8fc7\u6765\uff0c\u51b2\u5012\u4e86\u51e0\u4e2a\u5175\u58eb\uff0c\u624b \u626c\u5904\uff0c\u51e0\u679d\u5f13\u7bad\uff0c\u95ea\u7535\u822c\u5c04\u6765\uff0c\u6768\u4e91\u806a\u88ab\u5939\u5728\u4eba\u7fa4\u4e4b\u4e2d\uff0c\u65e0\u53ef\u95ea\u907f\uff0c\u4e00\u626d\u8170\u5de6\u80a9\u53c8\u4e2d\u4e86\u4e00 \u7bad\uff01\u6768\u4e91\u806a\u6025\u8fd0\u6c14\u51dd\u795e\u3002\u53cc\u624b\u6293\u4f4f\u4e24\u540d\u6e05\u5175\uff0c\u5411\u7eb3\u5170\u79c0\u5409\u63b7\u53bb\uff0c\u7eb3\u5170\u79c0\u5409\u7684\u6218\u9a6c\u72c2\u5636\u51e0\u58f0\uff0c \u5411\u4fa7\u9762\u51b2\u53bb\uff0c\u7eb3\u5170\u79c0\u5409\u7684\u4eb2\u5175\u7d27\u508d\u7740\u4e3b\u5e05\u5954\u9003\u4e00\u9762\u6768\u4e91\u806a\u4e5f\u7ed9\u8d25\u519b\u62e5\u7740\u76f4\u5411\u540e\u9000\uff0c\u970e\u65f6\u4e4b \u95f4\uff0c\u90a3\u679d\u5e05\u65d7\uff0c\u53c8\u5df2\u79bb\u5f00\u4ed6\u4e8c\u4e09\u5341\u4e08\u4e86\uff01 \u3000\u3000\u8fc7\u4e86\u4e00\u9635\uff0c\u6768\u4e91\u806a\u5ffd\u89c9\u5f97\u80c1\u4e0b\u53d1\u9ebb\uff0c\u5fc3\u5ff5\u4e00\u52a8\uff0c\u60f3\u9053\uff1a\u83ab\u975e\u4e2d\u4e86\u6bd2\u524d\uff0c\u767e\u5fd9\u4e2d\uff0c\u817e\u624b\u53d6 \u51fa\u5929\u5c71\u96ea\u83b2\u914d\u6210\u7684\u201c\u78a7\u7075\u4e39\u201d\u54bd\u4e0b\uff0c\u4f46\u4ecd\u662f\u611f\u5230\u5fc3\u5934\u53d1\u95f7\uff0c\u53cc\u817f\u4e5f\u89c9\u9178\u8f6f\uff1a\u8fd9\u65f6\u53ea\u8981\u7a0d\u4e00\u677e \u6c14\uff0c\u7acb\u523b\u5c31\u4f1a\u7ed9\u8d25\u5175\u6324\u5230\u5730\u4e0a\uff0c\u8df5\u8e0f\u800c\u6b7b\uff01\u6768\u4e91\u806a\u5fc3\u5185\u53eb\u9053\uff1a\u201c\u6211\u4e0d\u80fd\u6b7b\uff0c\u54c8\u8428\u514b\u7684\u5144\u5f1f\u4eec \u5c1a\u672a\u627e\u5230\uff0c\u6211\u4e0d\u80fd\u6b7b\uff01\u201d\u4e00\u79cd\u5947\u5f02\u7684\u7cbe\u795e\u529b\u91cf\u652f\u6301\u7740\u4ed6\uff0c\u53c8\u8dd1\u4e86\u4e00\u9635\uff0c\u5468\u56f4\u7684\u8d25\u5175\u5df2\u5206\u6210\u8bb8 \u591a\u5c0f\u80a1\u9003\u547d\uff0c\u201c\u4eba\u6f6e\u201d\u7684\u538b\u529b\u51cf\u4e86\u8bb8\u591a\u3002\u6768\u4e91\u806a\u8d8b\u52bf\u8131\u4e86\u51fa\u6765\uff0c\u4e13\u62e3\u4eba\u5c11\u7684\u5730\u65b9\u5954\u9003\u3002\u4e5f\u4e0d \u77e5\u8dd1\u4e86\u591a\u5c11\u65f6\u5019\uff0c\u6240\u89c1\u524d\u9762\u6709\u4e00\u4e2a\u5c71\u6c9f\uff0c\u91cc\u9762\u4f3c\u6709\u4eba\u58f0\u9a6c\u58f0\uff0c\u6562\u60c5\u4e5f\u662f\u85cf\u6709\u8d25\u519b\u3002\u4f46\u6768\u4e91\u806a \u8fd9\u65f6\u4e5f\u987e\u4e0d\u5f97\u4e86\uff0c\u4e00\u98d9\u8eab\u8fdb\u4e86\u5c71\u6c9f\uff0c\u6b63\u60f3\u594b\u529b\u8dc3\u4e0a\u5c71\u5761\uff0c\u5ffd\u7136\u53cc\u817f\u4e00\u9178\uff0c\u767e\u9aa8\u6b32\u6563\uff0c\u521a\u8dc3\u8d77 \u51e0\u5c3a\u5c31\u8dcc\u4e86\u4e0b\u6765\uff0c\u6768\u4e91\u806a\u795e\u5fd7\u672a\u4e71\uff0c\u77e5\u9053\u662f\u7cbe\u795e\u7528\u7684\u8fc7\u5ea6\uff0c\u652f\u6301\u4e0d\u4f4f\uff0c\u66f4\u517c\u6bd2\u7bad\u6240\u4f24\uff0c\u7275\u7d2f \u808c\u8089\u9ebb\u75f9\uff0c\u4ed6\u6025\u5fd9\u722c\u5230\u51e0\u5757\u5c71\u77f3\u56f4\u6210\u7684\u5ce6\u969c\u4e4b\u540e\u3001\u76d8\u819d\u6253\u5750\uff0c\u53c8\u54bd\u4e0b\u4e00\u7c92\u201c\u78a7\u7075\u4e39\u201d\uff0c\u8fd9 \u201c\u78a7\u7075\u4e39\u201d\u5584\u6cbb\u5185\u4f24\uff1b\u517c\u80fd\u89e3\u6bd2\uff0c\u53ea\u662f\u773c\u4e0b\u4e4b\u540e\uff0c\u5c31\u5e94\u9759\u5750\u3002\u6768\u4e91\u806a\u521a\u624d\u5954\u8dd1\u9003\u547d\uff0c\u672c\u6765\u975e \u5e38\u5371\u9669\uff0c\u5e78\u4ed6\u4ed7\u7740\u5185\u529f\u6df1\u6e5b\uff0c\u786c\u628a\u6bd2\u6c14\u8feb\u5728\u80c1\u4e0b\uff0c\u4e0d\u4f1a\u53d1\u6563\uff0c\u6240\u4ee5\u624d\u5f97\u6ca1\u4e8b\u3002\u73b0\u5728\u7cbe\u795e\u8017 \u5c3d\uff0c\u662f\u518d\u4e5f\u4e0d\u80fd\u62fc\u786c\u8d70\u52a8\u4e86\u3002 \u3000\u3000\u6768\u4e91\u806a\u5750\u4e86\u4e0b\u6765\uff0c\u7528\u77ed\u5251\u5728\u80c1\u4e0b\u8f7b\u8f7b\u5272\u4e00\u9053\u88c2\u53e3\uff0c\u5c06\u624b\u6307\u6309\u5728\u5468\u56f4\uff0c\u7528\u624b\u6307\u4e8c\u6324\uff0c\u9ed1\u8272 \u7684\u6d53\u8840\u6c69\u6c69\u6d41\u51fa\uff0c\u7ea6\u6d41\u4e86\u4e00\u5927\u8336\u676f\uff0c\u8840\u8272\u624d\u8f6c\u6de1\u7ea2\u3002\u6768\u4e91\u806a\u628a\u5185\u8863\u6495\u7834\uff0c\u5c06\u4f24\u53e3\u5305\u624e\u8d77\u6765\u6697 \u9053\uff1a\u201c\u597d\u6bd2\uff01\u201d \u3000\u3000\u8fd9\u65f6\u6bd2\u8840\u867d\u5df2\u53bb\u5c3d\uff0c\u7cbe\u795e\u5c1a\u672a\u6062\u590d\u3002\u6768\u4e91\u806a\u76d8\u819d\u9759\u5750\uff0c\u81ea\u5df1\u8fd0\u7528\u6c14\u529f\u7597\u6cd5\u6cbb\u7597\uff0c\u5fc3\u5185\u6697 \u81ea\u7977\u544a\uff1a\u201c\u5929\u89c1\u53ef\u601c\uff0c\u4e0d\u8981\u8ba9\u4eba\u95ef\u8fdb\u6765\uff01\u201d \u3000\u3000\u6768\u4e91\u806a\u6f5c\u5fc3\u9ed8\u5750\uff0c\u8fd0\u6c14\u6d3b\u8840\uff1b\u5bf9\u5468\u56f4\u4e00\u5207\uff0c\u51e0\u4e4e\u89c6\u800c\u4e0d\u89c1\u3001\u542c\u800c\u4e0d\u95fb\u3002\u4e5f\u4e0d\u77e5\u5750\u4e86\u591a\u5c11 \u65f6\u5019\uff0c\u81ea\u89c9\u6c14\u8fd0\u56db\u68a2\uff0c\u5c0f\u8179\u53d1\u70ed\uff0c\u77e5\u9053\u5df2\u4e0d\u788d\u4e8b\uff1b\u8fd9\u624d\u7ad9\u4e86\u8d77\u6765\u3002\u7741\u773c\u4e00\u770b\uff0c\u5df2\u662f\u5b50\u591c\uff0c\u98ce \u5439\u8349\u52a8\uff0c\u8fdc\u6709\u524d\u58f0\uff0c\u5c71\u6c9f\u5916\u4e00\u7247\u9759\u5bc2\uff0c\u6e3a\u65e0\u4eba\u5f71\uff0c\u4e24\u519b\u8ffd\u9010\uff0c\u4e0d\u77e5\u5df2\u5230\u4f55\u5904\u3002\u626c\u4e91\u806a\u8bd5\u8bd5\u6d3b \u52a8\u7b4b\u9aa8\uff0c\u81ea\u89c9\u9664\u4e86\u6c14\u529b\u7a0d\u51cf\u4ee5\u5916\uff0c\u5df2\u5982\u5e73\u5e38\u3002\u5ffd\u7136\u60f3\u8d77\u8dc3\u5165\u5c71\u6c9f\u4e4b\u65f6\uff0c\u4f3c\u6709\u9a6c\u5636\u4eba\u8bed\u4e4b\u58f0\uff0c \u4e0d\u77e5\u73b0\u5728\u662f\u5426\u8fd8\u5728\u5c71\u6c9f\u5185\uff0c\u662f\u53cb\u662f\u654c\uff0c\u5f97\u770b\u5206\u660e\uff0c\u4e8e\u662f\u62d4\u51fa\u77ed\u5251\u8dc3\u4e0a\u5c71\u5761\uff0c\u53ea\u89c1\u5c71\u6c9f\u5185\u8349\u957f \u903e\u4eba\uff0c\u4e71\u8349\u4e4b\u4e2d\u6709\u8f86\u7834\u70c2\u7684\u8f66\u5b50\uff0c\u6768\u4e91\u806a\u4f0f\u5730\u9759\u542c\u3002\u5ffd\u542c\u5f97\u6709\u4e00\u4e2a\u6781\u719f\u7684\u58f0\u97f3\u558a\u9053\uff0c\u201c\u4f60\u522b \u8fd1\u6211\uff01\u201d\u6768\u4e91\u806a\u5927\u5403\u4e00\u60ca\uff0c\u8fd9\u53ef\u4e0d\u6b63\u662f\u7eb3\u5170\u660e\u6167\u7684\u58f0\u97f3\uff01 \u3000\u3000\u6768\u4e91\u806a\u6025\u5fd9\u8dc3\u51fa\uff0c\u53ea\u89c1\u90a3\u8f86\u7834\u8f66\u4e4b\u65c1\uff0c\u6709\u4e24\u4e2a\u5f6a\u5f62\u5927\u6c49\uff0c\u5a01\u5413\u7740\u8f66\u4e0a\u7684\u5c11\u5973\u3002\u6768\u4e91\u806a\u5fc3 \u60f3\uff1a\u201c\u7eb3\u5170\u660e\u6167\u7684\u6b66\u529f\u4e5f\u975e\u6cdb\u6cdb\uff0c\u5982\u4f55\u4f1a\u7ed9\u522b\u4eba\u5a01\u80c1\uff0c\u83ab\u975e\u4e5f\u50cf\u6211\u4e00\u6837\u53d7\u4e86\u91cd\u4f24\uff01\u6b63\u7591\u60d1 \u95f4\uff0c\u53c8\u542c\u524d\u9762\u7684\u5927\u6c49\u53eb\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u59d1\u5a18\uff0c\u771f\u4e0d\u8bc6\u597d\u6b79\uff0c\u4f60\u5df2\u7ecf\u662f\u6211\u4eec\u7684\u4fd8\u864f\u4e86\uff0c\u5f97\u542c\u6211\u4eec \u5904\u7f6e\uff0c\u6211\u4eec\u4e00\u4e0d\u6740\u4f60\uff0c\u4e8c\u4e0d\u6253\u4f60\uff0c\u4f60\u8fd8\u53eb\u56b7\u4ec0\u4e48\uff1f\u201d\u7eb3\u5170\u660e\u6167\u53eb\u9053\uff1a\u201c\u8c01\u8fc7\u6765\uff0c\u6211\u5c31\u662f\u4e00 \u5251\uff0c\u4f60\u522b\u77a7\u6211\u4e0d\u80fd\u8d70\u52a8\uff0c\u4f60\u6562\u8d70\u8fd1\u6765\uff0c\u6211\u4e0d\u628a\u4f60\u6740\u6389\u624d\u602a\uff01\u201d\u4e24\u6761\u5927\u6c49\u54c8\u54c8\u5927\u7b11\uff0c\u8bf4\u9053\uff1a \u201c\u771f\u77a7\u4e0d\u51fa\uff0c\u4f60\u8fd9\u5c0f\u59d1\u5a18\u53e3\u6c14\u597d\u5927\uff01\u201d \u3000\u3000\u6768\u4e91\u806a\u4e00\u63a0\u800c\u524d\uff0c\u53eb\u9053\uff1a\u201c\u6162\u7740\uff01\u201d\u4e24\u6761\u5927\u6c49\u8dc3\u524d\u6570\u6b65\uff0c\u8fce\u4e86\u4e0a\u6765\uff0c\u559d\u9053\uff0c\u201c\u4f60\u662f \u8c01\uff1f\u201d\u6768\u4e91\u806a\u4e00\u770b\u8fd9\u4e24\u4e2a\u4eba\u8840\u67d3\u6218\u888d\uff0c\u7adf\u662f\u7ef4\u4eba\u88c5\u675f\uff0c\u6025\u5fd9\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u662f\u54ea\u4e2a\u90e8\u843d\u7684\uff1f\u53ef \u8ba4\u5f97\u98de\u7ea2\u5dfe\u5417\uff1f\u524d\u9762\u90a3\u6761\u5927\u6c49\u770b\u89c1\u6768\u4e91\u806a\u7684\u88c5\u675f\uff0c\u4e5f\u4f3c\u4e4e\u5403\u4e86\u4e00\u60ca\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u662f\u98de\u7ea2\u5dfe\u7684 \u90e8\u4e0b\u5417\uff1f\u201d\u6768\u4e91\u806a\u70b9\u4e86\u70b9\u5934\u3002\u90a3\u4e3a\u9996\u7684\u5927\u6c49\u9053\uff1a\u201c\u6211\u4eec\u662f\u5580\u8fbe\u5c14\u90e8\u843d\u7684\uff0c\u6211\u77e5\u9053\u98de\u7ea2\u5dfe\u505a\u4e86 \u5357\u7586\u5404\u90e8\u7684\u76df\u4e3b\uff0c\u53ea\u662f\u524d\u665a\u7684\u8349\u539f\u805a\u96c6\uff0c\u6211\u4eec\u90e8\u843d\u5e76\u6ca1\u6709\u53c2\u52a0\u3002\u201d\u6768\u4e91\u806a\u9053\uff1a\u201c\u65e2\u7136\u4f60\u4eec\u90fd \u662f\u5357\u7586\u5404\u90e8\u843d\u7684\u6218\u571f\uff0c\u90a3\u4e48\u54b1\u4eec\u662f\u4e00\u5bb6\uff0c\u628a\u8fd9\u4e2a\u59d1\u5a18\u653e\u4e86\u5427\uff01\u201d \u3000\u3000\u7eb3\u5170\u660e\u6167\u8fd9\u65f6\u4e5f\u770b\u51fa\u662f\u8c01\uff0c\u4e0d\u4f4f\u7684\u7528\u6c49\u8bed\u53eb\u9053\uff1a\u201c\u6768\u5927\u4fa0\uff0c\u6768\u5927\u4fa0\uff01\u7ed9\u6211\u628a\u90a3\u4e24\u4eba\u5e72 \u6389\uff01\u201d\u8fd9\u4e24\u6761\u5927\u6c49\u542c\u4e0d\u61c2\u5979\u8bf4\u4ec0\u4e48\uff0c\u6025\u95ee\u6768\u4e91\u806a\u9053\uff1a\u201c\u600e\u4e48\u6837\uff1f\u4f60\u8ba4\u8bc6\u5979\uff1f\u4f60\u548c\u6e05\u519b\u5c06\u5b98\u662f \u670b\u53cb\uff1f\u201d\u6768\u4e91\u806a\u6447\u6447\u5934\u9053\uff1a\u201c\u6211\u662f\u98de\u7ea2\u5dfe\u7684\u670b\u53cb\uff0c\u4e5f\u662f\u8fd9\u4f4d\u59d1\u5a18\u7684\u670b\u53cb\u3002\u4f60\u4eec\u4e0d\u80fd\u7ea0\u7f20 \u5979\uff01\u201d \u3000\u3000\u4e3a\u9996\u7684\u5927\u6c49\u5ffd\u7136\u51b7\u7b11\u8d77\u6765\uff0c\u8bf4\u5230\uff1a\u201c\u4f60\u62ff\u98de\u7ea2\u5dfe\u5413\u6211\uff1f\u54fc\uff01\u4f60\u61c2\u4e0d\u61c2\u89c4\u77e9\uff01\u5979\u662f\u6211\u4eec\u4e24 \u4eba\u7684\u4fd8\u864f\uff0c\u5c31\u662f\u98de\u7ea2\u5dfe\u6765\uff1b\u6211\u4eec\u4e0d\u653e\u5979\u4e5f\u6ca1\u6709\u529e\u6cd5\uff01\u4f60\u662f\u4e0d\u662f\u4e5f\u77a7\u4e0a\u5979\u4e86\uff01\u8001\u5b9e\u544a\u8bc9\u4f60\uff0c\u6211 \u5c3c\u7ecf\u8981\u5979\u505a\u59bb\u5b50\u5566\uff01\u8fd9\u4f4d\u5144\u5f1f\u8981\u62ff\u4ed6\u7684\u8f66\u8f86\u5175\u5668\u3002\u4f60\u662f\u540e\u5934\u6765\u7684\uff0c\u6ca1\u6709\u4f60\u7684\u4efd\uff01\u201d\u6768\u4e91\u806a\u6014 \u4e86\u6014\uff0c\u8349\u539f\u4e0a\u7684\u6e38\u7267\u6c11\u65cf\uff0c\u4f3c\u524d\u4e3a\u4e86\u4e89\u7267\u573a\uff0c\u4e89\u6c34\u6e90\uff0c\u65f6\u5e38\u4e92\u76f8\u6597\u6bb4\uff0c\u5404\u90e8\u843d\u7684\u89c4\u77e9\uff0c\u6349\u5230 \u5bf9\u65b9\u7684\u4eba\uff0c\u5c31\u8feb\u4ed6\u505a\u5df1\u65b9\u7684\u5974\u96b6\uff0c\u8c01\u6349\u5230\u7684\u4fd8\u864f\u5f52\u8c01\u5904\u7f6e\u3002\u540e\u6765\u6e05\u5175\u6253\u4e86\u8fc7\u6765\uff0c\u5404\u90e8\u843d\u6bd4\u4ee5 \u524d\u56e2\u7ed3\u4e86\u8bb8\u591a\uff0c\u4e92\u76f8\u6b8b\u6740\u7684\u4e8b\u60c5\u5df2\u662f\u5c11\u4e4b\u53c8\u5c11\uff0c\u53ea\u662f\u8fd9\u79cd\u593a\u4fd8\u864f\uff0c\u4efb\u7531\u8c01\u5904\u7f6e\u7684\u89c4\u77e9\uff0c\u8fd8\u6ca1 \u6709\u660e\u767d\u5ba3\u5e03\u5e9f\u9664\uff0c\u73b0\u5728\u8fd9\u4e24\u6761\u5927\u6c49\u62ac\u51fa\u8349\u539f\u4e0a\u53e4\u8001\u76f8\u4f20\u7684\u89c4\u77e9\u6765\uff0c\u6768\u4e91\u806a\u4e00\u65f6\u95f4\u5012\u4e0d\u77e5\u5982\u4f55 \u4f5c\u7b54\u4e86\uff01 \u3000\u3000\u7eb3\u5170\u660e\u6167\u53c8\u53eb\u56b7\u9053\uff1a\u201c\u6768\u4e91\u806a\uff01\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u5e2e\u6211\u8d76\u8d70\u4ed6\u4eec\uff1f\u4f60\u8981\u548c\u4ed6\u4eec\u4e00\u9f50\u7b97\u8ba1\u6211 \u5417\uff1f\u201d\u6768\u4e91\u806a\u5927\u58f0\u5e94\u9053\uff0c\u201c\u6709\u6211\u5728\u8fd9\u91cc\uff0c\u4ed6\u4eec\u4e0d\u4f1a\u4f24\u5bb3\u4f60\u7684\uff0c\u4f60\u653e\u5fc3\u3002\u201d\u6d3b\u8fd8\u6765\u4e86\uff0c\u90a3\u4e24\u6761 \u5927\u6c49\u5df2\u8981\u5411\u8f66\u8f86\u6251\u53bb\u3002\u6768\u4e91\u806a\u53cc\u624b\u4e00\u4f38\uff0c\u8f7b\u8f7b\u5c06\u4ed6\u4eec\u62c9\u4e86\u8fc7\u6765\uff0c\u4e00\u6761\u5927\u6c49\u53cd\u624b\u4e00\u5200\uff0c\u9a82\u9053\uff1a \u201c\u4f60\u5e72\u4ec0\u4e48\uff1f\u201d\u6768\u4e91\u806a\u4f38\u6307\u4e00\u94b3\uff0c\u5c06\u5200\u80cc\u94b3\u7740\uff0c\u90a3\u5927\u6c49\u600e\u4e48\u7528\u529b\u4e5f\u5288\u4e0d\u8fc7\u53bb\u3002\u6768\u4e91\u806a\u9053\uff1a \u201c\u4e14\u6162\uff01\u4f60\u5c31\u770b\u6211\u7684\u9762\u5b50\u628a\u5979\u653e\u4e86\u597d\u4e0d\u597d\uff1f\u6211\u7ed9\u4f60\u4eec\u6bcf\u4eba\u5341\u5339\u9a6c\u3002\u201d\u53e6\u4e00\u6761\u5927\u6c49\u559d\u9053\uff1a\u201c\u4f60 \u662f\u8c01\uff1f\u4e3a\u4ec0\u4e48\u8981\u770b\u4f60\u7684\u9762\u5b50\uff1f\u201d\u6768\u4e91\u806a\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u6211\u662f\u4ece\u5317\u7586\u6765\u7684\uff0c\u6211\u53eb\u6768\u4e91\u806a\uff0c\u4f60\u4eec\u6ca1 \u6709\u542c\u8bf4\u8fc7\u5417\uff1f\u201d\u6768\u4e91\u806a\u4ee5\u4e3a\u4ed6\u4eec\u542c\u4e86\uff0c\u603b\u5f97\u7ed9\u70b9\u9762\u5b50\u3002\u54ea\u6599\u8fd9\u6761\u5927\u6c49\u5403\u4e86\u4e00\u60ca\u4e4b\u540e\uff0c\u5ffd\u7136\u54c8 \u54c8\u5927\u7b11\u8d77\u6765\u3002\u4e00\u6761\u5927\u6c49\u9053\uff1a\u201c\u4f60\u771f\u662f\u6768\u4e91\u806a\uff1f\u6768\u4e91\u806a\u66ff\u54c8\u8428\u514b\u4eba\u6253\u4e86\u90a3\u4e48\u591a\u5e74\u4ed7\uff0c\u66f4\u5e94\u8be5\u61c2 \u5f97\u89c4\u77e9\uff0c\u82e5\u662f\u8d4f\u7f5a\u4e0d\u660e\uff0c\u6211\u4eec\u5e72\u5417\u8fd8\u8981\u6253\u4ed7\uff1f\u201d\u53e6\u4e00\u4e2a\u5927\u6c49\u5374\u9053\uff1a\u201c\u4f60\u5192\u5145\u4ec0\u4e48\u6768\u4e91\u806a\uff0c\u6768 \u4e91\u806a\u600e\u4f1a\u53ea\u8eab\u5230\u8fd9\u91cc\u6765\uff1f\u54fc\uff0c\u6211\u770b\u4f60\u548c\u6e05\u519b\u519b\u5b98\u7684\u5973\u513f\u8fd9\u6837\u4eb2\u70ed\uff0c\u53fd\u54e9\u5495\u565c\u7684\u4e0d\u77e5\u8bb2\u6c49\u8bdd\u8fd8 \u662f\u6ee1\u6d32\u6d3b\uff0c\u5206\u660e\u662f\u591a\u5e74\u7684\u670b\u53cb\u3002\u54fc\uff0c\u4f60\u4e00\u5b9a\u662f\u5978\u7ec6\u3002\u201d\u6768\u4e91\u806a\u53c8\u6c14\u53c8\u6025\uff0c\u8fd9\u8349\u539f\u90e8\u843d\u5904\u7f6e\u4fd8 \u864f\u7684\u964b\u4e60\uff0c\u4ed6\u5f88\u4e45\u4ee5\u6765\uff0c\u5c31\u60f3\u5e2e\u52a9\u4ed6\u4eec\u9769\u9664\uff0c\u53ef\u662f\u8fd9\u79cd\u4e60\u4fd7\u3002\u4e0d\u662f\u77ed\u671f\u5185\u5c31\u53ef\u6539\u9769\u7684\uff0c\u5e76\u4e14 \u56e0\u4e3a\u5fd9\u4e8e\u6297\u6e05\uff0c\u6240\u4ee5\u4e00\u76f4\u6ca1\u6709\u63d0\u51fa\uff1a\u73b0\u5728\u60f3\u8bf4\u670d\u4ed6\u4eec\u4e24\u4e2a\uff0c\u6599\u60f3\u4e00\u65f6\u4e5f\u8bf4\u670d\u4e0d\u4e86\u3002\u90a3\u4e24\u6761\u5927 \u6c49\u8d81\u7740\u6768\u4e91\u806a\u4e00\u6115\uff0c\u6323\u8131\u4e86\u624b\uff0c\u53c8\u5411\u7eb3\u5170\u660e\u6167\u6251\u53bb\u3002\u6768\u4e91\u806a\u6ca1\u6cd5\uff0c\u968f\u624b\u62fe\u8d77\u4e24\u5757\u6ce5\u571f\uff0c\u5411\u524d \u4e00\u63b7\u3002\u4e24\u6761\u5927\u6c49\u201c\u54ce\u54df\u201d\u53eb\u56b7\uff0c\u90fd\u7ed9\u6253\u4e2d\u817f\u5f2f\u7684\u7a74\u9053\u3002\u8dea\u4e86\u4e0b\u53bb\u3002\u6768\u4e91\u806a\u6b63\u5f85\u4e0a\u524d\uff0c\u5ffd\u7136\u53c8 \u542c\u5f97\u4e24\u58f0\u60e8\u53eb\uff0c\u4e24\u6761\u5927\u6c49\u6eda\u5728\u5730\u4e0a\uff0c\u8273\u8840\u67d3\u7ea2\u4e86\u4e00\u5927\u7247\u6ce5\u571f\uff01\u539f\u6765\u7eb3\u5170\u660e\u6167\u89c1\u8fd9\u4e24\u4e2a\u4eba\u8dd1\u4e86 \u8fd1\u6765\uff0c\u6768\u4e91\u806a\u53c8\u4e0d\u62e6\u963b\uff0c\u53c8\u6c14\u53c8\u6012\uff0c\u7528\u529b\u63b7\u51fa\u4e24\u7247\u98de\u5200\uff0c\u90a3\u4e24\u4eba\u7ed9\u6768\u4e91\u806a\u6253\u4e2d\u4e86\u7a74\u9053\uff0c\u65e0\u6cd5 \u8eb2\u95ea\uff0c\u90fd\u7ed9\u624e\u8fdb\u4e86\u5fc3\u7a9d\u3002 \u3000\u3000\u6768\u4e91\u806a\u8d70\u4e86\u4e0a\u53bb\uff0c\u8bf4\u9053\uff0c\u201c\u660e\u6167\uff0c\u4f60\u600e\u4e48\u8fd9\u6837\u624b\u8fa3\u3002\u201d\u7eb3\u5170\u660e\u6167\u5df2\u54ed\u5f97\u5982\u5e26\u96e8\u68a8\u82b1\uff0c\u53eb \u9053\uff1a\u201c\u539f\u6765\u4f60\u7684\u5fc3\u80a0\u7adf\u7136\u8fd9\u6837\uff0c\u522b\u4eba\u8981\u628a\u6211\u62a2\u53bb\u6c61\u8fb1\uff0c\u4f60\u4e5f\u4e0d\u7406\uff0c\u5230\u5934\u6765\u8fd8\u602a\u6211\uff01\u201d\u6768\u4e91\u806a \u4e0d\u89c9\u5fc3\u8f6f\uff0c\u60f3\u9053\uff1a\u201c\u5979\u4e3a\u4e86\u4fdd\u62a4\u81ea\u5df1\uff0c\u602a\u4e0d\u5f97\u5979\u51fa\u624b\u6bd2\u8fa3\u3002\u201d\u8d70\u4e0a\u8f66\u4e0a\uff0c\u7528\u8863\u8896\u8f7b\u8f7b\u7ed3\u5979\u63e9 \u6cea\u3002\u53ea\u89c1\u5979\u5934\u53d1\u84ec\u677e\uff0c\u6ee1\u9762\u8840\u6c61\uff0c\u6025\u5fd9\u95ee\u9053\uff1a\u201c\u4f60\u53d7\u4f24\u4e86\uff1f\u201d \u3000\u3000\u7eb3\u5170\u660e\u6167\u8fd9\u65f6\u5982\u9047\u4eb2\u4eba\uff0c\u8eab\u4e8e\u5ffd\u7136\u5012\u4e86\u4e0b\u53bb\uff0c\u4f0f\u5728\u6768\u4e91\u806a\u7684\u81c2\u5f2f\u4e0a\uff0c\u8bf4\u9053\uff1a\u201c\u55ef\uff0c\u662f\u53d7 \u4f24\u4e86\uff0c\u6211\u7684\u80a9\u5934\u9ebb\u8fa3\u8fa3\u7684\uff0c\u4f60\u8d76\u5feb\u7ed9\u6211\u77a7\uff0c\u662f\u4e0d\u662f\u4e2d\u4e86\u6709\u6bd2\u6697\u5668\uff1f\u201d \u3000\u3000\u6768\u4e91\u806a\u5230\u6b64\u5883\u5730\uff0c\u6ca1\u6cd5\u4e0d\u987e\uff0c\u7d22\u6027\u7ed9\u5979\u63e9\u5e72\u4e86\u9762\u4e0a\u7684\u8840\u8ff9\uff0c\u4e00\u770b\u5230\u6ca1\u6709\u53d7\u7740\u5200\u5203\u4f24\uff0c\u90a3 \u5f20\u9762\u5b54\u8fd8\u662f\u5982\u7f8e\u7389\u4e00\u822c\uff0c\u602a\u4fca\u4fcf\u52a8\u4eba\u3002\u5fc3\u5ff5\u4e00\u52a8\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u6740\u4e86\u4eba\u4e86\uff1f\u6cbe\u4e86\u90a3\u4e48\u591a\u7684\u8840 \u8ff9\uff1f\u201d\u7eb3\u5170\u660e\u6167\u9053\uff1a\u201c\u6211\u8981\u9003\u547d\u561b\uff0c\u4e0d\u6740\u4eba\uff0c\u4eba\u5c31\u6740\u6211\u4e86\uff0c\u600e\u4e48\u6837\uff0c\u4f60\u8fd8\u4e0d\u7ed9\u6211\u770b\u770b\u80a9\u5934\uff0c \u6211\u4e2d\u4e86\u5973\u9b54\u5934\u7684\u6bd2\u6697\u5668\u5566\uff01\u201d\u6768\u4e91\u806a\u8fd9\u65f6\u5fc3\u4e71\u5982\u9ebb\uff0c\u7eb3\u5170\u660e\u6167\u6740\u7684\u4eba\u4e00\u5b9a\u662f\u5357\u7586\u7267\u6c11\u7684\u6218 \u58eb\uff0c\u5979\u8fd9\u5c82\u4e0d\u662f\u81ea\u5df1\u7684\u654c\u4eba\uff1f\u4f46\u5979\u66fe\u6551\u8fc7\u81ea\u5df1\u7684\u547d\uff0c\u800c\u773c\u524d\u7684\u5979\uff0c\u53c8\u662f\u8fd9\u6837\u4e00\u7247\u5b9b\u8f6c\u53ef\u601c\u7684 \u6837\u5b50\uff0c\u53c8\u60f3\u81ea\u5df1\u5728\u4e71\u519b\u4e4b\u4e2d\u53ae\u6740\uff0c\u4e5f\u96be\u4fdd\u4e0d\u4f1a\u4f24\u4eba\uff1a\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u5e2e\u4f60\u7684\u7236\u4eb2\u51fa \u6765\u6253\u4ed7\u5417\uff1f\u201d\u7eb3\u5170\u660e\u6167\u6447\u4e86\u6447\u5934\uff0c\u5ffd\u53c8\u53eb\u9053\uff1a\u201c\u4f60\u5148\u7ed9\u6211\u6599\u7406\u597d\u4e0d\u597d\uff1b\u4f60\u4e00\u70b9\u4e5f\u4e0d\u75bc\u6211\uff01\u6bd2 \u6c14\u6563\u5f00\uff0c\u6211\u5c31\u8981\u6b7b\u5566\uff01\u201d \u3000\u3000\u6768\u4e91\u806a\u4f4e\u4e0b\u5934\u53bb\uff0c\u770b\u4e86\u4e00\u4f1a\uff0c\u4e0d\u89c1\u6709\u8840\uff0c\u5c06\u77ed\u5251\u628a\u5979\u7684\u80a9\u5934\u8863\u670d\u8f7b\u8f7b\u6311\u4e86\u4e00\u9053\u53e3\u5b50\uff0c\u53ea \u89c1\u4ed6\u90a3\u80a9\u5934\u9ed1\u80bf\uff0c\u53eb\u9053\uff1a\u201c\u554a\uff01\u4f60\u539f\u6765\u771f\u7684\u4e2d\u4e86\u6bd2\u9488\u4e86\uff01\u201d\u6025\u5fd9\u6478\u51fa\u4e24\u9897\u201c\u78a7\u7075\u4e39\u201d\u7ed9\u5979\u5403 \u4e0b\uff0c\u95f4\u9053\uff1a\u201c\u4f60\u5fcd\u5f97\u4f4f\u75bc\u5417\uff1f\u201d\u7eb3\u5170\u660e\u6167\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u201d\u6768\u4e91\u806a\u9053\uff1a\u201c\u6bd2\u9488\u8981\u5438\u94c1\u77f3\u624d\u80fd\u5438 \u5f97\u51fa\u6765\u3002\u8fd9\u91cc\u6ca1\u6cd5\u53bb\u627e\uff1a\u8981\u6551\u6cbb\u53ea\u6709\u628a\u5b83\u62d4\u51fa\u6765\uff01\u201d\u7eb3\u5170\u660e\u6167\u9053\uff1a\u201c\u4f60\u7ed9\u6211\u62d4\u5427\uff0c\u6211\u5fcd\u5f97\u4f4f \u75bc\u7684\uff01\u201d\u6768\u4e91\u806a\u7528\u5de6\u624b\u5b9a\u4f4f\u5979\u7684\u80a9\u5934\uff0c\u4fef\u9996\u4e0b\u53bb\uff0c\u53ea\u89c9\u5f97\u9999\u6c14\u88ad\u4eba\uff0c\u624b\u6307\u6240\u6309\u6709\u7684\u808c\u8089\u5982\u6709 \u78c1\u529b\uff0c\u8fd9\u662f\u4ed6\u7b2c\u4e00\u6b21\u548c\u5973\u4eba\u8fd9\u6837\u4eb2\u8fd1\u3002\u5fc3\u4e2d\u4e00\u8361\uff0c\u6025\u5fd9\u7528\u77ed\u5251\u8f7b\u8f7b\u628a\u5979\u7684\u8089\u525c\u5f00\uff0c\u627e\u7740\u4e86\u9488 \u5c16\uff1b\u8fd0\u5185\u529b\u7528\u6307\u4e00\u94b3\uff0c\u94b3\u4e86\u51fa\u6765\uff0c\u4e00\u8fde\u62e8\u4e09\u53e3\u94f6\u9488\uff0c\u5e76\u4ee3\u5979\u6324\u51fa\u6bd2\u8840\uff0c\u53c8\u628a\u5185\u8863\u6495\u5f00\uff0c\u6495\u6210 \u5e03\u6761\u66ff\u5979\u5b54\u597d\u4f24\u53e3\uff0c\u8bf4\u9053\uff0c\u201c\u4f60\u8eba\u4e0b\u6765\u4f11\u606f\u5427\uff0c\u8fd9\u53ef\u5c31\u597d\u4e86\uff01\u201d \u3000\u3000\u6768\u4e91\u806a\u62d4\u51fa\u4e09\u53e3\u94f6\u9488\uff0c\u60ca\u5f02\u4e0d\u5df2\u3002\u8fd9\u79cd\u7ec6\u5c0f\u6709\u6bd2\u7684\u6697\u5668\uff0c\u82e5\u975e\u5185\u529f\u6df1\u6e5b\uff0c\u65e0\u6cd5\u4f7f\u7528\uff0c\u5979 \u5230\u5e95\u78b0\u5230\u4ec0\u4e48\u4eba\uff1f\u6b63\u5f85\u95ee\u65f6\uff0c\u7eb3\u5170\u660e\u6167\u5df2\u5148\u81ea\u8bf4\u9053\uff1a\u201c\u6211\u672c\u6765\u662f\u8ddf\u7740\u7236\u4eb2\uff0c\u968f\u519b\u79fb\u52a8\uff0c\u60f3\u8f6c \u56de\u4f0a\u7281\u7684\uff0c\u4e2d\u9014\u770b\u89c1\u70fd\u706b\uff0c\u7236\u4eb2\u5e26\u5175\u8d76\u6765\uff0c\u6211\u90a3\u80fd\u4e0d\u968f\u7740\u6765\u5462\uff01\u90a3\u6599\u4e00\u5230\u8fd9\u91cc\uff0c\u5c31\u78b0\u7740\u5927\u6df7 \u6218\uff0c\u6211\u649e\u7740\u56db\u540d\u9a91\u58eb\u8981\u6211\u7684\u8f66\uff0c\u6211\u62d4\u5251\u4f24\u4e86\u4e24\u4eba\uff0c\u6709\u4e00\u5339\u9a6c\u4e0a\u7684\u4e00\u4e2a\u5973\u4eba\uff0c\u5ffd\u7136\u628a\u624b\u4e00\u9762\uff0c \u6211\u5c31\u53d7\u4e86\u4f24\u5566\uff01\u201d\u6768\u4e91\u806a\u9762\u8272\u5ffd\u53d8\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u98de\u7ea2\u5dfe\uff01\u201d \u3000\u3000----------- \u3000\u3000\u98ce\u4e91\u9601 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u585e\u5916\u5947\u4fa0\u4f20\u300b\u2014\u2014\u7b2c\u5341\u56db\u56de\u3000\u8349\u539f\u5fc3\u76df \u6881\u7fbd\u751f\u300a\u585e\u5916\u5947\u4fa0\u4f20\u300b \u7b2c\u5341\u56db\u56de\u3000\u8349\u539f\u5fc3\u76df \u3000\u3000\u201c\u98de\u7ea2\u5dfe\uff01\u201d\u7eb3\u5170\u660e\u6167\u4e5f\u558a\u4e86\u51fa\u6765\uff0c\u60ca\u5f02\u5730\u671b\u7740\u6768\u4e91\u806a\u53eb\u9053\uff1a\u201c\u4f60\u8ba4\u5f97\u98de\u7ea2\u5dfe\u4e48\uff1f\u5927 \u54e5\uff0c\u4f60\u66ff\u6211\u62a5\u4ec7\u3002\u201d\u5979\u7684\u5934\u7d22\u6027\u6795\u5728\u6768\u4e91\u806a\u7684\u819d\u4e0a\uff0c\u79f0\u547c\u4e5f\u7531\u201c\u5927\u4fa0\u201d\u6539\u6210\u5927\u54e5\uff0c\u4e00\u534a\u6492\u5a07 \u4e00\u534a\u55d4\u6012\u5730\u53eb\u9053\u3002\u6768\u4e91\u806a\u75db\u82e6\u7684\u201c\u55ef\u201d\u4e86\u4e00\u58f0\uff0c\u8f7b\u8f7b\u5730\u5c06\u5979\u6276\u8d77\uff0c\u8bf4\u9053\uff1a\u201c\u660e\u6167\uff0c\u8fd9\u4ec7\u62a5\u4e0d \u5f97\u54ea\uff01\u201d\u7eb3\u5170\u660e\u6167\u677f\u7740\u9762\u5b54\u95ee\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u54fc\u3002\u6211\u77e5\u9053\u4e86\uff0c\u5927\u54e5\u7231\u4e0a\u4e86\u8fd9\u8349\u539f\u4e0a\u7684\u5973\u9b54\u5934 \u5566\uff01\u201d \u3000\u3000\u6768\u4e91\u806a\u5ffd\u5730\u8f7b\u8f7b\u5730\u6273\u7740\u5979\u7684\u80a9\u5934\uff0c\u4e24\u53ea\u773c\u775b\uff0c\u5982\u5bd2\u51b0\u5229\u7bad\u4e00\u6837\u5bf9\u7740\u5979\u7684\u773c\u775b\uff0c\u7528\u4e00\u79cd\u6025 \u4fc3\u6c89\u91cd\u7684\u58f0\u8c03\u95ee\u9053\uff1a\u201c\u660e\u6167\uff0c\u6211\u4eec\u8bf4\u6b63\u7ecf\u7684\u3002\u4f60\u8bf4\uff0c\u5728\u4f60\u7684\u773c\u4e2d\uff0c\u98de\u7ea2\u5dfe\u662f\u4ec0\u4e48\u4eba\uff0c\u5979\u662f\u5973 \u9b54\u5934\uff1f\u662f\u4f60\u7684\u654c\u4eba\uff1f\u5982\u679c\u4e0d\u662f\u5979\u7528\u6bd2\u9488\u5c04\u4f24\u4e86\u4f60\uff0c\u4f60\u4e5f\u6068\u5979\uff0c\u56e0\u4e3a\u5979\u548c\u4f60\u7684\u65cf\u4eba\u4e3a\u654c\uff0c\u56e0\u4e3a \u4f60\u7684\u7236\u4eb2\u7ecf\u5e38\u63d0\u8d77\u5979\uff0c\u6559\u4f60\u6068\u5979\uff0c\u628a\u5979\u8bf4\u6210\u5973\u9b54\u5934\uff0c\u662f\u5417\uff1f\u201d\u6768\u4e91\u806a\u4e00\u53e3\u6c14\u8bf4\u4e86\u8fd9\u4e48\u591a\u6d3b\uff0c \u6000\u7740\u6124\u6fc0\u7684\u611f\u60c5\uff0c\u53c8\u6000\u7740\u6218\u6817\u7684\u611f\u60c5\uff0c\u671f\u5f85\u7740\u5979\u7684\u56de\u7b54\u3002\u7eb3\u5170\u660e\u6167\u7684\u6837\u5b50\u662f\u8fd9\u6837\u7684\u7231\u5a07\uff0c\u6768 \u4e91\u806a\u5728\u5979\u7684\u8eab\u65c1\u3002\u597d\u50cf\u611f\u5230\u4e00\u80a1\u6e29\u6696\uff1b\u7136\u800c\u7531\u5979\u7684\u8bdd\u8bed\u6240\u5e26\u7684\u9634\u5f71\uff0c\u53c8\u4f7f\u4ed6\u611f\u5230\u5bd2\u51b7\uff0c\u8fd9 \u65f6\uff0c\u4ed6\u7684\u5fc3\u91cc\u5df2\u7ecf\u6709\u4e86\u4e2a\u51b3\u5b9a\uff0c\u5982\u679c\u5979\u662f\u7ad9\u5728\u5979\u7236\u4eb2\u90a3\u8fb9\uff0c\u56e0\u4e3a\u98de\u7ea2\u5dfe\u662f\u8349\u539f\u7684\u5973\u82f1\u96c4\u800c\u6068 \u5979\u7684\u8bdd\uff0c\u90a3\u4e48\u5979\u5c31\u662f\u4ed6\u7684\u654c\u4eba\uff0c\u4ed6\u8981\u628a\u5979\u6740\u6b7b\uff01\u6700\u5c11\u4e5f\u4e0d\u7406\u5979\u3002\u6b63\u662f\u8fd9\u4e2a\u5ff5\u5934\uff0c\u4f7f\u4ed6\u7684\u8bed\u97f3 \u611f\u5230\u98a4\u6296\uff0c\u8bed\u58f0\u4e5f\u9707\u60ca\u4e86\u3002 \u3000\u3000\u7eb3\u5170\u660e\u6167\u5947\u5f02\u5730\u770b\u7740\u6768\u4e91\u806a\uff0c\u5979\u4e0d\u77e5\u9053\u6768\u4e91\u806a\u5fc3\u91cc\u7684\u5ff5\u5934\uff0c\u53ea\u662f\u5979\u611f\u5230\u6c14\u6c1b\u7684\u6c89\u91cd\uff1b\u5979 \u89c9\u5bdf\u5230\u6768\u4e91\u806a\u7684\u8bdd\uff0c\u4f3c\u4e4e\u5df2\u8d85\u51fa\u7231\u60c5\u4e4b\u5916\u4e86\uff0c\u4ed6\u7684\u8bdd\u4e0d\u662f\u4e00\u79cd\u513f\u5973\u4e4b\u60c5\uff1a\u800c\u597d\u50cf\u662f\u4ed6\u5df2\u5949\u732e \u7ed9\u4e00\u79cd\u795e\u5723\u7684\u4e1c\u897f\uff0c\u98de\u7ea2\u5dfe\u4e5f\u662f\u4e00\u6837\uff0c\u6240\u4ee5\u4ed6\u548c\u98de\u7ea2\u5dfe\u7684\u60c5\u8c0a\u662f\u7262\u4e0d\u53ef\u7834\u4e86\uff0c\u7eb3\u5170\u660e\u6167\u611f\u5230 \u5f02\u6837\u7684\u60b2\u54c0\uff0c\u5979\u4f4e\u58f0\u7684\u9053\uff1a\u201c\u4f60\u542c\u6211\u8bf4\uff0c\u6211\u538c\u6076\u6218\u4e89\uff0c\u4f60\u4e5f\u538c\u6076\u6218\u4e89\uff0c\u4f60\u5bf9\u6211\u8fd9\u6837\u8bf4\u8fc7\u7684\uff0c \u662f\u5417\uff1f\u4f46\u662f\u6211\u548c\u4f60\u538c\u6076\u6218\u4e89\uff0c\u6218\u4e89\u5374\u504f\u504f\u628a\u6211\u4eec\u5377\u8fdb\u53bb\u4e86\uff0c\u5982\u679c\u6709\u547d\u8fd0\u7684\u6d3b\uff0c\u8fd9\u6837\u6211\u4eec\u5c31\u662f \u4e00\u4e2a\u547d\u5b9a\u7684\u6076\u8fd0\u3002 \u3000\u3000\u201c\u6211\u4e0d\u8ba4\u8bc6\u98de\u7ea2\u5dfe\u3002\u4f46\u81ea\u4ece\u6211\u6765\u5230\u8fd9\u513f\uff0c\u6211\u5c31\u5e38\u542c\u4eba\u63d0\u8d77\u5979\u7684\u540d\u5b57\u3002\u662f\u7684\uff0c\u4f60\u8bf4\u7684\u4e0d \u9519\uff0c\u6211\u7684\u7236\u4eb2\uff0c\u6211\u7684\u65cf\u4eba\uff0c\u90fd\u628a\u5979\u8bf4\u6210\u5973\u9b54\uff0c\u6740\u4eba\u5982\u5272\u8349\u7684\u6076\u9b54\uff0c\u6211\u5bf9\u5979\u4e5f\u611f\u5230\u5bb3\u6015\u7684\uff0c\u53ef \u662f\u6211\u4e5f\u5e76\u4e0d\u5168\u4fe1\u6211\u7684\u7236\u4eb2\u7684\u8bdd\uff0c\u6211\u77e5\u9053\u6211\u4eec\u6253\u8fdb\u6765\u65f6\uff0c\u4e5f\u6740\u4e86\u4e0d\u5c11\u7684\u4eba\uff0c\u8fd9\u662f\u6218\u4e89\u561b\uff0c\u6211\u4eec \u6740\u4ed6\u4eec\uff0c\u4ed6\u4eec\u6740\u6211\u4eec\uff0c\u6211\u4eec\u628a\u98de\u7ea2\u5dfe\u79f0\u4e3a\u5973\u9b54\u5934\uff0c\u7109\u77e5\u4ed6\u4eec\u4e0d\u5c06\u6211\u7684\u7236\u4eb2\u79f0\u4e3a\u9b54\u5934\u3002\u201d \u3000\u3000\u201c\u6211\u6709\u65f6\u751a\u81f3\u8fd9\u6837\u60f3\uff0c\u4e00\u4e2a\u50cf\u98de\u7ea2\u5dfe\u90a3\u6837\u7684\u5c11\u5973\uff0c\u8de8\u7740\u6218\u9a6c\uff0c\u5728\u8349\u539f\u4e0a\u98de\u9a70\uff0c\u88ab\u5979\u7684\u65cf \u4eba\u5c0a\u5d07\uff0c\u88ab\u6211\u4eec\u7684\u4eba\u5492\u9a82\uff0c\u4e0d\u7ba1\u600e\u6837\uff0c\u5979\u90fd\u662f\u4e00\u4e2a\u82f1\u96c4\uff0c\u8001\u5b9e\u8bf4\u6211\u4e5f\u66fe\u5077\u5077\u7684\u7fa1\u6155\u8fc7\u5979\u54e9\uff01\u201d \u3000\u3000\u201c\u6211\u4e0d\u8ba4\u8bc6\u98de\u7ea2\u5dfe\uff0c\u76f4\u5230\u6211\u53d7\u5230\u5979\u7684\u6bd2\u9488\u5c04\u4f24\u7684\u65f6\u5019\uff0c\u6211\u731c\uff0c\u8fd9\u6837\u7cbe\u901a\u6b66\u827a\u7684\u5973\u5b50\uff0c\u4e00 \u5b9a\u662f\u98de\u7ea2\u5dfe\u3002\u5f53\u9488\u6bd2\u4ee4\u6211\u975e\u5e38\u75db\u82e6\u7684\u65f6\u5019\uff0c\u6211\u6068\u5979\uff0c\u6068\u5979\u51fa\u624b\u8fd9\u6837\u6bd2\u8fa3\u3002\u53e6\u5916\uff09\u6211\u8fd8\u6709\u6068\u5979 \u7684\uff0c\u5927\u54e5\uff0c\u6211\u4e0d\u8bf4\u4e86\uff0c\u6211\u77e5\u9053\u4f60\u4e00\u5b9a\u662f\u5979\u7684\u597d\u670b\u53cb\uff01\u201d\u7eb3\u5170\u660e\u6167\u5ffd\u7136\u5a07\u7f9e\u7684\u4f4e\u4e0b\u4e86\u5934\uff0c\u773c\u89c1 \u6709\u7740\u4e00\u79cd\u611f\u4eba\u5fc3\u9b44\u7684\u5149\u5f69\uff01 \u3000\u3000\u6768\u4e91\u806a\u677e\u4e86\u53e3\u6c14\uff0c\u662f\u7684\uff0c\u7eb3\u5170\u660e\u6167\u662f\u6068\u98de\u7ea2\u5dfe\u7684\uff0c\u53ef\u662f\u8fd9\u79cd\u6068\u7684\u6027\u8d28\u6bd4\u4ed6\u6240\u5bb3\u6015\u7684\u8981\u8f7b \u5f97\u591a\uff0c\u8f7b\u5f97\u591a\uff01\u5979\u7684\u6068\u8ddf\u5979\u7236\u4eb2\u7684\u6068\u662f\u5b8c\u5168\u4e0d\u540c\u7684\uff01\uff0c\u5979\u7684\u8bf4\u8bdd\u91cc\u4e5f\u6709\u7cca\u6d82\u7684\u5730\u65b9\uff0c\u5979\u628a\u6218 \u4e89\u4e2d\u7684\u53cc\u65b9\u540c\u4e00\u770b\u5f85\uff0c\u201c\u8fd9\u662f\u6218\u4e89\u561b\uff0c\u6211\u4eec\u6740\u4ed6\u4eec\uff0c\u4ed6\u4eec\u6740\u6211\u4eec\uff01\u201d\u597d\u50cf\u8fd9\u91cc\u9762\u6ca1\u6709\u662f\u975e\u9ed1 \u767d\u3002\u8fd9\u6837\u662f\u4e0d\u5bf9\u7684\uff0c\u4e0d\u5bf9\u7684\uff0c\u6768\u4e91\u806a\u5728\u5fc3\u91cc\u5934\u91cd\u91cd\u7684\u8bf4\u9053\uff1a\u201c\u4e0d\u5bf9\u7684\uff01\u201d\u6768\u4e91\u806a\u6709\u8bb8\u591a\u8bdd\u60f3 \u5bf9\u5979\u8bf4\uff0c\u60f3\u6559\u5979\u600e\u6837\u5206\u8fa8\u662f\u975e\uff0c\u53ef\u662f\u4ed6\u77e5\u9053\u4e9b\u9053\u7406\u4e0d\u662f\u5979\u4e00\u4e0b\u5b50\u80fd\u542c\u5f97\u8fdb\u53bb\u7684\u3002\u53e6\u4e00\u65b9\u9762\uff0c \u4ed6\u89c9\u5f97\u5728\u6ee1\u6d32\u4eba\u4e2d\uff0c\u6709\u8fd9\u6837\u7684\u4e00\u4e2a\u5973\u5b50\uff0c\u5df2\u7ecf\u662f\u4e00\u4e2a\u5947\u5f02\uff0c\u4ed6\u611f\u5230\uff0c\u4ed6\u548c\u5979\u4e4b\u95f4\uff0c\u5fc3\u7075\u4e0a\u4e5f \u6709\u4e92\u901a\u7684\u5730\u65b9\uff0c\u8fd9\u662f\u4e00\u79cd\u5947\u5f02\u7684\u611f\u60c5\uff0c\u548c\u4ec7\u4eba\u7684\u5973\u513f\uff0c\u5728\u5fc3\u7075\u4e0a\u4e92\u76f8\u611f\u5e94\u3002 \u3000\u3000\u6768\u4e91\u806a\u629a\u7740\u7eb3\u5170\u660e\u6167\u7684\u5934\u53d1\uff0c\u8f7b\u8f7b\u4ed6\u8bf4\u9053\uff1a\u201c\u660e\u6167\uff0c\u6211\u4e00\u70b9\u4e5f\u4e0d\u602a\u4f60\u4e86\uff0c\u4f60\u4e5f\u4e0d\u8981\u6068\u98de \u7ea2\u5dfe\u4e86\uff0c\u4f60\u7ed9\u5979\u7684\u6bd2\u9488\u5c04\u4f24\uff0c\u602a\u5979\u624b\u8fa3\uff0c\u53ef\u662f\u4f60\u77e5\u9053\uff0c\u6211\u4e0d\u662f\u4e5f\u7ed9\u6bd2\u7bad\u5c04\u4f24\uff0c\u51e0\u4e4e\u4e27\u547d\u4e86 \u5417\uff1f\u4f60\u53eb\u6211\u66ff\u4f60\u62a5\u4ec7\uff0c\u5982\u679c\u6211\u4e5f\u53eb\u4f60\u66ff\u6211\u62a5\u4ec7\uff0c\u4f60\u4f1a\u600e\u6837\u5462\uff1f\u201d \u3000\u3000\u7eb3\u5170\u660e\u6167\u6485\u8d77\u5634\u5df4\u9053\uff1a\u201c\u6211\u7684\u672c\u9886\u867d\u7136\u6bd4\u4f60\u5dee\u5f97\u591a\uff0c\u4f46\u4f60\u53c8\u600e\u77e5\u6211\u4e0d\u80fd\u7ed9\u4f60\u62a5\u4ec7\u5462\uff0c\u544a \u8bc9\u6211\uff0c\u8c01\u62ff\u6bd2\u7bad\u5c04\u4f24\u4f60\uff01\u201d\u6768\u4e91\u806a\u51b7\u51b7\u7684\u8bf4\u9053\uff1a\u201c\u4f60\u7684\u7236\u4eb2\uff01\u201d \u3000\u3000\u7eb3\u5170\u660e\u6167\u597d\u50cf\u7ed9\u96f7\u51fb\u7740\u4e00\u6837\uff0c\u9762\u8272\u4e00\u4e0b\u5b50\u53d8\u5f97\u975e\u5e38\u82cd\u767d\u3002\u8df3\u4e86\u8d77\u6765\uff0c\u53c8\u9893\u7136\u7684\u5012\u4e0b\u53bb\uff1a \u6768\u4e91\u806a\u6276\u7740\u5979\u95ee\u9053\uff1a\u201c\u600e\u4e48\u5566\uff1f\u201d\u7eb3\u5170\u660e\u6167\u95ed\u7740\u773c\u775b\u75db\u82e6\u7684\u9053\uff1a\u201c\u4f60\u4e00\u5b9a\u6068\u6b7b\u6211\u4e86\uff01\u201d\u6768\u4e91 \u806a\u6025\u5fd9\u8bf4\u8fd9\uff1a\u201c\u6211\u4e3a\u4ec0\u4e48\u8981\u6068\u4f60\uff0c\u4f60\u53c8\u4e0d\u662f\u4f60\u7684\u7236\u4eb2\uff01\u201d \u3000\u3000\u53ef\u662f\u7eb3\u5170\u660e\u6167\u4e0d\u80fd\u7406\u89e3\u4ed6\u7684\u611f\u60c5\uff0c\u5979\u5fc3\u91cc\u7ffb\u817e\u6c79\u6d8c\u7684\u6ce2\u6d6a\u3002\u5979\u81ea\u4ece\u89c1\u4e86\u6768\u4e91\u806a\u4ee5\u540e\uff0c\u5c31 \u6df1\u6df1\u4e3a\u4ed6\u7684\u82f1\u96c4\u6c14\u6982\u6240\u5438\u5f15\u4e86\uff0c\u79bb\u5f00\u4e4b\u540e\uff0c\u5979\u7684\u5fc3\u91cc\u597d\u50cf\u591a\u4e86\u4e00\u4e9b\u4ec0\u4e48\u4e1c\u897f\u4eba\u53c8\u597d\u50cf\u5c11\u4e86\u4e9b \u4ec0\u4e48\u4e1c\u897f\u3002\u5979\u5728\u68a6\u91cc\u66fe\u597d\u591a\u6b21\u89c1\u8fc7\u4ed6\uff0c\u60f3\u4e0d\u5230\u73b0\u5728\u5c31\u5728\u4ed6\u7684\u8eab\u65c1\u4e86\u3002\u800c\u4e14\u8fd8\u6795\u5728\u4ed6\u7684\u819d\u76d6 \u4e0a\uff0c\u53ef\u662f\u6b64\u523b\uff0c\u5979\u6df1\u5207\u7684\u611f\u5230\uff1b\u5979\u548c\u6768\u4e91\u806a\u8ddd\u79bb\u5f97\u8fd9\u6837\u8fd1\uff0c\u5374\u53c8\u662f\u8fd9\u6837\u8fdc\uff01\u201c\u4ed6\u662f\u5c5e\u4e8e\u98de\u7ea2 \u5dfe\u7684\u3001\u4e0d\u662f\u6211\u7684\uff01\u201d\u8fd9\u79cd\u601d\u60f3\u50cf\u94c1\u9524\u4e00\u6837\u6572\u51fb\u7740\u5979\u7684\u8111\u888b\u3002\u50cf\u5229\u9488\u4e00\u6837\uff0c\u63d2\u523a\u5979\u7684\u5fc3\u3002\u6bd4\u98de \u7ea2\u5dfe\u7684\u6bd2\u9488\u66f4\u4ee4\u5979\u75db\u82e6\uff01 \u3000\u3000\u6768\u4e91\u806a\u5ffd\u7136\u770b\u7740\u5979\u50cf\u51cb\u8c22\u7684\u82b1\u4e00\u6837\u67af\u840e\u4e0b\u53bb\uff0c\u9762\u8272\u82cd\u767d\uff0c\u547c\u5438\u8feb\u4fc3\uff0c\u7528\u624b\u628a\u5979\u7684\u8109\u640f\uff0c \u53ea\u89c9\u5f97\u8df3\u52a8\u5f97\u5feb\u7684\u51fa\u5947\uff0c\u4ed6\u77a7\u89c1\u5979\u7684\u9762\u4e0a\u7684\u808c\u8089\u5728\u75c9\u631b\uff0c\u5fc3\u91cc\u5947\u602a\u9053\uff1a\u201c\u600e\u4e48\u6211\u5c06\u5979\u4e2d\u7684\u6bd2 \u9488\u62d4\u51fa\u6765\u4e86\uff0c\u5979\u53cd\u800c\u5ffd\u7136\u75c5\u7684\u8fd9\u6837\u5389\u5bb3\uff1f\u201d\u5e7d\u8c37\u91cc\u9759\u5bc2\u65e0\u58f0\uff0c\u53ea\u6709\u8fd1\u5904\u5bd2\u866b\u51c4\u53eb\u3001\u8fdc\u5904\u5c71\u8c37 \u545c\u54bd\u3002\u6768\u4e91\u806a\u5ffd\u7136\u611f\u5230\u4e00\u9635\u5bb3\u6015\uff0c\u4ed6\u518d\u638f\u51fa\u4e24\u7c92\u5929\u5c71\u96ea\u83b2\u914d\u6210\u7684\u201c\u78a7\u7075\u4e39\u201d\u7ed9\u5979\u54bd\u4e0b\uff0c\u8bf4 \u9053\uff1a\u201c\u4f60\u597d\u597d\u4f11\u606f\uff0c\u6211\u4f1a\u5e26\u4f60\u51fa\u53bb\u7684\uff01\u201d \u3000\u3000\u8fd9\u4e00\u665a\u7eb3\u5170\u660e\u6167\u4e00\u591c\u53d1\u7740\u6076\u68a6\uff0c\u8bf4\u7740\u5453\u8bed\u3002\u4ed6\u4e0d\u65f6\u4ece\u68a6\u4e2d\u54ed\u9192\u8fc7\u6765\uff0c\u53eb\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4e0d \u8981\u6068\u6211\uff01\u201d\u6768\u4e91\u806a\u4e00\u518d\u7684\u5bf9\u5979\u8bf4\u3002\u201c\u6211\u4e0d\u6068\u4f60\uff0c\u201d\u53ef\u662f\u5979\u8fd8\u662f\u8fd9\u6837\u8bf4\u7740\u68a6\u8bdd\uff01 \u3000\u3000\u9ed1\u591c\u8fc7\u53bb\u4e86\uff0c\u767d\u5929\u53c8\u6765\u4e86\u3002\u8349\u539f\u4e0a\u7a7a\u53c8\u5e03\u6ee1\u9762\u5f69\u971e\u8f89\uff0c\u671d\u9633\u666e\u7167\u3002\u6768\u4e91\u806a\u6298\u817e\u4e86\u4e00\u591c\uff0c \u4e5f\u611f\u5230\u8eab\u4e0a\u75b2\u8f6f\uff0c\u53ef\u662f\u6709\u4e00\u4e2a\u75c5\u4eba\u8981\u5979\u7167\u6599\uff0c\u4e00\u79cd\u8d23\u4efb\u611f\u652f\u6301\u7740\u4ed6\uff0c\u4ed6\u8981\u5e26\u5979\u51fa\u53bb\uff0c\u5728\u8fd9\u5e7d \u8c37\u91cc\u6ca1\u6709\u533b\u836f\uff0c\u6ca1\u6709\u7cae\u98df\uff0c\u53ea\u597d\u542c\u6b7b\u3002\u5e26\u5979\u51fa\u53bb\u3002\u5047\u5982\u78b0\u7740\u6e05\u519b\uff1a\u5c31\u5c06\u5979\u4ea4\u7ed9\u4ed6\u4eec\uff0c\u81ea\u5df1\u9003 \u8dd1\uff0c\u5047\u5982\u78b0\u7740\u7267\u6c11\u6218\u58eb\uff0c\u51ed\u7740\u81ea\u5df1\u7684\u9762\u5b50\uff0c\u4e5f\u53ef\u4ee5\u4fdd\u5168\u5979\u3002 \u3000\u3000\u6768\u4e91\u806a\u4fee\u597d\u90a3\u8f86\u7834\u70c2\u7684\u9a6c\u8f66\uff0c\u5c06\u5979\u8f7b\u8f7b\u653e\u597d\uff0c\u63a8\u51fa\u5c71\u8c37\u3002\u8349\u539f\u4e0a\u5c3d\u662f\u6b7b\u5c38\u8c03\u5929\u7a7a\u4e0a\u6709\u6210 \u7fa4\u7684\u5927\u9e70\uff0c\u65f6\u4e0d\u65f6\u6251\u4e0b\u6765\u98df\u6b7b\u4eba\u7684\u5c38\u9996\uff01\u6709\u4e9b\u5927\u9e70\uff0c\u4e24\u7fc5\u5c55\u5f00\u7adf\u6709\u4e08\u4f59\u5bbd\uff0c\u6251\u4e0b\u6765\u5e26\u7740\u547c\u547c \u7684\u98ce\u58f0\uff0c\u5341\u5206\u53ef\u6016\uff01\u653e\u773c\u56db\u671b\uff0c\u8349\u539f\u4e0a\u4e00\u4e2a\u6d3b\u4eba\u4e5f\u6ca1\u6709\uff0c\u6709\u51e0\u5341\u5339\u5931\u53bb\u4e3b\u4eba\u7684\u6218\u9a6c\uff0c\u5728\u8349\u539f \u4e0a\u832b\u7136\u7684\u4e71\u8dd1\u5636\u9e23\u3002\u6768\u4e91\u806a\u6253\u4e86\u4e2a\u5bd2\u6218\u3002\u5583\u5583\u8bf4\u9053\uff1a\u201c\u6218\u4e89\u3001\u6218\u4e89\uff0c\u51e0\u65f6\u624d\u80fd\u6ca1\u6218\u4e89\u5462\uff1f\u201d \u3000\u3000\u6768\u4e91\u806a\u62c9\u6765\u4e86\u4e24\u5339\u6218\u9a6c\u3002\u5957\u4e0a\u9a6c\u8f66\uff0c\u53c8\u5728\u6218\u573a\u4e0a\u641c\u5230\u4e00\u4e9b\u7cae\u98df\uff0c\u653e\u5728\u8f66\u4e0a\uff0c\u9a91\u7740\u9a6c\u8f66\uff0c \u4e00\u8def\u5411\u5357\u8fb9\u8d70\u53bb\uff0c\u6cbf\u9014\u90fd\u662f\u5c38\u9996\uff0c\u4e00\u7247\u8352\u51c9\uff0c\u6628\u65e5\u53ae\u6740\u7684\u4e24\u519b\uff0c\u5df2\u4e0d\u77e5\u5230\u4ec0\u4e48\u5730\u65b9\u4e13\u4e86\u3002\u6e10 \u6e10\uff0c\u5c38\u9996\u5c11\u4e86\uff0c\u4f46\u4ecd\u7136\u627e\u4e0d\u5230\u6d3b\u4eba\u3002 \u3000\u3000\u7eb3\u5170\u660e\u6167\u7684\u75c5\uff0c\u597d\u50cf\u8d8a\u6765\u8d8a\u6c89\u91cd\u4e86\uff0c\u5979\u53d1\u7740\u9ad8\u70ed\uff0c\u4ecd\u7136\u4e0d\u505c\u7684\u8bf4\u68a6\u8bdd\uff0c\u6c14\u606f\u4e5f\u8d8a\u6765\u8d8a\u5f31\u3002 \u3000\u3000\u8349\u539f\u65e0\u8fb9\u65e0\u9645\uff0c\u597d\u50cf\u662f\u5ef6\u4f38\u5230\u5929\u8fb9\uff1a\u6628\u665a\u90a3\u4e48\u591a\u7684\u4eba\u5728\u8349\u539f\u7684\u201c\u9752\u8272\u7684\u6d77\u6d0b\u201d\u4e0a\u6d88\u5931\u3002 \u6768\u4e91\u806a\u72ec\u81ea\u9a71\u8f66\uff0c\u5728\u5927\u8349\u539f\u4e0a\u9a71\u9a70\uff0c\u611f\u5230\u5f02\u6837\u7684\u8352\u51c9\u3002\u7eb3\u5170\u660e\u6167\u7684\u75c5\uff0c\u66f4\u4f7f\u4ed6\u7684\u5fc3\u60c5\u7279\u522b\u6c89 \u91cd\u3002\u592a\u9633\u4ece\u4e1c\u8fb9\u5347\u8d77\uff0c\u53c8\u5feb\u8981\u4ece\u897f\u8fb9\u964d\u843d\u4e86\u3002 \u3000\u3000\u7eb3\u5170\u660e\u6167\u53cc\u988a\u706b\u7ea2\uff0c\u6768\u4e91\u806a\u7684\u5fc3\u7a81\u7a81\u5730\u8df3\uff0c\u5979\u7684\u6837\u5b50\u53ef\u7231\u6781\u4e86\uff01\u4f46\u4e5f\u6050\u6015\u662f\u201c\u56de\u5149\u53cd \u7167\u201d\uff0c\u4e34\u6b7b\u524d\u7684\u5a07\u8273\u4e86\u3002\u6768\u4e91\u806a\u8fd9\u65f6\u518d\u4e5f\u4e0d\u80fd\u987e\u4ec0\u4e48\u7537\u5973\u4e4b\u5acc\uff0c\u4ed6\u8f7b\u8f7b\u5730\u89e3\u5f00\u5979\u7684\u9886\u5b50\uff0c\u89e3 \u5f00\u5979\u7684\u8863\u94ae\uff0c\u7ed9\u5979\u63a8\u8840\u8fc7\u5bab\uff1b\u6768\u4e91\u806a\u5b66\u8fc7\u9488\u7078\uff0c\u53ef\u662f\u624b\u5934\u4e0a\u6ca1\u6709\u9488\uff0c\u53ea\u597d\u7528\u624b\u6307\u5728\u5979\u7684\u7a74\u9053 \u9aa8\u8282\u4e0a\u63c9\u634f\uff0c\u7eb3\u5170\u660e\u6167\u60a0\u60a0\u7684\u9192\u8f6c\u8fc7\u6765\uff0c\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u5927\u54e5\uff0c\u6211\u77e5\u9053\u6211\u5feb\u8981\u6b7b\u4e86\uff0c\u4f60\u5bf9\u6211\u8bf4 \u4e00\u53e5\u771f\u5fc3\u8bdd\uff0c\u4e00\u70b9\u4e5f\u4e0d\u8bb8\u6b3a\u7792\u6211\uff0c\u884c\u5417\uff1f\u201d\u6768\u4e91\u806a\u9053\uff1a\u201c\u4f60\u8bf4\u5427\uff0c\u6211\u4e00\u5b9a\u4f1a\u771f\u5fc3\u5730\u7b54\u4f60\uff01\u201d \u7eb3\u5170\u660e\u6167\u9762\u4e0a\u98de\u971e\uff0c\u76f4\u7ea2\u5230\u8116\u5b50\uff0c\u4f4e\u58f0\u8bf4\u9053\uff0c\u201c\u5927\u54e5\uff0c\u4f60\u8bf4\u2026\u2026\u4f60\u8981\u771f\u5fc3\u4ed6\u8bf4\uff0c\u4f60\u6b22\u559c\u6211 \u5417\uff1f\u201d\u6768\u4e91\u806a\u7684\u5fc3\u8df3\u5f97\u975e\u5e38\u5267\u70c8\uff0c\u5bf9\u4e00\u4e2a\u75c5\u5f97\u8fd9\u6837\u6c89\u91cd\u7684\u4eba\uff0c\u96be\u9053\u8fd8\u80fd\u7ed9\u5979\u5931\u671b\uff0c\u800c\u4e14\uff0c\u5979 \u5b9e\u5728\u4e5f\u4e0d\u80fd\u4ed4\u7ec6\u7684\u5206\u6790\u81ea\u5df1\u7684\u611f\u60c5\u4e86\uff0c\u4ed6\u7d27\u7d27\u7684\u62b1\u7740\u5979\uff0c\u5728\u5979\u8033\u8fb9\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u660e\u6167\uff0c\u6211\u771f \u5fc3\u7684\u6b22\u559c\u4f60\uff01\u201d \u3000\u3000\u67af\u840e\u7684\u82b1\u590d\u82cf\u4e86\uff01\u6768\u4e91\u806a\u8fd9\u53e5\u8bdd\u6bd4\u4ed6\u7684\u201c\u78a7\u7075\u4e39\u201d\u66f4\u6709\u6548\uff0c\u6bd4\u4e00\u5207\u4ed9\u4e39\u7075\u836f\u90fd\u6709\u6548\u3002\u7eb3 \u5170\u660e\u6167\u53ea\u89c9\u4e00\u80a1\u6696\u6d41\u6d41\u8fc7\u4e94\u810f\u516d\u8151\u3002\u6768\u4e91\u806a\u611f\u89c9\u5230\u5979\u63e1\u7740\u81ea\u5df1\u7684\u53cc\u624b\uff0c\u5ffd\u7136\u6709\u529b\u8d77\u6765\u4e86\uff0c\u6e10 \u6e10\u5730\u5979\u5750\u4e86\u8d77\u6765\uff0c\u5012\u5728\u6768\u4e91\u806a\u7684\u6000\u4e2d\uff0c\u53e3\u5507\u538b\u5728\u6768\u4e91\u806a\u7684\u9762\u4e0a\uff0c\u4e00\u9897\u706b\u70ed\u7684\u5c11\u5973\u7684\u5fc3\uff0c\u4e5f\u70eb \u5728\u6768\u4e91\u806a\u7684\u5fc3\u4e0a\uff0c\u8349\u539f\u7684\u9ec4\u660f\u6e10\u6e10\u5bd2\u51b7\u4e86\uff0c\u53ef\u662f\u6768\u4e91\u806a\u7684\u5fc3\uff0c\u5374\u611f\u5230\u5f02\u5e38\u7684\u70ed\uff0c\u70ed\uff0c\u70ed\uff0c \u3000\u3000\u6768\u4e91\u806a\u832b\u7136\u7684\u62b1\u7740\u5979\uff0c\u611f\u60c5\u50cf\u5954\u9a6c\u3001\u53c8\u5982\u5de8\u6f6e\uff0c\u6df7\u4e71\u6781\u4e86\uff0c\u4e5f\u6fc0\u52a8\u6781\u4e86\uff01\u4e0d\u80fd\u8bf4\u4ed6\u6ca1\u6709 \u4e00\u70b9\u540e\u6094\u4e4b\u611f\uff0c\u5728\u8fd9\u5239\u90a3\u95f4\uff0c\u4ed6\u66fe\u60f3\u8d77\u4e86\u98de\u7ea2\u5dfe\uff0c\u98de\u7ea2\u5dfe\u662f\u90a3\u6837\u7684\u723d\u6717\uff0c\u7b11\u58f0\u5c31\u50cf\u8349\u539f\u4e0a\u7684 \u9a7c\u94c3\uff01\u4ed6\u53c8\u60f3\u8d77\u8349\u539f\u591c\u796d\u4e4b\u540e\uff0c\u98de\u7ea2\u5dfe\u548c\u4ed6\u5728\u8349\u539f\u7684\u8d5b\u8dd1\u548c\u591c\u8bdd\uff0c\u662f\u90a3\u6837\u7684\u6dd8\u6c14\uff0c\u800c\u53c8\u662f\u90a3 \u6837\u7684\u8c6a\u8fc8\uff01\u90a3\u4e00\u665a\uff0c\u98de\u7ea2\u5dfe\u4e5f\u66fe\u5411\u4ed6\u8868\u793a\u8fc7\u6df1\u6c89\u7684\u611f\u60c5\uff0c\u4f46\u4ed6\u7684\u72b9\u8c6b\u8f7b\u8f7b\u7684\u5c06\u5979\u7684\u611f\u60c5\u5173\u5728 \u95e8\u5916\uff0c\u4ed6\u5e76\u6ca1\u6709\u4e3a\u5979\u6253\u5f00\u5fc3\u5e95\u95e8\u6249\uff0c\u867d\u7136\uff0c\u4ed6\u81ea\u89c1\u98de\u7ea2\u5dfe\u7b2c\u4e00\u9762\u540e\uff0c\u5c31\u628a\u5979\u5f53\u6210\u81ea\u5df1\u6700\u4eb2\u5bc6 \u7684\u4eba\uff0c\u90a3\u4efd\u611f\u60c5\uff0c\u531d\u8be5\u8bf4\u662f\u8fdc\u5728\u4ed6\u4e0e\u7eb3\u5170\u660e\u6167\u4e4b\u4e0a\u7684\uff01 \u3000\u3000\u4f46\u8fd9\u79cd\u540e\u6094\u7684\u5ff5\u5934\u970e\u90a3\u5c31\u8fc7\u53bb\u4e86\uff0c\u6768\u4e91\u806a\u662f\u4e00\u4e2a\u82f1\u96c4\uff0c\u4ed6\u82f1\u96c4\u7684\u5fc3\u547d\u4ee4\u4ed6\u4e0d\u8bb8\u53cd\u6094\u3002\u91cd \u89c6\u81ea\u5df1\u7684\u8bfa\u8a00\uff0c\u5df2\u7ecf\u6210\u4e3a\u4ed6\u7684\u4e60\u60ef\u4e86\uff0c\u4f55\u51b5\u6000\u4e2d\u7684\u5c11\u5973\u53c8\u662f\u90a3\u4e48\u6837\u771f\u631a\u7684\u7231\u4ed6\uff01\u4ed6\u53c8\u89c9\u5f97\u98de \u7ea2\u5dfe\u662f\u50cf\u4ed6\u4e00\u6837\u7684\u4eba\uff0c\u5e94\u8be5\u7ecf\u53d7\u5f97\u8d77\u4efb\u4f55\u632b\u6298\uff0c\u5305\u62ec\u611f\u60c5\u7684\u6298\u78e8\u5728\u5185\uff01\u800c\u7eb3\u5170\u660e\u6167\u5728\u4ed6\u7684\u773c \u4e2d\uff0c\u5374\u662f\u4e00\u6735\u5ae9\u5f31\u7684\u82b1\uff0c\u867d\u7136\u5979\u4e5f\u61c2\u5f97\u6b66\u827a\u3002\u5979\u662f\u90a3\u6837\u7684\u7eaf\u771f\u3001\u65e0\u90aa\u548c\u6e29\u67d4\uff0c\u5c31\u50cf\u5c0f\u5b69\u5b50\u4e00 \u6837\uff0c\u4ed6\u9700\u8981\u7231\u62a4\u5979\uff0c\u4fdd\u536b\u5979\uff0c\u5c06\u5979\u6162\u6162\u5f15\u5bfc\u5230\u81ea\u5df1\u8fd9\u9762\u6765\u3002 \u3000\u3000\u6768\u4e91\u806a\u548c\u7eb3\u5170\u660e\u6167\u7d27\u7d27\u5730\u62e5\u62b1\u7740\uff0c\u9677\u5728\u4e00\u79cd\u201c\u6df7\u4e71\u7684\u9676\u9189\u201d\u4e2d\uff0c\u8fc7\u4e86\u8bb8\u4e45\u8bb8\u4e45\uff0c\u624d\u7ed9\u4e00 \u9635\u9a6c\u94c3\u4e4b\u58f0\u6240\u60ca\u9192\u3002\u6768\u4e91\u806a\u62ac\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u8fdc\u65b9\u6709\u51e0\u5341\u5339\u9a6c\u98de\u9a70\u800c\u6765\uff0c\u970e\u90a3\u4fbf\u5230\u4e86\u8fd1\u5904\uff0c\u4e3a \u9996\u7684\u4eba\u563f\u563f\u51b7\u7b11\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u4f60\u5c31\u662f\u6768\u4e91\u806a\u5417\uff1f\u4f60\u4e3a\u4ec0\u4e48\u67aa\u4e86\u6211\u7684\u4fd8\u864f\uff0c\u53c8\u6740\u4e86\u6211\u7684\u4eba\uff1f\u201d \u3000\u3000----------- \u3000\u3000\u98ce\u4e91\u9601 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u585e\u5916\u5947\u4fa0\u4f20\u300b\u2014\u2014\u7b2c\u5341\u4e94\u56de\u3000\u6076\u6bd2\u7684\u8bec\u8511 \u6881\u7fbd\u751f\u300a\u585e\u5916\u5947\u4fa0\u4f20\u300b \u7b2c\u5341\u4e94\u56de\u3000\u6076\u6bd2\u7684\u8bec\u8511 \u3000\u3000\u6768\u4e91\u806a\u8d76\u5fd9\u653e\u5f00\u7eb3\u5170\u660e\u6167\uff0c\u7eb5\u6b65\u51fa\u6765\uff0c\u501a\u7740\u8f66\u8f95\u3002\u53ea\u89c1\u4e3a\u9996\u7684\u866c\u987b\u5927\u6c49\u53eb\u9053\uff1a\u201c\u6768\u4e91 \u806a\uff0c\u4f60\u8fd9\u53cd\u8d3c\uff0c\u5403\u6211\u4e00\u5200\uff01\u201d\u6768\u4e91\u806a\u8eab\u5b50\u4e00\u4fa7\uff0c\u56b7\u9053\uff1a\u201c\u4e14\u6162\uff0c\u4f60\u662f\u8c01\uff1f\u6211\u6768\u4e91\u806a\u662f\u9876\u5929\u7acb \u5730\u7684\u6c49\u5b50\uff0c\u5c82\u5bb9\u4f60\u6c61\u8a00\u4fae\u8511\uff0c\u6211\u51e0\u65f6\u53cd\u4e86\uff0c\u6211\u54ea\u4e00\u70b9\u5bf9\u4e0d\u7740\u4f60\u4eec\uff0c\u4f60\u8bf4\u4e0d\u51fa\u6765\uff0c\u6211\u4e5f\u8981\u63ea\u4f60 \u53bb\u89c1\u98de\u7ea2\u5dfe\uff01\u201d \u3000\u3000\u90a3\u866c\u987b\u6c49\u4e88\u201c\u54fc\u201d\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u98de\u7ea2\u5dfe\uff0c\u4f60\u5c31\u6653\u5f97\u62ff\u98de\u7ea2\u5dfe\u505a\u4f60\u7684\u62a4\u8eab\u7b26\uff01\u6211\u95ee\u4f60\uff0c\u4f60 \u6740\u5bb3\u6211\u4eec\u7684\u6218\u58eb\uff0c\u5305\u5e87\u654c\u4eba\uff0c\u62a2\u8d70\u6211\u7684\u4fd8\u864f\uff0c\u4f60\u8fd8\u6562\u5f3a\u786c\uff1f\u4f60\u4e0d\u662f\u53cd\u8d3c\u662f\u4ec0\u4e48\uff1f\u201d\u6768\u4e91\u806a\u6c14 \u5f97\u6ee1\u9762\u901a\u7ea2\uff0c\u559d\u9053\uff1a\u201c\u6211\u51e0\u65f6\u6740\u4e86\u4f60\u4eec\u7684\u6218\u58eb\u53c8\u5305\u5e87\u654c\u4eba\u6765\u4e86\uff1f\u6211\u5728\u5317\u50f5\u6253\u4e86\u51e0\u5e74\u4ed7\uff0c\u73b0\u5728 \u53c8\u5230\u5357\u7586\u548c\u4f60\u4eec\u4e00\u8d77\u6253\u4ed7\uff0c\u6211\u82e5\u8981\u53cd\u53db\uff0c\u4f55\u5fc5\u5343\u8f9b\u4e07\u82e6\uff0c\u6a2a\u6e21\u5927\u6c99\u6f20\uff0c\u5230\u4f60\u4eec\u8fd9\u91cc\u6765\u53cd\u53db\uff1f\u201d \u3000\u3000\u866c\u987b\u6c49\u5b50\u9053\uff1a\u201c\u6211\u95ee\u4f60\uff0c\u8fd9\u9a6c\u8f66\u4e0a\u8f7d\u7684\u662f\u51c6\uff1f\u4f60\u4eec\u5728\u5c71\u6c9f\u91cc\u6740\u7684\u4e24\u4e2a\u4eba\u53c8\u662f\u8c01\uff1f\u4eba\u8d43\u5e76 \u83b7\uff0c\u96be\u9053\u662f\u6211\u8d56\u4e86\u4f60\uff1f\u201d\u6768\u4e91\u806a\u6115\u7136\u4e00\u60ca\uff0c\u5fc3\u60f3\u8fd9\u8bef\u4f1a\u53ef\u5927\u4e86\uff0c\u6b63\u60f3\u8fa9\u89e3\u3002\u90a3\u6c49\u5b50\u53c8\u9053\uff0c \u201c\u4f60\u77e5\u9053\u6211\u662f\u8c01\uff1f\u6211\u5c31\u662f\u5580\u8fbe\u5c14\u65cf\u7684\u914b\u957f\u5b5f\u7984\uff0c\u4f60\u6740\u7684\u90a3\u4e2a\u4eba\u662f\u6211\u624b\u4e0b\u6700\u5f97\u529b\u7684\u6218\u58eb\uff0c\u4f60\u8f66 \u4e0a\u8f7d\u7684\u662f\u6211\u7684\u4fd8\u864f\uff01\u201d \u3000\u3000\u539f\u6765\u524d\u665a\u7eb3\u5170\u660e\u6167\u7528\u98de\u5200\u624e\u8fdb\u4e86\u90a3\u4e24\u4e2a\u4eba\u7684\u5fc3\u7a9d\uff0c\u5176\u4e2d\u4e00\u4e2a\u4e00\u65f6\u5c1a\u672a\u6b7b\u53bb\uff0c\u4e34\u6b7b\u524d\u6ee1\u6000 \u6124\u6012\uff0c\u60f3\u628a\u4ec7\u4eba\u7684\u540d\u5b57\u5212\u5728\u5730\u4e0a\uff0c\u4f46\u4ed6\u53c8\u4e0d\u77e5\u7eb3\u5170\u660e\u6167\u7684\u59d3\u540d\uff0c\u7cca\u91cc\u7cca\u6d82\uff0c\u5728\u4e34\u6b7b\u65f6\u8638\u8840\u5728 \u5730\u4e0b\u5c31\u5212\u4e86\u6768\u4e91\u806a\u4e09\u4e2a\u5927\u5b57\u3002\u90a3\u65f6\u6b63\u5f53\u9ed1\u591c\uff0c\u6768\u4e91\u806a\u53c8\u5fd9\u7740\u7167\u987e\u7eb3\u5170\u660e\u6167\uff0c\u7adf\u6ca1\u7559\u610f\u90a3\u4e2a\u6c49 \u5b50\u5728\u4e34\u6b7b\u524d\u7559\u4e0b\u6700\u6bd2\u6076\u7684\u8bec\u8511\uff01 \u3000\u3000\u201c\u5580\u8fbe\u5c14\u201d\u662f\u5357\u7586\u8349\u539f\u4e0a\u4e00\u4e2a\u597d\u52c7\u6597\u72e0\u7684\u90e8\u843d\uff0c\u4ed6\u4eec\u6709\u4e00\u4e2a\u53e4\u8001\u76f8\u4f20\u7684\u98ce\u4fd7\uff0c\u82e5\u662f\u548c\u654c \u4eba\u4e89\u6597\uff0c\u529b\u4e0d\u652f\u654c\uff0c\u88ab\u6740\u4f24\u65f6\uff0c\u82e5\u8ba4\u5f97\u654c\u4eba\u662f\u8c01\uff0c\u5728\u4e34\u6b7b\u524d\uff0c\u5c31\u8981\u7528\u9c9c\u8840\u5199\u4e0b\u654c\u4eba\u7684\u540d\u5b57\uff0c \u5e0c\u671b\u80fd\u8ba9\u65cf\u4eba\u770b\uff0c\u4ee3\u4e3a\u62a5\u4ec7\u3002 \u3000\u3000\u90a3\u65e5\u8349\u539f\u5927\u6df7\u6218\uff0c\u8d77\u5148\u662f\u5357\u7586\u5404\u65cf\u5360\u4f18\u52bf\uff0c\u540e\u6765\u6ee1\u6e05\u7684\u63f4\u519b\u8d76\u5230\uff1b\uff08\u5176\u65f6\u6768\u4e91\u806a\u5df2\u8dc3\u5165 \u5c71\u6c9f\uff09\u5357\u7586\u7684\u5404\u65cf\u6218\u58eb\u53cd\u7ed9\u5305\u56f4\uff0c\u5404\u65cf\u5404\u90e8\u843d\uff0c\u62fc\u547d\u7a81\u56f4\uff0c\u635f\u5931\u751a\u91cd\uff0c\u8fd9\u4e5f\u5c31\u662f\u6768\u4e91\u806a\u884c\u4e86 \u4e00\u5929\uff0c\u90fd\u78b0\u4e0d\u7740\u6d3b\u4eba\u7684\u9053\u7406\uff0c\u6e05\u519b\u5df2\u5411\u5357\u65b9\u7684\u5927\u57ce\u4f0a\u7281\u6536\u5175\uff0c\u800c\u5404\u65cf\u6218\u58eb\u53c8\u90fd\u5728\u6d69\u701a\u65e0\u9645\u7684 \u5927\u8349\u539f\u4e0a\u5206\u6563\u4e86\u3002\u5728\u90a3\u65e5\u7684\u5927\u6df7\u4e71\u4e2d\uff0c\u5580\u8fbe\u5c14\u65cf\u7684\u914b\u957f\u5b5f\u7984\u548c\u4ed6\u4eec\u7684\u6218\u58eb\uff0c\u90fd\u88ab\u622a\u5728\u4e00\u89d2\uff0c \u5927\u519b\u8ffd\u9010\uff0c\u53cd\u800c\u65e0\u6687\u6d88\u706d\u4ed6\u4eec\uff0c\u7ed9\u4ed6\u9003\u51fa\u6027\u547d\uff0c\u5728\u6218\u573a\u4e0a\u5230\u5904\u627e\u5bfb\u65cf\u4eba\uff0c\u627e\u5230\u4e86\u5c71\u6c9f\u91cc\uff1b\u5ffd \u7136\u53d1\u73b0\u4e24\u4e2a\u6218\u58eb\u7684\u6b7b\u5c38\uff0c\u5730\u4e0a\u7559\u6709\u8840\u5b57\u3002\u5b5f\u7984\u5927\u5403\u4e00\u60ca\uff0c\u6768\u4e91\u806a\u5728\u5317\u7586\u867d\u662f\u9f0e\u9f0e\u6709\u540d\uff0c\u5b5f\u7984 \u4e5f\u542c\u8fc7\u4ed6\u7684\u540d\u5b57\u3002\u4f46\u4ed6\u5374\u4e0d\u77e5\u9053\u6768\u4e91\u806a\u7684\u4e3a\u4eba\uff0c\u4e5f\u4e0d\u77e5\u9053\u6768\u4e91\u806a\u5728\u5317\u7586\u7684\u5a01\u671b\uff0c\u5c31\u5982\u98de\u7ea2\u4e2d \u5728\u5357\u7586\u4e00\u6837\u3002\u4ed6\u53ea\u9053\u6768\u4e91\u806a\u4e5f\u50cf\u695a\u662d\u5357\u4e00\u6837\uff0c\u53ea\u662f\u4e2a\u201c\u52a9\u62f3\u201d\u7684\u4eba\uff0c\u4ed7\u7740\u5251\u6cd5\u9ad8\u660e\uff0c\u6240\u4ee5\u624d \u6709\u540d\u6c14\u7684\u3002\u4ed6\u53c8\u604d\u60da\u542c\u4eba\u8bf4\u8fc7\uff1b\u6768\u4e91\u806a\u4e43\u662f\u695a\u662d\u5357\u7684\u5e08\u5144\uff0c\u5f53\u65e5\u695a\u662d\u5357\u6765\u6295\u5510\u52aa\u8001\u82f1\u96c4\uff0c\u6367 \u7684\u5c31\u662f\u6768\u4e91\u806a\u7684\u540d\u5934\u3002\u695a\u662d\u5357\u53cd\u53db\u4e4b\u4e8b\u4ed6\u662f\u77e5\u9053\u7684\uff0c\u4ed6\u53ea\u4ee5\u4e3a\u6768\u4e91\u806a\u7ed9\u4ed6\u7684\u5e08\u5f1f\u62c9\u53bb\uff0c\u5230\u5357 \u7586\u6765\u6697\u5bb3\u4ed6\u4eec\u3002\u56e0\u6b64\uff0c\u5e26\u7740\u4e09\u5341\u591a\u5339\u9a6c\uff0c\u4e00\u8def\u8ffd\u8e2a\u89c5\u8ff9\uff0c\u800c\u6768\u4e91\u806a\u53c8\u56e0\u5904\u5904\u8981\u7167\u987e\u7eb3\u5170\u660e \u6167\uff0c\u4e0d\u80fd\u9a71\u8f66\u75be\u8d70\uff0c\u7adf\u7136\u7ed9\u4ed6\u4eec\u8ffd\u4e0a\uff01 \u3000\u3000\u6768\u4e91\u806a\u4e00\u9635\u6115\u7136\uff0c\u7eb3\u5170\u6717\u6167\u5ffd\u7136\u63ed\u5f00\u8f66\u5e18\uff0c\u9732\u51fa\u8138\u6765\uff0c\u53eb\u9053\uff0c\u201c\u4f60\u4eec\u4e0d\u8981\u8d56\u4ed6\uff0c\u90a3\u4e24\u4e2a \u4eba\u662f\u6211\u6740\u7684\uff01\u201d\u7eb3\u5170\u660e\u6167\u5f97\u4e86\u7231\u60c5\u7684\u6ecb\u6da6\uff0c\u867d\u5728\u75c5\u540e\uff0c\u5374\u662f\u773c\u5982\u79cb\u6c34\uff0c\u5bb9\u5149\u7167\u4eba\uff0c\u5979\u672c\u662f\u65d7 \u4eba\u4e2d\u7684\u7b2c\u4e00\u4f4d\u7f8e\u4eba\uff0c\u5728\u8fd9\u8349\u539f\u84e6\u7136\u73b0\u51fa\u8272\u76f8\uff0c\u989c\u5bb9\u6620\u7740\u665a\u971e\uff0c\u5b5f\u7984\u53ea\u89c9\u5f97\u4e00\u9635\u5149\u91c7\u8feb\u4eba\uff0c\u773c \u82b1\u7efc\u4e71\uff0c\u6025\u5fd9\u5b9a\u4e0b\u5fc3\u795e\uff0c\u518d\u559d\u95ee\u9053\uff1a\u201c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u201d\u7eb3\u5170\u660e\u6167\u51b7\u7b11\u9053\uff0c\u201c\u4f60\u542c\u4e0d\u6e05\u695a\u4e48\uff1f\u90a3 \u4e24\u4e2a\u4eba\u662f\u672c\u59d1\u5a18\u6740\u7684\uff01\u201d \u3000\u3000\u5b5f\u7984\u8fd9\u65f6\u4e5f\u6ce8\u610f\u5230\u4e86\u8f66\u5e18\u4e0a\u7ee3\u7740\u7684\u201c\u7eb3\u5170\u201d\u4e24\u5b57\uff0c\u53c8\u60ca\u53c8\u559c\uff01\u4ed6\u8d77\u521d\u4ee5\u4e3a\u8f66\u4e0a\u53ea\u662f\u666e\u901a \u7684\u6e05\u519b\u5c06\u5b98\u7684\u7737\u5c5e\uff0c\u800c\u4eca\u89c1\u8fd9\u4e2a\u6c14\u6d3e\uff0c\u66ae\u7136\u60f3\u8d77\u4e45\u95fb\u6ee1\u6e05\u7684\u4f0a\u7281\u5c06\u519b\u7eb3\u5170\u79c0\u5409\uff0c\u6709\u4e00\u4e2a\u7f8e\u4e3d \u7684\u5973\u513f\uff0c\u6587\u6b66\u53cc\u5168\uff0c\u83ab\u4e0d\u662f\u5979\uff01 \u3000\u3000\u5b5f\u7984\u76ae\u97ad\u4e00\u6307\uff0c\u7b11\u9053\uff1a\u201c\u662f\u4f60\u6740\u7684\u4e5f\u597d\uff0c\u4e0d\u662f\u4f60\u6740\u7684\u4e5f\u597d\uff01\u4f60\u73b0\u5728\u662f\u6211\u7684\u4fd8\u864f\u4e86\uff0c\u968f\u6211 \u56de\u53bb\u518d\u8bf4\uff01\u201d\u7eb3\u5170\u660e\u6167\u53c8\u662f\u4e00\u58f0\u51b7\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4e5f\u60f3\u8ddf\u90a3\u4e24\u4e2a\u4eba\u53bb\u89c1\u960e\u738b\u5417\uff1f\u4ed6\u4eec\u5c31\u662f\u8bf4 \u8981\u6349\u6211\u505a\u4fd8\u864f\uff0c\u624d\u7ed9\u6211\u7528\u98de\u5200\u624e\u6b7b\u7684\uff01\u201d \u3000\u3000\u5b5f\u7984\u6307\u6325\u624b\u4e0b\uff0c\u5c31\u60f3\u6765\u6349\u3002\u6768\u4e91\u806a\u5927\u53eb\u4e00\u58f0\uff1a\u201c\u4f7f\u4e0d\u5f97\u3002\u201d\u5b5f\u7984\u4e00\u97ad\u6253\u53bb\uff0c\u559d\u9053\uff1a\u201c\u600e \u4e48\u4f7f\u4e0d\u5f97\uff1f\u201d\u6768\u4e91\u806a\u5939\u624b\u5c06\u97ad\u593a\u8fc7\uff0c\u6298\u4e3a\u4e24\u6bb5\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u4e3a\u4ec0\u4e48\u6253\u4ed7\uff1f\u201d\u5b5f\u7984\u89c1\u6768\u4e91\u806a \u53cc\u76ee\u5706\u7741\uff0c\u5a01\u98ce\u51db\u51db\uff0c\u4e00\u65f6\u5012\u4e0d\u6562\u8feb\u8fc7\u6765\u3002\u53cd\u95ee\u9053\uff1a\u201c\u4f60\u5230\u5e95\u662f\u5e2e\u8c01\u6253\u4ed7\uff1f\u201d\u6768\u4e91\u806a\u9053\uff1a \u201c\u6211\u548c\u6e05\u5175\u5927\u5c0f\u6570\u767e\u4ed7\uff0c\u4ece\u5317\u7586\u6253\u5230\u5357\u7586\uff0c\u53ef\u7b11\u4f60\u4eec\u8fde\u4e3a\u4ec0\u4e48\u8981\u6253\u4ed7\u90fd\u8fd8\u4e0d\u77e5\uff01\u201d\u5b5f\u7984\u624b\u4e0b \u7684\u4e00\u4e2a\u6218\u58eb\u6012\u9053\uff0c\u201c\u6768\u4e91\u806a\uff0c\u4f60\u4ee5\u4e3a\u5e2e\u6211\u4eec\u6253\u4ed7\uff0c\u5c31\u53ef\u4ee5\u80e1\u8bf4\u516b\u9053\u5417\uff1f\u6211\u4eec\u4e5f\u6253\u4e86\u8fd9\u4e48\u591a \u5e74\uff0c\u8c01\u4e0d\u77e5\u9053\u6253\u4ed7\u4e3a\u7684\u5c31\u662f\u8981\u628a\u9791\u5b50\u8d76\u51fa\u53bb\uff01\u201d \u3000\u3000\u6768\u4e91\u806a\u53c8\u8bf4\u9053\uff1a\u201c\u5bf9\u5440\uff01\u4f46\u4e3a\u4ec0\u4e48\u8981\u628a\u9791\u5b50\u8d76\u51fa\u53bb\u5462\uff1f\u96be\u9053\u4e0d\u662f\u4e3a\u4e86\u6ee1\u6d32\u9791\u5b50\u4e0d\u628a\u6211\u4eec \u5f53\u4eba\uff0c\u62a2\u63a0\u6211\u4eec\u7684\u725b\u7f8a\uff0c\u4fae\u8fb1\u6211\u4eec\u7684\u5987\u5973\uff0c\u5974\u5f79\u6211\u4eec\u7684\u767e\u59d3\u5417\uff1f\u73b0\u5728\u4f60\u4eec\u8981\u6349\u8fd9\u4e2a\u5973\u5b50\u505a\u4fd8 \u864f\uff0c\u4e0d\u662f\u4e5f\u8981\u4fae\u8fb1\u5979\uff0c\u4e0d\u628a\u5979\u5f53\u4eba\uff0c\u8981\u628a\u5979\u5f53\u5974\u96b6\u5417\uff1f\u4f60\u4eec\u4e0d\u8bb8\u9791\u5b50\u90a3\u6837\u505a\uff0c\u4e3a\u4f55\u4f60\u4eec\u53c8\u8981 \u8fd9\u6837\u505a\uff1f\u201d\u5b5f\u7984\u624b\u4e0b\u4e09\u5341\u591a\u4eba\u5374\u7b54\u4e0d\u51fa\u6765\uff0c\u8fd9\u9053\u7406\u4ed6\u4eec\u8fd8\u662f\u7b2c\u4e00\u6b21\u542c\u5230\uff0c\u8fd8\u6ca1\u529e\u6cd5\u5206\u51fa\u662f \u975e\uff0c\u5b5f\u7984\u53c8\u559d\u9053\uff1a\u201c\u5979\u662f\u6211\u4eec\u7684\u654c\u4eba\u5440\uff01\u5979\u8fd8\u6740\u6b7b\u4e86\u6211\u4eec\u4e24\u4e2a\u5f1f\u5144\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u80fd\u6349\u5979\u505a\u5974 \u96b6\uff1f\u201d\u6768\u4e91\u806a\u9053\uff1a\u201c\u548c\u4f60\u4eec\u6253\u4ed7\u662f\u6ee1\u6e05\u519b\u961f\uff0c\u4e0d\u662f\u5979\uff01\u5728\u6218\u573a\u4f60\u4eec\u6740\u62ff\u5200\u7684\u9791\u5b50\uff0c\u6740\u5f97\u8d8a\u591a \u8d8a\u597d\uff01\u4f46\u5728\u8fd9\u91cc\uff0c\u4f60\u4eec\u8981\u4fae\u8fb1\u4e00\u4e2a\u7a7a\u624b\u7684\u5c11\u5973\uff0c\u4f60\u4eec\u4e0d\u5bb3\u81ca\u5417\uff1f\u5979\u6740\u6b7b\u90a3\u4e24\u4e2a\u4eba\uff0c\u5c31\u662f\u56e0\u4e3a \u4ed6\u4eec\u8981\u6b3a\u8d1f\u5979\uff0c\u5979\u624d\u8feb\u5f97\u81ea\u536b\u3002\u6211\u8bf4\uff0c\u9519\u7684\u4e0d\u662f\u5979\uff0c\u662f\u4f60\u4eec\uff01\u201d \u3000\u3000\u5b5f\u7984\u7684\u624b\u4e0b\u90fd\u77e5\u9053\u6768\u4e91\u806a\u662f\u4e2a\u6297\u6e05\u7684\u82f1\u96c4\uff0c\u867d\u7136\u5b5f\u7984\u6000\u7591\u4ed6\u53cd\u53db\uff0c\u7387\u4ed6\u4eec\u6765\u8ffd\uff0c\u53ef\u662f\u5728 \u8fd8\u6ca1\u6709\u5f97\u5230\u786e\u5207\u8bc1\u636e\u4e4b\u524d\uff0c\u4ed6\u4eec\u5230\u5e95\u5bf9\u6768\u4e91\u806a\u8fd8\u6709\u591a\u5c11\u656c\u610f\u3002\u8fd9\u65f6\u6768\u4e91\u806a\u7406\u76f4\u6c14\u58ee\u7684\u8fd9\u4e48\u4e00 \u8bf4\u3002\u53c8\u4f3c\u4e4e\u9887\u6709\u9053\u7406\uff0c\u4f46\u6349\u4fd8\u864f\u505a\u5974\u96b6\u4e4b\u4e8b\uff0c\u662f\u90e8\u843d\u6c11\u65cf\u51e0\u5343\u5e74\u4f20\u4e0b\u6765\u7684\u4e60\u60ef\uff0c\u8fd9\u4e60\u60ef\u5df2\u6df1 \u5165\u4eba\u5fc3\uff0c\u56e0\u6b64\u53c8\u4f3c\u4e4e\u89c9\u5f97\u6768\u4e91\u806a\u662f\u5728\u5f3a\u8fa9\u3002 \u3000\u3000\u5b5f\u7984\u662f\u4e2a\u5fc3\u9ad8\u6c14\u50b2\u7684\u4eba\uff0c\u4ed6\u4e5f\u66fe\u6709\u610f\u4e8e\u98de\u7ea2\u5dfe\uff0c\u53ef\u662f\u98de\u7ea2\u5dfe\u4e0d\u7406\u776c\u4ed6\u3002\u63a8\u9009\u76df\u4e3b\u90a3\u665a\uff0c \u4ed6\u4e0d\u53c2\u52a0\uff0c\u4e00\u6765\u662f\u6709\u5fc3\u75c5\uff0c\u4e8c\u6765\u4e5f\u662f\u56e0\u4e3a\u4e0d\u670d\u98de\u7ea2\u5dfe\u3002\u6768\u4e91\u806a\u8bf4\u5b8c\u4e4b\u540e\uff0c\u4ed6\u77a7\u4e86\u7eb3\u5170\u660e\u6167\u4e00 \u773c\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u6768\u4e91\u806a\uff0c\u6211\u95ee\u4f60\u4e3a\u4ec0\u4e48\u8981\u4fdd\u62a4\u5979\uff0c\u4f60\u8bf4\u4f60\u4e0d\u662f\u53cd\u8d3c\uff0c\u662f\u5927\u82f1\u96c4\uff0c\u90a3\u4e48\u6211\u4eec \u7684\u5927\u82f1\u96c4\u4e3a\u4ec0\u4e48\u8981\u66ff\u4e00\u4e2a\u654c\u4eba\u5973\u513f\u9a7e\u8f66\uff0c\u505a\u8d77\u9a6c\u8f66\u592b\u6765\u5566\uff0c\u54c8\uff01\u54c8\uff01\u201d\u6768\u4e91\u806a\u6c14\u5f97\u8eab\u5b50\u98a4 \u6296\uff0c\u5b5f\u7984\u53c8\u5927\u58f0\u53eb\u9053\uff1a\u201c\u5f1f\u5144\u4eec\uff0c\u4f60\u770b\uff1b\u8fd9\u5c31\u662f\u5927\u82f1\u96c4\u6768\u4e91\u806a\u7684\u884c\u5f84\u3002\u4f60\u4eec\u77e5\u9053\u8fd9\u4e2a\u5973\u5b50\u662f \u8c01\u5417\uff1f\u5979\u5c31\u662f\u6ee1\u6e05\u7684\u4f0a\u7281\u5c06\u519b\u7eb3\u5170\u79c0\u5409\u7684\u5973\u513f\uff0c\u54fc\uff0c\u6768\u4e91\u806a\u5982\u4e0d\u662f\u65e9\u548c\u4ed6\u4eec\u6709\u52fe\u7ed3\uff0c\u4e3a\u4f55\u5904 \u5904\u8981\u7ef4\u62a4\u5979\uff0c\u751a\u81f3\u522b\u4eba\u6253\u4ed7\uff0c\u4ed6\u5374\u53bb\u66ff\u7eb3\u5170\u79c0\u5409\u7684\u5973\u513f\u9a7e\u8f66\u3002\u628a\u4ed6\u4eec\u4e24\u4e2a\u90fd\u6346\u8d77\u6765\u5427\uff0c\u5f1f\u5144 \u4eec\uff01\u201d \u3000\u3000\u5b5f\u7984\u4e00\u756a\u8bdd\u597d\u50cf\u5c06\u6cb9\u6cfc\u5728\u4eba\u4e0a\uff1b\u4ed6\u7684\u90e8\u4e0b\u679c\u7136\u53d7\u4e86\u717d\u52a8\uff0c\u8f70\u7136\u5608\u6742\u8d77\u6765\uff0c\u5200\u62a2\u9f50\u4e3e\uff0c\u7adf \u56f4\u4e0a\u6765\uff0c\u7eb3\u5170\u660e\u6167\u6478\u51fa\u98de\u5200\uff0c\u6768\u4e91\u806a\u6025\u53eb\u8fd9\uff1a\u201c\u4f7f\u4e0d\u5f97\uff01\u201d\u7eb3\u5170\u660e\u6167\u7684\u7b2c\u4e00\u53e3\u98de\u5200\u5df2\u7ecf\u51fa \u624b\uff0c\u94f6\u5149\u7535\u5c04\uff0c\u5bf9\u51c6\u5b5f\u7984\u7684\u5fc3\u7a9d\u98de\u53bb\uff0c\u6768\u4e91\u806a\u75be\u5fd9\u4e00\u5c55\u8eab\u5f62\uff0c\u5c06\u90a3\u53e3\u98de\u5200\u622a\u4f4f\uff0c\u90a3\u65f6\uff0c\u98de\u5200 \u79bb\u5b5f\u7984\u7684\u5fc3\u7a9d\u4e0d\u5230\u4e09\u5bf8\uff01\u5b5f\u7984\u614c\u5f20\u4e2d\u4e00\u5200\u5288\u4e0b\u6765\uff0c\u6768\u4e91\u806a\u4e00\u77ee\u8eab\u8eaf\uff0c\u5728\u4ed6\u5200\u950b\u4e0b\u94bb\u8fc7\uff0c\u53eb \u9053\uff1a\u201c\u660e\u6167\uff0c\u4f60\u8eb2\u8fdb\u53bb\uff01\u201d\u7eb3\u5170\u660e\u6167\u7ed9\u4ed6\u4e00\u559d\uff0c\u98de\u5200\u662f\u4e0d\u653e\u4e86\uff0c\u53ef\u662f\u5374\u4e0d\u80af\u8eb2\u8fdb\u53bb\uff0c\u5979\u8981\u770b \u6768\u4e91\u806a\u6253\u67b6\u5462\uff01 \u3000\u3000\u5b5f\u7984\u6beb\u4e0d\u9886\u60c5\uff0c\u9a6c\u5200\u53c8\u518d\u780d\u5230\uff0c\u4ed6\u7684\u624b\u4e0b\u4e5f\u7eb7\u7eb7\u6251\u4e86\u4e0a\u6765\uff0c\u8fd8\u5206\u4e86\u4e03\u516b\u4e2a\u4eba\u53bb\u6349\u7eb3\u5170\u660e \u6167\uff0c\u6768\u4e91\u806a\u6697\u53eb\u201c\u4e0d\u597d\uff01\u201d\u5fc3\u60f3\u8fd9\u4e8b\u4e0d\u80fd\u5584\u4f11\uff1b\u731b\u7136\u5c55\u5f00\u8f7b\u7075\u8fc5\u6377\u7684\u8eab\u6cd5\uff0c\u201c\u5728\u5200\u67aa\u7f1d\u4e2d\uff0c \u94bb\u6765\u94bb\u53bb\u3002\u4e3e\u624b\u6295\u8db3\u4e4b\u95f4\uff0c\u628a\u4e09\u5341\u591a\u6761\u5927\u6c49\u90fd\u70b9\u4e86\u7a74\u9053\uff1b\u8fde\u5b5f\u7984\u4e5f\u5728\u5185\uff0c\u6216\u4f5c\u52bf\u524d\u6251\uff0c\u6216\u4e3e \u5200\u6b32\u780d\uff0c\u90fd\u662f\u4e2a\u4e2a\u52a8\u5f39\u4e0d\u5f97\uff0c\u597d\u50cf\u7740\u4e86\u5b9a\u8eab\u6cd5\u4e00\u6837\uff0c\u5b9a\u5728\u90a3\u513f\u3002\u7eb3\u5170\u660e\u6167\u5728\u8f66\u4e0a\u7eb5\u58f0\u5a07\u7b11\uff0c \u6768\u4e91\u806a\u5374\u6709\u82e6\u8bf4\u4e0d\u51fa\u6765\uff0c\u8fd9\u771f\u662f\u8bef\u4f1a\u52a0\u4e0a\u8bef\u4f1a\uff0c\u4e0d\u77e5\u5982\u4f55\u624d\u80fd\u6536\u573a\uff01 \u3000\u3000\u731b\u7136\u95f4\uff0c\u7eb3\u5170\u660e\u6167\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u6e05\u5175\u6765\u4e86\uff01\u201d\u6768\u4e91\u806a\u8df3\u4e0a\u8f66\u9876\u4e00\u770b\uff0c\u679c\u7136\u8fdc\u5904\u5c18\u5934\u5927 \u8d77\uff0c\u6768\u4e91\u806a\u6025\u5fd9\u8df3\u4e0b\uff0c\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u8d76\u5feb\u8d70\u5427\uff0c\u6e05\u5175\u52bf\u5927\uff0c\u8ba9\u6211\u5728\u8fd9\u91cc\u7ed9\u4f60\u4eec\u62b5\u6321\u4e00 \u9635\uff01\u201d\u8bf4\u7f62\u53c8\u50cf\u7a7f\u82b1\u8774\u8776\u4e00\u822c\uff0c\u5728\u4eba\u7fa4\u4e2d\u7a7f\u6765\u63d2\u53bb\uff0c\u7247\u523b\u4e4b\u540e\uff0c\u53c8\u7ed9\u90a3\u4e9b\u4eba\u89e3\u5f00\u4e86\u7a74\u9053\uff0c\u5b5f \u7984\u51b7\u7b11\u9053\uff1a\u201c\u6211\u4e0d\u9886\u4f60\u7684\u60c5\u3001\u8de8\u4e0a\u9a6c\u80cc\uff1b\u5e26\u4e86\u961f\u4f0d\uff0c\u5f84\u81ea\u9a70\u53bb\u3002 \u3000\u3000\u6768\u4e91\u806a\u62d4\u51fa\u77ed\u5251\uff0c\u51c6\u5907\u6e05\u5175\u4e00\u5230\uff0c\u5c06\u7eb3\u5170\u5c0f\u59d0\u7684\u8eab\u4efd\u8bf4\u660e\uff0c\u81ea\u5df1\u9a6c\u4e0a\u7a81\u56f4\uff0c\u53bb\u627e\u98de\u7ea2\u5dfe \u89e3\u91ca\u3002\u6b63\u76d8\u7b97\u95f4\uff0c\u90a3\u961f\u6e05\u5175\u5df2\u6740\u4e86\u8fc7\u6765\u3002\u524d\u5934\u8dd1\u51fa\u4e24\u4e2a\u4eba\uff0c\u6768\u4e91\u806a\u8d77\u521d\u8fd8\u4ee5\u4e3a\u662f\u6e05\u519b\u7684\u519b \u5b98\uff0c\u8fd1\u5904\u4e00\u770b\uff0c\u59cb\u77e5\u4e0d\u662f\uff0c\u6e05\u519b\u5728\u540e\u9762\u653e\u7bad\uff0c\u8fd9\u4e24\u4eba\u6325\u5251\u62d4\u6253\uff0c\u65f6\u4e0d\u65f6\u8fd8\u56de\u8eab\u53ae\u6740\u4e00\u9635\u3002\u53c8 \u518d\u5954\u9003\u3002 \u3000\u3000\u6e05\u519b\u8d8a\u6765\u8d8a\u8fd1\uff0c\u6768\u4e91\u806a\u5df2\u770b\u5f97\u5206\u660e\uff0c\u8fd9\u4e24\u4eba\u662f\u4e00\u7537\u4e00\u5973\uff0c\u7537\u7684\u4e09\u5341\u591a\u5c81\uff0c\u5112\u751f\u6253\u626e\uff0c\u6b66 \u529f\u6781\u9ad8\uff0c\u5973\u7684\u4e8c\u5341\u6765\u5c81\uff0c\u8eab\u624b\u4e5f\u662f\u4e0d\u5f31\u3002\u6768\u4e91\u806a\u5fc3\u4e2d\u5927\u559c\uff0c\u8fd9\u5973\u7684\u81ea\u5df1\u4e0d\u8ba4\u5f97\uff0c\u7537\u7684\u5374\u662f\u81ea \u5df1\u7684\u597d\u53cb\uff0c\u6b66\u5f53\u6d3e\u7684\u540d\u5bbf\u5353\u4e00\u822a\uff0c\u636e\u5e08\u7236\u8bf4\uff0c\u4ed6\u4e5f\u662f\u56e0\u4e3a\u4e2d\u539f\u7cdc\u70c2\uff0c\u65b9\u4e07\u91cc\u6295\u8352\uff0c\u9690\u8eab\u6f20\u5916 \u7684\u3002\u5e08\u7236\u8fd8\u8bf4\uff0c\u4ed6\u5185\u529f\u7cbe\u6e5b\uff0c\u5e74\u8fd1\u516d\u65ec\uff0c\u770b\u6765\u8fd8\u50cf\u4e09\u5341\u4f59\u5c81\u3002\u6768\u4e91\u806a\u5728\u5929\u5c71\u65f6\uff0c\u66fe\u5c61\u6b21\u89c1\u8fc7 \u4ed6\uff0c\u4ed6\u5e76\u4e0d\u4ee5\u957f\u8f88\u81ea\u5c45\uff0c\u786c\u8981\u6768\u4e91\u806a\u4ee5\u5144\u5f1f\u76f8\u79f0\u3002\u6768\u4e91\u806a\u5f53\u7136\u4e0d\u6562\uff0c\u540e\u6765\u624d\u77e5\u9053\uff0c\u4ed6\u672c\u6765\u8981 \u62dc\u6666\u660e\u7985\u5e08\u4e4b\u95e8\u7684\uff0c\u6666\u660e\u7985\u5e08\u56e0\u4ed6\u65e9\u5df2\u662f\u4e00\u6d3e\u5927\u5e08\uff0c\u4e0d\u613f\u5c45\u4e3a\u5c0a\u957f\u3002\u56e0\u6b64\u5353\u4e00\u822a\u548c\u6666\u660e\u7985\u5e08 \u7684\u4ea4\u60c5\u662f\u8fd1\u4e4e\u5e08\u53cb\u4e4b\u95f4\uff0c\u800c\u5353\u4e00\u822a\u548c\u6768\u4e91\u806a\u7684\u4ea4\u60c5\u4e5f\u662f\u4ecb\u4e4e\u5e08\u53cb\u4e4b\u95f4\u3002 \u3000\u3000\u6768\u4e91\u806a\u4e00\u89c1\u5353\u4e00\u822a\u88ab\u6e05\u5175\u8ffd\u8d76\uff0c\u821e\u8d77\u77ed\u5251\uff0c\u4fbf\u8fce\u4e0a\u53bb\u3002\u5353\u4e00\u822a\u8fd9\u65f6\u4e5f\u8ba4\u51fa\u4e86\u6768\u4e91\u806a\uff0c\u5927 \u559c\u53eb\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u548c\u5979\u654c\u4f4f\u540e\u5934\u90a3\u56db\u6761\u5154\u606f\u3002\u6211\u53bb\u6740\u6563\u6e05\u5175\u3002\u201d\u4e00\u56de\u8eab\uff0c\u5c31\u5411\u654c\u4eba\u51b2\u53bb\uff0c \u6768\u4e91\u806a\u62ac\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u90a3\u961f\u6e05\u5175\uff0c\u7531\u56db\u540d\u519b\u5b98\u5e26\u9886\uff0c\u4e3a\u9996\u90a3\u4eba\u7adf\u662f\u4ee5\u524d\u5728\u6c99\u6f20\u4e2d\u548c\u695a\u662d\u5357\u5408 \u6597\u81ea\u5df1\u7684\u7ebd\u67af\u5e90\uff0c\u8fd9\u65f6\u5ffd\u7136\u542c\u5f97\u80cc\u540e\u7eb3\u5170\u5c0f\u59d0\u53eb\u4e86\u4e00\u58f0\uff0c\u7ebd\u67af\u5e90\u9762\u524d\u6709\u5f02\u8272\uff0c\u6768\u4e91\u806a\u65e0\u6687\u8ffd \u95ee\uff0c\u9f99\u5f62\u98de\u6b65\uff0c\u5251\u968f\u8eab\u8d70\uff0c\u4e00\u7f15\u9752\u5149\uff0c\u5237\u7684\u5411\u7ebd\u67af\u5e90\u523a\u53bb\uff01 \u3000\u3000----------- \u3000\u3000\u98ce\u4e91\u9601 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u585e\u5916\u5947\u4fa0\u4f20\u300b\u2014\u2014\u7b2c\u5341\u516d\u56de\u3000\u591a\u94ce\u8bf4\u4eb2 \u6881\u7fbd\u751f\u300a\u585e\u5916\u5947\u4fa0\u4f20\u300b \u7b2c\u5341\u516d\u56de\u3000\u591a\u94ce\u8bf4\u4eb2 \u3000\u3000\u7ebd\u67af\u5e90\u4e3e\u4e27\u95e8\u632b\u4e00\u6321\uff0c\u6768\u4e91\u806a\u95ea\u8eab\u76f4\u8fdb\uff0c\u77ed\u5251\u75be\u5982\u98ce\u5377\uff0c\u201c\u5580\u5693\u201d\u4e00\u58f0\uff0c\u628a\u7ebd\u67af\u5e90\u4e00\u4e2a \u540c\u4f34\u7684\u5175\u5668\u524a\u6389\uff0c\u65cb\u8eab\u4e00\u638c\uff0c\u53c8\u628a\u53e6\u4e00\u540d\u519b\u5b98\u9707\u51fa\u6570\u4e08\u4ee5\u5916\uff0c\u7b2c\u4e09\u540d\u519b\u5b98\u624b\u4f7f\u4e08\u4e8c\u957f\u67aa\uff0c\u91cd \u4e03\u5341\u4e8c\u65a4\uff0c\u594b\u529b\u4e00\u6311\uff0c\u731b\u7684\u6485\u6765\uff0c\u6768\u4e91\u806a\u907f\u5f00\u67aa\u5c16\uff0c\u5de6\u624b\u75be\u4f38\uff0c\u4e00\u628a\u63b3\u7740\u67aa\u6746\uff0c\u559d\u9053\uff1a \u201c\u5012\uff01\u201d\u4e0d\u6599\u90a3\u519b\u5b98\u662f\u6e05\u519b\u4e2d\u51fa\u540d\u7684\u5927\u529b\u58eb\uff0c\u867d\u7ed9\u6768\u4e91\u806a\u626f\u5f97\u8dc4\u8dc4\u8e09\u8e09\uff0c\u76f4\u8dcc\u8fc7\u6765\uff0c\u5374\u4e95\u672a \u5012\u4e0b\uff0c\u72b9\u5728\u6323\u624e\uff0c\u5c1a\u60f3\u652f\u6491\u3002\u7ebd\u67af\u5e90\u4e58\u52bf\u75be\u5ba1\u8fc7\u6765\uff0c\u4e27\u95e8\u632b\u4e00\u62db\u201c\u4ed9\u59d1\u9001\u5b50\u201d\uff0c\u76f4\u624e\u6768\u4e91\u806a \u7684\u201c\u5206\u6c34\u7a74\u201d\uff0c\u5de6\u638c\u66f4\u8fd0\u8db3\u529b\u6c14\uff0c\u731b\u5288\u6768\u4e91\u806a\u53f3\u80a9\uff0c\u6768\u4e91\u806a\u5927\u559d\u4e00\u58f0\uff0c\u957f\u67aa\u731b\u7684\u5f80\u524d\u4e00\u9001\uff0c \u90a3\u540d\u519b\u5b98\u7981\u4e0d\u4f4f\u6768\u4e91\u806a\u7684\u795e\u529b\uff0c\u60e8\u53eb\u4e00\u58f0\uff0c\u864e\u53e3\u6d41\u8840\uff0c\u7ed9\u81ea\u5df1\u7684\u957f\u67aa\u649e\u51fa\u6570\u4e08\u4ee5\u5916\uff0c\u767b\u65f6\u6655 \u5728\u5730\u4e0a\u3002\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u6768\u4e91\u806a\u53e3\u8eab\u4e00\u5251\u628a\u4e27\u95e8\u632b\u64a9\u4e0a\u534a\u5929\uff0c\u53cd\u624b\u4e00\u638c\u53c8\u8fce\u4e2a\u6b63\u7740\uff0c\u7ebd\u67af \u5e90\u5728\u5173\u5916\u53f7\u79f0\u201c\u94c1\u638c\u201d\uff0c\u7adf\u5403\u4e0d\u4f4f\u6768\u4e91\u806a\u638c\u529b\uff0c\u8eab\u5b50\u50cf\u65ad\u7ebf\u98ce\u7b5d\u4e00\u822c\u9707\u5f97\u817e\u8d77\u4e09\u4e08\u591a\u9ad8\uff0c\u5012 \u7ffb\u51fa\u53bb\uff0c\u5e78\u4ed6\u6b66\u529f\u4e5f\u6709\u76f8\u5f53\u9020\u8be3\uff0c\u5728\u534a\u7a7a\u4e2d\u4e00\u4e2a\u8ddf\u5934\uff0c\u843d\u5728\u4e71\u519b\u4e4b\u4e2d\uff0c\u62a2\u8def\u98de\u9003\u3002 \u3000\u3000\u8fd9\u65f6\u5353\u4e00\u822a\u548c\u90a3\u4e2a\u5c11\u5973\u4ed7\u5251\u6251\u5165\u6e05\u519b\u4e4b\u4e2d\uff0c\u53cc\u5251\u7eb5\u6a2a\u63d2\u970d\uff0c\u628a\u6e05\u5175\u6740\u5f97\u9b3c\u54ed\u795e\u568e\uff0c\u5982\u6c64 \u6cfc\u96ea\uff0c\u6b7b\u7684\u6b7b\uff0c\u4f24\u7684\u4f24\uff0c\u9003\u7684\u9003\uff0c\u4e00\u5927\u961f\u6e05\u5175\u970e\u65f6\u6d88\u6563\uff0c\u8349\u539f\u4e0a\u53c8\u53ea\u5269\u4e0b\u6768\u4e91\u806a\u7b49\u56db\u540d\u7537\u5973\u3002 \u3000\u3000\u5353\u4e00\u822a\u9053\uff1a\u201c\u4e91\u806a\uff0c\u60f3\u4e0d\u5230\u4f60\u529f\u529b\u5982\u6b64\u7cbe\u8fdb\uff01\u201d\u6768\u4e91\u806a\u9053\uff1a\u201c\u8fd8\u671b\u5e08\u53d4\u6559\u8bf2\u3002\u201d\u5353\u4e00\u822a \u671b\u671b\u8f66\u4e0a\u7684\u7eb3\u5170\u660e\u6167\uff0c\u9887\u611f\u60ca\u8bb6\uff0c\u6768\u4e91\u806a\u751f\u6015\u4ed6\u6ecb\u751f\u8bef\u4f1a\uff0c\u6025\u5fd9\u8bf4\u9053\uff0c\u201c\u5979\u5355\u8eab\u4e00\u4eba\uff0c\u79bb\u7fa4 \u6563\u5931\uff0c\u6d41\u6d6a\u5927\u6f20\uff0c\u6211\u60f3\u628a\u5979\u9001\u56de\u53bb\u3002\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u5e94\u8be5\uff01\u8bf4\u6765\u51d1\u5de7\uff0c\u4f60\u9001\u4eba\u6211\u4e5f\u9001\u4eba\u3002\u201d \u8bf4\u7f62\u66ff\u6768\u4e91\u806a\u4ecb\u7ecd\u9053\uff1a\u201c\u8fd9\u4f4d\u59d1\u5a18\u662f\u6211\u6545\u4eba\u7684\u5973\u513f\uff0c\u540d\u5524\u4f55\u7eff\u534e\uff0c\u6211\u8981\u628a\u5979\u9001\u56de\u5173\u5185\u3002\u65e5\u540e \u4f60\u82e5\u89c1\u5979\uff0c\u8fd8\u6258\u4f60\u591a\u591a\u7167\u5e94\u3002\u201d\u8bf4\u7f62\u628a\u624b\u4e00\u4e3e\uff0c\u4e0e\u6768\u4e91\u806a\u5306\u5306\u9053\u522b\uff0c\u5404\u81ea\u8d76\u8def\u3002\u6768\u4e91\u806a\u770b\u5353 \u4e00\u822a\u7709\u76ee\u4e4b\u95f4\u4f3c\u6709\u9690\u5fe7\uff0c\u800c\u4e14\u4ee5\u4ed6\u548c\u81ea\u5df1\u7684\u4e24\u4ee3\u4ea4\u60c5\uff0c\u82e5\u5728\u5e73\u65e5\uff0c\u4e00\u5b9a\u4e0d\u80af\u5c31\u8fd9\u6837\u5306\u52ff\u9053 \u522b\uff0c\u7eb5\u7b97\u5728\u767e\u5fd9\u4e4b\u4e2d\uff0c\u4e5f\u4f1a\u4e00\u53d9\u5951\u9614\uff0c\u800c\u73b0\u5728\u4ed6\u5374\u8fde\u5e08\u7236\u4e5f\u4e0d\u63d0\u8d77\u5c31\u8d70\u4e86\uff0c\u8fd9\u53ef\u771f\u662f\u602a\u4e8b\u3002 \u4ed6\u60f3\u4e0d\u900f\u50cf\u5353\u4e00\u822a\u6b66\u529f\u90a3\u6837\u9ad8\u7684\u4eba\uff0c\u8fd8\u6709\u4ec0\u4e48\u5fe7\u60e7\u3002\u4ed6\u5374\u4e0d\u77e5\u5353\u4e00\u822a\u6b64\u6b21\u5306\u5fd9\u8d76\u8def\uff0c\u4e43\u662f\u6015 \u767d\u53d1\u9b54\u5973\u6765\u627e\u4ed6\u7684\u6666\u6c14\u3002 \u3000\u3000\u5353\u4e00\u822a\u4e0e\u767d\u53d1\u9b54\u5973\u4e4b\u4e8b\u6682\u4e14\u4e0d\u63d0\uff0c\u4e14\u8bf4\u6768\u4e91\u806a\u4e0e\u7eb3\u5170\u660e\u6167\u518d\u8d70\u4e86\u51e0\u65e5\uff0c\u5230\u4e86\u4f0a\u7281\u57ce\u5916\u3002 \u8fd9\u65f6\u7eb3\u5170\u660e\u6167\u5df2\u5b8c\u5168\u5eb7\u590d\uff0c\u8f7b\u63a0\u4e91\u9b13\uff0c\u5bf9\u6768\u4e91\u806a\u7b11\u9053\uff1a\u201c\u4f60\u5165\u57ce\u4e0d\u65b9\u4fbf\u4e86\uff0c\u665a\u4e0a\u6211\u548c\u4f60\u7528\u591c \u884c\u672f\u56de\u53bb\u5427\uff01\u8fd9\u8f86\u9a6c\u8f66\uff0c\u4e0d\u8981\u5b83\u4e86\uff01\u201d\u6768\u4e91\u806a\u5fc3\u5982\u8f98\u8f73\uff0c\u6709\u5378\u4e0b\u91cd\u62c5\u4e4b\u611f\uff0c\u4e5f\u6709\u9aa4\u4f24\u79bb\u522b\u4e4b \u60b2\uff0c\u534a\u664c\u8bf4\u9053\uff1a\u201c\u4f60\u81ea\u5df1\u56de\u53bb\u5427\uff0c\u6211\u8d70\u4e86\uff0c\u4f60\u591a\u591a\u4fdd\u91cd\uff01\u201d\u7eb3\u5170\u660e\u6167\u4e00\u628a\u5c06\u4ed6\u62c9\u4f4f\uff0c\u5a07\u7b11 \u9053\uff1a\u201c\u4f60\u4e0d\u8981\u8d70\uff0c\u6211\u4e0d\u51c6\u4f60\u8d70\uff0c\u4f60\u4e00\u5b9a\u8981\u966a\u6211\u56de\u53bb\u3002\u4f60\u4e0d\u7528\u5bb3\u6015\uff0c\u6211\u4eec\u7684\u5c06\u519b\u5e9c\u5f88\u5927\uff0c\u4f60\u4e0d \u4f1a\u89c1\u7740\u6211\u7684\u7238\u7238\u7684\u3002\u6211\u6709\u4e00\u4e2a\u5988\u5988\uff0c\u5bf9\u6211\u975e\u5e38\u4e4b\u597d\uff0c\u5979\u4f4f\u5728\u5e9c\u91cc\u4e1c\u8fb9\u5934\u7684\u4e00\u4e2a\u9662\u5b50\u91cc\uff0c\u72ec\u81ea \u5360\u6709\u4e09\u95f4\u5c4b\u5b50\u5462\uff01\u59d4\u5c48\u4f60\u4e00\u4e0b\uff0c\u6211\u5e26\u4f60\u89c1\u5979\uff0c\u8981\u5979\u8ba4\u4f60\u505a\u8fdc\u623f\u4f84\u5b50\uff0c\u4f60\u4e0d\u8981\u4e71\u8d70\u52a8\u4e00\u5305\u6ca1\u6709 \u4eba\u770b\u7834\uff01\u201d\u6768\u4e91\u806a\u6447\u6447\u5934\u9053\uff1a\u201c\u4e0d\u884c\uff0c\u6211\u8fd8\u8981\u53bb\u627e\u54c8\u8428\u514b\u4eba\u3002\u201d\u7eb3\u5170\u660e\u6167\u6c89\u7740\u8138\u9053\uff1a\u201c\u8fd8\u6709 \u98de\u7ea2\u5dfe\u662f\u4e0d\u662f\uff1f\u201d\u6768\u4e91\u806a\u6b63\u8272\u8bf4\u9053\uff1a\u201c\u662f\u7684\uff0c\u6211\u4e3a\u4ec0\u4e48\u4e0d\u80fd\u627e\u5979\uff1f\u6211\u8981\u77e5\u9053\u5979\u4eec\u5357\u50f5\u5404\u65cf\u6253 \u5b8c\u4ed7\u540e\uff0c\u73b0\u5728\u5728\u4ec0\u4e48\u5730\u65b9\uff0c\u662f\u600e\u4e48\u4e2a\u60c5\u666f\uff1f\u201d\u7eb3\u5170\u660e\u6167\u53c8\u4f38\u4f38\u820c\u5934\u7b11\u9053\uff1a\u201c\u5927\u7237\uff0c\u4e00\u53e5\u6d3b\u5c31 \u628a\u4f60\u62db\u607c\u4e86\u662f\u4e0d\u662f\uff1f\u201d\u8c01\u8bf4\u4f60\u4e0d\u8be5\u53bb\u627e\u98de\u7ea2\u5dfe\u5462\uff0c\u53ea\u662f\u5927\u6218\u4e4b\u540e\uff0c\u8352\u6f20\u4e4b\u4e2d\uff0c\u662f\u90a3\u4e48\u5bb9\u6613\u627e \u5417\uff1f\u4e0d\u5982\u6682\u4f4f\u5728\u6211\u8fd9\u513f\uff0c\u6211\u7236\u4eb2\u7684\u6d88\u606f\u7075\u901a\uff0c\u5404\u5730\u90fd\u6709\u519b\u4e66\u7ed9\u4ed6\uff0c\u4ed6\u4e00\u5b9a\u4f1a\u77e5\u9053\u5357\u7586\u5404\u65cf\u5728 \u4ec0\u4e48\u5730\u65b9\u7684\uff0c\u6211\u7ed9\u4f60\u6253\u63a2\uff0c\u628a\u519b\u60c5\u90fd\u544a\u8bc9\u4f60\u3002\u5230\u4f60\u77e5\u9053\u4f60\u7684\u98de\u7ea2\u5dfe\u4e0b\u843d\u65f6\uff0c\u518d\u53bb\u627e\u5979\u4e5f\u4e0d\u4e3a \u8fdf\u5440\uff01\u201d\u6768\u4e91\u806a\u201c\u5478\u201d\u4e86\u4e00\u58f0\uff0c\u4f46\u968f\u5373\u60f3\u5230\uff0c\u5979\u8bf4\u5f97\u4e5f\u6709\u9053\u7406\u3002\u5c31\u8d81\u8fd9\u4e2a\u673a\u4f1a\uff0c\u63a2\u63a2\u654c\u4eba\u7684 \u60c5\u5f62\u4e5f\u597d\u3002 \u3000\u3000\u90a3\u665a\u7eb3\u5170\u660e\u6167\u679c\u7136\u5e26\u4ed6\u6084\u6084\u8fdb\u5165\u5e9c\u4e2d\uff0c\u627e\u5230\u5976\u5988\uff0c\u4e00\u8bf4\u4e4b\u4e0b\uff0c\u628a\u5976\u5988\u5413\u5f97\u4ec0\u4e48\u4f3c\u7684\u3002\u4f46 \u8fd9\u4e2a\u5976\u5988\u5e9e\u7231\u660e\u6167\uff0c\u6709\u5982\u4eb2\u751f\uff0c\u7981\u4e0d\u4f4f\u5979\u7684\u82e6\u82e6\u54c0\u6c42\uff0c\u7ec8\u4e8e\u7b54\u5e94\u4e86\uff0c\u4f46\u5976\u5988\u4e5f\u6709\u6761\u4ef6\uff0c\u8981\u6768 \u4e91\u806a\u53ea\u80fd\u5728\u4e09\u95f4\u5c4b\u5185\u8d70\u52a8\u3002\u6768\u4e91\u806a\u4e5f\u7b54\u5e94\u4e86\u3002\u7b2c\u4e8c\u5929\u4e00\u65e9\u3002\u7eb3\u5170\u660e\u6167\u53c8\u6084\u6084\u6e9c\u51fa\u57ce\u5916\uff0c\u9a7e\u7740 \u9a6c\u8f66\u56de\u6765\uff0c\u5979\u89c1\u4e86\u7236\u4eb2\u4e4b\u540e\u3002\u8c0e\u8bf4\u662f\u4ece\u4e71\u519b\u4e2d\u9003\u51fa\u6765\u7684\uff0c\u7eb3\u5170\u79c0\u5409\u4e00\u5411\u77e5\u9053\u4ed6\u5973\u513f\u7684\u6b66\u529f\uff0c \u679c\u7136\u4e0d\u8d77\u7591\u5fc3\u3002 \u3000\u3000\u4e00\u6643\u53c8\u8fc7\u4e86\u534a\u6708\uff0c\u7eb3\u5170\u660e\u6167\u8fd8\u6ca1\u6709\u63a2\u542c\u51fa\u98de\u7ea2\u5dfe\u548c\u5979\u65cf\u4eba\u7684\u4e0b\u843d\uff0c\u53e6\u4e00\u4ef6\u7a81\u5982\u5176\u6765\u7684 \u4e8b\uff0c\u5374\u50cf\u5927\u5c71\u4e00\u6837\u538b\u5728\u5979\u7684\u5fc3\u5934\uff0c\u90d1\u91cd\u538b\u53c8\u4e00\u6b21\u7684\u4f7f\u5979\u9677\u5165\u75db\u82e6\u7684\u6df7\u4e71\u4e4b\u4e2d\uff0c\u5c31\u50cf\u4e0a\u4e00\u6b21\u81ea \u5df1\u6000\u7591\u6768\u4e91\u806a\u7231\u4e0a\u4e86\u98de\u7ea2\u5dfe\u90a3\u65f6\u5019\u4e00\u6837\uff0c\u8fd9\u79cd\u5fc3\u5934\u7684\u91cd\u538b\u600e\u6837\u4e5f\u4e0d\u80fd\u6d88\u9664\u3002 \u3000\u3000\u4e0a\u4e00\u6b21\u5728\u5979\u5fc3\u5934\u9020\u6210\u91cd\u538b\u7684\u662f\u98de\u7ea2\u5dfe\u7684\u5f71\u5b50\uff0c\u800c\u8fd9\u4e00\u6b21\u5374\u662f\u4e00\u4f4d\u5c06\u519b\u5e9c\u4e2d\u7684\u8d35\u5ba2\uff01 \u3000\u3000\u5728\u5979\u56de\u6765\u4e4b\u540e\u5341\u591a\u5929\uff0c\u5c06\u519b\u5e9c\u4e2d\u5230\u4e86\u4e00\u4f4d\u8fdc\u65b9\u6765\u7684\u8d35\u5ba2\uff0c\u8fd9\u4f4d\u8d35\u5ba2\u53eb\u505a\u591a\u94ce\uff0c\u4eca\u5e74\u4ec5\u4ec5 \u4e8c\u5341\u4e94\u5c81\uff0c\u53ef\u662f\u5df2\u88ab\u4efb\u4e3a\u5b9a\u8fdc\u5c06\u519b\uff0c\u5b98\u804c\u6bd4\u81ea\u5df1\u7684\u7236\u4eb2\u8fd8\u5927\u3002\u800c\u4e14\uff0c\u4e0d\u5355\u5355\u662f\u5e74\u5c11\u9ad8\u5b98\uff0c\u4ed6 \u8fd8\u662f\u4e00\u4f4d\u4eb2\u738b\u7684\u513f\u5b50\uff0c\u5728\u7687\u5e1d\u8ddf\u524d\u751a\u4e3a\u5f97\u5ba0\uff0c\u90a3\u662f\u7eb3\u5170\u79c0\u5409\u8fdc\u6bd4\u4e0d\u4e0a\u7684\u3002\u4f46\u591a\u94ce\u4e4b\u80fd\u591f\u5e74\u5c11 \u9ad8\u5b98\uff0c\u5374\u5e76\u4e0d\u662f\u5168\u9760\u4ed6\u7236\u4eb2\u7684\u529b\u91cf\uff0c\u4ed6\u4e43\u662f\u65d7\u4eba\u4e2d\u6570\u4e00\u6570\u4e8c\u7684\u597d\u6c49\uff0c\u81ea\u5c0f\u5c31\u80fd\u62c9\u5f3a\u5f13\uff0c\u5fa1\u9a7e \u9a6c\uff0c\u9a91\u672f\u5251\u672f\uff0c\u5728\u516b\u65d7\u5175\u4e2d\u9996\u5c48\u4e00\u6307\u3002\u4e09\u5e74\u524d\u4ed6\u968f\u7687\u5e1d\u897f\u5f81\uff0c\u5e73\u5b9a\u4e86\u51c6\u5676\u5c14\u548c\u5927\u5c0f\u91d1\u5ddd\uff0c\u83ab \u540d\u8fdc\u64ad\uff0c\u6ee1\u671d\u6587\u6b66\uff0c\u8c01\u90fd\u7fa1\u6155\u4ed6\u3002 \u3000\u3000\u4ed6\u5e74\u7eaa\u9752\u9752\uff0c\u5c1a\u672a\u5b9a\u4eb2\u3002\u8d35\u65cf\u5927\u81e3\uff0c\u6765\u738b\u5e9c\u8bf4\u4eb2\u7684\uff0c\u771f\u662f\u7edc\u7ece\u4e0d\u7edd\u3002\u53ef\u662f\u4ed6\u773c\u754c\u5f88\u9ad8\u3002 \u65e0\u4e00\u5f53\u610f\uff1a\u4ed6\u7406\u60f3\u4e2d\u7684\u59bb\u5b50\u662f\u6587\u6b66\u5168\u624d\u7f8e\u5982\u5929\u4ed9\u7684\u4eba\uff0c\u53ef\u662f\u8fd9\u6837\u7684\u4eba\u5374\u54ea\u91cc\u53bb\u627e\uff01 \u3000\u3000\u81ea\u5341\u4e03\u516b\u5c81\u8d77\uff0c\u5c31\u6709\u4eba\u7ed9\u4ed6\u8bf4\u4eb2\uff0c\u8f6c\u77ac\u4e4b\u95f4\u5df2\u662f\u4e8c\u5341\u4e94\u5c81\u4e86\uff0c\u5728\u6e05\u521d\u7684\u65f6\u5019\uff0c\u7537\u5b50\u4e8c\u5341 \u4e94\u5c81\u5c1a\u672a\u5b9a\u4eb2\uff0c\u505a\u7236\u6bcd\u7684\u53ef\u62c5\u5fc3\u3002\u4ed6\u7684\u7236\u4eb2\u9102\u4eb2\u738b\u4e00\u6253\u542c\uff0c\u542c\u8bf4\u4f0a\u7281\u5c06\u519b\u7eb3\u5170\u79c0\u5409\u5e9c\u6709\u4e00\u4e2a \u5973\u513f\u3002\u7f8e\u8273\u806a\u660e\uff0c\u5728\u65d7\u4eba\u4e4b\u4e2d\uff0c\u582a\u5938\u7b2c\u4e00\u3002\u4eca\u5e74\u4e5f\u5feb\u8fd1\u4e8c\u5341\uff0c\u4e5f\u662f\u8fd8\u672a\u5b9a\u4eb2\u3002\u4ee5\u524d\u56e0\u4e3a\u660e\u6167 \u8fd8\u5c0f\uff0c\u800c\u7eb3\u5170\u79c0\u5409\u53c8\u8fdc\u5904\u585e\u5916\uff0c\u6240\u4ee5\u591a\u94ce\u7684\u7236\u4eb2\u5e76\u672a\u6ce8\u610f\u53ca\u5979\u3002\u800c\u4eca\u60f3\u8d77\u4e86\u5979\uff0c\u89c9\u5f97\u9664\u4e86 \u5979\uff0c\u6050\u6015\u518d\u96be\u627e\u9002\u5408\u7684\u4eba\u4e86\u3002 \u3000\u3000\u591a\u94ce\u7684\u7236\u6bcd\u548c\u4ed6\u4e00\u8bf4\uff0c\u591a\u94ce\u4e5f\u7d20\u95fb\u7eb3\u5170\u660e\u6167\u4e4b\u540d\uff0c\u5c24\u5176\u591a\u94ce\u7684\u4e00\u4e2a\u5e08\u53d4\u7ebd\u67af\u5e90\u5c31\u5728\u7eb3\u5170 \u79c0\u5409\u5e10\u4e0b\uff0c\u591a\u94ce\u5728\u9752\u6d77\u6253\u51c6\u5676\u5c14\u65cf\u65f6\uff0c\u7ebd\u67af\u5e90\u66fe\u4ece\u65b0\u7586\u6765\u89c1\u4ed6\uff0c\u8bf4\u8d77\u7eb3\u5170\u660e\u6167\uff0c\u7ebd\u67af\u5e90\u628a\u5979 \u5938\u5f97\u4e0d\u5f97\u4e86\u3002\u8bf4\u5979\u4e0d\u4f46\u7f8e\u82e5\u5929\u4ed9\uff0c\u5c31\u662f\u6b66\u529f\u4e5f\u8fdc\u5728\u516b\u65d7\u7684\u4e00\u822c\u52c7\u58eb\u4e4b\u4e0a\u3002\u4ed6\u8fd8\u7b11\u9053\uff1a\u201c\u5c06 \u519b\uff0c\u6211\u770b\u5979\u7684\u6b66\u529f\u6bd4\u4f60\u8fd8\u597d\u5462\uff01\u201d\u628a\u591a\u94ce\u542c\u5f97\u5fc3\u75d2\u75d2\u7684\u3002 \u3000\u3000\u53ef\u662f\u591a\u94ce\u672a\u4eb2\u773c\u89c1\u8fc7\uff0c\u603b\u6709\u70b9\u4e0b\u5927\u653e\u5fc3\uff0c\u7236\u6bcd\u8ddf\u4ed6\u63d0\u8d77\uff0c\u4ed6\u8bf4\uff1a\u201c\u6162\u70b9\u63d0\u4eb2\u5427\uff01\u4f8d\u6211\u5230 \u65b0\u7586\u53bb\u770b\u770b\u518d\u8bf4\u3002\u201d\u6070\u5de7\u65b0\u7586\u5404\u65cf\uff0c\u6297\u6e05\u751a\u4e3a\u6fc0\u70c8\u3002\u7eb3\u5170\u79c0\u5409\u5728\u4f0a\u7281\u7edf\u5175\uff0c\u867d\u7136\u8fde\u6253\u80dc\u4ed7\uff0c \u53ef\u662f\u4ecd\u65e0\u6cd5\u628a\u65b0\u7586\u7267\u6c11\u7684\u6297\u6e05\u8fd0\u52a8\u538b\u5e73\u3002\u591a\u94ce\u81ea\u8bf7\u5230\u65b0\u7586\u53bb\u5de1\u9605\u4e00\u6b21\uff0c\u7687\u5e1d\u5927\u559c\uff0c\u9a6c\u4e0a\u5c01\u4ed6 \u4e3a\u94a6\u5dee\u5927\u81e3\uff0c\u5230\u65b0\u7586\u53bb\u89c6\u5bdf\u519b\u52a1\u3002\u7687\u5e1d\u8fd8\u8bf4\uff0c\u4f60\u662f\u54b1\u4eec\u6ee1\u4eba\u4e2d\u7684\u7b2c\u4e00\u6d41\u5c06\u6750\uff0c\u53bb\u770b\u4e00\u6b21\uff0c\u66ff \u7eb3\u5170\u79c0\u5409\u51fa\u51fa\u4e3b\u610f\u4e5f\u597d\u3002\u7687\u5e1d\u5374\u4e0d\u77e5\u9053\u591a\u94ce\u5230\u65b0\u7586\u53bb\uff0c\u53e6\u6709\u6df1\u5fc3\u3002 \u3000\u3000\u591a\u94ce\u5230\u4e86\u65b0\u7586\u4f0a\u7281\u4e4b\u540e\uff1a\u4f4f\u5728\u5c06\u519b\u5e9c\u4e2d\uff0c\u4ed6\u662f\u7eb3\u5170\u79c0\u5409\u7684\u8d35\u5ba2\uff0c\u53c8\u662f\u4ed6\u7684\u4e0a\u53f8\uff0c\uff08\u4ed6\u4ee5 \u94a6\u5dee\u5927\u81e3\u7684\u8eab\u4efd\uff0c\u5728\u65b0\u7586\u671f\u95f4\uff0c\u7eb3\u5170\u79c0\u5409\u8981\u542c\u4ed6\u8c03\u5ea6\u3002\uff09\u7eb3\u5170\u79c0\u5409\u81ea\u7136\u628a\u4ed6\u5949\u627f\u5f97\u4e86\u4e0d\u5f97\uff0c \u7ebd\u67af\u5e90\u731c\u77e5\u4ed6\u5e08\u4f84\u7684\u6765\u610f\uff0c\u6084\u6084\u5730\u5bf9\u7eb3\u5170\u79c0\u5409\u9053\uff1a\u201c\u5c06\u519b\u5927\u559c\u5440\uff01\u5c0f\u738b\u7237\u8fd8\u672a\u5b9a\u4eb2\uff0c\u548c\u660e\u6167 \u5c0f\u59d0\u53ef\u4e0d\u6070\u662f\u4e00\u5bf9\uff1f\u201d\u7eb3\u5170\u79c0\u5409\u4e00\u9897\u5fc3\u6251\u6251\u5730\u8df3\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u600e\u4e48\u9ad8\u6500\u5f97\u4e0a\uff1f\u201d\u7ebd\u67af\u5e90\u9053\uff1a \u201c\u53ea\u8981\u5c06\u519b\u613f\u610f\uff0c\u8fd9\u4e8b\u5c31\u6210\u4e86\u5341\u4e4b\u516b\u4e5d\uff08\u5176\u4ed6\u7684\u5305\u5728\u6211\u7684\u8eab\u4e0a\uff0c\uff09\u4ed6\u867d\u7136\u5c0a\u8d35\uff0c\u8bf4\u8d77\u6765\u603b\u8fd8 \u662f\u6211\u7684\u5e08\u4f84\uff0c\u6211\u4e00\u8bf4\u51c6\u6210\u3002\u201d\u5176\u5b9e\u4ed6\u65e9\u5df2\u6599\u5230\u591a\u94ce\u5fc3\u610f\u3002\u8fd9\u4e00\u4e2a\u73b0\u6210\u5a92\u4eba\uff0c\u81ea\u4e0d\u59a8\u62a2\u6765\u505a\u3002 \u7eb3\u5170\u79c0\u5409\u53c8\u9053\uff1a\u201c\u9102\u4eb2\u738b\uff08\u591a\u94ce\u4e4b\u7236\uff09\u8fdc\u5728\u5317\u4eac\uff0c\u96be\u9053\u6211\u4eec\u5728\u8fd9\u8fb9\u585e\u4e4b\u5730\uff0c\u7a81\u7136\u5411\u4ed6\u63d0 \u4eb2\uff1f\u201d\u7ebd\u67af\u5e90\u9053\uff1a\u201c\u4e5f\u4e0d\u7528\u8fd9\u6837\u6025\uff0c\u8ba9\u4ed6\u4eec\u5148\u89c1\u89c1\u9762\uff0c\u6211\u62c5\u4fdd\u6211\u90a3\u5e08\u4f84\u56de\u4eac\u4e4b\u540e\uff0c\u8001\u738b\u7237\u4e00 \u5b9a\u6d3e\u4eba\u6765\u5411\u4f60\u6c42\u4eb2\u3002\u201d \u3000\u3000\u7eb3\u5170\u660e\u6167\u867d\u7136\u77e5\u9053\u6709\u4e2a\u94a6\u5dee\u5927\u81e3\u53eb\u505a\u591a\u94ce\u7684\u524d\u6765\u5de1\u9605\uff0c\u8d77\u521d\u5e76\u4e0d\u653e\u5728\u5fc3\u4e0a\u3002\u4e00\u65e5\u7236\u4eb2\u53eb \u5979\u5230\u540e\u82b1\u56ed\u53bb\u73a9\uff0c\u7236\u5973\u4fe9\u8d70\u5230\u4e86\u56ed\u5b50\u91cc\u7684\u7ec3\u6b66\u573a\uff0c\u7eb3\u5170\u79c0\u5409\u7b11\u9053\uff1a\u201c\u5973\u513f\uff0c\u6211\u548c\u4f60\u6bd4\u6bd4\u7bad \u6cd5\u3002\u201d\u660e\u6167\u89c1\u7236\u4eb2\u8fd9\u6837\u9ad8\u5174\uff0c\u5a07\u7b11\u9053\uff1a\u201c\u54ce\u5440\uff01\u7238\u7238\u8981\u8f83\u91cf\u6211\u4e86\uff0c\u597d\uff0c\u597d\uff0c\u6bd4\u5c31\u6bd4\u5427\uff0c\u5982\u679c \u6211\u8d62\u4e86\u7238\u7238\u7ed9\u6211\u4ec0\u4e48\uff1f\u201d\u7eb3\u5170\u79c0\u5409\u9053\uff1a\u201c\u7ed9\u4f60\u4e00\u4ef6\u6700\u597d\u7684\u4e1c\u897f\uff0c\u4ee4\u4f60\u4e00\u4e16\u8363\u534e\u5bcc\u8d35\uff01\u201d\u660e\u6167 \u9053\uff1a\u201c\u7238\u7238\u4f60\u4e71\u8bf4\uff0c\u54ea\u6709\u8fd9\u6837\u7684\u597d\u4e1c\u897f\uff0c\u6211\u4e5f\u4e0d\u7a00\u7f55\u54e9\uff01\u6211\u8d62\u4e86\u4f60\u628a\u730e\u5f97\u7684\u90a3\u5f20\u7280\u725b\u76ae\u9001\u7ed9 \u6211\u5427\uff01\u201d\u79c0\u5409\u9053\uff1a\u201c\u4e00\u5f20\u7280\u725b\u76ae\u7b97\u5f97\u4ec0\u4e48\uff1f\u597d\uff01\u54b1\u4eec\u5c04\u5427\uff01\u201d\u4ed6\u5f20\u5f13\u5f15\u7bad\uff0c\u5728\u767e\u6b65\u4e4b\u5916\uff0c \u53df\uff01\u53df\uff01\u81fe\uff01\u4e09\u7bad\u8fde\u4e2d\u7ea2\u5fc3\uff0c\u80cc\u8fc7\u5934\u6765\uff0c\u63a5\u8fde\u4e09\u7bad\u3002\u53c8\u662f\u8fde\u4e2d\u7ea2\u5fc3\uff0c\u63b7\u5f13\u957f\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u5973 \u513f\uff0c\u4f60\u770b\u4f60\u7238\u7238\u8fd8\u672a\u8001\u5427\uff01\u201d \u3000\u3000\u7eb3\u5170\u660e\u6167\u7b11\u9053\uff1a\u201c\u7238\u7238\u5f53\u7136\u4e0d\u7740\uff0c\u7bad\u6cd5\u597d\u5f97\u5f88\u5462\uff01\u53ef\u662f\u5973\u513f\u4e5f\u4e0d\u4f1a\u4e22\u4f60\u7684\u8138\uff0c\u4f60\u770b\u770b\u6211 \u7684\u5427\uff01\u201d\u5979\u5728\u5730\u4e0a\u62fe\u8d77\u5f13\u7bad\uff0c\u81fe\u7684\u7bad\u4e00\u5c04\u4e0a\u9ad8\u7a7a\uff0c\u8ddf\u7740\u53c8\u662f\u4e00\u7bad\uff0c\u7b2c\u4e00\u652f\u7bad\u521a\u521a\u843d\u4e0b\uff0c\u7ed9\u7b2c \u4e8c\u652f\u7bad\u5c04\u4e2a\u6b63\u7740\uff0c\u4e24\u7bad\u4e00\u78b0\uff0c\u53c8\u518d\u5347\u9ad8\uff0c\u7136\u540e\u98de\u843d\uff0c\u7eb3\u5170\u660e\u6167\u82e5\u4e0d\u7ecf\u610f\u7684\u624b\u4e0b\u505c\u5c04\uff0c\u8fde\u5c04\u516d \u7bad\uff0c\u6bcf\u4e00\u652f\u90fd\u8ddf\u4e0a\u4e00\u652f\u78b0\u4e2a\u6b63\u7740\uff01 \u3000\u3000\u201c\u771f\u597d\u7bad\u6cd5\uff01\u201d\u5728\u7eb3\u5170\u660e\u6167\u5a07\u7b11\u58f0\u4e2d\uff0c\u82b1\u6728\u4e1b\u4e2d\u84e6\u5730\u8f6c\u51fa\u4e24\u4e2a\u6c49\u5b50\uff0c\u4e00\u4e2a\u662f\u7ebd\u67af\u5e90\uff0c\u4e00 \u4e2a\u662f\u591a\u94ce\u3002\u7eb3\u5170\u660e\u6167\u89c1\u4e86\u7ebd\u67af\u5e90\uff0c\u60f3\u8d77\u90a3\u65e5\u81ea\u5df1\u548c\u6768\u4e91\u806a\u540c\u8f66\uff0c\u7ed9\u4ed6\u649e\u7740\u4e4b\u4e8b\uff0c\u867d\u7136\u4e0d\u77e5\u9053 \u4ed6\u5f53\u65f6\u6709\u5426\u770b\u6e05\uff0c\u53ef\u662f\u9762\u8272\u5df2\u662f\u5927\u53d8\u3002\u7eb3\u5170\u79c0\u5409\u62c9\u7740\u5979\uff0c\u6b63\u60f3\u4ecb\u7ecd\u5979\u89c1\u591a\u94ce\uff0c\u5979\u5df2\u84e6\u7136\u6323\u8131 \u4e86\u624b\uff0c\u4e00\u6e9c\u70df\u5730\u8dd1\u4e86\u3002\u79c0\u5409\u987f\u8db3\u9a82\u9053\uff1a\u201c\u771f\u6ca1\u6709\u89c4\u77e9\u3002\u738b\u7237\u8bf7\u522b\u89c1\u602a\u3001\u5973\u513f\u5bb6\u4e0d\u61c2\u4e8b\uff0c\u53c8\u6015 \u7f9e\uff0c\u5979\u4e0d\u77e5\u4f60\u662f\u738b\u7237\uff0c\u4e0d\u6562\u89c1\u751f\u4eba\u54e9\uff01\u201d\u5176\u5b9e\u7eb3\u5170\u660e\u6167\u7ecf\u5e38\u5728\u8349\u539f\u6e38\u730e\uff0c\u5979\u54ea\u91cc\u4f1a\u50cf\u6c49\u4eba\u4e00 \u6837\uff0c\u8bb2\u7a76\u7537\u5973\u6388\u53d7\u4e0d\u4eb2\uff0c\u662f\u79c0\u5409\u6545\u610f\u628a\u5979\u8bf4\u5f97\u50cf\u6c49\u4eba\u7684\u5927\u5bb6\u95fa\u79c0\u7f62\u4e86\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u591a\u94ce\u9b42\u9b44\u5df2\u98de\u81f3\u4e5d\u9704\u4e91\u5916\uff0c\u4ed6\u7edd\u6599\u4e0d\u5230\u4e16\u95f4\u771f\u7684\u6709\u8fd9\u6837\u7f8e\u82e5\u5929\u4ed9\u7684\u5c11\u5973\uff0c\u8fd8\u6709\u8fd9 \u6837\u9ad8\u7684\u6b66\u827a\uff01\u4ed6\u6839\u672c\u542c\u4e0d\u8fdb\u7eb3\u5170\u79c0\u5409\u8bf4\u4e9b\u4ec0\u4e48\u3002 \u3000\u3000\u518d\u8bf4\u7eb3\u5170\u660e\u6167\u8dd1\u56de\u53bb\u540e\uff0c\u5728\u5976\u5988\u5c4b\u4e2d\uff0c\u6084\u6084\u5730\u5bf9\u6768\u4e91\u806a\u9053\uff1a\u201c\u6211\u89c1\u7740\u90a3\u4e2a\u4ec0\u4e48\u591a\u94ce\u4e86\uff0c \u4ed6\u548c\u4f60\u4e00\u6837\u5f88\u5e74\u9752\u54e9\uff01\u201d\u6768\u4e91\u806a\u54ac\u7259\u5207\u9f7f\u9053\uff1a\u201c\u8fd9\u4e2a\u6df7\u86cb\uff0c\u4ed6\u6765\u65b0\u7586\u5e72\u5417\uff1f\u6562\u60c5\u53c8\u662f\u6765\u5c60\u6740 \u7267\u6c11\u4e86\uff0c\u54fc\uff0c\u6211\u8981\u628a\u4ed6\u523a\u4e2a\u900f\u660e\u5927\u7a9f\u7abf\uff01\u201d \u3000\u3000----------- \u3000\u3000\u98ce\u4e91\u9601 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u585e\u5916\u5947\u4fa0\u4f20\u300b\u2014\u2014\u7b2c\u5341\u4e03\u56de\u3000\u751f\u79bb\u6b7b\u522b \u6881\u7fbd\u751f\u300a\u585e\u5916\u5947\u4fa0\u4f20\u300b \u7b2c\u5341\u4e03\u56de\u3000\u751f\u79bb\u6b7b\u522b \u3000\u3000\u7eb3\u5170\u660e\u6167\u4f38\u4f38\u820c\u5934\u9053\uff1a\u201c\u54ce\u54df\uff0c\u8fd9\u6837\u72e0\uff01\u201d\u6768\u4e91\u806a\u677f\u7740\u9762\u5b54\uff0c\u4e0d\u4f5c\u4e00\u58f0\u3002\u7eb3\u5170\u660e\u6167\u62b1\u7740 \u4ed6\u7684\u8eab\u8eaf\uff0c\u6447\u4e86\u4e24\u6447\uff0c\u6492\u5a07\u7684\u8bf4\u9053\uff1a\u201c\u4e0d\u63d0\u4ed6\u4e86\uff0c\u522b\u751f\u6c14\u5566\uff0c\u7ed9\u6211\u8bb2\u4e2a\u6545\u4e8b\u597d\u4e0d\u597d\uff1f\u201d\u6768\u4e91 \u806a\u5657\u55e4\u4e00\u58f0\u7b11\u4e86\u51fa\u6765\uff0c\u7eb3\u5170\u660e\u6167\u4e58\u673a\u529d\u9053\uff1a\u201c\u4f60\u5355\u8eab\u5728\u8fd9\u91cc\uff0c\u5371\u9669\u5f97\u5f88\u3002\u4f60\u8fd8\u8981\u505a\u597d\u591a\u4e8b \u60c5\uff0c\u72af\u4e0d\u7740\u548c\u591a\u94ce\u53bb\u62fc\u554a\uff01\u5341\u4e2a\u591a\u94ce\u4e5f\u6bd4\u4e0d\u4e0a\u4e00\u4e2a\u4f60\uff0c\u4f60\u542c\u6211\u8bf4\uff0c\u4e0d\u8981\u53bb\u5e72\u50bb\u4e8b\u60c5\uff01\u201d \u3000\u3000\u6768\u4e91\u806a\u7684\u5fc3\u751c\u751c\u7684\uff0c\u611f\u5230\u4e00\u79cd\u5c11\u5973\u7684\u5173\u6000\u3002\u8fd9\u6837\u7684\u5173\u6000\u5728\u98de\u7ea2\u5dfe\u5904\u9886\u7565\u4e0d\u5230\u3002\u98de\u7ea2\u5dfe\u7f3a \u4e4f\u5c11\u5973\u7684\u6e29\u67d4\u672c\u8d28\uff0c\u5979\u8fd8\u4e0d\u61c2\u5f97\u600e\u6837\u8868\u73b0\u81ea\u5df1\u7ea4\u7ec6\u7684\u611f\u60c5\u3002\u5ffd\u7136\u95f4\uff0c\u4e00\u79cd\u5e78\u798f\u4e4b\u611f\u50cf\u7535\u6d41\u4f3c \u7684\u901a\u8fc7\u4e86\u6768\u4e91\u806a\u7684\u5fc3\u5934\uff0c\u4ed6\u7d27\u7d27\u62e5\u62b1\u7740\u660e\u6167\uff0c\u7528\u8138\u5b54\u8f7b\u64e6\u5979\u7684\u8138\u5b54\uff0c\u5598\u7740\u6c14\uff0c\u4e00\u53e5\u8bdd\u4e5f\u4e0d \u8bf4\u3002\u4ed6\u60f3\uff1a\u201c\u660e\u6167\u8bf4\u5f97\u5bf9\uff0c\u6211\u8981\u7ea0\u96c6\u54c8\u8428\u514b\u4eba\uff0c\u628a\u6ee1\u6e05\u7684\u519b\u961f\u9a71\u9010\u51fa\u53bb\u3002\u6253\u4ed7\u4e0d\u662f\u9760\u523a\u6740\u654c \u4eba\u4e00\u4e24\u4e2a\u5c06\u9886\u5c31\u80fd\u6210\u4e8b\u7684\u3002\u201d \u3000\u3000\u7b2c\u4e8c\u592d\uff0c\u7eb3\u5170\u660e\u6167\u7167\u5e38\u53bb\u7ed9\u7236\u4eb2\u8bf7\u5b89\u3002\u7eb3\u5170\u79c0\u5409\u4e00\u89c1\u5979\uff0c\u5c31\u5806\u6ee1\u7b11\u5bb9\uff0c\u8bf4\u9053\uff1a\u201c\u5973\u513f\uff0c \u4f60\u4eca\u5e74\u51e0\u5c81\u5566\uff1f\u201d\u660e\u6167\u6485\u7740\u5634\u513f\u7b54\u9053\uff1a\u201c\u597d\u4e00\u4e2a\u7cca\u6d82\u7684\u7238\u7238\uff0c\u5341\u4e5d\u5c81\u54df\uff0c\u7238\u7238\u8fde\u5973\u513f\u7684\u5c81\u6570 \u8fd8\u8bb0\u4e0d\u5f97\uff1f\u201d\u7eb3\u5170\u79c0\u5409\u7eb5\u58f0\u7b11\u9053\uff1a\u201c\u5341\u4e5d\u5c81\u4e86\u54df\uff01\u662f\u5440\uff01\u4f60\u7684\u7238\u7238\u771f\u7cca\u6d82\uff0c\u5973\u513f\u5341\u4e5d\u5c81\u4e86\uff0c \u8fd8\u4e0d\u7ed9\u5979\u627e\u5a46\u5bb6\uff01\u201d\u660e\u6167\u53d8\u8272\u9053\uff0c\u201c\u7238\u7238\uff0c\u6211\u4e0d\u51c6\u4f60\u62ff\u6211\u5f00\u73a9\u7b11\u3002\u201d\u7eb3\u5170\u79c0\u5409\u629a\u7740\u5973\u513f\u7684\u79c0 \u53d1\uff0c\u5728\u5979\u7684\u8033\u8fb9\u6084\u6084\u7684\u8bf4\u9053\uff1a\u201c\u660e\u6167\u522b\u5bb3\u7f9e\uff01\u7238\u7238\u771f\u7ed9\u4f60\u5bfb\u5230\u4e86\u4e00\u4e2a\u6700\u597d\u7684\u5a46\u5bb6\uff0c\u4f60\u5440\uff0c\u505a \u68a6\u4e5f\u6ca1\u6709\u60f3\u5230\uff01\u201d\u660e\u6167\u6025\u5f97\u7741\u5927\u773c\u775b\uff0c\u7eb3\u5170\u79c0\u5409\u81ea\u987e\u81ea\u7684\u8bf4\u4e0b\u53bb\u9053\uff1a\u201c\u4f60\u731c\u662f\u8c01\uff0c\u5c31\u662f\u591a\u94ce \u5440\uff01\u4f60\u5ac1\u8fc7\u53bb\u5c31\u662f\u4e2a\u73b0\u6210\u7684\u738b\u5983\uff01\u201d \u3000\u3000\u7eb3\u5170\u79c0\u5409\u559c\u5b5c\u5b5c\u7684\u770b\u7740\u5973\u513f\uff0c\u7eb3\u5170\u660e\u6167\u5ffd\u7136\u5927\u58f0\u53eb\u9053\uff1a\u201c\u6211\u4e0d\u5ac1\uff01\u201d\u773c\u6cea\u7ebf\u822c\u7684\u6389\u4e0b \u6765\uff0c\u7eb3\u5170\u79c0\u5409\u5927\u4e3a\u5947\u5f02\uff0c\u5927\u58f0\u95ee\u9053\uff1a\u201c\u8fd9\u6837\u7684\u4eba\u4f60\u4e0d\u5ac1\uff0c\u4f60\u8fd8\u5ac1\u8c01\uff1f\u9664\u4e86\u5f53\u671d\u592a\u5b50\uff0c\u8fd8\u6709\u8c01 \u6bd4\u5f97\u4e0a\u4ed6\uff1f\u4f60\u5440\uff0c\u522b\u5c0f\u5b69\u5b50\u6c14\u5566\uff01\u201d\u7eb3\u5170\u660e\u6167\u7a81\u7136\u63a9\u9762\u75db\u54ed\uff0c\u5636\u54d1\u7740\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u5ac1\u5c31\u662f\u4e0d \u5ac1\uff0c\u6211\u4e5f\u4e0d\u5e0c\u7f55\u4ec0\u4e48\u738b\u5983\u3002\u201d\u7eb3\u5170\u79c0\u5409\u6c14\u5f97\u8fde\u8fde\u987f\u8db3\uff0c\u8fd9\u65f6\u623f\u5916\u5ffd\u7136\u4f20\u6765\u7ebd\u67af\u5e90\u7684\u58f0\u97f3\uff0c\u7980 \u62a5\u6c42\u89c1\u3002\u7eb3\u5170\u79c0\u5409\u6325\u6325\u624b\u9053\uff1a\u201c\u4f60\u56de\u53bb\u4ed4\u7ec6\u60f3\u72d9\uff0c\u6211\u53eb\u4f60\u7684\u5988\u5988\u548c\u4f60\u8bf4\u3002\u201d\u4ed6\u4e00\u70b9\u4e5f\u4e0d\u77e5\u9053 \u6768\u4e91\u806a\u7684\u4e8b\u60c5\uff0c\u8fd8\u4ee5\u4e3a\u662f\u5973\u513f\u6545\u610f\u8bc8\u5a07\u3002 \u3000\u3000\u81ea\u6b64\u4e00\u8fde\u6570\u65e5\uff0c\u660e\u6167\u7684\u6bcd\u4eb2\u90fd\u966a\u4f34\u7740\u5973\u513f\uff0c\u5de6\u8bf4\u53f3\u8bf4\uff0c\u660e\u6167\u53ea\u662f\u6d41\u6cea\u3002\u6700\u540e\u5979\u6bcd\u4eb2\u9053\uff1a \u201c\u4f60\u60f3\u60f3\u6211\u5427\uff0c\u6211\u548c\u4f60\u7238\u5c31\u53ea\u6709\u4f60\u4e00\u4e2a\u5973\u513f\uff0c\u665a\u5e74\u4e5f\u5f97\u671b\u6709\u4e2a\u4f9d\u9760\u5440\uff01\u4f60\u662f\u65d7\u4eba\uff0c\u591a\u62e9\u94ce\u9102 \u4eb2\u738b\u7684\u72ec\u751f\u5b50\uff0c\u536b\u662f\u5e74\u7eaa\u8f7b\u8f7b\u5c31\u7acb\u4e86\u90a3\u4e48\u5927\u7684\u519b\u529f\uff0c\u4f60\u60f3\u5728\u5b97\u5ba4\u5b50\u5f1f\u4e2d\uff0c\u8fd8\u627e\u5f97\u51fa\u7b2c\u4e8c\u4e2a\uff1f \u4ed6\u53c8\u662f\u4f60\u7238\u7238\u7684\u4e0a\u53f8\uff0c\u4f60\u4e0d\u5ac1\u4ed6\uff1b\u4f60\u7238\u7238\u4e5f\u4e0b\u4e0d\u4e86\u53f0\u554a\uff01\u4f60\u8981\u6c14\u6b7b\u6211\u4eec\u5417\uff1f\u660e\u6167\uff0c\u4f60\u7d20\u6765\u5b5d \u987a\uff0c\u600e\u4e48\u8fd9\u6b21\u8fd9\u6837\u5201\u86ee\uff0c\u7238\u7238\u5988\u5988\u53c8\u90fd\u662f\u4e3a\u4f60\u597d\uff01\u201d\u660e\u6167\u542c\u4e86\u8fd9\u4e00\u5e2d\u8bdd\uff0c\u72b9\u5982\u4e94\u96f7\u8f70\u9876\uff0c\u6574 \u4e2a\u513f\u5446\u4f4f\u4e86\uff0c\u4e45\u4e45\u8bf4\u4e0d\u51fa\u8bdd\uff0c\u5988\u5988\u53f9\u4e00\u53e3\u6c14\uff0c\u8d70\u4e86\uff01 \u3000\u3000\u6bcd\u4eb2\u53bb\u540e\uff0c\u7eb3\u5170\u660e\u6167\u7684\u601d\u60f3\u5c31\u4f3c\u5927\u6d77\u4e2d\u7684\u6d77\u6d6a\uff0c\u8d77\u4f0f\u4e0d\u4f11\u3002\u5979\u6781\u7231\u6768\u4e91\u806a\u3002\u53ef\u662f\u6768\u4e91\u806a \u662f\u5979\u7238\u7238\u7684\u654c\u4eba\uff0c\u662f\u6ee1\u6e05\u7684\u654c\u4eba\uff0c\u5979\u548c\u4ed6\u75f4\u604b\u4e0b\u53bb\uff0c\u6709\u4ec0\u4e48\u7ed3\u679c\uff1f\u4ed6\u4eec\u662f\u7edd\u4e0d\u53ef\u80fd\u6210\u4e3a\u4e00\u5bf9 \u7684\u554a\uff0c\u800c\u4e14\uff0c\u5c31\u662f\u50cf\u73b0\u5728\u8fd9\u6837\uff0c\u628a\u6768\u4e91\u806a\u85cf\u5728\u81ea\u5df1\u7684\u8eab\u65c1\uff0c\u4e5f\u53ea\u80fd\u662f\u6682\u65f6\u7684\u554a\u3002\u5468\u56f4\u90fd\u662f\u60f3 \u4f24\u5bb3\u4ed6\u7684\u4eba\uff0c\u7eb5\u4f7f\u6709\u5929\u5927\u7684\u672c\u9886\uff1b\u5b64\u8eab\u9677\u5728\u654c\u4eba\u4e4b\u4e2d\u3001\u4e5f\u662f\u6781\u5927\u6781\u5927\u7684\u5371\u9669\u3002\u81ea\u5df1\u548c\u6768\u4e91\u806a \u82e5\u60f3\u6709\u597d\u7ed3\u679c\uff0c\u9664\u975e\u8ddf\u7740\u4ed6\u9003\u51fa\u53bb\uff0c\u8ddf\u7740\u4ed6\u62ff\u8d77\u5200\u67aa\uff0c\u53cd\u6297\u81ea\u5df1\u7684\u53cc\u4eb2\uff0c\u81ea\u5df1\u7684\u65cf\u4eba\uff01\u201c\u8fd9 \u662f\u4e0d\u53ef\u80fd\u7684\u554a\uff01\u201d\u5979\u662f\u7236\u6bcd\u7684\u72ec\u751f\u5973\u513f\uff0c\u53cd\u6297\u7236\u6bcd\uff0c\u90a3\u662f\u5979\u8fde\u60f3\u4e5f\u4e0b\u6562\u60f3\u7684\u4e8b\u3002\u5979\u7231\u6768\u4e91 \u806a\uff0c\u5979\u4e5f\u7231\u5979\u7684\u7236\u6bcd\u3002\u5979\u4e0d\u77e5\u9053\u8981\u727a\u7272\u8c01\uff0c\u5979\u6574\u6574\u60f3\u4e86\u4e00\u5929\u4e00\u591c\u3002 \u3000\u3000\u6768\u4e91\u806a\u4e00\u8fde\u6570\u5929\u4e0d\u89c1\u7eb3\u5170\u660e\u6167\u6765\u627e\u4ed6\uff0c\u6b63\u81ea\u5947\u602a\uff0c\u8fd9\u65e5\u665a\u4e0a\u4ed6\u72ec\u5750\u623f\u4e2d\u7eb3\u95f7\uff0c\u660e\u6167\u5ffd\u7136 \u6765\u4e86\uff0c\u6570\u5929\u4e0d\u89c1\uff0c\u5979\u7adf\u7136\u7626\u4e86\u8bb8\u591a\uff0c\u773c\u775b\u80bf\u5f97\u80e1\u6843\u4f3c\u7684\uff0c\u6768\u4e91\u806a\u4e00\u89c1\u5927\u60ca\u3002\u6025\u5fd9\u95ee\u9053\uff1a\u201c\u4f60 \u600e\u4e48\u5566\uff1f\u201d\u660e\u6167\u4e00\u4e0b\u6eda\u8fdb\u4ed6\u7684\u6000\u4e2d\uff0c\u75af\u72c2\u822c\u7684\u543b\u4ed6\uff0c\u63c9\u4ed6\u7d27\u62b1\u4ed6\uff0c\u6768\u4e91\u806a\u629a\u7740\u5979\u7684\u79c0\u53d1\uff0c\u7231 \u601c\u7684\u8bf4\u9053\uff1a\u201c\u660e\u6167\uff0c\u4ec0\u4e48\u4e8b\u60c5\u8fd9\u6837\u4ee4\u4f60\u6fc0\u52a8\uff0c\u548c\u4f60\u6700\u4eb2\u7231\u7684\u4eba\u8bf4\u8bf4\u5427\uff01\u4e0d\u8981\u8fd9\u6837\uff01\u201d\u7eb3\u5170\u660e \u6167\u95ee\u9053\uff1a\u201c\u4f60\u771f\u7684\u559c\u6b22\u6211\uff0c\u751f\u6b7b\u4e0d\u6e1d\uff1f\u201d\u6768\u4e91\u806a\u9053\uff1a\u201c\u8981\u4e0d\u8981\u6211\u628a\u5fc3\u6316\u7ed9\u4f60\u770b\uff1f\u201d\u660e\u6167\u5ffd\u7136 \u5730\u53eb\u9053\uff1a\u201c\u4f60\u7231\u6211\u5c31\u79bb\u5f00\u6211\u5427\uff01\u201d\u6768\u4e91\u806a\u9a87\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u660e\u6167\u54ed\u9053\uff1a\u201c\u4e00\u5207\u82e6\u96be\u7531\u6211\u627f \u5f53\uff0c\u6211\u4e0d\u613f\u610f\u4f60\u5728\u8fd9\u91cc\u5192\u7740\u751f\u547d\u7684\u5371\u9669\uff01\u201d\u6768\u4e91\u806a\u9053\uff1a\u201c\u660e\u6167\u4f60\u4e3a\u4ec0\u4e48\u8981\u8fd9\u6837\u8bf4\uff1f\u6211\u8981\u5c3d\u6211 \u7684\u529b\u536b\u62a4\u4f60\uff0c\u4f60\u4ee5\u4e3a\u6211\u4e0d\u80fd\u536b\u62a4\u4f60\u5417\uff1f\u8981\u4e0d\uff0c\u4f60\u548c\u6211\u4e00\u8d77\u8d70\u5427\uff01\u8349\u539f\u8fd9\u6837\u5e7f\u5927\uff0c\u96be\u9053\u4f60\u8fd8\u6015 \u627e\u4e0d\u5230\u5bb9\u8eab\u7684\u5730\u65b9\u5417\uff1f\u201d\u660e\u6167\u8f7b\u8f7b\u7684\u63a8\u5f00\u4e86\u4ed6\u7684\u624b\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u7edd\u4e0d\u80fd\u6210\u4e3a\u592b\u5987\u7684\uff0c\u7edd\u4e0d \u80fd\uff01\u201d\u6768\u4e91\u806a\u4f3c\u5403\u4e86\u4e00\u97ad\u4f3c\u7684\u8df3\u8d77\u6765\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u4e0d\u80fd\uff1f\u201d\u7eb3\u5170\u660e\u6167\u9053\uff1a\u201c\u4e0d\u5fc5\u95ee\u4e86\uff01\u4f60\u548c \u6211\u6ce8\u5b9a\u4e0d\u80fd\u5728\u4e00\u8d77\u7684\uff0c\u8c01\u6559\u4f60\u662f\u6c49\u4eba\uff01\u201d\u6768\u4e91\u806a\u9762\u8272\u5927\u53d8\uff0c\u60f3\u8d77\u4ed6\u662f\u654c\u4eba\u7684\u5973\u513f\uff0e\u5185\u5fc3\u7684\u58f0 \u97f3\u8d23\u5907\u4ed6\u9053\uff1a\u201c\u6e05\u9192\u8fc7\u6765\u5427\uff0c\u6768\u4e91\u806a\uff01\u662f\u554a\uff01\u4f60\u600e\u4e48\u80fd\u8ff7\u604b\u654c\u4eba\u7684\u5973\u513f\u3002\u201d\u4ed6\u4e0d\u80fd\u7406\u89e3\u7eb3\u5170 \u660e\u6167\u7ea4\u5f31\u7684\u611f\u60c5\uff0c\u4ed6\u542c\u5230\u5979\u8868\u793a\u4e0d\u613f\u8ddf\u4ed6\u51fa\u8d70\u4e4b\u540e\uff0c\u5fc3\u5934\u5982\u4e2d\u5229\u5251\uff0c\u4ed6\u4ee5\u4e3a\u7eb3\u5170\u660e\u6167\u59cb\u7ec8\u8fd8 \u662f\u7ad9\u5728\u5979\u7236\u4eb2\u7684\u90a3\u4e00\u8fb9\u3002 \u3000\u3000\u6768\u4e91\u806a\u6b63\u60f3\u63a8\u5f00\u7eb3\u5170\u660e\u6167\uff0c\u4f46\u770b\u7740\u5979\u6ee1\u9762\u6cea\u5149\uff0c\u624b\u53c8\u8f6f\u4e0b\u6765\u4e86\u3002\u7eb3\u5170\u660e\u6167\u53c8\u7d27\u7d27\u62b1\u7740 \u4ed6\uff0c\u5636\u58f0\u53eb\u9053\uff1a\u201c\u5728\u6211\u4eec\u5206\u624b\u4e4b\u524d\uff0c\u6211\u6c42\u4f60\u4e0d\u8981\u53d1\u6012\uff0c\u4e0d\u8981\u607c\u6211\uff01\u201d\u6768\u4e91\u806a\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u660e \u6167\uff0c\u6211\u6c38\u8fdc\u4e0d\u4f1a\u607c\u4f60\uff01\u201d\u660e\u6167\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u5728\u6000\u7591\uff0c\u6211\u613f\u610f\u89e3\u5f00\u4f60\u5fc3\u4e0a\u7684\u7ed3\u3002\u6211\u628a\u6211\u7684\u4e00 \u5207\u5949\u732e\u7ed9\u4f60\uff0c\u6211\u4eec\u867d\u7136\u4e0d\u80fd\u6210\u4e3a\u592b\u5987\uff0c\u4f46\u6211\u4ecd\u7136\u8fd8\u662f\u4f60\u7684\u59bb\u5b50\uff01\u201d\u6768\u4e91\u806a\u6323\u624e\u9053\uff1a\u201c\u660e\u6167\u4e0d \u8981\u8fd9\u6837\uff01\u201d\u4f46\u4e00\u970e\u90a3\u95f4\uff0c\u4ed6\u7684\u53e3\u5df2\u7ecf\u7ed9\u7eb3\u5170\u660e\u6167\u67d4\u8f6f\u7684\u5634\u5507\u538b\u4f4f\uff0c\u538b\u5f97\u4ed6\u900f\u4e0d\u8fc7\u6c14\u6765\u3002\u6e10 \u6e10\uff0c\u4ed6\u611f\u5230\u4e00\u9635\u660f\u8ff7\uff0c\u5728\u751f\u547d\u4e2d\u7b2c\u4e00\u6b21\u611f\u53d7\u5230\u5f3a\u70c8\u7684\u523a\u6fc0\u4e0e\u75db\u82e6\uff01 \u3000\u3000\u5230\u6e05\u9192\u8fc7\u6765\u65f6\uff0c\u7eb3\u5170\u660e\u6167\u5df2\u7ecf\u4e0d\u89c1\u4e86\uff0c\u5c0f\u623f\u5185\u53ea\u7559\u4e0b\u65e0\u8fb9\u7684\u9ed1\u6697\u4e0e\u7a7a\u865a\uff0c\u6768\u4e91\u806a\u53f9\u53e3\u6c14 \u9053\uff1a\u201c\u6211\u8be5\u8d70\u4e86\uff01\u201d\u6b63\u5f85\u6536\u62fe\u884c\u56ca\u3002\u5ffd\u7136\u7a97\u95e8\u5019\u7684\u6253\u5f00\uff0c\u8df3\u8fdb\u4e00\u6761\u6c49\u5b50\uff0c\u53eb\u9053\uff1a\u201c\u6768\u4e91\u806a\uff0c \u4f60\u662f\u8be5\u8d70\u4e86\uff01\u201d\u6765\u7684\u4eba\u4e43\u7ebd\u67af\u5e90\u3002 \u3000\u3000\u6768\u4e91\u806a\u6479\u7136\u8df3\u8d77\uff1a\u4f4e\u58f0\u559d\u9053\uff1a\u201c\u7ebd\u67af\u5e90\uff0c\u4f60\u627e\u6b7b\uff01\u201d\u7ebd\u67af\u5e90\u7b11\u9053\uff1a\u201c\u6211\u4e0d\u662f\u4f60\u7684\u5bf9\u624b\uff0c \u6211\u6015\u4f60\u6740\u6211\u6211\u5c31\u4e0d\u6765\u4e86\uff01\u6211\u65e9\u77e5\u9053\u4f60\u5728\u8fd9\u513f\uff0c\u4f60\u7231\u6211\u4eec\u7684\u5c0f\u59d0\u662f\u4e0d\u662f\uff1f\u201d\u6768\u4e91\u806a\u6012\u9053\uff1a\u201c\u4e0d \u8981\u4f60\u7ba1\uff01\u201d\u7ebd\u6854\u5e90\u9053\uff1a\u201c\u4f60\u81ea\u547d\u82f1\u96c4\u8c6a\u6770\u3002\u6211\u770b\u4f60\u5374\u6ca1\u6709\u4e00\u70b9\u82f1\u96c4\u672c\u8272\uff01\u201d\u6768\u4e91\u806a\u5706\u7741\u53cc\u76ee \u65a5\u9053\uff1a\u201c\u6211\u6709\u54ea\u70b9\u4e0d\u5bf9\uff0c\u4f60\u8bf4\uff01\u201d\u7ebd\u67af\u5e90\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u5982\u771f\u7684\u559c\u7231\u7eb3\u5170\u660e\u6167\u5c0f\u59d0\uff0c\u4e3a\u4ec0\u4e48\u4f60 \u4e0d\u66ff\u5979\u60f3\u60f3\uff1b\u5979\u5df2\u6709\u4e86\u610f\u4e2d\u4eba\u4e86\uff0c\u4e0d\u662f\u4eca\u5e74\u5c31\u662f\u660e\u5e74\uff0c\u5979\u5c31\u8981\u51fa\u5ac1\u4e86\uff0c\u5979\u7684\u4e08\u592b\u6bd4\u4f60\u597d\u5343\u500d \u4e07\u500d\uff0c\u4f60\u4e3a\u4ec0\u4e48\u8981\u7f20\u5979\uff0c\u4ee4\u5979\u53d7\u82e6\uff01\u201d\u6768\u4e91\u806a\u559d\u95ee\u9053\uff1a\u201c\u8c01\uff1f\u201d\u7ebd\u6854\u5e90\u5e94\u58f0\u7b54\u9053\uff1a\u201c\u5927\u5c06\u519b \u591a\u94ce\uff01\u201d\u8bdd\u521a\u8bf4\u5b8c\uff0c\u5ffd\u5730\u5495\u549a\u4e00\u58f0\u5012\u5728\u5730\u4e0a\u3002\u6768\u4e91\u806a\u51fa\u624b\u5982\u7535\uff0c\u4e00\u4e0b\u5b50\u5c31\u70b9\u4e86\u4ed6\u7684\u8f83\u9ebb\u7a74\u3002 \u3000\u3000\u7ebd\u67af\u5e90\u5728\u8349\u539f\u4e0a\u8ffd\u9010\u5353\u4e00\u822a\u65f6\u66fe\u78b0\u8fc7\u6768\u4e91\u806a\u548c\u7eb3\u5170\u660e\u6167\u5728\u4e00\u8d77\uff0c\u90a3\u65f6\u7eb3\u5170\u660e\u6167\u867d\u7136\u5f88\u5feb \u7684\u8eb2\u8fdb\u8f66\u4e2d\uff0c\u4f46\u4ed6\u5df2\u6e05\u6e05\u695a\u695a\u7684\u77a7\u89c1\u4e86\u5979\u7684\u9762\u5bb9\u3002\u8fd9\u4ef6\u4e8b\u4ed6\u4e00\u76f4\u85cf\u5728\u5fc3\u91cc\u4e0d\u6562\u8bf4\u51fa\u3002\u8fd9\u51e0\u5929 \u6765\uff0c\u4ed6\u9690\u7ea6\u542c\u5230\u7eb3\u5170\u5c0f\u59d0\u4e0d\u613f\u5ac1\u7ed9\u591a\u94ce\u7684\u4e8b\u3002\u4ed6\u548c\u7eb3\u5170\u79c0\u5409\u95f2\u8c08\uff0c\u7eb3\u5170\u79c0\u5409\u4e5f\u5509\u58f0\u53f9\u6c14\u3002\u867d \u7136\u6ca1\u6709\u8bb2\u660e\uff0c\u4f46\u7ebd\u67af\u5e90\u5df2\u6599\u5230\u5176\u4e2d\u5b9a\u53f3\u7f18\u6545\u3002\u4ed6\u60f3\u6765\u60f3\u53bb\uff0c\u60f3\u51fa\u4e2a\u201c\u91dc\u5e95\u62bd\u85aa\u201d\u4e4b\u7b56\uff0c\u9ed1\u591c \u91cc\u5355\u72ec\u6765\u89c1\u6768\u4e91\u806a\uff0c\u60f3\u7528\u8bf4\u8bdd\u628a\u4ed6\u6fc0\u8d70\u3002 \u3000\u3000\u518d\u8bf4\u6768\u4e91\u806a\u628a\u7ebd\u67af\u5e90\u70b9\u5012\u4e4b\u540e\uff0c\u5fc3\u4e2d\u53c8\u6c14\u53c8\u82e6\uff0c\u4ed6\u672c\u6765\u662f\u51c6\u5907\u8d70\u7684\u4e86\uff0c\u7ecf\u6b64\u4e00\u8bf4\uff0c\u53e6\u4e00 \u4e2a\u5ff5\u5934\u5ffd\u7136\u51fa\u73b0\uff0c\u6211\u4e14\u8fdb\u5c06\u519b\u5e9c\u53bb\u770b\u770b\uff01\u53cd\u6b63\u6211\u4e5f\u8981\u63a2\u63a2\u654c\u4eba\u7684\u60c5\u5f62\u3002\u4ed6\u4e00\u98d8\u8eab\u5c31\u51fa\u4e86\u7a97 \u6237\uff0c\u5728\u6025\u6012\u653b\u5fc3\u4e4b\u4e0b\uff0c\u4ed6\u6839\u672c\u4e0d\u7406\u4ec0\u4e48\u751f\u547d\u7684\u5371\u9669\u4e86\u3002 \u3000\u3000\u534a\u4e2a\u65f6\u8fb0\u4e4b\u540e\uff0c\u5c06\u519b\u5e9c\u4e2d\u6765\u4e86\u4e2a\u4e0d\u901f\u4e4b\u5ba2\uff0c\u4f0f\u5728\u5927\u5385\u7684\u5c4b\u6a90\u4e0a\u5411\u4e0b\u7aa5\u770b\uff01\u8fd9\u4eba\u6b63\u662f\u6768\u4e91 \u806a\u3002\u91cc\u9762\u6070\u597d\u5750\u7740\u7eb3\u5170\u79c0\u5409\u548c\u591a\u94ce\u3002\u6768\u4e91\u806a\u634f\u7d27\u77ed\u5251\uff0c\u60f3\u9053\u4ed6\u4eec\u4e00\u5b9a\u662f\u8c08\u660e\u6167\u7684\u5a5a\u4e8b\u4e48\u3002\u6211 \u4e14\u542c\u542c\u4ed6\u4eec\u8bf4\u4ec0\u4e48\uff1f\u6211\u62fc\u7740\u8840\u6d12\u9ec4\u6c99\uff0c\u4e5f\u8981\u7ed9\u591a\u94ce\u8fd9\u8d3c\u5b50\u4e00\u5251\uff0c\u6b63\u601d\u60f3\u95f4\uff0c\u53ea\u542c\u5f97\u7eb3\u5170\u79c0\u5409 \u5f00\u58f0\u9053\uff1a\u201c\u94a6\u5dee\u5927\u4eba\u3002\u6211\u4eec\u8fd9\u5c31\u63d0\u90a3\u4e24\u4e2a\u56de\u5b50\u6765\u5ba1\u95ee\u597d\u4e0d\u597d\uff1f\u201d\u6768\u4e91\u806a\u5fc3\u9053\uff1a\u201c\u54a6\uff0c\u5947\u4e86\uff0c \u539f\u6765\u4e0d\u662f\u8bf4\u5a5a\u4e8b\u4e48\uff0c\u5374\u8981\u63d0\u4ec0\u4e48\u56de\u5b50\u6765\u4e86\uff01\u201d \u3000\u3000\u4ed6\u4e0d\u77e5\u9053\u8fd9\u5a5a\u4e8b\u53ea\u662f\u6697\u4e2d\u8fdb\u884c\uff0c\u591a\u94ce\u7684\u7236\u4eb2\u8fdc\u5728\u4eac\u4e2d\uff0c\u6309\u4ed6\u4eec\u4eb2\u738b\u5b97\u5ba4\u7684\u89c4\u77e9\uff0c\u95ee\u8058\u4e00 \u4e2a\u738b\u5983\u7edd\u4e0d\u662f\u4e00\u4ef6\u7b80\u5355\u7684\u4e8b\uff0c\u7edd\u4e0d\u4f1a\u7531\u591a\u94ce\u4eb2\u81ea\u63d0\u51fa\u6765\u7684\u3002\u4ed6\u4eec\u8fd9\u6b21\u805a\u4f1a\uff0c\u529e\u7684\u5012\u771f\u662f\u201c\u516c \u4e8b\u201d\uff0c\u8981\u5ba1\u95ee\u54c8\u8428\u514b\u65cf\u7684\u6297\u6e05\u82f1\u96c4\u3002 \u3000\u3000\u7eb3\u5170\u79c0\u5409\u4f20\u4ee4\u4e0b\u53bb\uff0c\u7247\u523b\u4e4b\u540e\uff0c\u536b\u5175\u5e26\u8fdb\u4e00\u7537\u4e00\u5973\uff0c\u6768\u4e91\u806a\u4e00\u89c1\u70ed\u8840\u6cb8\u817e\uff0c\u8fd9\u4eba\u6b63\u662f\u81ea \u5df1\u7684\u7ed3\u76df\u5144\u5f1f\u9ea6\u76d6\u63d0\uff0c\u81ea\u90a3\u6b21\u5927\u98ce\u6c99\u4e2d\u6563\u5931\u4e4b\u540e\u3002\u4ed6\u5c31\u4e00\u76f4\u6ca1\u6709\u89c1\u8fc7\u9ea6\u76d6\u63d0\uff1a\u5728\u627e\u9ed1\u6cc9\u6c34\u7684 \u65f6\u5019\uff0c\u4ed6\u4e0e\u53e6\u4e00\u4f4d\u76df\u5f1f\u4f0a\u58eb\u8fbe\u76f8\u9022\uff0c\u4f0a\u58eb\u8fbe\u4e5f\u4e0d\u77e5\u9053\u9ea6\u76d6\u63d0\u7684\u751f\u6b7b\uff0c\u5374\u4e0d\u6599\u4f1a\u5728\u5c06\u519b\u5e9c\u4e2d\u9047 \u89c1\u3002\u800c\u4e14\u5728\u9ea6\u76d6\u63d0\u8eab\u8fb9\u8fd8\u6709\u4e00\u4f4d\u6f02\u4eae\u7684\u54c8\u8428\u514b\u59d1\u5a18\uff01 \u3000\u3000\u9ea6\u76d6\u63d0\u548c\u90a3\u4f4d\u59d1\u5a18\u5e26\u7740\u6c89\u91cd\u7684\u9501\u94fe\u3002\u7eb3\u5170\u79c0\u5409\u559d\u4ed6\u4eec\u8dea\u4e0b\uff0c\u9ea6\u76d6\u63d0\u548c\u90a3\u4f4d\u59d1\u5a18\u5374\u90fd\u50b2\u7136 \u4e0d\u7406\u3002\u591a\u94ce\u7fd8\u8d77\u62c7\u6307\u9053\uff1a\u201c\u597d\u6c49\u5b50\uff01\u4f60\u4eec\u54c8\u8428\u514b\u4eba\u805a\u96c6\u5728\u4ec0\u4e48\u5730\u65b9\uff0c\u4f60\u548c\u6211\u8bf4\u3002\u6211\u656c\u91cd\u4f60\u662f \u4e2a\u597d\u6c49\uff0c\u6211\u7b54\u5e94\u8ba9\u4f60\u53bb\u62db\u964d\uff0c\u4e00\u70b9\u4e5f\u4e0d\u4f1a\u4f24\u5bb3\u4f60\u7684\u65cf\u4eba\uff01\u201d\u9ea6\u76d6\u63d0\u6012\u559d\u8fd9\u201c\u8c01\u4fe1\u4f60\u4eec\u6ee1\u6d32\u9791 \u5b50\u7684\u8bdd\uff01\u201d\u7eb3\u5170\u79c0\u5409\u6012\u9053\uff1a\u201c\u656c\u9152\u4e0d\u5403\u5403\u7f5a\u9152\uff0c\u597d\uff0c\u62c9\u4e0b\u53bb\u6253\uff01\u201d\u8bdd\u58f0\u672a\u5b8c\uff0c\u5ffd\u542c\u5f97\u4e00\u58f0\u5927 \u559d\uff0c\u6768\u4e91\u806a\u81ea\u5c4b\u6a90\u4e0a\u4e00\u8dc3\u800c\u4e0b\uff0c\u77ed\u5251\u7535\u95ea\uff0c\u75be\u98ce\u822c\u7684\u5411\u591a\u94ce\u523a\u53bb\uff01 \u3000\u3000----------- \u3000\u3000\u98ce\u4e91\u9601 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u585e\u5916\u5947\u4fa0\u4f20\u300b\u2014\u2014\u7b2c\u5341\u516b\u56de\u3000\u9ea6\u76d6\u63d0\u548c\u66fc\u94c3\u5a1c \u6881\u7fbd\u751f\u300a\u585e\u5916\u5947\u4fa0\u4f20\u300b \u7b2c\u5341\u516b\u56de\u3000\u9ea6\u76d6\u63d0\u548c\u66fc\u94c3\u5a1c \u3000\u3000\u591a\u94ce\u9aa4\u89c1\u6768\u4e91\u806a\u5728\u5c4b\u6a90\u4e0a\u98de\u7eb5\u4e0b\u6765\uff0c\u5251\u5149\u5982\u7ec3\u3002\u76f4\u523a\u9762\u95e8\uff0c\u5927\u53eb\u4e00\u58f0\uff0c\u4e3e\u8d77\u5f20\u6905\u5b50\u4e00 \u6321\uff0c\u5580\u5693\u4e00\u58f0\uff0c\u6905\u5b50\u5df2\u88ab\u5288\u6210\u4e24\u534a\uff0c\u591a\u94ce\u53cd\u624b\u4e00\u638c\u6253\u53bb\uff0c\u6768\u4e91\u806a\u90a3\u4f1a\u7ed9\u4ed6\u6253\u7740\uff0c\u817e\u8d77\u4e00\u817f\uff0c \u628a\u4ed6\u8e22\u7ffb\u5728\u5730\uff0c\u5237\u7684\u4e00\u5251\uff0c\u4fef\u8eab\u523a\u4e0b\u3002\u5ffd\u89c1\u7eb3\u5170\u79c0\u5409\u820d\u547d\u62b1\u7740\u591a\u94ce\uff0c\u77aa\u7740\u53cc\u602a\u773c\uff1b\u7785\u7740\u81ea \u5df1\u3002\u201c\u4f60\u4e0d\u8bb8\u4f24\u5bb3\u6211\u7684\u7236\u4eb2\uff01\u201d\u7eb3\u5170\u660e\u6167\u7684\u8bdd\u5ffd\u7136\u5728\u8111\u6d77\u4e2d\u6d6e\u8d77\uff0c\u6768\u4e91\u806a\u7565\u4e00\u8fdf\u7591\uff0c\u7eb3\u5170\u79c0 \u5409\u548c\u591a\u950b\u5df2\u6eda\u51fa\u6570\u4e08\u5f00\u5916\uff01\u4e24\u65c1\u536b\u571f\u7eb7\u7eb7\u56f4\u4e0a\uff0c\u6768\u4e91\u806a\u820c\u7efd\u6625\u96f7\uff0c\u5288\u96f3\u822c\u4e00\u58f0\u5927\u559d\uff1a\u201c\u6321\u6211 \u8005\u6b7b\uff01\u201d\u638c\u5288\u5251\u6233\u3002\u970e\u90a3\u4e4b\u95f4\uff0c\u6740\u4e86\u4e94\u4eba\uff01\u4ece\u8eab\u4e00\u8dc3\uff0c\u77ed\u5251\u8fde\u6325\uff0c\u628a\u9ea6\u76d6\u63d0\u548c\u90a3\u4e2a\u54c8\u8428\u514b\u5c11 \u5973\u8eab\u4e0a\u7684\u94c1\u94fe\u65a9\u65ad\uff0c\u95ee\u9053\uff1a\u201c\u80fd\u4e0a\u5c4b\u5417\uff1f\u201d\u90a3\u5c11\u5973\u70b9\u4e86\u70b9\u5934\uff0c\u6768\u4e91\u806a\u5355\u5251\u65ad\u540e\uff0c\u53eb\u58f0 \u201c\u8d70\u201d\uff0c\u7834\u95e8\u800c\u51fa\uff0c\u8dc3\u4e0a\u74e6\u9762\u3002\u4e0b\u9762\u5f29\u7bad\uff0c\u96e8\u70b9\u822c\u5c04\u6765\u3002\u6768\u4e91\u806a\u8131\u4e86\u8eab\u4e0a\u957f\u886b\u4e00\u6697\u8fd0\u5185\u529b\uff0c \u4e0a\u4e0b\u98de\u821e\uff0c\u5f29\u7bad\u7ed9\u957f\u886b\u4e00\u8361\uff0c\u56db\u9762\u6fc0\u5c04\u5f00\u53bb\u3002\u7247\u523b\u4e4b\u540e\u4e09\u4eba\u5df2\u8131\u79bb\u9669\u5730\uff0c\u51fa\u4e86\u5c06\u8f66\u5e9c\u4e86\u3002\u6768 \u4e91\u806a\u5c06\u957f\u886b\u62ab\u4e0a\uff0c\u9ea6\u76d6\u63d0\u4ed4\u7ec6\u4e00\u770b\uff0c\u53ea\u89c1\u957f\u886b\u4e0a\u8fde\u4e00\u4e2a\u5c0f\u6d1e\u90fd\u8bbe\u6709\uff0c\u4e0d\u7981\u8d5e\u9053\uff1a\u201c\u6768\u5927\u4fa0\u771f \u597d\u529f\u592b\uff01\u201d\u6768\u4e91\u806a\u5fae\u5fae\u4e00\u7b11\uff0c\u5e26\u9886\u4ed6\u4eec\u6284\u5c0f\u8857\u964b\u5df7\uff0c\u8d70\u51fa\u57ce\u5916\u3002 \u3000\u3000\u5230\u4e86\u57ce\u5916\u3002\u9ea6\u76d6\u63d0\u5bf9\u90a3\u4e2a\u5c11\u5973\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u6211\u5e38\u5e38\u548c\u4f60\u63d0\u8d77\u7684\u6768\u5927\u54e5\uff01\u6768\u4e91\u806a\u5927\u4fa0\uff01\u201d \u5c11\u5973\u65bd\u4e86\u4e00\u672d\uff0c\u9ea6\u76d6\u63d0\u9053\uff1a\u201c\u5979\u5c31\u662f\u6211\u548c\u4f60\u8bf4\u8fc7\u7684\u90a3\u4f4d\u59d1\u5a18\u53eb\u66fc\u94c3\u5a1c\u3002\u201d\u66fc\u94c3\u5a1c\u662f\u4ed6\u5c0f\u65f6\u7684 \u597d\u53cb\uff0c\u4e24\u4eba\u5e38\u5e38\u4e00\u540c\u51fa\u53bb\u6253\u730e\u3001\u540e\u6765\u5979\u968f\u5979\u7684\u90e8\u843d\u8f6c\u5230\u5357\u7586\uff0c\u97f3\u4fe1\u9694\u7edd\uff0c\u9ea6\u76d6\u63d0\u5374\u8fd8\u60e6\u8bb0\u7740 \u5979\u3002\u4f0a\u58eb\u8fbe\u5e38\u5e38\u62ff\u4ed6\u4eec\u5f00\u73a9\u7b11\u3002\u6240\u4ee5\u6768\u4e91\u806a\u8033\u719f\u80fd\u8be6\u3002\u9ea6\u76d6\u63d0\u7684\u6545\u4e8b\u5f88\u7b80\u5355\uff0c\u4ed6\u5728\u90a3\u65e5\u5927\u98ce \u66b4\u4e4b\u540e\uff0c\u9047\u5230\u4e00\u961f\u5230\u5357\u7586\u53bb\u7684\u9a7c\u9a6c\u5546\u4eba\uff0c\u5176\u4e2d\u6070\u5de7\u5c31\u6709\u66fc\u94c3\u5a1c\u7684\u65cf\u4eba\u5728\u5185\uff0c\u9ea6\u76d6\u63d0\u5c31\u548c\u4ed6\u4eec \u540c\u884c\uff0c\u627e\u5230\u66fc\u94c3\u5a1c\uff0c\u90a3\u65e5\u4ed6\u4eec\u7684\u90e8\u843d\u6b63\u4e3e\u884c\u201c\u5201\u7f8a\u201d\u5927\u4f1a\uff0c\u5c0f\u4f19\u5b50\u4eec\u90fd\u7eb7\u7eb7\u9a91\u9a6c\u548c\u9752\u5e74\u59d1\u5a18 \u4eec\u4e92\u76f8\u8ffd\u9010\uff0c\u6709\u4eba\u9080\u66fc\u94c3\u5a1c\u53bb\u201c\u5201\u7f8a\u201d\u3002\u66fc\u94c3\u5a1c\u603b\u662f\u4e0d\u80af\uff0c\u6b63\u7ea0\u7f20\u95f4\uff0c\u6070\u597d\u9ea6\u76d6\u63d0\u6765\u5230\uff0c\u66fc \u94c3\u5a1c\u4e00\u58f0\u6b22\u547c\uff0c\u5c31\u53eb\u54e5\u54e5\u7ed9\u4e00\u5339\u9a6c\u7ed9\u4ed6\uff0c\u4e5f\u4e0d\u77e5\u9053\u522b\u540e\u60c5\u51b5\uff0c\u5c31\u548c\u4ed6\u53cc\u53cc\u201c\u5201\u7f8a\u201d\u53bb\u4e86\u3002\u90a3 \u4e9b\u5c0f\u4f19\u5b50\u4eec\u4e00\u95ee\uff0c\u77e5\u9053\u4ed6\u4eec\u662f\u4e45\u522b\u91cd\u9022\u7684\u60c5\u4fa3\u3002\u90fd\u66ff\u4ed6\u4eec\u9ad8\u5174\uff0c\u6768\u4e91\u806a\u542c\u4ed6\u53d9\u8ff0\u4e4b\u540e\uff0c\u4e5f\u8d76 \u5fd9\u5411\u4ed6\u4eec\u9053\u8d3a\u3002 \u3000\u3000\u9ea6\u76d6\u63d0\u8bf4\u8d77\u5357\u7586\u54c8\u8428\u514b\u4eba\u96c6\u5c45\u4e4b\u5730\uff0c\u539f\u6765\u4e0e\u98de\u7ea2\u5dfe\u90e8\u843d\u5b9a\u5c45\u4e4b\u6240\uff0c\u76f8\u9694\u4e0d\u8fc7\u4e09\u767e\u591a\u91cc\u3002 \u53ea\u662f\u8349\u539f\u5404\u65cf\uff0c\u5f80\u6765\u65e0\u5b9a\uff0c\u5927\u5bb6\u4e92\u4e0d\u77e5\u9053\uff0c\u54c8\u8428\u514b\u65cf\u662f\u65b0\u8fd1\u8fc1\u53bb\u7684\uff0c\u9664\u4e86\u66fc\u94c3\u5a1c\u90a3\u4e00\u90e8\u843d \u5916\uff0c\u8fd8\u6709\u8bb8\u591a\u90e8\u843d\u3002 \u3000\u3000\u6768\u4e91\u806a\u95ee\u8d77\u9ea6\u76d6\u63d0\u4e3a\u4ec0\u4e48\u88ab\u64d2\uff0c\u9ea6\u76d6\u63d0\u9762\u8272\u9aa4\u53d8\uff0c\u6068\u58f0\u8bf4\u9053\uff1a\u201c\u6768\u5927\u4fa0\uff0c\u4f60\u6837\u6837\u90fd\u597d\uff0c \u5c31\u662f\u6709\u4e00\u6837\u4e0d\u597d\uff01\u201d\u6768\u4e91\u806a\u5947\u9053\u3002\u201c\u54ea\u4e00\u6837\u4e0d\u597d\u5462\uff01\u201d\u9ea6\u76d6\u63d0\u9053\uff1a\u201c\u4f60\u6709\u4e00\u4e2a\u5f88\u574f\u7684\u5e08\u5f1f\u3002 \u4f60\u4e3a\u4ec0\u4e48\u4e0d\u7ba1\u6559\u4ed6\uff1f\u201d\u6768\u4e91\u806a\u70b9\u70b9\u5934\u9053\uff1a\u201c\u8fd9\u662f\u6211\u7684\u4e0d\u597d\uff01\u4f46\u6211\u4e5f\u662f\u6700\u8fd1\u624d\u77e5\u9053\u4ed6\u53d8\u574f\u7684\u3002 \u600e\u4e48\uff1f\u695a\u662d\u5357\u548c\u4f60\u4eec\u53c8\u6709\u4ec0\u4e48\u5173\u7cfb\u3002\u201d\u9ea6\u76d6\u63d0\u9053\uff1a\u201c\u5c31\u662f\u4ed6\u628a\u6211\u4eec\u6349\u7740\uff0c\u4ece\u5580\u5c14\u6c81\u8349\u539f\u76f4\u8fed \u5230\u4f0a\u7281\u57ce\u7684\u3002\u201d\u6768\u4e91\u806a\u5fd9\u95ee\u9053\uff1a\u201c\u662f\u4ed6\u9001\u4f60\u4eec\u6765\u7684\u5417\uff1f\u90a3\u4e48\u4ed6\u73b0\u5728\u5728\u4f0a\u7281\u57ce\uff1f\u6211\u56de\u53bb\u628a\u4ed6\u6293 \u6765\uff01\u201d\u9ea6\u76d6\u63d0\u9053\uff1a\u201c\u4e0d\u662f\u4ed6\u4eb2\u81ea\u9001\u6765\uff0c\u4ed6\u73b0\u5728\u624d\u5fd9\u5462\uff01\u4ed6\u548c\u4e00\u4e2a\u4ec0\u4e48\u5c06\u519b\u5e26\u9886\u4e00\u961f\u6e05\u5175\u9a7b\u9632 \u5728\u5580\u5c14\u6c81\u8349\u539f\u4e09\u5341\u91cc\u5916\u7684\u4e00\u4e2a\u57ce\u5821\uff0c\u76d1\u89c6\u6211\u4eec\u3002\u4ed6\u6d3e\u4eba\u6765\u8981\u54c8\u8428\u514b\u65cf\u4eba\u51fa\u7cae\uff0c\u914b\u957f\u628a\u6765\u4eba\u8f70 \u4e86\u51fa\u53bb\uff0c\u4ed6\u7a81\u7136\u534a\u591c\u5b64\u8eab\u6765\u88ad\uff0c\u628a\u914b\u957f\u7684\u513f\u5b50\u63d0\u53bb\uff0c\u5f53\u6210\u5165\u8d28\u8981\u631f\u3002\u914b\u957f\u5f3a\u786c\u4e0d\u7406\uff0c\u4f46\u7231\u5b50 \u60c5\u6df1\uff0c\u6697\u4e2d\u5374\u53eb\u6211\u53bb\u63a2\u67e5\u3002\u201d\u6768\u4e91\u806a\u70b9\u70b9\u5934\u9053\uff1a\u201c\u662f\u4e86\uff0c\u4f60\u548c\u4f0a\u571f\u8fbe\u662f\u54c8\u8428\u514b\u6700\u51fa\u540d\u7684\u4e24\u4e2a \u52c7\u571f\u3002\u4f60\u6765\u5230\u5357\u7586\uff0c\u4ed6\u81ea\u7136\u8981\u6d3e\u4f60\u53bb\u4e86\u3002\u6211\u731c\u914b\u957f\u8981\u4f60\u6697\u4e2d\u628a\u513f\u5b50\u593a\u56de\uff0c\u53ef\u662f\uff1f\u201d\u9ea6\u76d6\u63d0 \u9053\uff1a\u201c\u662f\u5440\uff01\u201d\u4ed6\u4e0d\u77e5\u9053\u6211\u7684\u6b66\u529f\u6bd4\u695a\u662d\u5357\u5dee\u5f97\u5f88\u8fdc\u3002\u6211\u89c1\uff0c\u6211\u5374\u4e0d\u80fd\u63a8\u8f9e\u4e0d\u53bb\u5440\uff01\u6211\u7684\u6b66 \u529f\u867d\u7136\u6bd4\u4e0d\u4e0a\u695a\u662d\u5357\uff0c\u53ef\u662f\u4f60\u77e5\u9053\u6211\u4eec\u54c8\u8428\u514b\u4eba\u3001\u4ece\u6765\u5c31\u4e0d\u6015\u6bd4\u81ea\u5df1\u5f3a\u5927\u7684\u654c\u4eba\uff0c\u6211\u4e0d\u80fd\u4e22 \u6211\u4eec\u54c8\u8428\u514b\u65cf\u7684\u8138\uff0c\u8bf4\u6211\u4e0d\u6562\u53bb\u5440\u3002\u800c\u4e14\u6211\u7684\u786e\u4e0d\u6015\u4ed6\uff0c\u6211\u60f3\u9047\u89c1\u4e86\u4ed6\uff0c\u6700\u591a\u4e0d\u8fc7\u4e00\u6b7b\uff0c\u90a3 \u53c8\u7b97\u5f97\u4ec0\u4e48\uff1f\u4e5f\u6559\u4ed6\u77e5\u9053\u77e5\u9053\uff0c\u6211\u4eec\u54c8\u8428\u514b\u65cf\u4e5f\u6709\u4e0d\u6015\u6b7b\u7684\u52c7\u58eb\uff0c\u4ed6\u534a\u591c\u88ad\u6211\u4eec\uff0c\u6211\u4eec\u4e5f\u4f1a \u8fd8\u656c\uff01\u201d\u6768\u4e91\u806a\u7fd8\u8d77\u62c7\u6307\u9053\uff1a\u201c\u597d\uff01\u4f60\u771f\u4e0d\u6127\u662f\u6211\u7684\u5144\u5f1f\uff01\u201d\u4ed6\u662f\u8877\u5fc3\u7684\u79f0\u8d5e\u9ea6\u76d6\u63d0\uff0c\u751a\u81f3 \u81ea\u5df1\u6697\u6697\u89c9\u5f97\u60ed\u6127\uff0c\u9ea6\u76d6\u63d0\u548c\u66fc\u94c3\u5a1c\u662f\u4e45\u522b\u91cd\u9022\u4e00\u5bf9\u7684\u60c5\u5021\uff0c\u76f8\u805a\u4e0d\u8fc7\u6570\u5929\uff0c\u9ea6\u76d6\u63d0\u5c31\u613f\u610f \u53bb\u62fc\u6b7b\u4e89\u53d6\u54c8\u8428\u514b\u4eba\u7684\u8363\u8a89\uff0c\u8bb2\u5f97\u90a3\u6837\u575a\u51b3\uff0c\u6beb\u4e0d\u72b9\u7591\uff0c\u597d\u50cf\u662f\u5929\u7ecf\u5730\u4e49\u4e00\u6837\uff0c\u81ea\u5df1\u81ea\u4fe1\uff0c \u82e5\u9047\u5230\u5fc5\u8981\u4e4b\u65f6\uff0c\u4e5f\u5b9a\u80fd\u89c6\u6b7b\u5982\u5f52\uff0c\u53ef\u662f\u73b0\u5728\u5374\u5272\u4e0d\u65ad\u5bf9\u654c\u4eba\u7684\u5973\u513f\u7684\u60c5\u611f\u3002\u9ea6\u76d6\u63d0\u53c8\u9053\uff1a \u201c\u4e0d\u7792\u4f60\u505a\u5927\u54e5\u7684\uff0c\u6211\u4e5f\u6709\u70b9\u820d\u4e0d\u5f97\u66fc\u94c3\u5a1c\u5462\uff0c\u4e34\u884c\u4e4b\u524d\uff0c\u6211\u548c\u5979\u8bf4\uff0c\u6211\u6b64\u53bb\u4e5d\u6b7b\u4e00\u751f\uff0c\u56e0 \u4e3a\u654c\u4eba\u6bd4\u6211\u5389\u5bb3\u5f97\u591a\u3002\u6211\u5bf9\u5979\u9053\uff1a\u2018\u6211\u6b7b\u4e4b\u540e\uff0c\u4f60\u597d\u597d\u4fdd\u91cd\uff0c\u4e0d\u8981\u60e6\u8bb0\u6211\u3002\u6211\u4eec\u54c8\u8428\u514b\u65cf\u6709 \u8bb8\u591a\u5e74\u9752\u7684\u5c0f\u4f19\u5b50\uff0c\u4f60\u5343\u4e07\u4e0d\u8981\u53d1\u50bb\uff0c\u4f60\u8981\u9009\u4e00\u4e2a\u597d\u7684\u7ed3\u5a5a\uff0c\u628a\u6211\u7684\u540d\u5b57\u7ed9\u4f60\u751f\u4e0b\u6765\u7684\u7b2c\u4e00 \u4e2a\u5b69\u5b50\uff0c\u6211\u5c31\u5fc3\u6ee1\u610f\u8db3\u4e86\u3002\u2019\u8fd9\u50bb\u59d1\u5a18\u5ffd\u7136\u6d41\u4e0b\u773c\u6cea\uff0c\u53c8\u5306\u5306\u5730\u63e9\u5e72\uff0c\u4e00\u5b9a\u8981\u548c\u6211\u540c\u53bb\uff0c\u6211 \u4e0d\u7b54\u5e94\uff0c\u5979\u5c31\u8981\u81ea\u520e\u5728\u6211\u7684\u9762\u524d\uff0c\u5979\u53c8\u8bf4\u6211\u770b\u4e0d\u8d77\u5973\u4eba\uff0c\u8bf4\u4e3a\u4ec0\u4e48\u7537\u4eba\u53bb\u5f97\uff0c\u5973\u4eba\u53bb\u4e0d\u5f97\u3002 \u6211\u9053\uff1a\u201c\u66fc\u94c3\u5a1c\uff0c\u6211\u77e5\u9053\u4f60\u4e5f\u4f1a\u6b66\u827a\uff0c\u4f46\u6211\u8981\u548c\u4f60\u5b9e\u8bf4\u3002\u6211\u548c\u4f60\u52a0\u8d77\u6765\uff0c\u6050\u6015\u90fd\u4e0d\u662f\u654c\u4eba\u5bf9 \u624b\uff01\u2019\u66fc\u94c3\u5a1c\u4e00\u70b9\u4e5f\u4e0d\u5728\u4e4e\uff0c\u5979\u53ea\u662f\u6de1\u6de1\u8bf4\u9053\uff1a\u2018\u8981\u6b7b\u5c31\u4e00\u540c\u6b7b\u597d\u4e86\uff0c\u4e5f\u6559\u654c\u4eba\u77e5\u9053\uff0c\u54c8\u8428 \u514b\u65cf\u7684\u59d1\u5a18\u4e5f\u4e0d\u662f\u53ef\u6b3a\u8d1f\u7684\u82f1\u96c4\uff01\u2019\u5979\u8bf4\u5f97\u90a3\u4e48\u81ea\u7136\u3002\u5c31\u597d\u50cf\u966a\u6211\u53bb\u6b7b\u662f\u8fde\u60f3\u4e5f\u4e0d\u7528\u60f3\u5c31\u53ef \u4ee5\u51b3\u5b9a\u7684\u4e8b\uff01\u201d\u6768\u4e91\u806a\u5659\u7740\u773c\u6cea\uff0c\u7b11\u9053\uff1a\u201c\u66fc\u94c3\u5a1c\u59d1\u5a18\uff0c\u4f60\u771f\u597d\uff01\u201d\u4ed6\u60f3\u8d77\u4e86\u7eb3\u5170\u660e\u6167\uff0c\u660e \u6167\u5c31\u4e0d\u80af\u820d\u5f03\u7236\u4eb2\uff0c\u8ddf\u968f\u81ea\u5df1\uff0c\u4ed6\u5bf9\u7740\u66fc\u94c3\u5a1c\uff0c\u53c8\u662f\u6b22\u559c\uff0c\u53c8\u611f\u8f9b\u9178\uff0c\u4ed6\u662f\u4e3a\u9ea6\u76d6\u63d0\u6b22\u559c\uff0c \u800c\u4e3a\u81ea\u5df1\u8f9b\u9178\uff0c\u66fc\u94c3\u632a\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u6768\u5927\u4fa0\uff0c\u4f60\u542c\u4ed6\u8bf4\u5462\uff01\u4e00\u70b9\u70b9\u4e0d\u503c\u4e00\u63d0\u7684\u5c0f\u4e8b\uff0c\u4ed6\u5c31 \u90a3\u4e48\u5927\u5439\u5927\u64c2\uff0c\u597d\u50cf\u8fd9\u4efd\u5e94\u505a\u7684\u4e8b\uff0c\u662f\u4ec0\u4e48\u4e86\u4e0d\u8d77\u7684\u5927\u4e8b\u4e00\u6837\uff0c\u771f\u8ba8\u538c\uff01\u201d\u6768\u4e91\u806a\u62cd\u62cd\u9ea6\u76d6 \u63d0\u7684\u80a9\u8180\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u5144\u5f1f\uff0c\u4f60\u7684\u6545\u4e8b\u5f88\u597d\u542c\uff0c\u8bf4\u4e0b\u53bb\u5427\u3002\u66fc\u94c3\u5a1c\u660e\u662f\u9a82\u4f60\uff0c\u5b9e\u662f\u6b22\u559c\u4f60\uff0c \u5979\u505a\u7684\u4e8b\u60c5\uff0c\u7684\u786e\u662f\u4e86\u4e0d\u8d77\uff0c\u4f60\u4e00\u70b9\u4e5f\u6ca1\u6709\u5938\u5927\u3002\u201d\u66fc\u94c3\u5a1c\u9053\uff1a\u201c\u54df\uff0c\u6768\u5927\u4fa0\uff0c\u4f60\u53bb\u66ff\u4ed6\u6325 \u8170\uff0c\u66f4\u628a\u4ed6\u7eb5\u574f\u54af\uff01\u201d \u3000\u3000\u9ea6\u76d6\u63d0\u63a5\u4e0b\u53bb\u8bf4\u9053\uff1a\u201c\u6211\u548c\u66fc\u94c3\u5a1c\u59d1\u5a18\u591c\u63a2\u695a\u662d\u5357\u6240\u4f4f\u7684\u57ce\u5821\uff0c\u8fd8\u6ca1\u6709\u627e\u5230\u914b\u957f\u7684\u513f \u5b50\uff0c\u5c31\u7ed9\u695a\u662d\u5357\u78b0\u7740\u4e86\uff0c\u695a\u662d\u5357\u8ba4\u5f97\u6211\uff0c\u6211\u4eec\u4e24\u4eba\u548c\u4ed6\u62fc\u6b7b\u6076\u6218\u3002\u4ed6\u771f\u635f\uff0c\u628a\u6211\u4eec\u6740\u6b7b\u4e5f\u8fd8 \u7f62\u4e86\uff0c\u4ed6\u5374\u7528\u5251\u5149\u628a\u6211\u4eec\u7f69\u7740\uff0c\u6211\u4eec\u4f24\u4e0d\u4e86\u4ed6\uff0c\u4ed6\u7684\u4e00\u67c4\u5b9d\u5251\u5374\u5728\u6211\u4eec\u7684\u9762\u95e8\u95ea\u6765\u95ea\u53bb\uff0c\u5927 \u58f0\u53eb\u6211\u4eec\u6295\u964d\uff0c\u6211\u4eec\u6c14\u6b7b\u5566\uff0c\u820d\u51fa\u6027\u547d\u5411\u4ed6\u7684\u5b9d\u5251\u51b2\u53bb\u3002\u5374\u4e0d\u77e5\u600e\u7684\uff0c\u4e00\u4e0b\u5b50\u6211\u4eec\u4e24\u4eba\u5c31\u5168 \u8eab\u9ebb\u8f6f\uff0c\u5012\u5728\u5730\u4e0a\u4e86\u3002\u201d\u6768\u4e91\u806a\u9053\uff1a\u201c\u4f60\u4eec\u7ed9\u4ed6\u70b9\u4e2d\u7a74\u9053\u4e86\u3002\u201d\u9ea6\u76d6\u63d0\u9053\uff1a\u201c\u6211\u4e5f\u66fe\u542c\u4f60\u8bf4 \u8fc7\u70b9\u7a74\u8fd9\u95e8\u529f\u592b\uff0c\u5374\u4e0d\u77e5\u5982\u6b64\u5389\u5bb3\uff01\u4ed6\u628a\u6211\u4eec\u6349\u7740\u4e4b\u540e\uff0c\u8bf4\u9053\uff1a\u597d\u5440\uff0c\u9ea6\u76d6\u63d0\uff0c\u6211\u65e9\u5c31\u77e5\u9053 \u4f60\u662f\u54c8\u8428\u514b\u65cf\u7684\u52c7\u58eb\uff0c\u6768\u4e91\u806a\u7684\u81c2\u818a\uff0c\u597d\uff0c\u6211\u5f97\u53eb\u4f60\u5403\u70b9\u82e6\u5934\uff0c\u66fc\u94c3\u5a1c\u4e5f\u88ab\u4eba\u8ba4\u51fa\u662f\u54c8\u8428\u514b \u65cf\u4e2d\u90a3\u4e2a\u6700\u52c7\u6562\u7684\u59d1\u5a18\u2014\u2014\u654c\u4eba\u4e00\u5411\u5c31\u662f\u8fd9\u6837\u7684\u53eb\u5979\u3002\u4e8e\u662f\u695a\u662d\u5357\u628a\u6211\u4eec\u6bcf\u4eba\u6253\u4e86\u4e8c\u5341\u97ad\uff0c \u628a\u6211\u4eec\u6253\u5f97\u51e0\u4e4e\u4e0d\u80fd\u52a8\u5f39\uff0c\u7136\u540e\u53eb\u4eba\u5c06\u6211\u4eec\u62bc\u4e0a\u4f0a\u7281\uff0c\u4ea4\u7ed9\u90a3\u4e2a\u4ec0\u4e48\u7eb3\u5170\u5c06\u519b\uff0c\u6211\u4eec\u5230\u4f0a\u7281 \u540e\uff0c\u88ab\u76d1\u7981\u5728\u5c06\u519b\u5e9c\u91cc\u767e\uff0c\u90a3\u4e9b\u4eba\u5bf9\u6211\u4eec\u5012\u5f88\u5ba2\u6c14\u3002\u5929\u5929\u6709\u9152\u6709\u8089\uff0c\u6211\u60f3\u6700\u591a\u662f\u6b7b\uff0c\u4e50\u5f97\u5403 \u4ed6\u7684\uff0c\u66fc\u94c3\u5a1c\u770b\u89c1\u6211\u7684\u98df\u76f8\uff0c\u8fd8\u66ff\u6211\u62c5\u5fc3\uff0c\u5979\u8bf4\uff1a\u9ea6\u76d6\u63d0\u5440\uff0c\u4f60\u53ef\u5f97\u5c0f\u5fc3\u3002\u4e0d\u8981\u4e0a\u654c\u4eba\u7684\u5f53 \u5440\uff01\u4ed6\u4eec\u8fd9\u6837\u6b3e\u5f85\u6211\u4eec\uff0c\u4e00\u5b9a\u662f\u60f3\u7528\u8f6f\u529f\uff0c\u628a\u6211\u4eec\u62c9\u8fc7\u53bb\uff0c\u8981\u6211\u4eec\u6295\u964d\uff0c\u4f60\u4e0d\u8981\u76f8\u4fe1\u8001\u864e\u7684 \u6148\u5fc3\u201d\u72af\u72f8\u7684\u5fae\u7b11\u5440\uff01\u6211\u5927\u7b11\u8d77\u6765\uff0c\u6084\u6084\u5bf9\u5979\u8bf4\u9053\uff1a\u4f60\u548c\u6211\u867d\u5206\u522b\u591a\u5e74\uff0c\u96be\u9053\u4f60\u8fd8\u4e0d\u77e5\u9053\u6211 \u7684\u6027\u5b50\uff0c\u6211\u4ece\u6765\u4e0d\u61c2\u5fe7\u6101\uff0c\u6709\u5403\u6709\u559d\u4f60\u8fd8\u5ba2\u6c14\u505a\u5565\uff1f\u505a\u4e2a\u9971\u9b3c\u603b\u597d\u8fc7\u505a\u4fc4\u9b3c\u5427\uff0c\u6211\u505a\u9b3c\u8138\uff0c \u628a\u5979\u9017\u5f97\u7b11\u4e86\uff0c\u5979\u540e\u6765\u5403\u5f97\u6bd4\u6211\u8fd8\u51f6\u3002\u201d\u66fc\u94c3\u5a1c\u547b\u9053\uff1a\u201c\u4e71\u56bc\u820c\u5934\uff01\u6768\u5927\u4fa0\uff0c\u6211\u77e5\u9053\u4ed6\u662f\u4e2a \u597d\u6c49\u5b50\uff0c\u4f46\u65f6\u523b\u63d0\u9192\u4ed6\uff0c\u603b\u4e0d\u662f\u574f\u4e8b\uff0c\u4f60\u8bf4\u662f\u5417\uff1f\u201d\u6768\u4e91\u806a\u8083\u7136\u8bf4\u9053\uff1a\u201c\u9ea6\u76d6\u63d0\uff0c\u4f60\u771f\u597d\u798f \u6c14\uff0c\u4f60\u7684\u59d1\u5a18\u662f\u771f\u6b63\u7684\u5173\u5fc3\u4f60\uff0c\u6bd4\u62e5\u7740\u4f60\u543b\uff0c\u8fd8\u5343\u767e\u500d\u7684\u7231\u4f60\uff01\u201d\u6b47\u4e86\u6b47\u53c8\u9053\uff1a\u201c\u9ea6\u76d6\u63d0\uff0c \u6211\u4e00\u5b9a\u66ff\u4f60\u51fa\u8fd9\u53e3\u6c14\uff0c\u6211\u8981\u628a\u695a\u662d\u5357\u6349\u6765\u4ea4\u7ed9\u4f60\u4eec\uff0c\u8ba9\u4f60\u4eec\u6bcf\u4eba\u628a\u4ed6\u6253\u56db\u5341\u97ad\u201d \u3000\u3000\u4e09\u4eba\u4e00\u8def\u8c08\u522b\u540e\u60c5\u5f62\uff0c\u8d70\u4e86\u4e94\u516d\u592d\uff0c\u5df2\u8fdb\u5165\u5927\u8349\u539f\uff0c\u79bb\u5f00\u4f0a\u7281\u5f88\u8fdc\u4e86\uff0c\u4e00\u65e5\u8d70\u81f3\u8349\u539f\u4e0a \u7684\u4e00\u4e2a\u9a7f\u7ad9\uff08\u7ed9\u9a6c\u5339\u52a0\u8349\u6599\u548c\u4f9b\u4eba\u4f4f\u5bbf\u7684\u5730\u65b9\uff09\uff0c\u9a7f\u7ad9\u65c1\u6709\u5c0f\u98df\u5e97\uff0c\u6709\u9a6c\u8089\u548c\u9152\u5356\u3002\u4e09\u4eba\u8fdb \u53bb\u4e70\u9152\uff0c\u5ffd\u89c1\u6709\u4e03\u516b\u4e2a\u6e05\u5175\u4e5f\u5728\u90a3\u91cc\u70e4\u9a6c\u8089\u3002\u5176\u4e2d\u6709\u4eba\u9053\uff1a\u201c\u7ebd\u6854\u5e90\u81ea\u5938\u6b66\u529f\u4e86\u5f97\uff0c\u662f\u5173\u5916 \u51fa\u540d\u7684\u6b66\u5e08\uff0c\u6253\u8d77\u6765\u5374\u4e00\u70b9\u4e5f\u4e0d\u9876\u4e8b\uff0c\u51e0\u767e\u4eba\u7ed9\u4e09\u4e2a\u4eba\u6253\u5f97\u4e03\u96f6\u516b\u843d\uff0c\u6211\u4eec\u6709\u4ec0\u4e48\u672c\u9886\uff0c\u90a3 \u8fd8\u7f62\u4e86\uff0c\u4ed6\u4e5f\u4e0d\u662f\u4e00\u4ea4\u624b\u5c31\u9003\u5c38\u53e6\u4e00\u4e2a\u9053\uff1a\u201c\u6211\u4eec\u8ffd\u90a3\u4e2a\u5353\u4e00\u822a\uff0c\u542c\u8bf4\u662f\u4ec0\u4e48\u6b66\u5f53\u6d3e\u6700\u5f3a\u7684 \u5251\u5ba2\uff0c\u540e\u6765\u90a3\u4e2a\u9752\u5e74\uff0c\u542c\u8bf4\u66f4\u662f\u5389\u5bb3\uff0c\u5c31\u662f\u540d\u9707\u5317\u7586\u7684\u6768\u4e91\u806a\uff01\u6211\u4eec\u6ca1\u6709\u89c1\u8fc7\u4ed6\uff0c\u4e0d\u77e5\u662f\u4e0d \u662f\uff1f\u4e0d\u8fc7\u6211\u5012\u76f8\u4fe1\u7ebd\u67af\u5e90\u7684\u8bdd\uff0c\u90a3\u65e5\u6211\u4eb2\u773c\u770b\u89c1\u90a3\u4e2a\u59d3\u6768\u7684\u628a\u51e0\u540d\u7edf\u9886\u6293\u8d77\u6765\u5c31\u6454\uff0c\u597d\u50cf\u5140 \u9e70\u6251\u9ebb\u96c0\u4e00\u6837\uff0c\u60f3\u6765\u4e0d\u662f\u6768\u4e91\u806a\u522b\u4eba\u4e5f\u6ca1\u90a3\u6837\u672c\u4e8b\uff01\u201d\u8bf4\u5230\u8fd9\u91cc\uff0c\u5ffd\u7136\u77a7\u89c1\u6768\u4e91\u806a\u6b63\u5728\u5bf9\u9762 \u7684\u89d2\u843d\u8e72\u7740\u559d\u9152\uff0c\u614c\u5f97\u5927\u53eb\u4e00\u58f0\uff0c\u201c\u5feb\u8d70\uff01\u5feb\u6559\u547d\u5440\uff01\u201d\u5176\u4ed6\u7684\u6e05\u5175\u6115\u7136\u4e0d\u77e5\uff0c\u6768\u4e91\u806a\u5927\u53e3 \u5927\u53e3\u7684\u5477\u9152\uff0c\u4e5f\u4e0d\u60f3\u7406\u4ed6\u4eec\u3002\u6e05\u5175\u4eec\u89c1\u540c\u4f34\u614c\u5f20\u7684\u53eb\u56b7\uff0c\u53c8\u6709\u51e0\u4e2a\u77a7\u89c1\u4e86\u6768\u4e91\u806a\uff0c\u4e00\u65f6\u60ca\u53eb \u4e4b\u58f0\u56db\u8d77\uff0c\u7eb7\u7eb7\u593a\u95e8\u800c\u51fa\uff0c\u5ffd\u7136\u9a7f\u7ad9\u4e2d\u6251\u51fa\u4e00\u4e2a\u8001\u5a46\u5a46\uff0c\u53cc\u81c2\u4e00\u795e\uff0c\u5c31\u628a\u51e0\u4e2a\u6e05\u5175\u5f39\u56de\u5e97 \u5185\uff0c\u53e6\u5916\u4e24\u4e2a\u60f3\u4ece\u5979\u52a9\u4e0b\u51b2\u8fc7\uff0c\u7ed9\u5979\u53cc\u624b\u4e00\u6293\uff0c\u5168\u90fd\u6454\u6b7b\u3002\u8001\u5a46\u5a46\u559d\u9053\uff1a\u201c\u4e0d\u51c6\u8d70\uff0c\u4f60\u4eec\u5feb \u8bf4\uff0c\u5353\u4e00\u822a\u5728\u54ea\u91cc\uff1f\u4f60\u4eec\u8ffd\u4ed6\u5e72\u5417\uff1f\u201d\u6e05\u5175\u4eec\u5413\u5f97\u9b42\u4e0d\u9644\u4f53\uff0c\u5927\u534a\u8bf4\u4e0d\u51fa\u8bdd\uff0c\u6709\u51e0\u4e2a\u6296\u6296\u7d22 \u7d22\u7684\u8bf4\u9053\uff1a\u201c\u4ed6\u7ed9\u6768\u4e91\u806a\u6551\u8d70\u4e86\uff0c\u53bb\u54ea\u91cc\u6211\u4eec\u4e0d\u77e5\u9053\u3002\u201d\u8001\u5a46\u5a46\u77a7\u89c1\u6768\u4e91\u806a\u5728\u559d\u9152\uff0c\u54fc\u9053\uff1a \u201c\u597d\uff01\u4f60\u4e5f\u5728\u8fd9\u91cc\uff01\u90a3\u5c31\u4e0d\u5fc5\u95ee\u4ed6\u4eec\u4e86\uff01\u201d\u968f\u8bf4\u968f\u628a\u6e05\u5175\u4e00\u4e2a\u4e2a\u6293\u8d77\uff0c\u5411\u5916\u4e71\u6454\uff0c\u6d3b\u5b8c\u65f6\uff0c \u4e03\u4eba\u4e2a\u6e05\u5175\u90fd\u5df2\u7ed9\u5979\u6454\u6b7b\u3002\u66fc\u94c3\u5a1c\u6084\u58f0\u95ee\u9053\uff1a\u201c\u8fd9\u8001\u5a46\u5a46\u662f\u8c01\uff1f\u8fd9\u6837\u51f6\uff0c\u8fd9\u4e9b\u6e05\u5175\u53c8\u4e0d\u662f\u5728 \u6218\u573a\u4e0a\u548c\u6211\u4eec\u6253\u4ed7\uff0c\u4f55\u5fc5\u8981\u4ed6\u4eec\u6b7b\u5f97\u8fd9\u6837\u60e8\uff01\u201d\u6768\u4e91\u806a\u4e5f\u6025\u5fd9\u6084\u58f0\u8bf4\u9053\uff1a\u201c\u5979\u662f\u767d\u53d1\u9b54\u5973\uff01 \u4f60\u4eec\u5343\u4e07\u522b\u5f97\u7f6a\u5979\uff01\u201d\u767d\u53d1\u9b54\u5973\u4f38\u624b\u6765\u6293\u6768\u4e91\u806a\uff0c\u6768\u4e91\u806a\u8f7b\u8f7b\u4e00\u95ea\uff0c\u5728\u65c1\u8fb9\u7ed9\u5979\u884c\u793c\uff0c\u8bf4 \u9053\uff1a\u201c\u767d\u8001\u524d\u8f88\uff0c\u4ec0\u4e48\u98ce\u628a\u4f60\u5439\u5230\u8fd9\u91cc\uff1f\u201d\u767d\u53d1\u9b54\u5973\u9053\uff1a\u201c\u6211\u6ca1\u6709\u529f\u592b\u548c\u4f60\u591a\u8bf4\uff0c\u4f60\u5feb\u544a\u8bc9 \u6211\uff0c\u5353\u4e00\u822a\u53bb\u4e86\u54ea\u91cc\uff1f\u4ed6\u662f\u4e0d\u662f\u548c\u4e00\u4e2a\u5973\u4eba\u5728\u4e00\u8d77\uff1f\u201d\u6768\u4e91\u806a\u9053\uff1a\u201c\u5353\u5e08\u53d4\u662f\u548c\u4e00\u4e2a\u59d1\u5a18\u5728 \u4e00\u8d77\uff0c\u4ed6\u8bf4\u4ed6\u8981\u9001\u90a3\u4f4d\u59d1\u5a18\u56de\u5173\u5185\u53bb\uff01\u201d\u767d\u53d1\u9b54\u5973\u51b7\u7b11\u9053\uff1a\u201c\u54fc\uff01\u6211\u624d\u4e0d\u4fe1\u4ed6\u7684\u9b3c\u8bdd\uff0c\u6211\u4ee5 \u524d\u8d74\u4ed6\u4eec\u90fd\u4e0d\u8d70\uff0c\u73b0\u5728\u5012\u80af\u4e56\u4e56\u7684\u8d70\u4e86\uff1f\u201d\u6768\u4e91\u806a\u4e0d\u77e5\u5979\u8bf4\u4ec0\u4e48\uff0c\u5b8c\u5168\u6478\u4e0d\u7740\u5934\u8111\u3002\u81ea\u53d1\u9b54 \u5973\u8fce\u9762\u53c8\u662f\u4e00\u6293\u6293\u6765\u3002\u53eb\u9053\uff1a\u201c\u6768\u4e91\u806a\uff0c\u4f60\u5e26\u6211\u53bb\u627e\u4ed6\u4eec\uff01\u201d \u3000\u3000----------- \u3000\u3000\u98ce\u4e91\u9601 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u585e\u5916\u5947\u4fa0\u4f20\u300b\u2014\u2014\u7b2c\u5341\u4e5d\u56de\u3000\u731c\u7591 \u6881\u7fbd\u751f\u300a\u585e\u5916\u5947\u4fa0\u4f20\u300b \u7b2c\u5341\u4e5d\u56de\u3000\u731c\u7591 \u3000\u3000\u6768\u4e91\u806a\u8eab\u5f62\u95ea\u52a8\uff0c\u767d\u53d1\u9b54\u5973\u4e00\u6293\u6293\u7a7a\u3002\u6768\u4e91\u806a\u9053\uff1a\u201c\u767d\u8001\u524d\u8f88\uff0c\u5f1f\u5b50\u5b9e\u5728\u4e0d\u77e5\u5353\u5e08\u53d4\u53bb \u5904\u3002\u201d\u81ea\u53d1\u9b54\u5973\u6014\u4e86\u4e00\u6014\uff0c\u602b\u7136\u4e0d\u60a6\uff0c\u51b7\u5ced\u8bf4\u9053\uff1a\u201c\u4f60\u7684\u6b66\u529f\u5df2\u5927\u6709\u8fdb\u5883\u4e86\uff0c\u5bf9\u540e\u751f\u665a\u8f88\uff0c \u6211\u4e00\u51fb\u4e0d\u4e2d\uff0c\u51b3\u4e0d\u518d\u5ea6\u51fa\u624b\u3002\u7b97\u4f60\u9020\u5316\uff0c\u4f60\u81ea\u53bb\u5427\uff0c\u6ca1\u6709\u4f60\u6211\u4e5f\u4e00\u6837\u80fd\u627e\u7740\u4ed6\u3002\u201d \u3000\u3000\u767d\u53d1\u9b54\u5973\u98d8\u7136\u897f\u53bb\uff0c\u6768\u4e91\u806a\u548c\u9ea6\u76d6\u63d0\u66fc\u94c3\u5a1c\u4e09\u4eba\u4e5f\u7eed\u5411\u5357\u884c\u3002\u4e00\u8def\u4e0a\uff0c\u9ea6\u76d6\u63d0\u72b9\u81ea\u6124\u6124 \u4e0d\u5e73\uff0c\u6768\u4e91\u806a\u9053\uff1a\u201c\u767d\u53d1\u9b54\u5973\u624b\u5e95\u6781\u8fa3\uff0c\u5979\u7684\u8bdd\u4e0d\u5bb9\u522b\u4eba\u4e0d\u542c\uff0c\u8fd9\u6b21\u8fd8\u7b97\u662f\u597d\u7684\u4e86\u3002\u201d\u81f3\u4e8e \u767d\u53d1\u9b54\u5973\u4e3a\u4ec0\u4e48\u8981\u627e\u5353\u4e00\u822a\uff0c\u6768\u4e91\u806a\u5c31\u4e0d\u77e5\u9053\u4e86\u3002 \u3000\u3000\u6768\u4e91\u806a\u7b49\u4e09\u4eba\u884c\u4e86\u4e03\u516b\u5929\uff0c\u5230\u4e86\u5580\u5c14\u6c81\u8349\u539f\uff0c\u6768\u4e91\u806a\u5174\u594b\u5f02\u5e38\uff0c\u4ed6\u6240\u8981\u627e\u7684\u54c8\u8428\u514b\u4eba\u7ec8 \u4e8e\u627e\u5230\u4e86\uff0c\u4ed6\u6b63\u81ea\u76d8\u7b97\u5982\u4f55\u91cd\u7ec4\u6297\u6e05\u4e49\u519b\uff0c\u9ea6\u76d6\u63d0\u5411\u524d\u4e00\u6307\uff0c\u6b23\u7136\u8bf4\u9053\uff1a\u201c\u8f6c\u8fc7\u8fd9\u4e00\u4e2a\u5c71 \u4e18\uff0c\u524d\u9762\u5c31\u662f\u6211\u4eec\u7684\u90e8\u843d\u4e86\u3002\u201d\u6768\u4e91\u806a\u4e00\u9a6c\u5f53\u524d\uff0c\u7ed5\u8fc7\u5c71\u4e18\uff0c\u679c\u7136\u89c1\u7740\u5927\u5927\u5c0f\u5c0f\u65e0\u6570\u5e10\u5e55\u3002 \u9ea6\u76d6\u63d0\u548c\u66fc\u94c3\u5a1c\u72c2\u547c\u9053\uff1a\u201c\u5144\u5f1f\u59d0\u59b9\u4eec\uff0c\u6211\u4eec\u56de\u6765\u4e86\uff01\u201d\u5e10\u5e55\u91cc\u7267\u6c11\u7eb7\u7eb7\u6d8c\u51fa\uff0c\u7834\u58f0\u96f7\u52a8\u3002 \u3000\u3000\u4eba\u7fa4\u4e2d\u5ffd\u89c1\u4e00\u6761\u7ea2\u5dfe\u8fce\u98ce\u98d8\u62c2\uff0c\u6768\u4e91\u806a\u5403\u4e86\u4e00\u60ca\uff0c\u4e00\u4e2a\u5c11\u5973\u75be\u98ce\u822c\u8d8a\u7fa4\u800c\u51fa\u3002\u9ad8\u58f0\u53eb \u9053\uff1a\u201c\u6768\u4e91\u806a\uff0c\u600e\u4e48\u4f60\u4e5f\u6765\u4e86\uff01\u201d\u8fd9\u5c11\u5973\u6b63\u662f\u98de\u7ea2\u5dfe\uff0c\u8fd9\u970e\u90a3\u95f4\uff0c\u6768\u4e91\u806a\u7684\u5fc3\u5c31\u5982\u5012\u7ffb\u4e86\u4e94 \u5473\u67b6\uff0c\u53c8\u82e6\u53c8\u751c\u53c8\u9178\u53c8\u8fa3\uff0c\u4e00\u65f6\u95f4\u7adf\u8bf4\u4e0d\u51fa\u6d3b\u6765\uff01 \u3000\u3000\u98de\u7ea2\u5dfe\u62bf\u5634\u4e00\u7b11\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u50bb\u4e86\u4e48\uff1f\u201d\u4e3a\u4ec0\u4e48\u8001\u662f\u770b\u6211\uff0c\u5374\u4e0d\u8bf4\u8bdd\uff1f\u201d\u8fd9\u970e\u90a3\u95f4 \u7eb3\u5170\u660e\u6167\u7684\u5f71\u5b50\u500f\u7684\u6cdb\u4e0a\u5fc3\u5934\uff0c\u6768\u4e91\u806a\u5ffd\u7136\u6709\u4e00\u79cd\u81ea\u759a\u4e4b\u611f\uff0c\u6b63\u4f8d\u8bf4\u8bdd\uff0c\u4e00\u4e2a\u866c\u987b\u5927\u6c49\u7a81\u7136 \u81ea\u65c1\u95ea\u51fa\uff0c\u7eb5\u58f0\u7b11\u9053\uff1a\u201c\u6768\u4e91\u806a\u53ef\u5e76\u4e0d\u50bb\uff0c\u6211\u4eec\u6253\u751f\u6253\u6b7b\u7684\u65f6\u5019\uff0c\u4ed6\u5374\u6709\u7f8e\u4eba\u540c\u8f66\uff0c\u62a4\u9001\u7eb3 \u5170\u79c0\u5409\u7684\u5973\u513f\u53bb\u4f0a\u7281\u5462\uff01\u201d\u6768\u4e91\u806a\u6012\u559d\u9053\uff0c\u201c\u95ed\u4f60\u7684\u9e1f\u53e3\uff01\u201d\u98de\u7ea2\u5dfe\u9762\u8272\u4e00\u53d8\uff0c\u968f\u5373\u9547\u9759\u4e0b \u672a\uff0c\u628a\u6768\u4e91\u806a\u548c\u5b5f\u7984\u62c9\u5f00\uff0c\u9762\u5411\u5b5f\u7984\u8bf4\u9053\uff0c\u201c\u6709\u8bdd\u4eca\u665a\u518d\u8bf4\uff0c\u54c8\u8428\u514b\u4eba\u6b63\u5728\u6b22\u8fce\u4ed6\u4eec\u65cf\u4e2d\u7684 \u82f1\u96c4\uff0c\u4f60\u5374\u5728\u8fd9\u91cc\u5435\u5634\uff01\u201d \u3000\u3000\u5357\u7586\u7684\u54c8\u8428\u514b\u914b\u957f\uff0c\u4e00\u542c\u6768\u4e91\u806a\u5230\u6765\uff0c\u5982\u540c\u7a81\u7136\u95f4\u4ece\u5929\u4e0a\u6389\u4e0b\u4e00\u4ef6\u5b9d\u8d1d\uff0c\u6768\u4e91\u806a\u8fd9\u51e0\u5e74 \u6765\u5e2e\u52a9\u5317\u7586\u7684\u54c8\u8428\u514b\u4eba\u6253\u4ed7\uff0c\u5357\u7586\u7684\u54c8\u8428\u514b\u4eba\u81ea\u7136\u4e5f\u8033\u719f\u80fd\u8be6\u3002\u914b\u957f\u9ad8\u9ad8\u5174\u5174\u7684\u8bf4\u9053\uff1a\u201c\u6768 \u5927\u4fa0\uff0c\u6211\u4eec\u65e5\u6c7e\u591c\u76fc\uff0c\u7ec8\u4e8e\u628a\u4f60\u76fc\u6765\u4e86\u3002\u524d\u51e0\u5929\u54c8\u739b\u96c5\u5973\u82f1\u96c4\u5230\u6765\uff0c\u8fd8\u63d0\u8d77\u4f60\uff0c\u4f60\u4eec\u4e24\u4eba\u539f \u6765\u662f\u8ba4\u8bc6\u7684\uff0c\u90a3\u771f\u662f\u5927\u597d\u4e86\uff0c\u6211\u6b63\u548c\u54c8\u739b\u96c5\u76df\u4e3b\u5546\u8bae\u52a0\u76df\u7684\u4e8b\u60c5\uff0c\u4f60\u6765\u4e86\uff0c\u53ef\u8981\u66ff\u6211\u4eec\u591a\u51fa \u70b9\u4e3b\u610f\u3002\u201d\u5b5f\u7984\u5728\u65c1\u8fb9\u563f\u563f\u51b7\u7b11\u3002\u6768\u4e91\u806a\u6ee1\u809a\u76ae\u95f7\u6c14\uff0c\u5f3a\u81ea\u5fcd\u7740\uff0c\u4e00\u9762\u4e0e\u54c8\u8428\u514b\u7684\u914b\u957f\u503e \u8c08\uff0c\u4e00\u9762\u95ee\u98de\u7ea2\u4e2d\u522b\u540e\u7684\u906d\u9047\u3002 \u3000\u3000\u539f\u6765\u90a3\u65e5\u5728\u8349\u539f\u7684\u5927\u6df7\u6218\uff0c\u8d77\u521d\u662f\u5357\u7586\u5404\u65cf\u5360\u4e86\u4e0a\u98ce\uff0c\u540e\u6765\u6e05\u5175\u5927\u4e3e\u589e\u63f4\uff0c\u7267\u6c11\u4eec\u62b5\u6321 \u4e0d\u4f4f\u3002\u56db\u6563\u5954\u9003\u3002\u98de\u7ea2\u5dfe\u5728\u63a2\u201c\u9ed1\u6cc9\u6c34\u201d\u4e4b\u65f6\uff0c\u8eab\u53d7\u7684\u4f24\uff0c\u5e78\u5f97\u582a\u6070\u65cf\u7684\u56db\u9a91\u571f\u4fdd\u62a4\uff0c\u76f4\u9003 \u51fa\u6570\u767e\u91cc\u5916\uff0c\u8fd9\u624d\u627e\u7740\u4e86\u54c8\u8428\u514b\u4eba\u3002\u81f3\u4e8e\u5b5f\u7984\uff0c\u5219\u662f\u540e\u6765\u548c\u752b\u7586\u7684\u5404\u65cf\u914b\u957f\u540c\u6765\u7684\uff0c \u3000\u3000\u8fd9\u4e00\u665a\u54c8\u8428\u514b\u65cf\u548c\u5357\u7586\u5404\u65cf\u9996\u957f\u6b3e\u5f85\u6768\u4e91\u806a\u3002\u6b63\u5f53\u54c8\u8428\u514b\u7684\u914b\u957f\u76db\u8d5e\u626c\u4e91\u806a\u4e4b\u65f6\uff1b\u5580\u8fbe \u5c14\u65cf\u7684\u914b\u957f\u5b5f\u7984\u5ffd\u7136\u7ad9\u8d77\u6765\u9053\u3002\u201c\u6211\u4eec\u2018\u62db\u5b50\u2019\uff08\u773c\u775b\uff09\u53ef\u8981\u653e\u4eae\u4e00\u70b9\uff1b\u522b\u7c91\u61e6\u592b\u5f53\u6210\u597d \u6c49\uff0c\u628a\u5978\u7ec6\u5f53\u6210\u82f1\u96c4\uff01\u201d\u54c8\u8428\u514b\u914b\u957f\u77aa\u773c\u8bf4\u9053\uff1a\u201c\u4ec0\u4e48\u8bdd\u3002\u201d\u5b5f\u7984\u51b7\u7b11\u9053\uff1a\u201c\u6768\u4e91\u806a\u5728\u5927\u6218 \u4e4b\u65f6\uff0c\u79c1\u81ea\u9003\u8131\uff0c\u5e2e\u52a9\u7eb3\u5170\u79c0\u5409\u7684\u5973\u513f\uff0c\u6740\u4e86\u6211\u4eec\u5580\u8fbe\u5c14\u65cf\u7684\u4e24\u540d\u52c7\u58eb\uff0c\u4e00\u8def\u4e0e\u654c\u4eba\u7684\u5973\u513f \u540c\u8f66\uff0c\u5728\u4f0a\u7281\u4f4f\u4e86\u8fd9\u4e48\u4e45\u624d\u56de\u6765\u3002\u6211\u60f3\u8bf7\u95ee\u54c8\u739b\u96c5\u76df\u4e3b\u548c\u5404\u65cf\u7684\u7236\u8001\u4eec\uff0c\u50cf\u6768\u4e91\u806a\u8fd9\u6837\u7684\u884c \u5f84\uff0c\u5230\u5e95\u662f\u5978\u7ec6\u8fd8\u662f\u82f1\u96c4\uff1f\u201d\u98de\u7ea2\u5dfe\u51db\u7136\u5bf9\u6768\u4e91\u806a\u9053\uff1a\u201c\u6709\u8fd9\u6837\u7684\u4e8b\u5427\uff1f\u201d\u5854\u5c71\u65cf\u7684\u914b\u957f\u53eb \u9053\uff1a\u201c\u6768\u4e91\u806a\u662f\u5978\u7ec6\uff0c\u6211\u6b7b\u4e5f\u4e0d\u4fe1\uff01\u201d \u3000\u3000\u6768\u4e91\u806a\u7f13\u7f13\u8d77\u7acb\uff0c\u9762\u5bf9\u7740\u98de\u7ea2\u5dfe\u9053\uff1a\u201c\u7eb3\u5170\u79c0\u5409\u7684\u5973\u513f\u662f\u6211\u6551\u51fa\u6765\u7684\uff01\u201d\u98de\u7ea2\u5dfe\u9762\u8272\u5927 \u53d8\uff0c\u5168\u5802\u54d7\u7136\u3002\u6768\u4e91\u806a\u9053\uff1a\u201c\u4f46\u5b5f\u7984\u4e5f\u662f\u6211\u6551\u51fa\u7684\uff0c\u6709\u4e00\u80a1\u6e05\u5175\u8ffd\u6765\uff0c\u662f\u6211\u548c\u4e00\u4f4d\u6b66\u6797\u524d\u8f88 \u6321\u4f4f\uff0c\u4ed6\u624d\u80fd\u4ece\u5bb9\u9003\u8d70\u7684\uff01\u201d\u76c2\u7984\u6ee1\u9762\u901a\u7ea2\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u6211\u4e0d\u9886\u4f60\u7684\u60c5\uff0c\u4f60\u5148\u628a\u6211\u7684\u7a74\u9053 \u70b9\u4e86\uff0c\u4f60\u540e\u53c8\u5047\u4ec1\u5047\u4e49\u7684\u66ff\u6211\u89e3\u5f00\uff0c\u548c\u90a3\u73ed\u6e05\u5175\u53ae\u6740\u3002\u201d\u98de\u7ea2\u5dfe\u9053\uff1a\u201c\u90a3\u4e48\u6768\u4e91\u806a\u66ff\u4f60\u6321\u4f4f \u6e05\u5175\u7684\u4e8b\u662f\u771f\u7684\u4e86\uff01\u201d\u5b5f\u7984\u4e0d\u8bed\uff0c\u9ea6\u76d6\u63d0\u5374\u53eb\u8d77\u6765\u9053\uff1a\u201c\u4f60\u4e0d\u9886\u4ed6\u7684\u60c5\uff0c\u6211\u9886\u4ed6\u7684\u60c5\uff0c\u6211\u4eec \u4e24\u4eba\u90fd\u662f\u4ed6\u6551\u51fa\u6765\u7684\uff01\u6211\u4eec\u5168\u9760\u4ed6\u6740\u9000\u7eb3\u5170\u79c0\u5409\u7684\u536b\u58eb\uff0c\u4f24\u4e86\u591a\u94ce\uff0c\u8fd9\u624d\u80fd\u9003\u8131\u51fa\u6765\uff01\u201d\u98de \u7ea2\u5dfe\u9053\uff1a\u201c\u6768\u4e91\u806a\uff0c\u6211\u4e5f\u4e0d\u4fe1\u4f60\u662f\u5978\u7ec6\uff0c\u4f46\u4f60\u4e3a\u4ec0\u4e48\u8981\u6551\u62a4\u7eb3\u5170\u79c0\u5409\u7684\u5973\u513f\uff1f\u201d\u5b5f\u7984\u52a0\u4e0a\u4e00 \u53e5\u6d3b\u9053\uff1a\u201c\u8fd8\u6709\u4f60\u4e3a\u4ec0\u4e48\u8981\u5e2e\u4ed6\u6740\u6389\u6211\u4eec\u7684\u4e24\u540d\u52c7\u58eb\uff1f \u3000\u3000\u6768\u4e91\u806a\u9762\u8272\u5e84\u4e25\uff0c\u5927\u58f0\u95ee\u98de\u7ea2\u5dfe\u9053\uff1a\u201c\u54c8\u739b\u96c5\uff0c\u4f60\u662f\u5973\u4eba\u3002\u6211\u95ee\u4f60\uff0c\u5047\u5982\u4f60\u906d\u53d7\u522b\u4eba\u7684 \u5f3a\u66b4\uff0c\u4f60\u62b5\u6297\u4e0d\u62b5\u6297\u5462\uff1f\u7eb3\u5170\u79c0\u5409\u662f\u6211\u4eec\u7684\u654c\u4eba\u3002\u4f46\u4ed6\u7684\u5973\u513f\u5374\u672a\u4e0e\u6211\u4eec\u4e3a\u654c\uff01\u5b5f\u7984\u7684\u624b\u4e0b \u8981\u4fb5\u72af\u5979\uff0c\u7ed9\u5979\u6740\u4e86\uff0c\u4e3a\u4ec0\u4e48\u8981\u5c06\u8d23\u4efb\u538b\u5728\u6211\u7684\u5934\u4e0a\uff1f\u201d\u5b5f\u7984\u9053\uff1a\u201c\u5979\u662f\u6211\u4eec\u7684\u4fd8\u864f\uff0c\u4e3a\u4ec0 \u4e48\u4e0d\u53ef\u4ee5\u968f\u6211\u4eec\u7684\u610f\u601d\u5904\u7f6e\uff1f\u201d\u6768\u4e91\u806a\u6717\u58f0\u9053\uff1a\u201c\u6211\u5c31\u53cd\u5bf9\u4e0d\u628a\u4fd8\u864f\u5f53\u4eba\u7684\u5904\u7f6e\uff0c\u6ee1\u6e05\u9791\u5b50 \u6349\u5230\u6211\u4eec\u7684\u4eba\u3002\u968f\u4fbf\u5978\u6deb\u5974\u5f79\uff0c\u96be\u9053\u4f60\u4e5f\u8981\u5b66\u4ed6\u4eec\u7684\u6837\u5b50\u3002\u201d\u4fd8\u864f\u5c5e\u4e8e\u80dc\u5229\u8005\u7684\u5236\u5ea6\uff0c\u662f\u90e8 \u843d\u6c11\u65cf\u51e0\u5343\u5e74\u6765\u7684\u4e60\u60ef\uff0c\u6768\u4e91\u806a\u7684\u6d3b\u4e00\u51fa\uff0c\u987f\u5f15\u8d77\u7a83\u7a83\u79c1\u8bae\u3002\u6768\u4e91\u806a\u53c8\u5bf9\u5b5f\u7984\u51b7\u7b11\u9053\uff1a\u201c\u4f55 \u51b5\u5979\u8fd8\u6ca1\u6709\u6210\u4e3a\u4f60\u4eec\u7684\u4fd8\u864f\uff0c\u4f60\u90a3\u4e24\u4f4d\u624b\u4e0b\uff0c\u521a\u4e0a\u524d\u52a8\u624b\uff0c\u5c31\u7ed9\u5979\u6740\u4e86\u3002\u90a3\u65f6\u5979\u8fd8\u5728\u91cd\u75c5\u4e4b \u4e2d\uff01\u201d \u3000\u3000\u98de\u7ea2\u5dfe\u9762\u8272\u6c89\u6697\uff0c\u5ffd\u7136\u62cd\u638c\u53eb\u5927\u5bb6\u9759\u4e0b\uff0c\u6bc5\u7136\u8bf4\u9053\uff1a\u201c\u6b3a\u8d1f\u75c5\u4e2d\u7684\u5987\u5973\uff0c\u90a3\u662f\u7f6a\u6709\u5e94 \u5f97\u3002\u53ea\u662f\u6768\u4e91\u806a\uff0c\u6211\u5012\u8981\u95ee\u4f60\uff0c\u4f60\u662f\u600e\u6837\u8ba4\u8bc6\u7eb3\u5170\u79c0\u5409\u7684\u5973\u513f\u7684\uff1f\u4f60\u4e3a\u4ec0\u4e48\u8981\u4fdd\u62a4\u5979\uff1f\u201d\u6768 \u4e91\u806a\u4f4e\u58f0\u8bf4\u8fd9\uff1a\u201c\u5bf9\u4e0d\u4f4f\uff0c\u98de\u7ea2\u5dfe\uff0c\u90a3\u662f\u6211\u7684\u79c1\u4e8b\uff01\u53ea\u8981\u5979\u4e0d\u662f\u6211\u4eec\u7684\u654c\u4eba\uff0c\u6211\u4e3a\u4ec0\u4e48\u4e0d\u53ef \u4ee5\u548c\u5979\u7ed3\u4ea4\uff01\u201d\u5b5f\u7984\u5927\u58f0\u559d\u9053\uff1a\u201c\u4f60\u5206\u660e\u5fc3\u91cc\u6709\u9b3c\uff0c\u7eb3\u5170\u79c0\u5409\u662f\u6211\u4eec\u7684\u6b7b\u5bf9\u5934\u3002\u4ed6\u7684\u5973\u513f\u5c31 \u4e0d\u662f\u597d\u4eba\uff0c\u5c82\u6709\u548c\u4ed6\u7684\u5973\u513f\u7ed3\u4ea4\uff0c\u5374\u53c8\u548c\u4ed6\u4e3a\u654c\u7684\u9053\u7406\u3002\u6768\u4e91\u806a\u3002\u6211\u63ed\u7a7f\u4e86\u4f60\u5427\uff0c\u6211\u770b\u4f60\u662f \u88ab\u4ed6\u5973\u513f\u7684\u7f8e\u8272\u8ff7\u4f4f\u4e86\uff01\u7ed9\u5979\u62db\u4f60\u505a\u5a07\u5ba2\u4e86\uff01\u201d\u98de\u7ea2\u5dfe\u5fc3\u4e2d\u9635\u9635\u523a\u75db\uff0c\u5374\u4e0d\u8bf4\u8bdd\u3002\u4f17\u4eba\u53c8\u7a83 \u7a83\u79c1\u8bae\uff0c\u5728\u654c\u4eba\u9635\u8425\u4e2d\u628a\u597d\u4eba\u5212\u5206\u51fa\u6765\u7684\u89c2\u5ff5\u3002\u5927\u591a\u6570\u7684\u914b\u957f\u4eec\u90fd\u8fd8\u672a\u6709\u3002\u6768\u4e91\u806a\u773c\u775b\u6a2a\u626b \u5168\u573a\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u4e5f\u77e5\u9053\u8fd9\u4f1a\u72af\u7591\uff0c\u4f46\u600e\u6837\u624d\u80fd\u4f7f\u4f60\u4eec\u4e0d\u7591\u5fc3\u5462\uff1f\u6211\u5012\u60f3\u5f97\u4e00\u4e2a\u529e\u6cd5\uff0c \u8bf8\u4f4d\u770b\u770b\u884c\u4e0d\u884c\uff1f\u201d\u5854\u5c71\u65cf\u7684\u914b\u957f\u9053\uff0c\u201c\u8bf7\u8bf4\uff01\u201d\u6768\u4e91\u806a\u9053\uff1a\u201c\u6211\u542c\u8bf4\u54c8\u8428\u514b\u65cf\u914b\u957f\u7684\u7231\u5b50 \u7ed9\u695a\u662d\u5357\u864f\u53bb\uff0c\u73b0\u5728\u8fd8\u672a\u653e\u56de\uff0c\u6211\u613f\u610f\u66ff\u4ed6\u628a\u7231\u5b50\u593a\u56de\uff0c\u5e76\u5c06\u695a\u662d\u5357\u6d3b\u6349\u56de\u6765\uff01\u201d\u54c8\u8428\u514b\u65cf \u7684\u914b\u957f\u773c\u89d2\u6f6e\u6e7f\uff0c\u5583\u5583\u8bf4\u9053\uff1a\u201c\u6768\u4e91\u806a\u6211\u53ef\u6ca1\u6709\u7591\u5fc3\u4f60\u554a\uff0c\u4f60\u662f\u6211\u4eec\u7684\u64ce\u5929\u4e00\u4f4f\uff0c\u6211\u53ef\u4e0d\u613f \u4f60\u5355\u9a91\u5192\u9669\uff01\u201d\u5b5f\u7984\u51b7\u7b11\u9053\uff0c\u201c\u8c01\u4e0d\u77e5\u9053\u695a\u662d\u5357\u662f\u4f60\u7684\u5e08\u5f1f\uff0c\u4f60\u54ea\u91cc\u662f\u4ec0\u4e48\u5355\u9a91\u5192\u9669\u3002\u4f60\u5206 \u660e\u662f\u60f3\u548c\u4ed6\u52fe\u7ed3\uff0c\u8ba9\u4f60\u53bb\u90a3\u662f\u653e\u864e\u5f52\u5c71\uff01\u201d\u6768\u4e91\u806a\u53cc\u77b3\u55b7\u706b\uff0c\u5fc3\u4e2d\u6012\u6781\uff0c\u53cc\u638c\u4e00\u51fb\uff0c\u5c31\u8981\u53d1 \u4f5c\u3002\u201d\u98de\u7ea2\u5dfe\u5ffd\u7136\u62cd\u638c\u8bf4\u9053\uff1a\u201c\u8bf8\u4f4d\u603b\u4e0d\u4f1a\u6000\u7591\u6211\u4e5f\u662f\u5978\u7ec6\u5427\uff1f\u6211\u966a\u4ed6\u53bb\uff0c\u6349\u4e0d\u7740\u695a\u662d\u5357\u6211 \u4eec\u5c31\u4e0d\u56de\u6765\uff0c\u6211\u7528\u4eba\u5934\u62c5\u4fdd\u6768\u4e91\u806a\u4e0d\u662f\u5978\u7ec6\uff01\u201d\u98de\u7ea2\u5dfe\u662f\u5357\u7586\u5404\u65cf\u7684\u76df\u4e3b\uff0c\u6b64\u8bdd\u4e00\u51fa\uff0c\u5168\u5802 \u8083\u7136\uff0c\u6ca1\u6709\u4eba\u6562\u8bf4\u7b2c\u4e8c\u53e5\u6d3b\u3002 \u3000\u3000\u7b2c\u4e8c\u5929\u665a\u4e0a\uff0c\u98de\u7ea2\u5dfe\u548c\u6768\u4e91\u806a\u6362\u4e0a\u591c\u884c\u8863\uff0c\u540c\u63a2\u51e0\u5341\u91cc\u5916\u695a\u662d\u5357\u6240\u9a7b\u7684\u57ce\u5821\uff0c\u4e00\u8def\u4e0a\u98de \u7ea2\u5dfe\u90fd\u662f\u542b\u55d4\u4e0d\u8bed\uff0c\u6768\u4e91\u806a\u5c61\u6b21\u60f3\u5411\u5979\u8bf4\u660e\u7eb3\u5170\u660e\u6167\u7684\u4e8b\u60c5\uff0c\u98de\u7ea2\u5dfe\u5374\u677f\u7740\u9762\u5b54\u9053\uff0c\u201c\u8fd9\u662f \u4f60\u7684\u79c1\u4e8b\uff0c\u6211\u7ba1\u4e0d\u7740\uff01\u4f55\u5fc5\u8bf4\u7ed9\u6211\u542c\uff01\u201d\u6768\u4e91\u806a\u6700\u540e\u6168\u7136\u8bf4\u9053\uff1a\u201c\u98de\u7ea2\u5dfe\u4ee5\u4f60\u6211\u7684\u4ea4\u60c5\uff0c\u4e3a \u4f55\u8fd9\u6837\u89c1\u5916\uff1f\u6211\u4e0d\u613f\u610f\u5bf9\u90a3\u4e9b\u4eba\u8bb2\uff0c\u5e76\u4e0d\u662f\u4e0d\u613f\u610f\u5bf9\u4f60\u8bb2\u5440\uff01\u6211\u628a\u4f60\u5f53\u6210\u81f3\u4eb2\u7684\u59d0\u59b9\uff0c\u5982\u679c \u4f60\u4e0d\u5acc\u5f03\u3002\u6211\u4e5f\u613f\u4f60\u628a\u6211\u5f53\u6210\u81f3\u4eb2\u7684\u5144\u5f1f\uff01\u201d\u98de\u7ea2\u5dfe\u5ae3\u7136\u7b11\u9053\uff1a\u201c\u662f\u5417\uff1f\u6211\u81ea\u7136\u613f\u53eb\u4f60\u505a\u54e5 \u54e5\uff0c\u53ea\u6015\u4f60\u89c1\u4e86\u59d0\u59d0\u5c31\u5fd8\u4e86\u59b9\u59b9\uff01\u201d\u6768\u4e91\u806a\u8e59\u7709\u8bf4\u9053\uff0c\u201c\u98de\u7ea2\u5dfe\uff0c\u6211\u8981\u5bf9\u4f60\u8bf4\u6211\u548c\u7eb3\u5170\u660e\u6167 \u4e4b\u95f4\u2026\u2026\u201d\u98de\u7ea2\u5dfe\u622a\u7740\u8bf4\u9053\uff1a\u201c\u5e76\u6ca1\u6709\u4ec0\u4e48\u82df\u4e14\u4e4b\u4e8b\uff0c\u662f\u5417\uff1f\u4f60\u4e0d\u8981\u5fd9\u7740\u89e3\u91ca\uff0c\u4e14\u5148\u628a\u695a\u662d \u5357\u6349\u56de\u518d\u8bf4\u5427\uff01\u201d\u573a\u4e91\u806a\u5fc3\u5982\u5200\u7ede\u3002\u4e3a\u5979\u96be\u8fc7\u3002\u5979\u8fd8\u4ee5\u4e3a\u81ea\u5df1\u548c\u660e\u6167\u5e76\u6ca1\u5176\u4ed6\u5173\u7cfb\uff0c\u60f3\u5411\u5979 \u89e3\u91ca\u660e\u767d\uff0c\u8c01\u77e5\u81ea\u5df1\u5df2\u548c\u660e\u6167\u6210\u4e86\u592b\u59bb\u3002\u6768\u4e91\u806a\u89c1\u5979\u8fd9\u4e2a\u6837\u5b50\uff0c\u8bdd\u5230\u53e3\u8fb9\uff0c\u53c8\u518d\u7559\u4f4f\u3002\u5fc3 \u60f3\uff0c\u4e00\u8bf4\u51fa\u6765\uff0c\u6050\u6015\u5979\u62b5\u53d7\u4e0d\u4f4f\uff0c\u5c82\u4e0d\u8bef\u4e86\u8981\u6d3b\u6349\u695a\u662d\u5357\u4e4b\u4e8b\uff1f\u4e5f\u7f62\uff0c\u7b49\u4e8b\u60c5\u529e\u5b8c\u4e4b\u540e\u518d\u8bf4 \u4e5f\u597d\u3002 \u3000\u3000\u4e24\u4eba\u8f7b\u529f\u8d85\u5353\uff0c\u8bdd\u672a\u8bf4\u5b8c\uff0c\u695a\u662d\u5357\u6240\u4f4f\u7684\u57ce\u5821\uff0c\u5df2\u73b0\u5728\u773c\u524d\uff0c\u4e24\u4eba\u7ea6\u597d\u6697\u53f7\uff0c\u4e00\u5357\u4e00 \u5317\uff0c\u98d8\u8eab\u767b\u4e0a\u57ce\u5934\u3002 \u3000\u3000----------- \u3000\u3000\u98ce\u4e91\u9601 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u585e\u5916\u5947\u4fa0\u4f20\u300b\u2014\u2014\u7b2c\u4e8c\u5341\u56de\u3000\u6d3b\u6349\u695a\u662d\u5357 \u6881\u7fbd\u751f\u300a\u585e\u5916\u5947\u4fa0\u4f20\u300b \u7b2c\u4e8c\u5341\u56de\u3000\u6d3b\u6349\u695a\u662d\u5357 \u3000\u3000\u6768\u4e91\u806a\u548c\u98de\u7ea2\u5dfe\u4e24\u4eba\u90fd\u662f\u8f7b\u529f\u7edd\u9876\uff0c\u8fdb\u4e86\u57ce\u5821\uff0c\u6cbf\u7740\u4e24\u8fb9\u6c11\u623f\uff0c\u9e64\u4f0f\u86c7\u884c\uff0c\u8f7b\u767b\u5de7\u7eb5\uff0c \u4e0d\u6d88\u591a\u65f6\uff0c\u5df1\u5230\u57ce\u4e2d\u7684\u5e9c\u8859\uff0c\u98de\u7ea2\u4e2d\u6b63\u8981\u8df3\u4e0a\u5c4b\u810a\uff0c\u51b7\u4e0d\u9632\u547c\u4e00\u58f0\u6025\u98ce\u98d2\u7136\uff0c\u4e00\u6761\u7855\u957f\u4eba \u5f71\uff0c\u5e26\u7740\u4e00\u80a1\u91d1\u98ce\uff0c\u76f4\u5411\u98de\u7ea2\u5dfe\u5934\u9876\u98de\u6251\u4e0b\u6765\uff0c\u98de\u7ea2\u5dfe\u51fa\u5176\u4e0d\u610f\uff0c\u51e0\u4e4e\u88ab\u4ed6\u6240\u7740\uff0c\u4e0d\u7981\u5927\u5403 \u4e00\u60ca\uff0c\u6765\u4e0d\u53ca\u62d4\u5251\u51fa\u9798\uff0c\u6025\u5fd9\u7528\u4e2a\u201c\u7ec6\u80f8\u5de7\u7ffb\u4e91\u201d\uff0c\u6258\u5730\u5411\u540e\u4e00\u8df3\uff0c\u65b9\u624d\u907f\u8fc7\u51f6\u950b\uff0c\u7b49\u5230\u5b9a \u775b\u770b\u65f6\uff0c\u89c1\u88ad\u51fb\u81ea\u5df1\u7684\uff0c\u7adf\u7136\u662f\u4e00\u4e2a\u9ad8\u5927\u756a\u50e7\uff0c\u624b\u4f7f\u4e00\u67c4\u5927\u65ab\u5200\uff0c\u98de\u7ea2\u5dfe\u4e00\u6b20\u8eab\uff0c\u94ee\u94ee\u4e24 \u54cd\uff0c\u77ed\u5251\u5411\u5200\u80cc\u4e0a\u4e00\u683c\uff0c\u628a\u756a\u50e7\u7684\u5927\u780d\u5200\u76f4\u64a9\u51fa\u53bb\uff0c\u756a\u50e7\u4e00\u51fb\u4e0d\u4e2d\uff0c\u8eab\u4f3c\u98ce\u8f66\uff0c\u500f\u7136\u4e00\u8f6c\uff0c \u5200\u5149\u95ea\u5904\uff0c\u547c\u58f0\u98ce\u54cd\uff0c\u201c\u602a\u87d2\u7ffb\u8eab\u201d\uff0c\u53c8\u5411\u98de\u7ea2\u4e2d\u62e6\u8170\u65a9\u6765\u3002\u98de\u7ea2\u4e2d\u52c3\u7136\u5927\u6012\uff0c\u957f\u97ad\u5237\u7684\u4e00 \u54cd\uff0c\u628a\u756a\u50e7\u624b\u8155\u7f20\u4f4f\uff0c\u8d81\u52bf\u4e00\u62c9\uff0c\u501f\u529b\u6253\u529b\uff0c\u628a\u756a\u50e7\u6c34\u725b\u822c\u7684\u8eab\u8eaf\uff0c\u76f4\u626f\u8fc7\u6765\u3002\u90a3\u756a\u50e7\u6b63\u8981 \u53eb\u558a\uff0c\u5ffd\u7136\u8170\u810a\u4e00\u9ebb\uff0c\u6768\u4e91\u806a\u5feb\u5982\u95ea\u7535\uff0c\u4f38\u6307\u70b9\u4e86\u4ed6\u7684\u7a74\u9053\uff0c\u98de\u7ea2\u4e2d\u4e00\u5251\u523a\u53bb\uff0c\u5374\u7ed9\u6768\u4e91\u806a \u6258\u7740\uff0c\u8bf4\u58f0\u201c\u4e14\u6162\uff01\u201d\u5b9d\u5251\u67b6\u5728\u756a\u50e7\u7684\u9888\u540e\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u5929\u9f99\u7985\u5e08\u7684\u95e8\u4e0b\uff1f\u201d\u756a\u50e7\u6012 \u5230\uff1b\u201c\u662f\u53c8\u600e\u6837\uff1f\u201d\u6768\u4e91\u806a\u9053\uff1a\u201c\u4e94\u5e74\u524d\uff0c\u6211\u5949\u5e08\u7236\u4e4b\u547d\uff0c\u53bb\u89c1\u5929\u9f99\u7985\u5e08\uff0c\u7b97\u6765\u4e5f\u662f\u670b\u53cb\uff0c \u6211\u4e0d\u4f24\u4f60\u7684\u6027\u547d\u3002\u4f60\u5feb\u8bf4\u54c8\u5c06\u519b\u5728\u54ea\u4e00\u95ee\u623f\u5b50\uff1f\u201d \u3000\u3000\u5929\u9f99\u7985\u5e08\u662f\u897f\u85cf\u4e00\u4e2a\u5927\u5587\u561b\uff0c\u6b66\u529f\u5353\u7edd\uff0c\u72ec\u521b\u4e00\u767e\u4e8c\u5341\u516d\u5f0f\u5929\u9f99\u638c\u6cd5\uff0c\u5200\u5251\u8def\u6570\uff0c\u5c31\u4ece \u638c\u6cd5\u53d8\u5316\u800c\u6765\uff0c\u522b\u5177\u4e00\u683c\u3002\u5929\u9f99\u7985\u5e08\u5728\u897f\u85cf\u5e7f\u6536\u95e8\u5f92\uff0c\u95fb\u5f97\u6666\u660e\u7985\u5e08\u6b66\u529f\u5251\u6cd5\u5730\u6b65\u6d77\u5185\uff0c\u6d3e \u4eba\u627e\u4ed6\u6bd4\uff0c\u90a3\u65f6\u6768\u4e91\u806a\u6b63\u6295\u5165\u54c8\u8428\u514b\u519b\u4e2d\uff0c\u6709\u4e8b\u8981\u5230\u897f\u85cf\uff0c\u8054\u7edc\u85cf\u6c11\uff0c\u5171\u540c\u6297\u6e05\u3002\u6666\u660e\u63b8\u5e08 \u61d2\u5f97\u4e0b\u5c71\uff0c\u5c31\u53eb\u6768\u4e91\u806a\u987a\u9053\u62dc\u8c12\u3002\u6768\u4e91\u806a\u548c\u5929\u9f99\u7985\u5e08\u8bba\u5251\uff0c\u77e5\u9053\u5929\u9f99\u5251\u6cd5\u867d\u7136\u9887\u6709\u72ec\u5230\u4e4b \u5904\uff0c\u5374\u662f\u7834\u7efd\u9887\u591a\u3002\u4ed6\u5e74\u5c11\u5766\u7387\uff0c\u76f4\u8bf4\u51fa\u6765\uff0c\u5929\u9f99\u7985\u5e08\u602b\u7136\u4e0d\u60a6\u3002\u5f53\u4e0b\u4fbf\u53eb\u5927\u5f1f\u5b50\u548c\u4ed6\u6bd4 \u8bd5\uff0c\u6768\u4e91\u806a\u4e0d\u8fc7\u6570\u62db\uff0c\u5c31\u628a\u4ed6\u7684\u5251\u6cd5\u7834\u53bb\uff0c\u5927\u5f1f\u5b50\u6124\u800c\u6bd4\u638c\uff0c\u53c8\u662f\u4e0d\u8fc7\u6570\u62db\uff0c\u5c31\u7ed9\u6768\u4e91\u806a\u5c01 \u7740\u638c\u529b\uff0c\u53d1\u4e0d\u51fa\u6765\u3002\u5929\u9f99\u7985\u5e08\u867d\u7136\u5984\u81ea\u5c0a\u5927\uff0c\u5374\u662f\u8ba5\u8d27\u7684\u4eba\uff0c\u4e00\u770b\u5c31\u77e5\u9053\u6768\u4e91\u806a\u7684\u529f\u529b\u8fd8\u5728 \u81ea\u5df1\u4e4b\u4e0a\uff0c\u66f4\u4e0d\u8981\u8bf4\u6666\u660e\u7985\u5e08\u4e86\u3002\u5f53\u4e0b\u50b2\u6c14\u5c3d\u6d88\uff0c\u53cd\u800c\u6298\u8282\u8bba\u4ea4\uff0c\u548c\u6768\u4e91\u806a\u7ed3\u4e86\u5fd8\u5e74\u4e4b\u4ea4\u3002 \u8fd9\u4e8b\uff0c\u5929\u9f99\u63b8\u5e08\u95e8\u4e0b\u591a\u6570\u77e5\u9053\u3002\u8fd9\u4e2a\u7ea2\u8863\u756a\u50e7\uff0c\u90a3\u65f6\u4e0d\u5728\u5929\u9f99\u7985\u5e08\u8ddf\u524d\uff0c\u542c\u6768\u4e91\u806a\u8bf4\u8d77\uff0c\u51db \u7136\u4e00\u60ca\uff0c\u5fd9\u95ee\u9053\uff1a\u201c\u4f60\u662f\u6768\u5927\u4fa0\u5417\uff1f\u201d\u6768\u4e91\u806a\u9053\uff0c\u201c\u4e0d\u6562\uff0c\u6211\u6b63\u662f\u6768\u4e91\u806a\u3002\u201d\u628a\u5251\u62ff\u4e86\u4e0b \u6765\uff0c\u89e3\u5f00\u4ed6\u7684\u7a74\u9053\u3002\u756a\u50e7\u9053\uff1a\u201c\u6211\u662f\u54c8\u5c06\u519b\u8bf7\u6765\u505a\u62a4\u9662\u7684\uff0c\u4e0d\u80fd\u5c06\u4ed6\u7684\u4f4f\u5904\u544a\u8bc9\u4f60\u3002\u4f60\u65e2\u662f \u6211\u5e08\u7236\u7684\u670b\u53cb\uff0c\u6211\u4e0d\u53eb\u558a\u662f\u4e86\u3002\u4f60\u82e5\u4e0d\u9ad8\u5174\uff0c\u8981\u6740\u5c3d\u7ba1\u6740\uff01\u201d\u6768\u4e91\u806a\u89c1\u8fd9\u4e2a\u5587\u561b\u5012\u662f\u4e00\u6761\u6c49 \u5b50\uff0c\u5fae\u7b11\u9053\uff1a\u201c\u597d\uff01\u5c31\u662f\u8fd9\u6837\u3002\u201d\u548c\u98de\u7ea2\u5dfe\u4f7f\u4e2a\u201c\u767d\u9e64\u51b2\u5929\u201d\u4e4b\u52bf\uff0c\u98de\u4e0a\u5c4b\u810a\u76f4\u5165\u5185\u9662\u3002 \u3000\u3000\u98de\u7ea2\u5dfe\u89c1\u9662\u843d\u6df1\u6df1\uff0c\u91cd\u95e8\u53e0\u6237\uff0c\u95ee\u9053\uff1a\u201c\u4f3c\u8fd9\u6d0b\uff0c\u5982\u4f55\u53bb\u627e\uff1f\u201d\u6768\u4e91\u806a\u9053\uff1a\u201c\u4f60\u522b\u6025\uff0c \u6211\u6709\u529e\u6cd5\u3002\u201d\u5728\u767e\u5b9d\u56ca\u4e2d\u53d6\u51fa\u786b\u78fa\u5f39\uff0c\u5411\u9a6c\u53a9\u4e00\u4e22\uff0c\u767b\u65f6\u70e7\u5c06\u8d77\u6765\u3002\u7fa4\u9a6c\u72c2\u5636\uff0c\u7834\u53a9\u800c\u51fa\uff0c \u5c06\u519b\u8859\u4e2d\u7684\u536b\u5352\uff0c\u4e5f\u4e0d\u77e5\u6765\u4e86\u591a\u5c11\u654c\u4eba\uff0c\u4e71\u6210\u4e00\u7247\u3002\u6768\u4e91\u806a\u548c\u98de\u7ea2\u5dfe\u4e00\u8eab\u9ed1\u8272\u591c\u884c\u8863\uff0c\u7eb5\u4e0a \u5c4b\u9876\uff0c\u770b\u5f97\u5206\u660e\uff0c\u53ea\u89c1\u4e00\u4e2a\u6ee1\u6d32\u5927\u6c49\uff0c\u7a7f\u7740\u6218\u888d\uff0c\u795e\u6001\u5a01\u4e25\uff0c\u6307\u6325\u536b\u5352\u9547\u4f4f\u4ed6\u4eec\u4e0d\u8bb8\u614c\u4e71\uff0c \u5012\u662f\u4e95\u4e95\u6709\u6761\u3002\u6768\u4e91\u806a\u9053\uff1a\u201c\u95fb\u5f97\u8fd9\u4e2a\u54c8\u5408\u56fe\u4e43\u662f\u591a\u950b\u5e10\u4e0b\u4e00\u5458\u5927\u5c06\uff0c\u6e05\u5ef7\u5728\u65b0\u7586\u7684\u5c06\u9886\uff0c \u9664\u4e86\u7eb3\u5170\u79c0\u5409\uff0c\u5c31\u6570\u5230\u4ed6\u3002\u770b\u6765\u4e5f\u771f\u6709\u70b9\u5c06\u624d\u3002\u201d\u626f\u4e00\u626f\u98de\u7ea2\u5dfe\uff0c\u4e24\u4eba\u4e0d\u7ea6\u800c\u540c\uff0c\u98de\u63a0\u4e0b \u53bb\uff0c\u4eba\u5149\u4e2d\u9752\u5f97\u5206\u660e\u3002\u5e95\u4e0b\u987f\u65f6\u54d7\u53eb\u8d77\u6765\uff0c\u51e0\u540d\u536b\u58eb\uff0c\u5982\u98de\u62a2\u5230\uff0c\u4e3a\u9996\u7684\u624b\u4f7f\u4e00\u5bf9\u516b\u5366\u6df7\u5143 \u724c\uff0c\u624d\u4e00\u7167\u9762\uff0c\u5c31\u7528\u201c\u72ec\u5288\u534e\u5c71\u201d\u62db\u6570\uff0c\u5411\u98de\u7ea2\u5dfe\u5f53\u5934\u5288\u843d\uff0c\u98de\u7ea2\u4e2d\u6b63\u8981\u626c\u97ad\u53cd\u51fb\uff0c\u90a3\u77e5\u6768 \u4e91\u806a\u51fa\u624b\uff0c\u6bd4\u5979\u8fd8\u5feb\uff0c\u5251\u5149\u4e00\u95ea\uff0c\u7531\u659c\u523a\u91cc\u76f4\u94f2\u8fc7\u6765\uff0c\u5bd2\u5149\u7ed5\u5904\uff0c\u628a\u8fd9\u536b\u58eb\u65a9\u4e3a\u4e24\u622a\uff01\u98de\u7ea2 \u5dfe\u626c\u97ad\u6025\u6325\uff0c\u628a\u7b2c\u4e8c\u540d\u536b\u58eb\u6454\u5165\u706b\u5806\u3002\u77ed\u5251\u4fee\u7ffb\uff0c\u5c06\u7b2c\u4e09\u540d\u536b\u58eb\u53c8\u523a\u4e86\u4e00\u4e2a\u900f\u660e\u7a9f\u7abf\u3002\u8fd9\u4e09 \u540d\u536b\u58eb\u4e43\u662f\u5c06\u519b\u8859\u4e2d\u6b66\u529f\u6700\u9ad8\u7684\u4e09\u4eba\uff0c\u4e0d\u8fc7\u4e00\u4e2a\u7167\u9762\uff0c\u5168\u90fd\u4e27\u547d\uff0c\u5176\u4ed6\u7684\u4eba\u53d1\u4e00\u58f0\u558a\uff0c\u56db\u6563 \u5954\u9003\uff0c\u54c8\u5408\u56fe\u9976\u662f\u5982\u4f55\u9547\u5b9a\uff0c\u4e5f\u53d1\u4e86\u614c\u3002\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u6768\u4e91\u806a\u5982\u5de8\u9e1f\u822c\u51cc\u7a7a\u6251\u4e0b\uff0c\u54c8\u5408 \u56fe\u4e00\u62f3\u6253\u51fa\u3002\u987f\u89c9\u5168\u8eab\u8f6f\u762b\uff0c\u9888\u9879\u7ed9\u6768\u4e91\u806a\u5de6\u624b\u5939\u7740\uff0c\u6349\u5c0f\u9e21\u62df\u7684\u63d0\u5c06\u8d77\u6765\uff01\u65ad\u7389\u5251\u51b7\u6c14\u68ee \u68ee\uff0c\u5728\u54c8\u5c06\u519b\u9762\u95e8\u4e00\u6643\uff0c\u559d\u9053\uff1a\u201c\u54c8\u8428\u514b\u914b\u957f\u7684\u513f\u5b50\u5728\u54ea\u91cc\uff0c\u5feb\u653e\u51fa\u6765\uff01\u201d \u3000\u3000\u706b\u5149\u4e2d\u95ea\u51fa\u4e00\u4e2a\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6768\u4e91\u806a\uff0c\u54c8\u8428\u514b\u914b\u957f\u513f\u5b50\u5728\u8fd9\u91cc\uff0c\u4f60\u6709\u672c\u4e8b\u5c31\u6765\u62a2\uff01\u201d \u98de\u7ea2\u5dfe\u9a82\u9053\uff1a\u201c\u695a\u662d\u5357\u4f60\u8fd9\u53db\u8d3c\uff01\u201d\u626c\u97ad\u4e00\u6325\uff0c\u695a\u662d\u5357\u5c06\u54c8\u8428\u514b\u914b\u957f\u7684\u513f\u5b50\u5411\u524d\u4e00\u63a8\uff0c\u7b11 \u9053\uff1a\u201c\u4f60\u72e0\uff0c\u4f60\u6253\u597d\u5566\uff01\u201d\u54c8\u8428\u514b\u914b\u957f\u7684\u513f\u5b50\u53ea\u662f\u4e2a\u5341\u4e09\u56db\u5c81\u7684\u5c11\u5e74\uff0c\u6ee1\u9762\u60ca\u60f6\u4e4b\u8272\uff0c\u98de\u7ea2 \u5dfe\u500f\u5730\u5c06\u97ad\u6536\u56de\u3002\u6768\u4e91\u806a\u559d\u9053\uff0c\u201c\u4f60\u5c06\u4ed6\u653e\u4e86\uff0c\u8981\u4e0d\u7136\u6211\u5c31\u628a\u4f60\u7684\u5c06\u519b\u6740\u6389\uff01\u201d\u695a\u662d\u5357\u563b\u76ae \u7b11\u8138\uff0c\u8bf4\u9053\uff1a\u201c\u5e08\u5144\uff0c\u4f60\u522b\u751f\u6c14\uff0c\u4f60\u5148\u628a\u54c8\u5c06\u519b\u653e\u4e86\uff0c\u6211\u518d\u5c06\u8fd9\u4e2a\u5b69\u5b50\u4ea4\u7ed9\u4f60\u3002\u201d\u6768\u4e91\u806a\u5fc3 \u4e2d\u6124\u6781\uff0c\u5ffd\u7136\u53eb\u4e86\u4e00\u58f0\u201c\u597d\uff0c\u4f60\u63a5\u7740\uff01\u201d\u53cc\u624b\u4e00\u63a8\uff0c\u628a\u54c8\u5408\u56fe\u50cf\u76ae\u7403\u822c\u76f4\u629b\u51fa\u53bb\u3002\u695a\u662d\u5357\u4e0b \u7981\u53cc\u624b\u6765\u63a5\u3002\u6768\u4e91\u806a\u5ffd\u5730\u957f\u5578\u4e00\u58f0\uff0c\u58f0\u5230\u4eba\u5230\uff0c\u4e00\u62db\u201c\u63a8\u7a97\u671b\u6708\u201d\uff0c\u628a\u695a\u662d\u5357\u8feb\u8fc7\u4e00\u8fb9\uff0c\u5de6 \u624b\u5c06\u90a3\u4e2a\u5c11\u5e74\u4e00\u5e26\uff0c\u98de\u7ea2\u5dfe\u4e00\u8dc3\u9762\u524d\uff0c\u8fde\u5fd9\u63a5\u8fc7\u3002\u695a\u662d\u5357\u628a\u54c8\u5408\u56fe\u4e00\u653e\uff0c\u6e38\u9f99\u5251\u94ee\u7136\u51fa\u624b\uff0c \u624b\u8d77\u4e00\u5251\u201c\u91d1\u4ec0\u5f15\u94b1\u201d\uff0c\u5237\u7684\u4e00\u7f15\u9752\u5149\uff0c\u5411\u98de\u7ea2\u5dfe\u80cc\u5fc3\u4fbf\u523a\uff0c\u6768\u4e91\u806a\u559d\u9053\uff1a\u201c\u4f60\u8fd8\u6562\u901e \u51f6\uff1f\u201d\u8eab\u5f62\u970d\u5730\u4e00\u8f6c\uff0c\u5251\u5149\u95ea\u5904\uff0c\u53cd\u5411\u695a\u662d\u5357\u80a9\u80cc\u523a\u53bb\uff0c\u695a\u662d\u5357\u5ffd\u7136\u5927\u53eb\u4e00\u58f0\uff1a\u201c\u5929\u8499\u7985\u5e08 \u5feb\u6765\u52a9\u6211\uff01\u201d\u529b\u6321\u6570\u5251\uff0c\u6768\u4e91\u806a\u53eb\u9053\uff1a\u201c\u98de\u7ea2\u5dfe\uff0c\u4f60\u5148\u8d70\uff0c\u5728\u57ce\u5916\u7b49\u6211\uff0c\u6211\u5c06\u8fd9\u53db\u8d3c\u64d2\u4e86\uff0c \u9a6c\u4e0a\u5c31\u6765\uff01\u201d\u695a\u662d\u5357\u53eb\u4e86\u6570\u58f0\uff0c\u65e0\u4eba\u7b54\u5e94\uff0c\u6768\u4e91\u806a\u4e00\u62db\u5feb\u4f3c\u4e00\u62db\uff0c\u695a\u662d\u5357\u65e0\u6cd5\u62db\u67b6\u3002\u633a\u8eab\u4e00 \u8dc3\uff0c\u8fd8\u672a\u8df3\u51fa\u5708\u5b50\uff0c\u6768\u4e91\u806a\u6b65\u4f3c\u733f\u7334\uff0c\u8eab\u5f62\u4e00\u95ea\uff0c\u5df2\u5230\u695a\u662d\u5357\u80cc\u540e\uff0c\u5de6\u624b\u5f80\u5916\u4e00\u62c2\uff0c\u51fb\u5728\u695a \u662d\u5357\u7684\u201c\u4e09\u91cc\u7a74\u201d\u4e0a\uff0c\u695a\u662d\u5357\u6b63\u5f85\u7f29\u624b\uff0c\u5df1\u6765\u4e0d\u53ca\uff0c\u867d\u6ca1\u6709\u7ed9\u6253\u6b63\u7a74\u9053\u3002\u4e00\u6761\u81c2\u818a\u4e5f\u9ebb\u6728 \u4e86\u3002\u6768\u4e91\u806a\u5939\u624b\u62a2\u8fc7\u4e86\u6e38\u9f99\u5251\uff0c\u53eb\u9053\uff1a\u201c\u8ddf\u6211\u8d70\u201d\uff0c\u4e09\u6307\u4e00\u634f\uff0c\u6263\u7740\u4ed6\u7684\u8109\u95e8\uff0c\u5f84\u81ea\u98de\u8eab\u4e0a \u5c4b\u3002\u536b\u58eb\u706f\u60ca\u9b42\u672a\u5b9a\uff0c\u6ca1\u6709\u4e00\u4e2a\u6562\u8dc3\u4e0a\u53bb\u8ffd\u8d76\uff01 \u3000\u3000\u7247\u523b\u4e4b\u540e\uff0c\u6768\u4e91\u806a\u51fa\u4e86\u57ce\u5821\uff0c\u5ffd\u542c\u5f97\u65f7\u91ce\u4e4b\u5904\uff0c\u6709\u53f1\u54a4\u5636\u6740\u4e4b\u58f0\uff0c\u653e\u773c\u770b\u65f6\uff0c\u53ea\u89c1\u98de\u7ea2 \u4e2d\u53f3\u624b\u62d6\u7740\u54c8\u8428\u514b\u914b\u957f\u7684\u513f\u5b50\uff0c\u53ea\u7528\u5de6\u624b\u957f\u97ad\uff0c\u548c\u4e00\u4e2a\u548c\u5c1a\u6253\u5f97\u5341\u5206\u6fc0\u70c8\u3002\u90a3\u548c\u5c1a\u624b\u4f7f\u4e00\u67c4 \u957f\u5251\uff0c\u6b65\u6309\u516b\u5366\u65b9\u4f4d\u3001\u628a\u98de\u7ea2\u5dfe\u8feb\u5f97\u53ea\u6709\u62db\u67b6\u4e4b\u529f\uff0c\u695a\u662d\u5357\u5931\u58f0\u53eb\u9053\uff1a\u201c\u5929\u8499\u7985\u5e08\uff0c\u6768\u4e91\u806a \u5728\u8fd9\u91cc\uff01\u201d \u3000\u3000\u5929\u8499\u7985\u5e08\u662f\u5929\u9f99\u7985\u5e08\u7684\u5e08\u5f1f\uff0c\u5251\u6cd5\u7cbe\u5999\uff0c\u95fb\u5f97\u6768\u4e91\u806a\u632b\u6298\u5929\u9f99\u4e4b\u4e8b\uff0c\u5fc3\u4e2d\u4e0d\u670d\uff0c\u603b\u60f3\u627e \u6768\u4e91\u806a\u6bd4\u8bd5\uff0c\u56e0\u6b64\u7ed9\u695a\u662d\u5357\u62c9\u6765\uff0c\u54c8\u5408\u56fe\u5f85\u4ed6\u751a\u4e3a\u5c0a\u656c\u3002\u6768\u4e91\u806a\u548c\u98de\u7ea2\u4e2d\u53cc\u53cc\u8dc3\u4e0b\u4e4b\u9645\uff0c\u4ed6 \u672c\u5df2\u5230\u573a\uff0c\u4f46\u4ed6\u4e0d\u8ba4\u8bc6\u6768\u4e91\u806a\uff0c\u89c1\u695a\u662d\u5357\u633a\u5251\u548c\u4e00\u4e2a\u5c11\u5e74\u76f8\u6597\uff0c\u800c\u4e00\u4e2a\u5c11\u5973\u5374\u62d6\u7740\u4eba\u8d28\u5728\u5916 \u98de\u9003\uff1b\u4ed6\u60f3\u695a\u662d\u5357\u6b66\u827a\u9ad8\u5f3a\uff0c\u5bf9\u4ed8\u4e00\u4e2a\u5c11\u5e74\u5fc5\u65e0\u95ee\u9898\uff0c\u52a0\u4e0a\u4eba\u58f0\u5608\u6742\uff0c\u4e5f\u542c\u4e0d\u6e05\u695a\u662d\u5357\u53eb\u4e9b \u4ec0\u4e48\uff0c\u4e0d\u5047\u601d\u7d22\uff0c\u4fbf\u53bb\u8ffd\u98de\u7ea2\u5dfe\u3002\u98de\u7ea2\u5dfe\u7684\u72ec\u95e8\u8f7b\u529f\uff0c\u672c\u5728\u5929\u8499\u4e4b\u4e0a\uff0c\u4f46\u56e0\u4e3a\u591a\u4e86\u4e2a\u7d2f\u8d58\uff0c \u7adf\u7136\u88ab\u4ed6\u8d76\u4e0a\uff0c\u6597\u4e86\u4e00\u767e\u591a\u62db\uff0c\u98de\u7ea2\u5dfe\u53ea\u5f97\u4e00\u53ea\u624b\u4f7f\u7528\uff0c\u7adf\u662f\u582a\u582a\u843d\u8d25\u3002 \u3000\u3000\u5929\u8499\u89c1\u695a\u662d\u5357\u88ab\u6768\u4e91\u806a\u50cf\u7275\u7f8a\u4e00\u6837\u80c1\u7275\u7740\uff0c\u5927\u5403\u4e00\u60ca\uff0c\u653e\u5f00\u98de\u7ea2\u5dfe\uff0c\u63d0\u5251\u8fc7\u6765\u3002\u6768\u4e91\u806a \u7528\u91cd\u624b\u6cd5\u70b9\u4e86\u695a\u662d\u5357\u7684\u6655\u7729\u7a74\uff0c\u7eb5\u4f7f\u4ed6\u80fd\u81ea\u89e3\u7a74\u9053\uff0c\u4e5f\u8981\u8fc7\u516d\u4e2a\u65f6\u8fb0\u3002\u5929\u8499\u8bb6\u9053\uff1a\u201c\u4f60\u4e0d\u662f \u695a\u662d\u5357\u7684\u5e08\u5144\uff1f\u201d\u6768\u4e91\u806a\u9053\uff1a\u201c\u695a\u662d\u5357\u5e2e\u52a9\u6e05\u5ef7\uff0c\u6b3a\u51cc\u65b0\u7586\u8499\u65cf\u7684\u8001\u767e\u59d3\uff0c\u4f60\u4e3a\u4ec0\u4e48\u8981\u52a9\u7ea3 \u4e3a\u8650\uff1f\u201d\u5929\u8499\u9053\uff1a\u201c\u6211\u51fa\u5bb6\u4eba\u4e0d\u7ba1\u4fd7\u5bb6\u4e8b\uff0c\u6211\u95fb\u5929\u9f99\u5e08\u5144\u8bf4\uff0c\u4f60\u5984\u6562\u8bae\u8bba\u6211\u4eec\u7684\u5251\u6cd5\uff0c\u6211\u5012 \u8981\u9886\u6559\u9886\u6559\uff01\u201d\u6768\u4e91\u806a\u9053\uff1a\u201c\u90a3\u65f6\u662f\u6211\u5e74\u5c11\u65e0\u77e5\uff1b\u5176\u5b9e\u5929\u9f99\u7985\u5e08\u7684\u638c\u6cd5\u5251\u6cd5\uff0c\u8fdc\u975e\u6211\u7b49\u540e\u5b66 \u80fd\u7aa5\u5802\u5965\u3002\u201d\u5929\u8499\u51b7\u7b11\u9053\uff1a\u201c\u5c45\u58eb\u4e0d\u80af\u8d50\u6559\uff0c\u90a3\u5c31\u662f\u5927\u770b\u8d2b\u50e7\u4e0d\u8d77\u4e86\uff01\u201d\u98de\u7ea2\u5dfe\u6c14\u8fd9\u548c\u5c1a\u4e0d \u8fc7\uff0c\u4e5f\u54fc\u4e86\u4e00\u58f0\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u8981\u4ed6\u8d50\u6559\uff0c\u90a3\u4e0d\u662f\u81ea\u8ba8\u82e6\u5403\uff01\u201d\u5929\u8499\u6ee1\u9762\u901a\u7ea2\uff0c\u52c3\u7136\u5927\u6012\uff0c \u53eb\u9053\uff1a\u201c\u6768\u82f1\u96c4\u7559\u5fc3\u63a5\u62db\u3002\u201d\u8bdd\u672a\u8bf4\u5b8c\uff0c\u5237\u7684\u4e00\u5251\u5206\u5fc3\u4fbf\u523a\u3002 \u3000\u3000\u6768\u4e91\u806a\u8eab\u968f\u5251\u8f6c\uff0c\u8fde\u95ea\u4e09\u5251\uff0c\u5929\u8499\u559d\u9053\uff1a\u201c\u4f60\u4e3a\u4f55\u4e0d\u62d4\u51fa\u5251\u6765\uff1f\u201d\u6768\u4e91\u806a\u5782\u624b\u8d34\u819d\uff0c\u6717 \u58f0\u8bf4\u9053\uff1a\u201c\u665a\u8f88\u4e0d\u6562\u5728\u524d\u8f88\u9762\u524d\u52a8\u5175\u5203\u3002\u201d\u8868\u9762\u8c26\u865a\uff0c\u5b9e\u662f\u4e0d\u5c51\uff0c\u5929\u8499\u66b4\u8df3\u5982\u96f7\uff0c\u8fde\u73af\u6570 \u5251\uff0c\u8fc5\u75be\u5f02\u5e38\uff0c\u9a82\u9053\uff1a\u201c\u4f60\u6562\u77a7\u6211\u4e0d\u8d77\uff1f\u201d\u6768\u4e91\u806a\u8eab\u968f\u610f\u8f6c\uff0c\u5929\u8499\u7684\u5251\u6cd5\u867d\u7136\u5389\u5bb3\uff0c\u5374\u4f24\u4ed6 \u4e0d\u5f97\u3002\u98de\u7ea2\u5dfe\u9053\uff1a\u201c\u4f60\u548c\u4ed6\u5ba2\u6c14\u4ec0\u4e48\uff1f\u6e05\u5175\u8ffd\u6765\u4e86\uff0c\u5c82\u4e0d\u9ebb\u70e6\u3002\u201d\u6768\u4e91\u806a\u4e8c\u60f3\u4e5f\u662f\u9053\u7406\uff0c\u84e6 \u7136\u95f4\u8eab\u5f62\u9aa4\u957f\uff0c\u4e24\u6307\u4e00\u4f38\uff0c\u7adf\u6307\u5411\u5929\u8499\u53cc\u76ee\uff0c\u5929\u8499\u5927\u5403\u4e00\u60ca\uff0c\u56de\u5251\u6321\u65f6\uff0c\u7ed9\u6768\u4e91\u806a\u5de6\u8098\u4e00 \u649e\uff0c\u957f\u5251\u767b\u65f6\u843d\u5730\u3002\u6768\u4e91\u806a\u9053\u58f0\u201c\u627f\u8ba9\u201d\uff01\u62b1\u8d77\u695a\u662d\u5357\uff0c\u4e0e\u98de\u7ea2\u5dfe\u75be\u9a70\u800c\u53bb\u3002\u5929\u8499\u6000\u6068\u5728 \u5fc3\uff0c\u81ea\u56de\u897f\u85cf\uff0c\u6309\u4e0b\u4e0d\u63d0\u3002 \u3000\u3000\u4e14\u8bf4\u54c8\u8428\u514b\u548c\u5404\u90e8\u843d\u7684\u914b\u957f\u5728\u6768\u4e91\u806a\u4e0e\u98de\u7ea2\u5dfe\u53bb\u540e\uff0c\u70b9\u8d77\u5927\u725b\u6cb9\u70db\uff0c\u56f4\u5750\u5e10\u5e55\u4e4b\u4e2d\u3002\u5927 \u591a\u6570\u7684\u914b\u957f\u5173\u5fc3\u98de\u7ea2\u5dfe\u548c\u6768\u4e91\u806a\uff0c\u4e0d\u80af\u53bb\u7761\uff0c\u53ea\u6709\u5b5f\u7984\uff0c\u8fd8\u7a83\u7a83\u79c1\u8bed\uff0c\u62c5\u5fc3\u6768\u4e91\u806a\u4e00\u53bb\u4e0d\u56de\u3002 \u3000\u3000\u5404\u65cf\u914b\u957f\u5254\u70db\u591c\u8c08\u3002\u4e0d\u89c9\u8fc7\u4e86\u4e00\u4e2a\u66f4\u6b21\uff0c\u582a\u6070\u65cf\u7684\u914b\u957f\u6253\u4e86\u4e00\u4e2a\u5475\u6b20\uff0c\u5854\u5c71\u65cf\u7684\u914b\u957f\u7b11 \u9053\uff1a\u201c\u600e\u4e48\u5982\u6b64\u4e0d\u6d4e\uff0c\u4eca\u591c\u6211\u4eec\u90fd\u4e0d\u6253\u7b97\u7761\u4e86\uff0c\u6700\u5c11\u4e5f\u8981\u7b49\u5230\u5929\u4eae\u3002\u201d\u54c8\u8428\u514b\u65cf\u7684\u914b\u957f\u5fe7\u5f62 \u4e8e\u8272\uff0c\u8bf4\u9053\uff1a\u201c\u53ea\u6015\u5929\u4eae\u4e5f\u4e0d\u80fd\u56de\u6765\u3002\u4e3a\u4e86\u72ac\u5b50\uff0c\u6559\u6768\u5927\u4fa0\u548c\u54c8\u739b\u96c5\u53bb\u5192\u9669\uff0c\u6211\u5b9e\u5728\u8fc7\u610f\u4e0d \u53bb\uff01\u201d\u5b5f\u7984\u51b7\u7b11\u9053\uff1a\u201c\u51e0\u5343\u6e05\u5175\u805a\u5728\u4e00\u4e2a\u5c0f\u57ce\uff0c\u66f4\u52a0\u4e0a\u695a\u662d\u5357\u90a3\u6837\u7684\u5389\u5bb3\u4eba\u7269\uff0c\u4ed6\u4eec\u4e24\u4eba\u8981 \u53bb\u6551\u4eba\u864f\u4eba\uff0c\u95ef\u8fdb\u95ef\u51fa\uff0c\u8981\u60f3\u5f97\u624b\uff0c\u4ef6\u975e\u505a\u68a6\u3002\u53ea\u6015\u6768\u4e91\u806a\u6b64\u65f6\u5df2\u548c\u4ed6\u7684\u5e08\u5f1f\u8054\u6210\u4e00\u6c14\uff0c\u628a \u6211\u4eec\u7684\u76df\u4e3b\u6263\u7559\u8d77\u6765\u4e86\uff01\u201d\u5854\u5c71\u65cf\u7684\u914b\u957f\u6a2a\u4e86\u4ed6\u4e00\u773c\uff0c\u6b63\u60f3\u53d1\u8bdd\u3002\u5ffd\u7136\u5e10\u5e55\u63ed\u5f00\uff0c\u98de\u7ea2\u5dfe\u7b11 \u541f\u541f\u7eb5\u6b65\u5165\u6765\u3002\u5c06\u90a3\u5c11\u5e74\u5411\u54c8\u8428\u514b\u65cf\u914b\u957f\u4e00\u63a8\uff0c\u8bf4\u9053\uff1a\u201c\u4ee4\u90ce\u56de\u6765\u4e86\uff0c\u6beb\u53d1\u65e0\u4f24\u3002\u6211\u4eec\u53ef\u4ee5 \u4ea4\u5dee\u4e86\uff01\u201d\u5b5f\u7984\u6025\u95ee\u9053\uff1a\u201c\u6768\u4e91\u806a\u5462\uff1f\u201d\u5e10\u7bf7\u5916\u6768\u4e91\u806a\u5e94\u58f0\u8d70\u4eba\u3002\u628a\u695a\u662d\u752b\u653e\u5728\u5e10\u5e55\u4e2d\u5fc3\uff0c \u54c8\u54c8\u7b11\u9053\uff0c\u201c\u5e78\u4e0d\u8fb1\u547d\uff01\u8fd9\u4eba\u5c31\u662f\u4f60\u4eec\u6240\u8981\u7684\u695a\u662d\u5357\uff01\u201d \u3000\u3000----------- \u3000\u3000\u98ce\u4e91\u9601 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u585e\u5916\u5947\u4fa0\u4f20\u300b\u2014\u2014\u7b2c\u4e8c\u5341\u4e00\u56de\u3000\u4e0d\u901f\u4e4b\u5ba2 \u6881\u7fbd\u751f\u300a\u585e\u5916\u5947\u4fa0\u4f20\u300b \u7b2c\u4e8c\u5341\u4e00\u56de\u3000\u4e0d\u901f\u4e4b\u5ba2 \u3000\u3000\u54c8\u8428\u514b\u65cf\u7684\u914b\u957f\u8fd9\u4e00\u4e0b\u60ca\u559c\u4ea4\u5e76\uff0c\u6402\u7740\u81ea\u5df1\u7684\u513f\u5b50\uff0c\u6ef4\u4e0b\u6cea\u6765\uff0c\u8fde\u8fde\u5411\u6768\u4e91\u806a\u9053\u8c22\uff0c\u5854 \u5c71\u65cf\u7684\u914b\u957f\u7fd8\u8d77\u5927\u62c7\u6307\uff0c\u5927\u58f0\u9053\u597d\u3002\u5b5f\u7984\u9ed8\u8a00\u65e0\u58f0\uff0c\u98de\u7ea2\u4e2d\u559c\u6c14\u6d0b\u6d0b\u3002 \u3000\u3000\u6768\u4e91\u806a\u5bf9\u54c8\u8428\u514b\u65cf\u7684\u9996\u957f\u9053\uff1a\u201c\u53db\u8d3c\u695a\u662d\u5357\u4ea4\u7ed9\u4f60\u4e86\u3002\u201d\u54c8\u8428\u514b\u65cf\u7684\u914b\u957f\u547d\u4eba\u5c06\u695a\u662d\u5357 \u7528\u94c1\u7d22\u7f1a\u4e2a\u7ed3\u5b9e\uff0c\u4efb\u4ed6\u591a\u597d\u6b66\u529f\u4e5f\u6323\u4e0d\u8131\uff0c\u51c6\u5907\u5728\u7b2c\u4e8c\u665a\u4e0a\uff0c\u518d\u53ec\u96c6\u5404\u65cf\u914b\u957f\u5230\u6765\uff0c\u4e3e\u884c\u590d \u4ec7\u7684\u4eea\u5f0f\uff0c\u5c06\u695a\u662d\u5357\u6d3b\u796d\u6b7b\u96be\u7684\u6218\u58eb\u3002\u6768\u4e91\u806a\u548c\u98de\u7ea2\u5dfe\u7d2f\u4e86\u4e00\u4e2a\u665a\u4e0a\uff0c\u996e\u4e86\u9a6c\u5976\u4e4b\u540e\uff0c\u5404\u81ea \u4f11\u606f\u3002\u5206\u624b\u524d\u98de\u7ea2\u5dfe\u5bf9\u6768\u4e91\u806a\u76c8\u76c8\u4e00\u7b11\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u660e\u513f\u89c1\uff0c\u54b1\u4eec\u518d\u7ec6\u7ec6\u8c08\u3002\u201d\u6768\u4e91\u806a\u9eef \u7136\u70b9\u5934\uff0c\u98de\u7ea2\u5dfe\u53c8\u7b11\u9053\uff0c\u201c\u5e72\u4e48\u4f60\u8fd8\u4e0d\u5f00\u5fc3\uff1f\u4f60\u6709\u4ec0\u4e48\u8bdd\u513f\uff0c\u660e\u5929\u597d\u597d\u7684\u8bf4\uff0c\u4f60\u6709\u4ec0\u4e48\u8981 \u6c42\uff0c\u6211\u90fd\u53ef\u4ee5\u7b54\u5e94\u4f60\u7684\u3002\u201d\u8bf4\u7f62\uff0c\u53c8\u56de\u7738\u4e00\u7b11\u3002\u98de\u7ea2\u5dfe\u6ee1\u5fc3\u4ee5\u4e3a\u660e\u5929\u6768\u4e91\u806a\u5c31\u4f1a\u5bf9\u81ea\u5df1\u8868\u767d \u76f8\u604b\u4e4b\u60c5\u3002\u8fd9\u4e00\u665a\u505a\u4e86\u597d\u51e0\u4e2a\u7f8e\u68a6\u3002 \u3000\u3000\u7b2c\u4e8c\u5929\u4e00\u65e9\uff0c\u6768\u4e91\u806a\u5728\u5e10\u7bf7\u91cc\u7ed9\u4eba\u5524\u9192\uff0c\u62a5\u8bf4\u5916\u9762\u4eba\u6709\u627e\u4ed6\uff1b\u6768\u4e91\u806a\u62ab\u8863\u8d77\u89c6\uff0c\u54c8\u8428\u514b \u65cf\u7684\u914b\u957f\u5e26\u4e86\u4e00\u4e2a\u4e2d\u5e74\u6c49\u5b50\u8fdb\u6765\uff1b\u6768\u4e91\u806a\u53eb\u9053\uff1a\u201c\u554a\uff0c\u8f9b\u9f99\u5b50\uff0c\u539f\u6765\u662f\u4f60\uff0c\u4f60\u600e\u4e48\u4e5f\u627e\u5230\u8fd9 \u91cc\u6765\u4e86\uff1f\u201d \u3000\u3000\u8f9b\u9f99\u5b50\u662f\u5353\u4e00\u822a\u5230\u65b0\u7586\u4e4b\u540e\u3001\u6240\u6536\u7684\u5f1f\u5b50\uff0c\u4ed6\u672c\u662f\u54c8\u8428\u514b\u65cf\u4e00\u4e2a\u7267\u6c11\u7684\u513f\u5b50\uff0c\u6295\u5e08\u4e4b \u540e\uff0c\u8654\u5fc3\u5411\u5b66\uff0c\u4e0d\u7406\u5916\u4e8b\uff1b\u5bf9\u672c\u95e8\u62f3\u5251\u5df2\u5f97\u771f\u4f20\uff0c\u5728\u5929\u5c71\u4e4b\u65f6\uff0c\u548c\u6768\u4e91\u806a\u695a\u662d\u5357\u90fd\u65f6\u76f8\u5728 \u8fd8\uff0c\u53ea\u662f\u4ed6\u813e\u6c14\u602a\u50fb\uff0c\u548c\u6768\u4e91\u806a\u5012\u5e76\u4e0d\u600e\u6837\u6295\u5408\uff0c\u53cd\u800c\u548c\u695a\u662d\u5357\u5f88\u8c08\u5f97\u6765\uff0c\u4e09\u4eba\u65f6\u65f6\u8bae\u8bba\u6b66 \u529f\uff0c\u90fd\u4ee5\u5144\u5f1f\u76f8\u79f0\u3002\u8f9b\u9f99\u5b50\u548c\u54c8\u8428\u514b\u65cf\u7684\u914b\u957f\uff0c\u672c\u6765\u76f8\u8bc6\uff0c\u54c8\u8428\u514b\u65cf\u7684\u914b\u957f\u4e5f\u5f88\u9ad8\u5174\uff0c\u81ea\u5df1 \u7684\u65cf\u4eba\u4e2d\uff0c\u6709\u8fd9\u6837\u4e00\u4e2a\u6b66\u5f53\u6d3e\u540d\u5251\u5ba2\u7684\u95e8\u5f92\u3002 \u3000\u3000\u8f9b\u9f99\u5b50\u89c1\u4e86\u6768\u4e91\u806a\uff0c\u7ffb\u7740\u602a\u773c\u95ee\u9053\uff1a\u201c\u6211\u7684\u5e08\u7236\u5462\uff1f\u4f60\u53ef\u77e5\u9053\u4ed6\u7684\u53bb\u5904\uff1f\u201d\u6768\u4e91\u806a\u7b11 \u9053\uff1a\u201c\u600e\u4e48\u6211\u8fd9\u51e0\u5929\u8001\u662f\u7ed9\u4eba\u67e5\u95ee\uff0c\u767d\u53d1\u9b54\u5973\u5411\u6211\u8981\u4f60\u7684\u5e08\u7236\uff0c\u73b0\u5728\u4f60\u53c8\u6765\u95ee\u6211\u4e86\u3002\u201d\u8f9b\u9f99 \u5b50\u9053\uff1a\u201c\u6211\u5c31\u662f\u78b0\u89c1\u767d\u53d1\u9b54\u5973\u8fd9\u8001\u5996\u602a\uff0c\u624d\u6765\u95ee\u4f60\u7684\u3002\u6211\u5411\u767d\u53d1\u9b54\u5973\u95ee\u5e08\u7236\u7684\u4e0b\u843d\uff0c\u5979\u628a\u6211 \u8e22\u4e86\u4e00\u4e2a\u7b4b\u6597\uff0c\u8fde\u8fde\u51b7\u7b11\u9053\uff1a\u2018\u4f60\u53bb\u95ee\u6666\u660e\u7985\u5e08\u7684\u5f1f\u5b50\u6768\u4e91\u806a\u53bb\u3002\u6211\u624d\u61d2\u5f97\u7ba1\u4f60\u7684\u5e08\u7236 \u5462\uff01\u2019\u54fc\uff0c\u5979\u4e0d\u7ba1\uff0c\u5979\u628a\u6211\u7684\u5e08\u7236\u8feb\u5f97\u5728\u5929\u5c71\u7acb\u4e0d\u4f4f\u8db3\u3002\u5982\u679c\u5979\u628a\u6211\u7684\u5e08\u7236\u5bb3\u4e86\uff0c\u6211\u867d\u7136\u672c \u9886\u4e0d\u6d4e\uff0c\u82e6\u7ec3\u51e0\u5341\u5e74\uff0c\u4e5f\u8981\u627e\u5979\u62a5\u4ec7\u3002\u201d\u6768\u4e91\u806a\u7b11\u9053\uff1a\u201c\u767d\u53d1\u9b54\u5973\u7edd\u4e0d\u4f1a\u4f24\u5bb3\u4f60\u7684\u5e08\u7236\u7684\uff0c \u4f60\u653e\u5fc3\u597d\u4e86\u3002\u4f60\u7684\u5e08\u7236\uff0c\u6211\u89c1\u662f\u89c1\u7740\u4e86\uff0c\u53ef\u662f\u4e00\u70b9\u4e5f\u4e0b\u77e5\u9053\u4ed6\u7684\u4e0b\u843d\u3002\u201d\u6768\u4e91\u806a\u628a\u5f53\u65e5\u7684\u60c5 \u5f62\u7ec6\u7ec6\u8bf4\u4e86\u3002\u8f9b\u9f99\u5b50\u7ea2\u7740\u773c\u775b\u9053\uff1a\u201c\u8d70\u904d\u8349\u539f\uff0c\u6211\u4e5f\u8981\u628a\u5e08\u7236\u627e\u5230\uff0c\u6211\u8fd8\u6709\u4e00\u4e24\u5957\u5251\u6cd5\u672a\u5b66 \u54e9\uff0c\u5c31\u53ef\u60dc\u6ca1\u6709\u4e00\u628a\u597d\u5251\u3002\u201d\u8bf4\u7f62\uff0c\u76ef\u7740\u6768\u4e91\u806a\u8170\u95f4\u7684\u4e24\u628a\u4f69\u5251\uff0c\u6768\u4e91\u806a\u7b11\u9053\uff1a\u201c\u53ef\u60dc\u6211\u8fd9 \u4e24\u628a\u4f69\u5251\u90fd\u662f\u5e08\u7236\u7684\u5b9d\u7269\uff0c\u8981\u4e0d\u7136\u9001\u4e00\u628a\u7ed9\u4f60\u4e5f\u6ca1\u6709\u95ee\u9898\u3002\u201d\u8f9b\u9f99\u5b50\u9053\u3002\u201c\u6211\u5c31\u662f\u89c9\u5f97\u5947 \u602a\uff0c\u600e\u4e48\u4f60\u4f69\u7740\u4e24\u628a\u5b9d\u5251\uff0c\u6211\u53ef\u6ca1\u6709\u60f3\u5230\u8981\u4f60\u7684\u4e1c\u897f\u3002\u201d\u6768\u4e91\u806a\u9053\uff1a\u201c\u8fd9\u4e24\u628a\u5251\u4f60\u8fd8\u4e0d\u8ba4\u8bc6 \u5417\uff1f\u4e00\u628a\u662f\u6211\u7684\u65ad\u7389\u5251\uff0c\u4e00\u628a\u662f\u695a\u662d\u5357\u7684\u6e38\u9f99\u5251\uff0c\u5728\u5929\u5c71\u4e4b\u65f6\uff0c\u4f60\u662f\u89c1\u8fc7\u4e86\u7684\u3002\u201d\u8f9b\u9f99\u5b50\u53c8 \u7ffb\u7740\u602a\u773c\u9053\uff1a\u201c\u600e\u4e48\u4ed6\u7684\u5b9d\u5251\u4f1a\u5230\u4f60\u7684\u624b\u4e2d\uff1f\u201d\u6768\u4e91\u806a\u9eef\u7136\u8bf4\u9053\uff1a\u201c\u6211\u8fd9\u4e0d\u6210\u6750\u7684\u5e08\u5f1f\uff0c\u4ed6 \u6295\u964d\u4e86\u6e05\u519b\uff0c\u7518\u5fc3\u4e3a\u864e\u4f5c\u6005\uff0c\u662f\u6211\u628a\u4ed6\u62ff\u4e0b\u6765\u4e86\u3002\u201d\u54c8\u8428\u514b\u65cf\u7684\u9996\u957f\u63d2\u53e3\u9053\uff1a\u201c\u662f\u5440\uff01\u4eca\u665a \u6211\u4eec\u8fd8\u8981\u4e3e\u884c\u590d\u4ec7\u4eea\u5f0f\u5462\uff01\u4f60\u4e5f\u7559\u5728\u6211\u91cc\u77a7\u77a7\u70ed\u95f9\u5427\u3002\u201d\u8f9b\u9f99\u5b50\u201c\u554a\u201d\u4e86\u4e00\u58f0\u8bf4\u9053\uff1a\u201c\u5e08\u5144 \u6d3b\u6349\u5e08\u5f1f\uff0c\u8fd9\u4e5f\u771f\u662f\u6b66\u6797\u4e2d\u7684\u5947\u4e8b\uff01\u201d\u6768\u4e91\u806a\u5ffd\u7136\u60f3\u8d77\u4e00\u4e8b\u3002\u95ee\u8f9b\u9f99\u5b50\u9053\uff1a\u201c\u4f60\u8fd8\u8981\u56de\u5929\u5c71 \u53bb\u7684\uff1f\u662f\u4e0d\u662f\uff1f\u201d\u8f9b\u9f99\u5b50\u70b9\u70b9\u5934\u9053\uff1a\u201c\u5f53\u7136\u56de\u53bb\uff0c\u6211\u53bb\u627e\u5e08\u7236\uff0c\u627e\u5230\u4e86\u5c31\u548c\u4ed6\u4e00\u9053\u56de\u5c71\uff0c\u82e5 \u627e\u4e0d\u7740\uff0c\u6211\u4e5f\u8981\u56de\u53bb\u4e00\u8f6c\uff0c\u62dc\u522b\u6666\u660e\u5e08\u4f2f\u518d\u53bb\u627e\u4ed6\u3002\u201d\u6768\u4e91\u806a\u89e3\u4e0b\u695a\u662d\u5357\u7684\u6e38\u9f99\u5251\uff0c\u9012\u7ed9\u8f9b \u9f99\u5b50\u9053\uff1a\u201c\u8fd9\u662f\u6211\u4eec\u9547\u5c71\u7684\u4e24\u5251\u4e4b\u4e00\uff0c\u4e0d\u80fd\u843d\u5728\u5916\u4eba\u624b\u4e2d\u3002\u6211\u4e1c\u98d8\u897f\u8361\uff0c\u51fa\u751f\u5165\u6b7b\uff0c\u4e0d\u77e5\u4ec0 \u4e48\u65f6\u5019\u80fd\u56de\u5929\u5c71\uff0c\u66f4\u4e0d\u77e5\u4ec0\u4e48\u65f6\u5019\u906d\u9047\u4e0d\u5e78\uff0c\u6211\u62dc\u6258\u4f60\u628a\u8fd9\u5251\u7f34\u56de\u7ed9\u6211\u7684\u5e08\u7236\uff0c\u540c\u65f6\u8bf7\u4e3a\u6211 \u5411\u4ed6\u544a\u7f6a\uff0c\u56e0\u4e3a\u695a\u662d\u5357\u72af\u4e86\u5e08\u95e8\u5927\u6212\uff0c\u6211\u6765\u4e0d\u53ca\u7980\u544a\u4ed6\u8001\u4eba\u5bb6\uff0c\u5df2\u5148\u81ea\u628a\u4ed6\u5904\u7f6e\u4e86\u3002\u201d\u8f9b\u9f99 \u5b50\u63a5\u8fc7\u5ba4\u5251\uff0c\u624b\u6307\u5fae\u5fae\u98a4\u6296\u3002 \u3000\u3000\u5e10\u5e55\u5916\u53c8\u6709\u4eba\u58f0\u7980\u544a\uff0c\u8fd9\u56de\u6765\u7684\u662f\u98de\u7ea2\u5dfe\u7684\u4f8d\u5973\uff0c\u5bf9\u6768\u4e91\u806a\u9053\uff1a\u201c\u54c8\u739b\u96c5\u5c0f\u59d0\u8bf7\u6768\u5927\u4fa0 \u8fc7\u53bb\u3002\u201d\u8f9b\u9f99\u5b50\u4e5f\u60f3\u544a\u8f9e\u4e86\uff0c\u54c8\u8428\u514b\u65cf\u7684\u914b\u957f\u82e6\u82e6\u628a\u4ed6\u7559\u7740\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u79bb\u5f00\u90e8\u843d\u5df2\u8bb8\u591a\u5e74 \u4e86\uff0c\u597d\u4e9b\u4e8b\u60c5\uff0c\u4f60\u90fd\u4e0d\u6e05\u695a\u3002\u6211\u4eec\u7684\u65cf\u4eba\u6b63\u7ed9\u4eba\u6b3a\u8d1f\u5462\u3002\u4f60\u5c31\u591a\u7559\u4e00\u4e24\u5929\uff0c\u548c\u65cf\u4eba\u53d9\u4e00\u53d9 \u5427\u3002\u201d\u8f9b\u9f99\u5b50\u70b9\u5934\u7b54\u5e94\uff0c\u6768\u4e91\u806a\u72ec\u81ea\u8d70\u8fc7\u98de\u7ea2\u4e2d\u7684\u5e10\u5e55\u3002\u8f9b\u9f99\u5b50\u597d\u5947\u95ee\u9053\uff0c\u201c\u600e\u4e48\u6768\u4e91\u806a\u548c \u4e00\u4e2a\u4ec0\u4e48\u5c0f\u59d0\u5f88\u6709\u4ea4\u60c5\u5417\uff1f\u201d\u54c8\u8428\u514b\u65cf\u7684\u914b\u957f\u7b11\u9053\uff1a\u201c\u8fd9\u4f4d\u54c8\u8682\u96c5\u5c0f\u59d0\u5c31\u662f\u5357\u7586\u9f0e\u9f0e\u5927\u540d\u7684 \u98de\u7ea2\u5dfe\u5973\u82f1\u96c4\u5440\uff1a\u4ed6\u4eec\u771f\u662f\u5929\u9020\u5730\u8bbe\u7684\u4e00\u65f6\u3002\u600e\u4e48\uff0c\u9f99\u5b50\uff1f\u4f60\u4e0d\u77e5\u9053\u98de\u7ea2\u5dfe\u7684\u5927\u540d\u5417\uff1f\u201d\u8f9b \u9f99\u5b50\u6447\u6447\u5934\u9053\uff1a\u201c\u6211\u5341\u4e8c\u3001\u4e09\u5c81\u4e0a\u5c71\uff0c\u4f4f\u5728\u5929\u5c71\u4e0a\u4e8c\u5341\u5e74\u4e86\uff0c\u600e\u4f1a\u77e5\u9053\u4f60\u4eec\u8349\u539f\u4e0a\u51fa\u4e86\u4e2a\u5973 \u82f1\u96c4\uff1f\u201d\u54c8\u8428\u514b\u65cf\u7684\u914b\u957f\u9053\uff1a\u201c\u542c\u8bf4\u4ed6\u5c31\u662f\u767d\u53d1\u9b54\u5973\u7684\u5f92\u5f1f\u5440\uff01\u201d\u8f9b\u9f99\u5b50\u6068\u6068\u5730\u9053\uff1a\u201c\u81ea\u53d1 \u9b54\u5973\u6b3a\u8d1f\u6211\u7684\u5e08\u7236\uff0c\u53ef\u662f\u5979\u4ece\u6765\u672a\u5e26\u8fc7\u5f92\u5f1f\u6765\uff0c\u6211\u600e\u4f1a\u77e5\u9053\u4ec0\u4e48\u98de\u7ea2\u5dfe\u98de\u767d\u5dfe\uff01\u54fc\uff0c\u767d\u53d1\u9b54 \u5973\u7684\u5f92\u5f1f\uff0c\u60f3\u6765\u4e5f\u4e0d\u4f1a\u662f\u4ec0\u4e48\u597d\u4eba\u3002\u201d\u54c8\u8428\u514b\u65cf\u7684\u914b\u957f\u76b1\u7740\u7709\u5934\u9053\uff1a\u201c\u4f60\u5168\u5fc3\u5b66\u827a\uff0c\u90a3\u662f\u975e \u5e38\u4e4b\u597d\uff0c\u53ef\u662f\u5bf9\u5916\u9762\u4e8b\u60c5\uff0c\u4e00\u70b9\u4e0d\u95fb\u4e0d\u95ee\uff0c\u90a3\u662f\u4f1a\u5403\u4e8f\u7684\u5440\u3002\u662f\u975e\u4e0d\u5206\uff0c\u9ed1\u767d\u4e0d\u660e\uff0c\u5f53\u5fc3\u4f1a \u4e0a\u5f53\u54e9\u3002\u98de\u7ea2\u5dfe\u662f\u5357\u7586\u5404\u65cf\u7684\u76df\u4e3b\uff0c\u5979\u6253\u4ed7\u6253\u5f97\u975e\u5e38\u4e4b\u597d\u3002\u4eba\u4eba\u90fd\u79f0\u8d5e\u5979\uff0c\u600e\u4e48\u4f1a\u4e0d\u662f\u597d \u4eba\uff01\u201d\u8f9b\u9f99\u5b50\u7ed9\u4ed6\u6559\u8bad\u4e00\u987f\uff0c\u5f88\u4e0d\u9ad8\u5174\uff0c\u4f46\u788d\u4e8e\u4ed6\u662f\u8001\u65cf\u957f\uff0c\u672a\u4fbf\u53d1\u4f5c\u3002\u6070\u597d\uff0c\u6709\u4eba\u6765\u8bf7\u65cf \u957f\uff0c\u54c8\u8428\u514b\u65cf\u7684\u9996\u957f\u9053\uff1a\u201c\u8fd9\u4e24\u5929\u4e8b\u60c5\u975e\u5e38\u4e4b\u5fd9\uff0c\u53cd\u6b63\u4f60\u662f\u6211\u4eec\u81ea\u5df1\u4eba\uff0c\u4f60\u5230\u5404\u5904\u53bb\u8d70\u4e00\u8d70 \u770b\u4e00\u770b\uff0c\u548c\u65cf\u4e2d\u7684\u5144\u5f1f\u59d0\u59b9\u4eec\u53d9\u4e00\u53d9\u5427\uff0c\u6211\u4e0d\u966a\u4f60\u4e86\u3002\u201d \u3000\u3000\u518d\u8bf4\u6768\u4e91\u806a\u8d70\u5230\u98de\u7ea2\u5dfe\u7684\u5e10\u5e55\uff0c\u98de\u7ea2\u4e2d\u8bf7\u4ed6\u5403\u4e86\u65e9\u9910\uff0c\u62c9\u4ed6\u5230\u8349\u539f\u6563\u6b65\u3002\u8349\u539f\u7684\u6e05\u6668\uff0c \u671d\u9633\u666e\u7167\uff0c\u7eff\u8349\u51dd\u73e0\uff0c\u5c31\u5b9b\u5982\u4e00\u4e2a\u521a\u521a\u68b3\u6d17\u8fc7\u7684\u5c11\u5973\uff0c\u5c55\u5f00\u5979\u7684\u7b11\u8138\uff0c\u7f8e\u4e3d\u6781\u4e86\uff0c\u5a07\u8273\u6781 \u4e86\u3002\u98de\u7ea2\u5dfe\u559c\u4e0a\u7709\u68a2\uff0c\u508d\u7740\u6768\u4e91\u806a\u4f4e\u58f0\u5531\u6b4c\uff0c\u6768\u4e91\u806a\u5fc3\u4e2d\u7684\u601d\u60f3\u5982\u6d6a\u6f6e\u51b2\u51fb\u3002\u90a3\u91cc\u542c\u5f97\u8fdb \u53bb\uff1f\u98de\u7ea2\u5dfe\u5531\u5b8c\u4e86\u51e0\u652f\u8349\u539f\u5c0f\u8c03\uff0c\u89c1\u6768\u4e91\u806a\u82e5\u6709\u6240\u601d\uff0c\u62c9\u7740\u4ed6\u7684\u624b\u9053\uff1a\u201c\u4e91\u806a\uff0c\u6709\u4ec0\u4e48\u8bdd\u4f60 \u8bf4\u5440\uff0c\u6211\u4eec\u76f8\u5904\u7684\u65e5\u5b50\u5f88\u77ed\uff0c\u4f46\u5374\u76f8\u5904\u5f97\u5f88\u597d\uff0c\u4f60\u8bf4\u662f\u5417\uff1b\u4f60\u6628\u665a\u8bf4\u628a\u6211\u5f53\u6210\u59b9\u59b9\uff0c\u90a3\u4e48\u54e5 \u54e5\u7684\u5fc3\u4e8b\uff0c\u59b9\u59b9\u5e94\u8be5\u77e5\u9053\u5440\uff0c\u4e91\u806a\uff0c\u4f60\u4e0d\u77e5\u9053\u3002\u5728\u90a3\u6b21\u8349\u539f\u6df7\u6218\uff0c\u5931\u6563\u4e86\u4f60\u4e4b\u540e\uff0c\u6211\u662f\u591a\u4e48 \u60e6\u8bb0\u7740\u4f60\uff01\u201d\u6768\u4e91\u806a\u54ac\u7740\u7259\u6839\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u54c8\u739b\u96c5\uff0c\u4f60\u662f\u6211\u7684\u597d\u59b9\u5b50\uff0c\u6211\u4e00\u751f\u90fd\u628a\u4f60\u5f53\u6210 \u597d\u59b9\u5b50\u3002\u201d\u98de\u7ea2\u5dfe\u76c8\u76c8\u7b11\u9053\uff1a\u201c\u9664\u4e86\u662f\u597d\u59b9\u5b50\u4e4b\u5916\uff0c\u5c31\u4e0d\u662f\u5176\u4ed6\u7684\u4e86\u5417\uff1f\u201d\u6768\u4e91\u806a\u70b9\u70b9\u5934 \u9053\uff1a\u201c\u662f\u7684\uff0c\u53ea\u662f\u5144\u59b9\u3002\u201d\u98de\u7ea2\u5dfe\u89c1\u4ed6\u975e\u5e38\u5e84\u91cd\uff0c\u9762\u4e0a\u6d41\u9732\u7740\u4e00\u79cd\u75db\u82e6\u7684\u5947\u602a\u7684\u8868\u60c5\uff0c\u84e6\u7136 \u5403\u4e86\u4e00\u60ca\uff0c\u8df3\u8d77\u6765\u9053\uff1a\u201c\u4e91\u806a\uff0c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u662f\u4e0d\u662f\u4f60\u53e6\u5916\u6709\u4e86\u4eba\u4e86\uff1f\u201d\u6768\u4e91\u806a\u70b9\u70b9\u5934\u9053\uff1a \u201c\u662f\u7684\uff01\u5728\u4f60\u4e4b\u524d\uff0c\u6211\u78b0\u7740\u4e00\u4f4d\u5c0f\u59d0\uff0c\u5979\u5c31\u662f\u2026\u2026\u201d\u98de\u7ea2\u5dfe\u98a4\u58f0\u63d2\u95ee\uff1a\u201c\u5979\u5c31\u662f\u7eb3\u5170\u79c0\u5409\u7684 \u5973\u513f\u5417\uff1f\u201d\u6768\u4e91\u806a\u53c8\u54ac\u7740\u7259\u6839\u7b54\u9053\uff1a\u201c\u662f\u7684\uff01\u201d\u98de\u7ea2\u5dfe\u7684\u9762\u4e0a\u7a81\u7136\u4e86\u53d8\u989c\u8272\uff0c\u6709\u5982\u660e\u6717\u7684\u5929 \u7a7a\uff0c\u906e\u4e0a\u4e4c\u4e91\u3002\u5979\u4e0d\u53d1\u8bdd\u3002\u5979\u5fcd\u7740\u773c\u6cea\uff0c\u575a\u5f3a\u7684\u6027\u683c\u4e0e\u521d\u604b\u5c11\u5973\u67d4\u8f6f\u7684\u5fc3\u51b2\u7a81\u8d77\u6765\uff0c\u8fd9\u970e\u90a3 \u95f4\uff0c\u4ed6\u5b8c\u5168\u6df7\u4e71\u4e86\uff0c\u5979\u4ece\u6765\u6ca1\u6709\u8bd5\u8fc7\u8fd9\u6837\u7684\u6fc0\u52a8\uff0c\u6700\u51f6\u731b\u7684\u654c\u4eba\u4e5f\u4e0d\u4f1a\u50cf\u6768\u4e91\u806a\u90a3\u6837\u4ee4\u5979\u9707 \u64bc\uff0c\u5352\u4e4b\uff0c\u5979\u5916\u8868\u7684\u575a\u5f3a\u7ed9\u5185\u5fc3\u7684\u75db\u82e6\u5f81\u670d\u4e86\uff0c\u5979\u63a9\u7740\u9762\u9053\uff1a\u201c\u54c8\uff0c\u5b5f\u7984\u4ed6\u4eec\u8bf4\u7684\u8bdd\u7adf\u662f\u771f \u7684\uff0c\u4f60\u771f\u7684\u7231\u4e0a\u654c\u4eba\u7684\u5973\u513f\u4e86\uff01\u201d\u6768\u4e91\u806a\u70b9\u70b9\u5934\u9053\uff1a\u201c\u662f\u771f\u7684\uff0c\u5979\u5c06\u662f\u6211\u4eca\u751f\u7684\u59bb\u5b50\uff01\u201d\u98de \u7ea2\u5dfe\u84e6\u7136\u53eb\u9053\uff1a\u201c\u6768\u4e91\u806a\uff0c\u4f60\u505a\u9519\u4e86\uff01\u201d\u6768\u4e91\u806a\u5168\u8eab\u98a4\u6296\uff0c\u5ffd\u7136\u7eb3\u5170\u660e\u6167\u7684\u5f71\u5b50\u6cdb\u4e0a\u5fc3\u5934\uff0c \u662f\u90a3\u6837\u6e29\u67d4\uff0c\u90a3\u4e48\u7aef\u6dd1\uff0c\u90a3\u6837\u7684\u4ee4\u4eba\u7231\u601c\uff0c\u7eb3\u5170\u660e\u6167\u50cf\u8349\u539f\u4e0a\u7684\u5c0f\u8349\uff0c\u9700\u8981\u4ed6\u7684\u4fdd\u62a4\u3002\u4ed6\u6297 \u58f0\u8fa9\u9053\uff1a\u201c\u98de\u7ea2\u5dfe\uff0c\u5979\u662f\u4e00\u4e2a\u597d\u4eba\uff0c\u6211\u60f3\u5979\u5c06\u6765\u4f1a\u53eb\u4f60\u505a\u59d0\u59d0\u7684\u3002\u4f60\u4e5f\u613f\u610f\u628a\u5979\u5f53\u6210\u59b9\u59b9 \u5417\uff1f\u201d\u98de\u7ea2\u5dfe\u84e6\u7136\u5411\u56de\u5934\u8def\u75be\u8dd1\uff0c\u5979\u7684\u773c\u6cea\u5df2\u7ecf\u6ef4\u51fa\u6765\u4e86\uff0c\u5979\u4e0d\u613f\u8ba9\u6768\u4e91\u806a\u770b\u5230\u5979\u7684\u773c\u6cea\u3001 \u770b\u5230\u5979\u611f\u60c5\u4e0a\u7684\u5f31\u70b9\uff0c\u867d\u7136\u6768\u4e91\u806a\u662f\u5979\u6700\u4eb2\u7231\u7684\u4eba\u3002 \u3000\u3000\u98de\u7ea2\u5dfe\u8fd9\u4e00\u7a81\u5982\u5176\u6765\u7684\u52a8\u4f5c\uff0c\u4ee4\u6768\u4e91\u806a\u624b\u8db3\u65e0\u63aa\uff0c\u62c9\u5979\u4e0d\u597d\uff0c\u4e0d\u62c9\u5979\u53c8\u4e0d\u597d\uff0c\u4ed6\u5b9a\u4e86\u5b9a \u795e\uff0c\u62d4\u8db3\u8ffd\u8d76\u53eb\u9053\uff1a\u201c\u98de\u7ea2\u5dfe\uff0c\u6211\u7684\u597d\u59b9\u5b50\uff0c\u8bf7\u7b49\u4e00\u7b49\uff0c\u7b49\u4e00\u7b49\u5440\uff01\u201d\u98de\u7ea2\u5dfe\u6d41\u7740\u6cea\u98de\u8dd1\uff0c \u6768\u4e91\u806a\u7684\u5fc3\u5b8c\u5168\u4e71\u4e86\uff0c\u5076\u7136\u5730\u8ddf\u7740\u5979\u8dd1\uff0c\u5ffd\u7136\u8fce\u9762\u51b2\u51fa\u51e0\u9a91\u5feb\u9a6c\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u6768\u5927\u4fa0\uff0c\u98de \u7ea2\u5dfe\uff0c\u4f60\u4eec\u77e5\u9053\u4e86\u5417\uff1f\u4e0d\u7528\u8d76\u56de\u6765\u4e86\uff0c\u5411\u897f\u5357\u8ffd\uff0c\u8d76\u5feb\u6362\u9a6c\u5427\uff01\u8ffd\u5440\uff01\u8ffd\u5440\uff01\u695a\u662d\u5357\u548c\u8f9b\u9f99 \u5b50\u9003\u8dd1\u4e86\uff01\u201d \u3000\u3000----------- \u3000\u3000\u98ce\u4e91\u9601 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u585e\u5916\u5947\u4fa0\u4f20\u300b\u2014\u2014\u7b2c\u4e8c\u5341\u4e8c\u56de\u3000\u8d1f\u6c14\u51fa\u5954 \u6881\u7fbd\u751f\u300a\u585e\u5916\u5947\u4fa0\u4f20\u300b \u7b2c\u4e8c\u5341\u4e8c\u56de\u3000\u8d1f\u6c14\u51fa\u5954 \u3000\u3000\u6768\u4e91\u806a\u4e00\u542c\uff0c\u5927\u5403\u4e00\u60ca\uff0c\u4ece\u60c5\u611f\u7684\u7eb7\u6270\u4e2d\u9661\u7136\u9192\u6765\uff0c\u63a5\u4e86\u4e00\u9a91\u9a6c\uff0c\u731b\u7684\u4e00\u97ad\uff0c\u5982\u98de\u8ffd \u53bb\uff0c\u5728\u9a6c\u80cc\u4e0a\u5e76\u9ad8\u58f0\u53eb\u9053\uff0c\u201c\u54c8\u739b\u96c5\uff0c\u52a9\u6211\u4e00\u81c2\u4e4b\u529b\uff0c\u5feb\u8ffd\uff0c\u5feb\u8ffd\uff0c\u628a\u90a3\u53db\u8d3c\u64d2\u56de\uff01\u201d\u98de\u7ea2 \u5dfe\u95f7\u58f0\u4e0d\u54cd\uff0c\u4e5f\u63a5\u4e86\u4e00\u9a91\u9a6c\uff0c\u8ddf\u7740\u8ffd\u53bb\u3002 \u3000\u3000\u8349\u539f\u4e0a\u56db\u9a91\u9a6c\u98ce\u9a70\u7535\u63a3\uff0c\u970e\u90a3\u95f4\u628a\u5176\u4ed6\u7684\u4eba\u629b\u5728\u80cc\u540e\uff0c\u6768\u4e91\u806a\u548c\u98de\u7ea2\u4e2d\u5e76\u9a91\u98ce\u9a70\uff0c\u53ef\u662f \u98de\u7ea2\u5dfe\u8fde\u770b\u4e5f\u4e0d\u770b\u4ed6\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u8f9b\u9f99\u5b50\u695a\u662d\u5357\u7684\u4e24\u9a91\u9a6c\u5df2\u7ecf\u5728\u671b\u3002\u6768\u4e91\u806a\u53cc\u817f\u4e00\u5939\u9a6c\u8179\uff0c \u75be\u98ce\u4e00\u6837\u7684\u51b2\u53bb\uff0c\u56de\u9996\u5bf9\u98de\u7ea2\u5dfe\u9053\uff1a\u201c\u7b49\u4e0b\u4f60\u622a\u7740\u90a3\u4e2a\u8f9b\u9f99\u5b50\uff0c\u4e0d\u8981\u4f24\u4ed6\u7684\u6027\u547d\uff0c\u6211\u53bb\u6349\u695a \u662d\u5357\u3002\u201d\u98de\u7ea2\u5dfe\u4ecd\u662f\u95ee\u58f0\u4e0d\u54cd\uff0c\u6768\u4e91\u806a\u7684\u9a6c\u5df2\u8dd1\u5230\u524d\u5934\uff0c\u770b\u770b\u5c31\u8981\u548c\u524d\u9762\u4e24\u9a91\uff0c\u8854\u5c3e\u76f8\u63a5\u3002 \u3000\u3000\u9661\u7136\u95f4\uff0c\u8fce\u9762\u53c8\u98de\u9a70\u6765\u4e24\u9a91\u5feb\u9a6c\uff0c\u6768\u4e91\u806a\u5c1a\u672a\u770b\u6e05\uff0c\u5ffd\u542c\u5f97\u524d\u9762\u8f9b\u9f99\u5b50\u5927\u58f0\u53eb\u9053\uff1a\u201c\u5e08 \u53d4\u7956\uff1a\u66ff\u6211\u6321\u4e00\u6321\uff0c\u4ed6\u4eec\u8981\u5bb3\u6211\uff01\u201d\u6768\u4e91\u806a\u9661\u7136\u4e00\u52d2\u9a6c\u7f30\uff0c\u90a3\u4e24\u9a91\u9a6c\u5df2\u51b2\u5230\u9762\u524d\uff0c\u9a6c\u4e0a\u4eba\u662f \u4e24\u4e2a\u9053\u58eb\u3002\u5404\u4f7f\u7740\u4e00\u628a\u5bd2\u5149\u95ea\u95ea\u7684\u957f\u5251\uff0c\u6768\u4e91\u806a\u6b63\u6b32\u53d1\u8bdd\uff0c\u80cc\u540e\u98de\u7ea2\u4e2d\u75be\u51b2\u4e0a\u6765\u3002\u5e74\u8001\u7684\u9ec4 \u51a0\u9053\u58eb\u559d\u9053\uff1a\u201c\u4f60\u662f\u767d\u53d1\u9b54\u5973\u7684\u4ec0\u4e48\u4eba\uff1f\u201d\u98de\u7ea2\u4e2d\u6b63\u6ee1\u809a\u95f7\u6c14\uff0c\u5237\u7684\u4e00\u97ad\u626b\u51fa\uff0c\u6012\u9053\uff1a\u201c\u4f60 \u4eec\u505a\u4ec0\u4e48\u62e6\u963b\u6211\uff1f\u4f60\u4eec\u8fd8\u80c6\u6562\u53eb\u6211\u5e08\u7236\u7684\u540d\u5b57\uff01\u201d\u4e24\u4e2a\u8001\u9053\u4e92\u76f8\u4e00\u671b\uff0c\u53eb\u9053\uff1a\u201c\u54c8\uff0c\u679c\u7136\u78b0 \u5230\uff0c\u9053\u7239\u4e14\u5148\u628a\u4f60\u4eec\u8fd9\u4e24\u4e2a\u5c0f\u5996\u5b7d\u5e9f\u4e86\u518d\u53bb\u627e\u4f60\u7684\u5e08\u5085\u3002\u201d\u4e00\u4eba\u4e00\u8fb9\uff0c\u957f\u5251\u4e00\u6307\uff0c\u5bd2\u5149\u7535 \u5c04\uff0c\u5168\u662f\u8fdb\u624b\u7684\u62db\u6570\u3002 \u3000\u3000\u6768\u4e91\u806a\u6025\u5fd9\u559d\u9053\uff1a\u201c\u5582\uff0c\u6709\u8bdd\u6162\u8bf4\uff01\u201d\u9053\u58eb\u559d\u9053\uff1a\u201c\u8c01\u8010\u70e6\u548c\u4f60\u8bf4\uff01\u201d\u5237\uff01\u5237\uff01\u5237\uff01\u8fde \u73af\u4e09\u5251\uff0c\u8fc5\u6377\u5f02\u5e38\uff0c\u7adf\u662f\u6b66\u5f53\u6d3e\u6781\u4e0a\u4e58\u7684\u5251\u6cd5\uff0c\u6768\u4e91\u806a\u867d\u7136\u6599\u5230\u4ed6\u4eec\u7684\u6765\u5386\uff0c\u4f46\u6b66\u6797\u9ad8\u624b\u5bf9 \u654c\uff0c\u751f\u6b7b\u5b58\u4ea1\u53ea\u662f\u6beb\u53d1\u4e4b\u95f4\uff0c\u4e0d\u80fd\u4e0d\u51dd\u5fc3\u4e00\u5fd7\uff0c\u7ec6\u62c6\u654c\u62db\uff0c\u90a3\u8001\u9053\u5251\u5251\u8f9b\u8fa3\uff0c\u800c\u4e14\u529f\u529b\u4e4b\u9ad8 \u7adf\u662f\u6768\u4e91\u806a\u5e73\u751f\u672a\u9047\u8fc7\u7684\u654c\u624b\u3002\u6768\u4e91\u806a\u65e0\u5948\uff0c\u628a\u5929\u5c71\u5251\u6cd5\u4e2d\u7684\u201c\u5bd2\u6d9b\u5251\u6cd5\u201d\u4f7f\u5c06\u51fa\u6765\uff0c\u77ed\u5251 \u4e00\u6296\uff0c\u84e6\u7136\u5bd2\u5149\u70b9\u70b9\uff0c\u4e00\u67c4\u5251\u5c31\u597d\u50cf\u5316\u4e86\u51e0\u5341\u67c4\u4e00\u6837\uff0c\u4f7f\u5230\u6025\u5904\uff0c\u771f\u5982\u5bd2\u6d9b\u63a0\u5730\uff0c\u6012\u6f6e\u5377 \u7a7a\uff0c\u94f6\u5149\u98de\u6d12\uff0c\u5343\u70b9\u4e07\u70b9\uff0c\u4e71\u6d12\u4e0b\u6765\uff01\u90a3\u8001\u9053\u4e5f\u7aef\u7684\u5389\u5bb3\uff0c\u4e00\u53e3\u5251\u4f7f\u5f97\u4e0d\u75be\u4e0d\u5f90\u3002\u5251\u5149\u7f2d \u7ed5\uff0c\u5251\u5f71\u5982\u5c71\uff0c\u7adf\u4f3c\u5728\u6768\u4e91\u806a\u4e4b\u524d\uff0c\u5e03\u4e86\u4e00\u9762\u94dc\u5899\u94c1\u58c1\uff0c\u6768\u4e91\u806a\u7684\u5251\u5c16\u6307\u5904\uff0c\u5230\u5904\u90fd\u78b0\u7740\u4e00 \u80a1\u6f5c\u529b\uff0c\u53cd\u51fb\u8fc7\u6765\uff0c\u5bd2\u6d9b\u5251\u6cd5\u5c06\u8981\u4f7f\u5b8c\uff0c\u5140\u662f\u4e0d\u80fd\u5c06\u4ed6\u51fb\u9000\uff0c\u767e\u5fd9\u4e2d\u5077\u770b\u98de\u7ea2\u5dfe\uff0c\u89c1\u5979\u5df2\u6218 \u81f3\u62ab\u5934\u6563\u53d1\uff0c\u957f\u97ad\u4e71\u821e\uff0c\u77ed\u5251\u76d8\u65cb\uff0c\u770b\u6765\u5df2\u662f\u4e0d\u6210\u7ae0\u6cd5\uff0c\u6768\u4e91\u806a\u5927\u6025\uff0c\u628a\u5929\u5c71\u5251\u6cd5\u7684\u7cbe\u5999\u62db \u6570\uff0c\u5c3d\u91cf\u65bd\u51fa\u6765\uff0c\u653b\u5982\u96f7\u9706\u8fc5\u7535\uff0c\u5b88\u5982\u6c5f\u6d77\u51dd\u5149\uff0c\u90a3\u8001\u9053\u5fae\u5fae\u55dc\u4e86\u4e00\u58f0\uff0c\u4ecd\u662f\u7d27\u5b88\u95e8\u6237\uff0c\u4e00 \u53e3\u5251\u4e0a\u4e0b\u7ffb\u98de\uff0c\u6697\u8fd0\u5185\u4e3a\uff0c\u65f6\u4e0d\u65f6\u628a\u6768\u4e91\u806a\u7684\u5251\u7c98\u51fa\u5916\u95e8\uff0c\u6768\u4e91\u806a\u6ee1\u5934\u5927\u6c57\uff0c\u5140\u662f\u4e0d\u80fd\u8131\u51fa \u56f5\u5b50\u3002\u6768\u4e91\u806a\u7684\u5929\u5c71\u5251\u6cd5\u672c\u662f\u5929\u4e0b\u65e0\u53cc\uff0c\u6bd4\u90a3\u9053\u4eba\u7cbe\u5999\u8bb8\u591a\uff0c\u4f46\u82e5\u8bba\u529f\u529b\uff0c\u5374\u8fd8\u4e0d\u5982\u9053\u4eba\u7684 \u6df1\u539a\uff0c\u56e0\u6b64\u7adf\u662f\u5904\u5728\u4e0b\u98ce\uff0c\u800c\u90a3\u4e00\u8fb9\u98de\u7ea2\u4e2d\u5df2\u529b\u7aed\u7b4b\u75b2\uff0c\u582a\u582a\u5c31\u8981\u843d\u8d25\uff0c\u6768\u4e91\u806a\u6beb\u65e0\u529e\u6cd5\uff0c \u6b63\u60f3\u65bd\u5c55\u7edd\u62db\u548c\u8001\u9053\u62fc\u547d\uff0c\u5ffd\u7136\u90a3\u8001\u9053\u6258\u5730\u8df3\u51fa\u5708\u5b50\uff0c\u5927\u53eb\uff1a\u201c\u4f4f\u624b\uff01\u4f4f\u624b\uff01\u201d\u6768\u4e91\u806a\u77ed\u5251 \u4e00\u6536\uff0c\u6a2a\u5728\u80f8\u524d\uff0c\u770b\u90a3\u8fb9\u65f6\uff0c\u98de\u7ea2\u4e2d\u4e5f\u5df2\u6c14\u5598\u5401\u5401\uff0c\u8df3\u51fa\u5708\u5b50\u3002 \u3000\u3000\u548c\u6768\u4e91\u806a\u5bf9\u654c\u7684\u8001\u9053\u62db\u547c\u4ed6\u7684\u540c\u4f34\u9053\uff1a\u201c\u5e08\u5f1f\uff0c\u8fd9\u4e24\u4e2a\u4eba\u6709\u70b9\u6765\u5386\uff01\u201d\u4e0e\u98de\u7ea2\u5dfe\u5bf9\u654c\u7684 \u9053\u58eb\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u662f\u6709\u70b9\u6765\u5386\uff0c\u5979\u7684\u72ec\u95e8\u6b66\u529f\uff0c\u6b63\u662f\u767d\u53d1\u9b54\u5973\u7684\u4f20\u6388\u3002\u5979\u5e76\u6ca1\u6709\u7792\u9a97\u6211\u4eec\uff0c \u4ed6\u4eec\u65e2\u662f\u767d\u53d1\u9b54\u5973\u7684\u5b6a\u5f92\uff0c\u5e08\u5144\u4e3a\u4f55\u7f62\u624b\uff1f\u201d\u9ec4\u51a0\u8001\u9053\u4ef0\u5929\u957f\u7b11\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u4e45\u95fb\u5929\u5c71\u5251 \u6cd5\uff0c\u5929\u4e0b\u65e0\u53cc\uff0c\u679c\u7136\u4e0d\u9519\u3002\u5484\uff0c\u4f60\u662f\u6666\u660e\u7985\u5e08\u7684\u4ec0\u4e48\u4eba\uff1f\u201d\u90a3\u8001\u9053\u4ee5\u51e0\u5341\u5e74\u52a8\u529b\uff0c\u6b66\u5f53\u6d3e\u7684 \u7b2c\u4e00\u9ad8\u624b\uff0c\u7adf\u7ed9\u5e74\u7eaa\u8f7b\u8f7b\u7684\u6768\u4e91\u806a\u62c6\u4e86\u8fd9\u4e48\u591a\u62db\uff0c\u989d\u4e0a\u4e5f\u662f\u5fae\u5fae\u6c81\u6c57\uff0c\u4e5f\u662f\u5341\u5206\u60ca\u8be7\u3002 \u3000\u3000\u6768\u4e91\u806a\u606d\u58f0\u7b54\u9053\uff1a\u201c\u6666\u660e\u7985\u5e08\u6b63\u662f\u5bb6\u5e08\u3002\u4e0d\u6562\u95ee\u8001\u524d\u8f88\u6cd5\u8bb3\u3002\u201d\u90a3\u8fb9\u7684\u9053\u58eb\u559d\u9053\uff1a\u201c\u4f60 \u65e2\u662f\u6666\u660e\u7684\u5f1f\u5b50\uff0c\u4e3a\u4f55\u98a0\u5012\u8d77\u6765\uff0c\u53cd\u7ed9\u767d\u53d1\u9b54\u5973\u7684\u5f92\u5f1f\u52a9\u62f3\uff1f\u201d\u6768\u4e91\u806a\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u6ca1\u95e8 \u6237\u4e4b\u89c1\uff0c\u8fd9\u4f4d\u5973\u82f1\u96c4\u662f\u5357\u7586\u5404\u65cf\u76df\u4e3b\uff0c\u9a70\u540d\u8349\u539f\u7684\u5973\u82f1\u96c4\u98de\u7ea2\u5dfe\uff0c\u6211\u4e3a\u4ec0\u4e48\u4e0d\u8be5\u5e2e\u5979\uff1f\u201d\u8001 \u9053\u84e6\u7136\u9053\uff1a\u201c\u54b3\uff0c\u539f\u6765\u8fd9\u4f4d\u5973\u5c45\u58eb\u5c31\u662f\u98de\u7ea2\u5dfe\u3002\u60f3\u4e0d\u5230\u5979\u7adf\u662f\u767d\u53d1\u9b54\u5973\u7684\u5f92\u5f1f\uff01\u201d\u98de\u7ea2\u5dfe\u50b2 \u7136\u9053\uff1a\u201c\u6211\u662f\u767d\u53d1\u9b54\u5973\u7684\u5f92\u5f1f\uff01\u585e\u5916\u82f1\u8c6a\uff0c\u8c01\u4e0d\u77e5\u9053\uff1f\u6211\u7684\u5e08\u7236\u600e\u4e48\uff0c\u5979\u662f\u6b66\u6797\u4e2d\u7b2c\u4e00\u4f4d\u5973 \u5251\u5ba2\uff0c\u6709\u4ec0\u4e48\u8fb1\u6ca1\u6b66\u6797\u4e4b\u5904\uff1f\u201d\u90a3\u8001\u9053\u58eb\u8bcd\u8272\u5df2\u8f6c\u6e29\u548c\uff0c\u6b49\u7136\u8bf4\u9053\uff1a\u201c\u5973\u82f1\u96c4\uff0c\u5931\u656c\u4e86\uff01\u8bf4 \u6765\u6d3b\u957f\uff0c\u6211\u4e0d\u613f\u5f53\u9762\u9a82\u4f60\u7684\u5e08\u7236\u3002\u4f46\u4f60\u5e74\u8f7b\u5c1a\u8f7b\uff0c\u8bb8\u591a\u4e8b\u60c5\u90fd\u4e0d\u77e5\u9053\uff0c\u4f60\u53bb\u6297\u6e05\u5175\uff0c\u884c\u4fa0 \u4e49\uff0c\u6211\u4eec\u53ea\u6709\u52a9\u4f60\u3002\u51b3\u4e0d\u963b\u6320\uff0c\u53ea\u662f\u4f60\u82e5\u542c\u4f60\u5e08\u7236\u5dee\u4f7f\uff0c\u53bb\u6b3a\u8d1f\u6211\u7684\u5e08\u4f84\uff0c\u90a3\u6211\u4eec\u53ef\u5c31\u4e0d\u80fd \u653e\u8fc7\u4f60\u4e86\uff01\u201d\u6768\u4e91\u806a\u60ca\u95ee\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u4e24\u4f4d\u662f\u5353\u5927\u4fa0\u7684\u5e08\u53d4\u4e86\uff01\u201d\u4e24\u4e2a\u9053\u58eb\u5fae\u4e00\u7a3d\u9996\uff0c\u8bf4 \u9053\uff1a\u201c\u6b63\u662f\uff01\u201d\u6392\u8d77\u6765\uff0c\u6768\u4e91\u806a\u8981\u4f4e\u4e24\u8f88\uff0c\u6025\u5fd9\u65bd\u793c\u3002\u8001\u9053\u58eb\u53c8\u9053\uff1a\u201c\u6211\u4eec\u548c\u6666\u660e\u90fd\u662f\u51e0\u4ee3 \u4ea4\u60c5\uff0c\u5404\u4ea4\u5404\u7684\uff0c\u6211\u4eec\u548c\u4ed6\u662f\u5e73\u8f88\u76f8\u79f0\uff0c\u4ed6\u56e0\u4e3a\u5c0a\u91cd\u6211\u4eec\u7684\u5e08\u4f84\u66fe\u662f\u4e00\u6d3e\u638c\u95e8\uff0c\u6240\u4ee5\u4ed6\u4eec\u662f \u5e73\u8f88\u76f8\u79f0\uff0c\u4f60\u4eec\u65e2\u7136\u6309\u73ed\u8f88\u53d9\u672d\uff0c\u90a3\u4f60\u5c31\u79f0\u6211\u5e08\u53d4\u597d\u4e86\u3002\u201d\u6768\u4e91\u806a\u9053\u58f0\uff1a\u201c\u5f97\u7f6a\u3002\u201d\u65bd\u793c\u4e4b \u540e\uff0c\u5341\u5206\u7eb3\u95f7\uff0c\u90fd\u4e0d\u6562\u52a8\u95ee\u3002 \u3000\u3000\u8fd9\u4e24\u4e2a\u9053\u58eb\uff0c\u90fd\u662f\u65b0\u4ece\u56db\u5ddd\u6765\u7684\u3002\u6240\u4ee5\u4e0d\u77e5\u9053\u98de\u7ea2\u5dfe\u6765\u5386\uff0c\u539f\u6765\u5353\u4e00\u822a\u672c\u662f\u8d35\u5bb6\u516c\u5b50\uff0c \u540e\u6765\u505a\u4e86\u6b66\u5f53\u638c\u95e8\uff0c\u4ed6\u5934\u4e0a\u8fd8\u6709\u56db\u4e2a\u5e08\u53d4\uff0c\u4ed6\u7684\u6b66\u529f\u9664\u4e86\u6bd4\u4e8c\u5e08\u53d4\u9ec4\u53f6\u9053\u4eba\uff08\u5373\u548c\u6768\u4e91\u806a\u5bf9 \u654c\u7684\u8fd9\u4eba\uff09\u7a0d\u4f4e\u5916\uff0c\u6bd4\u5176\u4ed6\u5e08\u53d4\u8fd8\u5f3a\uff0c\u548c\u98de\u7ea2\u5dfe\u5bf9\u654c\u7684\u5219\u662f\u5353\u4e00\u822a\u7684\u56db\u5e08\u53d4\uff0c\u540d\u5524\u767d\u77f3\u9053 \u4eba\u3002\u767d\u53d1\u9b54\u5973\u539f\u662f\u5ddd\u4e2d\u5927\u76d7\uff0c\u5353\u4e00\u822a\u4e0e\u5979\u76f8\u7231\uff0c\u5df2\u8bba\u5a5a\u5ac1\uff1b\u4ed6\u7684\u5e08\u53d4\u8f88\u5374\u8ba4\u4e3a\u6b66\u5f53\u6d3e\u662f\u6b66\u6797 \u6b63\u5b97\uff0c\u5353\u4e00\u822a\u662f\u672c\u95e8\u6700\u6770\u51fa\u7684\u4eba\uff0c\u53c8\u662f\u521d\u63a5\u638c\u95e8\u4e4b\u4f4d\uff0c\u4e0d\u5e94\u548c\u5973\u5f3a\u76d7\u5339\u914d\u3002\u5728\u90a3\u4e2a\u65f6\u5019\uff0c\u5a5a \u59fb\u8fd8\u662f\u8981\u542c\u7236\u6bcd\u4e4b\u547d\uff0c\u5c0a\u957f\u4e4b\u8a00\u3002\u5353\u4e00\u822a\u5df2\u65e0\u7236\u65e0\u6bcd\uff0c\u90a3\u5c31\u8be5\u542c\u5e08\u53d4\u7684\u8bdd\uff0c\u4ed6\u7684\u5e08\u53d4\u6a2a\u52a0\u963b \u6320\uff0c\u4ee4\u4ed6\u975e\u5e38\u82e6\u607c\u3002\u672c\u6765\uff0c\u8fd9\u8fd8\u4e0d\u662f\u4e0d\u53ef\u633d\u56de\uff0c\u504f\u751f\u767d\u53d1\u9b54\u5973\u6027\u60c5\u6781\u4e3a\u66b4\u8e81\uff0c\u4e00\u6012\u4e4b\u4e0b\uff1b\u7adf \u548c\u5353\u4e00\u822a\u7684\u5e08\u53d4\u5bf9\u654c\u8d77\u6765\uff0c\u5f53\u65f6\u9ec4\u53f6\u9053\u4eba\u548c\u767d\u77f3\u9053\u4eba\u90fd\u4e0d\u5728\u573a\uff0c\u5353\u4e00\u822a\u7684\u53e6\u5916\u4e24\u4e2a\u5e08\u53d4\u7ea2\u4e91 \u9053\u4eba\u548c\u9752\u84d1\u9053\u4eba\u7387\u9886\u95e8\u4e0b\u516d\u5927\u5f1f\u5b50\u56f4\u653b\u5979\u3002\u767d\u53d1\u9b54\u5973\u72ec\u6218\u6b66\u5f53\u6d3e\u516b\u540d\u9ad8\u624b\uff0c\u7adf\u628a\u7ea2\u4e91\u9053\u4eba\u4f24 \u4e86\uff0c\u800c\u5979\u81ea\u5df1\u4e5f\u4e2d\u4e86\u9752\u84d1\u9053\u4eba\u4e00\u5251\uff0c\u4e24\u8d25\u4ff1\u4f24\uff0c\u767d\u53d1\u9b54\u5973\u65e2\u5931\u610f\u60c5\u573a\uff0c\u53c8\u81ea\u77e5\u4e0d\u80fd\u5728\u5ddd\u4e2d\u7acb \u8db3\uff0c\u6240\u4ee5\u8fdc\u9041\u585e\u5916\uff0c\u72ec\u4e0a\u5929\u5c71\u3002\u5934\u53d1\u5728\u4e00\u591c\u4e4b\u95f4\uff0c\u5168\u90e8\u53d8\u767d\uff01\u5353\u4e00\u822a\u7ecf\u8fc7\u8fd9\u573a\u5927\u53d8\uff0c\u4e5f\u662f\u5fc3 \u7070\u610f\u51b7\uff1b\u5ffd\u7136\u6487\u4e0b\u638c\u95e8\u4e0d\u505a\uff0c\u4e5f\u8dd1\u5230\u585e\u5916\uff0c\u53ef\u662f\u767d\u53d1\u9b54\u5973\u548c\u4ed6\u4e4b\u95f4\uff0c\u8bef\u4f1a\u592a\u591a\uff0c\u5bf9\u4ed6\u53c8\u6068\u53c8 \u7231\uff0c\u53cd\u4e0d\u80af\u548c\u4ed6\u548c\u89e3\u4e86\u3002\u51e0\u5341\u5e74\u6765\uff0c\u4e24\u4eba\u5c31\u662f\u8fd9\u6837\u7684\u6069\u7231\u51a4\u5bb6\uff0c\u53c2\u5546\u5f02\u8def\uff0c\u65e0\u7f18\u590d\u5408\u3002\u6700\u8fd1 \u767d\u53d1\u9b54\u5973\u8bef\u4f1a\u4ed6\u4e0e\u9ec4\u53f6\u9053\u4eba\u7684\u4fd7\u5bb6\u5973\u5f1f\u5b50\u4f55\u7f18\u534e\u76f8\u604b\uff0c\u53d1\u6012\u8d77\u6765\uff0c\u8981\u628a\u4ed6\u4eec\u9010\u51fa\u65b0\u7586\uff0c\u5353\u4e00 \u822a\u77e5\u9053\u767d\u53d1\u9b54\u5973\u624b\u5e95\u6700\u8fa3\uff0c\u6015\u5979\u4f24\u5bb3\u4e86\u4f55\u7f18\u534e\uff0c\u6025\u5fd9\u628a\u5979\u9001\u51fa\u5173\u53bb\uff0c\u4e0d\u6599\u9ec4\u53f6\u9053\u4eba\u4e0d\u77e5\u4ece\u4f55 \u5f97\u8baf\uff0c\u8fdc\u8fdc\u8d76\u6765\u3002\u8f9b\u9f99\u5b50\u5c11\u65f6\u89c1\u8fc7\u9ec4\u53f6\u4e00\u9762\uff0c\u4ed6\u4eec\u8fd9\u4e00\u7a81\u7136\u649e\u6765\uff0c\u6070\u6070\u7737\u8f9b\u9f99\u5b50\u548c\u695a\u662d\u5357\u89e3 \u4e86\u56f0\u5384\u3002 \u3000\u3000\u518d\u8bf4\u98de\u7ea2\u5dfe\u542c\u4e86\u9ec4\u53f6\u9053\u4eba\u7684\u8bdd\uff0c\u5927\u4e3a\u751f\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u54fc\uff01\u4f60\u4eec\u8fd8\u8bf4\u5e2e\u52a9\u6211\u6297\u6e05\u5175\uff0c\u4f60\u4eec \u5374\u628a\u6e05\u5175\u7684\u5978\u7ec6\u653e\u4e86\uff01\u201d\u9ec4\u53f6\u9053\u4eba\u5927\u5403\u4e00\u60ca\uff0c\u6025\u5fd9\u95ee\u9053\uff1a\u201c\u600e\u4e48\uff0c\u8f9b\u9f99\u5b50\u662f\u5978\u7ec6\uff1f\u4e0d\u4f1a\u5427\uff01 \u6211\u867d\u7136\u4e0d\u5728\u5929\u5c71\uff0c\u4f46\u4e5f\u7d20\u95fb\u5353\u4e00\u822a\u8fd9\u4e2a\u5f92\u513f\uff0c\u5341\u5206\u865a\u5fc3\u5b66\u827a\uff0c\u4ed6\u600e\u4f1a\u51fa\u6765\u5e2e\u52a9\u6e05\u5ef7\uff01\u201d\u6768\u4e91 \u806a\u9053\uff1a\u201c\u8f9b\u5927\u54e5\u6216\u8bb8\u4e0d\u4f1a\uff0c\u53ef\u662f\u6055\u5f1f\u5b50\u76f4\u8bf4\uff0c\u4ed6\u4e3a\u4eba\u4e00\u5411\u7cca\u6d82\uff0c\u53ef\u80fd\u662f\u53d7\u695a\u662d\u5357\u8c0e\u8a00\u6240\u9a97\uff0c \u653e\u4ed6\u9003\u8d70\u4e86\uff01\u201d\u9ec4\u65f6\u9053\u4eba\u95ee\u9053\uff1a\u201c\u54ea\u4e2a\u695a\u662d\u5357\uff1f\u201d\u6768\u4e91\u806a\u9053\uff1a\u201c\u695a\u662d\u5357\u5c31\u662f\u5f1f\u5b50\u90a3\u4e0d\u6210\u6751\u7684 \u5e08\u5f1f\uff0c\u80cc\u53db\u5e08\u95e8\uff0c\u6295\u653e\u6e05\u519b\uff0c\u4e3a\u864e\u4f5c\u6005\uff0c\u6628\u665a\u4e3a\u5f1f\u5b50\u6240\u64d2\uff0c\u4eca\u671d\u7ed9\u4ed6\u9003\u8dd1\u4e86\uff01\u201d\u9ec4\u53f6\u9053\u4eba\u6572 \u6572\u989d\u89d2\uff0c\u8fde\u58f0\u8bf4\u9053\uff0c\u201c\u662f\u6211\u8001\u7cca\u6d82\u4e86\uff01\u8fd9\u6837\u5427\uff0c\u6211\u4eec\u627e\u7740\u5353\u4e00\u822a\uff0c\u8bf7\u4ed6\u4e25\u60e9\u8f9b\u9f99\u5b50\u597d\u4e86\u3002\u81f3 \u4e8e\u695a\u662d\u5357\uff0c\u4ed6\u4e0d\u662f\u6211\u672c\u95e8\u4e2d\u4eba\uff0c\u6211\u4eec\u4e0d\u4fbf\u7406\u4ed6\u3002\u201d\u8fd9\u65f6\uff0c\u8f9b\u9f99\u5b50\u548c\u695a\u662d\u5357\u65e9\u5df2\u53bb\u8fdc\uff0c\u8981\u8ffd\u4e5f \u8ffd\u4e0d\u5230\u4e86\u3002\u6768\u4e91\u806a\u548c\u98de\u7ea2\u5dfe\u53ea\u597d\u4e0e\u9ec4\u53f6\u3001\u767d\u77f3\u4e24\u4f4d\u9053\u4eba\u544a\u522b\uff0c\u56de\u8f6c\u54c8\u8428\u514b\u65cf\u7684\u8349\u539f\u8425\u5730\u3002 \u3000\u3000\u4e00\u8def\u4e0a\u6768\u4e91\u806a\u9017\u98de\u7ea2\u4e2d\u8bf4\u8bdd\uff0c\u98de\u7ea2\u5dfe\u90fd\u4e0d\u7406\u4e0d\u776c\uff0c\u6768\u4e91\u806a\u4e0d\u89c9\u6d41\u4e0b\u70ed\u6cea\uff0c\u8bda\u631a\u8bf4\u9053\uff1a \u201c\u98de\u7ea2\u5dfe\uff0c\u7b97\u6211\u8f9c\u8d1f\u4f60\u4e00\u756a\u5fc3\u610f\uff0c\u4f46\u54b1\u4eec\u8fd8\u662f\u8981\u5408\u529b\u6297\u6e05\u5440\uff01\u201d\u8c01\u77e5\u9053\u8fd9\u8bdd\u4e00\u51fa\uff0c\u8d8a\u53d1\u62db\u60f9 \u98de\u7ea2\u5dfe\u7684\u607c\u6012\uff0c\u6068\u58f0\u8bf4\u9053\uff1a\u201c\u6768\u4e91\u806a\uff0c\u8c01\u5bf9\u4f60\u6709\u4ec0\u4e48\u5fc3\u610f\u4e86\uff01\u4f60\u5c31\u628a\u6211\u98de\u7ea2\u4e2d\u770b\u5f97\u8fd9\u6837\u4e0b \u8d31\uff0c\u975e\u8981\u8ddf\u5b9a\u4f60\u4e0d\u884c\uff01\u54fc\uff01\u201d\u5979\u8fde\u6253\u51e0\u97ad\uff0c\u653e\u9a6c\u98de\u8dd1\uff0c\u6768\u4e91\u806a\u5413\u5f97\u518d\u4e5f\u4e0d\u6562\u8bf4\u8bdd\uff01 \u3000\u3000\u56de\u5230\u5e10\u5e55\u4e4b\u540e\uff0c\u6768\u4e91\u806a\u89c1\u4e86\u54c8\u8428\u514b\u65cf\u7684\u9996\u957f\uff0c\u544a\u7f6a\u4e4b\u540e\uff0c\u7ec6\u8bf4\u7ecf\u8fc7\u3002\u8001\u914b\u62c8\u987b\u7b11\u9053\uff1a \u201c\u7b97\u4e86\uff0c\u7ed9\u695a\u662d\u5357\u9003\u8131\uff0c\u867d\u7136\u53ef\u60dc\uff0c\u4f46\u6709\u4f60\u548c\u6211\u4eec\u5728\u4e00\u8d77\uff0c\u8fd8\u6015\u4e0d\u80fd\u518d\u6349\u4f4f\u4ed6\u5417\uff01\u6b63\u4e49\u5fc5 \u80dc\uff0c\u771f\u4e3b\u4fdd\u4f51\u6211\u4eec\uff0c\u654c\u4eba\u548c\u53db\u8d3c\u4e00\u5b9a\u4e0d\u80fd\u5f97\u901e\u7684\u3002\u4f60\u53bb\u4f11\u606f\u5427\uff01\u201d \u3000\u3000\u6768\u4e91\u806a\u5fc3\u5934\u82e6\u95f7\uff0c\u56de\u5230\u5e10\u5e55\uff0c\u53c8\u4e0d\u4fbf\u53bb\u627e\u98de\u7ea2\u5dfe\u3002\u7b2c\u4e8c\u5929\u4e00\u65e9\uff0c\u54c8\u8428\u514b\u65cf\u914b\u957f\u5ffd\u7136\u95ef \u8fdb\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u8fd9\u662f\u600e\u4e48\u8bf4\u7684\uff1f\u98de\u7ea2\u4e2d\u5e26\u5979\u7684\u4eba\u8d70\u4e86\uff01\u201d \u3000\u3000----------- \u3000\u3000\u98ce\u4e91\u9601 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u585e\u5916\u5947\u4fa0\u4f20\u300b\u2014\u2014\u7b2c\u4e8c\u5341\u4e09\u56de\u3000\u5b55\u80b2\u7740\u65b0\u7684\u751f\u547d \u6881\u7fbd\u751f\u300a\u585e\u5916\u5947\u4fa0\u4f20\u300b \u7b2c\u4e8c\u5341\u4e09\u56de\u3000\u5b55\u80b2\u7740\u65b0\u7684\u751f\u547d \u3000\u3000\u6768\u4e91\u806a\u5fc3\u5934\u4e00\u9707\uff0c\u5fd9\u95ee\u9053\uff1a\u201c\u600e\u4e48\u5979\u8fde\u591c\u8d70\u4e86\uff1f\u201d\u54c8\u8428\u514b\u65cf\u7684\u8001\u914b\u957f\u9012\u8fc7\u4e00\u5f20\u7f8a\u76ae\uff0c\u4e0a \u9762\u5199\u6ee1\u7ef4\u6587\uff0c\u539f\u6765\u662f\u98de\u7ea2\u5dfe\u7559\u4e0b\u6765\u7684\u3002\u6768\u4e91\u806a\u8bfb\u9053\uff1a\u201c\u6211\u4eec\u5357\u7586\u5404\u65cf\uff0c\u6b64\u6b21\u5e78\u8499\u6536\u5bb9\uff0c\u5341\u5206 \u611f\u6fc0\u3002\u73b0\u5728\u6d41\u6563\u7684\u6218\u58eb\u5df2\u91cd\u65b0\u805a\u96c6\uff0c\u5927\u90e8\u56de\u5f52\u8425\u5730\u3002\u6211\u4eec\u5728\u6b64\u5730\u7684\u6218\u58eb\uff0c\u51b3\u56de\u539f\u5730\uff0c\u91cd\u65b0\u7ecf \u8425\u7267\u573a\uff0c\u751f\u805a\u6559\u8bad\uff0e\u540c\u6297\u6e05\u5175\u3002\u4e0e\u8d35\u65cf\u613f\u6c38\u7ed3\u540c\u76df\uff0c\u8054\u4e07\u4e16\u4e4b\u597d\u3002\u54c8\u739b\u96c5\u3002\u201d\u6768\u4e91\u806a\u6c89\u541f \u8bf4\uff1a\u201c\u5979\u56de\u53bb\u5b89\u8f91\u6d41\u4ea1\uff0c\u91cd\u5efa\u7267\u573a\uff0c\u4e5f\u662f\u6b63\u4e8b\u3002\u5979\u4eec\u5357\u7586\u5404\u65cf\u5728\u6b64\uff0c\u539f\u662f\u4f5c\u5ba2\u3002\u4e0d\u80fd\u4e45\u7559\uff0c \u53ef\u8fd9\u6837\u5feb\u5c31\u8d70\uff0c\u5374\u662f\u51fa\u6211\u610f\u5916\u3002\u5979\u5e94\u8be5\u7b49\u5927\u8ba1\u8bae\u5b9a\u4e4b\u540e\u624d\u8d70\u7684\u3002\u201d\u54c8\u8428\u514b\u914b\u957f\u9ed8\u7136\u65e0\u8bed\uff0c\u6768 \u4e91\u806a\u66f4\u662f\u795e\u4f24\u3002 \u3000\u3000\u53ef\u662f\u6218\u60c5\u7d27\u5f20\uff0c\u6218\u4e91\u5bc6\u5e03\uff0c\u695a\u662d\u5357\u9003\u8d70\u4e4b\u540e\uff0c\u56de\u5230\u6e05\u519b\u9a7b\u5730\uff0c\u6218\u673a\u4e00\u89e6\u5373\u53d1\uff0c\u6768\u4e91\u806a\u8981 \u5e2e\u5fd9\u54c8\u8428\u514b\u7684\u914b\u957f\u7b56\u5212\uff0c\u4ed6\u662f\u518d\u65e0\u6687\u53bb\u60f3\u81ea\u5df1\u7684\u4e8b\u60c5\u4e86\u3002 \u3000\u3000\u6768\u4e91\u806a\u5728\u5580\u5c14\u6c81\u8349\u539f\u7684\u8425\u5e10\u4e2d\uff0c\u5fc3\u60c5\u5341\u5206\u7d27\u5f20\uff0c\u5343\u4f59\u91cc\u5916\uff0c\u7eb3\u5170\u660e\u6167\u5728\u4f0a\u7281\u7684\u5c06\u519b\u5e9c \u4e2d\uff0c\u5fc3\u60c5\u4e5f\u662f\u5341\u5206\u7d27\u5f20\uff0c\u81ea\u6768\u4e91\u806a\u53bb\u540e\uff0c\u5979\u7684\u8eab\u4f53\u53d1\u751f\u4e86\u53d8\u5316\uff0c\u603b\u662f\u611f\u89c9\u7761\u7720\u4e0d\u8db3\u4f3c\u7684\uff0c\u6e05 \u6668\u8d77\u6765\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u53c8\u662f\u95f7\u95f7\u6b32\u7761\uff0c\u80c3\u4e5f\u5f88\u4e0d\u8212\u670d\uff0c\u5e38\u5e38\u83ab\u540d\u5176\u5999\u7684\u5455\u5410\u8d77\u6765\uff0c\u5403\u4e86\u4e1c\u897f\u5c31 \u5410\uff0c\u800c\u4e14\u6709\u65f6\u7a7a\u809a\u5b50\u4f1a\u5410\u51fa\u9178\u6c34\u3002\u5979\u7f8e\u4e3d\u7684\u989c\u5bb9\uff0c\u4e5f\u5ffd\u7136\u8d77\u4e86\u4e00\u5c42\u9ed1\u6655\uff0c\u91cc\u9762\u8fd8\u751f\u4e86\u4e00\u4e9b\u6591 \u70b9\u3002\u5403\u4e1c\u897f\u4e5f\u5f88\u5947\u602a\uff0c\u4ee5\u524d\u6b22\u559c\u5403\u7684\u73b0\u5728\u53cd\u8ba8\u538c\u8d77\u6765\uff0c\u4ee5\u524d\u4e0d\u6b22\u559c\u5403\u7684\uff0c\u73b0\u5728\u53cd\u800c\u5f88\u60f3\u5c1d \u8bd5\uff0c\u7279\u522b\u559c\u6b22\u5403\u9178\u7684\u4e1c\u897f\uff0c\u813e\u6c14\u4e5f\u559c\u6012\u65e0\u5e38\uff0c\u548c\u4ece\u524d\u5927\u5927\u4e0d\u540c\uff0c\u8fde\u81ea\u5df1\u4e5f\u89c9\u5f97\u5947\u602a\u6781\u4e86\u3002\u7eb3 \u5170\u592b\u4eba\u5e76\u4e0d\u5e38\u89c1\u5230\u5979\uff0c\u6709\u4e00\u6b21\u89c1\u5230\uff0c\u6000\u7591\u5979\u662f\u751f\u75c5\uff0c\u8981\u8bf7\u533b\u751f\u7ed9\u5979\u8bca\u6cbb\u3002\u5979\u53ef\u4e0d\u77e5\u9053\u81ea\u5df1\u6709 \u4ec0\u4e48\u75c5\uff0c\u56de\u5230\u623f\u95f4\u91cc\uff0c\u53ea\u89c9\u975e\u5e38\u7126\u8e81\uff0c\u6ca1\u6765\u7531\u7684\u7830\u7830\u81a8\u81a8\u4e71\u6454\u4e1c\u897f\uff0c\u5976\u5988\u63a8\u95e8\u8fdb\u6765\uff0c\u7eb3\u5170\u660e \u6167\u53d1\u6c14\u9053\uff1a\u201c\u5988\u5988\u8981\u8bf7\u533b\u751f\u7ed9\u6211\u770b\u75c5\u54e9\uff0c\u4e0d\u77e5\u8fd9\u662f\u4ec0\u4e48\u602a\u75c5\u3002\u6210\u5929\u4e0d\u8212\u670d\uff0c\u5374\u53c8\u8bf4\u4e0d\u51fa\u539f\u7531 \u6765\uff01\u201d\u5976\u5988\u9762\u8272\u5341\u5206\u6c89\u91cd\uff0c\u63a9\u4e0a\u623f\u95e8\uff0c\u6084\u6084\u8bf4\u9053\uff1a\u201c\u5c0f\u59d0\uff0c\u672c\u6765\u6211\u4e0b\u8be5\u8bf4\u7684\uff0c\u6211\u60f3\u8fc7\u4e86\u597d\u51e0 \u5929\u597d\u51e0\u665a\uff0c\u89c9\u5f97\u8fd8\u662f\u5bf9\u5c0f\u59d0\u8bf4\u4e86\u7684\u597d\u3002\u73b0\u5728\u60c5\u52bf\u66f4\u6025\uff0c\u6211\u66f4\u975e\u8bf4\u4e0d\u53ef\uff0c\u5c0f\u59d0\uff0c\u4f60\u5343\u4e07\u4e0d\u80fd\u770b \u533b\u751f\uff01\u201d\u7eb3\u5170\u660e\u6167\u5341\u5206\u60ca\u8be7\u3001\u201c\u54a6\u201d\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u5976\u5988\uff0c\u4f60\u8bf4\u4ec0\u4e48\uff1b\u4ec0\u4e48\u4e8b\u60c5\u8fd9\u6837\u4e25\u91cd\uff1a\u4e3a \u4ec0\u4e48\u6211\u53c8\u4e0d\u80fd\u770b\u533b\u751f\u3002\u600e\u4e48\u4f60\u5c3d\u8bf4\u602a\u8bdd\uff01\u5976\u5988\u8f7b\u8f7b\u629a\u6478\u5979\u7684\u5934\u53d1\uff0c\u5728\u5979\u7684\u8033\u8fb9\u8bf4\u9053\uff0c\u201c\u5b69 \u5b50\uff0c\u4f60\u6709\u4e86\u8eab\u5b55\u4e86\uff01\u201d\u7eb3\u5170\u660e\u6167\u60ca\u6115\u5f97\u8bf4\u4e0d\u51fa\u8bdd\u6765\uff0c\u9893\u7136\u5012\u5728\u5730\u4e0a\uff0c\u4e0d\u77e5\u662f\u559c\u662f\u60b2\uff0c\u662f\u82e6\u662f \u4e50\uff0c\u773c\u6cea\u4e0d\u81ea\u89c9\u7684\u6d41\u51fa\u6765\u3002\u5976\u5988\u53cc\u624b\u73af\u62b1\u7740\u5979\uff0c\u7231\u601c\u7684\u53f9\u606f\u9053\uff1a\u201c\u6211\u53ef\u601c\u7684\u5b69\u5b50\uff0c\u4e0d\u8981\u54ed \u4e86\uff0c\u6211\u66ff\u4f60\u60f3\u60f3\u529e\u6cd5\u3002\u592b\u4eba\u8bf7\u7684\u533b\u751f\u662f\u4e07\u4e07\u4e0d\u80fd\u8ba9\u4ed6\u770b\u7684\u3002\u660e\u5929\u4f60\u5230\u8349\u539f\u53bb\u6563\u6b65\uff0c\u6211\u89c1\u5230\u4e86 \u592b\u4eba\u5c31\u8bf4\u4f60\u53ea\u662f\u7cbe\u795e\u7a0d\u574f\uff0c\u5e76\u6ca1\u6709\u4ec0\u4e48\u4e8b\uff0c\u73b0\u5728\u5df2\u7ecf\u597d\u4e86\u3002\u672c\u6765\u8ba9\u592b\u4eba\u77e5\u9053\u662f\u5e94\u8be5\uff0c\u53ea\u6050\u8001 \u7237\u77e5\u9053\uff0c\u90a3\u5c31\u4e0d\u5f97\u4e86\u4e86\u3002\u591a\u94ce\u6b63\u6d3e\u4eba\u5411\u4f60\u7236\u4eb2\u63d0\u4eb2\u54e9\u3002\u592b\u4eba\u4e00\u5411\u53c8\u6015\u8001\u7237\uff0c\u8001\u7237\u77e5\u9053\u4e86\uff0c\u4e0d \u9a82\u4f60\u4e5f\u4f1a\u9a82\u5979\u3002\u201d\u7eb3\u5170\u660e\u6167\u9053\uff0c\u201c\u90a3\u4e48\u5c06\u6765\u6211\u7684\u5b69\u5b50\u51fa\u4e16\uff0c\u600e\u80fd\u7792\u8fc7\u4ed6\u4eec\uff1f\u201d\u5976\u5988\u53c8\u53f9\u4e86\u4e00 \u58f0\u9053\uff1a\u201c\u5c0f\u59d0\uff0c\u6211\u518d\u5192\u5473\u8bf4\u4e00\u53e5\u8bdd\uff0c\u628a\u8fd9\u5b69\u5b50\u6253\u6389\u4e86\u597d\u4e0d\u597d\uff1f\u201d\u7eb3\u5170\u660e\u6167\u77aa\u773c\u8bf4\u9053\uff1a\u201c\u4f60\u662f \u8bf4\u8ba9\u6211\u6253\u80ce\uff1f\u201d\u5976\u5988\u9eef\u7136\u70b9\u4e86\u70b9\u5934\u3002\u7eb3\u5170\u660e\u6167\u4e0d\u77e5\u4ece\u54ea\u91cc\u5f97\u5230\u7684\u52c7\u6c14\uff0c\u5ffd\u7136\u8df3\u4e86\u8d77\u6765\uff0c\u7528\u575a \u5b9a\u7684\u6fc0\u52a8\u7684\u58f0\u97f3\u558a\u9053\uff1a\u201c\u4e0d\u884c\uff0c\u6211\u4e0d\u613f\u610f\uff01\u6211\u8981\u4fdd\u5b58\u8fd9\u4e2a\u5b69\u5b50\u3002\u4e0d\u7ba1\u4ed6\u662f\u7537\u662f\u5973\uff0c\u4ed6\u90fd\u662f\u6211 \u6700\u4eb2\u7231\u7684\u4eba\uff01\u201d\u8fd9\u65f6\uff0c\u5979\u5fc3\u4e2d\u5ffd\u7136\u5145\u6ee1\u4e86\u559c\u60a6\u3002\u611f\u5230\u6768\u4e91\u806a\u7684\u751f\u547d\u548c\u5979\u7684\u751f\u547d\u5df2\u7ecf\u8054\u7ed3\u5728\u4e00 \u8d77\uff0c\u53ea\u8981\u5b69\u5b50\u80fd\u591f\u987a\u5229\u8bde\u751f\uff0c\u90a3\u4e48\u6768\u4e91\u806a\u5c06\u6c38\u8fdc\u6d3b\u5728\u5979\u7684\u8eab\u8fb9\uff0c\u4e00\u76f4\u5230\u4ed6\u4eec\u4e24\u4eba\u90fd\u6b7b\u4e86\u4e4b \u540e\uff0c\u4ed6\u4eec\u7684\u751f\u547d\u4ecd\u4f1a\u7ee7\u7eed\u4e0b\u53bb\uff0c\u5728\u5b69\u5b50\u7684\u8eab\u4e0a\u7ee7\u7eed\u4e0b\u53bb\uff0c\u4ed6\u7231\u6781\u4e86\u6768\u4e91\u806a\uff0c\u4e5f\u7231\u6781\u4e86\u8fd9\u4e2a\u672a \u66fe\u6765\u5230\u4eba\u95f4\u3001\u4e0d\u77e5\u662f\u7537\u8fd8\u662f\u5973\u7684\u672a\u6210\u5f62\u7684\u5b69\u5b50\uff01\u5979\u7a81\u7136\u53eb\u51fa\u58f0\u9053\uff1a\u201c\u6211\u518d\u4e0d\u6015\u4ec0\u4e48\u98de\u7ea2\u5dfe \u4e86\u3002\u4ed6\u7684\u751f\u547d\u5df2\u7ecf\u6d3b\u5728\u6211\u7684\u4f53\u5185\u4e86\uff01\u201d\u5976\u5988\u5947\u9053\uff1a\u201c\u4ec0\u4e48\u98de\u7ea2\u5dfe\u5440\uff1f\u201d\u7eb3\u5170\u660e\u6167\u542b\u7b11\u4e0d\u7b54\u3002 \u5976\u5988\u7126\u6025\u5f02\u5e38\uff0c\u5fc3\u91cc\u6697\u9053\uff1a\u201c\u771f\u662f\u4e2a\u4e0d\u61c2\u4e8b\u7684\u5b69\u5b50\uff0c\u8fd8\u662f\u8fd9\u6837\u7684\u6dd8\u6c14\uff1f\u201d\u5979\u6c89\u601d\u4e86\u597d\u4e00\u4f1a\uff0c \u8f7b\u8f7b\u7684\u63a8\u7740\u7eb3\u5170\u660e\u6167\u9053\uff1a\u201c\u5c0f\u59d0\uff0c\u8d77\u6765\uff0c\u6211\u60f3\u51fa\u6cd5\u5b50\u4e86\u4f60\u770b\u80fd\u4e0d\u80fd\u884c\u5440\uff1f\u201d\u7eb3\u5170\u660e\u6167\u5982\u68a6\u521d \u68a6\uff0c\u5728\u81ea\u6211\u9676\u9189\u4e2d\u9192\u89c9\u8fc7\u6765\uff0c\u542b\u7f9e\u95ee\u9053\uff1a\u201c\u5976\u5988\uff0c\u4ec0\u4e48\u6cd5\u5b50\uff1f\u201d\u5976\u5988\u9053\uff1a\u201c\u5c0f\u59d0\uff0c\u4f60\u4e0d\u662f\u5e38 \u5e38\u6253\u730e\u5417\uff1f\u5230\u4e94\u4e2a\u6708\u5de6\u53f3\uff1b\u4f60\u5c31\u5e26\u5973\u5175\u53bb\u51e0\u767e\u91cc\u5916\u7684\u8349\u539f\u6253\u730e\uff0c\u6211\u6709\u4e00\u4e2a\u5be1\u5ac2\u4f4f\u5728\u90a3\u51e0\uff0c\u6211 \u7684\u4f84\u513f\u73b0\u5728\u5c06\u519b\u5e9c\u505a\u4e8b\uff0c\u5c31\u662f\u90a3\u4e2a\u50bb\u91cc\u50bb\u6c14\u7684\u695e\u5c0f\u5b50\uff0c\u4f60\u4e5f\u89c1\u8fc7\u4e86\u7684\uff0c\u5c31\u53eb\u4ed6\u966a\u4f60\u53bb\u3002\u4ed6\u4eba \u867d\u7136\u50bb\uff0c\u53ef\u662f\u5374\u6700\u542c\u6211\u7684\u8bdd\u3002\u201d\u7eb3\u5170\u660e\u6167\u559c\u5f97\u6402\u7740\u5976\u5988\u9053\uff1a\u201c\u5976\u5988\uff0c\u4f60\u771f\u60f3\u5f97\u5468\u5230\u3002\u6211\u8bf4\u8981 \u53bb\u6253\u730e\uff0c\u90a3\u4e00\u5b9a\u884c\uff0c\u6211\u5fd8\u8bb0\u544a\u8bc9\u4f60\uff0c\u6211\u7b2c\u4e00\u6b21\u78b0\u89c1\u5979\u7684\u7236\u4eb2\uff0c\u5c31\u662f\u5728\u6253\u730e\u7684\u65f6\u5019\u5440\uff01\u201d\u5976\u5976 \u95ee\u9053\uff1a\u201c\u90a3\u4e2a\u5979\u5440\uff1f\u201d\u4e00\u95ee\u51fa\u53e3\uff0c\u5c31\u9192\u609f\u8d77\u201c\u5979\u201d\uff0c\u5c31\u662f\u5c0f\u59d0\u809a\u4e2d\u7684\u5b69\u5b50\uff0c\u4e0d\u89c9\u201c\u683c\u201d\u7684\u4e00 \u58f0\u7b11\u4e86\u51fa\u6765\u3002 \u3000\u3000\u8f6c\u773c\u8fc7\u4e86\u51e0\u6708\uff0c\u7eb3\u5170\u660e\u6167\u5df2\u6709\u4e94\u4e2a\u6708\u8eab\u5b55\u4e86\u3002\u6070\u5de7\u7eb3\u5170\u79c0\u5409\u51fa\u53d1\u5230\u8fdc\u65b9\u4f5c\u6218\uff0c\u7eb3\u5170\u660e\u6167 \u53bb\u201c\u6253\u730e\u201d\u90a3\u5c31\u66f4\u65b9\u4fbf\u4e86\uff0c\u53ea\u544a\u8bc9\u6bcd\u4eb2\u4e00\u58f0\uff0c\u5c31\u5e26\u4e86\u5341\u591a\u4e2a\u5fc3\u8179\u7684\u5973\u5175\u548c\u90a3\u4e2a\u50bb\u5c0f\u5b50\u5230\u8349\u539f \u53bb\u4e86\u3002 \u3000\u3000\u7eb3\u5170\u660e\u6167\u8eb2\u5728\u8349\u539f\u7684\u5e10\u5e55\u91cc\uff0c\u7b49\u5019\u5b69\u5b50\u7684\u8bde\u751f\uff0c\u4e0d\u89c9\u53c8\u8fc7\u4e86\u56db\u4e2a\u591a\u6708\u3002\u4e00\u65e5\uff0c\u5ffd\u7136\u592b\u4eba \u5dee\u4e86\u51e0\u4e2a\u5973\u5175\u6765\u89c1\u5c0f\u59d0\uff0c\u5e26\u6765\u4e00\u4ef6\u60ca\u4eba\u7684\u6d88\u606f\uff0c\u4e09\u5929\u4e4b\u524d\uff0c\u4e00\u4e2a\u6708\u9ed1\u98ce\u9ad8\u7684\u665a\u4e0a\uff0c\u5c06\u519b\u5e9c \u91cc\uff0c\u5ffd\u7136\u6765\u4e86\u4e00\u4e2a\u5973\u98de\u8d3c\uff0c\u60f3\u627e\u8001\u7237\u627e\u4e0d\u5230\uff0c\u5374\u6293\u7740\u4e86\u5c0f\u59d0\u7684\u4e00\u4e2a\u4e2b\u5934\uff0c\u62f7\u95ee\u5c0f\u59d0\u7684\u6d88\u606f\u3002 \u8fd9\u4e2a\u5973\u98de\u8d3c\u672c\u9886\u5341\u5206\u9ad8\u5f3a\uff0c\u5979\u95ef\u8fdb\u5c06\u519b\u5e9c\u540e\uff0c\u76f4\u81f3\u6349\u7740\u5c0f\u4e2b\u5934\u62f7\u95ee\u4e4b\u65f6\uff0c\u90fd\u6ca1\u4eba\u53d1\u73b0\u5979\u3002\u5230 \u90a3\u5c0f\u4e2b\u5934\u88ab\u62f7\u6253\u558a\u51fa\u58f0\u540e\uff0c\u5c06\u519b\u5e9c\u91cc\u7684\u6b66\u5e08\u624d\u7eb7\u7eb7\u8d76\u6765\uff0c\u53ef\u662f\u8fd9\u4e2a\u5973\u98de\u8d3c\u5c45\u7136\u4e00\u70b9\u4e0d\u6015\uff0c\u5728 \u4f17\u6b66\u5e08\u7684\u56f4\u653b\u4e4b\u4e0b\uff0c\u7adf\u6beb\u53d1\u65e0\u4f24\uff0c\u6765\u53bb\u81ea\u5982\uff0c\u4e34\u884c\u524d\u8fd8\u7528\u957f\u97ad\u6253\u4f24\u4e86\u597d\u51e0\u540d\u6559\u5934\u3002\u592b\u4eba\u5341\u5206 \u5bb3\u6015\uff0c\u53eb\u5c0f\u59d0\u5c0f\u5fc3\uff0c\u8fd8\u53eb\u5c0f\u59d0\u6700\u597d\u56de\u6765\u7ed9\u5979\u58ee\u80c6\u3002\u7eb3\u5170\u660e\u6167\u8eb2\u5728\u5e8a\u4e0a\uff0c\u542c\u4e86\u5973\u5175\u7684\u8bf4\u8bdd\uff0c\u5fc3 \u77e5\u4e00\u5b9a\u662f\u98de\u7ea2\u5dfe\u6765\u627e\u5979\uff0c\u4e0d\u7981\u6068\u6068\u5730\u9a82\u9053\uff1a\u201c\u597d\u4e2a\u6bd2\u5fc3\u80a0\u7684\u5973\u8d3c\uff01\u201d\u4f46\u5979\u7684\u6b66\u529f\u8fd8\u4e0d\u53ca\u98de\u7ea2 \u5dfe\uff0c\u56de\u53bb\u4e5f\u6ca1\u4ec0\u4e48\u7528\uff0c\u66f4\u4f55\u51b5\u5979\u8ba1\u7b97\u65e5\u671f\uff0c\u4e34\u76c6\u53ea\u662f\u8fd9\u5341\u5929\u534a\u6708\u7684\u4e8b\u60c5\u4e86\uff0c\u5979\u53c8\u5982\u4f55\u80fd\u56de\u53bb \u5462\uff1f\u5979\u53ea\u597d\u53eb\u5976\u5988\u7684\u4f84\u513f\u56de\u53bb\uff0c\u62d6\u5b83\u4e00\u62d6\u3002\u53eb\u4ed6\u544a\u8bc9\u6bcd\u4eb2\uff0c\u4ed6\u8981\u8fc7\u201c\u51e0\u5929\u201d\u624d\u80fd\u56de\u6765\uff01 \u3000\u3000\u7eb3\u5170\u660e\u6167\u4f4f\u7684\u5730\u65b9\u867d\u7136\u9690\u79d8\uff0c\u53ef\u662f\u4e5f\u5f88\u6101\u6025\uff0c\u751f\u6015\u98de\u7ea2\u5dfe\u627e\u6765\uff0c\u5979\u53c8\u4e0d\u77e5\u90a3\u5c0f\u4e2b\u5934\u7ed9\u98de \u7ea2\u5dfe\u62f7\u95ee\uff0c\u6709\u6ca1\u6709\u900f\u9732\u6d88\u606f\u3002\u4f46\u5979\u53c8\u65cb\u5373\u81ea\u5df1\u5b89\u6170\u81ea\u5df1\u7684\u60f3\u9053\uff1a\u201c\u8349\u539f\u8fd9\u6837\u7684\u5927\uff0c\u5c31\u662f\u5979\u6765 \u5230\u8349\u539f\uff0c\u4e5f\u672a\u5fc5\u77e5\u9053\u6211\u5728\u8fd9\u513f\u3002\u201d\u5979\u53eb\u5fc3\u8179\u5973\u5175\u663c\u591c\u8f6e\u73ed\u9632\u5b88\uff0c\u5979\u81ea\u5df1\u867d\u7136\u884c\u52a8\u4e0d\u4fbf\uff0c\u4e5f\u5b89 \u4e00\u7b52\u7529\u624b\u7bad\u653e\u5728\u5e8a\u5934\uff0c\u51c6\u5907\u98de\u7ea2\u5dfe\u6765\u4e86\uff0c\u5c31\u548c\u5979\u6b7b\u62fc\u3002 \u3000\u3000\u7b2c\u4e09\u5929\u665a\u4e0a\uff0c\u53c8\u662f\u4e00\u4e2a\u6708\u9ed1\u98ce\u9ad8\u4e4b\u591c\u3002\u521a\u8fc7\u4e86\u5348\u591c\uff0c\u8349\u539f\u4e0a\u5ffd\u7136\u4f20\u6765\u4e86\u4e00\u9635\u9635\u7684\u9a6c\u8e44 \u58f0\uff0c\u5341\u51e0\u4e2a\u5f6a\u5f62\u5927\u6c49\u9a91\u7740\u5feb\u9a6c\u5954\u6765\u3002\u5976\u5988\u7684\u4f84\u513f\u7ed9\u53cd\u7ed1\u5728\u9a6c\u80cc\u4e0a\u3002\u5973\u5175\u4eec\u5728\u706b\u628a\u5149\u4e2d\u770b\u5f97\u6e05 \u6e05\u695a\u695a\uff0c\u4f46\u5374\u5e76\u4e0d\u89c1\u4e00\u4e2a\u5973\u4eba\u3002\u7eb3\u5170\u660e\u6167\u7684\u56db\u4e2a\u8d34\u8eab\u4e2b\u5934\u4ea4\u4e92\u671b\u4e86\u4e00\u773c\u3002\u8bf4\u9053\uff1a\u201c\u539f\u6765\u4e0d\u662f \u5973\u98de\u8d3c\uff01\u201d\u7acb\u523b\u62a1\u5200\u4f7f\u5251\uff0c\u5f20\u5f13\u98de\u7bad\uff0c\u548c\u90a3\u5341\u51e0\u4e2a\u5f6a\u5f62\u6c49\u5b50\u5927\u6218\u8d77\u6765\uff01 \u3000\u3000----------- \u3000\u3000\u98ce\u4e91\u9601 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u585e\u5916\u5947\u4fa0\u4f20\u300b\u2014\u2014\u7b2c\u4e8c\u5341\u56db\u56de\u3000\u4e00\u4e2a\u5973\u5b69\u5b50\u7684\u8bde\u751f \u6881\u7fbd\u751f\u300a\u585e\u5916\u5947\u4fa0\u4f20\u300b \u7b2c\u4e8c\u5341\u56db\u56de\u3000\u4e00\u4e2a\u5973\u5b69\u5b50\u7684\u8bde\u751f \u3000\u3000\u8fd9\u4e9b\u5973\u5175\u90fd\u662f\u7eb3\u5170\u660e\u6167\u4eb2\u624b\u8bad\u7ec3\u7684\uff0c\u6b66\u827a\u4e5f\u9887\u4e86\u5f97\uff0c\u5c24\u5176\u662f\u90a3\u56db\u4e2a\u8d34\u8eab\u4e2b\u5934\uff0c\u7bad\u6cd5\u66f4\u662f \u5389\u5bb3\uff0c\u5f3a\u76d7\u8fd8\u672a\u653b\u5230\u5e10\u5e55\u3002\u5df2\u7ed9\u5c04\u5012\u51e0\u4e2a\uff01\u539f\u6765\u8fd9\u5f6a\u4eba\u9a6c\uff0c\u4e43\u662f\u8349\u539f\u4e0a\u7684\u9a6c\u8d3c\uff0c\u4e3a\u9996\u7684\u53eb\u505a \u738b\u5927\u987b\u5b50\uff0c\u534a\u6708\u4e4b\u524d\uff0c\u4ed6\u542c\u51fa\u6709\u4e00\u7fa4\u5973\u5b50\uff0c\u5728\u8349\u539f\u4e0a\u6253\u730e\uff0c\u4ed6\u4e0d\u77e5\u9053\u662f\u7eb3\u5170\u5c0f\u59d0\uff0c\u53ea\u9053\u662f\u8349 \u539f\u4e0a\u4ec0\u4e48\u914b\u957f\u7684\u5973\u513f\u3002\u56e0\u6b64\u5e26\u4e86\u5341\u591a\u9a91\u5feb\u9a6c\uff0c\u4ece\u5580\u5c14\u6c81\u8349\u539f\u9a70\u672a\u884c\u52ab\u3002\u534a\u8def\u4e0a\u649e\u5230\u5976\u5988\u7684\u4f84 \u513f\uff0c\u987a\u624b\u628a\u4ed6\u64d2\u4e86\uff0c\u8feb\u4ed6\u5e26\u8def\u3002 \u3000\u3000\u4e00\u573a\u9984\u6218\uff0c\u9a6c\u8d3c\u5e76\u672a\u5360\u5f97\u4fbf\u5b9c\uff0c\u738b\u5927\u987b\u5b50\u6025\u4e86\uff0c\u5de6\u624b\u63a8\u7740\u5976\u5988\u7684\u4f84\u513f\uff0c\u53f3\u624b\u62a1\u5200\u731b\u65ab\uff0c \u5973\u5175\u4eec\u6295\u9f20\u5fcc\u5668\uff0c\u5c45\u7136\u7ed9\u4ed6\u51b2\u8fdb\u5e10\u5e55\u3002\u7eb3\u5170\u660e\u6167\u5750\u5728\u9526\u57ab\u4e0a\uff0c\u4e00\u89c1\u738b\u5927\u987b\u5b50\u51b2\u8fdb\uff0c\u626c\u6bdb\u5c31\u662f \u4e00\u628a\u98de\u5200\uff0c\u51c6\u75be\u5f02\u5e38\uff0c\u628a\u4ed6\u7684\u76ae\u5934\u524a\u4e86\u4e00\u5927\u7247\u76ae\u8089\uff0c\u738b\u5927\u987b\u5b50\u72c2\u543c\u4e00\u58f0\uff0c\u624b\u4e00\u677e\u52b2\uff0c\u5976\u5988\u7684 \u4f84\u513f\u8dcc\u8dcc\u649e\u649e\u5728\u5730\u4e0a\u7ffb\u6eda\uff0c\u738b\u5927\u987b\u5b50\u8de8\u6b65\u4e0a\u524d\uff0c\u4e00\u5200\u5411\u7eb3\u5170\u660e\u6167\u65a9\u53bb\uff0c\u7eb3\u5170\u660e\u6167\u4f0f\u5730\u4e00\u6eda\uff0c \u626c\u624b\u4e00\u67c4\u98de\u5200\uff0c\u5f53\u7684\u4e00\u58f0\uff0c\u738b\u5927\u987b\u5b50\u7684\u9a6c\u5200\u7adf\u7ed9\u51fb\u98de\u51fa\u624b\uff0c\u6014\u4e86\u4e00\u6014\uff0c\u5ffd\u7136\u7eb3\u5170\u660e\u6167\u201c\u54df \u5537\u201d\u8fde\u58f0\uff0c\u5979\u7528\u529b\u8fc7\u5ea6\uff0c\u8179\u4e2d\u9635\u75db\uff0c\u5934\u6655\u773c\u82b1\uff0c\u56db\u80a2\u65e0\u529b\uff0c\u547b\u541f\u53eb\u9053\uff1a\u201c\u6768\u4e91\u806a\u5440\u6768\u4e91\u806a\uff0c \u4f60\u7684\u5b69\u5b50\u4e0d\u80fd\u4fdd\u5168\u4e86\uff01\u201d \u3000\u3000\u5976\u5988\u7684\u4f84\u513f\u8fd9\u65f6\u5df2\u7ffb\u8d77\u8eab\u6765\uff0c\u62fc\u6b7b\u548c\u738b\u5927\u987b\u5b50\u7ea0\u7f20\uff0c\u4e0d\u8fc7\u51e0\u62db\uff0c\u53c8\u7ed9\u738b\u5927\u987b\u5b50\u6253\u5012\u3002\u738b \u5927\u987b\u5b50\u8fde\u58f0\u72de\u7b11\uff0c\u8de8\u4e0a\u4e00\u6b65\uff0c\u4e00\u6293\u5411\u7eb3\u5170\u660e\u6167\u6293\u53bb\uff0c\u5ffd\u7136\u5e10\u5e55\u5916\u54d7\u7136\u5927\u547c\uff0c\u738b\u5927\u987b\u5b50\u672a\u53ca\u56de \u5934\uff0c\u540e\u5fc3\u4e00\u9635\u5267\u75db\uff0c\u8eab\u5b50\u5df2\u7ed9\u4eba\u60ac\u7a7a\u63d0\u8d77\uff0c\u7eb3\u5170\u660e\u6167\u7741\u773c\u4e00\u770b\uff0c\u53ea\u89c1\u98de\u7ea2\u5dfe\u6ee1\u9762\u6740\u6c14\uff0c\u5de6\u624b \u957f\u97ad\u628a\u738b\u5927\u987b\u5b50\u5377\u7740\uff0c\u53f3\u624b\u6307\u7740\u7eb3\u5170\u660e\u6167\u9053\uff1a\u201c\u54fc\uff0c\u4f60\u5c31\u662f\u7eb3\u5170\u660e\u6167\u4e86\uff1f\u8fd9\u6837\u5a07\u602f\u7684\u6837\u5b50\uff0c \u5012\u771f\u662f\u4e2a\u5c0f\u59d0\u6a21\u6837\uff01\u201d \u3000\u3000\u98de\u7ea2\u5dfe\u81ea\u4ece\u79bb\u5f00\u6768\u4e91\u806a\u4e4b\u540e\uff0c\u6012\u6c14\u96be\u6d88\uff0c\u5b64\u8eab\u4e00\u4eba\uff0c\u8dd1\u5230\u4f0a\u7281\u5c06\u519b\u5e9c\u4e2d\u5927\u95f9\uff0c\u867d\u7136\u6ca1\u6293 \u7740\u7eb3\u5170\u660e\u6167\uff0c\u5374\u6293\u7740\u4e86\u5979\u7684\u4e2b\u5934\uff0c\u903c\u95ee\u51fa\u7eb3\u5170\u660e\u6167\u7684\u6d88\u606f\uff0c\u8d76\u5230\u8349\u539f\uff0c\u6b63\u597d\u9047\u4e0a\u8fd9\u573a\u6df7\u6218\u3002 \u98de\u7ea2\u5dfe\u4e0d\u7531\u5206\u8bf4\uff0c\u628a\u9a6c\u8d3c\u548c\u5973\u5175\uff0c\u5168\u90e8\u6253\u5f97\u7ffb\u7ffb\u6eda\u6eda\uff0c\u649e\u5165\u5e10\u7bf7\uff0c\u53ea\u4e00\u62db\u5c31\u628a\u738b\u5927\u987b\u5b50\u751f \u64d2\u3002\u5b58\u5fc3\u6298\u78e8\u7eb3\u5170\u660e\u6167\uff01 \u3000\u3000\u7eb3\u5170\u660e\u6167\u62ac\u5934\u671b\u7740\u98de\u7ea2\u5dfe\uff0c\u53e3\u89d2\u5659\u7740\u51b7\u7b11\uff0c\u4e00\u53cc\u660e\u5982\u79cb\u6c34\u7684\u773c\u775b\uff0c\u76ef\u5f97\u98de\u7ea2\u5dfe\u6253\u4e86\u4e2a\u5bd2 \u5664\u3002\u98de\u7ea2\u5dfe\u6c14\u5f97\u4e00\u97ad\u5c06\u738b\u5927\u987b\u5b50\u6454\u5230\u5899\u89d2\uff0c\u5389\u58f0\u9a82\u9053\uff0c\u201c\u4f60\u51b7\u7b11\u4ec0\u4e48\uff1f\u6709\u80c6\u7684\u5c31\u8d77\u8eab\u548c\u6211\u6597 \u51e0\u4e2a\u56de\u5408\u3002\u6211\u4e0d\u613f\u6740\u6beb\u65e0\u62b5\u6297\u7684\u4eba\u3002\u201d\u7eb3\u5170\u660e\u6167\u5c0f\u53e3\u5fae\u5fae\u5f00\u542f\uff0c\u8bed\u97f3\u867d\u5f31\uff0c\u98de\u7ea2\u5dfe\u542c\u6765\u5374\u5982 \u5e73\u5730\u7126\u96f7\uff01\u7eb3\u5170\u55ef\u6167\u8bf4\u9053\uff1a\u201c\u4f60\u8981\u6740\u6211\uff0c\u6211\u6beb\u4e0d\u8eb2\u95ea\uff0c\u4f60\u4e14\u7b49\u6211\u751f\u4e86\u5b69\u5b50\u518d\u6740\u6211\u884c\u4e0d\u884c\uff1f\u201d \u98de\u7ea2\u5dfe\u559d\u9053\uff1a\u201c\u4ec0\u4e48\uff0c\u4f60\u80b2\u4e86\u5b69\u5b50\uff1f\u8c01\u7684\u5b69\u5b50\uff1f\u201d\u7eb3\u5170\u660e\u6167\u9a84\u50b2\u7684\u7b11\u9053\uff1a\u201c\u6211\u548c\u6768\u5927\u4fa0\u7684\u5b69 \u5b50\uff01\u201d\u98de\u7ea2\u5dfe\u4e00\u770b\uff0c\u7eb3\u5170\u660e\u6167\u679c\u7136\u662f\u633a\u7740\u5927\u809a\u76ae\uff0c\u4e0d\u53d1\u4e00\u8a00\uff0c\u56de\u8eab\u4fbf\u8d70\uff0c\u5e10\u5e55\u5916\u9a6c\u8d3c\u548c\u5973\u5175 \u7ffb\u8d77\u8eab\u6765\u53c8\u6597\uff0c\u738b\u5927\u987b\u5b50\u4e5f\u5728\u5899\u89d2\u7ad9\u8d77\uff0c\u4fef\u8eab\u62fe\u4e86\u90a3\u53e3\u9a6c\u5200\uff0c\u98de\u7ea2\u5dfe\u7709\u5934\u4e00\u76b1\uff0c\u518d\u8f6c\u8fc7\u8eab \u6765\u3002\u559d\u95ee\u738b\u5927\u987b\u5b50\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u4f60\u6765\u8fd9\u91cc\u505a\u4ec0\u4e48\uff1f\u201d\u738b\u5927\u987b\u5b50\u521a\u624d\u770b\u89c1\u98de\u7ea2\u5dfe\u6b32\u6740\u7eb3\u5170\u660e \u6167\uff0c\u53ea\u9053\u5979\u4e5f\u662f\u7ebf\u4e0a\u7684\u5973\u532a\uff0c\u6025\u5fd9\u7b54\u9053\uff1a\u201c\u6211\u662f\u5580\u5c14\u6c81\u8349\u539f\u4e0a\u7684\u9a6c\u5e2e\u4e07\u5ba2\uff08\u9a6c\u8d43\u81ea\u79f0\uff09\uff0e\u59d1 \u5a18\u4f60\u662f\u54ea\u6761\u7ebf\u7684\uff1f\u8fd9\u4e2a\u81ed\u5a46\u5a18\u65e2\u662f\u5b55\u5987\uff0c\u54b1\u4eec\u6309\u89c4\u77e9\u4e0d\u6740\u5979\u597d\u4e86\uff0c\u5979\u770b\u6765\u662f\u4e2a\u914b\u957f\u7684\u5973\u513f\uff0c \u6cb9\u6c34\u53ef\u539a\u54e9\uff0c\u54b1\u4eec\u628a\u5979\u6d17\u52ab\u6765\u4e86\u5e73\u5206\u5427\uff0c\u59d1\u5a18\uff0c\u4f60\u72ec\u81ea\u8981\u4e00\u4efd\u597d\u4e86\uff0c\u6211\u738b\u5927\u987b\u5b50\u6700\u8bb2\u4e49 \u6c14\u3002\u201d\u98de\u7ea2\u5dfe\u9762\u4e00\u7ef7\uff0c\u559d\u9053\uff1a\u201c\u54c8\uff0c\u539f\u6765\u4f60\u662f\u9a6c\u8d3c\uff01\u201d\u738b\u5927\u987b\u5b50\u201c\u662f\u201d\u5b57\u8fd8\u672a\u51fa\u53e3\uff0c\u98de\u7ea2\u5dfe \u51fa\u624b\u5982\u7535\uff0c\u4e00\u97ad\u5c31\u628a\u4ed6\u7684\u5929\u7075\u76d6\u6253\u788e\uff0c\u8d70\u51fa\u5e10\u5e55\uff0c\u60e8\u53eb\u58f0\u968f\u4e4b\u800c\u8d77\uff0c\u4e0d\u8fc7\u7247\u523b\uff0c\u98de\u7ea2\u5dfe\u6ee1\u8eab \u6d74\u8840\uff0c\u8d70\u56de\u5e10\u5e55\uff0c\u51b7\u51b7\u7684\u5bf9\u7eb3\u5170\u660e\u9759\u8bf4\u9053\uff1a\u201c\u6211\u628a\u8fd9\u5e2e\u9a6c\u8d3c\u5168\u90fd\u6740\u4e86\uff0c\u4f60\u597d\u597d\u7684\u517b\u5b69\u5b50 \u5427\u3002\u201d\u7eb3\u5170\u660e\u6167\u5b9a\u7740\u53cc\u773c\uff0c\u4e0d\u77e5\u8bf4\u4e9b\u4ec0\u4e48\u624d\u597d\uff0c\u98de\u7ea2\u5dfe\u6536\u8d77\u957f\u97ad\uff0c\u63d2\u56de\u5b9d\u5251\uff0c\u5ffd\u51c4\u7136\u8bf4\u9053\uff1a \u201c\u6211\u8d70\u4e86\uff0c\u4f60\u89c1\u7740\u6768\u4e91\u806a\u65f6\u5c31\u544a\u8bc9\u4ed6\uff0c\u6211\u6c38\u4e0d\u4f1a\u518d\u627e\u4ed6\u4e86\u3002\u201d\u7eb3\u5170\u660e\u6167\u70b9\u4e86\u70b9\u5934\uff0c\u6b63\u60f3\u8bf4 \u8bdd\uff0c\u5ffd\u7136\u8179\u4e2d\u7ede\u75db\uff0c\u6025\u5fd9\u547c\u5524\u4e2b\u5934\uff0c\u5973\u5175\u7eb7\u7eb7\u8fdb\u6765\uff0c\u628a\u5976\u5988\u7684\u4f84\u513f\u63a8\u4e86\u51fa\u53bb\uff0c\u98de\u7ea2\u5dfe\u672c\u6765\u60f3 \u8d70\uff0c\u8fd9\u65f6\u5374\u5446\u5446\u7684\u7ad9\u7740\uff0c\u5ffd\u7136\u5e10\u5e55\u54cd\u8d77\u4e86\u201c\u545c\u54d7\u201d\u7684\u54ed\u58f0\uff0c\u6768\u4e91\u806a\u7684\u5b69\u5b50\u51fa\u751f\u4e86\uff0c\u5973\u5175\u4eec\u624b \u5fd9\u811a\u4e71\uff0c\u5e2e\u52a9\u7eb3\u5170\u660e\u6167\u6599\u7406\u3002\u8d34\u8eab\u7684\u5927\u4e2b\u5934\u628a\u65e9\u5df2\u51c6\u5907\u597d\u7684\u9526\u7f0e\uff0c\u5c06\u5b69\u5b50\u5168\u8eab\u5305\u7740\uff0c\u7eb3\u5170\u660e \u6167\u9762\u4e0a\u5145\u6ee1\u559c\u60a6\u7684\u795e\u60c5\uff0c\u5979\u5728\u5730\u4e0a\u5598\u7740\u6c14\u95ee\u9053\uff1a\u201c\u662f\u5c0f\u5b50\u8fd8\u662f\u59d1\u5a18\uff1f\u201d\u5927\u4e2b\u5934\u9053\uff1a\u201c\u606d\u559c\u5c0f \u59d0\u3002\u548c\u4f60\u4e00\u6837\uff01\u201d\u7eb3\u5170\u660e\u6167\u9053\uff1a\u201c\u5440\uff0c\u539f\u6765\u662f\u4e2a\u59d1\u5a18\uff0c\u4e5f\u597d\uff01\u62b1\u6765\u6211\u77a7\u77a7\uff0c\u201d\u4e2b\u5934\u9053\uff1a\u201c\u5979 \u53ef\u771f\u50cf\u5c0f\u59d0\u5462\uff01\u201d\u7eb3\u5170\u660e\u6167\u7528\u624b\u8f7b\u62cd\u5a74\u513f\uff0c\u4f4e\u58f0\u7b11\u9053\uff1a\u201c\u4e0d\uff01\u66f4\u50cf\u5979\u7684\u7238\u7238\uff01\u4f60\u77a7\uff0c\u5979\u7684\u5c0f \u53e3\u95ed\u5f97\u53ef\u7d27\uff0c\u957f\u5927\u4e86\u51c6\u50cf\u4ed6\u7238\u7238\u90a3\u6837\u5014\u5f3a\uff01\u201d\u5a74\u5b69\u53c8\u201c\u54d7\u201d\u7684\u4e00\u58f0\u54ed\u4e86\u8d77\u6765\uff0c\u7eb3\u5170\u6717\u6167\u7b11 \u9053\uff1a\u201c\u82e6\u547d\u7684\u5c0f\u4e2b\u5934\uff0c\u624d\u8bf4\u4f60\u53e3\u95ed\u5f97\u7d27\uff0c\u4f60\u53c8\u54ed\u8d77\u6765\u4e86\uff01\u201d\u7eb3\u5170\u660e\u6167\u5168\u795e\u8c03\u5f04\u5b69\u5b50\uff0c\u5b8c\u5168\u628a \u98de\u7ea2\u5dfe\u51b7\u843d\u4e86\u3002\u98de\u7ea2\u5dfe\u9eef\u7136\u795e\u4f24\u7ad9\u5728\u65c1\u8fb9\u3002\u4e5f\u4e0d\u77e5\u662f\u4ec0\u4e48\u6ecb\u5473\uff1f\u8fd9\u65f6\u5ffd\u7136\u8d70\u4e86\u4e0a\u6765\uff0c\u4f38\u624b\u65f6 \u7eb3\u5170\u660e\u6167\u9053\uff1a\u201c\u8ba9\u6211\u62b1\u4e00\u62b1\uff1f\u201d\u7eb3\u5170\u660e\u6167\u8fdf\u7591\u4e86\u4e00\u4f1a\uff0c\u5c06\u5b69\u5b50\u9012\u8fc7\u3002\u98de\u7ea2\u5dfe\u5c06\u5973\u5a74\u653e\u5728\u81c2\u5f2f \u4e0a\u4ed4\u7ec6\u7aef\u8be6\uff0c\u679c\u7136\u5f88\u50cf\u6768\u4e91\u806a\u3002\u4e0d\u77e5\u600e\u7684\uff0c\u5979\u5ffd\u7136\u89c9\u5f97\u5f88\u559c\u7231\u8fd9\u4e2a\u5a74\u5b69\uff0c\u5fc3\u4e2d\u7a81\u7136\u6cdb\u8d77\u4e00\u4e2a \u5ff5\u5934\uff0c\u60f3\u628a\u5979\u62b1\u8d70\u3002\u65c1\u8fb9\u7684\u4e2b\u5934\u9012\u4e0a\u534a\u6e29\u7684\u5f00\u6c34\uff0c\u4e00\u53e3\u4e00\u53e3\u7684\u5582\u5979\uff0c\u6709\u4e00\u4e2a\u5973\u5175\u7b11\u9053\uff1a\u201c\u5c0f \u59d0\uff0c\u4f60\u53ef\u8981\u5b66\u517b\u5b69\u5b69\u5b50\uff0c\u517b\u5b69\u5b50\u53ef\u4e0d\u6bd4\u821e\u5200\u5f04\u5251\uff0c\u9ebb\u70e6\u591a\u7740\u54e9\uff01\u201d\u98de\u7ea2\u5dfe\u5fae\u5fae\u4e00\u9707\uff0c\u6697\u7b11\u81ea \u5df1\u521a\u624d\u7684\u601d\u60f3\uff0c\u628a\u5b69\u5b50\u4ea4\u56de\u7eb3\u5170\u660e\u6167\uff0c\u53c8\u6478\u51fa\u4e00\u4e32\u73cd\u73e0\uff0c\u9012\u8fc7\u53bb\u9053\uff1a\u201c\u8fd9\u662f\u5357\u6d77\u6765\u7684\uff0c\u5c31\u9001 \u7ed9\u5979\u505a\u89c1\u9762\u793c\u5427\uff01\u201d\u5357\u6d77\u73cd\u73e0\u5728\u8349\u539f\u4e0a\u662f\u6781\u96be\u5f97\u7684\u4e1c\u897f\uff0c\u7eb3\u5170\u660e\u6167\u770b\u4e86\u4e00\u773c\uff1b\u5979\u4e0d\u5e0c\u7f55\u90a3\u4e32 \u73cd\u73e0\uff0c\u800c\u662f\u5e0c\u7f55\u98de\u7ea2\u5dfe\u90a3\u79cd\u611f\u60c5\u3002\u5979\u60f3\u4e0d\u5230\u5728\u6e05\u56fd\u519b\u4e2d\u6240\u4f20\u8bf4\u7684\u8349\u539f\u4e0a\u6740\u4eba\u4e0d\u7728\u773c\u7684\u9b54\u738b\uff0c \u4f1a\u6709\u8fd9\u6837\u7ec6\u817b\u7684\u611f\u60c5\u3002\u5979\u63a5\u4e86\u73cd\u73e0\uff0c\u773c\u5149\u5145\u6ee1\u8c22\u610f\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u59d0\u59d0\uff0c\u6211\u5c31\u628a\u5979\u53d6\u540d\u53eb\u505a \u5b9d\u73e0\uff0c\u8c22\u8c22\u4f60\u7684\u597d\u610f\uff01\u201d\u98de\u7ea2\u5dfe\u9762\u8272\u4e00\u6c89\uff0c\u5ffd\u7136\u53c8\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u51c6\u662f\u4f60\u7684\u59d0\u59d0\uff0c\u6211\u662f\u4f60\u7684\u654c \u4eba\uff0c\u8fc7\u4e86\u51e0\u5e74\uff0c\u6211\u8fd8\u8981\u518d\u627e\u4f60\u89c1\u4e2a\u9ad8\u4e0b\uff0c\u4f60\u597d\u597d\u7b49\u7740\u5427\uff01\u201d\u5973\u5a74\u201c\u54d7\u201d\u7684\u4e00\u58f0\u53c8\u54ed\u8d77\u6765\uff0c\u98de \u7ea2\u5dfe\u5c31\u5728\u5973\u5175\u4eec\u60ca\u5947\u7684\u6ce8\u89c6\u4e0b\u4e0e\u5b69\u5b50\u7684\u54ed\u58f0\u4e2d\u8d70\u51fa\u53bb\u4e86\uff01 \u3000\u3000\u518d\u8bf4\uff0c\u5728\u5580\u5c14\u6c81\u8349\u539f\u4e4b\u4e0a\uff0c\u6768\u4e91\u806a\u4e5f\u662f\u5174\u594b\u975e\u5e38\uff0c\u4ed6\u5e2e\u52a9\u54c8\u8428\u514b\u7684\u8001\u914b\u957f\u5c06\u695a\u662d\u5357\u6253\u5f97 \u5927\u8d25\uff0c\u628a\u9644\u8fd1\u6e05\u519b\u7684\u57ce\u5821\u4e5f\u5360\u636e\u4e86\u3002\u8fd9\u4e00\u5929\uff0c\u4ed6\u6b63\u548c\u5927\u914b\u957f\u70b9\u6570\u4fd8\u6765\u7684\u9a6c\u5339\uff0c\u5ffd\u7136\u4e00\u4e2a\u58eb\u5175 \u8d70\u6765\u62a5\u544a\uff0c\u8bf4\u662f\u6349\u5230\u4e86\u4e00\u4e2a\u964c\u751f\u4eba\uff0c\u8fd9\u4eba\u867d\u662f\u7267\u6c11\u773c\u9970\uff0c\u4f46\u95ee\u8d77\u6e38\u7267\u7684\u4e8b\u60c5\uff0c\u4ed6\u5374\u4e00\u7a8d\u4e0d \u901a\uff0c\u58eb\u5175\u4eec\u8981\u6253\u4ed6\uff0c\u4ed6\u624d\u558a\u51fa\u662f\u8981\u627e\u6768\u5927\u4fa0\u3002\u6768\u4e91\u806a\u53eb\u58eb\u5175\u63a8\u90a3\u4eba\u4e0a\u6765\uff0c\u4e00\u770b\u539f\u6765\u662f\u4e2a\u4e8c\u5341 \u591a\u5c81\u7684\u6d51\u5c0f\u5b50\u3002\u6768\u4e91\u806a\u9053\uff1a\u201c\u4f60\u662f\u4ec0\u4e48\u4eba\uff1f\u627e\u6211\u505a\u4ec0\u4e48\uff1f\u201d\u90a3\u4eba\u5468\u56f4\u671b\u4e86\u4e00\u671b\uff0c\u55eb\u55eb\u5685\u5685\u7684 \u8bf4\u9053\uff1a\u201c\u6768\u5927\u4fa0\uff0c\u6211\u662f\u7eb3\u5170\uff0c\u7eb3\u5170\u2026\u2026\u201d\u65c1\u8fb9\u7684\u58eb\u5175\u542c\u4e86\u7eb3\u5170\u4e8c\u5b57\uff0c\u5168\u90e8\u6115\u7136\uff0c\u54c8\u8428\u514b\u7684\u8001 \u9996\u957f\u5374\u4ece\u5bb9\u7b11\u9053\uff1a\u201c\u6768\u5927\u4fa0\u6709\u4e8b\uff0c\u6211\u4eec\u4e0d\u6253\u6270\u4e86\uff01\u201d\u8bf4\u7f62\u7387\u7740\u58eb\u5175\u8d70\u5f00\u3002\u6768\u4e91\u806a\u6697\u6697\u611f\u6fc0\u8001 \u914b\u957f\u5bf9\u81ea\u5df1\u4fe1\u4efb\uff0c\u518d\u559d\u95ee\u90a3\u4e2a\u4eba\u9053\uff1a\u201c\u4f60\u662f\u7eb3\u5170\u79c0\u5409\u6d3e\u6765\u7684\u5978\u7ec6\u4e48\uff1f\u201d\u90a3\u4eba\u7b54\u9053\uff1a\u201c\u4e0d\uff0c\u6211 \u662f\u7eb3\u5170\u5c0f\u59d0\u6d3e\u6765\u7684\uff0c\u7eb3\u5170\u5c0f\u59d0\u662f\u6211\u59d1\u59d1\u5976\u5927\u7684\u3002\u201d\u6768\u4e91\u806a\u201c\u54e6\u201d\u4e86\u4e00\u58f0\uff0c\u95ee\u9053\uff0c\u201c\u7eb3\u5170\u5c0f\u59d0 \u53eb\u4f60\u5e26\u8bdd\u7ed9\u6211\uff1f\u201d\u90a3\u4eba\u70b9\u4e86\u70b9\u5934\u3002\u4ece\u6000\u4e2d\u638f\u51fa\u4e00\u5f20\u7f8a\u76ae\uff0c\u4e00\u9762\u9012\u8fc7\u53bb\u4e00\u9762\u8bf4\u9053\uff1a\u201c\u5c0f\u59d0\u517b\u4e86 \u4e00\u4e2a\u6f02\u4eae\u7684\u5c0f\u599e\u54e9\uff01\u201d\u6768\u4e91\u806a\u5927\u5403\u4e00\u60ca\uff0c\u53cc\u624b\u5fae\u5fae\u53d1\u6296\uff0c\u63a5\u8fc7\u7f8a\u76ae\u4e00\u770b\uff0c\u679c\u7136\u662f\u7eb3\u5170\u660e\u6167\u4eb2 \u7b14\u5199\u6765\uff0c\u62a5\u9053\u751f\u4e86\u5973\u5b69\u7684\u4e66\u4fe1\u3002\u4fe1\u4e2d\u8fd8\u8bf4\u56e0\u4e3a\u5973\u5b69\u5b50\u5dee\u5341\u591a\u5929\u624d\u8db3\u6708\uff0c\u56e0\u6b64\u8eab\u9aa8\u7626\u5f31\uff0c\u5f88\u4e3a \u62c5\u5fc3\uff0c\u672a\u540e\u5e76\u5e0c\u671b\u6768\u4e91\u806a\u5077\u5077\u7684\u6765\u770b\u5979\u4e00\u6b21\u3002 \u3000\u3000\u8fd9\u970e\u90a3\u95f4\uff0c\u6768\u4e91\u806a\u53c8\u60ca\u53c8\u559c\uff0c\u4f46\u6e10\u6e10\u559c\u60a6\u7684\u611f\u60c5\u5927\u5927\u8d85\u8fc7\u4e86\u60ca\u60f6\u7684\u611f\u60c5\u3002\u5728\u6b64\u4e4b\u524d\uff0c\u4ed6 \u867d\u7136\u5f88\u7231\u7eb3\u5170\u660e\u6167\uff0c\u4f46\u603b\u89c9\u5f97\u90a3\u79cd\u611f\u60c5\uff0c\u5e76\u4e0d\u662f\u600e\u4e48\u5de9\u56fa\u7684\u611f\u60c5\uff0c\u800c\u4ee4\uff0c\u4ed6\u89c9\u5f97\u548c\u660e\u6167\u5df2\u662f \u771f\u6b63\u8054\u4e3a\u4e00\u4f53\u4e86\uff0c\u5bf9\u98de\u7ea2\u5dfe\u7684\u8d1f\u759a\u7684\u611f\u60c5\u4e5f\u6d88\u5931\u4e86\uff0c\u4ed6\u83ab\u540d\u5176\u5999\u7684\u7231\u90a3\u4e2a\u672a\u66fe\u89c1\u8fc7\u9762\u7684\u5b69 \u5b50\uff0c\u4ed6\u4e3a\u4ed6\u7684\u7626\u5f31\u800c\u62c5\u5fc3\uff0c\u4ed6\u5e7b\u60f3\u7740\u5979\u662f\u600e\u6837\u54ed\u558a\u53eb\u5524\u3002\u6536\u4e86\u7f8a\u76ae\u4fe1\u540e\uff0c\u4ed6\u5fc3\u91cc\u8fc5\u901f\u7684\u4f5c\u4e86 \u4e00\u4e2a\u51b3\u5b9a\uff0c\u8981\u5192\u9669\u5230\u5343\u91cc\u5916\u7684\u8349\u539f\u53bb\u770b\u81ea\u5df2\u7684\u5b69\u5b50\uff01 \u3000\u3000----------- \u3000\u3000\u98ce\u4e91\u9601 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u585e\u5916\u5947\u4fa0\u4f20\u300b\u2014\u2014\u7b2c\u4e8c\u5341\u4e94\u56de\u3000\u5929\u9f99\u5251\u9635 \u6881\u7fbd\u751f\u300a\u585e\u5916\u5947\u4fa0\u4f20\u300b \u7b2c\u4e8c\u5341\u4e94\u56de\u3000\u5929\u9f99\u5251\u9635 \u3000\u3000\u5f53\u6768\u4e91\u806a\u4ece\u5580\u5c14\u6c81\u8349\u539f\u8d76\u5411\u4f0a\u7281\u7684\u65f6\u5019\uff0c\u201c\u7eb3\u5170\u660e\u6167\u5df2\u56de\u5230\u4f0a\u7281\u57ce\u3002\u5979\u662f\u4e2a\u7ec3\u6b66\u7684\u4eba\uff0c \u8eab\u4f53\u5f88\u597d\uff0c\u751f\u4e0b\u5b69\u5b50\uff0c\u6ee1\u6708\u4e4b\u540e\uff0c\u5df2\u5982\u5e38\u4eba\u3002\u90a3\u4e9b\u5973\u5175\u90fd\u662f\u5979\u7684\u5fc3\u8179\uff0c\u5927\u5bb6\u5c06\u5b69\u5b50\u4fdd\u62a4\u5f97\u597d \u597d\u7684\uff0c\u8c01\u4e5f\u4e0d\u4f1a\u6cc4\u6f0f\u3002\u8baa\u56de\u5230\u5c06\u519b\u5e9c\uff0c\u5c31\u5c06\u5973\u5a74\u4ea4\u7ed9\u5976\u5988\uff0c\u5373\u7b97\u7ed9\u592b\u4eba\u53d1\u73b0\uff0c\u4e5f\u53ef\u63a8\u8bf4\u662f\u5976 \u5988\u6536\u517b\u7684\u5b69\u5b50\u3002 \u3000\u3000\u7eb3\u5170\u592b\u4eba\u89c1\u4e86\u5973\u513f\uff0c\u53c8\u662f\u6b22\u559c\uff0c\u53c8\u662f\u57cb\u6028\uff1a\u5979\u6402\u7740\u660e\u6167\u9053\uff1a\u201c\u5973\u513f\u5440\uff0c\u4f60\u600e\u4e48\u4e00\u53bb\u5c31\u53bb \u4e86\u534a\u5e74\u591a\uff01\u6253\u730e\u867d\u7136\u597d\u73a9\uff0c\u4e5f\u4e0d\u8be5\u53bb\u8fd9\u4e48\u4e45\u5440\uff01\u4f60\u770b\u5bb6\u91cc\u95f9\u6210\u4ec0\u4e48\u6837\u5b50\uff1f\u4f60\u7684\u7238\u7238\u53c8\u53bb\u5916\u9762 \u6253\u4ed7\uff0c\u5973\u98de\u8d3c\u4e00\u6765\uff0c\u95f9\u5f97\u4eba\u4ef0\u9a6c\u7ffb\uff0c\u90a3\u4e48\u591a\u4eba\u90fd\u64d2\u62ff\u5979\u4e0d\u4f4f\uff0c\u771f\u628a\u6211\u5413\u574f\u4e86\uff01\u8981\u662f\u4f60\u5728\u8fd9 \u513f\uff0c\u603b\u53ef\u4ee5\u7ed9\u90a3\u5973\u8d3c\u4e00\u70b9\u989c\u8272\uff01\u201d\u660e\u6167\u542c\u4e86\uff0c\u8e59\u7709\u4e0d\u8bed\uff0c\u5979\u4e0d\u6562\u544a\u8bc9\u6bcd\u4f6f\uff0c\u5973\u98de\u8d3c\u5c31\u662f\u5927\u540d \u9f0e\u9f0e\u7684\u98de\u7ea2\u5dfe\uff0c\u66f4\u4e0d\u6562\u544a\u8bc9\u6bcd\u4eb2\uff0c\u5979\u5bf9\u8fd9\u4e2a\u5973\u98de\u8d3c\u5176\u5b9e\u5374\u662f\u53c8\u6068\u53c8\u7231\uff0c\u81ea\u4ece\u98de\u7ea2\u5dfe\u5728\u5979\u533f\u5c45 \u7684\u8349\u539f\u5927\u95f9\u4e00\u573a\uff0c\u6740\u5c3d\u9a6c\u8d3c\uff0c\u8d60\u73e0\u7ed9\u5979\u7684\u5973\u5a74\u4e4b\u540e\uff0c\u5979\u5bf9\u98de\u7ea2\u5dfe\u7684\u611f\u60c5\u5df2\u6709\u4e86\u5fae\u5999\u7684\u53d8\u5316\uff0c \u5f53\u7136\u5979\u8fd8\u6068\u98de\u7ea2\u5dfe\uff0c\u6068\u98de\u7ea2\u5dfe\u5728\u6768\u4e91\u806a\u5fc3\u5934\u5360\u7740\u4e00\u89d2\uff0c\u4f46\u5979\u5df2\u7ecf\u4e0d\u628a\u98de\u7ea2\u5dfe\u5f53\u505a\u654c\u4eba\u4e86\u3002\u98de \u7ea2\u5dfe\u5728\u5979\u7684\u5fc3\u4e2d\u5df2\u7ecf\u4e0d\u662f\u4e00\u4e2a\u201c\u5973\u9b54\u5934\u201d\uff0c\u800c\u662f\u4e00\u4e2a\u9887\u6709\u4eba\u60c5\u5473\u7684\u5973\u82f1\u8c6a\u3002\u7eb3\u5170\u592b\u4eba\u89c1\u5973\u513f \u6c89\u601d\u7684\u6837\u5b50\uff0c\u8be7\u7136\u95ee\u9053\uff1a\u201c\u600e\u6837\u5566\uff0c\u5b69\u5b50\uff0c\u8fde\u4f60\u7684\u7238\u7238\u4e5f\u79f0\u8d5e\u4f60\u7684\u6b66\u529f\u884c\uff0c\u96be\u9053\u4f60\u4e5f\u5bb3\u6015\u90a3 \u4e2a\u5973\u98de\u8d3c\u3002\u201d\u7eb3\u5170\u660e\u6167\u82e6\u7b11\u9053\uff1a\u201c\u5988\u5988\uff0c\u6211\u542c\u4e86\u4e2b\u5934\u7684\u63cf\u8ff0\uff0c\u90a3\u5973\u98de\u8d3c\u7684\u6b66\u529f\u7684\u786e\u662f\u4e16\u95f4\u7f55 \u89c1\uff0c\u53ea\u6015\u5973\u513f\u771f\u7684\u4e0d\u662f\u5979\u7684\u5bf9\u624b\u3002\u201d\u7eb3\u5170\u592b\u4eba\u54c8\u54c8\u7b11\u9053\uff0c\u201c\u539f\u6765\u4f60\u5bb3\u6015\u8fd9\u4e2a\u3002\u524d\u51e0\u5929\u6211\u8fd8\u6015 \u5973\u98de\u8d3c\u4f1a\u518d\u6765\uff0c\u73b0\u5728\u5374\u4e00\u70b9\u4e5f\u4e0d\u614c\u4e86\u3002\u201d\u7eb3\u5170\u660e\u6167\u95ee\u9053\uff1a\u201c\u600e\u4e48\uff1f\u7236\u4eb2\u53c8\u8bf7\u6765\u4e86\u4ec0\u4e48\u80fd\u4eba \u4e86\uff1f\u201d\u7eb3\u5170\u592b\u4eba\u9053\uff1a\u201c\u4e0d\u662f\u4f60\u7238\u7238\u8bf7\u6765\u7684\uff0c\u662f\u7ebd\u67af\u5e90\u9080\u8bf7\u6765\u7684\u3002\u4e0d\u8fc7\u7ebd\u6854\u5e90\u65e9\u7980\u544a\u8fc7\u4f60\u7684\u7238 \u7238\uff0c\u6240\u4ee5\u4f60\u7238\u7238\u4e5f\u634e\u6709\u53e3\u4fe1\u56de\u6765\uff0c\u53eb\u90a3\u73ed\u4eba\u6682\u5728\u5c06\u519b\u5e9c\u4e2d\u5c45\u4f4f\u3002\u201d\u660e\u6167\u95ee\u9053\uff1a\u201c\u600e\u4e48\uff1f\u4e0d\u53ea \u4e00\u4e2a\u800c\u662f\u4e00\u73ed\u4e48\uff1f\u201d\u7eb3\u5170\u592b\u4eba\u9053\uff1a\u201c\u542c\u8bf4\u662f\u4ec0\u4e48\u897f\u85cf\u5929\u9f99\u6d3e\u7684\uff0c\u4e3a\u9996\u7684\u53eb\u5929\u8499\u7985\u5e08\uff0c\u4e00\u5171\u6765 \u4e86\u5341\u516b\u4e2a\u54e9\uff0c\u7ebd\u67af\u5e90\u8bf4\u5929\u9f99\u6d3e\u7684\u5251\u672f\u897f\u571f\u7b2c\u4e00\uff0c\u8bba\u5f53\u4eca\u5251\u6cd5\u7684\u5927\u5b97\u5e08\uff0c\u4ed6\u7684\u5e08\u7236\u9f50\u771f\u541b\u6700 \u9ad8\uff0c\u6666\u660e\u7985\u5e08\u7b2c\u4e8c\uff0c\u8fd9\u4e2a\u897f\u85cf\u5929\u9f99\u6d3e\u7684\u7956\u5e08\u4e5f\u53ef\u4ee5\u5750\u7b2c\u4e09\u628a\u4ea4\u6905\u54e9\uff01\u201d\u7eb3\u5170\u660e\u6167\u542c\u4e86\uff0c\u5fc3\u91cc \u6697\u6697\u597d\u7b11\u3002\u597d\u7b11\u7ebd\u67af\u5e90\u7684\u80e1\u4e71\u5439\u725b\u3002\u9f50\u771f\u541b\u7684\u5251\u672f\u5979\u6ca1\u89c1\uff0c\u4f46\u770b\u7ebd\u67af\u5e90\u90a3\u70b9\u6280\u827a\uff0c\u4ed6\u7684\u5e08\u7236 \u65e0\u8bba\u5982\u4f55\u4e0d\u4f1a\u8d85\u8fc7\u6666\u660e\u7985\u5e08\uff0c\u81f3\u4e8e\u5929\u9f99\u6d3e\u7684\u7956\u5e08\u4e43\u662f\u5929\u9f99\u4e0a\u4eba\uff0c\u5979\u542c\u6768\u4e91\u806a\u8bf4\u8fc7\uff0c\u5355\u8eab\u5165\u85cf \u548c\u5929\u9f99\u7985\u5e08\u8bba\u5251\uff0c\u6298\u670d\u5929\u9f99\u95e8\u4e0b\u7684\u6545\u4e8b\u3002\u5979\u60f3\u5929\u9f99\u7985\u5e08\u8fde\u6768\u4e91\u806a\u90fd\u6bd4\u4e0d\u4e0a\uff0c\u5982\u4f55\u80fd\u5750\u7b2c\u4e09\u628a \u4ea4\u6905\u3002\u7eb3\u5170\u592b\u4eba\u53c8\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u5929\u9f99\u6d3e\u7684\u5341\u516b\u9ad8\u624b\uff0c\u613f\u5e94\u7ebd\u67af\u5e90\u7684\u9080\u8bf7\uff0c\u636e\u8bf4\u662f\u56e0\u4e3a\u548c\u4e00\u4e2a \u53eb\u505a\u6768\u4e91\u806a\u7684\u6709\u4ec7\u3002\u6211\u542c\u4f60\u7238\u7238\u8bf4\u8fc7\uff0c\u90a3\u4e2a\u4ec0\u4e48\u6768\u4e91\u806a\u53ef\u662f\u54b1\u4eec\u6ee1\u6e05\u7684\u5927\u5bf9\u5934\u54e9\u3002\u201d\u7eb3\u5170\u660e \u6167\u9661\u7136\u4e00\u9707\uff0c\u5fc3\u60f3\uff1a\u201c\u54fc\uff0c\u539f\u6765\u4ed6\u4eec\u662f\u4e3a\u62a5\u4ec7\u6765\u7684\uff1a\u8fd9\u5929\u8499\u7985\u5e08\u4e43\u662f\u5929\u9f99\u7985\u5e08\u7684\u5e08\u5f1f\uff0c\u4ed6\u7684 \u6b66\u529f\u4e0d\u5728\u5e08\u5144\u4e4b\u4e0b\uff0c\u5927\u7ea6\u662f\u5929\u9f99\u4e0d\u597d\u610f\u601d\u51fa\u9762\uff0c\u6240\u4ee5\u53eb\u5e08\u5f1f\u51fa\u9762\u4e86\u3002\u6768\u4e91\u806a\u7684\u6b66\u529f\u867d\u7136\u4e86 \u5f97\uff0c\u5355\u6253\u72ec\u6597\uff0c\u7edd\u4e0d\u4f1a\u5931\u624b\uff0c\u53ea\u662f\u8981\u72ec\u6218\u5341\u516b\u4e2a\u9ad8\u624b\uff0c\u6050\u6015\u4e0d\u884c\u3002\u201d\u5979\u521a\u521a\u5dee\u9063\u4e86\u5976\u5988\u7684\u4f84 \u513f\uff0c\u9001\u4fe1\u7ed9\u6768\u4e91\u806a\uff0c\u8981\u4ed6\u5077\u5077\u5230\u4f0a\u7281\u6765\u770b\u671b\u81ea\u5df1\uff0c\u5982\u4eca\u542c\u4e86\u8fd9\u4e2a\u6d88\u606f\uff0c\u5374\u53c8\u6697\u6697\u76fc\u671b\u4ed6\u4e0d\u8981 \u6765\u4e86\uff01 \u3000\u3000\u53ef\u662f\u6768\u4e91\u806a\u7ec8\u4e8e\u6765\u4e86\uff0c\u5580\u5c14\u6c81\u8349\u539f\u6682\u65f6\u5e73\u9759\u65e0\u4e8b\uff0c\u4ed6\u522b\u4e86\u54c8\u8428\u514b\u7684\u8001\u914b\u957f\uff0c\u62ab\u661f\u6234\u6708\uff0c \u7ec8\u4e8e\u8d76\u6765\u4e86\u3002\u4ed6\u60f3\u5ff5\u7eb3\u5170\u660e\u6167\uff0c\u4e5f\u60f3\u5ff5\u4ed6\u4ece\u672a\u89c1\u8fc7\u9762\u7684\u5973\u513f\uff0c\u4ed6\u60f3\u8fd9\u6b21\u628a\u7eb3\u5170\u660e\u6167\u6bcd\u5973\u90fd\u5e26 \u51fa\u6765\u3002\u4ed6\u4e0d\u613f\u610f\u4ed6\u7684\u5973\u513f\u751f\u957f\u5728\u4e00\u4e2a\u6ee1\u6d32\u5c06\u519b\u7684\u5bb6\u91cc\u3002 \u3000\u3000\u8fd9\u665a\uff0c\u53c8\u662f\u4e00\u4e2a\u6708\u9ed1\u98ce\u9ad8\u7684\u665a\u4e0a\uff0c\u4ed6\u4ed7\u7740\u7edd\u9876\u8f7b\u529f\uff0c\u5077\u5077\u8fdb\u4e86\u4f0a\u7281\u57ce\uff0c\u6478\u5165\u4e86\u5c06\u519b\u5e9c \u5185\u3002\u5728\u98de\u8eab\u8fdb\u53bb\u7684\u65f6\u5019\uff0c\u66fe\u53d1\u73b0\u5c4b\u9876\u4e0a\u6709\u5f71\u7ef0\u7ef0\u7684\u4eba\u5f71\uff0c\u4f46\u4ed6\u81ea\u6043\u827a\u9ad8\u80c6\u5927\uff0c\u75be\u5982\u98de\u9e1f\uff0c\u5fc3 \u60f3\u90a3\u4e9b\u5e73\u5eb8\u7684\u6b66\u5e08\uff0c\u5c31\u662f\u81ea\u5df1\u4ece\u4ed6\u4eec\u8eab\u540e\u63a0\u8fc7\uff0c\u4ed6\u4eec\u4e5f\u672a\u5fc5\u53d1\u73b0\uff1b\u56e0\u6b64\u6beb\u4e0d\u5728\u610f\uff0c\u5faa\u7740\u719f\u8def \u8fdb\u5165\u4e86\u5976\u5988\u7684\u5c4b\u4e2d\u3002 \u3000\u3000\u7eb3\u5170\u660e\u6167\u8fd9\u65f6\u6b63\u548c\u5976\u5988\u95f2\u8bdd\uff0c\u84e6\u542c\u5f97\u7a97\u5916\u6709\u4eba\u8f7b\u6572\uff0c\u8df3\u4e86\u8d77\u6765\uff0c\u4e00\u770b\u7adf\u662f\u5979\u65e5\u601d\u591c\u60f3\u7684 \u5fc3\u4e0a\u4eba\uff0c\u4e0d\u89c9\u60ca\u559c\u4ea4\u5e76\uff0c\u4e24\u4eba\u7d27\u7d27\u76f8\u62e5\uff0c\u5976\u5988\u5728\u65c1\u8fb9\u6697\u6697\u6d41\u6cea\u3002 \u3000\u3000\u7eb3\u5170\u660e\u6167\u7d27\u7d27\u7684\u62b1\u4e86\u6768\u4e91\u806a\u4e00\u9635\uff0c\u500f\u53c8\u5c06\u4ed6\u63a8\u5f00\uff0c\u53eb\u9053\uff1a\u201c\u597d\uff0c\u4f60\u7ec8\u4e8e\u6765\u4e86\u73b0\u5728\u54b1\u4eec\u603b \u7b97\u89c1\u7740\u4e86\uff0c\u4f60\u5feb\u8d70\uff01\u201d\u6768\u4e91\u806a\u6124\u7136\u9053\uff1a\u201c\u4f60\u5343\u91cc\u5c06\u6211\u53ec\u6765\uff0c\u4e00\u89c1\u53c8\u8981\u8d76\u6211\u8d70\uff0c\u4f60\u8fd9\u662f\u4ec0\u4e48\u610f \u601d\uff1f\u201d\u7eb3\u5170\u660e\u6167\u987f\u8db3\u9053\uff1a\u201c\u4f60\u542c\u6211\u8bdd\uff0c\u5feb\u8d70\uff01\u5feb\u8d70\uff01\u8fd9\u91cc\u6709\u4eba\u7b49\u7740\u6349\u4f60\uff01\u201d\u6768\u4e91\u806a\u72c2\u7b11\u9053\uff1a \u201c\u4ec0\u4e48\u4eba\u80fd\u591f\u6349\u6211\uff1f\u201d\u7eb3\u5170\u660e\u6167\u65e0\u6687\u591a\u8bf4\uff0c\u53ea\u662f\u8fde\u58f0\u50ac\u4ed6\u9053\uff1a\u201c\u4ee5\u540e\u54b1\u4eec\u8fd8\u53ef\u89c1\u9762\uff0c\u4f60\u4e0d\u8981 \u518d\u5728\u8fd9\u91cc\u9017\u7559\u4e86\uff01\u201d\u6768\u4e91\u806a\u987f\u7136\u7591\u5fc3\u5927\u8d77\uff0c\u4ed6\u6000\u7740\u4e00\u80a1\u70ed\u60c5\u5230\u6765\uff0c\u4e0d\u60f3\u5374\u5982\u78b0\u7740\u4e00\u76c6\u51b7\u6c34\uff0c \u8fce\u5934\u6dcb\u4e0b\uff01\u4ed6\u6000\u7591\u7eb3\u5170\u660e\u6167\u820d\u4e0d\u5f97\u5bcc\u8d35\u8363\u534e\uff0c\u4e0d\u613f\u8ddf\u4ed6\u5728\u6c5f\u6e56\u98d8\u6cca\uff0c\u6240\u4ee5\u8fde\u58f0\u50ac\u4ed6\u51fa\u8d70\u3002\u4ed6 \u60f3\uff1a\u6211\u548c\u5979\u7684\u7236\u4eb2\u539f\u662f\u654c\u4eba\uff0c\u6211\u7684\u8ba1\u5212\u770b\u6765\u53ea\u662f\u5b69\u5b50\u7684\u5e7b\u60f3\u4e86\u3002\u7a81\u7136\uff0c\u4ed6\u677f\u7740\u8138\u5b54\u5bf9\u7eb3\u5170\u660e \u6167\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u7684\u5973\u513f\u5462\uff1f\u6211\u603b\u5f97\u89c1\u89c1\u5973\u513f\u624d\u80fd\u79bb\u5f00\u3002\u201d\u5976\u5988\u65e9\u8fdb\u5165\u5185\u5ba4\uff0c\u8fd9\u65f6\u6b63\u62b1\u7740\u5a74\u513f \u51fa\u6765\uff0c\u6768\u4e91\u806a\u8d76\u4e0a\u53bb\u4e00\u770b\uff0c\u53ea\u89c1\u5a74\u513f\u7761\u5f97\u6b63\u751c\uff0c\u7626\u524a\u6e05\u79c0\u7684\u9762\u5e9e\uff0c\u5341\u8db3\u662f\u4e2a\u5c0f\u7eb3\u5170\u660e\u6167\uff0c\u6768 \u4e91\u806a\u800c\u4e0b\u4e86\u5934\uff0c\u8f7b\u8f7b\u5728\u5973\u513f\u9762\u4e0a\u4eb2\u4e86\u4e00\u4e0b\uff0c\u7eb3\u5170\u660e\u6167\u53c8\u5728\u540e\u9762\u5401\u6c14\u8bf4\u9053\uff0c\u201c\u4f60\u5feb\u8d70\u5427\u3002\u201d\u6768 \u4e91\u806a\u5fc3\u5934\u706b\u6012\uff0c\u60f3\u628a\u5a74\u5b69\u593a\u4e86\u51fa\u8d70\uff0c\u4f46\u4e00\u60f3\u5979\u8fd8\u4e0d\u8fc7\u4e00\u4e2a\u6708\u5927\uff0c\u5c1a\u672a\u65ad\u5976\uff0c\u81ea\u5df1\u5982\u4f55\u80fd\u591f\u5e26 \u5979\uff1f\u6b63\u5728\u6b64\u65f6\u3002\u5ffd\u7136\u74e6\u9762\u6709\u8f7b\u5fae\u7684\u58f0\u97f3\uff0c\u6768\u4e91\u806a\u4e00\u542c\u5c31\u77e5\u662f\u6709\u6b66\u6797\u9ad8\u624b\u6765\u5230\u3002\u4ed6\u8f6c\u8fc7\u8eab\u8eaf\uff0c \u5bf9\u7eb3\u5170\u5c0f\u59d0\u4e00\u7a3d\u9996\uff0c\u53cd\u8eab\u8dc3\u51fa\u7a97\u5916\uff0c\u968f\u624b\u4f7f\u4e86\u4e00\u62db\u201c\u8fc7\u7a97\u671b\u6708\u201d\uff0c\u53ea\u542c\u5f97\u201c\u54ce\u54df\u201d\u8fde\u58f0\uff0c\u4e24 \u4e2a\u6697\u88ad\u7684\u4eba\uff0c\u5df2\u7ed9\u6768\u4e91\u806a\u8fd0\u638c\u529b\u5f39\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u6768\u4e91\u806a\u8dc3\u4e0a\u5c4b\u9876\uff0c\u53ea\u89c1\u74e6\u9762\u4e0a\u9ad8\u9ad8\u77ee\u77ee\uff0c\u7ad9\u6ee1\u4e86\u4eba\u3002\u4e2a\u4e2a\u624b\u4e0a\u90fd\u6709\u4e00\u628a\u660e\u6643\u6643\u7684\u5229\u5251\uff0c \u6768\u4e91\u806a\u8ba4\u5f97\u4e3a\u9996\u7684\u662f\u5929\u8499\u7985\u5e08\uff0c\u51b7\u51b7\u53d1\u8bdd\u8bf4\u9053\uff1a\u201c\u6211\u4e0e\u4f60\u4eec\u5929\u9f99\u6d3e\u65e7\u65e5\u65e0\u51a4\uff0c\u8fd1\u65e5\u65e0\u4ec7\uff0c\u4f60 \u4eec\u4e3a\u4ec0\u4e48\u524d\u6765\u6697\u7b97\uff1f\u201d\u5929\u8499\u6012\u9053\uff1a\u201c\u6768\u4e91\u806a\uff0c\u4f60\u5927\u8a00\u6b3a\u4e16\uff0c\u627e\u4e0a\u95e8\u6765\uff0c\u595a\u843d\u6211\u4eec\uff0c\u628a\u5929\u9f99\u5251 \u6cd5\u770b\u5f97\u4e00\u94b1\u4e0d\u503c\u3002\u8fd8\u8bf4\u65e0\u51a4\u65e0\u4ec7\uff1f\u201d\u6768\u4e91\u806a\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u5c45\u7136\u8fd8\u662f\u5b66\u6b66\u7684\u4eba\uff0c\u5fc3\u80f8\u5982\u6b64 \u72ed\u7a84\uff01\u5404\u5bb6\u5251\u6cd5\uff0c\u5404\u6709\u957f\u77ed\uff0c\u6211\u597d\u610f\u4e0e\u4f60\u4eec\u7684\u7956\u5e08\u8bba\u5251\uff0c\u4f55\u66fe\u595a\u843d\u4f60\u4eec\uff1f\u201d\u5929\u8499\u9053\uff1a\u201c\u4f60\u540e \u751f\u5c0f\u8f88\uff0c\u5984\u8bae\u7956\u5e08\uff0c\u8fd9\u5c31\u662f\u4e2a\u5927\u5927\u7684\u7f6a\u72b6\u3002\u4f60\u5728\u65b0\u7586\u4f5c\u4e71\uff0c\u5578\u805a\u7267\u6c11\uff0c\u53cd\u6297\u671d\u5ef7\uff0c\u8fd9\u66f4\u662f\u4e2a \u5929\u5927\u7684\u7f6a\u72b6\uff01\u201d\u6768\u4e91\u806a\u52c3\u7136\u53d8\u8272\uff0c\u53f1\u9053\uff1a\u201c\u6211\u8fd8\u9053\u4f60\u4eec\u53ea\u662f\u5b97\u6d3e\u4e4b\u4e89\uff0c\u539f\u6765\u4f60\u4eec\u8fd8\u8981\u52a9\u7ea3\u4e3a \u8650\uff01\u201d\u94ee\u7136\u4e00\u58f0\uff0c\u65ad\u7389\u5251\u500f\u5730\u51fa\u624b\uff0c\u5929\u8499\u7985\u5e08\u628a\u624b\u4e00\u62db\uff0c\u5341\u516b\u4e2a\u4eba\u5728\u5bbd\u9614\u7684\u74e6\u9762\u4e0a\uff0c\u7adf\u6392\u6210 \u4e86\u6574\u9f50\u7684\u9635\u52bf\u3002\u5927\u5bb6\u90fd\u662f\u4e00\u8eab\u8f7b\u529f\uff0c\u8e0f\u74e6\u65e0\u58f0\u3002\u5929\u8499\u53eb\u9053\uff1a\u201c\u6768\u4e91\u806a\uff0c\u4f60\u82e5\u8fc7\u5f97\u5929\u9f99\u5251\u9635\uff0c \u6211\u5c31\u9976\u4f60\u4e00\u547d\uff01\u6768\u4e91\u806a\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u77a7\u7740\u5427\uff01\u201d\u5929\u8499\u5f80\u524d\u4e00\u51b2\uff0c\u6768\u4e91\u806a\u4e00\u5251\u524a\u53bb\uff0c\u53cc\u5251\u76f8 \u4ea4\uff0c\u4e00\u9635\u560e\u91d1\u66f3\u7389\u4e4b\u58f0\uff0c\u4e24\u65b9\u90fd\u65e0\u4f24\u635f\u3002\u6768\u4e91\u806a\u6697\u9053\uff1a\u201c\u539f\u6765\u662f\u4e00\u628a\u5b9d\u5251\uff01\u201d\u4f8d\u518d\u8fdb\u62db\u4e4b \u65f6\uff0c\u5929\u8499\u5df2\u81ea\u8eab\u65c1\u63a0\u8fc7\uff0c\u53e6\u5916\u4e24\u4e2a\u5587\u561b\u50e7\u4ece\u4e24\u7ffc\u88ad\u6765\uff0c\u6768\u4e91\u806a\u4e00\u62db\u201c\u9f99\u95e8\u63a8\u6d6a\u201d\u5de6\u53f3\u5f00\u5f13\uff0c \u4e24\u4eba\u5374\u90fd\u662f\u865a\u523a\u4e00\u5251\uff0c\u4e00\u63a0\u5373\u8fc7\uff0c\u970e\u90a3\u95f4\uff0c\u9635\u52bf\u53d1\u52a8\uff0c\u5341\u516b\u540d\u5929\u9f99\u6d3e\u7684\u9ad8\u624b\uff0c\u6e90\u6e90\u800c\u4e0a\uff0c\u6b64 \u53bb\u5f7c\u6765\uff0c\u5404\u6309\u7740\u4e00\u5b9a\u7684\u65b9\u4f4d\uff0c\u914d\u5408\u5f97\u975e\u5e38\u4e4b\u597d\uff0c\u56db\u9762\u516b\u65b9\u90fd\u662f\u5929\u9f99\u5251\u6d3e\u7684\u4eba\uff0c\u5c06\u6768\u4e91\u806a\u56f4\u5f97 \u5bc6\u4e0d\u901a\u98ce\u3002\u6768\u4e91\u806a\u6697\u6697\u70b9\u5934\u9053\uff1a\u201c\u5929\u9f99\u5251\u9635\u4e5f\u8fd8\u6709\u70b9\u9053\u7406\uff01\u201d\u4ed6\u672c\u610f\u53ea\u5b88\u4e0d\u653b\uff0c\u770b\u770b\u4ed6\u4eec\u7684 \u4f0e\u4fe9\uff0c\u90a3\u6599\u5929\u8499\u7985\u5e08\u957f\u5251\u4e00\u6307\uff0c\u50ac\u7d27\u653b\u52bf\uff0c\u5341\u516b\u540d\u9ad8\u624b\uff0c\u7ed5\u7740\u5c4b\u9762\u5de6\u7a7f\u53f3\u63d2\uff0c\u5341\u4e5d\u53e3\u5229\u5251 \uff08\u5176\u4e2d\u6709\u4e00\u4eba\u540d\u5929\u534e\u548c\u5c1a\uff0c\u4e43\u5929\u8499\u7684\u5e08\u5f1f\uff0c\u5de6\u624b\u957f\u5251\u53f3\u624b\u77ed\u5251\uff09\u7adf\u5982\u72c2\u98ce\u66b4\u96e8\uff0c\u6742\u4e71\u65e0\u7ae0\u7684 \u5411\u6768\u4e91\u806a\u51fb\u6765\uff0c\u4f46\u770b\u4f3c\u6742\u4e71\u65e0\u7ae0\uff0c\u5176\u5b9e\u5374\u662f\u6309\u7740\u516b\u5366\u65b9\u4f4d\uff0c\u5947\u6b63\u76f8\u751f\uff0c\u6b64\u547c\u5f7c\u5e94\u3002\u5251\u5251\u90fd\u662f \u76f4\u6307\u8981\u5bb3\uff0c\u6768\u4e91\u806a\u52c3\u7136\u5927\u6012\uff0c\u5929\u5c71\u5251\u6cd5\u9aa4\u7684\u5c55\u5f00\uff0c\u6025\u5982\u63a3\u7535\uff0c\u5251\u82b1\u9519\u843d\uff0c\u5b9b\u5982\u6d12\u4e0b\u4e86\u6ee1\u5929\u5bd2 \u661f\uff01\u597d\u51e0\u540d\u5587\u561b\uff0c\u53d7\u4e86\u5251\u4f24\uff0c\u5931\u58f0\u547c\u75db\u3002\u6768\u4e91\u806a\u5fc3\u60f3\uff1a\u81ea\u5df1\u4e0e\u5929\u9f99\u6709\u8fc7\u4e00\u9762\u4e4b\u7f18\uff0c\u8fd9\u4e9b\u4eba\u4e5f \u8fd8\u662f\u521a\u88ab\u671d\u5ef7\u62db\u63fd\uff0c\u8fd8\u662f\u4e0d\u8981\u4f24\u4ed6\u4eec\u7684\u6027\u547d\u3002\u53cd\u6b63\u5929\u9f99\u5251\u9635\uff0c\u81ea\u5df1\u4e5f\u5df2\u6478\u719f\u3002\u4e3b\u610f\u6253\u5b9a\uff0c\u5b9d \u5251\u5f52\u9798\uff0c\u8eab\u6cd5\u4e00\u53d8\u3002\u610f\u7528\u7a7a\u624b\u5165\u767d\u5203\u7684\u529f\u592b\uff0c\u5728\u5929\u9f99\u5251\u9635\u4e2d\u7a7f\u63d2\u81ea\u5982\uff0c\u5b9b\u5982\u4e00\u6761\u6c34\u86c7\uff0c\u56db\u5904 \u6e38\u8d70\u3002\u90a3\u4e9b\u5587\u561b\uff0c\u4e00\u4e2a\u4e2a\u7684\u89c9\u5f97\u624b\u8155\u9ebb\u75db\uff0c\u7ade\u76f8\u60ca\u547c\uff0c\u6768\u4e91\u806a\u8fde\u6218\u5341\u516b\u540d\u9ad8\u624b\uff0c\u6bcf\u4eba\u90fd\u4e0d\u8fc7 \u4e00\u62db\u534a\u5f0f\uff0c\u5c31\u5c06\u4ed6\u7684\u5229\u5251\u593a\u53bb\uff0c\u63b7\u5728\u5730\u4e0a\uff0c\u7247\u5230\u4e4b\u95f4\uff0c\u5730\u4e0b\u6563\u4e86\u6ee1\u5730\u5229\u5251\u3002\u5176\u4e2d\u53ea\u6709\u5929\u8499\u7985 \u5e08\u6321\u4e86\u4e09\u62db\uff0c\u4e5f\u7ec8\u4e8e\u88ab\u6768\u4e91\u806a\u593a\u53bb\u624b\u4e2d\u7684\u5b9d\u5251\uff01 \u3000\u3000----------- \u3000\u3000\u98ce\u4e91\u9601 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u585e\u5916\u5947\u4fa0\u4f20\u300b\u2014\u2014\u7b2c\u4e8c\u5341\u516d\u56de\u3000\u72ec\u81c2\u4e10\u4fa0 \u6881\u7fbd\u751f\u300a\u585e\u5916\u5947\u4fa0\u4f20\u300b \u7b2c\u4e8c\u5341\u516d\u56de\u3000\u72ec\u81c2\u4e10\u4fa0 \u3000\u3000\u5c06\u519b\u5e9c\u7684\u536b\u5175\u5728\u5730\u4e0b\u770b\u4e0a\uff0c\u53ea\u89c1\u65e0\u6570\u9ed1\u5f71\uff0c\u4e00\u7247\u5251\u5149\uff0c\u5728\u5c4b\u810a\u4e0a\u7eb5\u6a2a\u98de\u821e\uff0c\u4e71\u4f5c\u4e00\u56e2\uff0c \u5176\u4e2d\u5374\u6709\u4e00\u9053\u767d\u7ec3\u4f3c\u7684\u767d\u5149\uff0c\u95ea\u7535\u4f3c\u7684\u5728\u65e0\u6570\u9ed1\u5f71\u4e2d\u7a7f\u6765\u63d2\u53bb\uff0c\u767d\u5149\u6240\u5230\uff0c\u9ed1\u5f71\u5982\u6ce2\u5206\u88c2\uff0c \u56db\u9762\u4e71\u7a9c\uff0c\u970e\u65f6\u95f4\u5c4b\u4e0a\u7684\u9ed1\u5f71\u88ab\u767d\u5149\u626b\u5f97\u4e00\u4e2a\u4e0d\u5269\uff0c\u4f3c\u65e0\u6570\u9ed1\u5f71\uff0c\u5316\u6210\u4e86\u4e00\u6e9c\u4e00\u6e9c\u9ed1\u70df\uff0c\u5411 \u5c4b\u89d2\u6eda\u6eda\u6563\u53bb\uff01\u536b\u5175\u4eec\u54ea\u91cc\u89c1\u8fc7\u5982\u6b64\u9635\u4f4d\uff0c\u5413\u5f97\u76ee\u5b9a\u53e3\u5446\uff0c\u624b\u8db3\u9178\u6b3e\uff0c\u5200\u65a7\u624b\u5200\u843d\u5c18\u57c3\uff0c\u5f13 \u7bad\u624b\u5f13\u5782\u5730\u4e0b\u3002\u518d\u770b\u65f6\uff0c\u90a3\u767d\u5149\u500f\u7684\u51dd\u6b62\u4e0d\u52a8\uff0c\u73b0\u51fa\u4e00\u4e2a\u82f1\u6c14\u8feb\u4eba\u7684\u5c11\u5e74\uff0c\u5927\u58f0\u559d\u9053\uff1b\u201c\u5929 \u9f99\u6d3e\u7684\u670b\u53cb\u4eec\uff0c\u8fd9\u56de\u53c8\u5c06\u4f60\u4eec\u7684\u5175\u5203\u7559\u4e0b\uff0c\u4e0b\u6b21\u518d\u89c1\uff0c\u4ffa\u5c31\u4e0d\u5ba2\u6c14\u4e86\uff01\u201d\u8fd9\u5c11\u5e74\u6b63\u662f\u6768\u4e91 \u806a\uff0c\u5730\u7a7f\u4e86\u4e00\u8eab\u767d\u8863\uff0c\u65bd\u5c55\u4e0a\u4e58\u7684\u7a7a\u624b\u5165\u767d\u5203\u529f\u592b\uff0c\u628a\u5929\u9f99\u6d3e\u5341\u516b\u540d\u9ad8\u624b\u7684\u5175\u5203\u5168\u90fd\u593a\u4e86\u3002 \u3000\u3000\u6768\u4e91\u806a\u65cb\u8eab\u8fc7\u6765\uff0c\u628a\u5929\u8499\u7985\u5e08\u90a3\u53e3\u5b9d\u5251\u6302\u5728\u8170\u95f4\uff0c\u864e\u543c\u4e00\u58f0\uff0c\u4e00\u8dc3\u800c\u4e0b\uff0c\u536b\u58eb\u4eec\u7eb7\u7eb7\u9003 \u907f\uff0c\u6768\u4e91\u806a\u4e5f\u4e0d\u4f24\u5bb3\u4ed6\u4eec\uff0c\u5411\u5c06\u519b\u5e9c\u518d\u95ef\uff0c\u4ed6\u8fd8\u60f3\u518d\u89c1\u4e00\u89c1\u7eb3\u5170\u660e\u6167\uff0c\u95ee\u4e2a\u660e\u767d\u3001\u8fd9\u65f6\u7ebd\u67af \u5e90\u5df2\u7387\u4e86\u4e00\u73ed\u5f13\u7bad\u624b\u4ece\u5185\u5e9c\u8d70\u51fa\uff0c\u89c1\u6768\u4e91\u806a\u7adf\u7136\u95ef\u8fc7\u5929\u9f99\u5251\u9635\u548c\u5916\u9762\u536b\u58eb\u7684\u91cd\u56f4\uff0c\u5927\u5403\u4e00 \u60ca\uff0c\u6025\u5fd9\u4e0b\u4ee4\u653e\u7bad\u3002\u6768\u4e91\u806a\u65e0\u6687\u7ea0\u7f20\uff0c\u8eab\u5f62\u8d77\u5904\uff0c\u5982\u5de8\u9e70\u659c\u98de\uff0c\u95ea\u5f00\u6b63\u9762\uff0c\u98de\u8eab\u8d8a\u8fc7\u51e0\u95f4\u5c4b \u810a\uff0c\u6251\u5165\u4e86\u540e\u82b1\u56ed\u3001\u5230\u4e86\u5976\u5988\u7684\u5c4b\u4e2d\uff0c\u7834\u7a97\u800c\u5165\uff0c\u56db\u5904\u5f20\u671b\uff0c\u7eb3\u5170\u5c0f\u59d0\u8e2a\u8ff9\u4e0d\u89c1\uff0c\u8fde\u5976\u5988\u4e5f \u4e0d\u77e5\u8eb2\u5230\u54ea\u91cc\u53bb\u4e86\uff0c\u6768\u4e91\u806a\u61ca\u607c\u5f02\u5e38\uff0c\u4ed6\u4ee5\u524d\u548c\u7eb3\u5170\u5c0f\u59d0\u76f8\u4f1a\uff0c\u603b\u662f\u501f\u5976\u5988\u7684\u5c4b\u5b50\uff0c\u7eb3\u5170\u5c0f \u59d0\u7684\u95fa\u623f\uff0c\u4ed6\u5374\u4ece\u672a\u5230\u8fc7\u3002\u5fc3\u60f3\uff1a\u504c\u5927\u4e00\u4e2a\u5c06\u519b\u5e9c\uff0c\u600e\u77e5\u5979\u4f4f\u5728\u54ea\u91cc\u3002\u53c8\u8f6c\u5ff5\u9053\uff1b\u201c\u5979\u8fd9\u6837 \u8eb2\u6211\uff0c\u663e\u89c1\u662f\u6069\u65ad\u4e49\u7edd\uff0c\u4e0d\u613f\u518d\u8ddf\u6211\u4e86\u3002\u201d\u53c8\u6c14\u53c8\u607c\uff0c\u53cd\u8eab\u518d\u8dc3\u51fa\u5c4b\u5b50\uff0c\u6b63\u81ea\u51b3\u4e0d\u5b9a\u8981\u4e0d\u8981 \u518d\u627e\uff0e\u5ffd\u7136\u6811\u836b\u4e0b\u8f6c\u51fa\u4e00\u4e2a\u4eba\u6765\uff0c\u4f4e\u58f0\u558a\u9053\uff1a\u201c\u662f\u6768\u5927\u4fa0\u5417\uff1f\u201d\u6768\u4e91\u806a\u4e00\u8dc3\u800c\u524d\uff0c\u63ea\u7740\u8fd9\u4eba \u4e00\u770b\u3002\u89c1\u4ed6\u56f4\u7740\u767d\u5dfe\uff0c\u7adf\u662f\u53a8\u5b50\u88c5\u675f\uff0c\u6025\u5fd9\u95ee\u9053\uff1b\u201c\u4f60\u662f\u8c01\uff1f\u201d\u90a3\u4eba\u4f4e\u58f0\u7b54\u9053\uff1b\u201c\u5c0f\u7684\u662f\u8fd9 \u5e9c\u4e2d\u7684\u53a8\u5b50\uff0c\u6211\u662f\u54c8\u8428\u514b\u4eba\uff0c\u4f60\u7684\u597d\u670b\u53cb\u4f0a\u58eb\u8fbe\u4eca\u6668\u8d77\u89e3\uff0c\u542c\u8bf4\u662f\u62e5\u5411\u5173\u5185\uff0c\u4f60\u7528\u5feb\u9a6c\u53bb \u8ffd\uff0c\u4e5f\u8bb8\u8fd8\u8fde\u5f97\u4e0a\uff01\u201d \u3000\u3000\u4f0a\u58eb\u8fbe\u5c31\u662f\u5728\u90a3\u6b21\u8349\u539f\u5927\u6218\u4e2d\u3001\u88ab\u6e05\u519b\u4fd8\u864f\u53bb\u7684\uff0c\u8fd9\u53a8\u5b50\u7ed9\u4ed6\u9001\u996d\uff0c\u4ea4\u6210\u670b\u53cb\u3002\u56e0\u6b64\u77e5 \u9053\u6768\u4e91\u806a\u662f\u4ed6\u670b\u53cb\u3002\u9002\u624d\u6768\u4e91\u806a\u5728\u5916\u9762\u5927\u95f9\u5c06\u519b\u5e9c\uff0c\u4e2a\u4e2a\u60ca\u60f6\uff0c\u4eba\u4eba\u85cf\u533f\u3001\u4ed6\u542c\u4eba\u8bf4\u6765\u95f9\u7684 \u662f\u6768\u4e91\u806a\uff0c\u4e0d\u987e\u5371\u9669\uff0c\u5077\u5077\u8d70\u51fa\u3001\u679c\u7136\u5e94\u4e2a\u6b63\u7740\u3002 \u3000\u3000\u6768\u4e91\u806a\u76ee\u95ea\u7cbe\u5149\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u8fd9\u8bdd\u53ef\u771f\uff1f\u201d\u90a3\u53a8\u5b50\u9053\uff1a\u201c\u5c0f\u7684\u5c82\u6562\u9a97\u4f60\uff1f\u201d\u5728\u56f4\u5dfe\u4e0b\u6478 \u51fa\u4e00\u5757\u4f69\u738b\u3002\u4e43\u662f\u4f0a\u58eb\u8fbe\u9001\u7ed9\u4ed6\u7684\uff0c\u6768\u4e91\u806a\u4e00\u770b\uff0c\u70b9\u4e86\u70b9\u5934\uff0c\u9053\u58f0\uff1b\u201c\u591a\u8c22\uff01\u201d\u8dd1\u51fa\u5c06\u519b\u5e9c \u5916\uff0c\u593a\u4e86\u4e00\u9a91\u5feb\u9a6c\uff0c\u5982\u98de\u8ffd\u53bb\u3001\u4ed6\u548c\u4f0a\u58eb\u8fbe\u7684\u4ea4\u60c5\u3002\u80dc\u4e8e\u9aa8\u8089\uff0c\u7eb3\u5170\u660e\u6167\u65e2\u907f\u800c\u4e0d\u89c1\uff0c\u4ed6\u81ea \u7136\u4e0d\u613f\u518d\u7559\u5728\u5c06\u519b\u5e9c\u4e86\u3002 \u3000\u3000\u94c1\u8e44\u8ffd\u98ce\uff0c\u5feb\u9a6c\u8e0f\u6708\u3002\u6768\u4e91\u806a\u79cd\u601d\u60d8\u60d8\uff0c\u4e0d\u77e5\u6b47\u606f\uff0c\u997f\u4e86\u5c31\u5403\u5e72\u7cae\uff0c\u7247\u523b\u4e0d\u505c\u3002\u8ffd\u4e86\u4e00 \u65e5\u4e00\u591c\u3002\u7b2c\u4e8c\u5929\u9ec4\u660f\u65f6\u5206\u3002\u5728\u8349\u539f\u4e0a\u679c\u7136\u9065\u89c1\u5341\u51e0\u9a91\u9a6c\uff0c\u62e5\u7740\u56da\u8f66\u3002\u518d\u8ffd\u4e86\u4e00\u56de\u3002\u90a3\u7fa4\u4eba\u5df2 \u5c06\u5165\u4e00\u4e2a\u5c71\u9e93\uff0c\u8fd9\u5ea7\u5c71\u4e43\u662f\u6a2a\u4e98\u8349\u539f\u7684\u5929\u5c71\u5c71\u8109\u7684\u5206\u652f\uff0c\u5e76\u4e0d\u600e\u6837\u9ad8\u5761\u3002\u6240\u4ee5\u9a7f\u9053\u80fd\u7a7f\u8fc7\u5c71 \u8c37\u3002\u8fd9\u5339\u5feb\u9a6c\u8dd1\u4e86\u4e00\u65e5\u4e00\u591c\uff0c\u76f4\u5598\u7740\u6c14\u3002\u5bb0\u5b83\u662f\u65b0\u7586\u7684\u540d\u9a6c\uff0c\u60ef\u8d70\u957f\u8def\uff0c\u5982\u6362\u662f\u5173\u5185\u7684\u9a6c\uff0c \u65e9\u5012\u4e0b\u6765\u4e86\u3001\u6768\u4e91\u806a\u5acc\u9a6c\u8d70\u5f97\u6162\uff0c\u7ffb\u4e0b\u9a6c\u80cc\uff0c\u4e00\u6e9c\u98ce\u7684\u76f4\u8ffd\u4e0a\u53bb\uff0c\u5230\u4e86\u8c37\u53e3\uff0c\u5ffd\u542c\u5f97\u91cc\u9762\u4e00 \u9635\u91d1\u94c1\u4ea4\u9e23\u4e4b\u58f0\uff0c\u6709\u4eba\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u7f57\u94c1\u7ba1\uff0c\u5e78\u4f1a\u5e78\u4f1a\uff0c\u4eca\u513f\u54b1\u5144\u5f1f\u53ef\u4e0e\u4f60\u89c1\u4e2a\u771f\u7ae0\u4e86\u3002\u201d \u6768\u4e91\u806a\u5947\u9053\uff1a\u201c\u7f57\u94c1\u7ba1\u600e\u4e48\u4f1a\u5728\u8fd9\u513f\u78b0\u7740\u4ec7\u5bb6\uff1f\u96be\u9053\u62bc\u89e3\u56da\u8f66\u7684\u4eba\u548c\u5730\u6709\u4e2a\u8fc7\u8282\uff1f\u201d\uff08\u201c\u8fc7 \u8282\u201d\u5373\u51a4\u4ec7\u4e4b\u610f\u3002\uff09\u56da\u8f66\u5df2\u9a76\u5165\u8c37\u4e2d\uff0c\u4ed6\u60f3\uff1a\u201c\u8f66\u4e2d\u82e5\u6709\u4f0a\u58eb\u8fbe\u5728\uff0c\u81ea\u5df1\u603b\u80fd\u628a\u4ed6\u6551\u51fa\uff0c\u4e14 \u5148\u770b\u770b\u518d\u8bf4\u3002\u201d\u4e00\u8dc3\u8eab\uff0c\u8df3\u4e0a\u4e86\u4e00\u5757\u5ca9\u77f3\u501f\u8349\u9690\u8eab\uff1b\u767b\u9ad8\u4e0b\u671b\uff0c\u53ea\u89c1\u8c37\u4e2d\u8fdc\u8fdc\u7acb\u7740\u4e00\u4e2a\u5947\u4e94 \u7684\u72ec\u81c2\u8001\u4e10\uff0c\u9762\u5982\u74dc\u76ae\uff0c\u53d1\u4f3c\u67af\u8349\uff0c\u9f3b\u5b54\u64a9\u5929\uff0c\u5de6\u81c2\u81ea\u80a9\u4ee5\u5916\u3001\u622a\u5982\u5200\u524a\uff0c\u6709\u7ba1\u4f38\u5c71\u9e1f\u74dc\u822c \u7684\u7626\u6307\uff0c\u63e1\u7740\u4e00\u6839\u53eb\u5316\u68d2\uff0c\u6b63\u662f\u7f57\u94c1\u7ba1\u90a3\u602a\u6a21\u6837\u3002 \u3000\u3000\u7f57\u94c1\u81c2\u662f\u585e\u5916\u7684\u6e38\u4fa0\uff0c\u548c\u5353\u4e00\u822a\u76f8\u8bc6\uff0c\u56e0\u6b64\u6768\u4e91\u806a\u4e5f\u8ba4\u5f97\u4ed6\u3002\u6b64\u9645\u53ea\u89c1\u4ed6\u53d1\u51fa\u563f\u563f\u602a \u7b11\uff0c\u5c16\u58f0\u8bf4\u9053\uff1a\u201c\u7126\u86ee\u4e8e\uff0c\u4e09\u5341\u5e74\u4e0d\u89c1\uff0c\u5c45\u7136\u5728\u8fd9\u91cc\u5e78\u4f1a\uff0c\u597d\uff0c\u8fd9\u4e00\u5200\u4e4b\u4ec7\uff0c\u54b1\u4eec\u7b97\u7b97\u3002\u201d \u6768\u4e91\u806a\u518d\u770b\u8fd9\u201c\u7126\u86ee\u5b50\u201d\u65f6\uff0c\u89c1\u662f\u4e2a\u4e94\u5341\u591a\u5c81\u7684\u8001\u51e0\uff0c\u4e24\u773c\u5982\u706b\u3002\u8eab\u8eaf\u7626\u5c0f\uff0c\u534a\u8eab\u8d64\u9732\uff0c\u80cc \u540e\u7ed3\u7740\u5927\u5927\u5c0f\u5c0f\u7684\u7599\u7629\uff0c\u76f8\u8c8c\u4e5f\u662f\u6781\u4e3a\u4e11\u964b\u3001\u6768\u4e91\u806a\u5fc3\u60f3\u3002\u4e45\u95fb\u7f57\u94c1\u81c2\u9887\u6709\u72ec\u95e8\u529f\u592b\uff0c\u96be\u5f97 \u6709\u6b64\u673a\u4f1a\uff0c\u4e14\u770b\u770b\u4ed6\u7684\u6280\u827a\u3001\u6768\u4e91\u806a\u6ee1\u4ee5\u4e3a\u90a3\u7126\u53d8\u5b50\u548c\u62bc\u56da\u8f66\u7684\u4eba\u5728\u4e00\u5904\uff0c\u6b66\u529f\u597d\u6781\u6709\u9650\uff0c \u8c05\u4ed6\u4eec\u6bd4\u4e0d\u4e0a\u8fd9\u4f4d\u4e10\u4fa0\uff0c\u6240\u4ee5\u6309\u7167\u6c5f\u6e56\u89c4\u5b9a\uff0c\u5728\u522b\u4eba\u5bfb\u4ef7\u62a5\u590d\u4e4b\u65f6\uff0c\u4e0d\u4e0b\u53bb\u6253\u5c94\u3002\u4ed6\u90a3\u77e5\u8fd9 \u4fe1\u86ee\u5e72\u4e43\u662f\u5173\u5185\u7684\u5927\u76d7\uff0c\u540e\u6765\u88ab\u6e05\u5ef7\u6536\u7f57\u592b\u5f53\u4e86\u5927\u5185\u7684\u4e00\u7b49\u536b\u58eb\uff0c\u7740\u5b9e\u6709\u4e9b\u529f\u592b\uff0e\u8fde\u90a3\u62bc\u56da \u8f66\u7684\uff0c\u4e5f\u975e\u5eb8\u624b\u3002 \u3000\u3000\u7126\u86ee\u5b50\u771f\u540d\u53eb\u7126\u5316\uff0c\u7eb3\u5170\u79c0\u5409\u56e0\u4e3a\u8fde\u5e74\u5f81\u6218\uff0c\u9664\u4e86\u7ebd\u67af\u5e90\u5916\uff0c\u8fd8\u60f3\u591a\u6dfb\u4e00\u4e24\u4e2a\u6b66\u529f\u9ad8\u5f3a \u7684\u4eba\uff0c\u56e0\u6b64\u6258\u591a\u94ce\u594f\u4e0a\u7687\u4e0a\uff0c\u6d3e\u4e24\u4e2a\u6b66\u827a\u9ad8\u5f3a\u7684\u5927\u5185\u536b\u58eb\u6765\uff0c\u8fd9\u7126\u5316\u5c31\u662f\u5176\u4e2d\u4e4b\u4e00\u3002\u90a3\u62bc\u56da \u8f66\u7684\u540d\u53eb\u7518\u5929\u7acb\uff0c\u4e5f\u662f\u5927\u5185\u7684\u536b\u58eb\uff0c\u6b66\u529f\u6bd4\u7126\u5316\u7a0d\u4f4e\uff0c\u548c\u7126\u5316\u540c\u88ab\u6d3e\u5230\u7eb3\u5170\u79c0\u5409\u5e10\u4e0b\uff0e\u8fd9\u6b21 \u7eb3\u5170\u79c0\u5409\u5dee\u9063\u7518\u5929\u7acb\u5230\u4f0a\u7281\u53bb\u62bc\u89e3\u72af\u4eba\uff0c\u653e\u5fc3\u4e0d\u4e0b\uff0c\u53c8\u5dee\u9063\u7126\u5316\u53bb\u63a5\u5e94\u3002\u6b63\u662f\u65e0\u5de7\u4e0d\u6210\u4e66\uff0c \u7126\u5316\u5728\u8c37\u4e2d\u78b0\u7740\u4e86\u7f57\u94c1\u81c2\u3001\u7518\u5929\u7acb\u62bc\u89e3\u7684\u56da\u8f66\u53c8\u521a\u521a\u649e\u5230\uff0c\u800c\u6768\u4e91\u806a\u4e5f\u5df2\u8ffd\u5230\u4e86\u8eab\u540e\u3002 \u3000\u3000\u7f57\u94c1\u81c2\u7b49\u7126\u5316\u53eb\u5b8c\u4e86\u4e00\u9635\uff0c\u4e00\u58f0\u602a\u7b11\uff0c\u4e00\u4e2a\u7bad\u6b65\uff0c\u7eb5\u6b65\u8fc7\u6765\uff0c\u5355\u81c2\u4e00\u626c\u3002\u5c31\u662f\u4e00\u4e2a\u72ec\u5288 \u534e\u5c71\u7684\u62db\u6570\uff0c\u5411\u7126\u5316\u5f53\u5934\u65ab\u4e0b\uff0c\u638c\u98ce\u98d2\u7136\uff0c\u75be\u5982\u5954\u96f7\u3002\u5982\u679c\u88ab\u4ed6\u65ab\u4e0a\uff0c\u8111\u888b\u4e5f\u8981\u5206\u5bb6\u3001\u7126\u5316 \u8eab\u4f53\u7626\u5c0f\uff0c\u6b66\u529f\u5374\u6781\u63a2\u6e5b\uff0c\u4e00\u504f\u8eab\u3002\u5de6\u8111\u865a\u52fe\u53f3\u62f3\u75be\u5410\uff0c\u907f\u5b9e\u5c31\u865a\uff0c\u671d\u7f57\u8f76\u81c2\u5de6\u80a9\u7a74\u51fb\u53bb\uff0c \u7f57\u94c1\u81c2\u63a5\u62db\u8fd8\u62db\uff0c\u4e00\u6761\u624b\u81c2\uff0c\u771f\u5982\u94c1\u94f8\u4e00\u822c\u3001\u5288\u63a5\u76f8\u62cd\uff0c\u7adf\u7136\u8fd0\u7528\u81ea\u5982\u3001\u7126\u5316\u5927\u543c\u4e00\u58f0\uff0c\u4f0f \u8eab\u63c9\u8fdb\uff0c\u53cc\u62f3\u53df\u53df\uff0c\u6b65\u8d70\u8fde\u73af\uff0c\u624b\u811a\u8d77\u5904\uff0c\u5168\u5e26\u52b2\u98ce\u3001\u6768\u4e91\u806a\u5fc3\u60f3\uff1a\u201c\u77a7\u4e0d\u51fa\u8fd9\u540d\u536b\u58eb\u5c45\u7136 \u8fd8\u6709\u4e24\u4e0b\uff0c\u4f7f\u7684\u7adf\u662f\u5317\u6d3e\u6b63\u5b97\u7684\u4f0f\u864e\u62f3\u62db\u5f0f\uff01\u201d\u518d\u770b\u65f6\uff0c\u53ea\u542c\u5f97\u7f57\u94c1\u81c2\u53c8\u662f\u4e00\u58f0\u602a\u7b11\uff0c\u81c2\u968f \u8eab\u8f6c\uff0c\u6307\u4e1c\u51fb\u897f\uff0c\u5ffd\u7eb5\u5ffd\u6a2a\uff0c\u53d8\u5316\u7e41\u590d\uff0c\u62db\u6570\u5947\u5999\uff0c\u679c\u7136\u4e0e\u4f17\u4e0d\u540c\uff0c\u5728\u638c\u6cd5\u4e2d\u72ec\u521b\u4e00\u683c\uff0c\u638c \u98ce\u6240\u5230\uff0c\u547c\u547c\u6709\u58f0\uff0c\u8fdc\u770b\u53bb\u597d\u50cf\u4ed6\u8eab\u4e0a\u7adf\u957f\u6ee1\u624b\u81c2\u4e00\u822c\uff0c\u6768\u4e91\u806a\u8d5e\u9053\uff1b\u201c\u7f57\u94c1\u81c2\u679c\u7136\u540d\u4e0d\u865a \u4f20\uff0c\u602a\u4e0d\u5f97\u4ee5\u5353\u5e08\u53d4\u90a3\u6837\u5d16\u5cb8\u81ea\u9ad8\u7684\u4eba\uff0c\u4e5f\u613f\u548c\u4ed6\u4ea4\u670b\u53cb\u3002\u201d \u3000\u3000\u4e24\u4eba\u4e09\u81c2\u3001\u6253\u4e86\u534a\u4e2a\u65f6\u8fb0\uff0c\u7126\u5316\u6e10\u5904\u4e0b\u98ce\uff0c\u6fc0\u6218\u4e2d\u5ffd\u7136\u602a\u53eb\u4e00\u58f0\uff0c\u6258\u5730\u8df3\u5f00\u4e08\u4f59\uff0c\u7f57\u94c1 \u81c2\u72ec\u81c2\u4e00\u62a1\uff0c\u8ddf\u8e2a\u8ffd\u4e0a\uff0c\u7518\u5929\u7acb\u5ffd\u7136\u4e00\u6296\u624b\uff0c\u98de\u51fa\u51e0\u70b9\u5706\u5149\uff0c\u671d\u7f57\u94c1\u81c2\u80f8\u524d\u6492\u53bb\uff0c\u7f57\u94c1\u81c2\u4e00 \u638c\u5288\u53bb\uff0c\u628a\u6697\u5668\u9707\u843d\u3002\u4f46\u4ed6\u5374\u60f3\u4e0d\u5230\u7518\u5929\u7acb\u7684\u6697\u5668\u975e\u5e38\u6b79\u6bd2\uff0c\u7528\u7684\u4e43\u662f\u5582\u6bd2\u8774\u8776\u9556\uff0c\u6697\u5668\u867d \u5c0f\uff0c\u5185\u4e2d\u5374\u85cf\u6709\u673a\u5173\uff0c\u7f57\u94c1\u81c2\u7684\u638c\u98ce\u867d\u52b2\uff0c\u5374\u53ea\u80fd\u628a\u5b83\u6253\u6c89\u843d\u5730\uff0c\u5728\u5730\u4e0a\u673a\u5173\u4e00\u52a8\u3002\u91cd\u53c8\u8dc3 \u8d77\uff0c\u7f57\u94c1\u81c2\u8f9e\u4e0d\u53ca\u9632\uff0c\u4e0b\u76d8\u7adf\u7ed9\u6253\u4e2d\u4e24\u679a\u3002\u4e00\u58f0\u6012\u543c\uff0c\u5355\u81c2\u5f53\u5934\u5288\u4e0b\uff0c\u7126\u5316\u62fc\u547d\u4e00\u62f3\u3002\u6321\u4e0d \u4f4f\u638c\u529b\uff0c\u534a\u8fb9\u8eab\u5b50\u7adf\u7ed9\u5288\u5f00\uff0c\u800c\u7f57\u94c1\u9aa8\u4e5f\u5df2\u6eda\u5728\u5730\u4e0a\uff0c\u4e0d\u80fd\u518d\u8d77\u3002 \u3000\u3000\u7518\u5929\u7acb\u6697\u5668\u594f\u529f\uff0c\u6b63\u60f3\u8865\u4ed6\u4e00\u5200\uff0c\u534a\u5c71\u8170\u5904\uff0c\u6768\u4e91\u806a\u65e9\u5982\u98de\u9e1f\u822c\u63a0\u4e0b\uff01\u7518\u5929\u7acb\u4e00\u6296\u624b\uff0c \u53c8\u662f\u51e0\u679a\u8774\u8776\u9556\u8fde\u7ffb\u98de\u51fa\uff0c\u6768\u4e91\u806a\u5b9d\u5251\u4e00\u62a1\uff0c\u6bd4\u7f57\u94c1\u81c2\u7684\u638c\u98ce\u5389\u5bb3\u5f97\u591a\uff0c\u51e0\u679a\u8774\u952e\u9556\u5168\u7ed9\u9707 \u5f97\u98de\u4e86\u51fa\u53bb\uff0c\u4e24\u540d\u5175\u7387\uff0c\u9996\u5f53\u5176\u51b2\u3002\u4e2d\u950b\u5012\u5730\uff0c\u6768\u4e91\u806a\u8eab\u968f\u5251\u8d70\uff0c\u4e00\u7f15\u9752\u5149\u3002\u500f\u7684\u5230\u7518\u5929\u7acb \u80cc\u540e\uff0c\u7518\u5929\u7acb\u6697\u5668\u867d\u9ad8\uff0c\u6b66\u529f\u5374\u5728\u7126\u5316\u4e4b\u4e0b\u3001\u867d\u7136\u4e0d\u7b97\u5eb8\u624b\uff0c\u4f46\u5982\u4f55\u6863\u5f97\u4f4f\u6768\u4e91\u806a\u7684\u5251\u6cd5\uff0c \u521a\u6321\u5f97\u4e00\u6321\u3002\u53f3\u624b\u4e94\u53ea\u6307\u5934\uff0c\u5df2\u5168\u7ed9\u5251\u950b\u524a\u53bb\uff01\u6768\u4e91\u806a\u987a\u624b\u518d\u8865\u4e00\u5251\uff0c\u628a\u4ed6\u523a\u4e86\u4e2a\u900f\u660e\u5927\u7a9f \u7abf\uff0c\u8f6c\u8fc7\u8eab\u6765\uff0c\u4e00\u9635\u8ffd\u9010\uff0c\u628a\u5341\u51e0\u4e2a\u62bc\u56da\u8f66\u7684\u6e05\u519b\u5168\u7ed9\u7ed3\u679c\u3001\u8df3\u4e0a\u4e86\u56da\u8f66\u4e0a\uff0c\u53ea\u89c1\u8f66\u4e2d\u56da \u72af\uff0c\u6b63\u662f\u4ed6\u7684\u76df\u5f1f\u4f0a\u58eb\u8fbe\uff0c\u6768\u7389\u806a\u65e0\u6687\u591a\u8bf4\uff0c\u53f2\u7389\u81fe\u2014\u8fde\u51e0\u5251\uff0c\u5288\u5f00\u4e86\u4ed6\u8eab\u4e0a\u7684\u9563\u94f8\uff0c\u53eb \u9053\uff1a\u201c\u8d24\u5f1f\u3002\u4f60\u81ea\u5df1\u51fa\u6765\u5427\uff0e\u6211\u8fd8\u8981\u53bb\u770b\u4e00\u4f4d\u8001\u524d\u8f88\u3002\u201d\u4ed6\u8dd1\u5230\u7f57\u94c1\u81c2\u8eab\u8fb9\u3002\u53eb\u58f0\u201c\u7f57\u53d4 \u53d4\u201d\uff0c\u7f57\u94c1\u81c2\u7741\u81ea\u4e00\u770b\uff0c\u4f9d\u7a00\u8fd8\u8ba4\u5f97\u662f\u6768\u4e91\u806a\u3002\u82e6\u7b11\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u4e2d\u7528\u4e86\uff01\u201d \u3000\u3000----------- \u3000\u3000\u98ce\u4e91\u9601 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u585e\u5916\u5947\u4fa0\u4f20\u300b\u2014\u2014\u7b2c\u4e8c\u5341\u4e03\u56de\u3000\u767d\u53d1\u9b54\u5973 \u6881\u7fbd\u751f\u300a\u585e\u5916\u5947\u4fa0\u4f20\u300b \u7b2c\u4e8c\u5341\u4e03\u56de\u3000\u767d\u53d1\u9b54\u5973 \u3000\u3000\u6768\u4e91\u806a\u89c1\u4ed6\u9762\u8272\u6de4\u9ed1\uff0c\u77e5\u9053\u6240\u8a00\u4e0d\u865a\u3002\u6b66\u6797\u8c6a\u4fa0\uff0c\u751f\u6b7b\u7f6e\u4e4b\u5ea6\u5916\uff0c\u4e5f\u4e0d\u4fbf\u4f5c\u513f\u5973\u4e4b\u6001\uff0c \u8eac\u8170\u95ee\u9053\uff1a\u201c\u8001\u524d\u8f88\u6709\u4ec0\u4e48\u8bdd\u8981\u7559\u4e0b\u7684\uff1f\u201d\u7f57\u94c1\u81c2\u9053\uff1b\u201c\u5353\u4e00\u822a\u6709\u5c01\u4fe1\u6258\u6211\u4ea4\u7ed9\u767d\u53d1\u9b54\u5973\u3002 \u4f60\u80fd\u4ee3\u6211\u9001\u5230\u4e48\uff1f\u201d \u3000\u3000\u539f\u6765\u7f57\u94c1\u81c2\u5f53\u5e74\u662f\u5ddd\u4e2d\u5927\u76d7\uff0c\u662f\u767d\u53d1\u9b54\u5973\u5f97\u529b\u7684\u52a9\u624b\uff0c\u800c\u5353\u4e00\u822a\u5219\u662f\u8d35\u5bb6\u516c\u5b50\u3001\u767d\u53d1\u9b54 \u5973\u548c\u5353\u4e00\u822a\u60c5\u7275\u7275\u8fde\uff08\u53c2\u89c1\u62d9\u8457\u300a\u767d\u53d1\u9b54\u5973\u4f20\u300b\u3015\uff0c\u7f57\u94c1\u81c2\u5168\u90fd\u77e5\u9053\u3001\u767d\u53d1\u9b54\u5973\u56e0\u5353\u4e00\u822a\u7684 \u5e08\u53d4\u4eec\u963b\u6320\u5a5a\u4e8b\uff0c\u5f15\u8d77\u4e89\u6267\uff0c\u628a\u6b66\u5f53\u4e24\u4e2a\u957f\u8005\u65ab\u4f24\uff0c\u9003\u5230\u65b0\u7586\uff0c\u5353\u4e00\u822a\u540e\u6765\u4e5f\u653e\u5f03\u4e86\u6b66\u5f53\u6d3e \u7684\u638c\u95e8\u4e0d\u505a\uff0c\u8ffd\u8e2a\u767d\u53d1\u9b54\u5973\uff0e\u4e0d\u6599\u8bef\u4f1a\u96be\u6d88\uff0c\u98ce\u6ce2\u8fed\u8d77\uff0c\u53c8\u63d2\u4e0a\u4f55\u7eff\u534e\u7684\u4e8b\uff0c\u767d\u53d1\u9b54\u5973\u8981\u627e \u4ed6\u4eec\u6666\u6c14\uff0c\u5f15\u5f97\u5353\u4e00\u822a\u7684\u4e24\u4e2a\u5e08\u6743\u9ec4\u53f6\u9053\u4eba\u548c\u767d\u77f3\u9053\u4eba\uff0c\u8fdc\u8fdc\u8d76\u6765\u3002\u90a3\u65e5\u5728\u8349\u539f\u4e0a\u548c\u6768\u4e91\u806a \u76f8\u9047\u4e4b\u540e\uff0c\u4e24\u4eba\u77e5\u9053\u611f\u9707\u5357\u7586\u7684\u5973\u82f1\u96c4\u98de\u7ea2\u5dfe\u5c31\u662f\u767d\u53d1\u9b54\u5973\u7684\u5f92\u5f1f\u3001\u5bf9\u767d\u53d1\u9b54\u5973\u8fd9\u624d\u7a0d\u6709\u597d \u611f\u3001\u5546\u4eba\u7ee7\u7eed\u5bfb\u627e\u3002\u4e0d\u4e45\u5c31\u627e\u5230\u4e86\u5353\u4e00\u822a\u3001\u5176\u65f6\u5353\u4e00\u822a\u5df2\u5c06\u4f55\u7ea7\u5355\u9001\u56de\u5173\u5185\uff0c\u53c8\u518d\u6298\u56de\u65b0 \u7586\u3002\u5728\u6155\u58eb\u5854\u683c\u5c71\u9690\u5c45\uff0c\u8f9b\u9f99\u5b50\u548c\u695a\u662d\u524d\u4e0a\u5c71\u62dc\u89c1\u3002\u7ed9\u4ed6\u75db\u65a5\u4e00\u987f\u3001\u695a\u662d\u5357\u5728\u8f9b\u9f99\u5b50\u624b\u4e2d\u53d6 \u8fc7\u4e86\u6e38\u9f99\u5b9d\u5251\u3002\u6124\u7136\u81ea\u53bb\uff0e\u8f9b\u9f99\u5b50\u5219\u7518\u53d7\u5e08\u5c0a\u8d35\u7f5a\uff0c\u6df1\u5c71\u9762\u58c1\u3002\u53d1\u8a93\u5728\u4e8c\u5341\u5e74\u5185\u3002\u4e0d\u518d\u4e0b\u5c71\u3002 \u3000\u3000\u9ec4\u53f6\u548c\u767d\u77f3\u5bfb\u4e0a\u95e8\u65f6\uff0c\u5353\u4e00\u822a\u6b63\u662f\u610f\u5174\u8427\u7d22\uff0c\u6f20\u6f20\u5be1\u6b22\u7684\u65f6\u5019\uff0e\u5a49\u8f6c\u62d2\u7edd\u4e86\u5e08\u53d4\u4eec\u8981\u4ed6 \u91cd\u638c\u6b66\u5f53\u6d3e\u7684\u8bf7\u6c42\u3001\u9ec4\u53f6\u548c\u767d\u77f3\u60f3\u7ee7\u7eed\u53bb\u627e\u767d\u53d1\u9b54\u5973\u6bd4\u5251\uff0c\u5353\u4e00\u822a\u53c8\u8dea\u4e0b\u529f\u6b62\u3002\u9ec4\u53f6\u9053\u4eba\u60f3 \u9053\uff1a\u201c\u90a3\u5973\u9b54\u5934\u4e0d\u8fd1\u4eba\u60c5\u3002\u770b\u4f60\u603b\u6709\u4e00\u65e0\u4f24\u5728\u5979\u624b\u4e0a\u3002\u201d\u5353\u4e00\u822a\u6ce3\u9053\uff1a\u201c\u5f1f\u5b50\u7f6a\u5b7d\u6df1\u91cd\uff0e\u82e5 \u906d\u6a2a\u6b7b\uff0c\u4e5f\u4e0d\u6562\u8bf7\u5e08\u6743\u62a5\u4ec7\u3002\u201d\u767d\u77f3\u9053\u4eba\u53f9\u4e86\u53e3\u6c14\uff0c\u77e5\u9053\u60c5\u4e4b\u6240\u949f\uff0c\u65e0\u7406\u53ef\u55bb\uff0c\u62c9\u4e86\u9ec4\u53f6\u906d \u4eba\u98d8\u7136\u81ea\u53bb\u3002 \u3000\u3000\u5353\u4e00\u822a\u9001\u4e24\u4e2a\u5e08\u53d4\u53bb\u540e\u3002\u5de6\u601d\u53f3\u60f3\uff0c\u89c9\u5f97\u8bef\u4f1a\u82e5\u4e0d\u6d88\u9664\uff0c\u5fc3\u4e8b\u7ec8\u96be\u4e86\u7ed3\uff0c\u60f3\u6765\u60f3\u53bb\uff0c\u60f3 \u8d77\u4e86\u7f57\u94c1\u81c2\u4e5f\u8bb8\u53ef\u4f5c\u8c03\u4eba\uff0c\u56e0\u6b64\u5199\u4e86\u4e00\u5c01\u4fe1\uff0c\u53eb\u7f57\u94c1\u81c2\u53bb\u9001\u7ed9\u767d\u53d1\u9b54\u5973\u3001\u4e0d\u6599\u7f57\u94c1\u81c2\u5728\u9014\u4e2d \u9047\u5230\u4ec7\u5bb6\u3002\u9634\u6c9f\u91cc\u7ffb\u8239\uff1b\u7adf\u7ed9\u7518\u5929\u7acb\u7684\u5582\u6bd2\u8774\u8776\u9556\u4f24\u4e86\u6027\u547d\u3002 \u3000\u3000\u90a3\u7126\u5316\u548c\u7518\u5929\u7acb\u539f\u662f\u56db\u5ddd\u7684\u4e24\u4e2a\u540d\u6355\u5934\uff0c\u4e8c\u5341\u4f59\u5e74\u524d\uff0c\u7f57\u94c1\u7ba1\u5728\u6210\u90fd\u9644\u8fd1\u505a\u6848\uff0c\u7ed9\u7126\u5316 \u8ffd\u6355\uff0c\u4e89\u6597\u8d77\u6765\uff0c\u5be1\u4e0d\u654c\u4f17\uff0c\u5e78\u5f97\u5353\u4e00\u822a\u8def\u8fc7\uff0c\u628a\u4ed6\u6551\u51fa\u3002\u6240\u4ee5\u7f57\u94c1\u81c2\u540e\u6765\u4e5f\u5230\u65b0\u7586\uff0c\u548c\u5353 \u4e00\u822a\u65f6\u65f6\u6765\u5f80\u3002 \u3000\u3000\u6768\u4e91\u806a\u542c\u7f57\u94c1\u81c2\u8bf4\u5353\u4e00\u822a\u6709\u4fe1\u8981\u7ed9\u767d\u53d1\u9b54\u5973\uff0c\u4ed6\u867d\u4e0d\u559c\u6b22\u81ea\u53d1\u9b54\u5973\uff0c\u5374\u548c\u5353\u4e00\u822a\u6709\u6df1\u539a \u7684\u4ea4\u60c5\uff0c\u4f55\u51b5\u53c8\u662f\u7f57\u94c1\u81c2\u4e34\u7ec8\u6240\u6258\uff0c\u5f53\u4e0b\u4e00\u53e3\u5e94\u627f\u9053\uff1a\u201c\u4f60\u8bf7\u653e\u5fc3\uff0c\u5353\u5e08\u53d4\u7684\u4e8b\u60c5\u6211\u4e00\u5b9a\u66ff \u4ed6\u505a\u5230\u3002\u201d\u7f57\u94c1\u81c2\u53cc\u773c\u4e00\u7ffb\uff0c\u542b\u7b11\u8bf4\u9053\uff1a\u201c\u6069\u4ec7\u4e86\u4e86\uff0c\u6211\u53ef\u4ee5\u5b89\u5fc3\u53bb\u4e86\uff01\u201d\u5355\u81c2\u5782\u4e0b\uff0c\u9616\u7136 \u957f\u901d\u3002 \u3000\u3000\u6768\u4e91\u806a\u6398\u4e86\u571f\u5751\uff0c\u5c06\u7f57\u94c1\u81c2\u8349\u8349\u57cb\u4e86\u3002\u5bf9\u4f0a\u58eb\u8fbe\u9053\uff1a\u201c\u5144\u5f1f\uff0c\u6211\u6709\u4e8b\u8981\u5230\u5929\u5c71\u7684\u5357\u9ad8 \u5cf0\uff0c\u4f60\u81ea\u5df1\u56de\u5580\u5c14\u6c81\u8349\u539f\u7684\u54c8\u8428\u514b\u8425\u5730\u53bb\u5427\uff0c\u9ea6\u76d6\u63d0\u548c\u4ed6\u7684\u59d1\u5a18\u66fc\u94c3\u54ea\u4e5f\u5728\u90a3\u91cc\u3002\u6b64\u5730\u79bb\u5580 \u5c14\u6c81\u8349\u539f\u53ea\u6709\u4e09\u65e5\u8def\u7a0b\uff0c\u9644\u8fd1\u7684\u6e05\u5175\u53c8\u5df2\u7ed9\u8d76\u8dd1\uff0c\u60f3\u6765\u4e0d\u4f1a\u6709\u4ec0\u4e48\u51f6\u9669\u7684\u4e86\u3002\u201d\u4f0a\u58eb\u8fbe\u542c\u8bf4 \u76df\u5f1f\u751f\u8fd8\uff0c\u751a\u4e3a\u9ad8\u5174\uff0c\u5bf9\u6768\u4e91\u806a\u9053\uff1a\u201c\u4f60\u4e5f\u5feb\u70b9\u56de\u6765\u5440\uff0c\u54b1\u4eec\u4e09\u4eba\u91cd\u805a\u4e00\u8d77\uff0c\u53c8\u53ef\u5927\u4e8e\u4e00\u573a \u4e86\u3002\u201d\u4e34\u522b\u524d\u6768\u4e91\u806a\u6015\u4ed6\u9047\u5230\u6b66\u529f\u9ad8\u5f3a\u7684\u654c\u4eba\uff0c\u53c8\u5c06\u593a\u81ea\u5929\u8499\u7985\u5e08\u7684\u53e4\u5251\u9001\u7ed9\u4ed6\u9053\uff1b\u201c\u8fd9\u628a \u5251\u4e43\u662f\u5929\u9f99\u6d3e\u9547\u5c71\u4e4b\u5b9d\u3002\u4f60\u8981\u597d\u597d\u4fdd\u5b58\u3002\u201d\u4f0a\u58eb\u8fbe\u63a5\u8fc7\u5b9d\u5251\uff0c\u53d1\u8a93\u8bf4\u9053\uff1a\u201c\u6211\u4e00\u5b9a\u4e0d\u8f9c\u8d1f\u8fd9 \u628a\u5b9d\u5251\u3002\u5982\u679c\u6211\u6b7b\u4e86\uff0c\u4e5f\u8981\u4f20\u7ed9\u77e2\u5fd7\u6297\u6e05\u7684\u4eba\u3002\u201d\u4e24\u4eba\u5404\u81ea\u53ee\u549b\uff0c\u63e1\u624b\u9053\u522b\u3002 \u3000\u3000\u4f0a\u58eb\u8fbe\u81ea\u56de\u8425\u5730\uff0c\u6309\u4e0b\u4e0d\u8868\u3002\u4e14\u8bf4\u6768\u4e91\u806a\u62ab\u661f\u6234\u6708\uff0c\u91cd\u4e0a\u5929\u5c71\uff0e\u5929\u5c71\u6a2a\u4e98\u4e09\u5343\u591a\u91cc\uff0c\u6666 \u660e\u7985\u5e08\u4f4f\u5728\u5317\u9ad8\u5cf0\uff0c\u767d\u53d1\u9b54\u5973\u4f4f\u5728\u5357\u9ad8\u5cf0\uff0c\u4e24\u5cf0\u76f8\u8ddd\uff0c\u4e5f\u6709\u4e00\u5343\u591a\u91cc\u3002\u6768\u4e91\u806a\u5148\u4e0a\u5317\u9ad8\u5cf0\u62dc \u89c1\u4e86\u81ea\u5df1\u7684\u5e08\u7236\u3002\u6666\u660e\u7985\u5e08\u5bf9\u4ed6\u9053\uff1a\u201c\u4f60\u8fd9\u4e9b\u5e74\u6765\uff0c\u5e72\u5f97\u8f70\u8f70\u70c8\u70c8\uff0c\u679c\u7136\u4e0d\u8d1f\u6211\u7684\u6559\u8bf2\u3002\u53ea \u662f\u6e05\u5175\u52bf\u5927\uff0e\u6210\u8d25\u96be\u4e8e\u9884\u6d4b\u3001\u4f46\u53ea\u6c42\u5c3d\u529b\u800c\u4e3a\uff0c\u7136\u5931\u8d25\u4e5f\u65e0\u8db3\u61be\uff0e\u4f60\u7684\u5e08\u7b2c\u695a\u662d\u5357\u5f88\u806a\u660e\uff0c \u4f60\u82e5\u80fd\u5f15\u4ed6\u56de\u5934\u6700\u597d\uff0c\u82e5\u7136\u4e0d\u80fd\u3002\u53ef\u4ee5\u66ff\u6211\u628a\u4ed6\u5e9f\u6389\u3002\u201d\u6768\u4e91\u806a\u8c22\u8fc7\u5e08\u7236\u6559\u8bad\uff0c\u6d77\u660e\u7985\u5e08\u53c8 \u9053\uff1a\u201c\u4f60\u7684\u5353\u5e08\u53d4\u662f\u6027\u60c5\u4e2d\u4eba\uff0c\u767d\u53d1\u9b54\u5973\u867d\u7136\u4e56\u50fb\uff0c\u4e5f\u662f\u6027\u60c5\u4e2d\u4eba\uff0c\u4f60\u4e0d\u8981\u6392\u9006\u5979\u7684\u610f \u601d\uff0e\u4e5f\u8bb8\u53ef\u4ee5\u66ff\u4ed6\u4eec\u4e24\u4eba\u8c03\u505c\u548c\u597d\u3002\u201d \u3000\u3000\u6768\u4e91\u806a\u548c\u5e08\u7236\u4f4f\u4e86\u4e24\u5929\uff0c\u518d\u79bb\u5f00\u5317\u9ad8\u5cf0\u5411\u524d\u5929\u5c71\u8fdb\u884c\uff0c\u5357\u5929\u5c71\u51b0\u6cb3\u5f88\u591a\uff0c\u5bfb\u4e86\u4e03\u516b\u5929\uff0c \u8fdc\u8fdc\u671b\u53bb\uff0c\u4e00\u51b0\u6cb3\u4eff\u602b\u767d\u7691\u7691\u79ef\u96ea\u5728\u6d41\u52a8\uff0c\u884c\u8fd1\u4e86\u7740\u3002\u53ea\u89c1\u51b0\u6cb3\u8868\u9762\uff0c\u53c8\u5f62\u6210\u4e86\u5343\u4e07\u4e2a\u9ad8\u4f4e \u8d77\u4f0f\u3001\u5927\u5c0f\u4e0d\u540c\u7684\u51b0\u63a8\uff0e\u8fd9\u4e9b\u51b0\u8c01\u6709\u9ad8\u8fbe\u6570\u5341\u5927\u7684\uff0e\u5728\u9633\u5149\u7167\u5c04\u4e0b\uff0c\u4e3d\u5f69\u6d6e\u7a7a\uff0c\u771f\u662f\u4eba\u95f4\u96be \u89c1\u7684\u5947\u666f\u3001\u6768\u4e91\u806a\u6cbf\u7740\u51b0\u5c71\u7684\u8fb9\u6cbf\uff0c\u4e00\u76f4\u884c\u8fdb\uff0c\u53c8\u8fc7\u4e86\u4e24\u5929\uff0c\u5df2\u63a5\u8fd1\u5cf0\u9876\u7f13\u7f13\u6d41\u52a8\u4e0b\u6765\u7684\u539f \u59cb\u51b0\u6cb3\uff0c\u8fdc\u671b\u5982\u767d\u8272\u7684\u5927\u6d77\u6d6a\u4ece\u6df1\u8c37\u91cc\u6d41\u6cfb\u800c\u4e0b\uff0c\u884c\u81f3\u8fd1\u65c1\uff0c\u624d\u770b\u6e05\u90a3\u4e9b\u6d6a\u5934\u90fd\u662f\u9ad8\u8fbe\u4e94\u516d \u6587\u7684\u5927\u51b0\u67f1\uff0c\u8d77\u4f0f\u5c42\u53e0\u3001\u6709\u7684\u4f3c\u900f\u660e\u7684\u5b9d\u5854\uff0c\u6709\u7684\u4f3c\u5de8\u4eba\u7684\u624b\u638c\uff0c\u5f62\u5f62\u8272\u8272\u5343\u5947\u4e07\u72b6\uff0e\u6768\u4e91 \u806a\u6cbf\u7740\u539f\u59cb\u51b0\u6cb3\u4e0a\u884c\uff0c\u518d\u8fc7\u534a\u5929\uff0c\u8d70\u8fc7\u4e00\u4e2a\u597d\u4f3c\u7011\u5e03\u72b6\u7684\u51b0\u574e\uff0c\u9762\u524d\u8c41\u7136\u5f00\u6717\uff0c\u73b0\u51fa\u4e00\u7247\u9ad8 \u8fbe\u5343\u4e08\u7684\u5927\u51b0\u5742\uff0c\u8fc7\u5742\u5c3d\u5934\u77d7\u7acb\u7740\u4e00\u5ea7\u660e\u4eae\u7684\u58ee\u5cf0\uff0c\u72ec\u51fa\u5e72\u7fa4\u5cf0\u4e4b\u4e0a\u3002\u6768\u4e91\u806a\u65bd\u5c55\u7edd\u9876\u8f7b \u529f\uff0c\u6500\u5230\u4e86\u51b0\u5cf0\u4e4b\u4e0a\uff0c\u53ea\u89c1\u5cf0\u9876\u53c8\u6709\u4e00\u95f4\u575a\u51b0\u7b51\u6210\u7684\u51b0\u5c4b\uff0e\u767d\u53d1\u9b54\u5973\u4f4e\u7709\u5408\u4ec0\u3001\u5750\u5728\u5f53\u4e2d\uff0c \u6768\u4e91\u806a\u65bd\u793c\u6c42\u89c1\uff0c\u826f\u4e45\u826f\u4e45\uff0c\u767d\u53d1\u9b54\u5973\u624d\u7741\u5f00\u773c\u775b\uff0c\u62db\u62db\u624b\u9053\uff1b\u201c\u4f60\u8fdb\u6765\uff01\u201d \u3000\u3000\u6768\u4e91\u806a\u8fdb\u4e86\u51b0\u5c4b\uff0c\u767d\u53d1\u9b54\u5973\u5389\u58f0\u8bf4\u9053\uff1b\u201c\u662f\u4f60\u5e08\u7236\u5dee\u9063\u4f60\u6765\u7684\u5417\uff1f\u201d\u6768\u4e91\u806a\u9053\uff1b\u201c\u4e0d \u662f\uff0c\u662f\u5353\u5e08\u53d4\u8981\u6211\u6765\u7684\u3002\u201d\u767d\u53d1\u9b54\u5973\u8138\u8272\u500f\u53d8\uff0c\u8bf4\u9053\uff1b\u201c\u6211\u867d\u5728\u51b0\u5c71\uff0c\u4e5f\u5df2\u77e5\u9053\u4ed6\u7684\u4e24\u4e2a\u5e08 \u6743\u4ece\u56db\u95fb\u8d76\u4e86\u5230\u6765\uff0e\u4ed6\u53eb\u4f60\u6765\uff0c\u662f\u4e0d\u662f\u8981\u7ea6\u6211\u53bb\u548c\u4ed6\u4eec\u6bd4\u5251\uff1f\u201d\u6768\u4e91\u806a\u6025\u5fd9\u8bf4\u9053\uff1b\u201c\u8fd9\u662f\u54ea \u91cc\u8bdd\u6765\uff0e\u9ec4\u53f6\u548c\u767d\u77f3\u4e24\u9053\u957f\u5df2\u56de\u56db\u5ddd\u53bb\u4e86\uff0e\u5353\u5e08\u6743\u6709\u4fe1\u95ee\u5019\u4f60\u3002\u201d\u767d\u53d1\u9b54\u5973\u8138\u8272\u7a0d\u7f13\uff0c\u53eb \u9053\uff1a\u201c\u62ff\u6765\uff01\u201d\u6768\u4e91\u806a\u5c06\u4e66\u4fe1\u5448\u4e0a\uff0c\u767d\u53d1\u9b54\u5973\u62c6\u5f00\u4e00\u7740\uff0c\u53ea\u89c1\u9526\u7b3a\u4e0a\u5199\u7740\u4e00\u9996\u4e03\u8a00\u5f8b\u8bd7\uff0c\u8bd7 \u9053\uff1b\u201c\u522b\u540e\u97f3\u4e66\u4e24\u4e0d\u95fb\uff0c\u9884\u77e5\u8c23\u7762\u5fc5\u7eb7\u7ead\uff0c\u53ea\u7f18\u6d77\u5185\u5b58\u77e5\u5df1\uff0c\u59cb\u4fe1\u5929\u6daf\u82e5\u6bd4\u90bb\u3002\u5386\u52ab\u4e86\u65e0\u751f \u6b7b\u5ff5\uff0c\u7ecf\u971c\u65b9\u663e\u50b2\u5bd2\u5fc3\uff0c\u51ac\u98ce\u591c\u6298\u82b1\u5343\u6811\uff0c\u5c1a\u6709\u5e7d\u9999\u653e\u4e0a\u6797\u3002\u201d \u3000\u3000\u6df1\u60c5\u4e00\u7247\uff0c\u8868\u767d\u771f\u5fc3\uff0e\u767d\u53d1\u9b54\u5973\u4e00\u7740\uff0c\u4e0d\u89c9\u6ef4\u4e0b\u6cea\u6765\u3002\u4f46\u51e0\u5341\u5e74\u6765\u8bef\u4f1a\u6a2a\u4e98\u80f8\u4e2d\uff0c\u867d\u7136 \u4e00\u65f6\u611f\u52a8\uff0c\u8f6c\u5ff5\u4e00\u60f3\uff0c\u53c8\u4f5b\u7136\u60f3\u9053\uff1b\u201c\u4ed6\u4eec\u6b66\u5f53\u6708\u4e0b\uff0c\u81ea\u547d\u6b66\u6797\u6b63\u5b97\uff0c\u628a\u6211\u5f53\u6210\u5996\u5b7d\uff0c\u6211\u4e5f \u9ad8\u6500\u4e0d\u4e0a\u4ed6\u4eec\u3002\u4f60\u56de\u53bb\u544a\u8bc9\u5353\u4e00\u822a\u5427\uff0c\u6211\u4ee5\u540e\u4e0d\u518d\u627e\u4ed6\u6666\u6c14\uff0c\u4f46\u8981\u60f3\u548c\u89e3\uff0c\u90a3\u5374\u662f\u4e07\u4e07\u4e0d \u80fd\u3002\u201d\u6768\u4e91\u806a\u4e0d\u77e5\u5176\u4e2d\u539f\u59d4\uff0c\u4f46\u7ec6\u5473\u8bed\u6c14\uff0c\u767d\u53d1\u9b54\u5973\u4e0e\u5353\u4e00\u822a\u4e4b\u95f4\uff0c\u4f3c\u4e4e\u9887\u6709\u4e00\u6bb5\u60c5\u5b7d\u3002\u5f53 \u4e0b\u5a49\u8a00\u8bf4\u9053\uff1a\u201c\u4eba\u751f\u4e0d\u8fc7\u767e\u5e74\uff0c\u4f55\u82e6\u4ee4\u672c\u6765\u4eb2\u7231\u7684\u4eba\u53d7\u82e6\uff0c\u81ea\u5df1\u4e5f\u4e00\u6837\u53d7\u82e6\uff1f\u201d\u767d\u53d1\u9b54\u5973\u767d \u53d1\u98d8\u98d8\uff0c\u53d8\u8272\u8bf4\u9053\uff1a\u201c\u5353\u4e00\u822a\u544a\u8bc9\u4f60\u4e86\uff1f\u201d\u6768\u4e91\u806a\u9053\uff1a\u201c\u5353\u5e08\u53d4\u4ece\u6765\u4e0d\u4e0e\u5f1f\u5b50\u8c08\u53ca\u79c1\u4e8b\u3002\u201d \u767d\u53d1\u9b54\u5973\u9053\uff1a\u201c\u90a3\u4e48\u8fd9\u662f\u4f60\u81ea\u4f5c\u4e3b\u5f20\u529d\u6211\u6765\u4e86\uff1f\u201d\u6768\u4e91\u806a\u4e0d\u6562\u7f6e\u7b54\u3002\u767d\u53d1\u9b54\u5973\u5ffd\u7136\u201c\u54fc\u201d\u4e86 \u4e00\u58f0\uff0c\u6307\u7740\u6768\u4e91\u806a\u9053\uff1a\u201c\u98de\u7ea2\u5dfe\u7b97\u4e0d\u7b97\u5f97\u662f\u4f60\u4eb2\u7231\u7684\u4eba\uff1f\u201d\u6768\u4e91\u806a\u7a81\u7136\u4e00\u9707\uff0c\u51c4\u7136\u8bf4\u9053\uff1a \u201c\u6211\u4e0e\u4ee4\u5f92\u60c5\u903e\u5144\u59b9\uff01\u201d\u767d\u53d1\u9b54\u5973\u5389\u58f0\u8bf4\u9053\uff1a\u201c\u90a3\u5c31\u662f\u4e86\uff01\u4f60\u4e3a\u4f55\u53c8\u4ee4\u5979\u53d7\u82e6\uff1f\u201d\u6768\u4e91\u806a\u5982 \u53d7\u5229\u5251\u6525\u5fc3\uff0c\u7b54\u4e0d\u51fa\u6d3b\u6765\u3001\u767d\u53d1\u9b54\u5973\u6ee1\u8154\u6012\u706b\u3002\u597d\u50cf\u8981\u5411\u6768\u4e91\u806a\u53d1\u6cc4\u4f3c\u7684\uff0c\u8bf4\u5f97\u53c8\u6025\u53c8\u5feb\u3002 \u6307\u7740\u6768\u4e91\u806a\u9053\uff1a\u201c\u4f60\u4eec\u540d\u5e08\u95e8\u4e0b\uff0c\u90fd\u662f\u81ea\u547d\u4e0d\u89c1\uff0e\u4f60\u5c06\u98de\u7ea2\u5dfe\u6297\u78e8\u6210\u4ec0\u4e48\u6837\u5b50\uff0c\u4f60\u77e5\u4e0d\u77e5 \u9053\uff1f\u4f60\u8fd8\u6765\u529d\u6211\uff1f\u4e0d\u662f\u98de\u7ea2\u5dfe\u4e00\u4e0a\u5c71\u5c31\u4e3a\u4f60\u6c42\u60c5\u3002\u53eb\u6211\u4e0d\u8981\u63d2\u624b\uff0c\u6211\u65e9\u628a\u4f60\u5e9f\u4e86\uff01\u201d\u6768\u4e91\u806a \u6025\u5fd9\u95ee\u9053\uff1a\u201c\u98de\u7ea2\u5dfe\u5728\u54ea\u513f\uff1f\u5979\u600e\u4e48\u6837\u4e86\uff1f\u201d\u767d\u53d1\u9b54\u5973\u9053\uff1a\u201c\u5c31\u5728\u8fd9\u513f\uff0c\u4e00\u53ef\u662f\u5979\u53d1\u8a93\u4e0d\u89c1 \u4f60\u4e86\uff01\u201d\u6768\u4e91\u806a\u6e38\u76ee\u56db\u987e\uff0c\u51b0\u5c4b\u91cc\u7a7a\u8361\u8361\u7684\u54ea\u6709\u65c1\u4eba\u3002\u767d\u53d1\u9b54\u5973\u5411\u7a97\u5916\u4e00\u6307\uff0c\u8bf4\u9053\uff1a\u201c\u98de\u7ea2 \u5dfe\u5728\u4e0b\u9762\u7684\u5c71\u5cf0\u7ed3\u6237\u72ec\u4f4f\uff0c\u4f60\u8fd8\u6709\u9762\u6562\u53bb\u6c42\u89c1\u5979\u5417\uff1f\u201d\u6768\u4e91\u806a\u53eb\u4e86\u4e00\u58f0\uff0c\u8f6c\u5934\u4fbf\u8131\u8fde\u8981\u5411\u767d \u53d1\u9b54\u5973\u544a\u8f9e\u4e5f\u5fd8\u8bb0\u4e86\u3002\u767d\u53d1\u9b54\u5973\u7eb5\u58f0\u72c2\u7b11\uff0c\u5ffd\u53c8\u9893\u7136\u7684\u5012\u5728\u51b0\u4e0a\u3002\u6768\u4e91\u806a\u7684\u80cc\u5f71\u5df2\u7ecf\u4e0d\u89c1\u4e86\u3002 \u3000\u3000----------- \u3000\u3000\u98ce\u4e91\u9601 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u585e\u5916\u5947\u4fa0\u4f20\u300b\u2014\u2014\u7b2c\u4e8c\u5341\u516b\u56de\u3000\u676d\u5dde\u5927\u5a5a \u6881\u7fbd\u751f\u300a\u585e\u5916\u5947\u4fa0\u4f20\u300b \u7b2c\u4e8c\u5341\u516b\u56de\u3000\u676d\u5dde\u5927\u5a5a \u3000\u3000\u79cb\u5929\u7684\u9633\u5149\uff0c\u628a\u96ea\u5c71\u51b0\u5cf0\u3002\u8feb\u5c04\u8d77\u5343\u4e07\u9053\u971e\u8f89\u4e3d\u5f69\uff0c\u53ef\u662f\u6768\u4e91\u806a\u5df2\u65e0\u5fc3\u6b23\u8d4f\u8fd9\u4eba\u95f4\u96be\u89c1 \u7684\u5947\u666f\u4e86\uff0c\u4ed6\u6025\u7740\u8981\u53bb\u89c1\u98de\u7ea2\u5dfe\uff0c\u201c\u98de\u7ea2\u5dfe\u4f1a\u4e0d\u4f1a\u89c1\u6211\u5462\uff1f\u201d\u8fd9\u4e00\u4e2a\u95ee\u53f7\u8feb\u4f7f\u4ed6\u50cf\u65cb\u98ce\u4e00\u6837 \u7684\u79bb\u5f00\u767d\u53d1\u9b54\u5973\u3002\u7ec8\u4e8e\u4ed6\u5728\u5929\u5c71\u5357\u9ad8\u5cf0\u7684\u5c71\u9e93\uff0c\u627e\u5230\u4e86\u4e00\u95f4\u6728\u5c4b\uff0c\u91cc\u9762\u9690\u9690\u4f20\u51fa\u4e86\u68b5\u5457\u4e4b\u58f0\u3002 \u3000\u3000\u201c\u98de\u7ea2\u5dfe\uff0c\u98de\u7ea2\u5dfe\uff0c\u6211\u6765\u4e86\u554a\uff01\u8bf7\u4f60\u5f00\u95e8\u3002\u5f00\u95e8\uff01\u201d\u6768\u4e91\u806a\u7528\u529b\u62cd\u95e8\uff0c\u5927\u58f0\u53eb\u558a\u3002\u53ef\u662f \u91cc\u9762\u7684\u4eba\u6beb\u65e0\u53cd\u5e94\u3002\u6768\u4e91\u806a\u7740\u6025\u6781\u4e86\uff0c\u62fc\u8005\u53d7\u98de\u7ea2\u5dfe\u7684\u8d23\u9a82\uff0c\u521a\u8eab\u7ffb\u4e0a\u5c4b\u6a90\uff0c\u8df3\u843d\u5c4b\u5185\u3002\u5c4b \u5185\u9999\u70df\u71ce\u7edf\uff0c\u4e00\u4e2a\u5973\u4eba\u6b63\u8dc3\u5750\u84b2\u56e2\u4e4b\u4e0a\uff0c\u95ed\u76ee\u5ff5\u7ecf\uff0c\u5bf9\u5916\u9762\u7684\u7eb7\u627e\uff0c\u7adf\u662f\u89c6\u800c\u4e0d\u89c1\u3001\u542c\u800c\u4e0d \u95fb\u3002\u6768\u4e91\u806a\u4e00\u773c\u671b\u53bb\u3002\u5fc3\u7075\u5982\u53d7\u98ce\u66b4\u88ad\u51fb\uff0c\u987f\u65f6\u5446\u5f97\u8bf4\u4e0d\u51fa\u8bdd\u6765\uff01 \u3000\u3000\u8fd9\u662f\u98de\u7ea2\u5dfe\u5417\uff1f\u8fd9\u84b2\u56e2\u4e0a\u7684\u5973\u4eba\u96be\u9053\u5c31\u662f\u90a3\u4e2a\u660e\u6717\u8c6a\u8fc8\u7684\u8349\u539f\u5973\u82f1\u96c4\uff1f\u6768\u4e91\u806a\u51e0\u4e4e\u4e0d\u6562 \u76f8\u4fe1\u81ea\u5df1\u7684\u773c\u775b\u4e86\u3002\u84b2\u56e2\u4e0a\u7684\u5973\u4eba\u767d\u53d1\u98d8\u98d8\uff0c\u5728\u80cc\u540e\u770b\u6765\uff0c\u548c\u5e74\u8fd1\u53e4\u7a00\u7684\u767d\u53d1\u9b54\u5973\uff0c\u7adf\u662f\u4e00 \u6a21\u4e00\u6837\u3002\u96be\u9053\u4e00\u4e2a\u4e8c\u5341\u6765\u5c81\u7684\u5c11\u5973\uff0c\u5c45\u7136\u4f1a\u767d\u53d1\u6ee1\u5934\u3002 \u3000\u3000\u201c\u5594\uff01\u53ef\u601c\u7684\u98de\u7ea2\u5dfe\uff0c\u592a\u591a\u7684\u4f18\u60a3\u4f7f\u5979\u53d8\u6210\u4e86\u8fd9\u4e2a\u6a21\u6837\uff01\u201d\u6768\u4e91\u806a\u4e00\u9635\u98a4\u6817\uff0c\u8fd9\u970e\u90a3 \u95f4\uff0c\u98de\u7ea2\u5dfe\u8fc7\u53bb\u7684\u5f62\u8c61\u84e6\u6d8c\u5fc3\u5934\uff0c\u8349\u539f\u4e0a\u7684\u5e76\u8f94\u9a71\u9a70\uff0c\u53e4\u5821\u4e2d\u7684\u6b22\u6109\u8c08\u7b11\uff0c\u8fd9\u4e00\u4e9b\u90fd\u8fc7\u53bb \u4e86\uff0c\u4e0d\u4f1a\u518d\u5728\u76ee\u524d\u7684\u8fd9\u4f4d\u201c\u767d\u53d1\u5c11\u5973\u201d\u7684\u8eab\u4e0a\u51fa\u73b0\u4e86\u3002\u6768\u4e91\u806a\u6fc0\u52a8\u5f97\u51e0\u4e4e\u8981\u8ddf\u4e0a\u53bb\u62d6\u7740\u5979\u3001 \u5411\u5979\u672c\u6055\u3002\u53ef\u662f\u6c42\u5979\u539f\u8c05\u4ec0\u4e48\uff1f\u7eb3\u5170\u660e\u6167\u7684\u5f71\u5b50\u4e5f\u6d8c\u73b0\u51fa\uff0c\u81ea\u5df1\u548c\u660e\u610f\u4e5f\u5e76\u6ca1\u6709\u9519\u5440\uff0c\u611f\u60c5 \u4e0a\u7684\u8d1f\u503a\uff0e\u6709\u65f6\u771f\u662f\u8fd8\u4e0d\u6e05\u7684\uff01 \u3000\u3000\u98de\u7ea2\u5dfe\u4ecd\u7136\u662f\u5728\u4f4e\u58f0\u5ff5\u7ecf\uff0e\u6768\u4e91\u806a\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u98de\u7ea2\u5dfe\uff0c\u8349\u539f\u4e0a\u7684\u5144\u5f1f\u4eec\u8981\u4f60\u3002\u4f60\u548c\u6211 \u4e0b\u5c71\u5427\uff01\u6211\u4eec\u6c38\u8fdc\u662f\u6700\u597d\u6700\u597d\u7684\u670b\u53cb\uff01\u201d\u98de\u7ea2\u5dfe\u5934\u4e5f\u4e0d\u62ac\uff0c\u610f\u7ecf\u5ff5\u5f97\u66f4\u8d77\u52b2\uff0c\u6768\u4e91\u806a\u9690\u9690\u7ea6 \u7ea6\u7684\u542c\u5f97\u5979\u5ff5\u9053\uff1b\u201c\u4e16\u6cd5\u5982\u5e7b\u5982\u68a6\uff0c\u5982\u54cd\u5982\u5149\uff0c\u5982\u5f71\u5982\u5316\u3001\u5982\u6c34\u4e2d\u6ce1\uff0c\u5982\u955c\u4e2d\u50cf\uff0c\u5982\u70ed\u65f6 \u708e\uff0c\u5982\u6c34\u4e2d\u6708\uff0c\u662f\u4ee5\u8bf8\u6cd5\u65e0\u5e38\uff0c\u4e00\u5ff5\u5728\u6211\u2026\u2026\u6469\u8bc3\u822c\u82e6\u6ce2\u7f57\u5bc6\u3002\u201d\u8fd9\u662f\u5927\u4e58\u822c\u82e5\u7ecf\u7684\u7ecf \u6587\uff0e\u6768\u4e91\u806a\u53eb\u8d77\u6765\u9053\uff1a\u201c\u98de\u7ea2\u5dfe\u3001\u4f60\u600e\u4e48\u5566\uff1f\u8349\u539f\u4e0a\u94c1\u9a6c\u91d1\u6208\uff0c\u72fc\u70df\u5904\u5904\uff0c\u4f60\u5374\u8bf4\u4ec0\u4e48\u5982\u5e7b \u5982\u68a6\uff0e\u96be\u9053\u5728\u6d74\u8840\u6b7b\u6218\u7684\u4f60\u7684\u65cf\u4eba\uff0c\u5728\u4f60\u7684\u5fc3\u76ee\u4e2d\u3002\u4e5f\u662f\u4e00\u56e2\u7684\u5e7b\u5f71\uff1f\u98de\u7ea2\u5dfe\uff0c\u4e0d\u8981\u53d1\u50bb \u4e86\uff0c\u8ddf\u6211\u4e0b\u5c71\u53bb\u5427\uff01\u201d\u98de\u7ea2\u5dfe\u4ecd\u5982\u4e0d\u95fb\u4e0d\u89c1\uff0c\u8dcc\u5750\u84b2\u56e2\u4e4b\u4e0a\uff0c\u9664\u4e86\u5634\u76ae\u5fae\u5fae\u5f00\u5408\u4e4b\u5916\uff0c\u7b80\u76f4 \u5c31\u50cf\u53e4\u4ee3\u9057\u7559\u4e0b\u6765\u7684\u4e00\u5c0a\u77f3\u50cf\u3002 \u3000\u3000\u6768\u4e91\u806a\u5446\u7136\u7acb\u5728\u98de\u7ea2\u5dfe\u8eab\u8fb9\u3002\u4e0d\u77e5\u5982\u4f55\u662f\u597d\u3001\u8fc7\u4e86\u8bb8\u4e45\uff0c\u5ffd\u7136\u60f3\u8d77\u6765\u9053\uff1b\u201c\u98de\u7ea2\u5dfe\u5b64\u8eab \u9041\u8ff9\u5bcc\u5c71\uff0c\u96be\u9053\u8349\u539f\u4e0a\u7684\u6297\u4e89\uff0c\u5df2\u7ecf\u88ab\u6e05\u5175\u6251\u706d\u4e86\uff1f\u201d\u8fd9\u4e00\u60f3\uff0c\u4e0d\u7981\u51b7\u6c57\u5982\u80cc\uff0c\u5401\u53e3\u6c14\u9053\uff1a \u201c\u98de\u7ea2\u5dfe\uff0c\u6211\u6b64\u523b\u4e0d\u80fd\u5728\u8fd9\u91cc\u4f34\u4f60\u4e86\uff0c\u6211\u8fd8\u8981\u4e0b\u5c71\u53bb\u770b\u7740\u6211\u7684\u5f1f\u5144\u3001\u8fc7\u4e9b\u65f6\u5019\uff0c\u6211\u518d\u6765\u89c1 \u4f60\u3002\u201d\u6a2a\u8d77\u5fc3\u80a0\uff0c\u53c8\u8d8a\u5899\u8d70\u4e86\u3002\u98de\u7ea2\u5dfe\u542c\u5f97\u6768\u4e91\u806a\u5df2\u7ecf\u8d70\u8fdc\uff0c\u628a\u4f5b\u7ecf\u4e00\u629b\uff0c\u987f\u7136\u53f9\u9053\uff1a\u201c\u4f60 \u6c38\u8fdc\u4e0d\u4f1a\u518d\u89c1\u6211\u4e86\uff01\u201d \u3000\u3000\u8fd9\u4e2a\u65f6\u5019\uff0c\u7eb3\u5170\u660e\u6167\u4e5f\u6b63\u662f\u9eef\u7136\u9b42\u6d88\u65e0\u9650\u4f24\u5fc3\uff0c\u4ed6\u7684\u7236\u4eb2\u7eb3\u5170\u79c0\u5409\u88ab\u8c03\u4efb\u676d\u5dde\u603b\u5175\uff0c\u542c \u8bf4\u8fd9\u8fd8\u662f\u591a\u94ce\u7684\u4e3b\u610f\uff0c\u591a\u94ce\u65b0\u8fd1\u5347\u4efb\u4e24\u6c5f\u63d0\u7763\uff0c\u5e73\u5b9a\u4e86\u524d\u660e\u9c81\u4e3b\u7684\u9053\u90e8\u3002\u53c8\u627f\u88ad\u4e86\u9102\u4eb2\u738b\u7684 \u738b\u4f4d\uff0c\u771f\u662f\u559c\u4e8b\u91cd\u91cd\u3002\u5341\u5206\u5f97\u610f\uff0c\u5979\u4e0d\u60f3\u5230\u585e\u5916\u5b8c\u5a5a\uff0c\u4e5f\u4e0d\u60f3\u4e07\u91cc\u8fce\u4eb2\u5230\u4eac\u4e2d\u5b8c\u5a5a\u3002\u56e0\u6b64\u51ed \u7740\u81ea\u5df1\u7684\u804c\u4f4d\uff0c\u4f7f\u7d22\u6027\u628a\u7eb3\u5170\u79c0\u5409\u8c03\u5230\u676d\u5dde\u6765\uff0c\u5f53\u81ea\u5df1\u7684\u5c5e\u4e0b\u3002\u5728\u6c5f\u5357\u6842\u5b50\u98d8\u9999\u4e4b\u65e5\u5b8c\u5a5a\uff0c \u90a3\u53ef\u662f\u4eba\u751f\u4e00\u5927\u4e50\u4e8b\u3002\u7eb3\u5170\u79c0\u5409\u65e2\u662f\u5b97\u5ba4\uff0c\u53c8\u5728\u65b0\u7586\u79ef\u6709\u6218\u529f\uff1b\u8c03\u4efb\u603b\u5175\uff0c\u90a3\u4e5f\u662f\u7838\u7406\u6210\u7ae0 \u4e4b\u4e8b\u3002 \u3000\u3000\u53ef\u662f\u7eb3\u5170\u660e\u6167\u5374\u662f\u67d4\u80a0\u5bf8\u65ad\uff0c\u5979\u7231\u4e0a\u4e86\u65b0\u7586\u7684\u8349\u539f\uff0c\u56e0\u4e3a\u8349\u539f\u4e0a\u6709\u5979\u6240\u7231\u7684\u4eba\u3002\u90a3\u665a\u6768 \u4e91\u806a\u5728\u5c06\u519b\u5e9c\u5185\u5927\u95f9\u4e4b\u540e\uff0c\u5979\u4e00\u76f4\u7b49\u7740\u4ed6\u518d\u6765\uff0c\u53ef\u662f\u6768\u4e91\u806a\u5374\u4e0d\u89c1\u518d\u6765\u4e86\u3002\u5979\u6000\u7591\u6768\u4e91\u806a\u607c \u5979\u6068\u5979\uff0c\u4e0d\u613f\u518d\u89c1\u5979\u4e86\u3002\u201d\u96be\u9053\u4f60\u5c31\u4e00\u70b9\u4e5f\u4e0d\u80fd\u4f53\u4f1a\u6211\u7684\u82e6\u5fc3\uff1f\u201d\u7eb3\u5170\u660e\u6167\u5728\u4f4e\u4f4e\u7684\u57cb\u6028 \u4e86\u3002\u53ef\u662f\u6768\u4e91\u806a\u4eba\u4e0d\u89c1\u518d\u6765\u3002\u5012\u662f\u5979\u7684\u7236\u4eb2\u7a81\u7136\u4ece\u5916\u9762\u56de\u6765\uff0c\u8ddf\u7740\u4fbf\u662f\u4e3e\u5bb6\u524d\u8fd4\u4e86\u3002 \u3000\u3000\u6768\u4e91\u806a\u4e0b\u4e86\u5929\u5c71\uff0c\u8349\u539f\u4e0a\u7684\u666f\u8c61\u5df2\u4e0e\u4ee5\u524d\u5927\u5927\u4e0d\u540c\uff0c\u6e05\u519b\u7684\u8425\u5e10\uff0c\u968f\u5904\u53ef\u89c1\u3002\u5404\u65cf\u7684\u6218 \u58eb\u4eec\uff0c\u5374\u5df2\u6d41\u6563\u56db\u65b9\uff0c\u6216\u85cf\u8d77\u5200\u67aa\uff0c\u6d88\u58f0\u533f\u8ff9\u4e86\u3002\u539f\u6765\u5c31\u5728\u4ed6\u5929\u5c71\u6765\u53bb\u7684\u4e00\u4e2a\u6708\u4e2d\u3002\u5357\u7586\u8349 \u539f\u4e0a\u7684\u6297\u6e05\u6b66\u88c5\uff0c\u53d7\u5230\u6781\u5927\u7684\u6253\u51fb\u3002\u5317\u7586\u7684\u6e05\u5175\u5728\u7edf\u6cbb\u5de9\u56fa\u4e4b\u540e\uff0c\u8c03\u5175\u5357\u6765\uff0c\u800c\u591a\u94ce\u4e5f\u8bf7\u51c6 \u7687\u4e0a\uff0c\u628a\u9752\u6d77\u9a7b\u5c6f\u519b\u7684\u9a91\u5175\u90fd\u8c03\u5230\u65b0\u7586\uff0e\u800c\u53e6\u4e00\u65b9\u9762\uff0c\u5580\u5c14\u6c81\u8349\u539f\u4e0a\uff0c\u81ea\u98de\u7ea2\u5dfe\u7387\u90e8\u79bb\u53bb \u540e\uff0c\u5b9e\u529b\u4e5f\u8f83\u524d\u5355\u8584\u3002\u7adf\u7ed9\u6e05\u519b\u5404\u4e2a\u51fb\u7834\u4e86\u3002 \u3000\u3000\u6768\u4e91\u806a\u5343\u8f9b\u4e07\u82e6\uff0c\u5728\u8349\u539f\u4e0a\u627e\u5230\u4e86\u4ed6\u7684\u4e24\u4e2a\u76df\u5144\u5f1f\uff0c\u4f0a\u58eb\u8fbe\u548c\u9ea6\u76d6\u63d0\uff0c\u95ee\u6e05\u60c5\u51b5\u3001\u597d\u4e86 \u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u4eca\u540e\u7684\u4e8b\uff0c\u5168\u9760\u4f60\u4eec\u4e86\uff0e\u6211\u8981\u79bb\u5f00\u65b0\u7586\u4e00\u8d9f\uff0e\u201d\u4ed6\u98d8\u7136\u6f5c\u5165\u4f0a\u7281\uff0c\u60f3\u548c\u7eb3\u5170 \u660e\u6167\u89c1\u6700\u540e\u4e00\u9762\uff0c\u95ee\u5979\u613f\u4e0d\u613f\u548c\u81ea\u5df1\u51fa\u5954\uff0c\u4ed6\u6253\u7b97\u4e0d\u8bba\u7eb3\u5170\u660e\u6167\u524d\u4e0d\u80af\u548c\u81ea\u5df1\u540c\u884c\uff0c\u4ed6\u90fd\u8981 \u8fdb\u5173\u5185\u4e86\uff0e\u5173\u5185\u6709\u4ed6\u7684\u8001\u8c61\uff0c\u800c\u4e14\u542c\u8bf4\u5728\u6d59\u6c5f\u5357\u90e8\u8fd8\u6709\u660e\u671d\u7687\u5ba4\u9c81\u738b\u7684\u65e7\u90e8\uff0c\u5728\u6e56\u5317\u8fd8\u6709\u674e \u95ef\u738b\u513f\u5b50\u674e\u9526\u7684\u5927\u519b\u3001\u5230\u4e86\u6c5f\u5357\uff0c\u4e5f\u8bb8\u8fd8\u53ef\u505a\u756a\u4e8b\u4e1a\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u6765\u5f97\u8fdf\uff0c\u7eb3\u5170\u660e\u6167\u5df2\u7ecf\u8d70\u4e86\u597d\u591a\u5929\u4e86\u3002\u4ed6\u6765\u5230\u4e86\u4f0a\u7281\uff0c\u624d\u77e5\u4f0a\u7281\u5c06\u519b\u5df2\u7ecf\u6362\u4e86 \u4eba\u3002\u4ed6\u8f93\u5165\u5c06\u519b\u5e9c\u770b\u770b\u6709\u6ca1\u6709\u4ee5\u524d\u7684\u719f\u4eba\u3002\u4e00\u8fde\u53bb\u4e86\u4e09\u665a\u3002\u624d\u53d1\u73b0\u90a3\u4e2a\u5976\u5988\u7684\u4f84\u513f\u8fd8\u7559\u5728\u5c06 \u519b\u5e9c\u91cc\u5f53\u5dee\u3001\u5728\u6df1\u4eba\u9759\u4e4b\u540e\uff0c\u4ed6\u5077\u5077\u5c06\u8fd9\u4e2a\u50bb\u5c0f\u5b50\u5524\u9192\uff0c\u67e5\u95ee\u4ed6\u5c0f\u59d0\u7684\u4e0b\u843d\u3002\u8fd9\u4f7f\u5c0f\u5b50\u5012\u4e0d \u5bb3\u6015\uff0c\u8fde\u9f13\u7740\u5634\u9a82\u4ed6\u9053\uff1b\u201c\u6211\u4eec\u7684\u5c0f\u59d0\u8981\u5230\u5357\u8fb9\u5b8c\u5a5a\u4e86\uff0c\u4f60\u8fd8\u627e\u5979\u505a\u4ec0\u4e48\uff1f\u6211\u7684\u59d1\u59d1\u4e34\u8d70\u524d \u5429\u5490\u6211\u3002\u5982\u679c\u6211\u78b0\u5230\u4f60\uff0c\u5c31\u8981\u6211\u8bf4\u7ed9\u4f60\u77e5\u9053\uff0c\u5bf9\u4f60\u5343\u4e07\u522b\u518d\u7f20\u6211\u4eec\u7684\u5c0f\u59d0\u4e86\u3002\u201d\u6768\u4e91\u806a\u4e00 \u542c\uff0c\u5982\u6674\u5929\u9739\u96f3\uff0c\u6025\u5fd9\u95ee\u9053\uff1a\u201c\u5c0f\u59d0\u56de\u5230\u54ea\u91cc\u5b8c\u5a5a\uff1f\u201d\u50bb\u5c0f\u5b50\u9053\uff1a\u201c\u676d\u5dde\uff01\u201d\u6768\u4e91\u806a\u72c2\u7b11\u4e00 \u58f0\uff0c\u8f6c\u8eab\u4fbf\u8dd1\u4e86\u3002 \u3000\u3000\u8fd9\u665a\u4ed6\u5f7b\u591c\u672a\u7720\uff0c\u60c5\u601d\u6c79\u6d8c\uff0c\u4e0d\u89c9\u63d0\u8d77\u7b14\u6765\uff0c\u586b\u4e86\u4e00\u9996\u8bcd\u9053\uff1a \u3000\u3000\u201c\u7b11\u6c5f\u6e56\u6d6a\u8ff9\u5341\u5e74\u6e38\uff0c\u7a7a\u8d1f\u5c11\u5e74\u5934\uff0c\u5bf9\u94dc\u9a7c\u5df7\u964c\uff0c\u541f\u60c5\u6e3a\u6e3a\uff0c\u5fc3\u4e8b\u60a0\u60a0\uff0c\u9152\u9192\u8bd7\u6b8b\u68a6 \u65ad\uff0c\u5357\u56fd\u6b63\u6e05\u79cb\u3002\u628a\u5251\u51c4\u7136\u671b\uff0c\u65e0\u5904\u62db\u5f52\u821f\u3002 \u3000\u3000\u660e\u65e5\u5929\u6daf\u8def\u8fdc\uff0c\u95ee\u8c01\u7559\u695a\u4f69\uff0c\u7559\u5f71\u4e2d\u5dde\uff1f\u6570\u82f1\u96c4\u513f\u5973\uff0c\u4fef\u4ef0\u53e4\u4eca\u6101\u3002\u96be\u6d88\u53d7\u706f\u660f\u7f57\u5e10\uff0c \u6005\u6619\u82b1\u4e00\u73b0\u6068\u96be\u4f11\uff01\u98d8\u96f6\u6124\uff0c\u91d1\u6208\u94c1\u9a6c\uff0c\u62fc\u846c\u8352\u4e18\uff01\u201d\u2014\u2014\u8c03\u5bc4\u516b\u58f0\u7518\u5dde\u3002 \u3000\u3000\u8bcd\u6210\u9152\u51b7\uff0c\u5929\u5df4\u9ece\u660e\uff0c\u4ed6\u8de8\u4e0a\u9a8f\u9a6c\uff0c\u7edd\u5c18\u800c\u53bb\uff0e\u81ea\u6b64\u8349\u539f\u4e0a\u4e0d\u518d\u89c1\u6768\u4e91\u806a\u7684\u8e2a\u5f71\uff0c\u53ea\u6709 \u4ed6\u7684\u82f1\u96c4\u6545\u4e8b\uff0c\u88ab\u8349\u539f\u7684\u6b4c\u624b\u7f16\u6210\u8bd7\u6b4c\uff0c\u6c38\u8fdc\u7559\u5728\u6c11\u95f4\u3002 \u3000\u3000\u4e00\u4e2a\u6708\u540e\uff0c\u4ed6\u5230\u4e86\u676d\u5dde\uff0c\u90a3\u65f6\u6b63\u662f\u4e2d\u79cb\u65b9\u8fc7\uff0c\u94b1\u5858\u6c5f\u5927\u6f6e\u5c31\u8981\u6765\u7684\u65f6\u5019\u3002\u676d\u5dde\u57ce\u5185\uff0c\u4eba \u5c71\u4eba\u6d77\uff0c\u70ed\u95f9\u5f02\u5e38\u3002\u8fd9\u4e9b\u4eba\u6709\u4e9b\u662f\u6765\u770b\u6f6e\u7684\u3002\u6709\u4e9b\u662f\u6765\u770b\u9102\u4eb2\u738b\u591a\u94ce\u6210\u5a5a\u5927\u5178\u7684\u3002\u591a\u94ce\u548c\u7eb3 \u5170\u660e\u6167\u7684\u5a5a\u671f\u5df2\u5b9a\u4e86\u5341\u516b\u65e5\u4e3e\u884c\uff0c\u6768\u4e91\u806a\u6b63\u597d\u5728\u4ed6\u4eec\u7684\u5a5a\u524d\u4e09\u5929\u8d76\u5230\u3002\u6b63\u662f\u4e07\u91cc\u5f52\u6765\u4eba\u672a \u8001\uff0c\u9999\u8f66\u5374\u6b32\u5165\u4faf\u95e8\u3002\u6b32\u77e5\u6768\u4e91\u806a\u662f\u5426\u7518\u5fc3\u8ba9\u7eb3\u5170\u660e\u6167\u51fa\u5ac1\u3002 \u3000\u3000\uff08\u5168\u4e66\u5b8c\uff0c\u8bf7\u7eed\u770b\u300a\u4e03\u5251\u4e0b\u5929\u5c71\u300b\uff09"}),showOnHomepage:!0}]}}]);