"use strict";(self.webpackChunkwebsite=self.webpackChunkwebsite||[]).push([[65007],{28503:(e,n,s)=>{s.r(n),s.d(n,{default:()=>a});var t=s(74848);const a=[{name:"\u5251\u6c14\u6d1e\u5f7b\u4e5d\u91cd\u5929",content:(0,t.jsx)(t.Fragment,{children:"\u7b2c \u4e00 \u56de\u3000\u8a93\u540c\u5fc3\u63a2\u79d8\u6d1b\u9633\u5c45 \u3000 \u3000\u3000\u7eb7\u6270\u4e0d\u606f\u7684\u6c5f\u6e56\uff0c\u4e00\u4e0b\u5b50\u5e73\u9759\u4e86\u4e0b\u6765\uff0c\u800c\u4e14\uff0c\u5e73\u9759\u4e86\u4e8c\u5341\u5e74\uff0c\u8fd9\u771f\u662f\u4e00\u6bb5\u5b89\u95f2\u7684\u5c81\u6708\uff0c\u4e5f\u662f\u6c5f\u6e56\u4e0a\u4ece\u672a\u6709\u8fc7\u7684\u5b81\u9759\u65e5\u5b50\u2026\u2026\u90a3\u662f\u4e09\u4f4d\u8eab\u8d1f\u7edd\u4e16\u6b66\u529f\uff0c\u5fc3\u6000\u5927\u613f\u7684\u9ad8\u4eba\uff0c\u7b56\u5212\u4e86\u4e09\u5e74\uff0c\u53c8\u8bf4\u52a8\u4e86\u5c11\u6797\u3001\u6b66\u5f53\u4e24\u5927\u95e8\u6d3e\u5168\u529b\u76f8\u52a9\uff0c\u624d\u5728\u4e00\u6b21\u6b66\u6797\u540c\u9053\u7684\u5927\u805a\u4f1a\u4e2d\uff0c\u4e00\u4e3e\u94f2\u9664\u4e86\u6c5f\u6e56\u4e0a\u6700\u6076\u6bd2\u7684\u56db\u5927\u51f6\u715e\u548c\u5341\u4e8c\u90aa\u795e\uff0c\u4e5f\u9547\u4f4f\u4e86\u80f8\u6000\u91ce\u5fc3\u7684\u5404\u65b9\u8c6a\u96c4\uff0c\u8ba2\u7acb\u4e86\u4e00\u5206\u4e25\u683c\u7684\u7ea6\u5b9a\u3002\u4e00\u4f53\u9075\u5b88\uff0c\u4e0d\u5f97\u903e\u8d8a\u3002 \u3000\u3000\u5927\u4f1a\u4e0a\uff0c\u6ca1\u6709\u63a8\u4e3e\u4e3b\u6301\u6b66\u6797\u4e8b\u52a1\u7684\u76df\u4e3b\uff0c\u56e0\u4e3a\uff0c\u4ed6\u4eec\u53d1\u89c9\u4e86\uff0c\u8bbe\u5b9a\u7684\u6b66\u6797\u76df\u4e3b\u4e4b\u4f4d\uff0c\u4f1a\u5f15\u53d1\u5f88\u591a\u6c5f\u6e56\u8c6a\u96c4\u5fc3\u4e2d\u7684\u4e0d\u5e73\uff0c\u751f\u51fa\u53d6\u4ee3\u4e4b\u5fc3\uff0c\u53cd\u6210\u4e3a\u6c5f\u6e56\u4e0a\u7978\u4e71\u4e4b\u6e90\u3002 \u3000\u3000\u56e0\u6b64\uff0c\u5404\u95e8\u6d3e\u90fd\u7ef4\u62a4\u4e86\u539f\u6709\u7684\u5f62\u8c8c\uff0c\u8fd9\u4f7f\u6bcf\u4e2a\u95e8\u6d3e\u9996\u8111\u4eba\u7269\u7684\u5fc3\u4e2d\uff0c\u90fd\u83b7\u5f97\u4e86\u4e00\u4efd\u5e73\u9759\u3002 \u3000\u3000\u5927\u4f1a\u5728\u4e5d\u534e\u5c71\u4e3e\u884c\uff0c\u8fd9\u4efd\u8ba2\u4e0b\u7684\u7ea6\u5b9a\uff0c\u5c31\u79f0\u8c13\u201c\u4e5d\u534e\u76df\u7ea6\u201d\u3002\u201c\u4e5d\u534e\u76df\u7ea6\u201d\u7adf\u7136\u7ef4\u6301\u4e86\u6c5f\u7322\u4e0a\u4e8c\u5341\u5e74\u7684\u5b89\u9759\u5c81\u6708\u3002 \u3000\u3000\u4f46\u90a3\u4e09\u4f4d\u7b56\u5212\u6b66\u6797\u540c\u9053\u5927\u4f1a\u7684\u9ad8\u4eba\uff0c\u5374\u5728\u5927\u4f1a\u4e4b\u540e\u5931\u8e2a\u4e86\u3002 \u3000\u3000\u591a\u5c11\u4ef0\u6155\u4ed6\u4eec\u7684\u6b66\u6797\u540e\u8d77\u4e4b\u79c0\uff0c\u8e0f\u904d\u4e86\u5343\u5c71\u4e07\u6c34\uff0c\u5e0c\u671b\u80fd\u4e00\u7779\u98ce\u91c7\u3002\u6c42\u6559\u7edd\u5b66\u3002\u4f46\u4e09\u4f4d\u9ad8\u4eba\u50cf\u662f\u6ef4\u5165\u5927\u6d77\u7684\u6c34\u73e0\uff0c\u518d\u4e5f\u627e\u4e0d\u5230\u4e00\u70b9\u75d5\u8ff9\u3002 \u3000\u3000\u4e5f\u8bb8\u4ed6\u4eec\u8fd8\u6d3b\u5728\u8fd9\u4e2a\u4e16\u754c\u4e0a\uff0c\u53ea\u662f\u6ca1\u6709\u4eba\u80fd\u627e\u5230\u4ed6\u4eec\u3002 \u3000\u3000\u4e8e\u662f\u3002\u6c5f\u6e56\u4e0a\u5f00\u59cb\u4e86\u7eb7\u575b\u7684\u4f20\u8bf4\u3002 \u3000\u3000\u6700\u5408\u4e4e\u60c5\u7406\uff0c\u800c\u53c8\u6d41\u884c\u6700\u5e7f\u7684\u4f20\u8bf4\u6709\u4e09\u4e2a\u3002 \u3000\u3000\u7b2c\u4e00\u4e2a\u662f\uff1a\u4ed6\u4eec\u867d\u7136\u640f\u6740\u4e86\u56db\u5927\u51f6\u715e\u3001\u5341\u4e8c\u90aa\u795e\u3002\u4f46\u4ed6\u4eec\u4e09\u4e2a\u4eba\u3002\u4e5f\u53d7\u4e86\u5f88\u91cd\u7684\u5185\u4f24\u3002 \u3000\u3000\u5f53\u573a\u4ee5\u5185\u529f\u903c\u4f4f\u4f24\u52bf\uff0c\u4e0d\u4f7f\u53d1\u4f5c\u3002\u5927\u4f1a\u4e4b\u540e\uff0c\u4f24\u53d1\u800c\u4ea1\u3002 \u3000\u3000\u7b2c\u4e8c\u4e2a\u4f20\u8bf4\u662f\uff1a\u4ed6\u4eec\u5f00\u62d3\u4e86\u6c5f\u6e56\u4e0a\u4ece\u672a\u6709\u8fc7\u7684\u6e05\u5e73\u4e16\u754c\u3002\u5fc3\u613f\u5df2\u4e86\uff0c\u5f52\u9690\u4e8e\u6df1\u5c71\u5927\u6cfd\uff0c\u8ffd\u6c42\u4ed9\u7f18\u5927\u9053\uff0c\u4e0d\u518d\u7406\u4f1a\u5c18\u4e16\u4e0a\u7684\u7eb7\u4e89\u3002 \u3000\u3000\u7b2c\u4e09\u4e2a\u662f\uff1a\u4ed6\u4eec\u51b3\u5fc3\u8981\u7ef4\u62a4\u201c\u4e5d\u534e\u76df\u7ea6\u201d\uff0c\u4e0d\u51c6\u6709\u4eba\u80cc\u5f03\u3002 \u3000\u3000\u6613\u5bb9\u6539\u88c5\uff0c\u5de1\u884c\u5929\u4e0b\uff0c\u6697\u91cc\u67e5\u8bbf\u8fdd\u7ea6\u80cc\u76df\u7684\u4eba\uff0c\u51fa\u624b\u6b7c\u706d\u2026\u2026\u53ef\u80fd\u662f\u7b2c\u4e09\u4e2a\u4f20\u8bf4\u7684\u9707\u9a87\uff0c\u4f7f\u6574\u4e2a\u6c5f\u6e56\u4e0a\u5e73\u9759\u4e86\u4e8c\u5341\u5e74\u3002 \u3000\u3000\u4f46\uff0c\u6c5f\u6e56\u4e0a\u771f\u6b63\u7684\u5e73\u9759\u4e86\u5417\uff1f\u96ea\u5c01\u5927\u5730\uff0c\u4e00\u7247\u94f6\u767d\uff0c\u897f\u98ce\u5982\u526a\uff0c\u523a\u9aa8\u751f\u5bd2\u3002 \u3000\u3000\u9102\u897f\u6469\u5929\u5cad\u7edd\u5cf0\u4e4b\u9876\uff0c\u5bd2\u98ce\u7684\u51db\u70c8\uff0c\u5c24\u5f3a\u5341\u500d\uff0c\u6df1\u6da7\u5947\u5cf0\u7684\u666f\u7269\uff0c\u4e5f\u5168\u4e3a\u5927\u96ea\u8986\u76d6\uff0c\u9664\u4e86\u5076\u800c\u53ef\u89c1\u7684\u4e00\u4e24\u53ea\u82cd\u9e70\u63a0\u7a7a\u800c\u8fc7\u5916\uff0c\u8fde\u8d70\u517d\u98de\u79bd\uff0c\u4e5f\u90fd\u8eb2\u5728\u5de2\u7a74\u4e2d\u3002 \u3000\u3000\u5947\u602a\u7684\u662f\u6469\u5929\u5cad\u7684\u5cf0\u9876\u4e0a\uff0c\u7adf\u7136\u7ad9\u4e86\u5f88\u591a\u7684\u4eba\uff0c\u4e24\u6392\u76f8\u5bf9\u3002\u5404\u6709\u5341\u4eba\u3002 \u3000\u3000\u4ed6\u4eec\u867d\u7136\u90fd\u7a7f\u7740\u68c9\u8884\u3002\u68c9\u88e4\uff0c\u4f46\u90fd\u5c3d\u91cf\u624e\u7ed3\u7684\u8d34\u8eab\u5229\u843d\uff0c\u8863\u670d\u7684\u989c\u8272\uff0c\u4e5f\u6e05\u695a\u5206\u660e\uff0c\u4e00\u65b9\u6d45\u84dd\u3001\u4e00\u65b9\u94f6\u767d\u3002 \u3000\u3000\u4f46\u90fd\u5df2\u88ab\u98d8\u843d\u7684\u96ea\u82b1\u63a9\u906e\u53bb\u5341\u4e4b\u4e03\u516b\uff0c\u5bd2\u98ce\u5439\u98de\u8d77\u8eab\u4e0a\u4e00\u7247\u79ef\u96ea\uff0c\u7acb\u523b\u53c8\u5e26\u6765\u53e6\u4e00\u7247\u96ea\u82b1\u8865\u4e0a\u3002 \u3000\u3000\u8fdc\u8fdc\u671b\u53bb\u3002\u4f3c\u662f\u88ab\u5806\u8d77\u7684\u96ea\u4eba\u3002 \u3000\u3000\u4ed6\u4eec\u4f69\u7740\u5175\u5203\u3001\u84dd\u8863\u7684\u4f69\u5251\uff0c\u767d\u8863\u7684\u4f69\u5200\uff0c\u770b\u4e0a\u53bb\u4f3c\u90fd\u662f\u6b66\u6797\u4e2d\u4eba\u3002 \u3000\u3000\u662f\u7684\uff01 \u3000\u3000\u4ed6\u4eec\u90fd\u662f\u6b66\u6797\u4e2d\u4eba\uff0c\u800c\u4e14\uff0c\u90fd\u662f\u5185\u5916\u517c\u4fee\u7684\u9ad8\u624b\u3002 \u3000\u3000\u5982\u82e5\u4e0d\u662f\u6709\u6df1\u539a\u7684\u6b66\u529f\u57fa\u7840\uff0c\u5c31\u7b97\u7a7f\u7740\u91cd\u88d8\uff0c\u4e5f\u8010\u4e0d\u4f4f\u8fd9\u5c71\u9876\u5bd2\u98ce\uff0c\u65e9\u5c31\u88ab\u51bb\u50f5\u800c\u6b7b\u4e86\u3002 \u3000\u3000\u4f46\u8fd9\u4e9b\u4eba\u3002\u90fd\u8fd8\u7b14\u76f4\u7684\u633a\u7acb\u7740\u3002 \u3000\u3000\u4e00\u58f0\u51b7\u7b11\u8fc7\u540e\u3002\u54cd\u8d77\u4e00\u4e2a\u6e05\u6717\u7684\u58f0\u97f3\uff0c\u9053\uff1a\u201c\u80e1\u5144\uff0c\u8be5\u662f\u8fc7\u5348\u7684\u65f6\u523b\u4e86\uff1f\u201d \u3000\u3000\u201c\u55ef\uff01\u4e5f\u8be5\u8131\u4e0b\u8863\u670d\u4e86\uff1f\u201d \u3000\u3000\u559d\uff01\u7adf\u7136\u8fd8\u6709\u4e24\u4e2a\uff0c\u4ed6\u4eec\u76d8\u819d\u800c\u5750\uff0c\u5927\u96ea\u63a9\u53bb\u5927\u90e8\u5206\u7684\u8eab\u8eaf\u3002 \u3000\u3000\u8fd9\u4e24\u4e2a\u5e74\u9f84\u90fd\u5df2\u662f\u56db\u5341\u51e0\u5c81\u4e86\uff0c\u80f8\u524d\u98d8\u52a8\u7740\u957f\u9aef\u3002 \u3000\u3000\u7b2c\u4e00\u4e2a\u8bf4\u8bdd\u7684\u4eba\uff0c\u970d\u7136\u7ad9\u8d77\uff0c\u6296\u843d\u4e00\u8eab\u79ef\u96ea\uff0c\u9732\u51fa\u4e00\u8eab\u94f6\u767d\u8863\u670d\uff0c\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u80e1\u5144\u4e45\u5c45\u5317\u56fd\uff0c\u4e0d\u754f\u98ce\u96ea\uff0c\u53ef\u662f\u8ba4\u4e3a\u5144\u5f1f\u548c\u95e8\u4e0b\u5b50\u5f1f\uff0c\u6321\u4e0d\u4f4f\u8fd9\u70b9\u98ce\u96ea\u4e4b\u5bd2\u5417\uff1f\u201d \u3000\u3000\u201c\u6bb5\u5144\uff0c\u8a00\u91cd\u4e86\uff0c\u80e1\u67d0\u76f8\u4fe1\u6bb5\u5144\u548c\u95e8\u4e0b\u5f1f\u5b50\uff0c\u4e2a\u4e2a\u5185\u529f\u7cbe\u6df1\u3002\u533a\u533a\u4e00\u70b9\u98ce\u96ea\uff0c\u5c82\u80fd\u4f24\u5230\u4ed6\u4eec\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u8fd9\u5c31\u8981\u80e1\u5144\u5f00\u5f00\u773c\u754c\u4e86\uff0c\u513f\u90ce\u4eec\uff01\u8131\u4e0b\u8863\u670d\uff0e\u770b\u770b\u6469\u5929\u5cad\u4e0a\u7684\u5bd2\u98ce\u5927\u96ea\uff0c\u80fd\u4e0d\u80fd\u51bb\u6b7b\u5357\u5200\u95e8\u4e0b\u7684\u4eba\u3002\u201d \u3000\u3000\u4ed6\u53e3\u4e2d\u8bf4\u770b\u8bdd\uff0c\u4eba\u5374\u662f\u9886\u5934\u884c\u52a8\uff0c\u53cc\u624b\u6325\u52a8\uff0c\u89e3\u5e26\u5bbd\u8863\uff0c\u7247\u523b\u4e4b\u95f4\u8863\u88e4\u5c3d\u9664\uff0c\u53ea\u7559\u4e0b\u4e00\u6761\u77ed\u88e4\uff0c\u548c\u811a\u4e0a\u7684\u9774\u5b50\u3002 \u3000\u3000\u5341\u4e2a\u767d\u8863\u4eba\uff0c\u4eff\u6548\u884c\u52a8\uff0c\u8131\u4e0b\u68c9\u7946\u3001\u68c9\u88e4\uff0c\u4e0a\u8eab\u88f8\u5149\uff0c\u4f46\u5374\u6293\u8d77\u4f69\u5e26\u7684\u957f\u5200\uff0c\u63d0\u5728\u624b\u4e2d\u3002 \u3000\u3000\u5bf9\u9762\u7ad9\u7acb\u7684\u84dd\u8863\u4eba\uff0c\u4e5f\u5f00\u59cb\u89e3\u53bb\u8863\u5e26\uff0c\u8131\u53bb\u4e0a\u8863\u68c9\u88e4\uff0c\u4e5f\u6293\u8d77\u4e86\u4f69\u5e26\u7684\u957f\u5251\u3002 \u3000\u3000\u8001\u5c11\u4e8c\u5341\u4e8c\u4e2a\u4eba\uff0c\u4e00\u822c\u6a21\u6837\uff0c\u5149\u7740\u4e0a\u8eab\uff0c\u88f8\u7740\u53cc\u817f\uff0c\u7ad9\u7acb\u5728\u98ce\u96ea\u4e2d\u3002 \u3000\u3000\u8fd9\u4e9b\u4eba\u662f\u4e0d\u662f\u75af\u4e86\uff1f\u5728\u51bb\u6b7b\u4eba\u7684\u5927\u98ce\u96ea\u4e2d\u7ad9\u4e86\u534a\u5929\uff0c\u8fd8\u5acc\u4e0d\u8fc7\u763e\u7adf\u7136\u8131\u4e0b\u4e86\u8863\u670d\u3001\u8fce\u7740\u523a\u9aa8\u5bd2\u98ce\uff0c\u9e45\u6bdb\u5927\u96ea\u3002 \u3000\u3000\u4e24\u4e2a\u8001\u7684\u66f4\u7edd\uff0c\u62c2\u62ed\u4e00\u4e0b\u957f\u9aef\u4e0a\u7684\u79ef\u96ea\uff0c\u7adf\u7136\u76d8\u819d\u5750\u4e86\u4e0b\u53bb\uff0c\u5750\u5728\u76c8\u5c3a\u7684\u5927\u96ea\u4e0a\u3002 \u3000\u3000\u4e56\u4e56\uff0c\u53ea\u90a3\u4e48\u4e00\u523b\u5de5\u592b\uff0c\u8fd9\u4e9b\u4eba\u7684\u8eab\u4e0a\uff0c\u5df2\u5e03\u6ee1\u4e86\u96ea\u7247\u3002 \u3000\u3000\u8bf4\u4ed6\u4eec\u4e0d\u51b7\uff0c\u5f88\u96be\u53eb\u4eba\u76f8\u4fe1\u3002 \u3000\u3000\u56e0\u4e3a\uff0c\u4ed6\u4eec\u7684\u8eab\u8eaf\u5fae\u5fae\u7684\u98a4\u52a8\u7740\uff0c\u5634\u5507\u4e5f\u51bb\u7684\u53d1\u9752\uff0c\u4f46\u4ed6\u4eec\u80fd\u5fcd\u8010\uff0c\u6ca1\u6709\u4e00\u4e2a\u5012\u4e0b\u53bb\uff0c\u4e5f\u6ca1\u6709\u4e00\u4e2a\u4eba\u53eb\u51fa\u4e00\u4e2a\u51b7\u5b57\u3002 \u3000\u3000\u4ed6\u4eec\u62b5\u6297\u5bd2\u51b7\u7684\u529e\u6cd5\u53ea\u6709\u4e00\u4e2a\uff0c\u63d0\u805a\u4e39\u7530\u771f\u6c14\uff0c\u8fd0\u96c6\u529f\u529b\u3002 \u3000\u3000\u548c\u9177\u5bd2\u6297\u62d2\u3002 \u3000\u3000\u8fd9\u662f\u4e00\u573a\u522b\u5f00\u751f\u9762\u7684\u5bf9\u51b3\u3002 \u3000\u3000\u5357\u5200\u3001\u5317\u5251\uff0c\u5404\u9009\u5341\u540d\u95e8\u4e0b\u6b66\u529f\u6700\u9ad8\u7684\u5f1f\u5b50\uff0c\u4f5c\u8fd9\u573a\u75af\u72c2\u7684\u5bf9\u51b3\u3002 \u3000\u3000\u4ed6\u4eec\u4f69\u5e26\u5200\u5251\uff0c\u5374\u4e0d\u7528\u5200\u3001\u5251\u640f\u6740\uff0c\u8981\u5728\u814a\u6708\u7684\u5927\u98ce\u96ea\u4e2d\uff0c\u5958\u5929\u5cad\u7edd\u5cf0\u4e4b\u9876\uff0c\u8131\u5149\u8863\u670d\uff0c\u6bd4\u8d5b\u5fcd\u8010\u5bd2\u51b7\u7684\u672c\u9886\u3002 \u3000\u3000\u5200\u3001\u5251\u53ef\u4ee5\u6740\u4eba\uff0c\u51bb\u6b7b\u7684\u6ecb\u5473\uff0c\u7edd\u4e0b\u6bd4\u5200\u3001\u5251\u6740\u6b7b\u597d\u8fc7\u3002 \u3000\u3000\u4eba\uff01\u8822\u8d77\u6765\uff0c\u5b9e\u5728\u53ef\u6015\uff0c\u7edd\u4e0d\u6bd4\u4e00\u822c\u52a8\u7269\u806a\u660e\uff01 \u3000\u3000\u53ef\u662f\uff0c\u4eba\u80fd\u60f3\u5230\uff0c\u4eba\u4f1a\u6790\u78e8\u522b\u4eba\uff0c\u4e5f\u4f1a\u6298\u78e8\u81ea\u5df1\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u4eba\u4e5f\u4f1a\u60f3\u51fa\u5f88\u591a\u7406\u7531\u89e3\u91ca\u4ed6\u4eec\u7684\u611a\u8822\u884c\u4e3a\uff0c\u4ed6\u4eec\u7834\u574f\u4e86\u522b\u4eba\u7684\u5e78\u798f\u3002\u7f8e\u597d\u3002\u5374\u53c8\u80fd\u8bf4\u51fa\u4e00\u756a\u52a8\u542c\u7684\u7406\u7531\u3002 \u3000\u3000\u5357\u5200\u6bb5\u4e5d\uff0c\u5317\u5251\u80e1\u840d\uff0c\u5c31\u662f\u8fd9\u4f6f\u7684\u4eba\u3002 \u3000\u3000\u4ed6\u4eec\u5343\u91cc\u8fe2\u8fe2\u7684\u8dd1\u4e0a\u6469\u5929\u5cad\uff0c\u6765\u6bd4\u8d5b\u5fcd\u53d7\u5927\u81ea\u7136\u5b55\u80b2\u51fa\u6765\u7684\u9177\u5bd2\u98ce\u96ea\u3002 \u3000\u3000\u51db\u51bd\u7684\u5bd2\u98ce\uff0c\u50cf\u523a\u9aa8\u7684\u94a2\u9488\uff0c\u88c2\u80a4\u7684\u5229\u5203\u3002\u6765\u81ea\u6c5f\u5357\u7684\u5357\u5200\u95e8\u5f1f\u5b50\uff0c\u7ec8\u662f\u6bd4\u4e0d\u8fc7\u6210\u957f\u5728\u98ce\u96ea\u4e2d\u7684\u5317\u5251\u95e8\u4eba\uff0c\u4e24\u4e2a\u4eba\u5012\u4e0b\u53bb\u4e86\uff0c\u6fc0\u8d77\u4e00\u7247\u98de\u96ea\u3002 \u3000\u3000\u5357\u5200\u6bb5\u4e5d\u7741\u5f00\u5fae\u95ed\u7684\u53cc\u76ee\uff0e\u770b\u4e86\u4e00\u773c\uff0c\u53c8\u7f13\u7f28\u95ed\u4e0a\u4e86\u773c\u775b\u3002 \u3000\u3000\u6ca1\u6709\u4e00\u8bed\u6170\u95ee\uff0c\u4e5f\u6ca1\u6709\u4e00\u58f0\u5457\u606f\u3002 \u3000\u3000\u80e1\u840d\u8c08\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6bb5\u5144\uff0c\u80dc\u8d1f\u4e43\u5175\u5bb6\u5e38\u4e8b\uff0c\u8ba4\u4e86\u5427\uff01\u5927\u4e08\u592b\u80fd\u5c48\u80fd\u4f38\u554a\uff01\u201d \u3000\u3000\u201c\u5357\u5200\u95e8\u4e0b\u65e0\u964d\u5175\uff0c\u80e1\u5144\uff0c\u522b\u5fd8\u4e86\u71ac\u8fc7\u8fd9\u4e00\u9635\u98ce\u96ea\u4e4b\u82e6\uff0c\u6bb5\u67d0\u4eba\u4e5f\u6709\u4e00\u756a\u73a9\u547d\u7684\u5b89\u6392\uff01\u201d \u3000\u3000\u201c\u73a9\u547d\uff01\u54c8\u54c8\uff0c\u6bb5\u5144\uff0c\u522b\u5fd8\u4e86\u2018\u4e5d\u534e\u76df\u7ea6\u2019\u7b2c\u4e00\u6212\uff0c\u4e0d\u51c6\u51fa\u52fa\u6740\u4eba\u54ea\uff01\u62d4\u5200\u6740\u4eba\u8005\u6b7b\uff01 \u3000\u3000\u96be\u9053\u6bb5\u5144\u8981\u80cc\u5f03\u76df\u7ea6\uff0c\u6765\u4e00\u573a\u5200\u3001\u5251\u62fc\u640f\u3002\u201d \u3000\u3000\u201c\u6bb5\u67d0\u662f\u4f55\u7b49\u4eba\u7269\uff0c\u5c82\u80af\u80cc\u5f03\u5929\u4e0b\u6b66\u6797\u540c\u9053\uff0c\u4e00\u4f53\u9075\u5b88\u7684\u76df\u7ea6\uff0c\u6bb5\u67d0\u5982\u662f\u80cc\u76df\u5f03\u7ea6\u7684\u4eba\uff0c\u4e5f\u4e0d\u4f1a\u8ba9\u6211\u8fd9\u4e9b\u751f\u957f\u5728\u674f\u82b1\u6c5f\u5357\u7684\u5f1f\u5b50\u4eec\uff0c\u627f\u53d7\u8fd9\u4e9b\u5bd2\u98ce\u900f\u808c\u4e4b\u82e6\u4e86\u3002\u201d \u3000\u3000\u201c\u8bf4\u7684\u4e5f\u662f\u3002\u4e0d\u8fc7\uff0c\u5317\u5251\u95e8\u4e0b\u5f1f\u5b50\u3002\u53ef\u4e5f\u662f\u8840\u8089\u4e4b\u8eaf\uff0c\u4e00\u6837\u4fb5\u88c2\u7740\u4ed6\u4eec\u7684\u808c\u80a4\u3002\u201d \u3000\u3000\u201c\u54fc\uff01\u80e1\u5144\uff0c\u7528\u4e0d\u7740\u8bf4\u98ce\u51c9\u8bdd\u4e86\uff0c\u4f60\u60f3\u51fa\u8fd9\u79cd\u6bd4\u8bd5\u7684\u529e\u6cd5\u3002 \u3000\u3000\u591f\u9ad8\u660e\uff0c\u4e5f\u591f\u6b79\u6bd2\uff0c\u5c31\u7b97\u5357\u5200\u95e8\u4e0b\u5f1f\u5b50\u51bb\u6b7b\u5149\u4e86\uff0c\u6211\u4e5f\u8981\u6491\u8fc7\u8fd9\u4e00\u9635\u3002\u7b2c\u4e8c\u9635\uff0c\u5144\u5f1f\u4f5c\u4e3b\u3002\u80e1\u5144\uff0c\u6709\u4f60\u597d\u77a7\u7684\u4e86\u3002\u201d \u3000\u3000\u201c\u80e1\u67d0\u4eba\u5b9e\u5728\u60f3\u4e0d\u51fa\u6765\uff0c\u4e0d\u52a8\u5200\u3001\u5251\u640f\u547d\uff0c\u4f60\u6bb5\u5144\u4f1a\u6709\u4ec0\u4e48\u529e\u6cd5\u80fd\u4f7f\u80e1\u67d0\u548c\u95e8\u4e0b\u5f1f\u5b50\u4eec\u8ba4\u8f93\u3002\u201d \u3000\u3000\u201c\u6bb5\u67d0\u7684\u529e\u6cd5\uff0c\u53ef\u4ee5\u8ba9\u4f60\u80e1\u5144\u62d4\u5251\uff0c\u5f53\u7136\uff0c\u6211\u4e5f\u53ef\u4ee5\u62bd\u5200\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u4e0d\u662f\u7528\u5b83\u6740\u4eba\uff01\u201d \u3000\u3000\u201c\u62d4\u5251\uff0c\u62bd\u5200\uff0c\u4e0d\u662f\u7528\u6765\u640f\u6740\u62fc\u547d\uff1f\u80e1\u67d0\u4eba\u5c31\u60f3\u4e0d\u901a\u505a\u4ec0\u4e48\u4e86\uff1f\u201d \u3000\u3000\u201c\u80e1\u5144\u6162\u6162\u7684\u60f3\u5427\uff01\u5144\u5f1f\u6682\u65f6\u4e0d\u4f1a\u544a\u8bc9\u4f60\u3002\u201d \u3000\u3000\u4f46\u95fb\u84ec\u84ec\u84ec\u4e09\u58f0\u8f7b\u9707\uff0c\u53c8\u6709\u4e09\u4e2a\u4eba\u5012\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u4e24\u4e2a\u662f\u5357\u5200\u95e8\u4e0b\u3002\u4e00\u4e2a\u662f\u5317\u5251\u5f1f\u5b50\u3002 \u3000\u3000\u867d\u7136\uff0c\u4ed6\u4eec\u9009\u7684\u90fd\u662f\u95e8\u4e0b\u6b66\u529f\u6700\u597d\u7684\u5f1f\u5b50\uff0c\u6bd5\u7adf\u662f\u827a\u4e1a\u6709\u6df1\u6d45\uff0c\u529f\u592b\u6709\u9ad8\u4f4e\uff0c\u5317\u5251\u95e8\u4e0b\uff0c\u4e00\u6837\u6709\u96be\u71ac\u9177\u585e\u7684\u4eba\u3002 \u3000\u3000\u5357\u5200\u6bb5\u4e5d\u8f7b\u8f7b\u5401\u4e00\u53e3\u6c14\u3002\u9053\uff1a\u201c\u4e24\u4e2a\u65f6\u8fb0\u5230\u4e86\u5427\uff1f\u201d \u3000\u3000\u80e1\u840d\u76ee\u5149\u626b\u63a0\u4e86\u4e00\u95e8\u4e0b\u5f1f\u5b50\u4e00\u773c\uff0c\u53d1\u73b0\u90a3\u98d8\u843d\u5728\u4ed6\u4eec\u8eab\u4e0a\u7684\u96ea\u82b1\u53d7\u5230\u4f53\u6e29\u7684\u84b8\u53d1\uff0c\u5916\u4e3a\u5bd2\u51e4\u903c\u8feb\uff0c\u5df2\u7136\u7ed3\u6210\u4e86\u4e00\u5c42\u8584\u51b0\uff0c\u5982\u82e5\u8fd9\u51b0\u5c42\u518d\u52a0\u539a\u4e00\u4e9b\uff0c\u7ed3\u6ee1\u5168\u8eab\u3002\u4efb\u4f55\u4eba\u90fd\u65e0\u6cd5\u652f\u6491\u4e0b\u53bb\u3002 \u3000\u3000\u518d\u770b\u6bb5\u4e5d\u5168\u8eab\u5192\u51fa\u767d\u6c14\u3002\u96ea\u82b1\u90fd\u88ab\u903c\u79bb\u5f00\u53bb\u3002\u5076\u6709\u843d\u5728\u8eab\u4e0a\u3002\u4e5f\u4f1a\u5316\u4f5c\u6c34\u6c14\u6d41\u5931\u3002\u5185\u529f\u7684\u7cbe\u6e5b\uff0c\u4f3c\u5df2\u5230\u5bd2\u6c14\u96be\u4fb5\u7684\u5883\u754c\u3002 \u3000\u3000\u5982\u662f\u518d\u82e6\u62fc\u4e0b\u53bb\u3002\u5357\u5200\u95e8\u4e0b\u5f1f\u5b50\uff0c\u6545\u96be\u5e78\u5154\uff0c\u53ea\u6015\u81ea\u5df1\u95e8\u4e2d\u5b50\u5f1f\uff0c\u4e5f\u5c06\u6709\u5927\u90e8\u50f5\u6bd9\u4e4b\u865e\u3002 \u3000\u3000\u4f46\u6700\u4f7f\u80e1\u840d\u4e0d\u5b89\u7684\uff0c\u5341\u540d\u5317\u5251\u95e8\u4eba\u4e2d\u3002\u6709\u4e00\u4e2a\u662f\u4ed6\u7684\u72ec\u5b50\u80e1\u4f55\u51b5\uff0c\u5929\u9c7c\u5df2\u6697\u4e86\u4e0b\u6765\uff0c\u4e24\u4e2a\u65f6\u8fb0\u7684\u7ea6\u5b9a\u662f\u8be5\u5230\u4e86\u3002 \u3000\u3000\u7edd\u5cf0\u4e4b\u4e0a\uff0c\u6ca1\u6709\u6f0f\u6597\u6d41\u6c99\u8ba1\u65f6\uff0c\u53ea\u80fd\u51ed\u4ed7\u4f30\u7b97\u4e86\u3002 \u3000\u3000\u6697\u4e2d\u76d8\u7b97\u8fc7\u573a\u4e2d\u60c5\u52bf\uff0c\u80e1\u840d\u5e72\u7b11\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u65e2\u7136\u6bb5\u5144\u8ba4\u4e3a\u7ea6\u5b9a\u7684\u65f6\u9650\u5df2\u5230\uff0c\u5c31\u7b97\u662f\u5230\u4e86\u5427\uff01\u201d \u3000\u3000\u6bb5\u4e5d\u5fcd\u4e0b\u4e00\u53e3\u6c14\uff0c\u559d\u4ee4\u95e8\u4e0b\u7a7f\u4e0a\u8863\u670d\u3002 \u3000\u3000\u4f46\u80fd\u591f\u9075\u7167\u884c\u52a8\u7684\uff0c\u53ea\u6709\u4e24\u4e2a\u4eba\uff0c\u4f59\u4e0b\u56db\u4eba\uff0c\u867d\u672a\u5012\u6454\u4e0b\u53bb\uff0c\u4e5f\u5df2\u88ab\u51bb\u7684\u5168\u8eab\u50f5\u786c\u3002\u65e0\u6cd5\u884c\u52a8\u3002 \u3000\u3000\u56de\u987e\u5317\u5251\u95e8\u4e0b\uff0c\u4e5d\u4e2a\u4eba\u4e2d\uff0c\u6709\u516d\u4eba\u80fd\u591f\u81ea\u5df1\u53d6\u8863\u7a7f\u7740\uff0c\u53e6\u5916\u4e24\u4e2a\u867d\u7136\u624b\u811a\u4e0d\u542c\u4f7f\u5524\uff0c\u4f46\u8eab\u5b50\u8fd8\u53ef\u626d\u52a8\u3002 \u3000\u3000\u8fd9\u4e00\u9635\u6bd4\u8bd5\uff0c\u5357\u5200\u95e8\u4e0b\u8d25\u7684\u5f88\u60e8\u3002 \u3000\u3000\u6bb5\u4e5d\u6c89\u4e0d\u4f4f\u6c14\u4e86\uff0c\u53cc\u624b\u9f50\u51fa\uff0c\u903c\u51fa\u5185\u529b\uff0c\u5206\u52a9\u56db\u4eba\uff0c\u4f46\u4e5f\u53ea\u6709\u4e24\u4eba\u501f\u52a9\u4e86\u5185\u529b\uff0c\u7f13\u5f00\u4e86\u624b\u811a\u3002\u53e6\u5916\u4e24\u4e2a\uff0c\u5374\u5012\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u5148\u7a7f\u597d\u8863\u670d\u7684\u4e24\u4e2a\u5f1f\u5b50\uff0c\u65bd\u5c55\u4e86\u4e00\u9635\u62f3\u811a\uff0c\u6d3b\u5f00\u8840\u8109\uff0c\u624d\u5e2e\u52a9\u540c\u95e8\u7a7f\u4e0a\u8863\u670d\u3002 \u3000\u3000\u6bb5\u4e5d\u54ac\u7259\u51fa\u58f0\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u90ed\u5929\u540c\uff0c\u4ed6\u4eec\u4f24\u52bf\u5982\u4f55\uff1f\u201d \u3000\u3000\u201c\u4e03\u5e08\u5f1f\u3001\u4e5d\u5e08\u5f1f\uff0c\u8fd8\u6709\u6c14\u606f\uff0c\u4e94\u3001\u516d\u3001\u516b\uff0c\u5341\u56db\u4f4d\u5e08\u5f1f\u3002 \u3000\u3000\u4f3c\u662f\u88ab\u51bb\u6b7b\u4e86\u3002\u201d \u3000\u3000\u201c\u7ed9\u4ed6\u4eec\u5403\u4e0b\u4e00\u9897\u62a4\u5fc3\u4e39\uff0c\u4ed6\u4eec\u51bb\u50f5\u4e86\uff0c\u672a\u5fc5\u5c31\u4f1a\u51bb\u6b7b\uff01\u9001\u4ed6\u4eec\u5230\u84ec\u5e10\u4e2d\u53bb\uff0c\u628a\u6211\u51c6\u5907\u597d\u7684\u4e1c\u897f\u5e26\u6765\u3002\u201d \u3000\u3000\u201c\u662f\uff01\u201d\u90ed\u5929\u540c\u662f\u5357\u5200\u95e8\u4e0b\u7684\u592a\u5f1f\u5b50\uff0c\u6307\u6325\u4e09\u5341\u8fd8\u80fd\u6d3b\u52a8\u7684\u5f1f\u5b50\uff0c\u80cc\u8d77\u4e24\u4e2a\u6c14\u606f\u72b9\u5b58\u7684\u5e08\u5f1f\uff0c\u53c8\u631f\u8d77\u53e6\u5916\u56db\u4eba\uff0c\u5954\u5411\u84ec\u5e10\u3002 \u3000\u3000\u201c\u6bb5\u5144\u4e0d\u7528\u96be\u8fc7\u2026\u2026\u201d\u80e1\u840d\u5fae\u7b11\u7740\u8bf4\uff1a\u201c\u80e1\u67d0\u95e8\u4e0b\uff0c\u4e5f\u51bb\u4f24\u4e86\u4e24\u4e2a\u2026\u2026\u201d \u3000\u3000\u80e1\u840d\uff0c\u7b49\u4e00\u4e0b\uff0c\u5e0c\u671b\u4f60\u8fdc\u80fd\u7b11\u5f97\u51fa\u6765\uff01\u6bb5\u4e5d\u7684\u8138\u4e0a\u3002\u6cdb\u73b0\u51fa\u4e00\u7247\u72e0\u9177\u4e4b\u8272\u3002 \u3000\u3000\u80e1\u840d\u77e5\u9053\u6bb5\u4e5d\u642d\u8d77\u4e86\u4e00\u5ea7\u84ec\u5e10\uff0c\u7edd\u4e0d\u662f\u4e3a\u4e86\u9632\u5bd2\u4e4b\u7528\uff0c\u56e0\u4e3a\uff0c\u6bb5\u4e5d\u6839\u672c\u5c31\u60f3\u4e0d\u5230\uff0c\u4ed6\u80e1\u840d\u4f1a\u60f3\u51fa\u4e86\u8fd9\u4e48\u4e00\u4e2a\u6bd4\u8bd5\u7684\u529e\u6cd5\u3002 \u3000\u3000\u80e1\u840d\u5f88\u5f97\u610f\u8fd9\u4efd\u51fa\u4eba\u610f\u5916\u7684\u806a\u660e\u8bbe\u8ba1\uff0c\u679c\u7136\u662f\u5927\u83b7\u5168\u80dc\u3002 \u3000\u3000\u5982\u679c\uff0c\u6bb5\u4e5d\u6551\u4e0d\u6d3b\u51bb\u50f5\u7684\u516d\u4e2a\u5f1f\u5b50\uff0c\u5357\u5200\u95e8\u7684\u5b9e\u529b\uff0c\u5c06\u6d88\u9000\u4e00\u534a\uff0c\u4ed6\u4eec\u90fd\u662f\u5357\u5200\u624b\u4e0b\u7684\u7cbe\u82f1\u3002 \u3000\u3000\u4f46\u80e1\u840d\u4e5f\u731c\u4e0d\u900f\u6bb5\u4e5d\u7684\u8bbe\u8ba1\uff0c\u4ed6\u8981\u7528\u4ec0\u4e48\u51fa\u5947\u65b9\u6cd5\u5bf9\u4ed8\u5317\u5251\u95e8\u3002 \u3000\u3000\u4f7f\u80e1\u840d\u5b89\u5fc3\u7684\u662f\uff0c\u76ee\u4e0b\u6bb5\u4e5d\u5b9e\u529b\u5927\u635f\uff0c\u4e0d\u7ba1\u600e\u4e48\u6bd4\u8bd5\uff0c\u5317\u5251\u95e8\u90fd\u5360\u4e86\u7edd\u5bf9\u7684\u4f18\u52bf\u3002 \u3000\u3000\u4f46\u89c1\u5357\u5200\u95e8\u4e0b\u9996\u5f92\u90ed\u5929\u540c\uff0c\u5e26\u7740\u4e00\u4f4d\u5e08\u5f1f\u5404\u4e3e\u4e00\u4e2a\u5f88\u5927\u7684\u68c9\u88ab\u56f4\u906e\u4e4b\u7269\uff0c\u5927\u6b65\u884c\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u53e6\u5916\u4e24\u4e2a\u8fd8\u53ef\u884c\u52a8\u7684\u5357\u5200\u95e8\u4e2d\u5f1f\u5b50\uff0c\u7559\u4e0b\u7167\u987e\u516d\u4e2a\uff0c\u51bb\u50f5\u7684\u540c\u95e8\u3002 \u3000\u3000\u80e1\u840d\u77a7\u4e0d\u51fa\u62ff\u6765\u7684\u662f\u4ec0\u4e48\u4e1c\u897f\uff0c\u7acb\u523b\u4ee5\u624b\u52bf\u4e0b\u4ee4\u95e8\u4e0b\u5f1f\u5b50\u6212\u5907\u3002 \u3000\u3000\u6bb5\u4e5d\u5374\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u600e\u4e48\uff1f\u80e1\u5144\u53ef\u662f\u5bb3\u6015\u4e86\uff1f\u201d \u3000\u3000\u80e1\u840d\u6ca1\u6709\u56de\u7b54\u6bb5\u4e5d\u4e4b\u8a00\uff0c\u4e24\u9053\u76ee\u5149\uff0c\u5374\u51dd\u6ce8\u5728\u68c9\u88ab\u63a9\u906e\u7684\u4e8b\u7269\u4e4b\u4e0a\u3002 \u3000\u3000\u6bb5\u4e5d\u4e00\u624b\u63ed\u5f00\u68c9\u88ab\uff0c\u80e1\u840d\u7acb\u523b\u5927\u653e\u5bbd\u5fc3\u3002 \u3000\u3000\u539f\u6765\uff0c\u90a3\u662f\u4e24\u4e2a\u5f88\u5927\u7684\u4e4c\u7b3c\uff0c\u5404\u88c5\u7740\u4e00\u53ea\u5de8\u5927\u7684\u8910\u8272\u82cd\u9e70\u3002 \u3000\u3000\u201c\u600e\u4e48\uff1f\u6bb5\u5144\u8981\u7528\u8fd9\u4e24\u53ea\u5927\u9e70\u5bf9\u4ed8\u6211\uff1f\u201d \u3000\u3000\u6bb5\u4e5d\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u4ea7\u81ea\u5341\u4e07\u5927\u5c71\u4e2d\u7684\u5de8\u9e70\uff0c\u722a\u5229\u529b\u5f3a\u3002 \u3000\u3000\u6709\u98de\u884c\u5343\u91cc\u4e4b\u80fd\uff0c\u559c\u98df\u6bd2\u86c7\uff0c\u6545\u800c\u53e3\u5185\u722a\u4e0a\uff0c\u7686\u6709\u5267\u6bd2\uff0c\u4f46\u8fd9\u6469\u5929\u5cad\u4e0a\uff0c\u5bd2\u98ce\u5982\u526a\uff0c\u5b83\u4eec\u5728\u8fd9\u6837\u7684\u5929\u6c14\u4e0b\u80fd\u98de\u591a\u8fdc\uff0c\u5144\u5f1f\u5c31\u4e0d\u6562\u8bba\u65ad\u4e86\u2026\u2026\u201d \u3000\u3000\u80e1\u840d\u5fc3\u4e2d\u5947\u9053\uff1a\u201c\u82cd\u9e70\u80fd\u98de\u591a\u8fdc\uff0c\u548c\u6bd4\u8bd5\u6709\u4ec0\u4e48\u5173\u7cfb\uff1f\u96be\u9053\u8981\u6bd4\u8bd5\u9a91\u9e70\u4e0d\u6210\uff1f\u201d \u3000\u3000\u4ed4\u7ec6\u770b\u53bb\uff0c\u53ea\u89c1\u9e1f\u7b3c\u4e0a\u6709\u4e24\u6761\u4e1d\u7d22\u3002\u7cfb\u5728\u82cd\u9e70\u7684\u817f\u4e0a\u3002\u9e70\u9888\u4e4b\u95f4\uff0c\u8fd8\u5957\u4e86\u4e00\u4e2a\u94a2\u73af\u3002 \u3000\u3000\u4efb\u662f\u80e1\u840d\u6709\u770b\u4e30\u5bcc\u7684\u6c5f\u6e56\u9605\u5386\uff0c\u4e00\u65f6\u95f4\u4e5f\u770b\u4e0d\u61c2\u8fd9\u4e9b\u5b89\u6392\u3002 \u3000\u3000\u4f5c\u7528\u4f55\u5728\uff1f\u4f46\u95fb\u6bb5\u4e5d\u8bf4\u9053\uff1a\u201c\u80e1\u5144\uff0c\u8fd9\u4e00\u6b21\u4e0d\u7528\u95e8\u4e0b\u5f1f\u5b50\u53c2\u52a0\uff0e\u7531\u5144\u5f1f\u548c\u80e1\u5144\u4e00\u51b3\u80dc\u8d1f\uff0c\u4e0d\u77e5\u80e1\u5144\u7684\u610f\u4e0b\u5982\u4f55\uff1f\u201d \u3000\u3000\u80e1\u840d\u5e72\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u53ea\u8981\u6bb5\u5144\u5212\u4e0b\u7684\u9053\u513f\u516c\u5e73\uff0c\u80e1\u67d0\u820d\u547d\u5949\u966a\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u5927\u592b\u4e00\u8a00\uff0c\u9a77\u9a6c\u96be\u8ffd\uff0c\u5e0c\u671b\u80e1\u5144\u4e0d\u8981\u540e\u6094\u624d\u597d\u3002\u201d \u3000\u3000\u201c\u6bb5\u5144\u653e\u5fc3\uff0c\u80e1\u67d0\u4eba\u8bf4\u51fa\u53e3\u7684\u8bdd\uff0c\u5982\u677f\u4e0a\u9489\u9489\uff0c\u4e0d\u8fc7\uff0c\u8fd9\u516c\u5e73\u4e8c\u5b57\uff0c\u5374\u662f\u6700\u4e3a\u91cd\u8981\uff01\u201d \u3000\u3000\u201c\u653e\u5fc3\uff0c\u7edd\u5bf9\u516c\u5e73\uff0c\u8fd9\u5bf9\u82cd\u9e70\uff0c\u90fd\u662f\u96c4\u9e70\uff0c\u4f46\u4e3a\u8ba9\u80e1\u5144\u5b89\u5fc3\u3002\u4f60\u5148\u9009\u4e00\u53ea\u3002\u201d \u3000\u3000\u80e1\u840d\u5ffd\u7136\u95f4\u53d8\u5f97\u5c0f\u5fc3\u8d77\u6765\uff0c\u4ed4\u7ec6\u770b\u4e86\u4e24\u53ea\u82cd\u9e70\uff0c\u679c\u7136\u4e00\u822c\u795e\u9a8f\uff0c\u8fde\u5927\u5c0f\u4e5f\u51e0\u4e4e\u4e00\u6837\u3002 \u3000\u3000\u4f46\u4ed6\u5e76\u672a\u7acb\u523b\u51b3\u5b9a\uff0c\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u8fd9\u4e48\u5427\uff01\u6bb5\u5144\u5148\u628a\u6bd4\u8bd5\u7684\u5e03\u7f72\u5b89\u6392\u59a5\u5f53\uff0c\u5144\u5f1f\u518d\u9009\u4e0d\u8fdf\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u5929\u540c\uff0c\u6253\u5f00\u9e70\u7b3c\uff0c\u7cfb\u9e70\u7f30\u3002\u201d \u3000\u3000\u201c\u662f\uff01\u201d\u90ed\u5929\u540c\u53cc\u624b\u6325\u52a8\uff0c\u719f\u7ec3\u5730\u5f00\u4e86\u9e70\u7b3c\uff0c\u53c8\u5728\u9e70\u9888\u7684\u94c1\u73af\u4e0a\uff0c\u7cfb\u4e86\u4e00\u6761\u4e09\u4e08\u957f\u77ed\u7684\u4e1d\u7d22\u3002 \u3000\u3000\u56da\u9e70\u7684\u5927\u6728\u7b3c\uff0c\u7ecf\u8fc7\u4e86\u7279\u522b\u7684\u8bbe\u8ba1\uff0c\u6253\u5f00\u4e4b\u540e\u3002\u53d8\u6210\u4e86\u4e24\u4e2a\u4e24\u5c3a\u591a\u9ad8\u6728\u7bee\u5b50\u3002 \u3000\u3000\u201c\u80e1\u5144\uff0c\u770b\u660e\u767d\u4e86\u5427\uff1f\u201d \u3000\u3000\u80e1\u840d\u4ed4\u7ec6\u6253\u91cf\u4e86\u4e00\u9635\uff0c\u5fc3\u4e2d\u4f3c\u6709\u6240\u609f\uff0c\u4f46\u53c8\u4e0d\u662f\u5b8c\u5168\u4e86\u89e3\u3002 \u3000\u3000\u4ed6\u662f\u4e2a\u8c28\u614e\u7684\u4eba\uff0c\u4e0d\u5b8c\u5168\u4e86\u89e3\uff0c\u5c31\u4e0d\u80af\u786c\u5145\u82f1\u96c4\u3001\u7b11\u4e00\u7b11\u3002 \u3000\u3000\u9053\uff1a\u201c\u6bb5\u5144\u8fd9\u5e03\u7f6e\uff0c\u770b\u6765\u590d\u6742\u65b0\u5947\uff0c\u6700\u597d\u662f\u89e3\u8bf4\u4e00\u4e0b\u3002\u201d \u3000\u3000\u201c\u5176\u5b9e\u5f88\u7b80\u5355\uff0c\u4ee5\u80e1\u5144\u4e4b\u80fd\uff0c\u6050\u662f\u65e9\u5df2\u4e86\u7136\u4e8e\u5fc3\uff0c\u4f46\u65e2\u8981\u6bb5\u67d0\u89e3\u8bf4\uff0c\u5144\u5f1f\u662f\u606d\u656c\u4e0d\u5982\u4ece\u547d\u4e86\u2026\u2026\u201d \u3000\u3000\u4ed6\u957f\u5401\u4e00\u53e3\u6c14\uff0c\u63a5\u9053\uff1a\u201c\u8fd9\u6728\u7bee\uff0c\u7cfb\u5728\u53cc\u9e70\u7684\u817f\u4e0a\uff0c\u4f60\u6211\u7ad9\u5728\u7bee\u4e2d\u3002\u60ca\u98de\u82cd\u9e70\uff0c\u7136\u540e\u3002 \u3000\u3000\u5404\u51fa\u5175\u5203\u3002\u65a9\u65ad\u5bf9\u65b9\u7684\u7cfb\u7bee\u4e1d\u7d22\uff0c\u8feb\u4f7f\u5bf9\u65b9\u8dcc\u6454\u4e0b\u53bb\uff0c\u81f3\u4e8e\u8dcc\u843d\u5728\u4ec0\u4e48\u5730\u65b9\u3002\u662f\u751f\u662f\u6b7b\uff0c\u90a3\u5c31\u5404\u51ed\u5929\u547d\u4e86\u2026\u2026\u201d \u3000\u3000\u80e1\u840d\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u90a3\u7cfb\u5728\u82cd\u9e70\u9888\u4e0a\u7684\u4e00\u6761\u4e1d\u7d22\u5462\uff1f\u201d \u3000\u3000\u201c\u66f4\u7b80\u5355\u3002\u6bb5\u67d0\u7684\u9e70\u9888\u4e1d\u7d22\u3002\u4ea4\u5728\u80e1\u5144\u624b\u4e2d\uff0c\u80e1\u5144\u4ea6\u7136\uff0c\u6240\u4ee5\u5bf9\u65b9\u7684\u82cd\u9e70\u65e0\u6cd5\u98de\u79bb\u592a\u8fdc\uff0c\u5f53\u7136\uff0c\u80e1\u5144\u53ef\u7528\u624b\u4e2d\u4e4b\u5251\uff0c\u65a9\u65ad\u4e1d\u7d22\u3002\u90a3\u5c31\u53cc\u9e70\u5206\u98de\uff0c\u5404\u4e0d\u76f8\u5173\u3002\u751f\u6b7b\u4ea6\u662f\u5404\u51ed\u8fd0\u6c14\u4e86\u3002\u201d \u3000\u3000\u201c\u64cd\u7eb5\u82cd\u9e70\u7684\u5347\u964d\u3002\u5168\u5728\u9888\u95f4\u4e1d\u7d22\u4e4b\u4e0a\uff0c\u4e1d\u7d22\u65ad\u53bb\uff0c\u4eba\u7684\u751f\u6b7b\u3002\u5c31\u7531\u9e70\u4f5c\u4e3b\u4e86\uff1f\u201d\u80e1\u840d\u4f4e\u58f0\u7684\u95ee\u3002 \u3000\u3000\u201c\u80e1\u5144\u80fd\u4e3e\u4e00\u53cd\u4e09\u3002\u679c\u7136\u662f\u8bc6\u89c1\u975e\u51e1\u3002\u6700\u597d\u662f\u80fd\u628a\u5bf9\u65b9\u624b\u4e2d\u4e1d\u7d22\u593a\u4e86\u8fc7\u6765\u3002\u80fd\u63a7\u5236\u81ea\u5df1\u82cd\u9e70\u7684\u5347\u964d\uff0c\u4e0d\u8fc7\uff0c\u53cc\u65b9\u624b\u4e2d\u90fd\u6709\u5175\u5203\uff0c\u4f60\u65a9\u7d22\uff0c\u6211\u5c01\u6321\u3002\u593a\u8fc7\u6765\u3002\u53ea\u6015\u662f\u4e0d\u592a\u5bb9\u6613\uff0c\u56e0\u4e3a\uff0c\u54b1\u4eec\u8981\u9075\u4ece\u2018\u4e5d\u534e\u76df\u7ea6\u2019\u3002\u5200\u3001\u5251\u53ea\u80fd\u65a9\u5272\u5bf9\u65b9\u7684\u4e1d\u7d22\uff0c\u4e0d\u8bb8\u653b\u51fb\u4eba\u8eab\u3002\u201d \u3000\u3000\u80e1\u840d\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u80fd\u4e0d\u80fd\u65a9\u4f24\u5bf9\u65b9\u7684\u6728\u7bee\u5462\uff1f\u201d \u3000\u3000\u201c\u53ef\u4ee5\uff0c\u9664\u4eba\u4e4b\u5916\uff0c\u90fd\u53ef\u4ee5\u65a9\u5272\u3001\u7834\u574f\uff0c\u53ea\u8981\u80e1\u5144\u89c9\u7740\u5bf9\u81ea\u5df1\u6709\u5229\u5c31\u884c\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u80e1\u67d0\u4eba\u518d\u8bf7\u6559\u6700\u540e\u4e00\u4e2a\u95ee\u9898\uff1f\u201d \u3000\u3000\u201c\u8bf7\u8bf4\u3002\u201d \u3000\u3000\u201c\u6f20\u5317\u7684\u5927\u9e4f\u3002\u71d5\u5c71\u7684\u9e5e\u9e70\uff0c\u80e1\u67d0\u89c1\u8fc7\u4e0d\u5c11\uff0c\u4f46\u8fd9\u5357\u86ee\u5927\u5c71\u4e2d\u7684\u4ea7\u7269\u3002\u5144\u5f1f\u8fd8\u662f\u521d\u89c1\u3002 \u3000\u3000\u4e0d\u77e5\u8fd9\u5bf9\u82cd\u9e70\u53d7\u60ca\u4e4b\u540e\uff0c\u53cd\u5e94\u5982\u4f55\uff1f\u201d \u3000\u3000\u201c\u80e1\u5144\u679c\u7136\u7ec6\u5fc3\u5f97\u5f88\u3002\u8fd9\u79cd\u5927\u9e70\u53d7\u5230\u60ca\u9a87\u3002\u6027\u5411\u662f\u5168\u529b\u9ad8\u98de\u3002\u4e0d\u8fc7\uff0c\u4eba\u91cd\u8fc7\u9e70\uff0c\u5b83\u4eec\u80fd\u98de\u591a\u9ad8\uff0c\u6bb5\u67d0\u4e5f\u65e0\u6cd5\u9884\u6599\uff0c\u5982\u662f\u80e1\u5144\u6ca1\u6709\u522b\u7684\u95ee\u9898\u3002\u54b1\u4eec\u53ef\u4ee5\u5f00\u59cb\u4e86\u3002\u201d \u3000\u3000\u80e1\u840d\u70b9\u70b9\u5934\u3002\u4f69\u597d\u957f\u5251\uff0c\u56de\u5934\u5bf9\u56f4\u5b88\u8eab\u4fa7\u7684\u5f1f\u5b50\u9053\uff1a\u201c\u6211\u548c\u6bb5\u5144\u4e58\u9e70\u9068\u6e38\u3002\u4e0d\u7ba1\u751f\u6b7b\u3002 \u3000\u3000\u90fd\u4e0d\u8bb8\u4f60\u4eec\u63d2\u624b\uff0c\u82cd\u9e70\u98de\u8d77\uff0c\u4f60\u4eec\u5c31\u79bb\u5f00\u8fd9\u91cc\uff0c\u56de\u5230\u71d5\u5c71\u9ec4\u53f6\u8c37\u53bb\u3002\u5982\u82e5\u4e09\u4e2a\u6708\u5185\uff0c\u4e3a\u5e08\u4ecd\u4e0d\u8fd4\u56de\u71d5\u5c71\u3002\u4e00\u5207\u90fd\u542c\u4f60\u4eec\u5e08\u5a18\u4f5c\u4e3b\u3002\u201d \u3000\u3000\u201c\u5e08\u7236\u3001\u8fd9\u2026\u2026\u201d \u3000\u3000\u5317\u5251\u95e8\u4e0b\u5341\u4e2a\u5f1f\u5b50\uff0c\u4e5d\u4e2a\u90fd\u8fd8\u662f\u6d3b\u8e66\u4e71\u8df3\u7684\u4eba\uff0c\u4e00\u4e2a\u53d7\u5230\u51bb\u4f24\uff0c\u4ea6\u53ef\u884c\u52a8\u3001\u6b63\u5728\u6d3b\u52a8\u8840\u8109\uff0c\u770b\u6837\u5b50\uff0c\u4e5f\u65e0\u5927\u788d\u4e86\u3002 \u3000\u3000\u201c\u4e0d\u8bb8\u591a\u8bf4\u2026\u2026\u201d\u80e1\u840d\u51b7\u5389\u5730\u559d\u6b62\u4f4f\u95e8\u4e0b\u7684\u5f1f\u5b50\uff0c\u56de\u5934\u5bf9\u6bb5\u4e5d\u8bf4\u9053\uff1a\u201c\u6bb5\u5144\uff0c\u53ef\u6709\u4ec0\u4e48\u8bdd\u5429\u5490\u95e8\u4e0b\uff1f\u201d \u3000\u3000\u6bb5\u4e5d\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u5927\u540c\uff0c\u7acb\u523b\u7387\u9886\u4f60\u5e08\u5f1f\u4eec\uff0c\u56de\u5230\u82cf\u5dde\u6bb5\u5bb6\u5821\u53bb\uff0c\u4e00\u5207\u5411\u4f60\u4eec\u5e08\u53d4\u8bf7\u793a\u3001\u542c\u547d\u884c\u4e8b\u2026\u2026\u201d \u3000\u3000\u201c\u5e08\u7236\u3001\u5f1f\u5b50\u60f3\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u8bb8\u591a\u60f3\uff0c\u6ede\u4f60\u5e08\u5f1f\u4eec\u4e0b\u5c71\u53bb\u5427\uff01\u51bb\u4f24\u7684\u7acb\u523b\u5c31\u533b\uff0c\u5b9e\u5728\u6570\u4e0d\u6d3b\uff0c\u628a\u5c38\u4f53\u8fd0\u56de\u6c5f\u5357\u3002 \u3000\u3000\u539a\u4e88\u57cb\u846c\u3002\u6bcf\u4eba\u81f4\u9001\u7eb9\u94f6\u4e09\u5343\u4e24\uff0c\u5b89\u629a\u4ed6\u4eec\u7684\u5bb6\u5c5e\u3002\u201d \u3000\u3000\u201c\u662f\uff01\u201d \u3000\u3000\u5357\u5200\u3001\u5317\u5251\u95e8\u4e0b\u5f1f\u5b50\u3002\u867d\u7136\u4e2a\u4e2a\u9894\u767e\u9886\u547d\uff0c\u4f46\u5374\u8083\u7acb\u5728\u539f\u5730\u4e0d\u52a8\u3002 \u3000\u3000\u6bb5\u4e5d\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5927\u4e08\u592b\u751f\u800c\u4f55\u6b22\uff0c\u6b7b\u800c\u4f55\u60e7\uff0c\u80e1\u5144\uff0c\u8bf7\u554a\uff01\u201d \u3000\u3000\u8fd9\u5c31\u662f\u6c5f\u6e56\u8c6a\u6770\uff0c\u8349\u83bd\u6027\u683c\uff0c\u751f\u6b7b\u5c3d\u4ed8\u8c08\u7b11\u4e2d\uff01 \u3000\u3000\u80e1\u840d\u9ed8\u5bdf\u7247\u523b\uff0c\u8de8\u5165\u6728\u7bee\u3002\u6bb5\u4e5d\u4e5f\u7d27\u968f\u7740\u8df3\u5165\u7bee\u4e2d\u3002 \u3000\u3000\u4e24\u4e2a\u4eba\u540c\u65f6\u6293\u4f4f\u4e86\u5bf9\u65b9\u7cfb\u5728\u9e70\u5934\u4e0a\u7684\u7d22\u7ef3\u3002 \u3000\u3000\u6bb5\u4e5d\u628a\u7d22\u7ef3\u8fc5\u5feb\u7684\u7cfb\u7d27\u8170\u95f4\u3002 \u3000\u3000\u80e1\u840d\u7acb\u523b\u7167\u6837\u8ddf\u8fdb\u3002 \u3000\u3000\u6bb5\u4e5d\u5927\u559d\u4e00\u58f0\uff0c\u62cd\u51fa\u53cc\u638c\uff0c\u5de8\u9e70\u53d7\u60ca\uff0c\u5c55\u7ffc\u8d77\u98de\uff0c\u4f46\u6728\u7bee\u6c89\u91cd\uff0c\u4e00\u98de\u4e0d\u8d77\uff0c\u5411\u524d\u8df3\u8dc3\u4e86\u4e00\u9635\uff0c\u624d\u98de\u79bb\u5cf0\u9876\u3002 \u3000\u3000\u8fd9\u5bf9\u5f85\u522b\u9009\u6349\u7684\u5de8\u9e70\uff0c\u529b\u91cf\u679c\u7136\u5f3a\u5927\u3001\u8d77\u98de\u4e4b\u540e\uff0c\u5f88\u5feb\u628a\u4e24\u4eba\u5e26\u5165\u4e86\u767e\u4e08\u9ad8\u7a7a\u3002 \u3000\u3000\u51db\u51bd\u7684\u897f\u98ce\uff0c\u628a\u4e24\u53ea\u7cfb\u5728\u9e70\u8eab\u4e4b\u4e0b\u7684\u6728\u7bee\uff0c\u5439\u5f97\u4e1c\u98d8\u897f\u8361\uff0c\u968f\u65f6\u53ef\u4ee5\u5b8c\u5168\u5730\u7ffb\u8f6c\u8fc7\u6765\uff0c\u903c\u8feb\u7740\u4e24\u4e2a\u4eba\u4e00\u624b\u6293\u4f4f\u6728\u7bee\uff0c\u4e00\u624b\u6293\u4f4f\u7ef4\u7cfb\u6728\u7bee\u7684\u7d22\u7ef3\uff0c\u4ee5\u4fdd\u6301\u8eab\u4f53\u7684\u5e73\u8861\uff0c\u5154\u4e8e\u8dcc\u843d\u4e4b\u5371\u3002 \u3000\u3000\u8fd9\u4e24\u5927\u6b66\u6797\u9ad8\u624b\uff0c\u5374\u65e0\u6cd5\u817e\u51fa\u4e00\u53ea\u624b\u6765\uff0c\u62d4\u51fa\u5200\u3001\u5251\u65a9\u5272\u5bf9\u65b9\u7684\u7d22\u7ef3\u3002 \u3000\u3000\u5355\u662f\u7ef4\u6301\u90a3\u6728\u7bee\u7684\u9690\u5b9a\uff0c\u5c31\u591f\u4ed6\u4eec\u5168\u529b\u5e94\u4ed8\u4e86\u3002 \u3000\u3000\u4ed6\u4eec\u4f4e\u4f30\u4e86\u5927\u81ea\u7136\u7684\u5a01\u529b\u3002 \u3000\u3000\u5347\u7a7a\u4e4b\u540e\uff0c\u5a01\u529b\u4f3c\u662f\u589e\u5f3a\u4e86\u5f88\u591a\u500d\uff0c\u631f\u5e26\u7684\u5bd2\u6c14\uff0c\u4e5f\u4f3c\u662f\u66f4\u5f3a\u5927\u4e86\u3002 \u3000\u3000\u80e1\u840d\u3001\u6bb5\u4e5d\u7a81\u7136\u90fd\u6709\u4e00\u79cd\u611f\u89c9\uff0c\u6709\u7740\u8eab\u9677\u51b0\u7a96\u7684\u5bd2\u610f\u2026\u2026\u5e94\u4ed8\u8fd9\u79cd\u81ea\u7136\u53d8\u5316\u7684\u80fd\u529b\uff0c\u4f3c\u4e4e\u662f\u4eba\u4e0d\u5982\u9e70\u3002 \u3000\u3000\u5176\u5b9e\uff0c\u4e24\u53ea\u5de8\u9e70\u98de\u5f97\u4e5f\u5f88\u5403\u529b\uff0c\u4f46\u5b83\u4eec\u5374\u6709\u7740\u987a\u5e94\u81ea\u7136\u7684\u672c\u80fd\uff0c\u968f\u7740\u98ce\u52bf\u65cb\u8f6c\u53d8\u5316\uff0c\u4ee5\u8282\u7701\u4f53\u529b\u3002 \u3000\u3000\u4e24\u4e2a\u4eba\u4f3c\u662f\u5df2\u6ca1\u6709\u88ad\u51fb\u5bf9\u65b9\u7684\u6253\u7b97\uff0c\u628a\u6240\u6709\u7684\u5e94\u53d8\u80fd\u529b\u96c6\u4e2d\u5728\u7ef4\u62a4\u81ea\u8eab\u7684\u5b89\u5168\u4e0a\u3002 \u3000\u3000\u8fd9\u771f\u662f\u75af\u72c2\u7684\u5bf9\u51b3\uff0c\u4f46\u8fc7\u7a0b\u5374\u53c8\u5b8c\u5168\u51fa\u4e86\u4eba\u610f\u6599\u4e4b\u5916\u3002 \u3000\u3000\u539f\u672c\uff0c\u4ed6\u4eec\u90fd\u89c9\u7740\u81ea\u5df1\u5f88\u5f3a\u5927\uff0c\u6570\u5341\u5e74\u82e6\u7ec3\u7684\u6b66\u529f\uff0c\u4ed6\u4eec\u81ea\u4fe1\u5df2\u80fd\u751f\u88c2\u864e\u8c79\uff0c\u529b\u654c\u767e\u4eba\uff0c\u4f46\u73b0\u5728\uff0c\u5374\u6709\u7740\u4e0d\u5982\u4e00\u53ea\u98de\u9e70\u7684\u611f\u89c9\uff0c\u53ea\u4e0d\u8fc7\u5347\u79bb\u9646\u5730\u4e00\u767e\u591a\u4e08\uff0c\u5c31\u5b8c\u5168\u5931\u53bb\u4e86\u4e3b\u5bb0\u81ea\u5df1\u7684\u80fd\u529b\u3002 \u3000\u3000\u5411\u4e0b\u770b\u53bb\uff0c\u4e00\u7247\u7691\u7691\u767d\u96ea\uff0c\u5f3a\u98ce\u5439\u98d8\u8fc7\u6765\u7684\u96ea\u82b1\uff0c\u6253\u5728\u8138\u4e0a\uff0c\u7adf\u7136\u9690\u9690\u4f5c\u75db\u3002 \u3000\u3000\u80e1\u840d\u7a81\u7136\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6bb5\u4e5d\u3002\u4f60\u60f3\u51fa\u4e86\u8fd9\u7b49\u5947\u602a\u7684\u8d22\u51b3\u65b9\u6cd5\uff0c\u60f3\u5fc5\u662f\u65e9\u5df2\u6df1\u601d\u719f\u8651\uff0c\u8ba1\u5212\u5468\u8be6\uff0c\u6709\u4ec0\u4e48\u5bf9\u4ed8\u80e1\u67d0\u4eba\u7684\u529e\u6cd5\uff0c\u53ef\u4ee5\u65bd\u5c55\u4e86\uff0c\u5c31\u7b97\u4f60\u52a8\u5200\u6740\u4e86\u6211\uff0c\u4e5f\u4e0b\u4f1a\u6709\u4eba\u77e5\u9053\u554a\uff01\u201d \u3000\u3000\u201c\u5144\u5f1f\u840c\u6709\u4e00\u4e9b\u7b97\u8ba1\uff0c\u53ea\u53ef\u60dc\u5c11\u7b97\u4e86\u4e00\u4ef6\u4e8b\uff0c\u6469\u5929\u5cad\u4e0a\u7684\u98ce\u529b\uff0c\u7adf\u662f\u5982\u6b64\u5f3a\u5927\uff0c\u5144\u5f1f\u53ea\u80fd\u5168\u529b\u81ea\u4fdd\uff0c\u8fde\u62d4\u5200\u7684\u65f6\u95f4\uff0c\u4e5f\u62bd\u4e0d\u51fa\u6765\u4e86\u2026\u2026\uff5d\u201c\u4f60\u8fd9\u7b97\u4ec0\u4e48\u8bbe\u8ba1\uff0c\u8fde\u81ea\u5df1\u4e5f\u5751\u8fdb\u6765\u4e86\uff1f \u3000\u3000\u54c8\u54c8\uff0c\u7b49\u4e00\u4e0b\u3002\u6211\u8dcc\u4e0b\u6728\u7bee\u6454\u6b7b\u4e86\uff0c\u4f60\u6bb5\u5144\u4e00\u4e2a\u4eba\uff0c\u9a6d\u9a7e\u53cc\u9e70\uff0c\u5728\u7a7a\u4e2d\u597d\u597d\u6e38\u73a9\u4e00\u756a\u5427\uff1f\u201d \u3000\u3000\u201c\u8bf4\u771f\u7684\uff0c\u80e1\u5144\uff0c\u6211\u5012\u662f\u4e0d\u5e0c\u671b\u4f60\u6454\u4e0b\u53bb\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u8fd9\u2026\u2026\u8fd9\u662f\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u4f60\u6b7b\u4e86\uff0c\u6bb5\u67d0\u4e00\u4e2a\u4eba\u5728\u8fd9\u5927\u96ea\u5c01\u9501\u7684\u6df1\u5c71\u4e2d\uff0c\u524d\u4e0d\u89c1\u53e4\u4eba\uff0c\u540e\u4e0d\u89c1\u6765\u8005\uff0c\u5c82\u4e0d\u662f\u5bc2\u5bde\u5f97\u5f88\uff1f\u201d \u3000\u3000\u201c\u8bf4\u4e86\u4e5f\u662f\u554a\uff01\u54b1\u4eec\u5929\u5357\u5730\u5317\uff0c\u76f8\u9694\u6570\u5343\u91cc\uff0c\u5f53\u771f\u662f\u4e95\u6c34\u4e0d\u72af\u6cb3\u6c34\uff0c\u60f3\u60f3\u8fd9\u4e00\u756a\u76f8\u7ea6\u8d22\u51b3\u3002\u5b9e\u5728\u662f\u65e0\u5473\u7684\u5f88\u3002\u201d \u3000\u3000\u5ffd\u7136\u95f4\uff0c\u98d8\u8361\u4e0d\u505c\u7684\u6728\u7bee\uff0c\u53cc\u9e70\u7684\u98de\u901f\uff0c\u4e5f\u5927\u4e3a\u51cf\u7f13\u3002 \u3000\u3000\u539f\u6765\uff0c\u4ed6\u4eec\u98de\u884c\u5230\u4e00\u5ea7\u9ad8\u5cf0\u80cc\u540e\uff0c\u9ad8\u5cf0\u6321\u4f4f\u4e86\u51db\u51bd\u7684\u98ce\u52bf\u3002 \u3000\u3000\u201c\u80e1\u5144\u3002\u6211\u770b\u8fd9\u4e24\u53ea\u5de8\u9e70\u6c14\u529b\u5c06\u5c3d\uff0c\u5f88\u96be\u518d\u5411\u524d\u98de\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u662f\u554a\uff01\u4e5f\u771f\u96be\u4e3a\u5b83\u4eec\u4e86\uff0c\u5e26\u7740\u6bd4\u5b83\u4eec\u8fd8\u91cd\u7684\u4e24\u4e2a\u4eba\uff0c\u8fce\u770b\u98ce\u96ea\uff0c\u98de\u4e86\u51e0\u5341\u91cc\u8fdc\uff0c\u4e0d\u77e5\u51e0\u65f6\u3002\u624d\u4f1a\u964d\u843d\u4e0b\u53bb\u3002\u201d \u3000\u3000\u201c\u8fd9\u79cd\u5de8\u9e70\uff0c\u751f\u6027\u575a\u5f3a\uff0c\u9664\u975e\u54b1\u4eec\u8dcc\u6454\u4e0b\u53bb\uff0c\u8fd8\u5b83\u4eec\u81ea\u7531\uff0c\u8ba9\u5b83\u4eec\u5e26\u6625\u5982\u6b64\u6c89\u91cd\u4e4b\u7269\u3002 \u3000\u3000\u5b83\u4eec\u4e0d\u4f1a\u8ba4\u8f93\u7684\uff0c\u5fc5\u4f1a\u98de\u5230\u529b\u5c3d\u5760\u6bd9\u2026\u2026\u201d \u3000\u3000\u201c\u770b\u6765\uff0c\u6bb5\u5144\uff0c\u5bf9\u8fd9\u4e24\u53ea\u5927\u82cd\u9e70\uff0c\u5012\u662f\u5341\u5206\u4e86\u89e3\u554a\uff01\u201d \u3000\u3000\u201c\u5144\u5f1f\uff0c\u5316\u4e86\u534a\u5e74\u591a\u7684\u5de5\u592b\uff0c\u8bf7\u6559\u4e86\u4e0d\u5c11\u4e86\u89e3\u8fd9\u7c7b\u5de8\u9e70\u7684\u4eba\u3002\u4e0e\u5176\u5f85\u5b83\u4eec\u529b\u5c3d\u4e4b\u540e\uff0c\u76f4\u5760\u800c\u4e0b\uff0c\u628a\u54b1\u4eec\u6454\u6b7b\uff0c\u4f55\u4e0d\u81ea\u5df1\u964d\u843d\u4e0b\u53bb\u3002\u201d \u3000\u3000\u201c\u597d\u662f\u597d\u554a\uff01\u53ef\u662f\u8fd9\u79cd\u6241\u6bdb\u98de\u79bd\uff0c\u542c\u4e0d\u61c2\u4eba\u8bdd\uff0c\u80e1\u67d0\u53c8\u4e0d\u4f1a\u9e1f\u8bed\uff0c\u5982\u4f55\u80fd\u8ba9\u5b83\u4eec\u843d\u5730\u5462\uff1f\u201d \u3000\u3000\u201c\u90a3\u5012\u4e0d\u96be\u3002\u80e1\u5144\u53ea\u8981\u7275\u52a8\u7cfb\u5728\u5de8\u9e70\u9888\u4e0a\u7684\u7d22\u7ef3\uff0c\u5b83\u4eec\u5c31\u4f1a\u5411\u4e0b\u964d\u843d\uff0c\u4e0d\u8fc7\uff0c\u4e0d\u80fd\u592a\u8fc7\u7528\u529b\uff0c\u8981\u662f\u52d2\u5f97\u592a\u7d27\uff0c\u5b83\u4eec\u547c\u5438\u4e0d\u7545\uff0c\u65e0\u529b\u5c55\u52a8\u53cc\u7ffc\uff0c\u8dcc\u843d\u4e0b\u53bb\u3002\u90a3\u5c31\u2026\u2026\u201d \u3000\u3000\u80e1\u840d\u5927\u7b11\u63a5\u9053\uff1a\u201c\u53ef\u662f\u3002\u5144\u5f1f\u7275\u7684\u662f\u4f60\u6bb5\u5144\u7684\u9e70\u554a\uff01\u201d \u3000\u3000\u201c\u8fd9\u5c31\u8981\u54b1\u4eec\u5408\u4f5c\u4e86\u3002\u201d \u3000\u3000\u4e24\u4eba\u540c\u65f6\u5f00\u59cb\uff0c\u5c0f\u5fc3\u7ffc\u7ffc\u5730\u6536\u7d27\u7d22\u7ef3\uff0c\u679c\u7136\uff0c\u53cc\u9e70\u540c\u65f6\u4e0b\u964d\u3002\u8ddd\u5730\u8fd8\u6709\u6570\u4e08\uff0c\u5df2\u96be\u63a7\u5236\uff0c\u76f4\u5760\u800c\u4e0b\u3002 \u3000\u3000\u843d\u7740\u5b9e\u5730\u3002\u4e24\u4eba\u624d\u53d1\u89c9\u90a3\u662f\u4e00\u5ea7\u6df1\u8c37\u4e4b\u4e2d\u3002\u5730\u4e0a\u79ef\u96ea\u6570\u5c3a\uff0c\u4e24\u4fa7\u7edd\u58c1\u5cd9\u7acb\uff0c\u524d\u540e\u867d\u4e0d\u89c1\u5c71\u58c1\u963b\u6321\uff0c\u4f46\u76ee\u529b\u6240\u53ca\uff0c\u4e0d\u89c1\u8fb9\u9645\u3002 \u3000\u3000\u4e24\u4eba\u8dc3\u51fa\u6728\u7bee\uff0c\u4f38\u5c55\u4e00\u4e0b\u624b\u811a\uff0c\u540c\u65f6\u957f\u957f\u5401\u4e00\u53e3\u6c14\u3002 \u3000\u3000\u90a3\u4e24\u53ea\u5de8\u9e70\u53cc\u817f\u4e0a\u7cfb\u7740\u91cd\u7269\uff0c\u4f3c\u662f\u4f24\u5bb3\u5f88\u91cd\u3002\u843d\u5728\u5730\u4e0a\uff0c\u5df2\u96be\u7ad9\u7acb\uff0c\u5168\u8eab\u6251\u4f0f\u5728\u96ea\u4e2d\uff0c\u7fbd\u6bdb\u98a4\u52a8\uff0c\u597d\u50cf\u5341\u5206\u75db\u82e6\u3002 \u3000\u3000\u6bb5\u4e5d\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u54b1\u4eec\u53ea\u53ef\u60dc\u5931\u53bb\u4e86\u5c1d\u8bd5\u4e00\u6b21\u3002\u7531\u767e\u4e08\u9ad8\u7a7a\u6454\u4e0b\u6765\u7684\u6ecb\u5473\uff01\u201d\uff0c\u80e1\u840d\u770b\u770b\u5730\u4e0a\u7684\u79ef\u96ea\uff0c\u7b11\u9053\uff1a\u201c\u79ef\u96ea\u5f88\u539a\uff0c\u5927\u6982\u4e0d\u4f1a\u6454\u6b7b\uff01\u201d\u4e0d\u77e5\u54ea\u4e00\u523b\u5f00\u59cb\uff0c\u6a2a\u6897\u5728\u4e24\u4eba\u4e4b\u95f4\u7684\u4ec7\u89c6\uff0c\u5df2\u5728\u65e0\u5f62\u4e2d\u5316\u89e3\u4e86\u3002\u6bb5\u4e5d\u4ed4\u7ec6\u6253\u91cf\u4e86\u4e00\u4e0b\u5468\u56f4\u7684\u5f62\u52bf\u3002\u9053\uff1a\u201c\u4ee5\u96ea\u4ee3\u6c34\uff0c\u5927\u6982\u53ef\u4ee5\u6d3b\u4e2a\u5341\u5929\u534a\u6708\uff0c\u5982\u80fd\u627e\u4e00\u5904\u5c71\u6d1e\u8eb2\u907f\u98ce\u96ea\u4fb5\u88ad\uff0c\u5c31\u4e0d\u4f1a\u6d3b\u7684\u592a\u82e6\u4e86\u3002\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u53ea\u662f\u997f\u4e86\u5982\u6b64\u4e4b\u4e45\u3002\u4f53\u80fd\u6d88\u9000\uff0c\u4e00\u65e6\u9047\u4e0a\u731b\u517d\uff0c\u5c82\u4e0d\u6210\u4e86\u5b83\u4eec\u53e3\u4e2d\u4e4b\u7269\u3002\u201d \u3000\u3000\u201c\u80e1\u5144\u53ef\u4ee5\u653e\u5fc3\uff0c\u5144\u5f1f\u5e26\u6709\u98df\u7269\uff0c\u54b1\u95e8\u7701\u5403\u4fed\u7528\uff0c\u771f\u7684\u9047\u4e0a\u864e\u3002\u8c79\u4e4b\u7c7b\u7684\u731b\u517d\u3001\u5012\u662f\u53ef\u4ee5\u4f9b\u54b1\u4eec\u5145\u505a\u53e3\u7cae\u4e86\uff01\u201d \u3000\u3000\u201c\u600e\u4e48\uff1f\u6bb5\u5144\u5e26\u6709\u5e72\u7cae\uff1f\u201d \u3000\u3000\u201c\u98ce\u5e72\u7684\u725b\u8089\u7c89\uff0c\u6570\u91cf\u867d\u7136\u4e0d\u591a\uff0c\u5728\u8fd9\u7b49\u5927\u96ea\u5c01\u5c71\u7684\u6df1\u8c37\u4e2d\uff0c\u4e5f\u7b97\u662f\u4e0a\u7b49\u7f8e\u5473\u4e86\u3002\u201d \u3000\u3000\u201c\u53ef\u60dc\uff0c\u5c11\u4e86\u4e00\u575b\u8001\u9152\u2026\u2026\u201d\u80e1\u840d\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6bb5\u5144\uff0c\u5144\u5f1f\u5fc3\u4e2d\u6709\u4e0d\u5c11\u7591\u95ee\uff1f\u4e0d\u77e5\u613f\u5426\u89c1\u544a\uff1f\u201d \u3000\u3000\u201c\u5f7c\u6b64\u3002\u5f7c\u6b64\uff0c\u6bb5\u67d0\u4e5f\u6709\u5f88\u591a\u4e0d\u89e3\u7684\u4e8b\u7269\uff0c\u60f3\u8bf7\u80e1\u5144\u89e3\u8bf4\u3002 \u3000\u3000\u54b1\u4eec\u5148\u627e\u4e00\u5904\u53ef\u4ee5\u5bb9\u8eab\u4e4b\u5730\u3002\u518d\u4f5c\u8be6\u8c08\u5982\u4f55\uff1f\u201d \u3000\u3000\u82b1\u8d39\u4e0d\u5230\u4e00\u4e2a\u65f6\u8fb0\uff0c\u5c31\u627e\u5230\u4e00\u5ea7\u5c71\u6d1e\u3002\u6d1e\u867d\u4e0d\u5927\uff0c\u4f46\u4ea6\u8db3\u591f\u4e24\u4eba\u6816\u8eab\u4e4b\u7528\u3002 \u3000\u3000\u4e24\u4eba\u4e5f\u5e26\u4e86\u817f\u4e0a\u53d7\u5230\u521b\u4f24\u7684\u82cd\u9e70\u5165\u6d1e\u3002 \u3000\u3000\u6bb5\u4e5d\u5df2\u6709\u517b\u9e70\u7684\u7ecf\u9a8c\uff0c\u7ec6\u67e5\u4f24\u52bf\uff0c\u5c1a\u5e78\u817f\u9aa8\u672a\u6298\uff0c\u62d4\u5200\u65a9\u65ad\u53cc\u9e70\u817f\u4e0a\u7d22\u7ef3\uff0c\u626d\u65ad\u9888\u95f4\u94c1\u73af\uff0c\u5728\u4f24\u5904\u6577\u4e86\u836f\u7269\uff0c\u8f7b\u629a\u7740\u9e70\u80cc\uff0c\u9053\uff1a\u201c\u4e24\u4f4d\u98df\u91cf\u60ca\u4eba\u3002\u6b64\u5730\u65e0\u7269\u53ef\u4f9b\uff0c\u4f60\u4eec\u7684\u675f\u7f1a\u5c3d\u53bb\uff0c\u53c8\u53ef\u7ff1\u7fd4\u957f\u7a7a\uff0c\u8fd8\u6211\u81ea\u7531\uff0c\u517b\u606f\u4e00\u4f1a\uff0c\u81ea\u5df1\u53bb\u5427\uff01\u201d \u3000\u3000\u628a\u4e24\u53ea\u82cd\u9e70\u79fb\u653e\u6d1e\u53e3\u3002 \u3000\u3000\u80e1\u840d\u4e00\u76f4\u9759\u9759\u7684\u5750\u7740\uff0c\u770b\u6bb5\u4e5d\u76f4\u5230\u5fd9\u5b8c\u5750\u4e0b\u4f11\u606f\uff0c\u624d\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u6bb5\u5144\uff0c\u53ef\u8981\u71c3\u5806\u706b\u6765\uff0c\u9010\u9010\u5bd2\u610f\uff1f\u201d \u3000\u3000\u539f\u6765\uff0c\u80e1\u840d\u5df2\u628a\u4e24\u53ea\u6728\u7bee\u62c6\u5f00\uff0c\u5806\u5728\u4e00\u4fa7\u3002 \u3000\u3000\u6bb5\u4e5d\u9053\uff1a\u201c\u90a3\u5c31\u597d\u3001\u8fd9\u70b9\u5bd2\u51b7\uff0c\u5144\u5f1f\u8fd8\u53ef\u627f\u53d7\u3002\u6b64\u523b\uff0c\u5927\u96ea\u6ee1\u5c71\u5907\uff0c\u5bfb\u627e\u5e72\u85aa\u4e0d\u6613\uff0c\u201c\u4e0d\u5982\u6682\u5b58\u8d77\u6765\uff0c\u4ee5\u5e94\u6025\u9700\uff0c\u4e0d\u8fc7\uff0c\u54b1\u4eec\u5728\u79bb\u6b64\u4e4b\u524d\uff0c\u8981\u62b1\u5b83\u5b8c\u5168\u711a\u70e7\uff0c\u4e0d\u80fd\u7559\u4e0b\u75d5\u8ff9\u2026\u2026\u201d \u3000\u3000\u8bed\u58f0\u7a0d\u987f\uff0c\u63a5\u9053\uff1a\u201c\u60f3\u4e0d\u5230\uff0c\u5357\u5200\u3001\u5317\u5251\u4e00\u573a\u75af\u72c2\u5bf9\u51b3\uff0c\u843d\u4e86\u8fd9\u4e48\u4e00\u4e2a\u597d\u7684\u4e0b\u573a\uff0c\u552f\u4e00\u4f7f\u6bb5\u67d0\u4e0d\u5b89\u7684\uff0c\u95e8\u4e0b\u5341\u4e2a\u6700\u6770\u51fa\u7684\u5f1f\u5b50\uff0c\u7adf\u6709\u516d\u4e2a\u4eba\u51bb\u6b7b\uff0c\u5bf9\u6bb5\u5bb6\u5821\u7684\u5b9e\u529b\uff0c\u662f\u4e00\u4e2a\u5f88\u5927\u7684\u632b\u4f24\u2026\u2026\u201d \u3000\u3000\u201c\u8fd9\u4e00\u70b9\uff0c\u6bb5\u5144\u4e0d\u7528\u62c5\u5fc3\uff0c\u6211\u770b\u4ed6\u4eec\u5185\u529f\u90fd\u5df2\u6709\u4e86\u76f8\u5f53\u7684\u57fa\u7840\uff0c\u5341\u4e8c\u4e2a\u65f6\u8fb0\u4e4b\u5185\uff0c\u704c\u4e0a\u4e00\u7897\u59dc\u6c64\uff0c\u4f11\u606f\u4e00\u4e24\u5929\u5c31\u53ef\u4ee5\u590d\u539f\u4e86\uff0c\u5982\u5f97\u5927\u592b\u5904\u65b9\u4e0b\u836f\uff0c\u590d\u539f\u66f4\u5feb\u3002\u201d \u3000\u3000\u201c\u771f\u7684\u2026\u2026\u201d\u6bb5\u4e5d\u60ca\u559c\u4ed6\u8bf4\uff1a\u201c\u4ed6\u4eec\u5982\u4f9d\u539f\u8def\u4e0b\u5c71\uff0c\u4f30\u7b97\u516d\u4e2a\u65f6\u8fb0\uff0c\u53ef\u4ee5\u5230\u8fbe\u4e00\u5904\u96c6\u9547\uff0c\u7acb\u523b\u5c31\u533b\uff0c\u516d\u4e2a\u4eba\u90fd\u53ef\u5f97\u6551\u4e86\u3002\u201d \u3000\u3000\u201c\u80e1\u67d0\u53ef\u4ee5\u62c5\u4fdd\uff0c\u4e5f\u8bb8\uff0c\u4e0b\u4e86\u6469\u5929\u5cad\uff0c\u4ee5\u4ed6\u4eec\u4f18\u8d8a\u7684\u4f53\u8d28\u3002 \u3000\u3000\u81ea\u5df1\u5c31\u4f1a\u9192\u8fc7\u6765\u3001\u8fde\u5927\u592b\u90fd\u4e0d\u7528\u770b\u4e86\u3002\u201d \u3000\u3000\u201c\u591a\u8c22\u80e1\u5144\u6307\u6559\uff0c\u8fd9\u5c31\u4f7f\u5144\u5f1f\u5b89\u5fc3\u591a\u4e86\u3002\u201d \u3000\u3000\u4e24\u4eba\u7531\u50f5\u5cd9\u7684\u5bf9\u51b3\uff0c\u7a81\u7136\u95f4\u8f6c\u53d8\u6210\u4e86\u670b\u53cb\uff0c\u5f7c\u6b64\u8c26\u8ba9\u5173\u6000\u3002 \u3000\u3000\u7b80\u76f4\u662f\u76f8\u656c\u5982\u5bbe\u4e86\u3002 \u3000\u3000\u4f46\u95fb\u7fbd\u7ffc\u6247\u98ce\uff0c\u4e24\u53ea\u82cd\u9e70\uff0c\u5df2\u6084\u7136\u884c\u51fa\u6d1e\u5916\uff0c\u5c55\u7ffc\u800c\u53bb\u3002 \u3000\u3000\u671b\u770b\u7834\u7a7a\u800c\u53bb\u7684\u53cc\u9e70\uff0c\u6bb5\u4e5d\u4f4e\u58f0\u8bf4\uff1a\u201c\u6d77\u9614\u51ed\u9c7c\u8dc3\uff0c\u5929\u7a7a\u4efb\u9e1f\u98de\uff0c\u770b\u6765\uff0c\u5b83\u4eec\u6bd4\u6211\u6bb5\u67d0\u4eba\u81ea\u7531\u591a\u4e86\u3002\u201d \u3000\u3000\u80e1\u840d\u5fc3\u4e2d\u4e00\u52a8\uff0c\u9053\uff1a\u201c\u6bb5\u5144\u51c6\u5907\u51e0\u65f6\u56de\u82cf\u5dde\u6bb5\u5bb6\u5821\u53bb\uff1f\u201d \u3000\u3000\u201c\u53ea\u6015\u6bb5\u67d0\u6709\u5bb6\u5f52\u4e0d\u5f97\u4e86\uff0c\u80e1\u5144\u5462\uff1f\u51c6\u5907\u51e0\u65f6\u79bb\u5f00\uff1f\u201d \u3000\u3000\u80e1\u840d\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u56de\u4e0d\u53bb\u4e86\uff0c\u71d5\u5c71\u9ec4\u53f6\u8c37\u7684\u5317\u5251\u95e8\uff0e\u65e9\u5df2\u975e\u5144\u5f1f\u6240\u6709\u2026\u2026\u201d \u3000\u3000\u6bb5\u4e5d\u5e76\u65e0\u60ca\u5947\u4e4b\u9c7c\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u843d\u5165\u4e86\u4ec0\u4e48\u4eba\u7684\u624b\u4e2d\uff1f\u201d \u3000\u3000\u201c\u8001\u5a46\u2026\u2026\u201d\u80e1\u840d\u8138\u4e0a\u5fae\u73b0\u5fff\u6012\u4e4b\u8272\uff0c\u8bf4\uff1a\u201c\u6bb5\u5144\u60f3\u4e0d\u5230\u5427\uff1f\u540c\u5e8a\u5171\u6795\u4e86\u4e8c\u5341\u5e74\u7684\u8001\u5a46\uff0c\u7adf\u662f\u8c0b\u9738\u6211\u5317\u5251\u95e8\u7684\u5143\u51f6\uff0c\u6bb5\u5144\u5462\uff1f\u201c\u597d\u4e0d\u5230\u54ea\u91cc\u53bb\uff01\u6211\u7684\u5e08\u5f1f\uff0c\u5ffd\u7136\u9732\u51fa\u771f\u6b63\u9762\u76ee\uff0c\u4e00\u4e3e\u5c3d\u593a\u5357\u5200\u95e8\u7684\u5b9e\u6743\uff0c\u6211\u6bb5\u67d0\u4eba\u4e24\u5e74\u4e4b\u524d\uff0c\u5df2\u7ecf\u662f\u4e2a\u6302\u540d\u7684\u638c\u95e8\u4eba\u4e86\u3002 \u3000\u3000\u5c31\u8fde\u8fd9\u4e00\u6b21\u7ea6\u4f1a\u80e1\u5144\uff0c\u4e5f\u662f\u5949\u547d\u884c\u4e8b\u3002\u201d \u3000\u3000\u201c\u5144\u5f1f\u4e5f\u662f\u4e00\u6837\uff0c\u8001\u5a46\u4e00\u58f0\u4ee4\u4e0b\uff0c\u5144\u5f1f\u5c31\u5e26\u4eba\u4e0a\u8def\uff0c\u539f\u60f3\u6469\u5929\u5cad\u8fd9\u4e2a\u5730\u65b9\uff0c\u8ddd\u6b66\u5f53\u4e0d\u8fdc\uff0c\u4e5f\u662f\u6b66\u5f53\u95e8\u4e0b\u5f1f\u5b50\u51fa\u6ca1\u4e4b\u5904\uff0c\u6211\u4eec\u5bf9\u51b3\u4e4b\u524d\uff0c\u5fc5\u6709\u6b66\u5f53\u5f1f\u5b50\u51fa\u9762\u963b\u6b62\uff0c\u60f3\u4e0d\u5230\u8001\u6742\u6bdb\u4eec\u7adf\u7136\u5168\u90e8\u51ac\u7720\u4e86\uff0c\u4e0d\u89c1\u4e00\u4e2a\u51fa\u6765\u3002\u201d \u3000\u3000\u6bb5\u4e5d\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u80e1\u5144\u679c\u662f\u6709\u5fc3\u4eba\uff0c\u8fd9\u91cc\u8ddd\u6b66\u5f53\u4e09\u5143\u89c2\u3002 \u3000\u3000\u4e0d\u8fc7\u4e8c\u767e\u91cc\uff0c\u6b63\u662f\u6b66\u5f53\u5f1f\u5b50\u4eec\u5de1\u6208\u4e4b\u533a\uff0c\u5144\u5f1f\u6765\u6b64\u8d74\u7ea6\u65f6\uff0c\u8fd8\u4e00\u8def\u62db\u6447\uff0c\u4ecd\u672a\u60ca\u52a8\u6b66\u5f53\u5f1f\u5b50\uff0c\u662f\u4e0d\u662f\u6709\u4e9b\u53ef\u7591\u5462\uff1f\u201d \u3000\u3000\u80e1\u840d\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a\u201d\u4f60\u662f\u8bf4\u6b66\u5f53\u6d3e\u548c\u54b1\u4eec\u4e00\u6837\uff0c\u638c\u95e8\u4eba\u65e9\u88ab\u67b6\u7a7a\u4e86\u3002\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u6bb5\u67d0\u90a3\u4f4d\u5e08\u5f1f\uff0c\u867d\u7136\u806a\u660e\u7edd\u9876\uff0c\u4f46\u4ed6\u5728\u6b66\u529f\u7684\u4fee\u4e3a\u4e0a\uff0c\u5e94\u8be5\u548c\u5144\u5f1f\u8fdc\u6709\u4e00\u6bb5\u8ddd\u79bb\uff0c\u4f46\u53cc\u65b9\u52a8\u4e0a\u624b\uff0c\u6211\u7adf\u7136\u63a5\u4e0d\u4e0b\u4ed6\u5341\u62db\uff0c\u5200\u6cd5\u4e4b\u5947\uff0c\u5185\u529b\u4e4b\u5f3a\uff0c\u80dc\u6211\u5341\u500d\uff0c\u800c\u4e14\uff0c\u90fd\u4e0d\u662f\u5357\u5200\u95e8\u4e2d\u5200\u6cd5\uff0c\u6240\u4ee5\uff0c\u5144\u5f1f\u89c9\u5f97\u4ed6\u4eec\u80cc\u540e\uff0c\u5fc5\u6709\u4e00\u80a1\u652f\u6301\u7684\u795e\u79d8\u529b\u91cf\uff0f\u3002\u201d \u3000\u3000\u201c\u6b63\u662f\u5982\u6b64\uff0c\u6211\u90a3\u8001\u5a46\u867d\u4e5f\u4e60\u8fc7\u6b66\u529f\uff0c\u4f46\u7edd\u975e\u80e1\u67d0\u4e4b\u654c\u3002\u8c01\u77e5\u4e00\u52a8\u4e0a\u624b\uff0c\u6211\u5c31\u62db\u67b6\u4e0d\u4f4f\uff0c\u4e0d\u5230\u5341\u62db\uff0c\u5c31\u88ab\u5979\u51fb\u843d\u957f\u5251\u3002\u8feb\u6211\u4ea4\u51fa\u5b9e\u6743\uff0c\u81ea\u662f\u827a\u6709\u6240\u5b97\uff0c\u4f46\u80e1\u67d0\u8d39\u5c3d\u5fc3\u673a\uff0c\u5374\u67e5\u4e0d\u51fa\u4e00\u70b9\u75d5\u8ff9\u3002\u201d \u3000\u3000\u6bb5\u4e5d\u9053\uff1a\u201c\u80e1\u5144\u4f5c\u4f55\u6253\u7b97\u5462\uff1f\u201d \u3000\u3000\u201c\u67e5\uff01\u67e5\u4e0d\u5230\u6c34\u843d\u77f3\u51fa\uff0c\u80e1\u67d0\u6b7b\u4e0d\u7791\u76ee\u3002\u201d \u3000\u3000\u201c\u6bb5\u67d0\u4ea6\u6709\u6b64\u5fc3\u3002\u6211\u8bbe\u8ba1\u8fd9\u98de\u9e70\u5bf9\u51b3\uff0c\u5c31\u5b58\u4e86\u501f\u673a\u9690\u8eab\u7684\u6253\u7b97\uff0c\u5012\u4e5f\u5929\u4ece\u4eba\u613f\u3002\u518d\u5f97\u80e1\u5144\u5408\u4f5c\uff0c\u4f7f\u5144\u5f1f\u4fe1\u5fc3\u5927\u589e\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u54b1\u4eec\u6613\u5bb9\u6539\u626e\uff0c\u6e38\u884c\u6c5f\u6e56\uff0c\u8ffd\u5b83\u4e2a\u6c34\u843d\u77f3\u51fa\u3002\u201d \u3000\u3000\u201c\u5317\u5251\u3001\u5357\u5200\u3002\u643a\u624b\u540c\u5fc3\uff0c\u67e5\u660e\u8fd9\u80a1\u6c5f\u6e56\u9006\u6d41\u4e2d\u6f5c\u4f0f\u7684\u9634\u8c0b\u3002 \u3000\u3000\u6bb5\u67d0\u5c31\u6b7b\u800c\u65e0\u61be\u4e86\u3002\u201d \u3000\u3000\u201c\u5bf9\uff01\u80e1\u67d0\u820d\u547d\u5949\u966a\u3002\u4f46\u5f97\u7559\u540d\u6c5f\u6eaf\uff0c\u4f55\u60e7\u91cd\u91cd\u9669\u963b\uff0c\u660e\u5929\uff0c\u54b1\u4eec\u5c31\u51c6\u5907\u4e0b\u5c71\u3002\u201d \u3000\u3000\u5357\u5200\u3001\u5317\u5251\uff0c\u5bf9\u51b3\u6469\u5929\u5cad\u7684\u4e8b\uff0c\u5728\u6c5f\u6e56\u4e0a\u54c4\u4f20\u5f00\u4e86\u3002 \u3000\u3000\u80e1\u840d\u3001\u6bb5\u4e5d\u4e58\u9e70\u5347\u7a7a\uff0c\u6d88\u5931\u5728\u832b\u832b\u98ce\u96ea\u4e4b\u4e2d\u3002 \u3000\u3000\u9707\u52a8\u6b66\u6797\u4eba\u5fc3\u7684\u4e00\u4e2a\u6d88\u606f\uff0c\u7d27\u63a5\u7740\u4f20\u5165\u4e86\u6c5f\u6e56\uff0c\u4e8c\u5341\u5e74\u524d\u4e3b\u6301\u201c\u4e5d\u534e\u76df\u7ea6\u201d\u7684\u5c11\u6797\u3001\u6b66\u5f53\u4e24\u5927\u95e8\u6d3e\uff0c\u7a81\u7136\u5ba3\u5e03\u5173\u95ed\u95e8\u6237\uff0c\u4e0d\u518d\u7406\u4f1a\u6c5f\u6e56\u4e2d\u4e8b\uff0c\u4e5f\u4e0d\u518d\u6536\u5f55\u5f1f\u5b50\u3002 \u3000\u3000\u9664\u975e\u6614\u5e74\u7b56\u52a8\u4e5d\u534e\u805a\u4f1a\u7684\u4e09\u4f4d\u5546\u4eba\u3002\u91cd\u73b0\u6c5f\u7322\uff0c\u767b\u9ad8\u4e00\u547c\u3002 \u3000\u3000\u91cd\u632f\u76df\u7ea6\u7cbe\u795e\uff0c\u201c\u4e5d\u534e\u76df\u7ea6\u201d\u4f3c\u662f\u5df2\u6d88\u6563\u98d8\u96f6\uff0c\u53d7\u5230\u4e86\u5f7b\u5e95\u7684\u7834\u574f\uff0c\u6c5f\u6e56\u4e0a\u5927\u4e71\u840c\u59cb\uff0c\u53c8\u5c06\u5f00\u59cb\u4e86\u8840\u96e8\u8165\u98ce\u7684\u4e89\u6597\u2026\u2026\u6697\u6d41\u5728\u6c5f\u6e56\u4e0a\u5149\u6d8c\uff0c\u867d\u7136\u8868\u9762\u8fd8\u4fdd\u6301\u76f8\u5f53\u7684\u5e73\u9759\uff0c\u53ef\u662f\uff0c\u4e00\u80a1\u65e0\u5f62\u7684\u538b\u529b\uff0c\u5df2\u7ef7\u7d27\u4e86\u5404\u95e8\u6d3e\u9996\u8111\u4eba\u7269\u7684\u5fc3\u5f26\u3002 \u3000\u3000\u5bd2\u98ce\u4ecd\u7136\u5730\u5439\uff0c\u5927\u96ea\u4f9d\u65e7\u5730\u98d8\uff0c\u5173\u6d1b\u9053\u4e0a\u8f66\u9a6c\u7a00\u3002\u957f\u5b89\u8857\u5934\u884c\u4eba\u5c11\u2026\u2026\u4f46\u5bd2\u98ce\u5927\u96ea\u5374\u963b\u4e0d\u4f4f\u6d8c\u5f80\u201c\u6d1b\u9633\u5c45\u201d\u7684\u4eba\u6f6e\u3002 \u3000\u3000\u201c\u6d1b\u9633\u5c45\u201d\u4e0d\u5728\u6d1b\u9633\uff0c\u800c\u5728\u957f\u5b89\u3002 \u3000\u3000\u957f\u5b89\uff01\u591a\u5c11\u4eba\u5411\u5f80\u7684\u5730\u65b9\u3002\u90a3\u91cc\u66fe\u7ecf\u662f\u51a0\u76d6\u4e91\u96c6\uff0c\u540d\u5229\u6c47\u805a\u4e4b\u5730\uff0c\u867d\u7136\u5b8b\u4ee3\u65f6\u56fd\u90fd\u4e1c\u79fb\uff0c\u4f46\u957f\u5b89\u7684\u5e02\u9762\u7e41\u534e\u4f9d\u65e7\uff0c\u591c\u591c\u7b19\u6b4c\u5230\u5904\u95fb\u3002 \u3000\u3000\u201c\u6d1b\u9633\u5c45\u201d\u5f00\u4e1a\u4e0d\u8fc7\u534a\u5e74\uff0c\u4f46\u5df2\u62a2\u5c3d\u4e86\u957f\u5b89\u5e02\u9762\u7684\u7e41\u534e\u98ce\u5149\u2026\u2026\u201c\u6d1b\u9633\u5c45\u201d\u7684\u5efa\u7b51\u5b8f\u5927\u3001\u7cbe\u5de7\uff0c\u5e03\u7f6e\u4e5f\u6e05\u96c5\u5b9c\u4eba\uff0c\u5206\u9694\u51fa\u6b4c\u575b\u3001\u8d4c\u573a\u3001\u996d\u5e84\uff0c\u5404\u5177\u7279\u8272\u7684\u5efa\u7b51\uff0c\u53c8\u628a\u5b83\u4e32\u8fde\u6210\u4e00\u4e2a\u6574\u4f53\u3002 \u3000\u3000\u8fd9\u91cc\u7684\u996d\u5e84\u4e2d\u6709\u6700\u597d\u7684\u9152\u83dc\uff0c\u4e5f\u4e91\u96c6\u4e86\u5404\u5730\u540d\u53a8\u3002\u6240\u4ee5\uff0c\u8fd9\u91cc\u662f\u957f\u5b89\u57ce\u4e2d\u6700\u8d35\u7684\u4e00\u5bb6\u996d\u5e84\uff0c\u4e5f\u662f\u6700\u597d\u7684\u4e00\u5bb6\u996d\u5e84\u3002 \u3000\u3000\u8d4c\u573a\u4e2d\u3002\u8bbe\u6709\u6700\u9f50\u5168\u7684\u8d4c\u5177\u3002 \u3000\u3000\u5c31\u7b97\u4f60\u4e0d\u4f1a\u8d4c\u535a\u3002\u4e5f\u4e0d\u559c\u6b22\u559d\u9152\u548c\u54c1\u5c1d\u4f73\u80b4\u7f8e\u5473\uff0c\u884c\u8d70\u4e00\u756a\uff0c\u4e5f\u8ba9\u4f60\u5fc3\u65f7\u795e\u6070\u4e86\u3002 \u3000\u3000\u603b\u4e4b\uff0c\u8fd9\u91cc\u5efa\u7b51\u534e\u4e3d\u3002\u53c8\u6781\u5c3d\u56ed\u6797\u4e4b\u80dc\u3002 \u3000\u3000\u4f46\u6700\u4f7f\u4eba\u7559\u604b\u5fd8\u8fd4\u7684\uff0c\u8fd8\u662f\u8fd9\u91cc\u7684\u6b4c\u575b\u201c\u6d1b\u9633\u697c\u201d\u3002 \u3000\u3000\u6d1b\u9633\u697c\u5efa\u7b51\u5728\u4e1b\u82b1\u73af\u56f4\u4e4b\u4e2d\uff0c\u662f\u6d1b\u9633\u5c45\u4e2d\u82b1\u8d39\u6700\u5c11\u7684\u5730\u65b9\u3002 \u3000\u3000\u4f46\u4e00\u4e2a\u4eba\u5750\u4e0a\u4e2a\u628a\u65f6\u8fb0\uff0c\u4e5f\u591f\u4e00\u4e2a\u8282\u4fed\u7684\u4eba\u534a\u6708\u5f00\u9500\u3002 \u3000\u3000\u6d1b\u9633\u697c\u4e2d\u4e5f\u8bbe\u6709\u7279\u522b\u7684\u96c5\u5ea7\uff0c\u5782\u5e18\u95f4\u9694\uff0c\u6709\u4e13\u95e8\u4f8d\u4faf\u7684\u7f8e\u4e3d\u4e2b\u5934\u3002\u4ef7\u94b1\u5c31\u8d35\u7684\u5413\u4eba\u4e86\u3002 \u3000\u3000\u8fd9\u91cc\u672c\u6765\u5c31\u662f\u957f\u5b89\u57ce\u4e2d\u7684\u9500\u91d1\u7a9f\u3002 \u3000\u3000\u4f46\u6d1b\u9633\u697c\u4e2d\u4e00\u5343\u591a\u4e2a\u5ea7\u4f4d\u5374\u662f\u5ea7\u65e0\u865a\u5e2d\u3002 \u3000\u3000\u56e0\u4e3a\uff0c\u8fd9\u91cc\u6709\u6700\u597d\u7684\u6b4c\u59ec\u6f14\u5531\u3002 \u3000\u3000\u6f14\u5531\u7684\u6b4c\u59ec\uff0c\u6709\u5357\u56fd\u4f73\u4e3d\uff0c\u4e5f\u6709\u5317\u5730\u80ed\u8102\uff0e\u4e2a\u4e2a\u90fd\u6709\u7740\u79c0\u4e3d\u7684\u59ff\u8272\u3002\u6b4c\u58f0\u597d\u3002\u4eba\u4e5f\u6f02\u4eae\u3002 \u3000\u3000\u4f46\u6700\u7ea2\u7684\u4e00\u4f4d\u6b4c\u59ec\uff0c\u540d\u53eb\u9752\u51e4\u3002\u636e\u8bf4\u662f\u6765\u81ea\u6d1b\u9633\u3002 \u3000\u3000\u4eba\u5df2\u7ecf\u6f02\u4eae\u5f97\u62d8\u9b42\u52fe\u9b42\u4e86\u3002\u4eec\u4eec\u6b4c\u58f0\u53c8\u5a49\u8f6c\u52a8\u4eba\uff0c\u53eb\u4f60\u542c\u5f97\u5982\u75f4\u5982\u9189\u3002 \u3000\u3000\u4f60\u6765\u542c\u542c\u6d41\u884c\u5728\u957f\u5b89\u57ce\u4e2d\u7684\u4e00\u9996\u6b4c\u8c23\u3002 \u3000\u3000\u6d1b\u9633\u5973\u513f\u957f\u5b89\u6e38\u3002 \u3000\u3000\u4e00\u66f2\u9500\u4eba\u4e07\u53e4\u6101\u3002 \u3000\u3000\u6b4c\u7f62\u767e\u82b1\u5931\u989c\u8272\u3002 \u3000\u3000\u4f59\u97f3\u72b9\u7ed5\u949f\u9f13\u697c\u3002 \u3000\u3000\u949f\u9f13\u697c\uff0c\u662f\u957f\u5b89\u57ce\u4e2d\u4e00\u5ea7\u53e4\u8001\u7684\u5efa\u7b51\u3002\u6668\u949f\u66ae\u9f13\uff0c\u5168\u57ce\u53ef\u95fb\u3002 \u3000\u3000\u63a5\u8e35\u64e6\u80a9\u7684\u4eba\u6f6e\uff0c\u6d8c\u5165\u4e86\u6d1b\u9633\u5c45\u3002\u5341\u4e4b\u516b\u4e5d\u662f\u4e3a\u4e86\u6765\u770b\u770b\u9752\u51e4\u4e4b\u7f8e\uff0c\u542c\u542c\u90a3\u9500\u4eba\u6101\u82e6\u7684\u6b4c\u58f0\u3002 \u3000\u3000\u6ca1\u6709\u770b\u8fc7\u9752\u51e4\u7684\u4eba\uff0c\u6ca1\u6709\u542c\u8fc7\u9752\u51e4\u6b4c\u58f0\u7684\u4eba\uff0c\u4e0d\u60dc\u5356\u4e86\u4e09\u5206\u5730\uff0c\u4e5f\u8981\u53bb\u542c\u4e00\u6b21\uff0c\u8fd9\u5c31\u662f\u8ff7\u604b\uff01\u88ab\u9752\u51e4\u7684\u4eba\u548c\u6b4c\u7ed9\u8ff7\u4f4f\u4e86\u3002 \u3000\u3000\u8fd9\u6837\u4e00\u4e2a\u6851\u5403\u3001\u8d4c\u3001\u58f0\u3001\u9c7c\u4e4b\u5a31\u7684\u7684\u5730\u65b9\uff0c\u5f53\u7136\u4f1a\u5e38\u5e38\u5f15\u53d1\u4e89\u6267\uff0c\u4f46\u5f88\u5feb\u5c31\u5e73\u606f\u4e0b\u53bb\uff0c\u5f88\u96be\u95f9\u5230\u5927\u6253\u51fa\u624b\u7684\u5883\u5730\uff0c\u56e0\u4e3a\uff0c\u8fd9\u91cc\u6709\u4fdd\u9556\u3002 \u3000\u3000\u4ed6\u4eec\u4e0d\u4f1a\u548c\u5ba2\u4eba\u51b2\u7a81\uff0c\u4f46\u5374\u4fdd\u62a4\u5ba2\u4eba\u5b89\u5168\uff1b\u4e24\u4e2a\u4eba\u4e00\u65e6\u52a8\u624b\u3001\u4ed6\u4eec\u5c31\u51fa\u73b0\u5728\u4e2d\u95f4\u3002 \u3000\u3000\u5f53\u7136\u3002\u4e5f\u4f1a\u9047\u4e0a\u4e00\u4e9b\u5b58\u5fc3\u95f9\u4e8b\uff0c\u8f6f\u786c\u4e0d\u5403\u7684\u4eba\uff0c\u5927\u543c\u5927\u53eb\uff0c\u8e22\u684c\u5b50\u7838\u7897\uff0c\u4ed6\u4eec\u4e5f\u4e0d\u4f1a\u548c\u4f60\u5bf9\u6253\u5bf9\u9a82\uff0c\u6700\u591a\u662f\u628a\u4f60\u67b6\u79bb\u73b0\u573a\u3002 \u3000\u3000\u8fd9\u91cc\u7684\u4fdd\u9556\uff0c\u4e0d\u662f\u6446\u6837\u5b50\u7684\uff0c\u4ed6\u4eec\u662f\u771f\u7684\u8981\u4ecb\u5165\u5ba2\u4eba\u4e4b\u95f4\u7684\u51b2\u7a81\u7ea0\u7eb7\u4e2d\uff0c\u4ee5\u7ef4\u5b89\u5b81\u3002 \u3000\u3000\u4ed6\u4eec\u7a7f\u7740\u4fbf\u670d\uff0e\u5f62\u540c\u5ba2\u4eba\uff0c\u6df7\u5728\u4eba\u7fa4\u4e2d\uff0c\u5f88\u96be\u770b\u5f97\u51fa\u6765\u3002 \u3000\u3000\u8336\u623f\u3001\u5802\u5b98\uff0c\u4f3c\u4e4e\u4e5f\u8d1f\u8d23\u4fdd\u9556\u7684\u5de5\u4f5c\u3002 \u3000\u3000\u6c5f\u6e56\u9053\u4e0a\u7684\u4eba\u7269\u597d\u5ba2\u3002\u670b\u53cb\u5f80\u6765\uff0c\u81ea\u7136\u8981\u76db\u5927\u7684\u62db\u5f85\u4e00\u756a\uff0c\u201c\u6d1b\u9633\u5c45\u201d\u8fd9\u79cd\u5730\u65b9\u6700\u4e3a\u9002\u5408\uff0c\u8c6a\u534e\u96c5\u81f4\uff0c\u5e94\u6709\u5c3d\u6709\uff0c\u5403\u4e0a\u4e00\u9910\u996d\uff0c\u4f1a\u4f7f\u4eba\u7ec8\u8eab\u96be\u5fd8\uff0c\u5728\u5173\u6d1b\u9053\u4e0a\uff0c\u627e\u4e0d\u51fa\u7b2c\u4e8c\u5bb6\u6765\u3002 \u3000\u3000\u4eca\u665a\u4e0a\uff0c\u996d\u5e84\u90e8\u7684\u8d35\u5bbe\u5385\uff0c\u5e03\u7f6e\u7684\u7279\u522b\u8f89\u714c\uff0c\u5385\u4e2d\u7684\u516b\u76cf\u5782\u82cf\u5bab\u706f\uff0c\u4e5f\u5b8c\u5168\u70b9\u8d77\uff0c\u7167\u7684\u4e00\u7247\u901a\u660e\uff0c\u82b1\u6811\u4e0a\u4e5f\u6302\u4e86\u71c3\u8d77\u7684\u98ce\u706f\u3002 \u3000\u3000\u56db\u4e2a\u9752\u8863\u5c11\u5973\uff0c\u5b88\u5728\u5385\u4e2d\u53f8\u9152\uff0c\u6bcf\u4e2a\u4eba\u90fd\u62ff\u4e2a\u9152\u58f6\uff0c\u5ba2\u4eba\u4e00\u5e72\u676f\u3002\u5c31\u7acb\u523b\u66ff\u4f60\u659f\u4e0a\u3002 \u3000\u3000\u8fd9\u91cc\u7684\u4e00\u5207\uff0c\u90fd\u5c3d\u91cf\u7684\u8bb2\u6c42\u5b8c\u7f8e\uff0c\u6700\u597d\u7684\u9152\u83dc\uff0c\u6700\u597d\u7684\u9648\u8bbe\u3001\u8ba9\u5ba2\u4eba\u6709\u7740\u6700\u597d\u7684\u4eab\u53d7\u3002 \u3000\u3000\u4f60\u5982\u4ed4\u7ec6\u770b\u4e00\u4e0b\u90a3\u56db\u4e2a\u4f8d\u9152\u7684\u59d1\u5a18\u3002\u4e2a\u4e2a\u8c8c\u7f8e\u5982\u82b1\uff0c\u52a8\u624b\u7075\u5de7\uff0c\u8fde\u4e2a\u5934\u90fd\u76f8\u5dee\u4e0d\u591a\u3002 \u3000\u3000\u5979\u4eec\u4e00\u822c\u7684\u5de7\u7b11\u5029\u516e\uff0c\u5a07\u5a9a\u52a8\u4eba\uff0c\u659f\u9152\u65f6\uff0c\u4e3e\u6b62\u8f7b\u67d4\uff0c\u52a8\u4f5c\u719f\u5de7\u3002 \u3000\u3000\u4f46\u73b0\u5728\uff0c\u8d35\u5bbe\u5385\u5403\u9152\u7684\u56db\u4e2a\u5ba2\u4eba\uff0c\u5c31\u6446\u4e86\u4e00\u526f\u4e25\u8083\u7684\u9762\u5b54\u3002 \u3000\u3000\u4e00\u70b9\u4e0d\u4e3a\u56db\u4e2a\u4f8d\u9152\u7f8e\u5973\u7684\u5a07\u67d4\u6240\u52a8\uff0c\u4e00\u526f\u89c6\u82e5\u65e0\u7779\u7684\u6837\u5b50\u2026\u2026\u3002 \u3000\u3000\u53cd\u800c\u662f\u56db\u4e2a\u5ba2\u4eba\u7684\u51b7\u8083\u795e\u60c5\uff0c\u611f\u67d3\u4e86\u56db\u4f4d\u4f8d\u9152\u7684\u7f8e\u5973\uff0c\u7adf\u7136\u4e5f\u53d8\u7684\u4e25\u8083\u8d77\u6765\u3002 \u3000\u3000\u4f46\u8fd9\u56db\u4e2a\u795e\u60c5\u50f5\u6728\u7684\u5ba2\u4eba\uff0c\u5374\u662f\u65e2\u4f1a\u4eab\u53d7\uff0c\u53c8\u6781\u4f1a\u6311\u5254\u7684\u5ba2\u4eba\uff0c\u56db\u4e2a\u4eba\u559d\u4e86\u56db\u79cd\u4e0d\u540c\u7684\u9152\uff0c\u6709\u7684\u8981\u70ed\u70eb\uff0c\u6709\u7684\u8981\u51b7\u996e\uff0c\u6240\u961f\u6bcf\u4e2a\u4eba\u90fd\u9700\u8981\u4e00\u4e2a\u4e13\u53f8\u4f8d\u9152\u7684\u7f8e\u5973\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u4eec\u7684\u50f5\u6728\u548c\u51b7\u6f20\uff0c\u5374\u53c8\u6df1\u6df1\u523a\u4f24\u4e86\u56db\u4f4d\u59d1\u5a18\u804c\u4e1a\u4e0a\u7684\u5c0a\u4e25\uff0c\u56e0\u4e3a\uff0c\u5979\u4eec\u65bd\u51fa\u4e86\u5168\u8eab\u7684\u672c\u9886\uff0c\u7adf\u65e0\u6cd5\u535a\u53d6\u5230\u5ba2\u4eba\u4e00\u62b9\u7b11\u610f\uff0c\u6781\u5c3d\u5a07\u67d4\uff0c\u4e5f\u65e0\u6cd5\u83b7\u5f97\u4e00\u4e0b\u9752\u7750\uff0c\u8fd9\u5c31\u4f7f\u56db\u4e2a\u59d1\u5a18\u611f\u89c9\u5230\u7d22\u7136\u65e0\u6bd4\u61d2\u6d0b\u6d0b\u7684\u7ad9\u5728\u4e00\u4fa7\u2026\u2026\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u5750\u5728\u5357\u9996\u7684\u5ba2\u4eba\uff0c\u7a81\u7136\u5f00\u4e86\u91d1\u53e3\uff0c\u9053\uff1a\u201c\u56db\u4f4d\u59d1\u5a18\u4eca\u5e74\u8d35\u5e9a\u554a\uff1f\u201d \u3000\u3000\u8bf4\u8bdd\u7684\u58f0\u97f3\uff0c\u867d\u7136\u51b7\u82e5\u51b0\u971c\uff0c\u4f46\u603b\u7b97\u5f00\u4e86\u53e3\u3002 \u3000\u3000\u56db\u4e2a\u4f8d\u9152\u7684\u5c11\u5973\uff0c\u7adf\u7136\u4e0d\u7ea6\u800c\u540c\u9f50\u58f0\u56de\u7b54\uff0c\u9053\uff1a\u201c\u5341\u516b\u5c81\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u597d\uff01\u56db\u4f4d\u662f\u540c\u5e74\u4e86\uff0c\u90fd\u662f\u5341\u516b\u5c81\uff1f\u5341\u516b\u7684\u59d1\u5a18\u4e00\u6735\u82b1\u3002 \u3000\u3000\u9752\u6625\u5e74\u534e\uff0c\u8273\u8272\u7167\u4eba\uff0c\u8001\u592b\u4e3a\u56db\u4f4d\u59d1\u5a18\u6d6e\u4e00\u5927\u767d\u3002\u201d \u3000\u3000\u7aef\u8d77\u9762\u524d\u7684\u9152\u676f\u3002\u4e00\u996e\u800c\u5c3d\u3002 \u3000\u3000\u56db\u5973\u76ee\u5149\u4ea4\u8f6c\uff0c\u8138\u4e0a\u6cdb\u8d77\u4e86\u4e00\u62b9\u5fae\u7b11\uff0c\u5fc3\u4e2d\u5374\u662f\u6697\u6697\u5fd6\u9053\uff1a\u201c\u6211\u8fd8\u8ba4\u4e3a\u4f60\u4eec\u771f\u662f\u94c1\u6253\u3001\u94dc\u6d47\u7684\u4eba\uff0c\u539f\u6765\uff0c\u4e5f\u4e00\u6837\u80fd\u611f\u53d7\u5230\u8273\u8272\u7167\u4eba\u90a3\u5c31\u597d\uff01\u53ea\u8981\u4f60\u4eec\u5f00\u542f\u4e86\u4e00\u6247\u5fc3\u6249\uff0c\u5c31\u8981\u9645\u4eec\u8dcc\u5165\u6e29\u67d4\u9677\u4e95\uff0c\u5c1d\u8bd5\u4e00\u4e0b\u6625\u82b1\u56db\u5a62\u7684\u5389\u5bb3\u2026\u2026\u201d \u3000\u3000\u56db\u5973\u4e0d\u7981\u7cbe\u795e\u4e00\u632f\uff0c\u5ae3\u7136\u4e00\u7b11\uff0c\u540c\u65f6\u633d\u8d77\u9152\u58f6\uff0c\u66ff\u56db\u4eba\u659f\u6ee1\u9152\u676f\u3002 \u3000\u3000\u4f46\u95fb\u5750\u5728\u5317\u9996\u7684\u5ba2\u4eba\u8bf4\u9053\uff1a\u201c\u56db\u4f4d\u59d1\u5a18\u5a07\u5a9a\u53ef\u4eba\uff0c\u5374\u53c8\u8eab\u624b\u4e0d\u51e1\uff0c\u4e0d\u77e5\u662f\u90a3\u4f4d\u540d\u5e08\uff0c\u8c03\u6559\u51fa\u5982\u6b64\u51fa\u8272\u7684\u9ad8\u5f92\uff1f\u201d \u3000\u3000\u8bed\u6c14\u5e73\u548c\uff0c\u4f3c\u662f\u90a3\u9897\u575a\u51b7\u4e4b\u5fc3\uff0c\u5df2\u4e3a\u6e29\u67d4\u8f6f\u5316\u3002 \u3000\u3000\u4f46\u89c1\u79cb\u6ce2\u6d41\u8f6c\uff0c\u5206\u7ad9\u5728\u5357\u3001\u5317\u3001\u897f\u65b9\u4f4d\u4e0a\uff0c\u4e09\u4f4d\u59d1\u5a18\u7684\u76ee\u5149\uff0c\u9f50\u9f50\u6295\u6ce8\u5230\u4e1c\u9762\u5c11\u5973\u8eab\u4e0a\uff0c\u4f3c\u662f\u7b49\u5f85\u5979\u7684\u51b3\u5b9a\uff1f\u663e\u7136\u7684\uff0c\u7ad9\u5728\u4e1c\u9996\u7684\u5c11\u5973\uff0c\u662f\u56db\u5973\u4e2d\u7684\u9886\u5934\u4eba\u7269\u3002 \u3000\u3000\u201c\u5a62\u5b50\u6843\u82b1\u2026\u2026\u201d\u5728\u4e09\u5973\u76ee\u5149\u903c\u89c6\u4e4b\u4e0b\uff0c\u6843\u82b1\u65e0\u6cd5\u4e0d\u5f00\u53e3\u4e86\uff0c\u8f7b\u542f\u6a31\u5507\uff0c\u5a49\u8f6c\u51fa\u4e00\u7f15\u5a07\u751c\u7684\u58f0\u97f3\uff0c\u9053\uff1a\u201c\u6625\u82b1\u56db\u5a62\uff0c\u53ea\u4e60\u6b4c\u821e\uff0c\u4e0d\u4f1a\u6b66\u529f\uff0c\u5927\u7237\u53ea\u6015\u662f\u770b\u8d70\u773c\u4e86\uff1f\u201d \u3000\u3000\u201c\u8bf4\u7684\u4e5f\u662f\u2026\u2026\u201d\u5750\u5728\u4e1c\u9762\uff0c\u6843\u82b1\u8eab\u65c1\u7684\u5ba2\u4eba\u7b11\u9053\uff1a\u201c\u56db\u4f4d\u6b65\u5c65\u8f7b\u76c8\uff0c\u5bf9\u6b4c\u821e\u4e00\u9053\uff0c\u5fc5\u7136\u4e0b\u8fc7\u4e00\u756a\u5de5\u592b\u3002\u201d \u3000\u3000\u201c\u5927\u7237\u5938\u5956\u2026\u2026\u201d\u6843\u82b1\u9053\uff1a\u201c\u4f8d\u9152\u5949\u5ba2\uff0c\u5356\u7b11\u5385\u5802\uff0c\u8fd9\u4e2a\u5a62\u5b57\uff0c\u5c82\u4e0d\u662f\u66f4\u9002\u5408\u6211\u4eec\u7684\u8eab\u4efd\u3002\u201d \u3000\u3000\u201c\u5356\u7b11\u5385\u5802\uff0c\u8fd9\u662f\u8868\u660e\u4e86\u5356\u7b11\u4e0d\u5356\u8eab\u4e86\uff1f\u201d\u63a5\u53e3\u662f\u5750\u5728\u5357\u9762\u7684\u5ba2\u4eba\uff0c\u8bed\u58f0\u4e2d\u5df2\u6ca1\u6709\u90a3\u80a1\u51b7\u51b7\u7684\u5473\u9053\u3002 \u3000\u3000\u679c\u7136\u662f\u6625\u82b1\u76f4\u4eba\uff0c\u6625\u98ce\u89e3\u51bb\uff0c\u56db\u4e2a\u51b7\u5389\u3001\u50f5\u6728\u7684\u4eba\uff0c\u4f3c\u5df2\u9010\u6e10\u878d\u5316\u5728\u56db\u5973\u7684\u6e29\u67d4\u4e4b\u4e2d\u3002 \u3000\u3000\u201c\u5927\u7237\u4ee5\u4e3a\u5462\uff1f\u2026\u2026\u201d\u6843\u82b1\u8bed\u6c14\u66a7\u6627\u7684\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5f31\u5973\u53ef\u6b3a\u3002\u5982\u662f\u9047\u4e0a\u4e86\u8d22\u5927\u6c14\u7c97\uff0c\u4e0d\u62e9\u624b\u6bb5\u7684\u7237\u95e8\uff0e\u5a62\u5b50\u4eec\u771f\u8fd8\u4e0d\u77e5\u9053\u5982\u4f55\u53bb\u6297\u62d2\u90a3\u6c89\u91cd\u7684\u538b\u529b\u5462\uff1f\u2026\u2026\u201d \u3000\u3000\u8fd9\u756a\u540e\u66f2\u6298\u6709\u81f4\uff0c\u4f46\u6843\u82b1\u7acb\u523b\u53c9\u63a5\u4e86\u4e0b\u53bb\uff0c\u9053\uff1a\u201c\u5e78\u597d\u5a62\u5b50\u4eec\u8fd8\u672a\u9047\u5230\u8fc7\u8fd9\u79cd\u4e8b\u60c5\uff1f\u201d \u3000\u3000\u56db\u4e2a\u5ba2\u4eba\u9f50\u9f50\u70b9\u5934\uff0c\u4f3c\u662f\u5f88\u6b23\u8d4f\u6843\u82b1\u8fd9\u756a\u8fa9\u89e3\uff0c\u9f50\u9f50\u7aef\u8d77\u9152\u676f\uff0c\u9053\uff1a\u201c\u8bf4\u5f97\u597d\uff01\u56db\u4f4d\u8bf7\u5c3d\u6b64\u676f\uff0c\u4ee5\u8868\u793a\u6211\u7b49\u4eba\u656c\u610f\u3002\u201d \u3000\u3000\u56db\u5973\u4e5f\u4e0d\u63a8\u8f9e\uff0c\u63a5\u8fc7\u9152\u676f\uff0c\u4e00\u53e3\u559d\u4e0b\u3002 \u3000\u3000\u4e1c\u9996\u5ba2\u4eba\u7b11\u9053\uff1a\u201c\u6843\u82b1\u59d1\u5a18\uff0c\u6709\u6625\u5fc5\u6709\u590f\uff0c\u7ee7\u4e4b\u79cb\u3001\u51ac\uff0c\u6625\u98ce\u56db\u82b1\u4e3a\u9996\uff0c\u53e6\u5fc5\u6709\u590f\u3001\u79cb\u51ac\u4e09\u5b63\u82b1\u9b41\u4e86\u3002\u201d \u3000\u3000\u6843\u82b1\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u53ea\u4e0d\u8fc7\uff0c\u6625\u82b1\u56db\u5a62\uff0c\u590f\u3001\u79cb\u4e8c\u5973\uff0c\u8bf4\u5230\u51ac\u561b\uff1f\u90a3\u5c31\u53ea\u6709\u4e00\u4f4d\u4e86\u3002\u201d \u3000\u3000\u201c\u8fd9\u662f\u600e\u4e48\u4e00\u4e2a\u8bf4\u6cd5\u2026\u2026\u201d\u897f\u9996\u7684\u5ba2\u4eba\u95ee\u9053\uff1a\u201c\u6843\u82b1\u59d1\u5a18\u80fd\u4e0d\u80fd\u8be6\u4e3a\u8bf4\u660e\u4e00\u4e0b\uff1f\u201d \u3000\u3000\u6843\u82b1\u9053\uff1a\u201c\u5a62\u5b50\u5df2\u7ecf\u8bf4\u7684\u5f88\u660e\u767d\u4e86\uff0c\u6625\u82b1\u6709\u56db\u5a62\uff0c\u590f\u3001\u79cb\u5404\u6709\u4e24\u4f4d\u59d1\u5a18\uff0c\u51ac\u82b1\u9b41\u9996\u3002\u53ea\u6709\u4e00\u4eba\u3002\u6885\u50b2\u971c\u96ea\u3002\u518d\u65e0\u522b\u7684\u82b1\uff0c\u53ef\u5728\u6f88\u9aa8\u5bd2\u51b7\u4e2d\u548c\u5b83\u7adf\u8273\u4e86\u3002\u2026\u2026\u201d \u3000\u3000\u4f46\u95fb\u6728\u95e8\u8f7b\u54cd\uff0c\u7a81\u5730\u88ab\u4eba\u63a8\u5f00\u3002\u4e00\u9635\u5bd2\u98ce\u5439\u4e86\u8fdb\u6765\uff0c\u4e5f\u5e26\u8fdb\u6765\u4e00\u4e2a\u8eab\u7740\u9ed1\u8272\u957f\u888d\u7684\u5e74\u8f7b\u4eba\u3002 \u3000\u3000\u6625\u82b1\u56db\u5a62\u770b\u4e86\u6765\u4eba\u4e00\u773c\uff0c\u8eab\u5b50\u9707\u98a4\u4e86\u4e00\u4e0b\uff0c\u6025\u6025\u62d8\u8d77\u9152\u58f6\uff0c\u4e3a\u56db\u4f4d\u5ba2\u4eba\u659f\u9152\u3002 \u3000\u3000\u9ed1\u8863\u63a9\u4e0a\u6728\u95e8\uff0e\u8f6c\u8eab\u884c\u8fd1\u684c\u524d\uff0c\u8eac\u8eab\u8bf4\u9053\uff1a\u201c\u56db\u4f4d\u5927\u7237\u8fd8\u8981\u52a0\u6dfb\u70b9\u9152\u83dc\u5417\uff1f\u201d \u3000\u3000\u4e1c\u9996\u5ba2\u4eba\u770b\u770b\u56db\u5973\u8138\u4e0a\u7684\u60ca\u6050\u795e\u8272\uff0c\u7ad9\u8d77\u8eab\u5b50\uff0c\u9053\uff1a\u201c\u4e0d\u7528\u4e86\u3002\u9152\u5df2\u516b\u5206\uff0c\u518d\u559d\u5c31\u9189\u4e86\uff0c\u7ed3\u5e10\u5427\uff1f\u201d \u3000\u3000\u6843\u82b1\u6025\u6025\u8bf4\u9053\uff1a\u201c\u56db\u4f4d\u5927\u7237\u521a\u521a\u5f15\u8d77\u8c08\u5174\uff0c\u600e\u4e48\u5c31\u8981\u8d70\u4e86\u5462\uff1f\u201d \u3000\u3000\u4e1c\u9996\u5ba2\u4eba\u63a2\u624b\u5165\u6000\uff0c\u53d6\u51fa\u4e00\u952d\u9ec4\u91d1\u3002\u9053\uff1a\u201c\u8fd9\u5341\u4e24\u9ec4\u91d1\uff0c\u5927\u6982\u591f\u8fd9\u4e00\u9910\u4e4b\u8d39\u4e86\u5427\uff01\u201d \u3000\u3000\u9ed1\u888d\u4eba\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u7528\u4e0d\u4e86\u3002\u8fd8\u8981\u627e\u56de\u4e00\u4e9b\u94f6\u4e24\u3002\u201d \u3000\u3000\u201c\u90a3\u5c31\u9001\u7ed9\u6625\u82b1\u56db\u5973\u4e70\u70b9\u80ed\u8102\u82b1\u7c89\u3002\u201d\u4e3e\u6b65\u5411\u5916\u884c\u53bb\u3002 \u3000\u3000\u53e6\u5916\u4e09\u4f4d\u5ba2\u4eba\u9c7c\u8d2f\u76f8\u968f\uff0c\u79bb\u5f00\u4e86\u8d35\u5bbe\u5385\u3002 \u3000\u3000\u9ed1\u888d\u4eba\u795e\u60c5\u51b7\u5cfb\u7684\u770b\u7740\u56db\u4eba\u8fdc\u53bb\uff0c\u624d\u63a9\u4e0a\u5385\u95e8\uff0c\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4ed6\u4eec\u662f\u4ec0\u4e48\u8eab\u4efd\u5462\uff1f\u201d \u3000\u3000\u201c\u8fdc\u6ca1\u6709\u95ee\u51fa\u6765\uff0c\u526f\u603b\u7ba1\u5c31\u5230\u4e86\u2026\u2026\u201d \u3000\u3000\u9ed1\u888d\u4eba\u6253\u65ad\u4e86\u6843\u82b1\u672a\u5b8c\u4e4b\u8a00\u3002\u63a5\u9053\uff1a\u201c\u6211\u5982\u518d\u4e0d\u6765\uff0c\u53ea\u6015\u4f60\u4eec\u80f8\u4e2d\u6240\u77e5\uff0c\u5168\u8981\u88ab\u4eba\u638f\u8d70\u4e86\u3002\u201d \u3000\u3000\u6843\u82b1\u9053\uff1a\u201c\u56db\u4e2a\u4eba\u5168\u50cf\u987d\u77f3\u96d5\u6210\u7684\u4eba\uff0c\u4e00\u76f4\u51b7\u51b0\u51b0\u7684\u559d\u95f7\u9152\uff0c\u6211\u548c\u4e09\u4f4d\u59b9\u59b9\uff0c\u65bd\u51fa\u4e86\u6df7\u8eab\u89e3\u6570\uff0c\u4ed6\u4eec\u7adf\u7136\u4e0d\u4e3a\u6240\u52a8\uff0c\u78e8\u83c7\u4e86\u4e00\u4e2a\u591a\u65f6\u8fb0\uff0c\u521a\u521a\u8ba9\u4ed6\u4eec\u6253\u5f00\u6d3b\u5323\u5b50\uff0c\u8fdc\u672a\u5f15\u5165\u6b63\u9898\u2026\u2026\u201d \u3000\u3000\u9ed1\u888d\u4eba\u63a5\u9053\uff1a\u201c\u542c\u53e3\u6c14\u3002\u4f60\u4f3c\u662f\u5fc3\u4e2d\u6709\u4e9b\u4e0d\u670d\u6c14\uff0c\u662f\u5417\uff1f\u201d \u3000\u3000\u201c\u5982\u82e5\u518d\u7ed9\u6211\u4eec\u4e00\u4e2a\u65f6\u8fb0\uff0c\u5a62\u5b50\u76f8\u4fe1\u3002\u80fd\u5957\u95ee\u51fa\u4ed6\u4eec\u7684\u8eab\u4efd\u6765\u5386\uff0f\u673a\u82b1\u4f4e\u58f0\u56de\u7b54\u3002 \u3000\u3000\u201c\u6843\u82b1\uff01\u4e0d\u7528\u5f3a\u5634\u3002\u4e5f\u4e0d\u8981\u4e0d\u670d\u6c14\u3001\u4f60\u4eec\u90fd\u4e60\u7ec3\u8fc7\u6444\u9b42\u5927\u6cd5\u3001\u82b1\u4e86\u4e00\u4e2a\u65f6\u8fb0\u3002\u8fd8\u65e0\u6cd5\u4f7f\u4eba\u5bb6\u62dc\u5012\u5728\u77f3\u69b4\u88d9\u4e0b\uff0c\u7545\u8ff0\u51fa\u8eab\u4efd\u6765\u5386\uff0c\u4f60\u53ef\u77e5\u9053\uff0c\u4ed6\u4eec\u4e3a\u4ec0\u4e48\u4e0d\u8a00\u4e0d\u8bed\u7684\u5750\u7740\u559d\u95f7\u9152\u5417\uff1f\u201d \u3000\u3000\u201c\u96be\u9053\uff0c\u96be\u9053\u2026\u2026\u201d\u6843\u82b1\u9053\uff1a\u201c\u4ed6\u4eec\u5728\u8fd0\u529f\u6297\u62d2\u6211\u4eec\u7684\u6444\u9b42\u5927\u6cd5\uff1f\u201d \u3000\u3000\u201c\u5bf9\uff01\u4ed6\u4eec\u5728\u8fd0\u529f\u6297\u62d2\uff0c\u800c\u4e14\u5df2\u7ecf\u6218\u80dc\u4e86\u4f60\u4eec\uff0c\u4ed6\u4eec\u770b\u5230\u4e86\u4f60\u4eec\u7b4b\u75b2\u529b\u5c3d\u3002\u5174\u81f4\u7d22\u7136\u7684\u795e\u60c5\u2026\u2026\u201d \u3000\u3000\u9ed1\u888d\u4eba\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u624d\u5c55\u5f00\u53cd\u51fb\uff0c\u5957\u95f4\u4f60\u4eec\u80f8\u4e2d\u4e4b\u79d8\uff0c\u5982\u82e5\u6211\u518d\u4e0d\u73b0\u8eab\uff0c\u53ea\u6015\u4f60\u4eec\u8981\u5c3d\u5410\u6240\u77e5\u4e86\u3002\u201d \u3000\u3000\u6625\u82b1\u56db\u5a62\u9f50\u9f50\u4e00\u5446\u3002\u9053\uff1a\u201c\u8fd9\u2026\u2026\u201d \u3000\u3000\u201c\u4f60\u4eec\u8fd8\u662f\u4e0d\u76f8\u4fe1\uff1f\u662f\u5417\uff0c\u597d\uff01\u6211\u518d\u95ee\u4f60\u4eec\u4e00\u4ef6\u4e8b\u3002\u90a3\u56db\u4e2a\u4eba\u957f\u76f8\u5982\u4f55\uff1f\u5982\u679c\u4ed6\u4eec\u6362\u4e86\u8863\u670d\u3002\u518d\u89c1\u9762\u65f6\uff0c\u4f60\u4eec\u662f\u5426\u80fd\u8ba4\u5f97\u51fa\u6765\uff1f\u201d \u3000\u3000\u6625\u82b1\u56db\u5a62\u7ec6\u60f3\u56db\u4f4d\u5ba2\u4eba\u5f62\u8c8c\uff0c\u4e2a\u4e2a\u8138\u8272\u51b7\u786c\uff0c\u795e\u60c5\u50f5\u6728\uff0c\u7adf\u7136\u6ca1\u6709\u4e00\u70b9\u8868\u60c5\uff0c\u6ca1\u6709\u4e00\u70b9\u53ef\u5fc3\u8bb0\u5fc6\u7684\u5730\u65b9\uff0c\u4e0d\u7981\u4e3a\u4e4b\u4e00\u5446\u3002 \u3000\u3000\u9ed1\u888d\u4eba\u63a5\u9053\uff1a\u201c\u4ed6\u4eec\u5728\u8138\u4e0a\u6d82\u4e86\u4e00\u5c42\u5f88\u539a\u7684\u836f\u7269\uff0c\u4f60\u4eec\u770b\u5230\u7684\uff0c\u4e0d\u662f\u4ed6\u4eec\u672c\u6765\u7684\u9762\u76ee\uff0e\u81ea\u7136\u65e0\u6cd5\u8bb0\u4e0b\u4ed6\u4eec\u8138\u5f62\u3001\u795e\u60c5\u3002\u201d \u3000\u3000\u6843\u82b1\u9053\uff1a\u201c\u4ed6\u4eec\u7a76\u7adf\u662f\u4ec0\u4e48\u4eba\u5462\uff1f\u201d \u3000\u3000\u201c\u4e0d\u77e5\u9053\u2026\u2026\u201d\u9ed1\u888d\u4eba\u9053\uff1a\u201c\u672c\u6765\u5e0c\u671b\u4f60\u4eec\u7528\u6444\u9b42\u5927\u6cd5\uff0c\u5957\u95ee\u51fa\u4ed6\u4eec\u7684\u8eab\u4efd\u2026\u2026\u201d \u3000\u3000\u6843\u82b1\u7a81\u7136\u8dea\u4e86\u4e0b\u53bb\uff0c\u9053\uff1a\u201c\u526f\u603b\u7ba1\u660e\u5bdf\u3002\u5a62\u5b50\u4eec\u5df2\u5c3d\u5168\u529b\u3002\u201d \u3000\u3000\u53e6\u5916\u4e09\u5973\uff0c\u7d27\u968f\u6843\u82b1\uff0c\u4e5f\u8dea\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u201c\u8d77\u6765\uff0c\u8d77\u6765\u2026\u2026\u201d\u9ed1\u888d\u4eba\u9053\uff1a\u201c\u6211\u5982\u6ca1\u6709\u5e2e\u4f60\u4eec\u4e00\u628a\u7684\u610f\u601d\uff0c\u4e5f\u4e0d\u4f1a\u5927\u715e\u98ce\u666f\u7684\u95ef\u8fdb\u6765\u4e86\u3002\u201d\u4f38\u624b\u6276\u8d77\u6843\u82b1\uff0c\u548c\u53e6\u5916\u4e09\u4e2a\u5973\u5a62\u3002 \u3000\u3000\u201c\u526f\u603b\u7ba1\u6069\u60c5\u5982\u5c71\uff0c\u6625\u82b1\u56db\u5a62\u611f\u6234\u83ab\u540d\uff0c\u65e5\u540e\u5982\u6709\u673a\u7f18\uff0c\u5b9a\u5f53\u62a5\u7b54\u3002\u201d\u6843\u82b1\u65e0\u9650\u60ca\u6050\u4ed6\u8bf4\u3002 \u3000\u3000\u6843\u82b1\u6d41\u4e0b\u6cea\u5939\uff0c\u9053\uff1a\u201c\u4eca\u5929\u7684\u4e8b\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u7528\u518d\u63d0\u4e09\u6211\u4f1a\u5411\u603b\u7ba1\u8bf4\u660e\uff0c\u5c31\u8bf4\u4ed6\u4eec\u53d1\u89c9\u6297\u62d2\u4e0d\u4f4f\u6444\u9b42\u5927\u6cd5\u65f6\u85c9\u6545\u79bb\u53bb\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\uff0c\u603b\u7ba1\u4f1a\u76f8\u4fe1\u5417\uff1f\u2026\u2026\u201d\u6843\u82b1\u4e0d\u653e\u5fc3\u5730\u95ee\u3002 \u3000\u3000\u201c\u6211\u4f1a\u66ff\u4f60\u4eec\u62c5\u5f85\uff0c\u56de\u53bb\u4f11\u606f\u5427\uff01\u201d \u3000\u3000\u56db\u5973\u53c8\u6df1\u6df1\u4e00\u63d6\uff0c\u624d\u8f6c\u8eab\u79bb\u53bb\u3002 \u3000\u3000\u8352\u51c9\u7684\u5927\u6bbf\u4e2d\uff0c\u7a81\u7136\u4eae\u8d77\u4e86\u4e00\u9053\u706b\u5149\uff0c\u70b9\u71c3\u8d77\u4e00\u622a\u814a\u70db\u3002\u5927\u6bbf\u4e2d\u7684\u666f\u7269\uff0c\u6e05\u6670\u53ef\u89c1\u3002 \u3000\u3000\u56db\u4e2a\u4eba\u56f4\u70db\u800c\u5750\u3002\u6b63\u662f\u90a3\u56db\u4e2a\u559d\u9152\u7684\u5ba2\u4eba\u3002 \u3000\u3000\u5317\u9996\u4e00\u4eba\u8131\u4e0b\u4e1d\u68c9\u957f\u888d\uff0c\u53d6\u4e0b\u5047\u53d1\u5934\u5957\uff0c\u9732\u51fa\u4e00\u5934\u7070\u767d\u6298\u84ec\u53d1\uff0c\u548c\u4e00\u8eab\u767e\u8896\u5927\u8902\uff0c\u9053\uff1a \u3000\u3000\u201c\u53eb\u5316\u5b50\u5145\u5458\u5916\uff0c\u53ef\u771f\u662f\u6253\u9e2d\u5b50\u4e0a\u67b6\u3002 \u3000\u3000\u8bf4\u4e0d\u51fa\u8fd9\u4e2a\u96be\u8fc7\uff0c\u5582\uff01\u9152\u8089\u548c\u5c1a\uff0c\u4f60\u90a3\u9897\u98ce\u5439\u65e5\u4e3d\u60ef\u4e86\u7684\u5149\u8111\u888b\uff0c\u6234\u4e0a\u5047\u53d1\uff0c\u80fd\u4e60\u60ef\u5417\uff1f\u201d \u3000\u3000\u201c\u4e0d\u884c\uff0c\u6211\u548c\u5c1a\u65e9\u5c31\u60f3\u62ff\u4e0b\u6765\u4e86\u2026\u2026\u201d\u53d6\u4e0b\u5047\u53d1\uff0c\u9732\u51fa\u4e86\u2014\u2014\u4e2a\u5149\u79c3\u79c3\u7684\u8111\u888b\uff0c\u53cc\u624b\u4e00\u63c9\uff0c\u6413\u4e0b\u4e86\u8138\u4e0a\u6d82\u7684\u836f\u7269\uff0c\u9732\u51fa\u672c\u6765\u9762\u76ee\u3002\u679c\u7136\u662f\u4e2a\u548c\u5c1a\u3002 \u3000\u3000\u53e6\u5916\u4e24\u4eba\u4e5f\u9664\u53bb\u8138\u4e0a\u836f\u7269\uff0c\u7adf\u662f\u5357\u5200\u6bb5\u4e5d\u3001\u5317\u5251\u80e1\u840d\u3002 \u3000\u3000\u201c\u597d\u5389\u5bb3\u6444\u9b42\u5927\u6cd5\u2026\u2026\u201d\u5357\u5200\u6bb5\u4e5d\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u5982\u975e\u65e9\u5f97\u4e03\u5b9d\u5927\u5e08\u6307\u70b9\uff0c\u51dd\u795e\u8fd0\u529f\u6297\u62d2\uff0c\u4eca\u665a\u4e0a\u4e00\u5b9a\u4f1a\u683d\u5728\u90a3\u56db\u4e2a\u4e2b\u5934\u7684\u624b\u4e2d\u3002\u201d \u3000\u3000\u201c\u8981\u8c22\u4e5f\u8be5\u8c22\u8c22\u8001\u53eb\u5316\u5b50\uff0c\u6211\u548c\u5c1a\u4e0d\u6562\u63a0\u7f8e\uff0c\u8fd9\u6d88\u606f\u662f\u8001\u53eb\u5316\u6316\u51fa\u6765\u7684\uff0c\u6211\u548c\u5c1a\u53ea\u662f\u53d7\u4ed6\u4e4b\u8bb8\uff0c\u8f6c\u544a\u4e24\u4f4d\u2026\u2026\u201d \u3000\u3000\u80e1\u840d\u63a5\u9053\uff1a\u201c\u867d\u662f\u65e9\u5df2\u6709\u4e86\u6212\u5907\uff0c\u4f46\u80e1\u67d0\u4eba\u8fd8\u662f\u5dee\u4e00\u70b9\u628a\u6301\u4e0d\u4f4f\uff0c\u8bf4\u6765\u60ed\u6127\u5f97\u5f88\uff0c\u5982\u975e\u795e\u4e10\u94b1\u8001\u65e9\u63e1\u667a\u73e0\uff0c\u6df1\u660e\u5185\u60c5\uff0c\u4eca\u665a\u4e0a\u5c31\u88ab\u4eba\u5bb6\u6446\u5e73\u5728\u6d1b\u9633\u5c45\u4e86\u3002\u201d \u3000\u3000\u201c\u8001\u53eb\u5316\u5341\u5e74\u524d\u5c31\u89c9\u7740\u6c5f\u6e56\u4e0a\u60c5\u5f62\u4e0d\u5bf9\uff0c\u4f46\u5374\u53c8\u77a7\u4e0d\u51fa\u54ea\u91cc\u4e0d\u5bf9\uff0c\u4f46\u8001\u53eb\u5316\u5374\u611f\u89c9\u5f97\u5230\u4e00\u80a1\u6c79\u6d8c\u7684\u6697\u6d41\u5728\u6c5f\u6e56\u7ffb\u817e\uff0c\u6240\u4ee5\u3002 \u3000\u3000\u6211\u6f5c\u8e2a\u9690\u5f62\uff0c\u6d88\u5931\u6c5f\u6e56\uff0c\u5e0c\u671b\u5728\u6697\u4e2d\u80fd\u67e5\u4e00\u70b9\u7709\u76ee\u3002\u53ef\u60b2\u7684\u662f\u8001\u53eb\u5316\u8d70\u904d\u4e86\u5927\u6c5f\u5357\u5317\uff0c\u82b1\u8d39\u4e86\u4e03\u516b\u5e74\u7684\u5de5\u592b\uff0c\u5374\u627e\u4e0d\u51fa\u4e00\u70b9\u53ef\u8d44\u8ffd\u67e5\u7684\u7ebf\u7d22\u2026\u2026\u201d \u3000\u3000\u76ee\u5149\u4e00\u63a0\u4e03\u5b9d\u548c\u5c1a\uff0c\u63a5\u9053\uff1a\u201c\u76f4\u5230\u6709\u4e00\u5929\uff0c\u9047\u5230\u4e86\u9152\u8089\u548c\u5c1a\uff0c\u4e00\u4e2a\u4eba\u8eb2\u4e5d\u534e\u5c71\u4e2d\u2026\u2026\u201d \u3000\u3000\u4e03\u5ba4\u548c\u5c1a\u63a5\u9053\uff1a\u201c\u6211\u548c\u5c1a\u5728\u5c71\u4e2d\u6bcf\u65e5\u5403\u8089\u996e\u9152\uff0c\u597d\u4e0d\u5feb\u4e50\uff0c\u5374\u88ab\u4f60\u8001\u53eb\u5316\u53c8\u62d6\u5165\u7ea2\u5c18\u4e4b\u4e2d\uff0c\u91cd\u53d7\u4eba\u95f4\u75be\u82e6\uff0c\u5509\uff01\u6211\u5c1a\u4e0d\u77e5\u90a3\u4e00\u6b21\u5ff5\u9519\u4e86\u7ecf\u6587\uff0c\u624d\u4ea4\u4e86\u4f60\u8fd9\u4e2a\u65e2\u7a77\u53c8\u8001\u7684\u53eb\u5316\u5b50\uff0c\u771f\u662f\u4ea4\u53cb\u4e0d\u614e\u554a\uff01\u201d \u3000\u3000\u201c\u8001\u53eb\u5316\u867d\u7136\u540d\u53eb\u94b1\u7f3a\uff0c\u53ef\u662f\u94b1\u7f3a\u5fb7\u4e0d\u7f3a\u2026\u2026\u201d \u3000\u3000\u6bb5\u4e5d\u63a5\u9053\uff1a\u201c\u4e24\u4f4d\u4e0d\u7528\u4e3a\u94f6\u5b50\u5fe7\u8651\uff0c\u6bb5\u67d0\u4eba\u5fc3\u4e2d\u65e9\u6709\u51c6\u5907\uff0c\u79bb\u5bb6\u65f6\u5e26\u4e86\u4e00\u4e9b\u94f6\u7968\uff0c\u8db3\u591f\u54b1\u4eec\u56db\u4e2a\u4eba\u6570\u5e74\u7528\u5ea6\u4e86\u3002\u201d \u3000\u3000\u201c\u80e1\u67d0\u4eba\u4e5f\u5e26\u4e86\u6570\u4e07\u4e24\u94f6\u7968\uff0c\u53ef\u4ee5\u8865\u6bb5\u5144\u4e4b\u4e0d\u8db3\u2026\u2026\u201d \u3000\u3000\u94b1\u7f3a\u9053\uff1a\u201c\u597d\uff01\u6709\u94b1\u597d\u529e\u4e8b\uff0c\u2026\u2026\u201d \u3000\u3000\u76ee\u5149\u4e00\u60ca\u4e03\u5b9d\u548c\u5c1a\u3001\u6bb5\u4e5d\u3001\u80e1\u840d\uff0c\u63a5\u9053\uff1a\u201c\u8001\u53eb\u5316\u628a\u9152\u8089\u548c\u5c1a\u62d6\u4e0b\u4e86\u4e5d\u534e\u5c71\uff0c\u67e5\u8bbf\u8fd9\u4e00\u80a1\u5149\u6d8c\u6697\u6d41\u7684\u6e90\u5934\uff0c\u662f\u8001\u53eb\u5316\u77e5\u9053\u548c\u5c1a\u7684\u4e3a\u4eba\uff0c\u9152\u8089\u7a7f\u80a0\u8fc7\uff0c\u4f5b\u5728\u5fc3\u5934\u5750\uff0c\u8868\u9762\u662f\u5927\u5e99\u4e0d\u6536\uff0c\u5c0f\u5e99\u4e0d\u7559\u7684\u91ce\u548c\u5c1a\uff0c\u4f46\u4ed6\u80f8\u6000\u7684\u6551\u4e16\u5fc3\u613f\uff0c\u5374\u662f\u5e38\u4eba\u96be\u53ca\u4e07\u4e00\uff0c\u5426\u5219\uff0c\u5929\u4e0b\u5927\u5c71\u4e07\u5343\uff0c\u4ed6\u4e3a\u4ec0\u4e48\u8981\u8eb2\u5728\u4e5d\u534e\u5c71\u4e2d\uff0c\u4ed6\u5c31\u662f\u5e0c\u671b\u80fd\u9047\u5230\u4e00\u4e9b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u8001\u53eb\u5316\u627e\u4e0a\u4ed6\uff0c\u5374\u6ca1\u6709\u4e00\u5957\u660e\u663e\u7684\u4f5c\u6cd5\uff0c\u56e0\u4e3a\uff0c\u8001\u53eb\u5316\u4e00\u76f4\u627e\u4e0d\u51fa\u6c5f\u6e56\u75c5\u6e90\u4f55\u5728\uff0c\u6211\u4eec\u4e24\u4e2a\u4eba\u5fc3\u7167\u4e0d\u5ba3\u5730\u5728\u6697\u4e2d\u6478\u7d22\uff0c\u4f46\u73b0\u5728\u60c5\u52bf\u4e0d\u540c\u4e86\uff0c\u6709\u4e86\u4f60\u4eec\u4e24\u4e2a\u4e00\u95e8\u9996\u8111\u52a0\u76df\uff0c\u8fd9\u5c31\u5f97\u628a\u4e8b\u60c5\u8bf4\u4e2a\u6e05\u695a\u624d\u884c\u4e86\u3002\u201d \u3000\u3000\u80e1\u840d\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u5317\u5251\u95e8\u5728\u6211\u624b\u4e2d\u5931\u53bb\uff0c\u593a\u53bb\u6211\u638c\u95e8\u4e4b\u6743\u7684\uff0c\u662f\u6211\u540c\u5e8a\u5171\u676d\u4e8c\u5341\u5e74\u7684\u59bb\u5b50\uff0c\u6211\u5b9e\u5728\u60f3\u4e0d\u51fa\u5317\u5251\u95e8\u4e2d\u8fd8\u6709\u51e0\u4e2a\u4eba\u80fd\u542c\u6211\u7684\u547d\u4ee4\uff0c\u6240\u4ee5\uff0c\u94b1\u8001\u8ba1\u7b97\u4eba\u624b\u65f6\uff0c\u53ea\u80fd\u7b97\u6211\u80e1\u67d0\u4e00\u4e2a\u4eba\u3002\u201d \u3000\u3000\u201c\u6bb5\u67d0\u4eba\u4e5f\u548c\u80e1\u5144\u4e00\u6837\uff0c\u6211\u4e2a\u4eba\u8d74\u6c64\u8e48\u706b\uff0c\u751f\u6b7b\u4e0d\u8ba1\uff0c\u4f46\u5357\u5200\u95e8\u4e2d\uff0c\u6050\u6015\u5df2\u7ecf\u6ca1\u6709\u80af\u542c\u6211\u547d\u4ee4\u7684\u4eba\u4e86\u3002\u201d \u3000\u3000\u201c\u5509\uff01\u53ef\u6015\u5904\uff0c\u4e5f\u5c31\u5728\u6b64\u4e86\uff0c\u5343\u767e\u5e74\u6765\uff0c\u6c5f\u6e56\u4e0a\u7eb7\u4e89\u4e0d\u606f\u3002 \u3000\u3000\u4f46\u5374\u4ece\u6ca1\u6709\u4e00\u6b21\u50cf\u8fd9\u6837\u7684\u8be1\u79d8\u96be\u6d4b\u3002\u2026\u2026\u201d \u3000\u3000\u94b1\u7f3a\u53f9\u606f\u4e00\u58f0\uff0c\u63a5\u9053\uff1a\u201c\u5c11\u6797\u3001\u6b66\u5f53\u4e5f\u5ba3\u5e03\u95ed\u5173\u81ea\u5b88\uff0c\u4e0d\u518d\u7406\u6c5f\u6e56\u4e2d\u4e8b\uff0c\u653e\u773c\u5f53\u4eca\u6b66\u6797\u4e4b\u4e16\uff0c\u8fd8\u6709\u4ec0\u4e48\u4eba\u80fd\u51fa\u9762\u4e3b\u4e8b\u2026\u2026\u201d \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u7a81\u7136\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8001\u53eb\u5316\u3002\u522b\u60f3\u7684\u90a3\u4e48\u574f\uff0c\u548c\u5c1a\u7684\u770b\u6cd5\u662f\uff0c\u76ee\u4e0b\u6b63\u662f\u63ed\u7a7f\u8fd9\u4ef6\u5e2d\u5377\u6c5f\u6e56\u5927\u9634\u8c0b\u7684\u4e00\u4e2a\u5951\u673a\uff0c\u8fc7\u53bb\u662f\u4e00\u80a1\u6697\u6d41\uff0c\u73b0\u5728\uff0c\u5df2\u7ecf\u53ef\u4ee5\u89c1\u5230\u9762\u4e0a\u7684\u6eaa\u6c34\u4e86\uff0c\u80e1\u3001\u6bb5\u4e24\u4f4d\u638c\u95e8\u4eba\uff0c\u5c31\u662f\u4e00\u4e2a\u597d\u4f8b\u5b50\uff0c\u6211\u76f8\u4fe1\uff0c\u89c9\u9192\u7684\u6b66\u6797\u540c\u9053\u3002\u5df2\u662f\u5927\u6709\u4eba\u5728\uff0c\u543e\u9053\u4e0d\u5b64\uff0c\u540e\u7ee7\u6709\u4eba\uff0c\u4f55\u51b5\uff0c\u6211\u4eec\u5df2\u7ecf\u53d1\u73b0\u4e00\u6761\u91cd\u8981\u7ebf\u7d22\uff0c\u6211\u548c\u5c1a\u8ba4\u4e3a\u773c\u4e0b\u6700\u91cd\u8981\u7684\u4e8b\u60c5\uff0c\u662f\u54b1\u4eec\u56db\u4e2a\u4eba\uff0c\u8981\u7814\u8ba8\u4e00\u4e0b\u8fdb\u884c\u7684\u8ba1\u5212\u548c\u51c6\u5907\u5de5\u4f5c\u3002\u201d \u3000\u3000\u201c\u5bf9\uff01\u8fd9\u4e00\u70b9\uff0c\u8001\u53eb\u5316\u65e9\u5df2\u60f3\u597d\u4e86\u4e00\u4e2a\u8179\u6848\uff0c\u8bf7\u4e09\u4f4d\u6307\u6559\u4e00\u4e0b\u2026\u2026\u201d \u3000\u3000\u201c\u4ec0\u4e48\u8fb0\u5149\u4e86\uff0c\u8fd8\u5356\u5173\u5b50\uff0c\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u4e0d\u8010\u7684\u8bf4\u9053\uff1a\u201c\u5feb\u628a\u4f60\u809a\u5b50\u91cc\u6240\u6709\u7684\u6742\u788e\u5168\u638f\u51fa\u6765\uff0c\u770b\u770b\u90a3\u4e9b\u6709\u7528\uff1f\u201d \u3000\u3000\u94b1\u7f3a\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u54b1\u4eec\u56db\u4e2a\u4eba\u662f\u661f\u661f\u4e4b\u4eba\uff0c\u80fd\u4e0d\u80fd\u4e00\u70e7\u71ce\u539f\uff0c\u4eca\u665a\u4e0a\u90a3\u5e2d\u9152\uff0c\u559d\u7684\u6211\u80c6\u98a4\u5fc3\u60ca\uff0c\u5bf9\u624b\u4e4b\u5f3a\uff0c\u51fa\u4eba\u610f\u6599\uff0c\u90a3\u4f4d\u9ed1\u8863\u4eba\u7684\u5e74\u7eaa\u4e0d\u5927\uff0c\u4f46\u4e00\u8eab\u4fee\u4e3a\u4e4b\u7cbe\uff0c\u8001\u53eb\u5316\u5c31\u7518\u62dc\u4e0b\u98ce\uff0c\u8bf4\u4e0d\u5b9a\u4e00\u671d\u706b\u62da\uff0c\u5c31\u5f04\u7684\u8001\u547d\u4e0d\u4fdd\uff0c\u4e03\u5206\u8fd0\u6c14\u9760\u5929\u547d\uff0c\u4e09\u5206\u672c\u9886\u5728\u4eba\u4e3a\uff0c\u8001\u53eb\u5316\u7684\u610f\u601d\uff0c\u7531\u6b64\u523b\u8d77\uff0c\u54b1\u4eec\u56db\u4e2a\u4eba\u5404\u51fa\u538b\u7bb1\u5e95\u7684\u672c\u9886\uff0c\u4e92\u76f8\u4f20\u6388\u7814\u7a76\u3002 \u3000\u3000\u5357\u5200\u6709\u5317\u5251\u4e4b\u957f\uff0c\u5317\u5251\u6709\u5357\u5200\u4e4b\u5a01\uff0c\u8001\u53eb\u4f26\u6709\u9152\u8089\u548c\u5c1a\u7684\u4e03\u5b9d\u3002\u548c\u5c1a\u4f1a\u8001\u53eb\u5316\u7684\u628a\u5f0f\uff0c\u96c6\u56db\u5bb6\u7edd\u6280\u4e8e\u4e00\u8eab\uff0c\u5927\u5bb6\u4e0d\u5f97\u85cf\u79c1\u2026\u2026\u201d \u3000\u3000\u201c\u597d\u529e\u6cd5\uff01\u80e1\u67d0\u5317\u5251\u95e8\u4e2d\u53ea\u6709\u71d5\u4e91\u4e09\u5251\u7b97\u5f97\u7edd\u6280\u3002\u201d \u3000\u3000\u201c\u5357\u5200\u95e8\u7684\u53cd\u624b\u4e00\u5200\u3002\u4e3a\u8d25\u4e2d\u6c42\u80dc\u4e4b\u5b66\u3002\u4f1a\u6b64\u4e00\u5200\u8005\u3002\u53ea\u6709\u6bb5\u67d0\u4e00\u4eba\u3002\u201d \u3000\u3000\u201c\u6211\u548c\u5c1a\u7684\u4e03\u5b9d\u62f3\uff0c\u6c5f\u6e56\u4e0a\u8fd8\u6ca1\u6709\u7b2c\u4e8c\u4e2a\u4eba\u5b66\u8fc7\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u8001\u53eb\u5316\u7684\u2018\u68d2\u6253\u54ee\u5929\u72ac\u2019\uff0c\u4e5f\u7b97\u662f\u4e0d\u4f20\u4e4b\u79d8\u3002\u201d \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u7b11\u9053\uff1a\u201c\u5999\u554a\uff01\u5999\u554a\uff01\u8001\u53eb\u5316\u4f1a\u7528\u5251\uff0c\u9152\u8089\u548c\u5c1a\u4f1a\u7528\u5200\uff0c\u8fd9\u624d\u662f\u5e99\u540e\u9762\u6709\u4e2a\u6d1e\uff0c\u5e99\uff08\u5999\uff09\u900f\u4e86\u3002\u201d \u3000\u3000\u94b1\u7f3a\u9053\uff1a\u201c\u8bf4\u5e72\u5c31\u5e72\uff0c\u54b1\u4eec\u7acb\u523b\u5f00\u59cb\u5427\uff01\u201d \u3000\u3000\u6bb5\u4e5d\u9053\uff1a\u201c\u5927\u5e08\uff0c\u6bb5\u67d0\u60f3\u5148\u8bf7\u6559\u4e00\u4ef6\u4e8b\uff1f\u201d \u3000\u3000\u201c\u522b\u53eb\u5927\u5e08\uff0c\u4f60\u8981\u5c0a\u79f0\u6211\uff0c\u53bb\u4e86\u9152\u8089\u4e24\u4e2a\u5b57\uff0c\u53eb\u6211\u548c\u5c1a\u5c31\u662f\u3002\u201d \u3000\u3000\u6bb5\u4e5d\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u8bf4\u53d1\u73b0\u4e86\u4e00\u6761\u91cd\u8981\u7ebf\u7d22\uff0c\u4e0d\u77e5\u53ef\u5426\u518d\u8bf4\u5f97\u660e\u767d\u4e00\u4e9b\u3002\u201d \u3000\u3000\u201c\u6444\u9b42\u5927\u6cd5\u3002\u6e90\u51fa\u4e8e\u5c0f\u897f\u5929\u96f7\u97f3\u5bfa\u2026\u2026\u201d\u516d\u5b9d\u548c\u5c1a\u8bf4\uff1a\u201c\u90a3\u662f\u9b54\u6559\u4e2d\u90aa\u95e8\u6b66\u529f\uff0c\u90a3\u4e9b\u4f8d\u9152\u7684\u4e2b\u5934\u4eec\uff0c\u90fd\u5b66\u4f1a\u4e86\u8fd9\u95e8\u90aa\u529f\uff0c\u770b\u6765\u548c\u9b54\u6559\u4e2d\u4eba\uff0c\u8131\u4e0d\u4e86\u5173\u7cfb\uff01\u201d \u3000\u3000\u80e1\u840d\u9053\uff1a\u201c\u6444\u9b42\u5927\u6cd5\u3002\u6280\u6b62\u4e8e\u6b64\u3002\u5012\u662f\u7b97\u4e0d\u5f97\u4ec0\u4e48\u9b54\u6559\u795e\u529f\u4e86\u3002\u201d \u3000\u3000\u94b1\u7f3a\u9053\uff1a\u201c\u4e0d\u80fd\u4f4e\u4f30\u9b54\u529f\uff0c\u90a3\u51e0\u4e2a\u4e2b\u5934\u7684\u529f\u529b\u5c1a\u6d45\uff0c\u5a01\u529b\u4e0d\u5927\uff0c\u6240\u4ee5\u3002\u5979\u4eec\u8981\u501f\u7528\u90a3\u98de\u626c\u7684\u79c0\u53d1\u626b\u5728\u53d7\u672f\u8005\u9762\u988a\u4e4b\u4e0a\uff0c\u4ee5\u52a9\u6444\u9b42\u672f\u7684\u5a01\u529b\uff0c\u957f\u5b89\u79e6\u516b\u516c\u529f\u529b\u4f55\u7b49\u6df1\u539a\u3002\u5c31\u683d\u5728\u4e86\u90a3\u4e9b\u5c0f\u4e2b\u5934\u7684\u624b\u4e2d\u2026\u2026\u201d \u3000\u3000\u201c\u79e6\u516b\u516c\u4e5f\u683d\u5728\u2018\u6d1b\u9633\u5c45\u2019\u4e86\u2026\u2026\u201d\u80e1\u840d\u5927\u5403\u4e00\u60ca\u4ed6\u8bf4\uff1a\u201c\u90a3\u6211\u4eec\u80fd\u9080\u8fc7\u8fd9\u4e00\u52ab\uff0c\u771f\u662f\u5e78\u8fd0\u6781\u4e86\u3002\u201d \u3000\u3000\u201c\u6240\u4ee5\uff0c\u4ed6\u4eec\u624d\u80fd\u5728\u957f\u5b89\u7eb5\u6a2a\u81ea\u5982\u2026\u2026\u201d\u94b1\u7f3a\u8bf4\uff1a\u201c\u5173\u6d1b\u6c5f\u6e56\u9053\u4e0a\uff0c\u5df2\u65e0\u4eba\u6562\u51fa\u9762\u5e72\u9884\uff0c\u8001\u53eb\u5316\u7531\u79e6\u516b\u516c\u90a3\u91cc\u63a2\u5f97\u6d88\u606f\uff0c\u624d\u8981\u8bf8\u4f4d\u5728\u8138\u4e0a\u6d82\u4e86\u4e00\u5c42\u5f88\u539a\u7684\u836f\u7269\u3002\u201d \u3000\u3000\u80e1\u840d\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\u3002\u201d \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u63a5\u9053\uff1a\u201c\u54b1\u4eec\u5750\u606f\u4e00\u9635\u3002\u5c31\u5f00\u59cb\u4e92\u4f20\u6280\u827a\uff0c\u5c3d\u4e00\u6708\u4e4b\u529f\uff0c\u5b66\u7ec3\u7eaf\u719f\uff0c\u518d\u5230\u2018\u6d1b\u9633\u6210\u2019\u53bb\u63a2\u542c\u6d88\u606f\u3002\u201c\u8fd9\u56db\u4e2a\u5929\u5357\u5730\u5317\uff0c\u4e92\u4e0d\u76f8\u5e72\u7684\u6b66\u6797\u4eba\u7269\uff0c\u56e0\u4e3a\u6709\u7740\u540c\u4e00\u7684\u76ee\u7684\uff0c\u5f7c\u6b64\u809d\u80c6\u76f8\u7167\u3002\u5f00\u59cb\u4e92\u76f8\u4f20\u6388\u7edd\u6280\u3002 \u3000\u3000\u6d1b\u9633\u5c45\u7684\u751f\u610f\u8d8a\u6765\u8d8a\u597d\uff0c\u9752\u51e4\u4e5f\u8d8a\u6765\u8d8a\u7ea2\uff0c\u90a3\u8361\u6c14\u56de\u80a0\u7684\u6b4c\u58f0\uff0c\u5a01\u529b\u5df2\u8fdc\u64ad\u4e8e\u957f\u5b89\u56db\u90bb\uff0c\u65b9\u5706\u767e\u91cc\u4e4b\u5185\uff0c\u90fd\u5728\u8c08\u8bba\u9752\u51e4\u3002 \u3000\u3000\u90a3\u4e2a\u65f6\u4ee3\uff0c\u914d\u5531\u7684\u662f\u6d1e\u8427\u3001\u4e8c\u80e1\u3001\u4e09\u5f26\u3001\u7435\u7436\u3002 \u3000\u3000\u9752\u51e4\u6b4c\u58f0\u52a8\u4eba\u4e4b\u5916\uff0c\u4e5f\u5f39\u7684\u4e00\u624b\u597d\u7435\u7436\uff0c\u96c6\u59ff\u8272\u624d\u827a\u4e8e\u4e00\u8eab\u3002 \u3000\u3000\u5531\u7684\u6709\u65b0\u8c31\u8bcd\u66f2\uff0c\u4e5f\u6709\u6d41\u884c\u5728\u5173\u6d1b\u4e00\u5e26\u4eba\u4eba\u90fd\u80fd\u54fc\u51e0\u53e5\u7684\u5c0f\u8c03\uff0c\u5979\u58f0\u97f3\u751c\u6da6\uff0c\u82b1\u8154\u5a49\u8f6c\u3002 \u3000\u3000\u957f\u5b89\u57ce\u4e2d\u4eba\u8bf4\u5b83\u662f\u6b4c\uff0c\u57ce\u5916\u4e61\u4e0b\u4eba\uff0c\u5374\u53eb\u5b83\u4e3a\u620f\u3002 \u3000\u3000\u4e3a\u542c\u9752\u51e4\u620f\uff0c\u591a\u8dd1\u5341\u91cc\u5730\u3002 \u3000\u3000\u542c\u5230\u9752\u51e4\u558a\u3002\u518d\u8dd1\u5341\u91cc\u4e5f\u4e0d\u8fdc\u3002 \u3000\u3000\u6d1b\u9633\u5c45\u72ec\u5360\u4e86\u957f\u5b89\u98ce\u6708\uff0c\u522b\u4eba\u5462\uff1f\u53d7\u4e0d\u4e86\u5566\uff01\u72d7\u6025\u8df3\u5899\u3002\u4eba\u4e00\u6025\u5c31\u8c41\u51fa\u53bb\u4e86\uff0c\u4f55\u51b5\uff0c\u80fd\u7ecf\u8425\u8fd9\u79cd\u98ce\u6708\u751f\u610f\u7684\u6709\u51e0\u4e2a\u662f\u89c4\u77e9\u4eba\uff0c\u51e0\u5341\u5bb6\u5e97\u4e1c\u4e3b\u6697\u4e2d\u805a\u4f1a\u5408\u8ba1\u4e86\u4e00\u756a\uff0c\u89c9\u7740\u5750\u7b49\u5173\u95e8\u4e0d\u662f\u529e\u6cd5\uff0c\u5927\u5bb6\u54ac\u54ac\u7259\uff0c\u96c6\u805a\u4e86\u4e00\u7b14\u5927\u94f6\u5b50\u3002\u4e00\u573a\u98ce\u66b4\u3002\u5f00\u59cb\u915d\u917f\u3002 \u3000\u3000\u8d1f\u8d23\u6536\u96c6\u94f6\u5b50\u7684\u4eba\u540d\u53eb\u5468\u5f3a\uff0c\u5468\u5f3a\u4e0d\u662f\u7ecf\u8425\u98ce\u6708\u7684\u5e97\u4e1c\u4e3b\u3002 \u3000\u3000\u4f46\u5374\u662f\u51fa\u4e3b\u610f\u5bf9\u4ed8\u6d1b\u9633\u5c45\u7684\u8c0b\u58eb\uff0c\u9876\u7740\u4e2a\u843d\u7b2c\u79c0\u624d\u7684\u540d\u8854\uff0c\u5230\u5904\u70b9\u98ce\u717d\u706b\uff0c\u4ece\u4e2d\u8c0b\u5229\u3002 \u3000\u3000\u4f46\u6d1b\u9633\u5c45\u624e\u786c\u7684\u540d\u58f0\uff0c\u65e9\u5df2\u4f20\u626c\u957f\u5b89\uff0c\u5468\u5f3a\u5fc3\u4e2d\u660e\u767d\u3002\u9752\u76ae\u5c0f\u6df7\u6df7\u52a8\u4e0d\u4e86\u6d1b\u9633\u5c45\uff0c\u4ed6\u7684\u76ee\u6807\u662f\u79e6\u516b\u516c\uff0c\u5468\u5f3a\u867d\u4e0d\u7b97\u662f\u6c5f\u6e56\u4eba\u7269\uff0c\u4f46\u4ed6\u5e38\u5e74\u5728\u9152\u8086\u3001\u8336\u697c\u4e2d\u8d70\u52a8\uff0c\u4e5f\u542c\u8fc7\u79e6\u516b\u516c\u7684\u5a01\u540d\u3002\u5173\u6d1b\u9053\u4e0a\u7684\u79e6\u516b\u516c\u8dfa\u8dfa\u811a\uff0c\u957f\u5b89\u57ce\u5c31\u4f1a\u6643\u52a8\uff0c\u6240\u4ee5\uff0c\u4ed6\u820c\u70c2\u83b2\u82b1\u3001\u8bf4\u670d\u4e86\u51e0\u5341\u4e2a\u98ce\u6708\u573a\u4e2d\u5e97\u4e1c\u4e3b\u6765\u6b21\u5927\u51fa\u8840\uff0c\u51d1\u8db3\u4e86\u5341\u4e07\u4e24\u94f6\u5b50\u3002 \u3000\u3000\u5341\u4e07\u4e24\u767d\u82b1\u82b1\u7684\u94f6\u5b50\uff0c\u662f\u53eb\u4eba\u773c\u7ea2\u7684\u5927\u6570\u76ee\uff0c\u79e6\u516b\u516c\u5c82\u4f1a\u4e0d\u4e3a\u6240\u52a8\uff1f\u6599\u4e0d\u5230\u7684\u662f\u3002\u79e6\u516b\u516c\u4e24\u4e2a\u6708\u524d\u6258\u75c5\u8c22\u5ba2\uff0c\u4efb\u4f55\u4eba\u90fd\u4e0d\u63a5\u89c1\uff0c\u5468\u79c0\u624d\u8fde\u53bb\u4e09\u6b21\u79e6\u5e9c\u3002\u90fd\u88ab\u6321\u9a7e\uff0c\u5468\u5f3a\u8fd9\u624d\u611f\u89c9\u5230\u4e8b\u60c5\u68d8\u624b\u4e86\u3002 \u3000\u3000\u4f46\u4e0a\u9a6c\u5bb9\u6613\u4e0b\u9a6c\u96be\uff0c\u529e\u4e0d\u597d\u8fd9\u6863\u5b50\u4e8b\uff0c\u90a3\u4e9b\u638f\u51fa\u94f6\u5b50\u7684\u5e97\u4e1c\u4e3b\uff0c\u672a\u5fc5\u4f1a\u9976\u8fc7\u4ed6\uff0c\u4f46\u6700\u91cd\u8981\u662f\u4ed6\u820d\u4e0d\u5f97\u4e00\u4e07\u4e24\u94f6\u5b50\u916c\u8c22\u3002\u4f55\u51b5\uff0c\u529e\u6210\u4e8b\uff0c\u8fd8\u53ef\u4ee5\u5f04\u4e00\u6210\u56de\u6263\u8fdb\u5165\u8377\u5305\uff0c\u5468\u5f3a\u7b97\u8fc7\u8fd9\u7b14\u5e10\uff0c\u505a\u6210\u8fd9\u4e00\u7b14\u751f\u610f\uff0c\u4e0b\u534a\u751f\u5c31\u53ef\u4ee5\u65e0\u5fe7\u65e0\u8651\u5730\u8fc7\u7740\u4eab\u4e50\u65e5\u5b50\u3002 \u3000\u3000\u60f3\u4e86\u51e0\u5929\u4e4b\u540e\uff0c\u5468\u5f3a\u51b3\u5b9a\u5230\u522b\u5904\u8bf7\u4eba\uff0c\u8fdc\u6765\u7684\u548c\u5c1a\u4f1a\u5ff5\u7ecf\u3002 \u3000\u3000\u8fd1\u6765\u7684\u9053\u58eb\u4f1a\u6253\u949f\uff0c\u5e0c\u671b\u80fd\u8bf7\u5230\u51e0\u4e2a\u5389\u5bb3\u4eba\u7269\uff0c\u628a\u6d1b\u9633\u5c45\u7ed9\u7838\u4e86\uff0c\u5f53\u7136\uff0c\u9762\u5bf9\u9762\u5730\u8ba8\u4ef7\u8fd8\u4ef7\uff0c\u4e5f\u53ef\u4ee5\u5c11\u4ed8\u4e9b\u8d39\u7528\uff0c\u81ea\u5df1\u591a\u8d5a\u4e00\u7968\u3002 \u3000\u3000\u7edd\u6280\u5c31\u662f\u7edd\u6280\uff0c\u96be\u5b66\u96be\u7ec3\uff0c\u80e1\u840d\u3001\u6bb5\u4e5d\u3001\u94b1\u7f3a\u3001\u4e03\u5b9d\u548c\u5c1a\u3002 \u3000\u3000\u867d\u7136\u90fd\u6709\u7740\u6839\u6df1\u539a\u7684\u6b66\u529f\u57fa\u7840\uff0c\u5f7c\u6b64\u4e5f\u90fd\u5c3d\u5fc3\u76f8\u6388\uff0c\u4f46\u4ecd\u7136\u82b1\u8d39\u4e86\u56db\u5341\u4e94\u5929\u65f6\u95f4\uff0c\u624d\u5b66\u4e60\u7eaf\u719f\u3002 \u3000\u3000\u56db\u4e2a\u4eba\u90fd\u89c9\u5f97\u5927\u6709\u6536\u83b7\uff0c\u4f46\u56db\u521a\u8001\u9aa8\u5934\uff0c\u4e00\u8fde\u6298\u817e\u4e86\u56db\u5341\u4e94\u5929\uff0c\u4e5f\u90fd\u6709\u8170\u9178\u80cc\u75bc\uff0c\u75b2\u7d2f\u4e0d\u582a\u7684\u611f\u89c9\uff0c\u51b3\u5fc3\u5750\u606f\u4e24\u5929\u3002\u518d\u4f1a\u6d1b\u9633\u5c45\u4e00\u63a2\u7a76\u7adf\u3002 \u3000\u3000\u56db\u4e2a\u4eba\u4e0d\u4f46\u4ed4\u7ec6\u5730\u6b22\u626e\u4e86\u4e00\u756a\uff0c\u800c\u4e14\uff0c\u8fd8\u5206\u4e24\u6279\u884c\u8d70\uff0c\u80e1\u840d\u548c\u6bb5\u4e5d\u626e\u6210\u4e86\u4e24\u4e2a\u751f\u610f\u4eba\uff0c\u957f\u888d\u9a6c\u8902\uff0c\u8001\u53eb\u5316\u548c\u4e03\u5b9d\u548c\u5c1a\uff0c\u5374\u7a7f\u6625\u4e00\u8eab\u7c97\u5e03\u68c9\u8884\uff0c\u9ed1\u8272\u6be1\u5e3d\uff0c\u4e00\u526f\u4e61\u5df4\u4f6c\u7684\u88c5\u626e\u3002 \u3000\u3000\u8fd9\u4e00\u6b21\uff0c\u4ed6\u4eec\u4e0d\u662f\u53bb\u5403\u996d\u559d\u9152\uff0c\u800c\u662f\u53bb\u89c1\u8bc6\u4e00\u4e0b\u540d\u52a8\u957f\u5b89\u7684\u9752\u51e4\u3002 \u3000\u3000\u6d1b\u9633\u697c\u7684\u751f\u610f\u592a\u597d\u4e86\uff0c\u56db\u4e2a\u4eba\u7b49\u4e86\u534a\u5929\uff0c\u624d\u6392\u4e0a\u5ea7\u4f4d\u3002 \u3000\u3000\u80e1\u840d\u3001\u6bb5\u4e5d\u5750\u7684\u662f\u5341\u4e2a\u94f6\u5b50\u4e00\u4e2a\u4eba\u7684\u5305\u53a2\uff0c\u6709\u9999\u8317\u7ec6\u70b9\u4f9b\u5e94\uff0c\u8fd8\u6709\u5e74\u8f7b\u7684\u5c0f\u59d1\u5a18\uff0c\u4e0d\u65f6\u9001\u4e0a\u70ed\u70eb\u7684\u624b\u4e2d\u628a\u5b50\u3002 \u3000\u3000\u94b1\u7f3a\u548c\u4e03\u5b9d\u548c\u5c1a\u5374\u5750\u7684\u4e94\u94b1\u94f6\u5b50\u7684\u5927\u4f17\u5750\u4f4d\u3002\u53ea\u4f9b\u5e94\u4e00\u676f\u9999\u8336\u3002 \u3000\u3000\u56db\u4e2a\u4eba\u90fd\u662f\u8001\u6c5f\u6eaf\u4e86\uff0c\u518d\u52a0\u4e0a\u8c28\u614e\u5c0f\u5fc3\u5730\u4ed4\u7ec6\u89c2\u5bdf\uff0c\u7acb\u523b\u53d1\u89c9\u573a\u4e2d\u7684\u60c5\u52bf\u4e0d\u65f6\u3002 \u3000\u3000\u90a3\u662f\u4e09\u4e2a\u7a7f\u7f8a\u76ae\u8884\u7684\u4e2d\u5e74\u4eba\uff0c\u4ed6\u4eec\u5c31\u5750\u5728\u4e03\u5b9d\u548c\u5c1a\uff0c\u8001\u53eb\u5316\u7684\u524d\u9762\u3002 \u3000\u3000\u4ed6\u4eec\u7684\u4e3e\u6b62\uff0c\u4e5f\u5f88\u5c0f\u5fc3\uff0c\u53ea\u662f\u90a3\u516d\u9053\u773c\u795e\u592a\u8fc7\u51cc\u5389\uff0c\u51b7\u7535\u4e00\u822c\u3002\u56db\u4e0b\u6253\u91cf\u3002 \u3000\u3000\u8001\u53eb\u5316\u4f4e\u58f0\u9053\uff1a\u201c\u8fd9\u51e0\u4e2a\u5bb6\u4f19\u4e00\u65e6\u548c\u6d1b\u9633\u5c45\u7684\u4eba\u52a8\u4e0a\u624b\u54b1\u4eec\u8981\u4e0d\u8981\u6697\u4e2d\u5e2e\u4ed6\u4eec\u4e00\u628a\uff1f\u201d \u3000\u3000\u201c\u6700\u597d\u4e0d\u8981\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u9053\uff1a\u201c\u770b\u4ed6\u4eec\u90a3\u4e2a\u795e\u8272\uff0c\u662f\u65e9\u5df2\u6709\u4e86\u51c6\u5907\uff0c\u5b9a\u7136\u8fd8\u6709\u63a5\u5e94\u7684\u4eba\uff0c\u54b1\u4eec\u6700\u91cd\u8981\u7684\u4e00\u4ef6\u4e8b\uff0c\u5c31\u662f\u8981\u67e5\u770b\u6d1b\u9633\u5c45\u7684\u53cd\u5e94\uff1f\u201d \u3000\u3000\u5750\u5728\u5305\u53a2\u91cc\u7684\u80e1\u840d\u3001\u6bb5\u4e5d\uff0c\u4e5f\u6697\u4e2d\u5546\u91cf\u5b9a\u6848\u3002\u4ed6\u4eec\u7684\u51b3\u5b9a\u662f\u770b\u94b1\u7f3a\u548c\u516d\u5b9d\u548c\u5c1a\u7684\u53cd\u5e94\uff0c\u518d\u51b3\u5b9a\u662f\u5426\u63d2\u624b\u3002 \u3000\u3000\u4e00\u9635\u6025\u7ba1\u7e41\u5f26\u4e4b\u540e\uff0c\u9752\u98ce\u51fa\u73b0\u5728\u7c3f\u53f0\u4e0a\uff0c\u5a49\u8f6c\u51fa\u4e00\u7f15\u6e05\u97f3\uff0c\u5168\u573a\u4e2d\u7acb\u523b\u9759\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u80e1\u840d\u3001\u6bb5\u4e5d\u5750\u7684\u5305\u53a2\u5c45\u9ad8\u4e34\u4e0b\uff0c\u89c6\u754c\u5e7f\u9614\uff0c\u53ea\u662f\u8ddd\u79bb\u9752\u51e4\u7684\u6b4c\u5408\u8fdc\u4e86\u4e00\u4e9b\uff0c\u53ea\u597d\u6697\u4e2d\u8fd0\u6c14\u3002\u51dd\u805a\u76ee\u529b\uff0c\u5e0c\u671b\u770b\u6e05\u695a\u8fd9\u4e2a\u98ce\u9761\u957f\u5b89\u7684\u5c24\u7269\uff0c\u7a76\u7adf\u5982\u4f55\u4e00\u4e2a\u7f8e\u6cd5\u3002 \u3000\u3000\u53ea\u89c1\u5979\u4f53\u6001\u8f7b\u76c8\u3002\u624b\u52bf\u3001\u8868\u60c5\u90fd\u968f\u7740\u6b4c\u58f0\u8f6c\u53d8\u3002\u770b\u4e86\u90a3\u4e48\u4e00\u9635\u3002\u80e1\u840d\u7acb\u523b\u611f\u89c9\u5230\u88ab\u4e00\u80a1\u9b45\u529b\u5438\u4f4f\u4f3c\u7684\uff0c\u4e0d\u7981\u5fc3\u795e\u4e00\u9707\uff0c\u8d74\u5fd9\u95ed\u4e0a\u53cc\u76ee\uff0c\u8fd0\u6c14\u8c03\u606f\u4e00\u9635\uff0c\u624d\u6062\u590d\u6e05\u9192\u3002 \u3000\u3000\u8f6c\u5934\u770b\u53bb\uff0c\u53ea\u89c1\u6bb5\u4e5d\u4e5f\u6b63\u7f13\u7f13\u7741\u5f00\u53cc\u773c\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5389\u5bb3\u5440\uff01 \u3000\u3000\u4eba\u8fd8\u6ca1\u6709\u77a7\u6e05\u695a\uff0c\u5c31\u5dee\u70b9\u4e00\u5171\u683d\u4e86\u4e0b\u53bb\u3002\u201d \u3000\u3000\u201c\u6211\u4e5f\u4e00\u6837\u2026\u2026\u201d\u80e1\u840d\u8bf4\uff1a\u201c\u54b1\u95e8\u62a4\u4f4f\u5fc3\u795e\uff0c\u518d\u8bd5\u8bd5\u770b\u3002\u201d \u3000\u3000\u6bb5\u4e5d\u9053\uff1a\u201c\u4f60\u518d\u8bd5\u8bd5\uff0c\u6211\u66ff\u4f60\u62a4\u6cd5\uff0c\u7559\u5fc3\u5468\u56f4\u7684\u53d8\u5316\u3002\u201d \u3000\u3000\u80e1\u840d\u518d\u4e00\u6b21\u51dd\u795e\u67e5\u770b\uff0c\u679c\u7136\u4e0d\u518d\u4e3a\u9752\u51e4\u7684\u9b45\u529b\u6240\u52a8\uff0c\u53ea\u89c9\u5979\u8eab\u6750\u4fee\u957f\u73b2\u73d1\uff0c\u662f\u4e2a\u5f88\u53ef\u7231\u7684\u59d1\u5a18\uff0c\u53ea\u662f\u8138\u4e0a\u7684\u8102\u7c89\u592a\u6d53\u4e86\u4e00\u4e9b\uff0c\u80e1\u840d\u65e0\u6cd5\u77a7\u6e05\u695a\u5979\u771f\u6b63\u7684\u9762\u76ee\u3002 \u3000\u3000\u5168\u573a\u4e2d\u4eba\uff0c\u5927\u90fd\u770b\u5f97\u5982\u75f4\u5982\u9189\uff0c\u77aa\u7740\u773c\u775b\uff0c\u6295\u5411\u6b4c\u5408\u3002 \u3000\u3000\u80e1\u840d\u77e5\u9053\uff0c\u7edd\u5927\u90e8\u5206\u7684\u4eba\uff0c\u662f\u770b\u4e0d\u6e05\u695a\u7684\uff0c\u770b\u5230\u7684\u53ea\u662f\u5979\u5927\u5e45\u6446\u52a8\u7684\u80a2\u4f53\uff0c\u914d\u5408\u6b4c\u58f0\u6f14\u51fa\u7684\u8868\u60c5\u2026\u2026\u5c31\u8fd9\u6837\uff0c\u9752\u51e4\u4ee5\u52a8\u4eba\u7684\u6b4c\u58f0\u914d\u5408\u80a2\u4f53\u8bed\u8a00\u3002\u63a7\u5236\u4e86\u5168\u573a\u89c2\u4f17\u2026\u2026\u3002 \u3000\u3000\u4f46\u6700\u4f7f\u4eba\u610f\u5916\u662f\u90a3\u4e09\u4e2a\u7a7f\u7740\u770b\u76ae\u7946\u7684\u5927\u6c49\u3002\u7adf\u7136\u6ca1\u6709\u4e00\u70b9\u610f\u5916\u4e3e\u52a8\uff0c\u76f4\u5230\u9752\u51e4\u5531\u5b8c\u4e86\u6700\u540e\u4e00\u66f2\uff0c\u8f6c\u56de\u540e\u53f0\uff0c\u4ed6\u4eec\u4ecd\u7136\u9759\u9759\u7684\u5750\u7740\u6ca1\u52a8\uff0c\u60f3\u662f\u4e5f\u88ab\u9752\u51e4\u795e\u97f5\u6b4c\u58f0\u6240\u8ff7\u3002 \u3000\u3000\u8fd9\u662f\u767d\u5929\u6700\u540e\u4e00\u573a\uff0c\u63a5\u4e0b\u6765\u662f\u591c\u573a\uff0c\u8981\u4e00\u756a\u65f6\u95f4\u51c6\u5907\uff0c\u5341\u51e0\u4e2a\u5e74\u8f7b\u4eba\uff0c\u8fdb\u5165\u6d1b\u9633\u697c\u4e2d\uff0c\u51c6\u5907\u6253\u626b\u573a\u5730\uff0c\u70b9\u71c3\u706f\u706b\u3002 \u3000\u3000\u8001\u53eb\u5316\u4e0d\u76f8\u4fe1\u4f1a\u770b\u8d70\u773c\uff0c\u6545\u610f\u7559\u5728\u540e\u9762\uff0c\u770b\u5230\u4e09\u4e2a\u5927\u6c49\u8d77\u8eab\u79bb\u53bb\uff0c\u624d\u7f13\u7f13\u6b65\u51fa\u6d1b\u9633\u697c\u3002 \u3000\u3000\u8fd9\u5929\uff0c\u662f\u6b63\u6708\u521d\u516d\uff0c\u539f\u6765\u3002\u56db\u4eba\u82e6\u7ec3\u6b66\u529f\uff0c\u8fde\u8fc7\u5e74\u8fd9\u7b49\u5927\u4e8b\uff0c\u7adf\u4e5f\u5fd8\u53bb\u3002 \u3000\u3000\u4f46\u957f\u5b89\u57ce\u4e2d\u6302\u706f\u7ed3\u5f69\uff0c\u5230\u5904\u5145\u6ee1\u7740\u8fc7\u7f55\u7684\u6c14\u606f\u3002 \u3000\u3000\u6d1b\u9633\u5c45\u66f4\u662f\u5e03\u7f6e\u7684\u5f69\u697c\u706f\u5c71\uff0c\u5404\u5904\u5df2\u5f00\u59cb\u70b9\u8d77\u706f\u706b\u3002 \u3000\u3000\u843d\u96ea\u516d\u4e03\u65e5\uff0c\u4eca\u5929\uff0c\u8d76\u4e0a\u4e2a\u6674\u6717\u7684\u65e5\u5b50\uff0c\u843d\u65e5\u665a\u971e\u548c\u5404\u79cd\u706f\u5149\u76f8\u6620\u6210\u8f89\uff0c\u518d\u52a0\u4e0a\u5f69\u697c\u63a9\u6620\uff0c\u7f6e\u8eab\u5176\u95f4\uff0c\u76f4\u4f3c\u8bef\u5165\u5929\u53f0\u3002 \u3000\u3000\u8001\u53eb\u5316\u4e5f\u4e0d\u7981\u6697\u6697\u8d5e\u53f9\uff0c\u4ed8\u9053\uff1a\u201c\u8fd9\u91cc\u786e\u662f\u6709\u5f88\u591a\u4eba\u624d\uff0c\u5355\u662f\u77a7\u77a7\u8fd9\u756a\u5e03\u7f6e\u3002\u5c31\u8be5\u503c\u4e94\u94b1\u94f6\u5b50\u4e86\u3002\u201d \u3000\u3000\u80e1\u840d\u3001\u6bb5\u4e5d\u7f13\u6b65\u884c\u6765\uff0c\u80e1\u840d\u4f4e\u58f0\u95ee\u9053\uff1a\u201c\u73b0\u5728\uff0c\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u201c\u518d\u542c\u4e00\u573a\u6b4c\uff0c\u4ed6\u4eec\u5927\u6982\u8981\u5728\u665a\u4e0a\u52a8\u624b\uff0c\u5176\u4ed6\u7684\u7ea6\u5b9a\u5982\u524d\u2026\u2026\u201d\u8001\u53eb\u5316\u8bf4\uff1a\u201c\u5b9a\u597d\u4f4d\u7f6e\u5c31\u5404\u5904\u901b\u901b\u3002\u201d \u3000\u3000\u4e24\u4eba\u64e6\u80a9\u800c\u8fc7\uff0c\u8bf4\u8bdd\u7684\u58f0\u97f3\u5f88\u4f4e\uff0c\u5c31\u7b97\u7ad9\u5728\u8eab\u4fa7\u7684\u4eba\uff0c\u4e5f\u5f88\u96be\u542c\u5230\uff0c\u8bdd\u8bf4\u5b8c\uff0c\u5df2\u76f8\u8ddd\u4e86\u4e09\u56db\u5c3a\u8fdc\u3002 \u3000\u3000\u6bb5\u4e5d\u4e00\u76f4\u5728\u5229\u7528\u8eab\u4f53\u7684\u4fa7\u8f6c\uff0c\u63a9\u62a4\u80e1\u840d\u7684\u884c\u52a8\u3002 \u3000\u3000\u4e24\u4e2a\u4eba\u4e5f\u90fd\u5f88\u6ee1\u610f\u81ea\u5df1\u7684\u8c28\u614e\u8bbe\u8ba1\uff0c\u80e1\u840d\u8bf4\u9053\uff1a\u201c\u9752\u51e4\u4e4b\u540d\u3002 \u3000\u3000\u679c\u4e0d\u865a\u4f20\uff0c\u54b1\u4eec\u518d\u542c\u4e00\u573a\u5982\u4f55\uff1f\u201d \u3000\u3000\u201c\u884c\uff01\u5144\u5f1f\u4e5f\u6709\u7740\u610f\u72b9\u672a\u5c3d\u4e4b\u611f\u2026\u2026\u201d\u6bb5\u4e5d\u4e00\u9762\u56de\u7b54\uff0c\u4e00\u9762\u6298\u4eba\u4e00\u6761\u788e\u77f3\u5c0f\u5728\u4e4b\u4e2d\u3002 \u3000\u3000\u53ea\u542c\u4e00\u4e2a\u4f4e\u5fae\u53c8\u6e05\u695a\u7684\u58f0\u97f3\uff0c\u94bb\u5165\u4e86\u8033\u4e2d\uff0c\u9053\uff1a\u201c\u5c0f\u5fc3\u4e9b\u901b\u901b\u53ef\u4ee5\uff0c\u5343\u4e07\u522b\u60f9\u9ebb\u70e6\uff0c\u4e0d\u8bba\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\u3002\u90fd\u4e0d\u8981\u63d2\u624b\u3002\u8bb0\u4f4f\uff0c\u628a\u6211\u7684\u8bdd\u544a\u8bc9\u8001\u53eb\u5316\u548c\u9152\u8089\u548c\u5c1a\uff0c\u660e\u5929\u4e0b\u5348\u6211\u6709\u7a7a\uff0c\u5982\u679c\u8bf8\u4f4d\u60f3\u548c\u6211\u78b0\u4e2a\u9762\uff0c\u57ce\u4e1c\u705e\u6865\u4e4b\u65c1\u6709\u5bb6\u98ce\u96ea\u5c0f\u9986\u3002\u6211\u5728\u90a3\u91cc\u5019\u9a7e\u3002\u201d \u3000\u3000\u58f0\u97f3\u4e2d\u65ad\uff0c\u666f\u7269\u4f9d\u65e7\uff0c\u6d41\u76ee\u56db\u987e\uff0c\u4e0d\u89c1\u4eba\u8ff9\u3002 \u3000\u3000\u80e1\u840d\u8f7b\u8f7b\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6bb5\u5144\uff0c\u4f60\u542c\u5230\u4ec0\u4e48\u6ca1\u6709\uff1f\u201d \u3000\u3000\u201c\u6709\u4eba\u7ea6\u6211\u4eec\u5230\u98ce\u96ea\u5c0f\u9986\u78b0\u9762\uff0c\u53bb\u4e0d\u53bb\u5462\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u8981\u53bb\uff0c\u705e\u6865\u98ce\u96ea\uff0c\u4e43\u957f\u5b89\u516b\u666f\u4e4b\u4e00\u2026\u2026\u201d\u80e1\u840d\u8bf4\uff1a\u201c\u54b1\u4eec\u53bb\u901a\u77e5\u4ed6\u4eec\u4e00\u58f0\u3002\u201d \u3000\u3000\u201c\u6211\u8fd8\u8ba4\u4e3a\u6211\u4eec\u6539\u626e\u7684\u5929\u8863\u65e0\u7f1d\uff01\u201d\u6bb5\u4e5d\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u65e9\u5df2\u88ab\u4eba\u5bb6\u7ed9\u76ef\u4e0a\u4e86\u3002\u201d \u3000\u3000\u80e1\u840d\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u542c\u53e3\u6c14\uff0c\u597d\u50cf\u5e76\u65e0\u6076\u610f\uff0c\u4e5f\u8bb8\uff0c\u6211\u4eec\u53c8\u9047\u4e0a\u4e86\u4e00\u4e2a\u5fd7\u540c\u9053\u5408\u7684\u9ad8\u4eba\u3002\u201d \u3000\u3000\u201c\u4f46\u613f\u5982\u6b64\u4e86\u2026\u2026\u201d\u6bb5\u4e5d\u8bf4\uff1a\u201c\u4ed6\u8bf4\u7684\u5f88\u660e\u767d\uff0c\u4eca\u665a\u4e0a\u8fd9\u91cc\u4f1a\u51fa\u4e8b\u60c5\u3002\u4e5f\u8bb8\u3002\u90a3\u4e9b\u540c\u4e8b\u7684\u4eba\u548c\u6211\u4eec\u5b58\u5fc3\u4e00\u6837\uff0c\u60f3\u501f\u673a\u6d4b\u63a2\u4e00\u4e0b\u6d1b\u9633\u5c45\u7684\u6df1\u6d45\uff0c\u4e07\u4e00\u4ed6\u4eec\u9047\u4e0a\u4e86\u5371\u9669\uff1f\u54b1\u4eec\u662f\u4e0d\u662f\u771f\u7684\u4e0d\u95fb\u4e0d\u95ee\u5462\uff1f\u201d \u3000\u3000\u80e1\u840d\u9053\uff1a\u201c\u5144\u5f1f\u4e5f\u5728\u4e3a\u8fd9\u4ef6\u4e8b\u53d1\u6101\uff0c\u5f88\u96be\u4f5c\u4e2a\u51b3\u5b9a\uff1f\u6211\u770b\u4e22\u7ed9\u8001\u53eb\u5316\u53bb\u4f24\u8111\u7b4b\u5427\uff1f\u201d \u3000\u3000\u4e24\u4eba\u82b1\u8d39\u4e86\u76f8\u5f53\u7684\u65f6\u95f4\uff0c\u624d\u627e\u5230\u4e86\u8001\u53eb\u5316\u548c\u9152\u8089\u548c\u5c1a\uff0c\u5e72\u8106\u62c9\u4e24\u4eba\u9000\u51fa\u6d1b\u9633\u5c45\u3002\u627e\u4e2a\u5c0f\u9986\u5403\u665a\u996d\uff0c\u8bf4\u51fa\u4e86\u7ecf\u8fc7\u60c5\u5f62\u3002 \u3000\u3000\u8001\u53eb\u5316\u548c\u4e03\u5b9d\u548c\u5c1a\uff0c\u90fd\u542c\u5f97\u76f8\u5f53\u7684\u5403\u60ca\uff0c\u6c89\u541f\u4e86\u826f\u4e45\uff0c\u94b1\u7f3a\u624d\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u770b\u6837\u5b50\uff0c\u54b1\u4eec\u65e9\u6ce2\u4eba\u5bb6\u76d1\u89c6\u4e86\uff0c\u6050\u6015\u8fde\u54b1\u4eec\u5728\u90a3\u7834\u5e99\u4e2d\u4e92\u4f20\u6b66\u529f\u7684\u4e8b\uff0c\u4e5f\u5df2\u7ecf\u843d\u5165\u4e86\u4eba\u5bb6\u773c\u4e2d\uff0c\u2026\u2026\u201d \u3000\u3000\u8001\u53eb\u5316\uff1a\u201c\u90a3\u4e5f\u662f\u6ca1\u6709\u529e\u6cd5\u7684\u4e8b\u60c5\uff0c\u81f3\u4e8e\u4eca\u665a\u4e0a\u7684\u4e8b\uff0c\u5927\u6982\u662f\u6307\u90a3\u4e09\u4e2a\u7a7f\u7f8a\u76ae\u8884\u7684\u5927\u6c49\u4e86\uff0c\u8001\u53eb\u5316\u4ed4\u7ec6\u5730\u77a7\u8fc7\u4ed6\u4eec\uff0c\u8138\u4e0a\u7684\u7eb9\u8def\u6e05\u6670\uff0c\u6ca1\u6709\u7ecf\u8fc7\u6613\u5bb9\uff0c\u4f46\u8001\u53eb\u5316\u5374\u4e0d\u8ba4\u8bc6\u4ed6\u4eec\u3002\u2026\u2026\u201d \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u63a5\u9053\uff1a\u201c\u770b\u4ed6\u4eec\u90a3\u80a1\u51f6\u608d\u4e4b\u6c14\uff0c\u5927\u6982\u4e0d\u662f\u4ec0\u4e48\u597d\u4eba\uff1f\u5c31\u8863\u7740\u800c\u8bba\uff0c\u4f3c\u662f\u6765\u81ea\u5173\u5916\uff0c\u8fd9\u4ef6\u4e8b\uff0c\u6211\u4e3b\u5f20\u614e\u91cd\u5e94\u4ed8\uff0c\u4e0d\u80fd\u6bdb\u71e5\u3002\u201d \u3000\u3000\u201c\u80fd\u4e0d\u51fa\u624b\uff0c\u6700\u597d\u5fcd\u4e0b\u2026\u2026\u201d\u8001\u53eb\u5316\u8bf4\uff1a\u201c\u501f\u673a\u4f1a\u770b\u770b\u6d1b\u9633\u5c45\u4e2d\u4eba\u7684\u8eab\u624b\uff1f\u662f\u6b66\u6797\u4e2d\u6b63\u6d3e\u4eba\u7269\uff0c\u90a3\u5c31\u53e6\u5f53\u522b\u8bba\u4e86\u3002\u201d \u3000\u3000\u80e1\u840d\u9053\uff1a\u201c\u597d\uff01\u5c31\u8fd9\u4e48\u51b3\u5b9a\uff0c\u6211\u548c\u6bb5\u5144\u4ee5\u94b1\u5144\u7684\u884c\u52a8\u4e3a\u51c6\u3002\u201d \u3000\u3000\u56db\u4eba\u8ba1\u8bae\u505c\u5f53\uff0c\u53c8\u8d76\u56de\u4e86\u6d1b\u9633\u697c\u3002 \u3000\u3000\u679c\u7136\u3002\u90a3\u4e09\u4e2a\u7a7f\u7f8a\u76ae\u8884\u7684\u5927\u6c49\u65e9\u5df2\u5728\u5ea7\u3002 \u3000\u3000\u8fd9\u4e00\u6b21\u3002\u4ed6\u95e8\u5750\u5728\u6700\u524d\u9762\u7684\u4e00\u6392\u5ea7\u4f4d\u4e0a\uff0c\u4e5f\u5c31\u662f\u8ddd\u9752\u51e4\u6b4c\u53f0\u6700\u8fd1\u7684\u5730\u65b9\u3002 \u3000\u3000\u4f46\u6d1b\u9633\u697c\u5e76\u6ca1\u6709\u7279\u522b\u7684\u6212\u5907\u3002\u81f3\u5c11\u77a7\u4e0d\u51fa\u6765\uff0c\u56db\u4e2a\u7a7f\u7740\u9ed1\u68c9\u8884\u7684\u4f19\u8ba1\uff0c\u7aef\u7740\u5927\u6728\u76d8\uff0c\u5206\u522b\u628a\u8336\u7897\u9001\u5230\u5ba2\u4eba\u7684\u5ea7\u4f4d\u4e0a\u3002 \u3000\u3000\u80e1\u840d\u3001\u6bb5\u4e5d\u4ecd\u7136\u4e70\u7684\u662f\u5305\u53a2\u4f4d\u7f6e\uff0c\u53ef\u4fef\u77b0\u5168\u573a\u5f62\u52bf\u3002 \u3000\u3000\u4e00\u9635\u6025\u5f6d\u5feb\u5f26\uff0c\u9752\u51e4\u4e00\u8eab\u8471\u7eff\u7684\u8863\u88d9\uff0c\u59d7\u59d7\u6b65\u4e0a\u6b4c\u53f0\u3002 \u3000\u3000\u8fd9\u4e00\u6b21\u5531\u7684\u662f\u957f\u5b89\u6d41\u884c\u5c0f\u8c03\uff0c\u4e00\u66f2\u6b4c\u7f62\uff0c\u66b4\u8d77\u4e86\u5982\u96f7\u7684\u638c\u58f0\u3002 \u3000\u3000\u4e09\u6761\u4eba\u5f71\uff0c\u7a81\u7136\u7531\u524d\u6392\u5750\u4f4d\u4e0a\u98de\u8d77\uff0c\u50cf\u4e09\u6761\u98de\u9e70\u822c\u8f7b\u5de7\u7684\u843d\u4e0a\u6b4c\u53f0\u3002 \u3000\u3000\u4e0d\u9517\uff0c\u5c31\u662f\u90a3\u4e09\u4e2a\u7a7f\u7740\u7f8a\u76ae\u8884\u7684\u5927\u6c49\u3002 \u3000\u3000\u4e24\u4e2a\u4eba\u5de6\u53f3\u963b\u622a\uff0c\u5148\u5c01\u4f4f\u4e86\u9752\u51e4\u7684\u9000\u8def\uff0c\u5c45\u4e2d\u4e00\u4eba\uff0c\u5374\u76f4\u5411\u9752\u51e4\u903c\u53bb\u3002 \u3000\u3000\u9752\u51e4\u4e00\u5bf9\u5927\u773c\u775b\u5de6\u53f3\u8f6c\u52a8\uff0c\u6253\u91cf\u4e09\u4e2a\u5927\u6c49\uff0c\u8138\u4e0a\u5374\u4e00\u7247\u5e73\u9759\uff0c\u5168\u65e0\u60ca\u614c\u4e4b\u8272\uff0c\u8fde\u90a3\u4e50\u5e08\u4eec\uff0c\u7adf\u7136\u4e5f\u89c1\u602a\u4e0d\u602a\uff0c\u867d\u7136\u505c\u4e0b\u4e86\u5f26\u7ba1\uff0c\u5374\u90fd\u9759\u5750\u5728\u539f\u4f4d\u4e0a\u4e0b\u52a8\u3002 \u3000\u3000\u5012\u662f\u53f0\u4e0b\u7684\u89c2\u4f17\u53d1\u51fa\u4e86\u559d\u53f1\u60ca\u53eb\u4e4b\u58f0\u3002 \u3000\u3000\u4e00\u58f0\u6e05\u8106\u5a07\u751c\u58f0\u97f3\u54cd\u8d77\uff0c\u9053\uff1a\u201c\u4e09\u4f4d\u5927\u7237\u6709\u4ec0\u4e48\u6307\u6559\u3002\u53ef\u5426\u5148\u8ba9\u9752\u51e4\u5531\u5b8c\u8fd9\u4e00\u573a\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u770b\u4e0d\u7528\u518d\u5531\u4e86\uff0c\u60f3\u5531\u561b\uff0c\u8ddf\u5927\u7237\u5230\u623f\u95f4\u91cc\u5531\u2026\u2026\u201d\u5c45\u4e2d\u5927\u6c49\u9053\uff1a\u201c\u5531\u4e2a\u5341\u516b\u6478\uff0c\u7ed9\u5927\u7237\u6211\u4e00\u4e2a\u4eba\u542c\u3002\u201d \u3000\u3000\u53f3\u624b\u626c\u8d77\uff0c\u4e94\u6839\u7c97\u58ee\u7684\u624b\u6307\uff0c\u6709\u5982\u4e94\u53ea\u94a2\u94a9\u4e00\u822c\uff0c\u6293\u5411\u9752\u51e4\u7684\u5de6\u80a9\u3002 \u3000\u3000\u5a07\u67d4\u7684\u9752\u51e4\u5982\u4f55\u80fd\u968f\u53d7\u5f97\u4f4f\u8fd9\u5927\u6c49\u4e94\u6307\u7684\u6263\u62ff\u3002\u53f0\u4e0b\u89c2\u4f17\u66b4\u51fa\u7c97\u53e3\u6012\u9a82\u3002 \u3000\u3000\u9752\u51e4\u5462\uff1f\u5927\u6982\u662f\u5413\u6655\u4e86\u3002\u66f0\u4e2d\u5624\u549b\u4e00\u58f0\uff0c\u5411\u540e\u5012\u53bb\uff0c\u9002\u65f6\u5730\u907f\u5f00\u4e86\u5927\u6c49\u7684\u64d2\u62ff\u3002 \u3000\u3000\u4e09\u6761\u4eba\u5f71\u95ea\u52a8\u3002\u98de\u4e0a\u6b4c\u53f0\u3002 \u3000\u3000\u80e1\u840d\u51dd\u795e\u6ce8\u89c6\uff0c\u7adf\u662f\u6367\u76d8\u9001\u8336\u7684\u4f19\u8ba1\u3002 \u3000\u3000\u4ed6\u4eec\u90fd\u662f\u5341\u51e0\u4e8c\u5341\u5c81\u7684\u5c0f\u4f19\u5b50\uff0c\u4f46\u52a8\u4f5c\u5374\u7075\u5de7\u5feb\u901f\uff0c\u8eab\u5b50\u95ea\u52a8\u4e2d\uff0c\u7adf\u4ece\u4e09\u4e2a\u5927\u6c49\u4e2d\u95f4\u7a7a\u9699\u7a7f\u5165\uff0c\u62e6\u5728\u9752\u51e4\u8eab\u524d\u3002 \u3000\u3000\u5355\u662f\u8fd9\u4e00\u624b\u7075\u5de7\u7684\u8f7b\u529f\uff0c\u5c31\u6bd4\u90a3\u4e09\u5343\u5927\u6c49\u9ad8\u660e\u3002 \u3000\u3000\u4ed6\u4eec\u62a4\u4f4f\u9752\u51e4\uff0c\u5374\u672a\u5bf9\u4e09\u4e2a\u5927\u6c49\u51fa\u624b\uff0c\u53cd\u9762\uff0c\u4e0d\u505c\u4f5c\u63d6\u6253\u8eac\u3002\u9053\uff1a\u201c\u4e09\u4f4d\u5927\u7237\uff0c\u6709\u8bdd\u597d\u8bf4\uff0c\u90a3\u91cc\u4e0d\u6ee1\u610f\uff0c\u5c0f\u7684\u4eec\u7acb\u523b\u6539\u8fdb\u3002\u201d \u3000\u3000\u4e09\u4e2a\u5927\u6c49\u4e00\u770b\u60c5\u5f62\uff0c\u5fc3\u4e2d\u660e\u767d\uff0c\u8981\u5e26\u8d70\u9752\u51e4\uff0c\u975e\u5f97\u5148\u6253\u53d1\u8fd9\u4e09\u4e2a\u9ed1\u8863\u5c11\u5e74\u4e0d\u53ef\u3002 \u3000\u3000\u5c45\u4e2d\u5927\u6c49\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4e0d\u5173\u4f60\u4eec\u7684\u4e8b\u3002\u6211\u4eec\u627e\u7684\u662f\u9752\u51e4\uff0c\u4e09\u4f4d\u8bf7\u81ea\u6c42\u591a\u798f\u5427\uff01\u201d \u3000\u3000\u201c\u5509\uff01\u5927\u7237\u2026\u2026\u201d\u9762\u5bf9\u5c45\u4e2d\u5927\u6c49\u7684\u5e74\u8f7b\u4eba\u8bf4\uff1a\u201c\u8fd9\u8bdd\u5c31\u4e0d\u5bf9\u677f\u4e86\u3002\u9752\u51e4\u662f\u5c0f\u7684\u4eec\u7684\u8863\u98df\u7236\u6bcd\uff0c\u5927\u7237\u6293\u8d70\u9752\u51e4\uff0c\u6216\u662f\u6740\u4e86\u9752\u51e4\u3002 \u3000\u3000\u6211\u4eec\u4ee5\u540e\u7684\u751f\u6d3b\uff0c\u5982\u4f55\u7ef4\u6301\uff1f\u201d \u3000\u3000\u5c45\u4e2d\u5927\u7237\u6012\u9053\uff1a\u201c\u4f60\u662f\u5f3a\u8981\u51fa\u5934\u4e86\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6cd5\u5b50\u3002\u4e3a\u4e86\u751f\u8bdd\uff0c\u53ea\u597d\u5e2e\u5e2e\u9752\u51e4\u59d1\u5a18\u4e86\u3002\u201d \u3000\u3000\u5927\u6c49\u626c\u624b\u4e00\u638c\uff0c\u62cd\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u5e74\u8f7b\u4eba\u5c45\u7136\u672a\u8fd8\u624b\uff0c\u4e5f\u672a\u95ea\u907f\u3002\u53ea\u662f\u8eab\u5b50\u5fae\u4fa7\uff0c\u907f\u5f00\u4e86\u8981\u5bb3\uff0c\u786c\u78b0\u786c\u5730\u6328\u4e86\u4e00\u638c\u3002 \u3000\u3000\u84ec\u7136\u54cd\u9732\u4e2d\uff0c\u9ed1\u8863\u5e74\u8f7b\u4eba\u88ab\u6253\u5f97\u8fde\u9000\u4e24\u6b65\uff0c\u4e00\u5c41\u80a1\u5750\u5728\u5730\u4e0a\u3002 \u3000\u3000\u8fd9\u4e00\u638c\u529b\u9053\u663e\u7136\u5f88\u91cd\u3002 \u3000\u3000\u5e74\u8f7b\u4eba\u7adf\u7136\u80fd\u627f\u53d7\u5f97\u4f4f\uff0c\u722c\u8d77\u6765\uff0c\u62cd\u62cd\u5c41\u80a1\u4e0a\u7684\u7070\u571f\uff0c\u7b11\u9053\uff1a\u201c\u5927\u7237\uff0c\u8fd9\u4e00\u638c\u597d\u91cd\u554a\uff01 \u3000\u3000\u81f3\u5c11\u6709\u4e09\u767e\u65a4\u7684\u6c14\u529b\uff0c\u5c0f\u7684\u6253\u4e5f\u6328\u4e86\uff0c\u9a82\u4e5f\u53d7\u4e86\uff0c\u4f60\u8001\u4e5f\u8be5\u6d88\u6d88\u6c14\u4e86\u3002\u201d \u3000\u3000\u5c45\u4e2d\u5927\u6c49\u8138\u8272\u4e00\u53d8\uff0c\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u6ca1\u6709\u4e8b\uff1f\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\u4f60\u8001\u662f\u5927\u4eba\u4e0d\u89c1\u5c0f\u4eba\u602a\uff0c\u624b\u4e0b\u7559\u60c5\u3001\u7ed9\u5c0f\u7684\u4e00\u6761\u6d3b\u8def\uff0c\u6211\u8fd9\u91cc\u591a\u8c22\u5927\u6069\u5927\u5fb7\u3002\u201d \u3000\u3000\u8bf4\u7f62\uff0c\u8eac\u8eab\u4e00\u4e2a\u957f\u63d6\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u9752\u51e4\u4e5f\u722c\u7ad9\u8d77\u6765\uff0c\u7adf\u7136\u6ca1\u6709\u907f\u8d70\uff0c\u4ecd\u7ad9\u5728\u539f\u5730\u53d1\u695e\u3002 \u3000\u3000\u8bf4\u5979\u53d1\u6123\uff0c\u662f\u77aa\u7740\u4e00\u65f6\u6c34\u6c6a\u6c6a\u7684\u773c\u775b\uff0c\u77a7\u7740\u90a3\u5c45\u4e2d\u5927\u6c49\uff0c\u8138\u4e0a\u662f\u4e00\u80a1\u81ea\u601c\u81ea\u60dc\u7684\u795e\u8272\uff0c\u52a8\u4eba\u6781\u4e86\u3002 \u3000\u3000\u90a3\u5c45\u4e2d\u5927\u6c49\u5ffd\u7136\u53f9\u606f\u4e00\u58f0\u3002\u9053\uff1a\u201c\u7f62\u4e86\uff01\u7f62\u4e86\uff01\u201d\u8f6c\u8eab\u8df3\u4e0b\u6b4c\u53f0\uff0c\u5feb\u6b65\u800c\u53bb\u3002 \u3000\u3000\u9752\u51e4\u79cb\u5f7c\u4e00\u8f6c\uff0c\u626b\u63a0\u4e86\u53e6\u5916\u4e24\u4e2a\u7a7f\u7f8a\u76ae\u8884\u7684\u5927\u6c49\u4e00\u773c\uff0c\u4e24\u4e2a\u4eba\u4e5f\u5168\u4f3c\u6ca1\u4e86\u706b\u6c14\uff0c\u7ffb\u8eab\u4e0b\u53f0\uff0c\u5306\u5306\u800c\u53bb\u3002 \u3000\u3000\u8fd9\u4e0d\u662f\u63b3\u5165\u6253\u67b6\u3002\u5f7b\u5934\u5f7b\u5c3e\u4f3c\u662f\u4e00\u573a\u95f9\u5267\uff0c\u4f46\u5374\u770b\u5f97\u8001\u53eb\u5316\u548c\u9152\u8089\u548c\u5c1a\u5fc3\u4e2d\u9707\u52a8\u4e0d\u5df2\uff0c\u9752\u51e4\u8868\u73b0\u7684\u6444\u9b42\u5927\u6cd5\uff0c\u6bd4\u8d77\u90a3\u4f8d\u9152\u7684\u6625\u82b1\u56db\u5a62\uff0c\u4e0d\u77e5\u9ad8\u660e\u591a\u5c11\u500d\u4e86\u3002 \u3000\u3000\u90a3\u9ed1\u8863\u5e74\u8f7b\u4eba\u7684\u6328\u6253\u529f\u592b\uff0c\u4e5f\u4e0d\u662f\u4e00\u822c\u7684\u6b66\u6797\u4eba\u7269\u6240\u80fd\u6bd4\u62df\uff0c\u91cd\u91cd\u7684\u4e00\u638c\uff0c\u7adf\u5bf9\u4ed6\u5168\u65e0\u4f24\u5bb3\u3002 \u3000\u3000\u6ca1\u6709\u4eba\u8ffd\u8d76\u90a3\u4e09\u4e2a\u95f9\u4e8b\u7684\u5927\u6c49\uff0c\u8df3\u4e0a\u6b4c\u53f0\u4e09\u4e2a\u4f8d\u8336\u4f19\u8ba1\u3002\u4e5f\u8ddf\u7740\u4e0b\u4e86\u6b4c\u53f0\u3002 \u3000\u3000\u4e8e\u662f\uff0c\u9752\u51e4\u7684\u60a0\u7f8e\u6b4c\u58f0\uff0c\u53c8\u81ea\u54cd\u8d77\u3002 \u3000\u3000\u66b4\u95f9\u559d\u9a82\u7684\u89c2\u4f17\uff0c\u4e5f\u5bc2\u9759\u4e0b\u6765\uff0c\u53c8\u805a\u7cbe\u4f1a\u795e\u7684\u542c\u9752\u51e4\u7684\u6b4c\u5531\u3002 \u3000\u3000\u6b63\u5f53\u9752\u51e4\u5531\u7684\u8d77\u52b2\uff0c\u89c2\u4f17\u542c\u5f97\u8fc7\u763e\u65f6\uff0c\u697c\u4e0a\u7684\u5305\u53a2\u4e2d\u5374\u7a81\u7136\u98de\u5c04\u51fa\u4e09\u9053\u7ec6\u5c0f\u7684\u5bd2\u8292\uff0c\u76f4\u5411\u6b4c\u53f0\u3002 \u3000\u3000 \u7b2c \u4e8c \u56de\u3000\u5de7\u5986\u626e\u9669\u95ef\u83ca\u82b1\u5385 \u3000 \u3000\u3000\u9752\u51e4\u6b4c\u58f0\u7a81\u7136\u4e2d\u65ad\uff0c\u53f3\u624b\u63a9\u4f4f\u5de6\u80f8\uff0c\u8e72\u4e86\u4e0b\u53bb\u3002\u8fd9\u4e0d\u662f\u505a\u4f5c\uff0c\u662f\u771f\u6b63\u5730\u53d7\u4e86\u4f24\u3002 \u3000\u3000\u7a81\u7136\u98de\u51fa\u7684\u6697\u5668\uff0c\u5728\u5979\u5168\u795e\u6b4c\u5531\u65f6\uff0c\u5c04\u4e2d\u4e86\u5979\u7684\u524d\u80f8\u3002\u53f0\u4e0b\u53c8\u662f\u4e00\u9635\u559d\u53eb\u5927\u4e71\u3002 \u3000\u3000\u4f46\u697c\u4e0a\u7684\u5305\u53a2\u4e2d\u5374\u54cd\u8d77\u4e86\u4e24\u58f0\u624b\u638c\u76f8\u51fb\u7684\u58f0\u97f3\uff0c\u7d27\u63a5\u7740\u4e00\u6761\u4eba\u5f71\u98de\u8dc3\u800c\u51fa\uff0c\u98de\u5411\u6d1b\u9633\u697c\u5916\u3002 \u3000\u3000\u8fd9\u4e00\u6b21\uff0c\u6709\u4eba\u8ffd\u4e86\uff0c\u4e09\u56db\u6761\u4eba\u5f71\uff0c\u98de\u5954\u8ffd\u53bb\u3002 \u3000\u3000\u80e1\u840d\u6697\u6697\u5618\u4e00\u53e3\u6c14\uff0c\u4ed8\u9053\uff1a\u201c\u6253\u51fa\u6697\u5668\u7684\u4eba\u3002\u5c31\u5728\u5de6\u8fd1\u3002\u5982\u679c\u4e0d\u662f\u65e9\u6709\u4e25\u5bc6\u7684\u76d1\u89c6\uff0c\u771f\u8fd8\u4e0d\u6613\u627e\u5f97\u51fa\u6765\u3002\u201d \u3000\u3000\u4e8b\u60c5\u53c8\u51fa\u4e86\u8001\u53eb\u4ed6\u7684\u610f\u5916\uff0c\u65e2\u6ca1\u6709\u77a7\u51fa\u95f9\u4e8b\u4eba\u4ec0\u4e48\u8eab\u4efd\uff0c\u4e5f\u6ca1\u673a\u4f1a\u770b\u770b\u6d1b\u9633\u5c45\u4e2d\u7684\u4eba\u6b66\u529f\u8def\u6570\uff0c\u53ea\u597d\u968f\u7740\u4eba\u6f6e\uff0c\u9000\u51fa\u4e86\u6d1b\u9633\u5c45\u3002 \u3000\u3000\u94b1\u7f3a\u3001\u516d\u5b9d\u548c\u5c1a\uff0c\u6742\u5728\u4e89\u76f8\u8d70\u907f\u7684\u5ba2\u4eba\u7fa4\u4e2d\uff0c\u6697\u91cc\u89c2\u5bdf\u56db\u5468\u5f62\u52bf\u3002 \u3000\u3000\u4ed6\u4eec\u53d1\u89c9\u4e86\u6df7\u4e71\u7684\u60c5\u5f62\uff0c\u53ea\u9650\u9752\u51e4\u732e\u5531\u7684\u6d1b\u9633\u697c\u8fd9\u4e2a\u533a\u57df\u3002\u5176\u4ed6\u7684\u996d\u5e84\u3001\u8d4c\u573a\u3002\u90fd\u672a\u53d7\u6ce2\u53ca\uff0c\u4ecd\u7136\u4fdd\u6301\u7740\u76f8\u5f53\u7684\u5e73\u9759\u3002 \u3000\u3000\u800c\u4e14\uff0c\u4e89\u9010\u6324\u9003\u7684\u5ba2\u4eba\uff0c\u4e5f\u5f88\u5feb\u7684\u9759\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u90a3\u662f\u56e0\u4e3a\u6d1b\u9633\u697c\u4e2d\u5949\u8336\u3001\u4f8d\u5ba2\u7684\u4eba\u5458\uff0c\u90fd\u9759\u9759\u7684\u7ad9\u5728\u90a3\u91cc\u3002\u8138\u4e0a\u662f\u4e00\u7247\u4eb2\u5207\u7684\u7b11\u5bb9\uff0c\u4ed6\u4eec\u4e0d\u963b\u6b62\u89c2\u4f17\u5954\u8d70\uff0c\u53ea\u662f\u5fae\u7b11\u7740\u5411\u5ba2\u4eba\u9894\u9996\u3001\u62db\u547c\uff0c\u4ed6\u4eec\u7684\u6c89\u7740\u3001\u7a33\u5065\uff0c\u4f7f\u614c\u6025\u7684\u5ba2\u4eba\u60c5\u7eea\uff0c\u9010\u6e10\u7684\u6062\u590d\u4e86\u6b63\u5e38\u2026\u2026\u3002 \u3000\u3000\u8fd9\u54ea\u91cc\u50cf\u5949\u4f8d\u5ba2\u4eba\u7684\u7537\u4ec6\u3001\u5973\u4f8d\uff0c\u7b80\u76f4\u662f\u4e00\u6279\u8bad\u7ec3\u6709\u7d20\u7684\u5e74\u8f7b\u9ad8\u624b\u2026\u2026\u3002 \u3000\u3000\u8001\u53eb\u5316\u5fc3\u5934\u9707\u9a87\u6781\u4e86\uff0c\u6253\u6d88\u505c\u4e0b\u6765\u4ed4\u7ec6\u89c2\u5bdf\u7684\u5ff5\u5934\u3002\u4f4e\u58f0\u5bf9\u4e03\u5b9d\u548c\u5c1a\u9053\uff1a\u201c\u901a\u77e5\u6bb5\u4e5d\u3001\u80e1\u840d\uff0c\u54b1\u4eec\u5728\u5ba2\u6808\u4e2d\u78b0\u5934\u3002\u201d \u3000\u3000\u90a3\u662f\u4e00\u5bb6\u5f88\u5c0f\u7684\u5ba2\u6808\uff0c\u50fb\u5904\u5728\u4e00\u6761\u5c0f\u5df7\u4e4b\u4e2d\uff0c\u540e\u9662\u7d27\u9760\u57ce\u5899\uff0c\u56db\u4e2a\u4eba\u5305\u4e0b\u4e86\u540e\u9662\u91cc\u56db\u95f4\u5ba2\u623f\u3002 \u3000\u3000\u8001\u53eb\u5316\u8981\u5e97\u4f19\u8ba1\u5907\u4e86\u9152\u83dc\uff0c\u56db\u4e2a\u4eba\u56f4\u5750\u5c0f\u914c\u3002 \u3000\u3000\u56db\u4e2a\u4eba\u7684\u795e\u60c5\u90fd\u5f88\u4e25\u8083\uff0c\u663e\u5f97\u5fc3\u4e8b\u91cd\u91cd\u3002 \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u5e72\u4e86\u7b2c\u4e09\u676f\u9152\uff0c\u624d\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4e09\u4f4d\u3002\u600e\u4e48\u90fd\u4e0d\u8bf4\u8bdd\u5440\uff1f\u201d \u3000\u3000\u8001\u53eb\u5316\u5401\u4e00\u53e3\u6c14\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u6211\u8d70\u4e86\u8fd1\u4e94\u5341\u5e74\u7684\u6c5f\u6e56\uff0c\u7ecf\u5386\u8fc7\u4e0d\u5c11\u7684\u5927\u98ce\u5927\u6d6a\uff0c\u4e5f\u89c1\u8bc6\u8fc7\u5c11\u6797\u3002\u6b66\u5f53\u5927\u95e8\u5927\u6d3e\u7684\u67b6\u52bf\u3002\u4f46\u90a3\u53ea\u80fd\u8868\u73b0\u51fa\u4e00\u79cd\u5a01\u6b66\u6c14\u6982\uff0c\u7ed9\u4eba\u7684\u5370\u8c61\u662f\u4e25\u8083\u3001\u58ee\u5927\uff0c\u4f46\u6d1b\u9633\u5c45\u4e2d\u90a3\u4e9b\u4ec6\u5f79\u3001\u4f8d\u5973\uff0c\u5374\u8ba9\u8001\u53eb\u5316\u5fc3\u4e2d\u751f\u51fa\u754f\u60e7\u7684\u611f\u89c9\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u9519\u3002\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u9053\uff1a\u201c\u4ed6\u4eec\u90a3\u4efd\u6c89\u7740\u3001\u7ec3\u8fbe\u3002\u4f3c\u662f\u7ecf\u8fc7\u4e86\u5343\u9524\u767e\u7ec3\uff0c\u4e2a\u4e2a\u90fd\u80fd\u5904\u53d8\u4e0d\u60ca\uff0c\u6ee1\u8138\u7b11\u5bb9\u7684\u7ad9\u5728\u6df7\u4e71\u7684\u4eba\u7fa4\u4e2d\uff0c\u50cf\u662f\u4e00\u6839\u6839\u7684\u7d27\u7262\u67f1\u77f3\uff0c\u7a33\u7a33\u7684\u6491\u4f4f\u4e86\u6d1b\u9633\u697c\uff0c\u2026\u2026\u201d \u3000\u3000\u76ee\u5149\u4e00\u60ca\u6bb5\u4e5d\u3001\u80e1\u840d\uff0c\u63a5\u9053\uff1a\u201c\u4e24\u4f4d\u90fd\u662f\u4e00\u95e8\u7684\u9996\u8111\u4eba\u7269\uff0c\u4f60\u4eec\u7684\u770b\u6cd5\u5982\u4f55\u5462\uff1f\u201d \u3000\u3000\u80e1\u840d\u6447\u6447\u5934\u9053\uff1a\u201c\u5317\u5251\u95e8\u4e2d\u5f1f\u5b50\uff0c\u7edd\u6ca1\u6709\u90a3\u4efd\u5b9a\u529b\uff0c\u80e1\u67d0\u4e5f\u65e0\u6cd5\u628a\u4ed6\u4eec\u8bad\u7ec3\u6210\u90a3\u79cd\u9762\u5bf9\u6df7\u4e71\uff0c\u4ecd\u80fd\u5fae\u7b11\u5982\u5e38\u7684\u6c14\u5ea6\u3002\u201d \u3000\u3000\u201c\u6211\u4e5f\u4e0d\u80fd\u2026\u2026\u201d\u6bb5\u4e5d\u8bf4\uff1a\u201c\u5357\u5200\u95e8\u7684\u5f1f\u5b50\uff0c\u53ef\u4ee5\u64cd\u5200\u62d2\u654c\u3002\u8840\u6d41\u4e94\u6b65\uff0c\u4f46\u4ed6\u4eec\u6ca1\u6709\u90a3\u4efd\u89c6\u6df7\u4e71\u82e5\u65e0\u7779\u7684\u4fee\u517b\u3002\u4e8b\u5b9e\u4e0a\u6bb5\u67d0\u4eba\u4e5f\u4e0d\u77e5\u9053\u7528\u4ec0\u4e48\u65b9\u6cd5\uff0c\u624d\u80fd\u628a\u4ed6\u4eec\u8bad\u7ec3\u51fa\u90a3\u4efd\u6c89\u6f5c\u7684\u773c\u795e\uff0c\u5728\u4eba\u7fa4\u4e2d\u641c\u5bfb\uff0c\u6211\u60f3\u4e0d\u80fd\u8ba9\u4ed6\u4eec\u6000\u7591\u5230\u6211\u4eec\uff0c\u6240\u4ee5\u5306\u5306\u79bb\u5f00\u3002\u201d \u3000\u3000\u201c\u8fd9\u6d1b\u9633\u5c45\u7edd\u5bf9\u53ef\u7591\uff0c\u4f46\u4ed6\u4eec\u9632\u5907\u5982\u94c1\u6876\u4e00\u822c\u3002\u5b9e\u5728\u5f88\u96be\u67e5\u51fa\u4ec0\u4e48\u2026\u2026\u201d\u80e1\u840d\u8bf4\uff1a\u201c\u9664\u54b1\u4eec\u51c6\u5907\u653e\u5f00\u624b\u95f9\u4ed6\u4e00\u573a\u3002\u201d \u3000\u3000\u201c\u5bf9\uff01\u597d\u6b79\u4e5f\u8be5\u63ed\u4e0b\u4ed6\u4eec\u4e00\u5c42\u795e\u79d8\u5916\u8863\u2026\u2026\u201d\u6bb5\u4e5d\u8bf4\uff1a\u201c\u5426\u5219\u3002\u2026\u2026\u201d \u3000\u3000\u201c\u63ed\u5f97\u5f00\u5417\uff1f\u5c31\u7b97\u4e0d\u60dc\u653e\u624b\u6253\u4e00\u67b6\uff0c\u4e5f\u5f88\u96be\u6709\u4ec0\u4e48\u6536\u83b7\uff1f\u5982\u82e5\u8001\u53eb\u5316\u7684\u63a8\u60f3\u4e0d\u9519\uff0c\u54b1\u4eec\u9762\u5bf9\u7684\u654c\u624b\u3002\u4e5f\u5c31\u662f\u90a3\u4e9b\u7537\u4ec6\u3001\u5973\u4f8d\u2026\u2026\u201d \u3000\u3000\u201c\u6253\u4e86\u5c0f\u7684\uff0c\u8fd8\u6015\u8001\u7684\u4e0d\u51fa\u9762\u5417\uff1f\u2026\u2026\u201d\u6bb5\u4e5d\u8bf4\uff1a\u201c\u5c31\u7b97\u4ed6\u4eec\u7684\u4e3b\u8111\u4eba\u7269\u4e0d\u9732\u9762\u54b1\u4eec\u4e5f\u53ef\u4ee5\u501f\u673a\u4f1a\u6293\u4ed6\u4eec\u4e00\u4e2a\u51fa\u6765\uff0c\u95ee\u660e\u5185\u618e\uff0c\u628a\u5b83\u516c\u8bf8\u6b66\u6797\uff0c\u4f7f\u6b66\u6797\u540c\u9053\u8054\u5408\u8d77\u6765\u3002\u5171\u540c\u5bf9\u4ed8\u5f3a\u654c\uff0c\u54b1\u4eec\u56db\u4e2a\u4eba\u9910\u98ce\u9732\u5bbf\uff0c\u9690\u5f62\u6539\u626e\uff0c\u5fcd\u6c14\u541e\u58f0\u3002\u4e0d\u5c31\u662f\u4e3a\u7684\u8fd9\u4e2a\u76ee\u7684\u5417\uff1f\u201d \u3000\u3000\u201c\u6bb5\u8001\u5f1f\u2026\u2026\u201d\u94b1\u7f3a\u559d\u5e72\u4e86\u9762\u524d\u4e00\u676f\u9152\uff0c\u9053\uff1a\u201c\u5982\u662f\u4ed6\u4e0d\u80af\u62db\u51fa\u6765\u5462\uff1f\u2026\u2026\u201d \u3000\u3000\u201c\u6c5f\u6e56\u4e0a\u903c\u4f9b\u7684\u624b\u6cd5\uff0c\u867d\u4e0d\u8db3\u53d6\uff0c\u4f46\u4e8b\u6025\u4ece\u6743\u3002\u6211\u76f8\u4fe1\u4ed6\u65e0\u6cd5\u5fcd\u53d7\u90a3\u4e9b\u5206\u7b4b\u9519\u9aa8\u7684\u75db\u82e6\u2026\u2026\u201d \u3000\u3000\u201c\u4ed6\u4eec\u4e0d\u53ef\u80fd\u77e5\u9053\u771f\u6b63\u7684\u673a\u5bc6\uff0c\u5c31\u7b97\u4f60\u80fd\u903c\u95ee\u51fa\u53e3\u4f9b\uff0c\u4e5f\u4e00\u6837\u65e0\u6cd5\u8bc1\u660e\u4ec0\u4e48\uff1f\u2026\u2026\u201d\u94b1\u7f3a\u8bed\u91cd\u5fc3\u957f\u7684\u8bf4\uff1a\u201c\u4f55\u51b5\uff0c\u6240\u8c13\u6b66\u6797\u540c\u9053\uff0c\u5927\u6982\u662f\u6c5f\u6e56\u4e0a\u5404\u5927\u95e8\u6d3e\uff0c\u53ef\u662f\u5c11\u6797\u3001\u6b66\u5f53\u4e24\u5927\u4e3b\u8109\uff0c\u5df2\u5ba3\u5e03\u95ed\u5173\u81ea\u5b88\uff0c\u4e0d\u518d\u95ee\u6c5f\u6e56\u4e2d\u4e8b\uff0c\u957f\u5b89\u5927\u8c6a\u79e6\u516b\u516c\uff0c\u6258\u75c5\u8c22\u5ba2\uff0c\u5df2\u7ecf\u4e0d\u80af\u518d\u548c\u6c5f\u6e56\u670b\u53cb\u89c1\u9762\uff0c\u6bb5\u8001\u5f1f\u4e0d\u59a8\u60f3\u60f3\u770b\uff0c\u4f60\u662f\u5357\u5200\u95e8\u7684\u638c\u95e8\u4eba\uff0c\u4f60\u73b0\u5728\u8fde\u5357\u5200\u95e8\u4e2d\u7684\u5f1f\u4e8e\u3002\u4e5f\u6ca1\u6709\u4fe1\u5fc3\u8c03\u52a8\uff0c\u2026\u2026\u201d \u3000\u3000\u6bb5\u4e5d\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u8fd9\u4e2a\u2026\u2026\u3002\u201d \u3000\u3000\u201c\u94b1\u5144\u8bf4\u7684\u4e0d\u9519\uff0c\u6211\u4eec\u76ee\u4e0b\uff0c\u5df2\u65e0\u5916\u63f4\uff0c\u4e00\u5207\u90fd\u8981\u9760\u81ea\u5df1\u7684\u529b\u91cf\u884c\u52a8\u4e86\u2026\u2026\u201d\u80e1\u840d\u8bf4\uff1a \u3000\u3000\u201c\u5176\u5b9e\uff0c\u8fd9\u4e9b\u3002\u6211\u4eec\u65e9\u5c31\u60f3\u5230\u4e86\uff0c\u53ea\u4e0d\u8fc7\uff0c\u611f\u53d7\u4e0a\u8fd8\u65e0\u6cd5\u5b8c\u5168\u9002\u5e94\u3002\u201d \u3000\u3000\u6bb5\u4e5d\u53f9\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u662f\u554a\uff01\u5ffd\u7136\u95f4\uff0c\u5b8c\u5168\u7684\u5b64\u7acb\u4e8e\u6c5f\u6e56\u4e4b\u4e0a\u3002\u653e\u773c\u5927\u6c5f\u5357\u5317\uff0c\u53ea\u6709\u6211\u4eec\u56db\u4e2a\u4eba\uff0c\u2026\u2026\u201d \u3000\u3000\u201c\u90a3\u5012\u4e0d\u4f1a\uff0c\u6211\u548c\u5c1a\u76f8\u4fe1\u6709\u5f88\u591a\u4eba\u548c\u6211\u4eec\u4e00\u6837\u3002\u5728\u6697\u4e2d\u6478\u7d22\u654c\u60c5\uff0c\u627e\u5bfb\u5fd7\u540c\u9053\u5408\u7684\u670b\u53cb\uff0c\u5c31\u50cf\u8001\u53eb\u5316\u627e\u5230\u4e86\u548c\u5c1a\uff0c\u4e00\u53d8\u4e3a\u4e8c\uff0c\u6211\u4eec\u53c8\u9047\u4e0a\u4e86\u4e24\u4f4d\uff0c\u5408\u800c\u4e3a\u56db\uff0c\u4e5f\u8bb8\uff0c\u54b1\u4eec\u5f88\u5feb\u5c31\u7ed3\u5408\u4e86\u4e00\u6279\u5fd7\u540c\u9053\u5408\u7684\u4eba\u2026\u2026\u201d \u3000\u3000\u80e1\u840d\u7a81\u7136\u63a5\u9053\uff1a\u201c\u5bf9\uff01\u90a3\u4f4d\u6697\u4e2d\u4f20\u8bdd\u793a\u8b66\u7684\u4eba\u3002\u53ef\u80fd\u662f\u4e00\u4e2a\u65b0\u7684\u670b\u53cb\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u9519\u3002\u201d\u6bb5\u4e5d\u63a5\u9053\uff1a\u201c\u6211\u4eec\u4e0d\u80fd\u5fd8\u4e86\u705e\u6865\u4e4b\u95f4\u98ce\u96ea\u5c0f\u9986\u4e4b\u7ea6\u2026\u2026\u3002\u201d\u705e\u6865\u61c2\u8de8\u705e\u6c34\u4e24\u5cb8\uff0c\u73af\u6709\u4e03\u5341\u4e8c\u5b54\uff0c\u98ce\u666f\u5982\u753b\uff0c\u4ea6\u662f\u5510\u4eba\u9001\u522b\u65f6\uff0e\u6298\u67f3\u5531\u9633\u5173\u4e09\u53e0\u4e4b\u5904\u3002 \u3000\u3000\u679c\u7136\u5728\u705e\u6865\u4e4b\u4fa7\uff0c\u6025\u52b2\u7684\u897f\u5317\u98ce\u4e2d\uff0c\u98d8\u626c\u7740\u4e00\u65b9\u201c\u98ce\u96ea\u5c0f\u9986\u201d\u7684\u9152\u62db\u3002 \u3000\u3000\u80e1\u840d\u5f53\u5148\u800c\u884c\uff0c\u63a8\u5f00\u6728\u95e8\u3002 \u3000\u3000\u53ea\u89c1\u5385\u4e2d\u6446\u7740\u56db\u5f20\u5c0f\u684c\u5b50\uff0c\u516b\u5f20\u7af9\u6905\u3002\u679c\u7136\u662f\u540d\u7b26\u5176\u5b9e\u7684\u5c0f\u9986\u3002 \u3000\u3000\u6bb5\u4e5d\u8f7b\u8f7b\u54b3\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6709\u4eba\u5417\uff1f\u201d \u3000\u3000\u4e00\u4e2a\u8eab\u7740\u9752\u8863\u7684\u534a\u8001\u5f90\u5a18\uff0c\u7531\u4e00\u5ea7\u5f71\u58c1\u540e\u9762\u8f6c\u51fa\u6765\u3001\u4f46\u5374\u4e00\u8bed\u4e0b\u53d1\u3002\u5706\u7741\u7740\u4e00\u5bf9\u773c\u775b\u3002 \u3000\u3000\u4e0d\u505c\u7684\u5728\u56db\u4eba\u8eab\u4e0a\u6253\u91cf\u3002 \u3000\u3000\u201c\u77a7\u4ec0\u4e48\uff1f\u6211\u4eec\u8138\u4e0a\u53ef\u6ca1\u6709\u96d5\u82b1\u523b\u5b57\uff0c\u2026\u2026\u201d\u8001\u53eb\u5316\u8bf4\uff1a\u201c\u54b1\u4eec\u662f\u6765\u5403\u676f\u9152\u9010\u9010\u5bd2\u610f\u3002\u201d \u3000\u3000\u9752\u8863\u5987\u4eba\u9053\uff1a\u201c\u56db\u4f4d\u8bf7\u7a0d\u5019\u4e00\u4e0b\u2026\u2026\u201d\u7a81\u7136\u884c\u51fa\u5ba4\u5916\uff0e\u53d6\u4e0b\u9152\u62db\uff0c\u56de\u5934\u53c8\u628a\u6728\u95e8\u62f4\u4e0a\uff0c\u63a5\u9053\uff1a\u201c\u56db\u4f4d\u8bf7\u628a\u4f2a\u88c5\u9664\u4e0b\u5982\u4f55\uff1f\u201d \u3000\u3000\u4e00\u773c\u5c31\u770b\u7a7f\u4e86\u56db\u4eba\u4e0d\u662f\u672c\u6765\u9762\u76ee\u3002 \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u9053\uff1a\u201c\u597d\u5389\u5bb3\u7684\u773c\u775b\uff0c\u2026\u2026\u201d\u53d6\u4e0b\u5047\u53d1\uff0c\u9732\u51fa\u5149\u5934\u3002 \u3000\u3000\u201c\u548c\u5c1a\uff0c\u2026\u2026\u201d\u9752\u8863\u5987\u4eba\u9053\uff1a\u201c\u53eb\u5316\u5b50\u3001\u5357\u5200\u6bb5\u4e5d\u3001\u5317\u5251\u80e1\u840d\uff0c\u4f60\u4eec\u8fd9\u4e9b\u6613\u5bb9\u6539\u5986\u7684\u6280\u672f\u592a\u5dee\u4e86\u3002\u53ea\u8981\u7a0d\u5fae\u7ec6\u5fc3\u4e00\u70b9\u7684\u4eba\u3002 \u3000\u3000\u5c31\u53ef\u4ee5\u77a7\u51fa\u7834\u7efd\u2026\u2026\u3002\u201d \u3000\u3000\u6bb5\u4e5d\u4e00\u64a9\u957f\u888d\uff0c\u5200\u5df2\u5728\u624b\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u592b\u4eba\u77e5\u9053\u7684\u592a\u591a\u4e86\u3002 \u3000\u3000\u9700\u77e5\u6000\u58c1\u5176\u7f6a\uff0c\u624d\u9732\u4f24\u5bff\u2026\u2026\u201d \u3000\u3000\u9752\u8863\u5f90\u5a18\u6253\u65ad\u4e86\u6bb5\u4e5d\u7684\u8bdd\uff0c\u9053\uff1a\u201c\u5662\uff01\u60f3\u6740\u6211\u706d\u53e3\uff0c\u4e5f\u8be5\u7b49\u6211\u66ff\u4f60\u6cbb\u597d\u4e86\u9152\u83dc\uff0c\u518d\u52a8\u624b\u4e0d\u8fdf\u3002\u201d \u3000\u3000\u56db\u4e2a\u4eba\u90fd\u4e3a\u9752\u8863\u5f90\u5a18\u7684\u5947\u7a81\u4e3e\u6b62\u6240\u52a8\u3002\u6b64\u65f6\u6b64\u60c5\uff0c\u8eab\u4efd\u7edd\u5bf9\u4e0d\u80fd\u6cc4\u9732\uff0c\u6740\u4eba\u706d\u53e3\uff0c\u4e5f\u662f\u5728\u6240\u4e0d\u60dc\u4e86\uff0c\u56db\u4e2a\u4eba\u4e00\u822c\u5fc3\u610f\u3002\u6697\u4e2d\u5df2\u5e03\u6210\u5305\u56f4\u4e4b\u52bf\u3002 \u3000\u3000\u201c\u4e0d\u9519\uff0c\u54b1\u4eec\u662f\u8d74\u7ea6\u800c\u6765\uff0c\u90a3\u4f4d\u2026\u2026\u201d\u8001\u53eb\u5316\u5446\u4e86\u4e00\u5446\uff0c\u63a5\u4e0d\u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u90a3\u4eba\u662f\u8001\u662f\u5c11\u3002\u662f\u9ad8\u662f\u77ee\u3002\u56db\u4e2a\u4eba\u5168\u65e0\u5370\u8c61\uff0c\u8fde\u4eba\u90fd\u6ca1\u6709\u89c1\u8fc7\uff0c\u5982\u4f55\u53eb\u5f97\u51fa\u540d\u5b57\uff1f\u5e78\u597d\u9752\u8863\u5f90\u5a18\u63a5\u4e86\u53e3\uff0c\u9053\uff1a\u201c\u90a3\u4f4d\u606d\u5019\u591a\u65f6\u4e86\uff0c\u56db\u4f4d\u8ddf\u6211\u6765\u5427\uff01\u201d \u3000\u3000\u7ed5\u8fc7\u5f71\u58c1\u3002\u662f\u4e00\u6761\u72ed\u7a84\u7684\u5c0f\u9053\u3002\u4ec5\u53ef\u5bb9\u4e00\u4eba\u901a\u8fc7\u3002\u9752\u8863\u5987\u4eba\u6253\u5f00\u5899\u89d2\u4e00\u6247\u6697\u95e8\uff0c\u7784\u4e86\u56db\u4eba\u4e00\u773c\uff0c\u9053\uff1a\u201c\u8bf7\u554a\uff01\u201d \u3000\u3000\u94b1\u7f3a\u5fae\u5fae\u4e00\u7b11\uff0c\u5f53\u5148\u8fdb\u5165\u6697\u95e8\u3002 \u3000\u3000\u90a3\u662f\u9752\u7816\u780c\u6210\u7684\u4e00\u6761\u5730\u9053\u3002\u659c\u5411\u4e0b\u9762\u901a\u53bb\u3002 \u3000\u3000\u9752\u7816\u9636\u68af\uff0c\u5341\u4e8c\u7ea7\u8f6c\u4e00\u4e2a\u5f2f\uff0c\u4e09\u6b21\u6298\u8f6c\uff0c\u666f\u7269\u4e00\u53d8\u3002 \u3000\u3000\u53ea\u89c1\u4e00\u5ea7\u5bbd\u655e\u7684\u5385\u5802\u4e0a\uff0c\u65e9\u6446\u4e86\u4e00\u5f20\u65b9\u684c\uff0c\u4e24\u76cf\u540a\u5728\u5c4b\u9876\u4e0a\u7684\u7cbe\u81f4\u5bab\u706f\u3002\u65e9\u5df2\u71c3\u8d77\u3002\u7167\u5f97\u4e00\u5ba4\u901a\u660e\u3002 \u3000\u3000\u66f2\u5f84\u901a\u5e7d\uff0c\u98ce\u96ea\u5c0f\u9986\u4e4b\u4e0b\uff0c\u7adf\u6709\u8fd9\u4e48\u4e00\u5904\u9690\u5bc6\u3001\u8c6a\u534e\u6240\u5728\u3002 \u3000\u3000\u8001\u53eb\u5316\u89c1\u591a\u8bc6\u5e7f\uff0c\u56db\u4e0b\u6253\u91cf\u4e00\u773c\uff0c\u5fc3\u4e2d\u5fd6\u9053\uff1a\u201c\u957f\u5b89\u5468\u56f4\u3002 \u3000\u3000\u591a\u5c45\u7a91\u6d1e\uff0c\u4ea6\u6709\u5730\u7a96\u4e4b\u8bbe\uff0c\u4f46\u8fd9\u5ea7\u6d1e\u5e9c\uff0c\u7b51\u5efa\u7cbe\u81f4\uff0c\u705e\u6865\u4e4b\u4fa7\uff0c\u5c4a\u6c11\u4e0d\u591a\uff0c\u5982\u6b64\u8c6a\u534e\u7684\u5730\u4e0b\u7a91\u6d1e\uff0c\u5f53\u662f\u524d\u4eba\u6240\u5efa\uff0c\u4e0a\u4ee5\u98ce\u96ea\u5c0f\u9986\u8986\u906e\uff0c\u5012\u662f\u5341\u5206\u9690\u5bc6\u7684\u6240\u5728\u3002\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u90a3\u4e2d\u5e74\u5987\u4eba\u8d64\u6b65\u5165\u5385\u4e2d\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u91cc\u51ac\u6696\u590f\u51c9\uff0c\u4e0d\u6015\u58f0\u8fbe\u5ba4\u5916\uff0c\u8bf8\u4f4d\u53ef\u4ee5\u5bbd\u8863\u843d\u5750\uff0c\u7545\u6240\u6b32\u8a00\u3002\u201d \u3000\u3000\u4e0d\u9519\uff0c\u94b1\u7f3a\u3001\u6bb5\u4e5d\u7b49\u3002\u90fd\u5df2\u6709\u70ed\u7684\u611f\u89c9\u3002 \u3000\u3000\u80e1\u840d\u56db\u987e\u4e86\u4e00\u773c\u3002\u9053\uff1a\u201c\u90a3\u4f4d\u7ea6\u6211\u4eec\u7684\u670b\u53cb\u5462\uff1f\u2026\u2026\u201d \u3000\u3000\u201c\u6025\u4ec0\u4e48\uff1f\u59d1\u5a18\u6211\u8fd8\u6ca1\u53bb\u70e7\u83dc\u2026\u2026\u201d \u3000\u3000\u5979\u5c31\u7b97\u6ca1\u6709\u56db\u5341\uff0c\u4e5f\u6709\u4e09\u5341\u4e94\u516d\u5c81\u4e86\u3002\u8fd9\u6837\u7684\u5e74\u9f84\u3002\u81ea\u79f0\u59d1\u5a18\uff0c\u8001\u53eb\u5316\u7b49\u90fd\u542c\u5f97\u5fcd\u4e0d\u4f4f\u5fae\u5fae\u4e00\u7b11\u3002 \u3000\u3000\u201c\u7b11\u4ec0\u4e48\uff1f\u5973\u4eba\u53ea\u8981\u6ca1\u6709\u5ac1\u4eba\uff0c\u90fd\u8be5\u79f0\u4e4b\u59d1\u5a18\u3002\u518d\u8bf4\u3002\u6bd4\u8d77\u56db\u4f4d\uff0c\u6211\u53ef\u662f\u5e74\u8f7b\u591a\u4e86\uff1f\u201d \u3000\u3000\u201c\u8bf4\u7684\u662f\u554a\uff01\u8001\u5219\u5316\u4eca\u5e74\u516d\u5341\u6709\u516b\uff0c\u6bd4\u8d77\u4f60\u59d1\u5a18\uff0c\u90a3\u53ef\u662f\u5927\u5f97\u591a\u4e86\uff1f\u201d \u3000\u3000\u9752\u8863\u5987\u4eba\u4e5f\u5fcd\u4e0d\u4f4f\u4e00\u7b11\uff0c\u9f7f\u5982\u7f16\u8d1d\u3002\u68a8\u6da1\u6df1\u9677\uff0c\u5f90\u5a18\u98ce\u97f5\uff0c\u53e6\u6709\u4e00\u756a\u64a9\u4eba\u98ce\u60c5\u3002 \u3000\u3000\u516d\u5b9d\u548c\u5c1a\u91cd\u91cd\u54b3\u4e86\u4e00\u58f0\u3001\u9053\uff1a\u201c\u5927\u59d1\u5a18\uff0c\u8bf7\u6559\u82b3\u540d\u554a\uff1f\u201d \u3000\u3000\u9752\u8863\u5987\u4eba\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u8fd9\u9152\u8089\u4e0d\u6212\u7684\u91ce\u548c\u5c1a\uff0c\u8fd8\u6709\u6574\u5929\u6df7\u5403\u6df7\u559d\u7684\u8001\u53eb\u5316\u4e8e\uff0c\u4f60\u4eec\u4e24\u4e2a\u4eba\uff0c\u90fd\u81ea\u8be9\u662f\u89c1\u591a\u8bc6\u5e7f\u7684\u8001\u6c5f\u6e56\u4e86\uff0c\u4f46\u770b\u4e86\u8fd9\u4e48\u4e45\uff0c\u8fd8\u77a7\u4e0d\u51fa\u672c\u59d1\u5a18\u7684\u8eab\u4efd\u5417\uff1f\u201d \u3000\u3000\u8fd9\u4e00\u4e0b\uff0c\u8001\u53eb\u5316\u548c\u4e03\u5b9d\u548c\u5c1a\uff0c\u9762\u5b50\u6709\u70b9\u6302\u4e0d\u6ce8\u4e86\uff0c\u56db\u53ea\u773c\u775b\u3002\u76ef\u5728\u9752\u8863\u5987\u4eba\u8138\u4e0a\u770b\u3002 \u3000\u3000\u56e0\u4e3a\uff0c\u542c\u53e3\u6c14\uff0c\u90a3\u9752\u8863\u5987\u4eba\u5206\u660e\u662f\u8ba4\u8bc6\u4ed6\u4eec\uff0c\u628a\u4e24\u4e2a\u4eba\u7684\u8eab\u4efd\u3001\u7ef0\u53f7\u968f\u53e3\u53eb\u4e86\u51fa\u6765\uff0c\u4f46\u4e24\u4eba\u5374\u8ba4\u4e0d\u51fa\u5bf9\u65b9\u662f\u8c01\uff1f\u9752\u8863\u5987\u4eba\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4e0d\u7528\u6789\u8d39\u5fc3\u673a\u4e86\uff0c\u521a\u597d\u501f\u673a\u4f1a\u7ed9\u4f60\u4eec\u4e00\u70b9\u6559\u8bad\uff0c\u6613\u5bb9\u672f\u662f\u4e00\u79cd\u6280\u827a\uff0c\u4e5f\u662f\u4e00\u95e8\u5b66\u95ee\uff0c\u6de1\u6de1\u51e0\u7b14\uff0c\u5c31\u80fd\u628a\u4e00\u4e2a\u4eba\u7684\u9762\u578b\u3001\u80a4\u8272\u5b8c\u5168\u6539\u53d8\uff0c\u50cf\u4f60\u4eec\u8fd9\u79cd\u7528\u836f\u7269\u5728\u8138\u7cca\u4e86\u539a\u539a\u7684\u4e00\u5c42\uff0c\u90a3\u91cc\u8fd8\u50cf\u4e00\u5f20\u4eba\u8138\u3001\u522b\u8bf4\u662f\u9605\u5386\u4e30\u5bcc\u7684\u6c5f\u6e56\u4eba\u4e86\uff0c\u5c31\u662f\u7a0d\u4e3a\u80af\u7559\u5fc3\u7684\u4eba\uff0c\u90fd\u80fd\u77a7\u51fa\u6bdb\u75c5\u2026\u2026\u201d \u3000\u3000\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u59d1\u5a18\u8fd9\u5f20\u8138\uff0c\u4e5f\u7ecf\u8fc7\u6613\u5bb9\u4e86\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u795e\u60c5\u8083\u7136\u4ed6\u8bf4\uff1a\u201c\u6613\u82b1\u624b\u5f20\u56db\u59d1\u662f\u59d1\u70af\u7684\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\u554a\uff01\u770b\u6765\u4e03\u5b9d\u548c\u5c1a\u6bd4\u53eb\u5316\u5b50\u7a0d\u7a0d\u9ad8\u660e\u4e00\u4e9b\uff1f\u80fd\u60f3\u5230\u6613\u82b1\u624b\u5f20\u56db\u59d1\uff0c\u4e3a\u4ec0\u4e48\u8fd8\u4e0d\u6562\u80af\u5b9a\u6211\u7684\u8eab\u4efd\uff1f\u201d \u3000\u3000\u201c\u96be\u9053\u4f60\u5c31\u662f\u5f20\u56db\u59d1\u2026\u2026\u201d \u3000\u3000\u201c\u600e\u4e48\uff1f\u6211\u53ef\u662f\u957f\u7684\u4e0d\u50cf\u2026\u2026\u201d\u7a81\u7136\u4e3e\u624b\u5728\u8138\u4e0a\u8f7b\u8f7b\u62c2\u8fc7\uff0c\u4e00\u5f20\u8138\u7acb\u523b\u6709\u4e86\u53d8\u5316\uff0c\u4f3c\u662f\u7a81\u7136\u95f4\u8001\u4e86\u4e09\u5341\u5e74\uff0c\u8138\u4e0a\u76b1\u7eb9\u5806\u7d2f\uff0c\u63a5\u9053\uff1a\u201c\u8fd9\u6837\u5462\uff1f\u50cf\u4e0d\u4fbf\u6613\u82b1\u624b\u5f20\u56db\u59d1\uff1f\u201d \u3000\u3000\u201c\u5f53\u771f\u662f\u7a77\u6781\u53d8\u5316\u4e4b\u5999\uff0c\u6211\u548c\u5c1a\u4eca\u8fd9\u662f\u5927\u5f00\u773c\u754c\u4e86\u3002\u201d \u3000\u3000\u4f55\u81f3\u662f\u4e03\u5b9d\u548c\u5c1a\uff0c\u94b1\u7f3a\u3001\u80e1\u840d\u3001\u6bb5\u4e5d\uff0c\u5168\u770b\u5f97\u5446\u4f4f\u4e86\u3002 \u3000\u3000\u201c\u6c5f\u6e56\u4e0a\u4f20\u8bf4\uff0c\u4f60\u5df2\u6b7b\u53bb\u591a\u5e74\uff0c\u8001\u53eb\u5316\u8fd8\u53f9\u606f\u7edd\u6280\u5931\u4f20\u5462\uff01 \u3000\u3000\u4f46\u4f60\u7adf\u7136\u8fd8\u597d\u597d\u5730\u6d3b\u7740\u2026\u2026\u201d \u3000\u3000\u201c\u4f60\u8001\u53eb\u5316\u5b50\u80fd\u6d3b\u4e0b\u6765\uff0c\u6211\u600e\u4e48\u4f1a\u6b7b\uff1f\u4f55\u51b5\uff0c\u6211\u8fd8\u5e74\u8f7b\u5f97\u5f88\u554a\uff01\u201d\u53f3\u624b\u53c8\u5728\u8138\u4e0a\u62c2\u8fc7\uff0c\u7d2f\u7d2f\u76b1\u7eb9\u987f\u6d88\uff0c\u53c8\u6062\u590d\u4e00\u5f20\u59e3\u597d\u7684\u5bb9\u8c8c\uff0c\u53ea\u662f\u770b\u4e0a\u8f83\u521a\u624d\u66f4\u4e3a\u5e74\u8f7b\u4e00\u4e9b\u3002 \u3000\u3000\u201c\u795e\u54ea\uff01\u795e\u54ea\uff01\u795e\u6781\u4e86\u2026\u2026\u201d\u6bb5\u4e5d\u5fcd\u4e0d\u5f80\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u6bb5\u67d0\u542c\u8bf4\u8fc7\u6613\u82b1\u624b\u7684\u6613\u5bb9\u7edd\u6280\uff0c\u72ec\u6b65\u6c5f\u7322\uff0c\u524d\u65e0\u53e4\u4eba\uff0c\u4f46\u60f3\u4e0d\u5230\u7adf\u662f\u5982\u6b64\u7684\u795e\u5947\u6cd5\u3002\u201d \u3000\u3000\u201c\u8bf8\u4f4d\u89c1\u8bc6\u4e86\u5979\u7684\u6613\u5bb9\u624b\u6cd5\uff0c\u7b49\u4e00\u4e0b\u518d\u54c1\u5c1d\u5979\u7684\u70f9\u4efb\u624b\u827a\uff0c\u4fdd\u7ba1\u8bf8\u4f4d\u4f1a\u8d5e\u4e0d\u7edd\u53e3\u3002\u201d \u3000\u3000\u8f6c\u5934\u770b\u53bb\u3002\u4e0d\u77e5\u4f55\u65f6\u3002\u5385\u4e2d\u5df2\u591a\u4e86\u4e00\u4e2a\u84dd\u886b\u5c11\u5e74\u3002 \u3000\u3000\u4e00\u8eab\u53e4\u94dc\u8272\u7684\u76ae\u80a4\uff0c\u660e\u663e\u7684\u4e94\u5b98\uff0c\u9ad8\u7ec6\u7684\u8eab\u6750\uff0c\u6e05\u660e\u7684\u53cc\u76ee\uff0c\u5f62\u5982\u83f1\u89d2\u7684\u53cc\u5507\uff0c\u7ed9\u4eba\u4e00\u79cd\u575a\u6bc5\u3001\u51b7\u9759\u3001\u53c8\u5584\u673a\u53d8\u7684\u611f\u89c9\u3002 \u3000\u3000\u80e1\u840d\u4e00\u62b1\u62f3\uff0c\u9053\uff1a\u201c\u5728\u4e0b\u80e1\u840d\uff0c\u6d1b\u9633\u5c45\u4e2d\u591a\u627f\u4f20\u8a00\u6307\u70b9\uff0c\u4f7f\u6211\u4eec\u80fd\u7f6e\u8eab\u4e8b\u5916\u2026\u2026\u201d \u3000\u3000\u201c\u4e5f\u5e78\u597d\u8bf8\u4f4d\u80fd\u60ac\u5d16\u52d2\u9a6c\uff0c\u624d\u672a\u88ab\u5377\u5165\u6f29\u6da1\uff0c\u56db\u4f4d\u7684\u8eab\u4efd\uff0c\u867d\u672a\u88ab\u4ed6\u4eec\u4e86\u89e3\uff0c\u4f46\u56db\u4f4d\u6613\u541d\u51fa\u5165\u6d1b\u9633\u5c45\u7684\u4e8b\uff0c\u65e9\u5df2\u5728\u4ed6\u76d1\u89c6\u4e4b\u4e0b\u2026\u2026\u201d \u3000\u3000\u6bb5\u4e5d\u63a5\u9053\uff1a\u201c\u8fd9\u4e00\u70b9\u6bb5\u67d0\u4eba\u4e0d\u6562\u82df\u540c\u3001\u90a3\u4e9b\u7537\u4ec6\u3001\u5973\u4f8d\u867d\u7136\u7cbe\u660e\uff0c\u4f46\u6bb5\u67d0\u4eba\u80af\u5b9a\u7684\u8bf4\u4e00\u53e5\uff0c\u4ed6\u4eec\u7edd\u6ca1\u6709\u5728\u6697\u4e2d\u76d1\u89c6\u6211\u4eec\u3002\u201d \u3000\u3000\u84dd\u886b\u5ba2\u6de1\u6de1\u4e00\u7b11\u3001\u9053\uff1a\u201c\u4ed6\u4eec\u53ea\u662f\u5949\u8336\u7aef\u6c34\u7684\u4f8d\u5e94\u3001\u4e0d\u8d1f\u76d1\u89c6\u53ef\u7591\u4eba\u7269\u8d23\u4efb\uff0c\u56db\u4f4d\u662f\u6d1b\u9633\u5c45\u5217\u5165\u5927\u654c\u7684\u4eba\u7269\uff0c\u6240\u4ee5\uff0c\u76d1\u89c6\u56db\u4f4d\u7684\u4e3e\u6b62\uff0c\u4e5f\u5341\u5206\u5c0f\u5fc3\uff0c\u4ed6\u4eec\u4e5f\u626e\u4f5c\u5ba2\u4eba\u7684\u8eab\u4efd\uff0c\u5c31\u5750\u5728\u8bf8\u4f4d\u8eab\u4fa7\u2026\u2026\u201d \u3000\u3000\u201c\u8fd9\u771f\u662f\u9632\u4e0b\u80dc\u9632\u4e86\u2026\u2026\u201d\u8001\u53eb\u5316\u9053\uff1a\u201c\u6d1b\u9633\u5c45\u4e2d\u3002\u7a76\u7adf\u6709\u591a\u5c11\u4e13\u53f8\u5176\u8d23\u7684\u4eba\u624b\u5462\uff1f\u201d \u3000\u3000\u84dd\u886b\u4eba\u9053\uff1a\u201c\u7ea6\u56db\u4f4d\u524d\u8f88\u5230\u6b64\uff0c\u665a\u8f88\u5df2\u51c6\u5907\u628a\u6d1b\u9633\u5c45\u4e2d\u7684\u4e8b\uff0c\u5c3d\u91cf\u7684\u8bf4\u51fa\u6765\uff0c\u56db\u4f4d\u524d\u8f88\u5148\u8bf7\u5165\u5ea7\u3002\u201d \u3000\u3000\u56db\u4eba\u5750\u4e86\u4e1c\u3001\u897f\u5ba2\u4f4d\uff0c\u84dd\u886b\u4eba\u5750\u4e86\u5317\u65b9\u7684\u4e3b\u4f4d\uff0c\u5357\u9762\u7a7a\u4e86\u51fa\u6765\uff0c\u60f3\u662f\u7559\u7ed9\u6613\u82b1\u624b\u5f20\u56db\u59d1\u7684\u5750\u4f4d\u3002 \u3000\u3000\u201c\u4e13\u53f8\u76d1\u89c6\u7684\u4eba\u624b\u4e0b\u591a\uff0c\u5c31\u665a\u8f88\u6240\u77e5\uff0c\u5927\u6982\u53ea\u6709\u4e8c\u5341\u56db\u4e2a\u3002 \u3000\u3000\u4ed6\u4eec\u5206\u6210\u516d\u73ed\uff0c\u6bcf\u73ed\u56db\u4eba\uff0c\u5728\u8d4c\u573a\u3001\u6b4c\u53f0\u4e2d\u5de1\u884c\uff0c\u5f53\u7136\uff0c\u5982\u82e5\u53d1\u73b0\u4e86\u53ef\u7591\u4eba\u7269\uff0c\u90a3\u5c31\u53e6\u8c03\u4e13\u4eba\u6697\u4e2d\u76ef\u4f4f\uff0c\u50cf\u56db\u4f4d\u8fd9\u6837\u7684\u5927\u4eba\u7269\u3002 \u3000\u3000\u5c31\u662f\u6d3e\u4e13\u4eba\u76ef\u7a0d\u4e86\u3002\u201d \u3000\u3000\u201c\u8001\u53eb\u5316\u81ea\u4fe1\u6211\u4eec\u7684\u4e3e\u52a8\u5341\u5206\u5c0f\u5fc3\u3002\u5982\u5c65\u8584\u51b0\u3002\u5982\u4e34\u6df1\u6e0a\u3002 \u3000\u3000\u7a76\u7adf\u54ea\u91cc\u9732\u51fa\u4e86\u7834\u7efd\u5462\uff1f\u201d\u201c\u6613\u5bb9\u672f\u2026\u2026\u201d\u84dd\u886b\u4eba\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u8bf8\u4f4d\u7684\u4e3e\u6b62\uff0c\u786e\u5b9e\u5c0f\u5fc3\u8c28\u614e\uff0c\u4f46\u8138\u4e0a\u6d82\u4e86\u4e00\u5c42\u5f88\u539a\u7684\u836f\u7269\u3002\u53d8\u6210\u4e86\u4e00\u5f20\u6b7b\u677f\u677f\u7684\u9762\u5b54\uff0c\u548c\u56db\u4f4d\u7075\u6d3b\u7684\u773c\u795e\uff0c\u5b8c\u5168\u4e0d\u80fd\u914d\u5408\uff0c\u770b\u4e0a\u53bb\u662f\u4e0d\u662f\u5f88\u602a\u5f02\u5462\uff1f\u6d1b\u9633\u5c45\u4e2d\u7684\u9556\u624b\uff0c\u53d1\u73b0\u4e86\u56db\u4f4d\u7ecf\u8fc7\u6613\u5bb9\uff0c\u800c\u4e14\uff0c\u4e5f\u53ef\u80fd\u6709\u7740\u76f8\u5f53\u4e0d\u9519\u7684\u6b66\u529f\uff0c\u5c31\u6d3e\u51fa\u4e86\u56db\u4f4d\u9556\u624b\u3002\u76d1\u89c6\u7740\u56db\u4f4d\uff0c\u6240\u5e78\u56db\u4f4d\u6ca1\u6709\u4efb\u4f55\u884c\u52a8\uff0c\u4ed6\u4eec\u4e5f\u4e0d\u77e5\u9053\u56db\u4f4d\u771f\u6b63\u7684\u8eab\u4efd\uff0c\u6240\u4ee5\u3001\u56db\u4f4d\u79bb\u5f00\u6d1b\u9633\u5c45\uff0c\u4ed6\u4eec\u5c31\u4e0d\u518d\u8c0a\u8e2a\u76d1\u89c6\u4e86\u3002\u201d \u3000\u3000\u201c\u5c11\u5144\u662f\u4ec0\u4e48\u8eab\u4efd\uff1f\u600e\u4e48\u77e5\u9053\u7684\u5982\u6b64\u6e05\u695a\u5462\uff1f\u201d\u6bb5\u4e5d\u63d0\u51fa\u4e86\u5fc3\u4e2d\u7684\u7591\u95ee\u3002 \u3000\u3000\u201c\u5176\u5b9e\uff0c\u6211\u4eec\u89c1\u8fc7\u9762\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u90a3\u4e0d\u80fd\u7b97\u89c1\u9762\uff0c\u90a3\u662f\u4f60\u5728\u6697\u4e2d\u76d1\u89c6\u6211\u4eec\uff0c\u4e0d\u80fd\u7b97\u662f\u89c1\u9762\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u8bf4\uff1a\u201c\u89c1\u9762\u662f\u53cc\u65b9\u78b0\u5934\uff0c\u5f7c\u6b64\u5bd2\u6684\u51e0\u53e5\uff0c\u81f3\u5c11\uff0c\u4e5f\u8be5\u76f8\u4e92\u770b\u4e0a\u4e24\u773c\uff0c\u624d\u7b97\u5f97\u4e0a\u662f\u89c1\u9762\u3002\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u5f20\u56db\u59d1\u5df2\u7aef\u4e0a\u56db\u5927\u76d8\u4e0b\u9152\u7684\u83dc\uff0c\u548c\u4e24\u58f6\u6c7e\u9152\u3002 \u3000\u3000\u201c\u5f20\u524d\u8f88\uff0c\u4e5f\u8bf7\u5165\u5ea7\u5427\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\uff01\u548c\u5c1a\u5634\u5201\uff0c\u8001\u5219\u5316\u66f4\u662f\u8d2a\u5403\u7f8e\u5473\uff0c\u6211\u518d\u53bb\u70e7\u51e0\u4e2a\u83dc\u3002 \u3000\u3000\u514d\u5f97\u8fd8\u8981\u843d\u4ed6\u4eec\u8bf7\u5ba2\u4e0d\u8ba9\u5403\u9971\u7684\u8bdd\u67c4\u3002\u201d \u3000\u3000\u84dd\u886b\u4eba\u4e0d\u518d\u8c26\u8ba9\uff0c\u4e3e\u8d77\u7b77\u5b50\uff0c\u9053\uff1a\u201c\u6613\u82b1\u624b\uff0c\u4e0d\u4f46\u6613\u5bb9\u7684\u624b\u6cd5\u72ec\u6b65\u6c5f\u6e56\uff0c\u8c03\u5473\u70f9\u4efb\u7684\u6280\u827a\uff0c\u4e5f\u53ef\u5f53\u5f97\u662f\u76cf\u4e16\u65e0\u53cc\uff0c\u8bf8\u4f4d\u8bf7\u54c1\u8bc4\u4e00\u4e0b\uff0c\u5728\u4e0b\u662f\u5426\u8a00\u8fc7\u5176\u5b9e\u3002\u201d \u3000\u3000\u83b1\u80b4\u4e0b\u684c\u3002\u90a3\u80a1\u9999\u5473\u5df2\u7136\u5f15\u8bf1\u5f97\u4e03\u5b9d\u548c\u5c1a\u3001\u8001\u53eb\u5316\u4e24\u4eba\u998b\u6d8e\u6b32\u6ef4\uff0c\u84dd\u886b\u4eba\u4e3e\u7b77\u4e00\u8ba9\u3002\u4e24\u4eba\u7acb\u523b\u8feb\u4e0d\u53ca\u5f85\u5730\u5927\u5403\u8d77\u6765\u3002 \u3000\u3000\u80e1\u840d\u3001\u6bb5\u4e5d\u867d\u7136\u4e0d\u662f\u5403\u5ba2\uff0c\u4f46\u4e24\u4eba\u5c1d\u4e86\u4e00\u53e3\u4e4b\u540e\uff0c\u4e5f\u89c9\u9999\u900f\u80ba\u8151\uff0c\u5473\u5165\u809d\u80a0\uff0c\u679c\u7136\u662f\u4ece\u672a\u5403\u8fc7\u7684\u4f73\u80b4\u3001\u7f8e\u5473\u3002 \u3000\u3000\u83dc\u662f\u666e\u901a\u7684\u9752\u83dc\u3001\u841d\u535c\uff0c\u8089\u662f\u4e00\u822c\u7684\u725b\u7f8a\u732a\u8089\uff0c\u5473\u9053\u9c9c\u7f8e\u3002 \u3000\u3000\u5168\u51fa\u5728\u914d\u6599\u6070\u5f53\u3002\u8c03\u5473\u5165\u83dc\u3002 \u3000\u3000\u4e5f\u8bb8\u662f\u7f8e\u5473\u5f00\u80c3\u3002\u56db\u4e2a\u4eba\u7adf\u7136\u5403\u5f97\u5fd8\u5176\u6240\u4ee5\uff0c\u7247\u523b\u529f\u592b\uff0c\u56db\u4e2a\u5927\u76d8\uff0c\u7adf\u88ab\u5403\u5f97\u70b9\u6ef4\u4e0d\u9057\u3002 \u3000\u3000\u94b1\u7f3a\u653e\u4e0b\u7b77\u4e8e\uff0c\u9053\uff1a\u201c\u75db\u5feb\uff01\u75db\u5feb\uff01\u4eca\u65e5\u5f97\u5c1d\u4f73\u80b4\uff0c\u5b9e\u6709\u4e0d\u865a\u6b64\u751f\u4e4b\u611f\u2026\u2026\u201d\u76ee\u5149\u4e00\u63a0\u84dd\u886b\u4eba\uff0c\u9053\uff1a\u201c\u6055\u8001\u53eb\u5316\u5b50\u6258\u5927\uff0c\u53eb\u4f60\u4e00\u58f0\u8001\u5f1f\u4e86\uff0c\u4f60\u5012\u8bf4\u8bf4\u770b\uff0c\u54b1\u4eec\u5728\u54ea\u91cc\u89c1\u8fc7\uff1f\u201d \u3000\u3000\u201c\u56db\u4f4d\u5728\u6d1b\u9633\u5c45\u4e2d\u996d\u5e84\u90e8\u7684\u8d35\u5bbe\u5385\u91cc\uff0c\u4ee5\u7cbe\u6e5b\u5185\u529f\uff0c\u6df1\u539a\u7684\u5b9a\u529b\u3002\u51fb\u8d25\u4e86\u6625\u82b1\u56db\u5a62\u2026\u2026\u201d \u3000\u3000\u201c\u90a3\u662f\u53d6\u5de7\uff0c\u6211\u4eec\u907f\u5f00\u4e86\u5979\u4eec\u7684\u53d1\u4e1d\u62c2\u51fb\u3002\u624d\u4fa5\u5e78\u80dc\u5f97\u4e00\u9635\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u8bf4\uff1a\u201c\u8bf4\u8d77\u6765\u60ed\u6127\u5f97\u5f88\u3002\u201d\u201c\u5982\u679c\uff0c\u6444\u9b42\u5927\u6cd5\u8fd8\u9700\u8981\u501f\u5934\u53d1\u68a2\u4e0a\u7684\u836f\u7269\uff0c\u624d\u751f\u6548\u7528\u3002\u90a3\u4e5f\u662f\u4e0b\u4e58\u529f\u592b\u4e86\uff01\u201d \u3000\u3000\u201c\u600e\u4e48\uff1f\u5979\u4eec\u53d1\u68a2\u4e4b\u4e0a\u3002\u8fd8\u6709\u836f\u7269\u2026\u2026\u201d\u8001\u53eb\u5316\u5403\u4e86\u4e00\u60ca\u3002 \u3000\u3000\u9053\uff1a\u201c\u90a3\u662f\u8bf4\u4e0d\u7528\u6444\u9b42\u5927\u6cd5\uff0c\u53ea\u8981\u52a0\u5f3a\u836f\u529b\uff0c\u4e00\u6837\u4e5f\u53ef\u4ee5\u5236\u670d\u4eba\u4e86\uff1f\u201d \u3000\u3000\u84dd\u886b\u4eba\u6709\u95ee\u5fc5\u7b54\u5730\u8bf4\uff1a\u201c\u5e94\u8be5\u5982\u6b64\uff0c\u4e0d\u8fc7\u53d1\u68a2\u4e0a\u6d78\u6cbe\u7684\u836f\u529b\u592a\u5f3a\uff0c\u4e5f\u53ef\u80fd\u4f24\u4e86\u81ea\u5df1\u3002\u5c31\u5728\u4e0b\u6240\u77e5\uff0c\u6625\u82b1\u56db\u5a62\u7684\u529f\u529b\u8fd8\u6d45\uff0c\u624d\u6709\u6b64\u4e00\u7740\uff0c\u53d1\u4e1d\u62c2\u9762\uff0c\u7528\u5fc3\u5728\u5206\u6563\u5ba2\u4eba\u7684\u7cbe\u795e\u3002\u9664\u975e\u5bf9\u624b\u592a\u5f3a\u65f6\uff0c\u5979\u4eec\u624d\u52a0\u4e0a\u836f\u7269\uff0c\u4e0d\u8fc7\uff0c\u5979\u4eec\u5bf9\u4ed8\u56db\u4f4d\uff0c\u5df2\u7ecf\u5c3d\u51fa\u516b\u5b9d\u2026\u2026\u201d \u3000\u3000\u201c\u90a3\u662f\u8bf4\uff0c\u6625\u82b1\u56db\u5ac2\uff0c\u4e5f\u5bf9\u6211\u4eec\u7528\u8fc7\u836f\u7269\u4e86\uff1f\u201d\u80e1\u840d\u63d2\u53e3\u95ee\u4e86\u4e00\u53e5\u3002 \u3000\u3000\u201c\u662f\uff01\u5979\u95e8\u5df2\u7ecf\u9ed4\u9a74\u6280\u7a77\uff0c\u53cd\u88ab\u56db\u4f4d\u4e58\u865a\u800c\u5165\uff0c\u638f\u53d6\u51fa\u5979\u4eec\u80f8\u4e2d\u7684\u79d8\u5bc6\u2026\u2026\u201d \u3000\u3000\u201c\u6240\u4ee5\uff0c\u4f60\u8001\u5f1f\u5c31\u53ca\u65f6\u73b0\u8eab\u2026\u2026\u201d\u8001\u53eb\u5316\u9053\uff1a\u201c\u5e2e\u5979\u4eec\u4e00\u81c2\u4e4b\u529b\u3002\u201d \u3000\u3000\u201c\u8001\u524d\u8f88\u770b\u665a\u8f88\u50cf\u5417\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4e00\u70b9\uff0c\u8001\u53eb\u5316\u672c\u6765\u662f\u60f3\u4e0d\u901a\u7684\uff0c\u4f46\u89c1\u8bc6\u4e86\u6613\u82b1\u624b\u65e0\u6240\u4e0d\u80fd\u7684\u6613\u5bb9\u6280\u5de7\uff0c\u8001\u53eb\u5316\u624d\u89e3\u5f00\u5fc3\u7ed3\uff0c\u4f60\u8001\u5f1f\u5c31\u662f\u90a3\u4f4d\u95ef\u5165\u8d35\u5bbe\u5385\u7684\u5250\u603b\u7ba1\u3002\u54b1\u4eec\u786e\u5b9e\u89c1\u8fc7\u9762\uff0c\u662f\u5427\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u524d\u8f88\u5b8c\u5168\u8bf4\u5bf9\u4e86\uff01\u6625\u82b1\u56db\u5a62\u4e0d\u80fd\u8d25\u5f97\u592a\u60e8\uff0c\u90a3\u53ef\u80fd\u4f1a\u8981\u4e86\u5979\u4eec\u7684\u547d\uff0c\u518d\u8bf4\uff0c\u5979\u4eec\u77e5\u9053\u5f97\u592a\u6709\u9650\u4e86\uff0c\u5c31\u7b97\u5168\u90e8\u88ab\u4f60\u4eec\u6316\u51fa\u6765\u3002\u4e5f\u6ca1\u6709\u4ec0\u4e48\u5927\u7528\uff0c\u6240\u4ee5\uff0c\u6211\u5c31\u53ca\u65f6\u6551\u4e86\u5979\u4eec\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u597d\uff01\u2026\u2026\u201d\u6bb5\u4e5d\u8bf4\uff1a\u201c\u6c5f\u6e56\u4e0a\u8fd8\u6709\u8fd9\u4e48\u591a\u5fc3\u6000\u5927\u4ec1\u7684\u6b66\u6797\u540c\u9053\uff0c\u770b\u6765\uff0c\u7a0d\u5047\u65f6\u65e5\uff0c\u6211\u4eec\u4e0d\u96be\u805a\u5408\u6210\u4e00\u80a1\u5e9e\u5927\u7684\u5b9e\u529b\u4e86\u3002\u201d \u3000\u3000\u201c\u6bb5\u638c\u95e8\u4e0d\u8981\u60f3\u5f97\u592a\u597d\uff0c\u5728\u4e0b\u5f88\u7559\u5fc3\u6c5f\u7322\u4e0a\u7684\u4e8b\u60c5\u53d8\u5316\u2026\u2026\u201d \u3000\u3000\u84dd\u886b\u5c11\u5e74\u8bf4\uff1a\u201c\u6211\u82b1\u4e86\u4e24\u5e74\u7684\u5de5\u592b\uff0c\u624d\u9047\u56db\u4f4d\u8fd9\u6837\u771f\u6b63\u5fc3\u5b58\u662f\u975e\uff0c\u4e0d\u8ba1\u751f\u6b7b\uff0c\u5168\u529b\u8ffd\u67e5\u7978\u6e90\uff0c\u63ed\u53d1\u8fd9\u573a\u9634\u8c0b\u7684\u4eba\uff0c\u7edd\u5927\u90e8\u5206\u7684\u6b66\u6797\u540c\u9053\uff0c\u662f\u5fc3\u6000\u754f\u60e7\uff0c\u9f9f\u7f29\u4e0d\u51fa\uff0c\u80c6\u5c0f\u6015\u4e8b\uff0c\u5f53\u7136\uff0c\u4e00\u4e9b\u5f3a\u5927\u7684\u95e8\u6237\u3002\u4e0d\u662f\u9996\u8111\u88ab\u5236\u670d\u5c31\u662f\u88ab\u67b6\u7a7a\u4e86\u6743\u52bf\uff0c\u6362\u4eba\u4ee5\u4ee3\u2026\u2026\u201d \u3000\u3000\u80e1\u840d\u3001\u6bb5\u4e5d\u53ea\u542c\u5f97\u8138\u4e0a\u4e00\u70ed\uff0c\u6697\u9053\u4e86\u4e24\u58f0\u60ed\u6127\uff0c\u5982\u975e\u95e8\u6237\u6709\u53d8\uff0c\u88ab\u4eba\u903c\u4e0a\u7edd\u8def\uff0c\u4e24\u4eba\u53ea\u6015\u4e5f\u6839\u96be\u6709\u8fd9\u79cd\u5766\u8361\u80f8\u6000\uff0c\u4ec1\u4fa0\u6c14\u5ea6\u3002 \u3000\u3000\u4f46\u542c\u90a3\u84dd\u8863\u5c11\u5e74\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u8fd9\u5c31\u5f62\u6210\u4e86\u76ee\u4e0b\u7684\u6c5f\u6e56\u60c5\u52bf\uff0c\u8868\u9762\u4e0a\u98ce\u5e73\u6d6a\u9759\uff0c\u9aa8\u5b50\u91cc\u65e9\u5df2\u6e83\u70c2\u8150\u673d\uff0c\u6700\u53ef\u7b11\u7684\u662f\uff0c\u5c11\u6797\u3001\u6b66\u5f53\u4e24\u5927\u95e8\u6d3e\uff0c\u6e83\u5012\u5728\u5373\uff0e\u8fd8\u6b7b\u8981\u9762\u5b50\uff0c\u5ba3\u5e03\u95ed\u5173\u81ea\u5b88\uff0c\u6682\u4e0d\u95ee\u6c5f\u6e56\u4e8b\u52a1\u2026\u2026\u201d \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u63a5\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u5c11\u6797\u3001\u6b66\u5f53\u4e24\u5927\u95e8\u6d3e\uff0c\u771f\u7684\u5df2\u7ecf\u88ab\u9b54\u6559\u63a7\u5236\u4e86\u3002\u201d \u3000\u3000\u201c\u81f3\u5c11\u4ed6\u4eec\u7684\u4e3b\u8111\u4eba\u7269\uff0c\u5df2\u88ab\u63a7\u5236\uff0c\u8fd9\u4e24\u5927\u95e8\u6d3e\uff0c\u89c4\u6212\u7d20\u4e25\uff0c\u552f\u638c\u95e8\u4eba\u4e4b\u547d\u662f\u4ece\u2026\u2026\u201d \u3000\u3000\u84dd\u886b\u4eba\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8fd9\u5c31\u7ed9\u4eba\u4ee5\u53ef\u4e58\u4e4b\u673a\uff0c\u53ea\u8981\u4f7f\u4e09\u4e24\u4e2a\u4e3b\u8111\u4eba\u7269\u53d7\u5236\uff0c\u5343\u767e\u4f4d\u95e8\u4eba\u5f1f\u5b50\u7684\u5e9e\u5927\u5b9e\u529b\uff0c\u4e5f\u968f\u7740\u88ab\u4eba\u638c\u63e1\u2026\u2026\u201d \u3000\u3000\u201c\u897f\u65b9\u9b54\u6559\uff0c\u66fe\u7ecf\u4e24\u5ea6\u5165\u4fb5\uff0c\u90fd\u88ab\u5404\u5927\u95e8\u6d3e\u8054\u624b\u5408\u529b\uff0c\u9010\u51fa\u4e2d\u539f\u2026\u2026\u201d\u8001\u53eb\u5316\u9053\uff1a\u201c\u8fd9\u4e00\u6b21\uff0c\u4ed6\u4eec\u6539\u53d8\u65b9\u6cd5\uff0c\u627e\u51fa\u4e86\u4e2d\u539f\u6b66\u6797\u9053\u4e0a\u5c0a\u5e08\u91cd\u9053\u7684\u4f20\u7edf\uff0c\u6697\u65bd\u7b97\u8ba1\uff0c\u5148\u5236\u670d\u95e8\u6237\u7684\u9996\u8111\u4eba\u7269\u3002 \u3000\u3000\u55ef\uff01\u8fd9\u529e\u6cd5\u591f\u9634\u72e0\u3002\u4e5f\u591f\u9ad8\u660e\uff0c\u52ff\u602a\u8001\u53eb\u5316\u67e5\u4e86\u5341\u591a\u5e74\u3002\u67e5\u4e0d\u51fa\u4e00\u70b9\u5934\u7eea\u3002\u9b54\u6559\u624b\u6bb5\u3002 \u3000\u3000\u679c\u7136\u6076\u6bd2\u3002\u201d \u3000\u3000\u84dd\u8863\u4eba\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u897f\u65b9\u9b54\u6559\u6b66\u529f\u3002\u867d\u7136\u8be1\u5947\uff0c\u672f\u6cd5\u4e5f\u81ea\u6210\u4e00\u5b97\uff0c\u4f46\u5982\u6ca1\u6709\u4e2d\u539f\u6b66\u6797\u4eba\u7684\u5f15\u5bfc\u914d\u5408\uff0c\u5f88\u96be\u4fb5\u5165\u4e2d\u539f\u2026\u2026\u201d \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u63a5\u9053\uff1a\u201c\u5c0f\u65bd\u4e3b\u7684\u610f\u601d\u662f\u3002\u4e2d\u539f\u6b66\u6797\u9053\u4e0a\uff0c\u6709\u4eba\u52fe\u7ed3\u9b54\u6559\u3001\u5f15\u72fc\u5165\u5ba4\u3001\u5b89\u6392\u4e0b\u8fd9\u4e00\u573a\u6c5f\u6e56\u52ab\u96be\uff1f\u201d \u3000\u3000\u201c\u7269\u5fc5\u81ea\u8150\u800c\u540e\u866b\u86c0\u4e4b\u2026\u2026\u201d\u84dd\u8863\u4eba\u9053\uff1a\u201c\u5c31\u5728\u4e0b\u7684\u770b\u6cd5\uff0c\u4e8b\u60c5\u8fd8\u4e0d\u662f\u5982\u6b64\u7b80\u5355\uff0c\u4e2d\u95f4\u60c5\u8282\uff0c\u53ef\u80fd\u5341\u5206\u590d\u6742\uff0c\u533a\u533a\u4e00\u4eba\u7684\u529b\u91cf\uff0c\u5b9e\u6709\u7740\u96be\u7aa5\u5168\u8c79\u4e4b\u82e6\uff0c\u5e0c\u671b\u80fd\u627e\u51e0\u4e2a\u5fc3\u610f\u76f8\u6295\u7684\u4eba\uff0c\u901a\u529b\u5408\u4f5c\uff0c\u8ffd\u67e5\u7978\u4e71\u4e4b\u6e90\uff0c\u90a3\u77e5\u5343\u4e07\u4eba\u4e2d\uff0c\u7adf\u662f\u4e00\u571f\u96be\u6c42\uff0c\u5b9e\u53eb\u5728\u4e0b\u5fc3\u7070\u610f\u61d2\uff0c\u5e78\u597d\uff0c\u53ca\u65f6\u9047\u4e0a\u4e86\u6613\u82b1\u624b\u5f20\u56db\u59d1\uff0c\u501f\u91cd\u5979\u4e30\u5bcc\u7684\u6c5f\u6e56\u7ecf\u9a8c\uff0c\u624d\u5728\u9519\u7efc\u4e07\u7aef\u4e2d\u3002\u7406\u51fa\u4e86\u4e00\u4e2a\u5934\u7eea\uff0c\u76ee\u4e0b\u6c5f\u6e56\u4e0a\u65e2\u65e0\u53ef\u7528\u4e4b\u5175\uff0c\u53ea\u597d\u5bc4\u8eab\u5728\u6d1b\u9633\u5c45\u4e2d\uff0c\u501f\u4ee5\u5b89\u8eab\u7acb\u547d\u2026\u2026\u201d \u3000\u3000\u53ea\u542c\u4e00\u4e2a\u5a07\u8106\u7684\u58f0\u97f3\u63a5\u9053\uff1a\u201c\u4ee5\u5b50\u4e4b\u77db\uff0c\u653b\u5b50\u4e4b\u76fe\uff0c\u529e\u6cd5\u5012\u662f\u4e0d\u9519\uff0c\u4e0d\u8fc7\uff0c\u4f60\u5c3d\u6536\u4e00\u4e9b\u5973\u5b69\u5b50\uff0c\u4e0d\u6015\u5979\u4eec\u4e89\u98ce\u5403\u916f\u5417\uff1f\u201d \u3000\u3000\u5f20\u56db\u59d1\u624b\u4e2d\u6258\u770b\u4e00\u4e2a\u5927\u6728\u76d8\uff0c\u53c8\u9001\u4e86\u4e0a\u4e94\u5927\u76d8\u4f73\u80b4\u3002 \u3000\u3000\u201c\u6ca1\u6709\u529e\u6cd5\u2026\u2026\u201d\u84dd\u8863\u4eba\u63a5\u9053\uff1a\u201c\u6211\u8fd9\u4e2a\u526f\u603b\u7ba1\uff0c\u7ba1\u7406\u996d\u5e84\u90e8\uff0c\u9664\u4e86\u6625\u3001\u590f\u3001\u79cb\u3001\u51ac\u56db\u5b63\u82b1\u5a62\u4e5d\u4e2a\u5973\u4e3b\u89d2\u4e4b\u5916\uff0c\u8fd8\u6709\u4e8c\u5341\u56db\u4e2a\u4f8d\u9152\u7684\u59d1\u5a18\uff0c\u5168\u7531\u6211\u7ba1\u7406\u8c03\u914d\uff0c\u6211\u53ea\u80fd\u548c\u5979\u4eec\u642d\u5173\u7cfb\u4e86\u3002\u201d \u3000\u3000\u80e1\u840d\u9053\uff1a\u201c\u5c11\u5144\u4e09\u5341\u4e09\u4e2a\u5973\u90e8\u4e0b\uff0c\u662f\u4e0d\u662f\u90fd\u4f1a\u6444\u9b42\u5927\u6cd5\uff1f\u201d \u3000\u3000\u201c\u4e0d\u662f\uff01\u53ea\u6709\u4e5d\u4e2a\u82b1\u5a62\u5b66\u8fc7\u6444\u9b42\u5927\u6cd5\uff0c\u4e60\u8fc7\u6b66\u529f\uff0c\u53e6\u5916\u4e8c\u5341\u56db\u4e2a\u4f8d\u9152\u59d1\u5a18\uff0c\u5168\u662f\u91cd\u91d1\u9009\u8d2d\u7684\u9152\u5973\uff0c\u6b4c\u59ec\uff0c\u5979\u4eec\u548c\u6c5f\u6e56\u4e0a\u7684\u4e8b\u52a1\u5168\u65e0\u5173\u7cfb\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u672a\u518d\u79bb\u53bb\uff0c\u62c9\u5f20\u6728\u6905\u3002\u5728\u4e00\u65c1\u5750\u4e0b\uff0c\u63a5\u9053\uff1a\u201c\u5c31\u6211\u89c2\u5bdf\uff0c\u8fd9\u4e2a\u7ec4\u5408\u5341\u5206\u5e9e\u5927\uff0c\u6d1b\u9633\u5c45\u53ea\u4e0d\u8fc7\u662f\u4ed6\u4eec\u7684\u636e\u70b9\u4e4b\u4e00\u2026\u2026\u201d \u3000\u3000\u201c\u4e89\u9738\u6c5f\u6e56\uff0c\u9010\u9e7f\u4e2d\u539f\uff0c\u6ee1\u6000\u96c4\u5fc3\u58ee\u5fd7\u2026\u2026\u201d\u80e1\u840d\u8bf4\uff1a\u201c\u7adf\u7136\u53c8\u6cbe\u624b\u9152\u8272\uff0c\u7ecf\u8425\u8d4c\u53f0\u98ce\u6708\uff0c\u96be\u9053\u4e0d\u6015\u4f24\u5bb3\u5230\u4ed6\u4eec\u6e05\u540d\u4e48\uff1f\u201d \u3000\u3000\u201c\u8fd9\u5c31\u662f\u4ed6\u4eec\u5389\u5bb3\u7684\u5730\u65b9\u4e86\uff0c\u56e0\u4e3a\uff0c\u4ed6\u4eec\u65e9\u5df2\u4e86\u89e3\uff0c\u4e89\u96c4\u6b66\u6797\uff0c\u6700\u91cd\u8981\u7684\u4e00\u4ef6\u4e8b\uff0c\u5fc5\u987b\u8981\u6709\u5927\u91cf\u7684\u91d1\u94b1\uff0c\u800c\u4e14\u8981\u6e90\u6e90\u4e0d\u7edd\u7684\u4f9b\u5e94\u2026\u2026\u201d \u3000\u3000\u84dd\u886b\u4eba\u53f9\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4e0d\u7ba1\u591a\u4e48\u82f1\u96c4\u7684\u4eba\u7269\uff0c\u6ca1\u6709\u94b1\uff0c\u5c31\u5f88\u96be\u98ce\u5149\u8d77\u6765\uff0c\u6c5f\u6c8f\u9010\u9e7f\uff0c\u66f4\u662f\u975e\u94b1\u83ab\u529e\uff0c\u5982\u662f\u8863\u98df\u4e0d\u5468\uff0c\u90a3\u8fd8\u80fd\u5168\u5fc3\u5168\u610f\u7684\u53bb\u5b66\u6b66\u529f\uff0c\u4f46\u6709\u5f88\u591a\u7cbe\u6e5b\u7684\u6280\u827a\uff0c\u5fc5\u987b\u8981\u5168\u795e\u8d2f\u6ce8\u3001\u624d\u80fd\u6709\u6240\u6210\u5c31\uff0c\u4e2d\u56fd\u6b66\u6797\u4f20\u7edf\u4f5c\u5e08\u7236\u7684\u5e38\u4f1a\u9690\u6280\u81ea\u73cd\uff0c\u4e0d\u80fd\u8ba9\u5f92\u5f1f\u6b66\u529f\u8d85\u8fc7\u5e08\u7236\uff0c\u4f20\u6388\u5f1f\u5b50\u65f6\uff0c\u5c31\u7559\u4e86\u4e00\u624b\uff0c\u4ee3\u4ee3\u5982\u6b64\uff0c\u5c31\u8fd9\u6837\u3002\u5f88\u591a\u7edd\u6280\u5931\u4f20\u4e86\u2026\u2026\u201d \u3000\u3000\u84dd\u886b\u4eba\u4f3c\u6709\u610f\u8457\u65e0\u610f\u7684\u770b\u4e86\u56db\u4eba\u4e00\u773c\u3002\u56db\u4e2a\u4eba\u90fd\u4e0d\u7981\u8138\u4e0a\u4e00\u70ed\u2026\u2026\u3002 \u3000\u3000\u8001\u53eb\u5316\u7b49\u56db\u4eba\uff0c\u5728\u6c5f\u6e56\u4e0a\uff0c\u79f0\u5f97\u4e0a\u662f\u6b63\u6d3e\u4eba\u58eb\uff0c\u4e49\u4fa0\u809d\u80c6\uff0c\u4f46\u4ed6\u95e8\u4e5f\u6709\u7740\u7559\u4e00\u624b\u7684\u5fc3\u6001\uff0c\u6240\u8c13\u4e0d\u4f20\u4e4b\u79d8\uff0c\u5c31\u662f\u5e08\u7236\u4f1a\u3002\u5f92\u5f1f\u4e0d\u4f1a\u3002 \u3000\u3000\u4f46\u95fb\u84dd\u8863\u5c11\u5e74\u63a5\u9053\uff1a\u201c\u4fa0\u4ee5\u6b66\u72af\u7981\uff0c\u8ba9\u7cbe\u5947\u7684\u6b66\u529f\u4ee3\u4ee3\u5931\u4f20\u4e5f\u597d\uff0c\u9700\u77e5\u8f7d\u821f\u4e4b\u6c34\uff0c\u4ea6\u53ef\u8986\u821f\uff0c\u5b66\u4f1a\u6b66\u529f\u7684\u4eba\uff0c\u51e0\u4e4e\u6709\u534a\u6570\u51ed\u4ed7\u6b66\u529f\u4e3a\u8a00\u4eba\u95f4\uff0c\u8bf4\u8d77\u6765\u4e0d\u7b97\u662f\u5927\u5bb3\uff0c\u4f46\u6c14\u52bf\u51cc\u4eba\uff0c\u51fa\u8a00\u4e0d\u900a\u3002 \u3000\u3000\u4e09\u53e5\u8bdd\u8bf4\u4e0d\u5bf9\uff0c\u5c31\u51fa\u624b\u63cd\u4eba\uff0c\u5982\u662f\u5927\u5bb6\u90fd\u4e0d\u4f1a\u6b66\u529f\uff0c\u8fd9\u4e16\u95f4\u662f\u4e0d\u662f\u5e73\u9759\u591a\u4e86\u5462\uff1f\u201d \u3000\u3000\u4ed6\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u8bf4\u7684\u5728\u60c5\u5165\u7406\uff0c\u5c0f\u4e8b\u618e\u3001\u5927\u5b66\u95ee\uff0c\u542c\u5f97\u94b1\u7f3a\u3001\u80e1\u840d\u3001\u6bb5\u4e5d\uff0c\u4e2a\u4e2a\u51dd\u795e\u503e\u6ce8\uff0c\u5c31\u8fde\u8df3\u51fa\u4e09\u754c\u5916\u7684\u4e03\u5b9d\u548c\u5c1a\u3002\u4e5f\u542c\u5f97\u5149\u8111\u888b\u4e0d\u505c\u7684\u70b9\u52a8\u3002 \u3000\u3000\u4ed6\u4eec\u5fc3\u4e2d\u672c\u6709\u5f88\u591a\u4e8b\u8981\u95ee\u7684\uff0c\u73b0\u5728\u5374\u542c\u5165\u795e\uff0c\u8fde\u95ee\u7684\u4e8b\u60c5\uff0c\u4e5f\u6682\u65f6\u5fd8\u4e86\u3002 \u3000\u3000\u84dd\u886b\u4eba\u4f3c\u662f\u81ea\u5df1\u4e5f\u8bf4\u5f97\u611f\u6168\u4e07\u7aef\uff0c\u957f\u957f\u5401\u4e00\u53e3\u6c14\uff0c\u53c8\u9053\uff1a\u201c\u771f\u7684\u7559\u4e00\u624b\uff0c\u8ba9\u5b83\u968f\u7740\u6b7b\u4ea1\uff0c\u4e5f\u5c31\u7f62\u4e86\uff0c\u4f46\u4ed6\u5782\u8001\u66ae\u5e74\u65f6\u3002\u5ffd\u7136\u53c8\u52a8\u4e86\u7559\u540d\u6b66\u6797\u7684\u5ff5\u5934\uff0c\u6240\u4ee5\uff0c\u628a\u90a3\u4e9b\u4e0d\u80af\u4f20\u7ed9\u5f92\u5f1f\u4e86\u6b66\u529f\u3002 \u3000\u3000\u7528\u7b14\u5199\u4e86\u4e0b\u6765\uff0c\u4ec0\u4e48\u5251\u6cd5\u8865\u5f55\uff0c\u4ec0\u4e48\u638c\u6cd5\u5947\u5b66\uff0c\u6240\u4ee5\u3002\u6c5f\u6e56\u4e0a\u4e0d\u65f6\u4f1a\u53d1\u73b0\u524d\u8f88\u9ad8\u624b\u7559\u4e16\u7684\u6b66\u5b66\u79d8\u7c4d\uff0c\u8fd9\u5c31\u5f15\u8d77\u6c5f\u6e56\u4eba\u7684\u5077\u89d1\uff0c\u53c8\u9020\u6210\u4e86\u4e00\u756a\u4e89\u593a\u3001\u640f\u6740\uff0c\u5f53\u7136\uff0c\u8fd9\u4e9b\u4eba\u5fc5\u987b\u662f\u540d\u52a8\u4e00\u65f6\u7684\u6c5f\u6e56\u9ad8\u4eba\uff0c\u79d8\u7c4d\u4e0a\u8bb0\u8ff0\u7684\u6b66\u529f\uff0c\u4e5f\u786e\u6709\u72ec\u5230\u4e4b\u5904\uff0c\u53ef\u662f\uff0c\u4ed6\u6d3b\u7684\u65f6\u5019\u4e3a\u4ec0\u4e48\u4e0d\u4f20\u7ed9\u522b\u4eba\u5462\uff1f\u81f3\u5c11\uff0c\u4ed6\u53ef\u4ee5\u627e\u4e00\u4e2a\u5fc3\u5730\u5584\u826f\uff0c\u624d\u667a\u3001\u54c1\u683c\uff0c\u90fd\u9002\u5408\u5b66\u4e60\u90a3\u79cd\u6b66\u529f\u7684\u4eba\uff0c\u5c82\u4e0d\u662f\u76f8\u5f97\u76ca\u5f70\uff0c\u7559\u4e0b\u4e00\u672c\u4e66\uff0c\u4ec0\u4e48\u4eba\u5f97\u5230\u5b83\uff0c\u597d\u50cf\u548c\u4ed6\u65e0\u5173\u4e86\uff0c\u53ef\u80fd\u5f97\u5230\u7684\u4eba\u3002\u4e0d\u8bc6\u5b57\uff0c\u62ff\u6765\u7528\u4f5c\u5f15\u706b\u7684\u7eb8\u7164\u4e86\u3002\u4e5f\u53ef\u80fd\u88ab\u6495\u4e0b\u5305\u4e1c\u897f\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u6709\u9053\u7406\u3002\u2026\u2026\u201d\u5f20\u56db\u59d1\u63a5\u9053\uff1a\u201c\u6211\u53ea\u9053\u4f60\u6b66\u529f\u9ad8\u660e\u3002\u60f3\u4e0d\u5230\u8fd8\u6709\u4e00\u809a\u5b50\u5b66\u95ee\uff0c\u5144\u5f1f\uff0c\u6587\u6b66\u5168\u624d\u561b\uff01\u201d \u3000\u3000\u201c\u5509\uff01\u4ed6\u4eec\u82b1\u4e86\u5341\u51e0\u5e74\u7684\u5fc3\u8840\u57f9\u517b\u6211\uff0c\u6211\u4e0d\u80fd\u8ba9\u4ed6\u4eec\u5931\u671b\uff0c\u542b\u6068\u4e5d\u6cc9\u2026\u2026\u201d \u3000\u3000\u8fd9\u51e0\u53e5\u8bdd\uff0c\u6ca1\u5934\u6ca1\u8111\uff0c\u542c\u5f97\u5728\u573a\u4e4b\u4eba\uff0c\u5168\u90fd\u4e3a\u4e4b\u4e00\u6014\u3002 \u3000\u3000\u201c\u5144\u5f1f\uff0c\u4ed6\u4eec\u662f\u8c01\u5440\uff1f\u201d \u3000\u3000\u201c\u6211\u7684\u5e08\u7236\u3001\u4e49\u7236\u3001\u5e72\u5a18\uff0c\u4ed6\u4eec\u62db\u5e0c\u671b\u5168\u90e8\u5bc4\u6258\u5728\u6211\u7684\u8eab\u4e0a\u3001\u8fd9\u4e8e\u65a4\u91cd\u62c5\uff0c\u538b\u5f97\u6211\u2026\u2026\u201d \u3000\u3000\u7a81\u7136\u6539\u53e3\uff0c\u7b11\u9053\uff1a\u201c\u5927\u59d0\uff0c\u6211\u8bf7\u4f60\u6536\u4e2a\u5973\u5f92\u5f1f\uff0c\u4f20\u6388\u4f60\u7684\u6613\u5bb9\u7edd\u827a\uff0c\u5927\u59d0\u6536\u4e86\u6ca1\u6709\u5462\uff1f\u201d \u3000\u3000\u201c\u6536\u4e86\uff0c\u6536\u4e86\u3002\u4f60\u521a\u624d\u9a82\u4e86\u534a\u5929\uff0c\u6562\u60c5\u662f\u9a82\u5927\u59d0\u6211\u5440\uff01\u2026\u2026\u201d \u3000\u3000\u5f20\u56db\u59d1\u8bf4\uff1a\u201c\u672c\u6765\uff0c\u6211\u662f\u4e0b\u51c6\u5907\u6536\u5f92\u5f1f\u7684\uff0c\u6211\u4e5f\u4e0d\u4f1a\u628a\u6613\u5bb9\u624b\u6cd5\u5199\u6210\u4e66\uff0c\u6211\u53ea\u60f3\u5230\u540e\u4eba\u63d0\u5230\u5f20\u56db\u59d1\u7684\u6613\u5bb9\u7edd\u6280\uff0c\u524d\u65e0\u53e4\u4eba\u2026\u2026\u201d \u3000\u3000\u201c\u4f60\u4e5f\u60f3\u540e\u65e0\u6765\u8005\uff0c\u662f\u5417\uff1f\u2026\u2026\u201d \u3000\u3000\u201c\u73b0\u5728\uff0c\u5df2\u7ecf\u6ce1\u6c64\u4e86\uff0c\u4f4d\u4f60\u4e24\u53e5\u7c73\u6c64\u704c\u5f97\u6211\u4e00\u8ff7\u7cca\uff0c\u5c31\u7b54\u5e94\u4f60\u6536\u5f92\u5f1f\u4e86\u2026\u2026\u201d\u5f20\u56db\u59d1\u53f9\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6c5f\u6e56\u4e0a\u4f60\u4e89\u6211\u593a\uff0c\u5927\u59d0\u6211\u65e9\u5c31\u538c\u5026\u4e86\uff0c\u6240\u4ee5\uff0c\u6211\u624d\u8eb2\u8d77\u6765\u3002\u8c01\u60f3\u5230\uff0c\u4f1a\u78b0\u4e0a\u4e86\u4f60\uff0c\u771f\u662f\u547d\u4e2d\u6ce8\u5b9a\u554a\uff01\u2026\u2026\u201d \u3000\u3000\u201c\u5927\u59d0\u867d\u4ee5\u6613\u5bb9\u672f\u4eab\u8a89\u6b66\u6797\uff0c\u4f46\u4f60\u90a3\u4e00\u8eab\u6b66\u529f\uff0c\u653e\u773c\u6c5f\u6e56\uff0c\u4e5f\u96be\u6709\u51e0\u4e2a\u654c\u624b\uff0c\u6211\u8981\u5927\u59d0\u5e2e\u52a9\u6211\u3002\u4e0d\u6b62\u662f\u6613\u5bb9\u624b\u6cd5\uff0c\u5fc5\u8981\u65f6\uff0c\u8fd8\u5f97\u5927\u59d0\u4eb2\u81ea\u51fa\u9a6c\uff0c\u2026\u2026\u201d \u3000\u3000\u201c\u884c\u4e86\uff0c\u6211\u7b54\u5e94\u4e86\u4f60\uff0c\u5c31\u4e00\u5207\u8ba4\u547d\uff0c\u5927\u59d0\u80fd\u529e\u5230\u7684\uff0c\u4e00\u5b9a\u5c3d\u529b\uff0c\u4e0d\u8fc7\uff0c\u6709\u4e00\u70b9\u6211\u60f3\u4e0d\u660e\u767d\u3002\u5144\u5f1f\uff0c\u4f60\u4eca\u4e8c\u5341\u4e8c\u4e09\u5c81\u5427\uff01\u201d \u3000\u3000\u201c\u662f\uff01\u5c0f\u5f1f\u4e8c\u5341\u4e09\u5c81\u3002\u201d \u3000\u3000\u201c\u4f60\u8fd9\u70b9\u5e74\u7eaa\uff0c\u5982\u662f\u5927\u59d0\u6211\u65e9\u4e00\u70b9\u5ac1\u4eba\u2026\u2026\u201d\u5ae3\u7136\u4e00\u7b11\uff0c\u6539\u53e3\u8bf4\u9053\uff1a\u201c\u4f60\u600e\u4e48\u4f1a\u77e5\u9053\u6211\uff0c\u53c8\u600e\u80fd\u627e\u4e0a\u6211\u4f4f\u7684\u5730\u65b9\uff1f\u201d \u3000\u3000\u201c\u5e08\u7236\u7ed9\u6211\u4e00\u4e2a\u5c0f\u672c\u5b50\u3002\u8bb0\u8ff0\u4e86\u5f53\u4ee3\u6c5f\u6e56\u9ad8\u4eba\u7684\u5f62\u8c8c\uff0c\u548c\u4ed6\u4eec\u7684\u7279\u6b8a\u624d\u80fd\u3002\u5f53\u7136\u3002\u4e5f\u8bf4\u660e\u4e86\u4ed6\u53ef\u80fd\u9690\u5c45\u7684\u5730\u65b9\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e48\u8bf4\uff0c\u4f60\u90a3\u8001\u9b3c\u5e08\u7236\uff0c\u548c\u6211\u4e5f\u662f\u719f\u4eba\u4e86\uff1f\u201d \u3000\u3000\u5979\u5b64\u505a\u81ea\u8d1f\uff0c\u8a00\u8bed\u4e4b\u95f4\uff0c\u76f4\u6765\u76f4\u5f80\uff0c\u4ece\u4e0d\u6ce8\u91cd\u793c\u6570\u3002 \u3000\u3000\u84dd\u886b\u4eba\u4f3c\u662f\u4e5f\u4e0d\u653e\u5728\u5fc3\u4e0a\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5927\u6982\u662f\u5427\uff01\u5e08\u7236\u8bb0\u8ff0\u5927\u59d0\uff0c\u7279\u522b\u5730\u8be6\u5c3d\uff0c\u6240\u4ee5\uff0c\u5c0f\u5f1f\u4e00\u4e0b\u5b50\u5c31\u627e\u5230\u4e86\u4f60\u3002\u201d \u3000\u3000\u201c\u80fd\u6559\u51fa\u4f60\u8fd9\u6837\u7684\u5f92\u5f1f\uff0c\u4f60\u90a3\u8001\u9b3c\u5e08\u7236\uff0c\u5927\u6982\u4e5f\u4e0d\u662f\u7b80\u5355\u4eba\u7269\u4e86\uff0c\u4ed6\u7a76\u7adf\u662f\u8c01\u5440\uff1f\u201d \u3000\u3000\u84dd\u886b\u4eba\u7b11\u9053\uff1a\u201c\u5927\u59d0\uff0c\u8fd9\u4e00\u70b9\uff0c\u5c0f\u5f1f\u73b0\u5728\u8fd8\u4e0d\u4fbf\u8bf4\uff0c\u2026\u2026\u201d \u3000\u3000\u76ee\u5149\u4e00\u63a0\u8001\u53eb\u5316\u7b49\u56db\u4eba\uff0c\u4e5f\u7b97\u662f\u7ed9\u4e86\u56db\u4eba\u4e00\u4e2a\u7b54\u590d\uff0c\u8fd9\u4ef6\u4e8b\uff0c\u6682\u65f6\u522b\u95ee\u3002 \u3000\u3000\u201c\u8bf4\u4e0d\u8bf4\u90fd\u6ca1\u5173\u7cfb\uff0c\u6211\u7b54\u5e94\u91cd\u51fa\u6c5f\u6e56\uff0c\u5168\u662f\u88ab\u4f60\u7684\u751c\u8a00\u871c\u8bed\u5403\u51fa\u6765\u7684\uff0c\u8fd8\u5e2e\u4f60\u70e7\u996d\u3001\u716e\u83dc\u3001\u6d17\u8863\u670d\u2026\u2026\u201d \u3000\u3000\u201c\u5927\u59d0\uff0c\u6211\u611f\u6fc0\u3002\u9664\u4e86\u5e08\u7236\u662f\u8c01\uff0c\u6682\u65f6\u4e0d\u80fd\u8bf4\u4e4b\u5916\uff0c\u5927\u59d0\u8981\u5c0f\u5f1f\u5e72\u4ec0\u4e48\uff1f\u5c0f\u5f1f\u7edd\u4e0d\u63a8\u8f9e\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u4f60\u8bb0\u6625\u8fd9\u53e5\u8bdd\u5c31\u884c\u4e86\uff0c\u73b0\u5728\uff0c\u4f60\u4eec\u8c08\u6b63\u7ecf\u4e8b\u3002\u6211\u53bb\u53a8\u623f\u6536\u62fe\u4e00\u4e0b\u3002\u201d \u3000\u3000\u201c\u5148\u8c08\u5927\u59d0\u7684\u4e8b\uff0c\u4f60\u90a3\u4e24\u4e2a\u5973\u5f92\u5f1f\uff0c\u624d\u667a\u5982\u4f55\uff1f\u4f1a\u4e0d\u4f1a\u6b66\u529f\uff1f\u8981\u591a\u4e45\u65f6\u95f4\uff0c\u624d\u80fd\u5e2e\u4eba\u6613\u5bb9\u3002\u201d \u3000\u3000\u201c\u624d\u667a\u8fc7\u4eba\uff0c\u806a\u660e\u673a\u7075\uff0c\u518d\u7ed9\u6211\u4e09\u4e2a\u6708\uff0c\u5c31\u53ef\u4ee5\u542c\u4f60\u7684\u5dee\u9063\u3002\u201d \u3000\u3000\u201c\u4e86\u4e0d\u8d77\u5440\uff01\u5927\u59d0\uff0c\u8fd9\u4e48\u77ed\u7684\u65f6\u95f4\u3002\u5c31\u80fd\u8bad\u7ec3\u51fa\u6765\u4e24\u4e2a\u6613\u5bb9\u9ad8\u624b\uff0c\u771f\u662f\u4f1a\u5bb6\u4e0d\u5fd9\uff0c\u5fd9\u5bb6\u4e0d\u4f1a\u3002\u201d \u3000\u3000\u201c\u6211\u7684\u6613\u5bb9\u624b\u6cd5\uff0c\u770b\u8d77\u6765\u867d\u7136\u795e\u5947\uff0c\u4f46\u6700\u91cd\u7684\u662f\u8c03\u914d\u989c\u6599\u3001\u836f\u7269\u3001\u77e5\u9053\u5982\u4f55\u4e0b\u624b\uff0c\u5927\u59d0\u4e0d\u6559\uff0c\u6478\u7d22\u5341\u5e74\u4e5f\u5b66\u4e0d\u4f1a\uff0c\u8ba4\u771f\u4f20\u6388\uff0c\u4e09\u4e94\u4e2a\u6708\u5c31\u53ef\u4ee5\u6d3e\u4e0a\u7528\u573a\u4e86\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e24\u4e2a\u4eba\u7684\u51fa\u8eab\uff0c\u5927\u59d0\u6e05\u695a\u5417\uff1f\u201d \u3000\u3000\u201c\u5979\u4eec\u7684\u5e74\u534e\u53cc\u5341\u3002\u8c8c\u7f8e\u5982\u82b1\uff0c\u8bf4\u6b66\u529f\u5417\uff1f\u5e94\u8be5\u7b97\u4e2d\u4e0a\u8eab\u624b\uff0c\u8fd9\u51e0\u5929\uff0c\u6211\u4e5f\u5728\u4f20\u6388\u5979\u4eec\u4e00\u4e9b\u5251\u6cd5\uff0c\u597d\u5728\uff0c\u5979\u4eec\u90fd\u6709\u5341\u51e0\u5e74\u7684\u5185\u529f\u57fa\u7840\uff0c\u5b66\u8d77\u6765\u4e0d\u7b97\u56f0\u96be\u3002\u201d \u3000\u3000\u201c\u5927\u59d0\uff0c\u6211\u80fd\u4e0d\u80fd\u89c1\u89c1\u5979\u4eec\u2026\u2026\u201d\u84dd\u886b\u4eba\u9053\uff1a\u201c\u5982\u679c\u5979\u4eec\u6765\u5386\u4e0d\u660e\u2026\u2026\u201d \u3000\u3000\u201c\u5979\u4eec\u6b63\u52e4\u4e8e\u7ec3\u529f\uff0c\u6682\u65f6\u4e0d\u80fd\u89c1\u4f60\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u5462\uff1f\u89c1\u4e2a\u9762\uff0c\u4e5f\u7528\u4e0d\u4e86\u591a\u5c11\u65f6\u95f4\u2026\u2026\u201d \u3000\u3000\u201c\u8001\u53eb\u5316\u548c\u80e1\u3001\u6bb5\u4e24\u4f4d\u638c\u95e8\u4eba\uff0c\u90fd\u662f\u6b63\u4eba\u541b\u5b50\uff0c\u6211\u60f3\u7ed9\u4f60\u7559\u70b9\u9762\u5b50\u3002\u4f60\u8fd9\u4e48\u8ffd\u7740\u95ee\uff0c\u5927\u59d0\u5c31\u53ea\u597d\u8bf4\u6e05\u695a\u4e86\uff0c\u4f60\u81ea\u5df1\u62ff\u955c\u5b50\u7167\u7167\u770b\u3002\u90a3\u4e2a\u6a21\u6837\u5df2\u7ecf\u4f7f\u5973\u5b69\u5b50\u6026\u7136\u5fc3\u52a8\uff0c\u4f55\u51b5\u4f60\u2026\u2026\u201d \u3000\u3000\u84dd\u886b\u4eba\u5fae\u5fae\u5782\u9996\uff0c\u95ed\u53e3\u4e0d\u8a00\u3002 \u3000\u3000\u201c\u4f55\u51b5\u4ec0\u4e48\uff1f\u5927\u5bb6\u90fd\u662f\u81ea\u5df1\u4eba\uff0c\u8bf4\u51fa\u6765\u542c\u542c\uff0c\u5f7c\u6b64\u591a\u4e9b\u4e86\u89e3\uff0c\u4e5f\u591a\u4e9b\u7167\u5e94\u3002\u201d \u3000\u3000\u8bf4\u8bdd\u7684\u7adf\u662f\u4e03\u5b9d\u548c\u5c1a\u3002 \u3000\u3000\u770b\u84dd\u886b\u4eba\u5e76\u65e0\u80c6\u6b62\u7684\u610f\u601d\u3002\u5f20\u56db\u59d1\u63a5\u9053\uff1a\u201c\u9ad8\u660e\u7684\u6613\u5bb9\u672f\u4e0d\u4f46\u53ef\u4ee5\u6539\u53d8\u4e00\u4e2a\u4eba\u7684\u5916\u8c8c\uff0c\u4e5f\u8981\u6539\u53d8\u4ed6\u7684\u795e\u97f5\uff0c\u5144\u5f1f\uff0c\u4f60\u7684\u4e3e\u6b62\u884c\u52a8\uff0c\u7709\u60c5\u76ee\u8bed\uff0c\u90fd\u628a\u4e00\u4e2a\u7537\u4eba\u7684\u9b45\u529b\uff0c\u5b8c\u5168\u7684\u6563\u53d1\u51fa\u6765\uff0c\u6240\u4ee5\u3002\u5927\u59d0\u7b2c\u4e00\u6b21\u89c1\u5230\u4f60\u65f6\u3002\u5c31\u88ab\u4f60\u8f7b\u6613\u8bf4\u52a8\u4e86\uff0c\u6211\u60f3\u4e86\u5f88\u4e45\uff0c\u8fd9\u4e0d\u662f\u5148\u5929\u5177\u6709\u7684\u529b\u91cf\uff0c\u800c\u662f\u4e00\u79cd\u6b66\u529f\uff0c\u540e\u5929\u4e60\u7ec3\u800c\u6210\uff0c\u6240\u4ee5\uff0c\u6211\u624d\u82b1\u4e86\u5f88\u5927\u5de5\u592b\u66ff\u4f60\u4f5c\u4e86\u4e00\u526f\u7cbe\u81f4\u7684\u9762\u5177\uff0c\u8ba9\u4f60\u6bcf\u5929\u6234\u7740\uff0c\u628a\u4f60\u6539\u53d8\u7684\u4e11\u4e00\u4e9b\uff0c\u51b7\u5389\u4e00\u4e9b\uff0c\u4e0d\u8fc7\uff0c\u6211\u770b\u4e5f\u4e0d\u4e00\u5b9a\u6709\u591a\u5927\u7684\u6548\u7528\u2026\u2026\u201d \u3000\u3000\u201c\u6709\uff01\u975e\u5e38\u5b8f\u5927\uff0c\u90a3\u4e9b\u5973\u5b69\u5b50\u770b\u5230\u6211\uff0c\u4e00\u4e2a\u4e2a\u90fd\u5f88\u5bb3\u6015\uff0c\u539f\u6765\u662f\u5927\u59d0\u90a3\u526f\u9762\u5177\u4f5c\u602a\uff0c\u2026\u2026\u201d \u3000\u3000\u201c\u522b\u5c0f\u770b\u90a3\u526f\u9762\u5177\u3002\u8db3\u8db3\u5316\u4e86\u6211\u4e09\u4e2a\u6708\u7684\u5de5\u592b\uff0c\u7167\u7740\u4f60\u7684\u8138\u578b\u5236\u7684\uff0c\u53ea\u662f\u6211\u5728\u9762\u5177\u4e0a\u52a0\u4e86\u4e00\u4e9b\u7eb9\u8def\u3002\u770b\u4e0a\u53bb\uff0c\u6574\u4e2a\u4eba\u5c31\u53d8\u4e86\u6837\u5b50\uff0c\u4e00\u8138\u51b7\u5389\u3001\u72e0\u9177\u7684\u795e\u8272\u3002\u201d \u3000\u3000\u201c\u73b0\u5728\uff0c\u6211\u6bcf\u5929\u90fd\u5f97\u6234\u4e0a\u5b83\u3002\u6d1b\u9633\u5c45\u7684\u603b\u7ba1\uff0c\u5f88\u6b23\u8d4f\u6211\u90a3\u526f\u5bb9\u8c8c\uff0c\u6240\u4ee5\uff0c\u624d\u6d3e\u6211\u4f5c\u526f\u603b\u7ba1\u3002\u89c6\u4f5c\u5fc3\u8179\u3002\u6211\u6bcf\u5929\u90fd\u5728\u62c5\u5fc3\u88ab\u4ed6\u770b\u51fa\u7834\u7efd\u6765\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e2a\uff0c\u4f60\u53ef\u4ee5\u653e\u5fc3\u3002\u6211\u5e2e\u4f60\u5236\u4f5c\u8fd9\u526f\u4eba\u76ae\u9762\u5177\uff0c\u4e0d\u4f46\u7cbe\u5de7\u3002 \u3000\u3000\u800c\u4e14\uff0c\u548c\u4f60\u7684\u8eab\u4f53\u80a4\u8272\u3002\u5b8c\u5168\u4e00\u6837\uff0c\u4f60\u53ea\u8981\u5c0f\u5fc3\u4e00\u4e9b\uff0c\u4ed6\u7edd\u5bf9\u770b\u4e0d\u51fa\u6765\uff0c\u6211\u4e0d\u653e\u5fc3\u7684\uff0c\u5012\u662f\u90a3\u4e9b\u5973\u5b69\u5b50\uff0c\u5c24\u5176\u90a3\u56db\u5b63\u82b1\u5a62\uff0c\u4e0d\u4f46\u6b66\u529f\u9ad8\u660e\uff0c\u4e5f\u4e60\u8fc7\u6444\u9b42\u5927\u6cd5\uff0c\u4f60\u548c\u4ed6\u4eec\u53ae\u7f20\u5728\u4e00\u8d77\uff0c\u4e00\u4e2a\u5931\u795e\uff0c\u9762\u5177\u88ab\u4ed6\u4eec\u6293\u4e0b\u6765\u3002\u90a3\u53ef\u662f\u4e0d\u80fd\u602a\u6211\u3002\u201d \u3000\u3000\u201c\u5927\u59d0\u653e\u5fc3\uff0c\u5c0f\u5f1f\u7684\u5904\u4e8b\uff0c\u81ea\u6709\u5206\u5bf8\uff0c\u8001\u5b9e\u8bf4\uff0c\u76ee\u4e0b\u6c5f\u6e56\u4e0a\u80fd\u4e3a\u6211\u4eec\u52a9\u529b\u7684\u4eba\u3002\u5b9e\u5728\u5f88\u5c11\u3002\u53ea\u6709\u4ece\u4ed6\u4eec\u90a3\u91cc\u62c9\u8fc7\u6765\u4e86\uff0c\u8fd9\u4e00\u70b9\u3002\u6211\u4f1a\u5c0f\u5fc3\uff0c\u5927\u59d0\u4e0d\u7528\u62c5\u5fc3\u3002\u2026\u2026\u201d \u3000\u3000\u84dd\u886b\u4eba\u7a0d\u4f5c\u6c89\u601d\uff0c\u63a5\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u6613\u5bb9\u672f\u4e2d\uff0c\u7adf\u6709\u90a3\u4e48\u5927\u7684\u5b66\u95ee\uff1f\u5927\u59d0\u8bf4\u7684\u4e0d\u9519\uff0c\u5c0f\u5f1f\u786e\u4e60\u8fc7\u4e00\u79cd\u6b66\u529f\uff0c\u53eb\u4f5c\u2018\u79fb\u5fc3\u672f\u2019\u3002 \u3000\u3000\u662f\u4e00\u79cd\u7cbe\u795e\u548c\u610f\u5fd7\u529b\u7684\u8fd0\u7528\u3002\u5c0f\u5f1f\u7a0d\u6709\u6210\u5c31\uff0c\u8c01\u77e5\u4f1a\u6210\u4e86\u73b0\u5728\u8fd9\u4e2a\u6837\u5b50\u3002\u201d \u3000\u3000\u201c\u8fd9\u529f\u592b\u4e5f\u662f\u4f60\u5e08\u7236\u6559\u7684\u2026\u2026\u201d \u3000\u3000\u84dd\u886b\u4eba\u7565\u4e00\u6c89\u541f\uff0c\u9053\uff1a\u201c\u662f\uff01\u5e08\u957f\u4eec\u6559\u7684\u3002\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u3002\u6709\u5f88\u5927\u7684\u8bed\u75c5\u3002\u53ea\u662f\u5927\u5bb6\u90fd\u672a\u7559\u5fc3\u3002 \u3000\u3000\u201c\u8fd9\u5e08\u7236\u7684\u540d\u5802\u771f\u8fd8\u4e0d\u5c11\u3002\u6211\u5012\u771f\u60f3\u89c1\u89c1\u4ed6\u4e86\u3002\u201d \u3000\u3000\u201c\u5e08\u7236\u63a8\u5d07\u5927\u59d0\u4e3a\u8fd1\u4ee3\u6c5f\u6e56\u4e2d\u7684\u5947\u4eba\u2026\u2026\u201d \u3000\u3000\u54df\uff01\u8fd8\u6709\u4eba\u8fd9\u6837\u6367\u6211\uff01\u5176\u5b9e\u3002\u6b66\u6797\u9053\u4e0a\u7684\u4eba\u600e\u4e48\u79f0\u547c\u6211\u3002 \u3000\u3000\u6211\u5fc3\u4e2d\u660e\u767d\u5f97\u5f88\u3002\u201d \u3000\u3000\u201c\u662f\u554a\uff01\u4eca\u65e5\u8fd9\u756a\u4ea4\u8c08\uff0c\u8001\u53eb\u5316\u624d\u660e\u767d\u4ec0\u4e48\u53eb\u505a\u5dee\u4e4b\u6beb\u5398\uff0c\u7f2a\u4e4b\u5343\u91cc\u4e86\uff0c\u4ed6\u4eec\u600e\u4e48\u53ef\u4ee5\u79f0\u2026\u2026\u201d\u7a81\u7136\u4f4f\u53e3\u4e0d\u8a00\u3002 \u3000\u3000\u5f20\u56db\u5a18\u7b11\u9053\uff1a\u201c\u6015\u4ec0\u4e48\uff1f\u8bf4\u4e0b\u53bb\u554a\uff01\u4ed6\u4eec\u662f\u4e0d\u662f\u53eb\u6211\u65ad\u80a0\u5a18\u201c\u4e0d\u9519\uff01\u4e5f\u6709\u53eb\u4f60\u6b47\u82b1\u624b\u7684\u3002\u4e0d\u8fc7\uff0c\u6211\u548c\u5c1a\u7684\u770b\u6cd5\uff0c\u5012\u662f\u6613\u82b1\u624b\u3002\u6bd4\u8f83\u6070\u5f53\u4e00\u4e9b\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u90fd\u4e00\u6837\u3002\u4eba\u5bb6\u53eb\u4f60\u9152\u8089\u548c\u5c1a\uff0c\u53c8\u559d\u9152\u53c8\u5403\u8089\u7684\u548c\u5c1a\uff0c\u4e00\u5b9a\u4e0d\u662f\u597d\u548c\u5c1a\u3002\u53ef\u662f\u8c01\u77e5\u9053\u4f60\u548c\u5c1a\u5fc3\u4e2d\u6709\u4f5b\u5728\uff0c\u662f\u771f\u6b63\u732e\u8eab\u6551\u4e16\u7684\u9ad8\u50e7\u5462\uff1f\u201d \u3000\u3000\u80e1\u840d\u3001\u6bb5\u4e5d\u53ea\u542c\u5f97\u8b66\u4f69\u4e0d\u5df2\u3002\u8fd9\u4e9b\u4eba\u8bf4\u8bdd\u76f4\u6765\u76f4\u5728\uff0c\u4e5f\u4e0d\u7ba1\u522b\u4eba\u9762\u5b50\u662f\u5426\u80fd\u6302\u5f97\u4f4f\u3002\u4f46\u4ed6\u4eec\u5fc3\u4e2d\u5374\u6709\u770b\u660e\u786e\u7684\u662f\u975e\u89c2\u5ff5\u3002\u7387\u6027\u81f3\u60c5\uff0c\u4e00\u8bfa\u5343\u91d1\uff0c\u5927\u4ec1\u5927\u4e49\u6240\u5728\uff0c\u7f6e\u751f\u6b7b\u5229\u5bb3\u4e8e\u5ea6\u5916\u3002 \u3000\u3000\u53ea\u542c\u5f20\u56db\u59d1\u67d4\u67d4\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u5144\u5f1f\uff0c\u4f60\u653e\u5fc3\u4e86\uff0c\u90a3\u4e24\u4e2a\u5973\u5b69\u5b50\uff0c\u662f\u6211\u4e00\u4f4d\u95fa\u4e2d\u597d\u53cb\u7684\u5973\u513f\uff0e\u8eab\u5bb6\u6e05\u767d\uff0c\u7edd\u65e0\u53ef\u7591\uff0c\u6211\u90a3\u4f4d\u670b\u53cb\u6e29\u67d4\u5185\u5411\uff0c\u559c\u7231\u6e05\u9759\uff0c\u53c8\u5ac1\u4e86\u4e00\u4f4d\u6587\u6b66\u5168\u624d\u3002\u559c\u7231\u5c71\u6c34\u7684\u4e08\u592b\uff0c\u592b\u5987\u4e24\u4eba\u3002\u5728\u5927\u5df4\u5c71\u4e00\u5904\u79d8\u8c37\u4e2d\u3002\u7ed3\u5e90\u9762\u5c45\u3002\u81ea\u5f97\u5176\u4e50\u3002\u53ea\u53ef\u60dc\u6211\u90a3\u4f4d\u95fa\u4e2d\u597d\u53cb\u3002\u809a\u76ae\u4e0d\u4e89\u6c14\u3002\u53ea\u4f1a\u5f04\u74e6\uff0c\u8fde\u751f\u4e86\u4e24\u4e2a\u5973\u513f\uff0e\u5979\u5fc3\u4e2d\u4e0d\u670d\uff0c\u51c6\u5907\u4e00\u76f4\u751f\u4e0b\u53bb\uff0c\u76f4\u5230\u751f\u51fa\u4e2a\u513f\u5b50\u4e3a\u6b62\u3002\u5012\u662f\u5979\u8001\u516c\u770b\u7684\u5f00\uff0c\u4ed6\u8bf4\u547d\u4e2d\u65e0\u5b50\uff0c\u4f55\u5fc5\u5f3a\u6c42\uff0c\u4e8e\u8bf4\u4e00\u4e2a\u5973\u513f\u662f\u534a\u5b50\uff0c\u4e24\u4e2a\u52a0\u8d77\u6765\uff0c\u4e5f\u7b49\u4e8e\u4e00\u4e2a\u513f\u5b50\uff0c\u5176\u5b9e\u5462\uff1f\u4ed6\u4eec\u592b\u5987\u5408\u7c4d\u53cc\u4fee\uff0c\u7cbe\u7814\u6b66\u6280\uff0c\u6210\u5c31\u5f88\u9ad8\u3002\u4ed6\u662f\u6015\u72ec\u751f\u5973\u751f\u591a\u4e86\u3002\u5f71\u54cd\u5230\u5979\u7684\u6b66\u529f\u8fdb\u5883\u3002 \u3000\u3000\u4e24\u4e2a\u4e2b\u5934\u5728\u4ed6\u95e8\u592b\u5987\u8c03\u6559\u4e4b\u4e0b\uff0c\u5df2\u5f97\u771f\u4f20\uff0c\u6211\u770b\u8fc7\u4e24\u4e2a\u4e2b\u5934\u7684\u62f3\u638c\u5251\u672f\uff0c\u6211\u771f\u7684\u5df2\u7ecf\u6ca1\u4ec0\u4e48\u597d\u4f20\u5979\u4eec\u4e86\uff0c\u53ea\u6709\u62ff\u51fa\u6211\u90a3\u70b9\u538b\u7bb1\u5e95\u7684\u672c\u9886\uff0c\u65ad\u80a0\u4e09\u5251\u2026\u2026\u201d \u3000\u3000\u201c\u4f20\u7ed9\u5979\u4eec\u6ca1\u6709\uff1f\u201d\u84dd\u886b\u4eba\u6025\u6025\u5730\u95ee\u3002 \u3000\u3000\u201c\u4f20\u4e86\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u56e0\u4e3a\uff0c\u6211\u53ea\u6709\u90a3\u4e09\u62db\u5251\u6cd5\u597d\u4f20\uff0c\u73b0\u5728\uff0c\u5979\u4eec\u6b63\u5728\u82e6\u7ec3\u90a3\u4e09\u62db\u5251\u6cd5\uff0c\u770b\u5979\u4eec\u8fdb\u5883\u4e4b\u901f\uff0c\u5927\u6982\u518d\u6709\u4e2a\u4e09\u4e94\u5929\u3002\u5c31\u7ec3\u7684\u6536\u53d1\u81ea\u5982\u4e86\uff0c\u4ee5\u5979\u4eec\u7684\u806a\u660e\u624d\u667a\u800c\u8bba\u3002 \u3000\u3000\u4e09\u4e2a\u6708\u5185\u3002 \u3000\u3000\u5e94\u8be5\u53ef\u4ee5\u5b66\u4f1a\u6211\u7684\u5feb\u901f\u6613\u5bb9\u624b\u6cd5\u3002\u201d \u3000\u3000\u201c\u6211\u8bf4\u5f20\u59d1\u5a18\u554a\uff01\u65e2\u7136\u6709\u8fd9\u4e48\u597d\u7684\u4eba\u624d\u3002\u57cb\u6ca1\u6df1\u5c71\uff0c\u5c82\u4e0d\u662f\u592a\u53ef\u60dc\u4e86\u2026\u2026\u201d\u94b1\u7f3a\u8bf4\uff1a \u3000\u3000\u201c\u5982\u80fd\u8bf7\u4ed6\u4eec\u592b\u5987\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u660e\u767d\u4f60\u7684\u5fc3\u610f\u3002\u9080\u4ed6\u4eec\u4e0b\u5c71\u52a9\u62f3\u2026\u2026\u201d \u3000\u3000\u201c\u5bf9\u5bf9\u5bf9\uff01\u8001\u53eb\u5316\u5c31\u662f\u8fd9\u4e2a\u610f\u601d\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u63a5\u9053\uff1a\u201c\u6211\u8bf4\u4e0d\u51fa\u53e3\uff0c\u4ed6\u4eec\u5c71\u5c45\u4e8c\u5341\u591a\u5e74\uff0c\u8db3\u8ff9\u5c31\u672a\u79bb\u5f00\u8fc7\u542c\u8749\u5c0f\u7b51\u5341\u91cc\u4e4b\u5916\uff0c\u6c5f\u6e56\u4e0a\u7684\u4eba\u4eba\u4e8b\u4e8b\uff0c\u548c\u4ed6\u4eec\u5b8c\u5168\u65e0\u5173\u3002\u6211\u600e\u4e48\u80fd\u7834\u574f\u4ed6\u4eec\u90a3\u4efd\u5b57\u9759\u5462\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u2026\u2026\u201d\u8001\u53eb\u5316\u8f7b\u8f7b\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4eba\u751f\u4e0d\u8fc7\u767e\u5e74\uff0c\u4ed9\u9053\u4e4b\u8bf4\u3002\u8fc4\u65e0\u51ed\u8bc1\uff0c\u4f60\u5f20\u59d1\u5a18\u4e0d\u4fbf\u5f00\u53e3\uff0c\u4f55\u4e0d\u7ed9\u8001\u53eb\u5316\u4e00\u4e2a\u673a\u4f1a\uff0c\u70df\u5c01\u6c5f\u6e56\uff0c\u96fe\u9501\u6b66\u6797\uff0c\u4ed6\u4eec\u96be\u9053\u771f\u5fcd\u5fc3\u5750\u89c6\u4e0d\u95ee\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u5fae\u5fae\u4e00\u7b11\u3002\u9053\uff1a\u201c\u8001\u53eb\u5316\uff0c\u4e0d\u7528\u767d\u8d39\u5fc3\u673a\u62ff\u8bdd\u6324\u6211\u3002 \u3000\u3000\u6211\u4e0d\u8bf4\u4ed6\u4eec\u7684\u59d3\u540d\u3001\u4f4f\u5904\uff0c\u5c31\u662f\u6015\u4f60\u95e8\u53bb\u6270\u4ed6\u4eec\u7684\u6e05\u4fee\uff0c\u4e0d\u8fc7\uff0c\u6709\u4e00\u70b9\uff0c\u6211\u5012\u662f\u89c9\u5f97\u5947\u602a\u2026\u2026\u201d \u3000\u3000\u76ee\u5149\u8f6c\u6ce8\u5230\u84dd\u8863\u5c11\u5e74\u8eab\u4e0a\uff0c\u63a5\u9053\uff1a\u201c\u4f60\u8981\u6211\u627e\u4e2a\u8863\u94b5\u4f20\u4eba\uff0c\u6211\u5c31\u60f3\u5230\u4e86\u4e24\u4e2a\u4e2b\u5934\u3002\u6211\u8fd9\u91cc\u4e00\u5f00\u53e3\uff0c\u4ed6\u4eec\u7acb\u523b\u7b54\u5e94\uff0c\u7b49\u6211\u89c1\u8bc6\u5230\u4e86\u4e24\u4e2a\u4e2b\u5934\u6b66\u529f\u3002\u5251\u6cd5\uff0c\u771f\u628a\u6211\u5413\u4e86\u4e00\u8df3\u3002 \u3000\u3000\u60f3\u6765\u60f3\u53bb\uff0c\u600e\u4e48\u4e09\u8a00\u4e24\u8bed\uff0c\u5c31\u628a\u751f\u957f\u6df1\u5c71\uff0c\u672a\u5c65\u7ea2\u5c18\u7684\u4e24\u4e2a\u5fc3\u809d\u5b9d\u8d1d\u4ea4\u7ed9\u6211\uff0c\u5c82\u771f\u662f\u5927\u59d0\u6211\u8fd9\u6613\u5bb9\u624b\u6cd5\uff0c\u662f\u5343\u53e4\u7edd\u54cd\uff0c\u4e24\u4e2a\u4e03\u60c5\u4e0d\u52a8\u7684\u9ad8\u5c71\u9690\u58eb\uff0c\u7adf\u4e5f\u4f1a\u4e3a\u6211\u8fd9\u96d5\u866b\u5c0f\u6280\u9676\u9189\u3002\u201d \u3000\u3000\u201c\u662f\u554a\uff01\u5927\u59d0\u7684\u6613\u5bb9\u72ec\u6b65\u6c5f\u6e56\u2026\u2026\u201d\u84dd\u8863\u4eba\u8bf4\uff1a\u201c\u5c31\u662f\u5c0f\u5f1f\u4e5f\u60f3\u2026\u2026\u201d\u201c\u5c11\u704c\u5927\u59d0\u7c73\u6c64\u2026\u2026\u201d\u5f20\u56db\u59d1\u8bf4\uff1a\u201c\u8fd9\u4e2d\u95f4\u53ef\u80fd\u522b\u6709\u8e4a\u8df7\u2026\u2026\u201d \u3000\u3000\u201c\u6709\u9053\u7406\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u63a5\u9053\uff1a\u201c\u5c71\u96e8\u6b32\u6765\u98ce\u6ee1\u697c\uff0c\u4ee5\u8d35\u53cb\u592b\u5987\u7684\u7075\u6167\u3002\u4e5f\u8bb8\u65e9\u5df2\u6d1e\u5bdf\u5148\u673a\uff0c\u4e8c\u5341\u5e74\u57cb\u9996\u4e60\u5251\uff0c\u5c82\u662f\u65e0\u4e3a\u3002\u201d \u3000\u3000\u201c\u6211\u4e5f\u7422\u78e8\u8fc7\u8fd9\u4ef6\u4e8b\u60c5\u2026\u2026\u201d\u5f20\u56db\u59d1\u8bf4\uff1a\u201c\u4ed6\u4eec\u628a\u4e24\u4e2a\u5973\u513f\u4ea4\u7ed9\u6211\u3002\u90a3\u662f\u8bf4\u660e\u4e86\u4ed6\u4eec\u8fd8\u505a\u4e0d\u5230\u65a9\u60c5\u706d\u6027\uff0c\u65ad\u4e49\u7edd\u4eb2\u2026\u2026\u201d \u3000\u3000\u76ee\u5149\u53c8\u8f6c\u5230\u90a3\u84dd\u886b\u4eba\u7684\u8138\u4e0a\uff0c\u63a5\u9053\uff1a\u201c\u4e24\u4e2a\u4e2b\u5934\u592a\u7eaf\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u5927\u59d0\u4e0d\u662f\u8bf4\u5979\u4eec\u5f88\u806a\u660e\u5417\uff1f\u2026\u2026\u201d \u3000\u3000\u201c\u806a\u660e\u5f52\u806a\u660e\uff0c\u4f46\u5979\u95e8\u89c1\u8bc6\u592a\u5c11\u4e86\uff0c\u9664\u4e86\u5979\u4eec\u7684\u7236\u6bcd\u4e4b\u5916\u3002 \u3000\u3000\u63a5\u89e6\u8fc7\u7684\u4eba\uff0c\u5c31\u53ea\u6709\u6211\uff0c\u7b49\u6211\u628a\u5979\u4eec\u4ea4\u7ed9\u4f60\u65f6\uff0c\u4f60\u5c31\u662f\u5979\u4eec\u7b2c\u4e00\u6b21\u63a5\u89e6\u7684\u5e74\u8f7b\u7537\u4eba\uff0c\u6211\u62c5\u5fc3\u2026\u2026\u201d \u3000\u3000\u201c\u5927\u59d0\u3002\u4e0d\u7528\u62c5\u5fc3\uff0c\u5979\u4eec\u662f\u5e2e\u6211\u5bf9\u4ed8\u5f3a\u654c\uff0c\u4e0d\u8bba\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\uff0c\u5979\u4eec\u90fd\u8981\u81ea\u5df1\u627f\u62c5\uff0c\u5927\u59d0\u80fd\u591f\u5e2e\u5979\u4eec\u7684\uff0c\u5c31\u662f\u591a\u544a\u8bc9\u5979\u4eec\u4e00\u4e9b\u6c5f\u6e56\u4e2d\u7684\u9669\u6076\uff0c\u544a\u8bc9\u5979\u4eec\u4e00\u4e9b\u9632\u8303\u7537\u4eba\u7684\u672c\u9886\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u51b2\u7740\u6211\u548c\u5979\u4eec\u6bcd\u4eb2\u90a3\u4efd\u4ea4\u60c5\uff0c\u6211\u4e0d\u80fd\u4e0d\u62a4\u7740\u5979\u4eec\u4e00\u4e9b\u2026\u2026\u201d \u3000\u3000\u201c\u5982\u679c\u5979\u4eec\u771f\u7684\u667a\u6167\u8fc7\u4eba\uff0c\u53c8\u6709\u4e00\u8eab\u9ad8\u660e\u7684\u6b66\u529f\uff0c\u518d\u52a0\u4e0a\u5927\u59d0\u77ac\u606f\u5343\u53d8\u7684\u6613\u5bb9\u624b\u6cd5\uff0c\u5e94\u8be5\u53ef\u4ee5\u81ea\u4fdd\u4e86\uff0c\u6211\u53ea\u80fd\u7b54\u5e94\u5927\u59d0\uff0c\u6211\u8bf7\u4ed6\u4eec\u4f5c\u4e8b\u4e4b\u524d\uff0c\u4e00\u5b9a\u7ed9\u5979\u4eec\u8bf4\u6e05\u695a\uff0c\u7531\u5979\u4eec\u81ea\u5df1\u51b3\u5b9a\uff0c\u7edd\u4e0d\u76f8\u5f3a\u3002\u201d \u3000\u3000\u201c\u8c22\u4e86\uff0c\u5144\u5f1f\uff0c\u6709\u4f60\u8fd9\u4e48\u51e0\u53e5\u8bdd\uff0c\u7b97\u6211\u5df2\u7ecf\u5c3d\u4e86\u5fc3\u529b\u3002\u201d \u3000\u3000\u516d\u5b9d\u548c\u5c1a\u7a81\u7136\u5e72\u4e86\u9762\u524d\u7684\u9152\u676f\uff0c\u9053\uff1a\u201c\u6211\u8bf4\u526f\u603b\u7ba1\u5927\u4eba\uff0c\u6211\u4eec\u56db\u4e2a\u4eba\u5e74\u7eaa\u867d\u7136\u5927\u4e86\u4e00\u4e9b\uff0c\u53ef\u662f\u6211\u4eec\u65e9\u5df2\u628a\u751f\u6b7b\u4e22\u7f6e\u8111\u540e\uff0c\u526f\u603b\u7ba1\u770b\u770b\u6211\u4eec\u6709\u4e9b\u4ec0\u4e48\u7528\u5904\uff1f\u201d \u3000\u3000\u84dd\u886b\u4eba\u6025\u6025\u7ad9\u8d77\uff0c\u62b1\u62f3\u4e00\u63d6\uff0c\u9053\uff1a\u201c\u6b63\u8981\u501f\u91cd\u56db\u4f4d\u5927\u529b\uff0c\u2026\u2026\u201d \u3000\u3000\u201c\u4f60\u5df2\u5728\u6697\u4e2d\u76d1\u89c6\u4e86\u6211\u4eec\u5f88\u4e45\uff0c\u5927\u6982\u4e5f\u770b\u8fc7\u6211\u4eec\u7ec3\u4e60\u6b66\u529f\u7684\u8fc7\u7a0b\u4e86\u2026\u2026\u201d\u8001\u53eb\u5316\u8bf4\uff1a \u3000\u3000\u201c\u4f60\u770b\u770b\uff0c\u6211\u4eec\u90a3\u70b9\u6280\u827a\u80fd\u62c5\u5f53\u4ec0\u4e48\u5dee\u4f7f\u2026\u2026\u201d \u3000\u3000\u201c\u5947\u602a\u4e86\u2026\u2026\u201d\u5f20\u56db\u59d1\u683c\u683c\u5927\u7b11\uff0c\u9053\uff1a\u201c\u4e00\u5411\u81ea\u8d1f\u7684\u8001\u53eb\u5316\u5b50\uff0c\u600e\u4e48\u53d8\u5f97\u8fd9\u6837\u8c26\u865a\u8d77\u6765\u4e86\u3002\u201d \u3000\u3000\u201c\u6c5f\u6e56\u8d8a\u8001\uff0c\u80c6\u5b50\u8d8a\u5c0f\uff0c\u4f55\u51b5\uff0c\u80fd\u8ba9\u4f60\u6613\u82b1\u624b\u5f20\u56db\u59d1\u670d\u5e16\u7684\u4eba\uff0c\u8001\u53eb\u5316\u5c82\u80fd\u4e0d\u670d\u3002\u201d \u3000\u3000\u84dd\u886b\u4eba\u8f7b\u8f7b\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5f88\u62b1\u6b49\uff0c\u665a\u8f88\u786e\u5728\u6697\u4e2d\u76d1\u89c6\u8fc7\u8bf8\u4f4d\uff0c\u4e5f\u770b\u5230\u4e86\u8bf8\u76f8\u6253\u7834\u964b\u89c4\uff0c\u4e92\u76f8\u4f20\u6388\u7edd\u6280\uff0c\u4ee5\u56db\u4f4d\u76ee\u4e0b\u7684\u6b66\u529f\u800c\u8a00\uff0c\u8db3\u4ee5\u548c\u4ed6\u4eec\u4e2a\u5bf9\u4e2a\u653e\u624b\u4e00\u640f\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u4ed6\u4eec\u8bad\u7ec3\u4e86\u5f88\u591a\u9ad8\u624b\uff0c\u800c\u4e14\u6280\u827a\u535a\u6742\uff0c\u4e0d\u5355\u662f\u6b66\u529f\u53ef\u6b32\u5339\u654c\uff0c\u4f55\u51b5\uff0c\u76ee\u4e0b\u6700\u91cd\u8981\u7684\u4e00\u4ef6\u4e8b\u3002\u662f\u8981\u6c5f\u6e56\u540c\u9053\u89c9\u9192\u3002\u54b1\u4eec\u513f\u4e2a\u4eba\u7684\u529b\u91cf\uff0c\u5c31\u7b97\u6b66\u529f\u518d\u9ad8\u4e00\u4e9b\u3002\u4e5f\u65e0\u6cd5\u548c\u4ed6\u95e8\u6297\u62d2\u2026\u2026\u201d \u3000\u3000\u80e1\u840d\u63a5\u9053\uff1a\u201c\u4ed6\u4eec\u662f\u4e0d\u662f\u5c0f\u897f\u5929\u96f7\u97f3\u5bfa\u7684\u9b54\u6559\u4e2d\u4eba\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4eec\u5177\u6709\u9b54\u6559\u90aa\u5f02\u6b66\u529f\u2026\u2026\u201d\u84dd\u886b\u4eba\u8bf4\uff1a\u201c\u90a3\u662f\u8bf4\u660e\u4e86\u6709\u9b54\u6559\u4e2d\u7684\u4eba\u53c2\u4e0e\uff0c\u4f46\u4ed6\u4eec\u6b66\u529f\u7684\u535a\u6742\uff0c\u5305\u542b\u4e86\u4e2d\u539f\u5404\u5927\u95e8\u6d3e\u7684\u6280\u827a\uff0c\u6d1b\u9633\u5c45\u53ea\u4e0d\u8fc7\u662f\u4ed6\u4eec\u4e00\u4e2a\u805a\u96c6\u91d1\u94b1\u7684\u636e\u70b9\uff0c\u6d3e\u6765\u7684\u4eba\u624b\uff0c\u4e5f\u4e0d\u662f\u7b2c\u4e00\u6d41\u7684\u95e8\u4eba\u5f1f\u5b50\uff0c\u4ed6\u4eec\u53ea\u662f\u8d1f\u8d23\u7ecf\u8425\u3001\u4fdd\u62a4\u6d1b\u9633\u5c45\u7684\u751f\u610f\u3002\u201d \u3000\u3000\u6bb5\u4e5d\u9053\uff1a\u201c\u4ed6\u4eec\u7a76\u7adf\u662f\u4e9b\u4ec0\u4e48\u4eba\u5462\uff1f\u4e3a\u4ec0\u4e48\u90a3\u4e9b\u7537\u5f79\u5973\u4f8d\uff0c\u90fd\u662f\u7ecf\u8fc7\u8bad\u7ec3\u7684\u9ad8\u624b\uff1f\u5355\u662f\u90a3\u4efd\u9047\u4e8b\u7684\u9547\u9759\u3001\u6c89\u7740\uff0c\u5c31\u4e0d\u662f\u4e00\u822c\u6c5f\u6e56\u95e8\u6d3e\u5f1f\u5b50\u80fd\u53ca\u3002\u201d \u3000\u3000\u201c\u5509\uff01\u5728\u4e0b\u6df7\u5165\u4e86\u6d1b\u9633\u5c45\u3002\u5df2\u6709\u534a\u5e74\u4e4b\u4e45\u3002\u4f46\u5374\u4e00\u76f4\u67e5\u4e0d\u51fa\u4ed6\u4eec\u6765\u5386\u2026\u2026\u201d\u84dd\u886b\u4eba\u8bf4\uff1a \u3000\u3000\u201c\u66f4\u53ef\u6015\u7684\u90a3\u4e9b\u7537\u4ec6\u5973\u4f8d\uff0c\u90fd\u662f\u4e2d\u539f\u6c49\u4eba\u7684\u5b50\u5f1f\uff0c\u4ed6\u4eec\u867d\u6709\u9b54\u6559\u4e2d\u901f\u6210\u624b\u6cd5\u5e2e\u52a9\u3002\u4f46\u4e5f\u8be5\u8981\u4e94\u5e74\u4ee5\u4e0a\u7684\u65f6\u95f4\uff0c\u624d\u80fd\u7ec3\u51fa\u90a3\u7b49\u8eab\u624b\uff0c\u8fd9\u662f\u4e2a\u8ba1\u5212\u5b8c\u5584\u7684\u5927\u9634\u8c0b\uff0c\u81f3\u5c11\u4ed6\u4eec\u5df2\u6709\u5341\u5e74\u4ee5\u4e0a\u7684\u5145\u5206\u51c6\u5907\uff0c\u624d\u80fd\u6709\u90a3\u4e9b\u6210\u5c31\uff0c\u665a\u8f88\u4e3a\u4e86\u9690\u5bc6\u8eab\u4efd\uff0c\u4e0d\u6562\u91c7\u53d6\u5927\u80c6\u7684\u884c\u52a8\uff0c\u4ee5\u81f4\u4e8e\u5f88\u591a\u7591\u56e2\u3002 \u3000\u3000\u65e0\u6cd5\u89e3\u5f00\uff1f\u201d \u3000\u3000\u201c\u8001\u5f1f\uff0c\u8001\u53eb\u5316\u5148\u8981\u8bf7\u6559\u4e00\u4ef6\u4e8b\uff0c\u6211\u4eec\u8be5\u600e\u4e48\u79f0\u547c\u4f60\uff1f\u201d \u3000\u3000\u201c\u665a\u8f88\u6c5f\u67ab\u2026\u2026\u201d\u84dd\u8863\u4eba\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f46\u665a\u8f88\u5728\u6d1b\u9633\u5c45\u7684\u540d\u5b50\u5374\u53eb\u97e9\u9738\uff0c\u526f\u603b\u7ba1\u97e9\u9738\u3002\u201d \u3000\u3000\u201c\u6ee1\u5c71\u67ab\u53f6\u7ea2\uff0c\u5c3d\u662f\u79bb\u4eba\u6cea\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u67ab\u7ea2\u72ec\u5360\u4e00\u5b63\u79cb\uff0c\u8fd9\u540d\u5b57\u8d77\u7684\u597d\u82cd\u7edd\u3002\u201d \u3000\u3000\u6c5f\u67ab\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5927\u59d0\uff0c\u4e07\u6c11\u82cd\u751f\u4e4b\u95f4\u3002\u540d\u5b57\u4e8e\u4eba\u3002\u53ea\u4e0d\u8fc7\u662f\u4fbf\u4e8e\u79f0\u547c\u7684\u7b26\u53f7\u7f62\u4e86\uff0c\u672a\u5fc5\u5c31\u6709\u90a3\u4e48\u6df1\u5965\u7684\u610f\u4e49\u5427\uff01\u201d \u3000\u3000\u201c\u4f46\u613f\u5982\u6b64\u4e86\uff0c\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u6211\u8bb0\u5f97\u4f60\u8bf4\u8fc7\u3002\u8fd9\u540d\u5b57\u662f\u4f60\u5e08\u7236\u53d6\u7684\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u9519\u2026\u2026\u201d\u6c5f\u67ab\u82e6\u7b11\u9053\uff1a\u201c\u5e08\u7236\u6536\u517b\u6211\u65f6\uff0c\u6211\u8fd8\u5e7c\u5c0f\uff0c\u8001\u5b9e\u8bf4\uff0c\u6211\u5e94\u8be5\u662f\u4e00\u4e2a\u5b64\u513f\uff0e\u662f\u4e0d\u662f\u771f\u7684\u59d3\u6c5f\u3002\u6211\u81ea\u5df1\u4e5f\u4e0d\u6e05\u695a\uff1f\u201d \u3000\u3000\u80e1\u840d\u5fc3\u4e2d\u5fd6\u9053\uff1a\u8fd9\u5f20\u56db\u59d1\u8bf4\u8bdd\u76f4\u6765\u76f4\u5f80\uff0c\u4f46\u5230\u4e86\u9aa8\u8282\u773c\u4e0a\uff0c\u7adf\u7136\u80fd\u6587\u4e0a\u51e0\u53e5\u3002\u771f\u60f3\u4e0d\u5230\u554a\uff01 \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u8f7b\u8f7b\u54b3\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5982\u4eca\u548c\u5c1a\u662f\u9152\u8db3\u996d\u9971\uff0c\u4e5f\u8be5\u542c\u542c\u6c5f\u65bd\u4e3b\u7684\u9ad8\u89c1\u4e86\uff1f\u201d \u3000\u3000\u201c\u597d\u4e00\u4e2a\u9152\u8089\u548c\u5c1a\u2026\u2026\u201d\u5f20\u56db\u59d1\u7b11\u9053\uff1a\u201c\u679c\u7136\u662f\u8179\u4e2d\u6709\u5b9d\u2026\u2026\u201d \u3000\u3000\u76ee\u5149\u8f6c\u5230\u6c5f\u67ab\u8eab\u4e0a\uff0c\u63a5\u9053\uff1a\u201c\u8bf4\u5427\uff01\u5927\u59d0\u4e5f\u60f3\u542c\u542c\u4f60\u7684\u9526\u56ca\u5999\u8ba1\u3002\u201d \u3000\u3000\u6c5f\u67ab\u7684\u8138\u8272\u3002\u7a81\u7136\u95f4\u4e25\u8083\u8d77\u6765\u3002\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u8fd9\u4e0d\u662f\u4e00\u822c\u7684\u6c5f\u6e56\u7eb7\u4e89\uff0c\u800c\u662f\u4e2d\u539f\u6b66\u6797\u9053\u4e0a\uff0c\u4ece\u672a\u6709\u8fc7\u7684\u4e00\u573a\u52ab\u96be\uff0c\u4ee5\u5f80\uff0c\u89e3\u51b3\u6c5f\u6e56\u7eb7\u4e89\u7684\u624b\u6bb5\u3001\u65b9\u6cd5\uff0c\u90fd\u5df2\u4e0d\u80fd\u6d3e\u4e0a\u7528\u573a\uff0c\u6211\u4eec\u5fc5\u9700\u5148\u627e\u51fa\u654c\u4eba\u662f\u8c01\uff1f\u654c\u4eba\u5728\u90a3\u91cc\uff0c\u2026\u2026\u201d \u3000\u3000\u201c\u62bd\u4e1d\u5265\u8327\u2026\u2026\u201d\u8001\u53eb\u5316\u8bf4\uff1a\u201c\u6d1b\u9633\u5c45\u65e2\u662f\u4ed6\u4eec\u7684\u636e\u70b9\u4e4b\u4e00\u3002 \u3000\u3000\u4f55\u4e0d\u5c31\u5728\u8fd9\u91cc\u4e0b\u624b\u3002\u90a3\u4e9b\u7537\u4ec6\u3001\u5973\u4f8d\u3002\u90fd\u662f\u53d7\u8fc7\u591a\u5e74\u8bad\u7ec3\u7684\u4eba\u3002 \u3000\u3000\u6293\u4e00\u4e24\u4e2a\u6765\u903c\u95ee\u53e3\u4f9b\u3002\u81f3\u5c11\u53ef\u4ee5\u627e\u51fa\u4ed6\u4eec\u5728\u90a3\u91cc\u5b66\u4e60\u6b66\u529f\uff0c\u4ec0\u4e48\u4eba\u4f20\u6388\u4ed6\u4eec\u7684\u827a\u4e1a\uff0c\u5faa\u7ebf\u8ffd\u7d22\u4e0b\u53bb\uff0c\u4e0d\u96be\u627e\u51fa\u5934\u7eea\u3002\u201d \u3000\u3000\u201c\u6211\u4e5f\u662f\u8fd9\u6837\u7684\u60f3\u6cd5\u3002\u6240\u4ee5\uff0c\u6210\u6df7\u5165\u4e86\u6d1b\u9633\u5c45\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a \u3000\u3000\u201c\u4f46\u5c31\u6211\u8fd9\u534a\u5e74\u89c2\u5bdf\u6240\u5f97\uff0c\u4ed6\u4eec\u6240\u77e5\u7684\u592a\u6709\u9650\u4e86\uff0c\u4ed6\u4eec\u90fd\u662f\u88ab\u9009\u62e9\u3001\u62b1\u517b\u7684\u513f\u7ae5\uff0c\u4ee5\u5404\u4eba\u7684\u8d44\u8d28\uff0c\u53d7\u4e0d\u540c\u8bad\u7ec3\uff0c\u5b66\u4e60\u4e0d\u540c\u7684\u6b66\u529f\uff0c\u6d1b\u9633\u5c45\u4e2d\u7684\u7537\u4ec6\u3001\u5973\u4f8d\uff0c\u90fd\u5f88\u5e74\u8f7b\uff0c\u5927\u90fd\u662f\u4ed6\u4eec\u4ece\u5c0f\u57f9\u517b\u51fa\u6765\u7684\u4eba\u3002\u4ed6\u4eec\u6709\u540d\u6709\u59d3\u3002\u4f46\u5374\u90fd\u4e0d\u77e5\u9053\u7236\u6bcd\u662f\u8c01\u3002\u6240\u4ee5\uff0c\u4ed6\u4eec\u7684\u5bb6\u65cf\u89c2\u5ff5\u5f88\u8c08\u3002 \u3000\u3000\u6ca1\u6709\u4eb2\u60c5\u80fd\u5f71\u54cd\u4ed6\u4eec\u3002\u4ed6\u4eec\u5404\u5b66\u4e86\u4e00\u8eab\u672c\u9886\uff0c\u5374\u6ca1\u6709\u4e00\u4e2a\u56fa\u5b9a\u7684\u5e08\u7236\u3002 \u3000\u3000\u56e0\u6b64\u3002\u4e5f\u6ca1\u6709\u5e08\u5f92\u76f8\u5904\u7684\u60c5\u5206\uff0c\u8fd9\u4e9b\u7ba1\u6559\u7684\u624b\u6bb5\u3001\u65b9\u5f0f\uff0c\u628a\u5979\u4eec\u8bad\u7ec3\u6210\u4e86\u65ad\u60c5\u7edd\u4eb2\u51b7\u9177\u6027\u683c\uff0c\u4e5f\u5177\u6709\u4e86\u7edd\u5bf9\u7684\u53ef\u5851\u6027\uff0c\u4ed6\u4eec\u80fd\u5728\u6781\u77ed\u7684\u65f6\u95f4\u63a5\u53d7\u804c\u4e1a\u4e0a\u7684\u5b8c\u5584\u8bad\u7ec3\u3002\u80fd\u591f\u4e0d\u62e9\u4efb\u4f55\u624b\u6bb5\u7684\u5168\u529b\u4ee5\u8d74\u2026\u2026\u201d \u3000\u3000\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u9053\uff1a\u201c\u4ed6\u4eec\u5fc3\u4e2d\u662f\u4e00\u7247\u7a7a\u767d\uff0c\u8c01\u80fd\u586b\u4e0a\u90a3\u7247\u7a7a\u767d\uff0c\u5c31\u53ef\u4ee5\u8fd0\u7528\u4ed6\u4eec\u4e86\uff1f\u201d \u3000\u3000\u201c\u95ee\u7684\u597d\u2026\u2026\u201d\u6c5f\u67ab\u7b11\u9053\uff1a\u201c\u5927\u5e08\u679c\u7136\u662f\u80f8\u6000\u7985\u673a\uff0c\u4e0d\u8fc7\uff0c\u4ed6\u4eec\u65e9\u5df2\u88ab\u586b\u8865\u4e86\u8fd9\u4e2a\u7a7a\u9699\uff0c\u2026\u2026\u201d \u3000\u3000\u201c\u8fd9\u8bdd\u600e\u4e48\u8bf4\u5462\uff1f\u2026\u2026\u201d\u8001\u53eb\u5316\u5947\u9053\uff1a\u201c\u5c82\u4e0d\u662f\u81ea\u76f8\u77db\u76fe\uff1f\u201d \u3000\u3000\u5f20\u56db\u59d1\u63a5\u9053\uff1a\u201c\u5144\u5f1f\uff0c\u4f60\u662f\u5426\u5df2\u7ecf\u627e\u51fa\u4e86\u4e00\u4e9b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u6211\u4e0d\u80fd\u80af\u5b9a\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u6211\u53ea\u662f\u53d1\u89c9\u4e86\u4ed6\u4eec\u88ab\u4e00\u79cd\u795e\u79d8\u7684\u529b\u91cf\u6240\u63a7\u5236\uff0c\u5f62\u8bf8\u4e8e\u5916\u7684\u662f\u4e00\u79cd\u7edd\u5bf9\u7684\u670d\u4ece\uff0c\u2026\u2026\u201d \u3000\u3000\u6bb5\u4e5d\u7a81\u7136\u63a5\u53e3\u9053\uff1a\u201c\u6c5f\u5144\uff0c\u6055\u6bb5\u67d0\u611a\u62d9\uff0c\u80fd\u4e0d\u80fd\u8bf4\u7684\u66f4\u6e05\u695a\u4e00\u4e9b\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u6574\u4e2a\u7684\u4e8b\u4ef6\uff0c\u5c31\u5145\u6ee1\u7740\u4e00\u79cd\u795e\u79d8\uff0c\u4e3e\u4e2a\u4f8b\u5b50\u8bf4\u5427\uff01\u90a3\u4e9b\u4eba\u548c\u6211\u5168\u65e0\u5173\u7cfb\uff0c\u4f46\u56e0\u4e3a\u6211\u662f\u526f\u603b\u7ba1\uff0c\u8d1f\u8d23\u7ba1\u7406\u996d\u5e84\u90e8\u95e8\uff0c\u5728\u8fd9\u91cc\u5de5\u4f5c\u7684\u4eba\uff0c\u90fd\u552f\u6211\u4e4b\u547d\u662f\u4ece\uff0c\u90a3\u4e0d\u662f\u56e0\u4e3a\u6211\u7684\u6b66\u529f\u9ad8\u8fc7\u5979\u4eec\u3002\u8001\u5b9e\u8bf4\uff0c\u5979\u4eec\u6839\u672c\u4e0d\u77e5\u9053\u6211\u7684\u827a\u4e1a\u4fee\u517b\u5230\u4ec0\u4e48\u7a0b\u5ea6\uff1f\u5979\u4eec\u656c\u754f\u6211\uff0c\u53ea\u56e0\u4e3a\u6211\u5360\u4e86\u526f\u603b\u7ba1\u90a3\u4e2a\u804c\u4f4d\uff0c\u5979\u4eec\u7684\u53ef\u8d35\u4e4b\u5904\uff0c\u5c31\u5728\u90a3\u7edd\u5bf9\u670d\u4ece\u547d\u4ee4\u7684\u7cbe\u795e\u2026\u2026\u201d \u3000\u3000\u201c\u6c5f\u5144\u53ef\u662f\u6307\u90a3\u6625\u82b1\u56db\u5a62\u800c\u8a00\uff1f\u2026\u2026\u201d\u80e1\u840d\u9053\uff1a\u201c\u4f46\u90a3\u4e9b\u7537\u3001\u5973\u4f8d\u5f79\uff0c\u662f\u5426\u4e5f\u4e00\u6837\u7684\u656c\u754f\u4f60\u8fd9\u4e2a\u526f\u603b\u7ba1\u5462\uff1f\u201d \u3000\u3000\u201c\u56db\u5b63\u82b1\u5a62\uff0c\u5b8c\u5168\u4e00\u6837\u2026\u2026\u201d\u6c5f\u67ab\u8bf4\uff1a\u201c\u7537\u3001\u5973\u4f8d\u5f79\uff0c\u4e5f\u4e00\u4f53\u542c\u547d\uff0c\u8fd9\u5c31\u9020\u6210\u4e86\u4e00\u79cd\u6c14\u6c1b\u3001\u538b\u529b\uff0c\u5bb3\u5f97\u6211\u5bf9\u603b\u7ba1\uff0c\u4e5f\u8981\u6446\u51fa\u4e00\u526f\u767e\u4f9d\u767e\u987a\u7684\u67b6\u5f0f\uff0c\u66f4\u91cd\u8981\u7684\u662f\uff0c\u53ea\u8981\u6709\u4e00\u4e2a\u804c\u4f4d\u8f83\u9ad8\u7684\u4eba\u5728\u573a\u3002\u7acb\u523b\u5c31\u5f62\u6210\u4e86\u8fdb\u9000\u6709\u5e8f\u7684\u56e2\u4f53\uff0c\u4e0d\u8bba\u53d1\u751f\u4efb\u4f55\u7684\u53d8\u6545\uff0c\u90fd\u4e0d\u81f4\u4e71\u6210\u4e00\u56e2\u2026\u2026\u201d \u3000\u3000\u201c\u5144\u5f1f\uff0c\u4f60\u7684\u610f\u601d\u662f\u8bf4\uff0c\u4ed6\u4eec\u5df2\u88ab\u8bad\u7ec3\u6210\u4e00\u79cd\u5de5\u5177\u2026\u2026\u201d\u5f20\u56db\u59d1\u76b1\u8d77\u4e86\u7709\u5934\u8bf4\u3002 \u3000\u3000\u201c\u5509\uff01\u770b\u6765\u5c31\u662f\u5982\u6b64\u4e86\u2026\u2026\u201d\u6c5f\u67ab\u82e6\u7b11\u4e4b\u4e0b\uff0c\u9053\uff1a\u201c\u9a6f\u670d\u4e00\u4e2a\u4eba\u7edd\u5bf9\u542c\u4ece\u67d0\u4e00\u4eba\u7684\u547d\u4ee4\uff0c\u5e76\u4e0d\u662f\u592a\u96be\u7684\u4e8b\uff0c\u4f46\u8981\u4ed6\u4eec\u80fd\u591f\u5f88\u9a6f\u670d\u7684\u63a5\u53d7\u4e00\u79cd\u5236\u5ea6\u6027\u7684\u670d\u4ece\uff0c\u90a3\u5c31\u4e0d\u662f\u4e00\u4ef6\u5bb9\u6613\u7684\u4e8b\u4e86\u3002\u201d \u3000\u3000\u80e1\u840d\u9053\uff1a\u201c\u8fd9\u4e0d\u662f\u6b63\u5408\u4e86\u4e2d\u539f\u6b66\u6797\u9053\u4e0a\u7684\u4f20\u627f\u6cd5\u5219\u5417\uff1f\u4ed6\u4eec\u5229\u7528\u5236\u5ea6\u6027\uff0c\u5efa\u7acb\u4e86\u9886\u5bfc\u7684\u6743\u5a01\u3002\u201d \u3000\u3000\u201c\u6709\u5f88\u5927\u7684\u4e0d\u540c\u2026\u2026\u201d\u6c5f\u67ab\u8bf4\uff1a\u201c\u4ed6\u4eec\u6ca1\u6709\u660e\u663e\u7684\u5206\u7ea7\u5236\u5ea6\uff0c\u4ed6\u4eec\u6240\u4ee5\u7edd\u5bf9\u670d\u4ece\uff0c\u6765\u81ea\u4e00\u79cd\u6743\u5a01\u529b\u91cf\u7684\u8f6c\u5ac1\uff0c\u7528\u4e00\u79cd\u4ec0\u4e48\u65b9\u6cd5\u3002\u80fd\u8fbe\u5230\u8fd9\u4e2a\u76ee\u7684\uff0c\u624d\u662f\u5173\u952e\u6240\u5728\u3002\u6211\u5e0c\u671b\u80fd\u5c3d\u5feb\u7684\u627e\u51fa\u8fd9\u4e2d\u95f4\u539f\u56e0\u3002\u2026\u2026\u201d \u3000\u3000\u201c\u6709\u4e00\u70b9\u7709\u76ee\u5417\uff1f\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u5982\u662f\u627e\u4e0d\u51fa\u5173\u952e\u539f\u56e0\uff0c\u90a3\u5c31\u518d\u60f3\u522b\u7684\u529e\u6cd5\u2026\u2026\u201d \u3000\u3000\u201c\u4ec0\u4e48\u529e\u6cd5\u2026\u2026\u201d\u94b1\u7f3a\u6025\u6025\u5730\u95ee\u9053\uff1a\u201c\u5feb\u4e9b\u8bf4\u51fa\u6765\u3002\u5927\u5bb6\u4e5f\u53ef\u4ee5\u5546\u91cf\u4e00\u4e0b\u3002\u201d \u3000\u3000\u201c\u4eba\u6027\u2026\u2026\u201d\u6c5f\u67ab\u8bf4\uff1a\u201c\u4ed6\u4eec\u7684\u8bad\u7ec3\u76f8\u5f53\u6210\u529f\uff0c\u4f46\u4ed6\u4eec\u5ffd\u7565\u4e86\u4eba\u6027\u4e2d\u7684\u7231\u6068\u6076\u6b32\uff0c\u4ed6\u4eec\u53d7\u5230\u632b\u8d25\u4e4b\u65f6\uff0c\u9700\u8981\u522b\u4eba\u7684\u5b89\u6170\uff0c\u4ed6\u4eec\u6210\u529f\u65f6\u4e5f\u9700\u8981\u522b\u4eba\u7684\u8d5e\u8d4f\u3002\u201d \u3000\u3000\u201c\u8fd9\u5927\u6982\u9700\u8981\u4e00\u6bb5\u5f88\u957f\u7684\u65f6\u95f4\uff0c\u548c\u4ed6\u95e8\u76f8\u5904\uff0c\u624d\u80fd\u529e\u5f97\u5230\u5427\u2026\u2026\u201d\u94b1\u7f3a\u8bf4\u3002 \u3000\u3000\u201c\u6240\u4ee5\u3001\u6211\u9700\u8981\u4e00\u6279\u5e74\u8f7b\u7684\u670b\u53cb\u3002\u5fc5\u8981\u65f6\u3002\u6df7\u5165\u4ed6\u4eec\u7684\u7ec4\u5408\u4e2d\u2026\u2026\u201d\u6c5f\u67ab\u8bf4\uff1a\u201c\u4f46\u66f4\u91cd\u8981\u7684\u662f\uff0c\u4ed6\u4eec\u9009\u62e9\u4e86\u7980\u8d4b\u3001\u54c1\u683c\uff0c\u90fd\u662f\u4e0a\u4e0a\u4e4b\u9009\u7684\u4eba\u624d\uff0c\u8fd9\u79cd\u4eba\u5b66\u4e60\u7684\u80fd\u529b\u5f3a\uff0c\u609f\u6027\u9ad8\uff0c\u5177\u6709\u76f8\u5f53\u7684\u7075\u6167\uff0c\u53ea\u8981\u542f\u53d1\u5f97\u5b9c\uff0c\u4ed6\u4eec\u5f88\u5feb\u80fd\u7701\u609f\u662f\u975e\u2026\u2026\u201d \u3000\u3000\u201c\u53ef\u662f\u3002\u54b1\u4eec\u603b\u4e0d\u80fd\u4e5f\u82b1\u4e0a\u5341\u51e0\u5e74\u8bad\u7ec3\u51fa\u4e00\u6279\u4eba\u6765\u5427\uff1f\u2026\u2026\u201d\u8001\u53eb\u5316\u76b1\u8d77\u7709\u5934\uff0c\u65e0\u9650\u5fe7\u8651\u5730\u8bf4\u3002 \u3000\u3000\u201c\u90a3\u5f53\u7136\u6765\u4e0d\u53ca\u4e86\uff0c\u6240\u4ee5\uff0c\u54b1\u4eec\u53ea\u80fd\u4ece\u5404\u5927\u95e8\u6d3e\u4e2d\u53bb\u5bfb\u627e\u9002\u5f53\u7684\u4eba\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u8fd9\u5c31\u8981\u8bf8\u4f4d\u82b1\u8d39\u4e00\u756a\u5fc3\u529b\u4e86\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5144\u5f1f\uff0c\u8fd9\u5c31\u662f\u4f60\u552f\u4e00\u7684\u5bf9\u654c\u4e4b\u7b56\u4e86\u3002\u201d \u3000\u3000\u201c\u8fd9\u53ea\u662f\u6211\u5728\u6d1b\u9633\u5c45\u4e2d\u7684\u89c2\u5bdf\u6240\u5f97\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u6211\u60f3\u4ed6\u4eec\u63a7\u5236\u5404\u5927\u95e8\u6d3e\u7684\u65b9\u6cd5\u3002\u53e6\u6709\u4e00\u5957\u8fdb\u884c\u7684\u624b\u6bb5\u3002\u6211\u4eec\u4e5f\u8981\u627e\u51fa\u6765\u2026\u2026\u201d \u3000\u3000\u201c\u5982\u6b64\u4e2a\u590d\u6742\u6cd5\uff0c\u53ef\u771f\u53eb\u4eba\u65e0\u4ece\u4e0b\u624b\u554a\u2026\u2026\u201d\u8001\u53eb\u5316\u82e6\u7b11\u9053\uff1a\u201c\u6c5f\u8001\u5f1f\uff0c\u4f60\u53ef\u6709\u5e94\u5bf9\u4e4b\u7b56\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u8bf4\u8fc7\uff0c\u6c5f\u6e56\u4e0a\u4f20\u7edf\u89e3\u51b3\u6069\u6028\u662f\u975e\u7684\u529e\u6cd5\uff0c\u5df2\u65e0\u6cd5\u9002\u5e94\u4eca\u65e5\u7684\u5c40\u9762\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a \u3000\u3000\u201c\u770b\u8d77\u6765\u6574\u4e2a\u6b66\u6797\u4e2d\u4e00\u7247\u70df\u4e91\uff0c\u5176\u5b9e\uff0c\u53ea\u8981\u7528\u5fc3\u7684\u60f3\u4e00\u60f3\uff0c\u4e5f\u4e0d\u96be\u627e\u51fa\u4e00\u6761\u53ef\u884c\u4e4b\u8def\u3002\u201d \u3000\u3000\u201c\u613f\u95fb\u9ad8\u89c1\u2026\u2026\u201d\u80e1\u840d\u8bf4\uff1a\u201c\u6c5f\u5c11\u5144\u3002\u80fd\u6307\u51fa\u4e00\u6761\u53ef\u884c\u7684\u529e\u6cd5\uff0c\u54b1\u4eec\u5c31\u7acb\u523b\u884c\u52a8\u3002\u201d \u3000\u3000\u5fae\u5fae\u4e00\u7b11\uff0c\u6c5f\u67ab\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u5c31\u62ff\u5317\u5251\u95e8\u8bf4\u5427\uff01\u4ee4\u592b\u4eba\u593a\u53bb\u4e86\u638c\u95e8\u5b9e\u6743\uff0c\u4f60\u4eec\u662f\u4e8c\u5341\u51e0\u5e74\u7684\u592b\u59bb\u4e86\uff0c\u5e94\u8be5\u4e86\u89e3\u5979\u662f\u4e0d\u662f\u5177\u6709\u91ce\u5fc3\u7684\u5973\u4eba\uff0c\u593a\u53bb\u4e86\u638c\u95e8\u4e4b\u4f4d\uff0c\u5bf9\u5979\u6709\u4ec0\u4e48\u597d\u5904\uff1f\u201d \u3000\u3000\u201c\u4e0d\u50cf\uff0c\u5979\u4e0d\u50cf\u5177\u6709\u91ce\u5fc3\u7684\u5973\u4eba\u3001\u81ea\u6211\u4eec\u7ed3\u5076\u4ee5\u6765\uff0c\u5979\u4e00\u76f4\u662f\u4e2a\u8d24\u6dd1\u7684\u5973\u4eba\uff0c\u5ffd\u7136\u95f4\u53d8\u4e86\u8138\uff0c\u4e00\u4e0b\u593a\u53bb\u4e86\u638c\u95e8\u5b9e\u6743\uff0c\u5230\u73b0\u5728\u6211\u8fd8\u662f\u4e0d\u592a\u76f8\u4fe1\uff0c\u5979\u6e29\u67d4\u8d24\u6dd1\u7684\u6837\u5b50\uff0c\u4e5f\u6ca1\u6709\u4ec0\u4e48\u6539\u53d8\uff0c\u60f3\u8d77\u6765\u771f\u662f\u900f\u7740\u4e00\u822c\u90aa\u6c14\uff0c\u4e8c\u5341\u51e0\u5e74\u7761\u5728\u4e00\u5f20\u5e8a\u4e0a\uff0c\u5c31\u662f\u4e0d\u77e5\u9053\u5979\u4e3a\u4ec0\u4e48\u4f1a\u53d8\u4e86\u5fc3\uff1f\u201d \u3000\u3000\u5f20\u56db\u59d1\u5947\u9053\uff1a\u201c\u80e1\u5927\u638c\u95e8\u554a\uff01\u4f60\u771f\u662f\u7b28\u5f97\u53ef\u4ee5\uff0c\u65e5\u65e5\u76f8\u5904\u7684\u8001\u5a46\uff0c\u4e00\u4e0b\u5b50\u9a91\u5230\u4e86\u4f60\u7684\u5934\u4e0a\uff0c\u4f60\u7adf\u7136\u77a7\u4e0d\u51fa\u4e00\u70b9\u5f81\u5146\u2026\u2026\u201d \u3000\u3000\u80e1\u840d\u4e00\u8138\u7f9e\u7ea2\uff0c\u65e0\u9650\u60ed\u6127\u7684\u63a5\u9053\uff1a\u201c\u8bf4\u8d77\u6765\u4e5f\u771f\u662f\u4e22\u4eba\u3002\u600e\u4e48\u4f1a\u5f04\u6210\u8fd9\u4e48\u4e00\u4e2a\u5c40\u9762\u3002\u6700\u4f7f\u6211\u5fc3\u4e2d\u4e0d\u670d\u6c14\u7684\uff0c\u662f\u5317\u5251\u95e8\u4e2d\u7684\u5f1f\u5b50\uff0c\u5341\u4e4b\u4e03\u516b\u7adf\u7136\u65e9\u548c\u5185\u4eba\u7ed3\u6210\u4e00\u515a\uff0c\u6240\u4ee5\uff0c\u6211\u4e00\u8d25\u5728\u8001\u5a46\u624b\u4e2d\uff0c\u5317\u5251\u95e8\u5c31\u8fd9\u4e48\u5b8c\u5168\u6574\u6574\u5730\u88ab\u5979\u63a5\u6536\u4e86\uff0c\u8fd9\u79cd\u53eb\u4eba\u96be\u4ee5\u7f6e\u4fe1\u7684\u5bb6\u4e11\uff0c\u5c31\u7b97\u4e0d\u987e\u9762\u5b50\u4ed6\u8bf4\u51fa\u53bb\uff0c\u4e5f\u4e0d\u4f1a\u6709\u4eba\u76f8\u4fe1\u3002\u201d \u3000\u3000\u201c\u6240\u4ee5\uff0c\u80e1\u5144\uff0c\u5c31\u5fcd\u4e0b\u4e86\u8fd9\u53e3\u6c14\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u4f60\u5fcd\u5f97\u5f88\u5bf9\uff0c\u4f60\u5982\u6fc0\u70c8\u53cd\u6297\u3001\u5f88\u53ef\u80fd\u9020\u6210\u60e8\u5267\uff0c\u2026\u2026\u201d \u3000\u3000\u201c\u5927\u4e08\u592b\u96be\u4fdd\u59bb\u8d24\u5b50\u5b5d\uff0c\u2026\u2026\u201d\u8001\u53eb\u5316\u8bf4\uff1a\u201c\u80e1\u5144\uff0c\u4e0d\u7528\u628a\u8fd9\u4ef6\u4e8b\u6545\u5728\u5fc3\u4e0a\u3002\u201d \u3000\u3000\u201c\u6211\u60f3\u8fc7\u3001\u4ed4\u7ec6\u5730\u60f3\u8fc7\u3002\u5fcd\u4e0b\u8fd9\u53e3\u6028\u6c14\u3002\u7559\u7740\u6709\u7528\u4e4b\u8eab\u3002\u6211\u624d\u80fd\u628a\u8fd9\u4ef6\u4e8b\u67e5\u4e2a\u6e05\u695a\u3002\u201d \u3000\u3000\u201c\u73b0\u5728\uff0c\u80e1\u5144\uff0c\u60f3\u51fa\u4e86\u539f\u56e0\u6ca1\u6709\u2026\u2026\u201d\u6c5f\u67ab\u8bf4\uff1a\u201c\u53ea\u6015\u6c5f\u6e56\u4e0a\u5f88\u591a\u7684\u95e8\u6d3e\uff0c\u90fd\u88ab\u4ed6\u4eec\u7be1\u593a\u4e86\u5b9e\u6743\uff0c\u6b64\u4e8b\u5173\u7cfb\u81f3\u5927\u3002\u80e1\u5144\u80fd\u8bf4\u7684\uff0c\u8bf7\u5c3d\u91cf\u8bf4\u51fa\u6765\u3002\u201d \u3000\u3000\u201c\u5509\uff01\u5c31\u80e1\u67d0\u5bf9\u5979\u7684\u8ba4\u8bc6\uff0c\u662f\u5c5e\u4e8e\u90a3\u79cd\u5b89\u4e8e\u5bb6\u5ba4\u7684\u5973\u4eba\uff0c\u5982\u8457\u8bf4\u5979\u79fb\u60c5\u53d8\u5fc3\uff0c\u90a3\u4e5f\u662f\u9690\u85cf\u5f97\u975e\u5e38\u6df1\u6ca1\u3002\u80e1\u67d0\u662f\u4e00\u70b9\u4e5f\u672a\u53d1\u89c9\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u795e\u60c5\u8083\u7136\u4ed6\u8bf4\u9053\uff1a\u201c\u80e1\u840d\uff0c\u6211\u5982\u679c\u8bf4\u9519\u4e86\u4ec0\u4e48\uff1f\u5e0c\u671b\u4f60\u80fd\u591f\u539f\u8c05\u3002\u2026\u2026\u201d \u3000\u3000\u201c\u8bf4\u5427\uff1f\u2026\u2026\u201d\u80e1\u840d\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e0d\u8981\u987e\u53ca\u4ec0\u4e48\uff1f\u5317\u5251\u95e8\u867d\u53ea\u662f\u6c5f\u6e56\u4e0a\u4e00\u4e2a\u5c0f\u5c0f\u7684\u95e8\u6237\uff0c\u4f46\u5df2\u4f20\u4e86\u4e03\u4ee3\uff0c\u5728\u6211\u80e1\u67d0\u4eba\u624b\u4e2d\u5931\u53bb\uff0c\u80e1\u67d0\u4eba\u5b9e\u6709\u7740\u6127\u5bf9\u5386\u4ee3\u5e08\u957f\u4e4b\u611f\uff0c\u4e0b\u7ba1\u80e1\u67d0\u80fd\u4e0d\u80fd\u91cd\u5174\u5317\u5251\u95e8\uff0c\u4f46\u81f3\u5c11\uff0c\u6211\u5e94\u8be5\u67e5\u51fa\u771f\u6b63\u7684\u539f\u56e0\u4f55\u5728\uff1f\u4e5f\u597d\u5728\u4e5d\u6cc9\u4e4b\u4e0b\u3002\u5411\u5386\u4ee3\u5e08\u957f\u4f5c\u4e2a\u4ea4\u4ee3\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u4ee4\u6b63\u593a\u6743\uff0c\u4e5f\u8bb8\u4e0d\u662f\u5979\u771f\u6b63\u7684\u5fc3\u613f\uff0c\u53ea\u662f\u88ab\u4e00\u79cd\u5f62\u52bf\u903c\u8feb\uff0c\u4f46\u5979\u4e8b\u5148\u7adf\u4e0d\u80af\u900f\u9732\u7ed9\u4f60\u3002\u90a3\u8bf4\u660e\u4e86\u65e9\u5df2\u9677\u5165\u4e86\u4e00\u79cd\u65e0\u53ef\u633d\u6551\u7684\u6ce5\u6cbc\u4e2d\u4e86\uff0c\u903c\u8feb\u4f60\u80e1\u5144\u548c\u6bb5\u638c\u95e8\uff0c\u51b3\u6597\u6469\u5929\u5cad\uff0c\u90a3\u5df2\u662f\u5fc3\u6027\u5927\u53d8\uff0c\u6ca6\u5165\u9b54\u9053\u4e86\u3002\u201d \u3000\u3000\u201c\u5979\u65e2\u7136\u5df2\u6ca1\u6709\u592b\u59bb\u4e4b\u60c5\uff0c\u80e1\u67d0\u4eba\u4e5f\u4e0d\u7528\u6709\u6240\u987e\u5fcc\uff0c\u5982\u82e5\u5317\u5251\u95e8\u80fd\u591f\u91cd\u6574\uff0c\u6211\u5fc5\u4ee5\u95e8\u89c4\u5904\u7f6e\u8fd9\u6b21\u53db\u4e71\u7684\u4eba\u3002\u201d \u3000\u3000\u6c5f\u67ab\u76ee\u5149\u8f6c\u6ce8\u5230\u6bb5\u4e5d\u7684\u8eab\u4e0a\uff0c\u9053\uff1a\u201c\u6bb5\u5144\uff0c\u4e5f\u6ca1\u6709\u53d1\u89c9\u4ee4\u5e08\u5f1f\u593a\u53d6\u638c\u95e8\u7684\u9634\u8c0b\u5417\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\uff0c\u6bb5\u67d0\u53d1\u89c9\u6c5f\u6e56\u4e0a\u60c5\u52bf\u53ef\u7591\u3002\u4e00\u76f4\u628a\u6ce8\u610f\u529b\uff0c\u6295\u6ce8\u4e8e\u5916\u5728\u56e0\u7d20\uff0c\u5357\u5200\u95e8\u7531\u4e94\u5e74\u524d\uff0c\u5c31\u4e0d\u518d\u6536\u5f55\u95e8\u4eba\u5f1f\u5b50\uff0c\u5509\uff01\u60f3\u4e0d\u5230\u53d8\u751f\u4e8e\u8098\u814b\u4e4b\u95f4\uff0c\u6211\u4ece\u5c0f\u8c03\u6559\u957f\u5927\u7684\u5e08\u5f1f\uff0c\u7adf\u7136\u593a\u53bb\u4e86\u6211\u4eec\u7684\u638c\u95e8\u5b9e\u6743\u3002\u201d \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u542c\u4e24\u4f4d\u7684\u53e3\u6c14\uff0c\u4f3c\u4e4e\u662f\u4ed6\u4eec\u8c0b\u593a\u4e24\u4f4d\u7684\u95e8\u6237\u5b9e\u6743\u3002 \u3000\u3000\u5e76\u975e\u662f\u65e9\u6709\u7528\u5fc3\uff0c\u800c\u662f\u4e34\u65f6\u8d77\u610f\uff0c\u6240\u4ee5\uff0c\u5c31\u5730\u53d6\u6750\uff1f\u201d \u3000\u3000\u201c\u4f46\u5317\u5251\u95e8\u4e2d\u7684\u5f1f\u5b50\uff0c\u600e\u4f1a\u6709\u5927\u90e8\u5206\u80cc\u53db\u6211\u5462\uff1f\u201d\u80e1\u840d\u6709\u4e9b\u4e0d\u670d\u4ed6\u8bf4\u3002 \u3000\u3000\u201c\u8fd9\u5c31\u662f\u4ed6\u4eec\u4e0d\u53ef\u8f7b\u4fae\u7684\u5730\u65b9\u2026\u2026\u201d\u6c5f\u67ab\u8bf4\uff1a\u201c\u6211\u4e0d\u77e5\u80e1\u5144\uff0c\u662f\u5426\u4e5f\u548c\u6bb5\u5144\u4e00\u6837\uff0c\u65e9\u5df2\u6ce8\u610f\u5230\u6c5f\u6e56\u4e2d\u7684\u60c5\u52bf\u3002\u800c\u4e14\u6709\u4e86\u4e00\u4e9b\u9632\u8303\u63aa\u65bd\u3002\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u80e1\u67d0\u4e5f\u548c\u6bb5\u5144\u4e00\u6837\uff0c\u4e94\u5e74\u524d\u5c31\u4e0d\u518d\u6536\u5f1f\u5b50\u3002\u800c\u4e14\u7763\u4fc3\u95e8\u4e0b\u52e4\u7ec3\u6b66\u529f\u2026\u2026\u201d\u80e1\u840d\u8bf4\uff1a\u201c\u5317\u5251\u95e8\u53ea\u662f\u4e00\u4e2a\u5fae\u4e0d\u8db3\u9053\u5c0f\u95e8\u6d3e\uff0c\u60f3\u4e0d\u5230\uff0c\u4ed6\u4eec\u7adf\u9009\u62e9\u4e86\u6211\u4eec\u4e0b\u624b\uff1f\u201d \u3000\u3000\u201c\u90a3\u662f\u56e0\u4e3a\uff0c\u4f60\u4eec\u65e9\u6709\u4e86\u8b66\u89c9\u4e4b\u5fc3\u3002\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u73b0\u5728\u3002 \u3000\u3000\u6211\u60f3\u5c31\u5148\u4ece\u5317\u5251\u3001\u5357\u5200\u4e24\u6d3e\u4e0b\u624b\u2026\u2026\u201d \u3000\u3000\u201c\u8fd9\u4e2a\uff0c\u8001\u53eb\u5316\u770b\u6cd5\u4e0d\u540c\u2026\u2026\u201d\u94b1\u7f3a\u8bf4\uff1a\u201c\u5c11\u6797\u3001\u6b66\u5f53\uff0c\u662f\u5f53\u4eca\u6b66\u6797\u4e2d\u4e24\u5927\u4e3b\u8109\uff0c\u4ed6\u4eec\u5f1f\u5b50\u4f17\u591a\uff0c\u4eba\u624d\u8f88\u51fa\uff0c\u5982\u82e5\u80fd\u89e3\u9664\u4efb\u4f55\u4e00\u6d3e\u7684\u7981\u5236\uff0c\u54b1\u4eec\u5c31\u5177\u6709\u4e86\u53ef\u7528\u7684\u4eba\u624b\uff0c\u636e\u4ee5\u53f7\u53ec\u6b66\u6797\u540c\u9053\uff0c\u5c55\u5f00\u53cd\u51fb\u2026\u2026\u201d \u3000\u3000\u6bb5\u4e5d\u63a5\u9053\uff1a\u201c\u94b1\u5144\u9ad8\u89c1\uff0c\u5728\u4e0b\u540c\u610f\uff0c\u5357\u5200\u95e8\u4e2d\uff0c\u5f1f\u5b50\u4e0d\u8fc7\u6570\u5341\u4eba\uff0c\u800c\u4e14\uff0c\u5927\u90e8\u5206\u90fd\u88ab\u6211\u90a3\u593a\u53bb\u638c\u95e8\u5b9e\u6743\u7684\u5e08\u5f1f\u638c\u63e1\uff0c\u82b1\u8d39\u540c\u6837\u7684\u6c14\u529b\uff0c\u4f55\u5982\u5148\u89e3\u6551\u5c11\u6797\u3001\u6b66\u5f53\u4e24\u5927\u95e8\u6237\uff1f\u201d \u3000\u3000\u201c\u5bf9\uff01\u5144\u5f1f\uff0c\u5e94\u4ee5\u6c5f\u6e56\u5927\u5c40\u4e3a\u91cd\uff0c\u8001\u53eb\u5316\u548c\u6bb5\u4e5d\u7684\u610f\u89c1\uff0c\u9887\u6709\u53ef\u53d6\u4e4b\u5904\u3002\u201d\u5f20\u56db\u59d1\u4e5f\u8868\u793a\u4e86\u81ea\u5df1\u7684\u610f\u89c1\u3002 \u3000\u3000\u6c5f\u67ab\u7565\u4e00\u6c89\u541f\uff0c\u9053\uff1a\u201c\u4ed6\u4eec\u7528\u4ec0\u4e48\u65b9\u6cd5\uff0c\u903c\u4f7f\u5c11\u6797\u3002\u6b66\u5f53\u4e24\u5927\u95e8\u6d3e\u5c48\u670d\uff0c\u6211\u4eec\u5168\u7136\u65e0\u8ff9\u53ef\u5bfb\uff0c\u800c\u4e14\uff0c\u6211\u76f8\u4fe1\u4ed6\u4eec\u9632\u5907\u5f97\u5341\u5206\u68ee\u4e25\uff0c\u4f55\u51b5\uff0c\u5c11\u6797\u3001\u6b66\u5f53\u9000\u51fa\u6c5f\u7322\uff0c\u4e5f\u53ea\u80fd\u8bc1\u660e\u5185\u90e8\u5df2\u53d1\u751f\u4e86\u53d8\u5316\uff0c\u8fd8\u672a\u5230\u5c18\u57c3\u843d\u5b9a\u7684\u65f6\u523b\u2026\u2026\u201d \u3000\u3000\u201c\u90a3\u624d\u597d\u554a\uff01\u5927\u5c40\u672a\u7a33\uff0c\u5185\u90e8\u8fd8\u6709\u4e89\u6267\uff0c\u6211\u4eec\u52a0\u628a\u529b\u3002\u5c82\u4e0d\u662f\u4e8b\u534a\u529f\u500d\uff0c\u8001\u53eb\u5316\u548c\u5c11\u6797\u5bfa\u8fbe\u6469\u9662\u7684\u4e3b\u6301\u8fd8\u6709\u70b9\u4ea4\u60c5\uff0c\u6b64\u4eba\u516c\u6b63\u65e0\u79c1\uff0c\u6211\u53ef\u4ee5\u5148\u53bb\u627e\u4ed6\u8c08\u8c08\uff0c\u6c5f\u5c11\u5144\u4ee5\u4e3a\u5982\u4f55\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u5c11\u6797\u3001\u6b66\u5f53\uff0c\u95e8\u6237\u5e9e\u5927\uff0c\u4eba\u6570\u4f17\u591a\uff0c\u5c31\u5728\u4e0b\u6240\u77e5\uff0c\u6709\u4e0d\u5c11\u8eab\u6000\u5927\u6210\u7684\u9ad8\u50e7\u3001\u9053\u957f\u4eec\uff0c\u606f\u9690\u4e8e\u5bfa\u3002\u4ed6\u4eec\u4e0d\u5e72\u4e0e\u5bfa\u3001\u89c2\u7684\u4e8b\u52a1\uff0c\u4f46\u4ed6\u4eec\u4e0d\u4f1a\u5750\u89c6\u95e8\u6237\u6ca6\u4ea1\u3002\u5728\u5927\u5c40\u6df7\u6d4a\u4e0d\u6e05\u4e2d\uff0c\u54b1\u4eec\u63d2\u624b\u5176\u4e2d\uff0c\u4e00\u5219\u6781\u6613\u66b4\u9732\u51fa\u8eab\u4efd\uff0c\u4f7f\u5bf9\u65b9\u751f\u51fa\u8b66\u89c9\uff0c\u6539\u53d7\u65b9\u5411\uff0c\u5168\u529b\u5bf9\u4ed8\u54b1\u4eec\u3002\u4e8c\u5219\u4f7f\u53d8\u8282\u7684\u9996\u8111\uff0c\u6709\u6240\u85c9\u53e3\u6df7\u6dc6\u662f\u975e\uff0e\u5012\u4e0d\u5982\u7b49\u4ed6\u4eec\u9634\u8c0b\u73b0\u5f62\uff0c\u5c40\u52bf\u660e\u6717\uff0c\u2026\u2026\u201d \u3000\u3000\u201c\u8fd9\u6050\u6015\u6709\u4e9b\u4e0d\u59a5\uff0c\u2026\u2026\u201d\u8001\u53eb\u5316\u8bf4\uff1a\u201c\u62d6\u5ef6\u65f6\u95f4\u3002\u5c82\u4e0d\u662f\u7ed9\u4e86\u4ed6\u4eec\u94f2\u9664\u5f02\u5df1\u7684\u673a\u4f1a\u3002\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u4f46\u4e5f\u4f1a\u5b8c\u5168\u66b4\u9732\u51fa\u4ed6\u4eec\u5185\u90e8\u771f\u8c61\uff0c\u8c01\u662f\u4e3b\u6301\u8fd9\u6b21\u9634\u8c0b\u7684\u9996\u8111\uff0c\u5fe0\u8d1e\u7684\u95e8\u4eba\u5f1f\u5b50\uff0c\u4e5f\u6709\u4e86\u8db3\u591f\u7684\u65f6\u95f4\uff0c\u8003\u8651\u5982\u4f55\u5e94\u4ed8\u53d8\u5c40\u2026\u2026\u201d\u6c5f\u67ab\u795e\u60c5\u8083\u7136\u4ed6\u8bf4\uff1a\u201c\u8fd9\u4e24\u5927\u95e8\u6237\u7684\u9996\u8111\uff0c\u62b1\u6b8b\u5b88\u7f3a\uff0c\u56fa\u6267\u5df1\u89c1\uff0c\u6709\u4e86\u8fd9\u4e00\u6b21\u6253\u51fb\uff0c\u4e5f\u597d\u8ba9\u4ed6\u6709\u6240\u6539\u8fdb\u3002\u201d \u3000\u3000\u201c\u597d\uff0c\u597d\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u9053\uff1a\u201c\u548c\u5c1a\u8d5e\u6210\u6c5f\u5c11\u5144\u7684\u9ad8\u89c1\u3002\u7b49\u5230\u5c40\u52bf\u6e05\u6717\uff0c\u5f84\u6e2d\u5206\u660e\u3002\u54b1\u4eec\u518d\u548c\u4ed6\u4eec\u8fde\u7edc\u4e0d\u8fdf\u3001\u514d\u5f97\u6b63\u90aa\u4e0d\u8fa8\u3002 \u3000\u3000\u8bef\u8e0f\u9677\u4e95\uff0c\u53cd\u4e3a\u6240\u7d2f\uff0c\u54b1\u4eec\u672c\u94b1\u592a\u5c11\uff0c\u7ecf\u4e0d\u8d77\u4e00\u6b21\u4e8f\u635f\uff0c\u201d \u3000\u3000\u8001\u53eb\u5316\u7565\u4e00\u6c89\u541f\uff0c\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u6709\u9053\u7406\uff0c\u4e0d\u8fc7\uff0c\u5bf9\u4ed8\u5317\u5251\u3001\u5357\u5200\u4e24\u95e8\uff0c\u5c31\u6709\u628a\u63e1\u5417\uff1f\u201d \u3000\u3000\u201c\u6210\u7b97\u5f88\u5927\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u8fd9\u4e24\u4e2a\u95e8\u6237\uff0c\u5f1f\u5b50\u4e0d\u591a\uff0c\u6b63\u90aa\u5206\u9645\uff0c\u5bb9\u6613\u74e3\u8bc6\u3002\u8fd8\u6709\u4e24\u4f4d\u638c\u95e8\u4eba\u4eb2\u81ea\u53c2\u4e0e\uff0c\u6211\u4eec\u8c28\u614e\u884c\u4e8b\uff0c\u4e0d\u96be\u4e00\u4e3e\u6210\u529f\u3002\u201d \u3000\u3000\u94b1\u7f3a\u9053\uff1a\u201c\u6c5f\u8001\u5f1f\u8bf7\u5206\u914d\u4e00\u4e0b\uff0c\u54b1\u4eec\u7acb\u523b\u884c\u52a8\u3002\u201d \u3000\u3000\u201c\u6025\u4e5f\u4e0d\u5728\u4e00\u65f6\uff0c\u6d1b\u9633\u5c45\u7684\u4e8b\uff0c\u5148\u8981\u6709\u4e2a\u5b89\u6392\u3002\u2026\u2026\u201d \u3000\u3000\u201c\u662f\u4e0d\u662f\u8981\u7b49\u4e24\u4e2a\u4e2b\u5934\u914d\u5408\u884c\u52a8\uff0c\u2026\u2026\u201d\u5f20\u56db\u59d1\u8bf4\uff1a\u201c\u6211\u4eca\u5929\u5c31\u5f00\u59cb\u4f20\u6388\u5979\u4eec\u7684\u6613\u5bb9\u624b\u6cd5\u3002\u201d \u3000\u3000\u201c\u8fd8\u8981\u6709\u4e00\u4e2a\u97e9\u9738\u7684\u4eba\u3002\u4ee3\u66ff\u6211\u526f\u603b\u7ba1\u7684\u8eab\u4efd\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u7b11\u9053\uff1a\u201c\u8fd9\u8981\u5927\u59d0\u60f3\u4e2a\u529e\u6cd5\u4e86\uff1f\u201d \u3000\u3000\u201c\u8fd9\u5c31\u96be\u4e86\uff0c\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u6211\u53ef\u4ee5\u6539\u626e\u97e9\u9738\u3002\u4f46\u4f60\u90a3\u4e00\u7fa4\u5973\u5b69\u5b50\uff0c\u6211\u6050\u6015\u5e94\u4ed8\u4e0d\u4e86\u2026\u2026\u201d\u76ee\u5149\u4e00\u63a0\u8001\u53eb\u5316\u7b49\u56db\u4eba\uff0c\u9053\uff1a \u3000\u3000\u201c\u4ed6\u4eec\u56db\u4e2a\u4eba\uff0c\u8eab\u6750\u90fd\u4e0d\u592a\u5bf9\uff0c\u90a3\u4f4d\u603b\u7ba1\u5927\u4eba\uff0c\u60f3\u5fc5\u662f\u53c8\u5978\u53c8\u9b3c\uff0c\u5927\u59d0\u80fd\u6613\u5bb9\uff0c\u4f46\u5374\u4e0d\u80fd\u6539\u53d8\u4e00\u4e2a\u4eba\u7684\u8eab\u6750\uff0c\u4e5f\u65e0\u6cd5\u4f7f\u4ed6\u4eec\u58f0\u97f3\u76f8\u540c\u3002\u201d \u3000\u3000\u6c5f\u67ab\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u5927\u59d0\uff0c\u53ef\u4ee5\u521b\u9020\u51fa\u4e00\u4e2a\u63a7\u5236\u6d1b\u9633\u5c45\u7684\u4eba\u2026\u2026\u201d \u3000\u3000\u4ed6\u8bf4\u51fa\u4e00\u756a\u8ba1\u5212\uff0c\u53ea\u542c\u5f97\u8001\u53eb\u5316\u7b49\u76ee\u77aa\u53e3\u5446\u3002 \u3000\u3000\u90a3\u662f\u5927\u80c6\u65e0\u6bd4\u7684\u8ba1\u5212\u3002\u4f7f\u5f97\u8001\u53eb\u5316\u7b49\u771f\u6b63\u7684\u751f\u51fa\u4e86\u60a6\u670d\u4e4b\u5fc3\uff0c\u5bf9\u6c5f\u67ab\u6709\u4e00\u756a\u65b0\u7684\u8ba4\u8bc6\u4e86\u3002 \u3000\u3000\u6d1b\u9633\u5c45\u7684\u751f\u610f\uff0c\u56e0\u9752\u51e4\u88ab\u523a\uff0c\u53d7\u5230\u4e86\u76f8\u5f53\u7684\u5f71\u54cd\uff0c\u4f46\u53ea\u4e0d\u8fc7\u4e94\u5929\u7684\u65f6\u95f4\uff0c\u9752\u51e4\u5c31\u4f24\u52bf\u75ca\u6108\uff0c\u91cd\u65b0\u767b\u5408\uff0c\u76db\u51b5\u4e5f\u7acb\u523b\u6062\u590d\u3002 \u3000\u3000\u7b2c\u516d\u5929\u4e2d\u5348\u65f6\u5206\uff0c\u6d1b\u9633\u5c45\u6765\u4e86\u4e00\u4e2a\u6c14\u6d3e\u5947\u5927\u7684\u4e2d\u5e74\u5ba2\u4eba\u3002 \u3000\u3000\u4e24\u4e2a\u9752\u8863\u7ae5\u5b50\u5f00\u9053\uff0c\u4e00\u4e2a\u62b1\u5200\u3001\u4e00\u4e2a\u6367\u5251\uff0c\u8fd8\u6709\u4e24\u4e2a\u968f\u884c\u7684\u4e2d\u5e74\u5065\u4ec6\uff0c\u5206\u6311\u4e86\u56db\u4e2a\u63cf\u91d1\u7bb1\u5b50\uff0c\u518d\u52a0\u4e00\u4e2a\u957f\u886b\u6587\u58eb\u968f\u4f8d\u8eab\u4fa7\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\uff0c\u5374\u5e26\u4e86\u4e94\u4e2a\u4ece\u4eba\u4f3a\u5019\u3002 \u3000\u3000\u6d1b\u9633\u5c45\u81ea\u4ece\u5f00\u5f20\u4ee5\u6765\uff0c\u4ece\u6765\u6ca1\u6709\u4eba\u516c\u7136\u5e26\u7740\u5175\u5203\u8fdb\u5165\u6b4c\u53f0\u3001\u996d\u5e84\uff0c\u867d\u7136\u6d1b\u9633\u5c45\u6ca1\u6709\u4e0d\u51c6\u5ba2\u4eba\u5e26\u5175\u5203\u7684\u89c4\u5b9a\uff0c\u4f46\u5230\u8fd9\u91cc\u7684\u4eba\uff0c\u5927\u90fd\u662f\u8d64\u624b\u7a7a\u62f3\u3002 \u3000\u3000\u4e5f\u8bb8\u6709\u4eba\u5e26\u4e86\u5bb6\u4f19\uff0c\u4f46\u90fd\u9690\u85cf\u4e8e\u957f\u886b\u4e4b\u4e0b\uff0c\u6216\u85cf\u4e8e\u5305\u88b1\u4e4b\u4e2d\uff0c\u50cf\u8fd9\u6837\u660e\u76ee\u5f20\u80c6\u7684\u6367\u5728\u624b\u4e2d\uff0c\u8fd8\u771f\u662f\u7b2c\u4e00\u6b21\u3002 \u3000\u3000\u90a3\u4e2d\u5e74\u5ba2\u4eba\u4e0d\u4f46\u6c14\u6d3e\u5927\uff0c\u800c\u4e14\u67b6\u5b50\u4e5f\u5927\uff0c\u5bf9\u4e00\u8def\u884c\u793c\u3001\u8fce\u63a5\u7684\u4f19\u8ba1\u4eec\uff0c\u770b\u90fd\u4e0d\u770b\u4e00\u773c\uff0c\u90fd\u7531\u90a3\u968f\u884c\u957f\u886b\u6587\u58eb\u4f5c\u4e3b\u6d3e\u8d4f\u3002 \u3000\u3000\u5ba2\u4eba\u7684\u67b6\u5b50\u5927\u3002\u8d4f\u8d50\u4e5f\u591a\uff0c\u53ea\u8981\u5e97\u91cc\u7684\u4f19\u8ba1\u4eec\u8eac\u4e2a\u8eab\u3002\u884c\u4e2a\u793c\uff0e\u5c31\u63a5\u5230\u4e00\u952d\u5341\u4e24\u91cd\u7684\u94f6\u5143\u5b9d\u3002 \u3000\u3000\u6d1b\u9633\u5c45\u65e5\u8fdb\u6597\u91d1\u3002\u8c6a\u5ba2\u4e0d\u5c11\uff0c\u4f46\u50cf\u8fd9\u6837\u5927\u65b9\u7684\u5ba2\u4eba\u8fd8\u672a\u89c1\u8fc7\uff0c\u4ed6\u4e00\u8def\u884c\u5165\u996d\u5e84\u90e8\u3002\u8fd8\u6ca1\u6709\u8fdb\u5165\u623f\u95f4\uff0c\u5df2\u7ecf\u4ed8\u51fa\u4e86\u5341\u4e8c\u4e2a\u94f6\u5143\u5b9d\u3002 \u3000\u3000\u4e00\u4e2a\u7a7f\u7740\u9ed1\u8272\u957f\u888d\u3001\u8138\u8272\u51b7\u5389\u7684\u5e74\u8f7b\u4eba\uff0c\u5306\u5306\u8fce\u4e86\u4e0a\u6765\uff0c\u4e00\u62b1\u62f3\uff0c\u9053\uff1a\u201c\u51e0\u4f4d\u5ba2\u4eba\u5462\uff1f\u201d \u3000\u3000\u201c\u4e00\u4f4d\u2026\u2026\u201d\u957f\u886b\u6587\u58eb\u7b54\u9053\uff1a\u201c\u8981\u4f60\u4eec\u6d1b\u9633\u5c45\u6700\u597d\u7684\u5bb4\u5ba2\u623f\u95f4\u3002\u201d \u3000\u3000\u9ed1\u888d\u4eba\u62db\u62db\u624b\uff0c\u7acb\u523b\u6709\u4e00\u4e2a\u7a7f\u7740\u9ed1\u8272\u68c9\u88e4\u3001\u68c9\u8884\u7684\u5e74\u8f7b\u4f19\u8ba1\u8dd1\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u201c\u5e26\u5ba2\u4eba\u5230\u83ca\u82b1\u5385\u53bb\u3002\u201d\u957f\u888d\u4eba\u81ea\u5df1\u4f5c\u4e86\u4e3b\u5f20\u3002 \u3000\u3000\u201c\u6162\u7740\u2026\u2026\u201d\u4e2d\u5e74\u6587\u58eb\u9053\uff1a\u201c\u83ca\u82b1\u5385\u662f\u4e0d\u662f\u6700\u597d\u7684\u623f\u95f4\uff1f\u201d \u3000\u3000\u201c\u4e0d\u662f\uff0c\u6700\u5927\u7684\u8d35\u5bbe\u5385\u5df2\u7ecf\u6709\u5ba2\u4eba\u5b9a\u4e86\u2026\u2026\u201d\u9ed1\u888d\u4eba\u9053\uff1a\u201c\u83ca\u82b1\u5385\u6709\u83ca\u82b1\u5385\u7684\u7279\u8272\uff0c\u8bf8\u4f4d\u4f55\u4e0b\u53bb\u770b\u770b\u518d\u8bf4\u3002\u201d \u3000\u3000\u4e2d\u5e74\u6587\u571f\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5e0c\u671b\u655d\u4e1c\u4e3b\u80fd\u770b\u5f97\u4e0a\u3002\u201d \u3000\u3000\u83ca\u82b1\u5385\u7684\u7279\u8272\u662f\uff0c\u56db\u58c1\u6302\u6ee1\u4e86\u5f69\u7ed8\u7684\u83ca\u82b1\u3002\u753b\u5de5\u7cbe\u7ec6\u3002\u51e0\u53ef\u4e71\u771f\u3002 \u3000\u3000\u4e2d\u5e74\u5ba2\u4eba\u5fae\u4e00\u70b9\u5934\uff0c\u5c31\u5728\u4e3b\u4f4d\u4e0a\u5750\u4e86\u4e0b\u6765\uff0c\u90a3\u4e2d\u5e74\u6587\u58eb\u7acb\u523b\u8d8b\u524d\uff0c\u63a2\u8eab\u503e\u542c\u90a3\u4e3b\u4eba\u4f4e\u8a00\u6570\u8bed\uff0c\u8f6c\u8138\u5429\u5490\u5e97\u4f19\u8ba1\uff0c\u9053\uff1a\u201c\u8bf7\u51e0\u4f4d\u7f8e\u4e3d\u7684\u59d1\u5a18\u4f8d\u9152\uff0c\u914d\u516b\u4e2a\u4e0b\u9152\u7684\u83dc\u3002\u201d \u3000\u3000\u7247\u523b\u5de5\u592b\uff0c\u9152\u83dc\u9f50\u4e0a\uff0c\u4e24\u4e2a\u7eff\u8863\u7eff\u88d9\u7684\u7f8e\u4e3d\u5c11\u5973\uff0c\u4e5f\u7f13\u6b65\u5165\u5385\u3002 \u3000\u3000\u4e2d\u5e74\u6587\u58eb\u6253\u4e86\u4e8c\u5973\u4e00\u773c\u3002\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u201d \u3000\u3000\u4e8c\u5973\u5fae\u4e00\u8eac\u8eab\uff0c\u9053\uff1a\u201c\u79cb\u82b1\u53cc\u5a62\uff0c\u83ca\u82b1\u3001\u6842\u82b1\uff0c\u8bf7\u5927\u7237\u6307\u6559\uff1f\u201d \u3000\u3000\u5750\u5728\u4e0a\u4f4d\u7684\u4e2d\u5e74\u8c6a\u5ba2\uff0c\u6253\u91cf\u4e86\u4e8c\u5973\u4e00\u9635\uff0c\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a \u3000\u3000\u201c\u540d\u5b57\u867d\u7136\u4fd7\u4e86\u4e00\u4e9b\uff0c\u4f46\u4eba\u8fd8\u770b\u5f97\u8fc7\u53bb\uff0c\u5c31\u7559\u4e0b\u5979\u4eec\u4e24\u4e2a\u5427\uff01\u201d \u3000\u3000\u957f\u886b\u6587\u58eb\u52a0\u4e86\u4e00\u53e5\u597d\u597d\u7684\u4f8d\u5019\u3001\u624d\u6253\u6a2a\u5750\u4e0b\u3002 \u3000\u3000\u4e24\u4e2a\u7eff\u8863\u5c11\u5973\uff0c\u957f\u5f97\u76f8\u5f53\u7684\u6f02\u4eae\uff0c\u4f46\u7eff\u8863\u7eff\u88d9\uff0c\u5374\u6311\u8d77\u4e86\u4e00\u79cd\u6de1\u6de1\u7684\u8427\u7d22\u97f5\u5473\u3002 \u3000\u3000\u5979\u4eec\u4e3e\u8d77\u4e86\u9152\u58f6\uff0c\u659f\u6ee1\u4e86\u9152\u676f\uff0c\u8f6c\u52a8\u7740\u4e24\u5bf9\u5927\u773c\uff0c\u884c\u8fd1\u90a3\u4e2d\u5e74\u8c6a\u5ba2\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u5927\u7237\u7528\u9152\uff01\u201d \u3000\u3000\u58f0\u97f3\u4e2d\u4e5f\u900f\u51fa\u51e0\u5206\u8427\u8427\u8f7b\u6101\uff0c\u8138\u4e0a\u662f\u4e00\u80a1\u81ea\u601c\u81ea\u60dc\u7684\u795e\u60c5\u3002 \u3000\u3000\u6709\u5982\u5a49\u8f6c\u5730\u8bc9\u8bf4\u7740\u5fc3\u4e2d\u59d4\u5c48\u2026\u2026\u3002 \u3000\u3000\u4f46\u5979\u4eec\u65e0\u6cd5\u63a5\u8fd1\u90a3\u4e2d\u5e74\u8c6a\u5ba2\uff0c\u56e0\u4e3a\u4e24\u4e2a\u6367\u5251\u3001\u62b1\u5200\u7684\u9752\u8863\u7f6e\u5b50\uff0c\u5206\u7ad9\u5728\u90a3\u8c6a\u5ba2\u4e24\u4fa7\uff0c\u4e24\u4e2a\u968f\u884c\u7684\u5065\u4ec6\uff0e\u633a\u7acb\u5728\u8c6a\u541d\u8eab\u540e\u5de6\u53f3\uff0c\u9664\u975e\u4e24\u4e2a\u7eff\u8863\u5c11\u5973\u63a8\u5f00\u90a3\u7ae5\u5b50\u3001\u58ee\u6c49\uff0c\u5c31\u65e0\u6cd5\u76f4\u63a5\u884c\u5230\u8c6a\u5ba2\u8eab\u524d\u3002 \u3000\u3000\u4f46\u89c1\u5bd2\u8292\u4e00\u95ea\uff0c\u5de6\u9996\u9752\u8863\u7ae5\u5b50\u7684\u5b9d\u5251\uff0c\u7a81\u7136\u51fa\u9798\uff0c\u9012\u5230\u6842\u82b1\u9762\u524d\uff0c\u9053\uff1a\u201c\u628a\u9152\u676f\u653e\u5728\u5251\u4e0a\u3002\u201d \u3000\u3000\u58f0\u97f3\u6e05\u4eae\uff0c\u5a49\u8f6c\u5a07\u8106\u4e0d\u5982\u79cb\u82b1\u53cc\u5a62\uff0c\u4f46\u5374\u53e6\u6709\u4e00\u80a1\u660e\u6717\u7684\u6c14\u52bf\u3002 \u3000\u3000\u6842\u82b1\u5fc3\u4e2d\u4e00\u52a8\uff0c\u5fd6\u9053\uff1a\u201c\u597d\u8106\u7684\u58f0\u97f3\uff0c\u8fd9\u4e24\u4e2a\u7ae5\u5b50\uff0c\u662f\u7537\u662f\u5973\uff1f\u201d \u3000\u3000\u4f46\u89c1\u4ed6\u4eec\u80a4\u8272\u6de1\u9ed1\uff0c\u6d53\u7709\u5927\u773c\uff0c\u53cc\u9b13\u539a\u5b9e\uff0c\u4e00\u526f\u7537\u513f\u6c14\u6982\uff0c\u4e0d\u7981\u5fc3\u4e2d\u6697\u7b11\uff0c\u542c\u58f0\u8fa8\u8c8c\uff0c\u7adf\u6709\u7740\u96cc\u96c4\u96be\u8fa8\u7684\u8bef\u5dee\uff0c\u7f13\u7f13\u628a\u9152\u676f\u653e\u5728\u5251\u4e0a\u3002 \u3000\u3000\u4e2d\u5e74\u8c6a\u5ba2\u4f38\u624b\u53d6\u8fc7\u9152\u676f\uff0c\u4e00\u996e\u800c\u5c3d\u3002\u5374\u628a\u9152\u676f\u3002\u629b\u5411\u6842\u82b1\u3002 \u3000\u3000\u53bb\u52bf\u6025\u52b2\uff0c\u5e26\u8d77\u4e00\u80a1\u5578\u98ce\u4e4b\u58f0\u3002 \u3000\u3000\u6842\u82b1\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u5927\u7237\u2026\u2026\u201d\u9152\u676f\u63a0\u988a\u800c\u8fc7\uff0c\u5c31\u90a3\u4e48\u8c6a\u5398\u4e4b\u5dee\uff0c\u6ca1\u6709\u6253\u4e2d\u3002 \u3000\u3000\u4f46\u6025\uff0c\u52b2\u7684\u9152\u676f\uff0c\u5374\u88ab\u690e\u95e8\u800c\u5165\u7684\u4e0a\u83dc\u4f19\u8ba1\uff0c\u4e00\u4f38\u5de6\u624b\uff0c\u63a5\u4eba\u4e86\u624b\u4e2d\u3002 \u3000\u3000\u4ed6\u53f3\u624b\u9ad8\u4e3e\u83dc\u76d8\uff0c\u5de6\u624b\u62ff\u7740\u9152\u676f\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u767d\u7389\u676f\u83b9\u51c0\u6d01\u767d\uff0c\u6454\u7834\u4e86\uff0c\u5b9e\u5728\u592a\u53ef\u60dc\u3002\u201d \u3000\u3000\u201c\u655e\u4e1c\u4e3b\u91d1\u94f6\u82e5\u5c71\uff0c\u2026\u2026\u201d\u957f\u886b\u6587\u58eb\u51b7\u51b7\u7684\u63a5\u9053\uff1a\u201c\u533a\u533a\u4e00\u4e2a\u7389\u676f\uff0c\u53c8\u80fd\u503c\u591a\u5c11\u94f6\u5b50\u3002\u201d \u3000\u3000\u7f13\u7f13\u628a\u9ad8\u4e3e\u6728\u76d8\u4e2d\u7684\u4e00\u76d8\u4f73\u770b\uff0c\u653e\u4e0a\u6728\u684c\uff0c\u5782\u624b\u8eac\u8eab\u7684\u5e94\u9053\uff1a\u201c\u662f\u5ba2\u4eba\u6709\u610f\u8981\u6253\u788e\u7389\u676f\u4e86\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u655e\u4e1c\u4e3b\u5c31\u7231\u542c\u90a3\u79cd\u788e\u7389\u7684\u58f0\u97f3\u2026\u2026\u201d\u9752\u886b\u6587\u58eb\u7684\u58f0\u97f3\uff0c\u7a81\u8f6c\u51b7\u5389\uff0c\u9053\uff1a\u201c\u4f60\uff01 \u3000\u3000\u5374\u626b\u4e86\u655d\u4e1c\u4e3b\u7684\u96c5\u5174\u3002\u201d \u3000\u3000\u201c\u5c0f\u7684\u8be5\u6b7b\u2026\u2026\u201d\u8f6c\u8eab\u5411\u5916\u884c\u53bb\u3002 \u3000\u3000\u957f\u886b\u6587\u58eb\u7a81\u7136\u51fa\u624b\uff0c\u70b9\u5411\u90a3\u4f19\u8ba1\u540e\u80cc\u3002 \u3000\u3000\u53bb\u52bf\u5947\u5feb\uff0c\u4f46\u5374\u6ca1\u6709\u4e00\u70b9\u58f0\u606f\u3002 \u3000\u3000\u201c\u5927\u7237\uff0c\u4ed6\u53ea\u662f\u4e00\u4e2a\u6367\u9152\u4e0a\u83dc\u7684\u4e0b\u4eba\u2026\u2026\u201d \u3000\u3000\u4e00\u8d1d\u7ea4\u7ea4\u7389\u624b\uff0c\u6a2a\u91cc\u98de\u6765\uff0c\u67b6\u4f4f\u4e86\u957f\u886b\u4e2d\u5e74\u7684\u53f3\u624b\u3002 \u3000\u3000\u76f4\u5230\u6b64\u523b\uff0c\u90a3\u559d\u53eb\u5927\u7237\u7684\u58f0\u97f3\u624d\u4f20\u5165\u8033\u9645\u3002 \u3000\u3000\u201c\u597d\uff0c\u597d\uff01\u6d1b\u9633\u5c45\u679c\u7136\u662f\u85cf\u9f99\u5367\u864e\u4e4b\u5730\uff0c\u4e00\u4e2a\u4f8d\u9152\u7684\u4e2b\u5934\uff0c\u4e5f\u6709\u7740\u5982\u6b64\u7684\u8eab\u624b\u3002\u201d \u3000\u3000\u957f\u886b\u6587\u58eb\u76ee\u5149\u8f6c\u52a8\uff0c\u53d1\u89c9\u90a3\u67b6\u4f4f\u53f3\u624b\u7684\u4eba\uff0c\u7adf\u662f\u6842\u82b1\uff0c\u53f3\u624b\u4e00\u6c89\uff0c\u4e94\u6307\u4e0a\u7ffb\u548c\u62ff\u6842\u82b1\u7684\u53f3\u8155\u3002 \u3000\u3000\u7eff\u8863\u98d8\u52a8\uff0c\u6842\u82b1\u95ea\u9000\u4e09\u5c3a\uff0c\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5927\u7237\uff0c\u8bf8\u4f4d\u662f\u6765\u5403\u9152\u7684\u3002\u8fd8\u662f\u6765\u6253\u4eba\u7684\uff0c\u6211\u4eec\u90fd\u662f\u4f8d\u5e94\u5ba2\u4eba\u7684\u9152\u5973\uff0c\u90a3\u91cc\u7981\u5f97\u8d77\u5927\u7237\u4eec\u7684\u62f3\u811a\uff01\u201d \u3000\u3000\u957f\u886b\u6587\u58eb\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8bf4\u7684\u597d\uff0c\u597d\u7537\u4e0d\u8ddf\u5973\u6597\uff0c\u4f60\u627e\u4e2a\u7537\u4eba\u6765\u5427\uff1f\u201d \u3000\u3000\u72ec\u5c45\u9996\u4f4d\u7684\u4e2d\u5e74\u8c6a\u5ba2\uff0c\u770b\u4e86\u90a3\u957f\u886b\u4eba\u4e00\u773c\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a \u3000\u3000\u201c\u67b6\u65e2\u7136\u6253\u4e0d\u6210\u3001\u90a3\u5c31\u6454\u4e24\u53ea\u7389\u676f\u7ed9\u6211\u542c\u542c\u5427\uff01\u201d \u3000\u3000\u957f\u886b\u6587\u58eb\u5e94\u4e86\u4e00\u58f0\uff0c\u4f38\u624b\u6293\u8d77\u4e24\u53ea\u7389\u676f\uff0c\u6454\u5728\u7816\u5730\u4e0a\uff0c\u54cd\u8d77\u4e24\u58f0\u8106\u545c\u3002 \u3000\u3000\u90a3\u662f\u4e0a\u597d\u767d\u7389\uff0c\u96d5\u5de5\u4e5f\u5f88\u7cbe\u7ec6\uff0c\u788e\u88c2\u7684\u58f0\u97f3\u4e5f\u5341\u5206\u8bf4\u8033\u3002 \u3000\u3000\u83ca\u82b1\u7684\u8138\u8272\u4e00\u53d8\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u770b\u8d77\u6765\uff0c\u8bf8\u4f4d\u4e0d\u662f\u6765\u5403\u9152\uff0c\u800c\u662f\u8bda\u5fc3\u627e\u9ebb\u70e6\u4e86\uff1f\u201d \u3000\u3000\u957f\u886b\u6587\u58eb\u9053\uff1a\u201c\u4e24\u4f4d\u59d1\u5a18\u5982\u679c\u8fd8\u4e0d\u77e5\u8da3\uff0c\u90a3\u5c31\u522b\u602a\u5728\u4e0b\u8fde\u5973\u4eba\u4e5f\u8981\u6253\u4e86\uff01\u201d \u3000\u3000\u201c\u5c0f\u5973\u5b50\u662f\u5403\u9ea6\u5403\u996d\u957f\u5927\u7684\uff0c\u53ef\u4e0d\u662f\u88ab\u4eba\u5413\u5927\u7684\u2026\u2026\u201d\u83ca\u82b1\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8d35\u5ba2\u771f\u60f3\u6253\u4eba\uff0c\u5c0f\u59b9\u5c31\u5949\u966a\u51e0\u62db\u5427\uff01\u201d \u3000\u3000\u201c\u597d\uff01\u4f60\u5c0f\u5fc3\u4e86\u2026\u2026\u201d\u957f\u886b\u6587\u58eb\u53f3\u624b\u4e00\u6325\uff0c\u62cd\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u83ca\u82b1\u7adf\u4e0d\u9000\u907f\uff0c\u53f3\u624b\u7ffb\u8155\u6a2a\u5207\uff0c\u5de6\u624b\u201c\u53cc\u9f99\u53d6\u73e0\u201d\u70b9\u5411\u53cc\u6c11\u4e24\u624b\u5e76\u7528\uff0c\u4e00\u653b\u4e00\u5b88\u3002 \u3000\u3000\u90a3\u9ad8\u5c45\u9996\u4f4d\u7684\u8c6a\u5ba2\uff0c\u4f3c\u662f\u771f\u7684\u5f88\u559c\u6b22\u770b\u4eba\u6253\u67b6\uff0c\u7adf\u662f\u770b\u5f97\u5168\u795e\u8d2f\u6ce8\u3002 \u3000\u3000\u4e24\u4e2a\u7ad9\u5728\u8eab\u540e\u7684\u4e2d\u5e74\u5065\u4ec6\uff0c\u4e5f\u662f\u77aa\u770b\u53cc\u76ee\uff0c\u51dd\u795e\u89c2\u6218\u3002 \u3000\u3000\u957f\u886b\u6587\u58eb\u7684\u8eab\u8eaf\u4fa7\u8f6c\uff0c\u53ea\u662f\u90a3\u4e48\u4e00\u8f6c\uff0c\u4e0d\u4f46\u907f\u5f00\u4e86\u83ca\u82b1\u7684\u51cc\u5389\u653b\u52bf\uff0c\u4eba\u4e5f\u95ea\u5230\u4e86\u83ca\u82b1\u8eab\u540e\uff0c\u53f3\u624b\u62cd\u51fa\uff0c\u51fb\u4e2d\u83ca\u82b1\u7684\u53f3\u540e\u80a9\u4e0a\u3002 \u3000\u3000\u638c\u4e2d\u542b\u529b\u4e0d\u91cd\uff0c\u6253\u5f97\u83ca\u82b1\u8eab\u4e0d\u7531\u5df1\u5730\u5411\u524d\u51b2\u51fa\u4e24\u6b65\u3002 \u3000\u3000\u4e24\u4e2a\u89c2\u6218\u7684\u4e2d\u5e74\u5065\u4ec6\uff0c\u6697\u6697\u70b9\u5934\u3002 \u3000\u3000\u8fd9\u4e00\u638c\u6ca1\u6709\u4f24\u5230\u83ca\u82b1\uff0c\u5374\u6fc0\u8d77\u4e86\u83ca\u82b1\u7684\u6012\u706b\uff0c\u53cd\u8eab\u6012\u53f1\u3002\u98de\u6251\u800c\u4e0a\uff0c\u53cc\u638c\u4ea4\u9519\uff0c\u5c55\u5f00\u4e86\u8fc5\u5982\u98d8\u98ce\u7684\u653b\u52bf\u3002 \u3000\u3000\u957f\u886b\u6587\u58eb\u65bd\u5c55\u8eab\u6cd5\uff0c\u4ee5\u95ea\u907f\u4e3a\u4e3b\uff0c\u5076\u800c\u8fd8\u51fb\u4e00\u62db\u3002 \u3000\u3000\u90a3\u4e2d\u5e74\u8c6a\u5ba2\u53cc\u76ee\u76ef\u7740\u770b\u7740\u83ca\u82b1\u7684\u653b\u52bf\u3002\u53e3\u4e2d\u5583\u5583\u81ea\u8bed\uff1a\u201c\u8fd9\u4e00\u62db\u9887\u50cf\u5c11\u6797\u7684\u7f57\u6c49\u62f3\uff01\u8fb0\u5dde\u8a00\u5bb6\u95e8\u7684\u2018\u7834\u68fa\u624b\u2019\uff0c\u9e70\u722a\u95e8\u7684\u2018\u9501\u5589\u4e09\u722a\u2019\u2026\u2026\u201d\u3002 \u3000\u3000\u83ca\u82b1\u8fde\u51fa\u5947\u62db\uff0c\u4e2d\u5e74\u8c6a\u5ba2\u5374\u4f4e\u58f0\u7684\u53eb\u51fa\u7531\u6765\u3002 \u3000\u3000\u4e2d\u5e74\u6587\u58eb\u4ed7\u7740\u7075\u5de7\u8eab\u6cd5\uff0c\u8fde\u8fde\u907f\u5f00\u4e86\u83ca\u82b1\u5341\u51e0\u6b21\u7684\u653b\u52bf\u3002\u5374\u4e5f\u88ab\u903c\u5f97\u6709\u4e9b\u614c\u4e71\u8d77\u6765\u3002 \u3000\u3000\u539f\u6765\uff0c\u83ca\u82b1\u8fd9\u5341\u51e0\u62db\u7684\u653b\u52bf\uff0c\u7adf\u7136\u7528\u51fa\u4e86\u5341\u51e0\u79cd\u8def\u6570\u5168\u4e0d\u540c\u7684\u6b66\u529f\uff0c\u6bcf\u4e00\u62db\u90fd\u662f\u5404\u5927\u95e8\u6d3e\u4e2d\u7684\u6740\u624b\u5947\u5b66\u3002 \u3000\u3000\u4e2d\u5e74\u6587\u58eb\u5728\u83ca\u82b1\u51cc\u5389\u653b\u52bf\u4e2d\uff0c\u4f3c\u662f\u4e5f\u6253\u51fa\u4e86\u6012\u706b\uff0c\u5c55\u5f00\u53cd\u4f46\u89c1\u638c\u6307\u4ea4\u9519\uff0c\u62db\u672f\u8f6c\u5316\u4e4b\u95f4\uff0c\u5b8c\u5168\u638c\u63e1\u4e86\u53d8\u5316\u7684\u5148\u673a\u3002\u7acb\u523b\u963b\u904f\u4e86\u83ca\u82b1\u7684\u6076\u6bd2\u653b\u52bf\u3002 \u3000\u3000\u6842\u82b1\u4e00\u76b1\u7709\u5934\uff0c\u9053\uff1a\u201c\u8fd9\u4f4d\u5927\u7237\uff0c\u4f60\u56db\u5341\u591a\u5c81\u4e86\u5427\uff01\u6b3a\u4fae\u4e00\u4e2a\u5973\u5b69\u5b50\uff0c\u4e5f\u4e0d\u89c9\u7740\u4e22\u4eba\u5417\uff1f\u201d \u3000\u3000\u4f59\u97f3\u8fd8\u5728\u820c\u5c16\u4e0a\u6253\u8f6c\uff0c\u4eba\u5df2\u98de\u8eab\u800c\u4e0a\u3002 \u3000\u3000\u79cb\u82b1\u53cc\u5a62\uff0c\u5c55\u5f00\u4e86\u8054\u624b\u5408\u51fb\u3002 \u3000\u3000\u62b1\u5200\u7684\u9752\u8863\u7ae5\u5b50\u4e00\u76b1\u7709\uff0c\u4e3e\u6b65\u8981\u52a8\uff0c\u5374\u88ab\u90a3\u6367\u5251\u7684\u7ae5\u5b50\u4ee5\u76ee\u5149\u62e6\u4f4f\u3002 \u3000\u3000\u90a3\u4e2d\u5e74\u8c6a\u5ba2\u548c\u7ad9\u5728\u8eab\u540e\u7684\u4e24\u4e2a\u4e2d\u5e74\u4ece\u4ec6\uff0c\u516d\u53ea\u773c\u775b\uff0c\u5168\u77aa\u5f97\u5706\u5706\u7684\uff0c\u770b\u4e09\u4eba\u52a8\u624b\u9887\u4e3a\u795e\u901f\uff0c\u4f46\u8138\u4e0a\u7684\u795e\u60c5\uff0c\u5374\u662f\u4e00\u7247\u51b7\u8083\u3002 \u3000\u3000\u957f\u886b\u6587\u58eb\u529b\u654c\u79cb\u82b1\u53cc\u624b\uff0c\u867d\u672a\u9732\u51fa\u8d25\u8c61\uff0c\u4f46\u795e\u60c5\u95f4\u4e00\u7247\u51b7\u5389\u3002\u663e\u662f\u6253\u7684\u5e76\u4e0d\u8f7b\u677e\u3002 \u3000\u3000\u5ffd\u7136\u95f4\u623f\u95e8\u5440\u7136\uff0c\u4e00\u4e2a\u9ed1\u888d\u4eba\u7f13\u6b65\u800c\u5165\u3002 \u3000\u3000\u8138\u4e0a\u51e0\u6761\u7eb5\u6a2a\u4ea4\u9519\u7684\u7eb9\u8def\uff0c\u4f7f\u4ed6\u770b\u4e0a\u53bb\u6709\u7740\u4e00\u80a1\u51b7\u9177\u65e0\u60c5\u7684\u5473\u9053\uff0c\u6b63\u662f\u90a3\u4f4d\u5429\u5490\u4f19\u8ba1\u5e26\u4ed6\u4eec\u8fdb\u5165\u83ca\u82b1\u5385\u7684\u5165\u3002 \u3000\u3000\u201c\u4f4f\u624b\uff01\u201d\u9ed1\u888d\u4eba\u51b7\u51b7\u559d\u4e86\u4e00\u58f0\uff0c\u79cb\u82b1\u53cc\u5a62\uff0c\u7acb\u523b\u98d8\u8eab\u800c\u9000\uff0c\u9f50\u9f50\u8eaf\u8eab\u4e00\u672d\uff0c\u9053\uff1a\u201c\u97e9\u526f\u603b\u7ba1\u3002\u201d \u3000\u3000\u97e9\u526f\u603b\u7ba1\uff0c\u4e3b\u7ba1\u6d1b\u9633\u5c45\u996d\u5e84\u90e8\u7684\u9996\u8111\u3002 \u3000\u3000\u201c\u4f60\u4eec\u9000\u4e0b\u53bb\u2026\u2026\u201d\u9ed1\u888d\u4eba\u4f4e\u58f0\u8bf4\u9053\u3002 \u3000\u3000\u83ca\u82b1\u9053\uff1a\u201c\u8fd9\u4e9b\u4eba\u662f\u8bda\u5fc3\u6765\u627e\u9ebb\u70e6\u7684\uff0c\u6454\u6746\u6253\u4eba\uff0c\u5168\u4e0d\u8bb2\u7406\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\uff0c\u65e2\u662f\u627e\u9ebb\u70e6\u7684\uff0c\u95f8\u4e0d\u7740\u4f60\u4eec\u8d39\u5fc3\u4e86\uff0c\u4e0b\u53bb\u4f11\u606f\u5427\uff01\u201d\u97e9\u526f\u603b\u7ba1\u8f7b\u8f7b\u4e00\u6325\u53f3\u624b\u3002 \u3000\u3000\u79cb\u82b1\u53cc\u5a62\u4e0d\u6562\u591a\u7559\uff0c\u8eb2\u8eab\u800c\u9000\u3002 \u3000\u3000\u6842\u82b1\u4f3c\u662f\u5fc3\u6709\u672a\u7518\uff0c\u4eba\u5230\u95e8\u53e3\uff0c\u5374\u53c8\u56de\u8fc7\u5934\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u7fa4\u4eba\u6765\u8def\u53ef\u7591\uff0c\u526f\u603b\u7ba1\u4e0d\u8981\u653e\u8fc7\u4ed6\u4eec\u3002\u201d \u3000\u3000\u5a07\u8eaf\u75be\u95ea\uff0c\u6d88\u5931\u4e0d\u89c1\u3002 \u3000\u3000\u201c\u5728\u4e0b\u97e9\u9738\uff0c\u8bf8\u4f4d\u662f\u2026\u2026\u201d\u653e\u4f4e\u4e86\u58f0\u97f3\uff0c\u63a5\u9053\uff1a\u201c\u5927\u59d0\uff0c\u4e24\u4e2a\u4e2b\u5934\u7684\u6b66\u529f\u5982\u4f55\uff1f\u201d \u3000\u3000\u4e2d\u5e74\u6587\u58eb\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u5389\u5bb3\uff0c\u679c\u7136\u662f\u827a\u4e1a\u7cbe\u6e5b\uff0c\u975e\u540c\u5c0f\u53ef\u3002 \u3000\u3000\u5927\u59d0\uff0c\u4e5f\u51e0\u4e4e\u5168\u529b\u65bd\u4e3a\u4e86\u2026\u2026\u201d \u3000\u3000\u63d0\u9ad8\u4e86\u58f0\u97f3\uff0c\u9053\uff1a\u201c\u6211\u4eec\u8981\u89c1\u6d1b\u9633\u5c45\u7684\u603b\u7ba1\u3002\u201c\u201c\u548c\u97e9\u67d0\u8bf4\u4e5f\u662f\u4e00\u6837\uff0c\u5982\u662f\u97e9\u67d0\u4eba\u4f5c\u4e0d\u4e86\u4e3b\u3002\u81ea\u4f1a\u5411\u603b\u7ba1\u8bf7\u793a\u3002\u201d \u3000\u3000\u201c\u4e0d\u884c\uff0c\u4f60\u4e0d\u8fc7\u4e00\u4e2a\u533a\u533a\u7684\u526f\u603b\u7ba1\uff0c\u80fd\u4f5c\u5f97\u51e0\u5206\u4e3b\u610f\uff0c\u6211\u4eec\u975e\u89c1\u603b\u7ba1\u4e0d\u53ef\u2026\u2026\u201d\u4e2d\u5e74\u8c6a\u5ba2\u6539\u4ee5\u5f88\u4f4e\u7684\u58f0\u97f3\uff0c\u63a5\u9053\uff1a\u201c\u4e56\u4e56\uff0c\u4e0d\u5f97\u4e86\u554a\uff01\u8001\u53eb\u5316\u77a7\u5979\u4eec\u7528\u51fa\u4e86\u5341\u51e0\u79cd\u7684\u6b66\u529f\u3002\u90fd\u662f\u4e2d\u539f\u5404\u5927\u95e8\u6d3e\u7684\u5947\u5b66\uff0c\u8fd8\u6709\u4e00\u90e8\u5206\uff0c\u8001\u53eb\u5316\u77a7\u4e0d\u51fa\u6765\uff1f\u201d \u3000\u3000\u9ed1\u888d\u4eba\u9053\uff1a\u201c\u6625\u3001\u590f\u3001\u79cb\u3001\u51ac\uff0c\u56db\u5b63\u82b1\u5a62\u3002\u4f3c\u662f\u4e00\u5b63\u5f3a\u8fc7\u4e00\u5b63\uff0c\u4f46\u79cb\u82b1\u4e8c\u5a62\uff0c\u80fd\u548c\u5927\u59d0\u4ea4\u624b\u6570\u5341\u62db\uff0c\u5012\u4e5f\u51fa\u4e86\u6211\u7684\u610f\u6599\u4e4b\u5916\u3001\u770b\u6765\u51ac\u6885\u90a3\u4e2a\u4e2b\u5934\u3002\u66f4\u6709\u4e9b\u4ee5\u6d4b\u9ad8\u6df1\u4e86\u3002\u4e5f\u8bb8\u3002\u5979\u4eec\u4e5f\u662f\u6d3e\u9a7b\u5728\u6d1b\u9633\u5c45\u4e3b\u529b\u4e4b\u4e00\u2026\u2026\u201d \u3000\u3000\u957f\u886b\u6587\u58eb\u63a5\u9053\uff1a\u201c\u4eca\u65e5\u5982\u662f\u4e00\u51fb\u4e0d\u4e2d\uff0c\u53ea\u6015\u96be\u4ee5\u518d\u6709\u673a\u4f1a\u2026\u2026\u201d \u3000\u3000\u9ed1\u888d\u4eba\u53cc\u76ee\u4e2d\u795e\u5149\u4e00\u53d8\uff0c\u9ad8\u58f0\u63a5\u9053\uff1a\u201c\u65e2\u7136\u8bf8\u4f4d\u975e\u89c1\u603b\u7ba1\u4e0d\u53ef\uff0c\u8bf7\u5728\u6b64\u5730\u7a0d\u4faf\uff0c\u5728\u4e0b\u8fd9\u5c31\u6d3e\u4eba\u53bb\u8bf7\uff0c\u4e0d\u8fc7\uff0c\u4e11\u8bdd\u8bf4\u5728\u524d\u5934\u3002 \u3000\u3000\u5982\u662f\u8bf8\u4f4d\u64c5\u79bb\u6b64\u5385\uff0c\u90a3\u5c31\u4e0d\u80fd\u602a\u97e9\u67d0\u4eba\u5931\u793c\u4e86\u3002\u201d \u3000\u3000\u201c\u5144\u5f1f\uff0c\u603b\u7ba1\u7684\u6b66\u529f\u5982\u4f55\uff1f\u201d\u8fd9\u4e00\u6b21\u7528\u7684\u672c\u97f3\uff0c\u67d4\u67d4\u8106\u8106\uff0c\u7adf\u662f\u5f20\u56db\u59d1\u7684\u58f0\u97f3\u3002 \u3000\u3000\u201c\u4e0d\u4f1a\u5f88\u5dee\uff0c\u5927\u59d0\u4e0d\u53ef\u604b\u6218\uff0c\u79bb\u6b64\u4e4b\u540e\uff0c\u8bf7\u56de\u98ce\u96ea\u5c0f\u9986\uff0c\u4e24\u65e5\u4e4b\u5185\u5c0f\u5f1f\u5b9a\u5f53\u8d76\u5f80\u4e00\u6664\u3002\u201d\u662f\u6c5f\u67ab\u7684\u58f0\u97f3\uff0c\u672c\u6765\u561b\uff01\u6c5f\u67ab\u5c31\u662f\u97e9\u9738\u3002 \u3000\u3000\u90a3\u5f20\u7cbe\u5de7\u7edd\u4f26\u7684\u4eba\u76ae\u9762\u5177\uff0c\u51e0\u9053\u660e\u663e\u7684\u4ea4\u9519\u7eb9\u8def\u3002\u628a\u4e00\u4e2a\u9b45\u529b\u56db\u5c04\u7684\u7f8e\u7537\u5b50\uff0c\u53d8\u5f97\u9762\u76ee\u9634\u68ee\u3002 \u3000\u3000\u53ea\u542c\u4e00\u4e2a\u4f4e\u6c89\u7684\u58f0\u97f3\uff0c\u4f20\u5165\u5ba4\u4e2d\uff0c\u9053\uff1a\u201c\u97e9\u526f\u603b\u7ba1\uff0c\u4ec0\u4e48\u4eba\u975e\u8981\u89c1\u672c\u5ea7\u4e0d\u53ef\uff1f\u201d \u3000\u3000\u90a3\u58f0\u97f3\u4f3c\u662f\u7531\u5f88\u8fdc\u7684\u5730\u65b9\u98d8\u4f20\u8fc7\u6765\u3002\u4f46\u58f0\u843d\u4eba\u73b0\uff0c\u4e00\u4e2a\u56db\u5341\u591a\u5c81\uff0c\u8eab\u7740\u9526\u888d\u7684\u5927\u6c49\uff0c\u5df2\u7f13\u6b65\u884c\u5165\u83ca\u82b1\u5385\u4e2d\u3002 \u3000\u3000\u97e9\u9738\u75be\u9000\u4e24\u6b65\uff0c\u8f6c\u8fc7\u8eab\u5b50\uff0c\u606d\u606d\u656c\u656c\u7684\u62b1\u62f3\u4e00\u793c\uff0c\u9053\uff1a\u201c\u5c5e\u4e0b\u773c\u62d9\uff0c\u77a7\u4e0d\u51fa\u6765\u4eba\u8eab\u4efd\u2026\u2026\u201d \u3000\u3000\u9526\u888d\u4eba\u55ef\u4e86\u4e00\u58f0\uff0c\u53f3\u624b\u5fae\u5fae\u4e00\u6446\uff0c\u97e9\u9738\u7acb\u523b\u9000\u5230\u9526\u888d\u4eba\u7684\u8eab\u4fa7\uff0c\u5782\u624b\u800c\u7acb\uff0c\u72b6\u6781\u606d\u987a\u3002 \u3000\u3000\u8fd9\u548c\u4ed6\u65e5\u524d\u5728\u98ce\u96ea\u5c0f\u9986\u4e4b\u4e2d\uff0c\u7eb5\u8bba\u6c5f\u6e56\u5927\u4e8b\u7684\u8c6a\u58ee\u6c14\u52bf\uff0c\u5b8c\u5168\u5224\u82e5\u4e24\u4eba\u3002 \u3000\u3000\u5f20\u56db\u59d1\u770b\u5f97\u6709\u4e9b\u60f3\u7b11\uff0c\u6697\u9053\uff1a\u201c\u6c5f\u5144\u5f1f\u554a\uff01\u4f60\u4e5f\u771f\u662f\u592a\u59d4\u66f2\u81ea\u5df1\u4e86\u3002\u201d \u3000\u3000\u9526\u888d\u4eba\u7684\u4e24\u9053\u773c\u795e\u5728\u51e0\u4eba\u8138\u4e0a\u5f88\u4ed4\u7ec6\u7684\u77a7\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u55ef\uff01 \u3000\u3000\u8bf8\u4f4d\u90fd\u6ca1\u6709\u5e26\u9762\u5177\u2026\u2026\u201d \u3000\u3000\u7ad9\u5728\u4e00\u4fa7\u7684\u6c5f\u67ab\u542c\u5f97\u5fc3\u5934\u4e00\u9707\uff0c\u5fd6\u9053\uff1a\u201c\u6211\u6bcf\u5929\u6234\u7740\u9762\u5177\u548c\u4ed6\u89c1\u9762\uff0c\u4e0d\u77e5\u4ed6\u662f\u5426\u65e9\u5df2\u77a7\u4e86\u51fa\u6765\uff1f\u201d \u3000\u3000\u201c\u4f60\u8fd8\u80fd\u77a7\u51fa\u4e9b\u4ec0\u4e48\uff1f\u2026\u2026\u201d\u4e2d\u5e74\u8c6a\u5ba2\u795e\u60c5\u51b7\u51b7\u95ee\u9053\u3002 \u3000\u3000\u9526\u888d\u4eba\u4e00\u76b1\u7709\u5934\uff0c\u9053\uff1a\u201c\u8bf8\u4f4d\u90fd\u9762\u751f\u7684\u5f88\uff1f\u201d \u3000\u3000\u9700\u77e5\u5f20\u56db\u59d1\u7684\u6613\u5bb9\u624b\u6cd5\uff0c\u5999\u5728\u989c\u8272\u8c03\u5408\uff0c\u7740\u624b\u5904\u5728\u6539\u53d8\u4e00\u4e2a\u4eba\u7684\u795e\u97f5\uff0c\u6de1\u6de1\u51e0\u7b14\uff0c\u753b\u9f99\u70b9\u775b\uff0c\u4f7f\u4e00\u4e2a\u4eba\u5f62\u8c8c\u5b8c\u5168\u6539\u89c2\u3002 \u3000\u3000\u201c\u8bf4\u5427\uff01\u4f60\u6307\u540d\u8981\u89c1\u672c\u603b\u7ba1\uff0c\u6709\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u9526\u888d\u4eba\u77a7\u4e0d\u51fa\u6765\u4eba\u8eab\u4efd\uff0c\u6539\u53d8\u4e86\u8bdd\u9898\uff0c\u5355\u5200\u76f4\u5165\uff0c\u903c\u95ee\u539f\u56e0\uff1f \u3000\u3000\u4e2d\u5e74\u8c6a\u5ba2\u5fae\u5fae\u4e00\u7b11\uff0c\u4ed8\u9053\uff1a\u201c\u5f20\u56db\u59d1\u7684\u6613\u5bb9\u624b\u6cd5\uff0c\u679c\u7136\u5999\u7edd\u6c5f\u6e56\uff0c\u72ec\u6b65\u6b66\u6797\uff0c\u5f53\u4e4b\u65e0\u6127\uff0c\u8fde\u9b54\u773c\u9093\u98de\u4e5f\u77a7\u4e0d\u51fa\u4e00\u70b9\u7834\u7efd\u3002\u201d \u3000\u3000\u53e3\u4e2d\u5374\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u5728\u4e0b\u60f3\u548c\u603b\u7ba1\u8c08\u7b14\u4ea4\u6613\uff1f\u201d \u3000\u3000\u201c\u4ea4\u6613\u2026\u2026\u201d\u9093\u98de\u5475\u5475\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u597d\uff01\u597d\uff01\u53ea\u8981\u4ea4\u6613\u6709\u4ef7\u503c\uff0c\u9093\u67d0\u4eba\u7edd\u4e0d\u4f1a\u8ba9\u8bf8\u4f4d\u5931\u671b\uff0c\u8bf4\u8bf4\u770b\uff0c\u4ec0\u4e48\u4ea4\u6613\uff1f\u201d \u3000\u3000\u4e2d\u5e74\u8c6a\u5ba2\u7565\u4e00\u6c89\u541f\uff0c\u9053\uff1a\u201c\u6d1b\u9633\u5c45\u4e2d\u6709\u4e00\u4f4d\u9752\u51e4\u59d1\u5a18\uff0c\u603b\u7ba1\u80fd\u4e0d\u80fd\u4ee3\u4ed6\u505a\u4e3b\uff1f\u201d \u3000\u3000\u201c\u6d1b\u9633\u5c45\u4e2d\u7684\u4eba\uff0c\u90fd\u5728\u9093\u67d0\u7684\u7ba1\u8f96\u4e4b\u4e0b\u2026\u2026\u201d\u7a81\u7136\u4f4f\u53e3\uff0c\u76ee\u5149\u4e2d\u95ea\u52a8\uff0c\u770b\u770b\u56db\u53ea\u653e\u5728\u90a3\u4e2d\u5e74\u8c6a\u5ba2\u8eab\u4fa7\u7684\u63cf\u91d1\u7bb1\u5b50\uff0c\u63a5\u9053\uff1a \u3000\u3000\u201c\u9601\u4e0b\u53ef\u662f\u60f3\u6e05\u9752\u51e4\u59d1\u5a18\u4f8d\u4faf\u4e00\u6bb5\u5c0f\u66f2\u7801\uff1f\u4f55\u4e0d\u5230\u6d1b\u9633\u697c\u53bb\u3002\u90a3\u91cc\u6709\u96c5\u5ea7\u2026\u2026\u201d \u3000\u3000\u201c\u55ef\uff01\u5dee\u7684\u8fdc\u4e86\u2026\u2026\u201d\u4e2d\u5e74\u8c6a\u5ba2\u8bf4\uff1a\u201c\u5728\u4e0b\u662f\u60f3\u628a\u4eba\u5e26\u8d70\uff01\u201d \u3000\u3000\u201c\u5e26\u8d70\u2026\u2026\u201d\u9093\u98de\u4f3c\u662f\u542c\u5230\u4e86\u4e00\u4ef6\u5f88\u53ef\u7b11\u7684\u4e8b\u60c5\u3002\u4ef0\u4e8e\u6253\u4e2a\u54c8\u54c8\uff0c\u63a5\u9053\uff1a\u201c\u6d1b\u9633\u5c45\u5f00\u5f20\u4ee5\u6765\uff0c\u8fd9\u53ef\u662f\u4ece\u672a\u6709\u8fc7\u7684\u4e8b\uff0c\u65b0\u9c9c\uff01 \u3000\u3000\u65b0\u9c9c\uff01\u201d \u3000\u3000\u201c\u603b\u7ba1\u53ef\u80fd\u8bef\u89e3\u4e86\u655d\u4e1c\u4e3b\u7684\u610f\u601d\u2026\u2026\u201d\u6539\u626e\u957f\u886b\u6587\u58eb\u7684\u5f20\u56db\u59d1\u8bf4\uff1a\u201c\u8fd9\u662f\u4ea4\u6613\uff0c\u8981\u53cc\u65b9\u540c\u610f\uff0c\u6211\u4eec\u4e0d\u662f\u63b3\u4eba\uff0c\u662f\u4e70\u4eba\u3002\u201d\u8bed\u97f3\u539a\u91cd\uff0c\u5b8c\u5168\u7537\u5b50\u7684\u58f0\u97f3\u3002 \u3000\u3000\u201c\u5662\uff01\u2026\u2026\u201d\u9093\u98de\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u9752\u51e4\u59d1\u5a18\u4e00\u5929\u80fd\u66ff\u6d1b\u9633\u5c45\u8d5a\u4e0a\u4e09\u5343\u4e24\u94f6\u5b50\uff0c\u8bf8\u4f4d\u4e70\u5f97\u8d77\u5417\uff1f\u201d \u3000\u3000\u97e9\u9738\u4f4e\u58f0\u9053\uff1a\u201c\u8fd9\u4e9b\u4eba\u4e00\u6d3e\u80e1\u8a00\uff0c\u6211\u53bb\u53ec\u96c6\u9556\u624b\uff0c\u628a\u4ed6\u4eec\u5708\u8d77\u6765\u2026\u2026\u201c\u201c\u4ed6\u4eec\u8d70\u4e0d\u4e86\u7684\u2026\u2026\u201d\u9093\u98de\u5fae\u7b11\u9053\u3002 \u3000\u3000\u5f20\u56db\u59d1\u76ee\u5149\u8f6c\u52a8\uff0c\u679c\u7136\u53d1\u89c9\u4e86\u83ca\u82b1\u5385\u5916\uff0c\u4eba\u5f71\u95ea\u52a8\uff0c\u60f3\u6765\u3002 \u3000\u3000\u90a3\u4e9b\u5c31\u662f\u4e13\u4ee5\u5bf9\u4ed8\u5165\u4fb5\u654c\u4eba\u7684\u9556\u624b\u4e86\uff0c\u4e0d\u77e5\u8fd9\u4e13\u505a\u4fdd\u9556\u7684\u4eba\uff0c\u6b66\u529f\u662f\u5426\u8981\u9ad8\u660e\u4e00\u4e9b\uff1f \u3000\u3000\u90a3\u4e2d\u5e74\u8c6a\u5ba2\u773c\u770b\u5f62\u6210\u50f5\u5c40\u3001\u51b7\u7b11\u4e00\u58f0\u3001\u9053\uff1a\u201c\u4e70\u5356\u4e0d\u6210\u4ec1\u4e49\u5728\uff0c\u603b\u7ba1\u5c31\u7b97\u4e0d\u613f\u4ea4\u6613\uff0c\u4e5f\u4e0d\u7528\u5f3a\u628a\u6211\u4eec\u7559\u5728\u6d1b\u9633\u5c45\u5427\uff1f\u201d \u3000\u3000\u8001\u53eb\u5316\u6539\u626e\u7684\u4e2d\u5e74\u8c6a\u5ba2\uff0c\u76ee\u7779\u4e86\u79cb\u82b1\u4e8c\u5a62\u7684\u6b66\u529f\uff0c\u867d\u7136\u5fc3\u4e2d\u5341\u5206\u60ca\u5947\uff0c\u4f46\u5e76\u4e0d\u89c9\u5f97\u6709\u4ec0\u4e48\u53ef\u6015\u4e4b\u5904\uff0c\u5927\u4e0d\u4e86\u653e\u624b\u4e00\u640f\uff0c\u51b2\u51fa\u6d1b\u9633\u5c45\uff0c\u5e94\u8be5\u662f\u4e00\u4ef6\u5f88\u5e73\u5e38\u7684\u4e8b\u3002 \u3000\u3000\u4f46\u81ea\u9b54\u773c\u9093\u98de\u73b0\u8eab\u4e4b\u540e\uff0c\u94b1\u7f3a\u7684\u5fc3\u4e2d\u6709\u4e86\u5f88\u5927\u7684\u53d8\u5316\u3002 \u3000\u3000\u4ed6\u4e86\u89e3\u9093\u98de\u7684\u4e3a\u4eba\uff0c\u4e5f\u77e5\u9053\u6c60\u4e00\u8eab\u6b66\u529f\uff0c\u9ad8\u8fc7\u76ee\u5df1\u5f88\u591a\u3002 \u3000\u3000\u5c24\u5176\u662f\u90a3\u53cc\u9b54\u773c\uff0c\u6709\u8fc7\u76ee\u4e0d\u5fd8\u4e4b\u80fd\uff0c\u4e0d\u8bba\u662f\u4ec0\u4e48\u6b66\u529f\u7edd\u6267\u53ea\u8981\u4ed6\u770b\u8fc7\u4e00\u6b21\uff0c\u5c31\u80fd\u8bb0\u5728\u5fc3\u4e2d\uff0c\u5bfb\u601d\u7834\u89e3\u4e4b\u6cd5\u3002 \u3000\u3000\u4e09\u5341\u5e74\u524d\uff0c\u94b1\u7f3a\u548c\u9093\u98de\u52a8\u8fc7\u624b\uff0c\u8001\u53eb\u5316\u4ee5\u201c\u68d2\u6253\u54ee\u5929\u72ac\u201d\u51fb\u8d25\u9093\u98de\u3002 \u3000\u3000\u4f46\u534a\u4e2a\u6708\u540e\uff0c\u9093\u98de\u627e\u4e0a\u95e8\u6765\uff0c\u94b1\u7f3a\u88ab\u903c\u5f97\u6b65\u6b65\u8d25\u9000\uff0c\u53ea\u597d\u518d\u7528\u201c\u68d2\u6253\u54ee\u5929\u72ac\u201d\u4e00\u8bb0\u7edd\u62db\uff0c\u5374\u4e0d\u6599\u53cd\u53d7\u6240\u5236\u3002 \u3000\u3000\u8001\u53eb\u5316\u8bb0\u5fc6\u6df1\u523b\uff0c\u4ee5\u540e\u5c31\u518d\u672a\u89c1\u8fc7\u9b54\u773c\u9093\u98de\uff0c\u597d\u50cf\u9093\u98de\u7a81\u7136\u95f4\u5728\u6c5f\u6e56\u4e2d\u6d88\u5931\u4e86\uff0c\u60f3\u4e0d\u5230\u9093\u98de\u91cd\u73b0\u6c5f\u6e56\uff0c\u7adf\u7136\u662f\u6d1b\u9633\u5c45\u4e2d\u7684\u603b\u7ba1\u3002 \u3000\u3000\u5f80\u4e8b\u50cf\u70d9\u5370\u4e00\u822c\uff0c\u6df1\u690d\u94b1\u7f3a\u7684\u5fc3\u4e2d\uff0c\u4e5f\u4f7f\u5f97\u8fd9\u4f4d\u98ce\u5c18\u4e49\u4fa0\u3002 \u3000\u3000\u751f\u51fa\u4e86\u6781\u5927\u7684\u60ca\u9a87\u3002\u6c14\u52bf\u4e0a\u5927\u4e3a\u51cf\u5f31\u3002 \u3000\u3000\u4f46\u95fb\u9093\u98de\u7b11\u9053\uff1a\u201c\u5f53\u7136\uff0c\u5f53\u7136\uff0c\u8005\u4f4d\u5982\u662f\u8bda\u5fc3\u4ea4\u6613\u800c\u6765\uff0c\u867d\u7136\u6709\u70b9\u5192\u5931\uff0c\u4f46\u6d1b\u9633\u5c45\u662f\u63a5\u5f85\u516b\u65b9\u884c\u65c5\u7684\u5730\u65b9\uff0c\u6211\u4eec\u7edd\u4e0d\u4f1a\u5f97\u7f6a\u5ba2\u4eba\uff0c\u4f46\u5982\u8bf8\u4f4d\u662f\u8bda\u5fc3\u7838\u573a\u5b50\u7684\uff0c\u90a3\u5c31\u53c8\u5f53\u522b\u8bba\u4e86\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u6253\u91cf\u8fc7\u5385\u5916\u5f62\u52bf\uff0c\u76ee\u5149\u53c8\u8f6c\u6ce8\u5230\u9093\u98de\u7684\u8138\u4e0a\uff0c\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u542c\u603b\u7ba1\u7684\u53e3\u6c14\uff0c\u4f3c\u662f\u5fc3\u6709\u6240\u7591\u4e86\u3002\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u9093\u67d0\u4eba\u6b63\u5728\u7b49\u5f85\u8bf8\u4f4d\u8868\u73b0\u51fa\u4ea4\u6613\u7684\u8bda\u610f\uff0c\u2026\u2026\u201d \u3000\u3000\u5f20\u56db\u59d1\u53cc\u76ee\u76ef\u6ce8\u5728\u9093\u98de\u8eab\u4e0a\u77a7\u770b\uff0c\u4f3c\u662f\u8981\u770b\u6e05\u695a\u4ed6\u8eab\u4e0a\u6bcf\u4e00\u4e2a\u5177\u6709\u7279\u8272\u7684\u5730\u65b9\uff0c\u53e3\u4e2d\u5374\u6de1\u6de1\u7684\u8bf4\u9053\uff1a\u201c\u603b\u7ba1\u8981\u6211\u4eec\u5982\u4f55\u8868\u73b0\u8bda\u610f\uff1f\u201d \u3000\u3000\u201c\u9093\u67d0\u60f3\u5148\u770b\u770b\u8bf8\u4f4d\u51c6\u5907\u7529\u4ec0\u4e48\u4e1c\u897f\u8d2d\u4e70\u9752\u51e4\uff1f\u2026\u2026\u201d \u3000\u3000\u201c\u8fd9\u8981\u4f60\u603b\u7ba1\u5f00\u4e2a\u4ef7\u4e86\uff1f\u201d\u5f20\u56db\u59d1\u8bf4\uff1a\u201c\u6ee1\u5929\u8981\u4ef7\uff0c\u5c31\u5730\u8fd8\u94b1\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u54b1\u4eec\u5e26\u6765\u4e86\u76f8\u5f53\u591a\u7684\u4e00\u7b14\u8d22\u5bcc\uff0c\u4e5f\u6709\u8bda\u610f\u4f5c\u6210\u751f\u610f\u3002\u201d \u3000\u3000\u9093\u98de\u56de\u987e\u4e86\u97e9\u9738\u4e00\u773c\uff0c\u9053\uff1a\u201c\u53bb\u8bf7\u738b\u526f\u603b\u7ba1\u6765\u3002\u201d \u3000\u3000\u97e9\u9738\u4f4e\u58f0\u9053\uff1a\u201c\u603b\u7ba1\uff0c\u4e0d\u662f\u771f\u7684\u51c6\u5907\u628a\u9752\u98ce\u59d1\u5a18\u5356\u7ed9\u4ed6\u4eec\u5427\uff1f \u3000\u3000\u9752\u51e4\u59d1\u5a18\u53ef\u662f\u4e00\u682a\u6447\u94b1\u6811\u554a\uff01\u2026\u2026\u201d \u3000\u3000\u738b\u526f\u603b\u7ba1\u662f\u4e3b\u7ba1\u6d1b\u9633\u697c\u548c\u8d4c\u53f0\u7684\u4eba\uff0c\u4e5f\u662f\u9752\u51e4\u7684\u9876\u5934\u4e0a\u53f8\u3002 \u3000\u3000\u201c\u5982\u82e5\u4ef7\u94b1\u5408\u9002\uff0c\u9752\u51e4\u59d1\u5a18\u53c8\u4f55\u5c1d\u4e0d\u53ef\u4ee5\u5356\u4e86\u5462\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u2026\u2026\u201d\u97e9\u9738\u72b9\u8c6b\u4e86\u4e00\u4e0b\uff0c\u8f6c\u8eab\u800c\u53bb\u3002 \u3000\u3000\u8001\u53eb\u5316\u5fc3\u4e2d\u660e\u767d\uff0c\u9b54\u773c\u9093\u98de\u6b63\u5728\u4e0d\u7740\u75d5\u8ff9\u7684\u96c6\u4e2d\u5b9e\u529b\uff0c\u90a3\u4f4d\u738b\u526f\u603b\u7ba1\uff0c\u4e5f\u4e0d\u77e5\u9053\u662f\u4e00\u4e2a\u4ec0\u4e48\u6837\u7684\u4eba\u7269\uff0c\u662f\u548c\u9093\u98de\u4e00\u6837\u65e9\u5e74\u606f\u9690\u7684\u51f6\u4eba\uff1f\u8fd8\u662f\u4ed6\u4eec\u8bad\u7ec3\u51fa\u6765\u7684\u65b0\u79c0\u3002\u4f46\u4e0d\u8bba\u5982\u4f55\uff1f\u4e00\u5b9a\u662f\u4e00\u4f4d\u5f88\u96be\u7f20\u7684\u4eba\u7269\uff1f \u3000\u3000\u4f46\u89c1\u5f20\u56db\u59d1\u6beb\u65e0\u8b66\u89c9\uff0c\u5fc3\u4e2d\u6697\u6697\u53eb\u82e6\u3002 \u3000\u3000\u5c40\u52bf\u53d8\u5316\u6210\u5982\u6b64\u4e00\u4e2a\u5f62\u6001\u3002\u5b8c\u5168\u51fa\u4e86\u8001\u53eb\u5316\u610f\u6599\u4e4b\u5916\uff0c\u9b54\u773c\u9093\u98de\u7684\u51fa\u73b0\u3002\u6700\u4f7f\u94b1\u7f3a\u5403\u60ca\u3002 \u3000\u3000\u5f20\u56db\u59d1\u62d6\u5ef6\u65f6\u95f4\uff0c\u4e0d\u4f5c\u51b3\u5b9a\uff0c\u4e5f\u4f7f\u8001\u53eb\u5316\u5934\u75bc\u4e0d\u5df1\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u6700\u610f\u5916\u7684\u8fd8\u662f\u6b63\u503c\u91cd\u8981\u5173\u53e3\uff0c\u97e9\u9738\u5374\u88ab\u9093\u98de\u9063\u6d3e\u79bb\u8001\u5b9e\u8bf4\uff0c\u94b1\u7f3a\u5e76\u4e0d\u592a\u6e05\u695a\u6c5f\u67ab\u6b66\u529f\u5982\u4f55\uff1f \u3000\u3000\u4f46\u4ed6\u5374\u6709\u79cd\u5947\u602a\u7684\u611f\u89c9\u3002\u6c5f\u67ab\u80fd\u7ed9\u4eba\u4e00\u79cd\u5b89\u5b9a\u7684\u529b\u91cf\uff0c\u73b0\u5728\u3002\u5316\u540d\u97e9\u9738\u7684\u6c5f\u67ab\u4e5f\u8d70\u4e86\u3002 \u3000\u3000\u8001\u53eb\u5316\u611f\u53d7\u5230\u7684\u538b\u529b\u3002\u53c8\u589e\u52a0\u4e86\u4e0d\u5c11\uff1f \u3000\u3000\u5f20\u56db\u59d1\u76ee\u7779\u6c5f\u67ab\u79bb\u53bb\uff0c\u4e5f\u6709\u4e9b\u832b\u71c3\u7684\u611f\u89c9\uff0c\u4e0d\u77e5\u8be5\u5982\u4f55\u5904\u7406\u76ee\u524d\u8fd9\u4e2a\u6df7\u6dc6\u4e0d\u6e05\u7684\u573a\u9762\u3002 \u3000\u3000\u4e24\u4e2a\u4e2d\u5e74\u5065\u4ec6\uff0e\u662f\u80e1\u840d\u3001\u6bb5\u4e5d\u6539\u626e\uff0c\u8fd9\u4e9b\u4eba\u90fd\u6709\u7740\u4e30\u5bcc\u7684\u6c5f\u6e56\u9605\u5386\uff0c\u7ecf\u5386\u8fc7\u5927\u98ce\u5927\u6d6a\uff0c\u4f46\u5374\u5f88\u5c11\u628a\u81ea\u5df1\u7f6e\u4e8e\u6b7b\u5730\u800c\u540e\u751f\u7684\u7edd\u5883\u3002\u4e00\u65e6\u8ba1\u5212\u5728\u8fdb\u884c\u4e2d\u6709\u4e9b\u53d8\u5316\uff0c\u90e8\u6709\u7740\u4e00\u5206\u60f6\u60d1\u4e0d\u5b89\u3002 \u3000\u3000\u6700\u4e3a\u6c89\u7740\u7684\u5012\u662f\u90a3\u4e24\u4e2a\u6367\u5251\u3001\u62b1\u5200\u7684\u7ae5\u5b50\uff0c\u4ed6\u65e7\u795e\u60c5\u58ee\u8083\uff0c\u5185\u5fc3\u4e2d\u4e5f\u5145\u6ee1\u7740\u8dc3\u8dc3\u6b32\u8bd5\u7684\u6597\u5fd7\uff0c\u5f62\u8bf8\u4e8e\u5916\u7684\u662f\u4e00\u5404\u5927\u65e0\u754f\u7684\u51db\u7136\u6c14\u52bf\u3002 \u3000\u3000\u53cc\u65b9\u6682\u65f6\u5f62\u6210\u4e86\u4e00\u79cd\u6c89\u9ed8\u7684\u5bf9\u5cd9\u3002 \u3000\u3000\u4f46\u4e8b\u5b9e\u4e0a\u5374\u662f\u9093\u98de\u638c\u63e1\u4e86\u4e00\u5207\u4e3b\u52a8\uff0c\u4ed6\u8981\u97e9\u9738\u53bb\u8bf7\u738b\u526f\u603b\u7ba1\uff0c\u4e5f\u53ef\u80fd\u662f\u53bb\u8c03\u96c6\u66f4\u591a\u7684\u4eba\u624b\u6765\uff0c\u80c6\u53c8\u80fd\u628a\u76ee\u4e0b\u654c\u5be1\u6211\u4f17\u7684\u73b0\u573a\u5f62\u52bf\uff0c\u7ed9\u51bb\u7ed3\u4f4f\uff0c\u7b49\u5f85\u53d8\u5316\u3002 \u3000\u3000\u8001\u53eb\u5316\u7a81\u7136\u60f3\u5230\u4e86\u6c5f\u67ab\u8bf4\u8fc7\uff0c\u4e2d\u539f\u6b66\u6797\u4e2d\u4f20\u627f\u6cd5\u5219\uff0c\u628a\u5145\u6ee1\u7740\u5bf8\u534e\u3002\u671d\u6c14\u7684\u5c11\u5e74\u5b50\u5f1f\u3001\u538b\u7f29\u6210\u65b9\u65b9\u6b63\u6b63\u7684\u5c0f\u5927\u4eba\uff0c\u9047\u4e0a\u4e86\u975e\u5e38\u7684\u53d8\u51b5\uff0c\u7adf\u4e0d\u77e5\u632f\u8d77\u5e94\u53d8\u2026\u2026\u3002 \u3000\u3000\u73b0\u5728\u3002\u5c82\u4e0d\u5c31\u662f\u8fd9\u79cd\u5c40\u9762\u3002\u6211\u4eec\u6765\u6d1b\u9633\u5c45\u672c\u6709\u6240\u4e3a\u3001\u600e\u4f1a\u88ab\u9093\u98de\u62ff\u8bdd\u5957\u4f4f\uff0c\u4e3b\u52a8\u5730\u51fa\u51fb\uff0c\u5374\u53d8\u6210\u4e86\u88ab\u52a8\u7684\u88ab\u56f0\uff0c\u8ba9\u4eba\u5bb6\u6709\u5145\u5206\u7684\u65f6\u95f4\u5e03\u7f6e\uff0c\u4e2d\u539f\u6b66\u6797\u4e2d\u5404\u5927\u95e8\u6d3e\u3002\u5927\u6982\u90fd\u6709\u8fd9\u4e2a\u6bdb\u75c5\u3002 \u3000\u3000\u8001\u53eb\u5316\u72ec\u6765\u72ec\u5f80\uff0c\u6ca1\u6709\u4ec0\u4e48\u95e8\u89c4\u675f\u7f1a\uff0c\u7adf\u7136\u4e5f\u4f1a\u72af\u4e86\u8fd9\u79cd\u9519\u8bef\uff0c\u770b\u8d77\u6765\u6c5f\u6e56\u4e0a\u8fd9\u4e9b\u957f\u4e45\u4ee5\u6765\u7684\u4f20\u7edf\u3002\u5df2\u5f62\u6210\u67d0\u79cd\u81ea\u7136\u7684\u7ea6\u675f\uff0c\u5750\u5931\u673a\u5b9c\uff0c\u6388\u654c\u4ee5\u53ef\u4e58\u4e4b\u673a\u2026\u2026\u3002 \u3000\u3000\u5ff5\u8f6c\u81f3\u6b64\uff0c\u51b7\u6c57\u6d43\u80cc\uff0c\u5927\u559d\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6253\u5f00\u7bb1\u5b50\u3002\u201d \u3000\u3000\u559d\u58f0\u5982\u918d\u9190\u704c\u9876\uff0c\u4f7f\u5f97\u5446\u5446\u7ad9\u7acb\u3002\u5fc3\u5b58\u60f6\u60d1\u7684\u80e1\u840d\u3001\u6bb5\u4e5d\uff0c\u90fd\u4e3a\u4e4b\u7cbe\u795e\u4e00\u632f\uff0c\u4e24\u4eba\u8e0f\u524d\u4e00\u6b65\uff0c\u4f38\u624b\u6253\u5f00\u4e86\u56db\u53ea\u63cf\u91d1\u7bb1\u5b50\u7684\u7bb1\u76d6\u3002 \u3000\u3000\u5239\u65f6\u95f4\uff0c\u5b9d\u5149\u8000\u76ee\uff0c\u6ee1\u5ba4\u751f\u8f89\u3002 \u3000\u3000\u6562\u60c5\uff0c\u90a3\u56db\u53ea\u7bb1\u5b50\u4e2d\u88c5\u7684\u90fd\u662f\u9ec4\u91d1\u3001\u73e0\u5b9d\u3002 \u3000\u3000\u81ea\u53e4\u4ee5\u6765\uff0c\u9ec4\u91d1\u73e0\u5b9d\uff0c\u90fd\u662f\u6700\u52a8\u4eba\u5fc3\u4e4b\u7269\uff0c\u5f53\u771f\u662f\u8000\u773c\u751f\u82b1\uff0c\u76ee\u4e3a\u4e4b\u593a\u3002 \u3000\u3000\u9093\u98de\u4e5f\u4e3a\u90a3\u91d1\u5149\u3001\u5b9d\u534e\u5f15\u7684\u5fae\u5fae\u4e00\u6014\u3002 \u3000\u3000\u53ea\u53ef\u60dc\uff0c\u4e0d\u5bb9\u4ed6\u770b\u7684\u4ed4\u7ec6\uff0c\u90a3\u6253\u5f00\u7684\u7bb1\u76d6\u5df2\u5408\u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u201c\u8fd9\u5c31\u662f\u6211\u4eec\u7684\u8bda\u610f\u2026\u2026\u201d\u957f\u886b\u6587\u58eb\u4e5f\u6062\u590d\u4e86\u6e05\u660e\u3002\u51b7\u9759\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u5927\u603b\u7ba1\u662f\u5426\u80fd\u4f5c\u4e2a\u51b3\u5b9a\u5462\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u2026\u2026\u8fd9\u4e2a\u2026\u2026\u201d \u3000\u3000\u9093\u98de\u7684\u4e3b\u52a8\u987f\u5931\uff0c\u4e00\u65f6\u95f4\uff0c\u7adf\u4e0b\u77e5\u5982\u4f55\u56de\u7b54\u3002 \u3000\u3000\u4ed6\u5df2\u4e3a\u90a3\u9ec4\u91d1\u3001\u73e0\u5b9d\u5fc3\u52a8\uff0c\u4f46\u53c8\u4e0d\u80fd\u771f\u7684\u5356\u4e86\u9752\u51e4\u3002 \u3000\u3000\u201c\u5927\u603b\u7ba1\u2026\u2026\u201d\u957f\u886b\u6587\u58eb\u8bf4\uff1a\u201c\u4f60\u6162\u6162\u7684\u8003\u8651\u5427\uff01\u6211\u4eec\u660e\u5929\u518d\u6765\u8ba8\u53d6\u56de\u97f3\uff0c\u8d22\u5df2\u9732\u767d\uff0c\u5b9e\u4e0d\u4fbf\u5728\u6b64\u4e45\u7559\u4e86\u3002\u201d \u3000\u3000\u9093\u98de\u6025\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u4f60\u4eec\u8981\u8d70\u554a\uff01\u201d \u3000\u3000\u201c\u4e0d\u9519\u2026\u2026\u201d\u4e2d\u5e74\u8c6a\u5ba2\u63a5\u53e3\u9053\uff1a\u201c\u6211\u4eec\u53ef\u4ee5\u7ed9\u4f60\u5927\u603b\u7ba1\u4e09\u5929\u7684\u65f6\u95f4\uff0c\u80fd\u5356\u9752\u51e4\uff0c\u7acb\u523b\u5165\u8d27\u4e24\u8bab\uff0c\u4e0d\u80fd\u5356\uff0c\u4e5f\u7b97\u4ea4\u4e2a\u670b\u53cb\uff0c\u544a\u8f9e\u4e86\uff01\u201d\u5f53\u5148\u4e3e\u6b65\u5411\u524d\u884c\u53bb\u3002 \u3000\u3000\u4e24\u4e2a\u4e2d\u5e74\u5065\u4ec6\uff0c\u6311\u8d77\u4e86\u56db\u4e2a\u6728\u7bb1\uff0c\u7d27\u968f\u8eab\u540e\u3002 \u3000\u3000\u6367\u5251\u3001\u62b1\u5200\u7684\u4e24\u4e2a\u9752\u8863\u7ae5\u5b50\uff0c\u62a2\u5728\u4e2d\u5e74\u8c6a\u5ba2\u4e4b\u524d\u5f00\u9053\u3002 \u3000\u3000\u201c\u6162\u4e00\u4e9b\u2026\u2026\u201d\u9093\u98de\u4e00\u6a2a\u8eab\uff0c\u62e6\u5728\u95e8\u66f0\u3002 \u3000\u3000\u201c\u5927\u603b\u7ba1\u2026\u2026\u201d\u957f\u886b\u6587\u58eb\u7b11\u9053\uff1a\u201c\u8be5\u4e0d\u662f\u60f3\u7559\u4e0b\u56db\u7bb1\u9ec4\u91d1\u3001\u73e0\u5b9d\u5427\uff01\u201d \u3000\u3000\u201c\u90a3\u600e\u4e48\u4f1a\u2026\u2026\u201d\u9093\u98de\u9053\uff1a\u201c\u6d1b\u9633\u5c45\u662f\u957f\u5b89\u57ce\u4e2d\u7684\u540d\u5e97\u3002\u201d \u3000\u3000\u201c\u6211\u60f3\u4e5f\u662f\u2026\u2026\u201d\u957f\u886b\u6587\u58eb\u8bf4\uff1a\u201c\u5982\u679c\u56e0\u4e3a\u8fd9\u56db\u7bb1\u9ec4\u91d1\u3001\u73e0\u5b9d\u5f15\u8d77\u4ec0\u4e48\u8bef\u4f1a\uff0c\u53ea\u6015\u5bf9\u8d35\u5e97\u7684\u58f0\u8a89\uff0c\u662f\u4e00\u6b21\u81f4\u547d\u7684\u6253\u51fb\u4e86\uff0c\u4f55\u51b5\uff0c\u6211\u4eec\u4e1c\u4e3b\u8ff7\u604b\u9752\u51e4\u81f3\u6df1\uff0c\u660e\u5929\uff0c\u6211\u5c31\u6765\u542c\u5927\u603b\u7ba1\u7684\u56de\u97f3\u3002\u201d \u3000\u3000\u4fa7\u8eab\u800c\u8fc7\uff0c\u6b65\u51fa\u4e86\u83ca\u82b1\u5385\u3002 \u3000\u3000\u9093\u98de\u4e00\u65f6\u95f4\uff0c\u60f3\u4e0d\u51fa\u5982\u4f55\u624d\u662f\u6700\u5b8c\u7f8e\u7684\u5904\u7f6e\uff0c\u5446\u5446\u671b\u7740\u4e00\u884c\u4eba\u5feb\u6b65\u79bb\u53bb\u3002 \u3000\u3000\u53cc\u65b9\u4ea4\u8c08\u7684\u58f0\u97f3\u5f88\u5927\uff0c\u5c24\u5176\u662f\u90a3\u957f\u886b\u6587\u58eb\u7684\u58f0\u97f3\uff0c\u8fde\u9644\u8fd1\u7684\u5ba2\u4eba\uff0c\u90fd\u542c\u5f97\u6e05\u695a\uff0c\u4e0d\u5c11\u4eba\u8dd1\u5230\u95e8\u5916\u89c2\u671b\uff0c\u56f4\u5b88\u5728\u83ca\u82b1\u5385\u5916\u7684\u9556\u624b\u3001\u4f8d\u5f79\uff0c\u66f4\u662f\u542c\u5f97\u6e05\u695a\uff0c\u5927\u603b\u7ba1\u65e2\u7136\u6ca1\u6709\u4e0b\u4ee4\u963b\u62e6\uff0c\u81ea\u662f\u4e0d\u4fbf\u51fa\u624b\u3002 \u3000\u3000\u4f55\u51b5\uff0c\u522b\u4eba\u5e26\u4e86\u5927\u6279\u9ec4\u91d1\u3001\u73e0\u5b9d\uff0c\u786e\u5b9e\u5b58\u5fc3\u4ea4\u6613\u800c\u6765\uff0c\u867d\u7136\uff0c\u4ea4\u6613\u5bf9\u8c61\u662f\u540d\u6ee1\u957f\u5b89\u7684\u6b4c\u59ec\u9752\u51e4\uff0c\u6709\u4e9b\u532a\u5937\u6240\u601d\uff0c\u4f46\u5374\u662f\u516c\u5e73\u5546\u8c08\u2026\u2026\u3002 \u3000\u3000\u97e9\u9738\u5e26\u7740\u738b\u526f\u603b\u7ba1\u8d76\u5230\u83ca\u82b1\u5385\u65f6\uff0c\u90a3\u4e2d\u5e74\u8c6a\u5ba2\u5df2\u5e26\u7740\u4ece\u4eba\u53bb\u8fdc\uff0c\u79bb\u5f00\u4e86\u6d1b\u9633\u5c4a\u3002 \u3000\u3000\u201c\u8fd9\u4e9b\u4eba\u6765\u8def\u4e0d\u660e\u2026\u2026\u201d\u97e9\u9738\u8bf4\uff1a\u201c\u603b\u7ba1\u653e\u4ed6\u4eec\u79bb\u53bb\uff0c\u5f53\u771f\u662f\u4fbf\u5b9c\u4ed6\u4eec\u4e86\uff1f\u201d \u3000\u3000\u201c\u4e0d\u80fd\u574f\u4e86\u6d1b\u9633\u5c45\u7684\u540d\u58f0\u2026\u2026\u201d\u9093\u98de\u8bf4\uff1a\u201c\u4ed6\u4eec\u5e26\u4e86\u6ee1\u6ee1\u7684\u56db\u7bb1\u9ec4\u91d1\u3001\u73e0\u5b9d\uff0c\u6765\u4e70\u9752\u51e4\u2026\u2026\u201d \u3000\u3000\u201c\u90a3\u5c31\u66f4\u4e0d\u80fd\u653e\u8d70\u4ed6\u4eec\u4e86\u3002\u201d\u738b\u526f\u603b\u7ba1\u9053\uff1a\u201c\u56db\u7bb1\u9ec4\u91d1\u73e0\u5b9d\u3002 \u3000\u3000\u4ef7\u503c\u81f3\u5c11\u767e\u4e07\u4e24\u94f6\u5b50\u4ee5\u4e0a\uff0c\u5982\u80fd\u7559\u4e0b\u5b83\uff0c\u5c82\u4e0d\u662f\u4e00\u4ef6\u5927\u5927\u7684\u529f\u52b3\u3002\u201d \u3000\u3000\u9093\u98de\u8138\u8272\u4e00\u53d8\uff0c\u4f3c\u60f3\u53d1\u4f5c\uff0c\u4f46\u5374\u5f3a\u81ea\u5fcd\u4e86\u4e0b\u53bb\uff0c\u9053\uff1a\u201c\u738b\u526f\u603b\u7ba1\u7684\u610f\u601d\u662f\uff0c\u6211\u4eec\u53ef\u4ee5\u5e73\u81ea\u65e0\u6545\u7684\u628a\u5ba2\u4eba\u7684\u8d22\u7269\u62a2\u8fc7\u6765\u4e86\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4eec\u5927\u95f9\u83ca\u82b1\u5385\uff0c\u5f3a\u4e70\u9752\u51e4\uff0c\u662f\u8bda\u5fc3\u8bda\u610f\u6765\u627e\u9ebb\u70e6\u7684\u2026\u2026\u201d\u738b\u526f\u603b\u7ba1\u8bf4\uff1a\u201c\u7559\u4e0b\u4ed6\u4eec\u7684\u9ec4\u91d1\u3001\u73e0\u5b9d\uff0c\u6d4e\u6211\u4eec\u4e4b\u9700\u8981\uff0c\u4efb\u51ed\u53d6\u8d22\uff0c\u4f55\u6127\u4e4b\u6709\uff1f\u201d \u3000\u3000\u201c\u4eba\u5462\uff1f\u2026\u2026\u201d\u97e9\u9738\u53e3\u6c14\u4e00\u8f6c\uff0c\u9053\uff1a\u201c\u4ed6\u4eec\u7edd\u4e0d\u4f1a\u7518\u5fc3\u7684\u653e\u5f03\u90a3\u7b14\u5e9e\u5927\u7684\u8d22\u5bcc\uff1f\u201d \u3000\u3000\u201c\u6740\uff01\u6b7b\u65e0\u5bf9\u8bc1\uff0c\u90a3\u6279\u8d22\u5bcc\uff0c\u5c31\u6ca1\u4eba\u77e5\u5176\u4e0b\u843d\u4e86\u3002\u201d \u3000\u3000----------- \u3000\u3000 \u7b2c \u4e09 \u56de\u3000\u5192\u795e\u533b\u4e32\u8fde\u594f\u516b\u516c \u3000 \u3000\u3000\u97e9\u9738\u7a81\u7136\u53d1\u89c9\u4e86\u4e00\u4e2a\u79d8\u5bc6\uff0c\u8fd9\u4f4d\u738b\u526f\u603b\u7ba1\u662f\u4e00\u4f4d\u6781\u6709\u6743\u52bf\u7684\u4eba\uff0c\u4f3c\u4e4e\u662f\u6ca1\u6709\u628a\u603b\u7ba1\u770b\u5728\u773c\u4e2d\uff0c\u8fd9\u548c\u6d1b\u9633\u5c45\u7684\u5f8b\u4f8b\u4e0d\u5408\uff0c\u4e0d\u7981\u6697\u53eb\u4e86\u4e24\u58f0\u60ed\u6127\u3002 \u3000\u3000\u603b\u7ba1\u9093\u98de\u7684\u795e\u60c5\u5f88\u5e73\u9759\uff0c\u7f13\u6b65\u884c\u5230\u5385\u95e8\u53e3\u5904\uff0c\u4e3e\u624b\u6325\u52a8\u4e86\u4e24\u4e0b\uff0c\u56f4\u5b88\u5728\u5385\u5916\u7684\u9556\u624b\u3001\u4f8d\u5f79\u3002\u4fcf\u7136\u6563\u53bb\u3002 \u3000\u3000\u83ca\u82b1\u5385\u7684\u56db\u5468\uff0c\u6062\u590d\u4e86\u539f\u6709\u7684\u5e73\u9759\u3002 \u3000\u3000\u97e9\u9738\u9759\u9759\u5730\u7ad9\u7740\uff0c\u9ed8\u67e5\u60c5\u52bf\u53d1\u5c55\uff0c\u6df7\u4eba\u4e86\u6d1b\u9633\u5c45\u4e2d\u5feb\u534a\u5e74\u4e86\uff0c\u4eca\u5929\u624d\u53d1\u89c9\u8fd9\u91cc\u7adf\u6709\u7740\u4e24\u80a1\u5bf9\u7acb\u7684\u52bf\u529b\u5b58\u5728\u3002 \u3000\u3000\u97e9\u9738\u7531\u9556\u624b\u8df3\u4e0a\u526f\u603b\u7ba1\u7684\u804c\u4f4d\uff0c\u53ea\u51ed\u603b\u7ba1\u4e00\u53e5\u8bdd\uff0c\u5c31\u9c7c\u8dc3\u9f99\u95e8\uff0c\u6240\u4ee5\uff0c\u4ed6\u4e00\u76f4\u8ba4\u4e3a\uff0c\u8fd9\u91cc\u7684\u4e00\u5207\u4e8b\u52a1\uff0c\u90fd\u7531\u603b\u7ba1\u51b3\u5b9a\u3002 \u3000\u3000\u73b0\u5728\uff0c\u738b\u526f\u603b\u7ba1\u5374\u7a81\u7136\u63a8\u7ffb\u4e86\u4ed6\u534a\u5e74\u89c2\u5bdf\u7ec4\u5408\u800c\u6210\u7684\u6982\u5ff5\u3002\u8fd9\u4e2a\u4eba\uff0c\u7a76\u7adf\u662f\u4ec0\u4e48\u8eab\u4efd\u5462\uff1f\u97e9\u9738\u5e73\u65f6\u7684\u5fc3\u529b\uff0c\u90fd\u6295\u6ce8\u5728\u603b\u7ba1\u548c\u56db\u5b63\u82b1\u5a62\u53ca\u4e8c\u5341\u56db\u4e2a\u9556\u624b\u7684\u8eab\u4e0a\uff0c\u5bf9\u738b\u526f\u603b\u7ba1\uff0c\u5e76\u4e0d\u592a\u6ce8\u610f\u3002 \u3000\u3000\u5927\u5bb6\u8eab\u4efd\u76f8\u540c\uff0c\u5404\u6709\u4e13\u53f8\uff0c\u5f7c\u6b64\u80fd\u591f\u548c\u7766\u76f8\u5904\uff0c\u4e0d\u8d77\u51b2\u7a81\uff0c\u5df2\u7ecf\u963f\u5f25\u9640\u4f5b\u4e86\uff0c\u4f46\u6b64\u523b\uff0c\u97e9\u9738\u4e0d\u5f97\u4e0d\u4ed4\u7ec6\u7684\u770b\u770b\u8fd9\u4f4d\u738b\u526f\u603b\u7ba1\u4e86\u3002 \u3000\u3000\u738b\u526f\u603b\u7ba1\u7684\u5e74\u7eaa\u4e0d\u5927\uff0c\u4e2a\u5b50\u4e0d\u9ad8\uff0c\u767d\u767d\u51c0\u51c0\u7684\uff0c\u5e94\u8be5\u662f\u5c5e\u4e8e\u548c\u6c14\u751f\u8d22\u578b\u7684\u4eba\u7269\uff0c\u4f46\u6b64\u523b\u5374\u6602\u7136\u800c\u7acb\uff0c\u53cc\u76ee\u4e2d\u795e\u5149\u9690\u73b0\uff0c\u663e\u9732\u4e00\u80a1\u575a\u6bc5\u4e0d\u5c48\u7684\u795e\u60c5\u3002 \u3000\u3000\u559d\uff01\u771f\u7684\u662f\u770b\u8f7b\u4ed6\u4e86\uff0c\u97e9\u9738\u6697\u53eb\u4e86\u4e24\u58f0\u60ed\u6127\uff01 \u3000\u3000\u9093\u98de\u7f13\u7f13\u8f6c\u8fc7\u8eab\u5b50\uff0c\u6162\u6162\u884c\u5230\u5385\u4e2d\uff0c\u5728\u4e00\u5f20\u592a\u5e08\u6905\u4e0a\u5750\u4e0b\u3002\u770b\u5f97\u51fa\u6765\uff0c\u4ed6\u662f\u85c9\u7740\u7f13\u6162\u7684\u52a8\u4f5c\uff0c\u4ee5\u5e73\u606f\u5fc3\u4e2d\u7684\u6012\u706b\u3002 \u3000\u3000\u201c\u6211\u60f3\u2026\u2026\u201d\u9093\u98de\u5c3d\u91cf\u4f7f\u58f0\u97f3\u5e73\u9759\u4ed6\u8bf4\uff1a\u201c\u5728\u6d1b\u9633\u5c45\u4e2d\u548c\u5ba2\u4eba\u51b2\u7a81\uff0c\u6740\u4f24\u4eba\u547d\uff0c\u6709\u80cc\u6211\u4eec\u7ecf\u8425\u7684\u58f0\u8a89\u3002\u662f\u4e0b\u4e0b\u4e4b\u7b56\u2026\u2026\u201d \u3000\u3000\u738b\u526f\u603b\u7ba1\u63a5\u9053\uff1a\u201c\u53ea\u6015\u8fd8\u6ca1\u6709\u8ba4\u51fa\u4ed6\u4eec\u7684\u8eab\u4efd\uff0c\u6478\u6e05\u695a\u4ed6\u4eec\u7684\u5e95\u5b50\u5427\uff1f\u201d \u3000\u3000\u201c\u5f53\u4eca\u6b66\u6797\u4e4b\u4e2d\uff0c\u80fd\u9003\u8fc7\u6211\u8fd9\u4e00\u53cc\u773c\u775b\u7684\u4eba\uff0c\u76f8\u4fe1\u4e0d\u4f1a\u592a\u591a\uff0c\u4e0d\u8fc7\uff0c\u4ed6\u4eec\u786e\u5b9e\u9762\u751f\u5f97\u5f88\u2026\u2026\u201d\u9093\u98de\u9053\uff1a\u201c\u96be\u9053\u738b\u526f\u603b\u7ba1\u5df2\u77e5\u6653\u4e86\u4ed6\u4eec\u7684\u6765\u5386\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4e5f\u5c31\u662f\u6211\u4e3b\u5f20\u7559\u4e0b\u4ed6\u4eec\u7684\u539f\u56e0\u2026\u2026\u201d\u738b\u526f\u603b\u7ba1\u8bf4\uff1a\u201c\u9752\u51e4\u88ab\u523a\u2026\u2026\u201d \u3000\u3000\u201c\u4f46\u51f6\u624b\u5df2\u88ab\u8feb\u6740\uff0c\u957f\u5b89\u57ce\u4e2d\u7684\u51e0\u5341\u4e2a\u7ecf\u8425\u6b4c\u575b\u3001\u5993\u9662\u7684\u5e97\u4e1c\u4e3b\uff0c\u4e3a\u4e86\u751f\u5b58\uff0c\u96c6\u8d44\u96c7\u8bf7\u6740\u624b\uff0c\u5bf9\u4ed8\u9752\u51e4\uff0c\u53ea\u662f\u51fa\u4e8e\u751f\u610f\u4e0a\u7684\u7ade\u4e89\u2026\u2026\u201d\u9093\u98de\u8bf4\uff1a\u201c\u8fd9\u4ef6\uff0c\u4e0d\u662f\u65e9\u5df2\u7ed3\u675f\u4e86\u4e48\uff1f\u96be\u9053\u6211\u4eec\u8981\u5927\u5f00\u6740\u6212\uff0c\u628a\u90a3\u51e0\u5341\u4e2a\u96c6\u8d44\u4e1c\u4e3b\uff0c\u5168\u90fd\u5904\u6b7b\u4e0d\u6210\uff1f\u201d \u3000\u3000\u201c\u4e00\u53f6\u77e5\u79cb\uff0c\u6c5f\u6e56\u4e0a\u8fd8\u6709\u4eba\u6562\u6301\u864e\u987b\uff0c\u800c\u6211\u4eec\u5374\u6478\u4e0d\u6e05\u522b\u4eba\u7684\u5e95\u7ec6\uff0c\u2026\u2026\u201d \u3000\u3000\u201c\u4f4f\u53e3\u2026\u2026\u201d\u9093\u98de\u5fcd\u4e0d\u4f4f\u4e86\uff0c\u8138\u8272\u4e00\u53d8\uff0c\u9053\uff1a\u201c\u523a\u6740\u9752\u51e4\u3002\u53ea\u662f\u6c5f\u7322\u4e2d\u7a81\u7136\u4e8b\u4ef6\uff0c\u548c\u6b66\u6797\u5927\u5c40\u65e0\u5173\uff0c\u90a3\u4e0d\u662f\u6709\u7ec4\u7ec7\u7684\u53cd\u6297\uff0c\u53c2\u4e0e\u7684\u4eba\uff0c\u4e5f\u90fd\u662f\u65e0\u540d\u5c0f\u5352\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e00\u6b21\u5462\uff1f\u80fd\u643a\u5e26\u767e\u4e07\u4ef7\u503c\u7684\u73e0\u5b9d\u3001\u9ec4\u91d1\uff0c\u7edd\u4e0d\u662f\u7a81\u53d1\u4e8b\u4ef6\u4e86\u3002\u201d\u738b\u526f\u603b\u7ba1\u51b7\u51b7\u4ed6\u8bf4\uff1a\u201c\u4ed6\u4eec\u662f\u6709\u5907\u800c\u6765\u2026\u2026\u201d \u3000\u3000\u201c\u53ea\u4e3a\u4e86\u4e70\u53bb\u9752\u51e4\u2026\u2026\u201d\u9093\u98de\u9053\uff1a\u201c\u518d\u8bf4\uff0c\u6211\u77a7\u4e0d\u51fa\u4ed6\u4eec\u7684\u8eab\u4efd\u6765\u5386\uff0c\u5c31\u53ef\u4ee5\u5224\u5b9a\u4ed6\u4eec\u7edd\u4e0d\u662f\u6b66\u6797\u9053\u4e0a\u7684\u6709\u540d\u4eba\u7269\u3002\u201d \u3000\u3000\u201c\u6765\u4eba\u5df2\u53bb\uff0c\u738b\u67d0\u4e5f\u4e0d\u613f\u518d\u548c\u603b\u7ba1\u4e89\u6267\u4e86\uff0c\u5728\u4e0b\u544a\u9000\u3002\u201d \u3000\u3000\u201c\u6162\u7740\u2026\u2026\u201d\u9093\u98de\u51b7\u51b7\u7684\u559d\u9053\u3002 \u3000\u3000\u738b\u526f\u603b\u7ba1\u53cc\u7709\u8038\u626c\uff0c\u505c\u4e0b\u811a\u6b65\uff0c\u9053\uff1a\u201c\u603b\u7ba1\u8fd8\u6709\u4ec0\u4e48\u5429\u5490\uff1f\u201d\u201c\u6211\u60f3\u4eca\u5929\u501f\u6b64\u673a\u4f1a\uff0c\u628a\u4f60\u6211\u4e4b\u95f4\u7684\u8eab\u4efd\u3001\u6743\u4f4d\u3002\u5f04\u4e2a\u6e05\u695a\u2026\u2026\u201d\u9093\u98de\u9053\uff1a\u201c\u514d\u5f97\u4eca\u540e\u518d\u6709\u62d2\u6297\u4ee4\u8c15\uff0c\u64c5\u4f5c\u4e3b\u5f20\u7684\u4e8b\u60c5\u53d1\u751f\u3002\u201d \u3000\u3000\u201c\u603b\u7ba1\u7684\u610f\u601d\u662f\u5927\u5bb6\u62c9\u4e0b\u8138\u6765\u8bf4\u4e2a\u660e\u767d\u4e86\u2026\u2026\u201d\u738b\u526f\u603b\u7ba1\u8bf4\u3002\u201c\u8fd9\u53ea\u6015\u5bf9\u603b\u7ba1\u4e0d\u662f\u4e00\u4ef6\u597d\u4e8b\uff1f\u201d \u3000\u3000\u9093\u98de\u5fae\u4e00\u6446\u5934\uff0c\u97e9\u9738\u7acb\u523b\u884c\u52a8\uff0c\u5173\u4e0a\u4e86\u4e24\u6247\u5385\u95e8\u3002 \u3000\u3000\u5316\u540d\u97e9\u9738\u7684\u6c5f\u67ab\uff0c\u8fc5\u5feb\u5730\u4f5c\u4e86\u51b3\u5b9a\uff0c\u4e0d\u8bba\u738b\u526f\u603b\u7ba1\u7684\u5b9e\u9645\u6743\u52bf\u5982\u4f55\uff1f\u4ed6\u5df2\u51b3\u5b9a\u7ad9\u5728\u9093\u98de\u8fd9\u8fb9\u3002 \u3000\u3000\u5982\u82e5\u4e00\u4e3e\u51fb\u6740\u738b\u526f\u603b\u7ba1\uff0c\u9093\u98de\u548c\u4ed6\u5229\u5bb3\u4e00\u81f4\uff0c\u5fc5\u7136\u4f1a\u5546\u8ba8\u5e94\u5bf9\u4e4b\u7b56\u3002\u81f3\u5c11\uff0c\u53ef\u4ee5\u5bf9\u8fd9\u4e2a\u7ec4\u5408\u6709\u591a\u4e00\u5c42\u7684\u4e86\u89e3\u3002 \u3000\u3000\u738b\u526f\u603b\u7ba1\u795e\u8272\u975e\u5e38\u9547\u9759\uff0c\u4e24\u9053\u76ee\u5149\u968f\u7740\u97e9\u9738\u7684\u8eab\u5b50\u8f6c\u52a8\uff0c\u5634\u89d2\u95ee\u6d6e\u52a8\u7740\u4e00\u62b9\u6de1\u6de1\u7684\u7b11\u610f\uff0c\u5bf9\u9093\u98de\u8fd9\u4e9b\u5904\u63aa\u3002\u4f3c\u4e4e\u4e00\u70b9\u4e5f\u4e0d\u653e\u5728\u5fc3\u4e0a\u3002 \u3000\u3000\u9093\u98de\u4e5f\u5f88\u6c89\u7740\uff0c\u5f53\u7136\uff0c\u97e9\u9738\u4e5f\u4fdd\u6301\u7740\u76f8\u5f53\u7684\u51b7\u9759\u3002 \u3000\u3000\u8fd9\u4e09\u4e2a\u4eba\uff0c\u4f3c\u4e4e\u90fd\u5145\u6ee1\u7740\u81ea\u4fe1\u5fc3\u3002 \u3000\u3000\u201c\u8001\u97e9\u2026\u2026\u201d\u738b\u526f\u603b\u7ba1\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u628a\u4e24\u6247\u95e8\u5173\u8d77\u6765\u3002\u7528\u5fc3\u4f55\u5728\uff1f\u201d \u3000\u3000\u201c\u6211\u662f\u542c\u547d\u884c\u4e8b\u2026\u2026\u201d\u97e9\u9738\u51b7\u51b7\u4ed6\u8bf4\uff1a\u201c\u603b\u7ba1\u5927\u4eba\uff0c\u8981\u5728\u4e0b\u5173\u4e0a\u5385\u95e8\uff0c\u6211\u5c31\u53ea\u597d\u5173\u4e0a\u4e86\u3002\u201d \u3000\u3000\u201c\u8bf4\u7684\u4e5f\u662f\u2026\u2026\u201d\u738b\u526f\u603b\u7ba1\u4e3e\u624b\u4e92\u51fb\u4e09\u638c\uff0c\u7b11\u9053\uff1a\u201c\u603b\u7ba1\u60f3\u5e72\u4ec0\u4e48\uff1f\u73b0\u5728\u53ef\u4ee5\u8bf4\u4e86\u3002\u201d \u3000\u3000\u4e09\u638c\u4e92\u51fb\u7684\u58f0\u97f3\uff0c\u975e\u5e38\u54cd\u4eae\u3002\u4efb\u4f55\u4eba\u60f3\u4e00\u4e0b\u90fd\u4f1a\u660e\u767d\uff0c\u90a3\u662f\u4e00\u79cd\u9884\u5148\u7ea6\u5b9a\u7684\u6697\u53f7\u3002 \u3000\u3000\u8fd9\u8bc1\u660e\u4e86\u6d1b\u9633\u5c45\u786e\u6709\u4e24\u80a1\u529b\u91cf\u5b58\u5728\uff0c\u603b\u7ba1\u9093\u98de\u7ad9\u5728\u660e\u5904\uff0c\u738b\u526f\u603b\u7ba1\u7684\u4e00\u80a1\u529b\u91cf\u9690\u4e8e\u6697\u4e2d\u3002 \u3000\u3000\u4f46\u97e9\u9738\u60f3\u4e0d\u901a\u7684\u662f\uff0c\u738b\u526f\u603b\u7ba1\u8fd9\u80a1\u529b\u91cf\u9690\u85cf\u4e8e\u4f55\u5904\uff1f\u5927\u6982\u9b54\u773c\u9093\u98de\u4e5f\u6709\u540c\u6837\u7684\u56f0\u60d1\uff0c\u6240\u4ee5\uff0c\u867d\u660e\u77e5\u738b\u526f\u603b\u7ba1\u5728\u62db\u8bf7\u4f0f\u5175\uff0c\u5374\u9759\u9759\u5730\u5750\u7740\u4e0d\u52a8\u3002\u5e0c\u671b\u770b\u770b\u51fa\u73b0\u7684\uff0c\u90fd\u662f\u4e9b\u4ec0\u4e48\u4eba\u7269\uff1f \u3000\u3000\u6ca1\u6709\u4eba\u7834\u95e8\u800c\u5165\uff0c\u4f46\u83ca\u82b1\u5385\u5916\u7684\u60c5\u5f62\uff0c\u5374\u56e0\u95e8\u7a97\u5173\u95ed\uff0c\u65e0\u6cd5\u77a7\u5f97\u6e05\u695a\uff01 \u3000\u3000\u201c\u738b\u9f99\uff0c\u4f60\u8bf7\u7684\u4eba\u5230\u4e86\u6ca1\u6709\uff1f\u201d\u9093\u98de\u51b7\u51b7\u4ed6\u8bf4\u9053\uff1a\u201c\u5e0c\u671b\u4ed6\u4eec\u6709\u80fd\u529b\u4fdd\u62a4\u4f60\uff01\u201d \u3000\u3000\u738b\u9f99\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\u201d\u4ed6\u4eec\u6765\u4e86\uff0c\u53ea\u4e0d\u8fc7\uff0c\u8fd8\u6ca1\u6709\u73b0\u8eab\u51fa\u6765\uff0c\u603b\u7ba1\uff0c\u4e8b\u60c5\u8fd8\u6ca1\u6709\u53d1\u5c55\u5230\u4e0d\u53ef\u6536\u62fe\u4e4b\u524d\uff0c\u6211\u5e0c\u671b\u4f60\u591a\u60f3\u60f3\uff0c\u6211\u738b\u9f99\u624d\u662f\u771f\u6b63\u7ba1\u7406\u6d1b\u9633\u5c45\u7684\u4eba\uff0c\u4f60\u53ea\u662f\u5e94\u8058\u7684\u6302\u540d\u603b\u7ba1\u2026\u2026\u201d \u3000\u3000\u201c\u9093\u67d0\u4eba\u4ece\u672a\u505a\u8fc7\u8fd9\u79cd\u7a9d\u56ca\u7684\u4e8b\uff0c\u6211\u53d7\u547d\u51fa\u638c\u6d1b\u9633\u5c45\u65f6\uff0c\u4e5f\u6ca1\u6709\u4eba\u544a\u8bc9\u6211\uff0c\u4f60\u662f\u8fd9\u91cc\u7684\u771f\u6b63\u603b\u7ba1\uff1f\u201d \u3000\u3000\u201c\u4e0d\u89c1\u68fa\u6750\u4e0d\u6389\u6cea\uff0c\u4e0d\u5230\u9ec4\u6cb3\u4e0d\u6b7b\u5fc3\u2026\u2026\u201d\u738b\u9f99\u7f13\u7f13\u7531\u8863\u888b\u4e2d\u6478\u51fa\u4e00\u9762\u7af9\u7b26\uff0c\u9053\uff1a\u201c\u603b\u7ba1\u8bc6\u5f97\u8fd9\u9762\u7af9\u7b26\u5427\uff1f\u201d \u3000\u3000\u9093\u98de\u4ed4\u7ec6\u7684\u770b\u4e86\u4e00\u9635\uff0c\u8138\u8272\u5927\u53d8\u3002 \u3000\u3000\u738b\u9f99\u9053\uff1a\u201c\u201d\u7b26\u4ee4\u6240\u81f3\uff0c\u4e00\u4f53\u9075\u4ece\uff0c\u9093\u98de\uff0c\u8fd8\u4e0d\u8dea\u4e0b\u542c\u547d\u3002\u201d\u9093\u98de\u8eab\u9a71\u98a4\u6296\uff0c\u53cc\u76ee\u51dd\u6ce8\u5728\u7af9\u7b26\u4e0a\uff0c\u4e00\u5f20\u8138\u6da8\u6210\u4e86\u7d2b\u7ea2\u8272\u3002\u5de6\u624b\u4e00\u64a9\u9526\u888d\uff0c\u4f3c\u662f\u5c31\u8981\u8dea\u4e0b\u3002 \u3000\u3000\u97e9\u9738\u5fc3\u4e2d\u4e00\u52a8\uff0c\u5fd6\u9053\uff1a\u201c\u4ed6\u5982\u771f\u7684\u8dea\u4e86\u4e0b\u53bb\uff0c\u90a3\u5c31\u662f\u629b\u53bb\u5c0a\u4e25\uff0c\u5b8c\u5168\u5c48\u670d\u5728\u7af9\u7b26\u7684\u6743\u5a01\u4e4b\u4e0b\uff0c\u53ea\u6015\u5f88\u96be\u518d\u6fc0\u8d77\u4ed6\u53cd\u6297\u7684\u52c7\u6c14\u4e86\u3002\u201d \u3000\u3000\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u6c89\u58f0\u559d\u9053\uff1a\u201c\u603b\u7ba1\uff0c\u8dea\u4e0d\u5f97\uff01\u201d \u3000\u3000\u9093\u98de\u5df2\u66f2\u5f2f\u53cc\u819d\uff0c\u97e9\u9738\u53ca\u65f6\u559d\u6b62\uff0c\u9093\u98de\u53cc\u817f\u4e00\u633a\uff0c\u53c8\u76f4\u76f4\u7684\u7ad9\u597d\uff0c\u9053\uff1a\u201c\u8001\u592b\u662f\u603b\u7ba1\u8eab\u4efd\uff0c\u5c82\u6709\u8eab\u53d7\u526f\u603b\u7ba1\u8981\u631f\u4e4b\u7406\uff1f\u201d \u3000\u3000\u97e9\u9738\u4f4e\u58f0\u9053\uff1a\u201c\u76ee\u524d\u5904\u5883\uff0c\u5982\u7bad\u5728\u5f26\uff0e\u4e0d\u5f97\u4e0d\u53d1\u4e86\u3002\u201d \u3000\u3000\u8eab\u5b50\u4e00\u4fa7\uff0c\u7a81\u7136\u6b3a\u8eab\u800c\u4e0a\uff0c\u53f3\u624b\u76f4\u6263\u738b\u9f99\u53f3\u8155\u8109\u7a74\u3002 \u3000\u3000\u51fa\u624b\u4e00\u51fb\uff0c\u5feb\u5982\u7535\u5149\u77f3\u706b\uff0c\u4e0d\u4f46\u738b\u9f99\u6709\u7740\u62db\u67b6\u4e0d\u53ca\u4e4b\u611f\u3001\u5c31\u662f\u9093\u98de\u4e5f\u770b\u5f97\u5446\u4e86\u4e00\u5446\uff0c\u6697\u9053\uff1a\u201c\u597d\u5feb\u7684\u4e00\u62db\u64d2\u62ff\u624b\u6cd5\u3002\u201d \u3000\u3000\u90a3\u77e5\u5ff5\u5934\u8fd8\u672a\u8f6c\u5b8c\uff0c\u7a81\u95fb\u97e9\u9738\u95f7\u54fc\u4e86\u4e00\u58f0\uff0c\u5411\u540e\u9000\u4e86\u4e09\u6b65\u3002\u738b\u9f99\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8001\u97e9\u54ea\uff01\u521a\u51fa\u624b\u65f6\uff0c\u679c\u7136\u5413\u4eba\u3002\u53ea\u53ef\u60dc\u5440\uff01\u8fd9\u540e\u52b2\u53d8\u5316\uff0c\u5c31\u5927\u5927\u5730\u4e0d\u5bf9\u677f\u4e86\u3002\u201d \u3000\u3000\u539f\u6765\uff0c\u97e9\u9738\u638c\u6307\u5df2\u642d\u4e0a\u738b\u9f99\u53f3\u8155\u65f6\uff0c\u5fc3\u4e2d\u7a81\u7136\u4e00\u52a8\uff0c\u5fd6\u9053\uff1a\u201c\u6211\u5982\u8868\u73b0\u7684\u950b\u8292\u592a\u9732\uff0c\u53ea\u6015\u4f1a\u5f15\u8d77\u9093\u98de\u7684\u6000\u7591\u3002\u201d \u3000\u3000\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u53bb\u52bf\u4e00\u7f13\u3002 \u3000\u3000\u738b\u9f99\u53f3\u624b\u626c\u8d77\u4e00\u6325\u3002\u5207\u5411\u97e9\u9738\u53f3\u8155\uff0c\u638c\u7f18\u89e6\u53ca\u4e86\u97e9\u9738\u624b\u80cc\u3002 \u3000\u3000\u97e9\u9738\u5931\u58f0\u800c\u9000\u3002 \u3000\u3000\u201c\u4f24\u5f97\u91cd\u5417\uff1f\u2026\u2026\u201d\u9093\u98de\u6d41\u9732\u51fa\u65e0\u9650\u5173\u5207\u4e4b\u60c5\uff0c\u671b\u7740\u97e9\u9738\u3002 \u3000\u3000\u201c\u5c5e\u4e0b\u8fd8\u597d\u3002\u603b\u7ba1\u4e0d\u7528\u62c5\u5fc3\u2026\u2026\u201d \u3000\u3000\u201c\u90a3\u5c31\u597d\uff0c\u770b\u8001\u592b\u66ff\u4f60\u51fa\u6c14\u2026\u2026\u201d\u9093\u98de\u8eab\u5b50\u4e00\u4fa7\uff0c\u75be\u5982\u98d8\u98ce\u822c\uff0c\u6b3a\u8eab\u800c\u4e0a\uff0c\u53f3\u638c\u626c\u8d77\uff0c\u8fd8\u672a\u5288\u4e0b\uff0c\u738b\u9f99\u53c8\u4e3e\u8d77\u4e86\u7af9\u7b26\uff0c\u9053\uff1a\u201c\u9093\u603b\u7ba1\uff0c\u4f60\u8be5\u77e5\u9053\uff0c\u6297\u62d2\u8fd9\u7af9\u7b26\u4ee4\u7684\u540e\u679c\uff0c\u738b\u67d0\u4eba\u518d\u7ed9\u4f60\u4e00\u6b21\u673a\u4f1a\u3002\u8dea\u4e0b\u542c\u547d\u3002\u201d \u3000\u3000\u9093\u98de\u626c\u8d77\u7684\u53f3\u624b\uff0c\u53c8\u7ea7\u7f13\u653e\u4e0b\uff0c\u53cc\u76ee\u51dd\u6ce8\u5728\u90a3\u7af9\u7b26\u4e0a\u2026\u2026\u3002 \u3000\u3000\u97e9\u9738\u5927\u5403\u4e00\u60ca\uff0c\u6697\u9053\uff1a\u201c\u8fd9\u7af9\u7b26\u4ee3\u8868\u4e86\u4ec0\u4e48\uff1f\u600e\u4e48\u6709\u5982\u6b64\u7684\u6743\u5a01\u3002\u9093\u98de\u5728\u62d2\u6297\u8fc7\u4e00\u6b21\u4e4b\u540e\uff0c\u4ecd\u7136\u65e0\u6cd5\u5177\u6709\u6297\u62d2\u7b2c\u4e8c\u6b21\u7684\u529b\u91cf\u3002\u201d \u3000\u3000\u7acb\u523b\u9ad8\u58f0\u8bf4\u9053\uff1a\u201c\u90a3\u53ea\u662f\u4e00\u5757\u7af9\u7247\u7f62\u4e86\uff0c\u6211\u6ca1\u6709\u770b\u5230\u4ec0\u4e48\u7b26\u4ee4\uff1f\u201d\u201c\u5bf9\uff01\u6211\u4e5f\u6ca1\u6709\u770b\u5230\u2026\u2026\u201d\u9093\u98de\u9053\uff1a\u201c\u7b26\u4ee4\u5728\u54ea\u91cc\uff1f\u5728\u54ea\u91cc\uff1f\u201d\u97e9\u9738\u7a81\u7136\u6b3a\u8fdb\u4e09\u6b65\uff0c\u6363\u51fa\u4e00\u62f3\uff0c\u8fd9\u5c31\u903c\u5f97\u738b\u9f99\u4e0d\u5f97\u4e0d\u51fa\u624b\u8fd8\u51fb\u3002 \u3000\u3000\u97e9\u9738\u51b7\u7b11\u4e00\u58f0\uff0c\u53cc\u624b\u8fde\u73af\u51fb\u51fa\uff0c\u90fd\u653b\u5411\u7389\u9f99\u53f3\u8155\uff0c\u5f88\u660e\u663e\uff0c\u662f\u60f3\u62a2\u738b\u9f99\u624b\u4e2d\u7684\u7af9\u7b26\u3002 \u3000\u3000\u9093\u98de\u4e5f\u5288\u51fa\u4e00\u638c\uff0c\u51fb\u5411\u738b\u9f99\u540e\u80cc\u3002 \u3000\u3000\u638c\u98ce\u547c\u5578\u3002\u5e26\u8d77\u4e86\u4e00\u80a1\u5f3a\u5927\u7684\u6f5c\u529b\u3002 \u3000\u3000\u738b\u9f99\u51b7\u54fc\u4e00\u58f0\uff0c\u8eab\u8eaf\u534a\u8f6c\uff0c\u7adf\u7528\u5de6\u624b\u8fce\u5411\u4e86\u9093\u98de\u7684\u53f3\u638c\u3002 \u3000\u3000\u4f46\u95fb\u84ec\u7136\u4e00\u58f0\uff0c\u53cc\u638c\u63a5\u5b9e\uff0c\u7389\u9f99\u8eab\u8eaf\u4e00\u6643\uff0c\u8db3\u4e0b\u7ea2\u7816\u788e\u4e86\u4e24\u5757\uff0c\u4f46\u9093\u98de\u5374\u4e00\u8fde\u540e\u9000\u4e86\u56db\u6b65\uff0c\u624d\u62ff\u6869\u7ad9\u7a33\u3002 \u3000\u3000\u9093\u98de\u5df2\u5e74\u8fd1\u82b1\u7532\uff0c\u738b\u9f99\u4e0d\u8fc7\u4e8c\u5341\u516d\u4e03\uff0c\u5982\u8bba\u5185\u529f\u4fee\u4e3a\uff0c\u9093\u98de\u8981\u5f3a\u8fc7\u738b\u9f99\u751a\u591a\u624d\u5bf9\u3002 \u3000\u3000\u4f46\u8fd9\u4e00\u638c\u786c\u62fc\uff0c\u738b\u9f99\u662f\u663e\u7136\u7684\u5360\u4e86\u4e0a\u98ce\u3002 \u3000\u3000\u97e9\u9738\u5fc3\u4e2d\u4ed8\u9053\uff1a\u201c\u4e0d\u80fd\u8ba9\u9093\u98de\u5931\u53bb\u4e86\u4fe1\u5fc3\u3002\u201d\u95ea\u7535\u6b3a\u4e0a\uff0c\u62cd\u51fa\u4e00\u638c\u3002 \u3000\u3000\u738b\u9f99\u51b7\u7b11\u4e00\u58f0\u9053\uff1a\u201c\u8001\u97e9\uff0c\u6211\u5df2\u7ecf\u5bf9\u4f60\u624b\u4e0b\u7559\u60c5\uff0c\u4f60\u5374\u81ea\u8981\u5bfb\u6b7b\u2026\u2026\u201d\u63a8\u51fa\u7684\u638c\u52bf\uff0c\u5df2\u8fce\u4e0a\u4e86\u97e9\u9738\u7684\u53f3\u638c\uff0c\u5fc3\u5934\u5927\u5927\u4e00\u9707\uff0c\u6d3b\u4e5f\u63a5\u4e0b\u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u9093\u98de\u968f\u7740\u5411\u524d\u5954\u51b2\u7684\u8eab\u5b50\uff0c\u4e00\u62f3\u51fb\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u738b\u9f99\u773c\u770b\u770b\u5de8\u62f3\u8fd1\u8eab\uff0c\u5c31\u662f\u65e0\u6cd5\u95ea\u907f\u3002 \u3000\u3000\u539f\u6765\u3002\u4ed6\u548c\u97e9\u9738\u89e6\u63a5\u7684\u53f3\u638c\uff0c\u4f3c\u662f\u88ab\u4e00\u80a1\u5f3a\u5927\u7684\u529b\u91cf\u5438\u5f15\u7740\uff0c\u4e0d\u4f46\u53f3\u638c\u65e0\u6cd5\u62bd\u52a8\uff0c\u8fde\u5168\u8eab\u4f3c\u4e4e\u90fd\u65e0\u6cd5\u79fb\u52a8\u4e86\u3002 \u3000\u3000\u773c\u770b\u7740\u90a3\u4e00\u62f3\u51fb\u5411\u5de6\u989d\uff0c\u7adf\u7136\u62bd\u4e0d\u7740\u624b\u53bb\u62b5\u62d2\uff0c\u800c\u4e14\u8fde\u95ea\u907f\u4e5f\u65e0\u6cd5\u505a\u5230\u3002\u4f3c\u662f\u5168\u8eab\u90fd\u88ab\u4e00\u79cd\u5f3a\u5927\u3001\u5947\u5f02\u7684\u529b\u9053\u5b9a\u4f4f\u3002 \u3000\u3000\u738b\u9f99\u773c\u770b\u7740\u90a3\u94c1\u9524\u4e00\u822c\u7684\u5927\u62f3\u51fb\u4e2d\u4e86\u5de6\u989d\uff0c\u611f\u89c9\u4e2d\u4e5f\u542c\u5230\u4e86\u5934\u9aa8\u788e\u88c2\u7684\u58f0\u97f3\u3002 \u3000\u3000\u97e9\u9738\u8fc5\u5feb\u7684\u6536\u56de\u53f3\u638c\uff0c\u738b\u9f99\u5c38\u4f53\u683d\u5012\u3002 \u3000\u3000\u9093\u98de\u51dd\u76ee\u671b\u53bb\uff0c\u53ea\u89c1\u738b\u9f99\u7684\u5de6\u989d\u6574\u4e2a\u7684\u6df1\u9677\u4e0b\u53bb\uff0c\u90a3\u662f\u975e\u6b7b\u4e0d\u53ef\u7684\u91cd\u4f24\u3002 \u3000\u3000\u97e9\u9738\u9053\uff1a\u201c\u603b\u7ba1\u597d\u91cd\u7684\u4e00\u62f3\uff0c\u738b\u526f\u603b\u7ba1\u53ea\u6015\u662f\u6d3b\u4e0d\u6210\u4e86\u3002\u201d\u201c\u738b\u9f99\u6b7b\u4e0d\u8db3\u60dc\uff01\u53ea\u662f\u8fd9\u4e00\u62f3\uff0c\u4ed6\u600e\u4f1a\u95ea\u907f\u4e0d\u5f00\u5462\uff1f\u201d \u3000\u3000\u9093\u98de\u63d0\u51fa\u4e86\u5fc3\u4e2d\u7684\u7591\u95ee\uff1f\u201c\u6211\u60f3\u2026\u2026\u201d\u97e9\u9738\u4f4e\u58f0\u9053\uff1a\u201c\u4ed6\u6b63\u5728\u548c\u5c5e\u4e0b\u6bd4\u62fc\u5185\u529b\uff0c\u90a3\u91cc\u8fd8\u6709\u80fd\u529b\u907f\u5f00\u8fd9\u4e00\u62f3\u5462\uff1f\u201d \u3000\u3000\u9093\u98de\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u8bf4\u7684\u4e5f\u662f\u2026\u2026\u201d\u76ee\u5149\u51dd\u6ce8\u5728\u97e9\u9738\u7684\u8138\u4e0a\u63a5\u9053\uff1a\u201c\u97e9\u526f\u603b\u7ba1\u89c9\u7740\u8fd9\u738b\u9f99\u7684\u6b66\u529f\u5982\u4f55\uff1f\u201d \u3000\u3000\u201c\u5f88\u9ad8\u660e\uff0c\u603b\u7ba1\u4e5f\u8bb8\u53ef\u4ee5\u548c\u4ed6\u6253\u4e2a\u5e73\u5206\u79cb\u8272\uff1f\u5982\u4e00\u5b9a\u8981\u5206\u51fa\u80dc\u8d1f\uff0c\u6050\u6015\u8981\u4e94\u767e\u62db\u4ee5\u4e0a\u4e86\uff0c\u4f46\u5c5e\u4e0b\u7edd\u975e\u5176\u654c\uff0c\u9002\u624d\u6bd4\u62fc\u5185\u529b\uff0c\u97e9\u67d0\u5df2\u611f\u89c9\u4e0d\u652f\uff0c\u5982\u975e\u603b\u7ba1\u53ca\u65f6\u4e00\u62f3\uff0c\u51fb\u6bd9\u4e86\u4ed6\uff0c\u53ea\u6015\u73b0\u5728\u6211\u5df2\u4f24\u5728\u4ed6\u7684\u624b\u4e0b\u4e86\uff1f\u201d \u3000\u3000\u9b54\u773c\u9093\u98de\u957f\u957f\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u738b\u9f99\u4e00\u76f4\u6536\u655b\u7740\u81ea\u5df1\uff0c\u4eca\u5929\u600e\u4f1a\u7a81\u7136\u95f4\u4ee5\u7af9\u7b26\u4ee4\u8981\u631f\u672c\u5ea7\uff0c\u96be\u9053\u2026\u2026\u96be\u9053\u4ed6\u4eec\u5df2\u5f00\u59cb\u6392\u9664\u5f02\u5df2\u4e86\u2026\u2026\u201d \u3000\u3000\u97e9\u9738\u5fc3\u4e2d\u4e00\u52a8\uff0c\u4ed8\u9053\uff1a\u6392\u9664\u5f02\u5df1\uff0c\u8fd9\u4e48\u8bf4\u6765\uff0c\u8fd9\u4e2a\u795e\u79d8\u7ec4\u5408\u4e4b\u4e2d\uff0c\u8fd8\u6709\u4e0d\u5c11\u5b58\u6d3b\u4e8e\u4e16\u7684\u6b66\u6797\u9ad8\u624b\u4e86\u2026\u2026\u3002 \u3000\u3000\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u4eba\u5374\u4f38\u624b\u53d6\u8fc7\u4ecd\u7136\u7d27\u63e1\u5728\u738b\u9f99\u624b\u4e2d\u7684\u7af9\u7b26\u4ee4\uff0c\u9053\uff1a\u201c\u54b1\u4eec\u90fd\u6ca1\u6709\u770b\u5230\u4ec0\u4e48\u7af9\u7b26\u4ee4\uff0c\u5c5e\u4e0b\u4e5f\u6ca1\u6709\u542c\u8fc7\u8fd9\u4e2a\u540d\u5b57\u3002\u201d \u3000\u3000\u53cc\u624b\u4e00\u5408\uff0c\u63c9\u6413\u4e86\u4e00\u4e0b\uff0c\u6574\u4e2a\u7684\u7af9\u7b26\uff0c\u5316\u4f5c\u788e\u7c89\uff0c\u6d12\u843d\u5730\u4e0a\u3002 \u3000\u3000\u9093\u98de\u4e00\u6014\u4e4b\u540e\uff0c\u54c8\u54c8\u5927\u7b11\uff0c\u9053\uff1a\u201c\u597d\uff01\u4eba\u8bc1\u6b7b\u4e86\uff0c\u7269\u8bc1\u6d88\u5931\u3002 \u3000\u3000\u81f3\u5c11\uff0c\u6211\u4eec\u53ef\u4ee5\u8bf4\u5f97\u5341\u5206\u660e\u767d\u3002\u201d \u3000\u3000\u97e9\u9738\u4f4e\u58f0\u9053\uff1a\u201c\u4f46\u738b\u9f99\u53ec\u96c6\u7684\u4eba\u624b\uff0c\u8fd8\u672a\u73b0\u8eab\u3002\u4ed6\u4eec\u53ef\u80fd\u5c31\u9690\u85cf\u5728\u8fd9\u5385\u5916\u56db\u5468\uff1f\u201d \u3000\u3000\u201c\u5c31\u7b97\u662f\u5427\uff01\u6211\u4eec\u4e5f\u6ca1\u6709\u529e\u6cd5\uff0c\u628a\u4ed6\u4eec\u53ec\u4eba\u8fd9\u83ca\u82b1\u5385\u4e2d\u2026\u2026\u201d \u3000\u3000\u97e9\u9738\u5fae\u5fae\u4e00\u6014\uff0c\u5fd6\u9053\uff1a\u770b\u6765\uff0c\u4ed6\u8fd9\u4e2a\u603b\u7ba1\uff0c\u679c\u7136\u662f\u5f92\u5177\u865a\u540d\uff0c\u8fde\u738b\u9f99\u5982\u4f55\u62db\u96c6\u4eba\u624b\uff0c\u4ed6\u4e5f\u5f04\u4e0d\u6e05\u695a\uff1f\u201c\u603b\u7ba1\uff0c\u8bf7\u6055\u97e9\u67d0\u591a\u8651\uff0c\u65a9\u8349\u4e0d\u9664\u6839\uff0c\u6625\u98ce\u5439\u53c8\u751f\u3002\u603b\u7ba1\u5982\u82e5\u77e5\u6653\u90a3\u4e9b\u4eba\u5728\u6d1b\u9633\u5c45\u7684\u8eab\u4efd\uff0c\u97e9\u67d0\u5c31\u53ef\u4ee5\u60f3\u529e\u6cd5\u9664\u53bb\u4ed6\u4eec\uff1f\u201d \u3000\u3000\u201c\u6211\u4e5f\u89c9\u7740\u5947\u602a\uff0c\u6d1b\u9633\u5c45\u4e2d\u7684\u56db\u5b63\u82b1\u5a62\u3002\u4e8c\u5341\u56db\u4f4d\u9556\u624b\u3002\u4eba\u5341\u56db\u4e2a\u6367\u8336\u4e0a\u9152\u7684\u4f19\u8ba1\uff0c\u90fd\u6709\u4e00\u8eab\u4e0d\u9519\u7684\u6b66\u529f\u2026\u2026\u201d\u9093\u98de\u6c89\u541f\u4e86\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u8fd9\u4e9b\u4eba\u5e94\u8be5\u4e0d\u4f1a\u662f\u4ed6\u7684\u5e2e\u624b\u624d\u5bf9\uff0c\u4f46\u9664\u6b64\u4e4b\u5916\uff0c\u54ea\u91cc\u8fd8\u6709\u4eba\u5462\uff1f\u201d \u3000\u3000\u97e9\u9738\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u8fd9\u65b9\u9762\u6211\u7684\u4f30\u7b97\uff0c\u5012\u662f\u5dee\u8ddd\u4e0d\u5927\u3002\u4f46\u4e0d\u77e5\u4e8c\u5341\u56db\u4f4d\u9556\u624b\u7684\u6b66\u529f\uff0c\u6bd4\u8d77\u90a3\u79cb\u82b1\u53cc\u5a62\u5982\u4f55\uff1f\u201d \u3000\u3000\u4ed6\u89c1\u8bc6\u5230\u79cb\u82b1\u53cc\u5a62\u7684\u6b66\u529f\uff0c\u5e94\u5217\u5165\u6b66\u6797\u4e2d\u4e00\u6d41\u9ad8\u624b\uff0c\u5982\u8bba\u6280\u827a\u7684\u535a\u6742\uff0c\u5c24\u4e3a\u96be\u5f97\u2026\u2026\u3002 \u3000\u3000\u4f46\u95fb\u9093\u98de\u8bf4\u9053\uff1a\u201c\u96be\u9053\u8fd9\u6d1b\u9633\u5c45\u5916\uff0c\u8fd8\u5e02\u4e0b\u4e86\u4e00\u652f\u4f0f\u5175\u4e0d\u6210\uff1f\u201d \u3000\u3000\u201c\u4e0d\u53ef\u80fd\u5427\u2026\u2026\u201d\u97e9\u9738\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u5c5e\u4e0b\u9041\u51fa\u5385\u5916\u53bb\u67e5\u770b\u4e00\u4e0b\uff0c\u4f46\u738b\u9f99\u7684\u5c38\u4f53\u2026\u2026\u201d \u3000\u3000\u201c\u8fd9\u4e2a\uff0c\u7531\u6211\u5904\u7406\uff0c\u9047\u4e0a\u4ed6\u4eec\u65f6\uff0c\u6700\u597d\u4e0d\u8981\u52a8\u624b\uff0c\u5148\u56de\u6765\u544a\u8bc9\u6211\uff0c\u518d\u51b3\u5b9a\u5982\u4f55\u5e94\u4ed8\uff1f\u201d \u3000\u3000\u201c\u5982\u82e5\u97e9\u67d0\u9047\u4e0a\u7684\uff0c\u90fd\u662f\u6d1b\u9633\u5c45\u4e2d\u7684\u4eba\u5462\uff1f\u201d \u3000\u3000\u201c\u90a3\u4f60\u5c31\u66f4\u8981\u5c0f\u5fc3\u89c2\u5bdf\u4e86\uff0c\u4ed6\u4eec\u5bf9\u4f60\u662f\u5426\u8fd8\u50cf\u8fc7\u53bb\u7684\u5c0a\u91cd\u3002 \u3000\u3000\u662f\u5426\u6709\u654c\u89c6\u4e4b\u5fc3\uff1f\u201d \u3000\u3000\u201c\u662f\uff01\u97e9\u67d0\u53bb\u53bb\u5c31\u6765\uff01\u201d\u95ea\u8eab\u7834\u7a97\u800c\u51fa\u3002 \u3000\u3000\u9093\u98de\u671b\u770b\u97e9\u9738\u7684\u80cc\u5f71\uff0c\u6c89\u541f\u4e0d\u8bed\u3002 \u3000\u3000\u4ed6\u53f7\u79f0\u9b54\u773c\uff0c\u6709\u8fc7\u76ee\u4e0d\u5fd8\u4e4b\u80fd\uff0c\u4e00\u773c\u4e4b\u4e0b\uff0c\u80fd\u628a\u4e00\u4e2a\u4eba\u7684\u9762\u5f62\u3001\u7279\u70b9\uff0c\u770b\u5f97\u6e05\u6e05\u695a\u695a\u3002 \u3000\u3000\u6240\u4ee5\uff0c\u4e00\u822c\u7684\u6613\u5bb9\u624b\u6cd5\uff0c\u4eba\u76ae\u9762\u5177\uff0c\u90fd\u5f88\u96be\u9003\u5f97\u8fc7\u4ed6\u9510\u5229\u7684\u76ee\u5149\u3002 \u3000\u3000\u97e9\u9738\u867d\u7136\u6781\u529b\u5728\u63a9\u9970\u81ea\u5df1\u7684\u6b66\u529f\uff0c\u4f46\u4ecd\u7136\u65e0\u6cd5\u9003\u8fc7\u9093\u98de\u9b54\u773c\u7684\u89c2\u5bdf\u3002 \u3000\u3000\u4ed6\u53d1\u89c9\u4e86\u97e9\u9738\u7684\u6b66\u529f\uff0c\u6bd4\u4ed6\u9884\u4f5c\u7684\u4f30\u7b97\uff0c\u9ad8\u51fa\u5f88\u591a\uff0c\u4f46\u6b64\u65f6\u6b64\u523b\u5374\u53c8\u662f\u4e2a\u66f4\u597d\u7684\u5e2e\u624b\u2026\u2026\u97e9\u9738\u56de\u5165\u5385\u4e2d\u65f6\uff0c\u738b\u9f99\u7684\u5c38\u4f53\u5df2\u5316\u4f5c\u4e86\u4e00\u6ee9\u9ec4\u6c34\uff0c\u6d88\u5931\u4e0d\u89c1\u3002 \u3000\u3000\u201c\u83ca\u82b1\u5385\u5916\u6ca1\u6709\u57cb\u4f0f\u7684\u4eba\u624b\uff0c\u4e5f\u6ca1\u6709\u56f4\u4e0a\u6765\u7684\u964c\u751f\u4eba\u2026\u2026\u201d \u3000\u3000\u97e9\u9738\u8bf4\uff1a\u201c\u738b\u9f99\u53ea\u662f\u5728\u865a\u5f20\u58f0\u52bf\u3002\u201d \u3000\u3000\u9093\u98de\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u4e0b\u4f1a\u7684\uff0c\u4e00\u5b9a\u6709\u4e00\u6279\u4eba\u624b\uff0c\u85cf\u5728\u6d1b\u9633\u5c45\u4e2d\u3002\u201d \u3000\u3000\u201c\u96be\u9053\u4f1a\u662f\u996d\u5e84\u3001\u8d4c\u573a\u4e2d\u7684\u5ba2\u4eba\u2026\u2026\u201d \u3000\u3000\u9093\u98de\u63a5\u9053\uff1a\u201c\u5f53\u7136\u4e0d\u4f1a\uff0c\u4e0d\u8fc7\uff0c\u738b\u9f99\u4e5f\u4e0d\u4f1a\u865a\u8a00\u6050\u5413\uff0c\u4e00\u5b9a\u6709\u4e00\u6279\u53ea\u6709\u4ed6\u624d\u80fd\u6307\u6325\u7684\u4eba\u624b\uff0c\u57cb\u4f0f\u5728\u5de6\u8fd1\u3002\u201d \u3000\u3000\u97e9\u9738\u5fc3\u4e2d\u4e00\u52a8\uff0c\u4ed8\u9053\uff1a\u96be\u9053\u738b\u9f99\u53e6\u6709\u4e00\u5957\u65b9\u6cd5\uff0c\u80fd\u4f7f\u8fd9\u6d1b\u9633\u5c45\u7684\u4eba\u624b\uff0c\u5728\u7a81\u7136\u4e4b\u95f4\uff0c\u80fd\u542c\u4ed6\u4e00\u4e2a\u4eba\u7684\u547d\u4ee4\uff0c\u8fd9\u771f\u662f\u4e00\u4ef6\u975e\u5e38\u53ef\u6015\u7684\u5b89\u6392\u4e86\u2026\u2026\u3002\u4e00\u5ff5\u53ca\u6b64\uff0c\u987f\u89c9\u80cc\u810a\u4e0a\u5192\u8d77\u4e00\u80a1\u5bd2\u610f\u3002 \u3000\u3000\u4f46\u4ed6\u6ca1\u6709\u8bf4\u51fa\u6765\u81ea\u5df1\u7684\u60f3\u6cd5\uff0c\u4ed6\u8981\u9690\u85cf\u6b66\u529f\uff0c\u4e5f\u8981\u9690\u85cf\u667a\u6167\u3002 \u3000\u3000\u9b54\u773c\u867d\u7136\u80fd\u6d1e\u5bdf\u7ec6\u5fae\uff0c\u4f46\u97e9\u9738\u7cbe\u5de7\u7684\u4eba\u76ae\u9762\u5177\uff0c\u63a9\u53bb\u4e86\u8138\u4e0a\u7684\u795e\u9c7c\u53d8\u5316\u3002 \u3000\u3000\u201c\u97e9\u9738\uff0c\u770b\u6765\u4f60\u5bf9\u8001\u592b\u771f\u7684\u662f\u5341\u5206\u5fe0\u8bda\u4e86\u2026\u2026\u201d\u9093\u98de\u5fae\u7b11\u9053\uff1a\u201c\u4f60\u96be\u9053\u548c\u4ed6\u4eec\u51fa\u8eab\u4e0d\u540c\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u6562\u6b3a\u7792\u603b\u7ba1\uff0c\u5728\u4e0b\u662f\u6df7\u8fdb\u6765\u7684\u3002\u201d \u3000\u3000\u8fd9\u662f\u9707\u64bc\u7684\u56de\u7b54\u3002\u9093\u98de\u4e5f\u542c\u5f97\u4e00\u6014\uff0c\u6c89\u541f\u4e86\u826f\u4e45\uff0c\u624d\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u600e\u4e48\u6df7\u8fdb\u6765\u7684\uff1f\u201d \u3000\u3000\u201c\u97e9\u67d0\u88ab\u5e08\u95e8\u957f\u8f88\u8feb\u5bb3\uff0c\u4e0d\u5f97\u4e0d\u5b64\u8eab\u51fa\u8d70\uff0c\u4e3a\u6682\u5bc4\u840d\u8e2a\uff0c\u6df7\u5165\u4e86\u9556\u624b\u7fa4\u4e2d\u3002\u201d \u3000\u3000\u9093\u98de\u51b7\u51b7\u63a5\u9053\uff1a\u201c\u6211\u4eec\u7ec4\u7ec7\u4e25\u5bc6\uff0c\u5c82\u662f\u968f\u4fbf\u6df7\u8fdb\u6765\u7684\u3002\u201d \u3000\u3000\u201c\u771f\u7684\u97e9\u9738\u5df2\u88ab\u6211\u6740\u6389\uff01\u201d \u3000\u3000\u9093\u98de\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u4f60\u4e5f\u7b97\u5fc3\u72e0\u624b\u8fa3\u4e86\uff1f\u201d \u3000\u3000\u201c\u65e0\u6bd2\u4e0d\u4e08\u592b\u3002\u50cf\u4eca\u65e5\u4e4b\u4e8b\uff0c\u5982\u4e0d\u662f\u603b\u7ba1\u65ad\u7136\u5904\u63aa\uff0c\u53ea\u6015\u73b0\u5728\u6211\u7b49\u5df2\u662f\u9636\u4e0b\u4e4b\u56da\u4e86\uff1f\u201d \u3000\u3000\u9093\u98de\u4e24\u9053\u76ee\u5149\u76ef\u5728\u97e9\u9738\u8138\u4e0a\uff0c\u9053\uff1a\u201c\u8fd9\u662f\u97e9\u9738\u5f62\u8c8c\uff0c\u8fd8\u662f\u4f60\u7684\u9762\u76ee\uff1f\u201d \u3000\u3000\u201c\u662f\u5728\u4e0b\u7684\u9762\u76ee\u3002\u201d \u3000\u3000\u9093\u98de\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u4f60\u7684\u59d3\u540d\u5462\uff1f\u201d \u3000\u3000\u201c\u672c\u540d\u6c5f\u67ab\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u60f3\u6765\u90a3\u97e9\u9738\u4e5f\u662f\u4ed6\u4eec\u6536\u7528\u7684\u6c5f\u6e56\u4eba\u7269\u4e86\uff1f\u201d \u3000\u3000\u201c\u662f\uff01\u6c5f\u67d0\u4eba\u95ee\u7684\u5f88\u6e05\u695a\uff0c\u97e9\u9738\u4e0d\u662f\u4ed6\u4eec\u8bad\u7ec3\u7684\u5b50\u5f1f\u3002\u201d \u3000\u3000\u9093\u98de\u7a81\u7136\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\uff0c\u54b1\u4eec\u95ef\u4e0b\u4e86\u6740\u8eab\u7684\u5927\u7978\u5417\uff1f\u201d \u3000\u3000\u201c\u603b\u7ba1\u5982\u662f\u6307\u738b\u526f\u603b\u7ba1\u7684\u4e8b\uff0c\u54b1\u4eec\u7ed9\u4ed6\u4e2a\u6b7b\u4e0d\u8ba4\u5e10\uff0c\u6211\u4eec\u5ec9\u6d01\u4e00\u81f4\uff0c\u4e5f\u8bb8\u53ef\u4ee5\u5e94\u4ed8\u8fc7\u53bb\uff1f\u201d \u3000\u3000\u201c\u770b\u6765\u4f60\u5bf9\u6d1b\u9633\u5c45\u7684\u4e86\u89e3\uff0c\u5b9e\u5728\u662f\u6709\u9650\u5f97\u5f88\u2026\u2026\u201d\u9093\u98de\u9053\uff1a\u201c\u8fd9\u91cc\u7684\u7ec4\u7ec7\u7cbe\u7ec6\u4e25\u5bc6\uff0c\u4efb\u4f55\u4e00\u5904\u53d7\u5230\u7834\u574f\uff0c\u4ed6\u4eec\u5f88\u5feb\u5c31\u67e5\u89c9\u5f97\u5230\uff1f\u201d \u3000\u3000\u201c\u6240\u4ee5\u3001\u8fd9\u65b9\u9762\u8981\u603b\u7ba1\u591a\u591a\u6307\u70b9\uff0c\u5c5e\u4e0b\u624d\u80fd\u914d\u5408\u5f97\u597d\uff1f\u201d \u3000\u3000\u9093\u98de\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u5e78\u597d\u8001\u592b\u4e86\u89e3\u7684\u591f\u591a\uff0c\u4f46\u4e5f\u4e0d\u80fd\u4e0d\u4f5c\u6700\u574f\u7684\u6253\u7b97\uff1f\u201d \u3000\u3000\u201c\u6c5f\u67ab\u4e00\u5207\u542c\u4ece\u603b\u7ba1\u5429\u5490\uff1f\u201d \u3000\u3000\u9093\u98de\u9053\uff1a\u201c\u597d\uff01\u7b2c\u4e00\uff0c\u8fd8\u7528\u4f60\u97e9\u9738\u7684\u540d\u5b57\uff0c\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u662f\uff01\u201d \u3000\u3000\u201c\u7b2c\u4e8c\uff0c\u597d\u597d\u638c\u63e1\u56db\u5b63\u82b1\u5a62\uff0c\u660e\u5929\uff0c\u4f60\u517c\u7ba1\u6d1b\u9633\u697c\uff0c\u6478\u6478\u9752\u51e4\u7684\u5e95\u5b50\uff0c\u81f3\u4e8e\u8d4c\u573a\u7684\u4e8b\uff0c\u7531\u8001\u592b\u4eb2\u81ea\u517c\u7406\u3002\u201d \u3000\u3000\u6c5f\u67ab\u5fc3\u5934\u5927\u5927\u5730\u4e00\u9707\uff0c\u5fd6\u9053\uff1a\u9752\u51e4\u7adf\u7136\u662f\u53e6\u6811\u4e00\u5e1c\uff0c\u8fde\u603b\u7ba1\u4e5f\u6478\u4e0d\u6e05\u5e95\u7ec6\uff1f\u4f46\u95fb\u9093\u98de\u63a5\u9053\uff1a\u201c\u81f3\u4e8e\u738b\u526f\u603b\u7ba1\u7684\u4e8b\uff0c\u5c31\u8bf4\u4ed6\u548c\u8001\u592b\u8a00\u8bed\u51b2\u649e\uff0c\u5bf9\u4e86\u4e00\u638c\u540e\uff0c\u7834\u7a97\u800c\u53bb\u3002\u201d \u3000\u3000\u6c5f\u67ab\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u603b\u7ba1\uff0c\u54b1\u4eec\u4eba\u624b\u592a\u5c11\uff0c\u5404\u65b9\u9762\u90fd\u7167\u987e\u4e0d\u5468\uff0c\u4f55\u4e0d\u7ea6\u8bf7\u51e0\u4e2a\u4eba\uff0c\u8fdb\u5165\u6d1b\u9633\u5c45\u4e2d\uff0c\u4f5c\u4e3a\u81c2\u52a9\uff1f\u201d \u3000\u3000\u9093\u98de\u7565\u4e00\u6c89\u541f\uff0c\u9053\uff1a\u201c\u4f60\u6709\u53ef\u4ee5\u4fe1\u4efb\u7684\u670b\u53cb\u4e48\uff1f\u201d \u3000\u3000\u201c\u53ef\u4ee5\u4fe1\u4efb\u7684\u670b\u53cb\uff0c\u73b0\u6709\u51e0\u4e2a\uff0c\u4e0d\u8fc7\uff0c\u6211\u5f97\u5148\u548c\u4ed6\u4eec\u8bf4\u660e\u767d\uff0c\u540c\u610f\u4e86\u6c5f\u67d0\u518d\u5411\u603b\u7ba1\u62a5\u544a\uff0c\u7531\u603b\u7ba1\u88c1\u51b3\uff1f\u201d \u3000\u3000\u4ed6\u867d\u7136\u662f\u5f15\u8fdb\u81ea\u5df1\u7684\u4eba\u3002\u4f46\u8bed\u610f\u5a49\u8f6c\uff0c\u542c\u5f97\u9093\u98de\u5fc3\u4e2d\u5341\u5206\u8212\u7545\uff0c\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u597d\uff01\u4f60\u53bb\u627e\u4eba\u5427\uff1f\u53ea\u8981\u4ed6\u4eec\u827a\u6709\u4e13\u7cbe\uff0c\u53c8\u80af\u5fe0\u5fc3\u6548\u547d\uff0c\u6211\u6765\u60f3\u529e\u6cd5\u5b89\u63d2\u4ed6\u4eec\u3002\u201d \u3000\u3000\u201c\u662f\uff01\u2026\u2026\u201d\u6c5f\u67ab\u4f4e\u58f0\u9053\uff1a\u201c\u957f\u5b89\u79e6\u516b\u516c\uff0c\u9886\u5bfc\u4e00\u80a1\u5f88\u5f3a\u5927\u7684\u5b9e\u529b\uff0c\u5982\u82e5\u6211\u4eec\u80fd\u628a\u4ed6\u5f15\u5165\u638c\u63e1\u3002\u5173\u6d1b\u9053\u4e0a\u7684\u4e00\u4e3e\u4e00\u52a8\u3001\u90fd\u5728\u6211\u4eec\u7684\u76d1\u89c6\u4e4b\u4e0b\u4e86\u3002\u201d \u3000\u3000\u9093\u98de\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u542c\u8bf4\u5176\u4eba\u81ea\u89c6\u751a\u9ad8\uff0c\u5c82\u7518\u4e3a\u6211\u4eec\u6548\u529b\u5417\uff1f\u201d \u3000\u3000\u201c\u79e6\u516b\u516c\u8001\u8c0b\u6df1\u7b97\u3002\u6b66\u529f\u7cbe\u6e5b\uff0c\u4f46\u4ed6\u6709\u4e00\u4e2a\u81f4\u547d\u7684\u7f3a\u70b9\u2026\u2026\u201d\u6c5f\u67ab\u5fae\u7b11\u9053\uff1a\u201c\u6027\u559c\u6e14\u8272\u3002\u89c1\u4e0d\u5f97\u7edd\u9c7c\u7f8e\u5973\u2026\u2026\u201d \u3000\u3000\u9093\u98de\u9053\uff1a\u201c\u4ed6\u6765\u8fd9\u91cc\u5403\u8fc7\u996d\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u4e5f\u4e2d\u4e86\u6625\u82b1\u56db\u5a62\u7684\u6697\u7b97\uff0c\u6015\u4ed6\u4e8b\u540e\u62a5\u590d\uff0c\u6765\u627e\u9ebb\u70e6\uff0c\u6240\u4ee5\uff0c\u5c5e\u4e0b\u53c8\u6697\u4e2d\u70b9\u4e86\u4ed6\u5947\u7ecf\u8109\u7a74\u3002\u201d \u3000\u3000\u9093\u98de\u9053\uff1a\u201c\u73b0\u5728\u5462\uff1f\u201d \u3000\u3000\u201c\u5509\uff01\u5982\u82e5\u4e8e\u62d6\u5ef6\u5341\u5929\uff0c\u4e0d\u89e3\u4ed6\u5947\u7ecf\u4f24\u8109\uff0c\u79e6\u516b\u516c\u5c31\u53ef\u80fd\u7ec8\u8eab\u6b8b\u5e9f\u4e86\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4e00\u70b9\uff0c\u4ed6\u77e5\u9053\u5417\uff1f\u201d\u9093\u98de\u9053\uff1a\u201c\u8fd9\u4e9b\u6210\u540d\u4eba\u7269\u6709\u4e00\u4e2a\u5171\u540c\u7684\u7279\u70b9\uff0c\u90a3\u5c31\u662f\u6b7b\u8981\u9762\u5b50\u6d3b\u53d7\u7f6a\uff0c\u9664\u975e\u662f\u8001\u547d\u4e0d\u4fdd\uff0c\u6211\u770b\u4ed6\u5f88\u96be\u4f1a\u548c\u6211\u4eec\u5408\u4f5c\uff1f\u201d \u3000\u3000\u201c\u603b\u7ba1\u653e\u5fc3\uff0c\u5c5e\u4e0b\u7528\u7684\u624b\u6cd5\u662f\u6b66\u5f53\u6d3e\u4e2d\u7684\u7edd\u6280\uff0c\u622a\u8109\u9707\u7a74\u6cd5\uff0c\u53d7\u4f24\u7684\u4eba\u3002\u4e0d\u4f1a\u6709\u5927\u7684\u611f\u89c9\uff0c\u4f46\u8109\u7a74\u4f24\u52bf\uff0c\u5374\u662f\u8d8a\u6765\u8d8a\u91cd\uff0c\u4e09\u4e2a\u6708\u540e\uff0c\u4f24\u8109\u67af\u840e\uff0c\u4f24\u7a74\u4e5f\u5c06\u56e0\u957f\u4e45\u7f3a\u8840\u67af\u6b7b\uff0c\u5c31\u7b97\u5927\u7f57\u4ed9\uff0c\u4e5f\u65e0\u6c49\u533b\u6cbb\u4e86\u3002\u201d \u3000\u3000\u201c\u6b66\u5f53\u6d3e\u6709\u8fd9\u4e48\u4e00\u79cd\u7edd\u6280\u3002\u8001\u592b\u600e\u4f1a\u672a\u66fe\u542c\u8fc7\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\uff0c\u8fd9\u4e0d\u662f\u7528\u6765\u51b3\u751f\u6b7b\u7684\u6b66\u529f\uff0c\u6ca1\u6709\u4eba\u4f1a\u6ce8\u610f\uff0c\u7ec3\u8d77\u6765\uff0c\u53c8\u8981\u8017\u8d39\u76f8\u5f53\u7684\u65f6\u95f4\u3002\u6240\u4ee5\uff0c\u5404\u4ee3\u5f1f\u5b50\uff0c\u90fd\u4e0d\u80af\u4e0b\u5de5\u592b\uff0c\u73b0\u5728\uff0c\u4e5f\u53ea\u6709\u4e24\u4f4d\u95e8\u4e2d\u957f\u8001\u4f1a\u4e86\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u53f9\u53e3\u6c14\uff0c\u9053\uff1a \u3000\u3000\u201c\u6b66\u5f53\u95e8\u4e2d\u672c\u4ee3\u5f1f\u5b50\uff0c\u6050\u53ea\u6709\u5c5e\u4e0b\u4e00\u4e2a\u4eba\u4f1a\u4e86\uff01\u201d \u3000\u3000\u9093\u98de\u7684\u9b54\u773c\u4e2d\u78a7\u5149\u95ea\u52a8\uff0c\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u771f\u662f\u6b66\u5f53\u95e8\u4e00\u5f1f\u5b50\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u603b\u7ba1\u53ef\u662f\u4e0d\u76f8\u4fe1\u5417\uff1f\u201d \u3000\u3000\u201c\u5f53\u4eca\u6c5f\u6e56\u4e0a\u5404\u5927\u95e8\u6237\uff0c\u5982\u8bba\u6b66\u529f\u535a\u5927\uff0c\u6280\u827a\u7cbe\u6e5b\uff0c\u5f53\u4ee5\u5c11\u6797\u3001\u6b66\u5f53\u4e3a\u4e3b\uff0c\u2026\u2026\u201d\u9093\u98de\u9053\uff1a\u201c\u4f46\u8fd9\u4e24\u4e2a\u5927\u95e8\u6d3e\uff0c\u6bcf\u4e00\u4ee3\u90fd\u6709\u6570\u767e\u540d\u5f1f\u5b50\u5165\u95e8\uff0c\u5374\u5f88\u5c11\u6709\u6770\u51fa\u7684\u4eba\u624d\u51fa\u73b0\u6c5f\u7322\uff0c\u6211\u60f3\u662f\u4ed6\u4eec\u6559\u6388\u7684\u65b9\u6cd5\u6709\u95ee\u9898\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u4e0d\u9519\uff0c\u603b\u7ba1\u4e00\u8bed\u9053\u4e2d\uff0c\u4ed6\u4eec\u4e0d\u77e5\u56e0\u6750\u65bd\u6559\uff0c\u4e0d\u77e5\u9053\u9009\u62e9\u6280\u827a\uff0c\u8fdb\u5ea6\u6162\uff0c\u4e5f\u4f7f\u624d\u534e\u5353\u7edd\u7684\u5f1f\u5b50\u4eec\uff0c\u63d0\u4e0d\u8d77\u5174\u81f4\uff0c\u5c5e\u4e0b\u5c31\u662f\u56e0\u6b64\u4e8b\u548c\u4e24\u4f4d\u4f20\u827a\u5e08\u53d4\u8d77\u4e86\u51b2\u7a81\uff0c\u88ab\u4ed6\u4eec\u591a\u65b9\u8bbe\u8ba1\u9677\u5bb3\uff0c\u903c\u5f97\u6211\u4ea1\u547d\u5929\u6daf\u3002\u201d \u3000\u3000\u201c\u4f60\u81ea\u89c9\u624d\u534e\u5353\u7edd\uff0c\u662f\u5417\uff1f\u2026\u2026\u201d\u9093\u98de\u5e72\u7b11\u4e24\u58f0\uff0c\u9053\uff1a\u201c\u4e0d\u8bba\u4f60\u51fa\u8eab\u90a3\u4e00\u95e8\u6d3e\uff1f\u4f46\u786e\u662f\u4e00\u4e2a\u80f8\u6000\u97ec\u7565\u3001\u673a\u667a\u591a\u53d8\u7684\u4eba\u7269\uff0c\u8001\u592b\u8fd9\u53cc\u9b54\u773c\uff0c\u80fd\u770b\u7a7f\u6c5f\u6e56\u4e0a\u5404\u79cd\u80f8\u6000\u673a\u8bc8\u7684\u4eba\u5fc3\uff0c\u6613\u5bb9\u836f\u7269\u3002\u4eba\u76ae\u9762\u5177\uff0c\u90fd\u65e0\u6cd5\u9003\u8fc7\u6211\u4e00\u773c\u7684\u9274\u5b9a\uff0c\u4f60\u7adf\u7136\u8499\u853d\u4e86\u8001\u592b\u6570\u6708\u4e4b\u4e45\u3002 \u3000\u3000\u53cd\u628a\u4f60\u89c6\u4f5c\u5fc3\u8179\u2026\u2026\u201d \u3000\u3000\u201c\u603b\u7ba1\u3002\u6c5f\u67d0\u4eba\u77e5\u6069\u56fe\u62a5\uff0c\u65e9\u5df2\u81ea\u8ba4\u662f\u603b\u7ba1\u5fc3\u8179\u4e86\uff0c\u4eca\u65e5\u4e4b\u4e8b\u3002\u603b\u7ba1\u4e00\u65e5\u4e86\u7136\uff0c\u5f53\u77e5\u6c5f\u67d0\u6240\u8a00\u4e0d\u865a\uff1f\u201d \u3000\u3000\u201c\u73b0\u5728\uff0c\u6211\u4eec\u5df2\u662f\u98ce\u96e8\u540c\u821f\uff0c\u4f60\u653e\u5f00\u624b\u53bb\u51c6\u5907\u5427\uff01\u8001\u592b\u81ea\u4f1a\u5168\u529b\u652f\u63f4\uff0c\u81f3\u4e8e\uff0c\u8fd9\u4e2a\u795e\u79d8\u7ec4\u5408\u4e2d\u7684\u4e8b\uff0c\u770b\u4f3c\u7b80\u660e\uff0c\u5b9e\u5219\u6df1\u8fdc\u83ab\u6d4b\uff0c\u8001\u592b\u4e5f\u65e0\u6cd5\u8bf4\u5f97\u6e05\u695a\uff0c\u4ee5\u540e\uff0c\u6211\u4f1a\u628a\u77e5\u9053\u7684\u5185\u60c5\u3001\u8d44\u6599\uff0c\u90fd\u544a\u8bc9\u4f60\u3002\u201d \u3000\u3000\u201c\u591a\u8c22\u603b\u7ba1\u539a\u7231\uff0c\u5c5e\u4e0b\u8fd9\u5c31\u53bb\u548c\u51e0\u4f4d\u53cb\u4eba\u5546\u91cf\uff0c\u987a\u4fbf\u8bf4\u670d\u79e6\u516b\u516c\u3002\u2026\u2026\u201d \u3000\u3000\u201c\u5982\u662f\u4ed6\u4e0d\u80af\u7b54\u5e94\u5462\uff1f\u201d \u3000\u3000\u201c\u90a3\u5c31\u53ea\u597d\u5148\u6740\u4e86\u4ed6\u2026\u2026\u201d\u6c5f\u67ab\u8bf4\uff1a\u201c\u603b\u7ba1\u8bf7\u5c0f\u5fc3\u4e00\u4e9b\u3002\u5c5e\u4e0b\u4f1a\u5c3d\u5feb\u5546\u8bf7\u670b\u53cb\u4eec\u8fdb\u5165\u6d1b\u9633\u5c45\u6765\uff0c\u4e00\u6709\u6d88\u606f\u3002\u7acb\u523b\u5411\u603b\u7ba1\u62a5\u544a\u3002\u201d \u3000\u3000\u201c\u653e\u5fc3\u53bb\u5427\uff01\u4e09\u4e94\u65e5\u5185\uff0c\u4ed6\u4eec\u8fd8\u4e0d\u4f1a\u627e\u4e0a\u6211\uff0c\u518d\u8bf4\uff0c\u72e1\u5154\u4e09\u7a9f\u3002\u4f55\u51b5\u4eba\u4e4e\uff01\u8001\u592b\u6570\u5341\u5e74\u6c5f\u6e56\u5386\u7ec3\uff0c\u5c82\u4f1a\u5168\u65e0\u51c6\u5907\uff1f\u201d \u3000\u3000\u4ec0\u4e48\u6837\u7684\u51c6\u5907\uff0c\u9093\u98de\u6ca1\u6709\u8bf4\uff0c\u6c5f\u67ab\u4e5f\u6ca1\u6709\u95ee\uff0c\u5f7c\u6b64\u4e4b\u95f4\uff0c\u867d\u7136\u5404\u6709\u4fdd\u7559\u3002\u4f46\u540c\u5fc3\u62d2\u654c\u8fd9\u4ef6\u4e8b\u4e0a\uff0c\u5374\u53d6\u5f97\u4e86\u4e00\u81f4\u7684\u8bc6\u89c1\u3002 \u3000\u3000\u98ce\u96ea\u5c0f\u9986\u7684\u9152\u62db\u3002\u5df2\u88ab\u6536\u8d77\uff0c\u5c0f\u5c0f\u7684\u74e6\u5c4b\u3002\u7d27\u95ed\u7740\u53cc\u95e8\u3002 \u3000\u3000\u4f46\u5730\u4e0b\u90a3\u5ea7\u5bbd\u655e\u7684\u5927\u5385\u4e2d\u56f4\u5750\u7740\u90a3\u6279\u79bb\u5f00\u6d1b\u9633\u5c45\u7684\u4e2d\u5e74\u8c6a\u5ba2\uff0c\u548c\u4e00\u884c\u4ece\u4eba\u3002 \u3000\u3000\u5f53\u7136\uff0c\u90a3\u7bb1\u4e2d\u7684\u9ec4\u91d1\u3001\u73e0\u5b9d\u53ea\u6709\u4e0a\u9762\u4e00\u5c42\u662f\u771f\u7684\u3002 \u3000\u3000\u6b64\u65f6\uff0c\u5ba4\u4e2d\u4eba\u90fd\u5df2\u9664\u53bb\u4e86\u6613\u5bb9\u836f\u7269\uff0c\u6062\u590d\u4e86\u672c\u6765\u9762\u76ee\u3002 \u3000\u3000\u8001\u53eb\u5316\u559d\u4e86\u4e00\u53e3\u8336\uff0c\u9053\uff1a\u201c\u9b54\u773c\u9093\u98de\uff0c\u6709\u8fc7\u76ee\u4e0d\u5fd8\u4e4b\u80fd\uff0c\u8001\u53eb\u5316\u5b50\u548c\u4ed6\u89c1\u8fc7\u4e86\u597d\u51e0\u6b21\u9762\uff0c\u53ea\u6015\u4ed6\u65e9\u5df2\u8ba4\u51fa\u6211\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u4f1a\u5427\uff01\u2026\u2026\u201d\u6bb5\u4e5d\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u9093\u98de\u5982\u82e5\u65e9\u5df2\u77a7\u51fa\u4f60\u7684\u8eab\u4efd\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u5f53\u9762\u63ed\u7a7f\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u4e00\u8138\u65e0\u5948\u7684\u795e\u9c7c\uff0c\u7f00\u6b65\u800c\u5165\u3002 \u3000\u3000\u4ed6\u4ecd\u7136\u662f\u97e9\u526f\u603b\u7ba1\u7684\u88c5\u626e\uff0c\u4e24\u4eba\u5bd2\u5149\u95ea\u95ea\u7684\u957f\u5251\uff0c\u5206\u62b5\u5728\u4ed6\u540e\u9888\u548c\u5de6\u808b\u4e4b\u4e0a\u3002 \u3000\u3000\u6562\u60c5\u6c5f\u67ab\u662f\u88ab\u4eba\u62bc\u8fdb\u6765\u7684\uff1f\u62bc\u89e3\u6c5f\u67ab\u7684\u5374\u662f\u4e24\u4e2a\u6e05\u79c0\u7edd\u4f26\u7684\u5c11\u5973\uff0c\u4e00\u8eab\u6de1\u9752\u8863\u88e4\uff0c\u9752\u5e15\u5305\u53d1\uff0c\u867d\u7136\u8863\u7740\u6709\u70b9\u6734\u7d20\u3001\u8001\u6c14\uff0c\u4f46\u5374\u63a9\u4e0d\u4f4f\u90a3\u80a1\u5a34\u96c5\uff0c\u8096\u7eaf\u7684\u7075\u79c0\u4e4b\u6c14\u3002 \u3000\u3000\u201c\u5f20\u59e8\uff0c\u4ed6\u8bf4\u8ba4\u8bc6\u4f60\uff1f\u2026\u2026\u201d\u5de6\u4fa7\u7684\u5c11\u5973\u671b\u7740\u5f20\u56db\u59d1\u8bf4\uff1a\u201c\u6211\u8fd8\u662f\u4e0d\u653e\u5fc3\uff0c\u6240\u4ee5\uff0c\u62bc\u7740\u4ed6\u8fdb\u6765\u89c1\u4f60\uff01\u201d \u3000\u3000\u5f20\u56db\u59d1\u53f3\u624b\u8f7b\u6325\uff0c\u62e6\u4f4f\u4e86\u8001\u53eb\u5316\u7b49\u4e09\u4eba\uff0c\u671b\u7740\u6709\u70b9\u72fc\u72c8\u7684\u6c5f\u67ab\uff0c\u7b11\u9053\uff1a\u201c\u54df\uff01\u8fd9\u4e0d\u662f\u6d1b\u9633\u5c45\u4e2d\u7684\u97e9\u526f\u603b\u7ba1\u5417\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u63a5\u9053\uff1a\u201c\u5927\u59d0\uff0c\u5979\u4eec\u5251\u4e0a\u51dd\u805a\u7684\u5f88\u5f3a\u7684\u5185\u529b\u3002\u7a0d\u4e00\u4e0d\u614e\uff0c\u5c31\u8981\u7c91\u5c0f\u5f1f\u5185\u8151\u6d1e\u7a7f\uff01\u4f60\u8bf4\u5979\u4eec\u5fc3\u4e2d\u7eaf\u6d01\u3001\u5584\u826f\uff0c\u6211\u770b\u4e0d\u662f\u90a3\u4e48\u56de\u4e8b\u4e86\uff1f\u201d \u3000\u3000\u5f20\u56db\u59d1\u7b11\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u6211\u8fd9\u4e9b\u65e5\u5b50\u91cc\u7684\u6559\u8bf2\u6210\u529f\u4e86\uff0c\u54a6\uff01\u5144\u5f1f\uff0c\u8981\u5979\u4eec\u51fa\u624b\u72e0\u4e00\u70b9\uff0c\u4e0d\u8981\u7559\u7ed9\u4eba\u53cd\u51fb\u7684\u4f59\u5730\uff0c\u8fd8\u662f\u4f60\u5144\u5f1f\u7ed9\u6211\u7684\u5b9d\u8d35\u610f\u89c1\u54ea\uff01\u201d \u3000\u3000\u201c\u4e0d\u9519\uff01\u4f46\u90a3\u662f\u5bf9\u654c\u4eba\u3001\u4e0d\u662f\u5bf9\u6211\u5440\uff01\u201d \u3000\u3000\u4e8c\u5973\u5df2\u7531\u4e24\u4eba\u5bf9\u8bdd\u4e2d\u542c\u51fa\u4e86\u4e00\u70b9\u7709\u76ee\u3002\u6e05\u79c0\u7684\u8138\u4e0a\uff0c\u5fae\u5c55\u7b11\u610f\uff0c\u4f46\u4e24\u67c4\u5251\u5374\u4ecd\u7136\u7d27\u62b5\u5728\u6c5f\u67ab\u7684\u8981\u5bb3\u4e0a\u3002 \u3000\u3000\u6c5f\u67ab\u611f\u89c9\u5230\u90a3\u5251\u5c16\u4e0a\u900f\u51fa\u7684\u51b7\u5229\u5251\u6c14\uff0c\u6d78\u5165\u808c\u80a4\u3002\u5fc3\u4e2d\u6697\u6697\u4f69\u670d\u4e24\u4e2a\u5341\u4e94\u516d\u5c81\u7684\u4e2b\u5934\uff0c\u7adf\u5df2\u6709\u5982\u6b64\u6210\u5c31\uff0c\u4ee5\u771f\u529f\u5b9e\u5b66\u800c\u8a00\uff0c\u4e8c\u5973\u7684\u6210\u5c31\uff0c\u5df2\u80af\u5b9a\u7684\u8d85\u8d8a\u8fc7\u56db\u5b63\u82b1\u5a62\u3002 \u3000\u3000\u5f53\u7136\uff0c\u56db\u5b63\u82b1\u5a62\u5177\u6709\u7684\u535a\u6742\u6280\u827a\uff0c\u4e5f\u8bb8\u5c31\u975e\u4e8c\u5973\u80fd\u53ca\u4e86\u3002 \u3000\u3000\u5f20\u56db\u59d1\u76ee\u5149\u4e00\u63a0\u4e8c\u5973\uff0c\u7b11\u9053\uff1a\u201c\u4e2b\u5934\u3002\u6536\u5251\u5427\uff01\u4f60\u4eec\u65e9\u77e5\u9053\u4ed6\u662f\u8c01\u4e86\uff1f\u201d \u3000\u3000\u4e8c\u5973\u8fd8\u5251\u5165\u9798\uff0c\u9f50\u9f50\u8eac\u8eab\u4e00\u793c\uff0c\u9053\uff1a\u201c\u5bf9\u4e0d\u4f4f\u6c5f\u53d4\u53d4\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u6014\u4e86\u4e00\u5f81\u3001\u9053\uff1a\u201c\u600e\u4e48\uff1f\u4f60\u4eec\u65e9\u77e5\u9053\u6211\u662f\u8c01\u4e86\uff1f\u201d \u3000\u3000\u53f3\u8fb9\u4e00\u4e2a\u7a0d\u77ee\u4e00\u70b9\u7684\u5c11\u5973\uff0c\u7b11\u9053\uff1a\u201c\u77e5\u9053\u662f\u6709\u70b9\u77e5\u9053\uff0c\u4e0d\u8fc7\uff0c\u5f20\u59e8\u544a\u8bc9\u6211\u4eec\u4eba\u5fc3\u96be\u6d4b\uff0c\u6d1b\u9633\u5c45\u4e2d\u7684\u4eba\u66f4\u662f\u53d8\u5316\u83ab\u607b\uff0c\u6211\u4eec\u4e0d\u5f97\u4e0d\u5c0f\u5fc3\u4e00\u4e9b\uff1f\u201d \u3000\u3000\u201c\u6709\u9053\u7406\uff0c\u6c5f\u6e56\u9669\u6076\uff0c\u4e0d\u80fd\u4e0d\u9632\u4eba\u4e00\u7740\uff0c\u4f60\u4eec\u8868\u73b0\u7684\u673a\u667a\u3001\u51b7\u9759\u3002\u5df2\u8db3\u53ef\u62c5\u5f53\u5927\u4efb\u4e86\u2026\u2026\u201d\u6c5f\u67ab\u8bf4\uff1a\u201c\u770b\u6765\u8981\u4e24\u4f4d\u2026\u2026\u201d \u3000\u3000\u201c\u6162\u6765\uff0c\u6162\u6765\u2026\u2026\u201d\u5f20\u56db\u59d1\u8bf4\uff1a\u201c\u5148\u628a\u4f60\u8138\u4e0a\u90a3\u5f20\u9762\u5177\u62ff\u4e0b\u6765\u3001\u8ba9\u5979\u4eec\u770b\u770b\u4f60\u7684\u771f\u9762\u76ee\u518d\u8c08\u522b\u7684\u3002\u201d \u3000\u3000\u201c\u5927\u59d0\u8bf4\u7684\u662f\u2026\u2026\u201d\u6c5f\u67ab\u5c0f\u5fc3\u7ffc\u7ffc\u7684\u53d6\u4e0b\u4eba\u76ae\u9762\u5177\u6298\u597d\uff0c\u653e\u5165\u6000\u4e2d\u3002 \u3000\u3000\u4e8c\u5973\u4ed4\u7ec6\u770b\u4e86\u6c5f\u67ab\u4e00\u773c\uff0c\u8138\u4e0a\u7acb\u523b\u6cdb\u73b0\u51fa\u4e00\u80a1\u751c\u7f8e\u7684\u7b11\u610f\u3002 \u3000\u3000\u5de6\u9996\u5c11\u5973\u9053\uff1a\u201c\u6c5f\u5927\u54e5\u5982\u6b64\u5e74\u8f7b\uff0c\u6211\u4eec\u53eb\u4ed6\u6c5f\u53d4\u53d4\uff0c\u5c82\u4e0d\u662f\u5927\u5403\u4e8f\u4e86\u3002\u201d \u3000\u3000\u53f3\u9996\u5c11\u5973\uff0c\u9053\uff1a\u201c\u5f20\u59e8\uff0c\u4f60\u5c31\u540c\u610f\u96ea\u59d0\u4e00\u6b21\u561b\uff01\u6211\u4e5f\u8d5e\u6210\u96ea\u59d0\u7684\u610f\u89c1\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u5fc3\u4e2d\u6697\u6697\u53f9\u606f\uff0c\u53e3\u4e2d\u5374\u8bf4\u9053\uff1a\u201c\u4ed6\u53eb\u5f20\u59e8\u5927\u59d0\u3002\u4f60\u4eec\u53eb\u4ed6\u6c5f\u5927\u54e5\uff0c\u8fd9\u4e2a\u8f88\u4efd\u600e\u4e48\u8bba\u6cd5\u5462\uff1f\u201d \u3000\u3000\u4e8c\u5973\u4f3c\u662f\u6ca1\u6709\u60f3\u5230\u8fd8\u6709\u8fd9\u4ef6\u9ebb\u70e6\u3002\u4e0d\u7981\u5fae\u5fae\u4e00\u5446\uff0c\u4e0d\u77e5\u8be5\u5982\u4f55\u56de\u7b54\u3002 \u3000\u3000\u201c\u6c5f\u6e56\u4ea4\u5f80\uff0c\u9053\u4e49\u4e3a\u5148\uff0c\u5927\u5bb6\u5404\u652f\u5404\u7684\u5c31\u662f\u2026\u2026\u201d\u8001\u53eb\u5316\u8bf4\uff1a\u201c\u6211\u770b\u6c5f\u8001\u5f1f\u8fd9\u4e2a\u6a21\u6837\uff0c\u8981\u96ea\u3001\u971c\u4e8c\u4f4d\u4f84\u5973\uff0c\u53eb\u4ed6\u53d4\u53d4\uff0c\u4e5f\u5927\u59d4\u5c48\u4e24\u4f4d\u4e2b\u5934\u4e86\u3002\u201d \u3000\u3000\u201c\u662f\u561b\uff01\u2026\u2026\u201d\u5de6\u9996\u7684\u5c11\u5973\u63a5\u9053\uff1a\u201c\u5c31\u7b97\u6211\u4eec\u53eb\u4ed6\u6c5f\u5927\u53d4\uff0c\u4ed6\u4e5f\u4e0d\u597d\u610f\u601d\u7b54\u5e94\u3002\u5927\u5bb6\u5e74\u9f84\u5dee\u4e0d\u591a\u5417\uff1f\u201d \u3000\u3000\u201c\u5582\uff01\u6c5f\u5927\u54e5\uff0c\u4f60\u81ea\u5df1\u8bf4\u5427\uff01\u2026\u2026\u201d\u53f3\u9996\u5c11\u5973\u53eb\u9053\uff1a\u201c\u8981\u6211\u4eec\u53eb\u4f60\u6c5f\u5927\u53d4\u5462\uff1f\u8fd8\u662f\u53eb\u4f60\u6c5f\u5927\u54e5\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5927\u53d4\u3001\u5927\u54e5\uff0c\u90fd\u4e0d\u91cd\u8981\uff0c\u4f60\u4eec\u81ea\u5df1\u60f3\u53eb\u4ec0\u4e48\u5c31\u53eb\u4ec0\u4e48\u5427\uff01\u201d \u3000\u3000\u5f20\u56db\u59d1\u9053\uff1a\u201c\u597d\u4e86\uff0c\u8fd9\u4ef6\u4e8b\u4e0d\u7528\u4e89\u5566\uff01\u6211\u4e5f\u61d2\u5f97\u518d\u7ba1\uff0c\u4f60\u4eec\u81ea\u5df1\u770b\u7740\u529e\u5427\uff01\u2026\u2026\u201d \u3000\u3000\u76ee\u5149\u8f6c\u5230\u6c5f\u67ab\u8138\u4e0a\uff0c\u6307\u7740\u5de6\u9996\u5c11\u5973\uff0c\u9053\uff1a\u201c\u59d0\u59d0\u541f\u96ea\uff0c\u90a3\u8fb9\u662f\u59b9\u59b9\u541f\u971c\u3002\u201d \u3000\u3000\u6c5f\u67ab\u5bf9\u4e8c\u5973\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u73b0\u5728\uff0c\u60c5\u52bf\u6709\u53d8\uff0c\u6211\u7279\u5730\u8d76\u6765\u548c\u8bf8\u4f4d\u5546\u91cf\u4e00\u4e0b\u3002\u201d \u3000\u3000\u4ed6\u5fc3\u4e2d\u5f88\u6025\uff0c\u4e0d\u5f85\u4eba\u95ee\uff0c\u5c31\u4ed4\u7ec6\u7684\u8bf4\u51fa\u4e86\u4e8b\u60c5\u7ecf\u8fc7\u3002 \u3000\u3000\u8001\u53eb\u5316\u9053\uff1a\u201c\u9b54\u773c\u9093\u98de\u4e0d\u662f\u7b49\u95ed\u4eba\u7269\uff0c\u6709\u4ed6\u5e2e\u6211\u4eec\uff0c\u589e\u52a0\u4e86\u4e0d\u5c11\u5b9e\u529b\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u522b\u6253\u5982\u610f\u7b97\u76d8\uff0c\u9093\u98de\u4e0d\u662f\u5e2e\u6211\u4eec\uff0c\u800c\u662f\u81ea\u6c42\u591a\u798f\uff0c\u73b0\u5728\uff0c\u6211\u4eec\u8981\u5982\u4f55\u884c\u52a8\uff0c\u624d\u662f\u6700\u91cd\u8981\u7684\u4e8b\u60c5\u3002\u201d \u3000\u3000\u201c\u5bf9\uff01\u5982\u80fd\u5148\u63a7\u5236\u4f4f\u6d1b\u9633\u5c60\uff0c\u6548\u7528\u975e\u5e38\u4e4b\u5927\uff0c\u53ea\u53ef\u60dc\uff0c\u6211\u4eec\u4eba\u624b\u592a\u5c11\uff0c\u5f88\u96be\u5206\u914d\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u53ea\u6015\u5927\u59d0\u548c\u96ea\u3001\u971c\u4e24\u4f4d\u59d1\u5a18\uff0c\u4e5f\u5f97\u51fa\u52a8\u4e86\u3002\u201d \u3000\u3000\u201c\u597d\u554a\uff01\u2026\u2026\u201d\u541f\u96ea\u8bf4\uff1a\u201c\u6628\u5929\uff0c\u6211\u4eec\u626e\u6210\u4e24\u4e2a\u5f00\u9053\u91cd\u5b50\uff0c\u73a9\u7684\u5f88\u8fc7\u763e\uff0c\u2026\u2026\u201d \u3000\u3000\u770b\u770b\u5f20\u56db\u59d1\uff0c\u7a81\u7136\u4f4f\u53e3\u4e0d\u8a00\u3002 \u3000\u3000\u5f20\u56db\u59d1\u9053\uff1a\u201c\u6211\u4eec\u5927\u4f19\u513f\u5168\u8fdb\u5165\u6d1b\u9633\u5c45\uff0c\u4e5f\u662f\u4e0d\u8db3\u5206\u914d\uff0c\u4f55\u51b5\uff0c\u8fd9\u91cc\u4e0d\u80fd\u6295\u4eba\u5750\u5b88\u2026\u2026\u201d \u3000\u3000\u201c\u6240\u4ee5\uff0c\u5c0f\u5f1f\u4e5f\u5728\u4e3a\u96be\uff0c\u2026\u2026\u201d \u3000\u3000\u6bb5\u4e5d\u63a5\u9053\uff1a\u201c\u6c5f\u8001\u5f1f\uff0c\u522b\u628a\u6211\u8ddf\u80e1\u5144\uff0c\u770b\u6210\u4e00\u6d3e\u638c\u95e8\u4eba\u7684\u8eab\u4efd\uff0c\u53ea\u8981\u80fd\u7528\u5f97\u7740\u6211\u4eec\u5730\u65b9\uff0c\u4ec0\u4e48\u6837\u7684\u8eab\u4efd\uff0c\u6211\u4eec\u90fd\u4e50\u4e8e\u6548\u547d\u3002\u201d \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u591a\u8c22\u4e24\u4f4d\u2026\u2026\u201d \u3000\u3000\u5f20\u56db\u59d1\u4e3e\u624b\u6309\u5507\uff0c\u9053\uff1a\u201c\u6709\u4eba\u6765\u4e86\u3002\u201d \u3000\u3000\u96ea\u3001\u971c\u4e8c\u5973\u8eab\u5b50\u4e00\u95ea\uff0c\u4eba\u5df2\u7a7f\u51fa\u5385\u53bb\u3002 \u3000\u3000\u6c5f\u67ab\u795e\u60c5\u51dd\u91cd\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5927\u59d0\uff0c\u6765\u4eba\u81f3\u5c11\u4e09\u4e2a\uff1f\u201d \u3000\u3000\u201c\u4f1a\u4e0d\u4f1a\u662f\u8ffd\u7740\u4f60\u6765\u7684\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u9093\u98de\u8fd9\u4e2a\u4eba\uff0c\u9634\u5f97\u5f88\uff0c\u4e0d\u80fd\u592a\u76f8\u4fe1\u4ed6\uff1f\u201d \u3000\u3000\u53ea\u89c1\u4eba\u5f71\u4e00\u95ea\uff0c\u541f\u971c\u5df2\u7a7f\u5165\u5385\u4e2d\uff0c\u9053\uff1a\u201c\u5f20\u59e8\uff0c\u548c\u5c1a\u4f2f\u4f2f\u56de\u6765\u4e86\uff0c\u8fd8\u5e26\u4e86\u4e24\u4e2a\u4eba\u6765\u3002\u201d \u3000\u3000\u80e1\u840d\u5fc3\u4e2d\u4e00\u52a8\uff0c\u4ed8\u9053\uff1a\u6c5f\u67ab\u597d\u7075\u7684\u8033\u76ee\uff0e\u4ed6\u7adf\u7136\u542c\u5f97\u51fa\u6709\u4e09\u4e2a\u4eba\u6765\u3002 \u3000\u3000\u5f20\u56db\u59d1\u9053\uff1a\u201c\u971c\u513f\uff0e\u548c\u5c1a\u5e26\u7684\u4e24\u4e2a\u4eba\uff0c\u5e74\u7eaa\u5982\u4f55\uff1f\u201d \u3000\u3000\u541f\u971c\u4fa7\u7740\u8138\u513f\uff0e\u671b\u7740\u6c5f\u67ab\uff0c\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6bd4\u6c5f\u5927\u54e5\u5927\u4e00\u4e9b\uff0c\u6bd4\u6bb5\u53d4\u53d4\u4eec\u90fd\u5e74\u8f7b\u591a\u4e86\u3002\u201d \u3000\u3000\u4f46\u95fb\u4e03\u5b9d\u548c\u5c1a\u5927\u58f0\u53eb\u9053\uff1a\u201c\u7d2f\u6b7b\u548c\u5c1a\u4e86\uff0c\u6211\u8fd9\u4e00\u6765\u4e00\u5f80\uff0c\u8dd1\u4e86\u4e00\u5343\u591a\u91cc\uff0c\u53ea\u4e0d\u8fc7\u4e94\u516d\u5929\u554a\uff01\u4f60\u4eec\u7b97\u7b97\u770b\uff0c\u548c\u5c1a\u8981\u6bcf\u5929\u8dd1\u4e09\u767e\u91cc\uff0c\u7b97\u4e0d\u7b97\u662f\u529f\u788c\u5954\u6ce2\u5462\uff1f\u201d \u3000\u3000\u968f\u7740\u547c\u559d\u58f0\uff0c\u9152\u8089\u548c\u5c1a\u884c\u5165\u4e86\u5927\u5385\u3002 \u3000\u3000\u7d27\u968f\u548c\u5c1a\u8eab\u540e\u7684\uff0c\u662f\u4e24\u4e2a\u5e74\u8f7b\u4eba\uff0c\u7adf\u662f\u5357\u5200\u95e8\u4e0b\u7684\u5927\u5f1f\u5b50\u90ed\u5929\u540c\uff0c\u548c\u5317\u5251\u95e8\u7684\u80e1\u5143\u3002 \u3000\u3000\u4e24\u4eba\u4e00\u773c\u770b\u5230\u80e1\u840d\u548c\u6bb5\u4e5d\u3002\u7acb\u65f6\u5927\u53eb\u4e00\u58f0\uff1a\u201c\u5e08\u7236\u3002\u201d \u3000\u3000\u6251\u5728\u800c\u4eba\u8eab\u524d\u8dea\u4e0b\u3002 \u3000\u3000\u80e1\u840d\u3001\u6bb5\u4e5d\u6276\u8d77\u4e24\u4eba\uff0c\u540c\u65f6\u95ee\u9053\uff1a\u201c\u600e\u4e48\u4f1a\u662f\u4f60\u2026\u2026\u201d \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u63a5\u9053\uff1a\u201c\u4ed6\u4eec\u6839\u672c\u5c31\u6ca1\u6709\u56de\u71d5\u5c71\u9ec4\u53f6\u8c37\uff0c\u82cf\u5dde\u6bb5\u5bb6\u5821\uff0c\u4ed6\u4eec\u7559\u5728\u6469\u5929\u5cad\u8ffd\u89c5\u4f60\u4eec\u4e24\u4eba\u7684\u4e0b\u843d\u3002\u201d \u3000\u3000\u80e1\u5143\u63a5\u9053\uff1a\u201c\u8bf7\u6055\u5b69\u513f\u4e0d\u5b5d\uff0c\u5b69\u513f\u8981\u7559\u4e0b\u6765\u5bfb\u627e\u7239\u7239\uff0c\u4e5d\u4f4d\u5e08\u5144\u5168\u90fd\u540c\u610f\uff0c\u4ed6\u4eec\u4e5f\u7b54\u5e94\u5b69\u513f\uff0c\u544a\u8bc9\u73b0\u5728\u7684\u638c\u95e8\u4eba\uff0c\u4e5f\u5c31\u662f\u5b69\u513f\u7684\u6bcd\u4eb2\uff0c\u5b69\u513f\u4e3a\u8ffd\u89c5\u7236\u4eb2\u884c\u8e2a\uff0c\u8dcc\u4e0b\u4e86\u6df1\u8c37\uff0c\u751f\u6b7b\u4e0d\u660e\u2026\u2026\u201d \u3000\u3000\u201c\u4f60\u662f\u8bf4\uff0c\u4f60\u4e5d\u4f4d\u5e08\u5144\u90fd\u613f\u610f\u5e2e\u4f60\u8bf4\u8c0e\uff1f\u2026\u2026\u201d\u80e1\u840d\u6709\u4e9b\u60ca\u5947\u8bf4\uff1a\u201c\u600e\u4e48\u4f1a\u5462\uff1f\u4ed6\u4eec\u90fd\u662f\u4f60\u5a18\u7684\u5fc3\u8179\u3002\u201d \u3000\u3000\u201c\u4e5f\u8bb8\uff0c\u4ed6\u4eec\u662f\u540c\u60c5\u5b69\u513f\u4e00\u756a\u5b5d\u5fc3\u5427\uff01\u4ed6\u4eec\u786e\u5b9e\u90fd\u4eb2\u53e3\u7b54\u5e94\u4e86\u6211\u3002\u201d \u3000\u3000\u80e1\u840d\u70b9\u70b9\u5934\uff0c\u671b\u770b\u6bb5\u4e5d\u548c\u90ed\u5929\u540c\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u660e\u767d\uff0c\u90ed\u5929\u540c\u548c\u80e1\u5143\u7684\u6bcf\u4e00\u53e5\u8bdd\uff0c\u90fd\u5173\u7cfb\u91cd\u5927\uff0c\u6240\u4ee5\uff0c\u80e1\u840d\u5728\u5782\u8be2\u80e1\u5143\u65f6\uff0c\u6bb5\u4e5d\u5373\u505c\u4e0b\u7b49\u5f85\u3002 \u3000\u3000\u770b\u80e1\u840d\u7236\u5b50\u505c\u4e0b\uff0c\u6bb5\u4e5d\u624d\u51b7\u51b7\u63a5\u9053\uff1a\u201c\u8bf4\u5427\uff01\u5929\u540c\uff0c\u8981\u7b80\u5355\u660e\u4e86\uff0c\u53eb\u4eba\u542c\u5f97\u6e05\u695a\uff01\u201d \u3000\u3000\u201c\u662f\uff01\u90a3\u4e9b\u53d7\u4e86\u51bb\u4f24\u7684\u5e08\u5f1f\u4eec\uff0c\u5728\u5c71\u4e0b\u517b\u606f\u4e86\u4e24\u4e09\u5929\uff0c\u5c31\u5927\u90fd\u75ca\u6108\uff0c\u6211\u8981\u4ed6\u4eec\u5148\u56de\u82cf\u5dde\u6bb5\u5bb6\u5821\u53bb\uff0c\u6211\u8981\u7559\u4e0b\u6765\uff0c\u8ffd\u67e5\u5e08\u7236\u7684\u884c\u8e2a\u2026\u2026\u201d \u3000\u3000\u201c\u4f60\u662f\u9996\u5ea7\u5f1f\u5b50\u2026\u2026\u201d\u6bb5\u4e5d\u8bf4\uff1a\u201c\u5982\u4e0d\u80fd\u5e26\u961f\u56de\u53bb\uff0c\u8981\u4ed6\u4eec\u5982\u4f55\u5411\u5e08\u53d4\u4ea4\u4ee3\uff1f\u201d \u3000\u3000\u201c\u5e08\u5f1f\u4eec\u90fd\u77e5\u9053\u6211\u5165\u95e8\u6700\u4e45\uff0c\u5f53\u7136\u4e5f\u77e5\u9053\u6211\u5bf9\u5e08\u7236\u7684\u656c\u6155\u6700\u6df1\uff0c\u2026\u2026\u201d\u90ed\u5929\u540c\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u4ed6\u4eec\u6ca1\u6709\u529d\u6211\uff0c\u53cd\u800c\u66ff\u6211\u60f3\u597d\u4e86\u5e94\u4ed8\u638c\u95e8\u5e08\u53d4\u7684\u529e\u6cd5\uff0c\u8bf4\u6211\u8ffd\u89c5\u5e08\u7236\uff0c\u8dcc\u4e0b\u60ac\u5d16\uff0c\u2026\u2026\u201d \u3000\u3000\u6bb5\u4e5d\u70b9\u70b9\u5934\uff0c\u63a5\u9053\uff1a\u201c\u4f60\u4eec\u600e\u4e48\u9047\u4e0a\u4e03\u5b9d\u5927\u5e08\u5462\uff1f\u201d \u3000\u3000\u201c\u5148\u9047\u4e0a\u80e1\u5144\u5f1f\uff0c\u2026\u2026\u201d\u90ed\u5929\u540c\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u867d\u7136\u5f7c\u6b64\u5c1a\u6709\u6545\u610f\uff0c\u4f46\u56e0\u8ffd\u89c5\u5e08\u7236\u7684\u5fc3\u610f\u76f8\u540c\uff0c\u5f7c\u6b64\u7ed3\u4f34\u800c\u884c\u3002\u4e0d\u6599\uff0c\u56e0\u4e3a\u4e00\u70b9\u8bef\u4f1a\u2026\u2026\u5c31\u2026\u2026\u5c31\u2026\u2026\u201d \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u63a5\u9053\uff1a\u201c\u4e0d\u8981\u4e3a\u96be\u3002\u4e0b\u9762\u7684\u7531\u6211\u548c\u5c1a\u8bf4\u5427\uff01\u8fd9\u4e24\u4e2a\u695e\u5c0f\u5b50\uff0c\u5c31\u5728\u6469\u5929\u5cad\u4e0b\u9762\u4e00\u5ea7\u5c71\u8c37\u4e2d\u3002\u52a8\u624b\u6253\u4e86\u8d77\u6765\u3002\u597d\u50cf\u8981\u8ba9\u5357\u5200\u3001\u5317\u5251\u7684\u6b66\u529f\uff0c\u5728\u4ed6\u4eec\u624b\u4e2d\u5206\u4e2a\u80dc\u8d1f\u51fa\u6765\uff0c\u5e78\u597d\u548c\u5c1a\u5df2\u5b66\u8fc7\u4f60\u4eec\u5251\u6cd5\u3001\u5200\u62db\uff0c\u4e00\u770b\u5c31\u8ba4\u4e86\u51fa\u6765\u3002\u770b\u4ed6\u4eec\u6253\u5f97\u5f88\u8ba4\u771f\u3002\u548c\u5c1a\u8fde\u67b6\u4e5f\u5fd8\u8bb0\u529d\u4e86\uff0c\u5c31\u6765\u4e2a\u5750\u5c71\u89c2\u864e\u6597\uff0c\u773c\u770b\u5929\u8272\u4eba\u591c\uff0c\u8fd9\u897f\u4e2a\u5c0f\u5b50\uff0c\u7a81\u7136\u505c\u4e86\u4e0b\u6765\uff0c\u4e24\u4eba\u7adf\u7136\u6253\u8d77\u5546\u91cf\u6765\u4e86\u2026\u2026\u201d\u201c\u559d\uff01\u771f\u662f\u957f\u6c5f\u540e\u6d6a\u63a8\u524d\u6d6a\uff0c\u4e00\u9762\u62fc\u547d\uff0c\u4e00\u9762\u8fd8\u80fd\u4ea4\u670b\u53cb\u2026\u2026\u201d \u3000\u3000\u8001\u53eb\u5316\u8bf4\uff1a\u201c\u4ed6\u4eec\u90fd\u8c08\u4e9b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u5bf9\uff01\u770b\u8d77\u6765\uff0c\u6c5f\u8001\u5f1f\u8bf4\u7684\u4e0d\u9519\uff0c\u4e0b\u4e00\u4ee3\u4e2d\u6709\u70b9\u624d\u534e\u7684\u5e74\u8f7b\u4eba\uff0c\u5982\u679c\u8005\u4e00\u4ee3\u522b\u628a\u4ed6\u4eec\u4e22\u5728\u9171\u7f38\u91cc\u3002\u4ed6\u4eec\u4f1a\u53d1\u5c55\u7684\u66f4\u597d\u4e00\u4e9b\u3002 \u3000\u3000\u4ed6\u4eec\u5546\u91cf\u5728\u5b50\u591c\u4e4b\u540e\uff0c\u4e8e\u6bd4\u8bd5\u4e00\u767e\u56de\u5408\u3002\u5982\u82e5\u4ecd\u7136\u4e0d\u5206\u80dc\u8d1f\uff0c\u5c31\u6682\u65f6\u505c\u624b\u3002\u5408\u529b\u5bfb\u627e\u5e08\u7236\u4e0b\u843d\u3002\u4ee5\u4e00\u4e2a\u6708\u4e3a\u671f\u3002\u5982\u82e5\u627e\u4e0d\u51fa\u7ebf\u7d22\uff0c\u5927\u5bb6\u5c31\u5404\u51ed\u672c\u9886\uff0c\u5728\u6469\u5929\u5cad\u4e0a\u62fc\u4e2a\u751f\u6b7b\uff0c\u90a3\u4e00\u6218\u5c06\u662f\u4e0d\u6b7b\u4e0d\u4f11\u3002\u201d \u3000\u3000\u80e1\u840d\u3001\u6bb5\u4e5d\uff0c\u90fd\u672a\u5f00\u53e3\uff0c\u4f46\u4e24\u4eba\u5185\u5fc3\u4e4b\u4e2d\uff0c\u5bf9\u95e8\u4e0b\u5f1f\u5b50\u7684\u8868\u73b0\u3002\u4f3c\u662f\u9887\u611f\u6ee1\u610f\u3002 \u3000\u3000\u53ea\u542c\u4e03\u5b9d\u548c\u5c1a\u63a5\u9053\uff1a\u201c\u7b49\u4ed6\u4eec\u5546\u91cf\u5b8c\u4e86\u3001\u548c\u5c1a\u5c31\u73b0\u8eab\u76f8\u89c1\uff0c\u4e24\u4e2a\u5c0f\u5b50\u5bf9\u548c\u5c1a\u5b58\u4e86\u5f88\u5927\u6212\u5fc3\uff0c\u5927\u6709\u8054\u624b\u5408\u51fb\u4e4b\u610f\uff0c\u76f4\u5230\u548c\u5c1a\u8bf4\u51fa\u4f60\u4eec\u4e24\u4f4d\u3002\u4ed6\u4eec\u5bf8\u7b97\u76f8\u4fe1\u4e86\u548c\u5c1a\u2026\u2026\u201d \u3000\u3000\u80e1\u840d\u3001\u6bb5\u4e5d\uff0c\u76f8\u89c6\u4e00\u7b11\uff0c\u9f50\u58f0\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u5927\u5e08\u2026\u2026\u201d \u3000\u3000\u201c\u6765\u7684\u597d\uff01\u6765\u7684\u53ca\u65f6\u2026\u2026\u201d\u6c5f\u67ab\u6253\u65ad\u4e86\u80e1\u840d\u3001\u6bb5\u4e5d\u672a\u5b8c\u4e4b\u97f3\uff0c\u63a5\u9053\uff1a\u201c\u548c\u5c1a\uff0c\u4f60\u8bf4\u4ed6\u4eec\u7684\u6b66\u529f\uff0c\u6bd4\u4ed6\u4eec\u7684\u5e08\u7236\u5982\u4f55\uff1f\u201d \u3000\u3000\u90ed\u5929\u540c\uff0c\u80e1\u5143\u542c\u5f97\u5446\u4e86\u4e00\u5446\uff0c\u5fd6\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u662f\u4ec0\u4e48\u4eba\uff1f\u53e3\u4e2d\u6ca1\u5927\u6ca1\u5c0f\u7684\uff0c\u5f92\u5f1f\u6b66\u529f\uff0c\u600e\u4e48\u80fd\u6bd4\u5e08\u7236\uff1f\u201d \u3000\u3000\u201c\u706b\u5019\u662f\u5dee\u4e00\u70b9\u4e86\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u8bf4\uff1a\u201c\u4f46\u62fc\u8d77\u6765\uff0c\u5374\u662f\u53c8\u731b\u53c8\u72e0\uff0c\u6240\u4ee5\uff0c\u548c\u5c1a\u5c31\u5728\u8def\u4e0a\u4f20\u4e86\u4ed6\u4eec\u4e00\u4e9b\u6b66\u529f\uff0c\u80e1\u5143\u5b66\u4e86\u2018\u71d5\u4e91\u4e09\u5251\u2019\uff0e\u90ed\u5929\u540c\u4e5f\u5b66\u4e86\u2018\u53cd\u624b\u4e00\u5200\u2019\uff0c\u90fd\u662f\u4ed6\u4eec\u672c\u95e8\u7684\u6b66\u529f\u3002\u201d \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u5927\u548c\u5c1a\u5e94\u8be5\u591a\u4f20\u4ed6\u4eec\u4e00\u4e9b\u6b66\u529f\u624d\u662f\u2026\u2026\u201d \u3000\u3000\u201c\u4f20\u4e86\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u7b11\u9053\uff1a\u201c\u548c\u5c1a\u81ea\u4ece\u542c\u4e86\u4f60\u4e00\u5e2d\u9ad8\u8bba\uff0c\u987f\u609f\u524d\u975e\uff0c\u6240\u4ee5\uff0c\u6211\u4f20\u4e86\u4ed6\u4eec\u4e03\u5b9d\u62f3\u2026\u2026\u201d \u3000\u3000\u5bf9\u4e03\u5b9d\u548c\u5c1a\uff0c\u90ed\u5929\u540c\u548c\u80e1\u5143\uff0c\u4f3c\u6709\u6625\u65e0\u6bd4\u5730\u656c\u91cd\uff0c\u8eac\u8eab\u5e94\u9053\uff1a\u201c\u5f1f\u5b50\u4eec\u611a\u7b28\uff0c\u4e03\u5b9d\u62f3\u5c1a\u672a\u80fd\u7ec3\u7684\u7eaf\u719f\u2026\u2026\u201d \u3000\u3000\u201c\u8fd9\u4e0d\u80fd\u602a\u4f60\u4eec\uff0c\u8def\u4e0a\u6025\u8d70\u7d27\u8d76\uff0c\u4e5f\u6ca1\u7ec3\u62f3\u7684\u65f6\u95f4\u3002\u201d \u3000\u3000\u6c5f\u67ab\u770b\u4e86\u80e1\u5143\u3001\u90ed\u5929\u540c\u4e00\u773c\uff0c\u9053\uff1a\u201c\u4e24\u4f4d\uff0c\u5927\u6982\u8981\u51e0\u5929\u624d\u80fd\u628a\u4e03\u5b9d\u62f3\u5b66\u7684\u5e94\u7528\u81ea\u5982\uff1f\u201d \u3000\u3000\u90ed\u5929\u540c\u548c\u80e1\u5143\u90fd\u662f\u5f88\u806a\u660e\u3001\u673a\u8b66\u7684\u4eba\uff0c\u4e24\u4eba\u7ecf\u8fc7\u8fd9\u4e00\u9635\u89c2\u5bdf\uff0c\u53d1\u89c9\u6c5f\u67ab\u7684\u5e74\u7eaa\u867d\u8f7b\uff0c\u4f46\u5374\u662f\u5f88\u53d7\u5c0a\u91cd\uff0c\u987f\u7136\u6536\u655b\u8d77\u8f7b\u89c6\u4e4b\u5fc3\uff0c\u6d4e\u58f0\u5e94\u9053\uff1a\u201c\u4e03\u5b9d\u62f3\u867d\u7136\u53ea\u6709\u4e03\u62db\uff0c\u4f46\u5374\u662f\u5316\u7e41\u4e3a\u7b80\u7684\u7edd\u5b66\uff0c\u5927\u6982\u8981\u5341\u5929\u5de6\u53f3\uff0c\u624d\u80fd\u4e60\u7ec3\u7eaf\u719f\u3002\u201d \u3000\u3000\u201c\u53ea\u6015\u6ca1\u6709\u90a3\u4e48\u591a\u65f6\u95f4\u4e86\uff0c\u4e24\u4f4d\u52a0\u628a\u52b2\uff0c\u4e09\u5929\u628a\u5b83\u5b66\u4f1a\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u76ee\u5149\u8f6c\u6ce8\u5230\u9152\u8089\u548c\u5c1a\u7684\u8eab\u4e0a\u3002\u63a5\u9053\uff1a\u201c\u5927\u5e08\uff0c\u4e5f\u8bf7\u8d39\u70b9\u5fc3\u529b\u3002 \u3000\u3000\u4e09\u5929\u4e4b\u5185\uff0c\u4e00\u5b9a\u8981\u7763\u4fc3\u4ed6\u4eec\u5b66\u7684\u7eaf\u719f\uff0c\u4e03\u5b9d\u62f3\u542b\u76d6\u4e86\u62f3\u52bf\u53d8\u5316\u7684\u7cbe\u8981\uff0c\u4e0d\u4f46\u6bcf\u4e00\u62db\u53ef\u4ee5\u72ec\u7acb\u62d2\u654c\u3002\u5b66\u5230\u719f\u80fd\u751f\u5de7\u65f6\uff0c\u53ef\u4ee5\u56e0\u65f6\u5236\u5b9c\uff0c\u81ea\u521b\u53d8\u5316\uff0c\u662f\u62f3\u6cd5\u4e2d\u96be\u5f97\u7684\u5947\u5b66\u3002\u201d \u3000\u3000\u80e1\u840d\u3001\u6bb5\u4e5d\u3001\u8001\u53eb\u5316\uff0c\u90fd\u65e9\u628a\u4e03\u5b9d\u62f3\u5b66\u4f1a\u4e86\uff0c\u4f46\u4ed6\u4eec\u90fd\u4e0d\u77e5\u9053\u8fd9\u5957\u62f3\u6cd5\uff0c\u7adf\u662f\u5982\u6b64\u7684\u795e\u5947\u3002 \u3000\u3000\u4f46\u89c1\u4e03\u5b9d\u548c\u5c1a\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u9ad8\u660e\u554a\uff01\u8001\u5f1f\uff0c\u5c31\u662f\u624d\u6167\u7edd\u4e16\u7684\u4eba\uff0c\u4e0d\u7ec3\u4e0a\u4e09\u4e94\u5e74\u4e03\u5b9d\u62f3\uff0c\u4e5f\u4f53\u4f1a\u4e0d\u51fa\u5b83\u7684\u795e\u5999\u4e4b\u5904\uff0c\u4f60\u8001\u5f1f\u600e\u4f1a\u770b\u5f97\u51fa\u6765\u3002\u800c\u4e14\uff0c\u4e00\u8bed\u9053\u7834\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u7b11\u9053\uff1a\u201c\u6211\u4f1a\u731c\uff0c\u73b0\u5728\u3002\u6211\u4eec\u5148\u6765\u7814\u7a76\u4e00\u4e0b\u6d1b\u9633\u5c45\u4e2d\u7684\u4e8b\u60c5\uff0c\u2026\u2026\u201d\u76ee\u6ce8\u4e03\u5b9d\u548c\u5c1a\uff0c\u9053\uff1a\u201c\u8fd9\u4e00\u6b21\uff0c\u8981\u52b3\u4f60\u548c\u5c1a\u4e86\uff0c\u5e26\u7740\u80e1\u8001\u5f1f\u548c\u9b54\u773c\u9093\u98de\u6253\u6b21\u4ea4\u9053\u2026\u2026\u201d \u3000\u3000\u201c\u4ec0\u4e48\uff1f\u9b54\u773c\u5728\u6d1b\u9633\u5c45\u4e2d\u2026\u2026\u201d \u3000\u3000\u201c\u9152\u8089\u548c\u5c1a\uff0c\u8fd9\u4e00\u6b21\u6709\u4f60\u597d\u73a9\u7684\u4e86\u2026\u2026\u201d\u8001\u53eb\u5316\u7b11\u9053\uff1a\u201c\u6d1b\u9633\u5c45\u7684\u603b\u7ba1\u5927\u4eba\uff0c\u5c31\u662f\u9b54\u773c\u9093\u98de\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u6211\u548c\u5c1a\u662f\u6bd4\u846b\u82a6\u753b\u74e2\uff0c\u6c5f\u8001\u5f1f\uff0c\u4f60\u8981\u5148\u9876\u793a\u4e00\u6761\u660e\u8def\u3002\u201d \u3000\u3000\u6c5f\u67ab\u63a5\u9053\uff1a\u201c\u6d1b\u9633\u5c45\u7ec4\u5408\u7684\u590d\u6742\uff0c\u8fde\u603b\u7ba1\u9093\u98de\uff0c\u4e5f\u4e00\u6837\u5f04\u4e0d\u6e05\u695a\uff0c\u56db\u5b63\u82b1\u5a62\u72ec\u6811\u4e00\u5e1c\u3002 \u3000\u3000\u5c24\u5176\u662f\u51ac\u5a62\u6885\u82b1\uff0c\u5b64\u50b2\u81ea\u8d1f\uff0c\u8868\u9762\u4e0a\u5bf9\u6211\u656c\u91cd\uff0c\u4f46\u4e8b\u5b9e\u4e0a\uff0c\u6839\u672c\u5c31\u6ca1\u6709\u628a\u6211\u653e\u5728\u773c\u4e2d\uff0c\u2026\u2026\u201d \u3000\u3000\u5f20\u56db\u59d1\u683c\u683c\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u597d\u554a\uff01\u8fd9\u624d\u53eb\u5403\u762a\u5440\uff01\u4f60\u5728\u6d1b\u9633\u5c45\u6df7\u4e86\u51e0\u4e2a\u6708\uff0c\u8fde\u4e00\u4e2a\u82b1\u5a62\u4e5f\u6ca1\u642d\u4e0a\u554a\uff1f\u201d \u3000\u3000\u201c\u5927\u59d0\u7ed9\u6211\u8fd9\u526f\u4eba\u76ae\u9762\u5177\uff0c\u4f7f\u5c0f\u5f1f\u8fd9\u526f\u5c0a\u5bb9\uff0c\u770b\u4e0a\u53bb\u4e00\u8138\u7684\u51b7\u9177\u3001\u5be1\u7edd\uff0c\u5973\u5b69\u5b50\u4eec\u4e00\u4e2a\u4e2a\u907f\u4e4b\u552f\u6050\u4e0d\u53ca\uff0c\u5c0f\u5f1f\u8f9b\u82e6\u4e86\u513f\u4e2a\u6708\uff0c\u53ea\u548c\u6625\u82b1\u56db\u5a62\u5efa\u7acb\u8d77\u4e00\u4e9b\u4ea4\u60c5\uff0c\u6240\u4ee5\uff0c\u73b0\u5728\uff0c\u8981\u91cd\u65b0\u5e03\u7f6e\u3001\u63a2\u67e5\u2026\u2026\u201d \u3000\u3000\u8001\u53eb\u5316\u63a5\u9053\uff1a\u201c\u600e\u4e48\u4e00\u4e2a\u5e03\u7f6e\u6cd5\uff1f\u201d \u3000\u3000\u201c\u5927\u5e08\u5e26\u7740\u80e1\u5143\uff0c\u80e1\u840d\u548c\u6bb5\u4e5d\u6613\u5bb9\u66f4\u540d\uff0c\u7531\u5144\u5f1f\u5f15\u5165\u6d1b\u9633\u5c45\u5f20\u56db\u59d1\u9053\uff1a\u201c\u6211\u5462\uff1f\u201d \u3000\u3000\u201c\u5e26\u7740\u541f\u96ea\u3001\u541f\u971c\u5750\u9547\u98ce\u96ea\u5c0f\u9986\uff0c\u4e5f\u8bb8\uff0c\u4f60\u90a3\u4f4d\u95fa\u4e2d\u597d\u53cb\uff0c\u8fd1\u65e5\u5185\u4f1a\u6765\u63a2\u671b\u4e24\u4f4d\u5973\u513f\u2026\u2026\u201d \u3000\u3000\u5f20\u56db\u59d1\u9053\uff1a\u201c\u6211\u660e\u767d\uff0c\u987a\u4fbf\u7763\u4fc3\u5979\u4eec\u5b66\u4e60\u5feb\u901f\u6613\u5bb9\u624b\u6cd5\u2026\u2026\u201d\u201c\u6211\u8001\u53eb\u5316\u5462\uff1f\u201d \u3000\u3000\u201c\u54b1\u4eec\u4e00\u8d77\u53bb\u8bf4\u670d\u79e6\u516b\u516c\uff0c\u6211\u8981\u89e3\u4e86\u4ed6\u7684\u53d7\u4f24\u7a74\u9053\uff0c\u518d\u62d6\u4e0b\u53bb\u3002\u4ed6\u5c31\u8981\u7ec8\u8eab\u6b8b\u5e9f\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u4ee5\u540e\u5462\uff1f\u8001\u53eb\u5316\u662f\u5426\u8981\u7559\u5728\u79e6\u516b\u516c\u90a3\u91cc\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u5e2e\u52a9\u4ed6\u91cd\u6574\u5173\u6d1b\u9053\u4e0a\u7684\u6c5f\u6e56\u5b9e\u529b\uff0c\u770b\u770b\u6709\u591a\u5c11\u53ef\u7528\u7684\u4eba\uff1f\u6536\u96c6\u6b66\u6797\u9053\u4e0a\u7684\u4f20\u8a00\u3001\u6d88\u606f\uff0c\u5f53\u7136\uff0c\u4e5f\u8981\u76f8\u673a\u4e89\u53d6\u4eba\u624d\uff0c\u968f\u65f6\u548c\u5927\u59d0\u8054\u7edc\uff0c\u975e\u7edd\u5bf9\u53ef\u4ee5\u4fe1\u4efb\u7684\u4eba\uff0c\u4e0d\u8981\u628a\u4ed6\u5f15\u4eba\u98ce\u96ea\u5c0f\u9986\u3002\u201d \u3000\u3000\u201c\u8001\u53eb\u5316\u660e\u767d\u4e86\uff0c\u6211\u4f1a\u5168\u529b\u4ee5\u8d74\u3002\u201d \u3000\u3000\u90ed\u5929\u540c\u770b\u770b\u6bb5\u4e5d\uff0c\u53c8\u770b\u770b\u4e03\u5b9d\u548c\u5c1a\uff0c\u624d\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u6c5f\u5144\uff0c\u4e0d\u77e5\u9053\u90ed\u67d0\u80fd\u4e0d\u80fd\u2026\u2026\u201d \u3000\u3000\u94b1\u7f3a\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u8ddf\u7740\u8001\u53eb\u5316\u5427\uff01\u201d \u3000\u3000\u201c\u4e0d\u884c\uff0c\u90ed\u5144\u8981\u62c5\u5f53\u4e00\u4e2a\u975e\u5e38\u91cd\u8981\u7684\u4e8b\u60c5\u2026\u2026\u201d\u6c5f\u67ab\u76ee\u5149\u8f6c\u6ce8\u5230\u5f20\u56db\u59d1\u7684\u8138\u4e0a\uff0c\u63a5\u9053\uff1a \u3000\u3000\u201c\u5927\u59d0\u8981\u518d\u4f5c\u4e00\u526f\u97e9\u9738\u7684\u9762\u5177\u3002\u201d \u3000\u3000\u201c\u8981\u4ed6\u626e\u6210\u97e9\u526f\u603b\u7ba1\u2026\u2026\u201d\u5f20\u56db\u59d1\u5947\u9053\uff1a\u201c\u4f60\u5462\uff1f\u201d \u3000\u3000\u201c\u6211\u8981\u817e\u51fa\u8eab\u5b50\uff0c\u53bb\u89c1\u79e6\u516b\u516c\uff0c\u4e5f\u8981\u9614\u6c14\u4e00\u756a\uff0c\u5230\u6d1b\u9633\u5c45\u4f1a\u4f1a\u51ac\u5a62\u6885\u82b1\uff0c\u4e0d\u8fc7\uff0c\u6700\u91cd\u8981\u7684\u662f\u53bb\u67e5\u67e5\u9752\u51e4\u7684\u5e95\u5b50\uff0c\u8fd9\u4e2b\u5934\u5728\u6d1b\u9633\u5c45\u8eab\u4efd\u72ec\u7279\uff0c\u8fde\u9093\u98de\u4e5f\u4e0d\u77e5\u9053\u5979\u7684\u6765\u8def\u2026\u2026\u201d \u3000\u3000\u201c\u771f\u628a\u6211\u542c\u7cca\u6d82\u4e86\u2026\u2026\u201d\u5f20\u56db\u59d1\u8bf4\uff1a\u201c\u6d1b\u9633\u5c45\u7a76\u7adf\u6709\u6ca1\u6709\u4e00\u4e2a\u771f\u6b63\u5f53\u5bb6\u4f5c\u4e3b\u7684\u4eba\uff1f\u201d \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u4f55\u6b62\u662f\u5f20\u56db\u59d1\uff0c\u8fde\u80e1\u840d\u3001\u6bb5\u4e5d\u3001\u8001\u53eb\u5316\u8fd9\u7b49\u8001\u6c5f\u6e56\uff0c\u4e5f\u542c\u5f97\u5934\u8111\u53d1\u80c0\uff0c\u90ed\u5929\u540c\u548c\u80e1\u5143\u3001\u541f\u96ea\u3001\u541f\u971c\uff0c\u66f4\u662f\u542c\u5f97\u5168\u795e\u8d2f\u6ce8\uff0c\u751f\u6050\u542c\u6f0f\u4e86\u4e00\u53e5\u6d3b\uff0c\u4e0b\u9762\u5c31\u542c\u4e0d\u61c2\u4e86\u3002 \u3000\u3000\u6c5f\u67ab\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5c31\u8868\u9762\u4e0a\u770b\uff0c\u4e00\u5207\u7531\u603b\u7ba1\u9093\u98de\u4f5c\u4e3b\u3002 \u3000\u3000\u5144\u5f1f\u4e5f\u4e00\u76f4\u8fd9\u4e48\u8ba4\u4e3a\uff0c\u4eca\u5929\u4f60\u4eec\u653e\u624b\u4e00\u540c\uff0c\u624d\u53c8\u63ed\u5f00\u4e86\u4e00\u5c42\u5916\u8863\uff0c\u79cb\u82b1\u53cc\u5a62\u7684\u6b66\u529f\u4e4b\u9ad8\uff0c\u5927\u51fa\u4e86\u6211\u610f\u6599\u4e4b\u5916\uff0c\u7ba1\u7406\u8d4c\u573a\u7684\u526f\u603b\u7ba1\u7389\u9f99\uff0c\u4e3a\u4f60\u4eec\u5e26\u53bb\u7684\u4e00\u6279\u8d22\u5b9d\u548c\u603b\u7ba1\u8d77\u4e86\u51b2\u7a81\uff0c\u7a81\u7136\u81ea\u66b4\u8eab\u4efd\uff0c\u597d\u50cf\u4ed6\u624d\u662f\u771f\u6b63\u4e3b\u6301\u6d1b\u9633\u5c45\u7684\u9996\u8111\uff0c\u773c\u770b\u9093\u98de\u5c31\u8981\u5c48\u670d\u4ed6\u53d6\u51fa\u7684\u7af9\u7b26\u4ee4\u4e0b\uff0c\u5144\u5f1f\u53ea\u597d\u51fa\u624b\uff0c\u8bf1\u4f7f\u9093\u98de\u6740\u4e86\u738b\u9f99\uff0c\u6bc1\u53bb\u7af9\u7b26\u4ee4\u624d\u77e5\u9053\u6d1b\u9633\u5c45\u4e2d\u662f\u90a3\u4e48\u590d\u6742\uff0c\u4f46\u9752\u51e4\u548c\u6885\u82b1\uff0c\u4f3c\u662f\u4e24\u4e2a\u91cd\u8981\u7684\u4eba\u7269\u2026\u2026\u201d \u3000\u3000\u201c\u679c\u7136\u662f\u70df\u5c01\u96fe\u9501\uff0c\u4e0d\u5230\u5c3d\u5934\uff0c\u96be\u89c1\u771f\u76f8\uff0c\u8bf4\u5427\uff01\u8981\u5927\u59d0\u600e\u4e48\u652f\u63f4\u4f60\uff1f\u201d \u3000\u3000\u201c\u5148\u9999\u90ed\u5929\u540c\u505a\u5267\u9762\u5177\uff0c\u73b0\u5728\uff0c\u6211\u548c\u94b1\u8001\u53bb\u89c1\u79e6\u516b\u516c\u3002\u201d \u3000\u3000\u79e6\u516b\u516c\u5728\u4e00\u5ea7\u96c5\u81f4\u7684\u5c0f\u5385\u4e2d\u63a5\u89c1\u4e86\u4e24\u4eba\u3002 \u3000\u3000\u7834\u4f8b\u89c1\u5ba2\uff0c\u6545\u7136\u662f\u56e0\u4e3a\u6808\u7f3a\u5728\u6c5f\u6e56\u4e0a\u7684\u540d\u6c14\u5f88\u5927\uff0c\u4f46\u66f4\u91cd\u8981\u7684\u662f\u8001\u53eb\u5316\u5728\u62dc\u5e16\u4e0a\u8bf4\u660e\u5e26\u4e86\u4e00\u4f4d\u795e\u533b\u540c\u6765\uff0c\u70b9\u5230\u4e3a\u6b62\u3002 \u3000\u3000\u8001\u53eb\u5316\u5e76\u6765\u8bf4\u660e\u5e26\u4e2a\u795e\u533b\u6765\u5e72\u4ec0\u4e48\uff1f\u795e\u533b\u5c31\u662f\u6c5f\u67ab\uff0c\u4ed6\u672a\u5e26\u9762\u5177\uff0c\u4f46\u5374\u52a0\u4e86\u4e09\u7f15\u957f\u9aef\uff0c\u84dd\u888d\u4fee\u8eaf\uff0c\u770b\u4e0a\u53bb\u771f\u8fd8\u6709\u4e00\u6d3e\u4ed9\u98ce\u9053\u9aa8\u7684\u6837\u5b50\u3002 \u3000\u3000\u79e6\u516b\u516c\u9526\u888d\u798f\u5c65\uff0c\u8863\u6625\u5149\u9c9c\uff0c\u4f46\u5374\u63a9\u4e0d\u4f4f\u7709\u5b57\u95f4\u7684\u6101\u82e6\u548c\u6194\u60b4\u795e\u8272\u3002 \u3000\u3000\u94b1\u7f3a\u559d\u4e86\u4e00\u53e3\u8336\uff0c\u671b\u671b\u5b88\u5728\u95e8\u53e3\u7684\u4e24\u4e2a\u52b2\u88c5\u5c11\u5e74\uff0c\u7b11\u9053\uff1a\u201c\u8981\u4e0d\u8981\u4ed6\u4eec\u4e0b\u53bb\u2026\u2026\u201d \u3000\u3000\u79e6\u516b\u516c\u63a5\u9053\uff1a\u201c\u597d\uff01\u4f60\u4eec\u4e24\u4e2a\u53bb\u5b88\u4f4f\u5ead\u9662\u524d\u540e\uff0c\u4e0d\u51c6\u95f2\u4eba\u63a5\u8fd1\u3002\u201d \u3000\u3000\u4e24\u4e2a\u52b2\u88c5\u5c11\u5e74\u4e00\u8eac\u8eab\uff0c\u9000\u4e86\u51fa\u53bb\uff0c\u79e6\u516b\u516c\u624d\u8f7b\u8f7b\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u94b1\u5144\uff0c\u6709\u8bdd\u53ef\u4ee5\u8bf4\u4e86\uff1f\u201d \u3000\u3000\u201c\u6211\u770b\u4f60\u795e\u8272\u4e0d\u5bf9\u2026\u2026\u201d\u8001\u53eb\u5316\u8bf4\uff1a\u201c\u662f\u4e0d\u662f\u75c5\u52bf\u8f6c\u91cd\u4e86\uff1f\u201d \u3000\u3000\u79e6\u516b\u516c\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u957f\u5b89\u57ce\u4e2d\u4e94\u5927\u540d\u533b\u90fd\u770b\u8fc7\u4e86\uff0c\u4e5f\u90fd\u7559\u4e0b\u4e86\u836f\u65b9\u5b50\uff0c\u4f46\u90fd\u8bf4\u4e0d\u51fa\u75c5\u60c5\u539f\u56e0\uff0c\u8fd9\u51e0\u5929\uff0c\u5144\u5f1f\u5df2\u89c9\u7740\u4e0d\u5bf9\uff0c\u4f3c\u4e4e\u6709\u4e00\u5904\u7ecf\u8109\u67af\u840e\uff0c\u5df2\u65e0\u6cd5\u8fd0\u6c14\u884c\u52a8\uff0c\u770b\u6837\u5b50\uff0c\u5144\u5f1f\u8fd9\u6761\u547d\uff0c\u6050\u6015\u662f\u62d6\u4e0d\u4e45\u4e86\u3002\u201d \u3000\u3000\u94b1\u7f3a\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u79e6\u5144\u4e5f\u4e0d\u7528\u96be\u8fc7\uff0c\u8ba9\u8fd9\u4f4d\u9ad8\u5144\u5f1f\u7ed9\u4f60\u77a7\u77a7\uff0c\u4ed6\u867d\u7136\u6ca1\u6709\u6302\u724c\uff0c\u4f46\u5374\u662f\u4e00\u4f4d\u533b\u672f\u7cbe\u6e5b\u7684\u9ad8\u624b\u3002\u201d \u3000\u3000\u6c5f\u67ab\u65e9\u5df2\u548c\u8001\u53eb\u5316\u8ba1\u8bae\u597d\u4e86\uff0c\u4ed6\u8bf4\u51fa\u75c5\u6e90\u4e4b\u540e\uff0c\u7531\u8001\u53eb\u5316\u51fa\u624b\u7597\u51b6\u3002\u5f53\u7136\uff0c\u6c5f\u67ab\u65e9\u5df2\u628a\u8fd9\u79cd\u72ec\u95e8\u7684\u89e3\u7a74\u624b\u6cd5\uff0c\u4f20\u7ed9\u4e86\u94b1\u7f3a\u3002 \u3000\u3000\u88c5\u7740\u4e8c\u526f\u82e5\u65e0\u5176\u4e8b\u7684\u6837\u5b50\uff0c\u6c5f\u67ab\u66ff\u79e6\u516b\u516c\u8bca\u4e86\u8109\u8c61\uff0c\u53f9\u606f\u4e00\u58f0\u3002\u9053\uff1a\u201c\u79e6\u8001\u81ea\u5df1\u8bf4\u7684\u4e0d\u9519\uff0c\u4f60\u6709\u4e00\u5904\u7ecf\u8109\u4e0d\u901a\uff0c\u518d\u4e0d\u6551\u6cbb\uff0c\u5927\u6982\u62d6\u4e0d\u8fc7\u534a\u4e2a\u6708\uff0c\u5c31\u4f24\u8109\u67af\u6b7b\uff0c\u4e0d\u4f1a\u8981\u4f60\u7684\u547d\uff0c\u4f46\u4f60\u5374\u7ec8\u8eab\u6b8b\u5e9f\u3002\u8fd9\u5df2\u7ecf\u4e0d\u662f\u836f\u7269\u53ef\u4ee5\u6551\u6cbb\uff0c\u5728\u4e0b\u4e0d\u4f1a\u6b66\u529f\uff0c\u5e2e\u4e0d\u4e0a\u5fd9\uff0c\u5148\u884c\u544a\u8f9e\u4e86\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u8d70\u5c31\u8d70\uff0c\u8d77\u8eab\u5c31\u5411\u5916\u884c\u53bb\u3002 \u3000\u3000\u79e6\u516b\u516c\u6025\u9053\uff1a\u201c\u5927\u592b\u4e00\u5f00\u53e3\u8bf4\u51fa\u75c5\u6e90\uff0c\u8db3\u89c1\u9ad8\u660e\uff0c\u5fc5\u6709\u826f\u65b9\u6551\u6cbb\uff0c\u8bf7\u7559\u6b65\u2026\u2026\u201d \u3000\u3000\u4e00\u4f38\u624b\u62e6\u4f4f\u4e86\u79e6\u516b\u516c\uff0c\u94b1\u7f3a\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u7559\u4e0d\u4f4f\u7684\uff0c\u4ed6\u6ca1\u6709\u628a\u63e1\u6cbb\u597d\u4eba\u5bb6\u7684\u75c5\u65f6\uff0c\u8fde\u53e3\u6c34\u90fd\u4e0d\u80af\u559d\uff0c\u4ed6\u8d77\u8eab\u800c\u53bb\uff0c\u662f\u65e0\u6cd5\u4e0b\u836f\u4e86\u3002\u201d \u3000\u3000\u79e6\u516b\u516c\u5927\u611f\u637b\u7684\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u8001\u592b\u662f\u6b7b\u5b9a\u4e86\u3002\u201d \u3000\u3000\u201c\u4e0d\u4f1a\u6b7b\u554a\uff01\u6700\u591a\u662f\u843d\u4e2a\u6b8b\u5e9f\uff0c\u5931\u53bb\u6b66\u529f\u2026\u2026\u201d\u8001\u53eb\u5316\u9053\uff1a\u201c\u4f60\u8fd8\u53ef\u4ee5\u5403\u9999\u7684\u3001\u559d\u8fa3\u7684\uff0c\u6d3b\u7684\u60a0\u60a0\u95f2\u95f2\uff0c\u8fc7\u4f60\u7684\u5feb\u6d3b\u65e5\u5b50\u3002\u201d \u3000\u3000\u201c\u5931\u53bb\u6b66\u529f\uff0c\u7ec8\u8eab\u6b8b\u5e9f\uff0c\u90a3\u8fd8\u4e0d\u5982\u6b7b\u6389\u7684\u597d\u2026\u2026\u201d\u79e6\u516b\u516c\u671b\u770b\u94b1\u7f3a\uff0c\u51b7\u51b7\u4ed6\u8bf4\uff1a\u201c\u4f60\u662f\u5728\u5e78\u707e\u4e50\u7978\uff1f\u201d \u3000\u3000\u201c\u79e6\u5144\u4e0d\u8981\u8bef\u4f1a\u2026\u2026\u201d\u8001\u53eb\u5316\u8bf4\uff1a\u201c\u4ed6\u4f1a\u533b\u75c5\uff0c\u80fd\u7740\u624b\u56de\u6625\uff0c\u4e5f\u4f1a\u6cbb\u4e00\u822c\u7684\u5200\u3001\u5251\u5916\u4f24\uff0c\u4f46\u4ed6\u5374\u4e0d\u4f1a\u6cbb\u7597\u8fd9\u7b49\u7ecf\u7a74\u5185\u4f24\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4e0d\u662f\u767d\u8bf4\u4e48\uff1f\u2026\u2026\u201d\u79e6\u516b\u516c\u9053\uff1a\u201c\u4e0d\u80fd\u6cbb\u7684\u75c5\uff0c\u662f\u7edd\u75c7\uff0c\u4e0d\u80fd\u533b\u7684\u4f24\uff0c\u662f\u2026\u2026\u201d \u3000\u3000\u201c\u4ed6\u4e0d\u80fd\u533b\u3002\u5e76\u4e0d\u662f\u8bf4\u5929\u4e0b\u5c31\u65e0\u4eba\u80fd\u533b\u2026\u2026\u201d \u3000\u3000\u6253\u65ad\u4e86\u8001\u53eb\u5316\u7684\u8bdd\uff0c\u79e6\u516b\u516c\u63a5\u9053\uff1a\u201c\u8c01\u80fd\u533b\u5462\uff1f\u201d \u3000\u3000\u201c\u6211\u5440\uff01\u2026\u2026\u201d\u8001\u53eb\u5316\u9053\uff1a\u201c\u65e9\u77e5\u4f60\u662f\u53d7\u4eba\u6697\u7b97\u53d7\u4e86\u5185\u4f24\uff0c\u4e5f\u4e0d\u7528\u5343\u91cc\u8fe2\u8fe2\u53bb\u628a\u4ed6\u8bf7\u6765\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u4f60\u80fd\u533b\u2026\u2026\u201d\u79e6\u516b\u516c\u4e00\u8138\u6000\u7591\u4ed6\u8bf4\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\u4f24\u6211\u7684\u4e0d\u662f\u4e00\u822c\u7684\u70b9\u7a74\u3001\u622a\u8109\u624b\u6cd5\uff0c\u5c11\u6797\u3001\u6b66\u5f53\u7684\u4eba\u90fd\u65e0\u6cd5\u4e0b\u624b\uff0c\u4f60\u2026\u2026\u201d \u3000\u3000\u4ed6\u4f3c\u662f\u81ea\u77e5\u5931\u8a00\uff0c\u6025\u6025\u4f4f\u53e3\u3002 \u3000\u3000\u94b1\u7f3a\u5fc3\u4e2d\u5403\u4e86\u4e00\u60ca\uff0c\u6697\u9053\uff1a\u5e78\u597d\u6211\u8fd8\u6ca1\u6709\u5927\u5439\u6cd5\u87ba\uff0c\u8bf4\u8fd9\u9707\u8109\u624b\u51fa\u81ea\u6b66\u5f53\uff0c\u5426\u5219\uff0c\u53ea\u6015\u8981\u5f53\u573a\u51fa\u4e11\u4e86\u3002 \u3000\u3000\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u53e3\u4e2d\u8bf4\u9053\uff1a\u201c\u8fd9\u79cd\u4e8b\u4e0d\u80fd\u80e1\u5439\uff0c\u7075\u4e0d\u7075\u4e00\u8bd5\u4fbf\u77e5\uff1f\u201d \u3000\u3000\u201c\u94b1\u5144\u3002\u6ca1\u6709\u628a\u63e1\uff0c\u6700\u597d\u522b\u8bd5\uff0c\u8fd9\u4e9b\u65f6\u65e5\u5185\uff0c\u5df2\u6709\u4e09\u4f4d\u9ad8\u624b\u8bd5\u8fc7\u2026\u2026\u201d\u79e6\u516b\u516c\u9053\uff1a\u201c\u4f60\u7d2f\u5f97\u4e00\u8eab\u5927\u6c57\uff0c\u6211\u53c8\u591a\u53d7\u4e00\u6b21\u6d3b\u7f6a\u3002\u201d \u3000\u3000\u8a00\u4e0b\u4e4b\u610f\uff0c\u5b8c\u5168\u4e0d\u76f8\u4fe1\u94b1\u7f3a\u4f1a\u6709\u89e3\u53bb\u72ec\u95e8\u70b9\u7a74\u624b\u6cd5\u7684\u80fd\u529b\u3002 \u3000\u3000\u8001\u53eb\u5316\u5fc3\u4e2d\u660e\u767d\uff0c\u5148\u6d3d\u597d\u79e6\u516b\u516c\u7684\u4f24\u52bf\uff0c\u624d\u80fd\u8d62\u5f97\u79e6\u516b\u516c\u7684\u656c\u91cd\u3001\u4fe1\u4efb\u3002 \u3000\u3000\u5f53\u4e0b\u54c8\u54c8\u4e00\u7b11\u3002\u9053\uff1a\u201c\u8001\u53eb\u5316\u767d\u767d\u84c4\u4f60\u7597\u4f24\uff0c\u5206\u6587\u4e0d\u6536\uff0c\u6211\u5982\u6ca1\u6709\u628a\u63e1\uff0c\u5927\u8001\u8fdc\u5730\u8dd1\u6765\u5e72\u4ec0\u4e48\uff1f\u4e5f\u7f62\uff0c\u4e3a\u4e86\u4f7f\u4f60\u76f8\u4fe1\uff0c\u8001\u53eb\u5316\u5c31\u8ddf\u4f60\u8d4c\u4e00\u4e0b\u5427\uff1f\u201d \u3000\u3000\u79e6\u516b\u516c\u8bc1\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u8d4c\u4e00\u4e0b\uff0c\u8d4c\u4ec0\u4e48\u5462\uff1f\u201d \u3000\u3000\u201c\u8001\u53eb\u5316\u5982\u679c\u4e00\u4e0b\u5b50\u533b\u597d\u4f60\u7684\u4f24\uff0c\u800c\u4e14\u662f\u624b\u5230\u4f24\u597d\uff0c\u9a6c\u4e0a\u8981\u4f60\u6062\u590d\u5982\u5e38\uff0c\u9c9c\u8e66\u6d3b\u8df3\u7684\uff0c\u4f60\u51c6\u5907\u5982\u4f55\u8c22\u6211\u8001\u53eb\u5316\u5b50\uff1f\u201d \u3000\u3000\u201c\u4f60\u771f\u6709\u90a3\u4e48\u4e00\u4e2a\u795e\u6cd5\uff0c\u5144\u5f1f\u5144\u5949\u4e0a\u4e00\u4e07\u4e24\u94f6\u5b50\u916c\u8c22\u2026\u2026\u201d \u3000\u3000\u79e6\u516b\u516c\u9053\uff1a\u201c\u5982\u662f\u94b1\u5144\u4e0d\u6ee1\u610f\uff0c\u5144\u5f1f\u53ef\u4ee5\u518d\u52a0\u4e00\u4e07\u4e24\u3002\u95ee\u9898\u662f\u6709\u8d62\u5c31\u8981\u6709\u8f93\u3001\u94b1\u5144\u51c6\u5907\u2026\u2026\u201d \u3000\u3000\u201c\u8001\u53eb\u5316\u5982\u662f\u62ff\u4eba\u5934\u4f5c\u8d4c\u6ce8\uff0c\u4f60\u79e6\u5144\u5927\u6982\u4f1a\u8bf4\u8001\u53eb\u5316\u8bb9\u8bc8\u4f60\uff0c\u8fd9\u4e48\u529e\u5427\uff1f\u5982\u662f\u6211\u533b\u4e0d\u597d\u4f60\u7684\u4f24\u52bf\uff0c\u8001\u53eb\u5316\u5c31\u722c\u7740\u51fa\u4f60\u7684\u79e6\u5e9c\u5927\u95e8\u2026\u2026\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u592a\u4e25\u91cd\u4e86\u5427\uff01\u94b1\u5144\uff0c\u6211\u60f3\u2026\u2026\u201d \u3000\u3000\u201c\u5c31\u8fd9\u4e48\u51b3\u5b9a\u4e86\uff0c\u544a\u8bc9\u6211\u4f60\u4f24\u5728\u90a3\u91cc\uff1f\u201d \u3000\u3000\u79e6\u516b\u516c\u9053\uff1a\u201c\u73b0\u5728\u5c31\u8981\u52a8\u624b\u5417\uff1f\u53ef\u8981\u51c6\u5907\u4e9b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u7597\u6cbb\u5185\u4f24\uff0c\u5168\u51ed\u4e00\u53cc\u624b\u52b2\uff0c\u4ec0\u4e48\u4e5f\u4e0d\u7528\u51c6\u5907\uff0c\u53ea\u8981\u628a\u53d7\u4f24\u7684\u5730\u65b9\u544a\u8bc9\u6211\u5c31\u884c\u4e86\uff01\u201d \u3000\u3000\u4f24\u5728\u90a3\u91cc\uff0c\u5982\u4f55\u6551\u6cbb\uff0c\u6c5f\u67ab\u65e9\u5df2\u6e05\u695a\u7684\u544a\u8bc9\u4e86\u94b1\u7f3a\uff0c\u79e6\u516b\u516c\u4e0d\u8bf4\u660e\uff0c\u4ed6\u4e5f\u4f1a\u51fa\u624b\u63a8\u7a74\uff0c\u4f46\u79e6\u516b\u516c\u5374\u8bf4\u7684\u5341\u5206\u4ed4\u7ec6\u3002 \u3000\u3000\u8001\u53eb\u5316\u6697\u4e2d\u8fd0\u6c14\uff0c\u7a81\u7136\u5410\u6c14\u51fa\u58f0\uff0c\u4e00\u53e3\u6c14\u8fde\u70b9\u4e86\u79e6\u516b\u516c\u4e94\u5904\u7a74\u9053\u3002 \u3000\u3000\u79e6\u516b\u516c\u968f\u7740\u90a3\u843d\u4e0b\u7684\u638c\u6307\uff0c\u5931\u58f0\u5927\u53eb\u3002 \u3000\u3000\u4f46\u89c1\u4eba\u5f71\u95ea\u52a8\uff0c\u56db\u4e2a\u52b2\u88c5\u5e74\u8f7b\u4eba\u95ea\u7535\u822c\u6025\u5954\u800c\u6765\uff0c\u51b2\u5165\u5c0f\u5385\u3002 \u3000\u3000\u76ee\u7779\u8001\u53eb\u5316\u6ee1\u5934\u5927\u6c57\u5728\u5e2e\u79e6\u516b\u516c\u63a8\u5bab\u8fc7\u7a74\uff0c\u56db\u4e2a\u4eba\u5446\u4e86\u4e00\u5446\uff0c\u8eac\u8eab\u4e00\u793c\uff0e\u6084\u7136\u9000\u5230\u95e8\u5916\u3002 \u3000\u3000\u4f46\u4ed6\u4eec\u5e76\u6765\u79bb\u53bb\uff0c\u56db\u4e2a\u4eba\u516b\u53ea\u773c\u775b\u671b\u7740\u94b1\u7f3a\u548c\u79e6\u516b\u516c\uff0c\u4f3c\u662f\u5728\u7b49\u5f85\u79e6\u516b\u516c\u7684\u53cd\u5e94\u3002 \u3000\u3000\u94b1\u7f3a\u505c\u4e0b\u624b\uff0c\u79e6\u516b\u516c\u957f\u957f\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u94b1\u5144\uff0c\u4f60\u662f\u771f\u4eba\u4e0d\u9732\u76f8\u554a\uff01\u2026\u2026\u201d \u3000\u3000\u7a81\u7136\u611f\u89c9\u5230\u8179\u4e2d\u4e00\u9635\u602a\u53eb\uff0c\u7acb\u523b\u4e00\u8dc3\u800c\u8d77\uff0c\u5411\u5916\u5954\u53bb\u3002 \u3000\u3000\u56db\u4e2a\u5b88\u5019\u5728\u95e8\u53e3\u7684\u5e74\u8f7b\u4eba\uff0c\u5403\u4e86\u4e00\u60ca\uff0c\u7d27\u8ffd\u8eab\u540e\u3002 \u3000\u3000\u4f46\u95fb\u79e6\u516b\u516c\u9a82\u9053\uff1a\u201c\u4f60\u4eec\u56db\u4e2a\u738b\u516b\u7f94\u5b50\uff0c\u8ddf\u7740\u6211\u5e72\u4ec0\u4e48\uff1f\u83ab\u4e0d\u662f\u8fd8\u8981\u5728\u8305\u623f\u91cc\u7ad9\u5c97\u4e0d\u6210\u3002\u201d \u3000\u3000\u56db\u4e2a\u4eba\u695e\u4e86\u4e00\u4e0b\uff0c\u79e6\u516b\u516c\u5df2\u6025\u6025\u5954\u5165\u8305\u623f\u4e2d\u3002 \u3000\u3000\u8fd9\u4e00\u5207\u53cd\u5e94\uff0c\u90fd\u5728\u6c5f\u67ab\u7684\u9884\u8a00\u4e4b\u4e2d\uff0c\u8001\u53eb\u5316\u5fc3\u4e2d\u7b03\u5b9a\u4e86\uff0c\u81ea\u5df1\u63d0\u58f6\uff0c\u5012\u4e86\u4e00\u676f\u8336\uff0c\u6162\u6162\u7684\u54c1\u7740\u3002 \u3000\u3000\u597d\u4e00\u9635\u5de5\u592b\uff0c\u79e6\u516b\u516c\u56de\u5230\u5c0f\u5385\u4e2d\uff0c\u7acb\u523b\u56de\u5934\u76ef\u7740\u8ddf\u5728\u540e\u9762\u7684\u56db\u4e2a\u4eba\uff0c\u9a82\u9053\uff1a\u201c\u4f60\u4eec\u5168\u90fd\u778e\u4e86\u662f\u4e0d\u662f\uff0c\u653e\u7740\u8d35\u5bbe\u5750\u5728\u5c0f\u5385\u91cc\uff0c\u4f60\u4eec\u8fde\u8336\u4e5f\u4e0d\u5012\uff0c\u70df\u4e5f\u4e0d\u4e0a\uff0c\u771f\u7ed9\u6211\u6f0f\u6c14\u4e22\u8138\u2026\u2026\u201d \u3000\u3000\u4ed6\u8fd9\u4e00\u9a82\uff0c\u56db\u4e2a\u5e74\u8f7b\u4eba\u614c\u8d77\u6765\u4e86\uff0c\u4e24\u4e2a\u4eba\u62a2\u7740\u5012\u8336\uff0c\u4e24\u4e2a\u4eba\u62a2\u7740\u4e0a\u70df\uff0c\u8001\u53eb\u5316\u63a5\u4e0b\u4e24\u676f\u8336\uff0c\u4e5f\u63a5\u4e0b\u4e24\u7ba1\u6c34\u70df\u888b\uff0c\u7b11\u9053\uff1a\u201c\u522b\u592a\u5fd9\u548c\uff0c\u6162\u6162\u6765\uff0c\u5c31\u4f1a\u6709\u6761\u4e0d\u7d0a\u4e86\u3002\u2026\u2026\u201d \u3000\u3000\u79e6\u516b\u516c\u4e00\u770b\u4e0d\u50cf\u8bdd\uff0c\u63a5\u4e0b\u8001\u53eb\u5316\u4e00\u7ba1\u6c34\u70df\u888b\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u901a\u901a\u7ed9\u6211\u6eda\u51fa\u53bb\u3002\u53eb\u53a8\u4e0b\u51c6\u5907\u4e00\u684c\u4e0a\u597d\u9152\u5e2d\uff0c\u628a\u548c\u5c1a\u3001\u9053\u58eb\u90fd\u8bf7\u6765\uff0c\u4eca\u5929\uff0c\u6211\u8981\u559d\u5b83\u4e2a\u4e0d\u9189\u4e0d\u4f11\u3002\u201d \u3000\u3000\u56db\u4e2a\u5e74\u8f7b\u4eba\uff0c\u76ee\u5149\u76ef\u5728\u79e6\u516b\u516c\u7684\u8138\u4e0a\u770b\uff0c\u5fc3\u4e2d\u5145\u6ee1\u7591\u4e91\uff01 \u3000\u3000\u4e0d\u9519\uff0c\u79e6\u516b\u516c\u5b8c\u5168\u590d\u539f\u4e86\uff0c\u56db\u4e2a\u5e74\u8f7b\u4eba\u79bb\u5f00\u4e4b\u540e\u3002\u79e6\u516b\u516c\u7acb\u523b\u5bf9\u94b1\u7f3a\u515c\u5934\u4e00\u4e2a\u957f\u63d6\uff0c\u9053\uff1a\u201c\u94b1\u5144\uff0c\u4f60\u53ef\u771f\u662f\u5927\u6148\u5927\u60b2\u7684\u6551\u4e16\u7f57\u6c49\uff0c\u5c31\u90a3\u4e48\u4e09\u51e0\u4e0b\u3002\u6211\u8fd9\u62d6\u4e86\u51e0\u4e2a\u6708\u7684\u592a\u4f24\uff0c\u5ffd\u7136\u597d\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u4f60\u662f\u7ecf\u7a74\u88ab\u5c01\uff0c\u8fd9\u7ecf\u7a74\u5df2\u89e3\uff0c\u81ea\u7136\u5c31\u75c5\u75db\u5168\u6d88\u4e86\uff0c\u2026\u2026\u201d \u3000\u3000\u8001\u53eb\u5316\u9053\uff1a\u201c\u8fd9\u4e0d\u662f\u75c5\uff0c\u4e5f\u4e0d\u662f\u6298\u9aa8\u3001\u65ad\u7b4b\u7684\u5927\u4f24\u3002\u201d \u3000\u3000\u201c\u5509\uff01\u8fd9\u771f\u662f\u5fd9\u5bb6\u4e0b\u4f1a\uff0c\u4f1a\u5bb6\u4e0d\u5fd9\u554a\uff01\u542c\u4f60\u8bf4\u7684\u8fd9\u4e48\u8f7b\u677e\uff0c\u5144\u5f1f\u771f\u662f\u60ed\u6127\u6781\u4e86\uff0c\u653e\u770b\u4f60\u94b1\u5144\u8fd9\u6551\u547d\u6b49\u82e6\u7684\u91d1\u521a\u3001\u7f57\u6c49\u4e0d\u62dc\u3002 \u3000\u3000\u5230\u5904\u53bb\u6c42\u533b\u7eed\u547d\u2026\u2026\u201d\u79e6\u516b\u516c\u9053\uff0c\u201c\u5144\u5f1f\u53ea\u602a\u81ea\u5df1\u6709\u773c\u4e0d\u8bc6\u91d1\u5c71\u7389\uff0c\u767d\u767d\u53d7\u4e86\u513f\u4e2a\u6708\u7684\u7f6a\uff0c\u94b1\u5144\uff0c\u4f60\u4e5f\u771f\u662f\u5fcd\u5fc3\u5f97\u5f88\u554a\uff01\u773c\u770b\u7740\u5144\u5f1f\u6211\u53d7\u82e6\u53d7\u96be\uff0c\u4f60\u5c31\u662f\u5750\u89c6\u4e0d\u7406\u2026\u2026\u201d\u8001\u53eb\u5316\u5fc3\u4e2d\u6697\u53eb\u4e86\u4e24\u58f0\u60ed\u6127\uff0c\u53e3\u4e2d\u5374\u8fa9\u9053\uff1a\u201c\u4f60\u4e00\u76f4\u8bf4\u4f60\u662f\u751f\u4e86\u75c5\uff0c\u8981\u662f\u65e9\u77e5\u9053\u4f60\u662f\u53d7\u4e86\u4f24\uff0c\u8001\u53eb\u5316\u4e5f\u4e0d\u4f1a\u5230\u5904\u8dd1\u7740\u5e2e\u4f60\u627e\u5927\u592b\u4e86\u3002\u201d \u3000\u3000\u79e6\u516b\u516c\u5927\u4e3a\u611f\u52a8\u5730\u9053\uff1a\u201c\u94b1\u5144\uff0c\u75be\u98ce\u77e5\u8349\u52b2\uff0c\u75c5\u69bb\u89c1\u771f\u60c5\uff0c\u79e6\u67d0\u4eba\u5728\u94b1\u5144\u5fc3\u4e2d\u6709\u5982\u6b64\u4efd\u91cf\uff0c\u6211\u4e00\u70b9\u90fd\u4e0d\u77e5\u9053\uff0c\u4ece\u4eca\u5929\u8d77\uff0c\u4f60\u94b1\u5144\u53ea\u8981\u4e0d\u5acc\u5f03\uff0c\u54b1\u4eec\u5c31\u662f\u751f\u6b7b\u81f3\u4ea4\u3002\u201d \u3000\u3000\u201c\u8001\u53eb\u5316\u65e9\u5c31\u60f3\u4ea4\u4f60\u8fd9\u4e2a\u670b\u53cb\u4e86\uff0c\u4e0d\u8fc7\u79e6\u5144\uff0c\u76ee\u4e0b\u6c5f\u6e56\u60c5\u52bf\u8be1\u5f02\u3002\u6b64\u540e\uff0c\u4e00\u5207\u7684\u884c\u52a8\uff0c\u90fd\u8981\u5c0f\u5fc3\u4e00\u4e9b\u624d\u884c\u3002\u201d \u3000\u3000\u79e6\u516b\u516c\u6c89\u541f\u4e86\u4e00\u9635\u3002\u9053\uff1a\u201c\u94b1\u5144\uff0c\u8fd9\u4ef6\u4e8b\u607c\u4eba\u5f97\u5f88\uff0c\u8001\u5b9e\u8bf4\uff0c\u5144\u5f1f\u6b63\u5728\u8ffd\u67e5\uff0c\u53ea\u53ef\u60dc\u6211\u8fd9\u4e2a\u4f24\uff0c\u5bb3\u5f97\u6211\u628a\u4e8b\u60c5\u62d6\u4e86\u4e0b\u6765\uff0c\u5927\u5bb6\u4e3a\u4e86\u5144\u5f1f\uff0c\u90fd\u53ea\u597d\u505c\u4e0b\u4e0d\u52a8\u2026\u2026\u201d \u3000\u3000\u201c\u6162\u6765\uff0c\u6162\u6765\uff0c\u6211\u542c\u5f97\u4e0d\u592a\u660e\u767d\u2026\u2026\u201d\u8001\u53eb\u5316\u8bf4\uff1a\u201c\u4f60\u80fd\u4e0d\u80fd\u628a\u4e8b\u60c5\u8bf4\u6e05\u695a\u4e00\u4e9b\u3002\u201d \u3000\u3000\u79e6\u516b\u516c\u9053\uff1a\u201c\u597d\uff01\u6211\u8bf4\u6e05\u695a\uff0c\u4e0d\u8fc7\uff0c\u4f60\u660e\u767d\u4e86\u3002\u5c31\u8981\u4f5c\u4e2a\u51b3\u5b9a\uff0c\u81f3\u5c11\uff0c\u8981\u7b54\u5e94\u6211\u4e25\u5b88\u79d8\u5bc6\u3002\u94b1\u5144\uff0c\u6211\u53ef\u4ee5\u8d4c\u4e0a\u81ea\u5df1\u4e00\u6761\u547d\uff0c\u4f46\u4ed6\u4eec\u4e3a\u4e86\u5927\u5c40\uff0c\u53ea\u6015\u4e0d\u4f1a\u653e\u8fc7\u4f60\u4e86\uff1f\u201d \u3000\u3000\u201c\u542c\u8d77\u6765\u5f88\u4e25\u91cd\u554a\uff01\u2026\u2026\u201d\u8001\u53eb\u5316\u9053\uff1a\u201c\u6211\u7b54\u5e94\u4f60\uff0c\u4e0d\u80fd\u51b3\u8d70\u65f6\uff0c\u6211\u4e00\u5b9a\u4fdd\u5b88\u79d8\u5bc6\u3002\u201d \u3000\u3000\u79e6\u516b\u516c\u4f4e\u58f0\u9053\uff1a\u201c\u6574\u4e2a\u6b66\u6797\uff0c\u90fd\u88ab\u4e00\u80a1\u83ab\u540d\u7531\u6765\u7684\u90aa\u6076\u529b\u91cf\u63a7\u5236\u4e86\uff0c\u5c11\u6797\u3001\u6b66\u5f53\uff0c\u90a3\u7b49\u8a89\u6ee1\u6c5f\u7322\u7684\u592a\u95e8\u6d3e\uff0c\u4e5f\u662f\u62db\u67b6\u4e0d\u4f4f\u3002 \u3000\u3000\u6240\u4ee5\uff0c\u4ed6\u4eec\u5404\u6d3e\u51fa\u4e86\u4e00\u4f4d\u957f\u8001\uff0c\u627e\u4e0a\u5144\u5f1f\uff0c\u73b0\u5728\uff0c\u5c31\u4f4f\u5728\u5144\u5f1f\u7684\u5bb6\u4e2d\uff0c\u53ea\u7b49\u5144\u5f1f\u4f24\u52bf\u597d\uff0c\u5927\u5bb6\u5c31\u5c55\u5f00\u4e00\u4e9b\u8ffd\u67e5\u7684\u884c\u52a8\uff0c\u60f3\u4e0d\u5230\u5144\u5f1f\u7684\u4f24\u52bf\uff0c\u5374\u8d8a\u6765\u8d8a\u91cd\uff0c\u5982\u975e\u94b1\u5144\u7597\u6cbb\u597d\u5144\u5f1f\u7684\u4f24\u52bf\u3002\u8fd9\u4e2a\u8ba1\u5212\uff0c\u53ea\u6015\u8981\u5c3d\u4ed8\u6d41\u6c34\uff0c\u81f3\u5c11\uff0c\u5144\u5f1f\u662f\u65e0\u80fd\u53c2\u4e0e\u4e86\u3002\u201d \u3000\u3000\u94b1\u7f3a\u795e\u618e\u4e25\u8083\u4ed6\u8bf4\u9053\uff1a\u201c\u79e6\u5144\uff0c\u90a3\u4e9b\u4eba\u90fd\u9760\u5f97\u4f4f\u5417\uff1f\u201d \u3000\u3000\u201c\u9760\u5f97\u4f4f\uff0c\u81f3\u5c11\uff0c\u5927\u90e8\u5206\u7684\u4eba\u9760\u5f97\u4f4f\u2026\u2026\u201d \u3000\u3000\u201c\u597d\u6781\u4e86\uff0c\u8001\u53eb\u5316\u4e5f\u8054\u5408\u4e86\u51e0\u4f4d\u670b\u53cb\uff0c\u5728\u8ffd\u67e5\u8fd9\u4ef6\u4e8b\u60c5\u3002\u5927\u5bb6\u5408\u8d77\u6765\u5427\uff01\u201d \u3000\u3000\u79e6\u516b\u516c\u9053\uff1a\u201c\u884c\uff0c\u884c\uff0c\u5144\u5f1f\u7acb\u523b\u7ed9\u4f60\u5f15\u89c1\u51e0\u4f4d\u5fd7\u540c\u9053\u5408\u7684\u670b\u53cb\uff0c\u7136\u540e\u3002\u54b1\u4eec\u5c31\u5f00\u59cb\u884c\u52a8\uff0c\u8ffd\u67e5\u4e0b\u53bb\u2026\u2026\u201d \u3000\u3000\u201c\u884c\u52a8\u2026\u2026\u201d\u8001\u53eb\u5316\u9053\uff1a\u201c\u4ece\u54ea\u91cc\u4e0b\u624b\uff1f\u201d \u3000\u3000\u201c\u5c31\u662f\u5144\u5f1f\u53d7\u4f24\u7684\u5730\u65b9\uff0c\u5341\u4e03\u516b\u5c81\u7684\u5c0f\u59d1\u5a18\uff0c\u7f8e\u7684\u50cf\u82b1\u6735\u4f3c\u7684\uff0c\u4e0b\u624b\u6740\u4eba\uff0c\u5374\u662f\u9762\u4e0d\u6539\u8272\uff0c\u90a3\u5730\u65b9\u53eb\u6cbb\u9633\u5c45\uff0c\u2026\u2026\u201d \u3000\u3000----------- \u3000\u3000 \u7b2c \u56db \u56de\u3000\u8fce\u5bbe\u9601\u610f\u5916\u6765\u9ad8\u4eba \u3000 \u3000\u3000\u6c5f\u67ab\u6765\u5230\u4e86\u6d1b\u9633\u5c45\uff0c\u5b8c\u5168\u662f\u672c\u6765\u9762\u76ee\uff0c\u5929\u84dd\u957f\u888d\uff0c\u767d\u8272\u7684\u7740\u6bdb\u56f4\u5dfe\uff0c\u4e00\u526f\u516c\u5b50\u54e5\u513f\u7684\u6253\u626e\uff0c\u53ea\u662f\u5934\u4e0a\u7f3a\u4e86\u4e00\u9876\u6b66\u751f\u5dfe\uff0c\u5374\u628a\u5934\u53d1\u76d8\u5728\u5934\u4e0a\uff0c\u770b\u4e0a\u53bb\u6709\u70b9\u602a\u5f02\uff0c\u4f46\u5374\u5f88\u65b0\u9896\uff0c\u589e\u591a\u4e86\u4e0d\u5c11\u5e05\u6c14\u3002 \u3000\u3000\u8fd9\u65f6\u523b\uff0c\u4e0d\u8fc7\u662f\u521a\u521a\u4e0a\u706f\u7684\u65f6\u5206\u3002 \u3000\u3000\u6c5f\u67ab\u4e00\u8def\u901b\u8fdb\u4e86\u6d1b\u9633\u5c45\uff0c\u76f4\u5954\u6885\u82b1\u5385\u3002 \u3000\u3000\u4e00\u4e2a\u9ed1\u88e4\u3001\u9ed1\u8884\u7684\u5e97\u4f19\u8ba1\uff0c\u521a\u521a\u70b9\u4e0a\u4e24\u76cf\u5782\u82cf\u5bab\u706f\uff0c\u4e00\u56de\u5934\u5c31\u89c1\u6c5f\u67ab\u7ad9\u5728\u95e8\u53e3\uff0c\u770b\u4ed6\u90a3\u65b0\u5947\u7684\u53d1\u578b\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u65e9\u554a\uff01\u5927\u5c11\u7237\u3002\u201d \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u914d\u516d\u4e2a\u6700\u597d\u7684\u4e0b\u9152\u83dc\uff0c\u8bf7\u6885\u82b1\u59d1\u5a18\u966a\u9152\u3002\u201d \u3000\u3000\u5e97\u4f19\u8ba1\u9053\uff1a\u201c\u5927\u5c11\u7237\uff0c\u6ca1\u6709\u6765\u8fc7\u5427\uff01\u201d \u3000\u3000\u201c\u6ca1\u6709\uff0c\u4f60\u600e\u4e48\u77e5\u9053\u5462\uff1f\u201d \u3000\u3000\u201c\u6211\u662f\u4e13\u8d23\u7167\u987e\u6885\u82b1\u5385\u7684\uff0c\u4e00\u76f4\u6ca1\u6709\u89c1\u8fc7\u4f60\u2026\u2026\u201d\u5e97\u4f19\u8ba1\u7b11\u9053\uff1a\u201d\u4f46\u4f60\u5374\u597d\u50cf\u5f88\u719f\u4e00\u6837\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u5fc3\u4e2d\u4e00\u52a8\uff0c\u5fd6\u9053\uff1a\u4e13\u8d23\u7167\u987e\u6885\u82b1\u5385\u7684\uff0c\u6211\u600e\u4e48\u4e0d\u77e5\u9053\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u770b\u6765\uff0c\u4ed6\u4eec\u662f\u79c1\u4e0b\u7ea6\u5b9a\uff0c\u6885\u82b1\u8fd9\u4e2a\u4eba\u679c\u4e0d\u7b80\u5355\u3002\u53e3\u4e2d\u5e94\u9053\uff1a\u201c\u662f\u542c\u670b\u53cb\u8bf4\u7684\uff0c\u6d1b\u9633\u5c45\u7684\u82b1\u5a62\u4e2d\uff0c\u4ee5\u6885\u82b1\u6700\u51fa\u8272\u3002\u201d \u3000\u3000\u201c\u8bdd\u662f\u4e0d\u9519\uff0c\u4e0d\u8fc7\uff0c\u6885\u82b1\u6700\u51b7\u2026\u2026\u201d\u53cc\u76ee\u76ef\u6ce8\u5728\u6c5f\u67ab\u8eab\u4e0a\uff0c\u6253\u91cf\u4e86\u4e00\u9635\uff0c\u63a5\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\uff0c\u4f60\u8fd9\u4e2a\u6837\u5b50\uff0c\u662f\u4e0d\u662f\u80fd\u8ba9\u6885\u82b1\u59d1\u5a18\u559c\u6b22\uff0c\u4e0d\u8fc7\uff0c\u4eca\u665a\u4e0a\u6885\u82b1\u59d1\u5a18\u5df2\u6709\u4e86\u4e24\u5904\u4e3b\u5bb4\u7684\u5730\u65b9\uff0c\u2026\u2026\u201d \u3000\u3000\u201c\u4f60\u662f\u8bf4\uff0c\u6885\u82b1\u59d1\u5a18\u4eca\u665a\u4e0a\u6ca1\u6709\u7a7a\u2026\u2026\u201d \u3000\u3000\u201c\u5c31\u7b97\u6709\u7a7a\uff0c\u4e5f\u4e0d\u8fc7\u662f\u5e94\u4e2a\u536f\u5c31\u8d70\u2026\u2026\u201d\u5e97\u4f19\u8ba1\u8bf4\uff1a\u201c\u4f60\u82b1\u4e86\u5f88\u591a\u94b1\uff0c\u5927\u534a\u65f6\u95f4\u5728\u4e00\u4e2a\u4eba\u5403\u95f7\u9152\uff0c\u6211\u770b\uff0c\u6539\u4e2a\u65e5\u5b50\u4e8e\u6765\u5427\uff01\u201d \u3000\u3000\u201c\u4e0d\u884c\uff0c\u6211\u662f\u4e0b\u6c5f\u4eba\uff0c\u660e\u5929\u4e0d\u4e0a\u8def\uff0c\u540e\u5929\u6765\u4e0d\u53ca\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff0c\u201c\u6885\u82b1\u59d1\u5a18\u4e4b\u7f8e\u8c8c\uff0c\u6211\u662f\u6155\u540d\u4e45\u77e3\uff01\u53ea\u8981\u80fd\u89c1\u4e00\u9762\uff0c\u4e5f\u5c31\u5fc3\u6ee1\u610f\u8db3\u5566\uff01\u201d \u3000\u3000\u201c\u597d\u5427\uff01\u6211\u53bb\u8bf7\u8bf7\u770b\uff0c\u4e5f\u8bb8\uff0c\u6885\u82b1\u59d1\u5a18\u53ef\u4ee5\u966a\u4f60\u4e00\u9635\uff0c\u53ef\u662f\u4ef7\u94b1\u5f88\u8d35\u5440\uff01\u201d\u5e97\u4f19\u8ba1\u8138\u4e0a\u6cdb\u8d77\u65e0\u5948\u7684\u795e\u8272\u3002 \u3000\u3000\u6c5f\u67ab\u5fd6\u9053\uff1a\u201c\u5e74\u8f7b\u4eba\u679c\u7136\u8fd8\u6709\u7eaf\u60c5\uff0c\u662f\u975e\u8fd8\u5206\u5f97\u6e05\u695a\uff0c\u5bf9\u8fd9\u7b49\u4ee5\u7f8e\u8272\u8bb9\u8bc8\u5ba2\u4eba\u7684\u4e8b\uff0c\u5fc3\u4e2d\u8fd8\u6709\u4efd\u60ed\u6127\u7684\u611f\u89c9\u3002\u201d \u3000\u3000\u53d6\u51fa\u4e00\u7efd\u9ec4\u91d1\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u4e2a\u591f\u4e0d\u591f\uff1f\u201d \u3000\u3000\u201c\u5982\u679c\u662f\u5341\u4e24\u4e00\u952d\uff0c\u5927\u6982\u53ef\u4ee5\u591f\u4e86\u2026\u2026\u201d\u5e97\u4f19\u8ba1\u7b11\u9053\uff1a\u201c\u4f60\u8981\u516d\u4e2a\u597d\u83dc\uff0c\u90a3\u5c31\u6ca1\u51c6\u4e86\uff0c\u4f60\u7ed9\u6765\u4e2a\u718a\u638c\u3001\u5929\u9e45\u8089\u4e1d\u7092\u867e\u6d46\uff0c\u8fd9\u5341\u4e24\u91d1\u5b50\uff0c\u5c31\u4e0d\u5b9a\u591f\u4e86\uff0c\u6211\u770b\u8fd9\u4e48\u529e\u5427\uff01\u83dc\u6211\u66ff\u4f60\u53eb\uff0c\u53ea\u6709\u4f60\u548c\u6885\u82b1\u59d1\u5a18\uff0c\u516d\u4e2a\u83b1\u592a\u591a\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u90a3\u5c31\u9ebb\u70e6\u5c0f\u54e5\u4e86\u3002\u201d\u6c5f\u67ab\u628a\u9ec4\u91d1\u9012\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u5e97\u4f19\u8ba1\u63a5\u8fc7\u9ec4\u91d1\uff0c\u7b11\u9053\uff1a\u201c\u6211\u770b\u4f60\u5f88\u987a\u773c\uff0c\u624d\u5e2e\u4f60\u8fd9\u4e2a\u5fd9\uff0c\u8fd9\u5730\u65b9\u4e00\u63b7\u5343\u91d1\uff0c\u4e0d\u662f\u6211\u4eec\u8fd9\u79cd\u5e74\u8f7b\u4eba\u8be5\u6765\u7684\u5730\u65b9\u2026\u2026\u201d \u3000\u3000\u4ed6\u534a\u540c\u60c5\u3001\u534a\u8d23\u5907\u5730\u8bf4\u4e86\u6c5f\u67ab\u4e00\u9635\uff0c\u624d\u8f6c\u8eab\u5feb\u6b65\u800c\u53bb\u3002 \u3000\u3000\u6c5f\u67ab\u5fc3\u4e2d\u5fd6\u9053\uff1a\u201c\u4e00\u4e2a\u4eba\u4e00\u9910\u996d\uff0c\u5341\u4e24\u9ec4\u91d1\u8fd8\u8981\u8ba1\u7b97\u7740\u82b1\u7528\uff0c\u8fd9\u6d88\u8d39\u4e4b\u9ad8\uff0c\u53ea\u4f2f\u662f\u5929\u4e0b\u7b2c\u4e00\u4e86\uff0c\u6885\u82b1\u5c0f\u59ae\u5b50\uff0c\u6709\u4ec0\u4e48\u80fd\u8010\uff0c\u80fd\u53eb\u4eba\u5982\u6b64\u7740\u8ff7\u82b1\u8d39\u2026\u2026\u201d \u3000\u3000\u5fd6\u601d\u4e4b\u95f4\uff0c\u4e0d\u89c9\u95f4\u6fc0\u8d77\u4e86\u5f3a\u70c8\u7684\u597d\u80dc\u4e4b\u5fc3\u3002 \u3000\u3000\u5e97\u4f19\u8ba1\u9001\u4e0a\u4e86\u56db\u76d8\u5927\u83dc\u548c\u4e00\u58f6\u52a0\u70ed\u7684\u6c7e\u9152\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u4f60\u5148\u81ea\u5df1\u559d\u5427\uff01\u6885\u82b1\u59d1\u5a18\u5728\u6362\u8863\u670d\uff0c\u53ea\u6015\u8981\u7b49\u4e00\u4e0b\u624d\u5230\u3002\u201d \u3000\u3000\u201c\u4e0d\u8981\u7d27\uff0c\u6211\u7b49\u5979\u2026\u2026\u201d\u6c5f\u67ab\u8868\u73b0\u51fa\u6b3e\u6b3e\u6df1\u60c5\u3002 \u3000\u3000\u5e97\u4f19\u8ba1\u4e00\u7b11\u800c\u53bb\uff0c\u8fd9\u79cd\u4e8b\uff0c\u4ed6\u89c1\u7684\u592a\u591a\u4e86\u3002 \u3000\u3000\u6885\u82b1\u5385\u76f8\u5f53\u7684\u5c0f\u5de7\uff0c\u662f\u6240\u6709\u5385\u623f\u4e2d\u6700\u5c0f\u7684\u4e00\u95f4\uff0c\u4f46\u4e5f\u662f\u5e03\u7f6e\u5f97\u6700\u96c5\u81f4\u7684\u4e00\u95f4\u3002 \u3000\u3000\u6728\u95e8\u5440\u7136\u800c\u5f00\uff0c\u5bd2\u98ce\u9001\u8fc7\u6765\u4e00\u9635\u5e7d\u9999\uff0c\u5bd2\u98ce\u88ab\u963b\u95e8\u5916\uff0c\u4eba\u5374\u5a09\u5a09\u5a77\u5a77\u7684\u8d70\u8fc7\u6765\u3002 \u3000\u3000\u4e0d\u9519\uff0c\u6885\u82b1\u59d1\u5a18\uff0c\u6c5f\u67ab\u65e9\u5df2\u7ecf\u8bb0\u5f97\u5f88\u719f\u3002\u53ea\u662f\u7ecf\u8fc7\u4e86\u4e00\u756a\u523b\u610f\u7684\u5986\u626e\u4e4b\u540e\uff0c\u770b\u4e0a\u53bb\u66f4\u89c9\u5a07\u67d4\u3001\u52a8\u4eba\u3002\u6885\u82b1\u59d1\u5a18\u8868\u73b0\u7684\u7f8e\uff0c\u4e0d\u662f\u5a07\u8273\u3001\u8bf1\u60d1\uff0c\u800c\u662f\u4e00\u79cd\u52a8\u4eba\u601c\u60dc\u7684\u67d4\u5a9a\u2026\u2026 \u3000\u3000\u5979\u7684\u8863\u7740\u3001\u4e3e\u6b62\uff0c\u5168\u8d70\u7684\u7edf\u4e00\u683c\u8c03\uff0c\u83b2\u6b65\u59d7\u59d7\u884c\u8fc7\u6765\uff0c\u8d70\u8fd1\u4e86\u6c5f\u67ab\u65f6\u5374\u7a81\u7136\u6709\u7740\u5f31\u4e0d\u80dc\u529b\u7684\u6837\u5b50\uff0c\u5de6\u624b\u5374\u6309\u5728\u4e86\u684c\u9762\u4e0a\uff0c\u7a33\u4f4f\u5411\u4e0b\u5012\u53bb\u7684\u8eab\u5b50\u3002 \u3000\u3000\u8fd9\u5c31\u903c\u5f97\u6c5f\u67ab\u4e0d\u5f97\u4e0d\u4f38\u51fa\u624b\u53bb\uff0c\u6276\u4f4f\u4e86\u5979\uff0c\u4e5f\u987a\u52bf\u63e1\u4f4f\u4e86\u5979\u4e00\u53ea\u7ea4\u7ea4\u5782\u624b\u3002 \u3000\u3000\u90a3\u662f\u4e00\u53ea\u7f8e\u4e3d\u7edd\u4f26\u7684\u624b\uff0c\u767d\u5ae9\u3001\u7ea4\u5de7\uff0c\u67d4\u82e5\u65e0\u9aa8\u3002 \u3000\u3000\u8f6c\u8138\u770b\u6885\u82b1\uff0c\u6c5f\u67ab\u4e5f\u4e0d\u7981\u5fc3\u5934\u4e00\u8df3\u3002 \u3000\u3000\u53ea\u89c1\u5979\uff0c\u96ea\u767d\u7684\u7389\u9f7f\uff0c\u8138\u4e0a\u662f\u4e00\u7247\u67d4\u67d4\u7684\u5a9a\u6001\uff0c\u76c8\u76c8\u76ee\u5149\uff0c\u76ef\u4f4f\u6c5f\u67ab\u7684\u8138\u4e0a\uff0c\u90a3\u6a21\u6837\uff0c\u53eb\u7537\u4eba\u6026\u7136\u5fc3\u52a8\uff0c\u6068\u4e0d\u5f97\u7d27\u7d27\u7684\u62b1\u8fc7\u6765\uff0c\u4eb2\u5979\u4e2a\u591f\u3002 \u3000\u3000\u6c5f\u67ab\u6709\u628a\u6885\u82b1\u62b1\u6765\uff0c\u4ed6\u6697\u4e2d\u8fd0\u6c14\uff0c\u53cc\u76ee\u66b4\u5c04\u51fa\u4e24\u80a1\u706b\u52bf\u7684\u60c5\u7130\uff0c\u8138\u4e0a\u6a2a\u6ea2\u7740\u4e00\u7247\u60c5\u7231\u3002 \u3000\u3000\u6885\u82b1\u4f3c\u662f\u8eab\u4e0d\u7531\u5df2\u7684\uff0c\u7f13\u7f13\u5411\u6c5f\u67ab\u6000\u4e2d\u6295\u53bb\u2026\u2026\u3002 \u3000\u3000\u4f46\u5979\u5374\u7a81\u7136\u795e\u8272\u4e00\u6574\uff0c\u6323\u624e\u7740\u7ad9\u76f4\u4e86\u8eab\u5b50\uff0c\u62bd\u51fa\u4e86\u88ab\u63e1\u7684\u53f3\u624b\u3002\u4f4e\u58f0\u9053\uff1b\u201c\u4f60\u597d\u574f\uff01\u90a3\u4e48\u529b\uff0c\u624b\u90fd\u88ab\u4f60\u63e1\u75bc\u4e86\u3002\u201d \u3000\u3000\u6c5f\u67ab\u6697\u53eb\u4e24\u58f0\u5389\u5bb3\uff0c\u8fd9\u6885\u82b1\u59d1\u5a18\u7684\u529f\u529b\u4e4b\u6df1\uff0c\u8fdc\u8fdc\u8d85\u8fc7\u6625\u82b1\u56db\u5a62\u3002 \u3000\u3000\u53e3\u4e2d\u5374\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u90a3\u4e24\u9053\u773c\u795e\uff0c\u4e00\u8138\u5a9a\u52b2\uff0c\u4e0d\u77e5\u9053\u6709\u591a\u4e48\u7684\u8ff7\u4eba\u2026\u2026\u201d \u3000\u3000\u201c\u53ef\u662f\uff0c\u6ca1\u6709\u8ff7\u4f4f\u4f60\u5440\uff01\u4f60\u6e05\u9192\u5f97\u5f88\uff1f\u2026\u2026\u201d\u6885\u82b1\u5a9a\u773c\u6d41\u6ce2\u5730\u8bf4\u9053\uff0c\u201c\u4f60\u662f\u5e72\u4ec0\u4e48\u7684\uff1f \u3000\u3000\u6210\u5bb6\u4e86\u6ca1\u6709\uff1f\u201d \u3000\u3000\u201c\u8ff7\u4f4f\u6211\u4e86\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff0c\u201c\u6211\u597d\u60f3\u628a\u4f60\u62b1\u8fc7\u6765\u4eb2\u4e00\u4e0b\u2026\u2026\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u4e0d\u4eb2\u6211\u5462\uff1f\u201d \u3000\u3000\u201c\u6211\u4e0d\u6562\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff0c\u201c\u6211\u6015\u4f60\u751f\u6c14\uff0c\u4f46\u6211\u53c8\u538b\u5236\u4e0d\u4f4f\u81ea\u5df1\u7684\u6b32\u671b\uff0c\u6240\u4ee5\uff0c\u634f\u75bc\u4e86\u4f60\u7684\u624b\u3002\u201d \u3000\u3000\u201c\u6ca1\u5173\u7cfb\uff0c\u4f60\u662f\u82b1\u94b1\u7684\u5927\u7237\uff0c\u6211\u53ea\u662f\u62ff\u94f6\u5b50\u966a\u9152\u7684\u5c0f\u9152\u5973\u7f62\u4e86\u2026\u2026\u201d\u6885\u82b1\u65e0\u9650\u59d4\u5c48\u5730\u8bf4\uff0c\u201c\u53ea\u8981\u6709\u94b1\uff0c\u8c01\u90fd\u53ef\u4ee5\u53eb\u6211\u966a\u9152\u554a\uff01\u201d \u3000\u3000\u6c5f\u67ab\u5fc3\u4e2d\u5fd6\u9053\uff1a\u201c\u54b1\u4eec\u5c31\u5bf9\u7740\u88c5\u4e0b\u53bb\u5427\uff01\u770b\u770b\u8c01\u5148\u6342\u4e0d\u4f4f\u3002\u201d\u63d0\u9ad8\u4e86\u58f0\u97f3\uff0c\u9053\uff1a\u201c\u4e0d\u5bf9\u5440\uff01\u8fd9\u91cc\u8d35\u5f97\u5f88\uff0c\u6211\u521a\u624d\u62ff\u51fa\u5341\u4e24\u9ec4\u91d1\uff0c\u5e97\u4f19\u8ba1\u544a\u8bc9\u6211\u8fd8\u4e0d\u4e00\u5b9a\u591f\uff0c\u6211\u53eb\u516d\u4e2a\u83dc\uff0c\u4ed6\u7ed9\u6211\u6539\u6210\u56db\u4e2a\u83dc\uff0c\u50cf\u4f60\u6885\u82b1\u59d1\u5a18\u8fd9\u6837\u7684\u8eab\u4ef7\uff0c\u5929\u4e0b\u6709\u591a\u5c11\u4eba\u80fd\u5e38\u5e38\u6765\uff0c\u62db\u4f60\u966a\u9152\u5462\uff1f\u201d \u3000\u3000\u201c\u4f60\u5462\uff1f\u662f\u4e0d\u662f\u4e5f\u82b1\u4e0d\u8d77\uff1f\u201d \u3000\u3000\u201c\u8001\u5b9e\u8bf4\uff0c\u5341\u5929\u534a\u6708\u6765\u4e00\u6b21\uff0c\u6211\u4e5f\u8bb8\u53ef\u4ee5\u6491\u8fc7\u53bb\uff0c\u8981\u662f\u5929\u5929\u6765\uff0c\u4e09\u4e2a\u6708\u6211\u5c31\u5f97\u5356\u623f\u5b50\u3001\u7530\u5730\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff0c\u201c\u4e00\u9910\u996d\u5341\u4e24\u9ec4\u91d1\u554a\uff01\u8d35\u7684\u5413\u4eba\uff1f\u201d \u3000\u3000\u6885\u82b1\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u8fd8\u6ca1\u6709\u56de\u7b54\u6211\u521a\u624d\u95ee\u7684\u8bdd\u2026\u2026\u201d \u3000\u3000\u201c\u54e6\uff01\u6211\u662f\u676d\u5dde\u4eba\uff0c\u6765\u957f\u5b89\u8d29\u5356\u7ef8\u7f0e\uff0c\u751f\u610f\u4e0d\u7b97\u5927\uff0c\u4f46\u4e5f\u4e0d\u7b97\u5c0f\u2026\u2026\u201d\u6c5f\u67ab\u8bf4\uff0c\u201c\u5e94\u8be5\u6210\u5bb6\u4e86\uff0c\u53ef\u662f\u8fd8\u662f\u5149\u68cd\u4e00\u6761\uff01\u201d \u3000\u3000\u201c\u4f60\u5e74\u7eaa\u5f88\u8f7b\u561b\uff01\u7236\u6bcd\u5462\uff1f\u201d \u3000\u3000\u201c\u7236\u6bcd\u53cc\u4ea1\uff0c\u8bb0\u5f97\u5988\u6b7b\u65f6\uff0c\u6211\u53ea\u6709\u5341\u516d\u5c81\u2026\u2026\u201d \u3000\u3000\u201c\u8fd9\u4e48\u8bf4\uff0c\u8fd9\u7247\u5929\u4e0b\u662f\u4f60\u81ea\u5df1\u95ef\u51fa\u6765\u7684\u2026\u2026\u201d\u6885\u82b1\u8bf4\uff0c\u201c\u6211\u8981\u542c\u5b9e\u8bdd\uff0c\u4e0d\u8bb8\u9a97\u6211\u3002\u201d \u3000\u3000\u201d\u7236\u4eb2\u672c\u6765\u5c31\u505a\u8d29\u5356\u7ef8\u7f0e\u751f\u610f\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff0c\u201c\u6211\u53ea\u7b97\u4e8e\u627f\u7236\u4e1a\uff0c\u591a\u4e8f\u7236\u4eb2\u51e0\u4e2a\u751f\u524d\u597d\u53cb\u63d0\u643a\uff0c\u603b\u7b97\u6df7\u5165\u8fd9\u4e00\u884c\u4e86\u3002\u201d \u3000\u3000\u201c\u4e00\u4e2a\u4eba\uff0c\u65e2\u6709\u94b1\uff0c\u53c8\u82f1\u4fca\uff0c\u6f02\u4eae\u7684\u5927\u59d1\u5a18\uff0c\u5e94\u8be5\u7a7f\u6210\u4e00\u4e32\u9001\u4e0a\u95e8\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u6210\u4eb2\u5462\uff1f\u201d \u3000\u3000\u201c\u5927\u6982\u662f\u2026\u2026\u7f18\u5206\u4e0d\u5230\u5427\uff01\u6211\u53c8\u4e0d\u613f\u51d1\u5408\uff0c\u5c31\u8fd9\u4e48\u62d6\u4e0b\u6765\u4e86\uff1f\u201d \u3000\u3000\u201c\u554a\uff01\u662f\u773c\u5149\u592a\u9ad8\uff0c\u4f60\u770b\u4e0a\u4e86\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u201c\u4f60\uff01\u6885\u82b1\u59d1\u5a18\u2026\u2026\u201d \u3000\u3000\u201c\u771f\u662f\u5413\u6211\u4e00\u8df3\uff0c\u8bf4\u8c0e\u8bdd\u4e5f\u8be5\u52a8\u70b9\u8111\u7b4b\u554a\uff01\u5982\u679c\uff0c\u6211\u7684\u8bb0\u5fc6\u4e0d\u9519\uff0c\u2026\u2026\u201d\u6885\u82b1\u8bf4\uff0c\u201c\u54b1\u4eec\u662f\u7b2c\u4e00\u6b21\u89c1\u9762\uff0c\u600e\u4f1a\u770b\u4e0a\u4e86\u6211\u5462\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u6211\u4eec\u662f\u521d\u6b21\u89c1\u9762\uff0c\u4f46\u6211\u8bf4\u7684\u53ef\u5168\u662f\u5b9e\u8bdd\u3002\u2026\u2026\u201d\u6c5f\u67ab\u4f38\u624b\u7531\u6000\u4e2d\u53d6\u51fa\u4e00\u65b9\u6298\u53e0\u7684\u767d\u7ee2\uff0c\u9053\uff1a\u201c\u4e0d\u76f8\u4fe1\uff0c\u4f60\u770b\u8fd9\u4e2a\uff1f\u201d \u3000\u3000\u6885\u82b1\u63a5\u8fc7\u767d\u7ee2\uff0c\u7f13\u7f13\u5c55\u5f00\uff0c\u5374\u771f\u7684\u5413\u4e86\u4e00\u8df3\u3002 \u3000\u3000\u6562\u60c5\u90a3\u662f\u4e00\u526f\u7ee2\u753b\uff0c\u4e0a\u9762\u753b\u7684\u5c31\u662f\u5979\u6885\u82b1\u7684\u753b\u50cf\u3002 \u3000\u3000\u60f3\u60f3\u94dc\u955c\u4e2d\u81ea\u5df1\u7684\u5bb9\u8c8c\uff0c\u753b\u7684\u8fd8\u771f\u6709\u516b\u4e5d\u5206\u50cf\u3002\u3000\u201c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u2026\u2026\u201d\u6885\u82b1\u8bf4\uff0c\u201c\u4ec0\u4e48\u4eba\u753b\u4e86\u8fd9\u5e45\u753b\u50cf\uff1f\u201d\u3000\u201c\u6211\u8bf7\u5165\u753b\u7684\uff0c\u4ed6\u4eec\u5f62\u5bb9\u4e86\u4f60\u7684\u7f8e\u4e3d\uff0c\u6211\u542c\u5f97\u60a0\u7136\u795e\u5f80\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u8bf4\uff0c\u201c\u5c31\u8bf7\u4e86\u4e00\u4e2a\u6700\u597d\u7684\u753b\u5e08\uff0c\u4e0d\u8fc7\uff0c\u4fee\u6539\u4e86\u5341\u51e0\u6b21\uff0c\u624d\u6210\u76ee\u524d\u8fd9\u4e2a\u6837\u5b50\u3002\u201d \u3000\u3000\u201c\u753b\u5e08\u5728\u54ea\u91cc\uff0c\u6211\u8981\u89c1\u89c1\u4ed6\uff1f\u201d \u3000\u3000\u201c\u5728\u676d\u5dde\uff0c\u59d1\u5a18\u613f\u610f\u53bb\uff0c\u5728\u4e0b\u5949\u966a\uff0c\u800c\u4e14\u8d1f\u62c5\u5f80\u8fd4\u8d39\u7528\uff0c\u8f66\u9a6c\u517c\u7a0b\uff0c\u4e09\u4e2a\u6708\u5e94\u8be5\u591f\u4e86\uff1f\u201d \u3000\u3000\u201c\u592a\u8fdc\u4e86\u2026\u2026\u201d\u6885\u82b1\u8f7b\u8f7b\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff0c\u201c\u6211\u90a3\u6709\u8fd9\u6837\u591a\u7684\u81ea\u7531\u3002\u201d \u3000\u3000\u7a81\u7136\u95f4\uff0c\u6885\u82b1\u6709\u4e86\u4e00\u79cd\u5947\u602a\u7684\u611f\u89c9\uff0c\u8fd9\u4e2a\u4eba\u8bf4\u7684\u8bdd\u5341\u5206\u5e73\u5b9e\uff0c\u4e5f\u5408\u60c5\u5408\u7406\uff0c\u4f46\u5374\u6ca1\u6709\u529e\u6cd5\u67e5\u8bc1\uff0c\u4ed6\u8bb2\u51fa\u4e86\u5f88\u591a\u7684\u4e8b\uff0c\u4f46\u53ea\u80fd\u542c\u8fc7\u5c31\u7b97\u4e86\u3002 \u3000\u3000\u201c\u4f60\u8d35\u59d3\u554a\uff01\u5927\u540d\u600e\u79f0\u547c\uff1f\u201d\u6885\u82b1\u7a81\u7136\u95ee\u4e00\u4e2a\u6700\u5bb9\u6613\u56de\u7b54\u7684\u95ee\u9898\uff0c\u4e24\u9053\u76ee\u5149\uff0c\u5374\u7d27\u76ef\u5728\u6c5f\u67ab\u7684\u8138\u4e0a\u3002 \u3000\u3000\u201c\u6211\u53eb\u7530\u7389\uff0c\u4e3a\u4e86\u8981\u89c1\u59d1\u5a18\u4e00\u9762\uff0c\u7279\u5728\u6b64\u591a\u7559\u4e86\u4e00\u5929\u2026\u2026\u201d \u3000\u3000\u201c\u539f\u6765\u662f\u7530\u516c\u5b50\u3001\u4eca\u591c\u5e78\u4f1a\uff0c\u6211\u656c\u4f60\u4e00\u676f\u2026\u2026\u201d\u6885\u82b1\u4e3e\u8d77\u9152\u676f\uff0c\u8138\u4e0a\u5a9a\u7b11\u8fce\u4eba\u3002 \u3000\u3000\u6025\u6025\u4e3e\u8d77\u9152\u676f\uff0c\u6c5f\u67ab\u6025\u6025\u8bf4\u9053\uff1a\u201c\u4e0d\u6562\u5f53\u554a\uff01\u4e0d\u6562\u5f53\uff0c\u6211\u656c\u6885\u82b1\u59d1\u5a18\u2026\u2026\u201d \u3000\u3000\u6885\u82b1\u6267\u676f\u7684\u53f3\u624b\uff0c\u7a81\u7136\u653e\u624b\u677e\u676f\uff0c\u70b9\u51fa\u4e00\u6307\uff0c\u6b63\u4e2d\u6c5f\u67ab\u7684\u201c\u8f6f\u9ebb\u7a74\u201d\uff0c\u51fa\u624b\u7684\u90a3\u4efd\u5feb\u52b2\uff0c\u5f53\u771f\u662f\u5df2\u5230\u4e86\u76ee\u4e0d\u6687\u63a5\u7684\u5883\u754c\u3002 \u3000\u3000\u56e0\u4e3a\u5979\u70b9\u4e2d\u6c5f\u67ab\u7a74\u9053\u7684\u53f3\u624b\u6536\u56de\u6765\uff0c\u7adf\u4ece\u5bb9\u7684\u63a5\u4f4f\u4e86\u5411\u4e0b\u8dcc\u843d\u7684\u676f\u5b50\uff0c\u9152\u676f\u4e2d\u4f59\u9152\u4ecd\u5728\u3002 \u3000\u3000\u6c5f\u67ab\u624b\u4e2d\u7684\u676f\u5b50\u8dcc\u843d\u4e86\uff0c\u5165\u4e5f\u5411\u4e00\u4fa7\u5012\u53bb\u3002 \u3000\u3000\u6885\u82b1\u7684\u5de6\u624b\u4e00\u63a2\uff0c\u62b1\u4f4f\u4e86\u6c5f\u67ab\u7684\u8eab\u5b50\uff0c\u8138\u4e0a\u7684\u7b11\u5bb9\uff0c\u7a81\u7136\u6d88\u5931\uff0c\u51b7\u51b7\u7684\u8bf4\u9053\uff1a\u201c\u4f60\u7ed9\u6211\u542c\u7740\uff0c\u4f60\u6ee1\u53e3\u8c0e\u8a00\uff0c\u80e1\u8bf4\u516b\u9053\uff0c\u5e0c\u671b\u4f60\u597d\u597d\u5730\u60f3\u60f3\uff0c\u73b0\u5728\uff0c\u4f60\u5148\u4e56\u4e56\u7684\u8eba\u4e00\u4f1a\uff0c\u7b49\u4e00\u4f1a\u5e0c\u671b\u4f60\u80fd\u8bf4\u51fa\u5b9e\u8bdd\u3002\u201d \u3000\u3000\u7ad9\u8d77\u8eab\u5b50\uff0c\u5355\u81c2\u4e00\u6536\uff0c\u7adf\u628a\u6c5f\u67ab\u7ed9\u631f\u4e86\u8d77\u6765\u3002 \u3000\u3000\u770b\u5979\u90a3\u7ea4\u7ea4\u67f3\u8170\uff0c\u67d4\u5f31\u5a07\u8eaf\uff0c\u631f\u8d77\u6c5f\u67ab\u8fd9\u4e48\u4e00\u6761\u5927\u6c49\uff0c\u7adf\u662f\u884c\u82e5\u65e0\u4e8b\uff0c\u8d70\u5230\u4e86\u58c1\u89d2\u4e4b\u5904\uff0c\u7528\u624b\u4e00\u63a8\uff0c\u5899\u58c1\u95f4\u7adf\u7136\u88c2\u73b0\u51fa\u4e00\u5ea7\u95e8\u6237\u3002 \u3000\u3000\u6c5f\u67ab\u53cc\u76ee\u5fae\u95ed\uff0c\u4efb\u51ed\u6446\u5e03\u3002 \u3000\u3000\u90a3\u662f\u4e00\u5904\u5f88\u5c0f\u7684\u5bc6\u5ba4\uff0c\u6709\u4e00\u5f20\u6728\u69bb\u548c\u4e24\u5f20\u6728\u501a\u3002 \u3000\u3000\u6885\u82b1\u628a\u6c5f\u67ab\u653e\u5728\u6728\u69bb\uff0c\u7a81\u7136\u53f9\u606f\u4e00\u58f0\uff0c\u81ea\u8a00\u81ea\u8bed\u5730\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u4e2a\u4ee4\u4eba\u52a8\u5fc3\u7684\u7537\u4eba\uff0c\u5e0c\u671b\u4f60\u6ca1\u6709\u9a97\u6211\uff0c\u4f60\u4e0d\u662f\u5f88\u60f3\u4eb2\u4eb2\u6211\u5417\uff1f\u201d \u3000\u3000\u4f0f\u4e0b\u8eab\u5b50\uff0c\u8f7b\u542f\u6a31\u5507\uff0c\u7adf\u7136\u9001\u4e0a\u4e86\u4e00\u4e2a\u957f\u543b\u3002 \u3000\u3000\u53ef\u60dc\u6c5f\u67ab\u7a74\u9053\u88ab\u70b9\uff0c\u7f8e\u5973\u6295\u6000\uff0c\u9999\u5507\u9001\u543b\uff0c\u4ed6\u5374\u4e00\u70b9\u4e5f\u65e0\u6cd5\u4eab\u53d7\u3002 \u3000\u3000\u826f\u4e45\uff0c\u6885\u82b1\u624d\u7f13\u7f13\u7ad9\u8d77\u8eab\u5b50\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u7530\u7389\uff0c\u597d\u597d\u7684\u7761\u4e00\u4f1a\uff0c\u6211\u4f1a\u5c3d\u5feb\u6765\u770b\u4f60\u7684\u3002\u201d\u63a9\u4e0a\u5bc6\u5ba4\u4e4b\u95e8\uff0c\u5feb\u6b65\u800c\u53bb\u3002 \u3000\u3000\u8fd9\u90a3\u91cc\u662f\u98ce\u6708\u8ff7\u4eba\u7684\u9500\u91d1\u7a9f\uff0c\u7b80\u76f4\u662f\u4e00\u5ea7\u8981\u4eba\u547d\u7684\u9ed1\u5e97\uff0c\u5230\u5904\u90fd\u5e03\u6709\u5bc6\u5ba4\u3001\u673a\u5173\u2026\u2026\u3002 \u3000\u3000\u6c5f\u67ab\u633a\u8eab\u5750\u4e86\u8d77\u6765\uff0c\u6447\u6447\u5934\u53f9\u53e3\u6c14\uff0c\u5fd6\u9053\uff1a\u201c\u6211\u5728\u6d1b\u9633\u5c45\u4e2d\u662f\u7ba1\u7406\u996d\u5e84\u7684\u526f\u603b\u7ba1\uff0c\u7adf\u7136\u4e0d\u77e5\u9053\u8fd9\u6885\u82b1\u5385\u4e2d\u6709\u8fd9\u4e48\u4e00\u95f4\u5bc6\u5ba4\uff0c\u5728\u8fd9\u91cc\uff0c\u5979\u4eec\u53ef\u4ee5\u4e0d\u9732\u75d5\u8ff9\u7684\u4e3a\u6240\u6b32\u4e3a\uff0c\u6885\u82b1\u5385\u4e2d\u6709\uff0c\u90a3\u83ca\u82b1\u5385\u3001\u8377\u82b1\u5385\u3001\u6843\u82b1\u5385\uff0c\u662f\u4e0d\u662f\u4e00\u6837\u5462\uff1f\u201d \u3000\u3000\u60ed\u6127\u554a\uff01\u60ed\u6127\uff0c\u6211\u6c5f\u67ab\u4e00\u5411\u8ba4\u4e3a\u81ea\u5df1\u662f\u76f8\u5f53\u806a\u660e\u7684\u4eba\uff0c\u4f46\u6765\u4e86\u51e0\u4e2a\u6708\uff0c\u7adf\u5bf9\u56db\u5b63\u82b1\u5a62\u4e00\u65e0\u6240\u77e5\u3002\u201d \u3000\u3000\u4ed6\u7ad9\u8d77\u8eab\u5b50\uff0c\u4f38\u52a8\u4e00\u4e0b\u624b\u811a\uff0c\u76d8\u5750\u5728\u6728\u69bb\u4e0a\u95ed\u76ee\u8c03\u606f\u3002 \u3000\u3000\u4ed6\u867d\u7136\u53ca\u65f6\u7528\u201c\u79fb\u7a74\u6362\u4f4d\u201d\u7684\u4e0a\u4e58\u5185\u529f\uff0c\u79fb\u5f00\u4e86\u7a74\u9053\uff0c\u4f46\u6885\u82b1\u90a3\u4e00\u6307\u529b\u91cf\u76f8\u5f53\u7684\u91cd\u5927\uff0c\u88ab\u6307\u529b\u70b9\u4e2d\u4e4b\u5904\uff0c\u7adf\u7136\u9690\u9690\u4f5c\u75bc\u3002 \u3000\u3000\u56db\u5b63\u82b1\u5a62\u4e2d\u5c5e\u6885\u82b1\u6700\u7f8e\uff0c\u4f46\u4e5f\u5c5e\u5979\u4e0b\u624b\u6700\u72e0\uff0c\u5fc3\u5730\u6700\u6bd2\uff0c\u8ff7\u60d1\u4eba\u5fc3\u7684\u9b54\u529f\u6700\u6df1\uff0c\u4e5f\u8bb8\uff0c\u8fd9\u4e2b\u5934\u624d\u662f\u771f\u6b63\u7edf\u7387\u6d1b\u9633\u5c45\u7684\u9996\u8111\u4eba\u7269\u2026\u2026\u201d \u3000\u3000\u5ffd\u7136\u95f4\uff0c\u6c5f\u67ab\u60f3\u5230\u4e86\u9752\u51e4\uff0c\u90a3\u4e2a\u98ce\u9761\u957f\u5b89\u7684\u6b4c\u5973\uff0c\u4ee5\u5979\u9020\u6210\u7684\u8f70\u52a8\u6765\u8bf4\uff0c\u662f\u5f3a\u8fc7\u6885\u82b1\u5343\u767e\u500d\u4e86\u2026\u2026\u3002 \u3000\u3000\u96be\u9053\uff0c\u5979\u624d\u662f\u7edf\u6cbb\u6d1b\u9633\u5c45\u7684\u9996\u8111\u3002 \u3000\u3000\u6c5f\u67ab\u6709\u70b9\u540e\u6094\u4e86\uff0c\u8fc7\u53bb\uff0c\u7adf\u7136\u5ffd\u7565\u4e86\u9752\u51e4\u2026\u2026\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u4e5f\u5ffd\u7565\u4e86\u56db\u5b63\u82b1\u5a62\u2026\u2026\u3002 \u3000\u3000\u6c5f\u67ab\u6447\u6447\u5934\uff0c\u5fd6\u9053\uff1a\u201c\u5e78\u597d\u662f\u53d1\u89c9\u53ca\u65f6\uff0c\u5426\u5219\uff0c\u4e03\u5b9d\u548c\u5c1a\u7b49\u4e00\u884c\u4eba\uff0c\u5c31\u7b97\u80fd\u907f\u8fc7\u9093\u98de\u89c2\u5bdf\uff0c\u5374\u53c8\u5982\u4f55\u80fd\u9003\u8fc7\u8fd9\u56db\u5b63\u82b1\u5a62\u7684\u6697\u7b97\u3002 \u3000\u3000\u4ee5\u4eca\u591c\u6885\u82b1\u7684\u884c\u4e8b\u65b9\u6cd5\u800c\u76f2\uff0c\u5979\u4eec\u90fd\u662f\u53ea\u6c42\u76ee\u7684\uff0c\u4e0d\u62e9\u624b\u6bb5\u7684\u4eba\u3002 \u3000\u3000\u5979\u4eec\u72e0\u6bd2\u3001\u660e\u5feb\uff0c\u884c\u4e8b\u8fc5\u901f\uff0c\u5b8c\u5168\u662f\u8bad\u7ec3\u6709\u7d20\u7684\u6740\u624b\u4f5c\u98ce\u2026\u2026\u3002 \u3000\u3000\u6c5f\u67ab\u5fc3\u4e2d\u5927\u5927\u5730\u4e00\u9707\uff0c\u5fd6\u9053\uff1a\u201c\u96be\u9053\u8fd9\u4e9b\u4eba\uff0c\u4e5f\u517c\u4efb\u6740\u624b\u7684\u5de5\u4f5c\u4e0d\u6210\u2026\u2026\u201d \u3000\u3000\u53ef\u6015\u5440\uff01\u706f\u7ea2\u9152\u7eff\u7684\u6d1b\u9633\u5c45\u3002 \u3000\u3000\u53ef\u6015\u5440\uff01\u5a07\u5a07\u7f8e\u5973\u86c7\u874e\u5fc3\u2026\u2026\u3002 \u3000\u3000\u6c5f\u67ab\u601d\u7eea\u5982\u6f6e\uff0c\u65e0\u6cd5\u518d\u8fd0\u6c14\u8c03\u606f\u3002 \u3000\u3000\u4e00\u756a\u6df1\u957f\u7684\u601d\u8651\u4e4b\u540e\uff0c\u4e5f\u575a\u5b9a\u4e86\u4ee5\u6fc0\u70c8\u624b\u6bb5\u5bf9\u6297\u56db\u5b63\u82b1\u5a62\u7684\u51b3\u5fc3\u3002 \u3000\u3000\u4e0d\u77e5\u9053\u8fc7\u53bb\u4e86\u591a\u5c11\u65f6\u5019\uff0c\u6c5f\u67ab\u542c\u5230\u4e86\u6b65\u5c65\u58f0\u606f\uff0c\u6025\u6025\u7684\u8eba\u5728\u5e8a\u4e0a\uff0c\u95ed\u4e0a\u53cc\u76ee\u3002 \u3000\u3000\u6697\u5ba4\u95e8\u88ab\u6253\u5f00\uff0c\u6885\u82b1\u59d1\u5a18\u8f7b\u67d4\u5730\u884c\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u706b\u5149\u95ea\u52a8\uff0c\u6697\u5ba4\u4e2d\u7adf\u7136\u4eae\u8d77\u4e86\u4e00\u652f\u706b\u70db\u3002 \u3000\u3000\u6885\u82b1\u5a07\u8273\u7684\u8138\u4e0a\uff0c\u5e26\u7740\u4e94\u5206\u9152\u610f\uff0c\u4e94\u638c\u8f7b\u6325\uff0c\u89e3\u5f00\u4e86\u6c5f\u67ab\u88ab\u70b9\u7684\u7a74\u9053\u3002 \u3000\u3000\u6c5f\u67ab\u7741\u5f00\u53cc\u76ee\uff0c\u633a\u8eab\u5750\u4e86\u8d77\u6765\u3002 \u3000\u3000\u4ed6\u88c5\u4f5c\u5f97\u5f88\u597d\uff0c\u4e00\u5411\u5fc3\u7ec6\u7684\u6885\u82b1\uff0c\u7adf\u7136\u6ca1\u6709\u77a7\u51fa\u7834\u7efd\u3002 \u3000\u3000\u201c\u7530\u7389\u2026\u2026\u201d\u6885\u82b1\u6e29\u67d4\u7684\u8bf4\u9053\uff1a\u201c\u8bf4\u5b9e\u8bdd\u5427\uff01\u4f60\u7a76\u7adf\u662f\u5e72\u4ec0\u4e48\u7684\uff1f\u201d \u3000\u3000\u201c\u5509\uff01\u4f60\u4f1a\u70b9\u7a74\u6cd5\uff0c\u6211\u77e5\u9053\u90a3\u662f\u4e00\u79cd\u5f88\u9ad8\u6df1\u7684\u6b66\u529f\u2026\u2026\u201d\u7530\u7389\u53f9\u606f\u4e00\u58f0\uff0c\u63a5\u9053\uff1a\u201c\u6211\u4e5f\u8ddf\u5f20\u5e08\u7236\u7ec3\u8fc7\u51e0\u5e74\u6b66\u529f\uff0c\u53ea\u53ef\u60dc\u4ed6\u4e0d\u4f1a\u70b9\u7a74\u6cd5\uff0c\u6ca1\u6709\u4f20\u7ed9\u6211\u4eec\u3002\u201d \u3000\u3000\u6885\u82b1\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8c01\u662f\u5f20\u5e08\u7236\uff1f\u201d \u3000\u3000\u201c\u676d\u5dde\u5927\u5c71\u6b66\u9986\u7684\u5f20\u5e08\u7236\u5f88\u6709\u540d\u6c14\u2026\u2026\u201d\u7530\u7389\u9053\uff1a\u201c\u6211\u8ddf\u4ed6\u5b66\u4e86\u4e09\u5e74\uff0c\u7ec3\u8fc7\u5927\u6d2a\u62f3\u3001\u5c0f\u6d2a\u62f3\uff0c\u4e09\u4e24\u4e2a\u5e74\u8f7b\u4eba\uff0c\u4e5f\u8fd1\u4e0d\u4e86\u8eab\uff0c\u6211\u53ea\u6709\u8fd9\u4e9b\u672c\u9886\uff0c\u73b0\u5728\uff0c\u5168\u90e8\u90fd\u8bf4\u51fa\u6765\u4e86\u3002\u201d \u3000\u3000\u6c5f\u67ab\u4e0d\u77e5\u9053\u676d\u5dde\u662f\u4e0d\u662f\u771f\u6709\u4e00\u5ea7\u5927\u5c71\u6b66\u9986\uff1f\u4f46\u6885\u82b1\u4e5f\u4e0d\u77e5\u9053\uff0c\u6240\u4ee5\uff0c\u6885\u82b1\u76f8\u4fe1\u4e86\u4ed6\u8bf4\u7684\u8bdd\u3002 \u3000\u3000\u4ed6\u867d\u7136\u88c5\u7684\u6709\u4e9b\u5e7c\u7a1a\uff0c\u4f46\u4ed6\u9b45\u529b\u56db\u5c04\u7684\u7537\u4eba\u6c14\u8d28\uff0c\u66f4\u52a0\u5f3a\u70c8\u3002 \u3000\u3000\u90a3\u662f\u5148\u5929\u7684\u6761\u4ef6\uff0c\u52a0\u4e0a\u540e\u5929\u7684\u8bad\u7ec3\uff0c\u57f9\u517b\u51fa\u6765\u7279\u522b\u6c14\u8d28\u3002 \u3000\u3000\u6240\u8c13\u540e\u5929\u7684\u8bad\u7ec3\uff0c\u5c31\u662f\u4e00\u79cd\u6b66\u529f\uff0c\u4e00\u79cd\u628a\u672c\u8eab\u9b45\u529b\u5b8c\u5168\u53d1\u6325\u7684\u6b66\u529f\uff0c\u5b83\u4e0d\u7b97\u6b63\u6d3e\u7684\u6b66\u5b66\uff0c\u4f46\u5374\u662f\u4e00\u79cd\u975e\u5e38\u6709\u6548\uff0c\u5b9e\u7528\u7684\u5de5\u592b\u201c\u60d1\u5fc3\u672f\u201d\u3002 \u3000\u3000\u5b83\u5177\u6709\u5bf9\u6297\u201c\u8ff7\u9b42\u5927\u6cd5\u201d\u548c\u201c\u5185\u5a9a\u672f\u201d\u7684\u529b\u91cf\u3002 \u3000\u3000\u800c\u6885\u82b1\u59d1\u5a18\u5c31\u662f\u4e60\u8fc7\u5185\u5a9a\u672f\u548c\u201c\u8ff7\u9b42\u5927\u6cd5\u201d\uff0c\u5f88\u6709\u6210\u5c31\u7684\u6f02\u4eae\u5973\u4eba\u3002 \u3000\u3000\u6240\u4ee5\uff0c\u6885\u82b1\u59d1\u5a18\u5728\u6d1b\u9633\u5c45\u4e2d\u6218\u65e0\u4e0d\u80dc\uff0c\u653b\u65e0\u4e0d\u514b\uff0c\u5979\u53d1\u89c9\u7684\u53ef\u7591\u7537\u4eba\uff0c\u5c31\u5f88\u96be\u9003\u8fc7\u5979\u7684\u5a9a\u529b\u8ff7\u60d1\uff0c\u4e0d\u662f\u5410\u9732\u771f\u8a00\uff0c\u5c31\u662f\u62dc\u670d\u5728\u77f3\u69b4\u88d9\u4e0b\u2026\u2026 \u3000\u3000\u4f46\u6885\u82b1\u59d1\u5a18\u662f\u51b7\u9177\u7684\uff0c\u6076\u6bd2\u800c\u591a\u60c5\u7684\uff0c\u88ab\u5979\u5f81\u670d\u7684\u7537\u4eba\uff0c\u4e0d\u662f\u88ab\u6697\u4e2d\u5904\u51b3\uff0c\u5c31\u662f\u88ab\u6084\u7136\u9001\u8d70\u2026\u2026\u3002 \u3000\u3000\u90a3\u4e9b\u4eba\u90fd\u662f\u6b66\u6797\u4e2d\u7684\u4eba\u7269\uff0c\u81f3\u4e8e\u4e00\u822c\u7684\u5546\u65c5\u5ba2\u4eba\uff0c\u9664\u4e86\u6155\u540d\u6307\u5b9a\u6885\u82b1\u59d1\u5a18\u966a\u9152\u4e4b\u5916\uff0c\u6885\u82b1\u662f\u5c3d\u91cf\u9003\u907f\uff0c\u9003\u907f\u4e0d\u4e86\uff0c\u4e5f\u6446\u51fa\u4e86\u4e00\u5e45\u51b7\u82e5\u51b0\u971c\u7684\u9762\u5b54\uff0c\u4f7f\u4eba\u5f88\u96be\u4eb2\u8fd1\uff0c\u9876\u591a\u80fd\u656c\u656c\u9152\uff0c\u78b0\u78b0\u624b\uff0c\u8bf4\u51e0\u53e5\u4fcf\u76ae\u8bdd\uff0c\u8fc7\u8fc7\u5e72\u763e\u2026\u2026\u3002 \u3000\u3000\u4f46\u9047\u4e0a\u4e86\u6c5f\u67ab\uff0c\u6885\u82b1\u7adf\u7136\u6709\u70b9\u628a\u6301\u4e0d\u4f4f\u3002 \u3000\u3000\u5f53\u7136\uff0c\u8fd9\u5c31\u50cf\u4e24\u4e2a\u5927\u8001\u5343\u78b0\u4e0a\u4e86\u5934\uff0c\u5927\u5bb6\u5404\u51ed\u9053\u884c\u51fa\u5343\uff0c\u8f93\u7684\u4e00\u65b9\uff0c\u4e00\u5b9a\u4f1a\u5c3d\u51fa\u6240\u6709\uff0c\u8f93\u5f97\u5f88\u60e8\u3002 \u3000\u3000\u6885\u82b1\u611f\u89c9\u5230\u60c5\u5f62\u6709\u70b9\u4e0d\u5bf9\uff0c\u56e0\u4e3a\uff0c\u5979\u5728\u966a\u5ba2\u4eba\u559d\u9152\u65f6\uff0c\u7adf\u7136\u5fc3\u795e\u4e0d\u5b9a\uff0c\u504f\u5de7\u4eca\u591c\u7684\u5ba2\u4eba\uff0c\u53c8\u662f\u5b98\u5e9c\u4e2d\u4eba\uff0c\u4e5f\u662f\u4ed6\u4eec\u4e0d\u80fd\u5f00\u7f6a\u7684\u4eba\u3002 \u3000\u3000\u5979\u88ab\u5ba2\u4eba\u7ea0\u7f20\uff0c\u5374\u53c8\u4e00\u8111\u5b50\u7530\u7389\u7684\u5f71\u50cf\u5728\u6643\u52a8\uff0c\u65e0\u6cd5\u51b7\u9759\u7684\u5e94\u4ed8\uff0c\u8fd9\u5c31\u4fbf\u5b9c\u4e86\u90a3\u4f4d\u5ba2\u4eba\uff0c\u6402\u6402\u62b1\u62b1\uff0c\u4e0a\u4e0b\u5176\u624b\uff0c\u5927\u5927\u88ab\u6298\u817e\u4e86\u4e00\u9635\uff0c\u6885\u82b1\u9006\u6765\u987a\u53d7\uff0c\u53ea\u597d\u95ed\u4e0a\u53cc\u76ee\uff0c\u628a\u5bf9\u65b9\u5e7b\u60f3\u6210\u7530\u7389\uff0c\u4efb\u5176\u8f7b\u6d6e\u3002 \u3000\u3000\u8fd9\u5c31\u7fa1\u715e\u4e86\u65c1\u89c2\u7684\u5ba2\u4eba\uff0c\u8ba4\u4e3a\u6885\u82b1\u4eca\u665a\u4e0a\u51b0\u6cb3\u89e3\u51bb\uff0c\u5218\u5e08\u7237\u7684\u8273\u798f\u4e0d\u6d45\u3002 \u3000\u3000\u5218\u5e08\u7237\u662f\u957f\u5b89\u5e9c\u7684\u5211\u540d\u6587\u6848\uff0c\u5728\u957f\u5b89\u4e5f\u7b97\u662f\u5f88\u6709\u6743\u52bf\u7684\u4eba\u7269\u3002 \u3000\u3000\u6885\u82b1\u7528\u4e86\u6700\u5927\u7684\u8010\u529b\uff0c\u5fcd\u53d7\u5230\u9152\u5bb4\u7ec8\u4e86\u3002\u7acb\u523b\u5982\u6e34\u9e66\u5954\u6cc9\uff0c\u56de\u5230\u6885\u82b1\u5385\u4e2d\uff0c\u63a9\u4e0a\u623f\u95e8\uff0c\u884c\u5165\u5bc6\u5ba4\uff0c\u70b9\u8d77\u706f\u706b\uff0c\u89e3\u5f00\u4e86\u7530\u7389\u7684\u7a74\u9053\u3002 \u3000\u3000\u201c\u6211\u76f8\u4fe1\u4f60\u2026\u2026\u201d\u6094\u82b1\u4f38\u51fa\u7ea4\u5de7\u7684\u7389\u624b\uff0c\u63e1\u4f4f\u4e86\u7530\u7389\u5de6\u624b\uff0c\u67d4\u5a9a\u5730\u7b11\u9053\uff0c\u201c\u4f60\u53ea\u662f\u542c\u4ed6\u4eec\u8bf4\u8d77\u6211\uff0c\u5c31\u80fd\u753b\u51fa\u4e86\u90a3\u5e45\u5f88\u50cf\u6211\u7684\u753b\u50cf\uff0c\u771f\u7684\u53eb\u4eba\u611f\u52a8\u2026\u2026\u201d\u7f13\u7f13\u504e\u5165\u4e86\u7530\u7389\u7684\u6000\u4e2d\u3002 \u3000\u3000\u7530\u7389\u4f3c\u662f\u5f88\u80c6\u5c0f\uff0c\u7f8e\u4eba\u5165\u6000\uff0c\u4ed6\u7adf\u7136\u4e0d\u6562\u7528\u624b\u53bb\u62b1\uff0c\u53cd\u5012\u662f\u6885\u82b1\u7684\u53f3\u624b\uff0c\u4f38\u4e86\u4e0a\u6765\uff0c\u7d27\u7d27\u5730\u62b1\u4f4f\u4e86\u7530\u7389\u7684\u80a9\u5934\u3002 \u3000\u3000\u201c\u7530\u7389\uff0c\u4f60\u4e0d\u662f\u5f88\u559c\u6b22\u6211\u4e48\uff1f\u4e3a\u4ec0\u4e48\u4e0d\u62b1\u4f4f\u6211\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u4e0d\u6562\uff0c\u6211\u4e0d\u77e5\u9053\u8fd8\u8981\u591a\u5c11\u9ec4\u91d1\u2026\u2026\u201d \u3000\u3000\u6885\u82b1\u5a07\u5a9a\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u7530\u7389\uff0c\u4f60\u771f\u662f\u50bb\u5f97\u53ef\u7231\uff0c\u6885\u82b1\u59d1\u5a18\u7684\u8eab\u4f53\uff0c\u5c82\u662f\u9ec4\u91d1\u80fd\u4e70\u5f97\u5230\u7684\uff1f\u201d \u3000\u3000\u201c\u9ec4\u91d1\u4e5f\u4e0d\u6210\u554a\uff01\u90a3\u8981\u2026\u2026\u201d \u3000\u3000\u201c\u5509\uff01\u7530\u7389\uff0c\u4f60\u662f\u771f\u7684\u4e0d\u61c2\u5462\uff1f\u8fd8\u662f\u5728\u6545\u610f\u88c5\u4f5c\u2026\u2026\u201d\u6885\u82b1\u5c3d\u91cf\u4f7f\u58f0\u97f3\u53d8\u5f97\u6e29\u67d4\u3002 \u3000\u3000\u201c\u6211\u88ab\u4f60\u5413\u6015\u4e86\u2026\u2026\u201d\u7530\u7389\u8bf4\uff0c\u201c\u4f60\u53ef\u4ee5\u51fa\u624b\u70b9\u6211\u7684\u7761\u7a74\uff0c\u4e5f\u53ef\u4ee5\u51fa\u624b\u70b9\u6211\u7684\u6b7b\u7a74\u2026\u2026\u201d \u3000\u3000\u201c\u539f\u6765\u4f60\u62c5\u5fc3\u6211\u4f1a\u6740\u4e86\u4f60\u2026\u2026\u6885\u82b1\u5a07\u5a9a\u4e00\u7b11\uff0c\u9053\uff0c\u201c\u4e0d\u4f1a\u7684\uff0c\u73b0\u5728\uff0c\u5c31\u662f\u4f60\u9a97\u4e86\u6211\uff0c\u6211\u4e5f\u820d\u4e0d\u5f97\u6740\u4f60\u4e86\u3002\u201d \u3000\u3000\u5979\u867d\u6709\u5f3a\u8005\u7684\u59ff\u6001\uff0c\u4f46\u5185\u5fc3\u4e2d\u5374\u5df2\u7ecf\u5f7b\u5e95\u7684\u6e83\u8d25\uff0c\u5979\u5df2\u5b8c\u5168\u5c48\u670d\u5728\u6c5f\u67ab\u7684\u9b45\u529b\u4e0b\u3002 \u3000\u3000\u6c5f\u67ab\u5f00\u59cb\u53cd\u51fb\uff0c\u6885\u82b1\u5df2\u5b8c\u5168\u5931\u53bb\u4e86\u81ea\u5236\u7684\u80fd\u529b\uff0c\u5979\u5bbd\u8863\u89e3\u5e26\uff0c\u5168\u529b\u5949\u732e\u4e86\u81ea\u5df1\u3002 \u3000\u3000\u6885\u82b1\u8ff7\u5931\u4e86\uff0c\u5341\u51e0\u5e74\u4fee\u70bc\u7684\u5185\u5a9a\u672f\uff0c\u4e5f\u5b8c\u5168\u5931\u53bb\u4e86\u4f5c\u7528\uff0c\u5c31\u5728\u5bc6\u5ba4\u7684\u5c0f\u5e8a\u4e0a\uff0c\u7761\u719f\u4e86\u8fc7\u53bb\u2026\u2026\u3002 \u3000\u3000\u9192\u6765\u65f6\uff0c\u5df2\u814a\u70db\u6210\u7070\uff0c\u5f00\u542f\u7684\u5bc6\u5ba4\u6728\u95e8\uff0c\u9690\u9690\u900f\u5165\u4e86\u5929\u5149\u3002 \u3000\u3000\u6562\u60c5\u5929\u8272\u5df2\u4eae\u3002 \u3000\u3000\u6885\u82b1\u9707\u60ca\u6781\u5b50\uff0c\u8fd9\u662f\u4ece\u672a\u53d1\u751f\u8fc7\u7684\u4e8b\u60c5\uff0c\u6025\u6025\u4e00\u8dc3\u4e0b\u5e8a\uff0c\u624d\u53d1\u89c9\u5168\u8eab\u672a\u7740\u5bf8\u7f15\u3002 \u3000\u3000\u5979\u5ffd\u7136\u53d1\u89c9\u4e86\u81ea\u5df1\u7684\u80f4\u4f53\uff0c\u7adf\u662f\u5982\u6b64\u7684\u7f8e\u4e3d\uff0c\u66f2\u7ebf\u73b2\u73d1\uff0c\u51b0\u80a4\u7389\u808c\uff0c\u4e0d\u7981\u751f\u51fa\u4e86\u4e00\u80a1\u81ea\u60dc\u81ea\u601c\u7684\u5fc3\u60c5\u2026\u2026\u3002 \u3000\u3000\u8111\u9645\u95f4\u53c8\u6cdb\u73b0\u51fa\u7530\u7389\u7684\u5f71\u5b50\uff0c\u90a3\u5e26\u7740\u7a1a\u6c14\u7684\u82f1\u633a\u7537\u4eba\uff0c\u4e00\u5bb5\u60c5\u4e1d\uff0c\u7adf\u7136\u6df1\u6df1\u7684\u76d8\u5c45\u5728\u5979\u7684\u5fc3\u91cc\u3002 \u3000\u3000\u5979\u7f13\u7f13\u7a7f\u4e0a\u8863\u670d\uff0c\u63a9\u4e0a\u5bc6\u5ba4\uff0c\u6b65\u51fa\u4e86\u6885\u82b1\u5385\u3002 \u3000\u3000\u8fd9\u91cc\u662f\u5979\u8fce\u5ba2\u966a\u9152\u7684\u5730\u65b9\uff0c\u90a3\u5ea7\u5bc6\u5ba4\uff0c\u662f\u5979\u903c\u95ee\u53e3\u4f9b\uff0c\u5904\u7f6e\u654c\u4eba\u7684\u6240\u5728\uff0c\u4e0d\u662f\u5979\u7684\u5367\u5ba4\u3002 \u3000\u3000\u6885\u82b1\u4ece\u6ca1\u6709\u5728\u90a3\u5bc6\u5ba4\u4e2d\u4f11\u606f\u8fc7\uff0c\u6628\u591c\uff0c\u4e5f\u662f\u7b2c\u4e00\u6b21\uff0c\u7761\u90a3\u5f20\u5c0f\u5c0f\u7684\u6728\u5e8a\u3002 \u3000\u3000\u6668\u98ce\u62c2\u9762\uff0c\u968f\u65f6\u90fd\u82b1\u9999\u6d6e\u52a8\uff0c\u53ea\u662f\u6e05\u6668\u7684\u82b1\u6c14\uff0c\u66f4\u89c9\u903c\u4eba\u2026\u2026\u201d \u3000\u3000\u6885\u82b1\u4f38\u5c55\u4e86\u4e00\u53cc\u81c2\uff0c\u6446\u52a8\u4e00\u4e0b\u67f3\u8170\uff0c\u987f\u89c9\u7cbe\u795e\u4e00\u723d\u3002 \u3000\u3000\u201c\u662f\u6885\u82b1\u59d1\u5a18\u4e48\uff1f\u201d\u6885\u82b1\u5fc3\u795e\u4e00\u9707\uff0c\u8f6c\u5934\u770b\u53bb\uff0c\u53ea\u89c1\u603b\u7ba1\u9093\u98de\u6b63\u7f13\u6b65\u884c\u4e86\u8fc7\u6765\uff0c\u4e00\u5bf9\u78a7\u5149\u95ea\u52a8\u7684\u9b54\u773c\uff0c\u4e0d\u505c\u7684\u4e0a\u4e0b\u6253\u91cf\uff0c\u4f3c\u662f\u8981\u4ece\u6885\u82b1\u7684\u8eab\u4e0a\uff0c\u6355\u6349\u4f4f\u4e00\u4e9b\u4ec0\u4e48\uff1f \u3000\u3000\u201c\u603b\u7ba1\u65e9\u554a\uff01\u2026\u2026\u201d\u6885\u82b1\u9547\u9759\u4e00\u4e0b\u5fc3\u795e\uff0c\u6324\u51fa\u4e00\u8138\u7b11\u5bb9\uff0c\u9053\uff0c\u201c\u5c0f\u5a62\u6628\u591c\u4f8d\u5ba2\uff0c\u591a\u996e\u4e86\u51e0\u676f\u9152\uff0c\u7adf\u5728\u6885\u82b1\u5385\u4e2d\u7761\u719f\uff0c\u521a\u521a\u9192\u6765\uff0c\u6b63\u51c6\u5907\u56de\u623f\u4f11\u606f\u3002\u201d \u3000\u3000\u201c\u4f60\u5403\u9189\u9152\u4e86\u2026\u2026\u201d\u9093\u98de\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u56db\u5b63\u82b1\u5a62\u4e2d\uff0c\u5c5e\u4f60\u7684\u6210\u5c31\u6700\u9ad8\u3001\u600e\u4f1a\u88ab\u4eba\u704c\u9189\u5462\uff1f\u201d \u3000\u3000\u6885\u82b1\u795e\u8272\u4e00\u51b7\uff0c\u9053\uff1a\u201c\u603b\u7ba1\u5bf9\u6211\u4e86\u89e3\u591a\u5c11\u5462\uff1f\u5076\u800c\u653e\u91cf\uff0c\u591a\u996e\u4e86\u51e0\u676f\u9152\uff0c\u4e5f\u4e0d\u7b97\u4ec0\u4e48\u5927\u4e8b\u554a\uff01\u201d \u3000\u3000\u9093\u98de\u8138\u8272\u4e00\u53d8\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u770b\u6765\uff0c\u6211\u8fd9\u4e2a\u603b\u7ba1\uff0c\u5b9e\u5728\u662f\u7ba1\u4e0d\u4e86\u4ec0\u4e48\uff1f\u4f60\u4eec\u5e73\u5e38\u5bf9\u6211\u7684\u656c\u754f\uff0c\u5168\u90fd\u662f\u88c5\u51fa\u6765\u7684\uff0c\u662f\u5417\uff1f\u201d \u3000\u3000\u6885\u82b1\u9053\uff1a\u201c\u603b\u7ba1\u65e2\u662f\u5982\u6b64\u6765\u60f3\uff0c\u5c0f\u5a62\u4e5f\u662f\u65e0\u53ef\u5948\u4f55\u4e86\uff01\u201d \u3000\u3000\u795e\u60c5\u51b7\u6de1\uff0c\u786e\u7136\u6ca1\u6709\u628a\u603b\u7ba1\u770b\u5728\u773c\u4e2d\u3002 \u3000\u3000\u201c\u6885\u82b1\uff0c\u4f60\u597d\u5927\u7684\u80c6\u5b50\u2026\u2026\u201d\u97e9\u9738\u7a81\u7136\u51fa\u73b0\uff0c\u5927\u6b65\u884c\u4e86\u8fc7\u6765\uff0c\u9053\uff0c\u201c\u9876\u649e\u603b\u7ba1\uff0c\u8be5\u5f53\u4f55\u7f6a\uff1f\u201d \u3000\u3000\u6885\u82b1\u62ac\u5934\u77a7\u77a7\u97e9\u9738\uff0c\u51b7\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u97e9\u526f\u603b\u7ba1\uff0c\u662f\u4e13\u95e8\u7ba1\u7406\u6211\u4eec\u7684\u9876\u5934\u4e0a\u53f8\u4e86\uff1f\u201d \u3000\u3000\u97e9\u9738\u9053\uff1a\u201c\u4f60\u4eec\u8fde\u603b\u7ba1\u90fd\u4e0d\u653e\u5728\u773c\u4e2d\uff0c\u90a3\u91cc\u8fd8\u4f1a\u628a\u6211\u8fd9\u4e2a\u526f\u603b\u7ba1\u6446\u5728\u5fc3\u4e0a\u3002\u201d \u3000\u3000\u201c\u6211\u4eec\u542c\u547d\u884c\u4e8b\uff0c\u53ef\u662f\u4e5f\u8981\u6709\u4e00\u4e2a\u9650\u5ea6\u2026\u2026\u201d\u6885\u82b1\u9053\uff0c\u201c\u4e8b\u5b9e\u4e0a\uff0c\u4f60\u4eec\u53ea\u662f\u4fdd\u62a4\u6211\u4eec\u7684\u4eba\uff0c\u4f60\u4eec\u7684\u8d23\u4efb\uff0c\u662f\u7ef4\u62a4\u6d1b\u9633\u5c45\u7684\u5b89\u5168\uff0c\u4f7f\u5ba2\u4eba\u5f80\u6765\uff0c\u90fd\u80fd\u5e73\u5b89\u65e0\u4e8b\uff0c\u4f7f\u8fd9\u91cc\u80fd\u987a\u5229\u8425\u4e1a\uff0c\u81f3\u4e8e\u7ba1\u7406\u6211\u4eec\u56db\u5b63\u82b1\u5a62\uff0c\u8001\u5b9e\u8bf4\uff0c\u4f60\u4eec\u8fd8\u4e0d\u591f\u8fd9\u4e2a\u8eab\u4efd\uff01\u201d \u3000\u3000\u201c\u770b\u6765\uff0c\u8fd9\u91cc\u590d\u6742\u5f97\u5f88\u2026\u2026\u201d\u9093\u98de\u8bf4\uff0c\u201c\u6211\u53d7\u547d\u4e3a\u6d1b\u9633\u5c45\u603b\u7ba1\u65f6\uff0c\u6388\u6709\u5168\u6743\uff0c\u8d1f\u8d23\u6d1b\u9633\u5c45\u4e2d\u7684\u4e8b\u52a1\uff0c\u5f53\u7136\uff0c\u4e5f\u5305\u62ec\u7ba1\u7406\u6240\u6709\u7684\u4eba\u3002\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u6d1b\u9633\u5c45\u7684\u4eba\uff0c\u662f\u7531\u4f60\u603b\u7ba1\u6307\u6325\uff0c\u90a3\u4e9b\u4eba\u5305\u62ec\u4e86\u7537\u3001\u5973\u4f8d\u5e94\uff0c\u9556\u624b\u3001\u5de5\u4eba\uff0c\u4f46\u4f60\u5374\u7ba1\u4e0d\u5230\u56db\u5b63\u82b1\u5a62\u2026\u2026\u201d \u3000\u3000\u6885\u82b1\u5fae\u5fae\u4e00\u7b11\u53c8\u9053\uff1a\u201c\u6211\u4eec\u6765\u81ea\u5185\u5bab\uff0c\u6839\u672c\u662f\u53e6\u4e00\u4e2a\u7cfb\u7edf\uff0c\u6211\u4eec\u6765\u8fd9\u91cc\uff0c\u662f\u5e2e\u4f60\u4eec\u7684\u5fd9\uff0c\u5e94\u8be5\u662f\u8d35\u5bbe\u7684\u8eab\u4efd\uff0c\u4f60\u53ef\u4ee5\u4e0d\u76f8\u4fe1\u6211\u7684\u8bdd\uff0c\u4e0d\u8fc7\uff0c\u603b\u7ba1\u53ef\u4ee5\u8bf7\u793a\u554a\uff01\u4f60\u4f1a\u5f88\u5feb\u5f97\u5230\u7b54\u590d\u3002\u201d \u3000\u3000\u97e9\u9738\u5fc3\u4e2d\u4e00\u52a8\uff0c\u5fd6\u9053\uff1a\u201c\u8fd9\u4e2a\u7ec4\u5408\u679c\u7136\u5404\u6709\u7cfb\u7edf\uff0c\u53ea\u662f\u4e0d\u77e5\u9053\u4ed6\u4eec\u5728\u54ea\u4e00\u4e2a\u9636\u5c42\u5408\u800c\u4e3a\u4e00\u2026\u2026\u3002\u201d \u3000\u3000\u5fc3\u4e2d\u5ff5\u5934\u8f6c\u52a8\uff0c\u53e3\u4e2d\u5374\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6885\u82b1\uff0c\u4e5f\u8bb8\uff0c\u6211\u548c\u603b\u7ba1\u90fd\u662f\u540e\u671f\u6295\u6548\u7684\u4eba\u7269\uff0c\u4e0d\u662f\u4f60\u4eec\u6b63\u7edf\u5f1f\u5b50\uff0c\u4f46\u6d1b\u9633\u5c45\u662f\u4e00\u4e2a\u5355\u4f4d\uff0c\u603b\u8be5\u6709\u4e00\u4e2a\u9886\u5bfc\u4eba\u7269\uff0c\u6240\u8c13\u5929\u65e0\u4e8c\u65e5\uff0c\u5982\u82e5\u90fd\u50cf\u4f60\u6885\u82b1\u59d1\u5a18\u4e00\u6837\uff0c\u8d85\u7136\u4e8e\u5916\uff0c\u72ec\u6811\u4e00\u5e1c\uff0c\u6210\u4f55\u4f53\u7edf\uff1f\u201d \u3000\u3000\u6885\u82b1\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u8bf4\u7684\u6709\u7406\uff0c\u6240\u4ee5\uff0c\u6211\u4eec\u5df2\u5c3d\u91cf\u548c\u4f60\u4eec\u914d\u5408\uff0c\u5e73\u65e5\u6211\u4eec\u5bf9\u603b\u7ba1\u548c\u4f60\u97e9\u526f\u603b\u7ba1\u8868\u73b0\u7684\u656c\u610f\uff0c\u5927\u6982\u8fd8\u8fc7\u5f97\u53bb\u5427\uff01\u6b64\u523b\uff0c\u6ca1\u6709\u5916\u4eba\uff0c\u628a\u5f7c\u6b64\u7684\u8eab\u4efd\u8bf4\u4e2a\u660e\u767d\u3001\u4ee5\u540e\uff0c\u5927\u5bb6\u624d\u80fd\u5904\u7684\u66f4\u4e3a\u548c\u7766\u3002\u201d \u3000\u3000\u97e9\u9738\u65e0\u6cd5\u4f5c\u4e3b\u4e86\uff0c\u56de\u5934\u671b\u7740\u9093\u98de\u3002 \u3000\u3000\u9093\u98de\u53f9\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6211\u4f1a\u95ee\u660e\u767d\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u65e9\u77e5\u8fd9\u6d1b\u9633\u5c45\u4e2d\u7684\u603b\u7ba1\uff0c\u662f\u8fd9\u6837\u4e00\u4e2a\u7a9d\u56ca\u7684\u89d2\u8272\uff0c\u9093\u67d0\u4eba\u4e5f\u4e0d\u4f1a\u63a5\u53d7\u8fd9\u4e2a\u5de5\u4f5c\u4e86\u3002\u201d \u3000\u3000\u201c\u603b\u7ba1\u4e5f\u4e0d\u7528\u5984\u81ea\u83f2\u8584\uff0c\u90a3\u4e9b\u9556\u624b\uff0c\u90fd\u662f\u4f18\u79c0\u7684\u6b63\u7edf\u5f1f\u5b50\uff0c\u2026\u2026\u201d\u6885\u82b1\u8bf4\uff0c\u201c\u90a3\u4e9b\u4f8d\u5e94\u4f19\u8ba1\uff0c\u4e5f\u90fd\u6709\u7740\u76f8\u5f53\u9ad8\u660e\u7684\u8eab\u624b\uff0c\u8fd9\u4e9b\u529b\u91cf\uff0c\u8db3\u53ef\u4ee5\u62b5\u4e0a\u6b66\u6797\u4e2d\u4e00\u4e2a\u4e2d\u7b49\u95e8\u6237\u3002\u201d \u3000\u3000\u201c\u4f60\u5e74\u7eaa\u4e0d\u5927\uff0c\u5bf9\u6c5f\u6e56\u4e2d\u7684\u4e8b\u52a1\u5012\u662f\u77e5\u9053\u7684\u5f88\u591a\u554a\uff01\u2026\u2026\u201d\u97e9\u9738\u51b7\u5389\u7684\u8138\u4e0a\uff0c\u6cdb\u8d77\u96be\u5f97\u4e00\u89c1\u7684\u7b11\u5bb9\uff0c\u63a5\u9053\uff1a\u201c\u6b64\u540e\uff0c\u8fd8\u671b\u59d1\u5a18\u591a\u591a\u5408\u4f5c\u3002\u201d \u3000\u3000\u201c\u6211\u4f1a\u7684\u2026\u2026\u201d\u6885\u82b1\u5fae\u7b11\u9053\uff1a\u201c\u50cf\u8fc7\u53bb\u4e00\u6837\uff0c\u4fdd\u8bc1\u4f7f\u4f60\u97e9\u526f\u603b\u7ba1\u6ee1\u610f\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u59d1\u5a18\u8bf7\u53bb\u4f11\u606f\u5427\uff01\u2026\u2026\u201d\u97e9\u9738\u8f6c\u5411\u603b\u7ba1\u9093\u98de\u8bf4\u9053\uff1a\u201c\u5c51\u4e0b\u56de\u6765\u65f6\uff0c\u5929\u8fd8\u672a\u4eae\uff0c\u5374\u53d1\u73b0\u4e00\u4e2a\u5e74\u8f7b\u4eba\u5728\u8fd9\u91cc\u8d70\u6765\u8d70\u53bb\uff0c\u5c51\u4e0b\u628a\u4ed6\u6293\u4f4f\u4e86\u3002\u201d \u3000\u3000\u9093\u98de\u9053\uff1a\u201c\u95ee\u6e05\u695a\u4ed6\u7684\u8eab\u4efd\uff1f\u5982\u65e0\u53ef\u7591\uff0c\u5c31\u653e\u4e86\u4ed6\uff0c\u5982\u662f\u53ef\u7591\uff0c\u5c31\u628a\u4ed6\u6697\u4e2d\u5904\u51b3\u6389\u4e86\u3002\u201d \u3000\u3000\u201c\u662f\uff01\u201d \u3000\u3000\u97e9\u9738\u606d\u606d\u656c\u656c\u7684\u5e94\u4e86\u4e00\u58f0\u3002 \u3000\u3000\u9093\u98de\u9053\uff1a\u201c\u7b49\u4e00\u4e0b\uff0c\u54b1\u4eec\u4e00\u8d77\u65e9\u9910\u3002\u201d \u3000\u3000\u201c\u5c5e\u4e0b\u9075\u547d\u3002\u201d \u3000\u3000\u97e9\u9738\u8868\u73b0\u51fa\u4e86\u65e0\u6bd4\u5730\u606d\u8c28\u3002 \u3000\u3000\u9093\u98de\u8f6c\u8eab\u800c\u53bb\u3002 \u3000\u3000\u97e9\u9738\u4e5f\u4e3e\u6b65\u5411\u524d\u884c\u53bb\u3002 \u3000\u3000\u4f46\u89c9\u5fae\u98ce\u98d2\u7136\uff0c\u6885\u82b1\u7a81\u7136\u51fa\u73b0\u97e9\u9738\u7684\u8eab\u524d\u3002 \u3000\u3000\u201c\u59d1\u5a18\u597d\u9ad8\u660e\u7684\u8eab\u624b\uff0c\u5728\u4e0b\u81ea\u53f9\u5f17\u5982\u4e86\uff1f\u201d \u3000\u3000\u201c\u526f\u603b\u7ba1\u8a00\u91cd\u4e86\u2026\u2026\u201d\u6885\u82b1\u966a\u4e0a\u4e00\u8138\u5fae\u7b11\uff0c\u9053\uff0c\u201c\u526f\u603b\u7ba1\u6293\u4e86\u4e00\u4e2a\u5e74\u8f7b\u4eba\uff1f\u201d \u3000\u3000\u201c\u662f\u554a\uff01\u4ed6\u5929\u4e0d\u4eae\u5c31\u5728\u8fd9\u91cc\u8f6c\u6765\u8f6c\u53bb\uff0c\u5f62\u8ff9\u5341\u5206\u53ef\u7591\uff1f\u201d \u3000\u3000\u201c\u4ed6\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u201d\u6885\u82b1\u4f4e\u58f0\u7684\u95ee\u3002 \u3000\u3000\u201c\u6211\u8fd8\u6ca1\u7a7a\u95ee\u4ed6\u2026\u2026\u201d\u97e9\u9738\u5947\u9053\uff1a\u201c\u6885\u82b1\u59d1\u5a18\u600e\u4f1a\u5173\u5fc3\u8fd9\u7b49\u5c0f\u4e8b\uff1f\u201d \u3000\u3000\u201c\u53ea\u662f\u968f\u4fbf\u95ee\u95ee\uff0c\u2026\u2026\u201d\u6885\u82b1\u9053\uff0c\u201c\u4ed6\u957f\u7684\u4ec0\u4e48\u6837\u5b50\uff1f\u201d \u3000\u3000\u201c\u597d\u50cf\u5f88\u5e74\u8f7b\uff0c\u8001\u5b9e\u8bf4\uff0c\u6211\u4e00\u4e0b\u5c31\u70b9\u4e86\u4ed6\u7684\u7a74\u9053\uff0c\u6839\u672c\u8fd8\u6ca1\u6709\u4ed4\u7ec6\u7684\u770b\u4ed6\u4e00\u773c\u2026\u2026\u201d \u3000\u3000\u201c\u73b0\u5728\u4f55\u5904\uff1f\u201d\u6885\u82b1\u6025\u6025\u5730\u95ee\u3002 \u3000\u3000\u201c\u88ab\u6211\u5173\u8d77\u6765\u4e86\u2026\u2026\u201d\u97e9\u9738\u9053\uff0c\u201c\u7b49\u4e00\u4e0b\uff0c\u6211\u4f1a\u53bb\u95ee\u95ee\u4ed6\uff0c\u5982\u679c\u4e0d\u662f\u6c5f\u6e56\u4e2d\u4eba\uff0c\u6211\u5c31\u7acb\u523b\u653e\u4e86\u4ed6\u3002\u201d \u3000\u3000\u201c\u526f\u603b\u7ba1\uff0c\u6211\u53ef\u4e0d\u53ef\u4ee5\u53bb\u770b\u770b\u4ed6\uff1f\u201d \u3000\u3000\u201c\u8fd9\u6837\u5427\uff01\u65e9\u9910\u4e4b\u540e\uff0c\u6211\u6d3e\u4eba\u628a\u4ed6\u9001\u5230\u4f60\u90a3\u91cc\u53bb\uff0c\u5982\u679c\u59d1\u5a18\u4e0d\u8ba4\u8bc6\uff0c\u518d\u4ea4\u8fd8\u7ed9\u6211\u5904\u51b3\u4ed6\u3002\u201d \u3000\u3000\u201c\u5982\u662f\u6211\u8ba4\u8bc6\u5462\uff1f\u201d\u6885\u82b1\u6cdb\u4e0a\u4e86\u4e24\u988a\u7f9e\u7ea2\uff0c\u5782\u4e0b\u773c\u5e18\uff0c\u4e0d\u6562\u770b\u5411\u97e9\u9738\u3002 \u3000\u3000\u201c\u90a3\u5f53\u7136\u7531\u59d1\u5a18\u53d1\u843d\u4e86\u2026\u2026\u201d\u97e9\u9738\u5fae\u7b11\u8bf4\uff0c\u201c\u59d1\u5a18\u662f\u6211\u4eec\u6d1b\u9633\u5c45\u4e2d\u7684\u8d35\u5bbe\u554a\uff01\u201d \u3000\u3000\u201c\u97e9\u526f\u603b\u7ba1\uff0c\u4e0d\u7ba1\u4ed6\u662f\u4e0d\u662f\u6211\u8ba4\u8bc6\u7684\u4eba\uff0c\u6885\u82b1\u90fd\u4f1a\u8bb0\u7740\u4f60\u8fd9\u4efd\u60c5\u610f\uff0c\u65e5\u540e\uff0c\u6211\u5fc5\u4ee5\u5343\u500d\u7684\u6069\u60c5\u62a5\u507f\u3002\u201d \u3000\u3000\u201c\u5c0f\u4e8b\u4e00\u6869\uff0c\u5728\u4e0b\u548c\u603b\u7ba1\u4e00\u8d77\u5403\u8fc7\u65e9\u9910\u540e\uff0c\u7acb\u523b\u5c31\u628a\u4eba\u9001\u53bb\uff0c\u5305\u7ba1\u4ed6\u6beb\u53d1\u65e0\u4f24\u3002\u201d \u3000\u3000\u6885\u82b1\u53e3\u9f7f\u542f\u52a8\uff0c\u6b32\u8a00\u53c8\u6b62\uff0c\u8eac\u8eab\u4e00\u793c\uff0c\u8f6c\u8eab\u884c\u53bb\u3002 \u3000\u3000\u60c5\u8fd9\u4e1c\u897f\uff0c\u76f4\u53eb\u4eba\u751f\u6b7b\u76f8\u8bb8\uff0c\u50cf\u6885\u82b1\u8fd9\u6837\u53d7\u8fc7\u4e25\u683c\u8bad\u7ec3\u7684\u6740\u624b\u4eba\u7269\uff0c\u4e00\u65e6\u52a8\u4e86\u60c5\uff0c\u7adf\u4e5f\u548c\u4e00\u822c\u7684\u5c11\u5973\u4e00\u6837\uff0c\u7f9e\u7f9e\u7b54\u7b54\uff0c\u6b32\u8bed\u53c8\u6b62\u3002 \u3000\u3000\u97e9\u9738\u8d76\u5230\u9093\u98de\u7684\u4f4f\u5904\uff0c\u9093\u98de\u5df2\u6446\u4e0a\u4e86\u65e9\u70b9\u7b49\u5019\u3002 \u3000\u3000\u201c\u97e9\u67d0\u7ea6\u8bf7\u7684\u51e0\u4f4d\u670b\u53cb\uff0c\u90fd\u5df2\u7b54\u5141\u6765\u6b64\u52a9\u603b\u7ba1\u4e00\u81c2\u4e4b\u529b\u3002\u201d \u3000\u3000\u9093\u98de\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u90fd\u662f\u4e9b\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u201c\u4e03\u5b9d\u548c\u5c1a\u2026\u2026\u201d\u97e9\u9738\u8f7b\u8f7b\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff0c\u201c\u60f3\u4e0d\u5230\u4ed6\u7adf\u7136\u4e00\u53e3\u7b54\u5e94\u3002\u201d \u3000\u3000\u9093\u98de\u4f3c\u662f\u88ab\u706b\u70e7\u4e86\u4e00\u4e0b\uff0c\u7a81\u7136\u8df3\u4e86\u8d77\u6765\uff0c\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u4e0d\u592a\u597d\u5427\uff01\u9053\u4e0d\u540c\u600e\u76f8\u4e3a\u8c0b\uff1f\u4e03\u5b9d\u548c\u5c1a\u662f\u65b9\u65b9\u6b63\u6b63\u7684\u4eba\u7269\uff0c\u613f\u610f\u6765\u6d1b\u9633\u5c45\uff0c\u53ea\u6015\u662f\u522b\u5177\u7528\u5fc3\uff1f\u201d \u3000\u3000\u201c\u81f3\u5c11\uff0c\u4ed6\u4f1a\u5168\u529b\u548c\u6211\u4eec\u5408\u4f5c\uff0c\u552f\u603b\u7ba1\u4e4b\u547d\u662f\u4ece\u2026\u2026\u201d\u97e9\u9738\u8bf4\uff0c\u201c\u8fd9\u4e9b\u5e74\u6c5f\u6e56\u4eba\u65e9\u5df2\u7ecf\u5fd8\u5374\u672c\u6765\uff0c\u9ed1\u3001\u767d\u4e24\u9053\uff0c\u4e5f\u5df2\u7ecf\u6df7\u6dc6\u4e0d\u6e05\uff0c\u4e03\u5b9d\u548c\u5c1a\u7b54\u5e94\u6613\u5bb9\u6539\u626e\uff0c\u63a9\u53bb\u548c\u5c1a\u7684\u8eab\u4efd\uff0c\u663e\u7136\u662f\uff0c\u4ed6\u6ca1\u6709\u4ec0\u4e48\u5730\u65b9\u53ef\u53bb\u3002 \u3000\u3000\u603b\u7ba1\u4e0d\u59a8\u4ed4\u7ec6\u7684\u60f3\u60f3\uff0c\u6d1b\u9633\u5c45\u4e2d\u6709\u90a3\u4e00\u80a1\u529b\u91cf\u53ef\u4e3a\u6211\u4eec\u6240\u7528\uff01\u642c\u7740\u6307\u5934\u6570\u4e00\u6570\uff0c\u53ea\u6709\u6211\u4eec\u4e24\u4e2a\u4eba\uff0c\u4e03\u5b9d\u548c\u5c1a\u548c\u4ed6\u7684\u51e0\u4f4d\u670b\u53cb\uff0c\u613f\u610f\u6295\u6548\u603b\u7ba1\uff0c\u81f3\u5c11\u6211\u4eec\u5728\u6d1b\u9633\u5c45\u4e2d\uff0c\u53ef\u4ee5\u5360\u5f97\u7a33\u4e00\u4e9b\uff0c\u603b\u7ba1\u4e5f\u6709\u53ef\u4ee5\u6d3e\u4e0a\u7528\u573a\u7684\u5fc3\u8179\u4eba\u4e86\u3002\u201d \u3000\u3000\u201c\u8fd8\u6709\u4e9b\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u201c\u6211\u6ca1\u6709\u4ed4\u7ec6\u5730\u95ee\uff0c\u4e0d\u8fc7\uff0c\u4e03\u5b9d\u548c\u5c1a\u8bf4\u7684\u5f88\u660e\u767d\uff0c\u4ed6\u4eec\u90fd\u6709\u76f8\u5f53\u7684\u8eab\u4efd\uff0c\u4e5f\u4e0d\u4fbf\u4ee5\u771f\u9762\u76ee\u6295\u5165\u6d1b\u9633\u5c45\u4e2d\uff0c\u6240\u4ee5\uff0c\u4ed6\u4eec\u90fd\u7ecf\u8fc7\u6613\u5bb9\uff0c\u6216\u662f\u6234\u4e0a\u4eba\u76ae\u9762\u5177\uff0c\u4f46\u4ed6\u4eec\u5f97\u5bf9\u603b\u7ba1\u7edd\u5bf9\u5fe0\u8bda\u2026\u2026\u201d\u97e9\u9738\u8bf4\uff0c\u201c\u6211\u544a\u8bc9\u4ed6\u4eec\u8bf4\u8981\u8bf7\u793a\u603b\u7ba1\u624d\u80fd\u51b3\u5b9a\uff0c\u4ed6\u4eec\u8fd8\u5728\u7b49\u6211\u56de\u97f3\uff1f\u4e0d\u8fc7\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u8fc7\u4ec0\u4e48\uff1f\u2026\u2026\u201d\u9093\u98de\u4f4e\u58f0\u95ee\u9053\uff0c\u201c\u4ed6\u4eec\u662f\u4e0d\u662f\u9644\u6709\u6761\u4ef6\uff1f\u201d \u3000\u3000\u201c\u6761\u4ef6\u5012\u662f\u6ca1\u6709\uff0c\u4f46\u4ed6\u4eec\u660e\u767d\u4e0d\u8bba\u5982\u4f55\u6613\u5bb9\uff0c\u4e5f\u9003\u4e0d\u8fc7\u603b\u7ba1\u4e00\u53cc\u795e\u76ee\uff0c\u4ed6\u4eec\u90fd\u77e5\u9053\u603b\u7ba1\u6709\u8fc7\u76ee\u4e0d\u5fd8\u4e4b\u80fd\uff0c\u6709\u6d1e\u7a7f\u6613\u5bb9\u6539\u626e\u7684\u773c\u529b\uff0c\u2026\u2026\u201d\u97e9\u9738\u4e5f\u4f4e\u58f0\u56de\u7b54\uff0c\u201c\u4ed6\u4eec\u51c6\u5907\u548c\u603b\u7ba1\u89c1\u9762\u4e4b\u540e\uff0c\u81ea\u767d\u8eab\u4efd\u3002\u201d \u3000\u3000\u8fd9\u9876\u9ad8\u5e3d\u5b50\u4e0d\u7740\u75d5\u8ff9\uff0c\u542c\u5f97\u9093\u98de\u5fc3\u4e2d\u8212\u4f46\u81f3\u6781\uff0c\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u8bdd\u5012\u662f\u4e0d\u9519\uff0c\u5929\u4e0b\u6700\u597d\u7684\u6613\u5bb9\u624b\u6cd5\uff0c\u4e5f\u9003\u4e0d\u8fc7\u6211\u8fd9\u53cc\u773c\u775b\u3002\u201d \u3000\u3000\u97e9\u9738\u9053\uff1a\u201c\u603b\u7ba1\u662f\u4e0d\u662f\u540c\u610f\u4e86\u5462\uff1f\u201d \u3000\u3000\u201c\u4e07\u4e00\u5f15\u72fc\u5165\u5ba4\uff0c\u65e5\u540e\u8981\u6211\u5982\u4f55\u4ea4\u4ee3\u2026\u2026\u201d\u9093\u98de\u8bf4\uff0c\u201d\u6211\u770b\u8fd9\u4ef6\u4e8b\uff0c\u8fd8\u5f97\u614e\u91cd\u7684\u60f3\u60f3\u3002\u201d \u3000\u3000\u201c\u6740\u738b\u9f99\u5df2\u79cd\u4e0b\u5927\u7978\u4e4b\u56e0\u2026\u2026\u201d\u97e9\u9738\u9053\uff0c\u201c\u73b0\u5728\u6700\u91cd\u8981\u7684\u662f\uff0c\u5982\u4f55\u5efa\u7acb\u8d77\u6211\u4eec\u81ea\u5df1\u7684\u529b\u91cf\uff0c\u4eca\u65e5\u6885\u82b1\u59d1\u5a18\u90a3\u756a\u8bdd\uff0c\u603b\u7ba1\u5f53\u8fd8\u8bb0\u5fc6\u6e05\u695a\uff0c\u6d1b\u9633\u5c45\u5c0f\u5c0f\u4e00\u4e2a\u636e\u70b9\uff0c\u7cfb\u7edf\u5e9e\u6742\uff0c\u5404\u81ea\u4e3a\u653f\uff0c\u4f60\u771f\u6b63\u80fd\u7ba1\u7684\uff0c\u53ea\u6709\u6211\u97e9\u67d0\u4e00\u4eba\uff0c\u6211\u5bf9\u5185\u60c5\u77e5\u9053\u4e0d\u591a\uff0c\u4f46\u603b\u7ba1\u4e5f\u597d\u50cf\u77e5\u9053\u7684\u6709\u9650\uff0c\u6211\u4eec\u968f\u65f6\u6709\u88ab\u89e3\u804c\u62d8\u62bc\u7684\u53ef\u80fd\uff0c\u2026\u2026\u201d \u3000\u3000\u9093\u98de\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4e0d\u9519\u2026\u2026\u201d \u3000\u3000\u97e9\u9738\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\u603b\u7ba1\u4f5c\u4f55\u6253\u7b97\uff0c\u6211\u662f\u7edd\u4e0d\u7518\u675f\u624b\u5c31\u7f1a\uff0c\u5230\u4e86\u8981\u547d\u7684\u65f6\u523b\uff0c\u6211\u4f1a\u5168\u529b\u53cd\u51fb\uff0c\u80fd\u8d70\u5c31\u8d70\uff0c\u8d70\u4e0d\u4e86\uff0c\u4e5f\u8981\u62fc\u640f\u81f3\u6b7b\uff0c\u4e00\u65e6\u88ab\u64d2\uff0c\u90a3\u5c31\u751f\u4e0d\u5982\u6b7b\u4e86\uff01\u201d \u3000\u3000\u201c\u55ef\uff01\u2026\u2026\u201d\u9093\u98de\u9053\uff1a\u201c\u8bf4\u7684\u6709\u7406\uff0c\u5b81\u53ef\u6218\u6b7b\uff0c\u4e0d\u80fd\u88ab\u64d2\uff0c\u6211\u77e5\u9053\u4ed6\u4eec\u5bf9\u5f85\u53db\u9006\u7684\u624b\u6bb5\uff0c\u771f\u53eb\u4eba\u6c42\u751f\u4e0d\u5f97\uff0c\u6c42\u6b7b\u4e0d\u80fd\u2026\u2026\u201d \u3000\u3000\u201c\u6240\u4ee5\u2026\u2026\u201d\u97e9\u9738\u8bf4\uff0c\u201c\u6295\u9760\u603b\u7ba1\u7684\u4eba\uff0c\u80fd\u548c\u6211\u4eec\u5168\u529b\u5408\u4f5c\uff0c\u6700\u4e3a\u91cd\u8981\u3002\u201d \u3000\u3000\u201c\u597d\u5427\uff01\u4f60\u5e26\u4ed6\u4eec\u6765\uff0c\u6211\u4eec\u5c3d\u529b\u63a9\u62a4\u4ed6\u4eec\uff0c\u4f46\u4e5f\u4f5c\u4f5c\u6700\u574f\u7684\u6253\u7b97\u2026\u2026\u201d \u3000\u3000\u201c\u662f\uff01\u2026\u2026\u201d\u97e9\u9738\u7ad9\u8d77\u8eab\u5b50\u9053\uff0c\u201c\u4eca\u665a\u4e0a\uff0c\u6211\u5c31\u5e26\u4ed6\u4eec\u6765\u89c1\u603b\u7ba1\u3002\u201d \u3000\u3000\u9093\u98de\u70b9\u70b9\u5934\uff0c\u97e9\u9738\u544a\u8f9e\u800c\u53bb\u3002 \u3000\u3000\u97e9\u9738\u53d6\u4e0b\u4eba\u76ae\u9762\u5177\uff0c\u6062\u590d\u4e86\u672c\u6765\u9762\u76ee\uff0c\u5e26\u4e09\u5206\u60ca\u602f\u5bb9\u8272\uff0c\u63a8\u5f00\u4e86\u6885\u82b1\u7684\u623f\u95e8\u3002 \u3000\u3000\u6885\u82b1\u4f4f\u7684\u5730\u65b9\u4e0d\u5927\uff0c\u53ea\u6709\u4e00\u623f\u4e00\u5385\uff0c\u5385\u4e2d\u5c0f\u684c\u4e0a\uff0c\u5df2\u6446\u597d\u4e24\u4efd\u70b9\u5fc3\uff0c\u6885\u82b1\u6b63\u5446\u5446\u7684\u5750\u7740\u51fa\u795e\uff0c\u62ac\u5934\u89c1\u7530\u7389\u9762\u5448\u60ca\u602f\u5730\u7684\u7f13\u6b65\u884c\u6765\uff0c\u7acb\u523b\u4e00\u8dc3\u800c\u8d77\uff0c\u5148\u63a2\u9996\u5411\u95e8\u5916\u56db\u4e0b\u77a7\u77a7\uff0c\u624d\u987a\u624b\u63a9\u4e0a\u623f\u95e8\uff0c\u62c9\u7740\u7530\u7389\u5728\u6728\u684c\u65c1\u5750\u4e0b\u3002 \u3000\u3000\u5148\u9650\u60dc\u601c\u7684\u8bf4\uff1a\u201c\u4f60\u771f\u662f\u6211\u547d\u4e2d\u7684\u9b54\u661f\uff0c\u53d7\u82e6\u4e86\u6ca1\u6709\uff1f\u201d \u3000\u3000\u7530\u7389\u6447\u6447\u5934\uff0c\u906d\uff1a\u201c\u6ca1\u6709\uff0c\u6211\u65e9\u6668\u51fa\u53bb\u6563\u6563\u6b65\uff0c\u4e0d\u60f3\u88ab\u4eba\u4e00\u4e0b\u5b50\u70b9\u4e2d\u4e86\u7a74\u9053\uff0c\u6211\u771f\u662f\u592a\u6ca1\u7528\u4e86\u3002\u201d \u3000\u3000\u201c\u5509\uff01\u4f60\u8fde\u4ec0\u4e48\u4eba\u70b9\u4e86\u4f60\u7684\u7a74\u9053\uff0c\u90fd\u6ca1\u6709\u77a7\u6e05\u695a\u5417\uff1f\u201d \u3000\u3000\u201c\u4ed6\u70b9\u7684\u6655\u7a74\uff0c\u6211\u4e00\u4e0b\u5b50\u5c31\u6655\u4e86\u8fc7\u53bb\uff0c\u90a3\u91cc\u8fd8\u77a7\u5230\u4ec0\u4e48\u4eba\u70b9\u6211\u7684\u7a74\u9053\uff0c\u4e0d\u8fc7\uff0c\u9001\u6211\u6765\u8fd9\u91cc\u7684\u4eba\uff0c\u81ea\u79f0\u59d3\u97e9\uff0c\u4ed6\u8bf4\u662f\u4f60\u7684\u670b\u53cb\uff0c\u4f60\u8981\u4ed6\u6551\u6211\u51fa\u6765\u3002\u201d \u3000\u3000\u6885\u82b1\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u8fd9\u4e48\u770b\u6765\uff0c\u4ed6\u8fd8\u662f\u771f\u591f\u670b\u53cb\uff0c\u997f\u4e86\u5427\uff01\u5feb\u4e9b\u5403\u70b9\u4e1c\u897f\uff0c\u6211\u5f97\u60f3\u60f3\u770b\u600e\u4e48\u5b89\u7f6e\u4f60\uff1f\u201d \u3000\u3000\u7530\u7389\u6447\u6254\u5934\uff0c\u9053\uff1a\u201c\u4e0d\u7528\u8d39\u5fc3\u4e86\uff0c\u5403\u8fc7\u4e1c\u897f\uff0c\u6211\u5c31\u8d70\uff01\u201d \u3000\u3000\u201c\u8d70\uff01\u201d\u6885\u82b1\u5446\u4e86\u4e00\u5446\u9053\uff0c\u201c\u5230\u54ea\u91cc\u53bb\uff0c\u4f60\u8981\u5c31\u8fd9\u6837\u79bb\u5f00\u6211\u5417\uff1f\u201d \u3000\u3000\u81ea\u540d\u7530\u7389\u7684\u6c5f\u67ab\u89c1\u6885\u82b1\u4e00\u8138\u60ca\u8be7\u4e4b\u8272\uff0c\u4ea6\u4e3a\u4e4b\u5fc3\u4e2d\u4e00\u52a8\uff0c\u5fd6\u9053\uff1a\u201c\u770b\u6837\u5b50\u8fd9\u4e2b\u5934\u771f\u7684\u4e00\u5934\u683d\u4e86\u8fdb\u53bb\uff0c\u5979\u4e60\u8fc7\u8ff7\u9b42\u5927\u6cd5\uff0c\u600e\u7684\u5b9a\u529b\u7adf\u5982\u6b64\u4e4b\u5dee\uff0c\u8fd8\u662f\u8fd9\u2018\u60d1\u5fc3\u672f\u2019\u592a\u8fc7\u9738\u9053\uff1f\u201d \u3000\u3000\u201c\u6211\u8981\u56de\u5230\u5ba2\u6808\uff0c\u6574\u7406\u884c\u88c5\uff0c\u6211\u4e0d\u80fd\u6c38\u8fdc\u7559\u5728\u957f\u5b89\u3002\u6885\u82b1\u2026\u2026\u201d\u7530\u7389\u8bf4\uff0c\u201c\u5ac1\u7ed9\u6211\u5427\uff01 \u3000\u3000\u8ddf\u6211\u56de\u676d\u5dde\u53bb\uff0c\u6211\u4eec\u5728\u897f\u6e56\u5cb8\u8fb9\uff0c\u76d6\u4e00\u5e62\u623f\u5b50\uff0c\u90a3\u91cc\u98ce\u5149\u660e\u5a9a\uff0c\u5c24\u80dc\u957f\u5b89\u3002\u201d \u3000\u3000\u524d\u9762\u4e24\u53e5\u8bdd\u8bf4\u7684\u4f3c\u751a\u51b3\u7edd\uff0c\u4f46\u540e\u9762\u53c8\u6df1\u60c5\u6b3e\u6b3e\uff0c\u6885\u82b1\u59d1\u5a18\u542c\u5f97\u662f\u53c8\u6c14\u53c8\u6025\uff0c\u5a07\u58f0\u55d4\u9053\uff1a \u3000\u3000\u201c\u4f60\u660e\u660e\u77e5\u9053\u6211\u4e0d\u80fd\u79bb\u5f00\u8fd9\u91cc\uff0c\u5982\u4f55\u80fd\u53bb\u676d\u5dde\uff1f\u201d \u3000\u3000\u201c\u90a3\u8be5\u600e\u4e48\u529e\u5462\uff1f\u2026\u2026\u201d\u7530\u7389\u53f9\u53e3\u6c14\u9053\uff0c\u201c\u603b\u4e0d\u80fd\u8ba9\u6211\u7559\u5728\u8fd9\u91cc\u5427\uff1f\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u4e0d\u80fd\uff1f\u65e2\u662f\u65e0\u6cd5\u5206\u5f00\uff0c\u6211\u53c8\u4e0d\u80fd\u8ddf\u4f60\u5357\u4e0b\u676d\u5dde\uff0c\u4f60\u53ea\u6709\u7559\u5728\u957f\u5b89\u4e86\u3002\u201d \u3000\u3000\u201c\u8fd9\u91cc\u592a\u5371\u9669\uff0c\u968f\u65f6\u90fd\u53ef\u80fd\u88ab\u4eba\u4e00\u4e0b\u6740\u6b7b\uff0c\u6211\u77e5\u9053\u4f60\u53ef\u4ee5\u4fdd\u62a4\u6211\uff0c\u4f46\u4f60\u603b\u4e0d\u80fd\u65f6\u65f6\u90fd\u5b88\u5728\u6211\u7684\u8eab\u8fb9\u5427\uff01\u201d \u3000\u3000\u7530\u7389\u6d41\u9732\u51fa\u65e0\u9650\u7684\u754f\u60e7\u3001\u54c0\u4f24\u3002 \u3000\u3000\u6885\u82b1\u4e24\u9053\u51b7\u5389\u7684\u773c\u795e\uff0c\u76ef\u6ce8\u5728\u7530\u7389\u7684\u8138\u4e0a\uff0c\u51dd\u89c6\u4e86\u826f\u4e45\uff0c\u51b7\u5389\u7684\u76ee\u5149\uff0c\u53d8\u5316\u51fa\u65e0\u9650\u67d4\u60c5\uff0c\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5509\uff01\u4f60\u600e\u4e48\u8fd9\u6837\u7a9d\u56ca\uff0c\u6ca1\u4e00\u70b9\u4e08\u592b\u6c14\u6982\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u53ea\u662f\u4e00\u4e2a\u8d29\u5356\u7ef8\u7f0e\u7684\u5546\u4eba\u2026\u2026\u201d\u7530\u7389\u8bf4\uff0c\u201c\u4e0d\u662f\u95ef\u8361\u6c5f\u6e56\u7684\u7eff\u6797\u597d\u6c49\uff0c\u4f46\u4f60\u4eec\u8fd9\u6d1b\u9633\u5c45\u5374\u4e0d\u50cf\u4e00\u822c\u7684\u6b4c\u575b\u996d\u5e84\uff0c\u597d\u50cf\u5f88\u591a\u4eba\u90fd\u4f1a\u70b9\u7a74\u624b\u6cd5\uff0c\u90a3\u662f\u7b2c\u4e00\u6d41\u7684\u6b66\u529f\uff0c\u5c31\u8bf4\u4f60\u5427\uff0c\u770b\u4e0a\u53bb\u5a07\u5f31\u6e29\u67d4\uff0c\u4f46\u4f60\u5374\u4e00\u8eab\u597d\u6b66\u529f\uff0c\u628a\u6211\u8fd9\u4e2a\u5927\u7537\u4eba\u5168\u4e0d\u653e\u5728\u773c\u4e2d\uff0c\u4e00\u62cd\u624b\u3001\u4e00\u6295\u8db3\uff0c\u5c31\u80fd\u8981\u4e86\u6211\u7684\u547d\u2026\u2026\u201d \u3000\u3000\u6885\u82b1\u5fcd\u4e0d\u4f4f\u55e4\u7684\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u5440\uff01\u4f60\u8fd8\u597d\u610f\u601d\u81ea\u79f0\u662f\u5927\u7537\u4eba\u554a\u2026\u2026\u201d \u3000\u3000\u7530\u7389\u5fae\u5fae\u4e00\u6014\uff0c\u9053\uff1a\u201c\u6211\u4e0d\u662f\u5927\u7537\u4eba\uff0c\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u662f\u2026\u2026\u662f\u5904\u5904\u8981\u4eba\u601c\u60dc\uff0c\u7167\u987e\u7684\u5c0f\u4e08\u592b\u2026\u2026\u201d\u6885\u82b1\u5ffd\u7136\u7eb5\u4f53\u5165\u6000\uff0c\u7d27\u7d27\u62b1\u4f4f\u4e86\u7530\u7389\uff0c\u63a5\u9053\uff1a\u201c\u6211\u4e0d\u8ba9\u4f60\u8d70\uff01\u6211\u820d\u4e0d\u5f97\u79bb\u5f00\u4f60\uff0c\u6211\u81ea\u5df1\u4e5f\u4e0d\u660e\u767d\uff0c\u600e\u4e48\u4f1a\u7231\u4e0a\u4e86\u4f60\u8fd9\u7ef8\u7f0e\u8d29\u5b50\uff0c\u800c\u4e14\uff0c\u52a8\u4e86\u771f\u60c5\uff0c\u7530\u7389\uff0c\u7559\u4e0b\u6765\u966a\u6211\uff0c\u6211\u53d7\u4e0d\u4e86\u79bb\u522b\u7684\u76f8\u601d\u75db\u82e6\u2026\u2026\u201d \u3000\u3000\u201c\u90a3\u5c31\u8ddf\u6211\u8d70\u554a\uff01\u6885\u82b1\uff0c\u4f60\u662f\u6211\u89c1\u5230\u8fc7\u6700\u597d\u5973\u4eba\u2026\u2026\u201d \u3000\u3000\u201c\u53ea\u662f\u6f02\u4eae\u5417\uff1f\u201d\u2026\u2026\u201d\u6885\u82b1\u5728\u7530\u7389\u6000\u4e2d\u4ef0\u8d77\u4e86\u8138\u513f\uff0c\u5a07\u5a9a\u6a2a\u6ea2\uff0c\u52a8\u4eba\u6781\u4e86\uff0c\u7530\u7389\u4e5f\u4e0d\u7981\u5fc3\u795e\u4e00\u8361\uff0c\u8d76\u5fd9\u5438\u53e3\u6c14\uff0c\u9547\u5b9a\u4e0b\u5fc3\u795e\u3002 \u3000\u3000\u201c\u5f53\u7136\uff0c\u4f60\u8fd8\u6709\u5f88\u591a\u52a8\u4eba\u4e4b\u5904\uff0c\u522b\u7684\u5973\u5b69\u5b50\uff0c\u96be\u53ca\u4e07\u4e00\u2026\u2026\u201d\u7530\u7389\u8bf4\uff0c\u201c\u6240\u4ee5\uff0c\u6211\u771f\u7684\u5e0c\u671b\u5e26\u4f60\u56de\u53bb\u2026\u2026\u201d\u6885\u82b1\u63a5\u9053\uff1a\u201c\u6211\u771f\u6709\u90a3\u4e48\u591a\u597d\u5904\u5417\uff1f\u4f46\u6211\u603b\u89c9\u5f97\u6293\u4e0d\u4f4f\u4f60\u7684\u5fc3\u2026\u2026\u201d \u3000\u3000\u201c\u53ef\u80fd\u662f\u6211\u5fc3\u91cc\u6709\u4e9b\u754f\u60e7\uff0c\u6709\u70b9\u6015\u4f60\uff0c\u6240\u4ee5\uff0c\u6709\u4e9b\u8bdd\uff0c\u6211\u4e0d\u6562\u8bf4\u51fa\u6765\u2026\u2026\u201d \u3000\u3000\u201c\u4f60\u5fc3\u4e2d\u60f3\u4ec0\u4e48\uff1f\u5c31\u8bf4\u4ec0\u4e48\u3002\u2026\u2026\u201d\u6885\u82b1\u8bf4\uff0c\u201c\u6211\u597d\u5e0c\u671b\u4f60\u575a\u5f3a\u8d77\u6765\uff0c\u4f60\u53ef\u4ee5\u9a82\u6211\uff0c\u751a\u81f3\u51fa\u624b\u6253\u6211\uff0c\u6211\u90fd\u4e0d\u4f1a\u602a\u4f60\uff0c\u6211\u4f1a\u5b66\u7740\u987a\u4ece\uff0c\u4f5c\u4e2a\u8981\u4e08\u592b\u601c\u7231\u7684\u5c0f\u5ab3\u5987\u2026\u2026\u201d \u3000\u3000\u201c\u90a3\u5c31\u8ddf\u6211\u8d70\u5427\u2026\u2026\u201d\u7530\u7389\u679c\u7136\u632f\u4f5c\u4e86\u8d77\u6765\uff0c\u63a5\u9053\uff1a\u201c\u4ed6\u4eec\u8981\u591a\u5c11\u94f6\u5b50\uff0c\u6211\u53ef\u4ee5\u51c4\u51d1\u770b\uff0c\u5982\u82e5\u4e0d\u591f\uff0c\u6211\u56de\u53bb\u628a\u7530\u4ea7\u623f\u5b50\u4e00\u8d77\u5356\u4e86\uff0c\u53ea\u8981\u80fd\u8d4e\u4f60\u4e4b\u8eab\uff0c\u6211\u662f\u4e0d\u60dc\u6240\u6709\u3002\u201d \u3000\u3000\u6885\u82b1\u6709\u70b9\u611f\u52a8\u5730\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u50bb\u54e5\u54e5\uff0c\u4e0d\u662f\u591a\u5c11\u94f6\u5b50\u7684\u4e8b\uff0c\u800c\u662f\u6211\u6839\u672c\u4e0d\u80fd\u79bb\u5f00\u8fd9\u91cc\uff1f\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u5462\uff1f\u2026\u2026\u201d\u7530\u7389\u63d0\u51fa\u4e86\u5fc3\u4e2d\u7684\u7591\u95ee\u3002 \u3000\u3000\u56e0\u4e3a\u79bb\u5f00\u8fd9\u91cc\uff0c\u6211\u4eec\u90fd\u4e0d\u80fd\u6d3b\u4e0b\u53bb\u2026\u2026\u201d \u3000\u3000\u6885\u82b1\u9ed8\u7136\u8bf4\u9053\uff1a\u201c\u4f1a\u6709\u5f88\u591a\u6b66\u529f\u9ad8\u5f3a\u7684\u4eba\u8ffd\u6740\u6211\u4eec\uff0c\u53ea\u6050\u6015\u8fd8\u6ca1\u6709\u56de\u5230\u676d\u5dde\uff0c\u6211\u4eec\u5df2\u7ecf\u6ca1\u6709\u547d\u4e86\uff1f\u201d \u3000\u3000\u201c\u4f60\u6b66\u529f\u90a3\u4e48\u597d\uff1f\u8fd8\u6709\u4eba\u80fd\u6740\u4e86\u4f60\u2026\u2026\u201d\u7530\u7389\u9053\uff0c\u201c\u90a3\u4e9b\u4eba\u662f\u4e0d\u662f\u6d1b\u9633\u5c45\u7684\u4fdd\u9556\uff1f\u201d \u3000\u3000\u6885\u82b1\u7f13\u7f13\u7ad9\u8d77\u8eab\u5b50\uff0c\u7406\u4e00\u4e0b\u9b13\u8fb9\u6563\u53d1\uff0c\u51b7\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6d1b\u9633\u5c45\u4e2d\u4eba\uff0c\u80fd\u591f\u6740\u6211\u7684\uff0c\u5927\u6982\u8fd8\u6ca1\u6709\uff0c\u552f\u4e00\u7684\u53ef\u80fd\u4eba\u662f\u6d1b\u9633\u697c\u7684\u9752\u51e4\u2026\u2026\u201d \u3000\u3000\u201c\u5979\uff01\u90a3\u4e2a\u8f70\u52a8\u957f\u5b89\u7684\u6b4c\u5973\uff0c\u4e5f\u662f\u90a3\u4e48\u5a07\u5a07\u67d4\u67d4\u7684\uff0c\u8fd9\u600e\u4e48\u53ef\u80fd\u5417\uff1f\u2026\u2026\u201d \u3000\u3000\u6885\u82b1\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\u5979\u662f\u4e0d\u662f\u6709\u80fd\u529b\u6740\u6211\uff0c\u5979\u53ea\u662f\u552f\u4e00\u7684\u53ef\u80fd\u4eba\u9009\uff0c\u6211\u6015\u7684\u662f\u522b\u4eba\u2026\u2026\u201d \u3000\u3000\u5979\u771f\u7684\u8dcc\u5165\u4e86\u7231\u60c5\u7684\u6f29\u6da1\u4e2d\uff0c\u65e0\u610f\u4e2d\u900f\u9732\u51fa\u5f88\u591a\u7684\u9690\u79d8\u3002 \u3000\u3000\u201c\u6211\u5c31\u4e0d\u61c2\u4e86\uff0c\u9664\u4e86\u6d1b\u9633\u5c45\u5916\uff0c\u8fd8\u6709\u4ec0\u4e48\u4eba\u4f1a\u8ffd\u6740\u6211\u4eec\uff0c\u548c\u4ed6\u4eec\u5168\u65e0\u5173\u8fde\u554a\uff01\u201d\u7530\u7389\u6ee1\u8138\u4e0d\u89e3\u7684\u795e\u60c5\u3002 \u3000\u3000\u201c\u6c5f\u6e56\u4e2d\u4e8b\uff0c\u4f60\u61c2\u5f97\u592a\u5c11\u4e86\u2026\u2026\u201d\u6885\u82b1\u53f9\u606f\u4e00\u58f0\u53c8\u9053\uff1a\u201c\u50cf\u6211\u8fd9\u6837\u2014\u8eab\u9ad8\u660e\u6b66\u529f\u7684\u4eba\uff0c\u600e\u4f1a\u6ca6\u843d\u5728\u98ce\u5c18\u4e2d\uff0c\u966a\u9152\u5356\u7b11\uff0c\u4efb\u4eba\u8f7b\u6d6e\u5462\uff1f\u6211\u4eca\u5e74\u5341\u4e5d\u5c81\uff0c\u5374\u5b66\u4e86\u5341\u4e8c\u5e74\u7684\u6b66\u529f\uff0c\u96be\u9053\u4f5c\u4e00\u4e2a\u5356\u7b11\u9152\u5973\uff0c\u8fd8\u8981\u4e0b\u5982\u6b64\u5927\u7684\u672c\u94b1\u5417\uff1f\u201d \u3000\u3000\u7530\u7389\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u662f\u554a\uff01\u6211\u8fd9\u91cc\u771f\u6709\u4e9b\u60f3\u4e0d\u660e\u767d\u5462\uff1f\u201d \u3000\u3000\u201c\u4eba\u751f\u96be\u5f97\u7cca\u6d82\uff0c\u4e0d\u8981\u592a\u660e\u767d\uff0c\u4f46\u6211\u8bf4\u7684\u5374\u662f\u53e5\u53e5\u771f\u5b9e\uff0c\u6211\u4e00\u4eba\u6253\u4e0d\u8fc7\u4ed6\u4eec\uff0c\u4e5f\u8bb8\u8fd8\u80fd\u9003\u8d70\uff01\u4f46\u4f60\u5374\u662f\u6ca1\u6709\u4e00\u70b9\u751f\u5b58\u7684\u673a\u4f1a\u2026\u2026\u201d\u6885\u82b1\u82e6\u7b11\u4e00\u4e0b\u53c8\u9053\uff1a\u201c\u6211\u771f\u7684\u7231\u4e0a\u4e86\u4f60\uff0c\u600e\u80fd\u5fcd\u5fc3\u5bb3\u4f60\uff0c\u4f46\u6211\u53c8\u4e0d\u7518\u5fc3\u5c31\u8fd9\u6837\u5206\u624b\u2026\u2026\u201d \u3000\u3000\u5979\u771f\u7684\u60f3\u4e0d\u51fa\u4e24\u5168\u5176\u7f8e\u7684\u529e\u6cd5\uff0c\u53cc\u7709\u6101\u9501\uff0c\u51dd\u795e\u9759\u601d\u3002 \u3000\u3000\u7530\u7389\u9053\uff1a\u201c\u6211\u4e5f\u6709\u4e9b\u820d\u4e0d\u5f97\u79bb\u5f00\u4f60\u4e86\uff0c\u8fd9\u6837\u5427\uff01\u6211\u5148\u56de\u5ba2\u6808\u53bb\uff0c\u6599\u7406\u4e00\u4e0b\u4e8b\u60c5\uff0c\u660e\u5929\u4e2d\u5348\u6211\u518d\u6765\u770b\u4f60\uff1f\u4e5f\u8bb8\uff0c\u90a3\u65f6\uff0c\u6211\u4eec\u5df2\u7ecf\u60f3\u51fa\u4e86\u89e3\u51b3\u4e8b\u60c5\u7684\u597d\u529e\u6cd5\uff1f\u201d \u3000\u3000\u6885\u82b1\u5fc3\u4e2d\u660e\u767d\uff0c\u7530\u7389\u5b9e\u4e0d\u5b9c\u5728\u6b64\u505c\u7559\uff0c\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u597d\u5427\uff01\u4f46\u4f60\u4e00\u5b9a\u8981\u6765\u554a\uff01\u201d \u3000\u3000\u201c\u7edd\u4e0d\u4f1a\u8d85\u8fc7\u660e\u65e5\u5348\u65f6\u2026\u2026\u201d \u3000\u3000\u7530\u7389\u7f13\u7f13\u7ad9\u8d77\u4e86\u8eab\u5b50\u3002 \u3000\u3000\u201c\u7b49\u4e00\u4e0b\u2026\u2026\u201d\u6885\u82b1\u62c9\u5f00\u5986\u53f0\u7684\u62bd\u5c49\uff0c\u53d6\u51fa\u4e00\u53e0\u94f6\u7968\uff0c\u9053\uff1a\u201c\u8fd9\u662f\u6211\u5b58\u7684\u79c1\u623f\u94b1\uff0c\u4f60\u62ff\u53bb\uff0c\u660e\u5929\u6765\u8fd9\u91cc\uff0c\u4e0d\u8981\u592a\u5c0f\u6c14\uff0c\u80fd\u5728\u6d1b\u9633\u5c45\u4e2d\uff0c\u627e\u6885\u82b1\u59d1\u5a18\u966a\u9152\u7684\u4eba\uff0c\u90fd\u662f\u4e00\u63b7\u5343\u91d1\u7684\u8c6a\u5ba2\u3002\u201d \u3000\u3000\u7530\u7389\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u6211\u600e\u4e48\u80fd\u8981\u4f60\u7684\u94b1\u2026\u2026\u4e0d\u884c\u2026\u2026\u201d \u3000\u3000\u201c\u5fc3\u90fd\u88ab\u4f60\u5077\u53bb\u4e86\uff0c\u8fd8\u5206\u7684\u4ec0\u4e48\u4f60\u6211\uff0c\u91d1\u94f6\u8d22\u5bcc\uff0c\u90fd\u662f\u8eab\u5916\u4e4b\u7269\uff0c\u518d\u8bf4\uff0c\u6211\u7559\u7740\u8fd9\u4e9b\u94b1\uff0c\u4e5f\u6ca1\u6709\u4ec0\u4e48\u7528\u5904\uff0c\u2026\u2026\u201d \u3000\u3000\u6885\u82b1\u62c9\u8d77\u7530\u7389\u7684\u624b\uff0c\u628a\u94f6\u7968\u4ea4\u5230\u7530\u7389\u7684\u624b\u4e2d\uff0c\u63a5\u9053\uff1a\u201c\u5e26\u7740\u5427\uff01\u8fd9\u91cc\u7684\u82b1\u8d39\u592a\u5927\uff0c\u4e0d\u662f\u4f60\u8d1f\u62c5\u5f97\u8d77\uff0c\u66f4\u91cd\u8981\u7684\u662f\uff0c\u6211\u8981\u8ba9\u4f60\u660e\u767d\uff0c\u6885\u82b1\u7684\u60c5\u7231\uff0c\u8eab\u4f53\uff0c\u4e0d\u662f\u7528\u91d1\u94b1\u80fd\u591f\u4e70\u5f97\u5230\u7684\u3002\u201d \u3000\u3000\u7530\u7389\u6570\u4e86\u4e00\u4e0b\uff0c\u5341\u4e09\u5f20\u94f6\u7968\uff0c\u52a0\u8d77\u6765\u6709\u4e94\u4e07\u4e09\u5e72\u591a\u4e24\u94f6\u5b50\uff0c\u771f\u662f\u5413\u4eba\u4e00\u8df3\u7684\u5927\u6570\u5b57\u3002\u6123\u4e86\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u8fd9\u4e48\u591a\uff0c\u6211\u2026\u2026\u6211\u2026\u2026\u201d \u3000\u3000\u201c\u4f60\u4ec0\u4e48\u90fd\u4e0d\u7528\u8bf4\u4e86\uff0c\u597d\u597d\u7684\u60f3\u60f3\u770b\uff0c\u5982\u4f55\u7684\u7559\u5728\u957f\u5b89\uff0c\u80fd\u8ba9\u6211\u6bcf\u5929\u770b\u5230\u4f60\uff0c\u5feb\u4e9b\u8d70\u5427\uff01\u8fd9\u91cc\u4e0d\u5b9c\u591a\u7559\u2026\u2026\u201d \u3000\u3000\u6885\u82b1\u63a8\u7740\u7530\u7389\u79bb\u5f00\uff0c\u987a\u624b\u63a9\u4e0a\u4e86\u623f\u95e8\u3002 \u3000\u3000\u539f\u6765\uff0c\u5979\u542c\u5230\u4f20\u6765\u7684\u6b65\u5c65\u58f0\uff0c\u6709\u4eba\u6b63\u5411\u8fd9\u8fb9\u884c\u6765\u3002 \u3000\u3000\u98ce\u96ea\u5c0f\u9986\u7684\u5730\u4e0b\u5bc6\u5ba4\u4e2d\uff0c\u5750\u6ee1\u4e86\u4eba\uff0c\u4e03\u5ba4\u548c\u5c1a\u3001\u80e1\u840d\u3001\u6bb5\u4e5d\u3001\u80e1\u5143\u3001\u90ed\u5929\u540c\u3001\u5f20\u56db\u59d1\u3001\u541f\u96ea\u3001\u541f\u971c\u3001\u94b1\u7f3a\u3001\u6c5f\u67ab\uff0c\u56f4\u5750\u5728\u4e00\u5f20\u5927\u5706\u684c\u7684\u5468\u56f4\u3002 \u3000\u3000\u6c5f\u67ab\u795e\u60c5\u8083\u7136\uff0c\u76ee\u5149\u5982\u7535\u7684\u73af\u987e\u4e86\u7fa4\u8c6a\u4e00\u773c\uff0c\u9053\uff1a\u201c\u4eca\u665a\u4e0a\uff0c\u6211\u5c31\u5e26\u8bf8\u4f4d\u8fdb\u5165\u6d1b\u9633\u5c45\uff0c\u4e00\u5207\u6309\u9884\u5b9a\u7684\u8ba1\u5212\u884c\u4e8b\uff0c\u9093\u98de\u6740\u4e86\u738b\u9f99\u4e4b\u540e\uff0c\u5df2\u611f\u53d7\u5230\u4eba\u5b64\u52bf\u5355\u7684\u538b\u529b\uff0c\u6211\u76f8\u4fe1\u4ed6\u4f1a\u63a5\u53d7\u6211\u4eec\u7684\u5b89\u6392\u3002\u201d \u3000\u3000\u94b1\u7f3a\u63a5\u9053\uff1a\u201c\u9093\u98de\u4e0d\u662f\u5e73\u5eb8\u4e4b\u8f88\uff0c\u6d1b\u9633\u5c45\u4e2d\uff0c\u4ecd\u6709\u7740\u65e0\u6cd5\u4f30\u7b97\u7684\u5b9e\u529b\uff0c\u4e00\u65e6\u4ed6\u7ffb\u8138\u53d8\u5366\uff0c\u4f60\u4eec\u5c82\u4e0d\u662f\u9677\u8eab\u91cd\u56f4\u4e4b\u4e2d\u3002\u201d \u3000\u3000\u201c\u6240\u4ee5\uff0c\u6211\u51b3\u5b9a\u63d0\u524d\u884c\u52a8\uff0c\u5982\u6ca1\u6709\u9093\u98de\u7684\u5408\u4f5c\uff0c\u6211\u4eec\u5f88\u96be\u5728\u6d1b\u9633\u5c45\u4e2d\u7acb\u8db3\uff0c\u4f46\u4e5f\u6709\u5e94\u53d8\u7684\u6253\u7b97\uff0c\u5982\u82e5\u9093\u98de\u4e0d\u80af\u5408\u4f5c\uff0c\u90a3\u5c31\u7acb\u523b\u884c\u52a8\uff0c\u6740\u4e86\u4ed6\u53d6\u800c\u4ee3\u4e4b\u2026\u2026\u201d\u6c5f\u67ab\u53cc\u76ee\u795e\u5149\u6e5b\u7136\uff0c\u51b7\u5389\u7684\u63a5\u9053\uff1a\u201c\u51ac\u5a62\u6885\u82b1\uff0c\u6b66\u529f\u4e4b\u9ad8\uff0c\u5927\u51fa\u610f\u5916\uff0c\u53ef\u80fd\u662f\u6d1b\u9633\u5c45\u4e2d\u6700\u96be\u5bf9\u4ed8\u7684\u9ad8\u624b\u4e4b\u4e00\u2026\u2026\u201d \u3000\u3000\u201c\u6885\u82b1\u7684\u6b66\u529f\uff0c\u6bd4\u8d77\u79cb\u82b1\u53cc\u5a62\u5982\u4f55\uff1f\u201d\u5f20\u56db\u59d1\u9053\uff0c\u201c\u54b1\u4eec\u8981\u8c0b\u5b9a\u800c\u540e\u52a8\uff0c\u5148\u8981\u5b89\u6392\u597d\u5bf9\u4ed8\u5979\u7684\u529e\u6cd5\u3002\u201d \u3000\u3000\u201c\u9ad8\u8fc7\u79cb\u82b1\u53cc\u5a62\u5f88\u591a\uff0c\u771f\u8981\u5f62\u6210\u654c\u5bf9\u4e4b\u52bf\uff0c\u5148\u8981\u9664\u53bb\u5979\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff0c\u201c\u4e0d\u8fc7\uff0c\u4ed6\u4eec\u6d3e\u7cfb\u7eb7\u6742\uff0c\u6240\u4ee5\uff0c\u738b\u9f99\u88ab\u6740\u4e00\u4e8b\uff0c\u5230\u73b0\u5728\u8fd8\u65e0\u4eba\u8ffd\u67e5\uff0c\u8bf8\u4f4d\u6df7\u5165\u6d1b\u9633\u5c45\u540e\uff0c\u5148\u8981\u4e86\u89e3\u60c5\u52bf\uff0c\u5b9a\u51fa\u5e94\u53d8\u7b56\u7565\uff0c\u6700\u597d\u662f\u56e0\u52bf\u5229\u5bfc\uff0c\u4e0d\u8981\u786c\u62fc\uff0c\u4f46\u5982\uff0c\u4e00\u65e6\u52a8\u624b\uff0c\u5c31\u8981\u4e00\u51fb\u4e2d\u7684\u3002\u201d \u3000\u3000\u201c\u6c5f\u8001\u5f1f\uff0c\u6885\u82b1\u53ea\u662f\u9ad8\u624b\u4e4b\u4e00\uff0c\u5373\u662f\u8bf4\u8fd8\u6709\u66f4\u96be\u5bf9\u4ed8\u7684\u4eba\u4e86\uff0c\u8001\u53eb\u5316\u60f3\u77e5\u9053\uff0c\u90a3\u4e00\u4f4d\u662f\u4ec0\u4e48\u4eba\u7269\uff0c\u662f\u4e0d\u662f\u9b54\u773c\u9093\u98de\uff1f\u201d \u3000\u3000\u201c\u4e0d\u662f\uff01\u94b1\u5144\u8eab\u517c\u6570\u5bb6\u4e4b\u957f\uff0c\u9093\u98de\u5df2\u975e\u654c\u624b\uff0c\u5c0f\u5f1f\u8bf4\u7684\u662f\u6b4c\u5973\u9752\u51e4\uff01\u201d \u3000\u3000\u201c\u9752\u51e4\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u6bd4\u6885\u82b1\u5982\u4f55\uff1f\u201d \u3000\u3000\u201c\u8fd8\u6ca1\u6709\u63a2\u51fa\u5979\u7684\u5e95\u4e8e\uff0c\u4e0d\u8fc7\uff0c\u5979\u81f3\u5c11\u5e94\u8be5\u662f\u6885\u82b1\u540c\u4e00\u7ea7\u7684\u8eab\u624b\uff0c\u4e5f\u53ef\u80fd\u66f4\u4e3a\u9ad8\u660e\u3002\u5947\u602a\u7684\u662f\uff0c\u9752\u51e4\u548c\u56db\u5b63\u82b1\u5a62\uff0c\u53c8\u975e\u540c\u4e00\u7cfb\u7edf\u7684\u4eba\uff0c\u5404\u81ea\u4e3a\u653f\u2026\u2026\u201d\u6c5f\u67ab\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u8fd9\u4e2a\u7ec4\u5408\uff0c\u5f53\u771f\u662f\u590d\u6742\u5f97\u5f88\u554a\uff1f\u201d \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u9053\uff1a\u201c\u5c71\u6709\u5c3d\u5904\uff0c\u6c34\u6709\u6e90\u5934\uff0c\u4ed6\u4eec\u8fd9\u7b49\u4f5c\u6cd5\uff0c\u770b\u4e0a\u53bb\u767e\u82b1\u8000\u773c\u76ee\u8ff7\u4e94\u8272\uff0c\u4f46\u4ed6\u7ed9\u4e86\u6211\u4eec\u5f88\u591a\u53ef\u4e58\u4e4b\u673a\uff0c\u4ee5\u540e\u7684\u4e8b\uff0c\u53ea\u6709\u968f\u673a\u5e94\u53d8\u4e86\uff0c\u597d\u5728\uff0c\u5927\u5bb6\u5728\u4e00\u8d77\uff0c\u968f\u65f6\u53ef\u4ee5\u5546\u91cf\u3002\u201d \u3000\u3000\u201c\u5bf9\uff01\u53d8\u5316\u96be\u6d4b\uff0c\u4e5f\u65e0\u6cd5\u5b9a\u51fa\u5c3d\u5584\u4e4b\u7b56\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u76ee\u5149\u8f6c\u6ce8\u90ed\u5929\u540c\u7684\u8eab\u4e0a\uff0c\u63a5\u9053\uff1a \u3000\u3000\u201c\u97e9\u9738\u7684\u53e3\u97f3\u3001\u4e3e\u6b62\uff0c\u90ed\u5144\u90fd\u719f\u6089\u4e86\u5427\uff1f\u201d \u3000\u3000\u201c\u5728\u4e0b\u5df2\u5c3d\u4e86\u5fc3\u529b\u2026\u2026\u201d\u90ed\u5929\u540c\u9053\uff1a\u201c\u53ea\u6050\u6015\u8fd8\u65e0\u6cd5\u5b66\u5230\u7ef4\u5999\u7ef4\u8096\u3002\u201d \u3000\u3000\u201c\u5927\u59d0\u7684\u4eba\u76ae\u9762\u5177\uff0c\u662f\u5426\u5df2\u7ecf\u5b8c\u6210\uff1f\u201d \u3000\u3000\u201c\u4f60\u63d0\u524d\u4e86\u4e24\u5929\uff0c\u53ea\u6709\u73b0\u5728\u52a0\u5de5\u8d76\u4e00\u4e0b\u4e86\u3002\u201d\u5f20\u56db\u59d1\u7b11\u9053\uff0c\u201c\u5927\u6982\u4e0d\u4f1a\u6709\u8fdd\u65b9\u547d\u5427\uff1f\u201d \u3000\u3000\u201c\u5927\u59d0\u8f9b\u82e6\u2026\u2026\u201d\u6c5f\u67ab\u76ee\u5149\u8f6c\u6ce8\u5230\u94b1\u7f3a\u7684\u8eab\u4e0a\uff0c\u63a5\u9053\uff1a\u201c\u79e6\u516b\u516c\u662f\u5426\u613f\u610f\u5408\u4f5c\uff1f\u201d \u3000\u3000\u201c\u51fa\u5165\u610f\u5916\u7684\u987a\u5229\uff0c\u8001\u53eb\u5316\u53d1\u89c9\u4e86\u6c5f\u6e56\u4e2d\u4eba\u5fc3\u672a\u6b7b\uff0c\u4ed6\u4eec\u65e9\u6709\u4e86\u51c6\u5907\uff0c\u9690\u85cf\u4e86\u4e00\u6279\u5f88\u5927\u7684\u5b9e\u529b\uff0c\u5c11\u6797\u3001\u6b66\u5f53\uff0c\u90fd\u6709\u9ad8\u624b\u53c2\u4e0e\uff0c\u79e6\u516b\u516c\u662f\u9886\u5bfc\u4eba\u7269\u4e4b\u4e00\uff01\u8001\u53eb\u5316\u7597\u6cbb\u597d\u4ed6\u7684\u4f24\u52bf\uff0c\u4ed6\u4e5f\u628a\u8001\u53eb\u5316\u5f15\u4e3a\u77e5\u5df1\uff0c\u5c3d\u5410\u80f8\u4e2d\u4e4b\u5bc6\u3002\u201d \u3000\u3000\u201c\u597d\u6781\u4e86\u2026\u2026\u201d\u6c5f\u67ab\u6709\u4e9b\u632f\u594b\u5730\u9053\uff0c\u201c\u5173\u6d1b\u9053\u4e0a\u5982\u6b64\uff0c\u60f3\u6765\u522b\u5904\u4ea6\u6709\u5fc3\u6000\u5fe0\u4e49\u7684\u6c5f\u6e56\u540c\u9053\uff0c\u5728\u6697\u4e2d\u7ed3\u5408\u6210\u53cd\u6297\u529b\u91cf\uff0c\u543e\u9053\u4e0d\u5b64\u77e3\uff01\u53ea\u8981\u987a\u5229\u5730\u638c\u63e1\u4e86\u6d1b\u9633\u5c45\uff0c\u54b1\u4eec\u5c31\u6709\u53cd\u51fb\u7684\u673a\u4f1a\u4e86\uff0c\u81f3\u5c11\uff0c\u53ef\u4ee5\u63ed\u5f00\u4ed6\u4eec\u4e00\u5c42\u795e\u79d8\u5916\u8863\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u9053\uff1a\u201c\u5144\u5f1f\uff0c\u4f60\u518d\u628a\u5206\u6d3e\u5404\u4eba\u7684\u5de5\u4f5c\u8bf4\u660e\u4e00\u4e0b\uff0c\u5927\u5bb6\u518d\u4f5c\u4e00\u4e9b\u51c6\u5907\uff0c\u5c31\u53ef\u4ee5\u884c\u52a8\u4e86\uff0c\u6211\u5148\u544a\u9000\uff0c\u628a\u4f60\u8981\u7684\u9762\u5177\u8d76\u51fa\u6765\u3002\u201d \u3000\u3000\u201c\u5927\u59d0\uff0c\u5c0f\u5f1f\u7684\u5fc3\u4e2d\u611f\u6fc0\uff0c\u533a\u2026\u2026\u201d \u3000\u3000\u201c\u611f\u6fc0\u6211\uff0c\u6446\u5728\u5fc3\u91cc\u5c31\u597d\uff0c\u5012\u662f\u5927\u59d0\u6709\u4ef6\u4e8b\u6ca1\u6709\u5e2e\u4f60\u529e\u597d\u2026\u2026\u201d\u5f20\u56db\u59d1\u53f9\u606f\u4e00\u58f0\u53c8\u9053\uff1a \u3000\u3000\u201c\u541f\u96ea\u3001\u541f\u971c\u7684\u5feb\u901f\u6613\u5bb9\u624b\u6cd5\uff0c\u5c1a\u672a\u5b66\u597d\u2026\u2026\u201d \u3000\u3000\u201c\u5f20\u59e8\uff01\u597d\u50cf\u6211\u5df2\u7ecf\u5b66\u5f97\u5dee\u4e0d\u591a\u4e86\uff0c\u5927\u6982\u53ef\u4ee5\u6d3e\u4e0a\u7528\u573a\u4e86\u2026\u2026\u201d\u541f\u96ea\u8bf4\uff1a\u201c\u59b9\u59b9\u6bd4\u6211\u8fd8\u806a\u660e\uff0c\u5e94\u8be5\u6bd4\u6211\u5b66\u5f97\u66f4\u597d\u4e9b\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5dee\u4e00\u70b9\u5c31\u4f1a\u7834\u7efd\u767e\u51fa\uff0c\u989c\u8272\u8c03\u9519\uff0c\u5c31\u662f\u4f7f\u8138\u8272\u548c\u80a4\u8272\u5927\u4e0d\u76f8\u540c\uff0c\u4e00\u773c\u5c31\u53ef\u4ee5\u770b\u51fa\u6bdb\u75c5\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u63a5\u9053\uff1a\u201c\u5927\u59d0\u8bf4\u7684\u662f\uff0c\u96ea\u3001\u971c\u4e24\u4f4d\u59d1\u5a18\uff0c\u6682\u7559\u5927\u59d0\u8eab\u4fa7\uff0c\u5750\u9547\u98ce\u96ea\u5c0f\u9986\uff0c\u63a5\u5e94\u516b\u65b9\uff0c\u5927\u59d0\uff0c\u8fd9\u53c8\u662f\u4e00\u4ef6\u5f88\u8270\u82e6\u7684\u4efb\u52a1\u554a\uff01\u201d \u3000\u3000\u201c\u6211\u53ea\u80fd\u8bf4\u5c3d\u529b\u800c\u4e3a\uff0c\u4e8b\u60c5\u529e\u4e0d\u597d\uff0c\u5927\u59d0\u4f1a\u62ff\u6027\u547d\u9876\u4e0a\u2026\u2026\u201d\u5f20\u56db\u59d1\u7684\u8bed\u6c14\u5f88\u5e73\u9759\uff0c\u4f46\u5b57\u5b57\u5982\u91d1\u7389\u649e\u51fb\uff0c\u8c6a\u6c14\u5e72\u4e91\uff0c\u542c\u5f97\u8001\u53eb\u5316\u3001\u6bb5\u4e5d\u7b49\u5fc3\u795e\u9707\u52a8\uff0c\u53ea\u89c9\u5979\u4fa0\u809d\u4e49\u80c6\uff0c\u5c24\u80dc\u987b\u7709\u3002 \u3000\u3000\u4f46\u6c5f\u67ab\u660e\u767d\uff0c\u5f20\u56db\u59d1\u8fd9\u756a\u809d\u80c6\u76f8\u7167\u7684\u8c6a\u8bed\uff0c\u5305\u542b\u4e86\u65e0\u5c3d\u7684\u60c5\u7231\uff0c\u8fd9\u4f4d\u4e00\u751f\u672a\u4f8d\u7537\u4eba\u7684\u5947\u5973\u5b50\uff0c\u5728\u5e74\u8fc7\u56db\u5341\uff0c\u9752\u6625\u5c06\u5c3d\u65f6\uff0c\u5374\u9047\u4e0a\u4e86\u6c5f\u67ab\u8fd9\u4e2a\u4e60\u7ec3\u5f02\u672f\u7684\u7537\u4eba\uff0c\u6405\u4e71\u4e86\u5979\u7684\u5fc3\uff0c\u4e5f\u5f15\u71c3\u4e86\u5979\u6df1\u85cf\u5fc3\u5e95\u7684\u60c5\u7130\uff0c\u70e7\u7684\u5979\u91cd\u51fa\u6c5f\u6e56\uff0c\u60c5\u7518\u6548\u547d\u3002 \u3000\u3000\u201c\u5927\u59d0\u2026\u2026\u201d\u625b\u67ab\u7684\u5fc3\u4e2d\u6709\u4e09\u5206\u60ed\u6127\uff0c\u4e03\u5206\u4e0d\u5b89\u5730\u8bf4\uff1a\u201c\u8fd9\u662f\u4e00\u573a\u8c6a\u8d4c\uff0c\u6b66\u6797\u547d\u8109\u80fd\u5426\u5ef6\u7eed\u4e0b\u53bb\uff0c\u5168\u5728\u6211\u4eec\u8fd9\u4e00\u51fb\u4e4b\u4e2d\uff0c\u5c0f\u5f1f\u4e0d\u77e5\u9053\uff0c\u8fd9\u4e00\u6218\uff0c\u8981\u6b7b\u4f24\u591a\u5c11\u7684\u82f1\u96c4\u8c6a\u6770\uff0c\u7559\u4e0b\u591a\u5c11\u60b2\u4f24\u61be\u4e8b\u2026\u2026\u201d \u3000\u3000\u8001\u53eb\u5316\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6c5f\u8001\u5f1f\uff0c\u4eba\u751f\u4e00\u4e16\uff0c\u8349\u957f\u4e00\u79cb\uff0c\u751f\u6b7b\u7b49\u95f2\u4e8b\uff0c\u5f53\u4ed8\u7b11\u8c08\u4e2d\uff0c\u8001\u5f1f\uff0c\u4e16\u65e0\u767e\u5c81\u82f1\u96c4\uff0c\u5927\u4e08\u592b\u5f53\u7559\u4e00\u4efd\u53bb\u601d\u2026\u2026\u201d \u3000\u3000\u201c\u8001\u53eb\u5316\u8bf4\u7684\u5bf9\uff0c\u8840\u6cea\u916c\u77e5\u5df2\uff0c\u4f24\u60c5\u4ed8\u6d41\u6c34\uff0c\u5915\u9633\u65e0\u9650\u597d\uff0c\u4f55\u82e6\u6101\u9ec4\u660f\uff0c\u4f60\u4eec\u5750\u5750\uff0c\u6211\u8981\u4e0b\u53a8\u53bb\uff0c\u4f5c\u51e0\u9053\u62ff\u624b\u7684\u597d\u83dc\uff0c\u8ba9\u8bf8\u4f4d\u5c3d\u4e00\u9910\u4e4b\u6b22\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u8f6c\u8eab\u800c\u53bb\uff0c\u53cc\u76ee\u4e2d\u6d6e\u52a8\u83b9\u6676\u7684\u6cea\u73e0\u3002 \u3000\u3000\u8fd9\u4f4d\u6c5f\u6e56\u5947\u5973\u5b50\uff0c\u4e5f\u7ec8\u4e8e\u5fcd\u4e0d\u4f4f\u4e86\uff0c\u5e7d\u5e7d\u6101\u6000\uff0c\u65e0\u9650\u60c5\u4f24\uff01 \u3000\u3000\u4eba\u751f\u554a\uff01\u7adf\u662f\u5982\u6b64\u60c6\u6005\u65e0\u5948\u3002 \u3000\u3000\u541f\u96ea\u3001\u541f\u971c\uff0c\u770b\u770b\u6c5f\u67ab\uff0c\u540c\u65f6\u7ad9\u8d77\uff0c\u9053\uff1a\u201c\u5f20\u59e8\uff01\u6211\u4eec\u5e2e\u4f60\u3002\u201d \u3000\u3000\u201c\u597d\u554a\uff01\u4e2b\u5934\uff0c\u6765\u5b66\u5b66\u5f20\u59e8\u7684\u70f9\u996a\u624b\u6cd5\uff0c\u53ea\u8981\u5f97\u6211\u5341\u4e4b\u516d\u4e03\uff0c\u4fdd\u8bc1\u80fd\u628a\u7537\u4eba\u62f4\u4f4f\u3002\u201d \u3000\u3000\u541f\u96ea\u3001\u541f\u971c\u672c\u662f\u7eaf\u6d01\u65e0\u90aa\u7684\u5c11\u5973\uff0c\u4f46\u8fd9\u4e9b\u65f6\u65e5\u4e4b\u4e2d\uff0c\u5f20\u56db\u59d1\u5374\u544a\u8bc9\u4e86\u5979\u4eec\u592a\u591a\u7684\u4e8b\uff0c\u4f7f\u4e24\u4f4d\u51b0\u96ea\u806a\u660e\u7684\u5c0f\u59d1\u5a18\u4e86\u89e3\u5230\u6c5f\u6e56\u7684\u9669\u6076\uff0c\u4e5f\u4f7f\u5979\u4eec\u5f00\u59cb\u7559\u5fc3\u53bb\u89c2\u5bdf\u8eab\u8fb9\u7684\u4e8b\u52a1\u3002 \u3000\u3000\u5979\u4eec\u53d1\u89c9\u4e86\u6c5f\u67ab\u3002 \u3000\u3000\u6c5f\u67ab\u4e0d\u662f\u90a3\u79cd\u9762\u5982\u6577\u7c89\u7684\u4fca\u4fcf\u4eba\u7269\uff0c\u4f46\u4ed6\u90a3\u80a1\u521a\u6bc5\u7684\u795e\u97f5\u548c\u82f1\u633a\u4e4b\u6c14\uff0c\u5f62\u6210\u4e86\u4e00\u79cd\u7279\u6709\u7684\u7537\u4eba\u9b45\u529b\u3002 \u3000\u3000\u4e24\u4e2a\u5c0f\u59d1\u5a18\u4e00\u9047\u4e0a\u6c5f\u67ab\uff0c\u603b\u662f\u5fcd\u4e0d\u4f4f\u591a\u770b\u51e0\u773c\u3002 \u3000\u3000\u542c\u8bf4\u5b66\u597d\u4e86\u70f9\u996a\u672f\uff0c\u80fd\u628a\u7537\u4eba\u62f4\u4f4f\uff0c\u4e8c\u5973\u540c\u65f6\u56de\u5934\u770b\u4e86\u6c5f\u67ab\u4e00\u773c\uff0c\u5fcd\u4e0d\u4f4f\u5fae\u5fae\u4e00\u7b11\u3002 \u3000\u3000\u5f20\u56db\u59d1\u5374\u77a7\u7684\u5fc3\u5934\u4e00\u9707\u3002 \u3000\u3000\u5e73\u5fc3\u800c\u8bba\uff0c\u6c5f\u67ab\u5e76\u6ca1\u6709\u65bd\u5c55\u60d1\u5fc3\u672f\u5f15\u8bf1\u4e8c\u5973\uff0c\u4ee5\u5229\u7528\u5979\u4eec\u5168\u5fc3\u6548\u547d\uff0c\u76f8\u53cd\u7684\u8fd8\u6709\u70b9\u9003\u907f\u7684\u7528\u5fc3\uff0c\u4ece\u4e0d\u5355\u72ec\u548c\u4e8c\u5973\u76f8\u5904\u3002 \u3000\u3000\u8bf4\u4e8c\u5973\u52a8\u4e86\u60c5\uff0c\u4e5f\u672a\u514d\u8a00\u8fc7\u5176\u5b9e\uff0c\u5979\u4eec\u7eaf\u6d01\u7684\u5fc3\u7075\u4e2d\uff0c\u53ea\u611f\u5230\u6c5f\u5927\u54e5\u5f88\u6709\u82f1\u96c4\u6c14\u6982\uff0c\u6c5f\u5927\u54e5\u5f88\u53ef\u7231\uff0c\u5f88\u60f3\u548c\u6c5f\u5927\u54e5\u76f8\u5904\u4e00\u8d77\u3002 \u3000\u3000\u4e24\u4e2a\u5c0f\u4e2b\u5934\u6162\u6162\u5730\u6b65\u5165\u60c5\u7f51\uff0c\u4f46\u5979\u4eec\u5374\u5168\u4e0d\u81ea\u77e5\u3002 \u3000\u3000\u6c5f\u67ab\u6ca1\u6709\u7559\u5fc3\u4e8c\u5973\uff0c\u4e5f\u6ca1\u6709\u770b\u5230\u5979\u4eec\u4e34\u53bb\u79cb\u6ce2\u7684\u56de\u7738\u4e00\u7b11\uff0c\u5374\u8f6c\u5411\u4e03\u5b9d\u548c\u5c1a\uff0c\u7814\u5546\u6df7\u5165\u6d1b\u9633\u5c45\u7684\u7ec6\u8282\u3002 \u3000\u3000\u9152\u8089\u548c\u5c1a\u662f\u5927\u667a\u82e5\u611a\u7684\u4eba\uff0c\u6c5f\u67ab\u7684\u8ba1\u5212\uff0c\u4ed6\u5f88\u5feb\u7684\u5168\u76d8\u9886\u609f\uff0c\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u548c\u5c1a\u60f3\u4e0d\u51fa\u597d\u4e3b\u610f\uff0c\u5374\u4fdd\u8bc1\u80fd\u6267\u884c\u4f60\u6c5f\u8001\u5f1f\u7684\u8ba1\u5212\uff0c\u4e0d\u8fc7\uff0c\u548c\u5c1a\u6709\u4e00\u70b9\u60f3\u4e0d\u51fa\u660e\u767d\uff0c\u90ed\u5929\u540c\u6613\u540d\u97e9\u9738\uff0c\u53d6\u4ee3\u4f60\u7684\u526f\u603b\u7ba1\uff0c\u4f60\u8001\u5f1f\u968f\u65f6\u53ef\u4ee5\u79bb\u5f00\u6d1b\u9633\u5c45\uff0c\u548c\u8001\u53eb\u5316\u5b50\u89c1\u9762\uff0c\u8eab\u5916\u5316\u8eab\uff0c\u91cc\u5916\u517c\u987e\uff0c\u529e\u6cd5\u662f\u5341\u5206\u9ad8\u660e\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u4f60\u56de\u5230\u6d1b\u9633\u5c45\uff0c\u518d\u4ee5\u97e9\u9738\u8eab\u4efd\u51fa\u73b0\u65f6\uff0c\u90ed\u8001\u5f1f\u53c8\u5f80\u90a3\u91cc\u53bb\u5462\uff1f\u9093\u98de\u5f88\u7cbe\u660e\uff0c\u56db\u5b63\u82b1\u5a62\u4e5f\u975e\u7b49\u95ed\u4eba\u7269\u2026\u2026\u201d \u3000\u3000\u201c\u8fd9\u4e00\u70b9\uff0c\u6211\u65e9\u6709\u8ba1\u8f83\u4e86\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u8bf4\uff1a\u201c\u6211\u7684\u4f4f\u5904\u6709\u4e00\u95f4\u5806\u653e\u8863\u7269\u7684\u623f\u95f4\uff0c\u6211\u5df2\u7ecf\u5e03\u7f6e\u59a5\u5f53\uff0c\u91cc\u9762\u53ef\u4ee5\u4f4f\u4e00\u4e2a\u4eba\uff0c\u5916\u9762\u6742\u7269\u906e\u63a9\uff0c\u5982\u679c\uff0c\u6ca1\u6709\u5f15\u8d77\u4ed6\u4eec\u7684\u6000\u7591\uff0c\u5927\u6982\u4e0d\u4f1a\u6709\u4eba\u641c\u67e5\uff0c\u53ea\u662f\u5730\u65b9\u592a\u5c0f\u4e86\u4e00\u4e9b\uff0c\u8981\u59d4\u5c48\u90ed\u5144\u5f1f\u4e86\u3002\u201d \u3000\u3000\u201c\u53ea\u8981\u6709\u4e00\u5e2d\u4e4b\u5730\uff0c\u80fd\u8ba9\u6211\u5bb9\u8eab\u8db3\u77e3\u2026\u2026\u201d\u90ed\u5929\u540c\u9053\uff0c\u201c\u53ea\u662f\u8fdb\u51fa\u4e4b\u95f4\uff0c\u662f\u5426\u65b9\u4fbf\u5417\uff1f\u201d \u3000\u3000\u201c\u90a3\u5012\u4e0d\u7528\u62c5\u5fc3\uff0c\u5c31\u5728\u6211\u4f4f\u7684\u623f\u5185\uff0c\u6211\u4eec\u8eab\u6750\u76f8\u5dee\u4e0d\u591a\uff0c\u6211\u5df2\u51c6\u5907\u597d\u4e86\u5f88\u591a\u76f8\u540c\u7684\u8863\u670d\u3002\u201d \u3000\u3000\u201c\u8fd9\u5c31\u597d\uff0c\u90ed\u67d0\u4eba\u4f1a\u5168\u529b\u4ee5\u8d74\u3002\u201d \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6c5f\u8001\u5f1f\uff0c\u4f60\u4e0d\u4f46\u80f8\u6709\u6210\u7af9\uff0c\u800c\u4e14\uff0c\u65e9\u5df2\u6709\u4e86\u51c6\u5907\uff0c\u597d\uff01\u54b1\u4eec\u5403\u8fc7\u6613\u82b1\u624b\u7684\u7cbe\u7f8e\u4f73\u80b4\uff0c\u5c31\u6613\u5bb9\u52a8\u8eab\u3002\u201d \u3000\u3000\u8001\u53eb\u5316\u7a81\u7136\u8bf4\u9053\uff1a\u201c\u6c5f\u8001\u5f1f\uff0c\u4f60\u8981\u4e0d\u8981\u89c1\u79e6\u516b\u516c\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u8981\u89c1\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff0c\u201c\u800c\u4e14\uff0c\u8fd8\u8981\u8bf4\u670d\u4ed6\u5047\u610f\u548c\u9093\u98de\u5408\u4f5c\uff0c\u4e92\u901a\u58f0\u606f\uff01\u201d \u3000\u3000\u201c\u53ef\u662f\uff0c\u4f60\u5df2\u7ecf\u89c1\u8fc7\u4ed6\u4e86\u2026\u2026\u201d\u8001\u53eb\u5316\u9053\uff0c\u201c\u79e6\u516b\u516c\u7684\u6c14\u91cf\u4e0d\u5927\uff0c\u5982\u679c\u4ed6\u8ba4\u4e3a\u4f60\u6211\u52fe\u7ed3\u800d\u4e86\u4ed6\uff0c\u53ea\u6015\u5fc3\u4e2d\u4f1a\u6709\u82a5\u8482\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u6211\u89c1\u4ed6\u65f6\uff0c\u4f1a\u4ee5\u97e9\u9738\u7684\u8eab\u4efd\u51fa\u73b0\uff0c\u9093\u98de\u4e0d\u662f\u4efb\u6211\u4eec\u73a9\u5f04\u4e8e\u638c\u80a1\u4e4b\u4e0a\u7684\u4eba\u7269\uff0c\u4ed6\u4e0d\u548c\u79e6\u516b\u516c\u5f53\u9762\u8c08\u8fc7\uff0c\u7edd\u4e0d\u4f1a\u542c\u6211\u4e00\u9762\u4e4b\u8bcd\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u8001\u53eb\u5316\u4e5f\u5f97\u6539\u626e\u4e00\u4e0b\u4e86\uff1f\u201d \u3000\u3000\u201c\u90a3\u5012\u4e0d\u7528\u2026\u2026\u201d\u6c5f\u67ab\u7b11\u4e00\u7b11\u53c8\u9053\uff1a\u201c\u6b63\u8981\u7528\u4f60\u8001\u524d\u8f88\u7684\u5a01\u540d\uff0c\u624d\u80fd\u8ba9\u9093\u98de\u4fe1\u773c\uff0c\u8fd9\u4e2d\u95f4\u5927\u8d39\u601d\u91cf\u7684\u662f\uff0c\u4e0d\u80fd\u8ba9\u9093\u98de\u5b8c\u5168\u4e86\u89e3\u6211\u4eec\u4eba\u5b9e\u529b\uff0c\u4e5f\u4e0d\u80fd\u7a7a\u53e3\u8bf4\u767d\u8bdd\uff0c\u4e0d\u8ba9\u4ed6\u770b\u5230\u4e00\u4e9b\u65a4\u4e24\uff0c\u4ed6\u4e0d\u4f1a\u76f8\u4fe1\uff0c\u8fd9\u4e2d\u95f4\u5f97\u7528\u4e9b\u5fc3\u673a\u624d\u884c\u3002\u201d \u3000\u3000\u201c\u8fd9\u4ef6\u4e8b\uff0c\u7531\u8001\u53eb\u5316\u548c\u79e6\u516b\u516c\u5546\u91cf\u4e00\u4e0b\uff0c\u9009\u51e0\u4e2a\u6709\u4e00\u5b9a\u5206\u91cf\u7684\u4eba\u7269\u548c\u4f60\u4eec\u89c1\u9762\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u53bb\u8bf4\u670d\u79e6\u516b\u516c\uff0c\u6211\u7b49\u4f60\u7684\u56de\u97f3\uff0c\u660e\u5929\u665a\u4e0a\uff0c\u5e0c\u671b\u80fd\u628a\u6d88\u606f\u4f20\u5165\u98ce\u96ea\u5c0f\u9986\uff0c\u56db\u59d1\u6709\u4e00\u5957\u8fde\u7edc\u65b9\u6cd5\uff0c\u4e0d\u7528\u4eba\u73b0\u8eab\uff0c\u4e00\u6837\u80fd\u4f20\u9012\u6d88\u606f\u3002\u201d \u3000\u3000\u201c\u5509\uff01\u8001\u53eb\u5316\u5e94\u8be5\u7acb\u523b\u8d70\u7684\uff0c\u4f46\u9519\u8fc7\u6613\u82b1\u624b\u8fd9\u987f\u996d\uff0c\u6211\u4f1a\u9057\u61be\u7ec8\u8eab\uff01\u201d \u3000\u3000\u201c\u5403\u8fc7\u518d\u8d70\u561b\uff01\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff0c\u201c\u6025\u4e5f\u4e0d\u5728\u5403\u987f\u996d\u7684\u65f6\u95f4\uff0c\u6211\u803d\u5fc3\u7684\u662f\u79e6\u516b\u516c\u5bf9\u4f60\u751f\u51fa\u8bef\u4f1a\uff0c\u6765\u4e2a\u7ffb\u8138\u4e0d\u8ba4\u4eba\u2026\u2026\u201d \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u63a5\u9053\uff1a\u201c\u53ea\u6015\u4ed6\u4eec\u4f1a\u6740\u4eba\u706d\u53e3\uff0c\u8001\u53eb\u5316\uff0c\u73b0\u5728\u7684\u6c5f\u6e56\u60c5\u52bf\uff0c\u548c\u8fc7\u53bb\u5927\u4e0d\u76f8\u540c\u4e86\uff0c\u5927\u5bb6\u90fd\u5728\u6697\u4e2d\u884c\u4e8b\uff0c\u52fe\u5fc3\u6597\u89d2\uff0c\u4e0d\u50cf\u8fc7\u53bb\u90a3\u79cd\u5149\u660e\u78ca\u843d\u7684\u884c\u52a8\uff0c\u4f60\u8981\u5f53\u5fc3\uff01\u4ed6\u4eec\u5982\u82e5\u771f\u5bf9\u4f60\u8d77\u6000\u7591\uff0c\u90a3\u5c31\u4f1a\u4e0d\u8ba1\u624b\u6bb5\u7684\u6740\u4e86\u4f60\u3002\u201d \u3000\u3000\u201c\u5bf9\uff01\u770b\u8d77\u6765\uff0c\u6700\u5b89\u5168\u7684\u5730\u65b9\uff0c\u53cd\u4f1a\u6700\u4e3a\u5371\u9669\u2026\u2026\u201d\u6c5f\u67ab\u8f6c\u773c\u770b\u7740\u4e03\u5b9d\u548c\u5c1a\u53c8\u9053\uff1a\u201c\u5927\u5e08\u6709\u4f55\u9ad8\u89c1\uff1f\u53ef\u4fdd\u94b1\u524d\u8f88\u7684\u5e73\u5b89\uff1f\u201d \u3000\u3000\u8001\u53eb\u5316\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8bf8\u4f4d\u63d0\u9192\u4e86\u6211\uff0c\u8001\u53eb\u5316\u81ea\u6709\u5e94\u4ed8\u4e4b\u9053\uff0c\u4e24\u4f4d\u4e0d\u7528\u4e3a\u94b1\u67d0\u5b89\u5371\u62c5\u5fc3\u3002\u201d \u3000\u3000\u201c\u4f46\u6211\u4eec\u5173\u5fc3\u6c5f\u6e56\u5927\u5c40\u7684\u53d8\u5316\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff0c\u201c\u6211\u77e5\u9053\u8001\u524d\u8f88\u89c6\u6b7b\u5982\u5f52\uff0c\u4f46\u4f60\u53ef\u66fe\u60f3\u8fc7\uff0c\u4f60\u6b7b\u4e86\uff0c\u8c01\u80fd\u9876\u4e0a\u4f60\u90a3\u4efd\u5de5\u4f5c\uff0c\u6211\u4eec\u9700\u8981\u7684\u662f\u6210\u529f\u7684\u82f1\u96c4\uff0c\u4e0d\u9700\u8981\u8c6a\u58ee\u7684\u70c8\u58eb\uff0c\u524d\u8f88\u8981\u73cd\u91cd\u81ea\u5df1\u3002\u201d \u3000\u3000\u201c\u662f\u662f\u662f\u2026\u2026\u201d\u94b1\u7f3a\u7684\u8138\u4e0a\u6cdb\u8d77\u4e86\u4e00\u9635\u7f9e\u60ed\u53c8\u9053\uff1a\u201c\u6211\u4f1a\u5c0f\u5fc3\u5e94\u4ed8\uff0c\u4f1a\u548c\u6613\u82b1\u624b\u5f20\u59d1\u5a18\u7814\u7a76\u4e00\u4e0b\u3002\u201d \u3000\u3000\u201d\u5bf9\uff01\u6709\u5907\u65e0\u60a3\uff0c\u6d1b\u9633\u5c45\u4e2d\u7684\u60c5\u52bf\u7a33\u5b9a\u4e0b\u6765\uff0c\u6211\u5c31\u5230\u98ce\u96ea\u5c0f\u9986\u6765\u606d\u5019\u4f73\u97f3\u3002\u201d \u3000\u3000\u201c\u9700\u8981\u5e2e\u52a9\u65f6\uff0c\u6211\u4f1a\u5411\u5f20\u56db\u59d1\u63d0\u51fa\u8981\u6c42\uff0c\u4e0d\u8fc7\uff0c\u8001\u53eb\u5316\u5c06\u5c3d\u91cf\u52a8\u4e4b\u4ee5\u8bda\uff0c\u5e0c\u671b\u80fd\u53d6\u5f97\u79e6\u516b\u516c\u7684\u4fe1\u4efb\u3002\u201d \u3000\u3000\u201c\u884c\u4e86\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff0c\u201c\u8001\u53eb\u5316\u53ea\u8981\u628a\u66b4\u8e81\u813e\u6c14\u538b\u4e00\u4e0b\uff0c\u529e\u4e8b\u80fd\u529b\uff0c\u7edd\u5bf9\u53ef\u4ee5\u4fe1\u8d56\u3002\u201d \u3000\u3000\u8001\u53eb\u5316\u542c\u5f97\u5fc3\u5934\u4e00\u9707\uff0c\u6697\u9053\uff1a\u201c\u539f\u6765\u4ed6\u4eec\u662f\u6015\u6211\u813e\u6c14\u6bdb\u8e81\uff0c\u8bef\u4e86\u5927\u4e8b\u3002\u201d \u3000\u3000\u541f\u96ea\u3001\u541f\u971c\uff0c\u9001\u4e0a\u4e86\u7cbe\u7f8e\u7684\u9152\u83b1\uff0c\u4e24\u4e2a\u5982\u82b1\u4f3c\u7389\u7684\u5c0f\u59d1\u5a18\uff0c\u7b11\u610f\u8fce\u4eba\uff0c\u4e00\u70b9\u6ca1\u6709\u6367\u9152\u3001\u9001\u83dc\u7684\u59d4\u5c48\u611f\u89c9\uff0c\u5927\u5bb6\u8d5e\u4f73\u80b4\u53ef\u53e3\uff0c\u4e24\u4e2a\u5c0f\u59d1\u5a18\u4e5f\u8ddf\u7740\u53eb\u597d\uff0c\u800c\u4e14\uff0c\u8fd8\u8bf4\u5728\u53a8\u623f\u65e9\u5df2\u5077\u5403\u4e86\u5f88\u591a\u3002 \u3000\u3000\u679c\u7136\u662f\u5fc3\u4e2d\u65e0\u8d35\u8d31\uff0c\u8d2b\u5bcc\u7686\u4e00\u822c\u7684\u7eaf\u6d01\u5c11\u5973\u3002 \u3000\u3000\u9093\u98de\u8981\u6c5f\u67ab\u5e26\u4e03\u5b9d\u548c\u5c1a\u5728\u4f4f\u5904\u89c1\u9762\uff0c\u90a3\u662f\u4e00\u5e62\u9752\u7816\u780c\u6210\u7684\u4e09\u5408\u9662\uff0c\u6c5f\u67ab\u4e5f\u672a\u5230\u8fc7\u7684\u5730\u65b9\u3002 \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u6253\u91cf\u4e86\u5c0f\u5ba2\u5385\u4e00\u773c\uff0c\u4e0d\u89c1\u5916\u4eba\uff0c\u7a81\u7136\u53d6\u4e0b\u5305\u5934\u7684\u9ed1\u4e2d\u3001\u5047\u53d1\u7b11\u9053\uff1a\u201c\u6211\u662f\u4e03\u5b9d\u548c\u5c1a\uff0c\u4e45\u95fb\u9093\u5144\u5927\u540d\uff0c\u4eca\u65e5\u6709\u5e78\u4e00\u4f1a\u3002\u201d \u3000\u3000\u9093\u98de\u989d\u9996\u5fae\u7b11\uff0c\u6c5f\u67ab\u6697\u6697\u8d5e\u8d4f\u9053\uff1a\u201c\u597d\u4e00\u4e2a\u80f8\u85cf\u9526\u7ee3\u7684\u5927\u548c\u5c1a\uff0c\u4e00\u4e0b\u5b50\u5c31\u62c9\u8fd1\u4e86\u8ddd\u79bb\uff0c\u5f15\u5165\u6b63\u9898\u3002\u201d \u3000\u3000\u201c\u6c5f\u8001\u5f1f\u544a\u8bc9\u6211\u2026\u2026\u4e0d\u5bf9\uff0c\u4e0d\u5bf9\u2026\u2026\u4ed6\u73b0\u5728\u5e94\u8be5\u662f\u53eb\u97e9\u9738\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u6709\u4e9b\u5931\u63aa\u5730\u8bf4\u3002 \u3000\u3000\u201c\u4e0d\u8981\u7d27\u2026\u2026\u201d\u9093\u98de\u4f4e\u58f0\u5b89\u6170\u7740\u4e03\u5b9d\u548c\u5c1a\uff0c\u9053\uff1a\u201c\u6c5f\u67ab\u5df2\u7ecf\u544a\u8bc9\u6211\u4ed6\u6740\u4e86\u97e9\u9738\u5192\u540d\u9876\u66ff\u7684\u4e8b\uff0c\u6c5f\u8001\u5f1f\u548c\u6211\u662f\u540c\u4e00\u5728\u6761\u8239\u4e0a\u7684\u4eba\u2026\u2026\u201d \u3000\u3000\u201c\u8fd9\u5c31\u597d\uff0c\u8fd9\u5c31\u597d\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u9053\uff0c\u201c\u4ed6\u544a\u8bc9\u6211\u8bf4\uff0c\u4f60\u9093\u603b\u7ba1\u9700\u8981\u51e0\u4e2a\u5fe0\u5fc3\u6548\u547d\u7684\u4eba\uff0c\u6b66\u529f\u8981\u597d\uff0c\u80c6\u5b50\u8981\u5927\uff0c\u6574\u5929\u6709\u9152\u559d\uff0c\u6708\u6708\u6709\u94b1\u62ff\uff0c\u548c\u5c1a\u867d\u7136\u8df3\u51fa\u4e09\u754c\u5916\uff0c\u4f46\u5374\u672a\u6212\u9152\u8089\u2026\u2026\u201d \u3000\u3000\u201c\u597d\uff01\u4f60\u5e26\u4e86\u591a\u5c11\u4eba\u6765\uff1f\u201d \u3000\u3000\u201c\u8fde\u548c\u5c1a\u7b97\u4e0a\uff0c\u4e00\u5171\u56db\u4e2a\u4eba\u3002\u201d \u3000\u3000\u9093\u98de\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u6211\u60f3\u4ed6\u4eec\u4e5f\u90fd\u662f\u6c5f\u6e56\u4e2d\u6709\u540d\u671b\u7684\u4eba\uff0c\u6211\u4e0d\u95ee\u4ed6\u4eec\u7684\u8eab\u4efd\u4e86\uff0c\u6211\u76f8\u4fe1\u6c5f\u8001\u5f1f\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u63a5\u9053\uff1a\u201c\u603b\u7ba1\u653e\u5fc3\uff0c\u65f6\u673a\u9002\u5f53\u65f6\uff0c\u4ed6\u4eec\u81ea\u5df1\u4f1a\u544a\u8bc9\u4f60\u3002\u201d \u3000\u3000\u9093\u98de\u5fae\u5fae\u4e00\u7b11\uff0c\u76ee\u6ce8\u4e03\u5b9d\u548c\u5c1a\u9053\uff1a\u201c\u5927\u5e08\uff0c\u4e3a\u4e86\u63a9\u5165\u8033\u76ee\uff0c\u8fd8\u662f\u6234\u4e0a\u5047\u53d1\u2026\u2026\u201d \u3000\u3000\u201c\u8fd9\u4e2a\uff0c\u548c\u5c1a\u65e9\u5c31\u60f3\u597d\u4e86\uff0c\u6211\u6cd5\u53f7\u4e03\u5b9d\uff0c\u4fd7\u59d3\u5e72\u8106\u59d3\u9f50\uff0c\u548c\u5c1a\u51fa\u5bb6\u65f6\uff0c\u6392\u884c\u7b2c\u56db\uff0c\u5c31\u53eb\u9f50\u56db\u5982\u4f55\uff1f\u201d \u3000\u3000\u201c\u6210\uff01\u5c31\u4f9d\u5927\u5e08\u7684\u5b89\u6392\u2026\u2026\u201d\u9093\u98de\u9053\uff0c\u201c\u968f\u6765\u7684\u4e09\u4f4d\uff0c\u4e5f\u7531\u5927\u5e08\u7387\u9886\uff0c\u7b97\u662f\u6d1b\u9633\u5c45\u4e2d\u4e00\u7ec4\u9556\u624b\uff0c\u4e0d\u8fc7\uff0c\u76f4\u63a5\u7531\u6211\u548c\u97e9\u526f\u603b\u7ba1\u6307\u6325\uff0c\u4f60\u6bcf\u6708\u652f\u4ff8\u94f6\u4e8c\u767e\u4e24\uff0c\u968f\u6765\u7684\u4e09\u4f4d\uff0c\u6bcf\u4eba\u6708\u652f\u4e00\u767e\u4e94\u5341\u4e24\uff0c\u4e0d\u77e5\u9053\u591f\u4e0d\u591f\u7528\uff1f\u201d \u3000\u3000\u201c\u591f\u4e86\uff01\u591f\u4e86\uff01\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u9053\uff1a\u201c\u6211\u80fd\u4e0d\u80fd\u5e26\u7740\u4ed6\u4eec\u56db\u4e0b\u8d70\u52a8\u8d70\u52a8\uff0c\u4e86\u89e3\u8fd9\u6d1b\u9633\u5c45\u4e2d\u7684\u5f62\u52bf\uff0c\u4e00\u65e6\u603b\u7ba1\u6709\u6240\u5dee\u9063\uff0c\u5e94\u53d8\u4e0a\u5c31\u65b9\u4fbf\u4e0d\u5c11\u3002\u201d \u3000\u3000\u201c\u53ef\u4ee5\uff0c\u6211\u9a6c\u4e0a\u53d1\u7ed9\u4f60\u4eec\u9556\u624b\u7684\u8170\u724c\uff0c\u4e0d\u8fc7\uff0c\u6709\u4e24\u4e2a\u5730\u65b9\uff0c\u4e0d\u8981\u592a\u63a5\u8fd1\u2026\u2026\u201d\u9093\u98de\u9053\uff0c\u201c\u897f\u5317\u89d2\u7684\u6816\u51e4\u697c\uff0c\u548c\u897f\u5357\u89d2\u7684\u8fce\u5bbe\u9601\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e24\u4e2a\u5730\u65b9\u662f\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u4e00\u8138\u832b\u7136\u7684\u95ee\u4e86\u4e00\u53e5\u3002 \u3000\u3000\u5bf9\u4ed6\u7684\u88c5\u4f5c\u529f\u592b\uff0c\u6c5f\u67ab\u5fc3\u4e2d\u4f69\u670d\u6781\u4e86\u3002 \u3000\u3000\u201c\u6816\u98ce\u697c\u4f4f\u7684\u6b4c\u59ec\u9752\u51e4\uff0c\u8fce\u5bbe\u9601\u7ecf\u5e38\u6709\u8d35\u5ba2\u501f\u5bbf\u2026\u2026\u201d\u9093\u98de\u9053\uff0c\u201c\u6211\u60f3\u4f60\u5e94\u8be5\u660e\u767d\u4e86\u3002\u201d \u3000\u3000\u201c\u662f\u662f\u662f\uff01\u5b8c\u5168\u660e\u767d\u4e86\u3002\u201d\u4e03\u5b9d\u548c\u5c1a\u8fde\u58f0\u5e94\u7740\u3002 \u3000\u3000\u9093\u98de\u63a5\u9053\uff1a\u201c\u6211\u5df2\u4ea4\u4ee3\u4ed6\u4eec\u4e3a\u56db\u4f4d\u5907\u597d\u4e86\u4e0b\u69bb\u4e4b\u5904\uff0c\u4f60\u4eec\u53bb\u770b\u770b\uff0c\u5408\u4e0d\u5408\u610f\uff1f\u201d \u3000\u3000\u4e3e\u638c\u4e00\u51fb\uff0c\u4e00\u4e2a\u4e8c\u5341\u56db\u4e94\u7684\u9ed1\u8863\u7cbe\u608d\u5c11\u5e74\uff0c\u51fa\u73b0\u5385\u4e2d\u3002 \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u5df2\u6234\u597d\u5047\u53d1\uff0c\u9053\uff1a\u201c\u9f50\u67d0\u544a\u9000\u3002\u201d\u80de\u7740\u90a3\u9ed1\u8863\u5c11\u5e74\u79bb\u53bb\u3002 \u3000\u3000\u76ee\u7779\u4e03\u5b9d\u548c\u5c1a\u80cc\u5f71\u8fdc\u53bb\uff0c\u9093\u98de\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u6c5f\u8001\u5f1f\uff0c\u8fd9\u4e9b\u4eba\u9760\u5f97\u4f4f\u5417\uff1f\u201d \u3000\u3000\u201c\u5e94\u8be5\u9760\u5f97\u4f4f\u7684\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff0c\u201c\u4ed6\u4eec\u7684\u5904\u5883\uff0c\u975e\u5e38\u8270\u56f0\uff0c\u8001\u5b9e\u8bf4\uff0c\u6c5f\u6e56\u867d\u5927\uff0c\u4ed6\u4eec\u5df2\u5f88\u96be\u627e\u5230\u4e00\u5e2d\u7acb\u8db3\u4e4b\u5730\uff0c\u603b\u7ba1\u63d0\u4f9b\u7ed9\u4ed6\u4eec\u5b89\u4e50\u4e4b\u5c45\uff0c\u9ad8\u4ff8\u3001\u7f8e\u98df\uff0c\u4ed6\u4eec\u4e3a\u603b\u7ba1\u6548\u547d\uff0c\u6b63\u662f\u5404\u5f97\u5176\u6240\u3002\u201d \u3000\u3000\u201c\u4ed6\u4eec\u662f\u88ab\u4eba\u8ffd\u6740\u5417\uff1f\u2026\u2026\u201d\u9093\u98de\u82e6\u7b11\u4e00\u58f0\u53c8\u9053\uff1a\u201c\u5982\u679c\u5f53\u65f6\u80fd\u5fcd\u4e00\u53e3\u6c14\uff0c\u4e0d\u6740\u738b\u9f99\uff0c\u4e5f\u4e0d\u4f1a\u95f9\u6210\u4eca\u65e5\u8fd9\u79cd\u5c40\u9762\u4e86\uff01\u201d \u3000\u3000\u6c5f\u67ab\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u4ed6\u4e0d\u662f\u6b63\u7edf\u5f1f\u5b50\uff0c\u4f46\u5374\u80fd\u6709\u4eca\u65e5\u8fd9\u79cd\u8eab\u4efd\uff0c\u5fc5\u6709\u7279\u6b8a\u7684\u539f\u56e0\uff0c\u4ed6\u77e5\u9053\u7684\u5185\u60c5\uff0c\u5b9a\u7136\u5f88\u591a\u2026\u2026\u201d \u3000\u3000\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u53e3\u4e2d\u5374\u5e94\u9053\uff1a\u201c\u4e5f\u8bb8\u90a3\u738b\u9f99\u65e9\u6709\u9664\u53bb\u603b\u7ba1\u7684\u6253\u7b97\u2026\u2026\u201d \u3000\u3000\u9093\u98de\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u4ed6\u4eec\u65e9\u60f3\u9664\u4e86\u6211\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u5982\u679c\u6ca1\u6709\u4eba\u6388\u610f\uff0c\u738b\u9f99\u600e\u6562\u5bf9\u603b\u7ba1\u6709\u6240\u4e0d\u656c\uff0c\u81f3\u5c11\uff0c\u4e5f\u4f1a\u4fdd\u6301\u4e2a\u8868\u9762\u7684\u656c\u610f\u3002\u201d \u3000\u3000\u9093\u98de\u9053\uff1a\u201c\u5509\uff01\u4e8b\u5df2\u81f3\u6b64\uff0c\u4e5f\u53ea\u597d\u8d70\u4e00\u6b65\u8bf4\u4e00\u6b65\u4e86\uff0c\u5173\u4e8e\u4e03\u5b9d\u548c\u5c1a\u7b49\u4e00\u6279\u4eba\uff0c\u600e\u4f1a\u65e0\u7acb\u8db3\u4e4b\u5730\uff0c\u4ed6\u4eec\u88ab\u4ec0\u4e48\u4eba\u6240\u8ffd\u6740\uff1f\u201d \u3000\u3000\u8fd9\u4e00\u6b21\u6c5f\u67ab\u6ca1\u6cd5\u5b50\u6253\u9a6c\u864e\u4e86\uff0c\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u597d\u50cf\u662f\u4e00\u6279\u7ec4\u5408\u4e25\u5bc6\u7684\u6740\u624b\uff0c\u6b66\u529f\u535a\u6742\uff0c\u8eab\u4efd\u8be1\u5bc6\uff0c\u56e0\u4e3a\u5c5e\u4e0b\u4e0d\u6562\u8ffd\u95ee\u4e0b\u53bb\u2026\u2026\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u4e0d\u95ee\u4e2a\u6e05\u6e05\u695a\u695a\u5462\uff1f\u201d\u9093\u98de\u9053\uff0c\u201c\u4e03\u5b9d\u548c\u5c1a\u662f\u6781\u53d7\u767d\u9053\u656c\u91cd\u7684\u4eba\u7269\uff0c\u5f62\u8ff9\u53ef\u7591\uff01\u201d \u3000\u3000\u201c\u5c5e\u4e0b\u62c5\u5fc3\u8ffd\u6740\u4ed6\u4eec\u7684\u5c31\u662f\u6211\u4eec\u8fd9\u4e2a\u7ec4\u5408\u4e2d\u4eba\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff0c\u201c\u4e00\u65e6\u95ee\u5230\u5173\u952e\u5904\uff0c\u5c5e\u4e0b\u4e0d\u77e5\u5982\u4f55\u63a5\u53e3\u4e86\u2026\u2026\u201d \u3000\u3000\u8bed\u6c14\u4e00\u987f\uff0c\u53c8\u9053\uff1a\u201c\u5c5e\u4e0b\u6597\u80c6\uff0c\u8bf7\u6559\u603b\u7ba1\u51e0\u53e5\u8bdd\uff1f\u201d \u3000\u3000\u9093\u98de\u9053\uff1a\u201c\u597d\uff01\u4f60\u8bf4\u3002\u201d \u3000\u3000\u201c\u5982\u679c\u6740\u738b\u9f99\u7684\u4e8b\u53d1\uff0c\u6211\u4eec\u8fd9\u4e2a\u7ec4\u5408\uff0c\u4f1a\u4e0d\u4f1a\u8ffd\u6740\u603b\u7ba1\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u8fd9\u51e0\u53e5\u8bdd\u95ee\u7684\u5f88\u5927\u80c6\uff0c\u4e5f\u6b63\u51fb\u4e2d\u4e86\u9093\u98de\u7684\u75bc\u5904\uff0c\u9093\u98de\u8138\u8272\u6c89\u91cd\u5730\u8bf4\u9053\uff1a\u201c\u5e0c\u671b\u4e0d\u4f1a\uff0c\u4f46\u4e5f\u4e0d\u80fd\u4e0d\u4f5c\u6700\u574f\u7684\u6253\u7b97\u3002\u201d \u3000\u3000\u201c\u5bf9\uff01\u4e03\u5b9d\u548c\u5c1a\u8fd9\u51e0\u4e2a\u4eba\uff0c\u5c31\u662f\u6211\u4eec\u53cd\u6297\u7684\u529b\u91cf\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u5982\u4e0d\u7518\u5fc3\u675f\u624b\u5c31\u622e\uff0c\u90a3\u5c31\u653e\u624b\u4e00\u6218\uff01\u201d \u3000\u3000\u201c\u4f60\u8ba4\u4e3a\u8fd9\u4e00\u70b9\u5fae\u672a\u7684\u529b\u91cf\uff0c\u80fd\u591f\u62b5\u6297\u4e48\uff1f\u2026\u2026\u201d\u9093\u98de\u82e6\u7b11\u4e00\u4e0b\u53c8\u9053\uff1a\u201c\u90a3\u662f\u83b9\u706b\u4e4b\u5149\u548c\u65e5\u6708\u4e89\u660e\uff0c\u4e0d\u582a\u4e00\u51fb\u3002\u201d \u3000\u3000\u201c\u603b\u7ba1\u4e5f\u4e0d\u7528\u592a\u8fc7\u81ea\u8c26\uff0c\u6211\u4eec\u5c31\u7b97\u6253\u4e0d\u8fc7\uff0c\u53ef\u4ee5\u9003\u5427\uff01\u2026\u2026\u201d\u6c5f\u67ab\u653e\u4f4e\u4e86\u58f0\u97f3\u53c8\u9053\uff1a \u3000\u3000\u201c\u4e03\u5b9d\u548c\u5c1a\u7b49\u9003\u8fc7\u8ffd\u6740\uff0c\u603b\u7ba1\u7edd\u4e0d\u4f1a\u8f93\u7ed9\u4ed6\u4eec\uff0c\u518d\u8bf4\u4ed6\u4eec\u5df2\u6709\u9003\u907f\u8ffd\u6740\u7684\u7ecf\u9a8c\uff0c\u53ef\u4ee5\u7528\u4f5c\u501f\u955c\u3002\u201d \u3000\u3000\u9093\u98de\u4f3c\u662f\u88ab\u8bf4\u670d\u4e86\uff0c\u7adf\u7136\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u8bf4\u7684\u4e5f\u662f\uff0c\u6253\u4e0d\u8fc7\uff0c\u53ef\u4ee5\u9003\uff0c\u9003\u4e0d\u6389\uff0c\u5c31\u62fc\u4e2a\u6b7b\u6d3b\uff0c\u7edd\u4e0d\u80fd\u88ab\u751f\u64d2\u6d3b\u6349\u3002\u201d \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u603b\u7ba1\uff0c\u8fd9\u6d1b\u9633\u5c45\u4e2d\u7684\u9556\u624b\uff0c\u4eba\u6570\u4e0d\u5c11\uff0c\u6b66\u529f\u4e5f\u4e0d\u9519\uff0c\u4ed6\u4eec\u5e74\u8f7b\u6562\u62fc\uff0c\u662f\u4e00\u80a1\u76f8\u5f53\u5927\u7684\u529b\u91cf\uff0c\u5982\u679c\u6536\u4e3a\u6211\u7528\uff0c\u518d\u52a0\u4e0a\u603b\u7ba1\u7684\u7edd\u4e16\u8eab\u624b\uff0c\u6216\u53ef\u4fdd\u4f4f\u6d1b\u9633\u5c45\u3002\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u4ed6\u4eec\u662f\u4e00\u80a1\u5f88\u5f3a\u5927\u7684\u529b\u91cf\uff0c\u4f46\u4e0d\u4f1a\u88ab\u6211\u4eec\u6536\u7528\uff0c\u8bf4\u4e0d\u5b9a\uff0c\u4e5f\u5c31\u662f\u5bf9\u4ed8\u6211\u4eec\u7684\u4e3b\u8981\u529b\u91cf\u2026\u2026\u201d\u9093\u98de\u9053\uff0c\u201c\u8868\u9762\u4e0a\uff0c\u4ed6\u4eec\u975e\u5e38\u542c\u4ece\u6211\u8fd9\u4e2a\u603b\u7ba1\u6307\u6325\uff0c\u9aa8\u5e72\u91cc\u5374\u53e6\u6709\u4e00\u79cd\u6307\u6325\u7cfb\u7edf\uff0c\u90a3\u4eba\u4e00\u65e6\u73b0\u8eab\uff0c\u4ed6\u4eec\u5c31\u4e0d\u518d\u7406\u4f1a\u6211\u4e86\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u4eba\uff0c\u53ef\u80fd\u5c31\u662f\u738b\u9f99\uff0c\u4f46\u5df2\u4e3a\u603b\u7ba1\u9664\u53bb\u4e86\u2026\u2026\u201d\u6c5f\u67ab\u8bf4\uff0c\u201c\u5c5e\u4e0b\u4ed4\u7ec6\u5730\u60f3\u8fc7\uff0c\u518d\u6ca1\u6709\u53ef\u7591\u7684\u4eba\u4e86\u3002\u201d \u3000\u3000\u9093\u98de\u6447\u6254\u5934\uff0c\u9053\uff1a\u201c\u4e0d\u4f1a\uff0c\u90a3\u4e48\u7b80\u5355\uff0c\u4ed6\u4eec\u4e00\u5b9a\u53e6\u6709\u9996\u8111\u4eba\u7269\uff01\u201d \u3000\u3000\u201c\u4f1a\u662f\u8c01\u5462\uff1f\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff0c\u201c\u627e\u51fa\u90a3\u4e2a\u4eba\uff0c\u5927\u628a\u4ed6\u4e89\u53d6\u8fc7\u6765\uff0c\u518d\u4e0d\u7136\u5c31\u7528\u5bf9\u4ed8\u738b\u9f99\u7684\u529e\u6cd5\u6740\u4e86\u4ed6\uff01\u201d \u3000\u3000\u201c\u6211\u4e0d\u77e5\u9053\uff0c\u53ef\u5c31\u662f\u9752\u51e4\uff0c\u4e5f\u53ef\u80fd\u662f\u6885\u82b1\uff0c\u4e5f\u8bb8\u662f\u9556\u624b\u4e4b\u4e2d\u7684\u4e00\u4e2a\uff0c\u2026\u2026\u201d\u9093\u98de\u8bf4\uff0c\u201c\u4ed6\u4eec\u53ef\u80fd\u88ab\u4e00\u79cd\u4ee4\u724c\u63a7\u5236\uff0c\u4e5f\u53ef\u80fd\u88ab\u4e00\u79cd\u6697\u8bed\u53f7\u4ee4\u3002\u201d \u3000\u3000\u6c5f\u67ab\u63a5\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u8fd9\u6279\u4eba\uff0c\u5b8c\u5168\u4e0d\u53d7\u63a7\u5236\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u90a3\u5012\u4e0d\u662f\u2026\u2026\u201d\u9093\u98de\u9053\uff1a\u201c\u53ea\u8981\u6211\u6d1b\u9633\u5c45\u8fd9\u4e2a\u603b\u7ba1\u804c\u4f4d\u4e0d\u52a8\uff0c\u4ed6\u4eec\u5c31\u4f1a\u9075\u4ece\u4ee4\u6e1d\uff0c\u4f46\u5982\u6211\u4e00\u65e6\u53bb\u804c\uff0c\u4ed6\u4eec\u5c31\u4e0d\u518d\u8ba4\u6211\u3002\u201d \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u90a3\u5c31\u60f3\u529e\u6cd5\u4fdd\u4f4f\u8fd9\u4e2a\u6d1b\u9633\u5c45\u603b\u7ba1\u7684\u4f4d\u7f6e\u3002\u201d \u3000\u3000\u9093\u98de\u9053\uff1a\u201c\u600e\u4e48\u4fdd\uff1f\u201d \u3000\u3000\u201c\u4e0d\u63a5\u53d7\u53bb\u804c\u7684\u4ee4\u8c15\u2026\u2026\u201d\u6c5f\u67ab\u7b11\u9053\uff0c\u201c\u800d\u8d56\u7684\u529e\u6cd5\uff0c\u4e0d\u79bb\u5f00\u8fd9\u4e2a\u804c\u4f4d\uff0c\u4e5f\u4e0d\u5141\u8bb8\u65b0\u603b\u7ba1\u8fdb\u5165\u6d1b\u9633\u5c45\u6765\u3002\u201d \u3000\u3000\u9093\u98de\u9053\uff1a\u201c\u542c\u8d77\u6765\u5f88\u96be\u554a\u2026\u2026\u201d \u3000\u3000\u201c\u53cd\u6b63\u4ed6\u4eec\u51b3\u5b9a\u514d\u53bb\u4f60\u603b\u7ba1\u8fd9\u4e2a\u804c\u4f4d\u65f6\uff0c\u5df2\u7ecf\u5230\u4e86\u751f\u6b7b\u5173\u5934\uff0c\u6211\u4eec\u7528\u4ec0\u4e48\u529e\u6cd5\u5bf9\u6297\uff0c\u90a3\u5c31\u662f\u6211\u4eec\u7684\u4e8b\u4e86\u3002\u201d \u3000\u3000\u9093\u98de\u9053\uff1a\u201c\u73b0\u5728\uff0c\u8fd8\u672a\u89c1\u4ec0\u4e48\u53cd\u5e94\uff0c\u4e5f\u8bb8\u4ed6\u4eec\u7684\u65b9\u6cd5\u5f88\u7f13\u548c\u2026\u2026\u201d \u3000\u3000\u201c\u6709\u4e8b\u60c5\uff0c\u5c3d\u65e9\u901a\u77e5\u6211\u2026\u2026\u201d\u6c5f\u67ab\u8d77\u8eab\u62b1\u62f3\u4e00\u793c\u9053\uff0c\u201c\u5c5e\u4e0b\u544a\u9000\u4e86\u3002\u201d \u3000\u3000\u9093\u98de\u6ca1\u6709\u633d\u7559\uff0c\u4e5f\u6ca1\u6709\u95ee\u8d77\u79e6\u516b\u516c\u7684\u4e8b\uff0c\u8fd9\u5012\u51fa\u4e86\u6c5f\u67ab\u7684\u610f\u5916\u3002 \u3000\u3000\u56de\u5934\u770b\u53bb\uff0c\u53ea\u89c1\u9093\u98de\u4ef0\u8138\u671b\u7740\u5c4b\u9876\uff0c\u5446\u5446\u51fa\u795e\uff0c\u4f3c\u662f\u6b63\u5728\u60f3\u7740\u4e00\u4ef6\u5341\u5206\u91cd\u5927\u7684\u4e8b\u3002 \u3000\u3000\u6c5f\u67ab\u5fc3\u7533\u660e\u767d\uff0c\u9093\u98de\u5fc3\u4e2d\u8fd8\u6709\u5f88\u591a\u7684\u79d8\u5bc6\uff0c\u8fd9\u4e2a\u8001\u6c5f\u6e56\u80fd\u591f\u5728\u5341\u5206\u590d\u6742\u7684\u73af\u5883\u4e2d\uff0c\u94bb\u5230\u6d1b\u9633\u5c45\u603b\u7ba1\u4f4d\u804c\uff0c\u5f53\u7136\u4e0d\u662f\u4e2a\u7b80\u5355\u4eba\u7269\uff0c\u4e0d\u80fd\u64cd\u4e4b\u8fc7\u6025\uff0c\u56e0\u4e3a\u4e00\u65e6\u5f15\u8d77\u4e86\u5730\u5fc3\u4e2d\u7684\u6000\u7591\uff0c\u53ef\u80fd\u4f1a\u5f04\u5de7\u6210\u62d9\uff0c\u524d\u529f\u5c3d\u5f03\u3002 \u3000\u3000\u5230\u76ee\u524d\u4e3a\u6b62\uff0c\u9093\u98de\u4e00\u5207\u7684\u53cd\u5e94\uff0c\u90fd\u5728\u6c5f\u67ab\u7684\u8ba1\u7b97\u4e4b\u4e2d\uff0c\u4ed6\u7684\u9884\u60f3\u4e2d\u5b89\u987f\u597d\u4e03\u5b9d\u548c\u5c1a\u7b49\u56db\u4eba\u4e4b\u540e\uff0c\u9093\u98de\u4f1a\u7acb\u523b\u95ee\u8d77\u79e6\u516b\u516c\uff0c\u90a3\u662f\u552f\u4e00\u7684\u5916\u63f4\u529b\u91cf\uff0c\u9093\u98de\u5e94\u8be5\u5341\u5206\u91cd\u89c6\u3002 \u3000\u3000\u4f46\u9093\u98de\u4e0d\u7406\u4e0d\u95ee\uff0c\u4f7f\u6c5f\u67ab\u6709\u7740\u4e00\u79cd\u5f88\u5927\u7684\u632b\u6298\u611f\u3002 \u3000\u3000\u4ed6\u8f6c\u8eab\u8d76\u5230\u4e86\u4e03\u5b9d\u548c\u5c1a\u7684\u4e0b\u69bb\u4e4b\u5904\uff0c\u5927\u548c\u5c1a\u4f3c\u662f\u65e9\u5df2\u5728\u7b49\u5019\u4e00\u822c\uff0c\u7acb\u523b\u4e00\u6325\u624b\uff0c\u80e1\u5143\u95ea\u8eab\u800c\u51fa\u3002 \u3000\u3000\u663e\u7136\u662f\u5728\u5916\u9762\u653e\u54e8\u3001\u6212\u5907\u3002 \u3000\u3000\u770b\u4ed6\u4e3e\u6b62\u5c0f\u5fc3\uff0c\u6c5f\u67ab\u5fc3\u4e2d\u5927\u60a6\uff0c\u6697\u9053\uff1a\u201c\u8fd9\u4e2a\u9152\u8089\u548c\u5c1a\uff0c\u786e\u5b9e\u53ef\u4ee5\u62c5\u5f53\u5927\u4efb\u7684\u4eba\u2026\u2026\u201d \u3000\u3000\u8d5e\u7f8e\u7684\u5ff5\u5934\u8fd8\u672a\u8f6c\u5b8c\uff0c\u9152\u8089\u548c\u5c1a\u5df2\u5f00\u4e86\u53e3\uff0c\u9053\uff1a\u201c\u6c5f\u8001\u5f1f\uff0c\u60c5\u5f62\u6709\u4e9b\u4e0d\u5bf9\uff01\u201d \u3000\u3000\u6c5f\u67ab\u5fc3\u5934\u4e00\u9707\uff0c\u9053\uff1a\u201c\u90a3\u91cc\u4e0d\u5bf9\u4e86\uff1f\u201d \u3000\u3000\u201c\u548c\u5c1a\u5230\u8fce\u5bbe\u9601\u7684\u9644\u8fd1\u6253\u4e2a\u8f6c\uff0c\u53d1\u89c9\u90a3\u91cc\u4f3c\u662f\u6709\u4eba\u5728\u6697\u4e2d\u6212\u5907\uff0c\u663e\u7136\u6709\u91cd\u8981\u4eba\u7269\u5230\u6b64\u3002\u201d \u3000\u3000\u201c\u770b\u5230\u4e86\uff0c\u8fd8\u662f\u63a8\u60f3\u51fa\u6765\u7684\uff1f\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff0c\u201c\u8fd9\u4ef6\u4e8b\u975e\u540c\u5c0f\u53ef\uff0c\u4e0d\u80fd\u9a6c\u864e\u4ece\u4e8b\u3002\u201d \u3000\u3000\u201c\u6ca1\u6709\u770b\u5230\u4eba\uff0c\u4f46\u5374\u53d1\u89c9\u8fce\u5bbe\u9601\u56db\u5468\u90fd\u6709\u4eba\u5728\u79d8\u5bc6\u7684\u8b66\u6212\u7740\uff0c\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u9053\uff0c\u201c\u4ed6\u4eec\u867d\u7136\u88c5\u4f5c\u82e5\u65e0\u5176\u4e8b\uff0c\u4f46\u7792\u4e0d\u8fc7\u6211\u548c\u5c1a\uff0c\u4ed6\u4eec\u90a3\u9510\u5229\u7684\u773c\u795e\uff0c\u4e0d\u505c\u5411\u56db\u5468\u641c\u5bfb\uff0c\u6700\u7cdf\u7684\u662f\u6211\u548c\u5c1a\u53d1\u89c9\u90a3\u4e9b\u4eba\uff0c\u4e0d\u662f\u6d1b\u9633\u5c45\u4e2d\u7684\u9556\u624b\u2026\u2026\u201d \u3000\u3000\u201c\u4f60\u770b\u5f97\u5f88\u6e05\u695a\u5417\uff1f\u201d\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff0c\u201c\u90a3\u4e9b\u4eba\u4e0d\u662f\u6d1b\u9633\u5c45\u4e2d\u7684\u4eba\uff1f\u201d \u3000\u3000\u201c\u7edd\u9519\u4e0d\u4e86\u2026\u2026\u201d\u9152\u8089\u548c\u5c1a\u9053\uff0c\u201c\u4ed6\u4eec\u7684\u8863\u7740\u4e0d\u540c\uff0c\u5e74\u9f84\u4e5f\u6709\u5f88\u5927\u7684\u5dee\u8ddd\u3002\u201d \u3000\u3000\u4f46\u95fb\u55e4\u7684\u4e00\u58f0\uff0c\u4e00\u8282\u67af\u679d\uff0c\u7a7f\u7a97\u800c\u5165\u3002 \u3000\u3000\u6c5f\u67ab\u51b7\u7b11\u4e00\u58f0\uff0c\u4f3c\u8981\u884c\u52a8\u3002 \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u80e1\u5143\u4f20\u5165\u7684\u8baf\u606f\uff0c\u6709\u4eba\u6765\u4e86\uff0c\u800c\u4e14\uff0c\u6765\u4eba\u8eab\u4efd\u5f88\u9ad8\uff0c\u53ef\u80fd\u5c31\u662f\u9b54\u773c\u9093\u98de\u3002\u201d \u3000\u3000\u201c\u6765\u5f97\u597d\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff0c\u201c\u6b63\u597d\u628a\u53d1\u73b0\u7684\u4e8b\u60c5\u544a\u8bc9\u4ed6\uff0c\u7528\u4e0d\u7740\u54b1\u4eec\u518d\u82b1\u8d39\u7cbe\u795e\u4e86\u3002\u201d \u3000\u3000\u9152\u8089\u548c\u5c1a\u7684\u63a8\u65ad\u4e0d\u9519\uff0c\u679c\u7136\u662f\u9093\u98de\u5feb\u6b65\u800c\u5165\u3002\u76ee\u7779\u6c5f\u67ab\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u679c\u7136\u5728\u8fd9\u91cc\uff0c\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u63a5\u9053\uff1a\u201c\u5c5e\u4e0b\u662f\u63a5\u5230\u6025\u62a5\u8fce\u5bbe\u9601\u4e2d\u60c5\u5f62\u5f02\u5e38\uff0c\u7279\u5730\u8d76\u6765\u3002\u201d\u3000\u201c\u6709\u8fd9\u7b49\u4e8b\uff0c\u8bf4\u8bf4\u770b\uff0c\u90a3\u65b9\u9762\u60c5\u5f62\u5f02\u5e38\uff1f\u201d\u3000\u4e03\u5b9d\u548c\u5c1a\u9053\uff1a\u201c\u4e0d\u5c11\u4eba\u5728\u56db\u5916\u6212\u5907\u2026\u2026\u201d\u3000\u201c\u662f\u4e0d\u662f\u6d1b\u9633\u5c45\u4e2d\u7684\u9556\u624b\uff1f\u2026\u2026\u201d\u9093\u98de\u9053\uff0c\u201c\u90a3\u4e9b\u4eba\u8863\u7740\u3001\u5f62\u8c8c\u5982\u4f55\uff1f\u201d \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\u6d1b\u9633\u5c45\u4e2d\u7684\u9556\u624b\uff0c\u662f\u5426\u4e5f\u8981\u8d1f\u8d23\u8fce\u5bbe\u9601\u7684\u6212\u5907\uff0c\u4f46\u90a3\u4e9b\u4eba\u7edd\u4e0d\u662f\u6211\u4eec\u7684\u9556\u624b\uff0c\u4ed6\u4eec\u8863\u7740\u4e0d\u540c\uff0c\u5e74\u9f84\u4e5f\u5728\u4e09\u5341\u4ee5\u4e0a\uff0c\u6240\u4ee5\uff0c\u5728\u4e0b\u624d\u5411\u97e9\u526f\u603b\u7ba1\u62a5\u544a\u3002\u201d\u559d\uff01 \u3000\u3000\u4e24\u4eba\u4f60\u8a00\u6211\u8bed\uff0c\u914d\u5408\u5f97\u5929\u8863\u65e0\u7f1d\u3002 \u3000\u3000\u9093\u98de\u76b1\u8d77\u7709\u5934\uff0c\u6765\u56de\u5728\u623f\u4e2d\u8d70\u52a8\uff0c\u9053\uff1a\u201c\u6628\u5929\u624d\u6765\u4e86\u4e24\u4e2a\uff0c\u51c6\u9053\u4eca\u5929\u53c8\u6709\u4eba\u6765\uff0c\u5982\u679c\u6709\u968f\u884c\u7684\u4e13\u4eba\u5728\u4f4f\u5904\u6212\u5907\uff0c\u90a3\u81f3\u5c11\u4e5f\u8be5\u662f\u62a4\u6cd5\u4ee5\u4e0a\u7684\u8eab\u4efd\u2026\u2026\u201d \u3000\u3000\u4ed6\u81ea\u8a00\u81ea\u8bed\uff0c\u6c5f\u67ab\u548c\u4e03\u5b9d\u548c\u5c1a\u5374\u8083\u7acb\u4e00\u4fa7\uff0c\u4e0d\u8a00\u4e0d\u8bed\u3002 \u3000\u3000\u8fd9\u7b49\u5173\u952e\u65f6\u523b\uff0c\u6c5f\u67ab\u611f\u89c9\u5230\uff0c\u591a\u8a00\u63d2\u53e3\uff0c\u4e0d\u5982\u9759\u4ee5\u5f85\u53d8\u7684\u597d\u3002 \u3000\u3000\u679c\u7136\uff0c\u9093\u98de\u505c\u4e0b\u8eab\u5b50\uff0c\u56de\u987e\u4e86\u6c5f\u67ab\u4e00\u773c\uff0c\u9053\uff0c\u201c\u8fd9\u4e48\u8bf4\uff0c\u8fce\u5bbe\u9601\u53c8\u6765\u4e86\u5ba2\u4eba\uff1f\u201d \u3000\u3000\u201c\u662f\uff01\u91cd\u8981\u7684\u5ba2\u4eba\u3002\u201d\u6c5f\u67ab\u9053\uff0c\u201c\u8fce\u5bbe\u9601\uff0c\u4e0d\u5bf9\u5916\u8425\u4e1a\uff0c\u80fd\u591f\u4e0a\u8fdb\u53bb\u7684\uff0c\u90fd\u8be5\u662f\u81ea\u5df1\u4eba\u624d\u5bf9\u3002\u201d \u3000\u3000\u9093\u98de\u9053\uff1a\u201c\u8fc7\u53bb\uff0c\u8eab\u4efd\u5728\u62a4\u6cd5\u7ea7\u4ee5\u4e0a\u7684\u4eba\uff0c\u4f4f\u5165\u8fce\u5bbe\u9601\u540e\uff0c\u7b2c\u4e00\u4ef6\u4e8b\uff0c\u5c31\u662f\u6d3e\u4eba\u901a\u77e5\u6211\uff0c\u7531\u6211\u5b89\u6392\u4ed6\u4eec\u7684\u98df\u3001\u8272\u751f\u6d3b\uff0c\u8fd9\u4e00\u6b21\uff0c\u4e3a\u4ec0\u4e48\u6ca1\u6709\u4eba\u6765\u544a\u8bc9\u6211\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u4e5f\u8bb8\uff0c\u8fd9\u4e00\u6b21\u4f4f\u5165\u8fce\u5bbe\u9601\u7684\u4eba\uff0c\u90fd\u662f\u62a4\u6cd5\u7ea7\u4ee5\u4e0b\u7684\u4eba\u3002\u201d \u3000\u3000\u201c\u4e0d\u5bf9\uff01\u53ea\u6709\u62a4\u6cd5\u7ea7\u4ee5\u4e0a\u7684\u4eba\uff0c\u624d\u4f1a\u6709\u957f\u968f\u4f8d\u4ece\uff0c\u5728\u4f4f\u5904\u6212\u5907\u2026\u2026\u201d\u9093\u98de\u9053\uff0c\u4f46\u8fd9\u7b49\u5927\u4e8b\uff0c\u4e3a\u4ec0\u4e48\u7adf\u6ca1\u6709\u4eba\u901a\u62a5\u4e8e\u6211\u5462\uff1f\u201d \u3000\u3000\u201c\u603b\u7ba1\uff0c\u6b64\u65f6\u6b64\u60c5\uff0c\u603b\u7ba1\u4e5f\u4e0d\u7528\u4fdd\u7559\u4ec0\u4e48\u4e86\uff0c\u6709\u4ec0\u4e48\u90fd\u8be5\u8bf4\u51fa\u6765\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u6211\u77e5\u65e0\u4e0d\u8a00\uff0c\u4f60\u60f3\u77e5\u9053\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u8fce\u5bbe\u9601\u662f\u63a5\u5f85\u81ea\u5df1\u4eba\u7684\u5730\u65b9\uff0c\u4f46\u5b83\u53d7\u4e0d\u53d7\u603b\u7ba1\u7684\u8282\u5236\u5462\uff1f\u201d \u3000\u3000\u201c\u6d1b\u9633\u5c45\u5e94\u8be5\u4ee5\u6211\u4e3a\u9996\u2026\u2026\u201d \u3000\u3000\u201c\u8fd9\u5c31\u591f\u4e86\uff0c\u4ed6\u4eec\u4e0d\u80af\u5448\u62a5\u603b\u7ba1\uff0c\u54b1\u4eec\u53ef\u4ee5\u81ea\u5df1\u53bb\u77a7\u77a7\u554a\u2026\u2026\u201d\u6c5f\u67ab\u8bf4\uff0c\u201c\u603b\u7ba1\u4e5f\u8be5\u91cd\u632f\u96c4\u98ce\uff0c\u529e\u51e0\u4e2a\u4e0d\u542c\u4ee4\u8c15\u7684\u4eba\uff0c\u518d\u6811\u6743\u5a01\u3002\u201d \u3000\u3000\u201c\u5982\u679c\uff0c\u4f4f\u5165\u8fce\u5bbe\u9601\u4eba\u8eab\u4efd\u5f88\u9ad8\uff0c\u5c82\u4e0d\u662f\u5f04\u5de7\u6210\u62d9\u4e86\u3002\u201d \u3000\u3000\u201c\u4e0d\u4f1a\uff01\u603b\u7ba1\u638c\u63e1\u4e86\u4e3b\u52a8\uff0c\u5982\u4f55\u63a5\u5f85\u90a3\u4eba\u7531\u603b\u7ba1\u4e34\u573a\u51b3\u5b9a\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff0c\u201c\u4e00\u5207\u64cd\u4e4b\u5728\u6211\uff0c\u5982\u679c\u6765\u4eba\u65e9\u5c31\u5bf9\u6211\u4eec\u6709\u4e86\u4e0d\u5229\u7684\u51c6\u5907\uff0c\u90a3\u5c31\u2026\u2026\u201d \u3000\u3000\u201c\u5f53\u573a\u7ffb\u8138\uff0c\u95f9\u4e2a\u4e0d\u6b22\u800c\u6563\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u4f1a\u90a3\u4e48\u8f7b\u677e\u5427\uff01\u603b\u7ba1\u5927\u4eba\uff0c\u62c9\u4e0b\u4e86\u8138\uff0c\u5c31\u6765\u4e2a\u5175\u5203\u76f8\u89c1\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff0c\u201c\u7b49\u4ed6\u4eec\u5f00\u59cb\u884c\u52a8\uff0c\u8fd8\u4e0d\u662f\u4e00\u6837\u7ed3\u679c\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u4e89\u53d6\u8fd9\u4e00\u70b9\u4e3b\u52a8\uff0c\u5148\u673a\uff1f\u201d \u3000\u3000\u9093\u98de\u9053\uff1a\u201c\u6211\u8981\u5e26\u4ec0\u4e48\u4eba\u5462\uff1f\u201d \u3000\u3000\u663e\u7136\uff0c\u4ed6\u5df2\u88ab\u6c5f\u67ab\u8bf4\u52a8\u3002 \u3000\u3000\u201c\u5c5e\u4e0b\u8fd9\u4e2a\u526f\u603b\u7ba1\u4e00\u5b9a\u5f97\u53bb\uff0c\u9f50\u56db\u5e26\u7684\u4e09\u4e2a\u5e2e\u624b\uff0c\u4e5f\u53ef\u4ee5\u968f\u884c\uff0c\u8fd9\u53ef\u4ee5\u8003\u9a8c\u4e00\u4e0b\u4ed6\u7684\u5fe0\u5b9e\u7a0b\u5ea6\uff0c\u4e5f\u53ef\u4ee5\u6d4b\u9a8c\u4e00\u4e0b\u4ed6\u4eec\u7684\u6b66\u529f\uff01\u201d \u3000\u3000\u201c\u4e24\u4f4d\u653e\u5fc3\u2026\u2026\u201d\u9152\u8089\u548c\u5c1a\u8bf4\uff0c\u201c\u6211\u4eec\u5168\u529b\u4ee5\u8d74\uff0c\u53ea\u8981\u603b\u7ba1\u4e00\u58f0\u4ee4\u4e0b\uff0c\u5c31\u7b97\u5bf9\u65b9\u662f\u94c1\u6253\u7684\u91d1\u521a\uff0c\u6211\u4eec\u4e5f\u522e\u4e0b\u5b83\u4e09\u65a4\u94c1\u5c51\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u54b1\u4eec\u73b0\u5728\u5c31\u53bb\u77a7\u77a7\uff01\u201d\u9093\u98de\u4f3c\u662f\u4e5f\u88ab\u4e24\u4eba\u6fc0\u8d77\u4e86\u8c6a\u6c14\u3002 \u3000\u3000\u201c\u603b\u7ba1\u2026\u2026\u201d\u6c5f\u67ab\u4f4e\u58f0\u9053\uff0c\u201c\u518d\u9009\u4e24\u4e2a\u9556\u624b\u5e26\u4e0a\uff0c\u6d4b\u8bd5\u4e00\u4e0b\u4ed6\u4eec\u662f\u5426\u8fd8\u542c\u4ece\u4f60\u7684\u4ee4\u8c15\u3002\u201d \u3000\u3000\u201c\u5bf9\uff01\u2026\u2026\u201d\u9093\u98de\u53cc\u76ee\u66b4\u5c04\u51fa\u78a7\u7eff\u7684\u5149\u7130\uff0c\u63a5\u9053\uff1a\u201c\u5982\u679c\u771f\u95f9\u8d77\u6765\uff0c\u5c31\u95f9\u5b83\u4e2a\u7ed3\u679c\u51fa\u6765\uff01\u201d \u3000\u3000\u8fce\u5bbe\u9601\u5efa\u5728\u6d1b\u9633\u5c45\u897f\u5357\u4e00\u89d2\uff0c\u662f\u4e00\u5904\u72ec\u7acb\u7684\u5ead\u9662\uff0c\u6574\u4e2a\u6d1b\u9633\u5c45\uff0c\u53ea\u6709\u8fd9\u91cc\u6709\u7af9\u5b50\u3002 \u3000\u3000\u9093\u98de\u5f53\u5148\u800c\u884c\uff0c\u76f4\u5954\u5230\u8fce\u5bbe\u9601\u7684\u5927\u95e8\u53e3\u5904\u3002 \u3000\u3000\u56db\u4e2a\u6563\u5e03\u5728\u9601\u5916\u56db\u5468\u7684\u4e2d\u5e74\u5927\u6c49\uff0c\u4e5f\u8fc5\u5feb\u96c6\u4e2d\u5728\u8fce\u5bbe\u9601\u7684\u5927\u95e8\u5916\u9762\uff0c\u800c\u4e14\uff0c\u52a8\u4f5c\u5947\u5feb\u3002 \u3000\u3000\u56db\u4e2a\u4eba\u4e00\u5b57\u6392\u5f00\uff0c\u6321\u5728\u95e8\u53e3\uff0c\u4e5f\u62e6\u4f4f\u4e86\u9093\u98de\u7b49\u4e00\u884c\u4eba\u7684\u53bb\u8def\u3002 \u3000\u3000\u9093\u98de\u6253\u91cf\u56db\u4eba\uff0c\u5e74\u7eaa\u5728\u4e09\u5341\u4e0a\u4e0b\uff0c\u8863\u670d\u7684\u989c\u8272\u867d\u7136\u4e0d\u540c\uff0c\u4f46\u5374\u662f\u4e00\u5f0f\u7684\u957f\u888d\uff0c\u9762\u76ee\u964c\u751f\uff0c\u90fd\u4e0d\u8ba4\u8bc6\u3002 \u3000\u3000\u201c\u56db\u4f4d\u53ef\u77e5\u9053\u8fd9\u662f\u4ec0\u4e48\u5730\u65b9\uff1f\u6211\u53c8\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u56db\u4e2a\u957f\u888d\u4eba\u6700\u5de6\u9996\u7684\u4e00\u4e2a\uff0c\u6307\u6307\u8eab\u540e\u7684\u201c\u8fce\u5bbe\u9601\u201d\u91d1\u5b57\u6728\u533e\uff0c\u53cc\u76ee\u76ef\u6ce8\u5728\u9093\u98de\u7684\u8138\u4e0a\uff0c\u6447\u6447\u5934\u3002 \u3000\u3000\u6c5f\u67ab\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u56db\u4f4d\u5982\u662f\u4e0d\u80af\u8ba9\u8def\uff0c\u90a3\u5c31\u53eb\u8fce\u5bbe\u9601\u7684\u638c\u67dc\u51fa\u6765\u3002\u201d \u3000\u3000\u201c\u6765\u4e86\u2026\u2026\u201d\u4e00\u4e2a\u7a7f\u7740\u9ed1\u8272\u957f\u888d\uff0c\u7559\u7740\u4e00\u628a\u5c71\u7f8a\u80e1\u5b50\u7684\u4eba\uff0c\u7f13\u6b65\u884c\u51fa\uff0c\u4e00\u62b1\u62f3\uff0c\u9053\uff1a \u3000\u3000\u201c\u8fce\u5bbe\u9601\u638c\u67dc\u738b\u80dc\uff0c\u89c1\u8fc7\u603b\u7ba1\u3002\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u56db\u4e2a\u62e6\u4f4f\u53bb\u8def\u7684\u4eba\u5df2\u95ea\u5230\u4e24\u4fa7\uff0c\u4f46\u5176\u4e2d\u4e00\u4eba\uff0c\u5374\u8f6c\u8eab\u5411\u5185\u884c\u53bb\uff0c\u53e6\u5916\u4e09\u4eba\uff0c\u516d\u53ea\u773c\u775b\uff0c\u7741\u5f97\u5706\u5706\u7684\uff0c\u770b\u7740\u9093\u98de\uff0c\u9887\u6709\u770b\u70ed\u95f9\u7684\u610f\u5473\u3002 \u3000\u3000\u201c\u738b\u638c\u67dc\u773c\u4e2d\u8fd8\u6709\u6211\u8fd9\u4e2a\u603b\u7ba1\u5417\uff1f\u201d \u3000\u3000\u201c\u603b\u7ba1\u8a00\u91cd\u4e86\u2026\u2026\u201d\u738b\u80dc\u9053\uff0c\u201c\u6211\u60f3\u4e0d\u51fa\u54ea\u91cc\u8f7b\u85d0\u4e86\u603b\u7ba1\uff1f\u201d \u3000\u3000\u201c\u8fd9\u8fce\u5bbe\u9601\u4e2d\u6765\u4e86\u8d35\u5bbe\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u5411\u672c\u603b\u7ba1\u901a\u62a5\u4e00\u58f0\uff1f\u201d \u3000\u3000\u738b\u80dc\u9053\uff1a\u201c\u603b\u7ba1\u8fd9\u662f\u6b32\u52a0\u4e4b\u7f6a\u4e86\uff0c\u4f4f\u5165\u8fce\u5bbe\u9601\u4e2d\u4eba\uff0c\u613f\u4e0d\u613f\u901a\u77e5\u603b\u7ba1\uff0c\u6089\u7531\u4ed6\u4eec\u81ea\u5df1\u51b3\u5b9a\uff01\u8fce\u5bbe\u9601\u4ece\u6765\u4e0d\u52a0\u8fc7\u95ee\u3002\u201d \u3000\u3000\u201c\u90a3\u8981\u4f60\u8fd9\u4e2a\u638c\u67dc\u7684\u505a\u4ec0\u4e48\uff1f\u2026\u2026\u201d\u6c5f\u67ab\u51b7\u51b7\u9053\uff0c\u201d\u77e5\u60c5\u4e0d\u62a5\uff0c\u4f7f\u603b\u7ba1\u7684\u8033\u76ee\u5931\u7075\uff0c\u5c31\u662f\u4e00\u6761\u4e0d\u53ef\u9976\u6055\u7684\u5927\u7f6a\u3002\u201d \u3000\u3000\u201c\u9601\u4e0b\u662f\u2026\u2026\u201d\u738b\u80dc\u772f\u7740\u4e00\u53cc\u773c\uff0c\u77a7\u7740\u6c5f\u67ab\uff0c\u8138\u4e0a\u662f\u4e00\u7247\u4e0d\u5c51\u4e4b\u8272\u3002 \u3000\u3000\u201c\u526f\u603b\u7ba1\u97e9\u9738\u3002\u201d \u3000\u3000\u201c\u6ca1\u542c\u8bf4\u8fc7\u2026\u2026\u201d\u738b\u80dc\u51b7\u7b11\u4e00\u58f0\u53c8\u9053\uff1a\u201c\u6211\u8fd9\u4e2a\u8fce\u5bbe\u9601\u7684\u638c\u67dc\uff0c\u4e5f\u4e0d\u662f\u6d1b\u9633\u5c45\u4e2d\u603b\u7ba1\u6d3e\u7684\u3002\u201d \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u6d1b\u9633\u5c45\u4e2d\u7684\u603b\u7ba1\uff0c\u8282\u5236\u6d1b\u9633\u5c45\u8303\u56f4\u4ee5\u5185\u7684\u4eba\u4eba\u4e8b\u4e8b\uff0c\u5f53\u7136\uff0c\u4e5f\u5305\u62ec\u4e86\u4f60\u8fd9\u4e2a\u8fce\u5bbe\u9601\u7684\u638c\u67dc\u4e86\u3002\u201d \u3000\u3000\u201c\u90a3\u4e0d\u8fc7\u662f\u540d\u4e49\u4e0a\u7684\u5ec9\u6d01\uff0c\u4e8b\u5b9e\u4e0a\uff0c\u8fce\u5bbe\u9601\u7684\u4eba\u4eba\u4e8b\u4e8b\uff0c\u90fd\u7531\u738b\u67d0\u4f5c\u4e3b\u3002\u201d \u3000\u3000\u201c\u4f60\u662f\u8bf4\u603b\u7ba1\u7ba1\u4e0d\u4f4f\u4f60\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff0c\u201c\u66f4\u4e0d\u628a\u6211\u8fd9\u4e2a\u526f\u603b\u7ba1\u770b\u5728\u773c\u4e2d\u4e86\uff1f\u201d \u3000\u3000\u738b\u80dc\u51b7\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u597d\u50cf\u4e0d\u9519\u5427\uff01\u201d \u3000\u3000\u9093\u98de\u5927\u58f0\u559d\u9053\uff1a\u201c\u738b\u80dc\uff0c\u4f60\u597d\u5927\u7684\u80c6\u5b50\uff01\u201d \u3000\u3000\u201c\u7389\u67d0\u7684\u80c6\u5b50\uff0c\u672c\u6765\u5c31\u4e0d\u5c0f\u2026\u2026\u201d \u3000\u3000\u9093\u98de\u6012\u9053\uff1a\u201c\u4f60\u8be5\u6b7b\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u5e94\u58f0\u51fa\u638c\uff0c\u5feb\u5982\u7535\u5149\u77f3\u706b\uff0c\u9093\u98de\u4e0b\u9762\u7684\u8bdd\uff0c\u8fd8\u672a\u51fa\u53e3\uff0c\u738b\u80dc\u5df2\u60e8\u54fc\u4e00\u58f0\uff0c\u8dcc\u6454\u5728\u5730\u4e0a\uff0c\u4e03\u7a83\u8840\u6d8c\uff0c\u547d\u7edd\u800c\u901d\u3002 \u3000\u3000\u9093\u98de\u5446\u4e01\u4e00\u5446\uff0c\u770b\u7740\u5730\u4e0a\u7684\u5c38\u4f53\uff0c\u9053\uff1a\u201c\u4f60\u6740\u4e86\u4ed6\uff1f\u201d \u3000\u3000\u201c\u662f\uff01\u4ed6\u65e2\u7136\u8be5\u6b7b\uff0c\u7559\u4ed6\u4f5c\u751a\u2026\u2026\u201d \u3000\u3000\u53ea\u542c\u4e00\u4e2a\u9634\u51b7\u7684\u58f0\u97f3\uff0c\u4f20\u4e86\u8fc7\u6765\uff0c\u9053\uff1a\u201c\u597d\u5feb\u7684\u4e00\u638c\uff0c\u9093\u8001\u5f1f\uff0c\u4f60\u7f57\u81f4\u4e86\u8fd9\u6837\u7684\u4eba\u624d\uff0c\u4e5f\u4e0d\u5411\u5802\u53e3\u5448\u62a5\u4e00\u4e0b\u3002\u201d \u3000\u3000\u90a3\u58f0\u97f3\u7ec6\u7ec6\u5f31\u5f31\uff0c\u4f46\u5374\u8ba9\u6bcf\u4e2a\u4eba\u542c\u5f97\u6e05\u695a\uff0c\u4ed6\u53ea\u8d5e\u6c5f\u67ab\u7684\u638c\u5feb\uff0c\u5374\u4e0d\u63d0\u738b\u80dc\u4e4b\u6b7b\u3002\u663e\u7136\u5e76\u65e0\u8d23\u65a5\u9093\u98de\u7684\u610f\u601d\u3002 \u3000\u3000\u4f46\u9093\u98de\u5374\u88ab\u5f31\u7ec6\u58f0\u97f3\u5413\u574f\u4e86\uff0c\u62b1\u62f3\u8fc7\u9876\uff0c\u8eac\u4e0b\u8eab\u5b50\uff0c\u9053\uff1a\u201c\u662f\u526f\u603b\u5802\u4e3b\u5417\uff1f\u9093\u98de\u53e9\u89c1\u4f60\u8001\u7684\u91d1\u5b89\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u53e9\u89c1\uff0c\u7adf\u7136\u662f\u771f\u7684\u8dea\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u8fd9\u5c31\u5bb3\u82e6\u4e86\u6c5f\u67ab\u548c\u4e03\u5b9d\u548c\u5c1a\uff0c\u53ea\u597d\u8ddf\u7740\u8dea\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u6c5f\u67ab\u7684\u4fee\u517b\u518d\u597d\uff0c\u4f46\u5e74\u5c11\u6c14\u76db\u7684\u6bdb\u75c5\u8fd8\u6709\uff0c\u8981\u8dea\u4e00\u4e2a\u4e0d\u76f8\u8bc6\u7684\u4eba\uff0c\u5b9e\u5728\u662f\u4e00\u4ef6\u6781\u4e0d\u60c5\u613f\u7684\u4e8b\uff0c\u5fc3\u4e2d\u72b9\u8c6b\u4e0d\u51b3\uff0c\u5e78\u597d\uff0c\u8033\u9645\u54cd\u8d77\u4e86\u4e03\u5b9d\u548c\u5c1a\u7684\u58f0\u97f3\uff0c\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u54b1\u4eec\u662f\u6f14\u620f\uff0c\u88c5\u9f99\u50cf\u9f99\uff0c\u8fd9\u88c5\u7f8a\u4e5f\u8981\u50cf\u7f8a\uff0c\u5feb\u8dea\u4e0b\u53bb\uff0c\u90a3\u8001\u5c0f\u4e8e\u76ee\u5149\u9510\u5229\uff0c\u522b\u8ba9\u4ed6\u77a7\u5230\u4e86\uff0c\u5c0f\u4e0d\u5fcd\u5219\u4e71\u5927\u8c0b\u554a\uff01\u201d \u3000\u3000\u6c5f\u67ab\u7684\u819d\u76d6\u7740\u5730\uff0c\u8033\u9645\u4e5f\u54cd\u8d77\u4e86\u7af9\u6756\u89e6\u5730\u4e4b\u58f0\uff0c\u4e00\u4e2a\u8eab\u7740\u6d45\u84dd\u957f\u886b\u7684\u8001\u8005\uff0c\u624b\u6267\u9752\u7af9\u6756\uff0c\u7f13\u6b65\u800c\u6765\u3002 \u3000\u3000\u90a3\u4f4d\u8dd1\u56de\u53bb\u7684\u4e2d\u5e74\u4eba\uff0c\u7d27\u968f\u5728\u8001\u8005\u8eab\u540e\u3002 \u3000\u3000\u201c\u4e0d\u77e5\u526f\u603b\u5802\u4e3b\u9a7e\u4e34\uff0c\u9093\u98de\u6709\u5931\u8fdc\u8fce\uff0c\u7948\u4e07\u8001\u6055\u7f6a\u3002\u201d \u3000\u3000\u6c5f\u67ab\u5fae\u62ac\u76ee\u5149\uff0c\u53d1\u89c9\u6765\u4eba\u679c\u7136\u5f88\u8001\uff0c\u767d\u9aef\u96ea\u53d1\uff0c\u8fde\u4e24\u6761\u5782\u76ee\u957f\u7709\uff0c\u4e5f\u4e00\u7247\u94f6\u767d\uff0c\u8138\u8272\u5012\u662f\u4e00\u7247\u7ea2\u6da6\uff0c\u53ea\u662f\u592a\u7626\u4e86\uff0c\u7626\u7684\u5168\u8eab\u522e\u4e0d\u4e0b\u4e24\u65a4\u51c0\u8089\u6765\uff01 \u3000\u3000\u4f46\u63e1\u6756\u7684\u53f3\u624b\u652f\u5f88\u5927\uff0c\u624b\u81c2\u5947\u957f\uff0c\u662f\u4e2a\u751f\u5177\u5f02\u76f8\u7684\u8001\u4eba\uff0c\u53ef\u60dc\uff0c\u6c5f\u67ab\u8ba4\u4e0d\u51fa\u4ed6\u7684\u8eab\u4efd\u6765\u3002 \u3000\u3000\u201c\u4f60\u4eec\u90fd\u8d77\u6765\u2026\u2026\u201d \u3000\u3000\u58f0\u97f3\u662f\u6709\u6c14\u65e0\u529b\uff0c\u4f46\u5374\u5b87\u5b57\u6e05\u695a\u3002 \u3000\u3000\u9093\u98de\u7ad9\u8d77\u8eab\u5b50\uff0c\u4f46\u6c5f\u67ab\u5374\u611f\u89c9\u5230\u4ed6\u7684\u8eab\u5b50\u5728\u5fae\u5fae\u53d1\u6296\uff0c\u663e\u662f\u5185\u5fc3\u4e2d\u7684\u60ca\u6050\uff0c\u5df2\u81f3\u65e0\u6cd5\u63a7\u5236\u3002 \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u4ea6\u8ba4\u51fa\u6765\u4eba\u662f\u8c01\uff0c\u5fc3\u5934\u4ea6\u89c9\u9707\u52a8\uff0c\u4f46\u4ed6\u5374\u4ecd\u80fd\u795e\u8272\u81ea\u82e5\u3002 \u3000\u3000\u968f\u6765\u7684\u80e1\u840d\u3001\u6bb5\u4e5d\uff0c\u4e5f\u8ba4\u51fa\u6765\u4eba\u8eab\u4efd\uff0c\u5fc3\u5934\u8df3\u52a8\u4e0d\u5df2\u3002 \u3000\u3000\u80e1\u5143\u4e0d\u8bc6\u6765\u8005\uff0c\u5012\u548c\u6c5f\u67ab\u4e00\u822c\u9547\u9759\u5982\u6052\uff0c\u8fd9\u5c31\u53eb\u804b\u5b50\u4e0d\u6015\u96f7\u3002 \u3000\u3000\u9093\u98de\u5e26\u6765\u7684\u4e24\u540d\u5e74\u8f7b\u9556\u624b\uff0c\u4e5f\u4fdd\u7740\u9547\u9759\u51b7\u6f20\uff0c\u65e0\u52a8\u4e8e\u8877\u3002 \u3000\u3000--------- \u7b2c \u4e94 \u56de\u3000\u6c5f\u5c11\u4fa0\u667a\u60d1\u9752\u51e4\u60c5 \u3000 \u3000\u3000\u201c\u9093\u8001\u5f1f\uff0c\u4f60\u5e26\u4e86\u8fd9\u591a\u4eba\u6765\uff0c\u53ef\u662f\u65e9\u5c31\u5b58\u5fc3\u6740\u4eba\u4e86\u2026\u2026\u3002\u201d \u3000\u3000\u767d\u53d1\u8001\u8005\u7684\u7ec6\u5f31\u58f0\u97f3\u4e2d\uff0c\u6709\u4e00\u80a1\u9634\u51b7\u7684\u5473\u9053\u3002 \u3000\u3000\u201c\u6211\u2026\u2026\u6211\u2026\u2026\u201d \u3000\u3000\u9093\u98de\u5fc3\u4e2d\u60ca\u6050\u8fc7\u751a\uff0c\u820c\u5934\u6253\u7ed3\uff0c\u6211\u4e86\u534a\u5929\uff0c\u6211\u4e0d\u51fa\u4e2a\u6240\u4ee5\u7136\u6765\u3002 \u3000\u3000\u6c5f\u67ab\u5374\u63a5\u4e86\u53e3\uff0c\u9053\uff1a\u201c\u8fce\u5bbe\u9601\u7684\u738b\u80dc\uff0c\u8f7b\u4fae\u603b\u7ba1\uff0c\u77e5\u60c5\u4e0d\u62a5\uff0c\u81f4\u4f7f\u526f\u603b\u5802\u4e3b\u5927\u9a7e\u5149\u4e34\uff0c\u603b\u7ba1\u4ea6\u65e0\u6240\u77e5\uff0c\u6740\u738b\u80dc\u6574\u8083\u7eaa\u521a\uff0c\u6709\u4f55\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u9093\u98de\u5403\u60ca\u5730\u9053\uff1a\u201c\u97e9\u526f\u603b\u7ba1\u5c11\u4e0d\u66f4\u4e8b\uff0c\u8bf7\u4e07\u8001\u6055\u4ed6\u65e0\u77e5\u3002\u201d \u3000\u3000\u767d\u53d1\u8001\u8005\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u9093\u8001\u5f1f\uff0c\u97e9\u526f\u603b\u7ba1\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u201d \u3000\u3000\u4ed6\u4e00\u53e3\u4e00\u4e2a\u9093\u8001\u5f1f\uff0c\u53eb\u7684\u5341\u5206\u4eb2\u8fd1\uff0c\u4f46\u4e0d\u660e\u767d\u9093\u98de\u4e3a\u4ec0\u4e48\u90a3\u6837\u5bb3\u6015\uff0c\u58f0\u97f3\u4e5f\u53d8\u5f97\u98a4\u6296\u8d77\u6765\uff0c\u9053\uff1a\u201c\u4ed6\u2026\u2026\u4ed6\u53eb\u97e9\u9738\uff01\u201d \u3000\u3000\u201c\u679c\u7136\u6709\u51e0\u5206\u9738\u6c14\u2026\u2026\u201d\u767d\u53d1\u8001\u8005\u8138\u4e0a\u6cdb\u73b0\u8d77\u4e00\u4e2a\u5fae\u7b11\u53c8\u9053\uff1a\u201c\u97e9\u9738\uff0c\u4f60\u90a3\u4e00\u638c\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\u540d\u5b57\uff0c\u5c31\u662f\u666e\u666e\u901a\u901a\u7684\u4e00\u638c\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u53ea\u4e0d\u8fc7\u51fa\u624b\u5feb\u4e86\u4e00\u4e9b\uff0c\u738b\u80dc\u60f3\u4e0d\u5230\u6211\u4f1a\u51fa\u624b\uff0c\u5c31\u8fd9\u6837\u4e00\u638c\u628a\u4ed6\u6253\u6b7b\u4e86\u3002\u201d \u3000\u3000\u8fd9\u7ffb\u8bdd\u5168\u65e0\u656c\u610f\uff0c\u9093\u98de\u5df2\u60f3\u4e0d\u51fa\u5982\u4f55\u518d\u66ff\u6c5f\u67ab\u6c42\u60c5\u3001\u63a9\u9970\uff0c\u5c31\u662f\u4e03\u5b9d\u548c\u5c1a\uff0c\u4e5f\u89c9\u7740\u6c5f\u67ab\u8bf4\u7684\u592a\u51b2\u3002 \u3000\u3000\u4f46\u6c5f\u67ab\u5fc3\u4e2d\uff0c\u5374\u53e6\u6709\u4e00\u756a\u6253\u7b97\uff0c\u5982\u82e5\u8fd9\u4f4d\u4e07\u526f\u603b\u5802\u4e3b\u7ffb\u7751\u6740\u4eba\uff0c\u90a3\u5c31\u4e00\u4e0d\u4f5c\u4e8c\u4e0d\u4f11\uff0c\u653e\u624b\u628a\u4ed6\u640f\u6740\u5728\u8fce\u5bbe\u9601\u4e2d\u3002 \u3000\u3000\u6240\u4ee5\uff0c\u4ed6\u53cc\u76ee\u76ef\u6ce8\u5728\u767d\u53d1\u8001\u4eba\u7684\u8138\u4e0a\uff0c\u53e3\u6c14\u4e2d\uff0c\u4e5f\u6ca1\u6709\u656c\u91cd\u7684\u59d4\u5a49\uff0c\u5927\u4e0d\u4e86\u662f\u653e\u624b\u4e00\u6218\u3002 \u3000\u3000\u767d\u53d1\u8001\u8001\u4f3c\u662f\u60f3\u5230\u4e86\u4ec0\u4e48\u91cd\u8981\u4e8b\u60c5\uff0c\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u5bf9\uff01 \u3000\u3000\u65e0\u62db\u80dc\u6709\u62db\uff0c\u90a3\u4e00\u638c\u5982\u662f\u65e9\u6709\u51c6\u5907\uff0c\u738b\u80dc\u5fc5\u6709\u611f\u89c9\uff0c\u90a3\u4e00\u51fb\uff0c\u4e5f\u6740\u4e0d\u4e86\u4ed6\uff0c\u738b\u80dc\u7684\u6b66\u529f\u4e0d\u5f31\uff0c\u800c\u4e14\u662f\u4e00\u4e2a\u975e\u5e38\u5c0f\u5fc3\u7684\u4eba\u3002\u201d \u3000\u3000\u9093\u98de\u5927\u6982\u9010\u6e10\u5730\u5b9a\u4e0b\u4e86\u5fc3\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u5c5e\u4e0b\u606d\u5019\u526f\u603b\u5802\u4e3b\u7684\u88c1\u793a\uff01\u201d \u3000\u3000\u201c\u738b\u80dc\u4e0d\u670d\u4ece\u4f60\u7684\u4ee4\u8c15\uff0c\u8be5\u6b7b\uff01\u201d\u767d\u53d1\u8001\u4eba\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u662f\u6d1b\u9633\u5c45\u7684\u603b\u7ba1\uff0c\u5e94\u8be5\u6709\u6574\u996c\u7eaa\u5f8b\u7684\u6743\u529b\uff0c\u2026\u2026\u201d\u76ee\u5149\u4e00\u63a0\u8eab\u4fa7\u4ece\u4eba\uff0c\u63a5\u9053\uff1a\u201c\u628a\u738b\u80dc\u5c38\u4f53\u62ac\u8d70\uff0c\u4e0d\u8981\u7559\u4e0b\u75d5\u8ff9\u3002\u201d \u3000\u3000\u4e24\u4e2a\u957f\u888d\u4eba\u5e94\u58f0\u51fa\u624b\u3001\u62ac\u8d70\u4e86\u738b\u80dc\u7684\u5c38\u4f53\u3002 \u3000\u3000\u6c5f\u67ab\u770b\u5f97\u5fc3\u4e2d\u4e00\u52a8\uff0c\u5fd6\u9053\uff1a\u201c\u770b\u9093\u98de\u7684\u754f\u60e7\uff0c\u663e\u7136\uff0c\u8fd9\u8001\u8005\u662f\u4e00\u4f4d\u975e\u5e38\u6770\u51fa\u7684\u4eba\u7269\uff0c\u800c\u4e14\uff0c\u51f6\u540d\u5353\u8457\uff0c\u6740\u4eba\u4e8e\u8c08\u7b11\u4e4b\u4e2d\uff0c\u4f46\u770b\u4ed6\u5bf9\u9093\u98de\u7684\u795e\u60c5\uff0c\u5374\u53c8\u6709\u7740\u5e87\u4f51\u7684\u7528\u5fc3\uff0c\u83ab\u975e\u8fd9\u8001\u4eba\u5c31\u662f\u9093\u98de\u7684\u9760\u5c71\u2026\u2026\u3002\u201d \u3000\u3000\u4f46\u95fb\u90a3\u8001\u8005\u63a5\u9053\uff1a\u201c\u9093\u98de\u2026\u2026\u201d \u3000\u3000\u201c\u5c5e\u4e0b\u5728\u2026\u2026\u201d \u3000\u3000\u201c\u542c\u8bf4\u4f60\u5728\u6d1b\u9633\u5c45\u641e\u5f97\u4e0d\u597d\uff0c\u5df2\u7ecf\u6709\u4e24\u5f20\u72b6\u5b50\u9760\u5230\u4e86\u5802\u53e3\uff0c\u603b\u5802\u4e3b\u7279\u5730\u6d3e\u6211\u6765\u67e5\u770b\u4e00\u4e0b\u2026\u2026\u201d\u767d\u53d1\u8001\u4eba\u8bf4\uff0c\u201c\u662f\u6211\u4e0d\u8ba9\u738b\u80dc\u901a\u62a5\u8fc7\u53bb\u7684\uff0c\u5c31\u4e8b\u8bba\u4e8b\uff0c\u4ed6\u5012\u662f\u6b7b\u7684\u51a4\u6789\u3002\u201d \u3000\u3000\u201c\u662f\u662f\u662f\u2026\u2026\u201d\u9093\u98de\u6025\u6025\u63a5\u9053\uff1a\u201c\u5c5e\u4e0b\u7c97\u5fc3\uff0c\u4ee5\u540e\u5168\u529b\u6539\u8fdb\uff0c\u52a1\u6c42\u8c28\u614e\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u80fd\u8d23\u602a\u603b\u7ba1\u2026\u2026\u201d\u6c5f\u67ab\u53c8\u5f00\u53e3\u4e86\uff0c\u201c\u738b\u80dc\u53d6\u6b7b\u4e4b\u9053\uff0c\u602a\u4ed6\u76ee\u65e0\u6cd5\u7eaa\uff0c\u4eba\u662f\u97e9\u67d0\u6740\u7684\uff0c\u5982\u6709\u5931\u8bef\u9886\u8d23\u4e4b\u5904\uff0c\u4e5f\u548c\u603b\u7ba1\u65e0\u5173\uff0c\u97e9\u67d0\u613f\u4e00\u529b\u627f\u62c5\u3002\u201d \u3000\u3000\u9093\u98de\u6012\u58f0\u53f1\u9053\uff1a\u201c\u97e9\u9738\uff0c\u6709\u6211\u5728\u6b64\uff0c\u90a3\u6709\u4f60\u8bf4\u8bdd\u7684\u4efd\uff0c\u9000\u4e0b\u53bb\uff0c\u95ed\u4e0a\u4f60\u90a3\u5f20\u5634\u2026\u2026\u201d \u3000\u3000\u201c\u662f\u662f\u662f\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u8868\u73b0\u51fa\u65e0\u6bd4\u5730\u5c0a\u656c\uff0c\u9000\u540e\u4e09\u6b65\uff0c\u5782\u624b\u800c\u7acb\u3002 \u3000\u3000\u539f\u6765\uff0c\u6c5f\u67ab\u4e00\u76f4\u5b58\u5fc3\u6311\u8d77\u767d\u53d1\u8001\u4eba\u7684\u6012\u706b\uff0c\u51c6\u5907\u4e00\u8bd5\u8eab\u624b\uff0c\u4f46\u90a3\u8001\u4eba\u5374\u4e00\u76f4\u6ca1\u52a8\u6740\u673a\uff0c\u9093\u98de\u4e00\u518d\u4ecb\u5165\uff0c\u7ef4\u62a4\u4e4b\u60c5\uff0c\u663e\u7136\u53ef\u89c1\uff0c\u6c5f\u67ab\u53ea\u597d\u6539\u53d8\u4e86\u4e3b\u610f\uff0c\u6d88\u9000\u53bb\u597d\u80dc\u4e4b\u5fc3\uff0c\u5bf9\u9093\u98de\u8868\u73b0\u51fa\u7edd\u5bf9\u606d\u987a\u3002 \u3000\u3000\u201c\u597d\uff01\u597d\u2026\u2026\u201d\u767d\u53d1\u8001\u4eba\u8fde\u53eb\u4e86\u4e24\u4e2a\u597d\u5b57\uff0c\u63a5\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u5012\u6709\u51e0\u5206\u50b2\u9aa8\uff0c\u4e8c\u5341\u51e0\u5e74\u4e86\uff0c\u4ece\u6765\u6ca1\u6709\u4eba\u6562\u5982\u6b64\u5bf9\u6211\u4e0d\u656c\uff0c\u548c\u6211\u9876\u649e\u2026\u2026\u201d \u3000\u3000\u201c\u97e9\u67d0\u4e0d\u662f\u9876\u61a7\uff0c\u662f\u548c\u526f\u603b\u5802\u4e3b\u8bf4\u7406\u3001\u8bf7\u7f6a\u2026\u2026\u201d \u3000\u3000\u201c\u4f60\u8fd8\u6562\u591a\u5634\uff0c\u9000\u4e0b\u53bb\u2026\u2026\u201d\u9093\u98de\u6012\u58f0\u5927\u543c\u3002 \u3000\u3000\u6c5f\u67ab\u53c8\u9000\u4e09\u6b65\uff0c\u5782\u9996\u8083\u7acb\u3002 \u3000\u3000\u201c\u9093\u8001\u5f1f\uff0c\u4e0d\u8981\u602a\u4ed6\uff0c\u8fd9\u5c0f\u5b50\u7684\u5473\u9053\uff0c\u5012\u548c\u8001\u592b\u5f53\u5e74\u521d\u5165\u6c5f\u6e56\u65f6\uff0c\u6709\u4e9b\u795e\u4f3c\u2026\u2026\u201d\u767d\u53d1\u8001\u4eba\u9053\uff1a\u201c\u8001\u592b\u5012\u662f\u60f3\u548c\u4ed6\u591a\u804a\u51e0\u53e5\u3002\u201d \u3000\u3000\u9093\u98de\u5fae\u5fae\u4e00\u5446\uff0c\u8fde\u5e94\u4e24\u4e2a\u662f\u5b57\u3002 \u3000\u3000\u6c5f\u67ab\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u6211\u80a9\u8d1f\u5927\u4efb\uff0c\u4e0d\u8ba1\u6bc1\u8a89\uff0c\u884c\u4e8b\u867d\u7136\u4e0d\u62e9\u624b\u6bb5\uff0c\u4f46\u5b58\u5fc3\u53ef\u9274\u5929\u65e5\uff0c\u600e\u4f1a\u548c\u4f60\u795e\u4f3c\u2026\u2026\u201d \u3000\u3000\u4f46\u95fb\u81ea\u53d1\u8001\u4eba\u63a5\u9053\uff1a\u201c\u97e9\u9738\uff0c\u4f60\u6b66\u529f\u4e0d\u5728\u9093\u98de\u4e4b\u4e0b\uff0c\u600e\u80af\u5c48\u5c31\u8fd9\u526f\u603b\u7ba1\u7684\u4f4d\u7f6e\uff1f\u201d \u3000\u3000\u8001\u72d0\u72f8\u679c\u7136\u591a\u7591\u3002 \u3000\u3000\u201c\u63d0\u62d4\u4e4b\u6069\u4e0d\u53ef\u5fd8\uff0c\u5728\u4e0b\u8bda\u5fc3\u8ffd\u968f\uff0c\u9093\u603b\u7ba1\u4efb\u804c\u4e00\u5929\uff0c\u97e9\u67d0\u51b3\u4e0d\u4ed6\u53bb\u3002\u201d \u3000\u3000\u201c\u4f60\u5982\u6b64\u5fe0\u5fc3\u803f\u803f\uff0c\u8001\u592b\u5012\u662f\u4e0d\u4fbf\u5f3a\u4f60\u4e86\u2026\u2026\u201d\u767d\u53d1\u8001\u4eba\u9053\uff1a\u201c\u8001\u592b\u672c\u6709\u610f\u8c03\u4f60\u4e3a\u6211\u957f\u968f\uff0c\u73b0\u5728\uff0c\u6b64\u60f3\u53ea\u597d\u4f5c\u7f62\u2026\u2026\u201d \u3000\u3000\u76ee\u5149\u8f6c\u6ce8\u5230\u9093\u98de\u8eab\u4e0a\uff0c\u63a5\u9053\uff1a\u201c\u4f60\u548c\u5185\u5bab\u7cfb\u7edf\u7684\u4eba\uff0c\u641e\u7684\u4e0d\u592a\u597d\u5427\uff1f\u201d \u3000\u3000\u201c\u662f\uff01\u2026\u2026\u201d\u9093\u98de\u56de\u5934\u6325\u624b\uff0c\u793a\u610f\u4e03\u5b9d\u548c\u5c1a\u4e24\u4e2a\u9556\u624b\u7b49\u5168\u9000\u5f00\u53bb\uff0c\u53ea\u8ba9\u6c5f\u67ab\u7559\u5728\u8eab\u4fa7\uff0c\u63a5\u9053\uff1a\u201c\u4ed6\u4eec\u81ea\u8ba4\u51fa\u8eab\u6b63\u5b97\uff0c\u5b8c\u5168\u4e0d\u628a\u54b1\u4eec\u6c5f\u6e56\u51fa\u8eab\u7684\u5802\u53e3\u4e2d\u4eba\u653e\u5728\u773c\u4e2d\uff0c\u6240\u4ee5\uff0c\u5f15\u8d77\u4e86\u51e0\u6b21\u51b2\u7a81\u3002\u201d \u3000\u3000\u767d\u53d1\u8001\u4eba\u4e00\u76b1\u7709\u5934\uff0c\u9053\uff1a\u201c\u8bf4\u4e0b\u53bb\u554a\uff01\u4f60\u7a76\u7adf\u4f5c\u4e86\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u201c\u6740\u6b7b\u4e86\u526f\u603b\u7ba1\u738b\u9f99\u2026\u2026\u201d\u9093\u98de\u9053\uff1a\u201c\u4ed6\u8981\u5c5e\u4e0b\u4ea4\u51fa\u603b\u7ba1\u804c\u4f4d\uff0c\u675f\u624b\u5c31\u7f1a\uff0c\u5c5e\u4e0b\u5fcd\u65e0\u53ef\u5fcd\uff0c\u51fa\u624b\u6740\u6b7b\u4e86\u4ed6\uff01\u201d \u3000\u3000\u201c\u4f60\u2026\u2026\u80fd\u4e00\u51fb\u6740\u6b7b\u738b\u9f99\u2026\u2026\u201d\u767d\u53d1\u8001\u4eba\u4e0d\u592a\u76f8\u4fe1\u7684\u63a5\u9053\uff1a\u201c\u6ca1\u6709\u522b\u4eba\u5e2e\u5fd9\u2026\u2026\u201d \u3000\u3000\u201c\u4ed6\u6b63\u5728\u548c\u97e9\u9738\u52a8\u624b\uff0c\u4e24\u4eba\u76f8\u6301\u4e0d\u4e0b\u2026\u2026\u201d \u3000\u3000\u201c\u55ef\uff01\u53c8\u662f\u97e9\u9738\u5e2e\u5fd9\u2026\u2026\u201d\u767d\u53d1\u8001\u4eba\u70b9\u70b9\u5934\uff0c\u63a5\u9053\uff1a\u201c\u97e9\u9738\uff0c\u4f60\u548c\u738b\u9f99\u4e4b\u6218\uff0c\u80dc\u8d1f\u5982\u4f55\uff1f\u201d \u3000\u3000\u201c\u97e9\u67d0\u4eba\u53ef\u80fd\u7565\u900a\u4e00\u7b79\uff0c\u5e78\u5f97\u603b\u7ba1\u51fa\u624b\uff0c\u4e00\u51fb\u6bd9\u654c\u3002\u201d \u3000\u3000\u9093\u98de\u63a5\u9053\uff1a\u201c\u738b\u80dc\u662f\u5802\u53e3\u4e2d\u4eba\uff0c\u4f46\u7adf\u4e5f\u4e0d\u628a\u6211\u653e\u5728\u773c\u4e2d\uff0c\u5982\u4e0d\u5927\u529b\u6574\u996c\uff0c\u8fd9\u6d1b\u9633\u5c45\u53ea\u6709\u4ea4\u7ed9\u4ed6\u4eec\u63a5\u7ba1\u4e86\u3002\u201d \u3000\u3000\u767d\u53d1\u8001\u4eba\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u4f60\u51c6\u5907\u5982\u4f55\u5e94\u4ed8\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u6562\u9690\u7792\u4e07\u8001\uff0c\u738b\u9f99\u7684\u5c38\u4f53\uff0c\u5df2\u7528\u5316\u9aa8\u7c89\u5316\u53bb\uff0c\u6b7b\u4e0d\u7559\u75d5\u3002\u201d \u3000\u3000\u201c\u5982\u662f\u5185\u5bab\u6d3e\u4eba\u6765\u67e5\uff0c\u4f60\u8981\u5982\u4f55\u5e94\u4ed8\u2026\u2026\u201d \u3000\u3000\u201c\u5c5e\u4e0b\u4e00\u53e3\u56de\u7edd\uff0c\u4e0d\u77e5\u4ed6\u4e0b\u843d\u4f55\u5904\uff01\u201d \u3000\u3000\u201c\u4f60\u81ea\u5df1\u5448\u62a5\u4e0a\u53e3\uff0c\u8001\u592b\u5e26\u56de\u53bb\u4f60\u7684\u5448\u62a5\u6587\u4e66\uff0c\u4e0d\u8fc7\uff0c\u65e5\u671f\u8981\u5199\u4e09\u5929\u4e4b\u524d\uff0c\u90a3\u65f6\uff0c\u8001\u592b\u8fd8\u672a\u52a8\u8eab\u6765\u6b64\uff0c\u6587\u4e2d\u53ea\u8bf4\u4e0d\u89c1\u738b\u9f99\u5f52\u6765\uff0c\u4e0d\u77e5\u4ed6\u884c\u8e2a\u4f55\u5904\uff0c\u4e0d\u8981\u628a\u4e8b\u60c5\u8bf4\u7684\u5f88\u4e25\u91cd\u2026\u2026\u201d\u767d\u53d1\u8001\u4eba\u9053\uff1a\u201c\u81f3\u4e8e\u738b\u80dc\u4e4b\u6b7b\uff0c\u8001\u592b\u4eb2\u81ea\u6240\u89c1\uff0c\u6b7b\u6709\u5e94\u8be5\uff0c\u4f60\u81ea\u5df1\u9009\u6d3e\u4e2a\u4eba\uff0c\u63a5\u638c\u8fce\u5bbe\u9601\u7684\u638c\u67dc\u804c\u4f4d\u3002\u201d \u3000\u3000\u201c\u591a\u8c22\u526f\u603b\u5802\u4e3b\u7684\u6210\u5168\uff0c\u6b8a\u6069\u6df1\u91cd\uff0c\u6ca1\u9f7f\u4e0d\u5fd8\u2026\u2026\u201d \u3000\u3000\u9093\u98de\u4e00\u63d6\u5230\u5730\uff0c\u65e0\u9650\u611f\u6fc0\u5730\u8bf4\u3002 \u3000\u3000\u201c\u5802\u53e3\u4e0a\u4eba\u548c\u6765\u81ea\u5185\u5bab\u7684\u4eba\uff0c\u5df2\u6709\u591a\u6b21\u51b2\u7a81\uff0c\u4f46\u5404\u5730\u4e92\u76f8\u8f83\u52b2\uff0c\u5927\u90fd\u662f\u6211\u4eec\u5403\u4e8f\uff0c\u6d1b\u9633\u5c45\u662f\u552f\u4e00\u7684\u5927\u83b7\u5168\u80dc\u7684\u5730\u65b9\u2026\u2026\u201d\u767d\u53d1\u8005\u8005\u8bf4\uff0c\u201c\u4e0d\u8fc7\uff0c\u6211\u60f3\u5185\u5bab\u4e2d\u4eba\uff0c\u5fc5\u6709\u53cd\u51fb\uff0c\u603b\u5802\u4e0d\u4fbf\u6d3e\u4eba\u652f\u63f4\uff0c\u8fd9\u8981\u4f60\u81ea\u5df1\u8bbe\u6cd5\u5e94\u4ed8\u4e86\u3002\u201d \u3000\u3000\u201c\u662f\uff01\u5c5e\u4e0b\u4f1a\u5168\u529b\u4ee5\u8d74\uff0c\u4e07\u8001\u80fd\u7559\u591a\u4e45\uff0c\u5c5e\u4e0b\u4e5f\u597d\u5b89\u6392\u4e00\u4e0b\uff0c\u7a0d\u5c3d\u5b5d\u656c\u4e4b\u5fc3\uff1f\u201d \u3000\u3000\u201c\u4e0d\u7528\u4e86\u2026\u2026\u201d\u767d\u53d1\u8001\u4eba\u8bf4\uff0c\u201c\u6211\u7acb\u523b\u5c31\u8d70\uff0c\u548c\u5802\u4e3b\u5546\u91cf\u4e00\u4e0b\uff0c\u770b\u770b\u80fd\u4e0d\u80fd\u7ed9\u4f60\u4e00\u4e9b\u5e2e\u52a9\uff1b\u4f60\u56de\u53bb\u5427\uff01\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u2026\u2026\u201d \u3000\u3000\u9093\u98de\u8fd8\u5f85\u8bf7\u6c42\uff0c\u767d\u53d1\u8001\u4eba\u5df2\u8fde\u8fde\u6325\u624b\uff0c\u9093\u98de\u4e0d\u6562\u518d\u8bf4\uff0c\u8f6c\u8eab\u884c\u53bb\u3002 \u3000\u3000\u6c5f\u67ab\u7d27\u968f\u8eab\u540e\uff0c\u4eba\u5df2\u884c\u51fa\u4e86\u8fce\u5bbe\u9601\u3002 \u3000\u3000\u90a3\u767d\u53d1\u8001\u4eba\u7a81\u7136\u8bf4\u9053\uff1a\u201c\u7b49\u4e00\u4e0b\u3002\u201d \u3000\u3000\u9093\u98de\u6025\u6b65\u5954\u56de\u3002 \u3000\u3000\u8fd9\u4e00\u6b21\uff0c\u6c5f\u67ab\u6ca1\u6709\u968f\u884c\u8ddf\u5165\uff0c\u7ad9\u5728\u95e8\u5916\uff0c\u4f46\u5374\u6697\u8fd0\u5185\u529f\uff0c\u51dd\u795e\u503e\u542c\u3002 \u3000\u3000\u4ed6\u5185\u529f\u7cbe\u6e5b\uff0c\u5df2\u5230\u4e86\u4e94\u4e08\u5185\u53ef\u95fb\u843d\u53f6\u7740\u5730\u4e4b\u58f0\uff0c\u4ed6\u4e5f\u660e\u767d\uff0c\u767d\u53d1\u8005\u4eba\u4e0d\u80af\u4e00\u53e3\u6c14\u8bf4\u5b8c\u5fc3\u4e2d\u7684\u8bdd\uff0c\u5728\u9093\u98de\u79bb\u5f00\u8fce\u5bbe\u9601\u540e\uff0c\u53c8\u53ec\u4ed6\u56de\u53bb\uff0c\u663e\u7136\u662f\u53e6\u6709\u673a\u5bc6\u8981\u8c08\u3002 \u3000\u3000\u679c\u7136\uff0c\u53ea\u542c\u767d\u53d1\u8001\u4eba\u7528\u6781\u4f4e\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u6211\u770b\u4f60\u5e26\u6765\u7684\u5927\u90e8\u5206\u4ece\u4eba\uff0c\u90fd\u975e\u5802\u53e3\u4e0a\u4eba\uff0c\u53ef\u90fd\u662f\u4f60\u81ea\u5df1\u627e\u5230\u7684\u4eba\u5417\uff1f\u201d \u3000\u3000\u201c\u662f\uff01\u6d1b\u9633\u5c45\u7684\u82b1\u5a62\u3001\u9556\u624b\uff0c\u4f3c\u90fd\u662f\u5185\u5bab\u5f1f\u5b50\uff0c\u5c5e\u4e0b\u53ea\u597d\u53e6\u8f9f\u8e4a\u5f84\uff0c\u81ea\u627e\u4eba\u624b\uff0c\u4ee5\u4e3a\u52a9\u529b\uff01\u201d \u3000\u3000\u201c\u97e9\u9738\u8fd9\u4e2a\u4eba\u9760\u5f97\u4f4f\u5417\uff1f\u201d \u3000\u3000\u201c\u4ed6\u5bf9\u6211\u975e\u5e38\u5fe0\u5b9e\u2026\u2026\u201d\u9093\u98de\u8bf4\uff0c\u201c\u8fd9\u4e00\u70b9\u5c5e\u4e0b\u4e5f\u66fe\u4ed4\u7ec6\u7684\u60f3\u8fc7\uff0c\u76ee\u4e0b\u6c5f\u6e56\u4e0a\u60c5\u52bf\uff0c\u5df2\u5927\u90e8\u5206\u5165\u6211\u4eec\u7684\u638c\u63e1\uff0c\u7eb5\u6709\u4e00\u4e2a\u4eba\u7684\u53cd\u6297\u5b58\u5728\uff0c\u4f46\u4e09\u4e94\u4eba\u7684\u7ec4\u5408\uff0c\u4e5f\u65e0\u6cd5\u6210\u4e8b\uff0c\u4f55\u51b5\uff0c\u5230\u76ee\u524d\u4e3a\u6b62\uff0c\u4ed6\u4eec\u8fd8\u627e\u4e0d\u5230\u8be5\u5411\u4f55\u4eba\u53cd\u51fb\uff0c\u771f\u6b63\u91cd\u8981\u4e8b\uff0c\u5012\u662f\u76ee\u524d\u8fd9\u5185\u6597\u7684\u51f6\u9669\u6fc0\u70c8\uff0c\u5185\u5bab\u4e00\u7cfb\u7684\u4eba\uff0c\u597d\u50cf\u5df2\u4e0d\u613f\u7b49\u5f85\uff0c\u51c6\u5907\u62ff\u6211\u4eec\u5f00\u5200\u4e86\uff01\u201d \u3000\u3000\u201c\u8fd9\u662f\u8fdf\u65e9\u7684\u4e8b\u4e86\uff0c\u603b\u5802\u4e3b\u4e5f\u770b\u5230\u4e86\u8fd9\u4e00\u6b65\uff0c\u6240\u4ee5\uff0c\u4e5f\u4e0d\u613f\u518d\u4f5c\u9000\u8ba9\u3001\u5fcd\u8010\uff0c\u8fd9\u5c31\u662f\u4e0d\u64a4\u6362\u4f60\u8fd9\u4e2a\u603b\u7ba1\u7684\u539f\u56e0\u2026\u2026\u201d\u767d\u53d1\u8001\u4eba\u8bf4\uff0c\u201c\u8001\u592b\u5c3d\u91cf\u53bb\u8bf4\u670d\u603b\u5802\u4e3b\uff0c\u7ed9\u4f60\u4e00\u4e9b\u5b9e\u529b\u4e0a\u7684\u652f\u63f4\uff0c\u4e0d\u8fc7\uff0c\u4f60\u4e5f\u4e0d\u8981\u5bc4\u671b\u592a\u5927\u2026\u2026\u201d \u3000\u3000\u201c\u9093\u98de\u611f\u6fc0\u4e0d\u5c3d\uff0c\u611f\u6fc0\u4e0d\u5c3d\u2026\u2026\u3002\u201d \u3000\u3000\u767d\u53d1\u8001\u4eba\u53f9\u9053\uff1a\u201c\u8001\u592b\u518d\u544a\u8bc9\u4f60\u4e00\u4ef6\u673a\u5bc6\uff0c\u6240\u8c13\u5185\u5bab\u4e2d\u4eba\uff0c\u4e5f\u6709\u4e24\u80a1\u5b9e\u529b\u5b58\u5728\uff0c\u5176\u4e2d\u4e00\u80a1\uff0c\u548c\u603b\u5802\u6697\u6597\u5f88\u70c8\uff0c\u53e6\u4e00\u80a1\u52bf\u529b\u5374\u6309\u5175\u672a\u52a8\u3002\u201d \u3000\u3000\u201c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\u5462\uff1f\u2026\u2026\u201d\u9093\u98de\u9053\uff1a\u201c\u8fd8\u8bf7\u4e07\u8001\u6307\u793a\u673a\u5b9c\u3002\u201d \u3000\u3000\u201c\u8001\u5b9e\u8bf4\uff0c\u6211\u4e5f\u4e0d\u662f\u592a\u6e05\u695a\u2026\u2026\u201d\u767d\u53d1\u8001\u4eba\u9053\uff1a\u201c\u5c31\u7b97\u603b\u5802\u4e3b\u4e5f\u4e0d\u89c1\u5f97\u771f\u80fd\u4e86\u89e3\uff0c\u4f60\u8fd9\u91cc\u662f\u76ee\u524d\u6700\u91cd\u8981\u7684\u636e\u70b9\uff0c\u65e5\u8fdb\u6597\u91d1\uff0c\u4f46\u4e5f\u662f\u6700\u590d\u6742\u7684\u4e00\u4e2a\u5730\u65b9\uff0c\u4f46\u4f60\u5982\u80fd\u4ed4\u7ec6\u5730\u89c2\u5bdf\uff0c\u5b9a\u6709\u53d1\u73b0\uff0c\u4f60\u53bb\u5427\uff01\u201d \u3000\u3000\u4ed6\u4eec\u5e76\u6ca1\u6709\u65bd\u7528\u4f20\u97f3\u4e4b\u672f\u4ea4\u8c08\u3002 \u3000\u3000\u56e0\u4e3a\uff0c\u4ed6\u4eec\u5224\u65ad\u6c5f\u67ab\u4e0d\u53ef\u80fd\u542c\u5230\u5341\u4e08\u5916\u7684\u4f4e\u58f0\u8c08\u8bdd\uff0c\u9664\u975e\u6c5f\u67ab\u5df2\u7ec3\u6210\u5929\u89c6\u5730\u542c\u7684\u4e0a\u4e58\u6b66\u529f\uff0c\u624d\u6709\u53ef\u80fd\u542c\u5230\u4ed6\u4eec\u7684\u8c08\u8bdd\u3002 \u3000\u3000\u4f46\u6c5f\u67ab\u5374\u542c\u5f97\u5b57\u5b57\u5165\u8033\u3002 \u3000\u3000\u9093\u98de\u5fc3\u4e2d\u4e00\u6b22\u6109\uff0c\u5bf9\u6c5f\u67ab\u548c\u4e03\u5b9d\u548c\u5c1a\uff0c\u4e5f\u5341\u5206\u5ba2\u6c14\u8d77\u6765\uff0c\u4eb2\u81ea\u5949\u4e0a\u4e86\u4e24\u676f\u9999\u8317\uff0c\u7b11\u9053\uff1a \u3000\u3000\u201c\u4e24\u4f4d\u5750\u554a\uff01\u201d \u3000\u3000\u8fd9\u662f\u9093\u98de\u4f4f\u5904\u7684\u96c5\u81f4\u5c0f\u5385\uff0c\u6b64\u523b\u5385\u4e2d\u53ea\u6709\u4e09\u4e2a\u4eba\uff0c\u6240\u4ee5\u9093\u98de\u8981\u4eb2\u81ea\u52a8\u624b\u5012\u8336\u3002 \u3000\u3000\u201c\u8fd9\u91cc\u6709\u56db\u4e2a\u4eba\u7167\u987e\u6211\u7684\u751f\u6d3b\uff0c\u4e5f\u8d1f\u8d23\u4fdd\u62a4\u8fd9\u91cc\u7684\u5b89\u5168\uff0c\u73b0\u5728\uff0c\u4ed6\u4eec\u90fd\u5206\u5b88\u8fd9\u5ea7\u5b85\u9662\u56db\u5468\uff0c\u53ea\u8981\u6709\u4eba\u63a5\u8fd1\u8fd9\u5ea7\u5b85\u9662\u5341\u4e08\u4e4b\u5185\uff0c\u4ed6\u4eec\u5c31\u4f1a\u4f20\u5165\u8b66\u8baf\uff0c\u6240\u4ee5\uff0c\u54b1\u4eec\u53ef\u4ee5\u653e\u5fc3\u7684\u4ea4\u8c08\u3002\u4e24\u4f4d\u6709\u4ec0\u4e48\u9ad8\u89c1\uff0c\u5c3d\u7ba1\u8bf7\u8bf4\uff1f\u201d \u3000\u3000\u9093\u98de\u8bf4\u5b8c\uff0c\u78a7\u7eff\u7684\u76ee\u5149\uff0c\u7f13\u7f13\u4ece\u4e24\u4eba\u8138\u4e0a\u626b\u8fc7\uff0c\u8138\u4e0a\u662f\u4e00\u7247\u6b23\u7136\u81ea\u5f97\u7684\u795e\u60c5\u3002 \u3000\u3000\u201c\u6211\u7559\u5fc3\u89c2\u5bdf\u4e86\u8fd9\u91cc\u7684\u9556\u624b\u3001\u4f8d\u5e94\uff0c\u4ed6\u4eec\u7684\u5e74\u7eaa\u6728\u5927\uff0c\u4f46\u6b66\u529f\u90fd\u6709\u7740\u76f8\u5f53\u7684\u57fa\u7840\uff0c\u4e00\u5bf9\u4e00\uff0c\u6211\u76f8\u4fe1\u8db3\u53ef\u5e94\u4ed8\u3002\u4f46\u5982\u4ed6\u4e00\u6d8c\u800c\u4e0a\uff0c\u6211\u4eec\u56db\u4e94\u4e2a\u4eba\u7684\u529b\u91cf\uff0c\u5c31\u5f88\u96be\u5bf9\u4ed8\u4e86\u3002\u201d\u4e03\u5b9d\u548c\u5c1a\u63d0\u51fa\u4e86\u95ee\u9898\u3002 \u3000\u3000\u201c\u97e9\u9738\uff0c\u4f60\u8bf4\u79e6\u516b\u516c\u624b\u4e0b\u6709\u4e00\u6279\u53ef\u7528\u7684\u4eba\u2026\u2026\u201d\u9093\u98de\u8bf4\uff0c\u201c\u4e0d\u77e5\u4ed6\u73b0\u5728\u75c5\u60c5\u5982\u4f55\u4e86\uff1f\u201d \u3000\u3000\u201c\u79e6\u516b\u516c\u6709\u591a\u5c11\u5b9e\u529b\uff0c\u5c5e\u4e0b\u4e0d\u592a\u6e05\u695a\uff0c\u81f3\u4e8e\u4ed6\u7684\u75c5\u60c5\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u63a5\u9053\uff1a\u201c\u5927\u6982\u8fd8\u662f\u4e2a\u4e0d\u6b7b\u4e0d\u6d3b\u7684\u5c40\u9762\u5427\uff01\u201d \u3000\u3000\u9093\u98de\u7b11\u4e00\u7b11\u9053\uff1a\u201c\u4f60\u53bb\u533b\u597d\u4ed6\u7684\u4f24\u7a74\uff0c\u8981\u4ed6\u53ec\u96c6\u4eba\u624b\uff0c\u5c31\u5728\u957f\u5b89\u7ec4\u5408\u6210\u4e00\u80a1\u529b\u91cf\u2026\u2026\u3002\u201d \u3000\u3000\u6c5f\u67ab\u5fc3\u4e2d\u5fd6\u9053\uff1a\u201c\u5929\u7a7a\u4efb\u9e1f\u98de\uff0c\u7531\u4ed6\u4eec\u5728\u5916\u9762\u81ea\u7531\u53d1\u5c55\uff0c\u5f53\u53ef\u805a\u96c6\u8d77\u66f4\u4e3a\u5f3a\u5927\u7684\u529b\u91cf\u3002\u201d \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u542c\u5f97\u5fc3\u5934\u4e00\u52a8\uff0c\u63a5\u9053\uff1a\u201c\u4e0d\u628a\u79e6\u516b\u516c\u53ec\u5165\u6d1b\u9633\u5c45\u4e2d\uff0c\u5982\u4f55\u80fd\u638c\u63e1\u4ed6\u4eec\u7684\u529b\u91cf\uff1f\u201d \u3000\u3000\u201c\u73b0\u5728\u60c5\u52bf\u6709\u4e86\u53d8\u5316\u2026\u2026\u201d\u9093\u98de\u8bf4\uff0c\u201c\u603b\u5802\u5bf9\u6211\u9093\u67d0\u4eba\u5341\u5206\u652f\u6301\uff0c\u4e0d\u4f46\u53ef\u4ee5\u4fdd\u4f4f\u8fd9\u4e2a\u6d1b\u9633\u5c45\u7684\u603b\u7ba1\u4f4d\u7f6e\uff0c\u8fd8\u8981\u6d3e\u4eba\u6697\u4f5c\u652f\u63f4\uff0c\u6211\u76f8\u4fe1\u90a3\u6697\u4e2d\u652f\u63f4\u7684\u529b\u91cf\uff0c\u5fc5\u4f1a\u5f88\u5feb\u5230\u6765\uff0c\u81f3\u4e8e\u79e6\u516b\u516c\u8fd9\u80a1\u529b\u91cf\uff0c\u53ea\u662f\u4e00\u79cd\u51c6\u5907\u3002\u201d \u3000\u3000\u201c\u6211\u76f8\u4fe1\u79e6\u516b\u516c\u4e5f\u627e\u4e0d\u5230\u4ec0\u4e48\u6770\u51fa\u7684\u9ad8\u624b\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u65e2\u7136\u7528\u4e0d\u7740\u4ed6\u4eec\uff0c\u90a3\u5c31\u7528\u4e0d\u7740\u548c\u79e6\u516b\u516c\u63a5\u89e6\u5546\u8c08\u4e86\u3002\u201d \u3000\u3000\u201c\u97e9\u9738\u2026\u2026\u201d\u9093\u98de\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u79e6\u516b\u516c\u4e5f\u8bb8\u4e0d\u8db3\u4e3a\u5bb3\uff0c\u4f46\u4ed6\u5982\u771f\u7684\u805a\u96c6\u4e86\u4e00\u80a1\u529b\u91cf\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u628a\u4ed6\u4eec\u7f6e\u5165\u638c\u63e1\u4e4b\u4e2d\uff1f\u201d \u3000\u3000\u201c\u5bf9\uff01\u638c\u63e1\u4f4f\u4ed6\u4eec\u90a3\u80a1\u529b\u91cf\uff0c\u53ef\u4e3a\u6211\u7528\u2026\u2026\u201d\u6c5f\u67ab\u4f4e\u58f0\u9053\uff1a\u201c\u5fc5\u8981\u65f6\uff0c\u4e5f\u53ef\u4ee5\u628a\u4ed6\u4eec\u5f15\u5165\u57cb\u4f0f\uff0c\u4e00\u4e3e\u6b7c\u706d\u3002\u201d \u3000\u3000\u9093\u98de\u592b\u4f5c\u6b63\u9762\u7b54\u590d\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\uff0c\u5c31\u7531\u4f60\u53bb\u529e\u5427\uff01 \u3000\u3000\u6211\u6682\u65f6\u4e0d\u548c\u4ed6\u4eec\u89c1\u9762\u4e86\u3002\u201d \u3000\u3000\u97e9\u9738\u9053\uff1a\u201c\u662f\uff01\u5c5e\u4e0b\u8fd9\u5c31\u53bb\u89c1\u89c1\u79e6\u516b\u516c\u2026\u2026\u3002\u201d \u3000\u3000\u79bb\u5f00\u4e86\u9093\u98de\u7684\u5c45\u5904\uff0c\u4e03\u5b9d\u548c\u5c1a\u628a\u6c5f\u67ab\u5f15\u5165\u4e0b\u69bb\u4e4b\u5904\uff0c\u80e1\u840d\u3001\u6bb5\u4e5d\uff0c\u65e9\u5df2\u623f\u4e2d\u7b49\u5019\u3002 \u3000\u3000\u80e1\u5143\u4e00\u76f4\u81ea\u52a8\u62c5\u8d1f\u8d77\u52b3\u52a1\u7684\u5de5\u4f5c\uff0c\u5728\u5ba4\u5916\u8b66\u6212\u3002 \u3000\u3000\u201c\u6c5f\u8001\u5f1f\uff0c\u8ba4\u8bc6\u90a3\u4f4d\u4e07\u526f\u603b\u5802\u4e3b\u5417\uff1f\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u4f4e\u58f0\u95ee\u9053\u3002 \u3000\u3000\u201c\u4e0d\u8ba4\u8bc6\uff0c\u4e0d\u8fc7\uff0c\u60f3\u6765\uff0c\u4e00\u5b9a\u662f\u4e00\u4f4d\u5f88\u96be\u7f20\u7684\u4eba\u7269\u2026\u2026\u201d\u6c5f\u67ab\u8bf4\uff1a\u201c\u4e09\u4f4d\u5bf9\u4ed6\u5f88\u4e86\u89e3\u5427\uff01\u201d \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4e00\u638c\u62db\u9b42\u4e07\u65b9\u8fd9\u4e2a\u4eba\uff0c\u8001\u5f1f\u53ef\u66fe\u542c\u5e08\u957f\u63d0\u8fc7\uff1f\u201d \u3000\u3000\u770b\u4e03\u5b9d\u548c\u5c1a\u7684\u51dd\u91cd\u795e\u8272\uff0c\u6c5f\u67ab\u5fc3\u77e5\u90a3\u67af\u7626\u6709\u5982\u75c5\u592b\u4e00\u822c\u7684\u4eba\uff0c\u5b9a\u662f\u6c5f\u6e56\u4e0a\u4e00\u4f4d\u6781\u6709\u4efd\u91cf\u7684\u4eba\u7269\u3002 \u3000\u3000\u4f46\u641c\u7d22\u67af\u6768\uff0c\u5374\u8bb0\u4e0d\u5f97\u90a3\u4f4d\u5e08\u957f\u63d0\u8fc7\uff0c\u53ea\u597d\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u6ca1\u6709\u2026\u2026\u201d \u3000\u3000\u201c\u4e5f\u96be\u602a\uff0c\u8fd9\u4e2a\u4e07\u65b9\u597d\u50cf\u6709\u56db\u5341\u5e74\u6ca1\u5728\u6c5f\u6e56\u4e0a\u51fa\u73b0\u8fc7\u4e86\u2026\u2026\u201d \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u9053\uff1a\u201c\u5f53\u5e74\u4e5d\u534e\u5c71\u7684\u82f1\u96c4\u5927\u4f1a\uff0c\u4e5f\u6ca1\u6709\u89c1\u4ed6\u51fa\u5e2d\uff0c\u4f20\u8bf4\u4e2d\u4ed6\u662f\u88ab\u4e3b\u6301\u4e5d\u534e\u76df\u7ea6\u7684\u4e09\u5927\u9ad8\u4eba\u4e4b\u4e00\uff0c\u5929\u72d0\u592b\u4eba\u91cd\u5251\u800c\u6b7b\uff0c\u60f3\u4e0d\u5230\uff0c\u4ed6\u7adf\u7136\u8fd8\u6d3b\u5728\u4e16\u4e0a\u3002\u201d \u3000\u3000\u201c\u4ed6\u4f24\u5728\u5929\u72d0\u592b\u4eba\u4e4b\u624b\u2026\u2026\u201d \u3000\u3000\u201c\u662f\u554a\uff01\u5929\u72d0\u592b\u4eba\uff0c\u6c5f\u5c11\u5144\uff0c\u5927\u6982\u542c\u8bf4\u8fc7\u5427\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u542c\u8fc7\u5979\u8001\u4eba\u5bb6\u7684\u5927\u540d\uff0c\u4e0d\u8fc7\uff0c\u8be6\u7ec6\u60c5\u5f62\u5c31\u4e0d\u592a\u6e05\u695a\u4e86\uff1f\u201d \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u548c\u5c1a\u4e5f\u60f3\u4e0d\u901a\uff0c\u6c5f\u6e56\u4e2d\u4eba\uff0c\u600e\u4f1a\u9001\u5979\u4e2a\u5929\u72d0\u7684\u5916\u53f7\uff0c\u5927\u6982\u4e3a\u5979\u6b66\u529f\u5947\u5e7b\u53d8\u5316\u5982\u5929\u9645\u4e4b\u72d0\u5427\uff1f\uff1f\u201d \u3000\u3000\u80e1\u840d\u63a5\u9053\uff1a\u201c\u5979\u7684\u5996\u5a9a\u4e5f\u662f\u88ab\u79f0\u4f5c\u5929\u72d0\u7684\u539f\u56e0\u4e4b\u4e00\uff0c\u636e\u8bf4\u5929\u72d0\u592b\u4eba\u6709\u4e00\u7b11\u503e\u57ce\u7684\u59ff\u8272\uff0c\u4f20\u8bf4\u5979\u95ef\u8361\u6c5f\u6e56\u65f6\uff0c\u7528\u4e0d\u7740\u548c\u4eba\u52a8\u624b\uff0c\u53ea\u8981\u51b2\u7740\u5bf9\u65b9\u7b11\u4e00\u7b11\uff0c\u5bf9\u65b9\u5c31\u5f03\u68b0\u5c31\u7f1a\u4e86\uff01\u201d \u3000\u3000\u201c\u5929\u72d0\u4e4b\u7b11\uff0c\u5982\u82b1\u76db\u653e\uff0c\u786e\u6709\u8fd9\u79cd\u9b45\u529b\uff0c\u6240\u4ee5\uff0c\u4e2d\u5e74\u4e4b\u540e\uff0c\u5979\u5c31\u5f88\u5c11\u7b11\u4e86\uff0c\u5979\u7684\u6b66\u529f\u81ea\u6210\u4e00\u5bb6\uff0c\u6781\u5c3d\u53d8\u5316\u4e4b\u5999\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u8bf4\uff0c\u201c\u88ab\u79f0\u8c13\u6b66\u6797\u4e09\u4ed9\u4e4b\u4e00\uff0c\u5b9e\u975e\u5e78\u81f3\u3002\u201d \u3000\u3000\u6c5f\u67ab\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u90a3\u662f\u8bf4\u5929\u72d0\u7684\u79f0\u547c\uff0c\u53ea\u662f\u9882\u8d5e\u5979\u7684\u7f8e\u4e3d\u4e86\uff1f\u201d \u3000\u3000\u201c\u4e0d\u5168\u662f\u5982\u6b64\u201d\u6bb5\u4e5d\u7a81\u7136\u63d2\u53e3\u8bf4\u9053\uff1a\u201c\u5c31\u5728\u4e0b\u6240\u77e5\uff0c\u6b66\u6797\u4e09\u4ed9\u4e2d\u4ee5\u5929\u72d0\u592b\u4eba\u7684\u6b66\u529f\u6700\u662f\u602a\u5f02\uff0c\u4e5f\u6700\u90aa\u6c14\uff0c\u5979\u4e00\u751f\u672a\u5a5a\uff0c\u81ea\u53f7\u592b\u4eba\uff0c\u672c\u6765\uff0c\u6b66\u6797\u4e2d\u4eba\uff0c\u79f0\u5979\u4e3a\u5929\u72d0\u4ed9\u5b50\uff0c\u5979\u5374\u6539\u53f7\u592b\u4eba\uff0c\u4f20\u8bf4\u4e2d\u6709\u51e0\u4f4d\u5f53\u4e16\u9ad8\u624b\uff0c\u90fd\u66fe\u4f5c\u8fc7\u5979\u88d9\u4e0b\u4e4b\u81e3\u3002\u201d \u3000\u3000\u201c\u5979\u53f7\u79f0\u5929\u72d0\uff0c\u4e0d\u8bba\u6b66\u529f\uff0c\u4e3a\u4eba\uff0c\u90fd\u5e26\u70b9\u72d0\u6027\uff0c\u5979\u4e5f\u4ece\u4e0d\u63a9\u9970\u81ea\u5df1\uff0c\u636e\u8bf4\u5f53\u9762\u79f0\u5979\u5929\u72d0\u592b\u4eba\uff0c\u5979\u4e5f\u4e00\u7b11\u7f6e\u4e4b\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u7455\u4e0d\u63a9\u745c\uff0c\u5979\u5728\u6b66\u6797\u4e2d\u7684\u4f5c\u4e3a\uff0c\u5374\u662f\u9876\u5929\u7acb\u5730\uff0c\u5927\u662f\u5927\u975e\u4e4b\u5904\uff0c\u628a\u63e1\u5f97\u975e\u5e38\u4e25\u7d27\uff0c\u4f20\u8bf4\u4e2d\u5979\u6709\u4e8c\u4f24\u5fc3\u7231\u7684\u7537\u53cb\uff0c\u56e0\u52ab\u8272\u4f24\u547d\uff0c\u88ab\u5979\u4eb2\u624b\u5904\u6b7b\uff0c\u7136\u540e\uff0c\u53c8\u4eb2\u624b\u6b93\u846c\uff0c\u5728\u575f\u524d\u54ed\u4e86\u4e09\u65e5\u4e09\u591c\uff0c\u6cea\u5c3d\u8840\u6d41\uff0c\u663e\u89c1\u60c5\u7231\u662f\u4f55\u7b49\u7684\u6df1\u539a\uff1f\u4f46\u5979\u5374\u80fd\u4eb2\u624b\u6740\u4e4b\uff0c\u5979\u7684\u72ec\u7279\u5f02\u884c\uff0c\u5b9e\u662f\u6027\u60c5\u4e2d\u4eba\uff0c\u4ee5\u548c\u5c1a\u7684\u770b\u6cd5\uff0c\u5979\u662f\u4e2a\u975e\u5e38\u53ef\u656c\u7684\u4eba\u7269\u3002\u201d \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u7387\u6027\u4ee5\u884c\uff0c\u8868\u73b0\u51fa\u4e86\u4eba\u6027\u4e00\u4e2a\u771f\u5b57\u2026\u2026\u201d\u8bed\u58f0\u4e00\u987f\uff0c\u8f6c\u8fc7\u8bdd\u9898\u53c8\u9053\uff1a\u201c\u4e07\u65b9\u53f7\u79f0\u4e00\u638c\u62db\u9b42\uff0c\u5fc5\u6709\u7279\u6b8a\u7684\u6b66\u529f\u4e86\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\uff01\u548c\u5c1a\u542c\u5230\u7684\u4f20\u8bf4\u662f\uff0c\u4ed6\u4e3e\u624b\u4e4b\u95f4\uff0c\u80fd\u53d6\u4eba\u6027\u547d\uff0c\u56e0\u4e3a\u4ed6\u7ec3\u6210\u4e86\u4e00\u79cd\u5f88\u9738\u9053\u7684\u6b66\u529f\u2018\u6467\u5fc3\u638c\u2019\uff0c\u56db\u5341\u5e74\u6765\u53ea\u6015\u4ed6\u8fd9\u79cd\u9738\u9053\u7684\u6b66\u529f\uff0c\u66f4\u5177\u706b\u5019\u4e86\u3002 \u3000\u3000\u548c\u5c1a\u6025\u7740\u8981\u628a\u8fd9\u4ef6\u4e8b\u544a\u8bc9\u4f60\uff0c\u662f\u8981\u4f60\u65e9\u6709\u9632\u5907\uff0c\u514d\u5f97\u9aa4\u4e0d\u53ca\u9632\uff0c\u4f24\u5728\u4ed6\u7684\u624b\u4e0b\u3002\u201d \u3000\u3000\u6c5f\u67ab\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u591a\u8c22\u5927\u5e08\uff0c\u4e0d\u8fc7\uff0c\u8bf8\u4f4d\u4e5f\u8981\u5f53\u5fc3\uff0c\u5c3d\u91cf\u907f\u5f00\u53ef\u80fd\u53d7\u5230\u7684\u4f24\u5bb3\uff0c\u89c1\u9762\u4e4b\u65f6\uff0c\u5c3d\u91cf\u79bb\u4ed6\u8fdc\u4e00\u4e9b\u2026\u2026\u201d \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u63a5\u9053\uff1a\u201c\u5728\u8fce\u5bbe\u9601\u4e2d\uff0c\u4f60\u591a\u6b21\u9876\u649e\u90a3\u8001\u9b54\u5934\uff0c\u548c\u5c1a\u4e00\u76f4\u62c5\u5fc3\u89e6\u52a8\u4ed6\u7684\u6740\u673a\uff01\u201d \u3000\u3000\u201c\u73b0\u5728\uff0c\u6211\u77e5\u9053\u4e86\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u5982\u679c\u60c5\u52bf\u903c\u4eba\uff0c\u90a3\u5c31\u53ea\u597d\u5148\u6740\u6389\u4ed6\u4e86\u3002\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u8c6a\u6c14\u51b2\u5929\uff0c\u5374\u542c\u5f97\u80e1\u840d\u3001\u6bb5\u4e5d\u7b49\u6014\u5728\u5f53\u573a\u3002 \u3000\u3000\u80e1\u840d\u6697\u6697\u4ed8\u9053\uff1a\u201c\u8fd9\u5e74\u8f7b\u4eba\u7684\u53e3\u6c14\uff0c\u5b9e\u5728\u662f\u592a\u5927\u4e86\uff0c\u4e07\u65b9\u662f\u4f55\u7b49\u4eba\u7269\uff1f\u5c82\u662f\u8f7b\u6613\u80fd\u6740\u5f97\u4e86\u7684\u3002\u201d \u3000\u3000\u6bb5\u4e5d\u4e5f\u5728\u6697\u4f5c\u5fd6\u601d\uff1a\u201c\u6c5f\u8001\u5f1f\u7684\u6b66\u529f\u3001\u624d\u6167\uff0c\u786e\u90fd\u9ad8\u8fc7\u6211\u4eec\u5f88\u591a\uff0c\u4f46\u5982\u60f3\u4e00\u4e3e\u6740\u6389\u4e07\u65b9\uff0c\u90a3\u5c31\u5927\u5938\u5f20\u4e86\uff0c\u5e74\u8f7b\u4eba\u554a\uff01\u603b\u662f\u6709\u4e00\u70b9\u534e\u800c\u4e0d\u5b9e\u7684\u6bdb\u75c5\u3002\u201d \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u4e5f\u4e0d\u76f8\u4fe1\u6c5f\u67ab\u80fd\u640f\u6740\u4e07\u65b9\uff0c\u4e0b\u8fc7\uff0c\u4ed6\u5374\u4e0d\u50cf\u6bb5\u4e5d\u3001\u80e1\u840d\u90a3\u6837\u795e\u8272\u660e\u663e\u7684\u6123\u5728\u90a3\u91cc\uff0c\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6c5f\u8001\u5f1f\uff0c\u548c\u5c1a\u8981\u7d27\u7684\u8bdd\uff0c\u90fd\u8bf4\u5b8c\u4e86\uff0c\u4f60\u53ef\u8981\u53bb\u770b\u770b\u8001\u53eb\u5316\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u4e5f\u77a7\u51fa\u4e86\u4e09\u4eba\u4e0d\u4fe1\u7684\u795e\u8272\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e09\u4f4d\u90fd\u89c1\u8fc7\u4e86\u6c5f\u67d0\u4eba\u7684\u771f\u6b63\u9762\u76ee\uff0c\u5982\u82e5\u5728\u6d1b\u9633\u5c45\u4e2d\u89c1\u5230\u4e86\uff0c\u5343\u4e07\u8981\u88c5\u7740\u4e0d\u8bc6\uff0c\u4e0d\u53ef\u9732\u51fa\u7834\u7efd\u2026\u2026\u201d\u8bf4\u5b8c\u8bdd\uff0c\u5927\u6b65\u79bb\u53bb\u3002 \u3000\u3000\u76ee\u7779\u6c5f\u67ab\u7684\u80cc\u5f71\u6d88\u5931\uff0c\u80e1\u840d\u5401\u4e86\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8fd9\u2026\u2026\u8fd9\u662f\u4ece\u4f55\u8bf4\u8d77\u554a\uff1f\u4ed6\u8981\u4ee5\u771f\u9762\u76ee\u6765\u6d1b\u9633\u5c45\u4e2d\u529e\u4e8b\uff0c\u53c8\u4f55\u5982\u4ee5\u97e9\u526f\u603b\u7ba1\u7684\u8eab\u4efd\u6d3b\u52a8\u65b9\u4fbf\uff1f\u201d \u3000\u3000\u201c\u8bf4\u7684\u4e5f\u662f\u554a\u2026\u2026\u201d\u6bb5\u4e5d\u9053\uff1a\u201c\u5929\u5929\u6539\u626e\u4ed6\uff0c\u53ea\u662f\u65b9\u4fbf\u4ed6\u6084\u7136\u5916\u51fa\uff0c\u4ed6\u5982\u6709\u65f6\u95f4\u5728\u6d1b\u9633\u5c45\u4e2d\uff0c\u53c8\u4f55\u7528\u5929\u5929\u5192\u9669\uff1f\u201d \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e0d\u7528\u591a\u8d39\u601d\u91cf\u4e86\uff0c\u6c5f\u8001\u5f1f\u7684\u795e\u51fa\u9b3c\u6ca1\uff0c\u5fc5\u6709\u5176\u7cbe\u5999\u7684\u8bbe\u8ba1\uff0c\u4ed6\u4e0d\u80af\u4e8b\u5148\u8bf4\u660e\uff0c\u5fc5\u6709\u539f\u56e0\uff01\u201d \u3000\u3000\u201c\u6c5f\u8001\u5f1f\u7684\u667a\u6167\u3001\u6b66\u529f\uff0c\u65e0\u7591\u95ee\u9ad8\u51fa\u6211\u4eec\u5f88\u591a\u2026\u2026\u201d\u80e1\u840d\u8bf4\uff0c\u201c\u4f46\u4ed6\u80fd\u640f\u6740\u4e00\u638c\u62db\u9b42\u4e07\u65b9\uff0c\u5c31\u6709\u4e9b\u4e0d\u53ef\u601d\u8bae\u4e86\uff0c\u5927\u5e08\u7684\u770b\u6cd5\u5462\uff1f\u201d \u3000\u3000\u201c\u548c\u5c1a\u4e5f\u4e0d\u80fd\u4f5c\u4e2a\u8bba\u65ad\uff0c\u4e0d\u8fc7\uff0c\u4ee5\u6c5f\u8001\u5f1f\u7684\u6027\u683c\u800c\u8a00\uff0c\u5e94\u8be5\u4e0d\u662f\u8f7b\u8a00\u5938\u5927\u7684\u4eba\uff0c\u8fd9\u4ef6\u4e8b\uff0c\u54b1\u4eec\u542c\u5165\u8033\u4e2d\uff0c\u8bb0\u5728\u5fc3\u91cc\u5c31\u662f\u3002\u201d \u3000\u3000\u4ed6\u4eec\u90fd\u662f\u7ecf\u9a8c\u4e30\u5bcc\uff0c\u9605\u5386\u5e7f\u591a\u7684\u4eba\uff0c\u90fd\u4e0d\u4f1a\u968f\u98ce\u98d8\u6447\uff0c\u4eba\u4e91\u4ea6\u4e91\u3002 \u3000\u3000\u6c5f\u67ab\u4f1a\u89c1\u4e86\u90ed\u5929\u540c\uff0c\u8fd9\u51e0\u65e5\u6765\uff0c\u90ed\u5929\u540c\u5168\u5fc3\u5728\u63e3\u6478\u97e9\u9738\u8fd9\u4e2a\u4eba\u7684\u6027\u683c\uff0c\u679c\u7136\u662f\u5927\u6709\u8fdb\u5883\uff0c\u4e0d\u8bba\u662f\u4e3e\u6b62\u884c\u52a8\u3001\u53e3\u6c14\u3001\u6027\u683c\uff0c\u90fd\u5df2\u6709\u4e5d\u6210\u795e\u4f3c\u3002 \u3000\u3000\u5f53\u7136\uff0c\u6c5f\u67ab\u4e5f\u4ed4\u7ec6\u7684\u8bf4\u660e\u4e86\u97e9\u9738\u548c\u6d1b\u9633\u5c45\u4e2d\u5404\u65b9\u4eba\u7269\u7684\u5173\u7cfb\uff0c\u8bf4\u590d\u6742\uff0c\u662f\u5f88\u96be\u5e94\u5bf9\u7684\u6070\u5230\u597d\u5904\u3002\u6d1b\u9633\u5c45\u4e2d\u4eba\u624b\u867d\u591a\uff0c\u4f46\u6c5f\u67ab\u771f\u6b63\u8981\u5bf9\u4ed8\u7684\u53ea\u6709\u603b\u7ba1\u9093\u98de\u548c\u56db\u5b63\u82b1\u5a62\u3002 \u3000\u3000\u90ed\u5929\u540c\u63d0\u51fa\u4e86\u548c\u56db\u5b63\u82b1\u5a62\u5e94\u5bf9\u7684\u6001\u5ea6\u8bed\u6c14\uff0c\u6c5f\u67ab\u4e5f\u8be6\u4f5c\u89e3\u8bf4\uff0c\u7136\u540e\uff0c\u6c5f\u67ab\u5c31\u6084\u7136\u7684\u79bb\u5f00\u4e86\u6d1b\u9633\u5c45\uff0c\u76f4\u5954\u98ce\u96ea\u5c0f\u9986\u3002 \u3000\u3000\u5f20\u56db\u59d1\u4e3a\u60c5\u52a8\u6027\uff0c\u5168\u529b\u6295\u5165\u5de5\u4f5c\uff0c\u6c5f\u67ab\u5355\u72ec\u6765\u8bbf\uff0c\u4e5f\u9887\u6709\u4e00\u6170\u82b3\u5fc3\u7684\u7528\u610f\uff0c\u53ea\u53ef\u6068\u96ea\u3001\u971c\u4e8c\u5973\u8ddf\u4e2a\u5bf8\u6b65\u4e0d\u79bb\uff0c\u5f20\u56db\u59d1\u6839\u672c\u6ca1\u6709\u548c\u6c5f\u67ab\u5355\u72ec\u76f8\u5904\u7684\u673a\u4f1a\uff0c\u5f20\u56db\u59d1\u53ea\u6709\u542b\u60c5\u82e6\u7b11\uff0c\u6c5f\u67ab\u5374\u4e00\u672c\u6b63\u7ecf\u7684\u8bf4\u51fa\u4e86\u6d1b\u9633\u5c45\u4e2d\u7684\u53d8\u5316\u3002 \u3000\u3000\u201c\u6c5f\u6e56\u5e7f\u9614\uff0c\u4e94\u6e56\u56db\u6d77\u2026\u2026\u201d\u5f20\u56db\u7ad9\u8bf4\uff0c\u201c\u60f3\u4e0d\u5230\u6d1b\u9633\u5c45\u8fd9\u5ea7\u9500\u91d1\u7a9f\uff0c\u7adf\u6210\u4e86\u63ed\u53d1\u9634\u8c0b\u7684\u5173\u952e\u6240\u5728\uff0c\u4e24\u4e2a\u4e2b\u5934\u7684\u5feb\u901f\u6613\u5bb9\u624b\u6cd5\uff0c\u5df2\u6709\u6210\u5c31\uff0c\u518d\u6709\u4e2a\u4e03\u516b\u5929\uff0c\u5c31\u53ef\u4ee5\u53bb\u5e2e\u52a9\u4f60\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u554a\uff01\u8fd8\u8981\u4e03\u516b\u5929\u54ea\uff01\u2026\u2026\u201d\u541f\u96ea\u8bf4\uff1a\u201c\u6da8\u59e8\uff0c\u6211\u53d1\u89c9\u5df2\u7ecf\u5b8c\u5168\u4f1a\u4e86\uff0c\u4eca\u5929\uff0c\u5c31\u53ef\u4ee5\u8ddf\u7740\u6c5f\u5927\u54e5\u8d70\u4e86\u3002\u201d \u3000\u3000\u201c\u4e0d\u884c\uff01\u5dee\u4e4b\u6beb\u5398\uff0c\u8c2c\u4e4b\u5343\u91cc\u2026\u2026\u201d\u5f20\u56db\u59d1\u8bf4\uff0c\u201c\u4f60\u4eec\u8981\u5168\u795e\u8d2f\u6ce8\uff0c\u5927\u6982\u4e03\u516b\u5929\u624d\u80fd\u51fa\u5e08\u3002\u5982\u662f\u5fc3\u795e\u604d\u5ffd\uff0c\u8bf4\u4e0d\u5b9a\u8fd8\u8981\u4e09\u4e24\u4e2a\u6708\uff01\u201d \u3000\u3000\u4e8c\u5973\u4e00\u4f38\u820c\u5934\uff0c\u679c\u7136\u4e0d\u6562\u518d\u8bf4\u3002 \u3000\u3000\u6c5f\u67ab\u8d77\u8eab\u544a\u8f9e\uff0c\u5f20\u56db\u7ad9\u9001\u5230\u95e8\u53e3\uff0c\u770b\u4e8c\u5973\u6ca1\u6709\u9650\u6765\uff0c\u624d\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5144\u5f1f\uff0c\u6211\u662f\u6625\u8695\u4f5c\u8327\u81ea\u7f1a\uff0c\u4e1d\u5c3d\u8695\u6b7b\uff0c\u4f46\u6211\u5e7b\u60f3\u7740\u4f1a\u5316\u53ea\u5f69\u8776\u800c\u53bb\uff0c\u4f46\u96ea\u3001\u971c\u4e8c\u5973\uff0c\u5374\u8ba9\u6211\u803d\u5fc3\u4e0d\u5df2\uff0c\u4f60\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u4e0d\u77e5\u9053\uff0c\u6c5f\u6e56\u9634\u973e\u9732\u66d9\u5149\uff0c\u5c0f\u5f1f\u6b63\u5168\u529b\u4ee5\u8d74\uff0c\u5bf9\u65b9\u4ee5\u6781\u4e3a\u9634\u6bd2\u7684\u624b\u6cd5\uff0c\u5f81\u670d\u6c5f\u6e56\uff0c\u5fc5\u9700\u4ee5\u6bd2\u653b\u6bd2\uff0c\u6216\u53ef\u529b\u633d\u72c2\u6f9c\uff0c\u5c0f\u5f1f\u4ea6\u77e5\u9053\u7528\u7684\u65b9\u6cd5\u4e0d\u592a\u6b63\u5927\uff0c\u4f46\u51e0\u4f4d\u6069\u5e08\u4f20\u4e86\u6211\u8fd9\u4e9b\u672f\u827a\uff0c\u6211\u5fc5\u987b\u8981\u5b8c\u6210\u4ed6\u4eec\u5fc3\u613f\u2026\u2026\u201d \u3000\u3000\u5f20\u56db\u59d1\u63a5\u9053\uff1a\u201c\u4f60\u6709\u5f88\u591a\u5e08\u7236\uff1f\u201d \u3000\u3000\u201c\u662f\u7684\uff01\u5c0f\u5f1f\u8fd9\u4e00\u8eab\u535a\u6742\u6280\u827a\uff0c\u5c82\u662f\u4e00\u4e2a\u5e08\u7236\u80fd\u591f\u4f20\u6388\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u6709\u4e9b\u660e\u767d\u4e86\uff0c\u4f60\u90a3\u51e0\u4f4d\u5e08\u7236\u53ef\u662f\u2026\u2026\u201d \u3000\u3000\u201c\u522b\u8bf4\u51fa\u6765\uff0c\u4f60\u8bf4\u9519\u4e86\uff0c\u6211\u4e0d\u613f\u5c06\u9519\u5c31\u9519\uff0c\u4e88\u4ee5\u9ed8\u8ba4\uff0c\u8bf4\u5bf9\u4e86\uff0c\u6211\u73b0\u5728\u53c8\u4e0d\u80fd\u627f\u8ba4\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u5927\u59d0\uff0c\u6211\u4f1a\u544a\u8bc9\u4f60\u6211\u51e0\u4f4d\u5e08\u957f\u59d3\u540d\u7684\uff0c\u4e0d\u8fc7\uff0c\u65f6\u673a\u8fd8\u4e0d\u5230\u2026\u2026\u201d \u3000\u3000\u201c\u5144\u5f1f\uff0c\u5927\u59d0\u5c31\u4e0d\u660e\u767d\u4e86\uff0c\u4f60\u5e08\u7236\u662f\u8c01\uff1f\u8bf4\u51fa\u6765\uff0c\u4f1a\u6709\u4ec0\u4e48\u5173\u7cfb\uff0c\u4ed6\u4eec\u6536\u7559\u4f60\uff0c\u4f20\u4f60\u6b66\u529f\uff0c\u96be\u6ee1\u4e0d\u613f\u627f\u8ba4\u4f60\u8fd9\u4e2a\u5f92\u5f1f\uff01\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u5173\u7cfb\u5927\u4e86\u2026\u2026\u201d\u6c5f\u67ab\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u6211\u73b0\u5728\u7684\u884c\u4e8b\u65b9\u6cd5\uff0c\u6709\u70b9\u90aa\u6c14\uff0c\u5982\u82e5\u80fd\u626b\u9664\u4e86\u6c5f\u6e56\u4e0a\u7684\u90aa\u9b54\u5996\u6c1b\uff0c\u5927\u5bb6\u4e5f\u8bb8\u80fd\u591f\u8c05\u89e3\uff0c\u81f3\u5c11\u53ef\u4ee5\u8bf4\u884c\u4e8b\u867d\u4e0d\u62e9\u624b\u6bb5\uff0c\u4f46\u5374\u5fc3\u5b58\u6b66\u6797\u5927\u4e49\uff0c\u9739\u96f3\u624b\u6bb5\uff0c\u83e9\u8428\u5fc3\u80a0\uff0c\u4f46\u5982\u4e0d\u80fd\u6210\u529f\uff0c\u522b\u4eba\u5982\u4f55\u6c61\u8511\u6211\uff0c\u90a3\u5c31\u5f88\u96be\u9884\u4f30\u4e86\uff0c\u4e0d\u77e5\u51e0\u4f4d\u5e08\u957f\u59d3\u540d\uff0c\u7f6a\u5728\u6211\u6c5f\u67ab\u4e00\u4eba\uff0c\u5982\u82e5\u8fde\u7d2f\u5230\u4ed6\u4eec\u7684\u6e05\u8a89\u53d7\u635f\uff0c\u90a3\u5c31\u975e\u6211\u4e4b\u613f\u4e86\uff1f\u201d \u3000\u3000\u201c\u5144\u5f1f\u76f8\u8bc6\u4ee5\u6765\uff0c\u6211\u7b2c\u4e00\u6b21\u89c1\u5230\u4f60\u8868\u73b0\u5112\u5bb6\u7684\u5927\u9053\uff0c\u2026\u2026\u201d \u3000\u3000\u201c\u8fd9\u4e5f\u7b97\u5927\u9053\u5417\uff1f\u201d \u3000\u3000\u201c\u600e\u4e48\u4e0d\u662f\uff0c\u5c0a\u5e08\u91cd\u9053\u554a\uff01\u201d\u5f20\u56db\u59d1\u7b11\u4e00\u7b11\u53c8\u9053\uff1a\u201c\u4f60\u6709\u8fd9\u79cd\u72ec\u4efb\u6bc1\u8c24\u7684\u6c14\u6982\uff0c\u53eb\u4eba\u656c\u91cd\uff0c\u4f46\u4f60\u90a3\u80a1\u5e26\u70b9\u90aa\u6c14\u7684\u653e\u8361\uff0c\u5374\u53eb\u4eba\u7231\u715e\u4e86\u2026\u2026\u201d\u5f20\u56db\u59d1\u7f13\u7f13\u884c\u4e86\u8fc7\u6765\uff0c\u76ee\u4e2d\u60c5\u7130\u5982\u706b\u3002 \u3000\u3000\u6c5f\u67ab\u5f20\u5f00\u53cc\u81c2\uff0c\u7d27\u7d27\u7684\u62e5\u62b1\u4f4f\u5f20\u56db\u59d1\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5927\u59d0\uff0c\u771f\u7684\u82e6\u4e86\u4f60\u5566\uff01\u201d \u3000\u3000\u201c\u6211\u613f\u610f\uff0c\u4f60\u5982\u80fd\u5fd9\u4e2d\u5077\u95f2\uff0c\u7ed9\u5927\u59d0\u4e00\u70b9\u6e29\u5b58\uff0c\u6211\u5c31\u66f4\u6ee1\u8db3\u4e86\u3002\u201d \u3000\u3000\u6c5f\u67ab\u62b1\u7d27\u4e86\u5f20\u56db\u59d1\u7684\u5a07\u8eaf\uff0c\u5f20\u56db\u59d1\u9001\u4e0a\u4e86\u4e24\u7247\u6a31\u5507\u2026\u2026 \u3000\u3000\u9676\u9189\u5728\u6e29\u67d4\u4e2d\u7684\u5f20\u56db\u59d1\u4f3c\u662f\u7a81\u7136\u88ab\u9488\u624e\u4e86\u4e00\u4e0b\uff0c\u6025\u6025\u63a8\u5f00\u4e86\u6c5f\u67ab\u3002 \u3000\u3000\u6c5f\u67ab\u501f\u52bf\u4e00\u4e2a\u8f6c\u8eab\uff0c\u98de\u8dc3\u800c\u53bb\u3002 \u3000\u3000\u5f20\u56db\u59d1\u9547\u9759\u4e00\u4e0b\u5fc3\u795e\uff0c\u56de\u5934\u770b\u53bb\uff0c\u53ea\u89c1\u541f\u96ea\u3001\u541f\u971c\uff0c\u7f13\u7f13\u884c\u4e86\u8fc7\u6765\uff0c\u8138\u4e0a\u662f\u4e00\u7247\u5fae\u5fae\u7684\u7b11\u610f\u3002 \u3000\u3000\u5f20\u56db\u59d1\u8138\u4e0a\u6cdb\u8d77\u4e86\u4e00\u7247\u7f9e\u7ea2\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u4f60\u4eec\u6765\u591a\u4e45\u4e86\uff1f\u201d \u3000\u3000\u201c\u521a\u521a\u5230\u2026\u2026\u201d\u541f\u96ea\u7b11\u9053\uff1a\u201c\u6211\u4eec\u5148\u53eb\u4f60\uff0c\u7136\u540e\u624d\u8d70\u8fc7\u6765\u3002\u201d \u3000\u3000\u201c\u5f20\u59e8\u2026\u2026\u201d\u541f\u971c\u9053\uff1a\u201c\u6211\u4eec\u4ec0\u4e48\u90fd\u6ca1\u6709\u770b\u5230\uff01\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\uff0c\u753b\u86c7\u6dfb\u8db3\uff0c\u5f20\u56db\u59d1\u53ea\u597d\u8f6c\u8fc7\u8bdd\u9898\uff0c\u9053\uff1a\u201c\u56de\u53bb\u5427\uff01 \u3000\u3000\u6211\u8981\u7763\u4fc3\u4f60\u4eec\u5c3d\u5feb\u5b66\u597d\u6613\u5bb9\u672f\uff0c\u53bb\u5e2e\u6c5f\u67ab\u4e00\u628a\u3002\u201d \u3000\u3000\u201c\u771f\u7684\uff01\u6c5f\u5927\u54e5\u662f\u4e0d\u662f\u5f88\u9700\u8981\u6211\u4eec\u2026\u2026\u201d\u541f\u96ea\u9053\uff1a\u201c\u4ed6\u4e00\u4e2a\u4eba\u8981\u5bf9\u4ed8\u5f88\u591a\u654c\u4eba\uff0c\u5b9e\u5728\u4e5f\u5f88\u53ef\u601c\u54ea\uff01\u201d \u3000\u3000\u201c\u5f20\u59e8\u60f3\u901a\u4e86\uff0c\u5e08\u7236\u9886\u8fdb\u95e8\uff0c\u4fee\u884c\u5728\u4e2a\u4eba\uff0c\u8be5\u8bf4\u7684\u5f20\u59e8\u90fd\u8bf4\u8fc7\u4e86\uff0c\u4f60\u4eec\u80fd\u4e0d\u80fd\u9003\u53bb\u52ab\u6570\uff0c\u5168\u5728\u4f60\u4eec\u81ea\u5df1\u4e86\u3002\u4e0d\u8fc7\uff0c\u8bdd\u8bf4\u524d\u5934\uff0c\u5f20\u59e8\u53ef\u8981\u628a\u8fd9\u4ef6\u4e8b\uff0c\u544a\u8bc9\u4f60\u4eec\u5988\u5988\u3002\u201d \u3000\u3000\u53f6\u96ea\u5ae3\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5f20\u59e8\uff0c\u6211\u4eec\u72af\u4e86\u4ec0\u4e48\u9519\u5417\uff1f\u201d \u3000\u3000\u5f20\u56db\u59d1\u5fae\u5fae\u4e00\u6014\u9053\uff1a\u201c\u90a3\u5012\u6ca1\u6709\u3002\u201d \u3000\u3000\u53f6\u96ea\u9053\uff1a\u201c\u90a3\u5f20\u59e8\u8981\u544a\u8bc9\u5988\u5988\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5f20\u56db\u59d1\u6123\u4f4f\u4e86\u3002 \u3000\u3000\u541f\u96ea\u95ee\u7684\u5f88\u5c16\u9510\uff0c\u5f20\u56db\u59d1\u4e00\u65f6\u95f4\u771f\u8fd8\u60f3\u4e0d\u51fa\u5982\u4f55\u56de\u7b54\u3002 \u3000\u3000\u541f\u971c\u5374\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u54ce\u5440\uff01\u59d0\uff0c\u4f60\u771f\u7684\u5f88\u7b28\u554a\uff01\u5f20\u59e8\u544a\u8bc9\u5988\u7684\uff0c\u4e00\u5b9a\u662f\u6c5f\u5927\u54e5\u7684\u4e8b\u60c5\u3002\u201d \u3000\u3000\u201c\u971c\u4e2b\u5934\uff0c\u8bf4\u4e0b\u53bb\uff0c\u6c5f\u5927\u54e5\u4ec0\u4e48\u4e8b\u60c5\uff1f\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\u2026\u2026\u201d\u541f\u971c\u8bf4\uff0c\u201c\u5f20\u59e8\u4e00\u5b9a\u611f\u89c9\u5230\uff0c\u6211\u4eec\u5f88\u559c\u6b22\u6c5f\u5927\u54e5\uff0c\u662f\u5417\uff1f\u201d \u3000\u3000\u5f20\u56db\u59d1\u5fc3\u4e2d\u5fd6\u9053\uff1a\u201c\u8fd9\u4e24\u4e2a\u4e2b\u5934\u806a\u660e\u7edd\u4f26\uff0c\u4e5f\u5230\u4e86\u5c11\u5973\u6000\u6625\u7684\u5e74\u9f84\uff0c\u548c\u5979\u4eec\u660e\u9523\u660e\u9f13\u7684\u8bf4\u4e2a\u6e05\u695a\u4e5f\u597d\u2026\u2026 \u3000\u3000\u5979\u4eec\u5b8c\u5168\u660e\u767d\u4e86\uff0c\u4ee5\u540e\u7684\u4e8b\uff0c\u5c31\u7531\u5979\u4eec\u81ea\u5df1\u51b3\u5b9a\u5427\uff01 \u3000\u3000\u6211\u8fd9\u628a\u5c81\u6570\u4e86\uff0c\u5c31\u628a\u6301\u4e0d\u4f4f\u81ea\u5df1\u2026\u2026 \u3000\u3000\u4f55\u51b5\uff0c\u8fd9\u4e24\u4e2a\u60c5\u7aa6\u521d\u5f00\u7684\u4e2b\u5934\uff0c\u628a\u8fd9\u4ef6\u4e8b\u544a\u8bc9\u5979\u4eec\u6bcd\u4eb2\uff0c\u5e72\u8106\u628a\u5979\u4eec\u5e26\u56de\u5c71\u4e0a\uff0c\u90a3\u5c31\u4e00\u4e86\u767e\u4e86\u3002\u201d \u3000\u3000\u6709\u4e86\u8fd9\u5c42\u60f3\u6cd5\uff0c\u5f20\u56db\u59d1\u51b3\u5fc3\u548c\u4e8c\u5973\u8c08\u4e2a\u6e05\u695a\uff0c\u9690\u9690\u6697\u793a\uff0c\u5df2\u65e0\u6cd5\u5e94\u4ed8\u773c\u4e0b\u7684\u60c5\u52bf\u53d1\u5c55\u4e86\u3002 \u3000\u3000\u201c\u4f60\u4eec\u662f\u4e0d\u662f\u5f88\u559c\u6b22\u6c5f\u5927\u54e5\u5462\uff1f\u201d \u3000\u3000\u201c\u662f\uff01\u2026\u2026\u201d\u541f\u971c\u7b54\u590d\u7684\u5f88\u660e\u786e\uff1a\u201c\u6211\u559c\u6b22\uff0c\u59d0\u59d0\u4e5f\u559c\u6b22\uff0c\u5f20\u59e8\u4e0d\u662f\u8981\u6211\u4eec\u5168\u529b\u5e2e\u52a9\u6c5f\u5927\u54e5\u5417\uff1f\u6211\u4eec\u559c\u6b22\u4ed6\uff0c\u624d\u4f1a\u5fc3\u7518\u60c5\u613f\u7684\u4e3a\u4ed6\u4ed8\u51fa\u4e00\u5207\u3002\u201d \u3000\u3000\u7b80\u6d01\u660e\u6717\uff0c\u5b57\u5b57\u6709\u529b\uff0c\u5f20\u56db\u59d1\u53cd\u4e0d\u77e5\u5982\u4f55\u56de\u7b54\u4e86\u3002 \u3000\u3000\u201c\u6211\u4eec\u660e\u767d\u5f20\u59e8\u7684\u82e6\u8877\uff01\u6240\u4ee5\uff0c\u5c3d\u7ba1\u544a\u8bc9\u5988\u5988\uff0c\u6211\u4eec\u4e0d\u4f1a\u602a\u5f20\u59e8\u7684\u2026\u2026\u201d\u541f\u96ea\u8bf4\uff0c\u201c\u5988\u5988\u6765\u4e86\uff0c\u5c31\u662f\u5f20\u59e8\u4e0d\u8bf4\uff0c\u6211\u4eec\u4e5f\u4f1a\u5bf9\u5988\u5988\u8bf4\u7684\uff0c\u7239\u5988\u65e2\u7136\u7b54\u5e94\u4e86\u8ba9\u6211\u884c\u4eba\u6c5f\u6e56\uff0c\u5c31\u5e94\u8be5\u627f\u53d7\u8fd9\u4e9b\u53ef\u80fd\u53d1\u751f\u7684\u4e8b\u60c5\u2026\u2026\u201d \u3000\u3000\u201c\u662f\u554a\uff01\u5f20\u59e8\u2026\u2026\u201d\u541f\u971c\u8bf4\uff0c\u201c\u79cd\u690d\u5728\u9752\u5c71\u7fe0\u8c37\u7684\u677e\u7af9\uff0c\u5fc5\u987b\u8981\u9762\u5bf9\u7740\u98ce\u96e8\u7684\u4fb5\u88ad\uff0c\u6211\u4eec\u4e5f\u5f88\u60f3\u77e5\u9053\u7239\u548c\u5988\u6709\u4ec0\u4e48\u597d\u7684\u529e\u6cd5\uff0c\u80fd\u628a\u4ed6\u4e00\u53cc\u5973\u513f\u4fdd\u62a4\u4f4f\uff0c\u4e0d\u53d7\u98ce\u5439\u96e8\u6253\u3002\u201d \u3000\u3000\u8fd9\u756a\u8bdd\u4f3c\u662f\u800c\u975e\uff0c\u4f46\u5374\u663e\u793a\u4e8c\u5973\u5f3a\u70c8\u7684\u81ea\u6211\u610f\u613f\u3002 \u3000\u3000\u5f20\u56db\u59d1\u82e6\u7b11\u4e8c\u4e0b\uff0c\u9053\uff1a\u201c\u6211\u65e9\u8be5\u548c\u4f60\u4eec\u8c08\u8c08\u7684\uff0c\u4f60\u4eec\u8fc7\u53bb\u7684\u751f\u6d3b\u662f\u4e00\u7247\u7eaf\u6d01\uff0c\u4f46\u5f20\u59e8\u5ffd\u7565\u4e86\u8d8a\u7eaf\u51c0\u7684\u751f\u6d3b\uff0c\u8d8a\u96be\u5e94\u4ed8\u751f\u6d3b\u4e2d\u7a81\u6765\u7684\u53d8\u5316\uff0c\u5c31\u50cf\u4e00\u5757\u6d01\u767d\u7684\u7ee2\u5e03\uff0c\u6cbe\u4e0a\u7684\u8272\u5f69\u4e5f\u8d8a\u663e\u7684\u660e\u8273\u3002 \u3000\u3000\u771f\u7684\uff0c\u5f20\u59e8\u5df2\u7ecf\u611f\u89c9\u5230\u5fc3\u529b\u4ea4\u7601\u4e86\uff0c\u4e5f\u8bb8\u4f60\u5988\u5988\u6709\u80fd\u89e3\u5f00\u4f60\u4eec\u8fd9\u4e2a\u6b7b\u7ed3\u3002\u201d \u3000\u3000\u541f\u971c\u8e59\u8d77\u4e86\u79c0\u7709\u513f\uff0c\u9053\uff1a\u201c\u5f20\u59e8\uff0c\u4f60\u7a76\u7adf\u5728\u62c5\u5fc3\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u6211\u6015\u2026\u2026\u201d\u5f20\u56db\u59d1\u795e\u8272\u8083\u7136\u5730\u8bf4\uff0c\u201c\u6211\u6015\u4f60\u4eec\u8eab\u9677\u60c5\u7f51\uff0c\u4f5c\u8327\u81ea\u7f1a\uff0c\u8981\u5f20\u59e8\u65e5\u540e\u5982\u4f55\u5411\u4f60\u4eec\u7239\u5a18\u4ea4\u4ee3\uff1f\u201d \u3000\u3000\u541f\u971c\u8138\u4e0a\u662f\u4e00\u7247\u4f3c\u61c2\u975e\u61c2\u7684\u795e\u8272\uff0c\u6447\u6447\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u5f20\u59e8\uff01 \u3000\u3000\u4e0d\u7528\u4e3a\u96be\uff0c\u5988\u5988\u5df2\u4f20\u6765\u8baf\u606f\uff0c\u8fd9\u51e0\u5929\u5c31\u5230\u957f\u5b89\u3002\u5f20\u59e8\u60f3\u8bf4\u4ec0\u4e48\uff1f \u3000\u3000\u5c31\u660e\u660e\u767d\u767d\u544a\u8bc9\u5988\u5988\u5427\u4e86\u4e5f\u8bb8\u2014\u2014\u6211\u4eec\u771f\u7684\u6709\u4e86\u4ec0\u4e48\u8fc7\u9519\uff1f\u53ea\u662f\uff0c\u6211\u548c\u59d0\u59d0\u4e00\u76f4\u60f3\u4e0d\u660e\u767d\uff0c\u9519\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u201c\u4f60\u4eec\u6ca1\u6709\u9519\uff0c\u5f20\u59e8\u662f\u9632\u60a3\u672a\u7136\uff0c\u6c5f\u67ab\u4fee\u4e60\u7684\u6b66\u529f\u592a\u535a\u6742\u4e86\uff0c\u6240\u4ee5\uff0c\u4ed6\u5177\u6709\u4e86\u5f3a\u70c8\u7684\u7537\u4eba\u9b45\u529b\uff0c\u4f46\u4ed6\u4e0d\u662f\u4e2a\u53ef\u6258\u7ec8\u8eab\u7684\u4eba\uff01\u4e5f\u4e0d\u662f\u4e00\u4e2a\u53ef\u4ee5\u4ea4\u5f80\u7684\u670b\u53cb\u2026\u2026\u201d \u3000\u3000\u201c\u5662\uff01\u6c5f\u5927\u54e5\u662f\u8fd9\u4e48\u574f\u7684\u4e00\u4e2a\u4eba\u5417\uff1f\u2026\u2026\u201d\u541f\u9738\u9053\uff1a\u201c\u90a3\u6211\u4eec\u4e3a\u4ec0\u4e48\u8fd8\u8981\u5e2e\u52a9\u4ed6\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4e0d\u662f\u574f\u2026\u2026\u201d\u5f20\u56db\u59d1\u8bf4\uff0c\u201c\u53ea\u662f\u4ed6\u80a9\u8d1f\u4e86\u592a\u6c89\u91cd\u7684\u8d23\u4efb\uff0c\u4ed6\u5fc5\u9700\u8981\u5b8c\u6210\u5b83\uff0c\u6240\u4ee5\uff0c\u884c\u4e8b\u53ea\u6c42\u6210\u529f\uff0c\u4e0d\u62e9\u624b\u6bb5\uff0c\u4ed6\u5fc3\u4e2d\u53ea\u6709\u594b\u8fdb\uff0c\u6ca1\u6709\u79c1\u4eba\u60c5\u611f\uff0c\u4efb\u4f55\u4eba\u5bf9\u4ed6\u52a8\u4e86\u60c5\uff0c\u5c31\u4f1a\u53d7\u5230\u4f24\u5bb3\u3002\u8fd9\u6837\u7684\u4e00\u4e2a\u7537\u4eba\uff0c\u662f\u53ef\u4ee5\u5171\u4e8b\uff0c\u4e0d\u80fd\u5bc4\u60c5\u5728\u4ed6\u7684\u8eab\u4e0a\u3002\u201d \u3000\u3000\u201c\u5662\uff01\u6211\u4eec\u660e\u767d\u4e86\u2026\u2026\u201d\u541f\u96ea\u8bf4\uff0c\u201c\u5988\u5988\u51e0\u65f6\u53ef\u5230\u957f\u5b89\uff1f\u201d \u3000\u3000\u201c\u660e\u5929\uff01\u6211\u5c31\u53bb\u63a5\u5979\u2026\u2026\u201d\u5f20\u56db\u59d1\u8bf4\uff1a\u201c\u5979\u8bf4\u8fd9\u4e24\u5929\u5230\uff0c\u6700\u8fdf\u540e\u592d\u5427\uff01\u4f60\u4eec\u4e00\u5b9a\u53ef\u4ee5\u89c1\u5230\u5988\u5988\u4e86\u3002\u201d \u3000\u3000\u201c\u7239\u5462\uff1f\u662f\u4e0d\u662f\u8ddf\u5988\u4e00\u8d77\u6765\uff1f\u2026\u2026\u201d\u541f\u971c\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5509\uff01\u6211\u95ee\u7684\u597d\u8822\u554a\uff01\u7239\u5a18\u4e00\u5411\u662f\u5bf8\u6b65\u4e0d\u79bb\uff0c\u5988\u6765\u4e86\uff0c\u7239\u81ea\u7136\u8ddf\u7740\u6765\u4e86\uff01\u201d \u3000\u3000\u5f20\u56db\u59d1\u6293\u4f4f\u4e86\u673a\u4f1a\u8bf4\u9053\uff1a\u201c\u4f60\u7239\u5a18\u624d\u662f\u4eba\u95f4\u6700\u5e78\u798f\u7684\u4eba\uff0c\u6069\u7231\u6570\u5341\u5e74\uff0c\u76f8\u770b\u4e24\u4e0d\u538c\uff0c\u5f53\u4e16\u4e4b\u4e2d\uff0c\u4e5f\u5f88\u96be\u627e\u5f97\u51fa\u51e0\u5bf9\u6765\uff1f\u201d \u3000\u3000\u541f\u96ea\u3001\u541f\u971c\u76f8\u89c6\u4e00\u7b11\uff0c\u672a\u518d\u591a\u8a00\u3002 \u3000\u3000\u5f20\u56db\u59d1\u5fc3\u4e2d\u4e00\u52a8\uff0c\u5fd6\u9053\uff1a\u201c\u8fd9\u4e24\u4e2a\u4e2b\u5934\u7b11\u7684\u597d\u751f\u602a\u5f02\uff0c\u96be\u9053\u2026\u2026\u201d \u3000\u3000\u96be\u9053\u5982\u4f55\uff1f\u5f20\u56db\u59d1\u4e0d\u8ba9\u81ea\u5df1\u518d\u60f3\u4e0b\u53bb\uff0c\u4e8c\u5973\u7236\u6bcd\uff0c\u662f\u5979\u5fc3\u76ee\u4e2d\u7684\u795e\u4ed9\u7737\u5c5e\uff0c\u5979\u4e0d\u613f\u610f\u7834\u574f\u8fd9\u4e2a\u7f8e\u597d\u7684\u5370\u8c61\u3002 \u3000\u3000\u6c5f\u67ab\u66f4\u6362\u4e86\u4e00\u8eab\u9ad8\u96c5\u7684\u8863\u670d\uff0c\u7f13\u6b65\u884c\u5165\u4e86\u6d1b\u9633\u5c45\u3002 \u3000\u3000\u4ed6\u8fd9\u756a\u523b\u610f\u7684\u88c5\u626e\uff0c\u679c\u7136\u5f15\u8d77\u4e86\u6d1b\u9633\u5c45\u4e2d\u4f19\u8ba1\u7684\u656c\u91cd\uff0c\u63a5\u5f85\u7684\u5341\u5206\u5ba2\u6c14\u3001\u4eb2\u5207\u3002 \u3000\u3000\u4ed6\u6ca1\u5954\u8d74\u6885\u82b1\u7684\u7ea6\u4f1a\uff0c\u5374\u6447\u6447\u6446\u6446\u7684\u8fdb\u5165\u4e86\u6d1b\u9633\u697c\u3002 \u3000\u3000\u8fd9\u6b63\u662f\u4e2d\u5348\u65f6\u5206\uff0c\u8ddd\u79bb\u9752\u51e4\u7b2c\u4e00\u6b21\u6f14\u5531\uff0c\u8fd8\u6709\u4e00\u4e2a\u65f6\u8fb0\u3002 \u3000\u3000\u6c5f\u67ab\u62db\u6765\u4e86\u4e00\u4e2a\u5e97\u4f19\u8ba1\uff0c\u63d0\u51fa\u4e00\u4e2a\u4ece\u672a\u6709\u8fc7\u7684\u8981\u6c42\uff0c\u4ed6\u8981\u5305\u4e0b\u697c\u4e0a\u7b2c\u4e00\u6392\u56db\u4e2a\u76f8\u8fde\u7684\u5305\u53a2\uff0c\u8fd8\u8981\u4e86\u4e00\u684c\u4e30\u76db\u7684\u5bb4\u5e2d\uff0c\u5f00\u5728\u56db\u4e2a\u76f8\u8fde\u7684\u5305\u53a2\u4e2d\uff0c\u64a4\u51fa\u56db\u4e2a\u5305\u53a2\u4e4b\u95f4\u7684\u9694\u963b\u58c1\u677f\uff0c\u4f7f\u5b83\u6210\u4e86\u4e00\u4e2a\u7279\u5927\u7684\u5305\u53a2\u3002 \u3000\u3000\u5e97\u4f19\u8ba1\u9762\u6709\u96be\u8272\uff0c\u8fd9\u91cc\u7684\u751f\u610f\u662f\u5750\u65e0\u865a\u5e2d\uff0c\u6c5f\u67ab\u63d0\u51fa\u7684\u8981\u6c42\uff0c\u66f4\u4f7f\u4ed6\u4eec\u4e3a\u96be\uff0c\u4f46\u6c5f\u67ab\u613f\u610f\u4ed8\u51fa\u4e94\u5343\u4e24\u94f6\u5b50\u7684\u9ad8\u4ef7\uff0c\u4f7f\u6d1b\u9633\u697c\u7684\u9886\u73ed\u9ad8\u5927\u6597\u6026\u7136\u5fc3\u52a8\uff0c\u4ed6\u5fc3\u4e2d\u7b97\u8fc7\u8fd9\u4e2a\u5e10\uff0c\u56db\u4e2a\u5305\u53a2\u7684\u552e\u4ef7\uff0c\u4e0d\u8fc7\u662f\u56db\u5341\u4e24\u94f6\u5b50\uff0c\u52a0\u4e0a\u4e00\u767e\u4e24\u94f6\u5b50\u7684\u4e00\u5e2d\u9152\u5bb4\uff0c\u7b97\u4e0a\u5929\u4e5f\u4e0d\u8fc7\u662f\u4e00\u767e\u56db\u5341\u4e24\u94f6\u5b50\u3002 \u3000\u3000\u9ad8\u5927\u6597\u7684\u5e74\u7eaa\u4e0d\u5927\uff0c\u4f46\u5904\u4e8b\u679c\u51b3\uff0c\u8ff3\u81ea\u4f5c\u4e86\u51b3\u5b9a\uff0c\u7acb\u523b\u62c6\u51fa\u58c1\u677f\uff0c\u5df2\u5b9a\u4e86\u5305\u53a2\u7684\u5ba2\u4eba\uff0c\u52a0\u500d\u9000\u94b1\uff0c\u800c\u4e14\uff0c\u518d\u514d\u8d39\u9001\u4e0b\u4e00\u573a\u7684\u53a2\u4f4d\u3002 \u3000\u3000\u4e00\u573a\u53ef\u80fd\u5f15\u8d77\u7ea0\u7eb7\u7684\u9ebb\u70e6\uff0c\u5c31\u5728\u9ad8\u5927\u6597\u7684\u660e\u5feb\u5904\u7f6e\u4e0b\uff0c\u5b8c\u5168\u89e3\u51b3\u3002 \u3000\u3000\u6c5f\u67ab\u7ec8\u4e8e\u770b\u5230\u4e86\u4ed6\u4eec\u5904\u4e8b\u7684\u80fd\u529b\uff0c\u4e5f\u53d1\u89c9\u5185\u5bab\u7cfb\u7edf\u4e2d\u7684\u5f1f\u5b50\uff0c\u7684\u786e\u6709\u4e0d\u5c11\u4eba\u624d\u3002 \u3000\u3000\u4f46\u6c5f\u67ab\u6536\u83b7\u66f4\u5927\u7684\u662f\u53d1\u89c9\u4e86\u5404\u5904\u7684\u4f19\u8ba1\u9886\u73ed\uff0c\u624d\u662f\u5177\u6709\u4e00\u65b9\u6743\u52bf\u7684\u4eba\u7269\uff0c\u6bcf\u4e00\u5904\u7684\u5e97\u4f19\u8ba1\u5bf9\u9886\u73ed\u662f\u552f\u547d\u662f\u4ece\u3002 \u3000\u3000\u6d1b\u9633\u697c\u6570\u5341\u4e2a\u7537\u5973\u4f8d\u5e94\uff0c\u662f\u6e05\u4e00\u8272\u7684\u5e74\u8f7b\u4eba\uff0c\u9093\u98de\u7684\u52bf\u529b\uff0c\u5168\u88ab\u963b\u7edd\u3002 \u3000\u3000\u9ad8\u5927\u6597\u662f\u8fd9\u91cc\u53ef\u5f53\u5bb6\u4f5c\u4e3b\u7684\u4eba\uff0c\u4f46\u7edd\u975e\u771f\u6b63\u7684\u9886\u5bfc\u4eba\u7269\uff0c\u4e5f\u8bb8\u9752\u51e4\u624d\u662f\u771f\u6b63\u7684\u9996\u8111\u2026\u2026\u3002 \u3000\u3000\u4ed6\u60f3\u8d77\u4e86\u4e07\u65b9\u7684\u8bdd\u548c\u6885\u82b1\u7684\u63a8\u6d4b\uff0c\u9752\u98ce\u4f3c\u662f\u53e6\u4e00\u80a1\u52bf\u529b\u4e2d\u4eba\uff0c\u5982\u679c\uff0c\u4e86\u89e3\u4e86\u9752\u51e4\uff0c\u4e5f\u8bb8\uff0c\u6574\u4e2a\u795e\u79d8\u7ec4\u5408\u7684\u5f62\u6001\uff0c\u5c31\u53ef\u4ee5\u6709\u4e00\u4e2a\u5927\u6982\u7684\u8f6e\u5ed3\u4e86\u2026\u2026\u3002 \u3000\u3000\u6c5f\u67ab\u4ed8\u51fa\u4e86\u4e94\u5343\u4e24\u94f6\u7968\u4e4b\u540e\uff0c\u6084\u7136\u79bb\u53bb\uff0c\u4ee5\u6781\u4e0a\u4e58\u7684\u8f7b\u529f\u8eab\u6cd5\uff0c\u5728\u4f17\u591a\u7684\u5ba2\u4eba\u4e2d\uff0c\u95ea\u5165\u4e86\u82b1\u6811\u4e1b\u4e2d\u3002 \u3000\u3000\u679c\u7136\u53d1\u89c9\u4e86\u9ad8\u5927\u6597\u62ff\u7740\u94f6\u7968\uff0c\u5954\u884c\u5165\u9752\u51e4\u697c\u4e2d\u3002 \u3000\u3000\u8fd9\u5ea7\u957f\u5b89\u6700\u5927\u7684\u9500\u91d1\u7a9f\u4e2d\uff0c\u5145\u6ee1\u7740\u6b4c\u58f0\u6b22\u7b11\uff0c\u4e5f\u6709\u7740\u6597\u4e89\u6740\u673a\u3002 \u3000\u3000\u9752\u51e4\u767b\u53f0\u6f14\u5531\u4e86\uff0c\u76ee\u5149\u6295\u6ce8\u5728\u90a3\u56db\u5408\u4e00\u7684\u5927\u5305\u53a2\u4e2d\uff0c\u4f46\u89c1\u9152\u83dc\u7f57\u5217\uff0c\u4e24\u4e2a\u5e74\u8f7b\u5973\u5a62\u6014\u6014\u7684\u7ad9\u5728\u90a3\u91cc\uff0c\u5374\u4e0d\u89c1\u82b1\u4e86\u4e94\u5343\u4e24\u94f6\u5b50\u6446\u4e0b\u8fd9\u5927\u6392\u573a\u7684\u4e3b\u4eba\u3002 \u3000\u3000\u5fc3\u5934\u95ea\u63a0\u8fc7\u4e00\u79cd\u5947\u602a\u5730\u611f\u89c9\uff0c\u51e0\u9996\u5531\u8154\u8d70\u677f\uff0c\u8d76\u5fd9\u5438\u53e3\u6c14\u5b9a\u4e0b\u5fc3\u795e\u3002 \u3000\u3000\u5c31\u5728\u5979\u5fc3\u795e\u7a0d\u5b9a\u65f6\uff0c\u6c5f\u67ab\u6380\u5f00\u5782\u5e18\uff0c\u7f13\u6b65\u884c\u5165\u4e86\u5305\u53a2\u3002 \u3000\u3000\u8fd9\u5c31\u5f15\u8d77\u4e86\u9752\u51e4\u5f3a\u70c8\u7684\u597d\u5947\u5fc3\u5ff5\uff0c\u53cc\u76ee\u51dd\u795e\uff0c\u671b\u5411\u6c5f\u67ab\u3002 \u3000\u3000\u6c5f\u67ab\u611f\u53d7\u5230\u4e24\u9053\u51cc\u5389\u7684\u773c\u795e\uff0c\u867d\u76f8\u8ddd\u6570\u4e08\u4e4b\u8fdc\uff0c\u4e5f\u6709\u7740\u6d1e\u5bdf\u7ec6\u5fae\u7684\u80fd\u529b\uff0c\u90a3\u662f\u7cbe\u6df1\u5185\u529f\u7684\u5f81\u8c61\u3002 \u3000\u3000\u72d0\u72f8\u7ec8\u4e8e\u9732\u51fa\u4e86\u5c3e\u5df4\uff0c\u6885\u82b1\u7684\u63a8\u65ad\u4e0d\u9519\uff0c\u9752\u51e4\u5df2\u88ab\u6c5f\u67ab\u7684\u8bbe\u8ba1\uff0c\u5f15\u73b0\u51fa\u5b9e\u529b\u3002 \u3000\u3000\u6c5f\u67ab\u9762\u5bf9\u7740\u9752\u51e4\uff0c\u5fae\u5fae\u9894\u9996\uff0c\u6f47\u6d12\u4e00\u7b11\uff0c\u201c\u60d1\u5fc3\u672f\u201d\u7684\u9b45\u529b\uff0c\u4e5f\u5b8c\u529b\u53d1\u6325\u51fa\u6765\u3002 \u3000\u3000\u56db\u76ee\u4ea4\u6ce8\uff0c\u9752\u51e4\u5ffd\u515a\u5fc3\u5934\u4e00\u8361\uff0c\u4f46\u5979\u7acb\u523b\u8b66\u89c9\uff0c\u8f6c\u8fc7\u7751\u53bb\u3002 \u3000\u3000\u201c\u597d\u5389\u5bb3\u7684\u4e2b\u5934\u2026\u2026\u201d\u6c5f\u98ce\u5fc3\u4e2d\u6697\u8d5e\uff0c\u201c\u6210\u5c31\u4e4b\u9ad8\uff0c\u679c\u7136\u5728\u6885\u82b1\u4e4b\u4e0a\u2026\u2026\u201d \u3000\u3000\u4e24\u4e2a\u4f8d\u5e94\u7684\u5973\u5a62\uff0c\u884c\u4e86\u8fc7\u6765\uff0c\u628a\u6c5f\u67ab\u7275\u5165\u6b63\u4e2d\u5750\u6905\u4e0a\uff0c\u659f\u9152\u5949\u83dc\uff0c\u7167\u987e\u7684\u65e0\u5fae\u4e0d\u81f3\u3002 \u3000\u3000\u4e24\u4e2a\u5973\u5a62\uff0c\u5012\u662f\u5168\u5fc3\u5728\u7167\u5e94\u5ba2\u4eba\uff0c\u5979\u4eec\u611f\u89c9\u5230\uff0c\u8fd9\u662f\u5979\u4eec\u89c1\u5230\u6700\u52a8\u4eba\u7684\u7537\u4eba\uff0c\u6700\u53d7\u6b22\u8fce\u7684\u5ba2\u4eba\uff0c\u5e94\u8be5\u4ee5\u6700\u6e29\u67d4\u7684\u65b9\u9488\uff0c\u8ba8\u53d6\u4ed6\u7684\u6b22\u5fc3\u624d\u5bf9\u2026\u2026\u3002 \u3000\u3000\u4e24\u4e2a\u5973\u5a62\u4e89\u76f8\u4ee5\u5168\u529b\u5949\u732e\u81ea\u5df1\uff0c\u5c3d\u91cf\u628a\u5a07\u8eaf\u9760\u8fd1\u6c5f\u67ab\u3002 \u3000\u3000\u4f46\u6c5f\u67ab\u5374\u6d51\u5982\u4e0d\u89c9\uff0c\u4ed6\u6ca1\u6709\u63a8\u62d2\u4e8c\u5973\u7684\u732e\u5a9a\uff0c\u4e5f\u672a\u62d2\u4e8c\u5973\u4f8d\u9152\u656c\u83dc\uff0c\u53ea\u662f\u7aef\u5750\u6905\u4e2d\uff0c\u76ee\u4e0d\u659c\u89c6\uff0c\u671b\u4e5f\u672a\u671b\u4e8c\u5973\u4e00\u773c\u2026\u2026\u3002 \u3000\u3000\u7a81\u7136\u95f4\uff0c\u6b4c\u58f0\u4e00\u53d8\uff0c\u5b9b\u8f6c\u6e05\u97f3\u4e2d\uff0c\u900f\u51fa\u4e86\u67d4\u5a9a\u7684\u65e0\u6bd4\u7684\u8bf1\u60d1\u3002 \u3000\u3000\u8fd9\u662f\u9752\u51e4\u4ece\u672a\u5531\u51fa\u8fc7\u7684\u6b4c\u58f0\uff0c\u9761\u9761\u4e4b\u97f3\uff0c\u5374\u542c\u5f97\u4eba\u60ca\u5fc3\u52a8\u9b44\u3002 \u3000\u3000\u573a\u4e2d\u542c\u4f17\u6709\u798f\u4e86\uff0c\u9752\u98ce\u5c55\u73b0\u4e86\u53e6\u4e00\u79cd\u8ff7\u4eba\u7684\u97f3\u97f5\u2026\u2026\u3002 \u3000\u3000\u4e56\u4e56\uff0c\u542c\u5f97\u4eba\u53d1\u51fa\u4e86\u6c89\u91cd\u7684\u547c\u606f\u4e4b\u58f0\u3002 \u3000\u3000\u6c5f\u67ab\u957f\u957f\u5401\u4e00\u53e3\u6c14\uff0c\u5fd6\u9053\uff1a\u201c\u8fd9\u7b49\u8680\u9aa8\u52a8\u5fc3\u7684\u9b54\u97f3\uff0c\u4e00\u822c\u4eba\u5982\u4f55\u80fd\u627f\u5f97\u4e86\u2026\u2026\u3002\u201d \u3000\u3000\u53ea\u542c\u6e05\u97f3\u9ad8\u62d4\uff0c\u5982\u4e0a\u4e91\u9704\uff0c\u5982\u75f4\u5982\u9189\u7684\u89c2\u4f17\uff0c\u7a81\u7136\u6e05\u9192\u8fc7\u6765\u3002 \u3000\u3000\u4f59\u97f3\u7ed5\u8033\u4e2d\uff0c\u9752\u51e4\u5df2\u8f6c\u8eab\u4e0b\u53f0\u3002 \u3000\u3000\u66b4\u8d77\u7684\u5982\u96f7\u7684\u638c\u58f0\uff0c\u5386\u4e45\u4e0d\u7edd\u3002 \u3000\u3000\u9ad8\u5927\u6597\u7f13\u6b65\u884c\u5165\u5305\u53a2\uff0c\u76ee\u5149\u5230\u5904\uff0c\u7acb\u523b\u76b1\u8d77\u4e86\u7709\u5934\u3002 \u3000\u3000\u4e0d\u50cf\u8bdd\u5440\uff01\u4e24\u4e2a\u7167\u987e\u8d35\u5bbe\u7684\u5973\u5a62\uff0c\u53cc\u987b\u7eef\u7ea2\uff0c\u51e0\u4e4e\u662f\u762b\u4f0f\u5728\u5ba2\u4eba\u7684\u8eab\u4e0a\u3002 \u3000\u3000\u9ad8\u5927\u6597\u91cd\u91cd\u7684\u54b3\u4e86\u4e00\u58f0\uff0c\u4e8c\u5973\u5982\u68a6\u521d\u9192\uff0c\u6025\u6025\u7ad9\u76f4\u8eab\u5b50\u3002 \u3000\u3000\u201c\u4e0b\u53bb\u5427\uff01\u201d\u9ad8\u5927\u6597\u5e76\u672a\u8d23\u5907\u4e8c\u5973\uff0c\u9752\u51e4\u90a3\u4e00\u9635\u9707\u98a4\u4eba\u5fc3\u7684\u6b4c\u58f0\uff0c\u80fd\u6709\u591a\u5c11\u4eba\u628a\u6301\u5f97\u4f4f\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u5947\u602a\u7684\u662f\u6c5f\u67ab\u7adf\u7136\u4f1a\u82e5\u65e0\u5176\u4e8b\uff0c\u8fd9\u4e2a\u4f3c\u4e4e\u662f\u5b8c\u5168\u672a\u53d7\u5230\u9752\u51e4\u90a3\u52a8\u4eba\u7684\u6b4c\u58f0\u4fb5\u6270\uff0c\u4e8c\u5973\u5728\u6000\uff0c\u7adf\u7136\u80fd\u65e0\u52a8\u4e8e\u8877\u3002 \u3000\u3000\u9ad8\u5927\u6597\u6df1\u77e5\u9752\u51e4\u90a3\u6b4c\u58f0\u4e2d\u900f\u51fa\u7684\u52a8\u4eba\u9b45\u529b\uff0c\u8eab\u4fa7\u5982\u6709\u7f8e\u5973\uff0c\u90a3\u662f\u7edd\u5bf9\u65e0\u6cd5\u63a7\u5236\u7684\u3002 \u3000\u3000\u4e24\u4e2a\u4f8d\u5e94\u7684\u4e2b\u5934\uff0c\u867d\u975e\u5929\u59ff\u56fd\u8272\uff0c\u4f46\u5979\u4eec\u4e5f\u90fd\u662f\u767e\u4e2d\u9009\u4e00\u7684\u7f8e\u5973\uff0c\u5e74\u9f84\u90fd\u5728\u5341\u516d\u516b\u5c81\uff0c\u6b63\u662f\u82b1\u513f\u5410\u854a\u7684\u5e74\u9f84\u3002 \u3000\u3000\u5341\u516b\u65e0\u4e11\u5973\uff0c\u4f55\u51b5\uff0c\u5979\u4eec\u672c\u5c31\u4e0d\u4e11\u3002 \u3000\u3000\u4f46\u66f4\u4ee4\u9ad8\u5927\u6597\u5403\u60ca\u7684\u662f\uff0c\u9752\u51e4\u53d1\u51fa\u7684\u9761\u9761\u9b54\u97f3\uff0c\u8fd8\u5bf9\u7740\u6c5f\u67ab\uff0c\u5341\u4e4b\u516b\u4e5d\u7684\u5a01\u529b\uff0c\u88ab\u5185\u529b\u675f\u97f3\u6210\u7ebf\uff0c\u76f4\u653b\u6c5f\u67ab\uff0c\u5176\u4ed6\u7684\u542c\u4f17\u3001\u5ba2\u4eba\uff0c\u53ea\u662f\u88ab\u8fb9\u7f18\u7684\u88d9\u5e26\u6240\u53ca\uff0c\u4f46\u5df2\u51e0\u4e4e\u95f9\u6210\u5168\u573a\u75af\u72c2\uff0c\u5982\u975e\u9752\u51e4\u90a3\u4e00\u58f0\u9ad8\u62d4\u6e05\u97f3\uff0c\u5982\u96f7\u8d2f\u8033\uff0c\u5524\u9192\u5168\u573a\u89c2\u4f17\u7684\u75f4\u8ff7\uff0c\u73b0\u5728\uff0c\u53ea\u6015\u5df2\u5168\u573a\u5927\u4e71\u2026\u2026\u3002 \u3000\u3000\u4f46\u6b63\u4e3b\u513f\uff0c\u5374\u662f\u5168\u7136\u4e0d\u53d7\u5f71\u54cd\u3002 \u3000\u3000\u6df1\u6df1\u5438\u4e00\u53e3\u6c14\uff0c\u9ad8\u5927\u6597\u62b1\u62f3\u4e00\u793c\uff0c\u9053\uff1a\u201c\u9601\u4e0b\u597d\u5b9a\u529b\u3002\u201d \u3000\u3000\u6c5f\u67ab\u6ca1\u6709\u56de\u7b54\uff0c\u751a\u81f3\u8fde\u770b\u4e5f\u6ca1\u6709\u770b\u9ad8\u5927\u6597\u4e00\u773c\uff0c\u9ad8\u5927\u6597\u81ea\u8fdb\u5165\u8fd9\u95f4\u7279\u5927\u7684\u5305\u53a2\uff0c\u53f1\u9000\u4e8c\u5973\uff0c\u6c5f\u67ab\u597d\u50cf\u5168\u65e0\u611f\u89c9\u4e00\u822c\u3002 \u3000\u3000\u9ad8\u5927\u6597\u6709\u70b9\u706b\u4e86\uff0c\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u670b\u53cb\uff0c\u4f60\u597d\u5927\u7684\u67b6\u5b50\u554a\uff01\u201d \u3000\u3000\u53f3\u624b\u4e00\u4f38\uff0c\u6293\u5411\u6c5f\u67ab\u3002 \u3000\u3000\u76ee\u5149\u53ca\u5904\uff0c\u53d1\u89c9\u6c5f\u67ab\u53cc\u76ee\u5706\u7741\uff0c\u76f4\u76f4\u7684\u76ef\u4f4f\u53f0\u4e0a\u7684\u9752\u51e4\u3002 \u3000\u3000\u539f\u6765\uff0c\u9752\u51e4\u53c8\u6362\u4e86\u4e00\u8eab\u94f6\u7ea2\u8272\u7ee3\u82b1\u7684\u5939\u8884\u7f57\u88d9\uff0c\u91cd\u56de\u5230\u53f0\u4e0a\u732e\u5531\u3002 \u3000\u3000\u9ad8\u5927\u6597\u53f3\u624b\u5df2\u7136\u5c06\u89e6\u53ca\u6c5f\u67ab\uff0c\u7a81\u7136\u505c\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u56e0\u4e3a\uff0c\u4ed6\u53d1\u89c9\u4e86\u9752\u51e4\u4e24\u9053\u660e\u4eae\u7684\u773c\u795e\uff0c\u4e5f\u6b63\u77a7\u5411\u8fd9\u91cc\u3002 \u3000\u3000\u4e24\u4eba\u56db\u76ee\u76f8\u89e6\uff0c\u5982\u78c1\u5438\u94c1\u3002 \u3000\u3000\u6c5f\u67ab\u53cc\u76ee\u53d1\u76f4\uff0c\u77aa\u5f97\u53c8\u5927\u53c8\u5706\uff0c\u4f3c\u662f\u4e2d\u4e86\u90aa\u822c\u3002 \u3000\u3000\u9752\u98ce\u5374\u662f\u5fae\u7b11\u5982\u82b1\uff0c\u8138\u4e0a\u6d0b\u6ea2\u7740\u65e0\u9650\u6b22\u6109\u795e\u60c5\u3002 \u3000\u3000\u201c\u8fd9\u5c0f\u5b50\uff0c\u662f\u600e\u4e48\u56de\u4e8b\u2026\u2026\u201d\u9ad8\u5927\u6597\u770b\u7740\u6c5f\u67ab\u5fc3\u4e2d\u6697\u6697\u5fd6\u9053\uff1a\u201c\u53ef\u662f\u5df2\u7ecf\u88ab\u9752\u51e4\u90a3\u4f20\u6765\u7684\u9b54\u97f3\uff0c\u52fe\u53bb\u4e86\u9b42\u9b44\uff0c\u65e9\u5df2\u5931\u53bb\u4e86\u63a7\u5236\u81ea\u5df1\u7684\u80fd\u529b\u3002\u201d \u3000\u3000\u8f6c\u5934\u770b\u53bb\uff0c\u53ea\u89c1\u9752\u51e4\u53f3\u624b\u6325\u52a8\uff0c\u4f3c\u662f\u8981\u9ad8\u5927\u6597\u53bb\u540e\u53f0\u89c1\u5979\u3002 \u3000\u3000\u518d\u770b\u6c5f\u67ab\uff0c\u4ecd\u7136\u662f\u76ee\u77aa\u53e3\u5446\u7684\u671b\u7740\u9752\u51e4\u51fa\u795e\u3002 \u3000\u3000\u9ad8\u5927\u6597\u5fc3\u4e2d\u5fd6\u9053\uff1a\u201c\u539f\u6765\u8fd9\u5c0f\u5b50\u88ab\u9752\u51e4\u59d1\u5a18\u7684\u9b54\u97f3\u62db\u53bb\u4e86\u9b42\u9b44\uff0c\u5f04\u5f97\u4eba\u4e8b\u4e0d\u77e5\uff0c\u6211\u8fd8\u4ee5\u4e3a\u9047\u4e0a\u9ad8\u4eba\u5462\uff1f\u201d \u3000\u3000\u6ca1\u6709\u518d\u62db\u547c\u6c5f\u67ab\uff0c\u9ad8\u5927\u6597\u6084\u7136\u9000\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u9752\u51e4\u5531\u5b8c\u4e86\u6700\u540e\u4e00\u66f2\uff0c\u5ba2\u4eba\u7eb7\u7eb7\u6563\u53bb\uff0c\u9ad8\u5927\u6597\u518d\u56de\u5230\u7279\u5927\u7684\u5305\u53a2\u4e2d\uff0c\u53ea\u89c1\u6c5f\u67ab\u4f0f\u6848\u800c\u7761\uff0c\u4f3c\u662f\u4e0d\u77e5\u9053\u5df2\u7ecf\u6563\u573a\uff0c\u4e0d\u7981\u5fae\u5fae\u4e00\u7b11\uff0c\u6697\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u5012\u6709\u4e00\u526f\u597d\u5fb7\u6027\uff0c\u5982\u75f4\u5982\u9189\uff0c\u5374\u4e5f\u4e0d\u53eb\u4e0d\u95f9\u3002\u201d \u3000\u3000\u884c\u8fd1\u6728\u6848\uff0c\u8f7b\u8f7b\u62cd\u4e86\u6c5f\u67ab\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u5ba2\u5b98\uff0c\u9192\u9192\u5427\uff01\u5df2\u7ecf\u6563\u573a\u4e86\u3002\u201d \u3000\u3000\u6c5f\u67ab\u5982\u68a6\u521d\u9192\u822c\uff0c\u7741\u5f00\u773c\u775b\uff0c\u770b\u770b\u9ad8\u5927\u6597\uff0c\u7ad9\u8d77\u8eab\u5b50\uff0c\u4f38\u4e2a\u61d2\u8170\uff0c\u9053\uff1a\u201c\u597d\u7d2f\u5440\uff01\u597d\u7d2f\u2026\u2026\u201d \u3000\u3000\u9ad8\u5927\u6597\u7b11\u9053\uff1a\u201c\u5ba2\u5b98\uff0c\u9752\u51e4\u59d1\u5a18\u7684\u6b4c\u58f0\u4e0d\u9519\u5427\uff1f\u201d \u3000\u3000\u201c\u597d\uff01\u597d\uff01\u597d\uff01\u201d\u6c5f\u67ab\u8fde\u53eb\u4e86\u4e09\u4e2a\u597d\u5b57\uff0c\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u76f8\u8ddd\u4e86\u6570\u4e08\u8ddd\u79bb\uff0c\u6709\u5982\u96fe\u4e2d\u8d4f\u82b1\uff0c\u65e0\u6cd5\u770b\u5f97\u771f\u5207\uff0c\u8bda\u7136\u662f\u7f8e\u4e2d\u4e0d\u8db3\u3002\u201d \u3000\u3000\u201c\u9601\u4e0b\u53ef\u662f\u60f3\u89c1\u9752\u51e4\u59d1\u5a18\uff1f\u201d \u3000\u3000\u201c\u662f\u554a\uff01\u53ea\u53ef\u60dc\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u7528\u53ef\u60dc\u4e86\u2026\u2026\u201d\u9ad8\u5927\u6597\u8bf4\uff0c\u201c\u6d1b\u9633\u5c45\u5f00\u4e1a\u4ee5\u6765\uff0c\u4f60\u662f\u552f\u4e00\u53ef\u4ee5\u5728\u6b4c\u53f0\u4e0b\u9762\u89c1\u5230\u9752\u51e4\u59d1\u5a18\u7684\u5ba2\u4eba\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u2026\u2026\u201d\u6c5f\u67ab\u832b\u7136\u4e0d\u89e3\u7684\u8bf4\uff0c\u201c\u600e\u4e48\u4f1a\u5462\uff1f\u201d \u3000\u3000\u201c\u4eba\u8d70\u8fd0\uff0c\u9a6c\u8d70\u8198\u554a\uff01\u4f60\u4e0d\u4f46\u53ef\u4ee5\u89c1\u5230\u9752\u51e4\u59d1\u5a18\u2026\u2026\u201d\u9ad8\u5927\u6597\u8bf4\uff0c\u201c\u800c\u4e14\u9752\u51e4\u59d1\u5a18\u8fd8\u8981\u8bf7\u4f60\u8001\u5144\u559d\u4e00\u676f\uff01\u201d \u3000\u3000\u201c\u8fd9\u771f\u662f\u53d7\u5ba0\u82e5\u60ca\u4e86\u2026\u2026\u201d\u6c5f\u67ab\u7b11\u4e00\u7b11\u53c8\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u548c\u6211\u5f00\u73a9\u7b11\u5427\uff01\u201d \u3000\u3000\u201c\u5343\u771f\u4e07\u786e\u7684\u4e8b\uff0c\u4e0d\u8fc7\u8981\u7b49\u4eca\u591c\u6563\u573a\u2026\u2026\u201d\u9ad8\u5927\u6597\u9053\uff1a\u201c\u4eca\u591c\u4ea5\u672b\u5b50\u521d\u65f6\u5206\uff0c\u6211\u5728\u6d1b\u9633\u697c\u95e8\u53e3\u7b49\u4f60\u2026\u2026\u201d\u7a81\u7136\u653e\u4f4e\u4e86\u58f0\u97f3\uff1a\u201c\u6b64\u4e8b\u5343\u4e07\u4e0d\u53ef\u5f20\u626c\u51fa\u53bb\uff0c\u56e0\u4e3a\uff0c\u8fd9\u662f\u4ece\u672a\u6709\u8fc7\u7684\u4e8b\u3002\u201d \u3000\u3000\u201c\u653e\u5fc3\uff0c\u653e\u5fc3\uff0c\u5728\u4e0b\u662f\u5b88\u53e3\u5982\u74f6\u2026\u2026\u201d\u6c5f\u67ab\u7531\u6000\u4e2d\u53d6\u51fa\u4e00\u5f20\u94f6\u7968\uff0c\u4ea4\u7ed9\u4e86\u9ad8\u5927\u6597\uff0c\u63a5\u9053\uff1a\u201c\u4e07\u4e8b\u62dc\u6258\uff0c\u533a\u533a\u8c22\u610f\uff0c\u656c\u8bf7\u7b49\u7eb3\u3002\u201d\u8f6c\u8eab\u5927\u6b65\u800c\u53bb\u3002 \u3000\u3000\u76ee\u7779\u6c5f\u67ab\u7684\u80cc\u5f71\u6d88\u5931\uff0c\u9ad8\u5927\u6597\u624d\u5c55\u5f00\u624b\u4e2d\u7684\u94f6\u7968\uff0c\u76ee\u5149\u53ca\u8fdc\uff0c\u4e0d\u7981\u4e00\u5446\uff0c\u4e56\u4e56\uff0c\u662f\u4e09\u5343\u4e24\u7684\u5de8\u989d\u94f6\u7968\uff0c\u5c31\u8fd9\u4e48\u968f\u624b\u8d4f\u7ed9\u4eba\u4e86\u3002 \u3000\u3000\u5f53\u771f\u662f\u4e00\u63b7\u5343\u91d1\uff0c\u9762\u65e0\u541d\u8272\uff0c\u8fd9\u5c0f\u5b50\u771f\u662f\u8c6a\u60c5\u4e07\u4e08\uff0c\u4e00\u573a\u6b4c\u542c\u4e86\u516b\u5343\u4e24\u94f6\u5b50\uff0c\u52ff\u602a\u8fde\u9752\u51e4\u59d1\u5a18\u4e5f\u5bf9\u4ed6\u53e6\u773c\u770b\u5f85\u4e86\u3002 \u3000\u3000\u94b1\u82b1\u5728\u5200\u53e3\u4e0a\uff0c\u4f7f\u9ad8\u5927\u6597\u4e5f\u4e0d\u81ea\u89c9\u4e2d\u5bf9\u6c5f\u67ab\u751f\u51fa\u4e86\u4e00\u79cd\u656c\u670d\u4e4b\u5fc3\u3002 \u3000\u3000\u4e0d\u8bba\u9ad8\u5927\u6597\u4e00\u8eab\u6b66\u529f\u5982\u4f55\u7cbe\u6e5b\uff0c\u4e09\u5343\u8d4f\u94f6\uff0c\u5728\u9ad8\u5927\u6597\u662f\u505a\u68a6\u4e5f\u4e0d\u60f3\u5230\u7684\u4e8b\u60c5\u3002 \u3000\u3000\u6c5f\u67ab\u79bb\u5f00\u4e86\u6d1b\u9633\u5c45\uff0c\u627e\u4e00\u5ea7\u5f88\u5c0f\u7684\u5ba2\u6808\uff0c\u8981\u4e86\u4e00\u4e2a\u623f\u95f4\uff0c\u9759\u5750\u8c03\u606f\uff0c\u4ed6\u8981\u517b\u597d\u7cbe\u795e\uff0c\u5bf9\u4ed8\u9752\u51e4\u3002 \u3000\u3000\u4ed6\u6ca1\u6709\u592a\u591a\u7684\u65f6\u95f4\uff0c\u6295\u6ce8\u5728\u9752\u51e4\u7684\u8eab\u4e0a\uff0c\u4eca\u591c\u4e00\u4f1a\uff0c\u4e00\u5b9a\u8981\u5206\u51fa\u4e2a\u80dc\u8d1f\u51fa\u6765\uff0c\u8fd9\u662f\u5173\u952e\u6027\u7684\u4e00\u573a\u8f83\u91cf\uff0c\u4e00\u65e6\u843d\u8d25\uff0c\u4ed6\u5728\u6d1b\u9633\u5c45\u7684\u82e6\u5fc3\u5e03\u7f72\uff0c\u5f88\u53ef\u80fd\u5f7b\u5e95\u74e6\u89e3\u3002 \u3000\u3000\u6c5f\u67ab\u7684\u4e3a\u4eba\u662f\u51b7\u5389\u7684\uff0c\u4ed6\u662f\u6b66\u6797\u4e2d\u51e0\u4f4d\u6700\u6709\u6210\u5c31\u7684\u9ad8\u4eba\u5168\u529b\u57f9\u517b\u7684\u4eba\u624d\uff0c\u4e00\u8eab\u517c\u5f97\u4e86\u6570\u5bb6\u4e4b\u957f\uff0c\u4ed6\u4e5f\u62c5\u8d1f\u4e86\u6c5f\u6e56\u4e2d\u4eba\u4ece\u672a\u6709\u8fc7\u7684\u8270\u82e6\u4e4b\u8d23\uff0c\u5728\u4e07\u91cc\u9634\u973e\u4e2d\u627e\u51fa\u4e00\u6761\u5149\u660e\u4e4b\u8def\uff0c\u4ee5\u6062\u590d\u6b66\u6797\u751f\u673a\u2026\u2026\u3002 \u3000\u3000\u4ed6\u51b7\u9759\u3001\u575a\u6bc5\u3001\u6c89\u7740\u3001\u9510\u5229\uff0c\u6ca1\u6709\u4efb\u4f55\u95e8\u89c4\u3001\u6212\u5f8b\u7684\u7ea6\u675f\uff0c\u6709\u5929\u4f7f\u4e4b\u5fc3\uff0c\u7528\u9b54\u9b3c\u624b\u6cd5\uff0c\u4ee5\u6bd2\u653b\u6bd2\uff0c\u4ee5\u633d\u6551\u6c5f\u6e56\u5927\u52ab\u3002 \u3000\u3000\u4ea5\u672b\u65f6\u5206\uff0c\u6c5f\u67ab\u5982\u7ea6\u8d76\u5230\u6d1b\u9633\u697c\u5916\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u6d1b\u9633\u5c45\u7684\u8d4c\u573a\uff0c\u4ecd\u7136\u706f\u706b\u8f89\u714c\uff0c\u996d\u83dc\u90e8\u4ecd\u6709\u4eba\u591c\u996e\u672a\u6563\uff0c\u6700\u70ed\u95f9\u7684\u6d1b\u9633\u697c\u5374\u5df2\u706f\u706b\u9611\u73ca\u3002 \u3000\u3000\u9ad8\u5927\u6597\u7ad9\u5728\u697c\u5916\u4e00\u682a\u82b1\u6811\u4e0b\uff0c\u4e00\u89c1\u6c5f\u67ab\uff0c\u7acb\u523b\u5feb\u6b65\u8fce\u4e0a\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u8ddf\u6211\u6765\uff01\u201d \u3000\u3000\u6c5f\u67ab\u70b9\u70b9\u5934\uff0c\u7d27\u968f\u5728\u9ad8\u5927\u6597\u7684\u8eab\u540e\u3002 \u3000\u3000\u9ad8\u5927\u6597\u8d70\u7684\u5f88\u8c28\u614e\uff0c\u4e00\u76f4\u5728\u9634\u6697\u7684\u6811\u4e1b\u4e2d\u7a7f\u884c\u3002 \u3000\u3000\u6c5f\u67ab\u5fc3\u4e2d\u6697\u6697\u5fd6\u9053\uff1a\u201c\u770b\u6765\u9752\u51e4\u679c\u7136\u662f\u53e6\u5916\u4e00\u80a1\u52bf\u529b\u7684\u9996\u8111\uff0c\u548c\u56db\u5b63\u82b1\u5a62\uff0c\u4e0d\u662f\u540c\u51fa\u4e00\u7cfb\u3002\u201d \u3000\u3000\u9ad8\u5927\u6597\u505c\u4e0b\u811a\u6b65\u65f6\uff0c\u5df2\u5230\u4e86\u6816\u51e4\u697c\u524d\u3002 \u3000\u3000\u4e00\u4e2a\u8eab\u5168\u9ed1\u8863\u7684\u4e2d\u5e74\u5987\u4eba\uff0c\u65e9\u5df2\u7b49\u5019\u95e8\u5916\uff0c\u53ea\u89c1\u5979\u5168\u8eab\u9ed1\u8863\uff0c\u8eb2\u5728\u95e8\u5185\u9634\u6697\u5904\uff0c\u6709\u5982\u843d\u5728\u591c\u8272\u4e2d\uff0c\u4e0d\u8d70\u5230\u8ddf\u524d\u5f88\u96be\u53d1\u89c9\u3002 \u3000\u3000\u201c\u9ad8\u5927\u6597\u2026\u2026\u201d\u9ed1\u8863\u5987\u4eba\u4f4e\u58f0\u8bf4\uff1a\u201c\u9009\u516b\u4e2a\u7cbe\u5e72\u624b\u4e0b\uff0c\u5de1\u884c\u56db\u5468\uff0c\u5341\u4e08\u5185\u4e0d\u51c6\u4efb\u4f55\u4eba\u63a5\u8fd1\uff0c\u8fdd\u4ee4\u8005\u6740\u3002\u201d \u3000\u3000\u201c\u5982\u679c\u662f\u6d1b\u9633\u5c45\u4e2d\u7684\u5de1\u884c\u9556\u624b\u5462\uff1f\u2026\u2026\u201d\u9ad8\u5927\u6597\u95ee\uff1a\u201c\u662f\u4e0d\u662f\u4e5f\u4e0d\u51c6\u4ed6\u4eec\u8fdb\u5165\u5341\u4e08\u4e4b\u5185\uff1f\u201d \u3000\u3000\u201c\u662f\uff01\u5305\u62ec\u9093\u603b\u7ba1\u7684\u97e9\u526f\u603b\u7ba1\uff01\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u2026\u2026\u201d\u9ad8\u5927\u6597\u9762\u6709\u96be\u8272\u3002\u9ed1\u8863\u5987\u4eba\u9053\uff1a\u201c\u8fd9\u6837\u5427\uff01\u5982\u679c\u4ed6\u4eec\u8981\u5f3a\u884c\u63a5\u8fd1\uff0c\u4f60\u81ea\u77e5\u5e94\u4ed8\u4e0d\u4e86\uff0c\u90a3\u5c31\u901a\u62a5\u8fc7\u6765\u3002\u201d \u3000\u3000\u201c\u662f\uff01\u201d\u9ad8\u5927\u6597\u5e94\u4e86\u4e00\u58f0\uff0c\u8f6c\u8eab\u800c\u53bb\u3002 \u3000\u3000\u201c\u8fdb\u6765\u5427\uff01\u201d \u3000\u3000\u9ed1\u8863\u5987\u4eba\u76ee\u5149\u4e00\u63a0\u6c5f\u67ab\uff0c\u8ba9\u5230\u4e00\u4fa7\u3002 \u3000\u3000\u6c5f\u67ab\u8fdb\u5165\u5927\u95e8\uff0c\u5927\u95e8\u7acb\u523b\u63a9\u4e0a\uff0c\u9ed1\u8863\u5987\u4eba\u62a2\u524d\u4e00\u6b65\uff0c\u9053\uff1a\u201c\u6211\u5e26\u8def\u3002\u201d\u5f53\u5148\u884c\u53bb\u3002 \u3000\u3000\u201c\u6709\u52b3\u5927\u59d0\u2026\u2026\u201d\u6c5f\u67ab\u7684\u5634\u5df4\u5f88\u751c\u3002 \u3000\u3000\u9ed1\u8863\u5987\u4eba\u505c\u6b65\u8f6c\u8eab\uff0c\u53cc\u76ee\u76ef\u6ce8\u5728\u6c5f\u67ab\u7684\u8138\u4e0a\uff0c\u6253\u91cf\u4e86\u5f88\u4e45\uff0c\u624d\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u53eb\u6211\u738b\u5b37\uff01\u201d \u3000\u3000\u201c\u662f\uff01\u738b\u5b37\uff0c\u5728\u4e0b\u59d3\u7530\u2026\u2026\u201d\u738b\u5b37\u7b11\u4e00\u7b11\uff0c\u8f6c\u8eab\u5e26\u8def\u3002 \u3000\u3000\u6816\u51e4\u697c\u662f\u5e62\u9752\u7816\u780c\u6210\u7684\u4e8c\u697c\uff0c\u7389\u5a49\u5e26\u6c5f\u67ab\u76f4\u767b\u4e8c\u697c\uff0c\u8fdb\u5165\u4e86\u4e00\u4e2a\u96c5\u81f4\u7684\u5c0f\u5385\u4e2d\u3002 \u3000\u3000\u5385\u4e2d\u53ea\u70b9\u7740\u4e00\u53cc\u7ea2\u70db\uff0c\u706f\u5149\u4e0d\u662f\u5f88\u4eae\uff0c\u4f46\u666f\u7269\u53ef\u89c1\uff0c\u4e00\u5f20\u5c0f\u578b\u7684\u5706\u684c\u4e0a\uff0c\u65e9\u5df2\u6446\u597d\u4e86\u516d\u6837\u7cbe\u81f4\u7684\u5c0f\u83dc\u548c\u4e00\u58f6\u9152\uff0c\u4e24\u526f\u676f\u7b77\uff0c\u6446\u660e\u4e86\u53ea\u6709\u4e24\u4e2a\u4eba\u5bf9\u996e\u5c0f\u914c\u3002 \u3000\u3000\u4f46\u89c1\u5782\u5e18\u542f\u52a8\uff0c\u9752\u51e4\u4e00\u8eab\u7c89\u8884\u957f\u88e4\uff0c\u83b2\u6b65\u59d7\u59d7\u5730\u8d70\u4e86\u51fa\u6765\uff0c\u8f7b\u6325\u7389\u624b\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u738b\u5b37\uff01\u4f60\u9000\u4e0b\u53bb\u5427\uff01\u201d \u3000\u3000\u738b\u5b37\u5e94\u4e86\u4e00\u58f0\uff0c\u8f7b\u6b65\u9000\u51fa\uff0c\u987a\u624b\u5e26\u4e0a\u4e86\u5385\u95e8\u3002 \u3000\u3000\u8fd9\u662f\u9752\u51e4\u8d77\u5c45\u5ba4\uff0c\u8fde\u63a5\u7740\u9752\u51e4\u7684\u5367\u623f\uff0c\u6bd4\u8d77\u6885\u82b1\u7684\u4f4f\u5904\uff0c\u6c14\u6d3e\u5927\u591a\u4e86\uff0c\u7b80\u76f4\u662f\u4e0d\u53ef\u540c\u65e5\u800c\u8bed\u3002 \u3000\u3000\u201c\u516c\u5b50\u8bf7\u5750\u2026\u2026\u201d \u3000\u3000\u9752\u51e4\u4e00\u9762\u4f38\u624b\u8ba9\u5ba2\uff0c\u81ea\u5df1\u5374\u5148\u884c\u5750\u4e0b\u3002 \u3000\u3000\u6c5f\u67ab\u7f13\u7f13\u843d\u5750\uff0c\u4ed6\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u59d1\u5a18\u7684\u53ec\u5bb4\uff0c\u7530\u67d0\u4eba\u611f\u6fc0\u4e0d\u5c3d\u3002\u201d \u3000\u3000\u201c\u7530\u516c\u5b50\u9887\u5177\u5de7\u601d\uff0c\u90a3\u4efd\u52a8\u4eba\u7684\u6c14\u52bf\uff0c\u4e00\u4e0b\u5c31\u5f15\u8d77\u4e86\u8d31\u59be\u7684\u6ce8\u610f\u2026\u2026\u201d\u9752\u51e4\u5fae\u7b11\u7740\u633d\u8d77\u9152\u58f6\uff0c\u5148\u66ff\u81ea\u5df1\u5012\u6ee1\u9152\u676f\uff0c\u624d\u66ff\u6c5f\u67ab\u656c\u9152\uff0c\u7136\u540e\uff0c\u7aef\u8d77\u9152\u676f\u7b11\u9053\uff1a\u201c\u9152\u4e2d\u65e0\u6bd2\uff0c\u7530\u5144\u53ef\u4ee5\u653e\u5fc3\u996e\u7528\uff0c\u8d31\u59be\u5148\u5e72\u4e3a\u656c\u3002\u201d \u3000\u3000\u7389\u676f\u5c31\u5507\uff0c\u4e00\u996e\u800c\u5c3d\u3002 \u3000\u3000\u6c5f\u67ab\u6697\u4e2d\u8fd0\u6c14\u6212\u5907\uff0c\u7b11\u9053\uff1a\u201c\u6211\u7684\u9152\u91cf\u4e0d\u597d\u2026\u2026\u201d \u3000\u3000\u54c1\u4e86\u4e00\u4e0b\u9152\u5473\uff0c\u4e5f\u5e72\u4e86\u4e00\u676f\u3002 \u3000\u3000\u9752\u51e4\u5fae\u5fae\u4e00\u7b11\uff0c\u4e3e\u8d77\u7b77\u5b50\uff0c\u5728\u6bcf\u6837\u83dc\u4e0a\u5403\u4e86\u4e00\u53e3\uff0c\u9053\uff1a\u201c\u83dc\u4e5f\u6ca1\u6709\u95ee\u9898\uff0c\u7530\u516c\u5b50\u53ef\u4ee5\u653e\u5fc3\u98df\u7528\u3002\u201d \u3000\u3000\u6c5f\u67ab\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u770b\u6765\uff0c\u5979\u5df2\u7ecf\u5fc3\u5b58\u6212\u5907\uff0c\u628a\u6211\u5f53\u4f5c\u4e86\u654c\u4eba\u770b\u5f85\u3002\u201d \u3000\u3000\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u59d1\u5a18\u76db\u60c5\u9080\u7ea6\uff0c\u5728\u4e0b\u5e78\u4f55\u5982\u4e4b\uff0c\u600e\u6562\u5fc3\u5b58\u7591\u8651\u3002\u201d \u3000\u3000\u53cc\u76ee\u76ef\u6ce8\u5728\u9752\u51e4\u7684\u8138\u4e0a\u6253\u91cf\u3002 \u3000 \u7b2c \u516d \u56de\u3000\u9752\u51e4\u697c\u76df\u59fb\u7ed3\u60a3\u96be \u3000 \u3000\u3000\u8fd9\u65f6\uff0c\u5979\u5df2\u5378\u53bb\u6d53\u5986\uff0c\u8102\u7c89\u672a\u65bd\uff0c\u5b8c\u5168\u662f\u672c\u6765\u7684\u9762\u76ee\uff0c\u79c0\u4e3d\u8f6e\u5ed3\uff0c\u5ae9\u7ea2\u808c\u80a4\uff0c\u8bba\u59ff\u8272\u7edd\u4e0d\u5728\u6885\u82b1\u4e4b\u4e0b\uff0c\u53ea\u4e0d\u8fc7\u5979\u7684\u795e\u60c5\u542b\u8574\u4e86\u4e00\u80a1\u51cc\u5386\u7684\u6c14\u52bf\uff0c\u53cc\u76ee\u4e2d\u795e\u5149\u6e05\u6f88\uff0c\u5982\u5229\u5203\u4e00\u822c\uff0c\u4f3c\u662f\u8981\u900f\u4eba\u5fc3\u80f8\u3002 \u3000\u3000\u9752\u51e4\u6ca1\u6709\u8eb2\u907f\u6c5f\u67ab\u7684\u773c\u5149\uff0c\u53cd\u800c\u51dd\u795e\u56de\u89c6\uff0c\u4ed4\u7ec6\u7684\u6253\u91cf\u7740\u6c5f\u67ab\u3002 \u3000\u3000\u201c\u9752\u51e4\u59d1\u5a18\u5378\u53bb\u6d53\u5986\uff0c\u53cd\u800c\u500d\u589e\u6e05\u4e3d\uff0c\u7530\u67d0\u4eba\u4eca\u591c\u4e0d\u865a\u6b64\u884c\u3002\u201d \u3000\u3000\u201c\u4f60\u82b1\u4e86\u4e94\u5343\u4e24\u94f6\u5b50\uff0c\u6446\u51fa\u4e86\u5947\u5927\u7684\u67b6\u52bf\uff0c\u4e0d\u5c31\u662f\u8981\u6211\u6ce8\u610f\u5230\u4f60\u5417\u2026\u2026\u201d\u9752\u51e4\u5fae\u7b11\u9053\uff1a \u3000\u3000\u201c\u4e0d\u6562\u6709\u8fdd\u96c5\u610f\uff0c\u7279\u5907\u8584\u9152\uff0c\u9080\u541b\u4e00\u53d9\uff0c\u6b64\u5730\u4e0d\u4f1a\u6709\u4eba\u60ca\u6270\uff0c\u7530\u5144\u6709\u8bdd\uff0c\u4e5f\u53ef\u4ee5\u7545\u6240\u6b32\u8a00\u4e86\u3002\u201d \u3000\u3000\u201c\u51e4\u59d1\u5a18\u8273\u8272\u52a8\u4eba\uff0c\u7530\u67d0\u662f\u5e0c\u671b\u4e00\u4eb2\u82b3\u6cfd\u3002\u201d \u3000\u3000\u201c\u53ea\u662f\u8fd9\u4e00\u70b9\u5c0f\u5c0f\u5fc3\u613f\u5417\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\uff0c\u7530\u67d0\u8fd8\u5e0c\u671b\u80fd\u5f97\u59d1\u5a18\u5782\u987e\uff0c\u4f7f\u5728\u4e0b\u2026\u2026\u201d \u3000\u3000\u4f7f\u5728\u4e0b\u600e\u4e48\u6837\uff1f\u6c5f\u67ab\u6ca1\u6709\u8bf4\u4e0b\u53bb\uff0c\u6709\u4e9b\u8bdd\u70b9\u5230\u4e3a\u6b62\uff0c\u53cd\u800c\u66f4\u5177\u827a\u672f\u3002 \u3000\u3000\u5f53\u7136\u4e5f\u7559\u7ed9\u4e86\u81ea\u5df1\u4e89\u8fa9\u7684\u4f59\u5730\u3002 \u3000\u3000\u9752\u51e4\u8f7b\u8f7b\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u56db\u5144\uff0c\u54b1\u4eec\u5f7c\u6b64\u90fd\u4e0d\u7528\u65bd\u5c55\u5947\u672f\uff0c\u597d\u597d\u7684\u8c08\u8c08\u5982\u4f55\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u5fc3\u4e2d\u5fd6\u9053\uff1a\u201c\u770b\u6765\uff0c\u5979\u5df2\u5b8c\u5168\u6e05\u695a\uff0c\u90a3\u756a\u4ea4\u624b\uff0c\u6211\u867d\u88c5\u4f5c\u4e0d\u652f\uff0c\u4f46\u5df2\u6cc4\u5e95\uff0c\u5012\u4e5f\u4e0d\u7528\u88c5\u4f5c\u4e86\uff0c\u8fd9\u4e2a\u9752\u51e4\u679c\u7136\u6bd4\u6885\u82b1\u5389\u5bb3\u3002\u201d \u3000\u3000\u5fc3\u4e2d\u5b9a\u4e86\u4e3b\u610f\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u597d\uff01\u51e4\u59d1\u5a18\u7684\u5429\u5490\uff0c\u5728\u4e0b\u81ea\u5f53\u4ece\u547d\u3002\u201d \u3000\u3000\u201c\u90a3\u5c31\u5148\u8bf4\u8bf4\u4f60\u6765\u6d1b\u9633\u697c\u7684\u76ee\u7684\uff1f\u2026\u2026\u201d\u9752\u51e4\u8bf4\uff0c\u201c\u522b\u627e\u501f\u53e3\uff0c\u662f\u4e3a\u6211\u7f8e\u8272\u6240\u52a8\uff0c\u6211\u8981\u542c\u771f\u8bdd\uff01\u201d \u3000\u3000\u201c\u5982\u662f\u51e4\u59d1\u5a18\u7684\u7f8e\u8272\uff0c\u65e0\u6cd5\u53eb\u6211\u52a8\u5fc3\uff0c\u5728\u4e0b\u4e5f\u4e0d\u4f1a\u82b1\u4e94\u5343\u4e24\u94f6\u5b50\uff0c\u5f15\u8d77\u59d1\u5a18\u7684\u6ce8\u610f\u4e86\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u7b11\u9053\uff1a\u201c\u6211\u5982\u4e0d\u582a\u4e00\u51fb\uff0c\u51e4\u59d1\u5a18\u4e5f\u4e0d\u4f1a\u6709\u4eca\u665a\u7684\u9080\u5bb4\u4e86\u3002\u201d \u3000\u3000\u201c\u6211\u5f88\u60ca\u5f02\u4f60\u7684\u6210\u5c31\u2026\u2026\u201d\u9752\u51e4\u82e6\u7b11\u4e00\u4e0b\u53c8\u9053\uff1a\u201c\u4eca\u65e5\u4e0b\u5348\u90a3\u573a\u5bf9\u6297\uff0c\u7adf\u7136\u662f\u5e73\u5206\u79cb\u8272\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\uff01\u5728\u4e0b\u8d25\u4e86\u3002\u201d \u3000\u3000\u201c\u7528\u4e0d\u7740\u8c26\u865a\uff0c\u80dc\u8d1f\u4e4b\u95f4\uff0c\u6211\u4eec\u5fc3\u4e2d\u90fd\u8be5\u6709\u6570\u2026\u2026\u201d \u3000\u3000\u9752\u51e4\u4e3e\u624b\u7406\u4e00\u7406\u9b13\u8fb9\u7684\u6563\u53d1\uff0c\u63a5\u9053\uff1a\u201c\u4f7f\u6211\u5403\u60ca\u7684\u662f\uff0c\u4f60\u7684\u6b66\u529f\u4fee\u4e3a\uff0c\u4e0d\u4f3c\u4e2d\u539f\u5404\u5927\u95e8\u6d3e\u7684\u8def\u6570\uff0c\u662f\u4e00\u79cd\u90aa\u95e8\u7684\u6b66\u827a\u2026\u2026\u201d \u3000\u3000\u201c\u4e2d\u539f\u6b66\u529f\uff0c\u535a\u5927\u7cbe\u6df1\uff0c\u4f55\u51b5\u6b66\u529f\u4e00\u9053\uff0c\u4e5f\u6ca1\u6709\u6b63\u90aa\u53ef\u5206\u2026\u2026\u201d \u3000\u3000\u9752\u51e4\u7b11\u9053\uff1a\u201c\u8fdd\u5fc3\u4e4b\u8bba\uff0c\u7530\u516c\u5b50\uff0c\u6211\u662f\u5927\u884c\u5bb6\uff0c\u7ec3\u8fc7\u79fb\u9b42\u5927\u6cd5\uff0c\u4e5f\u7cbe\u4e8e\u5185\u5916\u5a9a\u672f\uff0c\u4f60\u5fc3\u4e2d\u660e\u767d\uff0c\u4e00\u4e2a\u4eba\u7684\u97f3\u97f5\u6b4c\u58f0\uff0c\u4e0d\u8bba\u5929\u8d4b\u5982\u4f55\u9ad8\u8d85\uff0c\u4e5f\u65e0\u6cd5\u4f7f\u4eba\u5982\u75f4\u5982\u72c2\uff0c\u5386\u4e45\u4e0d\u8870\uff0c\u6211\u80fd\u5982\u6b64\u7684\u6b4c\u52a8\u957f\u5b89\uff0c\u90a3\u662f\u56e0\u4e3a\u6211\u5728\u6b4c\u58f0\u4e2d\u6df7\u5165\u4e86\u6b66\u529f\uff0c\u662f\u4e00\u79cd\u52a8\u4eba\u7684\u5a9a\u672f\uff0c\u624d\u4f7f\u542c\u4f17\u5982\u996e\u9187\u9152\uff0c\u9189\u8ff7\u5176\u95f4\u3002 \u3000\u3000\u4f60\u80fd\u6297\u62d2\u4e86\u6211\u7684\u97f3\u97f5\u5a9a\u529f\uff0c\u5c82\u662f\u6b63\u6d3e\u7684\u6b66\u529f\uff0c\u5c31\u4e2d\u539f\u5404\u95e8\u6237\u7684\u6b66\u529f\u800c\u8a00\uff0c\u8d31\u59be\u5b9e\u5728\u60f3\u4e0d\u901a\uff0c\u5b83\u51fa\u4e8e\u54ea\u4e2a\u95e8\u6237\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u5fc3\u4e2d\u5927\u5927\u7684\u4e00\u9707\uff0c\u5fd6\u9053\uff1a\u201c\u5979\u4e0d\u4f46\u667a\u7565\u8fc7\u4eba\uff0c\u770b\u8d77\u6765\u6b66\u827a\u4e0a\u7684\u6210\u5c31\uff0c\u5927\u6982\u4e5f\u9ad8\u8fc7\u6885\u82b1\u5f88\u591a\uff0c\u6700\u53ef\u6015\u7684\u662f\u5979\u7adf\u7136\u5bf9\u6c5f\u6e56\u4e0a\u5f62\u52bf\u4e86\u89e3\u5f88\u6df1\uff0c\u770b\u6837\u5b50\u5bf9\u5404\u5927\u95e8\u6d3e\u7684\u6b66\u529f\u4e5f\u5f88\u719f\u77e5\uff0c\u8fd9\u624d\u662f\u771f\u6b63\u53ef\u6015\u7684\u654c\u4eba\uff0c\u4e00\u4e2a\u6587\u6b66\u5168\u624d\u7684\u654c\u4eba\u3002\u201d \u3000\u3000\u201c\u4e2d\u539f\u6b66\u6797\uff0c\u767e\u827a\u6742\u9648\uff0c\u8305\u5c71\u3001\u6392\u6559\u90fd\u7cbe\u901a\u5404\u79cd\u5f02\u672f\uff0c\u56db\u5ddd\u5510\u95e8\u7684\u7528\u6bd2\u6280\u827a\uff0c\u81ea\u6210\u4e00\u5bb6\uff0c\u6e58\u897f\u50f5\u5c38\u95e8\uff0c\u6b66\u529f\u66f4\u662f\u72ec\u7279\uff0c\u5c11\u6797\u4e03\u5341\u4e8c\u79cd\u7edd\u6280\uff0c\u4ea6\u6709\u65c1\u95e8\u6280\u827a\uff0c\u5728\u4e0b\u8fd9\u70b9\u6b66\u529f\uff0c\u5b9e\u4e5f\u7b97\u4e0d\u5f97\u4ec0\u4e48\uff01\u201d \u3000\u3000\u6c5f\u67ab\u8bf4\uff1a\u201c\u51e4\u59d1\u5a18\u65e2\u7136\u719f\u77e5\u4e2d\u539f\u5404\u95e8\u6d3e\u7684\u6b66\u529f\uff0c\u5f53\u77e5\u5728\u4e0b\u6240\u8a00\u975e\u865a\u4e86\u3002\u201d \u3000\u3000\u9752\u51e4\u7b11\u9053\uff1a\u201c\u8305\u5c71\u6d3e\u4ee5\u7b26\u5492\u9a97\u4eba\uff0c\u7528\u836f\u7269\u548c\u969c\u773c\u6cd5\u8f85\u52a9\u5b83\u7684\u5947\u6548\uff0c\u6839\u672c\u7b97\u4e0d\u4e0a\u5947\u672f\u3001\u6b66\u529f\u3002\u4e5f\u8bb8\u4ed6\u4eec\u5728\u89e3\u5384\u9010\u90aa\u4e0a\uff0c\u771f\u6709\u70b9\u795e\u901a\uff0c\u4f46\u90a3\u53ea\u662f\u4e9b\u96d5\u866b\u5c0f\u6280\u800c\u5df2\u3002 \u3000\u3000\u6392\u6559\u5012\u662f\u6709\u4e00\u70b9\u5947\u5f02\u7684\u6280\u827a\uff0c\u4f46\u4e5f\u53d8\u4e0d\u5230\u4e0a\u4e58\u4e4b\u5b66\uff0c\u4f60\u7530\u5144\u8868\u73b0\u51fa\u6765\u7684\u5f02\u672f\uff0c\u5374\u662f\u4e0a\u4e58\u5947\u5b66\u3002\u201d \u3000\u3000\u6c5f\u67ab\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u51e4\u59d1\u5a18\u8bf7\u6211\u53d9\u4f1a\uff0c\u5c31\u662f\u60f3\u8ffd\u6839\u7a76\u5e95\uff0c\u4e86\u89e3\u6211\u7684\u51fa\u8eab\u6765\u5386\u3002\u201d \u3000\u3000\u201c\u8fd9\u662f\u6700\u91cd\u8981\u7684\u539f\u56e0\u4e4b\u2014\u2026\u2026\u201d\u9752\u51e4\u8bf4\uff1a\u201c\u4f60\u662f\u6211\u7b2c\u4e00\u4e2a\u5728\u95fa\u623f\u4e2d\u63a5\u5f85\u7684\u7537\u4eba\uff0c\u8fd9\u91cc\u9762\u81ea\u7136\u542b\u6709\u79c1\u60c5\uff0c\u660e\u767d\u70b9\u8bf4\uff0c\u4f60\u662f\u4e2a\u53eb\u5973\u4eba\u52a8\u5fc3\u7684\u7537\u4eba\uff0c\u5c24\u5176\u662f\u81ea\u8d1f\u624d\u534e\u7684\u5973\u4eba\uff0c\u7f8e\u4e3d\u51fa\u4f17\u7684\u5973\u4eba\uff0c\u9752\u51e4\u59d1\u5a18\u7684\u95fa\u623f\uff0c\u5c82\u662f\u8f7b\u6613\u8ba9\u7537\u4eba\u8e0f\u5165\u7684\u5730\u65b9\u3002\u201d \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u53d7\u6b64\u8d5e\u8bed\uff0c\u5012\u662f\u6709\u6127\u4e86\uff0c\u51e4\u59d1\u5a18\u89c9\u7740\u6211\u6709\u4e00\u8eab\u7cbe\u6df1\u6b66\u529f\uff0c\u5728\u4e0b\u5374\u6ca1\u6709\u8fd9\u79cd\u81ea\u8d1f\u611f\u53d7\u2026\u2026\u201d \u3000\u3000\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u7530\u5144\u662f\u4e0d\u80af\u628a\u5e08\u627f\u51fa\u8eab\u8bf4\u51fa\u6765\u4e86\uff1f\u201d \u3000\u3000\u201c\u51e4\u59d1\u5a18\u80af\u4e0d\u80af\u628a\u5e08\u627f\u6765\u5386\u544a\u8bc9\u6211\u5462\uff1f\u201d\u6c5f\u67ab\u8bf4\uff0c\u201c\u5df2\u6240\u4e0d\u6b32\uff0c\u52ff\u65bd\u4e8e\u4eba\uff0c\u5f53\u7136\uff0c\u5982\u679c\u98ce\u59d1\u5a18\u4ee5\u771f\u8bda\u76f8\u5f85\uff0c\u80af\u628a\u51fa\u8eab\u6765\u5386\u8bf4\u660e\uff0c\u5728\u4e0b\u81ea\u5f53\u4e88\u4ee5\u56de\u62a5\uff0c\u5c3d\u5410\u80f8\u4e2d\u4e4b\u5bc6\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e00\u70b9\u6211\u6050\u6015\u5f88\u96be\u505a\u5230\u2026\u2026\u201d\u9752\u51e4\u9eef\u7136\u4e00\u7b11\u53c8\u9053\uff1a\u201c\u4e0d\u662f\u6211\u4e0d\u613f\uff0c\u800c\u662f\u6211\u4e0d\u80fd\uff0c\u5e0c\u671b\u7530\u5144\u8c05\u89e3\u6211\u3002\u201d \u3000\u3000\u201c\u90a3\u597d\uff0c\u54b1\u4eec\u5c31\u4e0d\u8c08\u8fd9\u4e9b\u607c\u4eba\u7684\u4e8b\uff0c\u4eca\u591c\u53ea\u8c08\u98ce\u6708\u5982\u4f55\uff1f\u201d \u3000\u3000\u201c\u7530\u5144\uff0c\u957f\u591c\u6f2b\u6f2b\uff0c\u98ce\u6708\u65e0\u8fb9\uff0c\u53c8\u4f55\u5fc5\u6025\u5728\u4e00\u65f6\u5462\uff1f\u5c0f\u59b9\u63a5\u5f85\u4f60\u8fdb\u5165\u95fa\u623f\uff0c\u591c\u805a\u5c0f\u996e\uff0c\u5df2\u51c6\u5907\u626b\u69bb\u5f85\u5ba2\uff0c\u4e0d\u8fc7\uff0c\u5c0f\u59b9\u81f3\u5c11\u5e94\u8be5\u5148\u4e86\u89e3\u7530\u5144\u5230\u6b64\u7684\u7528\u5fc3\u4f55\u5728\u2026\u2026\u201d \u3000\u3000\u201c\u51e4\u59d1\u5a18\u60f3\u5f97\u592a\u591a\u4e86\u2026\u2026\u201d\u6c5f\u67ab\u7b11\u9053\uff1a\u201c\u5982\u679c\uff0c\u4f60\u628a\u6211\u770b\u6210\u4e00\u4e2a\u62dc\u5012\u5728\u77f3\u69b4\u88d9\u4e0b\u7684\u5ba2\u4eba\uff0c\u90a3\u5c31\u4e0d\u4f1a\u5982\u6b64\u591a\u7591\u4e86\uff1f\u201d \u3000\u3000\u201c\u6211\u5f88\u60f3\u4f60\u662f\uff01\u53ef\u60dc\u4f60\u4e0d\u662f\u3002\u7530\u5144\uff0c\u5982\u679c\u8bf4\u4f60\u4e3a\u5c0f\u59b9\u7684\u59ff\u8272\u6240\u8ff7\uff0c\u60c5\u4e0d\u81ea\u7981\uff0c\u8fde\u5c0f\u59b9\u81ea\u5df1\u5c31\u4e0d\u76f8\u4fe1\uff0c\u4e60\u8fc7\u5a9a\u672f\u7684\u4eba\uff0c\u514b\u5236\u529b\u4e5f\u7279\u522b\u5f3a\u5927\uff0c\u6240\u4ee5\uff0c\u6211\u8ba4\u4e3a\u7530\u5144\u662f\u6709\u4e3a\u800c\u6765\u3002\u201d \u3000\u3000\u6c5f\u67ab\u5fc3\u5934\u9707\u52a8\uff0c\u9752\u51e4\u601d\u8651\u4e4b\u5468\uff0c\u610f\u5fd7\u4e4b\u5f3a\uff0c\u5b9e\u975e\u5c0f\u53ef\uff0c\u9ad8\u51fa\u6885\u82b1\uff0c\u4e0d\u77e5\u591a\u5c11\u4e86\uff0c\u8fd9\u6837\u4e00\u4e2a\u654c\u624b\uff0c\u5fc5\u8981\u65f6\u65f6\u63d0\u9ad8\u8b66\u89c9\uff0c\u7a0d\u6709\u758f\u5ffd\uff0c\u5c31\u53ef\u4e3a\u5979\u6240\u4e58\u3002 \u3000\u3000\u201c\u51e4\u59d1\u5a18\u7a76\u7adf\u5728\u6000\u7591\u4ec0\u4e48\u5462\uff1f\u201d \u3000\u3000\u201c\u6211\u5c31\u662f\u60f3\u4e0d\u51fa\u6765\uff0c\u624d\u5355\u5200\u76f4\u5165\u5730\u5411\u7530\u5144\u8bf7\u6559\u3002\u201d \u3000\u3000\u201c\u8fd9\u5c31\u5f88\u96be\u7b54\u590d\u4e86\uff0c\u6211\u662f\u4e3a\u59d1\u5a18\u59ff\u8272\u800c\u6765\uff0c\u4f46\u51e4\u59d1\u5a18\u5374\u4e0d\u80af\u76f8\u4fe1\uff0c\u770b\u6765\u805a\u6563\u79bb\u5408\uff0c\u679c\u7136\u6709\u7f18\u5206\u5b58\u5728\uff0c\u6709\u7f18\u76f8\u805a\uff0c\u65e0\u7f18\u81ea\u5206\u3002\u201d \u3000\u3000\u9752\u51e4\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u597d\uff01\u5c31\u7b97\u7530\u5144\u4e3a\u5c0f\u59b9\u59ff\u8272\u6240\u60d1\uff0c\u4e0d\u60dc\u4e00\u63b7\u6570\u5343\u91d1\uff0c\u5f15\u8d77\u5c0f\u59b9\u7684\u6ce8\u610f\uff0c\u5355\u662f\u8fd9\u4efd\u5de7\u601d\uff0c\u5c31\u5f88\u52a8\u4eba\u4e86\uff0c\u5c0f\u59b9\u80fd\u4e0d\u4e0a\u94a9\u5417\uff1f\u5f53\u7136\uff0c\u4f60\u672c\u8eab\u8fd9\u4e2a\u9975\uff0c\u4e5f\u53eb\u5c0f\u59b9\u52a8\u5fc3\uff0c\u73b0\u5728\uff0c\u7530\u5144\u53ef\u4ee5\u8bf4\u51fa\u6765\u4e86\uff0c\u5e0c\u671b\u5c0f\u59b9\u4e3a\u4f60\u4f5c\u4e9b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u8fd9\u4e00\u4e0b\uff0c\u53cd\u628a\u6c5f\u67ab\u95ee\u4f4f\u4e86\uff0c\u6c89\u541f\u4e86\u826f\u4e45\uff0c\u624d\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u51e4\u59d1\u5a18\uff0c\u6709\u4e9b\u4e8b\u8bf4\u51fa\u6765\uff0c\u5c31\u53d8\u5f97\u5f88\u4fd7\u6c14\u4e86\uff0c\u4e00\u4e0b\u5b50\uff0c\u4f1a\u7834\u574f\u4e86\u7f8e\u597d\u7684\u5370\u8c61\u2026\u2026\u201d \u3000\u3000\u9752\u51e4\u63a5\u9053\uff1a\u201c\u770b\u7530\u5144\u884c\u4e8b\u7684\u5927\u80c6\u3001\u79ef\u6781\uff0c\u4e0d\u50cf\u77bb\u524d\u987e\u540e\u7684\u4eba\u554a\uff01\u201d \u3000\u3000\u201c\u8fd9\u4ef6\u4e8b\u5374\u6709\u4e9b\u4e0d\u540c\uff0c\u5982\u679c\u6211\u8bf4\u5e0c\u671b\u4eca\u591c\u7559\u5bbf\u5728\u9752\u51e4\u59d1\u5a18\u7684\u95fa\u623f\uff0c\u662f\u4e0d\u662f\u6709\u4e9b\u5510\u7a81\u4f73\u4eba\uff1f\u201d \u3000\u3000\u201c\u8fd8\u662f\u8bf4\u51fa\u6765\u4e86\uff0c\u7530\u5144\uff0c\u6211\u4e0d\u662f\u540d\u95e8\u95fa\u79c0\uff0c\u5927\u5bb6\u5343\u91d1\uff0c\u4e25\u683c\u70b9\u8bf4\uff0c\u8fde\u826f\u5bb6\u5987\u5973\u4e5f\u7b97\u4e0d\u4e0a\uff0c\u5728\u4e00\u822c\u4eba\u7684\u773c\u4e2d\uff0c\u6211\u53ea\u662f\u4e00\u4eba\u5356\u5531\u7684\u6b4c\u5993\u7f62\u4e86\uff0c\u6240\u4ee5\uff0c\u8fd9\u79cd\u8bdd\uff0c\u6211\u5df2\u7ecf\u542c\u8fc7\u5f88\u591a\u6b21\u4e86\uff0c\u5012\u4e5f\u4e0d\u89c9\u5510\u7a81\u3002\u201d \u3000\u3000\u597d\u4e00\u5f20\u5229\u53e3\uff0c\u6c5f\u67ab\u5fc3\u4e2d\u6697\u6697\u5fd6\u9053\uff1a\u201c\u5979\u8bf4\u4e86\u534a\u5929\uff0c\u8fd8\u662f\u6ca1\u6709\u6b63\u9762\u56de\u7b54\u3002\u201d \u3000\u3000\u201c\u90a3\u662f\u540c\u4e86\u610f\uff1f\u8fd8\u662f\u5a49\u62d2\u5728\u4e0b\u7684\u8981\u6c42\u5462\uff1f\u201d \u3000\u3000\u201c\u7530\u5144\u521a\u624d\u53ea\u662f\u6253\u4e2a\u6bd4\u65b9\uff0c\u96be\u9053\u662f\u771f\u60c5\u6d41\u9732\uff0c\u8a00\u51fa\u80ba\u8151\u4e48\uff1f\u201d \u3000\u3000\u201c\u5982\u679c\u662f\u771f\u5fc3\u4e4b\u8a00\u5462\uff1f\u201d\u6c5f\u67ab\u611f\u89c9\u5230\uff0c\u5bf9\u7b54\u4e4b\u95f4\uff0c\u81ea\u5df1\u5df2\u88ab\u8feb\u843d\u5728\u4e0b\u98ce\u3002 \u3000\u3000\u201c\u8fd9\u4e0d\u80fd\u5982\u679c\uff0c\u7530\u5144\u5982\u771f\u4e3a\u5c0f\u59b9\u7684\u59ff\u8272\u6240\u9189\uff0c\u8fde\u4e00\u53e5\u80af\u5b9a\u7684\u8bdd\u4e5f\u4e0d\u80af\u8bf4\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u6697\u9053\uff1a\u201c\u597d\u5389\u5bb3\u7684\u4e2b\u5934\uff0c\u6b65\u6b65\u8feb\u903c\uff0c\u786c\u8981\u6211\u51fa\u4e11\uff0c\u53ef\u60dc\uff0c\u4f60\u7b97\u9519\u4e86\u4e00\u4ef6\u4e8b\uff0c\u6211\u6c5f\u67ab\u4e0d\u662f\u53ea\u8981\u9762\u5b50\u7684\u4eba\uff0c\u8fd9\u79cd\u4e2d\u539f\u6b66\u6797\u7684\u4f20\u7edf\uff0c\u5728\u6211\u6c5f\u67d0\u4eba\u7684\u773c\u91cc\uff0c\u53ef\u4e0d\u662f\u4ec0\u4e48\u4e86\u4e0d\u5f97\u7684\u5927\u4e8b\u3002\u201d\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u5728\u4e0b\u662f\u8bda\u5fc3\u8bda\u610f\uff0c\u4f01\u6c42\u4e00\u4eb2\u82b3\u6cfd\uff0c\u4e0d\u77e5\u51e4\u59d1\u5a18\u662f\u5426\u613f\u4ea4\u4e0b\u611a\uff1f\u201d \u3000\u3000\u201c\u5927\u8c26\u865a\u4e86\u2026\u2026\u201d\u9752\u51e4\u7b11\u5bb9\u5982\u82b1\u5730\u8bf4\uff0c\u201c\u4f60\u662f\u4e0b\u611a\uff0c\u6211\u5c31\u4e0d\u77e5\u9053\u54ea\u91cc\u80fd\u627e\u5230\u4e00\u4e2a\u4e0a\u667a\u7684\u7537\u4eba\u4e86\u3002\u201d \u3000\u3000\u201c\u90a3\u662f\u8bf4\uff0c\u51e4\u59d1\u5a18\u540c\u610f\u4e86\uff1f\u201d \u3000\u3000\u201c\u540c\u610f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u5fc3\u4e2d\u51b7\u7b11\uff0c\u53e3\u91cc\u5374\u8bf4\u9053\uff1a\u201c\u8ba9\u5728\u4e0b\u7559\u5bbf\u4e00\u5bb5\uff1f\u201d \u3000\u3000\u201c\u5509\uff01\u80fd\u4e0e\u541b\u5171\u6e21\u826f\u591c\uff0c\u5b9e\u8d31\u59be\u5fc3\u4e2d\u4e4b\u613f\uff0c\u53ea\u662f\u521d\u5ea6\u4f1a\u6664\uff0c\u5c31\u7559\u541b\u6df1\u95fa\uff0c\u59be\u8eab\u4e0e\u5993\u4f55\u5f02\uff1f\u7530\u5144\uff0c\u4e5f\u4e0d\u5fcd\u5fc3\u8ba9\u5c0f\u59b9\u6709\u6b64\u9057\u61be\u5427\uff01\u201d \u3000\u3000\u201c\u5bf9\uff01\u8bf4\u7684\u6709\u7406\uff0c\u5728\u4e0b\u5931\u6001\u4e4b\u5904\uff0c\u8fd8\u671b\u59d1\u5a18\u5927\u5ea6\u5305\u5bb9\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u89c1\u98ce\u8f6c\u8235\u5730\u8bf4\uff0c\u201c\u4eca\u591c\uff0c\u5728\u4e0b\u5c31\u9501\u4f4f\u5fc3\u5b69\u610f\u9a6c\uff0c\u676f\u9152\u8c08\u5fc3\u2026\u2026\u201d \u3000\u3000\u201c\u9501\u4f4f\u5fc3\u733f\u610f\u9a6c\uff01\u4f60\u8bf4\u7684\u597d\u53ef\u51b7\u554a\uff01\u8ba9\u5c0f\u59b9\u542c\u5f97\u5fc3\u75bc\u3002\u201d \u3000\u3000\u6c5f\u67ab\u5fc3\u4e2d\u5fae\u751f\u6012\u610f\uff0c\u4f46\u8138\u4e0a\u5374\u8868\u73b0\u4e00\u526f\u65e0\u53ef\u5948\u4f55\u7684\u795e\u60c5\uff0c\u9053\uff1a\u201c\u843d\u82b1\u6709\u610f\uff0c\u6d41\u6c34\u65e0\u60c5\uff0c\u5728\u4e0b\u603b\u4e0d\u80fd\u5f3a\u4eba\u6240\u96be\u5427\uff01\u201d \u3000\u3000\u5ae3\u7136\u4e00\u7b11\uff0c\u9752\u51e4\u7684\u8138\u4e0a\uff0c\u6cdb\u73b0\u51fa\u4e07\u79cd\u98ce\u60c5\uff0c\u5a9a\u773c\u5982\u4e1d\u7684\u8bf4\u9053\uff1a\u201c\u7537\u5973\u6709\u522b\uff0c\u7530\u5144\u96be\u9053\u8981\u5c0f\u59b9\u5f3a\u4f60\u4e0d\u6210\uff1f\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u5f88\u9732\u9aa8\uff0c\u914d\u5408\u5979\u7751\u4e0a\u7684\u8bf1\u4eba\u98ce\u60c5\uff0c\u53ea\u77a7\u7684\u6c5f\u67ab\u5fc3\u4e2d\u6026\u7136\u800c\u52a8\u3002 \u3000\u3000\u597d\u5bb6\u4f19\uff01\u8bf4\u8fc7\u5f7c\u6b64\u4e0d\u7528\u5a9a\u672f\u7684\uff0c\u4f60\u5012\u5148\u80cc\u5f03\u8bfa\u8a00\u4e86\u3002 \u3000\u3000\u6c5f\u67ab\u5fc3\u4e2d\u5fd6\u601d\uff0c\u4f46\u4eba\u5374\u5df2\u4e0d\u7531\u81ea\u4e3b\u7684\u7ad9\u4e86\u8d77\u6765\uff0c\u5927\u6709\u7acb\u523b\u6251\u5411\u9752\u51e4\u7684\u610f\u613f\u3002 \u3000\u3000\u9752\u51e4\u7684\u7b11\u5bb9\u66f4\u5a07\u5a9a\u4e86\uff0c\u8138\u513f\u7eef\u7ea2\uff0c\u53cc\u76ee\u4e2d\u55b7\u5c04\u51fa\u4e07\u7f15\u60c5\u7130\uff0c\u5f3a\u70c8\u7684\u5438\u5f15\u7740\u6c5f\u67ab\u3002 \u3000\u3000\u6c5f\u67ab\u5fc3\u4e2d\u660e\u767d\uff0c\u53ea\u8981\u6309\u4e0d\u4f4f\u8fd9\u4e00\u523b\u7684\u51b2\u52a8\uff0c\u5c31\u5c06\u6ca6\u5165\u9b54\u52ab\uff0c\u88ab\u9752\u51e4\u63a7\u5236\uff0c\u518d\u60f3\u53cd\u51fb\uff0c\u90a3\u5c31\u56f0\u96be\u4e07\u5206\u4e86\u3002 \u3000\u3000\u56e0\u4e3a\uff0c\u90a3\u65f6\uff0c\u6c5f\u67ab\u5c06\u5931\u53bb\u81ea\u6211\u610f\u5fd7\u3002 \u3000\u3000\u6697\u4e2d\u5438\u4e00\u53e3\u6c14\uff0c\u4fbf\u7ad9\u8d77\u7684\u8eab\u5b50\uff0c\u53c8\u7f13\u7f13\u5730\u5750\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u9752\u51e4\u9707\u60ca\u6781\u4e86\uff0c\u5979\u653b\u5176\u4e0d\u5907\uff0c\u7a81\u5c55\u5a9a\u672f\uff0c\u60f3\u5148\u593a\u5176\u795e\uff0c\u518d\u70b9\u4ed6\u7684\u7a74\u9053\uff0c\u6162\u6162\u7684\u903c\u4ed6\u5410\u9732\u771f\u6b63\u5fc3\u610f\u2026\u2026\u3002 \u3000\u3000\u5979\u5fc3\u4e2d\u4e5f\u5b9e\u5728\u559c\u6b22\u8fd9\u4e2a\u7537\u4eba\uff0c\u4e0d\u5fcd\u7a81\u4e0b\u6740\u624b\u3002 \u3000\u3000\u5982\u82e5\u6c5f\u67ab\u5728\u53d7\u5236\u5239\u90a3\u95f4\uff0c\u9752\u51e4\u7d27\u968f\u51fa\u624b\uff0c\u6c5f\u67ab\u5c31\u5f88\u96be\u95ea\u907f\u4e86\u3002 \u3000\u3000\u5c31\u662f\u8fd9\u4e00\u5239\u90a3\u72b9\u8c6b\uff0c\u7ed9\u4e86\u6c5f\u67ab\u673a\u4f1a\u3002 \u3000\u3000\u9752\u51e4\u7edd\u672a\u60f3\u5230\uff0c\u6c5f\u67ab\u7adf\u7136\u80fd\u60ac\u5d16\u52d2\u9a6c\uff0c\u5728\u5c8c\u5c8c\u53ef\u5371\u4e2d\uff0c\u63a7\u5236\u4f4f\u81ea\u5df1\u3002 \u3000\u3000\u5979\u51dd\u795e\u6212\u5907\uff0c\u51c6\u5907\u8fce\u63a5\u6c5f\u67ab\u7684\u53cd\u51fb\u3002 \u3000\u3000\u4f46\u6c5f\u67ab\u5e76\u672a\u53cd\u51fb\uff0c\u957f\u957f\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u51e4\u59d1\u5a18\uff0c\u5728\u4e0b\u5df2\u60c5\u7518\u8ba4\u8f93\uff0c\u4f60\u53c8\u4f55\u5fc5\u80cc\u5f03\u8fdb\u8a00\uff0c\u7a81\u65bd\u8ff7\u9b42\u5927\u6cd5\u5462\uff1f\u201d \u3000\u3000\u201c\u5c0f\u59b9\u662f\u65e0\u610f\u7684\u2026\u2026\u201d\u9752\u51e4\u5e7d\u5e7d\u5730\u8bf4\u9053\uff1a\u201c\u6211\u53ea\u662f\u6709\u4e9b\u5fcd\u8010\u4e0d\u4f4f\u90a3\u80a1\u7a81\u5982\u5176\u6765\u7684\u51b2\u52a8\uff0c\u60f3\u518d\u8bd5\u4e00\u6b21\u6c5f\u5144\u7684\u5b9a\u529b\u3002\u201d \u3000\u3000\u201c\u73b0\u5728\uff0c\u51e4\u59d1\u5a18\u662f\u5426\u6ee1\u610f\u4e86\u5462\uff1f\u201d \u3000\u3000\u201c\u5509\uff01\u5c0f\u59b9\u4f69\u670d\u6781\u4e86\uff0c\u4e5f\u613f\u610f\u4e3a\u8fd9\u6b21\u80cc\u5f03\u8bfa\u8a00\u4ed8\u51fa\u4ee3\u4ef7\u2026\u2026\u201d \u3000\u3000\u201c\u55ef\uff01\u51e4\u59d1\u5a18\u51c6\u5907\u5982\u4f55\u8865\u507f\u5462\uff1f\u201d \u3000\u3000\u201c\u7530\u5144\u60f3\u8981\u7684\uff0c\u5c0f\u59b9\u90fd\u4f1a\u7b54\u5e94\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u8fd9\u4e2b\u5934\u7684\u72e1\u8bc8\uff0c\u8d85\u8fc7\u6885\u82b1\u5341\u500d\uff0c\u5f53\u771f\u5f97\u6b65\u6b65\u63d0\u9632\u624d\u6210\uff01\u201d\u53e3\u91cc\u5374\u8bf4\u9053\uff1a\u201c\u51e4\u59d1\u5a18\u7684\u610f\u601d\u662f\u51c6\u8bb8\u6211\u7559\u5bbf\u5728\u6b64\u4e86\uff1f\u201d \u3000\u3000\u201c\u5982\u82e5\u8fd9\u662f\u7530\u5144\u7684\u552f\u4e00\u8981\u6c42\uff0c\u5c0f\u59b9\u81ea\u5f53\u52c9\u4e3a\u5176\u96be\uff0c\u7559\u541b\u4e00\u5bb5\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u4ed4\u7ec6\u7684\u89c2\u5bdf\uff0c\u7adf\u7136\u53d1\u73b0\u9752\u51e4\u7709\u5b87\u95f4\u6d6e\u8d77\u4e00\u7247\u5e7d\u6028\uff0c\u6e05\u6f88\u5982\u6c34\u7684\u76ee\u5149\u4e2d\uff0c\u9690\u542b\u7740\u754f\u60e7\u2026\u2026\u3002 \u3000\u3000\u8fd9\u4e0d\u662f\u5a9a\u672f\uff0c\u4e5f\u4e0d\u662f\u88c5\u4f5c\uff0c\u800c\u662f\u5185\u5fc3\u4e2d\u6d41\u73b0\u7684\u771f\u6b63\u611f\u53d7\u3002 \u3000\u3000\u8fd9\u4f4d\u540d\u52a8\u957f\u5b89\u7684\u6b4c\u59ec\uff0c\u6b63\u5728\u4e00\u79cd\u75db\u82e6\u4e2d\u6323\u624e\u2026\u2026\u3002 \u3000\u3000\u867d\u7136\uff0c\u5979\u6781\u529b\u63a7\u5236\u7740\u81ea\u5df1\uff0c\u4e0d\u8ba9\u8fd9\u4e9b\u5185\u5fc3\u7684\u611f\u53d7\uff0c\u6d41\u9732\u51fa\u6765\u2026\u2026\u3002 \u3000\u3000\u4f46\u5979\u9690\u85cf\u7684\u4e0d\u591f\u5b8c\u7f8e\uff0c\u4ecd\u88ab\u6c5f\u67ab\u9510\u5229\u7684\u89c2\u5bdf\u529b\u53d1\u89c9\u4e86\u5b83\u3002 \u3000\u3000\u201c\u59d1\u5a18\uff0c\u5982\u786e\u6709\u788d\u96be\u4e4b\u5904\uff0c\u5728\u4e0b\u7edd\u4e0d\u4f1a\u501f\u673a\u8981\u631f\uff0c\u80fd\u4e0d\u80fd\u544a\u8bc9\u6211\u4f60\u7684\u82e6\u54c0\uff1f\u5728\u4e0b\u529b\u80fd\u6240\u53ca\uff0c\u5fc5\u5c06\u5168\u529b\u4ee5\u8d74\uff0c\u52a9\u59d1\u5a18\u4e00\u81c2\u4e4b\u529b\u3002\u201d \u3000\u3000\u9752\u51e4\u611f\u89c9\u5230\u662f\u4e00\u79cd\u67d4\u7ec6\u7684\u58f0\u97f3\uff0c\u94bb\u5165\u8033\u4e2d\uff0c\u5fc3\u77e5\u5bf9\u65b9\u65bd\u5c55\u4f20\u97f3\u5165\u5bc6\u4e4b\u672f\uff0c\u5c31\u7b97\u6709\u4eba\u5b88\u5728\u8eab\u4fa7\uff0c\u4e5f\u662f\u65e0\u6cd5\u542c\u5230\uff0c\u5fc3\u4e2d\u5fd6\u6069\uff1a \u3000\u3000\u201c\u8fd9\u4e2a\u4eba\u7cbe\u660e\u7684\u5f88\uff0c\u6211\u5185\u5fc3\u4e2d\u7684\u9690\u5bc6\uff0c\u7adf\u4e5f\u88ab\u4ed6\u770b\u7a7f\uff0c\u4ed6\u662f\u4e2a\u5f3a\u52b2\u7684\u654c\u4eba\uff0c\u4e5f\u662f\u4e2a\u6709\u7528\u7684\u670b\u53cb\u3002\u201d \u3000\u3000\u51dd\u76ee\u671b\u53bb\uff0c\u53ea\u89c1\u6c5f\u67ab\u6ee1\u8138\u5173\u5207\u4e4b\u60c5\uff0c\u76ee\u5149\u6e05\u660e\uff0c\u662f\u4e00\u79cd\u51fa\u81ea\u5185\u5fc3\u4eba\u5173\u7231\uff0c\u679c\u7136\u672a\u501f\u673a\u65bd\u672f\u53cd\u51fb\u3002 \u3000\u3000\u201c\u7530\u5144\uff0c\u6211\u4e0d\u662f\u81ea\u7531\u4e4b\u8eab\uff0c\u738b\u5b37\u3001\u674e\u5b37\u7167\u987e\u6211\u7684\u751f\u6d3b\uff0c\u4e5f\u76d1\u89c6\u6211\u7684\u884c\u52a8\uff0c\u770b\u8d77\u6765\u662f\u8fd9\u5ea7\u51e4\u697c\u7684\u4e3b\u4eba\uff0c\u5176\u5b9e\u5462\uff1f\u6211\u53ea\u662f\u4e00\u53ea\u88ab\u56fd\u5728\u7b3c\u4e2d\u7684\u753b\u7709\u9e1f\u513f\u3002\u201d \u3000\u3000\u5979\u7528\u7684\u4e5f\u662f\u4f20\u97f3\u4e4b\u672f\uff0c\u663e\u7136\u662f\u6015\u4eba\u542c\u5230\u3002 \u3000\u3000\u8fd9\u786e\u8ba9\u6c5f\u67ab\u5927\u5927\u5730\u5403\u4e86\u4e00\u60ca\uff0c\u539f\u60f3\u9752\u51e4\u662f\u9547\u5b88\u4e00\u65b9\u7684\u9996\u8111\u4eba\u7269\uff0c\u6307\u6325\u7fa4\u96c4\uff0c\u5a01\u98ce\u516b\u9762\uff0c\u5374\u6599\u4e0d\u5230\uff0c\u5979\u7adf\u662f\u4e2a\u88ab\u56da\u4e8e\u51e4\u697c\u7684\u56da\u5973\u2026\u2026\uff0c\u4e00\u65f6\u95f4\u5446\u5728\u90a3\u91cc\u3002 \u3000\u3000\u201c\u5f88\u610f\u5916\u5427\uff01\u2026\u2026\u201d\u9752\u51e4\u82e6\u7b11\u4e00\u4e0b\uff0c\u4ecd\u7528\u4f20\u97f3\u4e4b\u672f\uff0c\u9053\uff1a\u201c\u738b\u5b37\u3001\u674e\u5b37\u7684\u6b66\u529f\uff0c\u975e\u540c\u5c0f\u53ef\uff0c\u5979\u4eec\u975e\u5e38\u5fe0\u4e8e\u804c\u8d23\u3002\u5e73\u5e38\u5bf9\u6211\u5012\u4e5f\u656c\u91cd\uff0c\u4f46\u5979\u4eec\u7edd\u4e0d\u5141\u8bb8\u6709\u4eba\u5360\u6709\u6211\u7684\u8eab\u4f53\uff0c\u6211\u82b1\u4e86\u5f88\u591a\u53e3\u820c\uff0c\u624d\u8bf4\u670d\u5979\u4eec\uff0c\u5f15\u4f60\u5230\u8fd9\u91cc\u6765\uff0c\u539f\u51c6\u5907\u4e00\u51fb\u5f97\u624b\uff0c\u6162\u6162\u903c\u95f4\u4f60\u7684\u53e3\u4f9b\uff0c\u60f3\u4e0d\u5230\u4f60\u7684\u529f\u529b\u6df1\u539a\uff0c\u5c24\u80dc\u6211\u4e00\u7b79\uff0c\u4f60\u5982\u8981\u7559\u5bbf\u5728\u6b64\uff0c\u4f1a\u6709\u4e09\u4e2a\u7ed3\u679c\u3002\u7530\u5144\uff0c\u4f60\u81ea\u5df1\u597d\u597d\u601d\u91cf\u4e00\u4e0b\uff0c\u5e94\u8be5\u5982\u4f55\uff1f\u201d \u3000\u3000\u4f46\u95fb\u623f\u95e8\u5440\u7136\uff0c\u738b\u5b37\u68d2\u7740\u4e00\u58f6\u9152\uff0c\u884c\u4e86\u8fdb\u6765\uff0c\u89c1\u4e24\u4eba\u4ecd\u7136\u5bf9\u5750\u5c0f\u914c\uff0c\u4e00\u5f20\u51b7\u5389\u7684\u8138\u4e0a\uff0c\u7acb\u523b\u6302\u4e0a\u7b11\u5bb9\uff0c\u9053\uff1a\u201c\u7ed9\u4e24\u4f4d\u9001\u9152\u6765\u4e86\u3002\u201d \u3000\u3000\u9752\u51e4\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u597d\uff01\u653e\u4e0b\u5427\uff01\u201d \u3000\u3000\u738b\u5b37\u653e\u4e0b\u9152\uff0c\u6084\u7136\u9000\u53bb\u3002 \u3000\u3000\u201c\u5979\u4eec\u8033\u76ee\u7075\u654f\uff0c\u5df2\u7ec3\u6210\u4e86\u7edd\u4f73\u7684\u542c\u529b\uff0c\u54b1\u4eec\u867d\u7136\u5c0f\u58f0\u4ea4\u8c08\uff0c\u5979\u4eec\u9644\u58c1\u503e\u542c\uff0c\u4ea6\u53ef\u5b57\u5b57\u5165\u8033\uff0c\u4f46\u8fd9\u4e00\u9635\uff0c\u5979\u4eec\u542c\u4e0d\u5230\u58f0\u606f\uff0c\u624d\u7834\u95e8\u800c\u5165\uff0c\u60f3\u6349\u2026\u2026\u201d \u3000\u3000\u6349\u4ec0\u4e48\uff1f\u9752\u98ce\u6ca1\u6709\u518d\u8bf4\u4e0b\u53bb\uff0c\u8138\u4e0a\u5fae\u73b0\u7f9e\u610f\uff0c\u4f3c\u662f\u6b63\u5728\u60f3\u5165\u975e\u975e\u2026\u2026\u3002 \u3000\u3000\u6c5f\u67ab\u63a5\u9053\uff1a\u201c\u5e78\u597d\uff01\u54b1\u4eec\u662f\u541b\u5b50\u3001\u6dd1\u5973\uff0c\u53d1\u4e4e\u4e8e\u60c5\uff0c\u6b62\u4e4e\u4e8e\u793c\uff0c\u6211\u770b\u5979\u8fdb\u95e8\u540e\u51b7\u5389\u7684\u8138\u4e0a\uff0c\u7a81\u7136\u6362\u4e0a\u7b11\u5bb9\uff0c\u867d\u7136\u5f88\u52c9\u5f3a\uff0c\u4f46\u603b\u662f\u7b11\u5bb9\u2026\u2026\u201d \u3000\u3000\u201c\u90a3\u662f\u56e0\u4e3a\u54b1\u4eec\u8863\u5c65\u6574\u9f50\uff0c\u5bf9\u5750\u5c0f\u996e\uff0c\u4e3e\u6b62\u89c4\u77e9\u2026\u2026\u201d\u9752\u51e4\u8bf4\uff0c\u201c\u54b1\u4eec\u8fde\u624b\u90fd\u672a\u62c9\u8fc7\u4e00\u4e0b\uff0c\u5927\u5927\u7684\u51fa\u4e86\u5979\u4eec\u7684\u610f\u6599\u4e4b\u5916\uff0c\u548c\u5979\u5185\u5fc3\u542c\u60f3\u6cd5\uff0c\u5b8c\u5168\u76f8\u53cd\u3002\u201d \u3000\u3000\u201c\u5979\u4eec\u662f\u6349\u5978\u4e0d\u6210\u4e86\u2026\u2026\u201d\u6c5f\u67ab\u7b11\u4e00\u7b11\u53c8\u9053\uff1a\u201c\u5982\u662f\u4e0d\u5e78\u88ab\u5979\u4eec\u6293\u5230\u4e86\u628a\u67c4\uff0c\u90a3\u4f1a\u662f\u4e2a\u4ec0\u4e48\u6837\u7684\u5c40\u9762\uff1f\u201d \u3000\u3000\u201c\u521a\u624d\uff0c\u5c0f\u59b9\u4e5f\u60f3\u5230\u4e86\u4e09\u4e2a\u7ed3\u679c\uff0c\u7530\u5144\uff0c\u662f\u4e0d\u662f\u60f3\u542c\u542c\u5462\uff1f\u201d \u3000\u3000\u201c\u6b63\u8981\u606d\u542c\u96c5\u6559\uff0c\u5374\u88ab\u738b\u5b37\u51b2\u8fdb\u6765\u6253\u6270\u4e71\u4e86\u60c5\u7eea\u3002\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u73b0\u5728\uff0c\u6211\u8fd9\u53a2\u6d17\u8033\u4ee5\u5f85\u4e86\u3002\u201d \u3000\u3000\u201c\u4e0d\u7528\u65bd\u5c55\u4f60\u7684\u8bf1\u60d1\u3001\u9b45\u529b\uff0c\u5c0f\u59b9\u4e5f\u5df2\u52a8\u5fc3\u2026\u2026\u201d\u9752\u51e4\u8bf4\uff0c\u201c\u4f60\u7684\u4e3e\u6b62\u4e4b\u95f4\uff0c\u5df2\u5e26\u7740\u5c11\u89c1\u7684\u7537\u4eba\u97f5\u5473\uff0c\u50cf\u5c16\u9525\u4e00\u822c\uff0c\u523a\u5165\u4e86\u5973\u4eba\u5fc3\u4e2d\uff0c\u5c24\u5176\u81ea\u8d1f\u624d\u827a\u7684\u5973\u4eba\uff0c\u6211\u76f8\u4fe1\u662f\u5929\u8d4b\u52a0\u540e\u5929\u7684\u4fee\u4e3a\uff0c\u56e0\u4e3a\uff0c\u6211\u4e00\u76f4\u4e0d\u592a\u76f8\u4fe1\u4f60\u5177\u6709\u7684\u9b45\u529b\uff0c\u771f\u662f\u4e0e\u751f\u4ff1\u6765\uff0c\u6d51\u7136\u5929\u6210\uff0c\u5a9a\u529f\u4e4b\u6700\uff0c\u4ee5\u5929\u72d0\u592b\u4eba\u7684\u2018\u60d1\u5fc3\u672f\u2019\u6700\u5f3a\uff0c\u5c0f\u59b9\u4e60\u7ec3\u7684\u2018\u8ff7\u9b42\u5927\u6cd5\u2019\uff0c\u5c31\u5217\u5165\u6b21\u7ea7\u5a9a\u672f\u4e86\uff0c\u4f46\u4f60\u4e0d\u53ef\u80fd\u548c\u5929\u72d0\u592b\u4eba\u6709\u6240\u5173\u8fde\u554a\uff01\u5979\u4e0d\u4f1a\u6536\u4e2a\u7537\u4eba\u4f5c\u5f92\u5f1f\uff0c\u6240\u4ee5\uff0c\u5c0f\u59b9\u8ba4\u4e3a\u4f60\u662f\u4e2a\u5929\u8d4b\u6781\u9ad8\u7684\u4eba\uff0c\u624d\u9aa8\u50b2\u4e16\uff0c\u4f46\u4e5f\u4fee\u4e60\u8fc7\u5a9a\u60d1\u5973\u4eba\u7684\u5f02\u672f\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u5fc3\u4e2d\u6697\u6697\u9707\u52a8\uff0c\u8fd9\u9752\u51e4\u8bc6\u89c1\u4e4b\u5e7f\uff0c\u7684\u662f\u53ef\u6015\uff0c\u6b64\u60c5\u666f\u548c\u5979\u62fc\u6597\u5185\u529f\u5f02\u672f\uff0c\u5012\u4e0d\u5982\u52a8\u4e4b\u4ee5\u8bda\u4e86\u3002 \u3000\u3000\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u53e3\u4e2d\u5374\u63a5\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\uff0c\u80fd\u628a\u4f60\u8fd9\u4f4d\u624d\u827a\u53cc\u7edd\u7684\u7f8e\u5973\uff0c\u4e0d\u8bb8\u4ed6\u4eba\u67d3\u6307\u2026\u2026\u201d \u3000\u3000\u201c\u4f60\u53ef\u4ee5\u5192\u9669\u4e00\u8bd5\uff0c\u4f46\u7b2c\u4e00\u5173\u4f1a\u9047\u4e0a\u738b\u5b37\u3001\u674e\u5b37\u7684\u8054\u624b\u653b\u51fb\uff0c\u90a3\u662f\u5929\u60ca\u5730\u52a8\u7684\u731b\u70c8\u653b\u6253\u2026\u2026\u201d \u3000\u3000\u9752\u51e4\u4e0d\u518d\u7528\u4f20\u97f3\u4e4b\u672f\u8bf4\u8bdd\uff0c\u597d\u50cf\u8fd9\u4e9b\u4ea4\u8c08\uff0c\u6709\u610f\u8ba9\u4eba\u542c\u5230\u3002 \u3000\u3000\u6c5f\u67ab\u4f1a\u610f\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u95ef\u8fc7\u4e86\u8fd9\u4e00\u5173\uff0c\u8fd8\u6709\u7b2c\u4e8c\u5173\u5417\uff1f\u201d \u3000\u3000\u201c\u6709\u2026\u2026\u201d\u9752\u51e4\u7728\u7728\u773c\u775b\uff0c\u9053\uff1a\u201c\u8d31\u59be\u5df2\u8d25\u5728\u541b\u7684\u624b\u4e0b\uff0c\u53cd\u6297\u65e0\u529b\uff0c\u4f60\u53ef\u80fd\u5f97\u507f\u5fc3\u613f\uff0c\u4f46\u4e00\u5bb5\u6625\u5c3d\uff0c\u51e4\u53bb\u697c\u7a7a\uff0c\u6b64\u540e\uff0c\u518d\u4e5f\u65e0\u6cd5\u76f8\u89c1\u4e86\u3002\u201d \u3000\u3000\u201c\u5662\uff01\u90a3\u662f\u8bf4\uff0c\u59d1\u5a18\u4f1a\u88ab\u4eba\u6740\u6b7b\u4e86\uff1f\u201d\u6c5f\u67ab\u9ad8\u58f0\u95ee\u9053\u3002 \u3000\u3000\u201c\u6ca1\u6709\u4eba\u6740\u6211\uff0c\u4f46\u8d31\u59be\u4e5f\u65e0\u989c\u5077\u751f\u4eba\u4e16\u4e86\uff01\u201d \u3000\u3000\u201c\u8fd8\u6709\u4e00\u4e2a\u7ed3\u679c\u5462\uff1f\u201d\u6c5f\u67ab\u9ad8\u58f0\u95ee\u9053\u3002 \u3000\u3000\u201c\u5929\u4e0b\u867d\u5927\uff0c\u6b64\u540e\uff0c\u5c06\u65e0\u4f60\u7acb\u8db3\u4e4b\u5730\uff0c\u4f60\u4f1a\u906d\u5230\u65e0\u6570\u7684\u9ad8\u624b\u8ffd\u6740\u2026\u2026\u201d\u9752\u51e4\u8bf4\uff0c\u201c\u4f55\u51b5\uff0c\u4f60\u8fde\u7b2c\u4e00\u5173\u4e5f\u672a\u5fc5\u95ef\u5f97\u8fc7\u53bb\uff01\u4e00\u591c\u6625\u5bb5\uff0c\u4ed8\u51fa\u5982\u6b64\u60e8\u91cd\u7684\u4ee3\u4ef7\uff0c\u7530\u5144\u60f3\u4e00\u60f3\uff0c\u662f\u5426\u503c\u5f97\u5462\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u5251\u7709\u8038\u52a8\uff0c\u4fca\u76ee\u653e\u5149\uff0c\u5927\u6709\u653e\u624b\u4e00\u8bd5\u4e4b\u610f\u3002 \u3000\u3000\u201c\u59d4\u66f2\u624d\u80fd\u6c42\u5168\u2026\u2026\u201d\u9752\u98ce\u6539\u7528\u4f20\u97f3\u4e4b\u672f\uff0c\u9053\uff1a\u201c\u544a\u8bc9\u6211\u4f60\u4f4f\u7684\u5730\u65b9\uff0c\u6211\u4f1a\u627e\u51fa\u65f6\u95f4\u53bb\u770b\u4f60\uff0c\u90a3\u65f6\uff0c\u518d\u7545\u53d9\u6b22\u5a31\u3002\u201d \u3000\u3000\u6c5f\u67ab\u70b9\u5934\u4f1a\u610f\uff0c\u4ee5\u624b\u6cbe\u9152\uff0c\u5728\u684c\u5b50\u4e0a\u5199\u4e00\u4e2a\u5730\u5740\uff0c\u8ba2\u4ee5\u8054\u7edc\u65b9\u6cd5\uff0c\u53e3\u5916\u5374\u6545\u610f\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5982\u6b64\u51f6\u9669\u7684\u7537\u5973\u76f8\u60a6\uff0c\u5176\u5f3a\u70c8\u7684\u523a\u6fc0\u6027\u56fa\u53ef\u4ee4\u4eba\u4ea2\u594b\uff0c\u4f46\u5374\u5e26\u7740\u6d53\u91cd\u7684\u8840\u8165\u6c14\u5473\uff0c\u5728\u4e0b\u53ea\u597d\u656c\u8c22\uff0c\u51e4\u59d1\u5a18\u591a\u591a\u4fdd\u91cd\uff0c\u533a\u533a\u544a\u8f9e\u3002\u201d \u3000\u3000\u7ad9\u8d77\u8eab\u5b50\uff0c\u51c6\u5907\u79bb\u53bb\u3002 \u3000\u3000\u865a\u63a9\u7684\u623f\u95e8\u518d\u5f00\uff0c\u4e24\u4e2a\u5168\u8eab\u9ed1\u8863\u7684\u4e2d\u5e74\u5987\u4eba\uff0c\u5e76\u80a9\u800c\u7acb\uff0c\u6321\u4f4f\u4e86\u53bb\u8def\u3002 \u3000\u3000\u6c5f\u67ab\u5446\u5728\u5f53\u5730\u3002 \u3000\u3000\u8fd9\u7a81\u7136\u7684\u53d8\u5316\uff0c\u4e5f\u5927\u51fa\u4e86\u9752\u51e4\u7684\u610f\u6599\u4e4b\u5916\u3002 \u3000\u3000\u738b\u5b37\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u7530\u516c\u5b50\uff0c\u7528\u4e0d\u7740\u6211\u4ecb\u7ecd\u4e86\uff0c\u51e4\u59d1\u5a18\u5df2\u5bf9\u4f60\u8bf4\u7684\u5f88\u6e05\u695a\uff0c\u6211\u59d3\u5b37\uff0c\u5979\u59d3\u674e\uff0c\u6211\u4eec\u90fd\u662f\u4f8d\u5019\u51e4\u59d1\u5a18\u7684\u8001\u5988\u5b50\u3002\u201d \u3000\u3000\u6c5f\u67ab\u8083\u7136\u800c\u7acb\uff0c\u51dd\u795e\u6212\u5907\uff0c\u4e24\u9053\u76ee\u5149\uff0c\u5f88\u4ed4\u7ec6\u7684\u6253\u91cf\u8fd9\u4e24\u4e2a\u4e2d\u5e74\u5987\u4eba\u3002 \u3000\u3000\u5979\u4eec\u7ea6\u6709\u4e09\u5341\u4e94\u516d\u7684\u5e74\u7eaa\uff0c\u5012\u4e5f\u98ce\u97f5\u6709\u81f4\uff0c\u53ea\u662f\u8fd9\u65f6\u523b\uff0c\u5979\u4eec\u90fd\u62c9\u957f\u4e86\u4e00\u5f20\u7751\uff0c\u770b\u4e0a\u53bb\uff0c\u5c31\u4e0d\u90a3\u4e48\u67d4\u548c\u53ef\u4eb2\u4e86\u3002 \u3000\u3000\u6c5f\u67ab\u5fc3\u4e2d\u6253\u8f6c\uff0c\u60f3\u7740\u8be5\u5982\u4f55\u5904\u7f6e\u8fd9\u79cd\u4e8b\u60c5\uff0c\u5168\u529b\u640f\u6740\uff1f\u6216\u662f\u7834\u56f4\u800c\u53bb\uff1f\u4e00\u65f6\u4e4b\u95f4\uff0c\u65e0\u6cd5\u5b9a\u4e0b\u4e3b\u610f\u3002 \u3000\u3000\u9752\u51e4\u4e00\u98a6\u67f3\u7709\u51e0\u9053\uff1a\u201c\u738b\u5b37\u3001\u674e\u5b37\uff0c\u4f60\u4eec\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u201c\u51e4\u59d1\u5a18\uff0c\u4e0d\u80fd\u8ba9\u4ed6\u8d70\u2026\u2026\u201d\u738b\u5b37\u8bf4\uff0c\u201c\u6211\u548c\u674e\u59d0\u5df2\u7ecf\u5546\u91cf\u597d\u4e86\uff0c\u8fd9\u4e9b\u5e74\u6765\uff0c\u51e4\u59d1\u5a18\u5f85\u6211\u4eec\u60c5\u4e49\u6df1\u539a\uff0c\u6211\u4eec\u4e00\u76f4\u6ca1\u6709\u673a\u4f1a\u62a5\u7b54\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u4eec\u770b\u5f97\u51fa\u6765\uff0c\u51e4\u59d1\u5a18\u662f\u771f\u5fc3\u7684\u559c\u6b22\u4ed6\u2026\u2026\u201d\u674e\u5b37\u63a5\u9053\uff1a\u201c\u5509\uff01\u8fd9\u4e9b\u5e74\u6765\uff0c\u4f60\u65e5\u591c\u767b\u53f0\u9ad8\u6b4c\uff0c\u98ce\u9761\u4e86\u957f\u5b89\uff0c\u53ef\u662f\uff0c\u4f60\u5374\u662f\u6625\u95fa\u6df1\u9501\u4e00\u5ba4\u6101\uff0c\u8fc7\u7740\u5bc2\u5bde\u7684\u65e5\u5b50\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u2026\u2026\u6211\u5f88\u597d\u554a\uff01\u201d\u9752\u51e4\u8bf4\uff0c\u201c\u4e3b\u4e0a\u5782\u987e\uff0c\u9752\u7750\u6709\u52a0\uff0c\u53c8\u6709\u4f60\u4eec\u8fd9\u4e24\u5927\u9ad8\u624b\uff0c\u4fdd\u62a4\u6211\u7684\u5b89\u5168\uff0c\u9526\u8863\u7389\u98df\uff0c\u51e4\u697c\u5e7d\u96c5\uff0c\u738b\u5983\u5343\u91d1\uff0c\u4e5f\u4e0d\u8fc7\u5982\u6b64\u751f\u6d3b\u5427\uff1f\u6211\u8fd8\u6709\u4ec0\u4e48\u4e0d\u6ee1\u8db3\u7684\u5462\uff1f\u201d \u3000\u3000\u201c\u51e4\u59d1\u5a18\uff0c\u9752\u51e4\u697c\u95e8\u7981\u68ee\u4e25\uff0c\u9664\u4e86\u4e3b\u4e0a\u4e4b\u5916\u4ece\u65e0\u4e00\u4e2a\u4e09\u5c3a\u7ae5\u5b50\uff0c\u5f97\u5165\u534a\u6b65\uff0c\u4eca\u671d\u4f60\u95fa\u623f\u7f6e\u9152\uff0c\u63a5\u5f85\u4f73\u5bbe\uff0c\u96be\u9053\u771f\u7684\u662f\u795e\u5973\u65e0\u60c5\u5417\uff1f\u2026\u2026\u201d\u738b\u5b37\u8bf4\uff0c\u201c\u6211\u77e5\u9053\uff0c\u4f60\u987e\u5fcc\u6211\u4eec\uff0c\u4e0d\u6562\u7eb5\u60c5\u6063\u653e\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u2026\u2026\u4f60\u4eec\u2026\u2026\u738b\u5b37\uff01\u674e\u5b37\uff01\u653e\u4ed6\u53bb\u5427\uff01\u2026\u2026\u201d\u9752\u51e4\u4f3c\u662f\u7a81\u7136\u5931\u53bb\u4e86\u63a7\u5236\u81ea\u5df1\u7684\u80fd\u529b\uff0c\u6709\u4e9b\u8bed\u65e0\u4f26\u6b21\u7684\u63a5\u9053\uff1a\u201c\u7559\u4e0b\u4ed6\uff0c\u6709\u4ec0\u4e48\u597d\uff0c\u4e00\u53a2\u60c5\u7f18\uff0c\u6211\u6015\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u8981\u6015\uff01\u2026\u2026\u201d\u674e\u5b37\u63a5\u9053\uff1a\u201c\u6709\u6211\u548c\u738b\u5b37\u5e2e\u4f60\u63a9\u906e\uff0c\u4e3b\u4e0a\u4e0d\u4f1a\u77e5\u9053\u3002\u201d \u3000\u3000\u9752\u51e4\u4e3e\u624b\u62ed\u53bb\uff0c\u593a\u7736\u800c\u53bb\u7684\u4e24\u884c\u6cea\u6c34\uff0c\u957f\u957f\u5438\u4e00\u53e3\u6c14\uff0c\u4eba\u5df2\u51b7\u9759\u4e0b\u6765\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u6211\u5f88\u611f\u6fc0\u4e24\u4f4d\u5b37\u5b37\u7684\u7231\u987e\uff0c\u53ef\u662f\u2026\u2026\u201d \u3000\u3000\u201c\u51e4\u59d1\u5a18\u2026\u2026\u201d\u738b\u5b37\u63a5\u9053\uff1a\u201c\u6211\u4eec\u662f\u771f\u5fc3\u7684\uff0c\u4e0d\u8981\u518d\u5fc3\u5b58\u7591\u8651\uff0c\u4f60\u77e5\u9053\uff0c\u6211\u548c\u674e\u5b37\u7ec3\u7684\u662f\u706d\u7edd\u795e\u529f\uff0c\u65e9\u5df2\u6d88\u53bb\u4e86\u8272\u6b32\u4e4b\u5ff5\uff0c\u82f1\u4fca\u90ce\u541b\uff0c\u6f47\u6d12\u516c\u5b50\uff0c\u5728\u6211\u4eec\u773c\u4e2d\u662f\u4e00\u6587\u4e0d\u503c\uff0c\u4f46\u8fd9\u5c0f\u5b50\u5374\u4e5f\u6405\u8d77\u4e86\u6211\u4eec\u65e9\u5df2\u51bb\u7ed3\u6210\u51b0\u7684\u90a3\u70b9\u6e29\u60c5\uff0c\u6211\u4eec\u540c\u60c5\u51e4\u59d1\u5a18\uff0c\u4e5f\u559c\u6b22\u8fd9\u5c0f\u5b50\uff0c\u4e5f\u52a8\u4e86\u4fc3\u6210\u4f60\u4eec\u4e00\u53cc\u4fe9\u597d\u7684\u5fc3\u613f\uff0c\u5f53\u65f6\uff0c\u6211\u8fd8\u8ba4\u4e3a\u81ea\u5df1\u6709\u70b9\u60f3\u5165\u975e\u975e\uff0c\u90a3\u77e5\u548c\u674e\u5b37\u4e00\u5546\u91cf\uff0c\u5979\u7adf\u7136\u4e5f\u6709\u8fd9\u4efd\u5fc3\u613f\u3002\u201d \u3000\u3000\u6c5f\u67ab\u5fc3\u4e2d\u5fd6\u9053\uff1a\u201c\u96be\u9053\u6211\u4fee\u4e60\u8fd9\u2018\u60d1\u5fc3\u672f\u2019\u9664\u4e86\u53ef\u6297\u62d2\u4ee4\u4eba\u8ff7\u9189\u7684\u5a9a\u529f\u4e4b\u5916\uff0c\u8fd8\u53ef\u4ee5\u5524\u56de\u4eba\u6027\u4e2d\u7684\u6e29\u60c5\uff0c\u8fd9\u738b\u5b37\u3001\u674e\u5b37\uff0c\u672c\u662f\u53d7\u547d\u76d1\u89c6\u9752\u51e4\u7684\u4eba\uff0c\u600e\u4e48\u53d1\u4e86\u75af\u4f3c\u7684\u786c\u8981\u4f5c\u7275\u7ebf\u7ea2\u5a18\u3002\u771f\u662f\u90aa\u5440\uff01\u201d \u3000\u3000\u4f46\u674e\u5b37\u8bf4\u9053\uff1a\u201c\u7530\u516c\u5b50\uff0c\u4f60\u600e\u4e48\u8bf4\uff1f\u201d \u3000\u3000\u201c\u6211\uff01\u4e0d\u77e5\u9053\uff0c\u4f46\u4e0d\u80fd\u5bb3\u4e86\u9752\u51e4\u59d1\u5a18\u5c31\u662f\uff0c\u5509\uff01\u5979\u771f\u8981\u4e3a\u6b64\u81ea\u7edd\uff0c\u6211\u5c82\u4e0d\u6210\u4e86\u8c0b\u5bb3\u5979\u7684\u51f6\u624b\u3002\u201d \u3000\u3000\u201c\u4f60\u5c0f\u5b50\u8fd8\u7b97\u6709\u70b9\u826f\u5fc3\uff0c\u2026\u2026\u201d\u674e\u5b37\u53f9\u606f\u4e00\u58f0\u53c8\u9053\uff1a\u201c\u4e16\u4e0a\u4e8b\uff0c\u5c31\u662f\u8fd9\u4e48\u7684\u602a\u5f02\u4e0d\u5e73\uff0c\u522b\u4eba\u5343\u91d1\u6c42\u89c1\uff0c\u60f3\u548c\u51e4\u59d1\u5a18\u8bf4\u51e0\u53e5\u8bdd\uff0c\u5c31\u96be\u5982\u6240\u613f\uff0c\u4f60\u5c0f\u5b50\u5374\u662f\u6843\u82b1\u7167\u547d\uff0c\u773c\u9ad8\u4e8e\u9876\u7684\u51e4\u59d1\u5a18\uff0c\u7adf\u7136\u4f1a\u5bb4\u5ba2\u6df1\u95fa\uff0c\u6700\u662f\u5947\u602a\u7684\u662f\u6211\u548c\u738b\u5b37\uff0c\u65e0\u7aef\u63d2\u4e0a\u4e00\u817f\uff0c\u4f60\u4eec\u597d\u597d\u8c08\u5427\uff01\u6211\u4eec\u544a\u9000\u4e86\u3002\u201d \u3000\u3000\u674e\u5b37\u3001\u738b\u5b37\uff0c\u4e92\u671b\u4e00\u773c\uff0c\u9f50\u58f0\u8bf4\u9053\uff1a\u201c\u5c0f\u5b50\uff0c\u522b\u5fd8\u4e86\u6211\u4eec\u8fd9\u4efd\u60c5\u610f\uff01\u201d\u53cc\u53cc\u9000\u51fa\u5ba4\u5916\uff0c\u987a\u624b\u5e26\u4e0a\u4e86\u623f\u95e8\u3002 \u3000\u3000\u9752\u51e4\u5446\u4e86\u4e00\u9635\uff0c\u624d\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u7ad9\u8fd9\u91cc\uff0c\u4e0d\u8981\u4e71\u52a8\u2026\u2026\u201d \u3000\u3000\u8f7b\u8f7b\u62c9\u5f00\u6728\u95e8\uff0c\u5a07\u8eaf\u4e00\u95ea\u800c\u6ca1\u3002 \u3000\u3000\u6c5f\u67ab\u679c\u7136\u7ad9\u7740\u672a\u52a8\u3002 \u3000\u3000\u4f46\u4ed6\u5fc3\u4e2d\u6b63\u5343\u56de\u767e\u8f6c\uff0c\u91cd\u65b0\u5bf9\u81ea\u5df1\u4f5c\u6b22\u8bc4\u4f30\uff0c\u4eca\u665a\u4e0a\u7684\u4e8b\u4ef6\u53d8\u5316\uff0c\u662f\u65e9\u5df2\u8bbe\u8ba1\u597d\u4e86\u7684\u9634\u8c0b\u3001\u5708\u5957\uff0c\u8fd8\u662f\u81ea\u5df2\u4fee\u4e60\u7684\u5947\u672f\uff0c\u771f\u6709\u6625\u98ce\u5316\u96e8\u7684\u80fd\u529b\u2026\u2026\u3002 \u3000\u3000\u4f46\u95fb\u5fae\u98ce\u98d2\u7136\uff0c\u9752\u51e4\u5df2\u91cd\u56de\u5ba4\u4e2d\uff0c\u63a9\u4e0a\u623f\u95e8\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u5979\u4eec\u771f\u7684\u5df2\u4e0d\u5728\u6697\u4e2d\u76d1\u89c6\u3001\u5077\u542c\uff0c\u53cc\u53cc\u5de1\u884c\u8fd9\u5ea7\u5b85\u9662\u7684\u56db\u5468\uff0c\u770b\u6837\u5b50\uff0c\u786e\u662f\u5b58\u5fc3\u8981\u4f5c\u6210\u8fd9\u6b21\u5927\u5a92\u5462\uff1f\u201d \u3000\u3000\u201c\u5979\u4eec\u53d7\u547d\u8ffd\u968f\u5728\u4f60\u7684\u5de6\u53f3\uff0c\u6697\u4e2d\u76d1\u89c6\u4f60\u7684\u884c\u52a8\uff0c\u81ea\u662f\u8d35\u4e3b\u4e0a\u7684\u5fc3\u8179\u4e4b\u81e3\uff0c\u600e\u4f1a\u53d1\u751f\u8fd9\u79cd\u81ea\u613f\u5012\u6208\u7684\u4e8b\u60c5\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u4f60\u770b\uff0c\u8fd9\u4e2d\u95f4\uff0c\u4f1a\u4e0d\u4f1a\u6709\u4ec0\u4e48\u9634\u8c0b\uff1f\u201d \u3000\u3000\u9752\u51e4\u6c89\u601d\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\uff0c\u4e0d\u8fc7\uff0c\u4e3b\u4e0a\u5df2\u7ecf\u6709\u4e09\u4e2a\u6708\u672a\u5230\u957f\u5b89\u4e86\uff0c\u5c31\u7b97\u8fd9\u662f\u5979\u4eec\u8bbe\u8ba1\u7684\u9634\u8c0b\uff0c\u4e5f\u662f\u51fa\u81ea\u4e24\u4eba\u7684\u8ba1\u5212\uff0c\u7530\u5144\u89c2\u5bdf\u5165\u5fae\uff0c\u53ef\u770b\u51fa\u4ec0\u4e48\u7834\u7efd\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u5168\u795e\u8d2f\u6ce8\u5728\u9752\u51e4\u7684\u795e\u60c5\u53d8\u5316\u4e0a\uff0c\u4ed6\u8981\u627e\u51fa\u9752\u51e4\u662f\u5426\u548c\u4e24\u4eba\u4e32\u901a\u4e00\u6c14\uff1f\u4f46\u5730\u5374\u770b\u4e0d\u51fa\u9752\u98ce\u6709\u4f5c\u4f2a\u7684\u6837\u5b50\u3002 \u3000\u3000\u201c\u662f\u4e0d\u662f\u4e5f\u6000\u7591\u6211\u2026\u2026\u201d\u9752\u51e4\u5e7d\u5e7d\u8bf4\u9053\uff1a\u201c\u4f60\u7a76\u7adf\u662f\u5e72\u4ec0\u4e48\u7684\uff1f\u76ee\u7684\u4f55\u5728\uff1f\u201d \u3000\u3000\u201c\u770b\u6765\uff0c\u51e4\u59d1\u5a18\u4e5f\u5bf9\u5728\u4e0b\u52a8\u7591\u4e86\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u5f53\u4eca\u6b66\u6797\u4e4b\u4e2d\u4f3c\u662f\u65e9\u4e3a\u4e00\u80a1\u795e\u79d8\u7684\u529b\u91cf\u7edf\u6cbb\u4e86\uff0c\u53ea\u4e0d\u8fc7\u533a\u533a\u662f\u4e00\u4e2a\u6f0f\u7f51\u4e4b\u9c7c\u3002\u201d \u3000\u3000\u6c5f\u67ab\u5355\u5200\u76f4\u5165\u7684\u8bd5\u63a2\uff0c\u5e0c\u671b\u628a\u4e8b\u4ef6\u5f15\u5165\u6b63\u9898\u3002 \u3000\u3000\u201c\u6211\u77e5\u9053\u4f60\u4e0d\u4f1a\u4e3a\u6211\u7684\u59ff\u8272\u6240\u8ff7\uff0c\u679c\u7136\u662f\u6709\u4e3a\u800c\u6765\uff0c\u53ef\u60b2\u7684\u662f\u59be\u8eab\u5374\u5df2\u81ea\u8e48\u5165\u60c5\u7f51\u4e4b\u4e2d\uff0c\u8bf4\u5427\uff01\u4f60\u60f3\u4ece\u8fd9\u91cc\u5f97\u5230\u4ec0\u4e48\uff1f\u8d81\u5979\u4eec\u672a\u5728\u5077\u542c\uff0c\u6211\u4e5f\u8bb8\u80fd\u63d0\u4f9b\u4e00\u4e9b\u5185\u60c5\u3002\u201d \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u597d\uff01\u90a3\u6211\u5c31\u76f4\u8a00\u6c42\u6559\u4e86\uff01\u201d \u3000\u3000\u9752\u51e4\u70b9\u70b9\u5934\u3002 \u3000\u3000\u201c\u59d1\u5a18\u5c31\u662f\u90a3\u80a1\u7edf\u6cbb\u6c5f\u6e56\u7684\u795e\u79d8\u7ec4\u5408\u4e2d\u4eba\u4e86\uff1f\u201d \u3000\u3000\u201c\u662f\uff01\u597d\u50cf\u6c5f\u6e56\u4e2d\u5404\u5927\u95e8\u6d3e\uff0c\u90fd\u5df2\u5165\u6211\u4eec\u7684\u638c\u63e1\u4e2d\u4e86\uff0c\u6b66\u6797\u9053\u4e0a\uff0c\u5df2\u7ecf\u6ca1\u6709\u5f3a\u5927\u7684\u529b\u91cf\u53cd\u6297\u6211\u4eec\uff0c\u4f60\u4e00\u4e2a\u4eba\uff0c\u53c8\u80fd\u6210\u4ec0\u4e48\u5927\u4e8b\uff1f\u2026\u2026\u201d\u9752\u51e4\u8bf4\uff0c\u201c\u6211\u4f69\u773c\u4f60\u6280\u827a\u4e0a\u7684\u6210\u5c31\uff0c\u4f46\u72ec\u6728\uff0c\u53c8\u4f55\u80fd\u6491\u8d77\u5c06\u503e\u7684\u5927\u53a6\u5462\uff1f\u201d \u3000\u3000\u201c\u6211\u5f88\u5b64\u72ec\uff0c\u6240\u4ee5\uff0c\u5f88\u9700\u8981\u50cf\u51e4\u59d1\u5a18\u8fd9\u6837\u7684\u7ea2\u989c\u77e5\u5df1\uff0c\u5e2e\u52a9\u6211\u2026\u2026\u201d \u3000\u3000\u201c\u4f60\u662f\u8bf4\uff0c\u5df2\u628a\u6211\u89c6\u4f5c\u77e5\u5df2\u7684\u597d\u53cb\u2026\u2026\u201d \u3000\u3000\u201c\u662f\u5440\uff01\u2026\u2026\u201d\u53f9\u606f\u4e00\u58f0\u53c8\u9053\uff1a\u201c\u76f8\u8bc6\u6ee1\u5929\u4e0b\uff0c\u77e5\u5df2\u6709\u51e0\u4eba\uff1f \u3000\u3000\u51e4\u59d1\u5a18\uff0c\u6211\u4eec\u76f8\u9022\u7684\u65f6\u673a\u4e0d\u597d\uff0c\u4f46\u80fd\u4e00\u89c1\u5982\u6545\u3002\u201d \u3000\u3000\u201c\u4f60\u4e0d\u662f\u5728\u9a97\u6211\u5427\uff1f\u2026\u2026\u201d\u9752\u51e4\u8bf4\uff0c\u201c\u77e5\u5df1\u4e8c\u5b57\uff0c\u5c82\u53ef\u8f7b\u76f8\u8bb8\u8bfa\u3002\u201d \u3000\u3000\u201c\u6211\u8bf4\u7684\u5b57\u5b57\u771f\u5b9e\uff0c\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u76f8\u4fe1\u2026\u2026\u201d\u9752\u51e4\u63a5\u9053\uff1a\u201c\u4f60\u5982\u501f\u673a\u53cd\u51fb\uff0c\u6211\u65e9\u5df2\u6ca6\u5165\u8d25\u4ea1\uff0c\u65f6\u95f4\u4e0d\u591a\uff0c\u5148\u8c08\u91cd\u8981\u7684\u4e8b\u60c5\u3002\u201d \u3000\u3000\u201c\u5bf9\uff01\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u4f60\u4eec\u7684\u4e3b\u4e0a\uff0c\u53ef\u5c31\u662f\u7edf\u7387\u90a3\u80a1\u795e\u79d8\u529b\u91cf\u7684\u9996\u8111\u5417\uff1f\u201d \u3000\u3000\u201c\u4ed6\u53ea\u662f\u5f88\u6709\u6743\u52bf\u9996\u8111\u4e4b\u4e00\uff0c\u2026\u2026\u201d\u9752\u51e4\u8bf4\uff0c\u201c\u4f46\u5374\u4e0d\u662f\u552f\u4e00\u7684\u9996\u8111\u4eba\u7269\u3002\u201d \u3000\u3000\u6c5f\u67ab\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u59d1\u5a18\u5728\u7ec4\u5408\u4e4b\u4e2d\uff0c\u4e5f\u7b97\u662f\u91cd\u8981\u4eba\u7269\u4e86\uff1f\u201d \u3000\u3000\u201c\u6211\u53ea\u662f\u4f17\u591a\u7684\u5973\u5f1f\u5b50\u4e4b\u4e00\uff0c\u53ea\u56e0\u7a0d\u5177\u59ff\u8272\u3001\u624d\u6167\uff0c\u88ab\u9009\u5165\u91d1\u82b1\u7ea7\u5f1f\u5b50\u4e4b\u4e2d\uff0c\u5b66\u5230\u4e86\u8f83\u6df1\u5965\u7684\u6b66\u529f\uff0c\u4e0d\u77e5\u662f\u5e78\u8fd0\u6216\u662f\u4e0d\u5e78\uff0c\u6211\u88ab\u4e3b\u4e0a\u770b\u4e2d\u4e86\uff0c\u53d7\u5230\u66f4\u591a\u7684\u7167\u987e\uff0c\u4f46\u5374\u732e\u4e0a\u4e86\u5c11\u5973\u7684\u6e05\u767d\u8eab\u5b50\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u76f8\u4fe1\u4f60\u6ca1\u6709\u9009\u62e9\u7684\u81ea\u7531\u2026\u2026\u201d\u6c5f\u67ab\u591a\u60c5\u5730\u8bf4\uff0c\u201c\u9752\u51e4\uff0c\u4e0d\u8981\u4e3a\u8fc7\u53bb\u6094\u6068\uff0c\u60f3\u60f3\u770b\u4eca\u540e\u8be5\u505a\u4e9b\u4ec0\u4e48\uff1f\u4f60\u4eec\u90a3\u4e2a\u4ee5\u8be1\u5f02\u3001\u66b4\u529b\u7edf\u6cbb\u7684\u7ec4\u5408\uff0c\u4efb\u4f55\u4e00\u4e2a\u5973\u5f1f\u5b50\uff0c\u90fd\u6ca1\u6709\u80fd\u529b\u6297\u62d2\u88ab\u4e3b\u4e0a\u5360\u6709\u7684\u80fd\u529b\u3002\u201d \u3000\u3000\u201c\u5185\u5bab\u4e00\u7cfb\u51fa\u8eab\u7684\u5973\u5f1f\u5b50\uff0c\u4e5f\u8bb8\u597d\u4e9b\u3002\u56e0\u4e3a\u7edf\u7387\u5979\u4eec\u7684\u4eba\uff0c\u90fd\u662f\u5973\u4eba\uff0c\u2026\u2026\u201d\u9752\u51e4\u8bf4\uff0c\u201c\u8fd9\u5ea7\u6d1b\u9633\u5c45\u4e2d\u7684\u56db\u5b63\u82b1\u5a62\uff0c\u5168\u662f\u5185\u5bab\u4e00\u7cfb\u4e2d\u4eba\u2026\u2026\u201d \u3000\u3000\u201c\u5979\u4eec\u8981\u966a\u9152\u3001\u5f85\u5ba2\uff0c\u5e94\u4ed8\u5404\u79cd\u4e0d\u540c\u7684\u7537\u4eba\uff0c\u6bd4\u8d77\u4f60\u8fd9\u4e2a\u5dee\u4e8b\uff0c\u8f9b\u82e6\u591a\u4e86\u3002\u201d\u6c5f\u67ab\u5fae\u7b11\u7740\u8bf4\u3002 \u3000\u3000\u201c\u4f60\u65e9\u5c31\u6765\u8fc7\u6d1b\u9633\u5c45\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u662f\uff01\u800c\u4e14\uff0c\u8fd8\u53ec\u8fc7\u6625\u82b1\u56db\u5a62\u966a\u9152\uff0c\u8bba\u624d\u6167\u3001\u59ff\u8272\uff0c\u5979\u4eec\u7edd\u65e0\u6cd5\u548c\u4f60\u76f8\u6bd4\u2026\u2026\u201d \u3000\u3000\u201c\u4f60\u627e\u9519\u4eba\u4e86\u2026\u2026\u201d\u9752\u51e4\u8bf4\uff0c\u201c\u56db\u5b63\u82b1\u5a62\u4e2d\u8d85\u7b49\u4eba\u7269\u662f\u6885\u82b1\u59d1\u5a18\uff0c\u5979\u624d\u662f\u56db\u5b63\u82b1\u5a62\u4e2d\u7684\u9b41\u9996\uff0c\u4f60\u5e94\u8be5\u53bb\u89c1\u8bc6\u4e00\u4e0b\u2026\u2026\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u5979\u7f8e\u8272\u593a\u4eba\uff0c\u5a9a\u672f\u7cbe\u6e5b\uff0c\u5979\u5b58\u5fc3\u53bb\u6293\u4f4f\u4e00\u4e2a\u7537\u4eba\uff0c\u90a3\u7537\u4eba\u7edd\u65e0\u6cd5\u9003\u8fc7\u2026\u2026\u201d\u9752\u51e4\u659c\u7750\u770b\u6c5f\u67ab\uff0c\u63a5\u9053\uff1a\u201c\u8fd9\u6837\u7684\u82b1\u9b41\u3001\u7f8e\u5973\uff0c\u4f60\u4e0d\u4f1a\u89c1\u8bc6\u4e00\u4e0b\uff0c\u5c82\u4e0d\u662f\u865a\u6b64\u957f\u5b89\u4e00\u884c\u3002\u201d \u3000\u3000\u6c5f\u67ab\u4e0d\u613f\u591a\u8bf4\u6885\u82b1\uff0c\u53e3\u6c14\u4e00\u53d8\uff1b\u9053\uff1a\u201c\u59d1\u5a18\u4e0d\u662f\u5185\u5bab\u7cfb\u4e2d\u4eba\uff1f\u201d \u3000\u3000\u201c\u6211\u4e0d\u662f\u2026\u2026\u201d\u9752\u51e4\u9053\uff1a\u201c\u6211\u662f\u5c5e\u4e8e\u5929\u738b\u95e8\u4e0b\u7684\u5f1f\u5b50\u2026\u2026\u201d \u3000\u3000\u201c\u53c8\u662f\u5929\u738b\u3001\u53c8\u662f\u5185\u5bab\uff0c\u8fd9\u54ea\u91cc\u50cf\u6c5f\u6e56\u4e0a\u7684\u7ec4\u5408\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u5b83\u7a76\u7adf\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d \u3000\u3000\u201c\u53ea\u662f\u4e00\u79cd\u79f0\u547c\u7f62\u4e86\uff0c\u53eb\u8d77\u6765\u53c8\u522b\u4e00\u822c\u6c5f\u6e56\u7ec4\u5408\uff0c\u7530\u5144\uff0c\u6211\u77e5\u9053\u7684\u53ea\u9650\u4e8e\u6b64\uff0c\u4f46\u5c31\u6211\u89c2\u5bdf\u6240\u5f97\uff0c\u4f3c\u4e4e\u5929\u738b\u4e0d\u81f3\u4e00\u4e2a\uff0c\u5e94\u8be5\u8fd8\u6709\u4e00\u79cd\u529b\u91cf\u63a7\u5236\u7740\u4ed6\u4eec\uff0c\u2026\u2026\u201d\u9752\u51e4\u8bf4\uff0c\u201c\u8fd9\u4e9b\u4e8b\uff0c\u6211\u53ea\u80fd\u542c\uff0c\u4e0d\u80fd\u95ee\uff0c\u90a3\u4f1a\u5f15\u8d77\u4ed6\u7684\u6000\u7591\u3002\u201d \u3000\u3000\u201c\u5185\u5bab\u5f1f\u5b50\u548c\u5929\u738b\u95e8\u4e0b\uff0c\u662f\u4e0d\u662f\u5904\u7684\u4e0d\u5927\u878d\u6d3d\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u4f60\u548c\u56db\u5b63\u82b1\u5a62\uff0c\u4e5f\u6ca1\u6709\u5f80\u6765\uff1f\u201d \u3000\u3000\u201c\u4f55\u81f3\u662f\u4e0d\u592a\u878d\u6d3d\uff0c\u7b80\u76f4\u662f\u51b0\u70ad\u4e0d\u80fd\u540c\u7089\uff0c\u73b0\u5728\u8fd8\u80fd\u7ef4\u6301\u4e2a\u548c\u7766\u76f8\u548c\u7684\u5c40\u9762\uff0c\u90a3\u5c31\u662f\u6211\u4eec\u5949\u5230\u4e86\u4e00\u79cd\u4e25\u683c\u7684\u547d\u4ee4\uff0c\u4e0d\u5f97\u540c\u5ba4\u64cd\u6208\uff0c\u8fdd\u8005\u5904\u6b7b\uff0c\u6211\u76f8\u4fe1\u8fd9\u9053\u4ee4\u5077\uff0c\u4e0d\u662f\u5929\u738b\u7684\u610f\u601d\uff0c\u4ee4\u8c15\u662f\u5929\u738b\u548c\u5bab\u4e3b\u4f1a\u8854\u9881\u4e0b\uff0c\u903c\u4ed6\u4eec\u9881\u4e0b\u8fd9\u9053\u4ee4\u8c15\u7684\u4eba\uff0c\u624d\u662f\u6700\u6709\u6743\u52bf\u7684\u4eba\u2026\u2026\u201d\u9752\u51e4\u786e\u6709\u5c3d\u5410\u6240\u77e5\u7684\u7528\u5fc3\uff0c\u7b11\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u8bf4\u8d77\u6765\uff0c\u6709\u4e9b\u53ef\u7b11\uff0c\u5929\u738b\u548c\u5bab\u4e3b\u539f\u672c\u662f\u4e00\u5bf9\u592b\u59bb\uff0c\u5982\u4eca\u5374\u5404\u6210\u4e00\u7cfb\uff0c\u4e92\u4e0d\u76f8\u5bb9\uff0c\u4e0d\u8fc7\uff0c\u4ed6\u4eec\u4e24\u4e2a\u4eba\u89c1\u9762\u65f6\uff0c\u8fd8\u80fd\u4fdd\u6301\u4e00\u5b9a\u98ce\u5ea6\uff0c\u5f7c\u6b64\u95f4\u8c08\u7b11\u98ce\u751f\u3002\u201d \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u8fd9\u6d1b\u9633\u5c45\u4e2d\uff0c\u90a3\u4e00\u65b9\u9762\u7684\u5b9e\u529b\u5927\u4e9b\uff1f\u201d \u3000\u3000\u201c\u8fd9\u91cc\u5c31\u66f4\u590d\u6742\u4e86\u2026\u2026\u201d\u9752\u51e4\u8bf4\uff1a\u201c\u5185\u5bab\u4e00\u7cfb\u4e2d\uff0c\u4ee5\u6885\u82b1\u4e3a\u9996\uff0c\u5929\u738b\u95e8\u4e0b\uff0c\u7531\u8d31\u59be\u62c5\u7eb2\uff0c\u4f46\u5929\u6b63\u62c5\u5fc3\u6211\u884c\u4e3a\u903e\u8d8a\uff0c\u6d3e\u4e86\u674e\u5b37\u3001\u738b\u5b37\u6765\u6697\u4e2d\u76d1\u89c6\u6211\u7684\u884c\u52a8\uff0c\u4e24\u4eba\u9664\u4e86\u4f7f\u6211\u4e2a\u4eba\u79c1\u751f\u6d3b\u4e0a\uff0c\u65e0\u6cd5\u81ea\u7531\u5916\uff0c\u516c\u4e8b\u4e0a\uff0c\u5374\u662f\u5927\u597d\u5e2e\u624b\uff0c\u5979\u4eec\u6b66\u529f\u7cbe\u5947\u3001\u673a\u8b66\u65e0\u6bd4\uff0c\u6d1b\u9633\u697c\u7684\u4f8d\u5e94\u7537\u5973\uff0c\u4ee5\u9ad8\u5927\u6597\u4e3a\u9996\uff0c\u5171\u6709\u5341\u516b\u4e2a\u4eba\uff0c\u8fd9\u5c31\u662f\u5929\u738b\u95e8\u4e0b\u7684\u5b9e\u529b\uff0c\u597d\u50cf\u4e0d\u5982\u5185\u5bab\u4e00\u7cfb\u7684\u4eba\u624b\u4f17\u591a\uff0c\u4f46\u6211\u76f8\u4fe1\uff0c\u6211\u4eec\u4eba\u624b\u867d\u5c11\uff0c\u4f46\u5374\u7cbe\u9510\u8fc7\u4e4b\uff0c\u9664\u6b64\u4e4b\u5916\uff0c\u8868\u9762\u4e0a\uff0c\u6211\u4eec\u90fd\u7531\u8fd9\u91cc\u7684\u603b\u7ba1\u7edf\u7387\uff0c\u4ed6\u662f\u5929\u9a6c\u603b\u5802\u4e2d\u6d3e\u6765\u7684\u4eba\uff0c\u4e5f\u662f\u771f\u6b63\u7684\u6c5f\u6e56\u4e2d\u4eba\u2026\u2026\u201d \u3000\u3000\u201c\u5929\u9a6c\u5802\u53c8\u662f\u600e\u4e48\u56de\u4e8b\u5462\uff1f\u6211\u90fd\u4f1a\u5c3d\u6211\u6240\u77e5\u7684\u544a\u8bc9\u4f60\uff0c\u201d \u3000\u3000\u9752\u51e4\u8bf4\uff0c\u201c\u5929\u9a6c\u5802\u662f\u771f\u6b63\u6c5f\u6e56\u4eba\u7269\u805a\u96c6\u7684\u7ec4\u5408\uff0c\u8fd1\u767e\u5e74\u6765\u7684\u6c5f\u6e56\u4e0a\u7684\u51f6\u795e\u3001\u6076\u715e\uff0c\u5927\u90fd\u96c6\u4e2d\u90a3\u91cc\uff0c\u5929\u9a6c\u603b\u5802\u7684\u5802\u4e3b\uff0c\u66f4\u662f\u6c5f\u6e56\u4e0a\u7b2c\u4e00\u51f6\u4eba\uff0c\u53ef\u60dc\uff0c\u6211\u4e0d\u77e5\u9053\u4ed6\u7684\u540d\u5b57\uff0c\u5929\u738b\u597d\u50cf\u63d0\u8fc7\uff0c\u4f46\u5f53\u65f6\u6211\u6ca1\u6709\u7559\u5fc3\u8bb0\u4e0b\u6765\u2026\u2026\u201d \u3000\u3000\u201c\u5929\u9a6c\u5802\u2026\u2026\u201d\u6c5f\u67ab\u6c89\u601d\u4e86\u4e00\u9635\u63a5\u9053\uff1a\u201c\u597d\u50cf\u6ca1\u6709\u542c\u8fc7\u8fd9\u4e2a\u7ec4\u5408\u3002\u201d \u3000\u3000\u201c\u4ed6\u4eec\u7684\u6d3b\u52a8\uff0c\u4e5f\u662f\u6697\u4e2d\u8fdb\u884c\uff0c\u5c31\u56e0\u4e3a\u5b83\u5177\u6709\u4e86\u795e\u79d8\u5916\u8863\uff0c\u6240\u4ee5\uff0c\u5b9e\u529b\u6269\u5c55\u7684\u5f88\u5feb\uff0c\u4e94\u5e74\u4e4b\u524d\uff0c\u6211\u4eec\u5c1a\u5728\u82e6\u7ec3\u6b66\u529f\u7684\u65f6\u5019\uff0c\u6240\u6709\u7684\u884c\u52a8\uff0c\u90fd\u900f\u8fc7\u5929\u9a6c\u603b\u5802\uff0c\u2026\u2026\u201d\u9752\u51e4\u8bf4\uff0c\u201c\u76f4\u5230\u6211\u4eec\u4e00\u3001\u4e8c\u3001\u4e09\uff0c\u4e09\u5c4a\u5f1f\u5b50\u51fa\u5e08\uff0c\u624d\u72ec\u7acb\u95e8\u6237\u3002\u201d \u3000\u3000\u201c\u597d\u6c89\u6df1\u7684\u57ce\u5e9c\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u5355\u662f\u8fd9\u4efd\u5fcd\u8010\u7684\u5de5\u592b\uff0c\u5c31\u975e\u5e38\u4eba\u80fd\u53ca\u4e86\uff0c\u4f46\u4ed6\u672a\u6599\u5230\uff0c\u8fd9\u79cd\u5206\u5316\u5b9e\u529b\u7684\u7b56\u7565\uff0c\u867d\u7136\u4fbf\u4e8e\u7edf\u7387\uff0c\u4f46\u5374\u6613\u751f\u5bf9\u7acb\uff0c\u867d\u592b\u5987\u4ea6\u6240\u96be\u514d\uff0c\u4f60\u4eec\u5929\u738b\u95e8\u4e0b\u548c\u5185\u5bab\u4e00\u7cfb\uff0c\u5c31\u662f\u4e2a\u6700\u597d\u8bc1\u660e\u4e86\u3002\u201d \u3000\u3000\u201c\u636e\u6211\u6240\u77e5\uff0c\u4ed6\u4eec\u592b\u5987\u5931\u548c\uff0c\u522b\u6709\u539f\u56e0\u2026\u2026\u201d\u9752\u51e4\u53e4\u602a\u7684\u7b11\u4e00\u7b11\u53c8\u9053\uff1a\u201c\u4f60\u77e5\u9053\u4ed6\u4eec\u4e3a\u4ec0\u4e48\u53eb\u5929\u9a6c\u5802\u5417\uff1f\u8fd9\u540d\u53f7\u4e0d\u4f46\u6ca1\u6709\u9738\u6c14\uff0c\u4e5f\u7f3a\u5c11\u6c14\u52bf\uff1f\u201d \u3000\u3000\u201c\u4f60\u77e5\u9053\uff1f\u201d\u6c5f\u67ab\u7b11\u9053\uff1a\u201c\u4ee5\u4f60\u7684\u8eab\u4efd\u5730\u4f4d\u800c\u8a00\uff0c\u77e5\u9053\u7684\u771f\u8fd8\u4e0d\u5c11\uff1f\u201d \u3000\u3000\u201c\u522b\u5fd8\u4e86\u6211\u662f\u5929\u738b\u7684\u5c0f\u60c5\u4eba\u2026\u2026\u201d\u9752\u51e4\u82e6\u6da9\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u4ed6\u4e3a\u4eba\u867d\u7136\u8c28\u614e\uff0c\u4f46\u4e5f\u96be\u514d\u9152\u540e\u5931\u8a00\uff0c\u6211\u77e5\u9053\u8fd9\u4e48\u591a\u4e8b\u60c5\uff0c\u90fd\u662f\u65e0\u610f\u4e2d\u8bf4\u51fa\u6765\u7684\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u90a3\u4f60\u5c31\u8bf4\u8bf4\uff0c\u5929\u9a6c\u5802\u7684\u6765\u5386\u5427\uff1f\u4ed6\u4eec\u4e3a\u4ec0\u4e48\u4e0d\u53d6\u4e00\u4e2a\u6709\u6c14\u52bf\u7684\u540d\u5b57\uff1f\u201d \u3000\u3000\u201c\u5929\u9a6c\u6e21\u5371\u2026\u2026\u201d\u9752\u51e4\u8bf4\uff0c\u201c\u5929\u9a6c\u603b\u5802\u4e2d\u4eba\uff0c\u6709\u5f88\u591a\u90fd\u662f\u88ab\u6b66\u6797\u4e2d\u5404\u5927\u95e8\u6d3e\u56f4\u527f\u7684\u6076\u9b54\u51f6\u715e\uff0c\u5728\u5371\u6025\u4e2d\u88ab\u4eba\u89e3\u6551\uff0c\u4e58\u5929\u9a6c\u98de\u9a70\u800c\u53bb\uff0c\u6e21\u8fc7\u6b7b\u4ea1\u5371\u96be\u2026\u2026\u201d \u3000\u3000\u201c\u597d\u8ba1\u8c0b\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u8fd9\u4e9b\u4eba\u4ee5\u540e\u5c31\u82e6\u4e3a\u6240\u56f0\u4e86\u3002\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u6240\u4ee5\uff0c\u5929\u9a6c\u603b\u5802\u96c6\u805a\u4e86\u4e09\u5c71\u4e94\u5cb3\u7684\u4eba\u7269\uff0c\u4e94\u6e56\u56db\u6d77\u7684\u51f6\u715e\u2026\u2026\u201d\u9752\u51e4\u9053\uff1a\u201c\u4ed6\u4eec\u7ec4\u5408\u6210\u4f60\u6240\u8c13\u795e\u79d8\u529b\u91cf\u7684\u521d\u671f\u4e3b\u529b\u3002\u201d \u3000\u3000\u6c5f\u67ab\u957f\u957f\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u679c\u7136\u662f\u4e86\u4e0d\u8d77\u7684\u8bbe\u8ba1\u2026\u2026\u201d \u3000\u3000\u76b1\u7709\u6c89\u541f\uff0c\u826f\u4e45\u4e0d\u8bed\u3002 \u3000\u3000\u53ea\u89c1\u4ed6\u6ee1\u8138\u51dd\u91cd\uff0c\u4f3c\u662f\u5728\u96c6\u4e2d\u5168\u795e\uff0c\u601d\u7d22\u4e00\u4ef6\u91cd\u5927\u7684\u95ee\u9898\u3002 \u3000\u3000\u8fd9\u65f6\u523b\uff0c\u9752\u51e4\u5982\u82e5\u7a81\u7136\u51fa\u624b\uff0c\u5341\u4e4b\u516b\u4e5d\u6709\u70b9\u4e2d\u6c5f\u67ab\u7a74\u9053\u7684\u53ef\u80fd\u3002 \u3000\u3000\u5ff5\u5934\u5728\u9752\u51e4\u5fc3\u4e2d\u63a0\u8fc7\uff0c\u6697\u4e2d\u8fd0\u6c14\uff0c\u7a81\u7136\u53cc\u624b\u5e76\u51fa\uff0c\u70b9\u4e2d\u4e86\u6c5f\u67ab\u56db\u5904\u7a74\u9053\u3002 \u3000\u3000\u6c5f\u67ab\u5343\u601d\u4e07\u8651\uff0c\u4e5f\u6ca1\u6709\u60f3\u5230\u9752\u51e4\u7adf\u662f\u5982\u6b64\u5978\u8bc8\u7684\u4e00\u4e2a\u5973\u4eba\uff0c\u5b8c\u5168\u65e0\u6cd5\u9884\u9632\uff0c\u7adf\u88ab\u4e00\u4e0b\u5b50\u70b9\u4e2d\u4e86\u7a74\u9053\u3002 \u3000\u3000\u4e0d\u7981\u4e00\u5446\uff0c\u9053\uff1a\u201c\u51e4\u59d1\u5a18\uff0c\u597d\u9ad8\u660e\u7684\u88c5\u4f5c\uff0c\u533a\u533a\u4f69\u670d\u6781\u4e86\u3002\u201d \u3000\u3000\u201c\u6211\u544a\u8bc9\u4f60\u7684\u5168\u662f\u5b9e\u8bdd\u2026\u2026\u201d\u9752\u51e4\u5ae3\u7136\u4e00\u7b11\u53c8\u9053\uff1a\u201c\u50cf\u4f60\u7530\u5144\u8fd9\u4e48\u806a\u660e\u7684\u4eba\u7269\uff0c\u4e0d\u8bf4\u5b9e\u8bdd\uff0c\u5982\u4f55\u80fd\u53d6\u4fe1\u4e8e\u4f60\u3002\u201d \u3000\u3000\u201c\u90a3\u662f\u8bf4\u51e4\u59d1\u5a18\u65e9\u5c31\u6709\u51fa\u624b\u88ad\u51fb\u6211\u7684\u8ba1\u5212\u4e86\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u53ea\u662f\u5728\u7b49\u5f85\u8fd9\u4e00\u4e2a\u9002\u5f53\u7684\u65f6\u4faf\u3002\u201d \u3000\u3000\u201c\u4e0d\u662f\u2026\u2026\u201d\u9752\u98ce\u8bf4\uff0c\u201c\u51fa\u624b\u70b9\u4f60\u7a74\u9053\uff0c\u53ea\u662f\u4e00\u65f6\u8d77\u610f\uff0c\u5728\u5c0f\u59b9\u7684\u611f\u89c9\u4e2d\uff0c\u7530\u5144\u4f4f\u4f55\u65b9\u9762\u7684\u6210\u5c31\uff0c\u90fd\u53ef\u80fd\u9ad8\u8fc7\u5c0f\u59b9\u5f88\u591a\uff0c\u6240\u4ee5\uff0c\u6211\u4e0d\u80fd\u518d\u5192\u9669\u4e86\uff0c\u6211\u5df2\u7ecf\u4e24\u5ea6\u8d25\u5728\u4e86\u7530\u5144\u7684\u624b\u4e2d\uff0c\u4f7f\u6211\u611f\u89c9\u5230\u2018\u8ff7\u9b42\u5927\u6cd5\u2019\u5df2\u4e0d\u53ef\u6043\uff0c\u6240\u4ee5\uff0c\u53ea\u597d\u8d76\u5230\u8fd9\u4e2a\u673a\u4f1a\u70b9\u4f60\u7a74\u9053\u7684\u4e86\u3002\u201d \u3000\u3000\u6c5f\u67ab\u7b11\u9053\uff1a\u201c\u679c\u7136\u4e00\u51fb\u5f97\u624b\uff0c\u73b0\u5728\uff0c\u51e4\u59d1\u5a18\u51c6\u5907\u5982\u4f55\u5904\u7f6e\u5728\u4e0b\u5462\uff1f\u201d \u3000\u3000\u4ed6\u5185\u529f\u7cbe\u6df1\uff0c\u53e3\u4e2d\u867d\u7136\u8bf4\u8bdd\uff0c\u4f46\u4ecd\u80fd\u6697\u4e2d\u8fd0\u6c14\u51b2\u7a74\u3002\u5bf9\u8fd9\u79cd\u81ea\u6551\u7684\u6b66\u529f\uff0c\u6c5f\u67ab\u4e0b\u8fc7\u4e86\u4e00\u756a\u82e6\u529f\uff0c\u522b\u4eba\u8981\u4e00\u4e2a\u65f6\u8fb0\u7684\u65f6\u5149\uff0c\u4ed6\u53ea\u8981\u4e00\u523b\u5de5\u592b\u3002 \u3000\u3000\u201c\u4f60\u8bf4\u5462\u2026\u2026\u201d\u9752\u51e4\u7b11\u610f\u76c8\u76c8\u7684\u56de\u7b54\u7740\uff0c\u4eba\u5374\u5f00\u59cb\u884c\u52a8\uff0c\u5185\u52b2\u900f\u6307\u800c\u51fa\uff0c\u53c8\u70b9\u4e86\u6c5f\u67ab\u516d\u5904\u5927\u7a74\u3002 \u3000\u3000\u6c5f\u67ab\u6697\u6697\u53f9\u606f\u4e00\u58f0\uff0c\u653e\u5f03\u4e86\u6323\u624e\u7684\u5ff5\u5934\uff0c\u8fd9\u516d\u5904\u5927\u7a74\u53d7\u5236\uff0c\u4f7f\u6c5f\u67ab\u65e0\u6cd5\u518d\u63d0\u805a\u5185\u529f\uff0c\u5168\u8eab\u53d7\u5236\uff0c\u53ea\u7531\u4efb\u4eba\u5bb0\u5272\u7684\u4efd\u4e86\u3002 \u3000\u3000\u201c\u73b0\u5728\uff0c\u8be5\u5c0f\u59b9\u59b9\u95ee\u95ee\u4f60\u4e86\u2026\u2026\u201d\u9752\u51e4\u8bf4\uff0c\u201c\u5148\u544a\u8bc9\u6211\u4f60\u771f\u5b9e\u7684\u59d3\u540d\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u82e6\u7b11\u4e00\u4e0b\uff0c\u95ed\u76ee\u4e0d\u8bed\u3002 \u3000\u3000\u9752\u51e4\u6447\u6447\u5934\uff0c\u7b11\u9053\uff1a\u201c\u770b\u6765\uff0c\u4f60\u548c\u4e00\u822c\u4eba\u5e76\u65e0\u592a\u5927\u7684\u4e0d\u540c\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u970d\u7136\u7741\u5f00\u53cc\u76ee\uff0c\u9053\uff1a\u201c\u8fd9\u8bdd\u4ece\u4f55\u8bf4\u8d77\uff1f\u201d \u3000\u3000\u201c\u4e00\u822c\u6b66\u6797\u4e2d\u4eba\uff0c\u88ab\u64d2\u4e4b\u540e\uff0c\u4e5f\u548c\u7530\u5144\u4e00\u6837\uff0c\u95ed\u76ee\u7b49\u6b7b\uff0c\u597d\u50cf\u9887\u6709\u4e00\u526f\u82f1\u96c4\u6c14\u6982\u2026\u2026\u201d \u3000\u3000\u9752\u51e4\u7b11\u9053\uff1a\u201c\u6211\u5982\u54ac\u54ac\u7259\u6740\u4e86\u4f60\u2026\u2026\u201d \u3000\u3000\u201c\u8d25\u519b\u4e4b\u5c06\uff0c\u4e0d\u8db3\u8a00\u52c7\uff0c\u4e0d\u8bba\u4f60\u7528\u4ec0\u4e48\u624b\u6bb5\u5236\u670d\u4e86\u6211\uff0c\u4f46\u4f60\u603b\u662f\u8d62\u4e86\uff0c\u533a\u533a\u8d25\u7684\u4e0d\u7518\uff0c\u4f46\u4e5f\u53ea\u597d\u8ba4\u547d\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u6740\u5250\u4efb\u51ed\uff0c\u4f46\u5982\u8981\u533a\u533a\u5411\u59d1\u5a18\u6c42\u6055\u9976\u547d\uff0c\u90a3\u5c31\u8981\u4f60\u5931\u671b\u4e86\u3002\u201d \u3000\u3000\u201c\u6740\u4f60\u5417\uff1f\u4e0d\u8fc7\u662f\u4e3e\u624b\u4e4b\u52b3\u2026\u2026\u201d\u9752\u51e4\u9053\uff1a\u201c\u5c31\u7b97\u6211\u4e0d\u5fcd\u4e0b\u624b\uff0c\u4e5f\u53ef\u4ee5\u8981\u4eba\u4ee3\u52b3\uff0c\u53ef\u662f\uff0c\u4f60\u80f8\u4e2d\u7684\u62b1\u8d1f\u3001\u7406\u60f3\uff0c\u5c82\u4e0d\u8981\u5c3d\u4ed8\u6d41\u6c34\uff0c\u4eba\u554a\uff01\u4e0d\u7ba1\u5982\u4f55\u5f3a\u58ee\uff0c\u7cbe\u608d\uff0c\u6b7b\u4e86\u5c31\u53d8\u6210\u4e00\u573a\u7a7a\u5e7b\uff0c\u4f60\u53ef\u76f8\u4fe1\u4f60\u771f\u80fd\u5316\u4e3a\u5386\u9b3c\uff0c\u627e\u6211\u7d22\u547d\uff0c\u6240\u4ee5\uff0c\u4f60\u5e94\u8be5\u6539\u53d8\u4e00\u4e0b\u2026\u2026\u201d \u3000\u3000\u201c\u6539\u53d8\u2026\u2026\u201d \u3000\u3000\u201c\u662f\u554a\uff01\u81f3\u5c11\uff0c\u4e5f\u8be5\u5bf9\u6211\u865a\u4ee5\u59d4\u5a49\uff0c\u7559\u5f97\u9752\u5c71\u5728\uff0c\u90a3\u6015\u6ca1\u67f4\u70e7\u2026\u2026\u201d\u9752\u51e4\u8f7b\u8f7b\u5728\u6c5f\u67ab\u7684\u7751\u4e0a\u4eb2\u4e86\u4e00\u4e0b\uff0c\u63a5\u9053\uff1a\u201c\u50cf\u6211\u4e00\u6837\uff0c\u534a\u771f\u534a\u5047\u7684\u8bf4\u51fa\u4f60\u7684\u76ee\u7684\u3001\u7528\u5fc3\uff0c\u627e\u673a\u4f1a\u65bd\u4e8e\u53cd\u51fb\uff0c\u4e5f\u8bb8\uff0c\u4f60\u8fd8\u53ef\u4ee5\u53cd\u8d25\u4e3a\u80dc\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u542c\u5f97\u5fc3\u5934\u6cdb\u751f\u8d77\u9635\u9635\u7684\u5bd2\u610f\uff0c\u5fd6\u9053\uff1a\u201c\u6211\u81ea\u89c9\u673a\u8b66\uff0c\u5fc3\u4e2d\u4e5f\u4e00\u76f4\u5b58\u5728\u4ee5\u6bd2\u653b\u6bd2\u7684\u5ff5\u5934\uff0c\u4e34\u654c\u4e4b\u65f6\uff0c\u7adf\u662f\u5fc3\u5b58\u4e86\u5987\u4eba\u4e4b\u4ec1\u2026\u2026\u201d \u3000\u3000\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u53e3\u4e2d\u8bf4\u9053\uff1a\u201c\u59d1\u5a18\u60f3\u95ee\u4ec0\u4e48\u5462\uff1f\u201d \u3000\u3000\u201c\u5148\u8bf4\u4f60\u7684\u771f\u5b9e\u59d3\u540d\u5427\uff01\u201d \u3000\u3000\u201c\u597d\uff01\u6211\u59d3\u6c5f\uff0c\u5355\u540d\u4e00\u4e2a\u67ab\u5b57\u3002\u201d \u3000\u3000\u9752\u51e4\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u59d3\u540d\u4e0d\u9519\uff0c\u518d\u8bf4\u4f60\u6765\u6d1b\u9633\u5c45\u7684\u7528\u5fc3\u5427\uff1f\u201d \u3000\u3000\u201c\u60f3\u67e5\u51fa\u4f60\u4eec\u7684\u6765\u5386\uff0c\u5728\u4e0b\u521a\u624d\u95ee\u4f60\u7684\u4e8b\uff0c\u5c31\u662f\u6211\u6765\u6b64\u5730\u7684\u76ee\u7684\uff01\u201d \u3000\u3000\u9752\u51e4\u7b11\u9053\uff1a\u201c\u6211\u76f8\u4fe1\u4f60\u4e0d\u662f\u4e00\u4e2a\u4eba\u7684\u72ec\u811a\u5927\u76d7\u5427\uff1f\u2026\u2026\u201d\u9752\u51e4\u7a81\u7136\u6251\u5411\u6c5f\u67ab\uff0c\u7d27\u7d27\u62e5\u62b1\uff0c\u4e00\u9635\u72c2\u543b\u4e4b\u540e\uff0c\u624d\u4f4e\u58f0\u63a5\u4e0a\uff1a\u201c\u5bf9\u4e0d\u8d77\uff0c\u6211\u5e94\u8be5\u8bf4\u662f\u72ec\u884c\u5927\u4fa0\u624d\u5bf9\uff01\u201d \u3000\u3000\u201c\u4f60\u4e0d\u6015\u4f60\u7684\u4e3b\u4e0a\u53d1\u89c9\u4f60\u5982\u6b64\u8f7b\u72c2\uff0c\u4f1a\u628a\u4f60\u4f9d\u89c4\u5904\u7f6e\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u90a3\u65f6\uff0c\u51e4\u59d1\u5a18\u7684\u9645\u9047\uff0c\u53ea\u6015\u6bd4\u6211\u6c5f\u67d0\u4eba\u8fd8\u8981\u4e0d\u5982\u4e86\uff01\u201d \u3000\u3000\u201c\u60c5\u4e0d\u81ea\u7981\u554a\uff01\u6c5f\u67ab\uff0c\u4f60\u96be\u9053\u4e00\u70b9\u4e5f\u4e0d\u6e05\u695a\u81ea\u5df1\uff0c\u662f\u4e00\u4e2a\u4ee4\u5973\u4eba\u53d1\u72c2\u7684\u7537\u4eba\u2026\u2026\u201d\u9752\u51e4\u628a\u7751\u513f\u7d27\u8d34\u5728\u6c5f\u67ab\u7684\u8138\u4e0a\uff0c\u63a5\u9053\uff1a \u3000\u3000\u201c\u770b\u5230\u4f60\u8eab\u53d7\u7981\u5236\uff0c\u771f\u53eb\u4eba\u5fc3\u75bc\u5f97\u5f88\u3002\u201d \u3000\u3000\u5979\u8bf4\u7684\u771f\u60c5\u6d41\u9732\uff0c\u771f\u53eb\u4eba\u96be\u5206\u771f\u5047\u3002 \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u51e4\u59d1\u5a18\u8fd8\u6709\u70b9\u559c\u6b22\u5728\u4e0b\u4e86\uff1f\u201d \u3000\u3000\u201c\u4f55\u81f3\u662f\u559c\u6b22\u2026\u2026\u201d\u9752\u51e4\u9053\uff1a\u201c\u7b80\u76f4\u662f\u7231\u5f97\u53d1\u72c2\uff0c\u6c5f\u67ab\uff0c\u6211\u77e5\u9053\u4f60\u4fee\u4e60\u8fc7\u5f02\u672f\uff0c\u56e0\u4e3a\u4e0d\u7ba1\u5982\u4f55\u82f1\u4fca\uff0c\u591a\u624d\u7684\u7537\u4eba\uff0c\u4e5f\u65e0\u6cd5\u5728\u5f88\u77ed\u7684\u65f6\u95f4\u4e2d\uff0c\u7528\u4e00\u7f15\u60c5\u4e1d\uff0c\u5982\u6b64\u7d27\u7d27\u5728\u5973\u4eba\u7684\u5fc3\u4e0a\uff0c\u6211\u76f8\u4fe1\u6108\u63a5\u8fd1\u4f60\uff0c\u9694\u5165\u6108\u6df1\uff0c\u5c31\u50cf\u6211\u73b0\u5728\u4e00\u6837\uff0c\u5df2\u7ecf\u5feb\u63a7\u5236\u4e0d\u4f4f\u81ea\u5df1\u4e86\uff0c\u6240\u4ee5\uff0c\u6211\u8981\u800d\u70b9\u82b1\u6837\uff0c\u8bf7\u6c5f\u5144\u591a\u591a\u539f\u8c05\u3002\u201d \u3000\u3000\u6c5f\u67ab\u5fc3\u5934\u4e00\u8df3\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\u82b1\u6837\uff1f\u201d \u3000\u3000\u9752\u51e4\u60f3\u5230\u5c31\u505a\uff0c\u800c\u4e14\uff0c\u52a8\u4f5c\u5947\u5feb\uff0c\u63a2\u624b\u7531\u6000\u4e2d\u53d6\u51fa\u4e00\u65b9\u767d\u8272\u7684\u7ee2\u5e15\uff0c\u8d77\u8eab\u62ff\u8fc7\u4e00\u53ea\u7b14\uff0c\u4e00\u76d2\u6731\u7802\uff0c\u5728\u767d\u7ee2\u4e0a\u753b\u4e86\u4e00\u4e2a\u9b3c\u8138\uff0c\u7b11\u9053\uff1a\u201c\u5c0f\u5b69\u5b50\u7684\u73a9\u610f\uff0c\u4e0d\u8fc7\uff0c\u53ef\u80fd\u5f88\u6709\u7528\u3002\u201d \u3000\u3000\u628a\u767d\u7ee2\u8499\u5728\u6c5f\u67ab\u8138\u4e0a\uff0c\u7b11\u9053\uff1a\u201c\u6211\u628a\u4f60\u90a3\u53cc\u52fe\u4eba\u9b42\u9b44\u7684\u773c\u775b\u7ed9\u906e\u8d77\u6765\uff0c\u6c5f\u5144\uff0c\u8fd9\u6837\u4e5f\u8bb8\u6211\u5c31\u53ef\u4ee5\u514b\u5236\u81ea\u5df1\u4e86\u3002\u201d \u3000\u3000\u6c5f\u67ab\u6ca1\u6709\u52a8\uff0c\u90a3\u65b9\u7ee2\u5e15\uff0c\u53ea\u662f\u8f7b\u8986\u5728\u8138\u4e0a\uff0c\u53ea\u8981\u5439\u53e3\u6c14\uff0c\u5c31\u53ef\u4ee5\u628a\u5b83\u5439\u8d70\u3002 \u3000\u3000\u4f46\u5439\u6389\u7ee2\u5e15\uff0c\u8fd8\u4e0d\u77e5\u9053\u9752\u51e4\u4f1a\u800d\u51fa\u522b\u7684\u4ec0\u4e48\u82b1\u6837\uff0c\u6c5f\u67ab\u5185\u5fc3\u4e4b\u4e2d\uff0c\u5b9e\u5728\u6709\u70b9\u754f\u60e7\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u660e\u767d\uff0c\u73b0\u5728\u662f\u65e0\u6cd5\u53cd\u6297\uff0c\u4efb\u4f55\u6fc0\u6012\u9752\u51e4\u7684\u52a8\u4f5c\uff0c\u90fd\u662f\u81ea\u53d6\u5176\u8fb1\u3002 \u3000\u3000\u4f46\u95fb\u9752\u51e4\u5a07\u5a9a\u7684\u58f0\u97f3\uff0c\u4f20\u5165\u8033\u9645\uff0c\u9053\uff1a\u201c\u6c5f\u5144\uff0c\u5c0f\u59b9\u7684\u753b\u827a\u4e0d\u4f73\uff0c\u8fd9\u5f20\u7751\u5b9e\u5728\u662f\u753b\u5f97\u592a\u96be\u770b\u4e86\uff0c\u4e0d\u8fc7\uff0c\u5c0f\u59b9\u5012\u662f\u8f7b\u677e\u591a\u4e86\u3002\u201d \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u51e4\u59d1\u5a18\uff0c\u6700\u597d\u7684\u529e\u6cd5\uff0c\u90a3\u5c31\u662f\u628a\u6211\u6740\u4e86\uff0c\u6c38\u7edd\u540e\u60a3\uff0c\u5982\u679c\uff0c\u51e4\u59d1\u5a18\u5bf9\u6211\u6c5f\u67d0\u4eba\u771f\u6709\u4e00\u70b9\u597d\u611f\uff0c\u6700\u597d\u4e0d\u8981\u60f3\u5c3d\u529e\u6cd5\u7f9e\u8fb1\u6211\uff01\u201d \u3000\u3000\u201c\u8a00\u91cd\u4e86\u2026\u2026\u201d\u9752\u51e4\u6025\u6025\u8bf4\u9053\uff1a\u201c\u5c0f\u59b9\u7684\u8fd9\u4e00\u5207\uff0c\u90fd\u662f\u4e3a\u4e86\u81ea\u4fdd\u3002\u201d \u3000\u3000\u201c\u597d\u5427\uff01\u541b\u5b50\u7edd\u4ea4\u4e0d\u51fa\u6076\u8a00\uff0c\u51e4\u59d1\u5a18\u51c6\u5907\u5982\u4f55\u5904\u7f6e\u6c5f\u67d0\u4eba\u4e5f\u53ef\u4ee5\u4e0b\u624b\u4e86\u3002\u201d \u3000\u3000\u201c\u6c5f\u5144\uff0c\u53ef\u662f\u60f3\u542c\u542c\u5c0f\u59b9\u7684\u60f3\u6cd5\u3002\u5982\u662f\u6c5f\u5144\u4e0d\u6ee1\u610f\uff0c\u54b1\u4eec\u53ef\u4ee5\u5546\u91cf\u4fee\u8ba2\u3002\u201d \u3000\u3000\u53e3\u6c14\u5a49\u8f6c\uff0c\u58f0\u97f3\u751c\u7f8e\uff0c\u6709\u5982\u6df1\u95fa\u79c1\u8bed\uff0c\u6781\u5c3d\u5a07\u67d4\uff0c\u90a3\u91cc\u50cf\u548c\u654c\u4eba\u5728\u8c08\u8bba\u751f\u6b7b\u5927\u4e8b\u3002 \u3000\u3000\u6c5f\u67ab\u5fc3\u4e2d\u4e00\u52a8\uff0c\u5fd6\u9053\uff1a\u201c\u542c\u5979\u7684\u58f0\u97f3\u4e4b\u4e2d\u6e17\u5165\u5185\u529b\uff0c\u53c8\u60f3\u65bd\u5c55\u2018\u8ff7\u9b42\u672f\u2019\u5bf9\u4ed8\u6211\u4e86\uff0c\u6211\u5982\u88c5\u4f5c\u4e3a\u5979\u8ff7\u9b42\u5927\u6cd5\u6240\u5236\uff0c\u4e0d\u77e5\u4f1a\u662f\u600e\u6837\u4e00\u4e2a\u5c40\u9762\uff1f\u53ea\u6015\uff0c\u6211\u51e0\u5904\u8981\u7a74\u53d7\u5236\uff0c\u65e0\u6cd5\u8fd0\u529f\u81ea\u536b\uff0c\u4f1a\u4e0d\u4f1a\u771f\u7684\u6ca6\u5165\u9b54\u9053\uff0c\u4e3a\u5979\u6240\u5236\uff0c\u6210\u4e3a\u9752\u51e4\u88d9\u4e0b\u7684\u4e0d\u4e8c\u4e4b\u81e3\u2026\u2026\u201d \u3000\u3000\u6b64\u65f6\u6b64\u60c5\uff0c\u6c5f\u67ab\u7b2c\u4e8c\u5e0c\u671b\u662f\uff0c\u600e\u751f\u628a\u88ab\u64d2\u7684\u6d88\u606f\u4f20\u5165\u4e03\u5b9d\u548c\u5c1a\u8033\u4e2d\uff0c\u8fd9\u4e2a\u9152\u8089\u4e0d\u6212\u7684\u5927\u548c\u5c1a\uff0c\u6e38\u620f\u98ce\u5c18\uff0c\u80f8\u6000\u7985\u673a\uff0c\u5b9e\u53eb\u4eba\u96be\u6d4b\u9ad8\u6df1\uff0c\u6c5f\u67ab\u6df1\u4fe1\uff0c\u53ea\u8981\u6709\u4e00\u70b9\u793a\u8b66\uff0c\u5927\u548c\u6dcc\u5c31\u5168\u529b\u63a2\u7a76\uff0c\u8bbe\u6cd5\u6551\u63f4\u3002 \u3000\u3000\u5982\u679c\uff0c\u80fd\u628a\u8fd9\u6d88\u606f\u4f20\u7ed9\u6885\u82b1\uff0c\u8fd9\u4e2a\u5df2\u4e3a\u81ea\u5df1\u5f02\u672f\u5f81\u773c\uff0c\u9677\u8eab\u60c5\u7f51\u7684\u59d1\u5a18\uff0c\u53c8\u4f1a\u6709\u4ec0\u4e48\u884c\u52a8\u5462\uff1f \u3000\u3000\u6c5f\u67ab\u60f3\u5230\u4e86\u5f88\u591a\u4f20\u51fa\u6d88\u606f\u7684\u65b9\u6cd5\uff0c\u4f46\u65e0\u6cd5\u5728\u9752\u51e4\u7684\u95fa\u623f\u4e2d\u65bd\u7528\u2026\u2026\u3002 \u3000\u3000\u5728\u6c5f\u67ab\u7684\u7751\u4e0a\uff0c\u8986\u76d6\u4e86\u4e00\u5f20\u6731\u7802\u753b\u7684\u9b3c\u7751\uff0c\u9752\u51e4\u56fa\u7136\u53ef\u4ee5\u9003\u907f\u5f00\u6c5f\u67ab\u4e00\u53cc\u773c\u795e\uff0c\u4f46\u5979\u4e00\u6837\u770b\u4e0d\u5230\u6c5f\u67ab\u7684\u795e\u8272\u3001\u8868\u60c5\uff0c\u65e0\u6cd5\u63e3\u77e5\u4ed6\u5fc3\u4e2d\u7684\u53cd\u5e94\u3002 \u3000\u3000\u5bf9\u6c5f\u67ab\u8fd9\u6837\u4e00\u4e2a\u80f8\u85cf\u9526\u7ee3\uff0c\u65e0\u6cd5\u6349\u6478\u7684\u4eba\uff0c\u9752\u51e4\u7684\u4e00\u5207\u4e3e\u6b62\uff0c\u90fd\u6781\u5c3d\u5c0f\u5fc3\u4e4b\u80fd\u4e8b\u3002 \u3000\u3000\u5979\u7ad9\u8d77\u8eab\u5b50\uff0c\u53c8\u70b9\u4e86\u6c5f\u67ab\u4e24\u5904\u7a74\u9053\uff0c\u624d\u683c\u683c\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u7b2c\u4e00\u4e2a\u529e\u6cd5\uff0c\u662f\u628a\u6c5f\u5144\u89e3\u5929\u738b\u5904\u7f6e\uff0c\u4ed6\u4eec\u6740\u4f60\u3001\u5250\u4f60\uff0c\u5c0f\u59b9\u662f\u9650\u773c\u4e0d\u89c1\uff0c\u5fc3\u4e0d\u70e6\uff0c\u8fd9\u4e2a\u529e\u6cd5\uff0c\u6c5f\u5144\u662f\u5426\u540c\u610f\u5462\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u53c8\u88ab\u70b9\u4e86\u4e24\u5904\uff0c\u5fc3\u4e2d\u6012\u706b\u5982\u70e7\uff0c\u8fd9\u5973\u4eba\u7684\u8c28\u614e\u3001\u6076\u6bd2\uff0c\u5b9e\u662f\u70b9\u6ef4\u4e0d\u6f0f\uff0c\u60f3\u6765\uff0c\u4f20\u51fa\u8baf\u606f\u7684\u6253\u7b97\uff0c\u53ea\u6015\u4e5f\u5b8c\u5168\u7edd\u671b\u4e86\u3002 \u3000\u3000\u201c\u6c5f\u5144\u4e0d\u8bb2\u8bdd\uff0c\u5927\u6982\u662f\u4e0d\u540c\u610f\u7b2c\u4e00\u4e2a\u529e\u6cd5\u4e86\u2026\u2026\u201d\u9752\u51e4\u5a07\u58f0\u8bf4\u9053\uff1a\u201c\u7b2c\u4e8c\u4e2a\u529e\u6cd5\uff0c\u90a3\u5c31\u7b80\u5355\u591a\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u8fc7\u662f\u4e3e\u624b\u4e4b\u52b3\u2026\u2026\u201d\u6c5f\u67ab\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u751f\u6b7b\u7531\u547d\uff0c\u59d1\u5a18\u8bf7\u51fa\u624b\u5427\uff01\u201d \u3000\u3000\u201c\u771f\u662f\u5929\u5927\u7684\u8bef\u4f1a\u554a\uff01\u2026\u2026\u201d\u9752\u51e4\u9053\uff1a\u201c\u6211\u8981\u6740\u4f60\uff0c\u53c8\u4f55\u7528\u548c\u4f60\u591a\u8d39\u5507\u820c\u5462\uff1f\u201d \u3000\u3000\u201c\u6c5f\u67d0\u8ba4\u547d\u4e86\uff0c\u4f60\u9ad8\u5174\u600e\u4e48\u6298\u78e8\u6211\uff0c\u5c3d\u7ba1\u8bf7\u4fbf\uff01\u201d \u3000\u3000\u201c\u770b\u8d77\u6765\uff0c\u4f60\u5bf9\u5c0f\u59b9\u7684\u8bef\u4f1a\u5f88\u6df1\uff0c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u5f80\u597d\u5904\u60f3\u60f3\u5462\uff1f \u3000\u3000\u6211\u662f\u5728\u6253\u7b97\u653e\u4e86\u4f60\uff01\u201d \u3000\u3000\u201c\u653e\u4e86\u6211\uff1f\u201d \u3000\u3000\u201c\u662f\u554a\uff01\u53ea\u8981\u4f60\u80fd\u4f7f\u6211\u76f8\u4fe1\uff0c\u4e0d\u5bf9\u6211\u62a5\u590d\uff0c\u4eca\u751f\u4e00\u4e16\uff0c\u6c38\u4e0d\u548c\u6211\u4e3a\u654c\uff0c\u6211\u5c31\u7acb\u523b\u653e\u4e86\u4f60\uff01\u201d \u3000\u3000\u201c\u5728\u4e0b\u7684\u4fdd\u8bc1\uff0c\u51e4\u59d1\u5a18\u80af\u76f8\u4fe1\u5417\uff1f\u201d \u3000\u3000\u201c\u672c\u6765\uff0c\u6c5f\u6e56\u4e0a\u4e00\u8bfa\u5343\u91d1\uff0c\u5c0f\u59b9\u5e94\u8be5\u76f8\u4fe1\uff0c\u4e0d\u8fc7\uff0c\u5c0f\u59b9\u4e00\u8eab\u90aa\u6c14\uff0c\u4f60\u6c5f\u5144\u4e5f\u4e0d\u662f\u6b63\u4eba\u541b\u5b50\uff0c\u7537\u5978\u5973\u8bc8\uff0c\u53ea\u7528\u5634\u5df4\u8bf4\u8bf4\uff0c\u53eb\u5c0f\u59b9\u5982\u4f55\u76f8\u4fe1\uff1f\u201d \u3000\u3000\u201c\u51e4\u59d1\u5a18\u7684\u610f\u601d\u5462\uff1f\u201d \u3000\u3000\u201c\u6211\u8981\u4f60\u5199\u4e0b\u6587\u4e66\u2026\u2026\u201d\u9752\u51e4\u9053\uff1a\u201c\u767d\u7eb8\u9ed1\u5b57\uff0c\u518d\u52a0\u4e0a\u4eba\u8bc1\uff0c\u4f60\u8d56\u4e5f\u8d56\u4e0d\u6389\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u597d\uff01\u4f60\u89e3\u5f00\u6211\u53f3\u624b\u7a74\u9053\uff0c\u6211\u5c31\u5199\u4efd\u5951\u7ea6\u7ed9\u4f60\u2026\u2026\u201d \u3000\u3000\u9752\u51e4\u4f38\u624b\u53d6\u4e0b\u8499\u5728\u6c5f\u67ab\u8138\u4e0a\u7684\u767d\u7ee2\uff0c\u795e\u60c5\u8083\u7136\u7684\u9053\uff1a\u201c\u4f60\u542c\u7740\uff0c\u8fd8\u6709\u4e0b\u6587\u5462\uff1f\u201d \u3000\u3000\u201c\u8bf4\u4e0b\u53bb\uff0c\u60c5\u52bf\u8feb\u4eba\uff0c\u80fd\u591f\u7b54\u5e94\u7684\uff0c\u6211\u90fd\u7b54\u5e94\u3002\u201d \u3000\u3000\u201c\u5a36\u6211\u4e3a\u59bb\uff0c\u800c\u4e14\uff0c\u8981\u5199\u5728\u6587\u4e66\u4e0a\uff0c\u8fd8\u8981\u4f60\u4e00\u4e2a\u670b\u53cb\u4f5c\u8bc1\u4eba\u2026\u2026\u201d\u9752\u98ce\u8bf4\uff0c\u201c\u6211\u8981\u4ed6\u53c2\u52a0\u5a5a\u793c\uff0c\u770b\u7740\u4f60\u6211\u62dc\u5929\u5730\uff0c\u5f53\u7136\uff0c\u4e5f\u8bf7\u4ed6\u559d\u676f\u559c\u9152\u2026\u2026\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u2026\u2026\u201d\u6c5f\u67ab\u5927\u611f\u4e3a\u96be\u3002 \u3000\u3000\u201c\u4e0d\u613f\u610f\u662f\u4e0d\u662f\uff1f\u2026\u2026\u201d\u9752\u51e4\u51b7\u7b11\u4e00\u58f0\u53c8\u9053\uff1a\u201c\u6211\u77e5\u9053\uff0c\u4f60\u5acc\u6211\u5df2\u4e0d\u662f\u6e05\u767d\u4e4b\u8eab\uff0c\u5993\u5973\u90fd\u53ef\u4ee5\u4ece\u826f\uff0c\u4f55\u51b5\uff0c\u6211\u9752\u51e4\u53ea\u4ea4\u8fc7\u4e00\u4e2a\u7537\u4eba\uff0c\u800c\u4e14\uff0c\u8fd8\u662f\u88ab\u4ed6\u6043\u52bf\u903c\u597d\uff0c\u96be\u9053\u4f60\u4e00\u70b9\u90fd\u4e0d\u53ef\u601c\u6211\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u662f\u8bf4\u2026\u2026\u201d\u6c5f\u67ab\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u4f60\u662f\u5929\u738b\u7684\u5ba0\u59be\uff0c\u4ed6\u600e\u4e48\u4f1a\u653e\u4e86\u4f60\uff1f\u201d \u3000\u3000\u201c\u4f60\u5982\u662f\u5a36\u4e86\u6211\uff0c\u6211\u5c31\u662f\u4f60\u7684\u59bb\u5b50\uff0c\u7edd\u4e0d\u4f1a\u518d\u8ba9\u4efb\u4f55\u7537\u4eba\u78b0\u6211\u4e00\u4e0b\u3002\u201d \u3000\u3000\u201c\u4f60\u8981\u80cc\u53db\u5929\u738b\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u4ed6\u4f1a\u6d3e\u4eba\u8ffd\u6740\u4f60\uff01\u201d \u3000\u3000\u201c\u6240\u4ee5\uff0c\u6211\u4e00\u5b9a\u8981\u5ac1\u7ed9\u4f60\u624d\u884c\uff0c\u4f60\u603b\u4e0d\u80fd\u773c\u770b\u7740\u59bb\u5b50\u88ab\u6740\uff0c\u8896\u624b\u4e0d\u7ba1\u5427\uff01\u2026\u2026\u201d\u9752\u51e4\u9053\uff1a\u201c\u518d\u8bf4\uff0c\u5929\u6daf\u8fbd\u9614\uff0c\u6211\u4eec\u53ef\u4ee5\u627e\u4e2a\u5730\u65b9\u8eb2\u8d77\u6765\u3002\u201d \u3000\u3000\u201c\u7b2c\u4e00\uff0c\u6211\u4e0d\u4e00\u5b9a\u662f\u5929\u738b\u7684\u654c\u624b\u2026\u2026\u201d\u6c5f\u67ab\u8bf4\uff0c\u201c\u66f4\u65e0\u6cd5\u548c\u4f60\u9041\u8ff9\u5c71\u6797\uff0c\u201c\u6211\u660e\u767d\u4e86\u2026\u2026\u201d\u9752\u51e4\u7b11\u4e00\u7b11\u53c8\u9053\uff1a\u201c\u4f60\u80f8\u6000\u5927\u5fd7\uff0c\u8981\u5728\u6c5f\u6e56\u4e0a\u95ef\u756a\u4e8b\u4e1a\uff0c\u90a3\u5c31\u66f4\u9700\u8981\u6211\u7684\u5e2e\u52a9\u4e86\uff01\u201d \u3000\u3000\u6c5f\u67ab\u5fc3\u4e2d\u6709\u82e6\u96be\u8a00\uff0c\u6709\u4e9b\u4e8b\uff0c\u65e0\u6cd5\u8bf4\u7684\u592a\u660e\u663e\uff0c\u53ea\u597d\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u9752\u51e4\uff0c\u4f60\u7684\u59ff\u8272\u3001\u624d\u6167\uff0c\u90fd\u662f\u4eba\u95f4\u6781\u54c1\uff0c\u6574\u4e2a\u7684\u957f\u5b89\u57ce\uff0c\u88ab\u4f60\u5f15\u8bf1\u7684\u5982\u75f4\u5982\u72c2\uff0c\u4ed6\u4eec\u53ea\u8981\u80fd\u4e00\u4eb2\u82b3\u6cfd\uff0c\u5927\u6982\u8981\u4ed6\u4eec\u7acb\u523b\u53bb\u6b7b\uff0c\u4ed6\u4eec\u4e5f\u4f1a\u7b54\u5e94\u3002\u201d \u3000\u3000\u201c\u8bf4\u4e86\u534a\u5929\uff0c\u4f60\u8fd8\u662f\u4e0d\u7b54\u5e94\u4e86\u2026\u2026\u201d\u9752\u51e4\u8bf4\uff0c\u201c\u4f60\u8981\u5728\u6c5f\u6e56\u4e0a\u6709\u756a\u4f5c\u4e3a\uff0c\u5fc5\u9700\u8981\u95ef\u8fc7\u5929\u9a6c\u5802\u548c\u6211\u4eec\u8fd9\u4e2a\u7ec4\u5408\u4e24\u5927\u5173\u53e3\uff0c\u6211\u5bf9\u4f60\u7684\u5e2e\u52a9\uff0c\u90a3\u5c31\u4e0d\u662f\u6211\u7684\u59ff\u8272\u80fd\u591f\u6bd4\u62df\u4e86\uff0c\u6c5f\u5144\uff0c\u4f60\u5982\u4f55\u51b3\u5b9a\uff1f\u5c0f\u59b9\u6b63\u7b49\u5f85\u7b54\u590d\u5462\uff1f\u201d \u3000\u3000\u201c\u6211\u7b54\u590d\u4f60\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u6211\u662f\u4e00\u4e2a\u6d6a\u5b50\uff0c\u6240\u4ee5\uff0c\u6211\u4e0d\u5728\u4e4e\u4f60\u662f\u4e0d\u662f\u6e05\u767d\u7684\u8eab\u5b50\uff0c\u6211\u7531\u8877\u7684\u63a5\u7eb3\u4f60\uff0c\u4e5f\u613f\u610f\u627f\u8ba4\u4f60\u662f\u6211\u7684\u59bb\u5b50\uff0c\u6211\u53ef\u4ee5\u5199\u4e2a\u5b57\u636e\u7ed9\u4f60\uff0c\u6211\u4eec\u4e24\u4e2a\u4eba\uff0c\u4e5f\u53ef\u4ee5\u884c\u793c\u5982\u4eea\uff0c\u8fdb\u5165\u6d1e\u623f\uff0c\u4f46\u8981\u514d\u53bb\u6211\u670b\u53cb\u89c2\u793c\u8fd9\u4ef6\u4e8b\uff0c\u76ee\u4e0b\u7684\u60c5\u52bf\uff0c\u6211\u4eec\u4e0d\u80fd\u62db\u6447\uff0c\u4f60\u5982\u679c\u76f8\u4fe1\u6211\uff0c\u54b1\u4eec\u5c31\u4e00\u8a00\u4e3a\u5b9a\uff0c\u5982\u662f\u4e0d\u80af\u76f8\u4fe1\u6211\uff0c\u9752\u51e4\u4f60\u5c31\u6740\u4e86\u6211\u5427\uff01\u6211\u4e0d\u662f\u5f88\u6709\u5ea6\u91cf\u7684\u4eba\uff0c\u4e00\u65e6\u8131\u56f0\uff0c\u6211\u4eec\u62a5\u590d\u624b\u6bb5\uff0c\u5341\u5206\u6076\u6bd2\u2026\u2026\u201d \u3000\u3000\u201c\u770b\u6765\uff0c\u4f60\u771f\u7684\u5341\u5206\u4e3a\u96be\uff0c\u6587\u4e66\u4e5f\u4e0d\u7528\u5199\u4e86\uff0c\u53ea\u8981\u8d77\u4e2a\u8a93\uff0c\u6211\u7acb\u523b\u89e3\u4f60\u7a74\u9053\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u4e5f\u6709\u4e0b\u6587\uff0c\u4f60\u542c\u7740\uff0c\u4f60\u662f\u6211\u552f\u4e00\u7684\u59bb\u5b50\uff0c\u4f46\u4e0d\u80fd\u5e72\u9884\u6211\u548c\u522b\u7684\u5973\u4eba\u5f80\u6765\uff0c\u5f53\u7136\uff0c\u6709\u4e00\u5929\uff0c\u6c5f\u6e56\u5b89\u9759\u4e86\uff0c\u6211\u4eec\u90fd\u8fd8\u6d3b\u7740\u2026\u2026\u201d\u6c5f\u67ab\u8bf4\uff0c\u201c\u90a3\u65f6\uff0c\u6211\u5e26\u7740\u4f60\u606f\u9690\u5c71\u6797\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e00\u70b9\u4f60\u53ef\u4ee5\u653e\u5fc3\u2026\u2026\u201d\u9752\u51e4\u8bf4\uff0c\u201c\u4f60\u53ef\u4ee5\u6709\u5927\u5806\u59be\u5a62\uff0c\u4e5f\u53ef\u6709\u6570\u4e0d\u5b8c\u7684\u5973\u4eba\uff0c\u53ea\u8981\u5979\u4eec\u4e0d\u53d6\u4ee3\u6211\u6b63\u5ba4\u7684\u5730\u4f4d\uff0c\u6211\u90fd\u53ef\u4ee5\u5bb9\u5fcd\u2026\u2026\u201d \u3000\u3000\u201c\u9752\u51e4\uff0c\u6211\u8bf4\u7684\u6574\u4e2a\u8fc7\u7a0b\uff0c\u6211\u8981\u548c\u5f88\u591a\u5973\u4eba\u63a5\u89e6\uff0c\u5c24\u5176\u662f\u4f60\u4eec\u90a3\u4e2a\u7ec4\u5408\u4e2d\uff0c\u5973\u4eba\u7279\u591a\u2026\u2026\u201d\u6c5f\u67ab\u8bf4\uff0c\u201c\u6211\u5fc5\u987b\u548c\u5979\u4eec\u5468\u65cb\u3002\u201d \u3000\u3000\u201c\u5c31\u7b97\u662f\u6c5f\u6e56\u5e73\u9759\u4e86\uff0c\u4f60\u4e5f\u53ef\u4ee5\u5f04\u51e0\u4e2a\u7231\u59be\u2026\u2026\u201d\u9752\u51e4\u9053\uff1a\u201c\u517b\u4e00\u7fa4\u6b4c\u59ec\uff0c\u6211\u4e0d\u4f1a\u5403\u918b\uff0c\u66f4\u4e0d\u4f1a\u5e72\u6d89\u4f60\u3002\u201d \u3000\u3000\u201c\u8fd9\u2026\u2026\u9752\u51e4\uff0c\u6211\u6709\u70b9\u6000\u7591\u4e86\uff0c\u4f60\u662f\u4e0d\u662f\u771f\u7684\u559c\u6b22\u6211\u4e86\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u4e16\u4e0a\u4f1a\u6709\u5982\u6b64\u5927\u65b9\u7684\u59bb\u5b50\u5417\uff1f\u201d \u3000\u3000\u201c\u5509\uff01\u6709\u4e00\u4e2a\u6b63\u5ba4\u7684\u540d\u5206\uff0c\u6211\u5df2\u7ecf\u5f88\u6ee1\u8db3\u4e86\u2026\u2026\u201d\u9752\u51e4\u7b11\u9053\uff1a\u201c\u8fd9\u662f\u8865\u507f\u4f60\u5440\uff01\u56e0\u4e3a\uff0c\u4f60\u5a36\u7684\u8001\u5a46\u5df2\u975e\u5b8c\u58c1\uff0c\u5979\u5c06\u7528\u6700\u5927\u7684\u5bb9\u5fcd\uff0c\u4f5c\u4e00\u4e2a\u6700\u67d4\u987a\u7684\u59bb\u5b50\uff0c\u73b0\u5728\uff0c\u6211\u4eec\u90fd\u6ca1\u6709\u5947\u672f\u8ff7\u60d1\u5bf9\u65b9\uff0c\u8bf4\u7684\u8bdd\uff0c\u5b57\u5b57\u51fa\u81ea\u771f\u60c5\uff0c\u80ba\u8151\uff0c\u6c5f\u67ab\uff0c\u4e8b\u5b9e\u4e0a\uff0c\u6211\u6839\u672c\u4e0d\u77e5\u9053\uff0c\u6211\u80fd\u966a\u4f60\u591a\u4e45\uff0c\u4e5f\u8bb8\u4e00\u5e74\u534a\u8f7d\uff0c\u4e5f\u8bb8\u4e09\u4e94\u4e2a\u6708\uff0c\u6211\u5c31\u4f1a\u4e22\u4e86\u6027\u547d\uff0c\u73b0\u5728\uff0c\u6211\u89e3\u4f60\u7a74\u9053\u3002\u201d \u3000\u3000\u679c\u7136\uff0c\u4fa7\u8eab\u800c\u4e0a\uff0c\u89e3\u5f00\u4e86\u6c5f\u67ab\u516b\u5904\u8981\u7a74\u3002 \u3000\u3000\u6c5f\u67ab\u4e5f\u91cd\u4fe1\u8bfa\uff0c\u7adf\u7136\u771f\u7684\u8d77\u4e86\u91cd\u8a93\uff0c\u628a\u9752\u51e4\u89c6\u82e5\u552f\u4e00\u7684\u59bb\u5b50\u3002 \u3000\u3000\u4f46\u6c5f\u67ab\u4e5f\u8868\u73b0\u4e86\u4e16\u65e0\u5176\u5339\u7684\u7537\u4eba\u98ce\u5ea6\uff0c\u4ed6\u8981\u9752\u51e4\u4ecd\u7559\u4e0b\u6765\uff0c\u5171\u540c\u4e3a\u633d\u6551\u6c5f\u6e56\u5927\u52ab\u5c3d\u4efd\u5fc3\u529b\u3002 \u3000\u3000\u4f46\u9752\u51e4\u5374\u53d1\u4e86\u6101\uff0c\u8bf4\uff1a\u201c\u6211\u5982\u7559\u5728\u9752\u51e4\u6402\uff0c\u4e00\u65e6\u5929\u738b\u5230\u6b64\uff0c\u6211\u8981\u5982\u4f55\u5e94\u4ed8\uff1f\u201d \u3000\u3000\u201c\u50cf\u8fc7\u53bb\u4e00\u6837\uff0c\u9752\u51e4\uff0c\u6211\u4eec\u7684\u5a5a\u7ea6\uff0c\u6446\u5728\u5fc3\u91cc\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u7b49\u5927\u4e8b\u5e95\u5b9a\uff0c\u6211\u4eec\u518d\u8bcf\u544a\u6c5f\u6e56\uff0c\u6b63\u5f0f\u4e3e\u884c\u5a5a\u672d\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u884c\u554a\uff01\u4f60\u600e\u4e48\u80fd\u8ba9\u5168\u6c5f\u6e56\u4e2d\u4eba\u90fd\u77e5\u9053\uff0c\u4f60\u5a36\u4e86\u6211\u8fd9\u6837\u7684\u4eba\u2026\u2026\u201d \u3000\u3000\u201c\u4f60\u5f88\u597d\uff01\u6b4c\u58f0\u52a8\u957f\u5b89\uff0c\u4f7f\u767e\u4e07\u4eba\u4e3a\u4f60\u98ce\u9761\uff0c\u6211\u6c5f\u67ab\u5a36\u4e86\u8fd9\u4e48\u4e00\u4e2a\u8001\u5a46\uff0c\u8c01\u5fc3\u4e2d\u4e0d\u7fa1\u6155\u5462\uff1f\u201d \u3000\u3000\u201c\u4eba\u5bb6\u8bf4\u771f\u7684\uff0c\u4f60\u5374\u5728\u5f00\u73a9\u7b11\u2026\u2026\u201d\u9752\u51e4\u6101\u7709\u82e6\u8138\u7684\u8bf4\u3002 \u3000\u3000\u201c\u5a5a\u7ea6\u5df2\u5b9a\uff0c\u6211\u53c8\u8d77\u4e86\u91cd\u8b66\uff0c\u4f60\u53ef\u4e0d\u80fd\u53d8\u5366\u5440\uff01\u9752\u51e4\uff0c\u6211\u8bf4\u7684\u4e5f\u662f\u771f\u8bdd\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u5b57\u5b57\u51fa\u81ea\u8877\u8bda\u3002\u201d \u3000\u3000\u201c\u90a3\u662f\u4ee5\u540e\u7684\u4e8b\uff0c\u4ee5\u540e\u518d\u8bf4\u5427\uff01\u773c\u524d\u6700\u91cd\u8981\u7684\u662f\uff0c\u6211\u8981\u5982\u4f55\u5e94\u4ed8\u5929\u738b\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u8bf4\u8fc7\uff0c\u50cf\u8fc7\u53bb\u4e00\u6837\u554a\uff01\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u53ef\u4ee5\uff0c\u6c5f\u67ab\uff0c\u6211\u53ef\u4ee5\u6b7b\uff0c\u4e0d\u80fd\u8ba9\u4ed6\u518d\u78b0\u6211\u2026\u2026\u201d\u9752\u98ce\u795e\u60c5\u575a\u51b3\uff0c\u5927\u6709\u4e0d\u60dc\u4e00\u6b7b\u4e4b\u52bf\u3002 \u3000\u3000\u6c5f\u67ab\u5bdf\u989c\u89c2\u8272\uff0c\u5fc3\u77e5\u6b64\u523b\u4e0d\u5b9c\u518d\u52a8\uff0c\u7565\u4e00\u6c89\u541f\uff0c\u9053\uff1a\u201c\u54b1\u4eec\u90fd\u60f3\u60f3\u770b\uff0c\u80fd\u4e0d\u80fd\u627e\u51fa\u4e2a\u4e24\u5168\u5176\u7f8e\u7684\u6cd5\u5b50\u3002\u201d \u3000\u3000\u9752\u51e4\u7a81\u7136\u6251\u5165\u6c5f\u67ab\u6000\u91cc\uff0c\u4f4e\u58f0\u54ed\u6ce3\u8d77\u6765\uff0c\u4e00\u9762\u5e7d\u5e7d\u8bf4\u9053\uff1a\u201c\u6c5f\u67ab\uff0c\u5341\u4e5d\u5e74\u6765\uff0c\u6211\u4ece\u6ca1\u6709\u771f\u6b63\u7684\u5feb\u4e50\u8fc7\uff0c\u5b8c\u5168\u6d3b\u5728\u6028\u6068\u548c\u60b2\u75db\u4e4b\u4e2d\uff0c\u73b0\u5728\uff0c\u6211\u624d\u89c9\u7740\u81ea\u5df1\u662f\u4e2a\u4eba\uff0c\u6c5f\u90ce\uff0c\u4f60\u8981\u597d\u597d\u7684\u60dc\u7231\u6211\uff01\u201d \u3000\u3000\u201c\u6211\u4f1a\u7684\uff0c\u9752\u51e4\uff0c\u6211\u4f1a\u5168\u529b\u4ee5\u8d74\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u4ee5\u540e\uff0c\u6211\u4eec\u4f1a\u5e38\u76f8\u89c1\uff0c\u4e0d\u8fc7\uff0c\u738b\u5b37\u3001\u674e\u5b37\u90a3\u8fb9\uff0c\u4f60\u591a\u7528\u4e00\u70b9\u5de5\u592b\u3002\u201d \u3000\u3000\u201c\u5979\u4eec\u4e0d\u662f\u7eb5\u5bb9\u6211\u2026\u2026\u201d\u9752\u51e4\u8bf4\uff0c\u201c\u56e0\u4e3a\u5979\u4eec\u662f\u5929\u738b\u7684\u5fc3\u8179\uff0c\u6211\u4e0d\u77e5\u9053\u4f60\u662f\u5426\u5bf9\u5979\u4eec\u7528\u8fc7\u2018\u8ff7\u5fc3\u4e4b\u672f\u2019\uff0c\u5979\u4eec\u7adf\u7136\u5168\u5fc3\u5728\u64ae\u5408\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u4f60\u53ef\u5c0f\u5fc3\u554a\uff01\u5979\u4eec\u662f\u4e3a\u4e86\u4f60\uff0c\u2026\u2026\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u2026\u2026\u201d\u6c5f\u67ab\u662f\u771f\u7684\u6709\u4e9b\u6015\u4e86\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u5c0f\u51e4\uff0c\u6211\u770b\uff0c\u54b1\u4eec\u5f97\u6362\u4e2a\u5730\u65b9\u89c1\u4e86\uff1f\u201d \u3000\u3000\u201c\u4e0d\u7528\u592a\u62c5\u5fc3\u2026\u2026\u201d\u9752\u51e4\u9053\uff1a\u201c\u5979\u4eec\u7ec3\u7684\u706d\u7edd\u9b54\u529f\uff0c\u6574\u4e2a\u4eba\u7ec3\u7684\u6ca1\u6709\u4e86\u6027\u6b32\uff0c\u5979\u4eec\u53ef\u80fd\u771f\u662f\u662f\u559c\u6b22\u4f60\uff0c\u4f46\u4e5f\u53ea\u662f\u559c\u6b22\u7f62\u4e86\uff0c\u4e0d\u4f1a\u6709\u522b\u7684\u8981\u6c42\uff0c\u5979\u4eec\u8981\u7684\u662f\u5fc3\u7406\u4e0a\u7684\u6170\u85c9\uff0c\u4f60\u5634\u5df4\u751c\u4e00\u70b9\uff0c\u8ba9\u5979\u4eec\u62cd\u62cd\u6478\u6478\uff0c\u9876\u591a\u5427\uff01\u62b1\u4f60\u4e00\u4e0b\u3002\u2026\u2026\u201d \u3000\u3000\u201c\u8fd9\u50cf\u8bdd\u5417\uff01\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u90a3\u6211\u7b97\u662f\u4ec0\u4e48\u4eba\u54ea\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\u6cd5\u5b50\u554a\uff01\u6c5f\u90ce\uff0c\u505a\u5927\u4e8b\uff0c\u4e0d\u62d8\u5c0f\u8282\uff0c\u518d\u8bf4\uff0c\u8fd9\u6837\u505a\u4e5f\u662f\u5e2e\u6211\u7684\u5fd9\uff0c\u5982\u679c\uff0c\u738b\u5b37\u3001\u674e\u5b37\u4e0d\u914d\u5408\uff0c\u4e09\u65e5\u4e4b\u5185\uff0c\u5929\u738b\u5c31\u4f1a\u8d76\u5230\u957f\u5b89\uff0c\u4f55\u51b5\uff0c\u5979\u4eec\u6b66\u529f\u5947\u9ad8\uff0c\u628a\u5979\u4eec\u4e89\u53d6\u8fc7\u6765\uff0c\u5bf9\u4f60\u7684\u5e2e\u52a9\u4e5f\u5f88\u5927\u3002\u201d \u3000\u3000\u6c5f\u67ab\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u5f53\u5e74\u5e08\u957f\u4f20\u6211\u2018\u60d1\u5fc3\u672f\u2019\u65f6\uff0c\u544a\u8bc9\u6211\uff0c\u80fd\u4f7f\u6240\u6709\u63a5\u8fd1\u6211\u7684\u5973\u4eba\uff0c\u90fd\u4f1a\u4e3a\u6211\u9676\u9189\uff0c\u6211\u81ea\u5df1\u8fd8\u6709\u70b9\u98d8\u98d8\u7136\u7684\u611f\u89c9\uff0c\u60f3\u8d77\u4eca\u4e16\u7f8e\u5973\uff0c\u90fd\u7cfb\u6211\u4e00\u7f15\u60c5\u4e1d\uff0c\u524d\u65e0\u53e4\u4eba\uff0c\u5374\u60f3\u4e0d\u5230\uff0c\u6211\u7adf\u56e0\u4e60\u6b64\u672f\uff0c\u884c\u5982\u7537\u2026\u2026\u201d \u3000\u3000\u9752\u51e4\u7a81\u7136\u4f38\u624b\uff0c\u63a9\u4f4f\u4e86\u6c5f\u67ab\u7684\u5634\u5df4\uff0c\u9053\uff1a\u201c\u522b\u81ea\u8d1f\u592a\u6df1\uff0c\u4f60\u5982\u672a\u4e60\u6b64\u672f\uff0c\u53c8\u5982\u4f55\u80fd\u6297\u62d2\u6211\u7684\u8ff7\u9b42\u5927\u6cd5\uff0c\u4e5f\u4e0d\u4f1a\u5f97\u5230\u738b\u5b37\u3001\u674e\u5b37\u91cd\u987e\uff0c\u4f60\u53ef\u80fd\u88ab\u4eba\u6740\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u8bf4\u7684\u4e5f\u662f\uff0c\u6709\u5229\u6709\u5f0a\uff0c\u4e5f\u53ea\u6709\u4e24\u5bb3\u76f8\u6743\u53d6\u5176\u8f7b\u4e86\u3002\u201d \u3000\u3000\u201c\u8fd1\u767e\u5e74\u6765\u2018\u60d1\u5fc3\u672f\u2019\u53ea\u6709\u5929\u72d0\u592b\u4eba\u7684\u6700\u4e3a\u7cbe\u6e5b\uff0c\u6c5f\u90ce\u53ef\u662f\u8ddf\u5929\u72d0\u592b\u4eba\u5b66\u7684\uff1f\u201d \u3000\u3000\u201c\u662f\uff01\u5979\u662f\u6211\u5e08\u957f\u4e4b\u2014\u2026\u2026\u201d \u3000\u3000\u201c\u8fd9\u5c31\u96be\u602a\u4e86\u2026\u2026\u201d\u9752\u51e4\u8bf4\uff0c\u201c\u5f53\u5e74\uff0c\u6211\u4eec\u4fee\u4e60\u6b64\u672f\u65f6\uff0c\u4f20\u827a\u5e08\u957f\uff0c\u4e5f\u66fe\u7ecf\u63d0\u9192\u8fc7\u6211\u4eec\uff0c\u2018\u8ff7\u7470\u5927\u6cd5\u2019\u4e0d\u80fd\u6297\u62d2\u2018\u60d1\u5fc3\u672f\u2019\uff0c\u4e0d\u8fc7\u5929\u72d0\u592b\u4eba\u662f\u5973\u7684\uff0c\u600e\u4f1a\u6536\u4f60\u8fd9\u4e2a\u5c0f\u7537\u751f\u4f5c\u5f92\u5f1f\u3002\u201d \u3000\u3000\u201c\u5c0f\u51e4\uff0c\u5929\u72d0\u592b\u4eba\u662f\u81f3\u60c5\u81f3\u6027\u7684\u4eba\uff0c\u4e00\u7b11\u4e4b\u4e0b\uff0c\u529b\u80fd\u98a0\u5012\u4f17\u751f\uff0c\u6240\u4ee5\uff0c\u5979\u4ee5\u540e\u5c31\u6c38\u4e0d\u518d\u7b11\uff0c\u4f60\u60f3\u60f3\u770b\uff0c\u4e00\u4e2a\u8981\u5fcd\u4f4f\u6570\u5341\u5e74\u4e0d\u7b11\uff0c\u662f\u591a\u4e48\u56f0\u96be\u7684\u4e8b\u3002\u201d \u3000\u3000\u201c\u662f\uff01\u5979\u662f\u5929\u72d0\u4ed9\u5b50\uff0c\u6211\u662f\u5c0f\u5996\u5973\uff0c\u6210\u4e86\u5427\uff01\u6211\u662f\u65e0\u610f\u7684\uff0c\u4f60\u4e0d\u8981\u751f\u6c14\u554a\uff01\u201d \u3000\u3000\u201c\u5509\uff01\u6211\u600e\u4f1a\u751f\u6c14\uff0c\u6211\u53ea\u662f\u544a\u8bc9\u4f60\u5979\u7684\u4f1f\u5927\uff0c\u5c0f\u51e4\uff0c\u6211\u4eec\u8981\u5b66\u5979\u8001\u4eba\u5bb6\u2026\u2026\u201d\u6c5f\u67ab\u8bf4\uff0c\u201c\u672f\u65e0\u90aa\u6b63\uff0c\u7528\u4e4b\u6b63\u9053\u5219\u6b63\u3002\u201d \u3000\u3000\u201c\u77e5\u9053\u4e86\uff0c\u592b\u541b\uff0c\u4e3a\u59bb\u5e2e\u4f60\u63d0\u6c34\u6d17\u6fa1\uff0c\u7b97\u7f5a\u6211\u4e0d\u656c\u4e4b\u7f6a\uff0c\u597d\u5417\uff1f\u201d \u3000\u3000\u5979\u6ca1\u6709\u518d\u65bd\u5f02\u672f\uff0c\u5374\u5b8c\u5b8c\u5168\u5168\u662f\u4e2a\u5c11\u5973\u7684\u5a07\u4fcf\u6d3b\u6cfc\uff0c\u6c5f\u67ab\u4e3a\u4e4b\u5fc3\u52a8\u3002 \u3000\u3000\u5979\u6781\u5c3d\u4eba\u5987\u4e4b\u9053\uff0c\u4e3a\u6c5f\u67ab\u5bbd\u8863\u89e3\u5e26\uff0c\u8131\u9774\u64e6\u80cc\uff0c\u4f3a\u5019\u7684\u65e0\u5fae\u4e0d\u81f3\u3002 \u3000\u3000\u6625\u5bb5\u5e72\u91d1\uff0c\u7f57\u5e10\u60c5\u6df1\uff0c\u9752\u51e4\u65bd\u5c55\u51fa\u6df7\u8eab\u89e3\u6570\uff0c\u9c7c\u9f99\u6f2b\u884d\uff0c\u6781\u5c3d\u5a07\u67d4\u2026\u2026\u3002 \u3000\u3000\u8fd9\u4e00\u89c9\uff0c\u76f4\u7761\u5230\u65e5\u4e0a\u4e09\u7aff\u3002 \u3000\u3000\u4e24\u4eba\u8d77\u5e8a\u9488\u5bf9\u6e05\u6d17\u4e4b\u540e\uff0c\u738b\u5b37\u3001\u674e\u5b37\u65e9\u5df2\u7ecf\u5907\u597d\u65e9\u70b9\uff0c\u5165\u5ba4\u76f8\u8bf7\u3002 \u3000\u3000\u559d\uff01\u8fd9\u4e24\u4e2a\u4e2d\u5e74\u5987\u4eba\uff0c\u7adf\u7136\u4e5f\u523b\u610f\u7684\u4fee\u9970\u4e00\u756a\uff0c\u9ed1\u53d1\u6cb9\u5149\uff0c\u8102\u7c89\u8584\u65bd\uff0c\u4e24\u5f20\u767d\u900f\u7ea2\u7684\u8138\u4e0a\uff0c\u4e00\u76f4\u6302\u7740\u8ff7\u4eba\u7684\u7b11\u610f\u3002 \u3000\u3000\u5947\u602a\u5440\uff01\u522b\u4eba\u6210\u5176\u597d\u4e8b\uff0c\u4e5f\u4e0d\u77e5\u9053\u5979\u4eec\u9ad8\u5174\u4e2a\u4ec0\u4e48\u52b2\u5934\u3002 \u3000\u3000\u770b\u5f97\u51fa\u4e24\u4e2a\u4eba\u5bf9\u6c5f\u67ab\u7279\u522b\u6bb7\u52e4\uff0c\u5c31\u50cf\u4e24\u4e2a\u6148\u6bcd\u8fce\u63a5\u5230\u4e07\u91cc\u5f52\u6765\u7684\u7231\u5b50\u4e00\u822c\u3002 \u3000\u3000\u4e0d\u5bf9\u5440\uff01\u5988\u5988\u5bf9\u513f\u5b50\uff0c\u4e0d\u50cf\u8fd9\u79cd\u6001\u5ea6\uff0c\u884c\u6b62\u95f4\u6709\u70b9\u6696\u5473\uff0c\u6478\u4e00\u628a\uff0c\u78b0\u4e00\u4e0b\uff0c\u5c31\u5dee\u6ca1\u6709\u62b1\u8fc7\u6765\u4eb2\u4e00\u4e0b\u3002 \u3000\u3000\u6c5f\u67ab\u6628\u591c\u6795\u7554\uff0c\u53d7\u4e86\u5bc6\u6559\uff0c\u8981\u5e94\u916c\u8fd9\u4e24\u4e2a\u4e2d\u5e74\u5987\u4eba\uff0c\u6240\u4ee5\uff0c\u6c5f\u67ab\u5fcd\u53d7\u7740\u5979\u4eec\u7684\u6298\u817e\uff0c\u8fd8\u8981\u9762\u5e26\u7b11\u5bb9\uff0c\u656c\u8c28\u63a5\u53d7\u3002 \u3000\u3000\u5403\u8fc7\u65e9\u9910\uff0c\u6c5f\u67ab\u544a\u8f9e\u79bb\u53bb\uff0c\u9752\u51e4\u5374\u7a81\u7136\u6251\u5230\u5728\u5730\uff0c\u8dea\u5728\u738b\u5b37\u3001\u674e\u5b37\u9762\u524d\uff0c\u63a9\u9762\u4f4e\u6ce3\u3002 \u3000\u3000\u4e24\u4e2a\u8001\u5988\u5b50\u5446\u4e86\u4e00\u5446\uff0c\u540c\u65f6\u4f38\u624b\uff0c\u6276\u8d77\u4e86\u9752\u51e4\uff0c\u738b\u5b37\u9053\uff1a\u201c\u51e4\u59d1\u5a18\uff0c\u6298\u6740\u6211\u4eec\u4e86\uff0c\u4ec0\u4e48\u4e8b\uff0c\u4f60\u53ea\u7ba1\u5429\u5490\u4e0b\u6765\u5c31\u662f\uff0c\u6211\u4eec\u529b\u6240\u80fd\u53ca\uff0c\u51b3\u4e0d\u63a8\u8f9e\u3002\u201d \u3000\u3000\u738b\u5b37\u3001\u674e\u5b37\u867d\u662f\u53d7\u547d\u76d1\u89c6\u9752\u51e4\uff0c\u4f46\u7a76\u7adf\u662f\u4e3b\u4ec6\u7684\u540d\u5206\uff0c\u9752\u51e4\u8fd9\u4e00\u62db\u5389\u5bb3\u65e0\u6bd4\uff0c\u628a\u4e24\u4e2a\u8001\u7684\u6298\u817e\u7684\u624b\u5fd9\u811a\u4e71\u3002 \u3000\u3000\u9752\u51e4\u4e00\u9762\u54ed\uff0c\u4e00\u9762\u8bf4\u9053\uff1a\u201c\u4e24\u4f4d\u5b37\u5b37\uff0c\u89c6\u51e4\u513f\u6709\u5982\u81ea\u5df1\u7684\u5973\u513f\u4e00\u822c\uff0c\u53d7\u987e\u60c5\u6df1\uff0c\u51e4\u513f\u4e5f\u628a\u4e24\u4f4d\u5f53\u5a18\u4eb2\u4e00\u770b\u5f85\u3002\u201d \u3000\u3000\u597d\u4e00\u9876\u9ad8\u5e3d\uff0c\u91cd\u903e\u5e72\u65a4\uff0c\u538b\u5f97\u4e24\u4e2a\u7edd\u60c5\u65ad\u4e49\u7684\u8001\u5988\u5b50\uff0c\u6447\u6447\u6b32\u5012\uff0c\u5374\u53c8\u5fc3\u5934\u8212\u5766\u3002 \u3000\u3000\u201c\u51e4\u59d1\u5a18\u2026\u2026\u201d\u674e\u5b37\u8bf4\uff0c\u201c\u4e0d\u8981\u54ed\uff0c\u4f60\u6709\u4ec0\u4e48\u56f0\u96be\uff0c\u544a\u8bc9\u6211\u548c\u738b\u5b37\u3002\u6c34\u91cc\u6c34\u91cc\u53bb\uff0c\u706b\u91cc\u706b\u91cc\u884c\uff0c\u5c31\u7b97\u5929\u584c\u4e86\uff0c\u6709\u6211\u4eec\u4e24\u4e2a\u5148\u9876\u7740\uff0c\u4e56\u51e4\u513f\uff0c\u522b\u54ed\u4e86\uff0c\u4f60\u8fd9\u4e48\u4e00\u54ed\uff0c\u6211\u4eec\u4e5f\u662f\u96be\u8fc7\u7684\u5f88\u54ea\uff01\u201d \u3000\u3000\u201c\u4e24\u4f4d\u5b37\u5b37\uff0c\u51e4\u513f\u53d7\u547d\uff0c\u4e0d\u6562\u8fdd\u80cc\uff0c\u6628\u591c\u7559\u4ed6\u4e00\u5bb5\uff0c\u4f46\u6211\u8d8a\u60f3\u8d8a\u6015\u5440\uff01\u201d \u3000\u3000\u5389\u5bb3\u5440\uff01\u5389\u5bb3\uff0c\u628a\u8fd9\u5077\u4eba\u7559\u5ba2\u7684\u5927\u5e3d\u5b50\uff0c\u4e00\u53e4\u8111\u5168\u6263\u5230\u4e24\u4e2a\u8001\u5988\u5b50\u7684\u5934\u4e0a\u3002 \u3000\u3000\u738b\u5b37\u3001\u674e\u5b37\uff0c\u76f8\u4e92\u770b\u4e86\u4e00\u773c\uff0c\u9053\uff1a\u201c\u6211\u4eec\u540c\u610f\u4e86\uff0c\u4f60\u8fd8\u6709\u4ec0\u4e48\u597d\u6015\u7684\uff01\u201d \u3000\u3000\u53e3\u6c14\u5f88\u5927\uff0c\u4f46\u5374\u6ce5\u8db3\u6df1\u9677\uff0c\u6b32\u62d4\u4e0d\u80fd\u4e86\u3002 \u3000\u3000\u201c\u51e4\u513f\u662f\u62c5\u5fc3\uff0c\u8fd9\u4ef6\u4e8b\u5982\u82e5\u88ab\u5929\u738b\u77e5\u9053\u4e86\uff0c\u4ed6\u4e0d\u628a\u51e4\u513f\u5343\u5200\u4e07\u5250\u624d\u602a\uff1f\u201d \u3000\u3000\u738b\u5b37\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u77e5\u9053\u4e86\uff0c\u662f\u6709\u70b9\u9ebb\u70e6\uff0c\u4e0d\u8fc7\uff0c\u8fd9\u4ef6\u4e8b\uff0c\u53ea\u6709\u6211\u4eec\u4e09\u4e2a\u4eba\u77e5\u9053\uff0c\u4ed6\u53c8\u4e0d\u4f1a\u9634\u9633\u516b\u5366\uff0c\u7b97\u51fa\u6765\u6628\u591c\u4e8b\uff1f\u201d \u3000\u3000\u201c\u662f\u554a\uff01\u6211\u4e09\u4e2a\u4eba\u90fd\u4e0d\u8bf4\uff0c\u4ed6\u53c8\u600e\u4f1a\u77e5\u9053\u5462\uff1f\u201d\u674e\u5b37\u8bf4\uff0c\u201c\u8fd9\u79cd\u4e8b\uff0c\u4f60\u4e0d\u8ba4\u5e10\uff0c\u6211\u4eec\u4ec0\u4e48\u90fd\u6ca1\u6709\u770b\u5230\uff0c\u4ed6\u5c31\u542c\u5230\u4ec0\u4e48\u95f2\u8bdd\uff0c\u4e5f\u662f\u67e5\u65e0\u5b9e\u636e\u554a\uff01\u201d \u3000\u3000\u201c\u53ef\u662f\u2026\u2026\u53ef\u662f\uff0c\u4ee5\u540e\u2026\u2026\u201d \u3000\u3000\u9752\u51e4\u9e23\u54bd\u7740\u8bf4\u4e0d\u4e0b\u53bb\u3002 \u3000\u3000\u201c\u51e4\u59d1\u5a18\uff0c\u4f60\u4e00\u5411\u8bf4\u8bdd\uff0c\u7b80\u5355\u660e\u4e86\u2026\u2026\u201d\u738b\u5b37\u8bf4\uff0c\u201c\u4e43\u4e00\u65b9\u9886\u8896\u4e4b\u624d\uff0c\u4eca\u65e5\u600e\u4e48\u541e\u541e\u5410\u5410\uff0c\u771f\u628a\u8001\u8eab\u6025\u6b7b\u4e86\u3002\u201d \u3000\u3000\u201c\u6015\u4ec0\u4e48\uff1f\u6211\u4eec\u4e09\u4eba\u4e00\u5fc3\uff0c\u5176\u5229\u65ad\u91d1\uff0c\u4e0d\u7528\u987e\u8651\u4ec0\u4e48\uff1f\u2026\u2026\u201d \u3000\u3000\u674e\u5b37\u9053\uff1a\u201c\u6709\u8bdd\u5c31\u660e\u767d\u8bf4\u554a\uff01\u201d \u3000\u3000\u201c\u51e4\u513f\u662f\u8bf4\uff0c\u522b\u8ba9\u4ed6\u518d\u6765\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u4f60\u662f\u6307\u6c5f\u516c\u5b50\u554a\uff01\u2026\u2026\u201d\u738b\u5a5a\u6c89\u58f0\u7684\u8bf4\u3002 \u3000\u3000\u539f\u6765\uff0c\u6c5f\u67ab\u5df2\u544a\u8bc9\u4e24\u4eba\u81ea\u5df1\u771f\u6b63\u59d3\u540d\u3002 \u3000\u3000\u9752\u51e4\u70b9\u70b9\u5934\u3002 \u3000\u3000\u201c\u4f60\u820d\u5f97\u554a\uff01\u51e4\u59d1\u5a18\u2026\u2026\u201d\u674e\u5b37\u8bf4\uff0c\u201c\u771f\u80fd\u5fcd\u4f4f\u4e0d\u60f3\u4ed6\uff01\u201d \u3000\u3000\u201c\u60f3\u662f\u60f3\uff0c\u53ef\u662f\u6027\u547d\u8981\u7d27\u554a\uff01\u2026\u2026\u201d\u9752\u51e4\u8bf4\uff0c\u201c\u51e4\u513f\u6b7b\u4e0d\u8db3\u60dc\uff0c\u53ea\u6015\u8fde\u7d2f\u4e86\u4e24\u4f4d\u5b37\u5b37\uff0c\u6211\u5c31\u4e07\u6b7b\u83ab\u8d4e\u4e86\u3002\u201d \u3000\u3000\u738b\u5b37\u6c89\u541f\u4e0d\u8bed\uff0c\u8138\u4e0a\u7684\u795e\u60c5\uff0c\u4e0d\u505c\u7684\u53d8\u5316\uff0c\u663e\u7136\uff0c\u5979\u5185\u5fc3\u4e4b\u4e2d\uff0c\u6b63\u5728\u4f5c\u6fc0\u70c8\u7684\u6323\u624e\u3002 \u3000\u3000\u826f\u4e45\u4e4b\u540e\uff0c\u738b\u5b37\u7a81\u7136\u4e00\u54ac\u7259\uff0c\u9053\uff1a\u201c\u674e\u5b37\u4f60\u600e\u4e48\u8bf4\uff1f\u201d \u3000\u3000\u201c\u6211\u542c\u4f60\u7684\uff01\u201d\u674e\u5b37\u7b54\u590d\u7684\u5f88\u5e72\u8106\u3002 \u3000\u3000\u201c\u51e4\u59d1\u5a18\u2026\u2026\u201d \u3000\u3000\u201c\u522b\u53eb\u6211\u59d1\u5a18\uff0c\u7531\u4eca\u65e5\u8d77\uff0c\u6211\u5c31\u62dc\u5728\u4e24\u4f4d\u819d\u4e0b\uff0c\u4eca\u540e\uff0c\u54b1\u4eec\u4ee5\u6bcd\u5973\u76f8\u79f0\u3002\u201d \u3000\u3000\u738b\u5b37\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u8fd9\u4e2a\u8001\u8eab\u5982\u4f55\u6562\u5f53\uff01\u201d \u3000\u3000\u6253\u94c1\u8d81\u70ed\uff0c\u9752\u51e4\u7acb\u523b\u62dc\u4e86\u4e0b\u53bb\uff0c\u9053\uff1a\u201c\u4e49\u6bcd\u3001\u59e8\u5a18\uff0c\u8bf7\u53d7\u5973\u513f\u4e00\u62dc\u3002\u201d \u3000\u3000\u9752\u51e4\u7acb\u523b\u5206\u522b\u5bf9\u4e24\u4eba\u884c\u4e86\u5927\u793c\u3002 \u3000\u3000\u738b\u5b37\u3001\u674e\u5b37\uff0c\u5fc3\u4e2d\u662f\u53c8\u9ad8\u5174\uff0c\u53c8\u662f\u7d27\u5f20\uff0c\u7cca\u7cca\u6d82\u5f90\u7684\u80a9\u4e0a\u4e86\u5343\u65a4\u91cd\u62c5\u3002 \u3000\u3000\u6276\u8d77\u9752\u51e4\uff0c\u738b\u5b37\u9762\u5e26\u6101\u82e6\u7684\u8bf4\uff0c\u201c\u4e0d\u6015\u4e00\u4e07\uff0c\u5c31\u6015\u4e07\u4e00\uff0c\u6c5f\u516c\u5b50\u53ea\u6b64\u4e00\u906d\uff0c\u8001\u8eab\u76f8\u4fe1\u662f\u7edd\u4e0d\u4f1a\u6709\u4ec0\u4e48\u98ce\u58f0\u6f0f\u51fa\u53bb\uff0c\u4f46\u5982\u6b64\u5e38\u76f8\u5f80\u6765\uff0c\u90a3\u5c31\u96be\u4ee5\u63a9\u4eba\u8033\u76ee\u2026\u2026\u201d \u3000\u3000\u201c\u603b\u4e0d\u80fd\u8ba9\u4ed6\u4eec\u4e24\u5730\u76f8\u601d\u2026\u2026\u201d\u674e\u5b37\u8bf4\uff0c\u201c\u90a3\u54b1\u4eec\u4e0d\u662f\u7ed9\u51e4\u513f\u5e2e\u5fd9\uff0c\u662f\u5bb3\u4e86\u5979\u4e86\u3002\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\uff0c\u6240\u4ee5\uff0c\u6211\u5728\u60f3\u2026\u2026\u201d\u738b\u5b37\u8bf4\uff0c\u6709\u6ca1\u6709\u4ec0\u4e48\u4e24\u5168\u5176\u7f8e\u7684\u529e\u6cd5\uff1f\u201d \u3000\u3000\u201c\u5509\uff01\u4e49\u6bcd\uff0c\u59e8\u5a18\u5982\u6b64\u7684\u715e\u8d39\u82e6\u5fc3\uff0c\u51e4\u513f\u4e00\u5b9a\u8981\u544a\u8bc9\u6c5f\u90ce\uff0c\u8981\u4ed6\u597d\u597d\u7684\u5b5d\u987a\u4e24\u4f4d\u3002\u201d \u3000\u3000\u738b\u5b37\u3001\u674e\u5b37\uff0c\u4e24\u4eba\u8138\u4e0a\u90fd\u6cdb\u8d77\u6ee1\u610f\u7684\u7b11\u5bb9\u3002 \u3000\u3000\u9752\u51e4\u671b\u7740\u738b\u5b37\u9053\uff1a\u201c\u4e49\u6bcd\uff0c\u5929\u738b\u7684\u813e\u6c14\u4f60\u5f88\u6e05\u695a\uff0c\u4ed6\u53ef\u662f\u5b81\u53ef\u9519\u6740\u4e00\u4e07\uff0c\u4e5f\u4e0d\u653e\u8fc7\u4e00\u4e2a\u53ef\u7591\u7684\u4eba\uff0c\u53ea\u8981\u4ed6\u542c\u5230\u98ce\u58f0\uff0c\u4ed6\u4e00\u5b9a\u4f1a\u5bf9\u6211\u4e0b\u624b\u3002\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u90a3\u65f6\u2026\u2026\u201d\u674e\u5b37\u8bf4\uff0c\u201c\u53ea\u6015\u8fde\u6211\u4eec\u4e5f\u4e0d\u53ef\u653e\u8fc7\u3002\u201d \u3000\u3000\u201c\u5bf9\uff01\u201d\u738b\u5b37\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\uff0c\u54b1\u4eec\u8981\u65e9\u4e9b\u51c6\u5907\u3002\u201d \u3000\u3000\u201c\u600e\u4e48\u51c6\u5907\u5462\uff1f\u201d\u9752\u51e4\u671b\u7740\u674e\u5b37\u9053\uff1a\u201c\u59e8\u5a18\u53ef\u6709\u9ad8\u89c1\uff1f\u201d \u3000\u3000\u674e\u5b37\u9053\uff1a\u201c\u4e5f\u4e0d\u7528\u592a\u62c5\u5fc3\uff0c\u8fd9\u4ef6\u4e8b\uff0c\u8ba9\u6211\u8ddf\u4f60\u4e49\u6bcd\u5408\u8ba1\u5408\u8ba1\u518d\u8bf4\uff0c\u603b\u4f1a\u627e\u51fa\u4e00\u4e2a\u65b9\u6cd5\u51fa\u6765\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u59e8\u5a18\u548c\u4e49\u6bcd\u5546\u91cf\u5546\u91cf\uff0c\u51e4\u513f\u4e0d\u6253\u6270\u4e86\uff0c\u6211\u4e5f\u8981\u4ed4\u7ec6\u7684\u60f3\u4e00\u60f3\u2026\u2026\u201d\u9752\u51e4\u8bf4\uff0c\u201c\u770b\u770b\u80fd\u4e0d\u80fd\u60f3\u51fa\u5584\u7b56\u3002\u201d \u3000\u3000\u201c\u51e4\u513f\u2026\u2026\u201d\u738b\u5b37\u8bf4\uff0c\u201c\u4e0d\u8981\u5927\u52b3\u795e\u4e86\uff0c\u4eca\u5929\u8fd8\u8981\u767b\u53f0\u3002\u201d \u3000\u3000\u201c\u51e4\u513f\u77e5\u9053\uff01\u201d\u6df1\u6df1\u4e00\u793c\uff0c\u8f6c\u8eab\u800c\u53bb\u3002 \u3000\u3000\u6c5f\u67ab\u79bb\u5f00\u4e86\u9752\u51e4\u697c\uff0c\u76f4\u5954\u4e00\u5bb6\u5ba2\u6808\u4e4b\u4e2d\uff0c\u90a3\u662f\u4ed6\u544a\u8bc9\u6885\u82b1\u7684\u4f4f\u5904\u3002 \u3000\u3000\u5e97\u4f19\u8ba1\u8fce\u4e0a\u6765\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5ba2\u5b98\uff0c\u4f60\u53ef\u56de\u6765\uff0c\u4e00\u4f4d\u5927\u59d1\u5a18\u5df2\u7ecf\u6765\u4e86\u4e24\u6b21\u627e\u4f60\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u55ef\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4eba\u5462\uff1f\u201d \u3000\u3000\u201c\u521a\u8d70\u4e86\uff0c\u5929\u4e0d\u4eae\u5c31\u8dd1\u6765\uff0c\u76f4\u7b49\u5230\u521a\u521a\u624d\u8d70\uff01\u201d \u3000\u3000\u5e97\u4f19\u8ba1\u8bf4\u7684\u58f0\u97f3\u4e0d\u5927\uff0c\u4f46\u7709\u773c\u9f50\u52a8\u3002 \u3000\u3000\u770b\u6837\u5b50\uff0c\u4ed6\u5fc3\u4e2d\u9ad8\u5174\u7684\u5f88\u3002 \u3000\u3000\u6c5f\u67ab\u4f4e\u58f0\u9053\uff1a\u201c\u5927\u59d1\u5a18\u957f\u7684\u4ec0\u4e48\u6837\u5b50\uff1f\u201d \u3000\u3000\u5e97\u4f19\u8ba1\u9053\uff1a\u201c\u752d\u8bf4\u6709\u591a\u6f02\u4eae\u4e86\uff0c\u6211\u6d3b\u4e86\u56db\u5341\u591a\u5e74\uff0c\u89c1\u8fc7\u5973\u5ba2\u4eba\u5c11\u8bf4\u70b9\uff0c\u4e5f\u6709\u4e2a\u4e07\u513f\u516b\u5343\u7684\uff0c\u5c31\u662f\u6ca1\u6709\u89c1\u8fc7\u90a3\u4e48\u7f8e\u7684\u4eba\uff0c\u6211\u7684\u5988\u5440\uff01\u90a3\u4e0d\u662f\u4eba\u54ea\uff01\u7b80\u76f4\u662f\u6708\u5bab\u5ae6\u5a25\u8ff7\u4e86\u8def\uff0c\u8dd1\u5230\u6211\u4eec\u5ba2\u6808\u91cc\u6765\uff01\u201d \u3000\u3000\u201c\u5662\uff01\u53ea\u662f\u5f88\u7f8e\u5417\uff1f\u201d \u3000\u3000\u201c\u7f8e\u7684\u5192\u6ce1\uff0c\u6211\u662f\u4e09\u751f\u6709\u5e78\u554a\uff01\u4e3a\u5979\u9001\u4e86\u676f\u8336\uff0c\u800c\u4e14\uff0c\u51fa\u624b\u5927\u65b9\uff0c\u4e00\u8d4f\u5c31\u662f\u5341\u4e24\u94f6\u5b50\uff0c\u6211\u4e09\u6708\u7684\u5de5\u8d44\u52a0\u8d77\u6765\uff0c\u4e5f\u51d1\u4e0d\u9f50\u8fd9\u4e2a\u6574\u6570\u554a\uff01\u201d\u5e97\u4f19\u8ba1\u53e3\u6cab\u6a2a\u98de\u7684\u8bf4\u3002 \u3000\u3000\u201c\u6211\u77e5\u9053\u4e86\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u90a3\u4f4d\u59d1\u5a18\u53ef\u6709\u4ec0\u4e48\u4ea4\u4ee3\uff1f\u201d \u3000\u3000\u201c\u5979\u8bf4\u6709\u8981\u7d27\u7684\u4e8b\uff0c\u5fc5\u9700\u548c\u7530\u5927\u7237\u5f53\u9762\u5546\u91cf\u3002\u201d \u3000\u3000\u201c\u8fd8\u6709\u5462\uff1f\u201d \u3000\u3000\u201c\u5979\u8bf4\u51ac\u96ea\u7559\u9999\uff0c\u7530\u5927\u7237\u5c31\u77e5\u9053\u4e86\uff0c\u5979\u5728\u5bb6\u91cc\u7b49\u4f60\uff0c\u8981\u4f60\u6700\u8fdf\u4eca\u665a\u4e0a\u4e00\u5b9a\u8981\u53bb\uff0c\u7530\u5927\u7237\uff0c\u5979\u662f\u8bf4\u4eca\u5929\u665a\u4e0a\u554a\uff01\u201d \u3000\u3000\u5e97\u4f19\u8ba1\u7279\u522b\u628a\u4eca\u5929\u665a\u4e0a\u91cd\u590d\u4e86\u4e00\u53e5\u3002 \u3000\u3000\u6c5f\u67ab\u53d6\u51fa\u4e00\u5757\u788e\u94f6\u5b50\uff0c\u5927\u6982\u6709\u4e09\u4e24\u591a\u91cd\uff0c\u8d4f\u7ed9\u5e97\u4f19\u8ba1\uff0c\u7b11\u9053\uff1a\u201c\u5979\u662f\u5343\u91d1\u5c0f\u59d0\uff0c\u6709\u94b1\u7684\u5f88\uff0c\u6211\u5c31\u4e0d\u5982\u5979\u4e86\uff0c\u4f19\u8ba1\uff0c\u5c06\u5c31\u7740\u6536\u4e0b\u5427\uff01\u201d \u3000\u3000\u5e97\u4f19\u8ba1\u63a5\u8fc7\u94f6\u5b50\uff0c\u8fde\u4f5c\u4e86\u4e24\u4e2a\u957f\u63d6\uff0c\u9053\uff1a\u201c\u7530\u7237\uff0c\u8c22\u8c22\u4f60\u7684\u539a\u8d50\u3002\u201d \u3000\u3000\u6c5f\u67ab\u6325\u6325\u624b\uff0c\u5e97\u4f19\u8ba1\u9000\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u63a9\u4e0a\u623f\u95e8\uff0c\u6c5f\u67ab\u5728\u6728\u69bb\u4e0a\u76d8\u819d\u800c\u5750\uff0c\u51c6\u5907\u597d\u597d\u7684\u8c03\u606f\u4e00\u4e0b\uff0c\u5b58\u8db3\u7cbe\u795e\uff0c\u53bb\u89c1\u6885\u82b1\u3002 \u3000\u3000\u6628\u591c\u548c\u9752\u51e4\u4ea4\u624b\u7684\u6f14\u53d8\uff0c\u5b8c\u5168\u51fa\u4e86\u4ed6\u610f\u6599\u4e4b\u5916\uff0c\u51e0\u4e4e\u547d\u4e27\u5728\u9752\u51e4\u697c\u4e2d\u3002 \u3000\u3000\u8fd9\u573a\u60ca\u9669\u7684\u5f80\u4e8b\uff0c\u867d\u7136\u5df2\u6210\u8fc7\u53bb\uff0c\u4f46\u7559\u5728\u4ed6\u5185\u5fc3\u4e2d\u7684\u8b66\u60d5\uff0c\u5374\u5982\u70d9\u5370\u4e00\u822c\uff0c\u6df1\u5370\u5728\u5fc3\u4e0a\u3002 \u3000\u3000\u4ed4\u7ec6\u63a8\u6572\u90a3\u591c\u6ca6\u843d\u4e0b\u98ce\u7684\u5173\u952e\u65f6\u523b\uff0c\u5c31\u662f\u90a3\u4e00\u77ac\u95f4\u7684\u5fd8\u53bb\u6212\u5907\u3002 \u3000\u3000\u5982\u662f\u6628\u591c\u4e2d\u9752\u51e4\u4e00\u51fb\u4e0d\u4e2d\uff0c\u6c5f\u67ab\u7684\u53cd\u51fb\uff0c\u5fc5\u5c06\u662f\u5f3a\u608d\u65e0\u6bd4\u3002 \u3000\u3000\u5f53\u7136\uff0c\u5c40\u52bf\u53d1\u5c55\u5230\u53cc\u65b9\u4e0d\u4ee5\u6b66\u529f\u62fc\u640f\uff0c\u4e0d\u4f1a\u6709\u6628\u591c\u90a3\u756a\u9189\u4eba\u5982\u9152\u7684\u6e29\u99a8\u65f6\u5149\u2026\u2026\u9752\u51e4\u697c\u5200\u5149\u3001\u5251\u5f71\uff0c\u8d31\u8840\u697c\u5934\uff0c\u5982\u4eca\u7684\u6d1b\u9633\u5c45\uff0c\u53ea\u6015\u5df2\u4eba\u4ef0\u9a6c\u7ffb\uff0c\u5929\u4e0b\u5927\u4e71\u3002 \u3000\u3000\u6709\u4e9b\u4e8b\uff0c\u5fcd\u7406\u4e4b\u5fff\uff0c\u53cd\u4f1a\u628a\u4e00\u573a\u51f6\u9669\u7684\u573a\u9762\uff0c\u53d8\u4f5c\u548c\u60a6\u4e4b\u5c40\uff0c\u4f7f\u5e72\u6208\u5316\u4f5c\u7389\u5e1b\u3002 \u3000\u3000\u8fd9\u756a\u51f6\u9669\u7684\u5386\u7ec3\uff0c\u4f7f\u6c5f\u67ab\u4f53\u4f1a\u5230\u90a3\u4e2a\u5df2\u7edf\u6cbb\u4e86\u6c5f\u6e56\u7684\u795e\u79d8\u7ec4\u5408\u4e2d\uff0c\u5145\u6ee1\u7740\u77db\u76fe\uff0c\u53ea\u8981\u80fd\u628a\u63e1\u673a\u4f1a\uff0c\u5c31\u53ef\u4ee5\u5206\u6563\u4ed6\u4eec\u7684\u529b\u91cf\u3002 \u3000\u3000\u5f53\u7136\uff0c\u4e00\u4e2a\u6700\u91cd\u8981\u7684\u57fa\u672c\u6761\u4ef6\uff0c\u662f\u672c\u8eab\u6280\u827a\u4fee\u4e3a\uff0c\u5177\u6709\u63a5\u53d7\u8003\u9a8c\u7684\u80fd\u529b\u3002 \u3000\u3000\u6c5f\u67ab\u5bf9\u672c\u8eab\u5177\u6709\u7684\u6f5c\u80fd\u529b\u91cf\uff0c\u4e5f\u6709\u6108\u6765\u6108\u5f3a\u7684\u4fe1\u5fc3\u3002 \u3000\u3000\u4f46\u4e5f\u4f53\u4f1a\u5230\u5bf9\u654c\u7528\u672f\u4e4b\u95f4\uff0c\u4e0d\u80fd\u6709\u4efb\u4f55\u5927\u610f\u3001\u677e\u61c8\uff0c\u4e5f\u8bb8\u7b2c\u4e8c\u6b21\u518d\u88ab\u4eba\u5236\u4f4f\u4e86\u7a74\u9053\uff0c\u5c31\u4e0d\u53ef\u80fd\u6709\u6628\u591c\u540c\u6837\u7684\u5e78\u8fd0\u3002 \u3000\u3000\u8fd9\u4e00\u756a\u6df1\u601d\u3001\u957f\u8651\uff0c\u4f7f\u6c5f\u67ab\u589e\u957f\u4e86\u4e0d\u5c11\u5bf9\u654c\u7684\u667a\u7565\uff0c\u4e3e\u4e00\u53cd\u4e09\uff0c\u4e5f\u591a\u4e86\u4e0d\u5c11\u7684\u6c5f\u6e56\u7ecf\u9a8c\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u8fd9\u4e00\u756a\u7528\u5fc3\u6df1\u601d\uff0c\u5df2\u65e0\u6cd5\u518d\u5b89\u9759\u4e0b\u6765\uff0c\u7d22\u6027\u7ad9\u8d77\u8eab\u5b50\uff0c\u6362\u8eab\u8863\u773c\uff0c\u76f4\u5954\u98ce\u96ea\u5c0f\u9986\u3002 \u3000\u3000\u5f20\u56db\u59d1\u8fce\u5165\u6c5f\u67ab\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u6765\u7684\u6b63\u597d\uff0c\u96ea\u3001\u971c\u4e8c\u5973\u7684\u7236\u6bcd\uff0c\u521a\u521a\u8d76\u5230\u3002\u201d \u3000\u3000\u201c\u597d\u6781\u4e86\u2026\u2026\u201d\u6c5f\u67ab\u8bf4\uff1a\u201c\u5c0f\u5f1f\u4e5f\u60f3\u5411\u4ed6\u4eec\u8bf7\u6559\u4e00\u4e9b\u4e8b\u60c5\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u505c\u4e0b\u811a\u6b65\uff0c\u9053\uff1a\u201c\u600e\u4e48\uff1f\u4f60\u8ba4\u8bc6\u4ed6\u4eec\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\u89c1\u8fc7\uff0c\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u4f46\u6211\u542c\u8bf4\u8fc7\u4ed6\u4eec\u7684\u5927\u540d\u3002\u201d \u3000\u3000\u201c\u80e1\u626f\u2026\u2026\u201d\u5f20\u56db\u59d1\u5fae\u7b11\u9053\uff1a\u201c\u4f60\u9a97\u4eba\uff0c\u9a97\u5230\u5927\u59d0\u7684\u5934\u4e0a\u4e86\uff0c\u4ed6\u4eec\u5f88\u5c11\u5728\u6c5f\u6e56\u4e0a\u8d70\u52a8\uff0c\u4ece\u4e0d\u548c\u6b66\u6797\u540c\u9053\u6765\u5f80\uff0c\u4ed6\u4eec\u7c4d\u7c4d\u65e0\u540d\uff0c\u4f60\u600e\u4f1a\u77e5\u9053\u4ed6\u4eec\uff1f\u201d \u3000\u3000\u201c\u5927\u59d0\uff0c\u7cbe\u660e\u554a\uff01\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u5c0f\u5f1f\u4e00\u8ddf\u6597\u7ffb\u4e2a\u5341\u4e07\u516b\u5343\u91cc\uff0c\u4e5f\u9003\u4e0d\u8fc7\u5927\u59d0\u7684\u624b\u638c\u5fc3\u4e86\u3002\u201d \u3000\u3000\u201c\u5c11\u704c\u8ff7\u6c64\u4e86\uff0c\u5927\u59d0\u4e0d\u5403\u8fd9\u4e2a\uff0c\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u8bf4\u5b9e\u8bdd\u3002\u201d \u3000\u3000\u201c\u5509\uff01\u662f\u51e0\u4f4d\u5e08\u957f\u544a\u8bc9\u6211\u7684\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u4ed6\u4eec\u9690\u5c45\u5929\u5c71\uff0c\u4e5f\u662f\u53d7\u4ed6\u4eec\u4e4b\u547d\uff0c\u5728\u90a3\u91cc\u82e6\u7ec3\u5251\u672f\u3002\u201d \u3000\u3000\u201c\u6211\u6709\u4e9b\u660e\u767d\u4e86\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u4ed6\u4eec\u662f\u5e03\u4e0b\u7684\u68cb\u5b50\u4e4b\u4e00\u3002\u201d \u3000\u3000\u6c5f\u67ab\u70b9\u70b9\u5934\u3002 \u3000\u3000\u201c\u6211\u5462\uff1f\u2026\u2026\u201d\u5f20\u56fd\u59d1\u5e7d\u5e7d\u8bf4\u9053\uff1a\u201c\u4e5f\u662f\u4f60\u51e0\u4f4d\u5e08\u957f\u5e03\u4e0b\u7684\u68cb\u5b50\u4e86\uff1f\u201d \u3000\u3000\u201c\u5927\u59d0\u662f\u4e00\u4ee3\u5947\u4eba\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u5bf9\u6062\u590d\u6b66\u6797\u6e05\u98ce\u3001\u660e\u6708\u7684\u5173\u7cfb\u81f3\u5927\uff0c\u5c0f\u5f1f\u786e\u5b9e\u662f\u5f97\u5230\u4e86\u5e08\u957f\u7684\u6307\u70b9\uff0c\u53bb\u6c42\u5927\u59d0\u76f8\u52a9\u3002\u201d \u3000\u3000\u201c\u55ef\uff01\u2026\u2026\u201d\u5f20\u56db\u59d1\u82e6\u6da9\u7684\u8bf4\uff0c\u201c\u4e5f\u662f\u4ed6\u4eec\u8981\u4f60\u65bd\u5c55\u5947\u672f\u8bf1\u60d1\u6211\u4e86\uff0c\u8ba9\u6211\u4e00\u8ddf\u5934\u683d\u5012\u4f60\u7684\u638c\u63e1\u4e2d\uff0c\u6b7b\u5fc3\u8e4b\u5730\u7684\u4e3a\u4f60\u6548\u547d\uff1f\u201d \u3000\u3000\u201c\u5927\u59d0\uff0c\u8a00\u91cd\u4e86\uff0c\u5c0f\u5f1f\u786e\u5b9e\u5bf9\u5927\u59d0\u65bd\u5c55\u8fc7\u5947\u672f\uff0c\u4e0d\u8fc7\uff0c\u5c0f\u5f1f\u5bf9\u5927\u59d0\u4e4b\u60c5\uff0c\u5374\u662f\u4e00\u7247\u771f\u60c5\u3002\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\uff0c\u2026\u2026\u201d\u5f20\u56db\u59d1\u5e7d\u5e7d\u4e00\u53f9\u63a5\u9053\uff1a\u201c\u9664\u4e86\u7b2c\u4e00\u6b21\u4e4b\u5916\uff0c\u4f60\u786e\u6765\u518d\u7528\u8fc7\u5947\u672f\u8ff7\u60d1\u6211\uff0c\u53ef\u662f\uff0c\u6211\u5df2\u7ecf\u683d\u8fdb\u53bb\u4e86\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u795e\u60c5\u8083\u7136\u7684\u8bf4\uff1a\u201c\u4f46\u5c0f\u5f1f\u6df1\u6302\u5fc3\u4e0a\u7684\u5374\u662f\u6c5f\u6e56\u91cd\u5149\u7684\u5927\u4e8b\uff0c\u5927\u59d0\u8981\u5c0f\u5f1f\u5982\u4f55\u4ea4\u4ee3\uff0c\u6c5f\u6e56\u5e95\u5b9a\u4e4b\u540e\uff0c\u5c0f\u5f1f\u65e0\u4e0d\u9075\u529e\u3002\u201d \u3000\u3000\u201c\u6211\u53ea\u6c42\u4f60\u4e00\u4ef6\u4e8b\uff0c\u4e0d\u8bba\u4f60\u65e5\u540e\u6709\u591a\u5c11\u7ea2\u7c89\u77e5\u5df1\uff0c\u4f46\u4f60\u5185\u5fc3\u4e2d\u4e00\u5b9a\u8981\u4e3a\u5927\u59d0\u7559\u4e00\u5e2d\u5b58\u8eab\u4e4b\u5730\u3002\u201d \u3000\u3000\u201c\u662f\uff01\u5c0f\u5f1f\u7edd\u4e0d\u6562\u8f9c\u8d1f\u5927\u59d0\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u60c5\u7f18\u6613\u5c3d\uff0c\u5b7d\u7f18\u96be\u4e86\uff0c\u5927\u59d0\u8fd9\u4e00\u8f88\u5b50\uff0c\u770b\u6765\u662f\u88ab\u4f60\u5403\u5b9a\u4e86\uff0c\u8d70\u5427\uff01\u522b\u53c8\u8ba9\u4e24\u4e2a\u4e2b\u5934\u7591\u795e\u7591\u9b3c\u7684\u3002\u201d \u3000 \u7b2c \u4e03 \u56de\u3000\u7a9d\u91cc\u5543\u8840\u6e85\u6d1b\u9633\u5c45 \u3000 \u3000\u3000\u5927\u5385\u5206\u5750\u7740\u4e00\u5bf9\u4e2d\u5e74\u7537\u5973\u3002 \u3000\u3000\u7537\u7684\u56db\u5341\u5de6\u53f3\uff0c\u4e00\u88ad\u9752\u888d\uff0c\u4e94\u7efa\u957f\u9aef\uff0c\u9762\u5982\u6ee1\u6708\uff0c\u76ee\u4f3c\u6717\u661f\uff0c\u6709\u4e00\u80a1\u98d8\u7136\u51fa\u5c18\u7684\u4ed9\u98ce\uff0c\u60f3\u662f\u4e45\u5e74\u5c71\u5c45\uff0c\u4e0d\u67d3\u4fd7\u6c14\u7684\u5173\u7cfb\u3002 \u3000\u3000\u81f3\u4e8e\u4e2d\u5e74\u5987\u4eba\uff0c\u9664\u4e86\u4e00\u8eab\u6708\u767d\u8863\u670d\uff0c\u662f\u4e2d\u5e74\u5987\u4eba\u7a7f\u7740\u989c\u8272\uff0c\u522b\u7684\u5374\u4e0d\u50cf\u4e2d\u5e74\u4eba\u7684\u6837\u5b50\u3002 \u3000\u3000\u541f\u96ea\u3001\u541f\u971c\uff0c\u4f9d\u504e\u8eab\u4fa7\uff0c\u6709\u5982\u4e09\u4e2a\u59d0\u59b9\u82b1\uff0c\u6df1\u5c71\u5e7d\u8c37\u80fd\u5b55\u80b2\u51fa\u5947\u82b1\u3001\u5f02\u9e1f\uff0c\u4e5f\u80fd\u57f9\u517b\u51fa\u4e0d\u6cbe\u4eba\u95f4\u70df\u706b\u7684\u4ed9\u5b50\u4eba\u7269\u3002 \u3000\u3000\u201c\u4f60\u5c31\u662f\u6c5f\u67ab\uff0c\u201d\u9752\u65bd\u4e2d\u5e74\u5fae\u7b11\u9053\uff1a\u201c\u679c\u7136\u662f\u4e00\u4e2a\u80a9\u627f\u52ab\u96be\uff0c\u80cc\u8d1f\u91cd\u6258\u7684\u5e72\u91cc\u9a79\u3002\u201d \u3000\u3000\u201c\u8fc7\u5956\u4e86\uff0c\u5c0f\u5f1f\u5fc3\u4eea\u5df2\u4e45\uff0c\u4eca\u65e5\u6709\u5e78\u62dc\u8bc6\u3002\u201d\u6df1\u6df1\u4e00\u63d6\uff0c\u64a9\u8d77\u8863\u895f\uff0c\u5c31\u8981\u62dc\u5012\u3002 \u3000\u3000\u201c\u4e0d\u6210\uff0c\u4e0d\u6210\uff0c\u201d\u9752\u888d\u4eba\u62e6\u4f4f\u4e86\u6c5f\u67ab\u5fd9\u9053\uff1a\u201c\u4f60\u5c31\u662f\u4e3b\u5e05\uff0c\u6211\u4eec\u662f\u53d7\u547d\u53d7\u4f60\u6307\u6325\u7684\u4eba\uff0c\u600e\u80fd\u672c\u672a\u5012\u7f6e\u3002\u201d \u3000\u3000\u201c\u662f\u554a\uff01\u5144\u5f1f\uff0c\u6211\u4eec\u5df2\u7ecf\u7b49\u4e86\u4f60\u5341\u516b\u5e74\u3002\u201d\u4e2d\u5e74\u592b\u4eba\u5fae\u7b11\u7740\u8bf4\u3002 \u3000\u3000\u201c\u5a18\u554a\uff01\u4f60\u600e\u4e48\u80fd\u53eb\u4ed6\u5144\u5f1f\uff0c\u201d\u541f\u96ea\u8bf4\uff0c\u201c\u6c5f\u5927\u54e5\u7684\u5e74\u9f84\u548c\u6211\u4eec\u5dee\u4e0d\u591a\u5440\uff01\u201d \u3000\u3000\u4e2d\u5e74\u5987\u4eba\u56de\u987e\u4e86\u541f\u96ea\u4e00\u773c\uff0c\u9053\uff1a\u201c\u6c5f\u5c11\u4fa0\u80f8\u7f57\u4e07\u6709\uff0c\u6b66\u827a\u7edd\u4e16\uff0c\u4f60\u4eec\u600e\u80fd\u6bd4\u4e07\u2014\u2026\u2026\u201d \u3000\u3000\u201c\u5a18\uff0c\u59d0\u59d0\u662f\u8bf4\u6c5f\u5927\u54e5\u7684\u5e74\u9f84\u554a\uff01\u53c8\u4e0d\u662f\u8bf4\u4ed6\u7684\u672c\u9886\u3002\u201d \u3000\u3000\u541f\u971c\u63a5\u9053\uff1a\u201c\u6c5f\u5927\u54e5\u7684\u672c\u9886\u5927\uff0c\u6211\u4eec\u77e5\u9053\uff0c\u5f20\u59e8\u5df2\u7ecf\u8bf4\u8fc7\u65e0\u6570\u904d\u4e86\uff0c\u4ee5\u540e\uff0c\u6211\u4eec\u8981\u8ffd\u968f\u5728\u4ed6\u7684\u8eab\u4fa7\uff0c\u4e00\u5b9a\u8981\u78e8\u7740\u4ed6\u4f20\u4e9b\u7edd\u6280\u7ed9\u6211\u4eec\u3002\u201d \u3000\u3000\u591f\u4e86\uff0c\u4ee5\u9752\u888d\u4eba\u592b\u5987\u7684\u806a\u6167\uff0c\u90a3\u91cc\u542c\u4e0d\u51fa\u4e00\u5bf9\u5973\u513f\u7684\u5fc3\u613f\uff0c\u592b\u5987\u76f8\u89c6\u4e00\u7b11\uff0c\u7adf\u4f3c\u5b8c\u5168\u4e0d\u653e\u5fc3\u4e0a\u3002 \u3000\u3000\u6c5f\u67ab\u5374\u542c\u5f97\u5934\u76ae\u53d1\u70b8\uff0c\u8f6c\u5411\u9752\u888d\u4eba\u9053\uff1a\u201c\u5982\u82e5\u5c0f\u5f1f\u6ca1\u6709\u8bb0\u9519\uff0c\u5927\u54e5\u662f\u590d\u59d3\u6b27\u9633\uff0c\u5355\u540d\u4e00\u4e2a\u662d\u5b57\u3002 \u3000\u3000\u201c\u597d\u8bb0\u6027\u2026\u2026\u201d\u6b27\u9633\u662d\u7b11\u9053\uff1a\u201c\u6211\u4eec\u5f7c\u6b64\u8bb0\u5728\u5fc3\u91cc\u4e8c\u5341\u5e74\uff0c\u4eca\u5e74\u603b\u7b97\u89c1\u9762\u4e86\u3002\u201d \u3000\u3000\u201c\u662f\uff0c\u5982\u975e\u5927\u59d0\u7684\u5b89\u6392\uff0c\u8fd8\u771f\u4e0d\u5bb9\u6613\u627e\u5230\u6b27\u9633\u5144\u957f\u592b\u5987\u5462\uff01\u201d \u3000\u3000\u6c5f\u67ab\u770b\u4e86\u5f20\u56db\u59d1\u4e00\u773c\uff0c\u6ee1\u8138\u611f\u6fc0\u795e\u8272\u3002 \u3000\u3000\u201c\u6c5f\u5144\u5f1f\uff0c\u201d\u6b27\u9633\u592b\u4eba\u9053\uff1a\u201c\u77e5\u9053\u5ac2\u5ac2\u6211\u7684\u540d\u5b57\u4e48\uff1f\u201d \u3000\u3000\u201c\u77e5\u9053\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u5ac2\u592b\u4eba\u59d3\u7f57\uff0c\u95fa\u8bb3\u4e00\u4e2a\u5170\u5b57\uff0c\u4e0d\u77e5\u5144\u5f1f\u662f\u5426\u8bb0\u60dc\u4e86\uff1f\u201d \u3000\u3000\u201c\u5a18\uff0c\u4ed6\u771f\u7684\u77e5\u9053\u554a\uff01\u201d\u541f\u96ea\u9053\uff1a\u201c\u5f20\u59e8\u8bf4\u6c5f\u5927\u54e5\u65e0\u6240\u4e0d\u77e5\uff0c\u5979\u600e\u4e48\u8fde\u5a18\u7684\u95fa\u540d\u4e5f\u77e5\u9053\u5462\uff1f\u201d \u3000\u3000\u8fd9\u5bf9\u5728\u5c71\u91ce\u4e45\u5c45\u7684\u592b\u5987\uff0c\u5bf9\u5973\u513f\u7684\u6559\u80b2\uff0c\u5168\u65e0\u8fc2\u8150\u4e4b\u6c14\uff0c\u4f7f\u5979\u4eec\u5145\u5206\u7684\u4fdd\u6709\u4e86\u5c11\u5973\u7684\u5929\u771f\u3002 \u3000\u3000\u5012\u662f\u7f57\u5170\u88ab\u5973\u513f\u95f4\u7684\u8138\u4e0a\u4e00\u70ed\uff0c\u9053\uff1a\u201c\u4f60\u7239\u548c\u5a18\uff0c\u5c31\u662f\u4e3a\u4e86\u4ed6\uff0c\u624d\u5e38\u4f4f\u6df1\u5c71\uff0c\u82e6\u4fee\u5251\u672f\uff0c\u4e8c\u5341\u5e74\u672a\u5c65\u6c5f\u6e56\u3002\u201d \u3000\u3000\u770b\u6765\uff0c\u5979\u4f3c\u662f\u4e00\u4e2a\u5916\u5411\u7684\u6027\u683c\uff0c\u8fd9\u4e8c\u5341\u5e74\u6df1\u5c71\u5e7d\u5c45\uff0c\u5fc3\u4e2d\u8fd8\u95f7\u4e86\u4e00\u53e3\u65e0\u5948\u4e4b\u6c14\u3002 \u3000\u3000\u201c\u662f\u554a\uff01\u6c5f\u5144\u5f1f\uff0c\u4f60\u5927\u5ac2\uff0c\u4e3a\u4e86\u5c71\u5c45\u4e2d\u7684\u5bc2\u5bde\u3002\u8fd8\u548c\u6211\u5455\u4e86\u5f88\u4e45\u7684\u6c14\u2026\u2026\u201d\u6b27\u9633\u662d\u5927\u7b11\uff0c\u9053\uff1a\u201c\u6709\u4e00\u5e74\u7684\u65f6\u95f4\uff0c\u8fde\u4e00\u53e5\u8bdd\u90fd\u4e0d\u8ddf\u6211\u8bf4\u3002\u201d \u3000\u3000\u201c\u6709\u8fd9\u79cd\u4e8b\uff0c\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u6211\u600e\u4e48\u4e00\u70b9\u90fd\u4e0d\u77e5\u9053\u5462\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u4e00\u4e2a\u957f\u63d6\uff0c\u9053\uff1a\u201c\u771f\u662f\u4e8f\u8d1f\u5ac2\u592b\u4eba\u4e86\uff0c\u90fd\u662f\u5c0f\u5f1f\u4e4b\u9519\u3002\u201d \u3000\u3000\u201c\u542c\u4ed6\u80e1\u8bf4\u516b\u9053\uff0c\u201d\u7f57\u5170\u8bf4\uff1a\u201c\u5f53\u5e74\u5c71\u5c45\u4e60\u5251\uff0c\u6211\u4e5f\u4e8b\u5148\u540c\u610f\u7684\uff0c\u600e\u4f1a\u548c\u4ed6\u4e89\u6267\u2026\u2026\u201d \u3000\u3000\u767d\u4e86\u4e08\u592b\u4e00\u773c\uff0c\u63a5\u9053\uff1a\u201c\u5435\u67b6\u662f\u6709\uff0c\u4e0d\u8fc7\uff0c\u4e0d\u662f\u4e3a\u4e86\u5c71\u5c45\u5bc2\u5bde\u2026\u2026\u201d \u3000\u3000\u201c\u5982\u662f\u4f60\u4eec\u5435\u67b6\u7684\u4e8b\uff0c\u548c\u5c0f\u5f1f\u65e0\u5173\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u5c0f\u5f1f\u5c31\u5fc3\u5b89\u591a\u4e86\u3002\u201d \u3000\u3000\u201c\u600e\u4f1a\u548c\u4f60\u65e0\u5173\uff0c\u201d\u6b27\u9633\u662d\u9053\uff1a\u201c\u6211\u4eec\u8fd9\u4e8c\u5341\u5e74\u6765\uff0c\u5b8c\u5168\u662f\u4e3a\u4e86\u4f60\u5728\u6d3b\u7740\uff0c\u6240\u4ee5\uff0c\u6211\u4eec\u592b\u5987\u7684\u4e8b\uff0c\u6837\u6837\u90fd\u548c\u4f60\u6709\u5173\uff01\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u2026\u2026\u201d\u6c5f\u67ab\u671b\u7740\u7f57\u5170\uff0c\u89c1\u7f57\u5170\u53cc\u988a\u7eef\u7ea2\uff0c\u5fc3\u4e2d\u7a81\u7136\u60f3\u5230\u4e86\u5f20\u56db\u59d1\u8bf4\u8d77\u7684\u4e00\u4ef6\u4e8b\u60c5\u3002 \u3000\u3000\u7f57\u5170\u8981\u751f\u4e2a\u513f\u5b50\uff0c\u4e08\u592b\u5374\u8981\u5979\u4e60\u6b66\u7b2c\u4e00\uff0c\u4e0d\u80af\u7b54\u5e94\uff0c\u60f3\u6765\uff0c\u5b9a\u7136\u4e3a\u4e86\u6b64\u4e8b\u4e89\u5435\uff0c\u4e0b\u7981\u5931\u58f0\u4e00\u7b11\u3002 \u3000\u3000\u90a3\u77e5\u7f57\u5170\u654f\u611f\u7684\u5f88\uff0c\u770b\u6c5f\u67ab\u7b11\u7684\u6696\u6627\uff0c\u4e0d\u7981\u4e00\u77aa\u773c\uff0c\u9053\uff1a\u201c\u4f60\u7b11\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\u554a\uff01\u201d\u6c5f\u67ab\u6025\u6025\u5426\u8ba4\u3002 \u3000\u3000\u5e78\u597d\u5f20\u56db\u59d1\u63d2\u53e3\u4e86\uff0c\u9053\uff1a\u201c\u7f57\u5170\uff0c\u4f60\u4eec\u4e00\u5bb6\u4eba\u90fd\u5728\u8fd9\u91cc\uff0c\u6c5f\u67ab\u4e5f\u662f\u4f60\u4eec\u795e\u4ea4\u4e86\u4e8c\u5341\u5e74\u7684\u670b\u53cb\uff0c\u6211\u5fc3\u4e2d\u6709\u51e0\u53e5\u8bdd\uff0c\u8981\u5f53\u4f60\u4eec\u592b\u5987\u4e4b\u9762\u8bf4\u6e05\u695a\u4e86\u3002\u201d \u3000\u3000\u201c\u56db\u59d1\uff0c\u4f60\u662f\u6211\u6700\u597d\u7684\u670b\u53cb\uff0c\u4e5f\u662f\u6211\u8fd9\u4e00\u751f\u552f\u4e00\u7684\u91d1\u5170\u59d0\u59b9\uff0c\u4ec0\u4e48\u8bdd\u90fd\u53ef\u4ee5\u8bf4\u2026\u2026\u201d\u7f57\u5170\u9053\uff1a\u201c\u8bf4\u9519\u4e86\uff0c\u4e5f\u4e0d\u4f1a\u6709\u4eba\u602a\u4f60\uff01\u201d \u3000\u3000\u201c\u4f60\u4eec\u5bf9\u6c5f\u67ab\u7684\u4e86\u89e3\uff0c\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u770b\u6837\u5b50\u662f\u6bd4\u6211\u8fd8\u6df1\u4e86\uff0c\u6211\u4eec\u53ef\u77e5\u9053\u4ed6\u4e00\u8eab\u6b66\u529f\u5f88\u535a\u6742\uff0c\u4e5f\u4e60\u7ec3\u8fc7\u8ff7\u60d1\u4eba\u5fc3\u7684\u5947\u672f\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u89c9\u7751\u4e0a\u53d1\u70ed\uff0c\u7f9e\u7ea2\u6ee1\u9762\uff0c\u5374\u53c8\u65e0\u53ef\u5948\u4f55\uff01 \u3000\u3000\u201c\u6211\u62c5\u5fc3\u4e24\u4e2a\u5c0f\u4f84\u5973\uff0c\u8ddf\u5728\u4ed6\u8eab\u4fa7\u5e2e\u5fd9\u2026\u2026\u201d\u5f20\u56db\u59d1\u6709\u4e9b\u8bf4\u4e0d\u4e0b\u53bb\u4e86\uff0c\u6539\u4e86\u53e3\u6c14\uff0c\u9053\uff1a \u3000\u3000\u201c\u662f\u4e0d\u662f\u4e0d\u5927\u65b9\u4fbf\u5462\uff1f\u201d \u3000\u3000\u7f57\u5170\u76ee\u5149\u8f6c\u52a8\uff0c\u53d1\u89c9\u4e00\u5bf9\u5973\u513f\uff0c\u56db\u53ea\u53cc\u773c\u775b\uff0c\u6b63\u51dd\u671b\u7740\u5979\u3002 \u3000\u3000\u5979\u4eec\u6ca1\u6709\u5426\u8ba4\u5f20\u56db\u59d1\u7684\u8bdd\uff0c\u81ea\u7136\u662f\u9ed8\u8ba4\u4e86\u3002 \u3000\u3000\u7f57\u5170\u5927\u611f\u4e3a\u96be\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u786e\u4e5f\u4e0d\u77e5\u8be5\u5982\u4f55\u56de\u7b54\uff0c\u53ea\u597d\u5582\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u542c\u5230\u6ca1\u6709\uff1f \u3000\u3000\u56db\u59cb\u5df2\u7ecf\u63d0\u51fa\u95ee\u9898\uff1f\u4f60\u8fd9\u4f5c\u7239\u7684\uff0c\u8be5\u62ff\u4e2a\u4e3b\u610f\u5440\uff01\u201d \u3000\u3000\u201c\u6211\u6ca1\u6709\u4e3b\u610f\u5440\uff0c\u201d\u6b27\u9633\u662d\u9053\uff1a\u201c\u5973\u513f\u5927\u4e86\uff0c\u6709\u5979\u4eec\u81ea\u5df1\u7684\u770b\u6cd5\u3001\u60f3\u6cd5\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u8d8a\u60f3\u8d8a\u4e0d\u5bf9\uff0c\u7ad9\u8d77\u8eab\u5b50\uff0c\u9053\uff1a\u201c\u8bf8\u4f4d\uff0c\u6211\u544a\u4e2a\u4fbf\uff01\u201d \u3000\u3000\u201c\u7528\u4e0d\u7740\u9003\u907f\uff0c\u201d\u6b27\u9633\u662d\u9053\uff1a\u201c\u4f60\u4e5f\u662f\u5f53\u4e8b\u4eba\uff0c\u6700\u597d\u4e5f\u5750\u5728\u8fd9\u91cc\uff0c\u542c\u4e2a\u660e\u767d\uff0c\u5982\u679c\u662f\u6211\u4eec\u5904\u7f6e\u6709\u9519\uff0c\u4f60\u4e5f\u53ef\u4ee5\u63d0\u51fa\u9ad8\u89c1\u554a\uff01\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u2026\u2026\u201d\u6c5f\u67ab\u957f\u53f9\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u76ee\u524d\u6c5f\u6e56\u4e0a\u63a7\u5236\u5c40\u9762\u7684\u662f\u4e00\u6279\u8eab\u6000\u5f02\u672f\u7684\u4eba\uff0c\u4ed6\u4eec\u6765\u81ea\u897f\u65b9\u9b54\u6559\uff0c\u548c\u6c5f\u6e56\u4e0a\u4e00\u4e9b\u606f\u9690\u7684\u9b54\u5934\uff0c\u7ed3\u5408\u6210\u4e00\u80a1\u5f3a\u5927\u65e0\u5339\u7684\u529b\u91cf\uff0c\u5c0f\u5f1f\u53d7\u547d\u4e8e\u5927\u5c40\u6666\u6697\uff0c\u9634\u973e\u904d\u5e03\u7684\u65f6\u523b\uff0c\u4e3a\u633d\u6551\u6b66\u6797\u52ab\u96be\uff0c\u786e\u5b9e\u4fee\u4e60\u4e86\u4e00\u4e9b\u4e0d\u4e3a\u6b63\u9053\u4eba\u58eb\u6240\u559c\u7684\u5947\u672f\u3002 \u3000\u3000\u6211\u65e0\u6cd5\u9884\u671f\u662f\u5426\u80fd\u8ba9\u6b66\u6797\u91cd\u5149\uff0c\u53ea\u6709\u5c3d\u529b\u800c\u4e3a\uff0c\u6b7b\u800c\u540e\u5df2\uff0c\u6240\u4ee5\uff0c\u6211\u65e0\u6cd5\u4fdd\u8bc1\u4ec0\u4e48\u3002 \u3000\u3000\u6b27\u9633\u5144\u5982\u662f\u89c9\u7740\u96ea\u3001\u971c\u4e24\u4f4d\u59d1\u5a18\uff0c\u4e0d\u5b9c\u5728\u6c61\u6d4a\u7684\u6c5f\u6e56\u4e2d\u8d70\u52a8\uff0c\u4f55\u4e0d\u628a\u5979\u4eec\u9001\u5165\u4e00\u7247\u6e05\u51c0\u7684\u73af\u5883\u4e2d\u6210\u957f\uff0c\u8ba9\u5979\u4eec\u76db\u653e\u51fa\u6e05\u9999\u4e4b\u82b1\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u8981\u2026\u2026\u201d\u541f\u96ea\u7a81\u7136\u5f00\u4e86\u53e3\uff0c\u9053\uff1a\u201c\u767d\u83b2\u82b1\u76db\u5f00\u4e8e\u6c61\u6ce5\u4e2d\uff0c\u65e0\u635f\u4e8e\u5b83\u7684\u6e05\u767d\uff0c\u4f55\u51b5\uff0c\u6211\u4e5f\u4e0d\u60f3\u81ea\u5df1\u6210\u4e3a\u4e00\u682a\u767d\u83b2\u82b1\u2026\u2026\u201d \u3000\u3000\u201c\u4e2b\u5934\uff0c\u201d\u6b27\u9633\u662d\u9053\uff1a\u201c\u90a3\u4f60\u60f3\u6210\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u6843\u4e5f\u597d\uff0c\u674e\u4e5f\u7f62\u2026\u2026\u201d\u541f\u96ea\u9053\uff1a\u201c\u6211\u53ea\u8981\u7ed3\u51fa\u751c\u7f8e\u7684\u679c\u5b9e\uff0c\u8981\u4eba\u4eec\u5403\u7684\u53e3\u820c\u751f\u6d25\u2026\u2026\u201d \u3000\u3000\u54c8\u54c8\u4e00\u7b11\uff0c\u6b27\u9633\u662d\u9053\uff1a\u201c\u6211\u4f5b\u5bb3\u5272\u8089\u9972\u9e70\uff0c\u6211\u513f\u53ef\u771f\u6709\u8fd9\u756a\u8c6a\u6c14\u5417\uff1f\u201d \u3000\u3000\u201c\u6c5f\u5927\u54e5\u8eab\u72af\u4e07\u96be\uff0c\u6b64\u540e\u906d\u9047\u7684\u51f6\u9669\uff0c\u5fc5\u5c06\u662f\u60ca\u5fc3\u52a8\u9b44\uff0c\u201d \u3000\u3000\u541f\u96ea\u58ee\u70c8\u5730\u8bf4\u9053\uff1a\u201c\u5973\u513f\u613f\u966a\u4ed6\u8d74\u6c64\u8e48\u706b\uff0c\u4e07\u6b7b\u4e0d\u60dc\u3002\u201d \u3000\u3000\u771f\u662f\u8bed\u4e0d\u60ca\u4eba\u6b7b\u4e0d\u4f11\uff0c\u4e00\u4e2a\u5728\u6df1\u5c71\u5e7d\u8c37\u4e2d\u957f\u5927\u7684\u5c11\u5973\uff0c\u600e\u4f1a\u5ffd\u7136\u95f4\uff0c\u6027\u683c\u5927\u53d8\u3002 \u3000\u3000\u201c\u541f\u96ea\uff0c\u542c\u7239\u5a18\u7684\u5b89\u6392\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u4ed6\u4eec\u4e3a\u633d\u6551\u6b66\u6797\u52ab\u96be\uff0c\u5df2\u5949\u732e\u4e86\u81ea\u5df1\uff01\u201d \u3000\u3000\u201c\u7239\u3001\u5a18\u628a\u6700\u5b9d\u8d35\u7684\u9752\u6625\uff0c\u6d88\u78e8\u5728\u6df1\u5c71\uff0c\u5251\u6280\u4e4b\u4e2d\u2026\u2026\u201d\u541f\u96ea\u63a5\u9053\uff1a\u201c\u4e3a\u7684\u662f\u8981\u52a9\u4f60\u4e00\u81c2\u4e4b\u529b\uff0c\u4ed6\u4eec\u7684\u5973\u513f\u5c82\u80fd\u8eb2\u907f\u51f6\u9669\u2026\u2026\u201d \u3000\u3000\u201c\u597d\u4e86\uff01\u6709\u5973\u5982\u6b64\u4e0e\u513f\u4f55\u5f02\u3002\u201d\u6b27\u9633\u662d\u9053\uff1a\u201c\u50b2\u5578\u98ce\u6708\uff0c\u9003\u907f\u751f\u52ab\uff0c\u9759\u5219\u9759\u77e3\uff01\u4f46\u5176\u4e0e\u8349\u6728\u4f55\u5f02\uff0c\u7f57\u5170\uff0c\u600e\u4e48\u8bf4\uff1f\u201d \u3000\u3000\u201c\u81ea\u7136\u662f\u59bb\u4ece\u592b\u610f\uff0c\u4f60\u80fd\u52d8\u7834\u4e16\u4fd7\uff0c\u8ba9\u4ed6\u4eec\u71c3\u70e7\u81ea\u5df1\uff0c\u6211\u4e5f\u53ea\u6709\u540c\u610f\u4e86\u3002\u201d \u3000\u3000\u201c\u541f\u96ea\uff0c\u8fd1\u706b\u6613\u711a\u2026\u2026\u201d\u6b27\u9633\u662d\u8bf4\uff0c\u201c\u6c5f\u67ab\u5df2\u7ecf\u8bf4\u7684\u5f88\u6e05\u695a\u4e86\uff0c\u4ed6\u4e0d\u4f1a\u627f\u8bfa\u4ec0\u4e48\uff0c\u4e0d\u8bba\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\uff0c\u4f60\u4eec\u53ef\u90fd\u8981\u81ea\u5df1\u627f\u62c5\u540e\u679c\uff0c\u522b\u6307\u671b\u7239\u5a18\u4f1a\u7ed9\u4f60\u4eec\u4ec0\u4e48\u5e2e\u52a9\u3002\u201d \u3000\u3000\u201c\u662f\uff01\u201d\u53f6\u96ea\u9053\uff1a\u201c\u5f20\u59e8\u544a\u8bc9\u6211\u4eec\u7684\uff0c\u6bd4\u7239\u8bf4\u7684\u8fd8\u8981\u660e\u663e\uff0c\u8fd9\u4ef6\u4e8b\uff0c\u6211\u548c\u971c\u59b9\u4e5f\u5546\u91cf\u8fc7\u5f88\u591a\u6b21\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\uff1f\u971c\u4e2b\u5934\u4e5f\u548c\u4f60\u6709\u7740\u4e00\u6837\u7684\u60f3\u6cd5\uff1f\u201d\u7f57\u5170\u6709\u4e9b\u5403\u60ca\u7684\u8bf4\u3002 \u3000\u3000\u201c\u662f\u554a\uff01\u5a18\u5982\u6ca1\u6709\u8ba9\u6211\u4eec\u9010\u9e7f\u6c5f\u6e56\u7684\u5fc3\u613f\uff0c\u600e\u80af\u8ba9\u5f20\u59e8\u5e26\u6211\u4eec\u4e0b\u5c71\u2026\u2026\u201d\u541f\u971c\u8bf4\uff0c\u201c\u65e2\u7136\u4eba\u5df2\u8df3\u8fdb\u4e86\u6c34\u91cc\uff0c\u90a3\u5c31\u4e0d\u7528\u62c5\u5fc3\u96e8\u6c34\u6253\u6e7f\u8863\u670d\u4e86\u3002\u201d \u3000\u3000\u7f57\u5170\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u660e\u77e5\u5c71\u6709\u864e\uff0c\u504f\u5411\u864e\u5c71\u884c\uff0c\u6709\u4e00\u5929\u8001\u864e\u4f24\u4e86\u4f60\u4eec\uff0c\u53ef\u4e0d\u8981\u53eb\u5988\uff01\u201d \u3000\u3000\u201c\u4e0d\u4f1a\u7684\uff0c\u201d\u541f\u971c\u7b11\u9053\uff1a\u201c\u5927\u6c34\u51b2\u4e86\u9f99\u738b\u5e99\uff0c\u6df9\u6b7b\u603b\u6bd4\u67af\u6b7b\u597d\uff0c\u5a18\u653e\u5fc3\u5427\uff01\u6211\u548c\u59d0\uff0c\u90fd\u4f1a\u627f\u62c5\u8d77\u706b\u711a\u8eab\u7684\u75db\u82e6\uff0c\u5e2e\u52a9\u6c5f\u5927\u54e5\u7167\u4eae\u6c5f\u6e56\u3002\u201d \u3000\u3000\u201c\u597d\u5427\uff01\u8fd9\u4ef6\u4e8b\u5230\u6b64\u4e3a\u6b62\uff0c\u6c5f\u8001\u5f1f\uff0c\u201d\u6b27\u9633\u662d\u9053\uff1a\u201c\u4e24\u4e2a\u4e2b\u5934\u4ea4\u7ed9\u4f60\u4e86\u3002\u201d \u3000\u3000\u6c5f\u67ab\u606d\u606d\u656c\u656c\u62b1\u62f3\u4e00\u63d6\uff0c\u9053\uff1a\u201c\u5c0f\u5f1f\u5c3d\u529b\u4fdd\u62a4\u3002\u201d \u3000\u3000\u201c\u6211\u4e0d\u62c5\u5fc3\u4e24\u4e2a\u4e2b\u5934\u4f1a\u62fc\u6218\u800c\u6b7b\uff0c\u6015\u7684\u662f\u5979\u4eec\u65e0\u80fd\u81ea\u5df1\uff0c\u706d\u9876\u4e8e\u60c5\u6d77\u4e4b\u4e2d\uff0c\u2026\u2026\u201d\u7f57\u5170\u8bf4\uff0c\u201c\u4f46\u6211\u7b54\u5e94\u8fc7\u5929\u72d0\u592b\u4eba\uff0c\u6b27\u9633\u4e00\u5bb6\u4eba\uff0c\u90fd\u5c06\u5e94\u52ab\u6c5f\u6e56\uff0c\u4e0d\u4f1a\u9003\u907f\u8d23\u4efb\u3002\u201d \u3000\u3000\u201c\u6240\u4ee5\uff0c\u5979\u624d\u4f20\u4e86\u4f60\u9752\u6625\u5e38\u9a7b\u4e4b\u672f\uff0c\u2026\u2026\u201d\u6b27\u9633\u662d\u7b11\u9053\uff1a\u201c\u5982\u679c\u8fd9\u4e00\u6b21\u5927\u96be\u4e0d\u6b7b\uff0c\u6211\u5c06\u53d1\u82cd\u82cd\uff0c\u89c6\u832b\u832b\uff0c\u8001\u6001\u9f99\u949f\uff0c\u4f60\u5374\u4ecd\u662f\u8273\u5149\u7167\u4eba\u2026\u2026\u201d \u3000\u3000\u201c\u8273\u8272\u4e0d\u892a\uff0c\u9752\u6625\u5e38\u9a7b\u2026\u2026\u201d\u7f57\u5170\u8bf4\uff0c\u201c\u603b\u6bd4\u9e21\u76ae\u9e64\u53d1\u597d\u770b\u4e9b\uff0c\u4e5f\u514d\u5f97\u4f60\u7559\u604b\u6625\u8272\uff0c\u5145\u6211\u4e8e\u4e0d\u987e\uff0c\u518d\u8bf4\u672f\u867d\u80fd\u9a7b\u989c\uff0c\u5374\u4e0d\u4f1a\u957f\u751f\uff0c\u6709\u4e00\u5929\uff0c\u4f60\u771f\u7684\u8001\u75c5\u800c\u6b7b\uff0c\u6211\u81ea\u4f1a\u520e\u9888\u7075\u524d\uff0c\u968f\u4f60\u6cc9\u4e0b\uff0c\u653e\u5fc3\u5427\uff01\u5a07\u59bb\u867d\u8273\u4e3d\uff0c\u4f46\u6b64\u5fc3\u5df2\u6c38\u4e3a\u541b\u6240\u6709\u4e86\u3002\u201d \u3000\u3000\u201c\u4f60\u542c\u542c\uff0c\u6c5f\u8001\u5f1f\uff0c\u4f60\u5927\u5ac2\u8fd9\u4efd\u751c\u871c\u7684\u6df1\u60c5\uff0c\u600e\u4e0d\u53eb\u8001\u54e5\u54e5\u6c38\u62dc\u88d9\u4e0b\uff0c\u7ec8\u8eab\u4e0d\u6e1d\u5462\uff01\u2026\u2026\u201d \u3000\u3000\u6b27\u9633\u662d\u62c2\u9aef\u5927\u7b11\uff0c\u5185\u5fc3\u4e2d\u7684\u5feb\u4e50\uff0c\u8868\u9732\u65e0\u9057\u3002 \u3000\u3000\u6c5f\u67ab\u5fc3\u4e2d\u4e00\u52a8\uff0c\u6697\u9053\uff1a\u201c\u770b\u6765\uff0c\u4eba\u751f\u771f\u6b63\u7684\u5e78\u798f\uff0c\u8fd8\u662f\u4e00\u592b\u4e00\u59bb\uff0c\u76f8\u656c\u76f8\u7231\uff0c\u624d\u80fd\u57f9\u517b\u51fa\u751f\u6b7b\u4e00\u4f53\uff0c\u4e92\u51fb\u5fc3\u7075\u7684\u6df1\u60c5\uff0c\u6211\u6c5f\u67ab\u8fd9\u4e00\u751f\uff0c\u53ea\u6015\u662f\u6c38\u65e0\u8fd9\u4e2a\u673a\u4f1a\u4e86\u3002\u201d \u3000\u3000\u773c\u770b\u6c5f\u67ab\u4f4e\u5934\u6c89\u601d\uff0c\u795e\u8272\u51c4\u4f24\uff0c\u6b27\u9633\u662d\u7ad9\u8d77\u8eab\u5b50\uff0c\u62cd\u62cd\u6c5f\u67ab\uff0c\u9053\uff1a\u201c\u5404\u6709\u56e0\u7f18\u83ab\u7fa1\u4eba\uff0c\u8001\u5f1f\uff0c\u4f60\u624d\u662f\u5929\u4e0b\u7537\u4eba\u5992\u6068\u3001\u7fa1\u6155\u7684\u5bf9\u8c61\u554a\uff01\u201d \u3000\u3000\u6c5f\u67ab\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u5c0f\u5f1f\u4e00\u76f4\u4e0d\u63d0\u51e0\u4f4d\u5e08\u957f\u7684\u540d\u53f7\uff0c\u751f\u6050\u753b\u864e\u4e0d\u6210\uff0c\u4f24\u5bb3\u5230\u4ed6\u4eec\u7684\u6e05\u8a89\uff0c\u81f3\u4e8e\u6c5f\u67d0\u4e2a\u4eba\u7684\u751f\u6b7b\uff0c\u65e9\u7f6e\u5ea6\u5916\uff0c\u5c31\u7b97\u843d\u4e0b\u4e2a\u5343\u53e4\u9a82\u540d\uff0c\u4e5f\u65e0\u6028\u8a00\uff0c\u4f46\u6211\u62c5\u5fc3\u7684\u662f\u654c\u52bf\u5f3a\u5927\uff01 \u3000\u3000\u5c0f\u5f1f\u4e00\u76f4\u65e0\u6cd5\u4e86\u89e3\u51e0\u4f4d\u5e08\u957f\uff0c\u5728\u6697\u4e2d\u5e03\u4e0b\u4e86\u591a\u5c11\u4eba\u624b\uff1f\u6b27\u9633\u5144\u6709\u8d50\u6559\u5417\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u8981\u4f60\u5931\u671b\u4e86\uff0c\u201d\u6b27\u9633\u662d\u8bf4\uff0c\u201c\u9664\u4e86\u6211\u4eec\u592b\u5987\u4e4b\u5916\uff0c\u5728\u4e0b\u4e5f\u4e0d\u77e5\u51e0\u4f4d\u524d\u8f88\u5e03\u6709\u591a\u5c11\u4f0f\u5175\u3002\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\uff0c\u201d\u7f57\u5170\u8bf4\uff0c\u201c\u4e3a\u4e86\u79d8\u5bc6\uff0c\u5b8c\u5168\u6ca1\u6709\u4f0f\u5175\u8fd9\u4e2a\u8ba1\u5212\uff0c\u6211\u4eec\u592b\u5987\u53ea\u662f\u4e2a\u5de7\u5408\u3002\u6240\u4ee5\uff0c\u5929\u72d0\u592b\u4eba\u4ee5\u4ea4\u6362\u7684\u6761\u4ef6\uff0c\u5b89\u6392\u4e0b\u6211\u4eec\u592b\u5987\u4f5c\u4e3a\u63f4\u624b\uff0c\u6211\u4eec\u662f\u4f60\u552f\u4e00\u7684\u540e\u63f4\uff0c\u6c5f\u5144\u5f1f\uff0c\u5929\u72d0\u592b\u4eba\u5bf9\u6211\u6709\u4f20\u827a\u4e4b\u6069\uff0c\u8bf4\u8d77\u6765\uff0c\u54b1\u4eec\u4e5f\u7b97\u6709\u540c\u95e8\u4e4b\u8c0a\uff0c\u636e\u6211\u6240\u77e5\uff0c\u4ed6\u4eec\u628a\u5168\u90e8\u7684\u5fc3\u8840\u6295\u6ce8\u5728\u4f60\u7684\u8eab\u4e0a\uff0c\u4f60\u4e5f\u662f\u4ed6\u4eec\u552f\u4e00\u7684\u5e0c\u671b\u2026\u2026\u201d \u3000\u3000\u201c\u8fd9\u2026\u2026\u201d\u6c5f\u67ab\u795e\u60c5\u8083\u7136\u5730\u8bf4\uff1a\u201c\u6211\u613f\u5168\u529b\u4e00\u6218\uff0c\u4f46\u5c0f\u5f1f\u4e00\u4eba\u2026\u2026\u201d \u3000\u3000\u201c\u8c01\u8bf4\u4f60\u4e00\u4e2a\u4eba\uff1f\u201d\u7f57\u5170\u76ee\u5149\u4e00\u63a0\u5f20\u56db\u59d1\uff0c\u63a5\u9053\uff1a\u201c\u6613\u82b1\u624b\u5343\u53d8\u4e07\u5316\uff0c\u4e00\u4eba\u53ef\u5f53\u4e00\u5927\u9ad8\u624b\u7528\uff0c\u6211\u4eec\u592b\u5987\u606f\u9690\u4e8e\u5927\u5df4\u5c71\u4e2d\uff0c\u5979\u4eec\u7684\u5e74\u7eaa\u4e0d\u5927\uff0c\u4f46\u5251\u672f\u4e0a\u7684\u9020\u8be3\uff0c\u5374\u5df2\u767b\u5802\u5165\u5ba4\uff0c\u5929\u72d0\u592b\u4eba\u4f20\u4e86\u6211\u4e0d\u5c11\u7684\u6280\u827a\uff0c\u6211\u4e5f\u4f20\u7ed9\u4e86\u4e24\u4e2a\u4e2b\u5934\uff0c\u4f60\u65e9\u8be5\u53d1\u89c9\u4e86\uff0c\u5979\u4eec\u90fd\u5e26\u4e86\u4e00\u70b9\u72d0\u6027\uff0c\u6df1\u5c71\u5927\u6cfd\u4e2d\u957f\u5927\u7684\u59d1\u5a18\uff0c\u90a3\u4f1a\u6709\u5982\u6b64\u7684\u4e0d\u754f\u4eba\u751f\u7684\u653e\u7eb5\u2026\u2026\u201d \u3000\u3000\u542c\u5230\u6b64\u5904\uff0c\u6c5f\u67ab\u5fcd\u4e0d\u4f4f\u56de\u5934\u671b\u4e86\u4e8c\u5973\u4e00\u773c\u3002 \u3000\u3000\u541f\u96ea\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6c5f\u5927\u54e5\uff0c\u6211\u4eec\u662f\u6df1\u85cf\u4e0d\u9732\u554a\uff01\u662f\u4e0d\u662f\u6709\u70b9\u610f\u5916\uff1f\u201d \u3000\u3000\u201c\u4e3b\u8981\u7684\u662f\u6211\u4eec\u548c\u5a18\u6253\u8d4c\uff0c\u201d\u541f\u971c\u8bf4\uff0c\u201c\u5982\u82e5\u6c5f\u5927\u54e5\u77a7\u4e0d\u51fa\u6211\u4eec\u7684\u5e95\u7ec6\uff0c\u5c31\u7b97\u6211\u4eec\u8d62\u4e86\uff0c\u5141\u8bb8\u6211\u4eec\u8ffd\u968f\u5de6\u53f3\uff0c\u6240\u4ee5\uff0c\u6211\u4eec\u628a\u81ea\u5df1\u63a9\u9970\u4e86\u8d77\u6765\u3002\u201d \u3000\u3000\u201c\u7f57\u5170\uff0c\u4f60\u4eec\u5a18\u4e09\u4e2a\uff0c\u53ef\u628a\u6211\u7ed9\u51a4\u82e6\u4e86\uff0c\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u5bb3\u5f97\u6211\u65e5\u591c\u66ff\u5979\u4eec\u62c5\u5fc3\u3002\u201d \u3000\u3000\u201c\u5509\uff01\u6211\u4e5f\u5f88\u62c5\u5fc3\uff0c\u201d\u7f57\u5170\u8bf4\uff0c\u201c\u5929\u72d0\u592b\u4eba\u4f20\u6388\u6211\u7684\u6280\u827a\uff0c\u6211\u81ea\u5df1\u5f88\u5c11\u4e60\u7ec3\uff0c\u5927\u90fd\u4f20\u6388\u7ed9\u4e24\u4e2a\u4e2b\u5934\u4e86\u2026\u2026\u201d \u3000\u3000\u770b\u4e86\u5927\u592b\u4e00\u773c\uff0c\u63a5\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u96ea\u3001\u971c\u4e24\u4e2a\u4e2b\u5934\u7684\u72d0\u6027\uff0c\u6bd4\u6211\u6df1\u591a\u4e86\uff0c\u5728\u6ca1\u6709\u89c1\u5230\u6c5f\u67ab\u4e4b\u524d\uff0c\u6211\u62c5\u5fc3\u8fd9\u4e24\u4e2a\u4e2b\u5934\uff0c\u81ea\u8d1f\u6280\u827a\uff0c\u4e0d\u4f1a\u628a\u6c5f\u67ab\u770b\u5728\u773c\u4e2d\uff0c\u73b0\u5728\u6211\u53c8\u62c5\u5fc3\u4e24\u4e2a\u4e2b\u5934\u8fd9\u70b9\u9053\u884c\uff0c\u4e00\u8ddf\u6597\u683d\u4e0b\u53bb\uff0c\u4e0d\u514b\u81ea\u62d4\uff01\u201d \u3000\u3000\u201c\u5927\u6982\uff0c\u8fd9\u4e2a\u4e16\u754c\u4e0a\u6ca1\u6709\u4e24\u5168\u5176\u7f8e\u7684\u4e8b\uff0c\u201d\u6b27\u9633\u662d\u9053\uff1a\u201c\u5979\u4eec\u4e60\u7ec3\u8fc7\u72d0\u5a9a\u4e4b\u672f\uff0c\u6050\u6015\u5df2\u65e0\u6cd5\u5efa\u7acb\u8d77\u4e00\u4e2a\u6b63\u5e38\u7684\u5bb6\u5ead\uff0c\u90a3\u4e2a\u4eba\u5a36\u4e86\u5979\u4eec\uff0c\u53ea\u6015\u8981\u88ab\u5979\u4eec\u6298\u817e\u5929\u7ffb\u5730\u8986\u4e86\uff0c\u4f55\u4e0d\u7531\u5979\u4eec\u53bb\u5427\uff01\u53cd\u6b63\u4f60\u5df2\u7ecf\u5bf9\u5929\u72d0\u592b\u4eba\u8bb8\u4e0b\u627f\u8bfa\uff0c\u6b27\u9633\u4e00\u5bb6\u4eba\uff0c\u90fd\u5c06\u5e94\u52ab\u6c5f\u6e56\uff01\u201d \u3000\u3000\u7f57\u5170\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u597d\uff01\u4f60\u60f3\u7684\u6bd4\u6211\u8fd8\u5f00\uff0c\u4eba\u5012\u6ca1\u6709\u62c5\u5fc3\u7684\u4e86\uff0c\u7531\u6b64\u523b\u8d77\uff0c\u4f60\u4eec\u4e24\u4e2a\u4e2b\u5934\uff0c\u5c31\u53ef\u4ee5\u8ddf\u6c5f\u67ab\u8d70\u4e86\uff01\u201d \u3000\u3000\u201c\u8c22\u8c22\u5a18\u2026\u2026\u201d \u3000\u3000\u541f\u96ea\u3001\u541f\u971c\u540c\u58f0\u8bf4\uff0c\u4e5f\u540c\u65f6\u8eac\u8eab\u5bf9\u7f57\u5170\u4e00\u793c\u3002\u663e\u89c1\u5979\u4eec\u5fc3\u4e2d\u5341\u5206\u5feb\u4e50\u3002 \u3000\u3000\u201c\u6162\u4e00\u70b9\uff0c\u5a18\u7684\u8bdd\u8fd8\u672a\u8bf4\u5b8c\u3002\u201d \u3000\u3000\u4e8c\u5973\u540c\u65f6\u4e00\u5446\uff0c\u631a\u7740\u7f57\u5170\uff0c\u76ee\u5149\u4e2d\u6ee1\u662f\u4e5e\u6c42\u4e4b\u8272\uff0c\u5979\u4eec\u5df2\u7ecf\u5fcd\u8010\u4e86\u5f88\u4e45\uff0c\u5341\u51e0\u5e74\u7684\u82e6\u5b66\u82e6\u7ec3\uff0c\u6025\u6b32\u4e00\u73b0\u8eab\u624b\u3002 \u3000\u3000\u6c5f\u67ab\u671b\u7740\u96ea\u3001\u971c\u4e8c\u5973\uff0c\u5fc3\u4e2d\u5374\u662f\u6697\u6697\u4f69\u670d\uff0c\u5979\u4eec\u4e60\u8fc7\u5929\u72d0\u592b\u4eba\u7684\u6b66\u529f\uff0c\u4f46\u5728\u5916\u5f62\u4e0a\u5979\u4eec\u7adf\u63a7\u5236\u5f97\u5f88\u597d\uff0c\u4e00\u70b9\u4e5f\u77a7\u4e0d\u51fa\u6765\uff0c\u7aef\u5e84\u7eaf\u6d01\uff0c\u5168\u65e0\u72d0\u5a9a\u4e4b\u6c14\u3002 \u3000\u3000\u4f46\u95fb\u7f57\u5170\u63a5\u9053\uff1a\u201c\u5929\u72d0\u592b\u4eba\u7684\u6b66\u529f\uff0c\u867d\u7136\u8be1\u5947\u7edd\u4f26\uff0c\u4f46\u4f60\u4eec\u529f\u529b\u8fd8\u6d45\uff0c\u800c\u4e14\uff0c\u9762\u5bf9\u7684\u654c\u4eba\uff0c\u53c8\u662f\u51f6\u72e1\u65e0\u6bd4\uff0c\u5982\u65e0\u5f20\u59e8\u7684\u6613\u5bb9\u795e\u6280\uff0c\u4f60\u4eec\u7edd\u96be\u9003\u8fc7\u654c\u4eba\u7684\u8ffd\u6740\uff0c\u66f4\u4e0d\u8981\u8bf4\u5e2e\u6c5f\u67ab\u7684\u5fd9\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u4eec\u5df2\u5b66\u5230\u4e86\u5f20\u59e8\u7684\u5feb\u901f\u6613\u5bb9\u624b\u6cd5\u2026\u2026\u201d\u541f\u96ea\u8bf4\uff0c\u201c\u7d27\u6025\u5e94\u53d8\uff0c\u5df2\u53ef\u5e94\u4ed8\u3002\u201d \u3000\u3000\u201c\u4f60\u4eec\u5f20\u59e8\u662f\u6700\u6770\u51fa\u7684\u6613\u5bb9\u9ad8\u624b\uff0c\u6570\u767e\u5e74\u6765\uff0c\u6b64\u4e2d\u9ad8\u624b\u65e0\u51fa\u5176\u53f3\u2026\u2026\u201d\u7f57\u5170\u8bf4\uff0c\u201c\u4f60\u4eec\u662f\u5426\u5df2\u6709\u5e94\u53d8\u7684\u80fd\u529b\uff0c\u8981\u5f20\u59e8\u51b3\u5b9a\u4e86\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u5fae\u5fae\u4e00\u7b11\uff0c\u671b\u7740\u96ea\uff0c\u971c\u4e8c\u5973\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u8ddf\u6211\u6765\u2026\u2026\u201d \u3000\u3000\u76ee\u5149\u4e00\u63a0\u6c5f\u67ab\uff0c\u63a5\u9053\uff1a\u201c\u4f60\u4e5f\u6765\uff0c\u6211\u628a\u79fb\u82b1\u63a5\u6728\u548c\u4e94\u884c\u6f5c\u672f\uff0c\u4f20\u7ed9\u4f60\u4eec\u3002\u201d \u3000\u3000\u53f6\u96ea\u3001\u541f\u971c\u3001\u6c5f\u67ab\uff0c\u9c7c\u8d2f\u8ffd\u968f\u5728\u5f20\u56db\u59d1\u8eab\u540e\u800c\u53bb\u3002 \u3000\u3000\u201c\u8c22\u8c22\u4f60\uff0c\u5170\u513f\uff0c\u201d\u6b27\u9633\u662d\u4f4e\u58f0\u9053\uff1a\u201c\u4f60\u7adf\u7136\u653e\u5f03\u4e86\u4e60\u7ec3\u5929\u72d0\u592b\u4eba\u7684\u6b66\u529f\uff0c\u6211\u77e5\u9053\uff0c\u662f\u4e3a\u4e86\u6211\u2026\u2026\u201d \u3000\u3000\u201c\u77e5\u9053\u5c31\u597d\uff0c\u201d\u7f57\u5170\u9053\uff1a\u201c\u5929\u72d0\u6b66\u529f\uff0c\u53d8\u5316\u5947\u5e7b\uff0c\u6211\u4e0d\u6562\u7ec3\u5b83\uff0c\u662f\u6015\u7ec3\u51fa\u4e00\u8eab\u90aa\u6c14\uff0c\u90a3\u5c31\u4e0d\u77e5\u9053\u4e00\u5929\u8981\u5435\u4e0a\u51e0\u6b21\u67b6\u4e86\u3002\u201d \u3000\u3000\u6b27\u9633\u662d\u884c\u8fd1\u7f57\u5170\uff0c\u4e00\u4e0b\u5b50\u628a\u7f57\u5170\u62e5\u5165\u6000\u4e2d\uff0c\u9053\uff1a\u201c\u6211\u6b27\u9633\u662d\u5a36\u5230\u4e86\u4f60\u8fd9\u6837\u4e00\u4e2a\u5929\u4ed9\u5316\u4eba\u7684\u59bb\u5b50\uff0c\u4e0d\u77e5\u662f\u51e0\u4e16\u4fee\u6765\u7684\u798f\u6c14\u2026\u2026\u201d \u3000\u3000\u7f57\u5170\u63a8\u5f00\u4e86\u4e08\u592b\u543b\u4e0a\u6765\u7684\u5634\u5df4\uff0c\u7b11\u9053\uff1a\u201c\u6211\u6ca1\u7ec3\u5929\u72d0\u6b66\u529f\uff0c\u4f60\u5230\u6cbe\u4e0a\u4e86\u4e0d\u5c11\u7684\u72d0\u6c14\uff0c\u5927\u767d\u5929\u7684\u5c31\u8fd9\u6837\u8272\u80c6\u5305\u5929\uff0c\u8981\u662f\u88ab\u56db\u59d1\u548c\u4e24\u4e2a\u5973\u513f\u770b\u5230\u4e86\uff0c\u4f60\u8981\u600e\u4e48\u4ea4\u4ee3\u2026\u2026\u201d \u3000\u3000\u6b27\u9633\u662d\u5c34\u5c2c\u4e00\u7b11\uff0c\u653e\u5f00\u4e86\u5a07\u59bb\uff0c\u9053\uff1a\u201c\u56db\u59d1\u8981\u4f20\u6388\u79fb\u82b1\u63a5\u6728\u548c\u4e94\u884c\u6f5c\u672f\u7ed9\u6c5f\u67ab\u548c\u4e24\u4e2a\u4e2b\u5934\uff0c\u90a3\u662f\u4ec0\u4e48\u6b66\u529f\u554a\uff1f\u6211\u600e\u4e48\u6ca1\u6709\u542c\u8fc7\uff1f\u201d \u3000\u3000\u201c\u662f\u56db\u59d1\u771f\u6b63\u7684\u7edd\u827a\uff0c\u2018\u79fb\u82b1\u63a5\u6728\u2019\u662f\u6613\u5bb9\u624b\u6cd5\u4e2d\u6700\u9ad8\u660e\u7684\u5ac1\u7978\u6280\u672f\uff0c\u5728\u6781\u77ed\u7684\u65f6\u95f4\u4e2d\uff0c\u80fd\u4f7f\u4eba\u654c\u53cb\u83ab\u8fa8\u2026\u2026\u201d\u7f57\u5170\u8bf4\uff1a\u201c\u6613\u5bb9\u672f\u548c\u4f20\u63a5\u6cd5\u7684\u4ea4\u76f8\u8fd0\u7528\uff0c\u4f7f\u4eba\u7269\u548c\u666f\u7269\u80fd\u5feb\u901f\u8f6c\u53d8\uff0c\u81f3\u4e8e\u4e94\u884c\u6f5c\u672f\uff0c\u597d\u50cf\u662f\u4e00\u79cd\u8272\u5f69\u8fd0\u7528\u7684\u7edd\u62db\uff0c\u8be6\u7ec6\u7684\u60c5\u5f62\uff0c\u6211\u4e5f\u4e0d\u592a\u6e05\u695a\u4e86\uff0c\u90a3\u662f\u56db\u59d1\u6bd5\u751f\u7684\u7edd\u6280\u3002\u201d \u3000\u3000\u201c\u60f3\u4e0d\u5230\u554a\uff01\u5f20\u56db\u59d1\u7adf\u7136\u662f\u4e00\u4f4d\u8eab\u8d1f\u5982\u6b64\u9ad8\u7ea7\u827a\u80fd\u7684\u4eba\u7269\u2026\u2026\u201d\u6b27\u9633\u662d\u9053\uff1a\u201c\u770b\u6765\u4e16\u4eba\u5bf9\u5979\u7684\u4e86\u89e3\uff0c\u771f\u4e0d\u53ca\u5341\u4e00\u4e86\uff1f\u201d \u3000\u3000\u201c\u5929\u72d0\u592b\u4eba\u4e5f\u63a8\u5d07\u8fc7\u5979\u7684\u6280\u827a\u2026\u2026\u201d\u7f57\u5170\u8bf4\uff0c\u201c\u6c5f\u67ab\u4e00\u51fa\u9053\u5c31\u627e\u4e0a\u5979\uff0c\u5c82\u662f\u65e0\u56e0\uff0c\u4e0d\u8fc7\u5979\u597d\u50cf\u544a\u8bc9\u8fc7\u6211\uff0c\u4e00\u79cd\u6f5c\u9690\u79d8\u672f\uff0c\u5c06\u968f\u5979\u6c38\u57cb\u6cc9\u4e0b\uff0c\u60f3\u4e0d\u5230\uff0c\u7adf\u7136\u4f1a\u6539\u53d8\u5fc3\u610f\uff0c\u80af\u628a\u5b83\u4f20\u4e0b\u6765\u4e86\uff01\u201d \u3000\u3000\u6b27\u9633\u662d\u9053\uff1a\u201c\u60c5\u4e4b\u4e3a\u7269\uff0c\u771f\u53eb\u4eba\u751f\u6b7b\u76f8\u8bb8\uff0c\u5f20\u56db\u59d1\u6539\u53d8\u5fc3\u610f\u6bc1\u5f03\u8a93\u8a00\uff0c\u662f\u4e0d\u662f\u4e3a\u60c5\u6240\u7d2f\uff0c\u8ff7\u604b\u4e0a\u4e86\u6c5f\u67ab\u2026\u2026\u201d \u3000\u3000\u201c\u6c5f\u67ab\u4fee\u4e60\u5929\u72d0\u529f\uff0c\u5df2\u6709\u4e86\u76f8\u5f53\u6df1\u7684\u706b\u5019\u3002\u56db\u59d1\u8ff7\u604b\u4ed6\uff0c\u5e94\u5c5e\u5e38\u60c5\uff0c\u201d\u7f57\u5170\u9053\uff1a\u201c\u4f46\u5979\u80af\u628a\u7edd\u6280\u4f20\u7ed9\u4e24\u4e2a\u4e2b\u5934\uff0c\u90a3\u53ef\u662f\u770b\u5728\u6211\u8ddf\u5979\u4ea4\u8c0a\u7684\u60c5\u5206\u4e4b\u4e0a\u3002\u201d \u3000\u3000\u201c\u6211\u770b\u5979\u4e5f\u662f\u4e3a\u4e86\u6c5f\u67ab\uff0c\u201d\u6b27\u9633\u662d\u9053\uff1a\u201c\u54b1\u4eec\u7834\u9664\u4e86\u4e16\u4fd7\u4e4b\u89c1\uff0c\u8ba9\u4e24\u4e2a\u5b9d\u8d1d\u5973\u513f\u5e94\u52ab\u6c5f\u6e56\uff0c\u52a9\u6c5f\u67ab\u4e00\u81c2\u4e4b\u529b\uff0c\u56db\u59d1\u7231\u5c4b\u53ca\u4e4c\uff0c\u4f20\u827a\u916c\u60c5\u2026\u2026\u201d \u3000\u3000\u201c\u4e5f\u8bb8\u4f60\u8bf4\u7684\u6709\u7406\uff0c\u4e0d\u8fc7\uff0c\u6700\u91cd\u8981\u7684\u8fd8\u662f\u5979\u90a3\u4e9b\u6280\u827a\uff0c\u4e5f\u6709\u70b9\u90aa\u6c14\uff0c\u2026\u2026\u201d\u7f57\u5170\u8bf4\uff0c\u201c\u5979\u53ef\u4ee5\u65e9\u4e9b\u4f20\u7ed9\u6c5f\u67ab\u7684\uff0c\u4f46\u5979\u6ca1\u6709\uff0c\u90a3\u662f\u56e0\u4e3a\u6c5f\u67ab\u4e0d\u80fd\u627f\u7ee7\u5979\u7684\u8863\u94b5\uff0c\u5979\u4e0d\u80fd\u628a\u5e08\u95e8\u7edd\u6280\uff0c\u968f\u4fbf\u7684\u62f1\u624b\u9001\u4eba\uff0c\u5979\u5e26\u4e24\u4e2a\u4e2b\u5934\u4e0b\u5c71\u65f6\uff0c\u5df2\u89c6\u4e3a\u8863\u94b5\u5f1f\u5b50\uff0c\u9690\u6280\u4e0d\u4f20\uff0c\u5927\u7ea6\u662f\u4e0d\u613f\u4e24\u4e2a\u4e2b\u5934\u6cbe\u90aa\u6c14\uff0c\u6211\u770b\u5979\u662f\u542c\u5230\u6211\u4eec\u4e24\u4e2a\u5b9d\u8d1d\u5973\u513f\u65e9\u5df2\u4e60\u8fc7\u5929\u72d0\u592b\u4eba\u7684\u6b66\u529f\uff0c\u624d\u4e0b\u5b9a\u4e86\u4f20\u827a\u4e4b\u5fc3\uff0c\u5df2\u7ecf\u6ce1\u5728\u4e86\u6c34\u91cc\u7684\u4eba\uff0c\u4e0d\u4f1a\u518d\u611f\u89c9\u5230\u96e8\u6ef4\u662f\u6e7f\u7684\u4e86\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u56db\u59d1\u5bf9\u4e24\u4e2a\u4e2b\u5934\u7684\u7231\u62a4\u4e4b\u5fc3\uff0c\u4e0d\u5728\u6211\u4eec\u592b\u5987\u4e4b\u4e0b\u4e86\u3002\u201d\u6b27\u9633\u662d\u6709\u4e9b\u611f\u6168\u7684\u8bf4\u3002 \u3000\u3000\u201c\u6709\u8fc7\u4e4b\u800c\u65e0\u4e0d\u53ca\uff0c\u201d\u7f57\u5170\u76ee\u5149\u8f6c\u6ce8\u5728\u4e08\u592b\u7684\u8138\u4e0a\uff0c\u9053\uff1a\u201c\u6211\u4eec\u5df2\u8fc7\u4e86\u4e8c\u5341\u51e0\u5e74\u7684\u751c\u871c\u751f\u6d3b\uff0c\u6b64\u756a\u4e0b\u5c71\uff0c\u5c65\u884c\u6211\u4eec\u5f53\u5e74\u7684\u627f\u8bfa\uff0c\u5168\u529b\u5e2e\u52a9\u6c5f\u67ab\uff0c\u4e00\u65e6\u548c\u654c\u4eba\u5bf9\u4e0a\u4e86\uff0c\u53ea\u6015\u6211\u5c31\u6ca1\u95f2\u6687\u4f59\u60c5\u6765\u7167\u987e\u4f60\u4e86\uff0c\u53ef\u4e0d\u8bb8\u80e1\u601d\u4e71\u60f3\uff0c\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\u2026\u2026\u201d\u6b27\u9633\u662d\u54c8\u54c8\u4e00\u7b11\u53c8\u9053\uff1a\u201c\u8fde\u4e24\u4e2a\u5fc3\u809d\u5b9d\u8d1d\u5973\u513f\u90fd\u820d\u4e86\uff0c\u8fd8\u6709\u4ec0\u4e48\u653e\u4e0d\u5f00\u7684\uff0c\u7531\u6b64\u523b\u8d77\uff0c\u6211\u4eec\u4e00\u5bb6\u4e3a\u6b66\u6797\u5927\u5c40\u751f\uff0c\u4e5f\u4e3a\u6b66\u6797\u5927\u5c40\u800c\u6b7b\uff0c\u5170\u513f\uff0c\u653e\u5fc3\u5427\uff01\u4f60\u5c3d\u4f60\u6240\u80fd\u7684\u53bb\u5e2e\u52a9\u6c5f\u67ab\uff0c\u6211\u4e5f\u4f1a\u4e3a\u4ed6\u8d74\u6c64\u8e48\u706b\uff0c\u4e07\u6b7b\u4e0d\u8f9e\u3002\u201d \u3000\u3000\u7f57\u5170\u7f13\u7f13\u504e\u5165\u4e86\u4e08\u592b\u7684\u6000\u4e2d\uff0c\u6cea\u6c34\u6c69\u6c69\u800c\u51fa\u3002 \u3000\u3000\u5947\u602a\uff0c\u50cf\u6b27\u9633\u662d\u8fd9\u79cd\u82f1\u96c4\u6c14\u6982\u7684\u4eba\u7269\uff0c\u7adf\u7136\u4e5f\u5fcd\u4e0d\u4f4f\u6d41\u4e0b\u4e86\u6cea\u6c34\u2026\u2026\u3002 \u3000\u3000\u4f46\u4e24\u4eba\u7684\u7709\u5b87\u95f4\uff0c\u5374\u6d0b\u6ea2\u7740\u4e00\u79cd\u820d\u8eab\u9504\u9b54\u7684\u4fa0\u4e49\u6c14\u6168\uff0c\u8bde\u751f\u51fa\u6c42\u4ec1\u6551\u4e16\u7684\u5723\u6d01\u5149\u8f89\u2026\u2026 \u3000\u3000\u5f20\u56db\u59d1\u679c\u7136\u503e\u56ca\u76f8\u6388\uff0c\u628a\u79fb\u82b1\u63a5\u6728\u548c\u4e94\u884c\u6f5c\u672f\uff0c\u4f5c\u4e86\u4e00\u756a\u4ed4\u7ec6\u7684\u89e3\u8bf4\uff0c\u90a3\u662f\u4e00\u79cd\u5229\u7528\u8272\u5f69\u6548\u679c\u548c\u8f6c\u66ff\u65b9\u6cd5\uff0c\u7ed3\u5408\u6210\u7684\u6280\u827a\uff0c\u5f53\u628a\u9ad8\u5f3a\u7684\u6b66\u529f\u6eb6\u5165\u8fd9\u95e8\u6280\u827a\u4e2d\uff0c\u4f1a\u4ea7\u751f\u66f4\u597d\u7684\u6548\u679c\u3002 \u3000\u3000\u5979\u8be6\u7ec6\u89e3\u8bf4\u6280\u5de7\u6cd5\u95e8\uff0c\u4ee5\u6c5f\u67ab\u548c\u541f\u96ea\u3001\u541f\u971c\u7684\u806a\u660e\uff0c\u5f88\u5feb\u9886\u609f\u4e86\u4e2a\u4e2d\u8981\u8bc0\u3002 \u3000\u3000\u4e09\u4e2a\u4eba\u90fd\u6709\u7740\u5929\u6293\u592b\u4eba\u7684\u6b66\u529f\u57fa\u7840\uff0c\u90a3\u662f\u5145\u6ee1\u7740\u673a\u53d8\u8be1\u8bc8\u7684\u6b66\u529f\uff0c\u5e26\u7740\u4e09\u5206\u90aa\u6c14\uff0c\u5c06\u58f0\u3001\u8272\u8868\u60c5\u5168\u6eb6\u5165\u4e86\u6b66\u529f\u53d8\u5316\u4e4b\u5185\uff0c\u6709\u4e86\u8fd9\u4e9b\u57fa\u7840\uff0c\u5b66\u4e60\u65c1\u95e8\u6280\u827a\uff0c\u5c31\u6709\u7740\u5f97\u5fc3\u5e94\u624b\uff0c\u7279\u522b\u5feb\u901f\u7684\u611f\u89c9\u3002 \u3000\u3000\u56e0\u4e3a\uff0c\u5f20\u56db\u59d1\u8fd9\u4e9b\u6280\u827a\uff0c\u4e5f\u5c5e\u4e8e\u65c1\u95e8\u5de6\u9053\u4e4b\u5b66\u3002 \u3000\u3000\u665a\u9910\u8fd9\u540e\uff0c\u6c5f\u67ab\u6d17\u4e2a\u6fa1\u6362\u8fc7\u8863\u670d\uff0c\u51c6\u5907\u79bb\u5f00\u98ce\u96ea\u5c0f\u9986\uff0c\u4f46\u5374\u88ab\u541f\u96ea\u3001\u541f\u971c\u62e6\u4f4f\u4e86\u53bb\u8def\u3002 \u3000\u3000\u201c\u6c5f\u5927\u54e5\uff0c\u8fd8\u8bb0\u5f97\u6211\u5a18\u8bf4\u8fc7\u7684\u8bdd\u4e48\uff1f\u201d\u541f\u96ea\u4e0d\u518d\u523b\u610f\u63a9\u9970\uff0c\u7b11\u5bb9\u4e2d\u900f\u51fa\u5a07\u5a9a\u4e4b\u6001\uff0c\u9053\uff1a \u3000\u3000\u201c\u5a18\u5df2\u7ecf\u628a\u6211\u4eec\u4ea4\u7ed9\u4f60\u4e86\uff01\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\uff0c\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u4f46\u6211\u8981\u53bb\u5b89\u6392\u4e00\u4e0b\uff0c\u8fc7\u4e00\u5929\uff0c\u6211\u518d\u5e26\u4f60\u4eec\u79bb\u5f00\u8fd9\u91cc\uff01\u201d \u3000\u3000\u201c\u6c5f\u5927\u54e5\uff0c\u6211\u548c\u59d0\u90fd\u4e0d\u4f1a\u4e8e\u9884\u4f60\u4ec0\u4e48\u3002\u2026\u2026\u201d\u541f\u971c\u9053\uff1a\u201c\u6211\u4eec\u7684\u773c\u775b\u4e0d\u597d\uff0c\u4e5f\u6709\u4e9b\u8033\u80cc\uff0c\u4e0d\u8be5\u770b\u5230\u7684\uff0c\u5c31\u7b97\u6446\u5728\u6211\u4eec\u7684\u9762\u524d\uff0c\u6211\u4eec\u4e5f\u770b\u4e0d\u5230\uff0c\u4e0d\u8be5\u542c\u5230\u7684\uff0c\u5c31\u7b97\u5982\u96f7\u8d2f\u8033\uff0c\u6211\u4eec\u4e5f\u542c\u4e0d\u5230\u4e00\u70b9\u58f0\u97f3\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u2026\u2026\u201d\u6c5f\u67ab\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u5df2\u7ecf\u7ec3\u8fc7\u4e86\u5929\u72d0\u5fd8\u60c5\u2026\u2026\u201d \u3000\u3000\u201c\u529f\u592b\u8fd8\u6ca1\u6709\u5230\u5bb6\uff0c\u8fd8\u8981\u4f60\u6c5f\u5927\u54e5\u6307\u70b9\u6307\u70b9\u4e86\u3002\u201d\u53f6\u96ea\u7b11\u9053\uff1a\u201c\u51dd\u795e\u5f52\u5143\uff0c\u89c6\u800c\u4e0d\u89c1\uff0c\u542c\u800c\u4e0d\u95fb\u3002\u201d \u3000\u3000\u201c\u60d1\u5fc3\u672f\u5462\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u7ec3\u8fc7\u6ca1\u6709\uff1f\u201d \u3000\u3000\u201c\u90a3\u662f\u5929\u72d0\u7edd\u6280\u4e4b\u4e00\uff0c\u5c82\u53ef\u4e0d\u7ec3\u2026\u2026\u201d\u541f\u971c\u7b11\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u5a18\u4e00\u76f4\u7b11\u6211\u4eec\u7f3a\u5c11\u8fd9\u65b9\u9762\u7684\u5929\u624d\uff0c\u7ec3\u7684\u4e0d\u6210\u6c14\u5019\uff0c\u4e00\u76f4\u7f3a\u5c11\u72d0\u5a9a\u4e4b\u6c14\u3002\u201d \u3000\u3000\u201c\u5343\u4e07\u4e0d\u53ef\u5bf9\u6211\u65bd\u5c55\uff0c\u201d\u6c5f\u67ab\u6025\u9053\uff1a\u201c\u6211\u2026\u2026\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u5462\uff1f\u2026\u2026\u201d\u541f\u96ea\u9053\uff1a\u201c\u6211\u4eec\u8fd9\u4e00\u95e8\u6b66\u529f\u706b\u5019\u6700\u5dee\uff0c\u6b63\u8981\u5411\u6c5f\u5927\u54e5\u8ba8\u6559\u8ba8\u6559\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u884c\uff0c\u4e0d\u884c\u2026\u2026\u201d\u6c5f\u67ab\u6025\u9053\uff1a\u201c\u6211\u5df2\u7834\u4e86\u8eab\u6212\uff0c\u53ea\u6015\u5b9a\u529b\u4e0d\u591f\uff0c\u65e0\u6cd5\u6297\u62d2\uff0c\u4e00\u65e6\u65bd\u672f\u53cd\u51fb\uff0c\u90a3\u5c31\u5929\u4e0b\u5927\u4e71\u4e86\uff01\u201d \u3000\u3000\u53f6\u96ea\u5ae3\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u90a3\u4f1a\u600e\u4e48\u6837\u5462\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u6b63\u5bb9\u8bf4\u9053\uff1a\u201c\u540e\u679c\u4e0d\u53ea\u53ef\u6015\uff0c\u800c\u4e14\u53ef\u60b2\uff0c\u5343\u4e07\u4e0d\u53ef\u5c1d\u8bd5\uff0c\u4f60\u4eec\u5982\u662f\u4e0d\u80af\u5e94\u5141\uff0c\u6211\u53ea\u6709\u656c\u8c22\u4e24\u4f4d\u63f4\u624b\u4e4b\u60c5\u4e86\u3002\u201d \u3000\u3000\u4e8c\u5973\u76f8\u4e92\u671b\u4e86\u4e00\u773c\u3002 \u3000\u3000\u541f\u971c\u63a5\u9053\uff1a\u201c\u597d\u5427\uff01\u4f60\u8bf4\u7684\u5982\u6b64\u4e25\u91cd\uff0c\u6211\u4eec\u8bb0\u4e0b\u5c31\u662f\u3002\u201d \u3000\u3000\u6c5f\u67ab\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u5728\u8fd9\u91cc\u7b49\u6211\uff0c\u4e24\u5929\u4e4b\u5185\uff0c\u6211\u6765\u63a5\u4f60\u4eec\uff01\u201d \u3000\u3000\u201c\u73b0\u5728\u8ddf\u4f60\u4e00\u8d77\u8d70\u4e0d\u884c\u5417\uff1f\u2026\u2026\u201d\u53f6\u96ea\u9053\uff1a\u201c\u6211\u4eec\u6709\u7740\u7167\u987e\u81ea\u5df1\u7684\u80fd\u529b\uff0c\u5bf9\u4f60\u53ea\u6709\u5e2e\u52a9\uff0c\u4e0d\u4f1a\u62d6\u7d2f\u3002\u201d \u3000\u3000\u201c\u4eca\u5929\u4e0d\u884c\uff0c\u6211\u8fd8\u662f\u6d1b\u9633\u5c45\u4e2d\u7684\u526f\u603b\u7ba1\uff0c\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u5e26\u4f60\u4eec\u56de\u53bb\uff0c\u5f88\u96be\u89e3\u8bf4\u6e05\u695a\uff0c\u4e00\u5b9a\u5f97\u7a0d\u4f5c\u5b89\u6392\u3002\u201d \u3000\u3000\u4e8c\u5973\u987a\u4ece\u5730\u70b9\u70b9\u5934\uff0c\u9f50\u58f0\u9053\uff1a\u201c\u6211\u4eec\u7b49\u4f60\u4e24\u5929\uff0c\u6c5f\u5927\u54e5\u4e0d\u4f1a\u5931\u4fe1\u5427\uff1f\u201d \u3000\u3000\u679c\u7136\u662f\u72d0\u6027\u5df2\u663e\uff0c\u72d0\u6027\u591a\u7591\uff01 \u3000\u3000\u201c\u4e0d\u4f1a\uff0c\u4e24\u5929\u5185\uff0c\u6211\u4e00\u5b9a\u6765\u3002\u201d \u3000\u3000\u6446\u8131\u4e86\u4e8c\u5973\uff0c\u6c5f\u67ab\u6025\u6025\u8d76\u56de\u6d1b\u9633\u5c45\uff0c\u53ea\u89c1\u4e03\u5b9d\u548c\u5c1a\u8eab\u7740\u9526\u888d\uff0c\u5934\u6234\u65b9\u5dfe\uff0c\u7d27\u968f\u90ed\u5929\u540c\u7684\u8eab\u4fa7\uff0c\u6b63\u5728\u5404\u5904\u5de1\u89c6\u3002 \u3000\u3000\u90ed\u5929\u540c\u884c\u52a8\u81ea\u7136\uff0c\u4e00\u8138\u51b7\u6f20\uff0c\u679c\u7136\u5df2\u628a\u97e9\u9738\u6a21\u4eff\u5f97\u5341\u5206\u795e\u4f3c\u3002 \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u660e\u660e\u770b\u5230\u4e86\u6c5f\u67ab\uff0c\u4f46\u5374\u89c6\u800c\u4e0d\u89c1\uff0c\u8fde\u671b\u4e5f\u672a\u591a\u671b\u6c5f\u67ab\u4e00\u773c\u3002 \u3000\u3000\u597d\u5389\u5bb3\u7684\u9152\u8089\u548c\u5c1a\uff0c\u9547\u9759\u7684\u5de5\u592b\uff0c\u5df2\u5230\u6cb9\u76d0\u4e0d\u6d78\u7684\u5883\u754c\u3002 \u3000\u3000\u4ed6\u4e0d\u4f46\u81ea\u5df1\u795e\u8272\u4e0d\u52a8\uff0c\u800c\u4e14\uff0c\u5de7\u5999\u5730\u4e00\u4e2a\u8f6c\u8eab\uff0c\u906e\u4f4f\u4e86\u90ed\u5929\u540c\u7684\u89c6\u7ebf\u3002 \u3000\u3000\u4ee5\u6c5f\u67ab\u4e4b\u80fd\uff0c\u7adf\u7136\u65e0\u6cd5\u5224\u65ad\u51fa\u90ed\u5929\u540c\u662f\u5426\u770b\u5230\u4e86\u4ed6\uff0c\u5982\u82e5\u662f\u770b\u5230\u4e86\uff0c\u8fd9\u5e74\u8f7b\u4eba\u7684\u9547\u9759\u5de5\u592b\uff0c\u4e5f\u5217\u5165\u4e86\u4e0a\u4e58\u8eab\u624b\u3002 \u3000\u3000\u6c5f\u67ab\u5f88\u6ee1\u610f\u8fd9\u51e0\u4e2a\u5e08\u5f92\u3001\u7236\u5b50\u4eec\u7684\u8868\u73b0\uff0c\u4ed6\u4eec\u5728\u5f3a\u654c\u73af\u4f3a\u7684\u6d1b\u9633\u5c45\u4e2d\uff0c\u8868\u73b0\u7684\u6070\u5982\u5176\u5206\u3002 \u3000\u3000\u76ee\u524d\u8fd9\u5e73\u9759\u7684\u6d1b\u9633\u5c45\uff0c\u5df2\u88ab\u6c5f\u67ab\u70b9\u8d77\u4e86\u51e0\u5904\u706b\u5934\uff0c\u4e0d\u8bba\u90a3\u4e00\u5904\u706b\u52bf\u5192\u51fa\u6765\uff0c\u7acb\u523b\u5c31\u6380\u8d77\u4e00\u9635\u60ca\u98ce\u9a87\u6d6a\u2026\u2026\u3002 \u3000\u3000\u4f46\u4f7f\u6c5f\u67ab\u4e0d\u89e3\u7684\u662f\u4f55\u4ee5\u738b\u9f99\u4e4b\u6b7b\uff0c\u7adf\u7136\u88ab\u538b\u4e86\u4e0b\u53bb\uff0c\u4e00\u4e2a\u526f\u603b\u7ba1\u8eab\u4efd\u7684\u4eba\uff0c\u6b7b\u4e86\u7adf\u65e0\u4eba\u8ffd\u67e5\u3002 \u3000\u3000\u6885\u82b1\u8fd9\u6761\u7ebf\u8be5\u5f15\u51fa\u4e00\u4e9b\u4ec0\u4e48\u6837\u7684\u98ce\u6ce2\uff0c\u6c5f\u67ab\u65e0\u6cd5\u4f30\u7b97\uff1f \u3000\u3000\u8fd9\u662f\u51fa\u9053\u4ee5\u6765\uff0c\u7b2c\u4e00\u6b21\u6210\u529f\u7684\u65bd\u5c55\u51fa\u201c\u60d1\u5fc3\u672f\u201d\uff0c\u6885\u82b1\u9677\u5165\u591a\u6df1\uff0c\u80fd\u5426\u81ea\u62d4\uff0c\u6c5f\u67ab\u90fd\u65e0\u6cd5\u4f30\u8ba1\uff0c\u8fd9\u4e24\u5929\u6545\u610f\u907f\u5f00\u6885\u82b1\uff0c\u7528\u4ee5\u6c42\u8bc1\u6548\u679c\u3002 \u3000\u3000\u73b0\u5728\uff0c\u6c5f\u67ab\u6b63\u884c\u5411\u6885\u82b1\u5385\u3002 \u3000\u3000\u6c5f\u67ab\u539f\u60f3\u7531\u6b27\u9633\u662d\u592b\u5987\u53e3\u4e2d\u63a2\u7d22\u51fa\uff0c\u51e0\u4f4d\u5e08\u957f\u66ff\u4ed6\u5e03\u4e0b\u7684\u540e\u63f4\u529b\u91cf\uff0c\u6709\u591a\u5c11\u53ef\u7528\u7684\u4eba\uff0c\u4f46\u5374\u4f7f\u6c5f\u67ab\u975e\u5e38\u7684\u5931\u671b\uff0c\u7adf\u7136\u53ea\u6709\u6b27\u9633\u662d\u4e00\u5bb6\u56db\u53e3\u3002 \u3000\u3000\u5f53\u7136\uff0c\u6c5f\u67ab\u5fc3\u4e2d\u660e\u767d\uff0c\u8fd9\u56db\u4e2a\u4eba\u529b\u91cf\u5341\u5206\u5f3a\u5927\u3002 \u3000\u3000\u6b27\u9633\u662d\u592b\u5987\u7684\u6b66\u529f\u9020\u8be3\uff0c\u8db3\u53ef\u548c\u5f53\u4eca\u6b66\u6797\u4e2d\u7b2c\u4e00\u6d41\u9ad8\u624b\u5bf9\u6297\uff0c\u6700\u610f\u5916\u662f\u96ea\u3001\u971c\u4e8c\u5973\uff0c\u7adf\u7136\u662f\u4e60\u7ec3\u4e86\u5929\u72d0\u592b\u4eba\u7684\u6b66\u529f\uff0c\u8fd9\u521d\u671f\u53cd\u51fb\u884c\u52a8\u4e2d\uff0c\u5012\u662f\u771f\u5e94\u4e86\u4ee5\u6bd2\u653b\u6bd2\u7684\u7b56\u5212\uff0c\u90aa\u672f\u5927\u5bf9\u51b3\u3002 \u3000\u3000\u5fd6\u601d\u4e4b\u95f4\uff0c\u5df2\u884c\u8fd1\u6885\u82b1\u5385\u5916\u3002 \u3000\u3000\u4eca\u591c\u6885\u82b1\u5385\u4e2d\u6709\u5bb4\u4f1a\uff0c\u5385\u4e2d\u706f\u706b\u8f89\u714c\uff0c\u900f\u51fa\u4e86\u7d27\u95ed\u7684\u95e8\u7a97\u4e4b\u5916\u3002 \u3000\u3000\u4ec0\u4e48\u4eba\u4f1a\u5728\u6885\u82b1\u5385\u4e2d\u5bb4\u5ba2\uff1f \u3000\u3000\u56e0\u4e3a\uff0c\u90a3\u662f\u4e00\u5ea7\u5c0f\u5de7\u7684\u5385\u5802\uff0c\u5ba2\u4eba\u4e0d\u4f1a\u592a\u591a\uff0c\u8d85\u8fc7\u56db\u4e2a\u5ba2\u4eba\uff0c\u5c31\u4f1a\u6709\u7740\u62e5\u6324\u7684\u611f\u89c9\u3002 \u3000\u3000\u4f46\u8fd9\u91cc\u4ef7\u683c\u5374\u53c8\u7279\u522b\u6602\u8d35\uff0c\u4e0d\u662f\uff5e\u822c\u4eba\u80fd\u591f\u8d1f\u62c5\u5f97\u8d77\u3002 \u3000\u3000\u6c5f\u67ab\u7f13\u6b65\u5411\u6885\u82b1\u5385\u4e2d\u884c\uff0c\u8fd8\u6709\u4e00\u4e08\u5de6\u53f3\u65f6\uff0c\u82b1\u6811\u4e1b\u4e2d\uff0c\u7a81\u7136\u95ea\u51fa\u4e86\u4e00\u4e2a\u5168\u8eab\u9ed1\u8863\u7684\u5e97\u4f19\u8ba1\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u5e74\u7eaa\u5f88\u8f7b\uff0c\u4f46\u8eab\u6cd5\u5947\u5feb\uff0c\u4eba\u5f71\u4e00\u95ea\uff0c\u5df2\u62e6\u5728\u6c5f\u67ab\u7684\u8eab\u524d\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u7530\u516c\u5b50\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u662f\u5e94\u6885\u82b1\u59d1\u5a18\u9080\u7ea6\u800c\u6765\u3002\u201d \u3000\u3000\u201c\u6240\u4ee5\uff0c\u6211\u624d\u5728\u8fd9\u91cc\u7b49\u4f60\u2026\u2026\u201d\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u8ddf\u6211\u6765\u5427\uff01\u5148\u5230\u83ca\u82b1\u5385\u4e2d\u5750\u5750\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\uff0c\u6211\u2026\u2026\u201d \u3000\u3000\u603b\u4e0d\u80fd\u7a7a\u5165\u5b9d\u5c71\u800c\u56de\uff0c\u6c5f\u67ab\u60f3\u95ee\u51fa\u4e00\u70b9\u6d88\u606f\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u4e00\u628a\u6293\u4f4f\u4e86\u6c5f\u67ab\u7684\u53f3\u8155\uff0c\u62d6\u5165\u82b1\u6811\u6797\u4e2d\uff0c\u9053\uff1a\u201c\u6885\u82b1\u59d1\u5a18\u5fd9\u5f97\u5f88\uff0c\u73b0\u5728\u4e0d\u80fd\u89c1\u4f60\uff0c\u7279\u522b\u4ea4\u4ee3\u6211\u5728\u8fd9\u91cc\u7b49\u4f60\uff0c\u6211\u5df2\u7ecf\u5728\u51b7\u98ce\u4e2d\u7ad9\u4e86\u534a\u4e2a\u65f6\u8fb0\u3002\u201d \u3000\u3000\u201c\u5bf9\u4e0d\u4f4f\u5566\uff01\u201d\u6c5f\u67ab\u6ca1\u6709\u53cd\u6297\uff0c\u5fae\u5e26\u6b49\u610f\u5730\u8bf4\u9053\uff1a\u201c\u5728\u4e0b\u89c1\u5230\u6885\u82b1\u59d1\u5a18\u4e4b\u540e\uff0c\u4e00\u5b9a\u8981\u5979\u8c22\u8c22\u8001\u5144\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u662f\u8bf4\uff0c\u6885\u82b1\u59d1\u5a18\u4f1a\u542c\u4f60\u7684\u8bdd\u5417\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u5fc3\u4e2d\u4e00\u52a8\uff0c\u7acb\u65f6\u63d0\u9ad8\u4e86\u8b66\u89c9\uff0c\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u5728\u4e0b\u5c31\u4e0d\u6e05\u695a\u4e86\uff0c\u5509\uff01\u98ce\u6708\u573a\u4e2d\u7684\u751c\u8a00\u871c\u8bed\uff0c\u786e\u4e5f\u5f53\u4e0d\u5f97\u771f\u3002\u201d \u3000\u3000\u201c\u4e0d\u8fc7\uff0c\u6885\u82b1\u59d1\u5a18\u5bf9\u4f60\u8fd8\u662f\u4e0d\u9519\u2026\u2026\u201d\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u5979\u7ea6\u4f60\u6367\u573a\uff0c\u56e0\u65e0\u6687\u89c1\u4f60\uff0c\u7adf\u4e3a\u4f60\u5b89\u6392\u4e86\u83ca\u82b1\u5385\u4e2d\u7684\u5c0f\u5bb4\uff0c\u8fd9\u53ef\u662f\u4ece\u672a\u6709\u8fc7\u7684\u4e8b\uff01\u201d \u3000\u3000\u542c\u5230\u6367\u573a\u4e24\u5b57\uff0c\u6c5f\u67ab\u5fc3\u4e2d\u7684\u7591\u8651\u5c3d\u6d88\uff0c\u8fd9\u4e2a\u9ed1\u8863\u4eba\uff0c\u628a\u4ed6\u770b\u6210\u4e86\u6885\u82b1\u77f3\u69b4\u88d9\u4e0b\u4e4b\u81e3\u2026\u2026\u201d \u3000\u3000\u4f46\u8fd9\u4e5f\u8bf4\u660e\u4e86\u6885\u82b1\u7684\u4e3e\u6b62\u5341\u5206\u5c0f\u5fc3\uff0c\u7edd\u4e0d\u8f7b\u6613\u628a\u5fc3\u4e2d\u4e4b\u79d8\u6cc4\u6f0f\u7ed9\u4eba\u3002 \u3000\u3000\u201c\u53bb\u770b\u770b\u79cb\u82b1\u53cc\u5a62\u4e5f\u597d\uff0c\u5728\u6d1b\u9633\u5c45\u4e2d\u80fd\u548c\u9752\u51e4\u76f8\u6297\u7684\uff0c\u5927\u6982\u4ee5\u79cb\u3001\u51ac\u82b1\u5a62\u4e3a\u9996\u4e86\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u5fc3\u4e2d\u6697\u6697\u5fd6\u9053\uff1a\u201c\u5982\u80fd\u628a\u79cb\u82b1\u53cc\u5a62\u4e5f\u4e89\u53d6\u8fc7\u6765\uff0c\u5927\u6982\u53ef\u4ee5\u63a7\u5236\u6d1b\u9633\u5c45\u4e2d\u5341\u4e4b\u4e03\u516b\u7684\u5b9e\u529b\u4e86\u3002\u201d \u3000\u3000\u83ca\u82b1\u5385\u4e2d\u53ea\u71c3\u7740\u4e00\u53ea\u7ea2\u70db\uff0c\u5149\u4eae\u6709\u4e9b\u6697\u6de1\uff0c\u7a7a\u65e0\u4ed6\u4eba\u3002 \u3000\u3000\u201c\u4f60\u5750\u4e00\u4f1a\u5427\uff01\u6211\u8981\u8d70\u4e86\u2026\u2026\u201d \u3000\u3000\u9ed1\u8863\u4eba\u8bf4\u5b8c\uff0c\u8f6c\u8eab\u5c31\u8d70\u3002 \u3000\u3000\u201c\u7b49\u4e00\u4e0b\uff01\u201d \u3000\u3000\u6c5f\u67ab\u4ed4\u7ec6\u5730\u6253\u91cf\u90a3\u9ed1\u8863\u4eba\uff0c\u7adf\u662f\u4ece\u672a\u89c1\u8fc7\u7684\u4eba\u3002 \u3000\u3000\u8fd9\u53c8\u4f7f\u6c5f\u67ab\u53d1\u73b0\u4e86\u4e00\u693f\u9690\u5bc6\uff0c\u8fd9\u6d1b\u9633\u5c45\u4e2d\uff0c\u8fd8\u9690\u85cf\u7740\u4e00\u4e9b\u770b\u3001\u4e0d\u89c1\u7684\u529b\u91cf\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u8138\u4e0a\u6709\u4e9b\u4e0d\u8010\u7684\u795e\u60c5\uff0c\u9053\uff1a\u201c\u8fd8\u6709\u4ec0\u4e48\u4e8b\u554a\uff1f\u201d \u3000\u3000\u201c\u5728\u4e0b\u4e00\u4e2a\u4eba\u5750\u5728\u8fd9\u91cc\u65b9\u4fbf\u5417\uff1f\u2026\u2026\u8fd9\u53ea\u9ad8\u71c3\u7684\u7ea2\u70db\uff0c\u662f\u8868\u793a\u5df2\u7ecf\u6709\u4eba\u5b9a\u4e86\u8fd9\u4e2a\u623f\u95f4\u2026\u2026\uff0c\u201d\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u8fd9\u4e2a\u4eba\u5c31\u662f\u4f60\uff0c\u83ca\u82b1\u3001\u6842\u82b1\u90fd\u5728\u5fd9\uff0c\u5979\u4eec\u817e\u51fa\u8eab\u5b50\uff0c\u81ea\u4f1a\u8fc7\u6765\u62db\u547c\u4f60\uff0c\u8001\u5144\uff0c\u4f60\u8eab\u4e0a\u53ea\u8981\u5e26\u4e86\u8db3\u591f\u7684\u94f6\u5b50\uff0c\u5305\u4f60\u662f\u5e73\u5b89\u65e0\u4e8b\u3002 \u3000\u3000\u5982\u679c\u662f\u6ca1\u6709\u94b1\uff0c\u90a3\u5c31\u5f88\u96be\u8bf4\u4e86\uff0c\u8fd9\u91cc\u662f\u957f\u5b89\u7684\u9500\u91d1\u7a9f\u554a\uff01\u8fd9\u91cc\u6709\u6700\u7f8e\u7684\u59d1\u5a18\uff0c\u6700\u597d\u7684\u9152\u83dc\uff0c\u53ef\u8981\u9ec4\u91d1\u3001\u767d\u94f6\u94fa\u6210\u8def\uff0c\u624d\u80fd\u5927\u6447\u5927\u6446\u7684\u8d70\u8fdb\u6765\u3002\u201d \u3000\u3000\u201c\u8bf4\u7684\u4e5f\u662f\u554a\uff01\u5728\u4e0b\u548c\u6885\u82b1\u59d1\u5a18\uff0c\u53ea\u4e0d\u8fc7\u89c1\u4e86\u4e09\u6b21\u9762\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u6709\u4e9b\u5fc3\u75bc\u5730\u8bf4\uff1a\u201c\u5df2\u7ecf\u82b1\u53bb\u4e86\u6211\u5e26\u6765\u7684\u4e00\u534a\u94f6\u5b50\u3002\u201d \u3000\u3000\u94f6\u5b50\u4f3c\u662f\u5f15\u8d77\u4e86\u9ed1\u8863\u4eba\u7684\u5174\u8da3\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u7684\u4e00\u534a\u94f6\u5b50\u662f\u591a\u5c11\uff1f\u201d \u3000\u3000\u201c\u4e09\u4e07\u4e24\u5427\uff01\u90a3\u53ef\u662f\u7acb\u53ef\u5151\u73b0\u7684\u94f6\u7968\u554a\uff01\u201d\u6c5f\u67ab\u76b1\u8d77\u4e86\u7709\u5934\u8bf4\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u5c31\u96be\u602a\u6885\u82b1\u59d1\u5a18\u4f1a\u5bf9\u4f60\u53e6\u773c\u770b\u5f85\u4e86\u2026\u2026\u201d\u8f6c\u8eab\u4e00\u8dc3\uff0c\u6d88\u5931\u5728\u95e8\u5916\u7684\u591c\u8272\u4e2d\u3002 \u3000\u3000\u6c5f\u67ab\u6697\u9053\uff1a\u201c\u8fd9\u5bb6\u4f19\u4e0d\u50cf\u4e00\u822c\u5e94\u4ed8\u4eba\u5458\u90a3\u6837\u62d8\u8c28\uff0c\u800c\u4e14\u8eab\u624b\u76f8\u5f53\u9ad8\u660e\uff0c\u770b\u8d77\u6765\uff0c\u597d\u50cf\u8fd8\u6709\u70b9\u6c5f\u6e56\u7ecf\u9a8c\u3002\u201d \u3000\u3000\u4f46\u95fb\u5a07\u7b11\u4e4b\u58f0\uff0c\u4f20\u5165\u8033\u9645\uff0c\u4e5f\u6253\u65ad\u4e86\u6c5f\u67ab\u5fc3\u4e2d\u601d\u7eea\u3002 \u3000\u3000\u8f6c\u5934\u770b\u53bb\uff0c\u53ea\u89c1\u83ca\u82b1\u3001\u6842\u82b1\uff0c\u9c7c\u8d2f\u76f8\u968f\u7740\u884c\u5165\u4e86\u5385\u4e2d\u3002 \u3000\u3000\u6c5f\u67ab\u5bf9\u4e8c\u5973\uff0c\u65e9\u5df2\u8033\u719f\u80fd\u8be6\uff0c\u4f46\u79cb\u82b1\u53cc\u5a62\u90fd\u662f\u7b2c\u4e00\u6b21\u89c1\u5230\u6c5f\u67ab\u771f\u6b63\u9762\u76ee\uff0c\u56db\u53ea\u773c\u775b\u76ef\u5728\u6c5f\u67ab\u7684\u8eab\u4e0a\u6253\u91cf\u3002 \u3000\u3000\u5385\u4e2d\u7684\u706f\u5149\u867d\u4e0d\u660e\u4eae\uff0c\u4f46\u79cb\u82b1\u53cc\u5a62\u76ee\u5149\u8fc7\u4eba\uff0c\u81ea\u7136\u628a\u6c5f\u67ab\u7531\u5934\u5230\u811a\uff0c\u770b\u4e2a\u6e05\u6e05\u695a\u695a\u3002 \u3000\u3000\u6c5f\u67ab\u9690\u85cf\u4e86\u4e00\u5207\uff0c\u6784\u6210\u9b45\u529b\u7684\u7279\u8d28\u3002 \u3000\u3000\u73b0\u5728\uff0c\u4ed6\u53ea\u662f\u4e00\u4e2a\u666e\u901a\u4eba\uff0c\u8eab\u6750\u7626\u524a\uff0c\u4e94\u5b98\u6e05\u79c0\u7684\u7537\u4eba\u3002 \u3000\u3000\u83ca\u82b1\u3001\u6842\u82b1\uff0c\u4f3c\u662f\u90fd\u559d\u4e86\u4e0d\u5c11\u7684\u9152\uff0c\u8138\u8272\u9c9c\u7ea2\uff0c\u9152\u6c14\u903c\u4eba\u3002 \u3000\u3000\u53ea\u542c\u6842\u82b1\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6211\u8fd8\u8ba4\u4e3a\u957f\u4e86\u4e24\u4e2a\u9f3b\u5b50\u5462\uff1f\u60f3\u4e0d\u901a\u554a\uff01\u6885\u82b1\u600e\u4f1a\u8981\u6211\u4eec\u7167\u987e\u4ed6\u5462\uff1f\u201d \u3000\u3000\u201c\u81f3\u5c11\uff0c\u4ed6\u4e0d\u96be\u770b\u554a\uff01\u2026\u2026\u201d\u83ca\u82b1\u5fae\u7b11\u7740\u8bf4\uff0c\u201c\u90a3\u5f20\u8138\u6709\u70b9\u7075\u6c14\uff0c\u773c\u775b\u4e5f\u4e0d\u9519\uff0c\u597d\u50cf\u7626\u5f31\u4e00\u70b9\u2026\u2026\u201d \u3000\u3000\u201c\u8bf4\u4ed6\u662f\u767d\u9762\u4e66\u751f\u5427\uff01\u76ae\u80a4\u5374\u53c8\u4e0d\u767d\u2026\u2026\u201d\u6842\u82b1\u9053\uff1a\u201c\u8fd9\u79cd\u4eba\uff0c\u968f\u624b\u5c31\u6293\u4e00\u5927\u628a\uff0c\u6885\u82b1\u600e\u4f1a\u2026\u2026\u201d \u3000\u3000\u201c\u522b\u80e1\u8bf4\uff0c\u201d\u83ca\u82b1\u622a\u4f4f\u4e86\u6842\u82b1\u7684\u8bdd\uff0c\u63a5\u9053\uff1a\u201c\u4e5f\u8bb8\u4eba\u5bb6\u8179\u6709\u8bd7\u4e66\uff0c\u80f8\u85cf\u9526\u7ee3\u5462\uff1f\u201d \u3000\u3000\u6842\u82b1\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5582\uff01\u4f60\u5c31\u662f\u7530\u516c\u5b50\u5427\uff01\u201d \u3000\u3000\u201c\u662f\uff01\u5728\u4e0b\u7530\u7389\u3002\u201d \u3000\u3000\u201c\u6885\u82b1\u8981\u6211\u4eec\u62db\u5f85\u4f60\uff0c\u201d\u6842\u82b1\u62c9\u5f00\u4e00\u5f20\u6905\u5b50\uff0c\u5728\u8eab\u65c1\u5750\u4e0b\uff0c\u9053\uff1a\u201c\u8bf4\u5427\uff01\u60f3\u5403\u4ec0\u4e48\u9152\uff1f\u201d \u3000\u3000\u201c\u7530\u67d0\u4e0d\u4f1a\u559d\u9152\u3002\u201d \u3000\u3000\u201c\u60f3\u5403\u4ec0\u4e48\u5462\uff1f\u201d\u6842\u82b1\u9053\uff1a\u201c\u603b\u4e0d\u80fd\u8fde\u4e1c\u897f\u4e5f\u4e0d\u4f1a\u5403\u5427\uff01\u201d \u3000\u3000\u6c5f\u67ab\u88ab\u8bc4\u5934\u8bba\u8db3\uff0c\u51b7\u8bbd\u70ed\u5632\uff0c\u95f9\u7684\u5fc3\u5934\u706b\u8d77\uff0c\u4f46\u4ed6\u5374\u5f3a\u81ea\u5fcd\u4e0b\uff0c\u4ed6\u60f3\u8bd5\u9a8c\u4e00\u4e0b\uff0c\u4e00\u4e2a\u666e\u666e\u901a\u901a\u7684\u6c5f\u67ab\uff0c\u4f1a\u7ed9\u4eba\u4e00\u4e2a\u4ec0\u4e48\u6837\u7684\u611f\u89c9\uff1f \u3000\u3000\u201c\u53d7\u4eba\u4e4b\u6258\uff0c\u5fe0\u4eba\u4e4b\u4e8b\u2026\u2026\u201d\u6842\u82b1\u53f9\u606f\u4e00\u58f0\uff0c\u63d0\u9ad8\u4e86\u58f0\u97f3\uff0c\u9053\uff1a\u201c\u4eae\u706f\uff0c\u4e0a\u83dc\u3002\u201d \u3000\u3000\u4e00\u4e2a\u9ed1\u8863\u4eba\u8fc5\u5feb\u5954\u6765\uff0c\u6643\u71c3\u4e00\u679a\u706b\u6342\u5b50\uff0c\u70b9\u8d77\u706f\u706b\uff0c\u4ed6\u52a8\u4f5c\u719f\u7ec3\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u56db\u76cf\u5782\u82cf\u5bab\u706f\uff0c\u5c3d\u88ab\u70b9\u71c3\uff0c\u83ca\u82b1\u5385\u4e2d\uff0c\u7acb\u523b\u5927\u653e\u5149\u660e\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u9000\u51fa\u5385\u5916\uff0c\u987a\u624b\u5e26\u4e0a\u623f\u95e8\u3002 \u3000\u3000\u6c5f\u67ab\u65e9\u77e5\u8fd9\u4e9b\u9001\u9152\u83dc\u7684\u9ed1\u8863\u4eba\uff0c\u8eab\u624b\u77eb\u5065\uff0c\u5e73\u5e38\u9690\u8eab\u5ba4\u5916\uff0c\u672a\u7ecf\u62db\u5524\uff0c\u4e0d\u4f1a\u8fdb\u5165\u5385\u4e2d\uff0c\u4e5f\u662f\u4e00\u6279\u8bad\u7ec3\u6709\u7d20\u7684\u4eba\uff0c\u4f46\u4ed6\u5374\u4e0d\u77e5\u9053\u79cb\u82b1\u53cc\u5a62\u548c\u51ac\u5a62\u6885\u82b1\uff0c\u624b\u4e0b\u53e6\u6709\u4e13\u95e8\u5dee\u9063\u7684\u8fd1\u4f8d\u3002 \u3000\u3000\u4eca\u591c\uff0c\u7ec8\u4e8e\u88ab\u6c5f\u67ab\u53d1\u89c9\u4e86\u3002 \u3000\u3000\u8fd9\u4e9b\u4eba\uff0c\u6c5f\u67ab\u90fd\u672a\u89c1\u8fc7\u3002 \u3000\u3000\u6c5f\u67ab\u4e00\u76f4\u7559\u5fc3\u7740\u6d1b\u9633\u5c45\u4e2d\u6bcf\u4e00\u4e2a\u4eba\uff0c\u4e8c\u5341\u56db\u4e2a\u9556\u624b\uff0c\u516b\u5341\u56db\u4e2a\u5e97\u4f19\u8ba1\uff0c\u6bcf\u4e2a\u4eba\u7684\u5f62\u8c8c\uff0c\u4ed6\u90fd\u8bb0\u5728\u5fc3\u4e2d\uff0c\u867d\u7136\u4e0d\u77e5\u540d\u53f7\uff0c\u4f46\u4e00\u89c1\u90fd\u80fd\u8ba4\u5f97\u51fa\u6765\u3002 \u3000\u3000\u4f46\u4eca\u591c\u4e0a\u89c1\u7684\u4e24\u4e2a\u9ed1\u8863\u4eba\uff0c\u6c5f\u67ab\u5df2\u80af\u5b9a\u8fc7\u53bb\u6ca1\u6709\u89c1\u8fc7\uff0c\u5fc3\u8005\u53ca\u6b64\uff0c\u4e0d\u7981\u5fc3\u5934\u4e00\u9707\u3002\u5fd6\u9053\uff1a\u201c\u96be\u9053\u65b0\u6765\u7684\u4eba\uff0c\u7a81\u7136\u4e4b\u95f4\uff0c\u6709\u4e3a\u591a\u9ad8\u624b\u8d76\u6765\u6b64\u5730\uff0c\u662f\u4e0d\u662f\u51c6\u5907\u8981\u6709\u6240\u884c\u52a8\u4e86\uff1f\u4ed6\u4eec\u8981\u5bf9\u4ed8\u4ec0\u4e48\u4eba\u5462\uff1f\u9093\u98de\u548c\u4e03\u5b9d\uff1f\u8fd8\u662f\u94b1\u7f3a\u548c\u6848\u516b\u516c\u90a3\u4e00\u6279\u4eba\uff1f\u2026\u2026\u201d \u3000\u3000\u60f3\u5230\u6b64\u5904\uff0c\u6c5f\u67ab\u7684\u5fc3\u795e\u4e71\u4e86\u3002 \u3000\u3000\u5385\u95e8\u88ab\u4eba\u63a8\u5f00\uff0c\u4e24\u4e2a\u5e74\u8f7b\u7684\u5e97\u4f19\u8ba1\u9001\u4e0a\u9152\u83dc\u3002 \u3000\u3000\u6c5f\u67ab\u4ed4\u7ec6\u770b\u4e86\u4e24\u4eba\u4e00\u773c\uff0c\u8ba4\u51fa\u662f\u6d1b\u9633\u5c45\u4e2d\u539f\u6709\u7684\u4eba\u3002 \u3000\u3000\u6842\u82b1\u659f\u6ee1\u4e86\u4e09\u53ea\u9152\u676f\uff0c\u770b\u4e5f\u4e0d\u770b\u6c5f\u67ab\u4e00\u773c\uff0c\u7aef\u8d77\u4e00\u676f\u9152\uff0c\u9053\uff1a\u201c\u7530\u516c\u5b50\uff0c\u6211\u656c\u4f60\u4e00\u676f\uff0c\u2026\u2026\u201d \u3000\u3000\u4e5f\u4e0d\u7ba1\u6c5f\u67ab\u662f\u5426\u5df2\u7aef\u8d77\u676f\u5b50\uff0c\u81ea\u5df1\u5148\u4e00\u996e\u800c\u5c3d\u3002 \u3000\u3000\u8fd9\u4e2a\u6587\u5f31\u7684\u7537\u4eba\uff0c\u5b9e\u5728\u6ca1\u6709\u770b\u5934\uff0c\u8fd9\u676f\u9152\uff0c\u53ea\u662f\u8868\u793a\u4e86\u5bf9\u6885\u82b1\u7684\u656c\u91cd\u548c\u53cb\u8c0a\u3002 \u3000\u3000\u6c5f\u67ab\u88ab\u6842\u82b1\u4e00\u58f0\u7530\u516c\u5b50\u60ca\u9192\uff0c\u4f46\u5374\u5e76\u672a\u88ab\u6842\u82b1\u7684\u51b7\u6f20\u3001\u8f7b\u89c6\u6fc0\u6012\uff0c\u7aef\u8d77\u9152\u676f\uff0c\u4e00\u53e3\u559d\u4e0b\u3002 \u3000\u3000\u8fd9\u573a\u9152\u559d\u5f97\u4e4f\u5473\u81f3\u6781\uff0c\u6842\u82b1\u8868\u73b0\u7684\u51b7\u6de1\u3001\u8f7b\u85d0\uff0c\u4efb\u4f55\u4eba\u90fd\u5f88\u96be\u627f\u53d7\u3002 \u3000\u3000\u4eae\u8d77\u4e86\u706f\u706b\u4e4b\u540e\uff0c\u6842\u82b1\u5c31\u6765\u518d\u770b\u8fc7\u6c5f\u67ab\u4e00\u773c\uff0c\u5979\u5fc3\u4e2d\u4e00\u76f4\u5728\u4e3a\u6885\u82b1\u4e0d\u503c\uff0c\u600e\u4e48\u4f1a\u770b\u4e0a\u4e86\u8fd9\u4e00\u4e2a\u6587\u5f31\uff0c\u754f\u602f\u7684\u7537\u4eba\u3002 \u3000\u3000\u6842\u82b1\u7684\u4e24\u9053\u76ee\u5149\u4e00\u76f4\u770b\u7740\u5385\u95e8\uff0c\u5979\u5e0c\u671b\u6885\u82b1\u65e9\u4e9b\u6765\uff0c\u4e5f\u597d\u4ea4\u5dee\u4e86\u4e8b\u3002 \u3000\u3000\u4f46\u83ca\u82b1\u5374\u53d1\u89c9\u4e86\u6c5f\u67ab\u7684\u53ef\u7231\u3002 \u3000\u3000\u539f\u6765\uff0c\u6c5f\u67ab\u5fc3\u6709\u6240\u601d\uff0c\u62c5\u5fc3\u4eca\u591c\u5c31\u4f1a\u6709\u4e00\u573a\u640f\u6740\uff0c\u5fc3\u4e2d\u4e00\u76f4\u5728\u76d8\u7b97\uff0c\u4e00\u65e6\u51b2\u7a81\u5c55\u5f00\uff0c\u5982\u4f55\u8981\u4fdd\u62a4\u4f4f\u4e03\u5b9d\u548c\u5c1a\u7b49\u4eba\u7684\u5b89\u5168\uff1f \u3000\u3000\u53c8\u5982\u4f55\u624d\u80fd\u63a2\u542c\u51fa\u4ed6\u4eec\u884c\u52a8\u7684\u6d88\u606f\uff0c\u65e0\u5fc3\u518d\u63a9\u9970\u81ea\u5df1\uff0c\u987f\u7136\u795e\u5149\u5145\u83b9\uff0c\u6563\u53d1\u51fa\u8bf1\u4eba\u7684\u9b45\u529b\u3002 \u3000\u3000\u53ef\u60dc\uff0c\u6842\u82b1\u672a\u518d\u770b\u4ed6\u4e00\u773c\uff0c\u4f46\u83ca\u82b1\u5374\u5728\u7ec6\u7ec6\u54c1\u8bc4\uff0c\u770b\u7684\u75f4\u4e86\u2026\u2026 \u3000\u3000\u6885\u82b1\u59d1\u5a18\u6765\u52bf\u5982\u7bad\uff0c\u51e0\u4e4e\u662f\u98de\u8dc3\u800c\u5165\u3002 \u3000\u3000\u5979\u4e00\u5411\u4e3e\u6b62\u4f18\u96c5\uff0c\u770b\u4e0a\u53bb\uff0c\u662f\u90a3\u79cd\u6e29\u67d4\u3001\u70df\u9759\u7684\u7684\u59d1\u5a18\uff0c\u8fd9\u79cd\u7834\u7a7a\u800c\u6765\u7684\u67b6\u52bf\uff0c\u7acb\u523b\u5c31\u7ed9\u4eba\u4e00\u79cd\u5341\u4e07\u706b\u6025\u7684\u611f\u89c9\u3002 \u3000\u3000\u6842\u82b1\u5fc3\u4e2d\u6697\u6697\u51b7\u7b11\uff0c\u5fd6\u9053\uff1a\u201c\u8fd9\u79cd\u7537\u4eba\uff0c\u957f\u5b89\u8857\u5934\u4e0a\u5230\u5904\u90fd\u662f\uff0c\u8fd8\u6015\u6211\u4eec\u5403\u4e86\u4ed6\u4e0d\u6210\uff0c\u770b\u4f60\u90a3\u4ed8\u7334\u6025\u7684\u6837\u5b50\uff0c\u6885\u82b1\u5440\uff01\u6885\u82b1\uff0c\u4f60\u6837\u6837\u5f3a\u8fc7\u6211\u4eec\uff0c\u8fd9\u4e00\u6b21\u662f\u9634\u6c9f\u91cc\u7ffb\u4e86\u8239\uff0c\u2026\u2026\u201d\u53e3\u4e2d\u5374\u7b11\u9053\uff1a\u201c\u6885\u82b1\u59d0\u59d0\uff0c\u6211\u4eec\u53ef\u662f\u7167\u7740\u4f60\u5429\u5490\u529e\u4e8b\uff0c\u5168\u5fc3\u5168\u610f\u5730\u63a5\u5f85\u4ed6\u3002\u201d \u3000\u3000\u201c\u8c22\u5566\uff01\u201d\u6885\u82b1\u795e\u8272\u4e25\u8083\u770b\u770b\u83ca\u82b1\uff0c\u6842\u82b1\u4e00\u773c\uff0c\u9053\uff1a\u201c\u6709\u6025\u4e8b\uff0c\u5b50\u65f6\u884c\u52a8\uff0c\u53bb\u628a\u8377\u82b1\u3001\u83f1\u82b1\u627e\u6765\u3002\u201d \u3000\u3000\u679c\u7136\u662f\u9886\u8896\u4eba\u7269\uff0c\u6c5f\u67ab\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u5927\u6982\u5185\u5bab\u4e00\u7cfb\u6d3e\u4f4f\u5728\u957f\u5b89\u7684\u9996\u8111\u4eba\u7269\uff0c\u5c31\u662f\u5979\u4e86\u3002\u201d \u3000\u3000\u5a07\u67d4\u7684\u6885\u82b1\u59d1\u5a18\uff0c\u4e00\u4e0b\u5b50\u628a\u8138\u62c9\u4e86\u4e0b\u6765\uff0c\u7adf\u7136\u6709\u4e00\u79cd\u4ee4\u4eba\u754f\u60e7\u7684\u51b7\u51db\u3002\u83ca\u82b1\u3001\u6842\u82b1\u7adf\u7136\u4e00\u9f50\u7ad9\u4e86\u8d77\u6765\u3002 \u3000\u3000\u201c\u53bb\u554a\uff01\u628a\u83f1\u82b1\u3001\u8377\u82b1\u627e\u6765\u3002\u201d\u6885\u82b1\u4f20\u51fa\u4e86\u4ee4\u6e1d\u3002 \u3000\u3000\u201c\u6211\u53bb\uff01\u201d \u3000\u3000\u6842\u82b1\u4e00\u95ea\u8eab\uff0c\u51fa\u4e86\u5385\u95e8\u3002 \u3000\u3000\u5728\u5982\u6b64\u7d27\u5f20\u7684\u6c14\u6c1b\u4e2d\uff0c\u6885\u82b1\u4ecd\u7136\u65e0\u6cd5\u5fd8\u60c5\u6c5f\u67ab\uff0c\u4f38\u51fa\u7ea4\u7ea4\u7389\u624b\uff0c\u62c9\u4f4f\u6c5f\u67ab\uff0c\u6e29\u67d4\u5730\u8bf4\u9053\uff1a\u201c\u5bf9\u4e0d\u4f4f\u5566\uff01\u4eca\u591c\u5f88\u5fd9\uff0c\u53ea\u6015\u6ca1\u6cd5\u5b50\u62db\u547c\u4f60\u4e86\uff01\u201d \u3000\u3000\u6c5f\u67ab\u5524\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\u554a\uff1f\u201d \u3000\u3000\u201c\u548c\u5916\u4eba\u65e0\u5173\u7684\u4e8b\uff01\u2026\u2026\u201d\u6885\u82b1\u9053\uff1a\u201c\u7a81\u7136\u53d1\u751f\u7684\uff0c\u6211\u4e5f\u662f\u521a\u521a\u63a5\u5230\u901a\u77e5\u3002\u201d \u3000\u3000\u6885\u82b1\u6ca1\u6709\u518d\u8bf4\u4e0b\u53bb\uff0c\u6c5f\u67ab\u81ea\u7136\u662f\u4e0d\u4fbf\u8ffd\u95ee\u3002 \u3000\u3000\u201c\u8981\u628a\u7530\u516c\u5b50\u5b89\u6392\u4e00\u4e0b\u624d\u884c\u2026\u2026\u201d\u83ca\u82b1\u5f00\u4e86\u53e3\uff0c\u4e5f\u4e0d\u77e5\u662f\u8ba8\u597d\u6885\u82b1\uff0c\u8fd8\u662f\u771f\u7684\u5173\u5fc3\u7530\u516c\u5b50\uff0c\u58f0\u97f3\u6e29\u67d4\uff0c\u65e0\u9650\u5173\u6000\u5730\u8bf4\uff0c\u201c\u4e00\u65e6\u5200\u5251\u51fa\u9798\uff0c\u73a9\u4e0a\u4e86\u547d\uff0c\u53ea\u6015\u5f88\u96be\u6709\u65f6\u95f4\u6765\u7167\u987e\u4ed6\u4e86\uff01\u201d \u3000\u3000\u201c\u8bf4\u7684\u662f\u554a\uff01\u2026\u2026\u201d\u6885\u82b1\u9053\uff1a\u201c\u4eca\u591c\u7684\u884c\u52a8\uff0c\u6050\u6015\u6ce2\u53ca\u5168\u573a\uff0c\u90a3\u91cc\u662f\u5b89\u5168\u7684\u5730\u65b9\uff0c\u5f88\u96be\u5212\u5b9a\uff0c\u6d1b\u9633\u697c\u90a3\u9762\u4f1a\u6709\u4e9b\u4ec0\u4e48\u53cd\u5e94\uff0c\u5b8c\u5168\u65e0\u6cd5\u9884\u6599\uff0c\u6211\u2026\u2026\u201d \u3000\u3000\u201c\u8fd9\u6837\u5427\uff01\u6211\u8fd8\u662f\u56de\u5230\u5ba2\u6808\u4e2d\u53bb\u2026\u2026\u201d\u6c5f\u67ab\u671b\u7740\u6885\u82b1\uff0c\u53cc\u76ee\u4e2d\u6ee1\u662f\u60c5\u610f\u7684\u63a5\u9053\uff1a\u201c\u660e\u5929\uff0c\u6211\u518d\u6765\u770b\u4f60\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u7528\u6765\u4e86\u2026\u2026\u201d\u6885\u82b1\u8bf4\uff0c\u201c\u5982\u679c\u662f\u6211\u8fd8\u6d3b\u7740\uff0c\u660e\u5929\u521d\u66f4\u65f6\u5206\uff0c\u6211\u4f1a\u53bb\u89c1\u4f60\u4e00\u9762\uff0c\u5509\uff01 \u3000\u3000\u4e8c\u66f4\u4e4b\u540e\uff0c\u8fd8\u4e0d\u89c1\u6211\uff0c\u4f60\u5c31\u4e0d\u7528\u518d\u7559\u5728\u957f\u5b89\u4e86\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\uff0c\u6211\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u88c5\u4f5c\u51fa\u65e0\u9650\u7684\u4f9d\u604b\uff0c\u53cc\u7709\u6101\u9501\u7684\u75db\u82e6\u795e\u60c5\u5012\u4e5f\u5165\u6728\u4e09\u5206\u3002 \u3000\u3000\u201c\u4e0d\u7528\u4e3a\u6211\u96be\u8fc7\u2026\u2026\u201d \u3000\u3000\u6885\u82b1\u4e0d\u518d\u907f\u5fcc\u83ca\u82b1\uff0c\u504e\u5165\u4e86\u6c5f\u67ab\u7684\u6000\u4e2d\u3002 \u3000\u3000\u6c5f\u67ab\u611f\u89c9\u90a3\u4f9d\u504e\u5165\u6000\u7684\u529b\u91cf\u5f88\u5927\uff0c\u6068\u4e0d\u5f97\u628a\u6574\u4e2a\u7684\u8eab\u4f53\uff0c\u90fd\u6df1\u5165\u4e86\u6c5f\u67ab\u7684\u8eab\u4f53\u91cc\u2026\u2026\u3002 \u3000\u3000\u4e00\u5411\u73a9\u7537\u4eba\u4e8e\u638c\u80a1\u4e4b\u4e0a\u7684\u6885\u82b1\u59d1\u5a18\uff0c\u4e00\u65e6\u52a8\u4e86\u771f\u60c5\uff0c\u7adf\u7136\u662f\u5982\u6b64\u7684\u6df1\u5207\uff0c\u5982\u6b64\u7684\u65e0\u6cd5\u81ea\u62e8\u3002 \u3000\u3000\u6885\u82b1\u7d27\u504e\u7740\u6c5f\u67ab\uff0c\u884c\u51fa\u4e86\u83ca\u82b1\u5385\uff0c\u884c\u5165\u4e86\u9634\u6697\u7684\u82b1\u6811\u4e1b\u4e2d\u3002 \u3000\u3000\u201c\u6211\u53bb\u627e\u8fc7\u4f60\u5f88\u591a\u6b21\uff0c\u2026\u2026\u201d\u6885\u82b1\u8bf4\uff0c\u201c\u53ef\u662f\uff0c\u4f60\u90fd\u4e0d\u5728\u65c5\u9986\u4e2d\u3002\u201d \u3000\u3000\u201c\u6765\u4e86\u4e00\u4e2a\u957f\u8f88\uff0c\u903c\u7740\u6211\u56de\u676d\u5dde\u53bb\u2026\u2026\u201d\u6c5f\u67ab\u8bf4\uff0c\u201c\u6211\u4eec\u8c08\u4e86\u5f88\u4e45\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u4e0d\u662f\u602a\u4f60\u2026\u2026\u201d\u6885\u82b1\u8bf4\uff0c\u201c\u53ea\u662f\u60f3\u5ff5\u4f60\u2026\u2026\u201d \u3000\u3000\u4f46\u95fb\u8863\u8882\u98d8\u98ce\u4e4b\u58f0\uff0c\u6842\u82b1\u5e26\u7740\u8377\u82b1\u3001\u83f1\u82b1\uff0c\u98de\u8dc3\u5165\u83ca\u82b1\u5385\u4e2d\u3002 \u3000\u3000\u201c\u5feb\u4e9b\u4eb2\u4eb2\u6211\uff0c\u79bb\u5f00\u8fd9\u91cc\u2026\u2026\u201d\u6885\u82b1\u8bf4\uff0c\u201c\u8bb0\u7740\uff0c\u660e\u665a\u4e0a\u521d\u66f4\u65f6\u5206\u5728\u5ba2\u820d\u4e2d\u7b49\u6211\uff01\u201d \u3000\u3000\u6c5f\u67ab\u672c\u60f3\u518d\u5957\u51fa\u4e00\u4e9b\u5185\u60c5\uff0c\u4f46\u5df2\u88ab\u6885\u82b1\u7684\u5c0f\u5de7\u6a31\u5507\u5835\u4e0a\u4e86\u5634\u5df4\u3002 \u3000\u3000\u63a8\u5f00\u6c5f\u67ab\uff0c\u6885\u82b1\u957f\u957f\u5598\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5feb\u4e9b\u8d70\u5427\uff01\u201d \u3000\u3000\u53cd\u8eab\u4e00\u8dc3\uff0c\u4eba\u5df2\u98de\u5165\u83ca\u82b1\u5385\u4e2d\u3002 \u3000\u3000\u6c5f\u67ab\u4e3e\u6b65\u884c\u53bb\uff0c\u8fd9\u91cc\u4ed6\u8def\u5f84\u5f88\u719f\uff0c\u7ed5\u8fc7\u4e24\u9053\u6811\u5899\uff0c\u884c\u5165\u4e86\u5c0f\u5f84\u4e0a\u3002 \u3000\u3000\u4f46\u89c1\u706f\u5149\u95ea\u52a8\uff0c\u8033\u9645\u95f4\u54cd\u8d77\u4e86\u9001\u5ba2\u4e4b\u58f0\uff0c\u60f3\u662f\u5404\u65b9\u90fd\u5df2\u5949\u5230\u4e86\u4ee4\u8c15\uff0c\u5c3d\u5feb\u628a\u5ba2\u4eba\u529d\u8d70\uff0c\u56e0\u4e3a\uff0c\u8fd9\u65f6\u95f4\uff0c\u6b63\u662f\u9152\u5ba2\u4e91\u96c6\uff0c\u534e\u706f\u9ad8\u7167\u7684\u8425\u4e1a\u9ad8\u5cf0\u3002 \u3000\u3000\u4e00\u4e2a\u9ad8\u5927\u9ed1\u8863\u4eba\uff0c\u7a81\u7136\u7531\u4e00\u675f\u82b1\u6811\u540e\uff0c\u884c\u4e86\u51fa\u6765\uff0c\u8fce\u4e0a\u6c5f\u67ab\uff0c\u8bf4\u9053\uff1a\u201c\u5feb\u4e9b\u56de\u5367\u5ba4\u53bb\uff0c\u9093\u98de\u6b63\u53ec\u96c6\u7d27\u6025\u4f1a\u8bae\uff0c\u9152\u9986\u6765\u7684\u51e0\u684c\u5ba2\u4eba\uff0c\u770b\u4e0a\u53bb\u5341\u5206\u53ef\u7591\uff01\u96e8\u6b32\u6765\u98ce\u6ee1\u6402\uff0c\u770b\u6765\uff0c\u8fd9\u6d1b\u9633\u5c45\u4eca\u665a\u4e0a\uff0c\u8981\u53d1\u751f\u5c45\u53d8\uff0c\u90ed\u5929\u540c\u7edd\u5bf9\u5e94\u4ed8\u4e0d\u4e86\uff0c\u5feb\u4e9b\u53bb\u6062\u590d\u97e9\u9738\u8eab\u4efd\uff0c\u5e94\u4ed8\u5927\u53d8\u2026\u2026\u201d \u3000\u3000\u662f\u4e03\u5b9d\u548c\u5c1a\u7684\u58f0\u97f3\uff0c\u8fd9\u4f4d\u7a7a\u95e8\u5947\u4eba\uff0c\u679c\u7136\u662f\u773c\u89c2\u56db\u9762\uff0c\u8033\u542c\u516b\u65b9\uff0c\u6d1b\u9633\u5c45\u7684\u4efb\u4f55\u53d8\u5316\uff0c\u90fd\u9003\u4e0d\u8fc7\u4ed6\u7684\u773c\u775b\u3002 \u3000\u3000\u6c5f\u67ab\u8d76\u56de\u5367\u5ba4\uff0c\u90ed\u5929\u540c\u51c6\u5907\u51fa\u95e8\uff0c\u4e00\u89c1\u6c5f\u67ab\uff0c\u6025\u6025\u8bf4\u9053\uff1a\u201c\u603b\u7ba1\u5df2\u6d3e\u4eba\u50ac\u4e86\u4e24\u6b21\uff0c\u5927\u5e08\u7236\u4e00\u76f4\u793a\u610f\u6211\u62d6\u5ef6\u65f6\u95f4\uff0c\u73b0\u5728\u5df2\u7ecf\u62d6\u4e0d\u4e0b\u53bb\u4e86\uff0c\u5e78\u597d\u4f60\u53ca\u65f6\u56de\u6765\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\uff0c\u4f60\u6062\u590d\u672c\u6765\u9762\u76ee\uff0c\u6362\u4e0a\u4e86\u591c\u884c\u88c5\u675f\u2026\u2026\u201d\u6c5f\u67ab\u8bf4\uff0c\u201c\u4f46\u4e0d\u8981\u4e71\u8dd1\uff0c\u7559\u5728\u8fd9\u91cc\uff0c\u8981\u4f60\u51fa\u52a8\u7684\u65f6\u5019\uff0c\u6211\u4f1a\u6765\u901a\u77e5\u4f60\u3002\u201d \u3000\u3000\u53e3\u4e2d\u5429\u5490\u90ed\u5929\u540c\uff0c\u4eba\u4e5f\u5f00\u59cb\u8981\u6362\u8863\u670d\uff0c\u6234\u4e0a\u4eba\u76ae\u9762\u5177\uff0c\u6325\u624b\u7184\u53bb\u5ba4\u4e2d\u706f\u706b\u3002 \u3000\u3000\u6c5f\u67ab\u5e26\u4e0a\u4e86\u623f\u95e8\uff0c\u76f4\u5954\u9093\u98de\u7684\u4f4f\u5904\uff0c\u7acb\u65f6\u7531\u6697\u5f71\u4e2d\u95ea\u51fa\u4e24\u4e2a\u9ed1\u8863\u4eba\u62e6\u4f4f\u4e86\u53bb\u8def\uff0c\u4f46\u770b\u6e05\u4e86\u6765\u4eba\u4e4b\u540e\uff0c\u7acb\u65f6\u95ea\u8ba9\u5230\u4e00\u4fa7\uff0c\u9053\uff1a\u201c\u603b\u7ba1\u5df2\u7ecf\u5019\u9a7e\u591a\u65f6\u4e86\u3002\u201d \u3000\u3000\u6c5f\u67ab\u4e3e\u6b65\u524d\u884c\uff0c\u5fc3\u4e2d\u5374\u6697\u6697\u5fd6\u9053\uff1a\u201c\u8fd9\u6d1b\u9633\u5c45\u79cd\u6ee1\u4e86\u5404\u8272\u82b1\u6811\uff0c\u767d\u95f4\u6781\u4e3a\u60a6\u76ee\uff0c\u4f46\u5728\u591c\u5e55\u4e4b\u4e0b\uff0c\u5927\u5bb6\u90fd\u7a7f\u7740\u9ed1\u8272\u7684\u519c\u670d\uff0c\u6df7\u6218\u8d77\u6765\uff0c\u5b9e\u662f\u96be\u5206\u654c\u6211\u2026\u2026\u3002\u201d \u3000\u3000\u5fd6\u601d\u4e4b\u95f4\uff0c\u4eba\u5df2\u8e0f\u5165\u4e86\u5ba2\u5385\u3002 \u3000\u3000\u5ba4\u4e2d\u70b9\u71c3\u7740\u4e24\u53ea\u7ea2\u70db\uff0c\u4f46\u53ef\u6e05\u695a\u7684\u76f4\u5230\u5404\u4eba\u9762\u76ee\u3002 \u3000\u3000\u9093\u98de\u9ad8\u5c45\u9996\u4f4d\uff0c\u5de6\u9996\u4ee5\u4e03\u5b9d\u548c\u5c1a\u4e3a\u9996\uff0c\u5750\u7740\u5317\u5251\u80e1\u840d\u3001\u5357\u5200\u3001\u6bb5\u4e5d\u3001\u80e1\u5143\u4e09\u4eba\uff0c\u53f3\u9996\u6392\u5750\u7740\u56db\u4e2a\u7070\u8863\u4eba\uff0c\u7b2c\u4e00\u4e2a\u767d\u9aef\u5782\u80f8\uff0c\u8eab\u6750\u9ad8\u5927\uff0c\u7751\u7ea2\u5982\u706b\uff0c\u659c\u80cc\u4e86\u4e00\u53ea\u9ec4\u5e03\u888b\u5b50\uff0c\u4e5f\u4e0d\u77e5\u85cf\u7684\u4ec0\u4e48\u5175\u5203\u3002 \u3000\u3000\u4f9d\u5e8f\u800c\u5750\u7684\u4e09\u4e2a\u7070\u8863\u4eba\uff0c\u5e74\u7eaa\u4e5f\u90fd\u5728\u4e94\u5341\u4e0a\u4e0b\uff0c\u8eab\u4e0a\u4f69\u5e26\u4e86\u4e0d\u540c\u7684\u5175\u5203\u3002 \u3000\u3000\u6c5f\u67ab\u4e0d\u8ba4\u8bc6\u56db\u4eba\uff0c\u4f46\u63a8\u60f3\u56db\u4eba\u5fc5\u662f\u5929\u9a6c\u603b\u5802\u6d3e\u6765\u7684\u4eba\u3002 \u3000\u3000\u770b\u8d77\u6765\uff0c\u5929\u9a6c\u603b\u5802\u7684\u8033\u76ee\u4e5f\u662f\u5341\u5206\u7075\u654f\uff0c\u5185\u5bab\u4e00\u7cfb\u4e2d\u7684\u884c\u52a8\uff0c\u5e76\u672a\u9003\u8fc7\u5929\u9a6c\u603b\u5802\u7684\u76d1\u89c6\u3002 \u3000\u3000\u9093\u98de\u795e\u60c5\u51b7\u8083\uff0c\u4f46\u5bf9\u6c5f\u67ab\u4ecd\u4fdd\u6301\u4e86\u76f8\u5f53\u7684\u5ba2\u6c14\uff0c\u62cd\u62cd\u8eab\u4fa7\u7684\u5750\u4f4d\uff0c\u9053\uff1a\u201c\u5750\u5427\uff01\u526f\u603b\u7ba1\uff0c\u5927\u5bb6\u90fd\u5728\u7b49\u4f60\u3002\u201d \u3000\u3000\u201c\u5c5e\u4e0b\u53d1\u89c9\u4e86\u4e00\u4e9b\u53ef\u7591\u7684\u53d8\u5316\u2026\u2026\u201d\u6c5f\u67ab\u8bf4\uff0c\u201c\u5fcd\u4e0d\u4f4f\u53bb\u8ffd\u67e5\u4e00\u4e0b\uff0c\u6240\u4ee5\uff0c\u665a\u6765\u4e86\u4e00\u6b65\uff0c\u8bf7\u603b\u7ba1\u9976\u6055\u3002\u201d \u3000\u3000\u628a\u62f3\u4e00\u4e2a\u957f\u63d6\uff0c\u8868\u73b0\u7684\u606d\u8c28\uff0c\u628a\u9093\u98de\u5fc3\u4e2d\u79ef\u5b58\u7684\u4e00\u70b9\u6012\u706b\uff0c\u4e5f\u5b8c\u5168\u7ed9\u538b\u7184\u4e0b\u53bb\u3002 \u3000\u3000\u7559\u5fc3\u89c2\u5bdf\u7684\u4e03\u5b9d\u548c\u5c1a\uff0c\u77a7\u7684\u5fc3\u4e2d\u76f4\u4e50\uff0c\u6697\u9053\uff1a\u201c\u8fd9\u624d\u662f\u80fd\u5c48\u80fd\u4f38\u7684\u5927\u4e08\u592b\u554a\uff01\u4e71\u4e16\u82f1\u96c4\uff0c\u98ce\u5c18\u5947\u4eba\uff0c\u4ee5\u6bd2\u6291\u6bd2\u7684\u9ad8\u624b\u2026\u2026\u201d \u3000\u3000\u201c\u8fd9\u5c31\u96be\u602a\u4e86\uff0c\u201d\u9093\u98de\u9053\uff1a\u201c\u526f\u603b\u7ba1\u53d1\u73b0\u4e86\u4ec0\u4e48\u6837\u7684\u53ef\u7591\u53d8\u5316\uff1f\u201d \u3000\u3000\u201c\u6211\u53d1\u73b0\u4e86\u4e00\u4e9b\u4e0d\u5c5e\u6d1b\u9633\u5c45\u7684\u4eba\uff0c\u5374\u7a7f\u7740\u7c7b\u4f3c\u4f8d\u5e94\u7684\u8863\u670d\uff0c\u51fa\u73b0\u5728\u6d1b\u9633\u5c45\u4e2d\u2026\u2026\u201d\u6c5f\u67ab\u8bf4\uff0c\u201c\u4ed6\u4eec\u7684\u5e74\u7eaa\u5f88\u8f7b\uff0c\u800c\u4e14\u6bcf\u4e2a\u4eba\u90fd\u4f3c\u5177\u6709\u4e86\u76f8\u5f53\u7684\u6b66\u529f\u2026\u2026\u201d \u3000\u3000\u201c\u4e00\u5171\u6709\u591a\u5c11\u4eba\uff1f\u201d\u9093\u98de\u8bf4\uff0c\u201c\u4ed6\u4eec\u90fd\u51fa\u73b0\u5728\u4ec0\u4e48\u5730\u65b9\uff1f\u201d \u3000\u3000\u201c\u591a\u5c11\u4eba\uff0c\u5c5e\u4e0b\u672a\u67e5\u6e05\u695a\uff0c\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u51fa\u73b0\u7684\u5730\u65b9\u5728\u6885\u82b1\u3001\u83ca\u82b1\u4e24\u5385\u4e4b\u95f4\uff0c\u540c\u65f6\uff0c\u5c5e\u4e0b\u4e5f\u53d1\u73b0\u4e86\u8377\u82b1\u3001\u83f1\u82b1\u3001\u83ca\u82b1\u3001\u6842\u82b1\u3001\u6885\u82b1\u4e94\u4e2a\u82b1\u5a62\uff0c\u4f1a\u805a\u5728\u83ca\u82b1\u5385\u4e2d\uff0c\u4f3c\u662f\u5728\u5546\u91cf\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u55ef\uff01\u2026\u2026\u201d\u9093\u98de\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u8fd9\u5927\u6982\u4e0d\u4f1a\u9519\u4e86\u2026\u2026\u201d\u76ee\u5149\u8f6c\u5230\u90a3\u767d\u9aef\u8001\u4eba\u7684\u8eab\u4e0a\uff0c\u63a5\u9053\uff1a\u201c\u770b\u6765\uff0c\u8fd9\u91cc\u7684\u9996\u8111\u4eba\u7269\uff0c\u5927\u6982\u662f\u6885\u82b1\u59d1\u5a18\u4e86\uff0c\u4e01\u8001\u5f97\u5230\u7684\u6d88\u606f\u662f\u2026\u2026\u201d \u3000\u3000\u201c\u8001\u673d\u5df2\u4e00\u4f59\u5e74\u672a\u8e0f\u5165\u6c5f\u6e56\u4e86\uff0c\u5bf9\u90a3\u4e9b\u540e\u751f\u665a\u8fdb\u7684\u4eba\u4e86\u89e3\u4e0d\u591a\uff0c\u201d\u4e01\u8001\u4e00\u634b\u957f\u9aef\uff0c\u63a5\u9053\uff1a\u201c\u5802\u4e3b\u547d\u8001\u673d\u7387\u9886\u4e09\u4f4d\u62a4\u6cd5\u8d76\u6765\u6d1b\u9633\u5c45\uff0c\u4e3a\u9093\u603b\u7ba1\u52a9\u62f3\uff0c\u8001\u673d\u4e00\u5207\u552f\u9093\u603b\u7ba1\u4e4b\u547d\u662f\u4ece\uff0c\u81f3\u4e8e\u654c\u4eba\u662f\u7537\u662f\u5973\uff1f\u4f55\u7b49\u8eab\u4efd\uff0c\u8001\u673d\u4e0d\u613f\u77e5\u9053\uff0c\u4e5f\u4e0d\u60f3\u591a\u95ee\uff01\u201d \u3000\u3000\u8fd9\u662f\u4e2a\u8f6f\u9489\u5b50\uff0c\u78b0\u5f97\u9093\u98de\u6123\u4f4f\u4e86\uff0c\u7adf\u4e0d\u77e5\u5982\u4f55\u63a5\u8a00\u3002 \u3000\u3000\u9707\u4e8e\u4e01\u8001\u6614\u5e74\u7684\u5a01\u540d\uff0c\u9093\u98de\u4e0d\u6562\u51fa\u8a00\u9876\u649e\uff0c\u4e5f\u4e0d\u6562\u56de\u7edd\u4e01\u8001\u4eba\u7684\u63f4\u52a9\uff0c\u66f4\u4e0d\u4fbf\u592a\u8fc7\u793a\u5f31\uff0c\u5f53\u771f\u662f\u8f7b\u4e0d\u5f97\uff0c\u91cd\u4e0d\u5f97\uff0c\u4e0d\u77e5\u5982\u4f55\u624d\u80fd\u5e94\u4ed8\u7684\u6070\u5230\u597d\u5904\u3002 \u3000\u3000\u5e78\u597d\u6c5f\u67ab\u5f00\u4e86\u53e3\uff0c\u7b11\u4e00\u7b11\uff0c\u8d77\u8eab\u8bf4\u9053\uff1a\u201c\u4e01\u8001\u8a00\u91cd\u4e86\uff0c\u4ee5\u4e01\u8001\u7684\u8d6b\u8d6b\u5a01\u540d\uff0c\u672c\u5e94\u8be5\u4e3b\u6301\u5927\u5c40\uff0c\u8c03\u5175\u3001\u9063\u5c06\uff0c\u5e94\u4ed8\u53d8\u5316\uff0c\u4f46\u4e01\u8001\u662f\u6d1b\u9633\u5c45\u4e2d\u7684\u8d35\u5bbe\uff0c\u5b9e\u5728\u4e0d\u6562\u6709\u52b3\uff0c\u97e9\u67d0\u4eba\u4ee3\u603b\u7ba1\u5148\u5411\u4e01\u8001\u81f4\u8c22\u2026\u2026\u201d\u606d\u606d\u656c\u656c\uff0c\u5bf9\u4e01\u8001\u4eba\u62b1\u62f3\u4e00\u793c\u3002 \u3000\u3000\u4e01\u8001\u4eba\u4e00\u6325\u624b\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u4e0d\u7528\u5ba2\u6c14\u3002\u201d \u3000\u3000\u6c5f\u67ab\u5fae\u5fae\u4e00\u7b11\uff0c\u5bf9\u8001\u4eba\u7684\u8f7b\u85d0\u51b7\u6f20\uff0c\u5b8c\u5168\u4e0d\u653e\u5fc3\u4e0a\uff0c\u8f6c\u5bf9\u4e09\u4e2a\u7070\u8863\u4eba\uff0c\u62b1\u62f3\u7b11\u9053\uff1a\u201c\u6d1b\u9633\u5c45\u526f\u603b\u7ba1\u97e9\u9738\uff0c\u89c1\u8fc7\u4e09\u4f4d\u62a4\u6cd5\u3002\u201d \u3000\u3000\u4e09\u4e2a\u7070\u8863\u4eba\u76f8\u4e92\u671b\u4e86\u4e00\u773c\uff0c\u5927\u6982\u662f\u88ab\u97e9\u9738\u8c26\u606d\u7684\u6001\u5ea6\u611f\u52a8\uff0c\u5fae\u5fae\u6b20\u8eab\uff0c\u7b97\u662f\u8fd8\u4e86\u4e00\u793c\uff0c\u9f50\u58f0\u8bf4\u9053\uff1a\u201c\u526f\u603b\u7ba1\u8bf7\u5750\u4e0b\u8bf4\u8bdd\u3002\u201d \u3000\u3000\u53e3\u6c14\u867d\u7136\u4e0d\u5c0f\uff0c\u4f46\u7528\u8bcd\u8fd8\u7b97\u5ba2\u6c14\u3002 \u3000\u3000\u80e1\u840d\u3001\u6bb5\u4e5d\u3001\u80e1\u5143\uff0c\u516d\u53ea\u773c\u775b\u77aa\u7740\u6c5f\u67ab\uff0c\u5fc3\u4e2d\u5374\u6697\u6697\u60f3\u9053\uff1a\u201c\u8fd9\u4e2a\u4eba\u7684\u5904\u4e8b\u65b9\u6cd5\uff0c\u5df2\u5b8c\u5168\u8131\u79bb\u4e86\u6c5f\u6e56\u4eba\u7684\u6c14\u8d28\uff0c\u4e0d\u4e89\u540d\uff0c\u4e0d\u4e89\u6c14\uff0c\u80fd\u5fcd\u4eba\u6240\u4e0d\u80fd\u5fcd\uff0c\u5df2\u8fbe\u5230\u884c\u7565\u4ee3\u8c0b\u7684\u4e0a\u4e58\u5883\u754c\u4e86\u3002\u201d \u3000\u3000\u4f46\u95fb\u6c5f\u67ab\u7b11\u9053\uff1a\u201c\u591a\u8c22\u4e09\u4f4d\u62a4\u6cd5\uff0c\u4e5f\u611f\u6fc0\u603b\u5802\u5bf9\u6d1b\u9633\u5c45\u7684\u5173\u5fc3\uff0c\u773c\u4e0b\u7684\u60c5\u5f62\u5341\u5206\u660e\u663e\uff0c\u662f\u6211\u4eec\u8fd9\u4e2a\u7ec4\u5408\u7684\u5185\u90e8\u4e4b\u4e89\uff0c\u53ea\u662f\u672c\u5802\u4e2d\u4eba\u5904\u843d\u4e0b\u98ce\uff0c\u4e00\u5207\u64cd\u4e4b\u5728\u4eba\uff0c\u53ea\u80fd\u6c34\u6765\u571f\u63a9\uff0c\u5175\u6765\u5c06\u6321\uff0c\u5e78\u5f97\u4e01\u8001\u548c\u4e09\u4f4d\u62a4\u6cd5\u5927\u9a7e\u4eb2\u4e34\uff0c\u4f7f\u6211\u4eec\u5b9e\u529b\u5927\u589e\uff0c\u81f3\u4e8e\u4eca\u591c\u4e2d\u7684\u60c5\u52bf\u53d8\u5316\uff0c\u6211\u4eec\u5904\u4e8e\u5b88\u52bf\uff0c\u53ea\u6709\u5750\u4ee5\u5f85\u53d8\uff0c\u5f85\u654c\u52bf\u4e34\u5934\uff0c\u518d\u7531\u4e01\u8001\u548c\u4e09\u4f4d\u62a4\u6cd5\u914c\u60c5\u88c1\u51b3\uff0c\u5982\u679c\u975e\u6253\u4e0d\u53ef\uff0c\u81ea\u7531\u6d1b\u9633\u5c45\u7684\u4eba\uff0c\u5148\u884c\u8fce\u6218\uff0c\u6211\u4eec\u652f\u6301\u4e0d\u4f4f\u65f6\uff0c\u518d\u7531\u4e01\u8001\u548c\u4e09\u4f4d\u62a4\u6cd5\u63f4\u624b\uff0c\u662f\u5426\u53ef\u884c\uff0c\u8fd8\u8bf7\u56db\u4f4d\u6307\u70b9\uff1f\u201d \u3000\u3000\u9093\u98de\u6ca1\u6709\u51fa\u8a00\u5e72\u9884\uff0c\u663e\u7136\u662f\u5bf9\u6c5f\u67ab\u7684\u5904\u7f6e\uff0c\u6df1\u8868\u8d5e\u540c\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u6c5f\u67ab\u5b8c\u5168\u4e0d\u8ba4\u8bc6\u8fd9\u56db\u4e2a\u4eba\uff0c\u4f46\u4ed6\u4f83\u4f83\u800c\u8c08\uff0c\u597d\u50cf\u65e9\u5df2\u95fb\u5f97\u56db\u4eba\u7684\u5927\u540d\uff0c\u8bed\u6c14\u4e2d\u500d\u6781\u63a8\u5d07\u3002 \u3000\u3000\u4e01\u8001\u4eba\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u8fd9\u6837\u5b89\u6392\u5f88\u597d\uff0c\u80fd\u591f\u4e0d\u4f24\u548c\u6c14\uff0c\u628a\u4e8b\u60c5\u6446\u5e73\u4e0b\u6765\uff0c\u624d\u662f\u4e0a\u4e0a\u4e4b\u7b56\u3002\u201d \u3000\u3000\u53e3\u6c14\u4e4b\u4e2d\uff0c\u7adf\u4f3c\u5bf9\u654c\u4eba\u6709\u6240\u504f\u4f46\u3002 \u3000\u3000\u6c5f\u67ab\u5fc3\u4e2d\u5fd6\u9053\uff1a\u201c\u4efb\u4f60\u597d\u4f3c\u9b3c\uff0c\u4e5f\u8981\u628a\u4f60\u62d6\u4e0b\u6c34\u3002\u201d\u4eba\u5374\u8f6c\u8eab\u5bf9\u7740\u9093\u98de\u9053\uff1a\u201c\u4e01\u8001\u5fb7\u9ad8\u671b\u91cd\uff0c\u7167\u7740\u4e01\u8001\u7684\u610f\u601d\u529e\u4e8b\uff0c\u7edd\u4e0d\u4f1a\u5403\u4e8f\uff0c\u603b\u7ba1\u662f\u5426\u540c\u610f\u5c5e\u4e0b\u7684\u610f\u89c1\uff1f\u201d \u3000\u3000\u201c\u5f88\u597d\uff0c\u5f88\u597d\u2026\u2026\u201d\u9093\u98de\u60f3\u4e0d\u51fa\u66f4\u597d\u7684\u529e\u6cd5\uff0c\u53c8\u89c1\u6c5f\u67ab\u9891\u9891\u4ee5\u76ee\u5149\u793a\u610f\uff0c\u53ea\u597d\u8bf4\u9053\uff1a \u3000\u3000\u201c\u591a\u81c2\u795e\u9b54\u4e01\u897f\u5c71\u8001\u524d\u8f88\uff0c\u4e09\u5341\u5e74\u524d\u5df2\u540d\u9707\u6c5f\u6e56\uff0c\u89c1\u591a\u8bc6\u5e7f\uff0c\u4eca\u65e5\u4e4b\u4e8b\uff0c\u81ea\u8be5\u4ee5\u4e01\u8001\u524d\u8f88\u7684\u9a6c\u9996\u662f\u77bb\u3002\u201d \u3000\u3000\u6c5f\u67ab\u5fc3\u4e2d\u5fd6\u9053\uff1a\u201c\u770b\u6765\uff0c\u9093\u98de\u662f\u6709\u610f\u5730\u70b9\u51fa\u4e01\u8001\u4eba\u7684\u540d\u53f7\u4e86\uff0c\u53ea\u53ef\u501f\uff0c\u4ed6\u672a\u628a\u53e6\u5916\u4e09\u4e2a\u4eba\u4e5f\u8bf4\u51fa\u2026\u2026\u201d \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u4fdd\u6301\u4e86\u76f8\u5f53\u7684\u5e73\u9759\uff0c\u4f46\u80e1\u840d\u3001\u6bb5\u4e5d\uff0c\u5374\u542c\u5f97\u5fc3\u5934\u9707\u52a8\u4e0d\u5df2\uff0c\u4e45\u95fb\u5927\u540d\uff0c\u672a\u66fe\u4e00\u6664\u7684\u8001\u9b54\u5934\uff0c\u7adf\u7136\u5728\u8fd9\u4e48\u4e00\u4e2a\u4ea6\u654c\u4ea6\u53cb\u7684\u5c40\u9762\u4e0b\u89c1\u9762\uff0c\u6b64\u4eba\u51f6\u540d\u4e45\u7740\uff0c\u5df2\u5728\u6c5f\u6e56\u4e0a\u6d88\u5931\u5341\u5e74\u4ee5\u4e0a\uff0c\u60f3\u4e0d\u5230\u7adf\u4e5f\u6295\u5165\u4e86\u8fd9\u4e2a\u795e\u79d8\u7684\u7ec4\u5408\u4e4b\u4e2d\u3002 \u3000\u3000\u4e01\u897f\u5c71\u5bf9\u9093\u98de\u8bdd\u4e2d\u6709\u8912\u6709\u8d2c\uff0c\u663e\u7136\u662f\u5927\u611f\u4e0d\u6ee1\uff0c\u51b7\u54fc\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5bbe\u4e0d\u538b\u4e3b\uff0c\u5982\u82e5\u9093\u603b\u7ba1\u4e0d\u4ee5\u8001\u673d\u4e4b\u8a00\u4e3a\u662f\uff0c\u8001\u673d\u7acb\u523b\u544a\u9000\u3002\u201d \u3000\u3000\u201c\u4e07\u4e07\u4e0d\u53ef\uff0c\u4e07\u4e07\u4e0d\u53ef\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u603b\u7ba1\u70b9\u51fa\u4e01\u8001\u7684\u540d\u53f7\uff0c\u4e01\u8001\u82f1\u540d\u76d6\u4e16\uff0c\u665a\u8fdb\u8fd9\u53a2\uff0c\u65e9\u5df2\u7ecf\u5927\u611f\u632f\u594b\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u597d\u4e00\u756a\u82b1\u8a00\u5de7\u8bed\u2026\u2026\u201d\u4e01\u897f\u5c71\u63a5\u9053\uff1a\u201c\u7eb5\u7136\u662f\u6ee1\u53e3\u8c0e\u8a00\uff0c\u4e5f\u542c\u5f97\u4eba\u5fc3\u5934\u8212\u4f46\uff0c\u8fc7\u4e86\u4eca\u591c\u8fd9\u573a\u98ce\u96e8\uff0c\u8001\u673d\u5012\u662f\u6307\u70b9\u4f60\u4e24\u624b\u529f\u592b\uff0c\u4ee5\u793a\u5956\u52b1\u3002\u201d \u3000\u3000\u201c\u591a\u8c22\u4e01\u8001\u7684\u7231\u62a4\uff0c\u4f46\u613f\u4eca\u591c\u5728\u4e01\u8001\u7684\u795e\u5a01\u7b3c\u7f69\u4e4b\u4e0b\uff0c\u80fd\u591f\u5e73\u5b89\u6e21\u8fc7\u3002\u201d\u6c5f\u67ab\u82e6\u7b11\u4e00\u4e0b\uff0c\u4f4f\u53e3\u4e0d\u8a00\u3002 \u3000\u3000\u201c\u4f60\u5012\u662f\u8bf4\u4e0b\u53bb\u554a\uff01\u2026\u2026\u201d\u4e01\u897f\u5c71\u9053\uff1a\u201c\u96be\u9053\u51e0\u5341\u4e2a\u665a\u751f\u540e\u8fdb\u7684\u5c0f\u4f19\u5b50\uff0c\u5a07\u6ef4\u6ef4\u7684\u5927\u59d1\u5a18\uff0c\u8fd8\u771f\u80fd\u79fb\u5c71\u5012\u6d77\uff0c\u95f9\u7ffb\u8fd9\u6d1b\u9633\u5c45\u4e0d\u6210\uff1f\u201d \u3000\u3000\u6562\u60c5\u4ed6\u4e0d\u662f\u504f\u4f46\u654c\u4eba\uff0c\u800c\u662f\u8f7b\u89c6\u654c\u624b\uff1f \u3000\u3000\u201c\u4e01\u8001\uff0c\u201d\u6c5f\u67ab\u795e\u60c5\u4e25\u8083\u5730\u8bf4\uff0c\u201c\u5802\u53e3\u4e0a\u4eba\u548c\u5185\u5bab\u4e00\u7cfb\u4e89\u9010\u6743\u52bf\uff0c\u5df2\u975e\u4e00\u65e5\uff0c\u54b1\u4eec\u5728\u5404\u5730\u90fd\u88ab\u8feb\u843d\u4e0b\u98ce\uff0c\u6d1b\u9633\u5c45\u65e5\u8fdb\u6597\u91d1\uff0c\u4e3a\u76ee\u4e0b\u5802\u53e3\u4e0a\u6700\u91cd\u8981\u7684\u636e\u70b9\uff0c\u5185\u5bab\u4e2d\u4eba\uff0c\u4e45\u5b58\u9738\u4e3b\u4e4b\u5fc3\uff0c\u4eca\u5bb5\u4e4b\u4e89\uff0c\u6050\u96be\u5584\u4e86\uff01\u201d \u3000\u3000\u201c\u4f60\u662f\u8bf4\uff0c\u4ed6\u4eec\u4f1a\u771f\u5200\u771f\u67aa\u786c\u5e72\u4e86\u2026\u2026\u201d\u4e01\u897f\u5c71\u8138\u8272\u6cdb\u8d77\u4e86\u6012\u610f\uff0c\u9053\uff1a\u201c\u8001\u592b\u65e2\u7136\u6765\u4e86\uff0c\u5c82\u5bb9\u4ed6\u4eec\u5f97\u901e\u3002\u201d \u3000\u3000\u6c5f\u67ab\u773c\u770b\u5df2\u6fc0\u8d77\u4e86\u4e01\u897f\u5c71\u7684\u706b\u6c14\uff0c\u5fc3\u77e5\u4e0d\u5b9c\u518d\u706b\u4e0a\u52a0\u6cb9\uff0c\u8fd9\u4e2a\u71c3\u7206\u706b\u836f\u7684\u4e8b\uff0c\u7559\u7ed9\u6885\u82b1\u4eec\u53bb\u70b9\u5427\uff01 \u3000\u3000\u7acb\u523b\uff0c\u6362\u4e0a\u4e00\u526f\u7b11\u8138\uff0c\u9053\uff1a\u201c\u8bf4\u5f97\u4e5f\u662f\uff0c\u4e5f\u8bb8\u4ed7\u51ed\u4e01\u8001\u548c\u4e09\u5927\u62a4\u6cd5\u7684\u5a01\u671b\uff0c\u771f\u80fd\u4f7f\u4ed6\u4eec\u77e5\u96be\u800c\u9000\u5462\uff1f\u201d \u3000\u3000\u4ed6\u6ca1\u6709\u5fd8\u8bb0\uff0c\u7ed9\u4e09\u5927\u62a4\u6cd5\uff0c\u4e5f\u6234\u4e0a\u4e00\u9876\u9ad8\u5e3d\u5b50\u3002 \u3000\u3000\u679c\u7136\uff0c\u4e09\u5f20\u51b7\u51b0\u51b0\u7684\u7751\u4e0a\uff0c\u90fd\u6cdb\u73b0\u51fa\u4e86\u7b11\u5bb9\u3002 \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u53ea\u770b\u5f97\u6697\u53eb\u8fc7\u763e\uff0c\u51e0\u4e4e\u5fcd\u4e0d\u4f4f\u9f13\u638c\u53eb\u597d\uff0c\u6c5f\u67ab\u8fd9\u5c0f\u5b50\u867d\u7136\u6ca1\u4ec0\u4e48\u6c5f\u6e56\u9605\u5386\uff0c\u4f46\u5374\u628a\u51c6\u4e86\u6c5f\u6e56\u4eba\u7269\u7684\u6027\u683c\uff0c\u4e00\u756a\u8bdd\u6311\u62e8\u8d77\u4ed6\u4eec\u7684\u597d\u80dc\u4e4b\u5fc3\uff0c\u79bb\u95f4\u65e2\u6210\uff0c\u5374\u53c8\u62bd\u51fa\u6d89\u8db3\u2026\u2026\u7b49\u7740\u4e00\u573a\u597d\u620f\u4e0a\u6f14\u3002 \u3000\u3000\u4e56\u4e56\uff0c\u8fd9\u5c0f\u5b50\u8eab\u8d1f\u7740\u9ad8\u6df1\u96be\u6d4b\u7684\u6b66\u529f\uff0c\u80f8\u85cf\u4e86\u5343\u822c\u5978\u8c0b\u72e1\u8ba1\uff0c\u4e00\u4e2a\u53ef\u5f53\u5f97\u5341\u4e07\u96c4\u5175\uff0c\u2026 \u3000\u3000\u662f\u4e2a\u6781\u53ef\u63e1\u7684\u5bf9\u624b\uff0c\u4f46\u613f\u4ed6\u4f1a\u662f\u4e2a\u53ef\u7231\u7684\u670b\u53cb\u2026\u2026\u3002 \u3000\u3000\u5927\u548c\u5c1a\u6b63\u60f3\u7684\u5165\u795e\uff0c\u4e00\u4e2a\u5168\u8eab\u9ed1\u8863\u7684\u5927\u6c49\u6025\u5954\u800c\u5165\uff0c\u8bf4\u9053\uff1a\u201c\u7980\u603b\u7ba1\uff0c\u6885\u82b1\u59d1\u5a18\u5e26\u7740\u6625\u3001\u590f\u3001\u79cb\u516b\u4f4d\u82b1\u5973\u548c\u4e00\u6279\u9ed1\u8863\u4eba\u5df2\u5230\u4e86\u5927\u95e8\u5916\u9762\u3002\u201d \u3000\u3000\u6c5f\u67ab\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u603b\u7ba1\uff0c\u522b\u5fd8\u4e86\u5411\u4e01\u8001\u8bf7\u793a\uff01\u201d \u3000\u3000\u9093\u98de\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4e01\u8001\uff0c\u4ed6\u4eec\u5df2\u627e\u4e0a\u4e86\u95e8\uff01\u8bf7\u4e01\u8001\u88c1\u593a\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u8ba9\u4ed6\u4eec\u8fdb\u6765\uff0c\u8001\u592b\u5012\u8981\u770b\u770b\u4ed6\u4eec\u662f\u4e0d\u662f\u90fd\u751f\u4e86\u4e09\u5934\u516d\u81c2\u3002\u201d\u4e01\u897f\u5c71\u4e0d\u518d\u81ea\u8c26\uff0c\u6446\u51fa\u4e00\u526f\u552f\u6211\u72ec\u5c0a\u7684\u67b6\u5f0f\uff0c\u4f46\u4e5f\u627f\u62c5\u4e86\u8fd9\u573a\u52ab\u6740\u7684\u8d23\u4efb\u3002 \u3000\u3000\u8001\u5978\u5de8\u6ed1\u7684\u4e01\u897f\u5c71\uff0c\u7ec8\u4e8e\u5165\u4e86\u5708\u5957\uff0c\u53ea\u4ed6\u4e00\u51fa\u9762\u548c\u6885\u82b1\u7b54\u4e0a\u4e86\u8bdd\uff0c\u5c31\u8981\u627f\u62c5\u80dc\u8d1f\u751f\u6740\u7684\u5927\u8d35\u91cd\u4efb\u3002 \u3000\u3000\u4f46\u9093\u98de\u5374\u6709\u7740\u5927\u6743\u65c1\u843d\u7684\u611f\u89c9\uff0c\u4e00\u6325\u624b\uff0c\u9053\uff1a\u201c\u53bb\uff0c\u53eb\u4ed6\u4eec\u8fdb\u6765\u3002\u201d \u3000\u3000\u6c5f\u67ab\u4fa7\u8f6c\u8eab\u8eaf\uff0c\u65bd\u5c55\u4f20\u97f3\u4e4b\u672f\uff0c\u9053\uff1a\u201c\u603b\u7ba1\uff0c\u89e3\u51b3\u4e86\u8fd9\u573a\u7eb7\u4e89\uff0c\u4e01\u8001\u4e5f\u4e0d\u4f1a\u7559\u5728\u6d1b\u9633\u5c45\uff0c\u5185\u5bab\u4e00\u7cfb\u6709\u5907\u800c\u6765\uff0c\u54b1\u4eec\u51e0\u4e2a\u4eba\uff0c\u5e94\u4ed8\u4e0d\u4e86\u3002\u201d \u3000\u3000\u4e00\u8bed\u63d0\u9192\u68a6\u4e2d\u4eba\uff0c\u9093\u98de\u6697\u53eb\u4e86\u4e24\u58f0\u60ed\u6127\uff0c\u7acb\u523b\u6539\u6362\u4e00\u526f\u7b11\u5bb9\u3002 \u3000\u3000\u5c31\u662f\u8fd9\u4e00\u9635\u5de5\u592b\uff0c\u6885\u82b1\u5df2\u5e26\u7740\u6625\u3001\u590f\u3001\u79cb\uff0c\u4e09\u5b63\u82b1\u5973\u884c\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u4f46\u6885\u82b1\u7684\u8eab\u540e\uff0c\u8fd8\u8ddf\u7740\u56db\u4e2a\u5168\u8eab\u52b2\u88c5\u7684\u5927\u6c49\u3002 \u3000\u3000\u6c5f\u67ab\u6253\u91cf\u56db\u4e2a\u52b2\u88c5\u5927\u6c49\uff0c\u5e74\u7eaa\u8981\u6bd4\u6d1b\u9633\u5c45\u7684\u9556\u624b\u3001\u4f8d\u5e94\u5927\u4e00\u4e9b\uff0c\u90fd\u5728\u4e8c\u5341\u4e94\u516d\u5c81\u5de6\u53f3\uff0c\u56db\u4e2a\u4eba\u4e00\u6837\u7684\u88c5\u675f\uff0c\u4e00\u6837\u7684\u5175\u5203\uff0c\u80cc\u63d2\u4e00\u628a\u5f2f\u6708\u5200\uff0c\u659c\u6302\u4e00\u5177\u9769\u56ca\u3002 \u3000\u3000\u6625\u82b1\u56db\u5973\u4f69\u7684\u957f\u5251\uff0c\u8377\u82b1\u3001\u83f1\u82b1\u5404\u4f69\u53cc\u5200\uff0c\u6842\u82b1\u3001\u83ca\u82b1\uff0c\u624b\u4e2d\u63d0\u4e00\u53ea\u4e09\u5c3a\u957f\u77ed\u7684\u9ed1\u8272\u68d2\u5b50\u3002 \u3000\u3000\u6885\u82b1\u5374\u8d64\u624b\u7a7a\u62f3\uff0c\u672a\u5e26\u5175\u5203\u3002 \u3000\u3000\u56db\u5b63\u82b1\u5a62\uff0c\u7528\u7684\u5175\u5203\u4e0d\u540c\uff0c\u4e5f\u8bc1\u660e\u4e86\u5979\u4eec\u5728\u6b66\u529f\u4fee\u4e3a\u4e0a\u5404\u6709\u6240\u957f\uff0c\u5979\u4eec\u672c\u8eab\u7684\u827a\u4e1a\uff0c\u4f3c\u662f\u90fd\u5404\u4f9d\u672c\u8eab\u7980\u8d4b\u4e60\u7ec3\uff0c\u8fd9\u662f\u4e2d\u539f\u5404\u5927\u95e8\u6d3e\u6709\u6240\u4e0d\u53ca\u4e4b\u5904\u3002 \u3000\u3000\u201c\u9093\u603b\u7ba1\uff0c\u201d\u6885\u82b1\u5a07\u9762\u4e0a\u4e00\u7247\u51b7\u5386\uff0c\u9053\uff1a\u201c\u6740\u6b7b\u738b\u526f\u603b\u7ba1\u7684\u4e8b\uff0c\u4f60\u8fd8\u8bb0\u5f97\u5427\uff01\u201d\u4e24\u9053\u76ee\u5149\u7531\u4e01\u897f\u5c71\u548c\u4e09\u4e2a\u7070\u8863\u8001\u8005\u7684\u8138\u4e0a\u63a0\u8fc7\uff0c\u76ef\u6ce8\u5728\u9093\u98de\u7684\u7751\u4e0a\u3002 \u3000\u3000\u9093\u98de\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5f53\u7136\u8bb0\u5f97\uff0c\u738b\u9f99\u526f\u603b\u7ba1\u4ee5\u4e0b\u72af\u4e0a\uff0c\u4e0d\u9075\u4ee4\u8c15\uff0c\u5df2\u4e3a\u672c\u603b\u7ba1\u4e0b\u4ee4\u9664\u53bb\uff0c\u8fd9\u4ef6\u4e8b\u5df2\u5448\u62a5\u603b\u5802\u3002 \u3000\u3000\u201c\u6740\u4eba\u507f\u547d\uff0c\u603b\u7ba1\u8be5\u542c\u4eba\u8bf4\u8fc7\u5427\uff1f\u201d\u6885\u82b1\u9053\uff1a\u201c\u4eca\u591c\u6885\u82b1\u5949\u547d\uff0c\u6765\u8ba8\u53d6\u8fd9\u7b14\u8840\u503a\u3002\u201d \u3000\u3000\u201c\u5927\u80c6\uff0c\u201d\u6c5f\u67ab\u51b7\u51b7\u5730\u559d\u9053\uff1a\u201c\u4f60\u662f\u4ec0\u4e48\u8eab\u4efd\uff0c\u6562\u5bf9\u603b\u7ba1\u5982\u6b64\u65e0\u793c\uff1f\u201d \u3000\u3000\u6885\u82b1\u659c\u816e\u7740\u6c5f\u67ab\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u97e9\u526f\u603b\u7ba1\uff0c\u6ca1\u4f60\u7684\u4e8b\uff0c\u4e56\u4e56\u7684\u9000\u4e00\u8fb9\u53bb\uff0c\u6211\u6b20\u4e86\u4f60\u4e00\u4efd\u60c5\uff0c\u4f60\u4e5f\u8bb8\u8fd8\u80fd\u4fdd\u4f4f\u8fd9\u4e2a\u526f\u603b\u7ba1\u7684\u4f4d\u7f6e\uff0c\u53cd\u6b63\u4f60\u4e5f\u4f5c\u4e0d\u4e86\u603b\u7ba1\uff0c\u4f55\u5fc5\u8ddf\u7740\u9093\u98de\u8eba\u8fd9\u6b21\u6df7\u6c34\u5462\uff1f\u201d \u3000\u3000\u9093\u98de\u4e00\u76b1\u7709\uff0c\u5fd6\u9053\uff1a\u201c\u602a\u4e86\uff0c\u6c5f\u67ab\u8fd9\u5c0f\u5b50\u4e00\u7751\u51b7\u5389\uff0c\u600e\u4f1a\u6709\u8fd9\u4e48\u597d\u7684\u4eba\u7f18\u5462\uff1f\u201d \u3000\u3000\u201c\u6885\u82b1\u59d1\u5a18\uff0c\u4f60\u9519\u4e86\uff0c\u201d\u6c5f\u67ab\u51b7\u51b7\u5730\u63a5\u9053\uff1a\u201c\u8fd9\u6d1b\u9633\u5c45\u4e2d\u7684\u603b\u7ba1\uff0c\u4e3a\u6d1b\u9633\u5c45\u4e2d\u9996\u8111\uff0c\u7efc\u7406\u4e00\u5207\u4e8b\u52a1\u2026\u2026\u201d \u3000\u3000\u201c\u97e9\u9738\uff0c\u6211\u8bb0\u5f97\uff0c\u6211\u5df2\u544a\u8bc9\u8fc7\u4f60\uff0c\u6211\u4eec\u5230\u6d1b\u9633\u5c45\u6765\uff0c\u662f\u5ba2\u537f\u8eab\u4efd\uff0c\u662f\u6765\u5e2e\u4f60\u4eec\u7684\u5fd9\u2026\u2026\u201d\u6885\u82b1\u7c89\u9762\u4e0a\u5982\u7f69\u5bd2\u971c\uff0c\u8bed\u6c14\u4e5f\u8f6c\u7684 \u3000\u3000\u5341\u5206\u9634\u5bd2\u53c8\u9053\uff1a\u201c\u6211\u5df2\u7ecf\u529d\u8fc7\u4f60\u4e86\uff0c\u5c3d\u5230\u4e86\u5fc3\u610f\uff0c\u542c\u4e0d\u542c\uff0c\u4f60\u81ea\u62ff\u4e3b\u610f\u5427\uff01\u201d \u3000\u3000\u6c5f\u67ab\u6765\u518d\u63a5\u53e3\uff0c\u5374\u8f6c\u7751\u671b\u7740\u4e01\u897f\u5c71\uff0c\u4e00\u526f\u606d\u5019\u88c1\u51b3\u7684\u795e\u6001\u3002 \u3000\u3000\u4e01\u897f\u5c71\u4e24\u9053\u76ee\u5149\uff0c\u4e00\u76f4\u76ef\u5728\u6885\u82b1\u7684\u8eab\u4e0a\u6253\u91cf\u3002 \u3000\u3000\u6562\u60c5\u4ed6\u5df2\u4e3a\u6885\u82b1\u7684\u5a07\u7f8e\u7684\u5bb9\u8272\u5438\u5f15\u4f4f\u4e86\u3002 \u3000\u3000\u201c\u539f\u6765\u662f\u6709\u4e86\u540e\u63f4\u2026\u2026\u201d\u6885\u82b1\u795e\u60c5\u51b7\u5cfb\u7684\u8bf4\uff0c\u201c\u60f3\u6765\u662f\u5929\u9a6c\u603b\u5802\u6d3e\u6765\u7684\u9ad8\u624b\u4e86\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u5fc3\u4e2d\u6697\u6697\u6253\u9f13\uff0c\u4ed8\u9053\uff1a\u201c\u8fd9\u51e0\u4e2a\u8001\u5bb6\u4f19\u5982\u679c\u4e3a\u56db\u5b63\u82b1\u5a62\u7684\u7f8e\u8272\u8ff7\u4f4f\uff0c\u4eca\u665a\u4e0a\u8fd9\u573a\u620f\u5c31\u65e0\u6cd5\u5531\u5f97\u5b8c\u7f8e\u4e86\uff0c\u5e72\u7b97\u4e07\u7b97\uff0c\u7adf\u7136\u672a\u7b97\u51fa\u8fd9\u4e2a\u5927\u5927\u7684\u6f0f\u6d1e\u3002\u201d \u3000\u3000\u201c\u4f60\u53eb\u6885\u82b1\uff1f\u201d\u4e01\u897f\u5c71\u5f00\u4e86\u53e3\u3002 \u3000\u3000\u201c\u4e0d\u9519\uff0c\u59d1\u5a18\u53eb\u6885\u82b1\uff0c\u201d\u6253\u65ad\u4e86\u4e86\u897f\u5c71\u7684\u8bdd\u53c8\u63a5\u9053\uff1a\u201c\u5929\u9a6c\u603b\u5802\u4e2d\u4eba\uff0c\u6700\u597d\u4e0d\u8981\u4ecb\u5165\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u5426\u5219\u6211\u4eec\u4e5f\u53ea\u597d\u5411\u5bab\u4e3b\u8bf7\u63f4\u4e86\uff01\u201d \u3000\u3000\u201c\u8001\u592b\u8ba4\u4e3a\uff0c\u5f7c\u6b64\u4e4b\u95f4\uff0c\u5c1a\u672a\u95f9\u5230\u4e0d\u53ef\u5f00\u4ea4\u7684\u65f6\u5019\uff0c\u5982\u679c\u80fd\u53ca\u65f6\u60ac\u5d16\u52d2\u9a6c\uff0c\u201d\u4e01\u897f\u5c71\u9053\uff1a\u201c\u53ef\u907f\u514d\u4e0d\u5c11\u4f24\u4ea1\u2026\u2026\u201d \u3000\u3000\u201c\u53ef\u4ee5\uff0c\u201d\u6885\u82b1\u8bf4\uff0c\u201c\u53ea\u8981\u628a\u6740\u6b7b\u738b\u526f\u603b\u7ba1\u7684\u51f6\u624b\u4ea4\u51fa\u6765\uff0c\u6211\u4eec\u7acb\u523b\u64a4\u8d70\uff01\u201d \u3000\u3000\u6c5f\u67ab\u5fc3\u4e2d\u5fd6\u9053\uff1a\u201c\u4e01\u897f\u5c71\u65e9\u5df2\u6012\u706b\u6ee1\u80f8\u819b\uff0c\u73b0\u5728\u5374\u6e29\u7684\u50cf\u56e2\u68c9\u82b1\uff0c\u96be\u9053\u4ed6\u5fc3\u4e2d\u5f88\u754f\u60e7\u5185\u76f4\u5b9e\u529b\u5417\uff1f\u201d \u3000\u3000\u4f46\u95fb\u9093\u98de\u8bf4\u9053\uff1a\u201c\u56fd\u6709\u56fd\u6cd5\uff0c\u5bb6\u6709\u5bb6\u89c4\uff0c\u662f\u672c\u603b\u7ba1\u4e0b\u4ee4\u6740\u6b7b\u4e86\u6297\u547d\u7684\u738b\u526f\u603b\u7ba1\uff0c\u59d1\u5a18\u4e00\u5207\u51b2\u7740\u6211\u6765\u5c31\u662f\u3002\u201d \u3000\u3000\u4ed6\u627f\u62c5\u8d77\u8d23\u4efb\uff0c\u4f46\u4e5f\u8bf4\u660e\u4e86\u6740\u4eba\u7684\u5e76\u4e0d\u662f\u4ed6\u3002 \u3000\u3000\u201c\u597d\u554a\uff01\u90a3\u5c31\u7531\u603b\u7ba1\u62b5\u547d\u5427\uff01\u201d \u3000\u3000\u6253\u86c7\u987a\u68cd\u4e0a\uff0c\u4e0d\u627e\u51f6\u624b\u4e86\uff0c\u53ea\u627e\u603b\u7ba1\uff0c\u8fd9\u4e0d\u662f\u62a5\u4ec7\uff0c\u662f\u660e\u6446\u7740\u7684\u593a\u6743\uff0c\u76ee\u5149\u4e00\u63a0\u6842\u82b1\u3001\u83ca\u82b1\u3002 \u3000\u3000\u4e0d\u7528\u4e0b\u4ee4\uff0c\u53ea\u662f\u4e00\u4e2a\u773c\u8272\uff0c\u6842\u82b1\u3001\u83ca\u82b1\uff0c\u5df2\u5411\u524d\u51b2\u6765\u3002 \u3000\u3000\u6c5f\u67ab\u4e00\u6a2a\u8eab\uff0c\u8fce\u5411\u4e8c\u5973\uff0c\u4e03\u5b9d\u548c\u5c1a\u3001\u80e1\u840d\u3001\u6bb5\u4e5d\uff0c\u4e5f\u8ddf\u7740\u7ad9\u4e86\u8d77\u6765\u3002 \u3000\u3000\u4f46\u95fb\u4e01\u897f\u5c71\u51b7\u51b7\u559d\u9053\uff1a\u201c\u90fd\u7ed9\u6211\u7ad9\u4f4f\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u4f9d\u8a00\u505c\u4e0b\uff0c\u4f46\u4e8c\u5973\u5374\u672a\u7406\u4f1a\uff0c\u6842\u82b1\u53f3\u624b\u4e00\u6325\uff0c\u6293\u5411\u6c5f\u67ab\u3002 \u3000\u3000\u5979\u4eec\u624b\u4e2d\u660e\u660e\u63d0\u7740\u5175\u5203\uff0c\u4f46\u5374\u6536\u8d77\u4e0d\u7528\u3002 \u3000\u3000\u6c5f\u67ab\u51dd\u7acb\u4e0d\u52a8\uff0c\u6842\u82b1\u7ea4\u5de7\u7684\u624b\u6307\uff0c\u5feb\u642d\u4e0a\u4e86\u6c5f\u67ab\u7684\u80a9\u5934\uff0c\u6c5f\u67ab\u4ecd\u672a\u95f4\u907f\u3002 \u3000\u3000\u8fd9\u4efd\u9547\u9759\u7684\u5de5\u592b\uff0c\u8fde\u6842\u82b1\u4e5f\u5fc3\u751f\u656c\u670d\u3002 \u3000\u3000\u5f53\u7136\uff0c\u6c5f\u67ab\u4e0d\u662f\u5584\u7537\u4fe1\u5973\uff0c\u6709\u7740\u4efb\u4eba\u5bb0\u5272\u7684\u6c14\u5ea6\uff0c\u4ed6\u5728\u7b49\uff0c\u51c6\u5907\u7b49\u5230\u6700\u540e\u5173\u952e\uff0c\u518d\u65bd\u53cd\u51fb\u3002 \u3000\u3000\u679c\u7136\uff0c\u4e01\u897f\u5c71\u52a8\u4e86\uff0c\u53f3\u624b\u62cd\u51fa\u4e00\u80a1\u5f3a\u52b2\u7684\u638c\u98ce\uff0c\u51fb\u5411\u6842\u82b1\uff0c\u529b\u9053\u96c4\u6d51\uff0c\u8feb\u7684\u6842\u82b1\u4e0d\u5f97\u4e0d\u6536\u56de\u5df2\u642d\u4e0a\u6c5f\u67ab\u5de6\u80a9\u7684\u53f3\u624b\uff0c\u75be\u9000\u4e09\u6b65\u3002 \u3000\u3000\u6c5f\u67ab\u6697\u6697\u5401\u4e00\u53e3\u6c14\uff0c\u5fd6\u9053\uff1a\u201c\u8fd9\u5934\u8001\u72d0\u72f8\u679c\u7136\u662f\u72e1\u8bc8\u767e\u51fa\uff0c\u8ba9\u4eba\u65e0\u6cd5\u731c\u6d4b\u51fa\u4ed6\u4e0b\u4e00\u6b65\u7684\u884c\u52a8\u3002\u201d \u3000\u3000\u6842\u82b1\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u7ec8\u4e8e\u5fcd\u4e0d\u4f4f\u4e86\uff1f\u201d \u3000\u3000\u201c\u8001\u592b\u65e2\u7136\u6765\u4e86\uff0c\u5c82\u80fd\u773c\u770b\u4f60\u4eec\u884c\u51f6\u3001\u4f24\u4eba\u3002\u201d\u4e01\u897f\u5c71\u62c2\u9aef\u4e00\u7b11\u53c8\u9053\uff1a\u201c\u5185\u5bab\u4e00\u7cfb\uff0c\u90fd\u662f\u4f60\u4eec\u8fd9\u79cd\u5c0f\u59d1\u5a18\u5417\uff1f\u6709\u6ca1\u6709\u80fd\u591f\u4f5c\u5f97\u4e3b\u610f\u7684\u4eba\uff0c\u51fa\u6765\u548c\u8001\u592b\u8bf4\u51e0\u53e5\u8bdd\uff1f\u201d \u3000\u3000\u6885\u82b1\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6211\u5c31\u662f\u80fd\u4f5c\u4e3b\u7684\u4eba\uff0c\u4ec0\u4e48\u8bdd\uff0c\u5c3d\u7ba1\u5bf9\u6211\u8bf4\u5427\uff01\u201d \u3000\u3000\u4e01\u897f\u5c71\u9053\uff1a\u201c\u597d\u5427\uff01\u6253\u4e86\u5c0f\u7684\uff0c\u4e0d\u6015\u8001\u7684\u4e0b\u51fa\u5934\u2026\u2026\u201d \u3000\u3000\u201c\u8001\u5339\u592b\uff0c\u597d\u5927\u7684\u53e3\u6c14\u2026\u2026\u201d \u3000\u3000\u4e5f\u8bb8\u662f\u6885\u82b1\u5b58\u5fc3\u6fc0\u6012\u4e01\u897f\u5c71\uff0c\u4e5f\u8bb8\u662f\u5979\u81ea\u8d1f\u6210\u5c31\uff0c\u771f\u6ca1\u6709\u628a\u4e01\u897f\u5c71\u770b\u5728\u773c\u4e2d\u3002 \u3000\u3000\u4f46\u4e00\u53e5\u8001\u5339\u592b\uff0c\u786e\u5df2\u6fc0\u8d77\u4e86\u4e01\u897f\u5c71\u7684\u6740\u673a\uff0c\u6012\u58f0\u559d\u9053\uff1a\u201c\u5c0f\u620f\u4eba\uff0c\u6211\u6d3b\u5288\u4e86\u4f60\u2026\u2026\u201d \u3000\u3000\u6325\u638c\u4e0b\u51fb\uff0c\u5e26\u8d77\u4e86\u547c\u5578\u7684\u98ce\u58f0\u3002 \u3000\u3000\u8fd9\u4e00\u638c\uff0c\u529b\u9053\u5947\u5f3a\u3002 \u3000\u3000\u6885\u82b1\u7adf\u7136\u4e0d\u95ea\u907f\uff0c\u53f3\u624b\u98df\u3001\u4e2d\u6307\u4e8c\u6307\u4e00\u5e76\uff0c\u8fce\u5411\u4e86\u897f\u5c71\u7684\u638c\u5fc3\u70b9\u53bb\u3002 \u3000\u3000\u4e00\u80a1\u9510\u98ce\uff0c\u50cf\u9525\u5c16\u4e00\u822c\u523a\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u4e01\u897f\u5c71\u611f\u53d7\u5230\u638c\u98ce\u88ab\u90a3\u80a1\u9510\u5229\u7684\u5c16\u9525\u7a7f\u8fc7\uff0c\u5927\u5927\u5403\u4e86\u4e00\u60ca\uff0c\u8feb\u7684\u4ed6\u6536\u638c\u95ea\u907f\uff0c\u4e5f\u6fc0\u8d77\u4e86\u66f4\u5f3a\u70c8\u7684\u6012\u706b\uff0c\u5927\u559d\u4e00\u58f0\uff0c\u53cc\u638c\u8fde\u73af\u5288\u51fa\uff0c\u5c55\u5f00\u4e00\u8f6e\u731b\u653b\u3002 \u3000\u3000\u5f3a\u731b\u7684\u638c\u98ce\uff0c\u9707\u5f97\u5385\u4e2d\u7269\u5177\u6447\u52a8\uff0c\u4e5f\u98d8\u626c\u8d77\u5385\u4e2d\u4eba\u7684\u8863\u8882\u3002 \u3000\u3000\u4e00\u4ee3\u9b54\u5934\uff0c\u5185\u529b\u96c4\u6d51\uff0c\u679c\u975e\u5c0f\u53ef\u3002 \u3000\u3000\u4f46\u89c1\u6885\u82b1\u5a07\u5c0f\u3001\u73b2\u73d1\u7684\u8eab\u8eaf\uff0c\u5728\u638c\u98ce\u4ea4\u51fb\u4e2d\u56de\u8f6c\uff0c\u6307\u70b9\u3001\u638c\u5207\uff0c\u5168\u7528\u7684\u65a9\u8109\u7a81\u7a74\u624b\u6cd5\uff0c\u7adf\u7136\u628a\u4e01\u897f\u5c71\u4e00\u8f6e\u5f3a\u731b\u7684\u653b\u52bf\u6321\u4f4f\uff0c\u800c\u4e14\u8fd8\u5e38\u5e38\u8feb\u7684\u4e01\u897f\u5c71\u6536\u81c2\u7f29\u62f3\uff0c\u95ea\u907f\u53cd\u51fb\u3002 \u3000\u3000\u9093\u98de\u53ea\u770b\u7684\u53cc\u773c\u53d1\u76f4\uff0c\u4ed6\u539f\u8ba4\u4e3a\u4e01\u897f\u5c71\u7684\u7cbe\u6e5b\u529f\u529b\uff0c\u8d6b\u8d6b\u5a01\u540d\uff0c\u4e09\u4e94\u62db\u5c31\u53ef\u4ee5\u6536\u62fe\u4e86\u6885\u82b1\uff0c\u90a3\u77e5\u4e24\u4eba\u7f20\u6597\u6570\u5341\u5408\uff0c\u7adf\u7136\u662f\u79cb\u8272\u5e73\u5206\u3002 \u3000\u3000\u8868\u9762\u4e0a\u770b\uff0c\u4e01\u897f\u5c71\u4f3c\u662f\u5360\u4e86\u4f18\u52bf\uff0c\u6885\u82b1\u4e00\u76f4\u5728\u4e01\u897f\u5c71\u5f3a\u731b\u7684\u638c\u98ce\u4e2d\u56de\u65cb\u8f6c\u52a8\u3002 \u3000\u3000\u4e00\u5f00\u59cb\u5c31\u662f\u8fd9\u6837\uff0c\u6570\u5341\u56de\u5408\u4e4b\u540e\uff0c\u4ecd\u662f\u5982\u6b64\uff0c\u4e01\u897f\u5c71\u7684\u638c\u529b\u4e0d\u89c1\u8870\u7aed\uff0c\u6885\u82b1\u7684\u8eab\u6cd5\u4ecd\u7136\u662f\u90a3\u4e48\u7075\u52a8\u3002 \u3000\u3000\u4f46\u770b\u7684\u6700\u5403\u60ca\u5374\u662f\u80e1\u840d\u3001\u6bb5\u4e5d\uff0c\u60f3\u4e0d\u5230\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u53c8\u6709\u70b9\u5a07\u5f31\u7684\u6885\u82b1\uff0c\u7adf\u662f\u4e00\u4f4d\u5982\u6b64\u96be\u7f20\u7684\u5bf9\u624b\u2026\u2026\u3002 \u3000\u3000\u4e01\u897f\u5c71\u4e45\u6218\u4e0d\u4e0b\uff0c\u80f8\u4e2d\u6012\u706b\u9ad8\u70e7\uff0c\u4f46\u5bf9\u6885\u82b1\u7075\u5de7\u53cd\u51fb\uff0c\u5fc3\u4e2d\u53c8\u6709\u4e9b\u6212\u60e7\u2026\u2026\u3002 \u3000\u3000\u8fd9\u4e24\u79cd\u60c5\u7eea\u6df7\u5408\u6210\u4e00\u79cd\u4e34\u654c\u65f6\u5c11\u6709\u7684\u72b9\u8c6b\uff0c\u4e00\u76f4\u76d8\u7b97\u7740\u662f\u5426\u8be5\u5168\u529b\u51fa\u624b\uff0c\u65bd\u5c55\u7edd\u62db\u7f6e\u654c\u4e8e\u6b7b\u3002 \u3000\u3000\u4f46\u5982\u4e00\u51fb\u4e0d\u4e2d\uff0c\u4e5f\u53ef\u62db\u6765\u6885\u82b1\u7684\u62fc\u547d\u53cd\u51fb\u548c\u5bf9\u65b9\u5168\u529b\u7fa4\u653b\u3002 \u3000\u3000\u4e01\u897f\u5c71\u4f30\u4e0d\u900f\u6c5f\u67ab\u548c\u4e03\u5b9d\u548c\u5c1a\u6280\u827a\u5982\u4f55\uff1f\u53ea\u80fd\u628a\u4e09\u4f4d\u968f\u884c\u7684\u62a4\u6cd5\u4f5c\u4e3a\u7b97\u8ba1\u7684\u5b9e\u529b\u3002 \u3000\u3000\u4ee5\u6885\u82b1\u4f5c\u4e3a\u6807\u51c6\uff0c\u4e09\u540d\u62a4\u6cd5\uff0c\u4e5f\u53ea\u5404\u62b5\u4e00\u4eba\uff0c\u4f18\u52a3\u4e4b\u52bf\uff0c\u5df2\u7ea6\u7565\u53ef\u89c1\uff0c\u662f\u4e2a\u654c\u4f17\u6211\u5be1\u7684\u5c40\u9762\uff0c\u521a\u624d\u7684\u81ea\u8d1f\u548c\u4e00\u80a1\u601c\u9999\u60dc\u7389\u4e4b\u5fc3\uff0c\u987f\u7136\u6d88\u5931\u3002 \u3000\u3000\u4f46\u540c\u6885\u82b1\u4e00\u58f0\u5a07\u7b11\uff0c\u9053\uff1a\u201c\u5929\u9a6c\u5802\u7684\u9ad8\u624b\uff0c\u4e5f\u4e0d\u8fc7\u5982\u6b64\u800c\u5df2\uff0c\u5f53\u5fc3\uff0c\u59d1\u5a18\u6211\u8981\u53cd\u51fb\u4e86\u3002\u201d \u3000\u3000\u638c\u6cd5\u4e00\u53d8\uff0c\u6613\u5b88\u4e3a\u653b\uff0c\u5239\u90a3\u95f4\u6307\u3001\u638c\u5e7b\u8d77\uff0c\u73af\u7ed5\u5728\u4e01\u897f\u5c71\u5927\u7a74\u3001\u8981\u5bb3\u4e4b\u95f4\u3002 \u3000\u3000\u679c\u7136\u662f\uff0c\u7acb\u523b\u642c\u56de\u4e86\u52a3\u52bf\uff0c\u8feb\u7684\u4e01\u897f\u5c71\uff0c\u6709\u70b9\u624b\u5fd9\u811a\u4e71\uff0c\u5e94\u4ed8\u4e0d\u6687\u3002 \u3000\u3000\u6c5f\u67ab\u51b7\u773c\u65c1\u89c2\uff0c\u53d1\u73b0\u4e86\u6885\u82b1\u6709\u5982\u9644\u8eab\u5f71\u5b50\u4e00\u822c\uff0c\u7d27\u968f\u5728\u4e86\u897f\u5c71\u7684\u8eab\u5f62\u56db\u5468\uff0c\u5b8c\u5168\u662f\u8fd1\u8eab\u8089\u640f\u3002 \u3000\u3000\u4e01\u897f\u5c71\u4e0d\u53ca\u6885\u82b1\u7075\u5de7\uff0c\u8fd1\u8eab\u4e4b\u640f\uff0c\u5148\u5df2\u5403\u4e86\u5927\u4e8f\uff0c\u7a7a\u6709\u4e00\u8eab\u6df1\u539a\u7684\u5185\u529f\uff0c\u96c4\u6d51\u7684\u638c\u529b\uff0c\u5374\u65e0\u6cd5\u53d1\u6325\u51fa\u6765\u3002 \u3000\u3000\u8fd9\u5c31\u662f\u5c3a\u6709\u6240\u77ed\uff0c\u5bf8\u6709\u6240\u957f\uff0c\u6885\u82b1\u4ee5\u667a\u6167\u9009\u62e9\u4e86\u5bf9\u5df2\u6709\u5229\u7684\u6253\u6cd5\u3002 \u3000\u3000\u6c5f\u67ab\u6b63\u60f3\u5f00\u53e3\uff0c\u63d0\u9192\u4e86\u897f\u5c71\uff0c\u53ea\u8981\u628a\u6885\u82b1\u903c\u51fa\u4e09\u5c3a\u4ee5\u5916\uff0c\u8fd9\u573a\u640f\u6597\u9e7f\u6b7b\u8c01\u624b\uff0c\u8fd8\u5f88\u96be\u8bf4\uff0c\u4f46\u5982\u50cf\u8fd9\u6837\u7684\u6253\u4e0b\u53bb\uff0c\u80af\u5b9a\u4e86\u897f\u5c71\u975e\u8d25\u4e0d\u53ef\u2026\u2026\u3002 \u3000\u3000\u7528\u4e0d\u7740\u6885\u82b1\u4f24\u4ed6\uff0c\u5355\u662f\u4e00\u4e2a\u7d2f\u5b57\uff0c\u4e5f\u8981\u628a\u4e01\u897f\u5c71\u62d6\u5012\u3002 \u3000\u3000\u4f46\u8bdd\u5c06\u51fa\u53e3\u65f6\uff0c\u6c5f\u67ab\u5fc3\u4e2d\u4e00\u52a8\uff0c\u53c8\u6539\u53d8\u4e86\u4e3b\u610f\u95ed\u53e3\u4e0d\u8a00\u3002 \u3000\u3000\u6885\u82b1\u662f\u654c\u4eba\uff0c\u4f46\u5929\u9a6c\u5802\u4e2d\u7684\u4e01\u897f\u5c71\uff0c\u4e5f\u662f\u654c\u4eba\uff0c\u4e00\u65e6\u4e01\u897f\u5c71\u6bc1\u5728\u4e86\u6885\u82b1\u624b\u4e2d\uff0c\u5929\u9a6c\u5802\u53ef\u80fd\u6709\u66f4\u6fc0\u70c8\u7684\u53cd\u5e94\uff0c\u8fd9\u5ea7\u6d1b\u9633\u5c45\uff0c\u5c31\u6210\u4e86\u4ed6\u4eec\u4e92\u76f8\u4e89\u593a\u7684\u91cd\u70b9\uff0c\u5185\u5bab\u4e00\u7cfb\u4e2d\u4eba\uff0c\u5982\u82e5\u8d25\u4e86\uff0c\u81ea\u662f\u4e5f\u4e0d\u7518\u5fc3\uff0c\u4ea6\u5fc5\u9020\u6d3e\u9ad8\u624b\u589e\u63f4\u3002 \u3000\u3000\u8fd9\u6d1b\u9633\u5c45\u65e5\u8fdb\u6597\u91d1\uff0c\u8c01\u4e5f\u4e0d\u613f\u610f\u6bc1\u4e86\u5b83\uff0c\u8c01\u4e5f\u4e0d\u613f\u9000\u51fa\u53bb\uff01 \u3000\u3000\u8fd9\u5ea7\u5c0f\u5c0f\u7684\u5730\u65b9\uff0c\u5c82\u4e0d\u662f\u6210\u4e86\u5185\u5bab\u548c\u5929\u9a6c\u5802\u7684\u9ad8\u624b\u640f\u547d\u4e4b\u5904\uff0c\u60e8\u70c8\u7684\u8840\u6218\uff0c\u5728\u6697\u4e2d\u8fdb\u884c\uff0c\u8c01\u90fd\u4e0d\u613f\u5f20\u626c\u3002 \u3000\u3000\u6211\u6c5f\u67ab\u53ea\u8981\u5728\u4e2d\u95f4\u7ef4\u6301\u4e00\u5177\u5fae\u5999\u7684\u5e73\u8861\uff0c\u4e0d\u8ba9\u4ed6\u4eec\u4efb\u4f55\u4e00\u65b9\u83b7\u5f97\u7edd\u5bf9\u4f18\u52bf\uff0c\u6076\u6218\u5c31\u4f1a\u6301\u7eed\u4e0b\u53bb\uff0c\u5bf9\u5185\u5bab\u548c\u5929\u9a6c\u5802\u7684\u5b9e\u529b\uff0c\u5927\u4f5c\u6d88\u8017\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u60f3\u5230\u4e86\u5f97\u610f\u4e4b\u5904\uff0c\u5fcd\u4e0d\u4f4f\u5634\u89d2\u95f4\u6cdb\u8d77\u7b11\u610f\u3002 \u3000\u3000\u4f46\u9093\u98de\u4e00\u76f4\u5728\u6697\u4e2d\u7559\u795e\u7740\u6c5f\u67ab\uff0c\u7ecf\u8fc7\u4e86\u4eca\u591c\u7684\u4e00\u756a\u51b2\u7a81\uff0c\u9093\u98de\u5bf9\u8fd9\u4f4d\u5c5e\u4e0b\u7684\u624d\u667a\uff0c\u53c8\u4f5c\u4e86\u4e00\u6b21\u8bc4\u4f30\uff0c\u53ea\u89c9\u4ed6\u4e0d\u4f46\u6b66\u529f\u96be\u6d4b\uff0c\u7528\u95f4\u884c\u8c0b\uff0c\u65e0\u4e0d\u9ad8\u4eba\u4e8c\u7b49\uff0c\u8fd9\u4e2a\u4eba\u4e00\u5b9a\u8981\u597d\u597d\u5730\u62c9\u4f4f\u3002 \u3000\u3000\u770b\u573a\u4e2d\u640f\u6597\uff0c\u4e01\u897f\u5c71\u7531\u76db\u800c\u8870\uff0c\u5df2\u5904\u52a3\u52bf\uff0c\u6c5f\u67ab\u5374\u6cdb\u8d77\u7b11\u610f\uff0c\u5fcd\u4e0d\u4f4f\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u526f\u603b\u7ba1\uff0c\u4f60\u770b\u8fd9\u573a\u4ed7\u518d\u6253\u4e0b\u53bb\uff0c\u4f1a\u6709\u4e00\u4e2a\u4ec0\u4e48\u7ed3\u679c\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u5fc3\u751f\u8b66\u89c9\uff0c\u8f6c\u8fc7\u8eab\u5b50\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4e01\u8001\u4ee5\u5df1\u4e4b\u77ed\uff0c\u5bf9\u4eba\u4e4b\u957f\uff0c\u5403\u4e8f\u592a\u5927\u4e86\u3002\u201d \u3000\u3000\u9093\u98de\u9053\uff1a\u201c\u8fd9\u5f00\u59cb\u4e00\u6218\u53cc\u65b9\u90fd\u662f\u4e3b\u8111\u5bf9\u640f\uff0c\u5982\u82e5\u4e01\u8001\u4e0d\u5e78\u843d\u8d25\uff0c\u54b1\u4eec\u5c31\u5341\u53bb\u4e03\u4eba\uff0c\u518d\u60f3\u633d\u56de\u5927\u5c40\uff0c\u53ea\u6015\u662f\u4e0d\u592a\u5bb9\u6613\uff0c\u90a3\u65f6\uff0c\u6211\u4eec\u8fd9\u603b\u7ba1\u548c\u526f\u603b\u7ba1\u7684\u804c\u4f4d\uff0c\u4e0d\u4f46\u8981\u62f1\u624b\u8ba9\u4eba\uff0c\u80fd\u4e0d\u80fd\u4fdd\u4f4f\u6027\u547d\uff0c\u4e5f\u5f88\u96be\u8bf4\u4e86\uff01\u201d \u3000\u3000\u6c5f\u67ab\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u770b\u8d77\u6765\uff0c\u9093\u98de\u662f\u771f\u6709\u4e9b\u6025\u4e86\uff0c\u8fd9\u4e2a\u4eba\u5bf9\u6211\u8d8a\u6765\u8d8a\u4fe1\u4efb\u4e86\uff0c\u7531\u4ed6\u5750\u4e3b\u603b\u7ba1\u7684\u4f4d\u7f6e\uff0c\u5bf9\u6211\u4eec\u7684\u6d3b\u52a8\uff0c\u5012\u662f\u65b9\u4fbf\u4e0d\u5c11\u2026\u2026\u3002\u201d \u3000\u3000\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u53e3\u4e2d\u8bf4\u9053\uff1a\u201c\u4ee5\u5c5e\u4e0b\u7684\u770b\u6cd5\uff0c\u4e01\u8001\u4e0d\u5e94\u8be5\u8d25\uff0c\u53ea\u8981\u4ed6\u628a\u640f\u6597\u7684\u65b9\u6cd5\u4fee\u6b63\u4e00\u4e0b\uff0c\u4e5f\u8bb8\u5c31\u53ef\u4ee5\u6273\u56de\u52a3\u52bf\uff01\u201d \u3000\u3000\u201c\u8981\u5982\u4f55\u4fee\u6b63\u5462\uff1f\u201d \u3000\u3000\u201c\u603b\u7ba1\u53ef\u4ee5\u63d0\u9192\u4e01\u8001\u4e00\u4e0b\uff0c\u8981\u4ed6\u628a\u6885\u82b1\u903c\u5f00\u4e09\u5c3a\u2026\u2026\u201d\u6c5f\u67ab\u8bf4\uff1a\u201c\u7136\u540e\uff0c\u4e01\u8001\u5c31\u53ef\u53d1\u6325\u51fa\u6df1\u539a\u7684\u529f\u529b\uff0c\u4ee5\u5f3a\u731b\u7684\u638c\u529b\uff0c\u538b\u5236\u654c\u4eba\u7684\u7075\u5de7\u53d8\u5316\uff0c\u4f7f\u6885\u82b1\u65e0\u6cd5\u5c3d\u5c55\u6240\u957f\u3002\u201d \u3000\u3000\u201c\u5bf9\uff01\u6211\u4e5f\u662f\u8fd9\u4e2a\u770b\u6cd5\u2026\u2026\u201d\u9093\u98de\u63a5\u9053\uff1a\u201c\u6885\u82b1\u5982\u6b64\u987d\u5f3a\u3001\u96be\u7f20\uff0c\u5b9e\u51fa\u4e00\u4eba\u7684\u610f\u6599\u4e4b\u5916\uff0c\u4f46\u4e0d\u77e5\u5176\u4ed6\u7684\u516b\u4f4d\u82b1\u5973\u6b66\u529f\u5982\u4f55\uff1f\u201d \u3000\u3000\u201c\u4ee5\u5c5e\u4e0b\u7684\u770b\u6cd5\uff0c\u6885\u82b1\u662f\u56db\u5b63\u82b1\u5973\u4e4b\u9b41\uff0c\u82b1\u5973\u4e2d\u6ca1\u6709\u4eba\u5f3a\u5f97\u8fc7\u5979\u3002\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u5012\u662f\u90a3\u56db\u4e2a\u8eab\u4f69\u5f2f\u5200\u7684\u5e74\u8f7b\u4eba\uff0c\u90fd\u662f\u6740\u624b\u578b\u7684\u4eba\u7269\uff0c\u5200\u6cd5\u4e0a\u5fc5\u7136\u6709\u51fa\u4eba\u610f\u8868\u7684\u53d8\u5316\uff0c\u4e0d\u53ef\u8f7b\u89c6\u3002\u201d \u3000\u3000\u9093\u98de\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u90a3\u662f\u8bf4\u4e01\u8001\u8fd9\u4e00\u4ed7\uff0c\u7edd\u5bf9\u4e0d\u80fd\u8d25\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u5bf9\uff01\u603b\u7ba1\u5feb\u63d0\u9192\u4e00\u4e0b\u4e01\u8001\uff0c\u518d\u8fd9\u6837\u6253\u4e0b\u53bb\uff0c\u4ed6\u5df2\u7ecf\u652f\u6491\u4e0d\u4e86\u597d\u4e45\uff0c\u5c31\u8981\u4f24\u5728\u6885\u82b1\u624b\u4e2d\u3002\u201d \u3000\u3000\u201c\u4e01\u8001\uff01\u7528\u4e0d\u7740\u548c\u4e00\u4e2a\u5c0f\u4e2b\u6bd4\u8bd5\u62db\u672f\u3001\u8eab\u6cd5\u2026\u2026\u201d\u9093\u98de\u63d0\u9ad8\u58f0\u97f3\uff0c\u9053\uff1a\u201c\u4ee5\u4e01\u8001\u6df1\u539a\u7684\u5185\u529f\uff0c\u4ee5\u6cf0\u5c71\u538b\u9876\u4e4b\u52bf\uff0c\u5f3a\u6253\u529b\u653b\u529b\u514b\u5f3a\u654c\uff0c\u5c31\u80dc\u6570\u53ef\u671f\u4e86\u2026\u2026\u201d \u3000\u3000\u8fd9\u756a\u8bdd\u8bf4\u7684\u5341\u5206\u660e\u663e\uff0c\u4e01\u897f\u5c71\u81ea\u7136\u660e\u767d\uff0c\u7a81\u7136\u9000\u540e\u4e09\u6b65\uff0c\u75be\u5feb\u7684\u5288\u51fa\u4e86\u4e24\u638c\u3002 \u3000\u3000\u4e24\u638c\u5408\u4e00\uff0c\u6c47\u96c6\u6210\u4e00\u80a1\u5f3a\u529b\u7684\u6697\u52b2\uff0c\u6321\u4f4f\u4e86\u6885\u82b1\u7684\u653b\u52bf\u3002 \u3000\u3000\u6885\u82b1\u88ab\u638c\u529b\u903c\u8fd1\u4e86\u4e09\u6b65\uff0c\u6b63\u60f3\u6b3a\u8eab\u518d\u4e0a\uff0c\u4f46\u4e01\u897f\u5c71\u5df2\u4e0d\u5bb9\u5979\u518d\u65bd\u6545\u6280\uff0c\u957f\u62f3\u3001\u5feb\u638c\uff0c\u4e00\u8f6e\u731b\u51fb\uff0c\u679c\u7136\uff0c\u628a\u6885\u82b1\u903c\u6321\u5728\u4e09\u5c3a\u4e4b\u5916\u3002 \u3000\u3000\u8fd9\u4e00\u4e0b\uff0c\u8f6e\u5230\u6885\u82b1\u5403\u82e6\u5934\u4e86\uff0c\u5979\u88ab\u903c\u5728\u4e09\u5c3a\u4e4b\u5916\uff0c\u638c\u3001\u6307\u90fd\u65e0\u6cd5\u8fd1\u654c\u4e4b\u8eab\uff0c\u5b8c\u5168\u65e0\u6cd5\u5bf9\u4e86\u897f\u5c71\u6784\u6210\u5a01\u534f\uff1a\u5012\u662f\u4e01\u897f\u5c71\u53ef\u4ee5\u4ece\u5bb9\u7684\u8fd0\u638c\u51fa\u51fb\uff0c\u903c\u5f97\u6885\u82b1\u7eb5\u8dc3\u95ea\u907f\u3002 \u3000\u3000\u8fd9\u5f53\u513f\uff0c\u7a81\u6709\u4e00\u4e2a\u9ed1\u8863\u4eba\uff0c\u5927\u6b65\u884c\u51fa\uff0c\u9053\uff1a\u201c\u628a\u8fd9\u4e2a\u8001\u5339\u592b\u4ea4\u7ed9\u5c5e\u4e0b\u5427\uff01\u201d \u3000\u3000\u4ed6\u53e3\u4e2d\u8bf4\u8bdd\uff0c\u5f2f\u5200\u5df2\u7ecf\u51fa\u9798\uff0c\u8bdd\u8bf4\u5b8c\uff0c\u4e00\u6e9c\u5bd2\u5149\uff0c\u5df2\u653b\u5411\u4e86\u4e01\u897f\u5c71\u3002 \u3000\u3000\u4f46\u95fb\u4e00\u9635\u523a\u5200\u7834\u98ce\u4e4b\u58f0\uff0c\u4e00\u628a\u9b3c\u5934\u5200\u6a2a\u91cc\u98de\u6765\uff0c\u67b6\u4f4f\u4e86\u5f2f\u5200\uff0c\u9053\uff1a\u201c\u4f60\u5c0f\u5b50\u60f3\u6253\uff0c\u5c31\u7531\u8001\u592b\u5565\u4f60\u73a9\u51e0\u62db\u3002\u201d \u3000\u3000\u4e00\u4e2a\u7070\u8863\u62a4\u6cd5\uff0c\u4eba\u968f\u5200\u81f3\uff0c\u6321\u4f4f\u4e86\u9ed1\u8863\u4eba\u7684\u9762\u524d\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u624b\u8d77\u5200\u843d\uff0c\u5587\u5587\u5587\uff01\u8fde\u653b\u4e86\u4e09\u5200\uff0c\u4e09\u5200\u5982\u4e00\uff0c\u4e32\u6210\u4e00\u7247\u5bd2\u5149\u3002 \u3000\u3000\u7070\u8863\u8001\u8005\u624b\u4e2d\u7684\u9b3c\u5934\u5200\uff0c\u5982\u6d8c\u8d77\u7684\u4e00\u56f4\u5149\u5708\uff0c\u786c\u628a\u4e09\u5200\u63a5\u4e86\u4e0b\u6765\uff0c\u9053\uff1a\u201c\u8bf4\u8bf4\u770b\uff0c\u4f60\u662f\u4ec0\u4e48\u8eab\u4efd\uff0c\u53eb\u4ec0\u4e48\u540d\u5b57\uff0c\u8001\u592b\u5200\u4e0b\u4e0d\u65a9\u65e0\u540d\u4e4b\u5352\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u2014\u2014\u201d \u3000\u3000\u201c\u5929\u9a6c\u603b\u638c\u62a4\u6cd5\u5218\u6e05\u3002\u201d \u3000\u3000\u201c\u5f2f\u5200\u5341\u4e09\u53f7\uff0c\u4f60\u62ff\u547d\u6765\u5427\uff01\u201d \u3000\u3000\u5f2f\u5200\u4e00\u8f6c\uff0c\u7a81\u7136\u7531\u4e00\u4e2a\u602a\u5f02\u7684\u89d2\u5ea6\u4e2d\u523a\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u5218\u6e05\u5fc3\u4e2d\u4e00\u60ca\uff0c\u4ed6\u5200\u5c01\u56db\u8def\uff0c\u5378\u6599\u4e0d\u5230\u5f2f\u5200\u7684\u653b\u52bf\uff0c\u7adf\u4ece\u4e00\u79cd\u6781\u4e0d\u53ef\u80fd\u7684\u89d2\u5ea6\u653b\u4e86\u51fa\u6765\u3002 \u3000\u3000\u8fd9\u5c31\u6597\u5f97\u5218\u6e05\u6709\u70b9\u624b\u5fd9\u811a\u4e71\uff0c\u5feb\u9000\u4e24\u6b65\uff0c\u624d\u628a\u4e00\u5200\u67b6\u4f4f\u3002 \u3000\u3000\u5f2f\u5200\u5341\u4e09\u53f7\uff0c\u51b7\u6f20\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u6b7b\u5b9a\u4e86\uff01\u201d \u3000\u3000\u9661\u7136\u4ee5\u8eab\u62a4\u5200\uff0c\u76f4\u5411\u5218\u6e05\u6251\u6765\u3002 \u3000\u3000\u5218\u6e05\u8eab\u7ecf\u767e\u6218\uff0c\u5374\u4ece\u672a\u89c1\u8fc7\u8fd9\u79cd\u6253\u6cd5\uff0c\u4eba\u5728\u524d\uff0c\u5200\u5728\u540e\uff0c\u8fd9\u4e0d\u662f\u8bda\u5fc3\u9001\u547d\u5417\uff1f \u3000\u3000\u5c31\u5728\u4ed6\u5fc3\u4e2d\u4e00\u72b9\u8c6b\u95f4\uff0c\u950b\u5229\u7684\u5f2f\u5200\uff0c\u6df1\u5165\u5185\u8151\u3002 \u3000\u3000\u5267\u70c8\u7684\u75bc\u75db\uff0c\u53cd\u4f7f\u5f97\u5218\u6e05\u7684\u795e\u5fd7\u5ffd\u7136\u6e05\u660e\u3002 \u3000\u3000\u4e0d\u7528\u67e5\u770b\u4f24\u52bf\uff0c\u5218\u6e05\u5df2\u77e5\u9053\u81ea\u5df1\u6d3b\u4e0d\u6210\u4e86\uff0c\u90a3\u4e00\u5200\u5207\u5f00\u4e86\u5fc3\u3001\u809d\u8981\u5bb3\uff0c\u6697\u4e2d\u63d0\u805a\u4e86\u6570\u5341\u5e74\u4fee\u7ec3\u7684\u4e00\u53e3\u771f\u5143\u4e4b\u6c14\uff0c\u9b3c\u5934\u5200\u4e00\u5f0f\u6a2a\u65a9\uff0c\u5bd2\u8292\u6d41\u52a8\uff0c\u751f\u751f\u628a\u90a3\u9ed1\u8863\u4eba\u8170\u65a9\u4e24\u65ad\u3002 \u3000\u3000\u5218\u6e05\u5168\u529b\u6325\u51fa\u4e00\u5200\u4e4b\u540e\uff0c\u524d\u80f8\u5f00\u88c2\uff0c\u9c9c\u8840\u72c2\u55b7\uff0c\u8eab\u8eaf\u5012\u5730\uff0c\u6c14\u7edd\u800c\u901d\u3002 \u3000\u3000\u597d\u60e8\u70c8\u7684\u4e00\u6218\uff0c\u4ea4\u624b\u4e24\u62db\uff0c\u4e00\u5200\u4e00\u547d\u3002 \u3000\u3000\u4e01\u897f\u5c71\u6025\u5288\u4e24\u638c\uff0c\u903c\u9000\u6885\u82b1\uff0c\u671b\u7740\u5218\u6e05\u7684\u5c38\u4f53\uff0c\u957f\u957f\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u540c\u5f52\u4e8e\u5c3d\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u201d\u6885\u82b1\u51b7\u51b7\u63a5\u9053\uff1a\u201c\u4ed6\u4eec\u4fee\u4e60\u7684\u5c31\u662f\u4e00\u51fb\u593a\u547d\u7684\u6b66\u529f\uff0c\u53ea\u6c42\u4e00\u51fb\u6740\u654c\uff0c\u81ea\u8eab\u7684\u5b89\u5371\uff0c\u4ece\u4e0d\u8ba1\u8f83\uff0c\u8fd9\u5c31\u662f\u6b7b\u58eb\uff0c\u90a3\u4e00\u4f4d\u613f\u610f\u518d\u8bd5\u4e00\u6b21\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u4e5f\u88ab\u90a3\u4e00\u5200\u6240\u52a8\uff0c\u76ee\u5149\u8f6c\u6ce8\u5230\u4e09\u4e2a\u9ed1\u8863\u4eba\u7684\u8eab\u4e0a\uff0c\u53ea\u89c1\u4ed6\u4eec\u795e\u8272\u51b7\u9759\uff0c\u5bf9\u540c\u4f34\u4e4b\u6b7b\uff0c\u5b8c\u5168\u65e0\u52a8\u4e8e\u8877\uff0c\u90a3\u80a1\u8f7b\u8c08\u751f\u6b7b\u7684\u51b7\u6f20\uff0c\u770b\u7684\u4eba\u5fc3\u751f\u5bd2\u610f\uff0c\u5fc3\u4e2d\u5fd6\u9053\uff1a\u201c\u8fd9\u624d\u662f\u771f\u6b63\u53ef\u6015\u7684\u654c\u4eba\uff0c\u53ea\u4e0d\u77e5\u4ed6\u4eec\u662f\u4e3a\u836f\u7269\u63a7\u5236\u795e\u5fd7\uff0c\u8fd8\u662f\u7ecf\u7531\u4e00\u79cd\u7279\u6b8a\u65b9\u6cd5\u8bad\u7ec3\u51fa\u6765\u7684\u6b7b\u58eb\uff0c\u5f88\u53ef\u60dc\uff0c\u521a\u624d\u672a\u80fd\u5168\u795e\u8d2f\u6ce8\uff0c\u770b\u6e05\u695a\u4ed6\u7684\u51fa\u5200\u65b9\u6cd5\uff0c\u662f\u5426\u6709\u7834\u89e3\u7684\u529e\u6cd5\uff1f\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u4e00\u4e2a\u9ed1\u8863\u6b66\u58eb\uff0c\u5df2\u7ecf\u6b65\u884c\u4e86\u51fa\u6765\uff0c\u7f13\u7f13\u62d4\u51fa\u5f2f\u6708\u5200\uff0c\u8bf4\u9053\uff1a\u201c\u5f2f\u5200\u5341\u4e8c\u53f7\uff0c\u90a3\u4e00\u4f4d\u4e0d\u6015\u6b7b\u7684\uff0c\u8bf7\u6765\u4e00\u8bd5\u3002\u201d \u3000\u3000\u8fd9\u4e9b\u4eba\u6ca1\u6709\u59d3\u540d\uff0c\u53ea\u6709\u4e00\u4e2a\u6570\u5b57\u4ee3\u53f7\uff0c\u8868\u793a\u51fa\u4ed6\u7684\u8eab\u4efd\uff0c\u8fd9\u8bf4\u660e\u4e86\uff0c\u4ed6\u4eec\u4e0d\u4f1a\u5728\u6c5f\u6e56\u4e0a\u626c\u540d\u7acb\u4e07\uff0c\u53ea\u662f\u4e00\u79cd\u4e89\u9738\u6c5f\u6e56\u7684\u5de5\u5177\u3001\u6740\u624b\u2026\u2026\u3002 \u3000\u3000\u8fd9\u4e9b\u4eba\u80fd\u628a\u5200\u6cd5\u7ec3\u5230\u8be1\u5947\u83ab\u6d4b\u7684\u5883\u754c\uff0c\u4e00\u5200\u53d6\u547d\uff0c\u5fc5\u5177\u6709\u76f8\u5f53\u7684\u5929\u4efd\uff0c\u8fd9\u6837\u7684\u4eba\uff0c\u53c8\u600e\u80af\u820d\u547d\u4e3a\u4eba\uff0c\u7518\u4f5c\u6740\u624b\uff0c\u770b\u4ed6\u4eec\u795e\u667a\u6e05\u660e\uff0c\u53c8\u4e0d\u4f3c\u88ab\u836f\u7269\u63a7\u5236\u7684\u4eba\uff0c\u95ee\u9898\u5fc5\u7136\u51fa\u5728\u8bad\u7ec3\u7684\u65b9\u6cd5\u4e0a\uff0c\u627e\u51fa\u4e2a\u4e2d\u7684\u9690\u5bc6\uff0c\u624d\u80fd\u89e3\u53bb\u8fd9\u4e9b\u4eba\u7684\u675f\u7f1a\u3002 \u3000\u3000\u6c5f\u67ab\u60f3\u5230\u4e86\u5173\u952e\u4e4b\u5904\uff0c\u6697\u4e2d\u4e0b\u5b9a\u51b3\u5fc3\uff0c\u96c6\u4e2d\u5168\u529b\u627e\u51fa\u4e2a\u4e2d\u7684\u795e\u79d8\u539f\u56e0\u3002 \u3000\u3000\u4f46\u95fb\u6885\u82b1\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8bf8\u4f4d\u65e2\u7136\u6ca1\u6709\u80c6\u6562\u8fce\u6218\u7684\u4eba\uff0c\u90a3\u5c31\u675f\u624b\u6295\u964d\u5427\uff01\u6211\u4eec\u53ea\u8981\u60e9\u5904\u5143\u51f6\u9996\u6076\uff0c\u4e0d\u4f1a\u591a\u4f5c\u6740\u622e\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u6536\u6444\u5fc3\u795e\uff0c\u8f6c\u773c\u770b\u53bb\uff0c\u53ea\u89c1\u4e24\u4e2a\u7070\u8863\u62a4\u6cd5\u548c\u4e01\u897f\u5c71\u8138\u8272\u94c1\u9752\uff0c\u4e00\u8bed\u4e0d\u53d1\uff0c\u795e\u60c5\u5341\u5206\u5c34\u5c2c\u3001\u602a\u5f02\u3002 \u3000\u3000\u90a3\u662f\u5fc3\u4e2d\u65e2\u4e0d\u7518\u53d7\u6885\u82b1\u8a00\u8bed\u8bbd\u6fc0\uff0c\u53c8\u4e0d\u6562\u51fa\u9762\u8fce\u6218\uff0c\u6127\u6068\u4ea4\u7ec7\u800c\u6210\u7684\u611f\u89c9\uff0c\u5f62\u6210\u7684\u602a\u5f02\u795e\u8272\u2026\u2026\u3002 \u3000 \u7b2c \u516b \u56de\u3000\u6d1b\u9633\u5c45\u98ce\u9759\u6697\u6d41\u6d8c \u3000 \u3000\u3000\u4f46\u6574\u5ea7\u5927\u5385\u4e2d\u8138\u8272\u6700\u4e3a\u96be\u770b\u7684\uff0c\u8981\u7b97\u662f\u9093\u98de\u4e86\uff0c\u4ed6\u5fc3\u4e2d\u660e\u767d\uff0c\u6885\u82b1\u53e3\u4e2d\u7684\u5143\u51f6\u3001\u9996\u6076\uff0c\u5c31\u662f\u6307\u4ed6\u800c\u8a00\u3002 \u3000\u3000\u5982\u679c\uff0c\u4e01\u897f\u5c71\u51b3\u5fc3\u7f62\u6218\u9000\u8d70\uff0c\u53ea\u6709\u4ed6\u5fc5\u987b\u653e\u624b\u4e00\u6218\uff0c\u56e0\u4e3a\uff0c\u4ed6\u5fc3\u4e2d\u5f88\u660e\u767d\uff0c\u6218\u6b7b\u867d\u7136\u53ef\u6015\uff0c\u4f46\u88ab\u64d2\u5c06\u66f4\u4e3a\u60b2\u60e8\uff0c\u90a3\u5c06\u662f\u6c42\u6d3b\u4e0d\u80fd\uff0c\u6c42\u6b7b\u4e0d\u6210\u7684\u60b2\u60e8\u5c40\u9762\u3002 \u3000\u3000\u5fc3\u4e2d\u51db\u6000\u754f\u60e7\uff0c\u4f7f\u5f97\u9093\u98de\u7adf\u7136\u4e0d\u6562\u5984\u63d2\u4e00\u8a00\uff0c\u751f\u6050\u591a\u8a00\u62db\u7978\uff0c\u4f7f\u5f97\u4e01\u897f\u5c71\u501f\u673a\u4e0b\u53f0\u3002 \u3000\u3000\u56e0\u4e3a\uff0c\u9093\u98de\u6240\u6709\u7684\u5e0c\u671b\uff0c\u90fd\u5bc4\u6258\u4e3b\u4e01\u897f\u5c71\u51fb\u8d25\u6885\u82b1\u7684\u4e00\u6218\u4e0a\u3002 \u3000\u3000\u6c5f\u67ab\u9ed8\u67e5\u573a\u4e2d\u60c5\u52bf\uff0c\u5fc3\u77e5\u65e0\u6cd5\u6fc0\u8d77\u4e01\u897f\u5c71\u7684\u6597\u5fd7\uff0c\u60f3\u4fdd\u4f4f\u6d1b\u9633\u5c45\u8fd9\u4e2a\u636e\u70b9\uff0c\u53ea\u6709\u633a\u8eab\u8fce\u6218\u4e86\u3002 \u3000\u3000\u4f46\u8fd9\u4e2a\u636e\u70b9\uff0c\u51b3\u4e0d\u80fd\u5931\u53bb\uff0c\u633a\u8eab\u800c\u6218\uff0c\u4e5f\u662f\u6700\u574f\u7684\u6253\u7b97\uff0c\u5fc3\u4e2d\u76d8\u7b97\u4e86\u4e00\u9635\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a \u3000\u3000\u201c\u4e01\u8001\uff0c\u5f2f\u6708\u5200\u7684\u6b66\u58eb\uff0c\u4f3c\u4e4e\u662f\u4e13\u4e1a\u6740\u624b\uff0c\u4ed6\u4eec\u4ee5\u81ea\u5df1\u7684\u6027\u547d\uff0c\u4e89\u53d6\u6740\u4eba\u7684\u673a\u4f1a\uff0c\u53ea\u8981\u80fd\u907f\u5f00\u4ed6\u81f4\u547d\u7684\u4e00\u51fb\uff0c\u4ed6\u4eec\u5c31\u4efb\u51ed\u5bb0\u5272\u4e86\u3002\u201d \u3000\u3000\u201c\u6709\u9053\u7406\u554a\u2026\u2026\u201d\u4e01\u897f\u5c71\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u6d1b\u9633\u5c45\u7684\u4eba\uff0c\u4e5f\u8be5\u6709\u4eba\u51fa\u9762\uff0c\u8bd5\u8bd5\u8fd9\u4e9b\u6740\u624b\u7684\u5a01\u529b\u4e86\u3002\u201d \u3000\u3000\u201c\u8bf4\u7684\u4e5f\u662f\uff0c\u6d1b\u9633\u5c45\u4e2d\u7684\u4eba\uff0c\u5e94\u8be5\u51fa\u9762\u4e86\uff0c\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u90a3\u5c31\u7531\u5728\u4e0b\u5148\u6321\u8fd9\u4e00\u5200\u8bd5\u8bd5\uff0c\u4e0d\u8fc7\uff0c\u4e01\u8001\u548c\u4e24\u4f4d\u62a4\u6cd5\u8981\u770b\u4ed4\u7ec6\uff0c\u97e9\u67d0\u4eba\u4ee5\u547d\u72af\u9669\uff0c\u7ed9\u4e09\u4f4d\u4e00\u4e2a\u5ba1\u67e5\u654c\u52bf\u7684\u673a\u4f1a\uff0c\u4f46\u5982\u8981\u4fdd\u4f4f\u6d1b\u9633\u5c45\u8fd9\u4e2a\u636e\u70b9\uff0c\u8fd8\u8981\u9760\u4e09\u4f4d\u4e4b\u529b\u4e86\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u8001\u592b\u7b54\u5e94\u4f60\uff0c\u201d\u4e01\u897f\u5c71\u8bf4\uff0c\u201c\u53ea\u8981\u4f60\u51fa\u9762\u63a5\u4e0b\u8fd9\u4e00\u51fb\uff0c\u4e0d\u8bba\u4f60\u662f\u751f\u662f\u6b7b\uff0c\u4ee5\u540e\u7684\u4e8b\uff0c\u7531\u8001\u592b\u548c\u4e24\u4f4d\u62a4\u6cd5\u627f\u62c5\u8d77\u6765\uff0c\u62fc\u4e0a\u6211\u4eec\u51e0\u6761\u8001\u547d\u5c31\u662f\u3002\u201d \u3000\u3000\u201c\u6709\u4e01\u8001\u8fd9\u4e2a\u8bdd\uff0c\u5728\u4e0b\u6b7b\u800c\u65e0\u61be\u2026\u2026\u201d\u6c5f\u67ab\u76ee\u5149\u8f6c\u6ce8\u5230\u4e24\u4e2a\u7070\u8863\u8001\u8005\u8eab\u4e0a\uff0c\u63a5\u9053\uff1a\u201c\u4e24\u4f4d\u62a4\u6cd5\uff0c\u600e\u4e48\u8bf4\uff1f\u201d \u3000\u3000\u201c\u6211\u4eec\u552f\u4e01\u8001\u4e4b\u547d\u662f\u4ece\uff0c\u201d\u4e24\u4e2a\u7070\u8863\u8001\u8005\uff0c\u9f50\u58f0\u8bf4\u9053\uff1a\u201c\u97e9\u526f\u603b\u7ba1\u820d\u547d\u8bd5\u5200\uff0c\u8001\u592b\u7b49\u76f8\u4fe1\uff0c\u5fc5\u53ef\u627e\u51fa\u7834\u89e3\u90a3\u4e00\u5200\u4e4b\u6cd5\u3002\u201d \u3000\u3000\u6c5f\u8bbd\u9053\uff1a\u201c\u597d\uff01\u8bf8\u4f4d\u4e00\u8a00\u5982\u5c71\uff0c\u97e9\u67d0\u4eba\u4fe1\u5f97\u8fc7\u2026\u2026\u201d\u4e3e\u6b65\u5411\u524d\u884c\u53bb\u3002 \u3000\u3000\u201c\u97e9\u9738\uff0c\u4f60\u4e3a\u6d1b\u9633\u5c45\u820d\u547d\u5c31\u4e49\uff0c\u9093\u67d0\u4eba\u7edd\u4e0d\u4f1a\u5fd8\u8bb0\u4f60\u4e00\u756a\u5fe0\u5fc3\u8c6a\u60c5\u3002\u201d\u9093\u98de\u8ba4\u5b9a\uff0c\u6c5f\u67ab\u5fc5\u6b7b\u4e8e\u90a3\u4e00\u5200\u4e4b\u4e0b\u3002 \u3000\u3000\u201c\u5728\u4e0b\u53d7\u603b\u7ba1\u7231\u987e\u3001\u63d0\u643a\uff0c\u7406\u5e94\u820d\u547d\u62a5\u6548\u3002\u201d \u3000\u3000\u201c\u526f\u603b\u7ba1\uff0c\u8ba9\u9f50\u67d0\u4eba\u4ee3\u4f60\u63a5\u8fd9\u4e00\u5200\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u5927\u6b65\u800c\u51fa\u3002 \u3000\u3000\u8fd9\u5c31\u662f\u6b63\u90aa\u4e4b\u95f4\u7684\u4e0d\u540c\u4e4b\u5904\uff0c\u90aa\u4ee5\u5229\u5bb3\u4e3a\u4e3b\uff0c\u6b63\u4ee5\u4e49\u7406\u4e3a\u5148\uff0c\u4e03\u5b9d\u548c\u5c1a\u611f\u89c9\u5230\u6c5f\u67ab\u662f\u4e3b\u6301\u5927\u5c40\u7684\u4eba\uff0c\u4e0d\u5e94\u4ee5\u8eab\u6d89\u9669\uff0c\u5c31\u62b1\u7740\u727a\u7272\u81ea\u5df1\u7684\u5fc3\u60c5\uff0c\u5192\u9669\u51fa\u6218\u3002 \u3000\u3000\u80e1\u840d\u3001\u6bb5\u4e5d\u4e89\u5148\u6050\u540e\u7684\u884c\u4e86\u51fa\u6765\uff0c\u4f46\u5374\u88ab\u6c5f\u67ab\u6325\u624b\u62e6\u4f4f\uff0c\u7b11\u9053\uff1a\u201c\u9000\u56de\u53bb\uff0c\u597d\u597d\u4fdd\u62a4\u9093\u603b\u7ba1\u2026\u2026\u201d\u4e3e\u6b65\u884c\u8fd1\u9ed1\u8863\u4eba\uff0c\u4f0f\u8eab\u6361\u8d77\u4e86\u5730\u4e0a\u7684\u5f2f\u6708\u5200\uff0c\u63a5\u9053\uff1a\u201c\u6765\u5427\uff01\u5728\u4e0b\u5c31\u4ee5\u8fd9\u67c4\u5f2f\u6708\u5200\uff0c\u63a5\u4f60\u4e00\u62db\u3002\u201d \u3000\u3000\u6885\u82b1\u51b7\u51b7\u7684\u6ce8\u89c6\u4e86\u6c5f\u67ab\u4e00\u773c\uff0c\u503c\uff1a\u201c\u526f\u603b\u7ba1\u679c\u7136\u662f\u5fe0\u5fc3\u62a4\u4e3b\u554a\uff01\u201d \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u5927\u4e0d\u4e86\u4e00\u6761\u547d\u561b\uff01\u8fd9\u5c31\u53eb\u4eba\u6b7b\u7559\u540d\u554a\uff01\u201d \u3000\u3000\u5f2f\u5200\u5341\u4e8c\u53f7\u56de\u987e\u4e86\u6885\u82b1\u4e00\u773c\uff0c\u4f3c\u662f\u5728\u8bf7\u793a\u673a\u5b9c\u3002 \u3000\u3000\u8fd9\u4e9b\u6740\u624b\uff0c\u608d\u4e0d\u754f\u6b7b\uff0c\u4f46\u5374\u53c8\u5177\u6709\u4e86\u7edd\u5bf9\u670d\u4ece\u7684\u7cbe\u795e\uff0c\u6c5f\u67ab\u4e5f\u4e0d\u5f97\u4e0d\u4f69\u670d\uff0c\u8fd9\u662f\u6781\u4e3a\u6210\u529f\u7684\u8bad\u7ec3\u4e86\u3002 \u3000\u3000\u201c\u6740\uff01\u201d\u6885\u82b1\u795e\u60c5\u51b7\u8083\u5730\u4e0b\u8fbe\u4e86\u4ee4\u6e1d\u3002 \u3000\u3000\u6740\u5b57\u51fa\u53e3\uff0c\u4eba\u5f71\u98de\u8d77\uff0c\u5f2f\u6708\u5200\u5316\u6210\u4e86\u4e00\u9053\u5bd2\u8292\uff0c\u65a9\u5411\u6c5f\u67ab\u3002 \u3000\u3000\u6c5f\u67ab\u624b\u4e2d\u7684\u5f2f\u6708\u5200\u5411\u4e0a\u64a9\u8d77\uff0c\u5200\u5149\u5e7b\u5316\u51fa\u4e00\u5708\u94f6\u5149\uff0c\u62a4\u4f4f\u80f8\u8179\u8981\u5bb3\u3002 \u3000\u3000\u4f46\u95fb\u4e00\u9635\u91d1\u94c1\u4ea4\u9e23\uff0c\u5f2f\u5200\u5341\u4e8c\u53f7\u653b\u51fa\u7684\u51cc\u5389\u4e00\u5200\uff0c\u7adf\u88ab\u6c5f\u67ab\u63a5\u4e0b\u3002 \u3000\u3000\u4e01\u897f\u5c71\u548c\u4e24\u4e2a\u7070\u8863\u62a4\u6cd5\uff0c\u90fd\u5168\u795e\u8d2f\u6ce8\u770b\u6c5f\u67ab\u63a5\u8fd9\u4e00\u5200\uff0c\u6c5f\u67ab\u63a5\u4e0b\u4e86\uff0c\u800c\u4e14\u5e73\u5b89\u65e0\u4e8b\u3002 \u3000\u3000\u201c\u54fc\uff01\u97e9\u526f\u603b\u7ba1\u7adf\u7136\u662f\u6df1\u85cf\u4e0d\u9732\u7684\u9ad8\u4eba\u2026\u2026\u201d\u6885\u82b1\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6211\u5012\u662f\u770b\u8d70\u773c\u4e86\uff01\u201d \u3000\u3000\u201c\u8fc7\u5956\uff0c\u8fc7\u5956\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u533a\u533a\u53ea\u662f\u5168\u529b\u8fd0\u5200\uff0c\u628a\u9632\u5b88\u7684\u5200\u52bf\u96c6\u4e2d\u4e8e\u5c3a\u8bb8\u65b9\u5706\u4e4b\u5904\uff0c\u52c9\u5f3a\u63a5\u4e0b\u4e00\u51fb\u7f62\u4e86\u3002\u201d \u3000\u3000\u4eba\u7684\u611f\u53d7\u5f88\u5947\u602a\uff0c\u5218\u6e05\u88ab\u5f2f\u6708\u5200\u5341\u4e09\u53f7\u6b66\u571f\uff0c\u53d1\u5200\u53d6\u547d\uff0c\u4f7f\u4e01\u897f\u5c71\u3001\u9093\u98de\u7b49\u4e2a\u4e2a\u80c6\u98a4\u5fc3\u60ca\uff0c\u4f46\u773c\u770b\u6c5f\u67ab\u7adf\u80fd\u63a5\u4e0b\u8fd9\u4e00\u8bb0\u5f2f\u6708\u5200\uff0c\u7acb\u523b\u53c8\u6709\u7740\u4e0d\u8fc7\u5982\u6b64\u4e4b\u611f\uff0c\u6c14\u52bf\u4e3a\u4e4b\u4e00\u632f\u3002 \u3000\u3000\u6c5f\u67ab\u4e0d\u4f46\u63a5\u4e0b\u4e86\u4e00\u5200\uff0c\u4e5f\u7b80\u660e\u7684\u8bf4\u51fa\u4e86\u4ed6\u63a5\u4e0b\u8fd9\u4e00\u5200\u7684\u65b9\u6cd5\u3002 \u3000\u3000\u90a3\u662f\u5168\u529b\u8fd0\u5200\uff0c\u5b9a\u6218\u8fce\u51fb\uff0c\u62a4\u4f4f\u80f8\u8179\u8981\u5bb3\u3002 \u3000\u3000\u4e01\u897f\u5c71\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8001\u592b\u51e0\u4e4e\u88ab\u8fd9\u4e00\u5200\u552c\u4f4f\u4e86\u3002\u201d\u76ee\u5149\u8f6c\u6ce8\u5230\u5de6\u9996\u7070\u8863\u8001\u8005\u8eab\u4e0a\uff0c\u63a5\u9053\uff1a\u201c\u5468\u62a4\u6cd5\uff0c\u6d1b\u9633\u5c45\u7684\u97e9\u526f\u603b\u7ba1\u867d\u80fd\u63a5\u4e00\u5200\uff0c\u53ea\u53ef\u60dc\u4ed6\u5df2\u65e0\u53cd\u51fb\u4e4b\u529b\uff0c\u5468\u5144\u53bb\u63a5\u8fd9\u4e00\u9635\uff0c\u4e0d\u7528\u624b\u4e0b\u7559\u60c5\u4e86\u3002\u201d \u3000\u3000\u8a00\u4e0b\u4e4b\u610f\uff0c\u4ecd\u6781\u8f7b\u89c6\u6c5f\u67ab\u3002 \u3000\u3000\u201c\u662f\uff01\u5468\u67d0\u4eba\u5168\u529b\u4ee5\u8d74\u3002\u201d\u7f13\u6b65\u884c\u51fa\uff0c\u540c\u65f6\u62bd\u51fa\u4e86\u5175\u5203\u3002 \u3000\u3000\u90a3\u662f\u4e00\u9762\u5f69\u8272\u8273\u4e3d\u7684\u957f\u5f62\u516b\u5366\u724c\uff0c\u5468\u8fb9\u950b\u5203\u5982\u5200\uff0c\u67c4\u957f\u4e00\u5c3a\u4e94\u5bf8\uff0c\u724c\u957f\u4e24\u5c3a\u516b\u5bf8\uff0c\u6a2a\u5bbd\u4e5f\u5728\u4e24\u5c3a\u4ee5\u4e0a\uff0c\u8fd9\u79cd\u5916\u95e8\u5175\u5203\uff0c\u4e0d\u5728\u4e8c\u5341\u516b\u822c\u5175\u5203\u4e4b\u5185\uff0c\u6700\u5927\u7684\u7279\u8272\u662f\uff0c\u5177\u6709\u76fe\u724c\u7684\u4f5c\u7528\uff0c\u9632\u5b88\u9762\u5f88\u5927\u3002 \u3000\u3000\u4e01\u897f\u5c71\u8981\u4ed6\u51fa\u6218\uff0c\u663e\u7136\u662f\u5fc3\u4e2d\u65e9\u6709\u8ba1\u7b97\u4e86\u3002 \u3000\u3000\u7070\u8863\u8001\u8005\u8d70\u7684\u5f88\u6162\uff0c\u6b65\u5c65\u4e5f\u5341\u5206\u6c89\u91cd\uff0c\u663e\u7136\u662f\u4e00\u9762\u8d70\uff0c\u4e00\u9762\u5728\u8fd0\u96c6\u529f\u529b\uff0c\u4eba\u81f3\u5385\u4e2d\uff0c\u53f3\u624b\u7a81\u7136\u5728\u724c\u67c4\u4e4b\u4e2d\uff0c\u53c8\u62bd\u51fa\u4e00\u628a\u67f3\u53f6\u5f62\u7684\u5c16\u7ec6\u957f\u5200\uff0c\u5de6\u624b\u516b\u5366\u5e73\u4e3e\u80f8\u524d\uff0c\u67f3\u53f6\u5200\u9690\u4e8e\u724c\u624b\uff0c\u51b7\u7136\u8bf4\u9053\uff1a\u201c\u5468\u6e90\u5019\u6559\uff01\u201d \u3000\u3000\u4eba\u5f71\u95ea\u52a8\uff0c\u5341\u56db\u53f7\u6b66\u58eb\u7a81\u7136\u76f4\u51b2\u800c\u4e0a\uff0c\u786c\u5411\u516b\u5366\u724c\u4e0a\u649e\u53bb\u3002 \u3000\u3000\u8fd9\u4e0d\u662f\u62fc\u6597\uff0c\u7b80\u76f4\u662f\u9001\u547d\uff0c\u53ea\u89c1\u4eba\u5f71\u51b2\u4e86\u4e0a\u6765\uff0c\u5374\u4e0d\u89c1\u5200\u5149\u6240\u5728\u3002 \u3000\u3000\u4f46\u5468\u6e90\u5374\u662f\u4e00\u70b9\u4e5f\u4e0d\u6562\u8f7b\u654c\uff0c\u4e00\u6536\u5165\u5366\u724c\uff0c\u62a4\u5728\u8eab\u524d\uff0c\u53f3\u624b\u5185\u67f3\u53f6\u5200\u76f4\u523a\u800c\u51fa\u3002 \u3000\u3000\u5200\u5982\u95ea\u7535\uff0c\u7531\u9ed1\u8863\u4eba\u7684\u5de6\u80f8\u76f4\u7a7f\u800c\u5165\uff0c\u76f4\u900f\u540e\u80cc\u3002 \u3000\u3000\u4f46\u90a3\u9ed1\u8863\u6b66\u58eb\u88ab\u5200\u6cbf\u7a7f\u7684\u8eab\u8eaf\uff0c\u4ecd\u7136\u5411\u51b2\u53bb\uff0c\u5468\u6e90\u5de6\u624b\u7684\u516b\u5366\u724c\u5411\u524d\u63a8\u51fa\uff0c\u4ee5\u62d2\u6321\u90a3\u9ed1\u8863\u4eba\u5411\u524d\u98de\u51b2\u7684\u8eab\u8eaf\u3002 \u3000\u3000\u7a81\u7136\u95f4\u5f2f\u5200\u98de\u51fa\uff0c\u4e00\u62b9\u51b7\u8292\uff0c\u7531\u4fa7\u9762\u653b\u5165\uff0c\u5468\u6e90\u4e00\u6761\u6267\u724c\u7684\u5de6\u81c2\uff0c\u88ab\u9f50\u8098\u5207\u65ad\uff0c\u4f46\u90a3\u5411\u524d\u63a8\u8fdb\u7684\u516b\u5366\u724c\uff0c\u5374\u672a\u505c\u6b62\uff0c\u649e\u51fb\u5728\u9ed1\u8863\u5341\u56db\u53f7\u6b66\u571f\u7684\u8eab\u4e0a\uff0c\u5f3a\u5927\u7684\u4e92\u649e\u4e4b\u529b\uff0c\u51fb\u788e\u4e86\u9ed1\u8863\u6b66\u58eb\u7684\u5934\u9885\uff0c\u4eba\u4e5f\u63a8\u6389\u5230\u516b\u5c3a\u5916\u3002 \u3000\u3000\u9ed1\u8863\u6b66\u58eb\u662f\u6d3b\u4e0d\u6210\u4e86\uff0c\u4f46\u5468\u6e90\u65ad\u4e86\u4e00\u6761\u5de6\u81c2\uff0c\u8840\u5982\u6cc9\u6d8c\uff0c\u4eba\u4e5f\u75bc\u51fa\u4e86\u4e00\u8eab\u5927\u6c57\u3002 \u3000\u3000\u4e01\u897f\u5c71\u8fd0\u6307\u5982\u98ce\uff0c\u8fde\u70b9\u4e86\u5468\u6e90\u4e09\u5904\u7a74\u9053\uff0c\u6b62\u4f4f\u6d41\u8840\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5468\u5144\uff0c\u633a\u5f97\u4f4f\u4e48\uff1f\u201d \u3000\u3000\u201c\u8fd8\u597d\uff0c\u603b\u7b97\u4fdd\u4f4f\u4e86\u8fd9\u6761\u8001\u547d\u2026\u2026\u201d\u5468\u6e90\u75db\u82e6\u7684\u8bf4\uff1a\u201c\u8fd9\u662f\u771f\u6b63\u7684\u6b7b\u571f\uff0c\u9632\u4e0d\u80dc\u9632\uff01\u201d \u3000\u3000\u4e01\u897f\u5c71\u53d6\u51fa\u4e00\u7c92\u4e39\u4e38\uff0c\u6295\u5165\u5468\u6e90\u53e3\u4e2d\uff0c\u63a5\u9053\uff1a\u201c\u5468\u5144\uff0c\u5feb\u8bf7\u5750\u606f\u4e00\u4e0b\u3002\u201d \u3000\u3000\u5468\u6e90\u541e\u4e0b\u4e39\u4e38\uff0c\u7f13\u6b65\u9000\u4e0b\u3002 \u3000\u3000\u5f2f\u5200\u6b66\u58eb\u4e0d\u754f\u6b7b\u4ea1\u7684\u608d\u52c7\uff0c\u518d\u4e00\u6b21\u9707\u9a87\u4e86\u4eba\u5fc3\uff0c\u4ed6\u4eec\u53ea\u6c42\u4f24\u3001\u654c\uff0c\u4e0d\u987e\u5b89\u5371\u7684\u6253\u6cd5\uff0c\u8fde\u4e01\u897f\u5c71\u8fd9\u7b49\u8eab\u7ecf\u767e\u6218\u7684\u8001\u6c5f\u6e56\uff0c\u5fc3\u5934\u4e5f\u6cdb\u8d77\u5bd2\u610f\uff0c\u60f3\u4e0d\u51fa\u4ed6\u4eec\u4e0b\u4e00\u6b21\u52a8\u624b\u65f6\uff0c\u53c8\u7528\u4ec0\u4e48\u6837\u7684\u82b1\u62db\u4f24\u4eba\u3002 \u3000\u3000\u56de\u987e\u4e86\u6c5f\u67ab\u4e00\u773c\uff0c\u4e01\u897f\u5c71\u5fc3\u4e2d\u5fd6\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\uff0c\u600e\u4f1a\u63a5\u4e0b\u4e00\u5200\uff0c\u5b89\u7136\u65e0\u6059\uff0c\u96be\u9053\u4ed6\uff0c\u4ed6\u4eec\u5728\u6d1b\u9633\u5c45\u4e2d\u957f\u65e5\u76f8\u5904\uff0c\u6709\u4e86\u4ea4\u5f80\uff0c\u5f7c\u6b64\u624b\u4e0b\u7559\u60c5\u2026\u2026\u201d \u3000\u3000\u60f3\u5230\u6b64\u5904\uff0c\u4e0d\u7981\u5fc3\u5934\u706b\u8d77\uff0c\u51b7\u7b11\u4e00\u7b11\u9053\uff1a\u201c\u97e9\u9738\uff0c\u4e0b\u4e00\u9635\uff0c\u53c8\u8be5\u4f60\u4eec\u6d1b\u9633\u5c45\u7684\u4eba\u63a5\u6218\u4e86\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u2026\u2026\uff0c\u201d\u6c5f\u67ab\u5fc3\u4e2d\u6697\u6697\u9a82\u9053\uff1a\u201c\u4e3a\u8001\u4e0d\u5c0a\uff0c\u53cd\u590d\u65e0\u5e38\uff0c\u4f60\u800d\u8001\u5978\uff0c\u90a3\u5c31\u522b\u602a\u6211\u7b11\u91cc\u85cf\u5200\u4e86\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e2a\uff0c\u90a3\u4e2a\uff0c\u4e8e\u4e8b\u65e0\u8865\uff0c\u201d\u4e01\u897f\u5c71\u9053\uff1a\u201c\u8001\u592b\u53ea\u662f\u89c9\u7740\u6709\u4e9b\u2026\u2026\u201d \u3000\u3000\u201c\u662f\u4e86\uff01\u4e01\u8001\u51c6\u5907\u6bc1\u5f03\u8bfa\u8a00\uff1f\u201d\u6c5f\u67ab\u63a5\u9053\uff1a\u201c\u54b1\u4eec\u65e9\u5df2\u8bf4\u597d\u7684\uff0c\u5728\u4e0b\u63a5\u8fc7\u4e00\u5200\uff0c\u4ee5\u540e\uff0c\u6709\u4e01\u8001\u548c\u51e0\u4f4d\u62a4\u6cd5\u5e94\u4ed8\uff0c\u8a00\u72b9\u5728\u8033\uff0c\u4e01\u8001\u5c31\u5fd8\u60c5\u4e86\u4e48\uff1f\u201d \u3000\u3000\u201c\u80e1\u8bf4\u2026\u2026\u201d\u4e01\u897f\u5c71\u6012\u9053\uff1a\u201c\u8001\u592b\u53ea\u662f\u89c9\u7740\u5947\u602a\uff0c\u4ee5\u4f60\u90a3\u70b9\u5fae\u7c73\u4e4b\u6280\uff0c\u600e\u4f1a\u63a5\u4e0b\u53d1\u5200\uff0c\u6beb\u53d1\u65e0\u4f24\uff0c\u5218\u3001\u5468\u4e24\u4f4d\u62a4\u6cd5\uff0c\u6b66\u529f\u80dc\u4f60\u80dc\u5341\u500d\uff0c\u7adf\u7136\u4e00\u6b7b\u4e00\u4f24\uff0c\u8001\u592b\u5fc3\u4e2d\uff0c\u81ea\u7136\u8981\u6709\u4e9b\u6000\u7591\u4e86\uff01\u201d \u3000\u3000\u6885\u82b1\u770b\u4ed6\u4eec\u7a9d\u91cc\u53cd\uff0c\u81ea\u8d77\u4e89\u6267\uff0c\u4e5f\u793a\u610f\u6682\u505c\u884c\u52a8\uff0c\u51b7\u773c\u65c1\u89c2\uff0c\u6700\u597d\u662f\u4ed6\u4eec\u81ea\u5df1\u6253\u8d77\u6765\uff0c\u4ee5\u6536\u6e14\u4eba\u4e4b\u5229\u3002 \u3000\u3000\u201c\u4e01\u8001\u6000\u7591\u4ec0\u4e48\uff1f\u97e9\u67d0\u4eba\u63a5\u4e0b\u4e00\u5200\uff0c\u9664\u4e86\u6211\u672c\u8eab\u7684\u6280\u827a\u4e4b\u827a\u5916\uff0c\u8fd8\u8fd0\u7528\u4e86\u6211\u7684\u667a\u6167\uff0c\u5c01\u4f4f\u4e86\u4ed6\u5200\u52bf\u7684\u53d8\u5316\uff0c\u4ee5\u4e01\u8001\u4e4b\u80fd\uff0c\u53ea\u8981\u7528\u5fc3\u60f3\u4e00\u4e0b\uff0c\u5e94\u8be5\u4e0d\u96be\u660e\u767d\u4e86\u3002\u201d \u3000\u3000\u4e01\u897f\u5c71\u60f3\u4e86\u4e00\u4e0b\uff0c\u5012\u4e5f\u4e0d\u9519\uff0c\u5982\u679c\u5468\u6e90\u7684\u516b\u5366\u724c\u80fd\u65e9\u4e00\u70b9\u63a8\u51fa\uff0c\u6216\u662f\u5411\u5de6\u79fb\u52a8\u534a\u5c3a\uff0c\u5f2f\u6708\u5200\u5c31\u65e0\u6cd5\u65a9\u5230\u4ed6\u7684\u5de6\u81c2\u4e86\u3002 \u3000\u3000\u4f46\u5bf9\u65b9\u8fd9\u62fc\u53bb\u6027\u547d\u7684\u6253\u6cd5\uff0c\u51e0\u4e4e\u662f\u4e00\u5b9a\u53ef\u4ee5\u627e\u51fa\u654c\u4eba\u7684\u7834\u7efd\uff0c\u56e0\u4e3a\uff0c\u4efb\u4f55\u4e00\u62db\u6b66\u529f\uff0c\u90fd\u65e0\u6cd5\u5b8c\u7f8e\u65e0\u6687\u3002 \u3000\u3000\u4f46\u95fb\u6c5f\u67ab\u63a5\u9053\uff1a\u201c\u5728\u4e0b\u518d\u63d0\u9192\u4e01\u8001\u4e00\u58f0\uff0c\u8fd9\u4e9b\u6740\u624b\uff0c\u80fd\u63d0\u4f4f\u4e00\u53e3\u771f\u6c14\uff0c\u4f7f\u751f\u547d\u4e2d\u7684\u6f5c\u80fd\uff0c\u4f5c\u4e86\u9ad8\u5ea6\u7684\u53d1\u6325\uff0c\u91cd\u4f24\u5782\u6b7b\u4e4b\u9645\uff0c\u4ecd\u6709\u53cd\u51fb\u4e4b\u80fd\uff0c\u7a0d\u4e00\u5927\u610f\uff0c\u5c31\u80fd\u4e0a\u5f53\uff0c\u4e01\u8001\u548c\u62a4\u6cd5\uff0c\u4ea6\u8bf7\u5343\u4e07\u5c0f\u5fc3\u3002\u201d \u3000\u3000\u4e01\u897f\u5c71\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u8bf4\u7684\u6709\u7406\uff0c\u4f60\u662f\u4e2a\u5f88\u7cbe\u660e\u7684\u4eba\uff0c\u4e0d\u8fc7\uff0c\u8fd9\u4e00\u9635\u8fd8\u662f\u8981\u6d1b\u9633\u5c45\u7684\u4eba\u51fa\u624b\uff0c\u8001\u592b\u518d\u89c2\u5bdf\u4e00\u4e0b\u4ed6\u4eec\u7684\u5200\u6cd5\u3002\u201d \u3000\u3000\u6c5f\u67ab\u56de\u987e\u4e86\u9093\u98de\u4e00\u773c\uff0c\u60f3\u8bf7\u793a\u4e00\u4e0b\uff0c\u4ed6\u89c9\u7740\u8fd9\u4e00\u9635\u72ec\u65ad\u72ec\u884c\uff0c\u6709\u4e9b\u55a7\u5bbe\u593a\u4e3b\u3002 \u3000\u3000\u90a3\u77e5\u9093\u98de\u4e00\u8f6c\u5934\uff0c\u770b\u89c1\u88c5\u4f5c\u6ca1\u770b\u89c1\uff0c\u6545\u610f\u56de\u907f\u4e86\u6c5f\u67ab\u7684\u76ee\u5149\u3002 \u3000\u3000\u6c5f\u67ab\u6697\u6697\u4e00\u7b11\uff0c\u5fd6\u9053\uff1a\u201c\u4e5f\u597d\uff01\u4f60\u65e2\u6709\u5fc3\u56de\u907f\uff0c\u6211\u5c31\u653e\u624b\u5927\u5e72\u4e00\u573a\u4e86\u3002\u201d \u3000\u3000\u5fc3\u610f\u6697\u5b9a\uff0c\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4e01\u8001\u65e2\u662f\u5fc3\u4e2d\u6000\u7591\uff0c\u501f\u53e3\u6bc1\u7ea6\uff0c\u665a\u8f88\u4eec\u767e\u65e5\u83ab\u8fa9\uff0c\u53ea\u597d\u8ba4\u4e86\uff0c\u73b0\u5728\uff0c\u8bf7\u4e01\u8001\u5429\u5490\u6e05\u695a\uff0c\u8981\u665a\u8f88\u4eec\u5982\u4f55\u4f5c\u4e3a\uff1f\u97e9\u67d0\u4eba\u8fd9\u53a2\u6d17\u8033\u606d\u542c\u4e86\uff01\u201d \u3000\u3000\u8fd9\u756a\u8bdd\u8f6f\u91cc\u5e26\u523a\uff0c\u542c\u5f97\u4e01\u897f\u5c71\u5927\u611f\u7a9d\u56ca\uff0c\u4f46\u6c5f\u67ab\u5360\u4e86\u4e00\u4e2a\u7406\u5b57\uff0c\u800c\u4e14\u5f3a\u654c\u5f53\u524d\uff0c\u4e5f\u4e0d\u662f\u7ffb\u8138\u7684\u65f6\u523b\uff0c\u5f3a\u6309\u4e0b\u5fc3\u5934\u6012\u706b\uff0c\u9053\uff1a\u201c\u4e0d\u8981\u7f57\u7f57\u779f\u55df\uff0c\u4f60\u4eec\u518d\u63a5\u4e00\u9635\uff0c\u4ee5\u540e\u7684\u4e8b\uff0c\u7531\u8001\u592b\u63a5\u4e0b\uff0c\u4e0d\u8fc7\uff0c\u8001\u592b\u5e26\u7684\u4eba\u624b\u4e0d\u591a\uff0c\u5982\u82e5\u5bf9\u65b9\u4e0b\u4e00\u9635\u5c55\u5f00\u7fa4\u653b\uff0c\u5927\u5bb6\u4e5f\u53ea\u6709\u5168\u90e8\u536f\u4e0a\u5e72\u4e86\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u4e01\u8001\u5429\u5490\u4e0b\u6765\u4e86\uff0c\u97e9\u67d0\u4eba\u53ea\u6709\u9075\u4ece\u3002\u201d\u7f13\u6b65\u800c\u51fa\uff0c\u63a5\u9053\uff1a\u201c\u6885\u82b1\u59d1\u5a18\uff0c\u97e9\u67d0\u4eba\u518d\u63a5\u4e00\u9635\uff01\u201d \u3000\u3000\u201c\u97e9\u9738\uff0c\u201d\u6885\u82b1\u795e\u60c5\u8083\u715e\u5730\u9053\uff1a\u201c\u4eba\u8d35\u81ea\u77e5\uff0c\u4f60\u8be5\u660e\u767d\u89c1\u597d\u5c31\u6536\u7684\u9053\u7406\uff0c\u4f60\u5df2\u7ecf\u9003\u8fc7\u4e86\u4e00\u6b21\u52ab\u96be\uff0c\u96be\u9053\u975e\u8981\u6e85\u8840\u5385\u5802\uff0c\u4f60\u624d\u7518\u5fc3\u5417\uff1f\u201d \u3000\u3000\u201c\u5509\uff01\u6ca1\u6709\u6cd5\u5b50\u554a\uff01\u6211\u5f53\u4e86\u6d1b\u9633\u5c45\u8fd9\u4e2a\u526f\u603b\u7ba1\uff0c\u603b\u5f97\u542c\u547d\u884c\u4e8b\uff0c\u201d\u97e9\u9738\u9053\uff1a\u201c\u5c31\u7b97\u4eba\u751f\u5982\u620f\u5427\uff01\u4e5f\u5f97\u5531\u4ec0\u4e48\u50cf\u4ec0\u4e48\u5440\uff01\u201d \u3000\u3000\u4ed6\u6234\u7684\u9762\u5177\uff0c\u5f62\u8c61\u51b7\u5389\uff0c\u662f\u5c5e\u4e8e\u90a3\u4f7f\u4eba\u754f\u60e7\u3001\u8ba8\u538c\u7684\u4e00\u5f62\uff0c\u8fd9\u756a\u5145\u6ee1\u65e0\u5948\u7684\u8a00\u8bed\uff0c\u867d\u662f\u59d4\u5a49\uff0c\u5374\u662f\u4e00\u70b9\u4e5f\u4e0d\u80fd\u535a\u5f97\u540c\u60c5\u3002\u6885\u82b1\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u81ea\u5df1\u627e\u6b7b\uff0c\u90a3\u5c31\u602a\u4e0d\u5f97\u6211\u4e86\uff0c\u83f1\u82b1\uff0c\u53bb\u6740\u4e86\u4ed6\uff0c\u4e0d\u7528\u624b\u4e0b\u7559\u60c5\u3002\u201d \u3000\u3000\u6d3e\u4e00\u4e2a\u82b1\u5a62\u51fa\u6218\uff0c\u5012\u662f\u5b8c\u5168\u51fa\u4e86\u4eba\u7684\u610f\u5916\u3002 \u3000\u3000\u83f1\u82b1\u5e94\u4e86\u4e00\u58f0\uff0c\u98de\u8eab\u800c\u51fa\uff0c\u5c31\u5728\u8eab\u5f62\u98de\u8dc3\u7684\u540c\u65f6\uff0c\u53cc\u5200\u5df2\u7136\u51fa\u9798\uff0c\u4e24\u9053\u5bd2\u8292\uff0c\u540c\u65f6\u653b\u51fa\uff0c\u5f62\u5982\u5229\u526a\u3002 \u3000\u3000\u6c5f\u67ab\u624b\u4e2d\u4ecd\u7136\u63e1\u7740\u67c4\u5f2f\u6708\u5200\uff0c\u5f2f\u5200\u6447\u52a8\uff0c\u6321\u5f00\u4e86\u53cc\u5200\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u83f1\u82b1\uff0c\u6211\u662f\u526f\u603b\u7ba1\u7684\u8eab\u4efd\uff0c\u4f60\u8fd9\u6837\u4ee5\u4e0b\u72af\u4e0a\u2026\u2026\u201d \u3000\u3000\u201c\u72af\u4f60\u4e2a\u5927\u5934\u9b3c\u5440\uff01\u4f60\u53bb\u6b7b\u2026\u2026\u201d\u83f1\u82b1\u53cc\u5200\u4e00\u7d27\uff0c\u653b\u52bf\u66f4\u89c1\u731b\u5389\uff0c\u5200\u5149\u5e7b\u8d77\u4e00\u7247\u5bd2\u8292\uff0c\u76f4\u538b\u4e0b\u6765\u3002 \u3000\u3000\u6c5f\u67ab\u88ab\u56f0\u5728\u4e00\u7247\u5200\u5149\u4e4b\u4e2d\uff0c\u770b\u4e0a\u53bb\uff0c\u5904\u5883\u5341\u5206\u51f6\u9669\uff0c\u80e1\u840d\u3001\u6bb5\u4e5d\u5f62\u8272\u7d27\u5f20\uff0c\u624b\u63e1\u5251\u628a\u3001\u5200\u67c4\uff0c\u968f\u65f6\u51c6\u5907\u51b2\u4e0a\u53bb\uff0c\u89e3\u6551\u6c5f\u67ab\u4e4b\u5371\u3002 \u3000\u3000\u4f46\u4e03\u5b9d\u548c\u5c1a\u5374\u662f\u4e00\u7247\u5b81\u9759\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u6c5f\u67ab\u7684\u6b66\u529f\u5982\u4f55\uff1f\u5927\u548c\u5c1a\u4e5f\u4e0d\u662f\u771f\u7684\u77e5\u9053\uff0c\u53ea\u4e0d\u8fc7\u4ed6\u5916\u62d9\u5185\u79c0\uff0c\u5934\u8111\u614e\u5bc6\uff0c\u6b66\u529f\u9020\u8be3\uff0c\u4e5f\u9ad8\u8fc7\u4e86\u80e1\u840d\u3001\u6bb5\u4e5d\u751a\u591a\uff0c\u76ee\u5149\u9510\u5229\uff0c\u6d1e\u67e5\u7ec6\u5fae\uff0c\u53d1\u89c9\u4e86\u6c5f\u67ab\u9632\u5b88\u7684\u5708\u5b50\u867d\u5c0f\uff0c\u4f46\u8fd0\u5200\u4ece\u5bb9\uff0c\u614c\u800c\u4e0d\u4e71\uff0c\u660e\u660e\u9732\u51fa\u4e86\u5f88\u5927\u7684\u7834\u7efd\uff0c\u4f46\u5374\u603b\u662f\u80fd\u53ca\u65f6\u5835\u585e\u2026\u2026\u3002 \u3000\u3000\u90a3\u662f\u6709\u610f\u7684\u88c5\u4f5c\uff0c\u56f0\u96be\u7684\u7a0b\u5e8f\uff0c\u6bd4\u4e00\u51fb\u53d6\u547d\uff0c\u8981\u96be\u4e0a\u5341\u500d\u3002 \u3000\u3000\u56e0\u4e3a\uff0c\u6c5f\u67ab\u8981\u8499\u9a97\u7684\u4eba\uff0c\u662f\u5927\u884c\u5bb6\uff0c\u4e01\u897f\u5c71\u548c\u9093\u98de\u53ca\u6885\u82b1\u59d1\u5a18\u3002 \u3000\u3000\u8fd9\u662f\u4e00\u573a\u5f88\u5947\u5999\u7684\u6253\u6597\uff0c\u6c5f\u67ab\u5728\u9669\u8c61\u73af\u751f\u4e2d\uff0c\u63a5\u4e0b\u4e86\u83f1\u82b1\u4e00\u767e\u591a\u5200\u3002 \u3000\u3000\u5728\u83f1\u82b1\u7684\u611f\u89c9\u4e2d\uff0c\u5200\u5200\u90fd\u53ef\u4ee5\u6740\u4e86\u6c5f\u67ab\uff0c\u4f46\u603b\u662f\u5dee\u90a3\u4e48\u4e00\u70b9\u70b9\uff0c\u88ab\u6c5f\u67ab\u53ca\u65f6\u7528\u5200\u67b6\u4f4f\uff0c\u6216\u662f\u95ea\u907f\u5f00\u53bb\u3002 \u3000\u3000\u4e01\u897f\u5c71\u770b\u7684\u76b1\u8d77\u7709\u5934\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u4f4f\u624b\u3002\u201d \u3000\u3000\u58f0\u97f3\u5341\u5206\u5b8f\u5927\uff0c\u4f46\u83f1\u82b1\u5374\u604d\u82e5\u672a\u95fb\uff0c\u53cc\u5200\u653b\u52bf\uff0c\u66f4\u89c1\u51f6\u6076\uff0c\u5200\u5200\u653b\u52bf\u53d6\u5411\u6c5f\u67ab\u8981\u5bb3\u3002 \u3000\u3000\u201c\u83f1\u82b1\uff0c\u9000\u4e0b\u6765\uff0c\u65f6\u95f4\u5f88\u957f\uff0c\u4e0d\u7528\u6025\u5728\u4e00\u65f6\u3002\u201d\u6885\u82b1\u7684\u58f0\u97f3\u5f88\u5c0f\uff0c\u4f46\u83f1\u82b1\u4e00\u6536\u53cc\u5200\uff0c\u9000\u4e86\u4e24\u6b65\uff0c\u770b\u770b\u6c5f\u67ab\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u97e9\u9738\uff0c\u4f60\u5e73\u65f6\u5f88\u795e\u6c14\u7aef\u7740\u526f\u603b\u7ba1\u7684\u67b6\u5b50\uff0c\u5230\u5904\u547c\u6765\u559d\u53bb\uff0c\u6211\u8fd8\u8ba4\u4e3a\u4f60\u771f\u6709\u51e0\u4e0b\u5b50\uff0c\u60f3\u4e0d\u5230\u7adf\u662f\u4e2a\u7ee3\u82b1\u8377\u5305\uff0c\u4e2d\u770b\u4e0b\u4e2d\u5403\u554a\uff01\u201d \u3000\u3000\u6c5f\u67ab\u6728\u7136\u7684\u8138\u4e0a\uff0c\u4e03\u60c5\u4e0d\u52a8\uff0c\u770b\u4e0d\u51fa\u83f1\u82b1\u8fd9\u756a\u9510\u5229\u7684\u6fc0\u8bbd\u4e4b\u8a00\uff0c\u662f\u5426\u4f24\u5230\u4e86\u4ed6\u7684\u5fc3\uff1f \u3000\u3000\u5176\u5b9e\uff0c\u6c5f\u67ab\u5fc3\u4e2d\u6b63\u81ea\u6697\u7b11\uff0c\u5bf9\u81ea\u5df1\u8fd9\u756a\u88c5\u4f5c\uff0c\u751a\u611f\u6ee1\u610f\uff0c\u5fd6\u9053\uff1a\u201c\u9b3c\u4e2b\u5934\uff0c\u6211\u6c5f\u67ab\u90a3\u91cc\u5f97\u7f6a\u4f60\u4e86\uff0c\u4f60\u7adf\u8fd9\u4e48\u6068\u6211\uff0c\u54b1\u4eec\u8bb0\u5728\u5e10\u672c\u4e0a\uff0c\u6709\u673a\u4f1a\u4e00\u5b9a\u8981\u597d\u597d\u6349\u5f04\u4f60\u4e00\u756a\u624d\u884c\u2026\u2026\u201d \u3000\u3000\u201c\u97e9\u526f\u603b\u7ba1\uff0c\u4f60\u8fd8\u597d\u5427\uff1f\u201d\u4e01\u897f\u5c71\u7684\u58f0\u97f3\u5f88\u51b7\u6f20\uff0c\u53eb\u4eba\u5f04\u4e0d\u6e05\u662f\u5173\u5fc3\uff1f\u8fd8\u662f\u8bbd\u523a\uff1f \u3000\u3000\u201c\u4e01\u8001\u7684\u547d\u4ee4\uff0c\u97e9\u67d0\u4eba\u53ea\u6709\u5168\u529b\u4ee5\u8d74\uff0c\u662f\u751f\u662f\u6b7b\uff0c\u4e5f\u5c31\u53ea\u597d\u542c\u5929\u7531\u547d\u4e86\uff01\u201d \u3000\u3000\u4e01\u897f\u5c71\u501a\u8001\u5356\u8001\u5730\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u867d\u7136\u5e2e\u5fd9\u4e0d\u5927\uff0c\u672a\u6bd9\u4e00\u654c\uff0c\u4f46\u4f60\u8fd8\u6709\u52c7\u6c14\u51fa\u6218\uff0c\u201d\u76ee\u5149\u4e00\u63a0\u9093\u98de\u548c\u4e03\u5b9d\u548c\u5c1a\u7b49\uff0c\u8bdd\u950b\u5ffd\u8f6c\uff0c\u63a5\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u6211\u5fc3\u4e2d\u4ecd\u7136\u6709\u5f88\u591a\u7684\u7591\u8651\u672a\u5931\uff0c\u8fd9\u4ef6\u4e8b\uff0c\u6211\u4e00\u5b9a\u4f1a\u8c03\u67e5\u6e05\u695a\u3002\u201d \u3000\u3000\u201c\u662f\uff01\u4e01\u8001\u5fc3\u4e2d\u65e2\u6709\u7591\u8651\uff0c\u5e94\u8be5\u67e5\u4e2a\u6e05\u695a\u3002\u201d\u6c5f\u67ab\u56de\u7b54\u7684\u4e0d\u4ea2\u4e0d\u5351\uff0c\u82e5\u65e0\u5176\u4e8b\uff0c\u4e01\u897f\u5c71\u5fae\u5fae\u4e00\u6014\uff0c\u9053\uff1a\u201c\u5f88\u597d\uff01\u5f88\u597d\uff01\u5e0c\u671b\u4f60\u4eec\u90fd\u662f\u6e05\u767d\u7684\uff0c\u662f\u8001\u592b\u8fc7\u8651\u4e86\u3002\u201d \u3000\u3000\u6885\u82b1\u7a81\u7136\u51b7\u7b11\u4e00\u58f0\uff0c\u63a5\u9053\uff1a\u201c\u9093\u98de\u8001\u8fc8\u660f\u5eb8\uff0c\u97e9\u9738\u53ea\u662f\u4e2a\u4e0d\u5165\u6d41\u7684\u6c5f\u6e56\u4eba\u7269\uff0c\u5c31\u7b97\u4ed6\u4eec\u8bda\u5fc3\u5f52\u670d\uff0c\u6211\u4e5f\u4f1a\u628a\u4ed6\u4eec\u9000\u56de\u5929\u9a6c\u603b\u5802\uff0c\u6d1b\u9633\u5c45\u4e0d\u4f1a\u7528\u4ed6\u4eec\uff0c\u4f60\u7528\u4e0d\u7740\u62c5\u5fc3\u6211\u4eec\u4f1a\u62c9\u62e2\u4ed6\u4eec\uff01\u201d \u3000\u3000\u201c\u542c\u4f60\u7684\u53e3\u6c14\uff0c\u4f3c\u662f\u6311\u660e\u4e86\u8ddf\u5929\u9a6c\u603b\u5802\u4f5c\u5bf9\u4e86\uff0c\u201d\u4e86\u897f\u5c71\u9053\uff1a\u201c\u8fd9\u53ef\u662f\u8d35\u5bab\u4e3b\u7684\u610f\u601d\uff1f\u201d \u3000\u3000\u201c\u4f60\u4e0d\u662f\u5929\u9a6c\u603b\u5802\u7684\u957f\u8001\u5417\uff1f\u201d\u6885\u82b1\u8bf4\uff0c\u201c\u5e26\u4e86\u4e09\u540d\u62a4\u6cd5\uff0c\u660e\u76ee\u5f20\u80c6\u7684\u5e2e\u52a9\u9093\u98de\uff0c\u4e0d\u662f\u6446\u7740\u5411\u5185\u5bab\u6311\u6218\u5417\uff1f\u54fc\uff01\u63a9\u8033\u76d7\u94c3\uff0c\u4e00\u70b9\u4e5f\u6ca1\u6709\u7537\u5b50\u6c49\u7684\u6c14\u6168\uff01\u201d \u3000\u3000\u4e01\u897f\u5c71\u88ab\u9a82\u7684\u8001\u7751\u4e00\u70ed\uff0c\u9053\uff1a\u201c\u5c0f\u4e2b\u5934\u522b\u592a\u56a3\u5f20\uff0c\u4eca\u65e5\u80dc\u8d1f\u8fd8\u672a\u5b9a\u5c40\u2026\u2026\u201d \u3000\u3000\u201c\u80fd\u591f\u633a\u8eab\u4e00\u6218\u7684\uff0c\u53ea\u6709\u4f60\u4e00\u4e2a\u4eba\u4e86\uff0c\u201d\u6885\u82b1\u51b7\u7b11\u9053\uff1a\u201c\u52c9\u5f3a\u7b97\u4e0a\u90a3\u4e2a\u6d3b\u7740\u7684\u62a4\u6cd5\uff0c\u4e5f\u53ea\u80fd\u7b97\u4e0a\u534a\u4e2a\u4eba\uff0c\u6211\u968f\u4fbf\u6d3e\u4e2a\u4eba\uff0c\u5c31\u7b97\u6740\u4e0d\u4e86\u4ed6\uff0c\u4e5f\u53ef\u4ee5\u628a\u4ed6\u7f20\u4f4f\uff0c\u6d1b\u9633\u5c45\u4e2d\u8fd9\u6279\u4eba\uff0c\u4e0d\u8fc7\u662f\u804b\u5b50\u7684\u8033\u6735\uff0c\u662f\u4e2a\u914d\u642d\uff0c\u81ea\u4e0b\u7684\u60c5\u52bf\uff0c\u5df2\u4e0d\u662f\u4f17\u5be1\u60ac\u6b8a\u53ef\u4ee5\u6bd4\u55bb\uff0c\u4f60\u662f\u4e2a\u8001\u6c5f\u6e56\uff0c\u5e94\u8be5\u770b\u7684\u5f88\u6e05\u695a\uff0c\u8bc6\u65f6\u52a1\u7684\uff0c\u7acb\u523b\u64a4\u8d70\uff0c\u6211\u5141\u8bb8\u4f60\u628a\u9093\u98de\u3001\u97e9\u9738\u7b49\u4e00\u6279\u4eba\uff0c\u4e00\u8d77\u5e26\u8d70\uff0c\u600e\u4e48\u6837\uff1f\u8fd9\u662f\u7ed9\u4f60\u6700\u540e\u7684\u673a\u4f1a\u4e86\uff0c\u540c\u4e0d\u540c\u610f\u4e00\u53e5\u8bdd\uff1f\u6211\u5df2\u7ecf\u6ca1\u6709\u8010\u6027\u548c\u4f60\u78e8\u83c7\u4e0b\u53bb\u4e86\uff01\u201d \u3000\u3000\u5979\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u4f46\u53e3\u6c14\u8001\u7ec3\uff0c\u4f3c\u4e4e\u662f\u4e45\u5386\u6c5f\u6e56\u7684\u4eba\u7269\u3002 \u3000\u3000\u4e01\u897f\u5c71\u770b\u770b\u9093\u98de\u3001\u97e9\u9738\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4f4d\u542c\u5230\u4e86\u5417\uff1f\u6211\u60f3\u542c\u542c\u4e24\u4f4d\u7684\u610f\u89c1\uff1f\u201d \u3000\u3000\u9093\u98de\u7f13\u7f13\u7ad9\u8d77\u8eab\u5b50\uff0c\u5c31\u7b97\u4ed6\u88c5\u804b\u4f5c\u54d1\uff0c\u4f46\u8fd9\u7b49\u6307\u540d\u53eb\u59d3\u7684\u95ee\u8bdd\uff0c\u4e5f\u65e0\u6cd5\u518d\u88c5\u4f5c\u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u8fd9\u4e00\u9635\u4ed6\u51b7\u773c\u65c1\u89c2\uff0c\u4e5f\u5f88\u7528\u5fc3\u542c\u4e86\u53cc\u65b9\u7684\u8c08\u8bdd\uff0c\u5bf9\u60c5\u52bf\u53d1\u5c55\uff0c\u5341\u5206\u4e86\u89e3\uff0c\u4e01\u897f\u5c71\u867d\u662f\u5217\u540d\u6c5f\u6e56\u9ad8\u624b\u7684\u4eba\u7269\uff0c\u4f46\u72ec\u6728\u96be\u652f\u3002 \u3000\u3000\u4eca\u65e5\u4e4b\u5c40\uff0c\u80dc\u7b97\u6781\u5fae\uff0c\u6c5f\u67ab\u867d\u4f7f\u4eba\u83ab\u6d4b\u9ad8\u6df1\uff0c\u4f46\u9002\u624d\u5bf9\u83f1\u82b1\u4e00\u6218\uff0c\u53ea\u662f\u4e2a\u52c9\u5f3a\u81ea\u4fdd\u4e4b\u5c40\u3002 \u3000\u3000\u6d1b\u9633\u5c45\u4e2d\u7684\u9556\u624b\u3001\u4f8d\u5e94\uff0c\u90fd\u662f\u5185\u5bab\u4e00\u7cfb\u4e2d\u4eba\uff0c\u7b97\u8d77\u6765\uff0c\u90fd\u662f\u6885\u82b1\u7684\u52a9\u529b\uff0c\u654c\u5f3a\u6211\u5f31\uff0c\u4e00\u76ee\u4e86\u7136\u3002 \u3000\u3000\u6c5f\u67ab\u867d\u66fe\u8bf4\u8fc7\uff0c\u957f\u5b89\u57ce\u4e2d\uff0c\u53e6\u6709\u5965\u63f4\uff0c\u53ea\u53ef\u60dc\uff0c\u603b\u5802\u7b54\u5141\u6d3e\u4eba\u76f8\u52a9\u4e4b\u540e\uff0c\u672a\u518d\u548c\u4ed6\u4eec\u63a5\u89e6\uff0c\u8fd9\u4e2a\u65f6\u523b\uff0c\u672a\u4fbf\u627e\u4ed6\u4eec\u6765\uff0c\u7eb5\u4f7f\u627e\u4ed6\u4eec\u6765\u4e86\uff0c\u4e5f\u672a\u5fc5\u662f\u6885\u82b1\u4e4b\u654c\uff1f \u3000\u3000\u4ed6\u662f\u79ef\u5e74\u8001\u8d3c\uff0c\u76d8\u7b97\u8fc7\u80dc\u8d1f\u5f97\u5931\u4e4b\u540e\uff0c\u51b3\u5b9a\u4e86\u8d70\u4e3a\u4e0a\u7b56\uff0c\u4ed6\u8981\u4fdd\u7559\u4e0b\u97e9\u9738\u3001\u9f50\u56db\u7b49\u4e00\u6279\u5fc3\u8179\uff0c\u52a9\u4ed6\u7a81\u56f4\uff0c\u4eca\u591c\u51b3\u6218\uff0c\u7edd\u4e0d\u80fd\u727a\u7272\u8fd9\u4e9b\u4eba\uff0c\u53ea\u6709\u63a8\u51fa\u4e86\u4e01\u897f\u5c71\u548c\u4e00\u4f4d\u8fd8\u53d7\u4f24\u7684\u62a4\u6cd5\u6321\u707e\uff0c\u89e3\u5371\u4e86\u3002 \u3000\u3000\u5fc3\u5ff5\u5df2\u5b9a\uff0c\u624d\u4e00\u62b1\u62f3\uff0c\u9053\uff1a\u201c\u4e01\u8001\u4e0d\u803b\u4e0b\u95ee\uff0c\u9093\u67d0\u5c31\u6597\u80c6\u9648\u8a00\u4e86\uff0c\u4eca\u591c\u4e4b\u6218\uff0c\u552f\u4e01\u8001\u9a6c\u9996\u662f\u77bb\uff0c\u53ef\u6218\u5219\u6218\uff0c\u5982\u662f\u65e0\u6cd5\u81f4\u80dc\uff0c\u5219\u6682\u907f\u950b\u8292\uff0c\u4ea6\u65e0\u4e0d\u53ef\uff0c\u4ee5\u4e01\u8001\u8bc6\u89c1\u4e4b\u5e7f\uff0c\u5a01\u671b\u4e4b\u91cd\uff0c\u4efb\u4f55\u51b3\u5b9a\uff0c\u6211\u4eec\u90fd\u552f\u547d\u662f\u4ece\u3002\u201d \u3000\u3000\u8fd9\u756a\u8bdd\uff0c\u9664\u4e86\u8bcd\u610f\u5a49\u8f6c\u4e4b\u5916\uff0c\u5c31\u662f\u66ff\u4e01\u897f\u5c71\u53c8\u6234\u4e86\u4e00\u9876\u9ad8\u5e3d\u5b50\uff0c\u8bf4\u5230\u5185\u5bb9\uff0c\u5374\u662f\u8bf4\u4e86\u7b49\u4e8e\u6ca1\u8bf4\uff0c\u4e0d\u4f46\u6ca1\u6709\u4e00\u70b9\u4e3b\u89c1\uff0c\u8fde\u4e00\u70b9\u5efa\u8bae\u4e5f\u672a\u63d0\u51fa\u3002 \u3000\u3000\u4e01\u897f\u5c71\u70b9\u5934\u5fae\u7b11\u4e00\u4e0b\u4e4b\u540e\uff0c\u7a81\u7136\u5446\u4f4f\uff0c\u4ed6\u4e5f\u662f\u8001\u72d0\u72f8\u4e86\uff0c\u9676\u9189\u4e00\u4e0b\uff0c\u7acb\u523b\u6e05\u9192\uff0c\u9053\uff1a \u3000\u3000\u201c\u9093\u98de\uff0c\u6211\u542c\u4e0d\u61c2\u4f60\u7684\u610f\u601d\uff0c\u4f60\u53ef\u5426\u8bf4\u7684\u660e\u767d\u4e00\u4e9b\uff1f\u201d \u3000\u3000\u9093\u98de\u9053\uff1a\u201c\u4eca\u591c\u65e2\u6709\u4e01\u8001\u4e3b\u6301\u5168\u5c40\uff0c\u54b1\u4eec\u5168\u542c\u4f60\u7684\u4e86\uff01\u201d \u3000\u3000\u4e01\u897f\u5c71\u957f\u957f\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u9093\u98de\uff0c\u8001\u592b\u53ea\u662f\u6765\u6b64\u52a9\u62f3\uff0c\u53ef\u4e0d\u4fbf\u72ec\u65ad\u72ec\u884c\uff0c\u8be5\u8d70\u8be5\u6218\uff0c\u5f97\u8981\u4f60\u62ff\u4e3b\u610f\u4e86\u3002\u201d \u3000\u3000\u6c5f\u67ab\u5fc3\u4e2d\u5fd6\u9053\uff1a\u201c\u4e01\u897f\u5c71\u5206\u660e\u5df2\u6709\u602f\u6218\u4e4b\u610f\uff0c\u53ea\u662f\u62c9\u4e0d\u4e0b\u8fd9\u5f20\u8001\u8138\uff0c\u60f3\u501f\u9093\u98de\u4e4b\u53e3\uff0c\u627e\u4e2a\u53f0\u9636\u4e0b\u6765\uff0c\u4ed6\u662f\u5929\u9a6c\u603b\u5802\u4e2d\u957f\u8001\uff0c\u8eab\u4efd\u4e0d\u4f4e\uff0c\u4eca\u591c\u628a\u4ed6\u5751\u9677\u4e8e\u6b64\uff0c\u603b\u5802\u5f88\u96be\u5fcd\u4e0b\u8fd9\u53e3\u6c14\u4e86\u2026\u2026\u201d \u3000\u3000\u6b63\u60f3\u717d\u98ce\u70b9\u706b\uff0c\u5f15\u8bf1\u6885\u82b1\u4e0b\u4ee4\u51fa\u624b\uff0c\u90a3\u77e5\u6885\u82b1\u610f\u5df2\u52a8\u4e86\u6740\u673a\uff0c\u7a81\u7136\u4e00\u6325\u624b\uff0c\u8eab\u540e\u4e24\u4e2a\u9ed1\u8863\u6740\u624b\uff0c\u50cf\u4e24\u652f\u79bb\u5f26\u7ba1\u4e00\u822c\uff0c\u6fc0\u5c04\u800c\u51fa\uff0c\u51b2\u5411\u4e01\u897f\u5c71\u3002 \u3000\u3000\u53d1\u52a8\u7684\u7a81\u7136\u6765\u52bf\u5982\u7535\uff0c\u4e01\u897f\u5c71\u8fde\u8bdd\u4e5f\u6765\u4e0d\u53ca\u8bf4\u4e86\uff0c\u53cc\u638c\u9f50\u51fa\uff0c\u8fce\u51fb\u4e24\u4eba\u3002 \u3000\u3000\u4e3a\u4e86\u4fdd\u5168\u8001\u547d\uff0c\u4e01\u897f\u5c71\u662f\u5168\u529b\u51fa\u624b\uff0c\u53cc\u638c\u805a\u96c6\u4e86\u6bd5\u751f\u7684\u529f\u529b\uff0c\u679c\u7136\u662f\u5a01\u529b\u60ca\u4eba\uff0c\u4e24\u4e2a\u5411\u524d\u51b2\u6765\u7684\u9ed1\u8863\u4eba\uff0c\u88ab\u90a3\u80a1\u5f3a\u731b\u7684\u638c\u98ce\u51fb\u4e2d\uff0c\u540c\u65f6\u8dcc\u6454\u5728\u5730\u3002 \u3000\u3000\u4e01\u897f\u5c71\u4e00\u51fb\u5f97\u624b\uff0c\u5fc3\u795e\u5927\u5b9a\uff0c\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8001\u592b\u8fd8\u8ba4\u4e3a\u4ed6\u4eec\u94dc\u6253\u94c1\u94f8\u7684\u4eba\uff0c\u7b97\u80fd\u6321\u5f97\u4f4f\u5343\u94a7\u638c\u529b\u2026\u2026\u201d \u3000\u3000\u4f46\u89c1\u5bd2\u5149\u98de\u95ea\uff0c\u4e24\u67c4\u5f2f\u5200\uff0c\u75be\u5c04\u800c\u51fa\uff0c\u6765\u52bf\u5feb\u901f\u5df2\u6781\u3002 \u3000\u3000\u4e01\u897f\u5c71\u5fae\u5fae\u4e00\u7b11\uff0c\u53cc\u5200\u5df2\u900f\u80f8\u800c\u8fc7\u3002 \u3000\u3000\u6562\u60c5\u90a3\u5f2f\u5200\u4e4b\u540e\uff0c\u6709\u4e00\u6761\u6781\u7ec6\u7684\u7d22\u7ef3\uff0c\u7cfb\u5728\u4e24\u4eba\u7684\u624b\u8155\u4e4b\u4e0a\uff0c\u4ed6\u4eec\u5411\u524d\u98de\u51b2\u4e4b\u65f6\uff0c\u5df2\u5168\u529b\u8fd0\u5200\uff0c\u7528\u8eab\u4f53\u786c\u63a5\u4e0b\u638c\u529b\uff0c\u5378\u53bb\u638c\u4e0a\u7684\u52b2\u9053\uff0c\u5f2f\u5200\u624d\u8131\u624b\u98de\u51fa\u3002 \u3000\u3000\u5f53\u771f\u662f\u4ee5\u6027\u547d\u6362\u53d6\u8fd9\u8131\u624b\u4e00\u5200\u3002 \u3000\u3000\u4e01\u897f\u5c71\u4e0a\u534a\u4e2a\u8eab\u4f53\uff0c\u5df2\u88ab\u4e24\u67c4\u5f2f\u5200\u5207\u5f00\uff0c\u5c31\u7b97\u662f\u534e\u4f26\u91cd\u751f\uff0c\u4e5f\u65e0\u6cd5\u6551\u6d3b\u5730\u4e86\uff0c\u4f46\u4ed6\u5374\u4ed7\u4e00\u53e3\u771f\u5143\u4e4b\u6c14\uff0c\u652f\u6491\u7740\u8eab\u8eaf\u4e0d\u5012\uff0c\u5706\u7741\u7740\u53cc\u76ee\uff0c\u77aa\u7740\u6c5f\u67ab\u3002 \u3000\u3000\u4e5f\u8bb8\u4ed6\u7a81\u6709\u6240\u609f\uff0c\u53d1\u89c9\u4e86\u6c5f\u67ab\u624d\u662f\u771f\u6b63\u8eab\u6000\u7edd\u6280\u7684\u9ad8\u4eba\uff0c\u4ed6\u4e00\u76f4\u88ab\u73a9\u5f04\u4e8e\u638c\u80a1\u4e4b\u4e0a\uff0c\u4e5f\u8bb8\u4ed6\u5e0c\u671b\u6c5f\u67ab\u5bf9\u4ed6\u6709\u6240\u4ea4\u4ee3\u3002 \u3000\u3000\u201c\u4e01\u8001\uff0c\u6211\u8bf4\u8fc7\uff0c\u4ed6\u4eec\u5782\u6b7b\u4e4b\u524d\uff0c\u5b9a\u6709\u51cc\u5389\u7684\u53cd\u51fb\uff0c\u5341\u5206\u53ef\u6015\uff0c\u73b0\u5728\uff0c\u6211\u8981\u4fee\u6b63\u4e00\u4e0b\uff0c\u4ed6\u4eec\u662f\u6545\u610f\u8fce\u5411\u6b7b\u4ea1\uff0c\u4ee5\u5236\u9020\u6740\u4eba\u7684\u673a\u4f1a\uff0c\u4e01\u8001\uff0c\u4e24\u547d\u6362\u4e00\u547d\uff0c\u4ed6\u4eec\u4e5f\u88ab\u4f60\u9707\u788e\u5185\u8151\u800c\u6b7b\u4e86\u3002\u201d \u3000\u3000\u4e01\u897f\u5c71\u53cc\u76ee\u4e00\u95ed\uff0c\u5c38\u4f53\u683d\u5012\u3002 \u3000\u3000\u6885\u82b1\u4e24\u9053\u6e05\u6f88\uff0c\u51b7\u5389\u7684\u76ee\u5149\uff0c\u51dd\u6ce8\u7740\u6c5f\u67ab\uff0c\u9053\uff1a\u201c\u97e9\u9738\uff0c\u8fd8\u770b\u51fa\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4e9b\u6740\u624b\uff0c\u4e3a\u4ec0\u4e48\u4e00\u51fa\u624b\uff0c\u5c31\u5f62\u540c\u75af\u72c2\uff0c\u201d\u6c5f\u67ab\u7b54\u975e\u6240\u95ee\u5730\u8bf4\uff0c\u201c\u81ea\u7edd\u751f\u8def\uff0c\u53ea\u6c42\u51cc\u5389\u7684\u4e00\u51fb\uff0c\u5982\u662f\u4e00\u51fb\u4e0d\u4e2d\u5462\uff1f\u201d \u3000\u3000\u201c\u6211\u4e5f\u662f\u89c9\u7740\u5947\u602a\uff0c\u4f60\u600e\u4e48\u80fd\u907f\u5f00\u4ed6\u4eec\u7684\u4e00\u5200\uff0c\u800c\u65e0\u635f\u4f24\uff1f \u3000\u3000\u2026\u2026\u201d\u6885\u82b1\u9053\uff1a\u201c\u544a\u8bc9\u6211\uff0c\u6211\u5c31\u89e3\u7b54\u4f60\u5fc3\u4e2d\u7684\u7591\u95ee\u3002\u201d \u3000\u3000\u6c5f\u67ab\u53f9\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4e5f\u8bb8\u6211\u51fa\u624b\u6c14\u52bf\u8f6f\u5f31\uff0c\u6ca1\u6709\u8fd8\u51fb\uff0c\u53ea\u6c42\u81ea\u4fdd\uff0c\u5168\u91c7\u5b88\u52bf\uff0c\u4ed6\u4eec\u611f\u53d7\u4e0d\u5230\u5a01\u534f\uff0c\u6d88\u53bb\u4e86\u8a93\u4e0d\u4e24\u7acb\u7684\u611f\u89c9\uff0c\u5200\u52bf\u5c31\u4e0d\u90a3\u4e48\u51f6\u72e0\u4e86\u3002\u201d \u3000\u3000\u6885\u82b1\u76b1\u8d77\u67f3\u7709\u513f\uff0c\u60f3\u4e86\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u4e5f\u8bb8\u662f\u6709\u4e9b\u9053\u7406\uff0c\u4f46\u4e0d\u4f1a\u5168\u7136\u5982\u6b64\uff0c\u4f60\u7684\u9632\u5b88\u7d27\u4e25\uff0c\u4ed6\u4eec\u770b\u4e0d\u51fa\u640f\u547d\u4e00\u51fb\u7684\u673a\u4f1a\u3001\u7834\u7efd\uff0c\u65e0\u6cd5\u5168\u529b\u51fa\u624b\u2026\u2026\u201d \u3000\u3000\u201c\u4e5f\u53ef\u80fd\u662f\u6211\u624b\u4e2d\u501f\u7528\u7684\u8fd9\u67c4\u5f2f\u5200\uff0c\u662f\u4ed6\u4eec\u5e38\u7528\u7684\u5175\u5203\uff0c\u7231\u5c4b\u53ca\u4e4c\uff0c\u624b\u4e0b\u7559\u60c5\u2026\u2026\u201d \u3000\u3000\u201c\u4ed6\u4eec\u4e0d\u4f1a\u624b\u4e0b\u7559\u60c5\u7684\uff0c\u201d\u6885\u82b1\u63a5\u9053\uff1a\u201c\u97e9\u9738\uff0c\u6211\u6709\u4e9b\u4f30\u4e0d\u900f\u4f60\u4e86\uff0c\u544a\u8bc9\u6211\uff0c\u613f\u4e0d\u613f\u610f\u7559\u4e0b\u6765\uff1f\u201d \u3000\u3000\u201c\u59d1\u5a18\u7684\u610f\u601d\u662f\u2014\u2014\u201d \u3000\u3000\u201c\u7559\u5728\u6d1b\u9633\u5c45\uff0c\u8fd8\u4f5c\u4f60\u7684\u526f\u603b\u7ba1\uff0c\u201d\u6885\u82b1\u8bf4\uff1a\u201c\u6211\u7ed9\u4f60\u7684\u6743\u9650\uff0c\u7edd\u4e0d\u4f1a\u4f4e\u4e8e\u9093\u98de\u3002\u201d \u3000\u3000\u8bf4\u7684\u5f88\u660e\u767d\u4e86\uff0c\u9093\u98de\u8fd9\u4e2a\u603b\u7ba1\uff0c\u5df2\u88ab\u5f00\u9769\uff0c\u4f46\u5374\u628a\u6c5f\u67ab\u7559\u4e0b\u6765\u3002 \u3000\u3000\u6c5f\u67ab\u56de\u5934\u770b\u53bb\uff0c\u53ea\u89c1\u9093\u98de\u77aa\u7740\u4e00\u53cc\u95ea\u52a8\u7eff\u5149\u7684\u9b54\u773c\uff0c\u770b\u7740\u81ea\u5df1\uff0c\u5fc3\u4e2d\u6697\u81ea\u76d8\u7b97\u9053\uff1a\u201c\u5929\u9a6c\u5802\u5927\u6982\u8ba4\u4e3a\u4e01\u897f\u5c71\u548c\u4e09\u4e2a\u62a4\u6cd5\uff0c\u8db3\u4ee5\u6297\u62d2\u5f3a\u654c\uff0c\u6682\u65f6\u4e0d\u4f1a\u518d\u6709\u63f4\u624b\u8d76\u6765\uff0c\u4eca\u591c\u5982\u8981\u4fdd\u5168\u9093\u98de\uff0c\u6050\u5c06\u653e\u624b\u4e00\u6218\uff0c\u4ed6\u4eec\u5bf9\u6211\u5df2\u7136\u52a8\u7591\uff0c\u6253\u80dc\u4e86\u8fd9\u4e00\u6218\uff0c\u52bf\u5fc5\u8981\u5f15\u8d77\u4ed6\u4eec\u5bf9\u6211\u7684\u6ce8\u610f\uff0c\u6df7\u5165\u6d1b\u9633\u5c45\u8fd9\u756a\u5fc3\u8840\uff0c\u6050\u5c06\u5c3d\u4ed8\u6d41\u6c34\uff0c\u5982\u4e0d\u7406\u9093\u98de\u7684\u751f\u6b7b\uff0c\u6295\u5165\u5185\u5bab\u4e00\u7cfb\uff0c\u5929\u9a6c\u603b\u5802\u5931\u53bb\u4e86\u8fd9\u4e2a\u636e\u70b9\u4e4b\u540e\uff0c\u8bf4\u4e0d\u5b9a\u4f1a\u5fcd\u4e0b\u8fd9\u53e3\u6028\u6c14\uff0c\u90a3\u5c31\u4e0d\u77e5\u9053\u8981\u62d6\u5ef6\u5230\u51e0\u65f6\uff0c\u624d\u80fd\u518d\u627e\u5230\u5f15\u8d77\u53cc\u65b9\u706b\u62fc\u7684\u673a\u4f1a\u3002\u201d \u3000\u3000\u8fd9\u662f\u4e2a\u975e\u5e38\u56f0\u96be\u7684\u51b3\u5b9a\uff0c\u6c5f\u67ab\u6c89\u601d\u4e86\u826f\u4e45\uff0c\u4ecd\u65e0\u6cd5\u4f5c\u51fa\u51b3\u5b9a\u3002 \u3000\u3000\u4f46\u95fb\u6885\u82b1\u8bf4\u9053\uff1a\u201c\u97e9\u9738\uff0c\u4f60\u5bf9\u9093\u98de\u7684\u4e00\u7247\u611a\u5fe0\uff0c\u4e5f\u8be5\u6e05\u9192\u4e00\u4e0b\u4e86\uff0c\u4ed6\u4e0d\u662f\u771f\u7684\u5bf9\u4f60\u4e86\uff0c\u521a\u624d\u8fd8\u8981\u4f60\u4ee5\u8eab\u6d89\u9669\uff0c\u96be\u9053\u4f60\u8fd8\u4e0d\u89c9\u609f\u4e48\uff1f\u201d \u3000\u3000\u9093\u98de\u9053\uff1a\u201c\u4e8b\u5173\u5404\u4f4d\u7684\u751f\u6b7b\uff0c\u90a3\u4e5f\u662f\u6ca1\u6709\u529e\u6cd5\u7684\u4e8b\uff0c\u9093\u67d0\u4eba\u4e0d\u76f8\u4fe1\u4f60\u6885\u82b1\u59d1\u5a18\u6740\u4e86\u97e9\u9738\u4e4b\u540e\uff0c\u4f1a\u653e\u8fc7\u8001\u592b\u3002\u201d \u3000\u3000\u201c\u5bf9\uff01\u4f60\u662f\u7978\u9996\uff0c\u975e\u6b7b\u4e0d\u53ef\uff0c\u201d\u6885\u82b1\u51b7\u5389\u5730\u8bf4\uff1a\u201c\u97e9\u9738\uff0c\u4f60\u5982\u62d6\u5ef6\u4e0d\u4f5c\u51b3\u5b9a\uff0c\u90a3\u5c31\u8fde\u4f60\u4e5f\u6740\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u4e0d\u80fd\u80cc\u53db\u603b\u7ba1\u3002\u201d\u6c5f\u67ab\u7ec8\u4f5c\u4e86\u51b3\u5b9a\u3002 \u3000\u3000\u201c\u597d\uff01\u60f3\u4e0d\u5230\u97e9\u526f\u603b\u7ba1\uff0c\u8fd8\u662f\u4e2a\u6ee1\u6000\u5fe0\u4e49\u7684\u4eba\u3002\u201d\u58f0\u97f3\u5a07\u751c\uff0c\u6e05\u8106\u52a8\u4eba\u3002 \u3000\u3000\u62ac\u5934\u770b\u53bb\uff0c\u53ea\u89c1\u9752\u51e4\u5728\u738b\u5b37\u3001\u674e\u5b37\uff0c\u5de6\u53f3\u62a4\u4ece\u4e4b\u4e0b\uff0c\u7f13\u6b65\u884c\u5165\u5385\u5802\u3002 \u3000\u3000\u201c\u662f\u4f60\uff0c\u9752\u51e4\u2026\u2026\u201d\u6885\u82b1\u6115\u7136\u8272\u53d8\u3002 \u3000\u3000\u201c\u662f\u6211\uff0c\u6885\u82b1\u59d1\u5a18\u662f\u4e0d\u662f\u6709\u4e9b\u610f\u5916\uff1f\u201d\u9752\u51e4\u6253\u65ad\u4e86\u6885\u82b1\u7684\u8bdd\uff0c\u63a5\u9053\uff1a\u201c\u4e09\u8db3\u9f0e\u7acb\uff0c\u5404\u6709\u4e13\u53f8\uff0c\u4fdd\u6301\u4e86\u6d1b\u9633\u5c45\u7684\u548c\u5e73\u5c40\u9762\uff0c\u6240\u4ee5\uff0c\u6211\u4e0d\u559c\u6b22\u88ab\u4eba\u7834\u574f\uff0c\u4f55\u51b5\uff0c\u8fd9\u91cc\u662f\u65e5\u8fdb\u6597\u91d1\u7684\u82b1\u56fd\u4e50\u56ed\uff0c\u4e00\u65e6\u9020\u6210\u8840\u8165\u6740\u622e\uff0c\u53ef\u80fd\u4f7f\u8fd9\u91cc\u53d8\u5f97\u95e8\u53ef\u7f57\u96c0\uff0c\u2026\u2026\u201d\u4e3e\u624b\u4e00\u62db\uff0c\u6d1b\u9633\u6402\u7684\u4f8d\u5e94\u9886\u73ed\u9ad8\u5927\u6597\uff0c\u5e26\u7740\u516d\u4e2a\u9ed1\u8863\u4eba\u51b3\u6b65\u800c\u5165\u3002 \u3000\u3000\u201c\u628a\u8fd9\u4e9b\u5c38\u4f53\u62ac\u8d70\uff0c\u63a9\u57cb\u8d77\u6765\uff0c\u4e0d\u80fd\u7559\u4e0b\u75d5\u8ff9\uff0c\u4e0d\u8bb8\u4f20\u51fa\u98ce\u58f0\u3002\u201d \u3000\u3000\u9ad8\u5927\u6597\u5e94\u4e86\u4e00\u58f0\uff0c\u7acb\u523b\u52a8\u624b\uff0c\u62ac\u8d70\u4e86\u5385\u4e2d\u5c38\u4f53\u3002 \u3000\u3000\u201c\u6885\u82b1\u59d0\u59d0\uff0c\u201d\u9752\u51e4\u5429\u5490\u8fc7\u9ad8\u5927\u6597\u540e\uff0c\u8f6c\u671b\u7740\u6885\u82b1\uff0c\u63a5\u9053\uff1a\u201c\u5c0f\u59b9\u7edd\u65e0\u548c\u4f60\u4e3a\u654c\u4e4b\u5fc3\u4f46\u6d1b\u9633\u5c45\u8fd9\u4e2a\u5730\u65b9\uff0c\u82b1\u4e86\u65e0\u6570\u540d\u5320\u7684\u5fc3\u8840\uff0c\u6295\u5165\u4e86\u5927\u6279\u7684\u8d22\u7269\uff0c\u4e00\u65e6\u6bc1\u53bb\uff0c\u5b9e\u5728\u53ef\u60dc\uff0c\u5c31\u7b97\u4f60\u4eca\u591c\u6740\u5c3d\u4e86\u8fd9\u91cc\u5929\u9a6c\u5802\u7684\u4eba\uff0c\u4f46\u5929\u9a6c\u603b\u5802\u5c82\u80af\u7f62\u4f11\uff0c\u4ed6\u4eec\u5df2\u7ecf\u6b63\u5f0f\u6d3e\u51fa\u957f\u8001\u3001\u62a4\u6cd5\u51fa\u9762\uff0c\u7edd\u4e0d\u4f1a\u653e\u5f03\u8fd9\u91cc\u7684\u5229\u76ca\uff0c\u5f7c\u6b64\u5c55\u5f00\u4e86\u4e89\u6597\u62fc\u6740\uff0c\u8fd9\u91cc\u5c31\u6c38\u65e0\u5b81\u65e5\u4e86\u3002\u201d \u3000\u3000\u201c\u8bf4\u7684\u6709\u7406\uff0c\u4e0d\u8fc7\uff0c\u6211\u5df2\u6740\u4e86\u5929\u9a6c\u603b\u5802\u4e2d\u6d3e\u6765\u4e00\u4f4d\u957f\u8001\u3001\u4e00\u4f4d\u62a4\u6cd5\uff0c\u53c8\u91cd\u521b\u4e86\u53e6\u4e00\u62a4\u6cd5\uff0c\u5929\u9a6c\u603b\u5802\u6050\u5df2\u4e0d\u80af\u5584\u5e72\u7f62\u4f11\u3002\u201d \u3000\u3000\u6885\u82b1\u8bf4\uff1a\u201c\u4f55\u51b5\uff0c\u5c0f\u59b9\u662f\u4e0a\u547d\u96be\u8fdd\uff0c\u9752\u51e4\u59d0\u6709\u4ee5\u6559\u6211\u5417\uff1f\u201d \u3000\u3000\u201c\u4f60\u4eec\u4e5f\u6709\u4f24\u4ea1\uff0c\u2026\u2026\u201d\u9752\u51e4\u9053\uff1a\u201c\u53cc\u65b9\u5c31\u7b97\u626f\u5e73\u4e86\uff0c\u4ee5\u540e\u7684\u4e8b\uff0c\u7531\u5929\u9a6c\u603b\u5802\u548c\u8d35\u4e0a\u53bb\u8bbe\u6cd5\u89e3\u51b3\u5427\uff01\u5982\u662f\u4f60\u4eec\u53cc\u65b9\u90fd\u4e0d\u80af\u7f62\u624b\uff0c\u957f\u5b89\u90ca\u5916\uff0c\u65f7\u91ce\u5343\u91cc\uff0c\u627e\u4e00\u5904\u51b3\u6218\u62fc\u547d\u7684\u5730\u65b9\uff0c\u7edd\u975e\u96be\u4e8b\uff0c\u53c8\u4f55\u5fc5\u4e00\u5b9a\u8981\u5728\u6d1b\u9633\u5c45\u4e2d\u62fc\u6740\u5462\uff1f\u201d \u3000\u3000\u9752\u51e4\u5ffd\u7136\u51fa\u9762\u5e72\u9884\uff0c\u5b8c\u5168\u51fa\u4e86\u9093\u98de\u7684\u610f\u6599\u4e4b\u5916\uff0c\u5c31\u662f\u6c5f\u67ab\u4e5f\u6ca1\u6709\u60f3\u5230\uff0c\u9752\u98ce\u4e3a\u4ec0\u4e48\u8981\u8eba\u8fd9\u4e00\u6c60\u6d51\u6c34\uff0c\u5728\u5929\u9a6c\u5802\u5927\u52bf\u5df2\u53bb\u65f6\uff0c\u624d\u51fa\u800c\u963b\u6b62\u6885\u82b1\uff0c\u662f\u4e0d\u662f\u522b\u6709\u7528\u5fc3\uff1f\u8fd9\u4e24\u4e2a\u6e90\u51fa\u4e00\u95e8\uff0c\u4eca\u591c\u4f1a\u4e0d\u4f1a\u4e5f\u95f9\u51fa\u4e00\u573a\u706b\u62fc\u6765\uff1f \u3000\u3000\u201c\u9752\u51e4\u59d1\u5a18\u7684\u610f\u601d\uff0c\u662f\u8981\u5c0f\u59b9\u7f62\u624b\u4e86\uff1f\u201d\u6885\u82b1\u6de1\u6de1\u7684\u7b11\u9053\uff1a\u201c\u770b\u6837\u5b50\uff0c\u5c0f\u59b9\u5982\u662f\u4e0d\u542c\u529d\u544a\uff0c\u4f60\u6211\u4e4b\u95f4\uff1b\u4e5f\u5c06\u662f\u65e0\u6cd5\u5584\u7ec8\u4e86\uff01\u201d \u3000\u3000\u201c\u6d1b\u9633\u5c45\u6536\u5165\u4e30\u539a\uff0c\u6211\u4eec\u4e09\u65b9\u9762\u5747\u5206\u5229\u6da6\uff0c\u6bcf\u4e2a\u6708\u5927\u5bb6\u90fd\u6709\u6570\u4e07\u4e24\u94f6\u5b50\u8fdb\u5e10\uff0c\u201d\u9752\u51e4\u9053\uff1a\u201c\u8c01\u90fd\u6ca1\u6709\u5403\u4e8f\uff0c\u770b\u5728\u94b1\u7684\u4efd\u4e0a\uff0c\u4f55\u4e0d\u5404\u9000\u4e00\u6b65\uff0c\u76f8\u5b89\u65e0\u4e8b\uff1f\u201d \u3000\u3000\u6885\u82b1\u9053\uff1a\u201c\u5982\u679c\u4e24\u4efd\u5747\u5206\uff0c\u5c82\u4e0d\u8fdb\u5e10\u66f4\u591a\u4e9b\uff1f\u201d \u3000\u3000\u9752\u51e4\u7b11\u9053\uff1a\u201c\u9010\u51fa\u5929\u9a6c\u5802\u7684\u4eba\uff0c\u6211\u4eec\u5e73\u5206\u4ed6\u4eec\u4e00\u5206\u2026\u2026\u201d \u3000\u3000\u201c\u5c31\u662f\u5982\u6b64\u4e86\uff0c\u201d\u6885\u82b1\u8bf4\uff0c\u201c\u6211\u4eec\u8fce\u5ba2\u4f8d\u9152\uff0c\u9752\u51e4\u59d0\u66f4\u662f\u98ce\u9761\u4e86\u957f\u5b89\uff0c\u5929\u9a6c\u5802\u7684\u4eba\uff0c\u5374\u628a\u4f4f\u4e86\u603b\u7ba1\u7684\u4f4d\u7f6e\uff0c\u4f5c\u5a01\u4f5c\u798f\uff0c\u6211\u4eec\u4ee5\u59ff\u8272\u98ce\u60c5\u6362\u5f97\u7684\u9ec4\u91d1\u3001\u767d\u94f6\uff0c\u5374\u8981\u5206\u7ed9\u4ed6\u4eec\u4e00\u4efd\uff0c\u8fd9\u96be\u9053\u7b97\u516c\u5e73\u5417\uff1f\u201d \u3000\u3000\u201c\u6885\u82b1\uff0c\u5929\u9a6c\u5802\u7684\u4eba\uff0c\u51fa\u8fc7\u5927\u529b\uff0c\u62fc\u8fc7\u6027\u547d\uff0c\u624d\u6362\u5f97\u4eca\u5929\u8fd9\u4e2a\u5c40\u9762\u2026\u2026\u201d\u9752\u51e4\u7b11\u9053\uff1a \u3000\u3000\u201c\u4ed6\u4eec\u6d41\u8fc7\u8840\u6c57\uff0c\u5f00\u7586\u62d3\u571f\uff0c\u7acb\u8fc7\u5927\u529f\uff0c\u4eca\u65e5\u5750\u4eab\u4e3e\u94f6\uff0c\u5e76\u65e0\u4e0d\u5f53\uff0c\u518d\u8bf4\uff0c\u6ca1\u6709\u4ed6\u4eec\u5c45\u4e2d\u7f13\u51b2\uff0c\u8d35\u6211\u53cc\u65b9\uff0c\u53ea\u6015\u4e5f\u4e0d\u4f1a\u5e73\u5b89\u65e0\u4e8b\uff0c\u4e09\u8db3\u9f0e\u7acb\uff0c\u603b\u6bd4\u53cc\u65b9\u5bf9\u5cd9\u6613\u8f83\u76f8\u5904\u3002\u201d \u3000\u3000\u201c\u770b\u6765\uff0c\u5c0f\u59b9\u662f\u65e0\u6cd5\u8bf4\u670d\u4f60\u4e86\uff0c\u201d\u6885\u82b1\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u597d\u53eb\u5c0f\u59b9\u4e3a\u96be\uff01\u201d \u3000\u3000\u201c\u4e0d\u7528\u4e3a\u96be\uff0c\u636e\u5b9e\u8f6c\u544a\u8d35\u4e0a\uff0c\u201d\u80b2\u51e4\u795e\u60c5\u7a81\u8f6c\u4e25\u8083\u5730\u8bf4\u9053\uff1a\u201c\u5927\u4e8b\u60c5\uff0c\u4f60\u6211\u90fd\u65e0\u6743\u51b3\u5b9a\uff0c\u95f9\u57ae\u4e86\u6d1b\u9633\u5c45\u5bf9\u5927\u5bb6\u90fd\u65e0\u597d\u5904\u3002\u201d \u3000\u3000\u201c\u597d\u5427\uff01\u9752\u51e4\u59d1\u5a18\u5982\u6b64\u575a\u6301\uff0c\u5c0f\u59b9\u53ea\u597d\u4ece\u547d\u4e86\uff0c\u4eca\u591c\u4e4b\u4e8b\u5230\u6b64\uff0c\u5c0f\u59b9\u4f1a\u8bf7\u793a\u5bab\u4e3b\u88c1\u51b3\uff0c\u5c4a\u65f6\u81ea\u5f53\u5148\u884c\u901a\u77e5\u9752\u51e4\u59d1\u5a18\u2026\u2026\u201d\u56de\u987e\u6842\u82b1\u3001\u8377\u82b1\u4e00\u773c\uff0c\u63a5\u9053\uff1a\u201c\u6211\u4eec\u8d70\uff01\u201d \u3000\u3000\u9752\u51e4\u9053\uff1a\u201c\u591a\u8c22\u8d4f\u8138\u3002\u201d \u3000\u3000\u6885\u82b1\u56de\u8fc7\u5934\uff0c\u9053\uff1a\u201c\u5982\u662f\u4ed6\u4eec\u8981\u51fa\u624b\u62a5\u590d\uff0c\u90a3\u5c31\u4e0d\u80fd\u602a\u5c0f\u59b9\u5168\u529b\u8fd8\u51fb\u4e86\uff01\u201d\u5f53\u5148\u6b65\u51fa\u5385\u95e8\u3002 \u3000\u3000\u9752\u51e4\u4f3c\u6709\u5fc3\u82e5\u65e0\u610f\u7684\u671b\u4e86\u97e9\u9738\u4e00\u773c\uff0c\u9053\uff1a\u201c\u97e9\u526f\u603b\u7ba1\uff0c\u529d\u529d\u9093\u603b\u7ba1\uff0c\u4ee5\u548c\u4e3a\u8d35\uff0c\u771f\u8981\u52a8\u624b\uff0c\u7ea6\u5230\u5916\u9762\u53bb\uff0c\u4e0d\u8981\u5f71\u54cd\u4e86\u6d1b\u9633\u5c45\u7684\u751f\u610f\u3002\u201d \u3000\u3000\u5e26\u7740\u738b\u5b37\u3001\u674e\u5b37\uff0c\u9000\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u6c5f\u67ab\u867d\u7136\u544a\u8bc9\u4e86\u9752\u51e4\u4e0d\u5c11\u7684\u4e8b\uff0c\u4f46\u4ecd\u7136\u4fdd\u7559\u4e86\u5927\u90e8\u5206\u7684\u9690\u5bc6\uff0c\u4ed6\u5316\u8eab\u97e9\u9738\u7684\u4e8b\uff0c\u5c31\u672a\u544a\u8bc9\u8fc7\u9752\u51e4\uff0c\u4f46\u90a3\u6df1\u60c5\u6b3e\u85aa\u7684\u4e00\u773c\uff0c\u5374\u770b\u7684\u6c5f\u67ab\u5fc3\u5934\u53d1\u6bdb\uff0c\u5fd6\u9053\uff1a\u201c\u96be\u9053\u5979\u5df2\u53d1\u89c9\u4e86\u6211\u7684\u8eab\u4efd\uff1f\u679c\u771f\u5982\u6b64\uff0c\u9752\u51e4\u6bd4\u6885\u82b1\u5c31\u9ad8\u660e\u592a\u591a\u4e86\u3002\u201d \u3000\u3000\u76ee\u7779\u4e24\u6279\u4eba\u9a6c\u5168\u90e8\u79bb\u53bb\uff0c\u9093\u98de\u624d\u56de\u8fc7\u5934\u770b\u770b\u4ec5\u4f59\u7684\u7070\u8863\u62a4\u6cd5\uff0c\u9053\uff1a\u201c\u4eca\u591c\u60c5\u5f62\uff0c\u9648\u5144\u662f\u4eb2\u773c\u6240\u89c1\uff0c\u5e0c\u671b\u80fd\u56de\u603b\u5802\u5e2e\u52a9\u9093\u67d0\u7f8e\u8a00\u51e0\u53e5\uff01\u201d \u3000\u3000\u201c\u9093\u5144\u653e\u5fc3\uff0c\u4eca\u591c\u5144\u5f1f\u4eb2\u773c\u770b\u5230\u4e86\u9093\u5144\u5904\u5883\u7684\u8270\u8f9b\uff0c\u4e01\u957f\u8001\u6e85\u8840\u6b92\u547d\uff0c\u5144\u5f1f\u8981\u65e9\u4e9b\u56de\u62a5\u603b\u5802\uff0c\u6211\u60f3\u5341\u65e5\u4e4b\u5185\uff0c\u5e94\u8be5\u4f1a\u6709\u88c1\u793a\u4f20\u5230\uff0c\u9093\u5144\u8bf7\u7b49\u4f73\u97f3\uff0c\u5144\u5f1f\u8fd9\u5c31\u544a\u8f9e\uff0c\u8fde\u591c\u4e0a\u8def\uff0c\u5468\u5144\u7559\u5728\u8fd9\u91cc\u517b\u4f24\uff0c\u8fd8\u8bf7\u8d39\u5fc3\u7167\u987e\u2026\u2026\u201d \u3000\u3000\u9093\u98de\u9053\uff1a\u201c\u8fd9\u4e2a\u5f53\u7136\uff0c\u7edd\u4e0d\u4f1a\u6709\u6240\u758f\u5ffd\u3002\u201d \u3000\u3000\u6c5f\u67ab\u7a81\u7136\u4e0a\u524d\u4e00\u6b65\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u9648\u62a4\u6cd5\uff0c\u8981\u7279\u522b\u8bf4\u660e\u90a3\u4e9b\u5f2f\u5200\u6740\u624b\u7684\u4e8b\u3002\u201d \u3000\u3000\u9648\u62a4\u6cd5\u70b9\u5934\uff0c\u8f6c\u8eab\u4e00\u8dc3\uff0c\u6d88\u5931\u5728\u591c\u6697\u4e2d\u3002 \u3000\u3000\u770b\u4ed6\u884c\u8272\u7684\u5306\u6025\uff0c\u663e\u7136\u5185\u5fc3\u6bd4\u9093\u98de\u8fd8\u8981\u7126\u6025\u3002 \u3000\u3000\u201c\u5927\u6982\u4e0d\u4f1a\u518d\u6709\u4e8b\u60c5\u4e86\uff0c\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u5927\u5bb6\u4f11\u606f\u65f6\uff0c\u522b\u5fd8\u4e86\u5c0f\u5fc3\u6212\u5907\u3002\u201d \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u3001\u80e1\u840d\u3001\u6bb5\u4e5d\u8ddf\u7740\u6c5f\u67ab\u56de\u5230\u4e86\u4f4f\u5904\uff0c\u80e1\u5143\u7559\u5728\u5ba4\u5916\u8b66\u6212\uff0c\u6c5f\u67ab\u5524\u51fa\u90ed\u5929\u540c\uff0c\u56f4\u5750\u4e00\u684c\uff0c\u7814\u5546\u5927\u8ba1\u3002 \u3000\u3000\u9152\u8089\u548c\u5c1a\u5148\u63d0\u51fa\u95ee\u9898\uff0c\u662f\u5426\u8fd8\u5e94\u8be5\u7559\u5728\u8fd9\u91cc\uff1f\u7559\u4e0b\u6765\u8981\u4e0d\u8981\u589e\u5f3a\u5b9e\u529b\uff1f \u3000\u3000\u6c5f\u67ab\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u5c3d\u53ef\u80fd\u7559\u4e0b\u6765\uff0c\u4eba\u5728\u6b64\u5730\uff0c\u624d\u80fd\u6311\u8d77\u5929\u9a6c\u5802\u548c\u5185\u5bab\u4e00\u7cfb\u7684\u4eba\uff0c\u5c55\u5f00\u62fc\u6597\uff0c\u6c5f\u6e56\u53ef\u7528\u7684\u529b\u91cf\u592a\u5c11\uff0c\u5929\u9a6c\u5802\u5374\u4e91\u96c6\u4e86\u5927\u6279\u9ad8\u624b\u3002 \u3000\u3000\u800c\u4e14\uff0c\u5927\u90fd\u662f\u7eff\u6797\u9053\u4e0a\u7684\u76d7\u532a\u3001\u9b54\u5934\uff0c\u4ed6\u4eec\u52a9\u7ea3\u4e3a\u8650\uff0c\u5e2e\u52a9\u90a3\u4e2a\u795e\u79d8\u7ec4\u5408\uff0c\u5f81\u670d\u4e86\u6b66\u6797\u4e2d\u5927\u90e8\u5206\u7684\u95e8\u6d3e\u3002 \u3000\u3000\u73b0\u5728\uff0c\u8981\u4ed6\u4eec\u4ee5\u9c9c\u8840\u8d4e\u7f6a\uff0c\u548c\u5185\u5bab\u4e2d\u4eba\u62fc\u547d\uff0c\u8be5\u662f\u5929\u9053\u5faa\u73af\uff0c\u7406\u6240\u5f53\u7136\uff0c\u4f55\u51b5\uff0c\u4ed6\u4eec\u65e9\u5df2\u4e3a\u4eba\u5217\u4e3a\u6e05\u9664\u7684\u76ee\u6807\uff0c\u975e\u88ab\u6574\u8083\u5c60\u622e\u4e0d\u53ef\u3002 \u3000\u3000\u6211\u4eec\u4ece\u65c1\u52a0\u628a\u52b2\uff0c\u53ea\u4e0d\u8fc7\u4fc3\u4f7f\u8fd9\u573a\u5927\u5bf9\u6297\u63d0\u524d\u7206\u53d1\uff0c\u62d6\u7684\u65f6\u95f4\u6108\u957f\uff0c\u5bf9\u5929\u9a6c\u5802\u6108\u662f\u4e0d\u5229\uff0c\u4ed6\u4eec\u90fd\u5df2\u662f\u8fdb\u5165\u8001\u8fc8\u4e4b\u5883\uff0c\u5bf9\u624b\u5374\u90fd\u662f\u5e74\u8f7b\u7537\u5973\uff0c\u8d81\u4ed6\u4eec\u8fd8\u6709\u4f59\u52c7\u53ef\u5356\u65f6\uff0c\u8ba9\u4ed6\u4eec\u4e3a\u6b66\u6797\u5927\u4e49\u5c3d\u4efd\u5fc3\u529b\uff0c\u9e1f\u5c3d\u5f13\u85cf\uff0c\u5154\u6b7b\u72d7\u70f9\uff0c\u4ed6\u4eec\u547d\u8fd0\u65e9\u5b9a\uff0c\u6240\u4ee5\uff0c\u8bf8\u4f4d\u7528\u4e0d\u7740\u5fc3\u5b58\u6148\u60b2\uff0c\u8fd9\u5c31\u662f\u6211\u4eec\u7559\u5728\u6d1b\u9633\u5c45\u6700\u5927\u7684\u76ee\u7684\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u8ba9\u5929\u9a6c\u5802\u8fd9\u6279\u9b54\u5934\u4eec\u5148\u6321\u9510\u950b\uff0c\u662f\u9010\u864e\u541e\u72fc\u7684\u9ad8\u62db\uff0c\u80e1\u67d0\u4eba\u5168\u529b\u62e5\u62a4\u3002\u201d \u3000\u3000\u201c\u6d1b\u9633\u697c\u7684\u6b4c\u5973\u9752\u51e4\uff0c\u4f3c\u4e4e\u662f\u53e6\u4e00\u80a1\u529b\u91cf\u4ee3\u8868\uff0c\u201d\u6bb5\u4e5d\u8bf4\uff1a\u201c\u770b\u6837\u5b50\u6885\u82b1\u5bf9\u5979\u754f\u60e7\u4e09\u5206\uff0c\u5979\u4ece\u4e2d\u4f5c\u6897\uff0c\u4f1a\u4e0d\u4f1a\u5f71\u54cd\u5230\u54b1\u4eec\u7684\u8ba1\u5212\u2026\u2026\u201d \u3000\u3000\u201c\u4eca\u591c\u7684\u60c5\u5f62\u4e0d\u540c\uff0c\u5982\u975e\u9752\u51e4\u51fa\u9762\uff0c\u90a3\u5c31\u4f1a\u903c\u5f97\u6211\u4eec\u5168\u529b\u51fa\u624b\uff0c\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u56e0\u4e3a\uff0c\u9093\u98de\u5df2\u65e0\u53ef\u7528\u4e4b\u5175\u3002\u201d \u3000\u3000\u201c\u6211\u548c\u5c1a\u4e00\u76f4\u62c5\u5fc3\u4f60\u6c5f\u8001\u5f1f\u88ab\u4e01\u897f\u5c71\u8a00\u8bed\u5957\u4f4f\uff0c\u8c6a\u6c14\u6d8c\u751f\uff0c\u4e00\u80a9\u627f\u62c5\uff0c\u6240\u5e78\uff0c\u4f60\u603b\u80fd\u53ca\u65f6\u56de\u907f\uff0c\u5c48\u5df1\u4ece\u52bf\uff0c\u8001\u5f1f\uff0c\u8fd9\u4efd\u81ea\u6211\u7ea6\u675f\u7684\u5fcd\u8010\u5de5\u592b\uff0c\u6211\u548c\u5c1a\u662f\u5927\u4e3a\u4f69\u670d\u3002\u201d \u3000\u3000\u201c\u6211\u770b\u6885\u82b1\u4f3c\u662f\u5df2\u5bf9\u6c5f\u8001\u5f1f\u52a8\u4e86\u6000\u7591\uff0c\u201d\u80e1\u840d\u8bf4\uff0c\u201c\u5012\u8981\u9884\u9632\u5979\u8ffd\u6839\u67e5\u4e0b\u53bb\u3002\u201d \u3000\u3000\u201c\u8fd9\u65b9\u9762\uff0c\u6211\u4f1a\u5c0f\u5fc3\uff0c\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u5927\u52bf\u5bf9\u6211\u4f3c\u4e0d\u5229\uff0c\u6b66\u6797\u4e4b\u4e2d\u5df2\u5f88\u96be\u805a\u96c6\u51fa\u4e00\u80a1\u5f3a\u5927\u7684\u529b\u91cf\uff0c\u5802\u5802\u6b63\u6b63\u7684\u548c\u654c\u4eba\u4e00\u51b3\u80dc\u8d1f\uff0c\u53ea\u6709\u4ee5\u5b50\u4e4b\u77db\u653b\u5b50\u4e4b\u76fe\uff0c\u4ee5\u654c\u5236\u654c\u7684\u65b9\u6cd5\uff0c\u8ba9\u4ed6\u4eec\u81ea\u76f8\u6b8b\u6740 \u3000\u3000\u795e\u60c5\u7a81\u7136\u95f4\u8f6c\u53d8\u7684\u5341\u5206\u4e25\u8083\uff0c\u63a5\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u975e\u4e07\u4e0d\u5f97\u5df2\uff0c\u8bf8\u4f4d\u4e0d\u8981\u8f7b\u6613\u51fa\u624b\uff0c\u4e00\u65e6\u51fa\u624b\uff0c\u52a1\u6c42\u5168\u529b\u6bd9\u654c\uff0c\u4ee5\u4fdd\u9690\u5bc6\u4e0d\u4f1a\u5916\u6cc4\uff0c\u800c\u4e14\uff0c\u8bf8\u4f4d\u6700\u597d\u80fd\u7ecf\u5e38\u805a\u96c6\u4e00\u5904\uff0c\u5fc5\u8981\u65f6\uff0c\u5408\u529b\u5e94\u4ed8\u5371\u673a\u3002\u201d \u3000\u3000\u8fd9\u756a\u8bdd\u5f26\u5916\u6709\u97f3\uff0c\u660e\u663e\u7684\u8bf4\u51fa\u6b64\u60c5\u6b64\u666f\uff0c\u4e0d\u662f\u8bb2\u9762\u5b50\uff0c\u901e\u82f1\u96c4\u7684\u65f6\u673a\uff0c\u5bf9\u654c\u7684\u624b\u6bb5\uff0c\u4e0d\u7528\u62d8\u6ce5\u4e8e\u6c5f\u6e56\u4f20\u7edf\u3002 \u3000\u3000\u6ca1\u6709\u56de\u7b54\u4ec0\u4e48\uff1f \u3000\u3000\u51e0\u4e2a\u4eba\u540c\u65f6\u70b9\u70b9\u5934\u8868\u793a\u9886\u6559\u3002 \u3000\u3000\u201c\u6211\u51c6\u5907\u5728\u6d1b\u9633\u5c45\u5916\u5efa\u7acb\u4e00\u4e24\u5904\u9690\u5bc6\u7684\u636e\u70b9\uff0c\u201d\u6c5f\u67ab\u8bf4\uff0c\u201c\u53ef\u4ee5\u5229\u7528\u4f5c\u4f20\u8baf\u7684\u5730\u65b9\uff0c\u4f7f\u5185\u5916\u52fe\u901a\uff0c\u548c\u94b1\u8001\u524d\u8f88\u4fdd\u6301\u7740\u8fde\u7edc\uff0c\u5fc5\u8981\u65f6\u4e5f\u53ef\u4ee5\u4f5c\u4e3a\u907f\u96be\u7684\u5730\u65b9\u3002\u201d \u3000\u3000\u7528\u4f5c\u907f\u96be\u7684\u5730\u65b9\uff1f\u8fd9\u5c31\u542c\u5f97\u4e03\u5b9d\u548c\u5c1a\u3001\u80e1\u840d\u3001\u6bb5\u4e5d\uff0c\u7741\u5927\u4e86\u773c\u775b\u770b\u5411\u6c5f\u67ab\u3002 \u3000\u3000\u56e0\u4e3a\uff0c\u80fd\u591f\u907f\u96be\u7684\u5730\u65b9\uff0c\u4e3b\u4e8b\u4eba\u7684\u667a\u6167\u3001\u6b66\u529f\uff0c\u90fd\u8981\u5177\u6709\u76f8\u5f53\u6c34\u51c6\u624d\u884c\uff0c\u5fc5\u8981\u65f6\u80fd\u51fa\u624b\u62d2\u6321\u654c\u4eba\u7684\u641c\u67e5\u3002 \u3000\u3000\u201c\u53ef\u8bf7\u6613\u82b1\u624b\u5f20\u56db\u59d1\u4eb2\u81ea\u4e3b\u6301\uff0c\u201d\u4e03\u5b9d\u548c\u5c1a\u60f3\u4e0d\u51fa\u8fd8\u6709\u4ec0\u4e48\u4eba\u80fd\u62c5\u8d1f\u8d77\u8fd9\u7b49\u6c89\u91cd\u7684\u4efb\u52a1\u3002 \u3000\u3000\u201c\u4e0d\u662f\uff0c\u6211\u60f3\u8bf7\u6b27\u9633\u541f\u96ea\u3001\u541f\u971c\u4e24\u4f4d\u59d1\u5a18\u4e3b\u6301\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u56db\u59d1\u8981\u5750\u9547\u98ce\u96ea\u5c0f\u9986\u3002\u201d \u3000\u3000\u201c\u5979\u4eec\u4ece\u672a\u5728\u6c5f\u6e56\u4e0a\u8d70\u52a8\u8fc7\uff0c\u201d\u80e1\u840d\u9053\uff1a\u201c\u5e74\u7eaa\u4e5f\u592a\u8f7b\u4e86\u4e00\u4e9b\uff0c\u62c5\u627f\u5982\u6b64\u91cd\u5927\u7684\u8d23\u4efb\uff0c\u4f1a\u4e0d\u4f1a\u592a\u8fc7\u6c89\u91cd\uff1f\u201d \u3000\u3000\u4ed6\u8bf4\u7684\u5f88\u542b\u84c4\uff0c\u4f46\u5df2\u70b9\u51fa\u4e86\u7528\u4e8c\u5973\u62c5\u8d1f\u5982\u6b64\u7684\u5927\u8d23\u91cd\u4efb\uff0c\u4e0d\u5f88\u9002\u5408\u3002 \u3000\u3000\u201c\u80e1\u524d\u8f88\u7684\u987e\u8651\u751a\u662f\uff0c\u4e00\u65e5\u4e4b\u524d\uff0c\u6211\u4e5f\u4f1a\u6709\u76f8\u540c\u7684\u770b\u6cd5\uff0c\u201d \u3000\u3000\u6c5f\u67ab\u7b11\u9053\uff1a\u201c\u4f46\u73b0\u5728\uff0c\u6211\u60f3\u5979\u4eec\u5df2\u5177\u6709\u5e94\u4ed8\u6c5f\u6e56\u4e00\u4e9b\u8be1\u8bc8\uff0c\u591a\u53d8\u7684\u80fd\u529b\u4e86\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u2026\u2026\uff0c\u201d\u6bb5\u4e5d\u9053\uff1a\u201c\u6c5f\u8001\u5f1f\u53ef\u5426\u8bf4\u5f97\u6e05\u695a\u4e00\u4e9b\uff1f\u201d \u3000\u3000\u201c\u5979\u4eec\u548c\u7236\u6bcd\u606f\u9690\u6df1\u5c71\uff0c\u4e00\u76f4\u5728\u82e6\u7ec3\u4e0a\u4e58\u5251\u6280\uff0c\u9020\u8be3\u751a\u9ad8\u2026\u2026\u201d\u6c5f\u67ab\u7b11\u9053\uff1a\u201c\u5f20\u56db\u59d1\u4f20\u6388\u7ed9\u5979\u4eec\u5feb\u901f\u7684\u6613\u5bb9\u624b\u6cd5\uff0c\u5979\u544a\u8bc9\u4e86\u5979\u4eec\u6c5f\u6e56\u4e0a\u9669\u8bc8\u4eba\u5fc3\uff0c\u4f46\u66f4\u91cd\u8981\u7684\u662f\u5979\u4eec\u65e9\u5df2\u4e60\u7ec3\u4e86\u5929\u72d0\u592b\u4eba\u7684\u6280\u827a\u2026\u2026\u201d \u3000\u3000\u201c\u8fd9\u5012\u662f\u5927\u5927\u51fa\u4eba\u610f\u5916\u4e86\uff0c\u5979\u4eec\u7eaf\u6d01\u6e05\u96c5\uff0c\u5982\u76db\u653e\u5728\u6df1\u8c37\u4e2d\u7684\u5e7d\u5170\uff0c\u6211\u548c\u5c1a\u5f88\u7559\u5fc3\u5730\u89c2\u5bdf\u8fc7\u5979\u4eec\uff0c\u5374\u662f\u4e00\u70b9\u4e5f\u77a7\u4e0d\u51fa\u6765\uff01\u201d \u3000\u3000\u201c\u662f\u7684\uff0c\u5979\u4eec\u9690\u85cf\u5730\u5f88\u6df1\u3001\u5f88\u597d\uff0c\u8fde\u6211\u4e5f\u88ab\u7792\u8fc7\u4e86\uff0c\u201d \u3000\u3000\u6c5f\u67ab\u8bf4\uff0c\u201c\u76f4\u5230\u5979\u4eec\u81ea\u8bf7\u51fa\u52a8\uff0c\u624d\u9732\u4e86\u4e00\u624b\u7ed9\u6211\u77a7\u77a7\uff0c\u6240\u4ee5\uff0c\u8bf8\u4f4d\u5c3d\u53ef\u653e\u5fc3\uff0c\u5979\u4eec\u53ea\u8981\u7a0d\u7ecf\u5386\u7ec3\uff0c\u5e94\u53d8\u4e4b\u80fd\uff0c\u7edd\u4e0d\u5728\u6885\u82b1\u4e4b\u4e0b\u3002\u201d \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u597d\u6781\u4e86\uff01\u770b\u6765\u6c5f\u8001\u5f1f\u8fd8\u6709\u5f88\u591a\u7684\u4f0f\u5175\u3001\u5e03\u7f72\u3002\u201d \u3000\u3000\u6c5f\u67ab\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u5c31\u53ea\u4ed6\u4eec\u4e00\u5bb6\u4eba\u8fd9\u70b9\u672c\u94b1\uff0c\u52a0\u8d77\u4e0d\u8fc7\u56db\u53e3\u3002\u201d \u3000\u3000\u4f46\u5e76\u672a\u8bf4\u51fa\u6765\uff0c\u6b64\u65f6\u6b64\u60c5\uff0c\u591a\u7ed9\u4eba\u4e00\u4e9b\u5e0c\u671b\uff0c\u4f1a\u589e\u52a0\u4ed6\u4eec\u594b\u6597\u7684\u52c7\u6c14\uff0c\u6240\u4ee5\uff0c\u6c5f\u67ab\u6765\u4e00\u4e2a\u7b11\u800c\u4e0d\u7b54\uff0c\u4f7f\u4eba\u83ab\u6d4b\u9ad8\u6df1\u3002 \u3000\u3000\u80e1\u840d\u7ad9\u8d77\u8eab\u5b50\uff0c\u4e00\u62b1\u62f3\uff0c\u9053\uff1a\u201c\u6c5f\u8001\u5f1f\u624d\u51a0\u6c5f\u6e56\uff0c\u624b\u63e1\u667a\u73e0\uff0c\u6211\u4eec\u4e00\u5207\u542c\u547d\u884c\u4e8b\u4e86\u3002\u201d \u3000\u3000\u201c\u5e03\u7f72\u505c\u5f53\uff0c\u6211\u4f1a\u5c3d\u5feb\u901a\u77e5\u4f60\u4eec\uff0c\u201d\u6c5f\u67ab\u8f7b\u8f7b\u53f9\u606f\u4e00\u58f0\uff0c\u63a5\u9053\uff1a\u201c\u6b64\u5730\u60c5\u5f62\uff0c\u8be1\u8bc8\u591a\u53d8\uff0c\u8bf8\u4f4d\u8bf7\u591a\u73cd\u91cd\u3002\u201d \u3000\u3000\u7a81\u95fb\u55e4\u7684\u4e00\u58f0\uff0c\u4e00\u7c92\u5c0f\u77f3\u5b50\u98de\u5165\u5ba4\u5185\u3002 \u3000\u3000\u8fd9\u662f\u80e1\u5143\u4f20\u6765\u7684\u8baf\u53f7\u3002 \u3000\u3000\u6bb5\u4e5d\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u53ef\u80fd\u662f\u603b\u7ba1\u6765\u4e86\uff01\u201d \u3000\u3000\u6c5f\u67ab\u6de1\u6de1\u4e00\u7b11\uff0c\u63d0\u9ad8\u4e86\u58f0\u97f3\uff0c\u9053\uff1a\u201c\u60c5\u52bf\u6bd4\u4eba\u5f3a\uff0c\u6211\u770b\u529d\u529d\u603b\u7ba1\u5411\u6885\u82b1\u59d1\u5a18\u6c42\u548c\u624d\u662f\u4e0a\u7b56\u2026\u2026\u201d \u3000\u3000\u201c\u6700\u597d\u662f\u6295\u6548\u8fc7\u6765\u2026\u2026\u201d \u3000\u3000\u6885\u82b1\u7a81\u7136\u51fa\u73b0\u95e8\u53e3\uff0c\u7f13\u6b65\u884c\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u6c5f\u67ab\u970d\u7136\u8d77\u7acb\uff0c\u6446\u51fa\u4e00\u526f\u8fce\u654c\u7684\u59ff\u52bf\u3002 \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u3001\u80e1\u840d\u3001\u6bb5\u4e5d\u3001\u90ed\u5929\u540c\uff0c\u4e5f\u8ddf\u7740\u7ad9\u8d77\uff0c\u62c9\u5f00\u4e86\u67b6\u5f0f\uff0c\u5fc3\u4e2d\u5374\u662f\u6697\u6697\u60ca\u9053\uff1a\u201c\u8fd9\u4e2b\u5934\u6765\u7684\u597d\u5feb\uff0c\u8b66\u8baf\u4f20\u6765\uff0c\u4eba\u5c31\u8d76\u5230\u3002\u201d \u3000\u3000\u201c\u4e0d\u7528\u7d27\u5f20\uff0c\u6211\u4e0d\u4f1a\u4f24\u5bb3\u4f60\u4eec\u3002\u201d \u3000\u3000\u6885\u82b1\u53e3\u6c14\u5f88\u5927\uff0c\u4f3c\u662f\u6839\u672c\u6ca1\u6709\u628a\u51e0\u4eba\u770b\u5728\u773c\u4e2d\u3002 \u3000\u3000\u201c\u53ea\u662f\u60f3\u548c\u4f60\u8c08\u8c08\u3002\u201d\u6885\u82b1\u5a9a\u9650\u8f6c\u52a8\u7f13\u7f13\u7531\u4e03\u5b9d\u548c\u5c1a\u7b49\u51e0\u4eba\u8138\u4e0a\u63a0\u8fc7\uff0c\u5374\u4ed4\u7ec6\u7684\u6253\u91cf\u4e86\u90ed\u5929\u540c\u4e00\u9635\u3002 \u3000\u3000\u5e78\u597d\uff0c\u90ed\u5929\u540c\u662f\u672c\u6765\u7684\u9762\u76ee\u51fa\u73b0\u3002 \u3000\u3000\u4f46\u6885\u82b1\u90a3\u76ee\u5149\u6709\u5982\u78c1\u77f3\u5438\u94c1\uff0c\u51e0\u4eba\u987f\u7136\u611f\u89c9\u5230\u5fc3\u795e\u9707\u98a4\uff0c\u90ed\u5929\u540c\u7684\u53cd\u5e94\u7279\u522b\u5f3a\u70c8\u3002 \u3000\u3000\u6c5f\u67ab\u5403\u4e86\u4e00\u60ca\uff0c\u5fd6\u9053\uff1a\u201c\u597d\u6076\u6bd2\u7684\u7528\u5fc3\uff0c\u7adf\u7136\u7528\u51fa\u4e86\u2018\u8ff7\u9b42\u5927\u6cd5\u2019\u3002\u201d \u3000\u3000\u6b63\u7b79\u601d\u7834\u89e3\u4e4b\u6cd5\uff0c\u6885\u82b1\u4e00\u53cc\u52fe\u4eba\u9b42\u9b44\u7684\u6843\u82b1\u773c\uff0c\u5df2\u8f6c\u5230\u4e86\u6c5f\u67ab\u7684\u8138\u4e0a\uff0c\u9053\uff1a\u201c\u97e9\u9738\uff0c\u8fd9\u4e9b\u4eba\u90fd\u662f\u4f60\u5f15\u5165\u6d1b\u9633\u5c45\u7684\u670b\u53cb\uff1f\u201d \u3000\u3000\u201c\u662f\uff01\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u8fd9\u4e9b\u4eba\u90fd\u5df2\u7ecf\u8fc7\u603b\u7ba1\u5141\u51c6\u3002\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\uff0c\u9093\u98de\u5728\u6d1b\u9633\u5c45\u5efa\u7acb\u8d77\u4e00\u6279\u5b8c\u5168\u5c5e\u4e8e\u81ea\u5df1\u7684\u529b\u91cf\u3002\u201d \u3000\u3000\u6885\u82b1\u6062\u590d\u4e86\u5e38\u6001\uff0c\u5979\u5df2\u611f\u89c9\u5230\u8fd9\u4e9b\u4eba\u90fd\u65e0\u80fd\u6297\u62d2\u201c\u8ff7\u9b42\u5927\u6cd5\u201d\uff0c\u4e0d\u8db3\u4e3a\u60a3\uff0c\u968f\u65f6\u90fd\u53ef\u4ee5\u628a\u4ed6\u4eec\u62c9\u5230\u8eab\u8fb9\u6765\uff0c\u6536\u4e3a\u5df2\u7528\u3002 \u3000\u3000\u201c\u9093\u98de\u6ca1\u6709\u80fd\u529b\u4fdd\u62a4\u4f60\u4eec\uff0c\u9664\u975e\u5929\u9a6c\u603b\u5802\uff0c\u771f\u7684\u51c6\u5907\u653e\u624b\u5927\u5e72\uff0c\u518d\u6d3e\u9063\u9ad8\u624b\u5230\u6b64\uff0c\u201d\u6885\u82b1\u63a5\u9053\uff1a\u201c\u4f46\u4e5f\u53ea\u662f\u591a\u6dfb\u4e9b\u6218\u6b7b\u7684\u51a4\u9b42\uff0c\u62d6\u5ef6\u4e00\u6bb5\u65f6\u95f4\uff0c\u7ec8\u5f52\u662f\u96be\u9003\u8d25\u4ea1\uff0c\u6211\u7b54\u5e94\u6536\u5bb9\u4f60\u4eec\uff0c\u4ecd\u7559\u5728\u6d1b\u9633\u5c45\u4e2d\uff0c\u4e0d\u8fc7\uff0c\u4f60\u4eec\u8981\u5148\u884c\u6295\u6548\uff01\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u2026\u2026\u201d\u6c5f\u67ab\u4e3a\u96be\u5730\u8bf4\uff0c\u201c\u53ef\u5426\u8ba9\u6211\u4eec\u5546\u91cf\u5546\u91cf\uff1f\u201d \u3000\u3000\u6885\u82b1\u7684\u76ee\u7684\u5df2\u8fbe\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u597d\uff01\u7ed9\u4f60\u4eec\u4e09\u5929\u7684\u65f6\u95f4\uff01\u201d\u5979\u5df2\u8ba4\u5b9a\u8fd9\u4e9b\u4eba\u4e0d\u8db3\u6784\u6210\u5a01\u534f\uff0c\u56de\u7b54\u7684\u5341\u5206\u5927\u65b9\uff0c\u800c\u4e14\u8f6c\u8eab\u79bb\u53bb\u3002 \u3000\u3000\u201c\u600e\u4e48\u5982\u6b64\u5927\u65b9\uff0c\u201d\u6bb5\u4e5d\u5947\u9053\uff1a\u201c\u597d\u50cf\u5979\u6765\u6b64\u76ee\u7684\uff0c\u53ea\u662f\u4e3a\u4e86\u529d\u8bf4\u6211\u4eec\u51e0\u53e5\uff1f\u201d \u3000\u3000\u201c\u6211\u770b\uff0c\u5979\u5df2\u7ecf\u8fbe\u5230\u76ee\u7684\u4e86\uff0c\u201d\u4e03\u5b9d\u548c\u5c1a\u8bf4\uff0c\u201c\u5979\u5df2\u5bf9\u6211\u4eec\u65bd\u5c55\u8fc7\u2018\u8ff7\u9b42\u5927\u6cd5\u2019\u3002\u201d \u3000\u3000\u201c\u5927\u5e08\u9ad8\u660e\uff0c\u6885\u82b1\u5728\u8fd9\u65b9\u9762\u9020\u8be3\u4e4b\u6df1\uff0c\u6625\u82b1\u56db\u5a62\u662f\u96be\u671b\u9879\u80cc\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u5979\u5df2\u81ea\u4fe1\u968f\u65f6\u90fd\u53ef\u4ee5\u5f81\u670d\u6211\u4eec\uff0c\u7528\u4e0d\u7740\u518d\u591a\u8d39\u53e3\u820c\u4e86\uff0c\u8bf8\u4f4d\u8981\u591a\u52a0\u5c0f\u5fc3\uff0c\u6885\u82b1\u53ef\u80fd\u4f1a\u5bf9\u8bf8\u4f4d\u4e2a\u522b\u65bd\u672f\u2026\u2026\u201d \u3000\u3000\u201c\u8fd9\u5c31\u9ebb\u70e6\u4e86\u2026\u2026\u201d\u4e03\u5b9d\u5927\u5e08\u9053\uff1a\u201c\u521a\u624d\u5979\u4e0d\u8fc7\u5306\u5306\u4e00\u773c\uff0c\u548c\u5c1a\u867d\u672a\u88ab\u5979\u52fe\u53bb\u9b42\u9b44\uff0c\u4e5f\u89c9\u5f97\u5fc3\u5934\u9707\u52a8\uff0c\u5982\u82e5\u5979\u4e13\u5fc3\u65bd\u672f\uff0c\u5bf9\u51c6\u6211\u548c\u5c1a\u4e0b\u624b\uff0c\u548c\u5c1a\u5c31\u5728\u52ab\u96be\u9003\u4e86\u3002\u201d \u3000\u3000\u80e1\u840d\u3001\u6bb5\u4e5d\u3001\u90ed\u5929\u540c\uff0c\u56de\u60f3\u521a\u624d\u60c5\u666f\uff0c\u5b9e\u6709\u5fc3\u795e\u6447\u8361\u4e4b\u611f\uff0c\u4e0d\u7981\u7751\u8272\u5927\u53d8\u3002 \u3000\u3000\u201c\u4e16\u4e0a\u771f\u6709\u8fd9\u7b49\u90aa\u95e8\u7684\u4e8b\u60c5\uff0c\u201d\u80e1\u840d\u8bf4\uff0c\u201c\u8fd9\u6bd4\u5200\u3001\u5251\u52a0\u8eab\uff0c\u66f4\u4e3a\u53ef\u6015\uff0c\u6c5f\u8001\u5f1f\uff0c\u4f60\u5f97\u60f3\u4e2a\u529e\u6cd5\u624d\u884c\uff01\u201d \u3000\u3000\u201c\u4ed6\u4eec\u80fd\u5728\u65e0\u58f0\u65e0\u606f\u4e2d\u5f81\u670d\u4e86\u6b66\u6797\u5404\u5927\u95e8\u6d3e\uff0c\u8fd9\u5927\u6982\u662f\u91cd\u8981\u7684\u624b\u6bb5\u4e4b\u2014\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a \u3000\u3000\u201c\u5175\u4e0d\u5203\u8840\uff0c\u4f7f\u5929\u4e0b\u82f1\u96c4\u62dc\u670d\u4e8e\u77f3\u69b4\u88d9\u4e0b\uff0c\u679c\u7136\u53ef\u6015\u3002\u201d \u3000\u3000\u201c\u548c\u5c1a\u9886\u6559\u8fc7\u6625\u82b1\u56db\u5a62\u7684\u5a9a\u529b\uff0c\u6211\u548c\u80e1\u5144\u3001\u6bb5\u5144\u5927\u6982\u53ef\u4ee5\u6297\u62d2\uff0c\u5bf9\u6885\u82b1\u6050\u6015\u5c31\u65e0\u80fd\u4e3a\u529b\u4e86\uff0c\u8981\u662f\u88ab\u5979\u98de\u4e86\u51e0\u4e2a\u5a9a\u773c\uff0c\u6211\u4eec\u5c31\u675f\u624b\u5c31\u7f1a\u4e86\uff0c\u8fd9\u4e2a\u4ed7\u90a3\u8fd8\u80fd\u6253\u5f97\u4e0b\u53bb\uff0c\u6211\u60f3\u2018\u8ff7\u9b42\u5927\u6cd5\u2019\u529f\u529b\u80fd\u5230\u6885\u82b1\u8fd9\u7b49\u5883\u754c\u7684\uff0c\u7edd\u975e\u4e00\u4eba\uff0c\u5982\u662f\u627e\u4e0d\u51fa\u6297\u62d2\u7684\u529e\u6cd5\uff0c\u6c5f\u8001\u5f1f\uff0c\u80fd\u591f\u5e2e\u4f60\u7684\u4eba\uff0c\u90a3\u5c31\u5c48\u6307\u53ef\u6570\u4e86\uff01\u201d \u3000\u3000\u201c\u50cf\u6885\u82b1\u8fd9\u7b49\u8eab\u5177\u5a9a\u672f\u5927\u6210\u7684\u4eba\uff0c\u5e94\u8be5\u4e0d\u4f1a\u592a\u591a\uff0c\u56e0\u4e3a\uff0c\u8fd9\u5fc5\u9700\u5148\u5929\u5177\u6709\u5a9a\u529b\u7684\u4eba\uff0c\u624d\u80fd\u8fbe\u5230\u5883\u754c\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u6885\u82b1\u5f88\u9002\u5408\u4e60\u7ec3\u8fd9\u79cd\u6b66\u529f\uff01\u201d \u3000\u3000\u201c\u6c5f\u8001\u5f1f\u5440\uff01\u2026\u2026\u201d\u4e03\u5b9d\u5927\u5e08\u8bf4\uff1a\u201c\u6709\u4e0a\u5341\u4e2a\u516b\u4e2a\u8fd9\u6837\u7684\u5996\u5973\uff0c\u5c31\u5929\u4e0b\u5927\u4e71\u4e86\uff0c\u90a3\u8fd8\u9700\u8981\u8bb8\u591a\uff1f\u62ff\u6b4c\u59ec\u9752\u51e4\u8bf4\u5427\uff0c\u5979\u5982\u4e60\u7ec3\u2018\u8ff7\u9b42\u5927\u6cd5\u2019\uff0c\u6210\u5c31\u7edd\u4e0d\u4f1a\u5728\u6885\u82b1\u4e4b\u4e0b\uff0c\u53ea\u6b64\u4e8c\u4eba\uff0c\u53ea\u6b64\u4e00\u672f\uff0c\u6211\u4eec\u5c31\u6ca1\u6709\u529e\u6cd5\u5bf9\u4ed8\u4e86\uff01\u201d \u3000\u3000\u201c\u662f\u554a\uff01\u6c5f\u8001\u5f1f\uff0c\u8fd9\u6bb5\u65e5\u5b50\u91cc\uff0c\u6211\u4eec\u5168\u5fc3\u6295\u5165\uff0c\u82e6\u7814\u6b66\u529f\uff0c\u76f8\u4e92\u6307\u70b9\uff0c\u5f7c\u6b64\u5207\u78cb\uff0c\u5fc3\u7a8d\u5927\u5f00\uff0c\u6570\u6708\u4e4b\u529f\uff0c\u80dc\u8fc7\u5f80\u6614\u5341\u5e74\u7684\u8fdb\u5883\uff0c\u201d\u6bb5\u4e5d\u8bf4\uff0c\u201c\u5c31\u7b97\u9047\u4e0a\u4e00\u6d41\u9ad8\u624b\uff0c\u81ea\u4fe1\u4e5f\u53ef\u653e\u624b\u4e00\u6218\u5440\uff0c\u4f46\u5982\u5200\u672a\u51fa\u9798\uff0c\u5251\u72b9\u5728\u62b1\uff0c\u5c31\u88ab\u4eba\u51e0\u4e2a\u5a9a\u7b11\uff0c\u5f04\u5f97\u4e22\u76d4\u5f03\u7532\uff0c\u8fd8\u624b\u65e0\u529b\uff0c\u53cd\u8eab\u6295\u654c\uff0c\u4e3a\u864e\u4f5c\u6005\uff0c\u90a3\u5c31\u751f\u4e0d\u5982\u6b7b\u4e86\uff01\u201d \u3000\u3000\u6c5f\u67ab\u6c89\u541f\u4e00\u9635\uff0c\u9053\uff1a\u201c\u4ee5\u90aa\u5236\u90aa\uff0c\u5fc5\u5f97\u8bf7\u6c42\u4fee\u70bc\u7684\u6df1\u6d45\uff0c\u6050\u975e\u77ed\u671f\u6240\u80fd\u594f\u6548\uff0c\u4e0d\u8fc7\uff0c\u6709\u4e00\u79cd\u6536\u6444\u5fc3\u795e\u7684\u529f\u592b\uff0c\u53ef\u4ee5\u6297\u62d2\u8fd9\u79cd\u5a9a\u672f\uff0c\u4e14\u6709\u901f\u6210\u4e4b\u6548\uff0c\u540d\u53eb\u5927\u60b2\u771f\u8bc0\uff0c\u53ea\u6709\u4e03\u4e03\u56db\u5341\u4e5d\u4e2a\u5b57\uff0c\u8fd0\u529f\u65f6\u8bf5\u771f\u8bc0\uff0c\u5f88\u5feb\u80fd\u5fc3\u795e\u5b81\u9759\uff0c\u5927\u6982\u53ef\u4ee5\u62d2\u6297\u2018\u8ff7\u9b42\u5927\u6cd5\u2019\u7684\u5a9a\u529b\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u662f\u5927\u6982\uff0c\u800c\u662f\u4e00\u5b9a\u53ef\u4ee5\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u8bf4\uff0c\u201c\u8fd9\u662f\u6211\u4f5b\u62b5\u5fa1\u5916\u9b54\u5e72\u6270\u7684\u5fc3\u6cd5\u771f\u8bc0\uff0c\u4f5b\u95e8\u4e2d\u5931\u4f20\u7edd\u5b66\uff0c\u60f3\u4e0d\u5230\u6c5f\u8001\u5f1f\u80fd\u8ba9\u5b83\u91cd\u73b0\u6c5f\u6e56\uff0c\u5f53\u771f\u662f\u2026\u2026\u201d \u3000\u3000\u201c\u5927\u5e08\u5fc3\u4e2d\u5982\u6709\u8d28\u7591\u4e4b\u60f3\uff0c\u54b1\u4eec\u4ee5\u540e\u518d\u8bf4\u2026\u2026\u201d\u6c5f\u67ab\u82e6\u7b11\u9053\uff1a\u201c\u73b0\u5728\u65f6\u673a\u8feb\u4fc3\uff0c\u54b1\u4eec\u5148\u5b66\u771f\u8bc0\u3002\u201d \u3000\u3000\u4e00\u5411\u653e\u8361\u4e0d\u7f81\u7684\u4e03\u5b9d\u548c\u5c1a\uff0c\u7a81\u4e00\u6539\u6545\u6001\uff0c\u795e\u60c5\u5e84\u4e25\u7684\u5408\u638c\u8eac\u8eab\uff0c\u8bf4\u9053\uff1a\u201c\u662f\uff01\u548c\u5c1a\u62dc\u9886\u539a\u8d50\u2026\u2026\u201d\u7f13\u7f13\u8dea\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u4ed6\u8fd9\u4e48\u5982\u5949\u7eb6\u65e8\u7684\u4e00\u505a\u4f5c\uff0c\u80e1\u840d\u3001\u6bb5\u4e5d\u3001\u90ed\u5929\u540c\uff0c\u90fd\u8ddf\u7740\u8dea\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u6c5f\u67ab\u8138\u4e0a\u6234\u7740\u9762\u5177\uff0c\u770b\u4e0d\u51fa\u4ed6\u7684\u795e\u8272\u5982\u4f55\uff0c\u4f46\u53ef\u4ece\u4ed6\u4e24\u9053\u76ee\u5149\u4e2d\u770b\u51fa\u4e25\u8083\u7684\u795e\u5149\uff0c\u6388\u53d7\u4e4b\u95f4\uff0c\u7acb\u523b\u88ab\u4e00\u79cd\u5e84\u4e25\u7684\u6c14\u6c1b\u6240\u7b3c\u7f69\uff0c\u6c5f\u67ab\u5ff5\u51fa\u5927\u60b2\u771f\u8bc0\uff0c\u4f17\u4eba\u8ddf\u7740\u80cc\u8bf5\uff0c\u8fde\u5ff5\u4e86\u5341\u4f59\u904d\uff0c\u611f\u89c9\u5230\u80e1\u840d\u7b49\u90fd\u5df2\u8bb0\u719f\uff0c\u624d\u89e3\u8bf4\u4e86\u914d\u5408\u771f\u8bc0\u7684\u8c03\u606f\u65b9\u6cd5\u3002 \u3000\u3000\u6c5f\u67ab\u7aef\u7aef\u6b63\u6b63\u5730\u7ad9\u7740\u8bb2\uff0c\u4e03\u5b9d\u548c\u5c1a\u3001\u80e1\u840d\u3001\u6bb5\u4e5d\u3001\u90ed\u5929\u540c\u795e\u60c5\u8083\u7136\u5730\u8dea\u7740\u542c\u3002 \u3000\u3000\u8fd9\u7b49\u4f5b\u95e8\u5927\u4e58\u65e0\u4e0a\u5fc3\u6cd5\u771f\u8bc0\uff0c\u4f1a\u4ee4\u4eba\u751f\u51fa\u8bda\u656c\u65e0\u6bd4\u7684\u611f\u89c9\uff0c\u8138\u4e0a\u795e\u60c5\u5145\u6ee1\u5e84\u4e25\u3002 \u3000\u3000\u201c\u4e3a\u4e86\u6297\u62d2\u5996\u5973\u5a9a\u672f\uff0c\u8bf8\u4f4d\u53ef\u4ee5\u9009\u62e9\u771f\u6b63\u613f\u4e3a\u6b66\u6797\u5927\u4e49\u732e\u8eab\u7684\u4eba\uff0c\u4f20\u6388\u771f\u8bc0\u795e\u529f\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u4e25\u8083\u5730\u8bf4\uff0c\u201c\u4e0d\u8fc7\uff0c\u5728\u4eba\u9009\u65b9\u9762\u8981\u7559\u5fc3\u4e00\u4e9b\uff0c\u4e00\u65e6\u4f20\u6388\u975e\u4eba\uff0c\u6d41\u5165\u9b54\u6559\uff0c\u90a3\u5c31\u77e5\u9053\u4f1a\u53d1\u751f\u4ec0\u4e48\u53d8\u5316\u4e86\uff01\u201d \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u7b49\u656c\u8c28\u53d7\u6559\uff0c\u7f13\u7f13\u7ad9\u8d77\u8eab\u5b50\u3002 \u3000\u3000\u6c5f\u67ab\u4e00\u6325\u624b\u9053\uff1a\u201c\u8bf8\u4f4d\u8bf7\u56de\u53bb\u5427\uff01\u201d \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u3001\u80e1\u840d\u3001\u6bb5\u4e5d\uff0c\u62db\u547c\u4e86\u80e1\u5143\uff0c\u4e00\u9f50\u79bb\u53bb\u3002 \u3000\u3000\u90ed\u5929\u540c\u4e00\u8eac\u8eab\uff0c\u9053\uff1a\u201c\u5728\u4e0b\u5462\uff1f\u201d \u3000\u3000\u201c\u4f60\u7acb\u523b\u6062\u590d\u97e9\u9738\u7684\u8eab\u4efd\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u6709\u4ec0\u4e48\u53d8\u6545\uff0c\u7acb\u523b\u548c\u4e03\u5b9d\u5927\u5e08\u7b49\u4f1a\u5408\uff0c\u4e00\u8d77\u884c\u52a8\uff0c\u9047\u5230\u4e3a\u96be\u4e4b\u5904\uff0c\u548c\u5e08\u5546\u91cf\u3002\u201d \u3000\u3000\u6325\u624b\u7184\u53bb\u706b\u70db\uff0c\u79bb\u5f00\u4e86\u6d1b\u9633\u5c45\uff0c\u52ff\u52ff\u8d76\u56de\u4e86\u5ba2\u6808\u4e4b\u4e2d\u3002 \u3000\u3000\u6c5f\u67ab\u7684\u63a8\u65ad\u4e0d\u9519\uff0c\u521a\u521a\u8131\u8863\u4e0a\u5e8a\uff0c\u5ba4\u5916\u5df2\u54cd\u8d77\u4e86\u53e9\u95e8\u7684\u58f0\u97f3\u3002 \u3000\u3000\u88c5\u51fa\u4e00\u526f\u60fa\u677e\u7761\u773c\uff0c\u71c3\u8d77\u4e86\u706f\u706b\uff0c\u6253\u5f00\u6728\u95e8\u3002 \u3000\u3000\u53ea\u89c1\u6885\u82b1\u4e00\u8eab\u8f7b\u88c5\uff0c\u7ad9\u5728\u95e8\u5916\u3002 \u3000\u3000\u201c\u662f\u4f60\u2026\u2026\u201d\u6c5f\u67ab\u63c9\u63c9\u773c\u775b\uff0c\u8138\u4e0a\u662f\u4e00\u526f\u60ca\u559c\u4ea4\u96c6\u7684\u795e\u60c5\uff0c\u4e00\u628a\u62b1\u8d77\u4e86\u6885\u82b1\uff0c\u884c\u5165\u5ba4\u4e2d\uff0c\u9053\uff1a\u201c\u7a76\u7adf\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\u60c5\uff0c\u6211\u597d\u62c5\u5fc3\u554a\uff01\u201d \u3000\u3000\u201c\u6211\u4e0d\u662f\u597d\u597d\u7684\u5417\uff1f\u5173\u4e0a\u95e8\uff0c\u62b1\u6211\u4e0a\u5e8a\uff0c\u771f\u7684\u6709\u70b9\u7d2f\u4e86\u2026\u2026\u201d \u3000\u3000\u6885\u82b1\u5e3d\u773c\u6d41\u6ce2\uff0c\u5fae\u5e26\u6492\u5a07\u5730\u8bf4\u9053\uff1a\u201c\u6211\u8981\u8eb2\u5728\u4f60\u7684\u6000\u62b1\u91cc\uff0c\u597d\u597d\u7684\u7761\u4e00\u89c9\u3002\u201d \u3000\u3000\u6c5f\u67ab\u62f4\u4e0a\u5ba4\u95e8\uff0c\u7b11\u9053\uff1a\u201c\u53ea\u6015\u662f\u7761\u4e0d\u597d\u4e86\uff1f\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6885\u82b1\u8138\u4e0a\u60c5\u7231\u6a2a\u6ea2\uff0c\u65e0\u9650\u6e29\u67d4\u5730\u8bf4\u3002 \u3000\u3000\u201c\u6211\u7684\u778c\u7761\u866b\u88ab\u4f60\u8d76\u8dd1\u4e86\uff0c\u6211\u73b0\u5728\u7cbe\u795e\u9971\u6ee1\uff0c\u9965\u997f\u96be\u8010\uff0c\u597d\u60f3\u5403\u4e86\u4f60\uff01\u201d\u6c5f\u67ab\u62b1\u8d77\u6885\u82b1\u7684\u5a07\u8eaf\uff0c\u6eda\u4e0a\u7259\u5e8a\u3002 \u3000\u3000\u201c\u521a\u7ecf\u8fc7\u4e00\u573a\u51f6\u9669\u7684\u640f\u6740\u2026\u2026\u201d\u6885\u82b1\u7ea4\u5de7\u7684\u4e94\u6307\uff0c\u8f7b\u629a\u7740\u6c5f\u67ab\u7684\u8138\u988a\uff0c\u9053\uff1a\u201c\u6d17\u4e2a\u6fa1\uff0c\u5c31\u5fcd\u4e0d\u4f4f\u8dd1\u6765\u770b\u4f60\uff0c\u5509\uff01\u4f60\u771f\u662f\u6211\u547d\u4e2d\u7684\u9b54\u661f\uff0c\u4f3c\u8fd9\u822c\u96be\u4ee5\u81ea\u62d4\uff0c\u4e0d\u77e5\u5982\u4f55\u5f97\u4e86\u554a\uff01\u201d \u3000\u3000\u6c5f\u67ab\u7f13\u7f13\u653e\u5f00\u7d27\u62b1\u5165\u6000\u7684\u5a07\u8eaf\uff0c\u6ee1\u8138\u5173\u6000\u7684\u8bf4\uff1a\u201c\u544a\u8bc9\u6211\uff0c\u600e\u4e48\u56de\u4e8b\uff1f\u201d \u3000\u3000\u201c\u6211\u5949\u547d\u6e05\u9664\u6d1b\u9633\u5c45\u4e2d\u7684\u95f2\u6742\u4eba\u7b49\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u6253\u65ad\u4e86\u6885\u82b1\u7684\u8bdd\uff0c\u9053\uff1a\u201c\u90a3\u4e9b\u4eba\u662f\u95f2\u6742\u4eba\u7b49\uff1f\u201d \u3000\u3000\u201c\u600e\u4e48\uff01\u4f60\u4e5f\u5173\u5fc3\u6d1b\u9633\u5c45\u7684\u4e8b\uff1f\u201d\u6885\u82b1\u53cc\u76ee\u76ef\u6ce8\u5728\u6c5f\u67ab\u7684\u8138\u4e0a\u95ee\u3002 \u3000\u3000\u201c\u6d1b\u9633\u5c45\u4e2d\u7684\u4eba\uff0c\u6211\u53ea\u5173\u5fc3\u4e24\u4e2a\uff0c\u7b2c\u4e00\u4e2a\u5f53\u7136\u662f\u4f60\u2026\u2026\u201d \u3000\u3000\u201c\u7b2c\u4e8c\u4e2a\u5462\uff1f\u662f\u83ca\u82b1\u8fd8\u662f\u6842\u82b1\uff1f\u201d \u3000\u3000\u201c\u90fd\u4e0d\u662f\uff0c\u90a3\u4e2a\u4eba\u662f\u7537\u7684\uff0c\u662f\u6d1b\u9633\u5c45\u4e2d\u7684\u526f\u603b\u7ba1\u2026\u2026\u201d \u3000\u3000\u201c\u4f60\u662f\u8bf4\u97e9\u9738\uff1f\u201d \u3000\u3000\u201c\u5bf9\uff01\u5c31\u662f\u4ed6\uff0c\u4ed6\u5e2e\u8fc7\u6211\u7684\u5fd9\uff0c\u6211\u6b20\u4ed6\u4e00\u4efd\u60c5\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u4eba\u4e00\u5f20\u8138\u51b7\u5389\u5982\u5200\u2026\u2026\u201d\u6885\u82b1\u9053\uff1a\u201c\u4f46\u5374\u6709\u4e00\u4efd\u53eb\u4eba\u611f\u53f9\u7684\u611a\u5fe0\uff0c\u6211\u5f88\u60f3\u653e\u4ed6\u4e00\u5200\uff0c\u4f46\u4ed6\u5374\u4e0d\u77e5\u597d\u6b79\uff0c\u4e0d\u8fc7\uff0c\u6211\u5df2\u7ecf\u5e2e\u4f60\u4ea4\u4ee3\u8fc7\u4e86\u3002\u201d \u3000\u3000\u201c\u4f60\u6740\u4e86\u4ed6\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u88c5\u51fa\u4e00\u526f\u5403\u60ca\u7684\u6837\u5b50\u3002 \u3000\u3000\u6885\u82b1\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6ca1\u6709\uff0c\u4ed6\u867d\u7136\u9762\u76ee\u53ef\u618e\uff0c\u4e0d\u8fc7\uff0c\u5012\u662f\u6709\u70b9\u7537\u5b50\u6c49\u7684\u6c14\u6168\uff0c\u4ed6\u6ca1\u6709\u631f\u6069\u6c42\u62a5\uff0c\u4e5f\u6ca1\u6709\u63d0\u8fc7\u653e\u4f60\u7684\u4e8b\u60c5\uff0c\u65e5\u540e\uff0c\u6211\u4f1a\u7559\u70b9\u673a\u4f1a\u7ed9\u4ed6\uff0c\u4fdd\u5168\u4ed6\u4e00\u6761\u6027\u547d\u5c31\u662f\u3002\u201d \u3000\u3000\u6c5f\u67ab\u5fc3\u4e2d\u5fd6\u9053\uff1a\u201c\u770b\u6765\uff0c\u4e0d\u8bba\u5982\u4f55\u7684\u4e25\u9177\u8bad\u7ec3\uff0c\u4e5f\u65e0\u6cd5\u5b8c\u5168\u62b9\u6740\u53bb\u4e0e\u751f\u4ff1\u6765\u7684\u4eba\u6027\uff0c\u8fd9\u662f\u4ed6\u4eec\u7684\u4e25\u91cd\u7f3a\u5931\uff0c\u8981\u5982\u4f55\u53bb\u638c\u63e1\u8fd0\u7528\uff0c\u624d\u80fd\u591f\u62e8\u4e71\u53cd\u6b63\u2026\u2026\u201d \u3000\u3000\u201c\u4f60\u5728\u60f3\u4ec0\u4e48\uff1f\u8fd9\u7b49\u5165\u795e\u2026\u2026\u201d\u6885\u82b1\u9053\uff1a\u201c\u7f8e\u4eba\u5728\u62b1\uff0c\u4e5f\u4e0d\u77e5\u601c\u60dc\u3001\u4eab\u7528\u3002\u201d \u3000\u3000\u201c\u6211\u5728\u60f3\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u4eca\u591c\u7684\u6d1b\u9633\u5c45\uff0c\u4e00\u5b9a\u662f\u8840\u96e8\u8165\u98ce\uff0c\u60e8\u4e0d\u5fcd\u7779\uff0c\u6885\u82b1\uff0c\u6211\u4eec\u8d70\u5427\uff01\u8fd9\u79cd\u65e5\u5b50\uff0c\u5982\u4f55\u80fd\u8fc7\u5f97\u4e0b\u53bb\uff0c\u65e5\u591c\u4e3a\u4f60\u7684\u5b89\u5371\u62c5\u5fc3\uff0c\u6211\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u8981\u803d\u5fc3\u6211\u2026\u2026\u201d \u3000\u3000\u6885\u82b1\u4f3c\u662f\u5927\u53d7\u611f\u52a8\uff0c\u53cc\u76ee\u4e2d\u7adf\u7136\u6709\u6cea\u6c34\u6d8c\u51fa\u3002 \u3000\u3000\u6c5f\u67ab\u6697\u6697\u53f9\u606f\u4e00\u58f0\uff0c\u4e3e\u624b\u5e2e\u6885\u82b1\u62ed\u53bb\u6cea\u75d5\uff0c\u5fc3\u4e2d\u5fd6\u9053\uff1a\u201c\u5979\u771f\u60c5\u6d41\u9732\uff0c\u5982\u6b64\u7684\u7f20\u7ef5\u76f8\u4f9d\uff0c\u6211\u771f\u80fd\u8fa3\u624b\u6467\u82b1\uff0c\u7f6e\u5979\u4e8e\u6b7b\u5417\uff1f\u201d \u3000\u3000\u4e00\u5ff5\u52a8\u60c5\uff0c\u51b3\u5fc3\u5c3d\u53ef\u80fd\u529d\u6885\u82b1\u5f03\u6697\u6295\u660e\uff0c\u81f3\u5c11\uff0c\u4e5f\u7559\u5979\u4e00\u6761\u751f\u8def\u3002 \u3000\u3000\u201c\u6211\u53ea\u662f\u4e00\u4e2a\u6d41\u6d6a\u5728\u4eba\u95f4\u7684\u5b64\u5973\uff0c\u81ea\u6211\u8bb0\u4e8b\u90a3\u5929\u7b97\u8d77\uff0c\u5c31\u88ab\u4eba\u6536\u5bb9\u5728\u4e00\u5ea7\u6df1\u5c71\u4e2d\u7684\u5927\u5b85\u9662\u5185\u2026\u2026\u201d\u6885\u82b1\u65e0\u9650\u9eef\u7136\u5730\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u8c01\u662f\u751f\u8eab\u7236\u6bcd\uff0c\u4f46\u4e5f\u517b\u6210\u4e86\u6211\u7684\u72ec\u7acb\uff0c\u575a\u5f3a\u6027\u683c\uff0c\u53ea\u77e5\u4e00\u5473\u4e89\u5f3a\uff0c\u51fa\u4eba\u5934\u5730\uff0c\u6574\u4e2a\u7684\u751f\u6d3b\u8fc7\u7a0b\uff0c\u5145\u6ee1\u7740\u5978\u8bc8\uff0c\u76f4\u5230\u9047\u4e0a\u4e86\u4f60\uff0c\u624d\u77e5\u9053\u6211\u4e5f\u6709\u8f6f\u5f31\u3001\u6e29\u67d4\u7684\u4e00\u9762\u2026\u2026\u201d \u3000\u3000\u7a81\u7136\u4e00\u6574\u8138\u8272\uff0c\u5a07\u58f0\u7b11\u9053\uff1a\u201c\u5c3d\u8bf4\u8fd9\u4e9b\u5e72\u4ec0\u4e48\uff1f\u4f60\u4e0d\u662f\u6c5f\u6e56\u4e2d\u4eba\uff0c\u600e\u4f1a\u77e5\u9053\u6c5f\u6e56\u4e2d\u4e8b\uff0c\u62b1\u7d27\u6211\uff0c\u7530\u7389\uff0c\u8ba9\u6211\u5728\u6d3b\u7740\u7684\u65f6\u5019\uff0c\u591a\u5c1d\u4e9b\u60c5\u7231\u6ecb\u5473\u2026\u2026\u201d \u3000\u3000\u7075\u6d3b\u7684\u5a07\u8eaf\uff0c\u86c7\u4e00\u6837\u7f20\u4e0a\u4e86\u6c5f\u67ab\u3002 \u3000\u3000\u76f8\u5904\u65f6\u6781\u5c3d\u5a07\u67d4\u3001\u7f20\u7ef5\uff0c\u4f46\u5929\u8272\u4e00\u4eae\uff0c\u6885\u82b1\u5c31\u7acb\u523b\u8d77\u8eab\u4e0b\u5e8a\uff0c\u7a7f\u4e0a\u8863\u670d\u8981\u79bb\u53bb\u3002 \u3000\u3000\u5979\u867d\u7136\u8ff7\u604b\u5931\u63a7\uff0c\u8fd8\u672a\u5230\u5fd8\u6211\u4e4b\u5883\u3002 \u3000\u3000\u6c5f\u67ab\u5374\u4e00\u628a\u62c9\u7740\u4e86\u6885\u82b1\uff0c\u9053\uff1a\u2018\u2019\u8fd9\u822c\u7684\u6765\u53bb\u5306\u5306\uff0c\u8981\u6211\u6392\u9063\u8fd9\u76f8\u601d\u4e4b\u82e6\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u4f1a\u5c3d\u91cf\u62bd\u65f6\u95f4\u8dd1\u6765\u770b\u4f60\uff0c\u7530\u7389\uff0c\u4e0d\u8fc7\uff0c\u8fd9\u4e24\u5929\u6050\u6015\u4e0d\u884c\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u5462\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\uff0c\u4eca\u5929\u5348\u65f6\u6709\u91cd\u8981\u5ba2\u4eba\u8981\u6765\uff0c\u6211\u62c5\u5fc3\u62bd\u4e0d\u51fa\u8eab\u5b50\uff0c\u6240\u4ee5\uff0c\u5077\u5f97\u8fd9\u534a\u5bb5\u8fb0\u5149\uff0c\u5306\u5306\u6765\u53bb\u2026\u2026\u201d \u3000\u3000\u201c\u4ec0\u4e48\u6837\u7684\u5ba2\u4eba\uff1f\u662f\u738b\u5b59\u516c\u5b50\uff0c\u8fd8\u662f\u957f\u5b89\u57ce\u4e2d\u7684\u5927\u8c6a\u3001\u540d\u7ec5\uff1f\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u662f\u90a3\u4e9b\u4eba\u2026\u2026\u201d\u6885\u82b1\u867d\u7136\u6781\u5c3d\u5c0f\u5fc3\uff0c\u4f46\u5fc3\u4e3a\u60c5\u7275\uff0c\u4e0d\u81ea\u89c9\u95f4\u6cc4\u6f0f\u4e86\u9690\u5bc6\uff0c\u9053\uff1a\u201c\u662f\u6211\u7684\u5e08\u95e8\u4e2d\u4eba\uff0c\u6211\u4e0d\u80fd\u6709\u4e1d\u6beb\u5730\u758f\u5ffd\uff0c\u6211\u77e5\u9053\u8fd9\u5f88\u59d4\u5c48\u4f60\uff0c\u4f46\u4e8b\u540e\uff0c\u6211\u4f1a\u5c3d\u529b\u8865\u507f\u3002\u201d\u4f0f\u8eab\u4e00\u543b\uff0c\u7834\u7a97\u800c\u53bb\u3002 \u3000\u3000\u8d70\u7684\u5306\u5306\uff0c\u8fde\u5f00\u95e8\u7684\u65f6\u95f4\u5c31\u7b49\u4e0d\u53ca\u4e86\u3002 \u3000\u3000\u6c5f\u67ab\u5df2\u5fc3\u4e2d\u660e\u767d\uff0c\u4eca\u5348\u6709\u5185\u5bab\u4e00\u7cfb\u4e2d\u8981\u4eba\u8d76\u6765\uff0c\u6d1b\u9633\u5c45\u4e2d\u7684\u706b\u62fc\u5c40\u9762\uff0c\u56e0\u9752\u51e4\u51fa\u9762\u5236\u6b62\u800c\u6682\u606f\u5e72\u6208\uff0c\u4f46\u5185\u5bab\u4e00\u7cfb\u4e2d\u4eba\uff0c\u80af\u5426\u7f62\u624b\uff0c\u4eca\u5929\u624d\u80fd\u4f5c\u6700\u540e\u7684\u51b3\u5b9a\u3002 \u3000\u3000\u5185\u5bab\u8981\u4eba\u6765\u7684\u5982\u6b64\u4e4b\u5feb\uff0c\u5929\u9a6c\u603b\u5802\u5462\uff1f \u3000\u3000\u5982\u679c\u5929\u9a6c\u603b\u5802\u65e9\u5df2\u6d3e\u4eba\u5728\u6697\u4e2d\u76d1\u89c6\u5185\u5bab\u4e00\u7cfb\u4e2d\u4eba\u7269\u7684\u4e3e\u52a8\uff0c\u7528\u4e0d\u7740\u9093\u98de\u7684\u98de\u9e3d\u4f20\u62a5\uff0c\u4e5f\u4e0d\u7528\u90a3\u4f4d\u9648\u62a4\u6cd5\u56de\u53bb\u8bf4\u660e\uff0c\u5929\u9a6c\u603b\u5802\u5e94\u8be5\u65e9\u5df2\u5f97\u5230\u4e86\u6d88\u606f\uff0c\u662f\u653e\u5f03\u6d1b\u9633\u5c45\u8fd9\u5757\u5728\u5634\u7684\u80a5\u8089\uff0c\u4ee5\u907f\u514d\u6b63\u9762\u51b2\u7a81\uff1f\u6216\u662f\u589e\u6d3e\u9ad8\u624b\uff0c\u4e0d\u60dc\u5168\u529b\u4e00\u6218\uff1f \u3000\u3000\u9752\u51e4\u6240\u5c5e\u7684\u5929\u738b\u95e8\u4e00\u7cfb\uff0c\u662f\u5426\u4f1a\u5168\u9762\u5377\u5165\u8fd9\u573a\u51b2\u7a81\uff1f\u6216\u662f\u8896\u624b\u65c1\u89c2\uff0c\u7b49\u7740\u5750\u6536\u6e14\u5229\uff0c\u5982\u679c\uff0c\u8fd9\u4e2a\u7ec4\u5408\u4e2d\u8fd8\u6709\u66f4\u9ad8\u7684\u4eba\u7269\uff0c\u4f1a\u4e0d\u4f1a\u51fa\u9762\u963b\u6b62\uff0c\u4ece\u4e2d\u8c03\u89e3\u2026\u2026\u3002 \u3000\u3000\u4e56\u4e56\uff0c\u597d\u620f\u8fde\u53f0\uff0c\u4e5f\u8bb8\u8fd9\u4e2a\u795e\u79d8\u5927\u7ec4\u5408\u4e2d\u5e55\u540e\u4eba\u7269\u90fd\u5c06\u63d2\u624b\u8fd9\u6b21\u7684\u7eb7\u4e89\uff0c\u4e00\u4e00\u663e\u9732\u51fa\u6765\uff0c\u81f3\u5c11\uff0c\u4e5f\u8be5\u4f1a\u9732\u51fa\u4e00\u70b9\u7709\u76ee\uff0c\u7ed9\u4eba\u8ffd\u7d22\u2026\u2026\u3002 \u3000\u3000\u60f3\u5230\u6b64\u5904\uff0c\u6c5f\u67ab\u4e5f\u7761\u4e0d\u5b89\u7a33\u4e86\uff0c\u7a7f\u597d\u8863\u670d\uff0c\u5306\u5306\u79bb\u53bb\u3002 \u3000\u3000\u4ed6\u6ca1\u6709\u56de\u5230\u6d1b\u9633\u5c45\uff0c\u5374\u8d76\u5230\u4e86\u98ce\u96ea\u5c0f\u9986\u3002 \u3000\u3000\u770b\u5929\u8272\u5c1a\u672a\u5927\u4eae\uff0c\u5f20\u56db\u59d1\u5df3\u77e5\u9053\u6709\u8981\u4e8b\u76f8\u5546\uff0c\u7acb\u523b\u53eb\u8d77\u4e86\u6b27\u9633\u662d\uff0c\u7f57\u5170\u592b\u5987\u3002 \u3000\u3000\u4f46\u6700\u5148\u8fdb\u5165\u5385\u4e2d\u7684\uff0c\u5374\u662f\u96ea\u3001\u971c\u4e8c\u5973\u3002 \u3000\u3000\u201c\u6c5f\u5927\u54e5\u679c\u7136\u662f\u8a00\u800c\u6709\u4fe1\uff0c\u4e00\u5927\u65e9\u5c31\u6765\u63a5\u6211\u4eec\u4e86\u3002\u201d\u541f\u96ea\u5fae\u7b11\u5982\u82b1\u5730\u8bf4\uff0c\u201c\u8981\u6211\u4eec\u505a\u4ec0\u4e48\uff1f\u5c3d\u7ba1\u5429\u5490\uff01\u201d \u3000\u3000\u201c\u6c34\u91cc\u6c34\u4e2d\u53bb\uff0c\u706b\u91cc\u706b\u4e2d\u884c\uff0c\u201d\u541f\u971c\u63a5\u9053\uff1a\u201c\u53ea\u8981\u4f60\u6c5f\u5927\u54e5\u8bf4\u4e00\u58f0\uff0c\u6211\u4eec\u5c31\u5168\u529b\u4ee5\u8d74\uff0c\u7edd\u4e0d\u63a8\u6258\u3002\u201d \u3000\u3000\u4e0d\u518d\u523b\u610f\u9690\u85cf\u5929\u72d0\u6280\u827a\uff0c\u4e24\u4e2a\u5c71\u5c45\u5341\u4f59\u5e74\u7684\u7eaf\u6d01\u5c11\u5973\uff0c\u4f3c\u4e4e\u7a81\u7136\u95f4\u53d8\u4e86\u6837\u5b50\uff0c\u8a00\u7b11\u4e4b\u95f4\uff0c\u5a9a\u6001\u6a2a\u751f\uff0c\u90a3\u79cd\u64a9\u4eba\u7684\u5a07\u4fcf\uff0c\u548c\u9752\u51e4\u3001\u6885\u82b1\u53c8\u81ea\u4e0d\u540c\uff0c\u6709\u5982\u5e7d\u5170\u9001\u9999\uff0c\u751c\u9152\u9189\u4eba\uff0c\u5929\u72d0\u6280\u827a\uff0c\u679c\u7136\u66f4\u9002\u5408\u5973\u5b50\u4e60\u7ec3\uff0c\u770b\u5f97\u6c5f\u67ab\u4e5f\u4e0d\u7981\u4e3a\u4e4b\u4e00\u5446\uff0c\u5fd6\u9053\uff1a\u201c\u8fd9\u4e24\u4e2a\u4e2b\u5934\uff0c\u518d\u7ecf\u5386\u7ec3\uff0c\u653e\u80c6\u65bd\u4e3a\uff0c\u53ea\u6015\u4e0d\u8ba9\u5929\u72d0\u592b\u4eba\u4e13\u7f8e\u4e8e\u524d\uff0c\u5f53\u4e16\u82f1\u96c4\uff0c\u80fd\u6709\u51e0\u4eba\u4e0d\u62dc\u670d\u5728\u77f3\u69b4\u88d9\u4e0b\uff0c\u90a3\u771f\u8981\u5929\u4e0b\u5927\u4e71\u4e86\uff0c\u4f46\u613f\u5979\u4eec\u80fd\u6536\u653e\u81ea\u5982\uff0c\u9690\u655b\u950b\u8292\u2026\u2026\u201d \u3000\u3000\u53ea\u542c\u4e00\u4e2a\u77eb\u8106\u7684\u58f0\u97f3\uff0c\u559d\u9053\uff1a\u201c\u4e0d\u77e5\u5929\u9ad8\u5730\u539a\u7684\u4e2b\u5934\uff0c\u4f60\u4eec\u6c5f\u5927\u54e5\u662f\u4f55\u7b49\u4eba\u7269\uff1f\u7adf\u6562\u73ed\u95e8\u5f04\u65a7\uff0c\u5c55\u73b0\u98ce\u60c5\uff0c\u4e0d\u6015\u60f9\u706b\u711a\u8eab\uff0c\u81ea\u8ba8\u82e6\u5403\u5417\uff1f\u201d\u7f57\u5170\u5d29\u7740\u4e00\u5f20\u8138\uff0c\u5feb\u6b65\u884c\u5165\u5385\u4e2d\u3002 \u3000\u3000\u201c\u60f3\u4e0d\u5230\u554a\uff01\u4e24\u4f4d\u59d1\u5a18\u5df2\u5f97\u5929\u72d0\u592b\u4eba\u7684\u771f\u4f20\u2026\u2026\u201d\u6c5f\u67ab\u6574\u8083\u795e\u60c5\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u4e0d\u77e5\u5979\u4eec\u662f\u5426\u5df2\u5230\u4e86\u81ea\u6211\u655b\u6536\u7684\u5883\u754c\u4e86\uff1f\u201d \u3000\u3000\u201c\u5e94\u8be5\u53ef\u4ee5\uff0c\u5979\u4eec\u4e60\u7ec3\u5929\u72d0\u7684\u6b66\u827a\uff0c\u5df2\u6709\u5341\u5e74\u4ee5\u4e0a\u7684\u65f6\u95f4\u2026\u2026\u201d\u7f57\u5170\u8bf4\uff0c\u201c\u6211\u867d\u7136\u6781\u5c3d\u5c0f\u5fc3\u7684\u8f6c\u6388\u5979\u4eec\u7684\u53e3\u8bc0\uff0c\u4e5f\u51e0\u4e4e\u6cbe\u67d3\u4e86\u4e00\u8eab\u72d0\u6c14\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u662f\u51e0\u4e4e\uff0c\u800c\u662f\u5df2\u7ecf\u6709\u4e86\u2026\u2026\u201d\u6b27\u9633\u662d\u5927\u7b11\u800c\u5165\u3002 \u3000\u3000\u201c\u6211\u53ef\u662f\u6ca1\u6709\u7ec3\u8fc7\uff0c\u201d\u7f57\u5170\u9053\uff1a\u201c\u6709\u4e9b\u6b66\u529f\u548c\u5a9a\u6001\u7ed3\u5408\uff0c\u6211\u5728\u4f20\u6388\u5979\u4eec\u7684\u65f6\u5019\uff0c\u90fd\u662f\u628a\u773c\u775b\u95ed\u8d77\u6765\u7684\u3002\u201d \u3000\u3000\u201c\u5e26\u4e09\u5206\u5a07\u5a9a\u60c5\u6001\uff0c\u624d\u628a\u6211\u6574\u6cbb\u5f97\u670d\u670d\u8d34\u8d34\u5440\uff01\u2026\u2026\u201d\u6b27\u9633\u662d\u7b11\u9053\uff1a\u201c\u6211\u53ef\u662f\u4ece\u6ca1\u6709\u53cd\u5bf9\u8fc7\u4f60\u7ec3\u5929\u72d0\u6b66\u529f\uff01\u201d \u3000\u3000\u201c\u6211\u770b\u4f60\u5012\u5df2\u7ecf\u662f\u6ee1\u8eab\u7684\u72d0\u6c14\u4e86\u2026\u2026\u201d\u7f57\u5170\u53f9\u9053\uff1a\u201c\u4f60\u4ee5\u4e3a\u6211\u4e0d\u60f3\u7ec3\u54ea\uff01\u6211\u662f\u6015\u8001\u516c\u5403\u918b\uff0c\u5929\u5929\u5435\u4e2a\u6ca1\u5b8c\u2026\u2026\u201d \u3000\u3000\u201c\u5509\uff01\u6ce1\u5728\u9c7c\u6c60\u91cc\uff0c\u90a3\u80fd\u9003\u5f97\u8fc7\u6cbe\u4e0a\u8165\u6c14\u2026\u2026\u201d\u6b27\u9633\u662d\u9053\uff1a\u201c\u4f60\u6ca1\u6709\u4e60\u7ec3\uff0c\u4f46\u4f60\u4f20\u6388\u4e24\u4e2a\u4e2b\u5934\u5341\u51e0\u5e74\u5929\u72d0\u795e\u529f\uff0c\u4f60\u6562\u8bf4\uff0c\u5168\u90fd\u628a\u5b83\u5fd8\u53bb\u4e86\uff1f\u201d \u3000\u3000\u201c\u8bf4\u7684\u4e5f\u662f\u554a\uff01\u201d\u7f57\u5170\u7b11\u9053\uff1a\u201c\u5929\u72d0\u795e\u529f\u592a\u8bf1\u4eba\u4e86\uff0c\u60f3\u5fd8\u4e86\u5b83\uff0c\u8c08\u4f55\u5bb9\u6613\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u8f6c\u5934\u770b\u53bb\uff0c\u53ea\u89c1\u96ea\u3001\u971c\u4e8c\u5973\uff0c\u8083\u5bb9\u800c\u7acb\uff0c\u4e00\u6d3e\u7aef\u5e84\uff0c\u5fc3\u4e2d\u5927\u611f\u5feb\u6170\uff0c\u70b9\u70b9\u5934\uff0c\u9053\uff1a \u3000\u3000\u201c\u597d\uff01\u4e24\u4e2a\u59d1\u5a18\u5df2\u80fd\u6536\u653e\u968f\u5fc3\uff0c\u4e00\u5207\u5c31\u64cd\u4e4b\u5728\u5df1\uff0c\u5ac2\u592b\u4eba\u679c\u7136\u662f\u6559\u5bfc\u6709\u65b9\u4e86\u3002\u201d \u3000\u3000\u201c\u8001\u5f1f\uff0c\u5929\u672a\u4eae\u5c31\u8d76\u6765\u98ce\u96ea\u5c0f\u9986\uff0c\u201d\u6b27\u9633\u662d\u4f4e\u58f0\u9053\uff1a\u201c\u60f3\u5fc5\u6709\u91cd\u5927\u4e8b\u60c5\u4e86\uff1f\u201d \u3000\u3000\u5f20\u56db\u59d1\u624b\u6367\u6728\u76d2\uff0c\u4e0a\u9762\u653e\u7740\u516d\u7897\u51b0\u7cd6\u71d5\u7a9d\u6c64\uff0c\u63a5\u9053\uff1a\u201c\u6765\uff01 \u3000\u3000\u8fb9\u5403\u8fb9\u8c08\u5427\uff01\u201d \u3000\u3000\u6b27\u9633\u662d\u7b11\u9053\uff1a\u201c\u6c5f\u8001\u5f1f\uff0c\u6258\u4f60\u7684\u798f\u554a\uff01\u4e00\u5927\u65e9\u5c31\u5403\u5230\u8fd9\u79cd\u53ef\u53e3\u7f8e\u6627\u2026\u2026\u201d\u5f53\u5148\u53d6\u8fc7\u4e00\u7897\u3002 \u3000\u3000\u6c5f\u67ab\u5fae\u5fae\u4e00\u7b11\uff0c\u53cc\u624b\u53d6\u8fc7\u7897\u5319\uff0c\u9053\uff1a\u201c\u5927\u59d0\uff0c\u9ebb\u70e6\u4f60\u4e86\uff01\u201d \u3000\u3000\u5f20\u56db\u59d1\u53ec\u8fc7\u4e8c\u5973\u5165\u5ea7\uff0c\u624d\u7f13\u7f13\u5750\u4e0b\uff0c\u7b11\u9053\uff1a\u201c\u7f57\u5170\uff0c\u4f60\u8fd9\u4e2a\u8001\u516c\u53e3\u5fb7\u4e0d\u4fee\uff0c\u5f53\u5fc3\u4ed6\u5fc3\u6000\u4e0d\u8f68\u5440\uff01\u201d \u3000\u3000\u201c\u6ca1\u6cd5\u5b50\u554a\uff01\u72d0\u59bb\u72d0\u5973\uff0c\u6211\u5c31\u662f\u60f3\u7aef\u4e5f\u7aef\u4e0d\u8d77\u6765\u5440\uff01\u201d \u3000\u3000\u6b27\u9633\u662d\u9053\uff1a\u201c\u6c5f\u8001\u5f1f\uff0c\u6211\u5012\u60f3\u8bf7\u6559\u4e00\u4e0b\uff0c\u4f60\u662f\u5929\u72d0\u592b\u4eba\u7684\u5ae1\u4f20\u5f1f\u5b50\uff0c\u4f60\u4eec\u5e08\u751f\u4e4b\u95f4\uff0c\u6388\u53d7\u6b66\u529f\u65f6\uff0c\u662f\u4e0d\u662f\u548c\u4e00\u822c\u7684\u5e08\u5f92\u4e00\u6837\uff0c\u5e08\u4e25\u5f92\u606d\uff0c\u8083\u7136\u53d7\u6559\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u5fc3\u4e2d\u660e\u767d\uff0c\u6b27\u9633\u662d\u8fd9\u7b49\u653e\u8361\u4e0d\u7f81\uff0c\u8a00\u7b11\u8f7b\u6d6e\uff0c\u662f\u5728\u89e3\u9664\u81ea\u5df1\u5fc3\u4e2d\u7684\u538b\u529b\uff0c\u8bd5\u60f3\u4e00\u4e2a\u7537\u4eba\uff0c\u5c24\u5176\u662f\u81ea\u8d1f\u82f1\u96c4\u7684\u7537\u4eba\uff0c\u59bb\u5996\u5973\u5a9a\uff0c\u503e\u60d1\u4f17\u751f\uff0c\u8981\u4ed6\u5e73\u5fc3\u9759\u6c14\u5730\u63a5\u53d7\u8fd9\u4e2a\u4e8b\u5b9e\uff0c\u5fc3\u6e56\u4e0d\u6ce2\uff0c\u662f\u4f55\u7b49\u8270\u6da9\u3001\u75db\u82e6\uff0c\u4ed6\u5728\u8ffd\u5bfb\u5404\u79cd\u7406\u7531\uff0c\u4ee5\u9002\u5e94\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u6240\u4ee5\uff0c\u95ee\u7684\u95ee\u9898\uff0c\u90fd\u5f88\u5c16\u9510\u3002 \u3000\u3000\u6709\u4e86\u8fd9\u4e2a\u8c05\u89e3\uff0c\u6c5f\u67ab\u5f88\u5e73\u9759\uff0c\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e0d\u540c\u4e8e\u4e00\u822c\u5e08\u5f92\u4e4b\u95f4\uff0c\u56e0\u4e3a\uff0c\u5929\u72d0\u7684\u6b66\u529f\uff0c\u529b\u6c42\u7f8e\u5316\uff0c\u592a\u4e25\u8083\u5c31\u683c\u683c\u4e0d\u5165\u4e86\uff0c\u4f55\u51b5\uff0c\u5a9a\u6001\u64a9\u4eba\uff0c\u5de7\u7b11\u60c5\u516e\uff0c\u4e5f\u662f\u5929\u72d0\u95e8\u6280\u827a\u7684\u7cbe\u534e\uff0c\u8fd0\u5251\u4e8e\u79cb\u6ce2\u4e00\u8f6c\u4e4b\u95f4\uff0c\u51fa\u638c\u4e8e\u56de\u7738\u4e00\u7b11\u4e4b\u4e2d\uff0c\u6625\u98ce\u4fcf\u6b65\u91cc\u8574\u85cf\u6740\u673a\uff0c\u5a9a\u773c\u8f7b\u629b\u4e2d\u8ffd\u9b42\u53d6\u547d\u3002\u6211\u5728\u4e60\u7ec3\u8fd9\u4e9b\u6b66\u529f\u65f6\uff0c\u8fd8\u8981\u5b66\u8f7b\u76c8\u821e\u6b65\uff0c\u6446\u52a8\u8170\u8eab\uff0c\u90a3\u4e2a\u82e6\u5c31\u5403\u7684\u5927\u4e86\u3002\u201d \u3000\u3000\u201c\u90a3\u53ef\u771f\u662f\u96be\u4e3a\u4f60\u4e86\uff0c\u6c5f\u5144\u5f1f\uff0c\u4e00\u4e2a\u5927\u7537\u4eba\uff0c\u8981\u7ec3\u51fa\u6625\u98ce\u4fcf\u6b65\u2026\u2026\u201d\u6b27\u9633\u662d\u9053\uff1a\u201c\u53ef\u771f\u662f\u4e0d\u5bb9\u6613\u5440\uff01\u201d \u3000\u3000\u201c\u597d\u4e86\u5427\uff01\u4f60\u5fc3\u618b\u4e86\u5341\u51e0\u5e74\uff0c\u4e00\u76f4\u60f3\u95ee\u6e05\u695a\u8fd9\u4ef6\u4e8b\u60c5\u2026\u2026\u201d \u3000\u3000\u7f57\u5170\u9053\uff1a\u201c\u4eca\u65e5\u603b\u7b97\u5fc3\u613f\u5f97\u507f\uff0c\u73b0\u5728\uff0c\u8be5\u8ba9\u6c5f\u5144\u5f1f\u8c08\u8c08\u6b63\u7ecf\u4e8b\u4e86\u3002\u201d \u3000\u3000\u201c\u5bf9\uff01\u2026\u2026\u201d\u6b27\u9633\u662d\u9053\uff1a\u201c\u6211\u80f8\u4e2d\u65e9\u65e0\u5757\u5792\uff0c\u6c5f\u8001\u5f1f\u4e00\u756a\u8bdd\uff0c\u66f4\u4f7f\u6211\u80f8\u6000\u5927\u5f00\uff0c\u4e2b\u5934\u4eec\u653e\u80c6\u65bd\u4e3a\u5427\uff01\u4f60\u4eec\u80fd\u98ce\u9761\u6c5f\u6e56\u8001\u7236\u4e5f\u4e0e\u6709\u8363\u7109\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u9053\uff1a\u201c\u6267\u5927\u4e49\uff0c\u4e0d\u62d8\u5c0f\u8282\uff0c\u4f60\u80fd\u60f3\u5f97\u5f00\uff0c\u624d\u662f\u8c6a\u6770\u80f8\u6000\uff0c\u6c5f\u67ab\uff0c\u8bf4\u5427\uff01\u4f60\u4e00\u65e9\u8dd1\u6765\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u5341\u4e07\u706b\u6025\uff0c\u5426\u5219\uff0c\u5c0f\u5f1f\u4e5f\u4e0d\u6562\u4e00\u5927\u65e9\u6765\u60ca\u52a8\u8bf8\u4f4d\u4e86\u3002\u201d \u3000\u3000\u201c\u8bf4\u7684\u9178\u6c14\u51b2\u5929\uff0c\u5168\u7136\u4e0d\u6cbe\u72d0\u6c14\uff0c\u201d\u6b27\u9633\u662d\u9053\uff1a\u201c\u6211\u4eec\u90fd\u5728\u606d\u5019\u5dee\u9063\uff0c\u4f60\u5c31\u4e0d\u7528\u5ba2\u6c14\uff01\u201d \u3000\u3000\u6c5f\u67ab\u4ed4\u7ec6\u7684\u8bf4\u660e\u4e86\u4e8b\u60c5\u7684\u7ecf\u8fc7\u3002 \u3000\u3000\u201c\u6c5f\u8001\u5f1f\uff0c\u679c\u7136\u662f\u624d\u534e\u975e\u51e1\uff0c\u77ed\u77ed\u51e0\u4e2a\u6708\uff0c\u5c31\u8ba9\u4ed6\u4eec\u7a9d\u91cc\u53cd\uff0c\u5200\u5175\u76f8\u89c1\uff0c\u201d\u6b27\u9633\u662d\u8c6a\u6c14\u624b\u4e91\u5730\u8bf4\uff0c\u201c\u73b0\u5728\uff0c\u54b1\u4eec\u662f\u4e0d\u662f\u8d81\u673a\u6740\u51fa\uff0c\u4e00\u4e3e\u51fb\u57ae\u6d1b\u9633\u5c45\uff1f\u201d \u3000\u3000\u201c\u6d1b\u9633\u5c45\u8fd8\u8981\u4fdd\u5b58\uff0c\u8ba9\u4ed6\u4eec\u9ad8\u624b\u5c3d\u51fa\uff0c\u5728\u90a3\u91cc\u5168\u529b\u5bf9\u51b3\uff0c\u5f53\u7136\uff0c\u54b1\u4eec\u8981\u5750\u4e2d\u70b9\u62e8\uff0c\u4f7f\u4ed6\u4eec\u6b32\u52a8\u4e0d\u80fd\uff0c\u6478\u6e05\u695a\u4ed6\u4eec\u5e55\u540e\u9996\u8111\uff0c\u518d\u4f5c\u81f4\u547d\u4e00\u51fb\u2026\u2026\u201d\u6c5f\u67ab\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u4eba\u624b\u592a\u5c11\uff0c\u529b\u91cf\u4e0d\u5927\uff0c\u65e0\u6cd5\u5168\u9762\u53cd\u51fb\uff0c\u53ea\u6709\u64d2\u8d3c\u5148\u64d2\u738b\u4e86\uff0c\u4f46\u654c\u52bf\u5e9e\u5927\uff0c\u975e\u540c\u5c0f\u53ef\uff0c\u6240\u4ee5\uff0c\u5c0f\u5f1f\u60f3\u5148\u505a\u4e00\u4e9b\u5b89\u6392\uff0c\u4e00\u65b9\u9762\u52a0\u6df1\u4ed6\u4eec\u7684\u88c2\u75d5\uff0c\u8ba9\u4ed6\u4eec\u6325\u6208\u81ea\u6b8b\uff0c\u4e00\u9762\u4fdf\u673a\u72d9\u6740\u4ed6\u4eec\u7684\u91cd\u8981\u4eba\u7269\uff0c\u6563\u5e03\u7591\u4e91\u2026\u2026\u201d \u3000\u3000\u201c\u542c\u8d77\u6765\uff0c\u4f60\u5df2\u7ecf\u80f8\u6709\u6210\u7af9\u4e86\uff0c\u201d\u7f57\u5170\u8bf4\uff0c\u201c\u53ef\u5426\u628a\u8ba1\u5212\u8bf4\u51fa\u6765\uff0c\u6211\u4eec\u5168\u529b\u914d\u5408\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u654c\u4eba\u5b9e\u529b\uff0c\u5f3a\u6211\u767e\u500d\uff0c\u4ee5\u5be1\u654c\u4f17\uff0c\u667a\u8c0b\u5f53\u5148\uff0c\u4f46\u80fd\u5426\u6210\u529f\uff0c\u5c0f\u5f1f\u4e0d\u6562\u9884\u8a00\uff0c\u53ea\u6709\u5c3d\u6211\u4e4b\u529b\uff0c\u51b3\u4e0d\u8f9c\u8d1f\u51e0\u4f4d\u5e08\u957f\u7684\u671f\u671b\u3001\u91cd\u6258\uff0c\u662f\u6210\u662f\u8d25\uff0c\u53ea\u6709\u59d4\u8bf8\u5929\u547d\u4e86\u3002\u201d\u8bf4\u51fa\u4e86\u4e00\u756a\u5e03\u7f72\u8ba1\u5212\u3002 \u3000\u3000\u201c\u597d\u8ba1\u5212\uff0c\u96ea\u3001\u971c\u4e24\u4e2a\u4e2b\u5934\uff0c\u5012\u662f\u5f88\u9002\u5408\u6267\u884c\u521d\u6b65\u8ba1\u5212\uff0c\u4f60\u5c31\u5e26\u7740\u5979\u4eec\u53bb\u5427\u2026\u2026\u201d\u6b27\u9633\u662d\u9053\uff1a\u201c\u6211\u4eec\u4f1a\u5c3d\u5feb\u548c\u94b1\u7f3a\u8fde\u7edc\uff0c\u968f\u65f6\u5019\u547d\u3002\u201d \u3000\u3000\u4ed6\u51c6\u5907\u4e00\u5bb6\u4eba\u90fd\u5168\u529b\u6295\u5165\u8fd9\u573a\u6c5f\u6e56\u5927\u51b3\u6218\u4e2d\uff0c\u751f\u6b7b\u8363\u8fb1\uff0c\u5728\u6240\u4e0d\u8ba1\u4e86\u3002 \u3000\u3000\u6c5f\u67ab\u76ee\u5149\u8f6c\u6ce8\u5230\u5f20\u56db\u59d1\u7684\u8eab\u4e0a\uff0c\u9053\uff1a\u201c\u5927\u59d0\uff0c\u5c0f\u5f1f\u7684\u8ba1\u5212\uff0c\u5b89\u6392\u5e03\u7f6e\u7684\u4e8b\uff0c\u6709\u52b3\u4f60\u51fa\u9a6c\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u597d\uff01\u6211\u7acb\u523b\u53bb\u627e\u4e2a\u9002\u5f53\u7684\u6240\u5728\uff0c\u52a8\u624b\u5e03\u7f6e\u3002\u201d\u5f20\u56db\u59d1\u6709\u6c42\u5fc5\u5e94\u7684\u8bf4\u3002 \u3000\u3000\u201c\u5927\u59d0\uff0c\u5c0f\u5f1f\u7684\u610f\u601d\u662f\u2026\u2026\u201d\u6c5f\u67ab\u8bf4\uff0c\u201c\u7531\u4f60\u5e26\u9886\u7740\u96ea\u3001\u971c\u4e24\u4f4d\u59d1\u5a18\u5750\u9547\u5728\u90a3\u91cc\uff0c\u98ce\u96ea\u5c0f\u9986\u7684\u4e8b\uff0c\u5c31\u59d4\u6258\u6b27\u9633\u5144\u5ac2\u627f\u62c5\u4e86\u3002\u201d \u3000\u3000\u201c\u597d\u554a\uff01\u6211\u8fd9\u628a\u5e74\u7eaa\u4e86\uff0c\u4f60\u7adf\u7136\u8981\u6211\u5e26\u7740\u4e24\u4e2a\u4e2b\u5934\u53bb\u5356\u5f04\u98ce\u60c5\u2026\u2026\u201d\u5f20\u56db\u59d1\u6709\u4e9b\u751f\u6c14\u7684\u8bf4\uff0c\u201c\u5927\u59d0\u8fd9\u4e00\u751f\uff0c\u9664\u4e86\u4f60\u6c5f\u67ab\u4e4b\u5916\uff0c\u4ece\u6ca1\u6709\u8ba9\u7537\u4eba\u78b0\u8fc7\u4e00\u4e0b\uff0c\u6211\u2026\u2026\u201d\u5fc3\u91cc\u4e00\u6025\uff0c\u8fde\u85cf\u5728\u5fc3\u91cc\u7684\u79d8\u5bc6\u4e5f\u6296\u51fa\u6765\u4e86\u3002 \u3000\u3000\u8bdd\u51fa\u53e3\uff0c\u624d\u89c9\u7740\u592a\u8fc7\u9732\u9aa8\uff0c\u4e00\u5f20\u8138\u987f\u6cdb\u7f9e\u7ea2\u3002 \u3000\u3000\u6c5f\u67ab\u4e5f\u4e0d\u7981\u5fae\u73b0\u6127\u8272\uff0c\u53cc\u988a\u98de\u7ea2\u3002 \u3000\u3000\u201c\u5f20\u59e8\uff0c\u201d\u541f\u96ea\u7a81\u7136\u63d2\u5634\uff0c\u9053\uff1a\u201c\u4f60\u5e26\u6211\u4eec\u4e0b\u5c71\u5c31\u662f\u4e3a\u4e86\u5e2e\u5fd9\u6c5f\u5927\u54e5\uff0c\u5982\u4eca\u7239\u548c\u5a18\u90fd\u540c\u610f\u4e86\u6211\u4eec\u6295\u8eab\u6c5f\u6e56\uff0c\u4f60\u600e\u4e48\u80fd\u6253\u9000\u5802\u9f13\u554a\uff01\u201d \u3000\u3000\u201c\u662f\u561b\uff01\u5f20\u59e8\uff0c\u6c5f\u5927\u54e5\u4eba\u5355\u52bf\u5b64\uff0c\u4f60\u4e0d\u5e2e\u52a9\u4ed6\uff0c\u8c01\u5e2e\u4ed6\u5462\uff1f \u3000\u3000\u2026\u2026\u201d\u541f\u971c\u9053\uff1a\u201c\u518d\u8bf4\uff0c\u6211\u8ddf\u59d0\u548c\u5f20\u59e8\u5e38\u4f4f\u4e00\u8d77\uff0c\u4e5f\u53ef\u7814\u7a76\u5929\u72d0\u795e\u529f\u3002\u201d \u3000\u3000\u4e24\u4e2a\u4e2b\u5934\u6015\u5f20\u56db\u59d1\u575a\u51b3\u62d2\u7edd\uff0c\u903c\u6c5f\u67ab\u6539\u53d8\u8ba1\u5212\uff0c\u591a\u65e5\u7684\u671f\u5f85\uff0c\u5c82\u4e0d\u5316\u4f5c\u6ce1\u5f71\uff0c\u5fcd\u4e0d\u4f4f\u5f00\u53e3\u529d\u8bf4\u3002 \u3000\u3000\u541f\u971c\u4eba\u5c0f\u9b3c\u5927\uff0c\u8bb8\u8bfa\u66f4\u5177\u8bf1\u60d1\uff0c\u4ed6\u8bf4\u7814\u7a76\u5929\u72d0\u6b66\u529f\uff0c\u5176\u5b9e\u5c31\u662f\u4f20\u6388\uff0c\u660e\u767d\u7684\u544a\u8bc9\u5f20\u56db\u59d1\uff0c\u8ddf\u6211\u5355\u72ec\u4f4f\u5728\u4e00\u8d77\uff0c\u6211\u4eec\u4f20\u6388\u4f60\u65e0\u72d0\u6b66\u529f\u3002 \u3000\u3000\u5929\u72d0\u592b\u4eba\u7684\u827a\u4e1a\uff0c\u5bf9\u5973\u4eba\u6700\u6709\u5f15\u8bf1\u7684\u529b\u91cf\uff0c\u62db\u6570\u5947\u7edd\uff0c\u8fd8\u5728\u5176\u6b21\uff0c\u91cd\u8981\u7684\u662f\u5b83\u80fd\u4f7f\u9752\u6625\u5e38\u9a7b\uff0c\u5f20\u56db\u59d1\u6026\u7136\u5fc3\u52a8\u3002 \u3000\u3000\u201c\u56db\u59d1\uff0c\u6211\u770b\u4f60\u5f88\u9002\u5e94\u4e60\u7ec3\u5929\u72d0\u6b66\u529f\uff0c\u4f60\u5b66\u7684\u4e0d\u662f\u6b63\u7edf\u6b66\u5b66\uff0c\u548c\u5929\u72d0\u6280\u827a\uff0c\u9887\u591a\u8fd1\u4f3c\uff0c\u5b66\u8d77\u4e8b\u534a\u529f\u500d\u2026\u2026\u201d\u7f57\u5170\u7b11\u9053\uff1a\u201c\u5929\u72d0\u6b66\u529f\uff0c\u8868\u9762\u4e0a\u51b6\u8361\u5996\u5a9a\uff0c\u98ce\u9a9a\u5165\u9aa8\uff0c\u4f46\u5185\u5728\u7684\u5374\u662f\u5e84\u4e25\u7684\uff0c\u4e0d\u4e60\u7ec3\u8fd9\u79cd\u6b66\u529f\uff0c\u65e0\u6cd5\u4f53\u4f1a\u2026\u2026\u201d \u3000\u3000\u201c\u7f57\u5170\uff0c\u6211\u53ef\u4ee5\u7ec3\u5417\uff1f\u201d\u5f20\u56db\u59d1\u6709\u4e9b\u6000\u7591\u4ed6\u8bf4\uff1a\u201c\u96be\u9053\u5929\u72d0\u95e8\u6ca1\u6709\u963b\u6b62\u3002\u201d \u3000\u3000\u201c\u5929\u72d0\u592b\u4eba\u4f20\u6211\u5404\u79cd\u53e3\u8bc0\u65f6\uff0c\u6ca1\u6709\u544a\u8bc9\u6211\u6709\u4ec0\u4e48\u9650\u5236\uff0c\u201d \u3000\u3000\u7f57\u5170\u8bf4\uff0c\u201c\u5c31\u7b97\u662f\u6709\uff0c\u4f46\u6211\u4e0d\u77e5\u9053\uff0c\u5c31\u7b49\u4e8e\u6ca1\u6709\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u2026\u2026\u201d\u5f20\u56db\u59d1\u72b9\u8c6b\u5730\u8bf4\uff1a\u201c\u6211\u5982\u4e0d\u662f\u5b88\u7740\u5e08\u95e8\u89c4\u6212\uff0c\u65e9\u5c31\u628a\u4e94\u884c\u9041\u672f\u4f20\u7ed9\u541f\u96ea\u3001\u541f\u971c\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u5927\u59d0\uff0c\u4e0d\u7528\u8003\u8651\u8fd9\u4ef6\u4e8b\uff0c\u5929\u72d0\u95e8\u7684\u6280\u827a\u9002\u4eba\u800c\u4f20\uff0c\u5927\u59d0\u5f88\u9002\u5408\u7ec3\u4e60\u5929\u72d0\u95e8\u7684\u6b66\u529f\u2026\u2026\u201d \u3000\u3000\u201c\u53ef\u6076\u2026\u2026\u201d\u5f20\u56db\u59d1\u6253\u65ad\u4e86\u6c5f\u67ab\u7684\u8bdd\uff0c\u63a5\u9053\uff1a\u201c\u90a3\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u65e9\u4f20\u7ed9\u6211\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5176\u5b9e\uff0c\u5927\u59d0\u65e9\u5c31\u5f00\u59cb\u5b66\u4e86\uff0c\u53ea\u4e0d\u8fc7\u5927\u59d0\u4e0d\u77e5\u9053\u90a3\u662f\u5929\u72d0\u95e8\u7684\u6b66\u529f\u7f62\u4e86\u3002\u201d \u3000\u3000\u5f20\u56db\u7ad9\u60f3\u4e86\u4e00\u4e0b\uff0c\u7a81\u7136\u611f\u89c9\u5230\u8138\u4e0a\u53d1\u70ed\uff0c\u4f46\u5fc3\u4e2d\u5374\u6709\u4e1d\u4e1d\u751c\u610f\uff0c\u5fd6\u9053\uff1a\u201c\u539f\u6765\u4ed6\u5bf9\u6211\u60c5\u4e49\u751a\u91cd\uff0c\u65e9\u5df2\u6697\u628a\u5929\u72d0\u95e8\u6b66\u529f\u4f20\u6388\u7ed9\u6211\uff0c\u6211\u7adf\u662f\u5168\u7136\u4e0d\u77e5\u3002\u201d\u5f53\u4e0b\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u597d\u561b\uff01 \u3000\u3000\u53bb\u5c31\u53bb\u5427\uff01 \u3000\u3000\u53cd\u6b63\u5927\u59d0\u8fd9\u4e00\u8f88\u5b50\u662f\u88ab\u4f60\u5403\u5b9a\u4e86\u3002\u201d \u3000\u3000\u201c\u7f57\u5170\uff0c\u201d\u6b27\u9633\u662d\u5f00\u4e86\u53e3\u9053\uff1a\u201c\u5929\u72d0\u6b66\u529f\uff0c\u65e2\u6709\u8fd9\u4e48\u591a\u597d\u5904\uff0c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u7ec3\u5462\uff1f\u201d \u3000\u3000\u201c\u6211\uff0c\u2026\u2026\u201d\u7f57\u5170\u60ca\u559c\u5730\u671b\u7740\u4e08\u592b\uff0c\u9053\uff1a\u201c\u6211\u80fd\u7ec3\u5417\uff1f\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u4e0d\u80fd\u3002\u6211\u90fd\u540c\u610f\u4e86\uff0c\u8c01\u4f1a\u53cd\u5bf9\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\uff01\u6211\u7ec3\u51fa\u4e86\u4e00\u8eab\u5a9a\u6001\u5a07\u6c14\uff0c\u4f60\u4e0d\u4f1a\u751f\u6c14\u5417\uff1f\u201d \u3000\u3000\u201c\u90a3\u597d\u554a\uff01\u6211\u53ef\u4ee5\u5927\u9971\u773c\u798f\uff0c\u6700\u597d\u4e5f\u4f20\u6211\u51e0\u62db\uff0c\u6211\u4eec\u4e00\u5bb6\u4eba\u90fd\u7ec3\u4e86\u4e00\u8eab\u72d0\u6c14\uff0c\u8c01\u4e5f\u4e0d\u7528\u8bf4\u8c01\u4e86\u2026\u2026\u201d\u6b27\u9633\u662d\u8f6c\u5934\u671b\u7740\u6c5f\u67ab\uff0c\u63a5\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u6211\u53ef\u5b66\u4e48\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u53ef\u4ee5\uff0c\u4e0d\u8fc7\u5927\u5ac2\u2026\u2026\u201d\u6c5f\u67ab\u7b11\u9053\uff1a\u201c\u4f20\u6388\u5927\u54e5\u5929\u72d0\u6280\u827a\u65f6\uff0c\u53ef\u8981\u9009\u62e9\u4e00\u4e0b\uff0c\u522b\u8981\u4ed6\u7ec3\u6210\u4e86\u4e00\u8eab\u52fe\u5f15\u5973\u4eba\u7684\u672c\u9886\uff0c\u90a3\u5c31\u9ebb\u70e6\u5927\u4e86\u3002\u201d \u3000\u3000\u201c\u6211\u4e0d\u4f1a\u4f20\u4ed6\u60d1\u5fc3\u672f\uff0c\u6211\u81ea\u5df1\u4e5f\u4e0d\u7ec3\u5b83\uff0c\u201d\u7f57\u5170\u9053\uff1a\u201c\u53ea\u4f20\u4ed6\u640f\u6740\u654c\u4eba\u6b66\u529f\uff0c\u5bf9\u6297\u88ab\u5973\u4eba\u52fe\u5f15\u7684\u672c\u9886\u3002\u201d \u3000\u3000\u7f57\u5170\u5fc3\u4e2d\u660e\u767d\uff0c\u6c5f\u67ab\u501f\u7740\u8bf4\u7b11\u8bdd\u7684\u673a\u4f1a\uff0c\u6697\u4e2d\u8b66\u544a\u5979\uff0c\u5929\u72d0\u592b\u4eba\u7684\u6b66\u529f\uff0c\u6709\u5f88\u591a\u662f\u4e0d\u9002\u5b9c\u4e00\u822c\u7537\u4eba\u5b66\u7684\uff0c\u7acb\u523b\u8868\u767d\u4e86\u5fc3\u610f\u3002 \u3000\u3000\u6c5f\u67ab\u5f88\u6ee1\u610f\u7f57\u5170\u7684\u7b54\u590d\uff0c\u56de\u987e\u7740\u6b27\u9633\u662d\u9053\uff1a\u201c\u6b27\u9633\u5144\uff0c\u4f60\u5df2\u7ecf\u89c1\u8fc7\u94b1\u7f3a\u4e86\uff0c\u8fd9\u5750\u9547\u98ce\u96ea\u5c0f\u9986\uff0c\u8fde\u7edc\u516b\u65b9\u82f1\u8c6a\u7684\u4e8b\uff0c\u5c31\u7531\u8d24\u4f09\u4fea\u627f\u62c5\u4e86\u3002\u201d \u3000\u3000\u201c\u653e\u5fc3\u4e86\uff0c\u201d\u6b27\u9633\u662d\u7b11\u9053\uff1a\u201c\u6211\u548c\u8001\u53eb\u5316\u4e00\u89c1\u5982\u6545\uff0c\u8c08\u7684\u5f88\u6295\u673a\uff0c\u4f1a\u548c\u4ed6\u4fdd\u6301\u8054\u7cfb\uff0c\u4f60\u4e00\u9053\u4ee4\u8c15\u4e0b\u6765\uff0c\u6211\u4eec\u7acb\u523b\u51fa\u52a8\u3002\u201d \u3000\u3000\u201c\u5c0f\u5f1f\u627f\u60c5\u2026\u2026\u201d\u6c5f\u67ab\u76ee\u5149\u8f6c\u6ce8\u5230\u5f20\u56db\u59d1\u7684\u8138\u4e0a\uff0c\u62b1\u62f3\u4e00\u793c\uff0c\u8083\u7136\u8bf4\u9053\uff1a\u201c\u5927\u59d0\uff0c\u5c0f\u5f1f\u6b20\u4f60\u7684\u592a\u591a\uff0c\u4e0d\u77e5\u9053\u5982\u4f55\u62a5\u7b54\u4e86\uff0c\u53ea\u6709\u8bb0\u5728\u5fc3\u91cc\u4e86\u3002\u201d \u3000\u3000\u201c\u5e72\u4ec0\u4e48\u5440\uff1f\u201d\u5f20\u56db\u7ad9\u5fae\u5e26\u7f9e\u610f\u5730\u8bf4\uff1a\u201c\u770b\u4f60\u6b63\u7ecf\u516b\u767e\u7684\u6837\u5b50\uff0c\u7b80\u76f4\u4e0d\u662f\u6c5f\u67ab\u4e86\uff0c\u5b8c\u5168\u6ca1\u6709\u4e86\u72d0\u6c14\u3002\u201d \u3000\u3000\u201c\u662f\uff01\u5c0f\u5f1f\u8bb0\u4e0b\u4e86\uff0c\u4ee5\u540e\u4e0d\u518d\u6b63\u7ecf\u5c31\u662f\u2026\u2026\u201d\u8bed\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u6211\u8981\u544a\u8f9e\u4e86\uff0c\u6d1b\u9633\u5c45\u60c5\u5f62\u968f\u65f6\u6709\u53d8\uff0c\u90ed\u5929\u540c\u6050\u6015\u5e94\u4ed8\u4e0d\u4e86\u3002\u201d\u8d77\u8eab\u5411\u5916\u884c\u53bb\u3002 \u3000\u3000\u541f\u96ea\u3001\u541f\u971c\u53cc\u53cc\u79bb\u5750\uff0c\u8ddf\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u884c\u8fd1\u5927\u95e8\uff0c\u541f\u96ea\u624d\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u5bf9\u4e0d\u4f4f\u4e86\u6c5f\u5927\u54e5\uff0c\u6211\u4eec\u4e00\u770b\u5230\u4f60\u5c31\u65e0\u6cd5\u63a7\u5236\u81ea\u5df1\uff0c\u5fcd\u4e0d\u4f4f\u561b\u2026\u2026\u201d \u3000\u3000\u201c\u6c5f\u5927\u54e5\u2026\u2026\u201d\u541f\u971c\u63a5\u9053\uff1a\u201c\u6211\u4eec\u4e0d\u662f\u5c55\u73b0\u5a9a\u672f\u554a\uff01\u6211\u4eec\u662f\u771f\u60c5\u6d41\u9732\u3002\u201d\u5f53\u771f\u662f\u8d8a\u63cf\u8d8a\u9ed1\u3002 \u3000\u3000\u6c5f\u67ab\u70b9\u70b9\u5934\uff0c\u52a0\u5feb\u4e86\u811a\u6b65\uff0c\u51fa\u95e8\u800c\u53bb\u3002 \u3000\u3000\u6d1b\u9633\u5c45\u4e2d\u4e00\u7247\u5b81\u9759\uff0c\u5df2\u6ca1\u6709\u6628\u591c\u90a3\u573a\u6e85\u8840\u6a2a\u5c38\u6076\u6218\u7684\u75d5\u8ff9\uff0c\u82b1\u8272\u4f9d\u65e7\u60a6\u76ee\uff0c\u82b1\u9999\u4f9d\u65e7\u88ad\u4eba\uff0c\u2026\u2026\u4e0d\u77e5\u5b83\u662f\u5426\u77e5\u9053\uff0c\u5b83\u5373\u5c06\u5316\u4f5c\u4e4c\u6709\uff0c\u968f\u98ce\u800c\u53bb\u2026\u2026\u3002 \u3000\u3000\u4f46\u4eba\u751f\u5462\uff1f\u660e\u77e5\u6b7b\u540e\u4e07\u4e8b\u7a7a\uff0c\u4f46\u6d3b\u5728\u4e16\u4e0a\u65f6\uff0c\u5374\u653e\u4e0d\u4e0b\u6743\u52bf\u7684\u6b32\u671b\uff0c\u672c\u6765\u662f\u5145\u6ee1\u7740\u7965\u548c\u5b81\u9759\u7684\u4eba\u751f\uff0c\u5374\u56e0\u6b64\u6380\u8d77\u4e86\u8165\u98ce\u8840\u96e8\u7684\u6597\u4e89\uff0c\u5230\u5904\u662f\u6068\u3001\u5bb3\u3001\u6076\u3001\u6b32\u2026\u2026\uff0c\u5a01\u540d\u8d6b\u8d6b\u7684\u4e01\u897f\u5c71\uff0c\u6628\u5929\u8fd8\u795e\u6c14\u6d3b\u73b0\u7684\u6307\u4f7f\u9093\u98de\uff0c\u4f46\u4e00\u5915\u4e4b\u95f4\uff0c\u9ec4\u571f\u57cb\u9aa8\uff0c\u5c31\u6b64\u6d88\u5931\u2026\u2026 \u3000\u3000\u6614\u65e5\u7684\u7f8e\u4eba\u3001\u540d\u5c06\uff0c\u800c\u4eca\u5b89\u5728\uff1f\u771f\u662f\u5927\u6c5f\u4e1c\u53bb\uff0c\u6d6a\u6dd8\u5c3d\u98ce\u6d6a\u4eba\u7269\u2026\u2026 \u3000\u3000\u201c\u526f\u603b\u7ba1\u65e9\u554a\uff01\uff01\u201d\u58f0\u97f3\u8d77\u81ea\u4eca\u540e\uff0c\u4e0d\u8fc7\u6570\u5c3a\u4e4b\u9065\u3002 \u3000\u3000\u6c5f\u67ab\u5927\u5927\u5403\u4e86\u4e00\u60ca\uff0c\u6697\u9053\uff1a\u201c\u6b64\u5730\u4f55\u5730\uff0c\u6b64\u65f6\u4f55\u65f6\uff0c\u6211\u600e\u80fd\u5982\u6b64\u7684\u795e\u4e0d\u5b88\u820d\uff0c\u677e\u61c8\u8b66\u89c9\u2026\u2026\u3002\u201d \u3000\u3000\u56de\u5934\u671b\u53bb\uff0c\u53ea\u89c1\u957f\u886b\u6be1\u5e3d\u7684\u4e03\u5b9d\u548c\u5c1a\uff0c\u7ad9\u5728\u4e09\u5c3a\u4e4b\u5916\u3002 \u3000\u3000\u56db\u987e\u65e0\u4eba\uff0c\u4e03\u5b9d\u548c\u5c1a\u7a81\u7136\u8e0f\u524d\u4e24\u6b65\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u60f3\u5fc3\u4e8b\u554a\uff01\u201d \u3000\u3000\u201c\u6211\u5728\u60f3\uff0c\u5e03\u7f72\u65e2\u5b9a\uff0c\u884c\u5c06\u5c55\u5f00\u7684\uff0c\u5374\u662f\u4e00\u573a\u65e0\u60c5\u7684\u5c60\u6740\uff0c\u800c\u4e14\u662f\u4e0d\u62e9\u624b\u6bb5\uff0c\u6781\u5c3d\u5978\u8bc8\u80fd\u4e8b\u2026\u2026\u201d\u6c5f\u67ab\u82e6\u7b11\u9053\uff1a\u201c\u4eba\u751f\u5982\u6b64\uff0c\u8fd8\u6709\u4ec0\u4e48\u610f\u5473\uff1f\u201d \u3000\u3000\u201c\u8bf4\u7684\u662f\u554a\uff01\u8001\u5f1f\uff0c\u4e0a\u5929\u6709\u597d\u751f\u4e4b\u5fb7\uff0c\u6240\u4ee5\uff0c\u6211\u548c\u5c1a\u8eb2\u5165\u7a7a\u95e8\uff0c\u9003\u907f\u4e86\u51e0\u5341\u5e74\uff0c\u53ef\u60dc\u6700\u540e\u8fd8\u662f\u9003\u4e0d\u8fc7\u8fd9\u573a\u52ab\u96be\uff0c\u53ea\u597d\u633a\u8eab\u800c\u51fa\uff0c\u8fd9\u4e00\u70b9\uff0c\u6211\u548c\u5c1a\u5b9e\u5728\u6bd4\u4e0d\u8fc7\u8001\u53eb\u5316\uff0c\u4ed6\u6740\u4eba\u65e0\u6570\uff0c\u800c\u4e14\u6740\u5f97\u7406\u76f4\u6c14\u58ee\u2026\u2026\u201d \u3000\u3000\u770b\u770b\u6c5f\u67ab\u6b63\u5728\u51dd\u795e\u9759\u542c\uff0c\u4e03\u5b9d\u548c\u5c1a\u5401\u4e86\u53e3\u6c14\uff0c\u63a5\u9053\uff1a\u201c\u6211\u548c\u5c1a\u4e5f\u66fe\u529d\u8fc7\u4ed6\uff0c\u8981\u4ed6\u624b\u4e0b\u7559\u60c5\uff0c\u514d\u906d\u5929\u9063\uff0c\u4ed6\u5374\u8c6a\u6c14\u4e8e\u4e91\u7684\u56de\u7b54\u8bf4\uff0c\u6740\u4e00\u4eba\u6551\u4e86\u5343\u767e\u4eba\uff0c\u8001\u53eb\u4f55\u7f6a\u4e4b\u6709\uff0c\u5982\u679c\u8bf4\u5929\u9053\u6e83\u6e83\uff0c\u786c\u8981\u8001\u53eb\u5316\u6b7b\u4e8e\u975e\u547d\uff0c\u8001\u53eb\u82b1\u4e5f\u6b7b\u7684\u5fc3\u5b89\u7406\u5f97\uff0c\u4fef\u4ef0\u65e0\u6127\u5929\u5730\uff0c\u5f53\u7136\uff0c\u5200\u5149\u5251\u5f71\uff0c\u8ffd\u9b42\u593a\u547d\uff0c\u662f\u4e0d\u5982\u4eba\u7ea6\u9ec4\u660f\u540e\uff0c\u6708\u4e0a\u67f3\u68a2\u5934\uff0c\u4fea\u5f71\u53cc\u53cc\uff0c\u8c08\u60c5\u8bf4\u7231\uff0c\u90a3\u79cd\u6c14\u6c1b\u6765\u7684\u8212\u9002\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u7a81\u7136\u6df1\u6df1\u4e00\u63d6\uff0c\u9053\uff1a\u201c\u5927\u548c\u5c1a\uff0c\u53d7\u6559\u4e86\uff01\u201d \u3000\u3000\u4e03\u5b9d\u5927\u5e08\u653e\u4e0b\u4e86\u5fc3\u4e2d\u4e00\u5757\u77f3\u5934\uff0c\u5fd6\u9053\uff1a\u201c\u4ed6\u662f\u7ec4\u9053\u9010\u9b54\u7684\u4e3b\u5e05\uff0c\u5982\u679c\u9996\u5148\u52a8\u4e86\u5987\u4eba\u4e4b\u4ec1\uff0c\u5fc3\u5fd7\u4e0d\u575a\uff0c\u8fd9\u4e2a\u4ed7\u90a3\u80fd\u6253\u5f97\u4e0b\u53bb\uff1f\u201d \u3000\u3000\u5fc3\u795e\u5b9a\u4e0b\uff0c\u624d\u60f3\u5230\u8fd8\u6709\u91cd\u8981\u7684\u4e8b\u60c5\u8981\u8bf4\uff0c\u6025\u6025\u63a5\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u603b\u7ba1\u9093\u98de\u4e00\u65e9\u79bb\u5f00\uff0c\u6b64\u523b\u5c1a\u672a\u89c1\u5f52\u6765\u2026\u2026\u201d \u3000\u3000\u201c\u5662\uff01\u56db\u5b63\u82b1\u5973\u90a3\u8fb9\uff0c\u53ef\u6709\u4ec0\u4e48\u6d88\u606f\uff0c\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u6885\u82b1\u59d1\u5a18\u662f\u5426\u4e5f\u79bb\u5f00\u4e86\u6d1b\u9633\u5c45\u2026\u2026\u201d \u3000\u3000\u201c\u6885\u82b1\u59d1\u5a18\u5728\u6628\u591c\u51b3\u6218\u4e4b\u540e\uff0c\u79bb\u5f00\u4e86\u6d1b\u9633\u5c45\uff0c\u9ece\u660e\u65f6\u5206\u5f52\u6765\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u8bf4\uff0c\u201c\u4e0d\u8fc7\u6625\u3001\u590f\u3001\u79cb\u4e09\u5b63\u82b1\u5973\uff0c\u90fd\u5df2\u7ecf\u96c6\u4f1a\u4e8e\u83ca\u82b1\u5385\u4e2d\uff0c\u4e0d\u77e5\u5728\u5546\u8ba8\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u5927\u5e08\u773c\u89c2\u56db\u9762\uff0c\u8033\u542c\u516b\u65b9\uff0c\u6709\u4f60\u8fd9\u4e48\u4e00\u4f4d\u601d\u8651\u614e\u5bc6\u7684\u9ad8\u624b\uff0c\u5750\u9547\u8fd9\u91cc\uff0c\u5bf9\u6211\u6c5f\u67d0\u4eba\u7684\u5e2e\u52a9\u592a\u5927\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u548c\u5c1a\u53ea\u662f\u5c3d\u6211\u6240\u80fd\u800c\u5df2\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u53ea\u89c9\u4e03\u5b9d\u548c\u5c1a\u5c71\u85cf\u6d77\u7eb3\uff0c\u53eb\u4eba\u6709\u4e9b\u96be\u6d4b\u9ad8\u6df1\uff0c\u662f\u4e2a\u53ef\u4ee5\u5bc4\u4ee5\u91cd\u4efb\u5927\u5c06\u4e4b\u624d\uff0c\u5f53\u4e0b\u95ee\u9053\uff1a\u201c\u80e1\u3001\u6bb5\u4e8c\u4f4d\u638c\u95e8\u4eba\u7684\u6b66\u529f\uff0c\u8fdb\u5883\u5982\u4f55\uff1f\u201d \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5f7c\u6b64\u4e92\u4ee5\u7edd\u6280\u76f8\u6388\uff0c\u8fdb\u5883\u4e00\u65e5\u5343\u91cc\uff0c\u4e24\u4eba\u7684\u57fa\u7840\u672c\u6781\u6df1\u539a\uff0c\u518d\u52a0\u4e0a\u80af\u4e0b\u5de5\u592b\uff0c\u5982\u82e5\u6211\u548c\u5c1a\u7684\u770b\u6cd5\u4e0d\u9519\uff0c\u4ed6\u4eec\u90fd\u5df2\u5177\u6709\u6297\u62d2\u6885\u82b1\u7684\u80fd\u529b\uff0c\u6280\u827a\u7edd\u4e0d\u5728\u4e01\u897f\u5c71\u4e4b\u4e0b\u3002\u201d \u3000\u3000\u201c\u597d\u6781\u4e86\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u6211\u76f8\u4fe1\u5927\u5e08\u7684\u4f30\u7b97\u3002\u201d \u3000\u3000\u201c\u80e1\u5143\u548c\u90ed\u5929\u540c\uff0c\u8fdb\u5883\u4ea6\u5f88\u5feb\u901f\uff0c\u7b97\u4e0a\u6211\u548c\u5c1a\uff0c\u867d\u53ea\u4e94\u4e2a\u4eba\uff0c\u4f46\u52c9\u5f3a\u7b97\u5f97\u4e0a\u662f\u4e00\u652f\u53ef\u7528\u4e4b\u5175\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u5f20\u56db\u59d1\u4e14\u5e26\u7740\u96ea\u3001\u971c\u4e8c\u5973\u5c55\u5f00\u5e03\u7f6e\uff0c\u5982\u679c\u6211\u4eec\u80fd\u5728\u8fd9\u91cc\u6311\u8d77\u4ed6\u4eec\u5168\u9762\u706b\u62fc\uff0c\u54b1\u4eec\u518d\u627e\u673a\u4f1a\u6697\u4e2d\u72d9\u6740\uff0c\u903c\u51fa\u4ed6\u4eec\u7684\u9996\u8111\u4eba\u7269\uff0c\u627e\u51fa\u7978\u6e90\u7f6a\u9b41\uff0c\u4e00\u4e3e\u6b7c\u706d\uff0c\u4e5f\u8bb8\u53ef\u4ee5\u6d88\u9664\u6c5f\u6e56\u8fd9\u573a\u5927\u52ab\u6570\u4e86\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u5c31\u6211\u6240\u77e5\uff0c\u5185\u5bab\u4e00\u7cfb\u4e2d\uff0c\u4eca\u5929\u6709\u9ad8\u624b\u8d76\u5230\uff0c\u4f1a\u4e0d\u4f1a\u6682\u606f\u5e72\u6208\uff0c\u4eca\u5929\u5e94\u8be5\u6709\u4e2a\u51b3\u5b9a\u3002\u201d \u3000\u3000\u201c\u6c5f\u8001\u5f1f\uff0c\u4f60\u5bf9\u9752\u51e4\u4e86\u89e3\u591a\u5c11\uff1f\u201d \u3000\u3000\u201c\u5979\u662f\u5c5e\u4e8e\u53e6\u4e00\u7cfb\u7684\u4eba\u9a6c\uff0c\u548c\u5185\u5bab\u4e00\u7cfb\u4e2d\u4eba\u65e9\u6709\u5fc3\u75c5\uff0c\u4f46\u4e5f\u4e0d\u4e00\u5b9a\u4f1a\u652f\u6301\u5929\u9a6c\u5802\uff0c\u8fd9\u4e2a\u5145\u6ee1\u795e\u79d8\u7684\u5927\u7ec4\u5408\u4e2d\uff0c\u6709\u591a\u5c11\u6d3e\u7cfb\uff0c\u6211\u4e0d\u77e5\u9053\uff0c\u5c31\u773c\u4e0b\u6240\u77e5\uff0c\u8fd9\u6d1b\u9633\u5c45\u4e2d\uff0c\u5df2\u6709\u4e09\u4e2a\u4e0d\u540c\u7684\u6d3e\u7cfb\uff0c\u4e92\u76f8\u4e89\u9010\uff0c\u5927\u5bb6\u90fd\u4e0d\u613f\u610f\u653e\u5f03\u8fd9\u91cc\u7684\u5229\u76ca\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u4ed6\u4eec\u7684\u5f31\u70b9\uff0c\u6211\u4eec\u7684\u673a\u4f1a\uff0c\u81f3\u4e8e\u5982\u4f55\u7684\u72d9\u6740\u4ed6\u4eec\uff0c\u5230\u65f6\u5019\uff0c\u518d\u89c1\u673a\u800c\u4f5c\uff0c\u76ee\u524d\u6700\u91cd\u8981\u7684\u4e24\u4ef6\u4e8b\uff0c\u4e00\u662f\u9093\u98de\u7684\u884c\u8e2a\uff0c\u662f\u5426\u6b63\u5728\u548c\u5929\u9a6c\u5802\u6765\u7684\u9ad8\u624b\u78b0\u9762\uff0c\u4e8c\u662f\u5185\u5bab\u4e00\u7cfb\u4e2d\u6765\u7684\u4eba\uff0c\u662f\u4f55\u8eab\u4efd\uff1f\u56db\u5b63\u82b1\u5a62\u9f50\u805a\u83ca\u5316\u5385\uff0c\u90a3\u662f\u8bf4\uff0c\u4ed6\u4eec\u4f1a\u8d76\u5230\u8fd9\u91cc\u5546\u91cf\uff0c\u6211\u56de\u53bb\u6c90\u6d74\u66f4\u8863\uff0c\u7136\u540e\u5c31\u5728\u82b1\u6811\u4e1b\u4e2d\u6e38\u8d70\u2026\u2026\u201d \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u63a5\u9053\uff1a\u201c\u8981\u90ed\u5929\u540c\u6062\u590d\u672c\u6765\u9762\u76ee\uff0c\u5c31\u7531\u4f60\u5e26\u5728\u8eab\u8fb9\uff0c\u6211\u4f1a\u548c\u80e1\u3001\u6bb5\u4e24\u4f4d\u638c\u95e8\u4eba\uff0c\u5e26\u7740\u80e1\u5143\uff0c\u88c5\u4f5c\u5de1\u89c6\uff0c\u4e5f\u5728\u82b1\u6811\u6797\u4e2d\u5de1\u89c6\uff0c\u6700\u597d\u6211\u4eec\u53cc\u65b9\u80fd\u4ee5\u76ee\u89c6\u7684\u8ddd\u79bb\u5185\u6d3b\u52a8\uff0c\u4ee5\u4f7f\u76f8\u4e92\u7167\u5e94\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u5c31\u8fd9\u4e48\u529e\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u8bb0\u7740\uff0c\u8981\u4ed6\u4eec\u5e26\u4e0a\u5175\u5203\u3001\u6697\u5668\u2026\u2026\u201d \u3000\u3000\u6885\u82b1\u786e\u5b9e\u4f1a\u5408\u4e86\u56db\u5b63\u82b1\u5a62\uff0c\u96c6\u4f1a\u5728\u83ca\u82b1\u5385\u4e2d\u3002 \u3000\u3000\u4f46\u83ca\u82b1\u5385\u7684\u56db\u5468\uff0c\u5374\u6709\u7740\u68ee\u4e25\u7684\u6212\u5907\uff0c\u4e8c\u5341\u56db\u540d\u9556\u624b\uff0c\u6709\u4e00\u534a\u96c6\u4e2d\u5728\u8fd9\u91cc\uff0c\u516b\u5341\u56db\u4e2a\u5e97\u4f19\u8ba1\uff0c\u4e5f\u6709\u4e8c\u5341\u540d\u96c6\u4e2d\u4e8e\u6b64\uff0c\u4e09\u5341\u4e8c\u4e2a\u4eba\u5e03\u7f6e\u4e86\u4e00\u7247\u586b\u5bc6\u7684\u8b66\u6212\u7f51\uff0c\u4efb\u4f55\u4eba\u4e5f\u65e0\u6cd5\u63a5\u8fd1\u83ca\u82b1\u5385\u4e94\u4e08\u4e4b\u5185\uff0c\u800c\u4e0d\u8ba9\u4ed6\u4eec\u53d1\u89c9\u3002 \u3000\u3000\u679c\u7136\uff0c\u90a3\u4e9b\u5e74\u8f7b\u7684\u9556\u624b\u548c\u8d4c\u573a\u3001\u996d\u5e84\u4e2d\u7684\u5e74\u8f7b\u4f19\u8ba1\uff0c\u90fd\u662f\u5185\u5bab\u4e00\u7cfb\u4e2d\u4eba\uff0c\u8fd9\u4e9b\u4eba\u5e73\u5e38\u8fce\u5ba2\u3001\u9001\u83b1\u3001\u4e0a\u83dc\u3001\u5949\u9152\uff0c\u4e00\u65e6\u6709\u4e8b\uff0c\u7adf\u90fd\u662f\u53ef\u4ee5\u7528\u4f5c\u5bf9\u654c\u7684\u4eba\u624b\u3002 \u3000\u3000\u4f46\u4e03\u5b9d\u548c\u5c1a\u6c5f\u67ab\u7684\u6d3b\u52a8\uff0c\u4e5f\u6781\u5c3d\u5c0f\u5fc3\uff0c\u4ed6\u4eec\u6ca1\u6709\u95ef\u8fd1\u83ca\u82b1\u5385\uff0c\u53ea\u662f\u8fdc\u8fdc\u7684\u76d1\u89c6\u3002 \u3000\u3000\u770b\u4e0a\u53bb\uff0c\u5c31\u50cf\u5e03\u7f72\u5728\u6700\u5916\u4e00\u5c42\u7684\u5b88\u536b\u3002 \u3000\u3000\u8fb0\u4e2d\u65f6\u523b\uff0c\u679c\u7136\u53d1\u73b0\u4e86\u56db\u4e2a\u9556\u4e8e\u5bfc\u5f15\u7740\u4e00\u5973\u4e00\u7537\uff0c\u7a7f\u8d8a\u82b1\u6811\uff0c\u76f4\u884c\u5165\u83ca\u82b1\u5385\u4e2d\u3002 \u3000\u3000\u80e1\u840d\u9690\u8eab\u4e00\u5ea7\u82b1\u6811\u4e1b\u4e2d\uff0c\u770b\u7684\u751a\u662f\u6e05\u695a\uff0c\u53d1\u89c9\u4e86\u4e00\u5bf9\u7537\u5973\u4e5f\u90fd\u662f\u4e8c\u5341\u56db\u4e94\u7684\u5e74\u8f7b\u4eba\uff0c\u6bd4\u56db\u5b63\u82b1\u5a62\u7684\u5e74\u9f84\u5927\u4e00\u4e9b\uff0c\u7537\u7684\u624b\u63d0\u4e00\u4e2a\u9ec4\u8272\u7684\u5e03\u888b\uff0c\u5973\u7684\u7f69\u7740\u4e00\u4ef6\u94f6\u72d0\u76ae\u7684\u62ab\u98ce\uff0c\u8170\u4e2d\u4f3c\u662f\u4f69\u6709\u5175\u5203\uff0c\u4f46\u5374\u77a7\u4e0d\u6e05\u695a\u662f\u4ec0\u4e48\u5f62\u72b6\u3002 \u3000\u3000\u663e\u7136\u7684\uff0c\u90a3\u662f\u4e00\u79cd\u5947\u5f62\u7684\u77ed\u5c0f\u4e4b\u7269\u3002 \u3000\u3000\u4ed6\u4eec\u5802\u5802\u800c\u884c\uff0c\u6beb\u65e0\u907f\u5fcc\u3002 \u3000\u3000\u80e1\u840d\u5fc3\u4e2d\u5fd6\u9053\uff1a\u201c\u600e\u4e48\u4e0d\u89c1\u6700\u8ba9\u4eba\u5934\u75bc\u7684\u6740\u624b\u4eba\u7269\uff1f\u201d\u5fc3\u4e2d\u53cd\u800c\u6709\u4e9b\u5931\u671b\uff0c\u5728\u80e1\u840d\u7684\u5fc3\u76ee\u4e2d\uff0c\u5929\u9a6c\u603b\u5802\u6700\u96be\u6297\u62d2\u7684\uff0c\u5e94\u8be5\u662f\u90a3\u4e9b\u9ed1\u8863\u5f2f\u5200\u7684\u6740\u624b\u2026\u2026 \u3000\u3000\u8d1f\u8d23\u76d1\u89c6\u9093\u98de\u4e3e\u52a8\u7684\u4e03\u5b9d\u548c\u5c1a\uff0c\u4e5f\u5306\u5306\u884c\u4e86\u8fc7\u6765\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u9093\u98de\u56de\u6765\u4e86\uff0c\u540c\u884c\u56db\u4eba\uff0c\u548c\u5c1a\u8ba4\u8bc6\u4e86\u4e24\u4e2a\uff0c\u4e00\u4fd7\u4e00\u9053\uff0c\u53e6\u5916\u4e24\u4e2a\u4eba\uff0c\u548c\u5c1a\u7adf\u7136\u662f\u8ba4\u4e0d\u51fa\u6765\uff0c\u4e0d\u8fc7\uff0c\u770b\u4ed6\u4eec\u8863\u7740\u88c5\u675f\uff0c\u4f3c\u4e0d\u662f\u4e2d\u539f\u4eba\u6c0f\u3002\u201d \u3000\u3000\u80e1\u840d\u4e5f\u5e26\u7740\u80e1\u5143\u8d76\u6765\uff0c\u548c\u6c5f\u67ab\u3001\u90ed\u5929\u540c\u4f1a\u805a\u4e00\u5904\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u6b64\u523b\u6d1b\u9633\u5c45\u7684\u82b1\u6811\u6797\u4e2d\uff0c\u4eba\u5f71\u6d41\u52a8\uff0c\u6c5f\u67ab\u7b49\u4e00\u6279\u4eba\uff0c\u4e5f\u65e9\u5c31\u843d\u5165\u5bf9\u65b9\u7684\u773c\u4e2d\uff0c\u4f46\u53cc\u65b9\u4fdd\u6301\u4e86\u4e00\u5b9a\u7684\u8ddd\u79bb\uff0c\u4e92\u76f8\u76d1\u89c6\uff0c\u672a\u5f15\u8d77\u6b63\u9762\u51b2\u7a81\u800c\u5df2\u3002 \u3000\u3000\u201c\u90a3\u4e00\u9053\u4e00\u4fd7\uff0c\u662f\u4ec0\u4e48\u4eba\u7269\uff1f\u6bd4\u4e01\u897f\u5c71\u5982\u4f55\uff1f\u201d\u6c5f\u67ab\u4f4e\u58f0\u95ee\u4e03\u5b9d\u548c\u5c1a\u3002 \u3000\u3000\u201c\u975e\u5e38\u96be\u7f20\u7684\u4e24\u4e2a\u5bb6\u4f19\uff0c\u8001\u9053\u4eba\u662f\u4e09\u5341\u5e74\u53db\u79bb\u6b66\u5f53\u6d3e\u7684\u5929\u865a\u5b50\uff0c\u7b97\u8d77\u6765\uff0c\u8fd8\u662f\u73b0\u4ee3\u6b66\u5f53\u6d3e\u638c\u95e8\u4eba\u7684\u5e08\u5144\u2026\u2026\u201d \u3000\u3000\u201c\u4ec0\u4e48\uff1f\u2026\u2026\u201d\u80e1\u840d\u6253\u65ad\u4e86\u4e03\u5b9d\u548c\u5c1a\u7684\u8bdd\uff0c\u9053\uff1a\u201c\u5929\u865a\u5b50\uff0c\u4e5f\u6295\u5165\u4e86\u5929\u9a6c\u603b\u5802\uff1f\u201d \u3000\u3000\u53e3\u6c14\u4e2d\uff0c\u5145\u6ee1\u4e86\u60ca\u8bb6\u3002 \u3000\u3000\u70b9\u70b9\u5934\uff0c\u4e03\u5b9d\u548c\u5c1a\u63a5\u9053\uff1a\u201c\u90a3\u4e2a\u4fd7\u88c5\u8001\u8005\uff0c\u66fe\u662f\u5404\u5927\u95e8\u6d3e\u5408\u529b\u56f4\u527f\uff0c\u903c\u4ed6\u8df3\u5165\u957f\u6c5f\u7684\u5201\u9e4f\u3002\u201d \u3000\u3000\u201c\u4e5d\u5934\u9e1f\u5201\u9e4f\u3002\u201d\u80e1\u840d\u8bf4\uff0c\u201c\u679c\u7136\u662f\u4e00\u4ee3\u51f6\u6076\u7684\u9b54\u5934\uff0c\u8fd9\u4e24\u4e2a\u4eba\uff0c\u4efb\u4f55\u4e00\u4e2a\uff0c\u90fd\u5e94\u8be5\u6bd4\u4e01\u897f\u5c71\u9ad8\u4e0a\u4e00\u7b79\u4e86\u3002\u201d \u3000\u3000\u201c\u6240\u4ee5\uff0c\u8fd9\u4e00\u6b21\u5982\u679c\u518d\u8d77\u51b2\u7a81\uff0c\u90a3\u5c31\u6bd4\u4e0a\u4e00\u6b21\u70ed\u95f9\u591a\u4e86\u2026\u2026\u201d \u3000\u3000\u9152\u8089\u548c\u5c1a\u9053\uff1a\u201c\u6c5f\u8001\u5f1f\uff0c\u8fd9\u4e00\u6b21\uff0c\u54b1\u4eec\u662f\u5426\u8fd8\u8981\u5e2e\u52a9\u9093\u98de\u3002\u201d \u3000\u3000\u201c\u5f53\u7136\uff0c\u5c31\u7b97\u6211\u4eec\u60f3\u6295\u5165\u5185\u5bab\u4e00\u7cfb\uff0c\u4ed6\u4eec\u4e5f\u672a\u5fc5\u4f1a\u6536\u5bb9\u6211\u4eec\u2026\u2026\u201d\u6c5f\u67ab\u5e76\u672a\u4e3a\u5929\u865a\u5b50\u548c\u5201\u9e4f\u4e24\u4eba\u7684\u5a01\u540d\u9707\u52a8\uff0c\u6de1\u7136\u8bf4\u9053\uff1a\u201c\u5176\u5b9e\uff0c\u9093\u98de\u4e5f\u53ea\u628a\u6211\u4eec\u5f53\u4f5c\u804b\u5b50\u7684\u8033\u6735\uff0c\u914d\u642d\uff0c\u5b8c\u5168\u6ca1\u6709\u628a\u6211\u4eec\u653e\u5728\u773c\u4e2d\uff0c\u6211\u4eec\u4e5f\u6b63\u597d\u85c9\u673a\u9690\u85cf\u8d77\u6765\uff0c\u975e\u7edd\u4e0d\u5f97\u5df2\uff0c\u4e0d\u53ef\u51fa\u624b\uff0c\u8d70\uff01\u54b1\u4eec\u89c1\u9093\u98de\u53bb\u3002\u201d \u3000\u3000\u80e1\u5143\u548c\u90ed\u5929\u540c\uff0c\u7559\u5728\u9093\u98de\u5b85\u9662\u5916\u7684\u82b1\u6811\u6797\u4e2d\uff0c\u6c5f\u67ab\u5e26\u7740\u4e03\u5b9d\u548c\u5c1a\u3001\u80e1\u840d\u3001\u6bb5\u4e5d\uff0c\u76f4\u95ef\u5165\u5385\u5802\u4e4b\u4e2d\u3002 \u3000\u3000\u201c\u6b63\u60f3\u6d3e\u4eba\u53bb\u627e\u4f60\uff0c\u4f60\u5374\u53ca\u65f6\u800c\u6765\uff0c\u201d\u9093\u98de\u62db\u547c\u6c5f\u67ab\u8fdb\u5165\u5385\u4e2d\uff0c\u4f46\u5374\u793a\u610f\u4e03\u5b9d\u548c\u5c1a\u3001\u80e1\u840d\u7b49\uff0c\u9000\u51fa\u5385\u5916\u7b49\u5019\u3002 \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u7b49\u4e5f\u8868\u73b0\u51fa\u7edd\u5bf9\u7684\u670d\u4ece\uff0c\u62b1\u62f3\u4e00\u793c\uff0c\u9000\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u201c\u8fd9\u5c31\u662f\u6211\u5bf9\u56db\u4f4d\u63d0\u8fc7\u7684\u526f\u603b\u7ba1\u97e9\u9738\uff0c\u201d\u9093\u98de\u9053\uff1a\u201c\u5fe0\u5fc3\u536b\u4e3b\uff0c\u4fdd\u62a4\u6d1b\u9633\u5c45\u3002\u201d \u3000\u3000\u5c31\u501f\u9093\u98de\u8bf4\u8bdd\u7684\u673a\u4f1a\uff0c\u6c5f\u67ab\u5df2\u76ee\u5149\u8f6c\u52a8\u6253\u91cf\u4e86\u5385\u4e2d\u56db\u4eba\u3002 \u3000\u3000\u4e00\u4e2a\u53d1\u9aef\u5168\u9ed1\u7684\u9752\u888d\u9053\u4eba\uff0c\u80cc\u63d2\u957f\u5251\uff0c\u7ec6\u7709\u5c0f\u773c\uff0c\u770b\u4e0a\u53bb\u6709\u70b9\u7626\u5f31\uff0c\u4e00\u70b9\u4e5f\u4e0d\u8d77\u773c\uff0c\u60f3\u6765\u5c31\u662f\u5929\u865a\u5b50\u4e86\u3002 \u3000\u3000\u53e6\u4e00\u4e2a\u9aef\u53d1\u5982\u971c\uff0c\u5927\u8033\u9614\u53e3\uff0c\u53f3\u988a\u4e0a\u6709\u4e00\u6761\u9c9c\u7ea2\u7684\u5200\u75a4\uff0c\u5de6\u624b\u53ea\u4f59\u4e0b\u4e86\u5927\u3001\u98df\u3001\u4e2d\u4e09\u4e2a\u6307\u5934\uff0c\u53ef\u60f3\u5f53\u5e74\u4ed6\u53d7\u4f24\u4e4b\u91cd\uff0c\u8fd9\u4e2a\u4eba\uff0c\u5927\u6982\u662f\u4e5d\u5934\u9e1f\u5201\u9e4f\u4e86\u3002 \u3000\u3000\u53e6\u4e2a\u4e24\u4e2a\uff0c\u5168\u8eab\u5927\u7ea2\u8272\u7684\u8863\u670d\uff0c\u4f46\u76ae\u80a4\u5f88\u9ed1\uff0c\u5e74\u9f84\u5927\u7ea6\u5728\u4e09\u5341\u81f3\u56db\u5341\u4e4b\u95f4\uff0c\u7ea2\u8272\u7684\u8863\u670d\u4e0a\uff0c\u7ee3\u4e86\u51e0\u6735\u91d1\u8272\u7684\u706b\u7130\uff0c\u770b\u4e0a\u53bb\u5341\u5206\u8be1\u5f02\u3002 \u3000\u3000\u8fd9\u56db\u4e2a\u4f7f\u67ab\u5fc3\u5934\u9707\u52a8\u7684\u5374\u662f\u90a3\u4e2a\u770b\u4e0a\u53bb\u5168\u4e0d\u8d77\u773c\u7684\u5929\u865a\u5b50\uff0c\u4ed6\u662f\u73b0\u4efb\u6b66\u5f53\u638c\u95e8\u4eba\u7684\u5e08\u5144\uff0c\u5e94\u8be5\u6709\u4e03\u5341\u4ee5\u4e0a\u7684\u5e74\u7eaa\u4e86\uff0c\u4f46\u4ed6\u987b\u53d1\u672a\u767d\uff0c\u8138\u4e0a\u4e0d\u89c1\u76b1\u7eb9\uff0c\u5f31\u5c0f\u7684\u8eab\u4f53\uff0c\u5374\u6709\u4e24\u53ea\u548c\u8eab\u4f53\u4e0d\u6210\u6bd4\u4f8b\u7684\u5927\u624b\uff0c\u624b\u80cc\u9752\u7b4b\u66b4\u51fa\uff0c\u4e00\u773c\u53ef\u89c1\u3002 \u3000\u3000\u8fd9\u4e9b\u90fd\u9700\u7089\u706b\u7eaf\u9752\u7684\u5185\u529f\u4fee\u4e3a\uff0c\u624d\u80fd\u51fa\u73b0\u7684\u5fbd\u76f8\uff0c\u5c24\u5176\u662f\u90a3\u4e00\u53cc\u9752\u7b4b\u66b4\u8d77\u7684\u5927\u624b\uff0c\u8868\u73b0\u51fa\u4e86\u4ed6\u63e1\u5251\u7684\u6709\u529b\u3001\u7a33\u5b9a\uff0c\u8fd9\u4e2a\u4eba\u7684\u5251\u672f\u9020\u8be3\uff0c\u4e0d\u4f46\u5df2\u767b\u5802\u5165\u5ba4\uff0c\u4e5f\u53ef\u80fd\u5df2\u6709\u4e86\u60ca\u5929\u52a8\u5730\u7684\u5927\u6210\u5c31\uff0c\u662f\u4e00\u4f4d\u53ef\u6015\u7684\u5251\u5ba2\u3002 \u3000\u3000\u9762\u5bf9\u7740\u5370\u6b64\u6c89\u6f5c\u7684\u5927\u884c\u5bb6\uff0c\u6c5f\u67ab\u7acb\u523b\u63d0\u9ad8\u4e86\u8b66\u89c9\uff0c\u795e\u83b9\u5185\u6f5c\uff0c\u9690\u655b\u4e86\u53cc\u76ee\u4e2d\u7684\u795e\u5149\u3002 \u3000\u3000\u679c\u7136\uff0c\u5929\u865a\u5b50\u4e24\u53ea\u5c0f\u773c\u775b\uff0c\u4e00\u5408\u4e00\u95ed\uff0c\u7acb\u65f6\u6709\u5982\u7535\u5149\u4f3c\u7684\u5728\u6c5f\u67ab\u7684\u8138\u4e0a\u626b\u63a0\u800c\u8fc7\uff0c\u4e0d\u7981\u76b1\u4e86\u4e00\u4e0b\u7709\u5934\uff0c\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u9093\u603b\u7ba1\u5f88\u5938\u8d5e\u4f60\uff0c\u4f60\u6709\u4e00\u7247\u5fe0\u5fc3\uff0c\u4e5f\u5c31\u591f\u4e86\u3002\u201d\u8a00\u8bed\u4e4b\u95f4\uff0c\u4f3c\u6781\u8f7b\u89c6\u3002 \u3000\u3000\u201c\u662f\uff01\u97e9\u9738\u53d7\u6559\u3002\u201d"}),showOnHomepage:!0},{name:"\u7b2c \u4e5d \u56de\u3000\u4e5d\u5934\u9e1f\u60ca\u670d\u4f9b\u5929\u9a6c",content:(0,t.jsx)(t.Fragment,{children:"\u5c31\u662f\u90a3\u4e48\u6beb\u5398\u4e4b\u5dee\uff0c\u6c5f\u67ab\u5148\u4e00\u6b65\uff0c\u9690\u53bb\u76ee\u4e2d\u795e\u5149\uff0c\u4f7f\u5f97\u5929\u865a\u5b50\u65e0\u6cd5\u7aa5\u51fa\u865a\u5b9e\uff0c\u5bf9\u6c5f\u67ab\u4e5f\u5c31\u653e\u5728\u5fc3\u4e0a\u4e86\u3002 \u3000\u3000\u8ba4\u4e3a\u9093\u98de\u4e00\u76f4\u79f0\u8d5e\u4ed6\uff0c\u53ea\u4e0d\u8fc7\u662f\u4e3a\u4e86\u8868\u626c\u4ed6\u7684\u5fe0\u8bda\uff0c\u4f55\u51b5\uff0c\u4ed6\u5bf9\u6c5f\u67ab\u8868\u73b0\u51fa\u7684\u8bda\u60f6\u795e\u6001\uff0c\u4e5f\u5f88\u6ee1\u610f\u3002 \u3000\u3000\u201c\u9093\u98de\u8bf4\uff0c\u4f60\u63a5\u4e0b\u4e86\u5185\u5bab\u6740\u624b\u4e00\u5200\uff0c\u2026\u2026\u201d\u5201\u9e4f\u9053\uff1a\u201c\u800c\u90a3\u4e9b\u6740\u624b\u51fb\u51fa\u7684\u4e00\u5200\uff0c\u53ef\u4ee5\u53d6\u53bb\u4e01\u897f\u5c71\u548c\u5218\u6e05\u7684\u6027\u547d\uff0c\u6211\u600e\u4e48\u4e5f\u77a7\u4e0d\u51fa\u6765\u4f60\u80fd\u9003\u8fc7\u90a3\u4e00\u5200\u4e4b\u5371\u3002\u201d \u3000\u3000\u201c\u4e5f\u8bb8\u662f\u5c5e\u4e0b\u7684\u8fd0\u6c14\u597d\u4e00\u4e9b\uff0c\u6211\u628a\u5168\u8eab\u4e4b\u529b\uff0c\u51dd\u805a\u5728\u4e00\u5200\u4e4b\u4e0a\uff0c\u62a4\u4f4f\u4e86\u524d\u80f8\u8981\u5bb3\uff0c\u90a3\u6740\u624b\u4e00\u5200\uff0c\u6b63\u51fb\u5411\u662f\u5904\uff0c\u6240\u4ee5\uff0c\u63a5\u4e0b\u4e86\u4e00\u5200\u3002\u201d\u6c5f\u67ab\u5c3d\u5fc3\u89e3\u8bf4\u7740\u3002 \u3000\u3000\u201c\u4e00\u5200\u80fd\u6740\u4e86\u4e01\u897f\u5c71\uff0c\u5374\u7559\u4e0b\u4f60\u4e00\u6761\u6027\u547d\uff0c\u6211\u5201\u67d0\u4eba\u5b9e\u5728\u60f3\u4e0d\u901a\u4e86\uff01\u201d \u3000\u3000\u201c\u5c0f\u5201\u2026\u2026\u201d\u5929\u865a\u5b50\u7b11\u9053\uff1a\u201c\u4eba\u7684\u8fd0\u6c14\uff0c\u5f88\u5947\u602a\uff0c\u5f53\u5e74\uff0c\u4f60\u5168\u8eab\u91cd\u4f24\uff0c\u8df3\u5728\u957f\u6c5f\u91cc\uff0c\u7adf\u7136\u9003\u4e86\u51fa\u6765\uff0c\u662f\u4e0d\u662f\u4e5f\u7b97\u5947\u8ff9\u5462\uff1f\u201d \u3000\u3000\u8fd9\u662f\u4e60\u9e4f\u4e00\u751f\u4e2d\u6700\u5927\u7684\u6068\u4e8b\uff0c\u8c01\u8981\u63ed\u4e86\u4ed6\u8fd9\u4e2a\u75ae\u75a4\uff0c\u7acb\u523b\u7ffb\u8138\uff0c\u53ef\u662f\u4ed6\u4e0d\u6562\u9876\u649e\u5929\u865a\u5b50\uff0c\u8138\u7ea2\u8116\u5b50\u7c97\u7684\uff0c\u5782\u9996\u4e0d\u8a00\u3002 \u3000\u3000\u6253\u4eba\u4e0d\u6253\u8138\uff0c\u63ed\u4eba\u4e0d\u63ed\u77ed\uff0c\u5929\u865a\u5b50\u4e00\u5f00\u53e3\u5c31\u63ed\u51fa\u4e86\u4e5d\u5934\u9e1f\u5201\u9e4f\u6700\u5927\u7684\u75ae\u75a4\uff0c\u867d\u7136\u975e\u5e38\u6709\u6548\u7684\u5236\u6b62\u4e5d\u5934\u9e1f\u5bf9\u6c5f\u67ab\u5201\u96be\uff0c\u4f46\u4e5f\u66b4\u9732\u51fa\u4ed6\u4e3a\u4eba\u7684\u51c9\u8584\u3001\u5c16\u82db\uff0c\u90a3\u5c31\u5306\u602a\u4ed6\u867d\u662f\u5e08\u5144\u7684\u8eab\u4efd\uff0c\u6b66\u5f53\u4e0a\u4e00\u4ee3\u638c\u95e8\u4eba\uff0c\u4e5f\u4e0d\u80af\u4f20\u4f4d\u7ed9\u4ed6\u4e86\u3002 \u3000\u3000\u201c\u9053\u957f\u2026\u2026\u201d\u9093\u98de\u6025\u6025\u4e8e\u91ca\u53bb\u5201\u9e4f\u7684\u7a98\u6001\uff0c\u8bf4\u9053\uff1a\u201c\u5185\u5bab\u4e00\u7cfb\u4e2d\u4eba\uff0c\u4e0d\u77e5\u5982\u4f55\u884c\u52a8\uff0c\u54b1\u4eec\u662f\u5750\u4ee5\u5f85\u6bd9\u5462\uff1f\u8fd8\u662f\u2026\u2026\u201d \u3000\u3000\u8fd8\u662f\u600e\u4e48\u6837\uff0c\u9093\u98de\u6ca1\u8bf4\u4e0b\u53bb\uff0c\u4ed6\u660e\u767d\u5929\u865a\u5b50\u7684\u6027\u683c\uff0c\u8bed\u6c14\u867d\u7136\u6e29\u548c\uff0c\u4f46\u5982\u548c\u4ed6\u7684\u5fc3\u4e2d\u7684\u60f3\u6cd5\u4e0d\u5408\uff0c\u4e00\u4e0b\u5c31\u5c16\u523b\u5982\u5200\uff0c\u8ba9\u4f60\u4e0b\u4e0d\u4e86\u53f0\u3002 \u3000\u3000\u201c\u7980\u603b\u7ba1\uff0c\u5185\u5bab\u4e2d\u5df2\u6709\u4e00\u7537\u4e00\u6b65\u8d76\u5230\uff0c\u6b63\u548c\u56db\u5b63\u82b1\u5a62\u5728\u83ca\u82b1\u5385\u4f1a\u5546\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u4ed6\u4eec\u8fb0\u4e2d\u65f6\u523b\u8d76\u5230\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u5f88\u8be6\u5b9e\uff0c\u52ff\u602a\u9093\u98de\u79f0\u8d5e\u4f60\uff0c\u8fde\u6211\u8001\u9053\u4e5f\u6709\u70b9\u559c\u6b22\u4f60\u4e86\uff0c\u8bb0\u4e0d\u8bb0\u5f97\u6765\u4eba\u6709\u591a\u5927\u5e74\u7eaa\uff1f\u201d \u3000\u3000\u201c\u4e00\u7537\u4e00\u5973\uff0c\u5e74\u7eaa\u90fd\u4e0d\u592a\u5927\uff0c\u7ea6\u5728\u4e8c\u5341\u4e94\u516d\u5c81\u5de6\u53f3\uff0c\u7537\u7684\u624b\u4e2d\u63d0\u4e86\u4e00\u4e2a\u9ec4\u8272\u7684\u5e03\u888b\uff0c\u5973\u7684\u62ab\u4e00\u4ef6\u94f6\u72d0\u62ab\u98ce\u2026\u2026\u3002\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u5374\u77a7\u4e0d\u51fa\u4ed6\u4eec\u5e26\u7684\u4ec0\u4e48\u5175\u5203\uff1f\u201d \u3000\u3000\u9093\u98de\u9053\uff1a\u201c\u8fd9\u5c31\u5947\u602a\u4e86\uff0c\u600e\u4e48\u53ea\u6709\u4e24\u4e2a\u4eba\u6765\u5462\uff1f\u201d \u3000\u3000\u201c\u6709\u4ec0\u4e48\u597d\u5947\u602a\u7684\uff1f\u201d\u5929\u865a\u5b50\u63a5\u9053\uff1a\u201c\u8fd8\u4e0d\u662f\u548c\u6211\u4eec\u4e00\u6837\uff0c\u628a\u4e00\u6279\u4eba\u624b\uff0c\u5b89\u7f6e\u6d1b\u9633\u5c45\u5916\u3002\u201d \u3000\u3000\u201c\u662f\u662f\u662f\u2026\u2026\u201d\u9093\u98de\u9053\uff1a\u201c\u8fd9\u4e48\u7b80\u5355\u7684\u4e8b\uff0c\u5c5e\u4e0b\u7adf\u7136\u662f\u60f3\u4e0d\u8d77\u6765\u3002\u201d \u3000\u3000\u8fde\u9093\u98de\u4e5f\u81ea\u79f0\u5c5e\u4e0b\uff0c\u663e\u89c1\u8fd9\u4f4d\u5929\u865a\u5b50\uff0c\u4e0d\u662f\u4e00\u822c\u957f\u8001\u3001\u62a4\u6cd5\u7684\u865a\u8854\uff0c\u5f53\u662f\u63e1\u6709\u5b9e\u6743\u7684\u4eba\u7269\u3002 \u3000\u3000\u201c\u5c0f\u5201\u2026\u2026\u201d\u5929\u865a\u5b50\u5fae\u7b11\u9053\uff1a\u201c\u4f60\u7684\u70b9\u5b50\u591a\uff0c\u8bf4\u8bf4\u770b\uff0c\u8be5\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u5c3d\u7ba1\u5201\u9e4f\u5fc3\u4e2d\u7684\u7f9e\u6127\u6012\u706b\u672a\u6d88\uff0c\u4f46\u5374\u4e0d\u6562\u7a0d\u73b0\u8fdf\u7591\uff0c\u63a5\u9053\uff1a\u201c\u6211\u770b\uff0c\u5f7c\u6b64\u5df2\u5fc3\u7167\u4e0d\u5ba3\uff0c\u7528\u4e0d\u7740\u7a81\u65bd\u88ad\u51fb\u4e86\uff0c\u5e72\u8106\u6311\u660e\u4e86\u5e72\uff0c\u5f53\u9762\u9f13\uff0c\u5bf9\u9762\u9523\u7684\u6572\u6253\u4e00\u9635\uff0c\u8c08\u4e0d\u597d\uff0c\u5927\u5bb6\u7ea6\u4e2a\u5730\u65b9\u5bf9\u7740\u5e72\u4e00\u573a\u2026\u2026\u201d \u3000\u3000\u201c\u6709\u9053\u7406\u2026\u2026\u201d\u5929\u865a\u5b50\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u4e0d\u80fd\u6bc1\u4e86\u6d1b\u9633\u5c45\u8fd9\u68f5\u6447\u94b1\u6811\uff0c\u9093\u98de\u2026\u2026\u201d\u5c5e\u4e0b\u5728\u3002 \u3000\u3000\u201c\u4f60\u53bb\u901a\u77e5\u4ed6\u4eec\u4e00\u58f0\uff0c\u8981\u4ed6\u4eec\u9886\u5934\u7684\u51fa\u6765\uff0c\u548c\u6211\u8001\u9053\u5148\u89c1\u4e2a\u9762\uff0c\u8c08\u8c08\u65a4\u4e24\uff0c\u8c08\u4e0d\u597d\u518d\u8c41\u5f00\u4e86\u5e72\uff0c\u8fd9\u53eb\u5148\u793c\u540e\u5175\u554a\u2026\u2026\u201d \u3000\u3000\u201c\u662f\uff01\u5c5e\u4e0b\u7acb\u523b\u5c31\u53bb\u3002\u201d \u3000\u3000\u8fd9\u5929\u865a\u5b50\u7684\u6743\u5a01\uff0c\u548c\u4e01\u897f\u5c71\u7684\u5b8c\u5168\u4e0d\u540c\uff0c\u9093\u98de\u8fde\u4e00\u53e5\u63a8\u6258\u4e4b\u8bcd\u4e5f\u4e0d\u6562\u8bf4\u3002 \u3000\u3000\u6c5f\u67ab\u62e6\u4f4f\u4e86\u9093\u98de\u9053\uff1a\u201c\u6709\u4e8b\u5c5e\u4e0b\u773c\u5176\u52b3\uff0c\u603b\u7ba1\u600e\u53ef\u8f7b\u8eab\u6d89\u9669\uff0c\u8fd8\u662f\u7531\u97e9\u67d0\u53bb\u5427\uff01\u201d \u3000\u3000\u9093\u98de\u56de\u5934\u671b\u5929\u865a\u5b50\uff0c\u5929\u865a\u5b50\u5fae\u7b11\u9894\u9996\uff0c\u9093\u98de\u624d\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u65e8\u5728\u4f20\u8baf\uff0c\u4e0d\u53ef\u548c\u4ed6\u4eec\u51b2\u7a81\u2026\u2026\u201d \u3000\u3000\u5201\u9e4f\u51b7\u51b7\u7684\u63a5\u4e86\u4e00\u53e5\uff0c\u9053\uff1a\u201c\u8bb0\u7740\u5e26\u4ed6\u4eec\u53e3\u4fe1\u56de\u6765\u3002\u201d \u3000\u3000\u201c\u662f\uff01\u201d\u6c5f\u67ab\u5f53\u771f\u662f\u6e29\u826f\u517c\u606d\u8ba9\uff0c\u4e94\u5fb7\u4ff1\u5168\uff0c\u4f7f\u6bcf\u4e2a\u4eba\u90fd\u611f\u89c9\u5230\u4ed6\u548c\u7766\u6613\u5904\uff0c\u5201\u9e4f\u672c\u6765\u5bf9\u4ed6\u5fc3\u5b58\u6210\u89c1\uff0c\u6b64\u523b\uff0c\u7adf\u4e5f\u6d88\u51cf\u4e86\u4e0d\u5c11\u3002 \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u8083\u7acb\u5ba4\u5916\uff0c\u51dd\u795e\u503e\u542c\uff0c\u6697\u6697\u611f\u53f9\u4e0d\u5df2\uff0c\u5fd6\u9053\uff1a\u201c\u4e5f\u771f\u7684\u96be\u4e3a\u4ed6\u4e86\uff0c\u5982\u6b64\u7684\u538b\u7f29\u81ea\u5df1\uff0c\u975e\u5927\u667a\u5927\u52c7\uff0c\u5f88\u96be\u529e\u5230\u3002\u201d \u3000\u3000\u6c5f\u67ab\u6ca1\u6709\u548c\u4e03\u5b9d\u548c\u5c1a\u7b49\u62db\u547c\uff0c\u770b\u4e5f\u672a\u770b\u51e0\u4eba\u4e00\u773c\uff0c\u4e00\u76f4\u5730\u5954\u5411\u83ca\u82b1\u5385\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u5929\u865a\u5b50\u795e\u76ee\u5982\u7535\uff0c\u4efb\u4f55\u4e00\u4e2a\u7ec6\u5fae\u5c0f\u8282\uff0c\u90fd\u65e0\u6cd5\u9003\u8fc7\u4ed6\u7684\u76d1\u89c6\u3002 \u3000\u3000\u4e00\u8def\u4e0a\uff0c\u6c5f\u67ab\u76d8\u7b97\u7740\u5982\u4f55\u5f00\u53e3\uff0c\u4f46\u4eba\u8ddd\u83ca\u82b1\u5385\u8fd8\u6709\u4e09\u4e08\u591a\u8fdc\uff0c\u5df2\u88ab\u5b88\u536b\u7684\u9556\u624b\u7ed9\u56f4\u4e86\u8d77\u6765\u3002 \u3000\u3000\u5c31\u5728\u6c5f\u67ab\u79bb\u5f00\u5385\u5802\u540e\uff0c\u5929\u865a\u5b50\u5fae\u4e00\u6446\u5934\uff0c\u4e5d\u5934\u9e1f\u4e5f\u7a81\u7136\u7ad9\u8d77\u4e86\u8eab\u5b50\uff0c\u53cc\u81c2\u4e00\u6296\uff0c\u7a7f\u7a97\u800c\u51fa\uff0c\u4eba\u5f71\u4e00\u95ea\u4e0d\u89c1\u3002 \u3000\u3000\u4ed6\u5916\u53f7\u4e5d\u5934\u9e1f\uff0c\u679c\u7136\u6709\u7740\u98de\u9e1f\u4e00\u822c\u7684\u8eab\u624b\u3002 \u3000\u3000\u8fd9\u662f\u76d1\u89c6\uff0c\u5f53\u7136\uff0c\u4e5f\u6709\u70b9\u63a5\u5e94\u7684\u610f\u601d\u3002 \u3000\u3000\u6c5f\u67ab\u505c\u4e0b\u811a\u6b65\uff0c\u9053\uff1a\u201c\u6211\u53d7\u547d\u4f20\u8baf\u800c\u6765\uff0c\u8bf7\u89c1\u6885\u82b1\u59d1\u5a18\u3002\u201d \u3000\u3000\u6885\u82b1\u5df2\u98de\u8eab\u800c\u81f3\uff0c\u76ee\u5149\u626b\u63a0\u4e86\u6c5f\u67ab\u8eab\u540e\u7684\u82b1\u6811\u4e00\u773c\uff0c\u9053\uff1a\u201c\u8bf4\u5427\uff01\u201d \u3000\u3000\u201c\u5929\u865a\u5b50\u9053\u957f\uff0c\u60f3\u548c\u8d35\u65b9\u7684\u9886\u5934\u4eba\u89c1\u6b21\u9762\uff0c\u5927\u5bb6\u8c08\u8c08\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u4e5f\u8bb8\u80fd\u5316\u89e3\u53bb\u4e00\u573a\u5e72\u6208\u3002\u201d \u3000\u3000\u83ca\u82b1\u5385\u4e2d\u4f20\u51fa\u6765\u4e00\u4e2a\u6e05\u51b7\u58f0\u97f3\uff0c\u9053\uff1a\u201c\u7b54\u5e94\u4ed6\uff0c\u6885\u82b1\u5e08\u59b9\uff0c\u4e2d\u5348\u65f6\u5206\uff0c\u5728\u8fce\u5bbe\u9601\u4e2d\u89c1\u9762\u3002\u201d \u3000\u3000\u6885\u82b1\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u97e9\u526f\u603b\u7ba1\u542c\u5230\u4e86\u5427\uff01\u201d \u3000\u3000\u201c\u662f\u542c\u5230\u4e86\u2026\u2026\u201d\u6c5f\u67ab\u8bf4\uff0c\u201c\u627e\u539f\u8bdd\u4f20\u62a5\uff0c\u5982\u679c\u6ca1\u6709\u53d8\u66f4\uff0c\u90a3\u5c31\u4e2d\u5348\u89c1\u4e86\u3002\u201d \u3000\u3000\u8f6c\u8eab\u76f4\u5954\u56de\u9093\u98de\u7684\u4f4f\u5904\u5385\u5802\u3002 \u3000\u3000\u4e5d\u5934\u9e1f\u5201\u9e4f\uff0c\u5374\u5df2\u5148\u4e00\u6b65\u56de\u5f52\u539f\u5750\u3002 \u3000\u3000\u6c5f\u67ab\u4e00\u62b1\u62f3\uff0c\u9053\uff1a\u201c\u4ed6\u4eec\u5df2\u7b54\u5141\u4e2d\u5348\u65f6\u5206\uff0c\u5728\u8fce\u5bbe\u9601\u4e2d\u89c1\u9762\u3002\u201d \u3000\u3000\u5929\u865a\u5b50\u70b9\u70b9\u5934\u3002 \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u5c5e\u4e0b\u544a\u9000\uff0c\u5148\u5230\u8fce\u5bbe\u9601\u53bb\u5b89\u6392\u4e00\u4e0b\u3002\u201d \u3000\u3000\u201c\u97e9\u9738\uff0c\u5c3d\u91cf\u907f\u5f00\u51b2\u7a81\u3002\u201d\u9093\u98de\u65e0\u9650\u5173\u6000\u5730\u8bf4\u3002 \u3000\u3000\u4ed6\u5bf9\u8fd9\u4f4d\u5c5e\u4e0b\uff0c\u662f\u8d8a\u6765\u8d8a\u770b\u91cd\u4e86\u3002 \u3000\u3000\u6c5f\u67ab\u7b11\u7740\u70b9\u70b9\u5934\uff0c\u8f6c\u8eab\u51fa\u5385\u3002 \u3000\u3000\u8fd9\u4e00\u6b21\uff0c\u4ed6\u5e26\u8d70\u4e86\u4e03\u5b9d\u548c\u5c1a\u7b49\u4eba\u3002 \u3000\u3000\u8fce\u5bbe\u9601\u7684\u638c\u67dc\uff0c\u672c\u662f\u9093\u98de\u7684\u4eb2\u4fe1\uff0c\u4f46\u5374\u56e0\u6625\u82b1\u56db\u5a62\u7684\u5148\u5230\uff0c\u63a7\u5236\u4e86\u6574\u4e2a\u7684\u5c40\u52bf\u3002 \u3000\u3000\u5728\u56db\u5b63\u82b1\u5a62\u4e2d\uff0c\u6c5f\u67ab\u548c\u6625\u82b1\u56db\u5a62\u5904\u5f97\u6700\u597d\uff0c\u56db\u4e2a\u82b1\u5973\uff0c\u5bf9\u4ed6\u4ecd\u4fdd\u6709\u4e00\u4efd\u656c\u610f\u3002 \u3000\u3000\u201c\u526f\u603b\u7ba1\uff0c\u6211\u4eec\u5949\u547d\u6765\u6b64\uff0c\u9884\u4f5c\u5e03\u7f6e\u2026\u2026\u201d\u6843\u82b1\u8bf4\uff0c\u201c\u6211\u4eec\u4e0d\u5e0c\u671b\u548c\u526f\u603b\u7ba1\u51b2\u7a81\uff0c\u4f46\u6211\u4eec\u4e5f\u4e0d\u80fd\u5c31\u6b64\u9000\u51fa\u53bb\uff0c\u8fd9\u8981\u600e\u4e48\u529e\u5462\uff1f\u201d \u3000\u3000\u201c\u6211\u4eec\u53ef\u4ee5\u5408\u4f5c\u2026\u2026\u201d\u6c5f\u67ab\u8bf4\uff0c\u201c\u5929\u9a6c\u5802\u4e5f\u6d3e\u6765\u4e86\u7edd\u9876\u9ad8\u624b\uff0c\u8fd9\u4e00\u6b21\u8c08\u5224\u5931\u8d25\uff0c\u53ef\u80fd\u4f1a\u5f15\u8d77\u4e00\u573a\u5927\u6218\uff0c\u6211\u4eec\u867d\u7136\u4e0d\u662f\u4e3b\u8981\u7684\u4eba\u7269\uff0c\u4f46\u5374\u672a\u5fc5\u4f1a\u6709\u4e0a\u4e00\u6b21\u540c\u6837\u7684\u5e78\u8fd0\u4e86\uff0c\u8bf4\u4e0d\u5b9a\uff0c\u628a\u6211\u4eec\u90fd\u5377\u8fdb\u53bb\uff0c\u6240\u4ee5\uff0c\u6211\u4eec\u6700\u597d\u4e0d\u8981\u628a\u4e8b\u60c5\u95f9\u8d77\u6765\uff0c\u7ed9\u4ed6\u4eec\u5e03\u7f6e\u4e00\u4e2a\u5f88\u597d\u7684\u8c08\u5224\u73af\u5883\uff0c\u4e5f\u8bb8\u4f1a\u4f7f\u4ed6\u4eec\u5fc3\u60c5\u6109\u5feb\uff0c\u8c08\u5224\u987a\u5229\uff0c\u514d\u53bb\u4e86\u4e00\u573a\u6740\u622e\u3002\u201d \u3000\u3000\u201c\u6709\u9053\u7406\u554a\u2026\u2026\u201d\u6843\u82b1\u9053\uff1a\u201c\u53ef\u662f\u6211\u4eec\u600e\u4e48\u5408\u4f5c\u5462\uff1f\u201d \u3000\u3000\u201c\u7b2c\u4e00\uff0c\u90fd\u4e0d\u80fd\u5b58\u6709\u79c1\u5fc3\uff0c\u8bbe\u4e0b\u6697\u7b97\u5bf9\u65b9\u7684\u5e03\u7f6e\u2026\u2026\u201d\u6c5f\u67ab\u8bf4\uff0c\u201c\u7b2c\u4e8c\uff0c\u53a8\u4e0b\u9152\u83dc\uff0c\u4e5f\u8981\u5404\u6d3e\u4e00\u4eba\u76d1\u89c6\uff0c\u4f7f\u53cc\u65b9\u4eba\u9a6c\uff0c\u80fd\u5403\u5f97\u5b89\u5fc3\u3002\u201d \u3000\u3000\u201c\u597d\u2026\u2026\u201d\u6843\u82b1\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u8fd8\u6709\u7b2c\u4e09\u5417\uff1f\u201d \u3000\u3000\u201c\u6709\uff01\u4e0a\u83dc\u8fdb\u9152\u7684\u4f8d\u5e94\u4eba\u5458\uff0c\u90fd\u8981\u7ecf\u8fc7\u641c\u8eab\uff0c\u4e0d\u80fd\u6697\u5e26\u5175\u5203\u3001\u6bd2\u836f\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u4f5c\u597d\u8fd9\u4e09\u4ef6\u4e8b\u60c5\uff0c\u6211\u4eec\u5df2\u7b97\u5c3d\u5230\u4e86\u5fc3\uff0c\u4ed6\u4eec\u80fd\u4e0d\u80fd\u8c08\u5224\u6210\u529f\uff0c\u90a3\u5c31\u4e0d\u662f\u6211\u4eec\u80fd\u7ba1\u7684\u4e8b\u4e86\u3002\u201d \u3000\u3000\u201c\u597d\u5427\uff01\u5c31\u7167\u4f60\u7684\u610f\u601d\u529e\u3002\u201d \u3000\u3000\u6625\u82b1\u56db\u5a62\u548c\u6c5f\u67ab\u826f\u597d\u7684\u5408\u4f5c\uff0c\u679c\u7136\u5e03\u7f6e\u51fa\u4e86\u4e00\u4e2a\u6c14\u6c1b\u5f88\u597d\u7684\u8bae\u4e8b\u6240\u5728\u3002 \u3000\u3000\u5929\u865a\u5b50\u3001\u4e5d\u5934\u9e1f\u3001\u9093\u98de\u4e09\u4eba\u4e0e\u4f1a\u3002 \u3000\u3000\u5185\u5bab\u4e00\u7cfb\u4e2d\u7531\u6765\u7684\u4e00\u7537\u4e00\u5973\u548c\u6885\u82b1\u53c2\u4e0e\u3002 \u3000\u3000\u5b88\u5728\u8bae\u573a\u7684\u4eba\uff0c\u662f\u6625\u82b1\u56db\u5a62\u3001\u97e9\u9738\u3001\u80e1\u840d\u3001\u6bb5\u4e5d\u548c\u4e03\u5b9d\u548c\u5c1a\u3002 \u3000\u3000\u771f\u6b63\u7684\u6740\u673a\u3001\u7d27\u5f20\uff0c\u5374\u662f\u53cc\u65b9\u672a\u53c2\u4e0e\u4f1a\u8bae\u7684\u4eba\uff0c\u4ed6\u4eec\u5e26\u7740\u5175\u5203\uff0c\u5b88\u5019\u8fce\u5bbe\u9601\u5916\u7684\u82b1\u6811\u6797\u4e2d\uff0c\u4e00\u65e6\u4f1a\u8bae\u95f9\u7ffb\uff0c\u5927\u6218\u7acb\u5373\u5f00\u5c55\u3002 \u3000\u3000\u8fd9\u4e9b\u4eba\u5305\u62ec\u4e86\u590f\u3001\u79cb\u56db\u5a62\u7684\u8377\u82b1\u3001\u83f1\u82b1\u3001\u83ca\u82b1\u3001\u6842\u82b1\uff0c\u548c\u5341\u4e8c\u4eba\u7cbe\u9009\u7684\u9556\u624b\u3002 \u3000\u3000\u5929\u9a6c\u5802\u4ee5\u4e24\u4e2a\u6765\u7684\u7ea2\u8863\u4eba\u4e3a\u9996\uff0c\u52a0\u4e0a\u90ed\u5929\u540c\u3001\u80e1\u5143\uff0c\u5373\u9093\u98de\u56db\u4e2a\u4eb2\u4fe1\u4f8d\u536b\u3002 \u3000\u3000\u5929\u865a\u5b50\u671b\u671b\u5bf9\u9762\u800c\u5750\u7684\u5e74\u8f7b\u4eba\uff0c\u7b11\u9053\uff1a\u201c\u8001\u9053\u5929\u865a\u5b50\uff0c\u662f\u5929\u9a6c\u603b\u5802\u7684\u4e09\u4f4d\u526f\u603b\u5802\u4e3b\u4e4b\u4e00\uff0c\u9601\u4e0b\u662f\u2026\u2026\u201d \u3000\u3000\u201c\u5185\u5bab\u5bab\u4e3b\u7684\u9996\u5ea7\u5f1f\u5b50\uff0c\u91d1\u5e26\u6b66\u58eb\u7684\u73ed\u5934\uff0c\u9ec4\u9f99\u3002\u201d \u3000\u3000\u201c\u597d\u3001\u597d\uff0c\u8001\u9053\u76f8\u4fe1\u4f60\u53ef\u4ee5\u4f5c\u4e3b\u4e86\u3002\u201d \u3000\u3000\u201c\u5728\u4e0b\u5df2\u5f97\u5bab\u4e3b\u6388\u6743\u2026\u2026\u201d\u9ec4\u9f99\u9053\uff1a\u201c\u5982\u662f\u8c08\u7684\u4e8b\u8d85\u8d8a\u4e86\u9ec4\u67d0\u7684\u6743\u9650\uff0c\u5728\u4e0b\u4f1a\u5411\u5bab\u4e3b\u8bf7\u793a\u3002\u201d \u3000\u3000\u201c\u5929\u9a6c\u5802\u548c\u5185\u5bab\u672c\u662f\u4e00\u4e2a\u7ec4\u5408\u4e2d\u4eba\uff0c\u5408\u529b\u521b\u529e\u4e86\u6d1b\u9633\u5c45\uff0c\u52a0\u4e0a\u4e86\u5929\u738b\u95e8\u4e2d\u4eba\uff0c\u4e09\u8db3\u9f0e\u7acb\uff0c\u5404\u6709\u804c\u638c\uff0c\u4f46\u8d35\u5bab\u4e2d\u4eba\uff0c\u5374\u7a81\u7136\u5411\u603b\u7ba1\u9093\u98de\u7a81\u88ad\uff0c\u800c\u4e14\uff0c\u6740\u4e86\u672c\u5802\u4e2d\u7684\u957f\u8001\u4e01\u897f\u5c71\u548c\u62a4\u6cd5\u5218\u6e05\uff0c\u53c8\u91cd\u521b\u4e00\u4f4d\u5468\u6e90\u62a4\u6cd5\u2026\u2026\u201d\u5929\u865a\u5b50\u9053\uff1a\u201c\u9601\u4e0b\u53ef\u6709\u4ec0\u4e48\u89e3\u91ca\uff1f\u201d \u3000\u3000\u201c\u4e8b\u5b9e\u4e0a\uff0c\u7528\u4e0d\u7740\u89e3\u91ca\u2026\u2026\u201d \u3000\u3000\u201c\u8fd9\u8bdd\u600e\u4e48\u8bf4\uff1f\u201d\u5929\u865a\u5b50\u8bed\u6c14\u5df2\u8f6c\u51b7\u6f20\u3002 \u3000\u3000\u201c\u56e0\u4e3a\uff0c\u9093\u98de\u5148\u6740\u4e86\u672c\u5bab\u6d3e\u9a7b\u5728\u6b64\u7684\u526f\u603b\u7ba1\u738b\u9f99\u2026\u2026\u201d\u9ec4\u9f99\u9053\uff1a\u201c\u4ed6\u662f\u5bab\u4e3b\u95e8\u4e0b\u7684\u4e09\u9f99\u4e4b\u4e00\uff0c\u4e5f\u662f\u9ec4\u67d0\u4eba\u7684\u5e08\u5f1f\uff0c\u5982\u679c\u4e00\u5200\u4e00\u5251\u7684\u640f\u6740\u800c\u6b7b\uff0c\u90a3\u4e5f\u7f62\u4e86\uff0c\u4f46\u6211\u76f8\u4fe1\uff0c\u4ed6\u662f\u8eab\u906d\u6697\u7b97\u800c\u6b7b\u3002\u201d \u3000\u3000\u201c\u8001\u9053\u542c\u8bf4\uff0c\u738b\u526f\u603b\u7ba1\u662f\u6297\u62d2\u603b\u7ba1\u9093\u98de\u7684\u4ee4\u8c15\uff0c\u51fa\u624b\u72af\u4e0a\uff0c\u624d\u88ab\u6740\u6b7b\u7684\u3002\u201d \u3000\u3000\u9ec4\u9f99\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u9ec4\u67d0\u4eba\u5f88\u96be\u76f8\u4fe1\u9093\u603b\u7ba1\u80fd\u6740\u6b7b\u6211\u90a3\u4f4d\u5e08\u5f1f\u3002\u6211\u8bf4\u8fc7\uff0c\u5982\u82e5\u4ed6\u662f\u640f\u6218\u800c\u6b7b\uff0c\u53ea\u602a\u4ed6\u5b66\u827a\u4e0d\u7cbe\uff0c\u6b7b\u4e86\u7b97\u5566\uff0c\u4f46\u5982\u662f\u8eab\u53d7\u6697\u7b97\uff0c\u90a3\u5c31\u662f\u6709\u8ba1\u5212\u7684\u8c0b\u6740\u884c\u4e3a\u4e86\u3002\u201d \u3000\u3000\u5929\u865a\u5b50\u4e00\u76b1\u7709\u5934\uff0c\u9053\uff1a\u201c\u9093\u98de\uff0c\u738b\u9f99\u7684\u5c38\u4f53\u57cb\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u201c\u5927\u767d\u5929\u8fd0\u5c38\u4e0d\u4fbf\uff0c\u5df2\u4e3a\u836f\u7269\u5316\u53bb\u2026\u2026\u201d\u9093\u98de\u53e3\u4e2d\u56de\u7b54\uff0c\u5fc3\u4e2d\u5374\u662f\u60ca\u9a87\u4e07\u5206\uff0c\u738b\u9f99\u9690\u533f\u4ed6\u7684\u8eab\u4efd\uff0c\u4eca\u65e5\u624d\u77e5\u4ed6\u662f\u5185\u5bab\u5bab\u4e3b\u7684\u95e8\u4e0b\u5f1f\u5b50\uff0c\u4e09\u9f99\u4e4b\u4e00\uff0c\u8fd9\u4e48\u4e00\u4f4d\u4eba\u7269\uff0c\u97e9\u9738\u7528\u7684\u4ec0\u4e48\u6b66\u529f\uff0c\u8ba9\u6211\u4e00\u638c\u5c31\u628a\u4eba\u6253\u6b7b\u4e86\u3002 \u3000\u3000\u201c\u8fd9\u5c31\u5f79\u529e\u6cd5\u67e5\u4e86\u2026\u2026\u201d\u5929\u865a\u5b50\u9053\uff1a\u201c\u4e0d\u77e5\u9ec4\u73ed\u5934\u6709\u4f55\u826f\u7b56\uff1f\u201d \u3000\u3000\u201c\u4e0d\u662f\u6ca1\u6709\u529e\u6cd5\u2026\u2026\u201d\u9ec4\u9f99\u8bf4\uff0c\u201c\u8ba9\u65e7\u4e8b\u91cd\u6f14\u4e00\u904d\uff0c\u5c31\u53ef\u4ee5\u8bc1\u660e\u4e86\uff01\u201d \u3000\u3000\u201c\u65e7\u4e8b\u91cd\u6f14\u2026\u2026\u201d\u5929\u865a\u5b50\u9053\uff1a\u201c\u9ec4\u73ed\u5934\u53ef\u5426\u8bf4\u7684\u6e05\u695a\u4e00\u4e9b\uff1f\u201d \u3000\u3000\u201c\u597d\uff01\u5728\u4e0b\u81ea\u4efb\u738b\u526f\u603b\u7ba1\uff0c\u8bf7\u9093\u603b\u7ba1\u628a\u90a3\u5929\u53d1\u751f\u4e8b\u6545\u7684\u7ecf\u8fc7\uff0c\u91cd\u884c\u6f14\u4e60\u4e00\u904d\u2026\u2026\u201d \u3000\u3000\u201c\u90a3\u662f\u5305\u62ec\u4e00\u638c\u51fb\u6bd9\u4e86\u2026\u2026\u201d \u3000\u3000\u6253\u65ad\u4e86\u9093\u98de\u7684\u8bdd\uff0c\u9ec4\u9f99\u63a5\u9053\uff1a\u201c\u5982\u679c\u9093\u603b\u7ba1\u80fd\u4e00\u638c\u628a\u6211\u9ec4\u9f99\u6253\u6b7b\uff0c\u90a3\u5c31\u4e0d\u7528\u8ffd\u7a76\u738b\u9f99\u7684\u6b7b\u56e0\u4e86\uff01\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u2026\u2026\u8fd9\u4e2a\u2026\u2026\u201d\u9093\u98de\u6709\u53e3\u96be\u8a00\u3002 \u3000\u3000\u201c\u600e\u4e48\u56de\u4e8b\uff0c\u541e\u541e\u5410\u5410\u7684\uff0c\u96be\u9053\u51e0\u53e5\u8bdd\u5c31\u8bf4\u4e0d\u6e05\u695a\u4e48\uff1f\u201d\u5929\u865a\u5b50\u5df2\u6709\u4e0d\u60a6\u4e4b\u8272\u3002 \u3000\u3000\u201c\u56de\u526f\u603b\u5802\u4e3b\u7684\u8bdd\uff0c\u6211\u53ea\u662f\u4f20\u51fa\u53e3\u8c15\uff0c\u53ef\u6ca1\u6709\u4eb2\u624b\u6740\u4ed6\u3002\u201d \u3000\u3000\u201c\u90a3\u662f\u4ec0\u4e48\u4eba\u4e0b\u7684\u624b\u5462\uff1f\u201d\u5929\u865a\u5b50\u4e5f\u6709\u4e9b\u597d\u5947\u4e86\uff0c\u4e00\u638c\u80fd\u51fb\u6bd9\u5185\u5bab\u4e8c\u9f99\u4e4b\u4e00\uff0c\u8fd9\u4e2a\u975e\u540c\u5c0f\u53ef\u3002 \u3000\u3000\u201c\u6211\u2026\u2026\u201d\u97e9\u9738\u633a\u8eab\u800c\u51fa\uff0c\u9053\uff1a\u201c\u738b\u9f99\u4ee5\u4e0b\u72af\u4e0a\uff0c\u7f6a\u6709\u5e94\u5f97\u3002\u201d \u3000\u3000\u5168\u573a\u4e2d\u4eba\u5927\u90fd\u6123\u4f4f\u4e86\uff0c\u5929\u865a\u5b50\u3001\u4e5d\u5934\u9e1f\u3001\u9ec4\u9f99\u3001\u6885\u82b1\u548c\u90a3\u8eab\u62ab\u94f6\u72d0\u76ae\u62ab\u98ce\u7684\u5c11\u5973\uff0c\u4e00\u9053\u773c\u795e\uff0c\u5168\u90fd\u6295\u6ce8\u5728\u6c5f\u67ab\u7684\u8eab\u4e0a\u3002 \u3000\u3000\u201c\u97e9\u9738\uff0c\u4f60\u4e0d\u8981\u5984\u56fe\u9876\u7f6a\uff0c\u8fd9\u8981\u771f\u672c\u9886\uff0c\u786c\u5de5\u592b\u624d\u884c\u2026\u2026\u201d \u3000\u3000\u6885\u82b1\u8bf4\uff0c\u201c\u4f60\u5c31\u662f\u7acb\u523b\u81ea\u520e\u6b7b\u4e86\uff0c\u4e5f\u65e0\u6cd5\u63a9\u62a4\u5143\u51f6\u3002\u201d \u3000\u3000\u201c\u8001\u9053\u4e5f\u6709\u4e9b\u4e0d\u592a\u76f8\u4fe1\uff0c\u9093\u98de\uff0c\u8fd9\u7a76\u7adf\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d \u3000\u3000\u201c\u662f\u771f\u7684\uff0c\u2026\u2026\u201d\u9093\u98de\u9053\uff1a\u201c\u6211\u8bf4\u8be5\u6b7b\uff0c\u97e9\u9738\u5c31\u4e00\u638c\u51fb\u51fa\uff0c\u6253\u6b7b\u4e86\u738b\u9f99\u3002\u201d \u3000\u3000\u9ec4\u9f99\u9053\uff1a\u201c\u738b\u9f99\u5e08\u5f1f\u7684\u6b66\u529f\uff0c\u867d\u7136\u5dee\u6211\u4e00\u6bb5\uff0c\u4f46\u5374\u975e\u4f60\u80fd\u4e00\u62f3\u51fb\u6bd9\uff0c\u4f55\u51b5\u97e9\u9738\u4e86\uff1f\u201d \u3000\u3000\u5929\u865a\u5b50\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u97e9\u9738\uff0c\u8bb2\u5b9e\u8bdd\uff0c\u5982\u6709\u865a\u9970\u63a9\u906e\uff0c\u6211\u8981\u628a\u4f60\u4e71\u5251\u5206\u5c38\uff01\u201d \u3000\u3000\u201c\u5b57\u5b57\u771f\u5b9e\u554a\uff01\u526f\u603b\u5802\u4e3b\u4e0d\u4fe1\uff0c\u4f55\u4e0d\u8ba9\u65e7\u4e8b\u91cd\u6f14\u4e00\u6b21\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u90a3\u5c31\u53ef\u4ee5\u5b8c\u5168\u8bc1\u660e\u4e86\u3002\u201d \u3000\u3000\u201c\u4f60\u5982\u5931\u624b\u5462\uff1f\u2026\u2026\u201d \u3000\u3000\u5929\u865a\u5b50\u9053\uff1a\u201c\u97e9\u9738\uff0c\u4f60\u4e00\u638c\u6253\u4e0d\u6b7b\u4eba\uff0c\u600e\u4e48\u4ea4\u5f85\uff1f\u201d \u3000\u3000\u201c\u4efb\u51ed\u526f\u603b\u5802\u4e3b\u5904\u7f6e\uff0c\u2026\u2026\u4e0d\u8fc7\uff0c\u6211\u90a3\u4e00\u638c\uff0c\u738b\u9f99\u6ca1\u6709\u95ea\u907f\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u91cd\u6f14\u65e7\u4e8b\uff0c\u626e\u88c5\u738b\u9f99\u7684\u4eba\uff0c\u4e5f\u4e0d\u80fd\u95ea\u907f\u624d\u884c\u3002\u201d \u3000\u3000\u5929\u865a\u5b50\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u9ec4\u73ed\u5934\uff0c\u4f60\u89c9\u7740\u97e9\u9738\u7684\u610f\u89c1\u5982\u4f55\uff1f\u201d \u3000\u3000\u9ec4\u9f99\u4e24\u9053\u76ee\u5149\u76ef\u6ce8\u5728\u6c5f\u67ab\u7684\u8138\u4e0a\uff0c\u770b\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u4f60\u662f\u8bf4\uff0c\u4f60\u662f\u653b\u5176\u4e0d\u5907\uff1f\u201d \u3000\u3000\u201c\u738b\u526f\u603b\u7ba1\u6709\u6ca1\u6709\u51c6\u5907\uff1f\u6211\u4e0d\u77e5\u9053\uff0c\u4f46\u6211\u7edd\u4e0d\u662f\u6697\u7b97\u4ed6\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u8bf4\uff0c\u201c\u4ed6\u6297\u62d2\u4e0a\u547d\uff0c\u7f6a\u4e0d\u53ef\u8d66\uff0c\u603b\u7ba1\u8bf4\u4ed6\u8be5\u6b7b\uff0c\u6211\u5c31\u51fb\u51fa\u4e00\u638c\uff0c\u5012\u662f\u60f3\u4e0d\u5230\u4f1a\u628a\u4ed6\u6253\u6b7b\u4e86\uff01\u201d \u3000\u3000\u201c\u4e0d\u53ef\u80fd\u7684\uff0c\u5c31\u7b97\u738b\u5e08\u5f1f\u6ca1\u6709\u6212\u5907\uff0c\u4f60\u90a3\u4e00\u638c\uff0c\u4e5f\u672a\u5fc5\u80fd\u6253\u4e2d\u4ed6\uff0c\u6253\u4e2d\u4e86\uff0c\u4e5f\u672a\u5fc5\u81f4\u547d\uff0c\u9664\u975e\u4ed6\u5df2\u5148\u53d7\u6697\u7b97\u2026\u2026\u201d\u9ec4\u9f99\u9053\uff1a\u201c\u4f60\u5c0f\u5b50\u540d\u4e0d\u89c1\u7ecf\u4f20\uff0c\u90a3\u4f1a\u6709\u5982\u6b64\u672c\u9886\u3002\u201d \u3000\u3000\u6c5f\u67ab\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u9601\u4e0b\u5462\uff1f\u4f60\u9ec4\u9f99\u8fd9\u4e2a\u540d\u5b57\uff0c\u97e9\u67d0\u4e5f\u662f\u7b2c\u4e00\u6b21\u542c\u4eba\u8bf4\u8d77\u2026\u2026\u201d \u3000\u3000\u9ec4\u9f99\u8138\u8272\u4e00\u53d8\uff0c\u9053\uff1a\u201c\u6211\u8981\u7acb\u523b\u6740\u4e86\u4f60\u2026\u2026\u201d\u53cc\u80a9\u4e00\u6643\uff0c\u4eba\u5df2\u79bb\u5750\u98de\u8d77\uff0c\u843d\u5728\u5385\u4e2d\u3002 \u3000\u3000\u4ed6\u5b9e\u5728\u5fcd\u4e0d\u4f4f\u50cf\u97e9\u9738\u8fd9\u6837\u8eab\u4efd\u7684\u4eba\uff0c\u51fa\u8a00\u6c61\u8fb1\uff0c\u6fc0\u6012\u4e4b\u4e0b\uff0c\u4f3c\u5df2\u4e71\u4e86\u65b9\u5bf8\u3002 \u3000\u3000\u5929\u865a\u5b50\u5fae\u4e00\u6446\u5934\uff0c\u4e5d\u5934\u9e1f\u5201\u9e4f\u7ad9\u8d77\u8eab\u5b50\uff0c\u53cc\u80a9\u4e00\u6643\uff0c\u53c8\u89c1\u4ed6\u4e3e\u6b65\u626c\u817f\uff0c\u4eba\u5df2\u98d8\u843d\u5230\u6c5f\u67ab\u7684\u8eab\u4fa7\uff0c\u9053\uff1a\u201c\u9ec4\u73ed\u5934\uff0c\u54b1\u4eec\u6b63\u5728\u8bb2\u7406\uff0c\u600e\u4e48\u5ffd\u7136\u53d8\u4e86\u8138\u8981\u51fa\u624b\u6740\u4eba\u54ea\uff01 \u3000\u3000\u201c\u97e9\u526f\u603b\u7ba1\u51fa\u8a00\u65e0\u72b6\uff0c\u6211\u8981\u6559\u8bad\u4ed6\u4e00\u987f\uff0c\u2026\u2026\u201d \u3000\u3000\u201c\u8981\u6559\u8bad\u97e9\u526f\u603b\u7ba1\uff0c\u4e5f\u8f6e\u4e0d\u5230\u4f60\u51fa\u624b\u2026\u2026\u201d\u5201\u9e4f\u6253\u65ad\u4e86\u9ec4\u9f99\u7684\u8bdd\uff0c\u63a5\u9053\uff1a\u201c\u5929\u9a6c\u5802\u7684\u526f\u603b\u5802\u4e3b\u5929\u865a\u5b50\u9053\u957f\u5728\u6b64\uff0c\u518d\u8bf4\u5201\u67d0\u4eba\u4ee5\u957f\u8001\u7684\u8eab\u4efd\uff0c\u4e5f\u53ef\u4ee5\u5904\u7406\u97e9\u526f\u603b\u7ba1\u7684\u4e8b\u3002\u201d \u3000\u3000\u201c\u662f\u554a\uff01\u2026\u2026\u201d\u5929\u865a\u5b50\u63a5\u53e3\u8bf4\uff0c\u201c\u4f60\u8981\u65e7\u4e8b\u91cd\u6f14\uff0c\u8001\u9053\u540c\u610f\uff0c\u9ec4\u73ed\u5934\u53ef\u662f\u8981\u6539\u53d8\u4e3b\u610f\uff0c\u51fa\u5c14\u53cd\u5c14\uff1f\u201d \u3000\u3000\u201c\u90a3\u5012\u4e0d\u7528\uff0c\u53ea\u662f\u52a0\u4e0a\u4e00\u70b9\u5f69\u5934\uff1f\u2026\u2026\u201d\u9ec4\u9f99\u9053\uff1a\u201c\u5982\u82e5\u97e9\u526f\u603b\u7ba1\u4e00\u638c\u53d6\u4e0d\u4e86\u5728\u4e0b\u7684\u6027\u547d\uff0c\u90a3\u5c31\u628a\u4ed6\u4ea4\u7531\u5728\u4e0b\u5904\u7f6e\u5982\u4f55\uff1f\u201d \u3000\u3000\u5929\u865a\u5b50\u77a7\u7740\u5201\u9e4f\uff0c\u9053\uff1a\u201c\u5c0f\u5201\uff0c\u4f60\u770b\u8fd9\u4e2a\u6761\u4ef6\u5982\u4f55\uff1f\u8be5\u4e0d\u8be5\u7b54\u5e94\u4eba\u5bb6\uff1f\u201d \u3000\u3000\u4ed6\u8bf4\u8bdd\u6162\u6761\u65af\u7406\uff0c\u8bed\u6c14\u4e5f\u5f88\u548c\u5584\uff0c\u4f46\u5185\u5bb9\u5374\u662f\u6076\u6bd2\u3001\u523b\u8584\uff0c\u53ea\u8981\u6697\u4e2d\u793a\u610f\uff0c\u5374\u8981\u522b\u4eba\u8bf4\u51fa\u7ed3\u8bba\uff0c\u53bb\u4f5c\u6076\u4eba\u3002 \u3000\u3000\u201c\u5201\u67d0\u7684\u770b\u6cd5\uff0c\u5982\u679c\u97e9\u9738\u5931\u624b\u632b\u8d25\uff0c\u54b1\u4eec\u5012\u4e5f\u662f\u4e0d\u4fbf\u7ef4\u62a4\u4ed6\u4e86\u3002\u201d \u3000\u3000\u6c5f\u67ab\u6697\u9053\uff1a\u201c\u8fd9\u725b\u9f3b\u5b50\u8001\u9053\u751f\u6027\u597d\u751f\u51c9\u8584\uff0c\u52ff\u602a\u4ed6\u65e0\u6cd5\u5728\u6b66\u5f53\u6d3e\u4e2d\u7acb\u8db3\uff0c\u6b63\u5927\u95e8\u6237\u4e4b\u4e2d\uff0c\u5c82\u5bb9\u4e0b\u8fd9\u7b49\u9634\u9669\u4eba\u7269\u3002\u201d \u3000\u3000\u201c\u97e9\u9738\uff0c\u4f60\u8981\u81ea\u6c42\u591a\u798f\u4e86\u2026\u2026\u201d\u5929\u865a\u5b50\u8bf4\uff0c\u201c\u9ec4\u73ed\u5934\u8bf4\u5f97\u6709\u7406\uff0c\u5201\u957f\u8001\u4e5f\u8868\u8d5e\u540c\uff0c\u8001\u9053\u4e5f\u4e0d\u4fbf\u7ef4\u62a4\u4f60\u4e86\u3002\u201d \u3000\u3000\u201c\u2026\u2026\u8bf4\u7684\u4e5f\u662f\uff0c\u9ec4\u73ed\u5934\u7ad9\u7740\u4e0d\u52a8\u7684\u8ba9\u6211\u6253\u4e00\u638c\uff0c\u5982\u662f\u6211\u6ca1\u6709\u529e\u6cd5\u53d6\u4eba\u6027\u547d\uff0c\u526f\u603b\u5802\u4e3b\u628a\u97e9\u67d0\u4ea4\u7ed9\u4eba\u5bb6\u5904\u7f6e\uff0c\u5012\u4e5f\u662f\u5408\u60c5\u5408\u7406\u4e86\u3002\u201d \u3000\u3000\u6c5f\u67ab\u6ca1\u6709\u54c0\u6c42\u5929\u865a\u5b50\u4f53\u5ff5\u4e0b\u5c5e\uff0c\u4e88\u4ee5\u7ef4\u62a4\uff0c\u53cd\u800c\u5927\u52a0\u8d5e\u540c\u5929\u865a\u5b50\u7684\u51b3\u5b9a\uff0c\u8fd9\u4e0d\u4f46\u5927\u51fa\u4e86\u5929\u865a\u5b50\u7684\u610f\u6599\u4e4b\u5916\uff0c\u8fde\u4e5d\u5934\u9e1f\u4e5f\u6709\u70b9\u83ab\u6d4b\u9ad8\u6df1\uff0c\u5446\u5446\u7684\u671b\u7740\u6c5f\u67ab\u51fa\u795e\u3002 \u3000\u3000\u4f46\u9093\u98de\u7684\u8138\u8272\u5374\u5f88\u96be\u770b\uff0c\u4ed6\u660e\u767d\u8fd9\u4e2a\u540e\u679c\uff0c\u97e9\u9738\u7edd\u5bf9\u6ca1\u6709\u529e\u6cd5\uff0c\u5728\u9ec4\u9f99\u5168\u529b\u6212\u5907\u4e4b\u4e0b\uff0c\u4e00\u51fb\u53d6\u547d\uff0c\u8fd9\u4e00\u6ce8\u662f\u8f93\u5b9a\u4e86\uff0c\u628a\u97e9\u9738\u4ea4\u8fc7\u53bb\uff0c\u4f1a\u6709\u4e9b\u4ec0\u4e48\u6837\u7684\u906d\u9047\uff1f\u90a3\u5c31\u4e0d\u5fcd\u60f3\u4e0b\u53bb\u4e86\uff0c\u5b9a\u7136\u5341\u5206\u60b2\u60e8\u3002 \u3000\u3000\u4f46\u6700\u9707\u60ca\u7684\u5374\u662f\u9ec4\u9f99\uff0c\u97e9\u9738\u7684\u53cd\u5e94\uff0c\u5b8c\u5168\u51fa\u4e86\u4ed6\u7684\u610f\u6599\u4e4b\u5916\uff0c\u96be\u9053\u8fd9\u4e2a\u4e16\u754c\u4e0a\u771f\u6709\u89c6\u6b7b\u5982\u5f52\u7684\u4eba \u3000\u3000\u9ec4\u9f99\u770b\u5230\u8fc7\u8bad\u7ec3\u6740\u624b\u7684\u65b9\u6cd5\uff0c\u5b8c\u5168\u662f\u62b9\u6740\u4e86\u4ed6\u4eec\u7684\u4eba\u6027\u3001\u5fc3\u667a\uff0c\u624d\u80fd\u6210\u529f\u7684\u8bad\u7ec3\u51fa\u4e00\u4e2a\u4e0d\u77e5\u9053\u6b7b\u4ea1\u53ef\u6015\u7684\u6740\u624b\u3002 \u3000\u3000\u4f46\u97e9\u9738\u5e76\u6ca1\u6709\u5931\u53bb\u4eba\u6027\uff0c\u5fc3\u667a\u8ff7\u4e71\uff0c\u4e3a\u4ec0\u4e48\u4f1a\u5982\u6b64\u7684\u6c89\u7740\u3002 \u3000\u3000\u51b7\u9759\uff1f \u3000\u3000\u9664\u975e\u4ed6\u6709\u7740\u6781\u5f3a\u7684\u4fe1\u5fc3\uff0c\u4e00\u638c\u6bd9\u654c\uff0c\u4ed6\u4e0d\u662f\u5df2\u7ecf\u4e00\u638c\u6253\u6b7b\u4e86\u738b\u9f99\u5417\uff1f \u3000\u3000\u201c\u9ec4\u73ed\u5934\u2026\u2026\u201d\u5929\u865a\u4e8e\u9634\u9634\u7684\u8bf4\u9053\uff1a\u201c\u5982\u662f\u97e9\u9738\u8fd9\u5c0f\u5b50\u4e00\u638c\u628a\u4f60\u6253\u4f24\u4e86\uff0c\u8fd9\u80dc\u8d1f\u4e4b\u7ea6\uff0c\u5e94\u8be5\u5982\u4f55\u5206\u6cd5\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u2026\u2026\u8fd9\u4e2a\u2026\u2026\u201d\u9ec4\u9f99\u9053\uff1a\u201c\u81ea\u7136\u7b97\u662f\u6211\u9ec4\u67d0\u4eba\u8d62\u4e86\uff01\u201d \u3000\u3000\u201c\u8fd9\u5c31\u4e0d\u592a\u516c\u5e73\u4e86\u2026\u2026\u201d\u5929\u865a\u5b50\u9053\uff1a\u201c\u4e00\u638c\u6253\u6b7b\u4f60\uff0c\u6211\u4eec\u8d62\u4e86\uff0c\u4f24\u4e0d\u4e86\u4f60\uff0c\u6211\u4eec\u7b97\u8f93\uff0c\u4f46\u4e00\u638c\u6253\u4f24\u4f60\uff0c\u5e94\u8be5\u662f\u4e00\u4e2a\u4e0d\u80dc\u4e0d\u8d25\u7684\u5c40\u9762\uff0c\u4f60\u6ca1\u6709\u8d25\uff0c\u6211\u4eec\u4e5f\u4e0d\u80fd\u7b97\u80dc\u624d\u5bf9\u3002\u201d \u3000\u3000\u201c\u8fd9\u5c31\u5f88\u96be\u8bba\u65ad\u4e86\u2026\u2026\u201d\u9ec4\u9f99\u9053\uff1a\u201c\u771f\u8981\u516c\u5e73\uff0c\u90a3\u5c31\u8981\u754c\u9650\u7684\u5341\u5206\u6e05\u695a\u624d\u884c\uff0c\u97e9\u9738\u4e00\u638c\u5982\u80fd\u6253\u5f97\u6211\u4ec6\u5012\u4e8e\u5730\uff0c\u8fd8\u662f\u6682\u65f6\u65e0\u6cd5\u5f00\u53e3\u8bf4\u8bdd\uff0c\u5c31\u7b97\u4f60\u4eec\u8d62\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u597d\u5c31\u8fd9\u4e48\u529e\u2026\u2026\u201d\u5929\u865a\u5b50\u9053\uff1a\u201c\u97e9\u9738\uff0c\u4f60\u53ef\u4ee5\u8fd0\u96c6\u529f\u529b\uff0c\u51c6\u5907\u51fa\u624b\u4e86\uff01\u201d \u3000\u3000\u201c\u5728\u4e0b\u65e9\u5df2\u84c4\u52bf\u5f85\u547d\uff0c\u53ea\u5f85\u526f\u603b\u5802\u4e3b\u4e00\u58f0\u4ee4\u4e0b\u4e86\u3002\u201d \u3000\u3000\u201c\u597d\u9ec4\u73ed\u5934\u51c6\u5907\u597d\u4e86\u4e48\uff1f\u201d\u5929\u865a\u5b50\u6025\u6025\u5730\u8bf4\u3002 \u3000\u3000\u201c\u6162\u4e00\u70b9\u2026\u2026\u201d\u548c\u9ec4\u9f99\u540c\u6765\u7684\u5c11\u5973\uff0c\u7f13\u7f13\u7ad9\u8d77\uff0c\u89e3\u53bb\u8eab\u4e0a\u7684\u72d0\u76ae\u62ab\u98ce\uff0c\u9732\u51fa\u4e00\u8eab\u7384\u8272\u52b2\u88c5\uff0c\u63a5\u9053\uff1a\u201c\u97e9\u9738\u8fd9\u4e00\u638c\u662f\u4ed6\u4e2a\u4eba\u7684\u751f\u6b7b\uff0c\u8fd8\u662f\u4ee3\u8868\u4e86\u5929\u9a6c\u5802\u51fa\u6218\uff1f\u4e00\u638c\u6253\u6b7b\u4e86\u9ec4\u9f99\uff0c\u6211\u4eec\u56de\u5934\u8d70\u4e86\uff01\u6d1b\u9633\u5c45\u8fd8\u7ef4\u6301\u73b0\u5728\u8fd9\u4e2a\u5c40\u9762\uff0c\u5982\u662f\u97e9\u9738\u8d25\u4e86\uff0c\u8d35\u5802\u51c6\u5907\u5982\u4f55\u8868\u793a\uff1f\u201d \u3000\u3000\u201c\u6211\u770b\u97e9\u9738\u548c\u5929\u9a6c\u5802\u7684\u5173\u7cfb\u4e0d\u5927\uff0c\u4ed6\u8eab\u4efd\u5351\u5fae\uff0c\u5982\u4f55\u80fd\u4ee3\u8868\u5929\u9a6c\u5802\u2026\u2026\u201d\u5929\u865a\u5b50\u8bf4\u9053\uff1a \u3000\u3000\u201c\u6211\u8001\u9053\u624d\u7b97\u662f\u5929\u9a6c\u5802\u4e2d\u771f\u6b63\u7684\u4ee3\u8868\uff01\u201d \u3000\u3000\u6c5f\u67ab\u867d\u7136\u5168\u529b\u5728\u5e84\u7f29\u81ea\u5df1\uff0c\u4f46\u4ecd\u88ab\u5929\u865a\u5b50\u90a3\u53e5\u8eab\u4efd\u5351\u5fae\u7684\u8bdd\u6240\u6fc0\u6012\uff0c\u5fd6\u9053\uff1a\u201c\u4ed6\u51fa\u8eab\u6b63\u5927\u95e8\u6237\u4e2d\u4eba\uff0c\u5fc3\u5730\u7684\u9634\u9669\u6076\u6bd2\uff0c\u5c24\u8fc7\u7eff\u6797\u9053\u4e0a\u4eba\u7269\uff0c\u51fa\u5356\u6b66\u5f53\u95e8\u4e2d\u7684\u673a\u5bc6\uff0c\u4e5f\u5fc5\u7136\u5341\u5206\u5f7b\u5e95\u4e86\uff0c\u8fd9\u4e2a\u4eba\u7559\u4ed6\u4e0d\u5f97\u2026\u2026\u3002\u201d \u3000\u3000\u7384\u8863\u5c11\u5973\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u9ec4\u9f99\uff0c\u4f60\u592a\u5403\u4e8f\u4e86\uff0c\u97e9\u9738\u4e00\u638c\u80fd\u6253\u6b7b\u738b\u9f99\uff0c\u5176\u638c\u4e0a\u5de5\u592b\uff0c\u5fc5\u7136\u6709\u7279\u522b\u7684\u9020\u8be3\uff0c\u5c31\u7b97\u4e00\u638c\u6253\u4e0d\u6b7b\u4f60\uff0c\u4e5f\u53ef\u80fd\u8ba9\u4f60\u53d7\u5230\u4f24\u5bb3\uff0c\u4f46\u97e9\u9738\u5728\u5929\u9a6c\u5802\u4e2d\uff0c\u53ea\u662f\u4e2a\u5fae\u4e0d\u8db3\u9053\u7684\u89d2\u8272\uff0c\u4f60\u8981\u5feb\u610f\u6069\u4ec7\uff0c\u51fa\u624b\u6740\u4e86\u4ed6\u5c31\u662f\uff0c\u7528\u4e0d\u7740\u8ba2\u4e0b\u8fd9\u4e00\u638c\u4e4b\u7ea6\uff01\u201d \u3000\u3000\u201c\u4e0d\u2026\u2026\u201d\u9ec4\u9f99\u4e24\u9053\u76ee\u5149\u51dd\u6ce8\u5728\u97e9\u9738\u7684\u8138\u4e0a\uff0c\u9053\uff1a\u201c\u6211\u82e5\u4e00\u5200\u6740\u4e86\u4ed6\uff0c\u592a\u4fbf\u5b9c\u4ed6\u4e86\uff0c\u6240\u4ee5\uff0c\u6211\u8981\u6316\u51fa\u4ed6\u7684\u5fc3\u809d\uff0c\u796d\u5960\u738b\u9f99\u5e08\u5f1f\uff0c\u4f46\u5929\u865a\u5b50\u4e0d\u662f\u6613\u4e0e\u7684\u654c\u4eba\uff0c\u53cc\u65b9\u653e\u624b\u4e00\u6218\u65f6\uff0c\u80dc\u8d1f\u5f88\u96be\u9884\u6599\uff0c\u81f3\u5c11\u53ef\u80fd\u4f7f\u97e9\u9738\u6709\u9003\u8d70\u7684\u673a\u4f1a\uff0c\u4f55\u51b5\uff0c\u6211\u4e00\u76f4\u60f3\u8bc1\u5b9e\u4e00\u4e0b\uff0c\u738b\u9f99\u5e08\u5f1f\u771f\u6b63\u7684\u6b7b\u4ea1\u539f\u56e0\uff1f\u201d \u3000\u3000\u6697\u4e2d\u63d0\u805a\u4e86\u529f\u529b\uff0c\u7f13\u7f13\u884c\u5165\u5385\u4e2d\uff0c\u63a5\u9053\uff1a\u201c\u9053\u957f\uff0c\u97e9\u9738\u4ea4\u7ed9\u5728\u4e0b\u7684\u5904\u7f6e\u7684\u627f\u8bfa\uff0c\u4e0d\u4f1a\u6539\u53d8\u5427\uff1f\u201d \u3000\u3000\u201c\u4e0d\u4f1a\u2026\u2026\u201d\u5929\u865a\u5b50\u7b11\u9053\uff1a\u201c\u8001\u9053\u518d\u7ed9\u4f60\u4e2a\u627f\u8bfa\uff0c\u4f60\u80dc\u4e86\uff0c\u5c31\u7ed9\u4f60\u534a\u4e2a\u65f6\u8fb0\uff0c\u8ba9\u4f60\u6d3b\u6740\u97e9\u9738\uff0c\u796d\u5960\u5e08\u5f1f\uff0c\u5728\u8fd9\u534a\u4e2a\u65f6\u8fb0\u4e4b\u95f4\uff0c\u8001\u9053\u7edd\u4e0d\u51fa\u624b\u963b\u6270\uff01\u201d \u3000\u3000\u201c\u9053\u957f\u8fd9\u6709\u4e9b\u5927\u8fc7\u5206\u4e86\u2026\u2026\u201d\u9093\u98de\u9053\uff1a\u201c\u4f55\u51b5\uff0c\u770b\u7740\u4ed6\u4eec\u6d3b\u6740\u6211\u4eec\u7684\u4eba\uff0c\u4e5f\u6709\u635f\u526f\u603b\u5802\u4e3b\u7684\u9762\u5b50\u554a\uff01\u201d \u3000\u3000\u201c\u9093\u603b\u7ba1\u591a\u8651\u4e86\u2026\u2026\u201d\u65e0\u865a\u5b50\u9053\uff1a\u201c\u4e00\u4e2a\u4eba\u51b3\u5b9a\u8981\u6b7b\u4e86\uff0c\u6b7b\u7684\u65b9\u6cd5\uff0c\u53c8\u6709\u4ec0\u4e48\u5173\u7cfb\u5462\uff1f \u3000\u3000\u5343\u5200\u4e07\u5250\u548c\u4e00\u5200\u6bd5\u547d\uff0c\u90fd\u662f\u4e00\u4e2a\u6b7b\u5b57\uff0c\u4f55\u51b5\uff0c\u9ec4\u9f99\u53ea\u6709\u534a\u4e2a\u65f6\u8fb0\u6d3b\u6740\u97e9\u9738\u7684\u65f6\u95f4\uff0c\u97e9\u9738\u4e00\u54ac\u7259\u5c31\u5fcd\u8fc7\u53bb\u4e86\u3002\u201d \u3000\u3000\u9093\u98de\u8001\u7751\u6da8\u7ea2\uff0c\u4f46\u5374\u4e0d\u6562\u518d\u8a00\u3002 \u3000\u3000\u97e9\u9738\u7a81\u7136\u8f6c\u8fc7\u5934\u6765\uff0c\u76ee\u6ce8\u5929\u865a\u5b50\uff0c\u53e4\u602a\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u526f\u603b\u5802\u4e3b\uff0c\u6709\u7f5a\u4e5f\u8be5\u6709\u8d4f\uff0c\u6211\u5982\u80dc\u4e86\uff0c\u526f\u603b\u5802\u4e3b\u8981\u5982\u4f55\u5956\u8d4f\u5c5e\u4e0b\u5462\uff1f\u201d \u3000\u3000\u5929\u865a\u5b50\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u97e9\u9738\uff0c\u8001\u9053\u770b\u4e0d\u51fa\u4f60\u6709\u4efb\u4f55\u80dc\u7b97\u7684\u673a\u4f1a\uff0c\u4f60\u5982\u771f\u7684\u80dc\u4e86\uff0c\u8001\u9053\u5c31\u4efb\u4f60\u63d0\u4e00\u4e2a\u6761\u4ef6\u5c31\u662f\uff01\u201d \u3000\u3000\u201c\u9053\u957f\uff0c\u4e00\u8a00\u65e2\u51fa\uff0c\u9a77\u9a6c\u96be\u8ffd\u2026\u2026\u201d\u97e9\u9738\u9053\uff1a\u201c\u5728\u573a\u7684\u4eba\uff0c\u90fd\u542c\u5230\u4e86\uff0c\u526f\u603b\u5802\u4e3b\u5230\u65f6\u5019\uff0c\u53ef\u4e0d\u80fd\u501f\u8bcd\u63a8\u6258\uff0c\u8d56\u4e0d\u8ba4\u5e10\u554a\uff01\u201d \u3000\u3000\u201c\u6df7\u5e10\uff0c\u8001\u9053\u8fd9\u4e00\u751f\u4e2d\uff0c\u4e5f\u8bb8\u505a\u8fc7\u4e0d\u5c11\u6709\u5e72\u5929\u548c\u7684\u4e8b\uff0c\u4f46\u5374\u4ece\u6ca1\u6709\u8bf4\u8bdd\u4e0d\u7b97\u8fc7\uff0c\u6240\u4ee5\uff0c\u8001\u9053\u7b54\u5e94\u4e86\u8ba9\u9ec4\u9f99\u6d3b\u6740\u4f60\uff0c\u5c4a\u65f6\u7edd\u4e0d\u4f1a\u51fa\u624b\u6551\u4f60\u3002\u201d \u3000\u3000\u201c\u77e5\u9053\u4e86\u2026\u2026\u201d\u97e9\u9738\u8f6c\u5411\u9ec4\u9f99\uff0c\u9053\uff1a\u201c\u9ec4\u73ed\u5934\uff0c\u8fd9\u662f\u4ee5\u547d\u8d4c\u547d\u7684\u4e8b\uff0c\u5728\u4e0b\u4f1a\u5168\u529b\u65bd\u4e3a\uff0c\u4f60\u8981\u597d\u597d\u7684\u51c6\u5907\u4e86\u3002\u201d \u3000\u3000\u6625\u82b1\u56db\u5a62\u548c\u97e9\u9738\u76f8\u5904\u8f83\u597d\uff0c\u6b64\u523b\u90fd\u6d41\u9732\u51fa\u65e0\u9650\u540c\u60c5\u795e\u8272\uff0c\u770b\u5411\u97e9\u9738\u3002 \u3000\u3000\u6bb5\u4e5d\u4f4e\u58f0\u9053\uff1a\u201c\u9f50\u5144\uff0c\u97e9\u526f\u603b\u7ba1\u8fd9\u4e00\u638c\u6210\u5417\uff1f\u201d \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u9053\uff1a\u201c\u5927\u6982\u53ef\u4ee5\u5427\uff01\u201d \u3000\u3000\u5fc3\u4e2d\u5374\u6697\u6697\u5fd6\u9053\uff1a\u201c\u4e00\u638c\u51fb\u6bd9\u4e86\u9ec4\u9f99\uff0c\u73b0\u9732\u51fa\u771f\u6b63\u6b66\u529f\uff0c\u8fd9\u4e2a\u526f\u603b\u7ba1\uff0c\u90a3\u91cc\u8fd8\u80fd\u4f5c\u5f97\u4e0b\u53bb\uff1f\u6253\u4e0d\u6b7b\u9ec4\u9f99\uff0c\u52bf\u5fc5\u5f15\u8d77\u4e00\u756a\u6076\u6597\uff0c\u8fd9\u4e2a\u526f\u603b\u7ba1\u7684\u8eab\u4efd\uff0c\u5927\u6982\u4e5f\u4fdd\u4e0d\u4f4f\u4e86\uff0c\u53ea\u662f\u4e0d\u77e5\u4ed6\u662f\u5426\u65e9\u6709\u8ba1\u5212\uff0c\u8c03\u96c6\u4e86\u4eba\u624b\uff0c\u8981\u4e00\u4e3e\u6b7c\u706d\u6d1b\u9633\u5c45\u7684\u654c\u4eba\u3002\u201d \u3000\u3000\u5927\u548c\u5c1a\u867d\u7136\u601d\u8651\u9547\u5bc6\uff0c\u4f46\u4e00\u65f6\u95f4\uff0c\u4e5f\u770b\u4e0d\u51fa\u6c5f\u67ab\u846b\u82a6\u4e2d\u5356\u7684\u4ec0\u4e48\u836f\uff0c\u53ea\u597d\u6697\u4e2d\u901a\u77e5\u80e1\u840d\u3001\u6bb5\u4e5d\uff0c\u5168\u795e\u6212\u5907\uff0c\u542c\u6c5f\u67ab\u4e4b\u547d\u884c\u4e8b\u3002 \u3000\u3000\u97e9\u9738\u7684\u4e00\u5f20\u7751\u867d\u7136\u96be\u770b\uff0c\u4f46\u58f0\u97f3\u5374\u51fa\u5947\u7684\u5e73\u548c\uff0c\u9053\uff1a\u201c\u9ec4\u73ed\u5934\uff0c\u5c0f\u5fc3\u4e86\uff0c\u5f62\u52bf\u903c\u4eba\uff0c\u602a\u4e0d\u5f97\u6211\u4e86\u2026\u2026\u201d\u4e00\u638c\u62cd\u5411\u524d\u80f8\u3002 \u3000\u3000\u9ec4\u9f99\u5168\u8eab\u8863\u670d\uff0c\u7a81\u7136\u9f13\u8d77\uff0c\u4eba\u5374\u7ad9\u7740\u672a\u52a8\u3002 \u3000\u3000\u679c\u7136\uff0c\u51c6\u5907\u4ee5\u4ed6\u6df1\u539a\u7684\u5185\u529f\uff0c\u63a5\u4e0b\u4e00\u638c\u3002 \u3000\u3000\u5168\u573a\u4e2d\u4eba\uff0c\u6240\u6709\u7684\u76ee\u5149\uff0c\u90fd\u6295\u6ce8\u5728\u6c5f\u67ab\u843d\u638c\u4e00\u51fb\u4e0a\u3002 \u3000\u3000\u9ec4\u9f99\u80fd\u4ee5\u62a4\u8eab\u771f\u6c14\uff0c\u9f13\u8d77\u8863\u670d\uff0c\u5c31\u7b97\u662f\u97e9\u9738\u7ec3\u8fc7\u94c1\u6c99\u638c\uff0c\u751a\u81f3\u5c11\u6797\u6d3e\u7684\u5927\u529b\u91d1\u94a2\u638c\uff0c\u4e5f\u672a\u5fc5\u80fd\u5728\u4e00\u51fb\u4e4b\u4e0b\uff0c\u4f24\u5230\u9ec4\u9f99\u3002 \u3000\u3000\u9093\u98de\u56e0\u65e0\u6cd5\u80fd\u4fdd\u4f4f\u8fd9\u4e2a\u5bf9\u4ed6\u5e2e\u52a9\u6781\u591a\u7684\u5c5e\u4e0b\uff0c\u7adf\u7136\u5fc3\u751f\u60ed\u548e\uff0c\u5782\u4e0b\u5934\u53bb\u3002 \u3000\u3000\u4e5d\u5934\u9e1f\u5374\u5401\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4e0b\u4e00\u573a\uff0c\u8981\u770b\u9ec4\u9f99\u5982\u4f55\u6765\u6d3b\u6740\u97e9\u9738\u4e86\uff1f\u201d \u3000\u3000\u201c\u4e0d\u81ea\u91cf\u529b\u2026\u2026\u201d\u5929\u865a\u5b50\u9053\uff1a\u201c\u5c31\u662f\u81ea\u53d6\u6b7b\u4ea1\uff0c\u602a\u4e0d\u5f97\u522b\u4eba\u4e86\uff1f\u2026\u2026\u201d \u3000\u3000\u4f46\u95ee\u9ec4\u9f99\u559d\u9053\uff1a\u201c\u4f60\u2026\u2026\u201d\u5f20\u5634\u55b7\u51fa\u4e00\u65e5\u6d53\u8840\uff0c\u7acb\u523b\u5012\u4e86\u4e0b\u53bb\uff0c\u4ee5\u4ed6\u6df1\u539a\u7684\u529f\u529b\uff0c\u7adf\u7136\u4e5f\u53ea\u80fd\u8bf4\u51fa\u4e00\u4e2a\u4f60\u5b57\u3002 \u3000\u3000\u90a3\u4e00\u638c\u4e0d\u89c1\u5feb\u901f\uff0c\u53ea\u662f\u8f7b\u98d8\u98d8\u7684\u843d\u5728\u4e86\u9ec4\u9f99\u7684\u524d\u80f8\u4e4b\u4e0a\u3002 \u3000\u3000\u6885\u82b1\u5a07\u8eaf\u4e00\u95ea\uff0c\u4eba\u5df2\u843d\u5230\u9ec4\u9f99\u7684\u8eab\u4fa7\uff0c\u6276\u8d77\u9ec4\u9f99\u7684\u8eab\u5b50\uff0c\u9053\uff1a\u201c\u5927\u5e08\u5144\uff0c\u5927\u5e08\u5144\uff0c\u4f60\u662f\u600e\u4e48\u5566\uff1f\u201d \u3000\u3000\u7eb5\u7136\uff0c\u4e8b\u5b9e\u4ff1\u5728\uff0c\u6885\u82b1\u8fd8\u662f\u4e0d\u592a\u76f8\u4fe1\u97e9\u9738\u80fd\u4e00\u638c\u5288\u6b7b\u4e86\u9ec4\u9f99\u3002 \u3000\u3000\u201c\u6ca1\u6709\u6551\u4e86\u2026\u2026\u201d\u90a3\u7384\u8863\u5c11\u5973\u9053\uff1a\u201c\u4ed6\u5410\u51fa\u7684\u4e00\u53e3\u6d53\u8840\u4e2d\uff0c\u5305\u62ec\u4e86\u4ed6\u7684\u5fc3\u80ba\u5185\u810f\u2026\u2026\u201d \u3000\u3000\u201c\u662f\uff01\u2026\u2026\u201d\u6885\u82b1\u7f13\u7f13\u6536\u56de\u6309\u5728\u9ec4\u9f99\u540e\u80cc\u7684\u5de6\u624b\uff0c\u9053\uff1a\u201c\u4e5f\u9707\u65ad\u4e86\u5927\u5e08\u5144\u7684\u5fc3\u8109\u3002\u201d \u3000\u3000\u201c\u97e9\u9738\u2026\u2026\u201d\u6885\u82b1\u62ac\u5934\u770b\u770b\u6c5f\u67ab\uff0c\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u7a76\u7adf\u662f\u4ec0\u4e48\u4eba\uff1f\u7528\u7684\u4ec0\u4e48\u6b66\u529f\u3002\u5982\u6b64\u7684\u9738\u9053\uff0c\u4e00\u51fb\u4e4b\u4e0b\uff0c\u9707\u65ad\u5fc3\u8109\uff0c\u517c\u788e\u4e94\u8151\uff0c\u4f60\u8bf4\u5b9e\u8bdd\u554a\uff01\u4f60\u662f\u8c01\u5440\uff1f\u201d \u3000\u3000\u6885\u82b1\u5fc3\u4e2d\u7684\u9707\u9a87\uff0c\u5df2\u5230\u4e86\u65e0\u6cd5\u81ea\u5236\u7684\u5883\u754c\uff0c\u5979\u4e00\u76f4\u4e0d\u66fe\u653e\u5728\u773c\u4e2d\u7684\u97e9\u9738\uff0c\u7adf\u662f\u4e2a\u8eab\u6000\u7edd\u4e16\u6b66\u529f\u7684\u9ad8\u4eba\uff0c\u5931\u63a7\u7684\u6cea\u6c34\u6eda\u6eda\u800c\u4e0b\u3002 \u3000\u3000\u4ee5\u97e9\u9738\u8868\u73b0\u7684\u6b66\u529f\u6765\u8bf4\uff0c\u5982\u6709\u6740\u5979\u6885\u82b1\u4e4b\u5fc3\uff0c\u5341\u4e2a\u6885\u82b1\uff0c\u4e5f\u88ab\u6740\u6b7b\u4e86\u3002 \u3000\u3000\u7384\u8863\u5c11\u5973\u98de\u843d\u5728\u6885\u82b1\u8eab\u4fa7\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u4e0d\u8981\u54ed\uff0c\u632f\u8d77\u7cbe\u795e\uff0c\u51c6\u5907\u5e94\u53d8\uff01\u201d \u3000\u3000\u5929\u865a\u5b50\u548c\u4e5d\u5934\u9e1f\u6240\u53d7\u5230\u7684\u60ca\u9707\uff0c\u7edd\u4e0d\u5728\u6885\u82b1\u4e4b\u4e0b\uff0c\u80fd\u4e00\u638c\u51fb\u6563\u9ec4\u9f99\u62a4\u8eab\u771f\u6c14\u4e4b\u540e\uff0c\u53c8\u65ad\u5fc3\u8109\uff0c\u788e\u53bb\u5185\u8151\uff0c\u4e5d\u5934\u9e1f\u81ea\u77e5\u7edd\u6ca1\u6709\u8fd9\u4efd\u529f\u529b\u2026\u2026\u3002 \u3000\u3000\u201c\u526f\u603b\u5802\u4e3b\uff0c\u54b1\u4eec\u80dc\u4e86\u2026\u2026\u201d\u5201\u9e4f\u8bf4\uff0c\u201c\u97e9\u526f\u603b\u7ba1\u662f\u6df1\u85cf\u4e0d\u9732\u7684\u9ad8\u4eba\uff0c\u6211\u4eec\u8d62\u5f97\u662f\u5149\u5149\u5f69\u5f69\u3002\u201d \u3000\u3000\u201c\u9093\u98de\uff0c\u6211\u8981\u529e\u4f60\u4e2a\u533f\u9690\u9ad8\u624b\u7684\u7f6a\u540d\u2026\u2026\u201d\u5929\u865a\u5b50\u9053\uff1a\u201c\u50cf\u97e9\u9738\u8fd9\u7b49\u8eab\u624b\u7684\u4eba\uff0c\u4f60\u7adf\u7136\u4e0d\u901a\u62a5\u603b\u5802\u2026\u2026\u201d \u3000\u3000\u201c\u526f\u603b\u5802\u4e3b\uff0c\u7528\u4e0d\u7740\u626f\u4e0a\u9093\u603b\u7ba1\u2026\u2026\u201d\u97e9\u9738\u9053\uff1a\u201c\u6211\u8bb0\u5f97\u4f60\u7b54\u5e94\u8fc7\uff0c\u97e9\u67d0\u4eba\u5982\u679c\u80dc\u4e86\uff0c\u53ef\u4ee5\u5411\u4f60\u63d0\u51fa\u4e00\u4e2a\u6761\u4ef6\uff1f\u5728\u4e0b\u6ca1\u6709\u8bb0\u9519\u5427\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u4e0d\u9519\uff0c\u4f60\u8981\u63d0\u51fa\u4e00\u4e2a\u8001\u9053\u80fd\u591f\u529e\u5230\u7684\u6761\u4ef6\u624d\u884c\uff1f\u201d \u3000\u3000\u53e3\u6c14\u4e2d\u5df2\u6709\u53d8\u5316\uff0c\u9884\u7559\u4e86\u9000\u6b65\u3002 \u3000\u3000\u9093\u98de\u4f3c\u4e4e\u6ca1\u6709\u542c\u5230\u5929\u865a\u5b50\u7684\u547c\u53eb\uff0c\u53ea\u662f\u5446\u5446\u7684\u671b\u7740\u97e9\u9738\u51fa\u795e\u3002 \u3000\u3000\u201c\u9ec4\u9f99\u7684\u5e74\u7eaa\u8f7b\u4e86\u4e00\u70b9\uff0c\u6240\u4ee5\uff0c\u4ed6\u7684\u5185\u529f\u8fd8\u672a\u5230\u7cbe\u7eaf\u7684\u5883\u754c\uff0c\u2026\u2026\u201d\u97e9\u9738\u8bf4\uff0c\u201c\u6240\u4ee5\uff0c\u5c5e\u4e0b\u60f3\u518d\u627e\u4e00\u4e2a\u4eba\u8bd5\u8bd5\u638c\u529b\u5982\u4f55\uff1f\u201d \u3000\u3000\u5929\u865a\u5b50\u9053\uff1a\u201c\u597d\u554a\uff01\u5bf9\u65b9\u8fd8\u6709\u4e24\u4e2a\u4eba\u2026\u2026\u201d \u3000\u3000\u201c\u5979\u4eec\u662f\u5973\u4eba\uff0c\u800c\u4e14\uff0c\u6bd4\u9ec4\u9f99\u7684\u5e74\u7eaa\u8fd8\u8f83\u2026\u2026\u201d\u97e9\u9738\u8bf4\uff0c\u201c\u5c5e\u4e0b\u5e0c\u671b\u627e\u4e00\u4e2a\u4fee\u4e60\u8fc7\u4e09\u5341\u5e74\u4ee5\u4e0a\u5185\u529f\u7684\u4eba\u3002\u201d \u3000\u3000\u201c\u4f60\u53ef\u662f\u60f3\u62ff\u6211\u8001\u9053\u8bd5\u8bd5\u5417\uff1f\u201d \u3000\u3000\u201c\u526f\u603b\u5802\u4e3b\uff0c\u8eab\u4efd\u4f55\u7b49\u5c0a\u8d35\uff0c\u97e9\u67d0\u4eba\u5c82\u6562\u52a8\u6b64\u5984\u5ff5\u3002\u201d \u3000\u3000\u201c\u55ef\uff01\u9664\u4e86\u6211\u8001\u9053\uff0c\u8fd9\u5927\u5385\u4e4b\u4e0a\uff0c\u6709\u4e09\u5341\u5e74\u4ee5\u4e0a\u529f\u529b\u7684\u4eba\uff0c\u53ea\u6709\u9093\u98de\u4e86\uff0c\u4f60\u53ef\u662f\u60f3\u62ff\u9093\u98de\u8bd5\u624b\uff1f\u201d \u3000\u3000\u201c\u8fd8\u6709\u4e00\u4f4d\uff0c\u4e5d\u5934\u9e1f\u5201\u9e4f\u5201\u957f\u8001\u3002\u201d \u3000\u3000\u201c\u4e0d\u884c\uff0c\u526f\u603b\u5802\u4e3b\u5343\u4e07\u4e0d\u53ef\u7b54\u5141\u4ed6\u2026\u2026\u201d\u5201\u9e4f\u6025\u6025\u8bf4\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u5ffd\u4e1c\u5ffd\u897f\uff0c\u65e2\u8981\u5bf9\u4ed8\u5185\u5bab\u4e00\u7cfb\u4e2d\u4eba\uff0c\u4e5f\u5bf9\u4ed8\u5929\u9a6c\u5802\u4e2d\u4eba\uff0c\u6211\u770b\u662f\u53ef\u7591\u7684\u5f88\u2026\u2026\u201d \u3000\u3000\u201c\u8bf4\u7684\u5bf9\uff01\u5c0f\u5201\uff0c\u4e5f\u5bf9\u4ed8\u5929\u9a6c\u5802\u4e2d\u4eba\uff0c\u90a3\u5c31\u7531\u4f60\u51fa\u624b\u6293\u4f4f\u4ed6\uff0c\u95ee\u4e2a\u660e\u767d\uff1f\u201d \u3000\u3000\u5929\u865a\u5b50\u4e0b\u8fbe\u4e86\u4ee4\u8c15\u4e4b\u540e\uff0c\u4e5f\u4e0d\u770b\u5201\u9e4f\u7684\u53cd\u5e94\uff0c\u76ee\u5149\u8f6c\u4f4f\u97e9\u9738\u7684\u8eab\u4e0a\uff0c\u63a5\u9053\uff1a\u201c\u751f\u6b7b\u5927\u4e8b\uff0c\u8001\u9053\u4e0d\u80fd\u4e0b\u4ee4\u8ba9\u522b\u4eba\u7ad9\u5728\u90a3\u91cc\u4efb\u4f60\u51fa\u638c\uff0c\u4f60\u53ea\u6709\u51ed\u4ed7\u6b66\u529f\uff0c\u548c\u4e60\u9e4f\u4e00\u51b3\u80dc\u8d1f\u4e86\uff1f\u201d \u3000\u3000\u97e9\u9738\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5c5e\u4e0b\u9075\u547d\uff01\u201d \u3000\u3000\u8f6c\u8eab\u884c\u5411\u4e5d\u5934\u9e1f\u7b11\u9053\uff1a\u201c\u4e5d\u5934\u9e1f\u662f\u5929\u4e0b\u7b2c\u4e00\u51f6\u9e1f\uff0c\u4e0d\u77e5\u4f60\u90a3\u4e00\u8eab\u6b66\u529f\uff0c\u662f\u5426\u4e5f\u6709\u4e5d\u5934\u9e1f\u7684\u51f6\u72e0\uff1f\u6765\uff01\u97e9\u67d0\u4eba\u8ba9\u4f60\u5148\u673a\uff0c\u51fa\u624b\u5427\uff1f\u201d \u3000\u3000\u5201\u9e4f\u65e9\u5df2\u84c4\u52bf\u6212\u5907\uff0c\u51c6\u5907\u5168\u529b\u4e00\u51fb\uff0c\u4f46\u97e9\u9738\u6307\u660e\u4e86\u8981\u4ed6\u5148\u884c\u51fa\u624b\uff0c\u5fc3\u4e2d\u5ffd\u52a8\u7591\u60d1\uff0c\u6536\u4f4f\u6b32\u53d1\u7684\u638c\u52bf\uff0c\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u97e9\u9738\uff0c\u8001\u592b\u548c\u4f60\u65e0\u6028\u65e0\u4ec7\uff0c\u4f60\u600e\u4e48\u4e00\u5207\u90fd\u51b2\u7740\u8001\u592b\u6765\u5462\uff1f\u2026\u2026\u201d \u3000\u3000\u201c\u9093\u98de\u5bf9\u5728\u4e0b\u6709\u63d0\u643a\u4e4b\u6069\uff01\u2026\u2026\u201d\u97e9\u9738\u8bf4\uff0c\u201c\u5929\u865a\u5b50\u526f\u603b\u5802\u4e3b\u53c8\u9ad8\u9ad8\u5728\u4e0a\uff0c\u5728\u4e0b\u90a3\u6562\u5192\u72af\uff0c\u9664\u4e86\u4f60\uff0c\u8fd9\u5927\u5385\u4e4b\u4e2d\uff0c\u518d\u4e5f\u6ca1\u6709\u9002\u5408\u7684\u4eba\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u97e9\u9738\uff0c\u4f60\u641e\u9519\u76ee\u6807\u4e86\uff0c\u6211\u662f\u5929\u9a6c\u638c\u4e2d\u7684\u957f\u8001\uff0c\u662f\u4f60\u7684\u670b\u53cb\u554a\uff01\u201d\u5201\u9e4f\u9053\uff1a\u201c\u773c\u524d\u8fd9\u8fce\u5bbe\u9601\u4e2d\uff0c\u8fd8\u6709\u4f60\u5f88\u591a\u7684\u654c\u4eba\uff0c\u4f60\u80fd\u628a\u4ed6\u4eec\u4e00\u4e00\u51fb\u6bd9\uff0c\u90a3\u53ef\u662f\u5927\u529f\u4e00\u4ef6\uff0c\u4e5f\u53ef\u6df7\u4e0a\u4e2a\u603b\u5802\u7684\u62a4\u6cd5\u5e72\u5e72\u3002\u201d \u3000\u3000\u6c5f\u67ab\u5fc3\u4e2d\u5fd6\u9053\uff1a\u201c\u6740\u4e86\u5201\u9e4f\uff0c\u6682\u6d88\u4e00\u65f6\u6c14\u5fff\uff0c\u5012\u4e0d\u5982\u5728\u4ed6\u5fc3\u4e2d\u64ad\u4e0b\u4e00\u4e9b\u731c\u7591\u7684\u79cd\u4e86\uff0c\u8ba9\u5b83\u4eec\u53d1\u82bd\u6210\u957f\uff0c\u5929\u9a6c\u603b\u5802\u4e2d\u4e91\u96c6\u4e86\u5929\u4e0b\u7684\u6076\u4eba\uff0c\u8981\u4ed6\u4eec\u4f5c\u541b\u5b50\u884c\u5f84\uff0c\u81ea\u662f\u7f18\u6728\u6c42\u9c7c\u3002\u201d \u3000\u3000\u4e00\u5ff5\u53ca\u6b64\uff0c\u5fc3\u4e2d\u6c14\u6012\u987f\u6d88\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5201\u957f\u8001\uff0c\u526f\u603b\u5802\u4e3b\u5df2\u4f20\u4e86\u4ee4\u8c15\uff0c\u54b1\u4eec\u5982\u4e0d\u52a8\u624b\u8fc7\u51e0\u62db\uff0c\u662f\u85d0\u89c6\u4e86\u526f\u603b\u5802\u7684\u6743\u5a01\uff0c\u6b64\u540e\u5c82\u4e0d\u662f\u9ebb\u70e6\u591a\u591a\uff0c\u4e3a\u4e86\u7ed9\u526f\u603b\u5802\u4e3b\u4e00\u4e2a\u9762\u5b50\uff0c\u54b1\u4eec\u53ea\u597d\u52a8\u624b\u4e00\u6218\u4e86\u3002\u201d \u3000\u3000\u5201\u9e4f\u9053\uff1a\u201c\u97e9\u5144\u51c6\u5907\u600e\u4e48\u4e00\u4e2a\u6253\u6cd5\u5462\uff1f\u201d \u3000\u3000\u53e3\u6c14\u5927\u53d8\uff0c\u7adf\u548c\u97e9\u9738\u79f0\u5144\u9053\u5f1f\u8d77\u6765\uff0c\u4e00\u4e0b\u5b50\uff0c\u628a\u5f7c\u6b64\u7684\u8eab\u4efd\u8ddd\u79bb\uff0c\u62c9\u8fd1\u4e86\u4e00\u500d\u3002 \u3000\u3000\u201c\u6211\u770b\uff0c\u54b1\u4eec\u52a8\u624b\u4ee5\u5341\u62db\u4e3a\u9650\u2026\u2026\u201d \u3000\u3000\u201c\u5341\u62db\uff0c\u5927\u591a\u4e86\u5427\uff1f\u2026\u2026\u201d \u3000\u3000\u5201\u9e4f\u6253\u65ad\u4e86\u97e9\u9738\u7684\u8bdd\uff0c\u63a5\u9053\uff1a\u201c\u81ea\u5df1\u4eba\u54ea\uff01\u8fc7\u624b\u4e2a\u4e09\u4e24\u62db\uff0c\u80fd\u7ed9\u526f\u603b\u5802\u4e3b\u4e00\u4e2a\u4ea4\u4ee3\uff0c\u4e5f\u5c31\u591f\u4e86\uff1f\u201d \u3000\u3000\u4e24\u4e2a\u4eba\u8bf4\u8bdd\u7684\u58f0\u97f3\u90fd\u5f88\u5927\uff0c\u6709\u610f\u7ed9\u5929\u865a\u5b50\u542c\u4e86\uff0c\u5929\u865a\u5b50\u786e\u5b9e\u542c\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u4f46\u5374\u6545\u4f5c\u672a\u66fe\u6240\u95fb\uff0c\u5fae\u95ed\u53cc\u76ee\u800c\u5750\u3002 \u3000\u3000\u6885\u82b1\u548c\u90a3\u7384\u8863\u5c11\u5973\uff0c\u4e00\u9762\u4f4e\u58f0\u4ea4\u8c08\uff0c\u4e00\u9762\u6ce8\u89c6\u7740\u573a\u4e2d\u53d8\u5316\uff0c\u770b\u97e9\u9738\u5ffd\u7136\u5411\u5201\u9e4f\u6311\u6218\uff0c\u4e5f\u5c31\u6309\u5175\u4e0d\u52a8\u3002 \u3000\u3000\u201c\u5341\u62db\u2026\u2026\u201d\u97e9\u9738\u56fa\u6267\u5730\u8bf4\uff0c\u201c\u4f46\u4f60\u653b\u6211\u4e5d\u62db\uff0c\u6211\u53ea\u8fd8\u4f60\u4e00\u62db\uff0c\u6211\u5c31\u51fa\u53e3\u7684\u8bdd\uff0c\u4e00\u5411\u4e0d\u559c\u66f4\u6539\uff01\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u2026\u2026\u201d\u4e5d\u5934\u9e1f\u5fc3\u4e2d\u4e0d\u505c\u7684\u8f6c\u52a8\u7740\uff0c\u5fd6\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u5f53\u771f\u662f\u72c2\u5f97\u5f88\u54ea\uff01\u4e5d\u62db\u72c2\u653b\uff0c\u6211\u5982\u8fd8\u4e0d\u80fd\u53d6\u4ed6\u6027\u547d\uff0c\u4e0d\u7528\u4ed6\u51fb\u90a3\u4e00\u62db\uff0c\u6211\u4e5f\u65e0\u6cd5\u518d\u6df7\u4e0b\u53bb\u4e86\uff0c\u5c31\u7b97\u662f\u5929\u865a\u5b50\uff0c\u4e5f\u65e0\u6cd5\u8ba9\u6211\u5201\u67d0\u4e5d\u62db\uff0c\u4f60\u5c0f\u4e8e\u662f\u6b7b\u5b9a\u4e86\u3002\u201d \u3000\u3000\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u53e3\u4e2d\u8bf4\u9053\uff1a\u201c\u97e9\u5144\u7684\u610f\u601d\u662f\uff0c\u4f60\u4e0d\u8fd8\u624b\uff0c\u8ba9\u6211\u653b\u4f60\u4e5d\u638c\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u9519\uff01\u800c\u4e14\u62f3\u638c\u5175\u5203\uff0c\u4efb\u4f60\u65bd\u4e3a\u2026\u2026\u201d\u97e9\u9738\u7684\u53e3\u6c14\uff0c\u8d8a\u6765\u8d8a\u5927\u7684\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u53f7\u79f0\u4e5d\u5934\u9e1f\uff0c\u60f3\u5fc5\u9634\u624b\u3001\u6697\u5668\uff0c\u4e5f\u662f\u4f60\u7684\u4e13\u957f\uff0c\u4e5f\u628a\u5b83\u7b97\u4e0a\u5427\uff01\u53ea\u8981\u4f60\u80fd\u628a\u6211\u6740\u6b7b\u5c31\u884c\uff0c\u4e0d\u8fc7\uff0c\u53d1\u51fa\u4e00\u6b21\u6697\u5668\uff0c\u4e5f\u7b97\u4e00\u62db\uff0c\u4f60\u8981\u8bb0\u6e05\u695a\u4e86\u3002\u201d \u3000\u3000\u8fd9\u756a\u8bdd\u58f0\u97f3\u4e0d\u5927\uff0c\u4f46\u5168\u573a\u4e2d\u4eba\uff0c\u90fd\u542c\u5f97\u5982\u96f7\u8d2f\u8033\uff0c\u5929\u865a\u5b50\u4e5f\u542c\u5f97\u970d\u7136\u7741\u5f00\u53cc\u76ee\uff0c\u76ef\u4f4f\u5728\u97e9\u9738\u7684\u8138\u4e0a\u77a7\u7740\u3002 \u3000\u3000\u9093\u98de\u3001\u6885\u82b1\uff0c\u7b49\u90fd\u542c\u5f97\u5446\u4f4f\u4e86\u3002 \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u6697\u6697\u5ff5\u4f5b\uff0c\u5fd6\u9053\uff1a\u201c\u7b80\u76f4\u662f\u592a\u5938\u5f20\u4e86\uff0c\u4f60\u4e5d\u62db\u4e0d\u80fd\u8fd8\u624b\uff0c\u4e5d\u5934\u9e1f\u6ca1\u6709\u540e\u987e\u4e4b\u5fe7\uff0c\u53ef\u4ee5\u5168\u529b\u65bd\u5c55\uff0c\u5341\u6210\u6b66\u529f\uff0c\u53ef\u4ee5\u53d1\u6325\u51fa\u5341\u500d\u7684\u5a01\u529b\uff0c\u5929\u4e0b\u9ad8\u624b\uff0c\u6709\u8c01\u80fd\u6321\u5f97\u4f4f\u5201\u9e4f\u5168\u529b\u731b\u653b\u7684\u4e5d\u62db\u3002\u201d \u3000\u3000\u5012\u662f\u4e5d\u5934\u9e1f\u5201\u9e4f\u542c\u5f97\u5fc3\u4e2d\u6709\u70b9\u53d1\u6bdb\u4e86\uff0c\u53c8\u6068\u53c8\u6015\uff0c\u4f46\u4e5f\u6fc0\u8d77\u4e86\u4ed6\u5168\u529b\u65bd\u5c55\u7684\u51b3\u5fc3\uff0c\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u97e9\u5144\u679c\u7136\u82f1\u96c4\uff0c\u5201\u67d0\u4eba\u606d\u656c\u4e0d\u5982\u4ece\u547d\u4e86\u3002\u201d \u3000\u3000\u97e9\u9738\u51b7\u51b7\u7684\u8bf4\u9053\uff1a\u201c\u4f60\u53ef\u4ee5\u51fa\u624b\u4e86\uff0c\u65f6\u95f4\u5b9d\u8d35\uff0c\u6211\u4e0d\u80fd\u7b49\u4f60\u592a\u4e45\u4e86\u3002\u201d \u3000\u3000\u5201\u9e4f\u51b7\u7b11\u4e00\u58f0\uff0c\u7a81\u7136\u98de\u6251\u9762\u4e0a\uff0c\u4e00\u9053\u95ea\u7535\u822c\u7684\u5bd2\u8292\uff0c\u98de\u5377\u800c\u81f3\u3002 \u3000\u3000\u4e56\u4e56\uff0c\u4e00\u51fa\u624b\u5c31\u4eae\u4e86\u5bb6\u4f19\uff0c\u4e00\u628a\u5bbd\u9762\u53cc\u5203\u5200\uff0c\u662f\u4e00\u79cd\u65e2\u7280\u5229\uff0c\u53c8\u9634\u72e0\u7684\u5175\u5203\u3002 \u3000\u3000\u5200\u52bf\u5feb\u5f97\u5982\u5212\u7a7a\u7684\u95ea\u7535\uff0c\u98de\u65cb\u7684\u5bd2\u8292\uff0c\u7b3c\u7f69\u4e86\u4e03\u5c3a\u65b9\u5706\uff0c\u771f\u662f\u593a\u547d\u8ffd\u9b42\u7684\u4e00\u51fb\u3002 \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u5fd6\u9053\uff1a\u201c\u6211\u548c\u5c1a\u5982\u4e0d\u8fd8\u51fb\uff0c\u5c31\u65e0\u6cd5\u95ea\u907f\u5f00\u8fd9\u4e00\u5200\u3002\u201d \u3000\u3000\u8fde\u5929\u865a\u5b50\u4e5f\u6709\u7740\u4e0d\u51fa\u5251\uff0c\u5f88\u96be\u62b5\u5fa1\u8fd9\u4e00\u5200\u4e4b\u5a01\u7684\u611f\u89c9\u3002 \u3000\u3000\u4f46\u97e9\u9738\u5374\u95ea\u907f\u5f00\u4e86\uff0c\u8eab\u5b50\u50cf\u98d8\u8d77\u7684\u98de\u7d6e\u4e00\u822c\uff0c\u5728\u98de\u8f6c\u5200\u5149\u4e2d\u98d8\u843d\u5728\u5201\u9e4f\u539f\u6765\u7684\u843d\u8db3\u4e4b\u5904\u3002 \u3000\u3000\u770b\u4e0a\u53bb\uff0c\u5c31\u50cf\u4e24\u4eba\u5f88\u5feb\u7684\u4ea4\u624b\u6362\u4e86\u4e00\u4e2a\u4f4d\u7f6e\u3002 \u3000\u3000\u4e5d\u5934\u9e1f\u4e00\u8fde\u4e09\u5200\uff0c\u4e00\u5200\u6bd4\u4e00\u5200\u51f6\u72e0\uff0c\u5200\u5149\u51e0\u4e4e\u7b3c\u7f69\u534a\u5ea7\u5927\u5385\uff0c\u4f46\u5c31\u662f\u5dee\u90a3\u4e48\u4e00\u70b9\u8ddd\u79bb\uff0c\u6ca1\u6709\u4f24\u5230\u97e9\u9738\u3002 \u3000\u3000\u4e09\u5200\u5df2\u8fc7\uff0c\u4e5d\u5934\u9e1f\u6c14\u52bf\u5927\u632b\uff0c\u8fd9\u662f\u4ed6\u5200\u6cd5\u4e2d\u7684\u4e09\u8bb0\u7edd\u62db\uff0c\u4e00\u4e0a\u624b\u5c31\u5168\u529b\u65bd\u5c55\u51fa\u6765\uff0c\u7adf\u7136\u65e0\u6cd5\u4f24\u4eba\u3002 \u3000\u3000\u201c\u4e0d\u8981\u6c14\u9981\uff0c\u5201\u5144\uff0c\u4f60\u8fd8\u6709\u6697\u5668\u6ca1\u6709\u65bd\u5c55\uff0c\u4e0d\u8fc7\uff0c\u53d1\u6697\u5668\u65f6\uff0c\u8981\u8ba4\u51c6\u4f4d\u7f6e\uff0c\u8981\u662f\u4f24\u5230\u4e86\u522b\u4eba\uff0c\u4eba\u5bb6\u627e\u4f60\u8ba8\u53d6\u516c\u9053\u7684\u8bdd\u90a3\u5c31\u5927\u715e\u98ce\u666f\u4e86\uff0c\u4f55\u51b5\uff0c\u9519\u4f24\u4e86\u526f\u603b\u5802\u4e3b\uff0c\u6211\u5c31\u5403\u4e0d\u4e86\u515c\u7740\u8d70\u5566\u3002\u201d\u97e9\u9738\u7684\u8bed\u6c14\uff0c\u975e\u5e38\u5e73\u9759\uff0c\u5c31\u50cf\u8001\u670b\u53cb\u8bdd\u522b\u4e00\u822c\u3002 \u3000\u3000\u201c\u6211\u770b\u4e0d\u7528\u518d\u73a9\u4e0b\u53bb\u4e86\uff0c\u5201\u67d0\u4eba\u8ba4\u8f93\u5c31\u662f\uff0f\u201d \u3000\u3000\u90a3\u4e09\u5200\u653b\u52bf\uff0c\u662f\u4ed6\u4e09\u5927\u7cbe\u6e5b\u6280\u827a\u4e4b\u4e00\uff0c\u8fde\u5bf9\u65b9\u8863\u89d2\u90fd\u672a\u78b0\u5230\uff0c\u5982\u679c\u6296\u51fa\u4e86\u538b\u7bb1\u5e95\u7684\u672c\u9886\uff0c\u4e00\u51fb\u4e0d\u4e2d\uff0c\u90a3\u53ea\u6709\u675f\u624b\u5c31\u622e\u7684\u4efd\u4e86\u3002 \u3000\u3000\u7ecf\u8fc7\u4e00\u756a\u601d\u7d22\uff0c\u4e5d\u5934\u9e1f\u51b3\u5fc3\u653e\u5f03\u5b64\u6ce8\u4e00\u63b7\u7684\u6253\u7b97\u3002 \u3000\u3000\u201c\u5201\u5144\u6267\u610f\u505c\u624b\uff0c\u97e9\u67d0\u4eba\u4e0d\u80fd\u52c9\u5f3a\uff0c\u4f46\u6211\u8fd9\u4e00\u62db\uff0c\u53ef\u662f\u975e\u8981\u8fd8\u51fb\u4e0d\u53ef\u3002\u201d \u3000\u3000\u201c\u53ea\u671b\u97e9\u5144\u624b\u4e0b\u7559\u60c5\uff0c\u5201\u67d0\u4eba\u8fd9\u53a2\u606d\u5019\u4e86\u2026\u2026\u201d\u8a00\u7f62\uff0c\u95ed\u4e0a\u53cc\u76ee\uff0c\u4e00\u526f\u60c5\u7518\u53d7\u6b7b\u7684\u795e\u6001\u3002 \u3000\u3000\u7a81\u7136\u6765\u7684\u53d8\u5316\uff0c\u4ee4\u4eba\u610f\u5916\u7684\u60ca\u9a87\uff0c\u83ab\u53ef\u9884\u6d4b\u7684\u7ed3\u679c\uff0c\u4f7f\u5f97\u4e5d\u5934\u9e1f\u5201\u9e4f\u8fd9\u7b49\u6c5f\u6e56\u51f6\u4eba\uff0c\u4e5f\u5b8c\u5168\u5931\u53bb\u4e86\u63a7\u5236\u81ea\u5df1\u7684\u80fd\u529b\uff0c\u4e0d\u5f97\u4e0d\u7528\u5fc3\u53bb\u60f3\uff0c\u8d8a\u60f3\u8d8a\u6015\uff0c\u5c31\u53d8\u6210\u4e86\u5b8c\u5168\u5c48\u773c\uff0c\u8fd9\u5c31\u662f\u6177\u6168\u8d74\u6b7b\u6613\uff0c\u4ece\u5bb9\u5c31\u4e49\u96be\uff0c\u6c5f\u6e56\u4eba\uff0c\u5931\u53bb\u4e86\u90a3\u80a1\u51f6\u72e0\u7684\u9738\u6c14\uff0c\u4e5f\u5c31\u4e27\u5931\u4e86\u518d\u6218\u7684\u52c7\u6c14\u3002 \u3000\u3000\u97e9\u9738\u679c\u7136\u62cd\u51fa\u4e86\u4e00\u638c\u3002 \u3000\u3000\u90a3\u662f\u4e00\u80a1\u8f7b\u98d8\u98d8\u7684\u638c\u529b\uff0c\u4efb\u4f55\u4eba\u90fd\u770b\u5230\u51fa\u6765\uff0c\u90a3\u638c\u529b\u4e0d\u4f1a\u4f24\u4eba\u3002 \u3000\u3000\u4f46\u5201\u9e4f\u5374\u611f\u89c9\u5230\u4e00\u80a1\u5c16\u9525\u4e00\u822c\u7684\u6697\u52b2\uff0c\u76f4\u900f\u9aa8\u8151\uff0c\u8033\u9645\u95f4\u540c\u65f6\u4f20\u5165\u4e86\u97e9\u9738\u7684\u58f0\u97f3\uff0c\u9053\uff1a \u3000\u3000\u201c\u8fd9\u662f\u72ec\u95e8\u70b9\u7a74\u624b\u6cd5\uff0c\u6ca1\u6709\u4eba\u80fd\u591f\u89e3\u5b83\uff0c\u4f46\u4e09\u65e5\u5185\uff0c\u4e0d\u4f1a\u53d1\u4f5c\uff0c\u6211\u4f1a\u5728\u4e09\u65e5\u5185\uff0c\u7ea6\u4f60\u89c1\u9762\uff0c\u4f60\u5982\u5984\u56fe\u89e3\u9664\u4f24\u7a74\uff0c\u90a3\u5c31\u81ea\u627e\u82e6\u5403\u4e86\u3002\u201d \u3000\u3000\u4f46\u95fb\u97e9\u9738\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5201\u5144\u5982\u6b64\u7684\u8c26\u8ba9\uff0c\u9738\u67d0\u4eba\u600e\u6562\u76f8\u72af\uff0c\u8fd9\u5c31\u4e24\u4e0b\u62c9\u5e73\u4e86\u3002\u201d \u3000\u3000\u201c\u5bf9\u5440\uff01\u5929\u9a6c\u5802\u4e2d\u4eba\uff0c\u610f\u80fd\u81ea\u76f8\u6b8b\u6740\u2026\u2026\u201d\u5929\u865a\u5b50\u4e00\u8138\u7b11\u5bb9\u7684\u8bf4\u9053\uff1a\u201c\u97e9\u9738\uff0c\u8001\u9053\u4ee5\u526f\u603b\u5802\u4e3b\u7684\u8eab\u4efd\uff0c\u6307\u6d3e\u4f60\u548c\u5185\u5bab\u4e00\u7cfb\u4e2d\u8c08\u5224\u2026\u2026\u201d \u3000\u3000\u201c\u662f\u4e0d\u662f\u5177\u6709\u51b3\u5b9a\u548c\u3001\u6218\u7684\u5168\u6743\u4ee3\u8868\uff1f\u2026\u2026\u201d\u97e9\u9738\u63a5\u9053\uff1a\u201c\u65e0\u6743\u65e0\u80fd\u7684\u4ee3\u8868\uff0c\u97e9\u9738\u58f0\u4e1c\u51fb\u897f\u4eba\u53ef\u4e0d\u613f\u5e72\uff01\u201d \u3000\u3000\u5929\u865a\u5b50\u5fae\u5fae\u4e00\u6014\uff0c\u9053\uff1a\u201c\u5f53\u7136\u662f\u51b3\u5b9a\u548c\u3001\u6218\u5927\u6743\u7684\u4ee3\u8868\u3002\u201d \u3000\u3000\u201c\u591a\u8c22\u526f\u603b\u5802\u4e3b\u7684\u4fe1\u4efb\u2026\u2026\u201d\u97e9\u9738\u76ee\u5149\u8f6c\u6ce8\u5230\u6885\u82b1\u7684\u8138\u4e0a\uff0c\u63a5\u9053\uff1a\u201c\u716e\u8c46\u71c3\u8c46\u7b95\uff0c\u76f8\u714e\u4f55\u5927\u6025\uff0c\u6885\u82b1\u59d1\u5a18\uff0c\u5929\u9a6c\u5802\u4e0b\uff0c\u85cf\u9f99\u5367\u864e\uff0c\u96c6\u5929\u4e0b\u5947\u4eba\u4e8e\u4e00\u5802\uff0c\u6211\u97e9\u67d0\u4eba\u53ea\u662f\u4e00\u540d\u9a6c\u524d\u5c0f\u5352\u7f62\u4e86\uff0c\u80fd\u7f62\u624b\u65f6\u4e14\u7f62\u624b\u2026\u2026\u201d\u76ee\u5149\u7a81\u7136\u505c\u5728\u7384\u8863\u5c11\u5973\u7684\u8138\u4e0a\uff0c\u51b7\u51b7\u7684\u8bf4\uff1a\u201c\u4f60\u51fa\u6765\uff0c\u6211\u8981\u5728\u4e09\u62db\u4e4b\u5185\uff0c\u53d6\u4f60\u6027\u547d\uff01\u201d \u3000\u3000\u7384\u8863\u5c11\u5973\u5fae\u5fae\u4e00\u6014\uff0c\u9053\uff1a\u201c\u6211\uff1f\u201d \u3000\u3000\u201c\u5bf9\uff01\u4f60\u548c\u9ec4\u9f99\u5343\u91cc\u8fe2\u8fe2\u7684\u8d76\u6765\u8fd9\u91cc\uff0c\u624d\u5f15\u8fd9\u4e00\u573a\u706b\u62fc\u3001\u98ce\u6ce2\uff0c\u4e0d\u6740\u4f60\u65e0\u6cd5\u4e86\u5c40\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u76ee\u5149\u4e2d\u6cdb\u8d77\u6740\u673a\uff0c\u63a5\u9053\uff1a\u201c\u518d\u8bf4\u9ec4\u9f99\u4e4b\u6b7b\uff0c\u4f60\u53c8\u4f55\u5fcd\u72ec\u751f\uff1f\u201d \u3000\u3000\u201c\u6211\u662f\u8c01\uff1f\u4f60\u53ef\u77e5\u9053\uff1f\u2026\u2026\u201d\u7384\u8863\u5c11\u5973\u51b7\u7b11\u4e00\u58f0\u53c8\u9053\uff1a\u201c\u9ec4\u9f99\u662f\u6211\u7684\u670b\u53cb\uff0c\u4f46\u6211\u4eec\u4e0d\u662f\u60c5\u4fa3\uff0c\u4f60\u4e0d\u8981\u80e1\u8bf4\u516b\u9053\u3002\u201d \u3000\u3000\u6c5f\u67ab\u5fc3\u4e2d\u5fd6\u9053\uff1a\u201c\u542c\u53e3\u6c14\uff0c\u5979\u597d\u50cf\u4e0d\u662f\u5185\u5bab\u4e00\u7cfb\u4e2d\u4eba\uff0c\u90a3\u5c31\u66f4\u597d\uff0c\u6740\u4e86\u4f60\u4f7f\u95ee\u9898\u590d\u6742\u4e00\u4e9b\u2026\u2026\u201d \u3000\u3000\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u53e3\u4e2d\u8bf4\u9053\uff1a\u201c\u4e0d\u7ba1\u4f60\u662f\u8c01\uff1f\u4f46\u4f60\u7834\u574f\u4e86\u8fd9\u6d1b\u9633\u5c45\u4e2d\u7684\u548c\u7766\uff0c\u53ea\u6b64\u4e00\u693f\uff0c\u5c31\u662f\u6b7b\u7f6a\u2026\u2026\u201d \u3000\u3000\u201c\u6b7b\u7f6a\uff0c\u2026\u2026\u201d\u7384\u8863\u5c11\u5973\u6c14\u6781\u800c\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u662f\u4e2a\u4ec0\u4e48\u4e1c\u897f\uff0c\u80fd\u591f\u5b9a\u6211\u6b7b\u7f6a\uff0c\u6211\u2026\u2026\u201d \u3000\u3000\u201c\u4f60\u975e\u6b7b\u4e0d\u53ef\u2026\u2026\u201d\u6c5f\u67ab\u626c\u624b\u63a8\u51fa\u4e00\u638c\u3002 \u3000\u3000\u4e0d\u95fb\u638c\u98ce\u547c\u5578\uff0c\u4e0d\u89c1\u60ca\u4eba\u5a01\u52bf\uff0c\u4f46\u5374\u95fb\u90a3\u7384\u8863\u5c11\u5973\u5524\u4e86\u4e00\u58f0\uff0c\u5168\u8eab\u4e00\u98a4\uff0c\u4e03\u7a83\u9c9c\u8840\u6d8c\u51fa\uff0c\u7136\u540e\uff0c\u7f13\u7f13\u7684\u5012\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u6885\u82b1\u5c31\u7ad9\u5728\u90a3\u7384\u8863\u5c11\u5973\u7684\u8eab\u4fa7\uff0c\u4f46\u5374\u6ca1\u6709\u4e00\u70b9\u611f\u89c9\u3002 \u3000\u3000\u201c\u6885\u82b1\uff0c\u9c9c\u8840\u67d3\u7ea2\u4e86\u4f60\uff0c\u4f60\u7a0d\u4e3a\u7528\u4e00\u70b9\u5fc3\u673a\uff0c\u5185\u5bab\u5c31\u4e0d\u6562\u52a8\u4f60\uff0c\u6d1b\u9633\u5c45\u4ecd\u4e2a\u662f\u82b1\u56fd\u4e50\u56ed\uff0c\u4f60\u4e0d\u72af\u4eba\uff0c\u6211\u76f8\u4fe1\u4e0d\u4f1a\u6709\u72af\u4f60\u2026\u2026\u201d\u6c5f\u67ab\u76ee\u5149\u8f6c\u5411\u9093\u98de\u9053\uff1a\u201c\u603b\u7ba1\uff0c\u8fd9\u91cc\u7684\u4e8b\uff0c\u6682\u65f6\u544a\u4e00\u6bb5\u843d\uff0c\u5c5e\u4e0b\u5148\u884c\u544a\u9000\u3002\u201d\u8f6c\u8eab\u5411\u5916\u884c\u53bb\u3002 \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u4e00\u65bd\u773c\u8272\uff0c\u80e1\u840d\u3001\u6bb5\u4e5d\uff0c\u7d27\u968f\u5728\u6c5f\u67ab\u8eab\u540e\u800c\u53bb\u3002 \u3000\u3000\u4e00\u53e3\u6c14\u884c\u56de\u5367\u5ba4\uff0c\u6c5f\u67ab\u624d\u56de\u5934\u5bf9\u4e03\u5b9d\u548c\u5c1a\uff0c\u9053\uff1a\u201c\u5b88\u4f4f\u623f\u95e8\uff0c\u7ed9\u6211\u4e00\u4e2a\u65f6\u8fb0\u7684\u8c03\u606f\u65f6\u95f4\u3002\u201d \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u4e00\u70b9\u5934\uff0c\u5429\u5490\u4e86\u80e1\u840d\u3001\u6bb5\u4e5d\u5b88\u5728\u95e8\u5916\uff0c\u4eba\u5374\u8ddf\u7740\u6c5f\u67ab\u884c\u5165\u4e86\u5185\u5ba4\u3002 \u3000\u3000\u6c5f\u67ab\u53d6\u4e0b\u7751\u4e0a\u7684\u9762\u5177\uff0c\u9732\u51fa\u4e00\u5f20\u82cd\u767d\u7684\u8138\u8272\uff0c\u9053\uff1a\u201c\u5927\u5e08\uff0c\u6211\u597d\u7d2f\uff0c\u4eba\u7a76\u7adf\u662f\u8840\u8089\u4e4b\u8eaf\u554a\uff01\u2026\u2026\u90a3\u4e00\u638c\u2026\u2026\u201d \u3000\u3000\u201c\u5feb\u4e9b\u8fd0\u6c14\u8c03\u606f\uff0c\u4e0d\u8981\u8bf4\u8bdd\uff0c\u548c\u5c1a\u52a9\u4f60\u4e00\u81c2\u4e4b\u529b\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u4f38\u51fa\u53f3\u638c\uff0c\u62b5\u5728\u6c5f\u67ab\u80cc\u5fc3\u4e0a\uff0c\u5185\u529b\u6e90\u6e90\u6d8c\u5165\u6c5f\u67ab\u4f53\u5185\uff0c\u5f15\u52a8\u4e86\u6c5f\u67ab\u4f53\u5185\u771f\u6c14\uff0c\u6d41\u5411\u56db\u80a2\u767e\u9ab8\u3002 \u3000\u3000\u8fce\u5bbe\u9601\u7684\u5927\u5385\u4e2d\uff0c\u5929\u865a\u5b50\u548c\u6885\u82b1\u7b49\u4e00\u6279\u4eba\uff0c\u5168\u90fd\u5446\u4f4f\u4e86\uff0c\u6ca1\u6709\u770b\u51fa\u97e9\u9738\u90a3\u4e00\u638c\u662f\u600e\u4e48\u56de\u4e8b\uff1f \u3000\u3000\u90a3\u7384\u8863\u5c11\u5973\u5c31\u90a3\u4e48\u6b7b\u4e86\u3002 \u3000\u3000\u6885\u82b1\u60ca\u9b42\u590d\u5143\uff0c\u8f7b\u8f7b\u53f9\u606f\u4e00\u58f0\uff0c\u62b1\u8d77\u4e86\u7384\u8863\u5c11\u5973\u7684\u5c38\u4f53\uff0c\u9053\uff1a\u201c\u56db\u4f4d\u5e08\u59b9\uff0c\u5e26\u4e0a\u5927\u5e08\u5144\u7684\u5c38\u4f53\uff0c\u8d70\uff01\u201d \u3000\u3000\u6625\u82b1\u56db\u5a62\u542c\u5f97\u6885\u82b1\u7684\u5429\u5490\uff0c\u624d\u5982\u68a6\u521d\u9192\u4e00\u822c\uff0c\u62b1\u8d77\u4e86\u9ec4\u9f99\u5c38\u4f53\uff0c\u5306\u5306\u79bb\u53bb\u3002 \u3000\u3000\u539f\u672c\u9884\u671f\u662f\u4e00\u573a\u60e8\u70c8\u7684\u6076\u6218\uff0c\u4f46\u5374\u6210\u4e86\u6c5f\u67ab\u7684\u4e2a\u4eba\u8868\u6f14\uff0c\u6280\u9707\u5168\u573a\u3002 \u3000\u3000\u201c\u9093\u98de\u5440\uff01\u4f60\u8fd9\u4e2a\u603b\u7ba1\u5e72\u7684\u5b9e\u5728\u8212\u773c\u554a\uff01\u2026\u2026\u201d\u5929\u865a\u5b50\u9053\uff1a\u201c\u6709\u4e86\u8fd9\u4e48\u4e00\u4f4d\u8eab\u6000\u7edd\u6280\u7684\u526f\u603b\u7ba1\uff0c\u53ef\u4ee5\u9ad8\u6795\u65e0\u5fe7\u4e86\u3002\u201d \u3000\u3000\u9093\u98de\u6413\u7740\u624b\uff0c\u9762\u6709\u6127\u8272\u7684\u8bf4\uff1a\u201c\u60ed\u6127\u5440\uff01\u60ed\u6127\uff01\u6211\u77e5\u9053\u4ed6\u6709\u4e9b\u6df1\u85cf\u4e0d\u9732\uff0c\u53ef\u662f\u6ca1\u6709\u60f3\u5230\u4ed6\u7adf\u662f\u5982\u6b64\u5353\u5c14\u4e0d\u7fa4\u2026\u2026\u3002\u201d \u3000\u3000\u201c\u526f\u603b\u5802\u4e3b\u2026\u2026\u201d\u5201\u9e4f\u8bf4\uff0c\u201c\u6211\u770b\u97e9\u9738\u7684\u8eab\u624b\uff0c\u5e94\u8be5\u6709\u603b\u5802\u4e2d\u957f\u8001\u7684\u8eab\u4efd\u4e86\uff1f\u201d \u3000\u3000\u201c\u5509\uff01\u8bf4\u5230\u4ed6\u6b66\u529f\u4e4b\u9ad8\uff0c\u5c31\u7b97\u526f\u603b\u5802\u4e3b\u7684\u8eab\u4efd\uff0c\u4e5f\u4e0d\u4e3a\u8fc7\uff0c\u90a3\u4e00\u638c\u4e4b\u51cc\u5389\u602a\u5f02\uff0c\u6bd4\u8001\u4e07\u90a3\u62db\u9b42\u4e00\u638c\u8fd8\u8981\u9ad8\u660e\uff0c\u53ef\u7b11\u4e07\u65b9\u548c\u4e01\u897f\u5c71\u7adf\u662f\u6709\u773c\u4e0d\u8bc6\u91d1\u5c71\u7389\uff0c\u5f53\u9762\u9519\u8fc7\u4e86\u8fd9\u7b49\u9ad8\u4eba\uff0c\u2026\u2026\u201d\u5929\u865a\u5b50\u5fae\u7b11\u8bf4\uff0c\u201c\u9093\u603b\u7ba1\uff0c\u97e9\u9738\u90a3\u4e00\u638c\uff0c\u53eb\u4ec0\u4e48\u540d\u5802\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4e2a\uff0c\u8fd9\u4e2a\uff0c\u5c5e\u4e0b\u4e5f\u2026\u2026\u4e5f\u2026\u2026\u201d \u3000\u3000\u201c\u526f\u603b\u5802\u4e3b\uff0c\u4e0d\u7528\u95ee\u4ed6\u4e86\uff0c\u95ee\u4e5f\u662f\u767d\u95ee\uff0c\u2026\u2026\u201d\u5201\u9e4f\u9053\uff1a\u201c\u91cd\u8981\u7684\u662f\u8981\u5982\u4f55\u5b89\u6392\u97e9\u9738\uff1f\u201d \u3000\u3000\u201c\u5bf9\uff01\u9093\u98de\uff0c\u4f60\u6709\u4ec0\u4e48\u610f\u89c1\uff1f\u2026\u2026\u201d\u5929\u864e\u5b50\u4e24\u9053\u76ee\u5149\u76ef\u6ce8\u5728\u9093\u98de\u7684\u8138\u4e0a\uff0c\u770b\u5f97\u9093\u98de\u9762\u8272\u5982\u571f\uff0c\u624b\u5fc3\u4e2d\u76f4\u5192\u51b7\u6c57\u3002 \u3000\u3000\u201c\u6211\u770b\u5c5e\u4e0b\u5e94\u8be5\u8f9e\u53bb\u8fd9\u4e2a\u804c\u4f4d\u2026\u2026\u201d\u9093\u98de\u8bf4\uff0c\u7531\u97e9\u9738\u63a5\u638c\u6d1b\u9633\u5c45\u7684\u603b\u7ba1\u3002\u201d \u3000\u3000\u201c\u95ee\u9898\u662f\uff0c\u4ed6\u544a\u4e0d\u80af\u5c48\u5c31\u2026\u2026\u201d\u5201\u9e4f\u8bf4\uff0c\u201c\u526f\u603b\u5802\u4e3b\u6167\u773c\u8bc6\u4eba\uff0c\u903c\u5730\u9732\u76f8\uff0c\u4f46\u4ed6\u4e00\u4e3e\u95f4\u8fde\u6bd9\u4e24\u540d\u654c\u4eba\u7684\u9996\u8111\u4eba\u7269\uff0c\u663e\u7136\u5df2\u4e0d\u4f1a\u518d\u4f5c\u86f0\u4f0f\uff0c\u6709\u5982\u5927\u9e4f\u5c55\u7ffc\uff0c\u8981\u4e00\u98de\u51b2\u5929\uff0c\u600e\u4f1a\u628a\u6d1b\u9633\u5c45\u8fd9\u4e2a\u603b\u7ba1\u7684\u8eab\u4efd\uff0c\u770b\u5728\u773c\u4e2d\uff1f\u201d \u3000\u3000\u5201\u9e4f\u88ab\u79f0\u4e5d\u5934\u9e1f\uff0c\u679c\u662f\u4eba\u5982\u5176\u8bf4\uff0c\u5c3d\u7ba1\u5fc3\u4e2d\u5bf9\u5929\u865a\u5b50\u6781\u4e3a\u4e0d\u6ee1\uff0c\u4f46\u4ecd\u5fd8\u4e0d\u4e86\u6293\u4f4f\u673a\u4f1a\u5c31\u62cd\u4e0a\u4e00\u8bb0\u9a6c\u5c41\u3002 \u3000\u3000\u201c\u5c0f\u5201\uff0c\u4f60\u8bf4\uff0c\u5e94\u8be5\u5982\u4f55\u5b89\u6392\u97e9\u9738\uff1f\u2026\u2026\u201d\u5201\u9e4f\u6b63\u60f3\u6bdb\u9042\u81ea\u8350\uff0c\u4ee5\u4fbf\u65e9\u4e9b\u89c1\u5230\u97e9\u9738\u3002 \u3000\u3000\u4f46\u9093\u98de\u5df2\u62a2\u5148\u8bf4\u9053\uff1a\u201c\u5bf9\uff01\u5c5e\u4e0b\u548c\u4ed6\u76f8\u5904\u7684\u4e00\u76f4\u4e0d\u9519\uff0c\u8fd9\u4ef6\u4e8b\uff0c\u5c31\u7531\u5c5e\u4e0b\u53bb\u529e\uff0c\u5c3d\u5feb\u5411\u526f\u603b\u5802\u4e3b\u56de\u62a5\uff01\u201d \u3000\u3000\u5929\u865a\u5b50\u9053\uff1a\u201c\u4e86\u89e3\u97e9\u9738\u7684\u5fc3\u610f\u4e4b\u540e\uff0c\u8001\u9053\u518d\u5411\u603b\u5802\u56de\u62a5\uff0c\u5c0f\u5201\uff0c\u53bb\u901a\u77e5\u5916\u9762\u7684\u4eba\u624b\uff0c\u8981\u4ed6\u4eec\u6682\u65f6\u4f4f\u5728\u5ba2\u6808\u4e2d\u900d\u9065\u51e0\u5929\uff0c\u4e0d\u7528\u8fdb\u9a7b\u6d1b\u9633\u5c45\u4e86\u3002\u201d \u3000\u3000\u6885\u82b1\u4e5f\u4f20\u51fa\u4ee4\u6109\uff0c\u968f\u540c\u9ec4\u9f99\u540c\u6765\u7684\u5251\u58eb\u3001\u6740\u624b\uff0c\u4e5f\u6682\u65f6\u4e0d\u5f97\u8fdb\u5165\u6d1b\u9633\u5c45\u4e2d\uff0c\u4e00\u9762\u9063\u4eba\u5411\u5bab\u4e3b\u5448\u62a5\u6d1b\u9633\u5c45\u4e2d\u7684\u53d8\u5316\uff0c\u4e8b\u4ef6\u91cd\u5927\uff0c\u6885\u82b1\u5df2\u4e0d\u6562\u64c5\u4f5c\u4e3b\u5f20\uff0c\u4e5f\u4e0d\u77e5\u8be5\u5982\u4f55\u5904\u7f6e\uff1f \u3000\u3000\u9093\u98de\u76f4\u884c\u5230\u97e9\u9738\u7684\u4f4f\u5904\uff0c\u5927\u51fa\u610f\u5916\u7684\u662f\u7adf\u88ab\u80e1\u840d\u548c\u6bb5\u4e5d\u6321\u4f4f\u3002 \u3000\u3000\u80e1\u840d\u5f88\u5a49\u8f6c\u5730\u8bf4\uff1a\u201c\u526f\u603b\u7ba1\u6b63\u5728\u8c03\u606f\uff0c\u6307\u4ee4\u6211\u4eec\u7d27\u5b88\u95e8\u6237\uff0c\u4efb\u4f55\u4eba\u4e0d\u80fd\u64c5\u81ea\u95ef\u5165\uff0c\u8fd9\u4e00\u70b9\uff0c\u8bf7\u603b\u7ba1\u591a\u591a\u9274\u8c05\u3002\u201d \u3000\u3000\u5982\u679c\u5728\u65e9\u4e00\u5929\uff0c\u9093\u98de\u4f1a\u7acb\u523b\u5927\u53d1\u813e\u6c14\uff0c\u5f53\u7136\uff0c\u80e1\u840d\u3001\u6bb5\u4e5d\u4e5f\u4f1a\u5f53\u9762\u5f00\u9500\uff0c\u5403\u4e00\u987f\u6392\u5934\u3002 \u3000\u3000\u4f46\u73b0\u5728\uff0c\u9093\u98de\u9093\u5fcd\u4e0b\u53bb\uff0c\u7b11\u4e00\u7b11\u9053\uff1a\u201c\u597d\u3001\u597d\uff0c\u97e9\u526f\u603b\u7ba1\u9192\u8fc7\u6765\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u4eec\u4f1a\u544a\u8bc9\u4ed6\u603b\u7ba1\u6765\u8fc7\uff0c\u8bf7\u4ed6\u7acb\u523b\u53bb\u5411\u603b\u7ba1\u62a5\u5230\u2026\u2026\u201d\u6bb5\u4e5d\u4e5f\u5b66\u4f1a\u4e86\u8ba8\u597d\u7684\u8bed\u6c14\u3002 \u3000\u3000\u201c\u4e0d\uff01\u53bb\u901a\u77e5\u6211\uff0c\u6211\u6765\u770b\u4ed6\u2026\u2026\u201d\u9093\u98de\u8bf4\uff0c\u201c\u97e9\u526f\u603b\u7ba1\u662f\u6d1b\u9633\u5c45\u7684\u5927\u529f\u81e3\uff0c\u4e5f\u662f\u5929\u9a6c\u5802\u7684\u5927\u529f\u81e3\uff0c\u4f60\u4eec\u8981\u597d\u597d\u7684\u4f8d\u5019\u7740\u3002\u201d \u3000\u3000\u6c5f\u67ab\u6062\u590d\u5f97\u5f88\u5feb\uff0c\u9093\u98de\u6765\u7684\u65f6\u5019\uff0c\u4ed6\u5df2\u5750\u606f\u9192\u6765\uff0c\u4f46\u4ed6\u6ca1\u6709\u8fce\u89c1\u9093\u98de\uff0c\u5374\u5728\u548c\u4e03\u5b9d\u548c\u5c1a\u7814\u5546\u5927\u8ba1\u3002 \u3000\u3000\u8fd9\u4e9b\u65f6\u65e5\u76f8\u5904\uff0c\u6c5f\u67ab\u53d1\u89c9\u4e86\u4e03\u5b9d\u548c\u5c1a\u4e0d\u4f46\u5934\u8111\u7f1c\u5bc6\u3001\u6599\u4e8b\u5982\u795e\uff0c\u6b66\u529f\u7684\u6df1\u539a\uff0c\u4e5f\u9ad8\u51fa\u4e86\u94b1\u7f3a\u7b49\u5f88\u591a\uff0c\u4f46\u4ed6\u80fd\u6df1\u85cf\u4e0d\u9732\uff0c\u800c\u4e14\uff0c\u4e00\u85cf\u6570\u5341\u5e74\uff0c\u6c5f\u6e56\u4e0a\u90fd\u77e5\u9053\u9152\u8089\u548c\u5c1a\u548c\u8001\u53eb\u5316\u662f\u4e00\u5bf9\u98ce\u5c18\u77e5\u5df1\u3002 \u3000\u3000\u4e24\u4eba\u7684\u6b66\u529f\uff0c\u4e5f\u5728\u4f2f\u3001\u4ef2\u4e4b\u95f4\uff0c\u4e0d\u76f8\u4e0a\u4e0b\uff0c\u4e8b\u5b9e\u4e0a\uff0c\u9152\u8089\u548c\u5c1a\u80f8\u7f57\u4e4b\u535a\uff0c\u6b66\u529f\u4e4b\u5f3a\uff0c\u4e09\u4e94\u4e2a\u8001\u53eb\u5316\u4e5f\u4e0d\u662f\u654c\u624b\uff0c\u4f46\u6c5f\u67ab\u5374\u53d1\u89c9\u4e86\u8fd9\u4e2a\u9690\u5bc6\uff0c\u90a3\u662f\u9152\u8089\u548c\u5c1a\u52a9\u4ed6\u884c\u529f\u65f6\uff0c\u8868\u73b0\u7684\u6df1\u539a\u529f\u529b\u3002 \u3000\u3000\u6df1\u8bd8\u4e4b\u4e0b\uff0c\u9152\u8089\u548c\u5c1a\u624d\u8bf4\u51fa\u4e86\u5fc3\u4e2d\u79d8\u5bc6\uff0c\u7adf\u662f\u6c5f\u67ab\u5e08\u7236\u795e\u50e7\u65e0\u76f8\u5927\u5e08\u7559\u7ed9\u6c5f\u67ab\u7684\u4e00\u4e2a\u52a9\u624b\u3002 \u3000\u3000\u65e0\u76f8\u5927\u5e08\u5316\u4e86\u534a\u5e74\u65f6\u95f4\uff0c\u6307\u70b9\u4e03\u5b9d\u548c\u5c1a\u7684\u6b66\u529f\uff0c\u4f46\u5374\u8981\u4ed6\u6e38\u8d70\u98ce\u5c18\uff0c\u9690\u533f\u5b9e\u529b\uff0c\u4e0d\u5f97\u8f7b\u6613\u70ab\u9732\u51fa\u6765\u3002\u534a\u5e74\u65f6\u95f4\uff0c\u4e0d\u662f\u5f88\u957f\uff0c\u4f46\u4e03\u5b9d\u548c\u5c1a\u5374\u719f\u8bb0\u4e86\u5404\u79cd\u8981\u8bc0\uff0c\u4e8c\u5341\u4e5d\u5e74\u6765\u4e00\u76f4\u5bc6\u7ec3\u3001\u82e6\u4fee\uff0c\u65e9\u5df2\u767b\u5802\u5165\u5ba4\uff0c\u8eab\u96c6\u5927\u6210\uff0c\u8fde\u5f53\u5e74\u4f20\u827a\u7684\u65e0\u76f8\u5927\u5e08\uff0c\u4e5f\u6ca1\u6709\u60f3\u5230\u8fd9\u4e2a\u9152\u8089\u4e0d\u6212\u7684\u91ce\u548c\u5c1a\u7adf\u80fd\u4e3e\u4e00\u53cd\u4e09\uff0c\u4fee\u70bc\u6210\u4eca\u65e5\u7684\u9ad8\u660e\u8eab\u624b\u3002 \u3000\u3000\u6c5f\u67ab\u4e86\u89e3\u4e86\u5185\u60c5\u4e4b\u540e\u5927\u4e3a\u653e\u5fc3\uff0c\u6709\u8fd9\u4e48\u4e00\u4f4d\u9ad8\u624b\u8ddf\u968f\u8eab\u4fa7\uff0c\u5e2e\u4ed6\u72ec\u6321\u4e00\u9762\uff0c\u6c5f\u67ab\u5c31\u53ef\u4ee5\u817e\u51fa\u8eab\u5b50\uff0c\u653e\u624b\u65bd\u4e3a\uff0c\u4e5f\u5c31\u628a\u5e03\u7f72\u7684\u8ba1\u5212\uff0c\u5168\u90fd\u544a\u8bc9\u4e86\u4e03\u5b9d\u548c\u5c1a\uff0c\u800c\u4e14\u8981\u4ed6\u5927\u529b\u683d\u57f9\u80e1\u5143\u3001\u90ed\u5929\u540c\uff0c\u4f7f\u4ed6\u4eec\u65e9\u6210\u5927\u5668\uff0c\u4ee5\u6210\u4e3a\u5bf9\u4ed8\u5f3a\u654c\u7684\u9ad8\u624b\u3002 \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e24\u4e2a\u5b69\u5b50\u90fd\u4e0d\u9519\uff0c\u6211\u548c\u5c1a\u5f53\u5c3d\u5168\u529b\u6559\u5bfc\uff0c\u7edd\u4e0d\u85cf\u79c1\uff0c\u4f46\u4f60\u4e5f\u4e0d\u8981\u671f\u671b\u592a\u9ad8\uff0c\u6211\u548c\u5c1a\u7684\u6b66\u529f\uff0c\u5f97\u81ea\u795e\u50e7\u6307\u70b9\uff0c\u90a3\u662f\u6b63\u7edf\u6b66\u5b66\uff0c\u5fc5\u987b\u82b1\u8d39\u591a\u5e74\u65f6\u95f4\uff0c\u624d\u80fd\u5960\u5b9a\u96c4\u539a\u7684\u57fa\u7840\uff0c\u53ef\u4e0d\u80fd\u548c\u5929\u72d0\u592b\u4eba\u7684\u6280\u827a\u6839\u6bd4\uff0c\u8be1\u5947\u8f9b\u8fa3\uff0c\u51fa\u4eba\u610f\u8868\uff0c\u6211\u770b\u773c\u524d\u80fd\u5f53\u5927\u4efb\u7684\u5012\u662f\u96ea\u3001\u971c\u4e24\u4f4d\u59d1\u5a18\uff0c\u5979\u4eec\u53ef\u80fd\u5df2\u627f\u7ee7\u4e86\u5929\u72d0\u771f\u4f20\u3002\u201d \u3000\u3000\u201c\u597d\u773c\u5149\u2026\u2026\u201d\u6c5f\u67ab\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5979\u4eec\u85cf\u771f\u7684\u672c\u9886\uff0c\u53ef\u4e0d\u5728\u4f60\u548c\u5c1a\u4e4b\u4e0b\uff0c\u6211\u51e0\u4e4e\u88ab\u5979\u4eec\u9a97\u8fc7\u53bb\u4e86\uff0c\u73b0\u5728\uff0c\u6211\u5df2\u7ecf\u653e\u5fc3\u7684\u91cd\u7528\u5979\u4eec\u4e86\uff0c\u5929\u72d0\u6280\u827a\uff0c\u8d35\u5728\u901f\u6210\uff0c\u4e24\u4e2a\u4e2b\u5934\u7ec3\u4e86\u5341\u51e0\u5e74\uff0c\u8fd9\u65b9\u9762\u7684\u6210\u5c31\uff0c\u53ef\u80fd\u5df2\u4e0d\u5728\u6211\u4e4b\u4e0b\u4e86\uff0c\u6570\u6708\u4e4b\u524d\uff0c\u6211\u8fd8\u4e3a\u72ec\u6491\u5927\u5c40\u82e6\u607c\uff0c\u60f3\u4e0d\u5230\u5e08\u957f\u4eec\u6069\u60c5\u6df1\u91cd\u5fc3\u613f\uff0c\u7136\u540e\u2026\u2026\u201d\u7a81\u7136\u4f4f\u56db\u4e0d\u8a00\u3002 \u3000\u3000\u201c\u7136\u540e\uff0c\u600e\u4e48\u6837\u554a\uff1f\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u542c\u51fa\u4e86\u5f26\u5916\u6709\u97f3\uff0c\u4f46\u5c31\u662f\u60f3\u4e0d\u51fa\u6c5f\u67ab\u5fc3\u4e2d\u75db\u82e6\u4e4b\u6e90\u3002 \u3000\u3000\u8fd9\u4f4d\u5916\u62d9\u5185\u79c0\u7684\u5927\u548c\u5c1a\uff0c\u5bf9\u81ea\u5df1\u6599\u4e8b\u4e4b\u80fd\uff0c\u4e00\u5411\u9887\u4e3a\u81ea\u8d1f\uff0c\u95fb\u5f26\u6b4c\u800c\u77e5\u96c5\u610f\uff0c\u4f46\u8fd9\u4e00\u6b21\uff0c\u5374\u662f\u5fc3\u5ff5\u5343\u8f6c\uff0c\u60f3\u4e0d\u51fa\u56f0\u6270\u6c5f\u67ab\u7684\u539f\u56e0\u3002 \u3000\u3000\u201c\u5929\u72d0\u6280\u827a\uff0c\u5728\u4e09\u4f4d\u5e08\u957f\u7684\u6b66\u529f\u4e2d\u6700\u6613\u6210\u5c31\uff0c\u4e5f\u6700\u90aa\u6c14\uff0c\u5bf9\u6297\u9b54\u6559\u6b66\u529f\uff0c\u4e5f\u6700\u80fd\u53d1\u6325\u6548\u7528\u3002\u201d \u3000\u3000\u201c\u90a3\u597d\u554a\uff01\u8001\u5f1f\uff0c\u76ee\u4e0b\u6c5f\u6e56\uff0c\u904d\u5e03\u90aa\u6076\uff0c\u4ee5\u9b54\u5236\u9b54\uff0c\u5e94\u8be5\u662f\u6700\u6709\u6548\u7684\u624b\u6bb5\u4e86\u3002\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u8bf4\uff0c\u201c\u6c5f\u6e56\u4e49\u7406\uff0c\u65e9\u5df2\u4e3a\u90aa\u6076\u63a9\u906e\uff0c\u9010\u4e0d\u8d70\u8fd9\u4e00\u5929\u9634\u6697\uff0c\u4e5f\u65e0\u6cd5\u8bba\u9053\u8bf4\u7406\uff0c\u8001\u5f1f\uff0c\u6240\u8c13\u9739\u96f3\u624b\u6bb5\uff0c\u83e9\u8428\u5fc3\u80a0\uff0c\u4e0d\u7528\u4e3a\u6b64\u81ea\u82e6\u3002\u201d \u3000\u3000\u201c\u6211\u6b65\u5165\u5f97\u592a\u6df1\u4e86\u2026\u2026\u201d\u6c5f\u67ab\u75db\u82e6\u5730\u8bf4\u9053\uff1a\u201c\u72d0\u6027\u8fc7\u91cd\uff0c\u4f46\u53c8\u65e0\u6cd5\u63a7\u5236\u5f97\u5f88\u597d\u2026\u2026\u201d \u3000\u3000\u5927\u548c\u5c1a\u6709\u70b9\u660e\u767d\u4e86\uff0c\u7b11\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u5934\u60ac\u6881\u3001\u9525\u523a\u9aa8\uff0c\u7ea2\u8896\u6dfb\u9999\u591c\u8bfb\u4e66\uff0c\u53ea\u8981\u80fd\u628a\u4e66\u8bfb\u597d\uff0c\u5982\u4f55\u4e00\u4e2a\u8bfb\u6cd5\uff0c\u90a3\u5c31\u4e0d\u7528\u8ba1\u8f83\u4e86\uff0c\u9152\u3001\u8272\u3001\u8d22\u3001\u6c14\uff0c\u6709\u56db\u5927\u6212\uff0c\u6211\u548c\u5c1a\u4e09\u6212\u4e0d\u7a7a\uff0c\u4f46\u9152\u8089\u7a7f\u80a0\u8fc7\uff0c\u4f5b\u5728\u6211\u5fc3\u5750\uff0c\u4e0d\u4e49\u4e4b\u8d22\uff0c\u53d6\u4e0d\u4f24\u5ec9\uff0c\u62ff\u8fc7\u6765\u5e03\u65bd\u4f17\u751f\uff0c\u7b97\u662f\u66ff\u4ed6\u4eec\u4f5c\u529f\u5fb7\uff0c\u8bf4\u5230\u6c14\u5b57\uff0c\u5982\u4e0d\u662f\u8fd9\u80a1\u6c14\u5fff\u96be\u5e73\uff0c\u548c\u5c1a\u65e9\u5c31\u627e\u4e00\u5904\u6e05\u9759\u5bfa\u9662\uff0c\u517b\u5fc3\u609f\u6027\uff0c\u5b89\u5b89\u9038\u9038\u8fc7\u65e5\u5b50\u4e86\uff0c\u53c8\u4f55\u82e6\u5954\u6ce2\u52b3\u788c\uff0c\u4e3a\u4ed6\u4eba\u4f5c\u5ac1\u8863\u88f3\uff0c\u72ec\u72ec\u4e00\u4e2a\u8272\u5b57\uff0c\u548c\u6211\u65e0\u7f18\uff0c\u90a3\u8981\u62dc\u6211\u8fd9\u526f\u5c0a\u5bb9\u4e4b\u8d50\u4e86\uff0c\u8001\u5b9e\u8bf4\uff0c\u771f\u8981\u6709\u4e00\u4f4d\u7edd\u8272\u7f8e\u5973\uff0c\u60c5\u7518\u5e03\u65bd\uff0c\u6211\u548c\u5c1a\u80fd\u4e0d\u80fd\u81ea\u5236\u5f97\u4f4f\uff0c\u53ef\u662f\u4e00\u70b9\u70b9\u4e5f\u6ca1\u6709\u628a\u63e1\u3002 \u3000\u3000\u56db\u5927\u6212\uff0c\u6211\u548c\u5c1a\u72af\u4e86\u4e8c\u6212\uff0c\u4f46\u6211\u5374\u6d3b\u7684\u5fc3\u5b89\u7406\u5f97\uff0c\u6740\u4e00\u4eba\uff0c\u6551\u5343\u767e\u4eba\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u6740\uff1f\u9664\u4e00\u6076\uff0c\u80dc\u4e3a\u767e\u5584\uff0c\u5c31\u7b97\u6b7b\u540e\u88ab\u6253\u5165\u5341\u516b\u5c42\u5730\u72f1\uff0c\u6211\u548c\u5c1a\u4e5f\u8ba4\u4e86\uff0c\u5272\u8089\u9972\u864e\uff0c\u864e\u58ee\u4f24\u4eba\uff0c\u600e\u4e48\u529e\u5462\uff1f\u6211\u548c\u5c1a\u7985\u529f\u4e0d\u6df1\uff0c\u8fd9\u4e00\u70b9\u6709\u4e9b\u60f3\u4e0d\u901a\uff1f\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u5012\u662f\u88ab\u9017\u7b11\u4e86\uff0c\u9053\uff1a\u201c\u4f60\u8fd9\u6b6a\u7406\u5341\u516b\u7ae0\uff0c\u542c\u8d77\u6765\uff0c\u5012\u4e5f\u662f\u632f\u632f\u6709\u8bcd\u554a\uff01\u201d \u3000\u3000\u201c\u4e24\u60c5\u76f8\u60a6\uff0c\u5fc3\u613f\u5f97\u507f\uff0c\u6b7b\u800c\u65e0\u61be\uff0c\u6d3b\u5f97\u7518\u5fc3\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u9053\uff1a\u201c\u81f3\u4e8e\u522b\u4eba\u7684\u770b\u6cd5\u5982\u4f55\uff1f\u53c8\u4f55\u5fc5\u653e\u5728\u5fc3\u4e0a\uff0c\u76ee\u4e0b\u6c5f\u6e56\u60c5\u52bf\uff0c\u6b63\u90aa\u51b3\u6218\u5728\u5373\uff0c\u6267\u5927\u4e49\uff0c\u4e0d\u62d8\u5c0f\u8282\uff0c\u6280\u827a\u6709\u9ad8\u4f4e\uff0c\u771f\u7406\u53ea\u4e00\u4e2a\uff0c\u4f60\u80a9\u8d1f\u5927\u4efb\uff0c\u90a3\u5c31\u53ea\u597d\u727a\u7272\u4e00\u4e9b\u4e86\uff0c\u9634\u9633\u76f8\u751f\u4e5f\u76f8\u514b\uff0c\u4f60\u4e0d\u514b\uff0c\u90a3\u5c31\u7b49\u4eba\u514b\u4f60\u4e86\uff0c\u4f55\u51b5\uff0c\u4f60\u8f93\u4e0d\u5f97\u554a\u2026\u2026\u3002\u201d \u3000\u3000\u6c5f\u67ab\u8138\u4e0a\u9634\u973e\u5c3d\u53bb\uff0c\u7b11\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u597d\u554a\uff01\u5927\u548c\u5c1a\uff0c\u6211\u4e0d\u5165\u5730\u72f1\uff0c\u8c01\u5165\u5730\u72f1\u2026\u2026\u201d \u3000\u3000\u201c\u653e\u5fc3\uff0c\u653e\u5fc3\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u9053\uff1a\u201c\u6211\u548c\u5c1a\u548c\u8001\u53eb\u5316\uff0c\u90fd\u4f1a\u5728\u5730\u72f1\u91cc\u7b49\u4f60\u3002\u201d \u3000\u3000\u96c4\u98ce\u91cd\u632f\uff0c\u6c5f\u67ab\u4e00\u8dc3\u800c\u8d77\uff0c\u9053\uff1a\u201c\u8bf4\u7684\u4e5f\u662f\uff0c\u4e09\u4f4d\u5e08\u957f\uff0c\u4e34\u53bb\u65f6\u90a3\u4efd\u9057\u61be\u7684\u795e\u60c5\uff0c\u4ecd\u53eb\u6211\u60f4\u60f4\u96be\u5b89\u2014\u201d \u3000\u3000\u201c\u4ed6\u4eec\u54c1\u5c1d\u4e86\u6148\u60b2\u7684\u82e6\u6da9\uff0c\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u53ea\u4f20\u4f60\u6b66\u529f\uff0c\u5374\u6ca1\u6709\u7559\u4e0b\u9650\u5236\u4f60\u7684\u89c4\u6212\uff0c\u5bf9\u5417\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u70b9\u70b9\u5934\u3002 \u3000\u3000\u201c\u4f46\u6211\u548c\u5c1a\u76f8\u4fe1\uff0c\u4ed6\u4eec\u7ed9\u4e86\u4f60\u8fa8\u8ba4\u662f\u975e\u7684\u80fd\u529b\uff0c\u4ed6\u4eec\u6559\u4f60\u6b66\u529f\uff0c\u4e5f\u8ba9\u4f60\u8bfb\u4e86\u5f88\u591a\u7684\u4e66\uff0c\u57f9\u517b\u4f60\u8fd9\u4e48\u4e00\u4e2a\u4eba\u624d\uff0c\u5316\u4e86\u4ed6\u4eec\u591a\u5c11\u7684\u5fc3\u8840\uff1f\u5982\u8ba9\u4f60\u51fa\u5165\u4ec1\u9014\uff0c\u7acb\u8eab\u5e99\u5802\uff0c\u5e94\u8be5\u6709\u66f4\u5927\u7684\u4f5c\u4e3a\u3002 \u3000\u3000\u4f46\u5374\u628a\u4f60\u653e\u4eba\u6c5f\u6e56\uff0c\u90a3\u662f\u56e0\u4e3a\u8fd9\u91cc\u9700\u8981\u4f60\uff0c\u4ed6\u4eec\u4e00\u5ff5\u4e4b\u4ec1\uff0c\u7559\u4e0b\u4e86\u7978\u60a3\uff0c\u6210\u4e86\u8336\u6bd2\u6b66\u6797\u7684\u5927\u5bb3\uff0c\u53ef\u80fd\u4ed6\u4eec\u4e5f\u5df2\u53d7\u5230\u4e86\u6697\u7b97\u4f24\u5bb3\uff0c\u6240\u4ee5\uff0c\u624d\u5168\u529b\u57f9\u517b\u4f60\uff0c\u8981\u4f60\u5b8c\u6210\u4ed6\u4eec\u7684\u5fc3\u8017\uff0c\u2026\u2026\u201d \u3000\u3000\u201c\u6709\u4e00\u70b9\u6211\u4e00\u76f4\u4e5f\u60f3\u4e0d\u660e\u767d\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u4ed6\u4eec\u4e3a\u4ec0\u4e48\u4e0d\u660e\u767d\u544a\u8bc9\u6211\u5143\u51f6\u9996\u6076\u5462\uff1f\u4e5f\u53ef\u7701\u53bb\u6211\u82b1\u8d39\u5f88\u591a\u65f6\u95f4\u5728\u6697\u4e2d\u6478\u7d22\u3002\u201d \u3000\u3000\u201c\u8fd9\u5c31\u662f\u6f5c\u79fb\u9ed8\u5316\uff0c\u8981\u4f60\u5728\u4e0d\u77e5\u4e0d\u89c9\u4e2d\u5168\u529b\u6295\u5165\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u53f9\u4e00\u53e3\u6c14\u53c8\u9053\uff1a\u201c\u4e5d\u534e\u5927\u4f1a\u4e4b\u540e\uff0c\u4e09\u4f4d\u8001\u4eba\u5bb6\u5c31\u6f5c\u9690\u4e0d\u89c1\uff0c\u6211\u60f3\uff0c\u90a3\u65f6\u5019\u4ed6\u4eec\u5df2\u81ea\u77e5\u8eab\u53d7\u6697\u7b97\uff0c\u65e0\u80fd\u4e3a\u529b\uff0c\u624d\u628a\u5e0c\u671b\u6295\u6ce8\u5230\u4f60\u7684\u8eab\u4e0a\u3002 \u3000\u3000\u6211\u548c\u5c1a\u4e0d\u77e5\u9053\u4ed6\u4eec\u5982\u4f55\u6491\u4e0b\u6765\u7684\uff0c\u4f46\u6211\u60f3\u4ed6\u4eec\u4e00\u5b9a\u6d3b\u5f97\u5f88\u8f9b\u82e6\uff0c\u628a\u6bd5\u751f\u7684\u4fee\u4e3a\uff0c\u8017\u6d88\u5728\u5ef6\u8fdf\u6b7b\u4ea1\u7684\u65f6\u95f4\u4e0a\uff0c\u4e5f\u8ba9\u5bf9\u65b9\u83ab\u6d4b\u9ad8\u6df1\uff0c\u4e0d\u6562\u660e\u76ee\u5f20\u80c6\u7684\u80e1\u4f5c\u975e\u4e3a\uff0c\u624d\u52c9\u5f3a\u7ef4\u6301\u4e86\u6b66\u6797\u4e00\u6bb5\u5e73\u9759\u5c81\u6708\u3002\u201d \u3000\u3000\u201c\u81f3\u4e8e\u4ed6\u4eec\u4e0d\u80af\u76f4\u63a5\u544a\u8bc9\u4f60\u9996\u6076\u662f\u8c01\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u9053\uff1a\u201c\u6211\u60f3\u662f\u6015\u4f60\u6c89\u4e0d\u4f4f\u6c14\uff0c\u76f4\u63a5\u627e\u4e0a\u95e8\u53bb\uff0c\u901e\u4e00\u65f6\u8c6a\u58ee\u4e4b\u6c14\uff0c\u8bef\u4e86\u5927\u4e8b\uff0c\u90a3\u4e0d\u4f46\u6388\u654c\u4ee5\u53ef\u4e58\u4e4b\u673a\uff0c\u4e5f\u5c06\u63ed\u7a7f\u4e86\u4ed6\u4eec\u53d7\u4f24\u4e4b\u79d8\uff0c\u6709\u4e9b\u4e8b\u5fc5\u987b\u8981\u4e00\u6bb5\u8270\u8f9b\u7684\u8fc7\u7a0b\uff0c\u624d\u80fd\u6dec\u7ec3\u51fa\u950b\u8292\uff0c\u51dd\u805a\u6210\u529b\u91cf\u3002\u201d \u3000\u3000\u6c5f\u67ab\u4e00\u62b1\u62f3\uff0c\u9053\uff1a\u201c\u6211\u5e94\u8be5\u53eb\u4f60\u4e00\u58f0\u5927\u5e08\u5144\u4e86\uff0c\u591a\u627f\u6307\u6559\u554a\uff01\u201d \u3000\u3000\u201c\u90a3\u5012\u662f\u4e0d\u6562\u5f53\u4e86\uff0c\u65e0\u76f8\u5927\u5e08\u5144\u4f20\u4e86\u6211\u534a\u5e74\u6b66\u529f\uff0c\u4f46\u5374\u672a\u51c6\u6211\u5217\u5165\u95e8\u5899\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u7b11\u9053\uff1a\u201c\u4f60\u624d\u662f\u6b63\u7edf\u4e3b\u5e05\uff0c\u6211\u548c\u5c1a\u53ea\u662f\u606d\u5019\u5dee\u9063\u7684\u90e8\u5c06\uff0c\u8001\u5f1f\uff0c\u522b\u8981\u5984\u81ea\u83f2\u8584\uff0c\u6d88\u78e8\u4e86\u4f60\u7684\u8c6a\u58ee\u6c14\u52bf\u3002\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u80e1\u840d\u63a2\u9996\u5411\u623f\u4e2d\u770b\u4e86\u4e00\u773c\uff0c\u9053\uff1a\u201c\u4e5d\u5934\u4e4c\u5201\u9e4f\u6c42\u89c1\uff01\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u4eba\u5927\u6709\u7528\u5904\u2026\u2026\u201d\u6c5f\u67ab\u70b9\u70b9\u5934\uff0c\u7b11\u9053\uff1a\u201c\u6211\u5728\u5c0f\u5ba2\u5385\u89c1\u4ed6\u3002\u201d \u3000\u3000\u53ea\u662f\u4e00\u5ea7\u5c0f\u5c0f\u5385\u5802\uff0c\u9664\u4e86\u4e00\u5f20\u8336\u51e0\uff0c\u56db\u5f20\u6728\u6905\u4e4b\u5916\uff0c\u522b\u65e0\u9648\u8bbe\u3002 \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u5b88\u5728\u5385\u95e8\u53e3\uff0c\u5201\u9e4f\u8fdb\u5165\u4e86\u5c0f\u5385\u4e4b\u540e\uff0c\u548c\u5c1a\u5c31\u7acb\u523b\u5e26\u4e0a\u623f\u95e8\u3002 \u3000\u3000\u6c5f\u67ab\u65e9\u5df2\u5728\u5385\u4e2d\u7b49\u5019\uff0c\u4eb2\u81ea\u5012\u4e86\u4e00\u676f\u8336\uff0c\u9053\uff1a\u201c\u5201\u5144\uff0c\u8bf7\u7528\u8336\u3002\u201d \u3000\u3000\u5201\u9e4f\u63a5\u8fc7\u8336\u676f\uff0c\u56db\u4e0b\u6253\u91cf\u4e86\u4e00\u773c\uff0c\u53f9\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8fd9\u4e48\u5c0f\u4e00\u4e2a\u5730\u65b9\uff0c\u53ef\u771f\u662f\u59d4\u5c48\u97e9\u5144\u4e86\uff0c\u9093\u98de\u8fd9\u8001\u5c0f\u5b50\uff0c\u771f\u662f\u5c0f\u5bb6\u5b50\u6c14\u2026\u2026\u201d \u3000\u3000\u201c\u6ca1\u6709\u529e\u6cd5\u554a\uff01\u8fd9\u91cc\u662f\u987e\u5ba2\u7b2c\u2014\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u81ea\u5df1\u4eba\u5c31\u53ea\u597d\u59d4\u5c48\u4e00\u4e0b\uff0c\u80fd\u6709\u4e2a\u5b89\u8eab\u7684\u5730\u65b9\uff0c\u4e5f\u5c31\u884c\u4e86\uff01\u201d \u3000\u3000\u201c\u9093\u98de\u89c1\u8fc7\u97e9\u5144\u4e86\u2026\u2026\u201d\u5201\u9e4f\u9053\uff1a\u201c\u5144\u5f1f\u672c\u8981\u8bf7\u547d\u6765\u89c1\u97e9\u5144\uff0c\u5374\u88ab\u9093\u98de\u62a2\u7740\u4e86\u5148\u97ad\u2026\u2026\u201d \u3000\u3000\u201c\u9093\u603b\u7ba1\u5230\u6b64\u65f6\uff0c\u5144\u5f1f\u6b63\u5728\u8c03\u606f\uff0c\u5f7c\u6b64\u6ca1\u6709\u89c1\u9762\u2026\u2026\u201d \u3000\u3000\u201c\u597d\u6781\u4e86\uff01\u6765\u7684\u65e9\uff0c\u4e0d\u5982\u6765\u7684\u5de7\u554a\uff01\u201d\u5201\u9e4f\u7b11\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u7f18\u5206\uff0c\u97e9\u5144\uff0c\u5144\u5f1f\u5df2\u7ecf\u5411\u5929\u865a\u5b50\u526f\u603b\u5802\u4e3b\u63d0\u8fc7\u4e86\uff0c\u6d1b\u9633\u5c45\u526f\u603b\u7ba1\u8fd9\u4e2a\u804c\u4f4d\uff0c\u5b9e\u5728\u592a\u59d4\u5c48\u4f60\u4e86\uff0c\u5e94\u8be5\u53e6\u4f5c\u5b89\u6392\u3002\u201d \u3000\u3000\u201c\u5929\u865a\u5b50\u7684\u610f\u89c1\u5462\uff1f\u201d\u6c5f\u67ab\u76f4\u547c\u5929\u865a\u5b50\uff0c\u672a\u518d\u52a0\u526f\u603b\u5802\u4e3b\u56db\u4e2a\u5b57\uff0c\u90a3\u79cd\u50b2\u7136\uff0c\u51b7\u6f20\u7684\u53e3\u6c14\uff0c\u4f3c\u4e4e\u5e76\u672a\u628a\u5929\u865a\u5b50\u6446\u5728\u5fc3\u4e0a\u3002 \u3000\u3000\u201c\u5728\u7b49\u4f60\u97e9\u5144\u7684\u51b3\u5b9a\u2026\u2026\u201d\u5201\u9e4f\u9053\uff1a\u201c\u770b\u6837\u5b50\u4ed6\u4f1a\u5c0a\u91cd\u97e9\u5144\u7684\u5fd7\u613f\u3002\u201d \u3000\u3000\u201c\u5929\u865a\u5b50\u5728\u5929\u9a6c\u603b\u5802\u6709\u591a\u5927\u4efd\u91cf\uff1f\u201d\u6c5f\u67ab\u8bf4\uff0c\u201c\u603b\u5802\u4e3b\u4e4b\u4e0b\uff0c\u6709\u51e0\u4e2a\u526f\u603b\u5802\u4e3b\uff1f\u90a3\u4e00\u4e2a\u6700\u6709\u6743\u52bf\uff1f\u201d \u3000\u3000\u5201\u9e4f\u6c89\u541f\u4e86\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u603b\u5802\u4e3b\u4e4b\u4e0b\uff0c\u6709\u56db\u4f4d\u526f\u603b\u5802\u4e3b\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u63a5\u9053\uff1a\u201c\u4e00\u638c\u8ffd\u9b42\u4e07\u65b9\uff0c\u8fd8\u6709\u5929\u865a\u5b50\u8fd9\u4f4d\u9053\u957f\uff0c\u6211\u5df2\u7ecf\u89c1\u8fc7\u4e24\u4e2a\u4e86\uff0c\u8fd8\u6709\u4e24\u4f4d\u662f\u2026\u2026\u201d \u3000\u3000\u201c\u5929\u9a6c\u884c\u7a7a\u53f8\u5f92\u827a\u2026\u2026\u201d\u5201\u9e4f\u9053\uff1a\u201c\u6b64\u4eba\u9a6f\u9a6c\u4e4b\u80fd\uff0c\u5929\u4e0b\u65e0\u53cc\uff0c\u5929\u9a6c\u6e21\u5371\u7684\u5929\u9a6c\uff0c\u90fd\u7531\u4ed6\u9a6f\u7ec3\u800c\u6210\u2026\u2026\u201d \u3000\u3000\u201c\u8fd8\u6709\u4f4d\u5462\uff1f\u201d \u3000\u3000\u201c\u91d1\u51e4\u51f0\u7533\u96ea\u541b\uff0c\u90a3\u662f\u4e00\u4f4d\u5973\u7684\u4e86\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u63d0\u51fa\u5fc3\u4e2d\u7684\u7591\u95ee\u3002 \u3000\u3000\u201c\u4e0d\u9519\uff0c\u662f\u4e00\u4f4d\u5dfe\u5e3c\u82f1\u8c6a\u2026\u2026\u201d\u5201\u9e4f\u9053\uff1a\u201c\u97e9\u5144\u5fc3\u4e2d\u6709\u4f55\u6253\u7b97\uff1f\u5e0c\u671b\u80fd\u544a\u8bc9\u5201\u67d0\uff0c\u5201\u67d0\u5f53\u5c3d\u6211\u4e4b\u80fd\uff0c\u8bf4\u52a8\u5929\u865a\u5b50\u526f\u603b\u5802\u4e3b\uff0c\u4ee5\u5b8c\u6210\u97e9\u5144\u7684\u5fc3\u613f\uff01\u201d \u3000\u3000\u201c\u591a\u8c22\u5201\u5144\u7684\u5173\u987e\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u53ea\u662f\u97e9\u67d0\u4eba\u5df2\u4e60\u60ef\u4e8e\u6d1b\u9633\u5c45\u7684\u751f\u6d3b\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u5c1a\u65e0\u79bb\u6b64\u7684\u6253\u7b97\uff01\u201d \u3000\u3000\u201c\u597d\u5427\uff01\u9093\u98de\u5df2\u6709\u8f9e\u53bb\u603b\u7ba1\uff0c\u8ba9\u7531\u97e9\u5144\u63a5\u638c\u4e4b\u610f\uff0c\u5201\u67d0\u8fd9\u5c31\u53bb\u8bf4\u670d\u526f\u603b\u5802\u4e3b\uff0c\u8981\u4ed6\u4e0b\u4ee4\u8d23\u6210\u9093\u98de\u4ea4\u51fa\u603b\u7ba1\u7684\u804c\u4f4d\u2026\u2026\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u4f7f\u4e0d\u5f97\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u9093\u603b\u7ba1\u6709\u63d0\u643a\u97e9\u67d0\u4e4b\u60c5\uff0c\u6211\u600e\u80fd\u5fd8\u6069\u8d1f\u4e49\uff0c\u8c0b\u593a\u4ed6\u603b\u7ba1\u4e4b\u4f4d\uff0c\u5012\u4e0d\u5982\u4e00\u5207\u7167\u5e38\u3002 \u3000\u3000\u97e9\u67d0\u552f\u7684\u4e00\u5e0c\u671b\u662f\u603b\u5802\u80fd\u6388\u9093\u603b\u7ba1\u8f83\u5927\u7684\u6743\u529b\uff0c\u5141\u8bb8\u4ed6\u6269\u5c55\u529b\u91cf\uff0c\u4ee5\u4fbf\u80fd\u548c\u5185\u5bab\u4e00\u7cfb\u4e2d\u4eba\uff0c\u957f\u671f\u5bf9\u6297\u2026\u2026\u201d \u3000\u3000\u201c\u7ecf\u8fc7\u4e86\u6b64\u756a\u632b\u6298\uff0c\u6211\u770b\u5185\u5bab\u4e00\u7cfb\u4e2d\u4eba\uff0c\u4e5f\u8be5\u6536\u655b\u4e00\u4e0b\u4e86\uff0c\u2026\u2026\u201d\u5201\u9e4f\u8bf4\uff1a\u201c\u77ed\u671f\u4e4b\u5185\uff0c\u4ed6\u4eec\u4e5f\u8bb8\u4e0d\u4f1a\u518d\u6709\u884c\u52a8\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e2a\uff0c\u97e9\u67d0\u7684\u770b\u6cd5\u4e0d\u540c\u2026\u2026\u201d\u6c5f\u67ab\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u4eec\u4fa5\u5e78\u80dc\u4e86\u4e24\u9635\uff0c\u5185\u5bab\u5bf9\u6b64\u4e8b\uff0c\u5fc5\u7136\u6000\u6068\u751a\u6df1\uff0c\u6d1b\u9633\u5c45\u592a\u8d5a\u94b1\u4e86\uff0c\u4ed6\u4eec\u4e0d\u4f1a\u653e\u5f03\u8fd9\u5757\u80a5\u8089\uff0c\u5982\u679c\u5185\u5bab\u4e00\u7cfb\u4eba\u9a6c\u518d\u4e00\u6b21\u53d1\u52a8\u653b\u52bf\uff0c\u5fc5\u5c06\u662f\u8c0b\u5b9a\u800c\u540e\u52a8\uff0c \u3000\u3000\u4e00\u65e6\u53d1\u52a8\uff0c\u5c06\u662f\u60ca\u5929\u52a8\u5730\u7684\u51f6\u731b\u653b\u52bf\uff0c\u5929\u9a6c\u5802\u5982\u4e0d\u80fd\u5728\u6d1b\u9633\u540e\u5e03\u7f72\u4e0b\u5f3a\u5927\u5b9e\u529b\uff0c\u52bf\u5fc5\u88ab\u4eba\u5168\u9762\u6251\u6740\uff0c\u90a3\u65f6\u518d\u60f3\u593a\u56de\u6765\uff0c\u53ea\u6015\u662f\u56f0\u96be\u91cd\u91cd\u4e86\u3002\u201d \u3000\u3000\u5201\u9e4f\u70b9\u7740\u5934\uff0c\u9053\uff1a\u201c\u9ad8\u89c1\u5440\uff01\u9ad8\u89c1\uff0c\u6211\u5c31\u6ca1\u6709\u60f3\u5230\u5982\u6b64\u6df1\u8fdc\uff0c\u6211\u7acb\u523b\u4f1a\u89c1\u526f\u603b\u5802\u4e3b\uff0c\u8f6c\u8fbe\u97e9\u5144\u7684\u770b\u6cd5\uff0c\u65e9\u4f5c\u5e03\u7f72\u3001\u51c6\u5907\u2026\u2026\u201d \u3000\u3000\u4ed6\u8bf4\u8d70\u5c31\u8d70\uff0c\u771f\u7684\u7ad9\u8d77\u4e86\u8eab\u5b50\u3002 \u3000\u3000\u201c\u6162\u4e9b\uff0c\u6162\u4e9b\u2026\u2026\u201d\u6c5f\u67ab\u4f38\u624b\u62e6\u4f4f\u4e86\u4e60\u9e4f\uff0c\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\uff0c\u5929\u865a\u5b50\u80fd\u591f\u4f5c\u5f97\u4e86\u4e3b\u5417\uff1f\u201d \u3000\u3000\u5201\u9e4f\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u6211\u4eec\u6765\u65f6\uff0c\u4e5f\u5e26\u4e86\u4e00\u6279\u4eba\u624b\uff0c\u6709\u56db\u4f4d\u62a4\u6cd5\uff0c\u4e24\u4f4d\u957f\u8001\uff0c\u8fd8\u6709\u5341\u51e0\u4f4d\u968f\u884c\u7684\u9999\u4e3b\uff0c\u4f46\u4ed6\u4eec\u90fd\u6682\u5bbf\u5728\u6d1b\u9633\u5c45\u5916\uff0c\u5f85\u547d\u884c\u52a8\uff0c\u97e9\u5144\u6280\u9547\u73b0\u573a\uff0c\u4e5f\u5c31\u514d\u53bb\u4e86\u4e00\u573a\u640f\u6740\u3002 \u3000\u3000\u8bba\u5b9e\u529b\uff0c\u6211\u4eec\u8fd9\u4e00\u884c\u4eba\uff0c\u7b97\u76f8\u5f53\u4e0d\u9519\uff0c\u4f46\u5982\u5185\u5bab\u4e00\u7cfb\u503e\u5de2\u800c\u6765\uff0c\u6216\u662f\u96c6\u4e2d\u4e86\u5927\u90e8\u5206\u529b\u91cf\u65f6\uff0c\u5c31\u4e0d\u662f\u6211\u4eec\u80fd\u654c\u4e86\uff0c\u6240\u4ee5\uff0c\u8fd9\u4ef6\u4e8b\uff0c\u6050\u6015\u8fd8\u5f97\u7531\u603b\u5802\u4f5c\u4e3b\uff0c\u8fd9\u662f\u5927\u52a8\u5e72\u6208\u7684\u4e8b\uff0c\u5929\u865a\u5b50\u53ea\u6015\u8fd8\u4e0d\u6562\u903e\u8d8a\uff1f\u201d \u3000\u3000\u201c\u8fd8\u6709\u4ef6\u4e8b\uff0c\u6211\u4e00\u76f4\u60f3\u4e0d\u5230\uff0c\u8bf7\u6559\u4e60\u5144\uff1f\u201d \u3000\u3000\u201c\u53ea\u7ba1\u5429\u5490\uff0c\u5144\u5f1f\u4e0d\u80fd\u56de\u7b54\u7684\uff0c\u4e5f\u5fc5\u8f6c\u544a\u4e0a\u53bb\uff0c\u5c3d\u91cf\u7ed9\u97e9\u5144\u4e00\u4e2a\u7b54\u590d\u3002\u201d \u3000\u3000\u201c\u4eca\u65e5\u4e4b\u5c40\uff0c\u54b1\u4eec\u5e94\u8be5\u7b97\u662f\u5927\u83b7\u5168\u80dc\uff0c\u97e9\u67d0\u4eba\u79bb\u5f00\u4e4b\u540e\uff0c\u5929\u865a\u5b50\u4e3a\u4ec0\u4e48\u4e0d\u4e0b\u4ee4\uff0c\u4e00\u4e3e\u5c3d\u5c60\u5185\u5bab\u4e2d\u4eba\uff0c\u72ec\u9738\u4e0b\u6d1b\u9633\u5c45\u5462\uff1f\u8fd9\u5e94\u8be5\u662f\u4e2a\u5f88\u597d\u7684\u673a\u4f1a\u5440\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u673a\u4f1a\u662f\u5343\u8f7d\u96be\u9022\uff0c\u53ea\u53ef\u60dc\u5929\u865a\u5b50\u526f\u603b\u5802\u4e3b\uff0c\u5fc3\u4e2d\u6709\u4e9b\u987e\u8651\u3002\u201d\u5201\u9e4f\u5927\u7ea6\u662f\u53d1\u89c9\u4e86\u97e9\u9738\u8fd8\u6709\u5f88\u591a\u8bdd\u8981\u8bf4\uff0c\u53ea\u597d\u53c8\u8d70\u56de\u539f\u4f4d\u5750\u4e0b\u3002 \u3000\u3000\u6c5f\u67ab\u5fc3\u4e2d\u5fd6\u9053\uff1a\u201c\u4e0d\u77e5\u9053\u8fd9\u5201\u9e4f\u77e5\u6653\u591a\u5c11\u79d8\u5bc6\uff1f\u4f46\u4eca\u5929\u4e00\u5b9a\u8981\u628a\u5b83\u5168\u638f\u51fa\u6765\u3002\u201d \u3000\u3000\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u53e3\u4e2d\u8bf4\u9053\uff1a\u201c\u987e\u8651\uff1f\u8fd9\u5c31\u602a\u4e86\uff0c\u65e2\u6709\u987e\u8651\uff0c\u5e72\u8106\u8ba9\u7ed9\u522b\u4eba\u7b97\u4e86\uff0c\u5929\u9a6c\u5802\u4e2d\u4eba\u5fcd\u4e0b\u4e00\u53e3\u6c14\uff0c\u5c31\u53ef\u4ee5\u5c11\u4e22\u6389\u51e0\u6761\u6027\u547d\u3002\u8fd9\u53ef\u662f\u62ce\u7740\u8111\u888b\u6253\u6c5f\u5c71\uff0c\u4e0d\u662f\u62ff\u4eba\u547d\u5f00\u73a9\u7b11\u554a\uff01\u201d \u3000\u3000\u201c\u5509\uff01\u97e9\u5144\uff0c\u8fd9\u4e2d\u95f4\u6709\u4e2a\u5f88\u5927\u7684\u539f\u56e0\uff0c\u8fd1\u4e24\u5e74\u6765\uff0c\u5185\u5bab\u4e00\u7cfb\u4e2d\u4eba\uff0c\u5904\u5904\u5728\u548c\u5929\u9a6c\u5802\u4e2d\u4eba\u4e89\u593a\u6743\u52bf\uff0c\u63a7\u5236\u5730\u76d8\u3002 \u3000\u3000\u5929\u9a6c\u5802\u4e5f\u4e00\u76f4\u662f\u5230\u5904\u9000\u8ba9\uff0c\u4ee5\u907f\u514d\u9020\u6210\u4e86\u5168\u9762\u7684\u5927\u51b2\u7a81\uff0c\u8fd9\u4e00\u6b21\u6d1b\u9633\u5c45\u7684\u4e8b\u4ef6\uff0c\u662f\u5929\u9a6c\u5802\u771f\u6b63\u5f00\u59cb\u53cd\u6297\u7684\u4e00\u6b21\uff01\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u5462\uff1f\u592d\u9a6c\u603b\u5802\u4e2d\u7684\u5b9e\u529b\u4e0d\u5982\u5185\u5bab\uff0c\u662f\u5417\uff1f\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u8fd8\u662f\u522b\u6709\u987e\u5fcc\uff0c\u4e0d\u6562\u653e\u624b\u4e00\u6218\uff1f\u201d \u3000\u3000\u201c\u6211\u4e0d\u77e5\u9053\u5185\u5bab\u4e00\u7cfb\u4e2d\u7684\u5b9e\u529b\u5982\u4f55\uff1f\u4f46\u5929\u9a6c\u603b\u5802\u5374\u662f\u805a\u96c6\u4e86\u8fd1\u767e\u5e74\u6765\uff0c\u6c5f\u6e56\u4e2d\u7684\u7cbe\u82f1\u4eba\u7269\uff0c\u4e09\u5341\u516d\u4f4d\u957f\u8001\uff0c\u4e03\u5341\u4e8c\u4e2a\u62a4\u6cd5\uff0c\u548c\u6570\u767e\u4f4d\u9999\u4e3b\uff0c\u5343\u540d\u4ee5\u4e0a\u7684\u9a91\u58eb\uff0c\u2026\u2026\u201d\u5201\u9e4f\u7565\u4e00\u601d\u7d22\uff0c\u63a5\u9053\uff1a\u201c\u8fd9\u4e9b\u4eba\u7684\u5b9e\u529b\uff0c\u5df2\u8d85\u4e2d\u539f\u6b66\u6797\u4efb\u4f55\u4e00\u4e2a\u95e8\u6d3e\uff0c\u4f46\u4ecd\u4e0d\u662f\u5929\u9a6c\u5802\u771f\u6b63\u7684\u5b9e\u529b\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u5fc3\u5934\u4e00\u9707\uff0c\u5fd6\u9053\uff1a\u201c\u679c\u7136\uff0c\u6c5f\u6e56\u4e0a\u7684\u5927\u5978\u5de8\u6076\uff0c\u5c3d\u96c6\u5728\u5929\u9a6c\u603b\u5802\uff0c\u5f53\u771f\u662f\u7fa4\u9b54\u4e71\u821e\uff0c\u5996\u6c1b\u5f25\u5929\u2026\u2026\u201d \u3000\u3000\u201c\u771f\u6b63\u7684\u5b9e\u529b\uff0c\u662f\u603b\u5802\u4e3b\u548c\u56db\u4f4d\u526f\u603b\u5802\u4e3b\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u5bf9\u4ed6\u4eec\u4e94\u4f4d\uff0c\u518d\u52a0\u4e0a\u798f\u5bff\u9662\u4e2d\u7684\u516d\u5927\u697c\u4e3b\uff0c\u624d\u662f\u5929\u9a6c\u603b\u5802\u7684\u771f\u6b63\u5b9e\u529b\u2026\u2026\u201d \u3000\u3000\u6015\u5f15\u8d77\u4e5d\u5934\u9e1f\u7684\u7591\u5fc3\uff0c\u6c5f\u67ab\u672a\u8ffd\u95ee\u516d\u5927\u697c\u4e3b\u7684\u59d3\u540d\u3001\u8eab\u4efd\uff0c\u5374\u6539\u53e3\u8bf4\u9053\uff1a\u201c\u65e2\u6709\u5982\u6b64\u5f3a\u5927\u7684\u5b9e\u529b\uff0c\u4e3a\u4ec0\u4e48\u8fd8\u8981\u5fcd\u8ba9\uff1f\u201d \u3000\u3000\u201c\u5929\u9a6c\u5802\u3001\u5185\u5bab\u3001\u5929\u738b\u95e8\uff0c\u672c\u5c31\u662f\u4e00\u4e2a\u7ec4\u5408\uff0c\u2026\u2026\u201d\u5201\u9e4f\u9053\uff1a\u201c\u5929\u9a6c\u5802\u4ee5\u805a\u96c6\u7684\u6c5f\u6e56\u4eba\u7269\u4e3a\u4e3b\uff0c\u5929\u738b\u95e8\u548c\u5185\u5bab\u5374\u662f\u8bad\u7ec3\u7684\u65b0\u4eba\u638c\u6743\uff0c\u5927\u5bb6\u5404\u81ea\u53d1\u5c55\u52bf\u529b\uff0c\u5c31\u5f15\u8d77\u8fd9\u4e9b\u65e0\u6cd5\u907f\u514d\u7684\u51b2\u7a81\u4e86\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u7531\u9752\u51e4\u53e3\u4e2d\u5f97\u5230\u5929\u738b\u95e8\u7684\u4e00\u7cfb\u7684\u8baf\u606f\uff0c\u6bd4\u4e5d\u5934\u9e1f\u8fd8\u8981\u4e30\u5bcc\uff0c\u6240\u4ee5\uff0c\u4e00\u8f6c\u8bdd\u9898\uff0c\u63a5\u9053\uff1a\u201c\u770b\u6765\uff0c\u6211\u4eec\u8fd9\u4e2a\u7ec4\u5408\u5341\u5206\u590d\u6742\uff0c\u8fd8\u4f4d\u5b8c\u6210\u6b66\u6797\u4e00\u7edf\u7684\u9738\u4e1a\uff0c\u81ea\u5df1\u5c31\u7a9d\u91cc\u53cd\uff0c\u76f8\u4e92\u6b8b\u6740\u8d77\u6765\uff1f\u201d \u3000\u3000\u201c\u8fd9\u65f6\u6ca1\u6cd5\u5b50\u7684\u4e8b\u2026\u2026\u201d\u5201\u9e4f\u65e0\u9650\u611f\u6168\u5730\u8bf4\uff1a\u201c\u53cc\u96c4\u4e0d\u5e76\u7acb\uff0c\u4f55\u51b5\u4e09\u8db3\u9f0e\uff0c\u4e5f\u8bb8\u662f\u56db\u96c4\u4e89\u9738\u3001\u4e94\u5f3a\u76f8\u6b8b\u2026\u2026\u201d \u3000\u3000\u201c\u56db\u96c4\u3001\u4e94\u5f3a\u3001\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u5201\u5144\uff0c\u53c8\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d \u3000\u3000\u201c\u5201\u67d0\u4eba\u6b66\u529f\uff0c\u7b97\u4e0d\u4e0a\u6c5f\u6e56\u4e2d\u9876\u5c16\u9ad8\u624b\uff0c\u4f46\u6211\u81ea\u4fe1\u7684\u89c2\u5bdf\u529b\uff0c\u6709\u7740\u8fc7\u4eba\u7684\u654f\u9510\uff0c\u6211\u611f\u89c9\u5230\u6709\u4e00\u80a1\u9690\u5728\u6697\u4e2d\u7684\u529b\u91cf\uff0c\u64cd\u7eb5\u7740\u8fd9\u4e2a\u7ec4\u5408\u2026\u2026\u201d \u3000\u3000\u201c\u8fd9\u5012\u6709\u7406\u2026\u2026\u201d\u6c5f\u67ab\u8bf4\uff0c\u201c\u4e5f\u8bb8\u5728\u5929\u9a6c\u603b\u5802\u3001\u5929\u738b\u95e8\uff0c\u5185\u5bab\u7cfb\u7edf\u4e4b\u4e0a\uff0c\u8fd8\u6709\u4e2a\u771f\u6b63\u7684\u9996\u8111\u4eba\u7269\u2026\u2026\uff0c\u53ef\u662f\u8fd9\u4e5f\u53ea\u6709\u56db\u96c4\u554a\uff01 \u3000\u3000\u90a3\u91cc\u6765\u7684\u4e94\u5f3a\uff1f\u201d \u3000\u3000\u201c\u6709\uff01\u53e6\u4e00\u80a1\u9690\u85cf\u5728\u6697\u4e2d\u7684\u529b\u91cf\uff0c\u4e00\u76f4\u5728\u5bfc\u5f15\u7740\u5929\u9a6c\u603b\u5802\u548c\u5185\u5bab\u4e2d\u4eba\u7684\u51b2\u7a81\u2026\u2026\u201d\u4e5d\u5934\u9e1f\u8bf4\uff0c\u201c\u90a3\u662f\u4e00\u6839\u770b\u4e0d\u5230\u7684\u7d22\u7ef3\uff0c\u5374\u628a\u5929\u9a6c\u603b\u5802\u548c\u5185\u5bab\u8fde\u63a5\u5728\u4e00\u8d77\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u5fc3\u4e2d\u4e00\u52a8\uff0c\u5fd6\u9053\uff1a\u201c\u6211\u52a0\u5165\u8fd9\u573a\u6df7\u6218\uff0c\u53ea\u662f\u8fd1\u534a\u6708\u7684\u4e8b\uff0c\u96be\u9053\u53e6\u5916\u8fd8\u6709\u4eba\u5728\u6697\u4e2d\u5bfc\u5f15\u4e0d\u6210\u2026\u2026\u3002\u201d \u3000\u3000\u5fc3\u4e2d\u7591\u8651\u5927\u8d77\uff0c\u63a5\u9053\uff1a\u201c\u5201\u5144\uff0c\u51e0\u65f6\u53d1\u73b0\u4e86\u8fd9\u4ef6\u4e8b\u60c5\uff1f\u201d \u3000\u3000\u201c\u5509\uff01\u5e94\u8be5\u6709\u534a\u5e74\u4ee5\u4e0a\u4e86\u2026\u2026\u201d\u5201\u9e4f\u9053\uff1a\u201c\u53ea\u662f\u627e\u4e0d\u51fa\u4e00\u70b9\u75d5\u8ff9\u3002\u201d \u3000\u3000\u6c5f\u67ab\u5fc3\u5934\u4e00\u677e\uff0c\u9053\uff1a\u201c\u5201\u5144\uff0c\u4f60\u8fd9\u4e48\u6025\u6025\u7684\u6765\u89c1\u6211\uff0c\u662f\u4e0d\u662f\u60f3\u8981\u6211\u89e3\u53bb\u4f60\u53d7\u5236\u7684\u7a74\u9053\uff1f\u201d \u3000\u3000\u201c\u4eba\u4e0d\u81ea\u79c1\uff0c\u5929\u8bdb\u5730\u706d\uff0c\u2026\u2026\u201d\u5201\u9e4f\u9053\uff1a\u201c\u6c42\u89e3\u4f24\u7a74\uff0c\u662f\u6211\u5fc3\u613f\u4e4b\u4e00\u2026\u2026\u201d \u3000\u3000\u201c\u5662\uff01\u4f60\u8fd8\u6709\u7b2c\u4e8c\u4e2a\u5fc3\u613f\u4e86\uff0c\u8bf4\u51fa\u6765\u542c\u542c\u5427\uff1f\u201d \u3000\u3000\u201c\u4ea4\u4f60\u97e9\u5144\u8fd9\u4e2a\u670b\u53cb\u2026\u2026\u201d\u5201\u9e4f\u9053\uff1a\u201c\u6211\u59d3\u4e60\u4e0d\u662f\u597d\u4eba\uff0c\u4f46\u5374\u6709\u4e00\u7247\u7ed3\u4ea4\u7684\u97e9\u5144\u7684\u8bda\u610f\u3002\u201d \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u597d\u2026\u2026\u201d\u53f3\u638c\u6325\u52a8\uff0c\u4e09\u8d77\u4e09\u843d\uff0c\u62cd\u4e2d\u5201\u9e4f\u4e09\u5904\u7a74\u4f4d\uff0c\u7b11\u9053\uff1a\u201c\u5201\u5144\uff0c\u8bf4\u670d\u5929\u865a\u5b50\u526f\u603b\u5802\u4e3b\uff0c\u8ba9\u6211\u7559\u5728\u6d1b\u9633\u5c45\uff0c\u9093\u7237\u4ecd\u7136\u662f\u603b\u7ba1\u8eab\u4efd\uff0c\u6211\u4ee5\u526f\u603b\u7ba1\u7684\u8eab\u4efd\uff0c\u884c\u4e8b\u8fd8\u65b9\u4fbf\u4e00\u4e9b\u3002 \u3000\u3000\u6700\u91cd\u8981\u7684\u662f\uff0c\u5929\u9a6c\u603b\u5802\u8981\u5927\u529b\u652f\u6301\u9093\u603b\u7ba1\uff0c\u97e9\u67d0\u4eba\u613f\u4e3a\u5148\u950b\u548c\u5185\u5bab\u4eba\u9a6c\u4e00\u51b3\u80dc\u8d1f\u2026\u2026\u201d \u3000\u3000\u8fd9\u756a\u8bdd\u50cf\u62dc\u6258\uff0c\u4e5f\u50cf\u547d\u4ee4\uff0c\u4e5d\u5934\u9e1f\u7adf\u7136\u62b1\u62f3\u4e00\u793c\uff0c\u9053\uff1a\u201c\u6211\u5c3d\u529b\u800c\u4e3a\u3002\u201d\u8f6c\u8eab\u5927\u6b65\u800c\u53bb\u3002 \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u63a8\u95e8\u800c\u5165\uff0c\u7b11\u9053\uff1a\u201c\u4e5d\u5934\u9e1f\u4ee5\u8be1\u8bc8\u95fb\u540d\u6c5f\u6e56\uff0c\u8fd9\u4e00\u6b21\uff0c\u4e5f\u7ffb\u4e0d\u51fa\u4f60\u8001\u5f1f\u7684\u624b\u638c\u5fc3\u4e86\uff01\u201d \u3000\u3000\u201c\u6211\u8981\u4ed6\u611f\u89c9\u5230\u4f24\u7a74\u672a\u6108\uff0c\u4f46\u4e5f\u4e0d\u4f1a\u53d1\u4f5c\u2026\u2026\u201d\u6c5f\u67ab\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u50cf\u4e00\u628a\u65e0\u5f62\u7684\u5fc3\u9501\uff0c\u4f7f\u4ed6\u6c38\u8fdc\u5e26\u7740\u4e00\u4efd\u70e6\u607c\u3002\u201d \u3000\u3000\u201c\u4e5f\u53ef\u4ee5\u8bd5\u8bd5\u770b\u5929\u865a\u5b50\u80fd\u4e0d\u80fd\u627e\u51fa\u4ed6\u53d7\u4f24\u7684\u7a74\u9053\uff1f\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u9053\uff1a\u201c\u4f46\u4f60\u5982\u4f55\u5bf9\u4ed8\u9093\u98de\uff0c\u53ef\u5df2\u80f8\u6709\u6210\u7af9\uff1f\u201d \u3000\u3000\u201c\u652f\u6301\u4ed6\uff0c\u8981\u4ed6\u5750\u7a33\u603b\u7ba1\u7684\u4f4d\u7f6e\uff0c\u4e5f\u8981\u4ed6\u6210\u4e3a\u5929\u9a6c\u5802\u4e2d\u91cd\u8981\u7684\u4eba\u7269\u2026\u2026\u201d \u3000\u3000\u201c\u597d\u529e\u6cd5\uff01\u7531\u4ed6\u9876\u7740\u8fd9\u4e2a\u603b\u7ba1\u7684\u4f4d\u7f6e\uff0c\u4f60\u5c31\u53ef\u4ee5\u6765\u53bb\u81ea\u5982\u4e86\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u7b11\u9053\uff1a\u201c\u6211\u4eec\u597d\u597d\u5730\u4fdd\u62a4\u4ed6\uff0c\u8ba9\u4ed6\u58f0\u8a89\u65e5\u9686\u3002\u201d \u3000 \u7b2c \u5341 \u56de\u3000\u96ea\u971c\u5973\u60c5\u5f00\u8bf1\u6c5f\u67ab \u3000 \u3000\u3000\u9093\u98de\u548c\u6c5f\u67ab\u7ecf\u4e00\u756a\u6df1\u8c08\u4e4b\u540e\uff0c\u7b80\u76f4\u611f\u52a8\u7684\u70ed\u6cea\u76c8\u7736\uff0c\u6c5f\u67ab\u4e0d\u4f46\u66ff\u4ed6\u80cc\u4e0a\u4e86\u6740\u6b7b\u738b\u9f99\u7684\u7f6a\u540d\uff0c\u800c\u4e14\uff0c\u8fd8\u5927\u529b\u652f\u6301\u4ed6\u6d1b\u9633\u5c45\u603b\u7ba1\u7684\u804c\u4f4d\uff0c\u6700\u91cd\u8981\u7684\u81ea\u613f\u7559\u4efb\u526f\u603b\u7ba1\uff0c\u4e0d\u4e3a\u4efb\u4f55\u5229\u7984\uff0c\u540d\u4f4d\u6240\u52a8\uff0c\u8a93\u5171\u8fdb\u9000\u3002 \u3000\u3000\u6240\u4ee5\uff0c\u9093\u98de\u5728\u664b\u89c1\u5929\u865a\u5b50\u65f6\uff0c\u7adf\u4e5f\u80fd\u8868\u73b0\u5f97\u4e0d\u4ea2\u4e0d\u5351\u3002 \u3000\u3000\u5929\u865a\u5b50\u65e9\u5df2\u5f97\u77e5\u4e5d\u5934\u9e1f\u4f20\u6765\u7684\u6d88\u606f\uff0c\u8fd9\u8001\u9053\u58eb\u60f3\u4e86\u5f88\u4e45\uff0c\u8fd8\u662f\u60f3\u4e0d\u901a\u3001\u97e9\u9738\u4e3a\u4ec0\u4e48\u4f1a\u5bf9\u9093\u98de\u5982\u6b64\u5fe0\u8bda\uff0c\u62e5\u62a4\u3002 \u3000\u3000\u6240\u4ee5\uff0c\u5728\u63a5\u89c1\u9093\u98de\u65f6\uff0c\u4e00\u76f4\u4e0d\u505c\u5730\u5728\u9093\u98de\u8eab\u4e0a\u6253\u91cf\uff0c\u4f3c\u662f\u8981\u6301\u7a7f\u9093\u98de\u7684\u5185\u8151\u4e94\u810f\uff0c\u53ef\u60dc\uff0c\u770b\u4e86\u5f88\u4e45\uff0c\u4ecd\u7136\uff0c\u627e\u4e0d\u51fa\u4e00\u70b9\u53ef\u4ee5\u4ee4\u4eba\u656c\u670d\u7684\u5730\u65b9\u3002 \u3000\u3000\u4f46\u5374\u770b\u5f97\u9093\u98de\u5fc3\u4e2d\u53d1\u6bdb\uff0c\u5fcd\u4e0d\u4f4f\u9053\uff1a\u201c\u526f\u603b\u5802\u4e3b\u5982\u82e5\u8ba4\u4e3a\u5c5e\u4e0b\u624d\u4e0d\u8db3\u5f53\u6b64\u5927\u4efb\uff0c\u5f53\u7136\u53ef\u4ee5\u8c03\u5c5e\u4e0b\u79bb\u5f00\u8fd9\u4e2a\u804c\u4f4d\uff0c\u53e6\u59d4\u8d24\u80fd\u63a5\u4efb\uff0c\u81f3\u4e8e\u97e9\u9738\u613f\u5426\u7559\u4efb\u526f\u603b\u7ba1\uff0c\u90a3\u5c31\u8bf7\u526f\u603b\u5802\u4e3b\u8e09\u4ed6\u5f53\u9762\u8c08\u4e86\u3002\u201d \u3000\u3000\u201c\u4f60\u5f88\u9002\u4efb\u73b0\u804c\uff3e\u201d\u5929\u865a\u5b50\u7b11\u9053\uff1a\u201c\u56e0\u4e3a\uff0c\u4f60\u6709\u4e00\u4e2a\u5fe0\u8bda\u62e5\u62a4\u4f60\u7684\u526f\u603b\u7ba1\u97e9\u9738\uff0c\u4f55\u51b5\uff0c\u4e07\u526f\u603b\u5802\u4e3b\u4e5f\u5bf9\u4f60\u4e0d\u9519\uff0c\u66fe\u7ecf\u9762\u6258\u6211\u7167\u987e\u4f60\uff0c\u4e0d\u8fc7\uff0c\u5929\u9a6c\u5802\u4e0a\u662f\u5426\u80af\u653e\u5f00\u624b\u548c\u5185\u5bab\u5927\u4e8e\u4e00\u573a\uff0c\u6211\u4e5f\u65e0\u6cd5\u4f5c\u4e3b\uff0c\u6211\u770b\u603b\u5802\u4e3b\u4e5f\u4e0d\u4fbf\u64c5\u81ea\u4f5c\u4e3b\uff0c\u6050\u6015\u8981\u53ec\u96c6\u798f\u5bff\u9662\u4e2d\u516d\u5927\u697c\u4e3b\u7814\u5546\u4e00\u756a\u624d\u4f1a\u6709\u4e2a\u51b3\u5b9a\u3002 \u3000\u3000\u6211\u8fd9\u5c31\u52a8\u8eab\u56de\u53bb\uff0c\u5feb\u5219\u5341\u65e5\uff0c\u8fdf\u5728\u4e00\u4e2a\u6708\u5185\uff0c\u5b9a\u4f1a\u6709\u4e00\u4e2a\u660e\u6717\u7684\u6d88\u606f\uff0c\u4e5f\u8bb8\u6211\u4f1a\u518d\u56de\u6765\uff0c\u8bf4\u4e0d\u5b9a\u8001\u4e07\u4e5f\u8ddf\u7740\u6211\u4e00\u8d77\u6765\uff0c\u4f60\u5c31\u7b49\u5019\u56de\u97f3\u5427\uff01\u8fd9\u6bb5\u65f6\u65e5\u91cc\uff0c\u53ef\u8981\u591a\u591a\u73cd\u91cd\u3002\u2026\u2026\u201d \u3000\u3000\u8fd9\u756a\u8bdd\u987f\u7136\u4f7f\u9093\u98de\u6709\u4e9b\u614c\u4e71\u8d77\u6765\uff0c\u6025\u6025\u8bf4\u9053\uff1a\u201c\u526f\u603b\u5802\u4e3b\uff0c\u6211\u4eec\u4e24\u632b\u5185\u5bab\u4e2d\u4eba\u7684\u9510\u6c14\uff0c\u6740\u6b7b\u4e86\u5bab\u4e3b\u95e8\u4e0b\u4e09\u9f99\u4e4b\u4e8c\uff0c\u8fd9\u4e2a\u4ec7\u6068\u5df2\u7ecf\u7ed3\u5f97\u5f88\u6df1\u4e86\uff0c\u5982\u662f\u603b\u5802\u4e0d\u652f\u6301\u6d1b\u9633\u5c45\uff0c\u9093\u67d0\u8fd9\u4e2a\u603b\u7ba1\uff0c\u90a3\u80fd\u5e72\u7684\u4e0b\u53bb\uff1f\u201d \u3000\u3000\u201c\u6709\u97e9\u9738\u5728\u8fd9\u91cc\uff0c\u6211\u60f3\u53ef\u4ee5\u6682\u65f6\u6491\u5f97\u4f4f\u573a\u9762\uff0c\u2026\u2026\u201d\u5929\u865a\u5b50\u7b11\u9053\uff1a\u201c\u8001\u9053\u53ea\u80fd\u7b54\u5e94\u4f60\uff0c\u5728\u603b\u5802\u672a\u51b3\u5b9a\u662f\u5426\u5bf9\u6297\u5185\u5bab\u4e4b\u524d\uff0c\u4f60\u4eec\u5982\u88ab\u9010\u79bb\u4e86\u6b64\u5730\uff0c\u603b\u5802\u53ef\u4ee5\u4e0d\u8ffd\u7a76\u4f60\u7684\u7f6a\u8d23\u2026\u2026\u201d \u3000\u3000\u201c\u5982\u5185\u5bab\u518d\u6d3e\u9ad8\u624b\u5230\u6b64\uff0c\u6211\u60f3\uff0c\u4ed6\u4eec\u4e0d\u4f1a\u7559\u4e0b\u6d3b\u53e3\u2026\u2026\u201d\u9093\u98de\u9053\uff1a\u201c\u6491\u4e0d\u4f4f\uff0c\u6211\u4eec\u53ea\u5c31\u6709\u6b7b\u8def\u4e00\u6761\uff0c\u5343\u7948\u4e07\u7977\uff0c\u5e0c\u671b\u526f\u603b\u5802\u4e3b\u80fd\u65e9\u4f5c\u51b3\u5b9a\uff0c\u6211\u4eec\u5728\u8fd9\u91cc\u606d\u5019\u4f73\u97f3\u4e86\uff01\u201d \u3000\u3000\u5929\u865a\u5b50\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u9093\u98de\u5440\uff01\u8d70\u9a6c\u884c\u8239\u4e09\u5206\u9669\uff0c\u4f55\u51b5\u5728\u6c5f\u6e56\u4e0a\u6253\u6eda\u7684\u4eba\uff0c\u4f60\u5df2\u6d3b\u4e86\u516d\u5341\u591a\u5c81\uff0c\u6b7b\u800c\u4f55\u61be\uff1f\u201d \u3000\u3000\u9093\u98de\u5fae\u5fae\u4e00\u5446\uff0c\u53e3\u4e2d\u8fde\u5e94\u4e86\u4e24\u4e2a\u662f\u5b57\uff0c\u5fc3\u4e2d\u5374\u6697\u6697\u9a82\u9053\uff1a\u201c\u4f60\u51fa\u8eab\u6b63\u5927\u95e8\u6237\uff0c\u4f46\u4f60\u5fc3\u5730\u7684\u9669\u72e0\u3001\u751f\u6027\u7684\u6b8b\u9177\uff0c\u5c31\u7b97\u4e0b\u4e94\u95e8\u51fa\u8eab\u7684\u7eff\u6797\u4eba\u7269\uff0c\u4e5f\u662f\u671b\u5c18\u83ab\u53ca\u4e86\u3002\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u5750\u5728\u4e00\u4fa7\u7684\u4e5d\u5934\u9e1f\u7a81\u7136\u63a5\u53e3\u8bf4\u9053\uff1a\u201c\u526f\u603b\u5802\u4e3b\uff0c\u4e3a\u4e86\u4f7f\u9093\u5144\u5b89\u5fc3\uff0c\u5c5e\u4e0b\u60f3\u7559\u4e0b\u6765\uff0c\u52a9\u9093\u603b\u7ba1\u4e00\u81c2\u4e4b\u529b\uff01\u201d \u3000\u3000\u201c\u4f60\u8981\u81ea\u613f\u7559\u4e0b\u6765\uff0c\u5c0f\u5201\uff0c\u4f60\u6ca1\u6709\u559d\u9189\u5427\uff1f\u201d\u5929\u864e\u5b50\u770b\u7740\u4e5d\u5934\u9e1f\uff0c\u8138\u4e0a\u662f\u4e00\u80a1\u5927\u611f\u5947\u5f02\u7684\u795e\u8272\u3002 \u3000\u3000\u201c\u5c5e\u4e0b\u6ef4\u9152\u672a\u6cbe\u2026\u2026\u201d\u5201\u9e4f\u9053\uff1a\u201c\u5c5e\u4e0b\u7559\u4e0b\u6765\uff0c\u4e5f\u662f\u4e3a\u526f\u603b\u5802\u4e3b\u6811\u7acb\u5fb7\u5a01\u3002\u201d \u3000\u3000\u4e5d\u5934\u9e1f\u679c\u7136\u4e0d\u51e1\uff0c\u4e00\u8bb0\u9a6c\u5c41\uff0c\u62cd\u5f97\u5929\u865a\u5b50\u602a\u5f02\u7684\u7751\u4e0a\u5ffd\u7136\u7efd\u5f00\u4e86\u7b11\u5bb9\uff0c\u9053\uff1a\u201c\u597d\u5427\uff0c\u4f60\u6709\u4e9b\u8c6a\u6c14\uff0c\u8001\u9053\u5c31\u628a\u5e26\u6765\u7684\u56db\u4f4d\u62a4\u6cd5\uff0c\u5341\u4f4d\u9999\u4e3b\u7559\u4e0b\u6765\uff0c\u7531\u4f60\u6307\u6325\uff0c\u81f3\u4e8e\u9c81\u957f\u8001\u548c\u4f60\u8eab\u4efd\u76f8\u82e5\uff0c\u7559\u4e0b\u6765\uff0c\u5bf9\u4f60\u53cd\u800c\u788d\u624b\u788d\u811a\uff0c\u8001\u9053\u5e26\u4ed6\u56de\u603b\u5802\u53bb\u3002\u201d \u3000\u3000\u201c\u591a\u8c22\u526f\u603b\u5802\u4e3b\u2026\u2026\u201d \u3000\u3000\u5201\u9e4f\u662f\u771f\u7684\u611f\u6fc0\uff0c\u7adf\u7136\u4e00\u64a9\u8863\u895f\uff0c\u8dea\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u9093\u98de\u8ddf\u7740\u4e5f\u77ee\u4e86\u534a\u622a\u3002 \u3000\u3000\u5929\u865a\u5b50\u6325\u6325\u624b\uff0c\u8ba9\u4e24\u4eba\u8d77\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u8001\u5b9e\u8bf4\uff0c\u5bf9\u5185\u5bab\u4e2d\u4eba\u90a3\u4efd\u8dcb\u6248\u6c14\u52bf\uff0c\u6211\u8001\u9053\u4e5f\u662f\u770b\u4e0d\u4e0b\u53bb\uff0c\u4f46\u6211\u4eec\u6740\u4e86\u5185\u5bab\u5bab\u4e3b\u4e24\u4e2a\u5f1f\u5b50\uff0c\u7406\u4e8f\u5728\u6211\uff0c\u661f\u706b\u71ce\u539f\uff0c\u4f55\u51b5\uff0c\u4f60\u4eec\u70e7\u8d77\u7684\u662f\u4e00\u628a\u5927\u706b\uff0c\u95f9\u4e0b\u53bb\uff0c\u53ef\u80fd\u4f1a\u9020\u6210\u4e00\u573a\u5927\u706b\u62fc\uff0c\u6ca1\u6709\u5145\u5206\u7684\u51c6\u5907\u4e4b\u524d\uff0c\u8c01\u4e5f\u4e0d\u6562\u62c5\u8d77\u8fd9\u4e2a\u62c5\u5b50\uff0c\u5c31\u662f\u603b\u5802\u4e3b\u4e5f\u6ca1\u6709\u8fd9\u4e48\u786c\u7684\u80a9\u80db\uff0c\u518d\u8bf4\uff0c\u8001\u9053\u8fd8\u62c5\u5fc3\u53e6\u5916\u4e00\u4ef6\u4e8b\uff0c\u4e0d\u77e5\u662f\u5426\u4f1a\u5f15\u8d77\u53e6\u4e00\u573a\u5927\u98ce\u6ce2\u51fa\u6765\u2026\u2026\u201d \u3000\u3000\u201c\u4ec0\u4e48\u4e8b\uff1f\u2026\u2026\u201d\u9093\u98de\u9053\uff1a\u201c\u4e5f\u548c\u5c5e\u4e0b\u8fd9\u6d1b\u9633\u5c45\u6709\u5173\u5417\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u6709\u5173\u2026\u2026\u201d\u65e0\u865a\u5b50\u9053\uff1a\u201c\u4f60\u8be5\u8bb0\u5f97\uff0c\u97e9\u9738\u6740\u6b7b\u90a3\u4f4d\u7384\u8863\u59d1\u5a18\u5427\uff1f\u5979\u5df2\u8bf4\u660e\u4e86\u4e0d\u662f\u5185\u5bab\u4e00\u7cfb\u4e2d\u4eba\uff0c\u4f46\u97e9\u9738\u4ecd\u4e00\u638c\u8981\u4e86\u5979\u7684\u547d\uff0c\u8fd9\u4e00\u638c\u5a01\u9547\u5168\u573a\uff0c\u6211\u8001\u9053\u4e5f\u81ea\u53f9\u5f17\u5982\uff0c\u53ef\u662f\uff0c\u4e3a\u4ec0\u4e48\u5462\uff1f\u4e00\u5b9a\u8981\u6740\u6b7b\u5979\uff1f\u5982\u679c\uff0c\u5979\u662f\u53e6\u4e00\u7cfb\u4e2d\u7684\u4eba\uff0c\u54b1\u4eec\u5c82\u4e0d\u662f\u591a\u7ed3\u4e86\u4e00\u4e2a\u5f3a\u654c\uff1f\u201d \u3000\u3000\u4ed6\u6ca1\u6709\u8bf4\u7684\u5f88\u660e\u767d\uff0c\u4f46\u9093\u98de\u548c\u4e60\u9e4f\u90fd\u542c\u5f97\u5fc3\u4e2d\u6709\u6570\uff0c\u800c\u8bdd\u4e2d\u6709\u8bdd\uff0c\u5bf9\u97e9\u9738\u5df2\u52a8\u4e86\u4e00\u4e9b\u7591\u5ff5\u3002 \u3000\u3000\u4ed6\u6027\u683c\u9634\u6bd2\uff0c\u8868\u9762\u4e0a\uff0c\u4e8b\u4e8b\u5fae\u6c42\u522b\u4eba\u610f\u89c1\uff0c\u5176\u5b9e\u5fc3\u4e2d\u65e9\u6709\u5b9a\u89c4\uff0c\u53c8\u4f1a\u968f\u673a\u5e94\u53d8\uff0c\u5bf9\u4eba\u523b\u8584\u5be1\u60c5\uff0c\u4f46\u5bf9\u4e8b\u7269\u7684\u89c2\u5bdf\uff0c\u5374\u53c8\u6d1e\u89c1\u7ec6\u5fae\uff0c\u5165\u9aa8\u4e09\u5206\uff0c\u5f53\u771f\u662f\u8001\u8c0b\u6df1\u7b97\uff0c\u5978\u6ed1\u65e0\u6bd4\u3002 \u3000\u3000\u9093\u98de\u9053\uff1a\u201c\u526f\u603b\u5802\u4e3b\u53ef\u662f\u6000\u7591\u97e9\u9738\u6545\u610f\u5728\u717d\u98ce\u70b9\u706b\uff0c\u5f15\u8d77\u7eb7\u4e89\uff1f\u201d \u3000\u3000\u201c\u6211\u6ca1\u6709\u8fd9\u6837\u8bf4\u5440\uff01\u2026\u2026\u201d\u5929\u865a\u5b50\u9053\uff1a\u201c\u4e5f\u8bb8\u4ed6\u5e74\u7eaa\u4e0d\u662f\u592a\u5927\uff0c\u597d\u80dc\u4e4b\u5fc3\u8fd8\u5f3a\uff0c\u60f3\u5728\u8001\u9053\u9762\u524d\u9732\u4e00\u624b\uff0c\u8ba9\u6211\u77bb\u4ef0\u77bb\u4ef0\u2026\u2026\u201d \u3000\u3000\u201c\u526f\u603b\u5802\u4e3b\u662f\u4e00\u8bed\u4e2d\u7684\u3002\u2026\u2026\u201d\u4e5d\u5934\u9e1f\u9053\uff1a\u201c\u97e9\u9738\u5982\u662f\u4e2a\u5e73\u5eb8\u4e4b\uff0a\uff0c\u65e9\u5c31\u88ab\u9ec4\u9f99\u6d3b\u6740\u4e86\uff0c\u8fd9\u53ef\u662f\u6027\u547d\u4ea4\u5173\u7684\u4e8b\uff0c\u5b9e\u4e5f\u602a\u4e0d\u5f97\u4ed6\u51fa\u624b\u72e0\u6bd2\uff1f\u201d\u7adf\u7136\u4e3a\u97e9\u9738\u8fa9\u62a4\u8d77\u6765\u3002 \u3000\u3000\u201c\u8bf4\u7684\u4e5f\u662f\uff0c\u5982\u679c\u4e0d\u662f\u4ed6\u90a3\u4e24\u638c\uff0c\u8bf4\u4e0d\u5b9a\u65e9\u5df2\u95f9\u51fa\u4e2a\u8840\u6dcb\u6dcb\u7684\u5c40\u9762\u4e86\uff0c\u2026\u2026\u201d\u5929\u865a\u5b50\u9053\uff1a\u201c\u4e0d\u8bba\u80dc\u8d25\uff0c\u53cc\u65b9\u90fd\u4f1a\u6709\u5f88\u5927\u7684\u4f24\u4ea1\u2026\u2026\u201d \u3000\u3000\u7ad9\u8d77\u8eab\u5b50\u63a5\u9053\uff1a\u201c\u4e8b\u60c5\u7d27\u6025\uff0c\u8001\u9053\u5c31\u7acb\u523b\u52a8\u8eab\u3002\u201d \u3000\u3000\u8bf4\u8d70\u5c31\u8d70\uff0c\u53cc\u80a9\u4e00\u6643\uff0c\u4eba\u5df2\u5982\u8f7b\u70df\u822c\u7a7f\u7a97\u800c\u51fa\u4e86\uff0c\u6d88\u5931\u4e0d\u89c1\u3002 \u3000\u3000\u662f\u6709\u610f\u7684\u70ab\u9732\uff0c\u4e00\u79cd\u7edd\u9876\u9ad8\u660e\u7684\u8f7b\u529f\u3002 \u3000\u3000\u8fd9\u662f\u8fce\u5bbe\u9601\u4e00\u95f4\u5927\u5ba2\u623f\uff0c\u9093\u98de\u63a2\u7740\u7a97\u5916\u770b\u4e86\u597d\u4e00\u9635\u5b50\uff0c\u624d\u56de\u5934\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5201\u957f\u8001\uff0c\u526f\u603b\u7ba1\u597d\u50cf\u5bf9\u97e9\u9738\u52a8\u4e86\u6000\u7591\uff1f\u8fd9\u5c31\u9ebb\u70e6\u4e86\uff1f\u201d \u3000\u3000\u201c\u4e0d\u7528\u62c5\u5fc3\u2026\u2026\u201d\u5201\u9e4f\u9053\uff1a\u201c\u5929\u865a\u5b50\u526f\u603b\u5802\u4e3b\u4e00\u5411\u591a\u7591\uff0c\u5bf9\u8c01\u90fd\u4f1a\u5b58\u7740\u4e00\u5206\u6000\u7591\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\uff0c\u8fd9\u4ef6\u4e8b\u975e\u540c\u5c0f\u53ef\u2026\u2026\u201d\u5201\u9e4f\u6253\u65ad\u4e86\u9093\u98de\u7684\u8bdd\uff0c\u63a5\u9053\uff1a\u201c\u9093\u5144\uff0c\u4f60\u6bcf\u4e2a\u6708\u5e73\u5747\u89e3\u7f34\u603b\u5802\u591a\u5c11\u94f6\u4e24\uff1f\u201d \u3000\u3000\u201c\u5927\u6982\uff0c\u4e24\u4e07\u5230\u4e09\u4e07\u4e4b\u95f4\u2026\u2026\u201d\u9093\u98de\u9053\uff1a\u201c\u5201\u957f\u8001\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u7528\u8fd9\u4e48\u5ba2\u6c14\uff0c\u54b1\u4eec\u5e73\u8f88\u8bba\u4ea4\uff0c\u6211\u53eb\u4f60\u9093\u5144\uff0c\u4f60\u53eb\u6211\u5201\u5144\u3001\u8001\u5201\u90fd\u6210\uff0c\u5c31\u662f\u522b\u518d\u53eb\u957f\u8001\u3002\u201d \u3000\u3000\u201c\u5144\u5f1f\u606d\u656c\u4e0d\u5982\u4ece\u547d\uff0c\u5201\u5144\uff0c\u8fd9\u4ef6\u4e8b\u2026\u2026\u201d \u3000\u3000\u201c\u4e24\u4e07\u591a\u4e24\u94f6\u5b50\uff0c\u51e0\u8fd1\u603b\u5802\u6bcf\u6708\u5f00\u652f\u7684\u534a\u6570\u2026\u2026\u201d\u5201\u9e4f\u63a5\u9053\uff1a\u201c\u603b\u5802\u591a\u5904\u4e8b\u4e1a\u6536\u5165\uff0c\u90fd\u88ab\u5185\u5bab\u4e00\u7cfb\u4e2d\u4eba\u62a2\u53bb\uff0c\u76ee\u4e0b\u6536\u5165\u9510\u51cf\uff0c\u5c31\u4e0a\u4e0b\u60c5\u51b5\u800c\u8a00\uff0c\u603b\u5802\u53d7\u4e0d\u4e86\u8fd9\u4e2a\u635f\u5931\uff0c\u5c11\u4e86\u6d1b\u9633\u5c45\u8fd9\u4efd\u6536\u5165\uff0c\u603b\u5802\u5f88\u53ef\u80fd\u5165\u4e0d\u6577\u51fa\uff0c\u6240\u4ee5\uff0c\u4ed6\u4eec\u5fc5\u9700\u652f\u63f4\u6d1b\u9633\u5c45\u2026\u2026\u201d \u3000\u3000\u9093\u98de\u9053\uff1a\u201c\u771f\u7684\u2026\u2026\u201d \u3000\u3000\u201c\u5f53\u7136\u771f\u7684\uff0c\u603b\u5802\u4e5f\u660e\u767d\u65e5\u540e\u6536\u56de\u6bd4\u73b0\u5728\u4fdd\u6709\u56f0\u96be\u767e\u500d\uff0c\u6240\u4ee5\uff0c\u4ed6\u4eec\u4e0d\u4f1a\u4efb\u4ee4\u6d1b\u9633\u5c45\u518d\u843d\u4eba\u5185\u5bab\u4e00\u7cfb\u624b\u4e2d\uff0c\u2026\u2026\u201d\u5201\u9e4f\u7b11\u9053\uff1a\u201c\u603b\u5802\u4e2d\u4eba\u51e0\u4e4e\u5927\u90fd\u7531\u8fd9\u7b14\u6536\u5165\u4e2d\uff0c\u5206\u5230\u4e00\u4e9b\u5229\u76ca\uff0c\u4ed6\u4eec\u81ea\u4e0d\u4f1a\u5750\u89c6\u4e0d\u7ba1\uff0c\u8001\u5b9e\u8bf4\uff0c\u4f60\u4e00\u54ac\u7259\u771f\u8981\u64a4\u79bb\uff0c\u603b\u5802\u4e2d\u4eba\uff0c\u53ef\u80fd\u4f1a\u6bd4\u4f60\u66f4\u7d27\u5f20\uff01\u201d \u3000\u3000\u5201\u9e4f\u53c8\u9053\uff1a\u201c\u6211\u5e38\u4f4f\u603b\u5802\uff0c\u957f\u8001\u7684\u8eab\u4efd\uff0c\u4e0d\u9ad8\u4e0d\u4f4e\uff0c\u4e5f\u6709\u53c2\u4e0e\u673a\u8981\u7684\u673a\u4f1a\uff0c\u6240\u4ee5\uff0c\u6211\u5bf9\u603b\u5802\u4e2d\u7684\u60c5\u5f62\uff0c\u4e86\u89e3\u8f83\u591a\u2026\u2026\u201d \u3000\u3000\u9093\u98de\u9053\uff1a\u201c\u5c31\u9093\u67d0\u6240\u77e5\uff0c\u603b\u5802\u4e0d\u662f\u8fd8\u6709\u5927\u7b14\u7684\u9ec4\u91d1\u50a8\u5b58\u5417\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u603b\u5802\u8fd8\u6709\u4e94\u4e07\u4e24\u9ec4\u91d1\u5b58\u7740\uff0c\u2026\u2026\u201d\u5201\u9e4f\u8bf4\uff0c\u201c\u4f46\u90a3\u662f\u8001\u672c\u554a\uff01\u7528\u4e00\u4e2a\u5c11\u4e00\u4e2a\uff0c\u975e\u4e0d\u5f97\u5df2\uff0c\u8c01\u4e5f\u4e0d\u613f\u52a8\u7528\u2026\u2026\u201d \u3000\u3000\u201c\u6709\u9053\u7406\u2026\u2026\u201d\u9093\u98de\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u4fdd\u62a4\u6d1b\u9633\u5c45\u7684\u4e8b\uff0c\u7528\u4e0d\u7740\u6211\u62c5\u5fc3\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u4f46\u4f60\u62c5\u5fc3\u4e00\u4e0b\u4e5f\u597d\uff0c\u81f3\u5c11\uff0c\u8ba9\u5929\u865a\u5b50\u526f\u603b\u5802\u4e3b\u770b\u5230\u4f60\u7684\u7d27\u5f20\uff0c\u2026\u2026\u201d\u5201\u9e4f\u653e\u4f4e\u4e86\u58f0\u97f3\u53c8\u9053\uff1a\u201c\u8fd9\u8868\u793a\u4f60\u4e0d\u77e5\u9053\u4ed6\u5fc3\u4e2d\u60f3\u6cd5\uff0c\u4ed6\u4e00\u5411\u4e0d\u559c\u6b22\u4e86\u89e3\u4ed6\u5fc3\u4e2d\u60f3\u6cd5\u7684\u4eba\u3002\u201d \u3000\u3000\u201c\u627f\u6559\uff0c\u627f\u6559\u2026\u2026\u201d\u9093\u98de\u611f\u6fc0\u7684\u62b1\u62f3\u4e00\u6325\uff0c\u9053\uff1a\u201c\u5982\u679c\uff0c\u5929\u865a\u5b50\u526f\u603b\u5802\u4e3b\u9762\u62a5\u603b\u5802\u6b64\u884c\u7ecf\u8fc7\u65f6\uff0c\u8a00\u8bed\u95f4\u4f24\u5230\u4e86\u97e9\u9738\uff0c\u8bf4\u4ed6\u6545\u610f\u5236\u9020\u51b2\u7a81\uff0c\u90a3\u4f1a\u662f\u4e00\u4e2a\u4ec0\u4e48\u6837\u7684\u7ed3\u679c\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4e00\u70b9\uff0c\u66f4\u4e0d\u7528\u4f60\u591a\u8651\u4e86\uff0c\u2026\u2026\u201d\u5201\u9e4f\u7b11\u9053\uff1a\u201c\u4ed6\u5982\u6b64\u6279\u8bc4\u97e9\u9738\uff0c\u90a3\u65e0\u7591\u662f\u81ea\u6253\u5634\u5df4\uff0c\u4ed6\u5750\u9547\u73b0\u573a\uff0c\u6307\u6325\u5168\u5c40\uff0c\u5f04\u51fa\u5982\u6b64\u4e00\u4e2a\u5927\u6279\u6f0f\u6765\uff0c\u5982\u4f55\u80fd\u81ea\u5706\u5176\u8bf4\uff0c\u6240\u4ee5\uff0c\u4ed6\u4f1a\u4e00\u8def\u601d\u7d22\uff0c\u60f3\u51fa\u4e00\u4e2a\u6f02\u4eae\u7684\u8bf4\u6cd5\u51fa\u6765\u3002\u201d \u3000\u3000\u9093\u98de\u9053\uff1a\u201c\u4f46\u526f\u603b\u5802\u4e3b\uff0c\u63d0\u51fa\u7684\u4e00\u4e9b\u95ee\u9898\u4e5f\u5bf9\uff0c\u7ec6\u7ec6\u7684\u60f3\u4e00\u60f3\uff0c\u4eca\u65e5\u7684\u4e8b\uff0c\u4e5f\u786e\u6709\u53ef\u7591\u4e4b\u5904\uff1f\u97e9\u9738\u5982\u679c\u6ca1\u6709\u4e00\u638c\u51fb\u6bd9\u90a3\u7384\u8863\u5c11\u5973\uff0c\u4e8b\u60c5\u5c31\u4e0d\u4f1a\u5982\u6b64\u590d\u6742\u4e86\uff1f\u201d \u3000\u3000\u201c\u600e\u4e48\uff1f\u603b\u7ba1\u4e5f\u6000\u7591\u97e9\u526f\u603b\u7ba1\uff1f\u201d\u5201\u9e4f\u9053\uff1a\u201c\u9762\u5bf9\u5f3a\u654c\uff0c\u8fd8\u8981\u53d7\u5230\u81ea\u5df1\u4eba\u591a\u65b9\u903c\u8feb\uff0c\u4ed6\u80fd\u5fcd\u4e0b\u8fd9\u53e3\u6c14\uff0c\u5df2\u7ecf\u662f\u4e0d\u5bb9\u6613\u4e86\uff0c\u5982\u679c\u6362\u4e86\u6211\u4eec\u4e24\u4e2a\uff0c\u5177\u6709\u90a3\u7b49\u9ad8\u660e\u7684\u6b66\u529f\uff0c\u4f1a\u4e0d\u4f1a\u7ffb\u76ee\u76f8\u5411\u5462\uff1f \u3000\u3000\u201c\u5509\uff01\u4ed6\u66fe\u628a\u4e00\u53e3\u6028\u6c14\uff0c\u53d1\u6cc4\u5728\u5201\u5144\u8eab\u4e0a\uff0c\u4f46\u5201\u5144\u5bf9\u4ed6\uff0c\u5374\u53c8\u662f\u5982\u6b64\u7684\u5173\u6ce8\uff0c\u201d\u9093\u98de\u9053\uff1a\u201c\u97e9\u9738\u77e5\u6653\u6b64\u4e8b\uff0c\u5b9a\u4f1a\u5bf9\u5201\u5144\uff0c\u6781\u4e3a\u611f\u6fc0\u3002\u201d \u3000\u3000\u5201\u9e4f\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u8bf4\u8d77\u6765\uff0c\u5f53\u771f\u60ed\u6127\uff0c\u6211\u903c\u4ed6\u51fa\u624b\u4f24\u654c\uff0c\u800c\u4e14\u53c8\u51fa\u8a00\u8bbd\u6fc0\uff0c\u5982\u662f\u6362\u4e86\u6211\u5201\u67d0\u4eba\uff0c\u7edd\u4e0d\u4f1a\u7559\u4e0b\u90a3\u4e2a\u4eba\u4e00\u6761\u6027\u547d\uff0c\u4f46\u97e9\u9738\u5374\u653e\u8fc7\u4e86\u6211\uff0c\u5728\u4e0b\u5fc3\u4e2d\u5b58\u4e86\u4e00\u4efd\u6df1\u539a\u7684\u611f\u6fc0\uff0c\u6240\u4ee5\uff0c\u6211\u8981\u7559\u4e0b\u6765\uff0c\u5e0c\u671b\u80fd\u5bf9\u6d1b\u9633\u5c45\u7565\u5c3d\u7ef5\u529b\u2026\u2026\u201d \u3000\u3000\u201c\u8fd9\u5c31\u4e0d\u6562\u5f53\u4e86\u2026\u2026\u201d \u3000\u3000\u53ea\u542c\u4e00\u9635\u6025\u4fc3\u6b65\u5c65\u4e4b\u58f0\u4f20\u6765\uff0c\u6253\u65ad\u4e86\u9093\u98de\u672a\u5b8c\u4e4b\u8a00\u3002 \u3000\u3000\u201c\u4ec0\u4e48\u4eba\uff1f\u2026\u2026\u201d \u3000\u3000\u9093\u98de\u970d\u7136\u7ad9\u8d77\uff0c\u5389\u58f0\u559d\u95ee\u3002 \u3000\u3000\u6b65\u5c65\u58f0\u5728\u95e8\u5916\u505c\u4e0b\uff0c\u4e00\u4e2a\u6e05\u4eae\u7684\u58f0\u97f3\u4f20\u4e86\u8fdb\u6765\uff0c\u9053\uff1a\u201c\u5c5e\u4e0b\u80e1\u4e03\u6c42\u89c1\u603b\u7ba1\u3002\u201d \u3000\u3000\u201c\u8fdb\u6765\u5427\uff01\u201d \u3000\u3000\u80e1\u5c0f\u4e03\u5c31\u662f\u80e1\u5143\u7684\u4ee3\u53f7\uff0c\u63a8\u95e8\u800c\u5165\uff0c\u8bf4\u9053\uff1a\u201c\u4e24\u4f4d\u98ce\u5c18\u6ee1\u8eab\u7684\u4e2d\u5e74\u4eba\uff0c\u81ea\u79f0\u662f\u603b\u7ba1\u591a\u5e74\u7684\u597d\u53cb\uff0c\u6c42\u89c1\u603b\u7ba1\u3002\u201d \u3000\u3000\u201c\u53ef\u6709\u540d\u5e16\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\uff0c\u4e5f\u4e0d\u80af\u901a\u62a5\u59d3\u540d\uff0c\u4f46\u4ed6\u4eec\u8bf4\uff0c\u53ea\u8981\u4e00\u89c1\u9762\uff0c\u603b\u7ba1\u5c31\u7acb\u523b\u53ef\u4ee5\u8ba4\u51fa\u4ed6\u4eec\u3002\u201d \u3000\u3000\u80e1\u5143\u8bf4\u51fa\u4e86\u4e24\u4eba\u7684\u6a21\u6837\u3002 \u3000\u3000\u201c\u6709\u8fd9\u7b49\u4e8b\uff0c\u2026\u2026\u201d\u9093\u98de\u6709\u4e9b\u832b\u7136\u5730\u8bf4\uff0c\u201c\u4f1a\u6709\u4ec0\u4e48\u4eba\u5462\uff1f\u6211\u5230\u6d1b\u9633\u5c45\u7684\u4e8b\uff0c\u8001\u53cb\u6545\u65e7\u77e5\u6653\u7684\u4e0d\u591a\uff01\u201d \u3000\u3000\u4e5d\u5934\u9e1f\u4e5f\u76f4\u51b7\u51b7\u7684\u5728\u89c2\u67e5\u7740\u9093\u98de\uff0c\u770b\u51fa\u4ed6\u4e0d\u4f3c\u4f5c\u4f2a\uff0c\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u9093\u5144\uff0c\u6765\u4eba\u80fd\u627e\u4e0a\u8fce\u5bbe\u9601\u6765\uff0c\u663e\u7136\u5df2\u5bf9\u4f60\u7684\u884c\u8e2a\u5341\u5206\u6e05\u695a\uff0c\u2026\u2026\u201d \u3000\u3000\u201c\u5201\u5144\u7684\u610f\u601d\u662f\u2026\u2026\u201d \u3000\u3000\u201c\u51fa\u53bb\u770b\u770b\uff0c\u5144\u5f1f\u966a\u4f60\u53bb\uff0c\u5982\u82e5\u662f\u627e\u9ebb\u70e6\u7684\uff0c\u90a3\u5c31\u7528\u4e0d\u7740\u5e26\u4ed6\u4eec\u8fdb\u6765\u4e86\u2026\u2026\u201d\u5201\u9e4f\u8bf4\uff0c\u201c\u5c31\u5728\u8fce\u5bbe\u9601\u5916\u9762\uff0c\u628a\u4ed6\u4eec\u6482\u5012\u3002\u201d \u3000\u3000\u9093\u98de\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u80e1\u5c0f\u4e03\uff0c\u80fd\u4e0d\u80fd\u628a\u4ed6\u4eec\u7684\u6837\u5b50\uff0c\u5f62\u5bb9\u4e00\u4e0b\uff1f\u201d \u3000\u3000\u80e1\u5143\u6c89\u601d\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u4e24\u4e2a\u4eba\u7684\u4e2a\u5b50\uff0c\u90fd\u4e0d\u592a\u9ad8\uff0c\u56db\u5341\u5230\u4e94\u5341\u4e4b\u95f4\uff0c\u957f\u7684\u6709\u70b9\u795e\u4f3c\uff0c\u5e94\u8be5\u662f\u4e00\u5bf9\u5144\u5f1f\uff0c\u5e26\u7740\u4e00\u6837\u5175\u5203\uff0c\u662f\u4e00\u79cd\u5f88\u5947\u602a\u7684\u5175\u5203\uff0c\u50cf\u5200\uff0c\u4f46\u53c8\u4e0d\u662f\u5200\u2026\u2026\u201d \u3000\u3000\u201c\u6708\u7259\u5200\u2026\u2026\u201d\u9093\u98de\u9053\uff1a\u201c\u5200\u7684\u5c16\u7aef\uff0c\u6709\u4e00\u4e2a\u5f62\u5982\u6708\u7259\u7684\u4e1c\u897f\uff0c\u2026\u2026\u201d \u3000\u3000\u201c\u5bf9\uff01\u5c31\u662f\u90a3\u79cd\u602a\u5f02\u7684\u5175\u5203\u2026\u2026\u201d\u80e1\u5143\u8bf4\uff0c\u201c\u5728\u4e0b\u4ece\u672a\u89c1\u8fc7\u90a3\u6837\u7684\u5175\u5203\uff01\u201d \u3000\u3000\u201c\u8001\u9093\uff0c\u53ef\u662f\u5df2\u731c\u5230\u4ed6\u4eec\u662f\u8c01\u4e86\u2026\u2026\u201d\u5201\u9e4f\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u662f\u670b\u53cb\uff1f\u8fd8\u662f\u4ec7\u4eba\uff1f\u201d \u3000\u3000\u201c\u670b\u53cb\uff01\u4e0d\u8fc7\u4e8c\u5341\u5e74\u6ca1\u89c1\u9762\u4e86\uff0c\u600e\u4e48\u4f1a\u627e\u6765\u6d1b\u9633\u5c45\u5462\uff1f\u2026\u2026\u201d \u3000\u3000\u9093\u98de\u653e\u4f4e\u4e86\u58f0\u97f3\uff0c\u9053\uff1a\u201c\u4ed6\u4eec\u6ca1\u6709\u52a0\u5165\u5929\u9a6c\u5802\uff0c\u4e0d\u53ef\u80fd\u77e5\u9053\u6211\u5728\u8fd9\u91cc\uff1f\u8fd9\u4ef6\u4e8b\u6709\u70b9\u53e4\u602a\uff1f\u201d \u3000\u3000\u201c\u600e\u4e48\uff1f\u8fde\u4e8c\u5341\u51e0\u5e74\u8001\u670b\u53cb\uff0c\u4e5f\u4e0d\u76f8\u4fe1\uff01\u2026\u2026\u201d\u5201\u9e4f\u8bed\u610f\u53cc\u5173\u9053\uff1a\u201c\u9664\u975e\u4ed6\u4eec\u6295\u5165\u4e86\u5929\u738b\u95e8\uff0c\u5f53\u4eca\u6c5f\u6e56\u4e4b\u4e0a\uff0c\u8fd8\u6709\u90a3\u4e2a\u95e8\u6d3e\uff0c\u6562\u548c\u5929\u9a6c\u603b\u5802\u4e3a\u654c\uff1f\u201d \u3000\u3000\u201c\u5bf9\uff01\u5bf9\uff01\u5201\u5144\u4e00\u8a00\u63d0\u9192\u68a6\u4e2d\u4eba\uff0c\u54b1\u4eec\u53bb\u77a7\u77a7\u5427\uff01\u201d\u9093\u98de\u5e76\u672a\u53eb\u80e1\u5143\u5e26\u8def\uff0c\u5f53\u5148\u5411\u8fce\u5bbe\u9601\u5916\u884c\u53bb\u3002 \u3000\u3000\u8fce\u5bbe\u9601\u5916\uff0c\u679c\u7136\u7ad9\u7740\u4e24\u4e2a\u4e0d\u9ad8\u4e0d\u77ee\u7684\u4e2d\u5e74\u4eba\uff0c\u5e74\u7ea6\u4e94\u5341\u5de6\u53f3\uff0c\u4e24\u9b13\u5fae\u6591\uff0c\u8170\u4e2d\u5404\u6302\u7740\u4e00\u652f\u6c5f\u6e56\u4e0a\u6781\u5c11\u4eba\u4f7f\u7528\u7684\u6708\u7259\u5200\u3002 \u3000\u3000\u4f46\u5927\u51fa\u610f\u6599\u4e4b\u5916\u7684\u662f\u9093\u98de\u5e76\u672a\u7acb\u523b\u5411\u4e24\u4eba\u62db\u547c\u3002 \u3000\u3000\u80e1\u5143\u7ad9\u5728\u9093\u98de\u8eab\u540e\u6570\u5c3a\uff0c\u4e00\u526f\u4eba\u5fae\u8a00\u597d\u8f7b\u7684\u4f4e\u59ff\u6001\uff0c\u5b9e\u5219\u5168\u795e\u8d2f\u6c6a\uff0c\u7559\u5fc3\u770b\u60c5\u52bf\u7684\u53d1\u5c55\u3002 \u3000\u3000\u4ed6\u5949\u547d\u7559\u5b88\u8fce\u5bbe\u9601\uff0c\u804c\u53f8\u62a4\u536b\u9093\u98de\uff0c\u4f46\u6697\u4e2d\u5374\u8981\u6ce8\u610f\u60c5\u52bf\u7684\u53d1\u5c55\uff0c\u5fc5\u8981\u65f6\u7acb\u523b\u4f20\u62a5\u6c5f\u67ab\u3002\u660e\u660e\u662f\u9093\u98de\u4e8c\u5341\u5e74\u7684\u6545\u65e7\uff0c\u4e3a\u4ec0\u4e48\u9093\u98de\u7adf\u4f3c\u5168\u4e0d\u76f8\u8bc6\u3002 \u3000\u3000\u80e1\u5143\u770b\u7684\u6123\u4f4f\u4e86\uff0c\u6697\u9053\uff1a\u201c\u8fd9\u662f\u600e\u4e48\u540c\u4e8b\uff1f\u6cb3\u6e56\u8be1\u53d8\uff0c\u679c\u7136\u662f\u5f88\u96be\u6d4b\u5ea6\u3002\u201d \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u5201\u9e4f\u4e5f\u770b\u5f97\u4e00\u8138\u8ff7\u7cca\uff0c\u9093\u98de\u53ef\u4ee5\u88c5\u4f5c\u4e0d\u8bc6\u4e8c\u4eba\uff0c\u4f46\u4e24\u4eba\u600e\u7684\u4e5f\u4e0d\u8bc6\u9093\u98de\u5462\uff1f \u3000\u3000\u539f\u6765\uff0c\u5201\u9e4f\u548c\u9093\u98de\u5e76\u80a9\u800c\u884c\uff0c\u8d70\u5728\u4e00\u8d77\uff0c\u4e24\u4e2a\u4e2d\u5e74\u6c49\u5b50\uff0c\u56db\u9053\u76ee\u5149\u5206\u5728\u4e24\u4eba\u8138\u4e0a\u8f6c\u52a8\uff0c\u4e0d\u77e5\u5728\u770b\u4e9b\u4ec0\u4e48\uff1f \u3000\u3000\u5201\u9e4f\u8111\u9645\u95f4\u7075\u5149\u4e00\u95ea\uff0c\u9053\uff1a\u201c\u9093\u5144\uff0c\u5c0f\u5fc3\uff01\u201d \u3000\u3000\u9093\u5144\u4e24\u4e2a\u5b57\u4e00\u51fa\u53e3\uff0c\u4e24\u4e2a\u4e2d\u5e74\u4eba\u7a81\u7136\u6251\u5411\u4e86\u9093\u98de\uff0c\u5200\u5149\u95ea\u52a8\uff0c\u76f4\u53d6\u8981\u5bb3\u3002 \u3000\u3000\u4e24\u4e2a\u4eba\uff0c\u4e24\u67c4\u6708\u7259\u5200\uff0c\u5c55\u5e03\u51fa\u7684\u5200\u5149\uff0c\u8db3\u8db3\u6709\u5341\u516b\u5c3a\u65b9\u5706\uff0c\u5b8c\u5168\u628a\u9093\u98de\u5708\u7f69\u5728\u4e00\u7247\u5200\u5149\u4e4b\u4e2d\u3002 \u3000\u3000\u5201\u9e4f\u7684\u5bbd\u9762\u53cc\u5203\u5200\u51e0\u4e4e\u662f\u4e24\u4eba\u6708\u7259\u5200\u51fa\u624b\u7684\u540c\u65f6\uff0c\u4e5f\u79bb\u9798\u800c\u51fa\uff0c\u95ea\u98de\u8d77\u4e00\u9053\u5bd2\u5149\uff0c\u8fce\u5411\u90a3\u843d\u5411\u9093\u98de\u7684\u4e00\u7247\u5200\u5e55\uff0c\u62a4\u4f4f\u4e86\u9093\u98de\u3002 \u3000\u3000\u80e1\u5143\u6697\u6697\u8d5e\u9053\uff1a\u201c\u597d\u5feb\u7684\u4e00\u5200\uff0c\u5f53\u771f\u662f\u51f6\u540d\u4e4b\u4e0b\u65e0\u865a\u58eb\uff0c\u8fd9\u4e5d\u5934\u9e1f\u679c\u7136\u662f\u96be\u7f20\u7684\u4eba\u7269\u3002\u201d \u3000\u3000\u4ed6\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u624b\u4e2d\u7684\u957f\u5251\u4e5f\u79bb\u9798\u800c\u51fa\uff0c\u6740\u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u4e5d\u5934\u9e1f\u90a3\u4e00\u5200\u5177\u6709\u76f8\u5f53\u7684\u5a01\u529b\uff0c\u5c01\u5f00\u4e00\u628a\u6708\u7259\u5200\u540e\uff0c\u7acb\u65f6\u5411\u53e6\u4e00\u4e2a\u5927\u6c49\u653b\u53bb\u3002 \u3000\u3000\u5201\u9e4f\u5bf9\u81ea\u5df1\u7684\u6b66\u529f\uff0c\u5177\u6709\u5f3a\u70c8\u7684\u4fe1\u5fc3\uff0c\u653b\u654c\u5fc5\u6551\uff0c\u4e00\u62db\u89e3\u5371\u3002 \u3000\u3000\u90a3\u77e5\u5bf9\u65b9\u7adf\u7136\u5168\u4e0d\u7406\u4f1a\u4e60\u9e4f\u7684\u5200\u52bf\uff0c\u6708\u7259\u5200\u4ecd\u7136\u6307\u5411\u9093\u98de\u7684\u5de6\u80f8\u3002 \u3000\u3000\u5e78\u597d\u80e1\u5143\u7684\u957f\u5251\uff0c\u53ca\u65f6\u800c\u81f3\uff0c\u6321\u4f4f\u4e86\u6708\u7259\u5200\u3002 \u3000\u3000\u4e5d\u5934\u9e1f\u5374\u770b\u4e00\u5446\uff0c\u5fd6\u9053\uff1a\u201c\u8fd9\u4e24\u4e2a\u5c0f\u5b50\uff0c\u5168\u4e0d\u987e\u81ea\u5df1\u7684\u751f\u6b7b\uff0c\u53ea\u6c42\u4f24\u654c\uff0c\u7b97\u4ec0\u4e48\u6b66\u529f\u3002\u201d \u3000\u3000\u5c31\u5728\u4ed6\u4e00\u6014\u795e\u95f4\uff0c\u90a3\u67c4\u88ab\u4ed6\u5c01\u5f00\u7684\u6708\u7259\u5200\uff0c\u5ffd\u7136\u5377\u5730\u98de\u65cb\uff0c\u8f6c\u5411\u9093\u98de\u53cc\u817f\u3002 \u3000\u3000\u8fd9\u4e00\u62db\u53d8\u51fa\u610f\u5916\uff0c\u5201\u9e4f\u5403\u4e86\u4e00\u60ca\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u8001\u9093\uff0c\u5feb\u4e9b\u95ea\u907f\u5440\uff01\u2026\u2026\u201d\u559d\u58f0\u4e2d\u98de\u8eab\u800c\u8d77\uff0c\u5934\u4e0b\u811a\u4e0b\uff0c\u624b\u4e2d\u53cc\u5203\u5200\u63d2\u5730\u5982\u6869\u3002 \u3000\u3000\u91d1\u94c1\u4ea4\u9e23\u58f0\u4e2d\uff0c\u95ea\u8d77\u4e00\u6e9c\u706b\u5149\uff0c\u5c01\u4f4f\u5377\u5730\u5982\u96ea\u7684\u6708\u7259\u5200\uff0c\u5de6\u8896\u626c\u52a8\uff0c\u56db\u652f\u4eae\u94f6\u68f1\uff0c\u8131\u624b\u800c\u51fa\u3002 \u3000\u3000\u56db\u68f1\u7686\u4e2d\uff0c\u53f3\u9996\u5927\u6c49\uff0c\u5662\u4e86\u4e00\u58f0\uff0c\u7acb\u523b\u6bd9\u547d\u3002 \u3000\u3000\u80e1\u5143\u4eba\u5df2\u4ece\u9093\u98de\u8eab\u540e\u7a7f\u51fa\uff0c\u53cd\u624b\u4e00\u5251\uff0c\u523a\u5165\u4e86\u5de6\u9996\u5927\u6c49\u5fc3\u810f\u8981\u5bb3\uff0c\u5f53\u573a\u4e86\u5e10\u3002 \u3000\u3000\u8fd9\u4e00\u5251\u6709\u5982\u795e\u6765\u4e4b\u7b14\uff0c\u7ed8\u51fa\u4e86\u4e00\u7247\u8273\u4e3d\u5f69\u8272\u4e4b\u7ae0\uff0c\u770b\u7684\u9093\u98de\u3001\u5201\u9e4f\uff0c\u90fd\u4e3a\u4e4b\u795e\u8272\u4e00\u52a8\u4e86\u3002 \u3000\u3000\u8fd9\u6b63\u662f\u5357\u5200\u95e8\u6bb5\u4e5d\u7684\u53cd\u624b\u4e00\u5200\uff0c\u4f46\u5df2\u88ab\u80e1\u5143\u8fd0\u7528\u5230\u5251\u6cd5\u53d8\u5316\u4e4b\u4e0a\u3002 \u3000\u3000\u201c\u597d\u5251\u6cd5\uff0c\u8001\u9093\u554a\uff01\u4f60\u8fd9\u6279\u624b\u4e0b\uff0c\u2026\u2026\u201d\u5201\u9e4f\u9053\uff1a\u201c\u4e2a\u4e2a\u90fd\u662f\u9ad8\u624b\u561b\uff01\u201d \u3000\u3000\u201c\u5201\u5144\u5938\u5956\u4e86\u2026\u2026\u201d\u9093\u98de\u5728\u4e24\u67c4\u6708\u7259\u5200\u653b\u88ad\u8fc7\u7a0b\u4e2d\uff0c\u8868\u73b0\u7684\u9547\u9759\uff0c\u786e\u6709\u6cf0\u5c71\u5d29\u4e8e\u524d\u9762\u8272\u4e0d\u53d8\u7684\u6c14\u5ea6\u3002 \u3000\u3000\u4f7f\u5201\u9e4f\u5fc3\u4e2d\u6697\u751f\u656c\u670d\uff0c\u4f46\u4e5f\u5bf9\u9093\u98de\u8fd9\u7b49\u4e0d\u77e5\u6b7b\u6d3b\u7684\u4e3e\u6b62\uff0c\u6709\u4e9b\u4e0d\u6ee1\uff0c\u5fcd\u4e0d\u4f4f\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5947\u602a\u5440\uff01\u8001\u9093\uff0c\u5982\u82e5\u5728\u4e0b\u624b\u4e2d\u4e4b\u5200\uff0c\u665a\u90a3\u4e48\u4e00\u4e1d\u4e1d\uff0c\u5c01\u4e0d\u4f4f\u5bf9\u65b9\u7684\u6708\u7259\u5200\uff0c\u4f60\u90a3\u4e24\u6761\u817f\uff0c\u8fd8\u80fd\u591f\u4fdd\u5f97\u4f4f\u5417\uff1f\u201d \u3000\u3000\u9093\u98de\u884c\u8fd1\u4e24\u4e2a\u4e2d\u5e74\u5927\u6c49\u7684\u5c38\u4e4b\u524d\uff0c\u4f38\u624b\u5728\u4e24\u4eba\u7684\u7751\u4e0a\u63ed\u4e0b\u4e86\u4e24\u526f\u4eba\u76ae\u9762\u5177\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u9732\u51fa\u4e24\u5f20\u5e74\u8f7b\u4eba\u7684\u8138\u6765\u3002 \u3000\u3000\u6562\u60c5\uff0c\u6765\u4eba\u662f\u7ecf\u8fc7\u4e86\u7cbe\u5bc6\u7684\u6613\u5bb9\u624b\u6cd5\u3002 \u3000\u3000\u5201\u9e4f\u51dd\u76ee\u671b\u53bb\uff0c\u7531\u4e24\u4eba\u7684\u8138\u4e0a\u5224\u65ad\uff0c\u6765\u4eba\u6700\u591a\u5728\u4e8c\u5341\u4e09\u56db\u5de6\u53f3\u3002 \u3000\u3000\u4f46\u95fb\u9093\u98de\u8bf4\u9053\uff1a\u201c\u4e5f\u8bb8\u4fdd\u4e0d\u4f4f\u6211\u7684\u4e24\u6761\u817f\uff0c\u4e0d\u8fc7\uff0c\u9093\u67d0\u4eba\u5bf9\u5201\u5144\u7684\u6b66\u529f\uff0c\u5f88\u6709\u4fe1\u5fc3\u2026\u2026\u201d \u3000\u3000\u201c\u5c11\u704c\u8ff7\u6c64\uff0c\u5201\u67d0\u4eba\u4e0d\u5403\u8fd9\u4e2a\uff0c\u4f46\u6211\u5947\u602a\u7684\u662f\uff0c\u4f60\u600e\u4e48\u80fd\u80af\u5b9a\u6211\u5201\u67d0\u4eba\u4f1a\u62fc\u4e0a\u5168\u529b\u6551\u4f60\uff0c\u8fd9\u662f\u62ff\u8001\u547d\u5f00\u73a9\u7684\u4e8b\uff0c\u50cf\u4f60\u9093\u5144\u8fd9\u6837\u7684\u8001\u6c5f\u6e56\uff0c\u600e\u4f1a\u4e0d\u5206\u8f7b\u91cd\uff1f\u201d \u3000\u3000\u201c\u8fd9\u662f\u4e00\u79cd\u5224\u65ad\u80fd\u529b\u2026\u2026\u201d\u9093\u98de\u9053\uff1a\u201c\u6211\u4e00\u89c1\u4ed6\u4eec\u65f6\uff0c\u5df2\u77e5\u9053\u4ed6\u4eec\u4e0d\u662f\u6211\u7684\u670b\u53cb\uff0c\u800c\u662f\u6b7b\u4ea1\u6740\u624b\u6539\u626e\uff0c\u4ed6\u4eec\u65e0\u6cd5\u80af\u5b9a\u7684\u8ba4\u51fa\u6765\u4e60\u5144\u548c\u6211\u7684\u8eab\u4efd\uff1f\u5982\u4e0d\u662f\u4f60\u90a3\u4e00\u53e5\u9093\u5144\u5c0f\u5fc3\uff01\u4ed6\u4eec\u8fd8\u9700\u8981\u4e00\u6bb5\u72b9\u8c6b\u65f6\u95f4\uff0c\u4f46\u6700\u7ec8\u4ecd\u662f\u96be\u514d\u4e00\u6218\u4e86\u3002 \u3000\u3000\u8fd9\u4e9b\u6b7b\u4ea1\u6740\u624b\u7684\u53ef\u6015\u4e4b\u5904\uff0c\u5c31\u5728\u4ed6\u4eec\u8ba4\u8fd9\u4e00\u4e2a\u76ee\u6807\u4e4b\u540e\uff0c\u4e0d\u8ba1\u672c\u8eab\u7684\u751f\u6b7b\uff0c\u53ea\u6c42\u4f24\u654c\uff0c\u4e5f\u4e0d\u7406\u4f1a\u522b\u4eba\u7684\u653b\u51fb\uff0c\u4ed6\u4eec\u5fc3\u4e2d\u53ea\u6709\u4e00\u4e2a\u76ee\u6807\uff0c\u90a3\u5c31\u662f\u4ed6\u4eec\u5949\u547d\u6740\u6b7b\u7684\u4eba\uff01\u4e01\u957f\u8001\u548c\u5218\u6e05\u3001\u5468\u6e90\u4e24\u4f4d\u62a4\u6cd5\uff0c\u90fd\u6b7b\u4f24\u5728\u4ed6\u4eec\u7684\u624b\u4e2d\u3002\u201d \u3000\u3000\u201c\u5185\u5bab\u4e00\u7cfb\u4e2d\u8bad\u7ec3\u7684\u6740\u624b\u2026\u2026\u201d \u3000\u3000\u201c\u5bf9\uff01\u2026\u2026\u201d\u9093\u98de\u8bf4\uff0c\u201c\u4eca\u5929\uff0c\u5144\u5f1f\u80fd\u9003\u8fc7\u4e00\u52ab\uff0c\u5168\u51ed\u5201\u5144\u76f8\u62a4\u2026\u2026\u201d \u3000\u3000\u201c\u4f60\u7684\u62a4\u536b\uff0c\u4e5f\u5f88\u4e0d\u9519\u2026\u2026\u201d\u5201\u9e4f\u7684\u76ee\u5149\u6295\u6ce8\u5728\u80e1\u5143\u7684\u8eab\u4e0a\u3002 \u3000\u3000\u9093\u98de\u628a\u81ea\u5149\u51dd\u6ce8\u80e1\u65e0\u8eab\u4e0a\uff0c\u8138\u4e0a\u662f\u4e00\u79cd\u611f\u6fc0\u548c\u6000\u7591\u7ec4\u5408\u800c\u6210\u7684\u602a\u5f02\u795e\u8272\uff0c\u9053\uff1a\u201c\u662f\u7684\uff01 \u3000\u3000\u4f60\u5f88\u4e0d\u9519\u3002\u201d \u3000\u3000\u201c\u591a\u8c22\u603b\u7ba1\u2026\u2026\u201d\u80e1\u5143\u8eac\u8eab\u62b1\u62f3\uff0c\u8868\u73b0\u51fa\u4e86\u5f88\u9ad8\u7684\u5d07\u656c\u548c\u793c\u8c8c\u3002 \u3000\u3000\u201c\u5173\u4e8e\u5201\u5144\u5fc3\u4e2d\u4e4b\u7591\uff0c\u5144\u5f1f\u4e5f\u6709\u89e3\u91ca\u2026\u2026\u201d\u9093\u98de\u8bf4\uff0c\u201c\u8fd9\u4e9b\u6b7b\u4ea1\u6740\u624b\uff0c\u90fd\u6709\u9510\u5229\u7684\u6740\u4eba\u773c\u5149\uff0c\u53ea\u8981\u5144\u5f1f\u95ea\u907f\u3001\u8fd8\u624b\uff0c\u88ab\u4ed6\u4eec\u77a7\u51fa\u4e86\u7834\u7efd\u6240\u5728\uff0c\u90a3\u5c31\u4f1a\u5168\u529b\u653b\u6740\uff0c\u602a\u62db\u8fde\u51fa\uff0c\u8ffd\u9b42\u3001\u593a\u547d\uff0c\u51f6\u5389\u975e\u51e1\uff0c\u4f46\u5982\u5144\u5f1f\u7ad9\u7740\u4e0d\u52a8\uff0c\u4ed6\u4eec\u627e\u4e0d\u51fa\u7834\u7efd\u6240\u5728\uff0c\u5c31\u53ea\u6709\u5411\u6211\u53d1\u52a8\u4e00\u822c\u7684\u653b\u51fb\uff0c\u6709\u5201\u5144\u5728\u65c1\u76f8\u62a4\uff0c\u4ed6\u4eec\u53c8\u4e0d\u4f1a\u5bf9\u4f60\u4e0b\u624b\uff0c\u4ee5\u5201\u5144\u7684\u6280\u827a\uff0c\u80dc\u7b97\u5f88\u5927\uff0c\u6240\u4ee5\uff0c\u5144\u5f1f\u8d4c\u8fd9\u4e00\u8bb0\uff0c\u7b97\u662f\u88ab\u6211\u8d4c\u8d62\u4e86\u3002\u201d \u3000\u3000\u201c\u539f\u6765\u5982\u6b64\u2026\u2026\u201d\u5201\u9e4f\u8bf4\uff0c\u201c\u4ed6\u4eec\u65bd\u7528\u7684\u5175\u5203\uff0c\u5c31\u662f\u8fd9\u79cd\u6708\u7259\u5200\u5417\uff1f\u201d \u3000\u3000\u201c\u4e0d\u662f\uff01\u662f\u4e00\u79cd\u950b\u5229\u7684\u5f2f\u5200\u2026\u2026\u201d\u9093\u98de\u8bf4\uff0c\u201c\u5175\u5203\u7684\u4e0d\u987a\u624b\uff0c\u4e5f\u662f\u4ed6\u4eec\u5a01\u529b\u51cf\u5f31\u7684\u539f\u56e0\u4e4b\u4e00\u3002\u201d \u3000\u3000\u201c\u770b\u6765\uff0c\u5bf9\u4e8b\u7269\u89c2\u5bdf\u65b9\u9762\uff0c\u5201\u67d0\u662f\u5927\u5927\u7684\u4e0d\u53ca\u9093\u5144\u4e86\uff01\u201d \u3000\u3000\u201c\u5982\u679c\u6613\u5730\u800c\u5904\uff0c\u6211\u76f8\u4fe1\u5201\u5144\u4ea6\u5fc5\u6709\u5e94\u4ed8\u4e4b\u9053\u2026\u2026\u201d\u9093\u98de\u9053\uff1a\u201c\u73b0\u5728\u8bc1\u660e\u4e86\u4e00\u4ef6\u4e8b\uff0c\u90a3\u5c31\u662f\u5185\u5bab\u4e00\u7cfb\u4e2d\u9664\u6389\u6211\u9093\u67d0\u4e4b\u5fc3\uff0c\u5341\u5206\u79ef\u6781\uff0c\u660e\u53d6\u6697\u88ad\uff0c\u4e0d\u62e9\u624b\u6bb5\uff0c\u5144\u5f1f\u4e5f\u8be5\u4f5c\u4e00\u4e9b\u9632\u5907\u63aa\u65bd\u4e86\uff01\u201d \u3000\u3000\u201c\u4e0d\u9519\uff01\u5201\u67d0\u53ef\u4ee5\u628a\u7559\u5728\u957f\u5b89\u7684\u56db\u4f4d\u62a4\u6cd5\uff0c\u5341\u4f4d\u9999\u4e3b\u8c03\u5165\u6d1b\u9633\u5c45\u6765\uff0c\u4ee5\u589e\u5f3a\u4eba\u624b\u3002\u201d \u3000\u3000\u201c\u975e\u4e0d\u5f97\u5df2\uff0c\u4e0d\u80fd\u5728\u8fd9\u91cc\u52a8\u624b\uff0c\u8fd9\u6837\u5427\uff01\u2026\u2026\u201d\u9093\u98de\u8bf4\uff0c\u201c\u5201\u5144\u53ef\u4ee5\u628a\u56db\u5927\u62a4\u6cd5\u4e2d\u7684\u4e24\u4f4d\u8c03\u5165\u6d1b\u9633\u5c45\uff0c\u518d\u8c03\u4e24\u4e09\u4f4d\u9999\u4e3b\u8fdb\u6765\uff0c\u4f59\u4e0b\u7684\u4ecd\u7559\u5728\u6d1b\u9633\u5c45\u5916\uff0c\u7b49\u5f85\u5201\u5144\u4e4b\u547d\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u5c31\u8fd9\u4e48\u5b89\u6392\uff0c\u5201\u67d0\u4eb2\u81ea\u8d70\u4e00\u8d9f\uff0c\u628a\u4ed6\u4eec\u91cd\u65b0\u5e03\u7f72\u4e00\u4e0b\uff0c\u2026\u2026\u201d\u5927\u6b65\u5411\u5916\u884c\u53bb\u3002 \u3000\u3000\u76ee\u7779\u5201\u9e4f\u53bb\u8fdc\uff0c\u9093\u98de\u4e0b\u4ee4\u6536\u62fe\u73b0\u573a\uff0c\u4e00\u9762\u4e3e\u6b65\u5411\u82b1\u6811\u6797\u4e2d\u884c\u53bb\u3002 \u3000\u3000\u80e1\u5143\u53d7\u547d\u4fdd\u62a4\u9093\u98de\uff0c\u53ea\u597d\u7d27\u968f\u8eab\u540e\u3002 \u3000\u3000\u6797\u4e2d\u5e7d\u9759\uff0c\u56db\u987e\u65e0\u4eba\uff0c\u9093\u98de\u7a81\u7136\u505c\u4e0b\u811a\u6b65\uff0c\u56de\u987e\u7740\u80e1\u5143\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u90fd\u662f\u97e9\u9738\u5f15\u6765\u7684\u4eba\uff0c\u4e2a\u4eba\u8eab\u624b\u4e0d\u51e1\uff0c\u521a\u624d\u90a3\u53cd\u624b\u4e00\u5251\uff0c\u4e0d\u4f46\u9093\u67d0\u4eba\u81ea\u53f9\u5f17\u5982\uff0c\u5c31\u662f\u5201\u9e4f\u4e5f\u770b\u7684\u5fc3\u751f\u656c\u4f69\uff0c\u6211\u4e0d\u660e\u767d\uff0c\u4f60\u4eec\u8fd9\u7b49\u8eab\u624b\u9ad8\u660e\u7684\u4eba\u7269\uff0c\u600e\u80af\u5c48\u5c31\u6d1b\u9633\u5c45\u7684\u9556\u624b\uff1f\u201d \u3000\u3000\u8fd9\u756a\u8bdd\u5f88\u96be\u7b54\u590d\uff0c\u4e00\u8bed\u5931\u9519\uff0c\u5c31\u4f1a\u9732\u51fa\u9a6c\u811a\uff0c\u80e1\u5143\u8c28\u614e\u5c0f\u5fc3\u7684\u601d\u8003\u4e86\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u4e0d\u5230\u8fd9\u91cc\uff0c\u53c8\u80fd\u5230\u54ea\u91cc\u53bb\u5462\uff1f\u603b\u7ba1\u5fc3\u4e2d\u5982\u6709\u6000\u7591\uff0c\u4f55\u4e0d\u53bb\u627e\u97e9\u526f\u603b\u7ba1\u8c08\u8c08\u5462\uff1f\u201d \u3000\u3000\u201c\u4ed6\u5df2\u7ecf\u544a\u8bc9\u4e86\u6211\u5f88\u591a\u4e8b\u2026\u2026\u201d\u9093\u98de\u9053\uff1a\u201c\u73b0\u5728\uff0c\u6211\u5fc5\u987b\u8981\u4e86\u89e3\u7684\u4e00\u4ef6\u4e8b\u662f\uff0c\u4f60\u4eec\u51e0\u4eba\u7684\u6b66\u529f\uff0c\u662f\u4e0d\u662f\u90fd\u5f88\u9ad8\u660e\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4e2a\uff0c\u6ca1\u6709\u4e00\u5b9a\u4e4b\u89c4\uff0c\u5c5e\u4e0b\u5f88\u96be\u56de\u7b54\u2026\u2026\u201d\u80e1\u5143\u8bf4\uff0c\u201c\u4e0d\u8fc7\uff0c\u6709\u70b9\u53ef\u4ee5\u5411\u603b\u7ba1\u8bf4\u660e\u7684\uff0c\u6211\u4eec\u51e0\u4eba\u4e4b\u4e2d\uff0c\u5728\u4e0b\u7684\u8eab\u624b\uff0c\u662f\u6700\u5dee\u7684\u4e00\u4e2a\u4e86\u3002\u201d \u3000\u3000\u9093\u98de\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u4f60\u4e5f\u4e0d\u53eb\u80e1\u5c0f\u4e03\u4e86\uff1f\u201d \u3000\u3000\u201c\u5c5e\u4e0b\u662f\u59d3\u80e1\u2026\u2026\u201d\u80e1\u5143\u606d\u606d\u656c\u656c\u7684\u56de\u7b54\u3002 \u3000\u3000\u201c\u53ea\u8981\u4f60\u4eec\u6ca1\u6709\u9677\u5bb3\u6211\u7684\u7528\u5fc3\uff0c\u9093\u67d0\u4eba\u4e5f\u4e0d\u7528\u591a\u95ee\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u603b\u7ba1\u5e94\u8be5\u611f\u89c9\u5f97\u5230\uff0c\u97e9\u526f\u603b\u7ba1\u548c\u6211\u4eec\uff0c\u90fd\u5728\u5c3d\u5168\u529b\u4fdd\u62a4\u603b\u7ba1\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u4f60\u5f88\u660e\u786e\u7684\u8bf4\u51fa\u6765\u8fd9\u4e00\u70b9\uff0c\u4e5f\u5c31\u591f\u4e86\uff01\u201d\u9093\u98de\u679c\u7136\u4e0d\u518d\u591a\u95ee\uff0c\u6b65\u51fa\u82b1\u6811\u6797\uff0c\u76f4\u5411\u4f4f\u5904\u884c\u53bb\u3002 \u3000\u3000\u80e1\u5143\u7d27\u968f\u8eab\u540e\uff0c\u4e00\u526f\u5168\u795e\u62a4\u4ece\u7684\u6837\u5b50\u3002 \u3000\u3000\u6d1b\u9633\u5c45\u5730\u533a\u5e7f\u5927\uff0c\u82b1\u6811\u904d\u5e03\uff0c\u867d\u6709\u591a\u6b21\u7684\u640f\u6740\uff0c\u4f46\u90fd\u5c40\u9650\u4e00\u9685\uff0c\u518d\u52a0\u4e0a\u6709\u610f\u7684\u63a9\u906e\uff0c\u90fd\u672a\u4f20\u64ad\u51fa\u53bb\u3002 \u3000\u3000\u6240\u4ee5\uff0c\u6d1b\u9633\u540e\u7684\u751f\u610f\uff0c\u5e76\u672a\u53d7\u5230\u5f71\u54cd\uff0c\u4f9d\u65e7\u662f\u5ba2\u5982\u6d41\u6c34\u9a6c\u5982\u9f99\u3002 \u3000\u3000\u53ea\u662f\u6885\u82b1\u59d1\u5a18\u5374\u8bf7\u4e86\u75c5\u5047\uff0c\u4e13\u4e3a\u6885\u82b1\u800c\u6765\u7684\u5ba2\u4eba\uff0c\u65e0\u4e0d\u5927\u5931\u6240\u671b\u3002 \u3000\u3000\u6c5f\u67ab\u6062\u590d\u4e86\u672c\u6765\u9762\u76ee\uff0c\u4ea6\u6765\u63a2\u89c6\u6885\u82b1\uff0c\u4ed6\u8981\u6c42\u8bc1\u4e00\u4e0b\uff0c\u201c\u60d1\u5fc3\u672f\u201d\u5bf9\u4eba\u7684\u5f71\u54cd\uff0c\u80fd\u6709\u591a\u4e45\u3002 \u3000\u3000\u6885\u82b1\u7ecf\u8fc7\u4e86\u8fde\u756a\u7684\u5927\u632b\u6298\u4e4b\u540e\uff0c\u662f\u5426\u8fd8\u628a\u8fd9\u4e2a\u4e00\u5ea6\u523b\u9aa8\u96be\u5fd8\u7684\u7537\u53cb\uff0c\u653e\u5728\u5fc3\u4e0a\u3002 \u3000\u3000\u6b64\u523b\u6d1b\u9633\u5c45\u4e2d\u7684\u996d\u5e84\u3001\u8d4c\u573a\uff0c\u867d\u7136\u4ecd\u7167\u5e38\u8425\u4e1a\uff0c\u4f46\u5374\u5df2\u58c1\u5792\u5206\u660e\uff0c\u539f\u672c\u5206\u5e03\u5728\u5404\u5904\u7684\u5e74\u8f7b\u9556\u624b\uff0c\u5927\u90e8\u5206\u96c6\u4e2d\u9152\u5ba2\u4e70\u9189\u7684\u996d\u5e84\u4e2d\uff0c\u8d4c\u573a\u65b9\u9762\uff0c\u5374\u96c6\u4e2d\u4e86\u5929\u9a6c\u5802\u7684\u4eba\u624b\uff0c\u7ef4\u62a4\u7740\u573a\u5b50\u3002 \u3000\u3000\u6d1b\u9633\u697c\u662f\u6700\u5b89\u5b9a\u7684\u4e00\u4e2a\u5730\u65b9\uff0c\u9752\u51e4\u7684\u8eab\u4efd\uff0c\u5df2\u7ecf\u6446\u660e\u4e86\uff0c\u5929\u9a6c\u5802\u548c\u5185\u5bab\u4e2d\u4eba\uff0c\u90fd\u4e0d\u6562\u4fb5\u72af\u5230\u5979\u3002 \u3000\u3000\u6d1b\u9633\u697c\u7684\u4e8b\uff0c\u4e5f\u5c31\u6ca1\u6709\u4eba\u63d2\u624b\u591a\u7ba1\u4e86\uff0c\u4e00\u5207\u7531\u9752\u51e4\u63a5\u7ba1\u3002 \u3000\u3000\u6700\u91cd\u8981\u7684\u4e00\u4ef6\u4e8b\uff0c\u662f\u5206\u914d\u94f6\u5b50\uff0c\u539f\u672c\u662f\u7edf\u6536\u7edf\u652f\uff0c\u76c8\u4f59\u4e0b\u6765\u7684\u4e09\u5206\u5747\u5206\uff0c\u5982\u4eca\u5374\u6210\u4e86\u4e09\u5206\u5929\u4e0b\uff0c\u5404\u4eba\u6536\u5230\u7684\uff0c\u5c31\u5f52\u5404\u4eba\u6240\u6709 \u3000\u3000\u5316\u540d\u7530\u7389\u7684\u6c5f\u67ab\uff0c\u6b65\u5165\u4e86\u6885\u82b1\u5385\u4e2d\u3002 \u3000\u3000\u8fd9\u91cc\u662f\u6885\u82b1\u4e13\u7528\u7684\u5bb4\u5ba2\u96c5\u5ba4\uff0c\u53ea\u89c1\u623f\u4e2d\u4e00\u7247\u5e7d\u6697\uff0c\u548c\u522b\u5904\u706f\u5149\u8f89\u714c\u4e0d\u5927\u76f8\u540c\u3002 \u3000\u3000\u539f\u6765\uff0c\u6b64\u523b\uff0c\u5df2\u7ecf\u662f\u638c\u706f\u7684\u65f6\u5206\u3002 \u3000\u3000\u8fd9\u65f6\u5019\u7684\u6d1b\u9633\u5c45\u4e2d\u56db\u5b63\u82b1\u5a62\u7684\u4f4f\u5904\uff0c\u65e9\u5df2\u6212\u5907\u68ee\u4e25\uff0c\u5012\u4e0d\u662f\u9632\u5907\u6e38\u5ba2\u8bef\u5165\u5929\u53f0\uff0c\u800c\u662f\u963b\u6b62\u5929\u9a6c\u5802\u4e2d\u4eba\u524d\u5f80\u7aa5\u4f3a\u3002 \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u4e24\u5ea6\u9063\u4eba\u524d\u5f80\u5de1\u89c6\uff0c\u4f46\u90fd\u88ab\u5185\u5bab\u4e2d\u4e25\u5bc6\u7684\u5b88\u536b\u62e6\u4e0b\uff0c\u800c\u4e14\u6001\u5ea6\u575a\u51b3\uff0c\u5b8c\u5168\u6ca1\u6709\u901a\u878d\u7684\u4f59\u5730\uff0c\u6446\u51fa\u4e86\u4e0d\u60dc\u4e00\u6218\u7684\u51b3\u5fc3\u3002 \u3000\u3000\u5927\u548c\u5c1a\u5fc3\u4e2d\u660e\u767d\uff0c\u90a3\u5217\u5165\u7981\u5730\u7684\u6240\u5728\uff0c\u5f88\u53ef\u80fd\u6b63\u5728\u915d\u917f\u7740\u4e00\u573a\u9634\u8c0b\uff0c\u63a9\u853d\u7740\u4e00\u4e9b\u79d8\u5bc6\u3002 \u3000\u3000\u5982\u82e5\u5185\u5bab\u65e9\u5df2\u6709\u4eba\u8d76\u5230\u3002 \u3000\u3000\u90a3\u4eba\u4e5f\u6765\u7684\u5341\u5206\u79d8\u5bc6\uff0c\u5929\u9a6c\u5802\u4e2d\u4eba\u65e9\u5df2\u6d3e\u4e86\u4eba\u76d1\u89c6\u4e86\u5404\u5904\u8981\u9053\uff0c\u4f46\u5374\u6ca1\u6709\u53d1\u73b0\u6709\u5185\u5bab\u4e2d\u9ad8\u624b\u8d76\u5230\u3002 \u3000\u3000\u56db\u5b63\u82b1\u5a62\u7684\u4f4f\u5904\uff0c\u867d\u7136\u4e0d\u5927\uff0c\u4f46\u5374\u96c6\u4e2d\u5728\u4e00\u5904\u3002 \u3000\u3000\u5e73\u5e38\u8fd9\u4e00\u7fa4\u7f8e\u5973\u5c45\u4f4f\u7684\u5730\u65b9\uff0c\u5e76\u975e\u7537\u4eba\u7981\u5730\uff0c\u9047\u4e0a\u4e86\u5927\u51fa\u8840\u7684\u5ba2\u4eba\uff0c\u4e5f\u4f1a\u88ab\u5f15\u5165\u5404\u4eba\u7684\u95fa\u623f\u4e2d\u5c0f\u5750\uff0c\u4f46\u5374\u6709\u4e00\u4e2a\u4e25\u5389\u7684\u89c4\u5b9a\uff0c\u7edd\u4e0d\u80fd\u5728\u8fd9\u91cc\u7ffb\u4e91\u8986\u96e8\uff0c\u9876\u591a\u662f\u697c\u697c\u62b1\u62b1\uff0c\u6e29\u9999\u62e5\u6ee1\u6000\u2026\u2026\u3002 \u3000\u3000\u4f46\u56db\u5b63\u82b1\u5a62\u4e5f\u5e76\u975e\u5b8c\u5168\u65e0\u6cd5\u4eb2\u8fd1\uff0c\u4f46\u90a3\u4e2a\u4ee3\u4ef7\u53ef\u5c31\u5927\u7684\u5413\u4eba\u4e86\u3002\u800c\u4e14\uff0c\u6bcf\u4e2a\u4eba\u7684\u884c\u52a8\uff0c\u90fd\u5341\u5206\u9690\u5bc6\u3002 \u3000\u3000\u5f53\u7136\uff0c\u5979\u4eec\u522b\u6709\u76ee\u7684\u65f6\uff0c\u4e5f\u4f1a\u4e3b\u52a8\u7684\u6295\u6000\u9001\u62b1\u3002 \u3000\u3000\u6c5f\u67ab\u542c\u4e86\u4e03\u5b9d\u548c\u5c1a\u7684\u8bf4\u660e\uff0c\u51b3\u5fc3\u4e00\u63a2\u7a76\u7adf\uff0c\u5148\u56de\u5230\u548c\u6885\u82b1\u7ea6\u5b9a\u7684\u5ba2\u6808\u4e00\u8d9f\uff0c\u5e97\u5c0f\u4e8c\u544a\u8bc9\u4ed6\uff0c\u6885\u82b1\u6ca1\u6709\u6765\u8fc7\u2026\u2026\u3002 \u3000\u3000\u6c5f\u67ab\u5c31\u7acb\u523b\u8d76\u5f80\u6885\u82b1\u5385\u4e2d\u2026\u2026\u3002 \u3000\u3000\u4e5f\u8bb8\u771f\u6709\u9ad8\u4eba\u5230\u6765\uff0c\u70b9\u9192\u4e86\u6885\u82b1\uff0c\u8131\u51fa\u4e86\u60c5\u7f51\uff0c\u4e5f\u8bb8\u5979\u5df2\u7136\u8eab\u53d7\u7981\u5236\uff0c\u65e0\u80fd\u72ec\u81ea\u884c\u52a8\u2026\u2026\u3002 \u3000\u3000\u201c\u662f\u4f60\u2026\u2026\u201d\u6842\u82b1\u7f13\u6b65\u7531\u9ed1\u6697\u7684\u5c4b\u89d2\u4e2d\u884c\u4e86\u51fa\u6765\uff0c\u9053\uff1a\u201c\u6885\u82b1\u8eab\u4f53\u4e0d\u8212\u670d\uff0c\u8981\u4f11\u606f\u51e0\u5929\uff0c\u8fc7\u51e0\u5929\u518d\u6765\u3002\u201d \u3000\u3000\u5979\u4e00\u76f4\u6ca1\u6709\u53d1\u73b0\u5230\u6c5f\u67ab\u7684\u53ef\u7231\uff0c\u5fc3\u4e2d\u5341\u5206\u8f7b\u89c6\uff0c\u8bb2\u5b8c\u8bdd\uff0c\u8fde\u8fde\u6325\u624b\uff0c\u793a\u610f\u6c5f\u67ab\u8d76\u5feb\u79bb\u53bb\u3002 \u3000\u3000\u6842\u82b1\u4e0d\u5728\u706f\u706b\u8f89\u714c\u5904\u63a5\u5f85\u5ba2\u4eba\uff0c\u5374\u8dd1\u5230\u6885\u82b1\u5385\u4e2d\u9ed1\u6697\u4e00\u89d2\u91cc\u8eb2\u8d77\u6765\uff0c\u7528\u5fc3\u4f55\u5728\uff1f \u3000\u3000\u8fd9\u4e2a\u79d8\u5bc6\uff0c\u5f15\u8d77\u4e86\u6c5f\u67ab\u5f3a\u70c8\u7684\u597d\u5947\u3002 \u3000\u3000\u201c\u6842\u82b1\u59d1\u5a18\u554a\uff01\u5c0f\u5f1f\u5df2\u5f88\u4e45\u6ca1\u6709\u89c1\u5230\u6885\u82b1\u59d1\u5a18\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u591a\u4e45\u554a\uff1f\u2026\u2026\u201d\u6842\u82b1\u88ab\u6c5f\u67ab\u6e29\u67d4\u6001\u5ea6\uff0c\u5f15\u8d77\u4e86\u4e00\u4efd\u540c\u60c5\uff0c\u76ee\u5149\u8f6c\u6295\u5728\u6c5f\u67ab\u7684\u8138\u4e0a\u3002 \u3000\u3000\u201c\u5927\u6982\uff0c\u603b\u6709\u4e24\u4e09\u5929\u4e86\u5427\uff01\u2026\u2026\u201d \u3000\u3000\u201c\u5662\uff01\u4e00\u65e5\u4e0d\u89c1\uff0c\u5982\u9694\u4e09\u79cb\u554a\uff01\u2026\u2026\u201d\u6842\u82b1\u6709\u4e9b\u8ba5\u8bbd\u5730\u8bf4\uff0c\u201c\u4e24\u4e09\u5929\u4f60\u5c31\u5fcd\u4e0d\u4f4f\u4e86\uff0c\u7530\u516c\u5b50\uff0c\u6211\u770b\u6885\u82b1\u5c31\u662f\u88ab\u4f60\u8fd9\u4e00\u80a1\u78e8\u52b2\u7ed9\u78e8\u5f97\u53d7\u4e0d\u4e86\uff0c\u624d\u4e0d\u80af\u518d\u89c1\u4f60\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\uff01\u2026\u2026\u4e0d\u662f\u554a\uff01\u2026\u2026\u201d\u6c5f\u67ab\u88c5\u51fa\u4e00\u80a1\u9677\u8eab\u60c5\u6d77\u7684\u8ff7\u60d8\uff0c\u9053\uff1a\u201c\u6211\u60f3\u518d\u89c1\u6885\u82b1\u59d1\u5a18\u4e00\u9762\uff0c\u544a\u8bc9\u5979\u4e00\u4ef6\u91cd\u8981\u7684\u4e8b\uff01\u201d \u3000\u3000\u201c\u544a\u8bc9\u6211\u5427\uff01\u6211\u4f1a\u8f6c\u544a\u597d\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u60f3\u79bb\u5f00\u957f\u5b89\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u8981\u8d70\u4e86\uff1f\u2026\u2026\u201d\u6842\u82b1\u9053\uff1a\u201c\u5230\u54ea\u91cc\u53bb\uff1f\u201d \u3000\u3000\u201c\u662f\u56de\u5bb6\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u5982\u679c\u6211\u751f\u610f\u4f5c\u7684\u987a\u5229\uff0c\u660e\u5e74\uff0c\u518d\u6765\u63a2\u671b\u6885\u82b1\u59d1\u5a18\uff01\u201d\u8f6c\u8eab\u5411\u5916\u884c\u53bb\u3002 \u3000\u3000\u201c\u6162\u4e00\u70b9\u2026\u2026\u201d\u6842\u82b1\u9053\uff1a\u201c\u6211\u4f1a\u544a\u8bc9\u6885\u82b1\u59d1\u5a18\uff0c\u4f60\u660e\u5929\u6b64\u523b\u518d\u6765\u4e00\u8d9f\uff0c\u4e5f\u8bb8\u6885\u82b1\u59d1\u5a18\u4f1a\u62b1\u75c5\u89c1\u4f60\uff0c\u81f3\u5c11\uff0c\u6211\u4f1a\u5728\u8fd9\u91cc\u7b49\u4f60\uff0c\u7ed9\u4f60\u4e00\u4e2a\u4ea4\u4ee3\u3002\u201d \u3000\u3000\u5979\u53ef\u4ee5\u4e0d\u628a\u6c5f\u67ab\u653e\u5728\u773c\u91cc\uff0c\u4f46\u5979\u5374\u4e0d\u6562\u5f00\u7f6a\u6885\u82b1\u3002 \u3000\u3000\u201c\u80fd\u4e0d\u80fd\u9ebb\u70e6\u4f60\u6842\u82b1\u59d1\u5a18\uff0c\u73b0\u5728\u53bb\u544a\u8bc9\u6885\u82b1\u4e00\u58f0\uff1f\u2026\u2026\u201d\u6c5f\u67ab\u8bf4\uff0c\u201c\u6211\u5c31\u5728\u8fd9\u91cc\u7b49\u5979\uff0c\u660e\u5929\u4e2d\u5348\uff0c\u6709\u4e00\u5e2e\u5ba2\u4eba\u8981\u8fd0\u8d27\u5357\u4e0b\uff0c\u5c0f\u5f1f\u51c6\u5907\u548c\u4ed6\u4eec\u540c\u884c\u3002\u201d \u3000\u3000\u201c\u4f60\u5bb6\u4e5f\u5728\u6c5f\u5357\uff1f\u2026\u2026\u201d \u3000\u3000\u201c\u662f\uff01\u5c0f\u5730\u65b9\u676d\u5dde\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u2026\u2026\u201d\u6842\u82b1\u6c89\u541f\u5b50\u4e00\u9635\uff0c\u9053\uff1a\u201c\u597d\u5427\uff01\u4f60\u5750\u5728\u8fd9\u91cc\u522b\u52a8\uff0c\u7b49\u6211\u56de\u6765\u3002\u201d \u3000\u3000\u201c\u591a\u8c22\u59d1\u5a18\u2026\u2026\u201d\u6c5f\u67ab\u7f13\u7f13\u5728\u4e00\u5f20\u6728\u6905\u4e0a\u5750\u4e0b\u3002\u201d \u3000\u3000\u770b\u4ed6\u4e00\u526f\u8bda\u8bda\u656c\u656c\uff0c\u4e3a\u60c5\u60d8\u7136\u7684\u6837\u5b50\uff0c\u6842\u82b1\u5c31\u591a\u77a7\u4e86\u4e24\u773c\uff0c\u8fd9\u4e00\u770b\uff0c\u987f\u89c9\u7740\u82b3\u5fc3\u4e00\u52a8\u2026\u2026\u3002 \u3000\u3000\u53ea\u89c1\u4eba\u4f4e\u9996\u5782\u76ee\u800c\u5750\uff0c\u53cc\u624b\u653e\u5728\u819d\u4e0a\uff0c\u8138\u4e0a\u662f\u4e00\u80a1\u81ea\u601c\u81ea\u82e6\u7684\u795e\u8272\uff0c\u900f\u51fa\u4e86\u65e0\u6bd4\u7684\u6e34\u671b\u548c\u671f\u76fc\uff0c\u4f46\u5374\u53c8\u62d8\u8c28\u7684\u50cf\u4e00\u4e2a\u521a\u62f7\u6253\u8fc7\u7684\u5c0f\u5ab3\u5987\uff0c\u96c6\u51c4\u60f6\u6101\u82e6\u4e8e\u4e00\u8eab\uff0c\u662f\u90a3\u4e48\u52a8\u4eba\u601c\u60dc\uff0c\u662f\u90a3\u4e48\u5f15\u4eba\u540c\u60c5\u3002 \u3000\u3000\u8fd9\u5c31\u6fc0\u8d77\u4e86\u6842\u82b1\u7684\u4fa0\u6c14\uff0c\u548c\u6f5c\u4f0f\u4e8e\u5185\u5fc3\u6df1\u5904\u65e9\u5df2\u88ab\u4e25\u9177\u8bad\u7ec3\u63a9\u906e\u4f4f\u7684\u5973\u4eba\u5929\u6027\u2014\u2014\u6bcd\u7231\u3002 \u3000\u3000\u611f\u89c9\u5230\u8fd9\u4e2a\u7537\u4eba\u662f\u90a3\u4e48\u60b2\u82e6\u65e0\u52a9\uff0c\u518d\u4e5f\u65e0\u6cd5\u63a7\u5236\u81ea\u5df1\uff0c\u7f13\u6b65\u884c\u8fd1\u6c5f\u67ab\uff0c\u4f38\u51fa\u7ea4\u5de7\u7389\u624b\uff0c\u628a\u6c5f\u67ab\u7684\u5934\u8138\uff0c\u62e5\u5165\u6000\u4e2d\uff0c\u67d4\u58f0\u8bf4\u9053\uff1a\u201c\u7530\u516c\u5b50\uff0c\u522b\u8fd9\u6837\u6298\u78e8\u81ea\u5df1\uff0c\u4e0d\u8bba\u4f60\u6709\u4ec0\u4e48\u56f0\u96be\u4e8b\u60c5\uff0c\u6211\u90fd\u4f1a\u5e2e\u52a9\u4f60\uff0c\u4e56\u4e56\u7684\u5750\u7740\uff0c\u6211\u53bb\u53bb\u5c31\u6765\u3002\u201d \u3000\u3000\u5979\u611f\u89c9\u5230\u81ea\u5df1\u6b63\u5728\u5e2e\u52a9\u4e00\u4e2a\u5929\u4e0b\u6700\u9700\u8981\u5e2e\u52a9\u7684\u4eba\uff0c\u8981\u5f53\u4ec1\u4e0d\u8ba9\uff0c\u5168\u529b\u4ee5\u8d74\uff0c\u5185\u5fc3\u4e2d\u6e29\u67d4\u6d0b\u6ea2\uff0c\u7adf\u7136\u4f0f\u4e0b\u8eab\u5b50\uff0c\u9001\u4e0a\u4e86\u4e00\u4e2a\u6df1\u957f\u7684\u9999\u543b\uff0c\u624d\u8f6c\u8eab\u800c\u53bb\u3002 \u3000\u3000\u8fd9\u662f\u5929\u72d0\u592b\u4eba\u2018\u60d1\u5fc3\u672f\u2019\u4e2d\u7684\u82e6\u5b57\u8bc0\uff0c\u7adf\u7136\u4e5f\u6709\u7740\u5982\u6b64\u60ca\u5fc3\u52a8\u9b44\u7684\u5a01\u529b\uff0c\u5f53\u771f\u662f\u827a\u4eba\u4f73\u5883\uff0c\u65e0\u6240\u4e0d\u80fd\u4e86\u3002 \u3000\u3000\u76ee\u7779\u6842\u82b1\u53bb\u8fdc\uff0c\u6c5f\u67ab\u7f13\u7f13\u7ad9\u8d77\u8eab\u5b50\uff0c\u5728\u6885\u82b1\u5385\u4e2d\u641c\u67e5\u4e86\u4e00\u904d\uff0c\u4e0d\u89c1\u6709\u4eba\u6f5c\u4f0f\uff0c\u4e5f\u5c31\u80af\u5b9a\u4e86\u6842\u82b1\u7559\u5728\u8fd9\u5ea7\u6885\u82b1\u5385\u7684\u76ee\u7684\uff0c\u662f\u5728\u7b49\u4eba\uff01 \u3000\u3000\u6c5f\u67ab\u4e0d\u89e3\u7684\u662f\uff0c\u6842\u82b1\u65e2\u7136\u53d7\u547d\u5728\u6b64\u5730\u7b49\u5019\uff0c\u90a3\u4e2a\u4eba\u5b9a\u7136\u5341\u5206\u91cd\u8981\uff0c\u5374\u53c8\u4e3a\u4ec0\u4e48\u4e0d\u80fd\u76f4\u63a5\u5230\u6885\u82b1\u7684\u4f4f\u5904\u5546\u91cf\uff1f \u3000\u3000\u96be\u9053\u6765\u4eba\u4e0d\u662f\u5185\u5bab\u4e2d\u4eba\uff1f \u3000\u3000\u6240\u4ee5\uff0c\u5fc5\u987b\u5148\u5728\u6885\u82b1\u5385\u4e2d\u89c1\u9762\uff0c\u660e\u767d\u4e86\u6765\u4eba\u7684\u7528\u5fc3\u540e\uff0c\u518d\u4f5c\u51b3\u5b9a\u3002 \u3000\u3000\u5982\u679c\u4e0d\u662f\u5185\u5bab\u4e2d\u4eba\uff0c\u6765\u4eba\u53c8\u4f1a\u662f\u4e2a\u4ec0\u4e48\u8eab\u4efd\u5462\uff1f \u3000\u3000\u6c5f\u67ab\u731c\u6d4b\u4e0d\u51fa\uff0c\u4f46\u53ef\u4ee5\u9884\u6599\u7684\u662f\u5728\u5982\u6b64\u7d27\u5f20\u65f6\u523b\uff0c\u6885\u82b1\u7edd\u5bf9\u4e0d\u4f1a\u6709\u95f2\u60c5\u9038\u81f4\uff0c\u53bb\u63a5\u89c1\u4e00\u4e2a\u548c\u5927\u5c40\u5168\u7136\u65e0\u5173\u7684\u4eba\uff0c\u8fd9\u4e2a\u4eba\u7a76\u7adf\u662f\u8c01\uff1f \u3000\u3000\u5fd6\u601d\u4e4b\u95f4\uff0c\u5fae\u98ce\u98d8\u7136\uff0c\u6842\u82b1\u5df2\u98de\u8dc3\u800c\u5165\uff0c\u7ad9\u5728\u6c5f\u67ab\u8eab\u524d\uff0c\u9053\uff1a\u201c\u7530\u516c\u5b50\uff0c\u4e8b\u60c5\u6709\u4e9b\u53d8\u5316\uff0c\u6682\u65f6\u65e0\u6cd5\u628a\u6d88\u606f\u4f20\u7ed9\u6885\u82b1\uff0c\u8fd9\u91cc\u4f60\u4e0d\u4fbf\u4e45\u7559\uff0c\u5feb\u4e9b\u8d70\u5427\uff01\u201d \u3000\u3000\u5979\u53d7\u60d1\u4e0d\u6df1\uff0c\u8fd9\u4e00\u9635\u5954\u8d70\u6765\u53bb\uff0c\u4f3c\u662f\u5df2\u6e05\u9192\u4e0d\u5c11\u3002 \u3000\u3000\u6c5f\u67ab\u4e5f\u660e\u767d\u518d\u7559\u65e0\u76ca\uff0c\u7ad9\u8d77\u8eab\u5b50\uff0c\u51c6\u5907\u79bb\u53bb\uff0c\u5ffd\u89c1\u7ea2\u706f\u4e00\u95ea\u800c\u9690\u3002 \u3000\u3000\u6842\u82b1\u7a81\u7136\u51fa\u624b\uff0c\u4e00\u628a\u6293\u4f4f\u6c5f\u67ab\u7684\u53f3\u8155\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u8d70\u4e0d\u4e86\u5566\uff01 \u3000\u3000\u85cf\u5728\u8fd9\u91cc\u5427\uff01\u4f60\u81ea\u5df1\u5343\u4e07\u5c0f\u5fc3\uff0c\u5f62\u8ff9\u8d25\u9732\uff0c\u5fc5\u6b7b\u65e0\u7591\uff0c\u6211\u4e5f\u6551\u4e0d\u4e86\u4f60\u3002\u201d \u3000\u3000\u4e0d\u5f85\u6c5f\u67ab\u56de\u7b54\uff0c\u7275\u7740\u4ed6\u8d70\u5165\u5c4b\u89d2\u4e00\u5f20\u6728\u51e0\u4e4b\u65c1\u3002 \u3000\u3000\u6728\u51e0\u4e0a\u7f6e\u7740\u4e00\u53ea\u5f88\u5927\u7684\u82b1\u74f6\uff0c\u63d2\u4e86\u51e0\u652f\u665a\u51ac\u7684\u814a\u6885\uff0c\u6563\u53d1\u5e7d\u5e7d\u6e05\u9999\u3002 \u3000\u3000\u8fd9\u662f\u5f88\u597d\u7684\u85cf\u8eab\u6240\u5728\uff0c\u7f6e\u82b1\u7684\u6728\u51e0\u524d\u9762\uff0c\u6709\u4e24\u5f20\u592a\u5e08\u6905\u6321\u4f4f\uff0c\u7559\u4e0b\u4e86\u591f\u5927\u7684\u7a7a\u95f4\u3002 \u3000\u3000\u6842\u82b1\u5c31\u5728\u5de6\u9762\u90a3\u5f20\u592a\u5e08\u6905\u4e0a\u5750\u4e0b\u3002 \u3000\u3000\u6c5f\u67ab\u5fc3\u4e2d\u5fd6\u9053\uff1a\u201c\u65e0\u5fc3\u63d2\u67f3\u67f3\u6210\u836b\uff0c\u60f3\u4e0d\u5230\u4f1a\u6709\u8fd9\u6837\u4e00\u4e2a\u610f\u5916\u7684\u673a\u4f1a\uff0c\u89c1\u8bc6\u4e00\u4e0b\u6765\u8bbf\u7684\u795e\u79d8\u4eba\u7269\u3002\u201d \u3000\u3000\u4f46\u95fb\u4e00\u9635\u8f7b\u5fae\u7684\u6b65\u5c65\u4f20\u5165\u8033\u9645\uff0c\u884c\u4eba\u4e86\u6885\u82b1\u5385\u4e2d\u3002 \u3000\u3000\u6c5f\u67ab\u51dd\u795e\u503e\u542c\uff0c\u53d1\u89c9\u8fdb\u5165\u5385\u4e2d\u7684\u4eba\uff0c\u81f3\u5c11\u6709\u4e09\u4e2a\u4ee5\u4e0a\u3002 \u3000\u3000\u6842\u82b1\u8fce\u4e86\u4e0a\u53bb\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u6885\u987b\u900a\u96ea\u4e09\u5206\u767d\u2026\u2026\u201d \u3000\u3000\u4e00\u4e2a\u4f4e\u6c89\u7684\u7537\u5b50\u58f0\u97f3\uff0c\u9053\uff1a\u201c\u96ea\u5374\u8f93\u6885\u4e00\u6bb5\u9999\u3002\u201d \u3000\u3000\u6c5f\u67ab\u6697\u9053\uff1a\u201c\u8fd9\u6697\u8bed\u5012\u4e5f\u8ba2\u7684\u5ba2\u6c14\uff0c\u5f7c\u6b64\u8c26\u8ba9\u4e00\u756a\u3002\u201d \u3000\u3000\u201c\u6842\u82b1\u5949\u547d\u8fce\u5ba2\uff0c\u9601\u4e0b\u53ef\u5426\u53d6\u4e0b\u9762\u7eb1\uff0c\u8ba9\u8d31\u59be\u62dc\u8bc6\u91d1\u9762\u3002\u201d \u3000\u3000\u201c\u8001\u592b\u8001\u77e3\uff01\u7f9e\u4ee5\u4e11\u5bb9\u89c1\u59d1\u5a18\u3002\u201d \u3000\u3000\u201c\u5509\uff01\u8fd9\u4e48\u8bf4\u6765\uff0c\u620f\u59be\u662f\u65e0\u7f18\u8bc6\u8346\u4e86\uff01\u201d \u3000\u3000\u201c\u6765\u65e5\u65b9\u957f\uff0c\u59d1\u5a18\u53c8\u4f55\u5fc5\u6025\u5728\u4e00\u65f6\u5462\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u5fd6\u9053\uff1a\u201c\u6625\u3001\u590f\u3001\u79cb\u3001\u51ac\u56db\u5b63\u82b1\u5a62\uff0c\u4e0d\u4f46\u59ff\u8272\u79c0\u4e3d\uff0c\u6b66\u529f\u9ad8\u5f3a\uff0c\u4f3c\u662f\u4e5f\u8bfb\u4e86\u4e0d\u5c11\u7684\u8bd7\u4e66\uff0c\u624d\u517c\u6587\u6b66\uff0c\u6765\u8bbf\u4e4b\u4eba\uff0c\u7adf\u4e5f\u80fd\u5bf9\u7b54\u5f97\u4f53\uff0c\u4e0d\u89c1\u7c97\u4fd7\uff0c\u7a76\u7adf\u662f\u4ec0\u4e48\u6837\u7684\u4eba\u7269\u5462\uff1f\u201d \u3000\u3000\u4f46\u95fb\u6842\u82b1\u8bf4\u9053\uff1a\u201c\u5251\u6447\u5c71\u6cb3\u52a8\u2026\u2026\u201d \u3000\u3000\u201c\u5200\u6620\u65e5\u6708\u660e\uff0c\u6842\u82b1\u59d1\u5a18\uff0c\u8001\u592b\u4e0d\u80fd\u803d\u8bef\u592a\u4e45\uff0c\u52b3\u8bf7\u65e9\u4e9b\u901a\u62a5\u5427\uff01\u201d \u3000\u3000\u201c\u597d\uff01\u6842\u82b1\u5e26\u8def\u3002\u201d\u4e3e\u6b65\u5411\u5916\u884c\u53bb\u3002 \u3000\u3000\u53ea\u542c\u8863\u8896\u6325\u52a8\u4e4b\u58f0\uff0c\u8033\u9645\u54cd\u8d77\u90a3\u4f4e\u6c89\u7684\u58f0\u97f3\uff0c\u9053\uff1a\u201c\u5ba4\u4e2d\u6709\u4eba\uff0c\u8001\u592b\u8d4f\u4ed6\u4e00\u628a\u65ad\u9b42\u9999\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u5fc3\u4e2d\u4e00\u52a8\uff0c\u8d76\u5fd9\u95ed\u4f4f\u547c\u53ca\uff0c\u53ea\u53ef\u60dc\uff0c\u4ecd\u662f\u665a\u4e86\u4e00\u6b65\uff0c\u4e00\u5473\u5f02\u9999\u6251\u9f3b\uff0c\u7acb\u523b\u6655\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u597d\u9738\u9053\u7684\u8ff7\u9999\u3002 \u3000\u3000\u6c5f\u6e56\u4e0a\u51f6\u9669\u4e07\u7aef\uff0c\u6709\u4e9b\u65f6\u5019\uff0c\u867d\u6709\u7740\u7edd\u4e16\u6b66\u529f\uff0c\u5374\u4e5f\u65e0\u6cd5\u6d3e\u4e0a\u7528\u573a\uff0c\u4fdd\u5168\u81ea\u5df1\u3002 \u3000\u3000\u68a6\u91cc\u4e0d\u77e5\u8eab\u662f\u5ba2\uff0c\u6c5f\u67ab\u6e05\u9192\u7684\u65f6\u5019\uff0c\u53d1\u73b0\u81ea\u5df1\u6b63\u8eba\u5728\u4e00\u95f4\u5e03\u8bbe\u7b80\u5355\u7684\u96c5\u5b9d\u4e4b\u4e2d\u3002 \u3000\u3000\u8bf4\u7b80\u5355\uff0c\u786e\u5b9e\u7b80\u5355\uff0c\u8fd9\u623f\u4e2d\u9664\u4e86\u90a3\u5f20\u5bbd\u5927\u7684\u53cc\u4eba\u5e8a\u5916\uff0c\u53ea\u6709\u4e00\u4e2a\u6728\u5236\u7684\u957f\u6761\u684c\u5b50\uff0c\u4e24\u5f20\u7af9\u6905\uff0c\u684c\u5b50\u4e0a\u653e\u7f6e\u7740\u4e00\u9762\u94dc\u955c\u3002 \u3000\u3000\u5012\u662f\u5e8a\u4e0a\u7684\u9526\u5e10\u7ee3\u88ab\uff0c\u9887\u6709\u5bcc\u8c6a\u4e4b\u5bb6\u7684\u6c14\u6168\u3002 \u3000\u3000\u53e6\u4e00\u4e2a\u7279\u8272\u662f\u623f\u95f4\u5f88\u5c0f\uff0c\u4f46\u5374\u6253\u626b\u7684\u975e\u5e38\u5e72\u51c0\u3002 \u3000\u3000\u770b\u7a97\u5916\u65e5\u5149\u660e\u4eae\uff0c\u5e94\u8be5\u662f\u8fd1\u5348\u65f6\u5206\u3002 \u3000\u3000\u60f3\u6628\u591c\u6655\u5012\u65f6\uff0c\u662f\u534e\u706f\u521d\u4e0a\uff0c\u591c\u5e55\u4f4e\u5782\u65f6\u5206\uff0c\u8fd9\u4e00\u6655\u7adf\u7136\u8fc7\u53bb\u4e03\u516b\u4e2a\u65f6\u8fb0\u4e4b\u4e45\uff0c\u4e0d\u7981\u6697\u6697\u4e00\u53f9\uff0c\u5fd6\u9053\uff1a\u201c\u6c5f\u67ab\u554a\uff0c\u6c5f\u67ab\uff0c\u4f60\u5982\u6b64\u7684\u5927\u610f\u7c97\u5fc3\uff0c\u80fd\u6709\u51e0\u6761\u547d\u4e22\u2026\u2026\u201d \u3000\u3000\u201c\u6c5f\u5927\u54e5\uff0c\u4f60\u7ec8\u4e8e\u9192\u8fc7\u6765\u4e86\uff01\u2026\u2026\u201d\u541f\u96ea\u7b11\u5bb9\u5982\u82b1\uff0c\u624b\u6367\u4e00\u4e2a\u52a0\u76cd\u74f7\u7897\uff0c\u7f13\u6b65\u884c\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u6c5f\u67ab\u633a\u8eab\u5750\u8d77\uff0c\u624d\u53d1\u89c9\u4e0a\u8eab\u5149\u88f8\uff0c\u5403\u4e86\u4e00\u60ca\uff0c\u53c8\u6025\u6025\u8eba\u4e86\u4e0b\u53bb\uff0c\u76ee\u5149\u56db\u987e\u3002 \u3000\u3000\u53f6\u96ea\u7f13\u7f13\u628a\u624b\u91cc\u7684\u74f7\u7897\uff0c\u653e\u5728\u6728\u684c\u4e0a\uff0c\u987a\u624b\u62c9\u8fc7\u4e00\u5f20\u7af9\u6905\uff0c\u5728\u5e8a\u8fb9\u5750\u4e0b\uff0c\u9053\uff1a\u201c\u6c5f\u5927\u54e5\uff0c\u4f60\u770b\u4ec0\u4e48\u5440\uff1f\u201d \u3000\u3000\u201c\u8863\u670d\uff0c\u6211\u7684\u8863\u670d\u5462\uff1f\u201d \u3000\u3000\u201c\u6d17\u4e86\u2026\u2026\u201d\u53f6\u96ea\u9053\uff1a\u201c\u8863\u670d\u3001\u88e4\u5b50\uff0c\u8fd8\u6709\u4e00\u53cc\u81ed\u889c\u5b50\uff0c\u5168\u90fd\u6d17\u4e86\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u4f46\u624b\u4e00\u6478\uff0c\u8fde\u5185\u88e4\u4e5f\u672a\u7a7f\u7740\uff0c\u53ef\u771f\u662f\u88f8\u5367\u88ab\u4e2d\uff0c\u5bf8\u7f15\u672a\u7740\uff0c\u4e0d\u7981\u4e00\u76b1\u7709\u5934\uff0c\u9053\uff1a \u3000\u3000\u201c\u6211\u7684\u5185\u8863\u88e4\u2026\u2026\u201d \u3000\u3000\u201c\u8fde\u4f60\u7684\u4eba\u90fd\u6e05\u6d17\u4e86\uff0c\u5185\u8863\u88e4\u8fd8\u4f1a\u7a7f\u5728\u8eab\u4e0a\u4e0d\u6210\uff0c\u2026\u2026\u201d\u541f\u96ea\u7b11\u9053\uff1a\u201c\u6c5f\u5927\u54e5\uff0c\u4f60\u662f\u4e0d\u662f\u6709\u70b9\u5bb3\u7f9e\u554a\uff01\u201d \u3000\u3000\u6c5f\u67ab\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\u51fa\u7684\u4e3b\u610f\uff1f\u4ec0\u4e48\u4eba\u5e2e\u6211\u6d17\u6fa1\uff1f\u201d \u3000\u3000\u201c\u4e3b\u610f\u662f\u5f20\u59e8\u62ff\u7684\uff0c\u5979\u8bf4\u4f60\u5168\u8eab\u810f\u81ed\uff0c\u975e\u8981\u597d\u597d\u7684\u6e05\u6d17\u4e00\u4e0b\u4e0d\u53ef\u2026\u2026\u201d\u53f6\u96ea\u6696\u6627\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6709\u4e8b\u5f1f\u5b50\u670d\u5176\u52b3\uff0c\u5f20\u59e8\u4e0b\u4e86\u547d\u4ee4\uff0c\u6211\u4eec\u53ea\u597d\u52a8\u624b\u8131\u4f60\u7684\u8863\u670d\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u4f60\u4eec\uff1f\u8fd8\u6709\u8c01\u5440\uff01\u201d \u3000\u3000\u201c\u6211\u4e86\u2026\u2026\u201d\u541f\u971c\u6b3e\u6b3e\u83b2\u6b65\u8d70\u8fdb\u6765\u4e86\uff0c\u63a5\u9053\uff1a\u201c\u597d\u4e00\u9635\u5237\u6d17\u5440\uff01\u59d0\u548c\u6211\u4e24\u4e2a\u4eba\uff0c\u628a\u4f60\u653e\u5728\u4e00\u4e2a\u5927\u6728\u76c6\u91cc\uff0c\u8db3\u8db3\u6d17\u4e86\u534a\u4e2a\u65f6\u8fb0\uff01\u201d \u3000\u3000\u201c\u6211\u6709\u90a3\u4e48\u4e2a\u810f\u6cd5\u5417\uff1f\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u6211\u8bb0\u5f97\u6628\u5929\u624d\u6d17\u8fc7\u6fa1\u554a\uff01\u201d \u3000\u3000\u201c\u5927\u6982\u662f\u6b8b\u8102\u5269\u7c89\u5427\u2026\u2026\u201d\u541f\u971c\u9053\uff1a\u201c\u5f20\u59e8\u8bf4\u4f60\u810f\uff0c\u6211\u4eec\u5c31\u53ea\u597d\u62fc\u547d\u5e2e\u4f60\u6d17\u4e86\uff0c\u6c5f\u5927\u54e5\uff0c\u4f60\u77e5\u9053\u4e48\uff1f\u4f60\u7684\u8138\u4e0a\u3001\u5634\u4e0a\uff0c\u90fd\u7559\u7740\u6b8b\u4f59\u7684\u80ed\u8102\u3002\u201d \u3000\u3000\u6c5f\u67ab\u60f3\u8d77\u6628\u591c\u6842\u82b1\u90a3\u6df1\u957f\u7684\u4e00\u543b\uff0c\u987f\u89c9\u53cc\u988a\u98de\u7ea2\u3002 \u3000\u3000\u201c\u662f\u561b\uff01\u6708\u4e0a\u67f3\u68a2\u5934\uff0c\u4eba\u7ea6\u9ec4\u660f\u540e\u2026\u2026\u201d\u541f\u96ea\u7b11\u63a5\u9053\uff1a\u201c\u5e94\u8be5\u662f\u7389\u4eba\u5728\u62b1\uff0c\u8f7b\u7231\u5bc6\u601c\uff0c\u600e\u4e48\u4f1a\u95f9\u7684\u8eab\u4e2d\u8ff7\u836f\uff0c\u6655\u8ff7\u5f97\u4e0d\u9192\u4eba\u4e8b\uff0c\u6c5f\u5927\u54e5\uff0c\u4f60\u548c\u4ec0\u4e48\u59f9\u5973\u9b3c\u602a\u7ea6\u4f1a\uff0c\u95f9\u51fa\u4e86\u8fd9\u4e48\u4e00\u4e2a\u5927\u715e\u98ce\u666f\u7684\u7ed3\u5c40\u554a\uff01\u201d \u3000\u3000\u201c\u5f53\u5fc3\u554a\uff01\u9752\u7af9\u86c7\u513f\u53e3\uff0c\u9ec4\u8702\u5c3e\u4e0a\u9488\uff0c\u4e8c\u7269\u4e0d\u7b97\u6bd2\u2026\u2026\u201d\u541f\u971c\u4f4e\u4e0b\u5934\u53bb\uff0c\u5c0f\u5de7\u7684\u6a31\u5507\uff0c\u51e0\u4e4e\u78b0\u5728\u4e86\u6c5f\u67ab\u7684\u5634\u4e0a\uff0c\u9053\uff1a\u201c\u6700\u72e0\u5973\u4eba\u5fc3\u54ea\uff01\u201d \u3000\u3000\u6c5f\u67ab\u6025\u6025\u8f6c\u8fc7\u8138\u53bb\uff0c\u9053\uff1a\u201c\u79bb\u8fdc\u70b9\uff0c\u6c5f\u5927\u54e5\u7684\u5634\u5df4\u5f88\u810f\uff01\u201d \u3000\u3000\u201c\u5e78\u597d\uff0c\u6628\u5929\u628a\u4f60\u6d17\u5e72\u51c0\u4e86\u2026\u2026\u201d\u541f\u971c\u7b11\u9053\uff1a\u201c\u6c5f\u5927\u54e5\u53ea\u559c\u6b22\u5403\u80ed\u8102\uff0c\u6211\u548c\u59d0\u90fd\u8981\u8ddf\u5f20\u59e8\u5b66\u7740\u53bb\u6d82\u8102\u62b9\u7c89\u4e86\u3002\u201d \u3000\u3000\u4e8c\u5973\u5e7c\u5e74\u4e60\u7ec3\u5929\u72d0\u795e\u529f\uff0c\u5168\u8eab\u80a4\u8272\u767d\u91cc\u900f\u7ea2\uff0c\u6709\u5982\u4e09\u6708\u4e2d\u7684\u6843\u82b1\uff0c\u52a8\u4eba\u5df2\u6781\uff0c\u5929\u4e0b\u6700\u597d\u7684\u8102\u7c89\uff0c\u4e5f\u8c03\u4e0d\u51fa\u90a3\u4e48\u7f8e\u7684\u989c\u8272\u3002 \u3000\u3000\u201c\u4e0d\u4e0d\u4e0d\u2026\u2026\u201d\u6c5f\u67ab\u6025\u6025\u63a5\u9053\uff1a\u201c\u4f60\u4eec\u7684\u80a4\u8272\u8273\u5982\u6625\u82b1\uff0c\u5343\u4e07\u522b\u7528\u8102\u7c89\u63a9\u906e\u53bb\u73e0\u7389\u4e3d\u8d28\uff0c\u56fd\u8272\u5929\u9999\u3002\u201d \u3000\u3000\u201c\u6c5f\u5927\u54e5\u7684\u610f\u601d\uff0c\u53ef\u662f\u8bf4\u6211\u4eec\u4e3d\u8d28\u5929\u751f\uff0c\u957f\u7684\u5dee\u5f3a\u4eba\u610f\u4e86\u2026\u2026\u201d\u541f\u96ea\u9053\uff1a\u201c\u4e0d\u77e5\u662f\u7531\u8877\u4e4b\u8a00\uff0c\u8fd8\u662f\u54c4\u6211\u4eec\u51e0\u53e5\uff0c\u8ba9\u6211\u4eec\u542c\u5f97\u9ad8\u5174\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u5fc3\u4e2d\u660e\u767d\uff0c\u72d0\u6027\u591a\u7591\uff0c\u8fd9\u51e0\u53e5\u8bdd\uff0c\u4f3c\u662f\u73a9\u7b11\uff0c\u4e5f\u662f\u8bd5\u63a2\uff0c\u5982\u662f\u8a00\u8bed\u4e2d\u6709\u4e86\u9519\u5931\uff0c\u5f88\u53ef\u80fd\u4f1a\u523a\u4f24\u4e8c\u5973\u7684\u5fc3\u3002 \u3000\u3000\u5929\u72d0\u6b66\u5b66\uff0c\u987a\u60c5\u653e\u7eb5\uff0c\u5979\u4eec\u4e5f\u8bb8\u4e0d\u4f1a\u8ba1\u8f83\u5fc3\u4e2d\u60c5\u90ce\uff0c\u6709\u591a\u5c11\u7ea2\u7c89\u5973\u53cb\uff0c\u4f46\u5374\u5341\u5206\u91cd\u89c6\u81ea\u5df2\u5728\u60c5\u90ce\u5fc3\u4e2d\u7684\u5730\u4f4d\uff0c\u7528\u60c5\u6df1\u5982\u523b\u9aa8\uff0c\u5916\u8868\u957f\u5a9a\uff0c\u5185\u5fc3\u5374\u4e13\u60c5\u8d1e\u6d01\u3002 \u3000\u3000\u4ed6\u4e60\u7ec3\u5929\u72d0\u6b66\u529f\uff0c\u77e5\u4e4b\u751a\u6df1\u3002 \u3000\u3000\u6c5f\u67ab\u5fc3\u4e2d\u5341\u5206\u4e0d\u613f\u627f\u8ba4\u4e8c\u5973\u662f\u7ea2\u7c89\u77e5\u5df1\uff0c\u95fa\u4e2d\u817b\u53cb\uff0c\u4f46\u7ecf\u8fc7\u6628\u591c\u90a3\u4e00\u6b21\u88f8\u8eab\u6e05\u6d17\uff0c\u6050\u5df2\u7ecf\u96be\u518d\u63a8\u6258\u4e4b\u8bcd\u2026\u2026\u3002 \u3000\u3000\u518d\u770b\u4e8c\u5973\uff0c\u56db\u9053\u6e05\u6f88\u7684\u76ee\u5149\uff0c\u51dd\u795e\u6df1\u6ce8\uff0c\u5145\u6ee1\u7740\u6df1\u60c5\u671f\u5f85\uff0c\u53ea\u597d\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4e2a\u4eba\uff0c\u5f53\u771f\u4e00\u70b9\u4e5f\u4e0d\u4e86\u89e3\u81ea\u5df1\u5417\uff1f\u201d \u3000\u3000\u201c\u4e0d\u4e86\u89e3\u2026\u2026\u201d\u541f\u971c\u5fae\u5fae\u6447\u5934\uff0c\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u6211\u4eec\u8981\u6c5f\u5927\u54e5\u7ed9\u6211\u4eec\u4e00\u4e2a\u54c1\u7ea7\uff0c\u4f7f\u6211\u4eec\u77e5\u9053\u81ea\u5df1\u6709\u591a\u5c11\u7684\u4efd\u91cf\uff0c\u4e5f\u597d\u2026\u2026\u201d \u3000\u3000\u4e5f\u597d\u600e\u4e48\u6837\uff0c\u541f\u971c\u6ca1\u6709\u8bf4\u4e0b\u53bb\uff0c\u4f46\u6c5f\u67ab\u5374\u63a5\u4e86\u53e3\uff0c\u9053\uff1a\u201c\u5982\u8bba\u54c1\u7ea7\uff0c\u6c5f\u5927\u54e5\u7ed9\u4f60\u4eec\u5b9a\u7ea7\u4e5d\u54c1\u2026\u2026\u201d \u3000\u3000\u201c\u554a\uff01\u2026\u2026\u8fde\u4e2a\u4e03\u54c1\u7684\u77e5\u53bf\u4e5f\u8f6e\u4e0d\u5230\u554a\uff01\u2026\u2026\u201d\u53f6\u96ea\u9053\uff1a\u201c\u52ff\u602a\u6211\u4eec\u767e\u822c\u7684\u5949\u8fce\u53d6\u60a6\uff0c\u6c5f\u5927\u54e5\u5374\u4e00\u76f4\u4e0d\u628a\u6211\u4eec\u653e\u5728\u773c\u4e2d\u4e86\uff1f\u201d \u3000\u3000\u201c\u59d0\uff01\u4e5d\u54c1\u5b98\u5458\uff0c\u5927\u6982\u53ea\u662f\u4e2a\u9a7f\u957f\u7684\u8eab\u4efd\uff0c\u2026\u2026\u201d\u541f\u971c\u795e\u60c5\u9eef\u7136\u5730\u8bf4\uff0c\u201c\u8fc7\u53bb\uff0c\u54b1\u4eec\u662f\u81ea\u4f5c\u591a\u60c5\uff0c\u4eca\u540e\uff0c\u53ef\u8981\u81ea\u91cd\u81ea\u7231\u4e00\u4e9b\uff0c\u4e0d\u8981\u518d\u60f9\u4eba\u751f\u538c\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u5bf9\uff01\u4eba\u95f4\u4e5d\u54c1\uff0c\u53ea\u662f\u4e2a\u5c0f\u5c0f\u7684\u9a7f\u957f\u2026\u2026\u201d \u3000\u3000\u541f\u971c\u8bcd\u950b\u5982\u5200\u7684\u6253\u65ad\u4e86\u6c5f\u67ab\u7684\u8bdd\uff0c\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u6211\u4eec\u8fde\u4eba\u4e5f\u4e0d\u662f\u4e86\uff1f\u96be\u9053\u662f\u4e00\u5bf9\u4e0d\u4fee\u79c1\u5fb7\u7684\u5c0f\u72d0\u5973\uff1f\u201d \u3000\u3000\u201c\u4f60\u4eec\u5982\u662f\u4e00\u5bf9\u72d0\u5973\uff0c\u6c5f\u5927\u54e5\u5c82\u4e0d\u6210\u4e86\u4e00\u53ea\u5927\u72d0\u72f8\uff0c\u4f46\u6211\u7684\u610f\u601d\u662f\uff0c\u4f60\u4eec\u786e\u4e0d\u662f\u4eba\uff0c\u662f\u7476\u6c60\u4e2d\u7684\u4e5d\u54c1\u83b2\u82b1\uff0c\u8c2a\u4e0b\u51e1\u95f4\u7684\u5c0f\u4ed9\u5973\u2026\u2026\u201d \u3000\u3000\u201c\u771f\u7684\u5417\uff01\u2026\u2026\u201d\u541f\u96ea\u7684\u53cc\u76ee\u4e2d\u6cea\u5149\u6d8c\u73b0\uff0c\u9053\uff1a\u201c\u4e0d\u4f1a\u662f\u9a97\u6211\u4eec\u7684\u5427\uff01\u201d \u3000\u3000\u201c\u662f\u771f\u7684\uff0c\u6c5f\u5927\u54e5\u8a00\u51fa\u8877\u8bda\uff0c\u5982\u8bba\u4f60\u4eec\u4e4b\u7f8e\uff0c\u5f53\u4eca\u7f8e\u5973\uff0c\u6050\u5df2\u65e0\u4eba\u80fd\u591f\u53ca\u5f97\u4e86\u3002\u201d \u3000\u3000\u201c\u662f\u4e0d\u662f\u7edd\u4e16\u7f8e\u5973\uff0c\u6211\u4eec\u5e76\u4e0d\u653e\u5728\u5fc3\u4e0a\u2026\u2026\u201d\u541f\u96ea\u8bf4\uff0c\u201c\u6211\u4eec\u4e60\u7ec3\u5929\u72d0\u795e\u529f\uff0c\u77e5\u9053\u5408\u7c4d\u53cc\u4fee\u662f\u5929\u72d0\u795e\u529f\u4e2d\u6700\u9ad8\u7684\u5883\u754c\uff0c\u542c\u8bf4\uff0c\u4e0d\u4f46\u53ef\u4ee5\u5ef6\u5e74\u76ca\u5bff\uff0c\u6c38\u4fdd\u9752\u6625\uff0c\u800c\u4e14\uff0c\u4e5f\u53ef\u80fd\u6500\u767b\u4ed9\u754c\uff0c\u957f\u751f\u4e0d\u8001\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u8c01\u544a\u8bc9\u4f60\u8fd9\u4e9b\u4e8b\u60c5\uff1f\u201d \u3000\u3000\u201c\u600e\u4e48\uff1f\u6211\u8bf4\u7684\u4e0d\u5bf9\u5417\u2026\u2026\u201d\u53f6\u96ea\u9053\uff1a\u201c\u6211\u548c\u971c\u59b9\u7ec3\u4e86\u5341\u51e0\u5e74\u7684\u5929\u72d0\u795e\u529f\uff0c\u5fc3\u7075\u4e0a\u4e5f\u6709\u4e86\u8fd9\u79cd\u611f\u89c9\uff01\u201d \u3000\u3000\u201c\u4ed9\u9053\u65e0\u51ed\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u7fbd\u5316\u767b\u4ed9\uff0c\u8089\u4f53\u98de\u5347\uff0c\u53ea\u662f\u4e00\u79cd\u4f20\u8bf4\uff0c\u6c5f\u6e56\u5947\u4eba\u4e2d\uff0c\u4ee5\u5f20\u4e09\u4e30\u8ffd\u6c42\u4ed9\u4e1a\u6700\u4e3a\u5177\u4f53\uff0c\u79ef\u6781\uff0c\u4f46\u4e5f\u6ca1\u6709\u7559\u4e0b\u53ef\u8d44\u8003\u8bc1\u7684\u4ed9\u8ff9\uff0c\u541f\u96ea\uff0c\u4f60\u5982\u5fc3\u5b58\u6b64\u5ff5\uff0c\u53ea\u6015\u5927\u5927\u7684\u5931\u671b\u4e86\u3002\u201d \u3000\u3000\u201c\u6c5f\u5927\u54e5\uff0c\u6211\u4e0d\u8981\u8ffd\u6c42\u4ed9\u4e1a\uff01\u2026\u2026\u201d\u541f\u971c\u7b11\u9053\uff1a\u201c\u6211\u53ea\u8981\u5feb\u4e50\u5145\u5b9e\u7684\u6d3b\u4e2a\u51e0\u5341\u5e74\u4e5f\u5c31\u591f\u4e86\uff0c\u6211\u8981\u751f\u513f\u80b2\u5973\uff0c\u517b\u4e24\u4e2a\u5a03\u5a03\uff0c\u8fc7\u8fc7\u5f53\u5a18\u7684\u763e\u5934\uff0c\u53ef\u662f\uff0c\u6211\u4eec\u7ec3\u7684\u6b66\u529f\uff0c\u9690\u9690\u6709\u91c7\u8865\u7684\u6280\u5de7\uff0c\u90a3\u8fd8\u80fd\u5ac1\u4f5c\u5e38\u4eba\u5ab3\u5987\u5417\uff1f\u2026\u2026\u201d \u3000\u3000\u201c\u53ea\u8981\u4f60\u4e0d\u5b58\u5fc3\u5bb3\u4eba\uff0c\u5c31\u4e0d\u4f1a\u4f24\u4eba\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u5929\u72d0\u795e\u529f\u867d\u7136\u8be1\u5f02\uff0c\u4f46\u5374\u65e0\u59a8\uff0c\u5ac1\u4e86\u4eba\u4e00\u6837\u53ef\u4ee5\u8fc7\u6b63\u5e38\u7684\u65e5\u5b50\u2026\u2026\u201d \u3000\u3000\u201c\u90a3\u591a\u9ebb\u70e6\u5440\uff01\u95fa\u623f\u79c1\u60c5\uff0c\u8fd8\u8981\u5904\u5904\u5c0f\u5fc3\u2026\u2026\u201d\u541f\u971c\u9053\uff1a\u201c\u5c82\u4e0d\u662f\u592a\u8f9b\u82e6\u4e86\uff0c\u6211\u770b\u54ea\uff01 \u3000\u3000\u8fd8\u662f\u8ddf\u7740\u4f60\u8fd9\u53ea\u5927\u72d0\u72f8\u6bd4\u8f83\u5b89\u5168\uff0c\u4e5f\u514d\u5f97\u5931\u8db3\u6210\u9519\uff0c\u9020\u6068\u4eba\u95f4\uff0c\u5c0f\u72d0\u5973\u914d\u4e0a\u5927\u72d0\u72f8\uff0c\u90a3\u5c31\u5929\u4e0b\u592a\u5e73\u4e86\u3002\u201d \u3000\u3000\u201c\u6c5f\u5927\u54e5\uff0c\u4e0d\u8981\u518d\u62d2\u4eba\u4e8e\u5343\u91cc\u4e4b\u5916\u4e86\uff0c\u6211\u4eec\u5f00\u59cb\u7ec3\u4e60\u72d0\u795e\u529f\u90a3\u4e00\u523b\u8d77\uff0c\u5c31\u5df2\u7ecf\u6ce8\u5b9a\u662f\u4f60\u7684\u4eba\u4e86\uff0c\u6211\u4eec\u89c1\u4f60\u7684\u65f6\u65e5\u867d\u77ed\uff0c\u53ef\u662f\u4f60\u5df2\u7ecf\u5728\u6211\u4eec\u5fc3\u4e2d\u6210\u957f\u4e86\u5341\u5e74\uff0c\u4f60\u8be5\u77e5\u9053\uff1a\u5929\u72d0\u795e\u529f\uff0c\u4f1a\u4f7f\u4eba\u65e9\u719f\u3002 \u3000\u3000\u5a18\u867d\u7136\u662f\u4f20\u6388\u6211\u4eec\u6b66\u529f\u7684\u4eba\uff0c\u4f46\u5979\u6d89\u5165\u4e0d\u6df1\uff0c\u8fd8\u4f53\u4f1a\u4e0d\u51fa\u72d0\u5fc3\u76f8\u5370\uff0c\u6280\u827a\u76f8\u5438\u7684\u5929\u72d0\u6b66\u529f\u795e\u9ad3\u2026\u2026\uff01\u201d \u3000\u3000\u201c\u60f3\u4e0d\u5230\uff0c\u4f60\u4eec\u5df2\u8fbe\u5230\u4e86\u8fd9\u7b49\u5883\u754c\uff0c\u6c5f\u5927\u54e5\u5012\u662f\u5931\u656c\u4e86\u3002\u201d \u3000\u3000\u541f\u9738\u9053\uff1a\u201c\u6211\u4e0d\u4fe1\u4f60\u771f\u7684\u77a7\u4e0d\u51fa\u6765\uff0c\u4f60\u53ea\u662f\u5b58\u5fc3\u9003\u907f\u7f62\u4e86\uff0c\u6c5f\u5927\u54e5\uff0c\u4f60\u7a76\u7adf\u5728\u6015\u4ec0\u4e48\u5462\uff1f \u3000\u3000\u6211\u4eec\u7684\u7239\u5a18\u4e0d\u662f\u5df2\u7ecf\u7ed9\u4e86\u4f60\u660e\u786e\u7684\u8868\u793a\u5417\uff1f \u3000\u3000\u5f20\u59e8\u4e5f\u4e0d\u53cd\u5bf9\u6211\u4eec\u59d4\u8eab\u76f8\u4f8d\uff0c\u7236\u6bcd\u4e4b\u547d\uff0c\u5a92\u5981\u4e4b\u8a00\uff0c\u90fd\u5df2\u9f50\u5907\uff0c\u4e5f\u4e0d\u7b97\u6709\u6127\u4f26\u5e38\u5723\u5fb7\uff0c\u4f60\u5c31\u59d4\u5c48\u70b9\uff0c\u514d\u53bb\u6d1e\u623f\u82b1\u70db\u90a3\u4e00\u9053\u4fd7\u793c\u5427\uff01\u201d \u3000\u3000\u7a81\u7136\u6380\u5f00\u68c9\u88ab\uff0c\u94bb\u5165\u88ab\u7a9d\uff0c\u5a07\u8eaf\u5982\u86c7\u822c\uff0c\u7f20\u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u5979\u8c6a\u653e\u5927\u80c6\uff0c\u5c24\u8fc7\u541f\u96ea\u3002 \u3000\u3000\u541f\u96ea\u770b\u7740\u541f\u971c\u7684\u7eb5\u60c5\u6063\u653e\uff0c\u7adf\u7136\u662f\u5168\u65e0\u987e\u5fcc\uff0c\u5fae\u7b11\u5982\u82b1\u7684\u9053\uff1a\u201c\u971c\u59b9\uff0c\u4f60\u600e\u4e48\u4e0d\u7528\u70b9\u72d0\u5a9a\u624b\u6bb5\uff0c\u8ba9\u6c5f\u5927\u54e5\u54c1\u8bc4\u4e00\u4e0b\u3002\u2026\u2026\u201d \u3000\u3000\u201c\u4f60\u77aa\u7740\u4e00\u5bf9\u5927\u773c\u775b\uff0c\u770b\u7684\u90a3\u4e48\u5165\u795e\uff0c\u6211\u7eb5\u6709\u5343\u767e\u624b\u6bb5\uff0c\u4e5f\u6709\u4e9b\u65bd\u4e0d\u51fa\u6765\u5440\uff01\u2026\u2026\u201d\u541f\u971c\u9053\uff1a\u201c\u5c0f\u59b9\u6211\u529f\u529b\u867d\u6df1\uff0c\u53ef\u8fd8\u662f\u5934\u4e00\u6b21\u63a5\u8fd1\u7537\u4eba\u2026\u2026\u201d \u3000\u3000\u201c\u539f\u6765\u662f\u8fd8\u6709\u70b9\u5bb3\u7f9e\u2026\u2026\u201d\u541f\u96ea\u7b11\u9053\uff1a\u201c\u90a3\u5c31\u8ba9\u59d0\u59d0\u5e2e\u4f60\u4e00\u628a\uff0c\u6c5f\u5927\u54e5\uff0c\u9152\u8089\u548c\u5c1a\u628a\u4f60\u9001\u6765\u8fd9\u91cc\uff0c\u504f\u504f\u4f60\u53c8\u6655\u8ff7\u4e0d\u9192\uff0c\u5bb3\u5f97\u6211\u4eec\u4e24\u59d0\u59b9\u4e00\u591c\u672a\u66fe\u5408\u773c\uff0c\u4e3a\u4e86\u67e5\u9a8c\u4f60\u7684\u4f24\u52bf\uff0c\u770b\u904d\u4e86\u4f60\u5168\u8eab\u6bcf\u4e00\u5bf8\u5730\u65b9\uff0c\u60f3\u4e00\u60f3\uff0c\u6211\u4eec\u786e\u4e5f\u65e0\u6cd5\u518d\u5ac1\u522b\u4eba\uff0c\u8fd9\u4e0d\u80fd\u602a\u6211\u4eec\uff0c\u662f\u5929\u610f\uff0c\u5728\u52ab\u96be\u9003\u554a\uff01\u201d \u3000\u3000\u5979\u81ea\u8a00\u81ea\u8bed\u7740\uff0c\u4e5f\u94bb\u5165\u4e86\u88ab\u7a9d\u4e4b\u4e2d\u3002 \u3000\u3000\u4e8c\u5973\u70ed\u60c5\u6d0b\u6ea2\uff0c\u6781\u5c3d\u5a07\u67d4\uff0c\u628a\u6c5f\u67ab\u6298\u817e\u5f97\u70ed\u8840\u6cb8\u70e7\uff0c\u60c5\u6b32\u5954\u653e\u3002 \u3000\u3000\u4f46\u4e8c\u5973\u5374\u53c8\u7d27\u5b88\u7740\u6700\u540e\u4e00\u5173\uff0c\u84ec\u95e8\u4e0d\u80af\u4e3a\u541b\u5f00\u3002 \u3000\u3000\u5979\u4eec\u672a\u7ecf\u4eba\u9053\uff1a\u7537\u5973\u76f8\u62e5\uff0c\u4eb2\u543b\u629a\u6478\uff0c\u89c9\u7740\u5341\u5206\u597d\u73a9\uff0c\u4f46\u6c5f\u67ab\u8fd9\u4e2a\u82e6\uff0c\u53ef\u5c31\u5403\u7684\u5927\u4e86\uff0c\u88ab\u4e8c\u5973\u64a9\u9017\u7684\u6b32\u706b\u96be\u7981\uff0c\u5374\u53c8\u4e0d\u4fbf\u5f3a\u8feb\u4e8c\u5973\u5c31\u8303\uff0c\u5f53\u771f\u662f\u53d7\u5c3d\u4e86\u6b32\u706b\u711a\u8eab\u4e4b\u82e6\uff0c\u8fd8\u5f97\u54ac\u7740\u7259\u514b\u5236\u81ea\u5df1\u2026\u2026 \u3000\u3000\uff08\u54c8\u54c8\u2026\u2026\uff09 \u3000\u3000\u4e8c\u5973\u90fd\u662f\u5927\u884c\u5bb6\uff0c\u6c5f\u67ab\u53c8\u4e0d\u4fbf\u65bd\u5c55\u51fa\u5929\u72d0\u6b66\u529f\uff0c\u5e73\u606f\u6b32\u5ff5\uff0c\u4e5f\u4e0d\u80fd\u65bd\u5c55\u5f02\u672f\u8bf1\u5bf9\u65b9\uff0c\u771f\u662f\u8fdb\u9000\u4e0d\u5f97\uff0c\u6025\u51fa\u4e86\u4e00\u5934\u5927\u6c57\u3002 \u3000\u3000\u4e5f\u8bb8\u662f\u5f20\u56db\u59d1\u65e9\u5df2\u7ecf\u9690\u8eab\u5728\u6697\u4e2d\u89c2\u770b\uff0c\u4e5f\u8bb8\u662f\u5979\u8fd9\u5371\u6025\u65f6\u523b\u6b63\u5de7\u8d76\u4e86\u56de\u6765\u3002 \u3000\u3000\u53ea\u542c\u5f20\u56db\u59d1\u7684\u58f0\u97f3\u4f20\u4e86\u8fc7\u6765\uff0c\u9053\uff1a\u201c\u597d\u4e86\uff0c\u4e2b\u5934\u4eec\uff0c\u8d77\u6765\u5427\uff01 \u3000\u3000\u8981\u61c2\u5f97\u9002\u53ef\u800c\u6b62\uff0c\u518d\u8fd9\u6837\u6298\u817e\u4e0b\u53bb\uff0c\u4f60\u4eec\u7684\u6c5f\u5927\u54e5\u4e0d\u6b7b\u4e5f\u8981\u8131\u5c42\u76ae\u4e86\u3002\u201d \u3000\u3000\u4e8c\u5973\u51b7\u9759\u4e0b\u6765\uff0c\u53d1\u89c9\u4e86\u6c5f\u67ab\u7ea2\u4f3c\u706b\uff0c\u5168\u8eab\u70ed\u7684\u70eb\u4eba\uff0c\u4e0d\u7981\u5403\u4e86\u4e00\u60ca\uff0c\u5306\u5306\u8d77\u8eab\u4e0b\u5e8a\uff0c\u521a\u7406\u597d\u6ee1\u5934\u4e71\u53d1\u548c\u6298\u76b1\u7684\u8863\u670d\uff0c\u5f20\u56db\u59d1\u5df2\u63a8\u95e8\u800c\u5165\u3002 \u3000\u3000\u770b\u4e86\u6c5f\u67ab\u54ac\u7259\u800c\u5367\u7684\u795e\u6001\u4e00\u773c\uff0c\u518d\u770b\u770b\u4e8c\u5973\u5f62\u8c8c\u3001\u795e\u60c5\uff0c\u5f20\u56db\u59cb\u5fc3\u4e2d\u4e00\u52a8\uff0c\u6697\u9053\uff1a\u201c\u4e09\u4e2a\u5c0f\u98ce\u6d41\uff0c\u5728\u5e8a\u4e0a\u626d\u6210\u4e00\u56e2\uff0c\u7adf\u7136\u80fd\u4e0d\u53ca\u4e8e\u4e71\uff0c\u4fdd\u6301\u6e05\u767d\uff0c\u7b80\u76f4\u662f\u6709\u70b9\u795e\u4e4e\u5176\u8bf4\uff0c\u5982\u975e\u6211\u4eb2\u773c\u770b\u5230\uff0c\u5b9e\u96be\u76f8\u4fe1\u3002\u201d \u3000\u3000\u96ea\u3001\u971c\u4e8c\u5973\u56de\u987e\u4e86\u5f20\u56db\u59d1\u4e00\u773c\uff0c\u9f50\u9f50\u8eac\u8eab\u4e00\u793c\uff0c\u9053\uff1a\u201c\u5f20\u59e8\uff0c\u6c5f\u5927\u54e5\u597d\u50cf\u75c5\u4e86\u3002\u201d\u8138\u513f\u98de\u7ea2\u7684\u8dd1\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u7edd\uff01\u8dd1\u51fa\u53bb\u5c31\u7b97\u4e86\uff0c\u541f\u971c\u7279\u522b\u56de\u5934\uff0c\u4e00\u4e0b\u5b50\u5e26\u4e0a\u4e86\u623f\u95e8\u3002 \u3000\u3000\u5f20\u56db\u59d1\u5fc3\u4e2d\u5fd6\u9053\uff1a\u201c\u4eba\u5c0f\u9b3c\u5927\uff01\u2026\u2026\u201d \u3000\u3000\u4eba\u5374\u4e00\u5c41\u80a1\u5750\u5728\u6c5f\u67ab\u7684\u5e8a\u6cbf\u4e0a\uff0c\u9053\uff1a\u201c\u4f60\u4e2d\u7684\u4e00\u6b65\u5012\uff0c\u90a3\u662f\u8ff7\u9b42\u836f\u7269\u4e2d\u6700\u5389\u5bb3\u7684\u4e00\u79cd\uff0c\u836f\u6548\u5947\u901f\uff0c\u800c\u4e14\u6655\u8ff7\u7684\u65f6\u95f4\u53c8\u957f\uff0c\u597d\u50cf\u8981\u5341\u4e8c\u4e2a\u65f6\u8fb0\u624d\u4f1a\u836f\u6027\u6d88\u9000\uff0c\u5b8c\u5168\u6e05\u9192\uff0c\u4f60\u597d\u50cf\u63d0\u524d\u9192\u4e86\u8fc7\u6765\u3002\u201d \u3000\u3000\u6c5f\u67ab\u70b9\u70b9\u5934\uff0c\u795e\u60c5\u5374\u5341\u5206\u602a\u5f02\u3002 \u3000\u3000\u5f20\u56db\u59d1\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u4e09\u4e2a\u7ea0\u7f20\u4e86\u8fd1\u4e00\u4e2a\u65f6\u8fb0\uff0c\u4e24\u4e2a\u4e2b\u5934\uff0c\u4ecd\u662f\u5b8c\u58c1\uff0c\u8fd9\u4e00\u70b9\u541b\u5b50\u4e4b\u98ce\uff0c\u5927\u59d0\u5012\u662f\u4e0d\u80fd\u4e0d\u4f69\u670d\u4e86\u3002\u201d \u3000\u3000\u4f38\u51fa\u7ec6\u5de7\u7684\u7389\u624b\uff0c\u6309\u5728\u6c5f\u67ab\u7684\u5934\u4e0a\uff0c\u9053\uff1a\u201c\u597d\u70eb\u554a\uff01\u971c\u4e2b\u5934\u6ca1\u6709\u9a97\u6211\uff0c\u5144\u5f1f\uff0c\u600e\u4e48\u4f1a\u8fd9\u4e2a\u6837\u5b50\u5462\uff1f\u2026\u2026\u201d \u3000\u3000\u201c\u88ab\u5979\u4eec\u6574\u60e8\u4e86\u2026\u2026\u201d\u6c5f\u67ab\u6b32\u8a00\u53c8\u6b62\u3002 \u3000\u3000\u5f20\u56db\u59d1\u76b1\u76b1\u7709\u5934\uff0c\u9053\uff1a\u201c\u8bf4\u5440\uff01\u600e\u4e48\u56de\u4e8b\uff1f\u4e24\u4e2a\u4e2b\u5934\u5e2e\u4f60\u68c0\u67e5\u4f24\u52bf\uff0c\u88f8\u8eab\u6d17\u6d74\uff0c\u5c31\u7b97\u8eab\u4e3a\u4eba\u59bb\uff0c\u4e5f\u4e0d\u8fc7\u5982\u6b64\u7f62\u4e86\uff0c\u4f60\u4eec\u72d0\u54e5\u72d0\u59b9\uff0c\u4e00\u8109\u76f8\u627f\uff0c\u6211\u770b\uff0c\u4f60\u5c31\u6536\u4e86\u5979\u4eec\u5427\uff01\u5148\u8ba9\u5979\u4eec\u5b89\u5fc3\u5de5\u4f5c\uff0c\u62d2\u6321\u5f3a\u654c\uff0c\u4ee5\u540e\u7684\u4e8b\uff0c\u4ee5\u540e\u518d\u60f3\u529e\u6cd5\uff1f\u201d \u3000\u3000\u201c\u5979\u4eec\u90fd\u544a\u8bc9\u6211\u4e86\uff0c\u6211\u4e5f\u660e\u767d\u65e0\u6cd5\u63a8\u62d2\u4e86\uff0c\u6240\u4ee5\uff0c\u624d\u88ab\u5979\u4eec\u95f9\u6210\u4e86\u8fd9\u4e2a\u6837\u5b50\uff1f\u201d\u6c5f\u67ab\u82e6\u7b11\u4e00\u4e0b\uff0c\u63a5\u9053\uff1a\u201c\u5927\u59d0\uff0c\u4e0d\u8981\u78b0\u6211\uff0c\u6211\u88ab\u5979\u5f15\u52a8\u5fc3\u706b\uff0c\u771f\u6c14\u5c94\u7ecf\uff0c\u518d\u95f9\u4e0b\u53bb\uff0c\u771f\u8981\u4e00\u547d\u9e23\u547c\u4e86\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u662f\u4f55\u7b49\u7ecf\u9a8c\u4e30\u5bcc\u7684\u4eba\uff0c\u4f55\u51b5\u6c5f\u67ab\u90a3\u526f\u88ab\u6b32\u706b\u711a\u70e7\u7684\u60c5\u666f\uff0c\u7acb\u523b\u5fc3\u4e2d\u660e\u767d\uff0c\u53f9\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u5982\u6b64\u81ea\u82e6\uff0c\u8fd8\u4e0d\u5fcd\u903c\u8bf1\u5979\u4eec\u732e\u8eab\uff0c\u5927\u59d0\u5341\u5206\u611f\u52a8\uff0c\u8fd9\u5927\u6982\u5c31\u53eb\u4f5c\u76d7\u4ea6\u6709\u9053\u5427\uff01 \u3000\u3000\u5e78\u597d\u5927\u59d0\u6765\u7684\u53ca\u65f6\uff0c\u6211\u4f1a\u6551\u4f60\u7684\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u773c\u7ea2\u5982\u706b\uff0c\u671b\u7740\u5f20\u56db\u59d1\u9ed8\u7136\u4e0d\u8bed\uff0c\u4f46\u795e\u60c5\u5411\u5374\u6d41\u73b0\u51fa\u65e0\u6bd4\u7684\u611f\u6fc0\u3002 \u3000\u3000\u5f20\u56db\u59d1\u5bbd\u8863\u89e3\u5e26\uff0c\u5c3d\u53bb\u4e86\u5168\u8eab\u7684\u675f\u7f1a\u3002 \u3000\u3000\u5979\u867d\u662f\u5df2\u5c4a\u4e2d\u5e74\uff0c\u4f46\u4ecd\u7136\u7389\u808c\u96ea\u80a4\uff0c\u66f2\u7ebf\u73b2\u62e2\uff0c\u80f4\u4f53\u7684\u5b8c\u7f8e\uff0c\u5982\u4e8c\u5341\u8bb8\u4eba\u3002 \u3000\u3000\u6c5f\u67ab\u6380\u8d77\u4e86\u68c9\u88ab\uff0c\u5f20\u56db\u59d1\u9c7c\u4e00\u822c\u6ed1\u5165\u4e86\u88ab\u4e2d\u2026\u2026 \u3000 \u7b2c\u5341\u4e00\u56de\u3000\u9ed1\u5929\u9e45\u5bfb\u4ec7\u9669\u4e27\u547d \u3000 \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u5728\u98ce\u96ea\u5c0f\u9986\u4e2d\u89c1\u5230\u6c5f\u67ab\uff0c\u5374\u53c8\u662f\u534e\u706f\u521d\u4e0a\u7684\u5165\u591c\u65f6\u5019\u3002 \u3000\u3000\u5f20\u56db\u59d1\u5e26\u7740\u541f\u96ea\u3001\u541f\u971c\u5f20\u7f57\u51fa\u4e00\u684c\u5b50\u7f8e\u5473\u4f73\u80b4\u3002 \u3000\u3000\u4f46\u4e00\u5e2d\u5ba2\uff0c\u4e5f\u5c31\u53ea\u6709\u516d\u4e2a\u4eba\u3002 \u3000\u3000\u6b27\u9633\u662d\u592b\u5987\u4e4b\u5916\uff0c\u5c31\u662f\u6c5f\u67ab\u548c\u4e03\u5b9d\u548c\u5c1a\uff0c\u552f\u4e00\u7684\u5916\u5bbe\u662f\u8001\u53eb\u5316\u94b1\u7f3a\u5e26\u6765\u7684\u79e6\u516b\u516c\u3002 \u3000\u3000\u5f20\u56db\u59d1\u4f3c\u662f\u5fd9\u6d3b\u7684\u5f88\u9ad8\u5174\uff0c\u602a\u7684\u662f\u541f\u96ea\u3001\u541f\u971c\u4e5f\u7b11\u7684\u5341\u5206\u5f00\u5fc3\u3002 \u3000\u3000\u7f57\u5170\u62cd\u62cd\u65c1\u4fa7\u7684\u6728\u6905\u5b50\uff0c\u9053\uff1a\u201c\u56db\u59d1\u5750\u4e0b\u6765\u5427\uff01\u201d \u3000\u3000\u5f20\u56db\u59d1\u7b11\u4e00\u7b11\uff0c\u62c9\u7740\u541f\u96ea\u548c\u541f\u971c\u7f13\u6b65\u5165\u5e2d\uff0c\u9053\uff1a\u201c\u8001\u53eb\u5316\uff0c\u56db\u70ed\u7092\u56db\u51b7\u76d8\uff0c\u516d\u4e2a\u5927\u83dc\uff0c\u5341\u56db\u9053\u5168\u4e0a\u4e86\u684c\u5b50\uff0c\u4eca\u5929\u6211\u8981\u4f60\u7684\u8111\u6ee1\u80a0\u80a5\u987a\u7740\u5634\u89d2\u6d41\u6cb9\uff0c\u770b\u4f60\u4ee5\u540e\u8fd8\u6562\u4e0d\u6562\u998b\u5634\u3002\u201d \u3000\u3000\u201c\u653e\u5fc3\uff01\u653e\u5fc3\uff01\u8001\u53eb\u5316\u4fdd\u8bc1\u662f\u5403\u5230\u76d8\u5b50\u89c1\u5e95\uff0c\u8fd9\u53ef\u662f\u4e00\u751f\u4e2d\u96be\u5f97\u9047\u4e0a\u51e0\u6b21\u7684\u4ed9\u4f1a\uff0c\u80c0\u7834\u809a\u76ae\uff0c\u8001\u53eb\u5316\u4e5f\u53ea\u597d\u8ba4\u547d\u4e86\u3002\u201d \u3000\u3000\u96ea\u3001\u971c\u4e8c\u5973\u60c5\u6000\u521d\u5f00\uff0c\u72d0\u6027\u5c55\u9732\uff0c\u7b11\u7684\u5982\u82b1\u89e3\u8bed\uff0c\u5982\u7389\u751f\u9999\uff0c\u4f7f\u5f97\u521d\u7779\u7389\u5bb9\u7684\u79e6\u516b\u516c\u770b\u7684\u75f4\u4e86\uff0c\u6697\u9053\uff1a\u201c\u4e16\u4e0a\u7adf\u6709\u5982\u6b64\u52a8\u4eba\u7684\u7f8e\u5973\uff0c\u6d1b\u9633\u5c45\u7684\u56db\u5b63\u82b1\u5a62\uff0c\u4e5f\u662f\u671b\u5c18\u83ab\u53ca\u4e86\u3002\u201d \u3000\u3000\u201c\u94b1\u53d4\u53d4\uff0c\u6211\u4eec\u6b63\u5728\u8ddf\u5f20\u59e8\u5b66\u5979\u7684\u70f9\u4efb\u624b\u6cd5\uff0c\u53ef\u4e0d\u8981\u4e00\u6b21\u5403\u4f24\u4e86\uff0c\u6211\u4eec\u5b66\u4f1a\u4e86\uff0c\u4f1a\u5e38\u5e38\u7684\u8bf7\u4f60\u5403\u7684\u2026\u2026\u201d\u541f\u96ea\u5fae\u7b11\uff1a\u201c\u94b1\u53d4\u53d4\u8db3\u8ff9\u904d\u53ca\u5927\u6c5f\u5357\u5317\uff0c\u662f\u5927\u5403\u5ba2\uff0c\u6211\u4eec\u8fd8\u8981\u94b1\u53d4\u53d4\u54c1\u8bc4\u4e00\u4e0b\u6211\u4eec\u7684\u624b\u827a\u3002\u201d \u3000\u3000\u201c\u4e0d\u5bb3\u7f9e\u7684\u4e2b\u5934\uff0c\u4f60\u4eec\u5f20\u59e8\u662f\u6613\u7259\u672f\u7684\u5929\u624d\uff0c\u52a0\u4e0a\u591a\u5e74\u8bd5\u7ec3\u3001\u4f53\u4f1a\uff0c\u624d\u6709\u4eca\u65e5\u6210\u5c31\u2026\u2026\u201d\u7f57\u5170\u8bf4\uff0c\u201c\u666e\u901a\u7684\u9752\u83dc\u841d\u535c\uff0c\u5230\u4e86\u5979\u7684\u624b\u4e2d\uff0c\u4e5f\u4f1a\u53d8\u51fa\u53ef\u53e3\u4f73\u80b4\uff0c\u5c82\u662f\u4e09\u4e94\u5e74\u80fd\u6709\u6210\u5c31\u7684\uff1f\u201d \u3000\u3000\u201c\u6211\u53ef\u6ca1\u6709\u4f60\u8bf4\u90a3\u5927\u672c\u9886\uff0c\u8c03\u6599\u4e0d\u5168\uff0c\u8c01\u4e5f\u65e0\u6cd5\u505a\u51fa\u6765\u53ef\u53e3\u7f8e\u5473\u2026\u2026\u201d\u5f20\u56db\u59d1\u7b11\u9053\uff1a \u3000\u3000\u201c\u522b\u5c0f\u770b\u541f\u96ea\u3001\u541f\u971c\uff0c\u5979\u4eec\u7684\u806a\u6167\u3002\u609f\u6027\uff0c\u51fa\u4e86\u4eba\u610f\u6599\u4e4b\u5916\uff0c\u7f57\u5170\uff0c\u540e\u751f\u53ef\u754f\u5440\uff01\u518d\u8fc7\u4e2a\u4e09\u4e24\u4e2a\u6708\uff0c\u6211\u5c31\u6ca1\u6709\u4e1c\u897f\u6559\u5979\u4eec\u4e86\u3002\u201d \u3000\u3000\u6c5f\u67ab\u542c\u5f97\u5fc3\u5934\u4e00\u8df3\uff0c\u6025\u6025\u63a5\u9053\uff1a\u201c\u5927\u5e08\uff0c\u6211\u60f3\u77e5\u9053\uff1a\u4f60\u600e\u4e48\u628a\u6211\u6551\u51fa\u6765\u7684\uff1f\u201d \u3000\u3000\u5927\u548c\u5c1a\u4f5b\u6cd5\u7cbe\u6df1\uff0c\u5750\u90a3\u91cc\u4e03\u60c5\u4e0d\u52a8\uff0c\u76f4\u5230\u6c5f\u67ab\u95ee\u4e0a\u4ed6\uff0c\u624d\u5f00\u53e3\u8bf4\u9053\uff1a\u201c\u548c\u5c1a\u4e00\u76f4\u5728\u6697\u4e2d\u4fdd\u62a4\uff0c\u89c1\u4f60\u8fdb\u5165\u6885\u82b1\u5385\uff0c\u4e0d\u89c1\u51fa\u6765\uff0c\u5c31\u8fdb\u53bb\u628a\u4f60\u5e26\u51fa\u6765\uff0c\u4ea4\u7ed9\u4e86\u5f20\u59d1\u5a18\uff0c\u624d\u77e5\u9053\u4f60\u4e2d\u4e86\u6c5f\u6e56\u4e0a\u6700\u5389\u5bb3\u7684\u8ff7\u9b42\u836f\u7269\uff1a\u2018\u4e00\u6b65\u5012\u2019\uff0c\u5982\u975e\u5f20\u59d1\u5a18\u89c1\u591a\u8bc6\u5e7f\uff0c\u77a7\u51fa\u836f\u7269\u6765\u5386\uff0c\u90a3\u5c31\u4e0d\u77e5\u9053\u4f1a\u95f9\u51fa\u4ec0\u4e48\u4e8b\u60c5\u4e86\uff1f\u201d \u3000\u3000\u201c\u5904\u5904\u7559\u5fc3\u7686\u5b66\u95ee\u2026\u2026\u201d\u6c5f\u67ab\u8bf4\uff0c\u201c\u6211\u60f3\u4e0d\u5230\u4ed6\u4e34\u53bb\u4e4b\u9645\uff0c\u4f1a\u7a81\u7136\u6492\u51fa\u4e00\u628a\u8ff7\u9999\u2026\u2026\u201d \u3000\u3000\u4ed4\u7ec6\u7684\u8bf4\u51fa\u4e86\u4e8b\u60c5\u7ecf\u8fc7\uff0c\u63a5\u9053\uff1a\u201c\u5927\u5bb6\u8bf7\u7528\u70b9\u5fc3\u601d\u60f3\u60f3\uff0c\u90a3\u795e\u79d8\u4eba\u7269\u7a76\u7adf\u662f\u8c01\uff1f\u6211\u4eec\u5148\u6765\u559d\u9152\uff0c\u6211\u656c\u8bf8\u4f4d\u4e00\u676f\u3002\u201d \u3000\u3000\u4e00\u79cd\u6c89\u91cd\uff0c\u65e0\u5f62\u7684\u538b\u529b\uff0c\u4f7f\u5f97\u5927\u5bb6\u7684\u9152\u5174\u90fd\u63d0\u4e0d\u8d77\u6765\uff0c\u4f73\u80b4\u7f8e\u5473\uff0c\u4e5f\u65e0\u5fc3\u54c1\u5c1d\uff0c\u5306\u5306\u7684\u5403\u8fc7\u4e00\u987f\u9152\u996d\u3002 \u3000\u3000\u96ea\u3001\u971c\u4e8c\u5973\u6536\u62fe\u4e86\u6b8b\u5e2d\u3001\u7897\u7b77\uff0c\u9001\u4e0a\u4e00\u58f6\u9999\u8317\uff0c\u5750\u5728\u5f20\u56db\u59d1\u7684\u8eab\u540e\u3002 \u3000\u3000\u5979\u4eec\u77e5\u673a\u9002\u53d8\uff0c\u5fc3\u77e5\u6b64\u523b\u662f\u7814\u5546\u5927\u8ba1\u7684\u8fb0\u5149\uff0c\u5b89\u5b89\u9759\u9759\u5730\u5750\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u201c\u6211\u4eec\u592b\u5987\u4e45\u5c45\u6df1\u5c71\u5927\u6cfd\uff0c\u5bf9\u6c5f\u6e56\u4e2d\u7684\u4eba\u4eba\u4e8b\u4e8b\uff0c\u5168\u4e0d\u4e86\u89e3\u2026\u2026\u201d\u6b27\u9633\u662d\u8bf4\uff0c\u201c\u53ea\u6015\u65e0\u6cd5\u63d0\u4f9b\u4e00\u5f97\u4e4b\u611a\u4e86\uff01\u201d \u3000\u3000\u201c\u6c5f\u6e56\u4e0a\uff0c\u8fd8\u6709\u90a3\u4e00\u4e2a\u95e8\u6d3e\u4e2d\u7684\u9ad8\u624b\uff0c\u6709\u6b64\u72ec\u884c\u5176\u662f\u672c\u94b1\u2026\u2026\u201d\u6c5f\u67ab\u8bf4\uff0c\u201c\u80fd\u548c\u5185\u5bab\u4e00\u7cfb\u4e2d\u4eba\uff0c\u5e73\u884c\u8bba\u4ea4\u3002\u201d \u3000\u3000\u201c\u5982\u662f\u6c5f\u8001\u5f1f\u8bf4\u7684\u4e0d\u9519\uff0c\u5c31\u8bc1\u660e\u4e86\u4ed6\u4eec\u65e9\u6709\u52fe\u7ed3\uff0c\u548c\u5c1a\u60f3\u4e0d\u51fa\u90a3\u4e00\u4e2a\u95e8\u6d3e\uff0c\u4f1a\u6709\u8fd9\u4e48\u4e00\u4f4d\u4eba\u7269\u3002\u201d \u3000\u3000\u201c\u8001\u53eb\u5316\u4e8c\u5341\u5e74\u660e\u67e5\u6697\u8bbf\uff0c\u7ec6\u6570\u6b66\u6797\u9ad8\u4eba\uff0c\u5b9e\u5728\u627e\u4e0d\u51fa\u8fd9\u4f4d\u795e\u79d8\u5ba2\u4eba\u7684\u8eab\u4efd\uff0c\u771f\u53eb\u4eba\u5927\u8d39\u601d\u91cf\u4e86\u3002\u201d \u3000\u3000\u201c\u4e00\u6b65\u5012\u8ff7\u9b42\u836f\u7269\uff0c\u51fa\u5728\u5deb\u5c71\u795e\u5973\u5cf0\u660e\u6708\u89c2\u4e2d\uff0c\u2026\u2026\u201d\u5f20\u56db\u59d1\u8bf4\uff0c\u201c\u6c5f\u6e56\u4e2d\u975e\u4eba\u4eba\u53ef\u5f97\uff0c\u4f46\u80fd\u53d6\u5230\u6b64\u836f\u7684\u4eba\uff0c\u5374\u4e5f\u4e0d\u5728\u5c11\u6570\uff0c\u5c31\u76ee\u524d\u6c5f\u6e56\u4e0b\u7684\u5f62\u52bf\u800c\u8a00\uff0c\u80fd\u6325\u624b\u64a4\u6bd2\uff0c\u8ff7\u5012\u6c5f\u67ab\u8fd9\u7b49\u8eab\u624b\u7684\u4eba\u7269\uff0c\u5e94\u8be5\u4e0d\u591a\uff0c\u5404\u5927\u95e8\u6d3e\u5df2\u7136\u6ca1\u843d\uff0c\u8fde\u81ea\u4fdd\u90fd\u6709\u4e9b\u529b\u4e0d\u4ece\u5fc3\uff0c\u7eb5\u6709\u51e0\u4f4d\u9ad8\u624b\uff0c\u5185\u5bab\u4e00\u7cfb\u4e2d\u4eba\uff0c\u4e5f\u672a\u5fc5\u4f1a\u548c\u4ed6\u4eec\u5408\u4f5c\uff0c\u8bf8\u4f4d\uff0c\u4f55\u4e0d\u60f3\u60f3\u5929\u9a6c\u603b\u5802\u4e2d\u7684\u9ad8\u624b\u5462\uff1f\u201d \u3000\u3000\u201c\u9ad8\u554a\uff01\u771f\u662f\u4e00\u8bed\u60ca\u9192\u68a6\u4e2d\u4eba\uff0c\u5185\u5bab\u4e2d\u4eba\uff0c\u5904\u5904\u548c\u5929\u9a6c\u5802\u4e2d\u4eba\u4e89\u593a\u5730\u76d8\u3002\u6743\u52bf\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u9053\uff1a\u201c\u81ea\u662f\u6709\u53ef\u80fd\u548c\u5929\u9a6c\u603b\u5802\u4e2d\u4eba\uff0c\u65e9\u6709\u52fe\u7ed3\u4e86\uff1f\u201d \u3000\u3000\u201c\u5bf9\uff01\u653e\u773c\u6c5f\u6e56\uff0c\u5df2\u7ecf\u6ca1\u6709\u4e86\u4ed6\u4eec\u53ef\u4ee5\u4e89\u6597\u7684\u5bf9\u624b\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u90a3\u5c31\u53ea\u6709\u7a9d\u91cc\u53cd\uff0c\u81ea\u76f8\u6b8b\u6740\u4e86\uff1f\u201d \u3000\u3000\u201c\u5927\u6709\u53ef\u80fd\u2026\u2026\u201d\u8001\u53eb\u5316\u8bf4\uff0c\u201c\u95ee\u9898\u662f\u4ec0\u4e48\u4eba\uff1f\u5929\u9a6c\u603b\u5802\uff0c\u7fa4\u9b54\u6c47\u805a\uff0c\u5012\u662f\u6709\u4e0d\u5c11\u8fd9\u6837\u7684\u9ad8\u624b\uff01\u201d \u3000\u3000\u201c\u4e00\u822c\u4eba\u624b\uff0c\u4e5f\u4e0d\u4f1a\u4f7f\u5185\u5bab\u4e00\u7cfb\u4e2d\u4eba\uff0c\u6298\u8282\u4e0b\u4ea4\uff0c\u6240\u4ee5\uff0c\u90a3\u4e2a\u4eba\u8eab\u4efd\u5fc5\u9ad8\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u5929\u9a6c\u5802\u6709\u4e09\u5341\u516d\u5927\u957f\u8001\uff0c\u4e03\u5341\u4e8c\u4f4d\u62a4\u6cd5\uff0c\u8fd9\u4e9b\u4eba\u4e0d\u4f1a\u662f\u5185\u5bab\u4e2d\u4eba\u7ed3\u4ea4\u7684\u5bf9\u8c61\uff0c\u9664\u6b64\u4e4b\u5916\uff0c\u8fd8\u6709\u603b\u5802\u4e3b\u548c\u56db\u4f4d\u526f\u603b\u5802\u4e3b\uff0c\u603b\u5802\u4e3b\u5927\u6743\u5728\u63e1\uff0c\u81ea\u4e0d\u4f1a\u52fe\u7ed3\u5916\u4eba\uff0c\u56db\u4f4d\u526f\u603b\u5802\u4e3b\u2026\u2026\u201d \u3000\u3000\u201c\u6c5f\u5144\u5f1f\uff0c\u4f60\u5bf9\u5929\u9a6c\u5802\u7684\u60c5\u5f62\uff0c\u4f3c\u662f\u5f88\u6e05\u695a\u554a\uff01\u2026\u2026\u201d\u94b1\u7f3a\u8bf4\uff0c\u201c\u4f46\u4e0d\u77e5\u662f\u5426\u77e5\u9053\u56db\u4f4d\u526f\u603b\u5802\u4e3b\u7684\u59d3\u540d\uff1f\u201d \u3000\u3000\u201c\u4e00\u4e2a\u78b0\u5de7\u7684\u673a\u4f1a\uff0c\u4f7f\u6211\u5bf9\u5929\u9a6c\u5802\u7684\u5185\u60c5\uff0c\u4e86\u89e3\u4e0d\u5c11\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u56db\u5927\u526f\u603b\u5802\u4e3b\u662f\u5929\u9a6c\u884c\u7a7a\u53f8\u5f92\u827a\u2026\u2026\u201d \u3000\u3000\u201c\u6b64\u4eba\u7cbe\u901a\u76f8\u9a6c\uff0c\u9a6f\u9a6c\u4e4b\u672f\u2026\u2026\u201d\u94b1\u7f3a\u63a5\u9053\uff1a\u201c\u6b66\u529f\u4e5f\u5341\u5206\u9ad8\u660e\uff0c\u4f46\u5374\u5f88\u5c11\u5728\u6c5f\u6e56\u4e0a\u8d70\u52a8\uff0c\u60f3\u4e0d\u5230\uff0c\u7adf\u7136\u88ab\u5929\u9a6c\u603b\u5802\u7f57\u81f4\uff0c\u5f53\u4e86\u526f\u603b\u5802\u4e3b\uff01\u201d \u3000\u3000\u201c\u7b2c\u4e8c\u4f4d\u53eb\u91d1\u51e4\u51f0\u7533\u96ea\u541b\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u542c\u8bf4\u5979\u7684\u7edd\u6280\u662f\u5341\u51e4\u9f50\u98de\u2026\u2026\u201d \u3000\u3000\u201c\u65e0\u4eba\u80fd\u5728\u5341\u51e4\u9f50\u98de\u4e0b\uff0c\u5168\u8eab\u800c\u9000\u2026\u2026\u201d\u5f20\u56db\u59d1\u63a5\u4e86\u53e3\u9053\uff1a\u201c\u79f0\u4e3a\u7edd\u6280\uff0c\u5f53\u4e4b\u65e0\u6127\u2026\u2026\u201d \u3000\u3000\u201c\u5bf9\uff01\u5f53\u5e74\u56db\u5927\u51f6\u715e\u5782\u6d8e\u7533\u96ea\u541b\u7684\u7f8e\u8272\uff0c\u66fe\u8054\u624b\u4f0f\u51fb\uff0c\u51c6\u5907\u628a\u5979\u751f\u64d2\u56de\u53bb\uff0c\u4f46\u5374\u4f24\u5728\u4e86\u4e00\u51e4\u9f50\u98de\u4e4b\u4e0b\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u8bf4\uff0c\u201c\u540e\u6765\uff0c\u56db\u5927\u51f6\u715e\u5728\u4e5d\u534e\u5728\u4f1a\u4e0a\u4f24\u5728\u91d1\u4e39\u4e66\u751f\u7684\u5927\u7f57\u638c\u4e0b\uff0c\u8fd9\u4f4d\u5dfe\u5e3c\u5973\u8c6a\uff0c\u5e94\u8be5\u662f\u767d\u9053\u4e2d\u4eba\uff0c\u600e\u4f1a\u5f53\u4e86\u5929\u9a6c\u5802\u7684\u526f\u603b\u5802\u4e3b\u5462\uff1f\u201d \u3000\u3000\u201c\u81ea\u90a3\u4e00\u6218\u4e4b\u540e\uff0c\u7533\u96ea\u541b\u5c31\u672a\u5728\u6c5f\u6e56\u4e0a\u51fa\u73b0\u8fc7\uff0c\u6c5f\u6e56\u591a\u53d8\uff0c\u88ab\u7f57\u81f4\u5165\u5929\u9a6c\u5802\u4e2d\uff0c\u4e5f\u4e0d\u662f\u4ec0\u4e48\u5947\u602a\u7684\u4e8b\u60c5\u4e86\u3002\u201d \u3000\u3000\u201c\u5c31\u8001\u53eb\u5316\u5b50\u6240\u77e5\uff0c\u5f53\u5e74\u7533\u96ea\u541b\u5355\u5ff5\u91d1\u4e39\u4e66\u751f\uff0c\u597d\u4e8b\u96be\u5055\uff0c\u7ffb\u76ee\u76f8\u5411\uff0c\u4e24\u4eba\u8fd8\u5927\u6253\u51fa\u624b\uff0c\u62fc\u4e86\u4e00\u573a\uff0c\u7533\u96ea\u541b\u53d7\u4f24\u9038\u8d70\uff0c\u4e0d\u77e5\u6240\u7ec8\uff0c\u539f\u6765\uff0c\u8fdb\u5165\u4e86\u5929\u9a6c\u5802\u4e2d\u3002\u201d \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u7b2c\u4e09\u4f4d\u5929\u865a\u5b50\uff0c\u51fa\u8eab\u6b66\u5f53\u2026\u2026\u201d \u3000\u3000\u201c\u4ed6\u7b97\u662f\u6b66\u5f53\u53db\u5f92\u2026\u2026\u201d\u94b1\u7f3a\u8bf4\uff0c\u201c\u4f46\u5251\u672f\u4e0a\u7684\u9020\u8be3\uff0c\u5374\u662f\u4e00\u4ee3\u5947\u624d\uff0c\u539f\u56e0\u662f\uff0c\u4ed6\u80fd\u628a\u6b66\u5f53\u5251\u6cd5\u4e2d\u5f03\u4e4b\u4e0d\u7528\u7684\u60e8\u9177\u5251\u62db\uff0c\u6df7\u5165\u65bd\u5c55\u2026\u2026\u201d \u3000\u3000\u201c\u6c5f\u67d0\u53ef\u4ee5\u80af\u5b9a\uff0c\u90a3\u4e2a\u795e\u79d8\u4eba\u7269\u4e0d\u4f1a\u662f\u4ed6\uff0c\u4ed6\u6ca1\u6709\u90a3\u4e48\u9ad8\u5927\u7684\u8eab\u6750\uff0c\u4f55\u51b5\uff0c\u4e5f\u80fd\u542c\u5f97\u51fa\u4ed6\u7684\u58f0\u97f3\uff0c\u4f59\u4e0b\u7684\u4e00\u4f4d\u526f\u603b\u5802\u4e3b\uff0c\u53eb\u4e00\u638c\u62db\u9b42\u4e07\u65b9\u3002\u201d \u3000\u3000\u201c\u4e5f\u662f\u4e00\u4f4d\u5927\u9b54\u5934\uff0c\u8001\u53eb\u5316\u5b50\u8ba4\u8bc6\u4ed6\uff0c\u8fd9\u4e2a\u4eba\u5584\u6076\u96be\u5b9a\uff0c\u6740\u4eba\u5168\u51ed\u559c\u6012\u4e3a\u4e4b\uff0c\u4e5f\u8bb8\u5c31\u662f\u4ed6\u4e86\uff01\u201d \u3000\u3000\u201c\u4e0d\u4f1a\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u9053\uff1a\u201c\u7b2c\u4e00\uff0c\u4e07\u65b9\u8fdc\u4e0d\u591f\u8fd9\u4e2a\u4efd\u91cf\uff0c\u8981\u5185\u5bab\u4e2d\u4eba\u548c\u4ed6\u5e73\u884c\u8bba\u4ea4\uff0c\u7b2c\u4e8c\uff0c\u4e07\u65b9\u5230\u8fc7\u6d1b\u9633\u5c45\uff0c\u5bf9\u5929\u9a6c\u5802\u8868\u73b0\u51fa\u4e00\u7247\u5fe0\u5fc3\uff0c\u7b2c\u4e09\uff0c\u5982\u679c\u662f\u4ed6\uff0c\u7edd\u5bf9\u9003\u4e0d\u8fc7\u6c5f\u8001\u5f1f\u7684\u6cd5\u773c\u3002\u201d \u3000\u3000\u6c5f\u67ab\u70b9\u70b9\u5934\u3002 \u3000\u3000\u201c\u6c5f\u5144\u5f1f\uff0c\u8fd9\u56db\u5927\u526f\u603b\u5802\u4e3b\uff0c\u4f60\u90fd\u8bf4\u51fa\u6765\u4e86\uff0c\u4f46\u603b\u5802\u4e3b\u662f\u8c01\u5462\uff1f\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u8fd9\u603b\u7528\u4e0d\u7740\u4fdd\u5bc6\u4ed6\u7684\u8eab\u4efd\u5427\uff01\u201d \u3000\u3000\u201c\u5c0f\u5f1f\u662f\u771f\u4e0d\u77e5\u9053\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u4e3a\u4e86\u4e0d\u8ba9\u4ed6\u4eec\u52a8\u7591\uff0c\u5c0f\u5f1f\u6ca1\u6709\u8ffd\u95ee\uff0c\u4e0d\u8fc7\uff0c\u8fd9\u4ef6\u4e8b\uff0c\u5e94\u8be5\u4e0d\u662f\u592a\u96be\uff01\u201d \u3000\u3000\u201c\u522b\u770b\u7684\u5f88\u5bb9\u6613\uff0c\u5929\u9a6c\u5802\u4e2d\u4eba\uff0c\u90fd\u77e5\u9053\u6709\u4e00\u4f4d\u603b\u5802\u4e3b\uff0c\u4f46\u77e5\u9053\u603b\u5802\u4e3b\u662f\u8c01\u7684\u4eba\uff1f\u5c31\u672a\u5fc5\u5f88\u591a\u4e86\u2026\u2026\u201d\u5f20\u56db\u59d1\u5fae\u7b11\u9053\uff1a\u201c\u4f60\u60f3\u60f3\u770b\u561b\uff0c\u90a3\u4eba\u80fd\u628a\u51e0\u4f4d\u526f\u603b\u5802\u4e3b\u4e00\u4e00\u7684\u8bf4\u7ed9\u4f60\u542c\uff0c\u4f46\u5374\u4e0d\u63d0\u4e00\u53e5\u603b\u5802\u4e3b\u7684\u59d3\u540d\u2026\u2026\u201d \u3000\u3000\u201c\u5927\u59d0\u7684\u610f\u601d\u53ef\u662f\u8bf4\uff0c\u4ed6\u4eec\u4e5f\u4e0d\u77e5\u9053\uff1f\u2026\u2026\u201d\u6c5f\u67ab\u795e\u60c5\u8083\u7136\u4ed6\u8bf4\u3002 \u3000\u3000\u201c\u6211\u662f\u8fd9\u6837\u60f3\uff0c\u77e5\u9053\u4e86\uff0c\u4ed6\u65e9\u5c31\u8bf4\u51fa\u6765\u4e86\uff0c\u4ed6\u80fd\u4ed4\u7ec6\u5730\u544a\u8bc9\u4f60\u5929\u9a6c\u7684\u4e8b\u60c5\uff0c\u201d\u5f20\u56db\u59d1\u8bf4\uff0c\u201c\u5206\u660e\u662f\u5728\u8ba8\u597d\u4f60\uff0c\u6211\u4e0d\u77e5\u9053\u4f60\u7528\u7684\u4ec0\u4e48\u624b\u6bb5\u5f81\u670d\u4e86\u4ed6\uff0c\u4f46\u6211\u53ef\u4ee5\u80af\u5b9a\u4ed6\u6709\u6c42\u4e8e\u4f60\u3002\u201d \u3000\u3000\u6c5f\u67ab\u6123\u4f4f\u4e86\u3002 \u3000\u3000\u201c\u5f20\u59d1\u5a18\u7684\u5206\u6790\uff0c\u5e94\u8be5\u4e0d\u9519\uff0c\u90a3\u4e2a\u603b\u5802\u4e3b\u795e\u79d8\u7684\u5f88\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u7b11\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u5f88\u5feb\u5c31\u53ef\u80fd\u6c42\u8bc1\u3002\u201d \u3000\u3000\u201c\u5509\uff01\u770b\u8d77\u6765\uff0c\u6211\u6c5f\u67ab\u8fd8\u662f\u592a\u5ae9\u4e86\uff01\u201d \u3000\u3000\u201c\u522b\u592a\u81ea\u8c26\uff0c\u4f60\u53ef\u4ee5\u8bfb\u4e66\u4e07\u5377\uff0c\u4e0a\u77e5\u5929\u6587\uff0c\u4e0b\u77e5\u5730\u7406\u2026\u2026\u201d\u5f20\u56db\u59d1\u5ae3\u7136\u7b11\u9053\uff1a\u201c\u4f46\u6c5f\u6e56\u7ecf\u9a8c\uff0c\u5374\u662f\u8981\u70b9\u70b9\u6ef4\u6ef4\u5730\u7d2f\u79ef\u8d77\u6765\uff0c\u6211\u4eec\u90fd\u6709\u5f88\u591a\u5730\u65b9\u4e0d\u5982\u4f60\uff0c\u4f46\u90fd\u613f\u4ee5\u5fc3\u4e2d\u6240\u77e5\u8865\u4f60\u7684\u4e0d\u8db3\uff0c\u4e5f\u613f\u610f\u4e3a\u4f60\u4ed8\u51fa\u4efb\u4f55\u7684\u727a\u7272\u4ee3\u4ef7\uff0c\u5e0c\u671b\u6211\u8fd9\u4e9b\u5766\u7136\u76f4\u8a00\uff0c\u4e0d\u4f1a\u4f24\u5bb3\u5230\u4f60\u3002\u201d \u3000\u3000\u201c\u4e0d\u4f1a\u7684\uff0c\u5927\u59d0\uff0c\u5c0f\u5f1f\u53d7\u6559\u826f\u591a\u3002\u201d \u3000\u3000\u6b27\u9633\u662d\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6c47\u6d93\u6ef4\u800c\u6210\u5927\u6cb3\uff0c\u6c5f\u8001\u5f1f\uff0c\u4e0d\u7ba1\u90a3\u4f4d\u603b\u5802\u4e3b\u662f\u4ec0\u4e48\u4eba\uff0c\u4f46\u4ed6\u7edd\u4e0d\u4f1a\u548c\u5185\u5bab\u4e00\u7cfb\u4e2d\u4eba\u52fe\u7ed3\uff01\u201d \u3000\u3000\u6c5f\u67ab\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u5bf9\uff01\u5982\u679c\u662f\u5929\u9a6c\u5802\u4e2d\u4eba\uff0c\u53ef\u80fd\u662f\u798f\u5bff\u9662\u4e2d\u7684\u516d\u5927\u697c\u4e3b\u4e4b\u4e00\u4e86\u3002\u201d \u3000\u3000\u201c\u516d\u5927\u697c\u4e3b\u2026\u2026\u201d\u94b1\u7f3a\u5947\u9053\uff1a\u201c\u90a3\u91cc\u53c8\u5192\u51fa\u6765\u4e86\u516d\u5927\u697c\u4e3b\uff1f\u201d \u3000\u3000\u201c\u662f\u7684\uff01\u5929\u9a6c\u603b\u5802\u4e2d\u6709\u5ea7\u798f\u5bff\u9662\uff0c\u91cc\u9762\u5206\u4e3a\u516d\u5927\u6402\uff0c\u5404\u6709\u697c\u4e3b\u4e00\u4eba\uff0c\u6240\u4ee5\uff0c\u4ed6\u4eec\u516d\u4e2a\u4eba\u548c\u56db\u4f4d\u526f\u603b\u5802\u4e3b\u624d\u662f\u5929\u9a6c\u5802\u4e2d\u771f\u6b63\u7684\u4e3b\u8111\u4eba\u7269\u2026\u2026\u201d\u6c5f\u67ab\u8bf4\uff0c\u201c\u6211\u4e0d\u77e5\u9053\u516d\u5927\u697c\u4e3b\u662f\u4ec0\u4e48\u4eba\u7269\uff0c\u4f46\u5fc5\u7136\u662f\u6c5f\u6e56\u4e2d\u7684\u5de8\u51f6\u8001\u9b54\uff0c\u6709\u4e9b\u4e8b\uff0c\u603b\u5802\u4e3b\u4f3c\u662f\u4e5f\u4e0d\u80fd\u72ec\u65ad\u72ec\u884c\uff0c\u5fc5\u9700\u8981\u53ec\u96c6\u516d\u5927\u697c\u4e3b\u548c\u56db\u4f4d\u526f\u603b\u5802\u4e3b\u4f1a\u5546\u4e4b\u540e\uff0c\u624d\u80fd\u51b3\u5b9a\u3002\u201d \u3000\u3000\u201c\u8fd9\u5c31\u5927\u6709\u53ef\u80fd\u4e86\uff0c\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u9053\uff1a\u201c\u4e0d\u7ba1\u516d\u5927\u697c\u4e3b\u53d7\u5230\u5982\u4f55\u7684\u5d07\u656c\uff0c\u8eab\u4efd\u5982\u4f55\u5c0a\u9ad8\uff0c\u4f46\u4ed6\u4eec\u5e76\u65e0\u5b9e\u6743\u2026\u2026\u201d \u3000\u3000\u201c\u90a3\u5c31\u5047\u5b9a\u795e\u79d8\u4eba\u662f\u516d\u5927\u697c\u4e3b\u4e4b\u4e00\uff0c\u54b1\u4eec\u4e5f\u7528\u4e0d\u7740\u63ed\u7a7f\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u2026\u2026\u201d\u7f57\u5170\u9053\uff1a\u201c\u6211\u4eec\u7684\u76ee\u7684\u662f\uff0c\u6311\u8d77\u4ed6\u4eec\u7684\u4e92\u76f8\u6740\u622e\uff0c\u5929\u9a6c\u5802\u4e2d\u5b9e\u529b\u5982\u6b64\u5f3a\u5927\uff0c\u8db3\u53ef\u548c\u5185\u5bab\u4e00\u7cfb\u4e2d\u4eba\u653e\u624b\u4e00\u6218\uff0c\u6709\u4e00\u4e2a\u5978\u7ec6\u5728\u5176\u4e2d\u4f5c\u795f\uff0c\u90a3\u5c31\u4f7f\u5185\u5bab\u4e2d\u4eba\u638c\u63e1\u4e00\u4e9b\u5148\u673a\uff01\u201d \u3000\u3000\u201c\u7528\u654c\u4eba\u7684\u529b\u91cf\uff0c\u6253\u51fb\u654c\u4eba\uff0c\u8fd9\u4e2a\u7b56\u7565\u8ba2\u7684\u9ad8\u660e\u2026\u2026\u201d\u5f20\u56db\u59d1\u8bf4\uff0c\u201c\u6211\u4eec\u5df2\u6563\u64ad\u7f57\u7f51\uff0c\u5e0c\u671b\u80fd\u5c3d\u5feb\u6536\u5230\u6548\u7528\uff0c\u8ba9\u4ed6\u4eec\u731c\u5fcc\u65e5\u6df1\uff0c\u62fc\u6740\u66f4\u70c8\uff0c\u6c34\u6d45\u9c7c\u81ea\u73b0\uff0c\u5230\u4ed6\u4eec\u4e3b\u6301\u7684\u4eba\u7269\u51fa\u52a8\uff0c\u5927\u6168\u5c31\u53ef\u4ee5\u627e\u51fa\u771f\u6b63\u7684\u9996\u8111\u4e86\uff0c\u73b0\u5728\uff0c\u8be5\u542c\u542c\u8001\u53eb\u5316\u548c\u79e6\u516b\u516c\u6709\u4e9b\u4ec0\u4e48\u6536\u83b7\u4e86\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u70b9\u70b9\u5934\uff0c\u6240\u6709\u4eba\u7684\u76ee\u5149\uff0c\u90fd\u6295\u6ce8\u5411\u94b1\u7f3a\u7684\u8eab\u4e0a\u3002 \u3000\u3000\u201c\u8001\u53eb\u5316\u7684\u6d88\u606f\u4e0d\u597d\uff0c\u6240\u4ee5\uff0c\u4e0d\u592a\u6562\u63d0\u51fa\u62a5\u544a\uff0c\u79e6\u5144\u805a\u96c6\u7684\u5b9e\u529b\uff0c\u8fd1\u65e5\u4e5f\u65e0\u6cd5\u6269\u5f20\uff0c\u6c5f\u6e56\u5927\u5c40\uff0c\u5f62\u5982\u4e00\u6c60\u6b7b\u6c34\uff0c\u590d\u751f\u4e4b\u673a\uff0c\u6050\u6015\u8981\u5168\u770b\u6c5f\u8001\u5f1f\u7684\u8fdb\u5c55\u4e86\uff01\u2026\u2026\u201d \u3000\u3000\u201c\u522b\u8bf4\u7684\u8fd9\u4e48\u65e0\u5948\uff0c\u6b64\u523b\u5f62\u52bf\uff0c\u6bd4\u8d77\u4e09\u4e2a\u6708\u524d\uff0c\u5f62\u5982\u5929\u58e4\u4e4b\u522b\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u9053\uff1a\u201c\u628a\u4f60\u77e5\u9053\u7684\u636e\u5b9e\u8bf4\u51fa\u6765\u5c31\u662f\u3002\u201d \u3000\u3000\u201c\u5bf9\uff01\u6ca1\u6709\u4e00\u756a\u9707\u52a8\u6c5f\u6e56\u4eba\u5fc3\u7684\u4f5c\u4e3a\uff0c\u5c31\u5f88\u96be\u805a\u96c6\u8d77\u4e00\u80a1\u529b\u91cf\uff0c\u2026\u2026\u201d\u6b27\u9633\u662d\u9053\uff1a\u201c\u6211\u4eec\u65e9\u5df2\u6709\u51b3\u5fc3\u5b64\u519b\u594b\u6218\uff0c\u6ca1\u6709\u805a\u96c6\u8d77\u540e\u63f4\u4e4b\u529b\uff0c\u5e76\u4e0d\u610f\u5916\uff0c\u94b1\u5144\uff0c\u7528\u4e0d\u7740\u96be\u8fc7\uff0c\u4e5f\u4e0d\u7528\u6709\u6240\u907f\u5fcc\uff0c\u53ea\u7ba1\u628a\u4e8b\u5b9e\u8bf4\u51fa\u6765\u3002\u201d \u3000\u3000\u201c\u5bf9\uff01\u8001\u53eb\u5316\u5df2\u5c3d\u4e86\u5fc3\u529b\uff0c\u4f46\u5c11\u6797\u3002\u6b66\u5f53\uff0c\u4e24\u5927\u652f\u6301\u6b66\u6797\u6b63\u4e49\u7684\u4e3b\u529b\uff0c\u4f3c\u5df2\u5b8c\u5168\u88ab\u6e17\u900f\uff0c\u63a7\u5236\uff0c\u4ed6\u4eec\u5982\u6d3e\u51fa\u4eba\u624b\u52a9\u6218\uff0c\u53ea\u6015\u53cd\u4f1a\u662f\u6211\u4eec\u7684\u654c\u4eba\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u70b9\u70b9\u5934\uff0c\u7b11\u9053\uff1a\u201c\u4f20\u627f\u7684\u89c4\u5f8b\u3002\u6388\u827a\u7684\u6cd5\u5219\uff0c\u5982\u4e0d\u80fd\u5927\u5e45\u66f4\u53d8\uff0c\u4e2d\u539f\u6b66\u6797\uff0c\u5fc5\u5c06\u65e5\u8d8b\u6ca1\u843d\uff0c\u94b1\u524d\u8f88\u4e0d\u7528\u4e3a\u6b64\u611f\u4f24\uff0c\u6211\u4eec\u8fd9\u756a\u884c\u52a8\uff0c\u867d\u662f\u4e3a\u6b66\u6797\u6b63\u4e49\u594b\u6218\uff0c\u4e5f\u662f\u4e3a\u4e00\u4e9b\u53d8\u6539\u50ac\u751f\uff0c\u4f46\u4e0d\u77e5\u6b64\u523b\u5df2\u805a\u96c6\u5728\u957f\u5b89\u7684\u6709\u591a\u5c11\u4eba\u624b\uff1f\u201d \u3000\u3000\u201c\u6211\u770b\u8fd9\u8981\u79e6\u5144\u8bf4\u660e\u4e86\uff0c\u2026\u2026\u201d\u94b1\u7f3a\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u53ef\u80fd\u4f1a\u8ba9\u8bf8\u4f4d\u5927\u5931\u6240\u671b\u4e86\u3002\u201d \u3000\u3000\u79e6\u516b\u516c\u957f\u957f\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5305\u62ec\u5404\u5927\u95e8\u6d3e\u4e2d\u4eba\uff0c\u805a\u96c6\u5728\u957f\u5b89\u7684\uff0c\u6709\u4e03\u5341\u4e5d\u540d\uff0c\u4e0d\u8fc7\uff0c\u80fd\u548c\u94b1\u5144\u5e76\u5217\u7684\u9ad8\u624b\uff0c\u52c9\u5f3a\u53ef\u4ee5\u7b97\u51fa\u4e24\u4e2a\uff0c\u4e00\u4e2a\u662f\u5c11\u6797\u5bfa\u7684\u94c1\u4f5b\u5927\u5e08\uff0c\u4e00\u4e2a\u662f\u6b66\u5f53\u95e8\u4e0b\u7684\u9752\u9e64\u9053\u957f\uff0c\u4f59\u4e0b\u4e03\u5341\u540d\u4eba\u624b\uff0c\u5927\u7ea6\u6709\u4e09\u5341\u540d\u53ef\u4ee5\u6d3e\u4e0a\u7528\u573a\uff0c\u5176\u4ed6\u7684\uff0c\u4e5f\u53ea\u80fd\u6447\u65d7\u5450\u558a\uff0c\u58ee\u58ee\u58f0\u52bf\u3002\u201d \u3000\u3000\u201c\u4eba\u6570\u4e0d\u5c11\u554a\uff01\u94b1\u5144\uff0c\u4e09\u5341\u540d\u53ef\u7528\u4e4b\u624b\uff0c\u5c31\u7531\u94b1\u5144\u8d1f\u8d23\uff0c\u7f16\u4f5c\u4e09\u961f\uff0c\u7531\u94c1\u4f5b\u5927\u5e08\u3001\u9752\u9e64\u9053\u957f\u548c\u79e6\u5144\u5404\u7387\u4e00\u961f\uff0c\u773c\u4e0b\u65e0\u4e8b\u53ef\u4f5c\uff0c\u627e\u4e00\u4e2a\u9002\u5f53\u7684\u5730\u65b9\uff0c\u8981\u4ed6\u4eec\u4e60\u7ec3\u6b66\u529f\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u8bf4\uff0c\u201c\u5982\u6709\u9700\u8981\u65f6\uff0c\u5144\u5f1f\u518d\u901a\u77e5\u8bf8\u4f4d\u884c\u52a8\u3002\u201d \u3000\u3000\u201c\u8001\u53eb\u5316\u6559\u9886\u53f0\u547d\u3002\u201d \u3000\u3000\u79e6\u516b\u516c\u63a5\u9053\uff1a\u201c\u79e6\u67d0\u4eba\u7acb\u523b\u5e26\u4ed6\u4eec\u79bb\u5f00\u957f\u5b89\u57ce\u5185\uff0c\u6211\u548c\u94b1\u5144\u65e9\u5df2\u627e\u597d\u4e00\u5904\u9690\u5bc6\u6240\u5728\uff0c\u6682\u65f6\u6816\u9690\u6b64\u5730\uff0c\u4e00\u9762\u5019\u547d\u6548\u529b\uff0c\u4e00\u9762\u518d\u8bbe\u6cd5\u62db\u7f57\u4eba\u624b\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u5c31\u8fd9\u4e48\u51b3\u5b9a\u4e86\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u94b1\u5144\u3001\u79e6\u5144\uff0c\u5148\u884c\u8bf7\u56de\u5427\uff01\u201d \u3000\u3000\u94b1\u7f3a\u3001\u79e6\u516b\u516c\u7ad9\u8d77\u8eab\u5b50\uff0c\u8f9e\u79bb\u800c\u53bb\u3002 \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u9053\uff1a\u201c\u6211\u548c\u5c1a\u4ee3\u66ff\u6c5f\u5144\u5f1f\u9001\u5ba2\u3002\u201d\u7d27\u5728\u4e24\u4eba\u8eab\u540e\u800c\u53bb\u3002 \u3000\u3000\u201c\u52ff\u602a\u6c5f\u6e56\u4e0a\u542c\u4e0d\u5230\u4e00\u70b9\u53cd\u5bf9\u7684\u98ce\u58f0\uff0c\u770b\u6765\uff0c\u4ed6\u4eec\u8fd9\u4e00\u756a\u5f81\u670d\u6c5f\u6e56\u7684\u5e03\u7f72\uff0c\u5341\u5206\u7cbe\u5bc6\uff0c\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u7edf\u6cbb\u5f97\u4e5f\u5f88\u4e25\u8c28\uff0c\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u63a5\u9053\uff1a\u201c\u8fd9\u4e9b\u4eba\u7528\u4e8e\u5bf9\u654c\uff0c\u81ea\u4e0d\u53ef\u6043\uff0c\u4f46\u5728\u5927\u5c40\u5f62\u6210\u5bf9\u5cd9\u5c40\u9762\u65f6\uff0c\u4ed6\u4eec\u767b\u9ad8\u4e00\u547c\uff0c\u6216\u53ef\u4f7f\u4e3a\u654c\u6240\u7528\u7684\u529b\u91cf\uff0c\u6709\u6240\u89c9\u9192\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\uff0c\u4f60\u5b89\u6392\u5f97\u5f88\u597d\uff0c\u76ee\u4e0b\u6700\u91cd\u8981\u7684\u4e8b\uff0c\u662f\u6211\u548c\u541f\u96ea\u3001\u541f\u971c\uff0c\u5e94\u8be5\u5982\u4f55\u884c\u52a8\uff1f\u6211\u9009\u7684\u90a3\u4e2a\u5730\u65b9\uff0c\u8ddd\u79bb\u6d1b\u9633\u5c45\u4e0d\u8fdc\uff0c\u5982\u679c\u5f00\u4e00\u5bb6\u8336\u697c\u9152\u9986\uff0c\u6211\u76f8\u4fe1\u53ef\u4ee5\u5438\u5f15\u4e0d\u5c11\u6d1b\u9633\u5c45\u7684\u5ba2\u4eba\uff0c\u4e0d\u8fc7\uff0c\u4e5f\u5c06\u4f1a\u5f15\u8d77\u4e0d\u65ad\u7684\u7eb7\u4e89\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u2026\u2026\u201d\u6b27\u9633\u662d\u5947\u9053\uff1a\u201c\u53ef\u662f\u6015\u62db\u60f9\u8d77\u6d1b\u9633\u5c45\u7684\u6ce8\u610f\u5417\uff1f\u201d \u3000\u3000\u201c\u8fd9\u53ea\u662f\u539f\u56e0\u4e4b\u4e00\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u4f46\u6700\u91cd\u8981\u7684\u8fd8\u662f\u4e00\u5bf9\u5b9d\u8d1d\u5973\u513f\uff0c\u79e6\u516b\u516c\u6765\u6b64\u4f5c\u5ba2\uff0c\u5c31\u5f15\u8d77\u4ed6\u4e24\u773c\u53d1\u76f4\uff0c\u5982\u679c\u8981\u5979\u4eec\u5f53\u7089\u5356\u9152\uff0c\u63d0\u58f6\u51b2\u8336\uff0c\u76f8\u4fe1\u5f88\u5feb\u5c31\u4f1a\u5f15\u52a8\u957f\u5b89\u57ce\u7684\u6e38\u8702\u72c2\u8776\uff0c\u6211\u4eec\u6ca1\u6709\u6d1b\u9633\u5c45\u90a3\u4e48\u5927\u7684\u6c14\u52bf\uff0c\u8fd9\u7ea0\u7eb7\u8fd8\u80fd\u5c11\u5f97\u4e86\u4e48\uff1f\u201d \u3000\u3000\u6b27\u9633\u662d\u671b\u4e86\u4e8c\u5973\u4e00\u773c\uff0c\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u56db\u59d1\u554a\uff01\u624d\u8ddf\u4f60\u51fa\u53bb\u4e09\u5929\uff0c\u600e\u4e48\u4e24\u4e2a\u4e2b\u5934\u5c31\u53d8\u4e86\u6837\u5b50\uff1f\u201d \u3000\u3000\u201c\u7236\u6bcd\u7684\u9057\u4f20\u554a\uff01\u7239\u82f1\u4fca\uff0c\u5a18\u7f8e\u8273\uff0c\u751f\u51fa\u5973\u513f\uff0c\u517c\u5f97\u4e86\u4e24\u4eba\u4e4b\u957f\uff0c\u518d\u52a0\u4e0a\u5929\u72d0\u6b66\u827a\uff0c\u600e\u4f1a\u4e0d\u98ce\u60c5\u4e07\u79cd\uff0c\u60f9\u5165\u8ff7\u60d1\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u7528\u4e0d\u7740\u6d1b\u9633\u5c45\u7684\u4eba\u6765\u627e\u9ebb\u70e6\uff0c\u6211\u770b\uff0c\u6bcf\u5929\u5c31\u95f9\u5f97\u5934\u6655\u8111\u80c0\u4e86\uff0c\u6c5f\u67ab\u554a\uff01\u4f60\u4f5c\u4e2a\u51b3\u5b9a\u5427\uff01\u8fd9\u4ef6\u4e8b\u8be5\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u671b\u671b\u4e8c\u5973\uff0c\u53d1\u89c9\u5979\u4eec\u7709\u68a2\u3001\u773c\u89d2\u95f4\u98ce\u91c7\u98d8\u8361\uff0c\u90a3\u80a1\u8ff7\u4eba\u7684\u5473\u9053\uff1a\u662f\u7537\u4eba\u90fd\u5e94\u8be5\u65e0\u6cd5\u6297\u62d2\uff0c\u4e0d\u7981\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5927\u59d0\u65e2\u7136\u4e0d\u613f\u629b\u5934\u9732\u9762\uff0c\u6211\u770b\u5f97\u6539\u53d8\u4e2a\u65b9\u6cd5\u4e86\u3002\u201d \u3000\u3000\u7f57\u5170\u4e5f\u53d1\u89c9\u4e86\uff0c\u4e8c\u5973\u5a9a\u6001\u6a2a\u751f\uff0c\u5c31\u7b97\u655b\u6536\u8d77\u5929\u72d0\u6b66\u529f\uff0c\u5355\u662f\u8fd9\u5929\u751f\u4e3d\u8d28\u6563\u53d1\u51fa\u7684\u9b45\u529b\uff0c\u5c31\u53eb\u4eba\u96be\u80fd\u6297\u62d2\u4e86\uff0c\u5fc3\u5934\u4ea6\u4e3a\u4e4b\u9707\u52a8\uff0c\u5fd6\u9053\uff1a\u201c\u4e24\u4e2a\u4e2b\u5934\u600e\u4e48\u4e86\uff0c\u8fd9\u4e24\u4e09\u5929\u7684\u5de5\u592b\uff0c\u5df2\u60c5\u6000\u5f00\u52a8\uff0c\u98ce\u60c5\u98d8\u98de\uff0c\u4f46\u4ed4\u7ec6\u5bdf\u770b\uff0c\u53d1\u89c9\u4e8c\u5973\u4ecd\u5c5e\u5b8c\u58c1\uff0c\u8fd9\u7a76\u7adf\u662f\u600e\u56de\u4e8b\u5462\uff1f\u201d \u3000\u3000\u201c\u56db\u59d1\uff0c\u4f60\u771f\u662f\u6559\u5bfc\u6709\u65b9\u554a\uff01\u6211\u6559\u5979\u4eec\u7ec3\u4e86\u5341\u51e0\u5e74\u7684\u5929\u72d0\u6b66\u529f\uff0c\u5c31\u4e0d\u5982\u4f60\u8fd9\u4e24\u4e09\u5929\u7684\u8bad\u7ec3\uff0c\u771f\u662f\u6210\u5c31\u60ca\u4eba\u554a\uff01\u201d\u7f57\u5170\u6709\u70b9\u5fc3\u75bc\uff0c\u4e5f\u6709\u4f69\u670d\u5730\u8bf4\u3002 \u3000\u3000\u201c\u600e\u4e48\u80fd\u602a\u5230\u5f20\u59e8\u7684\u5934\u4e0a\u2026\u2026\u201d\u541f\u96ea\u8bf4\uff0c\u201c\u8fd8\u4e0d\u662f\u5a18\u6559\u5bfc\u4e4b\u529f\uff0c\u8fc7\u53bb\uff0c\u6211\u4eec\u86f0\u4f0f\u6df1\uff0c\u6bcf\u5929\u89c1\u5230\u7684\u5c31\u662f\u7239\u5a18\u4e24\u4e2a\uff0c\u5c31\u7b97\u5df2\u4e60\u6210\u5929\u72d0\u7684\u6280\u827a\uff0c\u4e5f\u662f\u65e0\u6cd5\u53d1\u6325\u554a\uff01\u201d \u3000\u3000\u201c\u662f\u561b\uff01\u957f\u5b89\u57ce\u4e2d\u90a3\u4e48\u591a\u7ea2\u7537\u7eff\u5973\uff0c\u81ea\u7136\u4f1a\u5f15\u53d1\u51fa\u6211\u4eec\u4e89\u80dc\u4e4b\u5fc3\uff0c\u2026\u2026\u201d\u541f\u971c\u8bf4\uff0c\u201c\u8fd9\u5c31\u5982\u5c71\u6d2a\u66b4\u53d1\uff0c\u5c31\u4e00\u53d1\u96be\u6536\u4e86\u3002\u201d \u3000\u3000\u7f57\u5170\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u53cd\u6b63\uff0c\u5a18\u4e0d\u5982\u5f20\u59e8\u5c31\u662f\uff0c\u65e9\u77e5\u5982\u6b64\uff0c\u8be5\u65e9\u4e9b\u628a\u4f60\u4eec\u4ea4\u7ed9\u5f20\u59e8\uff0c\u4e5f\u8bb8\u65e9\u6210\u6c14\u5019\u4e86\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u53ea\u6709\u82e6\u7b11\uff0c\u618b\u4e86\u4e00\u8154\u6012\u706b\uff0c\u4e00\u80a1\u8111\u513f\u53d1\u5728\u4e86\u6c5f\u67ab\u7684\u5934\u4e0a\uff0c\u9053\uff1a\u201c\u6211\u53ea\u662f\u544a\u8bc9\u4f60\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u53ef\u6ca1\u6709\u8981\u4f60\u6539\u53d8\u65b9\u6cd5\uff0c\u5927\u59d0\u8ba4\u547d\u4e86\uff0c\u4f60\u8981\u6211\u5e72\u4ec0\u4e48\uff0c\u6211\u90fd\u7b54\u5e94\uff0c\u8bf4\u5427\uff01\u4f60\u51c6\u5907\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u5bf9\u5f20\u56db\u59d1\u6c5f\u67ab\u5fc3\u4e2d\u6709\u4e00\u4efd\u6df1\u6df1\u7684\u5080\u548e\uff0c\u5979\u786e\u5b9e\u4e3a\u6c5f\u67ab\u5728\u71c3\u70e7\u81ea\u5df1\uff0c\u656c\u91cd\u8fc7\u4efd\uff0c\u5c31\u53d8\u6210\u6709\u70b9\u6015\u4e86\uff0c\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u597d\uff01\u5343\u9519\u4e07\u9519\uff0c\u9519\u5728\u5c0f\u5f1f\u4e00\u4eba\uff0c\u5927\u5c40\u5e73\u5b9a\u4e4b\u540e\uff0c\u5c0f\u5f1f\u4ee5\u6027\u547d\u5411\u5927\u59d0\u8bf7\u7f6a\uff0c\u8981\u6211\u6b7b\u3001\u8981\u6211\u6d3b\uff0c\u53ea\u51ed\u5927\u59d0\u4e00\u53e5\uff0c\u5c0f\u5f1f\u77e5\u9053\uff1a\u6211\u4e8f\u6b20\u4f60\u7684\u592a\u591a\u4e86\u3002\u201d \u3000\u3000\u201c\u600e\u4e48\uff1f\u4f60\u5728\u8bda\u5fc3\u5455\u6211\u5417\uff1f\u2026\u2026\u201d\u5f20\u56db\u59d1\u4f59\u6012\u672a\u606f\u7684\u8bf4\u3002 \u3000\u3000\u201c\u4e0d\uff01\u5c0f\u5f1f\u8a00\u51fa\u8877\u8bda\uff0c\u7edd\u65e0\u4e00\u5b57\u865a\u5047\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u7684\u53cc\u76ee\u4e2d\u8574\u542b\u4e86\u6cea\u5149\uff0c\u5f3a\u5fcd\u7740\u6ca1\u8ba9\u773c\u6cea\u6d41\u4e0b\u6765\u3002 \u3000\u3000\u8fd9\u4e0d\u662f\u5929\u72d0\u6280\u827a\uff0c\u800c\u662f\u81f3\u60c5\u81f3\u6027\u7684\u6d41\u9732\u3002 \u3000\u3000\u201c\u5f20\u59e8\uff01\u4f60\u628a\u6c5f\u5927\u54e5\u9a82\u54ed\u4e86\uff0c\u2026\u2026\u201d\u541f\u96ea\u9eef\u7136\u8bf4\u9053\uff1a\u201c\u4ed6\u662f\u8eab\u8d1f\u91cd\u4efb\u7684\u82f1\u96c4\uff0c\u4f60\u4e0d\u80fd\u8fd9\u6837\u6298\u78e8\u4ed6\u5440\u3002\u201d \u3000\u3000\u201c\u5f20\u59e8\uff01\u4f60\u60f3\u9a82\u4eba\uff0c\u5c31\u9a82\u6211\u8ddf\u59d0\u5427\uff01\u9a82\u7684\u4e0d\u6d88\u6c14\uff0c\u6253\u6211\u4eec\u4e00\u987f\u4e5f\u884c\uff0c\u522b\u518d\u602a\u6c5f\u5927\u54e5\u4e86\uff0c\u4ed6\u597d\u5bb3\u6015\u4f60\u751f\u6c14\u5440\uff01\u201d \u3000\u3000\u5f20\u56db\u59d1\u6014\u4f4f\u4e86\uff0c\u5979\u7edd\u672a\u60f3\u5230\u6c5f\u67ab\u5bf9\u5979\u7528\u60c5\u662f\u5982\u6b64\u4e4b\u6df1\uff0c\u5fc3\u4e2d\u90a3\u4efd\u611f\u52a8\uff0c\u771f\u5230\u4e86\u60c5\u7518\u6548\u6b7b\u7684\u5883\u754c\uff0c\u4e24\u884c\u70ed\u6cea\u593a\u7736\u800c\u51fa\uff0c\u7b11\u9053\uff1a\u201c\u5f20\u59e8\u90a3\u91cc\u662f\u602a\u4ed6\uff0c\u6211\u662f\u8bf7\u547d\u554a\uff01\u4ed6\u662f\u4e3b\u5e05\uff0c\u8981\u5f20\u59e8\u53bb\u6b7b\uff0c\u5f20\u59e8\u4e5f\u96be\u8bf4\u4e2a\u4e0d\u5b57\u554a\u3002\u201d \u3000\u3000\u7f57\u5170\u4e5f\u6709\u7740\u610f\u5916\u7684\u611f\u89c9\uff0c\u5929\u72d0\u4f20\u5165\uff0c\u600e\u4f1a\u6709\u5982\u6b64\u6df1\u601d\u539a\u60c5\u3002 \u3000\u3000\u8fd9\u5c31\u662f\u4eba\u6027\uff0c\u4efb\u4f55\u7cbe\u7edd\u7684\u6280\u827a\uff0c\u4e5f\u65e0\u6cd5\u6539\u53d8\u7684\u4eba\u6027\u3002 \u3000\u3000\u8fd9\u4e5f\u4f7f\u6b27\u9633\u662d\u548c\u7f57\u5170\u5bf9\u6c5f\u67ab\u6709\u4e86\u66f4\u6df1\u4e00\u5c42\u7684\u8ba4\u8bc6\u3002\u201c\u5927\u59d0\uff0c\u5929\u72d0\u6b66\u529f\uff0c\u867d\u7136\u6deb\u8361\u8be1\u5f02\uff0c\u4f46\u90a3\u53ea\u662f\u5b83\u7684\u5916\u8868\uff0c\u771f\u5230\u4e86\u7089\u706b\u7eaf\u9752\u4e4b\u5883\uff0c\u53cd\u800c\u662f\u60c5\u771f\u610f\u91cd\uff0c\u500d\u4e8e\u5e38\u4eba\uff0c\u5929\u72d0\u592b\u4eba\u7684\u540e\u671f\u4e3a\u4eba\uff0c\u53ef\u4e3a\u4f50\u8bc1\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u8981\u8bf4\u4e86\uff0c\u770b\u4f60\u5e73\u5e38\u6f47\u6d12\u4e0d\u7fa4\uff0c\u4e00\u526f\u4e0d\u5728\u4e4e\u7684\u6837\u5b57\uff0c\u4f3c\u4e4e\u662f\u5929\u584c\u4e86\uff0c\u4e5f\u4e0d\u5173\u4f60\u7684\u4e8b\uff0c\u2026\u2026\u201d\u5f20\u56db\u59d1\u8bf4\uff0c\u201c\u600e\u77e5\u9053\u662f\u7eb8\u672d\u7684\u4eba\uff0c\u51e0\u53e5\u8bdd\u5c31\u4f1a\u70ed\u6cea\u76c8\u7736\uff0c\u4e00\u4e2a\u5927\u7537\u4eba\uff0c\u4e5f\u4e0d\u5bb3\u7f9e\u554a\uff01\u4ee5\u540e\uff0c\u6211\u518d\u4e0d\u4f1a\u8ddf\u4f60\u62ac\u6760\u4e86\u3002\u201d \u3000\u3000\u5979\u6570\u8bf4\u6c5f\u67ab\uff0c\u4f46\u81ea\u5df1\u7684\u773c\u6cea\u5374\u50cf\u4e0b\u96e8\u4f3c\u7684\u6ef4\u6ef4\u55d2\u55d2\u5411\u4e0b\u6eda\u843d\u3002 \u3000\u3000\u201c\u56db\u59d1\uff0c\u4f60\u662f\u5973\u4e2d\u8c6a\u6770\uff0c\u6211\u662f\u4ece\u672a\u89c1\u4f60\u54ed\u8fc7\uff0c\u4eca\u5929\u5b9e\u5728\u5f00\u773c\u754c\u4e86\uff0c\u2026\u2026\u201d\u6b27\u9633\u662d\u5927\u7b11\u9053\uff1a\u201c\u6c5f\u5144\u5f1f\uff0c\u5927\u4e08\u592b\u6d41\u8840\u4e0d\u6d41\u6cea\uff0c\u4f60\u53ef\u662f\u4e0d\u80fd\u54ed\u554a\u3002\u201d \u3000\u3000\u201c\u65e2\u7136\u51fa\u4e86\u4e11\uff0c\u5e72\u8106\u5c31\u5927\u54ed\u4e00\u573a\u5427\uff01\u2026\u2026\u201d\u5f20\u56db\u59d1\u5927\u7b11\u8d77\u6765\uff0c\u7b11\u58f0\u548c\u6cea\u6c34\u6df7\u5728\u4e00\u8d77\uff0c\u770b\u4e0a\u53bb\u522b\u5177\u60c5\u8da3\u3002 \u3000\u3000\u7f57\u5170\u53d6\u51fa\u624b\u5e15\uff0c\u62ed\u7740\u5f20\u56db\u59d1\u6eda\u843d\u7684\u6cea\u6c34\uff0c\u9053\uff1a\u201c\u6211\u660e\u767d\u4f60\u5fc3\u4e2d\u59d4\u5c48\uff0c\u54ed\u5427\uff01\u54ed\u51fa\u4f60\u5fc3\u4e2d\u6240\u6709\u7684\u7231\u548c\u6068\u3002\u201d \u3000\u3000\u201c\u6211\u5df2\u7ecf\u6ca1\u6709\u6068\u4e86\u2026\u2026\u201d\u5f20\u56db\u59d1\u8bf4\uff0c\u201c\u7f57\u5170\uff0c\u671d\u95fb\u9053\u5915\u6b7b\u53ef\u77e3\uff01\u6211\u8fd9\u4e00\u751f\u4e2d\u771f\u7684\u5df2\u518d\u65e0\u9057\u61be\u3002\u201d \u3000\u3000\u7f57\u5170\u56de\u987e\u4e86\u6b27\u9633\u662d\u4e00\u773c\uff0c\u9ed8\u7136\u65e0\u8bed\u3002 \u3000\u3000\u201c\u770b\u4ec0\u4e48\uff1f\u2026\u2026\u201d\u6b27\u9633\u662d\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u5982\u662f\u6709\u4e86\u4e09\u957f\u4e24\u77ed\uff0c\u6211\u8fd8\u80fd\u6d3b\u5f97\u4e0b\u53bb\u5417\uff1f\u201d \u3000\u3000\u201c\u8fd9\u53e5\u8bdd\uff0c\u6211\u60f3\u4e86\u51e0\u5341\u5e74\uff0c\u4eca\u5929\u603b\u7b97\u542c\u5230\u4e86\u2026\u2026\u201d\u7f57\u5170\u5fae\u7b11\u5982\u82b1\u5730\u8bf4\uff0c\u201c\u4f60\u6ca1\u6709\u4e8b\u5c31\u627e\u6211\u5435\u67b6\u3001\u6c14\u6211\uff0c\u5455\u6211\uff0c\u5176\u5b9e\uff0c\u6211\u5fc3\u4e2d\u5f88\u660e\u767d\uff0c\u4f60\u60c5\u6df1\u5982\u6d77\uff0c\u53ef\u662f\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u80af\u8bf4\u51fa\u6765\u5462\uff1f\u8981\u4e0d\u662f\u6c5f\u5144\u5f1f\u548c\u56db\u59d1\u8fd9\u4e00\u95f9\uff0c\u4e5f\u903c\u4e0d\u51fa\u4f60\u8fd9\u53e5\u8bdd\u6765\uff0c\u5343\u4e07\u53e5\u751c\u8a00\u871c\u8bed\uff0c\u4e5f\u6ca1\u6709\u8fd9\u53e5\u8bdd\u6765\u7684\u8e0f\u5b9e\u3002\u201d \u3000\u3000\u201c\u4f60\u5462\uff1f\u2026\u2026\u201d\u6b27\u9633\u662d\u9053\uff1a\u201c\u6211\u662f\u8bf4\u51fa\u6765\uff0c\u2026\u2026\u201d \u3000\u3000\u201c\u95ee\u7684\u597d\uff01\u4f60\u6b7b\u4e86\uff0c\u6211\u7edd\u4e0d\u72ec\u751f\uff0c\u82b1\u5bb9\u6708\u8c8c\uff0c\u4e3a\u541b\u8d4f\uff0c\u2026\u2026\u201d\u7f57\u5170\u7b11\u9053\uff1a\u201c\u643a\u624b\u5171\u9752\u9e3e\u3002\u201d \u3000\u3000\u201c\u8fd9\u624d\u662f\u795e\u4ed9\u7737\u5c5e\uff0c\u4e0a\u7a77\u78a7\u843d\u4e0b\u9ec4\u6cc9\uff0c\u9634\u9633\u65e0\u963b\u9694\u3002\u201d \u3000\u3000\u6b27\u9633\u662d\u653e\u58f0\u5927\u7b11\u3002 \u3000\u3000\u201c\u7239\uff01\u6211\u4eec\u5462\uff1f\u201d \u3000\u3000\u541f\u96ea\u53cc\u76ee\u6df1\u6ce8\u6b27\u9633\u662d\u3002 \u3000\u3000\u201c\u4f60\u4eec\u2026\u2026\u201d\u6b27\u9633\u662d\u679c\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u5e74\u7eaa\u8f7b\u8f7b\u7684\uff0c\u600e\u4e48\u4f1a\u6b7b\uff1f\u201d \u3000\u3000\u201c\u6211\u4eec\u6b7b\u4e86\uff0c\u4e5f\u5c31\u7f62\u4e86\u2026\u2026\u201d\u541f\u96ea\u9053\uff1a\u201c\u6211\u662f\u8bf4\u6c5f\u5927\u54e5\u6b7b\u4e86\uff0c\u6211\u4eec\u8be5\u600e\u4e48\u529e\u5462\uff1f\u201d \u3000\u3000\u6b27\u9633\u662d\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u8fd9\u4e2a\u2026\u2026\u7f57\u5170\u4f60\u8bf4\u5440\uff1f\u201d \u3000\u3000\u201c\u95ee\u95ee\u6c5f\u5144\u5f1f\u5427\uff01\u201d \u3000\u3000\u6c5f\u67ab\u5fc3\u5934\u9707\u52a8\uff0c\u4f46\u5374\u53c8\u65e0\u6cd5\u5f00\u53e3\u3002 \u3000\u3000\u201c\u8981\u6211\u95ee\u4ec0\u4e48\u5462\uff1f\u6c5f\u5144\u5f1f\uff0c\u8fd9\u4e2b\u5934\u771f\u628a\u6211\u95ee\u7cca\u6d82\u4e86\uff1f\u201d \u3000\u3000\u5f20\u56db\u59d1\u5fae\u5fae\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u4e24\u4e2a\u4e2b\u5934\u7684\u5fc3\u610f\u6211\u6e05\u695a\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u4f60\u6e05\u695a\uff0c\u4f60\u5c31\u8bf4\u51fa\u6765\u5427\uff01\u2026\u2026\u201d\u6b27\u9633\u662d\u9053\uff1a\u201c\u6211\u53ef\u662f\u778e\u5b50\u6478\u8c61\uff0c\u6478\u4e0d\u5230\u5934\u8111\u4f55\u5728\uff1f\u201d \u3000\u3000\u201c\u4e24\u4e2a\u4e2b\u5934\u8981\u4f60\u4e00\u53e5\u8bdd\uff0c\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u4e5f\u5728\u8981\u6c5f\u67ab\u7684\u627f\u8bfa\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u2026\u2026\u201d\u6b27\u9633\u662d\u6709\u70b9\u660e\u767d\u4e86\uff0c\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u6211\u770b\uff0c\u5c31\u7531\u4f60\u56db\u59d1\u4f5c\u4e3b\u4e86\uff0c\u5979\u4eec\u4e5f\u662f\u4f60\u7684\u5f92\u5f1f\uff0c\u6211\u770b\uff0c\u6211\u548c\u5170\u513f\u5c31\u5168\u6743\u59d4\u6258\u4e86\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u770b\u770b\u7f57\u5170\uff0c\u9053\uff1a\u201c\u7f57\u5170\uff0c\u4f60\u6709\u4ec0\u4e48\u610f\u89c1\uff1f\u201d \u3000\u3000\u201c\u6211\u2026\u2026\u6211\u4e0d\u77e5\u9053\uff1a\u59bb\u4ece\u592b\u610f\uff0c\u5c31\u7531\u4f60\u4f5c\u4e3b\u5427\uff01\u201d \u3000\u3000\u5f20\u56db\u59d1\u5401\u4e00\u53e3\u6c14\uff0c\u770b\u770b\u541f\u96ea\u3001\u541f\u971c\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u5462\uff1f\u201d \u3000\u3000\u201c\u4e5f\u7531\u5f20\u59e8\u4f5c\u4e3b\u3002\u201d\u4e8c\u5973\u9f50\u58f0\u56de\u7b54\u3002 \u3000\u3000\u5f20\u56db\u59d1\u7684\u76ee\u5149\u76ef\u6ce8\u6c5f\u67ab\u7684\u8138\u4e0a\uff0c\u9053\uff1a\u201c\u5144\u5f1f\uff0c\u4f60\u53ef\u6709\u4ec0\u4e48\u9ad8\u89c1\uff1f\u201d \u3000\u3000\u201c\u53ea\u6709\u2014\u4e2a\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u5927\u59d0\u5429\u5490\u4e0b\u6765\uff0c\u5c0f\u5f1f\u65e0\u4e0d\u4ece\u547d\uff0c\u4f46\u4e00\u5207\u90fd\u8981\u7b49\u6c5f\u6e56\u4e0a\u5927\u4e8b\u5df2\u5b9a\u4e4b\u540e\uff0c\u624d\u80fd\u7b97\u6570\uff0c\u5982\u679c\u6211\u4e0d\u5e78\u6218\u6b7b\u4e86\uff0c\u90a3\u5c31\u4e00\u5207\u4f5c\u7f62\u3002\u201d \u3000\u3000\u201c\u6c5f\u5927\u54e5\uff0c\u4f60\u6b7b\u4e86\uff0c\u6211\u4eec\u4e5f\u4e0d\u80fd\u6d3b\u4e0b\u53bb\u2026\u2026\u201d\u541f\u971c\u9053\uff1a\u201c\u4f60\u6d3b\u7740\uff0c\u968f\u4fbf\u505a\u4ec0\u4e48\uff0c\u6211\u4eec\u90fd\u4e0d\u4f1a\u7ba1\u4f60\uff0c\u4f46\u4f60\u6b7b\u4e86\uff0c\u6211\u4eec\u5e0c\u671b\u548c\u4f60\u5e76\u68fa\u5893\u7a74\u2026\u2026\u201d \u3000\u3000\u4e0d\u91cd\u751f\u524d\u91cd\u6b7b\u540e\uff0c\u4e8c\u5973\u7528\u60c5\u7684\u6df1\u523b\uff0c\u4e5f\u4e0d\u8ba9\u7236\u6bcd\u4e13\u7f8e\u4e86\u3002 \u3000\u3000\u5f20\u56db\u59d1\u9053\uff1a\u201c\u597d\uff01\u7531\u6b64\u523b\u8d77\uff0c\u4f60\u4eec\u5c31\u662f\u6c5f\u67ab\u7684\u4eba\uff0c\u751f\u4e3a\u6b66\u6797\u6548\u547d\uff0c\u5927\u5c40\u5b89\u5b9a\u540e\uff0c\u518d\u8bba\u5a5a\u59fb\uff0c\u5f20\u59e8\u4fdd\u8bc1\u6c5f\u67ab\u4e00\u5b9a\u5802\u5802\u6b63\u6b63\u7684\u5a36\u4f60\u4eec\uff0c\u4f46\u6211\u53ef\u4e0d\u4fdd\u8bc1\u4f60\u4eec\u662f\u6392\u884c\u7b2c\u51e0\uff1f\u201d \u3000\u3000\u201c\u5927\u5bb6\u90fd\u77e5\u9053\u5929\u72d0\u795e\u529f\u5996\u5a9a\u60d1\u4eba\u2026\u2026\u201d\u541f\u96ea\u9053\uff1a\u201c\u5374\u4e0d\u77e5\u9053\u5929\u72d0\u6b66\u529f\u90aa\u4e2d\u6709\u6b63\uff0c\u5a18\u53ea\u53e3\u8ff0\uff0c\u6ca1\u6709\u7ec3\u8fc7\uff0c\u4e0d\u77e5\u9053\u4e00\u65e6\u5b83\u60c5\u6709\u6240\u949f\uff0c\u5c31\u4f1a\u7ec8\u8eab\u4e0d\u6e1d\uff0c\u4f46\u5916\u5728\u8868\u73b0\u7684\u4ecd\u7136\u662f\u70df\u89c6\u5a9a\u884c\uff0c\u8d1e\u8282\u5728\u5bf8\u5fc3\u4e4b\u95f4\u3002\u201d \u3000\u3000\u201c\u597d\u4e86\uff0c\u8001\u5b50\u5df2\u7ecf\u660e\u767d\u4f60\u4eec\u7684\u5fc3\u610f\u2026\u2026\u201d\u6b27\u9633\u662d\u9053\uff1a\u201c\u4f46\u5a5a\u5ac1\u7684\u65e5\u5b50\u4e0d\u5b8c\uff0c\u73b0\u5728\u7528\u4e0d\u7740\u6539\u53e3\u79f0\u547c\u5427\uff01\u201d \u3000\u3000\u201c\u90a3\u5c31\u4e0d\u662f\u5929\u72d0\u5f1f\u5b50\u4e86\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u5929\u72d0\u6b66\u529f\u91cd\u5b9e\u8d28\uff0c\u4e0d\u91cd\u5916\u8868\u3002\u201d \u3000\u3000\u53ea\u542c\u4e00\u58f0\u91cd\u91cd\u7684\u54b3\u55fd\uff0c\u4e03\u5b9d\u548c\u5c1a\u5927\u6b65\u884c\u4e86\u8fdb\u6765\uff0c\u9053\uff1a\u201c\u8bf8\u4f4d\u7684\u5bb6\u52a1\u4e8b\uff0c\u8c08\u5b8c\u4e86\u5427\uff01\u201d \u3000\u3000\u6b27\u9633\u662d\u7b11\u9053\uff1a\u201c\u4f60\u548c\u5c1a\u53ef\u662f\u5728\u80cc\u540e\u5077\u542c\u554a\uff01\u201d \u3000\u3000\u201c\u6211\u548c\u5c1a\u5982\u8bf4\u6ca1\u6709\u542c\u5230\uff0c\u4e0d\u4f46\u4f60\u4eec\u4e0d\u4fe1\uff0c\u8fde\u6211\u548c\u5c1a\u81ea\u5df1\u4e5f\u4e0d\u76f8\u4fe1\u2026\u2026\u201d \u3000\u3000\u201c\u4f60\u542c\u5230\u591a\u5c11\uff1f\u51fa\u5bb6\u4eba\u6212\u6253\u8bf3\u8a00\uff0c\u53ef\u8981\u5b9e\u8bdd\u5b9e\u8bf4\u554a\u2026\u2026\u201d\u5f20\u56db\u59d1\u53ee\u4e0a\u4e00\u53e5\u3002 \u3000\u3000\u201c\u8be5\u542c\u5230\u7684\uff0c\u6211\u548c\u5c1a\u542c\u5230\u4e86\uff0c\u4e0d\u8be5\u542c\u5230\u7684\uff0c\u548c\u5c1a\u4e00\u8f6c\u5934\uff0c\u5168\u90fd\u5fd8\u4e86\u3002\u201d \u3000\u3000\u201c\u90a3\u6700\u597d\uff01\u5927\u548c\u5c1a\uff0c\u4f60\u662f\u4f4d\u6709\u9053\u9ad8\u50e7\uff0c\u73b0\u5728\uff0c\u6211\u4eec\u60f3\u542c\u542c\u4f60\u5bf9\u654c\u4e4b\u7b56\u3002\u201d\u541f\u96ea\u51e0\u53e5\u8bdd\u626d\u8f6c\u4e86\u8bdd\u9898\u3002 \u3000\u3000\u201c\u5bf9\uff01\u548c\u5c1a\u4e5f\u6b63\u8981\u8c08\u8c08\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u5f00\u9152\u697c\u8336\u9986\u8fd9\u6863\u4e8b\uff0c\u5c31\u6b64\u4f5c\u7f62\uff0c\u4e24\u4f4d\u59d1\u5a18\u7edd\u4ee3\u98ce\u534e\uff0c\u5f88\u5feb\u4f1a\u5f15\u8d77\u654c\u4eba\u6ce8\u610f\u3002\u201d \u3000\u3000\u201c\u5927\u548c\u5c1a\u6709\u4f55\u826f\u7b56\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u6211\u4eec\u4e0d\u5b9c\u518d\u53d6\u5b88\u52bf\uff0c\u5175\u6765\u5c06\u6321\uff0c\u6c34\u6765\u571f\u63a9\uff0c\u5e94\u8be5\u5c55\u5f00\u4e00\u9635\u51cc\u5389\u7684\u653b\u52bf\uff0c\u4f7f\u4ed6\u4eec\u9635\u811a\u81ea\u4e71\u3002\u201d \u3000\u3000\u201c\u4e0d\u9519\uff01\u6240\u4ee5\u8981\u5c55\u5f00\u72d9\u6740\uff0c\u548c\u5c1a\u7684\u770b\u6cd5\u662f\u4ee5\u5185\u5bab\u4e00\u7cfb\u4e2d\u4eba\u4e3a\u4e3b\uff0c\u903c\u4ed6\u4eec\u5168\u529b\u6295\u5165\u2026\u2026\u201d \u3000\u3000\u4e03\u5b9d\u5927\u5e08\u8bf4\u51fa\u4e86\u81ea\u5df1\u7684\u4e00\u756a\u8ba1\u5212\u3002 \u3000\u3000\u90a3\u662f\u4e00\u79cd\u4e25\u5bc6\u7684\u5e03\u7f72\u72d9\u51fb\uff0c\u4e00\u6ce2\u4e0d\u6210\uff0c\u4e00\u6ce2\u518d\u8d77\uff0c\u9009\u5b9a\u4e86\u5bf9\u8c61\uff0c\u5c31\u975e\u8981\u6740\u6b7b\u4e0d\u53ef\u3002 \u3000\u3000\u9152\u8089\u548c\u5c1a\u7684\u72d9\u6740\u8ba1\u5212\uff0c\u867d\u662f\u8fde\u73af\u51fa\u624b\uff0c\u5c42\u5c42\u963b\u7edd\uff0c\u4f46\u6295\u5165\u7684\u4eba\u529b\u5e76\u975e\u5f88\u591a\uff0c\u4e3b\u8981\u7684\u6267\u884c\u4eba\u662f\u96ea\u3001\u971c\u4e8c\u5973\u3002 \u3000\u3000\u5f20\u56db\u59d1\u548c\u7f57\u5170\u6216\u524d\u6216\u540e\uff0c\u731d\u65bd\u6740\u624b\uff0c\u4e00\u51fb\u4e4b\u540e\uff0c\u7acb\u523b\u64a4\u8d70\uff0c\u7edd\u4e0d\u604b\u6218\uff0c\u7b97\u662f\u7b2c\u4e00\u52a9\u624b\uff0c\u518d\u4e0b\u6765\uff0c\u5c31\u7531\u6b27\u9633\u662d\u548c\u4e03\u5b9d\u548c\u5c1a\u8054\u624b\uff0c\u5fc5\u6740\u65e0\u8d66\uff0c\u625b\u67ab\u6bbf\u540e\u4e00\u51fb\u2026\u2026\u3002 \u3000\u3000\u4f46\u4e03\u5b9d\u548c\u5c1a\u5e76\u975e\u4e3b\u5f20\u6ee5\u6740\uff0c\u72d9\u6740\u7684\u4e00\u5b9a\u662f\u7b2c\u4e00\u6d41\u9ad8\u624b\uff0c\u5148\u4f5c\u9009\u5b9a\uff0c\u518d\u4f5c\u5e03\u7f6e\uff0c\u800c\u4e14\u7531\u5f20\u56db\u59d1\u7b56\u8ba2\u51fa\u591a\u6837\u7684\u4f20\u8fbe\u65b9\u5f0f\uff0c\u9009\u62e9\u72d9\u6740\u7684\u5bf9\u8c61\uff0c\u5374\u7531\u6c5f\u67ab\u548c\u4e03\u5b9d\u548c\u5c1a\u51b3\u5b9a\u3002 \u3000\u3000\u5f53\u771f\u662f\u6807\u51c6\u7684\u4ee5\u6bd2\u653b\u6bd2\uff0c\u4ee5\u5be1\u72d9\u4f17\u7684\u7b56\u7565\u3002 \u3000\u3000\u5f20\u56db\u59d1\u7cbe\u6e5b\u7684\u6613\u5bb9\u624b\u6cd5\uff0c\u53ef\u4f7f\u4e00\u4e2a\u4eba\u77ac\u606f\u95f4\u8eab\u4efd\u6539\u53d8\uff0c\u5bb9\u8c8c\u6613\u5f62\uff0c\u4e8c\u5973\u5df2\u662f\u5927\u6709\u5fc3\u5f97\uff0c\u5728\u957f\u5b89\u57ce\u4e2d\u8fd9\u7b49\u7e41\u534e\u4eba\u591a\u7684\u5730\u65b9\uff0c\u6700\u4e3a\u9002\u7528\u3002 \u3000\u3000\u4e8c\u5973\u521d\u5f53\u5927\u4efb\uff0c\u4e00\u526f\u8dc3\u8dc3\u6b32\u8bd5\u7684\u5fc3\u60c5\uff0c\u541f\u96ea\u5a07\u5a9a\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u548c\u5c1a\u4f2f\u4f2f\uff0c\u65e9\u4e00\u70b9\u9009\u4e2a\u72d9\u6740\u5bf9\u8c61\u554a\uff01\u4f84\u5973\u6211\u5df2\u7ecf\u6709\u4e9b\u8feb\u4e0d\u53ca\u5f85\u4e86\u3002\u201d \u3000\u3000\u201c\u662f\u53fc\uff01\u6211\u4eec\u7acb\u4e0b\u529f\u52b3\uff0c\u5c31\u7092\u51e0\u6837\u53ef\u53e3\u5c0f\u83b1\u8bf7\u4f60\u559d\u9152\u2026\u2026\u201d\u541f\u971c\u8bf4\uff0c\u201c\u5e0c\u671b\u4f60\u6bcf\u5929\u90fd\u559d\u7684\u98d8\u98d8\u6b32\u4ed9\u3002\u201d \u3000\u3000\u201c\u5c0f\u4e2b\u5934\u2026\u2026\u201d\u6b27\u9633\u662d\u9053\uff1a\u201c\u4f24\u79bd\u6012\u51fb\uff0c\u56f0\u517d\u53cd\u6251\u90a3\u662f\u4ed6\u6bd5\u751f\u529f\u529b\u6240\u805a\u7684\u51cc\u5389\u53cd\u51fb\uff0c\u6108\u662f\u9ad8\u624b\uff0c\u6c14\u606f\u6108\u957f\uff0c\u53cd\u51fb\u4e5f\u6700\u53ef\u6015\uff0c\u4f60\u4eec\u89c9\u7740\u90a3\u662f\u597d\u73a9\u54ea\uff01\u201d \u3000\u3000\u201c\u4ee4\u5c0a\u8bf4\u7684\u4e0d\u9519\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u76ee\u6ce8\u4e8c\u5973\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u672a\u786e\u5b9a\u4e00\u51fb\u80fd\u591f\u6bd9\u654c\u65f6\uff0c\u66f4\u8981\u7279\u522b\u5c0f\u5fc3\uff0c\u5f3a\u654c\u5782\u6b7b\u7684\u53cd\u51fb\uff0c\u4e0d\u53ef\u901e\u5f3a\u786c\u63a5\uff0c\u80fd\u95ea\u907f\uff0c\u6700\u597d\u95ea\u907f\uff0c\u6709\u4e9b\u4eba\u53d7\u4f24\u540e\uff0c\u4f1a\u88c5\u4f5c\u8bc8\u6b7b\uff0c\u5f15\u4eba\u4e0a\u5f53\u3002\u201d \u3000\u3000\u201c\u90a3\u5c31\u6709\u4ed6\u4eec\u7684\u82e6\u5934\u5403\u4e86\u2026\u2026\u201d\u541f\u96ea\u7b11\u9053\uff1a\u201c\u5929\u72d0\u6b66\u529f\u4e2d\u6709\u5f88\u591a\u60e9\u5236\u5978\u3001\u8bc8\u7684\u624b\u6cd5\uff0c\u4e13\u5bf9\u4ed8\u9634\u9669\u90aa\u6076\u7684\u4eba\u2026\u2026\u201d \u3000\u3000\u201c\u600e\u4e48\uff1f\u72d0\u6027\u5341\u7591\uff0c\u5236\u654c\u767e\u53d8\uff0c\u8fd9\u4e00\u7bc7\u5929\u72d0\u6280\u827a\u603b\u7eb2\u4f60\u4eec\u4e5f\u5b66\u8fc7\u4e86\uff1f\u201d\u6c5f\u67ab\u671b\u4e86\u4e8c\u5973\u4e00\u773c\uff0c\u9053\uff1a\u201c\u7cbe\u901a\u6b64\u672f\uff0c\u5929\u4e0b\u65e0\u5904\u4e0d\u53ef\u53bb\u5f97\uff0c\u4f60\u4eec\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u548c\u59d0\u5929\u751f\u4e3d\u8d28\uff0c\u53c8\u5f97\u4e86\u7239\u548c\u5a18\u7684\u806a\u6167\u9057\u4f20\uff0c\u662f\u5929\u751f\u7684\u72d0\u95e8\u5f1f\u5b50\u2026\u2026\u201d\u541f\u971c\u5a07\u58f0\u7b11\u9053\uff1a\u201c\u5929\u72d0\u95e8\u5341\u7591\uff0c\u767e\u53d8\uff0c\u6280\u827a\u6d69\u701a\uff0c\u58ee\u9614\u5343\u91cc\uff0c\u53ef\u4e5f\u5f97\u627f\u7ee7\u6709\u4eba\u554a\uff01\u59d0\u548c\u6211\u5f53\u4ec1\u4e0d\u8ba9\uff0c\u6311\u8d77\u4e86\u53d1\u626c\u5149\u5927\u7684\u91cd\u4efb\uff0c\u6c5f\u54e5\u54e5\uff0c\u4f60\u53ef\u77e5\u9053\u5a18\u4f20\u4e86\u6211\u4eec\u8fd9\u4e00\u7bc7\u5929\u72d0\u6280\u827a\u603b\u7eb2\u4e4b\u540e\uff0c\u6211\u4eec\u6709\u4e9b\u4ec0\u4e48\u611f\u89c9\uff1f\u201d \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u5ffd\u7136\u53d1\u89c9\u4e86\u541f\u971c\u8bf4\u8bdd\u795e\u60c5\u8bed\u6c14\uff0c\u914d\u5408\u7740\u4e00\u5f20\u6a31\u6843\u5c0f\u53e3\u7684\u5634\u5f62\u53d8\u5316\uff0c\u7279\u522b\u7a81\u51fa\u4e86\u90a3\u7ea2\u5ae9\u7684\u53cc\u5507\u4e4b\u7f8e\uff0c\u90a3\u4e00\u58f0\u6c5f\u54e5\u54e5\uff0c\u66f4\u662f\u6492\u51fa\u4e86\u4e00\u628a\u5a07\u5a9a\uff0c\u9189\u4eba\u82e5\u9152\uff0c\u8fde\u5b9a\u529b\u9ad8\u5f3a\u7684\u5927\u548c\u5c1a\uff0c\u4e5f\u751f\u8d77\u4e86\u4eb2\u4eb2\u90a3\u5f20\u5634\uff0c\u6b7b\u800c\u4f55\u61be\u7684\u611f\u89c9\u2026\u2026\u3002 \u3000\u3000\u7f57\u5170\u5b9a\u5b9a\u795e\uff0c\u559d\u9053\uff1a\u201c\u4e8c\u4e2b\u5934\uff0c\u4f60\u5728\u5e72\u4ec0\u4e48\uff1f\u5927\u548c\u5c1a\u662f\u957f\u8f88\uff0c\u600e\u53ef\u76ee\u65e0\u5c0a\u957f\uff1f\u201d \u3000\u3000\u201c\u6211\u53ea\u662f\u501f\u548c\u5c1a\u4f2f\u4f2f\u6df1\u539a\u7684\u7985\u529f\u3001\u5b9a\u529b\uff0c\u8bd5\u8bd5\u770b\uff0c\u6211\u7684\u5929\u72d0\u529f\u529b\uff0c\u5230\u4e86\u4ec0\u4e48\u5883\u754c\u3002\u201d\u541f\u971c\u9053\uff1a\u201c\u5bf9\u4e0d\u4f4f\u4e86\uff0c\u548c\u5c1a\u4f2f\u4f2f\u3002\u8fc7\u4e00\u5929\u6211\u8bf7\u4f60\u559d\u9152\uff0c\u7ed9\u4f60\u8d54\u7f6a\u2026\u2026\u3002\u201d \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\u4f60\u4eec\u6c5f\u5927\u54e5\u7684\u611f\u89c9\u5982\u4f55\uff1f\u4f46\u6211\u548c\u5c1a\u5374\u771f\u6709\u70b9\u5e94\u4ed8\u4e0d\u6765\uff0c\u867d\u672a\u5fc3\u795e\u8361\u6f3e\uff0c\u5374\u5df2\u7eee\u5ff5\u6a2a\u751f\uff0c\u4e2b\u5934\u4eec\uff0c\u4e0b\u4e0d\u4e3a\u4f8b\uff0c\u5927\u548c\u5c1a\u8fd9\u70b9\u9053\u884c\uff0c\u7f69\u4e0d\u4f4f\u554a\uff01\u201d \u3000\u3000\u6c5f\u67ab\u7b11\u9053\uff1a\u201c\u60d1\u5fc3\u672f\u5a9a\u6ce2\u56db\u6ea2\uff0c\u800c\u4e14\uff0c\u80fd\u96c6\u4e8e\u4e00\u70b9\u5c55\u73b0\uff0c\u8ba9\u4e03\u5b9d\u5927\u5e08\u4f4e\u5934\uff0c\u5e94\u8be5\u5df2\u767b\u4e0a\u4e86\u516b\u6210\u5883\u754c\uff0c\u771f\u662f\u96be\u4e3a\u4f60\u4eec\u4e86\u3002\u201d \u3000\u3000\u201c\u5509\uff01\u6c5f\u8001\u5f1f\uff0c\u8fd9\u600e\u5f97\u4e86\u554a\uff01\u513f\u5927\u4e0d\u7531\u7237\uff0c\u2026\u2026\u201d\u6b27\u9633\u662d\u65e0\u9650\u611f\u6168\u5730\u8bf4\uff0c\u201c\u8fd9\u4e24\u4e2a\u5973\u513f\uff0c\u6211\u53ef\u4ee5\u4e0d\u8981\uff0c\u53ef\u662f\u4f60\u4ee5\u540e\u8981\u600e\u4e48\u5fcd\u53d7\uff1f\u4e07\u7d2b\u5343\u7ea2\u6625\u65e0\u9650\uff0c\u9999\u98ce\u5343\u6668\u5f15\u8776\u98de\uff0c\u7ec3\u6b66\u529f\u7ec3\u5230\u4e86\u8fd9\u79cd\u5883\u754c\uff0c\u771f\u8981\u4e00\u9189\u89e3\u5e72\u6101\u4e86\u3002\u201d \u3000\u3000\u53e3\u6c14\u4e4b\u95f4\u5927\u4e3a\u540c\u60c5\u8fd9\u4f4d\u672a\u6765\u7684\u5973\u5a7f\u3002 \u3000\u3000\u201c\u6c5f\u67ab\u5df2\u662f\u5929\u72d0\u95e8\u638c\u95e8\uff0c\u2026\u2026\u201d\u7f57\u5170\u7b11\u9053\uff1a\u201c\u53ef\u6ca1\u6709\u4f60\u8fd9\u6837\u7684\u5c0f\u5fc3\u773c\u3002\u201d \u3000\u3000\u201c\u56db\u5b63\u82b1\u5a62\uff0c\u6b4c\u575b\u9752\u51e4\uff0c\u4e2a\u4e2a\u90fd\u662f\u4eba\u95f4\u5c24\u7269\u2026\u2026\u201d\u5f20\u56db\u59d1\u7b11\u9053\uff1a\u201c\u6c5f\u67ab\u8fd8\u4e0d\u662f\u5e94\u4ed8\u7684\u5934\u5934\u662f\u9053\uff0c\u6b27\u9633\u5927\u54e5\uff0c\u4f60\u4e0d\u7528\u770b\u4e09\u56fd\u6389\u773c\u6cea\uff0c\u66ff\u53e4\u4eba\u62c5\u5fe7\u4e86\uff01\u201d \u3000\u3000\u201c\u7239\uff01\u771f\u7684\u4e0d\u7528\u62c5\u5fc3\u2026\u2026\u201d\u541f\u96ea\u9053\uff1a\u201c\u6b66\u6797\u5927\u5c40\uff0c\u6062\u590d\u4e86\u5b81\u9759\u4e4b\u540e\uff0c\u7239\u8ba8\u538c\u6211\u4eec\u8fd9\u79cd\u6837\u5b50\uff0c\u6211\u4eec\u4f1a\u628a\u6b66\u529f\u6563\u53bb\uff0c\u90a3\u5c31\u6210\u4e86\u6807\u51c6\u7684\u6df1\u95fa\u5f31\u5973\uff0c\u5904\u5904\u4f9d\u9760\u4e08\u592b\u7684\u5c0f\u5ab3\u5987\u4e86\u3002\u201d \u3000\u3000\u201c\u5927\u4e2b\u5934\uff0c\u8fd9\u79cd\u8bdd\uff0c\u4e0d\u53ef\u968f\u4fbf\u51fa\u53e3\uff0c\u2026\u2026\u201d\u7f57\u5170\u9053\uff1a\u201c\u4e60\u7ec3\u8fc7\u5929\u72d0\u6b66\u529f\u7684\u4eba\uff0c\u4e00\u65e6\u6563\u53bb\u529f\u529b\uff0c\u4f60\u53ef\u77e5\u9053\u4f1a\u662f\u600e\u6837\u4e00\u4e2a\u7ed3\u679c\u5417\uff1f\u201d \u3000\u3000\u201c\u77e5\u9053\uff0c\u5982\u9c7c\u5931\u6c34\uff0c\u5982\u82b1\u51cb\u96f6\uff0c\u4f1a\u53d8\u7684\u67af\u840e\u5931\u8272\uff0c\u2026\u2026\u201d\u541f\u96ea\u9eef\u7136\u7684\u8bf4\u3002 \u3000\u3000\u201c\u8fd9\u53c8\u592a\u4e25\u91cd\u4e86\uff0c\u2026\u2026\u201d\u6b27\u9633\u662d\u7231\u5973\u4e4b\u5fc3\uff0c\u987f\u7136\u9ad8\u6da8\uff0c\u63a5\u9053\uff1a\u201c\u8363\u3001\u67af\u4e4b\u95f4\uff0c\u7ade\u6709\u5929\u58e4\u4e4b\u522b\uff0c\u96be\u9053\u5c31\u6ca1\u6709\u4e2d\u5eb8\u4e4b\u9053\u4e48\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\u2026\u2026\u201d\u6c5f\u67ab\u7b11\uff1a\u201c\u6240\u4ee5\uff0c\u5979\u4eec\u4e0d\u80fd\u6563\u53bb\u529f\u529b\uff0c\u53ea\u6709\u66f4\u4e0a\u5c42\u697c\uff0c\u8fdb\u5165\u4e0a\u4e58\u5883\u754c\u3002\u201d \u3000\u3000\u201c\u5973\u513f\u7684\u4e8b\uff0c\u7531\u5979\u4eec\u81ea\u62ff\u4e3b\u610f\u5427\uff01\u2026\u2026\u201d\u7f57\u5170\u9053\uff1a\u201c\u8001\u5b9e\u8bf4\uff0c\u5979\u4eec\u7684\u6b66\u529f\u6210\u5c31\uff0c\u65e9\u5df2\u4e0d\u5728\u4f60\u6211\u4e4b\u4e0b\uff0c\u8bf4\u5230\u5bf9\u654c\u7684\u624b\u6bb5\u3001\u5fc3\u673a\uff0c\u6211\u4eec\u5df2\u6709\u4e0d\u5982\uff0c\u4f60\u6211\u5df2\u5c3d\u4e86\u6559\u517b\u7684\u8d23\u4efb\uff0c\u7528\u4e0d\u7740\u518d\u4e3a\u5979\u4eec\u64cd\u5fc3\u4e86\u3002\u201d \u3000\u3000\u201c\u5bf9\u5bf9\u5bf9\uff0c\u5927\u548c\u5c1a\uff0c\u6709\u6ca1\u6709\u5174\u81f4\u559d\u4e00\u676f\u2026\u2026\u201d \u3000\u3000\u6b27\u9633\u662d\u7a81\u7136\u611f\u89c9\u5230\u4ed6\u7684\u770b\u6cd5\u3001\u8bba\u70b9\uff0c\u548c\u8fd9\u4e9b\u5e74\u8f7b\u4eba\u7684\u601d\u60f3\u3001\u4f5c\u6cd5\uff0c\u6709\u7740\u4e00\u6bb5\u76f8\u5f53\u7684\u8ddd\u79bb\u3002 \u3000\u3000\u201c\u56fa\u6240\u613f\u4e5f\uff0c\u4e0d\u6562\u8bf7\u5c14\uff0c\u8d70\uff01\u559d\u9152\u53bb\u3002\u201d\u5927\u548c\u5c1a\u7ad9\u8d77\u4e86\u8eab\u5b50\u3002 \u3000\u3000\u201c\u6211\u53bb\u5e2e\u4f60\u4eec\u4f5c\u4e24\u4e2a\u4e0b\u9152\u7684\u5c0f\u83dc\u2026\u2026\u201d\u5f20\u56db\u59d1\u4e5f\u7ad9\u8d77\u6765\uff0c\u8ddf\u7740\u4e24\u4eba\u79bb\u53bb\u3002 \u3000\u3000\u671b\u7740\u4e09\u4eba\u79bb\u53bb\u7684\u80cc\u5f71\uff0c\u6c5f\u67ab\u53f9\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u867d\u7136\u662f\u60c5\u52bf\u903c\u4eba\uff0c\u4f46\u6211\u4eec\u7684\u884c\u4e8b\u65b9\u6cd5\uff0c\u786e\u4e5f\u6709\u4e00\u70b9\u53db\u7ecf\u79bb\u9053\uff1a\u52ff\u602a\u6b27\u9633\u524d\u8f88\u6709\u4e9b\u770b\u4e0d\u60ef\u4e86\u3002\u201d \u3000\u3000\u201c\u6ca1\u6cd5\u5b50\u554a\uff01\u5317\u5b66\u7684\u6b63\u5b97\u6b66\u529f\uff0c\u8bb2\u7a76\u7684\u662f\u5802\u5802\u6b63\u6b63\u7684\u505a\u4eba\uff0c\u4e00\u677f\u4e00\u773c\u7684\u505a\u4e8b\u2026\u2026\u201d\u7f57\u5170\u7b11\u9053\uff1a\u201c\u73b0\u5728\u5df2\u7ecf\u597d\u591a\u4e86\uff0c\u8fc7\u53bb\u554a\uff01\u90a3\u79cd\u4e00\u4e1d\u4e0d\u82df\u7684\u6027\u683c\uff0c\u50cf\u4e00\u53ea\u88c5\u6ee1\u4e86\u76d0\u6c34\u7684\u5927\u9171\u7f38\uff0c\u4e0d\u7ba1\u4f60\u662f\u5c71\u73cd\u6d77\u5473\uff0c\u90fd\u628a\u4f60\u6df9\u6210\u9171\u83dc\u3002\u201d \u3000\u3000\u6c5f\u67ab\u7b11\u4e00\u7b11\uff0c\u76ee\u5149\u4e00\u63a0\u96ea\u3001\u971c\u4e8c\u5973\uff0c\u9053\uff1a\u201c\u8bf4\u8bf4\u770b\uff0c\u4f60\u4eec\u5bf9\u5929\u72d0\u6b66\u529f\u603b\u7eb2\uff0c\u5df2\u6709\u4e86\u591a\u5c11\u6210\u5c31\uff1f\u201d \u3000\u3000\u201c\u5a18\u4f20\u6211\u4eec\u5929\u72d0\u6b66\u529f\uff0c\u53ea\u8bf4\u4e0d\u7ec3\uff0c\u6211\u4eec\u867d\u7136\u5df2\u628a\u53e3\u51b3\u5fc3\u6cd5\uff0c\u6df1\u5370\u5fc3\u4e2d\uff0c\u4f46\u603b\u89c9\u7f3a\u5c11\u4e86\u4e00\u4e9b\u4ec0\u4e48\uff1f\u2026\u2026\u201d\u541f\u96ea\u8bf4\uff0c\u201c\u6709\u4e9b\u6280\u827a\uff0c\u5fc5\u9700\u5668\u5177\u8f85\u52a9\uff0c\u624d\u80fd\u53d1\u6325\u3002\u6211\u548c\u971c\u59b9\u7814\u7a76\u6478\u7d22\uff0c\u867d\u7136\u627e\u51fa\u4e86\u4e00\u4e9b\u4ee3\u7528\u5668\u5177\uff0c\u4f46\u5374\u6709\u7740\u4e0d\u591f\u7280\u5229\u7684\u611f\u89c9\u3002\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u6b32\u5584\u5176\u4e2d\uff0c\u5229\u5176\u5668\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u73b0\u5728\u5462\uff1f\u201d \u3000\u3000\u201c\u81ea\u548c\u5f20\u59e8\u89c1\u9762\u4e4b\u540e\uff0c\u60c5\u51b5\u5927\u4e3a\u6539\u5584\u2026\u2026\u201d\u541f\u971c\u770b\u4e86\u6bcd\u4eb2\u4e00\u773c\uff0c\u63a5\u9053\uff1a\u201c\u5f20\u59e8\u7684\u6c5f\u6e56\u9605\u5386\u4e30\u5bcc\uff0c\u53c8\u7cbe\u901a\u5404\u79cd\u5c0f\u5de7\u5f02\u672f\uff0c\u548c\u5929\u72d0\u767e\u53d8\u6280\u827a\uff0c\u82e5\u6709\u4e00\u8109\u76f8\u901a\u4e4b\u52bf\u3002\u7ecf\u5979\u6307\u70b9\uff0c\u6211\u4eec\u5df2\u6253\u9020\u4e86\u5341\u51e0\u5904\u7cbe\u5de7\u3001\u7280\u5229\u7684\u8f85\u52a9\u5668\u5177\uff0c\u5df2\u5728\u82e6\u7ec3\uff0c\u76f8\u4fe1\u53ef\u4ee5\u6d3e\u4e0a\u7528\u573a\u4e86\u3002\u201d \u3000\u3000\u6c5f\u67ab\u4e5f\u4e0d\u8ffd\u95ee\u4e8c\u5973\u8be6\u60c5\uff0c\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u7cbe\u901a\u767e\u53d8\u6280\u827a\uff0c\u53ef\u8282\u7701\u4e86\u4e0d\u5c11\u6c14\u529b\uff0c\u8bf4\u5230\u56db\u59d1\u7684\u6b66\u529f\uff0c\u5374\u548c\u5929\u72d0\u529f\u827a\u76f8\u8fd1\uff0c\u5979\u7684\u4e94\u884c\u9041\u672f\uff0c\u79fb\u82b1\u63a5\u6728\uff0c\u66f4\u662f\u72ec\u6b65\u6c5f\u6e56\u7684\u5947\u6280\uff0c\u4f60\u4eec\u5b66\u8fc7\u6ca1\u6709\uff1f\u201d \u3000\u3000\u201c\u5f20\u59e8\u4e00\u70b9\u4e5f\u4e0d\u85cf\u79c1\uff0c\u8ba4\u771f\u7684\u503e\u56ca\u76f8\u6388\u3001\u53ea\u662f\u6211\u548c\u971c\u59b9\u592a\u7b28\u4e86\uff0c\u7ec3\u5230\u73b0\u5728\u3002\u4e94\u884c\u9041\u672f\uff0c\u8fd8\u6ca1\u6709\u767b\u4eba\u5ba4\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u9519\u4e86\u2026\u2026\u201d\u5f20\u56db\u59d1\u7f13\u6b65\u800c\u5165\uff0c\u6253\u65ad\u4e86\u541f\u96ea\u7684\u8bdd\uff0c\u63a5\u9053\uff1a\u201c\u5f53\u5e74\u5f20\u59e8\u7ec3\u4e86\u4e00\u5e74\uff0c\u8fd8\u6ca1\u6709\u4f60\u4eec\u8fd9\u4e00\u4e2a\u6708\u7684\u6210\u5c31\u5927\uff0c\u53ea\u4e0d\u8fc7\u5f20\u59e8\u8fd9\u4e9b\u6b66\u529f\uff0c\u9700\u7528\u7684\u5668\u5177\u592a\u591a\uff0c\u6709\u4e9b\u70e6\u4eba\u3002\u201d \u3000\u3000\u201c\u5927\u548c\u5c1a\u63d0\u51fa\u7684\u72d9\u6740\u8ba1\u5212\uff0c\u53ea\u662f\u7eb2\u9886\u2026\u2026\u201d\u6c5f\u67ab\u770b\u7740\u5f20\u56db\u59d1\uff0c\u9053\uff1a\u201c\u7ec6\u8282\u65b9\u9762\uff0c\u8fd8\u8981\u5927\u59d0\u7cbe\u5fc3\u7b56\u5212\u4e00\u4e0b\u3002\u201d \u3000\u3000\u201c\u6211\u4f1a\u7528\u5fc3\u601d\uff0c\u4e0d\u8fc7\uff0c\u8981\u72d9\u6740\u4ec0\u4e48\u4eba\uff1f\u6211\u5012\u5e0c\u671b\u4f60\u591a\u60f3\u60f3\u518d\u4f5c\u51b3\u5b9a\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a \u3000\u3000\u201c\u6211\u4eec\u4e3b\u8981\u7684\u76ee\u6807\uff0c\u662f\u8981\u4ed6\u4eec\u5f7c\u6b64\u731c\u5fcc\uff0c\u4e92\u76f8\u6b8b\u6740\uff0c\u5e94\u8be5\u6ce8\u91cd\u5728\u6311\u62e8\u3001\u79bb\u95f4\u4e4b\u4e0a\u3002\u5982\u679c\u53d1\u96f7\u9706\u653b\u51fb\uff0c\u8fde\u756a\u72d9\u6740\uff0c\u4f1a\u4e0d\u4f1a\u4f7f\u4ed6\u4eec\u5fc3\u751f\u8b66\u89c9\uff0c\u6d88\u9664\u9694\u9602\uff0c\u518d\u4f5c\u7d27\u5bc6\u7684\u5408\u4f5c\uff0c\u5168\u529b\u5bf9\u5916\u3002\u201d \u3000\u3000\u201c\u5927\u59d0\u4e00\u8bed\u60ca\u5fc3\uff0c\u5207\u4e2d\u5176\u5f0a\uff0c\u5c0f\u5f1f\u4f1a\u8c28\u614e\u4ece\u4e8b\uff0c\u2026\u2026\u201d\u8bed\u58f0\u4e00\u987f\uff0c\u7a81\u7136\u65bd\u4f4e\u4e86\u58f0\u97f3\uff0c\u9053\uff1a\u201c\u6211\u60f3\u6df7\u5165\u5929\u9a6c\u603b\u5802\u53bb\uff0c\u5b9e\u5730\u89c2\u5bdf\u4e00\u4e0b\u5929\u9a6c\u5802\u7684\u5b9e\u529b\uff0c\u5927\u59d0\u7684\u770b\u6cd5\u5982\u4f55\uff1f\u201d \u3000\u3000\u201c\u6211\u770b\u6682\u65e0\u5fc5\u8981\u2026\u2026\u201d\u5f20\u56db\u59d1\u8bf4\uff0c\u201c\u5929\u9a6c\u603b\u5802\u4e2d\u867d\u7136\u6c47\u805a\u4e86\u6c5f\u6e56\u4e0a\u7684\u9b54\u5934\u51f6\u90aa\uff0c\u4f46\u4f60\u5df2\u77e5\u6653\u4e86\u5927\u6982\u60c5\u5f62\uff0c\u771f\u6b63\u795e\u79d8\u7684\u662f\u5929\u738b\u95e8\u548c\u5185\u5bab\u4e2d\u4eba\u2026\u2026\u201d \u3000\u3000\u201c\u56db\u59d1\u8bf4\u7684\u5bf9\u2026\u2026\u201d\u7f57\u5170\u9053\uff1a\u201c\u4f60\u4e0d\u7528\u5192\u9669\u6df7\u5230\u5929\u9a6c\u603b\u5802\u4e2d\u53bb\uff0c\u771f\u6b63\u7684\u62fc\u6740\u6240\u5728\uff0c\u8fd8\u5728\u957f\u5b89\uff0c\u5929\u738b\u95e8\u548c\u5185\u5bab\u4e00\u7cfb\u4e2d\u4eba\u7269\u6240\u4ee5\u795e\u79d8\uff0c\u90a3\u662f\u56e0\u4e3a\u4ed6\u4eec\u53ea\u6709\u95e8\u4e0b\u5f1f\u5b50\u5728\u51fa\u9762\u6d3b\u52a8\uff0c\u5982\u679c\u80fd\u903c\u4ed6\u4eec\u8001\u4e00\u8f88\u7684\u4eba\u73b0\u8eab\u51fa\u6765\uff0c\u6211\u76f8\u4fe1\u4ee5\u56db\u59d1\u7684\u7ecf\u9a8c\u4e4b\u4e30\uff0c\u5fc5\u53ef\u770b\u51fa\u4ed6\u4eec\u7684\u771f\u6b63\u6765\u5386\uff0c\u6811\u6709\u6839\u3001\u6c34\u6709\u6e90\uff0c\u6211\u4e0d\u4fe1\u4ed6\u4eec\u662f\u51ed\u7a7a\u8df3\u51fa\u6765\u7684\uff0c\u4f55\u51b5\uff0c\u8fd8\u6709\u4e03\u5b9d\u5927\u5e08\u548c\u8001\u53eb\u5316\u4e24\u4e2a\u89c1\u591a\u8bc6\u5e7f\u7684\u6c5f\u6e56\u901a\uff0c\u4e94\u5341\u5e74\u6765\u7684\u6c5f\u6e56\u4eba\u7269\uff0c\u5927\u6982\u90fd\u9003\u4e0d\u8fc7\u4ed6\u4eec\u4e24\u4f4d\u7684\u6cd5\u773c\u3002\u201d \u3000\u3000\u201c\u6709\u9053\u7406\uff0c\u5c31\u8fd9\u4e48\u51b3\u5b9a\u4e86\uff0c\u72d9\u6740\u8ba1\u5212\u4e0d\u53d8\uff0c\u6211\u4f1a\u5c0f\u5fc3\u51b3\u5b9a\uff0c\u60f3\u529e\u6cd5\u628a\u94b1\u7f3a\u53ec\u56de\u6765\uff0c\u987a\u4fbf\u8981\u4ed6\u5e26\u4e00\u6279\u7cbe\u660e\u7684\u4eba\u624b\u540c\u6765\uff0c\u8bf7\u5927\u59d0\u66ff\u4ed6\u4eec\u6539\u626e\u4e00\u4e0b\uff0c\u6211\u60f3\u5728\u957f\u5b89\u56db\u5468\u5e7f\u5e03\u8033\u76ee\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u529e\u6cd5\u4e0d\u9519\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u4f60\u73b0\u5728\u9664\u4e86\u4e86\u89e3\u6d1b\u9633\u5c45\u4e2d\u53d8\u5316\u4e4b\u5916\uff0c\u5bf9\u6d1b\u9633\u5c45\u5916\u7684\u654c\u52bf\u6d88\u957f\uff0c\u5168\u4e0d\u77e5\u6653\uff0c\u672c\u6765\uff0c\u6211\u51c6\u5907\u5e26\u7740\u4e24\u4e2a\u5c0f\u7f8e\u4eba\uff0c\u5e2e\u4f60\u63a2\u67e5\u6d1b\u9633\u5c45\u5916\u7684\uff0c\u73b0\u5728\u4e8c\u5973\u4ee5\u6267\u884c\u72d9\u6740\u4e3a\u4e3b\uff0c\u5012\u662f\u4e0d\u5b9c\u518d\u5206\u8eab\u65c1\u987e\u4e86\u3002 \u3000\u3000\u94b1\u7f3a\u5e26\u7684\u8fd9\u6279\u4eba\u6b66\u529f\u4e0d\u8db3\u4ee5\u62c5\u6321\u5927\u4efb\uff0c\u4f46\u5927\u90fd\u662f\u5e38\u5e74\u5728\u6c5f\u6e56\u4e0a\u8d70\u52a8\u7684\u4eba\u7269\uff0c\u4ed6\u4eec\u7684\u89c2\u5bdf\u80fd\u529b\uff0c\u5c31\u975e\u6211\u548c\u4e8c\u5973\u80fd\u53ca\u4e86\u3002\u201d \u3000\u3000\u201c\u5927\u59d0\u4e0d\u8981\u8c26\u865a\u4e86\uff0c\u4e24\u4e2a\u5c0f\u7f8e\u4eba\u6b20\u7f3a\u6c5f\u6e56\u9605\u5386\uff0c\u8bf4\u5979\u4eec\u80fd\u8005\u65e0\u6240\u4e0d\u80fd\uff0c\u672a\u514d\u8a00\u8fc7\u5176\u5b9e\u2026\u2026\u201d\u6c5f\u67ab\u7b11\u9053\uff1a\u201c\u81f3\u4e8e\u5927\u59d0\u4f60\uff0c\u53ef\u8981\u80fd\u8005\u591a\u52b3\u4e86\uff0c\u8001\u53eb\u5316\u5e26\u6765\u7684\u4eba\u624b\uff0c\u4f60\u8981\u5e2e\u4ed6\u4eec\u7f16\u7ec4\u4e00\u4e0b\uff0c\u7136\u540e\uff0c\u5206\u6d3e\u4ed6\u4eec\u76d1\u89c6\u7684\u533a\u57df\uff0c\u8ba2\u4e00\u4e9b\u6ce8\u610f\u7684\u89c4\u6212\uff0c\u81f3\u4e8e\u98ce\u96ea\u5c0f\u9986\u8fd9\u4e2a\u5730\u65b9\uff0c\u9664\u4e86\u8001\u53eb\u5316\u5916\uff0c\u6682\u65f6\u522b\u8ba9\u4ed6\u4eec\u77e5\u9053\uff1a\u4f60\u4e5f\u4e0d\u7528\u548c\u4ed6\u4eec\u76f4\u63a5\u89c1\u9762\uff0c\u4e00\u5207\u90fd\u900f\u8fc7\u8001\u53eb\u5316\u4f20\u8fbe\uff0c\u6700\u597d\u628a\u4ed6\u4eec\u4e4b\u95f4\u7684\u6a2a\u5411\u8fde\u7cfb\uff0c\u4e5f\u4e88\u5207\u65ad\uff0c\u7531\u8001\u53eb\u5316\u9009\u51e0\u4e2a\u6b66\u529f\u8f83\u9ad8\uff0c\u5b58\u6709\u5fc5\u6b7b\u51b3\u5fc3\u7684\u4eba\uff0c\u8d1f\u8d23\u4e32\u8fde\uff0c\u6536\u96c6\u5404\u5904\u7684\u89c1\u95fb\u8d44\u6599\uff0c\u7279\u522b\u91cd\u8981\u7684\uff0c\u5927\u59d0\u548c\u6211\u8fde\u7cfb\u4e00\u4e0b\uff0c\u4e00\u822c\u6027\u7684\u4e8b\u52a1\uff0c\u5927\u59d0\u5c31\u7528\u6211\u7684\u540d\u4e49\uff0c\u4ee3\u6211\u5904\u7406\u4e86\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u6211\u4f1a\u8c28\u614e\u4ece\u4e8b\uff0c\u4f60\u7684\u987e\u8651\u5f88\u5bf9\uff0c\u4e0d\u8bba\u5982\u4f55\u5c0f\u5fc3\u5e03\u7f72\uff0c\u4e5f\u96be\u514d\u767e\u5bc6\u4e00\u758f\u2026\u2026\u201d\u5f20\u56db\u59d1\u4f83\u4f83\u800c\u8c08\uff0c\u201c\u6211\u76f8\u4fe1\u4ed6\u4eec\u903c\u4f9b\u7684\u624b\u6bb5\u5fc5\u662f\u51f6\u6b8b\u7edd\u4f26\uff0c\u80fd\u591f\u5fcd\u53d7\u7684\u4eba\uff0c\u7edd\u65e0\u4ec5\u6709\uff0c\u5fc3\u5b58\u5fc5\u6b7b\u7684\u4eba\uff0c\u4e5f\u8981\u65e9\u6709\u51c6\u5907\u624d\u884c\uff0c\u4e00\u65e6\u5931\u624b\uff0c\u7acb\u523b\u81ea\u7edd\u2026\u2026\u201d \u3000\u3000\u201c\u56db\u59d1\uff0c\u771f\u7684\u90a3\u4e48\u53ef\u6015\u4e48\uff1f\u2026\u2026\u201d\u7f57\u5170\u51dd\u6ce8\u7740\u8fd9\u4f4d\u95fa\u4e2d\u597d\u53cb\u95ee\u9053\uff1a\u201c\u6211\u4eec\u662f\u4e0d\u662f\u4e5f\u8be5\u6709\u4e9b\u51c6\u5907\uff1f\u201d \u3000\u3000\u201c\u5e94\u8be5\u6709\uff0c\u2026\u2026\u201d\u5f20\u56db\u59d1\u795e\u60c5\u8083\u7136\u5730\u8bf4\uff0c\u201c\u7f57\u5170\uff0c\u90a3\u79cd\u532a\u5937\u6240\u601d\u7684\u65bd\u5211\u624b\u6bb5\uff0c\u7edd\u5bf9\u8d85\u8fc7\u4e86\u4e00\u4e2a\u4eba\u6240\u80fd\u5fcd\u53d7\u7684\u6781\u9650\uff0c\u81f3\u5c11\uff0c\u6211\u5c31\u4e0d\u76f8\u4fe1\u81ea\u5df1\u80fd\u71ac\u5f97\u8fc7\u53bb\uff0c\u4f55\u51b5\u3002\u6211\u4eec\u5973\u4eba\u8fd8\u6709\u66f4\u53ef\u6015\u7684\u906d\u9047\uff0c\u90a3\u5c31\u662f\u51cc\u8fb1\uff0c\u4ed6\u4eec\u52a0\u8bf8\u7684\u66b4\u529b\u884c\u4e3a\uff0c\u4f1a\u4f7f\u4eba\u6709\u751f\u4e0d\u5982\u6b7b\u7684\u611f\u89c9\uff0c\u4e0d\u8fc7\uff0c\u6211\u76f8\u4fe1\u4ee5\u4f60\u6b66\u529f\u4e0a\u7684\u6210\u5c31\uff0c\u5c31\u7b97\u9047\u4e0a\u4ed6\u4eec\u6700\u5f3a\u608d\u7684\u4eba\u7269\uff0c\u4e5f\u6709\u81ea\u4fdd\u7684\u673a\u4f1a\uff0c\u81f3\u5c11\u53ef\u4ee5\u9003\u8d70\uff0c\u81ea\u7edd\u662f\u6700\u540e\u7684\u624b\u6bb5\uff0c\u786e\u5b9a\u4e86\u4ed6\u4eec\u4f1a\u5bf9\u4f60\u65bd\u5c55\u6b8b\u9177\u7684\u5211\u6c42\uff0c\u51cc\u8fb1\u65f6\uff0c\u90a3\u5c31\u4ee5\u6b7b\u53bb\u9003\u907f\u5427\uff01\u4e0d\u8bba\u591a\u7f8e\u7684\u5973\u4eba\uff0c\u6b7b\u4e86\u4e4b\u540e\uff0c\u5c31\u4f1a\u5931\u53bb\u8bf1\u4eba\u7684\u9b45\u529b\u3002\u201d \u3000\u3000\u201c\u5927\u59d0\uff0c\u522b\u8bf4\u7684\u90a3\u4e48\u53ef\u6015\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u4f60\u628a\u7f57\u5170\u548c\u541f\u96ea\u3001\u541f\u7528\u5413\u574f\u4e86\uff01\u201d \u3000\u3000\u201c\u6c5f\u5927\u54e5\uff0c\u4e0d\u8981\u62c5\u5fc3\u6211\u548c\u971c\u59b9\u2026\u2026\u201d\u541f\u96ea\u9053\uff1a\u201c\u5929\u72d0\u5341\u7591\uff0c\u5df2\u628a\u4eba\u6027\u4e2d\u6781\u6076\u3001\u72e1\u8bc8\uff0c\u4f30\u7b97\u5728\u5185\uff0c\u5236\u654c\u767e\u53d8\uff0c\u66f4\u662f\u6709\u7740\u5e94\u4ed8\u5404\u79cd\u610f\u5916\u7684\u51c6\u5907\uff0c\u771f\u8981\u6709\u4e2a\u4eba\uff0c\u80fd\u591f\u628a\u6211\u4eec\u5236\u4f4f\u4e86\uff0c\u4f7f\u6211\u4eec\u65e0\u6cd5\u53cd\u51fb\uff0c\u90a3\u4e2a\u4eba\u662f\u4e86\u4e0d\u8d77\u7684\u4eba\u624d\uff0c\u6211\u4eec\u53ea\u6709\u8ba4\u547d\u4e86\u3002\u201d \u3000\u3000\u8fd9\u756a\u8bdd\u542c\u8d77\u6765\uff0c\u6709\u7740\u76f8\u5f53\u7684\u81ea\u8d1f\u548c\u60b2\u58ee\u6c14\u52bf\uff0c\u4f46\u4e5f\u8868\u793a\u51fa\u4e86\u5f3a\u70c8\u7684\u81ea\u4fe1\u3002 \u3000\u3000\u201c\u90fd\u4e0d\u662f\uff0c\u6211\u53ea\u662f\u60f3\u8bf4\u51fa\u6765\uff0c\u6211\u81ea\u5df1\u7684\u770b\u6cd5\uff0c\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u5979\u4eec\u65e9\u5df2\u77e5\u9053\u4e86\u4f60\u6211\u4e4b\u95f4\u7684\u4e8b\uff0c\u4f46\u5979\u4eec\u4e0d\u4f46\u6ca1\u6709\u5992\u6068\uff0c\u53cd\u800c\u5904\u5904\u60f3\u7ed9\u6211\u4eec\u5236\u9020\u673a\u4f1a\uff0c\u5927\u59d0\u8fc7\u53bb\u5bf9\u4f60\u7684\u98ce\u6d41\u97f5\u4e8b\uff0c\u867d\u7136\u4e0d\u95fb\u4e0d\u95ee\uff0c\u4f46\u6709\u65f6\u95f4\u60f3\u8d77\u6765\uff0c\u4e5f\u4f1a\u6709\u60c6\u7136\u82e5\u5931\u7684\u96be\u8fc7\u3002 \u3000\u3000\u53ef\u662f\u4e24\u4e2a\u4e2b\u5934\uff0c\u5374\u4e0d\u4f1a\uff0c\u5979\u4eec\u53ea\u60f3\u53d6\u5f97\u5979\u4eec\u7684\u4e00\u4efd\uff0c\u5fc3\u4e2d\u5c31\u5f88\u5feb\u4e50\u4e86\uff0c\u6211\u73b0\u5728\uff0c\u771f\u7684\u662f\u5fc3\u80f8\u5927\u5f00\uff0c\u5c31\u7b97\u4f60\u643a\u7f8e\u540c\u6e38\uff0c\u5728\u5927\u59d0\u9762\u524d\u6447\u6765\u6446\u53bb\uff0c\u6211\u4e5f\u80fd\u89c6\u82e5\u4e0d\u89c1\uff0c\u5168\u65e0\u5992\u706b\uff0c\u56e0\u4e3a\uff0c\u6211\u5df2\u7ecf\u5f97\u5230\u4e86\u592a\u591a\uff0c\u800c\u4e14\uff0c\u662f\u90a3\u4e48\u771f\u631a\u3001\u611f\u4eba\uff0c\u6211\u8fd9\u4e9b\u8f6c\u53d8\uff0c\u8001\u5b9e\u8bf4\uff0c\u90fd\u662f\u56e0\u89c2\u5bdf\u4e8c\u5973\u4e3e\u6b62\u4ee5\u53ca\u548c\u5979\u4eec\u4ea4\u8c08\u6240\u5f97\u3002\u201d \u3000\u3000\u201c\u770b\u6765\uff0c\u5979\u4eec\u771f\u7684\u5df2\u53c2\u900f\u4e86\u5929\u72d0\u6b66\u529f\u7684\u4e0a\u4e58\u771f\u8c1b\uff0c\u6446\u8131\u4e86\u4e16\u4fd7\u4e4b\u89c1\u3002\u201d \u3000\u3000\u201c\u8c22\u8c22\u6c5f\u5927\u54e5\u7684\u5938\u5956\uff0c\u2026\u2026\u201d\u541f\u971c\u624b\u6367\u8336\u76d8\uff0c\u7f13\u6b65\u800c\u5165\uff0c\u9053\uff1a\u201c\u6211\u4eec\u53ea\u60f3\u5e2e\u52a9\u4f60\uff0c\u53ea\u60f3\u8ba9\u4f60\u5feb\u4e50\uff0c\u6211\u548c\u59d0\u90fd\u613f\u5316\u4f5c\u6625\u96e8\uff0c\u4f7f\u4f60\u7efd\u5f00\u51fa\u4e00\u6735\u6700\u9c9c\u7f8e\u7684\u82b1\uff0c\u8000\u7167\u6b66\u6797\uff0c\u5149\u8f89\u6c5f\u6e56\uff0c\u6240\u4ee5\uff0c\u4e0d\u7528\u62c5\u5fc3\u6211\u4eec\u4f1a\u963b\u6270\u4f60\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u548c\u971c\u59b9\u65e9\u5c31\u5546\u91cf\u597d\u4e86\uff0c\u2026\u2026\u201d\u541f\u96ea\u63d0\u7740\u8336\u58f6\u884c\u4e86\u8fdb\u6765\uff0c\u63a5\u9053\uff1a\u201c\u6211\u4eec\u53ea\u4ed8\u51fa\uff0c\u4e0d\u8981\u6c42\u4efb\u4f55\u62a5\u507f\u2026\u2026\u201d \u3000\u3000\u201c\u4e8b\u5b9e\u4e0a\uff0c\u5728\u6211\u4eec\u4ed8\u51fa\u7684\u8fc7\u7a0b\u4e2d\uff0c\u6211\u4eec\u5df2\u5f97\u5230\u4e86\u6ee1\u8db3\u3001\u5feb\u4e50\u2026\u2026\u201d\u541f\u971c\u9053\uff1a\u201c\u6211\u4eec\u4f1a\u5c3d\u91cf\u4e0d\u8ba9\u59d0\u548c\u6211\u5e26\u7ed9\u4f60\u4efb\u4f55\u70e6\u607c\uff0c\u90a3\u6015\u662f\u4e00\u70b9\u70b9\uff0c\u4e5f\u975e\u6211\u4eec\u6240\u613f\uff0c\u6211\u4eec\u8981\u4f5c\u4e00\u4ef6\u4e8b\u65f6\uff0c\u90fd\u8003\u8651\u518d\u4e09\uff0c\u9664\u975e\u662f\uff0c\u6211\u4eec\u5b8c\u5168\u6ca1\u6709\u60f3\u5230\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u8981\u7d27\uff0c\u6211\u4eec\u60f3\u4e0d\u5230\uff0c\u5f20\u59e8\u4f1a\u6307\u70b9\u6211\u4eec\u2026\u2026\u201d\u541f\u96ea\u7b11\u9053\uff1a\u201c\u5f20\u59e8\u4f20\u6211\u4eec\u7684\u6613\u5bb9\u624b\u6cd5\uff0c\u518d\u52a0\u4e0a\u4e94\u884c\u9041\u672f\uff0c\u6211\u4eec\u6f5c\u5165\u6d1b\u9633\u5c45\uff0c\u4ea6\u975e\u96be\u4e8b\u2026\u2026\u201d \u3000\u3000\u201c\u8bf4\u4e0d\u5b9a\u4f1a\u9690\u85cf\u5728\u4f60\u7684\u8eab\u8fb9\u54ea\u2026\u2026\u201d\u541f\u971c\u53c8\u63a5\u4e86\u4e00\u53e5\u3002 \u3000\u3000\u201c\u5343\u4e07\u4e0d\u53ef\uff0c\u5343\u4e07\u4e0d\u53ef\u2026\u2026\u201d\u6c5f\u67ab\u5927\u4e3a\u7d27\u5f20\u5730\u8bf4\uff0c\u201c\u6211\u77e5\u9053\u4f60\u4eec\u5f20\u59e8\u7684\u4e94\u884c\u9041\u672f\u7684\u6613\u5bb9\u624b\u6cd5\uff0c\u975e\u5e38\u9ad8\u660e\uff0c\u771f\u8981\u6f5c\u5165\u6d1b\u9633\u5c45\u4e2d\uff0c\u786e\u5b9e\u4e0d\u6613\u5bfb\u627e\uff0c\u4f46\u6b64\u523b\u65f6\u673a\u4e0d\u5bf9\uff0c\u4e0d\u80fd\u5fc3\u5b58\u6e38\u620f\uff0c\u8bef\u4e86\u5927\u4e8b\uff01\u201d \u3000\u3000\u201c\u597d\u561b\uff01\u597d\u561b\uff01\u6c5f\u5927\u54e5\u4e0d\u559c\u6b22\u6211\u4eec\u6e38\u620f\u8bef\u4e8b\uff0c\u6211\u4eec\u4e0d\u53bb\u5c31\u662f\u2026\u2026\u201d\u541f\u96ea\u6e29\u67d4\u7684\u8bf4\u9053\uff1a \u3000\u3000\u201c\u4f46\u6211\u4eec\u5982\u662f\u6709\u4e8b\u8bf7\u547d\uff0c\u8981\u5982\u4f55\u624d\u80fd\u89c1\u4f60\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u6211\u5df2\u60f3\u597d\u4e86\u4e00\u4e2a\u529e\u6cd5\uff0c\u54b1\u4eec\u5728\u957f\u5b89\u57ce\u4e2d\uff0c\u5e03\u7f6e\u4e86\u4e00\u5ea7\u5c45\u4f4f\u4e4b\u5904\uff0c\u4f60\u4eec\u53ef\u4ee5\u653e\u5fc3\uff0c\u4e0d\u4f1a\u8ba9\u4f60\u4eec\u53d7\u59d4\u5c48\u4e86\uff0c\u90a3\u5730\u65b9\u662f\u6df1\u5b85\u5927\u9662\uff0c\u8fd8\u8981\u8bf7\u4e00\u4e9b\u4ec6\u4ece\u5973\u5a62\u4f8d\u5019\u3002\u201d \u3000\u3000\u541f\u971c\u7b11\uff1a\u201c\u597d\u554a\uff01\u6211\u81ea\u61c2\u4e8b\u4e4b\u540e\uff0c\u90fd\u662f\u81ea\u5df1\u52a8\u624b\uff0c\u4ece\u6ca1\u6709\u8981\u522b\u4eba\u7167\u987e\u8fc7\u6211\uff0c\u80fd\u5728\u5f20\u59e8\u7684\u5b89\u6392\u4e0b\uff0c\u7528\u4e2a\u4e2b\u5934\uff0c\u8fc7\u8fc7\u5343\u91d1\u5c0f\u59d0\u7684\u763e\u5934\u4e5f\u597d\u3002\u201d \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u597d\uff01\u5c31\u8fd9\u4e48\u51b3\u5b9a\u4e86\uff0c\u6211\u4e5f\u8be5\u56de\u53bb\u4e86\u2026\u2026\u201d\u7ad9\u8d77\u8eab\u5b50\u5411\u5916\u884c\u53bb\u3002 \u3000\u3000\u56de\u5230\u6d1b\u9633\u5c45\u5185\uff0c\u624d\u77e5\u9053\u6b63\u53d1\u751f\u4e86\u4e00\u4ef6\u4e0d\u53ef\u5f00\u4ea4\u7684\u5927\u4e8b\u3002 \u3000\u3000\u539f\u6765\uff0c\u6885\u82b1\u6765\u62dc\u8bbf\u97e9\u9738\uff0c\u4f46\u5374\u88ab\u80e1\u840d\u3001\u6bb5\u4e5d\u62e6\u5728\u95e8\u5916\uff0c\u6885\u82b1\u8981\u5165\u5185\u67e5\u770b\uff0c\u80e1\u840d\u4e0d\u80af\uff0c\u4e00\u5207\u7b49\u97e9\u9738\u56de\u6765\u4e4b\u540e\u518d\u8bf4\u3002 \u3000\u3000\u548c\u6885\u82b1\u540c\u6765\u7684\u662f\u4e00\u4e2a\u5168\u8eab\u9ed1\u8863\u795e\u60c5\u51b7\u5cfb\u7684\u5c11\u5973\uff0c\u5979\u4e00\u76f4\u9759\u9759\u7684\u7ad9\u5728\u6885\u82b1\u8eab\u4fa7\uff0c\u6ca1\u8bf4\u8fc7\u4e00\u53e5\u8bdd\u3002 \u3000\u3000\u4f46\u5979\u7684\u795e\u60c5\u95f4\uff0c\u4f3c\u65e9\u5df2\u6d41\u73b0\u51fa\u4e0d\u8010\uff0c\u5374\u4e00\u76f4\u7528\u529b\u5728\u514b\u5236\u7740\u3002 \u3000\u3000\u5201\u9e4f\u542c\u5230\u4e86\u6d88\u606f\uff0c\u5306\u5306\u8d76\u6765\uff0c\u4f46\u770b\u5230\u90a3\u5c11\u5973\u4e4b\u540e\uff0c\u7acb\u523b\u6084\u7136\u9000\u8d70\u3002 \u3000\u3000\u5e78\u597d\uff0c\u6c5f\u67ab\u53ca\u65f6\u800c\u5f52\u3002 \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u6253\u91cf\u4e86\u90a3\u5c11\u5973\u4e00\u773c\uff0c\u795e\u8272\u7a81\u7136\u4e00\u53d8\uff0c\u4f46\u4ed6\u5df2\u6765\u4e0d\u53ca\u5411\u6c5f\u67ab\u8bf4\u4ec0\u4e48\u4e86\uff01 \u3000\u3000\u539f\u6765\uff0c\u6885\u82b1\u5df2\u8fce\u4e86\u4e0a\u53bb\uff0c\u9053\uff1a\u201c\u6b63\u8981\u627e\u4f60\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u63a5\u9053\uff1a\u201c\u597d\uff01\u6211\u4f4f\u7684\u5730\u65b9\u72ed\u5c0f\uff0c\u54b1\u4eec\u5230\u8fce\u5bbe\u9601\u4e2d\u8c08\u5427\uff01\u201d \u3000\u3000\u201c\u4e0d\u7528\u4e86\uff0c\u4f55\u5904\u9ec4\u571f\u4e0d\u57cb\u9aa8\uff0c\u5c31\u5728\u97e9\u526f\u603b\u7ba1\u4f4f\u5904\u8c08\u8c08\u5427\uff01\u201d \u3000\u3000\u90a3\u4e00\u76f4\u6c89\u9ed8\u7684\u5c11\u5973\uff0c\u7a81\u7136\u5f00\u4e86\u53e3\uff0c\u679c\u7136\u662f\u51fa\u8bed\u60ca\u4eba\uff0c\u5b57\u5b57\u5982\u94c1\u9524\u51fb\u5ca9\u4e00\u822c\uff0c\u542c\u5f97\u4eba\u5fc3\u5934\u9707\u52a8\u3002 \u3000\u3000\u201c\u65e2\u662f\u5982\u6b64\uff0c\u8bf7\u5165\u5185\u4e00\u8c08\u5427\uff01\u201d \u3000\u3000\u6c5f\u67ab\u5411\u524d\u4e00\u6b65\uff0c\u5e26\u8def\u800c\u884c\u3002 \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u793a\u610f\u6bb5\u4e5d\u53bb\u544a\u8bc9\u9093\u98de\uff0c\u4eba\u5374\u7d27\u968f\u5728\u4fae\u82b1\u8eab\u540e\u800c\u5165\u3002 \u3000\u3000\u539f\u6765\uff0c\u90a3\u9ed1\u8863\u5c11\u5973\u81ea\u548c\u6c5f\u67ab\u5bf9\u8bdd\u4e4b\u540e\uff0c\u4e00\u5207\u53d8\u7684\u4e3b\u52a8\uff0c\u4eba\u4e5f\u62a2\u5728\u4e86\u6885\u82b1\u4e4b\u524d\uff0c\u7d27\u968f\u5728\u6c5f\u67ab\u8eab\u540e\u3002 \u3000\u3000\u6c5f\u67ab\u5934\u672a\u56de\u987e\uff0c\u4f46\u6697\u4e2d\u5374\u51dd\u805a\u4e86\u5168\u8eab\u7684\u529f\u529b\u6212\u5907\u3002 \u3000\u3000\u90a3\u9ed1\u8863\u5c11\u5973\u89c1\u5230\u4e86\u6c5f\u67ab\u4e4b\u540e\uff0c\u8eab\u4e0a\u7a81\u7136\u6563\u64ad\u51fa\u4e00\u80a1\u51b7\u70c8\u7684\u6740\u6c14\uff0c\u80e1\u840d\u548c\u4e03\u5b9d\u548c\u5c1a\u4e5f\u7acb\u523b\u611f\u53d7\u5f97\u5230\u3002 \u3000\u3000\u4e24\u4eba\u90fd\u662f\u7ecf\u9a8c\u4e30\u5bcc\u7684\u4eba\uff0c\u5df2\u77e5\u90a3\u9ed1\u8863\u5c11\u5973\u662f\u4e3a\u6c5f\u67ab\u800c\u6765\uff0c\u672a\u89c1\u6c5f\u67ab\u4e4b\u524d\uff0c\u4e00\u76f4\u9690\u533f\u8d77\u950b\u8292\u3001\u9510\u5904\uff0c\u4e0d\u8ba9\u6740\u6c14\u5916\u9732\u3002 \u3000\u3000\u6c5f\u67ab\u89c1\u5ba2\u7684\u5c0f\u5385\uff0c\u679c\u7136\u662f\u65e2\u5c0f\u53c8\u5e03\u7f6e\u7b80\u5355\uff0c\u4e03\u5b9d\u548c\u5c1a\u4f38\u624b\u62c9\u4e0a\u623f\u95e8\uff0c\u6c5f\u67ab\u4e5f\u4e0d\u518d\u5ba2\u5957\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u770b\u6837\u5b50\uff0c\u6885\u82b1\u59d1\u5a18\u4f3c\u662f\u6025\u7740\u627e\u6211\uff0c\u73b0\u5728\uff0c\u53ef\u4ee5\u8bf4\u4e86\uff0c\u6709\u4f55\u89c1\u6559\uff1f\u201d \u3000\u3000\u201c\u6211\u627e\u4f60\u2026\u2026\u201d\u9ed1\u8863\u5c11\u5973\u63a5\u9053\uff1a\u201c\u6885\u82b1\u53ea\u662f\u966a\u6211\u6765\u7684\uff01\u201d \u3000\u3000\u201c\u4f60\u662f\u2026\u2026\u201d \u3000\u3000\u201c\u9ed1\u5929\u9e45\u2026\u2026\u201d \u3000\u3000\u201c\u662f\u7ef0\u53f7\u4e86\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u795e\u60c5\u4e5f\u51b7\u5389\u8d77\u6765\uff0c\u51b7\u91cd\u7684\u6740\u6c14\uff0c\u4e5f\u7acb\u523b\u6563\u53d1\u51fa\u6765\u3002 \u3000\u3000\u201c\u679c\u7136\u662f\u4e00\u4f4d\u9ad8\u624b\uff0c\u6df1\u85cf\u4e0d\u9732\uff0c\u2026\u2026\u201d\u9ed1\u5929\u9e45\u9053\uff1a\u201c\u6211\u4e0d\u662f\u5185\u5bab\u4e2d\u4eba\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u662f\u4e13\u7a0b\u6765\u627e\u6211\u97e9\u67d0\u4eba\u4e86\u3002\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u56e0\u4e3a\uff0c\u4f60\u65e0\u7f18\u65e0\u6545\u7684\u6740\u4e86\u6211\u4eec\u4e00\u4e2a\u4eba\u2026\u2026\u201d \u3000\u3000\u201c\u662f\u90a3\u4f4d\u7384\u8863\u59d1\u5a18\u4e86\uff1f\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u5979\u4e5f\u662f\u4e00\u53ea\u5929\u9e45\u4e86\uff0c\u2026\u2026\u201d \u3000\u3000\u201c\u9519\u4e86\uff0c\u5979\u5982\u662f\u4e00\u53ea\u5929\u9e45\uff0c\u76f8\u4fe1\u4f60\u4e5f\u6740\u4e0d\u4e86\u5979\uff0c\u2026\u2026\u201d\u9ed1\u5929\u9e45\u9053\uff1a\u201c\u53ef\u60dc\uff0c\u5979\u53ea\u662f\u4e00\u53ea\u559c\u9e4a\uff0c\u624d\u6b7b\u5728\u4e86\u4f60\u7684\u624b\u4e2d\uff01\u201d \u3000\u3000\u201c\u6211\u88ab\u641e\u80e1\u6d82\u4e86\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u53c8\u662f\u559c\u9e4a\uff0c\u53c8\u662f\u5929\u9e45\uff0c\u7a76\u7adf\u662f\u600e\u4e48\u56de\u4e8b\u554a\uff01\u201d \u3000\u3000\u9ed1\u5929\u9e45\u9053\uff1a\u201c\u4f60\u65e2\u7136\u5982\u6b64\u5b64\u964b\u5be1\u95fb\uff0c\u4e5f\u4e0d\u7528\u548c\u4f60\u591a\u8d39\u53e3\u820c\u4e86\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u63a5\u9053\uff1a\u201c\u597d\uff01\u90a3\u5c31\u8a00\u5f52\u6b63\u4f20\u5427\uff01\u4f60\u627e\u6211\uff0c\u5df2\u7ecf\u627e\u5230\u4e86\uff0c\u51c6\u5907\u5e72\u4ec0\u4e48\uff1f\u4e5f\u8be5\u8bf4\u660e\u767d\u4e86\u3002\u201d \u3000\u3000\u201c\u6740\u4f60\uff0c\u66ff\u90a3\u4f4d\u559c\u9e4a\u62a5\u4ec7\u2026\u2026\u201d\u9ed1\u5929\u9e45\u9053\uff1a\u201c\u4f60\u51c6\u5907\u597d\u4e86\uff0c\u6211\u5c31\u51fa\u624b\uff01\u201d \u3000\u3000\u201c\u65e9\u5c31\u51c6\u5907\u597d\u4e86\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u4f60\u968f\u65f6\u90fd\u53ef\u4ee5\u51fa\u624b\uff0c\u4e0d\u8fc7\uff0c\u6211\u8fd8\u662f\u8981\u628a\u8bdd\u8bf4\u4e2a\u6e05\u695a\uff0c\u90a3\u4f4d\u559c\u9e4a\u59d1\u5a18\uff0c\u4f34\u968f\u9ec4\u9f99\u6765\u6d1b\u9633\u5c45\u4e2d\u627e\u9ebb\u70e6\uff0c\u5929\u9a6c\u5802\u4e2d\u4eba\u5df2\u7ecf\u518d\u4e09\u5fcd\u8ba9\uff0c\u6211\u4eec\u5df2\u5728\u5404\u5904\uff0c\u635f\u5931\u4e86\u4e0d\u5c11\u7684\u5730\u76d8\uff0c\u8fd9\u91cc\u4e0d\u80fd\u518d\u4e22\u4e86\uff01\u201d \u3000\u3000\u201c\u6240\u4ee5\uff0c\u5c31\u6d3e\u4e86\u8fd9\u626e\u732a\u5403\u8001\u864e\u7684\u97e9\u9738\u6765\u4f5c\u526f\u603b\u7ba1\uff1f\u201d\u9ed1\u5929\u9e45\u9053\uff1a\u201c\u6885\u82b1\u59d1\u5a18\u544a\u8bc9\u6211\uff0c\u5979\u65e9\u58f0\u660e\u8fc7\u4e0d\u662f\u5185\u5bab\u4e00\u7cfb\u4e2d\u4eba\uff0c\u4f46\u4f60\u8fd8\u662f\u6740\u4e86\u5979\u3002\u201d \u3000\u3000\u201c\u4e5f\u8bb8\u5728\u4e0b\u542c\u9519\u4e86\uff0c\u5979\u53ea\u8bf4\u4e0d\u662f\u9ec4\u9f99\u7684\u60c5\u4fa3\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u5c31\u50cf\u4f60\u59d1\u5a18\u4e00\u6837\uff0c\u8bf4\u4e86\u534a\u5929\uff0c\u8fd8\u672a\u8bf4\u51fa\u4f60\u771f\u6b63\u7684\u8eab\u4efd\uff1f\u5929\u9e45\u3001\u559c\u9e4a\uff0c\u53c8\u662f\u600e\u4e48\u6837\u4e00\u4e2a\u7ec4\u5408\uff0c\u548c\u5185\u5bab\u4e00\u7cfb\u4e2d\uff0c\u6709\u4ec0\u4e48\u4ea4\u60c5\uff1f\u4e3a\u4ec0\u4e48\u8981\u5e2e\u4ed6\u4eec\u548c\u5929\u9a6c\u603b\u5802\u4e2d\u4eba\u4f5c\u5bf9\uff1f\u201d \u3000\u3000\u9ed1\u5929\u9e45\u9053\uff1a\u201c\u4f60\u4e0d\u77e5\u9053\u559c\u9e4a\u662f\u4ec0\u4e48\u8eab\u4efd\uff0c\u51fa\u8eab\u4f55\u5904\uff1f\u4f46\u4f60\u6740\u4e86\u5979\uff0c\u6211\u73b0\u5728\u5c31\u4e3a\u5979\u62a5\u4ec7\uff01\u201d \u3000\u3000\u53f3\u624b\u4e00\u626c\uff0c\u62cd\u5411\u6c5f\u67ab\u3002 \u3000\u3000\u8fd9\u5ea7\u72ed\u5c0f\u7684\u5ba2\u5385\u4e2d\uff0c\u53cc\u65b9\u7684\u8ddd\u79bb\u5f88\u8fd1\uff0c\u4f46\u624b\u5c31\u53ef\u4ee5\u89e6\u53ca\u5230\u5bf9\u65b9\uff0c\u8fd9\u4e00\u638c\u62cd\u5411\u524d\u80f8\uff0c\u95ea\u907f\u90fd\u4e0d\u5bb9\u6613\u3002 \u3000\u3000\u4f46\u6c5f\u67ab\u6ca1\u6709\u95ea\u907f\uff0c\u53f3\u624b\u4e00\u62ac\uff0c\u4e94\u6307\u53cd\u5411\u9ed1\u5929\u9e45\u7684\u53f3\u8155\u6263\u53bb\uff0c\u52a8\u4f5c\u5947\u5feb\u3002 \u3000\u3000\u201c\u597d\uff01\u2026\u2026\u201d\u9ed1\u5929\u9e45\u5fae\u4e00\u632b\u8155\uff0c\u6613\u638c\u4e3a\u6307\uff0c\u70b9\u5411\u6c5f\u67ab\u7684\u4eba\u4e2d\u7a74\u3002 \u3000\u3000\u6c5f\u67ab\u7a81\u7a74\u622a\u8109\uff0c\u5c01\u95ed\u4e86\u9ed1\u5929\u9e45\u4e00\u8fde\u5341\u4e8c\u62db\u7684\u653b\u52bf\u3002 \u3000\u3000\u8fd9\u5730\u65b9\u592a\u5c0f\u4e86\uff0c\u65e0\u6cd5\u95ea\u907f\uff0c\u4e5f\u65e0\u6cd5\u98de\u817f\u653b\u654c\uff0c\u4e24\u4eba\u4e00\u756a\u4ea4\u624b\uff0c\u5168\u5728\u638c\u6307\u7684\u53d8\u5316\u4e0a\u62a2\u5236\u5148\u673a\u3002 \u3000\u3000\u4e00\u8f6e\u62a2\u653b\u4e4b\u540e\uff0c\u9ed1\u5929\u9e45\u653b\u52bf\u4e00\u7f13\u3002 \u3000\u3000\u4f46\u6c5f\u67ab\u5374\u501f\u673a\u5c55\u5f00\u4e86\u51cc\u5389\u7684\u53cd\u51fb\uff0c\u6307\u70b9\u638c\u5207\uff0c\u628a\u9ed1\u5929\u9e45\u903c\u9000\u4e86\u4e24\u6b65\u3002 \u3000\u3000\u9ed1\u5929\u9e45\u5fc3\u5934\u9707\u60ca\uff0c\u8fd9\u4e2a\u4eba\u679c\u975e\u5c0f\u53ef\uff0c\u539f\u672c\u60f3\u6765\uff0c\u6740\u654c\u4e0d\u8fc7\u662f\u4e3e\u624b\u4e4b\u52b3\uff0c\u90a3\u6653\u5f97\u7adf\u7136\u9047\u4e0a\u4e86\u771f\u6b63\u7684\u9ad8\u624b\u3002 \u3000\u3000\u8fd9\u72ed\u5c0f\u7684\u5385\u623f\uff0c\u5c31\u6210\u4e86\u5979\u5fc5\u8d25\u7684\u9650\u5236\uff0c\u56e0\u4e3a\uff0c\u6709\u5f88\u591a\u7edd\u6280\uff0c\u65e0\u6cd5\u5728\u5982\u6b64\u72ed\u5c0f\u7684\u7a7a\u95f4\u65bd\u5c55\u3002 \u3000\u3000\u6c5f\u67ab\u4e5f\u6697\u6697\u79f0\u8d5e\u9ed1\u5929\u9e45\u7684\u9ad8\u660e\uff0c\u51e0\u4e4e\u88ab\u8feb\u7684\u8981\u5168\u529b\u65bd\u5c55\uff0c\u624d\u80fd\u591f\u5e94\u4ed8\u4e0b\u6765\uff01\u8fd9\u4e2a\u5973\u4eba\u8eab\u624b\u7edd\u5bf9\u5728\u6885\u82b1\u4e4b\u4e0a\uff0c\u53ea\u662f\u4e0d\u80fd\u9884\u6d4b\uff0c\u6740\u4e86\u8fd9\u9ed1\u5929\u9e45\u4e4b\u540e\uff0c\u4f1a\u6709\u4e9b\u4ec0\u4e48\u540e\u679c\uff1f \u3000\u3000\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u53d8\u62db\u66f4\u89c1\u5feb\u901f\uff0c\u4e00\u62db\u2018\u667a\u73e0\u5728\u63e1\u2019\uff0c\u6263\u4f4f\u4e86\u9ed1\u5929\u9e45\u53f3\u8155\u8109\u7a74\u3002 \u3000\u3000\u9ed1\u5929\u9e45\u7acb\u523b\u5168\u8eab\u53d7\u5236\uff0c\u6709\u5982\u7827\u677f\u4e0a\u8089\uff0c\u542c\u51ed\u65a9\u5272\u4e86\u3002 \u3000\u3000\u6c5f\u67ab\u5de6\u624b\u626c\u8d77\u2026\u2026 \u3000\u3000\u201c\u4f60\u4e0d\u80fd\u6740\u5979\u2026\u2026\u201d\u7ad9\u5728\u4e00\u4fa7\u89c2\u6218\u7684\u6885\u82b1\u5927\u58f0\u53eb\u9053\uff1a\u201c\u5979\u4e0d\u662f\u5185\u5bab\u4e2d\u4eba\uff0c\u6740\u4e86\u5979\uff0c\u4f60\u4f1a\u540e\u6094\u7684\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u5df2\u7ecf\u6740\u4e86\u4e00\u53ea\u559c\u9e4a\uff0c\u4ec7\u6068\u5df2\u7ed3\uff0c\u518d\u6740\u4e00\u53ea\u5929\u9e45\uff0c\u6709\u4f55\u4e0d\u53ef\uff1f\u201d\u6c5f\u67ab\u5927\u58f0\u7684\u56de\u5e94\uff0c\u76ee\u5149\u5374\u4e00\u77a5\u9ed1\u5929\u9e45\u8138\u4e0a\u795e\u8272\u3002 \u3000\u3000\u4ed6\u53d1\u89c9\u9ed1\u5929\u9e45\u7684\u8138\u4e0a\uff0c\u662f\u4e00\u79cd\u60ca\u6127\u4ea4\u96c6\u7684\u795e\u60c5\uff0c\u4f3c\u662f\u5bf9\u88ab\u6263\u7a74\u751f\u64d2\u4e00\u4e8b\uff0c\u611f\u5230\u610f\u5916\uff0c\u4e5f\u6ca1\u6709\u608d\u4e0d\u754f\u6b7b\u7684\u6c14\u52bf\u3002 \u3000\u3000\u6536\u56de\u4e86\u5de6\u638c\uff0c\u6c5f\u67ab\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u6885\u82b1\u59d1\u5a18\u7684\u610f\u601d\u662f\uff0c\u8981\u6211\u653e\u4e86\u5979\uff1f\u201d \u3000\u3000\u5fc3\u4e2d\u6697\u4f5c\u51b3\u5b9a\uff0c\u903c\u6885\u82b1\u8bf4\u51fa\u8fd9\u9ed1\u5929\u9e45\u7684\u6765\u5386\u3002 \u3000\u3000\u201c\u5c11\u6740\u4e00\u4e2a\u4eba\uff0c\u5bf9\u4f60\u6709\u4ec0\u4e48\u574f\u5904\uff1f\u2026\u2026\u201d\u6885\u82b1\u8bf4\uff0c\u201c\u4f55\u51b5\u4f60\u4eec\u4e4b\u95f4\u4e5f\u6ca1\u6709\u4ec0\u4e48\u4ec7\u6068\u3002\u201d \u3000\u3000\u201c\u653e\u4e86\u5979\u5bf9\u6211\u4e5f\u6ca1\u6709\u4ec0\u4e48\u597d\u5904\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u5728\u6c5f\u6e56\u4e0a\u6211\u5c31\u591a\u4e86\u5979\u4e00\u4e2a\u4ec7\u4eba\uff0c\u65e5\u540e\uff0c\u5979\u5982\u9047\u4e0a\u673a\u4f1a\uff0c\u81ea\u7136\u4f1a\u51fa\u624b\u62a5\u4ec7\u3002\u201d \u3000\u3000\u6885\u82b1\u9053\uff1a\u201c\u90a3\u4f60\u662f\u975e\u8981\u6740\u4ed6\u4e0d\u53ef\u4e86\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u662f\u554a\u2026\u2026\u201d \u3000\u3000\u201c\u5979\u771f\u7684\u4e0d\u662f\u6211\u4eec\u7684\u4eba\u2026\u2026\u201d\u6885\u82b1\u9053\uff1a\u201c\u5979\u53f7\u79f0\u9ed1\u5929\u9e45\uff0c\u5929\u9a6c\u5802\u4e2d\u4eba\uff0c\u4e5f\u8be5\u77e5\u9053\u5979\u7684\u8eab\u4efd\u554a\u3002\u201d \u3000\u3000\u4f46\u95fb\u4e00\u9635\u6b65\u5c65\u58f0\u4f20\u5165\u8033\u9645\uff0c\u5173\u95ed\u7684\u5927\u95e8\uff0c\u88ab\u4eba\u63a8\u5f00\u3002 \u3000\u3000\u9093\u98de\u5f53\u95e8\u800c\u7acb\uff0c\u77a7\u4e86\u9ed1\u5929\u9e45\u4e00\u773c\uff0c\u9053\uff1a\u201c\u97e9\u9738\uff0c\u5feb\u4e9b\u653e\u4eba\uff01\u201d \u3000\u3000\u6c5f\u67ab\u5fae\u5fae\u4e00\u6014\uff0c\u9053\uff1a\u201c\u653e\u4eba\uff1f\u5979\u662f\u4e13\u7a0b\u6765\u6740\u6211\u7684\u5440\uff1f\u201d \u3000\u3000\u9093\u98de\u6025\u9053\uff1a\u201c\u4f46\u4f60\u8fd8\u597d\u597d\u7684\u6d3b\u7740\uff0c\u6beb\u53d1\u65e0\u996c\u3002\u201d \u3000\u3000\u201c\u5982\u82e5\u5c5e\u4e0b\u653e\u4e86\u5979\uff0c\u5979\u518d\u627e\u5c5e\u4e0b\u62a5\u4ec7\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u5c82\u4e0d\u662f\u81ea\u5bfb\u70e6\u607c\u5417\uff1f\u201d \u3000\u3000\u9093\u98de\u6709\u4e9b\u6025\u4e86\uff0c\u9ad8\u58f0\u8bf4\u9053\uff1a\u201c\u9093\u67d0\u4eba\u4fdd\u8bc1\u4e0d\u4f1a\uff0c\u5feb\u4e9b\u628a\u4eba\u653e\u4e86\u3002\u201d \u3000\u3000\u6c5f\u67ab\u677e\u5f00\u53f3\u624b\uff0c\u653e\u4e86\u9ed1\u5929\u9e45\u3002 \u3000\u3000\u9093\u98de\u6bd5\u606d\u6bd5\u656c\u5730\u8bf4\uff0c\u201c\u6d1b\u9633\u5c45\u603b\u7ba1\u9093\u98de\uff0c\u606d\u9001\u9ed1\u5929\u9e45\u59d1\u5a18\u3002\u201d \u3000\u3000\u201c\u4f60\u8fd9\u4f4d\u526f\u603b\u7ba1\u7684\u8eab\u624b\u4e0d\u9519\u2026\u2026\u201d\u9ed1\u5929\u9e45\u9053\uff1a\u201c\u6211\u6b20\u4e86\u4f60\u4e00\u4efd\u60c5\uff0c\u6709\u673a\u4f1a\uff0c\u6211\u4f1a\u62a5\u7b54\u3002\u201d \u3000\u3000\u4e00\u62c9\u6885\u82b1\uff0c\u5927\u6b65\u800c\u53bb\u3002 \u3000\u3000\u9093\u98de\u62b1\u62f3\u8eac\u8eab\uff0c\u9001\u8d70\u4e86\u9ed1\u5929\u9e45\uff0c\u9053\uff1a\u201c\u97e9\u8001\u5f1f\u771f\u7684\u4e0d\u8ba4\u8bc6\u5979\u5417\uff1f\u201d \u3000\u3000\u201c\u4e0d\u8ba4\u8bc6\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u8fd8\u671b\u603b\u7ba1\u6307\u70b9\uff0c\u6307\u70b9\uff1f\u201d \u3000\u3000\u201c\u9ed1\u3001\u767d\u3001\u7ea2\u3001\u9ec4\uff0c\u56db\u5929\u9e45\u2026\u2026\u201d\u9093\u98de\u9053\uff1a\u201c\u662f\u5929\u738b\u95e8\u4e2d\u7684\u4eba\uff0c\u5728\u5929\u9a6c\u5802\u548c\u5185\u5bab\u4e00\u7cfb\u4e2d\u4e89\u6597\u65e5\u70c8\u4e4b\u9645\uff0c\u81ea\u7136\u662f\u6709\u7740\u4e3e\u8db3\u8f7b\u91cd\u7684\u529b\u91cf\uff0c\u6240\u4ee5\uff0c\u54b1\u4eec\u4e0d\u80fd\u6740\u5979\uff0c\u628a\u5929\u738b\u95e8\u903c\u5f97\u548c\u5185\u5bab\u4e00\u7cfb\u8054\u624b\u3002\u201d \u3000\u3000\u201c\u653e\u4e86\u5979\uff0c\u5979\u4f1a\u548c\u6211\u4eec\u5408\u4f5c\u5417\uff1f\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u5947\u602a\u7684\u662f\uff0c\u5979\u65e2\u5df2\u8fdb\u4e86\u6d1b\u9633\u5c45\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u548c\u5979\u4eec\u81ea\u5df1\u4eba\u8fde\u7edc\u5462\uff1f\u5374\u627e\u4e0a\u4e86\u6885\u82b1\uff1f\u201d \u3000\u3000\u9093\u98de\u4f4e\u58f0\u9053\uff1a\u201c\u4f60\u8bf4\u5979\u4eec\u81ea\u5df1\u7684\u4eba\uff0c\u662f\u6307\u90a3\u4e2a\uff1f\u201d \u3000\u3000\u201c\u9752\u51e4\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u5929\u738b\u95e8\u4e2d\u7684\u4eba\uff0c\u603b\u7ba1\u771f\u7684\u4e0d\u77e5\u9053\u4e48\uff1f\u201d \u3000\u3000\u9093\u98de\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u77e5\u9053\uff1a\u4f46\u5374\u65e0\u6cd5\u8bc1\u5b9e\uff0c\u80af\u5b9a\u3002\u201d \u3000\u3000\u201c\u9752\u51e4\u3001\u9ed1\u5929\u9e45\uff0c\u2026\u2026\u51e4\u3001\u9e45\u3001\u559c\u9e4a\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u4f3c\u6709\u6240\u609f\u7684\u9053\uff1a\u201c\u5979\u4eec\u7528\u9e1f\u6d3e\u540d\uff0c\u5206\u51fa\u7b49\u7ea7\uff0c\u51e4\u4e3a\u767e\u9e1f\u4e4b\u738b\uff0c\u53ef\u80fd\u662f\u6700\u9ad8\u7684\u4e00\u7ea7\u4e86\uff01\u5929\u9e45\u5e94\u8be5\u5728\u51e4\u4e4b\u4e0b\uff0c\u662f\u7b2c\u4e8c\u7ea7\u4e86\u3002\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u4e0d\u9519\uff0c\u5929\u738b\u95e8\u4e2d\u6700\u6d3b\u8dc3\u7684\u4eba\u7269\uff0c\u5c31\u662f\u56db\u5927\u5929\u9e45\u4e86\u3002\u201d\u9093\u98de\u9053\uff1a\u201c\u5979\u4eec\u65e2\u7136\u51fa\u4e86\u9762\uff0c\u770b\u6765\uff0c\u5929\u738b\u95e8\uff0c\u4e5f\u4e0d\u51c6\u5907\u7f6e\u8eab\u4e8b\u5916\u4e86\uff0c\u8fd9\u4ef6\u4e8b\uff0c\u6211\u5e94\u8be5\u65e9\u4e00\u4e9b\u62a5\u4e0a\u603b\u5802\uff0c\u4e5f\u597d\u8ba9\u4ed6\u4eec\u6709\u6240\u51c6\u5907\u3002\u201d \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u603b\u7ba1\u7684\u610f\u601d\u662f\u5929\u738b\u95e8\u4e2d\u4eba\uff0c\u4f1a\u5e2e\u52a9\u5185\u5bab\u4e00\u7cfb\u4e2d\u4eba\u3002\u5bf9\u4ed8\u6211\u4eec\u4e86\uff1f\u201d \u3000\u3000\u201c\u73b0\u5728\uff0c\u9ed1\u5929\u9e45\u5df2\u7ecf\u63d2\u624b\u4e86\u201d\u9093\u98de\u9053\uff1a\u201c\u4f46\u9752\u51e4\u53c8\u4f3c\u662f\u504f\u5411\u6211\u4eec\u4e00\u4e9b\uff0c\u8fd9\u7a76\u7adf\u662f\u600e\u4e48\u56de\u4e8b\u5462\uff1f\u201d \u3000\u3000\u201c\u6211\u6740\u4e86\u4e00\u53ea\u559c\u9e4a\uff0c\u53ef\u80fd\u662f\u9ed1\u5929\u9e45\u7684\u624b\u4e0b\uff0c\u6240\u4ee5\uff0c\u5979\u6765\u627e\u6211\u62a5\u4ec7\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u7eaf\u5c5e\u79c1\u4eba\u884c\u52a8\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u7ba1\u5982\u4f55\uff0c\u8fd9\u4ef6\u4e8b\uff0c\u7edd\u4e0d\u80fd\u9690\u7792\uff0c\u6211\u5f97\u4f20\u62a5\u603b\u5802\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u7528\u603b\u7ba1\u8d39\u5fc3\u4e86\u2026\u2026\u201d\u5201\u9e4f\u5feb\u6b65\u884c\u4e86\u8fdb\u6765\uff0c\u63a5\u9053\uff1a\u201c\u6211\u5df2\u7ecf\u4ee3\u4f60\u6548\u52b3\uff0c\u5448\u62a5\u603b\u5802\u53bb\u4e86\u3002\u201d \u3000\u3000\u9093\u98de\u55ef\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5201\u5144\uff0c\u7528\u7684\u4ec0\u4e48\u65b9\u6cd5\u5448\u62a5\uff1f\u201d \u3000\u3000\u201c\u4fe1\u9e3d\uff0c\u800c\u4e14\u4e09\u9e3d\u5e76\u98de\uff0c\u5341\u4e07\u706b\u6025\u2026\u2026\u201d\u5201\u9e4f\u9053\uff1a\u201c\u9ed1\u5929\u9e45\u4e0d\u8db3\u4e3a\u654c\uff0c\u4f46\u5979\u4ee3\u8868\u7684\u529b\u91cf\uff0c\u5c31\u5341\u5206\u53ef\u6015\u4e86\uff01\u201d \u3000\u3000\u76ee\u5149\u8f6c\u6ce8\u5230\u6c5f\u67ab\u7684\u8138\u4e0a\uff0c\u63a5\u9053\uff1a\u201c\u97e9\u5144\u548c\u9ed1\u5929\u9e45\u52a8\u8fc7\u624b\u4e86\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u5982\u975e\u603b\u7ba1\u4e0b\u4ee4\u653e\u4eba\uff0c\u4e5f\u8bb8\uff0c\u97e9\u67d0\u4eba\u65e9\u5df2\u8ba9\u5979\u8840\u6d41\u4e94\u6b65\uff0c\u6b7b\u4e8e\u6b64\u5730\u4e86\uff0c\u4e0d\u77e5\u5201\u5144\uff0c\u4f55\u4ee5\u8ba4\u8bc6\u9ed1\u5929\u9e45\u3002\u201d \u3000\u3000\u201c\u53bb\u5e74\u4e03\u6708\uff0c\u5929\u738b\u8bbf\u95ee\u5929\u9a6c\u603b\u5802\uff0c\u56db\u5927\u5929\u9e45\u968f\u884c\u4fdd\u9a7e\u2026\u2026\u201d\u5201\u9e4f\u9053\uff1a\u201c\u603b\u5802\u4e2d\u4eba\uff0c\u6709\u5f88\u591a\u89c1\u8fc7\u5979\u4eec\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u5929\u738b\u548c\u603b\u5802\u4e3b\u5e94\u8be5\u6709\u4e9b\u4ea4\u60c5\u4e86\uff0c\u53ef\u662f\uff0c\u9ed1\u5929\u9e45\u53c8\u4e3a\u4ec0\u4e48\u8981\u5e2e\u52a9\u5185\u5bab\u4e2d\u4eba\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u90a3\u5929\u6211\u6740\u6b7b\u7684\u7384\u8863\u5c11\u5973\uff0c\u636e\u9ed1\u5929\u9e45\u8bf4\uff0c\u5979\u662f\u4e00\u53ea\u559c\u9e4a\uff0c\u5201\u5144\u53ef\u77e5\u9053\uff1a\u5929\u9e45\u548c\u559c\u9e4a\u4e4b\u95f4\uff0c\u662f\u600e\u4e48\u4e00\u56de\u4e8b\u554a\uff1f\u201d \u3000\u3000\u201c\u4e00\u53ea\u5929\u9e45\u56db\u53ea\u9e4a\uff0c\u559c\u9e4a\u662f\u5929\u9e45\u7684\u624b\u4e0b\uff0c\u4f46\u5929\u9e45\u53ea\u6709\u56db\u4e2a\uff0c\u4ee5\u670d\u8272\u5206\u522b\u2026\u2026\u201d\u5201\u9e4f\u9053\uff1a \u3000\u3000\u201c\u559c\u9e4a\u6709\u591a\u5c11\uff1f\u5982\u4f55\u4e00\u4e2a\u5206\u6cd5\uff0c\u662f\u5929\u738b\u95e8\u4e2d\u7684\u79d8\u5bc6\uff0c\u522b\u4eba\u5c31\u4e0d\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u6c5f\u67ab\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u603b\u7ba1\u5982\u662f\u665a\u6765\u4e00\u6b65\uff0c\u5728\u4e0b\u5bb0\u4e86\u9ed1\u5929\u9e45\uff0c\u90a3\u5c31\u4e0d\u77e5\u9053\u4f1a\u662f\u600e\u6837\u4e00\u4e2a\u5c40\u9762\u4e86\uff1f\u201d \u3000\u3000\u201c\u603b\u5802\u4e00\u5b9a\u4f1a\u8ffd\u67e5\u8fd9\u4ef6\u4e8b\u60c5\u2026\u2026\u201d\u5201\u9e4f\u9053\uff1a\u201c\u4f46\u4e0d\u77e5\u8005\u4e0d\u7f6a\u5440\uff01\u97e9\u5144\u4e0d\u8ba4\u8bc6\u9ed1\u5929\u9e45\uff0c\u6740\u4e86\u5979\uff0c\u4e5f\u662f\u6ca1\u6709\u529e\u6cd5\u7684\u4e8b\u4e86\u3002\u201d \u3000\u3000\u201c\u9ed1\u5929\u9e45\u7684\u6b66\u529f\u4e0d\u9519\uff0c\u5728\u4e0b\u80dc\u5f97\u4fa5\u5e78\uff0c\u8fd9\u5730\u65b9\u592a\u72ed\u5c0f\u4e86\uff0c\u5979\u6709\u4e9b\u65bd\u5c55\u4e0d\u5f00\uff0c\u5929\u9e45\u561b\uff01\u603b\u8981\u80fd\u98de\u80fd\u8df3\u7684\u5730\u65b9\uff0c\u624d\u80fd\u65bd\u5c55\u5f00\u624b\u811a\u3002\u201d \u3000\u3000\u9093\u98de\u53cc\u76ee\u76ef\u6ce8\u5728\u97e9\u9738\u7684\u8138\u4e0a\uff0c\u77a7\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u97e9\u5144\u5f1f\uff0c\u4f60\u2026\u2026\u4f60\u7684\u6b66\u529f\uff0c\u7a76\u7adf\u5230\u4e86\u4ec0\u4e48\u5883\u754c\uff0c\u8fd8\u6709\u591a\u5c11\u7edd\u62db\u672a\u66fe\u65bd\u5c55\uff0c\u4f60\u7684\u51fa\u8eab\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u603b\u7ba1\uff0c\u4f60\u95ee\u7684\u592a\u6025\u4e86\uff0c\u8fd9\u4e9b\u4e8b\uff0c\u603b\u7ba1\u60f3\u77e5\u9053\uff0c\u6211\u4e00\u5b9a\u4f1a\u8be6\u7ec6\u5949\u544a\u3002\u201d \u3000\u3000\u201c\u4e0d\u6b62\u662f\u6211\uff0c\u518d\u8bf4\uff0c\u6211\u77e5\u9053\u6709\u4f60\u8fd9\u4e48\u4e00\u4e2a\u597d\u526f\u603b\u7ba1\u5c31\u884c\u4e86\uff0c\u2026\u2026\u201d\u9093\u98de\u9053\uff1a\u201c\u4f46\u5929\u865a\u5b50\u526f\u603b\u5802\u4e3b\u5c31\u4e0d\u540c\u4e86\uff0c\u4ed6\u9650\u6211\u5728\u4eca\u591c\u4e4b\u524d\uff0c\u95ee\u51fa\u4f60\u7684\u51fa\u8eab\u6765\u5386\uff0c\u7136\u540e\uff0c\u7528\u98de\u9e3d\u4f20\u62a5\u603b\u5802\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u7b49\u4e00\u4e0b\uff0c\u6211\u5c31\u5199\u7ed9\u4f60\u2026\u2026\u201d\u6c5f\u67ab\u53c8\u8f6c\u6ce8\u7740\u5201\u9e4f\uff0c\u9053\uff1a\u201c\u4ee5\u5201\u5144\u89c1\u8bc6\u4e4b\u5e7f\uff0c\u731c\u731c\u770b\u4ed6\u4eec\u4e0b\u4e00\u6b65\uff0c\u4f1a\u6709\u5982\u4f55\u7684\u884c\u52a8\uff1f\u201d \u3000\u3000\u201c\u9ed1\u5929\u9e45\u9510\u6c14\u5df2\u632b\uff0c\u5728\u6ca1\u6709\u65b0\u7684\u63f4\u624b\u8d76\u5230\u4e4b\u524d\uff0c\u4ed6\u4eec\u5e94\u8be5\u4e0d\u4f1a\u518d\u6709\u4ec0\u4e48\u884c\u52a8\u4e86\u2026\u2026\u201d\u5201\u9e4f\u8bf4\uff0c\u201c\u76ee\u4e0b\u6700\u5927\u7684\u95ee\u9898\u662f\uff0c\u9ed1\u5929\u9e45\u8fd9\u6b21\u632b\u8d25\uff0c\u4f1a\u5728\u5929\u738b\u95e8\u4e2d\u53d1\u751f\u4e9b\u4ec0\u4e48\u5f71\u54cd\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u5201\u5144\u7684\u610f\u601d\uff0c\u53ef\u662f\u8bf4\uff0c\u5929\u738b\u95e8\u4f1a\u56e0\u9ed1\u5929\u9e45\u8fd9\u756a\u632b\u8d25\uff0c\u4f1a\u548c\u5185\u5bab\u4e2d\u4eba\u8054\u624b\u4e86\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4e2a\uff0c\u5f88\u96be\u80af\u5b9a\u4e86\uff0c\u9ed1\u5929\u9e45\u5728\u5929\u738b\u95e8\u4e2d\u7684\u8eab\u4efd\u54c1\u7ea7\uff0c\u4e0d\u662f\u9876\u9ad8\uff0c\u53ea\u4e0d\u8fc7\uff0c\u5979\u5e38\u5e38\u8ffd\u968f\u5728\u5929\u738b\u8eab\u4fa7\uff0c\u8fd1\u6c34\u697c\u53f0\uff0c\u80fd\u548c\u5929\u738b\u8c08\u4e0a\u79c1\u8bdd\uff0c\u5982\u662f\u5979\u52a0\u6cb9\u6dfb\u918b\u7684\u65e0\u4e2d\u751f\u6709\uff0c\u90a3\u5c31\u53ef\u80fd\u5f71\u54cd\u5230\u5929\u738b\u7684\u5224\u65ad\u548c\u51b3\u5b9a\u4e86\u3002\u201d \u3000\u3000\u201c\u5201\u5144\u5206\u6790\u5165\u5fae\uff0c\u5341\u5206\u6709\u7406\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u4f46\u6700\u91cd\u8981\u7684\u662f\u603b\u5802\u7684\u770b\u6cd5\u548c\u6001\u5ea6\u4e86\uff01\u5185\u5bab\u4e00\u7cfb\uff0c\u593a\u53d6\u5929\u9a6c\u5802\u7684\u5730\u76d8\uff0c\u5929\u738b\u95e8\u53c8\u4f55\u5f53\u6ca1\u6709\u8fd9\u4efd\u5fc3\u610f\uff0c\u53ea\u4e0d\u8fc7\uff0c\u8fdc\u6ca1\u6709\u4ed8\u8bf8\u884c\u52a8\u7f62\u4e86\u3002\u201d \u3000\u3000\u5201\u9e4f\u9053\uff1a\u201c\u97e9\u5144\u9ad8\u89c1\uff0c\u8fd9\u4e00\u70b9\uff0c\u5144\u5f1f\u4e5f\u5c3d\u5feb\u8f6c\u62a5\u603b\u5802\uff0c\u63d0\u4f9b\u53c2\u8003\u3002\u201d \u3000\u3000\u6c5f\u67ab\u5fc3\u4e2d\u6697\u6697\u9a82\u9053\uff1a\u201c\u4eba\u8001\u6210\u5978\uff0c\u8fd9\u4e24\u4e2a\u4eba\uff0c\u4e2a\u4e2a\u6ed1\u4e0d\u6e9c\u624b\uff0c\u8fde\u4e00\u53e5\u6709\u62c5\u5f53\u7684\u8bdd\uff0c\u4e5f\u4e0d\u80af\u8bf4\u3002\u201d\u5f53\u4e0b\u6325\u6325\u624b\uff0c\u9053\uff1a\u201c\u603b\u7ba1\u548c\u5201\u5144\u8bf7\u56de\u5427\uff01\u521a\u624d\u4e00\u756a\u6076\u6218\uff0c\u5144\u5f1f\u5b9e\u5728\u6709\u70b9\u7d2f\u4e86\uff0c\u6211\u8981\u5750\u606f\u4e00\u4e0b\u624d\u884c\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\uff0c\u4f60\u7684\u51fa\u8eab\u6765\u5386\u2026\u2026\u201d\u9093\u98de\u671b\u7740\u6c5f\u67ab\uff0c\u6709\u4e9b\u8270\u6da9\u5730\u8bf4\u3002 \u3000\u3000\u201c\u7b49\u4e00\u4e0b\uff0c\u6211\u4f1a\u6d3e\u5165\u9001\u8fc7\u53bb\u2026\u2026\u201d \u3000\u3000\u5201\u9e4f\u4e00\u62b1\u62f3\uff0c\u9053\uff1a\u201c\u597d\uff01\u97e9\u5144\u8bf7\u4f11\u606f\uff0c\u5144\u5f1f\u6731\u544a\u9000\u3002\u201d \u3000\u3000\u9093\u98de\u4e5f\u53ea\u597d\u79bb\u5f00\u3002 \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u9001\u8d70\u4e86\u4e24\u4eba\uff0c\u56de\u5230\u6c5f\u67ab\u7684\u5367\u5ba4\u4e2d\uff0c\u7b11\u9053\uff1a\u201c\u662f\u4e0d\u662f\u6709\u4e9b\u5931\u671b\uff1f\u201d \u3000\u3000\u201c\u5bf9\uff01\u8fd9\u4e24\u4e2a\u4eba\u8001\u5978\u5de8\u6ed1\uff0c\u6709\u529f\u5c31\u62a2\uff0c\u6709\u8fc7\u5c31\u63a8\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u770b\u7684\u53eb\u4eba\u751f\u6c14\uff01\u201d \u3000\u3000\u201c\u5176\u5b9e\uff0c\u4e24\u4e2a\u4eba\u90fd\u6709\u8bdd\u8981\u544a\u8bc9\u4f60\u2026\u2026\u201d\u5927\u548c\u5c1a\u8bf4\uff0c\u201c\u4e0d\u8fc7\uff0c\u4ed6\u4eec\u62c5\u5fc3\u88ab\u5bf9\u65b9\u6293\u4f4f\u628a\u67c4\uff0c\u8c01\u4e5f\u4e0d\u80af\u5148\u8bf4\u5b9e\u8bdd\uff0c\u4f60\u63a8\u6211\u6258\uff0c\u6253\u8d77\u592a\u6781\u62f3\u4e86\u3002\u201d \u3000\u3000\u6c5f\u67ab\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u6211\u5c31\u628a\u4ed6\u4eec\u8d76\u8d70\uff0c\u8981\u548c\u4f60\u7814\u7a76\u4e00\u4e0b\uff0c\u8be5\u5982\u4f55\u884c\u52a8\u4e86\u3002\u201d \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u4f4e\u58f0\uff0c\u9053\uff1a\u201c\u53ef\u662f\u60f3\u72d9\u6740\u9ed1\u5929\u9e45\uff1f\u201d \u3000\u3000\u201c\u6211\u60f3\u53bb\u89c1\u89c1\u9752\u51e4\uff0c\u63a2\u542c\u4e00\u4e0b\u5929\u738b\u95e8\u771f\u7684\u6001\u5ea6\u5982\u4f55\uff1f\u2026\u2026\u201d\u6c5f\u67ab\u8bf4\uff0c\u201c\u9ed1\u5929\u9e45\u6b66\u529f\u4e0d\u9519\uff0c\u4f46\u8fd8\u4e0d\u5230\u6211\u4eec\u72d9\u6740\u7684\u6c34\u51c6\uff0c\u653e\u624b\u4e00\u6218\uff0c\u6211\u76f8\u4fe1\u4e09\u5341\u62db\uff0c\u4f60\u53ef\u4ee5\u53d6\u5979\u6027\u547d\u3002\u201d \u3000\u3000\u201c\u9752\u51e4\u5462\uff1f\u662f\u4e0d\u662f\u53ef\u4ee5\u6536\u4e3a\u6211\u7528\uff1f\u2026\u2026\u201d \u3000\u3000\u5927\u548c\u5c1a\u95ee\u4e86\u51e0\u53e5\u4f7f\u6c5f\u67ab\u5f88\u96be\u56de\u7b54\u7684\u8bdd\u3002 \u3000\u3000\u201c\u73b0\u5728\uff0c\u8fd8\u4e0d\u6562\u80af\u5b9a\u3002\u4e0d\u8fc7\uff0c\u4e0b\u756a\u5de5\u592b\uff0c\u8981\u5979\u6295\u8fc7\u6765\uff0c\u4e5f\u4e0d\u662f\u592a\u96be\u7684\u4e8b\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a \u3000\u3000\u201c\u4f46\u6211\u7684\u770b\u6cd5\u662f\uff0c\u5979\u5982\u771f\u80af\u5e2e\u6211\u4eec\uff0c\u7559\u5728\u5929\u738b\u95e8\uff0c\u6bd4\u6295\u6548\u8fc7\u6765\uff0c\u7528\u5904\u5927\u7684\u591a\u4e86\u3002\u201d \u3000\u3000\u201c\u6211\u548c\u5c1a\u4e5f\u6709\u540c\u611f\uff0c\u653e\u5fc3\u53bb\u5427\uff01\u8fd9\u91cc\u6211\u4f1a\u5b89\u6392\u3002\u201d \u3000\u3000\u201c\u8fd8\u5f97\u8bf7\u4f60\u5e2e\u4e2a\u5fd9\u2026\u2026\u201d\u6c5f\u67ab\u7b11\u9053\uff1a\u201c\u60f3\u60f3\u770b\uff0c\u6211\u662f\u4e2a\u4ec0\u4e48\u51fa\u8eab\uff0c\u624d\u80fd\u8ba9\u5929\u9a6c\u603b\u5802\u4e2d\u4eba\u76f8\u4fe1\u6211\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4e2a\uff0c\u5f97\u4ed4\u7ec6\u63a8\u6572\u4e00\u756a\u4e86\uff0c\u4f60\u6b66\u529f\u9ad8\u5f3a\uff0c\u5df2\u662f\u4eba\u5c3d\u7686\u77e5\uff0c\u8981\u5177\u6709\u8bf4\u670d\u529b\u7684\u8eab\u4e16\uff0c\u624d\u80fd\u4f7f\u4eba\u76f8\u4fe1\u2026\u2026\u201d \u3000\u3000\u201c\u6162\u6162\u7684\u60f3\u5427\uff01\u2026\u2026\u201d\u6c5f\u67ab\u8bf4\uff0c\u201c\u60f3\u597d\u4e86\uff0c\u5c31\u5199\u51fa\u6765\uff0c\u6d3e\u4eba\u9001\u7ed9\u9093\u98de\uff0c\u7136\u540e\uff0c\u518d\u544a\u8bc9\u6211\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u6b64\u523b\u662f\u7d27\u6025\u65f6\u523b\uff0c\u4e0d\u8981\u7559\u8fde\u6e29\u67d4\uff0c\u4e94\u66f4\u4e4b\u524d\uff0c\u4e00\u5b9a\u8981\u56de\u6765\u3002\u201d \u3000\u3000\u6c5f\u67ab\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4fdd\u8bc1\u5728\u4e94\u66f4\u4e4b\u524d\uff0c\u2026\u2026\u201d\u6b63\u51c6\u5907\u63a8\u7a97\u800c\u51fa\uff0c\u7a81\u7136\u4e00\u9635\u4e52\u4e52\u4e53\u4e53\u4e4b\u58f0\uff0c\u4f20\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u8033\u9645\u95f4\u54cd\u8d77\u4e86\u6885\u82b1\u7684\u58f0\u97f3\uff0c\u9053\uff1a\u201c\u4ed6\u53ef\u4ee5\u6740\u6b7b\u6211\uff0c\u4f46\u6211\u4e00\u5b9a\u8981\u89c1\u4ed6\u3002\u201d \u3000\u3000\u6c5f\u67ab\u884c\u51fa\u5ba4\u5916\u3002 \u3000\u3000\u53ea\u89c1\u6885\u82b1\u548c\u6bb5\u4e5d\u52a8\u624b\uff0c\u53cc\u65b9\u867d\u7136\u672a\u4eae\u5175\u5203\uff0c\u4f46\u638c\u5288\u3001\u62f3\u51fb\uff0c\u5012\u4e5f\u6253\u7684\u5341\u5206\u6fc0\u70c8\u3002 \u3000\u3000\u6c5f\u67ab\u6c89\u58f0\u559d\u9053\uff1a\u201c\u4f4f\u624b\uff01\u201d \u3000\u3000\u6bb5\u4e5d\u6536\u638c\u800c\u9000\uff0c\u9053\uff1a\u201c\u526f\u603b\u7ba1\u51fa\u6765\u4e86\uff0c\u59d1\u5a18\u6709\u4e8b\uff0c\u81ea\u5df1\u5bf9\u4ed6\u8bf4\u5427\uff01\u201d \u3000\u3000\u6885\u82b1\u53cc\u76ee\u51dd\u6ce8\u7740\u6c5f\u67ab\uff0c\u9053\uff1a\u201c\u6211\u8981\u548c\u4f60\u5355\u72ec\u7684\u5bc6\u8c08\u4e00\u6b21\u3002\u201d \u3000\u3000\u65e2\u8981\u5355\u72ec\uff0c\u53c8\u662f\u5bc6\u8c08\uff0c\u90a3\u662f\u4e0d\u80fd\u8ba9\u522b\u4eba\u542c\u5230\u4e86\u3002 \u3000\u3000\u5927\u548c\u5c1a\u8bc6\u8da3\u7684\u7b11\u9053\uff1a\u201c\u597d\uff01\u6211\u66ff\u4e24\u4f4d\u628a\u98ce\uff0c\u4f60\u4eec\u597d\u597d\u8c08\u5427\uff01\u201d \u3000\u3000\u6c5f\u67ab\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8bf7\u5427\uff01\u201d\u628a\u6885\u82b1\u5411\u5c0f\u5385\u4e2d\u8ba9\u53bb\u3002 \u3000\u3000\u90a3\u77e5\u6885\u82b1\u4e00\u4f38\u624b\uff0c\u62c9\u4f4f\u4e86\u6c5f\u67ab\uff0c\u51b2\u5411\u6c5f\u67ab\u7684\u5367\u5ba4\u3002 \u3000\u3000\u6c5f\u67ab\u7684\u5367\u5ba4\uff0c\u5e03\u7f6e\u7684\u4e5f\u7b80\u5355\uff0c\u9664\u4e86\u4e00\u5f20\u5bbd\u5927\u4e00\u4e9b\u7684\u6728\u69bb\u5916\uff0c\u53ea\u6709\u4e00\u4e2a\u8863\u6a71\u3002 \u3000\u3000\u6885\u82b1\u62c9\u5f00\u8863\u6a71\uff0c\u5f88\u4ed4\u7ec6\u7684\u770b\u4e86\u6302\u6ee1\u7684\u8863\u670d\uff0c\u76ee\u5149\u624d\u8f6c\u5230\u6c5f\u67ab\u7684\u8138\u4e0a\uff0c\u9053\uff1a\u201c\u97e9\u9738\uff0c\u4f60\u662f\u4e0d\u662f\u6709\u70b9\u559c\u6b22\u6211\uff1f\u201d \u3000\u3000-- \u3000 \u7b2c\u5341\u4e8c\u7ae0\u3000\u4f17\u8c6a\u4fa0\u6697\u4f0f\u8fce\u6740\u624b \u3000 \u3000\u3000\u8bdd\u662f\u4e00\u53e5\u5145\u6ee1\u7740\u67d4\u60c5\u871c\u610f\u7684\u8bdd\uff0c\u53ea\u662f\u8bf4\u7684\u65f6\u673a\u4e0d\u5bf9\uff0c\u6c14\u6c1b\u4e0d\u5bf9\uff0c\u542c\u8d77\u6765\u5c31\u6709\u70b9\u7a81\u5140\u4e86\uff0c\u7f3a\u5c11\u4e86\u90a3\u79cd\u6b3e\u6b3e\u6df1\u60c5\uff0c\u64a9\u4eba\u5fc3\u6001\u7684\u9b45\u529b\u3002 \u3000\u3000\u201c\u8fd9\u4e2a\u2026\u2026\u201d\u6c5f\u67ab\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u4ee5\u6885\u82b1\u59d1\u5a18\u4e4b\u7f8e\uff0c\u82e5\u8bf4\u5728\u4e0b\u4e0d\u4f1a\u52a8\u5fc3\uff0c\u90a3\u662f\u6b3a\u4eba\u4e4b\u8c08\uff0c\u4e0d\u8fc7\uff0c\u8bf4\u97e9\u67d0\u4f1a\u4e3a\u7f8e\u8272\u75f4\u8ff7\uff0c\u90a3\u5c31\u662f\u5c0f\u770b\u6211\u97e9\u67d0\u4eba\u4e86\uff0c\u5728\u4e0b\u662f\u4e00\u4e2a\u9887\u6709\u81ea\u77e5\u3001\u80fd\u591f\u628a\u63e1\u5206\u5bf8\u7684\u4eba\uff0c\u6211\u4e86\u89e3\u81ea\u5df1\uff0c\u7edd\u4e0d\u662f\u90a3\u79cd\u80fd\u4f7f\u7f8e\u5973\u52a8\u60c5\u7684\u7537\u4eba\u3002\u201d \u3000\u3000\u6885\u82b1\u9053\uff1a\u201c\u4f46\u4f60\u6b63\u5728\u7a81\u663e\u81ea\u5df1\uff0c\u6563\u53d1\u51fa\u8000\u773c\u7684\u5149\u8292\u3002\u201d \u3000\u3000\u201c\u8fd9\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u4e00\u65f6\u4e4b\u95f4\uff0c\u60f3\u4e0d\u900f\u6885\u82b1\u7684\u7528\u5fc3\u3002 \u3000\u3000\u201c\u8fd9\u5c31\u4f7f\u6211\u5168\u5fc3\u5168\u610f\u7684\u53bb\u6ce8\u610f\u4f60\uff0c\u7ec8\u4e8e\u88ab\u6211\u53d1\u89c9\u4e86\uff1f\u4f60\u5c31\u662f\u90a3\u4e2a\u7537\u4eba\uff01\u2026\u2026\u201d\u6885\u82b1\u5fae\u5fae\u4e00\u7b11\uff0c\u4f46\u5374\u662f\u4e00\u4e2a\u51c4\u51c9\u7684\u5fae\u7b11\uff0c\u5145\u6ee1\u7740\u5e7d\u82e6\u65e0\u5948\u7684\u5fae\u7b11\u3002 \u3000\u3000\u6c5f\u67ab\u5fc3\u5934\u9707\u52a8\u4e86\u4e00\u4e0b\uff0c\u4f46\u5e76\u672a\u627f\u8ba4\uff0c\u6545\u4f5c\u832b\u7136\u7684\u9053\uff1a\u201c\u6211\u4e0d\u61c2\u4f60\u5728\u8bf4\u4e9b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u7530\u7389\u2026\u2026\u201d\u6885\u82b1\u9053\uff1a\u201c\u4f60\u5c31\u662f\u7530\u7389\uff0c\u4f60\u53ef\u4ee5\u63a9\u906e\u53bb\u4f60\u7684\u5f62\u8c8c\uff0c\u4f46\u5374\u65e0\u6cd5\u5b8c\u5168\u63a9\u53bb\u4f60\u90a3\u79cd\u773c\u795e\u2026\u2026\u201d \u3000\u3000\u201c\u6885\u82b1\u59d1\u5a18\uff0c\u8fd9\u8bf4\u6cd5\u592a\u7384\u4e86\u5427\uff1f\u2026\u2026\u201d\u6c5f\u67ab\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u4e00\u76f4\u5728\u9690\u85cf\u81ea\u5df1\uff0c\u53ea\u53ef\u60dc\u5f62\u52bf\u8feb\u4eba\uff0c\u903c\u5f97\u6211\u52a8\u624b\u81ea\u4fdd\u2026\u2026\u201d \u3000\u3000\u201c\u662f\u6740\u4eba\uff1f\u2026\u2026\u201d\u6885\u82b1\u51b7\u51b7\u63a5\u9053\uff1a\u201c\u4e0d\u662f\u81ea\u4fdd\uff0c\u6709\u4e9b\u4eba\u4f60\u53ef\u4ee5\u4e0d\u6740\u7684\uff0c\u4f46\u4f60\u8fd8\u662f\u6740\u4e86\u4ed6\u4eec\uff0c\u7a76\u7adf\u662f\u4e3a\u4e86\u4ec0\u4e48\uff1f\u4f60\u77e5\u9053\u5417\uff1f\u4f60\u97e9\u9738\u7684\u5a01\u540d\uff0c\u5df2\u5f88\u5feb\u4f20\u4e86\u51fa\u53bb\uff0c\u8fbe\u5230\u4e86\u5343\u91cc\u4e4b\u5916\u3002\u201d \u3000\u3000\u201c\u90a3\u597d\u554a\uff01\u6c5f\u6e56\u4e2d\u4eba\uff0c\u4e0d\u662f\u6c42\u4e00\u4e2a\u626c\u540d\u7acb\u4e07\u5417\uff1f\u540d\u4f20\u5343\u91cc\uff0c\u8be5\u662f\u4ef6\u5927\u5927\u7684\u8363\u8a89\u2026\u2026\u201d \u3000\u3000\u201c\u8fd9\u5df2\u7ecf\u4e0d\u662f\u90a3\u79cd\u7fa4\u96c4\u7eb7\u8d77\uff0c\u9010\u9e7f\u6b66\u6797\u7684\u65f6\u4ee3\uff0c\u4f60\u7684\u5f3a\u608d\uff0c\u4f1a\u62db\u6765\u65e0\u6570\u7684\u5f3a\u654c\u56f4\u6740\uff0c\u5c31\u6211\u6240\u77e5\uff0c\u4f60\u5df2\u88ab\u5217\u5165\u4e86\u5fc5\u6740\u7684\u5bf9\u8c61\uff0c\u6b63\u6709\u591a\u4f4d\u4e00\u6d41\u9ad8\u624b\uff0c\u8d76\u6765\u957f\u5b89\uff0c\u5feb\u5219\u660e\u5348\uff0c\u8fdf\u5219\u660e\u665a\uff0c\u4f60\u5c31\u4f1a\u9677\u5165\u4e86\u88ab\u56f4\u6740\u9669\u5883\uff0c\u6211\u4e0d\u662f\u5371\u76f2\u8038\u542c\uff0c\u97e9\u9738\u2026\u2026\u201d\u6885\u82b1\u5e7d\u5e7d\u8bf4\u9053\uff0c\u201c\u6211\u53ea\u662f\u8981\u6c42\u8bc1\u4e00\u4e0b\u5fc3\u4e2d\u7591\u60d1\uff1f\u6211\u4e24\u6b21\u53bb\u5ba2\u6808\u4e2d\u627e\u4f60\uff0c\u4f46\u4f60\u4e0d\u5728\uff0c\u6211\u6ca1\u6709\u60ca\u52a8\u5e97\u4e2d\u7684\u4eba\uff0c\u4e5f\u6ca1\u6709\u7559\u4e0b\u4efb\u4f55\u97f3\u8baf\u6d88\u606f\uff0c\u6842\u82b1\u544a\u8bc9\u6211\u4f60\u6765\u8fc7\uff0c\u4f46\u4f60\u5374\u5728\u4e2d\u4e86\u4e00\u6b65\u5012\u8ff7\u9999\u4e4b\u540e\uff0c\u7a81\u7136\u5931\u8e2a\u3002\u201d \u3000\u3000\u97e9\u9738\u8f7b\u8f7b\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6885\u82b1\uff0c\u4f60\u7a76\u7adf\u60f3\u5f97\u4ec0\u4e48\uff1f\u76ee\u7684\u4f55\u5728\uff1f\u201d \u3000\u3000\u201c\u6211\u8981\u8bc1\u5b9e\u4f60\u662f\u4e0d\u662f\u7530\u7389\u7684\u5316\u8eab\u2026\u2026\u201d\u6885\u82b1\u8bf4\uff0c\u201c\u90a3\u4e2a\u5bb3\u5f97\u6211\u9b42\u8426\u68a6\u7ed5\u7684\u7537\u4eba\uff0c\u6211\u51e0\u756a\u6df1\u601d\uff0c\u89c9\u7740\u662f\u4f60\uff0c\u6211\u8981\u8bc1\u5b9e\u81ea\u5df1\u7684\u60f3\u6cd5\uff1f\u201d \u3000\u3000\u201c\u8fd9\u6050\u6015\u5f88\u96be\u4e86\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u4f60\u8981\u5982\u4f55\u8bc1\u660e\u5462\uff1f\u201d \u3000\u3000\u201c\u6211\u548c\u4ed6\u5171\u6d74\u620f\u6c34\uff0c\u80af\u5b9a\u4ed6\u6ca1\u6709\u5e26\u9762\u5177\u2026\u2026\u201d\u6885\u82b1\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u6211\u8bf7\u6c42\u526f\u603b\u7ba1\u7ed9\u6211\u4e00\u4e2a\u673a\u4f1a\uff0c\u8ba9\u6211\u4ed4\u7ec6\u7684\u77a7\u77a7\u4f60\uff0c\u662f\u5426\u6234\u6709\u9762\u5177\uff1f\u201d \u3000\u3000\u5979\u53cc\u76ee\u76ef\u6ce8\u5728\u6c5f\u67ab\u7684\u8138\u4e0a\uff0c\u53c8\u9053\uff1a\u201c\u5236\u4f5c\u7684\u592a\u7cbe\u5de7\uff0c\u770b\u4e0d\u51fa\u4e00\u70b9\u7834\u7efd\u3002\u201d \u3000\u3000\u201c\u4ee5\u4f60\u7684\u76ee\u5149\u3001\u7ecf\u9a8c\uff0c\u5168\u795e\u6ce8\u89c6\u4e4b\u4e0b\uff0c\u8fd8\u77a7\u4e0d\u51fa\u7834\u7efd\uff0c\u5e94\u8be5\u4e0d\u4f1a\u5047\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u6240\u4ee5\uff0c\u6211\u8fd8\u6709\u4e00\u4e2a\u9a8c\u660e\u8bc1\u8eab\u7684\u529e\u6cd5\u3002\u201d\u6885\u82b1\u82e6\u6da9\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u867d\u7136\uff0c\u8fd9\u4ef6\u4e8b\u7f9e\u4e8e\u51fa\u53e3\uff0c\u4f46\u4e3a\u4e86\u6c42\u8bc1\u6211\u5fc3\u4e2d\u4e4b\u7591\uff0c\u4e5f\u53ea\u597d\u5192\u9669\u4e00\u8bd5\u4e86\uff01\u201d \u3000\u3000\u6c5f\u67ab\u5fc3\u4e2d\u5fd6\u9053\uff1a\u201c\u770b\u6765\uff0c\u6211\u6709\u4e0d\u5c11\u7684\u7834\u7efd\u843d\u5728\u4e86\u5979\u7684\u773c\u4e2d\uff0c\u6b64\u540e\u6709\u6240\u884c\u52a8\uff0c\u8981\u66f4\u52a0\u5f53\u5fc3\u624d\u662f\u3002\u201d \u3000\u3000\u201c\u8bf4\u8bf4\u770b\u5427\uff01\u80fd\u591f\u7b54\u5e94\u7684\uff0c\u6211\u5c3d\u91cf\u914d\u5408\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u4e0d\u4f1a\u8ba9\u4f60\u526f\u603b\u7ba1\u6709\u5403\u4e8f\u7684\u611f\u89c9\uff0c\u7b54\u5e94\u6211\u7684\u6c42\u8bc1\u6761\u4ef6\uff0c\u6211\u4f1a\u8865\u507f\u4f60\u3002\u201d \u3000\u3000\u201c\u8865\u507f\u6211\u2026\u2026\u201d\u6c5f\u67ab\u7b11\u9053\uff0c\u201d\u5982\u4f55\u8865\u507f\u5462\uff1f\u201d \u3000\u3000\u6885\u82b1\u51b7\u51b7\u5730\u8bf4\u9053\uff1a\u201c\u6211\u5bf9\u4f60\u6700\u5927\u7684\u5438\u5f15\u529b\uff0c\u5927\u6982\u662f\u6211\u8fd9\u4e00\u526f\u5dee\u5f3a\u4eba\u610f\u7684\u7f8e\u4e3d\u4e86\uff0c\u53ea\u8981\u526f\u603b\u7ba1\u7b54\u5e94\u8ba9\u6211\u67e5\u9a8c\u4e00\u4e0b\uff0c\u8bc1\u660e\u4f60\u4e0d\u662f\u7530\u7389\uff0c\u4f60\u5c31\u53ef\u4ee5\u5f97\u5230\u6211\u8fd9\u4e2a\u8eab\u4f53\u3002\u201d \u3000\u3000\u6c5f\u67ab\u76b1\u4e00\u4e0b\u7709\u5934\uff0c\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u662f\u4e0d\u662f\u592a\u8fc7\u513f\u620f\uff1f\u201d \u3000\u3000\u6885\u82b1\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6211\u4e00\u70b9\u4e5f\u4e0d\u559c\u6b22\u4f60\uff0c\u4f46\u6211\u7edd\u5bf9\u4e0d\u4f1a\u98df\u8a00\uff0c\u4f60\u53ef\u4ee5\u5148\u70b9\u6211\u4e24\u5904\u7a74\u9053\uff0c\u5f53\u7136\uff0c\u5982\u679c\u526f\u603b\u7ba1\u5e0c\u671b\u80fd\u4ee5\u522b\u7684\u7269\u54c1\uff0c\u6885\u82b1\u80fd\u591f\u4f5c\u5230\uff0c\u4e00\u5b9a\u4ece\u547d\u3002\u201d \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u4f60\u5148\u8bf4\u8bf4\u770b\uff0c\u8981\u5982\u4f55\u68c0\u67e5\u6211\uff1f\u201d \u3000\u3000\u201c\u8131\u4e0b\u4f60\u7684\u8863\u670d\uff0c\u2026\u2026\u201d\u6885\u82b1\u8bf4\uff0c\u201c\u4f60\u80cc\u4e0a\u6709\u4e00\u9897\u9ec4\u8c46\u5927\u5c0f\u7684\u6731\u7802\u75e3\u3002\u201d \u3000\u3000\u6c5f\u67ab\u5fc3\u4e2d\u4e00\u52a8\uff0c\u6697\u9053\uff1a\u201c\u56db\u59d1\u4e5f\u544a\u8bc9\u8fc7\u6211\uff0c\u90a3\u4e00\u70b9\u5c0f\u75e3\uff0c\u7ea2\u8273\u6b32\u6ef4\uff0c\u975e\u5e38\u9c9c\u7f8e\uff0c\u6211\u81ea\u5df1\u867d\u7136\u6ca1\u6709\u770b\u5230\u8fc7\uff0c\u4f46\u60f3\u6765\u662f\u4e0d\u4f1a\u9519\u4e86\u3002\u201d \u3000\u3000\u60f3\u5230\u6885\u82b1\u5e8a\u7b2c\u95f4\u7684\u6e29\u5a49\u5a07\u67d4\uff0c\u4e0d\u7981\u6697\u6697\u4e00\u53f9\uff0c\u9053\uff1a\u201c\u6885\u82b1\uff0c\u53ea\u4e3a\u4e86\u8bc1\u5b9e\u4e00\u4e0b\u6211\u7684\u8eab\u4efd\uff0c\u4f60\u4e0d\u89c9\u7740\u4ed8\u7684\u4ee3\u4ef7\u592a\u5927\u4e86\u5417\uff1f \u3000\u3000\u5982\u679c\u6211\u662f\u7530\u7389\uff0c\u6211\u4f1a\u8f7b\u89c6\u4f60\uff0c\u5982\u679c\u6211\u4e0d\u662f\u7530\u7389\uff0c\u90a3\u5c31\u66f4\u53ef\u60b2\u4e86\uff0c\u4f60\u8981\u966a\u4e00\u4e2a\u81ea\u5df1\u7edd\u4e0d\u559c\u6b22\u7684\u4eba\u4e0a\u5e8a\uff0c\u5fc3\u4e2d\u5fc5\u7136\u4f1a\u7559\u4e0b\u4e86\u5f88\u6df1\u7684\u521b\u75db\uff1f\u201d \u3000\u3000\u201c\u4e0d\u662f\u521b\u75db\uff0c\u800c\u662f\u4e00\u6761\u4eba\u547d\u2026\u2026\u201d\u6885\u82b1\u9053\uff1a\u201c\u6211\u6ca1\u6709\u5bf9\u4ed6\u4f5c\u8fc7\u4efb\u4f55\u627f\u8bfa\uff0c\u4f46\u6211\u81ea\u5df1\u6697\u4e0b\u51b3\u5fc3\uff0c\u9664\u4ed6\u4e4b\u5916\uff0c\u7edd\u4e0d\u4f1a\u518d\u522b\u7684\u7537\u4eba\u78b0\u6211\u7684\u8eab\u4f53\u3002\u201d \u3000\u3000\u201c\u80e1\u8bf4\u2026\u2026\u201d\u97e9\u9738\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u6d1b\u9633\u5c45\u4e2d\u56db\u5b63\u82b1\u9b41\uff0c\u5982\u82e5\u60c5\u6709\u6240\u949f\uff0c\u4ece\u4e00\u800c\u7ec8\uff0c\u8fd9\u4e2a\u751f\u610f\uff0c\u90a3\u91cc\u8fd8\u80fd\u4f5c\u7684\u4e0b\u53bb\uff0c\u4f60\u6700\u8fd1\u5f88\u5c11\u63a5\u5f85\u9152\u5ba2\uff0c\u5df2\u7ecf\u4f7f\u5f88\u591a\u5e55\u540d\u800c\u6765\u7684\u4eba\uff0c\u8d25\u5174\u800c\u53bb\u3002\u201d \u3000\u3000\u201c\u597d\u4e86\uff0c\u97e9\u526f\u603b\u7ba1\uff0c\u4f60\u4eec\u5929\u9a6c\u5802\u73b0\u5728\u53ea\u7ba1\u8d4c\u573a\uff0c\u9152\u9986\u80fd\u8d5a\u591a\u5c11\u94b1\uff1f\u548c\u4f60\u4eec\u5b8c\u5168\u65e0\u5173\u4e86\u2026\u2026\u201d\u6885\u82b1\u9053\uff1a\u201c\u6d1b\u9633\u697c\u7531\u9752\u51e4\u4e3b\u6301\uff0c\u4f60\u4eec\u4e0d\u6562\u8fc7\u95ee\uff0c\u6211\u4e5f\u5e0c\u671b\uff0c\u4f60\u4eec\u522b\u518d\u4fb5\u5bb3\u5230\u996d\u5e84\u7684\u6536\u5165\uff0c\u97e9\u9738\uff0c\u4f60\u5df2\u7ecf\u7ed3\u4e0b\u4e86\u592a\u591a\u7684\u4ec7\u6068\uff0c\u5e0c\u671b\u4f60\u80fd\u591f\u7a0d\u4f5c\u6536\u655b\u4e00\u4e9b\uff0c\u81f3\u4e8e\u6211\uff0c\u6211\u4f1a\u7ed9\u7530\u7389\u4e00\u4e2a\u4ea4\u4ee3\u7684\uff0c\u4f60\u5982\u613f\u610f\u8d4c\u4e00\u4e0b\uff0c\u5f88\u53ef\u80fd\u5c31\u662f\u6211\u8fd9\u4e00\u751f\u4e2d\u6700\u540e\u7684\u4e00\u4e2a\u7537\u4eba\u3002\u201d \u3000\u3000\u6c5f\u67ab\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\uff0c\u6211\u4e0d\u80fd\u80cc\u5f03\u4e86\u81ea\u5df1\u8bb8\u4e0b\u7684\u8a93\u8a00\uff0c\u6211\u4e0d\u80fd\u8ba9\u7530\u7389\u611f\u89c9\u5230\u7a9d\u56ca\uff1b\u59d4\u5c48\uff0c\u6240\u4ee5\uff0c\u6211\u8fd8\u4ed6\u4e00\u6761\u547d\uff01\u201d \u3000\u3000\u201c\u4f60\u662f\u8bf4\uff0c\u4f60\u4f1a\u4e3a\u7530\u7389\u6b89\u60c5\u800c\u6b7b\u2026\u2026\u201d\u6c5f\u67ab\u5fc3\u4e2d\u5927\u5927\u7684\u9707\u52a8\u4e86\uff0c\u8fd9\u4e9b\u5185\u5bab\u4e00\u7cfb\u4e2d\u8bad\u7ec3\u51fa\u6765\u7684\u6740\u624b\uff0c\u90fd\u662f\u5343\u4e2d\u9009\u4e00\u7684\u7f8e\u5973\uff0c\u5979\u4eec\u4ee5\u7f8e\u8272\u8bf1\u4eba\uff0c\u53ea\u95ee\u76ee\u7684\uff0c\u4e0d\u62e9\u624b\u6bb5\uff0c\u600e\u4f1a\u751f\u51fa\u4e3a\u60c5\u6b89\u8eab\u7684\u5ff5\u5934\uff0c\u96be\u9053\u2018\u60d1\u5fc3\u672f\u2019\u771f\u80fd\u591f\u8ba9\u4e00\u4e2a\u4eba\u6b7b\u5fc3\u584c\u5730\u7684\u6c38\u76f8\u8ffd\u968f\uff0c\u4e0d\u518d\u6539\u53d8\u4e48\uff1f\u201d \u3000\u3000\u60f3\u5230\u6b64\u5904\uff0c\u6c5f\u67ab\u7a81\u7136\u60ca\u51fa\u4e86\u4e00\u8eab\u51b7\u6c57\uff0c\u6697\u9053\uff1a\u201c\u5f20\u56db\u59d1\u5973\u4e2d\u8c6a\u6770\uff0c\u8f7b\u6de1\u5929\u4e0b\u7537\u4eba\uff0c\u548c\u6211\u63a5\u89e6\u65f6\uff0c\u72b9\u662f\u5904\u5b50\u4e4b\u8eab\uff0c\u90a3\u662f\u8bf4\u660e\u4e86\uff0c\u5979\u5df2\u4e0b\u5b9a\u4e86\u7ec8\u8eab\u4e0d\u5ac1\u7684\u51b3\u5fc3\uff0c\u4f46\u5374\u88ab\u6211\u65bd\u672f\uff0c\u7834\u53bb\u5979\u6570\u5341\u5e74\u7684\u575a\u8d1e\u5fd7\u8282\u3002 \u3000\u3000\u4e24\u4e2a\u5c0f\u72d0\u5973\uff0c\u72b9\u5728\u5c11\u5973\u6000\u6625\u7684\u5e74\u9f84\uff0c\u4f46\u5374\u4e0d\u6c42\u751f\u65f6\u76f8\u4f9d\u3001\u6c38\u4e0d\u5206\u79bb\uff0c\u7adf\u5148\u6c42\u6b7b\u540e\u540c\u846c\u4e00\u7a74\u3002 \u3000\u3000\u9752\u51e4\u53ea\u6c42\u4e00\u4e2a\u59bb\u5b50\u540d\u4efd\uff0c\u4f3c\u662f\u5fc3\u613f\u5df2\u8db3\uff0c\u6885\u82b1\u672c\u662f\u5e7f\u5e03\u8272\u8eab\u7684\u6740\u624b\uff0c\u7adf\u4e5f\u4f1a\u751f\u51fa\u4e86\u4e00\u8eab\u6b89\u60c5\u4e4b\u5fc3\uff01\u201d \u3000\u3000\u5929\u554a\uff01\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\u5462\uff1f\u662f\u5929\u72d0\u6280\u827a\u7684\u9b45\u529b\uff0c\u8fd8\u662f\u5947\u672f\u771f\u80fd\u60d1\u5fc3\uff0c\u4f7f\u5979\u4eec\u8ff7\u5931\u4e86\u81ea\u6211\uff0c\u2026\u2026\u3002 \u3000\u3000\u6885\u82b1\u6c89\u601d\u4e86\u826f\u4e45\uff0c\u7a81\u7136\u8bf4\u9053\uff1a\u201c\u544a\u8bc9\u4f60\uff0c\u4f60\u4e5f\u4e0d\u4f1a\u660e\u767d\uff0c\u97e9\u9738\uff0c\u4f60\u786e\u6709\u60ca\u4eba\u7684\u6b66\u529f\uff0c\u6211\u4eec\u4e5f\u975e\u5e38\u4f69\u670d\u4f60\u628a\u81ea\u5df1\u9690\u85cf\u7684\u5f88\u597d\uff0c\u4f60\u53ef\u80fd\u662f\u5929\u9a6c\u603b\u5802\u4e2d\u5168\u529b\u57f9\u517b\u51fa\u6765\u7684\u4e00\u4e2a\u4eba\u624d\uff0c\u628a\u4f60\u5b89\u6392\u5728\u6d1b\u9633\u5c45\u4e2d\u3002 \u3000\u3000\u4e0d\u8bba\u4f60\u662f\u4f55\u7b49\u7684\u82f1\u96c4\u597d\u6c49\uff1f\u5982\u4f55\u51fa\u7c7b\u62d4\u8403\u7684\u6b66\u6797\u9ad8\u624b\uff1f\u4f46\u4f60\u53ea\u662f\u4e00\u4e2a\u6b66\u592b\uff0c\u4e00\u4e2a\u6c5f\u6e56\u8c6a\u5ba2\uff0c\u4f60\u4e0d\u4f1a\u61c2\u90a3\u79cd\u4e24\u60c5\u76f8\u4f9d\uff0c\u76f8\u601d\u523b\u9aa8\u7684\u60c5\u6000\uff0c\u751f\u6b7b\u7b49\u95f2\u4e8b\uff0c\u4e24\u60c5\u4f55\u5fcd\u5206\u7684\u7edd\u4fd7\u610f\u5883\uff0c\u90a3\u662f\u8d85\u8131\u4eba\u95f4\u751f\u6b7b\u7684\u60c5\u64cd\uff0c\u4e00\u4e2a\u53ea\u77e5\u64cd\u5200\u6740\u4eba\u7684\u6b66\u592b\uff0c\u90a3\u91cc\u80fd\u591f\u660e\u767d\uff1f\u9664\u975e\uff0c\u4f60\u5c31\u662f\u7530\u7389\u7684\u5316\u8eab\u3002\u201d \u3000\u3000\u6c5f\u67ab\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6885\u82b1\u59d1\u5a18\uff0c\u5982\u82e5\u6211\u662f\u7530\u7389\uff0c\u5c82\u4e0d\u662f\u6210\u4e86\u4e00\u4e2a\u53ef\u6015\u3001\u53ef\u618e\u7684\u9a97\u5b50\uff0c\u9a97\u4e86\u4f60\u7684\u4eba\u2026\u2026\u201d \u3000\u3000\u201c\u4e5f\u5077\u8d77\u4e86\u6211\u7684\u5fc3\u2026\u2026\u201d\u6885\u82b1\u82e6\u7b11\u4e00\u4e0b\u53c8\u9053\uff1a\u201c\u6211\u597d\u60f3\u4f60\u662f\u7530\u7389\uff0c\u56e0\u4e3a\u6211\u6e34\u671b\u89c1\u5230\u4ed6\uff0c\u4e5f\u5e0c\u671b\u4f60\u4e0d\u662f\u7530\u7389\uff0c\u4e5f\u597d\u653e\u624b\u65bd\u5c55\uff0c\u548c\u4f60\u4e00\u51b3\u80dc\u8d1f\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u6885\u82b1\u59d1\u5a18\u5bf9\u6211\u97e9\u67d0\u4eba\uff0c\u4e00\u76f4\u662f\u624b\u4e0b\u7559\u60c5\u4e86\uff1f\u201d \u3000\u3000\u201c\u4e0d\u7ba1\u4f60\u662f\u5426\u76f8\u4fe1\uff1f\u90a3\u90fd\u65e0\u5173\u91cd\u8981\uff0c\u6211\u6c42\u7684\uff0c\u53ea\u662f\u6211\u81ea\u5df1\u4e00\u4e2a\u5fc3\u5b89\u2026\u2026\u201d\u6885\u82b1\u95ed\u4e0a\u53cc\u76ee\uff0c\u9ad8\u4e3e\u53cc\u624b\uff0c\u9053\uff1a\u201c\u4f60\u53ef\u4ee5\u51fa\u624b\u4e86\uff0c\u70b9\u4e2d\u6211\u4e24\u5904\u7a74\u9053\uff0c\u7136\u540e\uff0c\u6211\u4eec\u5c31\u5f00\u59cb\u8fd9\u573a\u8d4c\u7ea6\u2026\u2026\u201d \u3000\u3000\u201c\u518d\u8bf4\u6e05\u695a\u4e00\u70b9\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u6211\u4e0d\u662f\u4f60\u60f3\u7684\u7530\u7389\u5462\uff1f\u201d \u3000\u3000\u201c\u5c31\u5728\u8fd9\u91cc\uff0c\u4f60\u7acb\u523b\u53ef\u4ee5\u5f97\u5230\u6211\uff0c\u6211\u4f1a\u5c3d\u529b\u627f\u6b22\uff0c\u8ba9\u4f60\u5c1d\u4e00\u4e0b\u4e16\u95f4\u6700\u597d\u7684\u98ce\u6d41\u6ecb\u5473\uff0c\u4ece\u6b64\u6885\u82b1\u51cb\u8c22\uff0c\u4e16\u754c\u4e0a\u518d\u4e5f\u6ca1\u6709\u6211\u8fd9\u4e2a\u4eba\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u81ea\u7edd\u800c\u6b7b\u2026\u2026\u201d\u8ba7\u67ab\u9053\uff1a\u201c\u4f55\u82e6\u5462\uff1f\u201d \u3000\u3000\u201c\u6211\u518d\u8ba9\u7537\u4eba\u78b0\u4e86\u6211\uff0c\u5b9e\u5728\u6709\u4e9b\u6127\u5bf9\u60c5\u90ce\uff0c\u4ee5\u6b7b\u8c22\u7f6a\uff0c\u6211\u6b7b\u7684\u5fc3\u5b89\u7406\u5f97\u2026\u2026\u201d \u3000\u3000\u201c\u53ea\u6015\u7530\u7389\u90a3\u4e2a\u5c0f\u5b50\uff0c\u4e0d\u4f1a\u6709\u8fd9\u79cd\u60f3\u6cd5\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u5982\u679c\u4ed6\u771f\u7684\u559c\u6b22\u4f60\uff0c\u4e00\u5b9a\u4f1a\u518d\u6765\u770b\u4f60\uff0c\u5982\u662f\u4ed6\u4e00\u53bb\u4e0d\u56de\uff0c\u4f60\u6b7b\u7684\u6709\u4f55\u4ef7\u503c\uff1f\u201d \u3000\u3000\u201c\u97e9\u9738\uff0c\u6211\u4eec\u4e0d\u8c08\u8fd9\u4ef6\u4e8b\u4e86\u2026\u2026\u201d\u6885\u82b1\u9053\uff1a\u201c\u6211\u8bf4\u7684\u4f60\u4e0d\u61c2\uff0c\u4f60\u8bf4\u7684\u6211\u542c\u4e0d\u8fdb\u53bb\uff0c\u5982\u662f\u4f60\u4e00\u70b9\u4e5f\u4e0d\u559c\u6b22\u6211\u8fd9\u4e2a\u4eba\uff0c\u53ef\u4ee5\u6362\u4e2a\u4eba\uff0c\u56db\u5b63\u82b1\u5a62\uff0c\u4efb\u4f60\u6311\u9009\u4e00\u4e2a\uff0c\u6211\u62c5\u4fdd\u80fd\u4f7f\u5979\u4eec\u81ea\u8350\u6795\u5e2d\uff0c\u5f53\u7136\uff0c\u4f60\u5982\u679c\u7ec3\u4e86\u4e0d\u80fd\u7834\u8eab\u7684\u6b66\u529f\uff0c\u90a3\u5c31\u8c08\u8c08\u522b\u7684\u6761\u4ef6\uff0c\u8981\u94f6\u5b50\uff0c\u8981\u73e0\u5b9d\uff0c\u6211\u90fd\u613f\u5c3d\u6211\u7684\u80fd\u529b\u51d1\u51fa\u6765\uff0c\u4f55\u51b5\uff0c\u6211\u8981\u6c42\u7684\u4e8b\u60c5\uff0c\u662f\u90a3\u4e48\u7b80\u5355\uff0c\u5bf9\u4f60\uff0c\u4e0d\u4f1a\u6709\u4efb\u4f55\u4f24\u5bb3\u2026\u2026\u201d \u3000\u3000\u201c\u4f24\u5bb3\u5927\u4e86\u2026\u2026\u201d\u6c5f\u67ab\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u4e0d\u80fd\u80af\u5b9a\u6211\u662f\u4ec0\u4e48\u4eba\uff1f\u5c31\u4e0d\u5fcd\u5fc3\u5bf9\u6211\u4e0b\u624b\uff0c\u51b3\u5b9a\u4e86\u6211\u4e0d\u662f\u7530\u7389\u5316\u8eab\uff0c\u4f60\u5c31\u8981\u653e\u548c\u6211\u62fc\u547d\uff0c\u8fd9\u4ef6\u4e8b\uff0c\u662f\u4e0d\u662f\u5f88\u4e25\u91cd\u5462\uff1f\u201d \u3000\u3000\u201c\u5c31\u7b97\u6211\u6c42\u4f60\u5427\uff01\u2026\u2026\u201d\u6885\u82b1\u771f\u7684\u6025\u4e86\uff0c\u773c\u6cea\u4e5f\u593a\u7736\u800c\u51fa\uff0c\u9053\uff1a\u201c\u5c31\u7b97\u8bc1\u660e\u4e86\u4f60\u4e0d\u662f\u7530\u7389\uff0c\u6211\u4e5f\u6ca1\u6709\u529e\u6cd5\u8bf4\u51fa\u53bb\uff0c\u4f60\u53ef\u4ee5\u7acb\u523b\u6740\u4e86\u6211\u3002\u201d \u3000\u3000\u201c\u5982\u679c\u662f\u5f88\u4e0d\u5e78\uff0c\u6211\u662f\u7530\u7389\u5462\uff1f\u201d \u3000\u3000\u6885\u82b1\u7728\u52a8\u4e86\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u4f60\u662f\u7530\u7389\uff1f\u201d \u3000\u3000\u201c\u6211\u662f\u8bf4\uff0c\u5047\u5982\u6211\u662f\u7530\u7389\u5462\uff1f\u59d1\u5a18\u662f\u4e0d\u662f\u8981\u6740\u4e86\u4ed6\uff1f\u201d \u3000\u3000\u201c\u4e0d\u4f1a\uff0c\u4ed6\u6c42\u8fc7\u6211\u79bb\u5f00\u6b64\u5730\uff0c\u548c\u4ed6\u5171\u8d74\u6c5f\u5357\uff0c\u5e38\u4f4f\u5728\u897f\u6e56\u4e4b\u7554\uff0c\u90a3\u662f\u4f55\u7b49\u5f85\u60c5\u753b\u610f\u7684\u751f\u6d3b\uff0c\u53ef\u662f\u6211\u62d2\u7edd\u4e86\u4ed6\u2026\u2026\u201d\u6885\u82b1\u9053\uff0c\u201c\u51fa\u624b\u5427\uff01\u97e9\u9738\uff0c\u6211\u5c3d\u5feb\u8bc1\u660e\u4e86\u8fd9\u4ef6\u4e8b\u60c5\u3002\u201d \u3000\u3000\u201c\u4e0d\u7528\u8bc1\u660e\u4e86\uff0c\u6211\u5c31\u662f\u7530\u7389\u2026\u2026\u201d\u4e00\u4e2a\u8f6c\u8eab\uff0c\u53d6\u4e0b\u4e86\u7cbe\u5de7\u7684\u9762\u5177\uff0c\u6062\u590d\u4e86\u672c\u6765\u9762\u76ee\u3002 \u3000\u3000\u6885\u82b1\u53cc\u76ee\u51dd\u6ce8\u5728\u6c5f\u67ab\u7684\u8138\u4e0a\u77a7\u4e86\u826f\u4e45\uff0c\u9053\uff1a\u201c\u679c\u7136\u662f\u4f60\uff0c\u4f60\u597d\u574f\uff0c\u597d\u6076\u6bd2\uff0c\u628a\u6211\u6574\u5f97\u597d\u60b2\u60e8\u3002\u201d \u3000\u3000\u201c\u6885\u82b1\u2026\u2026\u201d\u6c5f\u67ab\u6b63\u5bb9\u8bf4\u9053\uff1a\u201c\u6211\u6ca1\u6709\u4f24\u5bb3\u4f60\uff0c\u53cd\u800c\u5c3d\u53ef\u80fd\u7684\u624b\u4e0b\u7559\u60c5\uff0c\u4f60\u5e94\u8be5\u660e\u767d\uff0c\u6211\u6709\u7740\u6740\u6b7b\u4f60\u7684\u672c\u9886\uff0c\u4e5f\u6709\u5f88\u591a\u7684\u673a\u4f1a\uff0c\u4f46\u6211\u6ca1\u6709\u4e0b\u624b\uff01\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u4e0d\u4e0b\u624b\u5462\uff1f\u662f\u72e0\u4e0d\u4e0b\u5fc3\u80a0\uff0c\u8fd8\u662f\u65e7\u60c5\u96be\u5fd8\uff1f\u2026\u2026\u201d\u6885\u82b1\u9053\uff1a\u201c\u6b7b\u5728\u4f60\u624b\u91cc\uff0c\u81f3\u5c11\u6bd4\u522b\u4eba\u6740\u4e86\u6211\u597d\u8fc7\u4e00\u4e9b\uff0c\u7530\u7389\uff0c\u4f60\u63a5\u8fd1\u6211\uff0c\u9a97\u53bb\u4e86\u6211\u7684\u611f\u60c5\uff0c\u7528\u5fc3\u4f55\u5728\uff1f\u73b0\u5728\uff0c\u5e94\u8be5\u8bf4\u4e2a\u660e\u767d\u4e86\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u597d\u5427\uff01\u8bf4\u6e05\u695a\uff0c\u6211\u4eec\u90fd\u53ef\u4ee5\u4f5c\u4e2a\u6289\u62e9\uff0c\u6211\u786e\u4f1a\u4e3a\u4f60\u7684\u7f8e\u8272\u6240\u52a8\uff0c\u6211\u4e5f\u4e0d\u662f\u4e00\u4e2a\u5341\u5206\u4e13\u60c5\u7684\u4eba\uff0c\u6211\u4e0d\u662f\u5929\u9a6c\u5802\u57f9\u80b2\u7684\u4eba\uff0c\u53ea\u80fd\u8bf4\uff0c\u6211\u5728\u914d\u5408\u5929\u9a6c\u5802\u4e2d\u4eba\u7684\u884c\u52a8\uff0c\u4e5f\u8bb8\u6709\u4e00\u5929\uff0c\u4ed6\u4eec\u4f1a\u5168\u529b\u6740\u6211\u3002\u201d \u3000\u3000\u201c\u90a3\u4f60\u7a76\u7adf\u662f\u4ec0\u4e48\u4eba\u5462\uff1f\u4ee3\u8868\u4e86\u90a3\u4e2a\u95e8\u6d3e\uff1f\u7528\u5fc3\u4f55\u5728\uff1f\u2026\u2026\u201d \u3000\u3000\u6885\u82b1\u9053\uff1a\u201c\u80fd\u4e0d\u80fd\u548c\u5185\u5bab\u4e2d\u4eba\u5408\u4f5c\uff0c\u6211\u4f1a\u5168\u529b\u4e3a\u4f60\u4f5c\u4fdd\uff01\u201d \u3000\u3000\u201c\u4e0d\u53ef\u80fd\u7684\uff0c\u6885\u82b1\uff0c\u5929\u9a6c\u5802\u6ca1\u6709\u529e\u6cd5\u6536\u7f57\u5230\u6211\uff0c\u5185\u5bab\u4e00\u7cfb\u4e2d\u81ea\u7136\u4e5f\u4e0d\u53ef\u80fd\uff0c\u6885\u82b1\uff0c\u90a3\u665a\u4e0a\u7528\u4e00\u6b65\u5012\u8ff7\u5012\u6211\u7684\u4eba\uff0c\u662f\u4e0d\u662f\u5929\u9a6c\u5802\u4e2d\u4eba\uff1f\u201d \u3000\u3000\u6885\u82b1\u5341\u5206\u4e3a\u96be\u7684\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u7ec8\u4e8e\u8fd8\u662f\u70b9\u70b9\u5934\uff0c\u7f13\u7f13\u5411\u6c5f\u67ab\u884c\u53bb\uff0c\u7f13\u7f13\u504e\u5165\u4e86\u6c5f\u67ab\u7684\u6000\u4e2d\uff0c\u9053\uff0c\u201d\u6211\u5fc3\u76ee\u4e2d\u7684\u7530\u7389\uff0c\u4e00\u76f4\u662f\u4e2a\u5f31\u5c0f\u754f\u602f\u7684\u7537\u4eba\uff0c\u60f3\u4e0d\u5230\u4f60\u662f\u5982\u6b64\u7684\u5f3a\u5927\uff0c\u6211\u73b0\u5728\uff0c\u771f\u9700\u8981\u4f60\u7684\u4fdd\u62a4\uff01\u201d \u3000\u3000\u201c\u53ef\u4ee5\uff0c\u6885\u82b1\uff0c\u53ea\u8981\u4f60\u613f\u610f\uff0c\u6211\u4f1a\u5c3d\u6211\u80fd\u529b\u4fdd\u62a4\u4f60\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u4f60\u8981\u6211\u80cc\u53db\u5185\u5bab\u662f\u5417\uff1f\u5979\u4eec\u517b\u6211\u957f\u5927\uff0c\u4f20\u6211\u6b66\u529f\uff0c\u6211\u2026\u2026\u600e\u80fd\u80cc\u53db\u5979\u4eec\uff1f\u201d \u3000\u3000\u201c\u6885\u82b1\uff0c\u4f60\u4e0d\u662f\u80cc\u53db\u4ed6\u4eec\uff0c\u53ea\u662f\u9009\u62e9\u4e86\u4e00\u4e2a\u66f4\u6709\u610f\u4e49\u7684\u4f7f\u547d\u2026\u2026\u201d\u6c5f\u67ab\u957f\u957f\u5401\u4e00\u53e3\u6c14\u53c8\u9053\uff1a\u201c\u6574\u4e2a\u6b66\u6797\uff0c\u90fd\u5728\u5979\u4eec\u7684\u538b\u5236\u4e0b\uff0c\u53d8\u6210\u4e86\u4e00\u7247\u6b7b\u5bc2\uff0c\u518d\u4e5f\u770b\u4e0d\u5230\u5145\u6ee1\u8c6a\u60c5\u7684\u4fa0\u58eb\u4eec\uff0c\u4f69\u5200\u98de\u9a6c\uff0c\u5954\u8d70\u5728\u6c5f\u6e56\u4e0a\uff0c\u518d\u4e5f\u542c\u4e0d\u5230\u8c6a\u58ee\u60b2\u6b4c\uff0c\u4ef0\u5929\u957f\u5578\u7684\u82f1\u96c4\u6c14\u6982\uff0c\u518d\u8bf4\u5929\u9a6c\u5802\u5e2e\u4ed6\u4eec\u6253\u4e0b\u4e86\u5929\u4e0b\uff0c\u5982\u4eca\u98de\u9e1f\u5c1a\u6ca1\u6709\u5168\u5c3d\uff0c\u4ed6\u4eec\u5df2\u5f00\u59cb\u6536\u62fe\u5929\u9a6c\u5802\u4e86\uff0c\u81f3\u4e8e\u4f60\u4eec\uff0c\u6211\u76f8\u4fe1\uff0c\u90fd\u662f\u5927\u6c49\u7684\u5b50\u6c11\uff0c\u4f46\u4f60\u4eec\u9664\u4e86\u77e5\u6388\u4e1a\u7684\u6069\u5e08\u4e4b\u5916\uff0c\u53ef\u77e5\u9053\u8eab\u4e16\u6765\u5386\uff0c\u4f60\u4eec\u662f\u6709\u7236\u6bcd\u7684\uff0c\u4f46\u4f60\u4eec\u89c1\u8fc7\u5417\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\u2026\u2026\u201d\u6885\u82b1\u8bf4\uff0c\u201c\u5e08\u7236\u8bf4\uff0c\u6211\u4eec\u90fd\u662f\u5b64\u513f\u2026\u2026\u201d \u3000\u3000\u201c\u8fd9\u5e94\u8be5\u662f\u6700\u5351\u9119\u7684\u8c0e\u8a00\u4e86\uff0c\u4f60\u4eec\u90fd\u662f\u5b64\u513f\uff0c\u90fd\u957f\u7684\u90a3\u4e48\u7f8e\u4e3d\uff0c\u8fd9\u600e\u4e48\u53ef\u80fd\u5462\uff1f\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u795e\u60c5\u8083\u7136\u5730\u8bf4\uff0c\u201c\u6885\u82b1\uff0c\u4f60\u806a\u660e\u7edd\u4e16\uff0c\u5e94\u8be5\u65e9\u60f3\u5230\u7684\uff0c\u8fd9\u4e00\u79cd\u6b3a\u9a97\u2026\u2026\u201d \u3000\u3000\u201c\u4f60\u662f\u8bf4\uff0c\u4ed6\u4eec\u6740\u4e86\u6211\u4eec\u7684\u7236\u6bcd\u2026\u2026\u201d \u3000\u3000\u6885\u82b1\u8138\u8272\u60e8\u53d8\uff0c\u4eba\u5374\u66f4\u7d27\u7684\u5047\u5728\u6c5f\u67ab\u6000\u4e2d\u3002 \u3000\u3000\u201c\u6211\u6ca1\u6709\u67e5\u8bc1\u8fc7\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u6240\u4ee5\uff0c\u6211\u4e0d\u6562\u7ed9\u4f60\u65a9\u9489\u622a\u94c1\u7684\u56de\u7b54\uff0c\u4e5f\u8bb8\u4f60\u4eec\u662f\u88ab\u5077\u6765\u7684\uff0c\u5f53\u7136\uff0c\u6740\u4eba\u706d\u53e3\uff0c\u90a3\u662f\u6700\u5b89\u5168\u7684\u529e\u6cd5\u4e86\uff01\u201d \u3000\u3000\u201c\u4f60\u8bf4\u7684\u5bf9\uff01\u6211\u4e5f\u65e9\u6000\u7591\u8fc7\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u2026\u2026\u201d\u6885\u82b1\u9053\uff1a\u201c\u4f46\u6211\u4eec\u6240\u53d7\u7684\u8bad\u7ec3\u592a\u4e25\u9177\u4e86\uff0c\u51e0\u4e4e\u662f\u6709\u4e9b\u4f7f\u7236\u6bcd\u8499\u7f9e\uff0c\u6240\u4ee5\uff0c\u6211\u4eec\u4e0d\u60f3\u95ee\u8d77\u7236\u6bcd\u5bb6\u4eba\uff0c\u53ea\u628a\u601d\u5ff5\u4e4b\u60c5\u6446\u5728\u5fc3\u4e2d\uff0c\u5f53\u7136\uff0c\u5fc3\u4e2d\u4e5f\u662f\u6709\u4e9b\u5bb3\u6015\uff0c\u4e0d\u6562\u53bb\u95ee\uff1f\u5c31\u8fd9\u6837\u5728\u90a3\u79cd\u4e25\u9177\u8bad\u7ec3\u4e2d\u957f\u5927\u4e86\u3002\u201d \u3000\u3000\u6c5f\u67ab\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u6211\u5f88\u9ad8\u5174\uff0c\u4f60\u65e9\u6709\u8fd9\u79cd\u6000\u7591\u4e86\uff0c\u90a3\u8bc1\u660e\u6211\u662f\u8a00\u51fa\u8877\u8bda\uff0c\u4e0d\u8fc7\uff0c\u6709\u4e00\u70b9\uff0c\u6211\u60f3\u4e0d\u660e\u767d\uff0c\u4f60\u80af\u4e0d\u80af\u544a\u8bc9\u6211\uff1f\u201d \u3000\u3000\u201c\u4f60\u8bf4\u5427\uff01\u2026\u2026\u201d\u6885\u82b1\u53cc\u81c2\u4f38\u51fa\uff0c\u6402\u7d27\u4e86\u6c5f\u67ab\uff0c\u9053\uff1a\u201c\u522b\u518d\u4e22\u5f03\u6211\uff0c\u4ec0\u4e48\u4e8b\u6211\u90fd\u544a\u8bc9\u4f60\u3002\u201d \u3000\u3000\u5979\u8138\u4e0a\u6cea\u75d5\u5c24\u65b0\uff0c\u4f46\u6a31\u5507\u8f7b\u542f\uff0c\u6cdb\u8d77\u4e86\u5fae\u5fae\u7684\u7b11\u610f\uff0c\u771f\u662f\u5973\u4eba\u5fc3\uff0c\u6d77\u5e95\u9488\uff0c\u4e0d\u77e5\u9053\u5979\u53c8\u60f3\u5230\u4e86\u4ec0\u4e48\uff1f \u3000\u3000\u6c5f\u67ab\u4f4e\u4e0b\u5934\u53bb\uff0c\u8f7b\u8f7b\u5728\u90a3\u534a\u542f\u7684\u6a31\u5507\u4e0a\u4eb2\u4e86\u4e00\u4e0b\uff0c\u7b11\u9053\uff0c\u201c\u4f60\u4eec\u8fd9\u4e9b\u82b1\u5a62\uff0c\u4e00\u4e2a\u4e2a\u90fd\u957f\u7684\u5982\u82b1\u4f3c\u7389\uff0c\u5982\u4f55\u5728\u63a5\u53d7\u4e25\u9177\u7684\u8bad\u7ec3\u4e2d\uff0c\u800c\u53c8\u80fd\u4f7f\u7f8e\u4e3d\u4e0d\u53d7\u4f24\u5bb3\u3002\u201d \u3000\u3000\u201c\u8fd9\u5c31\u662f\u5979\u4eec\u7684\u9ad8\u660e\u4e4b\u5904\u4e86\uff0c\u2026\u2026\u201d\u6885\u82b1\u9053\uff1a\u201c\u5f53\u7136\uff0c\u6211\u4eec\u5fc5\u9700\u5148\u5929\u5177\u6709\u7f8e\u5973\u7684\u6761\u4ef6\uff0c\u518d\u52a0\u4e0a\u5979\u4eec\u540e\u5929\u4e2d\u5c0f\u5fc3\u7684\u57f9\u517b\uff0c\u6211\u4eec\u8bad\u7ec3\u867d\u7136\u4e25\u9177\uff0c\u4f46\u751f\u6d3b\u5374\u88ab\u7167\u987e\u7684\u5f88\u597d\uff0c\u6211\u6bcf\u4e09\u5929\uff0c\u90fd\u8981\u6d17\u4e00\u4e2a\u836f\u6c34\u6fa1\uff0c\u8db3\u8db3\u8981\u6ce1\u4e0a\u4e24\u4e2a\u65f6\u8fb0\uff0c\u6240\u4ee5\uff0c\u6211\u4eec\u7684\u80a4\u8272\u5149\u6ed1\uff0c\u5341\u516d\u5c81\u5f00\u59cb\u4e86\u90a3\u73b7\u8fb1\u5148\u7956\u7684\u8bad\u7ec3\uff0c\u6211\u4eec\u628a\u4e60\u7ec3\u7684\u5a9a\u672f\uff0c\u771f\u67aa\u771f\u5200\u7684\u7528\u5728\u7537\u4eba\u8eab\u4e0a\uff0c\u4e00\u4e2a\u60c5\u7aa6\u521d\u5f00\u7684\u5c11\u5973\uff0c\u966a\u7740\u4e00\u4e2a\u5168\u65e0\u611f\u60c5\u7684\u7537\u4eba\uff0c\u8981\u628a\u4ed6\u8ff7\u5f97\u660f\u5929\u9ed1\u5730\uff0c\u6211\u4eec\u7cca\u7cca\u6d82\u6d82\u7684\u5931\u53bb\u4e86\u5b9d\u8d35\u7684\u8d1e\u64cd\uff0c\u7136\u540e\uff0c\u8fd8\u8981\u4eb2\u624b\u6740\u4e86\u4ed6\u3002\u201d \u3000\u3000\u6c5f\u67ab\u8f7b\u8f7b\u4e00\u53f9\uff0c\u9053\uff1a\u201c\u679c\u7136\uff0c\u80cc\u5f03\u4eba\u6027\u7684\u8bad\u7ec3\uff0c\u8981\u706d\u7edd\u4f60\u4eec\u7684\u4eba\u6027\uff01\u201d \u3000\u3000\u6885\u82b1\u9053\uff1a\u201c\u53ef\u60b2\u7684\u662f\uff0c\u8981\u6740\u4e86\u4e09\u4e2a\u73b7\u8fc7\u8eab\u5b50\u7684\u7537\u4eba\uff0c\u624d\u7b97\u8bad\u7ec3\u5b8c\u6210\uff0c\u6211\u4e5f\u6740\u8fc7\u4e09\u4e2a\u4eba\uff0c\u4e0d\u8fc7\uff0c\u6211\u4e5f\u8bb8\u662f\u806a\u660e\u4e00\u4e9b\uff0c\u9664\u4e86\u7b2c\u4e00\u6b21\uff0c\u88ab\u4ed6\u593a\u53bb\u4e86\u8d1e\u6362\u4e4b\u5916\uff0c\u53e6\u5916\u4e24\u4e2a\u4eba\u90fd\u672a\u73b7\u5230\u6211\u7684\u8eab\u5b50\uff0c\u5c31\u88ab\u6211\u6740\u4e86\u3002\u201d \u3000\u3000\u201c\u53ef\u601c\u7684\u7537\u4eba\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u771f\u662f\u6b7b\u4e0d\u7791\u76ee\u554a\uff01\u201d \u3000\u3000\u201c\u4f60\u96be\u9053\u5e0c\u671b\u6709\u5f88\u591a\u7684\u7537\u4eba\u8ddf\u6211\u4e0a\u5e8a\u2026\u2026\u201d \u3000\u3000\u201c\u5f53\u7136\u4e0d\u662f\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u6211\u662f\u8bf4\u4f60\u4eec\u9009\u7684\u7537\u4eba\uff0c\u672a\u5fc5\u90fd\u662f\u767b\u5f92\u5b50\uff0c\u4f60\u4eec\u9009\u4e2d\u4e86\uff0c\u4ed6\u5c31\u6b7b\u5b9a\u4e86\uff0c\u4ed6\u53ef\u80fd\u662f\u4e00\u4f4d\u5b5d\u5b50\uff0c\u662f\u4e00\u4f4d\u541b\u5b50\u2026\u2026\u201d \u3000\u3000\u201c\u4f46\u4ed6\u4eec\u5982\u771f\u80fd\u4e03\u60c5\u4e0d\u52a8\u3002\u5ffd\u89c6\u6211\u4eec\u7684\u8bf1\u60d1\uff0c\u6211\u4eec\u662f\u4e0d\u4f1a\u6740\u4ed6\u7684\u2026\u2026\u201d\u6885\u82b1\u8bf4\uff1a\u201c\u4ed6\u9189\u4e8e\u7f8e\u8272\uff0c\u5f53\u7136\u6709\u53d6\u6b7b\u4e4b\u9053\u4e86\u3002\u201d \u3000\u3000\u201c\u8fd9\u6807\u51c6\u592a\u9ad8\u4e86\uff0c\u4ee5\u4f60\u4eec\u7684\u7f8e\u8272\uff0c\u5929\u4e0b\u7537\u4eba\uff0c\u80fd\u6709\u51e0\u4e2a\u4eba\u4e0d\u53d7\u8bf1\u60d1\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u4f60\u4eec\u5728\u51fa\u9053\u4e4b\u524d\uff0c\u81f3\u5c11\u90fd\u5df2\u662f\u6740\u8fc7\u4e09\u4e2a\u4eba\u7684\u51f6\u624b\u4e86\uff01\u201d \u3000\u3000\u6885\u82b1\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f46\u6211\u5374\u4e00\u7b4b\u6597\u683d\u5012\u4e86\u4f60\u7684\u6000\u4e2d\u2026\u2026\u201d \u3000\u3000\u201c\u4f60\u53ef\u4ee5\u653e\u5fc3\uff0c\u6211\u4e0d\u4f1a\u6740\u4f60\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u6211\u8981\u4f60\u4e3a\u6c5f\u6e56\u4e0a\u7684\u9053\u4e49\u6548\u529b\uff0c\u548c\u6211\u5e76\u80a9\u4f5c\u6218\u3002\u201d \u3000\u3000\u201c\u7530\u7389\u4e0d\u662f\u4f60\u7684\u672c\u540d\u5427\uff01\u97e9\u9738\u4e5f\u4e0d\u662f\uff0c\u4f60\u662f\u8c01\u5462\uff1f\u7a76\u7adf\u8981\u505a\u4ec0\u4e48\uff1f\u2026\u2026\u201d\u6885\u82b1\u9053\uff1a\u201c\u544a\u8bc9\u6211\u5b9e\u8bdd\uff0c\u6211\u624d\u80fd\u6709\u6240\u51b3\u5b9a\uff1f\u201d \u3000\u3000\u201c\u6211\u53eb\u6c5f\u67ab\uff0c\u5fd7\u5728\u6062\u590d\u625b\u6e56\u4e0a\u7684\u6e05\u660e\u4e16\u754c\uff0c\u6211\u4e5f\u6709\u4e00\u6279\u670b\u53cb\u5e2e\u52a9\u6211\uff0c\u4ed6\u4eec\u4eba\u6570\u4e0d\u662f\u5f88\u591a\uff0c\u4f46\u5374\u662f\u4e2a\u4e2a\u8eab\u8d1f\u7edd\u5b66\uff01\u201d \u3000\u3000\u201c\u6c5f\u67ab\uff0c\u6c5f\u67ab\u2026\u2026\u6ca1\u6709\u542c\u8fc7\u8fd9\u4e2a\u540d\u5b57\uff0c\u4e5f\u6ca1\u6709\u542c\u5e08\u957f\u4eec\u8bf4\u8fc7\uff0c\u4f60\u6df7\u5165\u5929\u9a6c\u5802\u4e2d\uff0c\u5c31\u662f\u8981\u5229\u7528\u5929\u9a6c\u5802\u4e2d\u4eba\uff0c\u548c\u6211\u4eec\u5bf9\u6297\u5417\uff1f\u201d\u6885\u82b1\u8f7b\u8f7b\u6c49\u606f\u4e00\u58f0\u53c8\u9053\uff1a\u201c\u5929\u9a6c\u5802\u7684\u529b\u91cf\u7684\u786e\u5f88\u5927\uff0c\u4e0d\u8fc7\uff0c\u4ed6\u4eec\u5df2\u81ea\u8150\u866b\u86c0\uff0c\u6709\u4e00\u4e9b\u7edd\u9876\u9ad8\u624b\uff0c\u5df2\u6295\u6548\u6211\u4eec\u2026\u2026\u3002\u201d \u3000\u3000\u201c\u4ed6\u4eec\u90fd\u662f\u79ef\u5e74\u8001\u8d3c\uff0c\u4e2a\u4e2a\u90fd\u89c1\u591a\u8bc6\u5e7f\uff0c\u9605\u5386\u4e30\u5bcc\uff0c\u5e94\u8be5\u77e5\u9053\u5229\u5bb3\u5f97\u5931\u624d\u5bf9\uff0c\u503e\u5de2\u4e4b\u4e0b\u65e0\u5b8c\u5375\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u4f60\u4eec\u7528\u4ec0\u4e48\u65b9\u6cd5\uff0c\u4f7f\u4ed6\u4eec\u80cc\u5f03\u4e86\u5929\u9a6c\u5802\u5462\uff1f\u201d \u3000\u3000\u201c\u5973\u8272\u3001\u6743\u5229\u2026\u2026\u201d\u6885\u82b1\u9053\uff1a\u201c\u4ed6\u4eec\u4e5f\u8bb8\u7ecf\u5386\u8fc7\u5f88\u591a\u7684\u5973\u4eba\uff0c\u4f46\u7edd\u6ca1\u6709\u4e60\u7ec3\u8fc7\u5a9a\u672f\u5973\u4eba\u7684\u5389\u5bb3\uff0c\u53ef\u4ee5\u7ed9\u7537\u4eba\u6700\u9ad8\u7684\u4eab\u53d7\uff0c\u518d\u884c\u4ee5\u5927\u6743\u5f97\u5229\uff0c\u79ef\u5e74\u8001\u8d3c\u4e5f\u4e00\u6837\u81ea\u7518\u5165\u5f40\u4e86\uff01\u201d \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u4eba\u6027\u4e2d\u8fd9\u4e9b\u5f31\u70b9\uff0c\u6052\u4e45\u4e0d\u53d8\uff0c\u8c01\u53c8\u80fd\u9003\u8fc7\u5229\u60d1\u8272\u8bf1\u5462\uff1f\u4f55\u51b5\u662f\u7edd\u4e16\u7684\u7f8e\u8272\uff0c\u518d\u52a0\u4e0a\u8ff7\u9b42\u4e4b\u672f\u2026\u2026\u5bf9\u4e86\uff0c\u6885\u82b1\uff0c\u4f60\u5bf9\u6211\u7528\u8fc7\u8ff7\u9b42\u5927\u6cd5\u6ca1\u6709\uff1f\u201d \u3000\u3000\u6885\u82b1\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u6ca1\u6709\uff0c\u6211\u597d\u60f3\u65bd\u5c55\uff0c\u8ba9\u4f60\u5982\u9189\u5982\u75f4\u7684\uff0c\u4f9d\u9644\u7740\u6211\uff0c\u4f46\u6211\u4e0d\u5fcd\u5fc3\uff0c\u6211\u6015\u4f60\u4f1a\u88ab\u5f15\u8bf1\u5f97\u795e\u9b42\u98a0\u5012\uff0c\u65e0\u6cd5\u81ea\u62d4\uff0c\u778e\u649e\u80e1\u95f9\uff0c\u4e22\u4e86\u6027\u547d\uff0c\u8c01\u77e5\u4f60\u7adf\u662f\u5982\u6b64\u4e00\u4e2a\u5389\u5bb3\u4eba\u7269\uff0c\u771f\u662f\u767d\u8d39\u4e86\u6211\u4e00\u756a\u597d\u5fc3\u3002\u201d \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u73b0\u5728\uff0c\u6211\u4eec\u4e4b\u95f4\uff0c\u5df2\u8bf4\u7684\u5f88\u6e05\u695a\u4e86\uff0c\u4f60\u51c6\u5907\u4f5c\u4f55\u6253\u7b97\u5462\uff1f\u201d \u3000\u3000\u201c\u8fd8\u6ca1\u6709\u5f04\u6e05\u695a\uff0c\u6211\u5fc3\u4e2d\u8fd8\u6709\u7591\u95ee\uff1f\u2026\u2026\u201d\u6885\u82b1\u8bf4\uff0c\u201c\u6211\u4e00\u5411\u5b9a\u529b\u5f88\u5f3a\uff0c\u600e\u4e48\u5ffd\u7136\u95f4\u5bf9\u4f60\u8ff7\u604b\u5f97\u5982\u9677\u6df1\u6e0a\uff0c\u65e0\u6cd5\u81ea\u4e3b\uff0c\u544a\u8bc9\u6211\uff0c\u4f60\u662f\u4e0d\u662f\u4e5f\u7528\u4e86\u8ff7\u9b42\u5947\u672f\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u5fae\u5fae\u2014\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u8bf4\u5462\uff1f\u201d \u3000\u3000\u201c\u5e94\u8be5\u7528\u8fc7\u2026\u2026\u201d\u6885\u82b1\u9053\uff1a\u201c\u6211\u77e5\u9053\u4e0d\u8bba\u5982\u4f55\u4fca\u9038\u7684\u7537\u5b50\uff0c\u4e5f\u4e0d\u80fd\u4e00\u4e0b\u5b50\u8ba9\u6211\u4ee5\u5fc3\u76f8\u8bb8\uff0c\u4f46\u4f60\u5374\u5bb3\u82e6\u4e86\u6211\u3002\u201d \u3000\u3000\u201c\u662f\u7528\u8fc7\u2026\u2026\u201d\u6c5f\u67ab\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u6211\u5982\u6ca1\u6709\u4e60\u7ec3\u8fc7\u8fd9\u4e9b\u5947\u672f\uff0c\u65e9\u88ab\u4f60\u8ff7\u6655\u5934\u4e86\u3002\u201d \u3000\u3000\u201c\u90a3\u662f\u4ec0\u4e48\u6b66\u529f\uff1f\u201d \u3000\u3000\u201c\u60d1\u5fc3\u672f\u2026\u2026\u201d\u6c5f\u67ab\u4f46\u7136\u56de\u7b54\u3002 \u3000\u3000\u201c\u8fd9\u5c31\u96be\u602a\u4e86\uff0c\u662f\u8ff7\u9b42\u5927\u6cd5\u4e2d\u6700\u9ad8\u7684\u4e00\u79cd\u6b66\u529f\uff0c\u52ff\u602a\u6211\u843d\u5165\u4e0b\u98ce\u2026\u2026\u201d\u6885\u82b1\u53f9\u53e3\u6c14\u53c8\u9053\uff1a\u201c\u8c08\u8c08\u6211\u5427\uff01\u4f60\u51c6\u5907\u5982\u4f55\u5904\u7f6e\u6211\uff1f\u2019\uff0c \u3000\u3000\u201c\u50cf\u73b0\u5728\u4e00\u6837\u3002\u4f60\u8fd8\u662f\u6211\u7684\u7ea2\u7c89\u77e5\u5df1\uff0c\u53ea\u4e0d\u8fc7\uff0c\u8981\u5e2e\u52a9\u6211\u5bf9\u6297\u4f60\u4eec\u90a3\u4e2a\u90aa\u6076\u7684\u7ec4\u5408\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u5305\u62ec\u5929\u9a6c\u5802\u7684\u4eba\u624b\u5728\u5185\u3002\u201d \u3000\u3000\u201c\u4f60\u7684\u673a\u4f1a\u4e0d\u5927\u2026\u2026\u201d\u6885\u82b1\u8bf4\uff0c\u201c\u6211\u63a5\u5230\u7684\u901a\u77e5\uff0c\u5185\u5bab\u4e00\u7cfb\u4e2d\u5df3\u6d3e\u51fa\u516d\u5927\u6740\u624b\uff0c\u8d76\u6765\u957f\u5b89\uff0c\u4e13\u95e8\u4ee5\u4f60\u4e3a\u72d9\u6740\u5bf9\u8c61\uff0c\u6c5f\u67ab\uff0c\u4f60\u90a3\u4e2a\u97e9\u9738\u7684\u5916\u8c8c\uff0c\u65e9\u5df2\u88ab\u7ed8\u6210\u4e86\u56fe\u50cf\uff0c\u5448\u5165\u5185\u5bab\uff0c\u6211\u60f3\u6bcf\u4e00\u4e2a\u6740\u624b\u7684\u624b\u4e2d\uff0c\u90fd\u5df2\u6709\u4e86\u4f60\u7684\u5f62\u8c8c\uff0c\u6211\u770b\uff0c\u7531\u73b0\u5728\u5f00\u59cb\uff0c\u53ef\u4ee5\u5f03\u53bb\u97e9\u9738\u90a3\u4e2a\u5916\u8c8c\u4e0d\u7528\u4e86\u3002\u201d \u3000\u3000\u201c\u6211\u4e0d\u4f1a\u5f03\u53bb\u97e9\u9738\u7684\u8eab\u4efd\uff0c\u4ed6\u4eec\u8981\u6740\u97e9\u9738\uff0c\u6211\u6b63\u597d\u7528\u97e9\u9738\u5f15\u8bf1\u4ed6\u4eec\u51fa\u73b0\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a \u3000\u3000\u201c\u73b0\u5728\uff0c\u4f3c\u662f\u5df2\u65e0\u6cd5\u518d\u73a9\u6349\u8ff7\u85cf\u7684\u628a\u620f\u4e86\uff0c\u5404\u65b9\u5df2\u51fa\u52a8\u9ad8\u624b\uff0c\u4e5f\u8be5\u771f\u5200\u771f\u67aa\u7684\u4e0a\u9635\u4e86\u3002\u201d \u3000\u3000\u201c\u4f60\u597d\u50cf\u5f88\u6709\u4fe1\u5fc3\uff1f\u201d \u3000\u3000\u201c\u662f\u7684\uff0c\u6885\u82b1\uff0c\u6211\u5982\u6ca1\u6709\u4fe1\u5fc3\uff0c\u53c8\u600e\u80af\u62c9\u4f60\u8fc7\u6765\uff0c\u966a\u6211\u727a\u7272\u3002\u201d \u3000\u3000\u201c\u5509\uff01\u751f\u6b7b\u4e00\u4e8b\u6211\u5012\u662f\u4e0d\u5728\u4e4e\u4e86\uff0c\u6211\u65e9\u5df2\u7ecf\u51c6\u5907\u4e3a\u4f60\u6b89\u8eab\uff0c\u6211\u662f\u8bf4\uff0c\u6211\u6295\u6548\u8fc7\u6765\uff0c\u4f60\u662f\u4e3b\u5e05\u8eab\u4efd\uff0c\u6211\u8fd8\u80fd\u548c\u4f60\u50cf\u8fc7\u53bb\u4e00\u6837\uff0c\u540c\u5e8a\u5171\u6795\u5417\uff1f\u201d \u3000\u3000\u201c\u4e00\u6837\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u552f\u4e00\u7684\u4e0d\u540c\u662f\uff0c\u6211\u8ba4\u8bc6\u4e86\u5f88\u591a\u7684\u5973\u4eba\uff0c\u6211\u4fdd\u8bc1\u5979\u4eec\u4e0d\u4f1a\u5992\u5fcc\u4f60\uff0c\u4f46\u4f60\u4e5f\u8981\u5bbd\u5bb9\u5979\u4eec\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u6211\u660e\u767d\u2026\u2026\u201d\u6885\u82b1\u8bf4\uff0c\u201c\u9a8c\u8bc1\u4f60\u5bf9\u6211\u7684\u9b45\u529b\uff0c\u6211\u76f8\u4fe1\u4f60\u53ef\u4ee5\u902e\u4f4f\u83f1\u82b1\u90a3\u4e2a\u4e2b\u5934\uff01\u201d \u3000\u3000\u201c\u600e\u4e48\uff1f\u590f\u5a62\u83f1\u82b1\u2026\u2026\u201d\u625b\u67ab\u5947\u9053\uff1a\u201c\u4f60\u7684\u91cd\u8981\u52a9\u624b\uff0c\u4e0d\u662f\u79cb\u82b1\u53cc\u5a62\u5417\uff1f\u201d \u3000\u3000\u201c\u8868\u9762\u4e0a\u662f\u5982\u6b64\uff0c\u51ac\u82b1\u4e3a\u9996\uff0c\u79cb\u82b1\u6b21\u4e4b\uff0c\u4f46\u83f1\u82b1\u4e0d\u540c\uff0c\u5979\u638c\u63e1\u4e86\u901a\u5411\u5185\u5bab\u7684\u8baf\u606f\uff0c\u6709\u4e9b\u4e8b\uff0c\u8fde\u6211\u90fd\u4e0d\u77e5\u9053\uff0c\u5979\u76f4\u63a5\u53ef\u4ee5\u5411\u5bab\u4e3b\u63d0\u51fa\u62a5\u544a\u3002\u201d \u3000\u3000\u201c\u5389\u5bb3\u5440\uff01\u5389\u5bb3\uff0c\u8fd9\u7b49\u590d\u5f0f\u7684\u5e03\u7f6e\uff0c\u5f88\u5bb9\u6613\u628a\u4eba\u5bfc\u5165\u6b67\u9014\u3002\u201d\u6885\u82b1\u9053\uff1a\u201c\u916c\u541b\u4e4b\u60c5\u7518\u4e3a\u5974\uff0c\u6211\u53ef\u4ee5\u6295\u6548\u8fc7\u6765\uff0c\u4e0d\u8fc7\uff0c\u4f60\u53ef\u4e0d\u80fd\u5341\u5929\u516b\u5929\u4e0d\u7406\u6211\uff0c\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u4f1a\u7684\uff0c\u6885\u82b1\uff0c\u4f60\u7684\u5fc3\u6295\u5165\u6211\u4eec\uff0c\u4f46\u4eba\u8fd8\u8981\u7559\u5728\u90a3\u91cc\uff0c\u50cf\u73b0\u5728\u4e00\u6837\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u8bf4\uff0c\u201c\u5f7c\u6b64\u6697\u4e2d\u901a\u8baf\u606f\uff0c\u5f53\u7136\uff0c\u5982\u82e5\u4f60\u9047\u4e0a\u4e86\u4ec0\u4e48\u5371\u9669\uff0c\u7acb\u523b\u53ef\u4ee5\u8fdb\u6765\u6b64\u5904\u3002\u201d \u3000\u3000\u201c\u90a3\u4e2a\u6234\u7740\u5047\u53d1\u7684\u548c\u5c1a\uff0c\u4e5f\u662f\u4f60\u7684\u4eba\u5417\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u6c5f\u67d0\u4e0d\u5728\u65f6\uff0c\u90fd\u7531\u4ed6\u4f5c\u4e3b\uff0c\u6211\u4f1a\u901a\u77e5\u4ed6\u4eec\uff0c\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u4e0d\u77e5\u9053\uff0c\u79cb\u82b1\u53cc\u5a62\u4f1a\u4e0d\u4f1a\u53d7\u6211\u7684\u5f71\u54cd\uff0c\u2026\u2026\u201d\u6885\u82b1\u9053\uff0c\u201c\u6211\u4eec\u5bf9\u5e08\u95e8\u7684\u5fe0\u8bda\uff0c\u4e00\u5411\u90fd\u7531\u4e09\u5bab\u4e3b\u6240\u638c\u63e1\uff0c\u6240\u4ee5\uff0c\u6211\u4eec\u53ea\u8981\u7528\u4eba\uff0c\u4e0d\u5fc5\u6000\u7591\u4ed6\u7684\u5fe0\u8bda\uff0c\u83f1\u82b1\u7684\u8eab\u4efd\uff0c\u6211\u4e5f\u662f\u6700\u8fd1\u624d\u53d1\u73b0\uff0c\u8fd8\u662f\u5979\u81ea\u5df1\u6765\u544a\u8bc9\u6211\u7684\uff0c\u5979\u662f\u4e09\u5bab\u4e3b\u7684\u8033\u76ee\uff0c\u6709\u4e00\u5957\u5f88\u795e\u79d8\u7684\u4f20\u8baf\u65b9\u6cd5\uff0c\u53ef\u4ee5\u5728\u4e00\u65e5\u591c\u5185\uff0c\u4f20\u9012\u6570\u5343\u91cc\u2026\u2026\u201d \u3000\u3000\u201c\u90a3\u662f\u4fe1\u9e3d\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u5e94\u8be5\u4e0d\u662f\uff0c\u6211\u6ca1\u6709\u95ee\u8fc7\u83f1\u82b1\uff0c\u4e0d\u8be5\u6211\u7ba1\u7684\u4e8b\uff0c\u6211\u4e00\u5411\u4e0d\u95ee\u6c5f\u67ab\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u597d\uff01\u8fd9\u4ef6\u4e8b\u4ea4\u7ed9\u6211\u529e\uff0c\u6709\u4e8b\u5c31\u548c\u6211\u7684\u4eba\u8fde\u7edc\uff0c\u4ed6\u4eec\u4f1a\u5c3d\u5feb\u544a\u8bc9\u6211\uff0c\u4e5f\u4f1a\u8fc5\u901f\u7ed9\u4f60\u652f\u63f4\u2026\u2026\u201d \u3000\u3000\u201c\u600e\u4e48\uff1f\u64b5\u6211\u8d70\u554a\uff01\u2026\u2026\u201d\u6885\u82b1\u4e00\u7528\u529b\uff0c\u62b1\u7740\u6c5f\u67ab\u5012\u5728\u4e86\u5e8a\u4e0a\u3002 \u3000\u3000\u6e29\u5b58\u7247\u523b\uff0c\u6c5f\u67ab\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u6885\u82b1\uff0c\u8fd9\u91cc\u4e0d\u884c\uff0c\u548c\u5c1a\u6bb5\u4e5d\uff0c\u90fd\u5728\u95e8\u5916\uff0c\u4ed6\u4eec\u4f1a\u542c\u5f97\u6e05\u6e05\u695a\u695a\u3002\u201d \u3000\u3000\u6885\u82b1\u7ad9\u8d77\u8eab\u5b50\uff0c\u7406\u4e00\u4e0b\u9b13\u8fb9\u6563\u53d1\uff0c\u7b11\u9053\uff1a\u201c\u6211\u4e5f\u6ca1\u6709\u8fd9\u4e2a\u80c6\u5b50\uff0c\u60ca\u5929\u52a8\u5730\u4e00\u95f9\uff0c\u4ee5\u540e\u600e\u4e48\u89c1\u4ed6\u4eec\uff1f\u53ef\u662f\u2026\u2026\u201d \u3000\u3000\u201c\u8fd9\u51e0\u5929\u6211\u4f1a\u53bb\u770b\u4f60\uff0c\u4e0d\u8fc7\uff0c\u522b\u592a\u8ba8\u538c\u97e9\u9738\u90a3\u5f20\u8138\uff01\u201d \u3000\u3000\u201c\u6211\u8981\u4f60\u73b0\u5728\u7684\u8138\uff0c\u5b83\u4ee4\u4eba\u9676\u9189\u2026\u201d\u6885\u82b1\u7a81\u6536\u655b\u8d77\u6b3e\u6b3e\u6df1\u60c5\uff0c\u6b63\u5bb9\u8bf4\u9053\uff1a\u201c\u522b\u5fd8\u4e86\uff0c\u902e\u4f4f\u83f1\u82b1\u90a3\u4e2a\u5c0f\u4e2b\u5934\uff0c\u4f60\u5982\u80fd\u6293\u4f4f\u4e86\u5979\u7684\u5fc3\uff0c\u5979\u4f1a\u544a\u8bc9\u4f60\u5f88\u591a\u7684\u60ca\u4eba\u79d8\u5bc6\u3002\u201d \u3000\u3000\u201c\u6211\u8bd5\u8bd5\u770b\u5427\uff01\u4e0d\u4e00\u5b9a\u6bcf\u6b21\u90fd\u7075\u3002\u201d \u3000\u3000\u201c\u4e00\u5b9a\u8981\u7528\u2018\u60d1\u5fc3\u672f\u2019\u4e00\u51fb\u5fc5\u4e2d\uff0c\u5426\u5219\uff0c\u5979\u4f1a\u628a\u6d88\u606f\u4f20\u5165\u5185\u5bab\uff0c\u6211\u8d70\u4e86\uff0c\u4eca\u5929\u603b\u7b97\u89e3\u53bb\u4e86\u6211\u5fc3\u4e2d\u4e00\u4e2a\u5927\u7ed3\u2026\u2026\u201d \u3000\u3000\u201c\u6162\u4e00\u70b9\u2026\u2026\u201d \u3000\u3000\u6885\u82b1\u7a81\u7136\u56de\u8fc7\u8eab\u5b50\uff0c\u4e00\u65f6\u6c34\u6c6a\u6c6a\u7684\u5927\u773c\u775b\u76ef\u5728\u6c5f\u67ab\u7684\u8138\u4e0a\uff0c\u9053\uff1a\u201c\u4f60\u8fd8\u8981\u5e72\u4ec0\u4e48\uff1f\u8fd9\u91cc\u4e0d\u884c\u561b\u3002\u201d \u3000\u3000\u201c\u90a3\u591c\uff0c\u65bd\u5c55\u4e00\u6b65\u5012\u628a\u6211\u8ff7\u5012\u7684\u4eba\u662f\u8c01\uff1f\u4f60\u89c1\u8fc7\u6ca1\u6709\uff1f\u201d \u3000\u3000\u201c\u89c1\u8fc7\u2026\u2026\u201d\u6885\u82b1\u8bf4\uff0c\u201c\u4e0d\u8fc7\uff0c\u6211\u662f\u5949\u547d\u89c1\u4ed6\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u9894\u9996\u5fae\u7b11\uff0c\u7b11\u7684\u6709\u70b9\u8be1\u5bc6\u3002 \u3000\u3000\u6885\u82b1\u53cc\u773c\u4e00\u77aa\uff0c\u9053\uff1a\u201c\u4f60\u522b\u60f3\u6b6a\u91cc\u53bb\u4e86\uff0c\u4ed6\u5df2\u7ecf\u4e03\u5341\u591a\u5c81\u4e86\uff0c\u987b\u9b13\u5982\u94f6\uff0c\u90a3\u4f1a\u770b\u4e0a\u6211\u4eec\u8fd9\u79cd\u5c0f\u4e2b\u5934\uff0c\u4ed6\u53ef\u80fd\u662f\u4e09\u5bab\u4e3b\u5e03\u8bbe\u8272\u7f51\u4e0b\u7684\u4fd8\u864f\u3002\u201d \u3000\u3000\u201c\u771f\u662f\u4e0a\u884c\u4e0b\u6548\u554a\uff01\u2026\u2026\u201d\u6c5f\u67ab\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u5185\u5bab\u4e00\u7cfb\uff0c\u4e0a\u81ea\u5bab\u4e3b\uff0c\u4e0b\u81f3\u82b1\u5973\uff0c\u5168\u90fd\u628a\u7f8e\u8272\u5f53\u4f5c\u4e86\u5175\u5203\u65bd\u7528\uff0c\u800c\u4e14\u662f\u65e0\u5f80\u4e0d\u5229\u3002\u201d \u3000\u3000\u201c\u78b0\u5230\u4f60\uff0c\u5c31\u7b97\u5979\u5012\u5e3d\u4e86\uff0c\u6211\u770b\u4e09\u5bab\u4e3b\u7684\u9053\u884c\uff0c\u4e5f\u672a\u5fc5\u662f\u4f60\u654c\u624b\u3002\u201d\u6885\u82b1\u7b11\u9053\uff1a\u201c\u4e09\u5bab\u4e3b\u662f\u6559\u6211\u4eec\u4e60\u7ec3\u5a9a\u672f\u7684\u4eba\uff0c\u636e\u8bf4\uff0c\u5979\u8ff7\u9b42\u5927\u6cd5\uff0c\u5df2\u4eba\u4e86\u7b2c\u4e03\u91cd\u5883\u754c\uff0c\u4e00\u7b11\u4e4b\u4e0b\uff0c\u80fd\u9500\u4eba\u9b42\uff0c\u65e5\u540e\uff0c\u4f60\u8981\u662f\u78b0\u4e0a\u4e86\uff0c\u7ed9\u6211\u5c0f\u5fc3\u4e00\u4e9b\uff0c\u8fd8\u6ca1\u6709\u60d1\u5230\u522b\u4eba\u7684\u5fc3\uff0c\u5374\u88ab\u522b\u4eba\u52fe\u4e86\u9b42\u53bb\u2026\u2026\u201d \u3000\u3000\u201c\u6709\u8fd9\u6837\u5389\u5bb3\u4e48\uff1f\u2026\u2026\u201d \u3000\u3000\u201c\u5bf9\u4e86\uff0c\u987a\u4fbf\u63d0\u9192\u4f60\u4e00\u58f0\uff0c\u83f1\u82b1\u5c31\u662f\u4e09\u5bab\u4e3b\u4eb2\u4f20\u7684\u5f1f\u5b50\u4e4b\u4e00\uff0c\u8ff7\u9b42\u672f\u4e4b\u9ad8\u660e\uff0c\u6211\u662f\u7518\u62dc\u4e0b\u98ce\u3002\u4f60\u5343\u4e07\u4e0d\u53ef\u5927\u610f\u3002\u201d\u8f6c\u8eab\u4e0b\u8dc3\uff0c\u53bb\u52bf\u5982\u7bad\u3002 \u3000\u3000\u5979\u4e0d\u80fd\u8d70\u7684\u592a\u6162\uff0c\u4e00\u6162\u5c31\u4e0d\u60f3\u8d70\u4e86\u3002 \u3000\u3000\u6c5f\u67ab\u53ec\u6765\u4e86\u6bb5\u4e5d\u548c\u4e03\u5b9d\u548c\u5c1a\uff0c\u8bf4\u660e\u4e86\u6885\u82b1\u6295\u964d\u51b3\u5fc3\uff0c\u6700\u540e\uff0c\u5374\u53c8\u52a0\u4e0a\u4e00\u6bb5\u8bdd\uff0c\u9053\uff1a\u201c\u5185\u5bab\u4e00\u7cfb\uff0c\u4ee5\u672f\u6cd5\u63a7\u5236\u5f1f\u5b50\uff0c\u6885\u82b1\u662f\u5426\u771f\u80fd\u6323\u8131\u8fd9\u4e2a\u63a7\u5236\uff0c\u4e5f\u65e0\u6cd5\u5b8c\u5168\u8ba4\u5b9a\uff0c\u6240\u4ee5\uff0c\u4ea4\u5f80\u4e4b\u95f4\uff0c\u4ecd\u8981\u4fdd\u6301\u51e0\u5206\u8c28\u614e\u3002\u201d\u4e03\u5b9d\u548c\u5c1a\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u73b0\u5728\uff0c\u662f\u5426\u8981\u6539\u53d8\u4e00\u4e0b\u884c\u52a8\uff0c\u5148\u6293\u83f1\u82b1\u90a3\u4e2a\u4e2b\u5934\uff1f\u201d\u6c5f\u67ab\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u660e\u65e5\u8fc7\u5348\u4e4b\u540e\uff0c\u4ed6\u4eec\u6d3e\u6765\u7684\u6740\u624b\uff0c\u5c31\u8fdb\u5165\u4e86\u957f\u5b89\uff0c\u968f\u65f6\u4f1a\u5c55\u5f00\u5bf9\u6211\u7684\u72d9\u6740\uff0c\u90a3\u65f6\uff0c\u4ed6\u4eec\u4e5f\u4f1a\u4e25\u5bc6\u7684\u76d1\u89c6\u6211\u7684\u884c\u52a8\uff0c\u8fd9\u4e2a\u884c\u52a8\uff0c\u4e0d\u662f\u6885\u82b1\u6307\u6325\uff0c\u5c31\u662f\u83f1\u82b1\u4ece\u4e2d\u64cd\u7eb5\uff0c\u90a3\u65f6\u5019\uff0c\u5c31\u53ef\u4ee5\u8fa8\u522b\u51fa\u6885\u82b1\u662f\u771f\u5fc3\u5f52\u987a\uff0c\u8fd8\u662f\u522b\u6709\u7528\u5fc3\uff0c\u4e5f\u53ef\u80fd\u770b\u51fa\u6765\u83f1\u82b1\u5728\u56db\u5b63\u82b1\u5a62\u4e2d\u7684\u4efd\u91cf\u3002\u201d \u3000\u3000\u201c\u6211\u4eec\u7684\u76d1\u89c6\u7f51\u8def\uff0c\u8fd8\u6ca1\u6709\u5e03\u7f6e\u8d77\u6765\uff0c\u4eba\u5bb6\u5374\u65e9\u6709\u5b89\u6392\u4e86\uff0c\u6211\u4eec\u60f3\u5230\u63a5\u529b\u72d9\u6740\uff0c\u522b\u4eba\u5df2\u4ed8\u8bf8\u884c\u52a8\uff0c\u6211\u60f3\uff0c\u6211\u4eec\u8fd8\u624b\u4e00\u51fb\uff0c\u5fc5\u8ba9\u5b83\u5168\u519b\u8986\u6ca1\u624d\u6210\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u9053\uff1a\u201c\u8fd9\u91cc\u7684\u4eba\u624b\uff0c\u5c31\u663e\u5f97\u4e0d\u591f\u5206\u914d\u4e86\u3002\u201d \u3000\u3000\u201c\u90a3\u5c31\u8c03\u4ed6\u4eec\u51fa\u6765\u5427\uff01\u6700\u597d\u5148\u548c\u56db\u59d1\u8fde\u7edc\u4e00\u4e0b\uff0c\u884c\u52a8\u65f6\uff0c\u80fd\u6709\u63a9\u62a4\uff0c\u80fd\u4e0d\u8ba9\u654c\u4eba\u53d1\u73b0\u771f\u6b63\u9762\u76ee\uff0c\u90a3\u624d\u662f\u6700\u597d\u7684\u884c\u52a8\uff0c\u8ba9\u4ed6\u4eec\u83ab\u6d4b\u9ad8\u6df1\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u548c\u5c1a\u7acb\u523b\u53bb\u627e\u5f20\u59d1\u5a18\u5546\u91cf\uff0c\u6bb5\u5144\u4e5f\u8bf7\u544a\u8bc9\u80e1\u5144\uff0c\u4e5f\u8981\u5929\u540c\u3001\u80e1\u5143\u77e5\u9053\uff0c\u6211\u4eec\u5df2\u7ecf\u771f\u7684\u5c55\u5f00\u4e86\u5bf9\u654c\u884c\u52a8\u3002\u201d \u3000\u3000\u6bb5\u4e5d\u70b9\u5934\u800c\u53bb\u3002 \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u76ee\u7779\u6bb5\u4e5d\u53bb\u8fdc\uff0c\u624d\u62b5\u58f0\u8bf4\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\uff0c\u4f60\u8981\u4e0d\u8981\u548c\u9752\u51e4\u5546\u91cf\u4e00\u4e0b\uff0c\u6211\u770b\uff0c\u53cc\u65b9\u8fd9\u4e00\u4ea4\u624b\uff0c\u53ef\u80fd\u5c31\u6311\u660e\u4e86\u7acb\u573a\u2026\u2026\u201d \u3000\u3000\u201c\u6700\u597d\u7684\u529e\u6cd5\u662f\uff0c\u8fd8\u628a\u8fd9\u7b14\u5e10\u8bb0\u5728\u5929\u9a6c\u5802\u4e2d\uff0c\u975e\u5fc5\u8981\u6211\u4eec\u4e0d\u8981\u51fa\u9762\uff0c\u56e0\u4e3a\uff0c\u6211\u4eec\u8fd8\u4e0d\u591f\u5f3a\u5927\u2026\u2026\u201d \u3000\u3000\u201c\u597d\u529e\u6cd5\uff01\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u54b1\u4eec\u5c3d\u91cf\u4fdd\u6301\u6697\u6765\u6697\u5f80\uff0c\u4f7f\u4ed6\u4eec\u4e92\u76f8\u731c\u5fcc\uff0c\u5982\u80fd\u628a\u5929\u738b\u95e8\u518d\u62d6\u8fdb\u6765\uff0c\u90a3\u5c31\u66f4\u70ed\u95f9\u4e86\uff0c\u4e5f\u66f4\u663e\u5f97\u6251\u6714\u8ff7\u79bb\uff0c\u5f88\u96be\u7406\u4e00\u4e2a\u5934\u7eea\u51fa\u6765\u3002\u201d \u3000\u3000\u201c\u6211\u8bd5\u8bd5\u770b\u5427\uff01\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u4f60\u548c\u56db\u59d1\u6700\u597d\u4e5f\u628a\u6211\u884c\u52a8\u8def\u7ebf\u62df\u51fa\u6765\uff0c\u6211\u4eec\u4e0d\u80fd\u8ba9\u4ed6\u4eec\u9009\u62e9\u4e0b\u624b\u7684\u5730\u65b9\u3002\u201d \u3000\u3000\u201c\u5bf9\uff01\u4fbf\u4e8e\u4ed6\u4eec\u4e0b\u624b\uff0c\u66f4\u5229\u4e8e\u6211\u4eec\u53cd\u51fb\uff0c\u4e0d\u8fc7\uff0c\u4f60\u8fd8\u662f\u8981\u5c0f\u5fc3\u4e00\u4e9b\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u9053\uff1a \u3000\u3000\u201c\u4ed6\u4eec\u6d3e\u51fa\u7684\u4e13\u4efb\u6740\u624b\uff0c\u8fd9\u72d9\u6740\u7684\u624b\u6bb5\uff0c\u5fc5\u7136\u662f\u82b1\u62db\u767e\u51fa\uff0c\u4ee4\u4eba\u9632\u4e0d\u80dc\u9632\u4e86\u3002\u201d \u3000\u3000\u6c5f\u67ab\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u6211\u5750\u606f\u4e00\u4e0b\uff0c\u6d17\u4e2a\u6fa1\uff0c\u6362\u4ef6\u8863\u670d\u518d\u53bb\u3002\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u6885\u82b1\u7559\u5728\u4f60\u8eab\u4e0a\u7684\u8102\u7c89\u9999\u6c14\uff0c\u8fd8\u662f\u76f8\u5f53\u6d53\u70c8\u3002\u201d \u3000\u3000\u5927\u548c\u5c1a\u7b11\u4e00\u7b11\uff0c\u8f6c\u8eab\u79bb\u53bb\u4e86\u3002 \u3000\u3000\u6c5f\u67ab\u5374\u88ab\u90a3\u4e00\u7b11\uff0c\u95f9\u5f97\u5341\u5206\u4e0d\u5b89\uff0c\u5fd6\u9053\uff1a\u201c\u8fd9\u4e2a\u7985\u95e8\u9ad8\u618e\uff0c\u4e0d\u77e5\u662f\u8f7b\u6de1\u6211\u3002\u8fd8\u662f\u5728\u6fc0\u53d1\u6211\uff0c\u6211\u6c5f\u67ab\u65e5\u591c\u5728\u5973\u4eba\u5806\u4e2d\u6253\u6eda\uff0c\u60f3\u8d77\u6765\uff0c\u4e5f\u771f\u662f\u7f9e\u4e8e\u89c1\u4eba\uff0c\u53ef\u662f\uff0c\u5927\u548c\u5c1a\uff0c\u6211\u5e76\u975e\u653e\u7eb5\u8d2a\u998b\uff0c\u6211\u662f\u4e0d\u5f97\u5df2\u554a\uff01\u8c01\u8981\u4ed6\u4eec\u5168\u7528\u4e9b\u5973\u4eba\u5462\uff1f\u201d \u3000\u3000\u9752\u51e4\u697c\u4e00\u5207\u5982\u6614\uff0c\u4f46\u738b\u5b37\u3001\u674e\u5b37\u5bf9\u6c5f\u67ab\u7684\u6b22\u8fce\uff0c\u5374\u4e0d\u4f3c\u60f3\u8c61\u4e2d\u90a3\u4e48\u4eb2\u5207\uff0c\u751a\u81f3\u6709\u70b9\u51b7\u6de1\uff0c\u53ea\u5bf9\u6c5f\u67ab\u70b9\u70b9\u5934\uff0c\u5c31\u907f\u4e86\u5f00\u53bb\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u6b4c\u575b\u591c\u573a\u5df2\u8fc7\uff0c\u662f\u4e8c\u66f4\u8fc7\u540e\u7684\u65f6\u5206\u3002 \u3000\u3000\u9752\u51e4\u5df2\u5378\u53bb\u4e86\u6d53\u5986\uff0c\u6362\u4e86\u4e00\u8eab\u8f7b\u4fbf\u8863\u7740\uff0c\u501a\u95e8\u800c\u7acb\uff0c\u5fae\u7b11\u8fce\u5ba2\u3002 \u3000\u3000\u6c5f\u67ab\u6709\u4e9b\u7d27\u5f20\u7684\uff0c\u9053\uff1a\u201c\u5979\u4eec\u597d\u50cf\u4e0d\u592a\u9ad8\u5174\uff1f\u201d \u3000\u3000\u201c\u4f60\u662f\u8bf4\u738b\u5b37\u3001\u674e\u5b37\uff1f\u201d \u3000\u3000\u201c\u662f\u554a\uff01\u8138\u4e0a\u4e0d\u89c1\u7b11\u5bb9\uff0c\u795e\u60c5\u5341\u5206\u51b7\u6de1\u2026\u2026\u201d \u3000\u3000\u9752\u51e4\u6253\u65ad\u4e86\u6c5f\u67ab\u7684\u8bdd\uff0c\u63a5\u9053\uff1a\u201c\u5979\u4eec\u6068\u4f60\u51b7\u6de1\u65e0\u60c5\uff0c\u8fd9\u4e48\u4e45\uff0c\u8fd8\u4e0d\u6765\u4e00\u6b21\uff0c\u6628\u5929\uff0c\u8fd8\u542c\u5979\u4eec\u8bf4\uff0c\u4f60\u6765\u4e86\u5c31\u6446\u70b9\u8138\u8272\u7ed9\u4f60\u770b\u770b\u2026\u2026\u201d \u3000\u3000\u201c\u8981\u662f\u4eca\u5929\u4e0d\u6765\u5462\uff1f\u2026\u2026\u201d\u6c5f\u67ab\u4f4e\u58f0\u9053\uff1a\u201c\u5979\u4eec\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u201c\u660e\u5929\uff0c\u5979\u4eec\u5c31\u8981\u9a82\u4f60\u4e00\u756a\u2026\u2026\u201d\u9752\u98ce\u7b11\u9053\uff1a\u201c\u660e\u5929\u518d\u4e0d\u6765\uff0c\u90a3\u5c31\u95ee\u9898\u5927\u4e86\u3002\u201d \u3000\u3000\u201c\u600e\u4e48\u6837\u5462\uff1f\u201d \u3000\u3000\u201c\u5979\u4eec\u4f1a\u5b88\u5728\u5ead\u9662\u548c\u697c\u95e8\u53e3\u5904\uff0c\u2026\u2026\u201d\u9752\u51e4\u9053\uff1a\u201c\u8981\u4f60\u51ed\u4ed7\u6b66\u529f\u6253\u8fdb\u6765\u3002\u201d \u3000\u3000\u6c5f\u67ab\u4e00\u4f38\u820c\u5934\uff0c\u9053\uff1a\u201c\u4eca\u5929\u53ef\u8fd8\u7b97\u6765\u7684\u5de7\u554a\uff01\u201d \u3000\u3000\u201c\u522b\u8ddf\u5979\u4eec\u4e00\u822c\u89c1\u8bc6\uff0c\u6211\u77e5\u4f60\u6700\u5fd9\u7684\u5f88\uff0c\u4eca\u591c\u6765\u770b\u6211\uff0c\u5fc5\u7136\u662f\u6709\u5f88\u591a\u4e8b\uff0c\u60f3\u548c\u6211\u5546\u91cf\uff1f\u201d \u3000\u3000\u201c\u8fd8\u6709\u4e00\u4ef6\u4e8b\uff0c\u4eca\u591c\u6211\u60f3\u7559\u4e0b\u6765\uff0c\u8d24\u59bb\u53ef\u80af\u5141\u8bb8\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u53ef\u4ee5\uff0c\u65e2\u4ee5\u8eab\u76f8\u8bb8\uff0c\u4f55\u4e8b\u4e0d\u53ef\u4e3a\u541b\u4ed8\uff1f\u2026\u2026\u201d\u9752\u51e4\u7b11\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u4f60\u90a3\u4e48\u4e00\u4e2a\u5fd9\u6cd5\uff0c\u600e\u53ef\u80fd\u62bd\u6687\u5e94\u4ed8\u8fd9\u513f\u5973\u79c1\u60c5\u5462\uff1f\u201d \u3000\u3000\u201c\u4f60\u600e\u4e48\u77e5\u9053\u6211\u5f88\u5fd9\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u8fd9\u51e0\u5929\uff0c\u6211\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u201c\u5e94\u4ed8\u5185\u5bab\u4e00\u7cfb\u4e2d\u4eba\uff0c\u5df2\u591f\u4f60\u8f9b\u82e6\u4e86\uff0c\u4f55\u51b5\uff0c\u8fd8\u6709\u4e00\u53ea\u5929\u9e45\u98de\u6765\u6405\u548c\u2026\u2026\u201d\u9752\u51e4\u7b11\u4e00\u7b11\u53c8\u9053\uff1a\u201c\u6211\u76f8\u4fe1\uff0c\u8fd1\u65e5\u4e4b\u5185\uff0c\u5fc5\u6709\u5927\u6279\u9ad8\u624b\u8d76\u6765\uff0c\u4e13\u53f8\u5bf9\u4ed8\u4f60\u7684\u5de5\u4f5c\u3002\u201d \u3000\u3000\u6c5f\u67ab\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\u7684\uff1f\u201d \u3000\u3000\u201c\u5509\uff01\u6211\u4eec\u5df2\u662f\u592b\u59bb\uff0c\u4e3a\u59bb\u600e\u80fd\u6545\u5f04\u7384\u865a\uff0c\u6211\u5c31\u53ea\u597d\u5b9e\u8bdd\u5b9e\u8bf4\u4e86\uff01\u2026\u2026\u201d \u3000\u3000\u9752\u98ce\u63a2\u9996\u5411\u5916\u77a7\u4e86\u4e00\u773c\uff0c\u624d\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u738b\u5b37\u3001\u674e\u5b37\uff0c\u90fd\u4e0d\u5207\u9053\u6211\u6709\u8fd9\u4e2a\u672c\u9886\uff0c\u4f60\u662f\u7b2c\u4e00\u4e2a\u77e5\u9053\u7684\u4eba\uff0c\u5343\u4e07\u4e0d\u53ef\u6cc4\u6f0f\u51fa\u53bb\u3002\u201d \u3000\u3000\u6c5f\u67ab\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\u672c\u9886\u3002\u201d \u3000\u3000\u201c\u55c5\u89c9\uff01\u6211\u80fd\u5728\u4e94\u4e08\u4e4b\u5185\uff0c\u5206\u8fa8\u51fa\u4e00\u4e2a\u4eba\u8eab\u4e0a\u7684\u4f53\u9999\uff0c\u4f60\u60f3\u60f3\u770b\u5427\uff01\u6211\u5728\u4ec0\u4e48\u65f6\u95f4\uff0c\u77e5\u9053\u4e86\u4f60\u7684\u8eab\u4efd\u3002\u201d \u3000\u3000\u6c5f\u67ab\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u5929\u738b\u95e8\u4e2d\uff0c\u6709\u5982\u6b64\u7684\u4eba\u624d\uff0c\u592a\u53ef\u6015\u4e86\uff0c\u4efb\u4f55\u7684\u6613\u672f\u6cd5\uff0c\u90fd\u5728\u4f60\u9762\u524d\u5931\u53bb\u4e86\u6548\u7528\uff1f\u201d \u3000\u3000\u201c\u522b\u90a3\u4e48\u81ea\u827e\u81ea\u6028\u7684\uff0c\u59be\u8eab\u8fd9\u4e00\u70b9\u8d85\u8d8a\u4ed6\u4eba\u7684\u672c\u9886\uff0c\u5df2\u4e3a\u4e08\u592b\u6240\u7528\u2026\u2026\u201d\u9752\u51e4\u7b11\u9053\uff1a \u3000\u3000\u201c\u6b64\u523b\uff0c\u53ea\u8981\u8d31\u59be\u5728\u541b\u8eab\u4fa7\uff0c\u4efb\u4f55\u9ad8\u660e\u6740\u624b\uff0c\u4e5f\u522b\u60f3\u6f5c\u8fd1\u4f60\u4e94\u4e08\u4e4b\u5185\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\uff0c\u4f60\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u4e0d\u80fd\u957f\u5728\u4f60\u8eab\u4fa7\uff0c\u662f\u5417\uff1f\u201d \u3000\u3000\u201c\u5bf9\u5440\uff01\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u60f3\u5230\u5185\u5bab\u4e2d\u6d3e\u51fa\u516d\u5927\u6740\u624b\uff0c\u6765\u957f\u5b89\u53d6\u4ed6\u6027\u547d\uff0c\u5982\u6709\u9752\u51e4\u8fd9\u4e2a\u4eba\u5b88\u5728\u8eab\u4fa7\uff0c\u968f\u65f6\u53ef\u4ee5\u4fa6\u77e5\u6709\u4eba\u6b3a\u8fd1\u8eab\u6765\uff0c\u90a3\u624d\u662f\u5b89\u5168\u7684\u4fdd\u62a4\u3002 \u3000\u3000\u201c\u8fd9\u5c31\u53ea\u597d\u8bf7\u541b\u81ea\u91cd\u4e86\u2026\u2026\u201d\u9752\u51e4\u7b11\u4e00\u7b11\u53c8\u9053\uff1a\u201c\u518d\u8bf4\uff0c\u6211\u5c31\u7b97\u4e0d\u8ba1\u8f83\u5929\u738b\u95e8\u7684\u8ffd\u6740\uff0c\u5b88\u5728\u4f60\u7684\u8eab\u4fa7\uff0c\u53ea\u6015\u4e5f\u4f1a\u788d\u4f60\u7684\u5927\u4e8b\uff0c\u7b2c\u4e00\u4e2a\uff0c\u6885\u82b1\u5c31\u4e0d\u4f1a\u7f62\u4f11\uff0c\u5979\u53ef\u4ee5\u5bb9\u5fcd\u4f60\u53ca\u62e5\u522b\u7684\u5973\u4eba\uff0c\u53ef\u4e0d\u4e00\u5b9a\u4f1a\u5bb9\u5f97\u4e0b\u6211\uff0c\u4f55\u51b5\uff0c\u5f71\u54cd\u6240\u53ca\uff0c\u4f60\u4e5f\u65e0\u6cd5\u518d\u65bd\u5c55\u5947\u672f\uff0c\u53bb\u6355\u6349\u522b\u7684\u5973\u4eba\u3002\u201d \u3000\u3000\u6c5f\u67ab\u53ea\u542c\u5f97\u5934\u76ae\u53d1\u70b8\uff0c\u8fd9\u9752\u51e4\uff0c\u867d\u7136\u8bf4\u7684\u542b\u84c4\uff0c\u4f46\u5979\u597d\u50cf\u770b\u5230\u4e86\u5f88\u591a\u4e8b\u60c5\u4e00\u6837\uff1f\u4e0d\u7981\u53f9\u606f\u4e00\u58f0\uff1a\u9053\uff1a\u201c\u9752\u51e4\uff0c\u4f60\u597d\u50cf\u5728\u6697\u4e2d\u76d1\u89c6\u4e86\u6211\u7684\u884c\u52a8\u3002\u201d \u3000\u3000\u201c\u8a00\u91cd\u4e86\uff0c\u592b\u541b\uff0c\u6709\u4e24\u6b21\u5728\u6697\u4e2d\u4fdd\u62a4\u4f60\uff0c\u2026\u2026\u201d\u9752\u51e4\u7b11\u9053\uff0c\u201c\u8ba9\u6211\u53d1\u73b0\u4e86\u4e0d\u5c11\u4e8b\u60c5\uff0c\u4e0d\u8fc7\uff0c\u4f60\u653e\u5fc3\uff0c\u8eab\u4e3a\u6b63\u5ba4\uff0c\u6211\u7684\u6c14\u5ea6\uff0c\u7edd\u975e\u4e00\u822c\u7684\u5c0f\u59be\u53ef\u6bd4\uff0c\u6211\u8bf4\u8fc7\u4e0d\u7ba1\u4f60\uff0c\u7edd\u4e0d\u4f1a\u4e2d\u9014\u53d8\u5366\u3002\u201d \u3000\u3000\u6c5f\u67ab\u5fc3\u4e2d\u660e\u767d\uff0c\u518d\u8981\u6b3a\u9a97\uff0c\u771f\u53ef\u80fd\u60f9\u607c\u9752\u51e4\u7684\uff0c\u53ea\u597d\u4ece\u5b9e\u62db\u6765\u3002 \u3000\u3000\u4e00\u76f4\u8bf4\u5230\u6885\u82b1\u5df2\u6697\u4e2d\u5f52\u964d\uff0c\u5185\u5bab\u6d3e\u51fa\u4e86\u516d\u5927\u6740\u624b\u6765\u5bf9\u4ed8\u4ed6\uff0c\u56db\u5b63\u82b1\u5a62\u4e2d\uff0c\u83f1\u82b1\u624d\u662f\u8f6c\u8fd0\u4ee4\u8c15\uff0c\u638c\u63e1\u5404\u79cd\u8baf\u606f\u7684\u4eba\uff0c\u6885\u82b1\u8981\u4ed6\u902e\u4f4f\u83f1\u82b1\uff0c\u5f53\u53ef\u4fa6\u77e5\u5f88\u591a\u7684\u673a\u5bc6\u6d88\u606f\u3002 \u3000\u3000\u9752\u51e4\u542c\u5f97\u5f88\u7528\u5fc3\uff0c\u4e5f\u5f88\u5165\u795e\uff0c\u6c5f\u67ab\u8fd9\u4e00\u756a\u8be6\u7ec6\u8bf4\u660e\uff0c\u4e2a\u4e2d\u6709\u4e00\u5927\u534a\u90fd\u662f\u5979\u4e0d\u77e5\u9053\u7684\u4e8b\u60c5\u3002 \u3000\u3000\u201c\u771f\u7684\u662f\u7d2f\u574f\u4f60\u4e86\u2026\u2026\u201d\u9752\u51e4\u65e0\u9650\u540c\u60c5\u5730\u8bf4\u9053\uff1a\u201c\u6211\u8eab\u4e3a\u4eba\u59bb\uff0c\u5374\u4e0d\u80fd\u5802\u5802\u6b63\u6b63\u7684\u7ad9\u5728\u4f60\u8eab\u65c1\u5e2e\u52a9\u4f60\uff0c\u771f\u662f\u4e00\u5927\u61be\u4e8b\u4e86\uff01\u201d \u3000\u3000\u201c\u4e0d\u7528\uff0c\u9752\u51e4\uff0c\u4f60\u73b0\u5728\u7684\u8eab\u4efd\uff0c\u7ed9\u6211\u7684\u5e2e\u52a9\u66f4\u5927\uff0c\u66f4\u591a\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u95ee\u9898\u662f\u4f60\u3002 \u3000\u3000\u738b\u5b37\u3001\u674e\u5b37\uff0c\u80af\u7ed9\u4f60\u591a\u5927\u5e2e\u52a9\uff0c\u5982\u82e5\u4f60\u6b63\u9762\u53cd\u6297\u5929\u738b\uff0c\u4ed6\u4f1a\u600e\u4e48\u60e9\u7f5a\u4f60\uff0c\u4f60\u6709\u6ca1\u6709\u673a\u4f1a\u901a\u77e5\u6211\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u4e5f\u6b63\u5728\u601d\u7d22\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u8001\u5b9e\u8bf4\uff0c\u4e09\u4e94\u65e5\u5185\u5929\u738b\u5c31\u53ef\u80fd\u9a7e\u4e34\u957f\u5b89\uff0c\u4f60\u5982\u8fd9\u4e24\u5929\u8fd8\u4e0d\u6765\uff0c\u6211\u771f\u7684\u8981\u53bb\u627e\u4f60\u4e86\uff0c\u73b0\u5728\uff0c\u6211\u4eec\u6b63\u597d\u7814\u7a76\u4e00\u4e0b\u5982\u4f55\u5904\u7406\u8fd9\u51e0\u4ef6\u5927\u4e8b\uff1f\u2026\u2026\u201d\u9752\u51e4\u4f4e\u58f0\u9053\uff1a\u201c\u738b\u5b37\u3001\u674e\u5b37\uff0c\u4e5f\u5df2\u5377\u5165\u4e86\u8fd9\u4ef6\u4e8b\u4e2d\uff0c\u4e24\u4e2a\u4eba\u6b66\u529f\u5353\u7edd\uff0c\u662f\u4e24\u4e2a\u5f88\u597d\u7684\u5e2e\u624b\uff0c\u4f60\u53bb\u8bf7\u5979\u4eec\u8fc7\u6765\uff0c\u8c08\u8c08\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u8bb0\u7740\uff0c\u8981\u7559\u7ed9\u6211\u8bf4\u8bdd\u7684\u65f6\u95f4\uff0c\u6211\u4f1a\u628a\u4e00\u5207\u7684\u7f6a\u8fc7\uff0c\u5957\u5728\u5979\u4eec\u5934\u4e0a\uff0c\u81f3\u5c11\uff0c\u4e5f\u8981\u5979\u4eec\u81ea\u89c9\u6d89\u8db3\u6df1\u9677\uff0c\u5df2\u7ecf\u65e0\u6cd5\u81ea\u62d4\u4e86\u3002\u201d \u3000\u3000\u201c\u4f60\u5982\u6b64\u63a8\u8d5e\u4e24\u4eba\uff0c\u60f3\u6765\uff0c\u5979\u4eec\u5fc5\u6709\u7279\u6b8a\u7684\u6b66\u529f\u9020\u8be3\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u7ad9\u8d77\u8eab\u5b50\uff0c\u9053\uff1a\u201c\u597d\u5427\uff01\u6211\u53bb\u8bf7\u5979\u4eec\u6765\u3002\u201d \u3000\u3000\u9752\u51e4\u4e00\u628a\u6293\u4f4f\u4e86\u6c5f\u67ab\u7684\u53f3\u624b\uff0c\u8d34\u5728\u81ea\u5df1\u7684\u8138\u4e0a\uff0c\u9053\uff1a\u201c\u6c5f\u90ce\uff0c\u5c0f\u5fc3\u4e00\u4e9b\uff0c\u597d\u8bdd\u591a\u8bb2\uff0c\u591a\u966a\u5c0f\u5fc3\uff0c\u5343\u4e07\u4e0d\u53ef\u610f\u6c14\u7528\u4e8b\uff0c\u4e0d\u8bba\u4f60\u53d7\u5230\u4efb\u4f55\u59d4\u5c48\uff0c\u8d31\u59be\u90fd\u4f1a\u8865\u5c1d\uff0c\u4e3a\u4e86\u6211\uff0c\u628a\u5979\u4eec\u62c9\u8fc7\u6765\u3002\u201d \u3000\u3000\u6c5f\u67ab\u53f9\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6211\u4f1a\u5168\u529b\u4ee5\u8d74\uff0c\u2026\u2026\u201d \u3000\u3000\u9752\u51e4\u7f13\u7f13\u7ad9\u8d77\u8eab\u5b50\uff0c\u628a\u8138\u513f\u8d34\u5728\u4e86\u6c5f\u67ab\u7684\u524d\u80f8\u4e0a\uff0c\u9053\uff1a\u201c\u5979\u4eec\u9a82\u4f60\uff0c\u4f60\u4e5f\u8981\u5fcd\u4e0b\u6765\uff0c\u5979\u4eec\u9a82\u4f60\u4e00\u53e5\uff0c\u4f60\u53ef\u9a82\u6211\u5341\u53e5\u2026\u2026\uff0c\u201d \u3000\u3000\u201c\u6211\u61c2\u4e86\uff0c\u5979\u4eec\u9a82\uff0c\u6211\u4e0d\u8fd8\u53e3\uff0c\u5979\u4eec\u6253\uff0c\u6211\u4e0d\u8fd8\u624b\uff0c\u8fd9\u603b\u53ef\u4ee5\u4e86\u5427\u2026\u2026\u201d\u6c5f\u67ab\u4f4e\u58f0\u56de\u7b54\u5a07\u59bb\u3002 \u3000\u3000\u201c\u8fd8\u4e0d\u591f\u2026\u2026\u201d\u9752\u98ce\u6492\u5a07\u7684\u7b11\u7740\u3002 \u3000\u3000\u201c\u8fd8\u4e0d\u591f\u554a\uff01\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u96be\u9053\u8981\u5979\u4eec\u6740\u4e86\u6211\u2026\u2026\u201d \u3000\u3000\u201c\u5979\u4eec\u4e0d\u4f1a\u6740\u4f60\uff0c\u56e0\u4e3a\uff0c\u5979\u4eec\u597d\u559c\u6b22\u4f60\uff0c\u6211\u8981\u4f60\u50cf\u6211\u5bf9\u4f60\u4e00\u6837\u3002\u201d \u3000\u3000\u201c\u600e\u4e48\u6837\u554a\uff01\u2026\u2026\u201d \u3000\u3000\u201c\u5fc5\u8981\u65f6\uff0c\u5bf9\u5979\u4eec\u64a4\u4e2a\u5a07\u3002\u2026\u2026\u201d \u3000\u3000\u201c\u4ec0\u4e48\uff1f\u2026\u2026\u201d\u6c5f\u67ab\u50cf\u662f\u7a81\u7136\u88ab\u4eba\u7528\u9488\u523a\u4e86\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u6211\u600e\u4e48\u4f1a\uff1f\u201d \u3000\u3000\u201c\u90a3\u4f60\u5c31\u65bd\u7528\u2018\u60d1\u5fc3\u672f\u2019\u5427\uff01\u603b\u4e4b\uff0c\u8981\u628a\u5979\u4eec\u62c9\u8fc7\u6765\uff0c\u5979\u4eec\u662f\u5f3a\u654c\uff0c\u4e5f\u662f\u6700\u597d\u7684\u52a9\u624b\u3002\u2026\u2026\u201d\u9752\u51e4\u9053\uff1a\u201c\u6267\u5927\u4e49\uff0c\u4e0d\u62d8\u5c0f\u8282\uff0c\u6211\u5c31\u80fd\u7741\u7740\u773c\uff0c\u770b\u4f60\u548c\u522b\u7684\u5973\u4eba\u9b3c\u6df7\uff0c\u4f60\u5c31\u4e0d\u80fd\u4e3a\u6211\u5fcd\u53d7\u4e00\u70b9\u59d4\u5c48\u5417\uff1f\u201d \u3000\u3000\u201c\u597d\u597d\u597d\uff0c\u6211\u7b54\u5e94\u5c31\u662f\u2026\u2026\u201d \u3000\u3000\u201c\u6c5f\u90ce\uff0c\u522b\u8bf4\u7684\u90a3\u59d4\u5c48\uff0c\u8fd9\u4ef6\u4e8b\u8bf4\u6765\u8bf4\u53bb\uff0c\u4e5f\u8fd8\u662f\u4e3a\u4e86\u4f60\u2026\u2026\u201d \u3000\u3000\u201c\u4e3a\u4e86\u6211\u2026\u2026\u201d\u6c5f\u67ab\u5fc3\u4e2d\u5927\u611f\u4e0d\u670d\uff0c\u5fd6\u9053\uff1a\u201c\u6211\u5b81\u53ef\u5979\u4eec\u662f\u6211\u7684\u654c\u4eba\uff0c\u4e5f\u4e0d\u613f\u8fd9\u6837\u7684\u59d4\u5c48\u81ea\u5df1\uff1a\u201d \u3000\u3000\u201c\u6c5f\u90ce\uff0c\u4e0d\u8981\u5fc3\u4e2d\u4e0d\u670d\uff0c\u5979\u4eec\u4e24\u4e2a\u4eba\u5982\u4e0d\u5408\u4f5c\uff0c\u6211\u7edd\u65e0\u6cd5\u9003\u8fc7\u5929\u738b\u7684\u8feb\u5bb3\uff0c\u6211\u4eec\u5df2\u6709\u592b\u59bb\u7684\u540d\u4efd\uff0c\u5c31\u7b97\u4f60\u6709\u6234\u9876\u7eff\u5e3d\u5b50\u7684\u6db5\u517b\uff0c\u6211\u53ef\u4e0d\u613f\u80cc\u5f03\u4e86\u8bb8\u4e0b\u7684\u8a93\u8a00\u3002\u201d \u3000\u3000\u6c5f\u67ab\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u5979\u4eec\u80af\u5e2e\u5fd9\u5c31\u6ca1\u6709\u8fd9\u79cd\u5371\u9669\u4e86\u5417\uff1f\u201d \u3000\u3000\u770b\u6765\uff0c\u7eff\u5e3d\u5b50\u7684\u538b\u529b\uff0c\u5341\u5206\u5f3a\u5927\uff0c\u50cf\u6c5f\u67ab\u8fd9\u6837\u7684\u4eba\uff0c\u4e5f\u4f1a\u653e\u5728\u5fc3\u4e0a\uff0c\u80fd\u9003\u907f\u5c31\u5c3d\u91cf\u7684\u9003\u907f\u3002 \u3000\u3000\u201c\u5f53\u7136\uff0c\u5979\u4eec\u8054\u624b\uff0c\u81f3\u5c11\u53ef\u4ee5\u62d2\u6321\u5929\u738b\u4e94\u5343\u62db\u4ee5\u4e0a\u2026\u2026\u201d\u9752\u51e4\u9053\uff1a\u201c\u5979\u4eec\u7ec3\u7684\u706d\u7edd\u795e\u638c\uff0c\u4e5f\u53ef\u4ee5\u8981\u5929\u738b\u7684\u6027\u547d\u3002\u201d \u3000\u3000\u201c\u5373\u662f\u5982\u6b64\uff0c\u6211\u5c0f\u5fc3\u4ece\u4e8b\u5c31\u662f\u3002\u201d \u3000\u3000\u770b\u6c5f\u67ab\u5df2\u88ab\u5b8c\u5168\u8bf4\u670d\uff0c\u9752\u98ce\u624d\u653e\u4e86\u624b\uff0c\u4efb\u4ee4\u6c5f\u67ab\u79bb\u53bb\u3002 \u3000\u3000\u738b\u5b37\u3001\u674e\u5b37\uff0c\u6b63\u5750\u5728\u623f\u4e2d\u751f\u6c14\uff0c\u4e24\u5f20\u8138\u4e00\u822c\u7684\u51b0\u51b7\u96be\u770b\u3002 \u3000\u3000\u6c5f\u67ab\u8fdb\u95e8\u5c31\u662f\u4e00\u4e2a\u957f\u63d6\uff0c\u9053\uff1a\u201c\u665a\u8fdb\u6c5f\u67ab\uff0c\u7279\u6765\u5411\u4e24\u4f4d\u5b37\u5b37\u8bf7\u5b89\u3002\u201d \u3000\u3000\u738b\u5b37\u770b\u4e86\u6c5f\u67ab\u4e00\u773c\uff0c\u51b7\u51b7\u7684\u8f6c\u8fc7\u5934\u53bb\u3002 \u3000\u3000\u674e\u5b37\u5374\u8fde\u770b\u4e5f\u4e0d\u770b\u6c5f\u67ab\u4e00\u773c\u3002 \u3000\u3000\u6c5f\u67ab\u50f5\u5728\u90a3\u91cc\u4e86\uff0c\u5fc3\u4e2d\u5374\u6697\u6697\u5fd6\u9053\uff1a\u201c\u6211\u5982\u65bd\u5c55\u5947\u529f\uff0c\u2018\u60d1\u5fc3\u672f\u2019\u4e0d\u77e5\u9053\u4f1a\u6709\u4e9b\u4ec0\u4e48\u540e\u679c\uff0c\u8bc1\u8bf8\u4fae\u82b1\u7684\u60c5\u5f62\uff0c\u53ef\u80fd\u662f\u5e38\u65e5\u60f3\u5fc6\uff0c\u6709\u8fd9\u4e48\u4e24\u4e2a\u4eba\u7ea0\u7f20\u7740\u6211\uff0c\u6c38\u8fdc\u4e0d\u8981\u60f3\u5b89\u9759\u4e0b\u6765\u4e86\u2026\u2026\u201d \u3000\u3000\u5fc3\u4e2d\u76d8\u7b97\u7ed3\u679c\uff0c\u8fd9\u60d1\u5fc3\u672f\u662f\u4e07\u4e07\u4e0d\u80fd\u65bd\u5c55\uff0c\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u63a5\u9053\uff1a\u201c\u665a\u8fdb\u8fd1\u65e5\u4e2d\u8fde\u9047\u9ebb\u70e6\uff0c\u5e38\u5e38\u95f9\u7684\u795e\u9b42\u4e0d\u5b89\uff0c\u6240\u4ee5\u5c11\u6765\u95ee\u5019\uff0c\u8fd8\u8bf7\u4e24\u4f4d\u5b37\u5b37\u539f\u8c05\u3002\u201d \u3000\u3000\u738b\u5b37\u51b7\u54fc\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\uff0c\u4f60\u51b7\u843d\u4e86\u51e4\u513f\uff0c\u5979\u867d\u7136\u4e0d\u8bf4\u51fa\u6765\uff0c\u53ef\u662f\uff0c\u6211\u77e5\u9053\u5979\u5fc3\u4e2d\u96be\u8fc7\uff01\u201d \u3000\u3000\u674e\u5b37\u9053\uff1a\u201c\u51e4\u513f\uff0c\u53ef\u662f\u6211\u4eec\u7684\u5fc3\u809d\u5b9d\u8d1d\uff0c\u8c01\u4e5f\u4e0d\u8bb8\u4f24\u5bb3\u5230\u5979\u3002\u201d \u3000\u3000\u201c\u662f\u662f\u662f\uff01\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u665a\u8fdb\u77e5\u9053\u4e86\uff0c\u4ee5\u540e\uff0c\u5b9a\u5f53\u62bd\u6687\u591a\u6765\u63a2\u671b\u51e4\u59d1\u5a18\u2026\u2026\u201d \u3000\u3000\u201c\u55ef\uff01\u8fd9\u8fd8\u50cf\u53e5\u4eba\u8bdd\u2026\u2026\u201d\u738b\u5b37\u8bf4\uff0c\u201c\u4f60\u8fd1\u65e5\u8fde\u9047\u9ebb\u70e6\uff0c\u90fd\u662f\u4e9b\u4ec0\u4e48\u9ebb\u70e6\uff1f\u201d \u3000\u3000\u201c\u8fd8\u4e0d\u662f\u4e00\u4e9b\u81ea\u8d1f\u672c\u9886\u9ad8\u5f3a\u7684\u4eba\uff0c\u627e\u4e0a\u95e8\u6765\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff0c\u201c\u4e0d\u8fc7\uff0c\u90fd\u88ab\u665a\u8fdb\u7ed9\u6253\u53d1\u8d70\u4e86\uff0c\u603b\u7b97\u662f\u6709\u60ca\u65e0\u9669\u3002\u201d \u3000\u3000\u201c\u4ee5\u540e\uff0c\u518d\u9047\u4e0a\u8fd9\u79cd\u9ebb\u70e6\u2026\u2026\u201d\u674e\u5b37\u9053\uff1a\u201c\u53ea\u8981\u6765\u544a\u8bc9\u51e4\u513f\u4e00\u58f0\uff0c\u5979\u4f1a\u5e2e\u4f60\u6253\u53d1\u3002\u201d \u3000\u3000\u201c\u665a\u8f88\u8bb0\u4e0b\u4e86\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u9002\u624d\u51e4\u513f\u548c\u665a\u8f88\u8c08\u5230\u4e86\u4e00\u6869\u5f88\u4e3a\u96be\u7684\u4e8b\u60c5\uff0c\u51e4\u59d1\u5a18\u65e0\u6cd5\u4f5c\u4e3b\uff0c\u60f3\u8bf7\u4e24\u4f4d\u5b37\u5b37\u8fc7\u53bb\uff0c\u4ee3\u5979\u62ff\u4e2a\u4e3b\u610f\uff01\u201d \u3000\u3000\u738b\u5b37\u3001\u674e\u5b37\u5bf9\u671b\u4e86\u4e00\u773c\uff0c\u738b\u5b37\u5ffd\u7136\u53f9\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u90a3\u4e00\u5b9a\u662f\u4e00\u4ef6\u6bd4\u5929\u8fd8\u5927\u7684\u56f0\u96be\u4e86\uff1f\u201d \u3000\u3000\u674e\u5b37\u4f4e\u58f0\u95ee\u9053\uff1a\u201c\u6211\u4eec\u53bb\u4e0d\u53bb\u5462\uff1f\u201d \u3000\u3000\u738b\u5b37\u9053\uff1a\u201c\u4e0d\u53bb\u6210\u5417\uff1f\u6211\u4eec\u7ec3\u4e86\u8fd9\u4e00\u8eab\u706d\u7edd\u795e\u529f\uff0c\u8fd9\u4e00\u751f\u522b\u6307\u671b\u751f\u513f\u80b2\u5973\u4e86\uff0c\u4e5f\u53ea\u6709\u9760\u51e4\u513f\u548c\u8fd9\u5c0f\u5b50\u5c3d\u70b9\u5b5d\u5fc3\u4e86\u3002\u201d \u3000\u3000\u201c\u8bf4\u7684\u4e5f\u662f\u554a\uff01\u5c31\u7b97\u662f\u5929\u584c\u4e0b\u6765\uff0c\u54b1\u4eec\u4e5f\u5f97\u5e2e\u4ed6\u4eec\u625b\u4e00\u625b\u554a\uff01\u201d\u674e\u5b37\u9053\uff1a\u201c\u90a3\u5c31\u8fc7\u53bb\u5427\uff01\u201d \u3000\u3000\u738b\u5b37\u7ad9\u8d77\u8eab\uff0c\u8f7b\u8f7b\u5728\u6c5f\u67ab\u8138\u4e0a\u4eb2\u4e86\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u542c\u5230\u4e86\u6ca1\u6709\uff0c\u4f60\u4ee5\u540e\u8981\u5bf9\u51e4\u513f\u597d\uff0c\u53ef\u4e5f\u5f97\u5b5d\u987a\u6211\u4eec\u3002\u201d \u3000\u3000\u6c5f\u67ab\u5fc3\u91cc\u76f4\u6253\u9f13\uff0c\u6697\u9053\uff1a\u201c\u8fd9\u4e00\u4e0b\u5e78\u597d\u662f\u4eb2\u5728\u8138\u4e0a\uff0c\u8981\u662f\u88ab\u5979\u4eb2\u5728\u5634\u4e0a\uff0c\u771f\u4e0d\u77e5\u9053\u80fd\u4e0d\u80fd\u5fcd\u53d7\u5f97\u4f4f\uff1f\u201d \u3000\u3000\u5fc3\u6237\u5ff5\u8f6c\uff0c\u53e3\u4e2d\u5e94\u9053\uff1a\u201c\u662f\u662f\uff01\u665a\u8fdb\u65e5\u540e\uff0c\u5b9a\u5f53\u597d\u597d\u7684\u5b5d\u656c\u4e24\u4f4d\u3002\u201d \u3000\u3000\u201c\u7eb5\u662f\u8c0e\u8a00\u4e5f\u5f00\u5fc3\uff0c\u4f60\u5c0f\u5b50\u5c31\u662f\u8fd9\u4e00\u70b9\u8ba8\u4eba\u559c\u6b22\u2026\u2026\u201d\u674e\u5b37\u662f\u6709\u6837\u5b66\u6837\uff0c\u7adf\u7136\u4e5f\u5728\u6c5f\u67ab\u7684\u8138\u4e0a\u4eb2\u4e86\u4e00\u4e0b\uff0c\u63a5\u9053\uff1a\u201c\u8d70\u5427\uff01\u201d \u3000\u3000\u6c5f\u67ab\u4e0d\u6562\u4f38\u624b\u64e6\u8138\uff0c\u53ea\u597d\u8f6c\u8eab\u5e26\u8def\u3002 \u3000\u3000\u770b\u5230\u738b\u5b37\u3001\u674e\u5b37\u8138\u4e0a\u7684\u7b11\u5bb9\uff0c\u9752\u51e4\u653e\u5fc3\u4e86\uff0c\u6c5f\u67ab\u5e76\u672a\u65bd\u5c55\u60d1\u5fc3\u672f\uff0c\u4f46\u5374\u597d\u8a00\u597d\u8bed\u7684\u628a\u4e24\u4eba\u8bf7\u4e86\u6765\u3002 \u3000\u3000\u9752\u51e4\u79fb\u52a8\u5750\u6905\uff0c\u5148\u8ba9\u4e24\u4f4d\u5b37\u5b37\u5750\u4e0b\uff0c\u53c8\u6367\u4e0a\u4e24\u676f\u9999\u8317\uff0c\u624d\u966a\u5728\u4e00\u65c1\u5750\u4e0b\uff0c\u9053\uff1a\u201c\u4e49\u6bcd\u3001\u59e8\u5a18\u3001\u51e4\u513f\u60f3\uff0c\u5929\u738b\u8be5\u6765\u957f\u5b89\u4e86\uff1f\u201d \u3000\u3000\u201c\u662f\u5e94\u8be5\u6765\u4e86\uff0c\u2026\u2026\u201d\u738b\u5b37\u9053\uff1a\u201c\u4ed6\u5df2\u7ecf\u5fcd\u4e86\u4e09\u4e2a\u6708\uff0c\u6211\u770b\u4e5f\u662f\u5fcd\u4e0d\u4e0b\u53bb\u4e86\u3002\u201d \u3000\u3000\u674e\u5b37\u9053\uff1a\u201c\u4e0a\u6b21\u4ed6\u53bb\u65f6\u8bf4\u8fc7\uff0c\u4e8b\u52a1\u7e41\u5fd9\uff0c\u8fd9\u4e00\u6b21\u81f3\u5c11\u8981\u4e09\u4e2a\u6708\uff0c\u624d\u4f1a\u5230\u957f\u5b89\u770b\u4f60\uff0c\u642c\u6307\u5934\u7b97\u4e00\u7b97\uff0c\u518d\u6709\u4e09\u5929\uff0c\u5c31\u4e09\u6708\u671f\u6ee1\uff0c\u7edd\u4e0d\u4f1a\u518d\u62d6\u5ef6\u65f6\u95f4\u3002\u201d \u3000\u3000\u201c\u90a3\u8981\u600e\u4e48\u5462\uff1f\u2026\u2026\u201d\u9752\u51e4\u8bf4\uff0c\u201c\u5b69\u513f\u6101\u90fd\u6101\u6b7b\u4e86\uff01\u201d \u3000\u3000\u738b\u5b37\u9053\uff0c\u201d\u4f60\u544a\u8bc9\u8fd9\u5c0f\u5b50\u6ca1\u6709\uff1f\u201d \u3000\u3000\u76ee\u5149\u51dd\u5728\u6c5f\u67ab\u7684\u8138\u4e0a\u3002 \u3000\u3000\u9752\u51e4\u9053\uff1a\u201c\u8bf4\u662f\u8bf4\u4e86\u4e00\u4e9b\uff0c\u53ea\u662f\u6709\u4e9b\u8bdd\u4e0d\u4fbf\u5f00\u53e3\uff0c\u4e5f\u4e0d\u77e5\u4ed6\u662f\u4e0d\u662f\u542c\u660e\u767d\u4e86\uff1f\u201d \u3000\u3000\u8fd9\u79cd\u4e8b\uff0c\u4e0d\u80fd\u9a6c\u864e\uff0c\u6211\u5f97\u95ee\u4e2a\u6e05\u695a\u2026\u2026\u201d\u674e\u5b37\u76ee\u6ce8\u6c5f\u67ab\uff0c\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\uff0c\u5929\u738b\u5230\u4e86\u957f\u5b89\u4f1a\u53d1\u751f\u4e9b\u4ec0\u4e48\u4e8b\u60c5\uff1f\u201d \u3000\u3000\u8fd9\u4e00\u4e0b\uff0c\u771f\u628a\u6c5f\u67ab\u7ed9\u95ee\u4f4f\u4e86\uff0c\u5fc3\u4e2d\u660e\u660e\u77e5\u6653\uff0c\u4f46\u5374\u662f\u65e0\u6cd5\u56de\u7b54\uff0c\u6c89\u601d\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u51e4\u59d1\u5a18\u8bf4\u7684\u8bed\u8a00\u4e0d\u8be6\uff0c\u5728\u4e0b\u4e5f\u542c\u5f97\u4f3c\u61c2\u975e\u61c2\uff1f\u8fd8\u5f97\u5b37\u5b37\u6307\u6559\u4e86\uff01\u201d \u3000\u3000\u9752\u51e4\u5fc3\u4e2d\u6697\u7b11\u9053\uff1a\u201c\u539f\u6765\uff0c\u4f60\u4e5f\u6709\u4e0d\u80fd\u5f00\u53e3\u4e4b\u82e6\uff0c\u770b\u6765\uff0c\u6211\u8fd9\u4e2a\u59bb\u5b50\u8eab\u4efd\uff0c\u4ed6\u662f\u771f\u5728\u5fc3\u4e2d\u627f\u8ba4\u4e86\u3002\u201d \u3000\u3000\u674e\u5b37\u9053\uff1a\u201c\u4f60\u4f3c\u61c2\u975e\u61c2\uff0c\u90a3\u662f\u61c2\u4e00\u534a\u4e86\uff0c\u8ba9\u8001\u5a46\u5b50\u544a\u8bc9\u4f60\u5427\u3002\u201d \u3000\u3000\u9752\u51e4\u9053\uff1a\u201c\u59e8\u5a18\uff0c\u8fd9\u79cd\u4e8b\uff0c\u4ed6\u77e5\u9053\u4e86\uff0c\u600e\u4e48\u4f1a\u53d7\u5f97\u4e86\u5462\uff1f\u201d \u3000\u3000\u201c\u6709\u4ec0\u4e48\u53d7\u4e0d\u4e86\u7684\u2026\u2026\u201d\u674e\u5b37\u9053\uff1a\u201c\u4f60\u672c\u6765\u5c31\u662f\u5929\u738b\u7684\u4eba\uff0c\u88ab\u8fd9\u5c0f\u5b50\u7ed9\u6a2a\u5200\u593a\u53d7\u4e86\uff0c\u4ed6\u773c\u5df4\u5df4\u8dd1\u6765\u9752\u51e4\u697c\uff0c\u62a2\u4e86\u522b\u4eba\u7684\u5973\u4eba\uff0c\u5fc3\u4e2d\u5e94\u8be5\u6709\u6570\u554a\uff01\u201d \u3000\u3000\u8fd9\u4e00\u4e0b\uff0c\u6c5f\u67ab\u771f\u7684\u7ed9\u95f9\u4e00\u4e2a\u6ee1\u8138\u901a\u7ea2\uff0c\u4f46\u4ed6\u5fc3\u4e2d\u660e\u767d\uff0c\u8fd9\u53ef\u662f\u7d27\u8981\u5173\u53e3\uff0c\u53d1\u4f5c\u4e0d\u5f97\uff0c\u53ea\u597d\uff0c\u54d1\u5b50\u5403\u9ec4\u83b2\uff0c\u82e6\u5728\u5fc3\u5934\u3002 \u3000\u3000\u9752\u51e4\u9053\uff1a\u201c\u4ed6\u4e5f\u77e5\u9053\uff0c\u51e4\u513f\u5df2\u4e0d\u662f\u5973\u513f\u4e4b\u8eab\uff0c\u53ef\u662f\uff0c\u4ed6\u8fd8\u662f\u7b54\u5e94\u4e86\u5a36\u6211\u4e3a\u59bb\u3002\u201d \u3000\u3000\u738b\u5b37\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u4e8b\u60c5\u7684\u56f0\u96be\uff0c\u5c31\u5728\u8fd9\u91cc\u4e86\uff0c\u4f60\u662f\u4e0d\u4fbf\u518d\u966a\u5f85\u5929\u738b\u4e86\uff01\u201d \u3000\u3000\u201c\u7edd\u5bf9\u4e0d\u884c\uff0c\u770b\u6765\uff0c\u51e4\u513f\u53ea\u6709\u6b7b\u8def\u4e00\u6761\u4e86\uff01\u201d \u3000\u3000\u738b\u5b37\u3001\u674e\u5b37\u540c\u65f6\u76b1\u8d77\u4e86\u7709\u5934\uff0c\u6c89\u541f\u4e0d\u8bed\u3002 \u3000\u3000\u6c5f\u67ab\u6709\u4e00\u809a\u5b50\u7684\u8bdd\uff0c\u5374\u4e5f\u4e0d\u77e5\u5982\u4f55\u5f00\u53e3\uff0c\u6b64\u523b\u662f\u8bdd\u5982\u950b\u955d\uff0c\u4e00\u5f00\u53e3\u5c31\u4f1a\u4f24\u4eba\uff0c\u4e3a\u4e86\u6c5f\u6e56\u5927\u5c40\uff0c\u6c5f\u67ab\u53ea\u6709\u5fcd\u4e86\u3002 \u3000\u3000\u8c01\u8981\u5c0f\u5b50\u591a\u98ce\u6d41\uff0c\u8fd9\u5c31\u662f\u98ce\u6d41\u7f6a\u96be\u53d7\uff01 \u3000\u3000\u6c5f\u67ab\u6697\u6697\u6765\u9a82\u81ea\u5df1\uff0c\u5750\u5728\u4e00\u4fa7\u88c5\u50bb\u3002 \u3000\u3000\u738b\u5b37\u957f\u957f\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6c5f\u67ab\uff0c\u4f60\u73b0\u5728\u5e94\u8be5\u662f\u90fd\u660e\u767d\u4e86\uff1f\u201d\u201c\u662f\uff01\u5b8c\u5168\u660e\u767d\u4e86\uff01\u201d\u6c5f\u67ab\u6697\u4e2d\u54ac\u7259\u56de\u7b54\u3002 \u3000\u3000\u201c\u600e\u4e48\u6253\u7b97\u5462\uff1f\u2026\u2026\u201d\u738b\u5b37\u76ef\u7740\u95ee\u4e86\u4e00\u53e5\uff0c\u53ea\u95ee\u5f97\u6c5f\u67ab\u7684\u5168\u8eab\u53d1\u70ed\u3002 \u3000\u3000\u201c\u6211\u770b\uff0c\u8981\u7531\u51e4\u59d1\u5a18\u51b3\u5b9a\u4e86\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u65e0\u53ef\u5948\u4f55\u5730\u8bf4\uff0c\u201c\u6216\u662f\u7531\u4e24\u4f4d\u5b37\u5b37\u4f5c\u4e3b\u4e86\u5462\uff1f\u201d \u3000\u3000\u201c\u6211\u7684\u51b3\u5b9a\u53ea\u6709\u4e00\u4e2a\u2026\u2026\u201d\u9752\u51e4\u9053\uff1a\u201c\u81ea\u7edd\u4e00\u6b7b\uff01\u201d \u3000\u3000\u201c\u4e0d\u80fd\u6b7b\u2026\u2026\u201d\u738b\u5b37\u9053\uff1a\u201c\u6211\u4eec\u5949\u547d\u770b\u5b88\u4f60\uff0c\u4f60\u6b7b\u4e86\u6211\u4eec\u8981\u5982\u4f55\u4ea4\u5f85\uff1f\u201d \u3000\u3000\u201c\u4f46\u5982\u8981\u51e4\u513f\u518d\u5949\u4f8d\u5929\u738b\uff0c\u6211\u662f\u5b81\u6b7b\u4e0d\u4ece\u2026\u2026\u201d \u3000\u3000\u201c\u54b1\u4eec\u6162\u6162\u5546\u91cf\u2026\u2026\u201d\u674e\u5b37\u8bf4\uff0c\u201c\u5929\u738b\u7231\u4f60\u81f3\u6df1\uff0c\u4ed6\u8bf4\u8fc7\u8981\u4f60\u6276\u6b63\u7684\u8bdd\u3002\u201d \u3000\u3000\u201c\u90a3\u662f\u9a97\u4eba\u7684\u2026\u2026\u201d\u9752\u51e4\u9053\uff1a\u201c\u56db\u5927\u5929\u9e45\uff0c\u65e5\u5915\u8ffd\u968f\u5728\u4ed6\u7684\u8eab\u4fa7\uff0c\u966a\u4f8d\u6795\u5e2d\uff0c\u600e\u53ef\u80fd\u628a\u4e00\u9897\u5fc3\u7cfb\u5728\u6211\u7684\u8eab\u4e0a\u5462\uff1f\u201d \u3000\u3000\u738b\u5b37\u9053\uff1a\u201c\u51e4\u513f\uff0c\u6211\u4eec\u4e5f\u77e5\u9053\u4f60\u6df1\u7231\u6c5f\u67ab\uff0c\u4f46\u773c\u4e0b\u65f6\u673a\u592a\u8feb\u6025\uff0c\u4f60\u5982\u80fd\u901a\u6743\u8fbe\u53d8\uff0c\u54b1\u4eec\u591a\u4e86\u4e00\u4e9b\u65f6\u95f4\uff0c\u518d\u60f3\u4e2a\u5b8c\u5584\u4e4b\u7b56\u3002\u201d \u3000\u3000\u201c\u4e49\u6bcd\uff0c\u8fd9\u4e00\u70b9\u51e4\u513f\u96be\u7b54\u5141\uff0c\u6211\u5df2\u7ecf\u662f\u6c5f\u67ab\u7684\u59bb\u5b50\u4e86\uff0c\u600e\u80fd\u518d\u4ee5\u8eab\u4f8d\u4ed6\u4eba\uff1f\u201d \u3000\u3000\u201c\u90a3\u53ea\u6709\u9003\u8d70\u4e86\uff01\u201d\u674e\u5b37\u9053\uff1a\u201c\u5929\u738b\u5230\u6b64\u65f6\uff0c\u65e9\u5df2\u4eba\u53bb\u697c\u7a7a\uff01\u201d \u3000\u3000\u201c\u9003\u54ea\u91cc\u5462\uff1f\u2026\u2026\u201d\u738b\u5b37\u8bf4\uff0c\u201c\u5929\u4e0b\u867d\u5927\uff0c\u53ef\u662f\u5f88\u96be\u627e\u5230\u4e00\u5904\u5b58\u8eab\u4e4b\u5730\u3002\u201d \u3000\u3000\u201c\u5728\u4e0b\u5012\u6709\u4e00\u7b56\u2026\u2026\u201d\u6c5f\u67ab\u8c6a\u6c14\u98ce\u53d1\u5730\u8bf4\uff0c\u201c\u4e24\u4f4d\u5b37\u5b37\u5982\u80fd\u5b89\u6392\u4e00\u573a\u5355\u6253\u72ec\u95e8\uff0c\u665a\u8fdb\u613f\u548c\u5929\u738b\u4e00\u51b3\u751f\u6b7b\u3002\u201d \u3000\u3000\u201c\u597d\u5bb6\u4f19\uff01\u679c\u7136\u662f\u8272\u80c6\u5305\u5929\u554a\uff01\u2026\u2026\u201d\u738b\u5b37\u8bf4\uff0c\u201c\u4f60\u53ef\u77e5\u9053\u5929\u738b\u6b66\u529f\u4f55\u7b49\u9ad8\u5f3a\uff0c\u6325\u624b\u788e\u7891\uff0c\u6307\u900f\u91d1\u94c1\uff0c\u4e00\u8eab\u5947\u529f\uff0c\u5200\u67aa\u4e0d\u5165\uff0c\u4f60\u80fd\u548c\u4ed6\u4e00\u6218\u5417\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u60c5\u52bf\u8feb\u4eba\uff0c\u5c31\u7b97\u662f\u6218\u6b7b\u4e86\uff0c\u4e5f\u53ef\u7791\u76ee\u4e5d\u6cc9\uff01\u201d \u3000\u3000\u201c\u770b\u6765\uff0c\u4ed6\u8fd8\u771f\u6709\u826f\u5fc3\uff0c\u738b\u5b37\uff0c\u54b1\u4eec\u603b\u4e0d\u80fd\u653e\u624b\u4e0d\u7ba1\uff0c\u2026\u2026\u201d\u674e\u5b37\u8bf4\uff0c\u201c\u8ba9\u4ed6\u4eec\u6b89\u60c5\u800c\u6b7b\u5427\u3002\u201d \u3000\u3000\u738b\u5b37\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u7ba1\u5f97\u4e86\u5417\uff1f\u201d \u3000\u3000\u201c\u7ba1\u4e0d\u4e86\u4e5f\u5f97\u7ba1\u54ea\uff01\u2026\u2026\u201d\u674e\u5b37\u9053\uff1a\u201c\u5e10\u662f\u4e00\u7b14\uff0c\u4f46\u5374\u6709\u4e24\u79cd\u7b97\u6cd5\uff0c\u51e4\u513f\u6b7b\u4e86\uff0c\u4f60\u8ba4\u4e3a\u5929\u738b\u4f1a\u653e\u8fc7\u6211\u4eec\u5417\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u4e0d\u4f1a\u2026\u2026\u201d\u738b\u5b37\u8bf4\uff0c\u201c\u4f46\u76ee\u4e0b\u60c5\u52bf\uff0c\u9664\u4e86\u6211\u4eec\u633a\u8eab\u800c\u51fa\uff0c\u4fdd\u62a4\u51e4\u513f\uff0c\u4e0d\u501f\u548c\u5929\u738b\u4e00\u6218\u4e4b\u5916\uff0c\u8fd8\u6709\u4ec0\u4e48\u529e\u6cd5\u53ef\u60f3\uff1f\u201d \u3000\u3000\u201c\u5408\u6211\u4eec\u4e24\u4eba\u4e4b\u529b\uff0c\u4e0d\u77e5\u662f\u4e0d\u662f\u5929\u738b\u654c\u624b\uff1f\u2026\u2026\u201d\u674e\u5b37\u8bf4\uff0c\u201c\u5c31\u7b97\u4e0d\u662f\u654c\u624b\uff0c\u4e5f\u53ef\u4ee5\u6321\u4ed6\u4e2a\u4e09\u4e94\u5341\u62db\u5427\uff1f\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u662f\u80dc\u8d1f\u7684\u95ee\u9898\uff0c\u800c\u662f\u6211\u4eec\u8be5\u4e0d\u8be5\u548c\u5929\u738b\u52a8\u624b\uff1f\u201d\u738b\u5b37\u9eef\u7136\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u662f\u51fa\u8eab\u5929\u738b\u95e8\u7684\u4eba\uff0c\u4ed6\u662f\u6211\u4eec\u7684\u4e3b\u5b50\u3002\u201d \u3000\u3000\u674e\u5b37\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u53ea\u6709\u5750\u4ee5\u5f85\u6bd9\u4e86\u3002\u201d \u3000\u3000\u6c5f\u67ab\u8f7b\u8f7b\u5401\u4e00\u53e3\u6c14\uff0c\u51c6\u5907\u6b7c\u53e3\uff0c\u4f46\u5374\u88ab\u9752\u51e4\u4ee5\u76ee\u5149\u963b\u6b62\uff0c\u53ea\u597d\u5fcd\u4e0b\u3002 \u3000\u3000\u5ba4\u4e2d\u4e00\u7247\u9759\u5bc2\uff0c\u826f\u4e45\u4e4b\u540e\uff0c\u738b\u5b37\u624d\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u4f11\u606f\uff0c\u518d\u8ba9\u6211\u60f3\u4e00\u591c\uff0c\u770b\u770b\u6709\u6ca1\u4e24\u5168\u5176\u7f8e\u7684\u529e\u6cd5\u3002\u201d \u3000\u3000\u53ea\u542c\u9ad8\u5927\u6597\u7684\u58f0\u97f3\uff0c\u9065\u9065\u4f20\u4e86\u8fc7\u6765\uff0c\u9053\uff1a\u201c\u9ad8\u5927\u6597\u6709\u8981\u4e8b\u60c5\u89c1\u51e4\u59d1\u5a18\u3002\u201d \u3000\u3000\u8fd9\u662f\u4ece\u672a\u6709\u8fc7\u7684\u4e8b\uff0c\u9752\u51e4\uff0c\u738b\u5b37\u3001\u674e\u5b37\uff0c\u5168\u90fd\u542c\u5f97\u4e00\u6014\u3002 \u3000\u3000\u674e\u5b37\u9053\uff1a\u201c\u6211\u4e0b\u53bb\u770b\u770b\uff0c\u8fd9\u5c0f\u5b50\u6df1\u66f4\u534a\u591c\u7684\u8dd1\u6765\u6253\u6270\uff0c\u5982\u679c\u4e0d\u662f\u91cd\u5927\u7684\u4e8b\uff0c\u8001\u8eab\u8981\u597d\u597d\u7684\u6559\u8bad\u4ed6\u4e00\u987f\u3002\u201d \u3000\u3000\u9752\u51e4\u9053\uff1a\u201c\u54b1\u4eec\u4f55\u4e0d\u5728\u5385\u4e2d\u89c1\u4ed6\uff1f\u201d \u3000\u3000\u738b\u5b37\u9053\uff1a\u201c\u597d\uff01\u54b1\u4eec\u4e0b\u697c\u53bb\u3002\u201d \u3000\u3000\u5927\u5385\u4e2d\u7ad9\u7740\u4e00\u4e2a\u5168\u8eab\u6de1\u9752\u88c5\u675f\u7684\u5c11\u5973\uff0c\u8eab\u4f69\u53cc\u5200\uff0c\u7adf\u662f\u56db\u5b63\u82b1\u5a62\u4e2d\u7684\u83f1\u82b1\u3002 \u3000\u3000\u9752\u51e4\u65e9\u5df2\u77e5\u9053\u5979\u7684\u8eab\u4efd\uff0c\u56e0\u4e3a\uff0c\u6c5f\u67ab\u4e1a\u5df2\u8bf4\u51fa\u4e86\u5979\u662f\u638c\u7ba1\u4ee4\u8c15\u7684\u4eba\uff0c\u662f\u6885\u82b1\u7684\u52a9\u624b\uff0c\u4e5f\u662f\u76d1\u89c6\u6885\u82b1\u7684\u4eba\u3002 \u3000\u3000\u9752\u51e4\u6709\u4e9b\u610f\u5916\u4e4b\u611f\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u539f\u6765\u662f\u83f1\u82b1\u59d1\u5a18\uff0c\u6df1\u591c\u6765\u6b64\uff0c\u4e0d\u77e5\u6709\u4f55\u89c1\u6559\uff1f\u201d \u3000\u3000\u201c\u9752\u51e4\u59d0\u2026\u2026\u201d\u83f1\u82b1\u7684\u5634\u5df4\u5f88\u751c\uff0c\u8bf4\uff0c\u201c\u6d1b\u9633\u5c45\u4e2d\u8fde\u7eed\u53d1\u751f\u5927\u53d8\uff0c\u5c0f\u59b9\u76f8\u4fe1\u4ee5\u9752\u51e4\u59d0\u59d0\u4e4b\u80fd\uff0c\u65e9\u5df2\u7ecf\u4e86\u7136\u4e8e\u80f8\u2026\u2026\u201d \u3000\u3000\u201c\u8bf4\u6211\u4e0d\u77e5\u9053\uff0c\u662f\u9a97\u4eba\u7684\u8bdd\u2026\u2026\u201d\u9752\u51e4\u9053\uff1a\u201c\u4f46\u5982\u8bf4\u6211\u5168\u77e5\u5185\u60c5\uff0c\u70b9\u6ef4\u4e0d\u9057\uff0c\u672a\u514d\u662f\u81ea\u62ac\u81ea\u9ad8\u4e86\uff0c\u4f60\u4eec\u5185\u5bab\u4e00\u7cfb\u4e2d\u4eba\uff0c\u81ea\u6210\u2014\u4e2a\u683c\u5c40\uff0c\u548c\u5929\u738b\u95e8\u5f88\u5c11\u6765\u5f80\uff0c\u4f60\u4eec\u56db\u5b63\u82b1\u5973\uff0c\u4e5f\u4ece\u672a\u548c\u6211\u6709\u6240\u6765\u5f80\uff0c\u5c31\u7b97\u6211\u542c\u5230\u4e86\u4e00\u4e9b\u98ce\u58f0\uff0c\u4e5f\u4e0d\u4fbf\u51fa\u9762\u67e5\u95ee\uff0c\u90a3\u4e00\u6b21\u6211\u51fa\u9762\u8c03\u89e3\u4f60\u4eec\u548c\u9093\u98de\u7684\u51b2\u7a81\uff0c\u552f\u4e00\u539f\u56e0\u3002\u662f\u6015\u4f60\u4eec\u53cc\u65b9\u95f9\u7684\u592a\u5389\u5bb3\uff0c\u4f1a\u628a\u6d1b\u9633\u5c45\u7ed9\u95f9\u57ae\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u5c0f\u59b9\u77e5\u9053\u3002\u201d\u83f1\u82b1\u8bf4\uff0c\u201c\u5c0f\u59b9\u4eca\u756a\u6765\u6b64\uff0c\u4e00\u6765\u662f\u5411\u9752\u51e4\u59d0\u8bf7\u5b89\uff0c\u4e8c\u6765\u662f\u8f6c\u8fbe\u4e0a\u547d\u2026\u2026\u201d \u3000\u3000\u201c\u8bf7\u5b89\u5c31\u4e0d\u6562\u5f53\u4e86\u2026\u2026\u201d\u9752\u51e4\u5fc3\u4e2d\u5fd6\u9053\uff1a\u201c\u4f55\u4e0d\u501f\u673a\u8bc8\u5979\u2014\u4e0b\uff0c\u903c\u5979\u9732\u51fa\u4e00\u4e9b\u53e3\u98ce\uff0c\u4e5f\u7b97\u7ed9\u6c5f\u90ce\u4e00\u70b9\u5e2e\u52a9\u4e86\u3002\u201d \u3000\u3000\u7acb\u523b\u4e00\u6c89\u8138\u8272\uff0c\u9053\uff1a\u201c\u81f4\u4e8e\u8f6c\u8fbe\u4e0a\u547d\uff0c\u6211\u770b\u6765\u610f\u5c31\u4e0d\u591f\u8bda\u5fc3\u4e86\uff0c\u636e\u6211\u6240\u77e5\uff0c\u4f60\u4eec\u8fd9\u91cc\u7684\u9996\u8111\u4eba\uff0c\u5e94\u8be5\u662f\u6885\u82b1\u59d1\u5a18\uff0c\u5979\u53ef\u662f\u4e0d\u5c51\u6765\u6b64\u8f6c\u8fbe\u4e0a\u547d\u4e86\u3002\u201d \u3000\u3000\u201c\u4e0d\u662f\uff0c\u4e0d\u662f\u2026\u2026\u201d\u83f1\u82b1\u6025\u6025\u63a5\u9053\uff1a\u201c\u6885\u82b1\u786e\u662f\u6211\u4eec\u7684\u9886\u5934\u5927\u59d0\uff0c\u4f46\u8fd9\u4ef6\u4e8b\uff0c\u5979\u8fd8\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u201c\u8fd9\u5c31\u5947\u602a\u4e86\u2026\u2026\u201d\u9752\u51e4\u63a5\u9053\uff1a\u201c\u6885\u82b1\u8fd8\u4e0d\u77e5\u9053\u7684\u4e8b\uff0c\u4f60\u600e\u4e48\u5148\u77e5\u9053\u4e86\uff1f\u201d \u3000\u3000\u201c\u5c0f\u59b9\u662f\u638c\u7ba1\u4f20\u4ee4\u7684\u4eba\u2026\u2026\u201d\u83f1\u82b1\u82e6\u7b11\u4e00\u4e0b\uff0c\u5979\u5fc3\u4e2d\u660e\u767d\uff0c\u5df2\u4e0d\u5b9c\u9690\u7792\u4e86\uff0c\u53ea\u597d\u8bf4\u9053\uff1a \u3000\u3000\u201c\u6211\u4eec\u548c\u5bab\u4e2d\u7684\u8fde\u7cfb\uff0c\u4e00\u5411\u90fd\u7531\u5c0f\u59b9\u627f\u8f6c\uff0c\u518d\u7531\u6885\u82b1\u4e0b\u4ee4\u6267\u884c\u3002\u201d \u3000\u3000\u5979\u4e00\u53e3\u4e00\u4e2a\u6885\u82b1\uff0c\u770b\u8d77\u6765\uff0c\u5979\u5bf9\u6885\u82b1\u5e76\u975e\u5341\u5206\u5c0a\u91cd\u3002 \u3000\u3000\u201c\u539f\u6765\u4f60\u662f\u638c\u4ee4\u2026\u2026\u201d\u9752\u51e4\u7b11\u9053\uff1a\u201c\u5012\u662f\u5931\u656c\u4e86\uff0c\u4f46\u4e0d\u77e5\u4f60\u548c\u6885\u82b1\u59d1\u5a18\u7684\u6743\u4f4d\uff0c\u5982\u4f55\u4e00\u4e2a\u5206\u6cd5\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u662f\u4fae\u82b1\u59d1\u5a18\u4e3a\u9996\u2026\u2026\u201d\u83f1\u82b1\u9053\uff1a\u201c\u53ea\u4e0d\u8fc7\u5c0f\u59b9\u6709\u65f6\u4f1a\u63a5\u5230\u5bab\u4e2d\u7684\u5bc6\u4ee4\uff0c\u6307\u5b9a\u8981\u5c0f\u59b9\u4f5c\u4e00\u4e9b\u7279\u6b8a\u7684\u5de5\u4f5c\uff0c\u5c0f\u59b9\u5c31\u4e0d\u4fbf\u518d\u5411\u6885\u82b1\u59d1\u5a18\u8f6c\u5448\u4e86\u3002\u201d \u3000\u3000\u201c\u90a3\u662f\u8bf4\uff0c\u4f60\u662f\u5949\u6d3e\u76d1\u89c6\u6885\u82b1\u7684\u4eba\u4e86\u2026\u2026\u201d\u9752\u51e4\u9053\uff1a\u201c\u662f\u771f\u6b63\u63e1\u6709\u5b9e\u6743\u7684\u4eba\uff1f\u201d \u3000\u3000\u201c\u90a3\u5012\u4e0d\u662f\u2026\u2026\u201d\u83f1\u82b1\u89e3\u91ca\u8bf4\uff0c\u201c\u6885\u82b1\u59d1\u5a18\u7684\u6b66\u529f\uff0c\u9ad8\u8fc7\u5c0f\u59b9\u5f88\u591a\uff0c\u624d\u667a\u4e5f\u975e\u5c0f\u59b9\u80fd\u53ca\uff0c\u5185\u5bab\u6d3e\u6765\u6d1b\u9633\u5c45\u4e2d\u7684\u4eba\uff0c\u4e5f\u660e\u5b9a\u5979\u4e3a\u9996\u8111\uff0c\u4f46\u4e8b\u6743\u592a\u8fc7\u96c6\u4e2d\uff0c\u96be\u514d\u6709\u6240\u758f\u5ffd\uff0c\u5c0f\u59b9\u53ea\u662f\u5949\u547d\u5448\u62a5\u4e00\u4e9b\u758f\u5931\uff0c\u4ee5\u4fbf\u5bab\u4e3b\u80fd\u53ca\u65f6\u4e0b\u4ee4\u7ea0\u6b63\u3002\u201d \u3000\u3000\u9752\u51e4\u63a5\u9053\uff1a\u201c\u542c\u8bf4\u9664\u4e86\u4f60\u4eec\u4e5d\u5927\u82b1\u5973\u4e4b\u5916\uff0c\u8fd8\u6709\u8d35\u5bab\u4e2d\u7537\u5f1f\u5b50\u6d3e\u6765\u8fd9\u91cc\uff0c\u53ef\u6709\u6b64\u4e8b\u5417\uff1f\u201d \u3000\u3000\u201c\u6709\uff01\u9886\u5934\u7684\u5e08\u5144\u738b\u9f99\uff0c\u6b7b\u4e8e\u97e9\u526f\u603b\u7ba1\u624b\u4e2d\u2026\u2026\u201d\u83f1\u82b1\u9053\uff0c\u201c\u9ec4\u9f99\u5e08\u5144\u5949\u547d\u8ffd\u67e5\u6b64\u4e8b\uff0c\u4e5f\u88ab\u97e9\u9738\u51fb\u6740\uff0c\u2026\u2026\u201d \u3000\u3000\u8bed\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u8d35\u95e8\u4e2d\u4e00\u4f4d\u559c\u9e4a\u7ea7\u7684\u59d0\u59d0\uff0c\u4e5f\u6b7b\u5728\u4e86\u97e9\u9738\u7684\u624b\u4e2d\u2026\u2026\u201d \u3000\u3000\u201c\u9ed1\u5929\u9e45\u6765\u6b64\u95ee\u7f6a\uff0c\u4e5f\u8d25\u5728\u4e86\u97e9\u9738\u7684\u624b\u4e2d\u2026\u2026\u201d\u9752\u51e4\u63a5\u9053\uff0c\u201c\u8fd9\u4e2a\u97e9\u9738\uff0c\u5b9e\u5728\u53ef\u7591\uff0c\u4e0d\u77e5\u83f1\u82b1\u59d1\u5a18\u662f\u5426\u5df2\u67e5\u51fa\u4e86\u4ed6\u7684\u51fa\u8eab\u6765\u5386\u2026\u2026\u201d \u3000\u3000\u201c\u67e5\u4e0d\u51fa\u6765\u2026\u2026\u201d\u83f1\u82b1\u9053\uff1a\u201c\u4f46\u636e\u5c0f\u59b9\u63a8\u65ad\uff0c\u53ef\u80fd\u662f\u5929\u9a6c\u5802\u6697\u4e2d\u57f9\u517b\u7684\u4e00\u4f4d\u5e74\u8f7b\u9ad8\u624b\uff0c\u4ed6\u6b66\u529f\u7684\u535a\u6742\uff0c\u51fa\u624b\u7684\u51f6\u5389\uff0c\u7edd\u4e0d\u662f\u4e00\u4f4d\u5e08\u7236\u6240\u80fd\u8c03\u6559\u51fa\u6765\uff0c\u6240\u4ee5\uff0c\u5bab\u4e3b\u4e5f\u540c\u610f\u4e86\u5c0f\u59b9\u7684\u7814\u5224\uff0c\u51b3\u5fc3\u9664\u6389\u6b64\u4eba\uff0c\u4e00\u6765\u4e3a\u4e24\u4f4d\u5e08\u5144\u62a5\u4ec7\uff0c\u4e5f\u7b97\u7a0d\u6170\u90a3\u4f4d\u559c\u9e4a\u59d0\u59d0\u7684\u4e5d\u6cc9\u82f1\u7075\u4e86\uff01\u201d \u3000\u3000\u9752\u51e4\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u8d35\u4e0a\u6709\u4ec0\u4e48\u8981\u6211\u6548\u529b\u4e4b\u5904\u3002\u5c31\u8bf7\u5766\u7136\u76f8\u544a\u5427\uff01\u6211\u6743\u80fd\u6240\u53ca\uff0c\u7edd\u4e0d\u63a8\u8f9e\uff0c\u4f46\u5982\u975e\u6211\u80fd\u591f\u4f5c\u4e3b\u65f6\uff0c\u90a3\u5c31\u8981\u83f1\u82b1\u59d1\u5a18\u539f\u8c05\u4e86\uff0c\u6211\u4e5f\u5f97\u8bf7\u793a\u51b3\u5b9a\u3002\u201d \u3000\u3000\u201c\u5f53\u7136\uff0c\u5f53\u7136\uff0c\u7edd\u4e0d\u80fd\u8ba9\u9752\u51e4\u59d0\u4e3a\u96be\u2026\u2026\u201d\u83f1\u82b1\u8bf4\uff0c\u201c\u5c0f\u59b9\u6df1\u591c\u62dc\u8bbf\uff0c\u6700\u91cd\u8981\u7684\u662f\u5411\u9752\u51e4\u59d0\u7b80\u62a5\u6d1b\u9633\u5c45\u4e2d\u7684\u60c5\u52bf\uff0c\u548c\u672c\u5bab\u9664\u6389\u97e9\u9738\u7684\u51b3\u5fc3\uff0c\u5e0c\u671b\u9752\u51e4\u59d0\u80fd\u591f\u4f53\u8c05\uff0c\u4e8b\u5b9e\u4e0a\uff0c\u672c\u5bab\u4e2d\u4eba\u5df2\u5949\u547d\u5411\u5929\u738b\u95e8\u793a\u597d\uff0c\u800c\u4e14\u4e5f\u6709\u4e86\u6210\u7ee9\uff0c\u9ed1\u5929\u9e45\u59d0\u59d0\uff0c\u5927\u9a7e\u4eb2\u4e34\uff0c\u548c\u4e00\u4f4d\u559c\u9e4a\u7ea7\u59d0\u59d0\u5230\u6b64\uff0c\u5e94\u8be5\u662f\u6700\u597d\u7684\u8bc1\u660e\u4e86\uff01\u201d \u3000\u3000\u9752\u51e4\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e00\u53ea\u5929\u9e45\u56db\u53ea\u9e4a\uff0c\u56db\u5927\u5929\u9e45\u624b\u4e0b\uff0c\u5404\u6709\u56db\u53ea\u559c\u9e4a\u542c\u5019\u9057\u5dee\uff0c\u5979\u4eec\u4e5f\u4ee5\u8ffd\u968f\u7684\u5929\u9e45\u4f5c\u59d3\uff0c\u9ed1\u5929\u9e45\u7684\u624b\u4e0b\uff0c\u5c31\u662f\u9ed1\u559c\u9e4a\u2026\u2026\u201d \u3000\u3000\u201c\u5c0f\u59b9\u660e\u767d\u2026\u2026\u201d\u83f1\u82b1\u5c55\u73b0\u4e86\u624d\u6167\uff0c\u9732\u51fa\u4e56\u5de7\uff0c\u63a5\u9053\uff1a\u201c\u56db\u4f4d\u9ed1\u559c\u9e4a\u7684\u8eab\u4efd\u76f8\u82e5\uff0c\u4ee5\u7f16\u53f7\u4ee3\u66ff\uff0c\u4ee5\u4fbf\u6307\u6325\u3002\u201d \u3000\u3000\u201c\u4e0d\u9519\u2026\u2026\u201d\u9752\u51e4\u9053\uff1a\u201c\u83f1\u82b1\u59d1\u5a18\u4ec0\u4e48\u4e0a\u8c15\u8f6c\u544a\u5417\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\u4e86\u2026\u2026\u201d\u83f1\u82b1\u9053\uff1a\u201c\u672c\u5bab\u6d3e\u6765\u7684\u4e13\u4efb\u6740\u624b\uff0c\u660e\u65e5\u4e2d\u5348\uff0c\u5c31\u53ef\u4ee5\u8fdb\u5165\u4e86\u957f\u5b89\uff0c\u4ed6\u4eec\u53ef\u80fd\u548c\u5c0f\u59b9\u8fde\u7edc\uff0c\u4e5f\u53ef\u80fd\u4e0d\u518d\u8fde\u7edc\uff0c\u72ec\u7acb\u6267\u884c\u4efb\u52a1\uff0c\u4ed6\u4eec\u7684\u5fc3\u4e2d\uff0c\u5df2\u5370\u5165\u4e86\u97e9\u9738\u7684\u5f62\u50cf\uff0c\u53c8\u662f\u5355\u4e00\u7684\u4efb\u52a1\u884c\u52a8\u3002\u201d \u3000\u3000\u201c\u542c\u4f60\u7684\u53e3\u6c14\uff0c\u597d\u50cf\u6765\u7684\u4e0d\u6b62\u4e00\u4e2a\u4eba\u4e86\u2026\u2026\u201d\u9752\u51e4\u9053\uff1a\u201c\u4ed6\u4eec\u4f1a\u4e0d\u4f1a\u5728\u6d1b\u9633\u5c45\u4e2d\u4e0b\u624b\u5462\uff1f\u201d \u3000\u3000\u201c\u6211\u4e0d\u77e5\u9053\u4ed6\u4eec\u4f1a\u5728\u90a3\u91cc\u4e0b\u624b\u2026\u2026\u201d\u83f1\u82b1\u9053\uff1a\u201c\u4f46\u6211\u4eec\u7edd\u4e0d\u80fd\u4f4e\u4f30\u97e9\u9738\u3002\u4ed6\u6740\u4e86\u6211\u4e24\u4f4d\u5e08\u5144\uff0c\u5c24\u5176\u662f\u5927\u5e08\u5144\u9ec4\u9f99\uff0c\u6b66\u529f\u4e4b\u9ad8\uff0c\u4e8c\u4ee3\u5f1f\u5b50\u4e2d\u9996\u5c48\u4e00\u6307\uff0c\u4e0d\u80fd\u60f3\u50cf\u97e9\u9738\u7528\u4ec0\u4e48\u6b66\u529f\uff1f\u4e00\u638c\u80fd\u53d6\u4ed6\u6027\u547d\uff0c\u8d35\u95e8\u4e2d\u90a3\u4f4d\u559c\u9e4a\u7ea7\u7684\u59d0\u59d0\uff0c\u4e5f\u662f\u88ab\u4ed6\u4e00\u638c\u62cd\u6b7b\uff0c\u5c31\u5c0f\u59b9\u6240\u77e5\uff0c\u4ed6\u51fa\u638c\u8f7b\u6de1\uff0c\u770b\u4e0d\u51fa\u6709\u4f55\u5a01\u52bf\uff0c\u4f46\u4e00\u638c\u62cd\u51fa\uff0c\u5374\u662f\u8ffd\u9b42\u53d6\u547d\uff0c\u4e0d\u4f46\u6740\u4e86\u6211\u4eec\u7684\u4eba\uff0c\u4e5f\u4f7f\u4ed6\u4eec\u5929\u9a6c\u5802\u4e2d\u4eba\uff0c\u60ca\u9a87\u4e0d\u5df2\u3002 \u3000\u3000\u6240\u4ee5\uff0c\u5c0f\u59b9\u8bf7\u6c42\u5bab\u4e3b\u6d3e\u6700\u597d\u7684\u6740\u624b\u51fa\u52a8\uff0c\u6765\u4e86\u51e0\u4e2a\u4eba\uff0c\u5c0f\u59b9\u4e0d\u5f88\u6e05\u695a\uff0c\u4f46\u7edd\u4e0d\u6b62\u4e00\u4e2a\u4eba\uff1f\u201d \u3000\u3000\u201c\u6709\u4ec0\u4e48\u8981\u6211\u914d\u5408\u5417\uff1f\u2026\u2026\u201d\u9752\u98ce\u4f4e\u58f0\u95ee\u9053\u3002 \u3000\u3000\u201c\u6211\u770b\u662f\u4e0d\u7528\u4e86\uff0c\u5c0f\u59b9\u62a5\u544a\u5df2\u534e\uff0c\u5c31\u6b64\u544a\u9000\u3002\u201d \u3000\u3000\u8bf4\u8d70\u5c31\u8d70\uff0c\u8f6c\u8eab\u5411\u5916\u884c\u53bb\u3002 \u3000\u3000\u9752\u51e4\u6ca1\u6709\u633d\u7559\uff0c\u4e5f\u672a\u9001\u5ba2\uff0c\u4e8b\u5b9e\u4e0a\uff0c\u9752\u51e4\u4e5f\u672a\u8fce\u5ba2\u5165\u5ea7\uff0c\u5927\u5bb6\u90fd\u662f\u7ad9\u7740\u4ea4\u8c08\uff0c\u81ea\u662f\u4e0d\u4f1a\u62d6\u5ef6\u592a\u4e45\u3002 \u3000\u3000\u4e0d\u77e5\u9053\u738b\u5b37\u3001\u674e\u5b37\uff0c\u662f\u5426\u4e5f\u5df2\u77e5\u9053\u97e9\u9738\u5c31\u662f\u6c5f\u67ab\u7684\u5316\u8eab\uff0c\u4f46\u4e24\u4eba\u5374\u672a\u591a\u95ee\uff0c\u76f4\u63a5\u56de\u5230\u4e86\u623f\u4e2d\u3002 \u3000\u3000\u9752\u51e4\u56de\u5230\u95fa\u623f\uff0c\u968f\u624b\u62f4\u4e0a\u623f\u95e8\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u8981\u4e0d\u8981\u6211\u4ece\u5934\u8bf4\u4e00\u904d\uff1f\u201d \u3000\u3000\u201c\u4e0d\u7528\u4e86\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u6211\u542c\u5f97\u5b57\u5b57\u5165\u8033\uff0c\u591c\u5df2\u4e09\u66f4\uff0c\u7761\u89c9\u5427\uff01\u201d\u4e00\u628a\u62b1\u8d77\u9752\u98ce\uff0c\u884c\u5411\u7259\u5e8a\u3002 \u3000\u3000\u201c\u4f60\u771f\u7684\u8fd9\u4e48\u6f47\u6d12\uff0c\u4e00\u70b9\u4e5f\u4e0d\u653e\u5728\u5fc3\u4e0a\u2026\u2026\u201d\u9752\u98ce\u5fae\u5e26\u5fe7\u8651\u5730\u8bf4\u9053\uff1a\u201c\u660e\u67aa\u660e\u5200\uff0c\u6211\u76f8\u4fe1\u4f60\u80fd\u5e94\u4ed8\u5f97\u8fc7\uff0c\u4f46\u5185\u5bab\u4e2d\u7684\u4e13\u4efb\u6740\u624b\uff0c\u5374\u662f\u4e13\u5b66\u4e60\u6740\u4eba\u673a\u5de7\u7684\u4eba\uff0c\u4ed6\u4eec\u6740\u4eba\u65b9\u6cd5\uff0c\u82b1\u6837\u767e\u51fa\uff0c\u7b54\u5e94\u6211\uff0c\u4f60\u8981\u5168\u795e\u5bf9\u654c\uff0c\u522b\u8ba9\u6211\u672a\u7ed3\u5a5a\u5c31\u4f5c\u4e86\u5be1\u5987\uff01\u201d \u3000\u3000\u6c5f\u67ab\u70b9\u70b9\u5934\uff0c\u7b11\u9053\uff1a\u201c\u653e\u5fc3\uff0c\u653e\u5fc3\uff0c\u6211\u5df2\u7ecf\u51c6\u5907\u597d\u4e86\u5fa1\u654c\u4e4b\u7b56\u3002\u5012\u662f\u4e24\u4f4d\u5b37\u5b37\u7684\u4e8b\uff0c\u662f\u4e2a\u9ebb\u70e6\uff0c\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u53c8\u9053\uff1a\u201c\u6211\u62c5\u5fc3\u4e0d\u662f\u90a3\u4e9b\u4e13\u6765\u5bf9\u4ed8\u6211\u7684\u654c\u624b\uff0c\u800c\u662f\u4f60\u90a3\u4f4d\u5929\u738b\u4f1a\u7a81\u7136\u9a7e\u5230\u957f\u5b89\uff0c\u2026\u2026\u201d \u3000\u3000\u9752\u51e4\u5df2\u88ab\u653e\u5728\u5e8a\u4e0a\uff0c\u6c5f\u67ab\u6b63\u5e2e\u5979\u5bbd\u8863\u89e3\u5e26\uff0c\u4f46\u5374\u88ab\u9752\u51e4\u4e00\u628a\u6293\u4f4f\u4e86\u6c5f\u67ab\u7684\u624b\uff0c\u9053\uff1a\u201c\u6211\u65e0\u6cd5\u9884\u8ba1\u738b\u5b37\u548c\u674e\u5b37\u4f1a\u6709\u4e9b\u4ec0\u4e48\u51b3\u5b9a\uff0c\u6bd5\u7adf\u5979\u4eec\u662f\u5929\u738b\u7684\u5fc3\u8179\uff0c\u4e5f\u65e0\u6cd5\u7559\u4e0b\u4f60\u5e2e\u52a9\u6211\uff0c\u4f46\u6211\u81ea\u4fdd\u7684\u73af\u5883\u5f88\u597d\uff0c\u5355\u662f\u627e\u4e2a\u4eba\u53d6\u4ee3\u6211\u7684\u6b4c\u58f0\uff0c\u5c31\u4e0d\u592a\u5bb9\u6613\uff0c\u90a3\u9700\u8981\u957f\u65f6\u95f4\u7684\u57f9\u517b\u3001\u8bad\u7ec3\uff0c\u4e0d\u8fc7\uff0c\u6709\u4e00\u70b9\uff0c\u4f60\u53ef\u4ee5\u653e\u5fc3\uff0c\u6211\u65e9\u5df2\u51c6\u5907\u597d\u81ea\u7edd\u51b3\u5fc3\uff0c\u51b3\u4e0d\u4f1a\u4f7f\u4f60\u8499\u7f9e\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u542c\u5f97\u53c8\u611f\u52a8\uff0c\u53c8\u6709\u4e9b\u5fc3\u75bc\uff0c\u56de\u62a5\u7684\u65b9\u5f0f\u662f\u8f7b\u8f7b\u63a8\u5f00\u4e86\u9752\u51e4\u7684\u624b\u3002 \u3000\u3000\u5176\u5b9e\uff0c\u51e4\u59d1\u5a18\u65e9\u5df2\u7ecf\u4e3a\u90ce\u9676\u9189\uff0c\u54ea\u8fd8\u6709\u62d2\u7edd\u7684\u6c14\u529b\uff0c\u2026\u2026\u3002 \u3000\u3000\u76f8\u805a\u7684\u65f6\u95f4\u4e0d\u591a\uff0c\u4f46\u6c5f\u67ab\u4fee\u4e60\u7684\u5929\u72d0\u795e\u529f\uff0c\u594b\u52c7\u65e0\u53cc\uff0c\u5f81\u670d\u5973\u4eba\u7684\u529b\u91cf\u662f\u5b8c\u5168\u5f81\u670d\uff0c\u8eab\u548c\u5fc3\u5168\u90e8\u5360\u636e\u3002 \u3000\u3000\u9752\u51e4\u5f97\u5230\u4e86\u4ece\u672a\u7ecf\u8fc7\u7684\u5feb\u4e50\uff0c\u90a3\u79cd\u6ee1\u8db3\u7684\u611f\u53d7\uff0c\u66f4\u575a\u5b9a\u4e86\u60c5\u7518\u6548\u6b7b\u7684\u51b3\u5fc3\u3002 \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff0c\u4f60\u8ba9\u4eba\u5982\u6b64\u7684\u8ff7\u9189\u2026\u2026\u201d\u9752\u51e4\u7ec6\u5de7\u7684\u7389\u6307\uff0c\u8f7b\u629a\u7740\u6c5f\u67ab\u5149\u6ed1\u7684\u540e\u80cc\uff0c\u9053\uff1a \u3000\u3000\u201c\u4f1a\u53eb\u4eba\u5174\u8d77\u4e86\u2014\u5915\u60c5\u7f18\uff0c\u6b7b\u800c\u65e0\u61be\u7684\u611f\u89c9\uff0c\u6c5f\u90ce\uff0c\u53bb\u6355\u6349\u83f1\u82b1\u5427\uff01\u5c0f\u4e2b\u5934\u53c8\u597d\u53c8\u5201\uff0c\u4f46\u5374\u80f8\u85cf\u4e86\u65e0\u6570\u7684\u9690\u5bc6\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u770b\u5f97\u51fa\u6765\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u5979\u867d\u7136\u5168\u529b\u5728\u8868\u8fbe\u51fa\u4e00\u79cd\u8bda\u610f\uff0c\u4f46\u5374\u5fcd\u4e0d\u4f4f\u631f\u6742\u4e0a\u51e0\u53e5\u8c0e\u8a00\uff0c\u786e\u662f\u5929\u751f\u7684\u5978\u5201\u80da\u5b50\uff0c\u8fd9\u4e5f\u662f\u5979\u88ab\u9009\u4e0a\u638c\u4ee4\u7684\u672c\u94b1\uff0c\u8fd9\u79cd\u4eba\u6211\u4e0d\u60f3\u518d\u6d6a\u8d39\u7cbe\u795e\u53bb\u5e94\u916c\u5979\uff0c\u5fc5\u8981\u65f6\uff0c\u51fa\u624b\u6740\u4e86\u5979\u5c31\u662f\u3002\u201d \u3000\u3000\u9752\u98ce\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u53ef\u662f\uff0c\u6740\u4e86\u5979\uff0c\u4f60\u5c31\u65e0\u6cd5\u5f97\u5230\u5979\u80f8\u4e2d\u85cf\u6709\u7684\u673a\u5bc6\u4e86\u3002\u201d \u3000\u3000\u201c\u6211\u770b\uff0c\u90a3\u5df2\u7ecf\u4e0d\u662f\u5f88\u91cd\u8981\u7684\u4e8b\u4e86\u2026\u2026\u201d\u6c5f\u67ab\u8c6a\u60c5\u98ce\u53d1\u5730\u8bf4\uff0c\u201c\u5185\u5bab\u4e2d\u7b2c\u4e8c\u4ee3\u5f1f\u5b50\uff0c\u5927\u6982\u5df2\u7ecf\u6ca1\u6709\u4eba\u80fd\u6597\u8fc7\u6211\u4eec\u4e86\uff0c\u5982\u679c\uff0c\u5979\u4eec\u6d3e\u51fa\u7684\u4e13\u4efb\u6740\u624b\uff0c\u518d\u843d\u4e2a\u5168\u519b\u8986\u6ca1\uff0c\u5927\u6982\uff0c\u7b2c\u4e00\u4ee3\u7684\u9996\u8111\u4eba\u624b\uff0c\u5c31\u8981\u51fa\u52a8\u4e86\u3002\u201d \u3000\u3000\u201c\u5168\u519b\u8986\u6ca1\uff1f\u4f60\u597d\u50cf\u5df2\u667a\u73e0\u5728\u63e1\uff0c\u5f88\u6709\u628a\u63e1\u4e86\u2026\u2026\u201d\u9752\u51e4\u9053\uff0c\u201c\u6885\u82b1\u80fd\u7ed9\u4f60\u591a\u5927\u5e2e\u52a9\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\u628a\u5979\u8ba1\u7b97\u5728\u5185\u2026\u2026\u201d\u6c5f\u67ab\u7b11\u9053\uff1a\u201c\u5bf9\u4ed8\u5185\u5bab\u4e13\u4efb\u6740\u624b\uff0c\u5168\u7531\u6211\u7684\u4eba\u51fa\u624b\u5bf9\u6297\u2026\u2026\u201d \u3000\u3000\u201c\u4f60\u662f\u8bf4\u6885\u82b1\u548c\u6211\uff0c\u5e2e\u4e0d\u4e0a\u5fd9\u4e86\u2026\u2026\u201d\u9752\u51e4\u9053\uff1a\u201c\u4f60\u80fd\u6709\u591a\u5c11\u4eba\u624b\uff1f\u9664\u4e86\u5929\u9a6c\u5802\u4e4b\u5916\uff0c\u5b9e\u5728\u4eba\u624b\u6709\u9650\u4e86\uff01\u201d \u3000\u3000\u201c\u5012\u4e0d\u662f\u4f60\u4eec\u5e2e\u4e0d\u4e0a\u5fd9\uff0c\u5c24\u5176\u662f\u4f60\uff0c\u5177\u6709\u7684\u7279\u5f02\u80fd\u529b\uff0c\u662f\u5bf9\u4ed8\u523a\u5ba2\u6700\u4f73\u6280\u80fd\u2026\u2026\u201d\u6c5f\u67ab\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f46\u6211\u4e0d\u5e0c\u671b\u4f60\u4eec\u592a\u65e9\u66dd\u5149\uff0c\u4f60\u3001\u6885\u82b1\uff0c\u4ee5\u73b0\u5728\u7684\u8eab\u4efd\u5e94\u8be5\u7ed9\u6211\u7684\u5e2e\u52a9\u66f4\u5927\u2026\u2026\u201d \u3000\u3000\u201c\u53ef\u662f\uff0c\u6211\u8eab\u9677\u9669\u5883\u554a\uff01\u65f6\u65f6\u90fd\u6709\u88ab\u8feb\u5931\u8eab\u7684\u53ef\u80fd\u2026\u2026\u201d\u9752\u51e4\u9053\uff0c\u201c\u96be\u9053\u4f60\u771f\u60f3\u6211\u6b7b\u5417\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u770b\u738b\u5b37\u3001\u674e\u5b37\u7684\u53cd\u5e94\u518d\u8bf4\u5427\uff1f\u5979\u4eec\u5982\u5fc3\u5b58\u6545\u4e3b\uff0c\u5bf9\u4f60\u7684\u652f\u6301\uff0c\u53ea\u662f\u4e00\u65f6\u51b2\u52a8\uff0c\u8fd9\u91cc\u5c31\u4e0d\u7528\u8e72\u4e0b\u53bb\u4e86\uff0c\u5e72\u8106\u8ddf\u6211\u4eec\u4e00\u8d77\uff0c\u6311\u660e\u4e86\u5e72\u5427\uff01\u201d \u3000\u3000\u201c\u90a3\u5c82\u4e0d\u662f\u628a\u5929\u738b\u95e8\u903c\u8fc7\u53bb\uff0c\u548c\u5185\u5bab\u8054\u624b\u4e86\u2026\u2026\u201d\u9752\u51e4\u9053\uff0c\u201c\u4f24\u5bb3\u592a\u5927\u4e86\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u65e0\u53ef\u5948\u4f55\u5440\uff01\u4f60\u4e00\u4e2a\u4eba\u7559\u8fd9\u91cc\uff0c\u7eb5\u7136\u80fd\u60f3\u5230\u8ba9\u5929\u738b\u4e0d\u80fd\u78b0\u4f60\u7684\u529e\u6cd5\uff0c\u4f46\u738b\u5b37\u3001\u674e\u5b37\uff0c\u968f\u65f6\u53ef\u4ee5\u6293\u4f60\u5411\u5929\u738b\u732e\u529f\uff0c\u5c82\u4e0d\u662f\u592a\u5371\u9669\u4e86\u3002\u201d \u3000\u3000\u9752\u51e4\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u4e0d\u8fc7\u662f\u4e3a\u60c5\u5fe7\u5fc3\uff0c\u5e0c\u671b\u80fd\u60f3\u51fa\u4e00\u4e2a\u4e24\u5168\u5176\u7f8e\u7684\u529e\u6cd5\uff0c\u4f46\u5982\u771f\u903c\u7684\u6211\u653e\u624b\u53cd\u6297\uff0c\u6c5f\u90ce\uff0c\u4f60\u7684\u59bb\u5b50\uff0c\u7edd\u4e0d\u662f\u597d\u5403\u7684\u679c\u5b50\uff0c\u5c31\u7b97\u5929\u738b\u51fa\u624b\uff0c\u767e\u62db\u4e4b\u5185\uff0c\u4e5f\u65e0\u6cd5\u4f24\u5f97\u5230\u6211\uff0c\u4e24\u4e2a\u5b37\u5b37\u7684\u706d\u7edd\u795e\u529f\u867d\u7136\u5389\u5bb3\uff0c\u4f46\u4e00\u5bf9\u4e00\uff0c\u5979\u4eec\u7edd\u975e\u6211\u654c\u624b\uff0c\u4e24\u4e2a\u4eba\uff0c\u4e5f\u65e0\u6cd5\u56f0\u5f97\u4f4f\u6211\u3002 \u3000\u3000\u5929\u738b\u95e8\u4e2d\u53ea\u6709\u4e24\u53ea\u51e4\uff0c\u4e00\u53ea\u7559\u5728\u603b\u5802\uff0c\u638c\u7ba1\u6587\u4e66\u4ee4\u724c\uff0c\u4e00\u53ea\u5728\u957f\u5b89\u5356\u5531\u6b4c\u575b\uff0c\u8bba\u6587\u624d\u6211\u4e5f\u8bb8\u8981\u8f93\u7ed9\u84dd\u51e4\u4e09\u5206\uff0c\u4f46\u6b66\u529f\u4e0a\u7684\u6210\u5c31\uff0c\u7edd\u4e0d\u8f93\u5979\u2026\u2026\u201d \u3000\u3000\u5ffd\u7136\u504e\u5165\u6c5f\u67ab\u6000\u4e2d\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u4f60\u7684\u8001\u5a46\u5e94\u8be5\u662f\u5929\u738b\u95e8\u4e8c\u4ee3\u5f1f\u5b50\u4e2d\u7b2c\u4e00\u9ad8\u624b\uff0c\u53ea\u662f\u5979\u6df1\u85cf\u4e0d\u9732\uff0c\u5929\u738b\u548c\u4e24\u4f4d\u5b37\u5b37\uff0c\u90fd\u4e0d\u77e5\u9053\u5979\u771f\u6b63\u7684\u6210\u5c31\u3002 \u3000\u3000\u56e0\u4e3a\u5979\u4e0d\u613f\u610f\u62a2\u4e86\u84dd\u51e4\u7684\u98ce\u91c7\uff0c\u7518\u5c45\u84dd\u51e4\u4e4b\u4e0b\uff0c\u6240\u4ee5\uff0c\u6211\u4eec\u5e08\u59d0\u59b9\uff0c\u624d\u4fdd\u6301\u4e86\u76f8\u5f53\u7684\u60c5\u610f\u3002\u201d \u3000\u3000\u201c\u597d\u5bb6\u4f19\u2026\u2026\u201d\u6c5f\u67ab\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u4e24\u4e2a\u5c0f\u72d0\u5973\uff0c\u9690\u533f\u6210\u5c31\uff0c\u6574\u5f97\u6211\u4e03\u6655\u516b\u7d20\uff0c\u4f60\u8fd9\u53ea\u5c0f\u51e4\u51f0\uff0c\u7adf\u4e5f\u662f\u6df1\u85cf\u5bc6\u5c01\uff0c\u4e0d\u9732\u950b\u8292\uff0c\u4f60\u4eec\u5fc3\u673a\u6c89\u6df1\uff0c\u6211\u7684\u65e5\u5b50\u5c31\u96be\u8fc7\u4e86\u2026\u2026\u201d \u3000\u3000\u9752\u98ce\u72e0\u72e0\u7684\u4eb2\u4e86\u6c5f\u67ab\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u4eba\u5bb6\u4fdd\u5b58\u4e86\u5341\u516b\u4e5d\u5e74\u7684\u79d8\u5bc6\uff0c\u5168\u90fd\u88ab\u9a97\u4e86\u51fa\u6765\uff0c\u6211\u73b0\u5728\u771f\u6b63\u662f\u5168\u65e0\u9690\u5bc6\u4e86\uff0c\u88ab\u4f60\u8131\u5149\u8eab\u4e0a\u7684\u8863\u670d\uff0c\u4e5f\u88ab\u4f60\u6316\u53bb\u4e86\u5fc3\u4e2d\u6240\u6709\u79d8\u5bc6\u3002\u2026\u2026\u201d \u3000\u3000\u201c\u90a3\u4e2a\u84dd\u98ce\uff0c\u638c\u7406\u5929\u738b\u95e8\u4e2d\u6587\u4e66\u3001\u4ee4\u724c\uff0c\u662f\u5929\u738b\u95e8\u4e2d\u91cd\u8981\u4eba\u7269\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u600e\u4e48\uff1f\u60f3\u53cc\u51e4\u5e76\u6536\u554a\uff01\u2026\u2026\u201d\u9752\u98ce\u7b11\u9053\uff1a\u201c\u884c\uff01\u6211\u627e\u673a\u4f1a\u7ed9\u4f60\u5f15\u89c1\uff0c\u73b0\u5728\uff0c\u5148\u544a\u8bc9\u6211\u4e24\u4e2a\u5c0f\u72d0\u5973\u662f\u4ec0\u4e48\u6837\u7684\u4eba\u7269\uff1f\u201d \u3000\u3000\u7cdf\u554a\uff01\u8a00\u591a\u5fc5\u5931\uff0c\u6c5f\u67ab\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u5e94\u8be5\u662f\u5929\u72d0\u592b\u4eba\u7684\u5f1f\u5b50\uff0c\u5979\u4eec\u5df2\u7ec3\u5f97\u5927\u6709\u6210\u5c31\uff0c\u5374\u4e00\u70b9\u4e00\u6ef4\u7684\u73b0\u9732\u51fa\u6765\uff0c\u524d\u4e24\u5929\uff0c\u624d\u7b97\u6316\u51fa\u4e86\u5979\u7684\u5e95\u5b50\uff0c\u539f\u6765\uff0c\u65e9\u5df2\u767b\u5802\u5165\u5ba4\uff0c\u6210\u5c31\u5f88\u9ad8\u4e86\uff0c\u8fd9\u4e00\u6b21\uff0c\u5c31\u7531\u5979\u4eec\u4e3a\u4e3b\uff0c\u5bf9\u4ed8\u884c\u523a\u6211\u7684\u6740\u624b\u3002\u201d \u3000\u3000\u9752\u51e4\u7b11\u9053\uff1a\u201c\u72d0\u5a9a\u8ff7\u4eba\uff0c\u5e8a\u7b2c\u95f4\u5a49\u8f6c\u627f\u6b22\u5de5\u592b\uff0c\u5fc5\u4e0d\u662f\u8d31\u59be\u80fd\u53ca\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u9519\u4e86\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u5979\u4eec\u9690\u5c45\u7ec3\u529f\uff0c\u548c\u6211\u89c1\u9762\u4e0d\u4e45\uff0c\u6211\u4eec\u4e4b\u95f4\uff0c\u4ecd\u7136\u662f\u6e05\u6e05\u767d\u767d\u3002\u201d \u3000\u3000\u9752\u51e4\u5fae\u5fae\u4e00\u6014\uff0c\u9053\uff1a\u201c\u8fd9\u5c31\u96be\u5f97\u4e86\uff0c\u6211\u80fd\u4e0d\u80fd\u89c1\u89c1\u5979\u4eec\uff1f\u6211\u60f3\u77e5\u9053\uff0c\u9762\u5bf9\u7740\u4f60\u8fd9\u6837\u4e00\u4e2a\u7537\u4eba\uff0c\u5979\u4eec\u5982\u4f55\u80fd\u628a\u81ea\u5df1\u63a7\u5236\u7684\u90a3\u4e48\u597d\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u80fd\uff0c\u4f46\u8981\u627e\u4e2a\u9002\u5f53\u673a\u4f1a\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u633a\u8eab\u5750\u8d77\uff0c\u9053\uff0c\u201c\u6211\u8be5\u8d70\u4e86\u3002\u201d \u3000\u3000\u9752\u51e4\u6293\u8d77\u4e00\u4ef6\u957f\u888d\uff0c\u62ab\u5728\u6c5f\u67ab\u8eab\u4e0a\uff0c\u9053\uff1a\u201c\u6700\u8fdf\u4e24\u5929\u5185\u8981\u544a\u8bc9\u907f\u514d\u5929\u738b\u8fd1\u6211\u4e4b\u8eab\u7684\u529e\u6cd5\uff0c\u8d70\uff01\u53a8\u4e0b\u65e9\u5df2\u70e7\u597d\u70ed\u6c34\uff0c\u6211\u5e2e\u4f60\u64e6\u4e2a\u6fa1\u53bb\u3002\u201d \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u3001\u6bb5\u4e5d\u3001\u80e1\u840d\u3001\u80e1\u5143\uff0c\u4e00\u6392\u5e76\u5750\u5728\u6c5f\u67ab\u7684\u5367\u623f\u4e2d\u94fa\u7684\u4e00\u5f20\u5927\u8349\u5e2d\u4e0a\u3002 \u3000\u3000\u6c5f\u67ab\u8fdb\u5165\u5367\u5ba4\uff0c\u56db\u4e2a\u4eba\u540c\u65f6\u7ad9\u8d77\u3002 \u3000\u3000\u6562\u60c5\u56db\u4e2a\u4eba\u65e9\u5df2\u63a5\u5230\u90ed\u5929\u540c\u4f20\u6765\u7684\u8baf\u606f\uff0c\u90ed\u5929\u540c\u548c\u80e1\u5143\u5206\u522b\u5728\u5ba4\u5916\u8b66\u6212\uff0c\u73b0\u5728\u6b63\u662f\u90ed\u5929\u540c\u5f53\u503c\u65f6\u523b\u3002 \u3000\u3000\u201c\u548c\u5c1a\u89c1\u8fc7\u4e86\u5f20\u59d1\u5a18\uff0c\u5979\u5e0c\u671b\u5c3d\u5feb\u89c1\u4f60\u4e00\u9762\u2026\u2026\u201d\u8bed\u58f0\u5fae\u5fae\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u8861\u91cf\u8fc7\u773c\u4e0b\u7684\u60c5\u52bf\u53d8\u5316\u4e4b\u540e\uff0c\u6211\u548c\u5c1a\u64c5\u81ea\u4f5c\u4e3b\uff0c\u628a\u4eba\u624b\u96c6\u4e2d\u5728\u4f60\u7684\u4f4f\u5904\u65c1\u4fa7\uff0c\u4ee5\u4fbf\u4e8e\u76f8\u4e92\u7167\u987e\u3002\u201d \u3000\u3000\u6c5f\u67ab\u6697\u5fd6\uff1a\u201c\u679c\u7136\u662f\u601d\u8651\u7cbe\u5bc6\uff0c\u9632\u5236\u673a\u5148\u3002\u201d\u5f53\u4e0b\u70b9\u5934\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u597d\u6781\u4e86\uff0c\u628a\u4e24\u4fa7\u8fb9\u95f4\u7684\u5899\u58c1\u6253\u901a\uff0c\u5408\u6210\u4e00\u4f53\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e2a\uff0c\u548c\u5c1a\u4e5f\u548c\u9093\u603b\u7ba1\u8c08\u8fc7\uff0c\u603b\u7ba1\u5df2\u7ecf\u7b54\u5141\uff0c\u5929\u4eae\u5c31\u8981\u6728\u5de5\u52a8\u624b\uff0c\u6211\u60f3\u4e24\u4e2a\u65f6\u8fb0\u4e4b\u5185\uff0c\u5e94\u8be5\u53ef\u4ee5\u5b8c\u5de5\u3002\u201d \u3000\u3000\u201c\u4e00\u5b9a\u8981\u5728\u5348\u65f6\u524d\u5b8c\u5de5\uff0c\u8bf8\u4f4d\u4e5f\u5c3d\u91cf\u627e\u65f6\u95f4\u5750\u606f\u4e00\u4e0b\uff0c\u4fdd\u6301\u4f53\u80fd\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u660e\u65e5\u5348\u540e\uff0c\u53ef\u80fd\u4f1a\u6709\u884c\u52a8\uff0c\u6211\u53bb\u89c1\u5f20\u56db\u59d1\uff0c\u5b89\u6392\u4e00\u4e0b\u8fce\u654c\u4e4b\u7b56\u3002\u201d\u63a8\u5f00\u7a97\u5b50\uff0c\u7834\u7a7a\u800c\u53bb\u3002 \u3000\u3000\u4ed6\u8bf4\u7684\u5f88\u660e\u663e\uff0c\u4f3c\u662f\u5f3a\u654c\u5df2\u7136\u538b\u5883\u3002 \u3000\u3000\u5f20\u56db\u59d1\u5df2\u642c\u51fa\u4e86\u98ce\u96ea\u5c0f\u9986\uff0c\u4f4f\u5904\u79bb\u6d1b\u9633\u5c45\u53ea\u6709\u4e24\u6761\u8857\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u63a9\u62a4\u5f88\u597d\uff0c\u524d\u9762\u662f\u4e00\u5bb6\u767e\u5e74\u8001\u5b57\u53f7\u7684\u56db\u6d77\u7cae\u884c\uff0c\u662f\u4e00\u5e62\u6709\u4e94\u8fdb\u7684\u5927\u5b85\u5b50\uff0c\u5f20\u56db\u59d1\u9ad8\u4ef7\u79df\u4e0b\u4e86\u56db\u4e94\u4e24\u8fdb\uff0c\u800c\u4e14\uff0c\u8fd8\u5141\u8bb8\u7cae\u884c\u90e8\u5206\u5b58\u7cae\u653e\u5728\u539f\u5904\u3002\u8fd9\u91cc\u7684\u623f\u5b50\u6709\u4e9b\u8001\u65e7\uff0c\u4f46\u5374\u975e\u5e38\u5bbd\u5927\uff0c\u4f4f\u4e0a\u4e09\u4e8c\u5341\u53e3\u4eba\uff0c\u4e0d\u4f1a\u62e5\u6324\uff0c\u800c\u4e14\u4e5f\u4e0d\u4f1a\u8ba9\u4eba\u6ce8\u76ee\u3002 \u3000\u3000\u6c5f\u67ab\u6309\u56fe\u6307\u5f15\uff0c\u627e\u5165\u4e86\u56db\u6d77\u7cae\u884c\uff0c\u4f46\u89c1\u4e00\u7247\u9ed1\u6f06\uff0c\u4e0d\u7981\u5fc3\u4e2d\u52a8\u7591\uff0c\u6697\u9053\uff1a\u201c\u56db\u59d1\u5982\u82e5\u5728\u6b64\uff0c\u5b9a\u4f1a\u6d3e\u4eba\u5f15\u8def\u2026\u2026\u201d \u3000\u3000\u4f46\u95fb\u6697\u5f71\u4e2d\u4f20\u6765\u5f20\u56db\u59d1\u7684\u58f0\u97f3\uff0c\u9053\uff1a\u201c\u4f60\u7684\u6cf0\u5c71\u3001\u6cf0\u6c34\uff0c\u90fd\u5728\u5385\u4e2d\u5019\u9a7e\uff0c\u4e24\u4e2a\u5c0f\u7f8e\u4eba\u77aa\u7740\u56db\u53ea\u5927\u773c\u775b\uff0c\u770b\u4e86\u534a\u591c\uff0c\u4e0d\u89c1\u4f60\u7684\u5f71\u5b50\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u2026\u2026\u201d \u3000\u3000\u201c\u522b\u7ed9\u6211\u89e3\u91ca\uff0c\u60f3\u4e2a\u7406\u7531\u544a\u8bc9\u4f60\u90a3\u4f4d\u8001\u5cb3\u4e08\u2026\u2026\u201d\u5f20\u56db\u59d1\u65bd\u5c55\u4f20\u97f3\u4e4b\u672f\uff0c\u6253\u65ad\u4e86\u6c5f\u67ab\u7684\u8bdd\uff0c\u63a5\u9053\uff1a\u201c\u4ed6\u867d\u7136\u5df2\u9010\u6e10\u7684\u5f00\u901a\uff0c\u4e0d\u8fc7\uff0c\u90a3\u80a1\u521a\u6b63\u4e4b\u6c14\uff0c\u8fd8\u672a\u5168\u6d88\uff0c\u6211\u770b\uff0c\u4ed6\u65e9\u7b49\u51fa\u706b\u6c14\uff0c\u4f60\u60f3\u60f3\uff0c\u600e\u4e48\u5c31\u624d\u80fd\u6d88\u6c14\u5316\u706b\uff0c\u8fdb\u53bb\u5427\uff01\u201d \u3000\u3000\u5f20\u56db\u59d1\u5c31\u7ad9\u5728\u5385\u95f4\u53e3\u5904\uff0c\u63a8\u5f00\u6728\u95e8\u3002 \u3000\u3000\u5385\u4e2d\u706f\u706b\u8f89\u714c\uff0c\u666f\u7269\u60c5\u660e\uff0c\u6b27\u9633\u662d\u3001\u7f57\u5170\u3001\u94b1\u7f3a\u3001\u79e6\u516b\u516c\uff0c\u90fd\u5728\u7b49\u5019\u3002 \u3000\u3000\u96ea\u971c\u4e8c\u5973\u8138\u4e0a\u5df2\u89c1\u7b11\u610f\uff0c\u6c5f\u5927\u54e5\uff0c\u6765\u7684\u867d\u7136\u5f88\u665a\uff0c\u4f46\u4ecd\u7136\u6765\u4e86\u3002 \u3000\u3000\u6b27\u9633\u662d\u8138\u8272\u5982\u51b0\uff0c\u5982\u679c\u6c5f\u67ab\u4e0d\u662f\u4e3b\u5e05\u7684\u8eab\u4efd\uff0c\u4e5f\u8bb8\u65e9\u5df2\u7ecf\u8d77\u8eab\u543c\u53eb\u4e86\u3002 \u3000\u3000\u7f57\u5170\u7684\u8138\u8272\uff0c\u4e5f\u4e0d\u600e\u4e48\u597d\u770b\uff0c\u53ea\u4e0d\u8fc7\uff0c\u4e0d\u50cf\u6b27\u9633\u662d\u90a3\u4e48\u663e\u660e\u7f62\u4e86\u3002 \u3000\u3000\u6c5f\u67ab\u56de\u987e\u4e86\u7f13\u6b65\u5165\u5ea7\u7684\u5f20\u56db\u59d1\u4e00\u773c\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u5185\u5bab\u4e2d\u5df2\u6d3e\u51fa\u4e86\u516d\u4e2a\u4e13\u4efb\u6740\u624b\uff0c\u4eca\u5929\u5348\u65f6\u5de6\u53f3\uff0c\u53ef\u62b5\u957f\u5b89\uff0c\u4ed6\u4eec\u72d9\u6740\u7684\u5bf9\u8c61\uff0c\u662f\u6d1b\u9633\u5c45\u4e2d\u7684\u526f\u603b\u7ba1\u97e9\u9738\u3002\u2026\u2026\u201d \u3000\u3000\u201c\u97e9\u9738\u2026\u2026\u201d\u94b1\u7f3a\u9053\uff1a\u201c\u90a3\u4e0d\u5c31\u662f\u4f60\u5417\uff1f\u201d \u3000\u3000\u201c\u5bf9\uff01\u6b63\u662f\u6c5f\u67d0\uff0c\u5c31\u6211\u5f97\u5230\u7684\u6d88\u606f\uff0c\u8fd9\u516d\u5927\u6740\u624b\uff0c\u53ea\u6709\u4e00\u4e2a\u76ee\u7684\uff0c\u90a3\u5c31\u662f\u6740\u4e86\u6211\uff0c\u6240\u4ee5\uff0c\u4ed6\u4eec\u4e0d\u62e9\u624b\u6bb5\uff0c\u4e0d\u8ba1\u8eab\u4efd\u2026\u2026\u201d \u3000\u3000\u201c\u4ed6\u4eec\u662f\u4e0d\u662f\u76f4\u63a5\u627e\u4e0a\u6d1b\u9633\u5c45\u53bb\u2026\u2026\u201d\u5fcd\u4e0d\u4f4f\u6700\u5148\u63a5\u53e3\u7684\u7adf\u662f\u6b27\u9633\u662d\uff0c\u800c\u53bf\u8bed\u58f0\u5e73\u548c\uff0c\u5fc3\u4e2d\u7684\u6012\u6c14\uff0c\u4f3c\u662f\u5df2\u88ab\u8fd9\u4e2a\u9707\u52a8\u4eba\u5fc3\u7684\u6d88\u606f\uff0c\u51b2\u8d70\u4e86\u5927\u534a\u3002 \u3000\u3000\u201c\u6211\u5982\u5b88\u5728\u6d1b\u9633\u5c45\u4e2d\u4e0d\u51fa\u53bb\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u4ed6\u4eec\u5f53\u7136\u4f1a\u627e\u4e0a\u95e8\u53bb\uff0c\u53ea\u4e0d\u8fc7\uff0c\u4ed6\u4eec\u662f\u4f55\u79cd\u8eab\u4efd\u51fa\u73b0\uff0c\u90a3\u5c31\u65e0\u6cd5\u9884\u77e5\u4e86\uff01\u201d \u3000\u3000\u201c\u8fd8\u6709\u4ec0\u4e48\uff1f\u2026\u2026\u201d\u5f20\u56db\u59d1\u65e9\u5df2\u9884\u8ba1\u4e86\u5bf9\u4ed8\u6740\u624b\u7684\u8ba1\u5212\uff0c\u6240\u4ee5\uff0c\u8fd9\u4ef6\u4e8b\u5df2\u65e9\u5728\u5979\u9884\u6599\u4e4b\u4e2d\u3002 \u3000\u3000\u201c\u56db\u5b63\u82b1\u5a62\uff0c\u867d\u662f\u6885\u82b1\u4e3a\u9996\uff0c\u2026\u2026\u6c5f\u67ab\u8bf4\uff0c\u201c\u4f46\u771f\u6b63\u638c\u7406\u4ee4\u8c15\u7684\uff0c\u5374\u662f\u590f\u5a62\u83f1\u82b1\u2026\u2026\u201d \u3000\u3000\u201c\u679c\u7136\u5f88\u72e1\u733e\u7684\u590d\u5f0f\u5e03\u7f6e\uff0c\u9996\u5728\u8bf1\u654c\uff0c\u771f\u6b63\u7684\u653b\u51fb\u529b\u91cf\uff0c\u5374\u9690\u85cf\u4e8e\u8179\u3001\u8170\u4e4b\u4e2d\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u8fd8\u6709\u5417\uff1f\u201d \u3000\u3000\u201c\u6709\uff01\u66f4\u53ef\u6015\u654c\u4eba\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u867d\u4e0d\u8ba1\u5212\u4e2d\u7684\u88ad\u51fb\uff0c\u4f46\u4ed6\u7a81\u7136\u7684\u8d76\u5230\u957f\u5b89\uff0c\u53ef\u80fd\u662f\u5168\u5c40\u53d1\u751f\u53d8\u5316\uff01\u90a3\u5c31\u662f\u5929\u738b\u95e8\u7684\u638c\u95e8\uff0c\u5929\u738b\uff0c\u4f1a\u5728\u4e09\u5929\u5185\u8d76\u6765\u957f\u5b89\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u8fd9\u5c31\u771f\u7684\u4e0d\u597d\u73a9\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u597d\u5bb6\u4f19\uff0c\u4e00\u62e5\u800c\u6765\u5440\uff01\u770b\u6837\u5b50\u8981\u5927\u5e72\u4e00\u573a\u4e86\u2026\u2026\u201d\u6b27\u9633\u662d\u9053\uff1a\u201c\u54b1\u4eec\u4eba\u624b\u4e0d\u591a\uff0c\u771f\u8981\u597d\u597d\u8ba1\u5212\u4e00\u756a\u624d\u884c\u3002\u201d \u3000\u3000\u201c\u6240\u4ee5\uff0c\u6211\u5fd9\u4e86\u4e00\u591c\uff0c\u521a\u521a\u89c1\u5230\u4e03\u5b9d\u5927\u5e08\uff0c\u5c31\u5306\u5306\u8d76\u6765\u2026\u2026\u201d \u3000\u3000\u773c\u770b\u6b27\u9633\u662d\u3001\u7f57\u5170\uff0c\u90fd\u5df2\u706b\u6d88\u6c14\u6563\uff0c\u6c5f\u67ab\u624d\u9002\u65f6\u5730\u63d0\u51fa\u4e86\u89e3\u91ca\u3002 \u3000\u3000\u5012\u662f\u5f20\u56db\u59d1\u611f\u89c9\u5230\u4e8b\u6001\u4e25\u91cd\uff0c\u6025\u6025\u8bf4\u9053\uff1a\u201c\u6c5f\u67ab\uff0c\u5929\u738b\u95e8\uff0c\u8fd8\u6ca1\u6709\u6b63\u5f0f\u6295\u5165\u8fd9\u62fc\u6740\uff0c\u96be\u514d\u4f1a\u6d89\u53ca\u5230\u5929\u738b\u95e8\u4e2d\u4eba\uff0c\u4e5f\u8bb8\uff0c\u8fd9\u662f\u4ed6\u4eec\u523b\u610f\u7684\u5b89\u6392\u5462\uff1f\u201d\u201c\u6c5f\u67ab\uff0c\u6b64\u4e8b\u975e\u540c\u5c0f\u53ef\uff0c\u53ea\u9760\u627e\u6211\u4eec\u8fd9\u70b9\u529b\u91cf\uff0c\u5e94\u4ed8\u4e0d\u6613\u2026\u2026\u201d\u201c\u4f60\u80fd\u8c03\u52a8\u5929\u9a6c\u5802\u4e2d\u4eba\u5417\uff1f\u201d \u3000\u3000\u201c\u4e5f\u8bb8\u80fd\uff0c\u4e0d\u8fc7\uff0c\u6ca1\u6709\u7528\u7684\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u5929\u738b\u95e8\u548c\u51e0\u4e2a\u91cd\u8981\u5c5e\u4e0b\uff0c\u90fd\u548c\u5929\u9a6c\u603b\u5802\u4e2d\u91cd\u8981\u4eba\u7269\u8ba4\u8bc6\uff0c\u672a\u5230\u751f\u6b7b\u5173\u5934\uff0c\u4ed6\u4eec\u4e0d\u4f1a\u51b2\u7a81\uff0c\u76ee\u4e0b\u5728\u6d1b\u9633\u5c45\u7684\uff0c\u6709\u4e00\u4f4d\u957f\u8001\uff0c\u516d\u4e03\u4f4d\u62a4\u6cd5\u7ea7\u7684\u4eba\u7269\uff0c\u5c31\u7b97\u4ed6\u4eec\u80af\u51fa\u624b\uff0c\u4e5f\u62c5\u5f53\u4e0d\u8d77\u5927\u4efb\uff0c\u5f53\u7136\uff0c\u6211\u4e5f\u4e0d\u4f1a\u8ba9\u4ed6\u4eec\u5f88\u8f7b\u677e\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u9053\uff1a\u201c\u8001\u53eb\u5316\u5e26\u6765\u7684\u4eba\uff0c\u6211\u5df2\u7f16\u7ec4\u5b8c\u6210\uff0c\u5171\u5206\u5341\u7ec4\uff0c\u6563\u5e03\u5728\u957f\u5b89\u57ce\u4e2d\u3002\u2026\u2026\u201d \u3000\u3000\u94b1\u7f3a\u63a5\u9053\uff1a\u201c\u5f20\u59d1\u5a18\u4e00\u756a\u7528\u5fc3\u7f16\u7ec4\uff0c\u679c\u7136\u662f\u7acb\u7aff\u89c1\u5f71\uff0c\u5341\u4e2a\u7ec4\uff0c\u6bcf\u7ec4\u4e09\u4eba\uff0c\u4ea4\u4e92\u652f\u63f4\uff0c\u76d1\u89c6\u7684\u8303\u56f4\uff0c\u867d\u7136\u4e0d\u6562\u8bf4\u904d\u53ca\u957f\u5b89\uff0c\u4f46\u534a\u4e2a\u957f\u5b89\u57ce\uff0c\u4e0d\u7b97\u5938\u53e3\uff0c\u4ed6\u4eec\u4e5f\u7ecf\u8fc7\u7b80\u5355\u7684\u6613\u5bb9\uff0c\u8001\u53eb\u5316\u548c\u79e6\u516b\u516c\uff0c\u4e5f\u5728\u4e00\u5b9a\u533a\u57df\u4e2d\u6e38\u8d70\uff0c\u66f4\u96be\u5f97\u662f\u5f20\u59d1\u5a18\u9001\u4e86\u6bcf\u7ec4\u4e00\u7b52\u673a\u7c27\u53d1\u5c04\u7684\u6885\u82b1\u9488\uff0c\u4ee5\u5fa1\u5f3a\u654c\uff0c\u5f53\u7136\uff0c\u4ed6\u4eec\u4e5f\u5907\u597d\u4e86\u81ea\u7edd\u7684\u836f\u7269\u3002\u201d \u3000\u3000\u542c\u5230\u6885\u82b1\u9488\u7b52\uff0c\u6b27\u9633\u662d\u7a81\u7136\u4e00\u76b1\u7709\u5934\uff0c\u4f46\u5374\u5fcd\u4e86\u4e0b\u53bb\uff0c\u6ca1\u6709\u5f00\u53e3\u3002 \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u4f20\u8baf\u7684\u65b9\u6cd5\u5462\uff1f\u201d \u3000\u3000\u201c\u6709\u4e94\u79cd\u4e4b\u591a\uff0c\u2026\u2026\u201d\u94b1\u7f3a\u9053\uff1a\u201c\u6211\u76f8\u4fe1\u5728\u4e00\u523b\u4e4b\u95f4\uff0c\u6d88\u606f\u5c31\u53ef\u4ee5\u4f20\u5230\u8001\u53eb\u5316\u6216\u8005\u79e6\u5144\u7684\u8033\u4e2d\uff0c\u6211\u4eec\u4f1a\u4ee5\u6700\u5feb\u7684\u901f\u5ea6\uff0c\u62a5\u5230\u5f20\u59d1\u5a18\u90a3\u91cc\u3002\u201d \u3000\u3000\u6c5f\u67ab\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u6b27\u9633\u5144\u5ac2\uff0c\u8bf7\u548c\u5927\u59d0\u4e00\u8d77\u5750\u9547\u6b64\u5730\uff0c\u4ee5\u4fbf\u80fd\u53ca\u65f6\u9a70\u63f4\u2026\u2026\u201d \u3000\u3000\u201c\u653e\u5fc3\u554a\uff01\u2026\u2026\u201d\u6b27\u9633\u662d\u7b11\u9053\uff1a\u201c\u6211\u4eec\u7edd\u5bf9\u542c\u4ece\u56db\u59d1\u7684\u8c03\u5ea6\uff0c\u8981\u6211\u4eec\u6253\u8d25\u4ed7\uff0c\u7edd\u4e0d\u6c42\u80dc\u3002\u201d \u3000\u3000\u201c\u6c5f\u67ab\u554a\uff01\u4f60\u65e2\u7136\u5728\u8fd9\u91cc\uff0c\u5c31\u7531\u4f60\u4e0b\u8fbe\u7b2c\u4e00\u9053\u5bf9\u654c\u4ee4\u8c15\u5427\uff01\u2026\u2026\u201d\u5f20\u56db\u59d1\u5401\u53e3\u6c14\uff0c\u9053\uff1a \u3000\u3000\u201c\u5927\u59d0\u6211\u7ec8\u662f\u5973\u4eba\uff0c\u81ea\u5df1\u77e5\u9053\u9b44\u529b\u4e0d\u591f\u3002\u201d \u3000\u3000\u201c\u5927\u59d0\u4e0d\u7528\u8c26\u8f9e\uff0c\u4f60\u5584\u4e8e\u7b56\u5212\uff0c\u7cbe\u4e8e\u5e03\u7f6e\uff0c\u8d35\u95e8\u4e2d\u7684\u8fde\u7edc\u624b\u6cd5\uff0c\u80fd\u5728\u6df7\u4e71\u4e2d\uff0c\u53ec\u805a\u81ea\u5df1\u4eba\u624b\uff0c\u773c\u4e0b\u7b2c\u4e8c\u4ef6\u5927\u4e8b\uff0c\u5148\u6e05\u9664\u5185\u5bab\u4e2d\u6d3e\u6765\u7684\u6740\u624b\uff0c\u7531\u6211\u8bf1\u654c\uff0c\u96ea\u3001\u971c\u4e24\u4f4d\u59d1\u5a18\u4e3a\u4e3b\u529b\u622a\u6740\u2026\u2026\u201d\u6c5f\u67ab\u8bf4\u51fa\u4e86\u5fc3\u4e2d\u7684\u8ba1\u5212\u3002 \u3000\u3000\u94b1\u7f3a\u7ad9\u8d77\u8eab\u5b50\uff0c\u9053\uff1a\u201c\u597d\uff01\u8001\u53eb\u5316\u4e5f\u53bb\u8c03\u6574\u4e00\u4e0b\u5e03\u7f72\uff0c\u5168\u529b\u6ce8\u610f\u516d\u5927\u6740\u624b\u884c\u8e2a\uff0c\u770b\u770b\u80fd\u4e0d\u80fd\u638c\u63e1\u4f4f\u4ed6\u4eec\u7684\u884c\u6b62\u3002\u201d \u3000\u3000\u5e26\u7740\u79e6\u516b\u516c\u8d77\u8eab\u800c\u53bb\u3002 \u3000\u3000\u541f\u96ea\u9053\uff1a\u201c\u6c5f\u5927\u54e5\uff0c\u6211\u4eec\u4e5f\u8981\u53bb\u51c6\u5907\u4e00\u4e0b\uff0c\u4e0d\u80fd\u6709\u5931\u5929\u72d0\u95e8\u7684\u9762\u5b50\u554a\uff01\u201d \u3000\u3000\u201c\u59d0\u548c\u6211\uff0c\u867d\u7136\u5145\u6ee1\u7740\u4fe1\u5fc3\u2026\u2026\u201d\u541f\u971c\u9053\uff1a\u201c\u4f46\u7b2c\u4e00\u6b21\u561b\uff01\u603b\u96be\u514d\u6709\u4e9b\u7d27\u5f20\uff0c\u6c5f\u5927\u54e5\uff0c\u4e0d\u966a\u4f60\u4e86\u3002\u201d \u3000\u3000\u4e8c\u5973\u76f8\u89c6\u4e00\u7b11\uff0c\u7275\u7740\u624b\u79bb\u5f00\u5927\u5385\u3002 \u3000\u3000\u201c\u7b49\u4e86\u5927\u534a\u591c\uff0c\u771f\u8fd8\u662f\u6709\u70b9\u7d2f\u4e86\u2026\u2026\u201d\u6b27\u9633\u662d\u4f38\u4e2a\u61d2\u8170\uff0c\u9053\uff0c\u201c\u7f57\u5170\uff0c\u6211\u4eec\u4e5f\u8be5\u53bb\u5750\u606f\u4e00\u4e0b\uff0c\u8bf4\u4e0d\u5b9a\u5348\u540e\u8fd8\u6709\u4e00\u573a\u5927\u6218\u5462\uff01\u201d \u3000\u3000\u7f57\u5170\u9053\uff1a\u201c\u597d\u561b\uff01\u51fa\u5ac1\u4ece\u592b\uff0c\u4f60\u600e\u4e48\u8bf4\uff0c\u4e3a\u59bb\u5c31\u600e\u4e48\u542c\u4e86\u3002\u201d \u3000\u3000\u201c\u6c5f\u8001\u5f1f\uff0c\u4f60\u4eec\u5929\u72d0\u95e8\u7684\u6b66\u529f\uff0c\u679c\u7136\u5389\u5bb3\u2026\u2026\u201d\u6b27\u9633\u662d\u9053\uff0c\u201c\u968f\u624b\u5c31\u662f\u4e00\u7897\u8ff7\u9b42\u6c64\uff0c\u8fd8\u771f\u53eb\u4eba\u559d\u7684\u6709\u4e9b\u98d8\u98d8\u7136\u5462\uff1f\u201d \u3000\u3000\u5fcd\u4e0d\u4f4f\u5fae\u5fae\u4e00\u7b11\uff0c\u548c\u7f57\u5170\u8054\u8882\u800c\u53bb\u3002 \u3000\u3000\u4ed6\u7b11\u7684\u5f88\u5f00\u5fc3\uff0c\u663e\u662f\u5fc3\u4e2d\u706b\u6c14\u65e9\u6d88\u3002 \u3000\u3000\u5927\u5385\u4e2d\uff0c\u53ea\u4f59\u4e0b\u4e86\u5f20\u56db\u59d1\u548c\u6c5f\u67ab\u3002 \u3000\u3000\u201c\u884c\u554a\uff01\u6709\u4f60\u7684\u2026\u2026\u201d\u5f20\u56db\u59d1\u7b11\u9053\uff1a\u201c\u867d\u7136\u4f60\u529f\u529b\u6df1\u539a\uff0c\u7cbe\u795e\u7115\u53d1\uff0c\u4f46\u6211\u770b\u5f97\u51fa\u6765\uff0c\u4f60\u53bb\u98ce\u6d41\u4e86\u4e00\u591c\uff0c\u6240\u4ee5\uff0c\u5927\u548c\u5c1a\u627e\u4e0d\u5230\u4f60\uff0c\u4e5f\u4e0d\u4fbf\u53bb\u627e\uff0c\u6211\u76f8\u4fe1\u7f57\u5170\u4e5f\u770b\u5f97\u51fa\u6765\uff0c\u4e24\u4e2a\u4e2b\u5934\u4e5f\u8bb8\u77a7\u4e0d\u51fa\u7834\u7efd\uff0c\u4f46\u5979\u4eec\u53ef\u4ee5\u95fb\u5230\u4f60\u8eab\u4e0a\u6b8b\u7559\u7684\u8102\u7c89\u9999\u6c14\u3002\u201d \u3000\u3000\u6c5f\u67ab\u6697\u6697\u5fd6\u9053\uff1a\u201c\u5982\u975e\u9752\u51e4\u5e2e\u6211\u64e6\u4e86\u4e00\u4e2a\u6fa1\uff0c\u53ea\u6015\u5927\u5385\u4e2d\u4eba\uff0c\u90fd\u80fd\u95fb\u5230\u90a3\u4e9b\u8102\u7c89\u9999\u5473\u4e86\u3002\u201d \u3000\u3000\u4f46\u95fb\u5f20\u56db\u59d1\u9053\uff1a\u201c\u4f60\u4e00\u591c\u5927\u6218\uff0c\u4e5f\u8be5\u56de\u53bb\u4f11\u606f\u4e00\u4e0b\u4e86\uff0c\u4f55\u51b5\uff0c\u4e2d\u5348\u8fd8\u8981\u8fce\u654c\u3002\u201d\u7ad9\u8d77\u8eab\u5b50\uff0c\u8f6c\u8eab\u6b32\u53bb\u3002 \u3000\u3000\u201c\u5927\u59d0\uff0c\u5c0f\u5f1f\u7684\u4e8b\uff0c\u7792\u4e0d\u8fc7\u4f60\uff0c\u4e5f\u4ece\u6ca1\u6709\u51c6\u5907\u7792\u4f60\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u73b0\u5728\uff0c\u5c0f\u5f1f\u8981\u5949\u544a\u8be6\u60c5\u3002\u201d \u3000\u3000\u201c\u4e0d\u5fc5\u5427\uff01\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u90a3\u4e9b\u5e8a\u7b2c\u95f4\u7684\u537f\u537f\u6211\u6211\uff0c\u96be\u9053\u4e5f\u8981\u8bf4\u7ed9\u6211\u542c\u5417\uff1f\u5927\u59d0\u7684\u5ea6\u91cf\u518d\u5927\uff0c\u4e5f\u4f1a\u542c\u5f97\u4e0d\u8212\u670d\u554a\uff01\u201d \u3000\u3000\u201c\u662f\u8981\u5927\u59d0\u5e2e\u5fd9\u2026\u2026\u201d \u3000\u3000\u5f20\u56db\u59d1\u970d\u7136\u8f6c\u56de\u8eab\u5b50\uff0c\u7f13\u7f13\u843d\u5750\uff0c\u53cc\u76ee\u76ef\u6ce8\u5728\u6c5f\u67ab\u7684\u8138\u4e0a\u77a7\u770b\uff0c\u76ee\u5149\u4e2d\u662f\u4e00\u7247\u6e29\u67d4\u548c\u5173\u6000\u4ea4\u7ec7\u7684\u795e\u8272\uff0c\u9053\uff1a\u201c\u8bf4\u5440\uff01\u51fa\u4e86\u4ec0\u4e48\u6bdb\u75c5\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u5766\u7136\u8bf4\u51fa\u4e86\u4e00\u5207\u7ecf\u8fc7\u3002 \u3000\u3000\u5f20\u56db\u59d1\u70b9\u70b9\u5934\u9053\uff1a\u201c\u60f3\u4e0d\u5230\uff0c\u4f60\u6709\u5982\u6b64\u5927\u7684\u6210\u5c31\uff0c\u5f15\u51e4\u4e0a\u5e8a\uff0c\u91c7\u82b1\u5165\u63e1\uff0c\u90a3\u4e0d\u662f\u5df2\u7ecf\u638c\u63e1\u4e86\u6d1b\u9633\u5c45\u5417\uff1f\u201d \u3000\u3000\u201c\u4f46\u5c0f\u5f1f\u89c9\u7740\uff0c\u8ba9\u5979\u4eec\u7559\u5728\u539f\u4f4d\u4e0a\uff0c\u5e94\u8be5\u6bd4\u6295\u6548\u8fc7\u6765\u7684\u4f5c\u7528\u5927\u4e9b\u3002\u201d \u3000\u3000\u201c\u55ef\uff01\u4f60\u8fd8\u6ca1\u6709\u88ab\u80dc\u5229\u51b2\u660f\u5934\u8111\uff0c\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u83f1\u82b1\u672c\u662f\u4e00\u7740\u6697\u68cb\uff0c\u73b0\u5728\uff0c\u7a81\u7136\u5192\u51fa\u4e86\u5934\uff0c\u5927\u59d0\u6211\u7684\u770b\u6cd5\u662f\uff0c\u5979\u53ef\u80fd\u5df2\u53d1\u89c9\u5b50\u4ec0\u4e48\u8bc1\u636e\uff1f\u6885\u82b1\u7684\u5730\u4f4d\uff0c\u53ef\u80fd\u5df2\u7ecf\u4e0d\u7a33\uff0c\u4f46\u4e25\u91cd\u5230\u4ec0\u4e48\u7a0b\u5ea6\uff1f\u8981\u770b\u5979\u548c\u83f1\u82b1\u79c1\u4eba\u7684\u60c5\u611f\u5982\u4f55\u4e86\uff1f\u83f1\u82b1\u5448\u62a5\u5185\u5bab\u7684\u7980\u6587\uff0c\u5982\u4f55\u7f16\u6392\u6885\u82b1\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u5c0f\u5f1f\u60f3\u4e0d\u901a\uff0c\u83f1\u82b1\u5982\u4f55\u80fd\u628a\u6d88\u606f\u4f20\u51fa\u53bb\uff0c\u5185\u5bab\u5728\u90a3\u91cc\uff1f\u6211\u4e0d\u77e5\u9053\uff0c\u4f46\u81f3\u5c11\u5e94\u5728\u6570\u767e\u91cc\u5916\uff0c\u6211\u4eec\u66fe\u4ed4\u7ec6\u7684\u641c\u67e5\u8fc7\uff0c\u6d1b\u9633\u5c45\u5185\u5bab\u4e2d\u4eba\uff0c\u6ca1\u6709\u9972\u517b\u4fe1\u9e3d\u3002\u201d \u3000\u3000\u201c\u6709\u4e00\u79cd\u730e\u9e70\uff0c\u6bd4\u4fe1\u9e3d\u66f4\u6709\u6548\u7528\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u5979\u9ad8\u98de\u5165\u4e91\uff0c\u4e00\u65e5\u95f4\u5f80\u8fd4\u5343\u91cc\uff0c\u4e0d\u4f46\u884c\u8e2a\u96be\u89c5\uff0c\u800c\u4e14\u8fd8\u80fd\u6355\u6349\u4fe1\u9e3d\uff0c\u6211\u76f8\u4fe1\u5929\u9a6c\u5802\u7684\u4fe1\u9e3d\uff0c\u5df2\u88ab\u6355\u53bb\u4e86\u4e0d\u5c11\uff0c\u4e5f\u638c\u63e1\u4e86\u5929\u9a6c\u5802\u4e0d\u5c11\u7684\u673a\u5bc6\u6d88\u606f\uff0c\u4f46\u8fd9\u79cd\u4f20\u8baf\u7684\u730e\u9e70\u9a6f\u7ec3\u4e0d\u6613\uff0c\u81f3\u5c11\u4e5f\u8981\u4e24\u4e09\u5e74\u65f6\u95f4\uff0c\u624d\u6709\u9a6f\u7ec3\u6210\u529f\u7684\u5e0c\u671b\u3002\u201d \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\uff0c\u2026\u2026\u201d\u8bed\u58f0\u4e00\u987f\u3002\u63a5\u9053\uff1a\u201c\u9752\u51e4\u7684\u4e8b\uff0c\u5927\u59d0\u53ef\u6709\u826f\u7b56\uff1f\u6b64\u4e8b\u5173\u7cfb\u91cd\u5927\uff0c\u2026\u2026\u201d \u3000\u3000\u5f20\u56db\u59d1\u5ae3\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u62a2\u4e86\u4eba\u5bb6\u7684\u5973\u4eba\uff0c\u81ea\u5df1\u5374\u53c8\u6015\u6234\u7eff\u5e3d\u5b50\uff0c\u2026\u2026\u201d\u4f38\u51fa\u53f3\u624b\u98df\u6307\uff0c\u5728\u6c5f\u67ab\u7684\u9876\u95e8\u4e0a\uff0c\u91cd\u91cd\u7684\u70b9\u4e86\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u53bb\u544a\u8bc9\u9752\u98ce\uff0c\u4eca\u5929\u4e2d\u5348\uff0c\u6211\u53bb\u770b\u5979\uff0c\u53ea\u8981\u5979\u80af\u542c\u5927\u59d0\u7684\u8bdd\uff0c\u6211\u4fdd\u7ba1\u90a3\u4f4d\u597d\u8272\u5982\u547d\u7684\u5929\u738b\u4e0d\u6562\u78b0\u5979\uff0c\u751a\u81f3\u8fde\u4eb2\u5979\u4e00\u4e0b\u7684\u5fc3\u60c5\uff0c\u4e5f\u4f1a\u6d88\u5931\u3002\u201d \u3000\u3000\u201c\u90a3\u662f\u4ec0\u4e48\u65b9\u6cd5\uff0c\u7adf\u6709\u5982\u6b64\u7684\u795e\u6548\u2026\u2026\u201d\u6c5f\u67ab\u5fcd\u4e0d\u4e0b\u5fc3\u4e2d\u7684\u597d\u5947\uff0c\u95ee\u9053\uff1a\u201c\u5c0f\u5f1f\u53ef\u5426\u5148\u4f5c\u9884\u95fb\u3002\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u884c\uff0c\u60f3\u77e5\u9053\uff0c\u5c31\u53bb\u95ee\u95ee\u4f60\u90a3\u4f4d\u51c6\u5907\u4ee5\u8eab\u76f8\u6b89\u7684\u8d24\u59bb\u5427\uff01\u2026\u2026\u201d\u5f20\u56db\u59d1\u7b11\u9053\uff1a\u201c\u6cd5\u4e0d\u4f20\u516d\u8033\uff0c\u5144\u5f1f\uff0c\u4f60\u5c31\u5fcd\u4e00\u5fcd\u5427\uff01\u201d \u3000\u3000\u6c5f\u67ab\u6c89\u541f\u4e86\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u5927\u59d0\uff0c\u5c0f\u5f1f\u60f3\u5728\u4f60\u7684\u623f\u4e2d\u4f11\u606f\u4e00\u4e0b\u3002\u201d \u3000\u3000\u201c\u4e0d\u884c\uff0c\u4f60\u662f\u8bda\u5fc3\u8ba9\u7f57\u5170\u548c\u6b27\u9633\u662d\u770b\u7b11\u8bdd\u5440\uff01\u4f55\u51b5\uff0c\u8fd8\u6709\u4e24\u4e2a\u5c0f\u72d0\u5973\u5c31\u4f4f\u5728\u6211\u7684\u9694\u58c1\uff0c\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u4e00\u591c\u7f20\u7ef5\uff0c\u96be\u9053\u8fd8\u4e0d\u591f\u53d7\u7528\uff0c\u56de\u53bb\u6d1b\u9633\u5c45\u5427\uff01\u5bf9\u4ed8\u8fc7\u51e0\u4e2a\u6740\u624b\u4e4b\u540e\uff0c\u6211\u4f1a\u4e3a\u4f60\u5b89\u6392\u4e2a\u5c0f\u4f11\u5047\u671f\uff0c\u7ea6\u4e24\u4e2a\u5c0f\u72d0\u5973\u4e00\u8d77\u966a\u4f60\u3002\u201d \u3000\u3000\u201c\u5927\u59d0\uff0c\u96ea\u3001\u971c\u4e8c\u5973\u7684\u4e8b\uff0c\u6162\u6162\u518d\u8bf4\uff0c\u7b54\u5e94\u6211\u7684\u4e8b\uff0c\u53ef\u4e0d\u80fd\u53d8\u5366\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u6211\u662f\u7b2c\u4e00\u4e2a\u6cbe\u4f60\u8eab\u7684\u4eba\uff0c\u60f3\u5ff5\u6700\u591a\u7684\u4e5f\u662f\u4f60\u3002\u201d \u3000\u3000\u201c\u591f\u4e86\uff0c\u5144\u5f1f\uff0c\u518d\u704c\u6211\u8ff7\u9b42\u6c64\uff0c\u4f1a\u628a\u6211\u8ff7\u6b7b\uff0c\u5927\u59d0\u8fd9\u4e00\u751f\u5df2\u4e3a\u4f60\u800c\u6d3b\uff0c\u518d\u4e5f\u65e0\u6cd5\u7ed9\u4f60\u66f4\u591a\u7684\u4e1c\u897f\u4e86\uff0c\u2026\u2026\u201d\u5f20\u56db\u59d1\u8f7b\u8f7b\u628a\u6c5f\u67ab\u62e5\u5165\u6000\u4e2d\uff0c\u63a5\u9053\uff1a\u201c\u56de\u53bb\u5427\uff01\u80e1\u840d\u3001\u6bb5\u4e5d\uff0c\u90fd\u8fd8\u5728\u7b49\u7740\u4f60\u9063\u6d3e\u884c\u52a8\u5462\u3002\u201d \u3000\u3000\u6c5f\u67ab\u70b9\u70b9\u5934\uff0c\u8f6c\u8eab\u800c\u53bb\u3002 \u3000\u3000---- \u3000 \u7b2c\u5341\u4e09\u56de\u3000\u957f\u8857\u5934\u6740\u624b\u906d\u8840\u88c1 \u3000 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u4e03\u5b9d\u548c\u5c1a\u65e9\u5df2\u7ecf\u5148\u4f5c\u4e86\u5b89\u6392\uff0c\u80e1\u840d\u3001\u6bb5\u4e5d\uff0c\u5949\u4ee4\u5750\u606f\uff0c\u51c6\u5907\u4f5c\u6c5f\u67ab\u7684\u62a4\u4ece\u3002\u63a5\u5e94\uff0c\u80e1\u5143\u548c\u90ed\u5929\u540c\u9547\u5b88\u5728\u6d1b\u9633\u5c45\u4e2d\u3002 \u3000\u3000\u5927\u548c\u5c1a\u628a\u63e1\u7684\u65f6\u95f4\u5f88\u51c6\uff0c\u6c5f\u67ab\u56de\u5230\u4f4f\u5904\uff0c\u5201\u9e4f\u4e5f\u6b63\u597d\u8d76\u5230\u6c42\u89c1\u97e9\u9738\u3002 \u3000\u3000\u5201\u9e4f\u662f\u79ef\u5e74\u8001\u8d3c\uff0c\u9ed8\u67e5\u4e86\u80e1\u840d\u7b49\u7684\u4f4f\u5904\u5ffd\u7136\u79fb\u5230\u97e9\u9738\u9644\u8fd1\uff0c\u5df2\u89c9\u5230\u4e8b\u975e\u5bfb\u5e38\uff0c\u6240\u4ee5\uff0c\u5929\u8fd8\u672a\u4eae\uff0c\u5c31\u8d76\u6765\u67e5\u770b\uff0c\u679c\u7136\u53d1\u89c9\u4e86\u4e03\u5b9d\u548c\u5c1a\u7b49\u8fd8\u672a\u4f11\u606f\uff0c\u67e5\u770b\u5c31\u53d8\u6210\u4e86\u62dc\u8bbf\u3002 \u3000\u3000\u5201\u9e4f\u521a\u521a\u843d\u5750\uff0c\u6c5f\u67ab\u5df2\u7f13\u6b65\u884c\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u770b\u6c5f\u67ab\u8863\u7740\u6574\u9f50\uff0c\u4f3c\u662f\u4e00\u591c\u672a\u7720\u7684\u6837\u5b50\uff0c\u4e0d\u7981\u5fae\u5fae\u4e00\u6014\uff0c\u9053\uff1a\u201c\u97e9\u5144\u4e00\u591c\u672a\u7761\u554a\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4e24\u5929\u662f\u5e2d\u4e0d\u5b89\u6795\uff0c\u98df\u96be\u4e0b\u54bd\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u53f9\u606f\u4e00\u58f0\u53c8\u9053\uff1a\u201c\u4e0d\u77e5\u9053\u6709\u591a\u5c11\u9ad8\u624b\u3001\u523a\u5ba2\uff0c\u5df2\u6df7\u5165\u4e86\u957f\u5b89\u57ce\u4e2d\uff0c\u51c6\u5907\u53d6\u4ee3\u6211\u97e9\u9738\u4e4b\u547d\uff0c\u5728\u4e0b\u4e0d\u5f97\u4e0d\u9632\u5907\u4e00\u4e0b\u4e86\uff01\u201d \u3000\u3000\u201c\u6709\u8fd9\u7b49\u4e8b\uff0c\u5201\u67d0\u7adf\u7136\u662f\u5168\u7136\u4e0d\u77e5\u2026\u2026\u201d\u5201\u9e4f\u9053\uff1a\u201c\u6765\u7684\u662f\u90a3\u8def\u4eba\u9a6c\uff1f\u201d \u3000\u3000\u201c\u5185\u5bab\u4e00\u7cfb\u4e2d\u9063\u6d3e\u6765\u7684\u4e13\u4efb\u6740\u624b\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u5201\u5144\u6765\u7684\u6b63\u597d\uff0c\u5144\u5f1f\u6b63\u51c6\u5907\u9063\u4eba\u76f8\u8bf7\uff0c\u53ea\u662f\u4e0d\u4fbf\u6df1\u591c\u60ca\u6270\u3002\u2026\u2026\u201d \u3000\u3000\u5201\u9e4f\u63a5\u9053\uff1a\u201c\u97e9\u5144\u5982\u6709\u5dee\u9063\uff0c\u5c3d\u7ba1\u5429\u5490\uff0c\u5144\u5f1f\u662f\u5168\u529b\u4ee5\u8d74\u3002\u201d \u3000\u3000\u201c\u5bf9\u4ed8\u523a\u5ba2\u7684\u4e8b\uff0c\u5144\u5f1f\u5df2\u6709\u8ba1\u8f83\uff0c\u4e0d\u52b3\u5201\u5144\u70e6\u5fc3\uff0c\u53ea\u662f\u6d1b\u9633\u5c45\u4e2d\u7684\u4e8b\uff0c\u8981\u5201\u5144\u591a\u8d39\u5fc3\u4e86\u2026\u2026\u201d\u6c5f\u67ab\u6f47\u6d12\u4e00\u7b11\u53c8\u9053\uff1a\u201c\u6211\u65e0\u6cd5\u9884\u6599\u6d1b\u9633\u5c45\u4e2d\u4eba\uff0c\u5982\u4f55\u914d\u5408\u523a\u5ba2\u7684\u884c\u52a8\uff0c\u53ea\u597d\u628a\u56db\u5b63\u82b1\u5a62\u4ea4\u7ed9\u5201\u5144\u76d1\u89c6\u4e86\uff01\u201d \u3000\u3000\u201c\u884c\uff01\u5929\u865a\u5b50\u526f\u603b\u5802\u4e3b\u4e34\u53bb\u4e4b\u9645\uff0c\u7559\u4e0b\u4e86\u516d\u4f4d\u62a4\u6cd5\uff0c\u8fde\u5201\u67d0\u7b97\u4e0a\u4e00\u5171\u4e03\u4e2a\u4eba\uff0c\u5927\u6982\u90fd\u53ef\u4ee5\u6d3e\u4e0a\u7528\u573a\uff0c\u4e24\u4e2a\u62a4\u6cd5\uff0c\u5df2\u4f4f\u8fdb\u4e86\u6d1b\u9633\u5c45\u7684\u5bbe\u9986\u4e2d\uff0c\u56db\u4e2a\u4eba\u4efb\u5728\u5ba2\u6808\uff0c\u5929\u4e00\u4eae\uff0c\u5144\u5f1f\u5c31\u628a\u4ed6\u4eec\u5168\u90e8\u8c03\u8fdb\u6d1b\u9633\u5c45\u6765\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u6211\u4f1a\u62a5\u8bf7\u9093\u603b\u7ba1\u548c\u5201\u5144\u914d\u5408\u2026\u2026\u201d\u6c5f\u67ab\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u63a5\u9053\uff1a\u201c\u770b\u8d77\u6765\uff0c\u548c\u5185\u5bab\u4e00\u7cfb\u4e2d\u4eba\u7684\u51b2\u7a81\uff0c\u5df2\u7ecf\u65e0\u6cd5\u907f\u514d\uff0c\u4f46\u4e0d\u77e5\u603b\u5802\u6001\u5ea6\u5982\u4f55\uff1f\u5929\u865a\u5b50\u526f\u603b\u5802\u4e3b\uff0c\u8fd1\u65e5\u4e2d\uff0c\u4f1a\u4e0d\u4f1a\u91cd\u4e34\u957f\u5b89\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4e00\u70b9\uff0c\u5144\u5f1f\u8fd8\u672a\u63a5\u5230\u6307\u4ee4\u2026\u2026\u201d\u5201\u9e4f\u9053\uff1a\u201c\u6b64\u4e8b\u5173\u7cfb\u91cd\u5927\uff0c\u5c31\u7b97\u603b\u5802\u4e3b\uff0c\u4e5f\u4e0d\u4fbf\u8f7b\u7387\u51b3\u5b9a\uff0c\u4f46\u4e5f\u4e0d\u4f1a\u518d\u62d6\u4e0b\u53bb\uff0c\u4e09\u4e94\u65e5\u5185\uff0c\u5e94\u8be5\u6709\u4e00\u4e2a\u660e\u6717\u7684\u6307\u793a\u4e0b\u6765\u3002\u201d \u3000\u3000\u6c5f\u67ab\u5fc3\u4e2d\u4e00\u52a8\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5201\u5144\uff0c\u54b1\u4eec\u7684\u603b\u5802\u4e3b\uff0c\u662f\u600e\u6837\u7684\u4e00\u4e2a\u4eba\uff1f\u8bf4\u6765\uff0c\u60ed\u6127\u7684\u5f88\uff0c\u5144\u5f1f\u867d\u7136\u52a0\u5165\u4e86\u5929\u9a6c\u5802\uff0c\u8fd8\u4e0d\u77e5\u9053\u603b\u5802\u4e3b\u7684\u59d3\u540d\u5462\uff01\u201d \u3000\u3000\u5201\u9e4f\u7adf\u7136\u4e5f\u88ab\u95ee\u5f97\u5446\u4f4f\u4e86\uff0c\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u9664\u4e86\u51e0\u4e2a\u526f\u603b\u5802\u4e3b\u4e4b\u5916\uff0c\u5927\u7ea6\u90fd\u4e0d\u77e5\u9053\u4ed6\u771f\u7684\u8eab\u4efd\uff01\u201d \u3000\u3000\u6c5f\u67ab\u5fc3\u5934\u4e00\u9707\uff0c\u5fd6\u9053\uff1a\u201c\u8fd9\u4e16\u95f4\uff0c\u8fd8\u771f\u6709\u8fd9\u7b49\u602a\u4e8b\uff0c\u4e00\u4e2a\u9886\u5bfc\u7740\u5341\u5206\u5e9e\u5927\u7ec4\u5408\u7684\u9996\u8111\uff0c\u548c\u4f17\u591a\u7684\u5c5e\u4e0b\uff0c\u76f8\u5904\u4e86\u5f88\u591a\u5e74\uff0c\u7adf\u7136\u80fd\u628a\u59d3\u540d\u9690\u8d77\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\uff0c\u542c\u8d77\u6765\u6709\u4e9b\u4e0d\u53ef\u601d\u8bae\u2026\u2026\u201d\u5201\u9e4f\u671b\u7740\u6c5f\u67ab\u8138\u4e0a\u7684\u6000\u7591\u795e\u8272\uff0c\u63a5\u9053\uff1a\u201c\u4f46\u8fd9\u786e\u662f\u4e2a\u4e8b\u5b9e\uff0c\u6709\u65f6\u5019\uff0c\u6211\u66fe\u5728\u60f3\uff0c\u7a76\u7adf\u662f\u5426\u6709\u603b\u5802\u4e3b\u8fd9\u4e2a\u4eba\u2026\u2026\u201d \u3000\u3000\u201c\u6162\u6765\uff0c\u6162\u6765\u2026\u2026\u201d\u6c5f\u67ab\u62e6\u4f4f\u4e86\u4e60\u9e4f\uff0c\u62db\u547c\u4e03\u5b9d\u548c\u5c1a\u8fdb\u5165\u5c0f\u5385\uff0c\u624d\u63a9\u4e0a\u5385\u95e8\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u9f50\u5144\uff08\u4e03\u5b9d\u548c\u5c1a\u5316\u540d\u9f50\u56db\uff09\uff0c\u662f\u81ea\u5df1\u4eba\uff0c\u4e5f\u662f\u97e9\u67d0\u7684\u5fc3\u8179\uff0c\u5201\u5144\u4ee5\u540e\u6709\u4ec0\u4e48\u6307\u6559\uff0c\u627e\u4e0d\u5230\u5144\u5f1f\u65f6\uff0c\u53ef\u5b8c\u5168\u544a\u8bc9\u9f50\u5144\uff0c\u2026\u2026\u201d \u3000\u3000\u201c\u597d\uff01\u6709\u4f60\u97e9\u5144\u8fd9\u51e0\u53e5\u8bdd\uff0c\u5201\u67d0\u4eba\u81ea\u5f53\u9075\u529e\u2026\u2026\u201d\u8f6c\u5411\u4e03\u5b9d\u548c\u5c1a\u4e00\u62b1\u62f3\uff0c\u9053\uff1a\u201c\u4ee5\u540e\uff0c\u8fd8\u8981\u9f50\u5144\u591a\u591a\u5173\u7167\u3002\u201d \u3000\u3000\u201c\u8a00\u91cd\uff0c\u8a00\u91cd\u2026\u2026\u201d\u5927\u548c\u5c1a\u8f6c\u5411\u6c5f\u67ab\uff0c\u9053\uff1a\u201c\u521a\u521a\u4f20\u6765\u7684\u5feb\u62a5\uff0c\u7b2c\u4e00\u6279\u6d3e\u6765\u7684\u6740\u624b\uff0c\u5df2\u5728\u957f\u5b89\u57ce\u897f\u4e8c\u5341\u91cc\u5206\u5f00\uff0c\u5404\u81ea\u6df7\u5165\u957f\u5b89\uff0c\u884c\u8e2a\u975e\u5e38\u7684\u8c28\u614e\u3001\u8be1\u5bc6\u3002\u201d \u3000\u3000\u5201\u9e4f\u542c\u5f97\u4e00\u6014\uff0c\u9053\uff1a\u201c\u600e\u4e48\uff1f\u4f60\u4eec\u4e5f\u5e03\u6709\u773c\u7ebf\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u6211\u4eec\u7684\u4eba\u624b\u867d\u7136\u4e0d\u591a\uff0c\u4f46\u5206\u914d\u3001\u5e03\u7f72\u7684\u8fd8\u7b97\u4e0d\u9519\uff0c\u6d88\u606f\u4e5f\u8fd8\u7075\u901a\u3002\u201d \u3000\u3000\u8fd9\u756a\u8bdd\u5168\u662f\u80e1\u626f\uff0c\u4f46\u5728\u4e03\u5b9d\u548c\u5c1a\u4e00\u756a\u7528\u5fc3\u7f16\u6392\u4e0b\u6765\uff0c\u53ea\u542c\u5f97\u5201\u9e4f\u5168\u65e0\u6000\u7591\u3002 \u3000\u3000\u7531\u8896\u4e2d\u53d6\u51fa\u4e00\u4e2a\u9ec4\u7eeb\u8868\u76ae\u7684\u6298\u5b50\uff0c\u4e03\u5b9d\u5927\u5e08\u606d\u606d\u656c\u656c\u4ea4\u7ed9\u4e86\u97e9\u9738\uff0c\u9053\uff1a\u201c\u8fd9\u662f\u526f\u603b\u7ba1\u8981\u7684\u8d44\u6599\uff0c\u5df2\u7ecf\u8bb0\u8ff0\u6574\u9f50\uff0c\u606d\u8bf7\u8fc7\u76ee\u3002\u201d \u3000\u3000\u7531\u4e8e\u516d\u5b9d\u548c\u5c1a\u8fd9\u756a\u505a\u4f5c\uff0c\u5bb3\u5f97\u5201\u9e4f\u4e5f\u4e0d\u5f97\u4e0d\u5c0f\u5fc3\u7ffc\u7ffc\u8d77\u6765\uff0c\u5bf9\u6c5f\u67ab\u66f4\u52a0\u4e86\u51e0\u5206\u656c\u754f\u3002 \u3000\u3000\u201c\u5201\u5144\uff0c\u603b\u5802\u4e3b\u8eab\u4efd\u5c0a\u8d35\uff0c\u4f46\u5c5e\u4e0b\u4f17\u591a\uff0c\u5404\u79cd\u5927\u5c0f\u4e8b\u60c5\uff0c\u90fd\u9700\u8981\u4ed6\u51b3\u5b9a\uff0c\u6587\u4e66\u4e4b\u4e0a\uff0c\u603b\u4e5f\u6709\u4e2a\u79f0\u8c13\u5427\u3002\u201d\u6c5f\u67ab\u5f97\u51fa\u4e86\u8d28\u7591\u3002 \u3000\u3000\u201c\u6709\uff01\u4f46\u90fd\u662f\u526f\u603b\u5802\u4e3b\u4ee3\u4e3a\u53d1\u6587\u53d1\u884c\uff0c\u2026\u2026\u201d\u5201\u9e4f\u9053\uff1a\u201c\u4ee5\u53f8\u9a6c\u827a\u548c\u7533\u96ea\u541b\u4ee3\u529e\u51b3\u884c\u7684\u4e8b\u52a1\u8f83\u591a\uff0c\u4e0d\u8fc7\uff0c\u51e1\u662f\u91cd\u5927\u7684\u4e8b\u60c5\uff0c\u5148\u5728\u4f1a\u8bae\u4e0a\u8ba8\u8bba\u8fc7\u4e86\uff0c\u6240\u4ee5\uff0c\u8fd9\u591a\u5e74\u6765\uff0c\u4e5f\u4ece\u672a\u5f15\u8d77\u8fc7\u4e89\u8bae\u3002\u201d \u3000\u3000\u201c\u65e2\u662f\u5929\u9a6c\u5802\u4e2d\u7684\u91cd\u8981\u4f1a\u8bae\uff0c\u4ed6\u8eab\u4e3a\u603b\u5802\u4e3b\uff0c\u5e94\u8be5\u53c2\u52a0\u5427\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u4f4e\u58f0\u95ee\u9053\u3002 \u3000\u3000\u201c\u4e0d\u9519\uff0c\u5404\u79cd\u4f1a\u8bae\uff0c\u4ed6\u90fd\u662f\u6309\u65f6\u53c2\u52a0\u2026\u2026\u201d\u5201\u9e4f\u9053\uff1a\u201c\u53ea\u4e0d\u8fc7\u90a3\u662f\u4e00\u8f86\u56db\u5468\u5782\u7740\u6a80\u6728\u73e0\u5b50\u7684\u56db\u8f6e\u8f66\u6905\uff0c\u603b\u5802\u4e3b\uff0c\u5c31\u5750\u5728\u8f6e\u6905\u4e4b\u4e2d\u3002\u201d \u3000\u3000\u201c\u90a3\u662f\u8bf4\uff0c\u53ea\u8981\u6709\u8fd9\u8f86\u8f6e\u6905\u51fa\u73b0\uff0c\u5c31\u80fd\u591f\u4ee3\u8868\u603b\u5802\u4e3b\u4e86\u3002\u201d \u3000\u3000\u201c\u5c31\u5728\u4e0b\u664b\u5347\u4e3a\u957f\u8001\u8eab\u4efd\u4e4b\u540e\uff0c\u5177\u6709\u4e86\u53c2\u52a0\u4f1a\u8bae\u7684\u8d44\u683c\uff0c\u5341\u4f59\u5e74\u5982\u4e00\u65e5\uff0c\u6bcf\u6b21\u603b\u5802\u4e3b\u90fd\u53c2\u4e0e\u4f1a\u8bae\uff0c\u5c31\u6211\u8bb0\u5fc6\u6240\u53ca\uff0c\u4ed6\u53ea\u6709\u4e00\u6b21\u8fdf\u5230\u4e86\u534a\u4e2a\u65f6\u8fb0\uff0c\u4f1a\u8bae\u7531\u53f8\u9a6c\u827a\u4ee3\u4e3a\u4e3b\u6301\u3002\u201d \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u7a81\u7136\u63a5\u9053\uff1a\u201c\u5201\u5144\uff0c\u4f60\u80fd\u80af\u5b9a\u90a3\u5ea7\u8f6e\u6905\u4e4b\u4e0a\uff0c\u6bcf\u6b21\u5750\u7684\u90fd\u662f\u540c\u4e00\u4e2a\u4eba\u5417\uff1f\u201d \u3000\u3000\u201c\u6000\u7591\u8fd9\u4ef6\u4e8b\u7684\uff0c\u4e0d\u662f\u5728\u4e0b\u4e00\u4eba\uff0c\u6bcf\u4e00\u4f4d\u521d\u664b\u957f\u8001\uff0c\u90fd\u4f1a\u6709\u4e00\u6bb5\u6000\u7591\u65f6\u95f4\uff0c\u80c6\u5b50\u5927\u7684\uff0c\u53ef\u80fd\u4f1a\u627e\u4e2a\u501f\u53e3\u63a2\u95ee\u4e00\u4e0b\uff0c\u80c6\u5b50\u5c0f\u7684\uff0c\u53ea\u6709\u95f7\u5728\u5fc3\u4e2d\u4e86\uff0c\u2026\u2026\u201d\u5201\u9e4f\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u8fd9\u4ef6\u4e8b\u8fc7\u4e86\u4e09\u4e94\u4e2a\u6708\u4e4b\u540e\uff0c\u5fc3\u4e2d\u7684\u6000\u7591\uff0c\u5c31\u81ea\u7136\u7684\u6d88\u9664\u4e86\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u5462\uff1f\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u662f\u4e0d\u662f\u957f\u4e45\u4ee5\u6765\uff0c\u90a3\u80a1\u795e\u79d8\u7684\u529b\u91cf\u5f81\u670d\u4e86\u4f60\u4eec\uff1f\u201d \u3000\u3000\u201c\u6709\u70b9\u4e60\u60ef\u4e86\u7684\u611f\u89c9\u2026\u2026\u201d\u5201\u9e4f\u9053\uff1a\u201c\u4f46\u6700\u91cd\u8981\u7684\u662f\uff0c\u4ed6\u867d\u7136\u5750\u5728\u6728\u73e0\u5ef6\u7ed5\u7684\u8f6e\u6905\u4e2d\uff0c\u4f46\u4ed6\u6bcf\u6b21\uff0c\u90fd\u8bf4\u4e86\u5f88\u591a\u7684\u8bdd\uff0c\u5bf9\u5404\u79cd\u8ba8\u8bba\u7684\u4e8b\u60c5\uff0c\u4f5c\u51fa\u7ed3\u8bba\uff0c\u6211\u4eec\u53ef\u4ee5\u51c6\u786e\u7684\u542c\u51fa\u4ed6\u7684\u58f0\u97f3\u3002\u201d \u3000\u3000\u201c\u5b66\u8fc7\u53d8\u97f3\u672f\u7684\u4eba\uff0c\u8981\u6a21\u4eff\u4e00\u4e2a\u4eba\u7684\u58f0\u97f3\uff0c\u53ef\u662f\u8f7b\u800c\u6613\u4e3e\u7684\u4e8b\u2026\u2026\u201d\u6c5f\u67ab\u8bf4\uff0c\u201c\u5c31\u7b97\u662f\u58f0\u97f3\u4e00\u6837\uff0c\u53ef\u4e5f\u4e0d\u80fd\u8ba4\u5b9a\uff0c\u8f6e\u6905\u4e0a\u5750\u7684\u5c31\u662f\u603b\u5802\u4e3b\u672c\u4eba\uff1f\u201d \u3000\u3000\u201c\u90a3\u4ed6\u7684\u58f0\u97f3\u5f88\u7279\u6b8a\uff0c\u5145\u6ee1\u7740\u8bf1\u52a8\u4eba\u5fc3\u7684\u529b\u91cf\uff0c\u4e09\u4e24\u53e5\u8bdd\u4e5f\u8bb8\u53ef\u4ee5\u6a21\u4fe1\u51fa\u6765\u2026\u2026\u201d\u5201\u9e4f\u9053\uff1a\u201c\u4f46\u4e0d\u53ef\u80fd\u957f\u7bc7\u5927\u8bba\u7684\u6a21\u4eff\u6210\u529f \u3000\u3000\u201c\u5201\u5144\u7684\u610f\u601d\uff0c\u53ef\u662f\u8bf4\u90a3\u58f0\u97f3\u542c\u8d77\u6765\u5f88\u7279\u6b8a\uff0c\u662f\u5417\uff1f\u2026\u2026\u201d \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u63a5\u9053\uff1a\u201c\u80fd\u4e0d\u80fd\u5177\u4f53\u7684\u63cf\u8ff0\u4e00\u4e0b\uff1f\u201d \u3000\u3000\u201c\u8fd9\u6837\u8bf4\u5427\u2026\u2026\u201d\u5201\u9e4f\u7565\u4e00\u6c89\u601d\uff0c\u63a5\u9053\uff1a\u201c\u4ed6\u7684\u58f0\u97f3\u4f4e\u6c89\u3001\u5fae\u5e26\u6c99\u54d1\uff0c\u4f46\u5b57\u5b57\u6e05\u695a\uff0c\u90a3\u5ea7\u4f1a\u8bae\u5927\u5385\u5f88\u5927\uff0c\u6709\u65f6\u5019\uff0c\u6709\u4e8c\u767e\u4ee5\u4e0a\u7684\u4eba\u53c2\u52a0\u4f1a\u8bae\uff0c\u4ed6\u7684\u58f0\u97f3\u80fd\u591f\u4f7f\u6bcf\u4e2a\u4eba\u542c\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u53f8\u9a6c\u827a\u548c\u7533\u96ea\u541b\u4e5f\u6709\u8fd9\u4efd\u80fd\u529b\uff0c\u4f46\u53ef\u4ee5\u611f\u89c9\u7684\u51fa\u6765\uff0c\u4ed6\u4eec\u662f\u5728\u8fd0\u6c14\u53d1\u58f0\uff0c\u7528\u5185\u529f\u628a\u58f0\u97f3\u9001\u5165\u6bcf\u4e2a\u4eba\u7684\u8033\u4e2d\uff0c\u5c31\u6211\u4e60\u67d0\u4eba\u800c\u8a00\uff0c\u542c\u603b\u5802\u4e3b\u8bf4\u8bdd\uff0c\u5c31\u6ca1\u6709\u8fd9\u79cd\u611f\u89c9\uff0c\u4ed6\u7684\u58f0\u97f3\u4e00\u76f4\u662f\u90a3\u4e48\u5e73\u7a33\uff0c\u4f46\u5374\u6e0a\u6e90\u6d41\u957f\u3001\u5ef6\u7ed5\u5728\u5927\u5385\u4e2d\uff0c\u6e05\u695a\u81ea\u7136\uff0c\u8fd1\u8eab\u800c\u5750\uff0c\u548c\u5750\u5728\u4e94\u5927\u8ddd\u79bb\u8fd9\u5bbe\uff0c\u5b8c\u5168\u4e00\u6837\u3002\u201d \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u7751\u8272\u4e00\u53d8\u9053\uff1a\u201c\u4f60\u80fd\u80af\u5b9a\u90a3\u4f20\u904d\u8033\u9645\u7684\u58f0\u97f3\uff0c\u5728\u4e94\u4e08\u8ddd\u79bb\u4e4b\u540e\uff0c\u5b8c\u5168\u6ca1\u6709\u5206\u522b\u5417\uff1f\u201d \u3000\u3000\u201c\u662f\u7684\u2026\u2026\u201d\u5201\u9e4f\u601d\u7d22\u4e86\u826f\u4e45\uff0c\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u5b8c\u5168\u4e00\u6837\uff0c\u66f4\u53eb\u4eba\u5403\u60ca\u7684\u662f\uff0c\u90a3\u6c99\u54d1\u7684\u58f0\u97f3\uff0c\u5145\u6ee1\u7740\u5f3a\u70c8\u7684\u5f81\u670d\u80fd\u529b\uff0c\u4f7f\u4eba\u5fc3\u4e3a\u4e4b\u52a8\uff0c\u4e60\u60ef\u90a3\u58f0\u97f3\u4e4b\u540e\uff0c\u5c31\u9010\u6e10\u7684\u88ab\u90a3\u79cd\u58f0\u97f3\u5360\u636e\u4e86\u5fc3\u7075\u2026\u2026\u201d \u3000\u3000\u201c\u662f\u4e0d\u662f\u4f1a\u751f\u51fa\u4e00\u79cd\u8083\u7136\u8d77\u656c\u7684\u611f\u53d7\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u6253\u65ad\u4e86\u5201\u9e4f\u7684\u8bdd\uff0c\u63a5\u9053\uff1a\u201c\u4f46\u6709\u6240\u547d\uff0c\u751f\u6b7b\u4ece\u4e4b\u3002\u201d \u3000\u3000\u201c\u5bf9\u5bf9\u5bf9\u2026\u2026\u201d\u5201\u9e4f\u9053\uff1a\u201c\u5c31\u662f\u8fd9\u79cd\u611f\u53d7\uff0c\u6211\u76f8\u4fe1\u719f\u6089\u90a3\u58f0\u97f3\u4e4b\u540e\uff0c\u6ca1\u6709\u4eba\u4f1a\u6297\u62d2\u90a3\u58f0\u97f3\u53d1\u51fa\u7684\u4ee4\u8c15\u3002\u201d \u3000\u3000\u201c\u53ea\u662f\u4e00\u79cd\u58f0\u97f3\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u4e0d\u8bba\u662f\u8c01\uff0c\u53ea\u8981\u80fd\u53d1\u51fa\u76f8\u540c\u7684\u58f0\u97f3\uff0c\u4f60\u4eec\u90fd\u4f1a\u7edd\u5bf9\u670d\u4ece\uff0c\u5168\u529b\u4ee5\u8d74\uff0c\u5bf9\u5427\uff01\u201d \u3000\u3000\u5201\u9e4f\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u5bf9\uff01\u719f\u6089\u4e86\u90a3\u58f0\u97f3\u7684\u4eba\uff0c\u7edd\u4e0d\u4f1a\u751f\u51fa\u53cd\u6297\u5b83\u7684\u610f\u8bc6\u3002\u201d \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u770b\u770b\u6c5f\u67ab\uff0c\u53f9\u606f\u4e00\u58f0\uff0c\u6b32\u8a00\u53c8\u6b62\u3002 \u3000\u3000\u201c\u5927\u6982\u662f\u4e0d\u4f1a\u9519\u4e86\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u81ea\u8a00\u81ea\u8bed\u5730\u8bf4\uff0c\u201c\u4ed6\u679c\u7136\u8fd8\u6d3b\u5728\u4e16\u4e0a\uff0c\u2018\u5929\u9a6c\u6e21\u5371\u2019\u591a\u52a8\u542c\u7684\u53e3\u53f7\u554a\uff01\u7b80\u5355\u4e00\u53e5\u8bdd\uff0c\u5c31\u4e00\u4e0b\u5b50\u6df1\u5165\u4e86\u4eba\u4eec\u7684\u5fc3\u4e2d\uff0c\u4e0d\u662f\u7edd\u9876\u806a\u660e\u7684\u4eba\uff0c\u600e\u4f1a\u8fd0\u7528\u7684\u5982\u6b64\u4e4b\u5999\u2026\u2026\u201d \u3000\u3000\u201c\u90a3\u58f0\u97f3\uff0c\u867d\u7136\u53ef\u4ee5\u5f81\u670d\u4eba\uff0c\u4e5f\u4f1a\u4f24\u4eba\uff0c\u4f46\u5201\u5144\u7684\u53e3\u6c14\u91cc\uff0c\u5374\u672a\u542c\u5230\u6709\u4eba\u53d7\u4f24\u7684\u4e8b\uff0c\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u9053\uff1a\u201c\u5201\u5144\uff0c\u8bf7\u4ed4\u7ec6\u7684\u60f3\u60f3\u770b\uff0c\u4f60\u662f\u5426\u6709\u8fc7\u53d7\u4f24\u7684\u611f\u89c9\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\uff0c\u2026\u2026\u201d\u5201\u9e4f\u9053\uff1a\u201c\u4e5f\u6ca1\u6709\u542c\u8bf4\u8fc7\u6709\u4eba\u53d7\u4f24\u7684\u4e8b\u3002\u201d \u3000\u3000\u4ed6\u662f\u8001\u6c5f\u6e56\u4e86\uff0c\u867d\u7136\uff0c\u6c5f\u67ab\u548c\u4e03\u5b9d\u548c\u5c1a\u8bf4\u7684\u9690\u6d77\u3001\u542b\u7cca\uff0c\u4f46\u4ecd\u88ab\u5201\u9e4f\u542c\u51fa\u4e86\u4e00\u4e9b\u5934\u7eea\u6765\u3002 \u3000\u3000\u201c\u90a3\u662f\u66f4\u4e0a\u5c42\u697c\u7684\u7cbe\u8fdb\u8868\u73b0\uff0c\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u4e0d\u4f46\u4e0d\u518d\u4f24\u4eba\uff0c\u800c\u4e14\uff0c\u7531\u9738\u9053\u7684\u538b\u6291\u63a7\u5236\uff0c\u8f6c\u4eba\u4e86\u738b\u9053\u7684\u7f13\u6162\u5f81\u670d\uff0c\u4f7f\u4eba\u5728\u4e0d\u77e5\u4e0d\u89c9\u4e2d\u53d7\u5230\u63a7\u5236\uff0c\u81ea\u7518\u6548\u6b7b\uff01\u201d \u3000\u3000\u5201\u9e4f\u5fcd\u4e0d\u4f4f\u4e86\uff0c\u63a5\u9053\uff1a\u201c\u90a3\u662f\u4ec0\u4e48\u6b66\u529f\uff1f\u96be\u9053\u5c31\u662f\u603b\u5802\u4e3b\u90a3\u79cd\u5fae\u5e26\u6c99\u54d1\u7684\u58f0\u97f3\uff1f\u201d \u3000\u3000\u201c\u8680\u5fc3\u9b54\u97f3\uff0c\u5201\u5144\u53ef\u66fe\u542c\u8fc7\uff1f\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u9053\uff1a\u201c\u80fd\u53eb\u4eba\u4fef\u9996\u542c\u547d\u3002\u2026\u2026\u201d \u3000\u3000\u201c\u542c\u8fc7\uff0c\u2026\u2026\u201d\u5201\u9e4f\u7751\u8272\u51b7\u8083\uff0c\u795e\u60c5\u6fc0\u52a8\u7684\u63a5\u9053\uff1a\u201c\u6b66\u6797\u4e2d\u7b2c\u4e00\u5947\u529f\uff0c\u767e\u5e74\u6765\u871a\u58f0\u6c5f\u6e56\uff0c\u5201\u67d0\u6709\u5e78\uff0c\u672a\u9047\u5230\u90a3\u4f4d\u9ad8\u4eba\uff0c\u636e\u8bf4\u9b54\u97f3\u5165\u8033\u80fd\u547d\u654c\u81ea\u88c1\uff0c\u5f79\u4eba\u6548\u547d\uff0c\u4f46\u53d7\u5f79\u4eba\u7eb5\u80fd\u4e0d\u6b7b\uff0c\u4ea6\u5fc5\u5fc3\u53d7\u91cd\u521b\uff0c\u81f3\u5c11\u8981\u5367\u5e8a\u4e09\u6708\uff0c\u624d\u80fd\u590d\u5143\uff0c\u4f46\u8fd9\u548c\u603b\u5802\u4e3b\u7684\u58f0\u97f3\uff0c\u6709\u4ec0\u4e48\u5173\u8fde\u5462\uff1f\u201d \u3000\u3000\u76ee\u7779\u5201\u9e4f\u6fc0\u52a8\u795e\u60c5\uff0c\u6c5f\u67ab\u4e5f\u4e3a\u4e4b\u795e\u8083\u7136\uff0c\u63a5\u9053\uff1a\u201c\u5201\u5144\uff0c\u4e0d\u89c9\u7740\u6709\u4e9b\u5947\u602a\u5417\uff1f\u201d \u3000\u3000\u201c\u6709\u4ec0\u4e48\u597d\u5947\u602a\u7684\uff1f\u201d\u5201\u9e4f\u9053\uff1a\u201c\u4efb\u4f55\u4eba\u51fa\u8bed\u8f7b\u4fae\u5230\u603b\u5802\u4e3b\uff0c\u90fd\u662f\u5927\u9006\u4e0d\u9053\u7684\u4e8b\u2026\u2026\u201d \u3000\u3000\u201c\u5c31\u5728\u8fd9\u91cc\u4e86\u2026\u2026\u201d\u6c5f\u67ab\u4e00\u9762\u63d0\u805a\u771f\u6c14\uff0c\u51c6\u5907\u51fa\u624b\uff0c\u4e00\u9762\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u5e76\u6ca1\u6709\u63d0\u5230\u603b\u5802\u4e3b\uff0c\u5201\u5144\u600e\u4f1a\u6fc0\u52a8\u7684\u65e0\u6cd5\u63a7\u5236\uff0c\u5927\u6709\u51fa\u624b\u4e00\u62fc\u7684\u67b6\u52bf\u2026\u2026\u201d \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u53e3\u9f7f\u542f\u52a8\uff0c\u4e00\u7f15\u68b5\u97f3\uff0c\u51b2\u5165\u4e86\u5201\u9e4f\u7684\u8033\u4e2d\u3002 \u3000\u3000\u5201\u9e4f\u6fc0\u52a8\u7684\u60c5\u7eea\uff0c\u7a81\u7136\u5e73\u9759\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u201c\u5927\u60b2\u68b5\u5531\u2026\u2026\u201d\u6c5f\u67ab\u53cc\u76ee\u51dd\u6ce8\u7740\u516d\u5b9d\u548c\u5c1a\uff0c\u9053\uff1a\u201c\u4f60\u5df2\u7ecf\u7ec3\u6210\u529f\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u5341\u5e74\u6d3e\u5987\u71ac\u6210\u5a46\uff0c\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u53cc\u76ee\u5706\u7741\uff0c\u5b9d\u76f8\u5e84\u4e25\u5730\u8bf4\uff0c\u201c\u4e8c\u5341\u5e74\u7684\u82e6\u4fee\u82e6\u7ec3\uff0c\u4eca\u65e5\uff0c\u603b\u7b97\u529f\u5fb7\u5706\u6ee1\uff0c\u65e9\u4e2a\u5341\u5929\u534a\u6708\uff0c\u6211\u8fdc\u4e0d\u6562\u732e\u4e11\u5462\uff1f\u201d \u3000\u3000\u5201\u9e4f\u7a81\u7136\u4e00\u64a9\u8863\u895f\u7684\uff0c\u5bf9\u7740\u516d\u5b9d\u548c\u5c1a\u8dea\u4e86\u4e0b\u53bb\uff0c\u9053\uff1a\u201c\u5927\u68a6\u9192\u6765\uff0c\u987f\u609f\u524d\u975e\uff0c\u5927\u5e08\u6148\u60b2\uff0c\u8bf7\u6536\u5f1f\u5b50\u5217\u5165\u95e8\u5899\uff0c\u8ba9\u6211\u5728\u62a5\u5e94\u4e34\u5934\u4e4b\u524d\uff0c\u80fd\u83b7\u5f97\u4e00\u523b\u5e73\u9759\u3002\u2026\u2026\u201d \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u770b\u770b\u6c5f\u67ab\uff0c\u6c5f\u67ab\u5fae\u5fae\u70b9\u9996\uff0c\u4e03\u5b9d\u548c\u5c1a\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u653e\u4e0b\u5c60\u5200\uff0c\u7acb\u5373\u6210\u4f5b\uff0c\u53ea\u662f\u6b64\u523b\u60c5\u5f62\u7279\u6b8a\u3002\u201d \u3000\u3000\u201c\u5f1f\u5b50\u660e\u767d\u2026\u2026\u201d\u5201\u9e4f\u9053\uff1a\u201c\u5728\u52ab\u96be\u9003\uff0c\u53ea\u6c42\u95fb\u9053\u518d\u6b7b\uff0c\u4eca\u751f\u4fee\u4e0d\u6765\uff0c\u4fee\u4e2a\u6765\u4e16\u5427\uff01\u201d \u3000\u3000\u201c\u597d\uff01\u6e21\u4f60\u5165\u4f5b\u95e8\uff0c\u51c6\u5217\u4e03\u5b9d\u5ea7\u4e0b\uff0c\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u4f38\u624b\u6478\u6478\u5201\u9e4f\u7684\u5934\u9876\uff0c\u9053\uff1a\u201c\u6682\u5bc4\u4e09\u5343\u70e6\u607c\u4e1d\uff0c\u5927\u52ab\u8fc7\u540e\uff0c\u518d\u4e3a\u4f60\u5243\u5ea6\u6212\u658b\u3002\u201d \u3000\u3000\u201c\u591a\u8c22\u5e08\u7236\uff0c\u4e00\u8bfa\u5982\u5c71\uff0c\u5f1f\u5b50\u5fc3\u613f\u5df2\u8db3\u4e86\u3002\u201d \u3000\u3000\u5927\u62dc\u4e09\u62dc\uff0c\u8d77\u8eab\u7ad9\u5728\u4e00\u4fa7\u3002 \u3000\u3000\u201c\u5c31\u662f\u51ed\u4ed7\u90a3\u6539\u5934\u6362\u9762\uff0c\u66f4\u4e0a\u5c42\u697c\u7684\u8680\u5fc3\u5bbd\u97f3\uff0c\u7262\u56fa\u7684\u63a7\u5236\u4e86\u5929\u9a6c\u5802\u4e2d\u7684\u9ad8\u624b\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u4ed6\u6709\u6043\u65e0\u6050\u7684\u7a33\u5750\u7740\u603b\u5802\u4e3b\u7684\u4f4d\u7f6e\uff0c\u56e0\u4e3a\uff0c\u6709\u6570\u767e\u4f4d\u6c5f\u6e56\u4e2d\u7684\u4e00\u6d41\u9ad8\u624b\uff0c\u4e3a\u4ed6\u6548\u547d\uff0c\u800c\u4e14\uff0c\u5fe0\u5fc3\u4e0d\u4e8c\u3002\u201d \u3000\u3000\u201c\u8fd9\u771f\u662f\u592a\u53ef\u6015\u4e86\uff0c\u5982\u975e\u6069\u5e08\u53ca\u65f6\u89e3\u53bb\u4e86\u6211\u8eab\u53d7\u7684\u63a7\u5236\u2026\u2026\u201d \u3000\u3000\u5201\u9e4f\u76ee\u6ce8\u4e03\u5b9d\u548c\u5c1a\u65e0\u9650\u5d07\u656c\u5730\u8bf4\uff0c\u201c\u5f53\u771f\u662f\u6d3b\u7684\u6d51\u6d51\u5669\u5669\uff0c\u6b7b\u5219\u7cca\u7cca\u6d82\u6d82\u3002\u201d \u3000\u3000\u201c\u5929\u9a6c\u5802\u603b\u5802\u4e3b\u7684\u8eab\u4efd\uff0c\u603b\u7b97\u662f\u63ed\u7a7f\u4e86\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\uff0c\u8981\u5c3d\u65e9\u901a\u77e5\u5f20\u56db\u59d1\uff0c\u8981\u5979\u65e9\u4e9b\u51c6\u5907\u4e00\u4e0b\uff0c\u6b64\u4e8b\u975e\u540c\u5c0f\u53ef\uff0c\u4ed6\u5982\u679c\u5b58\u5fc3\u6269\u5c55\u5b9e\u529b\uff0c\u5f88\u53ef\u80fd\u5229\u7528\u4ed6\u66f4\u7cbe\u8fdb\u7684\u8680\u5fc3\u9b54\u97f3\uff0c\u5728\u4e0d\u77e5\u4e0d\u89c9\u4e2d\u5df2\u7edf\u6cbb\u4e00\u4e86\u5f88\u591a\u6c5f\u6e56\u4eba\u7269\u3002\u2026\u2026\u201d \u3000\u3000\u201c\u548c\u5c1a\u4e5f\u62c5\u5fc3\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u5929\u9a6c\u5802\u5f88\u53ef\u80fd\u53ea\u662f\u4ed6\u4e00\u5904\u660e\u5be8\uff0c\u4e00\u5b9a\u8fd8\u6709\u4e0d\u5c11\u5b9e\u529b\uff0c\u96c6\u7ed3\u5728\u6697\u5904\uff0c\u6240\u4ee5\uff0c\u4ed6\u5bf9\u5185\u5bab\u4e00\u7cfb\u7684\u4fb5\u72af\uff0c\u624d\u591a\u65b9\u5fcd\u8ba9\u3002\u201d \u3000\u3000\u201c\u4e0d\u9519\u2026\u2026\u201d\u5201\u9e4f\u9053\uff1a\u201c\u6bcf\u5f53\u5929\u9a6c\u5802\u53d7\u5230\u5185\u5bab\u4e00\u7cfb\u4e2d\u4eba\u7684\u6392\u6324\u65f6\uff0c\u7fa4\u60c5\u6fc0\u5fff\uff0c\u5927\u90fd\u4e3b\u5f20\u5168\u529b\u53cd\u51fb\uff0c\u4f46\u6bcf\u6b21\u90fd\u88ab\u603b\u5802\u4e3b\u5e84\u5236\u4e0b\u6765\uff0c\u5947\u602a\u7684\u662f\uff0c\u53ea\u8981\u4ed6\u51e0\u53e5\u8bdd\uff0c\u5927\u5bb6\u65e0\u4e0d\u6012\u706b\u6d88\u9000\uff0c\u5fc3\u5e73\u6c14\u548c\uff0c\u73b0\u5728\u60f3\u6765\uff0c\u4ed6\u5728\u501f\u8fd9\u4e9b\u4e8b\u4ef6\uff0c\u4e60\u7ec3\u5185\u529f\u2026\u2026\u201d \u3000\u3000\u201c\u5bf9\uff01\u4ed6\u4e0d\u505c\u7684\u5b9e\u4e60\u3001\u4fee\u6b63\uff0c\u628a\u4e00\u79cd\u9738\u9053\u65e0\u6bd4\u7684\u9b54\u529f\uff0c\u7ec3\u5165\u4e86\u738b\u9053\u7684\u5883\u754c\uff0c\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u9053\uff1a\u201c\u4ed6\u628a\u81ea\u5df1\u85cf\u4e8e\u7279\u5236\u7684\u8f6e\u8f66\u4e4b\u4e2d\uff0c\u9664\u4e86\u4fbf\u4e8e\u5b89\u6392\u66ff\u8eab\uff0c\u589e\u5f3a\u795e\u79d8\u4e4b\u5916\uff0c\u5fc5\u7136\u8fd8\u6709\u539f\u56e0\u3002\u201d \u3000\u3000\u5201\u9e4f\u9053\uff1a\u201c\u4f1a\u4e0d\u4f1a\u6709\u4ec0\u4e48\u6b8b\u7f3a\uff0c\u4e0d\u613f\u89c1\u4eba\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u539f\u56e0\u4e0d\u5927\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u6b66\u529f\u5230\u4e86\u4ed6\u90a3\u79cd\u5883\u754c\uff0c\u63a5\u7eed\u65ad\u80a2\uff0c\u6316\u8089\u8865\u75a4\uff0c\u5df2\u975e\u4ec0\u4e48\u96be\u4e8b\u3002\u5c31\u7b97\u6709\u4ec0\u4e48\u6b8b\u7f3a\uff0c\u4ed6\u4e5f\u4f1a\u5904\u7406\u7684\u4e0d\u7740\u75d5\u8ff9\uff0c\u5b9e\u4e5f\u7528\u4e0d\u7740\u5982\u6b64\u795e\u79d8\u3002\u201d \u3000\u3000\u201c\u8fd9\u4ef6\u4e8b\uff0c\u6682\u4e14\u6401\u7f6e\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u9053\uff1a\u201c\u91cd\u5934\u620f\u662f\u5148\u5e94\u4ed8\u516d\u5927\u6740\u624b\u2026\u2026\u201d\u76ee\u5149\u4e00\u63a0\u5201\u9e4f\u53c8\u9053\uff1a\u201c\u5927\u60b2\u68b5\u5531\uff0c\u5c1a\u5b58\u4eba\u95f4\uff0c\u4f60\u662f\u7b2c\u4e00\u4e2a\u542c\u5230\u8fd9\u79cd\u58f0\u97f3\u7684\u4eba\uff0c\u4e0d\u8fc7\uff0c\u6b64\u4e8b\u4e07\u4e07\u4e0d\u53ef\u6cc4\u6f0f\u3002\u201d \u3000\u3000\u201c\u5f1f\u5b50\u660e\u767d\u2026\u2026\u201d\u5201\u9e4f\u9053\uff1a\u201c\u5e08\u7236\u53ef\u4ee5\u653e\u5fc3\uff0c\u7531\u6b64\u523b\u8d77\uff0c\u5f1f\u5b50\u5df2\u628a\u6027\u547d\u63e1\u5728\u624b\u4e2d\uff0c\u968f\u65f6\u53ef\u4ee5\u629b\u5f03\u4e0d\u8981\uff0c\u6211\u7edd\u5bf9\u4e0d\u4f1a\u8ba9\u603b\u5802\u4e3b\u751f\u64d2\u5230\u6211\u2026\u2026\u201d \u3000\u3000\u201c\u90a3\u5012\u4e0d\u5fc5\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u751f\u547d\u662f\u5b9d\u8d35\u7684\uff0c\u53ea\u8981\u6709\u4e00\u7ebf\u751f\u673a\uff0c\u5c31\u5e94\u8be5\u4fdd\u4f4f\u5b83\uff0c\u6211\u5012\u5e0c\u671b\u4f60\u80fd\u660e\u54f2\u4fdd\u8eab\uff0c\u7559\u5728\u5929\u9a6c\u5802\u4e2d\u5367\u5e95\u3002\u201d \u3000\u3000\u201c\u662f\uff01\u5f1f\u5b50\u660e\u767d\u4e86\u2026\u2026\u201d\u5201\u9e4f\u53d1\u89c9\u4e86\u4e03\u5b9d\u548c\u5c1a\u5bf9\u6c5f\u67ab\u7684\u656c\u91cd\uff0c\u5fc3\u60f3\u81ea\u79f0\u5f1f\u5b50\uff0c\u7edd\u5bf9\u4e0d\u4f1a\u6709\u9519\u3002\u201d \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u6325\u6325\u624b\uff0c\u9053\uff1a\u201c\u4f60\u53bb\u5427\uff01\u8bb0\u7740\u6c5f\u5e08\u53d4\u7684\u8bdd\uff0c\u66f4\u8981\u73cd\u60dc\u6027\u547d\u3002\u201d \u3000\u3000\u201c\u5f1f\u5b50\u8bb0\u4e0b\u4e86\u2026\u2026\u201d \u3000\u3000\u5201\u9e4f\u606d\u606d\u656c\u656c\u7684\u5bf9\u4e03\u5b9d\u548c\u5c1a\u884c\u4e86\u4e00\u672d\uff0c\u8f6c\u8eab\u5411\u5916\u884c\u53bb\u3002 \u3000\u3000\u6c5f\u67ab\u671b\u671b\u5929\u8272\uff0c\u6b63\u60f3\u53bb\u89c1\u89c1\u9752\u51e4\uff0c\u544a\u8bc9\u5979\u5f20\u56db\u59d1\u5c06\u4f1a\u9020\u8bbf\uff0c\u7a81\u89c1\u4e00\u4e2a\u9ed1\u8863\u7684\u5e97\u4f19\u8ba1\u98de\u5954\u800c\u6765\u3002 \u3000\u3000\u76f4\u8dd1\u8fd1\u6c5f\u67ab\u8eab\u524d\u4e94\u5c3a\uff0c\u6c5f\u67ab\u624d\u5fc3\u4e2d\u52a8\u7591\uff0c\u5389\u58f0\u559d\u9053\uff1a\u201c\u7ad9\u4f4f\uff1f\u201d \u3000\u3000\u201c\u51f6\u4ec0\u4e48\uff1f\u2026\u2026\u201d\u7adf\u662f\u5f20\u56db\u59d1\u7684\u58f0\u97f3\u3002 \u3000\u3000\u201c\u771f\u7684\u662f\u7ef4\u7eb1\u7ef4\u8096\uff0c\u548c\u5c1a\u4e5f\u88ab\u7792\u8fc7\u4e86\u3002\u201d \u3000\u3000\u201c\u53ef\u80fd\u6709\u4e24\u4e2a\u6740\u624b\uff0c\u5df2\u7ecf\u6df7\u5165\u4e86\u6d1b\u9633\u5c45\uff0c\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u96ea\u3001\u971c\u4e8c\u5973\u6492\u7f51\u665a\u4e86\u4e00\u6b65\uff0c\u88ab\u4ed6\u4eec\u5148\u5230\u4e86\u4e00\u6b65\u3002\u201d \u3000\u3000\u201c\u4e13\u4efb\u6740\u624b\uff0c\u679c\u7136\u5389\u5bb3\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u6709\u4e9b\u7d27\u5f20\u5730\u9053\uff1a\u201c\u73b0\u5728\uff0c\u8981\u5982\u5e94\u4ed8\uff0c\u624d\u80fd\u4e07\u65e0\u4e00\u5931\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\u5341\u5168\u5341\u7f8e\u7684\u529e\u6cd5\uff0c\u2026\u2026\u201d\u5f20\u56db\u59d1\u76ee\u5149\u51dd\u6ce8\u5728\u6c5f\u67ab\u7684\u8138\u4e0a\u53c8\u9053\uff1a\u201c\u4f60\u8981\u81ea\u5df1\u5c0f\u5fc3\u4e86\uff0c\u4ed6\u4eec\u80fd\u6df7\u5165\u6d1b\u9633\u5c45\u4e2d\uff0c\u5fc5\u6709\u7740\u5f88\u7279\u6b8a\u7684\u6740\u4eba\u65b9\u6cd5\uff1f\u201d \u3000\u3000\u201c\u6211\u76f8\u4fe1\u53ef\u4ee5\u81ea\u4fdd\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u5927\u59d0\u5c3d\u7ba1\u653e\u5fc3\u3002\u201d \u3000\u3000\u201c\u5c31\u662f\u4e0d\u653e\u5fc3\uff0c\u6211\u624d\u8d76\u6765\u2026\u2026\u201d\u5f20\u4e2d\u59d1\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u522b\u592a\u5927\u610f\uff0c\u4ed6\u4eec\u4e0d\u4e00\u5b9a\u7528\u6697\u5668\u3001\u5175\u5203\u88ad\u51fb\u4f60\uff0c\u53ef\u80fd\u7528\u6bd2\uff0c\u4e5f\u53ef\u80fd\u7528\u86ca\u3001\u86c7\u3001\u866b\u3001\u9e1f\u3001\u517d\u3002\u6816\u51e4\u697c\u7528\u4e0d\u7740\u53bb\u4e86\uff0c\u6211\u4f1a\u53bb\u89c1\u5979\uff0c\u96ea\u3001\u971c\u4e8c\u5973\uff0c\u867d\u7136\u5b88\u5728\u6d1b\u9633\u5c45\u5916\uff0c\u4f46\u5979\u4eec\u592a\u5173\u5fc3\u4f60\uff0c\u5f88\u53ef\u80fd\u95ef\u8fdb\u6765\uff0c\u4f60\u8981\u5b89\u6392\u597d\u81ea\u5df1\u7684\u4eba\uff0c\u522b\u8981\u81ea\u5df1\u6253\u8d77\u6765\u3002\u201d \u3000\u3000\u201c\u6211\u73b0\u5728\u5c31\u51fa\u53bb\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u597d\u50cf\u662f\u6211\u4e00\u79bb\u5f00\u6d1b\u9633\u5c45\uff0c\u5c31\u8fdb\u5165\u4e86\u4e8c\u5973\u7684\u4fdd\u62a4\u533a\u4e86\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u5979\u4eec\u786e\u8d39\u4e86\u4e00\u756a\u5fc3\u601d\uff0c\u950b\u8292\u521d\u9732\uff0c\u5df2\u89c1\u624d\u534e\uff0c\u7edd\u5bf9\u662f\u4e24\u4e2a\u597d\u5e2e\u624b\uff0c\u6211\u8d70\u4e86\uff0c\u4f60\u591a\u73cd\u91cd\u3002\u201d \u3000\u3000\u95ea\u8eab\u4e00\u8dc3\uff0c\u5ffd\u7136\u6d88\u5931\u4e0d\u89c1\u3002 \u3000\u3000\u201c\u597d\u5feb\u7684\u8eab\u6cd5\u3002\u201d\u548c\u5c1a\u60f3\u4e0d\u4f69\u670d\u90fd\u4e0d\u884c\u3002 \u3000\u3000\u201c\u90a3\u4e0d\u662f\u8f7b\u529f\u8eab\u6cd5\uff0c\u662f\u4e00\u79cd\u2018\u6f5c\u9690\u672f\u2019\u2026\u2026\u201d\u6c5f\u67ab\u5fae\u7b11\u7740\u8bf4\u3002 \u3000\u3000\u201c\u662f\u6cd5\u672f\u554a\u3002\u201d \u3000\u3000\u89c1\u591a\u8bc6\u5e7f\u7684\u5927\u548c\u5c1a\uff0c\u4e5f\u6709\u4e9b\u8ff7\u60d1\u4e86\u3002 \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u63a5\u9053\uff1a\u201c\u5144\u5f1f\uff0c\u5f20\u59d1\u5a18\u5df2\u7ecf\u793a\u8b66\u7684\u5f88\u6e05\u695a\u4e86\uff0c\u4e5f\u4e0d\u80fd\u8f7b\u5fc3\u5927\u610f\uff0c\u6211\u770b\uff0c\u80e1\u840d\u3001\u6bb5\u4e5d\uff0c\u7ecf\u8fc7\u8fd9\u4e00\u9635\u8c03\u606f\uff0c\u4e5f\u8be5\u6062\u590d\u4e86\uff0c\u6211\u53bb\u53eb\u4ed6\u4eec\u51fa\u6765\u3002\u201d \u3000\u3000\u201c\u5927\u5e08\uff0c\u6211\u4eec\u5df2\u7ecf\u5019\u547d\u591a\u65f6\u4e86\u3002\u201d \u3000\u3000\u4e24\u4e2a\u7a7f\u7740\u7070\u8863\u53ca\u819d\u5927\u8902\u7684\u4eba\uff0c\u7f13\u6b65\u884c\u4e86\u51fa\u6765\u3002 \u3000\u3000\u6562\u60c5\u4e24\u4eba\u65e9\u5df2\u7ecf\u6539\u626e\u6613\u5bb9\u597d\u4e86\u3002 \u3000\u3000\u7a7f\u7684\u662f\u957f\u5b89\u57ce\u4e2d\u7684\u8f66\u592b\u88c5\uff0c\u4e5f\u662f\u65e9\u6668\u65f6\u957f\u5b89\u57ce\u4e2d\u6d3b\u52a8\u6700\u591a\u7684\u4eba\u3002 \u3000\u3000\u56e0\u4e3a\uff0c\u4ed6\u4eec\u662f\u8d1f\u8d23\u6e05\u7406\u957f\u5b89\u8857\u5934\u4e0a\u6742\u7269\u3001\u573e\u5783\u7684\u4eba\u3002 \u3000\u3000\u6c5f\u67ab\u5fae\u5fae\u4e00\u7b11\uff0c\u5fc3\u4e2d\u5341\u5206\u5b89\u6170\uff0c\u4ed6\u4eec\u4f1a\u81ea\u5df1\u9009\u62e9\u6700\u6709\u5229\u7684\u5f62\u52bf\u3001\u73af\u5883\u3002 \u3000\u3000\u201c\u4e24\u4e2a\u6740\u624b\u5df2\u7ecf\u6df7\u5165\u4e86\u6d1b\u9633\u5c45\u2026\u2026\u201d\u4e03\u5b9d\u5927\u5e08\u9053\uff1a\u201c\u7531\u6b64\u523b\u8d77\uff0c\u968f\u65f6\u53ef\u80fd\u4f1a\u51fa\u73b0\u51f6\u9669\u573a\u9762\uff0c\u4e24\u4f4d\u5c3d\u5168\u529b\u7559\u5fc3\u56db\u5916\u666f\u7269\uff0c\u522b\u8ba9\u4eba\u63a5\u8fd1\u6c5f\u67ab\u2026\u2026\u201d \u3000\u3000\u201c\u6700\u91cd\u8981\u7684\u662f\uff0c\u4e24\u4f4d\u4e5f\u8981\u987e\u53ca\u5230\u672c\u8eab\u7684\u5b89\u5168\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u5f88\u53ef\u80fd\uff0c\u4ed6\u4eec\u4f1a\u5148\u5411\u4e24\u4f4d\u4e0b\u624b\u3002\u201d \u3000\u3000\u201c\u6c5f\u5144\u653e\u5fc3\uff0c\u6211\u4eec\u5df2\u7ecf\u6709\u4e86\u5b8c\u5168\u51c6\u5907\u2026\u2026\u201d\u80e1\u840d\u7b11\u9053\uff1a\u201c\u4ed6\u4eec\u5982\u679c\u771f\u7684\u653b\u51fb\u6211\u4eec\uff0c\u90a3\u771f\u662f\u7687\u5929\u4fdd\u4f51\uff0c\u7ed9\u6c5f\u5c11\u5144\u4e00\u4e2a\u53cd\u51fb\u673a\u4f1a\u3002\u201d \u3000\u3000\u201c\u5927\u6982\u4e0d\u4f1a\u6709\u90a3\u4e48\u7b28\u7684\u6740\u624b\u2026\u2026\u201d\u4e03\u5b9d\u5927\u5e08\u9053\uff1a\u201c\u4ed6\u4eec\u65e9\u5df2\u4e86\u89e3\u6c5f\u5c11\u4fa0\u7684\u8eab\u624b\uff0c\u6b64\u884c\u7684\u76ee\u7684\uff0c\u4e5f\u5728\u5bf9\u4ed8\u6c5f\u5c11\u4fa0\uff0c\u5927\u6982\u4e0d\u4f1a\u7b28\u5230\u5148\u653b\u51fb\u522b\u4eba\uff0c\u66b4\u9732\u8eab\u4efd\uff0c\u7559\u7ed9\u6c5f\u67ab\u8fd8\u51fb\u7684\u673a\u4f1a\u3002\u201d \u3000\u3000\u201c\u6240\u4ee5\uff0c\u6211\u4eec\u5f88\u653e\u5fc3\u2026\u2026\u201d\u6bb5\u4e5d\u671b\u7740\u4e03\u5b9d\u548c\u5c1a\u53c8\u9053\uff1a\u201c\u6211\u4eec\u5168\u529b\u53cd\u51fb\uff0c\u5f88\u96be\u7559\u4e0b\u4f59\u529b\uff0c\u4e5f\u65e0\u6cd5\u63a7\u5236\u8f7b\u91cd\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u7528\u62c5\u5fc3\u5931\u624b\u4f24\u4eba\u2026\u2026\u201d\u4e03\u5b9d\u5927\u5e08\u9053\uff1a\u201c\u548c\u5c1a\u5012\u5e0c\u671b\u4f60\u4eec\u80fd\u4e00\u51fb\u53d6\u547d\uff0c\u4e0d\u7ed9\u4ed6\u4eec\u7b2c\u4e8c\u6b21\u673a\u4f1a\uff0c\u9700\u77e5\u4ed6\u4eec\u518d\u5ea6\u51fa\u624b\uff0c\u53ef\u80fd\u6709\u7740\u540c\u5f52\u4e8e\u5c3d\u7684\u6253\u6cd5\uff0c\u90a3\u5c31\u4e0d\u77e5\u9053\uff0c\u4f1a\u6709\u4ec0\u4e48\u6076\u6bd2\u7684\u624b\u6bb5\u4e86\u3002\u201d \u3000\u3000\u5927\u548c\u5c1a\u867d\u7136\u6ca1\u6709\u8bf4\u660e\uff0c\u4f46\u80e1\u840d\u548c\u6bb5\u4e5d\u90fd\u60f3\u5230\u4e86\u5b83\u7684\u53ef\u6015\uff0c\u4e24\u4eba\u76f8\u4e92\u770b\u4e86\u4e00\u773c\uff0c\u5fc3\u4e2d\u6697\u4f5c\u51b3\u5b9a\uff0c\u771f\u8981\u6709\u654c\u4eba\u4e8c\u5ea6\u51fa\u624b\uff0c\u4e0d\u60dc\u4ee5\u8eab\u76f8\u6b89\uff0c\u4e5f\u8981\u963b\u6b62\u4ed6\uff0c\u4e0d\u8bb8\u4ed6\u63a5\u8fd1\u6c5f\u67ab\u3002 \u3000\u3000\u6c5f\u67ab\u4e3e\u6b65\u5411\u884c\u53bb\uff0c\u6b65\u5c65\u4ece\u5bb9\u7684\u8d70\u4e86\u51fa\u6d1b\u9633\u5c45\u3002 \u3000\u3000\u5b81\u53ef\u5192\u70b9\u9669\uff0c\u4e5f\u4e0d\u5e0c\u671b\u96ea\u3001\u971c\u4e8c\u5973\u95ef\u5165\u8fd9\u91cc\u3002 \u3000\u3000\u9752\u98ce\u4e5f\u662f\u4e00\u5bbf\u672a\u7761\uff0c\u516d\u5927\u6740\u624b\uff0c\u56f4\u88ad\u6c5f\u90ce\uff0c\u5979\u8fd9\u4e2a\u4f5c\u59bb\u5b50\u7684\u90a3\u91cc\u8fd8\u80fd\u7761\u5f97\u5b89\u7a33\uff0c\u5929\u5df2\u4eae\uff0c\u7acb\u523b\u7ffb\u8eab\u800c\u8d77\uff0c\u7a7f\u4e86\u4e00\u5957\u65e9\u51c6\u5907\u597d\u7684\u84dd\u5e03\u88e4\u8902\uff0c\u5e0c\u671b\u628a\u81ea\u5df1\u6253\u626e\u6210\u4e00\u4e2a\u534a\u8001\u5f90\u5a18\uff0c\u88c5\u4f5c\u6d1b\u9633\u5c45\u4e2d\u6253\u6742\u7684\u8001\u5988\u5b50\u3002 \u3000\u3000\u8138\u4e0a\u6d82\u4e86\u4e00\u4e9b\u6613\u5bb9\u836f\u7269\uff0c\u8fd8\u52a0\u4e86\u4e0d\u5c11\u76b1\u7eb9\uff0c\u63fd\u955c\u81ea\u7167\uff0c\u81ea\u89c9\u7740\u6253\u626e\u7684\u5f88\u50cf\uff0c\u624d\u8f7b\u8f7b\u63a8\u5f00\u540e\u7a97\uff0c\u98de\u8dc3\u800c\u4e0b\uff0c\u501f\u82b1\u6811\u63a9\u62a4\uff0c\u7559\u51fa\u4e86\u6816\u51e4\u697c\u3002 \u3000\u3000\u201c\u662f\u9752\u51e4\u59d1\u5a18\u4e48\uff1f\u6211\u5949\u547d\u800c\u6765\uff0c\u4e3a\u59d1\u5a18\u9001\u4e00\u4e2a\u4fdd\u62a4\u6e05\u767d\u7684\u5999\u65b9\uff01\u201d \u3000\u3000\u8f6c\u5934\u770b\u53bb\uff0c\u53ea\u89c1\u4e00\u4e2a\u8eab\u7740\u9ed1\u8863\u7684\u4eba\uff0c\u7ad9\u5728\u4e00\u682a\u82b1\u6811\u4e4b\u65c1\u3002 \u3000\u3000\u867d\u662f\u7a7f\u7740\u7537\u88c5\uff0c\u4f46\u5374\u6e05\u660e\u7684\u662f\u5973\u5b50\u53e3\u97f3\u3002 \u3000\u3000\u201c\u5949\u8c01\u4e4b\u547d\uff0c\u662f\u4ec0\u4e48\u5999\u65b9\uff1f\u201d\u9752\u51e4\u6697\u4e2d\u6212\u5907\u7684\u95ee\u9053\u3002 \u3000\u3000\u201c\u6c5f\u67ab\u4e4b\u547d\uff0c\u53ef\u62d2\u5929\u738b\u4fb5\u72af\u7684\u836f\u65b9\uff01\u201d \u3000\u3000\u9752\u98ce\u5fc3\u5934\u4e00\u8df3\uff0c\u9053\uff1a\u201c\u4e16\u4e0a\u771f\u6709\u90a3\u79cd\u9ad8\u660e\u7684\u836f\u65b9\u5b50\u5417\uff1f\u201d \u3000\u3000\u201c\u6709\uff01\u6240\u4ee5\uff0c\u6211\u624d\u6765\uff0c\u6211\u4eec\u5df2\u52a8\u5458\u4e86\u5927\u6279\u4eba\u624b\uff0c\u4fdd\u62a4\u6c5f\u67ab\uff0c\u7528\u4e0d\u7740\u59d1\u5a18\u51fa\u624b\u4e86\u3002\u201d \u3000\u3000\u201c\u4f60\u662f\u8c01\uff1f\u2026\u2026\u201d\u9752\u51e4\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4ed6\u7edd\u5bf9\u5b89\u5168\u4e48\uff1f\u201d \u3000\u3000\u201c\u6211\u53eb\u5f20\u56db\u59d1\uff0c\u9752\u51e4\u59d1\u5a18\uff0c\u6c5f\u6e56\u4e0a\uff0c\u80fd\u4e00\u4e3e\u6740\u6b7b\u6c5f\u67ab\u7684\u4eba\uff0c\u672c\u5df2\u4e0d\u591a\uff0c\u4f55\u51b5\uff0c\u5728\u4ed6\u7684\u5468\u56f4\uff0c\u6211\u4eec\u5df2\u5e03\u4e0b\u4e86\u575a\u5f3a\u7684\u9632\u5b88\uff0c\u5929\u738b\u968f\u65f6\u53ef\u80fd\u8d76\u5230\uff0c\u90a3\u624d\u662f\u53ef\u6015\u7684\u5f3a\u654c\uff0c\u4ed6\u6700\u6302\u8651\u7684\u5c31\u662f\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u6240\u4ee5\uff0c\u8981\u6211\u5c3d\u5feb\u8d76\u6765\u3002\u201d \u3000\u3000\u201c\u5f20\u5927\u59d0\uff0c\u6c5f\u67ab\u63d0\u8fc7\u4f60\uff0c\u4f69\u773c\u7684\u4e0d\u5f97\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u6240\u4ee5\uff0c\u6211\u624d\u88ab\u4ed6\u9a97\u7684\u56e2\u56e2\u8f6c\uff0c\u2026\u2026\u201d\u5f20\u56db\u59d1\u7b11\u9053\uff1a\u201c\u5f53\u5fc3\u554a\uff01\u4e0a\u4e86\u8d3c\u8239\uff0c\u5c31\u65e0\u6cd5\u5168\u8eab\u800c\u9000\uff0c\u8fd9\u4ee5\u540e\uff0c\u6709\u5f97\u4f60\u7684\u82e6\u5934\u5403\u4e86\u3002\u201d \u3000\u3000\u9752\u51e4\u5fae\u5fae\u4e00\u7b11\u53c8\u9053\uff1a\u201c\u53ea\u8981\u4ed6\u5fc3\u91cc\u9762\u7ed9\u4e88\u6211\u4e00\u5e2d\u4e4b\u5730\uff0c\u5c0f\u59b9\u5012\u662f\u613f\u4e3a\u4ed6\u7c89\u8eab\u788e\u9aa8\u3002\u201d \u3000\u3000\u201c\u5509\uff01\u53c8\u6709\u4e00\u4e2a\u75f4\u60c5\u5973\u5b50\uff0c\u2026\u2026\u201d\u5f20\u56db\u59d1\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u7684\u6613\u5bb9\u624b\u6cd5\u592a\u5dee\uff0c\u8d70\uff01\u56de\u623f\u95f4\u53bb\uff0c\u6211\u544a\u8bc9\u4f60\u4e00\u70b9\u6613\u5bb9\u7684\u8bc0\u7a8d\uff0c\u4e5f\u4f20\u4f60\u4e00\u4e2a\u81ea\u4fdd\u7684\u836f\u65b9\u3002\u201d \u3000\u3000\u201c\u5f20\u5927\u59d0\uff0c\u6211\u4eec\u8981\u4e0d\u8981\u53bb\u770b\u770b\u4ed6\uff0c\u6211\u597d\u62c5\u5fc3\u554a\uff01\u516d\u4e2a\u6740\u624b\uff0c\u5bf9\u4ed8\u4ed6\u4e00\u4e2a\u4eba\uff0c\u5e94\u4ed8\u8d77\u6765\uff0c\u771f\u7684\u4e0d\u592a\u5bb9\u6613\uff01\u201d \u3000\u3000\u5f20\u56db\u59d1\u8f7b\u8f7b\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u4eca\u5e74\u51e0\u5c81\u4e86\uff1f\u201d \u3000\u3000\u201c\u521a\u6ee1\u4e8c\u5341\u5c81\u2026\u2026\u201d\u9752\u51e4\u8bf4\uff0c\u201c\u662f\u4e0d\u662f\u592a\u5e74\u8f7b\u4e86\uff0c\u5c11\u4e0d\u66f4\u4e8b\uff1f\u201d \u3000\u3000\u201c\u4e0d\u8981\u62c5\u5fc3\u4ed6\uff0c\u6211\u76f8\u4fe1\uff0c\u4ed6\u80fd\u5e94\u4ed8\u5f97\u6765\uff0c\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u4f60\u8fd9\u70b9\u6613\u5bb9\u624b\u6cd5\uff0c\u522b\u8bf4\u7792\u4e0d\u8fc7\u884c\u5bb6\uff0c\u5c31\u662f\u7ec6\u5fc3\u4e00\u70b9\u7684\u6c5f\u6e56\u4eba\uff0c\u4e00\u773c\u5c31\u53ef\u4ee5\u770b\u7a7f\u3002\u201d \u3000\u3000\u8bf4\u670d\u4e86\u9752\u51e4\uff0c\u4e24\u4eba\u53c8\u56de\u5230\u4e86\u6816\u51e4\u697c\u3002 \u3000\u3000\u5f20\u56db\u59d1\u6062\u590d\u4e86\u672c\u6765\u7684\u9762\u76ee\uff0c\u767d\u51c0\u9762\u76ae\uff0c\u6e05\u79c0\u8f6e\u5ed3\uff0c\u8bf4\u4e0d\u4e0a\u5f88\u7f8e\uff0c\u4f46\u5374\u6709\u4e00\u79cd\u8bf4\u4e0d\u51fa\u7684\u4eb2\u5207\u9b45\u529b\u3002 \u3000\u3000\u9752\u51e4\u4e5f\u6d17\u53bb\u4e86\u8138\u4e0a\u7684\u836f\u7269\uff0c\u5c55\u73b0\u51fa\u7edd\u4e16\u5bb9\u8272\u3002 \u3000\u3000\u5f20\u56db\u59d1\u8f7b\u8f7b\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u597d\u5bb6\u4f19\uff0c\u5929\u4e0b\u7684\u7f8e\u5973\uff0c\u90fd\u88ab\u4ed6\u4e00\u4e0b\u5b50\u5403\u5e72\u3001\u62b9\u51c0\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u5927\u59d0\u5938\u5956\u2026\u2026\u201d\u9752\u51e4\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u542c\u8bf4\u6709\u4e24\u4e2a\u5c0f\u7f8e\u5973\uff0c\u59ff\u8272\u51fa\u4f17\uff0c\u6bd4\u5c0f\u59b9\u5c24\u80dc\u51e0\u5206\uff0c\u5927\u59d0\uff0c\u53ef\u8981\u60f3\u529e\u6cd5\u7ed9\u6211\u5f15\u89c1\u5f15\u89c1\uff1f\u201d \u3000\u3000\u201c\u522b\u62c5\u5fc3\u4e24\u4e2a\u5c0f\u72d0\u5973\uff0c\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u5979\u4eec\u662f\u90a3\u79cd\u5f31\u6c34\u4e09\u5343\uff0c\u53d6\u4e00\u74e2\u996e\u7684\u6027\u683c\uff0c\u4e5f\u77e5\u9053\u6c5f\u67ab\u4e0d\u662f\u4ece\u4e00\u800c\u7ec8\u7684\u4eba\uff0c\u6240\u4ee5\uff0c\u5979\u4eec\u5f88\u597d\u76f8\u5904\u3002\u201d \u3000\u3000\u201c\u8fd9\u6211\u5c31\u653e\u5fc3\u4e86\u2026\u2026\u201d\u9752\u51e4\u9053\uff1a\u201c\u6211\u4e00\u76f4\u5bb3\u6015\u4e8c\u4f4d\u6f02\u4eae\u7684\u5c0f\u59b9\u5bb9\u4e0d\u4e0b\u6211\uff01\u201d \u3000\u3000\u201c\u9752\u51e4\uff0c\u4f60\u53ea\u8981\u6ca1\u6709\u72ec\u5360\u5730\u7684\u7528\u5fc3\uff0c\u5c31\u4e0d\u4f1a\u5f15\u8d77\u70e6\u607c\u2026\u2026\u201d \u3000\u3000\u5f20\u56db\u59d1\u9053\uff1a\u201c\u4ed6\u662f\u4e71\u4e16\u7684\u82f1\u96c4\uff0c\u4e2d\u6d41\u7825\u67f1\uff0c\u4f46\u5374\u662f\u5929\u751f\u7684\u60b2\u5267\u4eba\u7269\u3002 \u3000\u3000\u8fd9\u4ef6\u4e8b\uff0c\u4ee5\u540e\u518d\u8c08\u5427\uff01\u4f60\u80fd\u5982\u6b64\u5173\u5fc3\u4ed6\uff0c\u8db3\u89c1\u7231\u604b\u4e4b\u6df1\u2026\u2026 \u3000\u3000\u73b0\u5728\uff0c\u5148\u529e\u4f60\u7684\u4e8b\uff0c\u5927\u59d0\u7ed9\u4f60\u4e00\u4e9b\u836f\u7269\uff0c\u5341\u65e5\u5185\uff0c\u5305\u7ba1\u90a3\u4f4d\u8272\u80c6\u5947\u5927\u7684\u5929\u738b\uff0c\u4e5f\u4e0d\u6562\u78b0\u4f60\u3002\u201d \u3000\u3000\u201c\u771f\u7684\u2026\u2026\u201d\u9752\u98ce\u5c55\u73b0\u4e86\u59a9\u5a9a\u7684\u7b11\u5bb9\uff0c\u9053\uff1a\u201c\u771f\u8981\u8c22\u8c22\u4f60\uff0c\u4f60\u662f\u6211\u7684\u6551\u547d\u7684\u6069\u4eba\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u9053\uff1a\u201c\u8fd9\u836f\u529b\u53ea\u6709\u5341\u5929\uff0c\u7528\u8fc7\u516d\u4e2a\u65f6\u8fb0\u4e4b\u540e\uff0c\u624d\u4f1a\u6709\u6548\uff0c\u4f55\u65f6\u5e94\u7528\uff0c\u4f60\u81ea\u5df1\u7b97\u6e05\u695a\u4e86\u3002\u201d \u3000\u3000\u53d6\u51fa\u4e00\u5305\u836f\u7269\uff0c\u5f88\u4ed4\u7ec6\u7684\u8bf4\u51fa\u4e86\u5e94\u7528\u4e4b\u6cd5\u3002 \u3000\u3000\u9752\u98ce\u4e0d\u6562\u5ef6\u8bef\uff0c\u7acb\u523b\u6577\u7528\u3002 \u3000\u3000\u5f20\u56db\u59d1\u770b\u9752\u51e4\u4e00\u5207\u59a5\u5f53\uff0c\u624d\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u516d\u4e2a\u65f6\u8fb0\u4e4b\u540e\uff0c\u4f1a\u53d1\u51fa\u4e00\u80a1\u5f88\u96be\u95fb\u7684\u5473\u9053\uff0c\u8fd9\u80a1\u5473\u9053\u5c06\u4f7f\u4eba\u9000\u907f\u4e09\u820d\u3002\u201d \u3000\u3000\u9752\u51e4\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u5927\u59d0\uff0c\u8fd9\u4e0d\u884c\u554a\uff01\u8fd9\u4e00\u6765\uff0c\u6c5f\u67ab\u4e0d\u662f\u4ed6\u4e0d\u80af\u63a5\u8fd1\u6211\u4e86\uff1f\u201d \u3000\u3000\u201c\u5c0f\u59b9\u5b50\uff0c\u8fd9\u5c31\u8981\u5fcd\u4e00\u5fcd\u4e86\uff0c\u53cd\u6b63\u53ea\u6709\u5341\u5929\uff0c\u4e00\u5929\u4e4b\u540e\uff0c\u4e00\u5207\u5f62\u72b6\u590d\u5143\uff0c\u90a3\u80a1\u8ba9\u4eba\u6b32\u5455\u7684\u5473\u9053\uff0c\u4e5f\u4f1a\u6d88\u5931\u65e0\u8e2a\u4e86\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u5929\u4e0b\u4e8b\u6709\u5229\u5fc5\u6709\u5f0a\uff0c\u90a3\u5c31\u8981\u4e24\u5bb3\u76f8\u6743\u53d6\u5176\u8f7b\u4e86\u7684\u3002\u201d \u3000\u3000\u201c\u5c0f\u59b9\u77e5\u9053\u4e86\uff0c\u5927\u59d0\u2026\u2026\u201d\u9752\u51e4\u795e\u60c5\u4e25\u8083\u5730\u8bf4\uff0c\u201c\u544a\u8bc9\u6c5f\u67ab\uff0c\u8981\u4ed6\u5728\u5341\u5929\u8fd9\u5185\uff0c\u522b\u6765\u770b\u6211\uff0c\u6211\u4e0d\u8981\u4ed6\u95fb\u5230\u90a3\u80a1\u96be\u95fb\u7684\u5473\u9053\u3002\u201d \u3000\u3000\u201c\u597d\u597d\u597d\uff0c\u6211\u544a\u8bc9\u4ed6\u2026\u2026\u201d\u5f20\u56db\u59d1\u7b11\u9053\uff1a\u201c\u5927\u59d0\u6765\u770b\u4f60\uff0c\u884c\u4e0d\u884c\u5462\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u597d\uff0c\u2026\u2026\u201d\u9752\u51e4\u9053\uff1a\u201c\u6211\u60f3\uff0c\u8fd9\u51e0\u5929\uff0c\u6211\u4e00\u5b9a\u5f88\u5bc2\u5bde\uff0c\u5927\u59d0\u5440\uff01\u4f60\u4e00\u5b9a\u8981\u6765\u770b\u6211\u3002\u201d \u3000\u3000\u78b0\u4e0a\u4e86\u5f20\u56db\u59d1\u8fd9\u6837\u6210\u719f\uff0c\u4eb2\u5207\u7684\u5973\u4eba\uff0c\u9752\u51e4\u5ffd\u7136\u4ea7\u751f\u4e86\u5f3a\u70c8\u7684\u5b7a\u6155\u4e4b\u60c5\uff0c\u5f20\u56db\u59d1\u5df2\u5bf9\u5979\u5177\u6709\u4e86\u5f88\u5927\u7684\u5438\u5f15\u529b\uff0c\u4e0d\u81ea\u89c9\u504e\u5165\u4e86\u5f20\u56db\u59d1\u7684\u6000\u4e2d\uff0c\u4f4e\u58f0\u63a5\u9053\uff1a\u201c\u6211\u8fd8\u8981\u767b\u53f0\u6f14\u5531\uff0c\u90a3\u80a1\u96be\u95fb\u7684\u5473\u9053\u5982\u662f\u592a\u5f3a\u70c8\uff0c\u4f1a\u4e0d\u4f1a\u88ab\u542c\u4f17\u95fb\u5230\u3002\u201d \u3000\u3000\u201c\u6240\u4ee5\uff0c\u4f60\u8981\u597d\u597d\u5904\u7406\u5b83\uff0c\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u4e0d\u8ba9\u90a3\u5473\u98d8\u6563\u592a\u8fdc\uff0c\u4e0d\u8fc7\uff0c\u6211\u6709\u4e00\u7c92\u836f\u7269\u7ed9\u4f60\uff0c\u4f60\u81ea\u5df1\u53ef\u4ee5\u5f88\u5feb\u9002\u5e94\u5b83\uff0c\u6211\u8d70\u4e86\uff0c\u8bb0\u7740\uff0c\u6025\u7740\u89c1\u6211\u65f6\uff0c\u5c31\u5728\u5c45\u9876\u4e0a\u63d2\u4e00\u7247\u76f4\u7acb\u7684\u7af9\u53f6\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u5929\u738b\u7684\u8b66\u89c9\u5f88\u9ad8\uff0c\u6b66\u529f\u7cbe\u6e5b\uff0c\u7b2c\u4e00\u6d41\u7684\u8f7b\u8eab\u672f\uff0c\u4e5f\u65e0\u6cd5\u7792\u8fc7\u4ed6\u7684\u8033\u76ee\u2026\u2026\u201d\u9752\u51e4\u9053\uff1a\u201c\u4e0d\u89c1\u5c4b\u9876\u4e0a\u7ad6\u7acb\u7684\u7af9\u53f6\uff0c\u5927\u59d0\uff0c\u5343\u4e07\u4e0d\u53ef\u95ef\u5165\u6816\u51e4\u697c\u6765\u3002\u201d \u3000\u3000\u6c5f\u67ab\u6b65\u51fa\u4e86\u6d1b\u9633\u5c45\uff0c\u592a\u9633\u8fdc\u672a\u5b8c\u5168\u51fa\u6765\u3002 \u3000\u3000\u5f88\u591a\u8eab\u7740\u7070\u8863\u7684\u4eba\uff0c\u6b63\u5728\u6574\u7406\u8857\u9053\u3002 \u3000\u3000\u80e1\u840d\u3001\u6bb5\u4e5d\uff0c\u65e9\u5df2\u6df7\u5165\u4e86\u90a3\u4e9b\u4eba\u4e2d\uff0c\u4ee5\u6c5f\u67ab\u76ee\u5149\u7684\u9510\u5229\uff0c\u4e00\u65f6\u95f4\uff0c\u4e5f\u65e0\u6cd5\u627e\u51fa\u4ed6\u4eec\u505c\u8eab\u4f55\u5904\uff1f \u3000\u3000\u6c5f\u67ab\u60f3\u5230\u4e86\u96ea\u3001\u971c\u4e8c\u5973\uff0c\u8fd9\u5730\u65b9\u5df2\u7136\u51fa\u4e86\u6d1b\u9633\u5c45\u7684\u5927\u95e8\uff0c\u5df2\u8fdb\u5165\u4e86\u4e8c\u5973\u7684\u4fdd\u62a4\u8303\u56f4\u4e4b\u5185\u3002 \u3000\u3000\u4f46\u4e8c\u5973\u5462\uff1f \u3000\u3000\u6c5f\u67ab\u76ee\u5149\u8f6c\u52a8\uff0c\u56db\u4e0b\u6253\u91cf\uff0c\u76ee\u5149\u53ef\u53ca\u4e4b\u5904\uff0c\u5168\u90fd\u662f\u6253\u626b\u8857\u9053\uff0c\u6536\u53d6\u5783\u573e\u7684\u7070\u8863\u4eba\u3002 \u3000\u3000\u8fd9\u5ea7\u5e1d\u738b\u4e4b\u90fd\uff0c\u4e00\u76f4\u4fdd\u6301\u4e86\u7f8e\u597d\u7684\u4f20\u7edf\uff0c\u8857\u9053\u4e0a\uff0c\u6253\u626b\u7684\u5341\u5206\u5e72\u51c0\u3002 \u3000\u3000\u4e00\u8f86\u4eba\u62c9\u7740\u53cc\u8f6e\u6728\u8f66\uff0c\u88c5\u6ee1\u4e86\u5783\u573e\uff0c\u6b63\u7f13\u7f13\u5411\u6c5f\u67ab\u884c\u6765\u3002 \u3000\u3000\u6c5f\u673a\u6ca1\u6709\u9000\u907f\uff0c\u5374\u6697\u4e2d\u4e00\u63d0\u771f\u6c14\uff0c\u51c6\u5907\u8fce\u63a5\u6311\u6218\u3002 \u3000\u3000\u4ed6\u5e74\u5c11\u6c14\u76db\uff0c\u6280\u827a\u5353\u7edd\uff0c\u53e3\u4e2d\u867d\u7136\u5f3a\u8c03\u516d\u4e2a\u4e13\u4efb\u6740\u624b\u7684\u53ef\u6015\uff0c\u4f46\u90a3\u662f\u7528\u6765\u5e94\u4ed8\u6b27\u9633\u662d\u548c\u7f57\u5170\u7528\u7684\uff0c\u5185\u5fc3\u4e4b\u4e2d\uff0c\u5e76\u672a\u628a\u51e0\u4e2a\u6740\u624b\u653e\u5728\u773c\u4e2d\uff0c\u5012\u662f\u5f20\u56db\u59d1\u90a3\u4e00\u756a\u5f3a\u8c03\uff0c\u4f7f\u6c5f\u67ab\u63d0\u9ad8\u4e86\u4e0d\u5c11\u7684\u8b66\u89c9\u3002 \u3000\u3000\u62c9\u677f\u8f66\u7684\u662f\u4e2a\u4e94\u5341\u5de6\u53f3\u7684\u8001\u4eba\uff0c\u6ee1\u5934\u5927\u6c41\uff0c\u6c14\u606f\u7c97\u91cd\uff0c\u4f3c\u662f\u62c9\u5f97\u5341\u5206\u5403\u529b\u3002 \u3000\u3000\u6c5f\u67ab\u76ee\u5149\u9510\u5229\uff0c\u4e00\u773c\u5c31\u770b\u900f\u4e86\u90a3\u8001\u4eba\u4e0d\u662f\u8eab\u5177\u6b66\u529f\u7684\u4eba\uff0c\u7acb\u523b\u6d88\u53bb\u4e86\u6212\u5907\u3002 \u3000\u3000\u677f\u8f66\u7531\u6c5f\u67ab\u7684\u8eab\u524d\u884c\u8fc7\u3002 \u3000\u3000\u7a81\u7136\uff0c\u5783\u573e\u98de\u8d77\uff0c\u4e00\u9053\u5bd2\u8292\uff0c\u7531\u5783\u573e\u4e2d\u98de\u5c04\u800c\u51fa\uff0c\u5c04\u5411\u6c5f\u67ab\u3002 \u3000\u3000\u5b8c\u5168\u51fa\u4eba\u610f\u5916\u7684\u4e00\u5251\uff0c\u53cc\u65b9\u8ddd\u79bb\u65e2\u8fd1\uff0c\u90a3\u4e00\u5251\u53c8\u5feb\u5982\u95ea\u7535\uff0c\u6c5f\u67ab\u95ea\u907f\u5df2\u81ea\u4e0d\u53ca\u3002 \u3000\u3000\u773c\u770b\u4e00\u5251\u5c31\u8981\u523a\u4e2d\u524d\u80f8\u3002 \u3000\u3000\u4e00\u58f0\u51c4\u5389\u7684\u60e8\u53eb\uff0c\u4f20\u5165\u8033\u9645\uff0c\u9c9c\u8840\u55b7\u6d12\u4e2d\uff0c\u523a\u4e2d\u6c5f\u67ab\u7684\u957f\u5251\uff0c\u8131\u843d\u6389\u5730\u3002 \u3000\u3000\u4e00\u4e2a\u5a07\u751c\u7684\u58f0\u97f3\uff0c\u4f20\u5165\u4e86\u6c5f\u67ab\u7684\u8033\u9645\uff0c\u9053\uff1a\u201c\u6c5f\u5927\u54e5\uff0c\u4ed6\u85cf\u5728\u5783\u573e\u8f66\u4e2d\uff0c\u6211\u5c31\u626e\u6210\u4e00\u5806\u5783\u573e\u3002\u201d \u3000\u3000\u662f\u541f\u96ea\u7684\u58f0\u97f3\u3002 \u3000\u3000\u6c5f\u67ab\u5dee\u4e00\u70b9\u51b2\u53e3\u53eb\u51fa\u4e86\u541f\u96ea\u7684\u540d\u5b57\u3002 \u3000\u3000\u4f46\u89c1\u4e00\u56e2\u9ad8\u98de\u800c\u8d77\u7684\u9ed1\u5f71\uff0c\u6026\u7136\u4e00\u58f0\uff0c\u6454\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u662f\u4e00\u4e2a\u5168\u8eab\u9ed1\u8863\u7684\u4eba\uff0c\u5934\u8138\u90fd\u5305\u5728\u4e00\u7247\u9ed1\u5e03\u8fd9\u4e2d\uff0c\u5927\u6982\u662f\u4e3a\u4e86\u9632\u6b62\u5783\u573e\u7684\u7f18\u6545\uff0c\u5305\u5934\u7684\u9ed1\u5e03\u548c\u8863\u670d\u8fde\u5728\u4e00\u8d77\u3002 \u3000\u3000\u5947\u602a\u7684\u662f\u6b7b\u4e86\u4e00\u4e2a\u4eba\uff0c\u5e76\u6ca1\u6709\u5f15\u8d77\u4f4f\u4f55\u9a9a\u52a8\u3002 \u3000\u3000\u90a3\u62c9\u8f66\u7684\u8001\u4eba\uff0c\u4f3c\u662f\u6839\u672c\u5c31\u4e0d\u77e5\u9053\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\u60c5\uff1f\u505c\u4e5f\u672a\u505c\u8fc7\u4e00\u4e0b\uff0c\u4e00\u76f4\u4e0d\u505c\u7684\u5411\u524d\u884c\u53bb\u3002 \u3000\u3000\u541f\u96ea\u4e5f\u6ca1\u6709\u73b0\u8eab\uff0c\u90a3\u5a07\u751c\u7684\u58f0\u97f3\uff0c\u4f3c\u662f\u7531\u90a3\u8f86\u5783\u573e\u8f66\u4e0a\u4f20\u4e86\u8fc7\u6765\uff0c\u4f3c\u4e4e\u662f\u53f6\u96ea\u4ecd\u5728\u90a3\u8f86\u8f66\u4e0a\u3002 \u3000\u3000\u6c5f\u67ab\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u773c\u770b\u6253\u626b\u8857\u9053\u7684\u5de5\u4eba\uff0c\u9010\u6e10\u6563\u53bb\uff0c\u53ea\u4f59\u4e00\u8f86\u6728\u677f\u8f66\uff0c\u6025\u9a70\u800c\u6765\u3002 \u3000\u3000\u8f66\u4e0a\u4f38\u51fa\u4e00\u53ea\u624b\uff0c\u6293\u8d77\u4e86\u5730\u4e0a\u5c38\u4f53\uff0c\u62d6\u5165\u8f66\u4e2d\uff0c\u6025\u6025\u9a70\u53bb\u3002 \u3000\u3000\u7531\u62c9\u8f66\u4eba\u7684\u773c\u795e\u4e2d\uff0c\u6c5f\u67ab\u770b\u51fa\u4e86\u4ed6\u662f\u6bb5\u4e5d\uff0c\u6293\u8d70\u5c38\u4f53\u7684\u4eba\uff0c\u81ea\u7136\u662f\u80e1\u840d\u4e86\u3002 \u3000\u3000\u6709\u4e9b\u4e8b\uff0c\u4e0d\u80fd\u62d6\u5ef6\uff0c\u6c5f\u67ab\u7a81\u7136\u540e\u6094\uff0c\u7adf\u7136\u6ca1\u6709\u770b\u770b\u90a3\u6740\u624b\u7684\u771f\u6b63\u9762\u76ee\u3002 \u3000\u3000\u592a\u9633\u5347\u9ad8\u4e86\uff0c\u9633\u5149\u7167\u5c04\u5927\u5730\uff0c\u8857\u4e0a\u884c\u4eba\u6e10\u591a\uff0c\u957f\u5b89\u8857\u5934\uff0c\u53c8\u4e00\u5929\u65b0\u7684\u5f00\u59cb\u3002 \u3000\u3000\u6c5f\u67ab\u6697\u6697\u5fd6\u9053\uff1a\u201c\u5f20\u56db\u59d1\u8d5e\u8d4f\u4e8c\u5973\u8d39\u5c3d\u5fc3\u8840\uff0c\u5c55\u73b0\u4e86\u624d\u534e\uff0c\u770b\u6765\u679c\u7136\u4e0d\u9519\u3002\u201d \u3000\u3000\u63a5\u8fd1\u8fd9\u6d1b\u9633\u5c45\u7684\u6740\u624b\uff0c\u679c\u7136\u5df2\u5728\u5979\u4eec\u76d1\u89c6\u3001\u638c\u63e1\u4e4b\u4e0b\u3002 \u3000\u3000\u6740\u624b\u85cf\u4eba\u4e86\u5783\u573e\u8f66\u4e2d\uff0c\u541f\u96ea\u7adf\u548c\u4ed6\u85cf\u4e8e\u4e00\u8f66\u4e4b\u5185\uff0c\u5316\u4f5c\u4e00\u5806\u5783\u573e\u4e5f\u8bb8\u5e76\u4e0d\u592a\u96be\uff0c\u4f46\u8ba9\u5bf9\u65b9\u4e0d\u53d1\u89c9\uff0c\u5c31\u6709\u70b9\u96be\u4ee5\u60f3\u8c61\u4e86\u3002 \u3000\u3000\u5927\u6982\u8fd9\u5c31\u662f\u5f20\u56db\u59d1\u7684\u6f5c\u9690\u672f\uff0c\u6216\u79fb\u82b1\u63a5\u6728\u4e86\uff0c\u5c0f\u5c0f\u5e74\u7eaa\uff0c\u6709\u5982\u6b64\u7684\u8010\u5fc3\u3001\u5fcd\u6027\uff0c\u679c\u975e\u5c0f\u53ef\u4e86\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u4e00\u4e2a\u4e2d\u5e74\u5987\u4eba\uff0c\u53f3\u624b\u633d\u7740\u4e00\u4e2a\u7af9\u7bee\u5b50\uff0c\u5de6\u624b\u6276\u5728\u4e00\u4e2a\u5c0f\u5973\u5b69\u5b50\u7684\u80a9\u5934\u4e0a\uff0c\u7f13\u6b65\u884c\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u90a3\u5973\u5b69\u53ea\u6709\u5341\u4e00\u4e8c\u5c81\uff0c\u4f46\u6c5f\u67ab\u4ecd\u7136\u751f\u51fa\u8b66\u89c9\uff0c\u53cc\u76ee\u51dd\u795e\uff0c\u76ef\u6ce8\u5728\u90a3\u5973\u5b69\u5b50\u7684\u8138\u4e0a\u67e5\u770b\u3002 \u3000\u3000\u6c5f\u67ab\u667a\u6167\u5353\u7edd\uff0c\u4f46\u6c5f\u6e56\u4e0a\u7684\u7ecf\u9a8c\uff0c\u5e76\u975e\u592a\u591a\u3002 \u3000\u3000\u770b\u4e0a\u53bb\uff0c\u90a3\u53ea\u662f\u666e\u901a\u7684\u5c0f\u5973\u5b69\uff0c\u81f3\u5c11\uff0c\u6c5f\u67ab\u77a7\u4e0d\u51fa\u6709\u4ec0\u4e48\u53ef\u7591\u4e4b\u5904\uff1f \u3000\u3000\u884c\u8fd1\u6c5f\u67ab\u8eab\u524d\u65f6\uff0c\u5c0f\u5973\u5b69\u4f3c\u662f\u4e0d\u80dc\u8d1f\u91cd\uff0c\u7a81\u7136\u5411\u5730\u4e0b\u8e72\u53bb\u3002 \u3000\u3000\u6c5f\u67ab\u4e00\u5438\u6c14\uff0c\u8eab\u5b50\u5411\u540e\u75be\u9000\u4e09\u5c3d\u3002 \u3000\u3000\u4ed6\u5e76\u4e0d\u77e5\u9053\u662f\u5426\u6709\u4eba\u7b97\u8ba1\u4ed6\uff0c\u8fd9\u540e\u9000\u4e09\u5c3a\uff0c\u53ea\u662f\u4e00\u79cd\u672c\u80fd\u7684\u9632\u5fa1\u3002 \u3000\u3000\u53ef\u60dc\u7684\u662f\uff0c\u53ea\u9000\u4e86\u4e09\u5c3a\uff0c\u8ddd\u79bb\u592a\u8fd1\u4e86\u3002 \u3000\u3000\u90a3\u4e2d\u5e74\u5987\u4eba\u5728\u5c0f\u5973\u5b69\u8e72\u4e0b\u53bb\u65f6\uff0c\u4f3c\u662f\u5931\u53bb\u4e86\u91cd\u5fc3\uff0c\u8ddf\u7740\u4ec6\u5012\u5728\u5730\u4e0a\u3002 \u3000\u3000\u6c5f\u67ab\u6697\u6697\u53f9\u606f\u4e00\u58f0\uff0c\u5fd6\u9053\uff1a\u201c\u770b\u6765\uff0c\u6211\u662f\u592a\u591a\u7591\u4e86\u3002\u201d \u3000\u3000\u6b63\u5f85\u4f38\u624b\u53bb\u6276\u90a3\u4e2d\u5e74\u5987\u4eba\u4e00\u628a\uff0c\u5df2\u8e72\u5728\u5730\u4e0a\u7684\u5c0f\u5973\u5b69\uff0c\u5374\u7a81\u7136\u957f\u8eab\u800c\u8d77\u3002 \u3000\u3000\u4e00\u4e0b\u5b50\u957f\u9ad8\u4e86\u4e00\u500d\uff0c\u7adf\u7136\u662f\u4e8c\u5341\u4ee5\u4e0a\u7684\u5c11\u5973\uff0c\u53cc\u624b\u98de\u626c\uff0c\u4e24\u628a\u6dec\u6bd2\u94f6\u9488\uff0c\u98de\u5c04\u800c\u51fa\uff0c\u7b3c\u7f69\u4e86\u6c5f\u67ab\u5168\u8eab\u3002 \u3000\u3000\u6c5f\u67ab\u5438\u4e00\u53e3\u6c14\uff0c\u5168\u8eab\u8863\u670d\uff0c\u7acb\u523b\u81a8\u80c0\u800c\u8d77\uff0c\u53f3\u624b\u6325\u52a8\uff0c\u62cd\u51fa\u4e86\u4e00\u80a1\u638c\u98ce\u3002 \u3000\u3000\u4f46\u771f\u6b63\u7684\u53ef\u6015\u4eba\u7269\uff0c\u5374\u662f\u90a3\u4e2d\u5e74\u5987\u4eba\uff0c\u53ea\u89c1\u5979\u501f\u52bf\u4e00\u6eda\uff0c\u4eba\u5df2\u95ea\u5230\u4e86\u6c5f\u67ab\u80cc\u540e\uff0c\u633a\u8eab\u800c\u8d77\uff0c\u624b\u4e2d\u7684\u7af9\u7bee\u5b50\uff0c\u629b\u5411\u6c5f\u67ab\u3002 \u3000\u3000\u201c\u6c5f\u5927\u54e5\uff0c\u4e0d\u8981\u52a8\uff0c\u2026\u2026\u201d \u3000\u3000\u662f\u541f\u971c\u7684\u58f0\u97f3\uff0c\u800c\u4e14\uff0c\u58f0\u97f3\u5c31\u5728\u8eab\u4fa7\u3002 \u3000\u3000\u6c5f\u67ab\u6ca1\u6709\u52a8\uff0c\u4ed6\u76f8\u4fe1\u4e8c\u5973\uff0c\u5979\u4eec\u4e00\u5b9a\u4f1a\u5168\u5fc3\u5168\u610f\u7684\u4fdd\u62a4\u4ed6\u3002 \u3000\u3000\u4e00\u84ec\u9ed1\u8272\u7684\u70df\u4e91\uff0c\u5c31\u5728\u4e2d\u8fb9\u4e00\u682a\u6811\u8eab\u4e0a\u98de\u8d77\uff0c\u5ffd\u7136\u95f4\u5e03\u6563\u5f00\u6765\uff0c\u62a4\u4f4f\u4e86\u6c5f\u67ab\u3002 \u3000\u3000\u629b\u51fa\u7684\u7af9\u7bee\u4e2d\uff0c\u6b63\u98de\u51fa\u5343\u53ea\u4ee5\u4e0a\u7684\u7eff\u5934\u5927\u82b1\u8747\uff0c\u8702\u6d8c\u5411\u6c5f\u67ab\u3002 \u3000\u3000\u4f46\u5374\u88ab\u90a3\u6563\u98de\u7684\u9ed1\u8272\u70df\u4e91\u6321\u4f4f\uff0c\u70df\u4e91\u6d88\u6563\uff0c\u6536\u7f29\u6210\u4e00\u56e2\u3002 \u3000\u3000\u90a3\u7adf\u662f\u4e00\u5f20\u9ed1\u8272\u7684\u4e1d\u7f51\uff0c\u64cd\u7f51\u7684\u4eba\u4e5f\u5341\u5206\u9ad8\u660e\uff0c\u4e00\u515c\u4e4b\u4e0b\uff0c\u4e0a\u5343\u7684\u7eff\u8747\uff0c\u7adf\u7136\u65e0\u4e00\u6f0f\u7f51\u3002 \u3000\u3000\u6c5f\u67ab\u4e00\u8bb0\u5f3a\u52b2\u7684\u638c\u98ce\uff0c\u51fb\u843d\u4e86\u5927\u90e8\u5206\u5c04\u5411\u9762\u524d\u7684\u6bd2\u9488\uff0c\u5c04\u5411\u8eab\u4e0a\u7684\u6bd2\u9488\u53c8\u88ab\u80c0\u8d77\u7684\u8863\u773c\u6321\u4f4f\uff0c\u8dcc\u843d\u5728\u5730\u4e0a\u3002 \u3000\u3000\u8dc3\u8d77\u65bd\u88ad\u7684\u5c11\u5973\uff0c\u4e5f\u88ab\u90a3\u4e00\u8bb0\u5f3a\u731b\u7684\u6697\u52b2\uff0c\u9707\u9000\u4e86\u4e09\u6b65\u3002 \u3000\u3000\u4f46\u6c5f\u67ab\u540c\u65f6\u5ba4\u5de6\u8033\u5fae\u5fae\u4e00\u9ebb\uff0c\u5fcd\u4e0d\u4f4f\u4f38\u624b\u6293\u53bb\uff0c\u7adf\u7136\u6293\u5165\u624b\u4e2d\u4e00\u679a\u5c0f\u9488\uff0c\u957f\u4e0d\u8fc7\u534a\u5bf8\uff0c\u7ec6\u5982\u725b\u6bdb\uff0c\u4f46\u5168\u8eab\u6cdb\u8d77\u4e86\u4e00\u7247\u84dd\u6c6a\u6c6a\u7684\u989c\u8272\uff0c\u4e00\u773c\u95f4\uff0c\u5c31\u53ef\u4ee5\u8fa8\u8ba4\u51fa\u662f\u5267\u6bd2\u6dec\u7ec3\u4e4b\u7269\u3002 \u3000\u3000\u6c5f\u67ab\u6025\u6025\u8fd0\u6c14\uff0c\u95ed\u4f4f\u884c\u8840\u3002 \u3000\u3000\u201c\u5feb\u4e9b\u8fd0\u6c14\u903c\u6bd2\uff0c\u4f60\u4e2d\u7684\u662f\u5929\u4e0b\u4e03\u5927\u5947\u6bd2\u4e4b\u4e00\u7684\u5316\u8840\u6bd2\u2026\u2026\u201d \u3000\u3000\u7adf\u662f\u5f20\u56db\u59d1\u7684\u58f0\u97f3\u3002 \u3000\u3000\u90a3\u4eb2\u5207\u7684\u58f0\u97f3\uff0c\u7ed9\u4e86\u6c5f\u67ab\u4e00\u79cd\u6781\u5927\u7684\u5b89\u5b9a\u611f\uff0c\u7acb\u523b\u95ed\u4e0a\u53cc\u76ee\uff0c\u8fd0\u6c14\u903c\u6bd2\u3002 \u3000\u3000\u4ed6\u5185\u529f\u7cbe\u6e5b\uff0c\u662f\u6c5f\u6e56\u4e0a\u6781\u5c11\u89c1\u5230\u7684\u9ad8\u624b\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u5de6\u8033\u4e0a\u4e00\u5904\u7ec6\u5c0f\u7684\u4f24\u53e3\u5904\uff0c\u7a81\u7136\u6d41\u51fa\u4e00\u4e9b\u9ed1\u8840\u3002 \u3000\u3000\u4f46\u95fb\u5175\u5203\u4ea4\u51fb\u4e4b\u58f0\uff0c\u4e0d\u8fdc\u5904\u4f3c\u662f\u6b63\u5c55\u5f00\u7740\u4e00\u573a\u6fc0\u70c8\u7684\u640f\u6740\u3002 \u3000\u3000\u4e00\u58f0\u51b7\u5389\u7684\u60e8\u53eb\uff0c\u8d77\u81ea\u8eab\u4fa7\uff0c\u6c5f\u67ab\u611f\u89c9\u5230\u8138\u4e0a\u4f3c\u662f\u6e85\u4e0a\u4e86\u5fae\u6e29\u7684\u6c34\u6ef4\uff0c\u9f3b\u606f\u95f4\uff0c\u4e5f\u95fb\u5230\u4e86\u6de1\u6de1\u7684\u8165\u5473\u3002 \u3000\u3000\u662f\u8840\u3002 \u3000\u3000\u6fc0\u70c8\u7684\u6218\u6597\uff0c\u5c31\u5728\u6c5f\u67ab\u8eab\u8fb9\u3002 \u3000\u3000\u4f46\u6c5f\u67ab\u6ca1\u6709\u52a8\uff0c\u4ed6\u660e\u767d\uff0c\u5316\u8840\u6bd2\u7684\u5389\u5bb3\uff0c\u5982\u82e5\u4e2d\u9014\u5206\u5fc3\u505c\u606f\uff0c\u4f7f\u5185\u529f\u8feb\u96c6\u7684\u6bd2\u8840\uff0c\u6563\u5165\u7ecf\u8109\uff0c\u518d\u60f3\u628a\u6bd2\u8840\u56de\u96c6\u8d77\u6765\uff0c\u4e0d\u4f46\u8981\u5927\u8d39\u6c14\u529b\uff0c\u800c\u4e14\uff0c\u6210\u529f\u7684\u673a\u4f1a\u4e0d\u5927\u3002 \u3000\u3000\u201c\u6c5f\u67ab\uff0c\u542c\u7740\uff0c\u5316\u8840\u5947\u6bd2\uff0c\u4e00\u65e6\u4fb5\u5165\u5185\u8151\uff0c\u795e\u4ed9\u4e5f\u6551\u4e0d\u4e86\u4f60\uff0c\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u4e0d\u8981\u56de\u7b54\u6211\uff0c\u56e0\u4e3a\uff0c\u4f60\u7edd\u5bf9\u4e0d\u80fd\u5206\u5fc3\uff0c\u95ed\u4e0a\u53cc\u76ee\uff0c\u5168\u529b\u903c\u6bd2\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u8bf4\u7740\u8bdd\uff0c\u53f3\u624b\u5df2\u53d6\u51fa\u4e00\u53ea\u950b\u5229\u7684\u5c0f\u5200\uff0c\u770b\u51c6\u4e86\u4f4d\u7f6e\uff0c\u4e00\u5200\u6325\u51fa\u3002 \u3000\u3000\u201c\u54ce\u54df\uff01\u5f20\u59e8\uff0c\u4f60\u5272\u4e86\u4ed6\u4e00\u534a\u8033\u6735\u2026\u2026\u201d\u541f\u971c\u7684\u60ca\u53eb\u58f0\u7a81\u7136\u987f\u4f4f\u3002 \u3000\u3000\u56e0\u4e3a\uff0c\u4e00\u80a1\u9ed1\u8272\u7684\u8840\u7bad\uff0c\u5728\u6c5f\u67ab\u5185\u529b\u903c\u8feb\u4e0b\uff0c\u6fc0\u5c04\u800c\u51fa\u3002 \u3000\u3000\u201c\u5c11\u4e86\u534a\u4e2a\u8033\u6735\uff0c\u603b\u6bd4\u8981\u4ed6\u4e00\u6761\u547d\u597d\u2026\u2026\u201d\u5f20\u56db\u59d1\u51b7\u51b7\u7684\u63a5\u9053\uff1a\u201c\u51c6\u5907\u597d\u68c9\u82b1\u3001\u836f\u7269\uff0c\u8840\u8272\u89c1\u7ea2\uff0c\u7acb\u523b\u66ff\u4ed6\u5305\u624e\u8d77\u6765\u3002\u201d \u3000\u3000\u6c5f\u67ab\u786e\u4e5f\u6709\u4e00\u80a1\u72e0\u52b2\uff0c\u5f20\u56db\u59d1\u624d\u52a8\u624b\u5305\u624e\u5de6\u8033\u4f24\u52bf\uff0c\u4e00\u9762\u8981\u6c5f\u67ab\u505c\u6b62\u8fd0\u6c14\u903c\u8840\uff0c\u624d\u53f9\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u73b0\u5728\uff0c\u4f60\u53ef\u4ee5\u7741\u5f00\u773c\u775b\u770b\u770b\u4e86\uff0c\u4f60\u662f\u591a\u4e48\u4fa5\u5e78\u7684\u9003\u8fc7\u4e86\u8fd9\u6b21\u52ab\u96be\uff01\u201d \u3000\u3000\u6c5f\u67ab\u4e0d\u77e5\u9053\u8fd9\u4e00\u9635\u8fd0\u6c14\u903c\u6bd2\uff0c\u635f\u5931\u4e86\u591a\u5c11\u9c9c\u8840\uff0c\u4f46\u4e00\u505c\u4e0b\u6765\uff0c\u7adf\u6709\u4e9b\u6655\u7684\u611f\u89c9\u3002 \u3000\u3000\u7741\u773c\u770b\u53bb\uff0c\u53ea\u89c1\u90a3\u65bd\u653e\u6bd2\u9488\u7684\u5c11\u5973\uff0c\u5df2\u6a2a\u5c38\u773c\u524d\uff0c\u80e1\u840d\u3001\u6bb5\u4e5d\u624b\u4e2d\u7684\u5200\u3001\u5251\uff0c\u8840\u8ff9\u6765\u5e72\uff0c\u60f3\u6765\u662f\uff0c\u4e24\u4eba\u5408\u529b\u51fa\u624b\uff0c\u624d\u51fb\u6bd9\u4e86\u5f3a\u654c\u3002 \u3000\u3000\u541f\u971c\u7a7f\u7740\u4e00\u8eab\u67af\u6811\u76ae\u822c\u7684\u8863\u670d\uff0c\u9759\u9759\u7684\u7ad9\u5728\u4e00\u4fa7\uff0c\u89c6\u7740\u4e00\u5f20\u5fae\u5e26\u6b49\u610f\u7684\u5a07\u5a9a\u8138\u513f\uff0c\u770b\u4e0a\u53bb\u6709\u70b9\u602a\uff0c\u5c31\u50cf\u4e00\u622a\u5e72\u67af\u7684\u6811\u5e72\u4e0a\uff0c\u7a81\u7136\u7efd\u653e\u51fa\u4e00\u6735\u8273\u4e3d\u7684\u9c9c\u82b1\uff0c\u7ed9\u4eba\u4e00\u79cd\u6781\u4e0d\u8c03\u548c\u7684\u611f\u89c9\u3002 \u3000\u3000\u201c\u59d0\u59d0\u62c5\u8d1f\u8d77\u6e38\u52a8\u4fdd\u62a4\u7684\u8d23\u4efb\uff0c\u6211\u5374\u9009\u62e9\u4e86\u95e8\u53e3\u90a3\u68f5\u5927\u6811\uff0c\u6240\u4ee5\uff0c\u6211\u6f5c\u9690\u5728\u6811\u8eab\u4e0a\u2026\u2026\u201d\u541f\u971c\u7b11\u9053\uff1a\u201c\u6211\u63a8\u5224\u4ed6\u4eec\u4f1a\u9009\u5b9a\u6d1b\u9633\u5c45\u5927\u95e8\u53e3\u5904\uff0c\u4f5c\u91cd\u8981\u7684\u4e00\u51fb\uff0c\u5e78\u597d\u6ca1\u6709\u9009\u9519\u2026\u2026\u3002\u201d \u3000\u3000\u6c5f\u67ab\u770b\u770b\u541f\u971c\u7684\u8863\u670d\u989c\u8272\uff0c\u679c\u7136\u548c\u95e8\u53e3\u5fcd\u53d7\u7740\u9686\u51ac\u98ce\u96ea\u4fb5\u88ad\u7684\u8001\u69d0\u6811\uff0c\u4e00\u822c\u6a21\u6837\uff0c\u5fc3\u4e2d\u53c8\u611f\u52a8\uff0c\u53c8\u4f69\u670d\u5730\u8bf4\u9053\uff1a\u201c\u771f\u662f\u96be\u4e3a\u4f60\u4eec\u4e86\uff01\u201d \u3000\u3000\u201c\u4e0d\u8981\u8fd9\u6837\u561b\uff1f\u6c5f\u5927\u54e5\uff0c\u59d0\u59d0\u8eb2\u5728\u5783\u573e\u4e2d\uff0c\u5fcd\u53d7\u7740\u90a3\u767e\u7269\u6742\u9648\u81ed\u5473\uff0c\u6bd4\u6211\u8f9b\u82e6\u591a\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\u2026\u2026\u201d \u3000\u3000\u5f20\u56db\u59d1\u51b7\u51b7\u7684\u6253\u65ad\u4e86\u6c5f\u67ab\u7684\u8bdd\uff0c\u9053\uff1a\u201c\u8fd9\u4e00\u9635\u4ea4\u624b\uff0c\u4f60\u5df2\u7ecf\u6b7b\u4e86\u4e24\u6b21\uff0c\u4f60\u8f7b\u654c\u81ea\u8d1f\uff0c\u7adf\u7136\u88ab\u4e00\u679a\u5316\u8840\u6bd2\u9488\u5c04\u4e2d\uff0c\u5982\u975e\u4f60\u5185\u529f\u7cbe\u6df1\uff0c\u903c\u8840\u5feb\u901f\uff0c\u5355\u662f\u8fd9\u4e00\u679a\u5316\u8840\u6bd2\u9488\uff0c\u5c31\u8981\u4e86\u4f60\u7684\u6027\u547d\uff01\u201d \u3000\u3000\u201c\u8bf4\u6765\u60ed\u6127\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u6211\u60f3\u4e0d\u5230\uff0c\u5979\u53d1\u51fa\u6bd2\u9488\u65f6\uff0c\u7adf\u7136\u7559\u4e86\u4f59\u529b\uff0c\u5728\u6211\u638c\u529b\u53d1\u51fa\u4e4b\u540e\uff0c\u53c8\u6084\u7136\u5f39\u51fa\u4e86\u4e00\u679a\u6bd2\u9488\uff0c\u2026\u2026\u201d \u3000\u3000\u201c\u4f60\u90a3\u4e00\u638c\uff0c\u672c\u53ef\u4ee5\u53d6\u5979\u4e4b\u547d\uff0c\u2026\u2026\u201d\u5f20\u56db\u59d1\u795e\u8272\u51b7\u5cfb\u5730\u8bf4\uff0c\u201c\u5927\u6982\u89c1\u5bf9\u65b9\u662f\u4f4d\u59d1\u5a18\uff0c\u53c8\u52a8\u4e86\u601c\u9999\u60dc\u7389\u4e4b\u5fc3\uff0c\u6240\u4ee5\u624b\u4e0b\u7559\u60c5\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u2026\u2026\u201d \u3000\u3000\u201c\u6c5f\u5927\u54e5\uff0c\u4e0d\u8981\u96be\u8fc7\uff0c\u601c\u60dc\u5f31\u5973\uff0c\u5e94\u662f\u82f1\u96c4\u884c\u5f84\u2026\u2026\u201d\u541f\u971c\u9053\uff1a\u201c\u82f1\u96c4\u624d\u80fd\u8ba9\u7f8e\u4eba\u52a8\u529b\uff0c\u6211\u548c\u59d0\u59d0\uff0c\u90fd\u7b97\u662f\u5c0f\u7f8e\u4eba\u554a\uff01\u201d \u3000\u3000\u5979\u5c3d\u91cf\u628a\u81ea\u5df1\u6295\u5165\uff0c\u548c\u6c5f\u67ab\u7ad9\u4e00\u8d77\uff0c\u5171\u540c\u6765\u627f\u53d7\u5404\u79cd\u4efb\u80fd\u3001\u5c48\u8fb1\u3002 \u3000\u3000\u5f20\u56db\u59d1\u770b\u4e86\u541f\u971c\u4e00\u773c\uff0c\u63a5\u9053\uff1a\u201c\u654c\u4eba\u6765\u4e86\u4e24\u4e2a\uff0c\u4f60\u770b\u7684\u5f88\u6e05\u695a\uff0c\u65e9\u8be5\u6709\u9632\u4eba\u524d\u540e\u5939\u51fb\u7684\u6253\u7b97\uff0c\u53ef\u662f\u4f60\u7adf\u7136\u7167\u524d\u4e0d\u80fd\u987e\u540e\uff0c\u800c\u4e14\uff0c\u4e5f\u8be5\u65e9\u60f3\u5230\u8001\u59dc\u8fa3\u5fc3\uff0c\u540e\u9762\u7684\u653b\u51fb\uff0c\u5e94\u8be5\u6bd4\u524d\u9762\u6076\u6bd2\u4e00\u500d\uff0c\u53ef\u662f\u6c5f\u5927\u5c11\u7237\uff0c\u4f60\u7adf\u7136\u628a\u5f3a\u654c\u7ed9\u5fd8\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u90a3\u662f\u6c5f\u5927\u54e5\u65e9\u5c31\u53d1\u89c9\u4e86\u6211\uff0c\u624d\u6545\u610f\u628a\u654c\u4eba\u7559\u7ed9\u6211\u6765\u5bf9\u4ed8\uff01\u201d \u3000\u3000\u201c\u541f\u971c\uff0c\u8981\u4e0d\u63d2\u5634\uff0c\u5982\u82e5\u4ed6\u4e00\u773c\u5c31\u7740\u7a7f\u4e86\u4f60\u85cf\u8eab\u4e4b\u5904\uff0c\u5f20\u59e8\u7684\u4e94\u884c\u6f5c\u672f\uff0c\u5c82\u4e0d\u6210\u4e86\u9a97\u5c0f\u5b69\u7684\u73a9\u827a\u4e86\u3002\u201d \u3000\u3000\u201c\u541f\u971c\uff0c\u4e0d\u7528\u5e2e\u6211\u4e86\uff0c\u5927\u59d0\u6559\u8bad\u7684\u5bf9\uff01\u6211\u786e\u5b9e\u592a\u5927\u610f\u4e86\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u82e6\u7b11\u4e00\u4e0b\u53c8\u9053\uff1a \u3000\u3000\u201c\u5927\u59d0\uff0c\u4e5f\u4e00\u76f4\u8ddf\u7740\u5c0f\u5f1f\u4e86\u3002\u201d \u3000\u3000\u201c\u5927\u59d0\u662f\u53d7\u4eba\u4e4b\u6258\uff0c\u4e0d\u5f97\u4e0d\u6765\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u541f\u971c\u90a3\u4e00\u5251\u7684\u5feb\u901f\uff0c\u662f\u6211\u6bd5\u751f\u4ec5\u89c1\uff0c\u5979\u53eb\u4f60\u4e0d\u8981\u52a8\u65f6\uff0c\u5251\u5df2\u7ecf\u523a\u5165\u90a3\u4eba\u7684\u8eab\u4e0a\u3002\u7136\u540e\uff0c\u624d\u64a4\u51fa\u5929\u7f57\u4e1d\u7f51\uff0c\u7f51\u4f4f\u4e86\u90a3\u6279\u7eff\u5934\u6bd2\u8747\u3002\u201d \u3000\u3000\u6c5f\u67ab\u8f6c\u5934\u770b\u53bb\uff0c\u53ea\u89c1\u541f\u971c\u624b\u4e2d\u63d0\u7740\u4e00\u4e2a\u9ed1\u8272\u7684\u7ec6\u4e1d\u7f51\u515c\uff0c\u7f51\u515c\u4e2d\u805a\u6ee1\u4e86\u7eff\u5934\u82cd\u8747\u3002 \u3000\u3000\u90a3\u7f51\u515c\u6536\u7f29\u751a\u7d27\uff0c\u7f51\u4e2d\u7684\u6bd2\u8747\uff1b\u53ea\u80fd\u8815\u8815\u800c\u52a8\u3002 \u3000\u3000\u5f20\u56db\u59d1\u9053\uff1a\u201c\u516d\u5927\u6740\u624b\uff0c\u5df2\u7ecf\u6b7b\u4e86\u56db\u4e2a\uff0c\u8fd8\u6709\u4e8c\u4e2a\uff0c\u4ecd\u672a\u51fa\u73b0\uff0c\u4ed6\u4eec\u90a3\u6837\u6c89\u7740\uff0c\u5fc5\u7136\u662f\u66f4\u5389\u5bb3\u7684\u4eba\u7269\u3002\u201d \u3000\u3000\u201c\u7531\u6b64\u523b\u8d77\uff0c\u5c0f\u5f1f\u5168\u5fc3\u6212\u5907\uff0c\u7edd\u5bf9\u4e0d\u518d\u7ed9\u4ed6\u4eec\u5f97\u624b\u7684\u673a\u4f1a\u4e86\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u4e00\u5ff5\u4ec1\u6148\uff0c\u5df2\u7ecf\u4e22\u4e86\u534a\u53ea\u8033\u6735\uff0c\u5982\u518d\u5b58\u4ec1\u5fc3\uff0c\u53ef\u80fd\u4f1a\u4e22\u4e86\u4e00\u53ea\u773c\u775b\uff0c\u4e00\u6761\u817f\u4e86\uff0c\u90a3\u65f6\uff0c\u4f60\u53c8\u6b8b\u53c8\u778e\uff0c\u518d\u52a0\u4e0a\u7f3a\u4e86\u534a\u4e2a\u8033\u6735\uff0c\u60f3\u60f3\u770b\uff0c\u662f\u4e2a\u4ec0\u4e48\u5fb7\u884c\u5462\uff1f\u201d \u3000\u3000\u201c\u4e0d\u8981\u7d27\u554a\uff01\u6c5f\u5927\u54e5\u778e\u4e86\u8ddb\u4e86\uff0c\u6211\u548c\u59d0\u59d0\u7684\u7528\u5904\u5c31\u66f4\u5927\u4e86\uff0c\u6211\u4eec\u53ef\u4ee5\u5929\u5929\u966a\u7740\u4ed6\uff0c\u7167\u987e\u4ed6\uff0c\u2026\u2026\u201d\u541f\u971c\u9053\uff1a\u201c\u5929\u4e0b\u7684\u5973\u4eba\uff0c\u90fd\u4e0d\u559c\u6b22\u4ed6\u4e86\uff0c\u6211\u548c\u59d0\u4e5f\u4e0d\u4f1a\u80cc\u5f03\u4ed6\u3002\u201d \u3000\u3000\u8fd9\u662f\u8a93\u8a00\uff0c\u4e5f\u662f\u76df\u7ea6\uff0c\u8d64\u88f8\u88f8\u7684\u732e\u51fa\u4e86\u4e00\u9897\u5c11\u5973\u7684\u5fc3\u3002 \u3000\u3000\u6c5f\u67ab\u7adf\u7136\u4e0d\u6562\u591a\u770b\u541f\u971c\u4e00\u773c\uff0c\u5374\u8f6c\u671b\u7740\u5f20\u56db\u59d1\u8bf4\uff0c\u201c\u5927\u59d0\uff0c\u6211\u771f\u7684\u5c11\u4e86\u534a\u4e2a\u8033\u6735\uff1f\u201d \u3000\u3000\u201c\u600e\u4e48\uff1f\u4f60\u81ea\u5df1\u4e00\u70b9\u4e5f\u4e0d\u89c9\u5f97\u5417\uff1f\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u4f60\u4e00\u8eab\u7cfb\u5929\u4e0b\u5b89\u5371\uff0c\u751f\u6b7b\u5df2\u4e0d\u662f\u4f60\u4e2a\u4eba\u7684\u4e8b\u4e86\uff0c\u6240\u4ee5\uff0c\u6211\u53ea\u597d\u524a\u53bb\u4f60\u534a\u4e2a\u8033\u6735\uff0c\u653e\u5c3d\u6bd2\u8840\uff0c\u867d\u7136\u7834\u4e86\u76f8\uff0c\u4f46\u5374\u4fdd\u4f4f\u6027\u547d\u4e86\u3002\u201d \u3000\u3000\u6c5f\u67ab\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u5e78\u597d\u53ea\u662f\u534a\u53ea\u8033\u6735\uff0c\u5982\u82e5\u5c0f\u5f1f\u4f24\u5728\u624b\u81c2\u4e0a\uff0c\u5927\u59d0\u4e5f\u8981\u65a9\u65ad\u6211\u4e00\u53ea\u624b\u81c2\u4e86\uff1f\u201d \u3000\u3000\u201c\u6bd2\u86c7\u566c\u8155\uff0c\u58ee\u58eb\u65ad\u81c2\uff0c\u2026\u2026\u201d\u5f20\u56db\u59d1\u53f9\u606f\u4e00\u58f0\u53c8\u9053\uff1a\u201c\u4e3a\u4e86\u6551\u4f60\u4e00\u6761\u547d\uff0c\u6211\u4f1a\u6beb\u4e0d\u72b9\u8c6b\u7684\u51fa\u624b\u6325\u5200\u3002\u201d \u3000\u3000\u6c5f\u67ab\u7751\u8272\u5fae\u53d8\uff0c\u7b11\u4e00\u7b11\uff0c\u4e0d\u518d\u591a\u8a00\u3002 \u3000\u3000\u7b11\u7684\u5f88\u52c9\u5f3a\uff0c\u7b11\u5bb9\u4e2d\u900f\u51fa\u4e86\u65e0\u9650\u7684\u51c4\u51c9\u3002 \u3000\u3000\u5f20\u56db\u59d1\u5fc3\u4e2d\u6697\u7b11\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u4ed6\u8fd9\u6837\u6d12\u8131\u7684\u4eba\uff0c\u7adf\u7136\u4e5f\u4f1a\u5f88\u5728\u4e4e\u81ea\u5df1\u7684\u5f62\u8c8c\u3002\u201d \u3000\u3000\u53ea\u89c1\u6c5f\u67ab\u53e3\u4e2d\u5ff5\u5ff5\u6709\u8bcd\uff0c\u58f0\u97f3\u4f4e\u5fae\uff0c\u4e0d\u77e5\u9053\u5728\u8bf4\u4e9b\u4ec0\u4e48\uff1f \u3000\u3000\u201c\u6c5f\u5927\u54e5\uff0c\u4f60\u6ca1\u4e8b\u5427\uff1f\u201d\u541f\u971c\u5927\u4e3a\u62c5\u5fc3\u5730\u8bf4\uff0c\u201c\u4e0d\u8981\u803d\u5fc3\u4f60\u7684\u8033\u6735\uff0c\u4f60\u4e00\u76f4\u5c31\u662f\u6211\u4eec\u5fc3\u4e2d\u7684\u4eba\uff0c\u4f60\u778e\u4e86\uff0c\u8ddb\u4e86\uff0c\u90fd\u4e0d\u4f1a\u5f71\u54cd\u4f60\u5728\u6211\u548c\u59d0\u59d0\u5fc3\u4e2d\u7684\u5730\u4f4d\uff0c\u4f60\u8d8a\u9700\u8981\u6211\u4eec\uff0c\u6211\u4eec\u4f1a\u66f4\u611f\u5feb\u4e50\u3002\u201d \u3000\u3000\u201c\u6211\u4e0d\u4f1a\u803d\u5fc3\u81ea\u5df1\u53d8\u7684\u6709\u591a\u4e48\u4e11\u602a\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u6211\u803d\u5fc3\u7684\u662f\u4f1a\u4e0d\u4f1a\u5f71\u5230\u6211\u7684\u6b66\u529f\uff0c\u541f\u971c\uff0c\u6211\u4eec\u4e0d\u80fd\u5931\u8d25\uff0c\u5bf9\u654c\u4eba\u591a\u4e00\u5206\u4e86\u89e3\uff0c\u6211\u5c31\u589e\u591a\u4e86\u4e00\u5206\u60f6\u6050\uff0c\u6211\u4eec\u7684\u654c\u4eba\u4f17\u591a\uff0c\u800c\u4e14\uff0c\u5341\u5206\u5f3a\u5927\uff0c\u6211\u4eec\u5c31\u7b97\u4ee5\u4e00\u6321\u5341\uff0c\u4e5f\u65e0\u6cd5\u6297\u62d2\u4ed6\u4eec\uff0c\u6211\u4eec\u9700\u8981\u5e2e\u624b\uff0c\u53ef\u662f\u5df2\u7ecf\u6765\u4e0d\u53ca\u8bad\u7ec3\u4e86\uff0c\u6211\u4eec\u589e\u5f3a\u5b9e\u529b\u7684\u552f\u4e00\u529e\u6cd5\uff0c\u5c31\u662f\u4ece\u654c\u4eba\u90a3\u91cc\u4e89\u53d6\u8fc7\u6765\u3002\u201d \u3000\u3000\u201c\u6211\u660e\u767d\u4e86\u2026\u2026\u201d\u541f\u971c\u9053\uff1a\u201c\u6211\u4f1a\u8ddf\u59d0\u59d0\u597d\u597d\u7684\u5546\u91cf\u4e00\u4e0b\uff0c\u770b\u770b\u600e\u4e48\u5e2e\u52a9\u4f60\uff01\u201d \u3000\u3000\u5f20\u56db\u59d1\u6697\u6697\u5fd6\u9053\uff1a\u201c\u539f\u6765\uff0c\u4ed6\u5728\u62c5\u5fc3\u7834\u76f8\u4e4b\u540e\uff0c\u4f1a\u5f71\u54cd\u5230\u4ed6\u7684\u611f\u5fc3\u672f\uff1f\u8fd9\u4e5f\u96be\u602a\uff0c\u7f3a\u4e86\u534a\u622a\u8033\u6735\u7684\u7537\u4eba\uff0c\u7a76\u7adf\u662f\u4e00\u4e2a\u660e\u663e\u7684\u7f3a\u9677\uff0c\u8fd9\u4e16\u4e0a\uff0c\u80fd\u6709\u591a\u5c11\u5973\u4eba\uff1f\u771f\u7684\u6b23\u8d4f\u7f3a\u9677\u7f8e\u5462\uff1f\u4e5f\u4f1a\u771f\u7684\u5f71\u54cd\u5230\u4ed6\u7684\u60d1\u5fc3\u672f\u4e86\u3002\u201d \u3000\u3000\u201c\u6c5f\u67ab\uff01\u4e3a\u4ec0\u4e48\u4e0d\u5411\u6211\u6c42\u52a9\u5462\uff1f\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u4f60\u5fd8\u4e86\uff0c\u5927\u59d0\u7684\u533b\u672f\uff0c\u4e5f\u5341\u5206\u9ad8\u660e\u554a\uff01\u201d \u3000\u3000\u201c\u662f\u7684\uff01\u6211\u77e5\u9053\u5927\u59d0\u7684\u533b\u672f\u548c\u7597\u6bd2\u4e4b\u80fd\uff0c\u4e3e\u4e16\u65e0\u53cc\u3002\u2026\u2026\u201d \u3000\u3000\u201c\u4e3e\u4e16\u65e0\u53cc\uff0c\u5012\u4e0d\u6562\u5f53\uff0c\u4e0d\u8fc7\uff0c\u4e00\u70b9\u5c0f\u75c5\u5c0f\u6059\uff0c\u5012\u662f\u836f\u5230\u75c5\u9664\u3002\u201d \u3000\u3000\u6c5f\u67ab\u9eef\u7136\u8bf4\u9053\uff1a\u201c\u5927\u59d0\u624b\u672f\u65ad\u8033\uff0c\u5929\u4e0b\u540d\u533b\uff0c\u6709\u8c01\u80fd\u91cd\u7eed\u65ad\u808c\u3002\u201d \u3000\u3000\u201c\u6211\u80fd\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u5982\u8bba\u4f60\u98ce\u6d41\u7f6a\u5b7d\uff0c\u7f3a\u4e86\u534a\u622a\u8033\u6735\uff0c\u4e5f\u8bb8\u53ef\u4ee5\u51cf\u5c11\u4f60\u4e9b\u7f6a\u6076\uff0c\u4f46\u4f60\u53c8\u80a9\u8d1f\u5149\u590d\u6b66\u6797\u5927\u4efb\uff0c\u504f\u53c8\u7ec3\u4e86\u548c\u5f62\u8c8c\u76f8\u8f85\u4e3a\u6210\u7684\u6b66\u529f\uff0c\u5509\uff01\u4e3a\u4e86\u6c5f\u6e56\u5927\u5c40\uff0c\u5927\u59d0\u53ea\u597d\u514d\u4e3a\u5176\u96be\u7684\u5e2e\u52a9\u4f60\u8865\u4e0a\u65ad\u8033\u4e86\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u8fd9\u53ef\u662f\u6316\u8089\u8865\u75ae\u7684\u4e8b\uff0c\u522b\u4eba\u4e5f\u5e2e\u4e0d\u4e0a\u5fd9\uff0c\u53ea\u6709\u4ece\u4f60\u8eab\u4e0a\u6316\u4e00\u5757\u8089\u4e86\u3002\u201d \u3000\u3000\u6c5f\u67ab\u771f\u7684\u4fe1\u4e86\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u90a3\u5c31\u987e\u52b3\u5927\u59d0\u4e86\u3002\u201d \u3000\u3000\u53ea\u542c\u53f6\u96ea\u7684\u58f0\u97f3\uff0c\u4f20\u4e86\u8fc7\u6765\uff0c\u9053\uff1a\u201c\u4e24\u4e2a\u4eba\u85cf\u5f97\u5f88\u5bc6\uff0c\u627e\u4e0d\u51fa\u4e00\u70b9\u75d5\u8ff9\u3002\u201d\u4eba\u968f\u58f0\u5230\uff0c\u51fa\u73b0\u5728\u6c5f\u67ab\u8eab\u524d\u3002 \u3000\u3000\u201c\u6211\u4eec\u5148\u56de\u53bb\u5427\uff01\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u4e5f\u8bb8\uff0c\u4ed6\u4eec\u4f1a\u505c\u4e00\u9635\u518d\u4e0b\u624b\uff1f\u201d \u3000\u3000\u5f20\u56db\u59d1\u76ee\u5149\u4e00\u63a0\u96ea\u3001\u971c\u4e8c\u5973\uff0c\u9053\uff1a\u201c\u56de\u53bb\uff0c\u6d17\u4e2a\u6fa1\uff0c\u6362\u4ef6\u8863\u670d\u518d\u6765\uff0c\u6c5f\u67ab\u63a8\u65ad\u4e0d\u9519\uff0c\u4ed6\u4eec\u6682\u65f6\u4e0d\u4f1a\u73b0\u8eab\uff0c\u51fa\u624b\u4e86\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e00\u7f51\u8840\u8747\u5462\uff1f\u8981\u5982\u4f55\u5904\u7f6e\uff1f\u2026\u2026\u201d\u541f\u971c\u9053\uff1a\u201c\u6211\u770b\u53ea\u6709\u7528\u706b\u70e7\u4e86\uff0c\u53ef\u662f\uff0c\u8fd9\u9762\u5929\u8695\u4e1d\u7f51\u2026\u2026\u201d \u3000\u3000\u201c\u4ea4\u7ed9\u6211\u5427\uff01\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u8fd9\u79cd\u82d7\u7586\u8840\u8747\uff0c\u57f9\u517b\u4e0d\u6613\uff0c\u6bc1\u53bb\u672a\u514d\u53ef\u60dc\uff0c\u6211\u8981\u4ee5\u5176\u4eba\u4e4b\u9053\uff0c\u8fd8\u6cbb\u5176\u4eba\u2026\u2026\u201d \u3000\u3000\u201c\u8fd9\u8840\u8747\u5f88\u6076\u6bd2\u5417\uff1f\u2026\u2026\u201d\u6c5f\u67ab\u770b\u7740\u90a3\u4e00\u7f51\u6bd2\u8747\uff0c\u53ea\u89c9\u7740\u60f9\u4eba\u6076\u5fc3\u3001\u8ba8\u538c\uff0c\u5374\u662f\u77a7\u4e0d\u51fa\u6709\u4ec0\u4e48\u53ef\u4f2f\u4e4b\u5904\uff1f \u3000\u3000\u5f20\u56db\u59d1\u63a5\u8fc7\u4e1d\u7f51\u8840\u8747\uff0c\u6325\u624b\u4ee4\u96ea\u3001\u971c\u4e8c\u5973\u9000\u53bb\uff0c\u624d\u7f13\u6b65\u9000\u5165\u6d1b\u9633\u5c45\u4e2d\u3002 \u3000\u3000\u6b64\u65f6\u5c1a\u65e9\uff0c\u6d1b\u9633\u5c45\u4e2d\uff0c\u8fd8\u672a\u5f00\u59cb\u4e0a\u5ba2\u3002 \u3000\u3000\u6c5f\u67ab\u3001\u6bb5\u4e5d\u3001\u80e1\u840d\uff0c\u7d27\u968f\u8eab\u540e\uff0c\u76f4\u884c\u5165\u6c5f\u67ab\u5bbf\u4f4f\u6240\u5728\u3002 \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u8fce\u4e86\u51fa\u6765\uff0c\u770b\u4e86\u5f20\u56db\u59d1\u4e00\u773c\uff0c\u6b32\u8a00\u53c8\u6b62\u3002 \u3000\u3000\u201c\u8bf4\u5427\uff01\u5927\u5e08\uff0c\u6ca1\u6709\u4e8b\u80fd\u7792\u8fc7\u5927\u59d0\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u518d\u8bf4\uff0c\u6211\u6c5f\u67ab\u7684\u4e8b\uff0c\u4e5f\u4e0d\u7528\u7792\u5979\uff01\u201d \u3000\u3000\u201c\u662f\uff01\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u9053\uff1a\u201c\u6885\u82b1\u4f20\u6765\u4e86\u4fe1\u606f\uff0c\u4e09\u5bab\u4e3b\u5df2\u5230\u957f\u5b89\uff0c\u51e0\u65f6\u4f1a\u6765\u6d1b\u9633\u5c45\uff0c\u8fd8\u672a\u786e\u5b9a\u3002\u201d \u3000\u3000\u4f46\u89c1\u5201\u9e4f\u5feb\u901f\u5954\u4e86\u8fc7\u6765\uff0c\u9053\uff1a\u201c\u603b\u5802\u98de\u9e3d\u4f20\u4e66\uff0c\u5929\u865a\u5b50\u548c\u53e6\u4e00\u4e2a\u526f\u603b\u5802\u4e3b\uff0c\u7387\u9886\u4e86\u516b\u4f4d\u5251\u624b\u8d76\u6765\uff0c\u770b\u6837\u5b50\uff0c\u51c6\u5907\u653e\u624b\u4e00\u6218\uff0c\u4e0d\u518d\u9000\u8ba9\u4e86\uff01\u201d \u3000\u3000\u6c5f\u67ab\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u54b1\u4eec\u623f\u91cc\u5750\u5427\uff01\u201d \u3000\u3000\u5201\u9e4f\u770b\u6c5f\u67ab\u5de6\u8033\u4e0a\u5305\u624e\u4e86\u767d\u7eb1\uff0c\u5206\u660e\u662f\u53d7\u4e86\u4f24\uff0c\u5fc3\u4e2d\u5341\u5206\u5947\u602a\uff0c\u4ee5\u4ed6\u6b66\u529f\u4e4b\u5f3a\uff0c\u8c01\u80fd\u4f24\u5230\u4e86\u4ed6\uff0c\u5fc3\u4e2d\u7591\u7aa6\u91cd\u91cd\uff0c\u5374\u53c8\u4e0d\u4fbf\u591a\u95ee\u3002 \u3000\u3000\u5ffd\u7136\u95f4\uff0c\u53d1\u89c9\u4e86\u5f20\u56db\u59d1\u624b\u4e2d\u63d0\u7f51\uff0c\u4e00\u7f51\u6bd2\u8747\uff0c\u4ed6\u4e45\u8d70\u6c5f\u6e56\uff0c\u8bc6\u89c1\u5e7f\u535a\uff0c\u4e00\u773c\u8ba4\u51fa\u662f\u82d7\u7586\u6bd2\u7269\uff0c\u5fc3\u4e2d\u5927\u5403\u4e00\u60ca\uff0c\u6025\u6025\u5411\u8eab\u65c1\u4fa7\u6a2a\u79fb\u4e24\u6b65\u3002 \u3000\u3000\u539f\u6765\uff0c\u5f20\u56db\u59d1\u4e00\u8eab\u9ed1\u8863\uff0c\u8138\u4e0a\u4e5f\u6d82\u4e86\u836f\u7269\uff0c\u770b\u4e0a\u53bb\u5f62\u8c8c\u602a\u5f02\uff0c\u5b8c\u5168\u4e0d\u50cf\u4e2d\u539f\u4eba\u6c0f\u3002 \u3000\u3000\u51e0\u4eba\u884c\u5165\u5385\u4e2d\uff0c\u5206\u522b\u843d\u5ea7\u3002 \u3000\u3000\u5f20\u56db\u59d1\u624d\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u5201\u5144\uff0c\u8ba4\u51fa\u8fd9\u7f51\u4e2d\u4e4b\u7269\u4e48\uff1f\u201d \u3000\u3000\u201c\u8840\u8747\uff0c\u6b64\u7269\u4ea7\u4e8e\u82d7\u7586\uff0c\u54ac\u4eba\u4e00\u53e3\uff0c\u6bd2\u968f\u8840\u884c\uff0c\u5b50\u4e0d\u89c1\u5348\uff0c\u5348\u4e0d\u89c1\u5b50\uff0c\u5fc5\u6b7b\u65e0\u7591\uff0c\u6b64\u7b49\u6bd2\u7269\uff0c\u600e\u4f1a\u5728\u957f\u5b89\u51fa\u73b0\uff1f\u83ab\u975e\u82d7\u7586\u7684\u8001\u6bd2\u7269\uff0c\u4e5f\u5230\u4e86\u957f\u5b89\u3002\u201d\u5201\u9e4f\u6ed4\u6ed4\u4e0d\u7edd\uff0c\u4e00\u53e3\u6c14\u8bf4\u51fa\u4e86\u6bd2\u8747\u7684\u6765\u5386\u3002 \u3000\u3000\u6bb5\u4e5d\u770b\u770b\u90a3\u7eff\u5934\u5927\u82cd\u8747\uff0c\u9053\uff1a\u201c\u4e0d\u8fc7\u4e00\u53ea\u82cd\u8747\u7f62\u4e86\uff0c\u65e0\u7259\u65e0\u523a\u7684\uff0c\u600e\u4f1a\u4f24\u4eba\uff1f\u201d \u3000\u3000\u5201\u9e4f\u9053\uff1a\u201c\u6bb5\u5144\uff0c\u8840\u8747\u4e3a\u82d7\u7586\u4e00\u5927\u6d3b\u6bd2\u4e4b\u4e00\uff0c\u5343\u4e07\u4e0d\u53ef\u5c0f\u89d1\uff0c\u5b83\u4e0d\u4f46\u6bd2\u6027\u5947\u9ad8\uff0c\u800c\u4e14\u98de\u884c\u5feb\u901f\uff0c\u53e3\u4e2d\u4e00\u6839\u6bd2\u523a\uff0c\u5c16\u5982\u5229\u9488\u3002\u2026\u2026\u201d \u3000\u3000\u5201\u9e4f\u8d77\u8eab\u5bf9\u5f20\u56db\u59d1\u62b1\u62f3\u4e00\u793c\uff0c\u9053\uff1a\u201c\u670b\u53cb\u9ad8\u59d3\u5927\u540d\uff0c\u63d0\u4e86\u4e00\u7f51\u8840\u8747\uff0c\u5fc5\u662f\u7528\u6bd2\u7684\u5927\u5bb6\uff0c\u5c31\u5201\u67d0\u6240\u77e5\uff0c\u8840\u8747\u53e3\u5229\u5982\u5200\uff0c\u4e00\u822c\u7684\u4e1d\u7f51\uff0c\u7edd\u5bf9\u65e0\u6cd5\u7f51\u5f97\u4f4f\u5b83\uff1f\u201d \u3000\u3000\u201c\u5c0f\u59b9\u5f20\u56db\u59d1\uff0c\u5201\u89c1\u548c\u6211\u6709\u8fc7\u6570\u9762\u4e4b\u7f18\uff0c\u5c0f\u59b9\u867d\u7136\u6613\u4e86\u5bb9\uff0c\u4f46\u58f0\u97f3\u6ca1\u6709\u53d8\u554a\uff01\u201d \u3000\u3000\u201c\u6613\u82b1\u624b\u2026\u2026\u201d\u5201\u9e4f\u7b11\u9053\uff1a\u201c\u5931\u656c\uff0c\u5931\u656c\uff0c\u60f3\u4e0d\u5230\u4f60\u4e5f\u91cd\u5165\u6c5f\u6e56\u4e86\u3002\u201d \u3000\u3000\u201c\u503e\u5de2\u4e4b\u4e0b\u65e0\u5b8c\u5375\uff0c\u6ca1\u6709\u6cd5\u5b50\u554a\uff01\u8bf8\u4f4d\u5c0f\u5750\u7247\u523b\uff0c\u6211\u53bb\u5904\u7f6e\u8fd9\u4e9b\u6bd2\u7269\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u4e3a\u59d1\u5a18\u5f15\u8def\u2026\u2026\u201d \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u7ad9\u8d77\u8eab\u5b50\uff0c\u5f53\u5148\u5411\u5916\u884c\u53bb\u3002 \u3000\u3000\u5201\u9e4f\u6b63\u6b32\u4e3e\u6b65\u968f\u884c\uff0c\u5374\u88ab\u6c5f\u67ab\u53eb\u4f4f\uff0c\u9053\uff1a\u201c\u5929\u865a\u5b50\u91cd\u56de\u6d1b\u9633\u5c45\uff0c\u8fd8\u6709\u4e00\u4f4d\u526f\u5802\u4e3b\uff0c\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u201c\u4f20\u4e66\u4e2d\u6ca1\u6709\u63d0\u8d77\u2026\u2026\u201d\u5201\u9e4f\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u7edd\u4e0d\u4f1a\u662f\u4e07\u526f\u5802\u4e3b\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u5462\uff1f\u201d \u3000\u3000\u201c\u56de\u5e08\u53d4\u8bdd\u2026\u2026\u201d\u5201\u9e4f\u606d\u606d\u656c\u656c\u5730\u8bf4\u9053\uff1a\u201c\u5982\u662f\u4e07\u526f\u603b\u5802\u4e3b\u8981\u6765\uff0c\u4e66\u4e2d\u5fc5\u4f1a\u63d0\u8d77\uff0c\u4f46\u7565\u800c\u4e0d\u63d0\uff0c\u5c31\u5927\u5927\u7684\u6709\u5b66\u95ee\u4e86\uff0c\u4e5f\u7b49\u4e8e\u8bf4\u660e\u4e86\u4e0d\u662f\u4e07\u526f\u603b\u5802\u4e3b\u6765\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u5e94\u8be5\u662f\u4ed6\u6765\uff0c\u504f\u504f\u53c8\u4e0d\u6d3e\u4ed6\u6765\uff01\u201d \u3000\u3000\u201c\u5bf9\uff01\u5c31\u662f\u8fd9\u4e2a\u610f\u601d\uff0c\u53e6\u5916\u4e24\u4f4d\u526f\u603b\u5802\u4e3b\uff0c\u516c\u52a1\u7e41\u91cd\uff0c\u5f88\u5c11\u5165\u6c5f\u6e56\u8d70\u52a8\uff0c\u4e00\u65e6\u51fa\u52a8\uff0c\u5c31\u8bf4\u660e\u4e86\u6b64\u4e8b\u7684\u91cd\u8981\uff0c\u7edd\u5bf9\u4e0d\u51c6\u5931\u8d25\uff0c\u603b\u5802\u4e2d\u6ca1\u6709\u8fd9\u4e2a\u89c4\u5b9a\uff0c\u4f46\u5929\u9a6c\u5802\u4e2d\u4eba\u5374\u4f1a\u8fd9\u4e48\u60f3\uff01\u201d\u5201\u9e4f\u9053\uff1a\u201c\u81f3\u4e8e\u90a3\u516b\u5927\u5251\u624b\uff0c\u4e5f\u5fc5\u662f\u603b\u5802\u4e3b\u4eb2\u81ea\u8bad\u7ec3\u7684\u5251\u624b\u4e2d\u62e8\u51fa\u6765\u7684\u3002\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u662f\u603b\u5802\u4e2d\u7684\u957f\u8001\u62a4\u6cd5\u5417\uff1f\u201d \u3000\u3000\u201c\u4e0d\u662f\uff01\u5251\u624b\u662f\u4e00\u79cd\u7279\u522b\u7684\u79f0\u547c\uff0c\u542c\u8bf4\uff0c\u603b\u5802\u4e2d\u8bad\u7ec3\u4e86\u8fd9\u4e48\u4e00\u6279\u4eba\uff0c\u6211\u4eec\u90fd\u672a\u89c1\u8fc7\uff0c\u662f\u5929\u9a6c\u603b\u5802\u4e2d\u4e00\u6279\u9690\u5bc6\u4eba\u7269\u8fd9\u4e00\u6b21\uff0c\u7adf\u7136\u51fa\u52a8\u4e0b\u624b\u2026\u2026\u201d\u5201\u9e4f\u8bf4\uff0c\u201c\u9664\u4e86\u8868\u793a\u4e0d\u518d\u9000\u8ba9\u7684\u51b3\u5fc3\u4e4b\u5916\uff0c\u4e5f\u6709\u70ab\u8000\u5b9e\u529b\u7684\u610f\u601d\u3002\u201d \u3000\u3000\u201c\u8fd9\u5c31\u70ed\u95f9\u4e86\uff0c\u542c\u8bf4\u5185\u5bab\u4e00\u7cfb\u4e2d\u7684\u4e09\u5bab\u4e3b\uff0c\u4e5f\u5230\u4e86\u957f\u5b89\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u7b11\u9053\uff1a\u201c\u53cc\u65b9\u4e00\u65e6\u51b2\u7a81\uff0c\u867d\u975e\u5bf9\u738b\u7684\u5c40\u9762\uff0c\u53ef\u4e5f\u662f\u5c06\u5bf9\u5c06\u7684\u5927\u573a\u9762\u4e86\u3002\u201d \u3000\u3000\u5201\u9e4f\u5fae\u5fae\u4e00\u6014\uff0c\u9053\uff1a\u201c\u5e08\u53d4\uff0c\u8fd9\u4e2a\u6d88\u606f\uff0c\u8981\u4e0d\u8981\u4f20\u62a5\u5165\u5929\u9a6c\u603b\u5802\uff1f\u201d \u3000\u3000\u201c\u5bf9\u4f60\u6709\u5229\u7684\u4e8b\uff0c\u5f53\u7136\u8981\u62a5\u4e0a\u53bb\u2026\u2026\u201d\u6c5f\u67ab\u7b11\u9053\uff1a\u201c\u4ed6\u4eec\u5bf9\u4f60\u8d8a\u4fe1\u4efb\uff0c\u4f60\u529e\u8d77\u4e8b\u6765\u8d8a\u65b9\u4fbf\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u6211\u8fd9\u5c31\u53bb\uff01\u201d \u3000\u3000\u5201\u9e4f\u6025\u6025\u5730\u5411\u5916\u884c\u53bb\u3002 \u3000\u3000\u5f20\u56db\u59d1\u7f13\u6b65\u884c\u4e86\u51fa\u6765\uff0c\u63a5\u9053\uff1a\u201c\u6c5f\u67ab\uff0c\u6211\u770b\u4f60\u53d7\u4f24\u7684\u4e8b\uff0c\u4ed6\u4e5f\u4f1a\u62a5\u5165\u603b\u5802\u4e86\u3002\u201d \u3000\u3000\u201c\u5927\u59d0\u7684\u610f\u601d\uff0c\u53ef\u662f\u8bf4\uff0c\u8fd9\u4e2a\u4eba\u4e0d\u80fd\u4fe1\u4efb\uff1f\u2026\u2026\u201d \u3000\u3000\u201c\u90a3\u5012\u4e0d\u662f\uff0c\u4ed6\u5bf9\u4f60\u7684\u5fe0\u5b9e\uff0c\u7edd\u5bf9\u8d85\u8fc7\u5929\u9a6c\u5802\uff0c\u4f46\u4ed6\u597d\u5927\u559c\u529f\u7684\u751f\u6027\u672a\u6539\uff0c\u6709\u4e9b\u673a\u5bc6\u5927\u4e8b\uff0c\u8fd8\u4e0d\u80fd\u8ba9\u4ed6\u77e5\u9053\u2026\u2026\u201d\u5f20\u56db\u59d1\u8bf4\uff0c\u201c\u4ed6\u4e0d\u4f1a\u51fa\u5356\u4f60\uff0c\u4f46\u4ed6\u4f1a\u4e0d\u81ea\u89c9\u4ed6\u8bf4\u51fa\u6765\uff0c\u4ee5\u8868\u793a\u81ea\u5df1\u7684\u89c1\u591a\u8bc6\u5e7f\uff0c\u6d88\u606f\u7075\u901a\u3002\u6c5f\u6e56\u4e0a\uff0c\u8fd9\u79cd\u4eba\u5f88\u591a\u5f88\u591a\uff0c\u4e5f\u4e0d\u5fc5\u592a\u8fc7\u8d23\u602a\u5201\u9e4f\u3002\u201d \u3000\u3000\u201c\u5bf9\uff01\u4ed6\u4e00\u751f\u81ea\u8d1f\u597d\u80dc\uff0c\u603b\u5e0c\u671b\u51fa\u4eba\u5934\u5730\uff0c\u624b\u63e1\u6743\u67c4\uff0c\u7eb5\u6a2a\u81ea\u5982\uff0c\u8d39\u5c3d\u4e86\u5fc3\u673a\u624b\u6bb5\uff0c\u5e0c\u671b\u80fd\u6210\u4e3a\u6c5f\u6e56\u4e0a\u9876\u5c16\u4eba\u7269\uff0c\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u9053\uff1a\u201c\u4f46\u771f\u6b63\u7684\u4e00\u4e2a\u6b66\u6797\u9ad8\u624b\uff0c\u662f\u9700\u8981\u5e08\u627f\u3001\u5929\u8d4b\u3001\u673a\u7f18\u7b49\u8bf8\u822c\u5de7\u5408\uff0c\u518d\u52a0\u4e0a\u5168\u529b\u82e6\u7ec3\uff0c\u624d\u80fd\u8fbe\u6210\u7684\u4e0a\u4e58\u5883\u754c\uff0c\u76ee\u524d\u4ed6\uff0c\u2026\u2026\u201d \u3000\u3000\u76ee\u5149\u4e00\u63a0\u6c5f\u67ab\uff0c\u63a5\u9053\uff1a\u201c\u5201\u9e4f\u5df2\u5b58\u4e86\u5fc5\u6b7b\u4e4b\u5fc3\uff0c\u5fe0\u8bda\u4e0d\u7528\u6000\u7591\uff0c\u53ea\u4e0d\u8fc7\uff0c\u4ed6\u5fc3\u6027\u8fd8\u4e0d\u591f\u575a\u5b9a\uff0c\u6240\u4ee5\uff0c\u6709\u4e9b\u91cd\u5927\u7684\u4e8b\u60c5\uff0c\u8fd8\u4e0d\u5b9c\u8ba9\u4ed6\u77e5\u9053\u2026\u2026\u201d \u3000\u3000\u201c\u6709\u8fd9\u6837\u4e00\u4e2a\u57cb\u5728\u5929\u9a6c\u5802\u7684\u68cb\u5b50\uff0c\u662f\u4e00\u4ef6\u4e0d\u5bb9\u6613\u7684\u4e8b\u60c5\uff0c\u4f60\u4eec\u8981\u597d\u597d\u8fd0\u7528\uff0c\u5343\u4e07\u522b\u4f24\u5bb3\u5230\u4ed6\u2026\u2026\u201d\u5f20\u56fd\u59d1\u795e\u60c5\u4e25\u8083\u5730\u8bf4\u9053\uff1a\u201c\u5929\u9a6c\u603b\u5802\u6765\u4e86\u4e24\u4f4d\u526f\u603b\u5802\u4e3b\uff0c\u5185\u5bab\u4e2d\u4e5f\u6765\u4e86\u4e00\u4f4d\u4e09\u5bab\u4e3b\uff0c\u5929\u738b\u95e8\u4e2d\u7684\u5929\u738b\uff0c\u4e5f\u968f\u65f6\u53ef\u4ee5\u9a7e\u5230\u957f\u5b89\uff0c\u8fd9\u662f\u4e00\u573a\u5927\u76db\u4f1a\uff0c\u5982\u662f\u4e00\u4e2a\u5904\u7f6e\u4e0d\u597d\uff0c\u5f88\u53ef\u80fd\u4f1a\u5168\u519b\u5c3d\u6ca1\uff0c\u6c5f\u67ab\uff0c\u4f60\u51c6\u5907\u5982\u4f55\u5e94\u4ed8\uff1f\u201d \u3000\u3000\u201c\u6211\u2026\u2026\u201d\u6c5f\u67ab\u6123\u4e86\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u6211\u60f3\uff0c\u6211\u4eec\u7684\u5b9e\u529b\u53ea\u80fd\u5bf9\u6297\u4e00\u5904\u5f3a\u654c\u3002\u2026\u2026\u201d \u3000\u3000\u201c\u5bf9\u6297\u4e00\u5904\uff0c\u4e5f\u662f\u4e00\u4e2a\u5f88\u60b2\u60e8\u7684\u5c40\u9762\uff0c\u6740\u4eba\u4e00\u5343\uff0c\u81ea\u635f\u516b\u767e\uff0c\u4f60\u4eec\u6ca1\u6709\u8fd9\u4e2a\u672c\u94b1\u2026\u2026\u201d \u3000\u3000\u5f20\u56db\u59d1\u8bf4\uff0c\u201c\u66f4\u91cd\u8981\u7684\u662f\uff0c\u6211\u4eec\u5982\u679c\u633a\u8eab\u800c\u51fa\uff0c\u4e0d\u8bba\u80dc\u8d1f\uff0c\u90fd\u4f1a\u8ba9\u4ed6\u4eec\u627e\u51fa\u76ee\u6807\uff0c\u4ed6\u4eec\u5f88\u53ef\u80fd\u4f1a\u6682\u7f62\u79c1\u6597\uff0c\u5168\u529b\u6765\u5bf9\u4ed8\u6211\u4eec\uff0c\u5982\u679c\uff0c\u8fd9\u4e09\u80a1\u529b\u91cf\u5408\u4e8e\u4e00\u5904\uff0c\u5929\u4e0b\u6ca1\u6709\u4eba\u80fd\u591f\u6297\u62d2\u5f97\u4f4f\uff0c\u2026\u2026\u201d \u3000\u3000\u201c\u6240\u4ee5\uff0c\u6700\u597d\u7684\u529e\u6cd5\uff0c\u5c31\u662f\u8ba9\u4ed6\u4eec\u81ea\u76f8\u6b8b\u6740\uff0c\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u9053\uff1a\u201c\u4f46\u770b\u6837\u5b50\uff0c\u8fd9\u6b21\u6765\u7684\u4eba\uff0c\u90fd\u5f88\u7cbe\u660e\uff0c\u8981\u4ed6\u4eec\u81ea\u8e48\u9677\u9631\uff0c\u4e0d\u662f\u4ef6\u5bb9\u6613\u7684\u4e8b\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u73b0\u5728\uff0c\u6700\u91cd\u8981\u7684\u4e00\u6790\u4e8b\uff0c\u662f\u653e\u4f4e\u59ff\u6001\uff0c\u2026\u2026\u201d\u76ee\u5149\u4e00\u63a0\u6c5f\u67ab\uff0c\u63a5\u9053\uff1a\u201c\u4ee5\u67d4\u514b\u521a\uff0c\u4f60\u8981\u72ec\u4efb\u8270\u5de8\uff0c\u5468\u65cb\u4e8e\u4e09\u5927\u52bf\u529b\u4e4b\u95f4\uff0c\u975e\u7edd\u5bf9\u5fc5\u8981\uff0c\u4e0d\u80fd\u5984\u52a8\u6740\u673a\uff0c\u73b0\u5728\uff0c\u6211\u5148\u5e2e\u4f60\u6cbb\u597d\u4f24\u52bf\uff0c\u8ba9\u4f60\u5168\u5934\u5168\u8138\u7684\u5468\u65cb\u4e8e\u4e09\u5927\u52bf\u529b\u4e4b\u95f4\u3002\u201d \u3000\u3000\u6c5f\u67ab\u9759\u9759\u5730\u8eba\u5728\u4e00\u5f20\u6728\u5e8a\u4e0a\uff0c\u5de6\u8033\u4f24\u53e3\uff0c\u65e9\u5df2\u6108\u5408\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u5f20\u56db\u59d1\u53ea\u5728\u4ed6\u5de6\u8033\u4e0a\u5f00\u4e86\u4e00\u4e2a\u8f83\u5927\u7684\u4f24\u53e3\uff0c\u653e\u51fa\u6bcf\u8840\u3002 \u3000\u3000\u5f20\u56db\u59d1\u7cbe\u901a\u533b\u672f\uff0c\u5e94\u8be5\u7559\u4e0b\u6765\u7167\u987e\u6c5f\u67ab\uff0c\u4f46\u4e03\u5b9d\u548c\u5c1a\u548c\u7f57\u5170\uff0c\u7adf\u7136\u4e5f\u5b88\u5728\u75c5\u69bb\u524d\u9762\u3002 \u3000\u3000\u201c\u73b0\u5728\uff0c\u4ed6\u5df2\u7ecf\u5b8c\u5168\u6655\u8ff7\u4e86\u8fc7\u53bb\uff0c\u4e00\u4e2a\u65f6\u8fb0\u4e4b\u5185\uff0c\u7edd\u4e0d\u4f1a\u6e05\u9192\u8fc7\u6765\uff0c\u6709\u4ec0\u4e48\u4e8b\uff1f\u73b0\u5728\u53ef\u4ee5\u653e\u5fc3\u7684\u4ea4\u8c08\u4e86\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\u3002 \u3000\u3000\u201c\u5f20\u59d1\u5a18\uff0c\u8981\u4ed6\u5351\u8a00\u5c48\u819d\uff0c\u662f\u4e0d\u662f\u592a\u59d4\u5c48\u4ed6\u4e86\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u9053\uff1a\u201c\u8fd9\u4e00\u70b9\u8bf4\u670d\u4ed6\u7684\u5e0c\u671b\uff0c\u6050\u6015\u4e0d\u5927\u3002\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\uff0c\u6240\u4ee5\uff0c\u6211\u624d\u8bf7\u4e24\u4f4d\u5e2e\u5fd9\u2026\u2026\u201d\u5f20\u56db\u59d1\u795e\u60c5\u8083\u7136\u5730\u8bf4\uff0c\u201c\u6211\u4eec\u5168\u90fd\u628a\u751f\u6b7b\u7f6e\u4e4b\u5ea6\u5916\uff0c\u8ddf\u7740\u4ed6\u653e\u624b\u4e00\u6218\uff0c\u6210\u529f\u7684\u673a\u4f1a\uff0c\u4e5f\u53ea\u6709\u5341\u4e4b\u4e00\u4e8c\uff0c\u6211\u4eec\u8fd9\u6279\u4eba\uff0c\u6b7b\u4e0d\u8db3\u60dc\uff0c\u4f46\u6b66\u6797\u9053\u5c06\u6ca6\u5165\u9b54\u6559\u7edf\u5236\uff0c\u6c38\u65e0\u7ffb\u8eab\u7684\u673a\u4f1a\u4e86\u3002\u201d \u3000\u3000\u201c\u6211\u4eec\u613f\u5c3d\u5168\u529b\uff0c\u8bf4\u670d\u6c5f\u67ab\uff0c\u5e0c\u671b\u4ed6\u80fd\u914d\u5408\u4f60\u7684\u8ba1\u5212\uff0c\u2026\u2026\u201d \u3000\u3000\u7f57\u5170\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4f46\u6211\u4eec\u4e0d\u80fd\u4e0d\u4f5c\u6700\u574f\u7684\u6253\u7b97\uff0c\u4e00\u65e6\uff0c\u4ed6\u4e0d\u80af\u63a5\u53d7\u4f60\u7684\u5b89\u6392\uff0c\u4f60\u51c6\u5907\u4f5c\u4f55\u6253\u7b97\uff1f\u201d \u3000\u3000\u201c\u5e94\u8be5\u6740\u4e86\u4ed6\uff0c\u81f3\u5c11\uff0c\u4e5f\u8981\u6682\u65f6\u628a\u4ed6\u56da\u7981\u8d77\u6765\u3002\u2026\u2026\u201d \u3000\u3000\u201c\u8c01\u80fd\u53d6\u4ee3\u4ed6\u5462\uff1f\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u6253\u65ad\u4e86\u5f20\u56db\u59d1\u7684\u8bdd\uff0c\u9053\uff1a\u201c\u76ee\u524d\u4e3a\u6b62\uff0c\u6211\u76f8\u4fe1\u6c5f\u67ab\u8fd8\u4e0d\u77e5\u9053\u4f60\u771f\u6b63\u7684\u8eab\u4efd\uff0c\u4f60\u53d7\u547d\u76d1\u89c6\u6307\u5bfc\u4ed6\u7684\u884c\u52a8\uff0c\u4e3a\u4ec0\u4e48\u53c8\u4e0d\u80af\u76f4\u63a5\u544a\u8bc9\u4ed6\u5462\uff1f\u201d \u3000\u3000\u201c\u4ed6\u662f\u767e\u5e74\u4e2d\u96be\u5f97\u4e00\u89c1\u7684\u7ec3\u6b66\u4eba\u6750\uff0c\u4e5f\u662f\u552f\u4e00\u80fd\u5728\u77ed\u77ed\u4e8c\u5341\u5e74\u5185\uff0c\u8eab\u96c6\u4e09\u5927\u5947\u4eba\u7edd\u5b66\u4e8e\u4e00\u8eab\u7684\u5929\u624d\uff0c\u6211\u4eec\u6ca1\u6709\u529e\u6cd5\u6539\u53d8\u8fd9\u4e2a\u4e8b\u5b9e\uff0c\u6240\u4ee5\uff0c\u6211\u4eec\u53ea\u6709\u5c3d\u5168\u529b\u53bb\u7ef4\u62a4\u4ed6\uff0c\u5e2e\u52a9\u4ed6\uff0c\u2026\u2026\u201d \u3000\u3000\u5f20\u56db\u59d1\u9053\uff1a\u201c\u5408\u6211\u4eec\u4e09\u4eba\u4e4b\u529b\uff0c\u6211\u76f8\u4fe1\u6709\u7740\u5f88\u5f3a\u7684\u8bf4\u670d\u529b\u91cf\uff0c\u4e07\u4e00\u8bf4\u4e0d\u670d\uff0c\u4e24\u4f4d\u8bf7\u7acb\u523b\u70b9\u96ea\u3001\u971c\u4e8c\u5973\u7684\u7a74\u9053\uff0c\u5e26\u4ed6\u4eec\u79bb\u5f00\u8fd9\u91cc\u3002\u201d \u3000\u3000\u201c\u4f60\u5462\uff1f\u2026\u2026\u201d\u7f57\u5170\u9053\uff1a\u201c\u7559\u4e0b\u6765\uff1f\u201d \u3000\u3000\u201c\u662f\uff01\u6211\u7559\u4e0b\u6765\u966a\u4ed6\uff0c\u5c3d\u6211\u4e4b\u80fd\uff0c\u5e2e\u52a9\u4ed6\uff0c\u548c\u5bf9\u65b9\u51b3\u4e00\u6b7b\u6218\u3002 \u3000\u3000\u6211\u8981\u8ba9\u4ed6\u77e5\u9053\uff0c\u5728\u9c9c\u8840\u6d41\u5c3d\uff0c\u4ecd\u7136\u65e0\u6539\u53d8\u6c5f\u6e56\u5927\u5c40\u65f6\uff0c\u8fd9\u5c31\u662f\u5339\u592b\u4e4b\u52c7\uff0c\u5339\u592b\u4e4b\u52c7\u4e0d\u53ef\u6043\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u6211\u4f1a\u7528\u751f\u547d\u966a\u7740\u4ed6\uff0c\u8ba9\u4ed6\u77e5\u9053\uff0c\u575a\u6301\u9519\u8bef\u7684\u4ee3\u4ef7\u3002\u201d \u3000\u3000\u201c\u5f20\u963f\u59e8\uff0c\u4f60\u548c\u5988\u4e00\u8d77\u8d70\u5427\uff01\u2026\u2026\u201d\u541f\u96ea\u3001\u541f\u971c\uff0c\u5e76\u80a9\u884c\u4e86\u8fdb\u6765\uff0c\u9053\uff1a\u201c\u6211\u4eec\u6765\u966a\u6c5f\u5927\u54e5\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u884c\uff0c\u4f60\u4eec\u662f\u518d\u4f20\u85aa\u706b\u4e4b\u6e90\uff0c\u4e8c\u5341\u5e74\u540e\u91cd\u5149\u6c5f\u6e56\u7684\u5e0c\u671b\u6240\u7cfb\uff0c\u7edd\u4e0d\u80fd\u7559\u4e0b\u6765\uff0c\u2026\u2026\u201d \u3000\u3000\u5f20\u56db\u59d1\u9053\uff1a\u201c\u4ee4\u5c0a\uff0c\u5df2\u51b3\u5b9a\u653e\u624b\u4e00\u6218\uff0c\u4e0d\u6b7b\u4e0d\u4f11\uff0c\u4ee4\u5802\u4ea6\u8981\u820d\u8eab\u51b3\u6218\uff0c\u627e\u82b1\u4e86\u65e0\u6570\u53e3\u820c\uff0c\u624d\u628a\u4ee4\u5802\u8bf4\u670d\uff0c\u8981\u5979\u5e26\u7740\u4f60\u4eec\u79bb\u5f00\uff0c\u5509\uff01\u4f60\u4eec\u8fd9\u4e00\u6405\u5c40\u3002\u2026\u2026\u201d \u3000\u3000\u201c\u5f20\u59e8\uff0c\u6211\u4eec\u5982\u82e5\u548c\u6c5f\u5927\u54e5\u8054\u624b\u5bf9\u654c\uff0c\u6709\u6ca1\u6709\u7a81\u7834\u91cd\u56f4\u7684\u673a\u4f1a\u2026\u2026\u201d\u541f\u96ea\u9053\uff1a\u201c\u4f55\u51b5\uff0c\u6211\u4eec\u8fd8\u6709\u8bf4\u670d\u6c5f\u5927\u54e5\u7684\u673a\u4f1a\uff0c\u4e5f\u8bb8\u4ed6\u4f1a\u770b\u5728\u5f20\u59e8\u7684\u4efd\u4e0a\uff0c\u7b54\u5e94\u4f60\u4eec\u7684\u5b89\u6392\u3002\u201d \u3000\u3000\u201c\u5f20\u59e8\uff0c\u6c5f\u5927\u54e5\u6700\u542c\u4f60\u7684\u8bdd\u4e86\uff0c\u53ea\u8981\u4f60\u80af\u5f00\u53e3\u6c42\u4ed6\uff0c\u6211\u76f8\u4fe1\u4ed6\u4f1a\u7b54\u5e94\uff0c\u2026\u2026\u201d\u541f\u971c\u5fae\u7b11\u5982\u82b1\u5730\u8bf4\uff0c\u201c\u5982\u82e5\u6c5f\u5927\u54e5\u8fd8\u662f\u62d2\u7edd\u4e86\u4f60\uff0c\u4f60\u8fd8\u6709\u6700\u597d\u4e00\u4e2a\u6cd5\u5b9d\u53ef\u7528\uff0c\u6211\u4fdd\u8bc1\u6c5f\u5927\u54e5\u62db\u67b6\u4e0d\u4f4f\u3002\u201d \u3000\u3000\u201c\u90a3\u662f\u662f\u4ec0\u4e48\u6cd5\u5b9d\uff1f\u2026\u2026\u201d \u3000\u3000\u5f20\u56db\u59d1\u7adf\u7136\u88ab\u53f6\u971c\u8bf4\u7684\u6123\u4f4f\u4e86\u3002 \u3000\u3000\u201c\u54ed\uff01\u5f20\u59e8\u53ea\u8981\u80af\u63a9\u9762\u4f4e\u6ce3\uff0c\u54ed\u4e2a\u4f24\u5fc3\u6b32\u7edd\uff0c\u6211\u76f8\u4fe1\u6c5f\u5927\u54e5\u4f1a\u7b54\u5e94\u4f60\u4efb\u4f55\u8bf7\u6c42\u3002\u201d \u3000\u3000\u8fd9\u8be5\u662f\u4e00\u4ef6\u5f88\u5929\u771f\uff0c\u53ef\u7b11\u7684\u4e8b\uff0c\u4f46\u5f20\u56db\u59d1\u7adf\u7136\u542c\u5f97\u795e\u60c5\u8083\u7136\uff0c\u9053\uff1a\u201c\u4e3a\u4e86\u6c5f\u67ab\u4e00\u6761\u547d\uff0c\u4e3a\u4e86\u6c5f\u6e56\u5927\u5c40\uff0c\u6211\u53ef\u4ee5\u54ed\u4e00\u573a\uff0c\u4f46\u4f60\u4eec\u8981\u548c\u5f20\u59e8\u914d\u5408\uff0c\u4e0d\u8981\u7ad9\u5728\u4e00\u8fb9\u770b\u7b11\u8bdd\u3002\u201d \u3000\u3000\u541f\u96ea\u9053\uff1a\u201c\u5f20\u59e8\u53ef\u8981\u6211\u4eec\u966a\u7740\u54ed\u5417\uff1f\u201d \u3000\u3000\u201c\u90a3\u5012\u4e0d\u5fc5\uff0c\u4f46\u6211\u8981\u8bf4\u660e\u7684\u4e00\u4ef6\u4e8b\u662f\uff0c\u6c5f\u67ab\u5982\u4e0d\u80af\u59d4\u5c48\u6c42\u5168\uff0c\u6d1b\u9633\u5c45\u8fd9\u4e00\u573a\u8840\u6218\uff0c\u6c5f\u67ab\u8fde\u4e00\u5206\u751f\u5b58\u7684\u673a\u4f1a\uff0c\u4e5f\u6ca1\u6709\u2026\u2026\u201d \u3000\u3000\u541f\u971c\u9053\uff1a\u201c\u5f20\u59e8\uff0c\u80fd\u4e0d\u80fd\u544a\u8bc9\u6211\u4eec\uff0c\u6c5f\u5927\u54e5\u73b0\u5728\u9762\u5bf9\u7684\u654c\u4eba\uff0c\u90fd\u662f\u4e9b\u4ec0\u4e48\u4eba\u7269\uff1f\u6211\u548c\u59d0\u59d0\u5168\u529b\u65bd\u5c55\uff0c\u80fd\u4e0d\u80fd\u6321\u4f4f\u4ed6\u4eec\u4e00\u70b9\u5b9e\u529b\u3002\u201d \u3000\u3000\u201c\u4f60\u4eec\u90fd\u662f\u5929\u624d\uff0c\u4f60\u4eec\u4e5f\u5b66\u5f97\u4e86\u5929\u72d0\u6b66\u529f\u5341\u4e4b\u4e03\u516b\uff0c\u518d\u7ed9\u4f60\u4eec\u4e09\u4e94\u5e74\u65f6\u95f4\uff0c\u5f88\u53ef\u80fd\u4e0d\u8ba9\u5929\u72d0\u592b\u4eba\u4e13\u7f8e\u4e8e\u524d\uff0c\u4e0d\u8fc7\uff0c\u654c\u4eba\u5b9e\u529b\u4e4b\u5f3a\uff0c\u4e5f\u51fa\u4e86\u6211\u4eec\u7684\u610f\u5916\uff0c\u201d\u5f20\u56db\u59d1\u8bf4\uff0c\u201c\u6700\u91cd\u8981\u7684\u662f\u654c\u65b9\u7684\u4e09\u5927\u52bf\u529b\u4e2d\u7684\u91cd\u8981\u4eba\u7269\uff0c\u4e0d\u7ea6\u800c\u540c\u7684\u90fd\u5230\u4e86\u957f\u5b89\uff0c\u5982\u82e5\u4ed6\u4eec\u4e09\u65b9\u5408\u4f5c\uff0c\u6c5f\u67ab\u5c31\u5168\u65e0\u80dc\u7b97\uff0c\u4f60\u4eec\u4e24\u4e2a\u52a0\u4e0a\u53bb\uff0c\u9876\u591a\u80fd\u591a\u6740\u4e86\u51e0\u4e2a\u654c\u4eba\u800c\u5df2\uff01\u201d \u3000\u3000\u201c\u6700\u91cd\u8981\u7684\u662f\uff0c\u6c5f\u5c11\u4fa0\u6770\u51fa\u7684\u8868\u73b0\uff0c\u4f1a\u5f15\u8d77\u654c\u4eba\u7684\u9707\u52a8\u56e2\u7ed3\uff0c\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u63a5\u9053\uff1a \u3000\u3000\u201c\u8fd9\u4e09\u80a1\u529b\u91cf\u7ed3\u5408\u8d77\u6765\uff0c\u5df2\u7ecf\u7edf\u6cbb\u4e86\u6c5f\u6e56\uff0c\u5982\u679c\u4ed6\u4eec\u518d\u5ea6\u7ed3\u5408\u8d77\u6765\uff0c\u6c5f\u67ab\u9886\u5bfc\u6211\u4eec\u8fd9\u4e00\u6279\u529b\u91cf\uff0c\u7edd\u5bf9\u65e0\u6cd5\u5bf9\u6297\u2026\u2026\u201d \u3000\u3000\u201c\u5927\u5e08\u7236\u2026\u2026\u201d\u541f\u971c\u6c40\u65ad\u4e86\u4e03\u5b9d\u548c\u5c1a\u7684\u8bdd\uff0c\u9053\uff1a\u201c\u73b0\u5728\uff0c\u5bf9\u65b9\u53ea\u6765\u4e86\u51e0\u4e2a\u4eba\uff0c\u6211\u4eec\u5c31\u7f69\u4e0d\u4f4f\u4e86\uff0c\u2026\u2026\u201d \u3000\u3000\u201c\u8fd9\u4e2d\u95f4\u53ef\u80fd\u662f\u6709\u4e9b\u8bef\u4f1a\uff0c\u5f20\u59d1\u5a18\u4f4e\u4f30\u4e86\u6c5f\u5c11\u4fa0\u7684\u529b\u91cf\uff0c\u2026\u2026\u201d\u516d\u5b9d\u548c\u5c1a\u9053\uff1a\u201c\u5982\u82e5\u5351\u6253\u72ec\u6597\uff0c\u653e\u773c\u5929\u4e0b\uff0c\u6ca1\u6709\u4eba\u80fd\u662f\u6c5f\u67ab\u7684\u654c\u624b\u3002\u201d \u3000\u3000\u201c\u660e\u67aa\u6613\u8eb2\uff0c\u6697\u7bad\u96be\u9632\uff0c\u4ed6\u53ef\u4ee5\u72ec\u6321\u5929\u738b\uff0c\u4f46\u5982\u52a0\u4e00\u4e2a\u4e09\u5bab\u4e3b\uff0c\u5c31\u672a\u5fc5\u80fd\u591f\u5e94\u4ed8\u5f97\u4e86\uff1f\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u4f55\u51b5\uff0c\u5929\u9a6c\u5802\u8fd8\u6765\u4e86\u4e24\u4f4d\u526f\u603b\u5802\u4e3b\uff01\u201d \u3000\u3000\u201c\u4e24\u4e2a\u65f6\u8fb0\u4e4b\u5185\uff0c\u5c3d\u96c6\u5929\u4e0b\u9ad8\u624b\u4e8e\u6b64\uff0c\u4e5f\u672a\u5fc5\u80fd\u6740\u4e86\u6c5f\u67ab\uff0c\u4f46\u5982\u8ba9\u4ed6\u82e6\u6218\u4e24\u4e2a\u65f6\u8fb0\u4e4b\u540e\uff0c\u8fd8\u6709\u591a\u5c11\u4f59\u529b\u53ef\u7528\uff0c\u6211\u548c\u5c1a\u5c31\u4e0d\u6562\u65ad\u8a00\u4e86\uff0c\u2026\u2026\u201d \u3000\u3000\u76ee\u5149\u4e00\u63a0\u541f\u96ea\u3001\u541f\u971c\uff0c\u63a5\u8c28\uff0c\u201c\u5982\u6709\u4e24\u4f4d\u5c0f\u7f8e\u5973\u4ece\u65c1\u534f\u52a9\uff0c\u90a3\u5c31\u5982\u864e\u751f\u53cc\u7ffc\uff0c\u7ed9\u6c5f\u67ab\u4e00\u70b9\u5598\u606f\u65f6\u95f4\uff0c\u4ed6\u5c31\u53ef\u4ee5\u6491\u4e0a\u4e09\u4e94\u4e2a\u65f6\u8fb0\u4e86\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u516d\u4e2a\u6740\u624b\u627e\u4e0a\u95e8\uff0c\u6211\u4eec\u662f\u767d\u767d\u66ff\u4ed6\u62c5\u5fc3\u4e86\uff0c\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u8fd9\u53ef\u662f\u4ed6\u81ea\u5df1\u5236\u9020\u7d27\u5f20\u554a\uff01\u201d \u3000\u3000\u201c\u8fd9\u4e2a\uff0c\u6211\u548c\u5c1a\u5c31\u641e\u4e0d\u6e05\u695a\u4e86\uff0c\u597d\u50cf\u6709\u4e00\u79cd\u53eb\u4f5c\u7b56\u7565\u8fd0\u7528\u2026\u2026\u201d \u3000\u3000\u201c\u54fc\uff01\u4ed6\u5728\u800d\u6211\u4eec\uff0c\u662f\u561b\uff1f\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u662f\uff01\u5927\u59d0\uff0c\u6211\u6ca1\u6709\u548c\u5c1a\u8bf4\u7684\u90a3\u4e48\u597d\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u633a\u8eab\u5750\u4e86\u8d77\u6765\u53c8\u9053\uff1a\u201c\u5426\u5219\u4e5f\u4e0d\u4f1a\u8eab\u53d7\u6697\u7b97\u4e86\uff0c\u4f46\u4e5f\u4e0d\u662f\u5927\u59d0\u60f3\u7684\u90a3\u4e48\u574f\u3002\u2026\u2026\u201d \u3000\u3000\u201c\u597d\u554a\uff01\u4f60\u6839\u672c\u5c31\u6ca1\u6709\u88ab\u6211\u7684\u836f\u8ff7\u6655\u8fc7\u53bb\uff0c\u6211\u4eec\u8bf4\u7684\u8bdd\uff0c\u4f60\u5168\u542c\u5230\u4e86\u3002\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u6211\u6ca1\u6709\u6655\u8ff7\u8fc7\u53bb\uff0c\u4f46\u5927\u59d0\u7684\u836f\u7269\u5f88\u5389\u5bb3\uff0c\u6211\u8fd0\u8db3\u5168\u8eab\u529f\u529b\u6297\u62d2\uff0c\u624d\u628a\u836f\u529b\u62fc\u8bf8\u8eab\u5916\u3002\u201d \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u6211\u5982\u6ca1\u6709\u8bf8\u4eec\u534f\u52a9\uff0c\u6b66\u529f\u518d\u5f3a\u4e00\u500d\uff0c\u4e5f\u662f\u6ca1\u6709\u7528\u5904\uff0c\u6c5f\u6e56\u4e0a\u6709\u4e9b\u4e8b\uff0c\u4e0d\u662f\u7528\u6b66\u529f\u80fd\u591f\u89e3\u51b3\uff0c\u8bf8\u4f4d\u90fd\u662f\u4e09\u4f4d\u5e08\u957f\uff0c\u5b89\u6392\u5e2e\u52a9\u6211\u7684\u4eba\uff0c\u4eca\u540e\u501f\u91cd\u4e4b\u5904\u8fc7\u591a\uff0c\u5e0c\u671b\u6211\u4eec\u5168\u529b\u5408\u4f5c\uff0c\u80fd\u5c3d\u5feb\u7684\u6062\u590d\u4e86\u6c5f\u6e56\u4e0a\u6e05\u5e73\u5c81\u6708\u3002\u201d \u3000\u3000\u201c\u4e16\u4e0a\u771f\u6709\u4f60\u8fd9\u4e48\u4e00\u4e2a\u4eba\uff1f\u4e5f\u88ab\u4e09\u4f4d\u8001\u4eba\u5bb6\u627e\u5230\u4e86\u2026\u2026\u201d\u5f20\u56db\u59d1\u7b11\u4e00\u7b11\u53c8\u9053\uff1a\u201c\u770b\u6765\uff0c\u771f\u662f\u795e\u4f5b\u6709\u7075\uff0c\u6b66\u6797\u6709\u6551\u3002\u201d \u3000\u3000\u201c\u662f\u4e09\u4f4d\u8001\u4eba\u5bb6\u7684\u683d\u57f9\uff0c\u5929\u77e5\u9053\u4ed6\u4eec\u82b1\u4e86\u591a\u5c11\u5fc3\u8840\uff0c\u5305\u62ec\u8bf8\u4f4d\u5728\u5185\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a \u3000\u3000\u201c\u4e8c\u5341\u5e74\u524d\uff0c\u4ed6\u4eec\u5df2\u5728\u8bf8\u4f4d\u8eab\u4e0a\u7528\u529f\u592b\u4e86\uff01\u201d \u3000\u3000\u201c\u867d\u7136\u6ca1\u4eba\u544a\u8bc9\u6211\uff0c\u4f46\u7f57\u5170\u592b\u5987\u7684\u884c\u5f84\uff0c\u6211\u786e\u5b9e\u77a7\u51fa\u4e86\u4e00\u70b9\u8ff9\u8c61\uff0c\u552f\u4e00\u6ca1\u6709\u60f3\u5230\u7684\u662f\uff0c\u5979\u5f97\u4e86\u5929\u72d0\u592b\u4eba\u7684\u5168\u90e8\u771f\u4f20\uff0c\u9020\u5c31\u51fa\u4e86\u4e24\u4e2a\u4e00\u4ee3\u5929\u5a07\u7684\u59d0\u59b9\u82b1\uff0c\u8fd9\u4e9b\u65f6\u65e5\uff0c\u6211\u548c\u96ea\u3001\u971c\u4e8c\u5973\u76f8\u5904\u4e00\u8d77\uff0c\u624d\u53d1\u89c9\u5979\u4eec\u6280\u827a\u7684\u7cbe\u6e5b\uff0c\u65e9\u5df2\u975e\u6211\u80fd\u53ca\uff0c\u2026\u2026\u201d \u3000\u3000\u5f20\u56db\u59d1\u76ee\u5149\u8f6c\u5230\u4e03\u5b9d\u548c\u5c1a\u7684\u8eab\u4e0a\uff0c\u63a5\u9053\uff1a\u201c\u9152\u8089\u548c\u5c1a\u4e5f\u662f\u4e09\u4f4d\u8001\u4eba\u5bb6\u6446\u4e0b\u7684\u4e00\u9897\u68cb\u5b50\uff0c\u5012\u662f\u5b8c\u5168\u51fa\u4e86\u6211\u7684\u610f\u5916\u3002\u201d \u3000\u3000\u201c\u548c\u5c1a\u53ea\u80fd\u7b97\u534a\u4e2a\u95e8\u4eba\uff0c\u6211\u662f\u673a\u7f18\u5de7\u5408\uff0c\u649e\u4e0a\u4e86\u8fd9\u4e2a\u673a\u4f1a\u2026\u2026\u201d \u3000\u3000\u201c\u5927\u5e08\uff0c\u4e0d\u7528\u8c26\u865a\u4e86\uff0c\u4f60\u82b1\u7684\u5de5\u592b\u6700\u6df1\uff0c\u6210\u5c31\u7edd\u4e0d\u5728\u6c5f\u67d0\u4e4b\u4e0b\uff0c\u6c5f\u67ab\u4f55\u5176\u5e78\u8fd0\uff0c\u80fd\u5f97\u5230\u8bf8\u4f4d\u7684\u52a9\u529b\u3002\u201d \u3000\u3000\u201c\u6c5f\u5927\u54e5\uff0c\u6211\u4eec\u4eca\u5929\u9732\u7684\u4e00\u624b\uff0c\u80fd\u4e0d\u80fd\u770b\u5f97\u8fc7\u53bb\u2026\u2026\u201d\u541f\u96ea\u9053\uff1a\u201c\u90a3\u4e00\u8f66\u5783\u573e\uff0c\u597d\u81ed\u3001\u597d\u81ed\uff0c\u6211\u6574\u4e2a\u4eba\u6eb6\u5728\u4e00\u5806\u5783\u573e\u4e2d\uff0c\u867d\u7136\u95ed\u4f4f\u4e86\u547c\u5438\uff0c\u4f46\u4ecd\u7136\u82e6\u4e0d\u582a\u8a00\uff01\u201d \u3000\u3000\u201c\u597d\u6781\u4e86\uff0c\u6c5f\u5927\u54e5\u4f69\u670d\u5f97\u5f88\u2026\u2026\u201d \u3000\u3000\u201c\u5f20\u59e8\u7684\u4e94\u884c\u6f5c\u672f\uff0c\u6613\u61c2\u96be\u7ec3\uff0c\u2026\u2026\u201d\u541f\u971c\u9053\uff1a\u201c\u6211\u6f5c\u9677\u5728\u90a3\u68f5\u6811\u8eab\u4e4b\u4e0a\uff0c\u5316\u53bb\u4e86\u4e0d\u5c11\u7684\u6c14\u529b\uff0c\u51e0\u4e4e\u6d88\u53bb\u4e86\u653b\u51fb\u654c\u4eba\u8b66\u89c9\uff01\u201d \u3000\u3000\u201c\u975e\u5e38\u597d\u4e86\uff0c\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u5f20\u59e8\u6765\u505a\uff0c\u4e5f\u4e0d\u8fc7\u5982\u6b64\u7f62\u4e86\uff0c\u770b\u5230\u4f60\u4eec\u7684\u5b66\u4e60\u6f5c\u80fd\uff0c\u5f20\u59e8\u770b\u5230\u4e86\u6b66\u6797\u4e2d\u7684\u5e0c\u671b\u3002\u201d \u3000\u3000\u76ee\u5149\u8f6c\u5230\u6c5f\u67ab\u7684\u8138\u4e0a\uff0c\u63a5\u9053\uff1a\u201c\u8bf4\u5427\uff01\u4f60\u51c6\u5907\u5982\u4f55\u5e94\u4ed8\u76ee\u524d\u7684\u5f62\u52bf\u3002\u201d \u3000\u3000\u201c\u5927\u59d0\u64a4\u8d70\u7684\u529e\u6cd5\uff0c\u6211\u4e0d\u8d5e\u6210\uff0c\u4f46\u6211\u4eec\u8981\u5c3d\u91cf\u907f\u514d\u548c\u4ed6\u4eec\u6b63\u9762\u51b2\u7a81\uff0c\u6700\u9ad8\u660e\u7684\u624b\u6bb5\u662f\u6311\u8d77\u4ed6\u4eec\u7684\u51b2\u7a81\u3002\u201d \u3000\u3000\u201c\u8bf4\u6765\u5bb9\u6613\uff0c\u505a\u7740\u96be\uff0c\u2026\u2026\u201d\u7f57\u5170\u63a5\u4e86\u53e3\uff0c\u9053\uff1a\u201c\u5929\u9a6c\u603b\u5802\u6765\u4e86\u4e24\u4f4d\u526f\u603b\u5802\u4e3b\uff0c\u5185\u5bab\u4e00\u7cfb\u4e2d\u6765\u4e86\u4e09\u5bab\u4e3b\uff0c\u4e00\u660e\u4e00\u6697\u7684\u8d76\u5230\uff0c\u5982\u4f55\u624d\u80fd\u6311\u8d77\u4ed6\u4eec\u7684\u7eb7\u4e89\u5462\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u5148\u529d\u4f4f\u6b27\u9633\u5144\uff0c\u4e0d\u53ef\u71e5\u8fdb\uff0c\u628a\u529b\u91cf\u96c6\u4e2d\u5728\u56db\u6d77\u91cf\u884c\uff0c\u6211\u5148\u548c\u5929\u9a6c\u5802\u4e24\u4f4d\u526f\u603b\u5802\u4e3b\u89c1\u9762\uff0c\u5982\u679c\u4e00\u5207\u987a\u5229\uff0c\u7531\u4ed6\u4eec\u5411\u5185\u5bab\u4e2d\u4eba\u4e0b\u624b\u3002\u201d \u3000\u3000\u201c\u4e0d\u987a\u5229\u5462\uff1f\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u662f\u4e0d\u662f\u8981\u5b64\u6ce8\u4e00\u63b7\uff0c\u5168\u529b\u51fa\u624b\uff1f\u201d \u3000\u3000\u201c\u6211\u4e0d\u4f1a\u901e\u4e00\u65f6\u8c6a\u52c7\uff0c\u6709\u8d1f\u4e09\u4f4d\u8001\u4eba\u5bb6\u7684\u4ed8\u6258\uff0c\u6211\u4f1a\u89c1\u673a\u800c\u4f5c\uff0c\u5982\u80fd\u4e00\u4e3e\u523a\u6740\u4e00\u4f4d\u5929\u9a6c\u5802\u526f\u603b\u5802\u4e3b\uff0c\u5ac1\u7978\u7ed9\u5185\u5bab\u4e2d\u4eba\uff0c\u90a3\u624d\u662f\u4e0a\u4e0a\u4e4b\u7b56\uff0c\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u4e0d\u7ba1\u662f\u5426\u5f97\u624b\uff0c\u5982\u662f\u6d1b\u9633\u5c45\u65e0\u6cd5\u5b58\u8eab\uff0c\u5c0f\u5f1f\u4f1a\u8d76\u56de\u56db\u6d77\u91cf\u884c\uff0c\u4f1a\u5408\u5927\u59d0\uff0c\u79bb\u5f00\u8fd9\u91cc\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u70b9\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u5e0c\u671b\u4f60\u8a00\u884c\u5982\u4e00\uff0c\u6211\u4eec\u606d\u5019\u6d88\u606f\uff0c\u7f57\u5170\uff0c\u6211\u4eec\u4f1a\u963b\u6b62\u6b27\u9633\u5144\u3002\u201d \u3000\u3000\u62c9\u8d77\u7f57\u5170\uff0c\u5411\u5916\u884c\u53bb\uff0c\u5bf9\u96ea\u3001\u971c\u4e8c\u5973\u7684\u4e8b\uff0c\u5df2\u4e0d\u518d\u591a\u7ba1\u3002 \u3000\u3000\u7f57\u5170\u671b\u4e86\u4e00\u773c\uff0c\u6b32\u8a00\u53c8\u6b62\u3002 \u3000 \u7b2c\u5341\u56db\u56de\u3000\u7fe0\u7389\u5200\u52c7\u51fb\u516b\u5251\u58eb \u3000 \u3000\u3000\u201c\u6c5f\u5927\u54e5\uff0c\u5a18\u548c\u5f20\u59e8\u5df2\u7ecf\u627f\u8ba4\u6211\u4eec\u957f\u5927\u4e86\uff0c\u4ece\u4eca\u4e4b\u540e\uff0c\u6211\u4eec\u7684\u4e8b\uff0c\u7531\u6211\u4eec\u81ea\u5df1\u4f5c\u4e3b\u4e86\uff0c\u6c5f\u5927\u54e5\u6709\u4ec0\u4e48\u5429\u5490\uff0c\u53ef\u4ee5\u76f4\u63a5\u7684\u4e0b\u4ee4\u6211\u4eec\u4e86\uff0c\u2026\u2026\u201d\u53f6\u96ea\u8bf4\u7684\u795e\u60c5\u4e25\u8083\uff0c\u4e00\u6539\u5f80\u65e5\u7684\u5c11\u5973\u795e\u60c5\u3002 \u3000\u3000\u201c\u6211\u77e5\u9053\uff0c\u7ecf\u8fc7\u8fd9\u4e00\u6b21\u8003\u9a8c\uff0c\u6c5f\u5927\u54e5\u4e5f\u611f\u89c9\u5230\u4f60\u4eec\u53ef\u4ee5\u72ec\u5f53\u4e00\u9762\u4e86\u3002\u201d \u3000\u3000\u201c\u591a\u8c22\u6c5f\u5927\u54e5\uff0c\u4f60\u4e5f\u627f\u8ba4\u6211\u4eec\u957f\u5927\u4e86\uff0c\u2026\u2026\u201d\u541f\u971c\u9053\uff1a\u201c\u5f20\u59e8\u8ba9\u6211\u8f6c\u544a\u4f60\u4e00\u4ef6\u4e8b\uff0c\u51e4\u59d0\u7edd\u5bf9\u5b89\u5168\uff0c\u53eb\u4f60\u522b\u803d\u5fc3\u7eff\u4e91\u538b\u9876\u3002\u201d \u3000\u3000\u6c5f\u67ab\u7684\u8138\u4e0a\uff0c\u7adf\u7136\u4e5f\u98de\u8d77\u4e86\u4e00\u7247\u7f9e\u7ea2\uff0c\u4f46\u53c8\u5f97\u786c\u7740\u5934\u76ae\uff0c\u8bf4\uff0c\u201c\u4f60\u4eec\u90fd\u77e5\u9053\u4e86\u3002\u2026\u2026\u201d \u3000\u3000\u541f\u971c\u63a5\u9053\uff1a\u201c\u8fd8\u6709\u6885\u82b1\u59d1\u5a18\u7684\u4e8b\u3002\u4e3a\u4e86\u884c\u4e8b\u65b9\u4fbf\uff0c\u5f20\u59e8\u5168\u90fd\u8bf4\u7ed9\u6211\u4eec\u542c\u4e86\uff0c\u5982\u80fd\u5f97\u5979\u4eec\u5168\u529b\u5e2e\u52a9\uff0c\u6211\u60f3\uff0c\u5bf9\u4f60\u7684\u5e2e\u52a9\u5f88\u5927\u3002\u201d \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u4e24\u4f4d\u6709\u4f55\u9ad8\u89c1\uff1f\u201d \u3000\u3000\u201c\u628a\u4f60\u7684\u8ba1\u5212\u544a\u8bc9\u6211\u4eec\uff0c\u6211\u4eec\u5c31\u5168\u529b\u548c\u4f60\u914d\u5408\u2026\u2026\u201d\u541f\u96ea\u9053\uff1a\u201c\u72d0\u8ba1\u591a\u7aef\uff0c\u6c5f\u5927\u54e5\u5e94\u8be5\u660e\u767d\uff0c\u522b\u4e3a\u6211\u4eec\u62c5\u5fc3\uff0c\u6211\u4eec\u957f\u5927\u4e86\uff0c\u5c31\u8be5\u4f5c\u4e9b\u5927\u4eba\u4f5c\u7684\u4e8b\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u70b9\u70b9\u5934\uff0c\u8bf4\u51fa\u4e86\u4e00\u756a\u8ba1\u5212\u3002 \u3000\u3000\u201c\u679c\u7136\u662f\u53c8\u597d\u53c8\u6bd2\u7684\u597d\u8ba1\u5212\u2026\u2026\u201d\u541f\u96ea\u7b11\u9053\uff1a\u201c\u5f20\u59e8\u767d\u767d\u66ff\u4f60\u62c5\u5fc3\u4e86\uff0c\u5979\u867d\u7136\u7cbe\u4e8e\u7528\u5175\u3001\u5e03\u7f72\uff0c\u4f46\u5374\u5c11\u4e86\u90a3\u4e48\u4e00\u70b9\u72d0\u6c14\uff0c\u5bf9\u4ed8\u5929\u9a6c\u5802\u8fd9\u73ed\u90aa\u9b54\u5916\u9053\u4e2d\u4eba\uff0c\u8fd8\u662f\u5929\u72d0\u95e8\u7684\u65b9\u6cd5\u7ba1\u7528\u3002\u201d \u3000\u3000\u541f\u971c\u9053\uff1a\u201c\u6211\u4eec\u5168\u529b\u914d\u5408\uff0c\u81f3\u5c11\u53ef\u4ee5\u4f7f\u4f60\u7684\u8ba1\u5212\u6210\u529f\u4e00\u534a\uff0c\u4e0d\u8fc7\uff0c\u6211\u4eec\u8981\u53bb\u89c1\u89c1\u9752\u51e4\u548c\u6885\u82b1\uff0c\u6c5f\u5927\u54e5\u4e0d\u53cd\u5bf9\u5427\uff01\u201d \u3000\u3000\u201c\u770b\u8d77\u6765\uff0c\u6211\u53cd\u5bf9\u4e5f\u6ca1\u6709\u7528\uff0c\u5929\u72d0\u767e\u53d8\u7684\u5fc3\u6cd5\uff0c\u4f60\u4eec\u4f3c\u4e4e\u662f\u6bd4\u6211\u66f4\u6709\u5fc3\u5f97\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u80fd\u8bf4\u52a8\u5979\u4eec\u914d\u5408\uff0c\u4f60\u4eec\u5c31\u5168\u529b\u65bd\u5c55\u5427\uff01\u201d \u3000\u3000\u541f\u96ea\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6c5f\u5927\u54e5\u4f1a\u6162\u6162\u53d1\u73b0\uff0c\u6211\u4eec\u4e0d\u4f1a\u8ba9\u4f60\u5931\u671b\uff01\u201d \u3000\u3000\u7275\u7740\u541f\u971c\u4e00\u53ea\u624b\uff0c\u53cc\u53cc\u79bb\u53bb\u3002 \u3000\u3000\u671b\u7740\u4e8c\u5973\u8fdc\u53bb\u7684\u80cc\u5f71\uff0c\u4e03\u5b9d\u548c\u5c1a\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u662f\u4e24\u4e2a\u597d\u5e2e\u624b\uff0c\u6211\u770b\u5979\u65e9\u5df2\u80f8\u6709\u6210\u7af9\u4e86\u3002\u201d \u3000\u3000\u201c\u8fc7\u53bb\uff0c\u662f\u6709\u4e9b\u4f4e\u4f30\u5979\u4eec\u3002\u2026\u2026\u201d \u3000\u3000\u516d\u5b9d\u548c\u5c1a\u6253\u65ad\u4e86\u6c5f\u67ab\u7684\u8bdd\uff0c\u63a5\u9053\uff1a\u201c\u53ea\u4e00\u6b21\u8003\u9a8c\uff0c\u5c31\u786e\u5b9a\u4e86\u5979\u4eec\u7684\u6210\u5c31\uff0c\u4ed8\u4e8e\u5927\u4efb\uff0c\u4f60\u662f\u4ece\u54ea\u91cc\u770b\u51fa\u6765\u7684\uff1f\u201d \u3000\u3000\u662f\u95ee\u9898\uff0c\u4e5f\u662f\u8bf7\u6559\uff1f \u3000\u3000\u201c\u4ee5\u541f\u96ea\u4e4b\u7f8e\uff0c\u7adf\u7136\u6f5c\u9690\u4e8e\u5783\u573e\u4e4b\u4e2d\uff0c\u8fd9\u9700\u8981\u591a\u5927\u7684\u5fcd\u6027\uff0c\u541f\u971c\u7684\u6f5c\u6728\u672f\uff0c\u5df2\u5f97\u56db\u59d1\u7684\u795e\u9ad3\uff0c\u5979\u53ea\u4e0d\u8fc7\u7ec3\u4e86\u4e24\u4e2a\u6708\uff0c\u8fd9\u8bc1\u660e\u4e86\u5979\u4eec\u7684\u5929\u5206\u8fc7\u4eba\u4e4b\u5916\uff0c\u53c8\u80af\u8ba4\u771f\u7684\u82e6\u5b66\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u4f46\u6700\u91cd\u8981\u7684\u8fd8\u662f\u5979\u4eec\u5251\u672f\u4e0a\u7684\u6210\u5c31\uff0c\u4e00\u51fb\u53d6\u547d\uff0c\u6b63\u662f\u5929\u72d0\u5251\u6cd5\u7684\u7cbe\u8981\u3002\u201d \u3000\u3000\u201c\u8fd9\u771f\u8981\u606d\u559c\u6c5f\u5c11\u5144\u4e86\uff0c\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u9053\uff1a\u201c\u8fd9\u4e9b\u52a9\u624b\uff0c\u5927\u4e8b\u5b9a\u77e3\uff01\u201d \u3000\u3000\u201c\u770b\u770b\u5979\u4eec\u7684\u8868\u73b0\u5427\uff01\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u6211\u5e0c\u671b\u8fd9\u4e00\u6b21\u5f15\u8d77\u7684\u706b\u62fc\uff0c\u6709\u5982\u71ce\u539f\u7684\u661f\u706b\uff0c\u6269\u5c55\u53bb\uff0c\u8ba9\u4ed6\u4eec\u6b32\u7f62\u4e0d\u80fd\u2026\u2026\u201d \u3000\u3000\u4f46\u95fb\u80e1\u5143\u7684\u58f0\u97f3\uff0c\u4f20\u4e86\u8fdb\u6765\uff0c\u9053\uff1a\u201c\u5201\u9e4f\u6c42\u89c1\u3002\u201d \u3000\u3000\u201c\u8bf7\u4ed6\u8fdb\u6765\u3002\u201d \u3000\u3000\u5201\u9e4f\u4e00\u8138\u4e25\u8083\u7684\u795e\u60c5\uff0c\u9053\uff1a\u201c\u4e24\u4f4d\u526f\u603b\u5802\uff0c\u5df2\u9a7b\u8fdb\u4e86\u8fce\u5bbe\u9601\u4e2d\uff0c\u8bf7\u603b\u7ba1\u548c\u526f\u603b\u7ba1\u7acb\u523b\u664b\u89c1\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u4f60\u89c1\u8fc7\u4e24\u4f4d\u526f\u603b\u5802\u4e3b\u4e86\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u201c\u9664\u4e86\u5929\u865a\u5b50\u4e4b\u5916\uff0c\u53e6\u4e00\u4f4d\u662f\u5341\u51e4\u9f50\u98de\u7533\u96ea\u541b\uff0c\u771f\u662f\u5927\u5927\u7684\u51fa\u4eba\u610f\u5916\u2026\u2026\u201d\u5201\u9e4f\u606d\u606d\u656c\u656c\u7684\u56de\u7b54\u8bf4\u3002 \u3000\u3000\u201c\u5341\u51e4\u9f50\u98de\u7533\u541b\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u6709\u4e9b\u5403\u60ca\u5730\u8bf4\uff0c\u201c\u6709\u6570\u5341\u5e74\u672a\u5728\u6c5f\u6e56\u4e0a\u8d70\u52a8\u4e86\u3002\u201d \u3000\u3000\u201c\u662f\uff01\u6700\u5947\u602a\u7684\u662f\uff0c\u5979\u9a7b\u5165\u8fce\u5bbe\u9601\uff0c\u8336\u4e5f\u672a\u559d\u4e00\u53e3\uff0c\u5c31\u4e0b\u4ee4\u53ec\u89c1\u6c5f\u5e08\u53d4\uff0c\u6700\u540e\u624d\u52a0\u4e0a\u9093\u603b\u7ba1\uff0c\u6211\u770b\uff0c\u9093\u603b\u7ba1\u53ea\u662f\u804b\u5b50\u7684\u8033\u6735\uff0c\u662f\u4e2a\u914d\u642d\uff0c\u2026\u2026\u201d\u5201\u9e4f\u9053\uff1a\u201c\u5409\u51f6\u96be\u6d4b\u554a\uff01\u6211\u4eec\u8981\u4e0d\u8981\u6709\u4e9b\u51c6\u5907\uff1f\u201d \u3000\u3000\u201c\u4e0d\u8981\u7d27\uff0c\u6211\u53bb\u89c1\u5979\u2026\u2026\u201d\u6c5f\u67ab\u91cd\u65b0\u5305\u624e\u4e86\u4e00\u4e0b\u5de6\u8033\u4f24\u52bf\uff0c\u7b11\u9053\uff1a\u201c\u8d70\uff01\u4e00\u8def\u4e0a\u544a\u8bc9\u6211\u7533\u96ea\u541b\u7684\u559c\u597d\u3001\u5f31\u70b9\uff0c\u4e5f\u597d\u8ba9\u6211\u6709\u70b9\u51c6\u5907\u3002\u201d \u3000\u3000\u201c\u5979\u8868\u9762\u7aef\u5e84\uff0c\u795e\u60c5\u51b7\u8083\uff0c\u4e25\u683c\u8bf4\u8d77\u6765\uff0c\u6211\u4eec\u6839\u672c\u4e0d\u77e5\u9053\u5979\u6709\u5f31\u70b9\uff0c\u2026\u2026\u201d\u5201\u9e4f\u9053\uff1a \u3000\u3000\u201c\u5728\u5929\u9a6c\u603b\u5802\u4e2d\uff0c\u4e5f\u96be\u5f97\u89c1\u5979\u4e00\u6b21\uff0c\u603b\u5802\u4e3b\u8fd8\u5e38\u5e38\u4e3b\u6301\u4f1a\u8bae\uff0c\u5979\u8fde\u4f1a\u8bae\u4e5f\u5f88\u5c11\u51fa\u5e2d\u3002\u201d \u3000\u3000\u201c\u5929\u9a6c\u5802\u56db\u5927\u526f\u603b\u5802\u4e3b\u4e2d\uff0c\u5979\u662f\u4e0d\u662f\u6700\u5177\u6743\u52bf\u7684\u4e00\u4e2a\uff1f\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u5929\u865a\u5b50\u548c\u5979\u662f\u5426\u76f8\u5904\u5f97\u6765\uff1f\u201d \u3000\u3000\u201c\u5929\u865a\u5b50\u867d\u4e5f\u662f\u526f\u603b\u5802\u4e3b\u8eab\u4efd\uff0c\u4f46\u548c\u7533\u96ea\u541b\u76f8\u5dee\u4e00\u6bb5\u8ddd\u79bb\uff0c\u5927\u6982\u53ea\u6709\u542c\u547d\u884c\u4e8b\u7684\u4efd\uff0c\u8c08\u4e0d\u4e0a\u662f\u5426\u76f8\u5904\u5f97\u6765\u3002\u201d \u3000\u3000\u8c08\u8bdd\u4e4b\u95f4\uff0c\u5df2\u5230\u8fce\u5bbe\u9601\u5916\u3002 \u3000\u3000\u9093\u98de\u65e9\u5df2\u5728\u82b1\u6811\u6797\u5916\u7b49\u4faf\uff0c\u4e00\u89c1\u6c5f\u67ab\uff0c\u5927\u6b65\u8fce\u4e86\u4e0a\u6765\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u7533\u526f\u603b\u5802\u4e3b\u7684\u813e\u6c14\u4e0d\u597d\uff0c\u5e94\u5bf9\u4e4b\u95f4\uff0c\u8981\u5c0f\u5fc3\u4e00\u4e9b\u3002\u201d \u3000\u3000\u6c5f\u67ab\u70b9\u70b9\u5934\u3002 \u3000\u3000\u5201\u9e4f\u5f53\u5148\u5e26\u8def\uff0c\u8fdb\u4eba\u5927\u5385\u3002 \u3000\u3000\u53ea\u89c1\u5927\u5385\u4e2d\u5206\u7ad9\u7740\u516b\u4e2a\u9752\u8863\u4f69\u5251\u4eba\uff0c\u8170\u4e2d\u6a2a\u675f\u4e86\u4e00\u6761\u7ea2\u8272\u7684\u5e26\u5b50\u3002 \u3000\u3000\u6c5f\u67ab\u53ea\u770b\u5201\u9e4f\u795e\u8272\uff0c\u5df2\u7ecf\u610f\u8bc6\u5230\u516b\u4e2a\u9752\u8863\u4f69\u5251\u7684\u5e74\u8f7b\u4eba\uff0c\u5c31\u662f\u4ed6\u53e3\u4e2d\u6240\u79f0\u7684\u5251\u58eb\u4e86\u3002\u4e8b\u5b9e\u4e0a\uff0c\u516b\u4eba\u8170\u4e2d\u90a3\u6761\u9c9c\u7ea2\u7684\u5e26\u5b50\uff0c\u5df2\u7279\u522b\u7a81\u51fa\u4e86\u51e0\u4eba\u7684\u8eab\u4efd\u3002 \u3000\u3000\u4e00\u4e2a\u8eab\u7a7f\u9752\u7f0e\u5b50\u88d9\u886b\u7684\u7f8e\u4e3d\u5987\u4eba\uff0c\u7aef\u7aef\u6b63\u6b63\u7684\u5750\u5728\u5927\u5385\u4e2d\u592e\u3002 \u3000\u3000\u4f3c\u4e4e\u662f\u5ea7\u4f4d\u3001\u8ddd\u79bb\uff0c\u90fd\u7ecf\u8fc7\u4e86\u4e08\u91cf\uff0c\u5979\u5750\u7684\u5730\u65b9\u7ed9\u4eba\u4e00\u79cd\u4e3b\u5bb0\u5168\u5c40\u7684\u611f\u89c9\uff0c\u5c31\u50cf\u5979\u7a7f\u7684\u8863\u670d\u4e00\u822c\uff0c\u6734\u7d20\u3001\u6e05\u96c5\u4e2d\u53c8\u7ed9\u4eba\u4e00\u79cd\u9ad8\u8d35\u7684\u611f\u89c9\u3002 \u3000\u3000\u9752\u8863\u5987\u4eba\u4e24\u9053\u6e05\u6f88\u7684\u76ee\u5149\uff0c\u76ef\u4f4f\u6c5f\u67ab\u7684\u7751\u4e0a\uff0c\u6253\u91cf\u4e86\u4e00\u9635\uff0c\u7a81\u7136\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u53eb\u97e9\u9738\uff1f\u201d \u3000\u3000\u8fd9\u65f6\u5019\u7684\u6c5f\u67ab\uff0c\u5df2\u5b8c\u5168\u6062\u590d\u4e86\u97e9\u9738\u7684\u5f62\u8c8c\uff0c\u8eac\u8eab\u4e00\u793c\uff0c\u9053\uff1a\u201c\u5c5e\u4e0b\u53eb\u97e9\u9738\u3002\u201d \u3000\u3000\u201c\u5201\u9e4f\u2026\u2026\u201d\u7533\u96ea\u541b\u7684\u76ee\u5149\u8f6c\u6ce8\u5230\u4e5d\u5934\u9e1f\u7684\u8eab\u4e0a\uff0c\u9053\uff1a\u201c\u4f60\u53ef\u662f\u8d25\u5728\u4ed6\u7684\u624b\u4e2d\uff1f\u201d \u3000\u3000\u201c\u662f\uff01\u2026\u2026\u201d\u5201\u9e4f\u606d\u8c28\u7684\u5e94\u9053\uff1a\u201c\u4ed6\u624b\u4e0b\u7559\u60c5\uff0c\u66ff\u5c5e\u4e0b\u4fdd\u4f4f\u4e86\u5927\u90e8\u5206\u7684\u9762\u5b50\uff01\u201d \u3000\u3000\u7533\u96ea\u541b\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u97e9\u9738\uff0c\u6740\u5185\u5bab\u4e09\u9f99\u4e4b\u4e8c\uff0c\u53c8\u6740\u6b7b\u5929\u738b\u95e8\u4e0b\u4e00\u53ea\u559c\u9e4a\u7684\uff0c\u4e5f\u662f\u4f60\u4e86\uff1f\u201d \u3000\u3000\u201c\u662f\uff01\u5c5e\u4e0b\u4e3a\u4e86\u5929\u9a6c\u5802\u7684\u58f0\u8a89\uff0c\u5fcd\u65e0\u53ef\u5fcd\uff0c\u624d\u51fa\u624b\u60e9\u6cbb\u4ed6\u4eec\u3002\u201d \u3000\u3000\u201c\u5929\u9a6c\u5802\u4e2d\uff0c\u8fd8\u6709\u5982\u6b64\u5fe0\u5b9e\u7684\u90e8\u4e0b\uff0c\u771f\u662f\u53eb\u4eba\u5931\u656c\u4e86\u2026\u2026\u201d\u7533\u96ea\u541b\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u4f46\u4f60\u4e8b\u5148\u672a\u5f97\u6307\u793a\uff0c\u4e8b\u540e\u4e5f\u672a\u5411\u603b\u5802\u5448\u62a5\uff0c\u867d\u7136\u6740\u654c\u7acb\u5a01\uff0c\u4f46\u5374\u65e0\u529f\u2026\u2026\u201d \u3000\u3000\u201c\u97e9\u67d0\u4eba\u4e0d\u6c42\u6709\u529f\u2026\u2026\u201d \u3000\u3000\u201c\u8fc7\u5462\uff1f\u201d\u7533\u96ea\u541b\u51b7\u51b7\u770b\u7740\u6c5f\u67ab\u3002 \u3000\u3000\u201c\u6709\u8fc7\u5417\uff1f\u201d\u6c5f\u67ab\u8fd8\u6ce8\u7740\u7533\u96ea\u541b\u9053\uff1a\u201c\u7eb5\u7136\u6709\u8fc7\uff0c\u4e5f\u8be5\u4e0d\u7b97\u5927\u8fc7\uff0c\u5bf9\u5417\uff1f\u201d \u3000\u3000\u201c\u8fc7\u8fd8\u4e0d\u5c0f\uff0c\u6240\u4ee5\uff0c\u6211\u4eb2\u81ea\u8d76\u6765\uff0c\u8981\u628a\u4f60\u62bc\u89e3\u5230\u603b\u5802\u4e2d\u53bb\uff0c\u542c\u4faf\u5ba1\u8baf\uff01\u201d \u3000\u3000\u8bdd\u867d\u8bf4\u7684\u4e25\u5389\uff0c\u4f46\u8138\u4e0a\u4ecd\u7136\u5e26\u7740\u7b11\u5bb9\u3002 \u3000\u3000\u5979\u7b11\u7684\u5341\u5206\u52a8\u4eba\uff0c\u4f46\u4e5f\u663e\u73b0\u51fa\u4e86\u8138\u4e0a\u9c7c\u5c3e\u7eb9\u7684\u75d5\u8ff9\u3002 \u3000\u3000\u4e0d\u8bba\u4fee\u4e3a\u4f55\u7b49\u7cbe\u6df1\u7684\u4eba\uff0c\u4e5f\u65e0\u6cd5\u5b8c\u5168\u62b9\u53bb\u5c81\u6708\u7559\u4e0b\u7684\u75d5\u8ff9\u3002 \u3000\u3000\u201c\u526f\u603b\u5802\u4e3b\uff0c\u8fd9\u5c31\u6709\u4e9b\u662f\u975e\u4e0d\u5206\uff0c\u5f3a\u4eba\u6240\u96be\u4e86\uff01\u2026\u2026\u201d\u6c5f\u67ab\u5df2\u6709\u4e9b\u706b\u6c14\uff0c\u51b7\u51b7\u7684\u8bf4\u9053\uff1a \u3000\u3000\u201c\u62bc\u89e3\u4e24\u5b57\uff0c\u7528\u7684\u592a\u8fc7\u5206\u4e86\uff01\u201d \u3000\u3000\u201c\u679c\u7136\u662f\u4e00\u4e2a\u5f3a\u608d\u7684\u4eba\u7269\u2026\u2026\u201d\u7533\u96ea\u541b\u9053\uff1a\u201c\u529d\u9152\u4e0d\u5403\uff0c\u5403\u7f5a\u9152\uff0c\u90a3\u5c31\u6709\u4e9b\u5927\u715e\u98ce\u666f\u4e86\uff01\u201d \u3000\u3000\u201c\u65e2\u7136\u4e0d\u5403\uff0c\u90a3\u5c31\u7edd\u4e0d\u6cbe\u5507\uff0c\u7533\u526f\u603b\u5802\u4e3b\u4e0d\u80fd\u4e3a\u5c5e\u4e0b\u4f5c\u4e3b\uff0c\u4e5f\u5c31\u7f62\u4e86\uff0c\u4f46\u5982\u8981\u62bc\u89e3\u56de\u5230\u603b\u5802\u53d7\u5ba1\uff0c\u90a3\u5c31\u6709\u4e9b\u5f62\u540c\u52a9\u654c\u4e86\u3002\u201d \u3000\u3000\u201c\u542c\u4f60\u7684\u53e3\u6c14\uff0c\u4f3c\u662f\u8981\u6297\u547d\u62d2\u6355\u4e86\u2026\u2026\u201d\u7533\u96ea\u541b\u7684\u8138\u4e0a\u4ecd\u6709\u7b11\u610f\uff0c\u4f46\u53e3\u6c14\u5374\u66f4\u89c1\u51cc\u5389\uff0c\u63a5\u9053\uff1a\u201c\u9093\u98de\uff0c\u8fd9\u4e2a\u97e9\u9738\u662f\u4ec0\u4e48\u51fa\u8eab\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u2026\u2026\u5c5e\u4e0b\u4e0d\u592a\u6e05\u695a\u2026\u2026\u201d\u9093\u98de\u9053\uff1a\u201c\u6211\u4eec\u7528\u4eba\u552f\u624d\uff0c\u4ec0\u4e48\u51fa\u8eab\uff0c\u5c31\u67e5\u7684\u4e0d\u4e25\u4e86\uff1f\u201d \u3000\u3000\u201c\u4f60\u662f\u8bf4\uff0c\u4f60\u4e00\u70b9\u4e5f\u4e0d\u4e86\u89e3\u4e86\uff1f\u2026\u2026\u201d \u3000\u3000\u201c\u5c5e\u4e0b\u53ea\u77e5\u9053\u4ed6\u6587\u6b66\u517c\u5907\uff0c\u5341\u5206\u80fd\u5e72\uff0c\u6d1b\u9633\u5c45\u4e2d\u5f88\u591a\u96be\u9898\uff0c\u90fd\u7531\u4ed6\u51fa\u9762\u6446\u5e73\u2026\u2026\u201d \u3000\u3000\u201c\u9093\u98de\u2026\u2026\u201d \u3000\u3000\u201c\u5c5e\u4e0b\u5728\u2026\u2026\u201d \u3000\u3000\u201c\u4f60\u597d\u5927\u7684\u80c6\u5b50\u2026\u2026\u201d \u3000\u3000\u5979\u771f\u7684\u53d1\u6012\u4e86\uff0c\u4f46\u8138\u4e0a\u4ecd\u7136\u6709\u7740\u7b11\u5bb9\u3002 \u3000\u3000\u201c\u7528\u4e0d\u7740\u628a\u6012\u706b\u8f6c\u53d1\u5230\u9093\u603b\u7ba1\u7684\u5934\u4e0a\uff0c\u597d\u6c49\u505a\u4e8b\u597d\u6c49\u5f53\uff0c\u6211\u97e9\u67d0\u4eba\uff0c\u867d\u7136\u4e0d\u662f\u6c5f\u6e56\u4e0a\u7684\u540d\u4eba\uff0c\u4f46\u5927\u4e08\u592b\u751f\u6709\u4f55\u6b22\uff0c\u6b7b\u6709\u4f55\u60e7\uff1f\u8fd9\u4ef6\u4e8b\uff0c\u4e0d\u8bba\u6709\u591a\u4e48\u91cd\u5927\uff0c\u7531\u97e9\u67d0\u4eba\u4e00\u4e2a\u625b\u4e86\uff0c\u7528\u4e0d\u7740\u518d\u7275\u6d89\u5230\u522b\u4eba\u8eab\u4e0a\uff0c\u7533\u526f\u603b\u5802\u4e3b\uff0c\u5c31\u8bf7\u9ad8\u62ac\u8d35\u624b\uff0c\u653e\u8fc7\u6211\u4eec\u7684\u9093\u603b\u7ba1\u5427\uff01\u201d \u3000\u3000\u201c\u90a3\u662f\u8bf4\uff0c\u4f60\u613f\u610f\u8ddf\u6211\u56de\u5230\u603b\u5802\u53d7\u5ba1\u4e86\u2026\u2026\u201d\u7533\u96ea\u541b\u9053\uff1a\u201c\u8001\u5b9e\u8bf4\uff0c\u6211\u4eec\u5bf9\u4f60\u8fd9\u4e2a\u4eba\uff0c\u5145\u6ee1\u7740\u731c\u7591\uff1f\u6211\u4eec\u8981\u52a8\u5458\u5f88\u5e9e\u5927\u7684\u4eba\u529b\uff0c\u67e5\u660e\u4f60\u7684\u5e95\u7ec6\uff01\u8fd9\u624d\u662f\uff0c\u6211\u8981\u628a\u4f60\u5e26\u56de\u603b\u5802\u7684\u771f\u6b63\u7684\u539f\u56e0\u3002\u201d \u3000\u3000\u201c\u6211\u53ef\u4ee5\u5230\u5929\u9a6c\u603b\u5802\u8d70\u4e00\u8d9f\uff0c\u4f46\u6211\u4e0d\u559c\u6b22\u4f60\u7684\u65b9\u5f0f\uff0c\u6211\u8981\u6765\u53bb\u81ea\u5982\uff0c\u4e0d\u53d7\u76d1\u89c6\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\uff0c\u5185\u5bab\u4e00\u7cfb\u4e2d\u4eba\u6d3e\u6765\u4e86\u516d\u5927\u6740\u624b\uff0c\u53d6\u97e9\u67d0\u7684\u6027\u547d\uff0c\u5e78\u597d\uff0c\u97e9\u67d0\u4eba\u547d\u4e0d\u8be5\u7edd\uff0c\u9003\u8131\u4e86\u51e0\u6b21\u6697\u88ad\uff0c\u53ea\u53d7\u4e86\u4e00\u70b9\u8f7b\u4f24\u2026\u2026\u201d \u3000\u3000\u201c\u5509\uff01\u5185\u5bab\u4e00\u7cfb\u4e2d\u6d3e\u51fa\u7684\u4e13\u4efb\u6740\u624b\uff0c\u7adf\u662f\u5982\u6b64\u7684\u65e0\u80fd\uff1f\u2026\u2026\u201d \u3000\u3000\u201c\u4ed6\u4eec\u4e0d\u7b97\u5f88\u5dee\u4e86\uff0c\u4f24\u4e86\u97e9\u67d0\u4eba\u4e00\u53ea\u5de6\u6735\uff0c\u4f55\u51b5\u8fd8\u6709\u4e24\u4e2a\u6740\u624b\uff0c\u5c1a\u672a\u73b0\u8eab\uff0c\u4ed6\u4eec\u968f\u65f6\u53ef\u80fd\u51fa\u73b0\uff01\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u76ee\u5149\u8f6c\u52a8\uff0c\u56db\u4e0b\u6253\u91cf\uff0c\u597d\u50cf\u90a3\u4e24\u4e2a\u6740\u624b\uff0c\u5c31\u53ef\u80fd\u9690\u85cf\u5728\u8fd9\u5927\u5385\u4e4b\u4e2d\u3002 \u3000\u3000\u201c\u8fd9\u91cc\u7edd\u5bf9\u5b89\u5168\uff0c\u81f3\u5c11\uff0c\u8fd9\u5927\u5385\u4e4b\u4e2d\uff0c\u4e0d\u4f1a\u6709\u6740\u624b\u6f5c\u5165\u2026\u2026\u201d\u7533\u96ea\u541b\u7b11\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u6211\u770b\uff0c\u4f60\u4e0d\u662f\u90a3\u79cd\u7518\u5fc3\u675f\u624b\u5c31\u7f1a\u7684\u4eba\u4e86\uff0c\u4e3a\u4e86\u4e0d\u7559\u53e3\u5b9e\uff0c\u8bf4\u6211\u4ee5\u526f\u603b\u5802\u4e3b\u7684\u8eab\u4efd\uff0c\u4ee5\u5927\u538b\u5c0f\uff0c\u6240\u4ee5\uff0c\u6211\u60f3\u5c55\u793a\u4e00\u4e0b\u6b66\u529b\uff0c\u4f60\u53ef\u4ee5\u4ed4\u7ec6\u5730\u770b\u4e00\u4e0b\uff0c\u5982\u679c\u4f60\u81ea\u4fe1\u80fd\u591f\u6297\u62d2\uff0c\u5c3d\u7ba1\u51fa\u624b\u62d2\u6355\uff0c\u5982\u679c\u4f60\u89c9\u65e0\u529b\u6297\u62d2\uff0c\u90a3\u5c31\u4e56\u4e56\u7684\u8ddf\u6211\u8d70\u5427\uff01\u201d \u3000\u3000\u770b\u4e86\u516b\u4e2a\u5251\u624b\u4e00\u773c\uff0c\u6c5f\u67ab\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u5c31\u662f\u8fd9\u516b\u4f4d\u5251\u571f\u4e86\uff1f\u201d \u3000\u3000\u201c\u5bf9\uff01\u4ed6\u4eec\u516b\u4e2a\u4eba\uff0c\u53ef\u4ee5\u5bf9\u6297\u516b\u4f4d\u6b66\u6797\u4e2d\u7b2c\u4e00\u6d41\u7684\u9ad8\u624b\uff0c\u4e0d\u8981\u5c0f\u89d1\u4ed6\u4eec\u2026\u2026\u201d\u7533\u96ea\u541b\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\uff0c\u4f60\u53ef\u4ee5\u5bf9\u6297\u4ed6\u4eec\u51e0\u4e2a\uff0c\u4f46\u6211\u4eec\u5148\u8ba9\u4f60\u89c2\u6469\u4e00\u4e0b\u4ed6\u4eec\u7684\u5251\u672f\uff0c\u7ed9\u4f60\u4e00\u4e2a\u51c6\u5907\u5bf9\u6297\u4ed6\u4eec\u7684\u673a\u4f1a\uff01\u201d \u3000\u3000\u201c\u5c31\u7b97\u6211\u97e9\u67d0\u4eba\u60f3\u62d2\u7edd\uff0c\u53ea\u6015\u4e5f\u662f\u4e0d\u592a\u53ef\u80fd\u7684\u4e8b\u4e86\u2026\u2026\u201d\u6c5f\u67ab\u7b11\u9053\uff1a\u201c\u526f\u603b\u5802\u4e3b\uff0c\u6211\u5e0c\u671b\u8fd9\u7b14\u5e10\uff0c\u53ea\u7b97\u5728\u6211\u97e9\u67d0\u4eba\u4e00\u4e2a\u4eba\u7684\u8eab\u4e0a\uff0c\u4e0d\u8981\u518d\u7275\u626f\u5230\u5176\u4ed6\u4eba\u7684\u8eab\u4e0a\u4e86\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u6211\u7b54\u5e94\u4f60\uff0c\u4e0d\u7ba1\u6211\u80fd\u4e0d\u80fd\u628a\u4f60\u5e26\u56de\u603b\u5802\u53bb\u53d7\u5ba1\uff0c\u90fd\u4e0d\u7275\u626f\u4e0a\u522b\u7684\u4eba\u2026\u2026\u201d\u7533\u96ea\u541b\u7b11\u9053\uff1a\u201c\u4f60\u8fd8\u6709\u4ec0\u4e48\u8981\u6c42\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\u4e86\uff0c\u9093\u603b\u7ba1\uff0c\u8bf7\u9000\u51fa\u5927\u5385\uff0c\u6b64\u540e\uff0c\u97e9\u67d0\u4eba\u7684\u4e8b\uff0c\u7531\u6211\u4e00\u4eba\u627f\u62c5\uff0c\u7533\u526f\u603b\u5802\u4e3b\u7b54\u5e94\u4e0d\u7275\u626f\u522b\u4eba\uff0c\u6211\u76f8\u4fe1\u5979\u4e00\u8a00\u5982\u5c71\u3002\u201d \u3000\u3000\u9093\u98de\u770b\u4e86\u6c5f\u67ab\u51e0\u773c\uff0c\u6b32\u8a00\u53c8\u6b62\uff0c\u8f6c\u8eab\u5411\u5916\u884c\u53bb\u3002 \u3000\u3000\u4e5d\u5934\u9e1f\u5201\u9e4f\u4f4e\u58f0\u9053\uff1a\u201c\u8bf7\u793a\u526f\u603b\u5802\u4e3b\uff0c\u5201\u67d0\u4eba\u662f\u5426\u53ef\u4ee5\u7559\u4e0b\uff1f\u201d \u3000\u3000\u201c\u53ef\u4ee5\uff0c\u4f60\u7ad9\u5230\u6211\u7684\u8eab\u8fb9\uff0c\u514d\u5f97\u90a3\u6d41\u52a8\u7684\u5251\u6c14\uff0c\u4f24\u5bb3\u5230\u4f60\u4e86\u2026\u2026\u201d\u7533\u96ea\u541b\u7b11\u5bb9\u7a81\u655b\uff0c\u8138\u8272\u7a81\u7136\u5341\u5206\u4e25\u8083\uff0c\u8bf4\u9053\uff1a\u201c\u97e9\u9738\uff0c\u7559\u5fc3\u4e86\u2026\u2026\u201d \u3000\u3000\u7a81\u7136\u626c\u624b\u98de\u51fa\u4e86\u5341\u679a\u5236\u94b1\u3002 \u3000\u3000\u516b\u4e2a\u5251\u58eb\uff0c\u5341\u679a\u5236\u94b1\uff0c\u591a\u4e86\u4e24\u4e2a\u51fa\u6765\uff0c\u516b\u4eba\u5404\u81ea\u5bf9\u4ed8\u4e00\u4e2a\uff0c\u591a\u7684\u4e24\u4e2a\u5c31\u6210\u4e86\u5f88\u5927\u7684\u6f0f\u6d1e\u3002 \u3000\u3000\u56e0\u4e3a\uff0c\u7533\u96ea\u541b\u624b\u529b\u5f3a\u52b2\uff0c\u98de\u51fa\u7684\u5236\u94b1\uff0c\u5206\u5e03\u5728\u6574\u4e2a\u5927\u5385\u8303\u56f4\u4e4b\u4e0a\u3002 \u3000\u3000\u4f46\u89c1\u516b\u4e2a\u4eba\u5f71\uff0c\u540c\u65f6\u98de\u8d77\uff0c\u516b\u9053\u5251\u5149\uff0c\u5e03\u6210\u4e86\u4e00\u5251\u7f51\uff0c\u5168\u5385\u79fb\u52a8\uff0c\u4e00\u679a\u5236\u94b1\uff0c\u5168\u843d\u5728\u5251\u8eab\u4e4b\u4e0a\u3002 \u3000\u3000\u8fd9\u4e00\u62db\u770b\u4e0d\u51fa\u51f6\u5389\u3001\u7a00\u5947\u4e4b\u5904\uff0c\u4f46\u5374\u662f\u771f\u6b63\u7684\u529f\u592b\uff0c\u516b\u4eba\u7ed3\u5408\uff0c\u7ed5\u5385\u98de\u821e\uff0c\u4e45\u4e45\u4e0d\u843d\u5b9e\u5730\uff0c\u8fd9\u8bf4\u660e\u4e86\u6bcf\u4e2a\u4eba\u7684\u8f7b\u8eab\u529f\u592b\uff0c\u90fd\u5230\u4e86\u7089\u706b\u7eaf\u9752\u4e4b\u5883\u3002 \u3000\u3000\u516b\u5251\u5982\u4e00\uff0c\u7ed3\u5408\u4e00\u5904\uff0c\u8868\u793a\u4ed6\u4eec\u4e4b\u95f4\u914d\u5408\u7684\u7cbe\u7ec3\u3001\u7a33\u5065\u3002 \u3000\u3000\u201c\u597d\uff01\u597d\uff01\u914d\u5408\u5230\u516b\u5251\u5982\u4e00\uff0c\u679c\u7136\u662f\u975e\u540c\u5c0f\u53ef\u3002\u5e0c\u671b\u4ed6\u4eec\u6740\u4eba\u7684\u624b\u6cd5\uff0c\u4e5f\u80fd\u914d\u5408\u5230\u8fd9\u79cd\u5883\u754c\uff1f\u201d \u3000\u3000\u7533\u96ea\u541b\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u80c6\u5927\u5305\u5929\uff0c\u5f53\u4e4b\u65e0\u6127\uff0c\u53d6\u4e0b\u4f60\u7684\u4eba\u76ae\u9762\u5177\u6765\uff0c\u7ed9\u6211\u770b\u770b\u597d\u5417\uff1f\u201d \u3000\u3000\u679c\u7136\u662f\u795e\u76ee\u5982\u7535\uff0c\u7adf\u7136\u77a7\u51fa\u4e86\u6c5f\u67ab\u6234\u4e86\u4eba\u76ae\u9762\u5177\u3002 \u3000\u3000\u6c5f\u67ab\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u97e9\u67d0\u4eba\u81ea\u51fa\u9053\u4ee5\u6765\uff0c\u8fd8\u662f\u7b2c\u4e00\u4e2a\u88ab\u4eba\u77a7\u7834\u6211\u6234\u4e86\u4eba\u76ae\u9762\u5177\uff0c\u8db3\u89c1\u9ad8\u660e\uff0c\u4e0d\u8fc7\uff0c\u97e9\u67d0\u4eba\u4e5f\u6709\u82e6\u8877\uff0c\u73b0\u5728\u5185\u5bab\u4e2d\u4eba\u5df2\u6d3e\u51fa\u4e86\u516d\u5927\u6740\u624b\u8ffd\u6740\u6211\uff0c\u5982\u82e5\u6211\u97e9\u67d0\u53d6\u4e0b\u9762\u5177\uff0c\u663e\u9732\u51fa\u672c\u6765\u9762\u76ee\uff0c\u5929\u4e0b\u6740\u624b\uff0c\u5168\u8981\u96c6\u4e2d\u4e00\u5904\uff0c\u4ee5\u53d6\u6211\u4e4b\u547d\u4e3a\u9996\u8981\u4e4b\u52a1\u4e86\u3002\u201d \u3000\u3000\u201c\u4f60\u4e0d\u4f46\u80c6\u5927\u5305\u5929\uff0c\u800c\u4e14\u53e3\u820c\u5982\u5200\uff0c\u2026\u2026\u201d\u7533\u96ea\u541b\u9053\uff1a\u201c\u6740\u4e86\u4f60\uff0c\u6211\u4e00\u6837\u53ef\u4ee5\u53d6\u4e0b\u4eba\u76ae\u9762\u5177\uff0c\u770b\u5230\u4f60\u771f\u6b63\u7684\u9762\u76ee\uff0c\u6211\u4e0d\u76f8\u4fe1\u4f60\u53eb\u97e9\u9738\uff0c\u4e5f\u4e0d\u76f8\u4fe1\u51ed\u7a7a\u4f1a\u8df3\u51fa\u4f60\u8fd9\u4e48\u4e00\u53f7\u4eba\u7269\uff0c\u6811\u6709\u6839\uff0c\u6c34\u6709\u6e90\uff0c\u4e5f\u8bb8\u6211\u4eec\u8fd8\u662f\u6614\u5e74\u65e7\u8bc6\u5462\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u770b\u6765\uff0c\u526f\u603b\u5802\u4e3b\u7684\u8bef\u4f1a\u5f88\u6df1\uff0c\u6211\u53ef\u4ee5\u544a\u8bc9\u4f60\uff0c\u54b1\u4eec\u7edd\u5bf9\u6ca1\u6709\u89c1\u8fc7\uff0c\u6240\u4ee5\uff0c\u5728\u4e0b\u7528\u4e0d\u7740\u4eae\u76f8\u4e86\uff0c\u8981\u89c1\u5728\u4e0b\u7684\u771f\u9762\u76ee\uff0c\u53ea\u6709\u4e00\u4e2a\u529e\u6cd5\uff0c\u6740\u4e86\u6211\uff0c\u518d\u53d6\u4e0b\u6211\u7684\u9762\u5177\u3002\u201d \u3000\u3000\u201c\u597d\u72c2\u7684\u53e3\u6c14\uff0c\u6740\u2026\u2026\u201d\u7533\u96ea\u541b\u771f\u7684\u5fff\u6012\u4e86\uff0c\u8fd9\u4e9b\u5e74\u6765\uff0c\u5979\u517b\u5c0a\u5904\u4f18\uff0c\u54ea\u91cc\u53d7\u8fc7\u5982\u6b64\u7684\u620f\u5f04\uff1f \u3000\u3000\u516b\u9053\u5251\u5149\uff0c\u66b4\u5c04\u800c\u8d77\uff0c\u5bd2\u8292\u5c55\u5e03\uff0c\u5ffd\u4e4b\u95f4\uff0c\u5e03\u6ee1\u4e86\u6574\u5ea7\u7684\u5927\u5385\uff0c\u4f3c\u662f\u4e00\u56e2\u5149\u5c71\u822c\uff0c\u538b\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u5f53\u771f\u662f\u7ef5\u5bc6\u5982\u7f51\uff0c\u96c0\u9e1f\u96be\u6e21\u3002 \u3000\u3000\u5201\u9e4f\u5728\u6c5f\u6e56\u4e0a\u884c\u8d70\u591a\u5e74\uff0c\u5c31\u6ca1\u6709\u89c1\u8fc7\u5982\u6b64\u7684\u5251\u7f51\u3002 \u3000\u3000\u6c5f\u67ab\u4e5f\u770b\u7684\u6709\u4e9b\u60ca\u5fc3\uff0c\u4e00\u63d0\u771f\u6c14\uff0c\u8fd0\u96c6\u4e86\u5168\u8eab\u529f\u529b\uff0c\u8eab\u5b50\u5347\u8d77\uff0c\u8fce\u5411\u5251\u7f51\uff0c\u4e00\u9053\u7fe0\u7eff\u8272\u7684\u5149\u8292\uff0c\u540c\u65f6\u98de\u8d77\u3002 \u3000\u3000\u516b\u5251\u5408\u4e00\u7684\u51cc\u5389\u5251\u8292\uff0c\u7ec8\u4e8e\u903c\u7684\u6c5f\u67ab\u4eae\u51fa\u4e86\u5175\u5203\u3002 \u3000\u3000\u4f46\u95fb\u4e00\u9635\u53ee\u53ee\u549a\u549a\u7684\u8106\u54cd\uff0c\u7ef5\u5bc6\u7684\u5251\u7f51\uff0c\u88ab\u8361\u5f00\u4e86\u4e00\u4e2a\u5927\u6d1e\uff0c\u6c5f\u67ab\u7834\u7f51\u800c\u51fa\u3002 \u3000\u3000\u201c\u7fe0\u7389\u5200\u2026\u2026\u201d\u7533\u96ea\u541b\u60ca\u9053\uff1a\u201c\u5929\u72d0\u592b\u4eba\u7684\u968f\u8eab\u5175\u5203\uff0c\u600e\u4f1a\u5230\u4e86\u4f60\u7684\u624b\u4e2d\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u6b63\u662f\u7fe0\u7389\u5200\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u8fd9\u516b\u5927\u5251\u58eb\u624b\u4e2d\u7684\u78c1\u529b\u5251\uff0c\u80fd\u4f7f\u5404\u79cd\u5175\u5203\u5931\u63a7\u6613\u4f4d\uff0c\u53ef\u60dc\uff0c\u5b83\u5438\u4e0d\u4f4f\u7fe0\u7389\u5200\u3002\u201d \u3000\u3000\u201c\u4ed6\u4eec\u4e0d\u7528\u78c1\u5251\u7684\u5f15\u529b\uff0c\u7167\u6837\u53ef\u4ee5\u53d6\u4f60\u6027\u547d\uff01\u201d \u3000\u3000\u4f46\u89c1\u516b\u4e2a\u7ea2\u5e26\u5251\u58eb\uff0c\u7528\u529b\u4e00\u63a8\u5251\u67c4\uff0c\u5251\u8eab\u4e0a\u7a81\u7136\u8131\u4e0b\u4e86\u4e00\u4e2a\u5251\u5957\u3002 \u3000\u3000\u539f\u6765\uff0c\u90a3\u4e9b\u5251\u58eb\u7684\u5b9e\u5251\u4e4b\u4e0a\uff0c\u8fd8\u6234\u4e86\u534a\u4e2a\u5251\u5957\u3002 \u3000\u3000\u6240\u8c13\u7684\u534a\u4e2a\u5251\u5957\uff0c\u662f\u5957\u5728\u957f\u5251\u7684\u4e0b\u534a\u622a\uff0c\u770b\u4e0a\u53bb\uff0c\u8fd9\u628a\u5251\u6709\u4e9b\u602a\u5f02\uff0c\u4f46\u5374\u975e\u5e38\u5b9e\u7528\uff0c\u534a\u622a\u5251\u5957\u4e0a\u7684\u5f3a\u5927\u78c1\u529b\uff0c\u5728\u7075\u6d3b\u7684\u64cd\u4f5c\u4e0b\uff0c\u5e38\u5e38\u4f1a\u751f\u51fa\u610f\u60f3\u4e0d\u5230\u7684\u5999\u7528\uff0c\u5f15\u5f00\u654c\u4eba\u7684\u5175\u5203\uff0c\u90a3\u867d\u662f\u6beb\u5398\u4e4b\u5dee\uff0c\u4f46\u5728\u9ad8\u624b\u640f\u6597\u4e2d\uff0c\u5c31\u662f\u81f4\u547d\u7684\u5931\u8bef\u4e86\u3002 \u3000\u3000\u8fd9\u4e9b\u5251\u571f\uff0c\u4e0d\u4f46\u5185\u529f\u6df1\u539a\uff0c\u6280\u827a\u7cbe\u6e5b\uff0c\u800c\u4e14\u5175\u5203\u7684\u8bbe\u8ba1\uff0c\u4e5f\u8d39\u5c3d\u4e86\u4e0d\u5c11\u5fc3\u673a\uff0c\u4f3c\u4e4e\u4f4f\u4f55\u4e00\u70b9\u5fae\u672a\u5c0f\u8282\uff0c\u90fd\u5728\u529b\u6c42\u5b8c\u5584\u3001\u7280\u5229\u3002 \u3000\u3000\u6c5f\u67ab\u5fc3\u4e2d\u91cd\u65b0\u4f5c\u4e86\u4e00\u756a\u8bc4\u4f30\uff0c\u51b3\u5fc3\u5168\u529b\u65bd\u4e3a\uff0c\u4e00\u4e3e\u95f4\u640f\u6740\u5f3a\u654c\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u4e00\u756a\u4ea4\u624b\u4e4b\u540e\uff0c\u6c5f\u67ab\u5fc3\u4e2d\u4e5f\u660e\u767d\u8fd9\u516b\u4e2a\u5251\u58eb\uff0c\u6781\u5584\u5408\u640f\u4e4b\u672f\uff0c\u4e00\u4e0b\u5b50\u6740\u6b7b\u516b\u4eba\uff0c\u662f\u4e00\u4ef6\u7edd\u4e0d\u53ef\u80fd\u7684\u4e8b\uff0c\u6c5f\u67ab\u8fc5\u901f\u7684\u60f3\u8fc7\u4e86\u672c\u8eab\u51e0\u62db\u6740\u624b\u3001\u7edd\u6280\uff0c\u5c31\u60f3\u4e0d\u51fa\u4efb\u4f55\u4e00\u62db\u6b66\u529f\uff0c\u80fd\u6709\u5982\u6b64\u5f3a\u5927\u7684\u5a01\u529b\u3002 \u3000\u3000\u6cdb\u5347\u5728\u5fc3\u5934\u7684\u6740\u673a\uff0c\u7a81\u7136\u95f4\u6de1\u4e86\u4e0b\u6765\uff0c\u8fd1\u65e5\u7684\u6c5f\u6e56\u5386\u7ec3\uff0c\u4f7f\u5f97\u6c5f\u67ab\u5b66\u4f1a\u4e86\u5ba1\u5ea6\u654c\u52bf\uff0c\u4e09\u601d\u800c\u884c\uff0c\u611f\u89c9\u5230\u4e00\u51fb\u65e0\u6cd5\u6210\u529f\uff0c\u7acb\u523b\u6539\u53d8\u8ba1\u5212\uff0c\u51c6\u5907\u5148\u627e\u51fa\u654c\u4eba\u5251\u6cd5\u4e0a\u7684\u7f3a\u70b9\u540e\uff0c\u518d\u884c\u53cd\u51fb\u3002 \u3000\u3000\u4f46\u95fb\u7533\u96ea\u541b\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u5c31\u7b97\u5929\u72d0\u592b\u4eba\u8fd8\u9b42\u91cd\u751f\uff0c\u4e5f\u65e0\u6cd5\u9003\u8fc7\u4eca\u65e5\u4e4b\u5371\uff0c\u7fe0\u7389\u5200\u6cd5\u4e2d\u4ee5\u5929\u72d0\u4e03\u65a9\uff0c\u6700\u4e3a\u51cc\u5389\u51f6\u608d\uff0c\u4f60\u53ef\u4ee5\u65bd\u5c55\u51fa\u6765\u8bd5\u8bd5\uff1f\u201d \u3000\u3000\u542c\u53e3\u6c14\uff0c\u5979\u5bf9\u5929\u72d0\u592b\u4eba\u4f3c\u662f\u5341\u5206\u4e86\u89e3\u3002 \u3000\u3000\u6c5f\u67ab\u5bf9\u4e09\u4f4d\u5e08\u957f\u7684\u5f80\u4e8b\uff0c\u867d\u975e\u5341\u5206\u4e86\u89e3\uff0c\u4f46\u5374\u9690\u7ea6\u77e5\u9053\u4e09\u4f4d\u5e08\u957f\u4e4b\u95f4\uff0c\u4e5f\u6709\u7740\u4e00\u6bb5\u60c5\u4ec7\u3001\u6069\u6028\u3002 \u3000\u3000\u4f46\u4e09\u4e2a\u4eba\u90fd\u8bc6\u5927\u4f53\uff0c\u7ec8\u80fd\u629b\u5f03\u4e2a\u4eba\u7684\u597d\u6076\u4e4b\u5ff5\uff0c\u5408\u529b\u9020\u5c31\u51fa\u4ed6\u6c5f\u67ab\u8fd9\u4e2a\u4eba\uff0c\u4f7f\u4e09\u5927\u5947\u4eba\u7684\u7edd\u6280\uff0c\u96c6\u4e8e\u4e00\u4eba\u4e4b\u8eab\uff0c\u4ee5\u907f\u514d\u5404\u4f20\u8863\u94b5\uff0c\u5f15\u53d1\u4e86\u5f7c\u6b64\u4e0d\u670d\u5bf9\u65b9\u7684\u5fc3\u6001\uff0c\u8d3b\u8bef\u5927\u5c40\u3002 \u3000\u3000\u201c\u5929\u72d0\u6b66\u827a\uff0c\u4ee5\u8f7b\u529f\u3001\u6697\u5668\u89c1\u957f\u2026\u2026\u201d\u6c5f\u67ab\u5fcd\u4e0d\u4f4f\u8fa9\u9053\uff1a\u201c\u7fe0\u7389\u5200\u6cd5\u4e2d\u5929\u72d0\u4e03\u65a9\uff0c\u5e94\u8be5\u4e0d\u5217\u5165\u5929\u72d0\u7edd\u6280\u4e4b\u4e2d\u3002\u201d \u3000\u3000\u7533\u96ea\u541b\u8138\u8272\u4e00\u53d8\uff0c\u9053\uff1a\u201c\u4f60\u7a76\u7adf\u662f\u4ec0\u4e48\u4eba\uff1f\u5929\u72d0\u592b\u4eba\u7684\u5f1f\u5b50\u5417\uff1f \u3000\u3000\u5979\u81ea\u79f0\u76d7\u4ea6\u6709\u9053\uff0c\u4e3a\u4ec0\u4e48\u4f1a\u6536\u4e2a\u5927\u7537\u4eba\u4f5c\u8863\u94b5\u4f20\u4eba\uff1f\u201d \u3000\u3000\u770b\u5979\u6fc0\u52a8\u795e\u8272\uff0c\u6c5f\u67ab\u5fc3\u4e2d\u7a81\u6709\u6240\u609f\uff0c\u6697\u9053\uff1a\u201c\u83ab\u975e\u4e09\u4f4d\u5e08\u957f\u4e4b\u95f4\u7684\u6069\u6028\u60c5\u4ec7\uff0c\u548c\u5979\u6709\u5173\u5417\uff1f\u201d \u3000\u3000\u4f46\u95fb\u7533\u96ea\u541b\u63a5\u9053\uff1a\u201c\u8bf4\u8bdd\u5440\uff01\u6211\u4e5f\u8bb8\u4f1a\u653e\u4f60\u4e00\u5200\uff0c\u5982\u82e5\u4f60\u9ed8\u4e0d\u4f5c\u7b54\uff0c\u90a3\u5c31\u662f\u81ea\u5bfb\u6b7b\u8def\u4e86\u3002\u201d \u3000\u3000\u6c5f\u67ab\u7a81\u7136\u8eac\u8eab\u4e00\u793c\uff0c\u9053\uff1a\u201c\u8001\u524d\u8f88\u8ba4\u8bc6\u5929\u72d0\u592b\u4eba\u5417\uff1f\u201d \u3000\u3000\u201c\u54fc\uff01\u5c31\u7b97\u5979\u8840\u8089\u5316\u6ce5\uff0c\u5c38\u9aa8\u5316\u7070\uff0c\u6211\u4e5f\u80fd\u8ba4\u5f97\u51fa\u6765\u3002\u201d \u3000\u3000\u201c\u770b\u6765\uff0c\u8001\u524d\u8f88\u5bf9\u5979\u7684\u6068\u610f\u751a\u6df1\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u8981\u53eb\u6211\u524d\u8f88\u2026\u2026\u201d\u7533\u96ea\u541b\u63a5\u9053\uff1a\u201c\u5929\u72d0\u95e8\u4e2d\u5f1f\u5b50\uff0c\u4e0d\u914d\u53eb\u6211\u3002\u201d \u3000\u3000\u6c5f\u67ab\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5929\u72d0\u592b\u4eba\u5df2\u901d\u4e16\u4e03\u5e74\u4e86\uff0c\u8001\u524d\u8f88\u7684\u79ef\u6068\uff0c\u4e5f\u8be5\u6d88\u53bb\u4e00\u4e9b\u4e86\uff0c\u2026\u2026\u201d \u3000\u3000\u201c\u5979\u771f\u7684\u6b7b\u4e86\u2026\u2026\u201d\u7533\u96ea\u541b\u9053\uff1a\u201c\u72d0\u8ba1\u591a\u7aef\uff0c\u8c01\u80fd\u76f8\u4fe1\u90a3\u5934\u72e1\u733e\u7684\u6076\u5996\u72d0\u5462\uff1f\u201d \u3000\u3000\u201c\u8001\u524d\u8f88\uff0c\u8fd9\u5c31\u4e0d\u539a\u9053\u4e86\u2026\u2026\u201d\u6c5f\u67ab\u51b7\u7136\u8bf4\u9053\uff1a\u201c\u665a\u8f88\u8bf4\u7684\u5168\u662f\u5b9e\u8bdd\u3002\u201d \u3000\u3000\u7533\u96ea\u7fa4\u7a81\u7136\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8001\u5996\u72d0\u771f\u7684\u6b7b\u4e86\uff0c\u6211\u8fd8\u771f\u7684\u6709\u4e9b\u96be\u8fc7\uff01\u201d \u3000\u3000\u201c\u7236\u503a\u5b50\u8fd8\uff0c\u5e08\u6b20\u5f92\u8fd8\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u5728\u4e0b\u613f\u4ee3\u5e08\u507f\u8fd8\u65e7\u503a\u3002\u201d \u3000\u3000\u7533\u96ea\u7fa4\u4e24\u9053\u6e05\u6f88\uff0c\u51b7\u5389\u7684\u76ee\u5149\uff0c\u76ef\u6ce8\u5728\u6c5f\u67ab\u7684\u8138\u4e0a\u77a7\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u4f60\u771f\u662f\u72d0\u95e8\u5f1f\u5b50\uff1f\u201d \u3000\u3000\u201c\u662f\uff01\u665a\u8f88\u627f\u53d7\u4e86\u5929\u72d0\u592b\u4eba\u5927\u90e8\u5206\u7684\u6280\u827a\u3002\u201d \u3000\u3000\u7533\u96ea\u541b\u7a81\u7136\u957f\u53f9\u4e00\u58f0\uff0c\u8fde\u8fde\u6325\u624b\uff0c\u9053\uff1a\u201c\u4f60\u8d70\u5427\uff01\u8d70\u7684\u8d8a\u8fdc\u8d8a\u597d\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u8981\u53ef\u662f\u4e86\uff0c\u8fd9\u91cc\u7684\u4e8b\uff0c\u6211\u66ff\u4f60\u625b\u4e0b\u6765\uff0c\u6211\u4fdd\u8bc1\u4e0d\u4f1a\u6709\u4eba\u8ffd\u6740\u4f60\uff0c\u8bb0\u7740\uff0c\u5728\u4f60\u5e08\u7236\u7684\u575f\u524d\uff0c\u66ff\u6211\u8bf4\u4e24\u53e5\u8bdd\u2026\u2026\u201d\u7533\u96ea\u541b\u795e\u60c5\u8f6c\u53d8\u7684\u5341\u5206\u4f24\uff0c\u9053\uff1a\u201c\u6211\u6068\u5979\uff0c\u4e5f\u7231\u5979\uff0c\u6614\u65e5\u60c5\u4ec7\u4e00\u7b14\u6d88\uff0c\u8ba9\u5979\u5b89\u5fc3\u7684\u4f11\u606f\u5427\uff01\u201d \u3000\u3000\u6c5f\u67ab\u542c\u5f97\u4e00\u6014\uff0c\u6697\u9053\uff1a\u201c\u521a\u624d\u5979\u54ac\u7259\u793a\u6068\uff0c\u5927\u6709\u8a93\u4e0d\u4e24\u7acb\u7684\u6c14\u6982\uff0c\u6b64\u523b\u5374\u53c8\u9eef\u7136\u795e\u4f24\uff0c\u6709\u7740\u75db\u5931\u826f\u53cb\u7684\u60b2\u82e6\uff0c\u5f53\u771f\u662f\u4ec7\u4e2d\u6709\u60c5\uff0c\u60c5\u4e2d\u6709\u4ec7\uff1f\u5979\u4eec\u4e4b\u95f4\uff0c\u7a76\u7adf\u662f\u4ec0\u4e48\u6837\u7684\u60c5\u4ec7\u5462\uff1f\u201d \u3000\u3000\u201c\u8fd8\u4e0d\u5feb\u8d70\uff01\u7b49\u4e00\u4e0b\uff0c\u6211\u53ef\u80fd\u4f1a\u6539\u53d8\u4e3b\u610f\uff1f\u201d \u3000\u3000\u201c\u591a\u8c22\u597d\u610f\uff0c\u5728\u4e0b\u8fd8\u672a\u9886\u6559\u8fd9\u516b\u4f4d\u5251\u58eb\u7684\u9ad8\u8d85\u5251\u6cd5\uff0c\u8d70\u7684\u4e5f\u4e00\u4e0d\u7518\u5fc3\u3002\u201d \u3000\u3000\u201c\u5c31\u51ed\u4f60\u5e08\u7236\u90a3\u51e0\u62db\u5929\u72d0\u5200\u6cd5\uff0c\u7edd\u5bf9\u4e0d\u662f\u4ed6\u4eec\u7684\u654c\u624b\uff0c\u5feb\u4e9b\u9003\u547d\u53bb\u5427\uff01\u201d \u3000\u3000\u770b\u7533\u96ea\u541b\u8bf4\u7684\u5341\u5206\u8ba4\u771f\uff0c\u6c5f\u67ab\u5fc3\u4e2d\u5fd6\u9053\uff1a\u201c\u90a3\u5c31\u66f4\u8981\u9664\u53bb\u4ed6\u4eec\u4e86\uff0c\u7559\u4e0b\u4ed6\u4eec\uff0c\u5c82\u4e0d\u662f\u4e00\u5927\u7978\u60a3\u3002\u201d \u3000\u3000\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u7a81\u7136\u5927\u559d\u4e00\u58f0\uff0c\u7fe0\u7389\u5200\u7a81\u7136\u653b\u5411\u4e00\u4e2a\u5251\u58eb\u3002 \u3000\u3000\u7fe0\u7389\u5200\u5316\u4f5c\u4e00\u9053\u78a7\u8292\uff0c\u7535\u5c04\u800c\u51fa\u3002 \u3000\u3000\u8fd9\u4e00\u5200\u770b\u4f3c\u5e73\u8c08\uff0c\u4f46\u5374\u5a01\u529b\u65e0\u7a77\uff0c\u90a3\u5251\u571f\u4e3e\u5251\u4e00\u5c01\uff0c\u7adf\u7136\u672a\u80fd\u5c01\u4f4f\u3002 \u3000\u3000\u78a7\u5149\u4e2d\u5c04\u51fa\u4e86\u4e00\u9053\u8840\u6cc9\u3002 \u3000\u3000\u7389\u5200\u6b63\u4e2d\u54bd\u5589\u3002 \u3000\u3000\u6c5f\u67ab\u8fd9\u7a81\u7136\u4e00\u51fb\uff0c\u5201\u9e4f\u6709\u7740\u5927\u51fa\u610f\u5916\u4e4b\u611f\uff0c\u5fcd\u4e0d\u4f4f\u53eb\u4e86\u4e00\u58f0\uff01 \u3000\u3000\u7533\u96ea\u541b\u5374\u5931\u58f0\u53eb\u9053\uff1a\u201c\u7a7f\u5589\u4e00\u5251\uff0c\u4f60\u2026\u2026\u4f60\u662f\u91d1\u4e39\u4e66\u751f\u7684\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u201c\u5f1f\u5b50\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u795e\u60c5\u8083\u7136\u5730\u8bf4\u9053\uff1a\u201c\u8001\u524d\u8f88\u4e5f\u8ba4\u8bc6\u4ed6\u4e86\uff1f\u201d \u3000\u3000\u201c\u4f60\u4e5f\u627f\u7ee7\u4e86\u91d1\u4e39\u4e66\u751f\u7684\u8863\u94b5\u2026\u2026\u201d \u3000\u3000\u7533\u96ea\u541b\u4f3c\u662f\u7a81\u7136\u9677\u5165\u4e86\u4e00\u79cd\u6c89\u75db\u7684\u56de\u5fc6\u4e2d\uff0c\u8138\u8272\u82cd\u767d\uff0c\u808c\u8089\u98a4\u52a8\uff0c\u5185\u5fc3\u4e4b\u4e2d\u53d7\u5230\u7684\u51b2\u51fb\uff0c\u4f3c\u662f\u5f88\u5927\u3002 \u3000\u3000\u201c\u4e0d\u9519\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u665a\u8fdb\u627f\u7ee7\u4ed6\u4eec\u7684\u6280\u827a\uff0c\u4e5f\u627f\u7ee7\u4e86\u4ed6\u4eec\u7684\u5fc3\u613f\u3002\u201d \u3000\u3000\u201c\u4ed6\u4eec\u6709\u4ec0\u4e48\u5fc3\u613f\uff0c\u2026\u2026\u201d\u7533\u96ea\u541b\u9053\uff1a\u201c\u5bf9\u6211\u62a5\u590d\uff0c\u8fd8\u662f\u8981\u6740\u6389\u590f\u5929\u540c\uff01\u201d \u3000\u3000\u201c\u6ca1\u6709\uff0c\u4ed6\u4eec\u7684\u665a\u5e74\u4fee\u517b\uff0c\u90fd\u5df2\u8fdb\u5165\u4e86\u5316\u5883\uff0c\u80f8\u4e2d\u65e9\u5df2\u4e0d\u8bb0\u4ec7\u6068\uff0c\u90a3\u91cc\u8fd8\u4f1a\u6709\u6740\u4eba\u7684\u5ff5\u5934\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u4ed6\u4eec\u5ff5\u5ff5\u4e0d\u5fd8\u7684\u662f\u6b66\u6797\u6b63\u4e49\u3001\u6c5f\u6e56\u5927\u5c40\uff0c\u4ed6\u4eec\u7684\u5fc3\u613f\u5c31\u662f\u6c5f\u6e56\u91cd\u5149\uff0c\u518d\u89c1\u5929\u65e5\uff0c\u8001\u524d\u8f88\u662f\u4ed6\u4eec\u7684\u6545\u65e7\uff0c\u5e0c\u671b\u770b\u5728\u4e24\u4f4d\u8001\u524d\u8f88\u7684\u751f\u524d\u4ea4\u60c5\uff0c\u52a9\u665a\u8fdb\u4e00\u81c2\u4e4b\u529b\uff0c\u5b8c\u6210\u4ed6\u4eec\u7684\u9057\u5fd7\u2026\u2026\u201d \u3000\u3000\u201c\u4f60\u60f3\u7684\u5f88\u597d\uff01\u6211\u4e2a\u4eba\u4e5f\u6709\u8fd9\u4efd\u610f\u613f\uff0c\u53ef\u60dc\uff0c\u6211\u4e0d\u80fd\u2026\u2026\u201d\u7533\u96ea\u541b\u9053\uff1a\u201c\u4f60\u51e0\u65f6\u89c1\u8fc7\uff0c\u6709\u4eba\u62ff\u7740\u5bf9\u6297\u654c\u4eba\u7684\u5200\u3001\u5251\uff0c\u6765\u6740\u5bb3\u81ea\u5df1\uff1f\u201d \u3000\u3000\u201c\u539f\u6765\uff0c\u4f60\u5c31\u662f\u5236\u9020\u8fd9\u4e00\u6b21\u6c5f\u6e56\u5927\u52ab\u7684\u4eba\uff1f\u2026\u2026\u201d\u6c5f\u67ab\u795e\u60c5\u51b7\u8083\u5730\u8bf4\uff0c\u201c\u6709\u4e9b\u4e8b\uff0c\u8e0f\u7834\u94c1\u978b\u65e0\u89c5\u5904\uff0c\u4f46\u9e3f\u8fd0\u5f53\u5934\u65f6\uff0c\u5374\u53c8\u5f97\u6765\u5168\u4e0d\u8d39\u5de5\u592b\uff01\u201d \u3000\u3000\u201c\u4e0d\u8981\u60f3\u7684\u592a\u7b80\u5355\uff0c\u6211\u4e0d\u8fc7\u662f\u5176\u4e2d\u4e00\u4e2a\u5fae\u4e0d\u8db3\u9053\u7684\u5c0f\u4eba\u7269\uff0c\u4f46\u5bf9\u4f60\u6765\u8bf4\uff0c\u5374\u662f\u5927\u5927\u7684\u4e0d\u5999\u4e86\uff0c\u2026\u2026\u201d\u7533\u96ea\u541b\u9053\uff1a\u201c\u4f60\u5fc5\u987b\u5148\u95ef\u8fc7\u8fd9\u516b\u4e2a\u5251\u58eb\u56f0\u6740\u2026\u2026\u201d \u3000\u3000\u201c\u5728\u4e0b\u539f\u4e5f\u6ca1\u6709\u9003\u907f\u7684\u6253\u7b97\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u4f60\u53ef\u4ee5\u4e0b\u4ee4\u5979\u4eec\u51fa\u624b\u4e86\uff01\u201d \u3000\u3000\u7533\u96ea\u541b\u4f3c\u662f\u7a81\u7136\u53d7\u5230\u4e86\u4e00\u4e0b\u9488\u523a\u822c\uff0c\u5168\u8eab\u98a4\u52a8\u4e86\u4e00\u4e0b\uff0c\u53f3\u624b\u6325\u51fa\u558a\u4e86\u4e00\u4e2a\u6740\u5b57\uff0c\u5ffd\u7136\u8f6c\u8fc7\u8eab\u5b50\uff0c\u5feb\u6b65\u800c\u53bb\u3002 \u3000\u3000\u6c5f\u67ab\u6ca1\u6709\u673a\u4f1a\u770b\u6e05\u695a\u8fc7\u7a0b\uff0c\u56e0\u4e3a\u4e03\u4e2a\u5251\u571f\uff0c\u5df2\u5c55\u5f00\u4e86\u51cc\u5389\u7684\u653b\u52bf\u3002 \u3000\u3000\u4ee5\u5201\u9e4f\u89c1\u8bc6\u4e4b\u5e7f\uff0c\u640f\u6740\u4e4b\u591a\uff0c\u4e5f\u6ca1\u6709\u89c1\u5982\u6b64\u51f6\u5389\u7684\u653b\u52bf\uff0c\u50cf\u4e03\u9053\u66b4\u8d77\u7684\u95ea\u5149\uff0c\u4e03\u9053\u5bd2\u8292\uff0c\u540c\u65f6\u653b\u5411\u6c5f\u67ab\u3002 \u3000\u3000\u4e03\u53ea\u5251\u5728\u63a5\u8fd1\u6c5f\u67ab\u65f6\uff0c\u7a81\u7136\u5e7b\u5316\u51fa\u4e00\u7247\u5149\u5f71\uff0c\u5149\u7ec7\u6210\u4e00\u7247\u5251\u5e55\uff0c\u63b7\u5411\u6c5f\u67ab\u3002 \u3000\u3000\u90a3\u771f\u662f\u6ef4\u6c34\u4e0d\u9732\u7684\u4e00\u51fb\uff0c\u4ee4\u4eba\u773c\u82b1\u7f2d\u4e71\uff0c\u83ab\u53ef\u9884\u6d4b\u3002 \u3000\u3000\u4e5f\u662f\u65e0\u6cd5\u95ea\u907f\u7684\u4e00\u6b21\u5b8c\u5584\u653b\u52bf\u3002 \u3000\u3000\u6c5f\u67ab\u6ca1\u6709\u95ea\u907f\uff0c\u56e0\u4e3a\uff0c\u6ca1\u6709\u4efb\u4f55\u4e00\u79cd\u8eab\u6cd5\u80fd\u591f\u95ea\u907f\u5f00\u8fd9\u4e48\u7ef5\u5bc6\u7684\u4e00\u51fb\u3002 \u3000\u3000\u552f\u4e00\u7684\u529e\u6cd5\uff0c\u5c31\u662f\u5168\u529b\u627f\u63a5\u4e0b\u6765\u3002 \u3000\u3000\u6c5f\u67ab\u98de\u8eab\u800c\u8d77\uff0c\u8fce\u5411\u5251\u5e55\u3002 \u3000\u3000\u4eba\u5728\u98de\u8d77\u7684\u540c\u65f6\uff0c\u4e00\u9053\u78a7\u5149\u7ed5\u8eab\u800c\u8d77\u3002 \u3000\u3000\u5c31\u662f\u90a3\u4e48\u4e00\u8dc3\u4e4b\u95f4\uff0c\u7fe0\u7389\u5200\u5df2\u548c\u4eba\u5408\u800c\u4e3a\u4e00\u3002 \u3000\u3000\u4f46\u95fb\u4e00\u9635\u91d1\u7389\u4ea4\u51fb\u7684\u8106\u54cd\uff0c\u6709\u5982\u73e0\u8d70\u7389\u76d8\uff0c\u4e00\u5927\u7247\u95ea\u5149\u7684\u767d\u8292\u73af\u56f4\u7740\u4e00\u78a7\u5149\u5728\u95e8\u8f6c\u3001\u6d41\u52a8\u3002 \u3000\u3000\u8fd9\u90a3\u91cc\u50cf\u7528\u5200\u3001\u5251\u5728\u62fc\u6597\uff0c\u5012\u50cf\u4e00\u6b21\u6781\u96be\u89c1\u5230\u7684\u81ea\u7136\u666f\u89c2\uff0c\u4e00\u5927\u4e00\u5c0f\u4e24\u56e2\u6eda\u52a8\u7684\u5149\u8292\u5728\u51b2\u51fb\u3002 \u3000\u3000\u867d\u7136\u5200\u5251\u90fd\u63e1\u5728\u4ed6\u4eec\u7684\u624b\u4e2d\uff0c\u4f46\u7cbe\u5999\u719f\u7ec3\u7684\u5200\u5251\u62db\u672f\u53d8\u5316\uff0c\u5df2\u5230\u4e86\u5200\u3001\u5251\u4e4b\u6700\u9ad8\u7684\u5883\u754c\uff0c\u50cf\u4f20\u8bf4\u4e2d\u7684\u5251\u4ed9\u4e00\u822c\uff0c\u5200\u3001\u5251\u4eba\u5408\u800c\u4e3a\u4e00\u3002 \u3000\u3000\u5201\u9e4f\u770b\u5446\u4e86\u3002 \u3000\u3000\u7533\u96ea\u7fa4\u4e5f\u770b\u5446\u4f4f\u4e86\u3002 \u3000\u3000\u8fd9\u516b\u4e2a\u7ecf\u8fc7\u957f\u671f\u8bad\u7ec3\u800c\u6210\u7684\u5251\u58eb\uff0c\u5728\u5979\u7684\u5fc3\u76ee\u4e2d\u65e0\u575a\u4e0d\u6467\uff0c\u4ed6\u4eec\u662f\u7cbe\u6311\u7ec6\u9009\uff0c\u5343\u4e2d\u5bfb\u4e00\u7684\u5b66\u5251\u4eba\u624d\uff0c\u518d\u7ecf\u8bad\u7ec3\u6dd8\u6c70\uff0c\u4e00\u767e\u591a\u4eba\uff0c\u5165\u9009\u4e3a\u7ea2\u5e26\u5251\u571f\u7684\u53ea\u4e0d\u8fc7\u4e00\u5341\u516b\u4eba\uff0c\u9664\u4e24\u4e2a\u6210\u5c31\u7279\u9ad8\uff0c\u53e6\u6709\u4efb\u52a1\u5916\uff0c\u4f59\u4e0b\u5341\u516d\u4eba\u5206\u4f5c\u4e24\u7ec4\uff0c\u6bcf\u7ec4\u516b\u4eba\u3002 \u3000\u3000\u4ee5\u4ed6\u4eec\u5251\u672f\u4e4b\u7cbe\uff0c\u5b9e\u5df2\u81f3\u7edd\u9876\u9ad8\u624b\u7684\u5883\u754c\uff0c\u5929\u4e0b\u7528\u5251\u9ad8\u4eba\uff0c\u4ee5\u4e00\u5bf9\u4e00\u7684\uff0c\u5df2\u7136\u4e0d\u591a\uff0c\u80fd\u4ee5\u4e00\u5bf9\u516b\u7684\uff0c\u662f\u7edd\u65e0\u4ec5\u6709\u4e86\u3002 \u3000\u3000\u4f46\u6c5f\u67ab\u5c31\u662f\u7edd\u65e0\u4ec5\u6709\u4e2d\u7684\u4e00\u4e2a\uff0c\u5c31\u90a3\u4e48\u5de7\u5999\u7684\u78b0\u4e0a\u4e86\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u8fd9\u4e0d\u662f\u5de7\u5408\uff0c\u51e0\u4e4e\u662f\u5fc5\u7136\u7684\u7ed3\u679c\uff0c\u53cc\u65b9\u90fd\u82b1\u4e86\u5341\u51e0\u5e74\u7684\u5fc3\u529b\uff0c\u5728\u826f\u5e08\u4e25\u5389\u7684\u7763\u4fc3\u4e0b\uff0c\u4e60\u7ec3\u51fa\u4e00\u6d41\u7684\u6b66\u529f\uff0c\u8981\u5bf9\u6297\u7684\u4eba\u7269\uff0c\u4e5f\u5c06\u662f\u4e00\u6d41\u7684\u5f3a\u654c\uff0c\u7533\u96ea\u541b\u56e0\u9707\u4e8e\u97e9\u9738\u4e4b\u5a01\uff0c\u624d\u5e26\u4e86\u8fd9\u7b2c\u4e00\u6d41\u7684\u5251\u58eb\u8d76\u6765\uff0c\u4e0d\u60dc\u6cc4\u9732\u51fa\u5929\u9a6c\u603b\u5802\u4e2d\u9690\u85cf\u7684\u79d8\u5bc6\u5b9e\u529b\u2026\u2026 \u3000\u3000\u5f53\u7136\uff0c\u5185\u5bab\u4e00\u7cfb\u4e2d\u4eba\u82e6\u82e6\u76f8\u903c\uff0c\u5929\u9a6c\u5802\u9a6c\u51c6\u5907\u4eae\u51fa\u4e00\u4e9b\u5b9e\u529b\uff0c\u8ba9\u5bf9\u65b9\u77e5\u96be\u800c\u9000\uff0c\u5374\u4e0d\u6599\uff0c\u9047\u4e0a\u4e86\u771f\u6b63\u7684\u5f3a\u654c\u3002 \u3000\u3000\u6c5f\u67ab\u7684\u201c\u7a7f\u5589\u4e00\u5200\u201d\u80fd\u4e00\u4e3e\u6740\u6b7b\u4e86\u4e00\u4e2a\u7b2c\u4e00\u6d41\u7684\u5251\u624b\uff0c\u56fa\u7136\u662f\u91d1\u4e39\u4e66\u751f\u7684\u5200\u6cd5\u7cbe\u5999\uff0c\u8be1\u5f02\u591a\u53d8\uff0c\u4ee4\u4eba\u96be\u9632\uff0c\u4f46\u6c5f\u67ab\u5982\u4e0d\u80fd\u628a\u5200\u6cd5\u7ec3\u5165\u5316\u5883\uff0c\u4e5f\u65e0\u6cd5\u4e00\u51fb\u53d6\u547d\u3002 \u3000\u3000\u4f46\u91d1\u4e39\u4e66\u751f\u52a0\u4e0a\u5929\u72d0\u5200\u6cd5\uff0c\u4e5f\u65e0\u6cd5\u6297\u62d2\u8fd9\u4e03\u5927\u5251\u58eb\u7684\u5408\u51fb\u3002 \u3000\u3000\u4f46\u6c5f\u67ab\u7adf\u7136\u80fd\u652f\u6491\u4e0b\u6765\u3002 \u3000\u3000\u7fe0\u7389\u5200\u548c\u4e03\u53ea\u7cbe\u94a2\u957f\u5251\uff0c\u649e\u51fb\u4e86\u6570\u767e\u6b21\u540e\uff0c\u505c\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u53cc\u65b9\u843d\u7740\u5b9e\u5730\u3002 \u3000\u3000\u6c5f\u67ab\u548c\u516d\u4e2a\u5251\u58eb\u4e00\u6837\uff0c\u9762\u8272\u82cd\u767d\uff0c\u4e0d\u505c\u7684\u5598\u606f\u3002 \u3000\u3000\u663e\u7136\uff0c\u53cc\u65b9\u90fd\u8017\u5c3d\u4e86\u4f53\u80fd\uff0c\u65e0\u6cd5\u518d\u652f\u6491\u4e0b\u53bb\uff0c\u4e0d\u5f97\u4e0d\u505c\u4e0b\u4f11\u606f\u3002 \u3000\u3000\u4e0d\u540c\u7684\u662f\u6c5f\u67ab\u4ee5\u4e00\u5bf9\u4e03\u3002 \u3000\u3000\u5982\u82e5\u6c5f\u67ab\u6709\u673a\u4f1a\u4e00\u5bf9\u4e00\u7684\u548c\u8fd9\u4e9b\u5251\u571f\u52a8\u624b\uff0c\u76f8\u4fe1\u6ca1\u6709\u4eba\u80fd\u907f\u5f00\u91d1\u4e39\u4e66\u751f\u7559\u4f20\u7684\u4e09\u5927\u7edd\u6280\u4e4b\u4e00\u201c\u7a7f\u5589\u4e00\u5200\u201d\u3002 \u3000\u3000\u7533\u96ea\u541b\u8138\u4e0a\u95ea\u52a8\u8d77\u6740\u673a\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u73b0\u5728\uff0c\u4f60\u8fd8\u80fd\u63a5\u6211\u4e00\u51fb\u5417\uff1f\u201d \u3000\u3000\u201c\u7533\u5927\u59d1\u5a18\uff0c\u8fd9\u65f6\u523b\uff0c\u4f60\u51fa\u624b\u5bf9\u4ed8\u4e00\u4e2a\u665a\u8fdb\uff0c\u4e0d\u6015\u843d\u4eba\u8bdd\u67c4\u5417\uff1f\u201d \u3000\u3000\u5927\u5385\u95e8\u53e3\u5904\uff0c\u51fa\u73b0\u4e86\u4e00\u4e2a\u7ea2\u5149\u6ee1\u9762\u7684\u9752\u888d\u5927\u6c49\uff0c\u63a5\u9053\uff1a\u201c\u5982\u82e5\u7533\u5927\u59d1\u5a18\u4e00\u5b9a\u8981\u51fa\u624b\uff0c\u53ea\u7531\u533a\u533a\u820d\u547d\u7275\u966a\u4e86\u3002\u201d \u3000\u3000\u7533\u96ea\u541b\u51b7\u5389\u7684\u76ee\u5149\uff0c\u76ef\u4f4f\u5728\u9752\u888d\u5927\u6c49\u7684\u8138\u4e0a\uff0c\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u201d \u3000\u3000\u663e\u7136\uff0c\u5979\u8ba4\u4e0d\u51fa\u8fd9\u5927\u6c49\u7684\u8eab\u4efd\u3002 \u3000\u3000\u4f46\u6c5f\u67ab\u5df2\u542c\u51fa\u6765\uff0c\u4ed6\u662f\u4e03\u5b9d\u548c\u5c1a\u88c5\u626e\u3002 \u3000\u3000\u5f20\u56db\u59d1\u6613\u5bb9\u672f\u679c\u7136\u662f\u7edd\u5999\u65e0\u65b9\uff0c\u7533\u96ea\u541b\u7684\u8fc7\u4eba\u773c\u529b\uff0c\u4e5f\u77a7\u4e0d\u51fa\u4e00\u70b9\u7834\u7efd\u3002 \u3000\u3000\u201c\u7533\u59d1\u5a18\u6700\u5927\u7684\u9519\u8bef\u4e4b\u4e00\uff0c\u662f\u4f4e\u4f30\u5929\u72d0\u592b\u4eba\u7684\u6280\u827a\uff0c\u5929\u72d0\u9488\u80fd\u501f\u98ce\u529b\u4f24\u4eba\u2026\u2026\u201d\u9752\u888d\u4eba\u6ca1\u6709\u56de\u7b54\u7533\u96ea\u541b\u7684\u95ee\u9898\uff0c\u5374\u81ea\u8bf4\u81ea\u8bdd\u7684\u63a5\u9053\uff1a\u201c\u4f60\u5e26\u6765\u4e03\u4e2a\u5251\u58eb\uff0c\u90fd\u5df2\u7ecf\u4f24\u5728\u5929\u72d0\u9488\uff0c\u4e24\u4e2a\u65f6\u8fb0\u5185\u4e0d\u65bd\u672f\u6551\u6cbb\uff0c\u4ed6\u4eec\u5c31\u6b7b\u4e86\uff0c\u4f60\u4e00\u4e3e\u95f4\u5931\u53bb\u4e86\u516b\u5927\u5251\u571f\uff0c\u8981\u5982\u4f55\u5411\u603b\u5802\u4e3b\u4ea4\u4ee3\u5462\uff1f\u201d \u3000\u3000\u7533\u96ea\u541b\u8138\u8272\u4e00\u53d8\uff0c\u9053\uff1a\u201c\u4ed6\u8bf4\u7684\u662f\u771f\u662f\u5047\uff1f\u201d \u3000\u3000\u201c\u662f\u771f\u7684\u2026\u2026\u201d\u4e00\u4e2a\u7ea2\u5e26\u5251\u58eb\u8bf4\uff0c\u201c\u6211\u4eec\u867d\u4e2d\u4e86\u5929\u72d0\u9488\uff0c\u4f46\u4ecd\u6709\u518d\u6218\u4e4b\u80fd\uff01\u201d \u3000\u3000\u7533\u96ea\u541b\u9053\uff1a\u201c\u90a3\u53ea\u6709\u52a0\u5feb\u4f60\u4eec\u6b7b\u4ea1\u7684\u901f\u5ea6\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u7ea2\u5e26\u5251\u571f\uff0c\u89c6\u6b7b\u5982\u5f52\uff01\u201d\u4e03\u4e2a\u5251\u58eb\uff0c\u540c\u65f6\u9ad8\u58f0\u56de\u7b54\u3002 \u3000\u3000\u201c\u5c31\u7b97\u4f60\u4eec\u613f\u610f\u6218\u6b7b\uff0c\u6211\u4e5f\u4e0d\u80fd\u8ba9\u4f60\u4eec\u6b7b\uff0c\u8ddf\u6211\u8d70\uff01\u201d\u98de\u8eab\u4e00\u8dc3\u3002 \u3000\u3000\u51fa\u4e86\u5927\u5385\u3002 \u3000\u3000\u4e03\u4e2a\u5251\u58eb\u540c\u65f6\u98d8\u8eab\u800c\u8d77\uff0c\u968f\u540e\u8ffd\u53bb\u3002 \u3000\u3000\u4f46\u89c1\u4eba\u5f71\u4e00\u95ea\uff0c\u5927\u5385\u4e00\u89d2\u5904\uff0c\u7a81\u7136\u95ea\u51fa\u4e86\u4e24\u6761\u4eba\u5f71\u3002 \u3000\u3000\u7adf\u662f\u4e24\u4e2a\u5c0f\u72d0\u5973\uff0c\u541f\u96ea\u3001\u541f\u971c\u3002 \u3000\u3000\u9752\u888d\u4eba\u5bf9\u4e8c\u5973\u70b9\u5934\u5fae\u7b11\u7740\uff0c\u884c\u5165\u4e86\u5927\u5385\u3002 \u3000\u3000\u201c\u679c\u7136\u662f\u5929\u72d0\u767e\u53d8\uff0c\u6211\u548c\u5c1a\u5c31\u6ca1\u6709\u77a7\u51fa\u6765\uff0c\u4f60\u4eec\u7684\u9690\u8eab\u6240\u5728\uff1f\u201d \u3000\u3000\u201c\u8fd9\u662f\u5f20\u59e8\u7684\u6f5c\u6728\u672f\u2026\u2026\u201d\u541f\u96ea\u9053\uff1a\u201c\u53ef\u4e0d\u662f\u5929\u72d0\u95e8\u7684\u6b66\u529f\u3002\u201d \u3000\u3000\u201c\u4e94\u884c\u6f5c\u672f\uff0c\u679c\u7136\u662f\u795e\u4e4e\u5176\u6280\uff0c\u6211\u548c\u5c1a\u4eca\u5929\u662f\u5927\u5f00\u773c\u754c\uff0c\u5fc3\u91cc\u60f3\u4e0d\u670d\u4e5f\u4e0d\u6210\u4e86\u3002\u201d \u3000\u3000\u201c\u548c\u5c1a\u4f2f\u4f2f\uff0c\u6709\u4e8b\u6c42\u6211\u4eec\u5c3d\u7ba1\u4ec0\u53e3\uff0c\u7528\u5c0f\u7740\u8fd9\u6837\u704c\u8ff7\u6c64\u4e86\u2026\u2026\u201d \u3000\u3000\u541f\u971c\u5fae\u7b11\u5982\u82b1\u5730\u8bf4\uff0c\u201c\u4e0d\u8fc7\uff0c\u6211\u5012\u8981\u5148\u8bf7\u6559\u548c\u5c1a\u4f2f\u4f2f\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u201c\u884c\uff01\u5341\u4ef6\u516b\u4ef6\u90fd\u884c\uff0c\u548c\u5c1a\uff0c\u201d\u63a5\u9053\uff1a\u201c\u4e00\u80a1\u8111\u513f\u8bf4\u51fa\u6765\u3002\u201d \u3000\u3000\u201c\u90a3\u4e03\u4e2a\u5251\u58eb\u7684\u5251\u6cd5\u5982\u4f55\uff1f\u201d \u3000\u3000\u201c\u9ad8\u660e\u65e0\u5339\uff0c\u5251\u672f\u4e4b\u7cbe\uff0c\u5df2\u7efc\u5408\u4e86\u5929\u4e0b\u5251\u6cd5\u4e4b\u957f\u2026\u2026\u201d \u3000\u3000\u201c\u4ed6\u4eec\u548c\u6c5f\u5927\u54e5\u4ea4\u63a5\u767e\u62db\uff0c\u4e0d\u5206\u80dc\u8d1f\uff0c\u5f53\u7136\u662f\u7b2c\u4e00\u6d41\u7684\u5251\u624b\u2026\u2026\u201d\u541f\u9738\u9053\uff1a\u201c\u4ed6\u4eec\u591a\u6b7b\u4e00\u4e2a\uff0c\u6c5f\u5927\u54e5\u5c31\u5c11\u4e86\u4e00\u4e2a\u52b2\u654c\uff0c\u4f60\u77e5\u9053\u4ed6\u4eec\u4e2d\u4e86\u5929\u72d0\u9488\uff0c\u5341\u4e8c\u4e2a\u65f6\u8fb0\u540e\uff0c\u9488\u968f\u884c\u8840\uff0c\u63d2\u5165\u5fc3\u810f\uff0c\u5fc5\u6b7b\u65e0\u7591\uff0c\u4f60\u4e3a\u4ec0\u4e48\u8981\u544a\u8bc9\u4ed6\u4eec\uff1f\u201d \u3000\u3000\u201c\u4e0d\u544a\u8bc9\u4ed6\u4eec\uff0c\u4ed6\u4eec\u4e5f\u5df2\u77e5\u9053\uff0c\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u9053\uff1a\u201c\u4f55\u51b5\uff0c\u5385\u95e8\u8ddd\u6b64\uff0c\u6709\u56db\u4e08\u4e4b\u9065\uff0c\u4f60\u4eec\u7684\u6c5f\u5927\u54e5\uff0c\u5df2\u5185\u529b\u8017\u5c3d\uff0c\u7533\u96ea\u541b\u771f\u8981\u51fa\u624b\uff0c\u4ed6\u672a\u5fc5\u80fd\u63a5\u5f97\u4e0b\u6765\uff0c\u548c\u5c1a\u4e5f\u6ca1\u6709\u81ea\u4fe1\u80fd\u53ca\u65f6\u8d76\u5230\u3002\u201d \u3000\u3000\u201c\u539f\u6765\u5982\u6b64\uff0c\u662f\u6211\u9519\u602a\u4f60\u4e86\uff0c\u2026\u2026\u201d\u541f\u971c\u7acb\u523b\u8ba4\u9519\u53c8\u9053\uff1a\u201c\u548c\u5c1a\u4f2f\u4f2f\u8981\u4ec0\u4e48\uff1f\u5429\u5490\u4e00\u58f0\u5427\uff01\u201d \u3000\u3000\u201c\u548c\u5c1a\u60f3\u5b66\u4e00\u70b9\u4e94\u884c\u6f5c\u672f\uff0c\u4e24\u4f4d\u59d1\u5a18\uff0c\u80fd\u5426\u6210\u5168\u5462\uff1f\u201d \u3000\u3000\u201c\u8fd9\u5c31\u5947\u602a\u4e86\u2026\u2026\u201d\u53f6\u96ea\u9053\uff1a\u201c\u4e94\u884c\u6f5c\u672f\u662f\u5f20\u59e8\u7684\u72ec\u95e8\u5947\u672f\uff0c\u6211\u4eec\u53ea\u662f\u521d\u5b66\u4e4d\u7ec3\uff0c\u548c\u5c1a\u4f2f\u4f2f\u53ea\u8981\u80af\u5f00\u53e3\uff0c\u6211\u4fdd\u8bc1\u5f20\u59e8\u4f1a\u4e00\u53e3\u5e94\u5141\u3002\u201d \u3000\u3000\u201c\u8fd9\u6837\u5427\uff01\u2026\u2026\u201d\u541f\u971c\u63a5\u9053\uff1a\u201c\u4f60\u4e0d\u597d\u610f\u601d\u5f00\u53e3\uff0c\u6211\u66ff\u4f60\u8bf4\u4e00\u58f0\uff0c\u800c\u4e14\u4fdd\u8bc1\u4f60\u5982\u613f\u4ee5\u507f\u3002\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u7a81\u7136\u7741\u5f00\u4e86\u773c\u775b\uff0c\u7b11\u9053\uff1a\u201c\u5927\u548c\u5c1a\u60f3\u5b66\u4f60\u4eec\u7684\u4e94\u884c\u6f5c\u672f\uff0c\u5fc5\u7136\u6709\u7edd\u6280\u56de\u62a5\uff0c\u4e24\u4f4d\u600e\u4e48\u5ffd\u7136\u95f4\u72d0\u6027\u6d88\u5931\uff0c\u53d8\u6210\u4e86\u7eaf\u6d01\u5c0f\u59d1\u5a18\u4e86\uff1f\u201d \u3000\u3000\u201c\u660e\u767d\u4e86\uff0c\u548c\u5c1a\u4f2f\u4f2f\u8981\u7528\u4ec0\u4e48\u4ea4\u6362\u5462\uff1f\u2026\u2026\u201d\u541f\u971c\u9053\uff1a\u201c\u6211\u4f20\u4f60\u6f5c\u6728\u672f\uff0c\u662f\u4e94\u884c\u6f5c\u672f\u4e2d\u65bd\u7528\u6700\u591a\u7684\u4e00\u884c\u3002\u201d \u3000\u3000\u201c\u6211\u4f20\u4f60\u6f5c\u571f\u6728\uff0c\u6f5c\u6c34\u672f\u2014\u2026\u3001\u201d\u541f\u96ea\u9053\uff1a\u201c\u81f3\u4e8e\u6f5c\u91d1\u3001\u6f5c\u706b\uff0c\u6211\u4eec\u81ea\u5df1\u8fd8\u672a\u7ec3\u597d\uff0c\u90a3\u91cc\u8fd8\u80fd\u4f20\u4eba\uff1f\u201d \u3000\u3000\u201c\u5b66\u4f1a\u4e86\u6c34\u3001\u6728\u3001\u571f\u4e09\u884c\u6f5c\u672f\uff0c\u5df3\u53ef\u884c\u904d\u5929\u4e0b\uff0c\u91d1\u3001\u706b\u4e8c\u672f\uff0c\u4e0d\u7ec3\u4e5f\u7f62\uff0c\u4e0d\u8fc7\uff0c\u548c\u5c1a\u53ef\u6ca1\u6709\u90a3\u4e48\u591a\u672c\u9886\u4ea4\u6362\uff0c\u53ea\u80fd\u4f20\u4e24\u4f4d\u59d1\u5a18\u4e00\u6bb5\u5c0f\u66f2\u513f\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u4ec0\u4e48\uff1f\u5b66\u5531\u6b4c\u5440\uff01\u548c\u5c1a\u4f2f\u4f2f\uff0c\u4f60\u53ef\u662f\u771f\u80fd\u6405\u548c\u5440\uff01\u2026\u2026\u201d\u541f\u971c\u9053\uff1a\u201c\u8fd9\u672c\u9886\uff0c\u4e0d\u7528\u4f60\u4f20\u6388\uff0c\u6211\u4eec\u5df2\u7ecf\u5531\u4e86\u5341\u51e0\u5e74\u4e86\u3002\u201d \u3000\u3000\u5979\u867d\u662f\u59b9\u59b9\uff0c\u4f46\u5374\u4e00\u5411\u7528\u5b57\u5c16\u9510\uff0c\u4e00\u9488\u89c1\u8840\uff0c\u540c\u6837\u4e00\u53e5\u8bdd\uff0c\u5151\u7684\u5c31\u6bd4\u541f\u96ea\u523b\u8584\u4e86\u3002 \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5c31\u662f\u542c\u5230\u4e24\u4f4d\u4e00\u526f\u597d\u6b4c\u5589\uff0c\u6211\u624d\u60f3\u6559\u4e24\u4f4d\u4e00\u6bb5\u66f2\u8bb0\u8bcd\u3002\u201d \u3000\u3000\u201c\u5979\u4eec\u884c\u4e48\u2026\u2026\u201d\u6c5f\u67ab\u795e\u60c5\u8083\u7136\u9053\uff1a\u201c\u9053\u4e0d\u540c\u4e0d\u76f8\u4e3a\u8c0b\uff0c\u6211\u5c31\u4e0d\u6210\uff0c\u2026\u2026\u201d \u3000\u3000\u201c\u662f\u7684\uff01\u4f60\u662f\u4e0d\u884c\uff0c\u4f46\u548c\u5c1a\u89c2\u67e5\u8fc7\u4e86\uff0c\u5979\u4eec\u4e24\u4e2a\u53ef\u4ee5\uff0c\u4e0d\u4f46\u53ef\u4ee5\uff0c\u800c\u4e14\uff0c\u53ef\u4ee5\u901f\u6210\uff0c\u4e09\u4e2a\u6708\uff0c\u5c31\u53ef\u4ee5\u6d3e\u4e0a\u7528\u573a\u4e86\u3002\u201d \u3000\u3000\u96ea\u3001\u971c\u4e8c\u5973\u867d\u7136\u4e0d\u77e5\u9053\u4e24\u4eba\u8c08\u7684\u4ec0\u4e48\uff1f \u3000\u3000\u4f46\u89c1\u6c5f\u67ab\u4e25\u8083\u7684\u795e\u60c5\uff0c\u5df2\u77e5\u662f\u91cd\u5927\u4e8b\u60c5\uff0c\u7acb\u523b\u6536\u655b\u8d77\u5a07\u6001\u5a9a\u7b11\uff0c\u6062\u590d\u4e86\u7aef\u58ee\u7684\u5c11\u5973\u5f62\u8c61\u3002 \u3000\u3000\u201c\u8fd9\u5c31\u53eb\u5728\u4e0b\u60f3\u4e0d\u901a\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u5929\u4e0b\u6ca1\u6709\u5341\u5168\u5341\u7f8e\u7684\u4eba\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u63a5\u9053\uff1a\u201c\u4f60\u5df2\u5341\u5f97\u5176\u4e5d\uff0c\u603b\u5f97\u7559\u70b9\u7f3a\u9677\u7f8e\u5427\uff01\u201d \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u8fd9\u53ef\u662f\u4e00\u4ef6\u2026\u2026\u201d \u3000\u3000\u201c\u4e00\u4ef6\u52a8\u4eba\u7684\u8bcd\u66f2\uff0c\u80fd\u4f7f\u4eba\u5fc3\u65f7\u795e\u6021\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u6253\u65ad\u4e86\u6c5f\u67ab\u7684\u8bdd\uff0c\u63a5\u9053\uff1a\u201c\u7531\u4e24\u4f4d\u5929\u8d4b\u4e0a\u4f73\u6b4c\u5589\u7684\u5c0f\u59d1\u5a18\u5531\u51fa\u6765\uff0c\u4e00\u5b9a\u66f4\u4e3a\u52a8\u4eba\uff01\u201d \u3000\u3000\u4e8c\u5973\u7edd\u9876\u806a\u660e\uff0c\u5fc3\u4e2d\u65e9\u5df2\u6709\u6570\uff0c\u5927\u548c\u5c1a\u4f20\u6388\u7684\u5b9a\u662f\u60ca\u5929\u52a8\u5730\u7684\u7edd\u6280\uff0c\u7acb\u523b\u8eac\u8eab\u4e00\u793c\uff0c\u9053\uff1a\u201c\u6211\u4eec\u77e5\u9519\u4e86\uff0c\u5927\u5e08\u6210\u5168\u4e4b\u6069\uff0c\u7edd\u4e0d\u5fd8\u6000\u3002\u201d \u3000\u3000\u201c\u8fd9\u5c31\u4e0d\u6562\u4e86\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u9053\uff1a\u201c\u548c\u5c1a\u75f4\u957f\u51e0\u5c81\uff0c\u53eb\u6211\u58f0\u548c\u5c1a\u4f2f\u4f2f\uff0c\u548c\u5c1a\u6597\u80c6\u627f\u8ba4\u4e86\uff0c\u4f46\u5982\u53eb\u6211\u5927\u5e08\uff0c\u90a3\u5c31\u6709\u4e9b\u9ad8\u62ac\u6211\u4e86\uff0c\u8bba\u6280\u827a\uff0c\u54b1\u4eec\u5e73\u8f88\u8bba\u4ea4\uff0c\u4f55\u51b5\uff0c\u548c\u5c1a\u6559\u4f60\u4eec\u4e00\u6bb5\u8bcd\u66f2\uff0c\u6362\u5230\u4e86\u4e09\u884c\u6f5c\u672f\uff0c\u600e\u4e48\u7b97\u90fd\u4e0d\u5403\u4e8f\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u6211\u5df2\u5927\u4f53\u6062\u590d\u4e86\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u4f60\u4eec\u53bb\u4e92\u4f20\u6280\u827a\uff0c\u6211\u8be5\u56de\u623f\u53bb\u597d\u597d\u4f11\u606f\u4e0b\u4e86\u3002\u201d \u3000\u3000\u201c\u8fd8\u6709\u4e24\u4e2a\u6740\u624b\u6ca1\u6709\u73b0\u8eab\uff0c\u2026\u2026\u201d\u541f\u96ea\u9053\uff1a\u201c\u5c0f\u5fc3\u4e9b\u554a\uff01\u6211\u548c\u59b9\u8ddf\u7740\u5927\u5e08\u5b66\u5531\u6b4c\uff0c\u6682\u65f6\u4e0d\u80fd\u4fdd\u62a4\u4f60\u4e86\uff01\u201d \u3000\u3000\u201c\u80e1\u6c0f\u7236\u5b50\uff0c\u6bb5\u6c0f\u5e08\u5f92\uff0c\u90fd\u5df2\u8131\u80ce\u6362\u9aa8\uff0c\u8fd9\u70b9\u4e8b\uff0c\u4ed6\u4eec\u62c5\u5f97\u8d77\u6765\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u7b11\u9053\uff1a \u3000\u3000\u201c\u4f55\u51b5\uff0c\u8fd8\u6709\u5f20\u56db\u59d1\uff0c\u2026\u2026\u201d \u3000\u3000\u201c\u5f88\u4e0d\u597d\u7684\u6d88\u606f\uff0c\u4e09\u5bab\u4e3b\u5230\u4e86\u957f\u5b89\uff0c\u5929\u738b\u95e8\u4eba\uff0c\u4e5f\u4f4f\u8fdb\u5165\u6d1b\u9633\u5c45\u4e86\u2026\u2026\u201d\u5f20\u56db\u59d1\u4e00\u8eab\u9ed1\u8863\uff0c\u75be\u5954\u800c\u5165\u3002 \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u5bf9\u5201\u9e4f\u4e00\u6325\u624b\uff0c\u5201\u9e4f\u6025\u6025\u9000\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u201c\u4ed6\u4eec\u662f\u5426\u89c1\u8fc7\u7533\u96ea\u541b\uff1f\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u8fd9\u4e48\u4e00\u4e2a\u5de7\u6cd5\uff0c\u4f1a\u4e0d\u4f1a\u4e09\u65b9\u9762\u65e9\u5df2\u6709\u4e86\u9884\u7ea6\uff1f\u201d \u3000\u3000\u201c\u5e94\u8be5\u4e0d\u4f1a\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u7533\u96ea\u541b\u5e26\u6765\u4e86\u5f3a\u5927\u7684\u5b9e\u529b\uff0c\u5927\u6982\u662f\u6709\u610f\u8981\u70ab\u9732\u4e00\u4e0b\uff0c\u4f46\u5979\u6ca1\u6709\u60f3\u5230\u4f1a\u78b0\u4e0a\u4f60\u8fd9\u4e48\u4e00\u4e2a\u624e\u624b\u4eba\u7269\uff0c\u95f9\u5f97\u7070\u5934\u571f\u8138\uff0c\u72fc\u72c8\u800c\u9003\uff0c\u5979\u5fc3\u4e2d\u6700\u91cd\u8981\u7684\u4e00\u4ef6\u4e8b\uff0c\u5927\u6982\u5c31\u662f\u5c3d\u5feb\u628a\u6d88\u606f\u4f20\u56de\u5929\u9a6c\u603b\u5802\u53bb\u3002\u201d \u3000\u3000\u201c\u5927\u59d0\uff0c\u53ef\u6709\u4e86\u5e94\u5bf9\u4e4b\u7b56\uff1f\u2026\u2026\u201d \u3000\u3000\u201c\u6682\u4e0d\u62db\u60f9\u5929\u738b\u95e8\uff0c\u53ea\u8981\u4ed6\u4e0d\u627e\u4e0a\u95e8\u6765\u751f\u4e8b\uff0c\u6211\u4eec\u5c31\u5c3d\u91cf\u5bb9\u5fcd\uff0c\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u5c31\u7b97\u627e\u4e0a\u95e8\uff0c\u4e5f\u4e0d\u8981\u5f62\u6210\u5bf9\u6297\uff0c\u90a3\u4f1a\u7acb\u523b\u9020\u6210\u4ed6\u4eec\u4e09\u65b9\u8054\u624b\u7684\u53ef\u80fd\uff0c\u4f60\u5c3d\u91cf\u8eb2\u8d77\u6765\uff0c\u7531\u5927\u548c\u5c1a\u6765\u5e94\u4ed8\uff0c\u6211\u77e5\u9053\u548c\u5c1a\u88c5\u4f5c\u3001\u8bba\u8bb9\u7684\u672c\u9886\uff0c\u5531\u4f5c\u4ff1\u4f73\u3002\u201d \u3000\u3000\u201c\u5938\u5956\u3001\u5938\u5956\uff0c\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u63a5\u9053\uff1a\u201c\u4f46\u548c\u5c1a\u4f69\u670d\u9ad8\u89c1\uff0c\u5c11\u4e86\u5929\u738b\u95e8\u4ece\u4e2d\u7275\u7ebf\uff0c\u5185\u5bab\u4e00\u7cfb\u4e2d\u4eba\uff0c\u8fd8\u4e0d\u4fbf\u548c\u5929\u9a6c\u5802\u8a00\u5f52\u4e8e\u597d\uff0c\u53cc\u65b9\u4e5f\u65e0\u6cd5\u4ea4\u6362\u91cd\u8981\u7684\u60c5\u62a5\uff0c\u8fd9\u5c31\u7559\u7ed9\u4e86\u6211\u4eec\u5f88\u5927\u7684\u5468\u65cb\u7684\u4f59\u5730\uff0c\u6240\u4ee5\uff0c\u5bf9\u5185\u5bab\u4e00\u7cfb\u4e2d\u4eba\uff0c\u8981\u7a77\u8ffd\u731b\u6253\u2026\u2026\u201d \u3000\u3000\u201c\u5927\u59d0\u7684\u610f\u601d\u662f\uff0c\u8981\u4e00\u4e3e\u640f\u6740\u4e09\u5bab\u4e3b\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u53ea\u4e0d\u77e5\u6b64\u65f6\u662f\u5426\u65f6\u673a\u6070\u5f53\uff1f\u201d \u3000\u3000\u201c\u4f60\u5df2\u7ecf\u628a\u4e8b\u60c5\u63a8\u5230\u4e86\u60ac\u5d16\u8fb9\u7f18\uff0c\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u5df2\u7ecf\u65e0\u6cd5\u518d\u7ec4\u548c\u4e0b\u53bb\uff0c\u7533\u96ea\u541b\u53ea\u8981\u8bf4\u51fa\u4f60\u5e08\u627f\u8eab\u4efd\uff0c\u5929\u9a6c\u5802\u7acb\u523b\u4f1a\u52a8\u5458\u4e86\u5168\u90e8\u529b\u91cf\u5bf9\u4ed8\u4f60\uff0c\u800c\u4e14\uff0c\u4e5f\u4f1a\u5c3d\u53ef\u80fd\u548c\u5185\u5bab\u4e00\u7cfb\u4e2d\u4eba\u6c9f\u901a\uff0c\u4ee5\u4fbf\u96c6\u4e2d\u5168\u529b\u3002\u201d \u3000\u3000\u201c\u8bf4\u6765\u60ed\u6127\uff0c\u5c0f\u5f1f\u8fd8\u662f\u706b\u5019\u4e0d\u8db3\uff0c\u5fcd\u4e0d\u4e0b\u90a3\u4e00\u53e3\u6c14\uff0c\u88ab\u7533\u96ea\u541b\u51e0\u53e5\u8bdd\u6fc0\u51fa\u5fc3\u4e2d\u7684\u9690\u5bc6\u2026\u2026\u201d \u3000\u3000\u201c\u4f60\u4e5f\u4e0d\u7528\u81ea\u8d23\uff0c\u8001\u5b9e\u8bf4\uff0c\u9a97\u4e0d\u8fc7\u5979\u7684\uff0c\u5979\u5bf9\u91d1\u4e39\u4e66\u751f\u7684\u6280\u827a\u5341\u5206\u719f\u6089\uff0c\u4ed6\u4eec\u672c\u662f\u4e00\u5bf9\u60c5\u4eba\u2026\u2026\u201d \u3000\u3000\u5f20\u56db\u59d1\u4f3c\u662f\u77e5\u9053\u8bf4\u6e9c\u4e86\u5634\uff0c\u7a81\u7136\u4f4f\u53e3\u4e0d\u8a00\u3002 \u3000\u3000\u201c\u8bf4\u4e0b\u53bb\u554a\uff01\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u4e09\u4f4d\u5e08\u957f\u7684\u60c5\u4ec7\u7ea0\u7f20\uff0c\u6211\u4e5f\u542c\u8fc7\u4e00\u70b9\uff0c\u53ea\u4e0d\u8fc7\uff0c\u4ed6\u4eec\u8bf4\u7684\u542b\u84c4\uff0c\u6211\u53c8\u4e0d\u80fd\u8ffd\u95ee\uff0c\u77e5\u9053\u7684\u4e0d\u591f\u8be6\u5c3d\uff0c\u76ee\u4e0b\u6c5f\u6e56\u7684\u60c5\u52bf\uff0c\u4f3c\u662f\u5df2\u548c\u4ed6\u4eec\u4e2a\u4eba\u7684\u5f80\u4e8b\u60c5\u4ec7\uff0c\u7ea0\u7ed3\u4e00\u5904\uff0c\u6211\u5982\u4e0d\u77e5\u5185\u60c5\uff0c\u5c82\u4e0d\u662f\u6709\u5bb3\u5927\u5c40\u5417\uff1f\u201d \u3000\u3000\u201c\u8bf4\u7684\u6709\u7406\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u4e2a\u4eba\u4e4b\u95f4\u7684\u6069\u60c5\u662f\u975e\uff0c\u6709\u5f88\u591a\u4e0d\u8db3\u4e3a\u5916\u4eba\u9053\uff0c\u5929\u72d0\u592b\u4eba\u544a\u8bc9\u6211\u4e00\u4e9b\uff0c\u91d1\u4e39\u4e66\u751f\u4e5f\u548c\u6211\u4f5c\u4e00\u6b21\u957f\u8c08\uff0c\u4f46\u6211\u76f8\u4fe1\uff0c\u4ed6\u4eec\u4e00\u5b9a\u4f1a\u6709\u4e9b\u9690\u85cf\u3002\u201d \u3000\u3000\u201c\u5927\u59d0\uff0c\u6211\u60f3\u77e5\u9053\u7684\uff0c\u4e5f\u4e0d\u662f\u5168\u90e8\u7ec6\u8282\uff0c\u53ea\u662f\u4e00\u4e2a\u5927\u6982\u7684\u60c5\u5f62\uff0c\u8ba9\u6211\u4e86\u89e3\u5230\u4ed6\u4eec\u4e4b\u95f4\u7684\u7eb5\u6a2a\u5173\u7cfb\uff0c\u90a3\u5c31\u591f\u4e86\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u6c89\u541f\u4e86\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u5927\u548c\u5c1a\uff0c\u6211\u6765\u8bf4\uff0c\u5982\u82e5\u6709\u8bf4\u4e0d\u6e05\u695a\u7684\u5730\u65b9\uff0c\u7531\u4f60\u6765\u8865\u5145\u3002\u2026\u2026\u201d \u3000\u3000\u201c\u8fd9\u4e2a\uff0c\u548c\u5c1a\u65e0\u6cd5\u4ece\u547d\uff0c\u6211\u6ca1\u6709\u89c1\u8fc7\u91d1\u4e39\u4e66\u751f\uff0c\u548c\u5929\u72d0\u592b\u4eba\uff0c\u4e5f\u53ea\u6709\u5306\u5306\u4e00\u9762\u4e4b\u7f18\u3002\u2026\u2026\u201d \u3000\u3000\u201c\u4f46\u4f60\u8ba4\u8bc6\u65e0\u76f8\u5927\u5e08\uff0c\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u8fd9\u4f4d\u9ad8\u50e7\uff0c\u4ece\u6765\u662f\u91d1\u53e3\u96be\u5f00\uff0c\u6211\u89c1\u8fc7\u4ed6\u4e09\u6b21\u9762\uff0c\u4ed6\u5bf9\u6211\u70b9\u5934\u5fae\u7b11\u4e09\u6b21\uff0c\u8fde\u4e00\u53e5\u4e5f\u4e0d\u8ddf\u6211\u8bf4\u2026\u2026\u201d \u3000\u3000\u201c\u4f60\u53ef\u4ee5\u95ee\u4ed6\u5440\uff01\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u7b11\u9053\uff1a\u201c\u627e\u673a\u4f1a\u8981\u4ed6\u5f00\u53e3\uff0c\u5728\u4f60\u5f20\u59d1\u5a18\u6765\u8bf4\uff0c\u5e94\u8be5\u4e0d\u662f\u96be\u4e8b\uff1f\u201d \u3000\u3000\u201c\u6211\u8d39\u4e86\u65e0\u6570\u53e3\u820c\uff0c\u53ea\u542c\u5230\u4e00\u53e5\u4f5b\u53f7\uff0c\u2026\u2026\u201d\u5f20\u56db\u59d1\u8f6c\u5411\u6c5f\u67ab\uff0c\u95ee\u9053\uff1a\u201c\u4ed6\u4f20\u4f60\u6b66\u529f\uff0c\u5e94\u8be5\u8bf4\u8fc7\u4e0d\u5c11\u7684\u8bdd\uff0c\u4f60\u4eec\u4e4b\u95f4\uff0c\u53ef\u80fd\u662f\u8bf4\u8bdd\u6700\u591a\u7684\u4eba\u4e86\uff1f\u201d \u3000\u3000\u201c\u5e08\u7236\uff0c\u4f20\u6211\u6b66\u529f\u65f6\uff0c\u975e\u5e38\u7ec6\u5fc3\uff0c\u89e3\u8bf4\u4e5f\u5f88\u8be6\u5c3d\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u53f9\u53e3\u6c14\u53c8\u9053\uff1a\u201c\u4f46\u4ed6\u4ece\u6765\u6ca1\u6709\u548c\u6211\u8c08\u8bba\u8fc7\u6c5f\u6e56\u4e2d\u4e8b\uff0c\u5f53\u7136\uff0c\u4e5f\u6ca1\u6709\u8bf4\u8fc7\u4ed6\u79c1\u4eba\u7684\u4e8b\u60c5\u3002\u201d \u3000\u3000\u201c\u4ed6\u5982\u80af\u591a\u8bf4\u51e0\u53e5\u8bdd\uff0c\u4e5f\u8bb8\u4e8b\u60c5\u8fd8\u4e0d\u4f1a\u95f9\u6210\u8fd9\u6837\u4e00\u4e2a\u5c40\u9762\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e00\u70b9\uff0c\u548c\u5c1a\u4e0d\u6562\u82df\u540c\uff0c\u9634\u8c0b\u65e9\u5df2\u5728\u6697\u4e2d\u8fdb\u884c\uff0c\u4e5d\u534e\u76df\u7ea6\u8fc7\u7a0b\u4e2d\uff0c\u4ed6\u4eec\u4e09\u4f4d\u90fd\u5df2\u7ecf\u53d7\u5230\u4e86\u5f88\u91cd\u7684\u4f24\u5bb3\uff0c\u4f46\u4ed6\u5f3a\u8fd0\u5185\u529f\uff0c\u903c\u4f4f\u4f24\u52bf\uff0c\u4e0d\u8ba9\u53d1\u4f5c\uff0c\u9707\u4f4f\u4e86\u590f\u5929\u540c\u7b49\uff0c\u4f7f\u4ed6\u4eec\u4e0d\u6562\u5984\u52a8\uff0c\u4f46\u4e5f\u9020\u6210\u4e86\u4ed6\u4eec\u4e09\u4f4d\u65e0\u6cd5\u8865\u6551\u7684\u5185\u4f24\u3001\u529f\u529b\u635f\u5931\u5927\u534a\u3002\u2026\u2026\u201d \u3000\u3000\u53ea\u542c\u4e00\u9635\u6b65\u5c65\u4e4b\u58f0\uff0c\u80e1\u5143\u6025\u5954\u800c\u5165\u3002 \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u6709\u4e8b\u60c5\uff1f\u201d \u3000\u3000\u201c\u662f\uff01\u6885\u82b1\u59d1\u5a18\u6709\u6025\u4e8b\uff0c\u8bf7\u526f\u603b\u7ba1\u5230\u6885\u82b1\u5385\u4e2d\u4e00\u53d9\u3002\u201d \u3000\u3000\u201c\u6885\u82b1\u73b0\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u201c\u5927\u5385\u5916\u9762\u2026\u2026\u201d\u80e1\u5143\u9053\uff1a\u201c\u6b63\u5728\u548c\u9093\u603b\u7ba1\u8bf4\u8bdd\u3002\u201d \u3000\u3000\u201c\u5927\u6982\u662f\u4e09\u5bab\u4e3b\u4e5f\u5230\u4e86\u6d1b\u9633\u5c45\u3001\u8981\u89c1\u4f60\u4e00\u9762\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u4f60\u6709\u4ec0\u4e48\u6253\u7b97\uff1f\u201d \u3000\u3000\u201c\u5148\u53bb\u89c1\u89c1\u5979\u4e86\u2026\u2026\u201d\u6c5f\u67ab\u53cc\u76ee\u4e2d\u6740\u673a\u4e00\u95ea\uff0c\u4f4e\u58f0\u63a5\u9053\uff1a\u201c\u5982\u679c\u60c5\u52bf\u4e0d\u5229\uff0c\u6211\u5c31\u51fa\u624b\u6740\u4e86\u5979\u3002\u201d \u3000\u3000\u201c\u4e09\u5bab\u4e3b\u4e0d\u662f\u7b49\u95f2\u4e4b\u8f88\uff0c\u4e00\u51fb\u4e0d\u4e2d\uff0c\u2026\u2026\u201d\u5f20\u56db\u59a8\u9053\uff1a\u201c\u90a3\u5c31\u9020\u6210\u4e86\u8a93\u4e0d\u4e24\u7acb\u7684\u5c40\u9762\u4e86\u3002\u201d \u3000\u3000\u201c\u5927\u59d0\u4f4e\u4f30\u5c0f\u5f1f\u4e86\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u5982\u679c\u6211\u4e58\u4eba\u4e0d\u5907\uff0c\u7a81\u8d77\u53d1\u96be\uff0c\u5929\u4e0b\u96be\u591f\u907f\u5f00\u6211\u8fd9\u4e00\u51fb\u7684\u4eba\uff0c\u5927\u6982\u4e0d\u4f1a\u592a\u591a\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e00\u70b9\u6211\u76f8\u4fe1\uff0c\u4f46\u5982\u654c\u4eba\u6709\u5907\u5462\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u5fae\u5fae\u4e00\u6014\u3002 \u3000\u3000\u201c\u5144\u5f1f\u2026\u2026\u201d\u5f20\u56db\u59d1\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u4e2a\u97e9\u9738\u7684\u6076\u540d\uff0c\u65e9\u5df2\u4f20\u626c\u5728\u5916\uff0c\u90fd\u77e5\u9053\u4f60\u8eab\u8d1f\u4e00\u51fb\u53d6\u547d\u7684\u7edd\u6280\uff0c\u89c1\u4f60\u4e4b\u524d\uff0c\u5c82\u4f1a\u65e0\u5907\uff0c\u4e3a\u4ec0\u4e48\u4e00\u5b9a\u8981\u5728\u6b66\u529f\u4e0a\u62fc\u51fa\u80dc\u8d1f\u5462\uff1f\u201d \u3000\u3000\u201c\u5927\u59d0\u7684\u610f\u601d\u662f\u2026\u2026\u201d\u6c5f\u67ab\u82e5\u6709\u6240\u609f\u7684\u95ee\u3002 \u3000\u3000\u201c\u6362\u4e00\u79cd\u65b9\u5f0f\u5bf9\u4ed8\u5979\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u53d6\u4e0b\u4f60\u7684\u9762\u5177\u3002\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u61c2\u4e86\u2026\u2026\u201d\u541f\u971c\u63a5\u9053\uff1a\u201c\u6c5f\u5927\u54e5\uff0c\u5f20\u59e8\u8981\u4f60\u65bd\u5c55\u7f8e\u7537\u8ba1\uff01\u201d \u3000\u3000\u6c5f\u67ab\u53ea\u542c\u5f97\u8138\u4e0a\u53d1\u70ed\uff0c\u5e78\u597d\u7751\u4e0a\u6234\u4e86\u9762\u5177\u3002 \u3000\u3000\u201c\u6211\u8d5e\u6210\u5f20\u59e8\u7684\u4e3b\u610f\u2026\u2026\u201d\u541f\u96ea\u8bf4\uff0c\u201c\u52a8\u624b\u62fc\u547d\uff0c\u662f\u6700\u540e\u7684\u529e\u6cd5\uff0c\u4e0d\u6218\u800c\u5c48\u4eba\u4e4b\u5175\uff0c\u624d\u662f\u4e0a\u4e0a\u4e4b\u7b56\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u63a5\u9053\uff1a\u201c\u2018\u60d1\u5fc3\u672f\u2019\u5e94\u8be5\u53ef\u4ee5\u514b\u5236\u4f4f\u4e09\u5bab\u4e3b\u7684\u5a9a\u529f\uff0c\u5979\u662f\u5a9a\u672f\u9020\u8be3\u6700\u6df1\u7684\u9ad8\u624b\u4e4b\u4e00\uff0c\u4f60\u5982\u80fd\u80dc\u8fc7\u5979\uff0c\u90a3\u5c31\u8bc1\u660e\u4e86\u2018\u60d1\u5fc3\u672f\u2019\u786e\u4e3a\u5929\u4e0b\u7b2c\u4e00\u5947\u529f\u3002\u201d \u3000\u3000\u201c\u5982\u662f\u5c0f\u5f1f\u53cd\u4e3a\u5bf9\u65b9\u6240\u5236\u5462\uff1f\u201d \u3000\u3000\u201c\u90a3\u4e5f\u5c31\u8bc1\u660e\u4e86\u2018\u60d1\u5fc3\u672f\u2019\u5e76\u4e0d\u53ef\u6043\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u6211\u4eec\u5c3d\u5feb\u627e\u51fa\u4e00\u79cd\u53ef\u4ee5\u62d2\u6297\u5927\u654c\u7684\u6b66\u529f\u3002\u201d \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u5927\u59d0\uff0c\u4e16\u4e0a\u771f\u6709\u90a3\u79cd\u6b66\u529f\u4e48\uff1f\u201d \u3000\u3000\u201c\u6709\uff01\u4e0d\u8fc7\uff0c\u7ec3\u8d77\u6765\u727a\u7272\u592a\u5927\uff0c\u4f46\u60c5\u52bf\u8feb\u4eba\uff0c\u8bf4\u4e0d\u5f97\u53ea\u597d\u8ba4\u547d\u4e86\uff0c\u2026\u2026\u201d\u5f20\u56db\u59d1\u795e\u60c5\u8083\u7136\u5730\u8bf4\uff0c\u201c\u53bb\u5427\uff01\u8bd5\u8bd5\u60d1\u5fc3\u672f\uff0c\u5982\u82e5\u4e0d\u884c\uff0c\u7acb\u523b\u51fa\u624b\uff0c\u4e00\u4e3e\u51fb\u6740\u5979\u3002\u201d \u3000\u3000\u201c\u6211\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u6b32\u8a00\u53c8\u6b62\u5730\u4e3e\u6b65\u5411\u5916\u884c\u53bb\u3002 \u3000\u3000\u5f20\u56db\u59d1\u4f4e\u58f0\u5bf9\u4e8c\u5973\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u8d76\u53bb\u652f\u63f4\uff0c\u5982\u82e5\u4ed6\u7684\u60d1\u5fc3\u672f\u5236\u4e0d\u4f4f\u5bf9\u65b9\uff0c\u5c31\u4f1a\u88ab\u5bf9\u65b9\u6240\u5236\uff0c\u54b1\u4eec\u5c31\u7acb\u523b\u53d1\u52a8\u88ad\u51fb\uff0c\u8bb0\u4f4f\u51fa\u624b\u8981\u72e0\uff0c\u4e0d\u53ef\u7559\u60c5\uff0c\u4e00\u51fb\u4e0d\u4e2d\uff0c\u5c31\u4f1a\u5bb3\u4e86\u4f60\u4eec\u7684\u6c5f\u5927\u54e5\u3002\u201d \u3000\u3000\u541f\u96ea\u3001\u541f\u971c\u5e94\u4e86\u4e00\u58f0\uff0c\u98de\u8eab\u800c\u53bb\u3002 \u3000\u3000\u201c\u5927\u548c\u5c1a\uff0c\u63a5\u8fd1\u6885\u82b1\u5385\uff0c\u5355\u51ed\u9ad8\u660e\u7684\u8f7b\u529f\u4e0d\u884c\uff0c\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u4f60\u4e0d\u7528\u53bb\u5e2e\u5fd9\u4e86\uff0c\u56de\u53bb\u4f5c\u756a\u5e03\u7f72\uff0c\u4f5c\u6700\u574f\u7684\u6253\u7b97\uff0c\u628a\u6b27\u9633\u662d\u548c\u7f57\u5170\uff0c\u4e5f\u8c03\u8fdb\u6765\uff0c\u987a\u4fbf\u901a\u77e5\u4e00\u58f0\u8001\u53eb\u5316\u5b50\uff0c\u8fd9\u4e00\u4ed7\u6253\u4e0d\u8d62\uff0c\u5c31\u64a4\u51fa\u957f\u5b89\u57ce\u3002\u201d \u3000\u3000\u201c\u963f\u5f25\u9640\u4f5b\uff01\u4f46\u613f\u6c5f\u5c11\u4fa0\u80fd\u80dc\u5f97\u4e09\u5bab\u4e3b\uff0c\u4ed6\u4eec\u5982\u80fd\u5531\u51fa\u4e00\u540d\u91d1\u699c\u4e50\uff0c\u8fd9\u4e00\u6218\u5c31\u597d\u6253\u591a\u4e86\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u548c\u5c1a\u4ec0\u4e48\u90fd\u61c2\u5417\uff1f\u201d \u3000\u3000\u201c\u548c\u5c1a\u6ca1\u6709\u5403\u8fc7\u732a\u8089\uff0c\u53ef\u662f\u89c1\u8fc7\u732a\u8d70\u8def\u554a\uff01\u201d \u3000\u3000\u5f20\u56db\u59d1\u5fae\u5fae\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u6c5f\u67ab\u5360\u6709\u4e03\u6210\u80dc\u7b97\uff0c\u6211\u4eec\u96c6\u4e2d\u4eba\u624b\uff0c\u5e0c\u671b\u662f\u5907\u800c\u4e0d\u7528\u3002\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u8bf4\u5462\uff01\u5f20\u59d1\u5a18\u4ee4\u5982\u5c71\uff0c\u539f\u6765\u662f\u5fc3\u4e2d\u65e9\u6709\u6210\u7b97\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u4f4e\u58f0\u9053\uff1a\u201c\u4ed6\u662f\u7edf\u519b\u4e3b\u5e08\uff0c\u4e0d\u80fd\u8ba9\u4ed6\u53d7\u5230\u632b\u6298\uff0c\u90a3\u4f1a\u4f24\u4e86\u4ed6\u7684\u4fe1\u5fc3\u3002\u201d \u3000\u3000\u201c\u6211\u62c5\u5fc3\u7684\u662f\u4ed6\u81ea\u4fe1\u592a\u5f3a\uff0c\u9887\u6709\u76ee\u7a7a\u56db\u6d77\u7684\u6c14\u6982\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u6211\u5012\u5e0c\u671b\u4ed6\u9047\u5230\u4e00\u70b9\u632b\u6298\uff0c\u8ba9\u4ed6\u63d0\u9ad8\u4e00\u4e9b\u8b66\u89c9\uff0c\u5bf9\u5927\u5c40\uff0c\u4e5f\u8bb8\u66f4\u6709\u5e2e\u52a9\u3002\u201d \u3000\u3000\u201c\u5509\uff01\u5982\u8bba\u5355\u6253\u72ec\u6597\uff0c\u5929\u4e0b\u9ad8\u624b\uff0c\u80fd\u591f\u80dc\u8fc7\u4ed6\u7684\uff0c\u786e\u7136\u5f88\u96be\u627e\u51fa\u4e00\u4e2a\u6765\u4e86\uff0c\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u9053\uff1a\u201c\u4ed6\u8eab\u517c\u4e09\u5bb6\u4e4b\u957f\uff0c\u5c31\u7b97\u65e0\u76f8\u5927\u5e08\u590d\u751f\uff0c\u91d1\u4e39\u4e66\u751f\u8fd8\u9b42\uff0c\u5929\u72d0\u592b\u4eba\u518d\u73b0\u4eba\u4e16\uff0c\u4e5f\u65e0\u6cd5\u4e00\u5bf9\u4e00\u7684\u80dc\u5f97\u8fc7\u4ed6\u3002\u201d \u3000\u3000\u201c\u6700\u62c5\u5fc3\u7684\u5c31\u662f\u8fd9\u4e9b\u4e86\uff0c\u4ed6\u4e00\u628a\u7fe0\u7389\u5200\uff0c\u72ec\u6597\u516b\u4f4d\u5251\u571f\uff0c\u7533\u96ea\u541b\u73b0\u573a\u76ee\u7779\u3002\u2026\u2026\u201d \u3000\u3000\u201c\u662f\u7684\uff01\u5979\u672c\u6765\u4e0b\u4ee4\u51fa\u624b\u4e4b\u540e\uff0c\u4eba\u5df2\u79bb\u53bb\uff0c\u4e0d\u77e5\u4f55\u6545\uff0c\u7a81\u7136\u53c8\u56de\u5385\u4e2d\uff0c\u773c\u770b\u7740\u8fd9\u573a\u640f\u6740\u7ed3\u675f\u3002\u2026\u2026\u201d \u3000\u3000\u5f20\u56db\u59d1\u6253\u65ad\u4e86\u4e03\u5b9d\u548c\u5c1a\u7684\u8bdd\uff0c\u63a5\u9053\uff1a\u201c\u5c31\u5404\u65b9\u8d44\u6599\u7814\u5224\uff0c\u90a3\u4f4d\u5929\u9a6c\u603b\u5802\u7684\u5802\u4e3b\uff0c\u5927\u6982\u662f\u590f\u5929\u540c\u4e86\uff0c\u8fd9\u4e2a\u4eba\u667a\u8ba1\u767e\u51fa\uff0c\u5fc3\u72e0\u624b\u8fa3\uff0c\u5982\u82e5\u5f97\u5230\u4e86\u7533\u96ea\u541b\u7684\u62a5\u544a\uff0c\u5fc5\u4f1a\u5343\u65b9\u767e\u8ba1\u7684\u7b97\u8ba1\u6c5f\u67ab\u3002\u201d \u3000\u3000\u201c\u5bf9\uff01\u8fd9\u5012\u662f\u4e00\u4ef6\u5927\u9ebb\u70e6\u4e86\uff0c\u590f\u5929\u540c\u80fd\u4e00\u4e3e\u7b97\u8ba1\u4e86\u65e0\u76f8\u5927\u5e08\u3001\u91d1\u4e39\u4e66\u751f\u3001\u5929\u72d0\u592b\u4eba\uff0c\u4e09\u4f4d\u65f7\u4ee3\u5947\u4fa0\uff0c\u5168\u529b\u5bf9\u4ed8\u6c5f\u5c11\u4fa0\uff0c\u6211\u548c\u5c1a\u5c31\u4e0d\u77e5\u9053\u5982\u4f55\u53bb\u9632\u8303\uff01\u624d\u80fd\u4fdd\u62a4\u5230\u6c5f\u67ab\u7684\u5b89\u5168\u4e86\uff1f\u201d \u3000\u3000\u201c\u6240\u4ee5\uff0c\u54b1\u4eec\u8981\u7528\u5fc3\u8bbe\u8ba1\u4e00\u756a\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u529e\u6cd5\u4e5f\u4e0d\u662f\u6ca1\u6709\uff0c\u7b49\u6211\u81ea\u89c9\u60f3\u7684\u5b8c\u5168\u4e86\uff0c\u518d\u548c\u4f60\u4ed4\u7ec6\u7684\u5546\u91cf\u4e00\u4e0b\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u6211\u548c\u5c1a\u5168\u529b\u914d\u5408\uff0c\u6c34\u91cc\u706b\u91cc\u4e07\u6b7b\u4e0d\u8f9e\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u4e00\u626c\u624b\uff0c\u8f6c\u8eab\u5411\u4e00\u5835\u5899\u4e0a\u51b2\u53bb\u3002 \u3000\u3000\u660e\u660e\u662f\u4e00\u9053\u5899\uff0c\u4f46\u5f20\u56db\u59d1\u649e\u4e0a\u4e86\u5899\u58c1\uff0c\u4eba\u5374\u7a81\u7136\u6d88\u5931\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u201c\u597d\u9ad8\u660e\u7684\u6f5c\u571f\u672f\uff0c\u8c01\u8981\u5f97\u7f6a\u4e86\u8fd9\u4e48\u4e00\u4e2a\u4eba\u7269\uff0c\u5f53\u771f\u662f\u89c9\u4e5f\u7761\u4e0d\u5b89\u4e86\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u4eba\u5374\u98de\u8dc3\u800c\u8d77\uff0c\u9ad8\u5927\u7684\u8eab\u8eaf\uff0c\u6709\u5982\u8759\u874e\u5bb5\u98de\uff0c\u7adf\u7531\u4e00\u6247\u534a\u5f00\u7684\u5c0f\u7a97\u4e2d\u7a7f\u8d8a\u800c\u53bb\u3002 \u3000\u3000\u4ed6\u4e0d\u613f\u4ee5\u6539\u626e\u7684\u9762\u76ee\uff0c\u89c1\u5230\u9093\u98de\uff0c\u5c55\u4e86\u4e00\u6b21\u7edd\u4e16\u8f7b\u529f\uff0c\u80e1\u5143\u770b\u7684\u5446\u4f4f\u4e86\uff0c\u8fd9\u4e9b\u4eba\uff0c\u4e2a\u4e2a\u8eab\u8d1f\u7edd\u6280\u3002 \u3000\u3000\u518d\u8bf4\u6c5f\u67ab\u884c\u51fa\u5927\u5385\uff0c\u6885\u82b1\u5df2\u548c\u9093\u98de\u53cc\u53cc\u8fce\u4e86\u4e0a\u6765\u3002 \u3000\u3000\u201c\u662f\u4e0d\u662f\u4e09\u5bab\u4e3b\u7684\u5927\u9a7e\u5230\u4e86\u6d1b\u9633\u5c45\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u76ee\u7779\u6885\u82b1\uff0c\u9053\uff1a\u201c\u627e\u6211\u53bb\u5f53\u9762\u95ee\u7f6a\uff1f\u201d \u3000\u3000\u201c\u662f\u4e09\u5bab\u4e3b\u7684\u82b3\u9a7e\u5230\u4e86\u2026\u2026\u201d\u6885\u82b1\u9053\uff1a\u201c\u4f46\u662f\u4e0d\u662f\u5f53\u9762\u95ee\u7f6a\u3002\u6211\u5c31\u4e0d\u77e5\u9053\u4e86\uff0c\u4e0d\u8fc7\uff0c\u5979\u6307\u540d\u8981\u89c1\u4f60\uff0c\u6211\u53ea\u597d\u6765\u8bf7\u4e86\u3002\u201d \u3000\u3000\u9093\u98de\u4e00\u53ea\u78a7\u5149\u95ea\u95ea\u7684\u9b54\u773c\u4e2d\uff0c\u5947\u5149\u66b4\u5c04\uff0c\u770b\u7740\u6c5f\u67ab\uff0c\u4e5f\u4e0d\u77e5\u9053\u4ed6\u5fc3\u4e2d\u662f\u96be\u8fc7\uff0c\u8fd8\u662f\u6028\u5fff\uff0c\u60ca\u5947\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u9a97\u5f97\u6211\u597d\u82e6\u554a\uff01\u201d \u3000\u3000\u201c\u603b\u7ba1\u5fc3\u4e2d\u5982\u6709\u7591\u95ee\uff01\u7b49\u6211\u89c1\u8fc7\u4e09\u5bab\u4e3b\u56de\u6765\uff0c\u518d\u8be6\u4f5c\u89e3\u91ca\u5982\u4f55\uff1f\u201d \u3000\u3000\u201c\u8001\u5f1f\uff0c\u7528\u4e0d\u7740\u89e3\u91ca\u4e86\uff0c\u2026\u2026\u201d\u9093\u98de\u8bf4\uff0c \u3000\u3000\u201c\u6728\u5df2\u6210\u821f\uff0c\u8bf4\u4ec0\u4e48\u90fd\u662f\u7a7a\u8bdd\uff0c\u53ea\u5e0c\u671b\u4f60\u8001\u5f1f\u591a\u591a\u73cd\u91cd\uff0c\u4e0d\u4f46\uff0c\u6d1b\u9633\u5c45\u8fd9\u4e2a\u5c40\u9762\u8981\u4f60\u4fdd\u5168\uff0c\u6211\u4eec\u51e0\u5341\u6761\u6027\u547d\uff0c\u4e5f\u548c\u4f60\u606f\u606f\u76f8\u5173\u3002\u201d \u3000\u3000\u6c5f\u67ab\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u6d3b\u7684\u5f88\u597d\uff0c\u4e00\u70b9\u60f3\u6b7b\u7684\u611f\u89c9\u4e5f\u6ca1\u6709\uff0c\u53ea\u8981\u6211\u6d3b\u7740\uff0c\u5c31\u4f1a\u5168\u529b\u4fdd\u62a4\u603b\u7ba1\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u4f60\u81ea\u5df1\u4e5f\u8981\u5168\u529b\u6c42\u751f\u624d\u884c\uff0c\u81f3\u5c11\u5e94\u8be5\u6709\u53cd\u6297\u6b7b\u4ea1\u7684\u52c7\u6c14\u3002\u201d \u3000\u3000\u9093\u98de\u82e5\u6709\u6240\u609f\u7684\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u4f60\u4e0d\u5728\u65f6\uff0c\u7531\u90a3\u4e2a\u7a7f\u957f\u888d\u7684\u548c\u5c1a\u4f5c\u4e3b\u662f\u5417\uff1f\u201d \u3000\u3000\u201c\u539f\u6765\u603b\u7ba1\u65e9\u5c31\u77a7\u51fa\u6765\u4e86\uff0c\u2026\u2026\u201d \u3000\u3000\u9093\u98de\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u77a7\u51fa\u6765\u7684\u4e8b\u60c5\u591a\u4e86\uff0c\u2026\u2026\u201d\u770b\u4e86\u6885\u82b1\u4e00\u773c\uff0c\u63a5\u9053\uff1a\u201c\u4e0d\u8fc7\u59d1\u5a18\u53ef\u4ee5\u653e\u5fc3\uff0c\u6709\u4e9b\u4e8b\uff0c\u6211\u53ef\u662f\u4e00\u76f4\u6446\u5728\u5fc3\u91cc\uff0c\u5230\u4e86\u6211\u8fd9\u628a\u5e74\u7eaa\uff0c\u5b88\u53e3\u5982\u74f6\uff0c\u53ef\u4e0d\u662f\u4e00\u4ef6\u96be\u4e8b\u3002\u201d \u3000\u3000\u201c\u4f60\u597d\u50cf\u5728\u8bb9\u8bc8\u6211\u2026\u2026\u201d\u6885\u82b1\u9053\uff1a\u201c\u8bf4\u51fa\u6765\uff0c\u4f60\u7a76\u7adf\u770b\u5230\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u7530\u7389\u90a3\u5c0f\u5b50\u4e00\u53e3\u8c0e\u8a00\uff0c\u2026\u2026\u201d\u9093\u98de\u9053\uff1a\u201c\u9a97\u4e86\u5c0f\u59d1\u5a18\u7684\u611f\u60c5\uff0c\u4e5f\u5c31\u7f62\u4e86\uff0c\u8fde\u6211\u8001\u5934\u5b50\u4e5f\u4e0d\u653e\u8fc7\u3002\u2026\u2026\u201d \u3000\u3000\u201c\u6885\u82b1\uff0c\u7b97\u4e86\uff0c\u9b54\u773c\u7684\u7ef0\u53f7\uff0c\u5c82\u662f\u8ba9\u4eba\u81ea\u53eb\u7684\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u9093\u603b\u7ba1\uff0c\u6211\u548c\u6885\u82b1\u90fd\u4f1a\u627f\u4f60\u8fd9\u4efd\u60c5\uff0c\u56de\u53bb\u5427\uff01\u6709\u4e8b\u60c5\u53bb\u627e\u5927\u548c\u5c1a\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u201d\u9093\u98de\u5e94\u4e86\u4e00\u58f0\uff0c\u8f6c\u5411\u6885\u82b1\uff0c\u53cc\u624b\u4e00\u62b1\u624b\u9053\uff1a\u201c\u6885\u82b1\u59d1\u5a18\uff0c\u4f60\u90a3\u91cc\u6211\u4e0d\u4fbf\u53bb\uff0c\u97e9\u8001\u5f1f\u7684\u4e00\u5207\uff0c\u5c31\u5168\u4ef0\u4ed7\u59d1\u5a18\u62c5\u4ee3\u4e86\u3002\u201d \u3000\u3000\u6885\u82b1\u5fae\u5fae\u70b9\u4e86\u4e0b\u5934\uff0c\u5e7d\u5e7d\u5730\u8bf4\u4e86\u58f0\uff1a \u3000\u3000\u201c\u603b\u7ba1\u653e\u5fc3\u3002\u201d \u3000\u3000\u5176\u5b9e\uff0c\u9093\u98de\u90a3\u80fd\u653e\u5f97\u4e0b\u5fc3\uff0c\u5185\u5bab\u7684\u4e09\u5bab\u4e3b\u7a81\u7136\u4eb2\u4e34\u6d1b\u9633\u5c45\uff0c\u6b63\u610f\u5473\u7740\u5f88\u4e0d\u5bfb\u5e38\uff0c\u663e\u7136\u662f\u4e13\u4e3a\u5174\u5e08\u95ee\u7f6a\u800c\u6765\u3002 \u3000\u3000\u4f46\uff0c\u662f\u798f\u4e0d\u662f\u7978\uff0c\u662f\u7978\u8eb2\u4e0d\u8fc7\u3002 \u3000\u3000\u5c24\u5176\u4ed6\u8fd9\u6d1b\u9633\u5c45\u603b\u7ba1\uff0c\u65e9\u5df2\u662f\u5f62\u540c\u865a\u8bbe\uff0c\u8fde\u4e2a\u526f\u603b\u7ba1\u738b\u9f99\u90fd\u4e0d\u628a\u4ed6\u5f53\u56de\u4e8b\uff0c\u5728\u4e09\u5bab\u4e3b\u7684\u773c\u91cc\uff0c\u4ed6\u7b80\u76f4\u5c31\u6210\u4e86\u4e2a\u5fae\u4e0d\u8db3\u9053\u7684\u5c0f\u89d2\u8272\u3002 \u3000\u3000\u4e09\u5bab\u4e3b\u4e00\u5230\uff0c\u5c31\u6307\u540d\u8981\u89c1\u97e9\u9738\uff0c\u663e\u89c1\u6765\u610f\u4e0d\u5584\u3002 \u3000\u3000\u9093\u98de\u804c\u5351\u5fae\uff0c\u6b66\u529f\u66f4\u4e0d\u582a\u5185\u5bab\u7684\u9ad8\u624b\u4e00\u51fb\uff0c\u6697\u5bf9\u97e9\u5341\u5206\u5173\u5207\uff0c\u4e5f\u662f\u7231\u83ab\u80fd\u52a9\u3002 \u3000\u3000\u6240\u4ee5\uff0c\u4ed6\u53ea\u6709\u5bc4\u671b\u6885\u82b1\u80fd\u89c1\u673a\u884c\u4e8b\uff0c\u5168\u529b\u63f4\u52a9\u97e9\u526f\u603b\u7ba1\u4e86\u3002 \u3000\u3000\u76ee\u9001\u9093\u98de\u7684\u8eab\u5f71\u4e00\u95ea\u800c\u901d\uff0c\u6c5f\u67ab\u5373\u9053\uff1a\u201c\u6885\u82b1\uff0c\u6211\u4eec\u5feb\u53bb\u6885\u82b1\u5385\u5427\u3002\u201d \u3000\u3000\u4e0d\u6599\u6885\u82b1\u5374\u8bf4\uff0c\u201c\u4e0d\u6025\uff0c\u5148\u5230\u4f60\u5c4b\u91cc\u53bb\u3002\u201d \u3000\u3000\u6c5f\u67ab\u7709\u5934\u4e00\u76b1\u9053\uff1a\u201c\u53ef\u662f\uff0c\u4e09\u5bab\u4e3b\u547d\u4f60\u6765\u53ec\u6211\u2026\u2026\u201d \u3000\u3000\u201c\u7ba1\u5979\u7684\uff01\u201d\u6885\u82b1\u6beb\u4e0d\u5728\u4e4e\u9053\uff1a\u201c\u53cd\u6b63\u5979\u5df2\u7ecf\u53ec\u4e86\u8005\u534a\u5929\uff0c\u4e5f\u4e0d\u5728\u4e4e\u8fd9\u4e00\u4f1a\u513f\u561b\u3002\u201d \u3000\u3000\u6c5f\u67ab\u987e\u8651\u9053\uff1a\u201c\u4e0d\u592a\u597d\u5427\u2026\u2026\u201d \u3000\u3000\u6885\u82b1\u773c\u76ae\u4e00\u7ffb\uff0c\u60bb\u7136\u8bf4\uff0c\u201c\u4f60\u7b49\u4e0d\u53ca\u53bb\u9001\u6b7b\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u5f3a\u81ea\u4e00\u7b11\u9053\uff1a\u201c\u4f60\u600e\u4e48\u89e6\u6211\u9709\u5934\uff01\u201d \u3000\u3000\u201c\u4e0d\u662f\u89e6\u4f60\u9709\u5934\uff0c\u6211\u662f\u8bf4\u7684\u5b9e\u8bdd\u3002\u201d\u6885\u82b1\u6b63\u8272\u9053\uff1a\u201c\u5e38\u8a00\u8bf4\u7684\u597d\uff0c\u6765\u8005\u4e0d\u5584\u610f\uff0c\u5584\u8005\u4e0d\u6765\u3002 \u3000\u3000\u5982\u4eca\u4e09\u5bab\u4e3b\u65b0\u81ea\u51fa\u9a6c\uff0c\u51fa\u624b\u7edd\u4e0d\u4f1a\u7559\u60c5\u3002 \u3000\u3000\u4f60\u80fd\u6d3b\u547d\u7684\u5e0c\u671b\uff0c\u5b9e\u5728\u5fae\u4e4e\u5176\u5fae\uff0c\u4f46\u6211\u4e0d\u80fd\u773c\u7741\u7741\u770b\u7740\u4f60\u53bb\u9001\u6b7b\uff0c\u6240\u4ee5\uff0c\u6240\u4ee5\u2026\u2026\u201d \u3000\u3000\u597d\u8fde\u8bf4\u4e24\u58f0\u201c\u6240\u4ee5\u201d\uff0c\u5374\u8bf4\u4e0d\u51fa\u4e2a\u6240\u4ee5\u7136\u3002 \u3000\u3000\u6c5f\u67ab\u7b11\u95ee\uff1a\u201c\u65e2\u662f\u6d3b\u547d\u7684\u5e0c\u671b\u5fae\u4e4e\u5176\u5fae\uff0c\u4f60\u8fd8\u671f\u5f85\u4ec0\u4e48\u5947\u8ff9\uff1f\u201d \u3000\u3000\u6885\u82b1\u773c\u4e2d\u95ea\u7740\u5947\u5f02\u7684\u5149\u8292\uff0c\u9053\uff1a\u201c\u4e0d\u662f\u671f\u5f85\uff0c\u800c\u662f\u521b\u9020\u5947\u8ff9\u3002\u201d \u3000\u3000\u201c\u54e6\uff01\u201d\u6c5f\u67ab\u9732\u51fa\u4e00\u8138\u60ca\u8bb6\u4e4b\u8272\u3002 \u3000\u3000\u6885\u82b1\u50ac\u4fc3\u9053\uff1a\u201c\u4e8b\u4e0d\u5b9c\u8fdf\uff0c\u5feb\u5230\u4f60\u5c4b\u91cc\u53bb\u5427\u3002\u201d \u3000\u3000\u6c5f\u67ab\u4e0d\u77e5\u8fd9\u5c11\u5973\u80fd\u201c\u521b\u9020\u201d\u51fa\u4ec0\u4e48\u5947\u8ff9\uff0c\u5f53\u5373\u628a\u5979\u5e26\u8fdb\u4e86\u5185\u5ba4\u3002 \u3000\u3000\u6885\u82b1\u4e00\u8fdb\u623f\uff0c\u5c31\u90d1\u91cd\u5176\u4e8b\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u7ec3\u8fc7\u2018\u60d1\u5fc3\u672f\u2019\uff0c\u4f46\u4e09\u5bab\u4e3b\u7684\u2018\u5a9a\u672f\u2019\uff0c\u5df2\u8fdb\u5165\u81f3\u9ad8\u5883\u754c\uff0c\u5982\u679c\u4ee5\u6211\u7684\u90a3\u70b9\u706b\u5019\u8ddf\u5979\u76f8\u6bd4\uff0c\u72b9\u5982\u5c0f\u5deb\u89c1\u5927\u5deb\uff0c\u8424\u5149\u4e4b\u6bd4\u661f\u6708\uff0c\u8fd9\u6837\u7684\u5f3a\u654c\uff0c\u4f60\u81ea\u5df1\u4f30\u8ba1\u80fd\u6709\u51e0\u5206\u80a1\u7b97\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u541f\u4e00\u4e0b\u9053\uff0c \u3000\u3000\u201c\u4f60\u803d\u5fc3\u4e09\u5bab\u4e3b\u4e00\u89c1\u9762\u5c31\u65bd\u5c55\u2018\u5a9a\u672f\u2019\uff1f\u201d \u3000\u3000\u6885\u82b1\u70b9\u70b9\u5934\u8bf4\uff0c\u201c\u4e09\u5bab\u4e3b\u81ea\u89c6\u6781\u9ad8\uff0c\u5979\u7edd\u5bf9\u4e0d\u5c51\u4ee5\u6b66\u529f\u4eb2\u624b\u6740\u4f60\uff0c\u6781\u53ef\u80fd\u662f\u7528\u2018\u5a9a\u672f\u2019\uff0c\u5148\u4f7f\u4f60\u5931\u53bb\u62b5\u6297\u80fd\u529b\uff0c\u518d\u547d\u5979\u7684\u968f\u4ece\u4e0b\u624b\u3002\u201d \u3000\u3000\u6c5f\u67ab\u4e0d\u77e5\u662f\u5bf9\u201c\u60d1\u5fc3\u672f\u201d\u5145\u6ee1\u81ea\u4fe1\uff0c\u8ba4\u4e3a\u5fc5\u53ef\u538b\u5236\u4e09\u5bab\u4e3b\u7684\u2018\u5a9a\u672f\u2019\uff0c\u8fd8\u662f\u5bf9\u6885\u82b1\u7684\u5224\u65ad\u6709\u6240\u6000\u7591\uff0c\u8038\u8038\u80a9\u9053\uff1a\u201c\u90a3\u4e48\u5979\u7684\u6b66\u529f\uff0c\u7a76\u7adf\u9ad8\u5230\u4f55\u7b49\u7a0b\u5ea6\uff1f\u201d \u3000\u3000\u6885\u82b1\u5fe7\u5f62\u4e8e\u8272\u9053\uff1a\u201c\u8fd9\u5f88\u96be\u8bf4\uff0c\u56e0\u4e3a\u6211\u4ece\u672a\u89c1\u5979\u65bd\u5c55\u8fc7\uff0c\u53ea\u662f\uff0c\u7167\u6211\u4f30\u8ba1\uff0c\u5408\u4f60\u6211\u4e8c\u4eba\u4e4b\u529b\uff0c\u81f3\u591a\u4e5f\u53ea\u80fd\u62b5\u6321\u5979\u5341\u62db\uff0c\u6700\u540e\u5fc5\u8d25\u65e0\u7591\u3002\u201d \u3000\u3000\u6c5f\u67ab\u201c\u54e6\uff1f\u201d\u4e86\u4e00\u58f0\uff0c\u95ee\u9053\uff1a\u201c\u90a3\u4f60\u8fd8\u6307\u671b\u80fd\u2018\u521b\u9020\u2019\u51fa\u4ec0\u4e48\u5947\u8ff9\uff1f\u201d \u3000\u3000\u6885\u82b1\u80f8\u6709\u6210\u7af9\u8bf4\uff0c\u201c\u521a\u624d\u6211\u4e0d\u662f\u8bf4\u4e86\u5417\uff1f\u4e09\u5bab\u4e3b\u662f\u5185\u5bab\u4e09\u4f4d\u5bab\u4e3b\u4e4b\u4e00\uff0c\u4e00\u5411\u81ea\u89c6\u6781\u9ad8\uff0c\u82e5\u975e\u9ed1\u5929\u9e45\u53ca\u4e00\u4f4d\u559c\u9e4a\u7ea7\u7684\u5973\u5f1f\u5b50\uff0c\u5148\u540e\u4e27\u547d\u5728\u4f60\u624b\u4e2d\uff0c\u5979\u7edd\u4e0d\u4f1a\u4eb2\u81ea\u51fa\u9a6c\u3002 \u3000\u3000\u8eab\u4e3a\u5185\u5bab\u4e09\u5bab\u4e3b\u4e4b\u4e00\uff0c\u5979\u82e5\u4ed7\u6b66\u529f\u4eb2\u624b\u6740\u4f60\uff0c\u5c82\u4e0d\u6210\u4e86\u4e3a\u90a3\u4e24\u4e2a\u5973\u5f1f\u5b50\u62a5\u4ec7\uff1f\u4e3a\u4e86\u8fd9\u5c42\u987e\u5fcc\uff0c\u4ee5\u514d\u843d\u5165\u53e3\u5b9e\uff0c\u6240\u4ee5\u6211\u91cf\u5979\u4e0d\u81f4\u4e00\u89c1\u9762\u5c31\u8ddf\u4f60\u4ee5\u6b66\u529f\u89c1\u771f\u7ae0\u3002 \u3000\u3000\u4f46\u5979\u7684\u2018\u5a9a\u672f\u2019\u5df2\u81f3\u6700\u9ad8\u5883\u754c\uff0c\u53ef\u4ee5\u53d1\u4e8e\u65e0\u5f62\uff0c\u66f4\u4f55\u51b5\u6885\u82b1\u5385\u5185\u5916\u5e02\u7f72\u7684\uff0c\u5168\u662f\u542c\u4f7f\u4e8e\u5979\u7684\u4eba\uff0c\u5373\u4f7f\u660e\u77e5\u5b58\u5fc3\u65bd\u5c55\u2018\u5a9a\u672f\u2019\uff0c\u4e5f\u5fc5\u7136\u89c6\u82e5\u672a\u7779\u3002 \u3000\u3000\u8fd9\u6837\u4e00\u6765\uff0c\u5979\u4ee5\u2018\u5a9a\u672f\u2019\u5148\u5236\u4f4f\u4f60\uff0c\u4f7f\u4f60\u5931\u53bb\u62b5\u6297\u80fd\u529b\uff0c\u518d\u4e00\u58f0\u4ee4\u4e0b\uff0c\u7531\u5979\u5e26\u6765\u7684\u4eba\u51fa\u624b\u7f6e\u4f60\u4e8e\u6b7b\uff0c\u60c5\u5f62\u5c31\u5b8c\u5168\u4e0d\u540c\u4e86\u3002\u5c31\u50cf\u738b\u526f\u603b\u7ba1\u4e00\u6837\uff0c\u9093\u603b\u7ba1\u4e00\u58f0\u2018\u8be5\u6b7b\u2019\uff0c\u4f60\u5c31\u52a8\u624b\u6740\u4e86\u4ed6\u3002 \u3000\u3000\u6240\u4ee5\u6211\u6562\u6253\u8d4c\uff0c\u672c\u5bab\u4e3b\u7edd\u5bf9\u662f\u5b89\u7684\u8fd9\u4e2a\u5fc3\uff01\u201d \u3000\u3000\u6c5f\u67ab\u542c\u5979\u4e00\u53e3\u6c14\u8bf4\u5b8c\uff0c\u624d\u7b11\u95ee\u9053\uff1a\u201c\u4f60\u6240\u8c13\u7684\u2018\u521b\u9020\u2019\u5947\u8ff9\uff0c\u662f\u5426\u5df2\u6709\u5bf9\u4ed8\u4e09\u5bab\u4e3b\u7684\u9526\u56ca\u5999\u8ba1\uff1f\u201d \u3000\u3000\u201c\u55ef\uff01\u201d\u6885\u82b1\u9053\uff1a\u201c\u6211\u8981\u4f7f\u51fa\u6df7\u8eab\u89e3\u6570\uff0c\u5411\u4f60\u65bd\u5c55\u2018\u5a9a\u672f\u2019\u201d \u3000\u3000\u201c\u54e6\uff1f\u201d\u6c5f\u67ab\u4e3a\u4e4b\u4e00\u6014\uff0c\u832b\u7136\u9053\uff1a\u201c\u6211\u4e0d\u61c2\u4f60\u7684\u610f\u601d\u2026\u2026\u201d \u3000\u3000\u6885\u82b1\u63a5\u9053\uff1a\u201c\u8fd9\u5c31\u5982\u540c\u4f60\u51c6\u5907\u5bb4\u524d\uff0c\u6211\u5148\u8ba9\u4f60\u5c1d\u5c3d\u5929\u4e0b\u73cd\u54c1\uff0c\u90a3\u4e48\u4f60\u518d\u89c1\u5230\u5c71\u73cd\u6d77\u5473\uff0c\u4e5f\u5c31\u6beb\u65e0\u80c3\u53e3\u4e86\u3002\u201d \u3000\u3000\u6c5f\u67ab\u82e5\u6709\u6240\u609f\u9053\uff1a\u201c\u6211\u660e\u767d\u4e86\uff0c\u4f60\u662f\u8bf4\u2026\u2026\u201d \u3000\u3000\u6885\u82b1\u6b63\u8272\u9053\uff1a\u201c\u4e0d\u8981\u8ddf\u6211\u4e89\u8fa9\uff0c\u6211\u4eec\u5fc5\u987b\u4e89\u53d6\u65f6\u95f4\uff0c\u5373\u4f7f\u8fd9\u4e00\u7740\u5931\u7b97\uff0c\u60c5\u51b5\u5b8c\u5168\u51fa\u6211\u610f\u6599\u4e4b\u5916\uff0c\u90a3\u4e48\u6b64\u523b\u5c31\u7b97\u6211\u4eec\u4eca\u751f\u7684\u6700\u540e\u76f8\u805a\u5427\uff01\u201d \u3000\u3000\u8a00\u4e0b\u4e4b\u610f\uff0c\u4e07\u4e00\u4e09\u5bab\u4e3b\u89c1\u97e9\u9738\u5c31\u51fa\u624b\uff0c\u6885\u82b1\u4e5f\u5df2\u62b1\u5b9a\u5fc5\u6b7b\u51b3\u5fc3\u3002 \u3000\u3000\u6c5f\u67ab\u8f7b\u53f9\u4e00\u58f0\uff0c\u6765\u4e0d\u53ca\u529d\u963b\uff0c\u6885\u82b1\u5df2\u53cc\u624b\u9f50\u52a8\uff0c\u5bbd\u8863\u89e3\u5e26\uff0c\u5c06\u5168\u8eab\u8863\u7269\u5c3d\u892a\u3002 \u3000\u3000\u987f\u65f6\uff0c\u5448\u73b0\u5728\u6c5f\u67ab\u773c\u524d\u7684\uff0c\u662f\u4e2a\u5343\u5a07\u767e\u5a9a\uff0c\u66f2\u4f53\u73b2\u73d1\u7684\u8d64\u88f8\u5c11\u5973\uff01 \u3000\u3000\u6885\u82b1\u53cc\u988a\u6cdb\u7ea2\uff0c\u76ca\u589e\u51e0\u5206\u5a07\u8273\uff0c\u5a07\u58f0\u9053\uff1a\u201c\u770b\u7740\u6211\uff0c\u4f46\u5343\u4e07\u4e0d\u53ef\u4ee5\u2018\u60d1\u5fc3\u672f\u2019\uff0c\u6216\u662f\u4efb\u4f55\u5176\u5b83\u529b\u91cf\u6297\u62d2\uff0c\u5426\u5219\u6211\u5c31\u6789\u8d39\u5fc3\u673a\u4e86\u3002\u201d \u3000\u3000\u6c5f\u67ab\u4f9d\u8a00\u671b\u7740\u6885\u82b1\u3002 \u3000\u3000\u53ea\u89c1\u8fd9\u8d64\u88f8\u7684\u5c11\u5973\uff0c\u70df\u89c6\u5a9a\u884c\u5730\u8d70\u8fd1\u6c5f\u67ab\uff0c\u5168\u8eab\u5e76\u4e14\u6563\u53d1\u51fa\u4e00\u80a1\u4ee4\u4eba\u65e0\u6cd5\u6297\u62d2\u7684\u9b45\u529b\u3002 \u3000\u3000\u7167\u7406\u8bf4\uff0c\u6c5f\u67ab\u4e0e\u6885\u82b1\u5df2\u6625\u98ce\u6570\u5ea6\uff0c\u6709\u8fc7\u808c\u80a4\u4e4b\u4eb2\uff0c\u5bf9\u8fd9\u5c11\u5973\u7684\u80f4\u4f53\uff0c\u4e0d\u4f46\u65e9\u5df2\u904d\u89c8\u65e0\u9057\uff0c\u4e0a\u4e0b\u5176\u624b\uff0c\u66f4\u4e14\u771f\u4e2a\u9500\u9b42\uff0c\u4e0d\u81f4\u5982\u6b64\u7740\u8ff7\u3002 \u3000\u3000\u4f46\u6885\u82b1\u6b64\u523b\u7684\u8ff7\u4eba\u4e4b\u5904\uff0c\u5e76\u975e\u662f\u5979\u88f8\u9732\u7684\u8bf1\u4eba\u8d2d\u4f53\uff0c\u800c\u662f\u90a3\u64a9\u4eba\u5fc3\u5f26\u7684\u60c5\u6001\uff0c\u5c24\u5176\u662f\u90a3\u52fe\u9b42\u6444\u9b44\u7684\u773c\u795e\uff01 \u3000\u3000\u6c5f\u67ab\u6ca1\u6709\u65bd\u5c55\u2018\u60d1\u5fc3\u672f\u2019\uff0c\u66f4\u6ca1\u6709\u6297\u62d2\u5979\u7684\u610f\u5ff5\u3002 \u3000\u3000\u9010\u6e10\u5730\uff0c\u4ed6\u6709\u4e9b\u610f\u4e71\u60c5\u8ff7\u8d77\u6765\u2026\u2026 \u3000\u3000\u773c\u524d\u7684\u6885\u82b1\uff0c\u90fd\u662f\u82e5\u5373\u82e5\u79bb\uff0c\u4eff\u4f5b\u662f\u4e2a\u865a\u5ffd\u98d8\u6e3a\u7684\u5e7b\u5f71\uff0c\u59cb\u7ec8\u53ea\u5728\u6c5f\u67ab\u9762\u524d\u6643\u6765\u6643\u53bb\uff0c\u6414\u9996\u5f04\u59ff\u7684\uff0c\u6781\u5c3d\u5f15\u9017\u4e4b\u80fd\u4e8b\uff0c\u5e76\u4e0d\u91c7\u53d6\u8fdb\u4e00\u6b65\u7684\u884c\u52a8\u3002 \u3000\u3000\u8fd9\u662f\u6545\u610f\u540a\u6c5f\u67ab\u7684\u80c3\u53e3\uff1f \u3000\u3000\u6c5f\u67ab\u5df2\u88ab\u5979\u6311\u9017\u5f97\u6b32\u706b\u4e0a\u5347\uff0c\u518d\u4e5f\u6309\u637a\u4e0d\u4f4f\uff0c\u5931\u63a7\u5730\u7a81\u5411\u6885\u82b1\u6251\u53bb\u3002 \u3000\u3000\u6885\u82b1\u5e76\u672a\u95ea\u907f\uff0c\u88ab\u4ed6\u53cc\u81c2\u4e00\u5f20\uff0c\u62b1\u4e86\u4e2a\u6b63\u7740\u3002 \u3000\u3000\u6c5f\u67ab\u5f62\u540c\u75af\u72c2\uff0c\u5c06\u6885\u82b1\u697c\u8fdb\u6000\u91cc\u72c2\u543b\u4e0d\u5df2\u3002 \u3000\u3000\u6885\u82b1\u5fc3\u77e5\u65f6\u673a\u5df2\u7ecf\u6210\u719f\uff0c\u5a07\u58f0\u8bf4\uff0c\u201c\u5feb\u62b1\u6211\u5230\u5e8a\u4e0a\u53bb\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u5982\u5949\u61ff\u65e8\uff0c\u5fd9\u4e0d\u8fed\u5c06\u6885\u82b1\u62b1\u4e0a\u5e8a\uff0c\u8ba9\u5979\u5e73\u8eba\u5728\u5e8a\u4e0a\uff0c\u8feb\u4e0d\u53ca\u5f85\u5730\u5411\u5979\u904d\u4f53\u72c2\u543b\u3002 \u3000\u3000\u6885\u82b1\u6b64\u523b\u4e0e\u5e73\u65f6\u7b80\u76f4\u5224\u82e5\u4e24\u4eba\uff0c\u53ea\u542c\u5979\u4e0d\u65ad\u53d1\u51fa\u653e\u6d6a\u5f62\u9ab8\u7684\u7b11\u58f0\uff0c\u5982\u540c\u4e00\u4e2a\u72fc\u864e\u4e4b\u5e74\u7684\u8361\u5987\u3002 \u3000\u3000\u53ea\u542c\u5979\u6d6a\u7b11\u9053\uff1a\u201c\u6c5f\u90ce\uff0c\u59be\u8eab\u65e9\u5df2\u8eab\u5fc3\u90fd\u5c5e\u4e8e\u4f60\u4e86\uff0c\u5c3d\u60c5\u5730\u4eab\u53d7\u5427\uff01\u54c8\u54c8\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u66f4\u89c9\u6b32\u4eba\u96be\u7981\uff0c\u970d\u5730\u6491\u8d77\u8eab\u5b50\uff0c\u6b63\u5f85\u8131\u53bb\u8863\u7269\u4e0a\u722c\uff0c\u5c55\u5f00\u7ffb\u4e91\u8986\u96e8\u7684\u8089\u640f\u4e4b\u9645\uff0c\u5ffd\u542c\u4e03\u5b9d\u548c\u5c1a\u4ee5\u2018\u4f20\u97f3\u5165\u5bc6\u2019\u4e4b\u58f0\u53d1\u51fa\u8b66\u544a\uff1a \u3000\u3000\u201c\u6885\u82b1\u5385\u6d3e\u4eba\u6765\u50ac\u9a7e\u5566\uff01\u201d \u3000\u3000\u6c5f\u67ab\u4ece\u8ff7\u4e71\u4e2d\u7a81\u7136\u6e05\u9192\u3002 \u3000\u3000\u6885\u82b1\u5374\u7d27\u7d27\u6402\u4f4f\u4ed6\u8eab\u5b50\u4e0d\u653e\uff1a\u201c\u4e0d\u8981\u7406\u4f1a\uff0c\u4e0e\u5176\u6b7b\u5728\u4e09\u5bab\u4e3b\u624b\u4e2d\uff0c\u4e0d\u5982\u6b7b\u5728\u4f60\u6000\u91cc\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u7ed9\u4e86\u5979\u4e00\u4e2a\u70ed\u543b\uff0c\u5f53\u673a\u7acb\u65ad\u9053\uff1a\u201c\u5feb\u8d77\u6765\uff0c\u6211\u4eec\u53bb\u89c1\u4e09\u5bab\u4e3b\uff01\u201d \u3000\u3000\u6885\u82b1\u987f\u89c9\u6005\u7136\u82e5\u5931\uff0c\u60c5\u4e0d\u81ea\u7981\u6cea\u6c34\u593a\u7736\u800c\u51fa\u3002 \u3000 \u7b2c\u5341\u56db\u56de\u3000\u7fe0\u7389\u5200\u52c7\u51fb\u516b\u5251\u58eb \u3000 \u3000\u3000\u201c\u6c5f\u5927\u54e5\uff0c\u5a18\u548c\u5f20\u59e8\u5df2\u7ecf\u627f\u8ba4\u6211\u4eec\u957f\u5927\u4e86\uff0c\u4ece\u4eca\u4e4b\u540e\uff0c\u6211\u4eec\u7684\u4e8b\uff0c\u7531\u6211\u4eec\u81ea\u5df1\u4f5c\u4e3b\u4e86\uff0c\u6c5f\u5927\u54e5\u6709\u4ec0\u4e48\u5429\u5490\uff0c\u53ef\u4ee5\u76f4\u63a5\u7684\u4e0b\u4ee4\u6211\u4eec\u4e86\uff0c\u2026\u2026\u201d\u53f6\u96ea\u8bf4\u7684\u795e\u60c5\u4e25\u8083\uff0c\u4e00\u6539\u5f80\u65e5\u7684\u5c11\u5973\u795e\u60c5\u3002 \u3000\u3000\u201c\u6211\u77e5\u9053\uff0c\u7ecf\u8fc7\u8fd9\u4e00\u6b21\u8003\u9a8c\uff0c\u6c5f\u5927\u54e5\u4e5f\u611f\u89c9\u5230\u4f60\u4eec\u53ef\u4ee5\u72ec\u5f53\u4e00\u9762\u4e86\u3002\u201d \u3000\u3000\u201c\u591a\u8c22\u6c5f\u5927\u54e5\uff0c\u4f60\u4e5f\u627f\u8ba4\u6211\u4eec\u957f\u5927\u4e86\uff0c\u2026\u2026\u201d\u541f\u971c\u9053\uff1a\u201c\u5f20\u59e8\u8ba9\u6211\u8f6c\u544a\u4f60\u4e00\u4ef6\u4e8b\uff0c\u51e4\u59d0\u7edd\u5bf9\u5b89\u5168\uff0c\u53eb\u4f60\u522b\u803d\u5fc3\u7eff\u4e91\u538b\u9876\u3002\u201d \u3000\u3000\u6c5f\u67ab\u7684\u8138\u4e0a\uff0c\u7adf\u7136\u4e5f\u98de\u8d77\u4e86\u4e00\u7247\u7f9e\u7ea2\uff0c\u4f46\u53c8\u5f97\u786c\u7740\u5934\u76ae\uff0c\u8bf4\uff0c\u201c\u4f60\u4eec\u90fd\u77e5\u9053\u4e86\u3002\u2026\u2026\u201d \u3000\u3000\u541f\u971c\u63a5\u9053\uff1a\u201c\u8fd8\u6709\u6885\u82b1\u59d1\u5a18\u7684\u4e8b\u3002\u4e3a\u4e86\u884c\u4e8b\u65b9\u4fbf\uff0c\u5f20\u59e8\u5168\u90fd\u8bf4\u7ed9\u6211\u4eec\u542c\u4e86\uff0c\u5982\u80fd\u5f97\u5979\u4eec\u5168\u529b\u5e2e\u52a9\uff0c\u6211\u60f3\uff0c\u5bf9\u4f60\u7684\u5e2e\u52a9\u5f88\u5927\u3002\u201d \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u4e24\u4f4d\u6709\u4f55\u9ad8\u89c1\uff1f\u201d \u3000\u3000\u201c\u628a\u4f60\u7684\u8ba1\u5212\u544a\u8bc9\u6211\u4eec\uff0c\u6211\u4eec\u5c31\u5168\u529b\u548c\u4f60\u914d\u5408\u2026\u2026\u201d\u541f\u96ea\u9053\uff1a\u201c\u72d0\u8ba1\u591a\u7aef\uff0c\u6c5f\u5927\u54e5\u5e94\u8be5\u660e\u767d\uff0c\u522b\u4e3a\u6211\u4eec\u62c5\u5fc3\uff0c\u6211\u4eec\u957f\u5927\u4e86\uff0c\u5c31\u8be5\u4f5c\u4e9b\u5927\u4eba\u4f5c\u7684\u4e8b\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u70b9\u70b9\u5934\uff0c\u8bf4\u51fa\u4e86\u4e00\u756a\u8ba1\u5212\u3002 \u3000\u3000\u201c\u679c\u7136\u662f\u53c8\u597d\u53c8\u6bd2\u7684\u597d\u8ba1\u5212\u2026\u2026\u201d\u541f\u96ea\u7b11\u9053\uff1a\u201c\u5f20\u59e8\u767d\u767d\u66ff\u4f60\u62c5\u5fc3\u4e86\uff0c\u5979\u867d\u7136\u7cbe\u4e8e\u7528\u5175\u3001\u5e03\u7f72\uff0c\u4f46\u5374\u5c11\u4e86\u90a3\u4e48\u4e00\u70b9\u72d0\u6c14\uff0c\u5bf9\u4ed8\u5929\u9a6c\u5802\u8fd9\u73ed\u90aa\u9b54\u5916\u9053\u4e2d\u4eba\uff0c\u8fd8\u662f\u5929\u72d0\u95e8\u7684\u65b9\u6cd5\u7ba1\u7528\u3002\u201d \u3000\u3000\u541f\u971c\u9053\uff1a\u201c\u6211\u4eec\u5168\u529b\u914d\u5408\uff0c\u81f3\u5c11\u53ef\u4ee5\u4f7f\u4f60\u7684\u8ba1\u5212\u6210\u529f\u4e00\u534a\uff0c\u4e0d\u8fc7\uff0c\u6211\u4eec\u8981\u53bb\u89c1\u89c1\u9752\u51e4\u548c\u6885\u82b1\uff0c\u6c5f\u5927\u54e5\u4e0d\u53cd\u5bf9\u5427\uff01\u201d \u3000\u3000\u201c\u770b\u8d77\u6765\uff0c\u6211\u53cd\u5bf9\u4e5f\u6ca1\u6709\u7528\uff0c\u5929\u72d0\u767e\u53d8\u7684\u5fc3\u6cd5\uff0c\u4f60\u4eec\u4f3c\u4e4e\u662f\u6bd4\u6211\u66f4\u6709\u5fc3\u5f97\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u80fd\u8bf4\u52a8\u5979\u4eec\u914d\u5408\uff0c\u4f60\u4eec\u5c31\u5168\u529b\u65bd\u5c55\u5427\uff01\u201d \u3000\u3000\u541f\u96ea\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6c5f\u5927\u54e5\u4f1a\u6162\u6162\u53d1\u73b0\uff0c\u6211\u4eec\u4e0d\u4f1a\u8ba9\u4f60\u5931\u671b\uff01\u201d \u3000\u3000\u7275\u7740\u541f\u971c\u4e00\u53ea\u624b\uff0c\u53cc\u53cc\u79bb\u53bb\u3002 \u3000\u3000\u671b\u7740\u4e8c\u5973\u8fdc\u53bb\u7684\u80cc\u5f71\uff0c\u4e03\u5b9d\u548c\u5c1a\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u662f\u4e24\u4e2a\u597d\u5e2e\u624b\uff0c\u6211\u770b\u5979\u65e9\u5df2\u80f8\u6709\u6210\u7af9\u4e86\u3002\u201d \u3000\u3000\u201c\u8fc7\u53bb\uff0c\u662f\u6709\u4e9b\u4f4e\u4f30\u5979\u4eec\u3002\u2026\u2026\u201d \u3000\u3000\u516d\u5b9d\u548c\u5c1a\u6253\u65ad\u4e86\u6c5f\u67ab\u7684\u8bdd\uff0c\u63a5\u9053\uff1a\u201c\u53ea\u4e00\u6b21\u8003\u9a8c\uff0c\u5c31\u786e\u5b9a\u4e86\u5979\u4eec\u7684\u6210\u5c31\uff0c\u4ed8\u4e8e\u5927\u4efb\uff0c\u4f60\u662f\u4ece\u54ea\u91cc\u770b\u51fa\u6765\u7684\uff1f\u201d \u3000\u3000\u662f\u95ee\u9898\uff0c\u4e5f\u662f\u8bf7\u6559\uff1f \u3000\u3000\u201c\u4ee5\u541f\u96ea\u4e4b\u7f8e\uff0c\u7adf\u7136\u6f5c\u9690\u4e8e\u5783\u573e\u4e4b\u4e2d\uff0c\u8fd9\u9700\u8981\u591a\u5927\u7684\u5fcd\u6027\uff0c\u541f\u971c\u7684\u6f5c\u6728\u672f\uff0c\u5df2\u5f97\u56db\u59d1\u7684\u795e\u9ad3\uff0c\u5979\u53ea\u4e0d\u8fc7\u7ec3\u4e86\u4e24\u4e2a\u6708\uff0c\u8fd9\u8bc1\u660e\u4e86\u5979\u4eec\u7684\u5929\u5206\u8fc7\u4eba\u4e4b\u5916\uff0c\u53c8\u80af\u8ba4\u771f\u7684\u82e6\u5b66\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u4f46\u6700\u91cd\u8981\u7684\u8fd8\u662f\u5979\u4eec\u5251\u672f\u4e0a\u7684\u6210\u5c31\uff0c\u4e00\u51fb\u53d6\u547d\uff0c\u6b63\u662f\u5929\u72d0\u5251\u6cd5\u7684\u7cbe\u8981\u3002\u201d \u3000\u3000\u201c\u8fd9\u771f\u8981\u606d\u559c\u6c5f\u5c11\u5144\u4e86\uff0c\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u9053\uff1a\u201c\u8fd9\u4e9b\u52a9\u624b\uff0c\u5927\u4e8b\u5b9a\u77e3\uff01\u201d \u3000\u3000\u201c\u770b\u770b\u5979\u4eec\u7684\u8868\u73b0\u5427\uff01\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u6211\u5e0c\u671b\u8fd9\u4e00\u6b21\u5f15\u8d77\u7684\u706b\u62fc\uff0c\u6709\u5982\u71ce\u539f\u7684\u661f\u706b\uff0c\u6269\u5c55\u53bb\uff0c\u8ba9\u4ed6\u4eec\u6b32\u7f62\u4e0d\u80fd\u2026\u2026\u201d \u3000\u3000\u4f46\u95fb\u80e1\u5143\u7684\u58f0\u97f3\uff0c\u4f20\u4e86\u8fdb\u6765\uff0c\u9053\uff1a\u201c\u5201\u9e4f\u6c42\u89c1\u3002\u201d \u3000\u3000\u201c\u8bf7\u4ed6\u8fdb\u6765\u3002\u201d \u3000\u3000\u5201\u9e4f\u4e00\u8138\u4e25\u8083\u7684\u795e\u60c5\uff0c\u9053\uff1a\u201c\u4e24\u4f4d\u526f\u603b\u5802\uff0c\u5df2\u9a7b\u8fdb\u4e86\u8fce\u5bbe\u9601\u4e2d\uff0c\u8bf7\u603b\u7ba1\u548c\u526f\u603b\u7ba1\u7acb\u523b\u664b\u89c1\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u4f60\u89c1\u8fc7\u4e24\u4f4d\u526f\u603b\u5802\u4e3b\u4e86\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u201c\u9664\u4e86\u5929\u865a\u5b50\u4e4b\u5916\uff0c\u53e6\u4e00\u4f4d\u662f\u5341\u51e4\u9f50\u98de\u7533\u96ea\u541b\uff0c\u771f\u662f\u5927\u5927\u7684\u51fa\u4eba\u610f\u5916\u2026\u2026\u201d\u5201\u9e4f\u606d\u606d\u656c\u656c\u7684\u56de\u7b54\u8bf4\u3002 \u3000\u3000\u201c\u5341\u51e4\u9f50\u98de\u7533\u541b\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u6709\u4e9b\u5403\u60ca\u5730\u8bf4\uff0c\u201c\u6709\u6570\u5341\u5e74\u672a\u5728\u6c5f\u6e56\u4e0a\u8d70\u52a8\u4e86\u3002\u201d \u3000\u3000\u201c\u662f\uff01\u6700\u5947\u602a\u7684\u662f\uff0c\u5979\u9a7b\u5165\u8fce\u5bbe\u9601\uff0c\u8336\u4e5f\u672a\u559d\u4e00\u53e3\uff0c\u5c31\u4e0b\u4ee4\u53ec\u89c1\u6c5f\u5e08\u53d4\uff0c\u6700\u540e\u624d\u52a0\u4e0a\u9093\u603b\u7ba1\uff0c\u6211\u770b\uff0c\u9093\u603b\u7ba1\u53ea\u662f\u804b\u5b50\u7684\u8033\u6735\uff0c\u662f\u4e2a\u914d\u642d\uff0c\u2026\u2026\u201d\u5201\u9e4f\u9053\uff1a\u201c\u5409\u51f6\u96be\u6d4b\u554a\uff01\u6211\u4eec\u8981\u4e0d\u8981\u6709\u4e9b\u51c6\u5907\uff1f\u201d \u3000\u3000\u201c\u4e0d\u8981\u7d27\uff0c\u6211\u53bb\u89c1\u5979\u2026\u2026\u201d\u6c5f\u67ab\u91cd\u65b0\u5305\u624e\u4e86\u4e00\u4e0b\u5de6\u8033\u4f24\u52bf\uff0c\u7b11\u9053\uff1a\u201c\u8d70\uff01\u4e00\u8def\u4e0a\u544a\u8bc9\u6211\u7533\u96ea\u541b\u7684\u559c\u597d\u3001\u5f31\u70b9\uff0c\u4e5f\u597d\u8ba9\u6211\u6709\u70b9\u51c6\u5907\u3002\u201d \u3000\u3000\u201c\u5979\u8868\u9762\u7aef\u5e84\uff0c\u795e\u60c5\u51b7\u8083\uff0c\u4e25\u683c\u8bf4\u8d77\u6765\uff0c\u6211\u4eec\u6839\u672c\u4e0d\u77e5\u9053\u5979\u6709\u5f31\u70b9\uff0c\u2026\u2026\u201d\u5201\u9e4f\u9053\uff1a \u3000\u3000\u201c\u5728\u5929\u9a6c\u603b\u5802\u4e2d\uff0c\u4e5f\u96be\u5f97\u89c1\u5979\u4e00\u6b21\uff0c\u603b\u5802\u4e3b\u8fd8\u5e38\u5e38\u4e3b\u6301\u4f1a\u8bae\uff0c\u5979\u8fde\u4f1a\u8bae\u4e5f\u5f88\u5c11\u51fa\u5e2d\u3002\u201d \u3000\u3000\u201c\u5929\u9a6c\u5802\u56db\u5927\u526f\u603b\u5802\u4e3b\u4e2d\uff0c\u5979\u662f\u4e0d\u662f\u6700\u5177\u6743\u52bf\u7684\u4e00\u4e2a\uff1f\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u5929\u865a\u5b50\u548c\u5979\u662f\u5426\u76f8\u5904\u5f97\u6765\uff1f\u201d \u3000\u3000\u201c\u5929\u865a\u5b50\u867d\u4e5f\u662f\u526f\u603b\u5802\u4e3b\u8eab\u4efd\uff0c\u4f46\u548c\u7533\u96ea\u541b\u76f8\u5dee\u4e00\u6bb5\u8ddd\u79bb\uff0c\u5927\u6982\u53ea\u6709\u542c\u547d\u884c\u4e8b\u7684\u4efd\uff0c\u8c08\u4e0d\u4e0a\u662f\u5426\u76f8\u5904\u5f97\u6765\u3002\u201d \u3000\u3000\u8c08\u8bdd\u4e4b\u95f4\uff0c\u5df2\u5230\u8fce\u5bbe\u9601\u5916\u3002 \u3000\u3000\u9093\u98de\u65e9\u5df2\u5728\u82b1\u6811\u6797\u5916\u7b49\u4faf\uff0c\u4e00\u89c1\u6c5f\u67ab\uff0c\u5927\u6b65\u8fce\u4e86\u4e0a\u6765\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u7533\u526f\u603b\u5802\u4e3b\u7684\u813e\u6c14\u4e0d\u597d\uff0c\u5e94\u5bf9\u4e4b\u95f4\uff0c\u8981\u5c0f\u5fc3\u4e00\u4e9b\u3002\u201d \u3000\u3000\u6c5f\u67ab\u70b9\u70b9\u5934\u3002 \u3000\u3000\u5201\u9e4f\u5f53\u5148\u5e26\u8def\uff0c\u8fdb\u4eba\u5927\u5385\u3002 \u3000\u3000\u53ea\u89c1\u5927\u5385\u4e2d\u5206\u7ad9\u7740\u516b\u4e2a\u9752\u8863\u4f69\u5251\u4eba\uff0c\u8170\u4e2d\u6a2a\u675f\u4e86\u4e00\u6761\u7ea2\u8272\u7684\u5e26\u5b50\u3002 \u3000\u3000\u6c5f\u67ab\u53ea\u770b\u5201\u9e4f\u795e\u8272\uff0c\u5df2\u7ecf\u610f\u8bc6\u5230\u516b\u4e2a\u9752\u8863\u4f69\u5251\u7684\u5e74\u8f7b\u4eba\uff0c\u5c31\u662f\u4ed6\u53e3\u4e2d\u6240\u79f0\u7684\u5251\u58eb\u4e86\u3002\u4e8b\u5b9e\u4e0a\uff0c\u516b\u4eba\u8170\u4e2d\u90a3\u6761\u9c9c\u7ea2\u7684\u5e26\u5b50\uff0c\u5df2\u7279\u522b\u7a81\u51fa\u4e86\u51e0\u4eba\u7684\u8eab\u4efd\u3002 \u3000\u3000\u4e00\u4e2a\u8eab\u7a7f\u9752\u7f0e\u5b50\u88d9\u886b\u7684\u7f8e\u4e3d\u5987\u4eba\uff0c\u7aef\u7aef\u6b63\u6b63\u7684\u5750\u5728\u5927\u5385\u4e2d\u592e\u3002 \u3000\u3000\u4f3c\u4e4e\u662f\u5ea7\u4f4d\u3001\u8ddd\u79bb\uff0c\u90fd\u7ecf\u8fc7\u4e86\u4e08\u91cf\uff0c\u5979\u5750\u7684\u5730\u65b9\u7ed9\u4eba\u4e00\u79cd\u4e3b\u5bb0\u5168\u5c40\u7684\u611f\u89c9\uff0c\u5c31\u50cf\u5979\u7a7f\u7684\u8863\u670d\u4e00\u822c\uff0c\u6734\u7d20\u3001\u6e05\u96c5\u4e2d\u53c8\u7ed9\u4eba\u4e00\u79cd\u9ad8\u8d35\u7684\u611f\u89c9\u3002 \u3000\u3000\u9752\u8863\u5987\u4eba\u4e24\u9053\u6e05\u6f88\u7684\u76ee\u5149\uff0c\u76ef\u4f4f\u6c5f\u67ab\u7684\u7751\u4e0a\uff0c\u6253\u91cf\u4e86\u4e00\u9635\uff0c\u7a81\u7136\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u53eb\u97e9\u9738\uff1f\u201d \u3000\u3000\u8fd9\u65f6\u5019\u7684\u6c5f\u67ab\uff0c\u5df2\u5b8c\u5168\u6062\u590d\u4e86\u97e9\u9738\u7684\u5f62\u8c8c\uff0c\u8eac\u8eab\u4e00\u793c\uff0c\u9053\uff1a\u201c\u5c5e\u4e0b\u53eb\u97e9\u9738\u3002\u201d \u3000\u3000\u201c\u5201\u9e4f\u2026\u2026\u201d\u7533\u96ea\u541b\u7684\u76ee\u5149\u8f6c\u6ce8\u5230\u4e5d\u5934\u9e1f\u7684\u8eab\u4e0a\uff0c\u9053\uff1a\u201c\u4f60\u53ef\u662f\u8d25\u5728\u4ed6\u7684\u624b\u4e2d\uff1f\u201d \u3000\u3000\u201c\u662f\uff01\u2026\u2026\u201d\u5201\u9e4f\u606d\u8c28\u7684\u5e94\u9053\uff1a\u201c\u4ed6\u624b\u4e0b\u7559\u60c5\uff0c\u66ff\u5c5e\u4e0b\u4fdd\u4f4f\u4e86\u5927\u90e8\u5206\u7684\u9762\u5b50\uff01\u201d \u3000\u3000\u7533\u96ea\u541b\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u97e9\u9738\uff0c\u6740\u5185\u5bab\u4e09\u9f99\u4e4b\u4e8c\uff0c\u53c8\u6740\u6b7b\u5929\u738b\u95e8\u4e0b\u4e00\u53ea\u559c\u9e4a\u7684\uff0c\u4e5f\u662f\u4f60\u4e86\uff1f\u201d \u3000\u3000\u201c\u662f\uff01\u5c5e\u4e0b\u4e3a\u4e86\u5929\u9a6c\u5802\u7684\u58f0\u8a89\uff0c\u5fcd\u65e0\u53ef\u5fcd\uff0c\u624d\u51fa\u624b\u60e9\u6cbb\u4ed6\u4eec\u3002\u201d \u3000\u3000\u201c\u5929\u9a6c\u5802\u4e2d\uff0c\u8fd8\u6709\u5982\u6b64\u5fe0\u5b9e\u7684\u90e8\u4e0b\uff0c\u771f\u662f\u53eb\u4eba\u5931\u656c\u4e86\u2026\u2026\u201d\u7533\u96ea\u541b\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u4f46\u4f60\u4e8b\u5148\u672a\u5f97\u6307\u793a\uff0c\u4e8b\u540e\u4e5f\u672a\u5411\u603b\u5802\u5448\u62a5\uff0c\u867d\u7136\u6740\u654c\u7acb\u5a01\uff0c\u4f46\u5374\u65e0\u529f\u2026\u2026\u201d \u3000\u3000\u201c\u97e9\u67d0\u4eba\u4e0d\u6c42\u6709\u529f\u2026\u2026\u201d \u3000\u3000\u201c\u8fc7\u5462\uff1f\u201d\u7533\u96ea\u541b\u51b7\u51b7\u770b\u7740\u6c5f\u67ab\u3002 \u3000\u3000\u201c\u6709\u8fc7\u5417\uff1f\u201d\u6c5f\u67ab\u8fd8\u6ce8\u7740\u7533\u96ea\u541b\u9053\uff1a\u201c\u7eb5\u7136\u6709\u8fc7\uff0c\u4e5f\u8be5\u4e0d\u7b97\u5927\u8fc7\uff0c\u5bf9\u5417\uff1f\u201d \u3000\u3000\u201c\u8fc7\u8fd8\u4e0d\u5c0f\uff0c\u6240\u4ee5\uff0c\u6211\u4eb2\u81ea\u8d76\u6765\uff0c\u8981\u628a\u4f60\u62bc\u89e3\u5230\u603b\u5802\u4e2d\u53bb\uff0c\u542c\u4faf\u5ba1\u8baf\uff01\u201d \u3000\u3000\u8bdd\u867d\u8bf4\u7684\u4e25\u5389\uff0c\u4f46\u8138\u4e0a\u4ecd\u7136\u5e26\u7740\u7b11\u5bb9\u3002 \u3000\u3000\u5979\u7b11\u7684\u5341\u5206\u52a8\u4eba\uff0c\u4f46\u4e5f\u663e\u73b0\u51fa\u4e86\u8138\u4e0a\u9c7c\u5c3e\u7eb9\u7684\u75d5\u8ff9\u3002 \u3000\u3000\u4e0d\u8bba\u4fee\u4e3a\u4f55\u7b49\u7cbe\u6df1\u7684\u4eba\uff0c\u4e5f\u65e0\u6cd5\u5b8c\u5168\u62b9\u53bb\u5c81\u6708\u7559\u4e0b\u7684\u75d5\u8ff9\u3002 \u3000\u3000\u201c\u526f\u603b\u5802\u4e3b\uff0c\u8fd9\u5c31\u6709\u4e9b\u662f\u975e\u4e0d\u5206\uff0c\u5f3a\u4eba\u6240\u96be\u4e86\uff01\u2026\u2026\u201d\u6c5f\u67ab\u5df2\u6709\u4e9b\u706b\u6c14\uff0c\u51b7\u51b7\u7684\u8bf4\u9053\uff1a \u3000\u3000\u201c\u62bc\u89e3\u4e24\u5b57\uff0c\u7528\u7684\u592a\u8fc7\u5206\u4e86\uff01\u201d \u3000\u3000\u201c\u679c\u7136\u662f\u4e00\u4e2a\u5f3a\u608d\u7684\u4eba\u7269\u2026\u2026\u201d\u7533\u96ea\u541b\u9053\uff1a\u201c\u529d\u9152\u4e0d\u5403\uff0c\u5403\u7f5a\u9152\uff0c\u90a3\u5c31\u6709\u4e9b\u5927\u715e\u98ce\u666f\u4e86\uff01\u201d \u3000\u3000\u201c\u65e2\u7136\u4e0d\u5403\uff0c\u90a3\u5c31\u7edd\u4e0d\u6cbe\u5507\uff0c\u7533\u526f\u603b\u5802\u4e3b\u4e0d\u80fd\u4e3a\u5c5e\u4e0b\u4f5c\u4e3b\uff0c\u4e5f\u5c31\u7f62\u4e86\uff0c\u4f46\u5982\u8981\u62bc\u89e3\u56de\u5230\u603b\u5802\u53d7\u5ba1\uff0c\u90a3\u5c31\u6709\u4e9b\u5f62\u540c\u52a9\u654c\u4e86\u3002\u201d \u3000\u3000\u201c\u542c\u4f60\u7684\u53e3\u6c14\uff0c\u4f3c\u662f\u8981\u6297\u547d\u62d2\u6355\u4e86\u2026\u2026\u201d\u7533\u96ea\u541b\u7684\u8138\u4e0a\u4ecd\u6709\u7b11\u610f\uff0c\u4f46\u53e3\u6c14\u5374\u66f4\u89c1\u51cc\u5389\uff0c\u63a5\u9053\uff1a\u201c\u9093\u98de\uff0c\u8fd9\u4e2a\u97e9\u9738\u662f\u4ec0\u4e48\u51fa\u8eab\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u2026\u2026\u5c5e\u4e0b\u4e0d\u592a\u6e05\u695a\u2026\u2026\u201d\u9093\u98de\u9053\uff1a\u201c\u6211\u4eec\u7528\u4eba\u552f\u624d\uff0c\u4ec0\u4e48\u51fa\u8eab\uff0c\u5c31\u67e5\u7684\u4e0d\u4e25\u4e86\uff1f\u201d \u3000\u3000\u201c\u4f60\u662f\u8bf4\uff0c\u4f60\u4e00\u70b9\u4e5f\u4e0d\u4e86\u89e3\u4e86\uff1f\u2026\u2026\u201d \u3000\u3000\u201c\u5c5e\u4e0b\u53ea\u77e5\u9053\u4ed6\u6587\u6b66\u517c\u5907\uff0c\u5341\u5206\u80fd\u5e72\uff0c\u6d1b\u9633\u5c45\u4e2d\u5f88\u591a\u96be\u9898\uff0c\u90fd\u7531\u4ed6\u51fa\u9762\u6446\u5e73\u2026\u2026\u201d \u3000\u3000\u201c\u9093\u98de\u2026\u2026\u201d \u3000\u3000\u201c\u5c5e\u4e0b\u5728\u2026\u2026\u201d \u3000\u3000\u201c\u4f60\u597d\u5927\u7684\u80c6\u5b50\u2026\u2026\u201d \u3000\u3000\u5979\u771f\u7684\u53d1\u6012\u4e86\uff0c\u4f46\u8138\u4e0a\u4ecd\u7136\u6709\u7740\u7b11\u5bb9\u3002 \u3000\u3000\u201c\u7528\u4e0d\u7740\u628a\u6012\u706b\u8f6c\u53d1\u5230\u9093\u603b\u7ba1\u7684\u5934\u4e0a\uff0c\u597d\u6c49\u505a\u4e8b\u597d\u6c49\u5f53\uff0c\u6211\u97e9\u67d0\u4eba\uff0c\u867d\u7136\u4e0d\u662f\u6c5f\u6e56\u4e0a\u7684\u540d\u4eba\uff0c\u4f46\u5927\u4e08\u592b\u751f\u6709\u4f55\u6b22\uff0c\u6b7b\u6709\u4f55\u60e7\uff1f\u8fd9\u4ef6\u4e8b\uff0c\u4e0d\u8bba\u6709\u591a\u4e48\u91cd\u5927\uff0c\u7531\u97e9\u67d0\u4eba\u4e00\u4e2a\u625b\u4e86\uff0c\u7528\u4e0d\u7740\u518d\u7275\u6d89\u5230\u522b\u4eba\u8eab\u4e0a\uff0c\u7533\u526f\u603b\u5802\u4e3b\uff0c\u5c31\u8bf7\u9ad8\u62ac\u8d35\u624b\uff0c\u653e\u8fc7\u6211\u4eec\u7684\u9093\u603b\u7ba1\u5427\uff01\u201d \u3000\u3000\u201c\u90a3\u662f\u8bf4\uff0c\u4f60\u613f\u610f\u8ddf\u6211\u56de\u5230\u603b\u5802\u53d7\u5ba1\u4e86\u2026\u2026\u201d\u7533\u96ea\u541b\u9053\uff1a\u201c\u8001\u5b9e\u8bf4\uff0c\u6211\u4eec\u5bf9\u4f60\u8fd9\u4e2a\u4eba\uff0c\u5145\u6ee1\u7740\u731c\u7591\uff1f\u6211\u4eec\u8981\u52a8\u5458\u5f88\u5e9e\u5927\u7684\u4eba\u529b\uff0c\u67e5\u660e\u4f60\u7684\u5e95\u7ec6\uff01\u8fd9\u624d\u662f\uff0c\u6211\u8981\u628a\u4f60\u5e26\u56de\u603b\u5802\u7684\u771f\u6b63\u7684\u539f\u56e0\u3002\u201d \u3000\u3000\u201c\u6211\u53ef\u4ee5\u5230\u5929\u9a6c\u603b\u5802\u8d70\u4e00\u8d9f\uff0c\u4f46\u6211\u4e0d\u559c\u6b22\u4f60\u7684\u65b9\u5f0f\uff0c\u6211\u8981\u6765\u53bb\u81ea\u5982\uff0c\u4e0d\u53d7\u76d1\u89c6\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\uff0c\u5185\u5bab\u4e00\u7cfb\u4e2d\u4eba\u6d3e\u6765\u4e86\u516d\u5927\u6740\u624b\uff0c\u53d6\u97e9\u67d0\u7684\u6027\u547d\uff0c\u5e78\u597d\uff0c\u97e9\u67d0\u4eba\u547d\u4e0d\u8be5\u7edd\uff0c\u9003\u8131\u4e86\u51e0\u6b21\u6697\u88ad\uff0c\u53ea\u53d7\u4e86\u4e00\u70b9\u8f7b\u4f24\u2026\u2026\u201d \u3000\u3000\u201c\u5509\uff01\u5185\u5bab\u4e00\u7cfb\u4e2d\u6d3e\u51fa\u7684\u4e13\u4efb\u6740\u624b\uff0c\u7adf\u662f\u5982\u6b64\u7684\u65e0\u80fd\uff1f\u2026\u2026\u201d \u3000\u3000\u201c\u4ed6\u4eec\u4e0d\u7b97\u5f88\u5dee\u4e86\uff0c\u4f24\u4e86\u97e9\u67d0\u4eba\u4e00\u53ea\u5de6\u6735\uff0c\u4f55\u51b5\u8fd8\u6709\u4e24\u4e2a\u6740\u624b\uff0c\u5c1a\u672a\u73b0\u8eab\uff0c\u4ed6\u4eec\u968f\u65f6\u53ef\u80fd\u51fa\u73b0\uff01\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u76ee\u5149\u8f6c\u52a8\uff0c\u56db\u4e0b\u6253\u91cf\uff0c\u597d\u50cf\u90a3\u4e24\u4e2a\u6740\u624b\uff0c\u5c31\u53ef\u80fd\u9690\u85cf\u5728\u8fd9\u5927\u5385\u4e4b\u4e2d\u3002 \u3000\u3000\u201c\u8fd9\u91cc\u7edd\u5bf9\u5b89\u5168\uff0c\u81f3\u5c11\uff0c\u8fd9\u5927\u5385\u4e4b\u4e2d\uff0c\u4e0d\u4f1a\u6709\u6740\u624b\u6f5c\u5165\u2026\u2026\u201d\u7533\u96ea\u541b\u7b11\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u6211\u770b\uff0c\u4f60\u4e0d\u662f\u90a3\u79cd\u7518\u5fc3\u675f\u624b\u5c31\u7f1a\u7684\u4eba\u4e86\uff0c\u4e3a\u4e86\u4e0d\u7559\u53e3\u5b9e\uff0c\u8bf4\u6211\u4ee5\u526f\u603b\u5802\u4e3b\u7684\u8eab\u4efd\uff0c\u4ee5\u5927\u538b\u5c0f\uff0c\u6240\u4ee5\uff0c\u6211\u60f3\u5c55\u793a\u4e00\u4e0b\u6b66\u529b\uff0c\u4f60\u53ef\u4ee5\u4ed4\u7ec6\u5730\u770b\u4e00\u4e0b\uff0c\u5982\u679c\u4f60\u81ea\u4fe1\u80fd\u591f\u6297\u62d2\uff0c\u5c3d\u7ba1\u51fa\u624b\u62d2\u6355\uff0c\u5982\u679c\u4f60\u89c9\u65e0\u529b\u6297\u62d2\uff0c\u90a3\u5c31\u4e56\u4e56\u7684\u8ddf\u6211\u8d70\u5427\uff01\u201d \u3000\u3000\u770b\u4e86\u516b\u4e2a\u5251\u624b\u4e00\u773c\uff0c\u6c5f\u67ab\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u5c31\u662f\u8fd9\u516b\u4f4d\u5251\u571f\u4e86\uff1f\u201d \u3000\u3000\u201c\u5bf9\uff01\u4ed6\u4eec\u516b\u4e2a\u4eba\uff0c\u53ef\u4ee5\u5bf9\u6297\u516b\u4f4d\u6b66\u6797\u4e2d\u7b2c\u4e00\u6d41\u7684\u9ad8\u624b\uff0c\u4e0d\u8981\u5c0f\u89d1\u4ed6\u4eec\u2026\u2026\u201d\u7533\u96ea\u541b\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\uff0c\u4f60\u53ef\u4ee5\u5bf9\u6297\u4ed6\u4eec\u51e0\u4e2a\uff0c\u4f46\u6211\u4eec\u5148\u8ba9\u4f60\u89c2\u6469\u4e00\u4e0b\u4ed6\u4eec\u7684\u5251\u672f\uff0c\u7ed9\u4f60\u4e00\u4e2a\u51c6\u5907\u5bf9\u6297\u4ed6\u4eec\u7684\u673a\u4f1a\uff01\u201d \u3000\u3000\u201c\u5c31\u7b97\u6211\u97e9\u67d0\u4eba\u60f3\u62d2\u7edd\uff0c\u53ea\u6015\u4e5f\u662f\u4e0d\u592a\u53ef\u80fd\u7684\u4e8b\u4e86\u2026\u2026\u201d\u6c5f\u67ab\u7b11\u9053\uff1a\u201c\u526f\u603b\u5802\u4e3b\uff0c\u6211\u5e0c\u671b\u8fd9\u7b14\u5e10\uff0c\u53ea\u7b97\u5728\u6211\u97e9\u67d0\u4eba\u4e00\u4e2a\u4eba\u7684\u8eab\u4e0a\uff0c\u4e0d\u8981\u518d\u7275\u626f\u5230\u5176\u4ed6\u4eba\u7684\u8eab\u4e0a\u4e86\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u6211\u7b54\u5e94\u4f60\uff0c\u4e0d\u7ba1\u6211\u80fd\u4e0d\u80fd\u628a\u4f60\u5e26\u56de\u603b\u5802\u53bb\u53d7\u5ba1\uff0c\u90fd\u4e0d\u7275\u626f\u4e0a\u522b\u7684\u4eba\u2026\u2026\u201d\u7533\u96ea\u541b\u7b11\u9053\uff1a\u201c\u4f60\u8fd8\u6709\u4ec0\u4e48\u8981\u6c42\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\u4e86\uff0c\u9093\u603b\u7ba1\uff0c\u8bf7\u9000\u51fa\u5927\u5385\uff0c\u6b64\u540e\uff0c\u97e9\u67d0\u4eba\u7684\u4e8b\uff0c\u7531\u6211\u4e00\u4eba\u627f\u62c5\uff0c\u7533\u526f\u603b\u5802\u4e3b\u7b54\u5e94\u4e0d\u7275\u626f\u522b\u4eba\uff0c\u6211\u76f8\u4fe1\u5979\u4e00\u8a00\u5982\u5c71\u3002\u201d \u3000\u3000\u9093\u98de\u770b\u4e86\u6c5f\u67ab\u51e0\u773c\uff0c\u6b32\u8a00\u53c8\u6b62\uff0c\u8f6c\u8eab\u5411\u5916\u884c\u53bb\u3002 \u3000\u3000\u4e5d\u5934\u9e1f\u5201\u9e4f\u4f4e\u58f0\u9053\uff1a\u201c\u8bf7\u793a\u526f\u603b\u5802\u4e3b\uff0c\u5201\u67d0\u4eba\u662f\u5426\u53ef\u4ee5\u7559\u4e0b\uff1f\u201d \u3000\u3000\u201c\u53ef\u4ee5\uff0c\u4f60\u7ad9\u5230\u6211\u7684\u8eab\u8fb9\uff0c\u514d\u5f97\u90a3\u6d41\u52a8\u7684\u5251\u6c14\uff0c\u4f24\u5bb3\u5230\u4f60\u4e86\u2026\u2026\u201d\u7533\u96ea\u541b\u7b11\u5bb9\u7a81\u655b\uff0c\u8138\u8272\u7a81\u7136\u5341\u5206\u4e25\u8083\uff0c\u8bf4\u9053\uff1a\u201c\u97e9\u9738\uff0c\u7559\u5fc3\u4e86\u2026\u2026\u201d \u3000\u3000\u7a81\u7136\u626c\u624b\u98de\u51fa\u4e86\u5341\u679a\u5236\u94b1\u3002 \u3000\u3000\u516b\u4e2a\u5251\u58eb\uff0c\u5341\u679a\u5236\u94b1\uff0c\u591a\u4e86\u4e24\u4e2a\u51fa\u6765\uff0c\u516b\u4eba\u5404\u81ea\u5bf9\u4ed8\u4e00\u4e2a\uff0c\u591a\u7684\u4e24\u4e2a\u5c31\u6210\u4e86\u5f88\u5927\u7684\u6f0f\u6d1e\u3002 \u3000\u3000\u56e0\u4e3a\uff0c\u7533\u96ea\u541b\u624b\u529b\u5f3a\u52b2\uff0c\u98de\u51fa\u7684\u5236\u94b1\uff0c\u5206\u5e03\u5728\u6574\u4e2a\u5927\u5385\u8303\u56f4\u4e4b\u4e0a\u3002 \u3000\u3000\u4f46\u89c1\u516b\u4e2a\u4eba\u5f71\uff0c\u540c\u65f6\u98de\u8d77\uff0c\u516b\u9053\u5251\u5149\uff0c\u5e03\u6210\u4e86\u4e00\u5251\u7f51\uff0c\u5168\u5385\u79fb\u52a8\uff0c\u4e00\u679a\u5236\u94b1\uff0c\u5168\u843d\u5728\u5251\u8eab\u4e4b\u4e0a\u3002 \u3000\u3000\u8fd9\u4e00\u62db\u770b\u4e0d\u51fa\u51f6\u5389\u3001\u7a00\u5947\u4e4b\u5904\uff0c\u4f46\u5374\u662f\u771f\u6b63\u7684\u529f\u592b\uff0c\u516b\u4eba\u7ed3\u5408\uff0c\u7ed5\u5385\u98de\u821e\uff0c\u4e45\u4e45\u4e0d\u843d\u5b9e\u5730\uff0c\u8fd9\u8bf4\u660e\u4e86\u6bcf\u4e2a\u4eba\u7684\u8f7b\u8eab\u529f\u592b\uff0c\u90fd\u5230\u4e86\u7089\u706b\u7eaf\u9752\u4e4b\u5883\u3002 \u3000\u3000\u516b\u5251\u5982\u4e00\uff0c\u7ed3\u5408\u4e00\u5904\uff0c\u8868\u793a\u4ed6\u4eec\u4e4b\u95f4\u914d\u5408\u7684\u7cbe\u7ec3\u3001\u7a33\u5065\u3002 \u3000\u3000\u201c\u597d\uff01\u597d\uff01\u914d\u5408\u5230\u516b\u5251\u5982\u4e00\uff0c\u679c\u7136\u662f\u975e\u540c\u5c0f\u53ef\u3002\u5e0c\u671b\u4ed6\u4eec\u6740\u4eba\u7684\u624b\u6cd5\uff0c\u4e5f\u80fd\u914d\u5408\u5230\u8fd9\u79cd\u5883\u754c\uff1f\u201d \u3000\u3000\u7533\u96ea\u541b\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u80c6\u5927\u5305\u5929\uff0c\u5f53\u4e4b\u65e0\u6127\uff0c\u53d6\u4e0b\u4f60\u7684\u4eba\u76ae\u9762\u5177\u6765\uff0c\u7ed9\u6211\u770b\u770b\u597d\u5417\uff1f\u201d \u3000\u3000\u679c\u7136\u662f\u795e\u76ee\u5982\u7535\uff0c\u7adf\u7136\u77a7\u51fa\u4e86\u6c5f\u67ab\u6234\u4e86\u4eba\u76ae\u9762\u5177\u3002 \u3000\u3000\u6c5f\u67ab\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u97e9\u67d0\u4eba\u81ea\u51fa\u9053\u4ee5\u6765\uff0c\u8fd8\u662f\u7b2c\u4e00\u4e2a\u88ab\u4eba\u77a7\u7834\u6211\u6234\u4e86\u4eba\u76ae\u9762\u5177\uff0c\u8db3\u89c1\u9ad8\u660e\uff0c\u4e0d\u8fc7\uff0c\u97e9\u67d0\u4eba\u4e5f\u6709\u82e6\u8877\uff0c\u73b0\u5728\u5185\u5bab\u4e2d\u4eba\u5df2\u6d3e\u51fa\u4e86\u516d\u5927\u6740\u624b\u8ffd\u6740\u6211\uff0c\u5982\u82e5\u6211\u97e9\u67d0\u53d6\u4e0b\u9762\u5177\uff0c\u663e\u9732\u51fa\u672c\u6765\u9762\u76ee\uff0c\u5929\u4e0b\u6740\u624b\uff0c\u5168\u8981\u96c6\u4e2d\u4e00\u5904\uff0c\u4ee5\u53d6\u6211\u4e4b\u547d\u4e3a\u9996\u8981\u4e4b\u52a1\u4e86\u3002\u201d \u3000\u3000\u201c\u4f60\u4e0d\u4f46\u80c6\u5927\u5305\u5929\uff0c\u800c\u4e14\u53e3\u820c\u5982\u5200\uff0c\u2026\u2026\u201d\u7533\u96ea\u541b\u9053\uff1a\u201c\u6740\u4e86\u4f60\uff0c\u6211\u4e00\u6837\u53ef\u4ee5\u53d6\u4e0b\u4eba\u76ae\u9762\u5177\uff0c\u770b\u5230\u4f60\u771f\u6b63\u7684\u9762\u76ee\uff0c\u6211\u4e0d\u76f8\u4fe1\u4f60\u53eb\u97e9\u9738\uff0c\u4e5f\u4e0d\u76f8\u4fe1\u51ed\u7a7a\u4f1a\u8df3\u51fa\u4f60\u8fd9\u4e48\u4e00\u53f7\u4eba\u7269\uff0c\u6811\u6709\u6839\uff0c\u6c34\u6709\u6e90\uff0c\u4e5f\u8bb8\u6211\u4eec\u8fd8\u662f\u6614\u5e74\u65e7\u8bc6\u5462\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u770b\u6765\uff0c\u526f\u603b\u5802\u4e3b\u7684\u8bef\u4f1a\u5f88\u6df1\uff0c\u6211\u53ef\u4ee5\u544a\u8bc9\u4f60\uff0c\u54b1\u4eec\u7edd\u5bf9\u6ca1\u6709\u89c1\u8fc7\uff0c\u6240\u4ee5\uff0c\u5728\u4e0b\u7528\u4e0d\u7740\u4eae\u76f8\u4e86\uff0c\u8981\u89c1\u5728\u4e0b\u7684\u771f\u9762\u76ee\uff0c\u53ea\u6709\u4e00\u4e2a\u529e\u6cd5\uff0c\u6740\u4e86\u6211\uff0c\u518d\u53d6\u4e0b\u6211\u7684\u9762\u5177\u3002\u201d \u3000\u3000\u201c\u597d\u72c2\u7684\u53e3\u6c14\uff0c\u6740\u2026\u2026\u201d\u7533\u96ea\u541b\u771f\u7684\u5fff\u6012\u4e86\uff0c\u8fd9\u4e9b\u5e74\u6765\uff0c\u5979\u517b\u5c0a\u5904\u4f18\uff0c\u54ea\u91cc\u53d7\u8fc7\u5982\u6b64\u7684\u620f\u5f04\uff1f \u3000\u3000\u516b\u9053\u5251\u5149\uff0c\u66b4\u5c04\u800c\u8d77\uff0c\u5bd2\u8292\u5c55\u5e03\uff0c\u5ffd\u4e4b\u95f4\uff0c\u5e03\u6ee1\u4e86\u6574\u5ea7\u7684\u5927\u5385\uff0c\u4f3c\u662f\u4e00\u56e2\u5149\u5c71\u822c\uff0c\u538b\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u5f53\u771f\u662f\u7ef5\u5bc6\u5982\u7f51\uff0c\u96c0\u9e1f\u96be\u6e21\u3002 \u3000\u3000\u5201\u9e4f\u5728\u6c5f\u6e56\u4e0a\u884c\u8d70\u591a\u5e74\uff0c\u5c31\u6ca1\u6709\u89c1\u8fc7\u5982\u6b64\u7684\u5251\u7f51\u3002 \u3000\u3000\u6c5f\u67ab\u4e5f\u770b\u7684\u6709\u4e9b\u60ca\u5fc3\uff0c\u4e00\u63d0\u771f\u6c14\uff0c\u8fd0\u96c6\u4e86\u5168\u8eab\u529f\u529b\uff0c\u8eab\u5b50\u5347\u8d77\uff0c\u8fce\u5411\u5251\u7f51\uff0c\u4e00\u9053\u7fe0\u7eff\u8272\u7684\u5149\u8292\uff0c\u540c\u65f6\u98de\u8d77\u3002 \u3000\u3000\u516b\u5251\u5408\u4e00\u7684\u51cc\u5389\u5251\u8292\uff0c\u7ec8\u4e8e\u903c\u7684\u6c5f\u67ab\u4eae\u51fa\u4e86\u5175\u5203\u3002 \u3000\u3000\u4f46\u95fb\u4e00\u9635\u53ee\u53ee\u549a\u549a\u7684\u8106\u54cd\uff0c\u7ef5\u5bc6\u7684\u5251\u7f51\uff0c\u88ab\u8361\u5f00\u4e86\u4e00\u4e2a\u5927\u6d1e\uff0c\u6c5f\u67ab\u7834\u7f51\u800c\u51fa\u3002 \u3000\u3000\u201c\u7fe0\u7389\u5200\u2026\u2026\u201d\u7533\u96ea\u541b\u60ca\u9053\uff1a\u201c\u5929\u72d0\u592b\u4eba\u7684\u968f\u8eab\u5175\u5203\uff0c\u600e\u4f1a\u5230\u4e86\u4f60\u7684\u624b\u4e2d\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u6b63\u662f\u7fe0\u7389\u5200\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u8fd9\u516b\u5927\u5251\u58eb\u624b\u4e2d\u7684\u78c1\u529b\u5251\uff0c\u80fd\u4f7f\u5404\u79cd\u5175\u5203\u5931\u63a7\u6613\u4f4d\uff0c\u53ef\u60dc\uff0c\u5b83\u5438\u4e0d\u4f4f\u7fe0\u7389\u5200\u3002\u201d \u3000\u3000\u201c\u4ed6\u4eec\u4e0d\u7528\u78c1\u5251\u7684\u5f15\u529b\uff0c\u7167\u6837\u53ef\u4ee5\u53d6\u4f60\u6027\u547d\uff01\u201d \u3000\u3000\u4f46\u89c1\u516b\u4e2a\u7ea2\u5e26\u5251\u58eb\uff0c\u7528\u529b\u4e00\u63a8\u5251\u67c4\uff0c\u5251\u8eab\u4e0a\u7a81\u7136\u8131\u4e0b\u4e86\u4e00\u4e2a\u5251\u5957\u3002 \u3000\u3000\u539f\u6765\uff0c\u90a3\u4e9b\u5251\u58eb\u7684\u5b9e\u5251\u4e4b\u4e0a\uff0c\u8fd8\u6234\u4e86\u534a\u4e2a\u5251\u5957\u3002 \u3000\u3000\u6240\u8c13\u7684\u534a\u4e2a\u5251\u5957\uff0c\u662f\u5957\u5728\u957f\u5251\u7684\u4e0b\u534a\u622a\uff0c\u770b\u4e0a\u53bb\uff0c\u8fd9\u628a\u5251\u6709\u4e9b\u602a\u5f02\uff0c\u4f46\u5374\u975e\u5e38\u5b9e\u7528\uff0c\u534a\u622a\u5251\u5957\u4e0a\u7684\u5f3a\u5927\u78c1\u529b\uff0c\u5728\u7075\u6d3b\u7684\u64cd\u4f5c\u4e0b\uff0c\u5e38\u5e38\u4f1a\u751f\u51fa\u610f\u60f3\u4e0d\u5230\u7684\u5999\u7528\uff0c\u5f15\u5f00\u654c\u4eba\u7684\u5175\u5203\uff0c\u90a3\u867d\u662f\u6beb\u5398\u4e4b\u5dee\uff0c\u4f46\u5728\u9ad8\u624b\u640f\u6597\u4e2d\uff0c\u5c31\u662f\u81f4\u547d\u7684\u5931\u8bef\u4e86\u3002 \u3000\u3000\u8fd9\u4e9b\u5251\u571f\uff0c\u4e0d\u4f46\u5185\u529f\u6df1\u539a\uff0c\u6280\u827a\u7cbe\u6e5b\uff0c\u800c\u4e14\u5175\u5203\u7684\u8bbe\u8ba1\uff0c\u4e5f\u8d39\u5c3d\u4e86\u4e0d\u5c11\u5fc3\u673a\uff0c\u4f3c\u4e4e\u4f4f\u4f55\u4e00\u70b9\u5fae\u672a\u5c0f\u8282\uff0c\u90fd\u5728\u529b\u6c42\u5b8c\u5584\u3001\u7280\u5229\u3002 \u3000\u3000\u6c5f\u67ab\u5fc3\u4e2d\u91cd\u65b0\u4f5c\u4e86\u4e00\u756a\u8bc4\u4f30\uff0c\u51b3\u5fc3\u5168\u529b\u65bd\u4e3a\uff0c\u4e00\u4e3e\u95f4\u640f\u6740\u5f3a\u654c\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u4e00\u756a\u4ea4\u624b\u4e4b\u540e\uff0c\u6c5f\u67ab\u5fc3\u4e2d\u4e5f\u660e\u767d\u8fd9\u516b\u4e2a\u5251\u58eb\uff0c\u6781\u5584\u5408\u640f\u4e4b\u672f\uff0c\u4e00\u4e0b\u5b50\u6740\u6b7b\u516b\u4eba\uff0c\u662f\u4e00\u4ef6\u7edd\u4e0d\u53ef\u80fd\u7684\u4e8b\uff0c\u6c5f\u67ab\u8fc5\u901f\u7684\u60f3\u8fc7\u4e86\u672c\u8eab\u51e0\u62db\u6740\u624b\u3001\u7edd\u6280\uff0c\u5c31\u60f3\u4e0d\u51fa\u4efb\u4f55\u4e00\u62db\u6b66\u529f\uff0c\u80fd\u6709\u5982\u6b64\u5f3a\u5927\u7684\u5a01\u529b\u3002 \u3000\u3000\u6cdb\u5347\u5728\u5fc3\u5934\u7684\u6740\u673a\uff0c\u7a81\u7136\u95f4\u6de1\u4e86\u4e0b\u6765\uff0c\u8fd1\u65e5\u7684\u6c5f\u6e56\u5386\u7ec3\uff0c\u4f7f\u5f97\u6c5f\u67ab\u5b66\u4f1a\u4e86\u5ba1\u5ea6\u654c\u52bf\uff0c\u4e09\u601d\u800c\u884c\uff0c\u611f\u89c9\u5230\u4e00\u51fb\u65e0\u6cd5\u6210\u529f\uff0c\u7acb\u523b\u6539\u53d8\u8ba1\u5212\uff0c\u51c6\u5907\u5148\u627e\u51fa\u654c\u4eba\u5251\u6cd5\u4e0a\u7684\u7f3a\u70b9\u540e\uff0c\u518d\u884c\u53cd\u51fb\u3002 \u3000\u3000\u4f46\u95fb\u7533\u96ea\u541b\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u5c31\u7b97\u5929\u72d0\u592b\u4eba\u8fd8\u9b42\u91cd\u751f\uff0c\u4e5f\u65e0\u6cd5\u9003\u8fc7\u4eca\u65e5\u4e4b\u5371\uff0c\u7fe0\u7389\u5200\u6cd5\u4e2d\u4ee5\u5929\u72d0\u4e03\u65a9\uff0c\u6700\u4e3a\u51cc\u5389\u51f6\u608d\uff0c\u4f60\u53ef\u4ee5\u65bd\u5c55\u51fa\u6765\u8bd5\u8bd5\uff1f\u201d \u3000\u3000\u542c\u53e3\u6c14\uff0c\u5979\u5bf9\u5929\u72d0\u592b\u4eba\u4f3c\u662f\u5341\u5206\u4e86\u89e3\u3002 \u3000\u3000\u6c5f\u67ab\u5bf9\u4e09\u4f4d\u5e08\u957f\u7684\u5f80\u4e8b\uff0c\u867d\u975e\u5341\u5206\u4e86\u89e3\uff0c\u4f46\u5374\u9690\u7ea6\u77e5\u9053\u4e09\u4f4d\u5e08\u957f\u4e4b\u95f4\uff0c\u4e5f\u6709\u7740\u4e00\u6bb5\u60c5\u4ec7\u3001\u6069\u6028\u3002 \u3000\u3000\u4f46\u4e09\u4e2a\u4eba\u90fd\u8bc6\u5927\u4f53\uff0c\u7ec8\u80fd\u629b\u5f03\u4e2a\u4eba\u7684\u597d\u6076\u4e4b\u5ff5\uff0c\u5408\u529b\u9020\u5c31\u51fa\u4ed6\u6c5f\u67ab\u8fd9\u4e2a\u4eba\uff0c\u4f7f\u4e09\u5927\u5947\u4eba\u7684\u7edd\u6280\uff0c\u96c6\u4e8e\u4e00\u4eba\u4e4b\u8eab\uff0c\u4ee5\u907f\u514d\u5404\u4f20\u8863\u94b5\uff0c\u5f15\u53d1\u4e86\u5f7c\u6b64\u4e0d\u670d\u5bf9\u65b9\u7684\u5fc3\u6001\uff0c\u8d3b\u8bef\u5927\u5c40\u3002 \u3000\u3000\u201c\u5929\u72d0\u6b66\u827a\uff0c\u4ee5\u8f7b\u529f\u3001\u6697\u5668\u89c1\u957f\u2026\u2026\u201d\u6c5f\u67ab\u5fcd\u4e0d\u4f4f\u8fa9\u9053\uff1a\u201c\u7fe0\u7389\u5200\u6cd5\u4e2d\u5929\u72d0\u4e03\u65a9\uff0c\u5e94\u8be5\u4e0d\u5217\u5165\u5929\u72d0\u7edd\u6280\u4e4b\u4e2d\u3002\u201d \u3000\u3000\u7533\u96ea\u541b\u8138\u8272\u4e00\u53d8\uff0c\u9053\uff1a\u201c\u4f60\u7a76\u7adf\u662f\u4ec0\u4e48\u4eba\uff1f\u5929\u72d0\u592b\u4eba\u7684\u5f1f\u5b50\u5417\uff1f \u3000\u3000\u5979\u81ea\u79f0\u76d7\u4ea6\u6709\u9053\uff0c\u4e3a\u4ec0\u4e48\u4f1a\u6536\u4e2a\u5927\u7537\u4eba\u4f5c\u8863\u94b5\u4f20\u4eba\uff1f\u201d \u3000\u3000\u770b\u5979\u6fc0\u52a8\u795e\u8272\uff0c\u6c5f\u67ab\u5fc3\u4e2d\u7a81\u6709\u6240\u609f\uff0c\u6697\u9053\uff1a\u201c\u83ab\u975e\u4e09\u4f4d\u5e08\u957f\u4e4b\u95f4\u7684\u6069\u6028\u60c5\u4ec7\uff0c\u548c\u5979\u6709\u5173\u5417\uff1f\u201d \u3000\u3000\u4f46\u95fb\u7533\u96ea\u541b\u63a5\u9053\uff1a\u201c\u8bf4\u8bdd\u5440\uff01\u6211\u4e5f\u8bb8\u4f1a\u653e\u4f60\u4e00\u5200\uff0c\u5982\u82e5\u4f60\u9ed8\u4e0d\u4f5c\u7b54\uff0c\u90a3\u5c31\u662f\u81ea\u5bfb\u6b7b\u8def\u4e86\u3002\u201d \u3000\u3000\u6c5f\u67ab\u7a81\u7136\u8eac\u8eab\u4e00\u793c\uff0c\u9053\uff1a\u201c\u8001\u524d\u8f88\u8ba4\u8bc6\u5929\u72d0\u592b\u4eba\u5417\uff1f\u201d \u3000\u3000\u201c\u54fc\uff01\u5c31\u7b97\u5979\u8840\u8089\u5316\u6ce5\uff0c\u5c38\u9aa8\u5316\u7070\uff0c\u6211\u4e5f\u80fd\u8ba4\u5f97\u51fa\u6765\u3002\u201d \u3000\u3000\u201c\u770b\u6765\uff0c\u8001\u524d\u8f88\u5bf9\u5979\u7684\u6068\u610f\u751a\u6df1\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u8981\u53eb\u6211\u524d\u8f88\u2026\u2026\u201d\u7533\u96ea\u541b\u63a5\u9053\uff1a\u201c\u5929\u72d0\u95e8\u4e2d\u5f1f\u5b50\uff0c\u4e0d\u914d\u53eb\u6211\u3002\u201d \u3000\u3000\u6c5f\u67ab\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5929\u72d0\u592b\u4eba\u5df2\u901d\u4e16\u4e03\u5e74\u4e86\uff0c\u8001\u524d\u8f88\u7684\u79ef\u6068\uff0c\u4e5f\u8be5\u6d88\u53bb\u4e00\u4e9b\u4e86\uff0c\u2026\u2026\u201d \u3000\u3000\u201c\u5979\u771f\u7684\u6b7b\u4e86\u2026\u2026\u201d\u7533\u96ea\u541b\u9053\uff1a\u201c\u72d0\u8ba1\u591a\u7aef\uff0c\u8c01\u80fd\u76f8\u4fe1\u90a3\u5934\u72e1\u733e\u7684\u6076\u5996\u72d0\u5462\uff1f\u201d \u3000\u3000\u201c\u8001\u524d\u8f88\uff0c\u8fd9\u5c31\u4e0d\u539a\u9053\u4e86\u2026\u2026\u201d\u6c5f\u67ab\u51b7\u7136\u8bf4\u9053\uff1a\u201c\u665a\u8f88\u8bf4\u7684\u5168\u662f\u5b9e\u8bdd\u3002\u201d \u3000\u3000\u7533\u96ea\u7fa4\u7a81\u7136\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8001\u5996\u72d0\u771f\u7684\u6b7b\u4e86\uff0c\u6211\u8fd8\u771f\u7684\u6709\u4e9b\u96be\u8fc7\uff01\u201d \u3000\u3000\u201c\u7236\u503a\u5b50\u8fd8\uff0c\u5e08\u6b20\u5f92\u8fd8\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u5728\u4e0b\u613f\u4ee3\u5e08\u507f\u8fd8\u65e7\u503a\u3002\u201d \u3000\u3000\u7533\u96ea\u7fa4\u4e24\u9053\u6e05\u6f88\uff0c\u51b7\u5389\u7684\u76ee\u5149\uff0c\u76ef\u6ce8\u5728\u6c5f\u67ab\u7684\u8138\u4e0a\u77a7\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u4f60\u771f\u662f\u72d0\u95e8\u5f1f\u5b50\uff1f\u201d \u3000\u3000\u201c\u662f\uff01\u665a\u8f88\u627f\u53d7\u4e86\u5929\u72d0\u592b\u4eba\u5927\u90e8\u5206\u7684\u6280\u827a\u3002\u201d \u3000\u3000\u7533\u96ea\u541b\u7a81\u7136\u957f\u53f9\u4e00\u58f0\uff0c\u8fde\u8fde\u6325\u624b\uff0c\u9053\uff1a\u201c\u4f60\u8d70\u5427\uff01\u8d70\u7684\u8d8a\u8fdc\u8d8a\u597d\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u8981\u53ef\u662f\u4e86\uff0c\u8fd9\u91cc\u7684\u4e8b\uff0c\u6211\u66ff\u4f60\u625b\u4e0b\u6765\uff0c\u6211\u4fdd\u8bc1\u4e0d\u4f1a\u6709\u4eba\u8ffd\u6740\u4f60\uff0c\u8bb0\u7740\uff0c\u5728\u4f60\u5e08\u7236\u7684\u575f\u524d\uff0c\u66ff\u6211\u8bf4\u4e24\u53e5\u8bdd\u2026\u2026\u201d\u7533\u96ea\u541b\u795e\u60c5\u8f6c\u53d8\u7684\u5341\u5206\u4f24\uff0c\u9053\uff1a\u201c\u6211\u6068\u5979\uff0c\u4e5f\u7231\u5979\uff0c\u6614\u65e5\u60c5\u4ec7\u4e00\u7b14\u6d88\uff0c\u8ba9\u5979\u5b89\u5fc3\u7684\u4f11\u606f\u5427\uff01\u201d \u3000\u3000\u6c5f\u67ab\u542c\u5f97\u4e00\u6014\uff0c\u6697\u9053\uff1a\u201c\u521a\u624d\u5979\u54ac\u7259\u793a\u6068\uff0c\u5927\u6709\u8a93\u4e0d\u4e24\u7acb\u7684\u6c14\u6982\uff0c\u6b64\u523b\u5374\u53c8\u9eef\u7136\u795e\u4f24\uff0c\u6709\u7740\u75db\u5931\u826f\u53cb\u7684\u60b2\u82e6\uff0c\u5f53\u771f\u662f\u4ec7\u4e2d\u6709\u60c5\uff0c\u60c5\u4e2d\u6709\u4ec7\uff1f\u5979\u4eec\u4e4b\u95f4\uff0c\u7a76\u7adf\u662f\u4ec0\u4e48\u6837\u7684\u60c5\u4ec7\u5462\uff1f\u201d \u3000\u3000\u201c\u8fd8\u4e0d\u5feb\u8d70\uff01\u7b49\u4e00\u4e0b\uff0c\u6211\u53ef\u80fd\u4f1a\u6539\u53d8\u4e3b\u610f\uff1f\u201d \u3000\u3000\u201c\u591a\u8c22\u597d\u610f\uff0c\u5728\u4e0b\u8fd8\u672a\u9886\u6559\u8fd9\u516b\u4f4d\u5251\u58eb\u7684\u9ad8\u8d85\u5251\u6cd5\uff0c\u8d70\u7684\u4e5f\u4e00\u4e0d\u7518\u5fc3\u3002\u201d \u3000\u3000\u201c\u5c31\u51ed\u4f60\u5e08\u7236\u90a3\u51e0\u62db\u5929\u72d0\u5200\u6cd5\uff0c\u7edd\u5bf9\u4e0d\u662f\u4ed6\u4eec\u7684\u654c\u624b\uff0c\u5feb\u4e9b\u9003\u547d\u53bb\u5427\uff01\u201d \u3000\u3000\u770b\u7533\u96ea\u541b\u8bf4\u7684\u5341\u5206\u8ba4\u771f\uff0c\u6c5f\u67ab\u5fc3\u4e2d\u5fd6\u9053\uff1a\u201c\u90a3\u5c31\u66f4\u8981\u9664\u53bb\u4ed6\u4eec\u4e86\uff0c\u7559\u4e0b\u4ed6\u4eec\uff0c\u5c82\u4e0d\u662f\u4e00\u5927\u7978\u60a3\u3002\u201d \u3000\u3000\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u7a81\u7136\u5927\u559d\u4e00\u58f0\uff0c\u7fe0\u7389\u5200\u7a81\u7136\u653b\u5411\u4e00\u4e2a\u5251\u58eb\u3002 \u3000\u3000\u7fe0\u7389\u5200\u5316\u4f5c\u4e00\u9053\u78a7\u8292\uff0c\u7535\u5c04\u800c\u51fa\u3002 \u3000\u3000\u8fd9\u4e00\u5200\u770b\u4f3c\u5e73\u8c08\uff0c\u4f46\u5374\u5a01\u529b\u65e0\u7a77\uff0c\u90a3\u5251\u571f\u4e3e\u5251\u4e00\u5c01\uff0c\u7adf\u7136\u672a\u80fd\u5c01\u4f4f\u3002 \u3000\u3000\u78a7\u5149\u4e2d\u5c04\u51fa\u4e86\u4e00\u9053\u8840\u6cc9\u3002 \u3000\u3000\u7389\u5200\u6b63\u4e2d\u54bd\u5589\u3002 \u3000\u3000\u6c5f\u67ab\u8fd9\u7a81\u7136\u4e00\u51fb\uff0c\u5201\u9e4f\u6709\u7740\u5927\u51fa\u610f\u5916\u4e4b\u611f\uff0c\u5fcd\u4e0d\u4f4f\u53eb\u4e86\u4e00\u58f0\uff01 \u3000\u3000\u7533\u96ea\u541b\u5374\u5931\u58f0\u53eb\u9053\uff1a\u201c\u7a7f\u5589\u4e00\u5251\uff0c\u4f60\u2026\u2026\u4f60\u662f\u91d1\u4e39\u4e66\u751f\u7684\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u201c\u5f1f\u5b50\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u795e\u60c5\u8083\u7136\u5730\u8bf4\u9053\uff1a\u201c\u8001\u524d\u8f88\u4e5f\u8ba4\u8bc6\u4ed6\u4e86\uff1f\u201d \u3000\u3000\u201c\u4f60\u4e5f\u627f\u7ee7\u4e86\u91d1\u4e39\u4e66\u751f\u7684\u8863\u94b5\u2026\u2026\u201d \u3000\u3000\u7533\u96ea\u541b\u4f3c\u662f\u7a81\u7136\u9677\u5165\u4e86\u4e00\u79cd\u6c89\u75db\u7684\u56de\u5fc6\u4e2d\uff0c\u8138\u8272\u82cd\u767d\uff0c\u808c\u8089\u98a4\u52a8\uff0c\u5185\u5fc3\u4e4b\u4e2d\u53d7\u5230\u7684\u51b2\u51fb\uff0c\u4f3c\u662f\u5f88\u5927\u3002 \u3000\u3000\u201c\u4e0d\u9519\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u665a\u8fdb\u627f\u7ee7\u4ed6\u4eec\u7684\u6280\u827a\uff0c\u4e5f\u627f\u7ee7\u4e86\u4ed6\u4eec\u7684\u5fc3\u613f\u3002\u201d \u3000\u3000\u201c\u4ed6\u4eec\u6709\u4ec0\u4e48\u5fc3\u613f\uff0c\u2026\u2026\u201d\u7533\u96ea\u541b\u9053\uff1a\u201c\u5bf9\u6211\u62a5\u590d\uff0c\u8fd8\u662f\u8981\u6740\u6389\u590f\u5929\u540c\uff01\u201d \u3000\u3000\u201c\u6ca1\u6709\uff0c\u4ed6\u4eec\u7684\u665a\u5e74\u4fee\u517b\uff0c\u90fd\u5df2\u8fdb\u5165\u4e86\u5316\u5883\uff0c\u80f8\u4e2d\u65e9\u5df2\u4e0d\u8bb0\u4ec7\u6068\uff0c\u90a3\u91cc\u8fd8\u4f1a\u6709\u6740\u4eba\u7684\u5ff5\u5934\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u4ed6\u4eec\u5ff5\u5ff5\u4e0d\u5fd8\u7684\u662f\u6b66\u6797\u6b63\u4e49\u3001\u6c5f\u6e56\u5927\u5c40\uff0c\u4ed6\u4eec\u7684\u5fc3\u613f\u5c31\u662f\u6c5f\u6e56\u91cd\u5149\uff0c\u518d\u89c1\u5929\u65e5\uff0c\u8001\u524d\u8f88\u662f\u4ed6\u4eec\u7684\u6545\u65e7\uff0c\u5e0c\u671b\u770b\u5728\u4e24\u4f4d\u8001\u524d\u8f88\u7684\u751f\u524d\u4ea4\u60c5\uff0c\u52a9\u665a\u8fdb\u4e00\u81c2\u4e4b\u529b\uff0c\u5b8c\u6210\u4ed6\u4eec\u7684\u9057\u5fd7\u2026\u2026\u201d \u3000\u3000\u201c\u4f60\u60f3\u7684\u5f88\u597d\uff01\u6211\u4e2a\u4eba\u4e5f\u6709\u8fd9\u4efd\u610f\u613f\uff0c\u53ef\u60dc\uff0c\u6211\u4e0d\u80fd\u2026\u2026\u201d\u7533\u96ea\u541b\u9053\uff1a\u201c\u4f60\u51e0\u65f6\u89c1\u8fc7\uff0c\u6709\u4eba\u62ff\u7740\u5bf9\u6297\u654c\u4eba\u7684\u5200\u3001\u5251\uff0c\u6765\u6740\u5bb3\u81ea\u5df1\uff1f\u201d \u3000\u3000\u201c\u539f\u6765\uff0c\u4f60\u5c31\u662f\u5236\u9020\u8fd9\u4e00\u6b21\u6c5f\u6e56\u5927\u52ab\u7684\u4eba\uff1f\u2026\u2026\u201d\u6c5f\u67ab\u795e\u60c5\u51b7\u8083\u5730\u8bf4\uff0c\u201c\u6709\u4e9b\u4e8b\uff0c\u8e0f\u7834\u94c1\u978b\u65e0\u89c5\u5904\uff0c\u4f46\u9e3f\u8fd0\u5f53\u5934\u65f6\uff0c\u5374\u53c8\u5f97\u6765\u5168\u4e0d\u8d39\u5de5\u592b\uff01\u201d \u3000\u3000\u201c\u4e0d\u8981\u60f3\u7684\u592a\u7b80\u5355\uff0c\u6211\u4e0d\u8fc7\u662f\u5176\u4e2d\u4e00\u4e2a\u5fae\u4e0d\u8db3\u9053\u7684\u5c0f\u4eba\u7269\uff0c\u4f46\u5bf9\u4f60\u6765\u8bf4\uff0c\u5374\u662f\u5927\u5927\u7684\u4e0d\u5999\u4e86\uff0c\u2026\u2026\u201d\u7533\u96ea\u541b\u9053\uff1a\u201c\u4f60\u5fc5\u987b\u5148\u95ef\u8fc7\u8fd9\u516b\u4e2a\u5251\u58eb\u56f0\u6740\u2026\u2026\u201d \u3000\u3000\u201c\u5728\u4e0b\u539f\u4e5f\u6ca1\u6709\u9003\u907f\u7684\u6253\u7b97\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u4f60\u53ef\u4ee5\u4e0b\u4ee4\u5979\u4eec\u51fa\u624b\u4e86\uff01\u201d \u3000\u3000\u7533\u96ea\u541b\u4f3c\u662f\u7a81\u7136\u53d7\u5230\u4e86\u4e00\u4e0b\u9488\u523a\u822c\uff0c\u5168\u8eab\u98a4\u52a8\u4e86\u4e00\u4e0b\uff0c\u53f3\u624b\u6325\u51fa\u558a\u4e86\u4e00\u4e2a\u6740\u5b57\uff0c\u5ffd\u7136\u8f6c\u8fc7\u8eab\u5b50\uff0c\u5feb\u6b65\u800c\u53bb\u3002 \u3000\u3000\u6c5f\u67ab\u6ca1\u6709\u673a\u4f1a\u770b\u6e05\u695a\u8fc7\u7a0b\uff0c\u56e0\u4e3a\u4e03\u4e2a\u5251\u571f\uff0c\u5df2\u5c55\u5f00\u4e86\u51cc\u5389\u7684\u653b\u52bf\u3002 \u3000\u3000\u4ee5\u5201\u9e4f\u89c1\u8bc6\u4e4b\u5e7f\uff0c\u640f\u6740\u4e4b\u591a\uff0c\u4e5f\u6ca1\u6709\u89c1\u5982\u6b64\u51f6\u5389\u7684\u653b\u52bf\uff0c\u50cf\u4e03\u9053\u66b4\u8d77\u7684\u95ea\u5149\uff0c\u4e03\u9053\u5bd2\u8292\uff0c\u540c\u65f6\u653b\u5411\u6c5f\u67ab\u3002 \u3000\u3000\u4e03\u53ea\u5251\u5728\u63a5\u8fd1\u6c5f\u67ab\u65f6\uff0c\u7a81\u7136\u5e7b\u5316\u51fa\u4e00\u7247\u5149\u5f71\uff0c\u5149\u7ec7\u6210\u4e00\u7247\u5251\u5e55\uff0c\u63b7\u5411\u6c5f\u67ab\u3002 \u3000\u3000\u90a3\u771f\u662f\u6ef4\u6c34\u4e0d\u9732\u7684\u4e00\u51fb\uff0c\u4ee4\u4eba\u773c\u82b1\u7f2d\u4e71\uff0c\u83ab\u53ef\u9884\u6d4b\u3002 \u3000\u3000\u4e5f\u662f\u65e0\u6cd5\u95ea\u907f\u7684\u4e00\u6b21\u5b8c\u5584\u653b\u52bf\u3002 \u3000\u3000\u6c5f\u67ab\u6ca1\u6709\u95ea\u907f\uff0c\u56e0\u4e3a\uff0c\u6ca1\u6709\u4efb\u4f55\u4e00\u79cd\u8eab\u6cd5\u80fd\u591f\u95ea\u907f\u5f00\u8fd9\u4e48\u7ef5\u5bc6\u7684\u4e00\u51fb\u3002 \u3000\u3000\u552f\u4e00\u7684\u529e\u6cd5\uff0c\u5c31\u662f\u5168\u529b\u627f\u63a5\u4e0b\u6765\u3002 \u3000\u3000\u6c5f\u67ab\u98de\u8eab\u800c\u8d77\uff0c\u8fce\u5411\u5251\u5e55\u3002 \u3000\u3000\u4eba\u5728\u98de\u8d77\u7684\u540c\u65f6\uff0c\u4e00\u9053\u78a7\u5149\u7ed5\u8eab\u800c\u8d77\u3002 \u3000\u3000\u5c31\u662f\u90a3\u4e48\u4e00\u8dc3\u4e4b\u95f4\uff0c\u7fe0\u7389\u5200\u5df2\u548c\u4eba\u5408\u800c\u4e3a\u4e00\u3002 \u3000\u3000\u4f46\u95fb\u4e00\u9635\u91d1\u7389\u4ea4\u51fb\u7684\u8106\u54cd\uff0c\u6709\u5982\u73e0\u8d70\u7389\u76d8\uff0c\u4e00\u5927\u7247\u95ea\u5149\u7684\u767d\u8292\u73af\u56f4\u7740\u4e00\u78a7\u5149\u5728\u95e8\u8f6c\u3001\u6d41\u52a8\u3002 \u3000\u3000\u8fd9\u90a3\u91cc\u50cf\u7528\u5200\u3001\u5251\u5728\u62fc\u6597\uff0c\u5012\u50cf\u4e00\u6b21\u6781\u96be\u89c1\u5230\u7684\u81ea\u7136\u666f\u89c2\uff0c\u4e00\u5927\u4e00\u5c0f\u4e24\u56e2\u6eda\u52a8\u7684\u5149\u8292\u5728\u51b2\u51fb\u3002 \u3000\u3000\u867d\u7136\u5200\u5251\u90fd\u63e1\u5728\u4ed6\u4eec\u7684\u624b\u4e2d\uff0c\u4f46\u7cbe\u5999\u719f\u7ec3\u7684\u5200\u5251\u62db\u672f\u53d8\u5316\uff0c\u5df2\u5230\u4e86\u5200\u3001\u5251\u4e4b\u6700\u9ad8\u7684\u5883\u754c\uff0c\u50cf\u4f20\u8bf4\u4e2d\u7684\u5251\u4ed9\u4e00\u822c\uff0c\u5200\u3001\u5251\u4eba\u5408\u800c\u4e3a\u4e00\u3002 \u3000\u3000\u5201\u9e4f\u770b\u5446\u4e86\u3002 \u3000\u3000\u7533\u96ea\u7fa4\u4e5f\u770b\u5446\u4f4f\u4e86\u3002 \u3000\u3000\u8fd9\u516b\u4e2a\u7ecf\u8fc7\u957f\u671f\u8bad\u7ec3\u800c\u6210\u7684\u5251\u58eb\uff0c\u5728\u5979\u7684\u5fc3\u76ee\u4e2d\u65e0\u575a\u4e0d\u6467\uff0c\u4ed6\u4eec\u662f\u7cbe\u6311\u7ec6\u9009\uff0c\u5343\u4e2d\u5bfb\u4e00\u7684\u5b66\u5251\u4eba\u624d\uff0c\u518d\u7ecf\u8bad\u7ec3\u6dd8\u6c70\uff0c\u4e00\u767e\u591a\u4eba\uff0c\u5165\u9009\u4e3a\u7ea2\u5e26\u5251\u571f\u7684\u53ea\u4e0d\u8fc7\u4e00\u5341\u516b\u4eba\uff0c\u9664\u4e24\u4e2a\u6210\u5c31\u7279\u9ad8\uff0c\u53e6\u6709\u4efb\u52a1\u5916\uff0c\u4f59\u4e0b\u5341\u516d\u4eba\u5206\u4f5c\u4e24\u7ec4\uff0c\u6bcf\u7ec4\u516b\u4eba\u3002 \u3000\u3000\u4ee5\u4ed6\u4eec\u5251\u672f\u4e4b\u7cbe\uff0c\u5b9e\u5df2\u81f3\u7edd\u9876\u9ad8\u624b\u7684\u5883\u754c\uff0c\u5929\u4e0b\u7528\u5251\u9ad8\u4eba\uff0c\u4ee5\u4e00\u5bf9\u4e00\u7684\uff0c\u5df2\u7136\u4e0d\u591a\uff0c\u80fd\u4ee5\u4e00\u5bf9\u516b\u7684\uff0c\u662f\u7edd\u65e0\u4ec5\u6709\u4e86\u3002 \u3000\u3000\u4f46\u6c5f\u67ab\u5c31\u662f\u7edd\u65e0\u4ec5\u6709\u4e2d\u7684\u4e00\u4e2a\uff0c\u5c31\u90a3\u4e48\u5de7\u5999\u7684\u78b0\u4e0a\u4e86\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u8fd9\u4e0d\u662f\u5de7\u5408\uff0c\u51e0\u4e4e\u662f\u5fc5\u7136\u7684\u7ed3\u679c\uff0c\u53cc\u65b9\u90fd\u82b1\u4e86\u5341\u51e0\u5e74\u7684\u5fc3\u529b\uff0c\u5728\u826f\u5e08\u4e25\u5389\u7684\u7763\u4fc3\u4e0b\uff0c\u4e60\u7ec3\u51fa\u4e00\u6d41\u7684\u6b66\u529f\uff0c\u8981\u5bf9\u6297\u7684\u4eba\u7269\uff0c\u4e5f\u5c06\u662f\u4e00\u6d41\u7684\u5f3a\u654c\uff0c\u7533\u96ea\u541b\u56e0\u9707\u4e8e\u97e9\u9738\u4e4b\u5a01\uff0c\u624d\u5e26\u4e86\u8fd9\u7b2c\u4e00\u6d41\u7684\u5251\u58eb\u8d76\u6765\uff0c\u4e0d\u60dc\u6cc4\u9732\u51fa\u5929\u9a6c\u603b\u5802\u4e2d\u9690\u85cf\u7684\u79d8\u5bc6\u5b9e\u529b\u2026\u2026 \u3000\u3000\u5f53\u7136\uff0c\u5185\u5bab\u4e00\u7cfb\u4e2d\u4eba\u82e6\u82e6\u76f8\u903c\uff0c\u5929\u9a6c\u5802\u9a6c\u51c6\u5907\u4eae\u51fa\u4e00\u4e9b\u5b9e\u529b\uff0c\u8ba9\u5bf9\u65b9\u77e5\u96be\u800c\u9000\uff0c\u5374\u4e0d\u6599\uff0c\u9047\u4e0a\u4e86\u771f\u6b63\u7684\u5f3a\u654c\u3002 \u3000\u3000\u6c5f\u67ab\u7684\u201c\u7a7f\u5589\u4e00\u5200\u201d\u80fd\u4e00\u4e3e\u6740\u6b7b\u4e86\u4e00\u4e2a\u7b2c\u4e00\u6d41\u7684\u5251\u624b\uff0c\u56fa\u7136\u662f\u91d1\u4e39\u4e66\u751f\u7684\u5200\u6cd5\u7cbe\u5999\uff0c\u8be1\u5f02\u591a\u53d8\uff0c\u4ee4\u4eba\u96be\u9632\uff0c\u4f46\u6c5f\u67ab\u5982\u4e0d\u80fd\u628a\u5200\u6cd5\u7ec3\u5165\u5316\u5883\uff0c\u4e5f\u65e0\u6cd5\u4e00\u51fb\u53d6\u547d\u3002 \u3000\u3000\u4f46\u91d1\u4e39\u4e66\u751f\u52a0\u4e0a\u5929\u72d0\u5200\u6cd5\uff0c\u4e5f\u65e0\u6cd5\u6297\u62d2\u8fd9\u4e03\u5927\u5251\u58eb\u7684\u5408\u51fb\u3002 \u3000\u3000\u4f46\u6c5f\u67ab\u7adf\u7136\u80fd\u652f\u6491\u4e0b\u6765\u3002 \u3000\u3000\u7fe0\u7389\u5200\u548c\u4e03\u53ea\u7cbe\u94a2\u957f\u5251\uff0c\u649e\u51fb\u4e86\u6570\u767e\u6b21\u540e\uff0c\u505c\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u53cc\u65b9\u843d\u7740\u5b9e\u5730\u3002 \u3000\u3000\u6c5f\u67ab\u548c\u516d\u4e2a\u5251\u58eb\u4e00\u6837\uff0c\u9762\u8272\u82cd\u767d\uff0c\u4e0d\u505c\u7684\u5598\u606f\u3002 \u3000\u3000\u663e\u7136\uff0c\u53cc\u65b9\u90fd\u8017\u5c3d\u4e86\u4f53\u80fd\uff0c\u65e0\u6cd5\u518d\u652f\u6491\u4e0b\u53bb\uff0c\u4e0d\u5f97\u4e0d\u505c\u4e0b\u4f11\u606f\u3002 \u3000\u3000\u4e0d\u540c\u7684\u662f\u6c5f\u67ab\u4ee5\u4e00\u5bf9\u4e03\u3002 \u3000\u3000\u5982\u82e5\u6c5f\u67ab\u6709\u673a\u4f1a\u4e00\u5bf9\u4e00\u7684\u548c\u8fd9\u4e9b\u5251\u571f\u52a8\u624b\uff0c\u76f8\u4fe1\u6ca1\u6709\u4eba\u80fd\u907f\u5f00\u91d1\u4e39\u4e66\u751f\u7559\u4f20\u7684\u4e09\u5927\u7edd\u6280\u4e4b\u4e00\u201c\u7a7f\u5589\u4e00\u5200\u201d\u3002 \u3000\u3000\u7533\u96ea\u541b\u8138\u4e0a\u95ea\u52a8\u8d77\u6740\u673a\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u73b0\u5728\uff0c\u4f60\u8fd8\u80fd\u63a5\u6211\u4e00\u51fb\u5417\uff1f\u201d \u3000\u3000\u201c\u7533\u5927\u59d1\u5a18\uff0c\u8fd9\u65f6\u523b\uff0c\u4f60\u51fa\u624b\u5bf9\u4ed8\u4e00\u4e2a\u665a\u8fdb\uff0c\u4e0d\u6015\u843d\u4eba\u8bdd\u67c4\u5417\uff1f\u201d \u3000\u3000\u5927\u5385\u95e8\u53e3\u5904\uff0c\u51fa\u73b0\u4e86\u4e00\u4e2a\u7ea2\u5149\u6ee1\u9762\u7684\u9752\u888d\u5927\u6c49\uff0c\u63a5\u9053\uff1a\u201c\u5982\u82e5\u7533\u5927\u59d1\u5a18\u4e00\u5b9a\u8981\u51fa\u624b\uff0c\u53ea\u7531\u533a\u533a\u820d\u547d\u7275\u966a\u4e86\u3002\u201d \u3000\u3000\u7533\u96ea\u541b\u51b7\u5389\u7684\u76ee\u5149\uff0c\u76ef\u4f4f\u5728\u9752\u888d\u5927\u6c49\u7684\u8138\u4e0a\uff0c\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u201d \u3000\u3000\u663e\u7136\uff0c\u5979\u8ba4\u4e0d\u51fa\u8fd9\u5927\u6c49\u7684\u8eab\u4efd\u3002 \u3000\u3000\u4f46\u6c5f\u67ab\u5df2\u542c\u51fa\u6765\uff0c\u4ed6\u662f\u4e03\u5b9d\u548c\u5c1a\u88c5\u626e\u3002 \u3000\u3000\u5f20\u56db\u59d1\u6613\u5bb9\u672f\u679c\u7136\u662f\u7edd\u5999\u65e0\u65b9\uff0c\u7533\u96ea\u541b\u7684\u8fc7\u4eba\u773c\u529b\uff0c\u4e5f\u77a7\u4e0d\u51fa\u4e00\u70b9\u7834\u7efd\u3002 \u3000\u3000\u201c\u7533\u59d1\u5a18\u6700\u5927\u7684\u9519\u8bef\u4e4b\u4e00\uff0c\u662f\u4f4e\u4f30\u5929\u72d0\u592b\u4eba\u7684\u6280\u827a\uff0c\u5929\u72d0\u9488\u80fd\u501f\u98ce\u529b\u4f24\u4eba\u2026\u2026\u201d\u9752\u888d\u4eba\u6ca1\u6709\u56de\u7b54\u7533\u96ea\u541b\u7684\u95ee\u9898\uff0c\u5374\u81ea\u8bf4\u81ea\u8bdd\u7684\u63a5\u9053\uff1a\u201c\u4f60\u5e26\u6765\u4e03\u4e2a\u5251\u58eb\uff0c\u90fd\u5df2\u7ecf\u4f24\u5728\u5929\u72d0\u9488\uff0c\u4e24\u4e2a\u65f6\u8fb0\u5185\u4e0d\u65bd\u672f\u6551\u6cbb\uff0c\u4ed6\u4eec\u5c31\u6b7b\u4e86\uff0c\u4f60\u4e00\u4e3e\u95f4\u5931\u53bb\u4e86\u516b\u5927\u5251\u571f\uff0c\u8981\u5982\u4f55\u5411\u603b\u5802\u4e3b\u4ea4\u4ee3\u5462\uff1f\u201d \u3000\u3000\u7533\u96ea\u541b\u8138\u8272\u4e00\u53d8\uff0c\u9053\uff1a\u201c\u4ed6\u8bf4\u7684\u662f\u771f\u662f\u5047\uff1f\u201d \u3000\u3000\u201c\u662f\u771f\u7684\u2026\u2026\u201d\u4e00\u4e2a\u7ea2\u5e26\u5251\u58eb\u8bf4\uff0c\u201c\u6211\u4eec\u867d\u4e2d\u4e86\u5929\u72d0\u9488\uff0c\u4f46\u4ecd\u6709\u518d\u6218\u4e4b\u80fd\uff01\u201d \u3000\u3000\u7533\u96ea\u541b\u9053\uff1a\u201c\u90a3\u53ea\u6709\u52a0\u5feb\u4f60\u4eec\u6b7b\u4ea1\u7684\u901f\u5ea6\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u7ea2\u5e26\u5251\u571f\uff0c\u89c6\u6b7b\u5982\u5f52\uff01\u201d\u4e03\u4e2a\u5251\u58eb\uff0c\u540c\u65f6\u9ad8\u58f0\u56de\u7b54\u3002 \u3000\u3000\u201c\u5c31\u7b97\u4f60\u4eec\u613f\u610f\u6218\u6b7b\uff0c\u6211\u4e5f\u4e0d\u80fd\u8ba9\u4f60\u4eec\u6b7b\uff0c\u8ddf\u6211\u8d70\uff01\u201d\u98de\u8eab\u4e00\u8dc3\u3002 \u3000\u3000\u51fa\u4e86\u5927\u5385\u3002 \u3000\u3000\u4e03\u4e2a\u5251\u58eb\u540c\u65f6\u98d8\u8eab\u800c\u8d77\uff0c\u968f\u540e\u8ffd\u53bb\u3002 \u3000\u3000\u4f46\u89c1\u4eba\u5f71\u4e00\u95ea\uff0c\u5927\u5385\u4e00\u89d2\u5904\uff0c\u7a81\u7136\u95ea\u51fa\u4e86\u4e24\u6761\u4eba\u5f71\u3002 \u3000\u3000\u7adf\u662f\u4e24\u4e2a\u5c0f\u72d0\u5973\uff0c\u541f\u96ea\u3001\u541f\u971c\u3002 \u3000\u3000\u9752\u888d\u4eba\u5bf9\u4e8c\u5973\u70b9\u5934\u5fae\u7b11\u7740\uff0c\u884c\u5165\u4e86\u5927\u5385\u3002 \u3000\u3000\u201c\u679c\u7136\u662f\u5929\u72d0\u767e\u53d8\uff0c\u6211\u548c\u5c1a\u5c31\u6ca1\u6709\u77a7\u51fa\u6765\uff0c\u4f60\u4eec\u7684\u9690\u8eab\u6240\u5728\uff1f\u201d \u3000\u3000\u201c\u8fd9\u662f\u5f20\u59e8\u7684\u6f5c\u6728\u672f\u2026\u2026\u201d\u541f\u96ea\u9053\uff1a\u201c\u53ef\u4e0d\u662f\u5929\u72d0\u95e8\u7684\u6b66\u529f\u3002\u201d \u3000\u3000\u201c\u4e94\u884c\u6f5c\u672f\uff0c\u679c\u7136\u662f\u795e\u4e4e\u5176\u6280\uff0c\u6211\u548c\u5c1a\u4eca\u5929\u662f\u5927\u5f00\u773c\u754c\uff0c\u5fc3\u91cc\u60f3\u4e0d\u670d\u4e5f\u4e0d\u6210\u4e86\u3002\u201d \u3000\u3000\u201c\u548c\u5c1a\u4f2f\u4f2f\uff0c\u6709\u4e8b\u6c42\u6211\u4eec\u5c3d\u7ba1\u4ec0\u53e3\uff0c\u7528\u5c0f\u7740\u8fd9\u6837\u704c\u8ff7\u6c64\u4e86\u2026\u2026\u201d \u3000\u3000\u541f\u971c\u5fae\u7b11\u5982\u82b1\u5730\u8bf4\uff0c\u201c\u4e0d\u8fc7\uff0c\u6211\u5012\u8981\u5148\u8bf7\u6559\u548c\u5c1a\u4f2f\u4f2f\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u201c\u884c\uff01\u5341\u4ef6\u516b\u4ef6\u90fd\u884c\uff0c\u548c\u5c1a\uff0c\u201d\u63a5\u9053\uff1a\u201c\u4e00\u80a1\u8111\u513f\u8bf4\u51fa\u6765\u3002\u201d \u3000\u3000\u201c\u90a3\u4e03\u4e2a\u5251\u58eb\u7684\u5251\u6cd5\u5982\u4f55\uff1f\u201d \u3000\u3000\u201c\u9ad8\u660e\u65e0\u5339\uff0c\u5251\u672f\u4e4b\u7cbe\uff0c\u5df2\u7efc\u5408\u4e86\u5929\u4e0b\u5251\u6cd5\u4e4b\u957f\u2026\u2026\u201d \u3000\u3000\u201c\u4ed6\u4eec\u548c\u6c5f\u5927\u54e5\u4ea4\u63a5\u767e\u62db\uff0c\u4e0d\u5206\u80dc\u8d1f\uff0c\u5f53\u7136\u662f\u7b2c\u4e00\u6d41\u7684\u5251\u624b\u2026\u2026\u201d\u541f\u9738\u9053\uff1a\u201c\u4ed6\u4eec\u591a\u6b7b\u4e00\u4e2a\uff0c\u6c5f\u5927\u54e5\u5c31\u5c11\u4e86\u4e00\u4e2a\u52b2\u654c\uff0c\u4f60\u77e5\u9053\u4ed6\u4eec\u4e2d\u4e86\u5929\u72d0\u9488\uff0c\u5341\u4e8c\u4e2a\u65f6\u8fb0\u540e\uff0c\u9488\u968f\u884c\u8840\uff0c\u63d2\u5165\u5fc3\u810f\uff0c\u5fc5\u6b7b\u65e0\u7591\uff0c\u4f60\u4e3a\u4ec0\u4e48\u8981\u544a\u8bc9\u4ed6\u4eec\uff1f\u201d \u3000\u3000\u201c\u4e0d\u544a\u8bc9\u4ed6\u4eec\uff0c\u4ed6\u4eec\u4e5f\u5df2\u77e5\u9053\uff0c\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u9053\uff1a\u201c\u4f55\u51b5\uff0c\u5385\u95e8\u8ddd\u6b64\uff0c\u6709\u56db\u4e08\u4e4b\u9065\uff0c\u4f60\u4eec\u7684\u6c5f\u5927\u54e5\uff0c\u5df2\u5185\u529b\u8017\u5c3d\uff0c\u7533\u96ea\u541b\u771f\u8981\u51fa\u624b\uff0c\u4ed6\u672a\u5fc5\u80fd\u63a5\u5f97\u4e0b\u6765\uff0c\u548c\u5c1a\u4e5f\u6ca1\u6709\u81ea\u4fe1\u80fd\u53ca\u65f6\u8d76\u5230\u3002\u201d \u3000\u3000\u201c\u539f\u6765\u5982\u6b64\uff0c\u662f\u6211\u9519\u602a\u4f60\u4e86\uff0c\u2026\u2026\u201d\u541f\u971c\u7acb\u523b\u8ba4\u9519\u53c8\u9053\uff1a\u201c\u548c\u5c1a\u4f2f\u4f2f\u8981\u4ec0\u4e48\uff1f\u5429\u5490\u4e00\u58f0\u5427\uff01\u201d \u3000\u3000\u201c\u548c\u5c1a\u60f3\u5b66\u4e00\u70b9\u4e94\u884c\u6f5c\u672f\uff0c\u4e24\u4f4d\u59d1\u5a18\uff0c\u80fd\u5426\u6210\u5168\u5462\uff1f\u201d \u3000\u3000\u201c\u8fd9\u5c31\u5947\u602a\u4e86\u2026\u2026\u201d\u53f6\u96ea\u9053\uff1a\u201c\u4e94\u884c\u6f5c\u672f\u662f\u5f20\u59e8\u7684\u72ec\u95e8\u5947\u672f\uff0c\u6211\u4eec\u53ea\u662f\u521d\u5b66\u4e4d\u7ec3\uff0c\u548c\u5c1a\u4f2f\u4f2f\u53ea\u8981\u80af\u5f00\u53e3\uff0c\u6211\u4fdd\u8bc1\u5f20\u59e8\u4f1a\u4e00\u53e3\u5e94\u5141\u3002\u201d \u3000\u3000\u201c\u8fd9\u6837\u5427\uff01\u2026\u2026\u201d\u541f\u971c\u63a5\u9053\uff1a\u201c\u4f60\u4e0d\u597d\u610f\u601d\u5f00\u53e3\uff0c\u6211\u66ff\u4f60\u8bf4\u4e00\u58f0\uff0c\u800c\u4e14\u4fdd\u8bc1\u4f60\u5982\u613f\u4ee5\u507f\u3002\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u7a81\u7136\u7741\u5f00\u4e86\u773c\u775b\uff0c\u7b11\u9053\uff1a\u201c\u5927\u548c\u5c1a\u60f3\u5b66\u4f60\u4eec\u7684\u4e94\u884c\u6f5c\u672f\uff0c\u5fc5\u7136\u6709\u7edd\u6280\u56de\u62a5\uff0c\u4e24\u4f4d\u600e\u4e48\u5ffd\u7136\u95f4\u72d0\u6027\u6d88\u5931\uff0c\u53d8\u6210\u4e86\u7eaf\u6d01\u5c0f\u59d1\u5a18\u4e86\uff1f\u201d \u3000\u3000\u201c\u660e\u767d\u4e86\uff0c\u548c\u5c1a\u4f2f\u4f2f\u8981\u7528\u4ec0\u4e48\u4ea4\u6362\u5462\uff1f\u2026\u2026\u201d\u541f\u971c\u9053\uff1a\u201c\u6211\u4f20\u4f60\u6f5c\u6728\u672f\uff0c\u662f\u4e94\u884c\u6f5c\u672f\u4e2d\u65bd\u7528\u6700\u591a\u7684\u4e00\u884c\u3002\u201d \u3000\u3000\u201c\u6211\u4f20\u4f60\u6f5c\u571f\u6728\uff0c\u6f5c\u6c34\u672f\u2014\u2026\u3001\u201d\u541f\u96ea\u9053\uff1a\u201c\u81f3\u4e8e\u6f5c\u91d1\u3001\u6f5c\u706b\uff0c\u6211\u4eec\u81ea\u5df1\u8fd8\u672a\u7ec3\u597d\uff0c\u90a3\u91cc\u8fd8\u80fd\u4f20\u4eba\uff1f\u201d \u3000\u3000\u201c\u5b66\u4f1a\u4e86\u6c34\u3001\u6728\u3001\u571f\u4e09\u884c\u6f5c\u672f\uff0c\u5df3\u53ef\u884c\u904d\u5929\u4e0b\uff0c\u91d1\u3001\u706b\u4e8c\u672f\uff0c\u4e0d\u7ec3\u4e5f\u7f62\uff0c\u4e0d\u8fc7\uff0c\u548c\u5c1a\u53ef\u6ca1\u6709\u90a3\u4e48\u591a\u672c\u9886\u4ea4\u6362\uff0c\u53ea\u80fd\u4f20\u4e24\u4f4d\u59d1\u5a18\u4e00\u6bb5\u5c0f\u66f2\u513f\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u4ec0\u4e48\uff1f\u5b66\u5531\u6b4c\u5440\uff01\u548c\u5c1a\u4f2f\u4f2f\uff0c\u4f60\u53ef\u662f\u771f\u80fd\u6405\u548c\u5440\uff01\u2026\u2026\u201d\u541f\u971c\u9053\uff1a\u201c\u8fd9\u672c\u9886\uff0c\u4e0d\u7528\u4f60\u4f20\u6388\uff0c\u6211\u4eec\u5df2\u7ecf\u5531\u4e86\u5341\u51e0\u5e74\u4e86\u3002\u201d \u3000\u3000\u5979\u867d\u662f\u59b9\u59b9\uff0c\u4f46\u5374\u4e00\u5411\u7528\u5b57\u5c16\u9510\uff0c\u4e00\u9488\u89c1\u8840\uff0c\u540c\u6837\u4e00\u53e5\u8bdd\uff0c\u5151\u7684\u5c31\u6bd4\u541f\u96ea\u523b\u8584\u4e86\u3002 \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5c31\u662f\u542c\u5230\u4e24\u4f4d\u4e00\u526f\u597d\u6b4c\u5589\uff0c\u6211\u624d\u60f3\u6559\u4e24\u4f4d\u4e00\u6bb5\u66f2\u8bb0\u8bcd\u3002\u201d \u3000\u3000\u201c\u5979\u4eec\u884c\u4e48\u2026\u2026\u201d\u6c5f\u67ab\u795e\u60c5\u8083\u7136\u9053\uff1a\u201c\u9053\u4e0d\u540c\u4e0d\u76f8\u4e3a\u8c0b\uff0c\u6211\u5c31\u4e0d\u6210\uff0c\u2026\u2026\u201d \u3000\u3000\u201c\u662f\u7684\uff01\u4f60\u662f\u4e0d\u884c\uff0c\u4f46\u548c\u5c1a\u89c2\u67e5\u8fc7\u4e86\uff0c\u5979\u4eec\u4e24\u4e2a\u53ef\u4ee5\uff0c\u4e0d\u4f46\u53ef\u4ee5\uff0c\u800c\u4e14\uff0c\u53ef\u4ee5\u901f\u6210\uff0c\u4e09\u4e2a\u6708\uff0c\u5c31\u53ef\u4ee5\u6d3e\u4e0a\u7528\u573a\u4e86\u3002\u201d \u3000\u3000\u96ea\u3001\u971c\u4e8c\u5973\u867d\u7136\u4e0d\u77e5\u9053\u4e24\u4eba\u8c08\u7684\u4ec0\u4e48\uff1f \u3000\u3000\u4f46\u89c1\u6c5f\u67ab\u4e25\u8083\u7684\u795e\u60c5\uff0c\u5df2\u77e5\u662f\u91cd\u5927\u4e8b\u60c5\uff0c\u7acb\u523b\u6536\u655b\u8d77\u5a07\u6001\u5a9a\u7b11\uff0c\u6062\u590d\u4e86\u7aef\u58ee\u7684\u5c11\u5973\u5f62\u8c61\u3002 \u3000\u3000\u201c\u8fd9\u5c31\u53eb\u5728\u4e0b\u60f3\u4e0d\u901a\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u5929\u4e0b\u6ca1\u6709\u5341\u5168\u5341\u7f8e\u7684\u4eba\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u63a5\u9053\uff1a\u201c\u4f60\u5df2\u5341\u5f97\u5176\u4e5d\uff0c\u603b\u5f97\u7559\u70b9\u7f3a\u9677\u7f8e\u5427\uff01\u201d \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u8fd9\u53ef\u662f\u4e00\u4ef6\u2026\u2026\u201d \u3000\u3000\u201c\u4e00\u4ef6\u52a8\u4eba\u7684\u8bcd\u66f2\uff0c\u80fd\u4f7f\u4eba\u5fc3\u65f7\u795e\u6021\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u6253\u65ad\u4e86\u6c5f\u67ab\u7684\u8bdd\uff0c\u63a5\u9053\uff1a\u201c\u7531\u4e24\u4f4d\u5929\u8d4b\u4e0a\u4f73\u6b4c\u5589\u7684\u5c0f\u59d1\u5a18\u5531\u51fa\u6765\uff0c\u4e00\u5b9a\u66f4\u4e3a\u52a8\u4eba\uff01\u201d \u3000\u3000\u4e8c\u5973\u7edd\u9876\u806a\u660e\uff0c\u5fc3\u4e2d\u65e9\u5df2\u6709\u6570\uff0c\u5927\u548c\u5c1a\u4f20\u6388\u7684\u5b9a\u662f\u60ca\u5929\u52a8\u5730\u7684\u7edd\u6280\uff0c\u7acb\u523b\u8eac\u8eab\u4e00\u793c\uff0c\u9053\uff1a\u201c\u6211\u4eec\u77e5\u9519\u4e86\uff0c\u5927\u5e08\u6210\u5168\u4e4b\u6069\uff0c\u7edd\u4e0d\u5fd8\u6000\u3002\u201d \u3000\u3000\u201c\u8fd9\u5c31\u4e0d\u6562\u4e86\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u9053\uff1a\u201c\u548c\u5c1a\u75f4\u957f\u51e0\u5c81\uff0c\u53eb\u6211\u58f0\u548c\u5c1a\u4f2f\u4f2f\uff0c\u548c\u5c1a\u6597\u80c6\u627f\u8ba4\u4e86\uff0c\u4f46\u5982\u53eb\u6211\u5927\u5e08\uff0c\u90a3\u5c31\u6709\u4e9b\u9ad8\u62ac\u6211\u4e86\uff0c\u8bba\u6280\u827a\uff0c\u54b1\u4eec\u5e73\u8f88\u8bba\u4ea4\uff0c\u4f55\u51b5\uff0c\u548c\u5c1a\u6559\u4f60\u4eec\u4e00\u6bb5\u8bcd\u66f2\uff0c\u6362\u5230\u4e86\u4e09\u884c\u6f5c\u672f\uff0c\u600e\u4e48\u7b97\u90fd\u4e0d\u5403\u4e8f\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u6211\u5df2\u5927\u4f53\u6062\u590d\u4e86\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u4f60\u4eec\u53bb\u4e92\u4f20\u6280\u827a\uff0c\u6211\u8be5\u56de\u623f\u53bb\u597d\u597d\u4f11\u606f\u4e0b\u4e86\u3002\u201d \u3000\u3000\u201c\u8fd8\u6709\u4e24\u4e2a\u6740\u624b\u6ca1\u6709\u73b0\u8eab\uff0c\u2026\u2026\u201d\u541f\u96ea\u9053\uff1a\u201c\u5c0f\u5fc3\u4e9b\u554a\uff01\u6211\u548c\u59b9\u8ddf\u7740\u5927\u5e08\u5b66\u5531\u6b4c\uff0c\u6682\u65f6\u4e0d\u80fd\u4fdd\u62a4\u4f60\u4e86\uff01\u201d \u3000\u3000\u201c\u80e1\u6c0f\u7236\u5b50\uff0c\u6bb5\u6c0f\u5e08\u5f92\uff0c\u90fd\u5df2\u8131\u80ce\u6362\u9aa8\uff0c\u8fd9\u70b9\u4e8b\uff0c\u4ed6\u4eec\u62c5\u5f97\u8d77\u6765\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u7b11\u9053\uff1a \u3000\u3000\u201c\u4f55\u51b5\uff0c\u8fd8\u6709\u5f20\u56db\u59d1\uff0c\u2026\u2026\u201d \u3000\u3000\u201c\u5f88\u4e0d\u597d\u7684\u6d88\u606f\uff0c\u4e09\u5bab\u4e3b\u5230\u4e86\u957f\u5b89\uff0c\u5929\u738b\u95e8\u4eba\uff0c\u4e5f\u4f4f\u8fdb\u5165\u6d1b\u9633\u5c45\u4e86\u2026\u2026\u201d\u5f20\u56db\u59d1\u4e00\u8eab\u9ed1\u8863\uff0c\u75be\u5954\u800c\u5165\u3002 \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u5bf9\u5201\u9e4f\u4e00\u6325\u624b\uff0c\u5201\u9e4f\u6025\u6025\u9000\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u201c\u4ed6\u4eec\u662f\u5426\u89c1\u8fc7\u7533\u96ea\u541b\uff1f\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u8fd9\u4e48\u4e00\u4e2a\u5de7\u6cd5\uff0c\u4f1a\u4e0d\u4f1a\u4e09\u65b9\u9762\u65e9\u5df2\u6709\u4e86\u9884\u7ea6\uff1f\u201d \u3000\u3000\u201c\u5e94\u8be5\u4e0d\u4f1a\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u7533\u96ea\u541b\u5e26\u6765\u4e86\u5f3a\u5927\u7684\u5b9e\u529b\uff0c\u5927\u6982\u662f\u6709\u610f\u8981\u70ab\u9732\u4e00\u4e0b\uff0c\u4f46\u5979\u6ca1\u6709\u60f3\u5230\u4f1a\u78b0\u4e0a\u4f60\u8fd9\u4e48\u4e00\u4e2a\u624e\u624b\u4eba\u7269\uff0c\u95f9\u5f97\u7070\u5934\u571f\u8138\uff0c\u72fc\u72c8\u800c\u9003\uff0c\u5979\u5fc3\u4e2d\u6700\u91cd\u8981\u7684\u4e00\u4ef6\u4e8b\uff0c\u5927\u6982\u5c31\u662f\u5c3d\u5feb\u628a\u6d88\u606f\u4f20\u56de\u5929\u9a6c\u603b\u5802\u53bb\u3002\u201d \u3000\u3000\u201c\u5927\u59d0\uff0c\u53ef\u6709\u4e86\u5e94\u5bf9\u4e4b\u7b56\uff1f\u2026\u2026\u201d \u3000\u3000\u201c\u6682\u4e0d\u62db\u60f9\u5929\u738b\u95e8\uff0c\u53ea\u8981\u4ed6\u4e0d\u627e\u4e0a\u95e8\u6765\u751f\u4e8b\uff0c\u6211\u4eec\u5c31\u5c3d\u91cf\u5bb9\u5fcd\uff0c\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u5c31\u7b97\u627e\u4e0a\u95e8\uff0c\u4e5f\u4e0d\u8981\u5f62\u6210\u5bf9\u6297\uff0c\u90a3\u4f1a\u7acb\u523b\u9020\u6210\u4ed6\u4eec\u4e09\u65b9\u8054\u624b\u7684\u53ef\u80fd\uff0c\u4f60\u5c3d\u91cf\u8eb2\u8d77\u6765\uff0c\u7531\u5927\u548c\u5c1a\u6765\u5e94\u4ed8\uff0c\u6211\u77e5\u9053\u548c\u5c1a\u88c5\u4f5c\u3001\u8bba\u8bb9\u7684\u672c\u9886\uff0c\u5531\u4f5c\u4ff1\u4f73\u3002\u201d \u3000\u3000\u201c\u5938\u5956\u3001\u5938\u5956\uff0c\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u63a5\u9053\uff1a\u201c\u4f46\u548c\u5c1a\u4f69\u670d\u9ad8\u89c1\uff0c\u5c11\u4e86\u5929\u738b\u95e8\u4ece\u4e2d\u7275\u7ebf\uff0c\u5185\u5bab\u4e00\u7cfb\u4e2d\u4eba\uff0c\u8fd8\u4e0d\u4fbf\u548c\u5929\u9a6c\u5802\u8a00\u5f52\u4e8e\u597d\uff0c\u53cc\u65b9\u4e5f\u65e0\u6cd5\u4ea4\u6362\u91cd\u8981\u7684\u60c5\u62a5\uff0c\u8fd9\u5c31\u7559\u7ed9\u4e86\u6211\u4eec\u5f88\u5927\u7684\u5468\u65cb\u7684\u4f59\u5730\uff0c\u6240\u4ee5\uff0c\u5bf9\u5185\u5bab\u4e00\u7cfb\u4e2d\u4eba\uff0c\u8981\u7a77\u8ffd\u731b\u6253\u2026\u2026\u201d \u3000\u3000\u201c\u5927\u59d0\u7684\u610f\u601d\u662f\uff0c\u8981\u4e00\u4e3e\u640f\u6740\u4e09\u5bab\u4e3b\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u53ea\u4e0d\u77e5\u6b64\u65f6\u662f\u5426\u65f6\u673a\u6070\u5f53\uff1f\u201d \u3000\u3000\u201c\u4f60\u5df2\u7ecf\u628a\u4e8b\u60c5\u63a8\u5230\u4e86\u60ac\u5d16\u8fb9\u7f18\uff0c\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u5df2\u7ecf\u65e0\u6cd5\u518d\u7ec4\u548c\u4e0b\u53bb\uff0c\u7533\u96ea\u541b\u53ea\u8981\u8bf4\u51fa\u4f60\u5e08\u627f\u8eab\u4efd\uff0c\u5929\u9a6c\u5802\u7acb\u523b\u4f1a\u52a8\u5458\u4e86\u5168\u90e8\u529b\u91cf\u5bf9\u4ed8\u4f60\uff0c\u800c\u4e14\uff0c\u4e5f\u4f1a\u5c3d\u53ef\u80fd\u548c\u5185\u5bab\u4e00\u7cfb\u4e2d\u4eba\u6c9f\u901a\uff0c\u4ee5\u4fbf\u96c6\u4e2d\u5168\u529b\u3002\u201d \u3000\u3000\u201c\u8bf4\u6765\u60ed\u6127\uff0c\u5c0f\u5f1f\u8fd8\u662f\u706b\u5019\u4e0d\u8db3\uff0c\u5fcd\u4e0d\u4e0b\u90a3\u4e00\u53e3\u6c14\uff0c\u88ab\u7533\u96ea\u541b\u51e0\u53e5\u8bdd\u6fc0\u51fa\u5fc3\u4e2d\u7684\u9690\u5bc6\u2026\u2026\u201d \u3000\u3000\u201c\u4f60\u4e5f\u4e0d\u7528\u81ea\u8d23\uff0c\u8001\u5b9e\u8bf4\uff0c\u9a97\u4e0d\u8fc7\u5979\u7684\uff0c\u5979\u5bf9\u91d1\u4e39\u4e66\u751f\u7684\u6280\u827a\u5341\u5206\u719f\u6089\uff0c\u4ed6\u4eec\u672c\u662f\u4e00\u5bf9\u60c5\u4eba\u2026\u2026\u201d \u3000\u3000\u5f20\u56db\u59d1\u4f3c\u662f\u77e5\u9053\u8bf4\u6e9c\u4e86\u5634\uff0c\u7a81\u7136\u4f4f\u53e3\u4e0d\u8a00\u3002 \u3000\u3000\u201c\u8bf4\u4e0b\u53bb\u554a\uff01\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u4e09\u4f4d\u5e08\u957f\u7684\u60c5\u4ec7\u7ea0\u7f20\uff0c\u6211\u4e5f\u542c\u8fc7\u4e00\u70b9\uff0c\u53ea\u4e0d\u8fc7\uff0c\u4ed6\u4eec\u8bf4\u7684\u542b\u84c4\uff0c\u6211\u53c8\u4e0d\u80fd\u8ffd\u95ee\uff0c\u77e5\u9053\u7684\u4e0d\u591f\u8be6\u5c3d\uff0c\u76ee\u4e0b\u6c5f\u6e56\u7684\u60c5\u52bf\uff0c\u4f3c\u662f\u5df2\u548c\u4ed6\u4eec\u4e2a\u4eba\u7684\u5f80\u4e8b\u60c5\u4ec7\uff0c\u7ea0\u7ed3\u4e00\u5904\uff0c\u6211\u5982\u4e0d\u77e5\u5185\u60c5\uff0c\u5c82\u4e0d\u662f\u6709\u5bb3\u5927\u5c40\u5417\uff1f\u201d \u3000\u3000\u201c\u8bf4\u7684\u6709\u7406\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u4e2a\u4eba\u4e4b\u95f4\u7684\u6069\u60c5\u662f\u975e\uff0c\u6709\u5f88\u591a\u4e0d\u8db3\u4e3a\u5916\u4eba\u9053\uff0c\u5929\u72d0\u592b\u4eba\u544a\u8bc9\u6211\u4e00\u4e9b\uff0c\u91d1\u4e39\u4e66\u751f\u4e5f\u548c\u6211\u4f5c\u4e00\u6b21\u957f\u8c08\uff0c\u4f46\u6211\u76f8\u4fe1\uff0c\u4ed6\u4eec\u4e00\u5b9a\u4f1a\u6709\u4e9b\u9690\u85cf\u3002\u201d \u3000\u3000\u201c\u5927\u59d0\uff0c\u6211\u60f3\u77e5\u9053\u7684\uff0c\u4e5f\u4e0d\u662f\u5168\u90e8\u7ec6\u8282\uff0c\u53ea\u662f\u4e00\u4e2a\u5927\u6982\u7684\u60c5\u5f62\uff0c\u8ba9\u6211\u4e86\u89e3\u5230\u4ed6\u4eec\u4e4b\u95f4\u7684\u7eb5\u6a2a\u5173\u7cfb\uff0c\u90a3\u5c31\u591f\u4e86\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u6c89\u541f\u4e86\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u5927\u548c\u5c1a\uff0c\u6211\u6765\u8bf4\uff0c\u5982\u82e5\u6709\u8bf4\u4e0d\u6e05\u695a\u7684\u5730\u65b9\uff0c\u7531\u4f60\u6765\u8865\u5145\u3002\u2026\u2026\u201d \u3000\u3000\u201c\u8fd9\u4e2a\uff0c\u548c\u5c1a\u65e0\u6cd5\u4ece\u547d\uff0c\u6211\u6ca1\u6709\u89c1\u8fc7\u91d1\u4e39\u4e66\u751f\uff0c\u548c\u5929\u72d0\u592b\u4eba\uff0c\u4e5f\u53ea\u6709\u5306\u5306\u4e00\u9762\u4e4b\u7f18\u3002\u2026\u2026\u201d \u3000\u3000\u201c\u4f46\u4f60\u8ba4\u8bc6\u65e0\u76f8\u5927\u5e08\uff0c\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u8fd9\u4f4d\u9ad8\u50e7\uff0c\u4ece\u6765\u662f\u91d1\u53e3\u96be\u5f00\uff0c\u6211\u89c1\u8fc7\u4ed6\u4e09\u6b21\u9762\uff0c\u4ed6\u5bf9\u6211\u70b9\u5934\u5fae\u7b11\u4e09\u6b21\uff0c\u8fde\u4e00\u53e5\u4e5f\u4e0d\u8ddf\u6211\u8bf4\u2026\u2026\u201d \u3000\u3000\u201c\u4f60\u53ef\u4ee5\u95ee\u4ed6\u5440\uff01\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u7b11\u9053\uff1a\u201c\u627e\u673a\u4f1a\u8981\u4ed6\u5f00\u53e3\uff0c\u5728\u4f60\u5f20\u59d1\u5a18\u6765\u8bf4\uff0c\u5e94\u8be5\u4e0d\u662f\u96be\u4e8b\uff1f\u201d \u3000\u3000\u201c\u6211\u8d39\u4e86\u65e0\u6570\u53e3\u820c\uff0c\u53ea\u542c\u5230\u4e00\u53e5\u4f5b\u53f7\uff0c\u2026\u2026\u201d\u5f20\u56db\u59d1\u8f6c\u5411\u6c5f\u67ab\uff0c\u95ee\u9053\uff1a\u201c\u4ed6\u4f20\u4f60\u6b66\u529f\uff0c\u5e94\u8be5\u8bf4\u8fc7\u4e0d\u5c11\u7684\u8bdd\uff0c\u4f60\u4eec\u4e4b\u95f4\uff0c\u53ef\u80fd\u662f\u8bf4\u8bdd\u6700\u591a\u7684\u4eba\u4e86\uff1f\u201d \u3000\u3000\u201c\u5e08\u7236\uff0c\u4f20\u6211\u6b66\u529f\u65f6\uff0c\u975e\u5e38\u7ec6\u5fc3\uff0c\u89e3\u8bf4\u4e5f\u5f88\u8be6\u5c3d\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u53f9\u53e3\u6c14\u53c8\u9053\uff1a\u201c\u4f46\u4ed6\u4ece\u6765\u6ca1\u6709\u548c\u6211\u8c08\u8bba\u8fc7\u6c5f\u6e56\u4e2d\u4e8b\uff0c\u5f53\u7136\uff0c\u4e5f\u6ca1\u6709\u8bf4\u8fc7\u4ed6\u79c1\u4eba\u7684\u4e8b\u60c5\u3002\u201d \u3000\u3000\u201c\u4ed6\u5982\u80af\u591a\u8bf4\u51e0\u53e5\u8bdd\uff0c\u4e5f\u8bb8\u4e8b\u60c5\u8fd8\u4e0d\u4f1a\u95f9\u6210\u8fd9\u6837\u4e00\u4e2a\u5c40\u9762\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e00\u70b9\uff0c\u548c\u5c1a\u4e0d\u6562\u82df\u540c\uff0c\u9634\u8c0b\u65e9\u5df2\u5728\u6697\u4e2d\u8fdb\u884c\uff0c\u4e5d\u534e\u76df\u7ea6\u8fc7\u7a0b\u4e2d\uff0c\u4ed6\u4eec\u4e09\u4f4d\u90fd\u5df2\u7ecf\u53d7\u5230\u4e86\u5f88\u91cd\u7684\u4f24\u5bb3\uff0c\u4f46\u4ed6\u5f3a\u8fd0\u5185\u529f\uff0c\u903c\u4f4f\u4f24\u52bf\uff0c\u4e0d\u8ba9\u53d1\u4f5c\uff0c\u9707\u4f4f\u4e86\u590f\u5929\u540c\u7b49\uff0c\u4f7f\u4ed6\u4eec\u4e0d\u6562\u5984\u52a8\uff0c\u4f46\u4e5f\u9020\u6210\u4e86\u4ed6\u4eec\u4e09\u4f4d\u65e0\u6cd5\u8865\u6551\u7684\u5185\u4f24\u3001\u529f\u529b\u635f\u5931\u5927\u534a\u3002\u2026\u2026\u201d \u3000\u3000\u53ea\u542c\u4e00\u9635\u6b65\u5c65\u4e4b\u58f0\uff0c\u80e1\u5143\u6025\u5954\u800c\u5165\u3002 \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u6709\u4e8b\u60c5\uff1f\u201d \u3000\u3000\u201c\u662f\uff01\u6885\u82b1\u59d1\u5a18\u6709\u6025\u4e8b\uff0c\u8bf7\u526f\u603b\u7ba1\u5230\u6885\u82b1\u5385\u4e2d\u4e00\u53d9\u3002\u201d \u3000\u3000\u201c\u6885\u82b1\u73b0\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u201c\u5927\u5385\u5916\u9762\u2026\u2026\u201d\u80e1\u5143\u9053\uff1a\u201c\u6b63\u5728\u548c\u9093\u603b\u7ba1\u8bf4\u8bdd\u3002\u201d \u3000\u3000\u201c\u5927\u6982\u662f\u4e09\u5bab\u4e3b\u4e5f\u5230\u4e86\u6d1b\u9633\u5c45\u3001\u8981\u89c1\u4f60\u4e00\u9762\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u4f60\u6709\u4ec0\u4e48\u6253\u7b97\uff1f\u201d \u3000\u3000\u201c\u5148\u53bb\u89c1\u89c1\u5979\u4e86\u2026\u2026\u201d\u6c5f\u67ab\u53cc\u76ee\u4e2d\u6740\u673a\u4e00\u95ea\uff0c\u4f4e\u58f0\u63a5\u9053\uff1a\u201c\u5982\u679c\u60c5\u52bf\u4e0d\u5229\uff0c\u6211\u5c31\u51fa\u624b\u6740\u4e86\u5979\u3002\u201d \u3000\u3000\u201c\u4e09\u5bab\u4e3b\u4e0d\u662f\u7b49\u95f2\u4e4b\u8f88\uff0c\u4e00\u51fb\u4e0d\u4e2d\uff0c\u2026\u2026\u201d\u5f20\u56db\u59a8\u9053\uff1a\u201c\u90a3\u5c31\u9020\u6210\u4e86\u8a93\u4e0d\u4e24\u7acb\u7684\u5c40\u9762\u4e86\u3002\u201d \u3000\u3000\u201c\u5927\u59d0\u4f4e\u4f30\u5c0f\u5f1f\u4e86\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u5982\u679c\u6211\u4e58\u4eba\u4e0d\u5907\uff0c\u7a81\u8d77\u53d1\u96be\uff0c\u5929\u4e0b\u96be\u591f\u907f\u5f00\u6211\u8fd9\u4e00\u51fb\u7684\u4eba\uff0c\u5927\u6982\u4e0d\u4f1a\u592a\u591a\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e00\u70b9\u6211\u76f8\u4fe1\uff0c\u4f46\u5982\u654c\u4eba\u6709\u5907\u5462\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u5fae\u5fae\u4e00\u6014\u3002 \u3000\u3000\u201c\u5144\u5f1f\u2026\u2026\u201d\u5f20\u56db\u59d1\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u4e2a\u97e9\u9738\u7684\u6076\u540d\uff0c\u65e9\u5df2\u4f20\u626c\u5728\u5916\uff0c\u90fd\u77e5\u9053\u4f60\u8eab\u8d1f\u4e00\u51fb\u53d6\u547d\u7684\u7edd\u6280\uff0c\u89c1\u4f60\u4e4b\u524d\uff0c\u5c82\u4f1a\u65e0\u5907\uff0c\u4e3a\u4ec0\u4e48\u4e00\u5b9a\u8981\u5728\u6b66\u529f\u4e0a\u62fc\u51fa\u80dc\u8d1f\u5462\uff1f\u201d \u3000\u3000\u201c\u5927\u59d0\u7684\u610f\u601d\u662f\u2026\u2026\u201d\u6c5f\u67ab\u82e5\u6709\u6240\u609f\u7684\u95ee\u3002 \u3000\u3000\u201c\u6362\u4e00\u79cd\u65b9\u5f0f\u5bf9\u4ed8\u5979\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u53d6\u4e0b\u4f60\u7684\u9762\u5177\u3002\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u61c2\u4e86\u2026\u2026\u201d\u541f\u971c\u63a5\u9053\uff1a\u201c\u6c5f\u5927\u54e5\uff0c\u5f20\u59e8\u8981\u4f60\u65bd\u5c55\u7f8e\u7537\u8ba1\uff01\u201d \u3000\u3000\u6c5f\u67ab\u53ea\u542c\u5f97\u8138\u4e0a\u53d1\u70ed\uff0c\u5e78\u597d\u7751\u4e0a\u6234\u4e86\u9762\u5177\u3002 \u3000\u3000\u201c\u6211\u8d5e\u6210\u5f20\u59e8\u7684\u4e3b\u610f\u2026\u2026\u201d\u541f\u96ea\u8bf4\uff0c\u201c\u52a8\u624b\u62fc\u547d\uff0c\u662f\u6700\u540e\u7684\u529e\u6cd5\uff0c\u4e0d\u6218\u800c\u5c48\u4eba\u4e4b\u5175\uff0c\u624d\u662f\u4e0a\u4e0a\u4e4b\u7b56\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u63a5\u9053\uff1a\u201c\u2018\u60d1\u5fc3\u672f\u2019\u5e94\u8be5\u53ef\u4ee5\u514b\u5236\u4f4f\u4e09\u5bab\u4e3b\u7684\u5a9a\u529f\uff0c\u5979\u662f\u5a9a\u672f\u9020\u8be3\u6700\u6df1\u7684\u9ad8\u624b\u4e4b\u4e00\uff0c\u4f60\u5982\u80fd\u80dc\u8fc7\u5979\uff0c\u90a3\u5c31\u8bc1\u660e\u4e86\u2018\u60d1\u5fc3\u672f\u2019\u786e\u4e3a\u5929\u4e0b\u7b2c\u4e00\u5947\u529f\u3002\u201d \u3000\u3000\u201c\u5982\u662f\u5c0f\u5f1f\u53cd\u4e3a\u5bf9\u65b9\u6240\u5236\u5462\uff1f\u201d \u3000\u3000\u201c\u90a3\u4e5f\u5c31\u8bc1\u660e\u4e86\u2018\u60d1\u5fc3\u672f\u2019\u5e76\u4e0d\u53ef\u6043\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u6211\u4eec\u5c3d\u5feb\u627e\u51fa\u4e00\u79cd\u53ef\u4ee5\u62d2\u6297\u5927\u654c\u7684\u6b66\u529f\u3002\u201d \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u5927\u59d0\uff0c\u4e16\u4e0a\u771f\u6709\u90a3\u79cd\u6b66\u529f\u4e48\uff1f\u201d \u3000\u3000\u201c\u6709\uff01\u4e0d\u8fc7\uff0c\u7ec3\u8d77\u6765\u727a\u7272\u592a\u5927\uff0c\u4f46\u60c5\u52bf\u8feb\u4eba\uff0c\u8bf4\u4e0d\u5f97\u53ea\u597d\u8ba4\u547d\u4e86\uff0c\u2026\u2026\u201d\u5f20\u56db\u59d1\u795e\u60c5\u8083\u7136\u5730\u8bf4\uff0c\u201c\u53bb\u5427\uff01\u8bd5\u8bd5\u60d1\u5fc3\u672f\uff0c\u5982\u82e5\u4e0d\u884c\uff0c\u7acb\u523b\u51fa\u624b\uff0c\u4e00\u4e3e\u51fb\u6740\u5979\u3002\u201d \u3000\u3000\u201c\u6211\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u6b32\u8a00\u53c8\u6b62\u5730\u4e3e\u6b65\u5411\u5916\u884c\u53bb\u3002 \u3000\u3000\u5f20\u56db\u59d1\u4f4e\u58f0\u5bf9\u4e8c\u5973\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u8d76\u53bb\u652f\u63f4\uff0c\u5982\u82e5\u4ed6\u7684\u60d1\u5fc3\u672f\u5236\u4e0d\u4f4f\u5bf9\u65b9\uff0c\u5c31\u4f1a\u88ab\u5bf9\u65b9\u6240\u5236\uff0c\u54b1\u4eec\u5c31\u7acb\u523b\u53d1\u52a8\u88ad\u51fb\uff0c\u8bb0\u4f4f\u51fa\u624b\u8981\u72e0\uff0c\u4e0d\u53ef\u7559\u60c5\uff0c\u4e00\u51fb\u4e0d\u4e2d\uff0c\u5c31\u4f1a\u5bb3\u4e86\u4f60\u4eec\u7684\u6c5f\u5927\u54e5\u3002\u201d \u3000\u3000\u541f\u96ea\u3001\u541f\u971c\u5e94\u4e86\u4e00\u58f0\uff0c\u98de\u8eab\u800c\u53bb\u3002 \u3000\u3000\u201c\u5927\u548c\u5c1a\uff0c\u63a5\u8fd1\u6885\u82b1\u5385\uff0c\u5355\u51ed\u9ad8\u660e\u7684\u8f7b\u529f\u4e0d\u884c\uff0c\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u4f60\u4e0d\u7528\u53bb\u5e2e\u5fd9\u4e86\uff0c\u56de\u53bb\u4f5c\u756a\u5e03\u7f72\uff0c\u4f5c\u6700\u574f\u7684\u6253\u7b97\uff0c\u628a\u6b27\u9633\u662d\u548c\u7f57\u5170\uff0c\u4e5f\u8c03\u8fdb\u6765\uff0c\u987a\u4fbf\u901a\u77e5\u4e00\u58f0\u8001\u53eb\u5316\u5b50\uff0c\u8fd9\u4e00\u4ed7\u6253\u4e0d\u8d62\uff0c\u5c31\u64a4\u51fa\u957f\u5b89\u57ce\u3002\u201d \u3000\u3000\u201c\u963f\u5f25\u9640\u4f5b\uff01\u4f46\u613f\u6c5f\u5c11\u4fa0\u80fd\u80dc\u5f97\u4e09\u5bab\u4e3b\uff0c\u4ed6\u4eec\u5982\u80fd\u5531\u51fa\u4e00\u540d\u91d1\u699c\u4e50\uff0c\u8fd9\u4e00\u6218\u5c31\u597d\u6253\u591a\u4e86\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u548c\u5c1a\u4ec0\u4e48\u90fd\u61c2\u5417\uff1f\u201d \u3000\u3000\u201c\u548c\u5c1a\u6ca1\u6709\u5403\u8fc7\u732a\u8089\uff0c\u53ef\u662f\u89c1\u8fc7\u732a\u8d70\u8def\u554a\uff01\u201d \u3000\u3000\u5f20\u56db\u59d1\u5fae\u5fae\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u6c5f\u67ab\u5360\u6709\u4e03\u6210\u80dc\u7b97\uff0c\u6211\u4eec\u96c6\u4e2d\u4eba\u624b\uff0c\u5e0c\u671b\u662f\u5907\u800c\u4e0d\u7528\u3002\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u8bf4\u5462\uff01\u5f20\u59d1\u5a18\u4ee4\u5982\u5c71\uff0c\u539f\u6765\u662f\u5fc3\u4e2d\u65e9\u6709\u6210\u7b97\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u4f4e\u58f0\u9053\uff1a\u201c\u4ed6\u662f\u7edf\u519b\u4e3b\u5e08\uff0c\u4e0d\u80fd\u8ba9\u4ed6\u53d7\u5230\u632b\u6298\uff0c\u90a3\u4f1a\u4f24\u4e86\u4ed6\u7684\u4fe1\u5fc3\u3002\u201d \u3000\u3000\u201c\u6211\u62c5\u5fc3\u7684\u662f\u4ed6\u81ea\u4fe1\u592a\u5f3a\uff0c\u9887\u6709\u76ee\u7a7a\u56db\u6d77\u7684\u6c14\u6982\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u6211\u5012\u5e0c\u671b\u4ed6\u9047\u5230\u4e00\u70b9\u632b\u6298\uff0c\u8ba9\u4ed6\u63d0\u9ad8\u4e00\u4e9b\u8b66\u89c9\uff0c\u5bf9\u5927\u5c40\uff0c\u4e5f\u8bb8\u66f4\u6709\u5e2e\u52a9\u3002\u201d \u3000\u3000\u201c\u5509\uff01\u5982\u8bba\u5355\u6253\u72ec\u6597\uff0c\u5929\u4e0b\u9ad8\u624b\uff0c\u80fd\u591f\u80dc\u8fc7\u4ed6\u7684\uff0c\u786e\u7136\u5f88\u96be\u627e\u51fa\u4e00\u4e2a\u6765\u4e86\uff0c\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u9053\uff1a\u201c\u4ed6\u8eab\u517c\u4e09\u5bb6\u4e4b\u957f\uff0c\u5c31\u7b97\u65e0\u76f8\u5927\u5e08\u590d\u751f\uff0c\u91d1\u4e39\u4e66\u751f\u8fd8\u9b42\uff0c\u5929\u72d0\u592b\u4eba\u518d\u73b0\u4eba\u4e16\uff0c\u4e5f\u65e0\u6cd5\u4e00\u5bf9\u4e00\u7684\u80dc\u5f97\u8fc7\u4ed6\u3002\u201d \u3000\u3000\u201c\u6700\u62c5\u5fc3\u7684\u5c31\u662f\u8fd9\u4e9b\u4e86\uff0c\u4ed6\u4e00\u628a\u7fe0\u7389\u5200\uff0c\u72ec\u6597\u516b\u4f4d\u5251\u571f\uff0c\u7533\u96ea\u541b\u73b0\u573a\u76ee\u7779\u3002\u2026\u2026\u201d \u3000\u3000\u201c\u662f\u7684\uff01\u5979\u672c\u6765\u4e0b\u4ee4\u51fa\u624b\u4e4b\u540e\uff0c\u4eba\u5df2\u79bb\u53bb\uff0c\u4e0d\u77e5\u4f55\u6545\uff0c\u7a81\u7136\u53c8\u56de\u5385\u4e2d\uff0c\u773c\u770b\u7740\u8fd9\u573a\u640f\u6740\u7ed3\u675f\u3002\u2026\u2026\u201d \u3000\u3000\u5f20\u56db\u59d1\u6253\u65ad\u4e86\u4e03\u5b9d\u548c\u5c1a\u7684\u8bdd\uff0c\u63a5\u9053\uff1a\u201c\u5c31\u5404\u65b9\u8d44\u6599\u7814\u5224\uff0c\u90a3\u4f4d\u5929\u9a6c\u603b\u5802\u7684\u5802\u4e3b\uff0c\u5927\u6982\u662f\u590f\u5929\u540c\u4e86\uff0c\u8fd9\u4e2a\u4eba\u667a\u8ba1\u767e\u51fa\uff0c\u5fc3\u72e0\u624b\u8fa3\uff0c\u5982\u82e5\u5f97\u5230\u4e86\u7533\u96ea\u541b\u7684\u62a5\u544a\uff0c\u5fc5\u4f1a\u5343\u65b9\u767e\u8ba1\u7684\u7b97\u8ba1\u6c5f\u67ab\u3002\u201d \u3000\u3000\u201c\u5bf9\uff01\u8fd9\u5012\u662f\u4e00\u4ef6\u5927\u9ebb\u70e6\u4e86\uff0c\u590f\u5929\u540c\u80fd\u4e00\u4e3e\u7b97\u8ba1\u4e86\u65e0\u76f8\u5927\u5e08\u3001\u91d1\u4e39\u4e66\u751f\u3001\u5929\u72d0\u592b\u4eba\uff0c\u4e09\u4f4d\u65f7\u4ee3\u5947\u4fa0\uff0c\u5168\u529b\u5bf9\u4ed8\u6c5f\u5c11\u4fa0\uff0c\u6211\u548c\u5c1a\u5c31\u4e0d\u77e5\u9053\u5982\u4f55\u53bb\u9632\u8303\uff01\u624d\u80fd\u4fdd\u62a4\u5230\u6c5f\u67ab\u7684\u5b89\u5168\u4e86\uff1f\u201d \u3000\u3000\u201c\u6240\u4ee5\uff0c\u54b1\u4eec\u8981\u7528\u5fc3\u8bbe\u8ba1\u4e00\u756a\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u529e\u6cd5\u4e5f\u4e0d\u662f\u6ca1\u6709\uff0c\u7b49\u6211\u81ea\u89c9\u60f3\u7684\u5b8c\u5168\u4e86\uff0c\u518d\u548c\u4f60\u4ed4\u7ec6\u7684\u5546\u91cf\u4e00\u4e0b\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u6211\u548c\u5c1a\u5168\u529b\u914d\u5408\uff0c\u6c34\u91cc\u706b\u91cc\u4e07\u6b7b\u4e0d\u8f9e\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u4e00\u626c\u624b\uff0c\u8f6c\u8eab\u5411\u4e00\u5835\u5899\u4e0a\u51b2\u53bb\u3002 \u3000\u3000\u660e\u660e\u662f\u4e00\u9053\u5899\uff0c\u4f46\u5f20\u56db\u59d1\u649e\u4e0a\u4e86\u5899\u58c1\uff0c\u4eba\u5374\u7a81\u7136\u6d88\u5931\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u201c\u597d\u9ad8\u660e\u7684\u6f5c\u571f\u672f\uff0c\u8c01\u8981\u5f97\u7f6a\u4e86\u8fd9\u4e48\u4e00\u4e2a\u4eba\u7269\uff0c\u5f53\u771f\u662f\u89c9\u4e5f\u7761\u4e0d\u5b89\u4e86\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u4eba\u5374\u98de\u8dc3\u800c\u8d77\uff0c\u9ad8\u5927\u7684\u8eab\u8eaf\uff0c\u6709\u5982\u8759\u874e\u5bb5\u98de\uff0c\u7adf\u7531\u4e00\u6247\u534a\u5f00\u7684\u5c0f\u7a97\u4e2d\u7a7f\u8d8a\u800c\u53bb\u3002 \u3000\u3000\u4ed6\u4e0d\u613f\u4ee5\u6539\u626e\u7684\u9762\u76ee\uff0c\u89c1\u5230\u9093\u98de\uff0c\u5c55\u4e86\u4e00\u6b21\u7edd\u4e16\u8f7b\u529f\uff0c\u80e1\u5143\u770b\u7684\u5446\u4f4f\u4e86\uff0c\u8fd9\u4e9b\u4eba\uff0c\u4e2a\u4e2a\u8eab\u8d1f\u7edd\u6280\u3002 \u3000\u3000\u518d\u8bf4\u6c5f\u67ab\u884c\u51fa\u5927\u5385\uff0c\u6885\u82b1\u5df2\u548c\u9093\u98de\u53cc\u53cc\u8fce\u4e86\u4e0a\u6765\u3002 \u3000\u3000\u201c\u662f\u4e0d\u662f\u4e09\u5bab\u4e3b\u7684\u5927\u9a7e\u5230\u4e86\u6d1b\u9633\u5c45\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u76ee\u7779\u6885\u82b1\uff0c\u9053\uff1a\u201c\u627e\u6211\u53bb\u5f53\u9762\u95ee\u7f6a\uff1f\u201d \u3000\u3000\u201c\u662f\u4e09\u5bab\u4e3b\u7684\u82b3\u9a7e\u5230\u4e86\u2026\u2026\u201d\u6885\u82b1\u9053\uff1a\u201c\u4f46\u662f\u4e0d\u662f\u5f53\u9762\u95ee\u7f6a\u3002\u6211\u5c31\u4e0d\u77e5\u9053\u4e86\uff0c\u4e0d\u8fc7\uff0c\u5979\u6307\u540d\u8981\u89c1\u4f60\uff0c\u6211\u53ea\u597d\u6765\u8bf7\u4e86\u3002\u201d \u3000\u3000\u9093\u98de\u4e00\u53ea\u78a7\u5149\u95ea\u95ea\u7684\u9b54\u773c\u4e2d\uff0c\u5947\u5149\u66b4\u5c04\uff0c\u770b\u7740\u6c5f\u67ab\uff0c\u4e5f\u4e0d\u77e5\u9053\u4ed6\u5fc3\u4e2d\u662f\u96be\u8fc7\uff0c\u8fd8\u662f\u6028\u5fff\uff0c\u60ca\u5947\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u9a97\u5f97\u6211\u597d\u82e6\u554a\uff01\u201d \u3000\u3000\u201c\u603b\u7ba1\u5fc3\u4e2d\u5982\u6709\u7591\u95ee\uff01\u7b49\u6211\u89c1\u8fc7\u4e09\u5bab\u4e3b\u56de\u6765\uff0c\u518d\u8be6\u4f5c\u89e3\u91ca\u5982\u4f55\uff1f\u201d \u3000\u3000\u201c\u8001\u5f1f\uff0c\u7528\u4e0d\u7740\u89e3\u91ca\u4e86\uff0c\u2026\u2026\u201d\u9093\u98de\u8bf4\uff0c \u3000\u3000\u201c\u6728\u5df2\u6210\u821f\uff0c\u8bf4\u4ec0\u4e48\u90fd\u662f\u7a7a\u8bdd\uff0c\u53ea\u5e0c\u671b\u4f60\u8001\u5f1f\u591a\u591a\u73cd\u91cd\uff0c\u4e0d\u4f46\uff0c\u6d1b\u9633\u5c45\u8fd9\u4e2a\u5c40\u9762\u8981\u4f60\u4fdd\u5168\uff0c\u6211\u4eec\u51e0\u5341\u6761\u6027\u547d\uff0c\u4e5f\u548c\u4f60\u606f\u606f\u76f8\u5173\u3002\u201d \u3000\u3000\u6c5f\u67ab\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u6d3b\u7684\u5f88\u597d\uff0c\u4e00\u70b9\u60f3\u6b7b\u7684\u611f\u89c9\u4e5f\u6ca1\u6709\uff0c\u53ea\u8981\u6211\u6d3b\u7740\uff0c\u5c31\u4f1a\u5168\u529b\u4fdd\u62a4\u603b\u7ba1\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u4f60\u81ea\u5df1\u4e5f\u8981\u5168\u529b\u6c42\u751f\u624d\u884c\uff0c\u81f3\u5c11\u5e94\u8be5\u6709\u53cd\u6297\u6b7b\u4ea1\u7684\u52c7\u6c14\u3002\u201d \u3000\u3000\u9093\u98de\u82e5\u6709\u6240\u609f\u7684\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u4f60\u4e0d\u5728\u65f6\uff0c\u7531\u90a3\u4e2a\u7a7f\u957f\u888d\u7684\u548c\u5c1a\u4f5c\u4e3b\u662f\u5417\uff1f\u201d \u3000\u3000\u201c\u539f\u6765\u603b\u7ba1\u65e9\u5c31\u77a7\u51fa\u6765\u4e86\uff0c\u2026\u2026\u201d \u3000\u3000\u9093\u98de\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u77a7\u51fa\u6765\u7684\u4e8b\u60c5\u591a\u4e86\uff0c\u2026\u2026\u201d\u770b\u4e86\u6885\u82b1\u4e00\u773c\uff0c\u63a5\u9053\uff1a\u201c\u4e0d\u8fc7\u59d1\u5a18\u53ef\u4ee5\u653e\u5fc3\uff0c\u6709\u4e9b\u4e8b\uff0c\u6211\u53ef\u662f\u4e00\u76f4\u6446\u5728\u5fc3\u91cc\uff0c\u5230\u4e86\u6211\u8fd9\u628a\u5e74\u7eaa\uff0c\u5b88\u53e3\u5982\u74f6\uff0c\u53ef\u4e0d\u662f\u4e00\u4ef6\u96be\u4e8b\u3002\u201d \u3000\u3000\u201c\u4f60\u597d\u50cf\u5728\u8bb9\u8bc8\u6211\u2026\u2026\u201d\u6885\u82b1\u9053\uff1a\u201c\u8bf4\u51fa\u6765\uff0c\u4f60\u7a76\u7adf\u770b\u5230\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u7530\u7389\u90a3\u5c0f\u5b50\u4e00\u53e3\u8c0e\u8a00\uff0c\u2026\u2026\u201d\u9093\u98de\u9053\uff1a\u201c\u9a97\u4e86\u5c0f\u59d1\u5a18\u7684\u611f\u60c5\uff0c\u4e5f\u5c31\u7f62\u4e86\uff0c\u8fde\u6211\u8001\u5934\u5b50\u4e5f\u4e0d\u653e\u8fc7\u3002\u2026\u2026\u201d \u3000\u3000\u201c\u6885\u82b1\uff0c\u7b97\u4e86\uff0c\u9b54\u773c\u7684\u7ef0\u53f7\uff0c\u5c82\u662f\u8ba9\u4eba\u81ea\u53eb\u7684\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u9093\u603b\u7ba1\uff0c\u6211\u548c\u6885\u82b1\u90fd\u4f1a\u627f\u4f60\u8fd9\u4efd\u60c5\uff0c\u56de\u53bb\u5427\uff01\u6709\u4e8b\u60c5\u53bb\u627e\u5927\u548c\u5c1a\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u201d\u9093\u98de\u5e94\u4e86\u4e00\u58f0\uff0c\u8f6c\u5411\u6885\u82b1\uff0c\u53cc\u624b\u4e00\u62b1\u624b\u9053\uff1a\u201c\u6885\u82b1\u59d1\u5a18\uff0c\u4f60\u90a3\u91cc\u6211\u4e0d\u4fbf\u53bb\uff0c\u97e9\u8001\u5f1f\u7684\u4e00\u5207\uff0c\u5c31\u5168\u4ef0\u4ed7\u59d1\u5a18\u62c5\u4ee3\u4e86\u3002\u201d \u3000\u3000\u6885\u82b1\u5fae\u5fae\u70b9\u4e86\u4e0b\u5934\uff0c\u5e7d\u5e7d\u5730\u8bf4\u4e86\u58f0\uff1a \u3000\u3000\u201c\u603b\u7ba1\u653e\u5fc3\u3002\u201d \u3000\u3000\u5176\u5b9e\uff0c\u9093\u98de\u90a3\u80fd\u653e\u5f97\u4e0b\u5fc3\uff0c\u5185\u5bab\u7684\u4e09\u5bab\u4e3b\u7a81\u7136\u4eb2\u4e34\u6d1b\u9633\u5c45\uff0c\u6b63\u610f\u5473\u7740\u5f88\u4e0d\u5bfb\u5e38\uff0c\u663e\u7136\u662f\u4e13\u4e3a\u5174\u5e08\u95ee\u7f6a\u800c\u6765\u3002 \u3000\u3000\u4f46\uff0c\u662f\u798f\u4e0d\u662f\u7978\uff0c\u662f\u7978\u8eb2\u4e0d\u8fc7\u3002 \u3000\u3000\u5c24\u5176\u4ed6\u8fd9\u6d1b\u9633\u5c45\u603b\u7ba1\uff0c\u65e9\u5df2\u662f\u5f62\u540c\u865a\u8bbe\uff0c\u8fde\u4e2a\u526f\u603b\u7ba1\u738b\u9f99\u90fd\u4e0d\u628a\u4ed6\u5f53\u56de\u4e8b\uff0c\u5728\u4e09\u5bab\u4e3b\u7684\u773c\u91cc\uff0c\u4ed6\u7b80\u76f4\u5c31\u6210\u4e86\u4e2a\u5fae\u4e0d\u8db3\u9053\u7684\u5c0f\u89d2\u8272\u3002 \u3000\u3000\u4e09\u5bab\u4e3b\u4e00\u5230\uff0c\u5c31\u6307\u540d\u8981\u89c1\u97e9\u9738\uff0c\u663e\u89c1\u6765\u610f\u4e0d\u5584\u3002 \u3000\u3000\u9093\u98de\u804c\u5351\u5fae\uff0c\u6b66\u529f\u66f4\u4e0d\u582a\u5185\u5bab\u7684\u9ad8\u624b\u4e00\u51fb\uff0c\u6697\u5bf9\u97e9\u5341\u5206\u5173\u5207\uff0c\u4e5f\u662f\u7231\u83ab\u80fd\u52a9\u3002 \u3000\u3000\u6240\u4ee5\uff0c\u4ed6\u53ea\u6709\u5bc4\u671b\u6885\u82b1\u80fd\u89c1\u673a\u884c\u4e8b\uff0c\u5168\u529b\u63f4\u52a9\u97e9\u526f\u603b\u7ba1\u4e86\u3002 \u3000\u3000\u76ee\u9001\u9093\u98de\u7684\u8eab\u5f71\u4e00\u95ea\u800c\u901d\uff0c\u6c5f\u67ab\u5373\u9053\uff1a\u201c\u6885\u82b1\uff0c\u6211\u4eec\u5feb\u53bb\u6885\u82b1\u5385\u5427\u3002\u201d \u3000\u3000\u4e0d\u6599\u6885\u82b1\u5374\u8bf4\uff0c\u201c\u4e0d\u6025\uff0c\u5148\u5230\u4f60\u5c4b\u91cc\u53bb\u3002\u201d \u3000\u3000\u6c5f\u67ab\u7709\u5934\u4e00\u76b1\u9053\uff1a\u201c\u53ef\u662f\uff0c\u4e09\u5bab\u4e3b\u547d\u4f60\u6765\u53ec\u6211\u2026\u2026\u201d \u3000\u3000\u201c\u7ba1\u5979\u7684\uff01\u201d\u6885\u82b1\u6beb\u4e0d\u5728\u4e4e\u9053\uff1a\u201c\u53cd\u6b63\u5979\u5df2\u7ecf\u53ec\u4e86\u8005\u534a\u5929\uff0c\u4e5f\u4e0d\u5728\u4e4e\u8fd9\u4e00\u4f1a\u513f\u561b\u3002\u201d \u3000\u3000\u6c5f\u67ab\u987e\u8651\u9053\uff1a\u201c\u4e0d\u592a\u597d\u5427\u2026\u2026\u201d \u3000\u3000\u6885\u82b1\u773c\u76ae\u4e00\u7ffb\uff0c\u60bb\u7136\u8bf4\uff0c\u201c\u4f60\u7b49\u4e0d\u53ca\u53bb\u9001\u6b7b\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u5f3a\u81ea\u4e00\u7b11\u9053\uff1a\u201c\u4f60\u600e\u4e48\u89e6\u6211\u9709\u5934\uff01\u201d \u3000\u3000\u201c\u4e0d\u662f\u89e6\u4f60\u9709\u5934\uff0c\u6211\u662f\u8bf4\u7684\u5b9e\u8bdd\u3002\u201d\u6885\u82b1\u6b63\u8272\u9053\uff1a\u201c\u5e38\u8a00\u8bf4\u7684\u597d\uff0c\u6765\u8005\u4e0d\u5584\u610f\uff0c\u5584\u8005\u4e0d\u6765\u3002 \u3000\u3000\u5982\u4eca\u4e09\u5bab\u4e3b\u65b0\u81ea\u51fa\u9a6c\uff0c\u51fa\u624b\u7edd\u4e0d\u4f1a\u7559\u60c5\u3002 \u3000\u3000\u4f60\u80fd\u6d3b\u547d\u7684\u5e0c\u671b\uff0c\u5b9e\u5728\u5fae\u4e4e\u5176\u5fae\uff0c\u4f46\u6211\u4e0d\u80fd\u773c\u7741\u7741\u770b\u7740\u4f60\u53bb\u9001\u6b7b\uff0c\u6240\u4ee5\uff0c\u6240\u4ee5\u2026\u2026\u201d \u3000\u3000\u597d\u8fde\u8bf4\u4e24\u58f0\u201c\u6240\u4ee5\u201d\uff0c\u5374\u8bf4\u4e0d\u51fa\u4e2a\u6240\u4ee5\u7136\u3002 \u3000\u3000\u6c5f\u67ab\u7b11\u95ee\uff1a\u201c\u65e2\u662f\u6d3b\u547d\u7684\u5e0c\u671b\u5fae\u4e4e\u5176\u5fae\uff0c\u4f60\u8fd8\u671f\u5f85\u4ec0\u4e48\u5947\u8ff9\uff1f\u201d \u3000\u3000\u6885\u82b1\u773c\u4e2d\u95ea\u7740\u5947\u5f02\u7684\u5149\u8292\uff0c\u9053\uff1a\u201c\u4e0d\u662f\u671f\u5f85\uff0c\u800c\u662f\u521b\u9020\u5947\u8ff9\u3002\u201d \u3000\u3000\u201c\u54e6\uff01\u201d\u6c5f\u67ab\u9732\u51fa\u4e00\u8138\u60ca\u8bb6\u4e4b\u8272\u3002 \u3000\u3000\u6885\u82b1\u50ac\u4fc3\u9053\uff1a\u201c\u4e8b\u4e0d\u5b9c\u8fdf\uff0c\u5feb\u5230\u4f60\u5c4b\u91cc\u53bb\u5427\u3002\u201d \u3000\u3000\u6c5f\u67ab\u4e0d\u77e5\u8fd9\u5c11\u5973\u80fd\u201c\u521b\u9020\u201d\u51fa\u4ec0\u4e48\u5947\u8ff9\uff0c\u5f53\u5373\u628a\u5979\u5e26\u8fdb\u4e86\u5185\u5ba4\u3002 \u3000\u3000\u6885\u82b1\u4e00\u8fdb\u623f\uff0c\u5c31\u90d1\u91cd\u5176\u4e8b\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u7ec3\u8fc7\u2018\u60d1\u5fc3\u672f\u2019\uff0c\u4f46\u4e09\u5bab\u4e3b\u7684\u2018\u5a9a\u672f\u2019\uff0c\u5df2\u8fdb\u5165\u81f3\u9ad8\u5883\u754c\uff0c\u5982\u679c\u4ee5\u6211\u7684\u90a3\u70b9\u706b\u5019\u8ddf\u5979\u76f8\u6bd4\uff0c\u72b9\u5982\u5c0f\u5deb\u89c1\u5927\u5deb\uff0c\u8424\u5149\u4e4b\u6bd4\u661f\u6708\uff0c\u8fd9\u6837\u7684\u5f3a\u654c\uff0c\u4f60\u81ea\u5df1\u4f30\u8ba1\u80fd\u6709\u51e0\u5206\u80a1\u7b97\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u541f\u4e00\u4e0b\u9053\uff0c \u3000\u3000\u201c\u4f60\u803d\u5fc3\u4e09\u5bab\u4e3b\u4e00\u89c1\u9762\u5c31\u65bd\u5c55\u2018\u5a9a\u672f\u2019\uff1f\u201d \u3000\u3000\u6885\u82b1\u70b9\u70b9\u5934\u8bf4\uff0c\u201c\u4e09\u5bab\u4e3b\u81ea\u89c6\u6781\u9ad8\uff0c\u5979\u7edd\u5bf9\u4e0d\u5c51\u4ee5\u6b66\u529f\u4eb2\u624b\u6740\u4f60\uff0c\u6781\u53ef\u80fd\u662f\u7528\u2018\u5a9a\u672f\u2019\uff0c\u5148\u4f7f\u4f60\u5931\u53bb\u62b5\u6297\u80fd\u529b\uff0c\u518d\u547d\u5979\u7684\u968f\u4ece\u4e0b\u624b\u3002\u201d \u3000\u3000\u6c5f\u67ab\u4e0d\u77e5\u662f\u5bf9\u201c\u60d1\u5fc3\u672f\u201d\u5145\u6ee1\u81ea\u4fe1\uff0c\u8ba4\u4e3a\u5fc5\u53ef\u538b\u5236\u4e09\u5bab\u4e3b\u7684\u2018\u5a9a\u672f\u2019\uff0c\u8fd8\u662f\u5bf9\u6885\u82b1\u7684\u5224\u65ad\u6709\u6240\u6000\u7591\uff0c\u8038\u8038\u80a9\u9053\uff1a\u201c\u90a3\u4e48\u5979\u7684\u6b66\u529f\uff0c\u7a76\u7adf\u9ad8\u5230\u4f55\u7b49\u7a0b\u5ea6\uff1f\u201d \u3000\u3000\u6885\u82b1\u5fe7\u5f62\u4e8e\u8272\u9053\uff1a\u201c\u8fd9\u5f88\u96be\u8bf4\uff0c\u56e0\u4e3a\u6211\u4ece\u672a\u89c1\u5979\u65bd\u5c55\u8fc7\uff0c\u53ea\u662f\uff0c\u7167\u6211\u4f30\u8ba1\uff0c\u5408\u4f60\u6211\u4e8c\u4eba\u4e4b\u529b\uff0c\u81f3\u591a\u4e5f\u53ea\u80fd\u62b5\u6321\u5979\u5341\u62db\uff0c\u6700\u540e\u5fc5\u8d25\u65e0\u7591\u3002\u201d \u3000\u3000\u6c5f\u67ab\u201c\u54e6\uff1f\u201d\u4e86\u4e00\u58f0\uff0c\u95ee\u9053\uff1a\u201c\u90a3\u4f60\u8fd8\u6307\u671b\u80fd\u2018\u521b\u9020\u2019\u51fa\u4ec0\u4e48\u5947\u8ff9\uff1f\u201d \u3000\u3000\u6885\u82b1\u80f8\u6709\u6210\u7af9\u8bf4\uff0c\u201c\u521a\u624d\u6211\u4e0d\u662f\u8bf4\u4e86\u5417\uff1f\u4e09\u5bab\u4e3b\u662f\u5185\u5bab\u4e09\u4f4d\u5bab\u4e3b\u4e4b\u4e00\uff0c\u4e00\u5411\u81ea\u89c6\u6781\u9ad8\uff0c\u82e5\u975e\u9ed1\u5929\u9e45\u53ca\u4e00\u4f4d\u559c\u9e4a\u7ea7\u7684\u5973\u5f1f\u5b50\uff0c\u5148\u540e\u4e27\u547d\u5728\u4f60\u624b\u4e2d\uff0c\u5979\u7edd\u4e0d\u4f1a\u4eb2\u81ea\u51fa\u9a6c\u3002 \u3000\u3000\u8eab\u4e3a\u5185\u5bab\u4e09\u5bab\u4e3b\u4e4b\u4e00\uff0c\u5979\u82e5\u4ed7\u6b66\u529f\u4eb2\u624b\u6740\u4f60\uff0c\u5c82\u4e0d\u6210\u4e86\u4e3a\u90a3\u4e24\u4e2a\u5973\u5f1f\u5b50\u62a5\u4ec7\uff1f\u4e3a\u4e86\u8fd9\u5c42\u987e\u5fcc\uff0c\u4ee5\u514d\u843d\u5165\u53e3\u5b9e\uff0c\u6240\u4ee5\u6211\u91cf\u5979\u4e0d\u81f4\u4e00\u89c1\u9762\u5c31\u8ddf\u4f60\u4ee5\u6b66\u529f\u89c1\u771f\u7ae0\u3002 \u3000\u3000\u4f46\u5979\u7684\u2018\u5a9a\u672f\u2019\u5df2\u81f3\u6700\u9ad8\u5883\u754c\uff0c\u53ef\u4ee5\u53d1\u4e8e\u65e0\u5f62\uff0c\u66f4\u4f55\u51b5\u6885\u82b1\u5385\u5185\u5916\u5e02\u7f72\u7684\uff0c\u5168\u662f\u542c\u4f7f\u4e8e\u5979\u7684\u4eba\uff0c\u5373\u4f7f\u660e\u77e5\u5b58\u5fc3\u65bd\u5c55\u2018\u5a9a\u672f\u2019\uff0c\u4e5f\u5fc5\u7136\u89c6\u82e5\u672a\u7779\u3002 \u3000\u3000\u8fd9\u6837\u4e00\u6765\uff0c\u5979\u4ee5\u2018\u5a9a\u672f\u2019\u5148\u5236\u4f4f\u4f60\uff0c\u4f7f\u4f60\u5931\u53bb\u62b5\u6297\u80fd\u529b\uff0c\u518d\u4e00\u58f0\u4ee4\u4e0b\uff0c\u7531\u5979\u5e26\u6765\u7684\u4eba\u51fa\u624b\u7f6e\u4f60\u4e8e\u6b7b\uff0c\u60c5\u5f62\u5c31\u5b8c\u5168\u4e0d\u540c\u4e86\u3002\u5c31\u50cf\u738b\u526f\u603b\u7ba1\u4e00\u6837\uff0c\u9093\u603b\u7ba1\u4e00\u58f0\u2018\u8be5\u6b7b\u2019\uff0c\u4f60\u5c31\u52a8\u624b\u6740\u4e86\u4ed6\u3002 \u3000\u3000\u6240\u4ee5\u6211\u6562\u6253\u8d4c\uff0c\u672c\u5bab\u4e3b\u7edd\u5bf9\u662f\u5b89\u7684\u8fd9\u4e2a\u5fc3\uff01\u201d \u3000\u3000\u6c5f\u67ab\u542c\u5979\u4e00\u53e3\u6c14\u8bf4\u5b8c\uff0c\u624d\u7b11\u95ee\u9053\uff1a\u201c\u4f60\u6240\u8c13\u7684\u2018\u521b\u9020\u2019\u5947\u8ff9\uff0c\u662f\u5426\u5df2\u6709\u5bf9\u4ed8\u4e09\u5bab\u4e3b\u7684\u9526\u56ca\u5999\u8ba1\uff1f\u201d \u3000\u3000\u201c\u55ef\uff01\u201d\u6885\u82b1\u9053\uff1a\u201c\u6211\u8981\u4f7f\u51fa\u6df7\u8eab\u89e3\u6570\uff0c\u5411\u4f60\u65bd\u5c55\u2018\u5a9a\u672f\u2019\u201d \u3000\u3000\u201c\u54e6\uff1f\u201d\u6c5f\u67ab\u4e3a\u4e4b\u4e00\u6014\uff0c\u832b\u7136\u9053\uff1a\u201c\u6211\u4e0d\u61c2\u4f60\u7684\u610f\u601d\u2026\u2026\u201d \u3000\u3000\u6885\u82b1\u63a5\u9053\uff1a\u201c\u8fd9\u5c31\u5982\u540c\u4f60\u51c6\u5907\u5bb4\u524d\uff0c\u6211\u5148\u8ba9\u4f60\u5c1d\u5c3d\u5929\u4e0b\u73cd\u54c1\uff0c\u90a3\u4e48\u4f60\u518d\u89c1\u5230\u5c71\u73cd\u6d77\u5473\uff0c\u4e5f\u5c31\u6beb\u65e0\u80c3\u53e3\u4e86\u3002\u201d \u3000\u3000\u6c5f\u67ab\u82e5\u6709\u6240\u609f\u9053\uff1a\u201c\u6211\u660e\u767d\u4e86\uff0c\u4f60\u662f\u8bf4\u2026\u2026\u201d \u3000\u3000\u6885\u82b1\u6b63\u8272\u9053\uff1a\u201c\u4e0d\u8981\u8ddf\u6211\u4e89\u8fa9\uff0c\u6211\u4eec\u5fc5\u987b\u4e89\u53d6\u65f6\u95f4\uff0c\u5373\u4f7f\u8fd9\u4e00\u7740\u5931\u7b97\uff0c\u60c5\u51b5\u5b8c\u5168\u51fa\u6211\u610f\u6599\u4e4b\u5916\uff0c\u90a3\u4e48\u6b64\u523b\u5c31\u7b97\u6211\u4eec\u4eca\u751f\u7684\u6700\u540e\u76f8\u805a\u5427\uff01\u201d \u3000\u3000\u8a00\u4e0b\u4e4b\u610f\uff0c\u4e07\u4e00\u4e09\u5bab\u4e3b\u89c1\u97e9\u9738\u5c31\u51fa\u624b\uff0c\u6885\u82b1\u4e5f\u5df2\u62b1\u5b9a\u5fc5\u6b7b\u51b3\u5fc3\u3002 \u3000\u3000\u6c5f\u67ab\u8f7b\u53f9\u4e00\u58f0\uff0c\u6765\u4e0d\u53ca\u529d\u963b\uff0c\u6885\u82b1\u5df2\u53cc\u624b\u9f50\u52a8\uff0c\u5bbd\u8863\u89e3\u5e26\uff0c\u5c06\u5168\u8eab\u8863\u7269\u5c3d\u892a\u3002 \u3000\u3000\u987f\u65f6\uff0c\u5448\u73b0\u5728\u6c5f\u67ab\u773c\u524d\u7684\uff0c\u662f\u4e2a\u5343\u5a07\u767e\u5a9a\uff0c\u66f2\u4f53\u73b2\u73d1\u7684\u8d64\u88f8\u5c11\u5973\uff01 \u3000\u3000\u6885\u82b1\u53cc\u988a\u6cdb\u7ea2\uff0c\u76ca\u589e\u51e0\u5206\u5a07\u8273\uff0c\u5a07\u58f0\u9053\uff1a\u201c\u770b\u7740\u6211\uff0c\u4f46\u5343\u4e07\u4e0d\u53ef\u4ee5\u2018\u60d1\u5fc3\u672f\u2019\uff0c\u6216\u662f\u4efb\u4f55\u5176\u5b83\u529b\u91cf\u6297\u62d2\uff0c\u5426\u5219\u6211\u5c31\u6789\u8d39\u5fc3\u673a\u4e86\u3002\u201d \u3000\u3000\u6c5f\u67ab\u4f9d\u8a00\u671b\u7740\u6885\u82b1\u3002 \u3000\u3000\u53ea\u89c1\u8fd9\u8d64\u88f8\u7684\u5c11\u5973\uff0c\u70df\u89c6\u5a9a\u884c\u5730\u8d70\u8fd1\u6c5f\u67ab\uff0c\u5168\u8eab\u5e76\u4e14\u6563\u53d1\u51fa\u4e00\u80a1\u4ee4\u4eba\u65e0\u6cd5\u6297\u62d2\u7684\u9b45\u529b\u3002 \u3000\u3000\u7167\u7406\u8bf4\uff0c\u6c5f\u67ab\u4e0e\u6885\u82b1\u5df2\u6625\u98ce\u6570\u5ea6\uff0c\u6709\u8fc7\u808c\u80a4\u4e4b\u4eb2\uff0c\u5bf9\u8fd9\u5c11\u5973\u7684\u80f4\u4f53\uff0c\u4e0d\u4f46\u65e9\u5df2\u904d\u89c8\u65e0\u9057\uff0c\u4e0a\u4e0b\u5176\u624b\uff0c\u66f4\u4e14\u771f\u4e2a\u9500\u9b42\uff0c\u4e0d\u81f4\u5982\u6b64\u7740\u8ff7\u3002 \u3000\u3000\u4f46\u6885\u82b1\u6b64\u523b\u7684\u8ff7\u4eba\u4e4b\u5904\uff0c\u5e76\u975e\u662f\u5979\u88f8\u9732\u7684\u8bf1\u4eba\u8d2d\u4f53\uff0c\u800c\u662f\u90a3\u64a9\u4eba\u5fc3\u5f26\u7684\u60c5\u6001\uff0c\u5c24\u5176\u662f\u90a3\u52fe\u9b42\u6444\u9b44\u7684\u773c\u795e\uff01 \u3000\u3000\u6c5f\u67ab\u6ca1\u6709\u65bd\u5c55\u2018\u60d1\u5fc3\u672f\u2019\uff0c\u66f4\u6ca1\u6709\u6297\u62d2\u5979\u7684\u610f\u5ff5\u3002 \u3000\u3000\u9010\u6e10\u5730\uff0c\u4ed6\u6709\u4e9b\u610f\u4e71\u60c5\u8ff7\u8d77\u6765\u2026\u2026 \u3000\u3000\u773c\u524d\u7684\u6885\u82b1\uff0c\u90fd\u662f\u82e5\u5373\u82e5\u79bb\uff0c\u4eff\u4f5b\u662f\u4e2a\u865a\u5ffd\u98d8\u6e3a\u7684\u5e7b\u5f71\uff0c\u59cb\u7ec8\u53ea\u5728\u6c5f\u67ab\u9762\u524d\u6643\u6765\u6643\u53bb\uff0c\u6414\u9996\u5f04\u59ff\u7684\uff0c\u6781\u5c3d\u5f15\u9017\u4e4b\u80fd\u4e8b\uff0c\u5e76\u4e0d\u91c7\u53d6\u8fdb\u4e00\u6b65\u7684\u884c\u52a8\u3002 \u3000\u3000\u8fd9\u662f\u6545\u610f\u540a\u6c5f\u67ab\u7684\u80c3\u53e3\uff1f \u3000\u3000\u6c5f\u67ab\u5df2\u88ab\u5979\u6311\u9017\u5f97\u6b32\u706b\u4e0a\u5347\uff0c\u518d\u4e5f\u6309\u637a\u4e0d\u4f4f\uff0c\u5931\u63a7\u5730\u7a81\u5411\u6885\u82b1\u6251\u53bb\u3002 \u3000\u3000\u6885\u82b1\u5e76\u672a\u95ea\u907f\uff0c\u88ab\u4ed6\u53cc\u81c2\u4e00\u5f20\uff0c\u62b1\u4e86\u4e2a\u6b63\u7740\u3002 \u3000\u3000\u6c5f\u67ab\u5f62\u540c\u75af\u72c2\uff0c\u5c06\u6885\u82b1\u697c\u8fdb\u6000\u91cc\u72c2\u543b\u4e0d\u5df2\u3002 \u3000\u3000\u6885\u82b1\u5fc3\u77e5\u65f6\u673a\u5df2\u7ecf\u6210\u719f\uff0c\u5a07\u58f0\u8bf4\uff0c\u201c\u5feb\u62b1\u6211\u5230\u5e8a\u4e0a\u53bb\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u5982\u5949\u61ff\u65e8\uff0c\u5fd9\u4e0d\u8fed\u5c06\u6885\u82b1\u62b1\u4e0a\u5e8a\uff0c\u8ba9\u5979\u5e73\u8eba\u5728\u5e8a\u4e0a\uff0c\u8feb\u4e0d\u53ca\u5f85\u5730\u5411\u5979\u904d\u4f53\u72c2\u543b\u3002 \u3000\u3000\u6885\u82b1\u6b64\u523b\u4e0e\u5e73\u65f6\u7b80\u76f4\u5224\u82e5\u4e24\u4eba\uff0c\u53ea\u542c\u5979\u4e0d\u65ad\u53d1\u51fa\u653e\u6d6a\u5f62\u9ab8\u7684\u7b11\u58f0\uff0c\u5982\u540c\u4e00\u4e2a\u72fc\u864e\u4e4b\u5e74\u7684\u8361\u5987\u3002 \u3000\u3000\u53ea\u542c\u5979\u6d6a\u7b11\u9053\uff1a\u201c\u6c5f\u90ce\uff0c\u59be\u8eab\u65e9\u5df2\u8eab\u5fc3\u90fd\u5c5e\u4e8e\u4f60\u4e86\uff0c\u5c3d\u60c5\u5730\u4eab\u53d7\u5427\uff01\u54c8\u54c8\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u66f4\u89c9\u6b32\u4eba\u96be\u7981\uff0c\u970d\u5730\u6491\u8d77\u8eab\u5b50\uff0c\u6b63\u5f85\u8131\u53bb\u8863\u7269\u4e0a\u722c\uff0c\u5c55\u5f00\u7ffb\u4e91\u8986\u96e8\u7684\u8089\u640f\u4e4b\u9645\uff0c\u5ffd\u542c\u4e03\u5b9d\u548c\u5c1a\u4ee5\u2018\u4f20\u97f3\u5165\u5bc6\u2019\u4e4b\u58f0\u53d1\u51fa\u8b66\u544a\uff1a \u3000\u3000\u201c\u6885\u82b1\u5385\u6d3e\u4eba\u6765\u50ac\u9a7e\u5566\uff01\u201d \u3000\u3000\u6c5f\u67ab\u4ece\u8ff7\u4e71\u4e2d\u7a81\u7136\u6e05\u9192\u3002 \u3000\u3000\u6885\u82b1\u5374\u7d27\u7d27\u6402\u4f4f\u4ed6\u8eab\u5b50\u4e0d\u653e\uff1a\u201c\u4e0d\u8981\u7406\u4f1a\uff0c\u4e0e\u5176\u6b7b\u5728\u4e09\u5bab\u4e3b\u624b\u4e2d\uff0c\u4e0d\u5982\u6b7b\u5728\u4f60\u6000\u91cc\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u7ed9\u4e86\u5979\u4e00\u4e2a\u70ed\u543b\uff0c\u5f53\u673a\u7acb\u65ad\u9053\uff1a\u201c\u5feb\u8d77\u6765\uff0c\u6211\u4eec\u53bb\u89c1\u4e09\u5bab\u4e3b\uff01\u201d \u3000\u3000\u6885\u82b1\u987f\u89c9\u6005\u7136\u82e5\u5931\uff0c\u60c5\u4e0d\u81ea\u7981\u6cea\u6c34\u593a\u7736\u800c\u51fa\u3002 \u3000 \u7b2c\u5341\u516d\u56de\u3000\u8fce\u51b3\u6218\u4f17\u4fa0\u4f20\u6b66\u827a \u3000 \u3000\u3000\u795e\u3001\u9b3c\u53cc\u5200\uff0c\u66f4\u662f\u8dcc\u5728\u5730\u4e0a\uff0c\u53cc\u624b\u634f\u8bc0\uff0c\u6b63\u5728\u8fd0\u6c14\u8c03\u606f\u3002 \u3000\u3000\u770b\u4e0a\u53bb\uff0c\u795e\u3001\u9b3c\u53cc\u5200\u4f3c\u662f\u53d7\u521b\u8f83\u91cd\uff0c\u4f46\u5f20\u56db\u59d1\u5374\u77a7\u51fa\u4e86\u4e24\u4eba\u7684\u8138\u8272\uff0c\u4f3c\u662f\u6bd4\u7f57\u5170\u597d\u4e9b\u4e86\u3002 \u3000\u3000\u770b\u8fc7\u4e86\u573a\u4e2d\u5f62\u52bf\uff0c\u4e09\u5bab\u4e3b\u7f13\u7f13\u5411\u524d\u8d70\u4e86\u4e24\u6b65\u3002 \u3000\u3000\u4f46\u5979\u4e00\u52a8\uff0c\u541f\u96ea\u3001\u541f\u971c\uff0c\u5df2\u5feb\u5982\u95ea\u7535\u822c\uff0c\u62a1\u5728\u4e86\u7f57\u5170\u8eab\u524d\uff0c\u5de6\u624b\u659c\u51fa\u98df\u6307\uff0c\u53f3\u624b\u5411\u4e0a\u626c\u8d77\u3002 \u3000\u3000\u4e09\u5bab\u4e3b\u662f\u5927\u884c\u5bb6\uff0c\u4e00\u773c\u77a7\u51fa\uff0c\u4e8c\u5973\u5df2\u7ec3\u6210\u4e86\u5929\u72d0\u6307\uff0c\u522b\u77a7\u90a3\u4e00\u6839\u7ec7\u7ec7\u5982\u7389\u7684\u624b\u6307\uff0c\u5374\u542b\u8574\u4e86\u4e00\u80a1\u4f24\u7a74\u53d6\u547d\u7684\u5f3a\u5927\u529b\u9053\uff0c\u5fae\u626c\u7684\u53f3\u624b\u4e2d\uff0c\u4e5f\u53ef\u80fd\u63e1\u4e86\u4e00\u628a\u6c5f\u6e56\u4e0a\u6700\u9738\u9053\u7684\u6697\u5668\u201c\u5929\u72d0\u9488\u201d\uff0c\u5fc3\u4e2d\u6697\u6697\u5403\u4e86\u4e00\u60ca\uff0c\u5fd6\u9053\uff1a\u201c\u4e24\u4e2a\u5c0f\u72d0\u5973\uff0c\u5e74\u7eaa\u4e0d\u5927\uff0c\u4f3c\u4e4e\u662f\u5df2\u5f97\u4e86\u5929\u72d0\u771f\u4f20\uff0c\u80fd\u4e0d\u80fd\u63a5\u4e0b\u5979\u4eec\u4e00\u51fb\uff0c\u8fd8\u771f\u96be\u9884\u6599\uff1f\u201d \u3000\u3000\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u53e3\u4e2d\u5374\u7b11\u9053\uff1a\u201c\u5e72\u4ec0\u4e48\u5440\uff1f\u6211\u53ea\u662f\u8d70\u8fd1\u4e9b\u77a7\u77a7\u795e\u3001\u9b3c\u4e8c\u8001\u7684\u4f24\u52bf\u3002\u201d \u3000\u3000\u201c\u4ed6\u4eec\u6b7b\u4e0d\u4e86\uff0c\u201d\u541f\u971c\u51b7\u51b7\u5730\u8bf4\u9053\uff1a\u201c\u4f46\u4f60\u518d\u5f80\u524d\u8d70\u4e24\u6b65\uff0c\u90a3\u5c31\u5f88\u96be\u8bf4\u4e86\uff0c\u4ed6\u4eec\u5185\u529f\u518d\u597d\uff0c\u73b0\u5728\u8fd9\u4e2a\u60c5\u5f62\u4e0b\uff0c\u4e5f\u65e0\u6cd5\u8eb2\u5f00\u5929\u72d0\u9488\u3002\u201d \u3000\u3000\u201c\u8bf4\u6709\u7684\u6709\u7406\u2026\u2026\uff0c\u201d\u4e09\u5bab\u4e3b\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u4ee4\u5802\u7684\u4f24\u52bf\uff0c\u4e5f\u4e0d\u5f88\u8f7b\uff0c\u53cc\u65b9\u90fd\u9700\u8981\u4e00\u6bb5\u65f6\u95f4\u8c03\u606f\uff0c\u4eca\u65e5\u4e4b\u6218\uff0c\u7b97\u662f\u4e0d\u5206\u80dc\u8d1f\uff0c\u660e\u5929\uff0c\u6211\u4eec\u518d\u7ea6\u4ed6\u51b3\u4e00\u6b7b\u6218\uff0c\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u201c\u597d\u554a\uff01\u2026\u2026\u201d\u5f20\u56db\u59d1\u63a5\u9053\uff1a\u201c\u660e\u5929\u4ec0\u4e48\u65f6\u523b\uff1f\u5728\u4ec0\u4e48\u5730\u65b9\uff1f\u201d \u3000\u3000\u201c\u660e\u5929\u5348\u65f6\u4e4b\u524d\uff0c\u6211\u4f1a\u6d3e\u4eba\u901a\u77e5\uff0c\u201d\u4e09\u5bab\u4e3b\u9053\uff1a\u201c\u65e2\u662f\u660e\u5200\u660e\u67aa\u7684\u5bf9\u5792\uff0c\u5e94\u8be5\u627e\u4e2a\u5bbd\u5e7f\u7684\u5730\u65b9\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u6211\u4eec\u606d\u4faf\u901a\u77e5\uff0c\u541f\u96ea\u3001\u541f\u971c\uff0c\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u6276\u4f4f\u4f60\u5a18\uff0c\u8d70\uff01\u201d \u3000\u3000\u5f20\u56db\u59d1\u6321\u5728\u95e8\u53e3\u5168\u795e\u6212\u5907\uff0c\u4ee5\u9632\u8ffd\u88ad\uff0c\u76f4\u5f85\u4e8c\u5973\u53bb\u8fdc\uff0c\u624d\u8f6c\u8eab\u79bb\u5f00\u3002 \u3000\u3000\u4e09\u5bab\u4e3b\u5ffd\u7136\u53cc\u624b\u75be\u51fa\uff0c\u5206\u5411\u795e\u3001\u9b3c\u4e8c\u8001\u7684\u80cc\u4e0a\u62cd\u53bb\u3002 \u3000\u3000\u4f46\u89c1\u5bd2\u8292\u4e00\u95ea\uff0c\u795e\u3001\u9b3c\u4e8c\u8005\u8896\u4e2d\u5200\u8292\u75be\u73b0\uff0c\u6307\u5411\u4e86\u4e09\u5bab\u4e3b\u7684\u54bd\u5589\u8981\u5bb3\u3002 \u3000\u3000\u201c\u5e72\u4e48\u5440\uff01\u201d\u4e09\u5bab\u4e3b\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6211\u53ea\u662f\u8981\u52a9\u4e24\u4f4d\u4e00\u81c2\u4e4b\u529b\uff0c\u65e9\u4e9b\u7597\u597d\u4f24\u52bf\u3002\u201d \u3000\u3000\u201c\u6211\u4eec\u4e5f\u53ea\u662f\u60f3\u8bc1\u660e\u4e00\u4e0b\uff0c\u6211\u4eec\u8fd8\u6709\u4f59\u529b\u4fdd\u62a4\u81ea\u5df1\uff0c\u201d\u9b3c\u5200\u5fae\u7b11\u9053\uff1a\u201c\u4eba\u8001\u6210\u7cbe\uff0c\u7591\u5fc3\u4e5f\u91cd\uff0c\u6c5f\u6e56\u4e0a\u7684\u9634\u8c0b\u6697\u7b97\u770b\u591a\u4e86\uff0c\u96be\u514d\u4f1a\u6709\u4e9b\u5fc3\u795e\u7d27\u5f20\u3002\u201d \u3000\u3000\u201c\u662f\u554a\uff01\u201d\u795e\u8001\u63a5\u9053\uff1a\u201c\u4e09\u5bab\u4e3b\u591a\u591a\u62c5\u5f85\uff0c\u4f60\u8fd9\u4e48\u4e00\u58f0\u4e0d\u54cd\u7684\u626c\u8d77\u53cc\u624b\uff0c\u4e5f\u96be\u602a\u9b3c\u5144\u7d27\u5f20\uff01\u201d \u3000\u3000\u201c\u4f60\u8fd8\u4e0d\u662f\u4e00\u6837\uff0c\u201d\u4e09\u5bab\u4e3b\u9053\uff1a\u201c\u5200\u5c16\u5b50\u6307\u7740\u6211\u7684\u54bd\u5589\u3002\u201d \u3000\u3000\u201c\u6ca1\u6709\u6cd5\u5b50\uff0c\u51e0\u5341\u5e74\u7684\u8001\u4e60\u60ef\uff0c\u5c31\u7b97\u60f3\u6539\u2026\u2026\u201d\u795e\u8001\u9053\uff1a\u201c\u4e00\u4e0b\u5b50\u4e5f\u6539\u4e0d\u8fc7\u6765\u5440\uff01\u201d \u3000\u3000\u201c\u81ea\u53e4\u4ee5\u6765\uff0c\u795e\u5750\u5927\u6bbf\uff0c\u9b3c\u9690\u8352\u90ca\uff0c\u201d\u9b3c\u8001\u7b11\u9053\uff1a\u201c\u6211\u8bf4\u4e09\u5bab\u4e3b\u5440\uff01\u795e\u6cd5\u603b\u6bd4\u9b3c\u6cd5\u9ad8\uff01\u201d \u3000\u3000\u201c\u8fd9\u6211\u5c31\u4e0d\u61c2\u4e86\u2026\u2026\u201d\u738b\u5bab\u4e3b\u9053\uff1a\u201c\u4e24\u4f4d\u80fd\u8bf4\u80fd\u7b11\uff0c\u5206\u660e\u53d7\u521b\u4e0d\u91cd\uff0c\u4f46\u6211\u770b\u7f57\u5170\u4f3c\u662f\u5df2\u6c14\u529b\u8017\u5c3d\uff0c\u4e24\u4f4d\u4f55\u4e0d\u8d81\u673a\u51fa\u624b\uff0c\u53d6\u5979\u6027\u547d\uff0c\u5374\u7559\u4e0b\u4e86\u7978\u6839\u3002\u201d \u3000\u3000\u201c\u4e24\u4e2a\u5c0f\u72d0\u5973\uff0c\u4f3c\u5df2\u5f97\u5929\u72d0\u771f\u4f20\u3002\u5f20\u56db\u59d1\u4e5f\u975e\u7b49\u95f2\u4eba\u7269\uff0c\u201d\u795e\u8001\u6536\u56de\u8896\u4e2d\u5200\uff0c\u63a5\u9053\uff1a \u3000\u3000\u201c\u4f55\u51b5\uff0c\u90a3\u4e00\u523b\u8fb0\u5149\uff0c\u6211\u4eec\u4e5f\u6682\u5931\u53bb\u4e86\u653b\u51fb\u80fd\u529b\u3002\u201d \u3000\u3000\u201c\u5bf9\uff01\u90a3\u7247\u523b\u5de5\u592b\u4e4b\u5185\uff0c\u5c31\u7b97\u6211\u4eec\u52c9\u5f3a\u51fa\u5200\uff0c\u4e5f\u6ca1\u6709\u4f24\u4eba\u7684\u5a01\u529b\uff0c\u201d\u9b3c\u8001\u9053\uff1a\u201c\u4f60\u8eb2\u5728\u6211\u4eec\u8eab\u540e\uff0c\u6211\u4eec\u770b\u4e0d\u89c1\u4f60\u7684\u8868\u60c5\uff0c\u751a\u81f3\u4e0d\u77e5\u4f60\u662f\u5426\u8fd8\u5728\u5385\u4e2d\u3002\u5982\u82e5\u6211\u4eec\u77e5\u9053\u6709\u4eba\u80fd\u63a5\u4e0b\u5f20\u56db\u59d1\u548c\u4e24\u4e2a\u5c0f\u72d0\u5973\u7684\u653b\u52bf\uff0c\u6211\u4eec\u5728\u7565\u4f5c\u8c03\u606f\u4e4b\u540e\uff0c\u7edd\u5bf9\u6709\u6740\u6b7b\u7f57\u5170\u7684\u673a\u4f1a\u3002\u201d \u3000\u3000\u4e09\u5bab\u4e3b\u9053\uff1a\u201c\u8fd9\u8bf4\u6765\u8bf4\u53bb\uff0c\u53c8\u602a\u5230\u6211\u7684\u5934\u4e0a\u6765\u4e86\uff1f\u4f46\u4f60\u4eec\u548c\u5979\u5df2\u7ed3\u4e0b\u4e86\u751f\u6b7b\u4e4b\u4ec7\uff0c\u8fd9\u4e2a\u540e\u679c\uff0c\u6211\u5c31\u662f\u60f3\u5206\u62c5\uff0c\u4e5f\u5206\u62c5\u4e0d\u6765\u3002\u201d \u3000\u3000\u201c\u653e\u5fc3\uff01\u653e\u5fc3\uff01\u201d\u795e\u8001\u7b11\u9053\uff1a\u201c\u80fd\u628a\u5251\u9053\u7ec3\u5230\u5979\u90a3\u79cd\u7a0b\u5ea6\u7684\u4eba\uff0c\u4e0d\u4f1a\u8bb0\u62a5\u79c1\u4ec7\uff0c\u53ea\u6709\u516c\u654c\uff0c\u5012\u662f\u4f60\u4e09\u5bab\u4e3b\u8981\u5f53\u5fc3\u4e00\u4e9b\uff0c\u5728\u5979\u5802\u5802\u6b63\u6b63\u7684\u5251\u6cd5\u4e4b\u4e0b\uff0c\u9634\u8c0b\u6697\u7b97\uff0c\u53ea\u6015\u5f88\u96be\u5f97\u624b\u3002\u201d \u3000\u3000\u4e09\u5bab\u4e3b\u6697\u6697\u4e00\u54ac\u7259\uff0c\u628a\u4e00\u80a1\u6028\u5fff\u541e\u4e86\u4e0b\u53bb\uff0c\u7b11\u9053\uff1a\u201c\u6211\u5df2\u7ea6\u4ed6\u4eec\u660e\u65e5\u51b3\u6218\uff0c\u5c4a\u65f6\uff0c\u4e24\u4f4d\u4e0d\u548c\u7f57\u5170\u52a8\u624b\u5417\uff1f\u201d \u3000\u3000\u201c\u5c11\u7528\u6fc0\u5c06\u6cd5\uff0c\u8001\u592b\u4e0d\u5403\u8fd9\u4e2a\uff0c\u201d\u795e\u8001\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u4eca\u5929\u5df2\u8bd5\u51fa\u5979\u6df1\u6d45\uff0c\u518d\u6709\u673a\u4f1a\u4e00\u6218\uff0c\u5b9a\u53ef\u53d6\u5979\u6027\u547d\u3002\u201d \u3000\u3000\u201c\u6211\u4eec\u8eab\u85cf\u4e94\u628a\u5200\uff0c\u2026\u2026\u201d\u9b3c\u8001\u9053\uff1a\u201c\u4eca\u65e5\u53ea\u7528\u51fa\u4e00\u628a\u800c\u5df2\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u90a3\u5c31\u4ecd\u7531\u4e24\u4f4d\u5bf9\u4ed8\u7f57\u5170\u2026\u2026\u201d\u4e09\u5bab\u4e3b\u9053\uff1a\u201c\u83ca\u82b1\u3001\u6842\u82b1\uff0c\u6276\u4e24\u4f4d\u8001\u4eba\u5bb6\u5230\u2018\u8fce\u5bbe\u9601\u2019\u4e2d\u4f11\u606f\uff0c\u597d\u597d\u62db\u5f85\uff0c\u4e0d\u80fd\u62d2\u7edd\u4e24\u4f4d\u8001\u4eba\u5bb6\u4efb\u4f55\u8981\u6c42\u3002\u201d \u3000\u3000\u201c\u6211\u4eec\u2026\u2026\u201d \u3000\u3000\u6253\u65ad\u4e86\u6842\u82b1\u7684\u8bdd\uff0c\u4e09\u5bab\u4e3b\u51b7\u51b7\u63a5\u9053\uff1a\u201c\u6ca1\u6709\u4f60\u4eec\u3001\u6211\u4eec\uff0c\u7531\u6b64\u523b\uff0c\u4f60\u4eec\u5c31\u662f\u4e24\u4f4d\u8001\u4eba\u5bb6\u7684\u4f8d\u5a62\uff0c\u4f60\u4eec\u6709\u7684\uff0c\u5305\u62ec\u4f60\u4eec\u7684\u8eab\u4f53\u5728\u5185\uff0c\u90fd\u53ef\u7531\u4e24\u4f4d\u8001\u4eba\u5bb6\u5c3d\u60c5\u4eab\u53d7\uff0c\u4e0d\u5f97\u6709\u4efb\u4f55\u8fdd\u8bef\uff0c\u6211\u8bf4\u7684\u591f\u660e\u767d\u4e86\u5427\uff1f\u201d \u3000\u3000\u201c\u662f\uff01\u5f1f\u5b50\u4eec\u4e5f\u5b8c\u5168\u660e\u767d\u4e86\u2026\u2026\u201d\u6842\u82b1\u9053\uff1a\u201c\u6211\u4eec\u4f1a\u9075\u547d\u884c\u4e8b\u3002\u201d \u3000\u3000\u201c\u5f88\u597d\uff01\u53ea\u8981\u4e24\u4f4d\u8001\u4eba\u5bb6\u751f\u6d3b\u7684\u5feb\u4e50\uff0c\u6211\u4f1a\u91cd\u5956\u4f60\u4eec\u3002\u201d \u3000\u3000\u201c\u591a\u8c22\u4e09\u5e08\u7236\u2026\u2026\u201d\u79cb\u82b1\u53cc\u5a62\u9f50\u9f50\u8eac\u8eab\u884c\u793c\u3002 \u3000\u3000\u201c\u4e24\u4f4d\u8001\u4eba\u5bb6\uff0c\u8bf7\u597d\u597d\u7684\u517b\u606f\u3001\u4eab\u53d7\uff0c\u2026\u2026\u201d\u4e09\u5bab\u4e3b\u9053\uff1a\u201c\u6211\u8fd8\u8981\u53bb\u7ea6\u8bf7\u51e0\u4f4d\u52a9\u62f3\u7684\u670b\u53cb\uff0c\u665a\u4e0a\u518d\u53bb\u770b\u4f60\u4eec\uff0c\u5931\u966a\u4e86\u3002\u201d \u3000\u3000\u8f6c\u8eab\u4e00\u8dc3\uff0c\u4eba\u8e2a\u987f\u5931\u3002 \u3000\u3000\u770b\u770b\u79cb\u82b1\u53cc\u5a62\u5341\u5206\u5e74\u8f7b\u7f8e\u8c8c\uff0c\u62ff\u6743\u52bf\u5957\u4e0b\u6765\uff0c\u8fd8\u6709\u4ec0\u4e48\u5473\u9053\uff1f \u3000\u3000\u201c\u4e0d\uff01\u5a62\u5b50\u4eec\u656c\u4f69\u4e8c\u4f4d\u7684\u5200\u6cd5\uff0c\u65e9\u5df2\u5fc3\u7518\u60c5\u613f\uff0c\u4f8d\u5019\u6795\u5e2d\uff0c\u7167\u987e\u4e24\u4f4d\u7684\u751f\u6d3b\u8d77\u5c45\u3002\u201d\u79cb\u82b1\u53cc\u724c\uff0c\u9f50\u9f50\u6d41\u8f6c\u773c\u6ce2\u3001\u5a07\u58f0\u56de\u7b54\u3002 \u3000\u3000\u201c\u8fd9\u4e00\u70b9\uff0c\u4e24\u4f4d\u53ef\u8981\u60f3\u6e05\u695a\u554a\uff01\u6211\u4eec\u5df2\u8fd1\u767e\u5c81\uff0c\u4e24\u4f4d\u624d\u5341\u516b\u59d1\u5a18\u4e00\u6735\u82b1\uff0c\u2026\u2026\u201d\u9b3c\u8001\u9053\uff1a\u201c\u6b64\u540e\u7684\u65e5\u5b50\uff0c\u8fd8\u957f\u7684\u5f88\u54ea\uff01\u201d \u3000\u3000\u201c\u4e24\u4f4d\u8001\u4eba\u5bb6\u7684\u610f\u601d\u662f\u8bf4\uff0c\u6709\u4e86\u6211\u4eec\u4e4b\u540e\uff0c\u5c31\u4e0d\u518d\u627e\u522b\u7684\u5973\u4eba\u4e86\u2026\u2026\u201d\u6842\u82b1\u671b\u4e86\u4e8c\u8001\u4e00\u773c\uff0c\u63a5\u9053\uff1a\u201c\u4e00\u4e2a\u4f8d\u5a62\u8eab\u4efd\u7684\u4eba\uff0c\u53ef\u4e0d\u6562\u6709\u8fd9\u7b49\u5962\u671b\u3002\u201d \u3000\u3000\u795e\u8001\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e24\u4f4d\u59d1\u5a18\u5982\u662f\u771f\u5fc3\u4f8d\u5019\u6211\u4eec\uff0c\u6211\u4eec\u7edd\u4e0d\u4f1a\u53eb\u4e24\u4f4d\u5931\u671b\uff0c\u6211\u4eec\u65e0\u513f\u65e0\u5973\uff0c\u4e5f\u6ca1\u6709\u6536\u8fc7\u5f1f\u5b50\uff0c\u6b64\u540e\uff0c\u54b1\u4eec\u662f\u592b\u59bb\u4fa3\uff0c\u4e5f\u662f\u5e08\u7236\u5f1f\u5b50\uff0c\u2026\u2026\u9b3c\u5144\u554a\uff01\u8fc7\u53bb\u90fd\u662f\u6211\u795e\u5360\u5148\uff0c\u8fd9\u6b21\u8ba9\u4f60\u5148\u9009\uff01\u201d \u3000\u3000\u201c\u6842\u82b1\u3001\u83ca\u82b1\uff0c\u5404\u6781\u5176\u7f8e\uff0c\u8001\u5b9e\u8bf4\uff0c\u5148\u540e\u4e4b\u5e8f\uff0c\u771f\u8fd8\u65e0\u6cd5\u5206\u6392\uff0c\u6211\u770b\uff0c\u54b1\u4eec\u5916\u7525\u6253\u5ba0\uff0c\u7167\u65e7\uff08\u8205\uff09\u5427\uff01\u8fd8\u662f\u7531\u4f60\u5148\u6311\u4e00\u4e2a\uff1f\u201d \u3000\u3000\u6842\u82b1\u5fc3\u4e2d\u6697\u6697\u9a82\u9053\uff1a\u201c\u4e24\u4f4d\u8001\u8272\u9b3c\uff0c\u628a\u6211\u4eec\u5f53\u8d27\u54c1\u6311\u554a\uff01\u5982\u6b64\u63a8\u6765\u8ba9\u53bb\uff0c\u53ea\u6b64\u4e00\u4ef6\u4e8b\uff0c\u5c31\u6b7b\u6709\u4f59\u4e8b\u3002\u201d \u3000\u3000\u5fc3\u4e2d\u6709\u65e0\u9650\u4e0d\u6ee1\uff0c\u8138\u4e0a\u5374\u5806\u8d77\u7b11\u5bb9\uff0c\u9053\uff1a\u201c\u8bf4\u7684\u662f\u554a\uff01\u4e24\u4f4d\u5404\u6709\u795e\u6001\uff0c\u6211\u548c\u83ca\u82b1\u59d0\u59d0\uff0c\u4e5f\u4e0d\u77e5\u9053\u5982\u4f55\u5206\u914d\u5462\uff1f\u54b1\u4eec\u5e72\u8106\u6765\u4e2a\u778e\u5b50\u6478\u8c61\uff0c\u5404\u51ed\u8fd0\u6c14\u5427\u3002\u201d \u3000\u3000\u201c\u597d\u73a9\uff01\u597d\u73a9\uff01\u600e\u4e48\u4e00\u4e2a\u6478\u6cd5\u5462\uff1f\u2026\u2026\u201d\u9b3c\u8001\u9053\uff1a\u201c\u80fd\u4e0d\u80fd\u4e00\u4e2a\u4eba\u6478\u4e2d\u4e24\u4e2a\uff0c\u8ba9\u53e6\u4e00\u4e2a\u4eba\u6253\u7a7a\u624b\uff0c\u90a3\u624d\u662f\u771f\u6b63\u7684\u8981\u547d\u523a\u6fc0\u3002\u201d \u3000\u3000\u201c\u7898\u4e0d\u884c\uff0c\u5982\u662f\u6fc0\u6012\u4e86\u6253\u7a7a\u7684\u4eba\u2026\u2026\u201d\u6842\u82b1\u9053\uff1a\u201c\u628a\u6028\u6068\u53d1\u5728\u6211\u4eec\u8eab\u4e0a\uff0c\u62bd\u5200\u4e00\u51fb\uff0c\u5c82\u4e0d\u662f\u8981\u6211\u4eec\u7684\u547d\u5417\uff1f\u201d \u3000\u3000\u201c\u8bf4\u7684\u4e5f\u662f\u554a\uff01\u6709\u9053\u662f\u6b32\u706b\u711a\u8eab\uff0c\u6253\u7a7a\u7684\u4eba\uff0c\u771f\u6709\u4e00\u5929\u71ac\u4e0d\u4f4f\u3002 \u3000\u3000\u8bf4\u4e0d\u5b9a\u771f\u4f1a\u6740\u4eba\u2026\u2026\u201d\u795e\u8001\u9053\uff1a\u201c\u6842\u82b1\uff0c\u8bf4\u8bf4\u770b\uff0c\u4f60\u775b\u5b50\u6478\u8c61\uff0c\u600e\u4e48\u4e00\u4e2a\u73a9\u6cd5\uff1f\u201d \u3000\u3000\u201c\u8d70\uff01\u5230\u8fce\u5bbe\u9601\u53bb\uff0c\u6211\u518d\u544a\u8bc9\u4f60\u4eec\uff0c\u4fdd\u8bc1\u662f\u597d\u73a9\u7684\u5f88\u2026\u2026\u201d \u3000\u3000\u6842\u82b1\u4f38\u624b\u53bb\u6276\u795e\u8001\u3002 \u3000\u3000\u5374\u4e0d\u6599\u795e\u3001\u9b3c\u4e8c\u8001\u7a81\u7136\u4e00\u8dc3\u800c\u8d77\uff0c\u9053\uff1a\u201c\u6211\u4eec\u53ef\u4ee5\u81ea\u5df1\u8d70\u3002\u201d \u3000\u3000\u4e24\u4eba\u5185\u529f\u7cbe\u6e5b\u3001\u6df1\u539a\uff0c\u6ca1\u7247\u523b\u8c03\u606f\uff0c\u5df2\u53ef\u884c\u52a8\u5982\u5e38\u3002 \u3000\u3000\u6c5f\u67ab\u9759\u9759\u5730\u8eba\u5728\u5e8a\u4e0a\uff0c\u53cc\u76ee\u7d27\u95ed\uff0c\u8138\u8272\u82cd\u767d\uff0c\u7709\u5b87\u95f4\u9690\u9690\u6cdb\u5347\u8d77\u4e00\u7247\u9ed1\u6c14\u3002 \u3000\u3000\u7cbe\u6e5b\u7684\u5185\u529f\uff0c\u4f3c\u662f\u4e5f\u65e0\u6cd5\u538b\u5236\u4f4f\u8eab\u4e2d\u7684\u5947\u6bd2\uff0c\u6c5f\u67ab\u662f\u771f\u7684\u53d7\u4e86\u91cd\u4f24\u3002 \u3000\u3000\u4ed6\u4f3c\u5df2\u795e\u667a\u6655\u8ff7\uff0c\u8fde\u8bdd\u4e5f\u4e0d\u4f1a\u8bf4\u4e86\u3002 \u3000\u3000\u6885\u82b1\u5750\u5728\u5e8a\u8fb9\u53d1\u5446\uff0c\u7f8e\u76ee\u4e2d\u6cea\u6c34\u6eda\u6d47\uff0c\u4f46\u5374\u6ca1\u6709\u53d1\u51fa\u4efb\u4f55\u58f0\u606f\u3002 \u3000\u3000\u65e0\u58f0\u4e4b\u6ce3\uff0c\u662f\u6700\u4f24\u5fc3\u7684\u773c\u6cea\u3002 \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u7ad9\u5728\u5e8a\u8fb9\uff0c\u4e0d\u505c\u7684\u6413\u7740\u53cc\u624b\u3002 \u3000\u3000\u8fd9\u4f4d\u9605\u5386\u4e30\u5bcc\uff0c\u5386\u7ecf\u8fc7\u4e8e\u96be\u4e07\u9669\u7684\u5927\u548c\u5c1a\uff0c\u4f3c\u662f\u4e5f\u667a\u7a77\u529b\u7aed\uff0c\u65e0\u6cd5\u6551\u6cbb\u8fd9\u79cd\u6bd2\u4f24\u3002 \u3000\u3000\u4f46\u5927\u548c\u5c1a\u4ecd\u7136\u4f5c\u4e86\u7f1c\u5bc6\u7684\u9632\u5b88\u5e03\u7f72\u3002 \u3000\u3000\u80e1\u840d\u3001\u6bb5\u4e5d\u3001\u90ed\u5929\u540c\u3001\u80e1\u5143\uff0c\u5b88\u5728\u95e8\u5916\u3002 \u3000\u3000\u9093\u98de\u548c\u5929\u9a6c\u5802\u7684\u4eba\uff0c\u4e5f\u5e03\u5b88\u5728\u56db\u5468\u3002 \u3000\u3000\u6b27\u9633\u662d\u548c\u795e\u4e10\u94b1\u7f3a\uff0c\u4e5f\u5e26\u7740\u4e00\u6279\u4eba\u5b88\u5728\u6d1b\u9633\u5c45\u5916\uff0c\u5927\u548c\u5c1a\u53ea\u8981\u4e00\u58f0\u6697\u8bed\u62db\u547c\uff0c\u7acb\u523b\u53ef\u4ee5\u51b2\u6740\u8fdb\u6765\u63a5\u5e94\u3002 \u3000\u3000\u539f\u672c\u662f\u4e09\u80a1\u52bf\u529b\u5408\u6210\u4e00\u4f53\u7684\u6d1b\u9633\u5c45\uff0c\u6b64\u523b\uff0c\u5df2\u5448\u73b0\u4e86\u660e\u663e\u7684\u5206\u88c2\uff0c\u4ee5\u6d1b\u9633\u697c\u9752\u51e4\u4e3a\u4e3b\u7684\u4e00\u80a1\u52bf\u529b\uff0c\u4f3c\u4ecd\u4fdd\u6301\u7740\u4e2d\u7acb\u4e4b\u5916\u3002 \u3000\u3000\u5185\u5bab\u548c\u5929\u9a6c\u5802\u4e2d\u4eba\uff0c\u5df2\u7136\u4e92\u6210\u654c\u5bf9\u7684\u5c40\u9762\uff0c\u4e0d\u4f46\u4e92\u76f8\u6212\u5907\uff0c\u800c\u5df2\u5448\u5251\u62d4\u5f29\u5f20\uff0c\u5c31\u5dee\u62d4\u5200\u5b50\u62fc\u6740\u4e86\u3002 \u3000\u3000\u541f\u96ea\u3001\u541f\u971c\u6276\u7740\u7f57\u5170\u56de\u5230\u623f\u4e2d\u8c03\u606f\uff0c\u5f20\u56db\u59d1\u5374\u8f6c\u5230\u4e86\u6c5f\u67ab\u7684\u75c5\u69bb\u524d\u9762\u3002 \u3000\u3000\u6885\u82b1\u50cf\u89c1\u5230\u4e86\u6551\u661f\u4e00\u822c\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5f20\u59e8\uff0c\u4ed6\u56de\u6765\u4e4b\u540e\uff0c\u5c31\u6ca1\u6709\u6e05\u9192\u8fc7\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u593a\u9b42\u795e\u9488\u672c\u5c31\u662f\u5929\u4e0b\u81f3\u6bd2\u7684\u6697\u5668\uff0c\u4ed6\u4e00\u4e0b\u4e2d\u4e86\u4e24\u6839\uff0c\u4e5f\u771f\u591f\u4ed6\u53d7\u7684\u4e86\u3002\u201d \u3000\u3000\u201c\u5f20\u59d1\u5a18\u2026\u2026\u201d\u5927\u548c\u5c1a\u6ca1\u6709\u6d41\u6cea\uff0c\u4f46\u90a3\u80a1\u7126\u6025\u60b2\u82e6\u7684\u795e\u8272\uff0c\u770b\u4e0a\u53bb\uff0c\u6bd4\u54ed\u8fd8\u53eb\u4eba\u96be\u8fc7\uff0c\u9aa4\u7136\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6c5f\u8001\u5f1f\uff0c\u8fd8\u6709\u6ca1\u6709\u6551\uff1f\u201d \u3000\u3000\u201c\u4f60\u8bf4\u5462\uff1f\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u4f60\u4fee\u70bc\u7684\u4f5b\u95e8\u6b63\u5b97\u5fc3\u6cd5\uff0c\u80fd\u4e3a\u4ed6\u5c3d\u70b9\u4ec0\u4e48\u5fc3\u529b\uff1f\u201d \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u88ab\u95ee\u5f97\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u548c\u5c1a\u53ef\u4ee5\u4e3a\u4ed6\u6b7b\uff0c\u4f5b\u5bb6\u85aa\u706b\u76f8\u4f20\u4e4b\u672f\uff0c\u548c\u5c1a\u53ef\u4ee5\u4e00\u8eab\u529f\u529b\u8f6c\u5ac1\u4e8e\u4ed6\u3002\u201d \u3000\u3000\u201c\u90a3\u5012\u4e0d\u7528\u4e86\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u4ed6\u5185\u529f\u5df2\u76f8\u5f53\u6df1\u539a\uff0c\u73b0\u5728\u6700\u91cd\u8981\u7684\u662f\u89e3\u53bb\u4ed6\u8eab\u4e2d\u4e4b\u6bd2\uff01\u201d \u3000\u3000\u201c\u8fd9\u4e2a\uff0c\u548c\u5c1a\u5c31\u4e0d\u77e5\u9053\u8be5\u5982\u4f55\u4e0b\u624b\u4e86\uff0c\u5509\uff01\u771f\u662f\u8001\u864e\u5403\u523a\u732c\uff0c\u627e\u4e0d\u5230\u4e0b\u53e3\u7684\u5730\u65b9\u3002\u201d \u3000\u3000\u4ed6\u4e00\u5411\u6c89\u7740\u3001\u673a\u8b66\uff0c\u51b7\u773c\u65c1\u89c2\u4e8b\u7269\u53d8\u5316\uff0c\u5e38\u80fd\u8bed\u542b\u7985\u673a\uff0c\u4e00\u8bed\u649e\u7834\u4eba\u5fc3\u4e2d\u7591\u8651\uff01\u4f46\u6b64\u523b\uff0c\u5374\u52bf\u5982\u9505\u4e0a\u7684\u8682\u8681\uff0c\u4e0d\u505c\u8d70\u52a8\u7740\uff0c\u6ca1\u6709\u7247\u523b\u5b89\u9759\u3002 \u3000\u3000\u5f53\u771f\u662f\u4e8b\u4e0d\u5173\u5df1\uff0c\u5173\u5df1\u5219\u4e71\u3002 \u3000\u3000\u201c\u5927\u548c\u5c1a\uff0c\u4f60\u5982\u6b64\u7684\u7126\u8651\u4e0d\u5b89\uff0c\u96be\u9053\u771f\u7684\u53ea\u662f\u62c5\u5fc3\u6c5f\u67ab\u7684\u751f\u6b7b\u5417\uff1f\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a \u3000\u3000\u201c\u6885\u82b1\u4f24\u5fc3\u5343\u56de\uff01\u96ea\u3001\u971c\u4e8c\u5973\uff0c\u4e5f\u4f1a\u809d\u80a0\u75db\u65ad\uff0c\u4f46\u4ed6\u4eec\u662f\u4e24\u5fc3\u76f8\u8bb8\uff0c\u7ec8\u8eab\u76f8\u4f9d\u7684\u60c5\u4fa3\uff0c\u4f60\u548c\u5c1a\u4e3a\u4ec0\u4e48\u8fd9\u6837\u7684\u7126\u8651\u5462\uff1f\u5c31\u662f\u5144\u5f1f\u60c5\u4e49\uff0c\u624b\u8db3\u76f8\u8fde\uff0c\u4e5f\u4e0d\u8be5\u5982\u6b64\u4e00\u4e2a\u7126\u8651\u6cd5\u5440\uff01\u201d \u3000\u3000\u201c\u5f20\u59d1\u5a18\u3002\u201d\u5927\u548c\u5c1a\u9053\uff1a\u201c\u548c\u5c1a\u5df2\u8df3\u51fa\u4e09\u754c\u5916\uff0c\u4e0d\u5728\u4e94\u884c\u4e2d\uff0c\u65e9\u5e94\u8be5\u6446\u8131\u4e86\u4eba\u95f4\u7684\u60b2\u82e6\u3001\u7231\u6b32\uff0c\u4f46\u6c5f\u67ab\u7684\u60c5\u51b5\u4e0d\u540c\u2026\u2026\u201d \u3000\u3000\u201c\u6709\u4ec0\u4e48\u4e0d\u540c\u5462\uff1f\u4ed6\u6b7b\u4e86\uff0c\u53ea\u4e0d\u8fc7\u4eba\u95f4\u5c11\u4e00\u4e2a\u98ce\u6d41\u79cd\u5b50\uff0c\u9634\u95f4\u591a\u4e00\u4eca\u5e74\u8f7b\u7684\u9b3c\u9b42\u2026\u2026\u201d \u3000\u3000\u5f20\u56db\u59d1\u9053\uff1a\u201c\u5927\u548c\u5c1a\uff0c\u4ee5\u6c5f\u67ab\u7684\u79cd\u79cd\u4f5c\u4e3a\uff0c\u4f60\u603b\u4e0d\u4f1a\u5bc4\u671b\u4ed6\u80fd\u5230\u897f\u65b9\u6781\u4e50\u9759\u571f\u5427\uff01\u201d \u3000\u3000\u201c\u5f20\u59d1\u5a18\u548c\u6211\u8c08\u7985\u4e86\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u4e00\u6574\u8138\u8272\uff0c\u9053\uff1a\u201c\u6211\u4f5b\u6e21\u4e16\uff0c\u5e0c\u671b\u4eba\u95f4\u4e07\u5584\u5e76\u81fb\uff0c\u53ea\u53ef\u60dc\uff0c\u9053\u957f\u4e00\u5c3a\uff0c\u9b54\u957f\u4e00\u4e08\uff0c\u81f4\u4eba\u95f4\u9b3c\u9b54\u6a2a\u884c\uff0c\u6c5f\u6e56\u4e0a\u5c24\u5176\u5982\u662f\uff0c\u6c5f\u67ab\u662f\u9b3c\u4e2d\u4e4b\u9b3c\uff0c\u9b54\u4e2d\u4e4b\u9b54\uff0c\u4f46\u4ed6\u5374\u5fc3\u4e2d\u5e38\u6709\u4e00\u70b9\u7075\u706b\u4e4b\u5149\uff0c\u90a3\u662f\u6c5f\u6e56\u4e0a\u7684\u5e0c\u671b\u6240\u5bc4\uff0c\u5982\u82e5\u8fd9\u4e00\u70b9\u7075\u5149\u51cf\u53bb\uff0c\u767e\u5e74\u4e4b\u5185\uff0c\u6b66\u6797\u9053\u4e0a\uff0c\u5c06\u6c89\u6ca1\u4e8e\u9ed1\u6697\u4e4b\u4e2d\uff0c\u5f71\u54cd\u6240\u53ca\uff0c\u90a3\u5c31\u4f24\u5bb3\u5230\u4e07\u6c11\u82cd\u751f\u4e86\uff0c\u6c5f\u6e56\u4eba\u65e0\u6cd5\u65e0\u5929\uff0c\u7387\u6027\u800c\u884c\uff0c\u4e88\u53d6\u4e88\u6c42\uff0c\u5929\u4e0b\u4e07\u6c11\uff0c\u53ea\u6709\u4efb\u5176\u5bb0\u5272\u4e86\uff01 \u3000\u3000\u5f20\u59d1\u5a18\uff0c\u9053\u7edf\u4e2d\u65ad\uff0c\u7075\u706b\u7184\u53bb\uff0c\u60f3\u60f3\u770b\uff0c\u8981\u4f55\u5e74\u4f55\u6708\uff0c\u624d\u4f1a\u518d\u51fa\u4e00\u4e2a\u6c5f\u67ab\uff0c\u5c31\u7b97\u6c5f\u5c71\u4ee3\u6709\u624d\u4eba\u51fa\uff0c\u53c8\u5f80\u54ea\u91cc\u53bb\u627e\u65e0\u76f8\u5927\u5e08\u3001\u5929\u72d0\u592b\u4eba\u3001\u91d1\u4e39\u4e66\u751f\u8fd9\u6837\u4e09\u4f4d\u826f\u5e08\u5462\uff1f \u3000\u3000\u5f20\u59d1\u5a18\u5fc3\u4e2d\u660e\u767d\uff0c\u5929\u72d0\u592b\u4eba\u548c\u91d1\u4e39\u4e66\u751f\uff0c\u90fd\u662f\u7531\u90aa\u5f52\u6b63\u7684\u9ad8\u624b\uff0c\u548c\u7fa4\u9b54\u5468\u65cb\u3002 \u3000\u3000\u5728\u5343\u767e\u6b21\u7684\u5931\u8d25\u4e4b\u540e\uff0c\u7fa4\u9b54\u4e5f\u5b66\u4f1a\u4e86\uff0c\u4e00\u6280\u4e0d\u53ef\u6043\u7684\u7ecf\u9a8c\uff0c\u4ed6\u4eec\u4e5f\u96c6\u805a\u4e86\u5929\u4e0b\u9b54\u529f\u5927\u6210\uff0c\u5408\u800c\u4e3a\u4e00\u3002 \u3000\u3000\u4ed6\u4eec\u4e5f\u8bb8\u53ea\u60f3\u706d\u7edd\u5404\u5927\u95e8\u6d3e\uff0c\u4ee5\u96ea\u5343\u5e74\u6765\u5c61\u906d\u8d25\u4ea1\u4e4b\u803b\uff0c\u5374\u4e0d\u77e5\u6d41\u6bd2\u6240\u53ca\uff0c\u6b8b\u5bb3\u4e86\u5343\u4e07\u65e0\u8f9c\u82cd\u751f\u3002\u201d \u3000\u3000\u201c\u5927\u548c\u5c1a\uff0c\u5931\u656c\u4e86\uff01\u201d\u5f20\u56db\u59d1\u6df1\u6df1\u4e00\u63d6\uff0c\u9053\uff1a\u201c\u4f60\u624d\u662f\u80f8\u6000\u5927\u6211\uff0c\u53ef\u4f69\u53ef\u656c\uff01\u201d \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u63a5\u9053\uff1a\u201c\u5f20\u59d1\u5a18\uff0c\u5982\u662f\u65e0\u6cd5\u6551\u6d3b\u6c5f\u67ab\uff0c\u4e5f\u8981\u4ed6\u6e05\u9192\u4e00\u9635\u2026\u2026\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u5f20\u56db\u59d1\u5947\u9053\uff1a\u201c\u4ed6\u6e05\u9192\u4e00\u523b\uff0c\u5bf9\u5927\u5c40\u53c8\u6709\u4f55\u5e2e\u52a9\uff1f\u201d \u3000\u3000\u201c\u6211\u76f8\u4fe1\u4e09\u4f4d\u8001\u4eba\u5bb6\u5fc5\u6709\u540e\u5907\u7684\u8ba1\u5212\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u9053\uff1a\u201c\u8fd9\u4e2a\u8ba1\u5212\uff0c\u4e5f\u53ef\u80fd\u53ea\u6709\u6c5f\u67ab\u77e5\u9053\uff0c\u8ba9\u4ed6\u6e05\u9192\u4e00\u523b\uff0c\u8bf4\u51fa\u6765\u2026\u2026\u201d \u3000\u3000\u201c\u5c31\u7b97\u786e\u6709\u5176\u4e8b\uff0c\u4f46\u4e5f\u8981\u4e8c\u5341\u5e74\u4ee5\u540e\u624d\u80fd\u518d\u8bad\u7ec3\u51fa\u4e00\u4e2a\u6c5f\u67ab\uff0c\u6d3e\u4e0a\u7528\u573a\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u5927\u548c\u5c1a\uff0c\u6765\u5f97\u53ca\u5417\uff1f\u90a3\u65f6\u5019\uff0c\u53ef\u80fd\u5df2\u6ca1\u6709\u4e5d\u5927\u95e8\u6d3e\uff0c\u4e5f\u6ca1\u6709\u6b66\u6797\u95e8\u6237\u4e4b\u5206\u4e86\u3002\u201d \u3000\u3000\u201c\u4e0d\u9519\uff01\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u4f46\u6709\u603b\u6bd4\u6ca1\u6709\u597d\uff0c\u6211\u4eec\u4f20\u51fa\u85aa\u706b\uff0c\u53ea\u6c42\u5c3d\u5fc3\uff0c\u80fd\u6709\u591a\u5927\u4f5c\u7528\uff0c\u90a3\u5c31\u4e0d\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u201c\u548c\u5c1a\u4f2f\u4f2f\uff0c\u4e0d\u8981\u7070\u5fc3\uff0c\u9b54\u9053\u4e2d\u4eba\uff0c\u8d25\u4ea1\u6570\u5341\u767e\u6b21\uff0c\u4f46\u9b54\u706b\u4e0d\u7184\uff0c\u9020\u6210\u4eca\u5929\u5927\u52ab\uff0c\u6c5f\u5927\u54e5\u662f\u6b66\u6797\u5e0c\u671b\u6240\u5bc4\uff0c\u6211\u4fdd\u8bc1\u4ed6\u4e00\u5b9a\u4e0d\u4f1a\u6b7b\u3002\u201d \u3000\u3000\u8f6c\u5934\u770b\u53bb\uff0c\u541f\u96ea\u3001\u541f\u971c\u5e76\u80a9\u800c\u7acb\uff0c\u7ad9\u5728\u75c5\u69bb\u4e0d\u8fdc\u5904\u3002 \u3000\u3000\u201c\u4e8c\u4e2b\u5934\uff0c\u4f60\u597d\u5927\u7684\u53e3\u6c14\uff0c\u201d\u5f20\u56fd\u59d1\u9053\uff1a\u201c\u4f60\u6562\u4fdd\u8bc1\u4ed6\u4e0d\u4f1a\u6b7b\uff1f\u201d \u3000\u3000\u201c\u5f20\u59e8\uff0c\u522b\u518d\u5413\u548c\u5c1a\u4f2f\u4f2f\u4e86\uff0c\u4e5f\u522b\u5bb3\u6885\u82b1\u59d0\u59d0\u4f24\u5fc3\u3002\u2026\u2026\u2026\u201d\u53f6\u96ea\u63a5\u9053\uff1a\u201c\u518d\u8bf4\uff0c\u65f6\u95f4\u8d8a\u4e45\uff0c\u4e2d\u6bd2\u8d8a\u6df1\uff0c\u4e0d\u662f\u5bb3\u6c5f\u5927\u54e5\u591a\u53d7\u7f6a\u5417\uff1f\u201d \u3000\u3000\u5f20\u56db\u59d1\u67e5\u770b\u4e86\u6c5f\u67ab\u7684\u4f24\u52bf\u60c5\u5f62\uff0c\u9053\uff1a\u201c\u8981\u4e24\u4e2a\u65f6\u8fb0\u624d\u80fd\u52a8\u624b\u6551\u4ed6\u3002\u201d \u3000\u3000\u201c\u8fd9\u5012\u662f\u4ece\u672a\u542c\u8fc7\u7684\u4e8b\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u9053\uff1a\u201c\u6551\u4f24\u6cbb\u75c5\uff0c\u8d8a\u65e9\u8d8a\u597d\uff0c\u4e3a\u4ec0\u4e48\u8981\u62d6\u5ef6\u65f6\u95f4\uff0c\u8ba9\u6bd2\u6027\u6df1\u5165\uff0c\u624d\u8981\u52a8\u624b\uff1f\u201d \u3000\u3000\u201c\u5bf9\u4e00\u822c\u7684\u4f24\u8005\uff0c\u786e\u662f\u5982\u6b64\uff0c\u4f46\u6c5f\u67ab\u4e0d\u540c\uff0c\u4ed6\u529f\u529b\u6df1\u539a\uff0c\u91cd\u4f24\u4e4b\u540e\uff0c\u628a\u4e00\u80a1\u771f\u6c14\u96c6\u4e2d\u62a4\u4f4f\u4e86\u5fc3\u810f\uff0c\u529f\u529b\u672a\u5931\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u4f46\u795e\u667a\u5df2\u7136\u4e0d\u6e05\uff0c\u6211\u8fd9\u9897\u5927\u8fd8\u4e39\uff0c\u836f\u529b\u53c8\u6781\u5f3a\u731b\uff0c\u4e00\u4e0b\u5b50\u4f7f\u4ed6\u786c\u9192\u8fc7\u6765\uff0c\u4ed6\u7a81\u7136\u51fa\u624b\u53cd\u51fb\uff0c\u6211\u4eec\u8981\u5982\u4f55\u5bf9\u4ed8\uff1f\u201d \u3000\u3000\u201c\u90a3\u5bb9\u6613\u5440\uff01\u2026\u2026\u201d\u6885\u82b1\u9053\uff1a\u201c\u6211\u4eec\u5148\u70b9\u4f4f\u4ed6\u7684\u7a74\u9053\u3002\u2026\u2026\u201d \u3000\u3000\u201c\u4f60\u592a\u4f4e\u4f30\u4f60\u7684\u6c5f\u54e5\u54e5\u4e86\uff0c\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u4e09\u4f4d\u8001\u4eba\u5bb6\u8d39\u5c3d\u5fc3\u8840\u57f9\u517b\u4ed6\uff0c\u4f7f\u4ed6\u5177\u6709\u548c\u5929\u4e0b\u9b54\u9053\u9ad8\u624b\u5468\u65cb\u7684\u80fd\u529b\uff0c\u4ed6\u6709\u591a\u9ad8\u7684\u6b66\u529f\uff0c\u4e0d\u4f46\u6211\u4eec\u4e0d\u80fd\u6d4b\u5ea6\u8fde\u4ed6\u81ea\u5df1\u4e5f\u4e0d\u662f\u5f88\u6e05\u695a\uff0c\u8fd9\u5c0f\u4e8e\u53c8\u5f88\u4f1a\u88c5\u4f5c\uff0c\u548c\u4eba\u52a8\u624b\u65f6\uff0c\u53ea\u6c42\u52c9\u5f3a\u80dc\u4eba\u4e00\u62db\uff0c\u597d\u50cf\u4ed6\u5df2\u5c3d\u51fa\u5168\u529b\uff0c\u4e0d\u8ba9\u4eba\u5bb6\u4f30\u900f\u4ed6\uff0c\u4f46\u5728\u795e\u667a\u4e0d\u6e05\u65f6\uff0c\u51fa\u624b\u4e00\u51fb\uff0c\u5fc5\u662f\u5168\u529b\u65bd\u4e3a\uff0c\u90a3\u5c31\u4e0d\u77e5\u9053\u662f\u4ec0\u4e48\u602a\u62db\u4e86\uff0c\u6211\u4eec\u53ea\u6015\u8c01\u4e5f\u627f\u53d7\u4e0d\u4f4f\uff0c\u4e07\u4e00\u628a\u4f60\u4eec\u6253\u6b7b\u4e00\u4e2a\uff0c\u90a3\u5c31\u9ebb\u70e6\u5927\u4e86\u3002\u201d \u3000\u3000\u541f\u971c\u4f38\u4f38\u820c\u5934\uff0c\u9053\uff1a\u201c\u5f20\u59e8\uff0c\u771f\u6709\u90a3\u4e48\u5389\u5bb3\u5417\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u7b97\u4e0a\u5927\u548c\u5c1a\uff0c\u6211\u4eec\u8fd9\u4e9b\u4eba\uff0c\u6ca1\u6709\u4e00\u4e2a\u80fd\u63a5\u4e0b\u5730\u5168\u529b\u4e00\u51fb\uff0c\u9b45\u65b9\u5999\u5c31\u662f\u4e2a\u4f8b\u5b50\uff0c\u4ed6\u5728\u4e2d\u4e86\u593a\u9b42\u6bd2\u9488\u4e4b\u540e\uff0c\u4e00\u638c\u9707\u788e\u4e86\u65b9\u5999\u7684\u4e94\u85cf\u516d\u8151\uff0c\u5176\u5b9e\uff0c\u770b\u65b9\u5999\u5012\u4e0b\u53bb\u7684\u60c5\u5f62\uff0c\u6709\u5982\u4e00\u6ee9\u70c2\u6ce5\uff0c\u6211\u770b\u5168\u8eab\u7ecf\u8109\uff0c\u6709\u53ef\u80fd\u88ab\u4ed6\u4e00\u638c\u9707\u65ad\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u5401\u4e00\u53e3\u6c14\uff0c\u63a5\u9053\uff1a\u201c\u4f60\u4eec\u8981\u660e\u767d\uff0c\u90a3\u65f6\u4faf\uff0c\u65b9\u5999\u7d27\u7d27\u7684\u62b1\u4f4f\u4f60\u4eec\u6c5f\u5927\u54e5\uff0c\u4e24\u4e2a\u8eab\u4e8e\u8d34\u5728\u4e00\u5904\uff0c\u90a3\u4e00\u638c\u7684\u529b\u9053\uff0c\u5341\u6210\u53ea\u80fd\u53d1\u51fa\u56db\u6210\u5a01\u529b\uff0c\u4f46\u65b9\u5999\u5168\u8eab\u7ecf\u808c\u5185\u8151\uff0c\u90fd\u88ab\u9707\u65ad\u9707\u788e\u4e86\u3002\u201d \u3000\u3000\u60f3\u4e00\u60f3\uff0c\u90fd\u4e0d\u7981\u5fc3\u751f\u5bd2\u610f\uff0c\u679c\u7136\u662f\u8c01\u4e5f\u627f\u53d7\u4e0d\u4f4f\u6c5f\u67ab\u5168\u529b\u4e00\u51fb\u3002 \u3000\u3000\u201c\u5509\uff01\u5f20\u59d1\u5a18\uff0c\u5927\u548c\u5c1a\u7518\u62dc\u4e0b\u98ce\uff0c\u6211\u53bb\u95e8\u5916\u5b88\u7740\uff0c\u4f60\u4eec\u597d\u597d\u7684\u7ed9\u4ed6\u7597\u4f24\u5427\u3002\u201d\u6025\u6025\u8f6c\u8eab\u51fa\u53bb\u3002 \u3000\u3000\u5f20\u56db\u59d1\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4ed6\u603b\u7b97\u660e\u767d\u4e86\uff0c\u73b0\u5728\uff0c\u54b1\u4eec\u52a8\u624b\u5427\uff01\u201d \u3000\u3000\u201c\u5f20\u59e8\uff01\u6211\u4e0d\u61c2\u4e86\uff0c\u4e3a\u4ec0\u4e48\u548c\u5c1a\u4f2f\u4f2f\u5728\u8fd9\u91cc\u5c31\u4e0d\u80fd\u52a8\u624b\u7597\u4f24\u5462\uff1f\u201d\u541f\u971c\u4e00\u8138\u4e0d\u89e3\u7684\u95ee\u3002 \u3000\u3000\u5f20\u56db\u59d1\u9053\uff1a\u201c\u8131\u5149\u4f60\u6c5f\u5927\u54e5\u8eab\u4e0a\u7684\u8863\u670d\uff0c\u5927\u548c\u5c1a\u7ad9\u5728\u8fd9\u91cc\uff0c\u4f60\u6562\u4e0d\u6562\uff1f\u201d \u3000\u3000\u541f\u971c\u4e00\u4f38\u820c\u5934\uff0c\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\uff01\u201d \u3000\u3000\u5f20\u56db\u59d1\u9053\uff1a\u201c\u5f53\u7136\uff0c\u521a\u624d\u5f20\u59e8\u8bf4\u7684\u8bdd\u4e5f\u662f\u771f\u662f\uff0c\u4ed6\u5185\u529f\u592a\u6df1\uff0c\u672c\u80fd\u53cd\u5e94\u5f88\u5feb\uff0c\u6211\u4eec\u4e5f\u8981\u5c0f\u5fc3\u4e00\u4e9b\uff0c\u4e8c\u4e2b\u5934\u7684\u8bdd\u6700\u591a\uff0c\u5c31\u7f5a\u4f60\u8131\u4ed6\u8863\u670d\u3002\u201d \u3000\u3000\u201c\u8131\u5c31\u8131\uff0c\u8fd9\u4e00\u6b21\u6211\u5012\u8981\u4ed4\u7ec6\u7684\u77a7\u77a7\u7740\uff0c\u4ed6\u8eab\u4e0a\u6709\u4ec0\u4e48\u75a4\u75d5\uff01\u201d \u3000\u3000\u6885\u82b1\u4f4e\u58f0\u9053\uff1a\u201c\u541f\u971c\u59b9\u5b50\uff0c\u6211\u5e2e\u4f60\u2026\u2026\u201d \u3000\u3000\u541f\u971c\u7b11\u9053\uff1a\u201c\u597d\u554a\uff01\u59d0\uff0c\u6700\u597d\u4f60\u4e5f\u6765\uff0c\u4e09\u4e2a\u4eba\u52a8\u4f5c\u5feb\u4e00\u4e9b\u3002\u201d \u3000\u3000\u4e09\u4e2a\u4eba\u4e00\u9f50\u52a8\u624b\uff0c\u8fd9\u5c31\u5206\u51fa\u9ad8\u4e0b\u4e86\u3002 \u3000\u3000\u770b\u4e0a\u53bb\uff0c\u541f\u96ea\u3001\u541f\u971c\u624b\u811a\u867d\u5feb\uff0c\u6548\u679c\u5374\u4e0d\u89c1\u5f88\u597d\u3002 \u3000\u3000\u6885\u82b1\u6162\u6761\u65af\u7406\uff0c\u5374\u662f\u624b\u6cd5\u7eaf\u719f\uff0c\u624b\u5230\u5904\u6263\u5f00\u3001\u5e26\u89e3\u3002 \u3000\u3000\u5f88\u5feb\u6252\u5149\u4e86\u6c5f\u67ab\u7684\u8863\u670d\u3002 \u3000\u3000\u770b\u5230\u6c5f\u67ab\u524d\u80f8\u5904\u5df3\u6cdb\u51fa\u9ed1\u6c14\uff0c\u4e09\u5973\u4e5f\u6ca1\u6709\u5fc3\u60c5\u8bf4\u7b11\u4e86\u3002 \u3000\u3000\u201c\u628a\u4ed6\u7ffb\u8fc7\u6765\uff01\u201d\u5f20\u56db\u59d1\u4f4e\u58f0\u5429\u5490\u3002 \u3000\u3000\u7ffb\u8fc7\u6c5f\u67ab\u7684\u8eab\u5b50\uff0c\u53ea\u89c1\u540e\u8170\u4e24\u5757\u80bf\u8d77\u7684\u808c\u80a4\uff0c\u5df2\u53d8\u6210\u4e86\u7d2b\u9ed1\u4e4b\u8272\u3002 \u3000\u3000\u201c\u5f20\u59e8\uff01\u77a7\u4e0d\u5230\u9488\u5728\u54ea\u91cc\u5440\uff01\u2026\u2026\u201d\u541f\u96ea\u9053\uff1a\u201c\u8981\u4e0d\u8981\u5148\u628a\u6bd2\u9488\u8d77\u51fa\u6765\uff1f\u201d \u3000\u3000\u201c\u8981\uff01\u4e24\u6839\u6bd2\u9488\uff0c\u63d2\u5728\u808c\u80a4\u4e2d\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u4efb\u4f55\u7684\u7075\u836f\u4ed9\u4e39\uff0c\u4e5f\u6ca1\u6709\u6cd5\u5b50\u6cbb\u597d\u4ed6\u7684\u4f24\u52bf\u3002\u201d \u3000\u3000\u201c\u600e\u4e48\u53d6\u51fa\u6765\u5462\uff1f\u77a7\u4e0d\u5230\u554a\uff01\u201d\u541f\u971c\u95ee\u3002 \u3000\u3000\u201c\u53bb\u53d6\u4e00\u74f6\u70c8\u9152\u2026\u2026\u201d\u5f20\u53e3\u59d1\u7531\u8eab\u4e0a\u53d6\u51fa\u4e00\u628a\u5c0f\u5200\u5b50\u3002 \u3000\u3000\u541f\u971c\u62ff\u5230\u9152\uff0c\u5f20\u56db\u59d1\u968f\u624b\u53d6\u8fc7\u672c\u6848\u4e0a\u4e00\u53ea\u8336\u676f\uff0c\u71c3\u8d77\u676f\u4e2d\u70c8\u9152\uff0c\u7acb\u523b\u95f4\u8d77\u4e86\u4e00\u7247\u84dd\u8272\u7684\u706b\u7130\u3002 \u3000\u3000\u5c0f\u5200\u7528\u706b\u9152\u6d17\u8fc7\uff0c\u5f20\u56db\u59d1\u7acb\u523b\u52a8\u624b\uff0c\u5207\u5f00\u4e86\u7d2b\u80bf\u4f24\u5904\uff0c\u5de6\u624b\u7528\u529b\u538b\u4e0b\uff0c\u9ed1\u8272\u7684\u8840\u6db2\u98d8\u5c04\u800c\u51fa\u3002 \u3000\u3000\u76f4\u5230\u9ed1\u8840\u6d41\u5c3d\uff0c\u5f20\u56db\u59d1\u624d\u53d6\u51fa\u4e00\u4e2a\u94f6\u5236\u7684\u5939\u5b50\uff0c\u518d\u7528\u706b\u9152\u6d17\u8fc7\uff0c\u5939\u51fa\u4e86\u4e00\u679a\u4e00\u5bf8\u591a\u957f\u7684\u94a2\u9488\u3002 \u3000\u3000\u5982\u6cd5\u6ce1\u5236\uff0c\u4e5f\u53d6\u51fa\u53e6\u4e00\u652f\u6bd2\u9488\u3002 \u3000\u3000\u541f\u96ea\u6068\u6068\u7684\u770b\u4e86\u4e24\u679a\u6bd2\u9488\u4e00\u773c\uff0c\u9053\uff1a\u201c\u6211\u53bb\u628a\u4e24\u679a\u6bd2\u5bf9\u6572\u788e\uff0c\u6295\u5165\u70ec\u706b\uff0c\u8981\u5b83\u5316\u6210\u788e\u5c51\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u8981\u52a8\uff0c\u2026\u2026\u201d\u5f20\u56db\u59d1\u5927\u58f0\u559d\u9053\uff1a\u201c\u9488\u4e0a\u5267\u6bd2\uff0c\u4e0d\u80fd\u6cbe\u624b\uff0c\u518d\u8bf4\uff0c\u6c34\u53ef\u8986\u821f\uff0c\u4ea6\u80fd\u8f7d\u821f\uff0c\u8fd9\u4e24\u679a\u6bd2\u4ec0\uff0c\u7528\u5343\u5e74\u5bd2\u94c1\u78e8\u5236\uff0c\u9510\u5229\u6d1e\u94c1\u7a7f\u77f3\uff0c\u80fd\u7834\u62a4\u8eab\u7f61\u6c14\uff0c\u9738\u9053\u7684\u5f88\uff0c\u4f55\u4e0d\u7559\u4e0b\u6765\u3002\u201d \u3000\u3000\u201c\u5bf9\uff01\u4ee5\u5b50\u4e4b\u77db\u653b\u5b50\u4e4b\u76fe\uff0c\u2026\u2026\u201d\u541f\u971c\u7b11\u9053\uff1a\u201c\u8981\u4ed6\u4eec\u4e5f\u5c1d\u5c1d\u5bd2\u94c1\u6bd2\u9488\u7684\u5389\u5bb3\uff01\u201d \u3000\u3000\u5f20\u56db\u59d1\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u770b\u6765\uff0c\u6211\u4eec\u90fd\u4e0d\u662f\u80fd\u4e0a\u5929\u5802\u7684\u4eba\uff0c\u5982\u6b64\u6bd2\u7269\uff0c\u7adf\u7136\u4e0d\u5fcd\u628a\u5b83\u6bc1\u53bb\u3002\u201d \u3000\u3000\u201c\u5f20\u59e8\u2026\u2026\u201d\u541f\u971c\u9053\uff1a\u201c\u6c5f\u5927\u54e5\u4e0b\u4e86\u5730\u72f1\uff0c\u6211\u4eec\u662f\u592b\u5531\u5987\u968f\uff0c\u4f60\u4e00\u4e2a\u4eba\u4e0a\u4e86\u5929\u5802\uff0c\u90a3\u8be5\u591a\u5bc2\u5bde\u5440\uff01\u201d \u3000\u3000\u5f20\u56db\u59d1\u67e5\u770b\u6c5f\u67ab\u4e24\u5904\u4f24\u53e3\u4e00\u773c\uff0c\u7b11\u9053\uff1a\u201c\u8bf4\u7684\u4e5f\u662f\u554a\uff01\u6211\u66ff\u4f60\u4eec\u8bbe\u8ba1\u4e00\u4e2a\u53d1\u5c04\u7684\u9488\u7b80\uff0c\u4ee5\u4e1d\u8fde\u8d77\uff0c\u8ba9\u5b83\u80fd\u53d1\u80fd\u6536\u3002\u201d \u3000\u3000\u201c\u592a\u597d\u4e86\uff0c\u2026\u2026\u201d\u4e8c\u5973\u7a81\u7136\u62b1\u4f4f\u4e86\u5f20\u56db\u59d1\uff0c\u5206\u5728\u5f20\u56db\u59d1\u53cc\u988a\u4e0a\u4eb2\u4e86\u4e00\u4e0b\uff0c\u63a5\u9053\uff1a\u201c\u6709\u8fd9\u6837\u4e00\u679a\u5bd2\u94c1\u6bd2\u9488\uff0c\u80fd\u900f\u91d1\u77f3\uff0c\u4f55\u7591\u662f\u9001\u6211\u4eec\u6bcf\u4eba\u4e00\u628a\u524a\u94c1\u5982\u6ce5\u7684\u5b9d\u5251\uff0c\u5f20\u59e8\u771f\u662f\u6211\u4eec\u7684\u597d\u5e08\u7236\u554a\uff01\u201d \u3000\u3000\u4e8c\u5973\u62b1\u7740\u5f20\u56db\u59d1\u6492\u5a07\uff0c\u773c\u4e2d\u7684\u6cea\u6c34\u53ef\u5df2\u593a\u7736\u800c\u51fa\u3002 \u3000\u3000\u771f\u60c5\u6d41\u9732\uff0c\u65e0\u9650\u5b7a\u6155\u3002 \u3000\u3000\u5f20\u56db\u59d1\u62b1\u7d27\u4e8c\u5973\uff0c\u773c\u775b\u4e2d\u4e5f\u6709\u4e9b\u6e7f\u6da6\uff0c\u5fc3\u4e2d\u7684\u611f\u53d7\uff0c\u66f4\u662f\u590d\u6742\u4e07\u7aef\uff0c\u96ea\u3001\u971c\u4e8c\u5973\uff0c\u5a07\u5a9a\u5982\u82b1\uff0c\u62dc\u5165\u95e8\u4e0b\u627f\u6b22\u819d\u524d\u3002 \u3000\u3000\u4e8c\u5973\u806a\u6167\u7edd\u4f26\uff0c\u5584\u89e3\u4eba\u610f\u7ed9\u4e86\u5979\u4e0d\u5c11\u7684\u6b22\u7b11\u5b89\u6170\uff0c\u4f46\u60f3\u5230\u4e86\u548c\u6c5f\u67ab\u4e00\u6bb5\u60c5\u7f18\uff0c\u5f88\u53ef\u80fd\u4e3a\u4e8c\u5973\u62d4\u51fa\u6167\u5251\uff0c\u2026\u2026\u6c5f\u6e56\u4e0a\u91cd\u89c1\u5149\u660e\u4e4b\u65e5\uff0c\u4e5f\u5c06\u662f\u5979\u907f\u60c5\u8fdc\u8d70\u4e4b\u65f6\u2026\u2026\u3002 \u3000\u3000\u5979\u5fc3\u4e2d\u5f88\u660e\u767d\uff0c\u65ad\u7edd\u548c\u6c5f\u67ab\u8fd9\u7f15\u60c5\u7f18\uff0c\u5fc5\u7136\u662f\u65e0\u6bd4\u7684\u75db\u82e6\uff0c\u80fd\u5426\u8010\u5f97\u4f4f\u90a3\u4efd\u76f8\u601d\uff0c\u5b9e\u65e0\u628a\u63e1\uff0c\u4e5f\u8bb8\u4f1a\u903c\u5230\u5979\u81ea\u520e\u65ad\u60c5\uff0c\u4e00\u6b7b\u4e86\u4e4b\u3002 \u3000\u3000\u5927\u548c\u5c1a\u8bc4\u8bba\u6c5f\u67ab\u662f\u9b3c\u4e2d\u4e4b\u9b3c\uff0c\u9b54\u4e2d\u4e4b\u9b54\uff0c\u4eba\u90aa\u5fc3\u6b63\uff0c\u5f53\u771f\u662f\u4e00\u9488\u89c1\u8840\u7684\u9ad8\u8bba\u3002 \u3000\u3000\u8f6c\u5934\u770b\u53bb\uff0c\u53ea\u89c1\u6885\u82b1\u4e89\u6267\u4e00\u4e2a\u6cbe\u8fc7\u706b\u9152\u7684\u68c9\u82b1\uff0c\u5168\u795e\u8d2f\u6ce8\uff0c\u5f88\u7ec6\u5fc3\u7684\u62ed\u7740\u6c5f\u67ab\u4f24\u53e3\u5904\u7684\u8840\u8ff9\u3002 \u3000\u3000\u8840\u8272\u9c9c\u7ea2\uff0c\u4f3c\u662f\u5df2\u6324\u5b8c\u4e86\u6bd2\u8840\u3002 \u3000\u3000\u8f7b\u8f7b\u63a8\u5f00\u4e8c\u5973\uff0c\u6536\u597d\u6bd2\u9488\uff0c\u5f20\u56db\u59d1\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u79fb\u5f00\u6728\u6848\uff0c\u62ff\u8d70\u5ba4\u4e2d\u7684\u5200\u5251\u5229\u5668\uff0c\u6211\u8981\u8ba9\u4ed6\u670d\u7528\u836f\u7269\u4e86\u2026\u2026\u201d \u3000\u3000\u53d6\u51fa\u4e00\u4e2a\u7389\u74f6\uff0c\u5012\u51fa\u4e86\u7c92\u8721\u5c01\u7684\u836f\u4e38\uff0c\u63a5\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u540d\u95fb\u5929\u4e0b\u7684\u592a\u8fd8\u4e39\uff0c\u636e\u8bf4\u4e16\u4e0a\u53ea\u6709\u4e09\u9897\uff0c\u53e6\u4e24\u9897\u4e0b\u843d\u4e0d\u660e\uff0c\u4e00\u9897\u5728\u6b64\u3002\u201d \u3000\u3000\u624b\u6307\u7528\u529b\uff0c\u634f\u788e\u4e86\u8721\u5c01\u3002 \u3000\u3000\u4e00\u80a1\u836f\u9999\uff0c\u6251\u9f3b\u800c\u81f3\u3002 \u3000\u3000\u541f\u96ea\u3001\u541f\u971c\u79fb\u5f00\u4e86\u672c\u6848\uff0c\u53d6\u8d70\u4e86\u5ba4\u4e2d\u7684\u5175\u5203\u5229\u5668\u3002 \u3000\u3000\u6885\u82b1\u5374\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u8981\u4e0d\u8981\u66ff\u4ed6\u5305\u624e\u4f24\u53e3\uff0c\u7a7f\u4e0a\u8863\u670d\u3002\u201d \u3000\u3000\u201c\u4e0d\u7528\u4e86\u2026\u2026\u201d\u5f20\u56db\u59d1\u7b11\u9053\uff1a\u201c\u4e5f\u8bb8\u836f\u529b\u4e0d\u80fd\u903c\u51fa\u4ed6\u8eab\u4e0a\u6b8b\u7559\u7684\u6bd2\u8840\uff0c\u8ba9\u4ed6\u5149\u7740\u8eab\u5b50\uff0c\u53ef\u80fd\u4f1a\u4f7f\u4ed6\u65e9\u4e9b\u6e05\u9192\uff0c\u4f60\u4eec\u90fd\u9000\u4e0b\u53bb\u5427\uff01\u201d \u3000\u3000\u201c\u6211\u7559\u5728\u8fd9\u91cc\u966a\u4f60\u2026\u2026\u201d\u6885\u82b1\u9053\uff1a\u201c\u6709\u4e8b\u5f1f\u5b50\u670d\u5176\u52b3\uff0c\u5f20\u59e8\u5982\u82e5\u4fe1\u5f97\u8fc7\u6211\uff0c\u5c31\u8ba9\u6211\u5582\u4ed6\u670d\u836f\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u5de6\u624b\u634f\u5f00\u4e86\u6c5f\u67ab\u7684\u7259\u5173\uff0c\u53f3\u624b\u6295\u5165\u5927\u8fd8\u4e39\uff0c\u4e00\u628a\u6293\u4f4f\u4e86\u6885\u82b1\uff0c\u8f6c\u8eab\u79bb\u53bb\uff0c\u5374\u89c1\u96ea\u3001\u971c\u4e8c\u5973\uff0c\u4e5f\u5728\u69bb\u8fb9\u7ad9\u7740\uff0c\u4e0d\u7981\u6012\u9053\uff1a\u201c\u5feb\u8d70\u3002\u201d \u3000\u3000\u56db\u4e2a\u4eba\u884c\u5230\u5ba4\u5916\u5385\u4e2d\u3001\u5f20\u56db\u59d1\u987a\u624b\u5e26\u4e0a\u4e86\u623f\u95e8\u3002 \u3000\u3000\u7f57\u5170\u6b63\u597d\u7531\u5916\u9762\u6b65\u5165\u5385\u4e2d\uff0c\u9053\uff1a\u201c\u6c5f\u67ab\u600e\u4e48\u6837\u4e86\uff1f\u201d \u3000\u3000\u201c\u5a18\uff01\u4e0d\u8981\u8fdb\u5367\u5ba4\u53bb\uff0c\u2026\u2026\u201d\u541f\u96ea\u62e6\u4f4f\u4e86\u6bcd\u4eb2\uff0c\u817c\u8146\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6c5f\u5927\u54e5\u521a\u670d\u8fc7\u836f\uff0c\u8ba9\u4ed6\u4f11\u606f\u4e00\u4e0b\u3002\u201d \u3000\u3000\u201c\u7f57\u5170\uff0c\u4f60\u7684\u5185\u529f\uff0c\u4f3c\u662f\u53c8\u7cbe\u8fdb\u4e0d\u5c11\uff0c\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u770b\u4f60\u529b\u63a5\u795e\u9b3c\u53cc\u5200\u540e\u7684\u60e8\u767d\u8138\u8272\uff0c\u4f3c\u662f\u5df2\u6c14\u529b\u8017\u5c3d\uff0c\u4f46\u8fd9\u4e00\u9635\u8c03\u606f\uff0c\u53c8\u4f3c\u5df2\u5b8c\u5168\u590d\u5143\u4e86\uff1f\u201d \u3000\u3000\u201c\u4e24\u4e2a\u8001\u8272\u9b3c\u679c\u7136\u662f\u5389\u5bb3\uff0c\u201d\u7f57\u5170\u5401\u4e00\u53e3\u6c14\u53c8\u9053\uff1a\u201c\u90a3\u53cc\u5200\u5408\u74a7\u4e00\u51fb\uff0c\u6709\u7740\u91cd\u5982\u6cf0\u5c71\u538b\u9876\u7684\u611f\u89c9\uff0c\u6211\u51dd\u805a\u7684\u771f\u6c14\uff0c\u4e5f\u88ab\u90a3\u4e00\u51fb\u7ed9\u9707\u6563\u4e86\uff0c\u5982\u679c\u4ed6\u4eec\u518d\u653b\u4e00\u5200\uff0c\u6211\u53ea\u6709\u4efb\u4eba\u5bb0\u5272\u7684\u4efd\u4e86\uff0c\u2026\u2026\u201d \u3000\u3000\u76ee\u5149\u8f6c\u6ce8\u5230\u541f\u96ea\u7684\u8138\u4e0a\uff0c\u51b7\u51b7\u63a5\u9053\uff1a\u201c\u5927\u4e2b\u5934\uff0c\u770b\u4f60\u7b11\u7684\u90a3\u4e48\u8e4a\u8df7\uff0c\u8001\u5a18\u504f\u4e0d\u53d7\u9a97\uff0c\u6211\u975e\u8981\u8fdb\u53bb\u770b\u770b\u4e0d\u53ef\u3002\u201d \u3000\u3000\u541f\u971c\u4e00\u628a\u6293\u4f4f\u6bcd\u4eb2\u9053\uff1a\u201c\u5a18\uff0c\u4e0d\u80fd\u770b\u554a\uff01\u59d0\u59d0\u90a3\u4e48\u8001\u5b9e\u7684\u4eba\uff0c\u9887\u4e3a\u4ec0\u4e48\u4e0d\u76f8\u4fe1\u5979\u5462\uff1f\u201d \u3000\u3000\u201c\u5bf9\uff01\u4f60\u59d0\u59d0\u8001\u5b9e\uff0c\u53ef\u662f\uff0c\u4e8c\u4e2b\u5934\uff0c\u4f60\u53ef\u662f\u5201\u86ee\u5f97\u5f88\uff0c\u2026\u2026\u201d\u7f57\u5170\u7728\u52a8\u4e86\u4e00\u4e0b\u5927\u773c\u775b\uff0c\u63a5\u9053\uff1a\u201c\u4f60\u8fd9\u4e48\u4e00\u63d2\u624b\uff0c\u5a18\u66f4\u662f\u975e\u770b\u4e0d\u53ef\u4e86\u3002\u201d \u3000\u3000\u201c\u5a18\uff0c\u771f\u7684\u4e0d\u80fd\u770b\u554a\uff01\u56e0\u4e3a\uff0c\u6c5f\u5927\u54e5\u2026\u2026\u201d \u3000\u3000\u6c5f\u5927\u54e5\u4e86\u534a\u5929\uff0c\u54e5\u4e0d\u51fa\u4e2a\u6240\u4ee5\u7136\u6765\u3002 \u3000\u3000\u6c5f\u5927\u54e5\u5149\u7740\u5c41\u80a1\uff0c\u541f\u96ea\u5b9e\u65e0\u6cd5\u5728\u5a18\u9762\u524d\u8bf4\u5f97\u51fa\u53e3\u6765\u3002 \u3000\u3000\u201c\u6c5f\u5927\u54e5\u600e\u4e48\u6837\u4e86\uff1f\u5927\u4e2b\u5934\u8bf4\u51fa\u6765\u5440\uff01\u201d \u3000\u3000\u7f57\u5170\u795e\u60c5\u95f4\u6d41\u73b0\u51fa\u65e0\u6bd4\u7684\u5173\u5207\u3002 \u3000\u3000\u201c\u7f57\u5170\uff0c\u4ed6\u5df2\u9192\u8fc7\u6765\u4e86\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u73b0\u5728\uff0c\u4ed6\u6b63\u5728\u8fd0\u529f\u7597\u606f\uff0c\u53d7\u4e0d\u5f97\u60ca\u5fe7\uff0c\u6240\u4ee5\uff0c\u6211\u4eec\u90fd\u9000\u5230\u4e86\u5385\u4e2d\u3002\u201d \u3000\u3000\u201c\u5662\uff01\u8fd9\u6709\u4ec0\u4e48\u4e0d\u80fd\u8bf4\u7684\uff1f\u8fd9\u4e24\u4e2a\u4e2b\u5934\uff0c\u7adf\u7136\u541e\u541e\u5410\u5410\u7684\u8bf4\u4e0d\u6e05\u695a\u2026\u2026\u201d \u3000\u3000\u4f46\u95fb\u84ec\u7136\u4e00\u58f0\u5927\u9707\uff0c\u6881\u67f1\u4e0a\u79ef\u5c18\u98d8\u843d\uff0c\u4f3c\u662f\u4e00\u67c4\u5de8\u6775\u5927\u9524\uff0c\u6572\u6253\u5728\u4e00\u9762\u5899\u58c1\u4e0a\uff0c\u9707\u5f97\u5927\u5385\u6643\u52a8\u3002 \u3000\u3000\u201c\u6c5f\u5927\u54e5\u2026\u2026\u201d \u3000\u3000\u541f\u971c\u5c16\u53eb\u4e00\u58f0\uff0c\u63a8\u95e8\u98de\u8dc3\u5165\u5ba4\u3002 \u3000\u3000\u4f46\u5979\u4ecd\u672a\u5fd8\u8bb0\u63a9\u4e0a\u53cc\u95e8\u3002 \u3000\u3000\u4f46\u5df2\u7ecf\u591f\u4e86\uff0c\u4ee5\u7f57\u5170\u8033\u76ee\u7684\u7075\u654f\uff0c\u5df2\u7136\u770b\u5230\u6c5f\u67ab\u7ad9\u5728\u6728\u69bb\u4e0a\uff1b\u4f3c\u662f\u5bf8\u7f15\u672a\u7740\u3002 \u3000\u3000\u5929\u72d0\u6280\u827a\uff0c\u4fdd\u4f4f\u4e86\u7f57\u5170\u7684\u9752\u6625\u7f8e\u4e3d\uff0c\u4f46\u5979\u5df2\u662f\u4e2d\u5e74\u5987\u4eba\uff0c\u8fd9\u65b9\u9762\u9605\u5386\uff0c\u81ea\u975e\u96ea\u3001\u971c\u4e8c\u5973\u80fd\u53ca\uff0c\u770b\u89c1\u88c5\u4f5c\u6ca1\u6709\u770b\u89c1\uff0c\u82e5\u65e0\u5176\u4e8b\u822c\uff0c\u7ad9\u5728\u95e8\u53e3\u4e0d\u52a8\u3002 \u3000\u3000\u541f\u971c\u6251\u5165\u623f\u4e2d\uff0c\u6c5f\u67ab\u4f3c\u5df2\u6e05\u9192\u8fc7\u6765\uff0c\u6025\u6025\u5367\u5012\u5728\u5e8a\uff0c\u62c9\u8fc7\u68c9\u88ab\uff0c\u76d6\u5728\u8eab\u4e0a\u3002 \u3000\u3000\u4f46\u4e24\u5904\u4f24\u53e3\uff0c\u649e\u5728\u4e86\u5e8a\u4e0a\uff0c\u9c9c\u8840\u6cc9\u6d8c\u800c\u51fa\uff0c\u4eba\u4e5f\u75bc\u5f97\u4e00\u88c2\u5634\u5df4\uff01 \u3000\u3000\u201c\u6015\u4ec0\u4e48\u5417\uff01\u2026\u2026\u201d\u541f\u971c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u5df2\u6709\u4e86\u592b\u59bb\u7684\u540d\u5206\uff0c\u4f55\u51b5\uff0c\u4eba\u4e5f\u88ab\u4f60\u62b1\u8fc7\u3002\u2026\u2026\u201d \u3000\u3000\u62c9\u5f00\u68c9\u88ab\uff0c\u7ffb\u8f6c\u7740\u6c5f\u67ab\u7684\u8eab\u5b50\uff0c\u63a5\u9053\uff1a\u201c\u6211\u5148\u53eb\u4e86\u4e00\u58f0\uff0c\u5c31\u662f\u6015\u4f60\u614c\u4e71\uff0c\u649e\u5230\u4f24\u53e3\uff0c\u60f3\u4e0d\u5230\uff0c\u4f60\u4ecd\u7136\u8fd9\u4e48\u6c89\u4e0d\u4f4f\u6c14\uff0c\u771f\u662f\u5728\u52ab\u96be\u9003\u554a\uff01\u201d \u3000\u3000\u53d6\u8fc7\u68c9\u82b1\uff0c\u6cbe\u4e86\u706b\u9152\uff0c\u4ed4\u7ec6\u7684\u64e6\u62ed\u4f24\u53e3\u8840\u8ff9\u3002 \u3000\u3000\u6c5f\u67ab\u95ed\u76ee\u4e0d\u8bed\uff0c\u5fc3\u4e2d\u5374\u6697\u6697\u5fd6\u9053\uff1a\u201c\u6211\u8fd9\u4e48\u5927\u4e00\u4e2a\u4eba\uff0c\u4e00\u4e1d\u4e0d\u6302\u5730\u7ad9\u5728\u90a3\u91cc\uff0c\u5ffd\u7136\u623f\u95e8\u5927\u5f00\uff0c\u8981\u6211\u600e\u4e48\u4e0d\u614c\u2026\u2026\u201d \u3000\u3000\u7ad9\u5728\u5ba4\u5916\u7684\u541f\u96ea\u3001\u6885\u82b1\uff0c\u90fd\u6025\u7740\u60f3\u51b2\u5165\u5367\u623f\uff0c\u4f46\u7f57\u5170\u6321\u5728\u95e8\u4e2d\u4e0d\u52a8\uff0c\u8c01\u4e5f\u4e0d\u597d\u63a8\u5f00\u5979\uff0c\u53ea\u597d\u50f5\u5728\u539f\u5730\u3002 \u3000\u3000\u504f\u662f\u541f\u971c\u6c89\u5f97\u4f4f\u6c14\uff0c\u5c0f\u5fc3\u7ffc\u7ffc\u7684\u64e6\u62ed\u53bb\u6c5f\u67ab\u4f24\u53e3\u8840\u8ff9\u540e\uff0c\u53c8\u628a\u4f24\u53e3\u5305\u624e\u8d77\u6765\uff0c\u53d6\u8fc7\u6c5f\u67ab\u7684\u8863\u670d\uff0c\u5e2e\u4ed6\u7a7f\u597d\u3002 \u3000\u3000\u6c5f\u67ab\u5374\u7d27\u95ed\u7740\u53cc\u76ee\uff0c\u88c5\u51fa\u4e00\u526f\u8fd8\u672a\u6e05\u9192\u7684\u795e\u6001\u3002 \u3000\u3000\u541f\u971c\u5fcd\u4e0d\u4f4f\u55e4\u7684\u4e00\u7b11\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u50bb\u54e5\u54e5\uff0c\u4f60\u5728\u9a97\u6211\u5440\uff01\u8fd8\u662f\u9a97\u81ea\u5df1\uff1f\u2026\u2026\u201d \u3000\u3000\u8f7b\u8f7b\u5728\u6c5f\u67ab\u8138\u4e0a\u4eb2\u4e86\u4e00\u4e0b\uff0c\u63a5\u9053\uff1a\u201c\u7ee7\u7eed\u7684\u88c5\u4e0b\u53bb\u5427\uff01\u6211\u53bb\u53eb\u5979\u4eec\u8fdb\u6765\uff0c\u88c5\u50cf\u4e00\u4e9b\u5440\uff01 \u3000\u3000\u6211\u5a18\u4e5f\u6765\u4e86\u3002\u201d \u3000\u3000\u6253\u5f00\u6728\u95e8\uff0c\u541f\u96ea\u3001\u6885\u82b1\u3001\u5f20\u56db\u59d1\uff0c\u76f8\u7ee7\u800c\u5165\uff0c\u7f57\u5170\u7ad9\u5728\u6700\u524d\uff0c\u5374\u8d70\u5728\u6700\u540e\uff0c\u5fc3\u4e2d\u8fd8\u5728\u5fd0\u5fd1\uff0c\u4e0d\u77e5\u4eba\u5c0f\u9b3c\u5927\u7684\u4e8c\u4e2b\u5934\uff0c\u662f\u5426\u5df2\u8ba9\u6c5f\u67ab\u7a7f\u597d\u4e86\u8863\u670d\uff0c\u89c1\u6c5f\u67ab\u8863\u7740\u6574\u9f50\uff0c\u6b63\u76d8\u5750\u5728\u5e8a\u4e0a\u8c03\u606f\uff0c\u624d\u653e\u4e0b\u5fc3\u4e2d\u77f3\u5934\u3002 \u3000\u3000\u6c5f\u67ab\u4e5f\u4e0d\u662f\u88c5\u4f5c\uff0c\u771f\u6b63\u7684\u8fd0\u6c14\u8c03\u606f\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u660e\u767d\uff0c\u8fd9\u4e9b\u4eba\u4e2a\u4e2a\u7cbe\u7075\u65e0\u6bd4\uff0c\u4efb\u4f55\u88c5\u4f5c\uff0c\u90fd\u65e0\u6cd5\u9003\u8fc7\u4ed6\u4eec\u7684\u8033\u76ee\uff0c\u4f55\u4e0d\u501f\u6b64\u673a\u4f1a\uff0c\u8c03\u606f\u4e00\u756a\u3002 \u3000\u3000\u770b\u4ed6\u6c14\u606f\u5747\u5300\uff0c\u6e10\u5165\u4e86\u7269\u6211\u4e24\u5fd8\u4e4b\u5883\uff0c\u5f20\u56db\u59d1\u6446\u6446\u624b\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u54b1\u4eec\u5230\u5385\u4e2d\u5750\u53bb\uff0c\u522b\u6270\u4e86\u4ed6\u3002\u201d \u3000\u3000\u5f53\u5148\u8f6c\u8eab\u5411\u5916\u884c\u53bb\u3002 \u3000\u3000\u7f57\u5170\u76ee\u5149\u8f6c\u52a8\uff0c\u770b\u5230\u4e00\u9762\u5899\u58c1\u4e0a\u9752\u7816\u88c2\u4e86\u4e00\u6761\u5927\u7f1d\uff0c\u5fc3\u4e2d\u5927\u89c9\u5947\u602a\uff0c\u5fd6\u9053\uff1a\u201c\u521a\u624d\u90a3\u58f0\u5927\u9707\uff0c\u5b9a\u662f\u51fb\u88c2\u6b64\u5904\uff0c\u53ef\u662f\u8c01\u6709\u5982\u6b64\u6d51\u539a\u7684\u529f\u529b\u5462\uff1f\u96be\u9053\u662f\u6c5f\u67ab\u4e0d\u6210\uff1f\u2026\u2026\u201d \u3000\u3000\u5f20\u56db\u59d1\u5e26\u4e0a\u5367\u5ba4\u623f\u95e8\uff0c\u8ba9\u7f57\u5170\u5165\u4e86\u5750\uff0c\u624d\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u770b\u6837\u5b50\uff0c\u4ed6\u8eab\u4e0a\u5df2\u65e0\u4f59\u6bd2\uff0c\u8fd9\u4e00\u9635\u5750\u606f\u9192\u6765\uff0c\u5927\u6982\u5c31\u53ef\u4ee5\u5b8c\u5168\u590d\u5143\u4e86\uff0c\u5927\u8fd8\u4e39\u7684\u836f\u6548\uff0c\u679c\u7136\u662f\u975e\u540c\u5c0f\u53ef\u3002\u201d \u3000\u3000\u201c\u5927\u8fd8\u4e39\u2026\u2026\u201d\u7f57\u5170\u9053\uff1a\u201c\u8c01\u7ed9\u4ed6\u670d\u4e86\u5927\u8fd8\u4e39\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u662f\u5f20\u59e8\u4e86\u2026\u2026\u201d\u541f\u971c\u9053\u3002 \u3000\u3000\u201c\u56db\u59d1\uff0c\u4f60\u771f\u6709\u5927\u8fd8\u4e39\u2026\u2026\u201d \u3000\u3000\u7f57\u5170\u6709\u4e9b\u4e0d\u4fe1\u7684\u95ee\u9053\u3002 \u3000\u3000\u201c\u53ea\u6709\u8fd9\u4e48\u4e00\u9897\uff0c\u7ed9\u4f60\u5973\u5a7f\u5403\u4e86\uff0c\u2026\u2026\u2026\u201d\u5f20\u56db\u59d1\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5e0c\u671b\u4ed6\u4ee5\u540e\u80fd\u65e0\u707e\u65e0\u6b83\uff0c\u518d\u8981\u53d7\u4e00\u6b21\u8981\u547d\u7684\u6bd2\u4f24\uff0c\u8c01\u4e5f\u65e0\u80fd\u6551\u4e0d\u4e86\uff01\u201d \u3000\u3000\u76ee\u5149\u8f6c\u52a8\uff0c\u56db\u987e\u4e86\u4e00\u773c\uff0c\u63a5\u9053\uff1a\u201c\u4f60\u4eec\u90fd\u8981\u8bb0\u8005\uff0c\u80dc\u8d25\u4e43\u5175\u5bb6\u5e38\u4e8b\uff0c\u6027\u547d\u53ef\u53ea\u6709\u4e00\u6761\uff0c\u53d1\u89c9\u4e0d\u654c\u65f6\uff0c\u4e0d\u53ef\u786c\u62fc\uff0c\u6211\u4e0d\u60f3\u4f60\u4eec\u4efb\u4f55\u4eba\u53d7\u5230\u4f24\u5bb3\uff0c\u672c\u6765\u662f\u654c\u660e\u6211\u6697\u5bf9\u6211\u4eec\u5341\u5206\u6709\u5229\uff0c\u4f46\u6c5f\u67ab\u7684\u597d\u80dc\u4e4b\u5fc3\uff0c\u5374\u628a\u6211\u4eec\u66b4\u9732\u4e86\u51fa\u6765\uff0c\u6b64\u756a\u4e00\u6218\uff0c\u5982\u662f\u529b\u6709\u4e0d\u53ca\uff0c\u6211\u4eec\u5c31\u9690\u5165\u6697\u4e2d\u53bb\u3002\u201d \u3000\u3000\u201c\u5f20\u59e8\uff01\u5f88\u96be\u518d\u6709\u8fd9\u79cd\u673a\u4f1a\u4e86\u2026\u2026\u201d\u6885\u82b1\u8bf4\u3002 \u3000\u3000\u201c\u4e09\u5bab\u4e3b\u5e26\u6765\u4e86\u5927\u6279\u4eba\u624b\uff0c\u542c\u8bf4\u5929\u9a6c\u5802\u4e5f\u6d3e\u4e86\u5927\u6279\u9ad8\u624b\uff0c\u8fdb\u5165\u957f\u5b89\uff0c\u8fd9\u4e00\u6b21\uff0c\u4ed6\u4eec\u5f88\u53ef\u80fd\u6682\u606f\u5185\u4e89\uff0c\u5168\u529b\u5bf9\u4ed8\u6211\u4eec\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u8fd9\u5012\u662f\u5927\u9ebb\u70e6\u4e86\uff0c\u201d\u8bed\u58f0\u4e00\u987f\uff0c\u63a5\u9053\u201c\u5929\u738b\u95e8\u6709\u4e9b\u4ec0\u4e48\u53d8\u5316\uff1f\u201d \u3000\u3000\u201c\u6709\uff01\u2026\u2026\u201d\u6885\u82b1\u4f4e\u58f0\u9053\uff1a\u201c\u83f1\u82b1\u544a\u8bc9\u6211\uff0c\u5929\u738b\u672c\u4eba\u5df2\u6f5c\u5165\u957f\u5b89\uff0c\u53ef\u80fd\u9690\u8eab\u5728\u6816\u51e4\u697c\u4e2d\u2026\u2026\u201d \u3000\u3000\u5f20\u56db\u59d1\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u8fd8\u6709\u5417\uff1f\u201d \u3000\u3000\u201c\u542c\u8bf4\u5929\u738b\u95e8\u4e2d\u638c\u7406\u6587\u4e66\u3001\u4ee4\u724c\u7684\u84dd\u51e4\uff0c\u7387\u9886\u4e86\u56db\u5927\u5929\u9e45\u3001\u4e09\u9b54\u3001\u4e94\u602a\uff0c\u5929\u738b\u95e8\u4e2d\u7684\u5927\u90e8\u7cbe\u9510\uff0c\u4e5f\u5c06\u4e8e\u8fd1\u65e5\u4e2d\u8fdb\u5165\u957f\u5b89\u3002\u201d \u3000\u3000\u201c\u4e56\u4e56\uff0c\u51c6\u5907\u5728\u8fd9\u91cc\u5927\u5e72\u4e00\u573a\u4e86\u2026\u2026\u201d\u795e\u4e10\u94b1\u7f3a\u5728\u4e03\u5b9d\u548c\u5c1a\u966a\u540c\u4e0b\uff0c\u5927\u6b65\u8fdb\u5165\u5385\u4e2d\uff0c\u63a5\u9053\uff1a\u201c\u6055\u6211\u8001\u53eb\u5316\u5b50\u6405\u5c40\uff0c\u542c\u8bf4\u6c5f\u67ab\u53d7\u4e86\u6bd2\u4f24\uff0c\u8001\u53eb\u5316\u7126\u8651\u5f97\u5f88\uff0c\u6b27\u9633\u5144\u6302\u5ff5\u5973\u5a7f\uff0c\u4e5f\u662f\u8336\u996d\u4e0d\u601d\uff0c\u6240\u4ee5\uff0c\u6d3e\u6211\u6765\u63a2\u95ee\u4e00\u4e0b\uff1f\u201d \u3000\u3000\u201c\u6c5f\u67ab\u5927\u96be\u4e0d\u6b7b\uff0c\u4eba\u5df2\u5eb7\u590d\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u4f60\u6765\u7684\u6b63\u597d\uff0c\u628a\u8fd9\u91cc\u7684\u60c5\u52bf\u53d8\u5316\u5feb\u901f\uff0c\u4f20\u544a\u6b27\u9633\u5144\uff0c\u8981\u4ed6\u628a\u85cf\u5728\u7bb1\u5e95\u7684\u4e1c\u897f\u62ff\u51fa\u6765\u5427\uff01\u6211\u770b\u4e00\u573a\u5927\u51b3\u6218\uff0c\u5df2\u8feb\u7709\u776b\u4e86\u3002\u201d \u3000\u3000\u201c\u77e5\u592b\u83ab\u82e5\u59bb\uff0c\u8fd9\u53e5\u672a\u5fc5\u5bf9\u4e86\uff01\u2026\u2026\u201d\u7f57\u5170\u9053\uff1a\u201c\u6211\u8001\u516c\u8fd8\u85cf\u4e86\u4ec0\u4e48\u9690\u79c1\uff0c\u6211\u600e\u4e48\u4e00\u70b9\u4e5f\u4e0d\u77e5\u9053\u5462\uff1f\u201d \u3000\u3000\u201c\u653e\u5fc3\u554a\uff01\u2026\u2026\u201d\u5f20\u56db\u59d1\u7b11\u9053\uff1a\u201c\u6b27\u9633\u5144\u5bf9\u4f60\u5b9d\u8d1d\u7684\u5f88\uff0c\u5c31\u7b97\u628a\u5929\u4e0b\u7f8e\u5973\uff0c\u7edf\u7edf\u62ff\u6765\u6362\u4f60\uff0c\u4ed6\u4e5f\u672a\u5fc5\u5c31\u4f1a\u540c\u610f\u2026\u2026\u201d \u3000\u3000\u5bf9\u8fd9\u756a\u53d6\u7b11\uff0c\u7f57\u5170\u5fc3\u4e2d\u53cd\u6709\u7740\u751c\u7f8e\u3001\u8e0f\u5b9e\u7684\u611f\u89c9\uff0c\u8138\u4e0a\u6cdb\u5347\u8d77\u5fae\u5fae\u7684\u7b11\u610f\uff0c\u9053\uff1a\u201c\u9664\u4e86\u91d1\u5c4b\u85cf\u5a07\uff0c\u4ed6\u4e0d\u4fbf\u544a\u8bc9\u6211\u4e4b\u5916\uff0c\u8fd8\u6709\u4ec0\u4e48\u4e8b\u8981\u7792\u7740\u6211\u5462\uff1f\u201d \u3000\u3000\u201c\u6211\u4e5f\u662f\u521a\u77e5\u9053\u8fd9\u4e2a\u79d8\u5bc6\u4e0d\u4e45\u2026\u2026\u201d\u5f20\u56db\u59d1\u8bf4\uff0c\u201c\u6211\u544a\u8bc9\u4ed6\u7533\u96ea\u541b\u5e26\u6765\u4e86\u516b\u5927\u5251\u58eb\uff0c\u4e2a\u4e2a\u90fd\u9020\u8be3\u7cbe\u6df1\uff0c\u6b27\u9633\u5144\u4e0d\u7518\u793a\u5f31\uff0c\u624d\u8bf4\u4e86\u4e00\u4e2a\u5927\u79d8\u5bc6\uff0c\u5c31\u5728\u4f60\u4eec\u9690\u5c45\u7684\u4e0d\u8fdc\u4e4b\u5904\uff0c\u4ed6\u4e5f\u8bad\u7ec3\u4e86\u4e94\u4e2a\u5f1f\u5b50\u2026\u2026\u201d \u3000\u3000\u201c\u597d\u7d27\u7684\u53e3\u98ce\u2026\u2026\u201d\u7f57\u5170\u9053\uff1a\u201c\u4e00\u4e0b\u5b50\u8bad\u7ec3\u4e86\u4e94\u4e2a\u5251\u624b\uff0c\u6211\u7adf\u7136\u4e00\u70b9\u4e5f\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u201c\u4f60\u751f\u4e86\u4e24\u4e2a\u5973\u513f\uff0c\u6709\u597d\u957f\u4e00\u6bb5\u65f6\u95f4\uff0c\u628a\u7cbe\u795e\u5168\u653e\u5728\u5979\u4eec\u8eab\u4e0a\u2026\u2026\u201d\u5f20\u56db\u59d1\u8bf4\uff0c\u201c\u4f60\u8001\u516c\u8bad\u7ec3\u4e86\u4e94\u4e2a\u5251\u624b\uff0c\u4e5f\u53ef\u89e3\u53bb\u4ed6\u4e0d\u5c11\u7684\u5bc2\u5bde\uff0c\u7f57\u5170\uff0c\u9664\u975e\u767b\u4e0a\u4e86\u4ed9\u4f5b\u6bbf\u5802\uff0c\u518d\u8d85\u51e1\u7684\u4eba\uff0c\u4e5f\u4f1a\u6709\u60c5\u7eea\u4e0d\u7a33\u7684\u65f6\u4faf\uff0c\u4e5f\u4f1a\u6709\u96be\u8010\u3001\u5b64\u5bc2\u7684\u8fb0\u5149\u3002 \u3000\u3000\u4f55\u51b5\u2014\u2014 \u3000\u3000\u4f60\u4eec\u4f4f\u5728\u90a3\u79cd\u8352\u5c71\u5e7d\u8c37\u4e4b\u4e2d\uff0c\u98ce\u666f\u662f\u7f8e\u6781\u4e86\uff0c\u4f46\u662f\u524d\u4e0d\u89c1\u53e4\u4eba\uff0c\u540e\u4e0d\u89c1\u6765\u8005\u7684\u7a7a\u5bc2\uff0c\u8fd8\u771f\u5f97\u8981\u6781\u5927\u7684\u8010\u6027\u624d\u80fd\u4f4f\u4e0b\u53bb\uff0c\u4e00\u4f4f\u4e8c\u5341\u5e74\u554a\uff01 \u3000\u3000\u4f60\u9017\u5973\u4e3a\u4e50\uff0c\u4ed6\u9664\u4e86\u7ec3\u5251\u4e4b\u5916\uff0c\u53ea\u6709\u8010\u5fc3\u7684\u7b49\u4f60\u7a7a\u4e0b\u6765\uff0c\u624d\u80fd\u4eab\u53d7\u4e00\u523b\u6e29\u5b58\u2026\u2026\u201d \u3000\u3000\u7f57\u5170\u7684\u8138\u7ea2\u4e86\uff0c\u5550\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u56db\u59d1\uff0c\u8001\u53eb\u5316\uff0c\u5927\u548c\u5c1a\u5168\u5728\u8fd9\u91cc\uff0c\u8fd8\u6709\u4e24\u4e2a\u4e2b\u5934\uff0c\u4f60\u600e\u4e48\u53e3\u6ca1\u906e\u62e6\uff0c\u4ec0\u4e48\u8bdd\u90fd\u8bf4\u5f97\u51fa\u53e3\u2026\u2026\u201d\u5ae3\u7136\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u5c31\u7b97\u4ed6\u8bad\u7ec3\u4e86\u4e94\u4e2a\u5251\u624b\uff0c\u53ef\u662f\u8fdc\u5728\u8700\u4e1c\uff0c\u522b\u4eba\u53c8\u4e0d\u77e5\u9053\u4e94\u4eba\u85cf\u8eab\u7684\u5730\u65b9\uff0c\u975e\u5f97\u4ed6\u81ea\u5df1\u8dd1\u4e00\u8d9f\u624d\u6210\uff0c\u660e\u65e5\u4e4b\u6218\uff0c\u7edd\u5bf9\u662f\u8d76\u4e0d\u4e0a\u4e86\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u7a81\u7136\u8138\u8272\u4e00\u5bd2\uff0c\u51b7\u51b7\u63a5\u9053\uff1a\u201c\u7f57\u5170\uff0c\u4e0d\u8981\u968f\u4fbf\u4e71\u7b11\uff0c\u8bb0\u4e0d\u8bb0\u5f97\u5728\u6885\u82b1\u5385\u7b11\u51fa\u7684\u6bdb\u75c5\uff0c\u4f60\u7684\u5929\u72d0\u4e4b\u7b11\uff0c\u6210\u5c31\u4e4b\u9ad8\uff0c\u6050\u5c24\u5728\u4e24\u4e2a\u4e2b\u5934\u4e4b\u4e0a\u3002\u201d \u3000\u3000\u7f57\u5170\u5fc3\u4e2d\u8fd8\u6709\u4e9b\u4e0d\u4fe1\uff0c\u8f6c\u5934\u770b\u53bb\uff0c\u53ea\u89c1\u795e\u4e10\u94b1\u7f3a\u4e00\u8138\u8ff7\u60d8\uff0c\u8fde\u6885\u82b1\u4e5f\u770b\u7684\u77aa\u7740\u773c\u775b\uff0c\u5927\u548c\u5c1a\u5782\u7709\u95ed\u76ee\uff0c\u867d\u662f\u5b9d\u76f8\u5e84\u4e25\uff0c\u53ef\u4e5f\u8868\u793a\u51fa\u4e0d\u6562\u591a\u770b\u7684\u795e\u6001\u3002 \u3000\u3000\u201c\u5a18\uff0c\u4f60\u7b11\u7684\u592a\u597d\u770b\u4e86\u2026\u2026\u201d\u541f\u96ea\u9053\uff1a\u201c\u5929\u72d0\u5a9a\u7b11\uff0c\u4f60\u5df2\u5c3d\u795e\u9ad3\uff0c\u6211\u548c\u59b9\u59b9\uff0c\u90fd\u4e0d\u80fd\u53ca\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\uff0c\u6211\u6ca1\u6709\u7ec3\u8fc7\u5440\uff1f\u2026\u2026\u201d\u7f57\u5170\u6025\u6025\u8bf4\u9053\uff1a\u201c\u5a18\u5df2\u7ecf\u56db\u5341\u591a\u5c81\u4e86\uff0c\u8fd8\u7ec3\u7684\u4ec0\u4e48\u5929\u72d0\u5a9a\u7b11\u2026\u2026\u201d \u3000\u3000\u201c\u7f57\u5170\uff0c\u4f60\u53ef\u80fd\u662f\u6211\u4eec\u8fd9\u4e9b\u4eba\u4e2d\uff0c\u552f\u4e00\u89c1\u592d\u72d0\u592b\u4eba\u5a9a\u7b11\u7684\u4eba\u2026\u2026\u201d\u5f20\u53e3\u59d1\u53f9\u606f\u4e00\u58f0\u53c8\u9053\uff1a\u201c\u6211\u76f8\u4fe1\u4f60\u4f1a\u6709\u96be\u5fd8\u7684\u8bb0\u5fc6\uff0c\u4f60\u719f\u8bb0\u4e86\u592d\u6d3e\u6b66\u529f\u53e3\u8bc0\uff0c\u7167\u65b9\u6293\u836f\uff0c\u4f20\u7ed9\u5973\u513f\uff0c\u5374\u6ca1\u6709\u6f14\u7ec3\u7ed9\u5979\u4eec\u770b\u8fc7\uff0c\u5979\u4eec\u51ed\u85c9\u81ea\u5df1\u5929\u8d4b\uff0c\u81ea\u884c\u63e3\u6478\uff0c\u4f46\u4f60\u5374\u662f\u771f\u6b63\u89c1\u8fc7\u5929\u72d0\u5a9a\u7b11\u7684\u4eba\uff0c\u90a3\u79cd\u6df1\u5370\u8111\u9645\u5370\u8c61\uff0c\u5c3d\u5f97\u4e86\u4f20\u795e\u4e4b\u5904\uff0c\u4e8c\u5973\u5982\u4f55\u80fd\u53ca\uff1f\u201d \u3000\u3000\u7f57\u5170\u5782\u4e0b\u5934\u53bb\uff0c\u6ca1\u6709\u56de\u7b54\u3002\u4e0d\u56de\u7b54\uff0c\u7b49\u4e8e\u662f\u627f\u8ba4\u4e86\uff0c\u5df2\u5728\u4e0d\u77e5\u4e0d\u89c9\u4e2d\uff0c\u7ec3\u6210\u4e86\u6700\u9ad8\u660e\u7684\u5929\u72d0\u5a9a\u7b11\u3002 \u3000\u3000\u201c\u5a18\uff01\u8fd9\u4e9b\u65e5\u5b50\u91cc\uff0c\u6211\u4eec\u548c\u5f20\u59e8\u540c\u7ec3\u5929\u72d0\u6b66\u529f\u2026\u2026\u201d\u541f\u971c\u9053\uff1a\u201c\u5f97\u5f20\u59e8\u6307\u70b9\uff0c\u83b7\u76ca\u532a\u6d45\u2026\u2026\u201d \u3000\u3000\u201c\u597d\u6781\u4e86\uff01\u2026\u2026\u201d\u7f57\u5170\u9053\uff1a\u201c\u5a18\u4e5f\u5f88\u60f3\u548c\u4f60\u4eec\u4e00\u540c\u53c2\u7814\uff0c\u4f46\u4e3a\u4e86\u4f60\u7239\uff0c\u5a18\u4e0d\u80fd\u7ec3\u5929\u72d0\u6b66\u529f\u2026\u2026\u201d \u3000\u3000\u201c\u7239\u4e0d\u662f\u5df2\u7ecf\u540c\u610f\u8ba9\u5a18\u7ec3\u4e86\u5417\uff1f\u201d\u541f\u971c\u9053\uff1a\u201c\u5a18\u53c8\u4e3a\u4ec0\u4e48\u4e0d\u7ec3\u5462\uff1f\u201d \u3000\u3000\u201c\u5509\uff01\u4f60\u7239\u662f\u540c\u610f\u4e86\uff0c\u53ef\u662f\uff0c\u5e76\u975e\u771f\u7684\u5e0c\u671b\u5a18\u7ec3\u6210\u4e2a\u4e00\u8eab\u72d0\u6c14\u7684\u4eba\uff0c\u518d\u8bf4\uff0c\u6bcd\u5973\u540c\u5728\u4e00\u5904\uff0c\u4e60\u7ec3\u8fd9\u79cd\u6b66\u529f\uff0c\u4e5f\u6709\u5f88\u591a\u4e0d\u4fbf\u4e4b\u5904\uff0c\u90a3\u5c31\u5f88\u96be\u8fdb\u5165\u767b\u5cf0\u9020\u6781\u7684\u5883\u754c\u4e86\uff0c\u5f20\u59e8\u80af\u966a\u4f60\u4eec\uff0c\u662f\u6700\u9002\u5408\u7684\u4eba\u4e86\u3002\u201d \u3000\u3000\u201c\u8bf4\u7684\u4e5f\u662f\u554a\uff01\u2026\u2026\u201d\u541f\u971c\u9053\uff1a\u201c\u5a18\u521a\u624d\u90a3\u4e00\u7b11\uff0c\u7ed9\u4e86\u6211\u4e0d\u5c11\u542f\u793a\uff0c\u6211\u60f3\u59d0\u59d0\u4e5f\u662f\u4e00\u6837\uff0c\u4f7f\u6211\u4eec\u83b7\u76ca\u5f88\u5927\u3002\u201d \u3000\u3000\u7f57\u5170\u7ef7\u7740\u8138\uff0c\u9053\uff1a\u201c\u56db\u59d1\uff0c\u8c22\u8c22\u4f60\u7684\u6307\u6559\uff0c\u4ee5\u540e\uff0c\u6211\u4f1a\u8b66\u60d5\u81ea\u5df1\uff0c\u6211\u5df2\u4e86\u89e3\u592d\u72d0\u592b\u4eba\uff0c\u4e3a\u4ec0\u4e48\u5728\u4e2d\u5e74\u4e4b\u540e\uff0c\u5f88\u5c11\u518d\u7b11\u4e86\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u7a81\u7136\u4e0a\u524d\u4e00\u6b65\uff0c\u9644\u5728\u7f57\u5170\u7684\u8033\u8fb9\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u8fd9\u79cd\u90aa\u95e8\u6b66\u529f\uff0c\u6709\u5982\u70c8\u9152\u9189\u4eba\uff0c\u4f1a\u4f7f\u4eba\u4e71\u6027\uff0c\u6211\u8fd9\u4e00\u751f\u90fd\u8d70\u7684\u504f\u95e8\uff0c\u4f46\u6211\u548c\u4e8c\u4e2a\u4e2b\u5934\u7ec3\u6210\u4e00\u4e2a\u6708\u5929\u72d0\u6b66\u529f\uff0c\u5df2\u611f\u53d7\u5230\u5b83\u7684\u538b\u529b\uff0c\u90aa\u7684\u5389\u5bb3\u3002 \u3000\u3000\u6240\u4ee5\uff0c\u4f60\u8981\u5343\u4e07\u5c0f\u5fc3\uff0c\u53ef\u4e0d\u80fd\u5bf9\u6c5f\u67ab\u4e71\u7b11\uff0c\u4ed6\u5982\u5c55\u5f00\u53cd\u51fb\uff0c\u4f60\u4e5f\u672a\u5fc5\u80fd\u6321\u5f97\u4f4f\uff0c\u90a3\u5c31\u5929\u4e0b\u5927\u4e71\u4e86\u3002\u201d \u3000\u3000\u7f57\u5170\u5fc3\u5934\u4e00\u9707\uff0c\u9053\uff1a\u201c\u4ed6\u4e0d\u662f\u5b9a\u529b\u5f88\u5f3a\u5417\uff1f\u4e24\u4e2a\u4e2b\u5934\uff0c\u4ecd\u4fdd\u4f4f\u6e05\u767d\u8eab\u5b50\uff0c\u6211\u76f8\u4fe1\u662f\u6c5f\u67ab\u7684\u80fd\u529b\u3002\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u4ed6\u662f\u6297\u62d2\u4f4f\u4e86\u96ea\u3001\u971c\u4e8c\u5973\u7684\u8bf1\u60d1\uff0c\u4f46\u4e5f\u6297\u7684\u5f88\u8f9b\u82e6\u2026\u2026\u201d\u5f20\u56db\u7ad9\u9053\uff1a\u201c\u4f46\u4f60\u90a3\u4e00\u7b11\uff0c\u6709\u5982\u592d\u72d0\u91cd\u751f\uff0c\u6211\u5f88\u62c5\u5fc3\uff0c\u4ed6\u4f1a\u4e3a\u4e86\u81ea\u4fdd\u5168\u529b\u53cd\u51fb\uff0c\u4f60\u81ea\u4fe1\u80fd\u6297\u62d2\u5f97\u4f4f\u5417\uff1f\u201d \u3000\u3000\u201c\u6211\u2026\u2026\u6211\u4e0d\u77e5\u2026\u2026\u201d\u7f57\u5170\u5fc3\u5934\u53d1\u51c9\uff0c\u51b7\u6c57\u6ee1\u988a\uff0c\u9053\uff1a\u201c\u6211\u4e0d\u4f1a\u8f7b\u6613\u518d\u7b11\uff0c\u4e5f\u5c3d\u91cf\u907f\u5f00\u6c5f\u5e06\uff0c\u518d\u8c22\u8c22\u4f60\u63d0\u9192\u6211\uff0c\u4e00\u65e6\u661f\u706b\u71ce\u539f\uff0c\u90a3\u771f\u662f\u4e0d\u582a\u6536\u62fe\u4e86\u3002\u201d \u3000\u3000\u4f46\u95fb\u94b1\u7f3a\u957f\u957f\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8001\u53eb\u5316\u8fd9\u5c31\u53bb\u901a\u77e5\u6b27\u9633\u5144\uff0c\u4e8b\u60c5\u7d27\u6025\uff0c\u6211\u770b\u9152\u996d\u4e5f\u4e0d\u7528\u5403\u4e86\u3002\u201d \u3000\u3000\u4ed6\u4e00\u751f\u4e2d\u6700\u6ce8\u91cd\u5403\u3001\u559d\u4e24\u4ef6\u4e8b\uff0c\u672c\u6765\u60f3\u8ba9\u5f20\u56db\u59d1\u9976\u51e0\u4e2a\u5c0f\u83dc\u5403\u5403\uff0c\u518d\u559d\u4e0a\u4e24\u65a4\u8001\u9152\uff0c\u4f46\u7f57\u5170\u90a3\u4e00\u7b11\uff0c\u5bb3\u7684\u4ed6\u5931\u9b42\u843d\u9b44\uff0c\u6e05\u9192\u4e4b\u540e\uff0c\u5df2\u7ecf\u88ab\u5413\u7834\u4e86\u80c6\u5b50\uff0c\u54ea\u91cc\u8fd8\u6562\u505c\u7559\uff0c\u62c5\u5fc3\u518d\u770b\u7f57\u5170\u90a3\u4e00\u7b11\uff0c\u4f1a\u5931\u53bb\u63a7\u5236\uff0c\u5f53\u573a\u51fa\u4e11\u2026\u2026\u2026\u3002 \u3000\u3000\u201c\u7ad9\u4f4f\uff0c\u2026\u2026\u201d\u5f20\u56db\u59d1\u6025\u6025\u559d\u9053\uff1a\u201c\u544a\u8bc9\u6b27\u9633\u5144\uff0c\u8981\u4ed6\u628a\u4e94\u4e2a\u5f1f\u5b50\u62db\u6765\uff0c\u7b49\u5019\u6211\u7684\u901a\u77e5\uff0c\u660e\u5929\uff0c\u6709\u4e94\u573a\u751f\u6b7b\u5b58\u4ea1\u5f97\u5927\u51b3\u6218\uff0c\u6211\u4eec\u8981\u5168\u529b\u4ee5\u8d74\u3002\u201d \u3000\u3000\u201c\u597d\uff0c\u597d\uff0c\u8001\u53eb\u5316\u8bb0\u5f97\u5f88\u6e05\u695a\uff0c\u7edd\u4e0d\u4f1a\u6f0f\u6389\u4e00\u53e5\u8bdd\u2026\u2026\u201d\u53e3\u4e2d\u7b54\u5e94\u7740\uff0c\u4eba\u5df2\u51fa\u4e86\u5385\u95e8\u3002 \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8001\u53eb\u5316\u55dc\u9152\u5982\u547d\uff0c\u8fd9\u4e00\u6b21\uff0c\u8fde\u547d\u4e5f\u987e\u4e0d\u5f97\u4e86\uff0c\u60f6\u6050\u5982\u60ca\u5f13\u4e4b\u9e1f\uff0c\u6f0f\u7f51\u4e4b\u9c7c\uff0c\u770b\u6765\u4eba\u4e16\u4e0a\uff0c\u8fd8\u771f\u6709\u91cd\u8fc7\u751f\u547d\u7684\u4e1c\u897f\u3002\u201d \u3000\u3000\u4ed6\u6ca1\u6709\u518d\u8bf4\u4e0b\u53bb\uff0c\u4f46\u4eba\u5fc3\u4e2d\u90fd\u660e\u767d\uff0c\u90a3\u662f\u6307\u4e00\u79cd\u6c14\u8282\u3001\u58f0\u8a89\u3002 \u3000\u3000\u7f57\u5170\u4e00\u8eac\u8eab\uff0c\u9053\uff1a\u201c\u4e8b\u51fa\u65e0\u5fc3\uff0c\u5927\u5e08\u6d77\u6db5\uff0c\u6b64\u540e\uff0c\u4e0d\u4f1a\u518d\u6709\u8fd9\u79cd\u4e8b\u4e86\u3002\u201d \u3000\u3000\u5927\u548c\u5c1a\u5408\u638c\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e0d\u77e5\u8005\u4e0d\u7f6a\uff0c\u6b27\u9633\u592b\u4eba\u8a00\u91cd\u4e86\u3002\u201d \u3000\u3000\u52a0\u4e0a\u4e86\u6b27\u9633\u4e24\u4e2a\u5b57\uff0c\u663e\u7136\u662f\u63d0\u9192\u7f57\u5170\u7684\u8eab\u4efd\uff0c\u6709\u592b\u4e4b\u5987\uff0c\u4e0d\u53ef\u653e\u7eb5\u3002 \u3000\u3000\u53ea\u542c\u623f\u95e8\u5440\u7136\uff0c\u6c5f\u67ab\u7f13\u6b65\u7531\u5367\u5ba4\u4e2d\u884c\u4e86\u51fa\u6765\uff0c\u795e\u97f5\u6e05\u660e\uff0c\u4f3c\u662f\u4f59\u6bd2\u5c3d\u9664\uff0c\u4f24\u52bf\u5df2\u5b8c\u5168\u597d\u4e86\u3002 \u3000\u3000\u201c\u6211\u4f5b\u4fdd\u4f51\uff0c\u6c5f\u5c11\u4fa0\u5df2\u7ecf\u5b8c\u5168\u5eb7\u590d\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u53cc\u624b\u5408\u5341\uff0c\u5e84\u4e25\u7684\u5ba3\u4e86\u4e00\u58f0\u4f5b\u53f7\uff0c\u63a5\u9053\uff1a\u201c\u548c\u5c1a\u5728\u5385\u5916\u5f85\u547d\u3002\u201d\u7f13\u6b65\u9000\u51fa\u5ba2\u5385\u3002 \u3000\u3000\u76ee\u7779\u5927\u548c\u5c1a\u5bf9\u6c5f\u67ab\u90a3\u4efd\u656c\u91cd\uff0c\u5f20\u56db\u59d1\u3001\u7f57\u5170\uff0c\u4e5f\u90fd\u53d8\u7684\u4e25\u8083\u8d77\u6765\uff0c\u5bf9\u6c5f\u67ab\u6b20\u8eab\u4f5c\u793c\u3002 \u3000\u3000\u6c5f\u67ab\u5374\u606d\u606d\u656c\u656c\u7684\u5bf9\u7740\u5f20\u56db\u59d1\u6df1\u6df1\u4e00\u63d6\uff0c\u9053\uff1a\u201c\u5927\u59d0\uff0c\u53c8\u6551\u4e86\u5c0f\u5f1f\u4e4b\u547d\u3002\u201d \u3000\u3000\u201c\u5f20\u59e8\u4ee5\u4ec5\u6709\u7684\u4e00\u9897\u5927\u8fd8\u4e39\uff0c\u7ed9\u4f60\u670d\u7528\u2026\u2026\u201d\u541f\u96ea\u9053\uff1a\u201c\u6e05\u9664\u4e86\u4f60\u8eab\u4e0a\u8fd9\u4e4b\u6bd2\uff0c\u2026\u2026\u201d \u3000\u3000\u201c\u5927\u2014\u2014\u5927\u8fd8\u4e39\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u795e\u60c5\u8083\u7a46\uff0c\u5168\u8eab\u5fae\u5fae\u98a4\u6296\uff0c\u5fc3\u4e2d\u4f3c\u6709\u7740\u65e0\u6bd4\u7684\u6fc0\u52a8\u3002 \u3000\u3000\u201c\u662f\u554a\uff01\u542c\u8bf4\u662f\u4e00\u79cd\u5f88\u597d\u7684\u7597\u4f24\u4e39\u836f\uff0c\u2026\u2026\u201d\u5f20\u56db\u59d1\u5e73\u6de1\u5730\u8bf4\u9053\uff1a\u201c\u8fd8\u771f\u7684\u6709\u70b9\u6548\u7528\uff0c\u7adf\u7136\u628a\u4f60\u7684\u6bd2\u4f24\u7ed9\u533b\u597d\u4e86\u3002\u201d \u3000\u3000\u201c\u5927\u59d0\uff0c\u4e0d\u8981\u9a97\u6211\uff0c\u90a3\u4e0d\u662f\u7597\u4f24\u7684\u836f\u7269\uff0c\u800c\u662f\u4ed9\u4e39\uff0c\u5f53\u4eca\u6b66\u6797\uff0c\u53ea\u6709\u4e09\u9897\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u5b83\u53ef\u4ee5\u8d77\u6b7b\u56de\u751f\uff0c\u589e\u957f\u529f\u529b\uff0c\u662f\u4eba\u95f4\u73cd\u5b9d\u3002\u201d \u3000\u3000\u201c\u5de6\u53f3\u4e0d\u8fc7\u662f\u4e00\u7c92\u4e39\u836f\u7f62\u4e86\uff0c\u2026\u2026\u201d\u5f20\u56db\u59d1\u7b11\u9053\uff1a\u201c\u4f60\u53d7\u4e86\u6bd2\u4f24\uff0c\u670d\u4e0b\u5927\u8fd8\u4e39\uff0c\u6bd2\u4f24\u75ca\u6108\uff0c\u6211\u4eec\u5e94\u8be5\u597d\u597d\u5e86\u8d3a\u4e00\u4e0b\u624d\u5bf9\uff0c\u5e72\u5417\u8981\u6446\u51fa\u4e00\u526f\u6101\u7709\u82e6\u8138\u7684\u6837\u5b50\u3002\u201d \u3000\u3000\u201c\u6211\u2026\u2026\u6211\u2026\u2026\u6211\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u7ec8\u4e8e\u5fcd\u4e0d\u4f4f\u53cc\u6cea\u6c34\u6eda\u4e86\u51fa\u6765\uff0c\u9053\uff1a\u201c\u6211\u53d7\u5927\u59d0\u6069\u6cfd\u592a\u591a\uff0c\u771f\u4e0d\u77e5\u5982\u4f55\u62a5\u7b54\u4e86\uff1f\u201d \u3000\u3000\u201c\u6709\u4e8c\u4e2a\u62a5\u7b54\u6211\u7684\u65b9\u6cd5\u3002\u201d \u3000\u3000\u201c\u5c0f\u5f1f\u6d17\u8033\u606d\u542c\uff0c\u5927\u59d0\u5c3d\u7ba1\u5429\u5490\u3002\u201d \u3000\u3000\u201c\u4e0d\u8bb8\u4f60\u518d\u53d7\u4f24\u2026\u2026\u201d\u5f20\u56db\u59d1\u8bf4\uff0c\u201c\u6c5f\u6e56\u4e0a\u6709\u5f88\u591a\u795e\u7269\u5229\u5668\uff0c\u53ef\u4ee5\u6d1e\u7a7f\u62a4\u8eab\u7f61\u6c14\uff0c\u4e0d\u53ef\u81ea\u6043\u6b66\u529f\u6210\u5c31\uff0c\u8fd9\u4e00\u6b21\uff0c\u4f60\u5982\u80fd\u8c28\u614e\u4e00\u4e9b\uff0c\u5c31\u4e0d\u81f4\u4e8e\u4e3a\u4eba\u6697\u7b97\u4e86\u3002\u201d \u3000\u3000\u201c\u5c0f\u5f1f\u8bb0\u4e0b\u4e86\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u5927\u59d0\u8fd8\u6709\u4ec0\u4e48\u6307\u6559\uff1f\u201d \u3000\u3000\u201c\u4f60\u662f\u7edf\u5e05\uff0c\u6211\u5e94\u8be5\u5411\u4f60\u62a5\u544a\uff0c\u2026\u2026\u201d\u5f20\u56db\u59d1\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5148\u8bf7\u6559\u7edf\u5e05\u6c5f\u5c11\u4fa0\uff0c\u8eab\u4f53\u662f\u4e0d\u662f\u5168\u597d\u4e86\uff1f\u80fd\u4e0d\u80fd\u548c\u4eba\u52a8\u624b\uff1f\u201d \u3000\u3000\u201c\u672c\u7edf\u5e05\u5b8c\u5168\u5eb7\u590d\uff0c\u8bba\u5185\u529b\u7cbe\u795e\uff0c\u4f3c\u5c24\u80dc\u53d7\u4f24\u4e4b\u524d\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u4e00\u672c\u6b63\u7ecf\u5730\u8bf4\uff0c\u201c\u8bf7\u5973\u8bf8\u845b\u89e3\u8bf4\u4e00\u4e0b\uff0c\u8fd9\u662f\u4ec0\u4e48\u539f\u56e0\uff1f\u201d \u3000\u3000\u201c\u90a3\u662f\u7edf\u5e05\u5929\u8d4b\u4f18\u5f02\uff0c\u5185\u529f\u7cbe\u6df1\u3002\u2026\u2026\u201d \u3000\u3000\u201c\u548c\u670d\u7528\u5927\u8fd8\u4e39\u662f\u5426\u6709\u5173\u5462\uff1f\u201d \u3000\u3000\u201c\u5927\u6982\u6709\u70b9\u5173\u7cfb\u5427\uff01\u2026\u2026\u201d\u5f20\u56db\u59d1\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u5927\u6218\u8feb\u5728\u7709\u776b\uff0c\u6211\u4eec\u90fd\u5fc3\u6025\u5982\u711a\uff0c\u4e0d\u8981\u5c3d\u626f\u4e9b\u65e0\u5173\u7d27\u8981\u7684\u4e8b\uff01\u201d \u3000\u3000\u4e00\u4e2a\u5f3a\u8c03\u53d7\u6069\u6df1\u91cd\uff0c\u4e00\u4e2a\u5374\u4e0d\u613f\u627f\u8ba4\u3002 \u3000\u3000\u201c\u597d\u5427\uff01\u6709\u4ec0\u4e48\u91cd\u8981\u7684\u4e8b\uff1f\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u5927\u59d0\u8bf7\u8bf4\u51fa\u6765\u3002\u201d \u3000\u3000\u201c\u4e09\u5bab\u4e3b\u6b63\u9762\u6311\u6218\uff0c\u9080\u7ea6\u660e\u65e5\u4e00\u51b3\u80dc\u8d1f\uff0c\u4f60\u53d7\u4f24\u8fd8\u672a\u6e05\u9192\uff0c\u8bf7\u6055\u56db\u59d1\u64c5\u81ea\u4f5c\u4e3b\uff0c\u7b54\u5e94\u4e86\u5979\u7684\u6311\u6218\u3002\u201d \u3000\u3000\u201c\u6b63\u5408\u543e\u610f\uff0c\u786e\u5b9a\u7684\u65f6\u95f4\u3001\u5730\u70b9\u5462\uff1f\u201d \u3000\u3000\u201c\u65f6\u8fb0\u3001\u5730\u70b9\uff0c\u5979\u4eec\u4f1a\u5728\u4eca\u591c\u901a\u77e5\uff0c\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u6211\u62c5\u5fc3\uff0c\u4ed6\u4eec\u65e9\u6709\u51c6\u5907\u3001\u5c4a\u65f6\u4ed6\u4eec\u9080\u7ea6\u7684\u9ad8\u624b\u4e91\u96c6\uff0c\u6211\u4eec\u5c31\u4f1a\u5403\u5927\u4e8f\u4e86\uff01\u201d \u3000\u3000\u201c\u5185\u5bab\u4e00\u7cfb\u548c\u5929\u9a6c\u5802\u4e89\u6597\u5267\u70c8\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u9887\u7709\u5934\u53c8\u9053\uff1a\u201c\u96be\u9053\u4ed6\u4eec\u8fd8\u4f1a\u5408\u4f5c\u4e0d\u6210\uff1f\u201d \u3000\u3000\u201c\u53ef\u80fd\u4f1a\u5408\u4f5c\uff0c\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a \u3000\u3000\u201c\u610f\u5916\u7684\u53d8\u5316\u662f\u5929\u738b\u95e8\u7adf\u7136\u51fa\u52a8\u4e86\u5927\u90e8\u5206\u7684\u4e3b\u529b\uff0c\u56db\u5927\u5929\u9e45\u3001\u4e09\u9b54\u3001\u4e94\u602a\uff0c\u7531\u4e00\u5411\u5750\u9547\u5de2\u7a74\u7684\u84dd\u51e4\u9886\u519b\u3002\u201d \u3000\u3000\u201c\u5929\u738b\u672c\u4eba\u5462\uff1f\u201d\u6c5f\u67ab\u6025\u6025\u95ee\u9053\u3002 \u3000\u3000\u201c\u65e9\u6765\u4e86\u2026\u2026\u201d\u5f20\u56db\u59cb\u66a7\u6627\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f4f\u5728\u6816\u51e4\u6402\u4e2d\uff0c\u4e0d\u8fc7\uff0c\u51e4\u67d3\u6076\u75be\uff0c\u4ed6\u53ea\u80fd\u671b\u6885\u6b62\u6e34\u4e86\u3002\u201d \u3000\u3000\u6885\u82b1\u548c\u96ea\u3001\u971c\u4e8c\u5973\uff0c\u5168\u90fd\u4f4e\u4e0b\u5934\uff0c\u63a9\u53e3\u5077\u7b11\uff0c\u53ea\u6709\u7f57\u5170\u7ef7\u7740\u4e00\u5f20\u8138\uff0c\u604d\u5982\u672a\u95fb\u3002 \u3000\u3000\u6c5f\u67ab\u5fc3\u4e2d\u660e\u767d\u4e86\uff0c\u539f\u672c\u81ea\u8ba4\u8fd8\u5f88\u79d8\u5bc6\u7684\u4e8b\u60c5\uff0c\u6562\u60c5\u522b\u4eba\u662f\u65e9\u77e5\u9053\u5566\u3002 \u3000\u3000\u5f20\u56db\u59d1\u4ed4\u7ec6\u7684\u8bf4\u51fa\u4e86\u5f97\u5230\u7684\u654c\u60c5\u6d88\u606f\uff0c\u4e5f\u8bf4\u51fa\u4e86\u7f57\u5170\u4e00\u5251\u72ec\u6218\u795e\u9b3c\u53cc\u5200\u7684\u7ecf\u8fc7\uff0c\u4f46\u5374\u6ca1\u6709\u4f5c\u654c\u60c5\u5206\u6790\u3002 \u3000\u3000\u201c\u5927\u59d0\u4e00\u5411\u8c03\u5175\u9063\u5c06\uff0c\u6599\u654c\u5982\u795e\uff0c\u4e0d\u77e5\u6709\u4f55\u62d2\u654c\u9ad8\u89c1\uff1f\u201d \u3000\u3000\u5f20\u5927\u59d0\u4e0d\u80af\u63d0\u51fa\u9ad8\u89c1\uff0c\u6c5f\u5144\u5f1f\u53ea\u597d\u4e0d\u803b\u4e0b\u95ee\u3002 \u3000\u3000\u201c\u6211\u4e0d\u77e5\u9053\uff0c\u795e\u3001\u9b3c\u53cc\u5200\u4e24\u4e2a\u8001\u4e0d\u6b7b\u7684\u602a\u7269\uff0c\u7a81\u7136\u51fa\u73b0\uff0c\u771f\u628a\u6211\u8d6b\u4e86\u4e00\u8df3\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u5e78\u597d\u6709\u7f57\u5170\u5728\u573a\uff0c\u5426\u5219\uff0c\u6211\u4eec\u90fd\u4e0d\u662f\u53cc\u5200\u654c\u624b\uff0c\u6885\u82b1\u5385\u5730\u65b9\u5c40\u9650\uff0c\u5929\u72d0\u6b66\u529f\u548c\u4e94\u884c\u9041\u672f\uff0c\u90fd\u4e0d\u5b9c\u65bd\u5c55\uff0c\u51ed\u85c9\u771f\u672c\u9886\uff0c\u786c\u529f\u592b\u52a8\u624b\uff0c\u8001\u5b9e\u8bf4\uff0c\u6211\u63a5\u4e0d\u4e0b\u4ed6\u4eec\u4e8c\u5341\u62db\uff0c\u96ea\u3001\u971c\u4e8c\u5973\u5408\u8d77\u6765\uff0c\u4e5f\u96be\u80dc\u4eba\uff0c\u4e00\u65e6\u62fc\u4e0a\u4e86\uff0c\u4f1a\u95f9\u6210\u4ec0\u4e48\u5c40\u9762\uff0c\u771f\u53eb\u4eba\u4e0d\u6562\u60f3\u4e0b\u53bb\uff0c\u2026\u2026\u201d \u3000\u3000\u56de\u5934\u671b\u7f57\u5170\uff0c\u6c5f\u67ab\u5fae\u4e00\u6b20\u8eab\uff0c\u9053\uff1a\u201c\u591a\u8c22\uff01\u201d \u3000\u3000\u60f3\u53eb\u5ac2\u592b\u4eba\uff0c\u89c9\u7740\u4e0d\u5bf9\uff0c\u5e94\u8be5\u53eb\u5cb3\u6bcd\uff0c\u53c8\u53eb\u4e0d\u51fa\u53e3\uff0c\u6123\u4e86\u534a\u664c\uff0c\u624d\u60f3\u5230\u592b\u4eba\u4e24\u4e2a\u5b57\u3002 \u3000\u3000\u201c\u4e0d\u5ba2\u6c14\uff0c\u2026\u2026\u201d\u7f57\u5170\u5012\u662f\u5168\u4e0d\u5728\u610f\uff0c\u4e00\u8138\u6b63\u7ecf\u5730\u8bf4\u9053\uff1a \u3000\u3000\u201c\u4e24\u4e2a\u4e2b\u5934\u7684\u5251\u672f\u6839\u57fa\u5e94\u8be5\u5f88\u624e\u5b9e\uff0c\u4f46\u5979\u4eec\u751f\u6027\u592a\u4e60\u86ee\uff0c\u4e0d\u662f\u7ec3\u5251\u7684\u6750\u6599\uff0c\u6789\u8d39\u4e86\u5979\u4eec\u7236\u4eb2\u4e00\u756a\u5fc3\u8840\u3002\u201d \u3000\u3000\u201c\u5a18\uff01\u6211\u4eec\u5b66\u7684\u5f88\u7528\u5fc3\u554a\u3002\u201d\u96ea\u3001\u971c\u4e0d\u670d\u6c14\u5730\u8bf4\u3002 \u3000\u3000\u201c\u5509\uff01\u5251\u672f\u6613\u7ec3\uff0c\u5251\u9053\u96be\u5b66\uff0c\u7531\u672f\u5165\u9053\uff0c\u662f\u53e6\u4e00\u79cd\u5883\u754c\uff0c\u2026\u2026\u201d\u7f57\u5170\u76ee\u5149\u4e00\u63a0\u6c5f\u67ab\u548c\u5f20\u56db\u59d1\u53c8\u9053\uff1a\u201c\u5979\u4eec\u662f\u5929\u751f\u7684\u72d0\u95e8\u5f1f\u5b50\uff0c\u53ea\u6015\u8fd9\u4e00\u751f\u4e5f\u65e0\u6cd5\u7ec3\u5165\u5251\u9053\u4e2d\u53bb\u3002\u201d \u3000\u3000\u201c\u5927\u4e58\u5251\u9053\uff0c\u975e\u4e09\u4e94\u5341\u5e74\u7684\u5de5\u592b\uff0c\u96be\u5165\u4f73\u5883\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u4f46\u5c0f\u4e58\u672f\u53d8\uff0c\u5374\u6709\u901f\u6210\u7684\u6548\u679c\uff0c\u5929\u72d0\u6280\u827a\u4e2d\uff0c\u6709\u4e00\u5957\u4e03\u60c5\u5251\u5f0f\uff0c\u4e0d\u77e5\u9053\u592b\u4eba\u662f\u5426\u5b66\u8fc7\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\uff01\u2026\u2026\u201d\u7f57\u5170\u9053\uff1a\u201c\u542c\u90fd\u6ca1\u6709\u542c\u8fc7\uff01\u201d \u3000\u3000\u5979\u5fc3\u4e2d\u6709\u6240\u8b66\u60d5\uff0c\u5bf9\u6c5f\u67ab\u6709\u4e86\u5f88\u5927\u7684\u9694\u9602\uff0c\u8bf4\u8bdd\u7684\u8bed\u6c14\uff0c\u5c3d\u91cf\u51b7\u6de1\u4e4b\u5916\uff0c\u4e5f\u5c3d\u91cf\u907f\u5f00\u6c5f\u67ab\u7684\u76ee\u5149\u3002 \u3000\u3000\u201c\u4e5f\u8bb8\u662f\u592b\u4eba\u7ec3\u7684\u5927\u4e58\u5251\u6cd5\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u4e0d\u4fbf\u518d\u4ee5\u5929\u72d0\u4e03\u60c5\u5251\u5f0f\uff0c\u4ecb\u5165\u5176\u4e2d\uff0c\u2026\u2026\u201d \u3000\u3000\u76ee\u5149\u8f6c\u6ce8\u5230\u96ea\u3001\u971c\u4e8c\u5973\u8eab\u4e0a\uff0c\u63a5\u9053\uff1a\u201c\u4f60\u4eec\u5982\u679c\u613f\u610f\u7ec3\uff0c\u6211\u6559\u4f60\u4eec\u3002\u201d \u3000\u3000\u201c\u8c22\u8c22\u5566\uff01\u6c5f\u5927\u54e5\uff0c\u6885\u82b1\u59d0\u59d0\uff0c\u53ef\u4e0d\u53ef\u4ee5\u5b66\u5462\uff1f\u201d\u541f\u96ea\u9053\uff1a\u201c\u5979\u867d\u672a\u4e60\u8fc7\u5929\u72d0\u6b66\u529f\uff0c\u53ef\u4e5f\u662f\u5b66\u7684\u8fd1\u4f3c\u6280\u827a\uff0c\u53cd\u6b63\u90fd\u4e0d\u662f\u7384\u95e8\u6b63\u5b97\u3002\u201d \u3000\u3000\u201c\u5f53\u7136\u53ef\u4ee5\u5b66\uff0c\u4e0d\u8fc7\u3001\u5979\u672a\u4e60\u5929\u72d0\u5fc3\u6cd5\uff0c\u5b66\u8d77\u6765\uff0c\u53ef\u80fd\u8fdb\u5883\u6162\u4e00\u4e9b\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a \u3000\u3000\u201c\u4e8b\u5b9e\u4e0a\u5929\u72d0\u4e03\u60c5\u5251\u5f0f\uff0c\u5c31\u662f\u4eba\u6027\u4e2d\u7684\u4e03\u60c5\u516d\u6b32\uff0c\u628a\u4eba\u6027\u6b32\u5ff5\u6eb6\u5165\u4e86\u5251\u62db\u4e2d\u3002\u201d \u3000\u3000\u201c\u8fd9\u6837\u7684\u5251\u6cd5\uff0c\u6211\u5927\u6168\u53ef\u4ee5\u7ec3\u2026\u2026\u201d\u6885\u82b1\u8bf4\uff0c\u201c\u6211\u4e0d\u6c42\u901f\u6210\uff0c\u5b66\u4e00\u62db\u662f\u4e00\u62db\u4e86\u201d \u3000\u3000\u201c\u5927\u59d0\u5462\uff1f\u2026\u2026\u201d\u6c5f\u67ab\u51dd\u6ce8\u7740\u5f20\u56db\u59d1\u9053\uff1a\u201c\u4e03\u60c5\u5251\u5f0f\u914d\u4e0a\u4e94\u884c\u9041\u672f\uff0c\u5c06\u662f\u6700\u8be1\u5f02\u7684\u5251\u6cd5\u3002\u201d \u3000\u3000\u201c\u5b66\u662f\u60f3\u5b66\u5440\uff01\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u53cd\u6b63\u7269\u4ee5\u7c7b\u805a\u561b\uff01\u4e0d\u8fc7\uff0c\u53ea\u6015\u65f6\u95f4\u6765\u4e0d\u53ca\u4e86\uff0c\u4e00\u591c\u4e4b\u95f4\uff0c\u53c8\u80fd\u5b66\u5f97\u597d\u591a\uff1f\u4f55\u51b5\uff0c\u5927\u5bb6\u90fd\u8981\u597d\u597d\u5730\u5750\u606f\u4e00\u9635\uff0c\u624d\u80fd\u5e94\u4ed8\u5f3a\u654c\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u5148\u8ba9\u96ea\u3001\u971c\u8bd5\u8bd5\u3002\u4e03\u60c5\u5251\u62db\uff0c\u53ef\u4ee5\u5355\u4e00\u65bd\u7528\uff0c\u6211\u5c3d\u4e24\u4e2a\u65f6\u8fb0\u4e4b\u529b\uff0c\u5148\u4f20\u5979\u4eec\u4e00\u62db\u3002\u201d \u3000\u3000\u201c\u4e0d\u8981\u803d\u8bef\u4e86\u5979\u4eec\u5750\u606f\u7684\u65f6\u95f4\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u5145\u6c9b\u7684\u4f53\u529b\uff0c\u5bf9\u640f\u6740\u662f\u5f88\u91cd\u8981\u7684\u4e00\u4e2a\u6761\u4ef6\uff0c\u6211\u4eec\u4eba\u624b\u592a\u5c11\uff0c\u541f\u96ea\u3001\u541f\u971c\u662f\u4e24\u4e2a\u62c5\u5f53\u5927\u4efb\u7684\u4eba\u3002\u201d \u3000\u3000\u6c5f\u67ab\u70b9\u70b9\u5934\u3002 \u3000\u3000\u201c\u4f60\u4e5f\u8981\u4f11\u606f\uff0c\u8fd9\u4e00\u6218\uff0c\u4f60\u548c\u6b27\u9633\u592b\u5987\u53ca\u5927\u548c\u5c1a\uff0c\u662f\u6700\u4e3b\u8981\u7684\u529b\u91cf\u2026\u2026\u201d\u5f20\u56db\u59d1\u8bed\u91cd\u5fc3\u957f\u5730\u9053\uff1a\u201c\u6211\u5b8c\u5168\u65e0\u6cd5\u731c\u6d4b\u51fa\u654c\u4eba\u6709\u591a\u5c11\u7684\u9ad8\u624b\u4e34\u9635\uff1f\u53ea\u6709\u5c3d\u91cf\u5229\u7528\u6211\u4eec\u7684\u7279\u957f\uff0c\u7b97\u662f\u95ed\u7740\u773c\u775b\u6253\u8fd9\u4e00\u4f4d\uff0c\u6211\u5fc3\u4e2d\u597d\u60f6\u6050\u2026\u2026\u201d \u3000\u3000\u201c\u522b\u592a\u62c5\u5fc3\uff0c\u6211\u4eec\u867d\u4e0d\u77e5\u5f7c\uff0c\u4f46\u5374\u5145\u5206\u77e5\u5df1\uff0c\u5e94\u8be5\u6709\u516d\u6210\u4ee5\u4e0a\u7684\u80dc\u7b97\u2026\u2026\u201d\u6c5f\u67ab\u5fae\u5fae\u4e00\u7b11\u53c8\u9053\uff1a \u3000\u3000\u201c\u5c0f\u5f1f\u4f1a\u5728\u8fd9\u4e00\u6218\u4e2d\uff0c\u5168\u529b\u65bd\u4e3a\uff0c\u7edd\u4e0d\u8ba9\u5927\u59d0\u5931\u671b\u3002\u201d \u3000\u3000\u770b\u6c5f\u67ab\u8bf4\u7684\u8f7b\u677e\uff0c\u5f20\u56db\u59d1\u6101\u7709\u7a0d\u5c55\uff0c\u9053\uff1a\u201c\u8981\u4f20\u5251\u6cd5\uff0c\u5c31\u5feb\u4e9b\u5f00\u59cb\uff0c\u4e8c\u66f4\u4e4b\u524d\uff0c\u6211\u8981\u6765\u5e26\u8d70\u4e8c\u5973\uff0c\u903c\u5979\u4eec\u5750\u606f\u517b\u795e\u3002\u201d \u3000\u3000\u6c5f\u67ab\u70b9\u70b9\u5934\uff0c\u5e26\u7740\u96ea\u3001\u971c\u4e8c\u5973\uff0c\u884c\u5165\u623f\u4e2d\u3002 \u3000\u3000\u201c\u5f20\u59e8\uff0c\u6211\u60f3\u56de\u623f\u53bb\u5750\u606f\u4e00\u4e0b\u2026\u2026\u201d\u6885\u82b1\u9053\uff1a\u201c\u60f3\u60f3\u5bf9\u654c\u4e4b\u7b56\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u6885\u82b1\uff0c\u6211\u77e5\u9053\u4f60\u7684\u6b66\u529f\u548c\u624d\u6167\uff0c\u5728\u540c\u95e8\u4e4b\u4e2d\uff0c\u51fa\u7c7b\u62d4\u8403\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u4ed4\u7ec6\u7684\u5e2e\u6211\u7b56\u5212\u4e00\u4e0b\uff0c\u8981\u5982\u4f55\u624d\u80fd\u5bf9\u4ed8\u4ed6\u4eec\u7684\u56f4\u653b\uff01\u201d \u3000\u3000\u201c\u6211\u4f1a\u5c3d\u529b\u2026\u2026\u201d\u6885\u82b1\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u6211\u90a3\u4e9b\u5e08\u59d0\u3001\u5e08\u59b9\uff0c\u827a\u51fa\u540c\u95e8\uff0c\u4e00\u5bf9\u4e00\u6211\u8c01\u4e5f\u4e0d\u6015\uff0c\u4f46\u5982\u5979\u4eec\u4e09\u4e94\u4eba\u8054\u624b\u56f4\u653b\u6211\uff0c\u6211\u5c31\u5f88\u96be\u62db\u67b6\u4e86\u3002\u201d \u3000\u3000\u201c\u6211\u4eec\u4e0d\u4f1a\u548c\u4ed6\u4eec\u6349\u5bf9\u53ae\u6740\u2026\u2026\u201d\u5f20\u56db\u59d1\u53cc\u76ee\u4e2d\u95ea\u51fa\u6740\u673a\uff0c\u9053\uff1a \u3000\u3000\u201c\u6211\u4eec\u4e5f\u91c7\u7528\u622a\u6740\u3001\u56f4\u88ad\uff0c\u4f60\u3001\u6211\u548c\u96ea\u3001\u971c\u4e8c\u5973\u662f\u4e00\u7ec4\uff0c\u4e00\u8d77\u884c\u52a8\uff0c\u4ee5\u6697\u88ad\u6740\u4eba\u4e3a\u4e3b\uff0c\u660e\u5929\uff0c\u6211\u4eec\u4e00\u9762\u5bf9\u654c\uff0c\u4e00\u9762\u4f20\u6388\u4f60\u5929\u72d0\u6b66\u529f\u548c\u4e94\u884c\u9041\u672f\uff0c\u5c31\u6211\u6240\u77e5\uff0c\u5728\u640f\u6740\u4e2d\u4f20\u6388\u7684\u6280\u827a\uff0c\u73b0\u5b66\u73b0\u5356\uff0c\u4e00\u6b21\u5c31\u53ef\u4ee5\u5b66\u4f1a\uff0c\u800c\u4e14\u80fd\u5c3d\u5f97\u5965\u5999\u3002\u201d \u3000\u3000\u201c\u591a\u8c22\u5f20\u59e8\uff01\u591a\u8c22\u5f20\u59e8\uff01\u2026\u2026\u201d\u6885\u82b1\u9053\uff1a\u201c\u6211\u4f1a\u5168\u529b\u62a5\u6548\uff0c\u6b7b\u800c\u540e\u5df2\u3002\u201d \u3000\u3000\u201c\u4e0d\u8bb8\u80e1\u8bf4\uff0c\u5f20\u59e8\u820d\u4e0d\u5f97\u8ba9\u4f60\u6b7b\uff0c\u6c5f\u67ab\u66f4\u820d\u4e0d\u5f97\u4f60\u6b7b\u2026\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u2026\u2026\u6211\u597d\u5feb\u4e50\uff0c\u597d\u9ad8\u5174\uff01\u6211\u7b2c\u4e00\u6b21\u611f\u53d7\u5230\uff0c\u4eba\u4e0e\u4eba\u4e4b\u95f4\uff0c\u7adf\u6709\u5982\u6b64\u7684\u53cb\u7231\u3001\u6e29\u6696\u2026\u2026\u201d\u6885\u82b1\u4e3e\u624b\u64e6\u62ed\u7740\u8138\u4e0a\u7684\u6cea\u75d5\uff0c\u4f46\u5374\u63a9\u4e0d\u4f4f\u8138\u4e0a\u6cdb\u8d77\u7684\u7b11\u5bb9\u3002 \u3000\u3000\u5979\u81ea\u61c2\u4e8b\u4ee5\u6765\uff0c\u5c31\u5728\u5b64\u72ec\u65e0\u4f9d\u3001\u51b7\u9177\u3001\u673a\u8bc8\u7684\u73af\u5883\u4e2d\u6210\u957f\uff0c\u90a3\u91cc\u4f7f\u5979\u65e9\u719f\uff0c\u4f7f\u5979\u5b66\u4f1a\u4e86\u4fdd\u62a4\u81ea\u5df1\uff0c\u51fa\u5356\u522b\u4eba\u7684\u5404\u79cd\u65b9\u6cd5\u3002 \u3000\u3000\u4eba\u4e0e\u4eba\u4e4b\u95f4\uff0c\u5b8c\u5168\u662f\u53e3\u871c\u8179\u5251\uff0c\u76f8\u4e92\u8bc8\u9a97\u3001\u5229\u7528\uff0c\u90a3\u91cc\u53d7\u5230\u8fc7\u5982\u6b64\u7684\u8bda\u631a\u7167\u987e\uff0c\u987f\u7136\u771f\u60c5\u6fc0\u8361\uff0c\u5b7a\u6155\u5f25\u751f\uff0c\u6068\u4e0d\u5f97\u6251\u5165\u5f20\u56db\u59d1\u7684\u6000\u4e2d\uff0c\u653e\u58f0\u5927\u54ed\u4e00\u573a\uff0c\u628a\u79ef\u5b58\u5341\u51e0\u5e74\u7684\u5e7d\u5e7d\u6101\u6000\uff0c\u53d1\u6cc4\u51fa\u6765\u3002 \u3000\u3000\u4f46\u5979\u5fcd\u4f4f\u4e86\u3002 \u3000\u3000\u591a\u5e74\u7684\u78e8\u70bc\uff0c\u4f7f\u5979\u5177\u6709\u4e86\u5f02\u4e8e\u5e38\u4eba\u7684\u81ea\u6211\u514b\u5236\u80fd\u529b\u3002 \u3000\u3000\u5f20\u56db\u59d1\u8f7b\u8f7b\u628a\u6885\u82b1\u63fd\u5165\u6000\u4e2d\uff0c\u6148\u7231\u7684\u8bf4\uff1a\u201c\u6885\u82b1\uff0c\u6211\u4e0d\u6562\u4fdd\u8bc1\uff0c\u6211\u4eec\u90fd\u80fd\u6d3b\u4e0b\u53bb\uff0c\u4f46\u53ea\u8981\u6211\u4eec\u90fd\u6d3b\u7740\uff0c\u6211\u4f1a\u5c3d\u529b\u7167\u987e\u4f60\uff01\u201d \u3000\u3000\u201c\u6211\u4e0d\u6015\u6b7b\u2026\u2026\u201d\u4fae\u82b1\u9053\uff1a\u201c\u4f46\u6211\u4e0d\u613f\u88ab\u4ed6\u4eec\u9a97\u6b7b\u3001\u903c\u6b7b\uff0c\u6211\u8981\u5fc3\u7518\u60c5\u613f\u7684\u4e3a\u6b66\u6797\u6b63\u4e49\u6218\u6b7b\uff0c\u4e3a\u6c5f\u90ce\u800c\u6b7b\uff0c\u4e3a\u5f20\u59e8\u800c\u6b7b\uff01\u201d \u3000\u3000\u201c\u6211\u660e\u767d\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u6211\u4eec\u90fd\u4e0d\u6015\u6b7b\uff0c\u541f\u96ea\u3001\u541f\u971c\uff0c\u53ea\u4e0d\u8fc7\u521a\u521a\u61c2\u5f97\u4eba\u751f\uff0c\u4f46\u5979\u4eec\u5374\u6709\u820d\u8eab\u6740\u654c\u7684\u51b3\u5fc3\uff0c\u4e0d\u8fc7\uff0c\u6211\u4eec\u4e0d\u80fd\u8f7b\u6613\u5730\u53bb\u6b7b\u3002 \u3000\u3000\u6211\u4eec\u8981\u5168\u529b\u6c42\u751f\uff0c\u80fd\u4e0d\u6b7b\uff0c\u5c31\u4e0d\u8981\u6b7b\uff0c\u4f60\u4eec\u6d3b\u7740\uff0c\u624d\u80fd\u4e3a\u6c5f\u67ab\u5206\u62c5\u8d23\u4efb\uff0c\u6240\u4ee5\uff0c\u4e0d\u8981\u901e\u5339\u592b\u4e4b\u52c7\uff0c\u4e0d\u8981\u4e89\u4e00\u65f6\u4e4b\u6c14\uff0c\u61c2\u6211\u7684\u610f\u601d\u5417\uff1f\u201d \u3000\u3000\u201c\u6211\u61c2\uff0c\u8c22\u8c22\u5f20\u59e8\u7684\u6307\u6559\u2026\u2026\u201d \u3000\u3000\u6885\u82b1\u5e26\u7740\u5feb\u4e50\u7684\u5fae\u7b11\uff0c\u884c\u51fa\u5927\u5385\u3002 \u3000\u3000\u770b\u5385\u4e2d\u518d\u65e0\u522b\u4eba\uff0c\u7f57\u5170\u624d\u4f4e\u58f0\u9053\uff1a\u201c\u56db\u59d1\uff0c\u6211\u5f88\u4e25\u8083\u5427\uff01\u5bf9\u4ed6\u662f\u5b8c\u5168\u4e0d\u5047\u8bcd\u8272\uff01\u201d \u3000\u3000\u201c\u5bf9\u8c01\u5440\uff1f\u201d \u3000\u3000\u201c\u4f60\u660e\u77e5\u6545\u95ee\u554a\uff01\u6211\u53ef\u662f\u5168\u7167\u7740\u4f60\u7684\u610f\u601d\u884c\u4e8b\u2026\u2026\u201d\u7f57\u5170\u9053\uff1a\u201c\u4ee5\u540e\uff0c\u6211\u5c31\u8981\u4ee5\u4eca\u65e5\u8fd9\u79cd\u795e\u60c5\uff0c\u5bf9\u5f85\u6c5f\u67ab\uff1f\u201d \u3000\u3000\u5f20\u56db\u59d1\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u592a\u505a\u4f5c\u4e86\uff0c\u7f57\u5170\uff0c\u50cf\u8fc7\u53bb\u4e00\u6837\uff0c\u53ea\u662f\u4e0d\u8981\u5bf9\u4ed6\u7b11\u5c31\u884c\u4e86\u3002\u201d \u3000\u3000\u6c5f\u67ab\u5f00\u59cb\u4f20\u6388\u96ea\u3001\u971c\u4e8c\u5973\u7684\u5251\u6cd5\u3002 \u3000\u3000\u7537\u4eba\u65bd\u5c55\u8fd9\u4e03\u60c5\u5251\u62db\uff0c\u5b9e\u5728\u662f\u96be\u767b\u5927\u96c5\u4e4b\u5802\uff0c\u5ffd\u602a\u6c5f\u67ab\u8981\u5173\u4e0a\u623f\u95e8\u4f20\u6388\u4e86\u3002 \u3000\u3000\u53ea\u8981\u542c\u4e00\u5f0f\u5251\u62db\u540d\u79f0\uff0c\u4f60\u5c31\u660e\u767d\u7537\u4eba\u5f88\u96be\u65bd\u7528\u8fd9\u5957\u5251\u6cd5\u514b\u654c\uff0c\u867d\u7136\uff0c\u8fd9\u5957\u5251\u6cd5\u5341\u5206\u6709\u7528\u3002 \u3000\u3000\u7b2c\u4e00\u62db\u53eb\u201c\u5e72\u5a07\u767e\u5a9a\u52fe\u9b42\u6765\u3002\u201d \u3000\u3000\u8fd9\u4e00\u62db\u8981\u83b2\u6b65\u7ec6\u788e\uff0c\u67f3\u8170\u6b3e\u6446\uff0c\u773c\u6ce2\u6d41\u52a8\uff0c\u98ce\u60c5\u4e07\u79cd\uff0c\u767e\u5a9a\u5e76\u81f3\uff0c\u5343\u5a07\u6a2a\u751f\uff0c\u624d\u80fd\u4f7f\u4eba\u4e00\u89c1\u60ca\u8273\uff0c\u96be\u9501\u5fc3\u733f\u610f\u9a6c\uff0c\u5a9a\u6001\u5a07\u7b11\u4e2d\uff0c\u5229\u5251\u5df2\u7a7f\u900f\u654c\u4eba\u4e4b\u5fc3\u3002 \u3000\u3000\u5f53\u7136\uff0c\u8fd9\u79cd\u5251\u6cd5\uff0c\u6ca1\u6709\u4e60\u8fc7\u5929\u72d0\u6280\u827a\u7684\u4eba\uff0c\u5f88\u96be\u65bd\u5c55\uff0c\u5c31\u662f\u7ec3\u8fc7\u5927\u72d0\u5fc3\u6cd5\u7684\u4eba\uff0c\u4e5f\u4e0d\u662f\u90fd\u80fd\u591f\u65bd\u5c55\u51fa\u6765\u3002 \u3000\u3000\u6240\u4ee5\uff0c\u6c5f\u67ab\u6559\u7684\u5f88\u8f9b\u82e6\u3002 \u3000\u3000\u4e00\u4e2a\u5927\u7537\u5b50\uff0c\u8981\u6446\u51fa\u5343\u822c\u5a9a\u6001\uff0c\u5b9e\u5728\u662f\u76f8\u5f53\u56f0\u96be\u3002 \u3000\u3000\u4f46\u96ea\u3001\u971c\u4e8c\u5973\u5b66\u8d77\u6765\uff0c\u5374\u662f\u5982\u884c\u4e91\u6d41\u6c34\u4e00\u822c\uff0c\u5f88\u5feb\u5c31\u9752\u51fa\u4e8e\u84dd\uff0c\u5c24\u5176\u662f\u4e60\u7ec3\u8fc7\u5929\u72d0\u6b66\u529f\u7684\u7f8e\u5973\uff0c\u7ec3\u8d77\u6765\u5f97\u5fc3\u5e94\u624b\uff0c\u5f88\u5feb\u5c31\u767b\u5802\u5165\u5ba4\u3002 \u3000\u3000\u541f\u96ea\u638f\u51fa\u4e00\u65b9\u7ee2\u5e15\uff0c\u64e6\u62ed\u53bb\u6c5f\u67ab\u7751\u4e0a\u7684\u6c57\u6c34\uff0c\u7b11\u9053\uff1a\u201c\u5f88\u5bb9\u6613\u561b\uff01\u4f60\u600e\u4e48\u6559\u7684\u8fd9\u4e48\u8f9b\u82e6\uff1f\u201d \u3000\u3000\u201c\u827a\u6709\u4e13\u5c5e\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5929\u72d0\u95e8\u4e2d\u7684\u6280\u827a\uff0c\u6709\u5f88\u591a\u662f\u4e0d\u9002\u5b9c\u7537\u4eba\u7ec3\u7684\uff01\u201d \u3000\u3000\u541f\u971c\u62c9\u7740\u6c5f\u67ab\u4e00\u53cc\u624b\uff0c\u9053\uff1a\u201c\u6c5f\u5927\u54e5\uff0c\u5750\u4e0b\u6765\uff0c\u4f11\u606f\u4e00\u4e0b\u5427\uff0c\u770b\u5230\u4f60\u7d2f\u51fa\u4e00\u8eab\u5927\u6c57\uff0c\u8ba9\u6211\u60f3\u5230\u4f60\u53d7\u4f24\u540e\u7684\u60c5\u5f62\uff0c\u6211\u548c\u59d0\u59d0\u90a3\u79cd\u98df\u4e0d\u77e5\u5473\u7684\u7126\u8651\uff0c\u6467\u809d\u88c2\u5fc3\u7684\u75db\u82e6\uff0c\u6bd4\u4f60\u4f20\u6211\u4eec\u5251\u62db\u7684\u8f9b\u82e6\uff0c\u8d85\u8fc7\u4e86\u4f55\u81f3\u5343\u767e\u4e07\u500d\uff0c\u5f20\u59e8\u8bf4\u7684\u4e0d\u9519\uff0c\u4f60\u53ef\u4ee5\u907f\u8fc7\u90a3\u4e9b\u4eba\u88ad\u51fb\u7684\uff0c\u4f46\u4f60\u5374\u53d7\u4e86\u4f24\uff0c\u4e3a\u4ec0\u4e48\u5462\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u5446\u5446\u7684\u671b\u4e86\u541f\u971c\u4e00\u9635\uff0c\u53c8\u8f6c\u5934\u770b\u7740\u541f\u96ea\uff0c\u53d1\u89c9\u56db\u53cc\u7075\u6d3b\u7684\u5927\u773c\u775b\u4e2d\uff0c\u653e\u5c04\u51fa\u70bd\u70c8\u7684\u60c5\u7130\uff0c\u7078\u7684\u8ba9\u4eba\u5fc3\u75bc\u2026\u2026\u90a3\u662f\u7531\u5185\u5fc3\u4e2d\u70b9\u71c3\u8d77\u7684\u7231\u604b\u4e4b\u706b\u3002 \u3000\u3000\u4f46\u60c5\u7231\u7eaf\u51c0\uff0c\u4e0d\u542b\u4e00\u4e1d\u6b32\u5ff5\u3002 \u3000\u3000\u6c5f\u67ab\u957f\u957f\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u53ea\u4e0d\u8fc7\u5341\u4e94\u516d\u5c81\u5440\uff01\u800c\u4e14\u751f\u957f\u5728\u6df1\u5c71\u5e7d\u8c37\u4e4b\u4e2d\uff0c\u771f\u7684\u61c2\u5f97\u4ec0\u4e48\u662f\u60c5\u7231\u5417\uff1f\u201d \u3000\u3000\u541f\u96ea\u9053\uff1a\u201c\u5e94\u8be5\u662f\u4e0d\u61c2\u7684\uff0c\u53ef\u662f\u6211\u4eec\u7ec3\u4e86\u5929\u72d0\u6b66\u529f\uff0c\u96be\u9053\u4f60\u4e0d\u77e5\u9053\uff0c\u5929\u72d0\u6b66\u529f\uff0c\u4f1a\u8ba9\u4eba\u65e9\u719f\u5417\uff1f\u201d \u3000\u3000\u201c\u6c5f\u5927\u54e5\uff0c\u4f60\u7684\u5f62\u8c61\u3001\u5f71\u5b50\uff0c\u65e9\u5df2\u5370\u5728\u4e86\u6211\u95ea\u5fc3\u4e2d\uff0c\u548c\u6211\u4eec\u4e00\u8d77\u6210\u957f\uff0c\u2026\u2026\u201d\u541f\u971c\u7b11\u9053\uff1a\u201c\u4f60\u597d\u50cf\u662f\u6211\u4eec\u7684\u4e00\u90e8\u5206\uff0c\u4e5f\u8bb8\u6211\u4eec\u662f\u4f60\u7684\u4e00\u90e8\u5206\uff0c\u662f\u4e0d\u662f\u5929\u72d0\u6b66\u529f\uff0c\u5728\u6211\u4eec\u5fc3\u7075\u4e0a\u64ad\u79cd\u4e86\u4ec0\u4e48\uff1f\u4e0d\u81ea\u4e3b\u7684\u4e3b\u5bb0\u4e86\u6211\u4eec\uff0c\u4e00\u89c1\u4f60\uff0c\u5c31\u597d\u60f3\u62e5\u62b1\u4f60\u2026\u2026\u201d \u3000\u3000\u5979\u8bf4\u62b1\u5c31\u62b1\uff0c\u4e00\u4e0b\u5b50\u6251\u5165\u4e86\u6c5f\u67ab\u7684\u6000\u4e2d\u3002 \u3000\u3000\u59d0\u59d0\u8ddf\u7740\u59b9\u59b9\u95f9\uff0c\u4e00\u9635\u75af\u72c2\u6492\u5a07\uff0c\u6c5f\u67ab\u90a3\u91cc\u80fd\u62db\u67b6\u5f97\u4f4f\uff0c\u6025\u6025\u63a8\u5f00\u4e8c\u5973\uff0c\u9053\uff1a\u201c\u5feb\u4e9b\u8d70\u4e86\uff0c\u4f60\u4eec\u7684\u5f20\u59e8\u5c31\u8981\u6765\u63a5\u4f60\u4eec\u4e86\u3002\u201d \u3000\u3000\u201c\u600e\u4e48\uff1f\u2026\u2026\u201d\u541f\u96ea\u9053\uff1a\u201c\u53ea\u5b66\u4e00\u62db\u5251\u6cd5\uff0c\u5982\u4f55\u80fd\u514b\u654c\u5236\u80dc\u3002\u201d \u3000\u3000\u201c\u914d\u5408\u4e94\u884c\u9041\u672f\uff0c\u8fd9\u4e00\u62db\u5251\u6cd5\uff0c\u5c31\u53ef\u4ee5\u4f7f\u4e0d\u5c11\u5f3a\u654c\u6388\u9996\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u53bb\u5411\u5f20\u59e8\u8ba8\u6559\uff0c\u5982\u4f55\u624d\u80fd\u628a\u8fd9\u62db\u5251\u6cd5\u6eb6\u5165\u4e94\u884c\u9041\u672f\u4e4b\u4e2d\u3002\u201d \u3000\u3000\u541f\u96ea\u3001\u541f\u971c\u5f00\u59cb\u6574\u7406\u8863\u670d\u3001\u4e71\u53d1\uff0c\u8fd9\u4e00\u9635\u5b09\u95f9\u4e0b\u6765\uff0c\u4e8c\u4e2a\u5c0f\u72d0\u5973\uff0c\u4e5f\u5f04\u7684\u8863\u670d\u8936\u76b1\uff0c\u53d1\u4e71\u9497\u6a2a\u3002 \u3000\u3000\u201c\u6c5f\u5927\u54e5\u2026\u2026\u201d\u541f\u96ea\u7a81\u7136\u63d0\u51fa\u4e86\u4e00\u4e2a\u95ee\u9898\uff0c\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u628a\u83f1\u82b1\u6349\u56de\u6765\uff0c\u5979\u597d\u50cf\u77e5\u9053\u5f88\u591a\u79d8\u5bc6\u5440\uff1f\u201d \u3000\u3000\u201c\u5e08\u7236\u5df2\u7ecf\u51fa\u9a6c\u4e86\uff0c\u83f1\u82b1\u8fd8\u4f1a\u6709\u4ec0\u4e48\u4ef7\u503c\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u518d\u8bf4\uff0c\u4f60\u4eec\u771f\u7684\u5e0c\u671b\u6211\u627e\u5f88\u591a\u7684\u5973\u4eba\u56de\u6765\u5417\uff1f\u201d \u3000\u3000\u541f\u96ea\u504f\u5934\u60f3\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u4e0d\u662f\u5f88\u5e0c\u671b\uff0c\u4e0d\u8fc7\uff0c\u4eba\u591a\u4e86\u597d\u73a9\uff0c\u6240\u4ee5\uff0c\u4e5f\u4e0d\u662f\u5f88\u53cd\u5bf9\u4e86\u3002\u201d \u3000\u3000\u201c\u6c5f\u5927\u54e5\uff0c\u4f60\u4e5f\u7ec3\u8fc7\u5929\u72d0\u6b66\u529f\uff0c\u597d\u50cf\u5929\u6d3e\u6b66\u529f\u4e2d\uff0c\u53ef\u4ee5\u4f7f\u4e00\u4e2a\u4eba\u5fc3\u80f8\u6269\u5927\uff0c\u51cf\u5c11\u5ac9\u5fcc\u2026\u2026\u201d\u541f\u971c\u9053\uff1a\u201c\u5f20\u59e8\u4e5f\u5e38\u5e38\u95ee\u6211\u4eec\uff0c\u6c5f\u5927\u54e5\u548c\u5f88\u591a\u5973\u4eba\u4ea4\u5f80\uff0c\u6211\u4eec\u662f\u4e0d\u662f\u5f88\u96be\u8fc7\uff0c\u6211\u4eec\u544a\u8bc9\u5f20\u59e8\uff0c\u4e0d\u96be\u8fc7\u3002\u201d \u3000\u3000\u6c5f\u67ab\u70b9\u70b9\u5934\uff0c\u5fc3\u4e2d\u5fd6\u9053\uff1a\u201c\u5929\u72d0\u592b\u4eba\u5e74\u8f7b\u65f6\uff0c\u7f8e\u8c8c\u597d\u80dc\uff0c\u5ac9\u5fc3\u5947\u91cd\uff0c\u4f46\u4e2d\u5e74\u4e4b\u540e\uff0c\u5374\u53c8\u5904\u5904\u4e3a\u4eba\u8bbe\u60f3\uff0c\u6536\u653e\u81ea\u5982\uff0c\u96be\u9053\u5979\u771f\u7684\u5df2\u628a\u5316\u89e3\u5ac9\u5fcc\u4e4b\u5fc3\uff0c\u6eb6\u5165\u4e86\u6b66\u529f\u4e4b\u4e2d\uff0c\u8fd9\u4e24\u4e2a\u4e2b\u5934\u75af\u72c2\u7684\u8ffd\u6c42\u6b22\u4e50\uff0c\u5374\u4e00\u76f4\u4e0d\u751f\u6b32\u5ff5\uff0c\u5b9e\u662f\u5927\u80cc\u5e38\u60c5\u2026\u2026\u679c\u771f\u5982\u6b64\uff0c\u4e0a\u4e58\u7684\u6b66\u529f\u5fc3\u6cd5\uff0c\u771f\u7684\u80fd\u6539\u53d8\u4eba\u7684\u5929\u6027\u4e86\u2026\u2026\u3002\u201d \u3000\u3000\u53ea\u542c\u541f\u96ea\u5a07\u58f0\u7b11\u9053\uff1a\u201c\u6c5f\u5927\u54e5\uff0c\u4e0d\u8981\u62c5\u5fc3\uff0c\u6211\u4eec\u4e0d\u4f1a\u8ddf\u4f60\u4e89\u5435\uff0c\u6211\u4eec\u80fd\u63a5\u7eb3\u6885\u82b1\u59d0\u59d0\uff0c\u4e5f\u80fd\u63a5\u7eb3\u9752\u51e4\uff0c\u5c06\u6765\uff0c\u6211\u4eec\u76d6\u4e00\u5e62\u5f88\u5927\u7684\u623f\u5b50\uff0c\u5927\u5bb6\u4f4f\u5728\u4e00\u8d77\uff0c\u6bcf\u5929\u4e89\u5947\u6597\u8273\uff0c\u535a\u541b\u4e00\u7b11\uff0c\u90a3\u4e00\u5b9a\u597d\u73a9\u7684\u5f88\u3002\u201d \u3000\u3000\u53e3\u6c14\u4e4b\u95f4\uff0c\u7adf\u4f3c\u4ee5\u4e00\u4e2a\u59bb\u5b50\u7684\u8eab\u4efd\u81ea\u5c45\u3002 \u3000\u3000\u6c5f\u67ab\u53ea\u6709\u82e6\u7b11\u3002 \u3000\u3000\u541f\u971c\u62c9\u8d77\u59d0\u59d0\u4e00\u53ea\u624b\uff0c\u9053\uff1a\u201c\u6c5f\u5927\u54e5\uff0c\u4f60\u597d\u597d\u4f11\u606f\uff0c\u6211\u4eec\u53bb\u627e\u5f20\u59e8\u4e86\u3002\u201d \u3000\u3000\u6253\u5f00\u5ba4\u95e8\uff0c\u643a\u624b\u800c\u53bb\u3002 \u3000\u3000\u671b\u7740\u4e8c\u5973\u8fdc\u53bb\u7684\u80cc\u5f71\uff0c\u6c5f\u67ab\u8bf4\u4e0d\u51fa\u662f\u82e6\u662f\u751c\u3002 \u3000\u3000\u4ed6\u60f3\u89c1\u4e8c\u5973\uff0c\u5979\u4eec\u592a\u7f8e\u4e86\uff0c\u4e00\u98a6\u4e00\u7b11\uff0c\u65e0\u4e0d\u52a8\u4eba\u3002 \u3000\u3000\u4f46\u4ed6\u4e5f\u6015\u89c1\u4e8c\u5973\uff0c\u5979\u4eec\u653e\u7eb5\u53d6\u4e50\uff0c\u5374\u53c8\u662f\u70b9\u5230\u4e3a\u6b62\uff0c\u628a\u6c5f\u67ab\u6298\u817e\u7684\u82e6\u4e0d\u582a\u8a00\u3002 \u3000\u3000\u53ea\u542c\u4e03\u5b9d\u548c\u5c1a\u7684\u58f0\u97f3\uff0c\u4f20\u5165\u8033\u9645\uff0c\u9053\uff1a\u201c\u83f1\u82b1\u59d1\u5a18\u8981\u89c1\u54ea\u4e00\u4f4d\uff1f\u201d \u3000\u3000\u83f1\u82b1\u9053\uff1a\u201c\u4e0d\u7ba1\u90a3\u4e00\u4f4d\uff1f\u53ea\u8981\u80fd\u591f\u4f5c\u4e3b\u90fd\u884c\u3002\u201d \u3000\u3000\u201c\u97e9\u526f\u603b\u7ba1\u5982\u4f55\uff1f\u2026\u2026\u201d\u5927\u548c\u5c1a\u9053\uff1a\u201c\u4ed6\u662f\u6700\u80fd\u4f5c\u4e3b\u7684\u4eba\u4e86\u3002\u201d \u3000\u3000\u201c\u97e9\u9738\u5c31\u7b97\u8fdc\u6ca1\u6709\u6b7b\uff0c\u4e5f\u8be5\u5944\u5944\u4e00\u606f\u4e86\u2026\u2026\u201d\u83f1\u82b1\u9053\uff1a\u201c\u89c1\u5230\u4ed6\uff0c\u6709\u4ec0\u4e48\u7528\uff1f\u201d \u3000\u3000\u201c\u597d\u53eb\u59d1\u5a18\u5f97\u77e5\uff0c\u97e9\u526f\u603b\u7ba1\u5065\u5eb7\u7684\u5f88\uff0c\u2026\u2026\u201d\u5927\u548c\u5c1a\u7b11\u9053\uff1a\u201c\u83f1\u82b1\u59d1\u5a18\u5982\u662f\u4e0d\u76f8\u4fe1\uff0c\u4f55\u4e0d\u6c42\u8bc1\u4e00\u4e0b\u5462\uff1f\u201d \u3000\u3000\u83f1\u82b1\u9053\uff1a\u201c\u6211\u4e0d\u76f8\u4fe1\u88ab\u65b9\u5999\u6bd2\u9488\u523a\u4e2d\u7684\u4eba\uff0c\u8fd8\u80fd\u6d3b\u5f97\u4e0b\u53bb\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u5df2\u85c9\u4e24\u4eba\u8c08\u8bdd\u7684\u673a\u4f1a\uff0c\u6574\u7406\u597d\u88ab\u8925\uff0c\u6234\u4e0a\u4e86\u4eba\u76ae\u9762\u5177\u3002 \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u63a8\u5f00\u623f\u95e8\uff0c\u83f1\u82b1\u7acb\u523b\u62a2\u5148\u800c\u5165\u3002 \u3000\u3000\u53ea\u89c1\u97e9\u9738\u5750\u5728\u4e00\u5f20\u6728\u6905\u4e0a\uff0c\u53cc\u76ee\u4e2d\u795e\u5149\u5145\u8db3\uff0c\u679c\u7136\u4e0d\u50cf\u53d7\u4f24\u7684\u6837\u5b50\u3002 \u3000\u3000\u83f1\u82b1\u53cc\u76ee\u76ef\u6ce8\u5728\u6c5f\u67ab\u7751\u4e0a\uff0c\u77a7\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u4f60\u771f\u7684\u662f\u97e9\u9738\u5417\uff1f\u201d \u3000\u3000\u201c\u5728\u4e0b\u662f\u4e0d\u662f\u97e9\u9738\uff0c\u59d1\u5a18\u53c8\u5982\u4f55\u6c42\u8bc1\u5462\uff1f\u201d \u3000\u3000\u201c\u4e0d\u7528\u6c42\u8bc1\u4e86\uff0c\u4f60\u662f\u771f\u7684\u97e9\u9738\u2026\u2026\u201d\u83f1\u82b1\u9053\uff1a\u201c\u6211\u5df2\u4ece\u58f0\u97f3\u4e2d\u5206\u8fa8\u51fa\u6765\uff0c\u867d\u7136\u97e9\u9738\u53ea\u662f\u4e00\u4e2a\u865a\u6784\u7684\u8eab\u4efd\u2026\u2026\u201d \u3000\u3000\u201c\u83f1\u82b1\u59d1\u5a18\u2026\u2026\u201d\u5728\u56db\u5b63\u82b1\u5a62\u4e2d\uff0c\u6c5f\u67ab\u5bf9\u83f1\u82b1\u7684\u5370\u8c61\u6700\u574f\uff0c\u51b7\u51b7\u63a5\u9053\uff1a\u201c\u627e\u6211\u6709\u4ec0\u4e48\u91cd\u8981\u7684\u4e8b\uff0c\u53ef\u4ee5\u8bf4\u4e86\uff01\u201d \u3000\u3000\u201c\u53d6\u4e0b\u9762\u5177\uff0c\u8ba9\u6211\u77a7\u77a7\u4f60\u771f\u6b63\u7684\u9762\u76ee\uff0c\u51ed\u4ec0\u4e48\u80fd\u628a\u6885\u82b1\u8ff7\u7684\u60f0\u7518\u6548\u6b7b\u2026\u2026\u201d\u83f1\u82b1\u8138\u4e0a\u4e00\u7247\u4e0d\u5c51\u4e4b\u8272\uff0c\u63a5\u9053\uff1a\u201c\u6211\u5e26\u6765\u4e86\u51b3\u6218\u7684\u5730\u70b9\u3001\u65f6\u95f4\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u544a\u8bc9\u6211\uff0c\u4f60\u5c31\u53ef\u4ee5\u8d70\u4e86\uff0c\u6211\u4e0d\u4f1a\u96be\u4e3a\u4f60\uff01\u201d \u3000\u3000\u201c\u4e24\u56fd\u4ea4\u6218\uff0c\u4e0d\u65a9\u6765\u4f7f\u2026\u2026\u201d\u83f1\u82b1\u9053\uff1a\u201c\u6740\u4e86\u6211\uff0c\u662f\u4f60\u6ca1\u98ce\u5ea6\uff0c\u4e0d\u8fc7\uff0c\u5728\u6211\u6ca1\u6709\u8bf4\u51fa\u65f6\u95f4\u3001\u5730\u70b9\u4e4b\u524d\uff0c\u6211\u8981\u4e00\u7779\u5e90\u5c71\u771f\u9762\u76ee\uff0c\u6211\u4e0d\u76f8\u4fe1\u5f53\u4eca\u4e4b\u4e16\uff0c\u7adf\u7136\u771f\u6709\u8ff7\u4f4f\u5185\u5bab\u95e8\u4e0b\u5973\u5f1f\u5b50\u7684\u4eba\uff1f\u201d \u3000\u3000\u201c\u6211\u770b\u4e0d\u7528\u4e86\u3002\u2026\u2026\u201d\u97e9\u9738\u9053\uff1a\u201c\u5728\u4e0b\u4e0d\u5c51\u7531\u59d1\u5a18\u53e3\u4e2d\u5957\u51fa\u4ec0\u4e48\u9690\u5bc6\u3002\u201d \u3000\u3000\u201c\u4f60\u60f3\u7684\u7f8e\u5440\u4e86\u2026\u2026\u201d\u83f1\u82b1\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6885\u82b1\u7684\u6b66\u529f\u5f3a\u6211\u5f88\u591a\uff0c\u667a\u6167\u4e5f\u4e0d\u5728\u6211\u4e4b\u4e0b\uff0c\u5979\u8f93\u6211\u7684\u662f\u90a3\u4efd\u5b9a\u529b\uff0c\u544a\u8bc9\u4f60\u4e00\u4ef6\u4e8b\uff0c\u56db\u5b63\u82b1\u5728\u6d1b\u9633\u5c45\u4e2d\uff0c\u552f\u4e00\u6ca1\u6709\u88ab\u5ba2\u4eba\u6cbe\u8eab\u7684\uff0c\u5c31\u662f\u59d1\u5a18\u6211\u4e00\u4e2a\u4eba\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u59d1\u5a18\u8fd8\u662f\u5904\u5b50\u4e4b\u8eab\u4e86\u3002\u201d \u3000\u3000\u201c\u5f53\u7136\u4e0d\u662f\u2026\u2026\u201d\u83f1\u82b1\u9053\uff1a\u201c\u6211\u4eec\u827a\u6ee1\u51fa\u5e08\u524d\uff0c\u90fd\u88ab\u7834\u4e86\u8eab\u5b50\uff0c\u6700\u96be\u80fd\u7684\uff0c\u4e5f\u5c31\u662f\u5982\u6b64\u4e86\uff0c\u6211\u867d\u5df2\u975e\u5b8c\u74a7\uff0c\u4f46\u6211\u4ecd\u7136\u73cd\u89c6\u81ea\u5df1\u4e0d\u8ba9\u4eba\u8f7b\u6613\u5f97\u5230\u3002\u201d \u3000\u3000\u201c\u5475\u5f25\u9640\u4f5b\u2026\u2026\u201d\u5927\u548c\u5c1a\u7a81\u7136\u5ba3\u4e86\u4e00\u58f0\u4f5b\u53f7\uff0c\u9053\uff1a\u201c\u4e24\u4f4d\u6162\u6162\u8c08\u5427\uff01\u548c\u5c1a\u8981\u5148\u884c\u544a\u9000\u3002\u201d \u3000\u3000\u8d70\u5c31\u8d70\u5427\uff01\u987a\u624b\u8fd8\u5e26\u4e0a\u4e86\u623f\u95e8\u3002 \u3000\u3000\u201c\u548c\u5c1a\u5f88\u77e5\u8da3\uff0c\u2026\u2026\u201d\u83f1\u82b1\u9053\uff1a\u201c\u4f60\u7684\u5c5e\u4e0b\uff0c\u5012\u6709\u5f88\u591a\u53ef\u7231\u4e4b\u5904\u3002\u201d \u3000\u3000\u201c\u4ed6\u662f\u6709\u9053\u9ad8\u50e7\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u4e0d\u613f\u610f\u8033\u76ee\u53d7\u6c61\uff0c\u66f4\u91cd\u8981\u7684\u662f\u7ea0\u6b63\u4f60\u4e00\u4ef6\u4e8b\uff0c\u4ed6\u4e0d\u662f\u6211\u7684\u5c5e\u4e0b\uff0c\u662f\u6211\u7684\u670b\u53cb\uff01\u201d \u3000\u3000\u201c\u54fc\uff01\u6211\u770b\u8fc7\u5f88\u591a\u6bd4\u4ed6\u6709\u540d\u6c14\u7684\u8005\u548c\u5c1a\uff0c\u521d\u89c1\u65f6\u4e00\u7247\u5e84\u4e25\uff0c\u5230\u6700\u540e\uff0c\u8fd8\u4e0d\u662f\u8272\u4e0d\u8ff7\u4eba\u4eba\u81ea\u8ff7\uff0c\u2026\u2026\u201d\u83f1\u82b1\u9053\uff1a\u201c\u62ff\u4e0b\u4f60\u7684\u9762\u5177\uff0c\u8ba9\u6211\u6c42\u8bc1\u4e00\u4e0b\u5fc3\u4e2d\u4e4b\u7591\uff0c\u6211\u7acb\u523b\u5c31\u8d70\uff01\u201d \u3000\u3000\u201c\u6211\u53d7\u4f4f\u65b9\u5999\u6697\u7b97\uff0c\u4f60\u5e94\u8be5\u89c1\u8fc7\u6211\u3002\u201d \u3000\u3000\u201c\u90a3\u65f6\uff0c\u4f60\u4e00\u8138\u9ed1\u6c14\uff0c\u4e94\u5b98\u53d8\u5f62\uff0c\u90a3\u91cc\u770b\u5f97\u6e05\u695a\u3002\u2026\u2026\u201d\u83f1\u82b1\u9053\uff1a\u201c\u4f60\u7684\u5f62\u8c8c\uff0c\u5df2\u975e\u79d8\u5bc6\uff0c\u6211\u5e08\u7236\u8bb0\u5fc6\u8fc7\u4eba\uff0c\u53c8\u753b\u7684\u4e00\u624b\u597d\u753b\uff0c\u4e09\u4e94\u5929\u540e\uff0c\u4f60\u9601\u4e0b\u7684\u5c0a\u5bb9\uff0c\u5c31\u4f1a\u88ab\u63cf\u7ed8\u51fa\u6570\u4e00\u767e\u5e45\u51fa\u6765\uff0c\u8be5\u8ba4\u8bc6\u4f60\u7684\u4eba\uff0c\u90fd\u4f1a\u4eba\u624b\u4e00\u4efd\uff0c\u6211\u53ea\u4e0d\u8fc7\u662f\u60f3\u770b\u4e00\u770b\u6d3b\u751f\u751f\u7684\u4eba\uff0c\u4f1a\u6709\u4e9b\u4ec0\u4e48\u6837\u7684\u9b45\u529b\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u53d6\u4e0b\u9762\u5177\uff0c\u9053\uff1a\u201c\u666e\u901a\u7684\u5f88\u3002\u2026\u2026\u201d \u3000\u3000\u201c\u679c\u7136\u662f\u4f60\uff0c\u7530\u7389\u2026\u2026\u201d\u83f1\u82b1\u63a5\u9053\uff1a\u201c\u4f60\u63a5\u8fd1\u6885\u82b1\u65f6\uff0c\u6211\u5c31\u4e3b\u5f20\u6740\u4e86\u4f60\uff0c\u6211\u4e0d\u61c2\u79cb\u82b1\u53cc\u5a62\u4eec\u4e3a\u4ec0\u4e48\u4e0d\u80af\u4e0b\u624b\uff1f\u662f\u88ab\u4f60\u751c\u8a00\u871c\u8bed\u9a97\u8fc7\u4e86\uff0c\u8fd8\u662f\u6015\u6885\u82b1\u751f\u6c14\u62a5\u590d\uff1f\u201d \u3000\u3000\u201c\u6211\u4e5f\u4e0d\u592a\u660e\u767d\uff0c\u4f60\u65e2\u7136\u65e9\u6709\u4e86\u6740\u6211\u4e4b\u5fc3\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u80af\u81ea\u5df1\u4e0b\u624b\uff1f\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u5374\u53eb\u522b\u4eba\u884c\u51f6\uff1f\u201d \u3000\u3000\u201c\u6211\u662f\u4e2a\u5f88\u8c28\u614e\u7684\u4eba\uff0c\u4e0d\u613f\u66b4\u9732\u6211\u638c\u4ee4\u8eab\u4efd\uff0c\u4e5f\u4e0d\u613f\u81ea\u5df1\u6d89\u9669\u2026\u2026\u201d\u83f1\u82b1\u9053\uff1a\u201c\u80fd\u4f7f\u6885\u82b1\u8ff7\u604b\u7684\u5982\u75f4\u5982\u9189\uff0c\u7edd\u4e0d\u662f\u7b80\u5355\u7684\u4eba\u7269\u3002\u201d \u3000\u3000\u201c\u5c0f\u5c0f\u5e74\u7eaa\u5982\u6b64\u7684\u5de5\u4e8e\u5fc3\u8ba1\u3001\u7b97\u8ba1\uff0c\u5f53\u771f\u662f\u7f6a\u5f53\u8bdb\u6740\uff01\u201d\u6c5f\u67ab\u6709\u70b9\u6012\u706b\u4e0a\u5347\u3002 \u3000\u3000\u201c\u5175\u4e0d\u538c\u8bc8\uff0c\u65e2\u7136\u662f\u5f7c\u6b64\u654c\u5bf9\uff0c\u81ea\u7136\u8981\u5404\u901e\u5fc3\u673a\uff0c\u2026\u2026\u201d\u83f1\u82b1\u7a81\u7136\u957f\u957f\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a \u3000\u3000\u201c\u6211\u77a7\u4e0d\u51fa\u6765\uff0c\u6885\u82b1\u600e\u4e48\u9677\u5165\u4e86\u90a3\u4e48\u4e2a\u6df1\u6cd5\uff0c\u6211\u771f\u7684\u77a7\u4e0d\u51fa\u4f60\u6709\u4ec0\u4e48\u7279\u522b\u7684\u52a8\u4eba\u4e4b\u5904\uff0c\u8bc4\u5934\u8bba\u8db3\uff0c\u4f60\u53ea\u4e0d\u8fc7\u662f\u4e00\u4e2a\u4e0d\u8ba9\u4eba\u8ba8\u538c\u7684\u7537\u4eba\u7f62\u4e86\uff0c\u6885\u82b1\u5341\u51e0\u5e74\u7684\u5a9a\u4eba\u4e4b\u672f\uff0c\u5f53\u771f\u662f\u767d\u7ec3\u4e86\u5417\uff1f\u201d \u3000\u3000\u201c\u83f1\u82b1\uff0c\u4f60\u5df2\u7ecf\u89c1\u8fc7\u6211\u4e86\uff0c\u8bf4\u51fa\u6765\u7ea6\u5b9a\u7684\u65f6\u95f4\u3001\u5730\u70b9\uff0c\u4f60\u5c31\u53ef\u4ee5\u8d70\u4e86\u3002\u201d \u3000\u3000\u201c\u600e\u4e48\uff1f\u6211\u957f\u7684\u8ba9\u4eba\u8ba8\u538c\u5417\uff1f\u201d \u3000\u3000\u201c\u4e0d\u600e\u4e48\u8ba9\u4eba\u559c\u6b22\uff0c\u6240\u4ee5\uff0c\u6211\u4e0d\u5e0c\u671b\u548c\u4f60\u518d\u8c08\u4e0b\u53bb\u4e86\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u60f9\u706b\u4e86\u6211\uff0c\u4f1a\u8981\u7559\u4e0b\u4e00\u53ea\u8033\u6735\u518d\u8d70\uff01\u201d \u3000\u3000\u83f1\u82b1\u662f\u771f\u7684\u6709\u4e9b\u5bb3\u76f8\uff0c\u5979\u77e5\u9053\u6c5f\u67ab\u7684\u6b66\u529f\uff0c\u7edd\u975e\u81ea\u5df1\u80fd\u654c\uff0c\u867d\u7136\uff0c\u5979\u5df2\u5728\u4ea4\u8c08\u4e2d\u65bd\u5c55\u51fa\u8ff7\u4eba\u7684\u5a9a\u672f\uff0c\u4f46\u6c5f\u67ab\u5782\u7709\u95ed\u76ee\uff0c\u671b\u4e5f\u4e0d\u671b\u5979\u4e00\u773c\u3002 \u3000\u3000\u5979\u5fc3\u4e2d\u5df2\u660e\u767d\uff0c\u7edd\u5bf9\u65e0\u6cd5\u5f81\u670d\u8fd9\u4e2a\u7537\u4eba\u4e86\uff0c\u6025\u6025\u8bf4\u9053\uff1a\u201c\u660e\u65e5\u7533\u521d\uff0c\u5728\u6e34\u6865\u4e4b\u5317\u4e07\u6885\u576a\uff0c\u4e00\u51b3\u80dc\u8d1f\u3002\u201d \u3000\u3000\u6c5f\u67ab\u4e00\u76b1\u7709\u5934\uff0c\u5fd6\u9053\uff1a\u201c\u7ea6\u5230\u7533\u65f6\u4f1a\u6218\uff0c\u51ac\u65e5\u82e6\u77ed\uff0c\u90a3\u5df2\u662f\u5c06\u8fd1\u5915\u9633\u843d\u5c71\u65f6\u5206\uff0c\u96be\u9053\u5979\u4eec\u8981\u6311\u706f\u591c\u6218\uff0c\u8fd8\u662f\u51c6\u5907\u85c9\u591c\u8272\u63a9\u62a4\uff0c\u65bd\u5c55\u5947\u88ad\u3002\u2026\u2026\u201d \u3000\u3000\u4f46\u95fb\u83f1\u82b1\u63a5\u9053\uff1a\u201c\u4f20\u8baf\u5df2\u6bd5\uff0c\u606d\u5019\u56de\u97f3\u3002\u201d \u3000\u3000\u201c\u5982\u671f\u4e0e\u4f1a\u3002\u201d \u3000\u3000\u6c5f\u67ab\u4e00\u6325\u624b\uff0c\u83f1\u82b1\u54ea\u6562\u591a\u7559\uff0c\u6025\u6025\u8f6c\u8eab\u800c\u53bb\u3002 \u3000\u3000\u5979\u679c\u7136\u77e5\u673a\u7684\u5f88\uff0c\u8fde\u4e00\u53e5\u544a\u522b\u7684\u8bdd\u4e5f\u4e0d\u8bf4\u4e86\u3002 \u3000\u3000\u6c5f\u67ab\u6b63\u51c6\u5907\u53ec\u8bf7\u5f20\u56db\u59d1\u7b49\u8f6c\u544a\u4e0a\u60c5\uff0c\u4e03\u5b9d\u548c\u5c1a\u5df2\u5e26\u7740\u5f20\u56db\u59d1\u3001\u7f57\u5170\u548c\u96ea\u3001\u971c\u3001\u6885\u82b1\u7b49\u4e09\u5973\uff0c\u884c\u5165\u623f\u4e2d\u3002 \u3000\u3000\u8fd9\u4e2a\u5927\u548c\u5c1a\u4e0d\u4f46\u8eab\u6000\u7edd\u6280\uff0c\u667a\u8651\u5468\u8be6\uff0c\u63e3\u6469\u4e8b\u7406\uff0c\u4e5f\u6709\u72ec\u5230\u4e4b\u5904\u3002 \u3000\u3000\u6c5f\u67ab\u8bf4\u660e\u4e86\u83f1\u82b1\u4f20\u6765\u7684\u8baf\u606f\u3002 \u3000\u3000\u5f20\u56db\u59d1\u76b1\u8d77\u7709\u5934\uff0c\u9053\uff1a\u201c\u6bdb\u75c5\u51fa\u5728\u7ea6\u6218\u7684\u65f6\u95f4\u4e0a\uff0c\u4e07\u6885\u576a\u662f\u4e00\u5904\u8d4f\u96ea\u7684\u5730\u65b9\uff0c\u957f\u5b89\u57ce\u4e2d\u6709\u4e0d\u5c11\u6587\u4eba\u58a8\u5ba2\uff0c\u5728\u90a3\u91cc\u8d4f\u82b1\u996e\u9152\uff0c\u53ef\u60dc\u7684\u662f\uff0c\u6211\u6ca1\u6709\u53bb\u8fc7\uff1f\u201d\u76ee\u5149\u8f6c\u5411\u4e86\u4e03\u5b9d\u548c\u5c1a\u3002 \u3000\u3000\u201c\u5927\u548c\u5c1a\u53ef\u662f\u4ece\u4e0d\u6d89\u8db3\u3002\u201d\u4e03\u5b9d\u548c\u5c1a\u7b54\u9053\uff1a\u201c\u8fd9\u6b21\u4f4f\u4e86\u6d1b\u9633\u5c45\u8fd9\u5730\u67d4\u5c4b\u540d\u56ed\uff0c\u53ef\u662f\u5149\u5934\u8ddf\u7740\u6708\u4eae\u8d70\uff0c\u5360\u4e86\u6c5f\u5c11\u4fa0\u7684\u5149\u4e86\u3002\u201d \u3000\u3000\u201c\u6ca1\u53bb\u8fc7\uff0c\u5c31\u662f\u6ca1\u53bb\u8fc7\uff0c\u7528\u4e0d\u7740\u8f6c\u5f2f\u62b9\u89d2\u7684\u63a9\u7f9e\u3001\u906e\u4e11\uff0c\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u770b\u6765\uff0c\u8981\u6211\u8001\u53eb\u5316\u548c\u79e6\u516b\u516c\u6765\u4e00\u8d9f\u4e86\uff0c\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u53bb\u8fc7\u2026\u2026\u201d\u6885\u82b1\u63a5\u9053\uff1a\u201c\u5e74\u524d\u6709\u4e00\u4f4d\u4eac\u5b98\u8fc7\u5883\uff0c\u662f\u4e00\u4e2a\u98ce\u96c5\u4eba\u7269\uff0c\u957f\u5b89\u5e9c\u8bbe\u5bb4\u4e07\u6885\u576a\uff0c\u5e7f\u9080\u957f\u5b89\u540d\u4eba\u6587\u58eb\u4e0e\u4f1a\uff0c\u70ed\u95f9\u4e86\u4e24\u5929\uff0c\u624d\u66f2\u7ec8\u4eba\u6563\u3002\u201d \u3000\u3000\u201c\u4e24\u5929\u65f6\u95f4\uff0c\u5e94\u8be5\u5bf9\u90a3\u91cc\u5f88\u719f\u6089\u4e86\uff0c\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u8bf4\u8bf4\u770b\uff0c\u90a3\u91cc\u6709\u4e9b\u4ec0\u4e48\u666f\u7269\u7279\u8272\uff1f\u201d \u3000\u3000\u201c\u814a\u6885\u76db\u653e\uff0c\u6e05\u9999\u6251\u9f3b\uff0c\u4e00\u773c\u671b\u53bb\uff0c\u7ea2\u767d\u76f8\u6620\uff0c\u4e0d\u89c1\u8fb9\u9645\uff0c\u4e07\u6885\u4e4b\u79f0\uff0c\u5f53\u4e4b\u65e0\u6127\uff0c\u2026\u2026\u201d\u6885\u82b1\u9053\uff1a \u3000\u3000\u201c\u53ea\u662f\u904d\u5730\u79ef\u96ea\u76c8\u5c3a\uff0c\u5bd2\u6c14\u8feb\u4eba\uff0c\u666e\u901a\u7684\u8d4f\u82b1\u4eba\uff0c\u867d\u7740\u91cd\u88d8\uff0c\u4e5f\u6709\u4e9b\u4e0d\u80dc\u5317\u98ce\u900f\u9aa8\u5bd2\u5462\uff01\u201d \u3000\u3000\u201c\u9664\u4e86\u79ef\u96ea\u904d\u5730\u4e4b\u5916\uff0c\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u8fd8\u6709\u4e9b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u6885\u56ed\u3001\u7af9\u8f69\uff0c\u4e24\u4e2a\u8f83\u5927\u7684\u9910\u9986\uff0c\u2026\u2026\u201d\u6885\u82b1\u4e00\u9762\u60f3\uff0c\u4e00\u9762\u8bf4\u9053\uff1a\u201c\u867d\u79f0\u6885\u56ed\u3001\u7af9\u8f69\uff0c\u53ef\u662f\u7528\u9752\u7816\u780c\u6210\u7684\u623f\u5b50\uff0c\u539a\u88ab\u906e\u7a97\uff0c\u6728\u95e8\u7d27\u95ed\uff0c\u751f\u7740\u591a\u76c6\u70ad\u706b\uff0c\u5ba4\u5185\u53ef\u7740\u8584\u886b\u5355\u8863\uff0c\u4e0d\u89c9\u5bd2\u610f\u3002\u201d \u3000\u3000\u201c\u548c\u6211\u4eec\u8fd9\u6d1b\u9633\u5c45\u7684\u5e03\u8bbe\u76f8\u540c\u4e86\u2026\u2026\u201d\u6c5f\u67ab\u7b11\u9053\uff1a\u201c\u56ed\u3001\u8f69\u6625\u6696\uff0c\u5c82\u4e0d\u662f\u6709\u8d1f\u4e86\u8e0f\u96ea\u8d4f\u6885\u7684\u96c5\u610f\uff1f\u201d \u3000\u3000\u201c\u6c5f\u5927\u7237\uff0c\u90a3\u91cc\u9152\u83dc\u6602\u8d35\uff0c\u5c24\u8fc7\u6d1b\u9633\u5c45\uff0c\u2026\u2026\u201d\u6885\u82b1\u9053\uff1a\u201c\u5c31\u7b97\u5403\u4e00\u7897\u7f8a\u8089\u6ce1\u998d\uff0c\u4e5f\u8981\u4f60\u4e00\u4e24\u94f6\u5b50\uff0c\u5728\u957f\u5b89\u57ce\u4e2d\u53ef\u4ee5\u5403\u5230\u4e8c\u5341\u7897\uff0c\u9152\u5e2d\u4e4b\u8d39\uff0c\u4e0d\u5728\u8bdd\u4e0b\u4e86\uff0c\u9664\u4e86\u8c6a\u5bcc\u4e4b\u5916\uff0c\u4e00\u822c\u4eba\u5927\u90fd\u662f\u81ea\u5e26\u5e72\u7cae\uff0c\u6c81\u5fc3\u6885\u9999\u4e2d\uff0c\u5543\u4e24\u53e3\u5927\u997c\uff0c\u5403\u4e00\u53e3\u5364\u597d\u7684\u725b\u7f8a\u8089\uff0c\u5012\u4e5f\u6709\u51e0\u5206\u98ce\u96c5\uff0c\u81f4\u4e8e\u90a3\u4e9b\u5bcc\u8c6a\u5927\u8d3e\uff0c\u5343\u91d1\u4e70\u7b11\uff0c\u90a3\u5c31\u9189\u7fc1\u4e4b\u610f\u4e0d\u5728\u9152\u4e86\uff0c\u771f\u662f\u7a97\u5916\u8e0f\u96ea\u5bfb\u6885\u9999\uff0c\u5ba4\u5185\u4f73\u4eba\u6b4c\u821e\u5fd9\uff0c\u5929\u4e0b\u7684\u4e4c\u9e26\u4e00\u6837\u9ed1\uff0c\u6240\u8c13\u7684\u6885\u56ed\u3001\u7af9\u8f69\uff0c\u548c\u6211\u4eec\u8fd9\u6d1b\u9633\u5c45\u5dee\u4e0d\u591a\u4e86\u3002\u201d \u3000\u3000\u5f20\u56db\u59cb\u5fc3\u4e2d\u4e00\u52a8\uff0c\u63a5\u9053\uff1a\u201c\u6885\u82b1\uff0c\u4f1a\u4e0d\u4f1a\u4e5f\u662f\u5185\u5bab\u4e00\u7cfb\u4e2d\u4eba\u5e03\u7f6e\u7684\u636e\u70b9\u3001\u9677\u9631\uff1f\u201d \u3000\u3000\u6885\u82b1\u5fae\u5fae\u4e00\u6014\uff0c\u9053\uff1a\u201c\u7edd\u4e0d\u662f\u6b63\u7ecf\u5730\u65b9\uff0c\u5982\u8bf4\u548c\u5185\u5bab\u6709\u6240\u5173\u8fde\uff0c\u665a\u8f88\u5c31\u4e0d\u6562\u65ad\u8a00\u4e86\uff0c\u6211\u4e00\u76f4\u4e3b\u6301\u5185\u5bab\u5728\u6d1b\u9633\u5c45\u4e2d\u7684\u4e8b\u52a1\uff0c\u5374\u6ca1\u6709\u542c\u5230\u548c\u6885\u56ed\u3001\u7af9\u8f69\u4e2d\u6709\u4efb\u4f55\u7684\u8fde\u7cfb\uff1f\u201d \u3000\u3000\u201c\u6885\u56ed\u3001\u7af9\u8f69\u4e2d\uff0c\u4e5f\u6709\u7f8e\u5973\u4f8d\u9152\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u5982\u662f\u5185\u5bab\u4e00\u7cfb\u51fa\u8eab\uff0c\u4f60\u5e94\u8be5\u8ba4\u8bc6\u5979\u4eec\uff1f\u201d \u3000\u3000\u201c\u662f\uff01\u6709\u4f8d\u5973\u966a\u5ba2\uff0c\u90a3\u4e00\u6b21\u665a\u8f88\u548c\u6625\u82b1\u56db\u5a62\u540c\u884c\uff0c\u4f46\u6211\u4eec\u5374\u4e0d\u8bc6\u4e00\u4eba\u2026\u2026\u201d\u6885\u82b1\u9053\uff1a \u3000\u3000\u201c\u5982\u662f\u5185\u5b98\u51fa\u8eab\uff0c\u5e94\u8be5\u90fd\u662f\u4e09\u4f4d\u5bab\u4e3b\u95e8\u4e0b\u5f1f\u5b50\uff0c\u81f3\u5c11\u665a\u8f88\u4e5f\u4f1a\u8ba4\u8bc6\u51e0\u4e2a\u3002\u201d \u3000\u3000\u201c\u8fd9\u5c31\u5947\u602a\u4e86\uff0c\u2026\u2026\u201d\u5f20\u56db\u59d1\u6c89\u541f\u4e0d\u8bed\u3002 \u3000\u3000\u201c\u963f\u5f25\u9640\u4f5b\uff01\u4f1a\u4e0d\u4f1a\u662f\u5929\u738b\u95e8\u4e0b\u5462\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4f3c\u662f\u552f\u4e00\u7684\u53ef\u80fd\u4e86\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u5929\u738b\u95e8\u5728\u8fd9\u6b21\u4e89\u6597\u4e4b\u4e2d\uff0c\u4e00\u76f4\u4fdd\u6301\u4e2d\u7acb\uff0c\u4e3a\u4ec0\u4e48\u5ffd\u7136\u95f4\u5c3d\u51fa\u7cbe\u9510\u8fdb\u5165\u957f\u5b89\u2026\u2026\u201d \u3000\u3000\u201c\u5929\u738b\u95e8\u4e2d\u5973\u5f1f\u5b50\uff0c\u548c\u6211\u4eec\u540c\u51fa\u4e00\u6e90\u2026\u2026\u201d\u6885\u82b1\u9053\uff1a\u201c\u9752\u51e4\u6210\u5c31\u867d\u9ad8\u51fa\u6211\u4eec\u5f88\u591a\uff0c\u4f46\u4e07\u53d8\u4e0d\u79bb\u5176\u5b97\uff0c\u4ecd\u6709\u96f7\u540c\u53ef\u5bfb\uff0c\u4f46\u6885\u56ed\u3001\u7af9\u8f69\u7684\u5973\u4f8d\uff0c\u5374\u975e\u6211\u540c\u6e90\uff0c\u5979\u4eec\u4f1a\u6b66\u529f\uff0c\u4e5f\u4f1a\u65bd\u5c55\u5a9a\u672f\uff0c\u665a\u8f88\u867d\u65e0\u6cd5\u770b\u51fa\u5979\u4eec\u6b66\u529f\u7684\u9ad8\u4f4e\uff0c\u4f46\u6492\u5a07\u5a9a\u4eba\u7684\u624b\u6bb5\uff0c\u5374\u53ea\u662f\u53d1\u6325\u51fa\u4e00\u822c\u5973\u4eba\u5929\u6027\uff0c\u6ca1\u6709\u5f88\u7cbe\u6df1\u7684\u8bad\u7ec3\u3002\u201d \u3000\u3000\u201c\u4f1a\u4e0d\u4f1a\u662f\u5929\u9a6c\u5802\u4e2d\u7684\u4eba\u2026\u2026\u201d\u6c5f\u67ab\u7a81\u7136\u63d2\u53e3\u9053\uff1a\u201c\u5f53\u4eca\u6c5f\u6e56\u4e4b\u4e0a\uff0c\u9664\u4e86\u8fd9\u4e09\u4e2a\u4e32\u8fde\u4e00\u8d77\u7684\u7ec4\u5408\u4e4b\u5916\uff0c\u5e94\u8be5\u4e0d\u4f1a\u518d\u6709\u522b\u7684\u95e8\u6237\u4e86\uff01\u201d \u3000\u3000\u5f20\u56db\u59d1\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u5979\u4eec\u966a\u5f85\u5ba2\u4eba\u7684\u9152\u5973\uff0c\u59ff\u8272\u5982\u4f55\uff1f\u201d \u3000\u3000\u201c\u90a3\u4e00\u5929\uff0c\u5927\u90fd\u662f\u5bab\u5e9c\u4e2d\u4eba\uff0c\u5bb4\u5f00\u5341\u5e2d\uff0c\u6885\u56ed\u3001\u7af9\u8f69\uff0c\u5404\u6446\u4e94\u684c\uff0c\u665a\u8f88\u5728\u7af9\u8f69\u4f8d\u9152\uff0c\u7af9\u8f69\u4e2d\u6d3e\u51fa\u4e86\u5341\u4e8c\u4f4d\u59d1\u5a18\u4f8d\u5ba2\uff0c\u59ff\u8272\u7edd\u4f73\uff0c\u5e74\u9f84\u4e5f\u90fd\u5728\u4e8c\u5341\u5de6\u53f3\uff0c\u770b\u4e0a\u53bb\uff0c\u4f3c\u662f\u7ecf\u8fc7\u4e86\u5404\u5904\u641c\u5bfb\uff0c\u957f\u65f6\u8bad\u7ec3\u51fa\u6765\u7684\u7f8e\u4eba\uff0c\u5979\u4eec\u5168\u51ed\u7740\u5929\u8d4b\u5949\u8fce\u5ba2\u4eba\uff0c\u8c08\u4e0d\u4e0a\u65bd\u5c55\u5a9a\u672f\uff0c\u665a\u8f88\u51b7\u773c\u65c1\u89c2\uff0c\u8bb0\u5fc6\u7684\u5341\u5206\u6e05\u695a\u3002\u201d \u3000\u3000\u201c\u5e94\u8be5\u4e0d\u4f1a\u9519\u4e86\uff0c\u8fd9\u4e00\u5904\u5929\u9a6c\u5802\u7684\u9690\u5bc6\u636e\u70b9\uff0c\u8fde\u9093\u98de\u4e5f\u4e0d\u77e5\u9053\uff0c\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u770b\u6765\uff0c\u5185\u5bab\u4e00\u7cfb\u548c\u5929\u9a6c\u5802\u4e89\u5f97\u7684\u5929\u7ffb\u5730\u8986\uff0c\u53ea\u662f\u5175\u5352\u76f8\u6b8b\uff0c\u4f5c\u7ed9\u5929\u738b\u95e8\u4e2d\u4eba\u770b\u7740\u7f62\u4e86\uff0c\u5929\u738b\u95e8\u4ee5\u5c40\u5916\u4eba\u7684\u8eab\u4efd\uff0c\u5c3d\u8d77\u7cbe\u9510\u800c\u6765\uff0c\u5012\u662f\u503c\u5f97\u4e00\u756a\u6df1\u7a76\u4e86\uff0c\u5509\uff01\u6700\u53ef\u4f2f\u662f\u4ed6\u4eec\u4e09\u65b9\u9762\u8054\u624b\u4e00\u6c14\uff0c\u8fd9\u4e00\u6218\uff0c\u54b1\u4eec\u5f88\u53ef\u80fd\u4f1a\u5168\u519b\u8986\u6ca1\uff01\u201d \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u5927\u59d0\uff0c\u96be\u9053\u8981\u5f03\u6218\u9003\u8d70\u4e0d\u6210\uff1f\u201d \u3000\u3000\u5f20\u56db\u59d1\u76ee\u5149\u8f6c\u52a8\uff0c\u770b\u4e86\u5ba4\u4e2d\u5404\u4eba\u4e00\u773c\uff0c\u9053\uff1a\u201c\u4f60\u6570\u6570\u770b\uff0c\u54b1\u4eec\u6709\u591a\u5c11\uff1f\u6240\u4ee5\uff0c\u6211\u51b3\u5b9a\u4ee5\u7cbe\u5175\u8fce\u654c\uff0c\u4e0d\u8bba\u654c\u4eba\u591a\u5c11\u4eba\uff0c\u54b1\u4eec\u4ee5\u5341\u4e8c\u4e2a\u4eba\u8fce\u654c\uff0c\u800c\u4e14\u5148\u8981\u51b3\u5b9a\u4e86\u9000\u8d70\u7684\u4ee4\u8c15\u53ca\u8def\u7ebf\uff0c\u4e00\u65e6\u4ee4\u51fa\uff0c\u4efb\u4f55\u4eba\u4e0d\u5f97\u604b\u6218\uff0c\u4e0d\u5e78\u8eab\u53d7\u91cd\u4f24\u65e0\u6cd5\u5954\u8d70\u7684\u4eba\uff0c\u53ea\u6709\u81ea\u6c42\u591a\u798f\u4e86\uff0c\u4ee5\u5be1\u51fb\u4f17\uff0c\u800c\u4e14\u4e0d\u6551\u91cd\u4f24\u7684\u4eba\uff0c\u542c\u8d77\u6765\u5f88\u6b8b\u9177\uff0c\u4f46\u6211\u4eec\u53ea\u6709\u8fd9\u70b9\u672c\u94b1\uff0c\u4e0d\u80fd\u4e00\u4e0b\u8d4c\u5b8c\uff0c\u4f60\u662f\u7edf\u519b\u4e3b\u5e08\uff0c\u5148\u51b3\u5b9a\uff0c\u6211\u8fd9\u4e2a\u8fce\u654c\u4e4b\u7b56\uff0c\u662f\u5426\u53ef\u884c\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u5fc3\u4e2d\u5fd6\u9053\uff1a\u201c\u8bf4\u7684\u5982\u6b64\u51b7\u5389\uff0c\u542c\u5f97\u53eb\u4eba\u5bb6\u5fc3\u5bd2\uff0c\u8981\u6211\u5982\u4f55\u51b3\u5b9a\u5462\uff1f\u4f55\u51b5\u53c2\u6218\u4e4b\u4eba\uff0c\u53c8\u90fd\u662f\u81f3\u4eb2\u597d\u53cb\u2026\u2026\u201d \u3000\u3000\u53ea\u542c\u4e03\u5b9d\u548c\u5c1a\u9053\uff1a\u201c\u540c\u610f\u5f20\u59d1\u5a18\u7684\u9ad8\u8bba\uff0c\u653e\u624b\u65bd\u4e3a\uff0c\u5c3d\u5c55\u6240\u5b66\uff0c\u800c\u4e14\u540e\u9000\u6709\u8def\uff0c\u80fd\u4e0d\u80fd\u9000\u5f97\u8d70\uff0c\u90a3\u5c31\u5404\u51ed\u9020\u5316\u4e86\uff01\u201d \u3000\u3000\u56de\u5934\u671b\u7740\u7f57\u5170\uff0c\u6c5f\u67ab\u4f4e\u58f0\u9053\uff1a\u201c\u592b\u4eba\u7684\u610f\u601d\u5462\uff1f\u201d \u3000\u3000\u201c\u8d5e\u6210\uff01\u53ef\u4ee5\u653e\u624b\u6740\u654c\uff0c\u53c8\u4e0d\u9650\u6218\u6b7b\u6c99\u573a\uff0c\u8fd9\u662f\u591a\u4e48\u4ec1\u6148\u7684\u8bbe\u8ba1\u554a\uff01\u201d \u3000\u3000\u6c5f\u67ab\u671b\u671b\u96ea\u3001\u971c\u548c\u6885\u82b1\uff0c\u4e09\u5973\u9f50\u9f50\u542b\u7b11\u70b9\u7740\u5934\u3002 \u3000\u3000\u201c\u597d\uff01\u5c31\u8fd9\u4e48\u51b3\u5b9a\u4e86\uff0c\u8fd9\u64a4\u9000\u7684\u4ee4\u6e1d\uff0c\u7531\u4f55\u4eba\u53d1\u51fa\uff1f\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u5927\u59d0\u65e2\u4e3b\u5176\u4e8b\uff0c\u8981\u5229\u7528\u90a3\u904d\u5730\u79ef\u96ea\u548c\u4e07\u682a\u6885\u6811\uff0c\u8bd5\u8bd5\u770b\u4e94\u884c\u9041\u672f\uff0c\u7a76\u7adf\u80fd\u4e0d\u80fd\u7528\u4f5c\u5927\u89c4\u6a21\u7684\u5bf9\u9635\u640f\u6740\uff0c\u771f\u6b63\u51fa\u9762\u8fce\u654c\u7684\uff0c\u53ea\u6709\u6c5f\u5c11\u4fa0\u7387\u9886\u7684\u4e03\u4e2a\u4eba\u3002\u201d \u3000\u3000\u201c\u597d\u6781\u4e86\uff0c\u5947\u3001\u6b63\u4e92\u8f85\u2026\u2026\u201d\u4e03\u5ba4\u548c\u5c1a\u9053\uff1a\u201c\u8fd9\u4e00\u6218\u8d25\u4e5f\u4e0d\u4f1a\u4f24\u5bb3\u5f88\u5927\uff0c\u80dc\u4e48\uff1f\u90a3\u5c31\u8981\u654c\u4eba\u5143\u6c14\u5927\u4f24\uff01\u201d \u3000\u3000\u201c\u5927\u548c\u5c1a\u4e0d\u8981\u5c3d\u5f80\u597d\u5904\u60f3\uff01\u9700\u77e5\u54b1\u4eec\u5168\u65e0\u654c\u4eba\u7684\u8d44\u6599\uff0c\u77e5\u5df1\u4e0d\u80fd\u77e5\u5f7c\uff0c\u6700\u591a\u53ea\u6709\u4e00\u534a\u7684\u673a\u4f1a\uff01\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u4f60\u4eec\u5546\u91cf\u4e00\u4e0b\u8fce\u654c\u4eba\u624b\uff0c\u6211\u548c\u4e09\u4e2a\u4e2b\u5934\u5148\u8d70\u4e86\u3002\u201d \u3000\u3000\u8bf4\u8d70\u5c31\u8d70\uff0c\u4e24\u53ea\u624b\u5206\u7275\u7740\u4e09\u5973\uff0c\u5411\u5916\u884c\u53bb\u3002 \u3000\u3000\u96ea\u3001\u971c\u3001\u6885\u82b1\u60f3\u548c\u6c5f\u5927\u54e5\u8bf4\u51e0\u53e5\u544a\u522b\u7684\u8bdd\uff0c\u4e5f\u6ca1\u6709\u51fa\u53e3\u7684\u673a\u4f1a\u4e86\u3002 \u3000\u3000\u6c5f\u67ab\u5374\u6025\u6025\u53eb\u9053\uff1a\u201c\u5927\u59d0\uff0c\u4f60\u4e0d\u80fd\u4e3a\u5fb7\u4e0d\u5352\u554a\uff1f\u201d \u3000\u3000\u5f20\u56db\u59d1\u505c\u4e0b\u811a\u6b65\uff0c\u9053\uff1a\u201c\u600e\u4e48\u8bf4\uff1f\u201d \u3000\u3000\u201c\u8981\u6211\u5e26\u7740\u4e03\u4e2a\u4eba\u8fce\u654c\uff0c\u5230\u5e95\u662f\u54ea\u4e03\u4e2a\u5440\uff1f\u201d \u3000\u3000\u5f20\u56db\u59d1\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u53ea\u80fd\u63d0\u4e2a\u5efa\u8bae\uff0c\u6700\u540e\u7684\u51b3\u5b9a\u8981\u4f60\u88c1\u914c\u3001\u8c03\u5ea6\u4e86\uff01\u201d \u3000\u3000\u201c\u597d\u597d\u597d\uff0c\u5c0f\u5f1f\u6d17\u8033\u606d\u542c\u3002\u201d \u3000\u3000\u201c\u5927\u548c\u5c1a\u3001\u6b27\u9633\u662d\uff0c\u6b66\u529f\u4e5f\u5927\u6709\u8fdb\u5883\uff0c\u8981\u4ed6\u4eec\u4e34\u9635\u4e00\u6218\uff0c\u4e5f\u53ef\u4e00\u6cc4\u79ef\u6028\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u53e6\u5916\u4e24\u4e2a\u2014\u2014\u201d \u3000\u3000\u201c\u53ea\u6709\u80e1\u5143\u548c\u90ed\u5929\u540c\u4e86\uff0c\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u4ed6\u4eec\u8fd9\u4e9b\u65e5\u5b50\u4e2d\u82e6\u7ec3\u6b66\u529f\uff0c\u8fdb\u5883\u4e0d\u9519\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u7684\u770b\u6cd5\u4e0d\u540c\uff0c\u80e1\u5143\u3001\u90ed\u5929\u540c\u6682\u4ea4\u7ed9\u94b1\u7f3a\u7387\u9886\uff0c\u6709\u4ed6\u4eec\u4e24\u4e2a\u4eba\uff0c\u4e5f\u53ef\u4ee5\u5e2e\u8001\u53eb\u5316\u4e00\u70b9\u5fd9\u2026\u2026\u201d\u5f20\u56db\u7ad9\u7b11\u9053\uff1a\u201c\u53e6\u5916\u4e24\u4e2a\u4eba\uff0c\u53ef\u4ee5\u8bf7\u9093\u98de\u3001\u5201\u9e4f\u5145\u6570\uff0c\u4ed6\u4eec\u88ab\u903c\u4e0a\u4e86\u6881\u5c71\uff0c\u53ef\u80fd\u4f1a\u6709\u5f88\u591a\u7684\u654c\u60c5\u5949\u544a\u5462\uff01\u201d \u3000\u3000\u201c\u963f\u5f25\u9640\u4f5b\uff01\u9ad8\u554a\uff01\u4eca\u5929\uff0c\u6211\u624d\u662f\u771f\u6b63\u7684\u773c\u4e86\u4f60\u5f20\u59d1\u5a18\uff0c\u548c\u5c1a\u6211\u662f\u7518\u62dc\u4e0b\u98ce\u3002\u201d \u3000\u3000\u6c5f\u67ab\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5927\u548c\u5c1a\u53ef\u662f\u53e3\u8f6f\u5fc3\u786c\uff0c\u8981\u4ed6\u771f\u6b63\u670d\u4e2a\u4eba\uff0c\u53ef\u4e0d\u5bb9\u6613\uff0c\u8fd9\u4e24\u4e2a\u4eba\uff0c\u5728\u6211\u8eab\u8fb9\uff0c\u6211\u4e3a\u4ec0\u4e48\u60f3\u4e0d\u5230\u5462\uff1f\u201d \u3000\u3000\u201c\u6c5f\u5927\u54e5\uff0c\u4f60\u5fc3\u4e2d\u8bb0\u7684\u5973\u4eba\u592a\u591a\u4e86\uff0c\u6240\u4ee5\uff0c\u60f3\u4e0d\u5230\u7528\u7537\u4eba\u561b\uff01\u201d\u541f\u971c\u5fae\u7b11\u5982\u82b1\u5730\u8bf4\u3002 \u3000\u3000\u6c5f\u67ab\u5c45\u7136\u8033\u6839\u53d1\u70ed\uff0c\u6ee1\u8138\u901a\u7ea2\uff0c\u4e0d\u77e5\u662f\u5fc3\u4e2d\u60ed\u6127\u5462\uff1f\u8fd8\u662f\u5f53\u7740\u7f57\u5170\u4e4b\u9762\uff0c\u6709\u4e9b\u4e0b\u4e0d\u4e86\u53f0\u3002 \u3000\u3000\u541f\u971c\u770b\u7684\u5403\u4e86\u4e00\u60ca\uff0c\u5979\u4ece\u672a\u89c1\u8fc7\u6c5f\u67ab\u5982\u6b64\u5f62\u8272\uff0c\u5f88\u60f3\u8d70\u8fc7\u53bb\u95ee\u4e2a\u660e\u767d\uff0c\u4f46\u5df2\u88ab\u5f20\u56db\u59d1\u5f3a\u62c9\u7740\u884c\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u76ee\u7779\u5f20\u56db\u59d1\u7b49\u53bb\u8fdc\uff0c\u7f57\u5170\u5ffd\u7136\u8f7b\u8f7b\u54b3\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6c5f\u67ab\uff0c\u96ea\u3001\u971c\u4e24\u4e2a\u4e2b\u5934\u548c\u6885\u82b1\u59d1\u5a18\uff0c\u4ea4\u7ed9\u5f20\u56db\u59d1\uff0c\u4e0d\u7528\u591a\u7ba1\u4e86\uff0c\u4f46\u4f59\u4e0b\u7684\u4e8b\uff0c\u4f60\u8981\u597d\u597d\u7684\u5b89\u6392\u4e00\u4e0b\uff0c\u6d1b\u9633\u5c45\u7684\u8fce\u5bbe\u9601\u4e2d\u4f4f\u4e86\u4e0d\u5c11\u7684\u654c\u4eba\uff0c\u4ed6\u4eec\u4f1a\u4e0d\u4f1a\u51fa\u52a8\u5077\u88ad\uff0c\u53ef\u4e0d\u53ef\u9632\uff1f\u662f\u5426\u8981\u628a\u6211\u8001\u516c\u62db\u6765\u6b64\u5730\uff0c\u548c\u4ed6\u7814\u5546\u4e00\u4e0b\uff0c\u4e5f\u8be5\u4f5c\u4e2a\u51b3\u5b9a\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\uff0c\u5f53\u7136\uff0c\u665a\u8fdb\u6b63\u8981\u5411\u6b27\u9633\u524d\u8f88\u6709\u6240\u8bf7\u76ca\u2026\u2026\u201d\u6c5f\u673a\u9053\uff1a\u201c\u6211\u7acb\u523b\u6d3e\u4eba\u53bb\u8bf7\u3002\u201d \u3000\u3000\u201c\u55ef\uff01\u4ed6\u5728\u5251\u672f\u4e0a\u9020\u8be3\uff0c\u80dc\u6211\u5f88\u591a\uff0c\u2026\u2026\u201d\u7f57\u5170\u9053\uff1a\u201c\u4e5f\u8bb8\u8fd8\u53ef\u4ee5\u5e2e\u4f60\u7ea6\u51e0\u4e2a\u52a9\u62f3\u7684\u4eba\uff01\u201d \u3000\u3000\u5979\u4fee\u4e60\u5929\u72d0\u6b66\u529f\uff0c\u989c\u82e5\u6625\u82b1\uff0c\u5a07\u4e3d\u52a8\u4eba\uff0c\u600e\u4e48\u90fd\u4e0d\u50cf\u5df2\u5165\u4e2d\u5e74\u7684\u5973\u4eba\uff0c\u88c5\u51fa\u4e00\u526f\u8001\u6c14\u6a2a\u79cb\u7684\u6837\u5b50\u770b\u4e0a\u53bb\u5341\u5206\u53ef\u7b11\uff0c\u6c5f\u67ab\u5f88\u60f3\u7b11\uff0c\u4f46\u54ac\u7740\u7259\u5fcd\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u5927\u548c\u5c1a\u5374\u5fcd\u4e0d\u4f4f\u5475\u5475\u4e24\u58f0\uff0c\u9053\uff1a\u201c\u5bf9\u5bf9\u5bf9\uff0c\u8fd9\u4ef6\u4e8b\u4ea4\u7ed9\u6211\u548c\u5c1a\u53bb\u529e\uff0c\u987a\u4fbf\u628a\u90ed\u5929\u540c\u548c\u80e1\u5143\u5e26\u7ed9\u8001\u53eb\u5316\u5b50\uff0c\u4e0d\u8fc7\uff0c\u9093\u98de\u548c\u5201\u9e4f\u4e24\u4e2a\u4eba\uff0c\u6700\u597d\u5148\u522b\u60ca\u52a8\u4ed6\u4eec\uff0c\u660e\u5929\u4e2d\u5348\u8bf7\u4ed6\u4eec\u8fc7\u6765\uff0c\u5e26\u4ed6\u4eec\u4e00\u8d77\u5230\u4e07\u6885\u576a\u53bb\uff0c\u8def\u4e0a\u518d\u544a\u8bc9\u4ed6\u4eec\uff0c\u8ba9\u4ed6\u4eec\u56de\u5934\u65e0\u8def\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u8239\u5230\u6c5f\u5fc3\u56de\u5934\u96be\u2026\u2026\u201d\u7f57\u5170\u9053\uff1a\u201c\u903c\u4ed6\u4eec\u4f5c\u4e2a\u9009\u62e9\uff01\u201d \u3000\u3000\u201c\u597d\u4e3b\u610f\uff01\u5c31\u8fd9\u4e48\u51b3\u5b9a\u4e86\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u4e09\u5bab\u4e3b\u65e2\u7136\u6311\u660e\u4e86\u7ea6\u6218\u4e07\u6885\u576a\uff0c\u5927\u6982\u4e0d\u81f4\u4e8e\u53d1\u52a8\u591c\u88ad\uff0c\u4f46\u6709\u5907\u65e0\u60a3\uff0c\u8bf7\u5927\u5e08\u544a\u8bc9\u80e1\u840d\u3001\u6bb5\u4e5d\u4e00\u58f0\uff0c\u8981\u4ed6\u4eec\u5c0f\u5fc3\u4e00\u4e9b\uff0c\u4e00\u6709\u8b66\u8baf\uff0c\u7acb\u523b\u4f20\u62a5\u3002\u201d \u3000\u3000\u201c\u662f\uff01\u548c\u5c1a\u8fd9\u5c31\u53bb\u8bf7\u6b27\u9633\u5927\u4fa0\uff0c\u6700\u6162\u4e00\u4e2a\u65f6\u8fb0\uff0c\u8d76\u56de\u6765\u2026\u2026\u201d\u8bed\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u8981\u4e0d\u8981\u8001\u53eb\u5316\u9009\u6d3e\u51e0\u4e2a\u53ef\u7528\u7684\u4eba\u624b\uff0c\u5148\u53bb\u4e07\u6885\u576a\u4e2d\u57cb\u4f0f\uff0c\u5c31\u7b97\u5e2e\u4e0d\u4e0a\u5927\u5fd9\uff0c\u4f20\u4e2a\u6d88\u606f\uff0c\u4f5c\u4e2a\u63a5\u5e94\uff0c\u4e5f\u662f\u4e0d\u65e0\u5c0f\u8865\u3002\u201d \u3000\u3000\u6c5f\u67ab\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u8fd9\u4e00\u6218\u51f6\u60e8\u5728\u610f\u6599\u4e4b\u4e2d\uff0c\u5bf9\u65b9\u4eba\u624b\u591a\u6211\u5341\u500d\uff0c\u5fc5\u4f1a\u4f5c\u5f88\u4e25\u5bc6\u7684\u5e03\u7f6e\uff0c\u6d3e\u4ed6\u4eec\u57cb\u6869\u3001\u4f20\u8baf\uff0c\u65e0\u5f02\u662f\u7f8a\u5165\u864e\u53e3\uff0c\u8f6c\u544a\u94b1\u8001\u524d\u8f88\uff0c\u6700\u91cd\u8981\u7684\u4e24\u4ef6\u4e8b\uff0c\u4e00\u662f\u5168\u9762\u76d1\u89c6\u957f\u5b89\u57ce\u4e2d\u7684\u654c\u52bf\u53d8\u5316\uff0c\u4e8c\u662f\u8981\u9009\u51fa\u90a3\u4e00\u6279\u53ef\u9020\u4e4b\u6750\u7684\u5e74\u8f7b\u4eba\uff0c\u5168\u5fc3\u7814\u7ec3\u5200\u672f\u3001\u5251\u6cd5\uff0c\u90a3\u4e9b\u90fd\u662f\u6211\u9009\u53d6\u7684\u7cbe\u5947\u4e4b\u5b66\uff0c\u4ee5\u6280\u5de7\u4e3a\u4e3b\uff0c\u82e6\u7ec3\u4e0a\u4e09\u4e94\u4e2a\u6708\uff0c\u5e94\u8be5\u53ef\u4ee5\u6d3e\u4e0a\u7528\u573a\u4e86\u3002\u201d \u3000\u3000\u201c\u8001\u53eb\u5316\u544a\u8bc9\u8fc7\u548c\u5c1a\uff0c\u8fd9\u4e9b\u4eba\u5df2\u96c6\u4e2d\u5728\u4e00\u5904\u9690\u5bc6\u6240\u5728\uff0c\u5168\u529b\u82e6\u7ec3\uff0c\u4e0d\u8fc7\uff0c\u5165\u9009\u7684\u5341\u4e94\u4e2a\u4eba\uff0c\u53ea\u4f59\u4e0b\u516b\u4e2a\u4eba\u52c9\u53ef\u9002\u5e94\uff0c\u53e6\u5916\u4e03\u4e2a\u4eba\u5df2\u88ab\u8001\u53eb\u5316\u7ed9\u8c03\u6d3e\u4ed6\u7528\u4e86\u3002\u201d \u3000\u3000\u201c\u80fd\u6709\u516b\u4e2a\u4eba\uff0c\u4e5f\u7b97\u5dee\u5f3a\u4eba\u610f\u4e86\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u8981\u4ed6\u4eec\u5168\u5fc3\u82e6\u7ec3\u5427\uff01\u660e\u65e5\u5927\u6218\u4e4b\u540e\uff0c\u6211\u5982\u80fd\u62bd\u51fa\u65f6\u95f4\uff0c\u5c31\u53bb\u770b\u770b\u4ed6\u4eec\uff0c\u80fd\u4e0d\u80fd\u60f3\u51fa\u529e\u6cd5\u5e2e\u4ed6\u4eec\u4e00\u628a\uff1f\u201d \u3000\u3000\u201c\u5982\u679c\u90ed\u5929\u540c\u548c\u80e1\u5143\u80fd\u548c\u4ed6\u4eec\u5e38\u5904\u5728\u4e00\u8d77\uff0c\u65e2\u53ef\u4ece\u4e2d\u7763\u4fc3\u3001\u6307\u5bfc\uff0c\u4ed6\u4eec\u4e60\u7ec3\u6b66\u529f\uff0c\u4e5f\u53ef\u5bf9\u8fd9\u516b\u4eba\u7684\u5fc3\u6027\u4f5c\u756a\u89c2\u5bdf\u8003\u6838\uff0c\u65e5\u540e\u8fd9\u516b\u4eba\u5c31\u7531\u90ed\u5929\u540c\u548c\u80e1\u5143\u7387\u9886\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u2026\u2026\u201d\u6c5f\u67ab\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u90a3\u5c31\u6709\u52b3\u5927\u5e08\u8dd1\u4e00\u8d9f\u4e86\uff1f\u201d \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u5408\u638c\u4e00\u793c\uff0c\u8f6c\u8eab\u800c\u53bb\u3002 \u3000\u3000\u4e3e\u6b62\u95f4\uff0c\u5bf9\u6c5f\u67ab\u8868\u73b0\u51fa\u4e86\u76f8\u5f53\u7684\u5c0a\u656c\u3002 \u3000\u3000\u671b\u7740\u4e03\u5b9d\u548c\u5c1a\u6d88\u5931\u7684\u80cc\u5f71\uff0c\u7f57\u5170\u7728\u52a8\u4e00\u4e0b\u5927\u773c\u775b\uff0c\u9053\uff1a\u201c\u6211\u4eec\u592b\u5987\u3001\u6bcd\u5973\uff0c\u90fd\u662f\u4e8c\u5341\u5e74\u524d\u4e3a\u4f60\u5e03\u4e0b\u7684\u68cb\u5b50\uff0c\u5f20\u56db\u59d1\u4e5f\u5e94\u8be5\u662f\uff0c\u5979\u76db\u540d\u6b63\u7740\u65f6\uff0c\u5374\u7a81\u7136\u9000\u51fa\u4e86\u6c5f\u6e56\uff0c\u4f46\u5404\u79cd\u6280\u827a\u53c8\u7a81\u98de\u731b\u8fdb\uff0c\u5df2\u5165\u5316\u5883\uff0c\u8fd9\u8bf4\u660e\uff0c\u5979\u6295\u4e0b\u4e86\u5341\u51e0\u5e74\u82e6\u5fc3\u518d\u6f5c\u5fc3\u4fee\u7ec3\u3002 \u3000\u3000\u6211\u548c\u5979\u60c5\u540c\u59d0\u59b9\uff0c\u5979\u5374\u4ece\u672a\u900f\u8fc7\u4e86\u70b9\u53e3\u98ce\uff0c\u8fd9\u6b21\u5728\u957f\u5b89\u89c1\u9762\uff0c\u76f8\u5904\u4e00\u6bb5\u65f6\u95f4\u4e4b\u540e\uff0c\u6211\u624d\u53d1\u89c9\u5979\u5177\u6709\u7684\u6280\u827a\u3001\u624d\u6167\uff0c\u662f\u90a3\u4e48\u8d85\u7edd\u3001\u4e30\u76c8\uff0c\u6211\u4eec\u592b\u5987\u9664\u4e86\u5251\u672f\u6709\u70b9\u6210\u5c31\u4e4b\u5916\uff0c\u4e0d\u5982\u56db\u59d1\u751a\u591a\u3002 \u3000\u3000\u96ea\u3001\u971c\u4e24\u4e2a\u4e2b\u5934\uff0c\u66f4\u662f\u5929\u751f\u7684\u72d0\u95e8\u5f1f\u5b50\uff0c\u7adf\u7136\uff0c\u5bf9\u6211\u8fd9\u4f5c\u5a18\u7684\uff0c\u4e5f\u7528\u4e0a\u4e86\u5fc3\u673a\uff0c\u4e24\u5929\u4e4b\u524d\uff0c\u6211\u8fd8\u4e00\u76f4\u62c5\u5fc3\u5979\u4eec\u5e74\u7eaa\u5927\u8f7b\uff0c\u96be\u5165\u72d0\u9053\uff0c\u80fd\u6709\u56db\u4e94\u6210\u5929\u72d0\u6280\u827a\u6210\u5c31\uff0c\u5c31\u7b97\u4e0d\u9519\u4e86\uff0c\u6628\u5929\uff0c\u6211\u4e00\u756a\u76d8\u8bdd\uff0c\u903c\u5979\u4eec\u6f14\u7ec3\u4e00\u4e9b\u56f0\u96be\u7684\u6280\u827a\uff0c\u7ed9\u6211\u770b\u770b\uff0c\u5374\u628a\u6211\u5413\u4e86\u4e00\u8df3\uff0c\u2026\u2026\u201d \u3000\u3000\u201c\u600e\u4e48\u4e86\uff0c\u662f\u4e0d\u662f\u7834\u7efd\u5927\u591a\u2026\u2026\u201d\u6c5f\u67ab\u6025\u6025\u8ffd\u95ee\u3002 \u3000\u3000\u7f57\u5170\u5fae\u5fae\u4e00\u7b11\uff0c\u4f46\u7b11\u4e86\u4e00\u534a\uff0c\u5fc3\u4e2d\u7a81\u751f\u8b66\u60d5\uff0c\u7acb\u523b\u5bd2\u8d77\u4e00\u5f20\u8138\uff0c\u63a5\u9053\uff1a\u201c\u6211\u4e0d\u5fcd\u592a\u62c2\u9006\u8001\u516c\u5fc3\u610f\uff0c\u6240\u4ee5\uff0c\u6211\u6ca1\u6709\u4e60\u7ec3\u5929\u72d0\u6280\u827a\uff0c\u4f46\u6211\u719f\u8bb0\u53e3\u8bc0\uff0c\u6df1\u5370\u5fc3\u4e2d\u8fd1\u4e8c\u5341\u5e74\uff0c\u770b\u4e24\u4e2a\u4e2b\u5934\u7684\u6f14\u7ec3\uff0c\u542c\u5979\u7684\u56de\u7b54\uff0c\u6211\u5fc3\u4e2d\u63e3\u6478\u53e3\u8bc0\u4e2d\u7684\u7a8d\u8981\uff0c\u7adf\u7136\u662f\u9752\u51fa\u4e8e\u84dd\uff0c\u6709\u4e9b\u5730\u65b9\u6211\u8fd8\u4e0d\u592a\u660e\u767d\uff0c\u5979\u4eec\u5374\u80fd\u56de\u7b54\u7684\u5b8c\u5b8c\u6574\u6574\uff0c\u6211\u60f3\u8fd9\u90fd\u662f\u56db\u59d1\u548c\u4f60\u7684\u529b\u91cf\u3002 \u3000\u3000\u8fd9\u4e00\u4e2a\u6708\u7684\u65f6\u95f4\uff0c\u80dc\u5979\u4eec\u5c71\u4e2d\u4e00\u5e74\uff0c\u5979\u4eec\u5f00\u7a8d\u4e86\uff0c\u6709\u4e9b\u6ede\u788d\u4e4b\u5904\uff0c\u4e5f\u8c41\u7136\u8d2f\u901a\u4e86\u3002 \u3000\u3000\u72d0\u95e8\u6280\u827a\uff0c\u5927\u6982\u8981\u6709\u51e0\u5206\u72d0\u6c14\uff0c\u624d\u80fd\u767b\u5802\u5165\u5ba4\uff0c\u770b\u6837\u5b50\uff0c\u5979\u4eec\u5e94\u8be5\u6709\u516b\u5206\u4ee5\u4e0a\u7684\u6210\u5c31\u4e86\u2026\u2026\u3002\u201d \u3000\u3000\u201c\u771f\u662f\u6211\u4f5b\u6709\u7075\uff0c\u4e0a\u5929\u5782\u601c\u554a\uff01\u2026\u2026\u201d\u6c5f\u67ab\u5408\u638c\u5f53\u80f8\uff0c\u4e00\u7751\u8654\u8bda\u5730\u8bf4\uff1a\u201c\u770b\u8d77\u6765\u5979\u4eec\u7684\u72d0\u95e8\u6280\u827a\uff0c\u4e5f\u8bb8\u5df2\u7ecf\u8d85\u8fc7\u6211\u4e86\u3002\u201d \u3000\u3000\u201c\u6211\u4e0d\u77e5\u9053\u5929\u72d0\u592b\u4eba\u662f\u5426\u4fdd\u7559\u7edd\u6280\uff0c\u6ca1\u6709\u4f20\u6211\u53e3\u8bc0\uff0c\u4f46\u5c31\u6211\u6240\u77e5\u7684\u5929\u72d0\u6280\u827a\u4e2d\uff0c\u786e\u6709\u4e00\u4e9b\u4e0d\u9002\u5b9c\u7537\u4eba\u7ec3\u4e60\u2026\u2026\u201d\u7f57\u5170\u957f\u957f\u5401\u4e00\u53e3\u6c14\u53c8\u9053\uff1a\u201c\u6211\u8bf4\u4e86\u534a\u5929\uff0c\u4e5f\u8be5\u95ee\u95ee\u4f60\u4e86\uff0c\u4e03\u5b9d\u548c\u5c1a\u662f\u4e0d\u662f\u4e09\u4f4d\u8001\u4eba\u5bb6\u5b89\u6392\u7ed9\u4f60\u7684\u5e2e\u624b\uff1f\u201d \u3000\u3000\u201c\u662f\uff01\u5982\u679c\u665a\u8f88\u6ca1\u6709\u770b\u9519\uff0c\u4ed6\u5df2\u5f97\u4e86\u6211\u5e08\u7236\u5341\u4e4b\u4e03\u516b\u7684\u771f\u4f20\u2026\u2026\u201d \u3000\u3000\u201c\u4f60\u7684\u5e08\u7236\u662f\u2026\u2026\u201d \u3000\u3000\u201c\u65e0\u76f8\u5927\u5e08\u3002\u201d \u3000\u3000\u201c\u5929\u72d0\u592b\u4eba\u5462\uff1f\u201d \u3000\u3000\u201c\u5979\u8ba9\u6211\u53eb\u5979\u59e8\u5a18\uff0c\u4f46\u6211\u5728\u5b66\u827a\u7684\u8fc7\u7a0b\u4e2d\uff0c\u6709\u4e00\u534a\u65f6\u95f4\uff0c\u8ddf\u5979\u751f\u6d3b\u5728\u4e00\u8d77\u3002\u201d \u3000\u3000\u201c\u91d1\u4e39\u4e66\u751f\u5462\uff1f\u201d \u3000\u3000\u201c\u662f\u6211\u517b\u7236\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u4e8b\u5b9e\u4e0a\u4e09\u4f4d\u8001\u4eba\u5bb6\uff0c\u90fd\u662f\u6211\u7684\u6388\u4e1a\u6069\u5e08\u3002\u201d \u3000\u3000\u201c\u8fd9\u5c31\u96be\u602a\u4e86\uff0c\u4f60\u4e00\u4eba\u517c\u6b66\u6797\u4e09\u5723\u6280\u827a\u3002\u201d \u3000\u3000\u201c\u53ea\u6050\u662f\u535a\u800c\u4e0d\u7cbe\uff0c\u6709\u8d1f\u4e09\u4f4d\u8001\u4eba\u5bb6\u7684\u6559\u5bfc\uff01\u201d \u3000\u3000\u7f57\u5170\u795e\u60c5\u51c4\u82e6\u7684\u9053\uff1a\u201c\u4e0d\u8981\u9a97\u6211\uff0c\u6c5f\u67ab\uff0c\u4e09\u4f4d\u8001\u4eba\u5bb6\u662f\u4e0d\u662f\u771f\u7684\u6b7b\u4e86\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u5f3a\u5fcd\u7740\u4e24\u773c\u6cea\u6c34\uff0c\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u665a\u8fdb\u4e0d\u6562\u7792\u4f60\uff0c\u4f46\u6b64\u4e8b\u7edd\u4e0d\u80fd\u5f20\u626c\u51fa\u53bb\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u8fd9\u4ef6\u4e8b\uff0c\u6211\u4e00\u5b9a\u5b88\u53e3\u5982\u74f6\uff0c\u7edd\u4e0d\u544a\u8bc9\u7b2c\u4e09\u4e2a\u4eba\uff0c\u2026\u2026\u201d\u7f57\u5170\u9053\uff1a \u3000\u3000\u201c\u5927\u548c\u5c1a\u5982\u6b64\u6765\u5386\uff0c\u6211\u5c31\u653e\u5fc3\u4e86\uff0c\u4e0d\u8fc7\uff0c\u4f60\u4eca\u5929\u4e0d\u8bf4\u660e\u767d\uff0c\u6211\u8fd8\u662f\u770b\u4e0d\u900f\u4ed6\u7684\u5e95\u7ec6\uff0c\u4ed6\u90a3\u4efd\u88c5\u75f4\u626e\u5446\u7684\u672c\u9886\uff0c\u53ef\u771f\u662f\u5230\u5bb6\u5566\uff01\u201d \u3000\u3000\u201c\u6211\u4eec\u6ca1\u6709\u805a\u9996\u4e4b\u524d\uff0c\u8c01\u4e5f\u4e0d\u80fd\u6cc4\u9732\u98ce\u58f0\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff0c\u201c\u90a3\u4f1a\u5f15\u8d77\u5929\u738b\u95e8\u3001\u5929\u9a6c\u5802\u548c\u5185\u5bab\u4e00\u7cfb\u4e2d\u4eba\uff0c\u5168\u529b\u7684\u8ffd\u6740\uff0c\u4f60\u4eec\u592b\u5987\uff0c\u9690\u4e8e\u6df1\u5c71\uff0c\u5f20\u56db\u59d1\u4e5f\u9000\u51fa\u4e86\u6c5f\u6e56\uff0c\u53ea\u6709\u5927\u548c\u5c1a\u5728\u6c5f\u6e56\u4e0a\u8dd1\u6765\u8dd1\u53bb\uff0c\u4ed6\u65e2\u8981\u63a2\u67e5\u7fa4\u9b54\u7684\u52a8\u6001\uff0c\u53c8\u8981\u5077\u7ec3\u65e0\u76f8\u795e\u529f\uff0c\u8fd8\u4e0d\u80fd\u88ab\u4eba\u77a7\u51fa\u6765\uff0c\u8fd9\u5341\u51e0\u5e74\u5e73\u5b89\u65e0\u4e8b\uff0c\u5927\u667a\u82e5\u611a\uff0c\u5b9e\u662f\u5f53\u4e4b\u65e0\u6127\u4e86\u201d \u3000\u3000\u201c\u8bf4\u7684\u662f\u554a\uff01\u2026\u2026\u201d\u7f57\u5170\u9053\uff1a\u201c\u60f3\u4e00\u60f3\uff0c\u6211\u4eec\u90fd\u4e0d\u5982\u4ed6\uff0c\u53ef\u662f\uff0c\u8fd9\u4e9b\u5e74\uff0c\u6211\u4eec\u4e5f\u618b\u7684\u591f\u8f9b\u82e6\u4e86\uff0c\u4eb2\u5982\u592b\u59bb\uff0c\u6709\u5f88\u591a\u8bdd\uff0c\u4e5f\u4e0d\u80fd\u8bf4\u660e\u767d\uff0c\u6211\u548c\u5929\u72d0\u592b\u4eba\u4e4b\u95f4\u7684\u79d8\u5bc6\uff0c\u5230\u73b0\u5728\uff0c\u8fd8\u6709\u5f88\u591a\u6ca1\u6709\u544a\u8bc9\u8fc7\u6211\u8001\u516c\u3002\u201d \u3000\u3000\u201c\u90a3\u5012\u4e0d\u7528\u653e\u5728\u5fc3\u4e0a\uff0c\u6b27\u9633\u524d\u8f88\u627f\u7ee7\u4e86\u6211\u4e49\u7236\u7684\u5251\u672f\u7cbe\u4e1a\uff0c\u6211\u76f8\u4fe1\u4ed6\u53d7\u8fc7\u6211\u4e49\u7236\u7684\u4eb2\u81ea\u6307\u70b9\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\uff1f\u4f60\u8bf4\u91d1\u4e39\u4e66\u751f\u4eb2\u81ea\u6307\u70b9\u8fc7\u6211\u8001\u516c\u5251\u6cd5\uff0c\u600e\u4e48\uff0c\u4ed6\u6ca1\u6709\u544a\u8bc9\u8fc7\u6211\uff1f\u2026\u2026\u201d \u3000\u3000\u201c\u56e0\u4e3a\uff0c\u4ed6\u548c\u4f60\u4e00\u6837\uff0c\u5bf9\u91d1\u4e39\u4e66\u751f\u6709\u4e86\u627f\u8bfa\uff0c\u6240\u4ee5\uff0c\u53ea\u80fd\u4f20\u4f60\u5251\u6cd5\uff0c\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u5374\u4e0d\u80fd\u544a\u8bc9\u4f60\u4ed6\u548c\u91d1\u4e39\u4e66\u751f\u89c1\u9762\u7684\u4e8b\u3002\u201d \u3000\u3000\u7f57\u5170\u5fcd\u4e0d\u4f4f\u7b11\u4e86\u8d77\u6765\uff0c\u4f46\u7acb\u523b\u4e3e\u8d77\u8863\u8896\uff0c\u534a\u63a9\u7389\u9762\uff0c\u9053\uff1a\u201c\u6211\u4eec\u5fc3\u4e2d\uff0c\u90fd\u85cf\u4e00\u4efd\u9690\u5bc6\uff0c\u90a3\u5c31\u8c01\u4e5f\u4e0d\u5403\u4e8f\u4e86\uff0c\u6211\u6559\u51fa\u4e24\u4e2a\u5c0f\u72d0\u5973\uff0c\u603b\u7b97\u672a\u8d1f\u5929\u72d0\u592b\u4eba\u7684\u91cd\u6258\uff0c\u6211\u8001\u516c\u4e5f\u6697\u4e2d\u6536\u4e86\u4e94\u4e2a\u5f1f\u5b50\uff0c\u4e5f\u7b97\u5bf9\u91d1\u4e39\u4e66\u751f\u6709\u4e2a\u4ea4\u4ee3\u4e86\u3002\u201d \u3000\u3000\u201c\u6211\u60f3\u6b27\u9633\u524d\u8f88\u4e0d\u4f1a\u81ea\u4f5c\u4e3b\u5f20\u6536\u5f92\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u4ed6\u548c\u4f60\u9690\u5c45\u6df1\u5c71\uff0c\u53c8\u5f80\u54ea\u91cc\u53bb\u627e\u4e94\u4e2a\u9002\u5408\u4e60\u7ec3\u4e0a\u4e58\u5251\u6cd5\u7684\u5f1f\u5b50\u5462\uff1f\u201d \u3000\u3000\u201c\u5bf9\u5440\uff01\u4e94\u4e2a\u4eba\u4ece\u54ea\u91cc\u6765\u7684\u5462\uff1f\u2026\u2026\u201d\u7f57\u5170\u9053\uff1a\u201c\u4ed6\u4ece\u6ca1\u6709\u79bb\u5f00\u6211\u4e09\u5929\u4ee5\u4e0a\uff0c\u8981\u627e\u4e94\u4e2a\u8d44\u8d28\u4e0a\u597d\u7684\u7ec3\u5251\u4eba\u624d\uff0c\u4e0d\u8981\u4e00\u5e74\uff0c\u4e5f\u5f97\u534a\u8f7d\uff0c\u8fd8\u5f97\u8d70\u4e2a\u4e09\u4e94\u7701\uff0c\u5230\u5904\u5bfb\u89c5\uff0c\u96be\u9053\u662f\u91d1\u4e39\u4e66\u751f\u9001\u53bb\u7684\uff1f\u201d \u3000\u3000\u201c\u6211\u60f3\u662f\u4e49\u7236\u7684\u6388\u610f\uff0c\u4e00\u4e0b\u5b50\u4f20\u6388\u4e94\u4e2a\u4eba\uff0c\u5fc5\u7136\u8981\u7ec3\u4e00\u5957\u5408\u51fb\u7684\u5251\u9635\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a \u3000\u3000\u201c\u4e49\u7236\u5185\u4f24\u6781\u91cd\uff0c\u52c9\u5f3a\u6491\u7740\u6d3b\u4e0b\u53bb\uff0c\u6050\u6015\u6ca1\u6709\u65f6\u95f4\u5e2e\u4ed6\u5bfb\u5f97\u5f1f\u5b50\uff0c\u2026\u2026\u201d \u3000\u3000\u201c\u54ea\u4f1a\u662f\u8c01\u5462\uff1f\u603b\u4e0d\u4f1a\u662f\u5929\u72d0\u592b\u4eba\u5427\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u4f55\u4e0d\u731c\u4e00\u4e0b\u5462\uff1f\u201d \u3000\u3000\u201c\u4e03\u5b9d\u548c\u5c1a\u2026\u2026\u201d\u7f57\u5170\u9053\uff1a\u201c\u4e00\u5b9a\u662f\u4ed6\u3002\u201d \u3000\u3000\u201c\u5341\u4e4b\u516b\u4e5d\u4e86\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u6211\u4eec\u7684\u80a9\u5934\u4e0a\uff0c\u90fd\u625b\u4e86\u4e00\u526f\u6c89\u91cd\u7684\u62c5\u5b50\uff0c\u6211\u4eec\u6ca1\u6709\u540d\u5229\u4e4b\u5ff5\uff0c\u4e5f\u4e0d\u4f1a\u8ba1\u8f83\u6bc1\u8a89\uff0c\u6211\u4eec\u53ea\u6709\u4e00\u4e2a\u76ee\u7684\uff0c\u4f7f\u6b66\u6797\u91cd\u89c1\u5149\u660e\u3002\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\uff0c\u4f60\u5927\u4f24\u521d\u6108\uff0c\u56de\u623f\u53bb\u4f11\u606f\u4e00\u4e0b\u5427\uff01\u2026\u2026\u201d\u7f57\u5170\u9053\uff1a\u201c\u6211\u5728\u5385\u4e2d\u7b49\u4ed6\u4eec\u3002\u201d \u3000\u3000\u6c5f\u67ab\u786e\u6709\u5f88\u591a\u4e8b\u8981\u4ed4\u7ec6\u7684\u60f3\u4e00\u60f3\uff0c\u7f13\u6b65\u884c\u5165\u5367\u5ba4\uff0c\u63a9\u4e0a\u623f\u95e8\u3002 \u3000\u3000\u5f20\u56db\u59d1\u6b63\u5728\u6307\u70b9\u6885\u82b1\u9041\u672f\uff0c\u4e07\u6885\u576a\u79ef\u96ea\u672a\u5316\uff0c\u4e07\u682a\u6885\u6811\u6210\u6797\uff0c\u6885\u82b1\u5b66\u4e60\u7684\u91cd\u70b9\uff0c\u4e5f\u4ee5\u9041\u6728\u672f\u548c\u96ea\u91cc\u85cf\u8eab\u7684\u6280\u5de7\u4e3a\u4e3b\u3002 \u3000\u3000\u8fd9\u79cd\u5947\u95e8\u672f\u6cd5\uff0c\u4ee5\u673a\u53d8\u3001\u7ec6\u5fc3\u6700\u91cd\u8981\uff0c\u914d\u5408\u4e0a\u5230\u5904\u85cf\u653e\u7684\u9053\u5177\u53ca\u5404\u79cd\u8272\u5f69\u7684\u8863\u670d\uff0c\u5e7b\u53d8\u51fa\u914d\u5408\u666f\u8272\u7684\u4e07\u822c\u5f62\u8c61\u3002 \u3000\u3000\u6885\u82b1\u5f88\u806a\u660e\uff0c\u5b66\u7684\u4e5f\u7b97\u662f\u5947\u95e8\u6b66\u529f\uff0c\u4e60\u6027\u5341\u5206\u76f8\u8fd1\uff0c\u4f46\u4e5f\u7ec3\u7684\u5f88\u8f9b\u82e6\uff0c\u4e34\u9635\u78e8\u67aa\uff0c\u81ea\u662f\u8981\u5168\u529b\u4ee5\u8d74\uff0c\u597d\u5728\u541f\u96ea\u3001\u541f\u971c\u7ed9\u5979\u5f88\u5927\u7684\u5e2e\u52a9\uff0c\u4e0d\u541d\u6307\u70b9\u5979\u65bd\u5c55\u5929\u72d0\u8eab\u6cd5\uff0c\u4ee5\u9002\u5e94\u5f62\u4f53\u4e0a\u7684\u53d8\u5316\u3002 \u3000\u3000\u5728\u5185\u5bab\u4e00\u7cfb\u7684\u5973\u5f1f\u5b50\u4e2d\uff0c\u6885\u82b1\u662f\u975e\u5e38\u6770\u51fa\u7684\u4eba\u624d\uff0c\u4f46\u548c\u96ea\u3001\u971c\u4e8c\u5973\u4e00\u6bd4\uff0c\u6885\u82b1\u7acb\u523b\u6709\u7740\u76f8\u5dee\u5f88\u8fdc\u7684\u611f\u89c9\uff0c\u4e5f\u5c31\u66f4\u7528\u5fc3\u7684\u53bb\u4f53\u4f1a\u3001\u5b66\u4e60\u3002 \u3000\u3000\u65c1\u95e8\u6280\u827a\uff0c\u5df2\u5165\u7a8d\u95e8\uff0c\u5b66\u8d77\u6765\u5c31\u8fdb\u6b65\u5f88\u5feb\uff0c\u96ea\u91cc\u85cf\u8eab\uff0c\u591a\u501f\u91cd\u9053\u5177\u5e2e\u52a9\uff0c\u4f46\u4e5f\u6709\u7740\u5f88\u597d\u73a9\u7684\u611f\u89c9\u3002 \u3000\u3000\u4e09\u66f4\u5de6\u53f3\uff0c\u6885\u82b1\u6b63\u5b66\u7684\u5174\u81f4\u9ad8\u6da8\uff0c\u5f20\u56db\u59d1\u5374\u7a81\u7136\u53eb\u505c\uff0c\u8981\u4e09\u5973\u6574\u7406\u597d\u8863\u7269\u3001\u9053\u5177\u3001\u5175\u5203\u6697\u5668\uff0c\u795e\u60c5\u8083\u7136\u7684\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u73b0\u5728\u5c31\u8d70\uff0c\u8d76\u5230\u767e\u6885\u576a\u53bb\uff0c\u56db\u66f4\u4ee5\u524d\uff0c\u4e00\u5b9a\u8981\u5230\uff0c\u5e03\u7f6e\u597d\u540e\uff0c\u5c31\u5728\u96ea\u4e2d\u5750\u606f\uff0c\u591c\u51c9\u5982\u6c34\uff0c\u5bd2\u98ce\u4f3c\u5200\uff0c\u5728\u96ea\u5806\u4e2d\u6253\u5750\u8c03\u606f\uff0c\u5f53\u7136\u5341\u5206\u8f9b\u82e6\uff0c\u5fc5\u9700\u6709\u575a\u5f3a\u7684\u610f\u5fd7\u529b\uff0c\u624d\u80fd\u514b\u670d\uff0c\u6211\u5df2\u5907\u597d\u4e86\u4e00\u4e9b\u5e72\u7cae\u3001\u5364\u5473\uff0c\u4f60\u4eec\u5e26\u4e00\u4e9b\u5728\u8eab\u4e0a\uff0c\u5982\u679c\u6211\u63a8\u65ad\u4e0d\u9519\uff0c\u5929\u8272\u4e00\u4eae\uff0c\u4e09\u5bab\u4e3b\u5c31\u4f1a\u6d3e\u4eba\u5728\u4e07\u6885\u576a\u5b89\u6392\u57cb\u4f0f\uff0c\u6211\u4eec\u7b2c\u4e00\u5173\u662f\u8981\u8eb2\u8fc7\u4ed6\u4eec\u7684\u641c\u67e5\uff0c\u85cf\u5728\u96ea\u5806\u4e2d\uff0c\u4e00\u76f4\u8981\u7b49\u5230\u660e\u5929\u7533\u65f6\u3002 \u3000\u3000\u8fd9\u662f\u4e00\u6bb5\u5f88\u957f\u5f88\u82e6\u7684\u65f6\u95f4\uff0c\u5982\u4e0d\u80fd\u5584\u7528\u8863\u7269\u3001\u9053\u5177\uff0c\u4fdd\u6301\u4f53\u6e29\uff0c\u5f88\u53ef\u80fd\u51bb\u50f5\u5728\u90a3\u91cc\uff0c\u6240\u4ee5\uff0c\u8981\u85cf\u5f97\u9690\u5bc6\u5b89\u5168\uff0c\u624d\u80fd\u653e\u5fc3\u7684\u8fd0\u529f\u8c03\u606f\uff0c\u4fdd\u6301\u4f4f\u4f53\u80fd\u3001\u6218\u529b\uff0c\u8bb0\u7740\uff0c\u975e\u5230\u4e07\u4e0d\u5f97\u5df2\uff0c\u4e0d\u53ef\u548c\u4eba\u52a8\u624b\uff0c\u4e00\u65e6\u51fa\u624b\uff0c\u4e5f\u8981\u4e00\u51fb\u53d6\u547d\uff0c\u800c\u4e14\u8981\u4ee5\u6700\u5feb\u7684\u65b9\u6cd5\u85cf\u597d\u654c\u4eba\u7684\u5c38\u4f53\uff0c\u6d88\u9664\u75d5\u8ff9\uff0c\u770b\u8fc7\u73b0\u573a\u5f62\u52bf\u4e4b\u540e\uff0c\u6211\u518d\u7ed9\u4f60\u4eec\u4e00\u4e9b\u6307\u70b9\u3002\u201d \u3000\u3000\u56db\u4e2a\u4eba\u5206\u8d1f\u7740\u5927\u6279\u7684\u8863\u7269\u9053\u5177\uff0c\u6bcf\u4e2a\u4eba\u90fd\u80cc\u4e86\u516d\u5341\u65a4\u4ee5\u4e0a\u7684\u91cd\u91cf\uff0c\u501f\u591c\u8272\u63a9\u62a4\uff0c\u9041\u51fa\u4e86\u6d1b\u9633\u5c45\u3002 \u3000\u3000\u79bb\u5f00\u65f6\uff0c\u56db\u4e2a\u4eba\u90fd\u7a7f\u7740\u9ed1\u8272\u7684\u591c\u884c\u8863\u773c\uff0c\u4f46\u63a5\u8fd1\u4e07\u6094\u576a\u65f6\uff0c\u56db\u4e2a\u4eba\u90fd\u5df2\u6362\u6210\u4e86\u767d\u8272\u7684\u8863\u7740\uff0c\u767d\u5e03\u5305\u5934\uff0c\u767d\u8272\u7684\u9774\u5b50\uff0c\u8138\u4e0a\u4e5f\u6d82\u6ee1\u4e86\u767d\u8272\u7684\u7c89\u6599\u3002 \u3000\u3000\u8fd9\u4e00\u8def\u56db\u4e2a\u4eba\u5e76\u672a\u505c\u4e0b\u6765\u6362\u7a7f\u8863\u670d\u3002 \u3000\u3000\u539f\u6765\u5f20\u56db\u59d1\u7684\u6613\u5bb9\u6cd5\u4e4b\u4e00\uff0c\u5c31\u662f\u5728\u884c\u52a8\u4e2d\u53d8\u5316\u81ea\u5df1\uff0c\u5f53\u7136\uff0c\u90a3\u4e9b\u8863\u670d\u7684\u8bbe\u8ba1\u7f1d\u5236\uff0c\u4e5f\u548c\u4e00\u822c\u7684\u8863\u670d\u4e0d\u540c\u3002 \u3000\u3000\u56db\u4e2a\u7f8e\u4e3d\u7684\u5973\u4eba\uff0c\u5f88\u5feb\u6d88\u5931\u5728\u4e07\u6885\u576a\u7684\u96ea\u5730\u4e2d\u3002 \u3000\u3000\u6885\u82b1\u521d\u6b21\u884c\u52a8\uff0c\u867d\u7136\u5168\u529b\u4ee5\u8d74\uff0c\u4e5f\u95f9\u5f97\u624b\u5fd9\u811a\u4e71\uff0c\u4f46\u603b\u7b97\u662f\u5dee\u5f3a\u4eba\u610f\uff0c\u6ca1\u6709\u505c\u4e0b\u811a\u6b65\u6765\u3002 \u3000\u3000\u6562\u60c5\u4e94\u884c\u9041\u672f\uff0c\u65bd\u7528\u8d77\u6765\uff0c\u7adf\u7136\u662f\u5982\u6b64\u7684\u8f9b\u82e6\u3002 \u3000\u3000\u6bd4\u8d77\u5f20\u56db\u59d1\u548c\u4e09\u5973\uff0c\u6c5f\u67ab\u5c31\u8212\u670d\u591a\u4e86\u3002 \u3000\u3000\u4ed6\u73b0\u5728\u6b63\u5750\u5728\u95e8\u7a97\u7d27\u95ed\u7684\u5ba2\u5385\u4e2d\uff0c\u9762\u524d\u7684\u516b\u4ed9\u684c\u4e0a\u6709\u9152\u6709\u83dc\uff0c\u9ad8\u540a\u7684\u5bab\u706f\u4e0b\uff0c\u56f4\u5750\u7740\u6b27\u9633\u662d\u3001\u7f57\u5170\u548c\u4e03\u5b9d\u548c\u5c1a\u3002 \u3000\u3000\u4f46\u6700\u5148\u5f00\u53e3\u662f\u7f57\u5170\uff0c\u5979\u773c\u775b\u77aa\u7740\u6b27\u9633\u662d\uff0c\u9053\uff1a\u201c\u4f60\u7684\u4e94\u4e2a\u5f1f\u5b50\u5e26\u6765\u4e86\u6ca1\u6709\uff1f\u201d \u3000\u3000\u6b27\u9633\u662d\u5fae\u5fae\u4e00\u6014\uff0c\u9053\uff1a\u201c\u5bf9\u4e0d\u4f4f\u5566\uff01\u7f57\u5170\uff0c\u8fd9\u4ef6\u4e8b\u4e00\u76f4\u6ca1\u6709\u544a\u8bc9\u4f60\uff0c\u6700\u91cd\u8981\u7684\u662f\uff0c\u6211\u4e0d\u60f3\u8ba9\u96ea\u3001\u971c\u4e24\u4e2a\u4e2b\u5934\u548c\u4ed6\u4eec\u73a9\u5728\u4e00\u8d77\uff0c\u90a3\u4f1a\u5206\u4e86\u4ed6\u4eec\u7ec3\u5251\u7684\u610f\u5fd7\u3001\u7cbe\u795e\u3002\u201d \u3000\u3000\u201c\u54fc\uff01\u6211\u8fd8\u4e0d\u662f\u77e5\u9053\u4e86\uff0c\u8fd9\u4e9b\u4e8b\u90fd\u5df2\u8fc7\u53bb\uff0c\u4e0d\u8c08\u4e5f\u7f62\uff01\u73b0\u5728\u6700\u91cd\u8981\u7684\u662f\uff0c\u5982\u4f55\u628a\u4ed6\u4eec\u62db\u6765\u957f\u5b89\uff1f\u201d \u3000\u3000\u201c\u5df2\u7ecf\u6765\u4e86\u2026\u2026\u201d\u6b27\u9633\u662d\u7b11\u9053\uff1a\u201c\u4f60\u770b\u4e0d\u51fa\u6765\u5427\uff01\u6211\u8fd9\u4e2a\u65b9\u65b9\u6b63\u6b63\u7684\u4eba\uff0c\u4e5f\u4f1a\u4f5c\u51fa\u5982\u6b64\u9690\u5bc6\u7684\u4e8b\u60c5\uff1f\u201d \u3000\u3000\u201c\u522b\u592a\u5f97\u610f\uff0c\u6211\u4e5f\u6709\u5f88\u591a\u79d8\u5bc6\u4f60\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u6b27\u9633\u662d\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u7f57\u5170\uff0c\u522b\u592a\u5728\u610f\uff0c\u6211\u53d7\u91d1\u4e39\u2026\u2026\u201d \u3000\u3000\u7a81\u7136\u4f4f\u53e3\u4e0d\u8a00\uff0c\u671b\u7740\u6c5f\u67ab\u82e6\u7b11\u3002 \u3000 \u7b2c\u5341\u4e03\u56de\u3000\u4f17\u4fa0\u58eb\u795e\u529f\u65a9\u9b54\u5934 \u3000 \u3000\u3000\u201c\u73b0\u5728\uff0c\u4ec0\u4e48\u90fd\u53ef\u4ee5\u8bf4\u4e86\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u6b27\u9633\u524d\u8f88\uff0c\u6211\u4eec\u4e94\u4e2a\u4eba\u5df2\u5168\u90e8\u4f1a\u9f50\uff0c\u518d\u6ca1\u6709\u522b\u7684\u5e2e\u624b\u4e86\u3002\u201d \u3000\u3000\u201c\u5927\u548c\u5c1a\u88c5\u4f5c\u7684\u672c\u9886\uff0c\u53ef\u771f\u662f\u4e00\u7b49\u9ad8\u624b\uff0c\u2026\u2026\u201d\u6b27\u9633\u662d\u9053\uff1a\u201c\u6211\u4eec\u89c1\u8fc7\u9762\u51e0\u4e2a\u6708\u4e86\uff0c\u6211\u662f\u4e00\u70b9\u4e5f\u77a7\u4e0b\u51fa\u6765\uff01\u201d \u3000\u3000\u201c\u6bd4\u8d77\u4f60\u6b27\u9633\u5144\uff0c\u548c\u5c1a\u8fd8\u68cb\u5dee\u4e00\u7740\uff0c\u4f60\u90a3\u4e94\u4e2a\u5f92\u5f1f\uff0c\u5c31\u4f4f\u5728\u5e7d\u8c37\u8fd1\u5904\uff0c\u8ddd\u5e9c\u4e0a\u4e0d\u8fc7\u6570\u91cc\u4e4b\u9065\uff0c\u4f46\u4f60\u7adf\u7136\u80fd\u4f7f\u5ac2\u592b\u4eba\u5341\u51e0\u5e74\u6ca1\u6709\u53d1\u89c9\u2026\u2026\u201d \u3000\u3000\u6b27\u9633\u662d\u63a5\u9053\uff1a\u201c\u6211\u61c2\u4e86\uff0c\u662f\u4f60\uff0c\u5927\u548c\u5c1a\uff0c\u662f\u4f60\u628a\u4ed6\u4eec\u9001\u53bb\u7684\u3002\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u63a5\u9053\uff1a\u201c\u4f46\u4ed6\u4eec\u8eab\u4e0a\u7684\u4e66\u4fe1\uff0c\u786e\u662f\u91d1\u4e39\u4e66\u751f\u7684\u624b\u7b14\uff0c\u548c\u5c1a\u5949\u547d\u5e2e\u4f60\u5bfb\u627e\u4e94\u4e2a\u5f1f\u5b50\uff0c\u4e5f\u8d1f\u8d23\u628a\u4ed6\u4eec\u9001\u7ed9\u4f60\uff0c\u4f46\u4ed6\u4eec\u4e0d\u4f1a\u8bb0\u5f97\u6211\uff0c\u4e00\u5219\u662f\u4ed6\u4eec\u5e74\u7eaa\u592a\u5c0f\uff0c\u90fd\u53ea\u6709\u4e94\u516d\u5c81\u7684\u5e74\u7eaa\uff0c\u4f55\u51b5\u548c\u5c1a\u8fd8\u5e38\u5e38\u70b9\u4ed6\u4eec\u7684\u7761\u7a74\uff0c\u4ee5\u65b9\u4fbf\u884c\u52a8\u3002\u2026\u2026\u201d \u3000\u3000\u201c\u800c\u4e14\uff0c\u7a7f\u7684\u4e5f\u4e0d\u662f\u548c\u5c1a\u8863\u670d\uff1f\u2026\u2026\u201d\u7f57\u5170\u63a5\u9053\uff1a\u201c\u6234\u4e0a\u4e00\u9876\u5047\u53d1\uff0c\u8131\u53bb\u50e7\u888d\uff0c\u90a3\u5c31\u4e00\u70b9\u4e0d\u7740\u75d5\u8ff9\u4e86\u3002\u201d \u3000\u3000\u201c\u963f\u5f25\u9640\u4f5b\uff0c\u7f6a\u8fc7\uff0c\u7f6a\u8fc7\u3002\u201d \u3000\u3000\u201c\u5927\u548c\u5c1a\uff0c\u4f60\u628a\u6211\u6298\u817e\u60e8\u4e86\uff0c\u4e94\u4e2a\u5c0f\u5a03\u5a03\uff0c\u95f9\u5f97\u6211\u624b\u5fd9\u811a\u4e71\uff0c\u8981\u6211\u79d8\u5bc6\u7684\u4f20\u6388\u4ed6\u4eec\u5251\u672f\uff0c\u867d\u59bb\u513f\u4e5f\u4e0d\u80fd\u6cc4\u9732\uff0c\u90a3\u4e2a\u82e6\u53ef\u771f\u662f\u53d7\u7684\u5927\u4e86\u3002\u201d \u3000\u3000\u201c\u6b27\u9633\u5144\uff0c\u6211\u548c\u5c1a\u53d7\u7684\u82e6\u4e0d\u6bd4\u4f60\u5c0f\uff0c\u6211\u5728\u90a3\u5ea7\u5c71\u8c37\u4e2d\u4f4f\u4e86\u534a\u5e74\u4e4b\u4e45\uff0c\u4f60\u4eec\u592b\u59bb\u56e2\u805a\u65f6\uff0c\u6211\u5c31\u4ee3\u4f60\u53d7\u82e6\uff0c\u7167\u987e\u4ed6\u4eec\u5403\u559d\uff01\u4fdd\u62a4\u4ed6\u4eec\u5f97\u5b89\u5168\uff0c\u76f4\u5230\u4ed6\u4eec\u5b66\u4f1a\u7167\u987e\u81ea\u5df1\u65f6\uff0c\u6211\u624d\u79bb\u5f00\uff0c\u6bcf\u9694\u4e00\u4e24\u5e74\uff0c\u8fd8\u8981\u66ff\u4ed6\u4eec\u9001\u4e9b\u8863\u670d\u3002\u201d \u3000\u3000\u201c\u5509\uff01\u6211\u8fd8\u4e00\u76f4\u8ba4\u4e3a\u662f\u91d1\u4e39\u4e66\u751f\u524d\u8f88\u5728\u6697\u4e2d\u5e2e\u5fd9\uff0c\u539f\u6765\u662f\u4f60\u548c\u5c1a\u7167\u987e\uff0c\u2026\u2026\u201d\u6b27\u9633\u662d\u9053\uff1a\u201c\u90a3\u65f6\u4faf\uff0c\u4f60\u5df2\u7ecf\u77e5\u9053\u4e86\u6211\u4eec\u592b\u5987\u7684\u8eab\u4efd\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u80af\u89c4\u8eab\u76f8\u89c1\u5462\uff1f\u201d \u3000\u3000\u201c\u548c\u5c1a\u5949\u6709\u4ee4\u8c15\uff0c\u4e0d\u89c1\u6c5f\u67ab\u4e4b\u9762\uff0c\u4e0d\u5f97\u6cc4\u9732\u8eab\u4efd\u3002\u201d \u3000\u3000\u201c\u5176\u5b9e\uff0c\u5927\u548c\u5c1a\u548c\u6211\u89c1\u4e86\u9762\uff0c\u4e5f\u4e00\u76f4\u5728\u626e\u75f4\u88c5\u50bb\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u76f4\u5230\u6211\u88c5\u4f5c\u53d7\u4f24\uff0c\u4ed6\u4ee5\u5185\u529b\u52a9\u6211\u884c\u529f\uff0c\u624d\u7b97\u63ed\u9732\u4e86\u4ed6\u7684\u6765\u5386\u3002\u201d \u3000\u3000\u201c\u53ef\u6076\u554a\uff01\u4f60\u4eec\u5fc3\u4e2d\u65e9\u6709\u4e86\u8ba4\u540c\u7684\u611f\u89c9\uff0c\u5374\u4e00\u76f4\u4e0d\u80af\u5766\u8bda\u76f8\u89c1\uff0c\u2026\u2026\u201d\u7f57\u5170\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u5462\uff1f\u201d \u3000\u3000\u201c\u5ac2\u592b\u4eba\uff0c\u4f60\u548c\u5f20\u56db\u59d1\u60c5\u540c\u59ca\u59b9\uff0c\u4ea4\u5f80\u4e86\u4e8c\u5341\u51e0\u5e74\uff0c\u4e3a\u4ec0\u4e48\u4e5f\u4e0d\u80fd\u5766\u8bda\u76f8\u544a\u5462\uff1f\u548c\u5c1a\u76f8\u4fe1\uff0c\u4f60\u4eec\u4e4b\u95f4\uff0c\u4e5f\u65e9\u6709\u4e86\u8ba4\u77e5\u7684\u611f\u89c9\uff0c\u53ef\u662f\u611f\u89c9\u5f52\u611f\u89c9\uff0c\u4f46\u4e0d\u660e\u767d\u7684\u8bf4\u51fa\u6765\uff0c\u8fd8\u7b97\u79d8\u5bc6\uff0c\u6240\u4ee5\uff0c\u5929\u9a6c\u5802\u3001\u5929\u738b\u95e8\u7a77\u641c\u6c5f\u6e56\u4e8c\u5341\u5e74\uff0c\u4e5f\u627e\u4e0d\u5230\u4e09\u5723\u7684\u4e0b\u843d\uff0c\u4e5f\u627e\u4e0d\u51fa\u6211\u4eec\u8fd9\u51e0\u4e2a\u4eba\uff0c\u8fd9\u5c31\u662f\u4e09\u4f4d\u8001\u4eba\u5bb6\u9ad8\u660e\u5730\u65b9\u4e86\uff0c\u7ed9\u6211\u4eec\u4e25\u683c\u7684\u9650\u5236\uff0c\u5f7c\u6b64\u4e0d\u8c08\uff0c\u5c31\u4e0d\u4f1a\u6709\u98ce\u58f0\u6cc4\u6f0f\u51fa\u53bb\uff0c\u79d8\u5bc6\u5c31\u4fdd\u4f4f\u4e86\u2026\u2026\u201d \u3000\u3000\u76ee\u5149\u51dd\u6ce8\u5728\u6c5f\u67ab\u7751\u4e0a\uff0c\u63a5\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u9ad8\u554a\uff01\u90a3\u4e00\u6b21\u53d7\u4f24\u662f\u88c5\u7684\uff0c\u8fd8\u771f\u628a\u548c\u5c1a\u7ed9\u8499\u4f4f\u4e86\u3002\u201d \u3000\u3000\u6c5f\u67ab\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u88c5\u4f5c\u7684\u592a\u8ba4\u771f\uff0c\u592a\u9ad8\u660e\u4e86\uff0c\u6211\u60f3\u4e0d\u51fa\u7528\u4ec0\u4e48\u65b9\u6cd5\u624d\u80fd\u6d4b\u8bd5\u51fa\u4f60\u7684\u8eab\u4efd\uff0c\u53ea\u597d\u88c5\u4f5c\u771f\u6c14\u8017\u635f\u8fc7\u5ea6\uff0c\u4e0d\u652f\u53d7\u627f\uff0c\u5f15\u4f60\u52a9\u6211\u7597\u4f24\uff0c\u9732\u51fa\u5e95\u5b50\uff0c\u4e8b\u5b9e\u4e0a\uff0c\u90a3\u756a\u6df1\u8c08\uff0c\u786e\u4f7f\u6211\u83b7\u76ca\u532a\u6d45\u3002\u201d \u3000\u3000\u201c\u4e5f\u662f\u5728\u90a3\u6b21\u7597\u4f24\u7684\u8fc7\u7a0b\u4e2d\uff0c\u548c\u5c1a\u624d\u80af\u5b9a\u4e86\u4f60\u7684\u8eab\u4efd\uff0c\u6240\u4ee5\uff0c\u548c\u5c1a\u5c31\u5256\u5fc3\u8f93\u8bda\uff0c\u664b\u89c1\u4e3b\u5e05\u3002\u201d \u3000\u3000\u201c\u5927\u5e08\uff0c\u4e0d\u6562\u5f53\uff0c\u8fd8\u662f\u53eb\u6211\u6c5f\u67ab\u6216\u662f\u6c5f\u8001\u5f1f\u542c\u5f97\u8212\u9002\uff0c\u6765\u7684\u81ea\u7136\u3002\u201d \u3000\u3000\u201c\u600e\u4e48\u79f0\u547c\uff0c\u867d\u975e\u5f88\u91cd\u8981\uff0c\u4f46\u548c\u5c1a\u5fc3\u4e2d\u53ef\u662f\u628a\u4f60\u4f5c\u4e3b\u5e05\u656c\u91cd\uff0c\u6211\u4eec\u7b49\u4e86\u8fd1\u4e8c\u5341\u5e74\uff0c\u5c31\u662f\u7b49\u4f60\u51fa\u73b0\u6c5f\u6e56\uff0c\u4e3a\u4f60\u6548\u547d\u3002\u2026\u2026\u201d\u5927\u548c\u5c1a\u795e\u8272\u5e84\u4e25\u5730\u8bf4\uff0c\u201c\u6240\u4ee5\uff0c\u4e5d\u6210\u4e5d\u731c\u5230\u4f60\u7684\u8eab\u4efd\uff0c\u548c\u5c1a\u8fd8\u4e0d\u80af\u6cc4\u6f0f\u51fa\u6765\u5386\u8eab\u4efd\uff0c\u5341\u6210\u5168\u5f97\u8bc1\u5b9e\uff0c\u548c\u5c1a\u53ea\u597d\u8bf4\u660e\uff0c\u56e0\u4e3a\uff0c\u7531\u90a3\u4e00\u523b\u8d77\uff0c\u6211\u548c\u5c1a\u5df2\u628a\u751f\u6b7b\u4ea4\u4ed8\u4e8e\u4f60\uff0c\u4f46\u6709\u4ece\u547d\uff0c\u4e07\u6b7b\u4e0d\u8f9e\u3002\u201d \u3000\u3000\u201c\u548c\u5c1a\u8bf4\u5f97\u5bf9\u2026\u2026\u201d\u6b27\u9633\u662d\u9053\uff1a\u201c\u6211\u4eec\u82e6\u7ec3\u5251\u9053\u3001\u6b66\u529f\uff0c\u5c31\u662f\u7b49\u4f60\u51fa\u73b0\uff0c\u52a9\u4f60\u6210\u529f\uff0c\u6211\u5df2\u5bf9\u91d1\u4e39\u4e66\u751f\u4f5c\u4e86\u627f\u8bfa\uff0c\u6b64\u751f\u7edd\u4e0d\u4e3a\u4e2a\u4eba\u540d\u5229\u6253\u7b97\uff0c\u4e3a\u541b\u6548\u52b3\uff0c\u81f3\u6b7b\u4e0d\u6e1d\u3002\u201d \u3000\u3000\u201c\u6211\u4e5f\u5bf9\u5929\u72d0\u592b\u4eba\u8bb8\u4e0b\u8fc7\u8fd9\u79cd\u8a93\u8a00\uff0c\u2026\u2026\u201d\u7f57\u5170\u9053\uff1a\u201c\u6c5f\u67ab\uff0c\u8bdd\u5df2\u8bf4\u660e\u4e86\uff0c\u4f60\u600e\u4e48\u4e0b\u4ee4\uff0c\u6211\u4eec\u5c31\u600e\u4e48\u53bb\u505a\uff0c\u4e0a\u5200\u5c71\u4e0b\u6cb9\u9505\uff0c\u5728\u6240\u4e0d\u8ba1\u3002\u201d \u3000\u3000\u201c\u4e09\u4f4d\u5e08\u5144\u3001\u5e08\u59d0\uff0c\u5c0f\u5f1f\u6709\u5e78\uff0c\u83b7\u5f97\u4e86\u4e09\u4f4d\u8001\u4eba\u5bb6\u5982\u6b64\u7684\u5ba0\u7231\u4e3a\u6211\u4f5c\u4e86\u8fd9\u4e9b\u5b89\u6392\uff0c\u6211\u4eec\u6709\u5fd7\u4e00\u540c\uff0c\u5168\u529b\u53bb\u5b8c\u6210\u4ed6\u4eec\u7684\u5fc3\u613f\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u7b11\u4e00\u7b11\u53c8\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u6c5f\u6e56\u591a\u53d8\uff0c\u5c0f\u5f1f\u7684\u8bc6\u89c1\u592a\u6709\u9650\u4e86\uff0c\u8fd8\u8981\u4e09\u4f4d\u591a\u591a\u6307\u70b9\uff0c\u6211\u4eec\u5e08\u5144\u5e08\u59d0\u5f1f\u534f\u529b\u540c\u5fc3\uff0c\u626b\u9664\u6b66\u6797\u4e2d\u4e00\u5929\u9634\u973e\u3002\u201d \u3000\u3000\u201c\u548c\u5c1a\u662f\u667a\u6167\u3001\u6b66\u52c7\u5168\u90e8\u51fa\u7b3c\uff0c\u7edd\u4e0d\u85cf\u79c1\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u6211\u656c\u4e24\u4f4d\u5e08\u5144\u548c\u5e08\u59d0\u4e00\u676f\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u7aef\u8d77\u9152\u676f\u4e00\u996e\u800c\u5c3d\u3002 \u3000\u3000\u5927\u548c\u5c1a\u3001\u6b27\u9633\u662d\u3001\u7f57\u5170\u4e5f\u966a\u4e86\u4e00\u4e2a\u6ee1\u676f\u3002 \u3000\u3000\u56db\u4e2a\u53c8\u7814\u7a76\u4e86\u4e00\u756a\u5bf9\u654c\u8ba1\u5212\uff0c\u624d\u5404\u81ea\u5206\u5934\u5750\u606f\u3002 \u3000\u3000\u5927\u548c\u5c1a\u5341\u5206\u6c89\u5f97\u4f4f\u6c14\uff0c\u7b2c\u4e8c\u5929\u4e2d\u5348\u8fc7\u540e\uff0c\u624d\u8bf7\u6765\u4e86\u9093\u98de\u3001\u5201\u9e4f\u3002 \u3000\u3000\u6c5f\u67ab\u5df2\u653e\u5f03\u4e86\u97e9\u9738\u7684\u8eab\u4efd\uff0c\u4ee5\u672c\u6765\u9762\u76ee\u548c\u4e24\u4eba\u89c1\u9762\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u4e24\u4eba\u5fc3\u4e2d\u4e5f\u65e9\u77e5\u9053\u4e86\u97e9\u9738\u53ea\u662f\u4e2a\u865a\u6258\u7684\u8eab\u4efd\uff0c\u5fc3\u4e2d\u5e76\u65e0\u592a\u5927\u7684\u60ca\u5947\u3002 \u3000\u3000\u6c5f\u67ab\u5f00\u95e8\u89c1\u5c71\u7684\u8bf4\uff1a\u201c\u8fd9\u51e0\u65e5\uff0c\u6211\u5e2e\u4e86\u4e24\u4f4d\u4e0d\u5c11\u7684\u5fd9\uff0c\u4eca\u5929\u8981\u4e24\u4f4d\u5e2e\u52a9\u6211\u4e86\u3002\u201d \u3000\u3000\u201c\u884c\uff01\u2026\u2026\u201d\u5201\u9e4f\u56de\u7b54\u7684\u5341\u5206\u723d\u5feb\uff0c\u9053\uff1a\u201c\u5e72\u4ec0\u4e48\uff1f\u4f60\u53ea\u8981\u5429\u5490\u4e00\u58f0\u5c31\u884c\u4e86\uff01\u201d \u3000\u3000\u9093\u98de\u5374\u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u80fd\u4e0d\u80fd\u5148\u544a\u8bc9\u6211\u4eec\uff1f\u4ec0\u4e48\u4e8b\u60c5\uff1f\u9093\u67d0\u4e5f\u597d\u91cf\u529b\u800c\u4e3a\u3002\u201d \u3000\u3000\u201c\u4e3a\u4e86\u6d1b\u9633\u5c45\uff0c\u54b1\u4eec\u4e0d\u5f97\u4e0d\u594b\u8d77\u8fce\u6218\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u5bf9\u65b9\u662f\u5185\u5bab\u4e00\u7cfb\u4e2d\u4eba\uff0c\u603b\u7ba1\u4e0d\u7528\u591a\u8651\uff01\u201d \u3000\u3000\u201c\u6211\u770b\u2026\u2026\u201d\u9093\u98de\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u8981\u5411\u603b\u5802\u8bf7\u547d\uff0c\u7b49\u5019\u4ee4\u8c15\uff0c\u6838\u51c6\u4e86\uff0c\u6211\u5c31\u5c3d\u8d77\u6d1b\u9633\u5c45\u4e2d\u7684\u4eba\u624b\uff0c\u548c\u4ed6\u4eec\u653e\u624b\u4e00\u6218\uff01\u201d \u3000\u3000\u201c\u6d1b\u9633\u5c45\u4e2d\uff0c\u4f60\u6709\u4ec0\u4e48\u4eba\u624b\u597d\u8d77\uff1f\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u51b7\u51b7\u8bf4\u9053\u3002 \u3000\u3000\u201c\u9152\u697c\u3001\u6b4c\u575b\u4e2d\u7684\u4f8d\u5e94\u8dd1\u5802\uff0c\u4f60\u7ba1\u4e0d\u7740\uff0c\u8d4c\u573a\u4e2d\u90a3\u51e0\u4e2a\u4fdd\u9556\uff0c\u90fd\u662f\u7b2c\u516b\u6d41\u7684\u8eab\u624b\uff0c\u4e09\u811a\u63cf\u7684\u628a\u5f0f\uff0c\u4e0a\u4e0d\u4e86\u53f0\u76d8\u3002\u201d \u3000\u3000\u201c\u80fd\u591f\u62c9\u4e2a\u67b6\u5f0f\uff0c\u5728\u4e00\u8fb9\u5450\u558a\u52a9\u5a01\u7684\uff0c\u662f\u5201\u9e4f\u624b\u4e0b\u90a3\u51e0\u4e2a\u62a4\u6cd5\uff0c\u8001\u5b9e\u8bf4\uff0c\u8fd9\u6d1b\u9633\u5c45\u4e2d\u4f60\u662f\u552f\u4e00\u7684\u4e00\u53f7\uff0c\u6c5f\u5c11\u4fa0\u770b\u5f97\u8d77\u4f60\uff0c\u624d\u9080\u4f60\u540c\u884c\uff0c\u656c\u9152\u4e0d\u5403\uff0c\u5403\u7f5a\u9152\uff0c\u90a3\u5c31\u662f\u81ea\u627e\u96be\u770b\u4e86\u3002\u201d \u3000\u3000\u5927\u548c\u5c1a\u5e73\u5e38\u662f\u548c\u548c\u6c14\u6c14\uff0c\u4e00\u8138\u7b11\u5bb9\uff0c\u8fd9\u4e00\u62c9\u4e0b\u8138\u8fde\u9a82\u5e26\u6316\u82e6\u6c14\u5f97\u9093\u98de\u4e00\u53cc\u9b54\u773c\u4e2d\uff0c\u78a7\u5149\u4e71\u95ea\u3002 \u3000\u3000\u201c\u9093\u603b\u7ba1\u2026\u2026\u201d\u6c5f\u67ab\u4ecd\u7136\u4fdd\u6301\u7740\u5e73\u548c\u7684\u795e\u60c5\uff0c\u9053\uff1a\u201c\u5929\u9a6c\u5802\u65e9\u5df1\u4e0d\u628a\u4f60\u653e\u5728\u773c\u91cc\uff0c\u56db\u4f4d\u526f\u603b\u5802\u4e3b\uff0c\u5df2\u7ecf\u6709\u4e09\u4e2a\u6765\u8fc7\u6d1b\u9633\u5c45\uff0c\u9664\u4e86\u4e00\u638c\u62db\u9b42\u4e07\u65b9\uff0c\u5bf9\u4f60\u7a0d\u5047\u8bcd\u8272\u4e4b\u5916\uff0c\u7533\u96ea\u541b\u3001\u5927\u865a\u5b50\uff0c\u54ea\u4e00\u4e2a\u628a\u4f60\u5f53\u603b\u7ba1\u656c\uff0c\u4e00\u76f4\u5bf9\u4f60\u4fdd\u6301\u656c\u91cd\u7684\u662f\u6211\u6c5f\u67ab\uff0c\u53ea\u8981\u4f60\u80fd\u5408\u4f5c\uff0c\u6211\u4eec\u4e00\u5b9a\u662f\u4ec1\u5c3d\u4e49\u81f3\uff0c\u89c6\u4f60\u5982\u4f19\u4f34\u5144\u5f1f\u3002\u201d \u3000\u3000\u201c\u6211\u8bf4\u8001\u9093\u554a\uff01\u2026\u2026\u201d\u5201\u9e4f\u5f00\u4e86\u53e3\uff0c\u9053\uff1a\u201c\u9e2d\u5b50\u4e0a\u4e86\u67b6\uff0c\u8fd8\u6709\u4ec0\u4e48\u60f3\u4e0d\u5f00\u7684\uff0c\u4f60\u5df2\u7ecf\u516d\u5341\u591a\u4e86\uff0c\u6b7b\u4e86\u4e5f\u4e0d\u7b97\u5927\u5bff\uff0c\u6d3b\u7684\u7a9d\u7a9d\u56ca\u56ca\uff0c\u4f55\u5982\u6b7b\u7684\u8f70\u8f70\u70c8\u70c8\u3002 \u3000\u3000\u8ddf\u7740\u6c5f\u5c11\u4fa0\u8d70\uff0c\u4e5f\u8bb8\u662f\u9053\u9014\u8346\u68d8\u3001\u51f6\u9669\u91cd\u91cd\uff0c\u4e0d\u8fc7\uff0c\u6211\u4eec\u4f1a\u611f\u89c9\u5230\u662f\u4e2a\u4eba\u7269\uff0c\u6d3b\u7684\u6709\u610f\u4e49\uff0c\u6b7b\u7684\u6709\u5c0a\u4e25\uff0c\u600e\u4e48\u6837\uff1f\u60f3\u901a\u4e86\u6ca1\u6709\uff1f\u201d \u3000\u3000\u201c\u5201\u5144\uff01\u4f60\u2026\u2026\u4f60\u2026\u2026\u201d\u9093\u98de\u9053\uff1a\u201c\u4f60\u4e5f\u662f\u4ed6\u4eec\u4e00\u4f19\u7684\uff1f\u201d \u3000\u3000\u201c\u4f60\u770b\u6211\u50cf\u5417\uff1f\u2026\u2026\u201d\u5201\u9e4f\u7b11\u9053\uff1a\u201c\u6211\u8fd9\u4eba\u5927\u534a\u751f\u95ef\u8361\u6c5f\u6e56\uff0c\u53cc\u624b\u8840\u8165\uff0c\u4f46\u665a\u5e74\u5411\u4f5b\uff0c\u5df2\u62dc\u5728\u4e03\u5b9d\u5927\u5e08\u4e4b\u4e0b\u4e3a\u5f92\uff0c\u6b7b\u540e\u5c31\u7b97\u5230\u4e0d\u4e86\u897f\u65b9\u6781\u4e50\u4e16\u754c\uff0c\u81f3\u5c11\u53ef\u4ee5\u5728\u95e8\u53e3\u770b\u770b\uff0c\u81f3\u4e8e\u5929\u9a6c\u91cc\uff0c\u6211\u6bd4\u4f60\u6e05\u695a\u591a\u4e86\uff0c\u6211\u4eec\u53ea\u662f\u5343\u767e\u9897\u74e6\u783e\u4e2d\u7684\u4e00\u7c92\uff0c\u6ca1\u6709\u4eba\u771f\u7684\u628a\u6211\u4eec\u653e\u5728\u5fc3\u4e0a\u2026\u2026\u201d \u3000\u3000\u201c\u597d\u5427\uff01\u2026\u2026\u201d\u9093\u98de\u6253\u65ad\u4e86\u5201\u9e4f\u7684\u8bdd\uff0c\u63a5\u9053\uff1a\u201c\u4e0d\u662f\u6c5f\u67ab\uff0c\u6211\u8fd9\u6761\u8001\u547d\uff0c\u4e5f\u8bb8\u65e9\u88ab\u5185\u5bab\u4e00\u7cfb\u4e2d\u4eba\u7ed9\u6740\u4e86\uff0c\u8d27\u5356\u8bc6\u5bb6\uff0c\u6c5f\u5c11\u4fa0\u65e2\u7136\u770b\u91cd\u8001\u673d\uff0c\u9093\u67d0\u5356\u5356\u4f59\u52c7\uff0c\u518d\u63e1\u5c60\u5200\uff0c\u4f60\u4eec\u7b49\u6211\u4e00\u4e0b\uff0c\u6211\u53bb\u628a\u5bb6\u4f19\u53d6\u6765\uff0c\u8fd9\u4e9b\u5e74\u5b89\u9038\u60ef\u4e86\uff0c\u8fde\u5175\u5203\u4e5f\u672a\u5e26\u5728\u8eab\u4e0a\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u6211\u4eec\u7b49\u4f60\u987f\u996d\u65f6\u95f4\uff0c\u6765\u4e5f\u5728\u4f60\uff0c\u4e0d\u6765\u4e5f\u5728\u4f60\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u9053\uff1a\u201c\u6211\u4eec\u4e0d\u4f1a\u518d\u7ea0\u7f20\u4f60\u4e86\uff01\u201d \u3000\u3000\u9093\u98de\u6ca1\u6709\u56de\u7b54\uff0c\u5306\u5306\u800c\u53bb\uff0c\u4f46\u4e5f\u56de\u6765\u7684\u5f88\u5feb\u3002 \u3000\u3000\u539f\u672c\u4e00\u8eab\u8f7b\u888d\uff0c\u5df2\u7ecf\u6362\u6210\u4e86\u9ed1\u8272\u77ed\u8884\uff0c\u8fd8\u80cc\u4e86\u4e00\u628a\u56db\u5c3a\u957f\u5200\uff0c\u886c\u7740\u4e00\u53cc\u78a7\u773c\u548c\u98d8\u80f8\u957f\u9aef\uff0c\u5012\u4e5f\u6709\u51e0\u5206\u51f6\u9738\u7684\u9b54\u6c14\u3002 \u3000\u3000\u62c2\u9aef\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u795e\u3001\u9b3c\u53cc\u5200\u548c\u79cb\u82b1\u4e8c\u5a62\u521a\u521a\u79bb\u5f00\u8fce\u5bbe\u9601\uff0c\u5927\u6982\u4e5f\u662f\u8d76\u53bb\u8d74\u7ea6\uff0c\u6211\u5df2\u9063\u8d70\u4e86\u4e24\u4e2a\u4eb2\u4fe1\u4fdd\u9556\uff0c\u5927\u548c\u5c1a\u8bf4\u7684\u5bf9\uff0c\u4ed6\u4eec\u7684\u6b66\u529f\u5dee\u6211\u5341\u500d\uff0c\u6211\u7adf\u8981\u8bf7\u4ed6\u4eec\u4fdd\u62a4\u6211\u7684\u5b89\u5168\uff0c\u60f3\u4e00\u60f3\u5341\u5206\u53ef\u7b11\uff0c\u5927\u6982\u662f\u4e3a\u4e86\u8981\u6446\u8fd9\u4e2a\u603b\u7ba1\u7684\u67b6\u5f0f\uff0c\u73b0\u5728\uff0c\u9b54\u773c\u9093\u98de\uff0c\u53c8\u6062\u590d\u6c5f\u6e56\u4eba\u7684\u672c\u8272\uff0c\u53ef\u4ee5\u8d70\u5566\uff01\u5230\u4ec0\u4e48\u5730\u65b9\u8d74\u7ea6\uff1f\u201d \u3000\u3000\u201c\u4e07\u6885\u576a\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u8ddd\u957f\u5b89\u4e8c\u5341\u591a\u91cc\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\u90a3\u4e2a\u5730\u65b9\u2026\u2026\u201d\u9093\u98de\u62c2\u9aef\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u90a3\u91cc\u6709\u4e24\u5ea7\u5916\u9762\u4e0d\u5f88\u8d77\u773c\uff0c\u91cc\u9762\u5374\u5e03\u7f6e\u5341\u5206\u8c6a\u534e\u7684\u9152\u9986\uff0c\u4e00\u53eb\u6885\u56ed\uff0c\u4e00\u53eb\u7af9\u8f69\uff0c\u7af9\u8f69\u7684\u5927\u638c\u67dc\u548c\u6211\u89c1\u8fc7\u4e86\u51e0\u6b21\u9762\u2026\u2026\u201d \u3000\u3000\u201c\u4ed6\u4eec\u4e5f\u662f\u5929\u9a6c\u5802\u4e2d\u4eba\u5417\uff1f\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u548c\u6d1b\u9633\u5c45\u662f\u5426\u6709\u8fc7\u8fde\u7cfb\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\uff01\u2026\u2026\u201d\u9093\u98de\u8bf4\uff0c\u201c\u4ed6\u4eec\u4e5f\u4e0d\u50cf\u662f\u5929\u9a6c\u5802\u4e2a\u4eba\uff0c\u4f46\u7af9\u8f69\u7684\u5927\u638c\u67dc\u5374\u662f\u4e2a\u5386\u7ec3\u9887\u4e30\u7684\u6c5f\u6e56\u4eba\u7269\uff01\u201d \u3000\u3000\u201c\u59d3\u540d\u5462\uff1f\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u95ee\u9053\u3002 \u3000\u3000\u9093\u98de\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u597d\u50cf\u53eb\u6731\u8001\u4e09\uff0c\u4f46\u8fd9\u540d\u5b57\u4e0d\u50cf\u662f\u4e2a\u771f\u5b9e\u7684\u59d3\u540d\uff0c\u4e0d\u8fc7\uff0c\u90a3\u4e2a\u4e09\u5b57\uff0c\u53ef\u80fd\u662f\u8868\u660e\u4e00\u4e2a\u6392\u884c\uff0c\u4ed6\u867d\u7136\u6709\u610f\u9690\u85cf\uff0c\u4f46\u60f3\u7792\u8fc7\u6211\u8fd9\u53cc\u773c\u775b\uff0c\u8fd8\u4e0d\u592a\u5bb9\u6613\uff0c\u5982\u679c\u6211\u6ca1\u6709\u770b\u9519\uff0c\u5e94\u8be5\u662f\u4e00\u4e2a\u5185\u5916\u517c\u4fee\u7684\u9ad8\u624b\uff0c\u5e74\u9f84\u5728\u7ea6\u5728\u4e94\u5341\u5de6\u53f3\u2026\u2026\u201d \u3000\u3000\u201c\u8fd9\u5c31\u6709\u4e9b\u5947\u602a\u4e86\u2026\u2026\u201d\u5201\u9e4f\u63a5\u9053\uff1a\u201c\u8fd9\u6837\u4e00\u4e2a\u4eba\u7269\uff0c\u5e94\u8be5\u662f\u592d\u9a6c\u5802\u4e2d\u4eba\u4e86\uff0c\u4e3a\u4ec0\u4e48\u5201\u67d0\u4ece\u672a\u542c\u8fc7\u5462\uff1f\u201d \u3000\u3000\u201c\u90a3\u5c31\u4e0d\u662f\u5929\u9a6c\u5802\u4e2d\u4eba\u4e86\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u8fd9\u5df2\u7ecf\u4e0d\u91cd\u8981\u4e86\uff0c\u4eca\u65e5\u4e0e\u4f1a\u7684\u9ad8\u624b\uff0c\u4e0d\u77e5\u51e1\u51e0\uff0c\u6731\u8001\u4e09\u5927\u6982\u4e5f\u8981\u81ea\u73b0\u539f\u5f62\u2026\u2026\u201d\u76ee\u5149\u4e00\u63a0\u6c5f\u67ab\uff0c\u63a5\u9053\uff1a\u201c\u73b0\u5728\uff0c\u6211\u4eec\u53ef\u4ee5\u51fa\u53d1\u4e86\uff01\u201d \u3000\u3000\u6b27\u9633\u662d\u3001\u7f57\u5170\u3001\u80e1\u840d\u3001\u6bb5\u4e5d\uff0c\u65e9\u5df2\u7ed3\u675f\u505c\u5f53\uff0c\u5728\u5385\u5916\u7b49\u5019\u3002 \u3000\u3000\u81ea\u6628\u591c\u4e00\u756a\u6df1\u8c08\u4e4b\u540e\uff0c\u6b27\u9633\u662d\u548c\u7f57\u5170\u4e5f\u53d7\u4e86\u5927\u548c\u5c1a\u7684\u611f\u67d3\uff0c\u867d\u662f\u957f\u4e00\u8f88\u7684\u8eab\u4efd\uff0c\u4e5f\u628a\u6c5f\u67ab\u89c6\u4f5c\u4e86\u4e3b\u5e05\u656c\u91cd\u3002 \u3000\u3000\u4e07\u6885\u576a\u540d\u7b26\u5176\u5b9e\uff0c\u4e07\u682a\u6885\u6811\u5206\u5e03\u5728\u5e7f\u9614\u7684\u96ea\u5730\u4e0a\u3002 \u3000\u3000\u8fd9\u91cc\u672c\u662f\u4e00\u7247\u4e18\u9675\u8d77\u4f0f\u7684\u5730\u65b9\uff0c\u53ea\u4e0d\u8fc7\u571f\u5cad\u4e0d\u9ad8\uff0c\u770b\u6885\u6811\u9ad8\u77ee\u7684\u5dee\u8ddd\uff0c\u8fd9\u91cc\u5e94\u8be5\u6709\u6d45\u6c9f\u6eaa\u6d41\u3002 \u3000\u3000\u4f46\u79ef\u96ea\u8fc7\u6df1\uff0c\u4e00\u5207\u90fd\u88ab\u63a9\u53bb\uff0c\u770b\u4e0a\u53bb\u4e00\u7247\u94f6\u767d\u3002 \u3000\u3000\u4f46\u7af9\u8f69\u3001\u6885\u56ed\u5374\u4f3c\u7b51\u5efa\u5728\u4e00\u7247\u5e73\u539f\u4e0a\uff0c\u56e0\u4e3a\uff0c\u90a3\u91cc\u7684\u6885\u6811\uff0c\u9ad8\u77ee\u76f8\u82e5\uff0c\u5dee\u8ddd\u4e0d\u5927\u3002 \u3000\u3000\u6c5f\u67ab\u6253\u91cf\u8fc7\u56db\u5468\u5f62\u52bf\uff0c\u7b11\u9053\uff1a\u201c\u8e0f\u96ea\u8d4f\u6885\uff0c\u662f\u4f55\u7b49\u98ce\u96c5\u4e8b\uff0c\u60f3\u4e0d\u5230\uff0c\u8fd9\u91cc\u7adf\u7136\u4f1a\u53d8\u6210\u6e85\u8840\u640f\u547d\u7684\u6240\u5728\uff0c\u771f\u662f\u5927\u715e\u98ce\u666f\uff0c\u6709\u8fb1\u65af\u6587\u4e86\uff01\u201d \u3000\u3000\u201c\u4f60\u53ef\u662f\u771f\u6f47\u6d12\u554a\uff01\u2026\u2026\u201d\u7f57\u5170\u9053\uff1a\u201c\u5927\u654c\u5f53\u524d\uff0c\u80dc\u8d1f\u96be\u6599\uff0c\u4f60\u5374\u662f\u4e00\u70b9\u4e5f\u4e0d\u803d\u5fc3\uff01\u201d \u3000\u3000\u201c\u5927\u4e08\u592b\u751f\u800c\u4f55\u6b22\uff0c\u6b7b\u800c\u4f55\u60e7\u2026\u2026\u201d\u6c5f\u67ab\u7b11\u9053\uff1a\u201c\u8e0f\u5343\u4ede\u5982\u5c65\u5e73\u5730\uff0c\u5c0f\u5f1f\u7684\u5fc3\u60c5\uff0c\u786e\u662f\u5e73\u9759\u7684\u5f88\u3002\u201d \u3000\u3000\u201c\u597d\u4e00\u4e2a\u8e0f\u4e8e\u4ede\u5982\u5c65\u5e73\u5730\uff0c\u5355\u662f\u8fd9\u4efd\u6c14\u52bf\uff0c\u5c31\u53eb\u4eba\u5fc3\u6298\u2026\u2026\u201d\u6b27\u9633\u662d\u76ee\u5149\u56db\u987e\uff0c\u63a5\u9053\uff1a \u3000\u3000\u201c\u4ec0\u4e48\u65f6\u523b\u5566\uff01\u600e\u4e48\u4e0d\u89c1\u4e00\u4e2a\u4eba\u5f71\uff0c\u4e00\u70b9\u52a8\u9759\u3002\u201d \u3000\u3000\u201c\u4ed6\u4eec\u5e94\u8be5\u65e9\u5c31\u5230\u4e86\uff0c\u5f88\u53ef\u80fd\u85cf\u8eab\u5728\u7af9\u8f69\u3001\u6885\u56ed\u4e2d\u517b\u7cbe\u84c4\u9510\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u63a5\u9053\uff1a\u201c\u8ba9\u54b1\u4eec\u7b49\u5f85\u5fc3\u706b\u4e0a\u5347\u65f6\uff0c\u4ed6\u4eec\u518d\u73b0\u8eab\u8fce\u654c\u3002\u201d \u3000\u3000\u201c\u5201\u5144\uff0c\u9093\u5144\uff0c\u8fd9\u6885\u56ed\u3001\u7af9\u8f69\u662f\u4f55\u7269\u7b51\u5efa\uff1f\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u6d12\u4e0a\u6850\u6cb9\uff0c\u653e\u4e0a\u4e00\u628a\u706b\uff0c\u80fd\u4e0d\u80fd\u70e7\u5b83\u4e2a\u7247\u74e6\u4e0d\u5b58\uff1f\u201d \u3000\u3000\u201c\u4e91\u867d\u672a\u6563\uff0c\u5927\u96ea\u5df2\u505c\uff0c\u2026\u2026\u201d\u9093\u98de\u9053\uff1a\u201c\u8fd9\u628a\u706b\u70e7\u5f97\u8d77\u6765\uff01\u5201\u5144\uff0c\u8d70\u81ea\u4eec\u653e\u706b\u53bb\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u7684\u8bdd\uff0c\u662f\u8fd0\u529f\u4f20\u9001\u51fa\u53bb\uff0c\u9093\u98de\u4e5f\u63d0\u9ad8\u4e86\u58f0\u97f3\u56de\u7b54\uff0c\u522b\u8bf4\u7af9\u8f69\u3001\u6885\u56ed\u4e2d\u7684\u4eba\u4e86\uff0c\u5c31\u662f\u518d\u8fdc\u4e00\u4e9b\uff0c\u4e5f\u53ef\u4ee5\u542c\u5f97\u6e05\u695a\u3002 \u3000\u3000\u4f46\u7af9\u8f69\u3001\u6885\u56ed\u4e2d\u5e76\u65e0\u52a8\u9759\uff0c\u4e5f\u65e0\u4eba\u56de\u5e94\uff0c\u4f3c\u4e4e\u662f\u4eba\u90fd\u65e9\u5df2\u79bb\u53bb\uff0c\u8fd9\u7247\u832b\u832b\u7684\u96ea\u5730\u6885\u6797\u4e4b\u4e2d\uff0c\u53ea\u6709\u6c5f\u67ab\u7b49\u51e0\u4e2a\u4eba\u3002 \u3000\u3000\u5201\u9e4f\u5927\u58f0\u559d\u9053\uff1a\u201c\u96be\u9053\u8ba4\u4e3a\u6211\u4eec\u4e0d\u6562\u653e\u706b\u5417\uff1f\u70e7\u7ed9\u4ed6\u4eec\u77a7\u77a7\u3002\u201d \u3000\u3000\u53ea\u542c\u4e00\u4e2a\u7ec6\u5fae\u4f46\u5374\u6e05\u6670\u7684\u58f0\u97f3\uff0c\u4f20\u5165\u4e86\u6c5f\u67ab\u8033\u9645\uff0c\u9053\uff1a\u201c\u5c0f\u60c5\u90ce\uff0c\u70e7\u4e0d\u5f97\uff0c\u6885\u56ed\u3001\u7af9\u8f69\u4e2d\uff0c\u6ee1\u5e03\u4e86\u706b\u836f\u3001\u5947\u6bd2\uff0c\u4e00\u628a\u706b\uff0c\u836f\u7206\u3001\u6bd2\u6563\uff0c\u65b9\u5706\u767e\u4e08\u5185\uff0c\u5168\u65e0\u751f\u673a\uff0c\u4ed6\u4eec\u6765\u4eba\u5f88\u591a\uff0c\u8eb2\u5728\u4e24\u91cc\u4e4b\u5916\uff0c\u6b63\u5728\u770b\u653e\u706b\u70e7\u56ed\u7684\u597d\u620f\u5462\uff01\u201d \u3000\u3000\u542c\u8bf4\u662f\u5f20\u5927\u59d0\u7684\u58f0\u97f3\uff0c\u6c5f\u67ab\u5fc3\u4e2d\u53c8\u751c\u3001\u53c8\u60ca\u9a87\uff0c\u5f20\u56db\u59d1\u5f53\u9762\u65f6\u6709\u6240\u987e\u5fcc\uff0c\u5c24\u5176\u548c\u4e24\u4e2a\u5c0f\u72d0\u5973\u76f8\u5904\u4e4b\u540e\uff0c\u8a00\u8bed\u884c\u52a8\u3001\u65e0\u4e0d\u68c0\u70b9\uff0c\u4e0d\u6562\u7eb5\u60c5\u793a\uff0c\u8fd9\u4f1a\u513f\u65bd\u5c55\u4f20\u97f3\u5165\u5bc6\uff0c\u53cd\u6b63\u53ea\u6709\u6c5f\u67ab\u4e00\u4e2a\u4eba\u542c\u5230\uff0c\u5fc3\u65e0\u987e\u5fcc\uff0c\u4e00\u58f0\u5c0f\u60c5\u90ce\u53eb\u7684\u662f\u65e0\u9650\u5a07\u67d4\u3002 \u3000\u3000\u6c5f\u67ab\u6ca1\u6709\u56de\u5934\u671b\u53bb\uff0c\u7740\u5411\u58f0\u97f3\u4f20\u6765\u7684\u5730\u65b9\uff0c\u5374\u63d0\u9ad8\u4e86\u58f0\u97f3\uff0c\u53eb\u9053\uff1a\u201c\u9093\u5144\u3001\u5201\u5144\uff0c\u5feb\u8bf7\u56de\u6765\u3002\u201d \u3000\u3000\u6562\u60c5\uff0c\u4e24\u4e2a\u4eba\u662f\u771f\u7684\u51c6\u5907\u653e\u706b\uff0c\u5df2\u5411\u7af9\u8f69\u3001\u6885\u56ed\u5954\u53bb\u3002 \u3000\u3000\u542c\u5f97\u6c5f\u67ab\u547c\u53eb\uff0c\u4e24\u4e2a\u4eba\u53ea\u597d\u8dd1\u4e86\u56de\u6765\u3002 \u3000\u3000\u5201\u9e4f\u6c14\u547c\u547c\u7684\u8bf4\u9053\uff1a\u201c\u5e08\u7236\u8bf4\u7684\u4e0d\u9519\uff0c\u4ed6\u4eec\u660e\u660e\u8eb2\u5728\u6885\u56ed\u3001\u7af9\u8f69\u4e2d\u53d6\u6696\u559d\u9152\uff0c\u62d6\u5ef6\u65f6\u95f4\uff0c\u6545\u610f\u8981\u6211\u4eec\u5728\u8fd9\u91cc\u8fce\u7740\u897f\u5317\u98ce\uff0c\u7ad9\u5728\u96ea\u5730\u91cc\u53d7\u51bb\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u653e\u4e00\u628a\u706b\u628a\u4ed6\u4eec\u7ed9\u70e7\u51fa\u6765\u5462\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4ed6\u4eec\u7ea6\u597d\u7533\u521d\uff0c\u76f8\u4fe1\u4e0d\u4f1a\u5931\u7ea6\uff0c\u653e\u706b\u70e7\u5c4b\u7684\u4e8b\uff0c\u6709\u5931\u78ca\u843d\u98ce\u5ea6\uff0c\u4e0d\u70e7\u4e5f\u7f62\uff01\u201d \u3000\u3000\u201c\u6c5f\u5c11\u4fa0\u2026\u2026\u201d\u5201\u9e4f\u9053\uff1a\u201c\u6c5f\u6e56\u4e2d\u4e8b\uff0c\u5978\u9669\u767e\u51fa\uff0c\u53ef\u662f\u539a\u9053\u4e0d\u5f97\uff01\u201d \u3000\u3000\u6c5f\u67ab\u5c71\u4e0d\u89e3\u91ca\uff0c\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u54b1\u4eec\u518d\u7b49\u5019\u4e00\u9635\uff0c\u4e5f\u8bb8\u8fd8\u4e0d\u5230\u7533\u65f6\u5462\uff1f\u201d \u3000\u3000\u5201\u9e4f\u4e0d\u6562\u518d\u8fa9\uff0c\u62c9\u957f\u4e00\u5f20\u8138\uff0c\u8fce\u98ce\u800c\u7acb\uff0c\u4f3c\u662f\u8981\u90a3\u5982\u526a\u5317\u98ce\uff0c\u5439\u6d88\u4ed6\u5fc3\u4e2d\u7684\u706b\u6c14\u3002 \u3000\u3000\u7f57\u5170\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u670d\u6c14\uff0c\u4e3a\u4ec0\u4e48\u8ba9\u4ed6\u4eec\u5728\u91cc\u6696\u548c\uff0c\u6211\u4eec\u5728\u8fd9\u96ea\u91cc\u53d7\u51bb\uff1f\u201d \u3000\u3000\u5979\u90a3\u5f20\u5a07\u5ae9\u7684\u8138\u513f\uff0c\u4efb\u51ed\u5bd2\u98ce\u5439\u88ad\uff0c\u6b27\u9633\u662d\u53e3\u867d\u4e0d\u8a00\uff0c\u4f46\u5374\u770b\u7684\u6709\u4e9b\u5fc3\u75bc\u3002 \u3000\u3000\u201c\u5201\u9e4f\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5982\u82e5\u90a3\u6885\u56ed\u3001\u7af9\u8f69\u4e2d\u85cf\u4e86\u6850\u6cb9\u3001\u706b\u836f\uff0c\u4f60\u4eec\u653e\u4e86\u706b\uff0c\u5f15\u71c3\u836f\u4fe1\u3001\u80fd\u4e0d\u80fd\u6d3b\u7740\u56de\u6765\u5462\uff1f\u201d \u3000\u3000\u5927\u548c\u5c1a\u8fd8\u672a\u60f3\u5230\u836f\u4e2d\u6df7\u6709\u5947\u6bd2\uff0c\u53ef\u6ce2\u53ca\u767e\u4e08\u4ee5\u5916\u3002 \u3000\u3000\u4f46\u5201\u9e4f\u5df2\u542c\u5f97\u4e86\u4e00\u8eab\u51b7\u6c57\uff0c\u9053\uff1a\u201c\u5e08\u7236\u6559\u8bad\u7684\u662f\uff01\u201d \u3000\u3000\u201c\u771f\u662f\u957f\u6c5f\u540e\u6d6a\u63a8\u524d\u6d6a\uff0c\u2026\u2026\u201d\u9093\u98de\u65e0\u9650\u611f\u6168\u4ed6\u8bf4\u9053\uff1a\u201c\u540e\u4eba\u597d\u9669\u80dc\u524d\u4eba\u2026\u2026\u201d \u3000\u3000\u7acb\u523b\u60f3\u5230\uff0c\u8fd9\u4e0d\u662f\u4e5f\u628a\u6c5f\u67ab\u7ed9\u9a82\u4e0a\u4e86\u5417\uff1f\u6025\u6025\u4f4f\u53e3\u4e0d\u8a00\u3002 \u3000\u3000\u6b27\u9633\u662d\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8bf4\u7684\u662f\u554a\uff01\u6c5f\u6e56\u4e0a\u8fd9\u4e9b\u5978\u9669\u8bbe\u8ba1\uff0c\u53ef\u771f\u53eb\u4eba\u9632\u4e0d\u80dc\u9632\uff0c\u5f53\u771f\u5c0f\u5fc3\u8c28\u614e\u624d\u884c\u3002\u201d \u3000\u3000\u201c\u597d\u50cf\u662f\u6709\u4eba\u6765\u4e86\u2026\u2026\u201d\u6c5f\u67ab\u671b\u7740\u6b63\u5317\u7684\u65b9\u5411\uff0c\u9053\uff1a\u201c\u6765\u4eba\u4f3c\u662f\u4e0d\u5c11\uff0c\u4e09\u5bab\u4e3b\u4e00\u9a6c\u5f53\u5148\u2026\u2026\u201d \u3000\u3000\u90fd\u770b\u5230\u4e86\uff0c\u5927\u7ea6\u7531\u4e09\u5341\u591a\u4eba\uff0c\u5206\u6210\u4e86\u56db\u4e2a\u7fa4\u4f53\u884c\u52a8\uff0c\u6bcf\u4e00\u6279\u4f3c\u4e00\u4e2a\u805a\u96c6\u7684\u6218\u6597\u7fa4\uff0c\u4fdd\u5f85\u4e86\u4e08\u4f59\u7684\u8ddd\u79bb\u3002 \u3000\u3000\u4ed6\u4eec\u8d70\u7684\u5f88\u4ece\u5bb9\uff0c\u4e0d\u5feb\u4e0d\u6162\u3002 \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u9053\uff1a\u201c\u975e\u4e0d\u5f97\u5df2\uff0c\u4e0d\u8981\u548c\u4ed6\u4eec\u5c55\u5f00\u7fa4\u6597\uff0c\u5982\u82e5\u4ed6\u4eec\u4e00\u62e5\u800c\u4e0a\uff0c\u5206\u7531\u56db\u9762\u516b\u65b9\u653b\u6765\uff0c\u54b1\u4eec\u7ed3\u5408\u6210\u4e00\u4e2a\u5706\u9635\u67dc\u654c\u3002\u201d \u3000\u3000\u5927\u548c\u5c1a\u672a\u96e8\u7ef8\u7f2a\uff0c\u628a\u5404\u4eba\u7684\u4f4d\u7f6e\uff0c\u5206\u914d\u7684\u5f88\u6e05\u695a\u3002 \u3000\u3000\u80e1\u840d\u7d27\u90bb\u6c5f\u67ab\uff0c\u6bb5\u4e5d\u9760\u7740\u6b27\u9633\u662d\uff0c\u5201\u9e4f\u7d27\u65c1\u5927\u548c\u5c1a\uff0c\u7f57\u5170\u548c\u9093\u98de\u4e00\u7ec4\uff0c\u5206\u5bf9\u5411\u4e1c\u5357\u897f\u5317\u56db\u4e2a\u65b9\u4f4d\u3002 \u3000\u3000\u5b89\u6392\u597d\u5404\u4eba\u65b9\u4f4d\uff0c\u5927\u548c\u5c1a\u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u56db\u9762\u62d2\u654c\uff0c\u76f8\u4e92\u652f\u63f4\uff0c\u770b\u654c\u52bf\u53d8\u5316\uff0c\u5706\u9635\u8f6c\u52a8\uff0c\u5c31\u53ef\u4ee5\u8c03\u6574\u65b9\u4f4d\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u8fd9\u662f\u5bf9\u4ed8\u654c\u4eba\u7684\u7fa4\u653b\u65b9\u6cd5\uff0c\u4e5f\u662f\u6700\u574f\u7684\u4e00\u79cd\u6218\u6cd5\uff0c\u975e\u5fc5\u8981\uff0c\u6700\u597d\u4e0d\u7528\uff0c\u6700\u597d\u662f\u548c\u4ed6\u4eec\u4e00\u5bf9\u4e00\u7684\u52a8\u624b\uff0c\u5982\u80fd\u5148\u80dc\u51e0\u9635\uff0c\u4ee5\u632b\u654c\u9510\u6c14\uff0c\u8fd9\u4e00\u6218\u5c31\u53ef\u4ee5\u638c\u63e1\u80dc\u673a\u4e86\u3002\u201d \u3000\u3000\u5927\u548c\u5c1a\u8bf4\u7684\u9887\u5177\u81ea\u4fe1\uff0c\u4f46\u9093\u98de\u5374\u770b\u5230\u4e86\u5929\u9a6c\u5802\u4e2a\u7684\u4e00\u6279\u4eba\uff0c\u7adf\u7136\u6709\u4e24\u4f4d\u526f\u603b\u5802\u4e3b\u9886\u961f\u4e34\u9635\u3002 \u3000\u3000\u90a3\u662f\u4e00\u638c\u62db\u9b42\u4e07\u65b9\u548c\u5929\u865a\u5b50\uff0c\u968f\u884c\u516d\u4eba\uff0c\u90fd\u662f\u4e09\u5341\u516d\u957f\u8001\u4e2d\u7684\u9876\u5c16\u4eba\u7269\uff0c\u8fd9\u516b\u4e2a\u4eba\u7ed9\u4e86\u9093\u98de\u548c\u5201\u9e4f\u5f88\u5927\u7684\u9707\u9a87\uff0c\u56e0\u4e3a\uff0c\u4ed6\u4eec\u6ca1\u6709\u4fe1\u5fc3\u80fd\u80dc\u8fc7\u5176\u4e2d\u4efb\u4f55\u4e00\u4e2a\u4eba\uff01 \u3000\u3000\u4e09\u5bab\u4e3b\u4e3a\u9996\u7684\u4e00\u6279\uff0c\u9664\u4e86\u795e\u3001\u9b3c\u53cc\u5200\u3001\u79cb\u82b1\u53cc\u5a62\u4e4b\u5916\uff0c\u8fd8\u6709\u4e00\u4e2a\u624b\u6267\u7af9\u6756\u7684\u8001\u7fc1\uff0c\u4e24\u4e2a\u9762\u4e0a\u523a\u7740\u82b1\u7eb9\u7684\u5f3a\u58ee\u5987\u4eba\u3002 \u3000\u3000\u5979\u4eec\u4f53\u5f62\u7cbe\u58ee\uff0c\u5c24\u8fc7\u4e00\u822c\u7537\u4eba\uff0c\u6700\u5999\u7684\u8eab\u4e0a\u8863\u7740\uff0c\u4f3c\u662f\u5168\u7528\u86c7\u76ae\u505a\u6210\uff0c\u957f\u88e4\u76ae\u9774\u4e0a\u7684\u86c7\u9cde\uff0c\u66f4\u662f\u7247\u7247\u8038\u7acb\uff0c\u770b\u4e0a\u5c31\u4e0d\u50cf\u4e2d\u539f\u4eba\u7269\uff0c\u6709\u5982\u5de8\u86c7\u5e7b\u5316\u6210\u7684\u86c7\u4eba\u3002 \u3000\u3000\u7b2c\u4e09\u6279\u662f\u4e00\u4e2a\u7ae5\u989c\u9e64\u53d1\u7684\u8001\u8005\uff0c\u624b\u4e2d\u6267\u7740\u4e00\u67c4\u53e4\u5f62\u957f\u5200\uff0c\u8eab\u540e\u56db\u4f69\u5200\u5927\u6c49\uff0c\u90fd\u7a7f\u7740\u9ed1\u8272\u52b2\u88c5\uff0c\u4ee5\u88c5\u626e\u800c\u8a00\uff0c\u8fd9\u4e94\u4e2a\u662f\u6700\u6b63\u5e38\u6587\u96c5\u7684\u4e00\u7ec4\u3002 \u3000\u3000\u4f46\u5201\u9e4f\u5374\u77a7\u7684\u5fc3\u5934\u4e0a\u6cdb\u8d77\u4e86\u4e00\u80a1\u5bd2\u610f\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u6c5f\u5c11\u4fa0\uff0c\u90a3\u767d\u53d1\u8001\u8005\u662f\u798f\u5bff\u9662\u4e2d\u516d\u5927\u697c\u4e3b\u4e4b\u4e00\u3002 \u3000\u3000\u4e00\u54c1\u5200\u548c\u56db\u4e2a\u8ffd\u968f\u4ed6\u4e09\u5341\u5e74\u7684\u5f1f\u5b50\uff0c\u542c\u8bf4\uff0c\u4ed6\u7684\u5200\u6cd5\u5df2\u5165\u5316\u5883\uff0c\u80fd\u9a6d\u5200\u53d6\u654c\u4eba\u9996\u7ea7\u4e8e\u767e\u6b65\u4e4b\u5916\uff0c\u56db\u5927\u5f1f\u5b50\uff0c\u4e5f\u662f\u5200\u62db\u8be1\u5947\u96be\u6d4b\u5c11\u4fa0\u8981\u591a\u591a\u7559\u795e\u3002\u201d \u3000\u3000\u7b2c\u56db\u7ec4\uff0c\u8d70\u5728\u6700\u540e\uff0c\u7adf\u662f\u5929\u738b\u95e8\u7684\u9752\u51e4\uff0c\u5e26\u7740\u738b\u5b37\u3001\u674e\u5b37\u3001\u8eab\u4fa7\u4e00\u4e2a\u7a7f\u7740\u5929\u84dd\u52b2\u88c5\u7684\u5c11\u5973\uff0c\u5de6\u624b\u62b1\u7740\u4e00\u9762\u4ee4\u724c\uff0c\u548c\u4e00\u652f\u957f\u903e\u4e8c\u5c3a\u7684\u6731\u7b14\uff0c\u8eab\u540e\u8ddf\u7740\u4e09\u4e2a\u8eab\u9ad8\u516b\u5c3a\u9762\u8272\u9752\u7d2b\uff0c\u53d1\u4f3c\u6731\u7802\u7684\u4eba\uff0c\u5404\u6367\u7740\u4e00\u67c4\u516b\u5c3a\u957f\u7684\u94a2\u53c9\u3002 \u3000\u3000\u4ed6\u4eec\u548c\u524d\u9762\u4e09\u7ec4\u4fdd\u6301\u4e86\u76f8\u5f53\u7684\u8ddd\u79bb\uff0c\u4f3c\u4e4e\u662f\u53e6\u6709\u7528\u5fc3\u3002 \u3000\u3000\u6c5f\u67ab\u4f4e\u58f0\u9053\uff1a\u201c\u90a3\u4e2a\u84dd\u8863\u59d1\u5a18\uff0c\u5927\u6982\u662f\u5929\u738b\u95e8\u4e2d\u7684\u84dd\u98ce\u4e86\uff0c\u5e26\u7684\u4eba\u5e94\u8be5\u662f\u95e8\u4e0b\u4e09\u9b54\uff0c\u5929\u738b\u548c\u56db\u5927\u5929\u9e45\u53ca\u4e94\u602a\uff0c\u8fd8\u672a\u73b0\u8eab\uff0c\u5f53\u771f\u662f\u51fa\u52a8\u4e86\u5927\u90e8\u7cbe\u9510\u3002\u201d \u3000\u3000\u6b27\u9633\u662d\u9053\uff1a\u201c\u5e0c\u671b\u4ed6\u4eec\u80fd\u5b88\u70b9\u541b\u5b50\u98ce\u5ea6\uff0c\u4e00\u65f6\u4e00\u7684\u6bd4\u4e2a\u80dc\u8d1f\u3002\u201d \u3000\u3000\u524d\u9762\u4e09\u6279\u4eba\u624b\uff0c\u903c\u8fd1\u7684\u5230\u4e00\u5927\u8ddd\u79bb\uff0c\u624d\u505c\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u4f46\u84dd\u51e4\u3001\u9752\u98ce\u5374\u505c\u5728\u5341\u4e08\u4ee5\u5916\uff0c\u5927\u6709\u8896\u624b\u65c1\u89c2\u8fd9\u573a\u9f99\u4e89\u864e\u6597\u7684\u7528\u5fc3\u3002 \u3000\u3000\u6c5f\u67ab\u8e0f\u524d\u4e09\u6b65\uff0c\u7a81\u51fa\u4e8e\u4f17\u4eba\u4e4b\u5916\uff0c\u4e00\u4e2a\u4eba\u9762\u5bf9\u7740\u4e09\u6279\u6247\u5f62\u800c\u7acb\u7684\u6c5f\u6e56\u9ad8\u624b\uff0c\u51b7\u51b7\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8bf8\u4f4d\uff0c\u90a3\u4e00\u6279\u662f\u6253\u5934\u9635\u7684\uff1f\u201d \u3000\u3000\u4e09\u5bab\u4e3b\u8138\u8272\u4e00\u53d8\uff0c\u9053\uff1a\u201c\u6c5f\u67ab\uff0c\u4f60\u679c\u7136\u6ca1\u6709\u6b7b\uff01\u771f\u662f\u597d\u957f\u7684\u547d\uff1f\u201d \u3000\u3000\u201c\u593a\u9b42\u795e\u9488\u7684\u6ecb\u5473\uff0c\u53ef\u4e0d\u600e\u4e48\u597d\u53d7\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u4e09\u5bab\u4e3b\u8bf7\u5c0f\u5fc3\u4e86\uff0c\u4eca\u5929\u8bf4\u4e0d\u5b9a\u4f60\u4f1a\u6328\u4e00\u9488\uff0c\u54c1\u5c1d\u4e00\u4e0b\u9488\u4e0a\u7684\u6bd2\u6027\u5982\u4f55\uff1f\u201d \u3000\u3000\u201c\u5728\u8c01\u624b\u4e2d\uff0c\u4e09\u5bab\u4e3b\u8bf7\u6162\u6162\u7684\u731c\u5427\uff01\u6055\u5728\u4e0b\u4e0d\u8bf4\u660e\u4e86\uff0c\u201d\u6c5f\u67ab\u7684\u76ee\u5149\u8f6c\u6ce8\u4e07\u65b9\u3001\u5929\u865a\u5b50\u7684\u8138\u4e0a\uff0c\u9053\uff1a \u3000\u3000\u201c\u4e24\u4f4d\u526f\u603b\u5802\u4e3b\u53ef\u662f\u8d76\u6765\u5e2e\u886c\u6211\u4eec\u7684\uff1f\u201d \u3000\u3000\u5929\u865a\u5b50\u5e72\u7b11\u4e24\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u5c31\u662f\u90a3\u4e2a\u6234\u7740\u9762\u5177\uff0c\u6b3a\u9a97\u8d2b\u9053\u548c\u8001\u4e07\u7684\u97e9\u9738\u4e86\u3002\u201d \u3000\u3000\u201c\u4e0d\u60dc\uff0c\u4e5f\u5728\u4f60\u526f\u603b\u5802\u4e3b\u7684\u547d\u4ee4\u4e4b\u4e0b\uff0c\u6740\u4e86\u5185\u5bab\u548c\u2026\u2026\u201d \u3000\u3000\u5bf9\u8fd9\u9876\u6263\u8fc7\u6765\u7684\u5927\u5e3d\u5b50\uff0c\u5929\u865a\u5b50\u6709\u7740\u4e0d\u80dc\u8d1f\u8377\u4e4b\u611f\uff0c\u6025\u6025\u63a5\u9053\uff1a\u201c\u4f60\u5316\u540d\u9876\u66ff\u97e9\u9738\uff0c\u6613\u5bb9\u6df7\u5165\u4e86\u6d1b\u9633\u5c45\uff0c\u6311\u62e8\u672c\u5802\u548c\u5185\u5bab\u4e00\u7cfb\u4e2d\u4eba\u51b2\u7a81\uff0c\u5176\u884c\u53ef\u4ee5\uff0c\u5176\u5fc3\u53ef\u8bdb\uff0c\u8fd8\u5f15\u8bf1\u672c\u5802\u957f\u8001\u5201\u9e4f\uff0c\u8bef\u5165\u6b67\u9014\uff0c\u2026\u2026\u201d \u3000\u3000\u76ee\u5149\u8f6c\u6ce8\u5230\u5201\u9e4f\u7684\u8eab\u4e0a\uff0c\u63a5\u9053\uff1a\u201c\u5201\u9e4f\uff0c\u56de\u5934\u662f\u5cb8\uff0c\u65e2\u5f80\u4e0d\u7a76\uff0c\u53ea\u8981\u4f60\u53cd\u6b63\u5f52\u6765\uff0c\u8d2b\u9053\u4ee5\u526f\u603b\u5802\u4e3b\u7684\u8eab\u4efd\uff0c\u62c5\u4fdd\u4f60\u4e0d\u53d7\u4efb\u4f55\u60e9\u7f5a\u3002\u201d \u3000\u3000\u201c\u7b97\u4e86\u2026\u2026\u201d\u5201\u9e4f\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\u201c\u4f60\u8fd8\u662f\u82b1\u70b9\u5fc3\u601d\uff0c\u4fdd\u4f4f\u4f60\u8fd9\u4e2a\u526f\u603b\u5802\u4e3b\u7684\u4f4d\u7f6e\u5427\uff01\u4f60\u9634\u9669\u82db\u6bd2\uff0c\u8ddf\u8fc7\u4f60\u7684\u4eba\uff0c\u5927\u6982\u90fd\u88ab\u4f60\u6298\u817e\u7684\u4e0d\u6b7b\u4e5f\u8981\u8131\u6389\u4e00\u5c42\u76ae\u3002\u201d \u3000\u3000\u5bf9\u6c5f\u67ab\u5929\u865a\u5b50\u5fc3\u4e2d\u5b58\u4e86\u4e03\u5206\u656c\u754f\uff0c\u4f46\u5201\u9e4f\u5728\u5929\u865a\u5b50\u5fc3\u4e2d\uff0c\u53ef\u662f\u597d\u5403\u7684\u679c\u5b50\uff0c\u6012\u58f0\u559d\u9053\uff1a \u3000\u3000\u201c\u53cd\u4e86\uff0c\u53cd\u4e86\uff0c\u5fe4\u9006\u72af\u4e0a\uff0c\u7f6a\u4e0d\u53ef\u8d66\uff0c\u51fa\u6765\uff0c\u8001\u8981\u628a\u4f60\u4e07\u5251\u788e\u5c38\u3002\u201d \u3000\u3000\u51f6\u72e0\u51b7\u9177\uff0c\u54ea\u91cc\u6709\u51fa\u5bb6\u4eba\u7684\u6837\u5b50\u3002 \u3000\u3000\u5201\u9e4f\u4e5f\u706b\u4e86\uff0c\u5389\u58f0\u559d\u9053\uff1a\u201c\u725b\u9f3b\u5b50\u8001\u9053\uff0c\u8fc7\u53bb\u4f60\u4f5c\u5a01\u4f5c\u798f\uff0c\u628a\u5201\u5927\u7237\u547c\u6765\u559d\u53bb\u7684\u3001\u6446\u5c3d\u4e86\u4f60\u526f\u603b\u5802\u4e3b\u7684\u5a01\u98ce\uff0c\u73b0\u5728\uff0c\u5201\u5927\u7237\u4e0d\u5403\u8fd9\u4e00\u5957\u4e86\u2026\u2026\u201d \u3000\u3000\u53e3\u4e2d\u5c31\u5e94\u7740\u8bdd\uff0c\u4eba\u5374\u4e5f\u5411\u5916\u51b2\u53bb\u3002 \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u4e00\u4f38\u624b\uff0c\u62e6\u4f4f\u4e86\u5201\u9e4f\uff0c\u540c\u65f6\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u6c5f\u5c11\u4fa0\uff0c\u4f60\u522b\u52a8\uff0c\u4f60\u4e00\u51fa\u9635\uff0c\u4ed6\u5c31\u9f9f\u7f29\u4e0d\u51fa\u4e86\uff0c\u8fd9\u4e2a\u6742\u6bdb\u8001\u9053\u53ef\u6076\u7684\u5f88\uff0c\u4ea4\u7ed9\u548c\u5c1a\u6536\u62fe\uff01\u201d \u3000\u3000\u7f13\u7f13\u79fb\u52a8\u811a\u6b65\uff0c\u884c\u4e86\u51fa\u6765\uff0c\u7b11\u9053\uff1a\u201c\u8001\u9053\u58eb\uff0c\u54b1\u4eec\u6765\u73a9\u4e24\u624b\u5982\u4f55\uff1f\u201d \u3000\u3000\u5929\u865a\u5b50\u4e24\u9053\u76ee\u5149\u51dd\u6ce8\u5728\u4e03\u5b9d\u548c\u5c1a\u7684\u8138\u4e0a\u770b\u4e86\u826f\u4e45\uff0c\u7a81\u7136\u54e6\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u662f\u4f60\u5440\uff01\u9152\u8089\u548c\u5c1a\uff0c\u5728\u6d1b\u9633\u5c45\u4e2d\uff0c\u4f60\u6234\u4e0a\u4e86\u5047\u53d1\uff0c\u8131\u53bb\u50e7\u888d\uff0c\u8fd8\u771f\u628a\u6211\u8001\u9053\u7ed9\u9a97\u4f4f\u4e86\uff0c\u4eca\u65e5\u539f\u5f62\u6bd5\u9732\u4e86\uff0c\u8fd8\u6562\u51fa\u9762\u53eb\u9635\uff0c\u5475\u5475\uff01\u771f\u662f\u5bff\u661f\u516c\u540a\u9888\uff0c\u5acc\u547d\u957f\u4e86\u3002\u201d \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u4e00\u76f4\u5728\u6c5f\u6e56\u4e0a\u8d70\u52a8\uff0c\u6697\u4e2d\u89c2\u5bdf\u5f62\u52bf\uff0c\u4f46\u5374\u6df1\u85cf\u4e0d\u9732\uff0c\u8868\u9762\u4e0a\u5341\u5206\u7a9d\u56ca\uff0c\u522b\u8bf4\u5929\u865a\u5b50\u8fd9\u7b49\u8eab\u4efd\u7684\u4eba\u7269\u4e86\uff0c\u5c31\u662f\u4f4e\u4e24\u7ea7\u7684\u6c5f\u6e56\u540c\u9053\uff0c\u50cf\u5201\u9e4f\u4e00\u6837\u7684\u4eba\u7269\uff0c\u4e5f\u4e0d\u4f1a\u628a\u4e03\u5b9d\u548c\u5c1a\u770b\u5728\u773c\u4e2d\u3002 \u3000\u3000\u4e5f\u6b63\u56e0\u5982\u6b64\uff0c\u4ed6\u624d\u80fd\u5728\u6c5f\u6e56\u4e0a\u8dd1\u6765\u8dd1\u53bb\uff0c\u672a\u6b64\u8d77\u4eba\u7684\u6ce8\u610f\u3002 \u3000\u3000\u201c\u58eb\u522b\u4e09\u65e5\uff0c\u522e\u76ee\u76f8\u89c6\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u7b11\u9053\uff1a\u201c\u725b\u9f3b\u5b50\u8001\u9053\uff0c\u6211\u53ef\u662f\u5148\u7ed9\u4f60\u4e00\u4e2a\u8b66\u544a\uff0c\u52a8\u4e0a\u624b\uff0c\u683d\u5012\u6211\u548c\u5c1a\u624b\u4e2d\uff0c\u53ef\u4e0d\u8981\u6028\u5929\u5c24\u4eba\uff0c\u2026\u2026\u201d \u3000\u3000\u201c\u4f60\u9152\u8089\u548c\u5c1a\u5439\u725b\u7684\u672c\u9886\uff0c\u6c5f\u6e56\u4e0a\u8c01\u4eba\u4e0d\u77e5\uff0c\u90a3\u4e2a\u4e0d\u6653\u3002\u8001\u9053\u6211\u53ea\u7528\u4e00\u53ea\u5de6\u624b\uff0c\u5341\u62db\u4e4b\u5185\uff0c\u8981\u628a\u4f60\u6446\u7684\u5e73\u5e73\u6574\u6574\u2026\u2026\u201d\u5929\u865a\u5b50\u9053\uff1a\u201c\u4f60\u7528\u5175\u5203\uff0c\u8001\u9053\u7237\u6211\uff0c\u5c31\u966a\u4f60\u7a7a\u624b\u8fc7\u51e0\u62db\u5427\uff1f\u201d \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u53ef\u662f\u73a9\u547d\u7684\u4e8b\uff0c\u636e\u6211\u6240\u77e5\uff0c\u4f60\u5728\u5251\u4e0a\u9020\u8be3\u4e0d\u9519\uff0c\u4e0d\u62d4\u5251\uff0c\u4f60\u8fd8\u6709\u4ec0\u4e48\u800d\u7684\uff1f\u201d \u3000\u3000\u201c\u5bf9\u4ed8\u4f60\u9152\u8089\u548c\u5c1a\uff0c\u8001\u9053\u7237\u4e00\u53ea\u624b\u5c31\u7528\u4e0d\u5b8c\u4e86\uff01\u201d \u3000\u3000\u7a81\u7136\u8e0f\u524d\u4e00\u6b65\uff0c\u4e00\u638c\u5288\u4e0b\u3002 \u3000\u3000\u4ed6\u679c\u7136\u7528\u4ed6\u7684\u5de6\u624b\uff0c\u53ef\u4e5f\u638c\u98ce\u547c\u5578\uff0c\u9707\u7684\u79ef\u96ea\u7eb7\u98de\u3002 \u3000\u3000\u201c\u597d\u5427\uff01\u548c\u5c1a\u8ba9\u4f60\u4e09\u638c\uff0c\u4e5f\u597d\u4f7f\u4f60\u6b7b\u7684\u987a\u6c14\u4e00\u4e9b\u3002\u201d \u3000\u3000\u65e0\u865a\u5b50\u5927\u6012\uff0c\u5de6\u638c\u6325\u8f6c\uff0c\u8fde\u5288\u4e09\u638c\u3002 \u3000\u3000\u5927\u548c\u5c1a\u8bf4\u8ba9\u4e09\u638c\uff0c\u5c31\u53ea\u8ba9\u4e09\u638c\uff0c\u7b2c\u56db\u638c\u5c31\u5f00\u59cb\u53cd\u51fb\uff0c\u5de6\u638c\u4e00\u626c\u67b6\u5f00\u5929\u865a\u5b50\u7684\u638c\u52bf\uff0c\u53f3\u62f3\u76f4\u6363\u8fc7\u53bb\u3002 \u3000\u3000\u770b\u4e0a\u53bb\u5e73\u6de1\u65e0\u5947\uff0c\u53ea\u662f\u4e00\u62f3\u6253\u5411\u5929\u865a\u5b50\u7684\u524d\u80f8\u3002 \u3000\u3000\u5927\u548c\u5c1a\u6321\u5f00\u4ed6\u90a3\u4e00\u638c\uff0c\u4ed6\u5df2\u7ecf\u89c9\u51fa\u4e0d\u5bf9\uff0c\u90a3\u4e00\u80a1\u968f\u624b\u6392\u51fa\u7684\u6f5c\u529b\uff0c\u5b8c\u5168\u628a\u4ed6\u7684\u4e00\u6761\u5de6\u81c2\u5c01\u4f4f\uff0c\u8fce\u80f8\u6363\u6765\u7684\u4e00\u62f3\uff0c\u7b3c\u7f69\u4e86\u80f8\u8179\u4e03\u5904\u5927\u707e\uff0c\u7adf\u4e0d\u77e5\u4ed6\u653b\u5411\u4f55\u5904\u3002 \u3000\u3000\u5c31\u90a3\u4e48\u4e00\u72b9\u8c6b\uff0c\u524d\u80f8\u5df2\u7ed3\u7ed3\u5b9e\u5b9e\u6328\u4e86\u4e00\u62f3\u3002 \u3000\u3000\u5929\u865a\u5b50\u8fd0\u529f\u6297\u62d2\uff0c\u8eab\u5b50\u7ad9\u7a33\u5728\u539f\u5730\uff0c\u7adf\u7136\u4e00\u6b65\u4e5f\u672a\u79fb\u52a8\u3002 \u3000\u3000\u201c\u597d\u7cbe\u6df1\u7684\u529f\u529b\u2026\u2026\u201d\u4e00\u638c\u62db\u9b42\u4e07\u65b9\u4f4e\u58f0\u8d5e\u9053\uff1a\u201c\u9053\u5144\u7684\u4fee\u4e3a\u4e4b\u6df1\uff0c\u5144\u5f1f\u96be\u53ca\u2026\u2026\u201d \u3000\u3000\u5ffd\u89c1\u5929\u865a\u5b50\u9f3b\u3001\u8033\u3001\u53cc\u76ee\u4e2d\uff0c\u4e00\u9f50\u6d8c\u800c\u51fa\u8840\u6765\uff0c\u5634\u5df4\u4e00\u5f20\uff0c\u5410\u51fa\u4e00\u5927\u53e3\u8840\u5757\u3002 \u3000\u3000\u4ee5\u4e07\u65b9\u7684\u7ecf\u9a8c\uff0c\u4e00\u773c\u5c31\u770b\u51fa\u90a3\u662f\u88ab\u9707\u788e\u7684\u5185\u8151\uff0c\u4e0d\u7981\u5927\u5403\u4e00\u60ca\u3002 \u3000\u3000\u4f46\u89c1\u5929\u865a\u5b50\u8eab\u5b50\u4e00\u6643\uff0c\u5012\u4e86\u4e0b\u53bb\uff0c\u53e3\u4e2d\u8bf4\u9053\uff1a\u201c\u5c0f\u5fc3\u554a\uff01\u5927\u548c\u5c1a\u626e\u732a\u5403\u2026\u2026\u201d \u3000\u3000\u5927\u6168\u662f\u60f3\u8bf4\u626e\u732a\u5403\u8001\u864e\uff0c\u53ef\u60dc\u6ca1\u6709\u8bf4\u5b8c\uff0c\u4eba\u5c31\u6c14\u7edd\u800c\u901d\u3002 \u3000\u3000\u597d\u5389\u5bb3\u7684\u4e00\u62f3\uff0c\u6c5f\u67ab\u3001\u7f57\u5170\u3001\u6b27\u9633\u662d\uff0c\u770b\u5f97\u5927\u4e3a\u60ca\u5f02\uff0c\u4e5f\u90fd\u6709\u7740\u610f\u5916\u7684\u611f\u89c9\uff0c\u5927\u548c\u5c1a\u7684\u65e0\u76f8\u795e\u529f\uff0c\u4f3c\u5df2\u5230\u4e86\u6781\u9ad8\u7684\u5883\u754c\uff0c\u4e03\u5b9d\u62f3\u6cd5\uff0c\u4e5f\u5230\u4e86\u5b9e\u800c\u4e0d\u534e\uff0c\u5927\u5de7\u4f3c\u62d9\u7684\u5316\u5883\u4e86\u3002 \u3000\u3000\u9093\u98de\u548c\u5201\u9e4f\uff0c\u66f4\u662f\u60ca\u7684\u5446\u4f4f\u4e86\uff0c\u4e00\u62f3\u80fd\u6253\u6b7b\u50cf\u5929\u865a\u5b50\u8fd9\u6837\u7684\u6b66\u6797\u9ad8\u624b\uff0c\u53ef\u662f\u8fde\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\u7684\u4e8b\u60c5\u3002 \u3000\u3000\u4f46\u5b83\u7adf\u662f\u4e8b\u5b9e\uff0c\u4eba\u5df2\u6b7b\u4e86\uff0c\u8840\u6d8c\u4e03\u7a8d\uff0c\u6a2a\u5c38\u5728\u5730\u3002 \u3000\u3000\u8fd9\u4e00\u62f3\u4e4b\u5a01\uff0c\u5fc3\u4e2d\u6697\u6697\u4ed8\u9053\uff1a\u201c\u81ea\u5df1\u5168\u529b\u4e00\u62f3\u6253\u5728\u5929\u865a\u5b50\u7684\u524d\u80f8\uff0c\u4e5f\u672a\u5fc5\u80fd\u628a\u4ed6\u6d3b\u6d3b\u6253\u6b7b\u3002\u201d \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u5475\u5475\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8001\u9053\u58eb\uff0c\u4f60\u592a\u5927\u610f\u4e86\uff0c\u548c\u5c1a\u53ef\u662f\u5df2\u7ecf\u518d\u4e09\u63d0\u9192\u4f60\uff0c\u4f60\u4e0d\u542c\u3002\u201d\u76ee\u5149\u4e00\u63a0\u4e07\u65b9\uff0c\u9053\uff1a\u201c\u4e0d\u77e5\u54ea\u4e00\u4f4d\u60f3\u66ff\u8001\u9053\u58eb\u62a5\u4ec7\uff0c\u548c\u5c1a\u8fd9\u91cc\u5019\u6559\u4e86\uff01\u201d \u3000\u3000\u4e07\u65b9\u8f6c\u8138\u4ed6\u987e\uff0c\u542c\u5230\u88c5\u4f5c\u6ca1\u542c\u5230\u3002 \u3000\u3000\u4ed6\u867d\u53f7\u79f0\u4e00\u638c\u62db\u9b42\uff0c\u4f46\u5fc3\u4e2d\u660e\u767d\uff0c\u5c31\u662f\u4e24\u638c\uff0c\u4e5f\u62db\u4e0d\u4e86\u5929\u865a\u5b50\u7684\u9b42\uff0c\u4f46\u5929\u865a\u5b50\u5374\u88ab\u4eba\u5bb6\u4e00\u62f3\u6253\u6b7b\u3002 \u3000\u3000\u53ea\u542c\u4e24\u58f0\u54c7\u54c7\u602a\u53eb\uff0c\u4e24\u4e2a\u9762\u523a\u82b1\u7eb9\uff0c\u8eab\u4e0a\u7a7f\u7740\u9006\u9cde\u7ad6\u7acb\u8863\u7740\u7684\u5065\u58ee\u5987\u4eba\uff0c\u7a81\u7136\u5e76\u80a9\u5411\u4e03\u5b9d\u548c\u5c1a\u8d70\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u4e09\u5bab\u4e3b\u60f3\u963b\u6b62\u4e24\u4eba\uff0c\u4f46\u5df2\u665a\u4e86\u4e00\u6b65\uff0c\u5fc3\u4e2d\u6697\u6697\u9a82\u9053\uff1a\u201c\u5357\u8352\u86ee\u611a\u679c\u7136\u662f\u5168\u65e0\u5fc3\u673a\uff0c\u5929\u9a6c\u5802\u7684\u4eba\u672a\u6b7b\u5149\uff0c\u4f60\u4eec\u6025\u7740\u51fa\u5934\u5e72\u4ec0\u4e48\u5462\uff1f\u201d \u3000\u3000\u773c\u770b\u8038\u7acb\u7684\u9cde\u7247\u4e0a\u53d1\u7740\u84dd\u5149\uff0c\u5206\u660e\u6dec\u6709\u5267\u6bd2\uff0c\u4e5f\u8bb8\u5b83\u672c\u8eab\u5c31\u662f\u4e00\u79cd\u5947\u6bd2\u7684\u86c7\u76ae\u4f5c\u6210\uff0c\u6c5f\u67ab\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u5927\u548c\u5c1a\uff0c\u6b47\u4e00\u4e0b\uff0c\u8fd9\u4e00\u9635\u4ea4\u7ed9\u5c0f\u5f1f\uff01\u201d \u3000\u3000\u201c\u6700\u96be\u5bf9\u4ed8\u7684\u53ef\u80fd\u662f\u4e00\u54c1\u5200\uff0c\u548c\u4ed6\u968f\u6765\u7684\u56db\u5927\u5f1f\u5b50\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u65bd\u5c55\u4f20\u97f3\u4e4b\u672f\uff0c\u9053\uff1a \u3000\u3000\u201c\u4f60\u7559\u70b9\u7cbe\u795e\u5bf9\u4ed8\u4ed6\u4eec\u5427\uff01\u201d \u3000\u3000\u6c5f\u67ab\u671b\u4e86\u90a3\u7ae5\u989c\u9e64\u53d1\u7684\u8001\u4eba\u4e00\u773c\uff0c\u672a\u518d\u591a\u8a00\u3002 \u3000\u3000\u5927\u548c\u5c1a\u9762\u62d9\u5fc3\u5de7\uff0c\u4e00\u70b9\u4e5f\u4e0d\u6258\u5927\uff0c\u770b\u7740\u4e24\u4e2a\u5987\u4eba\u8eab\u4e0a\u7684\u8863\u670d\uff0c\u7b11\u9053\uff1a\u201c\u86c7\u9cde\u6bd2\u8863\uff0c\u5389\u5bb3\u5440\uff01\u5389\u5bb3\uff01\u8840\u8089\u4e4b\u8eaf\uff0c\u4f55\u80fd\u548c\u8fd9\u6bd2\u8863\u76f8\u6297\uff1f\u201d \u3000\u3000\u53e3\u4e2d\u53eb\u7740\uff0c\u4eba\u5374\u4ece\u8863\u888b\u4e2d\u53d6\u51fa\u4e86\u91d1\u3001\u94f6\u53cc\u5708\uff0c\u5206\u63e1\u53cc\u624b\u3002 \u3000\u3000\u201c\u91d1\u3001\u94f6\u3001\u94dc\u3001\u94c1\u3001\u9521\u2026\u2026\u201d\u4e00\u54c1\u5200\u5927\u58f0\u53eb\u9053\uff1a\u201c\u65e0\u76f8\u5927\u5e08\u7684\u4e94\u798f\u7167\u547d\u5708\uff0c\u8fd8\u6709\u94dc\u3001\u94c1\u3001\u9521\u4e09\u5708\u5462\uff1f\u201d \u3000\u3000\u201c\u4e8c\u5341\u5e74\u6765\u5728\u6c5f\u6e56\u4e0a\u51fa\u73b0\u8fc7\u4e86\uff0c\u7adf\u7136\u8fd8\u6709\u4eba\u8ba4\u5f97\u51fa\u6765\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u7b11\u9053\uff1a\u201c\u4f60\u6742\u6bdb\u5b50\u558a\u53eb\u4ec0\u4e48\uff1f\u63a5\u4e0b\u4e86\u548c\u5c1a\u7684\u91d1\u94f6\u53cc\u5708\uff0c\u94dc\u3001\u94c1\u3001\u9521\u81ea\u4f1a\u51fa\u73b0\u3002\u201d \u3000\u3000\u4e24\u4e2a\u903c\u5411\u5927\u548c\u5c1a\u7684\u7c97\u58ee\u5987\u4eba\uff0c\u867d\u7136\u4e0d\u8ba4\u8bc6\u4e94\u798f\u7167\u547d\u5708\uff0c\u4f46\u542c\u4e00\u54c1\u5200\u7684\u5927\u58f0\u558a\u53eb\uff0c\u53ef\u4e5f\u660e\u767d\uff0c\u662f\u5728\u5411\u4e24\u4eba\u793a\u8b66\uff0c\u8981\u5979\u4eec\u5c0f\u5fc3\u5e94\u654c\u3002 \u3000\u3000\u4e24\u4e2a\u4eba\u770b\u4e0a\u53bb\u7c97\u8822\u602a\u5f02\uff0c\u4f46\u5b9e\u5728\u4e0d\u50bb\uff0c\u7acb\u523b\u505c\u4e0b\u811a\u6b65\uff0c\u4e92\u76f8\u4f4e\u58f0\u5546\u91cf\u3002 \u3000\u3000\u53ea\u770b\u5230\u5979\u4eec\u53e3\u9f7f\u542f\u52a8\uff0c\u5495\u5495\u565c\u565c\uff0c\u53ef\u6ca1\u6709\u4eba\u542c\u5f97\u61c2\u5979\u4eec\u8bf4\u4e9b\u4ec0\u4e48\uff1f \u3000\u3000\u90a3\u662f\u5357\u8352\u571f\u8bed\uff0c\u8fde\u8db3\u8ff9\u904d\u5929\u4e0b\uff0c\u7cbe\u901a\u5404\u5730\u65b9\u8a00\u8bed\u7684\u4e03\u5b9d\u548c\u5c1a\uff0c\u4e5f\u4e00\u6837\u542c\u4e0d\u660e\u767d\u3002 \u3000\u3000\u4f46\u4e24\u4e2a\u5987\u4eba\u7684\u4e3e\u52a8\uff0c\u8868\u8fbe\u51fa\u5979\u4eec\u4ea4\u8c08\u7684\u76ee\u7684\u3002 \u3000\u3000\u53ea\u89c1\u4e24\u4eba\u540c\u65f6\u4f38\u624b\u5728\u5f62\u5982\u86c7\u9cde\u7684\u8863\u670d\u4e2d\uff0c\u5404\u53d6\u51fa\u4e24\u628a\u77ed\u5200\u3002 \u3000\u3000\u5200\u957f\u4e0d\u8fc7\u4e00\u5c3a\u516b\u5bf8\uff0c\u4f46\u5bf9\u8eab\u4e00\u7247\u84dd\u8292\uff0c\u663e\u7136\u662f\u5947\u6bd2\u6dec\u70bc\u7684\u6bd2\u5200\u3002 \u3000\u3000\u5927\u548c\u5c1a\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u8fdc\u5728\u5357\u8352\uff0c\u8fc7\u7684\u662f\u4f55\u7b49\u900d\u9065\u81ea\u5728\uff0c\u4e3a\u4ec0\u4e48\u8dd1\u5230\u4e2d\u539f\u6765\u8e0f\u6dcc\u8fd9\u6b21\u6d51\u6c34\u5462\uff1f\u201d \u3000\u3000\u8a00\u4e0b\u4e4b\u610f\uff0c\u6740\u673a\u5df2\u52a8\u3002 \u3000\u3000\u6c5f\u67ab\u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u9cde\u8863\u3001\u6bd2\u5200\uff0c\u6cbe\u8eab\u53d6\u547d\uff0c\u4f60\u5c3d\u7ba1\u65bd\u4e0b\u6bd2\u624b\uff0c\u4e0d\u8981\u5fc3\u5b58\u4ec1\u6148\u3002\u201d \u3000\u3000\u4e24\u4e2a\u7cbe\u58ee\u7684\u5987\u4eba\uff0c\u5df2\u7136\u602a\u543c\u4e00\u58f0\uff0c\u5206\u5de6\u53f3\u5411\u4e03\u5b9d\u548c\u5c1a\u6251\u53bb\u3002 \u3000\u3000\u5979\u4eec\u4e24\u4eba\u5e73\u98de\u800c\u8d77\uff0c\u9cde\u8863\u95ea\u5149\uff0c\u770b\u4e0a\u53bb\uff0c\u5b8c\u5168\u6ca1\u4eba\u7684\u5473\u9053\uff0c\u50cf\u4e24\u6761\u602a\u5f02\u7684\u98de\u86c7\u3002 \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u53cc\u5708\u4e00\u6325\uff0c\u7a81\u7136\u8131\u624b\u800c\u51fa\uff0c\u91d1\u3001\u94f6\u4e24\u5708\u5149\u534e\uff0c\u5206\u5411\u4e24\u4eba\u51fb\u53bb\u3002 \u3000\u3000\u539f\u6765\u3001\u4e94\u798f\u7167\u547d\u5708\uff0c\u53ef\u4ee5\u63b7\u51fa\u4f24\u654c\u3002 \u3000\u3000\u7f57\u5170\u4f4e\u58f0\u95ee\u9053\uff1a\u201c\u8001\u516c\uff0c\u4ed6\u53cc\u5708\u51fa\u624b\uff0c\u5982\u4f55\u6536\u56de\u6765\u5462\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u4e48\uff1f\u6211\u4e5f\u4e0d\u77e5\u9053\u4e86\u2026\u2026\u201d\u6b27\u9633\u662d\u9053\uff1a\u201c\u4f60\u95ee\u95ee\u6c5f\u67ab\u5427\uff01\u201d \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u5df2\u7ecf\u7528\u4e0d\u7740\u95ee\u4e86\u3002 \u3000\u3000\u53ea\u89c1\u4e24\u4e2a\u5065\u5987\u624b\u4e2d\u6bd2\u5200\u4e00\u62e8\uff0c\u60f3\u628a\u53cc\u5708\u62e8\u5f00\uff0c\u90a3\u77e5\u5200\u3001\u5708\u76f8\u89e6\uff0c\u91d1\u94f6\u53cc\u5708\uff0c\u7a81\u7136\u4e00\u8f6c\uff0c\u6a2a\u91cc\u98de\u53bb\uff0c\u4ea4\u9519\u800c\u8fc7\uff0c\u6b63\u51fb\u5728\u4e24\u4e2a\u9cde\u8863\u602a\u5987\u7684\u8170\u4e0a\uff0c\u90a3\u4e48\u5065\u58ee\u7684\u4e24\u4e2a\u4eba\uff0c\u7adf\u88ab\u6253\u7684\u6a2a\u98de\u4e09\u5c3a\uff0c\u8dcc\u5728\u96ea\u5730\u4e0a\u3002 \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u53cc\u624b\u4e00\u6536\uff0c\u53cc\u5708\u65cb\u98de\u800c\u56de\u3002 \u3000\u3000\u6562\u60c5\u53cc\u5708\u4e0a\u4ec5\u6709\u4e00\u6761\u7ec6\u7d22\uff0c\u6df1\u85cf\u8896\u4e2d\uff0c\u5927\u548c\u5c1a\u4ee5\u5185\u529b\u64cd\u7eb5\uff0c\u4e0d\u77e5\u5185\u60c5\u7684\u4eba\uff0c\u5f88\u96be\u770b\u5f97\u51fa\u6765\u3002 \u3000\u3000\u4e24\u4e2a\u7cbe\u58ee\u7684\u5065\u5987\uff0c\u8dcc\u4e0b\u53bb\uff0c\u5c31\u6ca1\u6709\u518d\u52a8\u8fc7\uff0c\u770b\u6837\u5b50\u5df2\u7ecf\u633a\u5c38\u4e86\uff0c\u5708\u4e0a\u7684\u52b2\u529b\uff0c\u5f53\u771f\u5982\u5de8\u9524\u51fb\u5ca9\u4e00\u822c\uff0c\u770b\u7684\u4eba\u5fc3\u51c9\u80c6\u98a4\u3002 \u3000\u3000\u597d\u5389\u5bb3\u7684\u5927\u548c\u5c1a\uff0c\u4e24\u6b21\u51fa\u624b\uff0c\u4e09\u4eba\u9001\u547d\uff0c\u4e00\u4e2a\u662f\u6c5f\u6e56\u9053\u4e0a\u7684\u4e00\u6d41\u9ad8\u624b\uff0c\u4e24\u4e2a\u662f\u6765\u81ea\u5357\u8352\u7684\u602a\u5ba2\u3002 \u3000\u3000\u201c\u77a7\u4e0d\u51fa\u554a\uff01\u5927\u548c\u5c1a\u7adf\u662f\u5982\u6b64\u7684\u9ad8\u660e\u795e\u52c7\uff0c\u2026\u2026\u201d\u7f57\u5170\u4f4e\u58f0\u95ee\u6c5f\u67ab\u9053\uff1a\u201c\u4ed6\u6bd4\u4ee4\u5e08\u65e0\u76f8\u5927\u5e08\u5982\u4f55\uff1f\u201d \u3000\u3000\u201c\u770b\u6837\u5b50\uff0c\u5df2\u5c3d\u5f97\u771f\u4f20\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u8bba\u4ed6\u5185\u529b\u7684\u96c4\u539a\uff0c\u51fa\u624b\u7684\u624e\u5b9e\uff0c\u5c0f\u5f1f\u4e5f\u81ea\u53f9\u5f17\u5982\u4e86\u3002\u201d \u3000\u3000\u5927\u548c\u5c1a\u6536\u56de\u53cc\u5708\uff0c\u4e0d\u518d\u7406\u4f1a\u654c\u4eba\uff0c\u8f6c\u5934\u8d70\u4e86\u56de\u6765\uff0c\u4e00\u9762\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u6c5f\u8001\u5f1f\uff0c\u548c\u5c1a\u8fd9\u4e24\u62db\u7528\u51fa\u4e86\u5168\u529b\u3002\u5f3a\u618b\u7740\u4e00\u53e3\u6c14\u6ca1\u6709\u5598\u51fa\u6765\uff0c\u518d\u6253\u4e0b\u53bb\u5c31\u8981\u73b0\u539f\u5f62\u4e86\u3002\u201d \u3000\u3000\u6b27\u9633\u662d\u76ee\u7779\u5927\u548c\u5c1a\u51db\u51db\u795e\u5a01\uff0c\u65e9\u5df2\u6280\u75d2\uff0c\u98de\u8eab\u4e00\u8dc3\u800c\u51fa\uff0c\u6a2a\u5251\u559d\u9053\uff1a\u201c\u533a\u533a\u6b27\u9633\u662d\uff0c\u6c5f\u6e56\u4e2d\u65e0\u540d\u5c0f\u5352\u3001\u54ea\u4e00\u4f4d\u4e0d\u541d\u8d50\u6559\uff0c\u8bf7\u51fa\u6765\u6307\u70b9\u6307\u70b9\uff1f\u201d \u3000\u3000\u4ed6\u8eab\u8eaf\u4fee\u4f1f\uff0c\u58f0\u5982\u6d2a\u949f\uff0c\u6a2a\u5251\u800c\u7acb\uff0c\u5148\u58f0\u593a\u4eba\uff0c\u4e00\u65f6\u95f4\uff0c\u7adf\u65e0\u4eba\u51fa\u9762\u8fce\u654c\u3002 \u3000\u3000\u5201\u9e4f\u4f4e\u58f0\u9053\uff1a\u201c\u8001\u9093\u554a\uff01\u5c0f\u5f1f\u6211\u95ef\u4e86\u51e0\u5341\u5e74\u6c5f\u6e56\uff0c\u53ef\u662f\u4ece\u6ca1\u6709\u89c1\u8fc7\u90a3\u4e48\u9738\u9053\u7684\u6b66\u529f\uff1f\u4e00\u51fb\u53d6\u547d\uff0c\u7528\u4e0d\u7740\u7b2c\u4e8c\u6b21\u51fa\u624b\uff0c\u4e56\u4e56\uff0c\u5f53\u771f\u662f\u5927\u5f00\u773c\u754c\u4e86\u3002\u201d \u3000\u3000\u201c\u5e78\u597d\u8fc7\u53bb\u6211\u6ca1\u6709\u60f9\u8fc7\u4ed6\u2026\u2026\u201d\u9093\u98de\u9053\uff1a\u201c\u770b\u4ed6\u6740\u6cd5\u7684\u5229\u843d\u53ef\u6015\uff0c\u4f3c\u4e0d\u5728\u6c5f\u67ab\u4e4b\u4e0b\uff1f\u201d \u3000\u3000\u6b27\u9633\u662d\u4e0d\u89c1\u6709\u4eba\u51fa\u9635\u56de\u8bdd\uff0c\u5fc3\u5934\u5341\u5206\u607c\u706b\uff0c\u957f\u5251\u4e00\u6307\u795e\u9b3c\u53cc\u5200\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4e2a\u51fa\u6765\uff0c\u6211\u8981\u4e00\u5251\u6597\u53cc\u5200\uff01\u201d \u3000\u3000\u201c\u600e\u4e48\uff1f\u6307\u540d\u6311\u6218\u54ea\uff01\u2026\u2026\u201d\u795e\u3001\u9b3c\u53cc\u5200\u662f\u6210\u540d\u591a\u5e74\u7684\u4eba\u7269\uff0c\u8fd9\u53e3\u6c14\u5fcd\u4e0b\u53bb\uff0c\u65e5\u540e\uff0c\u54ea\u91cc\u8fd8\u80fd\u89c1\u4eba\uff1f \u3000\u3000\u53ea\u597d\u53cc\u53cc\u7f13\u6b65\u800c\u51fa\uff0c\u795e\u5200\u62bd\u51fa\u7a84\u5200\uff0c\u63a5\u9053\uff1a\u201c\u4f60\u8981\u4e00\u5251\u6597\u53cc\u5200\uff1f \u3000\u3000\u5012\u662f\u8c6a\u6c14\u5e72\u4e91\uff0c\u4e0d\u77e5\u8fd9\u53e5\u8bdd\u53ef\u5426\u5f53\u771f\uff1f\u201d \u3000\u3000\u201c\u8001\u516c\uff0c\u4e0d\u80fd\u4e2d\u4ed6\u4eec\u7684\u9b3c\u8ba1\u2026\u2026\u201d\u7f57\u5170\u98de\u8dc3\u800c\u51fa\uff0c\u548c\u6b27\u9633\u662d\u5e76\u80a9\u800c\u7acb\uff0c\u63a5\u9053\uff1a\u201c\u6211\u4eec\u592b\u59bb\u8054\u624b\uff0c\u4e8c\u5bf9\u4e8c\uff0c\u624d\u7b97\u516c\u5e73\u554a\uff01\u201d \u3000\u3000\u5bf9\u7231\u59bb\u7684\u5173\u6000\uff0c\u6b27\u9633\u662d\u5341\u5206\u53d7\u7528\uff0c\u4f46\u8bdd\u5df2\u51fa\u53e3\uff0c\u76db\u6c34\u96be\u53d7\uff0c\u4e0d\u77e5\u5982\u4f55\u62d2\u7edd\u7f57\u5170\u63d2\u624b\u3002 \u3000\u3000\u53ea\u542c\u9b3c\u5200\u8bf4\u9053\uff1a\u201c\u6211\u8bf4\u795e\u5144\u554a\uff01\u725b\u76ae\u4eba\u4eba\u4f1a\u5439\uff0c\u8a00\u72b9\u5728\u8033\uff0c\u54ea\u53a2\u5df2\u7ecf\u6539\u53d8\u4e3b\u610f\u4e86\uff1f\u201d \u3000\u3000\u6b27\u9633\u662d\u53ea\u89c9\u8138\u4e0a\u53d1\u70ed\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u7f57\u5170\uff0c\u7ed9\u6211\u70b9\u9762\u5e72\u561b\uff01\u4ee5\u540e\uff0c\u8fd9\u79cd\u8bdd\uff0c\u6211\u4e0d\u518d\u8bf4\u5c31\u662f\uff01\u201d \u3000\u3000\u7f57\u5170\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u80dc\u8d25\u4e43\u5175\u5bb6\u5e38\u4e8b\uff0c\u4e0d\u8981\u592a\u901e\u5f3a\uff0c\u4e24\u4e2a\u8001\u8272\u9b3c\u8eab\u4e0a\u4e0d\u77e5\u85cf\u4e86\u591a\u5c11\u5200\uff0c\u4f60\u8981\u5c0f\u5fc3\u554a\uff01\u201d \u3000\u3000\u201c\u77e5\u9053\u4e86\u2026\u2026\u201d\u6b27\u9633\u662d\u9053\uff1a\u201c\u4f60\u9000\u4e0b\u53bb\u5427\uff01\u201d \u3000\u3000\u7f57\u5170\u53ea\u597d\u9000\u4e86\u4e08\u8bb8\uff0c\u4f46\u5251\u5728\u624b\u4e2d\uff0c\u5168\u795e\u8d2f\u6ce8\uff0c\u51c6\u5907\u968f\u65f6\u51fa\u624b\u6551\u63f4\u3002 \u3000\u3000\u6b27\u9633\u662d\u957f\u5251\u4e00\u632f\uff0c\u9053\uff1a\u201c\u6765\u5427\uff01\u73b0\u5728\u662f\u4e00\u5251\u6597\u53cc\u5200\u4e86\u3002\u201d \u3000\u3000\u795e\u3001\u9b3c\u53cc\u5200\u8d70\u7684\u975e\u5e38\u6162\uff0c\u4f46\u653b\u52bf\u5374\u5feb\u5982\u95ea\u7535\u3002 \u3000\u3000\u7f57\u5170\u9000\u4e0b\uff0c\u6b27\u9633\u662d\u90a3\u53e5\u4e00\u5251\u6597\u53cc\u5200\u4f59\u97f3\u72b9\u5728\uff0c\u4e24\u4eba\u624b\u4e2d\u7684\u7a84\u5200\u5200\u7ecf\u5230\u4e86\u6b27\u9633\u662d\u7684\u524d\u80f8\u3002 \u3000\u3000\u795e\u3001\u9b3c\u53cc\u5200\u867d\u662f\u540c\u65f6\u51fa\u624b\uff0c\u4f46\u4ed6\u4eec\u5e76\u672a\u5206\u7531\u5de6\u53f3\u5939\u653b\uff0c\u662f\u540c\u65f6\u7531\u6b63\u9762\u51fa\u624b\uff0c\u53cc\u5200\u5e76\u6392\u653b\u5230\u3002 \u3000\u3000\u6b27\u9633\u662d\u957f\u5251\u4e00\u5f0f\u201c\u957f\u8679\u7ecf\u5929\u201d\uff0c\u7adf\u662f\u786c\u5c01\u53cc\u5200\u3002 \u3000\u3000\u5251\u8d70\u8f7b\u76c8\uff0c\u4ed6\u8fd9\u79cd\u786c\u78b0\u786c\u7684\u6253\u6cd5\uff0c\u5927\u80cc\u4e86\u7528\u5251\u4e4b\u9053\u3002 \u3000\u3000\u4f46\u95fb\u4e00\u9635\u91d1\u94c1\u4ea4\u9e23\u4e4b\u58f0\uff0c\u4e24\u67c4\u7a84\u5200\uff0c\u7adf\u88ab\u4e00\u5251\u9707\u5f00\u3002 \u3000\u3000\u5200\u3001\u5251\u4e4b\u4e0a\uff0c\u53cc\u65b9\u90fd\u8d2f\u6ce8\u4e86\u5f3a\u5927\u7684\u5185\u529b\uff0c\u4e00\u5251\u5c01\u53cc\u5200\uff0c\u4e00\u5251\u8fd8\u7a0d\u5360\u4f18\u52bf\u3002 \u3000\u3000\u6b27\u9633\u662d\u7cbe\u795e\u5927\u632f\uff0c\u957f\u5251\u5c55\u53cd\u51fb\uff0c\u5251\u52bf\u5982\u957f\u6c5f\u5927\u6cb3\u4e00\u822c\uff0c\u7adf\u628a\u795e\u3001\u9b3c\u53cc\u5200\uff0c\u5708\u5165\u4e86\u4e00\u7247\u5251\u5149\u4e4b\u4e2d\u3002 \u3000\u3000\u4ed6\u5927\u534a\u751f\u4e13\u6ce8\u4e8e\u7ec3\u5251\u4e4b\u4e0a\uff0c\u5929\u751f\u795e\u529b\uff0c\u518d\u52a0\u4e0a\u6df1\u539a\u7684\u5185\u529f\uff0c\u5251\u4e0a\u52b2\u9053\u4e4b\u5f3a\uff0c\u80dc\u59bb\u4e00\u500d\uff0c\u795e\u3001\u9b3c\u53cc\u5200\u7acb\u523b\u611f\u89c9\u5230\uff0c\u68ee\u5bd2\u7684\u5251\u6c14\uff0c\u5bc6\u5e03\u56db\u5468\uff0c\u6709\u5982\u4e00\u4e2a\u9010\u6e10\u5408\u62e2\u7684\u94a2\u94b5\uff0c\u516b\u65b9\u56db\u9762\uff0c\u5168\u88ab\u5251\u5c01\u4f4f\uff0c\u5fc3\u4e2d\u5927\u611f\u9707\u9a87\u3002 \u3000\u3000\u795e\u3001\u9b3c\u53cc\u5200\u7eb5\u6a2a\u6c5f\u6e56\uff0c\u9664\u4e86\u53d7\u632b\u4e8e\u91d1\u4e39\u4e66\u751f\u548c\u5929\u72d0\u592b\u4eba\u4e4b\u5916\uff0c\u4ece\u672a\u5403\u8fc7\u8d25\u4ed7\uff0c\u4e5f\u4ece\u672a\u9047\u8fc7\u5982\u6b64\u9738\u9053\u7684\u5251\u6cd5\u3002 \u3000\u3000\u65e5\u524d\uff0c\u4e24\u4eba\u5408\u6597\u7f57\u5170\uff0c\u867d\u7136\u79cb\u8272\u5e73\u5206\uff0c\u4f46\u5374\u6709\u4e09\u5206\u8c26\u8ba9\u88c5\u4f5c\uff0c\u4e00\u5219\u662f\u81ea\u4fdd\u5fc3\u6001\uff0c\u4e0d\u613f\u6d89\u9669\u6740\u654c\uff0c\u4f7f\u81ea\u8eab\u53d7\u4f24\u3002 \u3000\u3000\u4e8c\u5219\u7f57\u5170\u5a07\u7f8e\u59ff\u8272\uff0c\u4e5f\u4f7f\u4e24\u4e2a\u8001\u8272\u9b3c\u5fc3\u5b58\u601c\u60dc\uff0c\u5f53\u7136\uff0c\u7f57\u5170\u5251\u6cd5\u7684\u7cbe\u5999\uff0c\u4e5f\u4f7f\u4e24\u4eba\u5927\u611f\u610f\u5916\u3002 \u3000\u3000\u4f46\u6b64\u523b\uff0c\u60c5\u52bf\u4e0d\u540c\u4e86\u3002 \u3000\u3000\u6b27\u9633\u662d\u9664\u4e86\u5251\u62db\u7cbe\u5999\u4e4b\u5916\uff0c\u5185\u529b\u4e5f\u5728\u4e24\u4eba\u4e4b\u4e0a\uff0c\u968f\u624b\u6325\u5251\uff0c\u81ea\u6210\u6587\u7ae0\uff0c\u4e00\u4e0a\u624b\uff0c\u4e24\u4eba\u5c31\u88ab\u8feb\u843d\u4e0b\u98ce\u3002 \u3000\u3000\u4f46\u795e\u3001\u9b3c\u53cc\u5200\uff0c\u88ab\u8a89\u4e3a\u4e00\u4ee3\u5200\u6cd5\u540d\u5bb6\uff0c\u4ea6\u975e\u5c0f\u53ef\u3002 \u3000\u3000\u4e24\u4eba\u4ea4\u6362\u4e86\u4e00\u4e2a\u773c\u8272\uff0c\u7a81\u7136\u6539\u53d8\u4e86\u6253\u6cd5\uff0c\u4e0d\u518d\u548c\u6b27\u9633\u662d\u62a2\u4e89\u5148\u673a\uff0c\u53cc\u5200\u6539\u91c7\u5b88\u52bf\uff0c\u76f8\u4e92\u652f\u63f4\u3002 \u3000\u3000\u6b27\u9633\u662d\u5251\u62db\u51cc\u5386\uff0c\u7740\u7740\u903c\u8fdb\uff0c\u5251\u82b1\u9519\u843d\uff0c\u5316\u51fa\u4e07\u9053\u5bd2\u5149\uff0c\u7d27\u7d27\u5708\u5b9a\u4e24\u4eba\u3002 \u3000\u3000\u4f46\u795e\u3001\u9b3c\u53cc\u5200\uff0c\u5b88\u7684\u7d27\u4e25\uff0c\u53cc\u5200\u5e7b\u51fa\u4e00\u7247\u5200\u5e55\uff0c\u62a4\u4f4f\u5168\u8eab\uff0c\u5251\u62db\u867d\u7136\u7ef5\u5bc6\uff0c\u4f46\u4e00\u65f6\u4e4b\u95f4\uff0c\u5374\u4e5f\u65e0\u6cd5\u7a81\u7834\u5200\u7f51\u3002 \u3000\u3000\u4e03\u5b9d\u5927\u5e08\u7ecf\u8fc7\u4e00\u9635\u8c03\u606f\uff0c\u5143\u6c14\u6062\u590d\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u795e\u3001\u9b3c\u53cc\u5200\u679c\u7136\u662f\u5978\u6ed1\u7684\u5f88\uff0c\u4ed6\u4eec\u4ee5\u5b88\u4ee3\u653b\uff0c\u51c6\u5907\u4e45\u6218\uff0c\u4ee5\u6d88\u8017\u6b27\u9633\u5144\u7684\u5185\u529b\u3002\u201d \u3000\u3000\u6c5f\u67ab\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4ed6\u4eec\u4f4e\u4f30\u4e86\u6b27\u9633\u5e08\u5144\u7684\u5251\u6cd5\uff0c\u4ed6\u7684\u6740\u7740\uff0c\u4e5f\u672a\u51fa\u624b\uff0c\u6b63\u7b49\u5f85\u9002\u5f53\u7684\u673a\u4f1a\uff0c\u4f5c\u81f4\u547d\u7684\u4e00\u51fb\uff0c\u5012\u662f\u4f60\uff0c\u53ef\u771f\u662f\u5185\u529b\u4e0d\u7ee7\u5417\uff1f\u201d \u3000\u3000\u8a00\u4e0b\u4e4b\u610f\uff0c\u9690\u9690\u8bd8\u95ee\uff0c\u5927\u548c\u5c1a\u662f\u5426\u53c8\u5728\u9690\u85cf\u771f\u6b63\u5b9e\u529b\u3002 \u3000\u3000\u201c\u548c\u5c1a\u8fd8\u6709\u518d\u6218\u4e4b\u80fd\uff0c\u53ea\u4e0d\u8fc7\u9700\u8981\u51e0\u53e3\u5927\u5598\u6c14\u8c03\u6574\u4f53\u80fd\uff0c\u54ea\u4f1a\u7ed9\u654c\u4eba\u4e00\u79cd\u7814\u5224\u7684\u8baf\u606f\uff0c\u9002\u65f6\u800c\u9000\uff0c\u8ba9\u4ed6\u4eec\u83ab\u6d4b\u9ad8\u6df1\u3002\u201d \u3000\u3000\u7f57\u5170\u5168\u5fc3\u6ce8\u89c6\u7740\u4e08\u592b\u548c\u795e\u3001\u9b3c\u53cc\u5200\u7684\u6076\u6218\uff0c\u5168\u795e\u8d2f\u6ce8\uff0c\u6839\u672c\u6ca1\u6709\u7559\u610f\u4e24\u4eba\u7684\u8c08\u8bdd\u3002 \u3000\u3000\u53ea\u542c\u5f20\u56db\u59d1\u7684\u58f0\u97f3\uff0c\u4f20\u4e86\u8fc7\u6765\uff0c\u9053\uff1a\u201c\u5982\u679c\u5929\u95e8\u90a3\u6279\u9ad8\u624b\uff0c\u4e0d\u52a0\u8fdb\u6765\uff0c\u770b\u6837\u5b50\uff0c\u4f60\u4eec\u53ef\u80fd\u5df2\u5177\u6709\u5e94\u4ed8\u5f3a\u654c\u7684\u80fd\u529b\uff0c\u5012\u662f\u6211\u4eec\u56db\u4e2a\u4eba\uff0c\u767d\u767d\u7684\u7a9d\u5728\u96ea\u4e2d\uff0c\u5fcd\u53d7\u4e86\u534a\u591c\u4e00\u65e5\u7684\u96ea\u5bd2\u4fb5\u8feb\u4e4b\u82e6\u3002\u201d \u3000\u3000\u201c\u5929\u9a6c\u5802\u4e00\u80a1\u529b\u91cf\uff0c\u4f3c\u662f\u9510\u6c14\u5df2\u632b\uff0c\u5929\u865a\u5b50\u88ab\u548c\u5c1a\u4e00\u62f3\u6253\u6b7b\uff0c\u4e07\u65b9\u80c6\u6c14\u5df2\u5bd2\uff0c\u8fd9\u6279\u4eba\u624b\u5df2\u65e0\u518d\u6218\u4e4b\u52c7\uff0c\u5982\u679c\u6b27\u9633\u662d\u80fd\u4e00\u4e3e\u51fb\u8d25\u795e\u3001\u9b3c\u53cc\u5200\uff0c\u5185\u5bab\u4eba\u9a6c\uff0c\u4ea6\u96be\u518d\u6709\u4f5c\u4e3a\uff0c\u53ea\u4e0d\u77e5\u4ed6\u4eec\u662f\u5426\u8fd8\u6709\u4f0f\u5175\u51fa\u6765\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u4e5f\u4ee5\u4f20\u97f3\u4e4b\u672f\u56de\u7b54\uff0c\u5927\u548c\u5c1a\u867d\u7136\u7ad9\u5728\u8eab\u4fa7\uff0c\u5374\u4e5f\u65e0\u6cd5\u542c\u5230\u3002 \u3000\u3000\u201c\u6211\u770b\u5230\u4e86\u5927\u548c\u5c1a\u7684\u5a01\u98ce\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u4ed6\u6df1\u85cf\u4e0d\u9732\uff0c\u8fde\u6211\u4e5f\u88ab\u4ed6\u7792\u4f4f\u4e86\uff0c\u6211\u77e5\u9053\u4ed6\u9690\u6280\u626e\u61f5\uff0c\u4f46\u5374\u672a\u6599\u5230\u4ed6\u6210\u5c31\u4e4b\u9ad8\uff0c\u8fde\u65e0\u76f8\u5927\u5e08\u7684\u770b\u5bb6\u672c\u9886\u4e94\u798f\u7167\u547d\u5708\uff0c\u4e5f\u88ab\u4ed6\u5b66\u4f1a\u4e86\uff0c\u5509\uff01\u8eab\u8d1f\u7edd\u6280\uff0c\u5374\u80fd\u4efb\u4eba\u6fc0\u8bbd\u3001\u8f7b\u85d0\uff0c\u4ed8\u4e4b\u4e00\u7b11\uff0c\u5355\u662f\u8fd9\u4e00\u4efd\u5fcd\u8010\u7684\u5de5\u592b\uff0c\u5c31\u975e\u5e38\u4eba\u80fd\u591f\u5b66\u5f97\u3002\u201d \u3000\u3000\u201c\u6211\u4e00\u76f4\u8ba4\u4e3a\u52bf\u5355\u529b\u5b64\uff0c\u65e0\u6cd5\u62d2\u6297\u5f3a\u654c\uff0c\u5374\u4e0d\u6599\u4e09\u4f4d\u5e08\u957f\uff0c\u65e9\u5df2\u672a\u96e8\u7ef8\u7f2a\uff0c\u4e3a\u6211\u5b89\u6392\u4e86\u5982\u6b64\u5f3a\u5927\u7684\u52a9\u624b\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u8fd9\u79cd\u5173\u7231\u4e4b\u60c5\uff0c\u53eb\u5c0f\u5f1f\u500d\u751f\u6000\u5ff5\u4e86\uff01\u201d \u3000\u3000\u5927\u548c\u5c1a\u867d\u7136\u542c\u4e0d\u5230\u4e24\u4eba\u4ea4\u8c08\u7684\u5185\u5bb9\uff0c\u4f46\u89c1\u4ed6\u53e3\u9f7f\u542f\u52a8\uff0c\u5df2\u77e5\u4ed6\u548c\u5f20\u56db\u59d1\u4e92\u7528\u4f20\u97f3\u5165\u5bc6\u4e4b\u672f\uff0c\u8c08\u8bba\u654c\u60c5\u3002 \u3000\u3000\u76ee\u5149\u8f6c\u52a8\uff0c\u56db\u4e0b\u73af\u987e\uff0c\u7adf\u662f\u77a7\u4e0d\u51fa\u5f20\u56db\u59d1\u9690\u8eab\u4e4b\u5904\uff0c\u5fc3\u4e2d\u6697\u6697\u4f69\u670d\uff0c\u5f20\u59d1\u5a18\u4e94\u9041\u5947\u672f\uff0c\u679c\u662f\u4eba\u95f4\u5947\u6280\u3002 \u3000\u3000\u201c\u5927\u548c\u5c1a\u5df2\u7ecf\u53d1\u89c9\u4e86\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u654c\u4eba\u8fd8\u6709\u4f0f\u5175\u672a\u51fa\uff0c\u53ef\u80fd\u662f\u5929\u9a6c\u5802\u4e2d\u9ad8\u624b\uff0c\u4e0d\u8fc7\uff0c\u6b27\u9633\u662d\u7684\u4e94\u4e2a\u5f1f\u5b50\uff0c\u4e5f\u9690\u4e8e\u4e8c\u91cc\u4e4b\u5916\u4e00\u5ea7\u519c\u820d\u7b49\u5019\u53ec\u5524\uff0c\u96ea\u3001\u971c\u3001\u6885\u82b1\uff0c\u90fd\u85cf\u5728\u5de6\u8fd1\u96ea\u4e2d\uff0c\u53ef\u601c\u4e09\u4e2a\u5a07\u6ef4\u7684\u5c0f\u7f8e\u5973\uff0c\u5fcd\u53d7\u4e86\u8fd1\u5341\u4e2a\u65f6\u8fb0\u7684\u96ea\u5bd2\u8feb\u4f53\u4e4b\u82e6\u3002\u2026\u2026\u201d \u3000\u3000\u201c\u4f1a\u4e0d\u4f1a\u51bb\u574f\u5979\u4eec\uff1f\u201d\u6c5f\u67ab\u65e0\u9650\u5173\u6000\u7684\u95ee\u9053\u3002 \u3000\u3000\u201c\u653e\u5fc3\u5427\uff01\u82e6\u662f\u82e6\u4e00\u70b9\uff0c\u4f46\u8fd8\u4e0d\u4f1a\u51bb\u574f\u5979\u4eec\uff0c\u5927\u59d0\u7684\u8c82\u76ae\u8863\u5957\uff0c\u53ef\u4ee5\u4f7f\u8986\u5728\u5979\u8eab\u4e0a\u7684\u79ef\u96ea\u4e0d\u5316\uff0c\u76f8\u6297\u9177\u5bd2\uff0c\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u5012\u662f\u4f60\u4eec\uff0c\u8981\u5c3d\u5feb\u7684\u640f\u6740\u4ed6\u4eec\u4e00\u4e9b\u9ad8\u624b\uff0c\u591c\u8272\u5df2\u4e34\uff0c\u771f\u6b63\u7684\u91cd\u5934\u620f\uff0c\u53ef\u80fd\u5c31\u8981\u4e0a\u573a\uff0c\u4e09\u5bab\u4e3b\u72e1\u733e\u591a\u667a\uff0c\u5fc5\u6709\u9b3c\u8ba1\u3002\u54b1\u4eec\u4ee5\u57cb\u4f0f\u5bf9\u6297\u4ed6\u4eec\u63f4\u624b\u4f0f\u5175\uff0c\u867d\u7136\u6280\u9ad8\u4e00\u7740\uff0c\u4f46\u4ed6\u4eec\u4eba\u624b\u4f17\u591a\uff0c\u6211\u4eec\u5229\u5728\u901f\u51b3\uff0c\u4e0d\u8ddf\u4f60\u591a\u8bf4\u4e86\uff0c\u5c0f\u60c5\u90ce\uff0c\u7528\u4e0d\u7740\u518d\u9690\u6280\u81ea\u73cd\u4e86\uff0c\u62ff\u771f\u672c\u9886\uff0c\u522b\u8981\u5927\u548c\u5c1a\u4e13\u7f8e\u4e8e\u524d\u3002\u201d \u3000\u3000\u6c5f\u67ab\u62ac\u5934\u56db\u987e\uff0c\u679c\u7136\uff0c\u591c\u5e55\u5df2\u5782\uff0c\u6b27\u9633\u662d\u548c\u795e\u3001\u9b3c\u53cc\u5200\u4e4b\u6218\uff0c\u5df2\u8fd1\u4e24\u767e\u591a\u56de\u5408\uff0c\u4ecd\u672a\u5206\u51fa\u80dc\u8d1f\uff0c\u6b63\u60f3\u6697\u52a9\u6b27\u9633\u662d\u4e00\u81c2\u4e4b\u529b\uff0c\u5ffd\u542c\u6b27\u9633\u662d\u957f\u5578\u4e00\u58f0\uff0c\u5251\u6c14\u653e\u6536\uff0c\u51dd\u6210\u4e00\u7ebf\uff0c\u6709\u5982\u5c16\u9525\u4e00\u822c\uff0c\u523a\u5165\u4e86\u6d51\u6210\u4e00\u4f53\u7684\u5200\u5149\u4e4b\u4e2d\u3002 \u3000\u3000\u4e00\u58f0\u51c4\u5389\u7684\u5c16\u53eb\uff0c\u9b3c\u5200\u7adf\u88ab\u4e00\u5251\u7a7f\u80f8\uff0c\u624b\u4e2d\u7a84\u5200\uff0c\u4ea6\u88ab\u9707\u98de\u51fa\u624b\u3002 \u3000\u3000\u597d\u9738\u9053\u7684\u4e00\u5251\u3002 \u3000\u3000\u4f46\u9b3c\u5200\u5e76\u6765\u540e\u9000\uff0c\u6446\u8131\u957f\u5251\uff0c\u5374\u4e00\u54ac\u7259\uff0c\u8eab\u5b50\u8eab\u524d\u51b2\u6765\uff0c\u957f\u5251\u900f\u80f8\u800c\u8fc7\uff0c\u4f46\u4ed6\u5374\u5df2\u6251\u8fd1\u4e86\u6b27\u9633\u662d\uff0c\u53cc\u624b\u524d\u63a8\uff0c\u4f3c\u662f\u53d1\u62f3\u51fb\u654c\uff0c\u4f46\u8896\u4e2d\u5374\u7a81\u7136\u5c04\u51fa\u4e24\u67c4\u5200\uff0c\u68ee\u68ee\u5bd2\u8292\uff0c\u4e5f\u523a\u5411\u4e86\u6b27\u9633\u662d\u7684\u524d\u80f8\u3002 \u3000\u3000\u8fd9\u7b49\u820d\u547d\u8fd8\u51fb\uff0c\u6697\u5200\u7a81\u51fa\uff0c\u672c\u662f\u6781\u96be\u9632\u5907\uff0c\u4f46\u7f57\u5170\u90a3\u53e5\u4ed6\u4eec\u8eab\u4e0a\u85cf\u5200\u65e0\u6570\u7684\u5631\u5490\uff0c\u4f7f\u6b27\u9633\u662d\u65e9\u751f\u6212\u5fc3\uff0c\u89c1\u9b3c\u5200\u4efb\u5251\u900f\u4f53\uff0c\u594b\u52c7\u6251\u4e0a\uff0c\u65e9\u5df2\u53f3\u817f\u62ac\u8d77\uff0c\u767b\u51fa\u4e00\u811a\u3002 \u3000\u3000\u4ed6\u4eba\u9ad8\u817f\u957f\uff0c\u8fd9\u4e00\u811a\u5148\u4e2d\u9b3c\u5200\u5c0f\u8179\uff0c\u9b3c\u5200\u7adf\u88ab\u8e6c\u7684\u5012\u98de\u51fa\u4e00\u4e08\u591a\u5916\uff0c\u6454\u5012\u96ea\u5730\u4e0a\uff0c\u4f46\u4ed6\u8896\u4e2d\u53cc\u5200\u7a81\u957f\uff0c\u7adf\u4e5f\u523a\u5165\u6b27\u9633\u662d\u7684\u524d\u80f8\u4e24\u5206\u4e4b\u6df1\uff0c\u76ae\u7834\u8840\u6d41\u3002 \u3000\u3000\u5c31\u662f\u90a3\u6beb\u5398\u4e4b\u5dee\uff0c\u751f\u6b7b\u6b8a\u9014\u3002 \u3000\u3000\u4f46\u795e\u5200\u5374\u5728\u8fd9\u7535\u5149\u77f3\u706b\u7684\u77ac\u95f4\uff0c\u7a84\u5200\u63b7\u51fa\uff0c\u98de\u523a\u54bd\u5589\u3002 \u3000\u3000\u6b27\u9633\u662d\u95ea\u907f\u4e0d\u53ca\uff0c\u4eba\u968f\u5200\u4ec6\uff0c\u5411\u540e\u5012\u53bb\u3002 \u3000\u3000\u5200\u63a0\u9f3b\u5c16\u800c\u8fc7\u3002 \u3000\u3000\u4f46\u795e\u5200\u771f\u6b63\u7684\u6740\u7740\uff0c\u5374\u5728\u53cc\u817f\u4e4b\u4e0a\uff0c\u98de\u8eab\u8dc3\u8d77\uff0c\u53cc\u811a\u8e0f\u5411\u6b27\u9633\u662d\u7684\u5c0f\u8179\u3002 \u3000\u3000\u4e56\u4e56\uff0c\u53cc\u817f\u7684\u88e4\u7ba1\u4e2d\uff0c\u7adf\u4e5f\u5f39\u51fa\u4e86\u4e24\u628a\u5c16\u5200\uff0c\u957f\u903e\u4e00\u5c3a\u3002 \u3000\u3000\u5f53\u771f\u662f\u5168\u8eab\u85cf\u5200\uff0c\u795e\u9b3c\u83ab\u6d4b\uff0c\u795e\u9b3c\u53cc\u5200\u4e4b\u540d\uff0c\u5927\u7ea6\u4e5f\u7531\u6b64\u800c\u6765\u4e86\uff0c\u5168\u8eab\u6709\u5200\u4e0d\u89c1\u5200\u3002 \u3000\u3000\u4f46\u6b27\u9633\u662d\u6536\u56de\u7684\u957f\u5251\uff0c\u4f3c\u662f\u65e9\u5df2\u7b49\u5728\u90a3\u91cc\uff0c\u4e0a\u7fd8\u7684\u5251\u5c16\uff0c\u66b4\u957f\u800c\u8d77\uff0c\u523a\u5165\u4e86\u795e\u5200\u7684\u5de6\u811a\u811a\u5fc3\uff0c\u5251\u4e0a\u4e3a\u9053\u5f3a\u5927\uff0c\u751f\u751f\u628a\u795e\u5200\u7ed9\u9707\u98de\u8d77\u6765\u3001\u4e00\u67c4\u957f\u5251\uff0c\u6cbf\u811a\u5fc3\u900f\u9aa8\u800c\u5165\uff0c\u76f4\u6ca1\u53ca\u5251\u67c4\u3002 \u3000\u3000\u539f\u6765\uff0c\u5251\u53e3\u7684\u5185\u529b\u5f3a\u5927\uff0c\u65e0\u575a\u4e0d\u6467\uff0c\u957f\u5251\u76f4\u5165\uff0c\u7adf\u7136\u672a\u504f\u51fa\u5927\u817f\u4e4b\u5916\u3002 \u3000\u3000\u795e\u5200\u4e5f\u88ab\u63a8\u98de\u8d77\u4e00\u4e08\u591a\u9ad8\uff0c\u8dcc\u6454\u5728\u96ea\u5730\u4e0a\u3002 \u3000\u3000\u957f\u5251\u7531\u811a\u5e95\u76f4\u5165\u80f8\u4e2d\uff0c\u52a0\u4e0a\u90a3\u91cd\u91cd\u4e00\u6454\uff0c\u771f\u6c14\u6da3\u6563\uff0c\u7acb\u523b\u6c14\u7edd\u3002 \u3000\u3000\u7f57\u5170\u60ca\u9b42\u521d\u5b9a\uff0c\u98de\u8dc3\u5728\u6b27\u9633\u662d\u7684\u8eab\u4fa7\uff0c\u9053\uff1a\u201c\u8001\u516c\uff0c\u4f60\u4f24\u52bf\u5982\u4f55\uff1f\u201d\u76ee\u5149\u5374\u76ef\u5728\u6b27\u9633\u662d\u524d\u80f8\u4f24\u5904\u3002 \u3000\u3000\u201c\u4e00\u70b9\u76ae\u8089\u4e4b\u4f24\u2026\u2026\u201d\u6b27\u9633\u662d\u633a\u8eab\u800c\u8d77\uff0c\u9053\uff1a\u201c\u603b\u7b97\u628a\u795e\u3001\u9b3c\u53cc\u5200\u7ed9\u89e3\u51b3\u4e86\uff0c\u53ea\u662f\u4e0d\u50cf\u5927\u548c\u5c1a\u90a3\u6837\u5e72\u8106\u5229\u843d\u3002\u2026\u2026\u201d \u3000\u3000\u201c\u8fd9\u662f\u4e24\u4ef6\u5b8c\u5168\u4e0d\u80fd\u76f8\u6bd4\u7684\u4e8b\uff0c\u2026\u2026\u201d\u5927\u548c\u5c1a\u9053\uff1a\u201c\u5341\u4e2a\u5929\u865a\u5b50\u52a0\u8d77\u6765\uff0c\u4e5f\u4e0d\u5982\u795e\u3001\u9b3c\u53cc\u5200\uff0c\u4ed6\u4eec\u662f\u771f\u6b63\u4e00\u4ee3\u5200\u6cd5\u5b97\u5e08\uff0c\u5355\u662f\u4ed6\u5168\u8eab\u85cf\u5200\u90a3\u4e00\u62db\uff0c\u5c31\u524d\u65e0\u53e4\u4eba\uff0c\u548c\u5c1a\u5b9e\u65e0\u628a\u63e1\u80fd\u80dc\u5f97\u4ed6\u4eec\u4e24\u4eba\u8054\u624b\uff1f\u201d \u3000\u3000\u201c\u7b97\u4e86\uff0c\u5927\u548c\u5c1a\uff0c\u6211\u53ef\u6ca1\u6709\u548c\u4f60\u6bd4\u9ad8\u4e0b\u7684\u610f\u601d\u2026\u2026\u201d\u6b27\u9633\u662d\u7b11\u9053\uff1a\u201c\u53ea\u4e0d\u8fc7\u662f\u4e00\u53e5\u611f\u6168\u4e4b\u8a00\u7f62\u4e86\uff0c\u8fd9\u795e\u3001\u9b3c\u53cc\u5200\uff0c\u5b9e\u5728\u4e0d\u597d\u5bf9\u4ed8\uff0c\u6211\u80fd\u640f\u6740\u4ed6\u4eec\uff0c\u5fc3\u4e2d\u8fd8\u771f\u6709\u70b9\u98d8\u98d8\u7136\u5462\uff01\u201d \u3000\u3000\u201c\u54fc\uff01\u4ee5\u540e\uff0c\u6700\u597d\u4e0d\u8981\u518d\u98d8\u4e86\u2026\u2026\u201d\u7f57\u5170\u5a07\u55d4\u9053\uff1a\u201c\u4f60\u98d8\u7684\u5feb\u6d3b\uff0c\u6211\u53ef\u662f\u63ea\u7740\u5fc3\u5728\u7b49\u5f85\uff0c\u96be\u9053\u4f60\u60f3\u6211\u4f5c\u2026\u2026\u201d \u3000\u3000\u5927\u6982\u662f\u60f3\u8bf4\u4f5c\u5be1\u5987\uff0c\u8bdd\u5230\u53e3\u8fb9\uff0c\u53c8\u541e\u4e86\u56de\u53bb\u3002 \u3000\u3000\u6b27\u9633\u662d\u8c6a\u58ee\u7684\u8bf4\u9053\uff1a\u201c\u74e6\u7f50\u4e0d\u79bb\u4e95\u53e3\u7834\uff0c\u5c06\u519b\u96be\u514d\u9635\u4e0a\u4ea1\uff0c\u6211\u4eec\u65e2\u5165\u4e86\u6c5f\u6e56\uff0c\u751f\u6b7b\u4e8b\u5c31\u8be5\u770b\u5f00\uff01\u201d \u3000\u3000\u201c\u4f60\u6b7b\u4e86\uff0c\u6211\u8fd8\u80fd\u6d3b\u5f97\u4e0b\u53bb\u5417\uff1f\u201d \u3000\u3000\u7f57\u5170\u4f4e\u58f0\u503e\u8bc9\uff0c\u70ab\u7136\u6b32\u6ce3\u3002 \u3000\u3000\u8fd9\u5927\u6982\u4e5f\u662f\u5929\u72d0\u6280\u827a\u4e4b\u4e00\u5427\uff01 \u3000\u3000\u6b27\u9633\u662d\u5fc3\u4e2d\u90a3\u4efd\u611f\u52a8\u3001\u5feb\u4e50\uff0c\u7b80\u76f4\u65e0\u6cd5\u63cf\u8ff0\uff0c\u6068\u4e0d\u5f97\u4e00\u62b1\u8fc7\u7f57\u5170\uff0c\u53eb\u5b83\u5343\u767e\u58f0\u8001\u5a46\u3001\u7231\u59bb\u3002 \u3000\u3000\u4f46\u4ed6\u54ac\u7740\u7259\u5fcd\u4e86\u4e0b\u53bb\uff0c\u8138\u4e0a\u5374\u7b11\u610f\u5145\u76c8\uff0c\u76ee\u4e2d\u795e\u5149\u707f\u7136\u3002 \u3000\u3000\u201c\u90a3\u4e00\u5251\u7531\u811a\u5fc3\u900f\u5165\u524d\u80f8\uff0c\u7b80\u76f4\u662f\u795e\u6765\u4e4b\u7b14\u2026\u2026\u201d\u6c5f\u67ab\u4f4e\u58f0\u8d5e\u9053\uff1a\u201c\u5c0f\u5f1f\u2026\u2026\u665a\u8fdb\u81ea\u53f9\u5f17\u5982\uff01\u201d \u3000\u3000\u5bf9\u6b27\u9633\u662d\u548c\u7f57\u5170\u7684\u79f0\u547c\uff0c\u6c5f\u67ab\u662f\u4f24\u900f\u4e86\u8111\u7b4b\uff0c\u65e2\u6709\u540c\u95e8\u5e08\u5144\u3001\u5e08\u59ca\u7684\u4e8b\u5b9e\uff0c\u53c8\u6709\u7fc1\u5a7f\u53e3\u76df\u7684\u7ea6\u5b9a\uff0c\u4e5f\u53ea\u6709\u914c\u91cf\u5f53\u65f6\u7684\u60c5\u666f\uff0c\u9002\u5b9c\u7684\u79f0\u547c\u4e86\u3002 \u3000\u3000\u201c\u4f60\u4e0d\u7528\u5fc3\u5b58\u987e\u5fcc\u2026\u2026\u201d\u6b27\u9633\u662d\u4f4e\u7b11\u9053\uff1a\u201c\u4e2b\u5934\u662f\u4e2b\u5934\uff0c\u6211\u662f\u6211\uff0c\u4f60\u9ad8\u5174\u600e\u4e48\u79f0\u547c\u90fd\u597d\uff0c\u8fd9\u4e5f\u771f\u96be\u4e3a\u4f60\u4e86\uff0c\u5c31\u9002\u60c5\u9002\u666f\u5730\u53eb\u5427\uff01\u81f3\u4e8e\u90a3\u795e\u6765\u4e00\u5251\uff0c\u53ef\u662f\u91d1\u4e39\u4e66\u751f\u7684\u4f20\u6388\uff0c\u4e13\u95e8\u5bf9\u4ed8\u795e\u3001\u9b3c\u53cc\u5200\u7684\u4e00\u62db\uff0c\u4e0d\u662f\u6211\u81ea\u5df1\u5212\u51fa\u6765\u7684\uff0c\u90a3\u662f\u7531\u7ecf\u9a8c\u4e2d\u82e6\u601d\u800c\u5f97\u3002\u201d \u3000\u3000\u4f46\u89c1\u4eba\u5f71\u95ea\u52a8\uff0c\u80e1\u840d\u5feb\u5982\u75be\u77e2\u4e00\u822c\u7684\u5c04\u51fa\uff0c\u62d4\u51fa\u795e\u5200\u811a\u4e0a\u7684\u957f\u5251\uff0c\u9000\u4e86\u56de\u6765\uff0c\u4ea4\u7ed9\u6b27\u9633\u662d\u3002 \u3000\u3000\u6b27\u9633\u662d\u5fae\u7b11\u63a5\u5251\uff0c\u9894\u9996\u793a\u8c22\u3002 \u3000\u3000\u4e5f\u8bb8\u8fd9\u4e24\u9635\u640f\u6740\u7684\u795e\u52c7\u3001\u51cc\u5386\uff0c\u628a\u5bf9\u65b9\u7ed9\u9a87\u4f4f\u4e86\uff0c\u6ca1\u4eba\u6562\u518d\u4e3b\u52a8\u6297\u6218\uff0c\u53cc\u65b9\u6682\u65f6\u5f62\u6210\u4e86\u6c89\u9ed8\u7684\u5bf9\u5cd9\u3002 \u3000\u3000\u4f46\u95fb\u8863\u8882\u98d8\u98ce\uff0c\u4e94\u4e2a\u4f69\u5251\u5c11\u5e74\uff0c\u5728\u591c\u8272\u4e2d\u98de\u5954\u800c\u6765\uff0c\u4e94\u4e2a\u4e8c\u5341\u5de6\u53f3\u7684\u5e74\u8f7b\u4eba\uff0c\u4e00\u8272\u6709\u7070\u5e03\u88e4\u8884\uff0c\u5bf9\u7740\u6b27\u9633\u662d\u8eac\u8eab\u4e00\u793c\uff0c\u8083\u7acb\u4e00\u4fa7\uff0c\u4e5f\u4e0d\u7406\u4f1a\u6c5f\u67ab\u3001\u7f57\u5170\u7b49\u4eba\u3002 \u3000\u3000\u201c\u522b\u602a\u4ed6\u4eec\uff0c\u4ed6\u4eec\u5e7d\u5c45\u6df1\u5c71\u5341\u51e0\u5e74\uff0c\u8fd9\u662f\u7b2c\u4e00\u6b21\u4e0b\u5c71\u2026\u2026\u201d\u4e03\u5b9d\u5927\u5e08\u7b11\u9053\uff1a\u201c\u4ed6\u4eec\u7eaf\u6734\u3001\u6728\u8bb7\uff0c\u4e0d\u4f1a\u548c\u751f\u4eba\u62db\u547c\u3002\u201d \u3000\u3000\u4e94\u4e2a\u7070\u8863\u5c11\u5e74\uff0c\u542c\u5230\u58f0\u97f3\uff0c\u4e00\u9053\u76ee\u5149\u4e00\u9f50\u6295\u6ce8\u5728\u4e03\u5b9d\u4eba\u5e08\u7684\u8138\u4e0a\uff0c\u6253\u91cf\u4e86\u826f\u4e45\uff0c\u624d\u4e00\u9f50\u8dea\u4e86\u4e0b\u53bb\uff0c\u9053\uff1a\u201c\u4f60\u662f\u548c\u5c1a\u4f2f\u4f2f\uff0c\u6211\u4eec\u597d\u60f3\u5ff5\u4f60\u3002\u201d \u3000\u3000\u201c\u662f\u554a\uff01\u603b\u6709\u5341\u5e74\u4e0d\u89c1\u9762\u4e86\uff0c\u6211\u4e5f\u5f88\u60f3\u5ff5\u4f60\u4eec\uff0c\u5feb\u4e9b\u8d77\u6765\u2026\u2026\u201d \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u7b11\u9053\uff1a\u201c\u7b49\u4e00\u4e0b\u8fd8\u8981\u4f60\u4eec\u5e2e\u5e08\u7236\u548c\u4eba\u6253\u67b6\u5462\uff01\u201d \u3000\u3000\u4e94\u4e2a\u4eba\u4e00\u9f50\u7ad9\u4e86\u8d77\u6765\uff0c\u5927\u6982\u662f\u4e0d\u77e5\u9053\u8be5\u518d\u8bf4\u4e9b\u4ec0\u4e48\uff1f\u5fae\u5fae\u4e00\u7b11\uff0c\u606d\u7acb\u6b27\u9633\u662d\u7684\u65c1\u4fa7\uff0c\u8138\u4e0a\u5374\u662f\u4e00\u7247\u8dc3\u8dc3\u6b32\u8bd5\u7684\u795e\u60c5\u3002 \u3000\u3000\u6b64\u65f6\u60c5\u666f\uff0c\u6b27\u9633\u662d\u4e5f\u65e0\u6687\u66ff\u4ed6\u4eec\u5f15\u4ecb\u5404\u4eba\uff0c\u5bf9\u654c\u8981\u7d27\uff0c\u4f4e\u58f0\u95ee\u9053\uff1a\u201c\u5251\u9635\u7ec3\u719f\u4e86\uff1f\u201d \u3000\u3000\u4e94\u4eba\u9f50\u58f0\u5e94\u9053\uff1a\u201c\u7ec3\u719f\u4e86\uff01\u201d\u5530\u7684\u4e00\u58f0\u62bd\u51fa\u4e0a\u957f\u5251\uff0c\u5c31\u8981\u6446\u5f00\u5251\u9635\u3002 \u3000\u3000\u6b27\u9633\u662d\u4e00\u76b1\u53cc\u7709\uff0c\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u7b49\u6211\u4ee4\u8c15\uff0c\u518d\u51fa\u624b\u3002\u201d \u3000\u3000\u4e94\u4eba\u8fd8\u5251\u5165\u9798\uff0c\u53c8\u662f\u5782\u624b\u8083\u7acb\u3002 \u3000\u3000\u201c\u539f\u6765\uff0c\u8fd8\u6709\u4f0f\u5175\uff0c\u5e94\u8be5\u4e0d\u6b62\u8fd9\u4e94\u4e2a\u5e74\u8f7b\u7684\u5251\u624b\u5427\uff01\u201d \u3000\u3000\u201c\u8fd9\u5c31\u4e0d\u52b3\u4e09\u5bab\u4e3b\u8d39\u5fc3\u4e86\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u8be5\u4ed6\u4eec\u51fa\u73b0\u7684\u65f6\u4faf\u3002 \u3000\u3000\u4ed6\u4eec\u81ea\u4f1a\u73b0\u8eab\uff0c\u7ea6\u6211\u4eec\u7533\u65f6\u4f1a\u9635\uff0c\u4f3c\u662f\u5df2\u51c6\u5907\u6311\u706f\u591c\u6218\u4e86\uff1f\u201d \u3000\u3000\u201c\u4e0d\uff01\u65e0\u706f\u53ef\u6311\u2026\u2026\u201d\u4e09\u5bab\u4e3b\u9053\uff1a\u201c\u5c31\u5728\u8fd9\u591c\u5e55\u7b3c\u7f69\u4e4b\u4e0b\uff0c\u4e00\u51b3\u80dc\u8d1f\u4e86\uff01\u201d \u3000\u3000\u201c\u6253\u591c\u6218\u5427\uff0c\u2026\u2026\u201d\u7f57\u5170\u62bd\u51fa\u957f\u5251\u4e00\u6446\uff0c\u9053\uff1a\u201c\u4e09\u5bab\u4e3b\uff0c\u5c31\u7531\u54b1\u4eec\u5148\u5f00\u59cb\u5982\u4f55\uff1f\u201d\u4e09\u5bab\u4e3b\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u597d\uff01\u2026\u2026\u201d\u7f13\u6b65\u8d70\u4e86\u51fa\u6765\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u591c\u8272\u6e10\u6d53\uff0c\u5929\u4e0a\u4e00\u70b9\u661f\u5149\uff0c\u4e5f\u88ab\u4e00\u7247\u4e4c\u4e91\u906e\u4f4f\uff0c\u5730\u4e0a\u867d\u6709\u7691\u7691\u79ef\u96ea\uff0c\u4e5f\u65e0\u6cd5\u6620\u5149\u751f\u8f89\uff0c\u89c6\u529b\u53ea\u80fd\u89c1\u6570\u5c3a\u5916\u7684\u666f\u7269\uff0c\u867d\u6709\u7cbe\u6df1\u5185\u529f\uff0c\u5f02\u4e8e\u5e38\u4eba\u6709\u7684\u76ee\u529b\uff0c\u4e5f\u6709\u7740\u4eba\u7269\u6a21\u7cca\u4e4b\u611f\u3002 \u3000\u3000\u8fd9\u7b49\u60c5\u5f62\u4e4b\u4e0b\uff0c\u5c31\u7b97\u662f\u4e00\u6d41\u9ad8\u624b\uff0c\u4e5f\u65e0\u6cd5\u9632\u5907\u5230\u6697\u5668\u7684\u88ad\u51fb\u3002 \u3000\u3000\u591c\uff0c\u7ed9\u4eba\u4e00\u79cd\u51c4\u8ff7\u4e4b\u7f8e\uff0c\u4e5f\u7ed9\u4eba\u4e00\u79cd\u53ef\u6015\u4e4b\u611f\u3002 \u3000\u3000\u4e24\u4e2a\u7f8e\u4e3d\u7684\u5973\u4eba\uff0c\u63d0\u7740\u68ee\u5bd2\u7684\u5175\u5203\uff0c\u76f8\u5bf9\u800c\u884c\uff0c\u53cc\u65b9\u5df2\u4e92\u76f8\u77e5\u9053\u4e00\u4e9b\u865a\u5b9e\uff0c\u5f7c\u6b64\u5fc3\u5b58\u7591\u6015\uff0c\u8c01\u4e5f\u4e0d\u6562\u5927\u610f\uff0c\u6b65\u5b50\u79fb\u52a8\uff0c\u5982\u5c65\u8584\u51b0\u624b\u4e2d\u4e4b\u5251\uff0c\u4e5f\u7f13\u7f13\u62ac\u8d77\uff0c\u6307\u5411\u5bf9\u65b9\uff0c\u51c6\u5907\u5e94\u53d8\u3002 \u3000\u3000\u201c\u4e0d\u5bf9\u5440\uff01\u2026\u2026\u201d\u5927\u548c\u5c1a\u7a81\u7136\u8bf4\u9053\uff1a\u201c\u5982\u6b64\u591c\u6218\uff0c\u662f\u4e09\u5bab\u4e3b\u8bbe\u8ba1\u5b9a\u6709\u9634\u8c0b\uff0c\u4e0d\u53ef\u4e0d\u9632\u3002\u201d \u3000\u3000\u8fd9\u4e00\u4e0b\uff0c\u8f6e\u5230\u6b27\u9633\u662d\u62c5\u5fc3\u4e86\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u7f57\u5170\uff0c\u9000\u56de\u6765\uff01\u201d \u3000\u3000\u7537\u3001\u5973\u4e0d\u540c\u4e4b\u5904\uff0c\u5c31\u660e\u786e\u7684\u663e\u793a\u4e86\u51fa\u6765\u3002 \u3000\u3000\u6b27\u9633\u660e\u8981\u9762\u5b50\uff0c\u5343\u65b9\u767e\u8ba1\u628a\u7f57\u5170\u529d\u9000\u56de\u53bb\u3002 \u3000\u3000\u7f57\u5170\u4e5f\u8981\u9762\u5b50\uff0c\u4f46\u5374\u5f88\u6e29\u67d4\u7684\u9000\u4e86\u56de\u6765\u3002 \u3000\u3000\u4e09\u5bab\u4e3b\u6ca1\u6709\u4e58\u673a\u62a2\u653b\uff0c\u751a\u81f3\u505c\u4e0b\u6765\uff0c\u7ed9\u7f57\u5170\u4e00\u79cd\u5b89\u5168\u540e\u9000\u7684\u611f\u89c9\u3002 \u3000\u3000\u9000\u662f\u9000\u56de\u6765\u4e86\uff0c\u4f46\u8138\u8272\u4e0d\u600e\u4e48\u597d\u770b\uff0c\u51b7\u51b7\u7684\u8bf4\u9053\uff1a\u201c\u8001\u516c\uff01\u6709\u4ec0\u4e48\u5429\u5490\uff1f\u4f60\u8981\u9762\u5b50\uff0c\u4e5f\u8981\u91cc\u5b50\uff0c\u73b0\u5728\uff0c\u6ee1\u8db3\u4e86\u5427\uff01\u8001\u5a46\u54ac\u7259\u5fcd\u53d7\u7684\u597d\u80dc\u4e4b\u5fc3\u3002\u5374\u53c8\u6e29\u67d4\u7684\u542c\u4f60\u7684\u4ee4\u8c15\uff01\u201d \u3000\u3000\u5927\u548c\u5c1a\u786c\u7740\u5934\u76ae\u625b\u4e86\u4e0b\u6765\uff0c\u7b11\u9053\uff1a\u201c\u5ac2\u592b\u4eba\uff0c\u548c\u5c1a\u60f3\u5230\u4e86\u4e00\u4ef6\u4e8b\uff0c\u53ea\u597d\u8ba9\u6b27\u9633\u5144\u628a\u4f60\u8bf7\u56de\u6765\u3002\u201d \u3000\u3000\u660e\u77e5\u548c\u5c1a\u80cc\u9ed1\u9505\uff0c\u7f57\u5170\u5374\u653e\u8d77\u5201\u6765\uff0c\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u5927\u548c\u5c1a\u8fde\u80dc\u4e24\u9635\uff0c\u90fd\u662f\u4e00\u62db\u6bd9\u654c\uff0c\u8fde\u53d6\u4e09\u4e2a\uff0c\u6211\u8001\u516c\u4e5f\u4e00\u5251\u65a9\u4e86\u795e\u3001\u9b3c\u53cc\u5200\uff0c\u6211\u5c31\u4e0d\u80fd\u548c\u4e09\u5bab\u4e3b\u4e00\u51b3\u5b58\u4ea1\u5417\uff1f\u8fd9\u5a01\u98ce\u6740\u6c14\uff0c\u90fd\u8ba9\u4f60\u4eec\u7537\u4eba\u5360\u5c3d\uff0c\u6211\u4eec\u5973\u4eba\uff0c\u53ea\u914d\u7a9d\u5728\u5bb6\u91cc\u770b\u5b69\u5b50\u4e86\uff01\u201d \u3000\u3000\u201c\u5ac2\u592b\u4eba\u2026\u2026\u201d\u5927\u548c\u5c1a\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u8fd9\u6478\u9ed1\u591c\u6218\uff0c\u5927\u80cc\u5e38\u60c5\uff0c\u662f\u51fa\u81ea\u4e09\u5bab\u4e3b\u7684\u7b56\u5212\uff0c\u548c\u5c1a\u4e0d\u77e5\u9053\u5979\u9634\u8c0b\u4f55\u5728\uff1f\u4f46\u5fc5\u6709\u9634\u8c0b\uff0c\u5f53\u53ef\u65ad\u8a00\uff0c\u6c5f\u8001\u5f1f\u4e5f\u89c9\u7740\u4e0d\u53ef\u5927\u610f\uff0c\u548c\u5c1a\u5c31\u6258\u8bf7\u6b27\u9633\u5144\u62db\u547c\u4f60\u4e00\u58f0\uff0c\u53ef\u522b\u6709\u6240\u8bef\u4f1a\uff0c\u5ac2\u592b\u4eba\u5728\u6597\u5ba4\u4e4b\u4e2d\uff0c\u4e00\u5251\u6597\u53cc\u5200\uff0c\u8fd9\u5a01\u98ce\u6740\u6c14\uff0c\u7edd\u4e0d\u4f1a\u8f93\u7ed9\u6b27\u9633\u5144\u5462\uff01\u201d \u3000\u3000\u660e\u77e5\u662f\u9876\u9ad8\u5e3d\u5b50\uff0c\u7f57\u5170\u8fd8\u662f\u5fcd\u4e0d\u4f4f\u7b11\u4e86\u3002 \u3000\u3000\u518d\u770b\u6b27\u9633\u90a3\u4efd\u5c34\u5c2c\uff0c\u4e5f\u5c31\u987a\u98ce\u6536\u5e06\uff0c\u8bf4\uff1a\u201c\u8fd9\u4e00\u4ed7\u603b\u8981\u6253\u5427\uff01\u591a\u8c22\u5173\u6000\uff0c\u6211\u4f1a\u5c0f\u5fc3\u5c31\u662f\u3002\u201d \u3000\u3000\u201c\u5bf9\uff01\u9996\u6c42\u81ea\u4fdd\uff0c\u6b21\u6c42\u514b\u654c\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u7f57\u5170\uff0c\u4f60\u5982\u53d7\u4f24\u5bb3\uff0c\u6211\u4eec\u5927\u5bb6\u90fd\u4f1a\u5fc3\u4e2d\u4e0d\u5b89\u3002\u201d \u3000\u3000\u201c\u8c22\u8c22\u4f60\u4e86\uff0c\u5c0f\u5e08\u5f1f\uff0c\u6211\u4f1a\u8c28\u614e\u81ea\u4fdd\u2026\u2026\u201d\u7f57\u5170\u9053\uff1a\u201c\u4e0d\u4f1a\u8ba9\u4f60\u4f24\u5fc3\u3002\u201d\u8f6c\u8eab\u5411\u4e09\u5bab\u4e3b\u8fce\u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u8fd9\u4e00\u5bf9\u5b9d\u8d1d\u592b\u59bb\uff0c\u867d\u662f\u8eab\u6000\u7edd\u6280\uff0c\u4f46\u5374\u5e38\u5e74\u5c71\u5c45\uff0c\u5c11\u5728\u6c5f\u6e56\u8d70\u52a8\u4ecd\u4fdd\u6709\u4e86\u4e00\u79cd\u8d64\u5b50\u5fc3\u60c5\uff0c\u5927\u4e8b\u4e0d\u8ba1\u8f83\uff0c\u53e3\u89d2\u5c0f\u4e8b\u5374\u4e0d\u80af\u670d\u8f93\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u4ed6\u4eec\u592b\u5987\u4e5f\u5c31\u4ee5\u4e89\u5435\u4f34\u5634\uff0c\u4ee5\u6392\u9063\u5c71\u4e2d\u7684\u5bc2\u5bde\u5c81\u6708\uff0c\u7779\u7779\u5c0f\u6c14\uff0c\u65e9\u6210\u79ef\u4e60\u3002 \u3000\u3000\u4e09\u5bab\u4e3b\u795e\u8272\u5e73\u9759\uff0c\u4f3c\u662f\u65e9\u5df2\u80f8\u6709\u6210\u7af9\uff0c\u624b\u4e2d\u5b9d\u5251\u4e3e\u8d77\uff0c\u62c9\u5f00\u67b6\u5f0f\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u5e08\u5144\u5e08\u59b9\uff0c\u592b\u59bb\u4fe9\u53e3\uff0c\u5546\u91cf\u597d\u4e86\u5427\uff1f\u201d \u3000\u3000\u7f57\u5170\u8138\u4e0a\u4e00\u70ed\uff0c\u9053\uff1a\u201c\u8981\u4f60\u7b49\u5019\u4e86\uff0c\u8ba9\u4f60\u5148\u673a\uff01\u201d \u3000\u3000\u4e09\u5bab\u4e3b\u9053\uff1a\u201c\u5c0f\u59b9\u5c31\u4e0d\u5ba2\u6c14\u4e86\u3002\u201d \u3000\u3000\u957f\u5251\u4e00\u63a2\uff0c\u523a\u5411\u524d\u80f8\u3002 \u3000\u3000\u7f57\u5170\u6325\u5251\u67b6\u5f00\uff0c\u53cd\u51fb\u4e00\u5251\uff0c\u4e24\u4eba\u7acb\u5373\u5c55\u5f00\u6076\u6597\u3002 \u3000\u3000\u6c5f\u67ab\u4e00\u76b1\u7709\u5934\uff0c\u9053\uff1a\u201c\u548c\u5c1a\uff0c\u4e09\u5bab\u4e3b\u7684\u8010\u5fc3\u5ba2\u6c14\uff0c\u6709\u4e9b\u5f02\u5e38\uff0c\u4f60\u5c0f\u5fc3\u66ff\u7f57\u5170\u63a0\u9635\u3002\u201d \u3000\u3000\u5927\u548c\u5c1a\u70b9\u70b9\u5934\uff0c\u7f13\u7f13\u5411\u524d\u79fb\u52a8\u8eab\u8eaf\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u90a3\u4f4d\u4e00\u54c1\u5200\uff0c\u7a81\u7136\u5e26\u7740\u56db\u4e2a\u5f1f\u5b50\uff0c\u5411\u524d\u884c\u6765\uff0c\u540c\u65f6\u62bd\u51fa\u4f69\u5200\u3002 \u3000\u3000\u6c5f\u67ab\u4e3e\u6b65\u800c\u51fa\u51c6\u5907\u8fce\u654c\uff0c\u6b27\u9633\u662d\u5df2\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u8fd9\u4e00\u6279\u4eba\u4ea4\u7ed9\u6211\u3002\u201d\u4e3e\u624b\u4e00\u6325\uff0c\u4e94\u4e2a\u5f1f\u4e8e\u4f69\u5251\u51fa\u9798\uff0c\u968f\u7740\u6b27\u9633\u662d\u8fce\u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u201c\u5f53\u5fc3\u4ed6\u7684\u8131\u624b\u5200\u6cd5\uff0c\u80fd\u5728\u767e\u6b65\u4e4b\u5185\u53d6\u4eba\u9996\u7ea7\u2026\u2026\u201d\u6c5f\u67ab\u6ca1\u6709\u4e89\u6297\u5f3a\u654c\uff0c\u5374\u70b9\u51fa\u4e86\u4e00\u54c1\u5200\u7684\u7cbe\u5947\u5200\u6cd5\u3002 \u3000\u3000\u4ed6\u5bf9\u6b27\u9633\u662d\u5145\u6ee1\u4fe1\u5fc3\uff0c\u4e5f\u60f3\u7740\u770b\u770b\u4ed6\u8bad\u7ec3\u4e94\u4e2a\u5f1f\u5b50\u7684\u5251\u9635\u5a01\u529b\uff0c\u8fd9\u662f\u91d1\u4e39\u4e66\u751f\u91cd\u4f24\u4e4b\u540e\uff0c\u609f\u51fa\u7684\u5251\u964d\uff0c\u6c5f\u67ab\u542c\u8fc7\uff0c\u5374\u6ca1\u6709\u5b66\u8fc7\u3002 \u3000\u3000\u5201\u9e4f\u9053\uff1a\u201c\u8001\u9093\uff0c\u54b1\u4eec\u6050\u6015\u4e5f\u95f2\u4e0d\u4f4f\u4e86\uff0c\u51c6\u5907\u4e00\u4e0b\uff0c\u62fc\u8001\u547d\u5427\uff01\u201d \u3000\u3000\u201c\u770b\u5230\u5927\u548c\u5c1a\u548c\u6b27\u9633\u662d\u7684\u8eab\u624b\uff0c\u771f\u6709\u70b9\u81ea\u60ed\u5f62\u79fd\u2026\u2026\u201d\u9093\u98de\u9053\uff1a\u201c\u4e0d\u6562\u8f7b\u6613\u7684\u51fa\u624b\u4e86\uff01\u201d \u3000\u3000\u201c\u8001\u9093\u554a\uff01\u8fd9\u662f\u4ec0\u4e48\u65f6\u523b\u4e86\uff0c\u4f60\u8fd8\u662f\u8fd9\u4e48\u770b\u4e0d\u5f00\u5440\uff01\u2026\u2026\u201d\u5201\u9e4f\u9053\uff1a\u201c\u6211\u867d\u7136\u8fd8\u4e0d\u80fd\u592a\u4e86\u89e3\u4ed6\u4eec\uff0c\u4f46\u6211\u611f\u89c9\u5230\u4ed6\u4eec\u662f\u4e00\u4e2a\u80f8\u6000\u5927\u5fd7\u7684\u7ec4\u5408\u2026\u2026\u201d\u653e\u4f4e\u4e86\u58f0\u97f3\uff0c\u63a5\u9053\uff1a\u201c\u800c\u4e14\u662f\u6b66\u6797\u4e09\u5723\u64ad\u4e0b\u7684\u79cd\u5b50\uff0c\u4ed6\u4eec\u90fd\u597d\u50cf\u548c\u6b66\u6797\u4e09\u5723\u6709\u5173\uff0c\u4e5f\u90fd\u7ee7\u627f\u4e86\u6b66\u6797\u4e09\u5723\u7684\u6b66\u529f\u3002\u201d \u3000\u3000\u201c\u4e0d\u9519\uff01\u2026\u2026\u201d\u9093\u98de\u9053\uff1a\u201c\u4e5d\u534e\u8ba2\u76df\u4e4b\u540e\uff0c\u76db\u4f20\u6b66\u6797\u4e09\u5723\u8eab\u906d\u6697\u7b97\uff0c\u90a3\u65f6\uff0c\u6211\u8fd8\u4e0d\u592a\u76f8\u4fe1\uff0c\u73b0\u5728\u60f3\u6765\uff0c\u5012\u662f\u5927\u6709\u53ef\u80fd\u4e86\uff0c\u4ed6\u4eec\u4ee5\u7edd\u4e16\u5185\u529f\uff0c\u538b\u5236\u7740\u4f24\u52bf\uff0c\u4e0d\u8ba9\u5b83\u53d1\u4f5c\uff0c\u5374\u5229\u7528\u5269\u4f59\u7684\u751f\u547d\uff0c\u57f9\u517b\u51fa\u4e86\u627f\u7ee7\u4ed6\u4eec\u9057\u5fd7\u7684\u4eba\u624d\u2026\u2026\u3002\u201d \u3000\u3000\u4e5f\u8bb8\u662f\u8fd9\u4ef6\u5927\u4e8b\u662f\u6b66\u6797\u4e2d\u90fd\u60f3\u4e86\u89e3\u3001\u77e5\u9053\u7684\u7b2c\u4e00\u79d8\u5bc6\uff0c\u6240\u4ee5\uff0c\u4e24\u4eba\u8c08\u8d77\u6765\uff0c\u7adf\u662f\u4e0d\u5fcd\u505c\u4e0b\u3002 \u3000\u3000\u4e5f\u8bb8\u662f\u4e24\u4eba\u501f\u8fd9\u756a\u8c08\u8bba\u6c5f\u6e56\u5f80\u4e8b\uff0c\u62d6\u5ef6\u51fa\u624b\u7684\u65f6\u95f4\u3002 \u3000\u3000\u201c\u6709\u4e00\u70b9\uff0c\u6211\u4e00\u76f4\u60f3\u4e0d\u660e\u767d\u2026\u2026\u201d\u5201\u9e4f\u9053\uff1a\u201c\u7b97\u7b97\u65f6\u95f4\uff0c\u6b66\u6797\u4e09\u5723\u5728\u8eab\u53d7\u6697\u7b97\u4e4b\u540e\uff0c\u81f3\u5c11\u8fd8\u6d3b\u4e86\u5341\u51e0\u5e74\uff0c\u8fd9\u4e9b\u65e5\u5b50\u4e2d\uff0c\u4ed6\u4eec\u5728\u6c5f\u6e56\u4e0a\u5954\u8d70\uff0c\u9910\u98ce\u9732\u5bbf\uff0c\u5403\u4e86\u4e0d\u5c11\u82e6\u5934\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u4eec\u8fd8\u8981\u9690\u5bc6\u884c\u8e2a\uff0c\u4ed6\u4eec\u7684\u884c\u52a8\uff0c\u53d7\u5230\u4e86\u5f88\u5927\u7684\u9650\u5236\uff0c\u5201\u67d0\u4eba\uff0c\u5c31\u662f\u5f53\u5e74\u53d7\u547d\u8ffd\u89c5\u4ed6\u4eec\u884c\u8e2a\u7684\u4eba\u624b\u4e4b\u4e00\u3002 \u3000\u3000\u5c31\u6211\u6240\u77e5\uff0c\u90a3\u4e00\u6b21\uff0c\u6295\u5165\u7684\u4eba\u624b\uff0c\u81f3\u5c11\u5728\u4e94\u767e\u4eba\u4ee5\u4e0a\uff0c\u50cf\u6211\u8fd9\u79cd\u8eab\u624b\u7684\u4eba\uff0c\u5c11\u8bf4\u70b9\uff0c\u4e5f\u8be5\u6709\u4e8c\u4e09\u5341\u4e2a\u4eba\uff0c\u8db3\u8ff9\u904d\u53ca\u5927\u6c5f\u5357\u5317\uff0c\u4e1c\u6d77\u897f\u57df\uff0c\u5357\u8352\u5317\u7586\uff0c\u6df1\u5c71\u5927\u6cfd\uff0c\u4e00\u627e\u5c31\u662f\u4e94\u5e74\uff0c\u9694\u4e00\u5e74\u518d\u4e00\u6b21\u5c55\u5f00\u641c\u5bfb\uff0c\u89c4\u6a21\u66f4\u4e3a\u5e9e\u5927\uff0c\u53c8\u627e\u4e86\u4e09\u5e74\u4e4b\u4e45\uff0c\u624d\u7b97\u505c\u4e86\u4e0b\u6765\u2026\u2026\u3002\u201d \u3000\u3000\u201c\u4ee5\u540e\u5462\uff1f\u2026\u2026\u201d\u9093\u98de\u9053\uff1a\u201c\u662f\u5426\u771f\u7684\u505c\u4e86\u4e0b\u6765\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\u2026\u2026\u201d\u5201\u9e4f\u9053\uff1a\u201c\u53ea\u4e0d\u8fc7\u7531\u660e\u8f6c\u6697\uff0c\u4f46\u53c2\u4e0e\u7684\u4eba\u624b\uff0c\u5374\u90fd\u662f\u6c5f\u6e56\u4e2d\u7684\u9ad8\u624b\u4e86\uff0c\u89c4\u6a21\u867d\u7136\u662f\u5c0f\u591a\u4e86\uff0c\u4f46\u641c\u5bfb\u7684\u533a\u57df\uff0c\u5374\u662f\u66f4\u4e3a\u6df1\u5165\uff0c\u8bf4\u6765\uff0c\u4e5f\u771f\u662f\u5947\u602a\uff0c\u6211\u4eec\u641c\u5bfb\u4e86\u5341\u51e0\u5e74\uff0c\u7adf\u7136\u672a\u627e\u51fa\u4e00\u70b9\u7709\u76ee\uff0c\u63a2\u542c\u5230\u4e00\u70b9\u6d88\u606f\uff0c\u90a3\u4e48\u7cbe\u5bc6\u7684\u7b56\u5212\uff0c\u90a3\u4e48\u5e9e\u5927\u7684\u4eba\u529b\uff0c\u5374\u5b8c\u5168\u5931\u8d25\u4e86\uff0c\u4ed6\u4eec\u7a76\u7adf\u662f\u5982\u4f55\u85cf\u8eab\u5462\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4ef6\u4e8b\uff0c\u6211\u4e5f\u611f\u89c9\u5230\u4e0d\u53ef\u601d\u8bae\u2026\u2026\u201d \u3000\u3000\u5201\u9e4f\u9053\uff1a\u201c\u770b\u6765\uff0c\u9093\u5144\u6bd4\u5201\u67d0\u4eba\u806a\u660e\u591a\u4e86\uff0c\u5230\u73b0\u5728\u6211\u8fd8\u662f\u4e0d\u660e\u767d\uff1f\u201d \u3000\u3000\u9093\u98de\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u4eec\u89c1\u5230\u4e86\u6613\u82b1\u624b\u5f20\u56db\u59d1\u2026\u2026\u201d \u3000\u3000\u201c\u660e\u767d\u4e86\u2026\u2026\u201d\u5201\u9e4f\u8bf4\uff0c\u201c\u5f20\u56db\u59d1\u66ff\u4ed6\u4eec\u5236\u9020\u4e86\u4e00\u526f\u7cbe\u5de7\u7684\u9762\u5177\uff0c\u63a9\u906e\u53bb\u4ed6\u4eec\u672c\u6765\u7684\u9762\u76ee\uff1f\u2026\u2026\u201d \u3000\u3000\u201c\u4e5f\u8bb8\u662f\u5f88\u591a\u526f\u7cbe\u5de7\u9762\u5177\uff0c\u4ee5\u9002\u5408\u4ed6\u4eec\u4e0d\u540c\u7684\u8eab\u4efd\u2026\u2026\u201d\u9093\u98de\u9053\uff1a\u201c\u5982\u679c\u771f\u662f\u8fd9\u6837\uff0c\u4f60\u60f3\u60f3\uff0c\u5c31\u7b97\u4ed6\u4eec\u7ad9\u5728\u4f60\u7684\u8eab\u4fa7\uff0c\u4f60\u4e5f\u662f\u6d51\u7136\u4e0d\u89c9\uff0c\u4ee5\u6c5f\u5c11\u4fa0\u800c\u8bba\uff0c\u90a3\u526f\u9762\u5177\u4e4b\u7cbe\u5de7\uff0c\u4ee5\u6211\u8fd9\u53cc\u9b54\u773c\uff0c\u7adf\u662f\u77a7\u4e0d\u51fa\u4e00\u70b9\u7834\u7efd\uff0c\u5929\u4e0b\u6b66\u529f\u9ad8\u8fc7\u6211\u7684\u4eba\uff0c\u4e0d\u8ba1\u5176\u6570\uff0c\u4f46\u80fd\u8d85\u8fc7\u6211\u8fd9\u53cc\u773c\u775b\u900f\u89c6\u80fd\u529b\u7684\u4eba\uff0c\u90a3\u5c31\u5c48\u6307\u53ef\u6570\u4e86\u3002\u201d \u3000\u3000\u201c\u5bf9\uff01\u63a8\u60f3\u8d77\u6765\uff0c\u5343\u53d8\u4e07\u5316\uff0c\u83ab\u53ef\u601d\u8bae\uff0c\u4f46\u4e00\u7ecf\u70b9\u7834\uff0c\u5374\u4e0d\u8fc7\u539f\u6765\u5982\u6b64\u2026\u2026\u201d\u5201\u9e4f\u9053\uff1a \u3000\u3000\u201c\u6613\u82b1\u624b\u5f20\u56db\u59d1\u5236\u9020\u9762\u5177\u7684\u624b\u6cd5\u65f7\u7edd\u4e00\u4ee3\uff0c\u4e3a\u4ec0\u4e48\u5929\u9a6c\u5802\u7684\u603b\u5802\u4e3b\uff0c\u7adf\u7136\u60f3\u4e0d\u5230\u8fd9\u4e0a\u9762\u53bb\uff1f\u201d \u3000\u3000\u201c\u6b66\u6797\u4e09\u5723\u7684\u8eab\u4efd\uff0c\u8c01\u53c8\u80fd\u60f3\u5230\u4ed6\u4eec\u63a9\u53bb\u672c\u6765\u9762\u76ee\u5462\uff1f\u2026\u2026\u201d \u3000\u3000\u9093\u98de\u9053\uff1a\u201c\u4f55\u51b5\uff0c\u90a3\u65f6\u95f4\uff0c\u6613\u82b1\u624b\u4e5f\u5982\u6c89\u5728\u5927\u6d77\u7684\u6ce5\u6c99\uff0c\u9690\u606f\u672a\u51fa\uff0c\u6ca1\u6709\u4eba\u4f1a\u628a\u4ee5\u90aa\u95e8\u6280\u827a\u626c\u540d\u6c5f\u6e56\u7684\u6613\u82b1\u624b\uff0c\u8ddf\u6b66\u6797\u4e09\u5723\u7ed9\u8fde\u5728\u4e00\u8d77\u3002\u201d \u3000\u3000\u5201\u9e4f\u9053\uff1a\u201c\u5144\u5f1f\uff0c\u8fd8\u6709\u4e00\u70b9\u7591\u95ee\uff0c\u8bf7\u6559\u9093\u5144\uff0c\u4ee5\u6b66\u6797\u4e09\u5723\u7684\u5a01\u671b\uff0c\u53ea\u8981\u4ee5\u672c\u6765\u9762\u76ee\u5728\u6c5f\u6e56\u4e0a\u51fa\u73b0\u51e0\u6b21\uff0c\u4e5f\u8db3\u4ee5\u9707\u6151\u7fa4\u96c4\uff0c\u4f7f\u4ed6\u4eec\u77e5\u96be\u800c\u9000\uff0c\u4e0d\u81f4\u4e8e\u540c\u6210\u73b0\u5728\u8fd9\u4e2a\u5c40\u9762\u3002\u201d \u3000\u3000\u201c\u5201\u5144\uff0c\u54b1\u4eec\u90fd\u662f\u4e3a\u8fd9\u4e2a\u7ec4\u5408\u6548\u8fc7\u529b\u7684\u4eba\u2026\u2026\u201d\u9093\u98de\u9053\uff1a\u201c\u54b1\u4eec\u5728\u6c5f\u6e56\u4e0a\u7684\u5b9a\u4f4d\uff0c\u4e0d\u8bba\u662f\u76d7\u662f\u5bc7\uff0c\u4e5f\u7b97\u662f\u7a0d\u6709\u540d\u6c14\uff0c\u4f46\u54b1\u4eec\u5bf9\u76ee\u4e0b\u7684\u6c5f\u6e56\u60c5\u52bf\uff0c\u6709\u591a\u5c11\u4e86\u89e3\u5462\uff1f\u201d \u3000\u3000\u5201\u9e4f\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u8bf4\u7684\u662f\u554a\uff1f\u9664\u4e86\u5929\u9a6c\u5802\u4e2d\u957f\u8001\u7ea7\u7684\u4eba\u4e8b\u79fb\u52a8\u3001\u53d8\u5316\u6211\u7a0d\u6709\u6240\u77e5\u4e4b\u5916\uff0c\u5176\u4ed6\u7684\u53ef\u7b97\u662f\u4e00\u65e0\u6240\u77e5\u3002\u201d \u3000\u3000\u201c\u56e0\u4e3a\uff0c\u77e5\u9053\u7684\u4e8b\uff0c\u5168\u662f\u5728\u4f1a\u8bae\u65f6\u542c\u5230\u7684\u3002\u73b0\u5728\uff0c\u60f3\u4e00\u60f3\uff0c\u6211\u80fd\u8bb0\u5f97\u7684\u6c5f\u6e56\u7684\u8bf8\u822c\u60c5\u4e8b\uff0c\u8fd8\u662f\u5f53\u5e74\u5728\u6c5f\u6e56\u4e0a\u8d70\u52a8\u65f6\u77e5\u9053\u7684\u3002\u201d \u3000\u3000\u9093\u98de\u9053\uff1a\u201c\u5f7c\u6b64\u3001\u5f7c\u6b64\uff0c\u4e0d\u8fc7\uff0c\u6211\u5728\u6d1b\u9633\u5c45\u4e2d\uff0c\u8fd9\u6837\u7684\u5730\u65b9\uff0c\u770b\u5230\u7684\u5e94\u8be5\u5f88\u591a\uff0c\u4f46\u5374\u4e5f\u548c\u5201\u5144\u4e00\u6837\uff0c\u5bf9\u6c5f\u6e56\u4e2d\u4e8b\u52a1\uff0c\u4e00\u65e0\u6240\u77e5\uff0c\u90a3\u662f\u56e0\u4e3a\uff0c\u6c5f\u6e56\u5df2\u4e0d\u6210\u4e3a\u6c5f\u6e56\u4e86\uff0c\u8868\u9762\u4e0a\u662f\u5e73\u9759\u65e0\u6ce2\uff0c\u8fc7\u53bb\u6c5f\u6e56\u4e0a\u7684\u70ed\u95f9\u7eb7\u4e89\uff0c\u90fd\u5df2\u4e0d\u89c1\uff0c\u6c5f\u5c11\u4fa0\u5230\u6d1b\u9633\u5c45\u6765\uff0c\u624d\u5f15\u8d77\u8fde\u756a\u640f\u6740\uff0c\u4e5f\u8bb8\u6697\u4e2d\u4e5f\u6709\u6fc0\u6d41\u6c79\u6d8c\uff0c\u53ea\u4e0d\u8fc7\u54b1\u4eec\u65e0\u6cd5\u770b\u5230\uff0c\u9664\u4e86\u5929\u9a6c\u5802\u548c\u5185\u5bab\u4e00\u7cfb\u4e2d\u4eba\u7684\u4e89\u6267\u4e4b\u5916\uff0c\u4e00\u5207\u90fd\u662f\u5168\u5728\u4ed6\u4eec\u7684\u638c\u63e1\u4e4b\u4e2d\u3002\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u5929\u738b\u95e8\u3001\u5929\u9a6c\u5802\uff0c\u548c\u5185\u5bab\u4e00\u7cfb\u8054\u624b\u7684\u529b\u91cf\uff0c\u4f3c\u662f\u5df2\u5b8c\u5168\u63a7\u5236\u4e86\u6c5f\u6e56\u4e2d\u4e00\u5207\u6d3b\u52a8\uff0c\u2026\u2026\u201d\u5201\u9e4f\u9053\uff1a\u201c\u5c11\u6797\u5bfa\u7684\u5a01\u540d\u6d88\u9000\u4e86\uff0c\u6b66\u5f53\u6d3e\u7684\u6c14\u52bf\u6c89\u6ca6\u4e86\uff0c\u9664\u4e86\u7a9d\u91cc\u7ffb\uff0c\u81ea\u5df1\u4eba\u4e92\u76f8\u6597\u4e89\u4e4b\u5916\uff0c\u771f\u662f\u5df2\u6ca1\u6709\u5916\u529b\u4ecb\u5165\u4e86\u3002\u201d \u3000\u3000\u201c\u4f46\u7269\u6781\u5fc5\u53cd\u2026\u2026\u201d\u9093\u98de\u9053\uff1a\u201c\u6c5f\u6e56\u5c31\u662f\u6c5f\u6e56\uff0c\u4ece\u6765\u6ca1\u6709\u4efb\u4f55\u4e00\u80a1\u529b\u91cf\u80fd\u6c38\u8fdc\u63a7\u5236\u5b83\uff0c\u4f7f\u5b83\u6c38\u8fdc\u5e73\u9759\uff0c\u4e07\u52ab\u4e0d\u590d\u3002\u201d \u3000\u3000\u5201\u9e4f\u9053\uff1a\u201c\u5929\u9a6c\u5802\u4e00\u76f4\u548c\u5185\u5bab\u4e00\u7cfb\u4e2d\u4eba\u4e0d\u505c\u7684\u4e89\u6597\uff0c\u4e3a\u4ec0\u4e48\u4e00\u4e0b\u5b50\u7adf\u4f1a\u8054\u5408\u8d77\u6765\uff0c\u5408\u4e8e\u4e00\u5904\uff0c\u5bf9\u4ed8\u6c5f\u5c11\u4fa0\u5462\uff1f\u201d \u3000\u3000\u201c\u8fd9\u624d\u662f\u771f\u6b63\u7684\u53ef\u6015\u6240\u5728\u4e86\u2026\u2026\u201d\u9093\u98de\u9053\uff1a \u3000\u3000\u201c\u5982\u82e5\u5728\u5e55\u540e\u6ca1\u6709\u4e00\u80a1\u53ef\u4ee5\u63a7\u5236\u5168\u5c40\u7684\u529b\u91cf\uff0c\u5c31\u4e0d\u53ef\u80fd\u6709\u8fd9\u79cd\u5947\u602a\u7684\u53d8\u5316\u3002\u201d \u3000\u3000\u201c\u9093\u5144\u662f\u8bf4\uff0c\u6709\u4e00\u80a1\u770b\u4e0d\u89c1\u7684\u529b\u91cf\u5728\u5bfc\u5f15\u5168\u5c40\u2026\u2026\u201d\u5201\u9e4f\u9053\uff1a\u201c\u90a3\u53c8\u662f\u4ec0\u4e48\u4eba\u5462\uff1f\u201d \u3000\u3000\u201c\u4e0d\u77e5\u9053\uff0c\u2026\u2026\u201d\u9093\u98de\u9053\uff1a\u201c\u4f46\u90a3\u80a1\u529b\u91cf\u786e\u7136\u5b58\u5728\uff0c\u4ed6\u7684\u91ce\u5fc3\uff0c\u4f3c\u4e4e\u662f\u4e0d\u6b62\u4ee5\u5f81\u670d\u6b66\u6797\u540c\u9053\u5c31\u4f1a\u6ee1\u8db3\uff0c\u4e5f\u8bb8\u6709\u66f4\u5927\u7684\u9634\u8c0b\u5728\u8fdb\u884c\u3002\u201d \u3000\u3000\u5201\u9e4f\u5446\u4e86\uff0c\u9053\uff1a\u201c\u8bf4\u7684\u662f\uff0c\u6c5f\u67ab\u9886\u5bfc\u7684\u8fd9\u80a1\u529b\u91cf\uff0c\u4e5f\u5c31\u66f4\u4e0d\u80fd\u5931\u8d25\u4e86\uff0c\u9093\u5144\uff0c\u54b1\u4eec\u867d\u53ea\u662f\u7c73\u7c92\u8424\u706b\u4e4b\u5149\uff0c\u4e5f\u8981\u5168\u529b\u4ee5\u8d74\uff0c\u80fd\u6d3b\u7740\u770b\u5230\u7a76\u7adf\uff0c\u6545\u7136\u662f\u597d\uff0c\u5c31\u7b97\u6218\u6b7b\uff0c\u4e5f\u662f\u5fc3\u65e0\u9057\u61be\u4e86\u3002\u201d \u3000\u3000\u5c31\u5728\u4e24\u4eba\u4ea4\u8c08\u4e4b\u95f4\uff0c\u53cc\u65b9\u7684\u640f\u6740\uff0c\u5df2\u7ecf\u66f4\u8d8b\u6fc0\u70c8\uff0c\u4e0d\u7edd\u4e8e\u8033\u7684\u5175\u5203\u649e\u51fb\u4e4b\u58f0\uff0c\u95ea\u8d77\u4e86\u4e00\u4e32\u4e32\u7684\u706b\u5149\u3002 \u3000\u3000\u8fd9\u7b49\u591c\u6697\u4e2d\u7684\u6fc0\u70c8\u640f\u6740\uff0c\u8fde\u6c5f\u67ab\u548c\u5927\u548c\u5c1a\u8fd9\u7b49\u7edd\u4e16\u9ad8\u624b\uff0c\u4e5f\u770b\u5f97\u5fc3\u60ca\u3001\u80c6\u98a4\uff0c\u6697\u6697\u76b1\u8d77\u4e86\u7709\u5934\u3002 \u3000\u3000\u56e0\u4e3a\uff0c\u8fd9\u4e2a\u591c\u665a\u592a\u6697\u4e86\uff0c\u6697\u7684\u4e0d\u89c1\u4e00\u70b9\u5149\u4eae\uff0c\u8fde\u6ee1\u5730\u79ef\u96ea\uff0c\u770b\u4e0a\u53bb\uff0c\u4e5f\u53ea\u662f\u4e00\u56e2\u6a21\u7cca\u7684\u767d\u5f71\uff0c\u4ee5\u6c5f\u67ab\u548c\u4e03\u5b9d\u548c\u5c1a\u7684\u76ee\u529b\uff0c\u4e5f\u53ea\u80fd\u770b\u5230\u5200\u3001\u5251\u7684\u6325\u52a8\uff0c\u770b\u4e0d\u771f\u5207\u5200\u62db\u3001\u5251\u62db\u7684\u53d8\u5316\u3002 \u3000 \u7b2c\u5341\u516b\u56de\u3000\u8bdb\u86ca\u4eba\u5c11\u4fa0\u5947\u5c11\u4fa0 \u3000 \u3000\u3000\u640f\u6740\u4e2d\u7684\u4eba\uff0c\u4e5f\u65e0\u6cd5\u89c1\u53ca\u7ec6\u5fae\uff0c\u8981\u51ed\u85c9\u7eaf\u719f\u7684\u6280\u827a\uff0c\u548c\u542c\u529b\u5224\u65ad\uff0c\u6325\u5200\u51fa\u5251\uff0c\u5c01\u67b6\u5bf9\u65b9\u7684\u653b\u51fb\u52bf\uff0c\u5c55\u5f00\u8fd8\u51fb\u3002 \u3000\u3000\u597d\u5728\uff0c\u53cc\u65b9\u5904\u5883\u4e00\u822c\uff0c\u8c01\u4e5f\u6ca1\u6709\u5360\u5230\u4f18\u52bf\u3002 \u3000\u3000\u5012\u662f\u90a3\u5175\u5203\u649e\u53ca\u65f6\u95ea\u51fa\u706b\u661f\uff0c\u7adf\u4f1a\u7ed9\u53cc\u65b9\u4e00\u79cd\u5feb\u901f\u5236\u654c\u7684\u611f\u89c9\uff0c\u4f1a\u51fa\u73b0\u5feb\u5200\u3001\u75be\u5251\u7684\u62a2\u653b\u3002 \u3000\u3000\u4f46\u90a3\u4e00\u5239\u800c\u901d\u7684\u5149\u4eae\uff0c\u53ea\u7ed9\u53cc\u65b9\u4e00\u4e24\u62db\u7684\u8fdb\u653b\u673a\u4f1a\u3002 \u3000\u3000\u6b27\u9633\u662d\u6ca1\u6709\u51fa\u624b\uff0c\u56e0\u4e3a\u4e00\u4eba\u5251\u9635\u7684\u53d8\u5316\uff0c\u6709\u4e00\u5b9a\u89c4\u5f8b\uff0c\u6b27\u9633\u662d\u52a0\u8fdb\u53bb\uff0c\u53cd\u6709\u7740\u963b\u788d\u5251\u9635\u53d8\u5316\u7684\u611f\u89c9\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u8fd9\u4e5f\u7ed9\u4e86\u4e94\u4e2a\u5e74\u8f7b\u5251\u624b\u8868\u73b0\u673a\u4f1a\uff0c\u4ed6\u4eec\u5251\u9635\u53d8\u5316\uff0c\u65e9\u5df2\u7ec3\u4e60\u7eaf\u719f\uff0c\u591c\u8272\u867d\u6697\uff0c\u5bf9\u5408\u640f\u5bf9\u654c\u7684\u5f71\u54cd\u4e0d\u5927\uff0c\u4e94\u4eba\u7b2c\u4e00\u6b21\u5bf9\u654c\uff0c\u8fd8\u6709\u751f\u6da9\u7684\u611f\u89c9\uff0c\u4f46\u640f\u6740\u4e00\u9635\u4e4b\u540e\uff0c\u9010\u6e10\u7684\u9002\u5e94\uff0c\u7adf\u628a\u4e00\u54c1\u5200\u548c\u56db\u4e2a\u5f1f\u5b50\u56f0\u5165\u9635\u4e2d\uff0c\u8feb\u5f97\u4e00\u54c1\u5200\u548c\u56db\u5927\u5f1f\u5b50\uff0c\u5b8c\u5168\u6539\u91c7\u5b88\u52bf\u3002\u4e00\u54c1\u5200\u867d\u662f\u8eab\u6000\u7edd\u6280\uff0c\u4f46\u5374\u4e0d\u6562\u5192\u9669\u65bd\u5c55\uff0c\u552f\u6050\u4f24\u5230\u4e86\u81ea\u5df1\u5f1f\u5b50\uff1a \u3000\u3000\u7f57\u5170\u4e09\u5bab\u4e3b\u8fd9\u4e00\u5bf9\u4e00\u5c31\u6253\u5f97\u5341\u5206\u8270\u82e6\u4e86\uff0c\u4e24\u4eba\u90fd\u6709\u7740\u675f\u624b\u7f1a\u811a\u7684\u611f\u89c9\uff0c\u4e0d\u80fd\u5c3d\u60c5\u53d1\u6325\u51fa\u4e00\u8eab\u6240\u5b66\uff0c\u4f5c\u4e00\u51b3\u80dc\u8d1f\u4e4b\u6218\u3002 \u3000\u3000\u5927\u548c\u5c1a\u8fd0\u8db3\u4e86\u5168\u90e8\u773c\u795e\uff0c\u76d1\u89c6\u4e09\u5bab\u4e3b\u548c\u7f57\u5170\u4e4b\u6218\uff0c\u51c6\u5907\u968f\u65f6\u51fa\u624b\uff0c\u6551\u63f4\u7f57\u5170\uff0c\u4f46\u53d1\u89c9\u8fd9\u573a\u591c\u6218\uff0c\u8868\u9762\u4e0a\u5341\u5206\u6050\u6016\uff0c\u4f46\u5404\u4eba\u90fd\u6253\u5f97\u5341\u5206\u9690\u5065\uff0c\u90fd\u4e0d\u6562\u5192\u9669\u71e5\u8fdb\uff0c\u5341\u6210\u6b66\u529f\uff0c\u53ea\u80fd\u53d1\u6325\u51fa\u4e94\u6210\u5a01\u529b\uff0c\u5927\u90e8\u5206\u7684\u5b9e\u529b\uff0c\u90fd\u7559\u4f5c\u81ea\u4fdd\uff0c\u8fd9\u79cd\u6253\u6cd5\uff0c\u81ea\u7136\u662f\u5f88\u5b89\u5168\u4e86\u3002 \u3000\u3000\u5927\u548c\u5c1a\u8f7b\u8f7b\u5401\u4e00\u53e3\u6c14\uff0c\u7f13\u7f13\u9000\u5230\u6c5f\u67ab\u8eab\u4fa7\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u73b0\u5728\u4e0d\u8fc7\u521d\u66f4\u5de6\u53f3\uff0c\u4ee5\u76ee\u524d\u8fd9\u79cd\u6253\u6cd5\uff0c\u53cc\u65b9\u4e0d\u4f1a\u6709\u6e85\u8840\u640f\u547d\u7684\u573a\u9762\uff0c\u4f46\u5374\u5bf9\u6211\u4eec\u5927\u5927\u7684\u4e0d\u5229\uff0c\u6253\u4e0b\u53bb\uff0c\u4e8e\u654c\u65e0\u635f\u3002\u201d \u3000\u3000\u201c\u6211\u4e5f\u6709\u8fd9\u79cd\u611f\u89c9\uff0c\u5728\u5982\u6b64\u9ed1\u6697\u7684\u591c\u95f4\u52a8\u624b\uff0c\u5c31\u7b97\u8001\u5982\u4e00\u54c1\u5200\u8fd9\u79cd\u4eba\u7269\uff0c\u5927\u6982\u4e5f\u662f\u521d\u5ea6\u7ecf\u5386\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u90fd\u6709\u7740\u5982\u5c65\u8584\u51b0\u7684\u538b\u529b\uff0c\u90a3\u91cc\u8fd8\u80fd\u65bd\u5c55\u51fa\u5947\u62db\u7edd\u6280\uff0c\u5236\u654c\u4e8e\u6b7b\u3002\u201d \u3000\u3000\u201c\u5bf9\uff01\u73b0\u5728\uff0c\u6211\u4eec\u662f\u5750\u5f85\u654c\u4eba\u9634\u8c0b\u51fa\u73b0\u2026\u2026\u201d\u5927\u548c\u5c1a\u9053\uff1a\u201c\u662f\u4e0d\u662f\u6709\u4e9b\u592a\u5403\u4e8f\u4e86\u3002\u201d \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u4e4c\u4e91\u853d\u5929\uff0c\u9ed1\u7684\u4f38\u624b\u4e0d\u89c1\u4e94\u6307\uff0c\u4e0d\u77e5\u662f\u4e0d\u662f\u4e5f\u5728\u4e09\u5bab\u4e3b\u7684\u9884\u8ba1\u4e4b\u5185\uff1f\u201d \u3000\u3000\u201c\u4e0d\u7ba1\u5982\u4f55\uff1f\u6211\u4eec\u90fd\u4e0d\u5b9c\u5728\u6b64\u5730\u6491\u4e0b\u53bb\uff0c\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u9053\uff1a\u201c\u6211\u4eec\u4f55\u4e0d\u6765\u4e2a\u6613\u5730\u800c\u6218\uff1f\u654c\u4eba\u5e0c\u671b\u628a\u6211\u4eec\u62d6\u5728\u6b64\u5730\uff0c\u6211\u4eec\u504f\u504f\u4e0d\u5728\u8fd9\u91cc\uff0c\u5f15\u4ed6\u4eec\u79bb\u5f00\uff0c\u9009\u4e00\u5904\u51b3\u6218\u4e4b\u5730\u3002\u201d \u3000\u3000\u6c5f\u67ab\u6c89\u541f\u4e0d\u8bed\uff0c\u5fc3\u4e2d\u5fd6\u9053\uff1a\u201c\u6b64\u5730\u4e4b\u5185\uff0c\u867d\u7136\u6709\u654c\u4eba\u9634\u8c0b\u5e03\u7f6e\uff0c\u4f46\u6211\u4eec\u4e5f\u6709\u7406\u4f0f\uff0c\u5f20\u56db\u59d1\u548c\u971c\u96ea\u4e8c\u5973\uff0c\u52a0\u4e0a\u6885\u82b1\uff0c\u90fd\u85cf\u4e8e\u79ef\u96ea\u4e4b\u4e2d\uff0c\u51c6\u5907\u7ed9\u654c\u4eba\u8fce\u5934\u75db\u51fb\uff0c\u6240\u4ee5\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u4e0d\u77e5\u8be5\u5982\u4f55\u56de\u7b54\u5927\u548c\u5c1a\u3002\u201d \u3000\u3000\u201c\u8001\u5f1f\u2026\u2026\u201d\u5916\u62d9\u5185\u79c0\u7684\u5927\u548c\u5c1a\uff0c\u4f3c\u662f\u5df2\u770b\u900f\u4e86\u6c5f\u67ab\u7684\u5fc3\u610f\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u5982\u8bba\u5bf9\u654c\u7b56\u7565\uff0c\u884c\u5175\u5e03\u7f6e\uff0c\u548c\u5c1a\u81ea\u77e5\u4e0d\u53ca\u5f20\u59d1\u5a18\uff0c\u8fd9\u4ef6\u4e8b\uff0c\u4f55\u4e0d\u548c\u5979\u5546\u91cf\u4e00\u4e0b\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u70b9\u70b9\u5934\uff0c\u8fd8\u672a\u53ca\u4e00\u5f00\u53e3\uff0c\u8033\u9645\u95f4\u5df2\u54cd\u8d77\u4e86\u5f20\u56db\u59d1\u7684\u58f0\u97f3\uff0c\u9053\uff1a\u201c\u4e0d\u7528\u5546\u91cf\u4e86\uff0c\u5927\u548c\u5c1a\u8bf4\u7684\u4e0d\u9519\uff0c\u6211\u770b\u4e09\u5bab\u4e3b\u7684\u9634\u8c0b\uff0c\u53ef\u80fd\u4e0d\u662f\u4ee5\u4f0f\u5175\u4e3a\u4e3b\uff0c\u7a76\u7adf\u662f\u4ec0\u4e48\u4e1c\u897f\uff1f\u6211\u4e5f\u65e0\u6cd5\u63a8\u65ad\uff0c\u4f60\u4eec\u4ee5\u5341\u4f59\u4eba\u4e4b\u529b\uff0c\u6297\u62d2\u4ed6\u4eec\u4e09\u8def\u5927\u519b\uff0c\u5a01\u540d\u5df2\u7acb\u3002 \u3000\u3000\u54b1\u4eec\u53ea\u6709\u8fd9\u70b9\u672c\u94b1\uff0c\u80fd\u80dc\u4e0d\u80fd\u8d25\uff0c\u6240\u4ee5\uff0c\u4e0d\u53ef\u5192\u9669\uff0c\u542c\u548c\u5c1a\u7684\u8bdd\uff0c\u64a4\u79bb\u6b64\u5730\uff0c\u6b64\u540e\uff0c\u54b1\u4eec\u8fd8\u8981\u6539\u53d8\u5bf9\u654c\u7684\u65b9\u6cd5\uff0c\u8fc7\u4e86\u4eca\u591c\uff0c\u6211\u4f1a\u627e\u4f60\u5546\u91cf\uff0c\u8fd8\u8981\u4f60\u597d\u597d\u53d1\u6325\u4e00\u756a\u3002\u201d \u3000\u3000\u4e56\u4e56\uff0c\u58f0\u97f3\u5c31\u5728\u8eab\u4fa7\u4e0d\u8fdc\u5904\uff0c\u5f20\u56db\u59d1\u4f3c\u5df2\u5728\u96ea\u4e2d\u6f5c\u884c\u4ea4\u624b\u7684\u6240\u5728\u3002 \u3000\u3000\u201c\u4f60\u5462\uff1f\u2026\u2026\u201d\u6c5f\u67ab\u4e5f\u4ee5\u4f20\u97f3\u4e4b\u672f\uff0c\u95ee\u9053\uff1a\u201c\u96ea\u971c\u4e8c\u5973\u548c\u6885\u82b1\uff0c\u662f\u5426\u80fd\u652f\u6301\u5f97\u4f4f\uff0c\u6211\u4eec\u8d70\u4e86\uff0c\u4f60\u4eec\u4eba\u5355\u52bf\u5b64\uff0c\u4e00\u65e6\u884c\u8e2a\u88ab\u4eba\u53d1\u73b0\uff0c\u8981\u5982\u4f55\u5e94\u654c\uff1f\u201d \u3000\u3000\u201c\u4e24\u4e2a\u5c0f\u72d0\u5973\u5185\u529f\u7cbe\u6e5b\uff0c\u5728\u91cd\u88d8\u62a4\u4f53\u4e4b\u4e0b\uff0c\u6d3b\u6cfc\u5982\u5e38\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u6885\u82b1\u867d\u7136\u5dee\u4e00\u70b9\uff0c\u4f46\u7cbe\u795e\u8fd8\u597d\uff0c\u800c\u4e14\uff0c\u8d8a\u6765\u8d8a\u80fd\u9002\u5e94\uff0c\u8fd9\u4e2b\u5934\u7684\u5185\u529f\uff0c\u9020\u8be3\u4ea6\u6df1\uff0c\u653e\u5fc3\u5427\uff01\u5979\u4eec\u90fd\u597d\u7684\u5f88\uff0c\u66f4\u4e0d\u7528\u4e3a\u6211\u4eec\u7684\u5b89\u5371\u62c5\u5fc3\uff0c\u6211\u8fd8\u60f3\u4e0d\u51fa\u4ec0\u4e48\u6bd2\u7269\uff0c\u80fd\u6df1\u4eba\u79ef\u96ea\u4f24\u4eba\uff0c\u4ed6\u4eec\u6700\u597d\u4e0d\u8981\u6fc0\u6012\u6211\uff0c\u7ed9\u4ed6\u4eec\u4e00\u70b9\u6559\u8bad\u4e5f\u5c31\u7b97\u4e86\uff0c\u4e0a\u5929\u6709\u597d\u751f\u4e4b\u5fb7\u561b\uff01 \u3000\u3000\u6211\u4e5f\u4e0d\u60f3\u6740\u4ed6\u4eec\u4e2a\u7247\u7532\u4e0d\u7559\uff0c\u5982\u679c\u662f\u4ed6\u4eec\u7528\u7684\u65b9\u6cd5\u592a\u6076\u6bd2\u3001\u4e0b\u6d41\uff0c\u90a3\u5c31\u53eb\u4ed6\u5403\u6b21\u5927\u82e6\u4e86\u3002\u201d \u3000\u3000\u6c5f\u67ab\u9ed8\u7136\u4e0d\u8bed\uff0c\u5fc3\u4e2d\u5374\u6697\u6697\u5fd6\u9053\uff1a\u201c\u5f20\u56db\u59d1\u4f1a\u7528\u4ec0\u4e48\u65b9\u6cd5\uff0c\u4ee5\u56db\u4eba\u4e4b\u529b\uff0c\u80fd\u6297\u62d2\u654c\u4eba\u90a3\u4e48\u5f3a\u5927\u7684\u5b9e\u529b\u5462\uff1f\u201d \u3000\u3000\u4f46\u95fb\u5f20\u56db\u59d1\u9053\uff1a\u201c\u4e0d\u8981\u5fe7\u5fc3\uff0c\u6211\u5728\u65bd\u5c55\u6bd2\u624b\u4e4b\u524d\uff0c\u4f1a\u5148\u901a\u544a\u4f60\u90a3\u53ea\u5c0f\u51e4\u513f\uff0c\u8981\u5979\u907f\u5f00\uff0c\u4e0d\u4f1a\u4f24\u5230\u5979\u7684\u3002\u201d \u3000\u3000\u6562\u60c5\u5f20\u56db\u59d1\u60f3\u5230\u53e6\u4e00\u9762\u53bb\u4e86\u3002 \u3000\u3000\u6c5f\u67ab\u6b63\u60f3\u8fa9\u89e3\u51e0\u53e5\uff0c\u8033\u9645\u4e2d\uff0c\u53c8\u4f20\u5165\u5f20\u56db\u59d1\u7684\u58f0\u97f3\uff0c\u9053\uff1a\u201c\u6211\u4e5f\u4f1a\u628a\u4f60\u5173\u5fc3\u4e09\u4e2a\u4e2b\u5934\u7684\u8bdd\uff0c\u544a\u8bc9\u4ed6\u4eec\uff0c\u4fdd\u8bc1\u5982\u4e00\u76c6\u7089\u706b\u4e00\u822c\u8ba9\u5979\u4eec\u5168\u8eab\u6e29\u6696\uff0c\u5feb\u4e9b\u8d70\u5427\uff01\u6211\u8981\u56db\u4e0b\u770b\u770b\u53bb\u4e86\u3002\u201d \u3000\u3000\u611f\u89c9\u8eab\u4fa7\u79ef\u96ea\u5fae\u52a8\uff0c\u6c5f\u67ab\u7684\u5de6\u8fb9\u5c0f\u817f\u4f3c\u662f\u88ab\u4eba\u91cd\u91cd\u7684\u634f\u4e86\u4e00\u4e0b\u3002 \u3000\u3000\u6c5f\u67ab\u5fc3\u77e5\u5f20\u56db\u59d1\u5df2\u6f5c\u96ea\u800c\u53bb\uff0c\u90a3\u4e00\u634f\uff0c\u8868\u8fbe\u51fa\u65e0\u9650\u5173\u5fc3\uff0c\u4e5f\u6709\u70b9\u82e6\u4e2d\u4f5c\u4e50\uff0c\u6253\u60c5\u9a82\u4fcf\u7684\u5473\u9053\u3002 \u3000\u3000\u4f46\u4f7f\u6c5f\u67ab\u8bb6\u5f02\u7684\uff0c\u5374\u662f\u5f20\u56db\u59d1\u96ea\u4e2d\u6f5c\u884c\u7684\u672c\u9886\uff0c\u90a3\u8be5\u662f\u4e00\u79cd\u5f88\u7279\u522b\u7684\u5de5\u592b\uff0c\u8fd9\u91cc\u79ef\u96ea\u6df1\u6d45\u4e0d\u5df2\uff0c\u4e00\u822c\u7684\u722c\u884c\u65b9\u6cd5\uff0c\u4f1a\u7acb\u523b\u9732\u51fa\u7834\u7efd\uff0c\u88ab\u4eba\u53d1\u89c9\uff0c\u4f46\u5f20\u56db\u59d1\u5374\u5982\u86c7\u884c\u8fdb\u6c34\uff0c\u4e0d\u89c1\u79ef\u96ea\u88c2\u52a8\uff0c\u4eba\u5df2\u8fdc\u53bb\u3002 \u3000\u3000\u8fd9\u4e2a\u5973\u4eba\uff0c\u8eab\u6000\u7684\u5f02\u672f\uff0c\u5947\u6280\u4e0d\u5f97\u4e0d\u53eb\u4eba\u5fc3\u4e2d\u656c\u773c\u3002 \u3000\u3000\u4ee5\u4e24\u4e2a\u72d0\u5973\u7684\u6027\u683c\uff0c\u65e9\u8be5\u8dd1\u5230\u5de6\u8fd1\u6765\uff0c\u548c\u6c5f\u5927\u54e5\u80e1\u95f9\u4e00\u9635\u4e86\uff0c\u4f46\u5979\u4eec\u5374\u9ed8\u7136\u65e0\u58f0\uff0c\u60f3\u662f\u96ea\u4e2d\u6f5c\u884c\u7684\u672c\u9886\uff0c\u8fdc\u672a\u7ec3\u6210\uff0c\u4e0d\u6562\u968f\u4fbf\u884c\u52a8\u3002 \u3000\u3000\u6c5f\u67ab\u653e\u5fc3\u4e86\uff0c\u5f20\u56db\u59d1\u80fd\u5728\u96ea\u4e2d\u6f5c\u884c\uff0c\u81ea\u7136\u53ef\u4ee5\u968f\u65f6\u884c\u8fd1\u96ea\u3001\u971c\u3001\u6885\u82b1\uff0c\u6307\u70b9\u5979\u4eec\u5e94\u53d8\u4e4b\u6cd5\u3002 \u3000\u3000\u53ea\u542c\u5927\u548c\u5c1a\u9053\uff1a\u201c\u8981\u4e0d\u8981\u79bb\u5f00\u8fd9\u91cc\uff0c\u5982\u82e5\u8981\u51b3\u5fc3\u7559\u4e0b\u6765\uff0c\u90a3\u5c31\u8981\u5168\u529b\u51fa\u624b\uff0c\u5148\u51fb\u6bd9\u4e09\u5bab\u4e3b\uff0c\u640f\u6740\u4e00\u54c1\u5200\uff0c\u5f7b\u5e95\u51fb\u6e83\u773c\u4e0b\u7684\u654c\u4eba\uff0c\u518d\u96c6\u4e2d\u5168\u529b\uff0c\u4ee5\u9759\u5236\u53d8\u3002\u201d\u201c\u8d70\uff01\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a \u3000\u3000\u201c\u62db\u547c\u7f57\u5170\u3001\u6b27\u9633\u662d\uff0c\u7387\u9886\u4e94\u5927\u5f1f\u5b50\u5148\u8d70\uff0c\u64a4\u56de\u6d1b\u9633\u5c45\uff0c\u90a3\u91cc\u65e0\u6cd5\u7fa4\u96c6\u56f4\u653b\uff0c\u6613\u4e8e\u5bf9\u654c\uff0c\u6b64\u540e\uff0c\u54b1\u4eec\u8981\u8981\u53d8\u66f4\u5bf9\u654c\u4e4b\u7b56\uff0c\u8981\u9093\u98de\u6ce8\u610f\u53a8\u4e0b\uff0c\u8336\u6c34\uff0c\u522b\u8981\u7740\u4eba\u9053\u513f\uff01\u201d \u3000\u3000\u201c\u5bf9\uff01\u987a\u4fbf\u628a\u6d1b\u9633\u5c45\u7684\u91d1\u94f6\u8d22\u7269\uff0c\u5e2d\u5377\u5e26\u8d70\u2026\u2026\u201d\u5927\u548c\u5c1a\u7b11\u9053\uff1a\u201c\u6b64\u540e\uff0c\u54b1\u4eec\u4eba\u624b\u4f17\u591a\u884c\u52a8\u8d77\u6765\uff0c\u53ef\u662f\u8981\u5927\u7b14\u94f6\u94b1\u624d\u884c\u3002\u201d \u3000\u3000\u6c5f\u67ab\u5fae\u5fae\u4e00\u7b11\uff0c\u4ed8\u9053\uff1a\u201c\u4f60\u8fd9\u51fa\u5bb6\u4eba\uff0c\u80f8\u85cf\u8c0b\u7565\uff0c\u667a\u8ba1\u767e\u51fa\uff0c\u624b\u6bb5\u7684\u6bd2\u8fa3\uff0c\u53ef\u662f\u5168\u65e0\u51fa\u5bb6\u4eba\u7684\u6148\u60b2\u5fc3\u80a0\u3002\u201d \u3000\u3000\u53e3\u4e2d\u5374\u5e94\u9053\uff1a\u201c\u5927\u5e08\u4ee3\u6211\u4f20\u4ee4\uff0c\u7531\u6211\u65ad\u540e\u963b\u654c\u3002\u201d \u3000\u3000\u5927\u548c\u5c1a\u4f20\u51fa\u4e86\u6c5f\u67ab\u7684\u4ee4\u8c15\uff0c\u53e3\u6c14\u575a\u51b3\uff0c\u7ed9\u4eba\u4e00\u79cd\u4e0d\u53ef\u8fdd\u6297\u7684\u611f\u89c9\u3002 \u3000\u3000\u6b27\u9633\u662d\u559d\u4e0a\u4e94\u5927\u5f1f\u5b50\uff0c\u7f57\u5170\u4e5f\u8feb\u9000\u4e86\u4e09\u5bab\u4e3b\uff0c\u5012\u8dc3\u4e09\u4e08\uff0c\u770b\u770b\u6c5f\u67ab\uff0c\u9053\uff1a\u201c\u600e\u4e48\u56de\u4e8b\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u4e00\u6325\u624b\uff0c\u9053\uff1a\u201c\u8ddf\u7740\u5927\u548c\u5c1a\u8d70\uff01\u201d \u3000\u3000\u795e\u60c5\u5f88\u4e25\u8083\uff0c\u6001\u5ea6\u4e5f\u6709\u70b9\u51b7\u3002 \u3000\u3000\u7f57\u5170\u662f\u4e0d\u6562\u518d\u95ee\uff0c\u8f6c\u8eab\u5c31\u8d70\uff01 \u3000\u3000\u6b27\u9633\u662d\u66f4\u662f\u9075\u547d\u884c\u4e8b\uff0c\u7acb\u523b\u64a4\u9000\u3002 \u3000\u3000\u5927\u548c\u5c1a\u4f4e\u58f0\u9053\uff1a\u201c\u8bf8\u4f4d\u5148\u56de\u6d1b\u9633\u5c45\uff0c\u6211\u7559\u4e0b\u6765\uff0c\u63a5\u5e94\u4e00\u4e0b\u6c5f\u8001\u5f1f\u3002\u201d \u3000\u3000\u5927\u5bb6\u90fd\u770b\u5230\u4e86\u4ed6\u7684\u6b66\u529f\uff0c\u4e5f\u6ca1\u4eba\u548c\u4ed6\u4e89\u8bba\uff0c\u7531\u9b54\u773c\u9093\u98de\u5e26\u8def\uff0c\u6025\u5954\u800c\u53bb\u3002 \u3000\u3000\u4e09\u5bab\u4e3b\u3001\u4e00\u54c1\u5200\u7b49\uff0c\u6ca1\u6709\u8ffd\u51fb\u64a4\u8d70\u7684\u6b27\u9633\u662d\u548c\u7f57\u5170\uff0c\u4f46\u6c5f\u67ab\u4ecd\u7136\u662f\u8fce\u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u5bf9\u654c\u4eba\u7a81\u7136\u5168\u519b\u64a4\u9000\uff0c\u4e09\u5bab\u4e3b\u6709\u4e9b\u5fff\u6068\uff0c\u6697\u6697\u9a82\u9053\uff1a\u201c\u89c1\u9b3c\u5566\uff01 \u3000\u3000\u4f60\u4eec\u4e00\u76f4\u5360\u7740\u4f18\u52bf\uff0c\u600e\u4e48\u4f1a\u7a81\u7136\u64a4\u8d70\u4e86\u5462\uff1f\u201d \u3000\u3000\u201c\u662f\u4e0d\u662f\u6709\u4e9b\u5931\u671b\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u624b\u4e2d\u7684\u7fe0\u7389\u5200\uff0c\u6307\u7740\u4e09\u5bab\u4e3b\uff0c\u9053\uff1a\u201c\u4f60\u6307\u793a\u5343\u9762\u4eba\u9b45\u65b9\u5999\u4f24\u6211\uff0c\u6211\u8fd9\u4e2a\u4eba\uff0c\u975e\u5e38\u6ca1\u6709\u98ce\u5ea6\uff0c\u73b0\u5728\u6211\u8981\u6740\u4f60\uff0c\u800c\u4e14\u5728\u5341\u62db\u4e4b\u5185\u3002\u201d \u3000\u3000\u5b57\u5b57\u5982\u5bd2\u51b0\u51fb\u9876\uff0c\u542c\u5f97\u4e09\u5bab\u4e3b\u5fc3\u4e0a\u6cdb\u5347\u8d77\u9635\u9635\u5bd2\u610f\u3002 \u3000\u3000\u201c\u4f60\u2026\u2026\u4f60\u2026\u2026\u6740\u5f97\u4e86\u6211\u4e48\uff1f\u2026\u2026\u201d\u4e09\u5bab\u4e3b\u9053\uff1a\u201c\u6211\u4eec\u6b66\u529f\u5728\u4f2f\u4ef2\u4e4b\u95f4\uff0c\u5c31\u7b97\u4f60\u5f3a\u6211\u4e00\u7b79\u5427\uff1f\u4e5f\u4e0d\u8fc7\u662f\u2026\u2026\u201d \u3000\u3000\u201c\u4f60\u53ef\u4ee5\u4e0d\u76f8\u4fe1\u3002\u2026\u2026\u201d\u6c5f\u67ab\u7b11\u9053\uff1a\u201c\u90a3\u5c31\u51fa\u624b\u8bd5\u8bd5\u5427\uff1f\u201d \u3000\u3000\u4e0d\u77e5\u4f55\u6545\uff0c\u4e09\u5bab\u4e3b\u5fc3\u4e2d\u4fe1\u4e86\uff0c\u611f\u89c9\u5230\u6c5f\u67ab\u786e\u6709\u6740\u5979\u7684\u80fd\u529b\u3002 \u3000\u3000\u201c\u96be\u9053\u2026\u2026\u96be\u9053\u2026\u2026\u4f60\u8ddf\u90a3\u4e2a\u5927\u548c\u5c1a\u4e00\u6837\uff1f\u2026\u2026\u201d\u4e09\u5bab\u4e3b\u8bf4\uff0c\u201c\u6df1\u85cf\u4e0d\u9732\uff0c\u626e\u732a\u5403\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u548c\u5927\u5e08\u4e0d\u4e00\u6837\uff0c\u6211\u5df2\u7ecf\u6740\u4e86\u4e0d\u5c11\u7684\u4eba\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u4e00\u53ea\u559c\u9e4a\uff0c\u4e24\u6761\u9f99\u548c\u51e0\u4e2a\u5929\u9a6c\u5802\u4e2d\u7684\u62a4\u6cd5\uff0c\u4e5f\u8bb8\uff0c\u8fd9\u4e9b\u4eba\uff0c\u90fd\u4e0d\u591f\u540d\u6c14\uff0c\u6740\u4e00\u4e0d\u8db3\u4ee5\u505a\u767e\uff0c\u6240\u4ee5\uff0c\u4eca\u5929\uff0c\u8981\u62ff\u4f60\u4e09\u5bab\u4e3b\u5f00\u5200\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u2026\u2026\u6211\u4e0d\u76f8\u4fe1\uff0c\u4f60\u80fd\u2026\u2026\u201d \u3000\u3000\u201c\u90a3\u5f88\u597d\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u7f13\u6b65\u5411\u524d\u903c\u8fdb\uff0c\u6253\u65ad\u4e86\u4e09\u5bab\u4e3b\u7684\u8bdd\uff0c\u63a5\u9053\uff1a\u201c\u4f60\u53ef\u4ee5\u51fa\u624b\u4e86\uff0c\u6211\u53ef\u4ee5\u8ba9\u4f60\u5148\u673a\uff0c\u4f46\u6211\u4e0d\u4f1a\u7b49\u4e0b\u53bb\uff0c\u5982\u679c\u8ba9\u6211\u5148\u51fa\u624b\uff0c\u4f60\u6839\u672c\u5c31\u6ca1\u6709\u8fd8\u51fb\u7684\u673a\u4f1a\u4e86\uff01\u201d \u3000\u3000\u4ed6\u8bed\u6c14\u5e73\u548c\uff0c\u4e00\u70b9\u4e5f\u4e0d\u5e26\u8083\u6740\u4e4b\u6c14\uff0c\u4f46\u5374\u5b57\u5b57\u5982\u5f3a\u5f13\u5229\u7bad\uff0c\u5c04\u4e2d\u4e86\u4e09\u5bab\u4e3b\u7684\u5fc3\u3001\u80c6\uff0c\u4e09\u5bab\u4e3b\u5ffd\u7136\u611f\u89c9\u5230\u754f\u6015\u4e86\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u6211\u4e0d\u8fd8\u624b\uff0c\u4f60\u603b\u4e0d\u4f1a\u6740\u6211\u5427\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u63d0\u805a\u771f\u6c14\uff0c\u65e0\u76f8\u795e\u529f\uff0c\u5df2\u904d\u5e03\u5168\u8eab\uff0c\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u4f1a\u6740\u4f60\uff0c\u5185\u5bab\u4e00\u7cfb\u4e2d\uff0c\u4f60\u662f\u9996\u6076\uff0c\u8feb\u903c\u5973\u5f1f\u5b50\u7834\u8eab\u6740\u4eba\uff0c\u8ba9\u5979\u4eec\u53d8\u6210\u51b7\u9177\u7684\u6740\u624b\uff0c\u2026\u2026\u201d \u3000\u3000\u201c\u90a3\u4e0d\u5173\u6211\u7684\u4e8b\u554a\uff01\u6211\u4e5f\u662f\u5949\u547d\u884c\u4e8b\u2026\u2026\u201d\u4e09\u5bab\u4e3b\u9053\uff1a\u201c\u6740\u4e86\u6211\uff0c\u8fd8\u6709\u522b\u4eba\u4f1a\u6267\u884c\u8fd9\u4e2a\u5de5\u4f5c\u3002\u201d \u3000\u3000\u5979\u5ffd\u7136\u53d1\u89c9\uff0c\u81ea\u5df1\u4f3c\u5df2\u53d8\u6210\u4e86\u4e00\u4e2a\u72af\u4eba\uff0c\u6c5f\u67ab\u95ee\u4ec0\u4e48\uff1f\u81ea\u5df1\u5c31\u7b54\u4ec0\u4e48\uff1f\u6c5f\u67ab\u8981\u6740\u81ea\u5df1\uff0c\u81ea\u5df1\u5c31\u6392\u547d\u89e3\u91ca\uff0c\u660e\u660e\u662f\u5728\u6c42\u9976\uff0c\u987f\u89c9\u8138\u7ea2\u8033\u70ed\uff0c\u60ed\u6127\u4e0d\u5df2\u3002 \u3000\u3000\u5e78\u597d\u591c\u8272\u5e7d\u6df1\uff0c\u770b\u4e0d\u6e05\u695a\u3002 \u3000\u3000\u4f46\u7ad9\u5728\u4e0d\u8fdc\u5904\u7684\u4e00\u54c1\u5200\u548c\u4ed6\u56db\u4e2a\u5f1f\u5b50\uff0c\u5e94\u8be5\u662f\u542c\u5f97\u6e05\u6e05\u695a\u695a\u4e86\u3002 \u3000\u3000\u201c\u90a3\u4e2a\u4e0b\u4ee4\u7684\u4eba\u66f4\u8be5\u6b7b\uff0c\u4f46\u4f60\u4e5f\u8be5\u662f\u6076\u8d2f\u6ee1\u76c8\u4e86\uff0c\u4e09\u5bab\u4e3b\uff0c\u6446\u70b9\u6c14\u52bf\u51fa\u6765\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u4f60\u6740\u4eba\u65e0\u6570\uff0c\u4e3a\u4ec0\u4e48\u81ea\u5df1\u5374\u5982\u6b64\u7684\u6015\u6b7b\u2026\u2026\u201d \u3000\u3000\u201c\u6c5f\u67ab\uff0c\u4e0d\u8981\u6b3a\u4eba\u8fc7\u751a\uff0c\u59d1\u5976\u4e0d\u662f\u6015\u4f60\u2026\u2026\u201d \u3000\u3000\u4e09\u5bab\u4e3b\u88ab\u7f9e\u8fb1\u7684\u6012\u706b\u66b4\u8d77\u4e86\u3002 \u3000\u3000\u201c\u542c\u8bf4\uff0c\u4f60\u662f\u5185\u5bab\u4e00\u7cfb\u4e2d\uff0c\u5a9a\u529f\u6700\u597d\u7684\u4e00\u4e2a\uff0c\u53ef\u60dc\u6c5f\u67d0\u4eba\u65e0\u7f18\u6c42\u6559\u4e86\uff1f\u201d \u3000\u3000\u201c\u6211\u53ef\u4ee5\u7ed9\u4f60\u4e00\u4e2a\u673a\u4f1a\u2026\u2026\u201d\u738b\u5bab\u4e3b\u9053\uff1a\u201c\u6211\u4e5f\u60f3\u8bd5\u8bd5\u4f60\u7684\u60d1\u5fc3\u672f\uff0c\u770b\u770b\u80fd\u4e0d\u80fd\u60d1\u6211\u4e4b\u5fc3\uff1f\u201d \u3000\u3000\u201c\u4e0d\u5fc5\u4e86\u2026\u2026\u201d\u6c5f\u67ab\u626c\u8d77\u4e86\u624b\u4e2d\u7684\u7fe0\u7389\u5200\uff0c\u9053\uff1a\u201c\u51fa\u624b\u5427\uff01\u201d \u3000\u3000\u4f46\u95fb\u52b2\u98ce\u7834\u7a7a\uff0c\u4e00\u53ea\u7af9\u6756\uff0c\u6cf0\u5c71\u538b\u9876\u4e00\u822c\uff0c\u6253\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u662f\u7af9\u6756\u7fc1\uff0c\u4e09\u5bab\u4e3b\u8eab\u4fa7\u90a3\u4e2a\u624b\u63e1\u7af9\u6756\u7684\u8001\u4eba\u3002 \u3000\u3000\u63d0\u8d77\u7af9\u6756\u7fc1\uff0c\u4ea6\u662f\u5a01\u540d\u8d6b\u8d6b\uff0c\u8a89\u6ee1\u6c5f\u6e56\u7684\u4eba\u7269\uff0c\u53ea\u53ef\u60dc\u4e3a\u8001\u4e0d\u4fee\uff0c\u7adf\u4e3a\u4e09\u5bab\u4e3b\u5a9a\u529f\u6240\u60d1\uff0c\u4e3a\u4e86\u80fd\u4e00\u4eb2\u82b3\u6cfd\uff0c\u7adf\u7136\u5c48\u4ece\u4e3a\u4e09\u5bab\u4e3b\u7684\u4ece\u536b\u3002 \u3000\u3000\u90a3\u4e00\u6280\u4e4b\u5a01\uff0c\u5c55\u51fa\u4e86\u4ed6\u4e00\u8eab\u529f\u529b\u6210\u5c31\u3002 \u3000\u3000\u53ea\u53ef\u60dc\u6c5f\u67ab\u6740\u673a\u65e9\u52a8\uff0c\u53ea\u89c1\u4ed6\u8eab\u5b50\u4e00\u8f6c\uff0c\u7fe0\u7389\u5200\u5ffd\u7136\u63a2\u51fa\u3002 \u3000\u3000\u4e00\u5200\u7834\u5589\uff0c\u9c9c\u8840\u98d8\u5c04\u800c\u51fa\u3002 \u3000\u3000\u4eba\u5df2\u4e2d\u5200\u800c\u4ea1\uff0c\u7af9\u6756\u624d\u51fb\u843d\u5728\u96ea\u5730\u4e0a\uff0c\u6709\u5982\u8c46\u6811\u5012\u4e0b\uff0c\u6fc0\u8d77\u4e00\u5927\u7247\u79ef\u96ea\uff0c\u5f25\u6f2b\u4e86\u6570\u652f\u65b9\u5706\u3002 \u3000\u3000\u7af9\u6756\u7fc1\u7684\u5c38\u4f53\uff0c\u4e5f\u968f\u7740\u98de\u8d77\u7684\u79ef\u96ea\u5012\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u4e09\u5bab\u4e3b\u9707\u9a87\u6781\u4e86\uff0c\u5979\u5df2\u5b8c\u5168\u76f8\u4fe1\u6c5f\u67ab\u53ef\u4ee5\u6740\u4e86\u5979\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u7ad9\u5728\u4e00\u4fa7\u7684\u4e00\u54c1\u5200\uff0c\u4e5f\u751f\u51fa\u754f\u60e7\uff0c\u4ed6\u65e9\u5df2\u6697\u8fd0\u529f\u529b\uff0c\u51c6\u5907\u9a6d\u5200\u4e00\u51fb\uff0c\u6740\u4e86\u6c5f\u67ab\uff0c\u4f46\u7af9\u6756\u7fc1\u5374\u65e9\u4e86\u4e00\u5239\u53d1\u52a8\u3002 \u3000\u3000\u770b\u4e0d\u51fa\u6c5f\u67ab\u7528\u7684\u4ec0\u4e48\u8eab\u6cd5\uff0c\u600e\u4f1a\u90a3\u4e48\u8f7b\u5de7\u7075\u52a8\u7684\u907f\u5f00\u4e86\u5a01\u52bf\u60ca\u4eba\u7684\u4e00\u6756\uff0c\u4e5f\u770b\u4e0d\u51fa\u6c5f\u67ab\u51fa\u5200\u7684\u624b\u6cd5\uff0c\u90a3\u4e48\u968f\u624b\u4e00\u6325\uff0c\u5c31\u628a\u4e00\u4e2a\u6c5f\u6e56\u58f0\u5a01\u751a\u7740\u7684\u4eba\u7ed9\u5bb0\u4e86\u3002 \u3000\u3000\u4e00\u54c1\u5200\u6536\u4e86\u5200\uff0c\u800c\u4e14\u60f0\u4e0d\u81ea\u7981\u7684\u5411\u540e\u9000\u4e00\u4e24\u6b65\u3002 \u3000\u3000\u201c\u6c5f\u5e06\uff0c\u6211\u8ba4\u8f93\u4e86\uff0c\u4f60\u53ef\u4ee5\u8d70\u4e86\u3002\u2026\u2026\u201d \u3000\u3000\u4e09\u5bab\u4e3b\u7a81\u7136\u60f3\u5230\u4e86\u8bdd\u6709\u8bed\u75c5\uff0c\u6025\u6025\u4f4f\u53e3\u3002 \u3000\u3000\u201c\u8c22\u4e86\u2026\u2026\u201d\u6c5f\u67ab\u76ee\u5149\u4e00\u63a0\u7af9\u6756\u7fc1\u7684\u5c38\u4f53\uff0c\u63a5\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u6709\u5f88\u6076\u6bd2\u7684\u9634\u8c0b\uff0c\u6240\u4ee5\uff0c\u4f60\u66f4\u8be5\u6b7b\uff01\u201d \u3000\u3000\u4f46\u95fb\u5f20\u56db\u59d1\u7684\u58f0\u97f3\uff0c\u4f20\u4e86\u8fc7\u6765\uff0c\u9053\uff1a\u201c\u5c0f\u60c5\u90ce\uff0c\u522b\u6740\u5979\uff0c\u8ba9\u5979\u628a\u4f60\u5f97\u5a01\u540d\u5e26\u56de\u5230\u4ed6\u4eec\u7684\u6838\u5fc3\u4e2d\u53bb\uff0c\u4f7f\u4ed6\u4eec\u8eab\u4efd\u66b4\u9732\u51fa\u6765\u3002 \u3000\u3000\u518d\u8bf4\uff0c\u4f60\u4e5f\u8be5\u548c\u5979\u6597\u6597\u5a9a\u529f\uff0c\u5979\u5df2\u5fc3\u5bd2\uff0c\u4f60\u5982\u80fd\u5f81\u670d\u4e86\u5979\uff0c\u4e5f\u662f\u4e2a\u6700\u597d\u7684\u81c2\u52a9\uff0c\u4f55\u51b5\uff0c\u6211\u8fd8\u8981\u770b\u770b\u5979\u6709\u4e9b\u4ec0\u4e48\u9634\u8c0b\uff0c\u4e5f\u597d\u60f3\u51fa\u4e2a\u7834\u89e3\u4e4b\u6cd5\u3002\u201d \u3000\u3000\u6c5f\u67ab\u7684\u5200\u5df2\u4e3e\u8d77\uff0c\u6307\u7740\u4e09\u5bab\u4e3b\uff0c\u4f46\u5374\u7f13\u7f13\u6536\u4e86\u56de\u6765\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u597d\uff01\u6211\u8981\u8bd5\u8bd5\u4f60\u7684\u5a9a\u4eba\u4e4b\u672f\uff0c\u518d\u6740\u4f60\u4e0d\u8fdf\u3002\u201d \u3000\u3000\u5ffd\u7136\u8f6c\u8eab\u4e00\u8dc3\uff0c\u7fe0\u7389\u5200\u5316\u6210\u4e00\u9053\u78a7\u5149\u6325\u51fa\uff0c\u51b7\u950b\u8fc7\u5904\uff0c\u4e24\u58f0\u95f7\u54fc\uff0c\u4e00\u54c1\u5200\u56db\u4e2a\u5f1f\u5b50\uff0c\u6709\u4e24\u4e2a\u54bd\u5589\u7834\u88c2\uff0c\u5012\u6454\u4e8e\u5730\u3002 \u3000\u3000\u5200\u52bf\u4e00\u6325\uff0c\u56db\u4e2a\u5f1f\u5b50\uff0c\u6b7b\u4e86\u4e24\u4e2a\uff0c\u4ed6\u4eec\u90fd\u662f\u7ec3\u5200\u4e8c\u5341\u51e0\u5e74\u7684\u5200\u9053\u9ad8\u624b\uff0c\u7adf\u7136\u65e0\u80fd\u63a5\u4e0b\u4e00\u51fb\u3002 \u3000\u3000\u90a3\u5730\u65b9\u672c\u662f\u4e00\u54c1\u5200\u7ad9\u7684\u4f4d\u7f6e\uff0c\u4ed6\u9000\u4e86\u4e24\u6b65\uff0c\u521a\u597d\u8eb2\u5728\u4e86\u4e24\u4e2a\u5f1f\u5b50\u7684\u8eab\u540e\u3002 \u3000\u3000\u7b49\u4e00\u54c1\u5200\u548c\u4e24\u4e2a\u672a\u6b7b\u4e8e\u5200\u4e0b\u7684\u5f1f\u5b50\u60f3\u5230\u81ea\u4fdd\uff0c\u62bd\u5200\u51fa\u9798\u65f6\uff0c\u6c5f\u67ab\u4eba\u5df2\u6d88\u5931\u4e8e\u591c\u8272\u4e2d\u3002 \u3000\u3000\u4e09\u5bab\u4e3b\u6697\u6697\u5401\u4e00\u53e3\u6c14\uff0c\u770b\u770b\u4e00\u54c1\u5200\u4e24\u4e2a\u5f1f\u5b50\u7684\u5c38\u4f53\uff0c\u5fc3\u4e2d\u53cd\u800c\u6709\u8f7b\u677e\u4e4b\u611f\uff0c\u5fd6\u9053\uff1a\u201c\u4f60\u4e00\u54c1\u5200\u4e5f\u683d\u4e86\u8fd9\u4e48\u4e00\u4e2a\u5927\u8ddf\u5934\uff0c\u5927\u6982\u4e0d\u4f1a\u8ba5\u7b11\u6211\u4e86\u3002\u201d \u3000\u3000\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u53e3\u4e2d\u8bf4\u9053\uff1a\u201c\u6881\u5144\uff0c\u90a3\u662f\u4ec0\u4e48\u5200\u6cd5\uff0c\u600e\u4e48\u7af9\u6756\u7fc1\u7adf\u7136\u63a5\u4e0d\u4e0b\u4e00\u62db\uff1f\u201d \u3000\u3000\u201c\u91d1\u4e39\u4e66\u751f\u7684\u2018\u9501\u5589\u4e00\u5200\u2019\uff0c\u4e5f\u53eb\u2018\u9501\u5589\u4e00\u5251\u2019\u2026\u2026\u201d\u4e00\u54c1\u5200\u8bf4\uff1a \u3000\u3000\u201c\u5200\u4e5f\u7f62\u3001\u5251\u4e5f\u7f62\uff0c\u8fd9\u4e00\u62db\u7528\u51fa\u6765\uff0c\u5c31\u6ca1\u6709\u4eba\u80fd\u591f\u907f\u5f00\uff0c\u80fd\u591f\u4e0d\u6b7b\uff0c\u5df2\u7ecf\u662f\u4e0a\u4e0a\u5927\u5409\u4e86\u3002\u201d \u3000\u3000\u201c\u5c0f\u59b9\u611f\u89c9\u7740\u5f88\u5947\u602a\uff0c\u65e2\u7136\u65e9\u5df3\u77e5\u9053\u4e86\u6709\u8fd9\u4e00\u62db\u53d6\u547d\u5200\u6cd5\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u7814\u7a76\u51fa\u7834\u89e3\u4e4b\u9053\u5462\uff1f\u201d \u3000\u3000\u201c\u4e09\u5bab\u4e3b\uff0c\u5927\u5bb6\u90fd\u77e5\u9053\u8fd9\u4e00\u5200\u5389\u5bb3\uff0c\u51fa\u5fc5\u4f24\u4eba\uff0c\u53ef\u662f\uff0c\u6ca1\u6709\u4eba\u770b\u5f97\u6e05\u695a\u8fd9\u4e00\u62db\u662f\u5982\u4f55\u53d8\u5316\u51fa\u6765\u7684\uff0c\u60f3\u8981\u7814\u51fa\u4e00\u4e2a\u7834\u89e3\u4e4b\u6cd5\uff0c\u90a3\u5c31\u5343\u96be\u4e07\u96be\u4e86\uff01\u201d \u3000\u3000\u201c\u5509\uff01\u6700\u53ef\u60dc\u7684\u662f\u8ba9\u4ed6\u4eec\u8dd1\u6389\u4e86\uff0c\u2026\u2026\u201d\u4e09\u5bab\u4e3b\u9053\uff1a\u201c\u6b20\u4e86\u4e00\u4efd\u5929\u5927\u7684\u4eba\u60c5\uff0c\u5374\u88ab\u654c\u4eba\u5b89\u5168\u7684\u64a4\u8d70\uff01\u201d \u3000\u3000\u201c\u628a\u65f6\u95f4\u5b89\u6392\u5728\u4e8c\u66f4\u9759\u591c\uff0c\u81ea\u96be\u514d\u53eb\u4eba\u6000\u7591\uff0c\u2026\u2026\u201d\u4e00\u54c1\u5200\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u7a0d\u6709\u6c5f\u6e56\u9605\u5386\u7684\u4eba\uff0c\u4e5f\u4f1a\u60f3\u5230\uff0c\u591c\u6218\u4e0d\u6311\u706f\uff0c\u9669\u8c0b\u6697\u4e2d\u884c\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u600e\u4e48\u4f1a\u77e5\u9053\uff0c\u90a3\u4e48\u591a\u9ad8\u624b\uff0c\u7adf\u7136\u662f\u4e0d\u582a\u4e00\u51fb\u2026\u2026\u201d\u4e09\u5bab\u4e3b\u9053\uff1a\u201c\u5927\u540d\u9f0e\u9f0e\u7684\u5929\u865a\u5b50\uff0c\u88ab\u9152\u8089\u548c\u5c1a\u4e00\u62f3\u6253\u6b7b\uff0c\u4e24\u4e2a\u5357\u8352\u6bd2\u4eba\uff0c\u4e5f\u88ab\u4e94\u798f\u7167\u547d\u5708\uff0c\u4e00\u4e0b\u5b50\u51fb\u6bd9\uff0c\u795e\u3001\u9b3c\u53cc\u5200\u4e5f\u4e0d\u8fc7\u6491\u5230\u767e\u62db\uff0c\u5982\u6b64\u591a\u7684\u9ad8\u624b\uff0c\u5982\u6b64\u4e00\u4e2a\u8d25\u6cd5\uff0c\u8c01\u4f1a\u6599\u5f97\u5230\u5462\uff1f\u201d \u3000\u3000\u201c\u7af9\u679d\u7fc1\u8fde\u4e00\u5200\u4e5f\u672a\u63a5\u4e0b\u2026\u2026\u201d\u4e00\u54c1\u5200\u8bf4\u3002 \u3000\u3000\u201c\u4f60\u90a3\u4e24\u4e2a\u5f1f\u5b50\uff0c\u7adf\u4e5f\u672a\u63a5\u4e0b\u4e00\u5200\u2026\u2026\u201d\u4e09\u5bab\u4e3b\u4e0d\u7518\u793a\u5f31\u5730\u9053\uff1a\u201c\u8fd9\u4e00\u4ed7\u5b9e\u5728\u662f\u6253\u7684\u5fc3\u6709\u4e0d\u7518\uff0c\u4e5f\u6253\u7684\u592a\u4e22\u4eba\u4e86\uff01\u201d \u3000\u3000\u4e00\u54c1\u5200\u6697\u4e2d\u5401\u53e3\u6c14\uff0c\u5fcd\u4e86\u5fc3\u4e2d\u6012\u706b\uff0c\u9053\uff1a\u201c\u8bf4\u7684\u4e5f\u662f\u554a\uff01\u4e0d\u8fc7\uff0c\u6881\u67d0\u53ef\u5426\u8bf7\u6559\u4e00\u4e0b\u4e09\u5bab\u4e3b\uff1f\u201d \u3000\u3000\u201c\u597d\uff01\u4f60\u8bf4\u554a\uff01\u6211\u4e00\u5b9a\u636e\u5b9e\u56de\u7b54\u3002\u201d \u3000\u3000\u201c\u4f60\u7a76\u7adf\u5e03\u7f6e\u7684\u4ec0\u4e48\u514b\u654c\u4ea1\u7b56\uff1f\u8981\u6211\u4eec\u628a\u640f\u6740\u62d6\u5ef6\u5230\u4e8c\u66f4\u65f6\u5206\uff1f\u2026\u2026\u201d\u4e00\u54c1\u5200\u8bf4\uff0c\u201c\u8fd9\u4f3c\u662f\u5df2\u88ab\u4ed6\u4eec\u770b\u7a7f\u4e86\uff0c\u6240\u4ee5\uff0c\u63d0\u524d\u64a4\u8d70\uff01\u201d \u3000\u3000\u4e09\u5bab\u4e3b\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u6211\u53ef\u4ee5\u544a\u8bc9\u6881\u5144\uff0c\u4f46\u4f60\u8981\u7b54\u5e94\u6211\u4fdd\u5b88\u79d8\u5bc6\uff0c\u4e0d\u5f97\u6cc4\u9732\u51fa\u53bb\u3002\u201d \u3000\u3000\u4e00\u54c1\u5200\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u597d\u5427\uff01\u6881\u67d0\u4eba\u7b54\u5e94\u4f60\u3002\u201d \u3000\u3000\u201c\u90a3\u5c31\u8bf7\u9644\u8033\u8fc7\u6765\u2026\u2026\u201d \u3000\u3000\u4e00\u54c1\u5200\u679c\u7136\u4f38\u8fc7\u5934\u53bb\uff0c\u4f46\u624b\u4e2d\u4e4b\u5200\uff0c\u5374\u4e5f\u4f38\u4e86\u8fc7\u53bb\uff0c\u6307\u5728\u4e09\u5bab\u4e3b\u7684\u5c0f\u8179\u4e0a\u3002 \u3000\u3000\u4e09\u5bab\u4e3b\u5fc3\u4e2d\u4e00\u9707\uff0c\u6697\u9053\uff1a\u201c\u8fd9\u8001\u5bb6\u4f19\uff0c\u9634\u7684\u72e0\u554a\uff01\u201d \u3000\u3000\u88c5\u4f5c\u4e0d\u77e5\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u653e\u86ca\uff0c\u591c\u6697\u4e4b\u4e2d\uff0c\u4e0d\u8bba\u6b66\u529f\u4f55\u7b49\u9ad8\u5f3a\u7684\u4eba\uff0c\u4e5f\u65e0\u6cd5\u9632\u6b62\u86ca\u7684\u4fb5\u88ad\uff01\u201d \u3000\u3000\u4e00\u54c1\u5200\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u90a3\u4e0d\u662f\u8fde\u6211\u4eec\u4e5f\u7b97\u8ba1\u8fdb\u53bb\u4e86\u5417\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\uff0c\u6211\u4f1a\u5728\u4e8b\u540e\u5949\u4e0a\u89e3\u836f\uff0c\u2026\u2026\u201d\u4e09\u5bab\u4e3b\u9053\uff1a\u201c\u4e0d\u4f1a\u8ba9\u6881\u5144\u53d7\u5bb3\uff01\u201d \u3000\u3000\u4e00\u54c1\u5200\u53cc\u76ee\u76ef\u6ce8\u5728\u4e09\u5bab\u4e3b\u7684\u8138\u4e0a\uff0c\u770b\u4e86\u826f\u4e45\uff0c\u5ffd\u7136\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5982\u662f\u4f60\u4e0d\u7ed9\u6211\u4eec\u89e3\u836f\uff0c\u8001\u592b\u5c82\u4e0d\u662f\u4e5f\u8981\u7cca\u7cca\u6d82\u5f90\u7684\u6b7b\u4e86\uff1f\u201d \u3000\u3000\u201c\u90a3\u600e\u4e48\u4f1a\u5462\uff01\u2026\u2026\u201d\u738b\u5bab\u4e3b\u5fae\u7b11\u5982\u82b1\u5730\u9053\uff1a\u201c\u4f60\u662f\u6211\u8bf7\u6765\u7684\u91cd\u8981\u5e2e\u624b\uff0c\u6211\u600e\u4e48\u4f1a\u5f03\u4f60\u4e0d\u987e\u3002\u2026\u2026\u201d \u3000\u3000\u597d\u5bb6\u4f19\uff0c\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u7a81\u7136\u65bd\u5c55\u51fa\u5a9a\u672f\uff0c\u8138\u4e0a\u6d6e\u52a8\u51fa\u4e00\u7247\u64a9\u4eba\u5fc3\u706b\u7684\u5a9a\u6001\u3002 \u3000\u3000\u591c\u8272\u867d\u7136\u5e7d\u6697\uff0c\u4f46\u53cc\u65b9\u8ddd\u79bb\u592a\u8fd1\u4e86\u3002 \u3000\u3000\u4e00\u54c1\u5200\u81ea\u529b\u5f88\u597d\uff0c\u7adf\u7136\u770b\u7684\u5f88\u6e05\u695a\uff0c\u4e0d\u7981\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4ee5\u8001\u592b\u8fd9\u4efd\u5b9a\u529b\uff0c\u7adf\u7136\u4e5f\u4e3a\u4e4b\u6026\u7136\u5fc3\u52a8\uff0c\u4e09\u5bab\u4e3b\u7684\u5a9a\u672f\uff0c\u679c\u7136\u662f\u975e\u540c\u5c0f\u53ef\uff01\u201d \u3000\u3000\u201c\u6881\u5144\u7684\u5b9a\u529b\u4e4b\u5f3a\uff0c\u5c0f\u59b9\u662f\u4ece\u672a\u89c1\u8fc7\uff0c\u201d\u4e09\u5bab\u4e3b\u9053\uff1a\u201c\u628a\u6211\u89c6\u82e5\u65e0\u7779\u7684\u7537\u4eba\uff0c\u9664\u4f60\u4e4b\u5916\uff0c\u518d\u65e0\u4ed6\u4eba\uff01\u201d \u3000\u3000\u679c\u7136\uff0c\u4e00\u54c1\u5200\u4e24\u4e2a\u5f1f\u5b50\u5df2\u770b\u7684\u773c\u775b\u53d1\u76f4\uff0c\u90a3\u79cd\u8dc3\u8dc3\u6b32\u52a8\u7684\u795e\u60c5\uff0c\u5927\u6709\u7acb\u523b\u6251\u8fc7\u53bb\u7684\u7528\u5fc3\u3002 \u3000\u3000\u4e00\u54c1\u5200\u5de6\u624b\u6325\u52a8\uff0c\u70b9\u4e86\u4e24\u4e2a\u5f1f\u5b50\u7684\u7a74\u9053\uff0c\u631f\u4e8e\u53cc\u808b\uff0c\u9053\uff1a\u201c\u5e74\u8f7b\u4eba\u5b9a\u529b\u4e0d\u591f\uff0c\u8001\u592b\u4e0d\u80fd\u8ba9\u4ed6\u4eec\u51fa\u4e11\uff0c\u4e24\u5177\u5c38\u4f53\uff0c\u5c31\u62dc\u6258\u4e09\u5bab\u4e3b\u63a9\u57cb\u4e00\u4e0b\u4e86\uff01\u201d \u3000\u3000\u8bf4\u5b8c\u8bdd\uff0c\u8f6c\u8eab\u5c31\u8dd1\uff0c\u4e5f\u4e0d\u7ba1\u4e09\u5bab\u4e3b\u662f\u5426\u5e94\u5141\u3002 \u3000\u3000\u671b\u7740\u4e00\u54c1\u5200\u8fdc\u53bb\u7684\u80cc\u5f71\uff0c\u4e09\u5bab\u4e3b\u4e5f\u4e0d\u7981\u6697\u6697\u4f69\u670d\uff0c\u9053\uff1a\u201c\u6881\u8001\u5934\u8fd9\u4efd\u5b9a\u529b\uff0c\u6bd4\u4ed6\u7684\u5200\u6cd5\uff0c\u53eb\u4eba\u656c\u4f69\u4e86\u3002\u201d \u3000\u3000\u201c\u4e09\u5bab\u4e3b\u8bef\u4f1a\u4e86\u3002\u2026\u2026\u201d \u3000\u3000\u4e00\u4e2a\u5168\u8eab\u9ed1\u8863\u7684\u4e2d\u5e74\u4eba\uff0c\u7a81\u7136\u7531\u591c\u8272\u4e2d\u5192\u4e86\u51fa\u6765\u3002 \u3000\u3000\u6b64\u4eba\u65e2\u7626\u53c8\u9ad8\uff0c\u7ad9\u5728\u591c\u8272\u4e2d\uff0c\u5c31\u50cf\u5728\u96ea\u5730\u4e0a\u63d2\u4e86\u4e00\u6839\u7af9\u7aff\u3002 \u3000\u3000\u4e09\u5bab\u4e3b\u5bf9\u8fd9\u4eba\u7a81\u5140\u7684\u51fa\u73b0\uff0c\u5e76\u672a\u6709\u60ca\u5f02\u4e4b\u611f\uff0c\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u6765\u4e86\u591a\u4e45\u5566\uff1f\u201d \u3000\u3000\u201c\u4e0d\u4e45\uff01\u2026\u2026\u201d\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u5c31\u5728\u4e09\u5bab\u4e3b\u65bd\u5c55\u5a9a\u672f\u7684\u8fb0\u5149\uff0c\u5728\u4e0b\u521a\u597d\u8d76\u4e0a\u3002\u201d \u3000\u3000\u201c\u4f60\u7ea6\u7684\u4eba\u5462\uff1f\u201d \u3000\u3000\u201c\u5df2\u7ecf\u5230\u4e86\uff0c\u2026\u2026\u201d\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u4ed6\u4eec\u5df2\u5206\u5e03\u4e24\u4fa7\uff0c\u53ea\u7b49\u4e09\u5bab\u4e3b\u4e00\u58f0\u4ee4\u4e0b\uff0c\u7acb\u523b\u653e\u51fa\u6d3b\u86ca\u3002\u201d \u3000\u3000\u201c\u6ca1\u6709\u7528\u4e86\u2026\u2026\u201d\u4e09\u5bab\u4e3b\u9053\uff1a\u201c\u654c\u4eba\u5df2\u7ecf\u8d70\u4e86\uff0c\u7ea6\u7684\u4eba\uff0c\u4e5f\u6b7b\u7684\u6b7b\uff0c\u9003\u7684\u9003\u4e86\uff0c\u73b0\u5728\uff0c\u53ea\u6709\u8377\u3001\u83f1\u3001\u83ca\u3001\u6842\u56db\u4e2a\u4e2b\u5934\u548c\u6211\u4e86\u3002\u201d \u3000\u3000\u201c\u4e09\u5bab\u4e3b\u4e0d\u7528\u7070\u5fc3\u2026\u2026\u201d\u9ed1\u8863\u4eba\u4f4e\u58f0\u63a5\u9053\uff1a\u201c\u82d7\u7586\u6d3b\u86ca\uff0c\u4e3a\u86ca\u4e2d\u4e4b\u6700\uff0c\u53ea\u8981\u5728\u4e94\u5341\u4e08\u4e4b\u5185\u653e\u51fa\uff0c\u4f1a\u81ea\u52a8\u8ffd\u4eba\u6f5c\u5165\u4f53\u5185\uff0c\u5176\u4eba\u7684\u751f\u6b7b\uff0c\u5c31\u88ab\u653e\u86ca\u4eba\u6240\u63a7\u5236\uff0c\u6c38\u4f5c\u4e0d\u4e8c\u4e4b\u81e3\u3002\u201d \u3000\u3000\u4e09\u5bab\u4e3b\u56db\u987e\u4e86\u4e00\u773c\uff0c\u5fd6\u9053\uff1a\u201c\u539f\u60f3\u591c\u95f4\u653e\u86ca\uff0c\u4f7f\u4eba\u65e0\u6cd5\u9884\u9632\uff0c\u4e00\u4e3e\u4e4b\u95f4\uff0c\u5c3d\u628a\u654c\u4eba\u7eb3\u5165\u638c\u63e1\uff0c\u8fde\u84dd\u51e4\u3001\u9752\u98ce\u4e24\u4e2a\u4e2b\u5934\uff0c\u4e5f\u4e00\u5e76\u7b97\u8ba1\u5728\u5185\uff0c\u5374\u4e0d\u6599\u6c5f\u67ab\u77e5\u673a\u800c\u9041\uff0c\u5929\u738b\u95e8\u4e2d\u53cc\u51e4\uff0c\u4f3c\u4ea6\u79bb\u53bb\u3002 \u3000\u3000\u4e3a\u4eca\u4e4b\u8ba1\uff0c\u53ea\u6709\u5148\u5bf9\u6c5f\u67ab\u7b49\u4eba\u4e0b\u624b\uff0c\u5982\u82e5\u6c5f\u67ab\u53d7\u5236\uff0c\u5176\u4f59\u7684\u4eba\u5927\u6982\u5c31\u53ef\u4ee5\u4e0d\u6218\u800c\u964d\u4e86\u3002\u201d \u3000\u3000\u5fc3\u4e2d\u6253\u5b9a\u4e86\u4e3b\u610f\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8bf4\u7684\u4e5f\u662f\uff0c\u53ea\u4e0d\u77e5\uff0c\u4ed6\u4eec\u80af\u4e0d\u80af\u628a\u5f79\u86ca\u4e4b\u6cd5\uff0c\u4f20\u6388\u7ed9\u6211\u5462\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u2026\u2026\u201d\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u8336\u3001\u9152\u3001\u996d\u3001\u83dc\u4e4b\u4e2d\u4e0b\u86ca\uff0c\u53ea\u8981\u53d6\u5f97\u86ca\u6bd2\u5373\u6210\uff0c\u4f46\u5f79\u65bd\u6d3b\u86ca\uff0c\u5374\u662f\u4e07\u86ca\u95e8\u4e2d\u4e0d\u4f20\u4e4b\u5bc6\uff0c\u80af\u4e0d\u80af\u4f20\u6388\u7ed9\u4f60\uff1f\u5728\u4e0b\u5b9e\u65e0\u628a\u63e1\uff0c\u8981\u548c\u4ed6\u4eec\u5546\u91cf\u4e00\u4e0b\u624d\u6210\u3002\u201d \u3000\u3000\u4e09\u5bab\u4e3b\u7b11\u9053\uff1a\u201c\u521a\u624d\uff0c\u4f60\u8bf4\u6211\u8bef\u4f1a\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u4e00\u54c1\u5200\u6881\u9b41\u2026\u2026\u201d\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u4ed6\u65e9\u5df2\u4e0d\u80fd\u4eba\u9053\uff0c\u6240\u4ee5\uff0c\u4ed6\u53ef\u4ee5\u6297\u62d2\u4e09\u5bab\u4e3b\u7684\u5a9a\u672f\u3002\u201d \u3000\u3000\u201c\u4f60\u5462\uff1f\u201d\u4e09\u5bab\u4e3b\u7b11\u9053\uff1a\u201c\u662f\u4e0d\u662f\u4e5f\u2026\u2026\u201d \u3000\u3000\u201c\u5728\u4e0b\u867d\u7136\u7626\u4e86\u4e00\u4e9b\uff0c\u201d\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u4f46\u5374\u6b63\u5e38\u7684\u5f88\u3002\u201d \u3000\u3000\u201c\u597d\u554a\uff01\u53ea\u8981\u4f60\u80fd\u8bf4\u670d\u4e07\u86ca\u95e8\u4e2d\u4eba\uff0c\u80af\u628a\u5f79\u65bd\u6d3b\u86ca\u4e4b\u6cd5\uff0c\u4f20\u6388\u7ed9\u6211\u2026\u2026\u201d\u4e09\u5bab\u4e3b\u5a9a\u7b11\u5982\u82b1\u5730\u9053\uff1a\u201c\u5c0f\u59b9\u5f53\u8bbe\u5bb4\u95fa\u623f\uff0c\u7559\u4f60\u4e09\u65e5\u591c\u5c3d\u60c5\u4e4b\u6b22\u3002\u2026\u2026\u201d \u3000\u3000\u76ee\u5149\u4e00\u63a0\u8eab\u4fa7\u7684\u8377\u82b1\u3001\u83f1\u82b1\u3001\u83ca\u82b1\u3001\u6842\u82b1\uff0c\u63a5\u9053\uff1a\u201c\u590f\u3001\u79cb\u56db\u7f8e\u5973\uff0c\u4e5f\u4f34\u6211\u4f8d\u9152\u6df1\u56f4\uff0c\u4e94\u7f8e\u5973\u76f8\u966a\uff0c\u8c2d\u5144\uff0c\u90a3\u53ef\u662f\u4eba\u4e16\u96be\u5f97\u7684\u8273\u798f\u554a\uff01\u201d \u3000\u3000\u201c\u679c\u7136\u662f\u52a8\u4eba\u5fc3\u5f26\u7684\u91cd\u8d4f\u2026\u2026\u201d\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u91cd\u8d4f\u4e4b\u4e0b\uff0c\u5fc5\u6709\u52c7\u592b\uff0c\u8c2d\u67d0\u4eba\u5c06\u5c3d\u6211\u4e4b\u80fd\u53bb\u8bd5\u4e00\u8bd5\u4e86\u3002\u201d \u3000\u3000\u8f6c\u8eab\u4e00\u8dc3\uff0c\u6d88\u5931\u4e8e\u591c\u8272\u4e4b\u4e2d\u3002 \u3000\u3000\u738b\u5bab\u4e3b\u5a07\u58f0\u8bf4\u9053\uff1a\u201c\u8c2d\u5144\uff0c\u5c0f\u59b9\u5728\u6d1b\u9633\u5c45\u4e2d\u606d\u5019\u4f73\u97f3\u4e86\u3002\u201d \u3000\u3000\u5fc3\u4e2d\u5374\u662f\u6697\u6697\u9a82\u9053\uff1a\u201c\u7626\u7684\u5168\u8eab\u522e\u4e0d\u4e0b\u534a\u65a4\u51c0\u8089\uff0c\u8fd8\u60f3\u4f60\u59d1\u5976\u5976\u7684\u597d\u4e8b\uff1f\u771f\u662f\u9a6c\u4e0d\u77e5\u8138\u957f\uff01\u201d \u3000\u3000\u6d1b\u9633\u5c45\u4e2d\u4ecd\u7136\u4fdd\u6301\u4e86\u76f8\u5f53\u7684\u5e73\u9759\uff0c\u4f3c\u662f\u53cc\u65b9\u7684\u4eba\uff0c\u90fd\u4e0d\u613f\u7834\u574f\u8fd9\u4e2a\u5730\u65b9\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u8fd9\u5ea7\u8bbe\u8ba1\u7279\u6b8a\uff0c\u7b51\u5efa\u7cbe\u7f8e\u7684\u5730\u65b9\uff0c\u7f57\u96c6\u4e86\u6c5f\u5357\u82b1\u6811\uff0c\u6f20\u5317\u94c1\u677e\uff0c\u82b1\u56e2\u9526\u7c07\uff0c\u7f8e\u4e0d\u80dc\u6536\uff0c\u5df2\u6210\u4e3a\u957f\u5b89\u57ce\u4e2d\u7b2c\u4e00\u7f8e\u666f\u3002 \u3000\u3000\u53ea\u4e0d\u8fc7\uff0c\u8fd1\u65e5\u53cc\u65b9\u51b2\u7a81\u6fc0\u70c8\uff0c\u56db\u5b63\u82b1\u5a62\u8ddf\u7740\u51fa\u52a8\uff0c\u4f7f\u5f97\u9152\u9986\u90e8\u4e2d\u7684\u751f\u610f\uff0c\u6e05\u6de1\u4e86\u4e0d\u5c11\u3002 \u3000\u3000\u9093\u98de\u4eb2\u4e34\u53a8\u4e0b\uff0c\u67e5\u770b\u8fc7\u9152\u8089\u9e21\u9e2d\uff0c\u624d\u5429\u5490\u51c6\u5907\u51e0\u684c\u9152\u5e2d\uff0c\u4f46\u4eba\u5374\u4e0d\u80af\u79bb\u5f00\uff0c\u4eb2\u81ea\u628a\u5b88\u53a8\u623f\uff0c\u4ee5\u9632\u4eba\u4e0b\u6bd2\u3002 \u3000\u3000\u7247\u523b\u4e4b\u540e\uff0c\u5201\u9e4f\u3001\u80e1\u840d\uff0c\u4e5f\u8fdb\u5165\u4e86\u53a8\u623f\uff0c\u5e03\u6210\u4e86\u4e00\u5ea7\u4e25\u5bc6\u7684\u76d1\u89c6\u7f51\u3002 \u3000\u3000\u4e24\u4e2a\u5357\u8352\u9cde\u8863\u602a\u4eba\u51fa\u73b0\u5343\u4e07\u6885\u576a\u6218\u9635\u4e4b\u95f4\uff0c\u63d0\u9192\u4e86\u5927\u548c\u5c1a\uff0c\u4e0d\u5f97\u4e0d\u8c28\u614e\u4ece\u4e8b\uff0c\u4ee5\u514d\u4e2d\u6bd2\u3002 \u3000\u3000\u53cc\u65b9\u5df2\u662f\u751f\u6b7b\u5927\u654c\uff0c\u4f46\u5374\u4ecd\u7136\u540c\u4f4f\u5728\u6d1b\u9633\u5c45\u4e2d\uff0c\u5c45\u5ba4\u76f8\u63a5\uff0c\u4e0d\u8fc7\u6570\u4e08\u8ddd\u79bb\uff0c\u540c\u98df\u4e00\u4e2a\u53a8\u623f\u7684\u9152\u996d\uff0c\u9910\u70b9\uff0c\u8c01\u4e5f\u4e0d\u6562\u5927\u610f\u4e86\u3002 \u3000\u3000\u53ea\u89c1\u8377\u82b1\u3001\u83f1\u82b1\uff0c\u4e5f\u6b65\u5165\u53a8\u623f\uff0c\u76d1\u89c6\u7740\u53a8\u5b50\u7092\u83dc\u3001\u6e29\u9152\u3002 \u3000\u3000\u597d\u5728\u6d1b\u9633\u5c45\u7684\u53a8\u623f\u5f88\u5927\uff0c\u5f7c\u6b64\u5404\u636e\u4e00\u65b9\uff0c\u8fd8\u80fd\u4fdd\u6301\u7740\u76f8\u5f53\u7684\u8ddd\u79bb\uff0c\u6ca1\u6709\u5f15\u8d77\u51b2\u7a81\u3002 \u3000\u3000\u4e00\u638c\u62db\u9b42\u4e07\u65b9\uff0c\u6ca1\u6709\u91cd\u56de\u6d1b\u9633\u5c45\u6765\uff0c\u4ed6\u53d1\u89c9\u7387\u9886\u7684\u4e00\u80a1\u529b\u91cf\uff0c\u8fde\u4e00\u4e2a\u9152\u8089\u548c\u5c1a\u4e5f\u5bf9\u4ed8\u4e0d\u4e86\uff0c\u53ec\u96c6\u4e86\u5929\u9a6c\u5802\u4e2d\u4eba\uff0c\u5168\u90e8\u64a4\u79bb\u3002 \u3000\u3000\u9152\u83dc\u6446\u9f50\uff0c\u6c5f\u67ab\u628a\u5201\u9e4f\u3001\u9093\u98de\u3001\u80e1\u840d\u3001\u6bb5\u4e5d\uff0c\u90fd\u7559\u4e86\u6765\uff0c\u518d\u52a0\u4e0a\u4e03\u5b9d\u548c\u5c1a\u3001\u6b27\u9633\u662d\u3001\u7f57\u5170\uff0c\u4e00\u5171\u516b\u4eba\u540c\u684c\u5171\u9910\uff0c\u8fd9\u5c31\u4f7f\u5f97\u9093\u98de\u3001\u5201\u9e4f\u6709\u7740\u53d7\u5ba0\u82e5\u60ca\u4e4b\u611f\uff0c\u5c31\u662f\u80e1\u840d\u3001\u6bb5\u4e5d\uff0c\u4e5f\u6709\u7740\u4e8b\u975e\u5bfb\u5e38\u7684\u611f\u89c9\u3002 \u3000\u3000\u679c\u7136\uff0c\u6c5f\u67ab\u656c\u8fc7\u4e86\u4e00\u8f6e\u9152\u540e\uff0c\u8bf4\u9053\uff1a\u201c\u5929\u9a6c\u5802\u548c\u5185\u5bab\u4e00\u7cfb\u4e2d\u4eba\uff0c\u867d\u7136\u4e89\u6597\u6fc0\u70c8\uff0c\u53cc\u65b9\u7684\u9996\u8111\u4eba\u7269\uff0c\u4e5f\u7ed9\u4e88\u4e86\u76f8\u5f53\u7684\u652f\u6301\uff0c\u4f46\u4e00\u65e6\u9047\u4e0a\u5916\u654c\uff0c\u4e24\u5e2e\u4eba\u9a6c\uff0c\u7acb\u523b\u5c31\u5408\u4e8c\u4e3a\u4e00\uff0c\u6350\u5f03\u6210\u89c1\uff0c\u5408\u529b\u67dc\u654c\u3002 \u3000\u3000\u8fd9\u8bf4\u660e\u4e86\u4ed6\u4eec\u4e4b\u95f4\uff0c\u8fd8\u6709\u4e00\u80a1\u795e\u79d8\u7684\u7edf\u6cbb\u7ef4\u7cfb\u529b\u91cf\uff0c\u5929\u738b\u95e8\u663e\u7136\u4e5f\u5728\u8fd9\u4e00\u80a1\u529b\u91cf\u7edf\u9a6d\u4e4b\u4e0b\uff0c\u4e0d\u5f97\u4e0d\u52a8\u5458\u53cc\u51e4\uff0c\u4ee5\u4e3a\u5965\u63f4\u3002\u201d \u3000\u3000\u201c\u5bf9\uff01\u4e00\u54c1\u5200\u6881\u9b41\uff0c\u662f\u5929\u9a6c\u5802\u798f\u5bff\u9662\u4e2d\u516d\u5927\u697c\u4e3b\u4e4b\u2014\u2026\u2026\u201d\u5201\u9e4f\u9053\uff1a\u201c\u5176\u4eba\u6700\u5927\u7684\u7279\u70b9\uff0c\u662f\u4e0d\u8fd1\u5973\u8272\uff0c\u7edd\u4e0d\u4f1a\u53d7\u5230\u4e09\u5bab\u4e3b\u7684\u5a9a\u672f\u86ca\u60d1\uff0c\u51fa\u73b0\u4e8e\u4e07\u6885\u576a\u4e2d\uff0c\u663e\u662f\u53d7\u547d\u800c\u6765\u3002\u201d \u3000\u3000\u201c\u798f\u5bfa\u9662\u4e2d\u7684\u516d\u5927\u697c\u4e3b\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u95ee\u9053\uff1a\u201c\u662f\u4e0d\u662f\u53d7\u4e8e\u603b\u5802\u4e3b\u7684\u5429\u5490\uff1f\u201d \u3000\u3000\u201c\u662f\u7684\uff01\u2026\u2026\u201d\u5201\u9e4f\u9053\uff1a\u201c\u4ed6\u4eec\u90fd\u662f\u6b66\u529f\u7cbe\u7edd\uff0c\u5bf9\u5929\u9a6c\u5802\u6709\u8fc7\u5f88\u5927\u8d21\u732e\u7684\u4eba\uff0c\u5e74\u7eaa\u5927\u4e86\uff0c\u6216\u662f\u559c\u6b22\u6e05\u9759\uff0c\u5c31\u8fdb\u5165\u798f\u5bff\u9662\u4e2d\uff0c\u53d7\u5929\u9a6c\u5802\u7684\u5949\u517b\u3002 \u3000\u3000\u5404\u5c45\u4e00\u697c\uff0c\u6709\u7537\u7ae5\u3001\u5973\u5a62\uff0c\u4e13\u7528\u53a8\u5b50\u4f3a\u5019\uff0c\u6781\u5c3d\u4eab\u53d7\u80fd\u4e8b\uff0c\u4ed6\u4eec\u5e73\u5e38\u4e0d\u53c2\u4e0e\u5802\u4e2d\u4e8b\u52a1\uff0c\u53ea\u6709\u603b\u5802\u4e3b\u624d\u80fd\u8bf7\u5f97\u52a8\u4ed6\u4eec\u3002\u201d \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u4e5f\u7b97\u662f\u5929\u9a6c\u5802\u4e2d\u4eba\u4e86\uff0c\u4f46\u4e3a\u4ec0\u4e48\u4e0d\u548c\u5929\u865a\u5b50\u3001\u4e07\u65b9\uff0c\u4e24\u4f4d\u526f\u603b\u5802\u4e3b\u5408\u4e8e\u4e00\u5904\u5462\uff1f\u201d \u3000\u3000\u201c\u516d\u5927\u697c\u4e3b\uff0c\u5404\u6709\u6210\u5c31\uff0c\u8bba\u8eab\u4efd\uff0c\u4ed6\u4eec\u4e0d\u5728\u526f\u603b\u5802\u4e3b\u4e4b\u4e0b\uff0c\u6b66\u529f\u8fc7\u4e4b\u2026\u2026\u201d\u5201\u9e4f\u9053\uff1a \u3000\u3000\u201c\u4ed6\u4eec\u53d7\u547d\u51fa\u52a8\uff0c\u4e5f\u662f\u72ec\u884c\u5176\u662f\uff0c\u5e26\u7740\u95e8\u4e0b\u5f1f\u5b50\uff0c\u6216\u662f\u697c\u4e2d\u9ad8\u624b\uff0c\u4e0d\u4f1a\u548c\u5929\u9a6c\u5802\u4e2d\u7684\u62a4\u6cd5\u3001\u957f\u8001\u4f1a\u96c6\u8fce\u654c\u3002\u201d \u3000\u3000\u201c\u600e\u4e48\uff1f\u5404\u697c\u4e3b\u8fd8\u6709\u4ed6\u4eec\u81ea\u5df1\u7684\u4eba\u624b\uff1f\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u9053\uff1a\u201c\u90a3\u4e0d\u662f\u6bcf\u4e00\u697c\u90fd\u53ef\u81ea\u6210\u4e00\u80a1\u52bf\u529b\u4e86\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\uff01\u516d\u5927\u697c\u4e3b\u90fd\u5404\u62e5\u6709\u4e00\u80a1\u529b\u91cf\u2026\u2026\u201d\u5201\u9e4f\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u4e5f\u6709\u4e00\u4e2a\u9650\u5236\uff0c\u90a3\u5c31\u662f\u6bcf\u4e00\u697c\u4e3b\u62e5\u6709\u7684\u4eba\u624b\uff0c\u4e0d\u5f97\u8d85\u8fc7\u5341\u4e2a\uff0c\u4eba\u624b\u867d\u7136\u4e0d\u591a\uff0c\u4f46\u5374\u90fd\u662f\u9ad8\u624b\uff0c\u4e5f\u662f\u5404\u5927\u697c\u4e3b\u7684\u4eb2\u4fe1\u3002\u201d \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u5bf9\u798f\u5bff\u9662\u7684\u4e8b\uff0c\u4f3c\u662f\u6781\u611f\u5174\u8da3\uff0c\u95ee\u9053\uff1a\u201c\u798f\u5bff\u9662\u4e2d\uff0c\u6709\u6ca1\u6709\u4e00\u4e2a\u9886\u5934\u7684\u4eba\u5462\uff1f\u201d \u3000\u3000\u201c\u6709\uff01\u798f\u5bff\u9662\u7684\u9662\u4e3b\uff0c\u5c31\u662f\u9886\u5934\u7684\u4eba\uff0c\u2026\u2026\u201d\u5201\u9e4f\u9053\uff1a\u201c\u9664\u4e86\u603b\u5802\u4e3b\u5916\uff0c\u4ed6\u662f\u552f\u4e00\u80fd\u6d3e\u52a8\u516d\u5927\u697c\u4e3b\u7684\u4eba\u4e86\uff0c\u5c31\u7b97\u662f\u603b\u5802\u4e3b\u5427\uff0c\u4e5f\u5f97\u900f\u8fc7\u4ed6\uff0c\u4f20\u8fbe\u4ee4\u8c15\u3002\u201d \u3000\u3000\u201c\u5201\u5144\uff0c\u53ef\u77e5\u9053\u4ed6\u7684\u59d3\u540d\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u51fa\u8eab\u4ec0\u4e48\u95e8\u6d3e\uff1f\u6b66\u529f\u5982\u4f55\uff1f\u201d \u3000\u3000\u201c\u8be6\u7ec6\u7684\u60c5\u5f62\uff0c\u4e0d\u592a\u6e05\u695a\uff0c\u4e0d\u8fc7\uff0c\u5728\u4e0b\u5012\u662f\u89c1\u8fc7\u4ed6\u4e24\u6b21\u2026\u2026\u201d\u5201\u9e4f\u9053\uff1a\u201c\u90a3\u662f\u4e2a\u987b\u53d1\u7686\u767d\u8001\u4eba\uff0c\u9762\u8272\u7ea2\u6da6\uff0c\u4f3c\u662f\u5df2\u4fee\u5230\u4e86\u90a3\u79cd\u8fd4\u8001\u8fd8\u7ae5\u5883\u754c\uff0c\u542c\u603b\u5802\u4e3b\u5c0a\u79f0\u767d\u8001\uff0c\u5927\u6982\u662f\u59d3\u767d\u4e86\u3002\u201d \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u3001\u9093\u98de\uff0c\u90fd\u5728\u51dd\u76ee\u6c89\u601d\uff0c\u4f3c\u662f\u60f3\u4ece\u8bb0\u5fc6\u4e2d\u641c\u89c5\u51fa\u4ec0\u4e48\u540d\u5802\u4e86\u3002 \u3000\u3000\u80e1\u840d\u3001\u6bb5\u4e5d\uff0c\u4e5f\u7b97\u4e00\u4ee3\u95e8\u6d3e\u7684\u5b97\u5e08\uff0c\u4f46\u5982\u6bd4\u5927\u548c\u5c1a\u548c\u9b54\u773c\u9093\u98de\u7684\u7ecf\u9a8c\uff0c\u90a3\u5c31\u5982\u5c0f\u5deb\u89c1\u5927\u5deb\u4e86\uff0c\u6240\u4ee5\uff0c\u4e24\u4e2a\u4eba\u7684\u76ee\u5149\u6295\u6ce8\u5728\u5927\u548c\u5c1a\u548c\u9093\u98de\u7684\u8138\u4e0a\uff0c\u7b49\u5f85\u4ed6\u4eec\u7684\u53cd\u5e94\u3002 \u3000\u3000\u201c\u4e00\u70b9\u4e5f\u60f3\u4e0d\u8d77\u6765\u2026\u2026\u201d\u5927\u548c\u5c1a\u9053\uff1a\u201c\u8fd1\u4e09\u5341\u5e74\u6765\uff0c\u6c5f\u6e56\u4e0a\uff0c\u597d\u50cf\u6ca1\u6709\u4e00\u4f4d\u59d3\u767d\u7684\u9ad8\u4eba\uff1f\u201d \u3000\u3000\u201c\u4e94\u5341\u5e74\u4ee5\u4e0a\u7684\u4e8b\uff0c\u9093\u67d0\u4eba\u4e0d\u6562\u5984\u8a00\uff0c\u4f46\u8fd1\u4e94\u5341\u5e74\u5185\uff0c\u6c5f\u6e56\u4e0a\u5e94\u8be5\u6ca1\u6709\u4e00\u4e2a\u51fa\u7c7b\u62d4\u8403\u7684\u9ad8\u624b\u59d3\u767d\u2026\u2026\u201d \u3000\u3000\u201c\u4f46\u65e2\u7136\u6709\u4e86\u8fd9\u4e48\u4e00\u4e2a\u4eba\u2026\u2026\u201d\u7f57\u5170\u9053\uff1a\u201c\u603b\u4e0d\u4f1a\u662f\u51ed\u7a7a\u6389\u4e0b\u6765\u7684\u5427\uff01\u201d \u3000\u3000\u201c\u96be\u5904\u4e5f\u5c31\u5728\u8fd9\u91cc\u4e86\u2026\u2026\u201d\u5927\u548c\u5c1a\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u59d3\u767d\u7684\uff0c\u4f55\u81f3\u5343\u4e07\uff0c\u4f46\u8fd1\u51e0\u5341\u5e74\u4e2d\u5374\u6ca1\u6709\u4e00\u4e2a\u59d3\u767d\u7684\u4eba\uff0c\u5728\u6c5f\u6e56\u4e0a\u95ef\u51fa\u54cd\u4eae\u7684\u540d\u53f7\u3002\u201d \u3000\u3000\u201c\u4f60\u548c\u5c1a\u5e08\u5144\uff0c\u5728\u6c5f\u6e56\u4e0a\u6253\u6eda\u4e86\u56db\u5341\u5e74\u2026\u2026\u201d\u7f57\u5170\u6709\u610f\u7684\u4e3a\u96be\uff0c\u9053\uff1a\u201c\u5c31\u662f\u8d1f\u8d23\u4e86\u89e3\u654c\u4eba\uff0c\u5982\u4eca\u8fde\u8fd9\u4e48\u4e00\u4e2a\u5927\u654c\u4eba\uff0c\u90fd\u4e00\u65e0\u6240\u77e5\uff0c\u6211\u770b\uff0c\u4f60\u8fd9\u56db\u5341\u5e74\uff0c\u53ef\u771f\u662f\u767d\u767d\u7684\u865a\u5ea6\u4e86\u3002\u201d \u3000\u3000\u8bdd\u8bf4\u7684\u592a\u91cd\uff0c\u8fde\u4e03\u5b9d\u548c\u5c1a\u8fd9\u6837\u7684\u4eba\uff0c\u7adf\u7136\u4e5f\u53cc\u988a\u98de\u7ea2\u3002 \u3000\u3000\u6c5f\u67ab\u626c\u8d77\u5251\u7709\uff0c\u4f3c\u60f3\u5f00\u53e3\uff0c\u4f46\u8bdd\u5230\u53e3\u8fb9\uff0c\u53c8\u5fcd\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u6b27\u9633\u662d\u5374\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u7f57\u5170\uff0c\u5927\u548c\u5c1a\u662f\u5e08\u5144\u7684\u8eab\u4efd\uff0c\u4f60\u600e\u4e48\u53e3\u6ca1\u906e\u62e6\u2026\u2026\u201d \u3000\u3000\u5176\u5b9e\uff0c\u770b\u5230\u4e86\u548c\u5c1a\u5c34\u5c2c\u7f9e\u6127\u4e4b\u8272\uff0c\u7f57\u5170\u5fc3\u4e2d\u5df2\u611f\u540e\u6094\uff0c\u4f46\u6b27\u9633\u662d\u4e00\u756a\u8bdd\uff0c\u53c8\u6fc0\u8d77\u4e86\u7f57\u5170\u5584\u65bd\u5c0f\u6027\u5b50\u7684\u6bdb\u75c5\uff0c\u8bf4\u9053\uff1a\u201c\u548c\u5c1a\u5e08\u5144\u4e00\u5411\u662f\u5927\u6148\u5927\u60b2\uff0c\u7231\u80cc\u9ed1\u9505\uff0c\u8fd9\u4e00\u6b21\uff0c\u6211\u8bf4\u7684\u53ef\u662f\u771f\u60c5\u5b9e\u8bdd\uff0c\u4e00\u70b9\u4e5f\u6ca1\u6709\u51a4\u4ed6\u554a\uff01\u201d \u3000\u3000\u201c\u7f57\u5170\uff0c\u4e0d\u8981\u653e\u5201\u2026\u2026\u201d\u5f20\u56db\u59d1\u4e00\u8eab\u9752\u8863\uff0c\u7a81\u7136\u63a8\u95e8\u800c\u5165\uff0c\u63a5\u9053\uff1a\u201c\u548c\u5c1a\u5e08\u5144\uff0c\u5df2\u5bf9\u4f60\u518d\u4e09\u5bb9\u5fcd\uff0c\u8fd9\u91cc\u4e0d\u662f\u5728\u4f60\u5927\u5df4\u5c71\u5bb6\u4e2d\uff0c\u4efb\u4f60\u7387\u6027\u80e1\u95f9\uff0c\u7f57\u5170\uff0c\u8981\u660e\u767d\u9002\u53ef\u800c\u6b62\u554a\uff01\u201d \u3000\u3000\u201c\u5927\u59d0\u8bf7\u5750\u2026\u2026\u201d\u6c5f\u67ab\u9996\u5148\u7ad9\u8d77\u8eab\u5b50\uff0c\u62b1\u62f3\u4f5c\u793c\u3002 \u3000\u3000\u7fa4\u8c6a\u968f\u7740\u7ad9\u8d77\uff0c\u9f50\u9f50\u884c\u793c\u3002 \u3000\u3000\u201c\u6298\u6740\u4eba\u4e86\u2026\u2026\u201d\u5f20\u56db\u59d1\u6025\u6025\u8fd8\u793c\uff0c\u9053\uff1a\u201c\u8bf8\u4f4d\u5feb\u8bf7\u5750\u4e0b\uff0c\u5c0f\u59b9\u6709\u8981\u4e8b\u5949\u544a\u3002\u201d \u3000\u3000\u201c\u5927\u59d0\u8fd9\u8fb9\u5750\u2026\u2026\u201d\u6c5f\u67ab\u987a\u624b\u62c9\u8fc7\u4e00\u5f20\u6728\u6905\uff0c\u653e\u5728\u8eab\u4fa7\u3002 \u3000\u3000\u76ee\u7779\u5c0f\u60c5\u90ce\u8bda\u5f62\u4e8e\u5916\uff0c\u5168\u7136\u4e0d\u907f\u5acc\u7591\uff0c\u5f20\u56db\u59d1\u5fc3\u4e2d\u53c8\u611f\u52a8\uff0c\u53c8\u6709\u70b9\u7f9e\u6029\uff0c\u4f46\u8fd8\u662f\u54ac\u54ac\u7259\u5750\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u201c\u56db\u59d1\u2026\u2026\u201d\u7f57\u5170\u62a2\u5148\u5f00\u4e86\u53e3\uff0c\u9053\uff1a\u201c\u6211\u53ea\u662f\u80e1\u8bf4\u60ef\u4e86\uff0c\u5927\u548c\u5c1a\u662f\u5bb0\u76f8\u809a\u91cc\u884c\u821f\u8239\uff0c\u54ea\u91cc\u4f1a\u8ddf\u6211\u8fd9\u5c0f\u5e08\u59b9\u4e00\u822c\u89c1\u8bc6\u3002\u201d \u3000\u3000\u5979\u719f\u8bb0\u5929\u72d0\u53e3\u8bc0\uff0c\u6027\u683c\u591a\u53d8\uff0c\u5ffd\u786c\u5ffd\u8f6f\uff0c\u8fde\u81ea\u5df1\u4e5f\u628a\u6301\u4e0d\u4f4f\u3002 \u3000\u3000\u201c\u548c\u5c1a\u662f\u771f\u7684\u60ed\u6127\uff0c\u638c\u7406\u798f\u5bff\u9662\u7684\u9662\u4e3b\uff0c\u5e94\u8be5\u662f\u6c5f\u6e56\u4e0a\u7684\u4e00\u6d41\u9ad8\u624b\uff0c\u6211\u7adf\u7136\u60f3\u4e0d\u51fa\u4ed6\u662f\u4ec0\u4e48\u4eba\uff01\u201d \u3000\u3000\u201c\u5e08\u7236\u2026\u2026\u201d\u5201\u9e4f\u9053\uff1a\u201c\u5f1f\u5b50\u4e5f\u5728\u6c5f\u6e56\u4e0a\u8d70\u52a8\u4e86\u56db\u5341\u5e74\uff0c\u4e5f\u4e00\u6837\u60f3\u4e0d\u51fa\u8fd9\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u6c5f\u6e56\u5e7f\u5927\uff0c\u7eb5\u6a2a\u5341\u4e07\u91cc\u2026\u2026\u201d\u6c5f\u67ab\u7b11\u9053\uff1a\u201c\u4e0d\u77e5\u9690\u5c45\u4e86\u591a\u5c11\u5927\u8d24\u3001\u5de8\u6076\uff0c\u4ed6\u4eec\u4e0d\u5728\u6c5f\u6e56\u4e0a\u626c\u540d\u7acb\u4e07\uff0c\u8c01\u53c8\u80fd\u5168\u90fd\u8ba4\u8bc6\u5462\uff1f\u201d \u3000\u3000\u201c\u5c0f\u59b9\u5012\u60f3\u5230\u4e86\u4e00\u4e2a\u4eba\u2026\u2026\u201d\u5f20\u56db\u59d1\u76ee\u5149\u8f6c\u52a8\uff0c\u56db\u987e\u4e86\u4e00\u773c\uff0c\u89c1\u6240\u6709\u4eba\u7684\u76ee\u5149\uff0c\u90fd\u6295\u6ce8\u5979\u7684\u8eab\u4e0a\uff0c\u6ee1\u8138\u671f\u5f85\u4e4b\u8272\uff0c\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u9634\u9633\u79c0\u624d\u767d\u5929\u5316\uff0c\u5404\u4f4d\u662f\u5426\u8bb0\u5f97\uff1f\u201d \u3000\u3000\u201c\u597d\u50cf\u56db\u5341\u5e74\u524d\uff0c\u6709\u4e00\u4f4d\u59d3\u767d\u7684\u79c0\u624d\uff0c\u5728\u6bbf\u8bd5\u4e2d\u8003\u4e0a\u4e86\u5934\u540d\u72b6\u5143\uff0c\u2026\u2026\u201d\u4e03\u5ba4\u548c\u5c1a\u9053\uff1a \u3000\u3000\u201c\u4f46\u5374\u4e0d\u5c51\u4e3a\u5b98\uff0c\u5f03\u804c\u800c\u53bb\uff0c\u95f9\u7684\u6ee1\u57ce\u98ce\u96e8\uff0c\u5211\u90e8\u8fd8\u52d2\u4eca\u5929\u4e0b\u5404\u5dde\u53bf\u7684\u6355\u5934\uff0c\u8ffd\u67e5\u4ed6\u7684\u4e0b\u843d\uff0c\u95f9\u4e86\u4e24\u4e09\u5e74\uff0c\u624d\u5e73\u9759\u7109\u3002\u201d \u3000\u3000\u201c\u5bf9\uff01\u5c31\u662f\u4ed6\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u4ed6\u81ea\u53f7\u9634\u9633\u79c0\u624d\uff0c\u80fd\u77e5\u9634\u9633\u4e24\u754c\u4e2d\u4e8b\u2026\u2026\u201d \u3000\u3000\u201c\u90a3\u600e\u4e48\u53ef\u80fd\u5462\uff1f\u201d\u7f57\u5170\u9053\uff1a\u201c\u4e0d\u7ba1\u4ed6\u8179\u6709\u591a\u5c11\u8bd7\u4e66\uff0c\u80f8\u7f57\u591a\u5c11\u5947\u5b66\uff0c\u4f46\u4ed6\u603b\u662f\u4eba\u554a\uff01 \u3000\u3000\u90a3\u80fd\u7ba1\u5230\u9634\u95f4\u9b3c\u9b42\u3002\u201d \u3000\u3000\u201c\u8bf4\u7684\u5bf9\uff01\u5b50\u4e0d\u8bed\u602a\u529b\u4e71\u795e\uff0c\u9b3c\u9b42\u4e4b\u8bf4\uff0c\u5e76\u65e0\u660e\u8bc1\uff0c\u201d\u5f20\u56db\u59d1\u7b11\u9053\uff1a\u201c\u4ed6\u81ea\u5439\u81ea\u64c2\u7684\u610f\u601d\uff0c\u662f\u8868\u793a\u4ed6\u65e0\u6240\u4e0d\u80fd\uff0c\u5929\u6587\u3001\u5730\u7406\uff0c\u9634\u9633\u3001\u4eba\u9b3c\u4e4b\u5b66\uff0c\u90fd\u96be\u4e0d\u5012\u4ed6\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u4eba\u5982\u6b64\u6258\u5927\uff0c\u60f3\u5fc5\u6709\u70b9\u672c\u9886\uff0c\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u5982\u6709\u673a\u4f1a\uff0c\u5c0f\u5f1f\u5012\u662f\u60f3\u4f1a\u4f1a\u4ed6\u4e86\uff01\u201d \u3000\u3000\u9093\u98de\u53cc\u76ee\u76ef\u6ce8\u5728\u5f20\u56db\u59d1\u7684\u7751\u4e0a\uff0c\u9053\uff1a\u201c\u5f20\u59d1\u5a18\uff0c\u8001\u673d\u5fc3\u4e2d\u6709\u4e00\u70b9\u4e0d\u89e3\u4e4b\u5904\uff0c\u4e0d\u77e5\u53ef\u5426\u8bf7\u6559\uff1f\u201d \u3000\u3000\u201c\u8bf7\u95ee\u5427\uff01\u201d\u5f20\u56db\u59d1\u7b11\u9053\uff1a\u201c\u6211\u60f3\u9093\u5144\u5fc3\u4e2d\u4e4b\u7591\uff0c\u4e5f\u53ef\u80fd\u662f\u5728\u573a\u6240\u6709\u4eba\u5fc3\u4e2d\u4e4b\u7591\u3002\u5c0f\u59b9\u7b54\u4e0d\u51fa\u6765\uff0c\u6211\u4e5f\u4f1a\u636e\u5b9e\u76f8\u544a\uff0c\u80fd\u8bf4\u7684\u5c0f\u59b9\u8a00\u65e0\u4e0d\u5c3d\u3002\u201d \u3000\u3000\u9093\u98de\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u8001\u673d\u4e5f\u542c\u8fc7\u767d\u79c0\u624d\u5f03\u5b98\u6f5c\u9003\u7684\u4e8b\uff0c\u4f46\u90a3\u65f6\uff0c\u59d1\u5a18\u53ea\u4f2f\u8fd8\u6ca1\u6709\u51fa\u751f\u5427\uff01\u201d \u3000\u3000\u201c\u9093\u5144\u8bf4\u7684\u4e0d\u9519\uff0c\u767d\u79c0\u624d\u5f03\u5b98\u6f5c\u9003\u65f6\uff0c\u5c0f\u59b9\u786e\u8fd8\u672a\u751f\u51fa\u6765\uff0c\u5c0f\u59b9\u4eca\u5e74\u4e09\u5341\u516b\u5c81\uff0c\u5728\u9634\u9633\u79c0\u624d\u5f03\u5b98\u4e09\u5e74\u540e\u51fa\u751f\u3002\u201d \u3000\u3000\u201c\u597d\u554a\uff01\u56db\u59d1\uff0c\u4f60\u8fd8\u6bd4\u6211\u5c0f\u554a\uff01\u2026\u2026\u201d\u7f57\u5170\u5927\u53eb\u9053\uff1a\u201c\u4f60\u4e00\u76f4\u8ba9\u6211\u53eb\u4f60\u5927\u59d0\uff0c\u88ab\u4f60\u51a4\u4e86\u8fd9\u4e48\u591a\u5e74\uff0c\u4f60\u8981\u600e\u4e48\u8d54\u7ed9\u6211\uff1f\u201d \u3000\u3000\u201c\u7f57\u5170\uff0c\u4f60\u81ea\u5df1\u62ff\u955c\u4e86\u77a7\u77a7\u4f60\u90a3\u526f\u5c0a\u5bb9\uff0c\u2026\u2026\u201d\u5f20\u56db\u59d1\u7b11\u9053\uff1a\u201c\u7a7f\u4e0a\u5c0f\u82b1\u8884\uff0c\u8bf4\u4f60\u624d\u4e8c\u5341\u51fa\u5934\uff0c\u4eba\u4eba\u90fd\u4f1a\u76f8\u4fe1\uff0c\u9664\u4e86\u5b9e\u9645\u7684\u5e74\u9f84\u4e4b\u5916\uff0c\u6211\u54ea\u91cc\u4e0d\u50cf\u4f60\u7684\u5927\u59d0\uff1f\u201d \u3000\u3000\u8fd9\u756a\u8bdd\uff0c\u5f15\u5f97\u5168\u573a\u4e2d\u4eba\uff0c\u65e0\u4e0d\u5931\u58f0\u800c\u7b11\u3002 \u3000\u3000\u201c\u6211\u751f\u4e86\u4e24\u4e2a\u5973\u513f\u2026\u2026\u201d\u7f57\u5170\u9053\uff1a\u201c\u4f60\u5462\uff1f\u8fde\u86cb\u4e5f\u672a\u751f\u8fc7\u4e00\u4e2a\uff0c\u4ee5\u540e\uff0c\u4e00\u5b9a\u8981\u5012\u8fc7\u6765\u53eb\uff0c\u6211\u4e5f\u8981\u8fc7\u8fc7\u5927\u59d0\u7684\u763e\u3002\u201d \u3000\u3000\u201c\u597d\u4e86\uff0c\u7f57\u5170\uff0c\u54b1\u4eec\u662f\u5435\u60ef\u4e86\uff0c\u73b0\u5728\u662f\u8ba8\u8bba\u6c5f\u6e56\u5927\u4e8b\uff0c\u4e0d\u80fd\u80e1\u95f9\uff0c\u2026\u2026\uff0c\u201d\u6b27\u9633\u662d\u9053\uff1a \u3000\u3000\u201c\u8bf8\u4f4d\uff0c\u5185\u4eba\u4e00\u5411\u8bf4\u7b11\u60ef\u4e86\uff0c\u8bf8\u4f4d\u4e0d\u8981\u653e\u5728\u5fc3\u4e0a\u3002\u201d \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u767d\u5929\u5316\u7684\u4e8b\uff1f\u201d \u3000\u3000\u201c\u5bb6\u5e08\u7559\u4f20\u4e0b\u4e00\u672c\u8bb0\uff0c\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u4e0a\u9762\u8bb0\u8f7d\u4e86\u8fd1\u767e\u5e74\u4e2d\u4e03\u4e2a\u6700\u8be1\u79d8\u7684\u4eba\u7269\uff0c\u767d\u5929\u5316\u5c31\u662f\u5176\u4e2d\u4e4b\u4e00\uff0c\u4ed6\u7cbe\u901a\u661f\u535c\u4e4b\u7406\uff0c\u82e6\u7814\u5947\u672f\uff0c\u5bf9\u533b\u9053\uff0c\u4e5f\u6709\u6781\u7279\u6b8a\u7684\u6210\u5c31\uff0c\u6211\u4ee5\u4e3a\u4ed6\u65e9\u5df2\u6b7b\u4e86\uff0c\u60f3\u4e0d\u5230\u7adf\u7136\u8fd8\u6d3b\u5728\u4e16\u4e0a\uff0c\u800c\u4e14\uff0c\u9690\u5c45\u4e8e\u5929\u9a6c\u603b\u5802\u2026\u2026\u3002\u201d \u3000\u3000\u53ca\u4e86\uff0c\u5f20\u56db\u59d1\u5df2\u8f6c\u4e86\u8bdd\u9898\uff0c\u795e\u60c5\u4e25\u8083\u7684\u8bf4\uff1a\u201c\u4e09\u5bab\u4e3b\u6df1\u591c\u6311\u6218\uff0c\u4e0d\u4f46\u60f3\u4e00\u4e3e\u628a\u6211\u4eec\u5236\u670d\uff0c\u8fde\u592d\u9a6c\u5802\u3001\u592d\u738b\u95e8\u4e2d\u4eba\uff0c\u4e5f\u8981\u7f6e\u5165\u5979\u638c\u63e1\u4e4b\u4e2d\u3002\u201d \u3000\u3000\u201c\u8fd9\u771f\u662f\u5f02\u60f3\u5929\u5f00\u4e86\u2026\u2026\u201d\u6b27\u9633\u662d\u9053\uff1a\u201c\u5929\u865a\u5b50\u3001\u795e\u9b3c\u53cc\u5200\uff0c\u4e5f\u7b97\u662f\u6c5f\u6e56\u4e0a\u7684\u9ad8\u624b\u4e86\uff0c\u628a\u4e00\u54c1\u5200\u4e5f\u7b97\u4e0a\u5427\uff01\u4e0d\u8fc7\u5c31\u662f\u90a3\u4e48\u4e00\u70b9\u9053\u884c\u2026\u2026\u201d \u3000\u3000\u201c\u6b27\u9633\u5144\u2026\u2026\u201d\u5f20\u56db\u7ad9\u63a5\u9053\uff1a\u201c\u5982\u679c\u4f60\u77e5\u9053\u4e86\u4e09\u5bab\u4e3b\u7684\u8bbe\u8ba1\u3001\u624b\u6bb5\uff0c\u6050\u6015\u5c31\u4e0d\u4f1a\u8fd9\u4e48\u60f3\u4e86\u2026\u2026\u201d\u7a81\u7136\u4f4f\u53e3\u4e0d\u8a00\u3002 \u3000\u3000\u201c\u5c3d\u7ba1\u8bf7\u8bf4\uff0c\u548c\u5c1a\u5df2\u8981\u80e1\u5143\u548c\u90ed\u5929\u540c\uff0c\u7387\u9886\u7740\u6b27\u9633\u5144\u7684\u4e94\u4e2a\u5f1f\u5b50\uff0c\u5e03\u5b88\u56db\u5468\u3002\u201d \u3000\u3000\u6b27\u9633\u662d\u9053\uff1a\u201c\u56db\u59d1\u542c\u4f60\u7684\u53e3\u6c14\uff0c\u4e09\u5bab\u4e3b\u57cb\u4f0f\u7684\u529b\u91cf\uff0c\u5f88\u96be\u6297\u62d2\u4e86\uff1f\u201d \u3000\u3000\u201c\u4e0d\u662f\u5f88\u96be\u6297\u62d2\uff0c\u800c\u662f\u65e0\u6cd5\u6297\u62d2\uff0c\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\u65e0\u76f8\u795e\u529f\uff0c\u80fd\u4e0d\u80fd\u6297\u62d2\u8fd9\u4e9b\u6bd2\u7269\uff1f\u201d \u3000\u3000\u201c\u7a76\u7adf\u662f\u4ec0\u4e48\u4e1c\u897f\u554a\uff01\u2026\u2026\u201d\u7f57\u5170\u9053\uff1a\u201c\u5982\u6b64\u7684\u4e00\u4e2a\u53ef\u6015\u6cd5\uff1f\u201d \u3000\u3000\u201c\u86ca\uff01\u2026\u2026\u201d \u3000\u3000\u5168\u573a\u4e2d\u4eba\u8138\u8272\u5927\u53d8\u3002 \u3000\u3000\u5927\u548c\u5c1a\u5401\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u65e0\u76f8\u795e\u529f\uff0c\u7ec3\u5230\u67d0\u79cd\u7a0b\u5ea6\uff0c\u4e5f\u8bb8\u53ef\u6297\u62d2\u4e00\u822c\u7684\u5200\u5251\u4f24\u5bb3\uff0c\u80fd\u4e0d\u80fd\u6297\u86ca\u6bd2\uff1f\u548c\u5c1a\u5c31\u4e0d\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u201c\u542c\u8bf4\uff0c\u86ca\u6bd2\u90fd\u4e0b\u5728\u9152\u83dc\u4e4b\u4e2d\u2026\u2026\u201d\u7f57\u5170\u9053\uff1a\u201c\u54b1\u4eec\u53c8\u4e0d\u5403\u4ed6\u4eec\u7684\u4e1c\u897f\uff0c\u4ed6\u5982\u4f55\u4e0b\u86ca\u5462\uff1f\u201d \u3000\u3000\u201c\u662f\u76c5\u4e2d\u4e4b\u6700\u7684\u6d3b\u86ca\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u5c31\u6211\u6240\u77e5\uff0c\u5b83\u53ef\u4ee5\u7531\u8033\u3001\u9f3b\u3001\u53e3\u4e2d\u8fdb\u5165\u4eba\u4f53\uff0c\u4e5f\u80fd\u54ac\u7834\u808c\u80a4\uff0c\u6f5c\u5165\u5185\u8151\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u662f\u5b8c\u5168\u65e0\u6cd5\u9884\u9632\u4e86\uff1f\u2026\u2026\u201d\u7f57\u5170\u9053\uff1a\u201c\u5982\u975e\u6c5f\u67ab\u4e0b\u4ee4\u53ca\u65f6\u64a4\u9000\uff0c\u73b0\u5728\uff0c\u54b1\u4eec\u5c82\u4e0d\u90fd\u5df2\u4e2d\u4e86\u86ca\u6bd2\u3002\u201d \u3000\u3000\u201c\u5927\u59d0\uff0c\u771f\u6709\u8fd9\u4e48\u5389\u5bb3\u7684\u6d3b\u76c5\u5417\uff1f\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u6709\u6ca1\u6709\u5bf9\u6297\u7684\u529e\u6cd5\uff1f\u201d \u3000\u3000\u201c\u6211\u6ca1\u6709\u53bb\u8fc7\u82d7\u7586\uff0c\u4e5f\u6ca1\u6709\u89c1\u8fc7\u86ca\u662f\u4ec0\u4e48\u6837\u5b50\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u8fde\u4e2d\u4e86\u76d8\u6bd2\u7684\u4eba\uff0c\u6211\u90fd\u6ca1\u6709\u89c1\u8fc7\uff0c\u6211\u4eec\u5bf9\u5b83\u4e00\u65e0\u6240\u77e5\uff0c\u56e0\u800c\uff0c\u7edd\u4e0d\u80fd\u8ba9\u5b83\u5728\u4e2d\u539f\u5b58\u5728\uff0c\u4e07\u86ca\u95e8\u4e2d\u4eba\uff0c\u5df2\u5230\u4e86\u957f\u5b89\uff0c\u800c\u4e14\uff0c\u968f\u8eab\u643a\u5e26\u7740\u6d3b\u86ca\uff0c\u6211\u51c6\u5907\u5e26\u7740\u96ea\u3001\u971c\u548c\u6885\u82b1\u4e09\u4e2a\u4e2b\u5934\uff0c\u5148\u628a\u4e07\u86ca\u95e8\u7684\u5165\u7ed9\u5bb0\u4e86\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u8981\u4ece\u957f\u8ba1\u8bae\u2026\u2026\u201d\u5927\u548c\u5c1a\u8bf4\uff0c\u201c\u4e09\u5bab\u4e3b\u5982\u4f55\u548c\u4e07\u86ca\u95e8\u642d\u4e0a\u7684\uff0c\u96be\u9053\u4e07\u86ca\u95e8\u4e5f\u662f\u4ed6\u4eec\u7ec4\u5408\u4e2d\u7684\u4e00\u4e2a\u5355\u4f4d\u2026\u2026\u201d \u3000\u3000\u201c\u4e07\u86ca\u95e8\u5927\u6982\u8fd8\u672a\u548c\u4ed6\u4eec\u4e32\u6210\u4e00\u6c14\uff0c\u8fd9\u6279\u4eba\u662f\u7531\u9b3c\u5f71\u5b50\u8c2d\u9e9f\u7ea6\u8bf7\u6765\u7684\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a \u3000\u3000\u201c\u4e09\u5bab\u4e3b\u8bb8\u4e86\u8c2d\u9e9f\u4e09\u65e5\u4e4b\u6b22\uff0c\u8981\u8c2d\u9e9f\u8bf7\u4e07\u86ca\u95e8\u4e2d\u4eba\uff0c\u4f20\u6388\u5979\u5f79\u65bd\u6d3b\u86ca\u4e4b\u6cd5\u3002\u201d \u3000\u3000\u201c\u8981\u7acb\u523b\u6d88\u706d\u6389\u8c2d\u9e9f\u548c\u4e07\u86ca\u95e8\u4e2d\u4eba\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u8bf4\uff0c\u201c\u4e0d\u8fc7\u52a8\u624b\u7684\u4eba\uff0c\u8981\u8c03\u6574\u4e00\u4e0b\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u53d6\u4ee3\u4e24\u4e2a\u4e2b\u5934\u2026\u2026\u201d\u7f57\u5170\u9053\uff1a\u201c\u6c5f\u67ab\uff0c\u4e0d\u8981\u62d2\u7edd\u6211\u3002\u201d \u3000\u3000\u6bcd\u5973\u4e4b\u60c5\u6ea2\u4e8e\u8a00\u8868\u3002 \u3000\u3000\u201c\u4e0d\u8981\u6c42\u6211\u2026\u2026\uff0c\u201d\u6c5f\u67ab\u51b7\u51b7\u5730\u8bf4\u9053\uff1a\u201c\u6211\u81ea\u5df1\u4f1a\u9009\u4eba\uff0c\u9009\u4e0a\u7684\u4e0d\u8bb8\u63a8\u8f9e\uff0c\u9009\u4e0d\u4e0a\u4e5f\u4e0d\u51c6\u81ea\u52a8\u8bf7\u7f28\u3002\u201d \u3000\u3000\u6c5f\u67ab\u62c9\u4e0b\u8138\u6765\uff0c\u8fd8\u771f\u6709\u51e0\u5206\u6210\u4e25\uff0c\u7f57\u5170\u4e5f\u4e0d\u6562\u591a\u8bdd\u4e86\u3002 \u3000\u3000\u201c\u5f20\u5927\u59d0\u3001\u4e03\u5b9d\u50e7\u3001\u9093\u98de\u548c\u6211\uff0c\u8d1f\u8d23\u8ffd\u6740\u8c2d\u9e9f\u548c\u4e07\u86ca\u95e8\u4e2d\u4eba\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u4f59\u4e0b\u7684\u4eba\uff0c\u7531\u6b27\u9633\u5144\u9886\u5934\uff0c\u5b88\u5728\u6d1b\u9633\u5c45\u3002\u660e\u5929\u4e2d\u5348\u65f6\u5206\uff0c\u6211\u4eec\u8fd8\u4e0d\u56de\u6765\uff0c\u5c31\u51c6\u5907\u64a4\u79bb\u6b64\u5730\uff0c\u9000\u56de\u5927\u5df4\u5c71\u53bb\u3002\u201d \u3000\u3000\u201c\u6c5f\u5144\u5f1f\uff0c\u4f60\u662f\u4e3b\u5e05\uff0c\u4e0d\u53ef\u6d89\u9669\uff0c\u54b1\u4eec\u4e24\u6362\u4e00\u4e0b\u2026\u2026\u201d\u6b27\u9633\u662d\u9053\uff1a\u201c\u56db\u59d1\uff0c\u4f60\u5e2e\u5fd9\u8bf4\u53e5\u8bdd\u5440\uff01\u7edd\u4e0d\u80fd\u8ba9\u4ed6\u53bb\u554a\uff01\u201d \u3000\u3000\u4ed6\u5fc3\u4e2d\u660e\u767d\uff0c\u53ea\u6709\u5f20\u56db\u59d1\u4e5f\u8bb8\u80fd\u6539\u53d8\u6c5f\u67ab\u7684\u51b3\u5b9a\u3002 \u3000\u3000\u201c\u4ed6\u5df2\u7ecf\u6709\u8a00\u5728\u5148\uff0c\u6211\u4e5f\u4e0d\u6562\u591a\u53e3\u554a\uff01\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u4f55\u51b5\uff0c\u6211\u89c9\u5f97\u4ed6\u9009\u7684\u4eba\u624b\u4e0d\u9519\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u5c31\u8fd9\u4e48\u51b3\u5b9a\u4e86\uff0c\u6211\u4eec\u7acb\u523b\u884c\u52a8\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u5df2\u5f53\u5148\u7ad9\u4e86\u8d77\u6765\uff0c\u5411\u5916\u884c\u53bb\u3002 \u3000\u3000\u201c\u6162\u4e00\u70b9\u2026\u2026\u201d\u5f20\u56db\u59d1\u7275\u4f4f\u4e86\u6c5f\u67ab\u7684\u8863\u8896\uff0c\u7b11\u9053\uff1a\u201c\u6025\u4e5f\u4e0d\u5728\u4e00\u65f6\u554a\uff01\u6211\u867d\u7136\u4e0d\u6562\u80af\u5b9a\u96c4\u9ec4\u53ef\u4ee5\u907f\u86ca\uff0c\u4f46\u96c4\u9ec4\u53ef\u4ee5\u4f7f\u767e\u6bd2\u56de\u907f\uff0c\u54b1\u4eec\u51c6\u5907\u4e9b\u96c4\u9ec4\u8bd5\u8bd5\u5427\uff01\u201d \u3000\u3000\u201c\u6211\u53bb\u4e70\u3002\u201d \u3000\u3000\u5201\u9e4f\u7ad9\u8d77\u8eab\u5b50\uff0c\u6025\u6b65\u800c\u53bb\u3002 \u3000\u3000\u5f20\u56db\u59d1\u5341\u5206\u5c0f\u5fc3\uff0c\u4e0d\u4ec5\u4ee5\u96c4\u9ec4\u9152\u914d\u4eba\u6e05\u6c34\u4e2d\u8981\u5927\u5bb6\u6d17\u4e86\u4e2a\u6fa1\uff0c\u8fd8\u6ce1\u4e86\u56db\u74f6\u96c4\u9ec4\u9152\uff0c\u8981\u56db\u4eba\u5404\u5e26\u4e00\u74f6\uff0c\u800c\u4e14\uff0c\u5728\u5bbf\u4f4f\u4e4b\u5904\uff0c\u4e5f\u6492\u4e86\u5f88\u591a\u7684\u96c4\u9ec4\u7c89\u3002\u8fd9\u4e48\u4e00\u6298\u817e\uff0c\u5929\u5df2\u5927\u4eae\u4e86\u3002 \u3000\u3000\u6c5f\u67ab\u8feb\u4e0d\u53ca\u5f85\u5730\u9053\uff1a\u201c\u5927\u59d0\uff0c\u8be5\u8d70\u4e86\uff0c\u5929\u5df2\u5927\u4eae\uff0c\u54b1\u4eec\u8fd8\u8981\u627e\u4eba\u554a\uff01\u201d \u3000\u3000\u201c\u53bb\u54ea\u91cc\u627e\uff01\u2026\u2026\u201d\u5f20\u56db\u59d1\u7b11\u9053\uff1a\u201c\u5927\u767d\u5929\u5bf9\u54b1\u4eec\u6709\u5229\u65e0\u5bb3\uff0c\u4f55\u51b5\uff0c\u86ca\u6bd2\u4e4b\u7269\uff0c\u8fd8\u672a\u5907\u9f50\u3002\u201d \u3000\u3000\u201c\u8fd8\u6709\u4ec0\u4e48\u5462\uff1f\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u5927\u59d0\uff0c\u53ef\u771f\u662f\u6c89\u5f97\u4f4f\u6c14\u554a\uff01\u201d \u3000\u3000\u8c08\u8bdd\u4e4b\u95f4\uff0c\u6885\u82b1\u548c\u541f\u96ea\u624b\u4e2d\u6367\u7740\u7f1d\u5236\u7684\u62a4\u8033\u3001\u53e3\u7f69\uff0c\u4e00\u8138\u6101\u82e6\u7684\u884c\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u5f20\u56db\u59d1\u7b11\u4e00\u7b11\u9053\uff1a\u201c\u770b\u4f60\u4eec\u6101\u7709\u82e6\u7751\u7684\u6837\u5b50\uff0c\u662f\u62c5\u5fc3\u5e08\u7236\u554a\uff01 \u3000\u3000\u8fd8\u662f\u62c5\u5fc3\u4f60\u4eec\u7684\u6c5f\u54e5\u54e5\uff1f\u201d \u3000\u3000\u6885\u82b1\u4f4e\u4e0b\u5934\uff0c\u6ca1\u6709\u56de\u7b54\u3002 \u3000\u3000\u541f\u96ea\u5374\u4f4e\u58f0\u9053\uff1a\u201c\u5f20\u59e8\uff0c\u8ba9\u6211\u4e5f\u53bb\u597d\u4e0d\u597d\uff1f\u201d \u3000\u3000\u201c\u4e0d\u884c\uff01\u2026\u2026\u201d\u63a5\u53e3\u7684\u662f\u6c5f\u67ab\uff1a\u201c\u4e56\u4e56\u5730\u5b88\u5728\u8fd9\u91cc\uff0c\u7b49\u6211\u4eec\u56de\u6765\u3002\u201d \u3000\u3000\u4ed6\u7d27\u7ef7\u7740\u4e00\u5f20\u7751\uff0c\u541f\u96ea\u4e0d\u6562\u6297\u8fa9\u4e86\uff0c\u5374\u4e3e\u624b\u62ed\u53bb\u6d41\u51fa\u7684\u773c\u6cea\u3002 \u3000\u3000\u5f20\u56db\u59d1\u62cd\u62cd\u541f\u96ea\uff0c\u53d6\u8fc7\u62a4\u8033\u3001\u53e3\u7f69\uff0c\u9053\uff1a\u201c\u8fd9\u4e2d\u95f4\u90fd\u5939\u6709\u96c4\u9ec4\u7c89\uff0c\u63a5\u8fd1\u4e07\u86ca\u95e8\u4e2d\u4eba\u65f6\uff0c\u518d\u6234\u8d77\u6765\u3002\u201d \u3000\u3000\u201c\u53ea\u4e0d\u77e5\u4e07\u86ca\u95e8\u4e2d\u4eba\uff0c\u73b0\u5728\u4f55\u5904\uff1f\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u770b\u7740\u5f20\u56db\u59d1\uff0c\u5e0c\u671b\u80fd\u5f97\u5230\u56de\u7b54\u3002 \u3000\u3000\u4f46\u89c1\u4eba\u5f71\u4e00\u95ea\uff0c\u4e00\u4e2a\u5934\u6234\u6be1\u5e3d\u7684\u7070\u8863\u4eba\uff0c\u51b2\u5165\u5ba4\u4e2d\uff0c\u63a5\u9053\uff1a\u201c\u5f20\u59e8\uff0c\u5e78\u672a\u8fb1\u547d\uff0c\u957f\u5b89\u57ce\u8fd9\u4e48\u591a\u5ba2\u6808\uff0c\u4ed6\u4eec\u4e0d\u4f4f\uff0c\u5374\u7a9d\u5230\u57ce\u5317\u5f90\u5bb6\u5893\u56ed\u91cc\uff0c\u4e24\u4e2a\u770b\u5893\u56ed\u7684\u8001\u4eba\uff0c\u4e5f\u88ab\u4ed6\u4eec\u56f0\u4e86\u8d77\u6765\uff0c\u2026\u2026\u201d \u3000\u3000\u7a81\u7136\u6325\u52a8\u5c0f\u624b\uff0c\u63a5\u9053\uff1a\u201c\u4ec0\u4e48\u5473\u9053\u554a\uff01\u4f60\u4eec\u559d\u9152\u4e86\uff1f\u201d \u3000\u3000\u201c\u597d\u6781\u4e86\uff0c\u541f\u971c\u5927\u529f\u4e00\u4ef6\uff0c\uff05\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u5feb\u53bb\u6362\u8863\u670d\uff0c\u597d\u597d\u7761\u4e00\u89c9\uff0c\u7b49\u5f20\u59e8\u56de\u6765\u2026\u2026\u201d \u3000\u3000\u56de\u6765\u4e86\u600e\u4e48\u6837\uff0c\u5f20\u56db\u59d1\u6ca1\u6709\u8bf4\uff0c\u541f\u971c\u4e5f\u6ca1\u6709\u95ee\u3002 \u3000\u3000\u5927\u548c\u5c1a\u3001\u9093\u98de\u90fd\u5728\u573a\uff0c\u541f\u971c\u4e5f\u65e0\u6cd5\u5411\u6c5f\u5927\u54e5\u6492\u5a07\uff0c\u53d6\u4e0b\u6be1\u5e3d\uff0c\u5ae3\u7136\u4e00\u7b11\u9053\uff1a\u201c\u6211\u771f\u7684\u5f88\u997f\u4e86\uff0c\u4e24\u4f4d\u59d0\u59d0\uff0c\u966a\u6211\u53bb\u5403\u4e1c\u897f\u5427\uff01\u201d \u3000\u3000\u7275\u7740\u541f\u96ea\u548c\u6885\u82b1\u9000\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u201c\u540d\u5e08\u51fa\u9ad8\u5f92\u554a\uff01\u4e24\u4e2a\u4e2b\u5934\uff0c\u5df2\u88ab\u4f60\u5728\u77ed\u77ed\u51e0\u6708\u4e2d\uff0c\u8c03\u6559\u957f\u5927\u4e86\u2026\u2026\u201d\u5927\u548c\u5c1a\u7b11\u9053\uff1a \u3000\u3000\u201c\u54b1\u4eec\u4e5f\u8be5\u51fa\u52a8\u4e86\u5427\uff01\u201d \u3000\u3000\u9093\u98de\u9053\uff1a\u201c\u6211\u5e26\u8def\uff0c\u5f90\u5bb6\u5893\u56ed\u79bb\u8fd9\u91cc\u6709\u5341\u51e0\u91cc\u8def\uff0c\u8d70\u6162\u70b9\uff0c\u5230\u90a3\u91cc\u592a\u9633\u4e5f\u8be5\u51fa\u6765\u4e86\u3002\u201d \u3000\u3000\u5f90\u5bb6\u5893\u56ed\uff0c\u53e4\u67cf\u68ee\u68ee\uff0c\u5468\u56f4\u4e0d\u89c1\u4eba\u5bb6\uff0c\u662f\u4e00\u5904\u5f88\u8352\u51c9\u7684\u5730\u65b9\u3002 \u3000\u3000\u5893\u56ed\u4e00\u89d2\uff0c\u4e00\u682a\u9ad8\u5927\u7684\u53e4\u67cf\u4e0b\uff0c\u6709\u4e24\u95f4\u74e6\u820d\u3002 \u3000\u3000\u6c5f\u67ab\u3001\u4e03\u5b9d\u548c\u5c1a\u5df2\u6f5c\u8fd1\u74e6\u820d\u7a97\u4e0b\u3002 \u3000\u3000\u5f20\u56db\u59d1\u548c\u9093\u98de\u5206\u5b88\u5728\u74e6\u820d\u524d\u540e\u3002 \u3000\u3000\u53ea\u542c\u5ba4\u4e2d\u4f20\u6765\u4e86\u4e89\u8fa9\u4e4b\u58f0\uff0c\u5374\u542c\u4e0d\u61c2\u8bf4\u7684\u4ec0\u4e48\u3002 \u3000\u3000\u5927\u548c\u5c1a\u4f38\u51fa\u4e09\u4e2a\u6307\u5934\uff0c\u8868\u793a\u5ba4\u4e2d\u6709\u4e09\u4e2a\u4eba\u3002 \u3000\u3000\u6c5f\u67ab\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u4f60\u66ff\u6211\u63a0\u9635\u3002\u201d \u3000\u3000\u6234\u4e0a\u62a4\u8033\u3001\u53e3\u7f69\uff0c\u4e00\u811a\u8e22\u5f00\u672c\u95e8\uff0c\u95ea\u8eab\u800c\u5165\u3002 \u3000\u3000\u4e24\u4e2a\u8eab\u7740\u9ebb\u8863\uff0c\u9762\u76ee\u9edd\u9ed1\u7684\u4eba\uff0c\u5404\u62b1\u7740\u4e00\u4e2a\u5f62\u5982\u846b\u82a6\u4e4b\u7269\uff0c\u9b3c\u5f71\u5b50\u8c2d\u9e9f\uff0c\u5750\u5728\u4e24\u4eba\u5bf9\u9762\uff0c\u6b63\u5728\u4e89\u8bba\u3002 \u3000\u3000\u4e09\u4e2a\u4eba\u90fd\u6709\u7740\u4e0d\u9519\u7684\u6b66\u529f\uff0c\u53cd\u5e94\u4ea6\u5feb\uff0c\u6c5f\u67ab\u7834\u95e8\u800c\u4eba\uff0c\u4ed6\u4eec\u5df2\u6025\u6025\u633a\u8eab\u800c\u8d77\u3002 \u3000\u3000\u4f46\u6c5f\u67ab\u66f4\u5feb\uff0c\u53cc\u624b\u6325\u52a8\uff0c\u5927\u9053\u9752\u8292\uff0c\u6fc0\u5c04\u800c\u51fa\uff0c\u4e24\u4e2a\u9ebb\u8863\u4eba\u95ea\u907f\u4e0d\u53ca\uff0c\u5168\u4e2d\u8981\u5bb3\uff0c\u624b\u4e2d\u4e24\u4e2a\u846b\u82a6\uff0c\u4e5f\u8dcc\u843d\u5730\u4e0a\uff0c\u84ec\u7136\u4f5c\u54cd\u3002 \u3000\u3000\u8c2d\u9e9f\u672c\u5df2\u8f6c\u8eab\u653b\u5411\u6c5f\u67ab\uff0c\u4f46\u5374\u56de\u8eab\u4e00\u6251\uff0c\u4e24\u53cc\u624b\u6025\u6025\u6309\u4f4f\u4e86\u4e24\u4e2a\u846b\u82a6\u7684\u585e\u5b50\u3002 \u3000\u3000\u6c5f\u67ab\u7684\u52a8\u4f5c\u5feb\u6781\u4e86\uff0c\u8eab\u5b50\u4e00\u4fa7\uff0c\u53f3\u624b\u5df2\u6309\u5728\u8c2d\u9e9f\u7684\u9876\u95e8\u4e4b\u4e0a\u3002 \u3000\u3000\u201c\u6740\u4e86\u6211\uff0c\u8fd9\u65b9\u5706\u6570\u5341\u91cc\u7684\u4eba\u755c\uff0c\u90fd\u5c06\u906d\u6b83\u2026\u2026\u201d\u8c2d\u9e9f\u9053\uff1a\u201c\u8fd9\u4e24\u4e2a\u94dc\u846b\u82a6\u4e2d\uff0c\u6709\u6570\u767e\u53ea\u6d3b\u86ca\uff0c\u98de\u884c\u5feb\u901f\uff0c\u518d\u597d\u7684\u6b66\u529f\uff0c\u4e5f\u65e0\u6cd5\u963b\u6b62\u5b83\u7528\u7684\u8086\u8650\u3002\u201d \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u6c5f\u67ab\u5e76\u65e0\u6740\u4ed6\u4e4b\u5fc3\u3002 \u3000\u3000\u5927\u548c\u5c1a\u3001\u5f20\u56db\u59d1\u3001\u9093\u98de\u76f8\u7ee7\u4eba\u5ba4\uff0c\u770b\u4e24\u4e2a\u9ebb\u8863\u4eba\uff0c\u54bd\u5589\u3001\u7709\u5fc3\u3001\u524d\u80a9\u5904\uff0c\u5404\u4e2d\u4e00\u622a\u67cf\u679d\uff0c\u5168\u662f\u8981\u5bb3\u6240\u5728\uff0c\u800c\u4e14\u6df1\u5165\u6570\u5bf8\uff0c\u65e0\u602a\u4e24\u4eba\u8fde\u58f0\u97f3\u4e5f\u672a\u53d1\u51fa\uff0c\u5c31\u6c14\u7edd\u800c\u901d\u3002 \u3000\u3000\u9093\u98de\u6697\u6697\u5401\u53e3\u6c14\uff0c\u5fd6\u9053\uff1a\u201c\u8fd9\u624d\u662f\u771f\u6b63\u7684\u9ad8\u660e\u6b66\u529f\uff0c\u6458\u53f6\u4f24\u654c\uff0c\u98de\u82b1\u53d6\u547d\u3002\u201d \u3000\u3000\u6c5f\u67ab\u5c55\u9732\u51fa\u771f\u6b63\u7684\u6b66\u529f\uff0c\u5927\u548c\u5c1a\u4e5f\u770b\u7684\u6697\u6697\u656c\u4f69\uff0c\u5fd6\u9053\uff1a\u201c\u81ea\u5df1\u51fa\u624b\uff0c\u7edd\u96be\u8fbe\u5230\u5982\u6b64\u5e72\u51c0\u4fd0\u843d\u7684\u5883\u754c\u3002\u201d \u3000\u3000\u201c\u8c2d\u9e9f\uff0c\u8981\u6740\u4f60\uff0c\u4f60\u73b0\u5728\u65e9\u5df2\u6ca1\u547d\u2026\u2026\u201d\u5927\u548c\u5c1a\u9053\uff1a\u201c\u4f60\u4e5f\u4e0d\u4f1a\u6709\u62d4\u6389\u5be8\u5b50\u7684\u673a\u4f1a\u3002\u201d \u3000\u3000\u201c\u8fd9\u4f4d\u5144\u53f0\u7684\u8eab\u624b\uff0c\u786e\u5b9e\u9ad8\u660e\uff0c\u6211\u8c2d\u67d0\u751f\u5e73\u4ec5\u89c1\uff0c\u4e0d\u8fc7\uff0c\u4f60\u4eec\u4e5f\u8bb8\u4e0d\u77e5\u9053\u8fd9\u6d3b\u86ca\u7684\u5389\u5bb3\u3002\u201d \u3000\u3000\u201c\u6b63\u8981\u5411\u4f60\u8bf7\u6559\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u638c\u4e0a\u5185\u529b\u7a81\u51fa\uff0c\u8c2d\u9e9f\u7a81\u89c9\u5168\u8eab\u65e0\u529b\uff0c\u53cc\u624b\u5782\u843d\uff0c\u79bb\u5f00\u4e86\u846b\u82a6\u3002 \u3000\u3000\u5f20\u56db\u59d1\u6025\u884c\u4e00\u6b65\uff0c\u628a\u4e24\u53ea\u62a4\u8033\u53d6\u4e0b\uff0c\u5957\u5728\u94dc\u846b\u82a6\u53e3\u7684\u76d6\u5b50\u4e0a\uff0c\u9093\u98de\u5374\u4e00\u628a\u62d6\u8fc7\u8c2d\u9e9f\uff0c\u987a\u624b\u70b9\u4e86\u8c2d\u9e9f\u4e24\u5904\u7a74\u9053\u3002 \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u9093\u5144\uff0c\u4ea4\u7ed9\u4f60\u4e86\uff0c\u95ee\u95ee\u4ed6\u86ca\u6bd2\u7684\u4e8b\u3002\u201d \u3000\u3000\u201c\u662f\uff01\u2026\u2026\u201d\u9093\u98de\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u8c2d\u9e9f\uff0c\u5e0c\u671b\u4f60\u5408\u4f5c\uff0c\u6211\u4e0d\u4f1a\u591a\u95ee\uff01\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u6c5f\u67ab\u7b49\u90fd\u5df2\u53d6\u4e0b\u62a4\u8033\u53e3\u7f69\u3002 \u3000\u3000\u201c\u9b54\u773c\u9093\u98de\u2026\u2026\u201d\u8c2d\u9e9f\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u5728\u6d1b\u9633\u5c45\u4e2d\u4f5c\u603b\u7ba1\u5417\uff1f\u96be\u9053\u5929\u9a6c\u5802\u771f\u7684\u548c\u5929\u540e\u5bab\u5e72\u4e0a\u4e86\uff1f\u201d \u3000\u3000\u201c\u5929\u540e\u5bab\u3001\u5929\u738b\u95e8\u3001\u5929\u9a6c\u5802\uff0c\u5168\u662f\u5929\u5b57\u6392\u540d\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u9053\uff1a\u201c\u8fd9\u4e00\u4e2a\u5929\u5b87\u4e00\u5b9a\u4ee3\u8868\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u5929\u540e\u5bab\u5c31\u662f\u5185\u5bab\u4e86\u2026\u2026\u201d\u6c5f\u67ab\u76ee\u6ce8\u9093\u98de\u5411\u9053\uff1a\u201c\u5185\u5bab\u7684\u5927\u5bab\u4e3b\u542c\u8bf4\u539f\u662f\u5929\u738b\u95e8\u95e8\u4e3b\u7684\u592b\u4eba\uff0c\u592b\u59bb\u4e24\u4eba\u5374\u5206\u638c\u4e86\u4e24\u4e2a\u95e8\u6237\uff0c\u662f\u4e0d\u662f\u771f\u7684\u95f9\u7ffb\u4e86\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4ef6\u4e8b\u8be1\u79d8\u96be\u6d4b\u2026\u2026\u201d\u9093\u98de\u9053\uff1a\u201c\u662f\u771f\u662f\u5047\u5927\u6982\u8fde\u6885\u82b1\u4e5f\u641e\u4e0d\u6e05\u695a\uff1f\u201d \u3000\u3000\u201c\u5148\u95ee\u86ca\u6bd2\u7684\u4e8b\u5427\uff01\u2026\u2026\u201d\u5f20\u56db\u59d1\u671b\u7740\u6c5f\u67ab\u7b11\u9053\uff1a\u201c\u60f3\u4e86\u89e3\u5929\u540e\u5bab\u7684\u4e8b\uff0c\u4f55\u4e0d\u5411\u5927\u59d0\u8bf7\u6559\uff1f\u8fde\u6885\u82b1\u4e5f\u4e0d\u4f1a\u6bd4\u6211\u77e5\u9053\u7684\u591a\u3002\u201d \u3000\u3000\u6c5f\u67ab\u70b9\u70b9\u5934\u3002 \u3000\u3000\u9093\u98de\u53f3\u624b\u4e00\u6c89\uff0c\u98df\u3001\u4e2d\u4e8c\u6307\uff0c\u5df2\u6309\u5728\u8c2d\u9e9f\u7684\u53f3\u773c\u7736\u4e0a\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u6709\u79cd\u8bf4\u8c0e\uff0c\u90a3\u5c31\u8981\u8bf4\u7684\u5929\u8863\u65e0\u7f1d\uff0c\u53ea\u8981\u6211\u4eec\u542c\u51fa\u7834\u7efd\uff0c\u5c31\u5148\u6316\u6389\u8fd9\u53ea\u773c\u775b\uff0c\u4f60\u8fd8\u6709\u5de6\u773c\u3001\u53cc\u624b\u3001\u4e24\u53ea\u811a\uff0c\u54b1\u4eec\u6162\u6162\u6765\uff0c\u4f60\u8c2d\u9e9f\u81ea\u4fe1\u80fd\u5fcd\u53d7\u6316\u773c\u3001\u65ad\u80a2\u7684\u75db\u82e6\uff0c\u90a3\u5c31\u4e0d\u7528\u56de\u7b54\u4e86\u3002\u201d \u3000\u3000\u6c5f\u67ab\u542c\u5f97\u5fc3\u5934\u4e00\u9707\uff0c\u5fd6\u9053\uff1a\u201c\u9635\u524d\u6740\u654c\uff0c\u6325\u5200\u53d6\u547d\uff0c\u6740\u5b83\u4e2a\u6a2a\u5c38\u904d\u91ce\uff0c\u6211\u5927\u6982\u8fd8\u4e0d\u4f1a\u624b\u8f6f\uff0c\u50cf\u8fd9\u6837\u6316\u773c\u65ad\u80a2\uff0c\u5927\u6574\u6d3b\u4eba\u7684\u624b\u6bb5\uff0c\u6211\u7edd\u5bf9\u4e0b\u4e0d\u4e86\u624b\u3002\u201d \u3000\u3000\u201c\u4e09\u4f4d\u8981\u95ee\u4ec0\u4e48\uff1f\u53ef\u4ee5\u95ee\u4e86\u3002\u201d \u3000\u3000\u9093\u98de\u98df\u3001\u4e2d\u4e8c\u6307\u5df2\u538b\u5165\u4e86\u8c2d\u9e9f\u773c\u7736\u4e09\u5206\uff0c\u522b\u8bf4\u5f53\u4e8b\u4eba\u4e86\uff0c\u5c31\u662f\u5c40\u5916\u4eba\u4e5f\u770b\u5f97\u51fa\u6765\uff0c\u8fd9\u662f\u771f\u7684\u4e0b\u624b\uff0c\u7edd\u975e\u865a\u58f0\u606b\u5413\u3002 \u3000\u3000\u201c\u77e5\u9053\u7684\u6211\u4e00\u5b9a\u8bf4\u2026\u2026\u201d\u8c2d\u4ee5\u4e5f\u771f\u7684\u6015\u4e86\uff0c\u6316\u6389\u4e24\u53ea\u773c\u775b\uff0c\u4e0d\u4f46\u4eba\u4f1a\u6b8b\u5e9f\uff0c\u90a3\u79cd\u75db\u82e6\uff0c\u4e5f\u5c06\u662f\u6781\u96be\u5fcd\u53d7\u3002 \u3000\u3000\u5f20\u56db\u59d1\u770b\u4e86\u8c2d\u9e9f\u4e00\u773c\uff0c\u9053\uff1a\u201c\u5148\u8bf4\u8fd9\u4e24\u4e2a\u94dc\u846b\u82a6\u4e2d\u7684\u6d3b\u86ca\u662f\u4ec0\u4e48\u4e1c\u897f\uff1f\u201d \u3000\u3000\u201c\u597d\u50cf\u662f\u91d1\u8695\u86ca\uff0c\u2026\u2026\u201d\u8c2d\u9e9f\u9053\uff1a\u201c\u4e8b\u5b9e\u4e0a\uff0c\u6211\u6ca1\u6709\u7740\u5230\u8fc7\uff0c\u8fd9\u4e1c\u897f\uff0c\u4e0d\u662f\u7ec3\u86ca\u7684\u4eba\uff0c\u7edd\u4e0d\u80fd\u89c1\uff0c\u540d\u53eb\u91d1\u8695\uff0c\u5b9e\u5219\u4f1a\u98de\uff0c\u5176\u6027\u51f6\u6b8b\uff0c\u89c1\u4eba\u5c31\u54ac\uff0c\u5c31\u662f\u731b\u72ac\u98de\u79bd\uff0c\u9047\u4e0a\u4e86\u4e5f\u96be\u5e78\u514d\u3002 \u3000\u3000\u636e\u8bf4\u662f\u6d3b\u86ca\u4e2d\u6700\u53ef\u6015\u7684\u4e00\u79cd\uff0c\u56e0\u4e3a\u5b83\u4f53\u578b\u7ec6\u5c0f\uff0c\u98de\u884c\u5feb\u901f\uff0c\u80fd\u591f\u94bb\u5165\u4eba\u4f53\uff0c\u8fd9\u4e9b\u90fd\u662f\u542c\u4ed6\u4eec\u8bf4\u7684\u3002\u201d \u3000\u3000\u201c\u4f60\u4eec\u51c6\u5907\u591c\u95f4\u653e\u51fa\uff0c\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u96be\u9053\u672c\u8eab\u5c31\u4e0d\u6015\u4f24\u5bb3\u5417\uff1f\u201d \u3000\u3000\u201c\u7ec3\u86ca\u4eba\u81ea\u7136\u4e0d\u6015\u2026\u2026\u201d\u8c2d\u9e9f\u9053\uff1a \u3000\u3000\u201c\u800c\u4e14\uff0c\u4ed6\u4eec\u6709\u4e00\u79cd\u836f\u7269\u53ef\u4ee5\u4f7f\u98de\u86ca\u4e0d\u6562\u8fd1\u8eab\uff0c\u53ea\u4e0d\u8fc7\u836f\u6548\u53ea\u6709\u56db\u4e2a\u65f6\u8fb0\uff0c\u8fc7\u65f6\u65e0\u6548\u3002\u201d \u3000\u3000\u201c\u662f\u5916\u64e6\u8fd8\u662f\u5185\u670d\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u9053\uff1a\u201c\u836f\u7269\u662f\u5426\u8fd8\u5728\u4e24\u4eba\u8eab\u4e0a\uff1f\u201d \u3000\u3000\u201c\u5e94\u8be5\u8fd8\u5728\uff0c\u662f\u88c5\u5728\u74f7\u74f6\u4e2d\u7684\u836f\u6c34\u2026\u2026\u201d\u8c2d\u4ee5\u9053\uff1a\u201c\u53ea\u8981\u64e6\u5728\u8eab\u4e0a\u5c11\u8bb8\uff0c\u56db\u4e2a\u65f6\u8fb0\u5185\uff0c\u98de\u86ca\u5c31\u4e0d\u6562\u8fd1\u8eab\u3002\u201d \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u7acb\u523b\u52a8\u624b\uff0c\u679c\u7136\u5728\u4e24\u4eba\u8eab\u4e0a\u641c\u51fa\u4e86\u4e24\u4e2a\u74f7\u74f6\uff0c\u74f6\u4e2d\u6ee1\u84c4\u6c34\u6c41\uff0c\u4e00\u80a1\u6de1\u6de1\u7684\u9999\u5473\uff0c\u7531\u74f6\u4e2d\u900f\u51fa\u3002 \u3000\u3000\u548c\u5c1a\u5012\u4e00\u70b9\u5728\u638c\u5fc3\u77a7\u7740\uff0c\u53ea\u89c1\u5176\u8272\u7fe0\u7eff\uff0c\u4f3c\u662f\u7531\u591a\u79cd\u8349\u836f\u5408\u914d\u800c\u6210\uff0c\u5374\u77a7\u4e0d\u51fa\u662f\u4ec0\u4e48\u4e1c\u897f\uff1f \u3000\u3000\u6447\u6447\u5934\u53f9\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u548c\u5c1a\u662f\u77a7\u4e0d\u51fa\u4e00\u70b9\u540d\u5802\uff0c\u5f20\u59d1\u5a18\u7cbe\u4e60\u533b\u535c\u5947\u672f\uff0c\u80fd\u4e0d\u80fd\u770b\u51fa\u4e00\u70b9\u95e8\u9053\uff1f\u201d \u3000\u3000\u201c\u770b\u4e0d\u51fa\u6765\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u6211\u5b66\u7684\u867d\u662f\u65c1\u95e8\u5de6\u9053\uff0c\u4f46\u5bf9\u86ca\u672f\u5374\u5b8c\u5168\u5916\u884c\uff0c\u8fd9\u79cd\u5357\u8352\u6bd2\u7269\uff0c\u4e0d\u6df1\u5165\u5176\u5730\uff0c\u4f5c\u957f\u65f6\u95f4\u505c\u7559\u7814\u7a76\uff0c\u53ea\u6015\u96be\u660e\u5176\u7406\u3002\u201d \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u5408\u4e0a\u74f6\u585e\u9053\uff1a\u201c\u8fd9\u4e24\u74f6\u836f\u7269\uff0c\u5c31\u7531\u5f20\u59d1\u5a18\u6536\u8d77\u6765\uff0c\u627e\u673a\u4f1a\u8bd5\u8bd5\u770b\uff0c\u5bf9\u522b\u7684\u6bd2\u7269\uff0c\u662f\u5426\u4e5f\u6709\u514b\u5236\u4f5c\u7528\uff1f\u201d \u3000\u3000\u5f20\u56db\u59d1\u6536\u597d\u4e24\u4e2a\u74f7\u74f6\u540e\uff0c\u9053\uff1a\u201c\u8c2d\u9e9f\uff0c\u5982\u4f55\u624d\u80fd\u6bc1\u53bb\u8fd9\u4e9b\u6bd2\u7269\uff1f\u201d \u3000\u3000\u201c\u706b\u70e7\uff0c\u2026\u2026\u201d\u8c2d\u9e9f\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u8981\u76f8\u5f53\u7684\u70ed\u5ea6\uff0c\u624d\u53ef\u4ee5\u4f7f\u5b83\u6b7b\u4ea1\uff0c\u94dc\u74f6\u53ef\u4ee5\u8010\u4e45\uff0c\u4f46\u74f6\u53e3\u4e24\u6728\u585e\uff0c\u53ea\u6015\u96be\u6297\u706b\u52bf\uff0c\u4e00\u65e6\u6728\u585e\u70e7\u6bc1\uff0c\u6bd2\u7269\u8fd8\u672a\u6b7b\u53bb\uff0c\u88ab\u86ca\u98de\u4e86\u51fa\u6765\uff0c\u5728\u573a\u4e4b\u4eba\uff0c\u90fd\u5c06\u96be\u9003\u751f\u3002\u201d \u3000\u3000\u201c\u6211\u4eec\u6709\u907f\u6bd2\u836f\u6c34\uff0c\u2026\u2026\u201d\u5f20\u56db\u59d1\u7b11\u9053\uff1a\u201c\u6b7b\u4e8e\u6bd2\u86ca\u4e4b\u53e3\uff0c\u53ea\u662f\u4f60\u4e00\u4e2a\u7f62\u4e86\u3002\u201d \u3000\u3000\u201c\u90a3\u5c31\u8bf7\u5f20\u59d1\u5a18\u5148\u6740\u4e86\u6211\u5427\uff01\u2026\u2026\u201d\u8c2d\u9e9f\u9053\uff1a\u201c\u6b7b\u4e8e\u6d3b\u86ca\u4e4b\u53e3\uff0c\u5176\u82e6\u65e0\u6bd4\u3002\u201d \u3000\u3000\u201c\u4f60\u5982\u4e0d\u60f3\u6b7b\u4e8e\u86ca\u6bd2\u4e4b\u4e0b\uff0c\u90a3\u5c31\u81ea\u5df1\u8bf4\u51fa\u6765\u5427\uff01\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u8bdd\u8981\u7b80\u77ed\u3001\u660e\u786e\u3002\u201d \u3000\u3000\u201c\u8bf4\u5b8c\u4e4b\u540e\u5462\uff1f\u2026\u2026\u201d\u8c2d\u9e9f\u9053\uff1a\u201c\u662f\u4e0d\u662f\u5c31\u653e\u4e86\u6211\uff1f\u201d \u3000\u3000\u201c\u4f60\u6ca1\u8ba8\u4ef7\u7684\u672c\u94b1\uff0c\u8c2d\u9e9f\u2026\u2026\u201d \u3000\u3000\u9093\u98de\u53f3\u624b\u52a0\u529b\uff0c\u8c2d\u9e9f\u773c\u7736\u7686\u88c2\uff0c\u6d41\u51fa\u9c9c\u8840\u3002 \u3000\u3000\u201c\u6211\u8bf4\uff0c\u6211\u8bf4\u2026\u2026\u201d\u8c2d\u9e9f\u9053\uff1a\u201c\u4e09\u5bab\u4e3b\u4ee5\u4e07\u4e24\u9ec4\u91d1\u7684\u4ee3\u4ef7\uff0c\u7ea6\u8bf7\u4e86\u4e07\u86ca\u95e8\u4e2d\u4eba\u65bd\u653e\u86ca\u6bd2\uff0c\u5c0f\u53ef\u56e0\u901a\u6653\u5357\u8352\u571f\u8bed\uff0c\u5c31\u6d3e\u4efb\u4f7f\u8005\u3002\u201d \u3000\u3000\u201c\u90a3\u662f\u8bf4\uff0c\u5929\u540e\u5bab\u4e2d\u4eba\uff0c\u8fd8\u672a\u548c\u4e07\u86ca\u95e8\u76f4\u63a5\u4ea4\u5f80\u4e86\uff1f\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u9664\u4f60\u4e4b\u5916\uff0c\u8fd8\u6709\u4ec0\u4e48\u4eba\u80fd\u548c\u4e07\u86ca\u95e8\u4e2d\u4eba\u63a5\u5f97\u4e0a\u5934\uff1f\u201d \u3000\u3000\u201c\u90a3\u5730\u65b9\u50fb\u5904\u6df1\u5c71\uff0c\u4ee5\u5404\u79cd\u86ca\u6bd2\u8bbe\u9632\uff0c\u4e0d\u4f1a\u5357\u8352\u571f\u8bed\uff0c\u7edd\u96be\u63a5\u8fd1\u2026\u2026\u201d\u8c2d\u9e9f\u9053\uff1a\u201c\u4e8b\u5b9e\u4e0a\u4e07\u86ca\u95e8\u4e2d\u7684\u4eba\u6570\u4e0d\u591a\uff0c\u4ed6\u4eec\u5c31\u5357\u8352\u571f\u4eba\u4e2d\u9009\u51fa\u53ef\u9020\u4e4b\u624d\uff0c\u5f15\u8fdb\u4e07\u86ca\u95e8\uff0c\u4e0d\u662f\u4efb\u4f55\u4eba\u90fd\u53ef\u4ee5\u6295\u9760\u7684\u3002\u201d \u3000\u3000\u201c\u6709\u6ca1\u6709\u5973\u5f1f\u5b50\uff1f\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u5979\u4eec\u662f\u4e0d\u662f\u957f\u7684\u548c\u6b7b\u7684\u4e24\u4e2a\u4eba\u4e00\u6837\u96be\u770b\uff1f\u201d \u3000\u3000\u201c\u4e0d\u662f\uff0c\u2026\u2026\u201d\u8c2d\u9e9f\u9053\uff1a\u201c\u4e0d\u77e5\u9053\u4ec0\u4e48\u539f\u56e0\uff0c\u4e07\u86ca\u95e8\u4e2d\u7684\u5973\u5f1f\u5b50\uff0c\u4e2a\u4e2a\u5982\u82b1\u4f3c\u7389\uff0c\u80a4\u8272\u767d\u91cc\u900f\u7ea2\uff0c\u7537\u7684\u5374\u5341\u4e4b\u4e03\u516b\uff0c\u90fd\u957f\u7684\u53c8\u9ed1\u53c8\u58ee\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u4f60\u5728\u4e07\u86ca\u95e8\u4e2d\uff0c\u73a9\u7684\u4e00\u5b9a\u5f88\u5f00\u5fc3\u4e86\uff01\u201d \u3000\u3000\u201c\u60f3\u5230\u5979\u4eec\u4e00\u8eab\u662f\u86ca\uff0c\u5c0f\u53ef\u54ea\u6709\u8fd9\u4e2a\u80c6\u5b50\u2026\u2026\u201d\u8c2d\u9e9f\u9053\uff1a\u201c\u5a07\u5a03\u6709\u6bd2\u3001\u73ab\u7470\u591a\u523a\uff0c\u6211\u53ea\u80fd\u9971\u9971\u773c\u798f\uff0c\u5c31\u7b97\u6709\u4e00\u4eb2\u82b3\u6cfd\u7684\u673a\u4f1a\uff0c\u4e5f\u4e0d\u6562\u8f7b\u6613\u5c1d\u8bd5\uff01\u201d \u3000\u3000\u5f20\u56db\u59d1\u770b\u4e86\u6c5f\u67ab\u4e00\u773c\uff0c\u9053\uff1a\u201c\u5c0f\u5fc3\u554a\uff01\u5c31\u6211\u6240\u77e5\uff0c\u5357\u8352\u786e\u6709\u4e00\u65cf\u6c34\u6446\u5937\u4eba\uff0c\u7537\u4eba\u751f\u7684\u5927\u90fd\u7c97\u58ee\u9edd\u9ed1\uff0c\u5973\u7684\u5374\u7ec6\u76ae\u767d\u8089\uff0c\u5a07\u5ae9\u5982\u82b1\u3002\u5206\u5e03\u5728\u5357\u8352\u5341\u4e07\u5927\u5c71\u4e4b\u4e2d\uff0c\u5979\u4eec\u559c\u7231\u5e72\u51c0\uff0c\u5e38\u5e38\u8131\u5149\u8863\u670d\uff0c\u5728\u6da7\u6eaa\u4e2d\u620f\u6c34\u4f5c\u800d\u3002 \u3000\u3000\u5979\u4eec\u867d\u4e0d\u662f\u4e07\u86ca\u95e8\u4e2d\u4eba\uff0c\u4f46\u4fd8\u63b3\u60c5\u90ce\u7684\u624b\u6bb5\uff0c\u5374\u5f88\u53ef\u6015\uff0c\u4f1a\u5728\u996e\u98df\u4e2d\u6697\u653e\u4e00\u79cd\u86ca\u836f\uff0c\u518d\u914d\u5236\u4e00\u79cd\u6291\u5236\u7684\u836f\u7269\uff0c\u8ba9\u4f60\u670d\u7528\uff0c\u4f60\u53ea\u8981\u79bb\u5f00\u5979\u4eec\uff0c\u86ca\u6bd2\u5c31\u4f1a\u53d1\u4f5c\uff0c\u90a3\u53ef\u4e0d\u662f\u4e00\u822c\u7684\u89e3\u6bd2\u836f\u7269\u80fd\u591f\u6cbb\u7597\u5566\uff01\u201d \u3000\u3000\u8bdd\u662f\u8bf4\u7ed9\u5927\u5bb6\u542c\uff0c\u4f46\u76ee\u5149\u5374\u4e00\u76f4\u5728\u6c5f\u67ab\u8138\u4e0a\u6253\u8f6c\uff0c\u628a\u6c5f\u67ab\u8138\u90fd\u770b\u7ea2\u4e86\u3002 \u3000\u3000\u201c\u4e0d\u9519\u2026\u2026\u201d\u8c2d\u9e9f\u9053\uff1a\u201c\u6c34\u6446\u5937\u53ea\u662f\u4e00\u4e2a\u7edf\u79f0\uff0c\u65cf\u5185\u53c8\u5206\u5361\u94a6\u3001\u5361\u74e6\u7b49\u5341\u51e0\u4e2a\u65cf\u7c7b\uff0c\u5176\u4e2d\u6709\u4e24\u4e09\u4e2a\u65cf\uff0c\u90fd\u4f1a\u7528\u86ca\u3002 \u3000\u3000\u4f46\u90a3\u53ea\u662f\u666e\u901a\u7684\u7528\u86ca\u624b\u6cd5\uff0c\u4f60\u53ea\u8981\u5c0f\u5fc3\u996e\u98df\uff0c\u5c31\u53ef\u4ee5\u9884\u9632\uff0c\u4f46\u4e07\u86ca\u95e8\uff0c\u5374\u662f\u4e13\u95e8\u73a9\u86ca\u7684\u4eba\u3002 \u3000\u3000\u4ed6\u4eec\u70bc\u5236\u86ca\u836f\uff0c\u9972\u517b\u6d3b\u86ca\uff0c\u7ec8\u65e5\u548c\u86ca\u6bd2\u4e3a\u4f0d\uff0c\u636e\u8bf4\u70bc\u5230\u767b\u5cf0\u9020\u6781\u7684\u5883\u754c\uff0c\u80fd\u628a\u6d3b\u86ca\u85cf\u5728\u8eab\u4e0a\uff0c\u6325\u624b\u6295\u8db3\uff0c\u5c31\u53ef\u653e\u86ca\u4f24\u4eba\u3002\u201d \u3000\u3000\u201c\u518d\u56de\u7b54\u6700\u540e\u4e00\u4e2a\u95ee\u9898\uff0c\u6211\u867d\u672a\u53bb\u8fc7\u5357\u8352\uff0c\u4f46\u6211\u5bf9\u4e07\u86ca\u95e8\u5374\u6709\u4e00\u4e9b\u8ba4\u8bc6\uff0c\u4f60\u6700\u597d\u636e\u5b9e\u56de\u7b54\uff0c\u4e0d\u77e5\u9053\u7684\uff0c\u5c31\u4e0d\u8981\u52c9\u5f3a\uff0c\u968f\u53e3\u80e1\u8bcc\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u9664\u4e86\u8fd9\u4e24\u4e2a\u4eba\u5916\uff0c\u662f\u5426\u53ef\u80fd\u8fd8\u6709\u522b\u7684\u4eba\u8ddf\u6765\uff1f\u201d \u3000\u3000\u201c\u8fd9\u5012\u6ca1\u6709\uff0c\u4e0d\u8fc7\uff0c\u8fd9\u4e24\u4eba\u90fd\u662f\u4e07\u86ca\u95e8\u4e2d\u7684\u5f79\u86ca\u9ad8\u624b\uff0c\u4ed6\u4eec\u8ddf\u6211\u8fdb\u6765\u4e2d\u539f\uff0c\u662f\u53d7\u4e86\u95e8\u4e3b\u4e4b\u547d\u2026\u2026\u201d\u8c2d\u9e9f\u9053\uff1a\u201c\u5982\u82e5\u4e45\u4e0d\u5357\u5f52\uff0c\u4e07\u86ca\u95e8\u4e2d\u4eba\u4e00\u5b9a\u4f1a\u627e\u6765\u6b64\u5730\u2026\u2026\u201d \u3000\u3000\u201c\u80e1\u8bf4\uff0c\u4e2d\u539f\u5982\u6b64\u5e7f\u5927\uff0c\u4ed6\u4eec\u600e\u4e48\u4f1a\u627e\u5230\u8fd9\u91cc\u2026\u2026\u201d \u3000\u3000\u201c\u9093\u5144\uff0c\u2026\u2026\u201d\u8c2d\u9e9f\u6253\u65ad\u4e86\u9093\u98de\u7684\u8bdd\uff0c\u63a5\u9053\uff1a\u201c\u4ed6\u4eec\u6709\u4e00\u79cd\u5f88\u5947\u602a\u7684\u80fd\u529b\uff0c\u80fd\u8ffd\u8e2a\u5230\u4ed6\u4eec\u7684\u4eba\uff0c\u867d\u7136\u9065\u9694\u4e07\u91cc\uff0c\u5f62\u52bf\u964c\u751f\uff0c\u4f46\u4ed6\u4eec\u5374\u6709\u8ffd\u7d22\u7684\u80fd\u529b\u2026\u2026\u201d \u3000\u3000\u201c\u5982\u679c\u8fd9\u4e9b\u4eba\u5df2\u7ecf\u6b7b\u4e86\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u9053\uff1a\u201c\u4ed6\u4eec\u4e5f\u80fd\u627e\u5230\u5417\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4eec\u80fd\u4e07\u91cc\u8ffd\u7d22\uff0c\u8fd9\u4e9b\u4eba\u662f\u6b7b\u662f\u6d3b\uff0c\u90fd\u4e0d\u662f\u5f88\u91cd\u8981\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u628a\u5c38\u4f53\u70e7\u4e86\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u4ed6\u4eec\u662f\u5426\u8fd8\u6709\u8ffd\u8e2a\u7684\u80fd\u529b\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u2026\u2026\u201d\u8c2d\u7fa4\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u5e94\u8be5\u53ef\u4ee5\u2026\u2026\u201d \u3000\u3000\u201c\u7b80\u76f4\u80e1\u8bf4\u516b\u9053\uff0c\u201d\u9093\u98de\u51b7\u51b7\u63a5\u9053\uff1a\u201c\u95f4\u5173\u4e07\u91cc\uff0c\u5c71\u6cb3\u963b\u9694\u5c38\u4f53\u706b\u5316\u6210\u7070\uff0c\u4ed6\u4eec\u51ed\u4ec0\u4e48\u80fd\u591f\u627e\u5230\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4e2a\uff0c\u8c2d\u67d0\u4eba\u5c31\u4e0d\u77e5\u9053\u4e86\uff01\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\u2026\u2026\u201d\u9093\u98de\u9053\uff1a\u201c\u4f60\u901a\u6d88\u606f\u7ed9\u4ed6\u4eec\uff0c\u628a\u4f60\u4e5f\u7ed9\u5bb0\u4e86\uff0c\u90a3\u5c31\u5929\u4e0b\u592a\u5e73\u3002\u201d \u3000\u3000\u8bf4\u5bb0\u5c31\u5bb0\uff0c\u5de6\u624b\u63e1\u62f3\uff0c\u51c6\u5907\u51fb\u4e0b\u3002 \u3000\u3000\u201c\u7b49\u4e00\u7b49\u2026\u2026\u201d\u5f20\u56db\u59d1\u62e6\u4f4f\u4e86\u9093\u98de\uff0c\u9053\uff1a\u201c\u4ed6\u4eec\u8ffd\u8e2a\u7684\u4e0d\u662f\u4eba\u3002\u662f\u86ca\u2026\u2026\u201d \u3000\u3000\u201c\u86ca\u4e5f\u88ab\u70e7\u6b7b\u4e86\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u96be\u9053\u70e7\u5316\u6210\u7070\u7684\u86ca\uff0c\u8fd8\u80fd\u590d\u6d3b\uff1f\u201d \u3000\u3000\u201c\u6211\u8be5\u8bf4\u7684\u6e05\u695a\u4e9b\uff0c\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u4ed6\u4eec\u7528\u86ca\u672f\u8ffd\u8e2a\uff0c\u81f3\u4e8e\u5982\u4f55\u4e00\u4e2a\u8ffd\u6cd5\uff0c\u90a3\u5c31\u975e\u6211\u80fd\u77e5\u4e86\uff01\u201d \u3000\u3000\u201c\u5927\u59d0\uff0c\u8fd9\u662f\u4f60\u7684\u63e3\u6d4b\u4e86\uff1f\u201d \u3000\u3000\u201c\u6211\u4e5f\u4f1a\u8ffd\u8e2a\u672f\u2026\u2026\u201d\u5f20\u56db\u59d1\u7b11\u9053\uff1a\u201c\u7a0d\u52a8\u624b\u811a\uff0c\u5c31\u4e0d\u6015\u4ed6\u6d41\u7a9c\u5343\u91cc\uff0c\u4e0d\u8fc7\uff0c\u6211\u7684\u8ffd\u8e2a\u80fd\u529b\u53ea\u6709\u4e00\u4e2a\u6708\uff0c\u4e00\u4e2a\u6708\u5185\u8ffd\u4e0d\u4e0a\uff0c\u90a3\u5c31\u4efb\u4eba\u900d\u9065\u4e86\uff0c\u6240\u4ee5\uff0c\u6211\u76f8\u4fe1\u4ed6\u4eec\u6709\u8ffd\u7d22\u4e07\u91cc\u7684\u80fd\u529b\u3002\u201d \u3000\u3000\u201c\u5f20\u59d1\u5a18\u8bf4\u7684\u5bf9\uff01\u4ed6\u4eec\u662f\u86ca\u672f\u8ffd\u8e2a\u2026\u2026\u201d\u8c2d\u9e9f\u76ee\u5149\u4e00\u63a0\u4e24\u5c38\u4f53\uff0c\u9053\uff1a\u201c\u4ed6\u4eec\u6765\u65f6\uff0c\u5e38\u5728\u9014\u4e2d\u505c\u4e0b\u6765\uff0c\u6492\u4e00\u4e9b\u4e1c\u897f\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u6837\u7684\u4e1c\u897f\uff1f\u4f60\u770b\u5230\u8fc7\u6ca1\u6709\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u76f8\u9694\u591a\u5c11\u8ddd\u79bb\uff1f\u201d \u3000\u3000\u201c\u6211\u4ed4\u7ec6\u7684\u770b\u8fc7\uff0c\u4f46\u770b\u4e0d\u51fa\u4ec0\u4e48\u4e1c\u897f\u2026\u2026\u201d\u8c2d\u9e9f\u9053\uff1a\u201c\u5927\u6982\u76f8\u8ddd\u4e94\u5341\u91cc\u5de6\u53f3\u5427\uff0c\u4f46\u5982\u6d89\u6c34\u800c\u8fc7\u65f6\uff0c\u6cb3\u4e24\u5cb8\u90fd\u52a8\u624b\u811a\uff0c\u597d\u50cf\u662f\u4e00\u79cd\u7c89\u672b\uff0c\u6df7\u5165\u571f\u4e2d\uff0c\u5c31\u4e0d\u89c1\u75d5\u8ff9\u4e86\u3002\u201d \u3000\u3000\u201c\u4ed6\u4eec\u4e5f\u80fd\u8ffd\u7d22\u654c\u4eba\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u6211\u6740\u6b7b\u4ed6\u4eec\uff0c\u4ed6\u4eec\u7684\u4eba\u4f1a\u4e0d\u4f1a\u957f\u8ffd\u6211\u5462\uff1f\u201d \u3000\u3000\u201c\u5e94\u8be5\u4e0d\u4f1a\u2026\u2026\u201d\u8c2d\u51fa\u9053\uff1a\u201c\u4ed6\u4eec\u8fde\u653e\u86ca\u90fd\u6765\u4e0d\u53ca\uff0c\u4e0d\u53ef\u80fd\u5728\u5c11\u4fa0\u8eab\u4e0a\u52a8\u4ec0\u4e48\u624b\u811a\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u7b11\u9053\uff1a\u201c\u6c5f\u67ab\uff0c\u653e\u4e86\u8c2d\u9e9f\u5427\uff01\u201d \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u597d\uff01\u5927\u59d0\u66ff\u4ed6\u6c42\u60c5\uff0c\u5c0f\u5f1f\u7109\u6562\u4e0d\u51c6\uff0c\u9093\u98de\uff0c\u653e\u4e86\u4ed6\uff01\u201d \u3000\u3000\u9093\u98de\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u542c\u542c\uff0c\u8c2d\u9e9f\uff0c\u8fd9\u5c31\u662f\u6b63\u3001\u90aa\u4e0d\u540c\u4e4b\u5904\uff0c\u4f60\u5c0f\u5b50\u8fd0\u6c14\u5b9e\u5728\u4e0d\u9519\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u62cd\u62cd\u8c2d\u9e9f\u7684\u80a9\u5934\uff0c\u9053\uff1a\u201c\u4f60\u53ef\u4ee5\u8d70\u4e86\u3002\u201d \u3000\u3000\u201c\u6211\u2026\u2026\u6211\u2026\u2026\u201d \u3000\u3000\u8c2d\u9e9f\u7ad9\u8d77\u8eab\u5b50\uff0c\u5374\u6ca1\u6709\u79bb\u5f00\u3002 \u3000\u3000\u201c\u600e\u4e48\uff1f\u662f\u4e0d\u662f\u60f3\u7559\u4e0b\u70b9\u4e1c\u897f\u518d\u8d70\uff01\u201d\u9093\u98de\u51b7\u51b7\u5730\u8bf4\u3002 \u3000\u3000\u201c\u6211\u7684\u6b66\u529f\uff0c\u662f\u4e0d\u662f\u88ab\u5e9f\u4e86\uff1f\u201d\u8c2d\u9e9f\u770b\u770b\u6c5f\u67ab\uff0c\u9053\uff1a\u201c\u6211\u73b0\u5728\u5168\u8eab\u65e0\u529b\uff0c\u6574\u4e2a\u4eba\u90fd\u4e0d\u5bf9\u52b2\uff0c\u653e\u4e86\u6211\uff0c\u6211\u4e5f\u9003\u4e0d\u8fc7\u5929\u540e\u5bab\u4e2d\u4eba\u7684\u8ffd\u6740\uff01\u201d \u3000\u3000\u6c5f\u67ab\u5fae\u5fae\u4e00\u7b11\uff0c\u53f3\u624b\u6325\u52a8\uff0c\u62cd\u4e86\u8c2d\u9e9f\u4e09\u5904\u7a74\u4f4d\uff0c\u9053\uff1a\u201c\u73b0\u5728\u53ef\u4ee5\u8d70\u4e86\u3002\u201d \u3000\u3000\u8c2d\u9e9f\u8fd0\u6c14\u4e00\u8bd5\uff0c\u679c\u7136\u6b66\u529f\u5c3d\u590d\uff0c\u7acb\u523b\u8eac\u4e00\u793c\uff0c\u9053\uff1a\u201c\u5c0f\u53ef\u5f85\u544a\u9000\uff0c\u4e0d\u6740\u4e4b\u6069\uff0c\u6c38\u94ed\u80ba\u8151\uff0c\u8bb0\u7740\uff0c\u5728\u94dc\u846b\u82a6\u53e3\u5904\uff0c\u518d\u52a0\u4e00\u5708\u62a4\u94c1\u518d\u70e7\uff0c\u86ca\u6bd2\u53ef\u6015\uff0c\u4e0d\u53ef\u5927\u610f\u3002\u201d \u3000\u3000\u8f6c\u8fc7\u8eab\u5b50\uff0c\u5927\u6b65\u800c\u53bb\u3002 \u3000\u3000\u201c\u9b3c\u5f71\u5b50\u72e1\u8bc8\u7684\u5f88\u2026\u2026\u201d\u9093\u98de\u9053\uff1a\u201c\u8fd9\u6837\u653e\u4e86\u4ed6\uff0c\u6211\u62c5\u5fc3\u662f\u7eb5\u864e\u5f52\u5c71\u3002\u201d \u3000\u3000\u201c\u653e\u5fc3\u5427\uff01\u9093\u5144\uff0c\u6211\u4f1a\u638c\u63e1\u4f4f\u4ed6\u7684\u884c\u8e2a\uff0c\u2026\u2026\u201d\u5f20\u56db\u59d1\u7b11\u9053\uff1a\u201c\u4ed6\u5982\u56de\u5230\u4e09\u5bab\u4e3b\u90a3\u91cc\uff0c\u6211\u4eec\u518d\u6293\u56de\u6765\uff0c\u8fd8\u53ef\u4ee5\u95ee\u51fa\u4e0d\u5c11\u673a\u5bc6\uff0c\u90a3\u65f6\u5019\uff0c\u518d\u6740\u4ed6\u4e0d\u8fdf\u3002\u201d \u3000\u3000\u201c\u5f20\u59d1\u5a18\u4e4b\u80fd\uff0c\u6211\u548c\u5c1a\u4e00\u771f\u7684\u5fc3\u670d\u4e86\uff0c\u2026\u2026\u201d\u63d0\u8d77\u4e24\u4e2a\u94dc\u846b\u82a6\u63a5\u9053\uff1a\u201c\u6211\u53bb\u5904\u7406\u86ca\u6bd2\u2026\u2026\u201d \u3000\u3000\u201c\u5165\u571f\u4e3a\u5b89\uff0c\u6211\u53bb\u57cb\u4e86\u8fd9\u4e24\u5177\u5c38\u4f53\u3002\u201d \u3000\u3000\u9093\u98de\u6293\u8d77\u4e24\u5177\u5c38\u4f53\uff0c\u5411\u5916\u884c\u53bb\u3002 \u3000\u3000\u201c\u5927\u59d0\uff0c\u4eca\u540e\uff0c\u54b1\u4eec\u8be5\u5982\u4f55\u884c\u52a8\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u4e00\u628a\u62d6\u8fc7\u5f20\u56db\u59d1\uff0c\u63fd\u5165\u6000\u4e2d\u3002 \u3000\u3000\u5f20\u56db\u59d1\u4efb\u4ed6\u8f7b\u8584\u4e00\u9635\uff0c\u624d\u63a8\u5f00\u6c5f\u67ab\u9053\uff1a\u201c\u4f60\u660e\u5929\u4e00\u65e9\u52a8\u8eab\uff0c\u8d76\u5f80\u84bf\u5c71\u5c11\u6797\u5bfa\u53bb\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u2026\u2026\u201d\u6c5f\u67ab\u5947\u9053\uff1a \u3000\u3000\u201c\u5feb\u5200\u65a9\u4e71\u9ebb\uff0c\u4f55\u4e0d\u5148\u628a\u4e09\u5bab\u4e3b\u8fd9\u80a1\u529b\u91cf\u6d88\u706d\uff1f\u201d \u3000\u3000\u201c\u6211\u8d8a\u60f3\u8d8a\u4e0d\u5bf9\u4e86\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u6211\u4eec\u771f\u6b63\u7684\u654c\u4eba\uff0c\u4e0d\u662f\u5929\u9a6c\u5802\uff0c\u4e5f\u4e0d\u662f\u5929\u738b\u95e8\u548c\u5929\u540e\u5bab\uff01\u201d \u3000\u3000\u201c\u90a3\u4f1a\u662f\u8c01\u5462\uff1f\u201d \u3000\u3000\u201c\u6211\u4e0d\u77e5\u9053\uff0c\u6c5f\u67ab\uff0c\u4e0d\u7528\u5bf9\u7f57\u5170\u4ed6\u4eec\u89e3\u8bf4\u4ec0\u4e48\uff0c\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u6709\u4e9b\u4e8b\u8bf4\u51fa\u6765\u5f92\u4e71\u4eba\u610f\uff0c\u4f60\u53ea\u7ba1\u4e0b\u547d\u4ee4\u5c31\u884c\u4e86\uff0c\u800c\u4e14\uff0c\u4f60\u53ea\u5e26\u4e09\u4e2a\u4eba\u53bb\u3002\u201d \u3000\u3000\u201c\u90a3\u4e09\u4e2a\u4eba\uff1f\u201d \u3000\u3000\u201c\u5927\u548c\u5c1a\u548c\u4e24\u4e2a\u5c0f\u72d0\u5973\u3002\u201d \u3000\u3000\u201c\u5927\u59d0\u5462\uff1f\u2026\u2026\u201d\u6c5f\u67ab\u6025\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u8ddf\u6211\u53bb\uff1f\u201d \u3000\u3000\u201c\u6211\u548c\u6885\u82b1\u8fd8\u8981\u7559\u5728\u957f\u5b89\uff0c\u53ea\u662f\u7531\u660e\u8f6c\u6697\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u8981\u6b27\u9633\u662d\u548c\u7f57\u5170\u53ec\u96c6\u4e86\u8001\u53eb\u5316\u94b1\u7f3a\u4e00\u6279\u4eba\uff0c\u9041\u5165\u7ec8\u5357\u5c71\u4e2d\uff0c\u501f\u673a\u4f1a\u7531\u4e24\u4eba\u4f20\u6388\u6b66\u529f\uff0c\u8fd9\u6837\u53ef\u4ee5\u8ba9\u4ed6\u4eec\u8d23\u6709\u4e13\u53f8\uff0c\u4e5f\u53ef\u4ee5\u4f7f\u6211\u4eec\u591a\u4e00\u4e9b\u53ef\u7528\u7684\u4eba\uff0c\u6211\u4f1a\u627e\u673a\u4f1a\u548c\u9752\u51e4\u8fde\u7edc\uff0c\u8bf7\u5979\u5728\u6697\u4e2d\u5e2e\u5fd9\uff0c\u7b49\u6211\u957f\u5b89\u4e8b\u4e86\uff0c\u6211\u4f1a\u53bb\u627e\u4f60\u4eec\u3002\u201d \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u7b2c\u4e00\uff0c\u6211\u4e0d\u660e\u767d\u4e3a\u4ec0\u4e48\u8981\u53bb\u5c11\u6797\u5bfa\uff1f\u5c11\u6797\u5bfa\u65e9\u5df2\u7ecf\u88ab\u5929\u738b\u95e8\u3001\u5929\u9a6c\u5802\u6240\u7edf\u6cbb\uff0c\u7b2c\u4e8c\uff0c\u4f60\u4e0d\u8ddf\u6211\u4eec\u4e00\u8d77\u53bb\uff0c\u4e00\u65e6\u6709\u6211\u610f\u5916\u53d8\u5316\uff0c\u6211\u8981\u8ddf\u8c01\u5546\u91cf\uff1f\u201d \u3000\u3000\u5f20\u56db\u59d1\u8bf4\uff1a\u201c\u7b2c\u4e00\uff0c\u6211\u4e0d\u4fe1\u5c11\u6797\u5bfa\u4f1a\u88ab\u5b8c\u5168\u5f81\u773c\uff0c\u4ed6\u4eec\u6709\u5343\u5e74\u4f20\u7edf\uff0c\u7ecf\u8fc7\u4e86\u65e0\u6570\u7684\u98ce\u6d6a\uff0c\u65e9\u5df2\u7ecf\u6709\u4e86\u5e94\u53d8\u7684\u8c0b\u7565\uff0c\u5982\u679c\u6211\u731c\u7684\u4e0d\u9519\uff0c\u4f60\u53ef\u80fd\u4f1a\u6709\u5947\u9047\u3002 \u3000\u3000\u56e0\u4e3a\uff0c\u771f\u6b63\u7684\u6b66\u6280\u7cbe\u534e\uff0c\u8fd8\u662f\u5728\u5c11\u6797\u5bfa\u4e2d\uff0c\u5355\u662f\u4e03\u5341\u4e8c\u79cd\u7edd\u6280\uff0c\u5c31\u53ef\u4ee5\u8ba9\u4e16\u4e0a\u6700\u806a\u660e\u7684\u4eba\uff0c\u4e00\u8f88\u5b50\u4e5f\u5b66\u4e0d\u5168\uff0c\u2026\u2026\u201d\u5f20\u56db\u59d1\u63a5\u9053\uff1a\u201c\u7b2c\u4e8c\uff0c\u5927\u548c\u5c1a\u7684\u7ecf\u9a8c\u3001\u9605\u5386\uff0c\u80dc\u6211\u5341\u500d\uff0c\u4ed6\u8bf4\u4f69\u670d\u6211\uff0c\u53ea\u662f\u79f0\u8d5e\u6211\u7684\u5de6\u9053\u6280\u827a\uff0c\u5982\u8bba\u6599\u654c\u65ad\u4e8b\u4e4b\u80fd\uff0c\u4ed6\u7edd\u4e0d\u5728\u6211\u4e4b\u4e0b\u3002 \u3000\u3000\u7b2c\u4e09\uff0c\u4e24\u4e2a\u5c0f\u72d0\u5973\u548c\u4f60\u8d70\u5728\u4e00\u8d77\uff0c\u8131\u5f00\u4e86\u6211\u548c\u7f57\u5170\u592b\u5987\u7684\u89c6\u7ebf\uff0c\u8db3\u591f\u4f60\u5e94\u4ed8\u4e86\uff0c\u4f60\u8fd8\u6015\u65c5\u9014\u5bc2\u5bde\u5417\uff1f\u201d \u3000\u3000\u201c\u5927\u59d0\uff0c\u6211\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u8981\u8bf4\u4e86\uff0c\u6211\u77e5\u9053\u4f60\u7684\u5fc3\u610f\uff0c\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u4e24\u4e2a\u5c0f\u72d0\u5973\u5df2\u7ecf\u662f\u4f60\u7684\u4eba\u4e86\uff0c\u5979\u4eec\u7684\u7239\u5a18\u4e5f\u5df2\u5141\u5a5a\uff0c\u4f55\u51b5\uff0c\u5929\u72d0\u592b\u4eba\u6709\u610f\u57f9\u517b\u4f60\u7684\u90aa\u6c14\uff0c\u4f60\u5f88\u5bb9\u6613\u4e71\u6027\uff0c\u5fcd\u4e0d\u4f4f\u5c31\u5403\u4e86\u5979\u4eec\u5427\uff01\u201d \u3000\u3000\u201c\u8fd9\u2026\u2026\u201d\u6c5f\u67ab\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a \u3000\u3000\u201c\u59e8\u5a18\u4f20\u6211\u7684\u5185\u529f\u786e\u5b9e\u6709\u70b9\u90aa\u95e8\uff0c\u6211\u517c\u4e60\u65e0\u76f8\u795e\u529f\uff0c\u624d\u80fd\u52c9\u5f3a\u514b\u5236\uff0c\u8981\u4e0d\u7136\uff0c\u6211\u771f\u7684\u53ef\u80fd\u6210\u4e86\u8272\u4e2d\u997f\u9b3c\uff0c\u6240\u4ee5\uff0c\u6211\u5e0c\u671b\u5927\u59d0\u80fd\u8ddf\u6211\u540c\u53bb\u3002\u201d \u3000\u3000\u201c\u4e0d\u884c\uff01\u6211\u8981\u7559\u5728\u957f\u5b89\u3001\u627e\u51fa\u771f\u76f8\uff0c\u81f3\u5c11\u4e5f\u8981\u67e5\u51fa\u4e00\u70b9\u7709\u76ee\uff0c\u4e24\u4e2a\u5c0f\u72d0\u5973\u5f88\u8ba4\u771f\u7684\u8f6c\u6388\u6211\u5929\u72d0\u6b66\u529f\uff0c\u6211\u5b66\u7684\u6280\u827a\u548c\u5929\u72d0\u6b66\u529f\u6709\u70b9\u7c7b\u4f3c\uff0c\u6240\u4ee5\uff0c\u5f88\u5feb\u53d1\u89c9\u4e86\uff0c\u5929\u72d0\u6b66\u529f\u66f2\u5f84\u901a\u5e7d\uff0c\u4e0d\u8fc7\uff0c\u5929\u72d0\u592b\u4eba\u4f20\u7ed9\u7f57\u5170\u7684\u5df2\u7ecf\u8fc7\u6ee4\uff0c\u4e71\u6027\u90e8\u5206\uff0c\u51cf\u81f3\u6700\u4f4e\uff0c\u8fd9\u5c31\u662f\u4e24\u4e2a\u5c0f\u72d0\u5973\uff0c\u80fd\u5b88\u4f4f\u6700\u540e\u4e00\u5173\u7684\u539f\u56e0\uff0c\u4f20\u7ed9\u4f60\u7684\uff0c\u5927\u6982\u662f\u5168\u76d8\u76f8\u6388\u4e86\u3002 \u3000\u3000\u6240\u4ee5\uff0c\u4f60\u90a3\u4e48\u6df1\u539a\u7684\u5185\u529f\uff0c\u5374\u4e0d\u80fd\u514b\u5236\u81ea\u5df1\uff0c\u2018\u60d1\u5fc3\u672f\u2019\u6311\u9017\u8d77\u522b\u4eba\u7684\u60c5\u7130\u6b32\u706b\u3001\u81ea\u5df1\u4e5f\u4e00\u6837\u628a\u6301\u4e0d\u4f4f\u3002 \u3000\u3000\u6211\u60f3\uff0c\u8fd9\u662f\u5929\u72d0\u592b\u4eba\u6709\u610f\u7684\u9020\u5c31\uff0c\u4f7f\u4f60\u7684\u2018\u60d1\u5fc3\u672f\u2019\u80fd\u53d1\u6325\u7684\u6dcb\u6f13\u5c3d\u81f4\uff0c\u5927\u59d0\u4e0d\u80fd\u6297\u62d2\u4f60\u7684\u9b45\u529b\uff0c\u5927\u6982\u662f\u5929\u4e0b\u5f88\u591a\u5973\u4eba\u4e5f\u4e0d\u80fd\u591f\u6297\u62d2\u5f97\u4e86\u3002 \u3000\u3000\u6211\u6700\u62c5\u5fc3\u7684\u4e00\u4ef6\u4e8b\uff0c\u5c31\u662f\u7f57\u5170\uff0c\u5979\u5df2\u7ecf\u628a\u5929\u72d0\u5a9a\u7b11\u70bc\u4eba\u5316\u5883\uff0c\u81ea\u5df1\u8fd8\u8ba4\u4e3a\u672a\u4e60\u5929\u72d0\u6b66\u529f\uff0c\u4e3b\u640f\u6740\u6280\u5de7\u800c\u8a00\u5979\u662f\u6ca1\u6709\u7ec3\u4e60\uff0c\u4f46\u5374\u4e0d\u81ea\u89c9\u7684\u7ec3\u6210\u4e86\u5929\u72d0\u5a9a\u7b11\uff0c\u4f60\u5403\u4e86\u4e24\u4e2a\u5c0f\u72d0\u5973\uff0c\u4f1a\u4f7f\u4f60\u66f4\u52a0\u8b66\u60d5\u3002 \u3000\u3000\u6211\u5df2\u7ecf\u8b66\u544a\u8fc7\u7f57\u5170\uff0c\u8981\u5979\u5c0f\u5fc3\u8c28\u614e\uff0c\u5979\u53c8\u6709\u7231\u7b11\u7684\u6bdb\u75c5\uff0c\u771f\u662f\u51a4\u5bb6\u8def\u7a84\uff0c\u4f60\u4e5f\u8981\u7ed9\u6211\u7559\u5fc3\u4e00\u4e9b\u624d\u884c\u2026\u2026\u201d \u3000\u3000\u201c\u5927\u59d0\u4e0d\u63d0\uff0c\u5c0f\u5f1f\u4e5f\u4e0d\u6562\u4e71\u8bf4\uff0c\u7f57\u5170\u7b11\u8d77\u6765\uff0c\u771f\u7684\u8fd8\u6709\u4e9b\u50cf\u59e8\u5a18\u91cd\u751f\uff0c\u4e0d\u8fc7\uff0c\u5c0f\u5f1f\u76f8\u4fe1\u8fd8\u53ef\u4ee5\u514b\u5236\u81ea\u5df1\u2026\u2026\u201d \u3000\u3000\u201c\u522b\u81ea\u4fe1\uff0c\u5c0f\u60c5\u90ce\uff0c\u5e73\u5e38\u5f53\u7136\u4e0d\u4f1a\u6709\u4e8b\uff0c\u6211\u6015\u5979\u4e0d\u81ea\u89c9\u7684\u5bf9\u4f60\u5c55\u73b0\u5929\u72d0\u5a9a\u7b11\uff0c\u4f60\u53c8\u4e0d\u81ea\u4e3b\u4ee5\u2018\u60d1\u5fc3\u672f\u2019\u548c\u5979\u5bf9\u6297\uff0c\u53cc\u65b9\u8f83\u4e0a\u4e86\u52b2\uff0c\u4f60\u80fd\u81ea\u6301\u5f97\u4f4f\u4e48\uff1f\u201d\u5f20\u56db\u59d1\u53f9\u53e3\u6c14\u53c8\u9053\uff1a\u201c\u5982\u679c\u7f57\u5170\u6297\u62d2\u4e0d\u4f4f\uff0c\u6295\u6000\u9001\u62b1\uff0c\u4f60\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u987f\u89c9\u5fc3\u5e95\u5904\u6cdb\u5f00\u51fa\u4e00\u80a1\u5bd2\u610f\uff0c\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u5bf9\uff01\u4e00\u65bd\u5c55\u2018\u60d1\u5fc3\u672f\u2019\uff0c\u6211\u7684\u81ea\u5236\u80fd\u529b\u5c31\u5927\u4e3a\u51cf\u5f31\uff0c\u4e00\u65e6\u5982\u6b64\uff0c\u90a3\u771f\u662f\u5929\u4e0b\u5927\u4e71\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u591a\u5c11\u6068\u4e8b\uff0c\u90fd\u662f\u6b32\u5f15\u8d77\uff0c\u7f57\u5170\u5fc3\u5730\u7eaf\u771f\uff0c\u80f8\u65e0\u57ce\u5e9c\uff0c\u4e00\u65e6\u52a8\u60c5\u6cdb\u6ee5\uff0c\u5c31\u4f1a\u7387\u6027\u800c\u4e3a\uff0c\u9664\u4e86\u81ea\u7edd\u4e00\u6b7b\uff0c\u4e0d\u4f1a\u904f\u6b62\uff0c\u5c0f\u60c5\u90ce\uff0c\u4f60\u8981\u7279\u522b\u5c0f\u5fc3\u554a\uff01\u5c3d\u91cf\u907f\u514d\u548c\u7f57\u5170\u5355\u72ec\u76f8\u5904\u3002\u201d \u3000\u3000\u201c\u662f\u662f\u662f\uff01\u5c0f\u5f1f\u8bb0\u4e0b\u4e86\u2026\u2026\u201d\uff0c\u6c5f\u67ab\u957f\u957f\u5401\u4e00\u53e3\u6c14\u63a5\u9053\uff1a\u201c\u5927\u59d0\u8d50\u4e8e\u5c0f\u5f1f\u7684\u5927\u591a\u4e86\uff0c\u6559\u6211\u4f5c\u4eba\uff0c\u4e3a\u6211\u4f5c\u4e8b\uff0c\u6211\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u5fc5\u60ed\u548e\uff0c\u4e0d\u662f\u4f60\uff0c\u5927\u59d0\u8fd9\u4e00\u751f\u5927\u6982\u4e5f\u54c1\u5c1d\u4e0d\u5230\u4f5c\u5973\u4eba\u7684\u6ecb\u5473\uff0c\u518d\u8bf4\uff0c\u6211\u5b66\u8fd9\u4e00\u8eab\u6280\u827a\uff0c\u5982\u4e0d\u65bd\u5c55\u51fa\u6765\uff0c\u57cb\u5165\u9ec4\u571f\u548c\u8349\u6728\u540c\u673d\uff0c\u5c82\u4e0d\u662f\u592a\u53ef\u60dc\u4e86\uff0c\u2026\u2026\u201d\u5f20\u56db\u59d1\u7b11\u4e00\u7b11\uff0c\u9053\uff1a \u3000\u3000\u201c\u542c\u6211\u7684\u8bdd\uff0c\u660e\u5929\u5c31\u52a8\u8eab\u8d76\u5f80\u5c11\u6797\u5bfa\u53bb\uff0c\u6709\u5927\u548c\u5c1a\u8fd0\u7b79\u5e37\u5e44\uff0c\u6211\u76f8\u4fe1\u4f1a\u6709\u6536\u83b7\uff0c\u9047\u4e0a\u673a\u7f18\uff0c\u8ba9\u4e24\u4e2a\u5c0f\u72d0\u5973\u540c\u6c90\u5c11\u6797\u7edd\u827a\uff0c\u65e5\u540e\u771f\u5230\u51b3\u6218\u65f6\u523b\uff0c\u6b66\u6797\u4e09\u5723\u4f20\u6388\u4f60\u7684\u6b66\u529f\uff0c\u672a\u5fc5\u6709\u514b\u654c\u81f4\u80dc\u7684\u628a\u63e1\u3002 \u3000\u3000\u5c0f\u60c5\u90ce\uff0c\u4eba\u8981\u660e\u767d\uff0c\u80fd\u4e3a\u4f60\u5206\u62c5\u5343\u94a7\u91cd\u4efb\u7684\uff0c\u4e24\u4e2a\u5c0f\u72d0\u5973\u662f\u6700\u91cd\u8981\u7684\u4eba\u7269\uff0c\u5929\u72d0\u6280\u827a\uff0c\u4f7f\u5979\u4eec\u63d0\u65e9\u5f00\u7a8d\uff0c\u5341\u51e0\u5c81\u7684\u5c0f\u4e2b\u5934\uff0c\u673a\u8b66\u7075\u5de7\uff0c\u6bd4\u8d77\u5f53\u5e74\u5927\u59d0\u6211\u5c24\u6709\u8fc7\u4e4b\u3002\u201d \u3000\u3000\u201c\u5c0f\u5f1f\u53d7\u547d\uff0c\u660e\u5929\u4e00\u65e9\u52a8\u8eab\u3002\u201d \u3000\u3000\u53ea\u542c\u6b65\u5c65\u4e4b\u58f0\u4f20\u6765\uff0c\u9093\u98de\u884c\u5165\u74e6\u820d\u7b11\u9053\uff1a\u201c\u6709\u4e00\u4e2a\u4e08\u591a\u6df1\u7684\u571f\u5751\uff0c\u7701\u4e86\u6211\u4e0d\u5c11\u7684\u624b\u811a\uff0c\u6211\u628a\u571f\u5751\u586b\u6ee1\u77f3\u571f\uff0c\u53c8\u79fb\u690d\u9752\u8349\u5728\u4e0a\uff0c\u6211\u4e0d\u76f8\u4fe1\u80fd\u6709\u4eba\u627e\u5230\u4ed6\u4eec\u7684\u5c38\u4f53\u3002\u201d \u3000\u3000\u201c\u548c\u5c1a\u627e\u5230\u4e86\u4e00\u4e2a\u6c40\u94c1\u94fa\u2026\u2026\u201d\u4e03\u5b9d\u5927\u5e08\u58f0\u5230\u4eba\u5230\uff0c\u8bf4\uff1a\u201c\u82b1\u4e86\u4e94\u4e24\u94f6\u5b50\uff0c\u79df\u7528\u987f\u996d\u8fb0\u5149\uff0c\u4e56\u4e56\uff0c\u53ef\u771f\u662f\u70c8\u706b\u718a\u718a\u554a\uff01\u4e24\u4e2a\u94dc\u846b\u82a6\u5316\u6210\u6d41\u6c57\uff0c\u548c\u5c1a\u624d\u79bb\u5f00\u90a3\u91cc\uff0c\u5c31\u7b97\u662f\u94c1\u94f8\u7684\u6d3b\u86ca\uff0c\u4e5f\u8be5\u5c38\u9aa8\u4e0d\u5b58\u4e86\u3002\u201d \u3000\u3000\u201c\u6839\u597d\uff0c\u54b1\u4eec\u56de\u53bb\u5427\uff01\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u5927\u5e08\uff0c\u660e\u5929\u4e00\u65e9\uff0c\u4f60\u8fd8\u8981\u8ddf\u6211\u8d76\u8d74\u5d69\u5c71\u3002\u201d \u3000\u3000\u5927\u548c\u5c1a\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff01\u90a3\u91cc\u662f\u6b66\u5b66\u6839\u6e90\uff0c\u548c\u5c1a\u4e0d\u76f8\u4fe1\u5c11\u6797\u5bfa\u4f1a\u88ab\u5b8c\u5168\u5f81\u773c\uff0c\u864e\u9690\u5c71\u5c97\uff0c\u9f99\u6f5c\u6df1\u6e0a\uff0c\u4ed6\u4eec\u6b63\u5728\u7b49\u4f60\uff01\u201d \u3000\u3000\u201c\u6bd4\u8d77\u5f20\u59d1\u5a18\uff0c\u548c\u5c1a\u81ea\u77e5\u6709\u6240\u4e0d\u53ca\u2026\u2026\u201d \u3000\u3000\u201c\u597d\u4e86\uff0c\u5927\u548c\u5c1a\uff0c\u522b\u628a\u6211\u6367\u5f97\u592a\u9ad8\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u4f60\u4eec\u4e00\u884c\u56db\u4e2a\u4eba\uff0c\u660e\u5929\u4e00\u65e9\u5c31\u8d70\uff01\u201d \u3000\u3000\u201c\u90a3\u4e24\u4e2a\u53ef\u662f\u96ea\u3001\u971c\u4e8c\u5973\uff1f\u201d \u3000\u3000\u5f20\u56db\u59d1\u70b9\u70b9\u5934\u3002 \u3000\u3000\u201c\u4f69\u773c\uff01\u4f69\u773c\uff01\u2026\u2026\u201d\u4e03\u5b9d\u5927\u5e08\u9053\uff1a\u201c\u70b9\u5c06\u7528\u5175\uff0c\u65e0\u4e0d\u6070\u5230\u597d\u5904\uff0c\u4e0a\u5c11\u6797\uff0c\u672c\u5e94\u8be5\u7531\u4f60\u5f20\u59d1\u5a18\u966a\u6c5f\u8001\u5f1f\u53bb\uff0c\u4f46\u548c\u5c1a\u81ea\u77e5\u62c5\u4e0d\u8d77\u7559\u4e0b\u6765\u7684\u91cd\u6765\u5927\u4efb\uff0c\u53ea\u597d\u62e3\u70b9\u8f7b\u5de7\u4e8b\u5e72\u5566\uff01\u201d \u3000\u3000\u6c5f\u67ab\u5947\u9053\uff1a\u201c\u4f60\u4e5f\u8d5e\u6210\u96ea\u3001\u971c\u540c\u53bb\uff1f\u201d \u3000\u3000\u201c\u662f\u554a\uff01\u653e\u773c\u5f53\u4eca\u6b66\u6797\uff0c\u9664\u4e86\u4f60\u548c\u8001\u5f1f\u5177\u6709\u901f\u6210\u6280\u827a\u7684\u624d\u80fd\u4e4b\u5916\uff0c\u8fd8\u6709\u8c01\u80fd\u6bd4\u5f97\u96ea\u3001\u971c\u4e24\u4f4d\u59d1\u5a18\u7684\u5438\u6536\u624d\u6167\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u8bf4\uff0c\u201c\u5f20\u59d1\u5a18\u7684\u6280\u827a\uff0c\u535a\u6742\u4e07\u7aef\uff0c\u4e24\u4f4d\u7f8e\u4eba\uff0c\u53ea\u82b1\u4e86\u4e24\u4e2a\u6708\u7684\u65f6\u95f4\uff0c\u5df2\u80fd\u767b\u5802\u5165\u5ba4\uff0c\u9a7e\u8f7b\u5c31\u719f\u3002\u201d \u3000\u3000\u6c5f\u67ab\u5fc3\u4e2d\u6709\u70b9\u4e0d\u670d\u6c14\uff0c\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5927\u548c\u5c1a\uff0c\u4f60\u53ef\u77e5\u9053\u5927\u59d0\u4e3a\u4ec0\u4e48\u4e0d\u80fd\u8ddf\u6211\u4eec\u4e00\u8d77\u53bb\u5c11\u6797\u5bfa\u5417\uff1f\u201d \u3000\u3000\u201c\u597d\u50cf\u662f\u8981\u67e5\u660e\u4e00\u4e9b\u4e8b\u60c5\u5427\uff01\u81f3\u4e8e\u4ec0\u4e48\u4e8b\uff1f\u548c\u5c1a\u5c31\u8bf4\u4e0d\u4e0a\u6765\u4e86\u3002\u201d \u3000\u3000\u201c\u5509\uff01\u52ff\u602a\u5927\u59d0\u4e00\u76f4\u5bf9\u4f60\u8d5e\u4e0d\u7edd\u53e3\uff0c\u4f60\u4eec\u771f\u662f\u82f1\u96c4\u6240\u89c1\u7565\u540c\u3002\u201d \u3000\u3000\u9093\u98de\u770b\u4e86\u6c5f\u67ab\u4e00\u773c\uff0c\u6b32\u8a00\u53c8\u6b62\u3002 \u3000\u3000\u201c\u9093\u5144\uff0c\u4e0d\u8981\u7126\u8651\uff0c\u6c5f\u67ab\u65e9\u6709\u4e86\u5b89\u6392\u2026\u2026\u201d\u5f20\u56db\u59d1\u8bf4\uff0c\u201c\u660e\u5929\uff0c\u5927\u5bb6\u4e00\u8d77\u79bb\u5f00\u957f\u5b89\u3002\u201d \u3000\u3000\u9093\u98de\u70b9\u5934\u9053\uff1a\u201c\u8fd9\u5c31\u597d\uff0c\u8fd9\u5c31\u597d\u3002\u201d \u3000\u3000\u201c\u8001\u9093\u554a\uff01\u4f60\u8fd9\u4e2a\u603b\u7ba1\uff0c\u80fd\u4e0d\u80fd\u5e26\u8d70\u94f6\u5b50\uff1f\u201d \u3000\u3000\u201c\u5927\u5e08\u8981\u591a\u5c11\uff1f\u9093\u67d0\u4eba\u51d1\u51d1\u770b\uff0c\u4e09\u4e94\u4e07\u4e24\uff0c\u5927\u6982\u62ff\u5f97\u51fa\u6765\u3002\u201d \u3000\u3000\u201c\u4e0d\u591f\uff01\u4e0d\u591f\uff01\u2026\u2026\u201d\u5927\u548c\u5c1a\u7b11\u9053\uff1a\u201c\u6709\u597d\u591a\uff0c\u5c31\u62ff\u8d70\u597d\u591a\uff0c\u4ee5\u540e\uff0c\u5927\u5bb6\u7684\u65e5\u5b50\uff0c\u5c31\u9760\u8fd9\u4e00\u7968\u6d3b\u4e0b\u53bb\u4e86\u3002\u201d \u3000\u3000\u201c\u6211\u660e\u767d\u4e86\u2026\u2026\u201d\u9093\u98de\u9053\uff1a\u201c\u56de\u957f\u5b89\uff0c\u6211\u5c31\u4e0b\u624b\uff0c\u6211\u60f3\u8fde\u5077\u5e26\u62a2\uff0c\u5e94\u8be5\u6709\u4e2a\u4e09\u56db\u5341\u4e07\u4e24\u5427\uff01\u201d \u3000\u3000\u201c\u8d70\uff01\u90a3\u5c31\u5feb\u56de\u957f\u5b89\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u5c31\u8bf7\u5927\u5e08\u5e2e\u9093\u5144\u4e00\u628a\uff0c\u4e09\u5bab\u4e3b\u5982\u679c\u53d1\u73b0\u963b\u6b62\uff0c\u90a3\u5c31\u5927\u5f00\u6740\u6212\u3002\u201d \u3000\u3000\u201c\u6709\u4e86\u6c5f\u8001\u5f1f\u8fd9\u53e5\u8bdd\uff0c\u54b1\u4eec\u5c31\u662f\u540d\u6b63\u8a00\u987a\u7684\u90a3\u94f6\u5b50\u4e86\uff0c\u548c\u5c1a\u5168\u529b\u4ee5\u8d74\u3002\u201d \u3000 \u7b2c\u5341\u4e5d\u56de\u3000\u6c74\u4eac\u57ce\u8bbf\u533b\u5f97\u771f\u8a00 \u3000 \u3000\u3000\u5927\u548c\u5c1a\u6234\u4e0a\u5047\u53d1\uff0c\u7a7f\u4e86\u4e00\u4ef6\u8001\u7f8a\u76ae\u7684\u5927\u8884\uff0c\u8d76\u4e86\u4e00\u8f86\u53cc\u5957\u84ec\u8f66\uff0c\u5065\u9a6c\u5feb\u8f66\uff0c\u5954\u9a70\u5728\u4e1c\u4e0a\u7684\u5927\u9053\u4e2d\u3002 \u3000\u3000\u6c5f\u67ab\u5f88\u8212\u670d\u7684\u8eba\u5728\u9a6c\u8f66\u4e0a\uff0c\u4e24\u4e2a\u5c0f\u72d0\u5973\uff0c\u5206\u5728\u4ed6\u5de6\u53f3\u4e24\u4fa7\u3002 \u3000\u3000\u4e24\u4e2a\u5c0f\u7f8e\u4eba\u9ad8\u5174\u6781\u4e86\uff0c\u8f66\u4e2d\u5c31\u70ed\u95f9\u7684\u4e0d\u80fd\u770b\u5566\uff01 \u3000\u3000\u5979\u4eec\u5ffd\u7136\u8eba\u4e0b\u53bb\uff0c\u5ffd\u7136\u5750\u8d77\u6765\uff0c\u4e00\u4f1a\u513f\u50cf\u626d\u80a1\u7cd6\u4f3c\u7f20\u5728\u6c5f\u5927\u54e5\u7684\u8eab\u4e0a\uff0c\u4e00\u4f1a\u513f\u6295\u6000\u9001\u62b1\uff0c\u6492\u5c3d\u5a07\u5a9a\u2026\u2026\u3002 \u3000\u3000\u53cd\u6b63\u662f\u5df2\u6709\u540d\u5206\uff0c\u518d\u8bf4\u8f66\u7bf7\u4f4e\u5782\uff0c\u65e2\u53ef\u6321\u98ce\uff0c\u5916\u9762\u7684\u4eba\u4e5f\u77a7\u4e0d\u5230\u3002 \u3000\u3000\u6c5f\u67ab\u662f\u95ed\u7740\u773c\u775b\u4eab\u53d7\uff0c\u6697\u91cc\u5374\u9ed8\u8fd0\u65e0\u76f8\u795e\u529f\uff0c\u514b\u5236\u6b32\u671b\uff0c\u4e24\u4e2a\u5c0f\u7f8e\u4eba\u672a\u66fe\u6cbe\u8fc7\u96e8\u9732\u60c5\uff0c\u548c\u5fc3\u4e2d\u60c5\u90ce\u4eb2\u543b\u62e5\u62b1\uff0c\u8033\u9b13\u53ae\u78e8\uff0c\u89c9\u7740\u975e\u5e38\u597d\u73a9\u54ea\uff0c\u4f46\u6c5f\u5927\u54e5\u5374\u88ab\u6311\u9017\u7684\u5341\u5206\u8f9b\u82e6\uff0c\u5f53\u771f\u662f\u6700\u96be\u6d88\u53d7\u7f8e\u4eba\u6069\u3002 \u3000\u3000\u5f20\u56db\u59d1\u548c\u6b27\u9633\u662d\u3001\u6c5f\u67ab\u7b49\u4e00\u8d77\u51fa\u57ce\uff0c\u79bb\u57ce\u4e94\u91cc\uff0c\u5206\u9053\u800c\u884c\uff0c\u8001\u53eb\u5316\u94b1\u7f3a\u5e26\u8def\uff0c\u4ee5\u6b27\u9633\u662d\u592b\u5987\u4e3a\u9996\u7684\u6570\u5341\u4eba\u76f4\u5954\u7ec8\u5357\u5c71\u53bb\u3002 \u3000\u3000\u6885\u82b1\u548c\u5f20\u56db\u59d1\u5374\u6613\u5bb9\u6298\u56de\uff0c\u91cd\u5165\u957f\u5b89\u3002 \u3000\u3000\u4e09\u5bab\u4e3b\u53d1\u89c9\u5230\u60c5\u51b5\u602a\u5f02\uff0c\u6c5f\u67ab\u7b49\u5df2\u8d70\u4e86\u4e00\u4e2a\u65f6\u8fb0\u3002 \u3000\u3000\u539f\u6765\uff0c\u5f20\u56db\u59d1\u65e9\u6709\u8bbe\u8ba1\uff0c\u5927\u5bb6\u5206\u6279\u6f5c\u79bb\u6d1b\u9633\u5c45\uff0c\u8d70\u7684\u662f\u98ce\u5e73\u6d6a\u9759\uff0c\u8349\u6728\u4e0d\u60ca\u3002 \u3000\u3000\u4f46\u6700\u4e25\u91cd\u7684\u662f\u9093\u98de\u5e26\u8d70\u4e86\u6d1b\u9633\u5c45\u6240\u6709\u7684\u94f6\u94b1\uff0c\u5305\u62ec\u8d4c\u573a\u7684\u672c\u94b1\uff0c\u94f6\u53f7\u7684\u5b58\u6b3e\uff0c\u8fde\u9152\u9986\u7684\u4e70\u83dc\u94b1\u4e5f\u672a\u7559\u4e0b\u3002 \u3000\u3000\u4e09\u5bab\u4e3b\u4e0b\u4ee4\u8ffd\u7d22\u6c5f\u67ab\u7b49\u4eba\u7684\u884c\u8e2a\uff0c\u4e8b\u5b9e\u4e0a\uff0c\u53ea\u662f\u865a\u5f20\u58f0\u52bf\uff0c\u6ca1\u6709\u4eba\u6562\u8ba4\u771f\u53bb\u8ffd\u67e5\uff0c\u56e0\u4e3a\u771f\u7684\u8ffd\u4e0a\u4e86\uff0c\u7edd\u5bf9\u4fdd\u4e0d\u4f4f\u6027\u547d\u3002 \u3000\u3000\u5c11\u6797\u5bfa\uff0c\u4f4d\u4e8e\u5d69\u5c71\u5c11\u5ba4\u5cf0\u7684\u5317\u9e93\uff0c\u5efa\u4e8e\u5317\u9b4f\u5b5d\u6587\u5e1d\u65f6\u4ee3\uff0c\u56e0\u8fbe\u6469\u7956\u5e08\u4f4f\u6301\u5c11\u6797\u5bfa\uff0c\u4f20\u7559\u4e0b\u6b66\u6280\u7cbe\u534e\u3002 \u3000\u3000\u73b0\u5728\uff0c\u6c5f\u67ab\u5df2\u5230\u4e86\u5c11\u6797\u5bfa\u7684\u95e8\u5916\u9762\u3002 \u3000\u3000\u8868\u9762\u4e0a\u770b\u53bb\uff0c\u5c11\u6797\u5e76\u6ca1\u6709\u4ec0\u4e48\u6539\u53d8\uff0c\u5927\u95e8\u53e3\u5904\uff0c\u4ecd\u6709\u5f53\u503c\u7684\u50e7\u4fa3\u5b88\u62a4\uff0c\u53ea\u662f\u592a\u9759\u4e86\u4e00\u4e9b\uff0c\u9759\u7684\u4e0d\u89c1\u4e00\u4e2a\u9999\u5ba2\u3002 \u3000\u3000\u4e03\u5b9d\u5927\u5e08\u7a7f\u7740\u4fd7\u5bb6\u8863\u7740\uff0c\u96ea\u3001\u971c\u4e8c\u59b9\u5374\u6539\u7740\u4e86\u7537\u88c5\uff0c\u626e\u4f5c\u6c5f\u67ab\u7684\u968f\u8eab\u5c0f\u53ae\u3002 \u3000\u3000\u6c5f\u67ab\u8eab\u7740\u9526\u7f0e\u957f\u888d\uff0c\u5927\u6447\u5927\u6446\u5730\u5411\u5bfa\u4e2d\u884c\u53bb\u3002 \u3000\u3000\u4e24\u4e2a\u5b88\u5c71\u95e8\u7684\u4e2d\u5e74\u548c\u5c1a\uff0c\u5bf9\u6c5f\u67ab\u76f4\u95ef\u5165\u5bfa\u7684\u4e3e\u52a8\uff0c\u6709\u4e9b\u5947\u602a\uff0c\u597d\u591a\u5e74\u6ca1\u6709\u8fd9\u79cd\u4e8b\u4e86\uff0c\u4e24\u4eba\u79fb\u6b65\u963b\u62e6\u65f6\uff0c\u6c5f\u67ab\u5df2\u8fdb\u4e86\u5bfa\u95e8\u3002 \u3000\u3000\u4f46\u4e8c\u50e7\u8eab\u6cd5\u5947\u5feb\uff0c\u4e00\u4e2a\u8f6c\u8eab\u98de\u8dc3\uff0c\u5df2\u6321\u5728\u4e86\u6c5f\u67ab\u8eab\u524d\uff0c\u663e\u793a\u51fa\u76f8\u5f53\u539a\u5b9e\u7684\u6b66\u529f\u57fa\u7840\u3002 \u3000\u3000\u6c5f\u67ab\u4e00\u62b1\u62f3\uff0c\u9053\uff1a\u201c\u4e24\u4f4d\u5927\u5e08\u7236\u8bf7\u4e86\uff01\u2026\u2026\u201d \u3000\u3000\u4e24\u4e2a\u548c\u5c1a\uff0c\u90fd\u5728\u4e09\u5341\u56db\u5de6\u53f3\uff0c\u90a3\u662f\u4e00\u822c\u5b66\u6b66\u4eba\u7684\u9ec4\u91d1\u65f6\u95f4\uff0c\u529f\u529b\u3001\u4f53\u80fd\u90fd\u5230\u4e86\u6700\u4f73\u72b6\u6001\uff0c\u4e00\u8272\u7684\u7070\u5e03\u50e7\u888d\uff0c\u663e\u793a\u4e24\u4eba\u8eab\u4efd\u76f8\u82e5\uff0c\u5c5e\u4e8e\u540c\u4e00\u7b49\u7ea7\u3002 \u3000\u3000\u4e24\u4e2a\u4eba\u672c\u60f3\u6012\u58f0\u8d23\u95ee\uff0c\u4f46\u6c5f\u67ab\u5f6c\u5f6c\u6709\u793c\u7684\u795e\u6001\uff0c\u53cd\u4f7f\u4e24\u4e2a\u548c\u5c1a\u5fae\u5fae\u4e00\u6014\uff0c\u8fd8\u672a\u6765\u53ca\u8d28\u95ee\uff0c\u6c5f\u67ab\u53c8\u62a2\u5148\u5f00\u4e86\u53e3\u3002 \u3000\u3000\u201c\u672c\u5b98\u5357\u4e0b\u516c\u5e72\uff0c\u4e45\u95fb\u5c11\u6797\u5bfa\u7684\u5927\u540d\uff0c\u7279\u5730\u62bd\u51fa\u4e00\u65e5\u7a7a\u6687\u6765\u6b64\u77bb\u4ef0\u4e00\u756a\uff0c\u8bf7\u4e24\u4f4d\u5927\u5e08\u7236\uff0c\u4ee3\u4e3a\u4f20\u62a5\u65b9\u4e08\uff0c\u672c\u5b98\u5728\u6b64\u606d\u5019\u56de\u8bdd\u3002\u201d \u3000\u3000\u4e0d\u4f46\u662f\u5b98\uff0c\u800c\u4e14\u662f\u4eac\u5b98\uff0c\u5357\u4e0b\u516c\u5e72\uff0c\u7279\u5730\u62bd\u4e00\u65e5\u65f6\u95f4\uff0c\u8fd9\u8bf4\u660e\u4e86\u7edd\u4e0d\u4f1a\u591a\u7559\uff0c\u53ef\u80fd\u662f\u5927\u6982\u7684\u770b\u770b\u5c31\u8d70\uff0c\u8bdd\u867d\u8bf4\u7684\u5a49\u8f6c\uff0c\u4f46\u5b98\u8154\u5b98\u8c03\uff0c\u8f6f\u4e2d\u6709\u786c\u3002 \u3000\u3000\u4e24\u4e2a\u548c\u5c1a\u5fc3\u4e2d\u76d8\u7b97\u4e86\u4e00\u756a\uff0c\u4e92\u76f8\u5bf9\u671b\u4e00\u773c\uff0c\u7531\u5de6\u9996\u7070\u8863\u50e7\u4eba\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u4eac\u91cc\u6765\u7684\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\uff01\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u672c\u5b98\u4f4f\u804c\u5175\u90e8\uff0c\u5927\u5e08\u7236\u53ef\u8981\u770b\u770b\u672c\u5b98\u7684\u516c\u5e72\u6587\u4ef6\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u7528\uff01\u4e0d\u7528\uff01\u201d\u4e24\u4e2a\u548c\u5c1a\u663e\u7136\u662f\u6ca1\u6709\u9605\u5386\u7684\u4eba\uff0c\u4f3c\u662f\u88ab\u6c5f\u67ab\u7ed9\u552c\u4f4f\u4e86\u3002 \u3000\u3000\u5f53\u7136\uff0c\u6c5f\u67ab\u7684\u6c14\u52bf\uff0c\u4e5f\u9887\u6709\u4f5c\u5b98\u7684\u67b6\u5f0f\uff0c\u5927\u548c\u5c1a\u8eab\u7740\u9ed1\u8272\u957f\u888d\uff0c\u8eab\u8eaf\u4fee\u4f1f\uff0c\u8170\u675f\u7d2b\u5e26\uff0c\u8fd8\u7cfb\u4e86\u4e00\u628a\u5b9d\u77f3\u9576\u67c4\u7684\u8170\u5200\u3002 \u3000\u3000\u541f\u96ea\u3001\u541f\u971c\u5df2\u957f\u7684\u5a77\u5a77\u7389\u7acb\uff0c\u626e\u4e24\u4e2a\u968f\u8eab\u5c0f\u53ae\uff0c\u6709\u5982\u4e66\u4e2d\u4ed9\u7ae5\uff0c\u518d\u52a0\u6c5f\u67ab\u9526\u888d\u864e\u5578\uff0c\u90a3\u4efd\u6c14\u6c1b\uff0c\u786e\u7ed9\u4eba\u4e00\u79cd\u516c\u4faf\u738b\u5b50\u7684\u611f\u89c9\u3002 \u3000\u3000\u4f46\u95fb\u5de6\u9996\u7684\u548c\u5c1a\u8bf4\u9053\uff1a\u201c\u5927\u4eba\u8bf7\u7a0d\u5019\u7247\u523b\uff0c\u5c0f\u50e7\u7acb\u523b\u901a\u62a5\u65b9\u4e08\uff0c\u4f46\u53ea\u4e0d\u77e5\u8981\u5982\u4f55\u4e0a\u7980\uff1f\u201d \u3000\u3000\u867d\u662f\u6ca1\u6709\u9605\u5386\u3001\u7ecf\u9a8c\uff0c\u4f46\u5374\u9887\u6709\u667a\u7565\u3002 \u3000\u3000\u201c\u672c\u5b98\u59d3\u98ce\uff0c\u4efb\u804c\u5175\u90e8\u5175\u9a6c\u53f8\u2026\u2026\u201d \u3000\u3000\u201c\u539f\u6765\u662f\u98ce\u5927\u4eba\uff0c\u5c0f\u50e7\u53bb\u53bb\u5c31\u6765\u2026\u2026\u201d\u8f6c\u8eab\u5feb\u6b65\u800c\u53bb\u3002 \u3000\u3000\u201c\u5947\u602a\u554a\uff01\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u671b\u7740\u7559\u4e0b\u7684\u4e00\u4e2a\u548c\u5c1a\uff0c\u9053\uff1a\u201c\u8fd9\u5c11\u6797\u5bfa\u4e43\u4f5b\u95e8\u540d\u5239\uff0c\u4e00\u5411\u9999\u5ba2\u4f17\u591a\uff0c\u4efb\u4eba\u671d\u62dc\uff0c\u600e\u4e48\u73b0\u5728\u2026\u2026\u3002\u201d \u3000\u3000\u201c\u5165\u4e61\u968f\u4fd7\u2026\u2026\u201d\u6c5f\u67ab\u559d\u4f4f\u4e86\u4e03\u5b9d\u548c\u5c1a\u9053\uff1a\u201c\u50cf\u5c11\u6797\u6797\u8fd9\u6837\u7684\u4f5b\u95e8\u5723\u5730\uff0c\u81ea\u7136\u6709\u4ed6\u4eec\u7684\u89c4\u77e9\u3002\u201d \u3000\u3000\u201c\u5927\u4eba\u6055\u7f6a\uff0c\u5c0f\u4eba\u591a\u5634\u4e86\u2026\u2026\u201d \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u4e00\u9762\u56de\u7b54\uff0c\u4e00\u9762\u8eac\u8eab\u884c\u793c\u3002 \u3000\u3000\u4e24\u4e2a\u4eba\u6f14\u8d77\u620f\u6765\uff0c\u8fd8\u771f\u7684\u6709\u6a21\u6709\u6837\uff0c\u4e24\u4e2a\u5c0f\u72d0\u5973\u4e5f\u5177\u6f14\u620f\u7684\u5929\u624d\uff0c\u4e0d\u4f46\u5fcd\u4f4f\u4e86\u6ca1\u6709\u7b11\uff0c\u800c\u4e14\uff0c\u7d27\u7ef7\u7740\u4e24\u5f20\u7751\uff0c\u5fc3\u4e2d\u4f3c\u662f\u5927\u611f\u4e0d\u6ee1\u3002 \u3000\u3000\u770b\u770b\u56db\u4eba\u795e\u60c5\uff0c\u7070\u8863\u50e7\u4eba\u65e0\u6cd5\u4e0d\u89e3\u91ca\u4e86\uff0c\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u4f4d\u7ba1\u5bb6\uff0c\u2026\u2026\u201d \u3000\u3000\u671b\u7740\u4e03\u5b9d\u548c\u5c1a\u63a5\u9053\uff1a\u201c\u5c11\u6797\u5bfa\u672c\u6765\u662f\u4efb\u4eba\u51fa\u5165\uff0c\u62dc\u4f5b\u664b\u9999\uff0c\u53ea\u662f\u53bb\u5e74\u6709\u6279\u4eba\u4f2a\u88c5\u9999\u5ba2\uff0c\u5927\u95f9\u5c11\u6797\u5bfa\uff0c\u4f24\u4e86\u6211\u4eec\u4e0d\u5c11\u5e08\u5144\u5f1f\uff0c\u4e5f\u6b7b\u4f24\u4e86\u5f88\u591a\u65e0\u8f9c\u7684\u9999\u5ba2\uff0c\u6240\u4ee5\uff0c\u5c31\u5f00\u59cb\u5bf9\u9999\u5ba2\u7a0d\u4f5c\u76d8\u67e5\uff0c\u4e8b\u5b9e\u4e0a\uff0c\u81ea\u90a3\u6b21\u4e8b\u4ef6\u4e4b\u540e\uff0c\u9999\u5ba2\u81ea\u52a8\u65e5\u6e10\u51cf\u5c11\u3002\u201d \u3000\u3000\u201c\u539f\u6765\u5982\u6b64\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u8d1f\u624b\u56db\u987e\uff0c\u4e0d\u518d\u7406\u4f1a\u7070\u8863\u50e7\u4eba\uff0c\u5fc3\u4e2d\u5374\u6697\u6697\u5fd6\u9053\uff1a\u201c\u770b\u6765\uff0c\u4ed6\u4eec\u5bf9\u5b98\u5e9c\u4e2d\u4eba\u8fd8\u5fc3\u5b58\u987e\u5fcc\uff0c\u8fd8\u771f\u88ab\u5927\u548c\u5c1a\u7ed9\u6599\u4e2d\u4e86\u3002\u201d \u3000\u3000\u4f46\u89c1\u4f20\u62a5\u7684\u7070\u8863\u50e7\u4eba\u98de\u5954\u800c\u6765\uff0c\u8eab\u540e\u8ddf\u7740\u4e00\u4e2a\u8eab\u62ab\u9ec4\u8272\u8888\u88df\u7684\u9ad8\u5927\u548c\u5c1a\u3002 \u3000\u3000\u6c5f\u67ab\u7aef\u8db3\u67b6\u5b50\uff0c\u671b\u7740\u90a3\u9ec4\u8863\u548c\u5c1a\uff0c\u5374\u4e0d\u5f00\u53e3\u3002 \u3000\u3000\u9ec4\u8863\u50e7\u4eba\u5012\u8bda\u5fc3\u8bda\u610f\u8fce\u5ba2\uff0c\u4e00\u505c\u4e0b\u5c31\u5408\u638c\u5f53\u80f8\uff0c\u9053\uff1a\u201c\u8d2b\u50e7\uff0c\u77e5\u5ba2\u50e7\u53f8\u5584\uff0c\u4e0d\u77e5\u5927\u4eba\u9a7e\u5230\uff0c\u672a\u80fd\u8fdc\u8fce\u3002\u201d \u3000\u3000\u201c\u4e0d\u7528\u5ba2\u6c14\u2026\u2026\u201d\u6c5f\u67ab\u4e00\u6446\u624b\uff0c\u9053\uff1a \u3000\u3000\u201c\u672c\u5b98\u4e45\u95fb\u5c11\u6797\u76db\u540d\uff0c\u5e0c\u671b\u664b\u89c1\u8d35\u5bfa\u65b9\u4e08\u4e00\u9762\uff0c\u5982\u6709\u9700\u5b98\u65b9\u534f\u987e\u3002\u201d \u3000\u3000\u53e3\u6c14\u867d\u7136\u6e29\u548c\uff0c\u4f46\u5374\u9690\u900f\u8eab\u4ef7\uff0c\u9ad8\u4e0d\u53ef\u6500\u3002 \u3000\u3000\u201c\u591a\u8c22\u5927\u4eba\uff0c\u591a\u8c22\u5927\u4eba\u2026\u2026\u201d\u53f8\u5584\u6025\u6025\u8bf4\u9053\uff1a\u201c\u8d2b\u50e7\u5df2\u7740\u4eba\u901a\u62a5\u65b9\u4e08\uff0c\u4e0d\u4f1a\u6162\u5f85\u5927\u4eba\uff0c\u5bfa\u4e2d\u4ea7\u4e1a\uff0c\u5c1a\u53ef\u7cca\u50e7\u7fa4\u4e4b\u53e3\uff0c\u4e0d\u6562\u52b3\u52a8\u5927\u4eba\uff0c\u4f46\u5927\u4eba\u8fd9\u756a\u5fc3\u610f\uff0c\u8d2b\u50e7\u5148\u81f4\u8c22\u610f\uff0c\u8bf7\u5165\u5ba2\u623f\u5f85\u8336\uff0c\u65b9\u4e08\u60f3\u5fc5\u4f1a\u4eb2\u8fce\u8d35\u5bbe\u3002\u201d \u3000\u3000\u201c\u597d\uff0c\u597d\uff0c\u89c1\u8fc7\u8d35\u5bfa\u65b9\u6587\u540e\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u8fd8\u8bf7\u5927\u5e08\u5e26\u8def\uff0c\u6e38\u8d4f\u4e00\u4e0b\u5c11\u6797\u5bfa\u7684\u98ce\u5149\uff0c\u54b3\uff01\u53ea\u53ef\u60dc\u672c\u5b98\u516c\u52a1\u7d27\u6025\uff0c\u6050\u4e0d\u80fd\u591a\u7559\u5728\u6b64\u3002\u201d \u3000\u3000\u201c\u5176\u5b9e\uff0c\u5c11\u6797\u5bfa\u4e5f\u6ca1\u6709\u52a8\u4eba\u7684\u5c71\u6c34\u98ce\u5149\uff0c\u2026\u2026\u201d\u53f8\u5584\u9053\uff1a \u3000\u3000\u201c\u5343\u4f5b\u6bbf\u7684\u4e94\u767e\u7f57\u6c49\u50cf\uff0c\u662f\u753b\u5723\u5434\u9053\u5b50\u7684\u624b\u7b14\uff0c\u987b\u7709\u5b9b\u7136\uff0c\u6781\u4e3a\u5de5\u6574\uff0c\u8fbe\u6469\u7956\u5e08\u7684\u9762\u58c1\u77f3\uff0c\u6709\u7f18\u7684\u4eba\uff0c\u9690\u7ea6\u53ef\u89c1\u77f3\u4e0a\u7956\u5e08\u8eab\u5f71\u7559\u75d5\uff0c\u9664\u6b64\u4e4b\u5916\uff0c\u8d2b\u50e7\u4e00\u65f6\u95f4\u4e5f\u771f\u7684\u60f3\u4e0d\u51fa\u5e26\u5927\u4eba\u770b\u4e9b\u4ec0\u4e48\u4e86\u3002\u201d \u3000\u3000\u6c5f\u67ab\u5728\u53f8\u5584\u8bf7\u8ba9\u4e2d\uff0c\u4e00\u9762\u4e3e\u6b65\u524d\u884c\uff0c\u4e00\u9762\u8bf4\u9053\uff1a\u201c\u542c\u8bf4\u5c11\u6797\u5bfa\u7684\u85cf\u7ecf\u9601\uff0c\u6709\u7ecf\u4e66\u4e07\u5377\uff0c\u8bb0\u8f7d\u516d\u5341\u4e8c\u79cd\u7edd\u6280\u7684\u5bc6\u672c\uff0c\u8fd8\u6709\u2018\u5929\u7afa\u2019\u539f\u6587\u7ecf\u5377\uff0c\u90fd\u662f\u7a00\u4e16\u4e4b\u5b9d\u554a\uff1f\u201d \u3000\u3000\u201c\u600e\u4e48\uff1f\u5927\u4eba\u4e5f\u4f1a\u6b66\u529f\uff1f\u2026\u2026\u201d\u53f8\u5584\u6709\u70b9\u610f\u5916\u5730\u8bf4\uff1a\u201c\u4e5f\u61c2\u2018\u5929\u7afa\u2019\u6587\u5b57\uff1f\u201d \u3000\u3000\u201c\u6211\u719f\u8bc6\u6218\u9635\u5175\u6cd5\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u4e5f\u7ec3\u8fc7\u5200\u3001\u5f13\uff0c\u77f3\u7684\u5de5\u592b\uff0c\u7eb5\u9a6c\u7586\u573a\uff0c\u51b2\u950b\u9677\u9635\uff0c\u4e5f\u8bb8\u53ef\u4ee5\uff0c\u50cf\u4f60\u4eec\u5c11\u6797\u5bfa\u90a3\u79cd\u98de\u6a90\u8d70\u58c1\uff0c\u70b9\u7a74\u622a\u8109\u4e00\u7c7b\u7684\u6b66\u529f\u5c31\u4e00\u7a8d\u4e0d\u901a\u4e86\u3002\u201d \u3000\u3000\u201c\u5927\u4eba\u2026\u2026\u201d\u53f8\u5584\u63a5\u9053\uff1a\u201c\u90a3\u53ea\u662f\u5c0f\u5de7\u4e4b\u6280\uff0c\u4e0d\u767b\u5927\u96c5\u4e4b\u5802\uff0c\u884c\u5175\u5e03\u9635\uff0c\u8fd0\u7b79\u5e37\u63e1\uff0c\u624d\u662f\u5927\u5c06\u519b\u7684\u624d\u80fd\u3002\u201d \u3000\u3000\u6c5f\u67ab\u5fc3\u4e2d\u4e00\u52a8\uff0c\u5fd6\u9053\uff1a\u201c\u5e78\u597d\u4ed6\u63a5\u4e86\u53e3\uff0c\u8981\u8ba9\u6211\u4e00\u76f4\u8bf4\u4e0b\u53bb\uff0c\u53ea\u6015\u4f1a\u8bf4\u6f0f\u4e86\u5634\uff0c\u4e0d\u662f\u66b4\u9732\u8eab\u4efd\uff0c\u5c31\u662f\u65e0\u6cd5\u4e0b\u53f0\uff0c\u771f\u662f\u8a00\u591a\u5fc5\u5931\u554a\uff01\u201d \u3000\u3000\u53f8\u5584\u5feb\u884c\u4e00\u6b65\uff0c\u63a8\u5f00\u4e00\u6247\u623f\u95e8\uff0c\u9053\uff1a\u201c\u5927\u4eba\uff0c\u8bf7\u5165\u5ba2\u623f\u5f85\u8336\u3002\u201d \u3000\u3000\u53ea\u89c1\u4e00\u4e2a\u5c0f\u6c99\u5f25\u5feb\u6b65\u5954\u4e86\u8fc7\u6765\uff0c\u9053\uff1a\u201c\u53f8\u5584\u5e08\u53d4\uff0c\u65b9\u4e08\u8bf7\u98ce\u5927\u4eba\u5165\u7985\u623f\u53d9\u8bdd\u3002\u201d \u3000\u3000\u53f8\u5584\u54fc\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u98ce\u5927\u4eba\u662f\u5175\u90e8\u5927\u5458\uff0c\u65b9\u4e08\u5e94\u8be5\u4eb2\u8eab\u8fce\u63a5\u3002\u201d \u3000\u3000\u6c5f\u67ab\u5df2\u4e3e\u6b65\u8de8\u5165\u95e8\u5185\uff0c\u7acb\u523b\u53c8\u9000\u4e86\u51fa\u6765\uff0c\u9053\uff1a\u201c\u4e0d\u59a8\uff0c\u4e0d\u59a8\uff0c\u8d35\u5bfa\u65b9\u4e08\u8eab\u4efd\u5c0a\u5d07\uff0c\u672c\u5b98\u5e94\u8be5\u767b\u95e8\u62dc\u4f1a\uff0c\u600e\u6562\u6548\u52b3\u3002\u201d \u3000\u3000\u201c\u8fd9\u771f\u662f\u59d4\u5c48\u5927\u4eba\u4e86\uff0c\u8d2b\u50e7\u4e3a\u5927\u4eba\u5f15\u8def\u3002\u201d \u3000\u3000\u201c\u4e0d\u6562\uff01\u4e0d\u6562\uff01\u7531\u5c0f\u6c99\u5f25\u5e26\u8def\u5c31\u597d\u2026\u201d \u3000\u3000\u6c5f\u67ab\u4f38\u624b\u62e6\u963b\u4e86\u53f8\u5584\uff0c\u4ed8\u9053\uff1a\u201c\u4f60\u7ec8\u4e8e\u9732\u51fa\u9a6c\u811a\u4e86\uff0c\u5c11\u6797\u5bfa\u7684\u638c\u95e8\uff0c\u5728\u5bfa\u4e2d\u50e7\u7fa4\u5185\u5fc3\u8eab\u4efd\u5c0a\u8d35\u65e0\u6bd4\uff0c\u4f60\u7adf\u6562\u51fa\u8a00\u62b1\u6028\uff0c\u7edd\u975e\u5c11\u6797\u5bfa\u51fa\u8eab\u7684\u771f\u548c\u5c1a\u4e86\uff0c\u770b\u6765\uff0c\u5c11\u6797\u5bfa\u65b9\u4e08\uff0c\u8fd8\u662f\u65e7\u4eba\u2026\u2026\u201d \u3000\u3000\u53f8\u5584\u4e5f\u4e0d\u575a\u6301\uff0c\u5408\u638c\u7b11\u9053\uff1a\u201c\u8d2b\u50e7\u5c31\u606d\u656c\u4e0d\u5982\u4ece\u547d\u4e86\u3002\u201d \u3000\u3000\u65b9\u4e08\u5ba4\u662f\u4e00\u5ea7\u72ec\u5904\u4e8e\u7fe0\u7af9\u73af\u7ed5\u7684\u9ec4\u8272\u7816\u623f\uff0c\u5e7d\u9759\u6e05\u96c5\uff0c\u5c0f\u6c99\u5f25\u56de\u5934\u770b\u770b\u4e24\u4e2a\u5c0f\u72d0\u5973\u548c\u4e03\u5b9d\u548c\u5c1a\uff0c\u4f3c\u662f\u60f3\u51fa\u53e3\u963b\u6b62\uff0c\u5374\u53c8\u4e0d\u6562\uff0c\u6572\u6572\u95e8\uff0c\u9ad8\u58f0\u8bf4\u9053\uff1a\u201c\u98ce\u5927\u4eba\u8bf7\u89c1\u65b9\u4e08\u3002\u201d \u3000\u3000\u201c\u8bf7\u3002\u201d\u7985\u623f\u4e2d\u4f20\u51fa\u4e86\u4e00\u4e2a\u4f4e\u6c89\u58f0\u97f3\u3002 \u3000\u3000\u5c0f\u6c99\u5f25\u63a8\u5f00\u623f\u95e8\uff0c\u5374\u672a\u8ddf\u8fdb\u3002 \u3000\u3000\u6c5f\u67ab\u6b65\u5165\u5ba4\u4e2d\uff0c\u56db\u987e\u4e86\u4e00\u773c\uff0c\u53ea\u89c1\u9ad8\u5927\u7684\u65b9\u4e08\u5ba4\u4e2d\uff0c\u9648\u8bbe\u975e\u5e38\u7b80\u5355\uff0c\u58c1\u4e0a\u7684\u6302\u753b\u75d5\u8ff9\u53ef\u89c1\uff0c\u4f46\u753b\u5df2\u4e0d\u77e5\u53bb\u5411\uff0c\u6728\u6848\u4e0a\u9999\u70ec\u72b9\u5728\uff0c\u5374\u4e0d\u89c1\u8885\u8885\u6a80\u9999\u70df\u6c14\uff0c\u7ed9\u4eba\u4e00\u79cd\u5931\u843d\u7684\u82cd\u51c9\u4e4b\u611f\u3002 \u3000\u3000\u8001\u65b9\u5927\u76d8\u5728\u4e00\u5f20\u5bbd\u5927\u7684\u6728\u6905\u4e0a\uff0c\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5927\u4eba\u8bf7\u5750\uff0c\u6055\u8001\u8872\u8eab\u4f53\u4e0d\u9002\uff0c\u4e0d\u80fd\u8d77\u8eab\u8fce\u5ba2\u3002\u201d \u3000\u3000\u90a3\u79cd\u4e07\u5ff5\u4ff1\u7070\uff0c\u751f\u6b7b\u6f20\u7136\u7684\u795e\u60c5\uff0c\u4f7f\u6c5f\u67ab\u4e00\u9635\u5fc3\u98a4\u3002 \u3000\u3000\u9886\u8896\u5929\u4e0b\u6b66\u6797\u7684\u5c11\u6797\u65b9\u4e08\uff0c\u7adf\u88ab\u8feb\u5bb3\u5230\u5982\u6b64\u5883\u5730\u3002 \u3000\u3000\u4e03\u5b9d\u5927\u5e08\u5374\u7a81\u7136\u75be\u884c\u4e24\u6b65\uff0c\u4f38\u624b\u6309\u5728\u65b9\u4e08\u7684\u9876\u95e8\u4e0a\u3002 \u3000\u3000\u8001\u65b9\u4e08\u6ca1\u6709\u95ea\u907f\uff0c\u53cd\u800c\u95ed\u4e0a\u53cc\u76ee\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u65e2\u8981\u5229\u7528\u8005\u8001\u8872\uff0c\u5374\u53c8\u4e0d\u80af\u4fe1\u4efb\uff0c\u8fde\u756a\u9063\u4eba\uff0c\u6613\u5bb9\u6d4b\u8bd5\uff0c\u4f55\u5982\u6740\u4e86\u8001\u8872\uff0c\u4ee5\u7edd\u7591\u8651\u2026\u2026\u201d \u3000\u3000\u5df2\u786e\u77e5\u8001\u65b9\u4e08\u672a\u5e26\u4eba\u76ae\u9762\u5177\uff0c\u7acb\u523b\u4f20\u97f3\u8bf4\u9053\uff1a\u201c\u5f1f\u5b50\u4e03\u5b9d\u548c\u5c1a\uff0c\u8ddf\u968f\u6c5f\u67ab\u5c11\u4fa0\u5165\u5bfa\u63a2\u8bbf\u65b9\u4e08\u3001\u201d \u3000\u3000\u8001\u65b9\u4e08\u7a81\u7741\u53cc\u76ee\uff0c\u795e\u5149\u5982\u7535\uff0c\u76ef\u4f4f\u5728\u4e03\u5b9d\u50e7\u7684\u8138\u4e0a\u3002 \u3000\u3000\u5927\u548c\u5c1a\u5df2\u6536\u56de\u53f3\u624b\uff0c\u4f20\u97f3\u63a5\u9053\uff1a\u201c\u6c5f\u67ab\u5c11\u4fa0\u662f\u6b66\u6797\u4e09\u5723\u9020\u5c31\u7684\u4f20\u4eba\uff0c\u8eab\u517c\u4e09\u4eba\u4e4b\u957f\uff0c\u65b9\u4e08\u5982\u6709\u6307\u70b9\uff0c\u8bf7\u5c3d\u5feb\u8bf4\u660e\uff0c\u6211\u4eec\u9ad8\u58f0\u4ea4\u8c08\uff0c\u6697\u65bd\u4f20\u97f3\uff0c\u4ee5\u4e71\u76d1\u89c6\u4eba\u7684\u8033\u76ee\u2026\u2026\u201d\u63d0\u9ad8\u4e86\u58f0\u97f3\uff0c\u9053\uff1a\u201c\u65b9\u4e08\u8eab\u4f53\u4e0d\u9002\uff0c\u5927\u4eba\u4e0d\u4f1a\u89c1\u602a\u3002\u201d \u3000\u3000\u201c\u8001\u9500\u767e\u660e\uff0c\u6127\u5bf9\u5386\u4ee3\u5e08\u7956\uff0c\u5c11\u6797\u5168\u5bfa\uff0c\u5c3d\u5165\u654c\u4eba\u638c\u63e1\uff0c\u5927\u90e8\u5206\u5fe0\u5fc3\u5f1f\u5b50\uff0c\u4e5f\u90fd\u5728\u9690\u5fcd\u5f85\u673a\u2026\u2026\u2026\u201d\u6545\u610f\u9ad8\u58f0\u5e94\u9053\uff1a\u201c\u591a\u8c22\u98ce\u5927\u4eba\u9274\u8c05\u3002\u201d \u3000\u3000\u6c5f\u67ab\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8001\u65b9\u4e08\u5b89\u5fc3\u9759\u517b\uff0c\u672c\u5b98\u770b\u7740\u5c31\u8d70\uff0c\u4eca\u665a\u8fd8\u8981\u8d76\u56de\u5ba2\u6808\u3002\u201d\u653e\u4f4e\u58f0\u97f3\uff0c\u9053\uff1a\u201c\u65b9\u4e08\u6709\u4f55\u6307\u6559\uff0c\u665a\u8f88\u6d17\u8033\u606d\u542c\u3002\u201d \u3000\u3000\u767e\u660e\u9053\uff1a\u201c\u5c11\u6797\u5bfa\u7f3a\u5c71\u6c34\u4e4b\u80dc\u53ea\u5f97\u4e86\u53e4\u6734\u4e8c\u5b57\uff0c\u6050\u662f\u5f88\u96be\u7559\u4e0b\u5927\u4eba\u4e86\u3002\u201d\u653e\u4f4e\u58f0\u8a89\uff0c\u63a5\u9053\uff1a\u201c\u654c\u4eba\u6b66\u529f\u5947\u9ad8\uff0c\u672c\u5bfa\u4e2d\u8fbe\u6469\u3001\u6212\u5f8b\u4e24\u9662\u4e3b\u6301\uff0c\u662f\u8001\u8872\u5e08\u5f1f\uff0c\u6b66\u529f\u6210\u5c31\u90fd\u5728\u8001\u8872\u4e4b\u4e0a\uff0c\u5e26\u7740\u5bfa\u4e2d\u5341\u4e8c\u4f4d\u4e0a\u5ea7\u9ad8\u50e7\uff0c\u8840\u6218\u6b89\u96be\uff0c\u7f57\u6c49\u5927\u9635\uff0c\u4e5f\u88ab\u654c\u4eba\u4ee5\u836f\u7269\u8ff7\u5012\u3002 \u3000\u3000\u4e3a\u4e86\u4fdd\u5168\u5343\u4f4d\u50e7\u4f17\u6027\u547d\uff0c\u514d\u5404\u5239\u6bc1\u4e8e\u5927\u706b\uff0c\u8001\u8872\u4e0d\u5f97\u4e0d\u964d\uff0c\u4f46\u4ed6\u4eec\u4ecd\u4e0d\u653e\u5fc3\uff0c\u5728\u8001\u8872\u8eab\u4e0a\u52a0\u4e86\u5404\u79cd\u7981\u5236\uff0c\u5c11\u6797\u5bfa\u60c5\u5f62\u5982\u6b64\uff0c\u60f3\u6765\u6b66\u5f53\u4e5f\u96be\u5e78\u514d\u2026\u2026\u201d \u3000\u3000\u201c\u662f\u7684\uff0c\u6c5f\u6e56\u4e0a\u6240\u6709\u95e8\u6d3e\u3001\u5e2e\u4f1a\uff0c\u5df2\u5168\u5165\u4ed6\u4eec\u7684\u638c\u63e1\u4e4b\u4e2d\u2026\u2026\u201d \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u9053\uff1a\u201c\u654c\u52bf\u904d\u5e03\u6c5f\u6e56\uff0c\u5f1f\u5b50\u624d\u5e26\u9886\u6c5f\u5c11\u4fa0\u524d\u6765\u664b\u8c12\uff0c\u6c42\u65b9\u4e08\u6307\u70b9\u4e00\u6761\u660e\u8def\u3002\u201d \u3000\u3000\u767e\u660e\u8138\u4e0a\u808c\u8089\u98a4\u6296\uff0c\u4f3c\u662f\u5185\u5fc3\u4f9d\u5f3a\u70c8\u7684\u6597\u4e89\uff0c\u201c\u8d35\u6559\u4f5b\u6bbf\u4e2d\u4e94\u767e\u7f57\u6c49\uff0c\u4e2a\u4e2a\u795e\u60c5\u4e0d\u540c\u2026\u2026\u201d\u6c5f\u67ab\u5927\u58f0\u95ee\u9053\uff1a\u201c\u542c\u8bf4\u662f\u51fa\u81ea\u753b\u5723\u5434\u9053\u5b50\u7684\u624b\u7b14\uff1f\u6b64\u4e8b\u662f\u771f\u662f\u5047\uff1f\u201d \u3000\u3000\u201c\u5bfa\u4e2d\u6807\u8bb0\uff0c\u4f20\u8f7d\u751a\u660e\uff0c\u81ea\u7136\u4e0b\u5047\u4e86\u2026\u2026\u201d\u767e\u660e\u4e5f\u9ad8\u58f0\u56de\u7b54\u3002 \u3000\u3000\u53e3\u4e2d\u5e94\u5bf9\uff0c\u4eba\u5374\u53cc\u76ee\u7d27\u95ed\uff0c\u663e\u7136\u5185\u5fc3\u4e2d\uff0c\u4ecd\u672a\u80fd\u4f5c\u4e0b\u51b3\u5b9a\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u4e24\u4e2a\u5c0f\u72d0\u5973\u5e76\u672a\u7ad9\u5728\u6c5f\u5927\u54e5\u7684\u8eab\u4fa7\uff0c\u5374\u5206\u5b88\u5728\u7985\u623f\u524d\u540e\u7a97\u53e3\u5904\uff0c\u5411\u5916\u63a2\u89c6\uff0c\u4ee5\u9632\u6709\u4eba\u6f5c\u8fd1\u3002 \u3000\u3000\u5982\u6b64\u7684\u806a\u6167\u53ef\u4eba\uff0c\u8c01\u80fd\u4e0d\u52a8\u5fc3\u60dc\u7231\u3002 \u3000\u3000\u201c\u6b66\u6797\u8499\u96be\uff0c\u6c5f\u6e56\u5e94\u52ab\uff0c\u8001\u65b9\u4e08\u5982\u53d7\u5bfa\u4e2d\u6cd5\u89c4\u7f1a\u675f\uff0c\u5f1f\u5b50\u4e5f\u4e0d\u6562\u5f3a\u6c42\uff0c\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u62dc\u4f0f\u4e8e\u5730\uff0c\u53e9\u4e86\u4e09\u4e2a\u5934\uff0c\u9053\uff1a\u201c\u5f1f\u5b50\u544a\u8f9e\uff0c\u8001\u65b9\u4e08\u591a\u591a\u73cd\u91cd\u3002\u201d \u3000\u3000\u7ad9\u8d77\u8eab\u5b50\uff0c\u8f6c\u8eab\u884c\u53bb\u3002 \u3000\u3000\u6c5f\u67ab\u5fc3\u4e2d\u5fd6\u9053\uff1a\u201c\u96be\u9053\u5c11\u6797\u5bfa\u771f\u8fd8\u6709\u9ad8\u8fc7\u4e09\u4f4d\u5e08\u957f\u7684\u7edd\u5b66\u4e0d\u6210\uff0c\u8fd9\u8d9f\u6050\u6015\u662f\u767d\u6765\u4e86\uff1f\u201d \u3000\u3000\u5bf9\u767e\u660e\u62b1\u62f3\u4e00\u63d6\uff0c\u4e5f\u8f6c\u8eab\u884c\u53bb\u3002 \u3000\u3000\u4ed6\u795e\u60c5\u5e73\u9759\uff0c\u6ca1\u6709\u5931\u671b\u7684\u611f\u89c9\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u672c\u5c31\u4e0d\u592a\u76f8\u4fe1\u5c11\u6797\u5bfa\u4e2d\u4f1a\u6709\u9ad8\u8fc7\u6b66\u6797\u4e09\u5723\u7684\u7edd\u6280\uff0c\u4f46\u5f20\u56db\u59d1\u65e2\u7136\u8bf4\u4e86\uff0c\u4e0d\u80fd\u4e0d\u6765\u3002 \u3000\u3000\u5fc3\u4e2d\u672a\u62b1\u592a\u5927\u5e0c\u671b\uff0c\u81ea\u7136\u5fc3\u6ce2\u4e0d\u8d77\u3002 \u3000\u3000\u201c\u56de\u6765\u2026\u2026\u201d \u3000\u3000\u767e\u660e\u5927\u5e08\u7684\u58f0\u97f3\u867d\u4f4e\uff0c\u4f46\u5374\u4f3c\u7528\u5c3d\u4e86\u5168\u8eab\u6c14\u529b\uff0c\u8fde\u8eab\u4f53\u4e5f\u6709\u4e9b\u98a4\u6296\u4e86\u3002 \u3000\u3000\u663e\u7136\uff0c\u4ed6\u5df2\u4f5c\u4e86\u91cd\u5927\u7684\u51b3\u5b9a\u3002 \u3000\u3000\u4e24\u4eba\u5e94\u58f0\u800c\u56de\u3002 \u3000\u3000\u201c\u8001\u8872\u53ea\u6709\u4e00\u4e2a\u8981\u6c42\uff0c\u5c5e\u4e8e\u5c11\u6797\u5bfa\u7684\uff0c\u65e5\u540e\u8981\u8fd8\u7ed9\u5c11\u6797\u3002\u201d \u3000\u3000\u6c5f\u67ab\u6de1\u6de1\u4e00\u7b11\uff0c\u5fae\u5fae\u9894\u9996\uff0c\u770b\u5f97\u51fa\uff0c\u4ed6\u5fc3\u4e2d\u6839\u672c\u6ca1\u91cd\u89c6\u8fd9\u4ef6\u4e8b\u60c5\u3002 \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u4f4e\u58f0\u9053\uff1a\u201c\u6c5f\u5c11\u4fa0\uff0c\u7ed9\u65b9\u4e08\u4e00\u4e2a\u4fdd\u8bc1\uff0c\u4e00\u4e2a\u660e\u663e\u7684\u627f\u8bfa\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u7684\u795e\u60c5\u5e84\u4e25\uff0c\u6c5f\u67ab\u53ea\u597d\u5e94\u9053\uff1a\u201c\u5f97\u81ea\u5c11\u6797\u7684\uff0c\u665a\u8f88\u4fdd\u8bc1\u5168\u6570\u5f52\u8fd8\uff0c\u7edd\u4e0d\u85cf\u79c1\u3002\u201d \u3000\u3000\u201c\u6b66\u6797\u4e09\u5723\u662f\u4e24\u767e\u5e74\u6765\uff0c\u6c5f\u6e56\u4e2d\u7684\u8d85\u51e1\u4eba\u7269\uff0c\u4e0d\u8fc7\uff0c\u654c\u4eba\u592a\u5f3a\u4e86\u2026\u2026\u201d\u767e\u660e\u5927\u5e08\u9053\uff1a \u3000\u3000\u201c\u8001\u8872\u504f\u5411\u7985\u5b97\u3001\u4f5b\u5b66\uff0c\u4e60\u6b66\u4e0d\u52a8\uff0c\u4f46\u8fbe\u6469\u3001\u6212\u5f8b\u4e24\u9662\u7684\u9662\u4e3b\uff0c\u5374\u662f\u4e60\u6b66\u7684\u5947\u624d\uff0c\u4ed6\u4eec\u7684\u6b66\u529f\u6210\u5c31\uff0c\u4e5f\u8bb8\u4e0d\u53ca\u4e09\u5723\uff0c\u4f46\u4e5f\u662f\u5c11\u6797\u5bfa\u4e2d\u8fd1\u4e24\u767e\u5e74\u6765\uff0c\u672a\u66fe\u6709\u7684\u9ad8\u624b\uff0c\u6bcf\u4eba\u90fd\u517c\u901a\u4e86\u5341\u79cd\u4ee5\u4e0a\u7684\u7edd\u6280\u3002 \u3000\u3000\u52a0\u4e0a\u672c\u5bfa\u5341\u4e8c\u4f4d\u4e0a\u5ea7\u9ad8\u50e7\uff0c\u5c3d\u51fa\u672c\u8eab\u6280\u827a\uff0c\u82e6\u6218\u4e8c\u4e2a\u65f6\u8fb0\u4e4b\u4e45\uff0c\u4ecd\u6b7b\u4f24\u4e8e\u5bf9\u65b9\u4e4b\u624b\uff0c\u5bf9\u65b9\u4ea6\u662f\u51ed\u85c9\u7740\u771f\u5b9e\u672c\u9886\uff0c\u51fb\u8d25\u6211\u4eec\uff0c\u672a\u65bd\u5c55\u9b3c\u8ba1\u3001\u6697\u7b97\uff01\u201d \u3000\u3000\u201c\u4ed6\u4eec\u662f\u4ec0\u4e48\u4eba\uff1f\u7537\u7684\uff0c\u8fd8\u662f\u5973\u7684\uff1f\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u5927\u5e08\u603b\u8be5\u77e5\u9053\u4e00\u70b9\u5427\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u8001\u8872\u53ea\u77e5\u9053\u4e00\u70b9\u70b9\uff0c\u4ed6\u4eec\u6709\u7537\u6709\u5973\uff0c\u7537\u7684\u662f\u5929\u738b\u95e8\u4e3b\uff0c\u5973\u7684\u662f\u5929\u540e\u5bab\u4e3b\uff0c\u5176\u4ed6\u4ece\u4eba\u867d\u591a\uff0c\u8001\u8872\u4e5f\u8ba4\u8bc6\u4e0d\u5c11\uff0c\u4e0d\u8fc7\uff0c\u90fd\u672a\u51fa\u624b\uff0c\u51fa\u624b\u7684\u4e00\u5171\u516d\u4e2a\u4eba\uff0c\u8001\u8872\u53ea\u77e5\u9053\u4e24\u4e2a\uff0c\u53e6\u5916\u56db\u4e2a\u4eba\uff0c\u8001\u8872\u4e0d\u8ba4\u8bc6\uff0c\u4e5f\u672a\u542c\u8bf4\u8fc7\u3002 \u3000\u3000\u4ed6\u4eec\u5c3d\u6bc1\u5c11\u6797\u5bfa\u4e24\u4ee3\u7cbe\u82f1\uff0c\u5230\u73b0\u5728\u4e3a\u6b62\uff0c\u8001\u8872\u8fd8\u4e0d\u77e5\u4ed6\u4eec\u7684\u59d3\u540d\uff0c\u53ea\u8bb0\u5f97\u5176\u4e2d\u4e00\u4eba\u5de6\u624b\u638c\u5fc3\u4e2d\u6709\u4e00\u4e2a\u7ea2\u75de\uff0c\u624b\u5fc3\u957f\u75de\uff0c\u7edd\u65e0\u4ec5\u6709\u3002 \u3000\u3000\u4e24\u4f4d\u65e5\u540e\uff0c\u4e5f\u8bb8\u4f1a\u9047\u5230\u4ed6\uff0c\u516d\u4e2a\u4eba\u4e2d\uff0c\u4e24\u5973\u56db\u7537\uff0c\u4e00\u4e2a\u5973\u7684\u662f\u5929\u540e\u5bab\u4e3b\uff0c\u53e6\u4e00\u4e2a\u8001\u8872\u53ea\u89c1\u5979\u4e00\u6b21\uff0c\u5fc3\u72e0\u624b\u8fa3\uff0c\u5c24\u8fc7\u7537\u4eba\uff01\u201d \u3000\u3000\u201c\u665a\u8f88\u8bb0\u4e0b\u4e86\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u8c6a\u6c14\u98ce\u5ea6\u7684\u63a5\u9053\uff1a\u201c\u65e5\u540e\u9047\u4e0a\u90a3\u4e9b\u4eba\uff0c\u665a\u8f88\u7edd\u4e0d\u653e\u8fc7\u3002\u201d \u3000\u3000\u4e24\u4eba\u4ea4\u8c08\uff0c\u5404\u8bf4\u5404\u8bdd\u3002 \u3000\u3000\u767e\u660e\u5927\u5e08\u9690\u9690\u6697\u793a\uff0c\u6b66\u6797\u4e09\u5723\u6b66\u529f\u4e0d\u8db3\u4ee5\u514b\u654c\u5236\u80dc\uff0c\u4f46\u6c5f\u67ab\u5374\u4e00\u76f4\u672a\u8868\u793a\u51fa\u5bf9\u5c11\u6797\u6b66\u6280\u7684\u70ed\u8877\uff0c\u663e\u662f\u4e0d\u4fe1\u5c11\u6797\u6709\u8d85\u8d8a\u4e09\u5723\u7684\u6b66\u529f\u3002 \u3000\u3000\u201c\u8001\u65b9\u4e08\uff0c\u65f6\u673a\u8feb\u4fc3\u554a\uff01\u201d \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u53d1\u89c9\u4e86\uff0c\u8981\u8bf4\u670d\u6c5f\u67ab\u76f8\u4fe1\u5c11\u6797\u5bc6\u85cf\u6709\u7edd\u4e16\u6b66\u827a\uff0c\u4e00\u65f6\u95f4\uff0c\u4e0d\u5927\u5bb9\u6613\uff0c\u53ea\u597d\u8bf4\u670d\u8001\u548c\u5c1a\u4e86\u3002 \u3000\u3000\u201c\u5c11\u6797\u5bfa\u6709\u5f88\u591a\u4f20\u7ee7\u7edd\u5b66\u7684\u5e94\u5371\u51c6\u5907\uff0c\u4f46\u8fd9\u4e00\u6b21\uff0c\u5927\u90fd\u88ab\u7834\u9664\u3001\u65a9\u65ad\uff0c\u8bf8\u4f4d\u6765\u7684\u5f88\u597d\u2026\u2026\u201d\u767e\u660e\u65b9\u4e08\u7531\u8d34\u8eab\u5185\u8863\u4e2d\u53d6\u51fa\u4e00\u7247\u9ec4\u8272\u5e03\u5757\uff0c\u63a5\u9053\uff1a\u201c\u597d\u597d\u6536\u85cf\uff0c\u5230\u6c74\u4eac\u76f8\u56fd\u5bfa\u5916\uff0c\u627e\u72d7\u76ae\u846b\u82a6\uff0c\u628a\u8fd9\u4e2a\u4ea4\u7ed9\u4ed6\uff0c\u7b49\u4ed6\u9a8c\u660e\u771f\u4f2a\uff0c\u4e24\u4f4d\u8981\u4ec0\u4e48\uff0c\u5c31\u76f4\u5bf9\u4ed6\u8bf4\u5427\uff01\u201d \u3000\u3000\u201c\u72d7\u76ae\u846b\u82a6\uff0c\u662f\u4e2a\u4eba\uff0c\u8fd8\u662f\u4e00\u4ef6\u4e1c\u897f\uff1f\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u8001\u65b9\u4e08\u80fd\u5426\u6307\u70b9\u7684\u8be6\u5c3d\u4e00\u4e9b\uff1f\u201d \u3000\u3000\u201c\u5b9e\u5728\u8bf4\uff0c\u8001\u8872\u4e5f\u4e0d\u6e05\u695a\uff0c\u4f46\u4e0d\u5b9a\u4f1a\u5728\u76f8\u56fd\u5bfa\u9644\u8fd1\uff0c\u8fd9\u662f\u4e0a\u4ee3\u638c\u95e8\u4f20\u4e0b\u7684\u4e00\u6b65\u6b7b\u68cb\uff0c\u7528\u8fc7\u5c31\u4e0d\u4f1a\u518d\u7528\u3002 \u3000\u3000\u767e\u591a\u5e74\u6765\uff0c\u4ece\u672a\u7528\u8fc7\uff0c\u5730\u70b9\u660e\u786e\uff0c\u8303\u56f4\u6709\u9650\uff0c\u627e\u8d77\u6765\uff0c\u5e94\u8be5\u4e0d\u4f1a\u592a\u96be\uff0c\u8bf8\u4f4d\u82e5\u65e0\u5176\u4e8b\u800c\u6765\uff0c\u8fd8\u8bf7\u82e5\u65e0\u5176\u4e8b\u800c\u53bb\uff0c\u65e2\u53ef\u514d\u88ab\u4eba\u8ffd\u8e2a\u4e4b\u9669\u4e5f\u66ff\u5c11\u6797\u5bfa\u514d\u53bb\u4e00\u4e9b\u60b2\u60e8\u60c5\u4e8b\u2026\u2026\u201d \u3000\u3000\u767e\u660e\u5927\u5e08\u70b9\u70b9\u5934\u3002 \u3000\u3000\u6c5f\u67ab\u6012\u5bb9\u6d8c\u73b0\uff0c\u9053\uff1a\u201c\u53ef\u6076\uff0c\u53ef\u6068\uff0c\u5927\u5e08\uff0c\u4ed6\u4eec\u662f\u5426\u7a7f\u7740\u50e7\u888d\uff0c\u5982\u4f55\u624d\u80fd\u5206\u8fa8\uff1f\u201d \u3000\u3000\u201c\u5f88\u96be\u5206\u8fa8\u2026\u2026\u201d\u767e\u660e\u5927\u5e08\u9053\uff1a\u201c\u4ed6\u4eec\u5243\u5149\u4e86\u5934\uff0c\u7a7f\u8d77\u50e7\u8863\u8888\u88df\uff0c\u4e0d\u8ba4\u8bc6\uff0c\u4e5f\u65e0\u6cd5\u5206\u8fa8\uff0c\u8001\u8872\u62c5\u5fc3\u7684\u662f\uff0c\u4ed6\u4eec\u8033\u76ee\u5e7f\u5e03\uff0c\u53c8\u6781\u591a\u7591\uff0c\u4f60\u4eec\u5343\u4e07\u5c0f\u5fc3\uff0c\u76ee\u4e0b\u771f\u6b63\u5c11\u6797\u5bfa\u4e2d\u7684\u50e7\u4fa3\uff0c\u4e0d\u80fd\u64c5\u81ea\u884c\u52a8\uff0c\u8001\u8872\u65e0\u6cd5\u6d3e\u4eba\u5e2e\u4f60\u4eec\uff0c\u4e00\u5207\u7531\u4f60\u4eec\u81ea\u5df1\u62c5\u5f85\u4e86\u3002\u201d \u3000\u3000\u201c\u8001\u65b9\u4e08\u653e\u5fc3\uff0c\u5f1f\u5b50\u4f1a\u5c0f\u5fc3\u5e94\u4ed8\u2026\u2026\u3002\u201d \u3000\u3000\u767e\u660e\u65b9\u4e08\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u8d70\u5427\uff01\u7559\u4e45\u4e86\uff0c\u4f1a\u4f7f\u4ed6\u4eec\u52a8\u7591\uff01\u201d \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u76ee\u7779\u8001\u65b9\u4e08\u8eab\u53d7\u82e6\u96be\uff0c\u5f1f\u5b50\u7adf\u7136\u65e0\u6cd5\u63f4\u6551\uff0c\u8fd8\u8bf7\u73cd\u91cd\uff0c\u5f1f\u5b50\u544a\u9000\u4e86\u3002\u201d \u3000\u3000\u4ed6\u6ca1\u6709\u52c9\u5f3a\u6c5f\u67ab\uff0c\u4f46\u672c\u8eab\u5374\u5bf9\u767e\u660e\u5341\u5206\u656c\u91cd\uff0c\u53c8\u53e9\u4e86\u4e00\u4e2a\u5934\uff0c\u624d\u79bb\u5f00\u7985\u623f\u3002 \u3000\u3000\u51fa\u4e86\u65b9\u4e08\u5ba4\uff0c\u884c\u4e0d\u8fc7\u4e00\u4e08\uff0c\u53ea\u89c1\u77e5\u5ba2\u589e\u53f8\u5584\uff0c\u5e26\u7740\u4e00\u4e2a\u9762\u76ee\u6e05\u79c0\u7684\u5c0f\u548c\u5c1a\uff0c\u606d\u5019\u8def\u4fa7\uff0c\u5408\u638c\u7b11\u9053\uff1a\u201c\u8d2b\u50e7\u7279\u5730\u9009\u4e86\u4e00\u4e2a\u53e3\u9f7f\u4f36\u4fd0\u7684\u5c0f\u548c\u5c1a\uff0c\u4f34\u968f\u4f8d\u5019\uff0c\u5927\u4eba\u53ef\u8981\u5148\u8fdb\u4e9b\u70b9\u5fc3\uff0c\u4f11\u606f\u7247\u523b\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u7528\u4e86\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u54b1\u4eec\u770b\u770b\u5343\u4f5b\u6bbf\uff0c\u8fbe\u6469\u9762\u58c1\u77f3\uff0c\u5c31\u79bb\u5f00\u8fd9\u91cc\uff0c\u5927\u5e08\u8bf4\u7684\u4e0d\u9519\uff0c\u8fd9\u5730\u65b9\u786e\u5b9e\u6ca1\u6709\u4f7f\u4eba\u7559\u604b\u5fd8\u8fd4\u7684\u98ce\u5149\u3002\u201d \u3000\u3000\u53f8\u5584\u5fae\u5fae\u4e00\u6014\uff0c\u9053\uff1a\u201c\u5927\u4eba\uff0c\u662f\u4e0d\u662f\u672c\u5bfa\u65b9\u4e08\u5f97\u7f6a\u4e86\u4f60\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u4ed6\u8eab\u4f53\u4e0d\u597d\uff0c\u6211\u4eec\u8c08\u8bdd\u4e0d\u591a\u3002\u201d \u3000\u3000\u201c\u8fd9\u5012\u4e5f\u662f\uff0c\u8001\u65b9\u4e08\u4e00\u8eab\u6b66\u529f\uff0c\u5341\u5206\u9ad8\u660e\u2026\u2026\u201d\u53f8\u5584\u9053\uff1a\u201c\u53ef\u662f\u82f1\u96c4\u53ea\u6015\u75c5\u6765\u78e8\uff0c\u5e74\u7eaa\u4e00\u5927\u5440\uff01\u6b66\u529f\u4e5f\u4e0d\u7ba1\u7528\u4e86\uff0c\u4e00\u573a\u5c0f\u75c5\uff0c\u5c31\u628a\u8001\u65b9\u4e08\u7ed9\u6298\u817e\u7684\u53d7\u4e0d\u4e86\u5566\uff01\u5982\u6709\u5f00\u7f6a\u4e4b\u5904\uff0c\u8d2b\u50e7\u8fd9\u53a2\u966a\u793c\u3002\u201d \u3000\u3000\u53cc\u624b\u62b1\u62f3\uff0c\u6df1\u6df1\u4e00\u63d6\uff0c\u4f3c\u662f\u5fd8\u8bb0\u4e86\u548c\u5c1a\u7684\u8eab\u4efd\u3002 \u3000\u3000\u6c5f\u67ab\u4e5f\u4e0d\u53bb\u70b9\u7834\uff0c\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6ca1\u4ec0\u4e48\u5566\uff01\u8001\u65b9\u4e08\u8eab\u4f53\u4e0d\u597d\uff0c\u6211\u4eec\u4e0d\u4fbf\u591a\u6240\u6253\u5fe7\uff0c\u8d70\u5427\uff01\u6211\u4eec\u5148\u53bb\u770b\u770b\u5343\u4f5b\u6bbf\u3002\u201d \u3000\u3000\u4e94\u767e\u7f57\u6c49\u50cf\uff0c\u5de5\u827a\u7cbe\u7edd\uff0c\u4f46\u6c5f\u67ab\u4e5f\u53ea\u662f\u8d70\u9a6c\u770b\u82b1\uff0c\u5012\u662f\u5bf9\u8fbe\u6469\u7559\u5f71\u77f3\uff0c\u770b\u5f97\u5341\u5206\u4ed4\u7ec6\u3002 \u3000\u3000\u6e38\u8d4f\u5c11\u6797\u5bfa\uff0c\u53ea\u4e0d\u8fc7\u82b1\u4e86\u4e24\u4e2a\u65f6\u8fb0\uff0c\u8f9e\u522b\u77e5\u5ba2\u53f8\u5584\uff0c\u76f4\u5954\u767b\u5c01\u5bc4\u653e\u9a6c\u8f66\u7684\u5ba2\u6808\uff0c\u5403\u70b9\u4e1c\u897f\uff0c\u8fde\u591c\u8d77\u7a0b\uff0c\u5954\u5411\u6c74\u4eac\u3002 \u3000\u3000\u5927\u548c\u5c1a\u867d\u7136\u89c9\u7740\uff0c\u8d70\u5f97\u592a\u6025\u4e86\uff0c\u6765\u514d\u62db\u4eba\u8d77\u7591\uff0c\u4f46\u4ed6\u4e00\u76f4\u5c0f\u5fc3\u56db\u987e\uff0c\u672a\u53d1\u89c9\u6709\u4eba\u8ffd\u8e2a\uff0c\u4f46\u4ecd\u7136\u653e\u5fc3\u4e0d\u4e0b\uff0c\u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u4e24\u4f4d\u59d1\u5a18\uff0c\u4f60\u4eec\u8981\u7559\u5fc3\u4e00\u4e9b\uff0c\u522b\u8981\u4eba\u76ef\u4e0a\u4e86\uff0c\u6211\u4eec\u8fd8\u4e0d\u77e5\u9053\uff1f\u201d \u3000\u3000\u201c\u5f88\u5947\u602a\u7684\u611f\u89c8\u2026\u2026\u201d\u541f\u971c\u9053\uff1a\u201c\u6211\u548c\u59d0\u59d0\u5341\u5206\u5c0f\u5fc3\uff0c\u4f46\u5374\u77a7\u4e0d\u51fa\u6709\u4eba\u8ffd\u8e2a\uff0c\u4f46\u611f\u89c9\u4e0a\uff0c\u597d\u50cf\u6709\u4eba\u8ffd\u8e2a\u4e00\u6837\u3002\u201d \u3000\u3000\u201c\u6211\u5df2\u7559\u795e\u56db\u5904\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u6ca1\u6709\u53d1\u73b0\u8ffd\u8e2a\u7684\u4eba\u3002\u201d \u3000\u3000\u201c\u541f\u971c\uff0c\u8fd9\u79cd\u611f\u89c9\u7531\u4f55\u800c\u6765\uff0c\u662f\u5929\u72d0\u6b66\u529f\uff1f\u2026\u2026\u201d\u5927\u548c\u5c1a\u9053\uff1a\u201c\u8fd8\u662f\u5f20\u59d1\u5a18\u7684\u5947\u6280\u5462\uff1f\u201d \u3000\u3000\u201c\u90fd\u4e0d\u662f\u3002\u53ef\u80fd\u662f\u6211\u591a\u5fc3\u4e86\uff1f\u6211\u76f8\u4fe1\uff0c\u771f\u6709\u4eba\u8ffd\u8e2a\uff0c\u7edd\u5bf9\u9003\u4e0d\u8fc7\u6211\u4eec\u7684\u76d1\u89c6\u3002\u201d \u3000\u3000\u5927\u548c\u5c1a\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u7591\u5fc3\u751f\u6697\u9b3c\uff0c\u4f60\u771f\u628a\u6211\u5413\u4e86\u4e00\u8df3\uff01\u201d \u3000\u3000\u4e03\u5b9d\u5927\u5e08\u65e0\u6cd5\u786e\u77e5\u6c5f\u67ab\u7684\u6b66\u529f\uff0c\u9ad8\u5230\u4f55\u7b49\u5883\u754c\uff1f \u3000\u3000\u4f46\u4ed6\u77e5\u9053\u5c11\u6797\u5bfa\u8fbe\u6469\u3001\u6212\u5f8b\u4e24\u9662\u9662\u4e3b\u7684\u6b66\u529f\uff0c\u7edd\u4e0d\u5728\u81ea\u5df1\u4e4b\u4e0b\uff0c\u5bf9\u65b9\u6ca1\u6709\u501a\u591a\u4e3a\u80dc\uff0c\u53cd\u800c\u4ee5\u5be1\u51fb\u4f17\u3002 \u3000\u3000\u516d\u4e2a\u4eba\u5bf9\u4ed8\u4e24\u9662\u4e3b\u6301\uff0c\u548c\u5341\u4e8c\u4f4d\u4e0a\u5ea7\u9ad8\u50e7\uff0c\u8fd9\u5341\u56db\u4e2a\u4eba\uff0c\u662f\u5c11\u6797\u5bfa\u7684\u771f\u6b63\u7cbe\u9510\uff0c\u6c5f\u67ab\u53c8\u80fd\u5bf9\u4ed8\u4eba\u5bb6\u51e0\u5341\u4eba\u5462\uff1f \u3000\u3000\u4e00\u5bf9\u4e00\uff0c\u5927\u548c\u5c1a\u76f8\u4fe1\u6c5f\u67ab\u53ef\u4ee5\uff0c\u4f46\u7edd\u4e0d\u80fd\u540c\u65f6\u5bf9\u4ed8\u4e24\u4e2a\u4eba\uff0c\u4e24\u4e2a\u5c0f\u72d0\u5973\uff0c\u5408\u5bf9\u8d77\u6765\u53ea\u6015\u4e5f\u5bf9\u4e0d\u4e86\u4e00\u4e2a\uff0c\u8fd9\u8d9f\u5c11\u6797\u5bfa\u4e4b\u884c\uff0c\u5b8c\u5168\u6253\u57ae\u4e86\u5927\u548c\u5c1a\u7684\u4fe1\u5fc3\uff0c\u82e6\u7684\u662f\u6ee1\u6000\u5fe7\u8651\uff0c\u8fd8\u4e0d\u80fd\u8bf4\u51fa\u6765\u3002 \u3000\u3000\u4e00\u65e6\u4f7f\u6c5f\u67ab\u4e86\u89e3\u4e86\u771f\u76f8\uff0c\u5c31\u6709\u4e24\u4e2a\u7ed3\u679c\uff0c\u7b2c\u4e00\u662f\u4ed6\u6839\u672c\u4e0d\u76f8\u4fe1\u654c\u4eba\u6b66\u529f\u9ad8\u5230\u90a3\u79cd\u7a0b\u5ea6\uff0c\u5b58\u5fc3\u4e00\u8bd5\uff0c\u8bf4\u4e0d\u5b9a\u4f1a\u8f6c\u56de\u5c11\u6797\u5bfa\u53bb\uff0c\u627e\u90a3\u6697\u4e2d\u628a\u6301\u5bfa\u52a1\u7684\u4eba\uff0c\u653e\u624b\u4e00\u62da\u3002 \u3000\u3000\u7b2c\u4e8c\u662f\u4fe1\u5fc3\u5d29\u6e83\uff0c\u89c9\u7740\u4e09\u4f4d\u5e08\u957f\u7684\u57f9\u517b\u82e6\u5fc3\uff0c\u767d\u8d39\u4e86\uff0c\u6b66\u6797\u4e09\u5723\u6280\u827a\u96c6\u4e8e\u4e00\u8eab\uff0c\u7adf\u662f\u8fd8\u4e0d\u5982\u4eba\uff0c\u90a3\u5c31\u5fc3\u7070\u610f\u61d2\uff0c\u518d\u65e0\u6597\u5fd7\uff0c\u4ed6\u662f\u6b66\u6797\u4e2d\u5e0c\u671b\u6240\u4f20\uff0c\u4e00\u65e6\u4ed6\u5fd7\u6c14\u6d88\u6c89\uff0c\u8fd9\u4e2a\u4ed7\uff0c\u54ea\u91cc\u8fd8\u6253\u5f97\u4e0b\u53bb\u3002 \u3000\u3000\u5927\u548c\u5c1a\u5fc3\u4e2d\u82e6\u554a\uff01\u201c\u82e6\u4e0d\u582a\u8a00\u201d\uff0c\u5927\u6982\u5c31\u662f\u8fd9\u79cd\u6ecb\u5473\u4e86\u3002 \u3000\u3000\u4ed6\u6bd4\u6c5f\u67ab\u66f4\u5e0c\u671b\u65e9\u5230\u6c74\u4eac\uff0c\u4ee5\u63ed\u5f00\u8c1c\u5e95\u3002 \u3000\u3000\u6240\u4ee5\uff0c\u4ed6\u8ba1\u5212\u8d76\u8def\uff0c\u5728\u9a6c\u529b\u8d1f\u8377\u7684\u6781\u9650\u4e0b\uff0c\u52a0\u5feb\u901f\u5ea6\u3002 \u3000\u3000\u539f\u6765\u8981\u4e03\u516b\u5929\u7684\u884c\u7a0b\uff0c\u5927\u548c\u5c1a\u53ea\u5316\u4e86\u4e09\u5929\u4e24\u591c\uff0c\u5c31\u5230\u4e86\u6c74\u4eac\u3002 \u3000\u3000\uff08\u5f00\u5c01\uff09 \u3000\u3000\u5927\u548c\u5c1a\u6c89\u9ed8\u4e86\uff0c\u4e00\u8def\u4e0a\u5f88\u5c11\u8bf4\u8bdd\uff0c\u4f46\u4ed6\u7684\u4e3e\u6b62\u884c\u52a8\uff0c\u4ecd\u7136\u5341\u5206\u5c0f\u5fc3\uff0c\u5148\u8981\u4e24\u4e2a\u5c0f\u72d0\u5973\u6e9c\u4e0b\u9a6c\u8f66\uff0c\u6f5c\u4f0f\u9053\u4e2d\uff0c\u624d\u628a\u9a6c\u8f66\u9a70\u5165\u4e00\u5ea7\u8352\u6797\uff0c\u653e\u4e86\u5065\u9a6c\uff0c\u5288\u788e\u8f66\u8f86\uff0c\u57cb\u4e8e\u5730\u4e0b\uff0c\u53c8\u6234\u4e0a\u5f20\u56db\u7ad9\u7ed9\u4ed6\u4eec\u7684\u4eba\u76ae\u9762\u5177\uff0c\u624d\u5f97\u51fa\u8352\u6797\u3002 \u3000\u3000\u4e24\u4e2a\u5c0f\u72d0\u5973\u4f3c\u662f\u611f\u53d7\u5230\u5927\u548c\u5c1a\u7684\u4f20\u67d3\uff0c\u7adf\u7136\u4e0d\u518d\u5b09\u95f9\uff0c\u4e00\u6539\u6545\u6001\uff0c\u53d8\u6210\u4e86\u7aef\u7aef\u5e84\u5728\u7684\u5c0f\u59d1\u5a18\u3002 \u3000\u3000\u770b\u4e8c\u5973\u6c89\u9ed8\u4e0d\u8a00\uff0c\u5927\u548c\u5c1a\u7684\u5fc3\u4e2d\u6709\u70b9\u96be\u8fc7\uff0c\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6709\u6ca1\u6709\u53d1\u89c9\u53ef\u7591\u7684\u4eba\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\u2026\u2026\u201d\u541f\u96ea\u9053\uff1a\u201c\u59b9\u59b9\u6f5c\u9690\u9053\u65c1\uff0c\u672a\u89c1\u884c\u4eba\uff0c\u96ea\u513f\u767b\u5728\u4e00\u682a\u5927\u6811\u9876\u7aef\uff0c\u4e94\u91cc\u5185\u6765\u89c1\u53ef\u7591\u4eba\u7269\u3002\u201d \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u548c\u5c1a\uff0c\u4f60\u597d\u50cf\u5fe7\u5fc3\u5fe1\u5fe1\u3002\u2026\u2026\u201d \u3000\u3000\u201c\u662f\u554a\uff01\u5e0c\u671b\u80fd\u65e9\u4e9b\u67e5\u51fa\u72d7\u76ae\u846b\u82a6\uff0c\u770b\u7740\u5c11\u6797\u5bfa\u9690\u85cf\u7684\u795e\u79d8\u6b66\u529f\uff0c\u5bf9\u6b66\u6797\u662f\u5426\u771f\u6709\u5e2e\u52a9\uff1f\u201d \u3000\u3000\u201c\u5c11\u6797\u5bfa\u4e03\u5341\u4e8c\u79cd\u7edd\u6280\uff0c\u65e9\u5df2\u8bb0\u4e8e\u5b58\u518c\u4e4b\u4e0a\uff0c\u53ea\u6050\u65e9\u5df2\u88ab\u4eba\u53d6\u8d70\u4e86\uff0c\u5168\u5bfa\u50e7\u4f17\uff0c\u90fd\u4efb\u4eba\u5bb0\u5272\uff0c\u65e0\u80fd\u6297\u62d2\uff0c\u96be\u9053\u8fd8\u80fd\u4fdd\u62a4\u6b66\u5b66\u79d8\u7b08\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u63d0\u51fa\u4e86\u5fc3\u4e2d\u7684\u7591\u95ee\u3002 \u3000\u3000\u201c\u679c\u771f\u5982\u6b64\uff0c\u8001\u65b9\u4e08\u4e5f\u7528\u4e0d\u7740\u8981\u6211\u4eec\u6765\u6c74\u4eac\u4e86\uff0c\u6211\u548c\u5c1a\u62c5\u5fc3\u7684\u662f\u72d7\u76ae\u846b\u82a6\uff0c\u80fd\u5426\u8fd8\u6d3b\u7740\u4e16\u95f4\uff0c\u5bf9\u6211\u4eec\u6709\u6240\u4ea4\u4ee3\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4eec\u6839\u672c\u6ca1\u6709\u542c\u5230\u72d7\u76ae\u846b\u82a6\u8fd9\u53e5\u8bdd\uff0c\u5982\u4f55\u4f1a\u627e\u4e0a\u5730\uff1f\u201d\u541f\u971c\u9053\uff1a\u201c\u54b1\u4eec\u4e00\u89c1\u9762\uff0c\u5c31\u4e0d\u518d\u79bb\u5f00\u4ed6\uff0c\u4e00\u76f4\u628a\u4e8b\u60c5\u5f04\u6e05\u695a\u518d\u8d70\uff01\u201d \u3000\u3000\u201c\u597d\u4e3b\u610f\uff0c\u6c5f\u8001\u5f1f\u5fc3\u610f\u5982\u4f55\uff1f\u201d \u3000\u3000\u5927\u548c\u5c1a\u501f\u77f3\u95ee\u8def\uff0c\u8981\u6c5f\u67ab\u4f5c\u4e2a\u660e\u786e\u7684\u51b3\u5b9a\u3002 \u3000\u3000\u201c\u597d\uff01\u2026\u2026\u201d\u6c5f\u67ab\u4e5f\u662f\u806a\u660e\u4eba\uff0c\u5df2\u77e5\u5927\u548c\u5c1a\u7684\u82e6\u5fc3\uff0c\u53f9\u53e3\u6c14\uff0c\u63a5\u9053\uff1a\u201c\u4f46\u613f\u5c11\u6797\u5bfa\u795e\u79d8\u4fdd\u5b58\u7684\u7edd\u827a\uff0c\u786e\u6709\u4ef7\u503c\uff01\u201d \u3000\u3000\u4ed6\u5fc3\u4e2d\u4ecd\u662f\u4e0d\u592a\u76f8\u4fe1\uff0c\u4f46\u603b\u7b97\u7b54\u5e94\u4e86\u59d4\u5c48\u6c42\u5168\u3002 \u3000\u3000\u76f8\u56fd\u5bfa\u5916\uff0c\u662f\u6c74\u4eac\u6709\u540d\u7684\u6742\u800d\u5e02\u573a\uff0c\u767e\u827a\u6742\u9648\uff0c\u4e07\u8d27\u4e91\u96c6\uff0c\u4e0a\u81f3\u540d\u4eba\u4e66\u753b\uff0c\u4e0b\u81f3\u82b1\u5e03\u978b\u889c\u3002 \u3000\u3000\u8bf4\u4e66\u7684\u3001\u5356\u5531\u7684\u3001\u5356\u836f\u7684\uff0c\u4f60\u53ea\u8981\u6709\u65f6\u95f4\uff0c\u8dd1\u4e2a\u5927\u534a\u5929\uff0c\u8fd8\u89c2\u8d4f\u4e0d\u5c3d\uff0c\u6240\u4ee5\uff0c\u90a3\u91cc\u4eba\u6f6e\u6c79\u6d8c\uff0c\u5ddd\u6d41\u4e0d\u606f\uff0c\u4e5f\u662f\u4e09\u6559\u4e5d\u6d41\u4eba\u7269\u7684\u51fa\u6ca1\u4e4b\u5904\u3002 \u3000\u3000\u6c5f\u67ab\u4e00\u88ad\u65e7\u9752\u886b\uff0c\u518d\u52a0\u4e0a\u4e00\u5f20\u4eba\u6b21\u9762\u5177\uff0c\u770b\u4e0a\u53bb\u6781\u50cf\u56db\u5341\u5de6\u53f3\u7684\u843d\u7b2c\u79c0\u624d\u3002 \u3000\u3000\u53f6\u96ea\u3001\u541f\u971c\u4e5f\u8863\u7740\u7834\u65e7\uff0c\u8138\u8272\u9edd\u9ed1\uff0c\u867d\u4e0d\u662f\u8981\u996d\u7684\uff0c\u53ef\u662f\u4e00\u770b\u5c31\u77e5\u9053\u662f\u7a77\u4eba\u5bb6\u7684\u5b69\u5b50\u4e86\u3002 \u3000\u3000\u5927\u548c\u5c1a\u7070\u5e03\u957f\u888d\uff0c\u8170\u675f\u767d\u5e26\uff0c\u50cf\u4e2a\u8dd1\u5355\u5e2e\u7684\u5ba2\u4eba\uff0c\u56db\u4e2a\u4eba\uff0c\u770b\u4e0a\u53bb\uff0c\u5c31\u5c5e\u4ed6\u50cf\u6837\u4e00\u4e9b\u4e86\u3002 \u3000\u3000\u96ea\u3001\u971c\u4e8c\u5973\uff0c\u4ece\u6765\u6ca1\u6709\u89c1\u8fd9\u6837\u70ed\u95f9\u7684\u5730\u65b9\uff0c\u5145\u6ee1\u7740\u597d\u5947\uff0c\u5de6\u987e\u53f3\u76fc\uff0c\u770b\u7684\u5f00\u5fc3\u6781\u4e86\u3002 \u3000\u3000\u5730\u65b9\u5f88\u70ed\u95f9\uff0c\u4f46\u8303\u56f4\u4e0d\u5927\uff0c\u8d70\u4e86\u4e00\u5708\uff0c\u4e5f\u6ca1\u6709\u53d1\u89c9\u4ec0\u4e48\u662f\u72d7\u76ae\u846b\u82a6\uff1f \u3000\u3000\u201c\u6ca1\u6709\u8fd9\u4e2a\u5730\u65b9\uff0c\u96be\u9053\u72d7\u76ae\u846b\u82a6\u662f\u4e2a\u4eba\u7684\u540d\u5b57\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u8fd9\u6050\u4f2f\u8981\u627e\u4e2a\u4eba\u6253\u542c\u4e00\u4e0b\uff1f\u201d \u3000\u3000\u201c\u6211\u770b\u5230\u5356\u72d7\u76ae\u818f\u836f\u7684\u2026\u2026\u201d\u541f\u971c\u9053\uff1a\u201c\u597d\u50cf\u53ef\u4ee5\u6cbb\u8dcc\u6253\u635f\u4f24\uff0c\u662f\u4e00\u5bb6\u5c0f\u5e97\u94fa\uff0c\u4e00\u4e2a\u4eba\u7ad9\u5728\u95e8\u53e3\uff0c\u8bb2\u7684\u53e3\u6cab\u6a2a\u98de\u3002\u201d \u3000\u3000\u201c\u518d\u8d70\u4e00\u904d\u770b\u770b\uff0c\u8001\u65b9\u4e08\u7edd\u4e0d\u4f1a\u9a97\u6211\u4eec\uff0c\u2026\u2026\u201d\u5927\u548c\u5c1a\u9053\uff1a\u201c\u72d7\u76ae\u846b\u82a6\uff0c\u53ef\u80fd\u662f\u4e00\u79cd\u6807\u5e1c\u3002\u201d \u3000\u3000\u8fd9\u79cd\u5730\u65b9\u70ed\u95f9\u597d\u73a9\uff0c\u53ea\u662f\u54c1\u6d41\u590d\u6742\uff0c\u6252\u624b\u51fa\u6ca1\uff0c\u4e00\u4e0d\u5c0f\u5fc3\uff0c\u5c31\u4f1a\u88ab\u6252\u53bb\u8d22\u7269\uff0c\u5927\u548c\u5c1a\u5c31\u9047\u4e0a\u4e86\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u4ee5\u5927\u548c\u5c1a\u7684\u7cbe\u6df1\u529f\u529b\uff0c\u654f\u9510\u7684\u611f\u89c9\uff0c\u8fd9\u4e9b\u6252\u624b\uff0c\u81ea\u7136\u65e0\u6cd5\u5f97\u624b\uff0c\u4f46\u5927\u548c\u5c1a\u6545\u4f5c\u4e0d\u77e5\uff0c\u6252\u624b\u6478\u5230\u4e00\u5757\u94f6\u5b50\uff0c\u5374\u88ab\u5927\u548c\u5c1a\u56de\u624b\u4e00\u628a\u6263\u4f4f\u53f3\u8155\uff0c\u4f4e\u58f0\u95ee\uff1a\u201c\u8001\u5f1f\uff0c\u5e72\u8fd9\u884c\u4e1a\u591a\u4e45\u4e86\uff1f\u201d \u3000\u3000\u6252\u624b\u53f3\u624b\u62ff\u7740\u94f6\u5b50\uff0c\u4f46\u53f3\u817f\u88ab\u6263\u4f4f\uff0c\u5168\u8eab\u52b2\u529b\u987f\u5931\uff0c\u5fc3\u77e5\u9047\u4e0a\u4e86\u9ad8\u4eba\uff0c\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a \u3000\u3000\u201c\u5341\u5e74\u4e86\uff0c\u4f60\u8001\u5144\u662f\u7b2c\u4e00\u6b21\u6765\u901b\u76f8\u56fd\u5bfa\u5427\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u6211\u662f\u7b2c\u4e00\u6b21\u6765\u5230\u8d35\u5b9d\u5730\uff0c\u4f60\u8001\u5f1f\u6280\u827a\u592a\u5dee\u4e86\uff0c\u4e00\u5e74\u7684\u5386\u7ec3\uff0c\u5e94\u8be5\u662f\u5e72\u51c0\u4fd0\u843d\uff0c\u600e\u4e48\u4f1a\u5931\u624b\u5462\uff1f\u201d \u3000\u3000\u201c\u6211\u4e86\u5f6a\u662f\u76f8\u56fd\u5bfa\u5730\u76d8\u4e0a\u7684\u6252\u624b\u5934\u513f\uff0c\u6280\u672f\u4e00\u6d41\uff0c\u53ea\u662f\u6709\u773c\u65e0\u7403\uff0c\u9047\u4e0a\u4e86\u9ad8\u4eba\uff0c\u653e\u4e86\u6211\uff0c\u6211\u4fdd\u8bc1\u4f60\u5728\u6c74\u4eac\u5730\u9762\u4e0a\uff0c\u7edd\u4e0d\u4f1a\u518d\u901a\u6252\u7a83\uff0c\u8fd9\u5757\u94f6\u5b50\u8fd8\u7ed9\u4f60\uff0c\u800c\u4e14\uff0c\u4f60\u8001\u5144\u5728\u8fd9\u91cc\u7684\u82b1\u9500\u3001\u7528\u5ea6\uff0c\u6211\u5168\u5305\u4e86\uff0c\u987f\u987f\u9152\u8089\uff0c\u591c\u591c\u6625\u5bb5\uff0c\u600e\u4e48\u6837\uff1f\u4ea4\u4e2a\u670b\u53cb\u561b\uff01\u201d \u3000\u3000\u5927\u548c\u5c1a\u653e\u5f00\u4e86\u4e01\u5f6a\uff0c\u7b11\u9053\uff1a\u201c\u4ea4\u4e2a\u670b\u53cb\uff0c\u94f6\u5b50\u4f60\u6536\u4e0b\uff0c\u6709\u9053\u662f\u8d3c\u4e0d\u7a7a\u624b\u554a\uff01\u4f60\u5728\u8fd9\u91cc\u6df7\u4e86\u5341\u5e74\uff0c\u60f3\u5fc5\u662f\u5f88\u719f\u6089\u4e86\u3002\u201d \u3000\u3000\u4e24\u4e2a\u4eba\u5581\u5581\u8f7b\u8c08\uff0c\u5c31\u50cf\u662f\u8001\u670b\u53cb\u5728\u53d9\u65e7\u4e00\u6837\uff0c\u5012\u4e5f\u4e0d\u4f1a\u5f15\u4eba\u6ce8\u76ee\u3002 \u3000\u3000\u201c\u719f\uff0c\u719f\u7684\u5f88\uff0c\u4f60\u8981\u627e\u4eba\uff0c\u53ea\u8981\u8bf4\u51fa\u59d3\u540d\uff0c\u6211\u80fd\u8bf4\u51fa\u4ed6\u8eab\u4e0a\u6709\u51e0\u6761\u808b\u9aa8\uff0c\u627e\u5730\u65b9\uff0c\u6211\u80fd\u6570\u6e05\u695a\u76f8\u56fd\u5bfa\u8fd9\u4e2a\u5730\u76d8\u4e0a\uff0c\u6709\u51e0\u4e2a\u8001\u9f20\u7684\u6d1e\u7a74\uff0c\u2026\u2026\u201d\u4e01\u5f6a\u5927\u8a00\u4e0d\u60ed\u7684\u8bf4\u9053\uff1a\u201c\u5c3d\u7ba1\u8bf7\u95ee\u5427\uff1f\u201d \u3000\u3000\u201c\u6211\u627e\u72d7\u76ae\u846b\u82a6\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\uff1f\u72d7\u76ae\u846b\u82a6\uff0c\u662f\u4eba\u8fd8\u662f\u5730\u540d\uff1f\u201d\u4e86\u5f6a\u76b1\u8d77\u4e86\u7709\u5934\u3002 \u3000\u3000\u201c\u5c31\u662f\u4e0d\u6e05\u695a\u554a\uff1f\u2026\u2026\u201d\u5927\u548c\u5c1a\u6709\u70b9\u5fc3\u614c\u4e86\uff0c\u8fd9\u4e48\u4e00\u4e2a\u4eba\uff0c\u4e5f\u4e0d\u77e5\u9053\u72d7\u76ae\u846b\u82a6\u662f\u4ec0\u4e48\uff1f \u3000\u3000\u53ef\u771f\u662f\u6709\u70b9\u9ebb\u70e6\u4e86\u3002 \u3000\u3000\u201c\u4f60\u786e\u5b9a\u4ed6\u5728\u76f8\u56fd\u5bfa\uff0c\u2026\u2026\u201d \u3000\u3000\u201c\u786e\u5b9a\uff0c\u800c\u4e14\uff0c\u5df2\u7ecf\u5728\u8fd9\u91cc\u4f4f\u4e86\u51e0\u5341\u5e74\u3002\u201d\u5927\u548c\u5c1a\u8bf4\u3002 \u3000\u3000\u4e01\u5f6a\u6293\u7740\u5934\u76ae\uff0c\u6c89\u601d\u4e86\u826f\u4e45\uff0c\u9053\uff1a\u201c\u83ab\u975e\u662f\u4ed6\uff1f\u201d \u3000\u3000\u201c\u8c01\uff1f\u8bf4\u8bf4\u770b\u2026\u2026\u201d\u5927\u548c\u5c1a\u9053\uff1a\u201c\u627e\u5230\u4e86\uff0c\u6211\u518d\u4ed8\u4e00\u767e\u4e24\u94f6\u5b50\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u8bdd\uff1f\u2026\u2026\u201d\u4e01\u5f6a\u9053\uff1a\u201c\u54b1\u4eec\u5df2\u662f\u670b\u53cb\uff0c\u8fd8\u8c08\u4ec0\u4e48\u94f6\u5b50\u4e0d\u94f6\u5b50\uff0c\u53ea\u662f\u6211\u6ca1\u6709\u628a\u63e1\uff0c\u8fd9\u6863\u4e8b\uff0c\u8fd8\u771f\u628a\u6211\u96be\u4f4f\u4e86\uff0c\u72d7\u76ae\u846b\u82a6\uff0c\u4e0d\u662f\u5730\u65b9\uff0c\u4e5f\u4e0d\u662f\u4e00\u4e2a\u4eba\uff0c\u800c\u662f\u4e00\u4e2a\u7ef0\u53f7\u3002\u201d \u3000\u3000\u201c\u963f\u5f25\u9640\u4f5b\uff01\u603b\u7b97\u6709\u70b9\u7709\u76ee\u4e86\u2026\u2026\u201d\u5927\u548c\u5c1a\u5fc3\u91cc\u5ff5\u4f5b\uff0c\u53e3\u4e2d\u8bf4\u9053\uff1a\u201c\u5927\u6982\u662f\u4e2a\u7ef0\u53f7\u5427\uff1f\u201d \u3000\u3000\u201c\u6709\u4e00\u4e2a\u5356\u8349\u836f\u7684\u90ce\u4e2d\uff0c\u6ce1\u5236\u4e00\u79cd\u836f\u9152\uff0c\u836f\u9152\u88c5\u5728\u846b\u82a6\u91cc\uff0c\u6bcf\u5e74\u53ea\u5356\u4e24\u767e\u846b\u82a6\uff0c\u56db\u6708\u521d\u4e00\u5f00\u5e02\uff0c\u5356\u5b8c\u4e86\u5c31\u5173\u95e8\uff0c\u660e\u5e74\u56db\u6708\u521d\u4e00\u518d\u6765\uff0c\u5341\u4e24\u94f6\u4e00\u846b\u82a6\uff0c\u4ef7\u94b1\u662f\u5413\u4eba\uff0c\u4f46\u836f\u9152\u5374\u7075\u7684\u90aa\u6c14\u3002 \u3000\u3000\u751f\u610f\u597d\u5230\u6324\u7834\u5934\uff0c\u5f00\u5e02\u7b2c\u4e00\u5929\uff0c\u5c31\u88ab\u62a2\u8d2d\u4e00\u5149\uff0c\u846b\u82a6\u626f\u5230\u62d8\u76ae\u4e0a\uff0c\u90a3\u662f\u56e0\u4e3a\u4ed6\u9664\u4e86\u836f\u9152\u4e4b\u5916\uff0c\u8fd8\u5356\u4e00\u79cd\u72d7\u76ae\u818f\u836f\uff0c\u8170\u9178\u80cc\u75db\uff0c\u8d34\u4e0a\u5c31\u597d\uff0c\u80fd\u6563\u70ed\u62d4\u6bd2\u2026\u2026\u201d\u4e01\u5f6a\u8bf4\uff0c\u201c\u53ef\u60dc\u7684\u662f\u818f\u836f\u6bd4\u846b\u82a6\u9152\u66f4\u5c11\uff0c\u6bcf\u5e74\u53ea\u5356\u4e00\u767e\u5f20\uff0c\u4e0d\u6536\u5b9a\u91d1\uff0c\u4e5f\u4e0d\u9884\u7ea6\uff0c\u56db\u6708\u521d\u4e00\u53bb\u6392\u961f\uff0c\u6324\u6b7b\u4e86\u662f\u4f60\u5bb6\u4e8b\u3002\u201d \u3000\u3000\u201c\u636e\u8bf4\u662f\u7956\u4f20\u79d8\u65b9\uff0c\u8fd9\u4e2a\u4eba\uff0c\u53ea\u6709\u5f00\u5e02\u90a3\u4e00\u5929\u5728\u76f8\u56fd\u5bfa\u5e94\u5e02\uff0c\u4e0d\u5230\u4e2d\u5348\u836f\u9152\u3001\u818f\u836f\uff0c\u5168\u4f1a\u5356\u5b8c\uff0c\u4e25\u683c\u7684\u8bf4\uff0c\u4e00\u5e74\u53ea\u5728\u8fd9\u91cc\u505c\u7559\u4e24\u4e2a\u65f6\u8fb0\uff0c\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u8981\u627e\u4ed6\uff0c\u8d8a\u5feb\u8d8a\u597d\uff0c\u2026\u2026\u201d\u5927\u548c\u5c1a\u8bf4\uff1a \u3000\u3000\u201c\u80fd\u4e0d\u80fd\u627e\u5230\u4ed6\u7684\u4f4f\u5904\uff1f\u201d \u3000\u3000\u4e01\u5f6a\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u4ed6\u4e0d\u559c\u548c\u4eba\u4ea4\u5f80\uff0c\u542c\u8bf4\u662f\u5e73\u5e38\u90fd\u5728\u5bb6\u71ac\u836f\u3001\u6ce1\u9152\uff0c\u4f4f\u54ea\u91cc\u6211\u4e0d\u77e5\u9053\uff0c\u6211\u5f97\u6253\u542c\u4e00\u4e0b\uff0c\u7ed9\u6211\u534a\u5929\u65f6\u95f4\uff0c\u665a\u996d\u65f6\u523b\u54b1\u4eec\u78b0\u5934\uff0c\u5c31\u5728\u524d\u9762\u7684\u8c46\u513f\u5e84\u559d\u4e00\u676f\uff0c\u8c46\u513f\u5728\u867d\u7136\u4e0d\u5927\uff0c\u4f46\u624b\u827a\u7cbe\u5de7\uff0c\u4e00\u9053\u9ec4\u6cb3\u9ca4\u9c7c\uff0c\u70e7\u7684\u4e0d\u8f93\u53c8\u4e00\u6751\uff0c\u6211\u5148\u53bb\u5b9a\u4e2a\u96c5\u5ea7\uff0c\u8981\u4e0d\u7136\uff0c\u5230\u65f6\u5019\u7edd\u627e\u4e0d\u5230\u4f4d\u7f6e\u3002\u201d \u3000\u3000\u5927\u548c\u5c1a\u5fc3\u91cc\u6025\u5f97\u8981\u547d\uff0c\u4f46\u8868\u9762\u4e0a\u5374\u88c5\u4f5c\u9547\u9759\uff0c\u7b11\u9053\uff1a\u201c\u600e\u4e48\uff1f\u4e00\u4e2a\u5c0f\u996d\u5e84\uff0c\u751f\u610f\u5c31\u597d\u5230\u4eba\u6324\u4eba\uff0c\u76f8\u56fd\u5bfa\u53ef\u771f\u662f\u4eba\u591a\u554a\uff01\u201d \u3000\u3000\u201c\u8c46\u513f\u5e84\u5f00\u5f20\u4e0d\u4e00\u5e74\u591a\u4e9b\uff0c\u4f46\u5ba2\u4eba\u5374\u591a\u5230\u6392\u961f\u5019\u5ea7\uff0c\u624b\u827a\u597d\u662f\u5927\u539f\u56e0\uff0c\u5e97\u4e1c\u4e3b\u4e09\u4e2a\u5982\u82b1\u4f3c\u7389\u7684\u5973\u513f\uff0c\u4e5f\u662f\u539f\u56e0\u4e4b\u4e00\uff0c\u2026\u2026\u201d\u4e01\u5f6a\u795e\u79d8\u4e00\u7b11\uff0c\u9053\uff1a \u3000\u3000\u201c\u8001\u677f\u5a18\u638c\u53a8\uff0c\u5927\u5973\u513f\u4f5c\u4e0b\u624b\uff0c\u4e8c\u59b9\u3001\u4e09\u59b9\u8dd1\u5802\uff0c\u5728\u6c74\u4eac\u53ef\u662f\u53ea\u6b64\u4e00\u5bb6\uff0c\u522b\u65e0\u5206\u53f7\uff0c\u4e09\u4e2a\u5973\u513f\u4ee5\u8c46\u4e3a\u540d\uff0c\u5927\u8c46\u3001\u4e8c\u8c46\u3001\u4e09\u8c46\uff0c\u8c46\u513f\u5e84\u5c31\u662f\u4ee5\u5979\u4eec\u59ca\u59b9\u4e3a\u540d\uff0c\u4ef7\u94b1\u867d\u7136\u8d35\u4e00\u70b9\uff0c\u53ef\u662f\u503c\u5f97\uff0c\u4f60\u8001\u5144\u53bb\u770b\u8fc7\u3001\u5403\u8fc7\uff0c\u5c31\u77e5\u9053\u540d\u4e0d\u865a\u4f20\u4e86\u3002\u201d \u3000\u3000\u5927\u548c\u5c1a\u5fc3\u4e2d\u4e00\u52a8\uff0c\u9053\uff1a\u201c\u597d\uff0c\u54b1\u4eec\u665a\u4e0a\u5728\u8c46\u513f\u5e84\u89c1\uff0c\u4f60\u8bf7\u5ba2\uff0c\u6211\u4ed8\u8d26\uff0c\u600e\u4e48\u6837\uff0c\u4e94\u5341\u4e24\u94f6\u5b50\u591f\u4e0d\u591f\uff1f\u201d \u3000\u3000\u201c\u600e\u4e48\uff1f\u4f60\u8981\u4e70\u4eba\uff0c\u90a3\u5c31\u5c11\u4e86\u4e00\u4e9b\uff0c\u53ea\u5403\u4e00\u987f\u996d\uff0c\u4e09\u4e94\u4e24\u94f6\u5b50\uff0c\u5c31\u591f\u6211\u4eec\u62e3\u597d\u7684\u5403\u4e86\uff0c\u2026\u2026\u201d\u4e01\u5f6a\u9053\uff1a \u3000\u3000\u201c\u522b\u5fd8\u4e86\u6211\u4eec\u5df2\u7ecf\u662f\u670b\u53cb\uff0c\u6211\u8bf7\u5ba2\uff0c\u6211\u4ed8\u8d26\uff0c\u4f60\u53ea\u7ba1\u4e24\u4e2a\u80a9\u8180\u62ac\u5f20\u5634\u53bb\u5403\u5c31\u884c\u4e86\u3002\u201d\u8bf4\u5b8c\u8bdd\uff0c\u8f6c\u8eab\u5c31\u8d70\u3002 \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u6ca1\u6709\u62e6\u963b\uff0c\u4e5f\u6ca1\u6709\u793a\u610f\u4e24\u4e2a\u5c0f\u72d0\u5973\u6697\u4e2d\u8ddf\u8e2a\uff0c\u4f5c\u6252\u624b\u7684\u90fd\u5f88\u673a\u8b66\uff0c\u4f55\u51b5\uff0c\u4e01\u5f6a\u662f\u76f8\u56fd\u5bfa\u7684\u6252\u624b\u5934\u513f\uff0c\u624b\u4e0b\u5144\u5f1f\u4f17\u591a\uff0c\u518d\u597d\u7684\u8ddf\u8e2a\u672f\uff0c\u4e5f\u53ef\u80fd\u88ab\u53d1\u89c9\uff0c\u6240\u4ee5\uff0c\u5927\u548c\u5c1a\u51b3\u5b9a\u8d4c\u4e00\u4e0b\uff0c\u5c31\u7b97\u4e01\u5f6a\u5931\u7ea6\u4e0d\u6765\uff0c\u8c46\u513f\u5728\u4e5f\u503c\u5f97\u53bb\u89c2\u5bdf\u4e00\u4e0b\u3002 \u3000\u3000\u6b64\u523b\u4e0d\u8fc7\u662f\u8fd1\u5348\u65f6\u5206\uff0c\u8981\u8fd1\u4e09\u4e2a\u65f6\u8fb0\u7684\u7b49\u5f85\u8fb0\u5149\uff0c\u53ea\u597d\u5728\u76f8\u56fd\u5bfa\u901b\u7740\u73a9\u4e86\u3002 \u3000\u3000\u8fd9\u4e00\u6b21\u662f\u771f\u6b63\u7684\u6e38\u73a9\u3001\u89c2\u8d4f\uff0c\u4e0d\u4f46\u96ea\u3001\u971c\u4e8c\u5973\u770b\u5f97\u8fc7\u7624\uff0c\u5c31\u662f\u6c5f\u67ab\u4e5f\u662f\u770b\u5f97\u5341\u5206\u6295\u5165\uff0c\u4ed6\u4eec\u4ece\u6ca1\u6709\u5728\u8fd9\u79cd\u5730\u65b9\u73a9\u8fc7\uff0c\u6c5f\u6e56\u4e0a\u7684\u8bf4\u3001\u5b66\u3001\u9017\u3001\u5531\uff0c\u5982\u80fd\u53bb\u7ec6\u5fc3\u4f53\u4f1a\uff0c\u8fd8\u771f\u4f1a\u589e\u52a0\u4eba\u7684\u89c1\u8bc6\u3001\u7ecf\u9a8c\u3002 \u3000\u3000\u5927\u548c\u5c1a\u5374\u662f\u65e0\u5fc3\u89c2\u8d4f\uff0c\u4e00\u9762\u7559\u795e\u4eba\u7fa4\u4e2d\u662f\u5426\u6709\u53ef\u7591\u4eba\u7269\uff0c\u4e00\u9762\u6697\u4f5c\u76d8\u7b97\uff0c\u627e\u4e0d\u5230\u72d7\u76ae\u846b\u82a6\uff0c\u5c31\u7acb\u523b\u8d76\u56de\u7ec8\u5357\u5c71\u548c\u5f20\u56db\u59d1\u4ed4\u7ec6\u7684\u7814\u5546\u4e00\u4e0b\uff0c\u7a76\u7adf\u8be5\u4f55\u53bb\u4f55\u4ece\uff1f \u3000\u3000\u5ffd\u7136\u95f4\u611f\u89c9\u5230\u6613\u82b1\u624b\u5f20\u56db\u59d1\u662f\u90a3\u4e48\u91cd\u8981\uff01 \u3000\u3000\u5979\u80f8\u7f57\u535a\u6742\uff0c\u827a\u51fa\u65c1\u95e8\uff0c\u5fc3\u4e2d\u5c3d\u662f\u9b3c\u70b9\u5b50\uff0c\u52ff\u602a\u6c5f\u67ab\u5bf9\u5979\u501a\u9760\u6781\u6df1\uff0c\u4f46\u6700\u91cd\u8981\u7684\u8fd8\u662f\u5979\u5bf9\u6c5f\u67ab\u7684\u5f71\u54cd\u529b\uff0c\u5927\u548c\u5c1a\u5df2\u611f\u89c9\u5230\u6c5f\u67ab\u7684\u4e3b\u89c1\u5f88\u91cd\uff0c\u4f46\u5374\u5bf9\u5f20\u56db\u59d1\u8a00\u542c\u8ba1\u4ece\u3002 \u3000\u3000\u4e00\u5207\u90fd\u5341\u5206\u6b63\u5e38\uff0c\u4ee5\u4e03\u5b9d\u548c\u5c1a\u7684\u7cbe\u660e\uff0c\u4e95\u77a7\u4e0d\u51fa\u4e00\u70b9\u53ef\u7591\u4e4b\u5904\u3002 \u3000\u3000\u6536\u83b7\u6700\u5927\u7684\u662f\u96ea\u3001\u971c\u4e8c\u5973\uff0c\u5979\u4eec\u4e0d\u4f46\u770b\u7684\u5f00\u5fc3\uff0c\u4e5f\u6709\u4e86\u4e0d\u5c11\u5fc3\u5f97\uff0c\u4f53\u4f1a\u51fa\u5f88\u591a\u8fc7\u53bb\u4e0d\u61c2\u7684\u4e8b\u3002 \u3000\u3000\u76f4\u5230\u5915\u9633\u897f\u4e0b\uff0c\u5927\u548c\u5c1a\u624d\u8bf4\u51fa\u548c\u4e01\u5f6a\u7ea6\u4f1a\u7684\u4e8b\u3002 \u3000\u3000\u6709\u4e9b\u51fa\u4eba\u610f\u5916\uff0c\u4e01\u5f6a\u7adf\u7136\u65e9\u5728\u8c46\u513f\u5728\u95e8\u53e3\u7b49\u5019\uff0c\u770b\u770b\u6c5f\u67ab\u548c\u4e24\u5c0f\u72d0\u5973\uff0c\u4f4e\u58f0\u95ee\u9053\uff1a\u201c\u8fd9\u4e8c\u4f4d\u662f\u4f60\u7684\u670b\u53cb\uff1f\u201d \u3000\u3000\u201c\u662f\u554a\uff01\u2026\u2026\u201d \u3000\u3000\u201c\u90a3\u5c31\u884c\uff0c\u4f60\u8001\u5144\u7684\u670b\u53cb\uff0c\u6211\u90fd\u6b22\u8fce\u2026\u2026\u201d\u4e86\u5f6a\u4f4e\u58f0\u63a5\u9053\uff1a\u201c\u54b1\u4eec\u5df2\u7ecf\u662f\u670b\u53cb\uff0c\u6211\u8fd8\u4e0d\u77e5\u9053\u4f60\u8d35\u59d3\u5927\u540d\u554a\uff1f\u201d \u3000\u3000\u201c\u6211\u59d3\u9f50\uff0c\u9f50\u56db\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u56de\u5934\u770b\u770b\u6c5f\u67ab\u548c\u96ea\u3001\u971c\u4e8c\u5973\uff0c\u81ea\u5df1\u4e5f\u89c9\u7740\u597d\u7b11\uff0c\u4e09\u5f20\u4eba\u76ae\u9762\u5177\uff0c\u906e\u53bb\u4e86\u82f1\u4fca\u3001\u7f8e\u4e3d\u7684\u5bb9\u8c8c\uff0c\u5e03\u8863\u9648\u65e7\uff0c\u56db\u4e2a\u4eba\u4e2d\u5c31\u5c5e\u4ed6\u5927\u548c\u5c1a\u7a7f\u7740\u6700\u50cf\u6837\uff0c\u4e5f\u96be\u602a\u4e01\u5f6a\u8981\u95ee\u4e2a\u660e\u767d\u4e86\uff01 \u3000\u3000\u8c46\u513f\u5728\u4ec5\u6709\u7684\u4e00\u95f4\u96c5\u623f\uff0c\u88ab\u4e01\u5f6a\u5b9a\u4e86\uff0c\u8fce\u5ba2\u7684\u662f\u4e00\u4f4d\u82b1\u679d\u62db\u5c55\u7684\u7f8e\u59d1\u5a18\u3002 \u3000\u3000\u4f46\u5927\u548c\u5c1a\u7559\u5fc3\u7684\u662f\u5e97\u4e1c\u4e3b\uff0c\u5750\u5728\u5927\u95e8\u53e3\u5904\u67dc\u53f0\u4e0a\u7684\u8001\u5148\u751f\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u5e97\u4e1c\u4e3b\u5e76\u4e0d\u592a\u8001\uff0c\u4e94\u5341\u5de6\u53f3\u7684\u5e74\u7eaa\uff0c\u6d53\u7709\u864e\u76ee\uff0c\u9887\u5177\u6c14\u52bf\uff0c\u4f46\u8863\u7740\u88c5\u626e\u4e0a\u5374\u6545\u4f5c\u8001\u6c14\u3002 \u3000\u3000\u5927\u548c\u5c1a\u65e0\u6cd5\u4ece\u8bb0\u5fc6\u4e2d\u627e\u51fa\u4ed6\u7684\u8eab\u4efd\uff0c\u4f46\u5374\u80af\u5b9a\u4ed6\u662f\u4e2a\u5177\u6709\u6b66\u529f\u7684\u6c5f\u6e56\u4eba\u7269\u3002 \u3000\u3000\u96be\u9053\u8fd9\u4e5f\u662f\u5929\u9a6c\u5802\uff0c\u6216\u5929\u738b\u95e8\u7684\u7ec4\u5408\u8033\u76ee\uff1f \u3000\u3000\u4e01\u5f6a\u8bf4\u7684\u4e0d\u9519\uff0c\u8c46\u513f\u5e84\u5ea7\u65e0\u865a\u5e2d\uff0c\u8fd8\u6709\u4eba\u5728\u6392\u961f\u7b49\u5019\uff0c\u8fce\u5ba2\u5c11\u5973\u5e26\u51e0\u4eba\u884c\u5165\u96c5\u623f\uff0c\u96c5\u623f\u4e2d\u5df2\u71c3\u4e0a\u706f\uff0c\u4e24\u53ea\u9ad8\u53f0\u7ea2\u70db\uff0c\u7167\u5f97\u6ee1\u5ba4\u901a\u660e\u3002 \u3000\u3000\u201c\u6211\u53eb\u4e09\u8c46\uff0c\u8bf8\u4f4d\u5148\u8bf7\u7528\u8336\uff0c\u9152\u83dc\u7acb\u523b\u53ef\u4e0a\uff0c\u6211\u8fd9\u5c31\u53bb\u7aef\u83dc\u4e0a\u9152\uff0c\u5c0f\u5e97\u4eba\u624b\u592a\u5c11\uff0c\u4e0d\u80fd\u62db\u547c\u8bf8\u4f4d\uff0c\u8fd8\u8bf7\u62c5\u5f85\u4e00\u4e8c\u3002\u201d \u3000\u3000\u5012\u4e86\u4e94\u676f\u8336\uff0c\u542b\u7b11\u800c\u53bb\u3002 \u3000\u3000\u201c\u6253\u542c\u5230\u6d88\u606f\u6ca1\u6709\uff1f\u2026\u2026\u201d \u3000\u3000\u201c\u5e78\u672a\u8fb1\u547d\uff0c\u72d7\u76ae\u846b\u82a6\u3002\u2026\u2026\u201d \u3000\u3000\u5927\u548c\u5c1a\u6253\u65ad\u4e86\u4e01\u5f6a\u548c\u8bdd\uff0c\u9053\uff1a\u201c\u5403\u8fc7\u996d\u5e26\u6211\u4eec\u53bb\uff0c\u73b0\u5728\uff0c\u559d\u9152\u8981\u7d27\u3002\u201d \u3000\u3000\u4e86\u5f6a\u4e0d\u592a\u660e\u767d\u5927\u548c\u5c1a\u7684\u5fc3\u610f\uff0c\u4f46\u4ed6\u5374\u672a\u518d\u591a\u95ee\u3002 \u3000\u3000\u9152\u83dc\u90fd\u5f88\u53ef\u53e3\uff0c\u4e0a\u83dc\u7684\u4e09\u59d1\u5a18\u4e5f\u5f88\u5e84\u91cd\uff0c\u4e8b\u5b9e\u4e0a\uff0c\u6c5f\u67ab\u7b49\u56db\u4e2a\u4eba\uff0c\u4e5f\u4e0d\u5165\u4e09\u59d1\u5a18\u7684\u6cd5\u773c\u3002 \u3000\u3000\u96c5\u5b9d\u4e2d\u6700\u82f1\u4fca\u7684\u4eba\u7269\u662f\u6252\u624b\u4e01\u5f6a\uff0c\u4e09\u59d1\u5a18\u4e00\u56e2\u548c\u6c14\uff0c\u6ee1\u8138\u7b11\u5bb9\uff0c\u5927\u6982\u4e5f\u662f\u804c\u4e1a\u5173\u7cfb\uff0c\u5982\u679c\u6c5f\u67ab\u6062\u590d\u4e86\u672c\u6765\u9762\u76ee\uff0c\u60c5\u666f\u662f\u5426\u5982\u6b64\uff1f \u3000\u3000\u90a3\u5c31\u4e0d\u80fd\u4fdd\u8bc1\u4e86\u3002 \u3000\u3000\u6c5f\u67ab\u6ca1\u6709\u8bf4\u8bdd\uff0c\u96ea\u3001\u971c\u4e8c\u5973\uff0c\u66f4\u662f\u4e0d\u80fd\u5f00\u53e3\uff0c\u5979\u4eec\u5a07\u751c\u7684\u58f0\u97f3\uff0c\u548c\u73b0\u5728\u7684\u8eab\u4efd\u5b8c\u5168\u4e0d\u80fd\u914d\u5408\u3002 \u3000\u3000\u79bb\u5f00\u8c46\u513f\u5e84\uff0c\u5927\u548c\u5c1a\u62a2\u5148\u8bf4\u9053\uff1a\u201c\u4e01\u8001\u5f1f\uff0c\u73b0\u5728\u5c31\u5e26\u6211\u4eec\u53bb\uff01\u201d \u3000\u3000\u201c\u4ed6\u4eec\u4e09\u4f4d\u5462\uff1f\u201d \u3000\u3000\u201c\u4e00\u8d77\u53bb\u2026\u2026\u201d \u3000\u3000\u201c\u53ef\u662f\uff0c\u72d7\u76ae\u846b\u82a6\u4e0d\u559c\u6b22\u548c\u4eba\u4ea4\u5f80\u2026\u2026\u201d\u4e01\u5f6a\u9053\uff1a \u3000\u3000\u201c\u53bb\u8fd9\u4e48\u591a\u4eba\uff0c\u4ed6\u5982\u4e0d\u80af\u89c1\u5ba2\u2026\u2026\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u4e0d\u7528\u4f60\u4e86\u8001\u5f1f\u62c5\u5fc3\u2026\u2026\u201d\u5927\u548c\u5c1a\u9053\uff1a \u3000\u3000\u201c\u65e2\u7136\u627e\u4e0a\u95e8\uff0c\u90a3\u5c31\u7531\u4e0d\u5f97\u4ed6\u4e86\u3002\u201d \u3000\u3000\u4e01\u5f6a\u5fae\u5fae\u4e00\u6014\uff0c\u9053\uff1a\u201c\u9f50\u5144\uff0c\u7530\u5927\u592b\u4e0d\u662f\u597d\u6b3a\u4fae\u7684\u4eba\u2026\u2026\u201d \u3000\u3000\u201c\u8001\u5f1f\u4e0d\u8981\u8bef\u4f1a\uff0c\u6211\u4eec\u5343\u91cc\u8d76\u6765\u767b\u95e8\u6c42\u6559\uff0c\u4e0d\u89c1\u4eba\u5982\u4f55\u80fd\u591f\u56de\u53bb\uff1f\u2026\u2026\u201d\u5927\u548c\u5c1a\u9053\uff1a \u3000\u3000\u201c\u8fd9\u6869\u4e8b\uff0c\u4e0d\u4f1a\u8fde\u7d2f\u5230\u4f60\uff0c\u5e26\u6211\u4eec\u5230\u7530\u5927\u592b\u7684\u95e8\u53e3\uff0c\u4f60\u5c31\u5148\u8d70\u4e00\u6b65\u3002\u201d \u3000\u3000\u4e01\u5f6a\u5df2\u77e5\u9053\u5927\u548c\u5c1a\u662f\u8eab\u6000\u6b66\u529f\u7684\u4eba\uff0c\u53ef\u80fd\u662f\u6709\u4e3a\u800c\u6765\uff0c\u4e5f\u5c31\u4e0d\u518d\u591a\u8a00\u3002 \u3000\u3000\u7530\u5927\u592b\u5c31\u4f4f\u5728\u79bb\u76f8\u56fd\u5bfa\u4e0d\u592a\u8fdc\u7684\u5730\u65b9\u3002 \u3000\u3000\u4e01\u5f6a\u6307\u6307\u4e00\u5ea7\u6c34\u5858\u65c1\u8fb9\u7684\u74e6\u820d\uff0c\u9053\uff1a\u201c\u90a3\u5c31\u662f\u4e86\uff0c\u4f60\u4eec\u53bb\u5427\uff01\u6211\u5c31\u544a\u4fbf\u4e86\u3002\u201d \u3000\u3000\u5927\u548c\u5c1a\u53d6\u51fa\u4e00\u5f20\u4e09\u767e\u4e24\u7684\u94f6\u7968\uff0c\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u6536\u4e0b\u5427\uff01\u7b97\u662f\u8001\u54e5\u6211\u4e00\u70b9\u5fc3\u610f\uff0c\u5c71\u4e0d\u8f6c\u8def\u8f6c\uff0c\u65e5\u540e\uff0c\u54b1\u4eec\u53ef\u80fd\u8fd8\u6709\u89c1\u9762\u7684\u673a\u4f1a\uff0c\u4eca\u5929\u7684\u4e8b\uff0c\u8981\u4e25\u5b88\u79d8\u5bc6\uff0c\u4f20\u51fa\u53bb\uff0c\u5bf9\u4f60\u6709\u5bb3\u65e0\u76ca\uff0c\u5f04\u4e0d\u597d\u6789\u9001\u4e00\u6761\u6027\u547d\uff0c\u8fd8\u6b7b\u7684\u83ab\u540d\u5176\u5999\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u61c2\u4e86\uff0c\u4f60\u9f50\u5144\uff0c\u4e0d\u80af\u6740\u6211\u706d\u53e3\uff0c\u8db3\u89c1\u9ad8\u4e49\uff0c\u94f6\u7968\u6211\u6536\u4e0b\uff0c\u4eca\u665a\u4e0a\uff0c\u6211\u5c31\u8d70\u4eba\uff0c\u8eb2\u5b83\u4e09\u4e2a\u6708\u518d\u56de\u6765\u2026\u2026\u201d\u4e01\u5f6a\u9053\uff1a\u201c\u4e0b\u6b21\u518d\u5230\u5bf9\u4eac\uff0c\u8bb0\u7740\u627e\u6211\u3002\u201d \u3000\u3000\u8f6c\u8eab\u8fc8\u6b65\uff0c\u53bb\u52bf\u5982\u98de\uff0c\u5f88\u5feb\u7684\u6d88\u5931\u5728\u591c\u8272\u4e2d\u3002 \u3000\u3000\u5927\u548c\u5c1a\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8d70\u81ea\u4eec\u95ef\u8fdb\u53bb\u95ee\u4e2a\u660e\u767d\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u56db\u987e\u4e00\u773c\uff0c\u63a5\u9053\uff1a\u201c\u8d81\u73b0\u5728\u56db\u4e0b\u65e0\u4eba\u3002\u201d \u3000\u3000\u8bf4\u662f\u767b\u95e8\u6c42\u6559\uff0c\u5176\u5b9e\u662f\u8d8a\u5899\u800c\u5165\uff0c\u597d\u5728\u8fd9\u4e2a\u5730\u65b9\u4e09\u9762\u6c34\u5858\uff0c\u9636\u65e0\u90bb\u820d\uff0c\u662f\u6c74\u4eac\u57ce\u4e2d\u6700\u8352\u51c9\u7684\u4e00\u4e2a\u5730\u65b9\u3002 \u3000\u3000\u201c\u8bf8\u4f4d\u591c\u95f4\u6c11\u5b85\uff0c\u975e\u597d\u5373\u76d7\uff0c\u8bf4\u4e0d\u51fa\u539f\u56e0\uff1f\u53ef\u522b\u602a\u6211\u65bd\u6bd2\u4f24\u4eba\u3002\u201d\u5b85\u5185\u4eba\u76f8\u5f53\u8b66\u89c9\uff1b\u56db\u4e2a\u4eba\u843d\u5165\u9662\u4e2d\uff0c\u5c4b\u5185\u5df2\u4f20\u8bdd\u51fa\u6765\u3002 \u3000\u3000\u201c\u6211\u4eec\u6765\u81ea\u5c11\u6797\u5bfa\uff0c\u627e\u4e00\u4f4d\u72d7\u76ae\u846b\u82a6\u3002\u201d\u5927\u548c\u5c1a\u9053\uff1a\u201c\u9601\u4e0b\u662f\u8fce\u5ba2\u5165\u5185\uff0c\u8fd8\u662f\u6211\u4eec\u81ea\u5df1\u8fdb\u53bb\u9762\u8c12\uff1f\u201d \u3000\u3000\u8bdd\u8bf4\u7684\u867d\u7136\u5ba2\u6c14\uff0c\u5374\u662f\u6446\u660e\u4e86\u975e\u8981\u89c1\u4eba\u4e0d\u53ef\u3002 \u3000\u3000\u5ba4\u4e2d\u4eae\u8d77\u706f\u706b\uff0c\u5385\u95e8\u5440\u7136\u800c\u5f00\uff0c\u4e00\u4e2a\u624b\u6267\u62d0\u6756\u7684\u9ed1\u8863\u4eba\u5f53\u95e8\u800c\u7acb\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u5c11\u6797\u5bfa\u5168\u662f\u548c\u5c1a\uff0c\u56db\u4f4d\u5374\u662f\u4fd7\u4eba\uff0c\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u4eec\u5e26\u6709\u8bc1\u7269\uff0c\u5927\u592b\u8bf7\u67e5\u9a8c\u660e\u767d\u518d\u8bf4\u3002\u201d \u3000\u3000\u53d6\u51fa\u767e\u660e\u65b9\u4e08\u4ea4\u7ed9\u7684\u4e00\u7247\u9ec4\u8272\u5e03\u5757\uff0c\u9012\u4e86\u8fc7\u53bb\uff0c\u4e8b\u5b9e\u4e0a\uff0c\u6c5f\u67ab\u4e5f\u4e0d\u660e\u767d\uff0c\u4e00\u7247\u9ec4\u5e03\uff0c\u4ee3\u8868\u7740\u4ec0\u4e48\uff1f \u3000\u3000\u4f46\u9ed1\u8863\u4eba\u5374\u5341\u5206\u91cd\u89c6\uff0c\u5f03\u53bb\u62d0\u6756\uff0c\u53cc\u624b\u63a5\u8fc7\uff0c\u9053\uff1a\u201c\u8bf7\u5728\u5385\u5916\u7a0d\u5019\uff0c\u7530\u67d0\u9a8c\u8bc1\u4e4b\u540e\uff0c\u518d\u6765\u63a5\u5f85\u3002\u201d\u7adf\u7136\u63a9\u4e0a\u5385\u95e8\u3002 \u3000\u3000\u6c5f\u67ab\u6ca1\u963b\u6b62\uff0c\u5374\u793a\u610f\u96ea\u3001\u4e8c\u5973\u98de\u4e0a\u5c4b\u9762\u76d1\u89c6\uff0c\u4ee5\u9632\u7530\u5927\u592b\u7531\u540e\u7a97\u79bb\u53bb\u3002 \u3000\u3000\u7b49\u4e86\u8db3\u8db3\u6709\u4e00\u76cf\u70ed\u8336\u5de5\u592b\uff0c\u9ed1\u8863\u4eba\u518d\u5ea6\u6253\u5f00\u5385\u95e8\uff0c\u62b1\u62f3\u8fce\u5ba2\u4eba\u5ba4\u3002 \u3000\u3000\u96ea\u3001\u971c\u4e8c\u5973\u8eab\u6cd5\u7075\u5de7\uff0c\u65e0\u58f0\u65e0\u606f\u7684\u98d8\u843d\u5b9e\u5730\uff0c\u7530\u5927\u592b\u4f3c\u672a\u53d1\u89c9\u3002 \u3000\u3000\u4e5f\u8bb8\u4ed6\u5df2\u7ecf\u77e5\u9053\u4e86\uff0c\u6545\u4f5c\u4e0d\u77e5\u3002 \u3000\u3000\u201c\u6211\u53eb\u7530\u5e73\uff0c\u4e5f\u5c31\u662f\u4f60\u4eec\u8981\u627e\u7684\u72d7\u76ae\u846b\u82a6\uff0c\u6211\u4eec\u4e09\u4ee3\u76f8\u4f20\uff0c\u606d\u5019\u4e8e\u6b64\uff0c\u4eca\u65e5\u603b\u7b97\u6709\u4e86\u7528\u5904\uff0c\u5c11\u6797\u5bfa\u767e\u5e74\u4e4b\u524d\uff0c\u4f20\u4e86\u6211\u7956\u7236\u6b66\u529f\u3001\u79d8\u65b9\uff0c\u6211\u4eec\u4ed7\u4ee5\u7d2f\u79ef\u4e86\u4e07\u9877\u5bb6\u4ea7\uff0c\u4f46\u4e5f\u82e6\u82e6\u7b49\u4e86\u4e09\u4ee3\u3002\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u63a5\u9053\uff1a\u201c\u5927\u592b\u7684\u5bb6\u4eba\u4e5f\u4f4f\u5728\u8fd9\u91cc\u5417\uff1f\u201d \u3000\u3000\u4ed6\u5185\u529f\u7cbe\u6e5b\uff0c\u8033\u76ee\u7075\u654f\uff0c\u51dd\u795e\u503e\u542c\uff0c\u4e0d\u95fb\u4ed6\u4eba\u58f0\u606f\uff0c\u4f3c\u4e4e\u662f\u8fd9\u5ea7\u5b85\u9662\u4e2d\uff0c\u53ea\u4f4f\u7530\u5e73\u4e00\u4eba\u3002 \u3000\u3000\u201c\u4e0d\u6562\u76f8\u7792\uff0c\u8fd9\u91cc\u53ea\u67d0\u4e00\u4eba\uff0c\u5144\u5f1f\u5bb6\u4eba\uff0c\u65e9\u5df2\u8fc1\u79fb\u4ed6\u5904\uff0c\u8fd9\u6869\u79d8\u5bc6\uff0c\u53ea\u4f20\u957f\u5b50\uff0c\u5c11\u6797\u5bfa\u4f20\u6765\u7684\u6b66\u529f\uff0c\u4e5f\u53ea\u4f20\u957f\u5b50\u4e00\u4eba\u3002 \u3000\u3000\u7530\u5bb6\u4eba\u76ee\u524d\u53ea\u6709\u7530\u5e73\u4e00\u4eba\u4f1a\u6b66\uff0c\u8bf8\u4f4d\u518d\u4e0d\u6765\uff0c\u6211\u5c31\u8981\u5f00\u59cb\u57f9\u517b\u4e0b\u4e00\u4ee3\u7684\u63a5\u73ed\u4eba\u4e86\uff0c\u6211\u4eca\u5e74\u56db\u5341\u5c81\uff0c\u4e94\u5341\u4e94\u5c81\u5c31\u8981\u4ea4\u7ed9\u4e0b\u4e00\u4ee3\u63a5\u638c\u79d8\u5bc6\u3002 \u3000\u3000\u8001\u5b9e\u8bf4\uff0c\u8fd9\u79cd\u65e5\u4e8e\u4e0d\u597d\u8fc7\uff0c\u867d\u7136\u94dc\u8863\u7389\u98df\uff0c\u4f46\u5374\u6570\u5341\u5e74\u4e0d\u80fd\u79bb\u5f00\u6b64\u5730\uff0c\u5e73\u5e38\u4e5f\u53ea\u6709\u4e00\u4e2a\u8001\u4ec6\u4eba\u4f8d\u5019\u6211\u7684\u8d77\u5c45\u3002 \u3000\u3000\u4ed6\u6bcf\u5929\u5de5\u4f5c\u5b8c\uff0c\u90fd\u53ef\u4ee5\u548c\u5bb6\u4eba\u6b22\u805a\uff0c\u6211\u5374\u4e0d\u80fd\u79bb\u6b64\u4e00\u65e5\uff0c\u751f\u6d3b\u7684\u5f88\u5bc2\u5bde\uff0c\u6211\u80fd\u7b49\u5230\u62a5\u6548\u5c11\u6797\u7684\u673a\u4f1a\uff0c\u4e5f\u7b97\u6709\u6240\u4ea4\u5f85\uff0c\u9a8c\u8bc1\u65e0\u8bef\uff0c\u8bdd\u4e5f\u8bf4\u660e\uff0c\u8bf8\u4f4d\u8bf7\u8bf4\u660e\u6765\u610f\u5427\uff01\u201d \u3000\u3000\u201c\u6211\u4eec\u8981\u5c11\u6797\u7684\u7edd\u6280\u2026\u2026\u201d\u5927\u548c\u5c1a\u9053\uff1a\u201c\u5982\u4f55\u80fd\u591f\u53d6\u5f97\uff1f\u201d \u3000\u3000\u201c\u5c11\u6797\u7edd\u6280\uff1f\u201d\u7530\u5e73\u6014\u4e86\u4e00\u6014\uff0c\u7531\u6000\u4e2d\u53d6\u51fa\u4e24\u7247\u65e7\u7684\u9ec4\u8272\u5e03\u7247\uff0c\u4e00\u7247\u5927\u7684\u6709\u4e24\u5c3a\u89c1\u65b9\uff0c\u4e00\u7247\u5c0f\u7684\u6b63\u662f\u6c5f\u67ab\u5e26\u6765\u4e4b\u7269\u3002 \u3000\u3000\u5927\u7247\u7684\u4e2d\u6709\u4e00\u6d1e\uff0c\u5c0f\u7247\u7684\u521a\u597d\u8865\u4e0a\uff0c\u7530\u5e73\u5728\u706f\u4e0b\u5f88\u4ed4\u7ec6\u7684\u77a7\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u5e03\u7f1d\u5bc6\u5408\u540c\u662f\u767e\u5e74\u4ee5\u4e0a\u7684\u65e7\u5e03\uff0c\u5e94\u8be5\u4e0d\u4f1a\u9519\u5440\uff1f\u53ef\u662f\u4f60\u4eec\u8981\u7684\u4e1c\u897f\u4e0d\u5bf9\uff01\u201d \u3000\u3000\u201c\u4f60\u4fdd\u7ba1\u7684\u662f\u4e00\u6837\u4e1c\u897f\uff1f\u2026\u2026\u201d\u5927\u548c\u5c1a\u9053\uff1a\u201c\u5c11\u6797\u5bfa\u5b58\u653e\u4e8e\u6b64\uff0c\u90a3\u5c31\u4e0d\u4f1a\u9519\u4e86\uff0c\u6b64\u4e8b\u4f20\u627f\u767e\u4f59\u5e74\uff0c\u8a00\u8bcd\u4e4b\u95f4\uff0c\u672a\u5fc5\u5b8c\u5168\u543b\u5408\u3002\u201d \u3000\u3000\u201c\u8bf4\u7684\u6709\u7406\uff0c\u8bf8\u4f4d\u8ddf\u6211\u6765\u2026\u2026\u201d\u7530\u5e73\u6536\u8d77\u5e03\u7247\uff0c\u884c\u5165\u5367\u5ba4\uff0c\u201c\u6211\u4ece\u672a\u6253\u5f00\u770b\u8fc7\uff0c\u73b0\u5728\u4ea4\u7ed9\u8bf8\u4f4d\u4e86\u3002\u201d \u3000\u3000\u957f\u957f\u5401\u4e00\u95e8\u6c14\uff0c\u6709\u5982\u5378\u4e0b\u4e86\u5343\u65a4\u91cd\u62c5\u3002 \u3000\u3000\u6c5f\u67ab\u63a5\u8fc7\u94c1\u7bb1\uff0c\u6325\u638c\u5288\u843d\u94c1\u9501\uff0c\u6253\u5f00\u4e00\u770b\uff0c\u7bb1\u4e2d\u653e\u7740\u4e00\u5757\u7f8a\u8102\u767d\u7389\uff0c\u4e09\u5bf8\u89c1\u65b9\uff0c\u4e00\u9762\u5149\u6ed1\uff0c\u4e00\u9762\u5374\u523b\u5165\u516b\u4e2a\u5b57\u201c\u8eab\u540e\u65e0\u4f59\u4e8b\uff0c\u7891\u4e0b\u571f\u4e09\u5c3a\u201d\uff0c\u6c5f\u67ab\u5ff5\u4e86\u4e24\u904d\uff0c\u6447\u6447\u5934\u9053\uff1a\u201c\u770b\u4e0d\u61c2\uff0c\u770b\u4e0d\u61c2\u2026\u2026\u201d \u3000\u3000\u5927\u548c\u5c1a\u4e5f\u770b\u4e0d\u61c2\uff0c\u4f46\u7530\u5e73\u5374\u542c\u5f97\u8138\u4e0a\u4f3c\u9752\uff0c\u9053\uff1a\u201c\u8eab\u540e\u65e0\u4f59\u4e8b\uff0c\u7891\u4e0b\u571f\u4e09\u5c3a\uff0c\u6211\u61c2\u4e86\uff0c\u6211\u61c2\u4e86\u3002\u201d \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u7530\u5927\u592b\u679c\u662f\u9ad8\u4eba\uff0c\u8fd8\u8bf7\u6307\u70b9\u6307\u70b9\u3002\u201d \u3000\u3000\u201c\u8fd9\u5757\u7389\u662f\u6211\u5bb6\u4f20\u4e4b\u7269\uff0c\u52ff\u602a\u6211\u770b\u8d77\u6765\u6709\u4e9b\u773c\u719f\uff0c\u2026\u2026\u201d\u7530\u5927\u592b\u53f9\u53e3\u6c14\u63a5\u9053\uff1a\u201c\u4e24\u4f4d\u4e0d\u8981\u8bef\u4f1a\uff0c\u8fd9\u5757\u7389\u539f\u957f\u516d\u5bf8\uff0c\u4e2d\u5206\u4e3a\u4e8c\uff0c\u4e00\u534a\u7559\u4f5c\u6211\u4eec\u4f20\u5bb6\u4e4b\u5b9d\uff0c\u4e00\u534a\u5374\u85cf\u5728\u8fd9\u94c1\u7bb1\u4e4b\u4e2d\u3002 \u3000\u3000\u8001\u5b9e\u8bf4\u8fd9\u5757\u767d\u7389\u5e02\u503c\u4e0d\u8fc7\u6570\u5341\u4e24\u94f6\u5b50\uff0c\u5148\u7956\u6307\u5b9a\u4f5c\u4f20\u4e4b\u5b9d\uff0c\u7adf\u662f\u53e6\u6709\u7528\u5fc3\uff0c\u5927\u6982\u662f\u6015\u6211\u4eec\u4fdd\u4e0d\u4f4f\u673a\u5bc6\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u4f4e\u58f0\u9053\uff1a\u201c\u6709\u4eba\u6765\u4e86\u3002\u201d \u3000\u3000\u547c\u4e00\u58f0\uff0c\u547c\u7184\u706b\u70db\u3002 \u3000\u3000\u201c\u4ee4\u7956\u7684\u5893\u5730\u5728\u2026\u2026\u201d \u3000\u3000\u5927\u548c\u5c1a\u51e0\u4e4e\u9644\u5728\u7530\u5e73\u7684\u8033\u8fb9\u5904\u8bf4\u3002 \u3000\u3000\u201c\u57ce\u4e1c\u9f99\u738b\u5e99\uff0c\u5bb6\u7956\u7684\u5893\u7891\u540e\u9762\u523b\u4e86\u4e00\u4e2a\u9152\u846b\u82a6\u2026\u2026\u201d \u3000\u3000\u201c\u8c22\u5566\uff01\u8eb2\u8d77\u6765\uff0c\u4e0d\u5173\u4f60\u7684\u4e8b\u4e86\u2026\u2026\u201d\u5927\u548c\u5c1a\u9053\uff1a\u201c\u6bc1\u6389\u7389\u724c\u3002\u201d \u3000\u3000\u6c5f\u67ab\u634f\u788e\u7389\u724c\uff0c\u63e1\u5728\u624b\u4e2d\u3002 \u3000\u3000\u96ea\u3001\u971c\u4e8c\u5973\u5df2\u6084\u7136\u6f5c\u51fa\u5367\u5ba4\uff0c\u9690\u4e8e\u5385\u4e2d\u3002 \u3000\u3000\u6c5f\u67ab\u6b65\u51fa\u5385\u5916\u3002 \u3000\u3000\u591c\u8272\u4e2d\u53ea\u89c1\u4e94\u4e2a\u9ed1\u8863\u4eba\uff0c\u65e0\u58f0\u65e0\u606f\u7684\u7ad9\u5728\u9662\u4e2d\u3002 \u3000\u3000\u4e0d\u7981\u4e00\u6014\uff0c\u5fd6\u9053\uff1a\u201c\u6765\u7684\u597d\u5feb\u3002\u201d \u3000\u3000\u5c45\u4e2d\u7684\u9ed1\u8863\u4eba\u4e00\u6325\u624b\uff0c\u4e24\u4fa7\u56db\u4e2a\u9ed1\u8863\u4eba\uff0c\u56db\u628a\u5200\uff0c\u98de\u6251\u6c5f\u67ab\u3002 \u3000\u3000\u6c5f\u67ab\u6740\u673a\u65e9\u52a8\uff0c\u4e00\u626c\u624b\uff0c\u4e00\u628a\u788e\u7389\u98de\u6d12\u800c\u5c71\uff0c\u56db\u4e2a\u9ed1\u8863\u4eba\u5168\u88ab\u51fb\u4e2d\uff0c\u653b\u52bf\u4e00\u505c\uff0c\u6c5f\u67ab\u7684\u6307\u98ce\u5df2\u5230\uff0c\u53d6\u547d\u593a\u9b42\uff0c\u56db\u4e2a\u4eba\u4e00\u9f50\u5012\u6454\u4e0b\u53bb\u3002 \u3000\u3000\u201c\u5929\u72d0\u6307\u2026\u2026\u201d\u5c45\u4e2d\u9ed1\u8863\u4eba\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u8001\u5996\u72d0\u8fd8\u672a\u6b7b\u5417\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u7684\u7b54\u590d\u662f\u8fce\u9762\u4e00\u638c\u3002 \u3000\u3000\u4e00\u80a1\u6697\u52b2\u6c79\u6d8c\u800c\u53bb\uff0c\u8868\u9762\u4e0a\u5374\u53c8\u65e0\u58f0\u65e0\u606f\uff0c\u6c5f\u67ab\u8fd9\u4e00\u638c\uff0c\u8fd0\u8d77\u4e86\u516d\u6210\u529f\u529b\uff0c\u5b58\u5fc3\u628a\u5bf9\u65b9\u6bd9\u4e8e\u638c\u4e0b\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u51b7\u7b11\u4e00\u58f0\uff0c\u4e00\u626c\u624b\uff0c\u8fce\u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u6c5f\u67ab\u6697\u9053\uff1a\u201c\u627e\u6b7b\uff01\u201d\u4e00\u63d0\u771f\u6c14\uff0c\u53c8\u52a0\u4e24\u6210\u5185\u529b\u3002 \u3000\u3000\u53cc\u65b9\u7684\u638c\u52bf\uff0c\u76f8\u8ddd\u8fd8\u6709\u4e94\u5c3a\uff0c\u4f46\u5185\u52b2\u5df2\u5148\u89e6\u63a5\u3002 \u3000\u3000\u7a81\u7136\u95f4\uff0c\u72c2\u98ce\u5377\u8d77\uff0c\u6c99\u571f\u6a2a\u98de\uff0c\u6709\u5982\u75be\u98ce\u63a0\u5730\uff0c\u597d\u5f3a\u731b\u7684\u4e24\u80a1\u638c\u529b\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u88ab\u9707\u9000\u4e86\u4e00\u6b65\uff0c\u6c5f\u67ab\u4f3c\u662f\u7a0d\u7a0d\u80dc\u4e86\u4e00\u7b79\uff0c\u4f46\u6c5f\u67ab\u5fc3\u4e2d\u7684\u9707\u60ca\uff0c\u5374\u662f\u60f6\u60f6\u4e0d\u5df2\u3002 \u3000\u3000\u81ea\u5165\u6c5f\u6e56\uff0c\u548c\u4eba\u640f\u6740\uff0c\u6c5f\u67ab\u7b2c\u4e00\u6b21\u7528\u51fa\u5982\u6b64\u5f3a\u5927\u7684\u5185\u529b\uff0c\u5374\u4e0d\u6599\u5230\u7adf\u4e5f\u9047\u4e0a\u4e86\u5982\u6b64\u5f3a\u608d\u7684\u654c\u4eba\u3002 \u3000\u3000\u201c\u8001\u548c\u5c1a\u65e0\u76f8\u795e\u529f\u4e5f\u4f20\u7ed9\u4e86\u4f60\u4e86\u2026\u2026\u201d\u9ed1\u8863\u4eba\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u91d1\u4e39\u4e66\u751f\u7684\u5927\u7f57\u638c\uff0c\u548c\u2018\u7a7f\u5589\u4e00\u5251\u2019\u7edd\u6280\uff0c\u60f3\u5fc5\u4e5f\u4f20\u6388\u7ed9\u4f60\u3002\u65bd\u5c55\u51fa\u6765\uff0c\u8ba9\u5728\u4e0b\u89c1\u8bc6\uff0c\u89c1\u8bc6\u5427\uff01\u201d \u3000\u3000\u6c5f\u67ab\u6ca1\u6709\u8bf4\u8bdd\uff0c\u4ed6\u5fc3\u4e2d\u9707\u9a87\u5df2\u6781\uff0c\u53ea\u7ba1\u5168\u529b\u65bd\u5c55\uff0c\u5c3d\u51fa\u5341\u6210\u529f\u529b\u3002 \u3000\u3000\u6b66\u6797\u4e09\u5723\u5168\u529b\u57f9\u517b\u7684\u5f1f\u5b50\uff0c\u679c\u975e\u5c0f\u53ef\uff0c\u90a3\u9ed1\u8863\u4eba\u867d\u7136\u529f\u529b\u6df1\u539a\uff0c\u6280\u827a\u7cbe\u6e5b\uff0c\u4f46\u5728\u6c5f\u67ab\u5168\u529b\u65bd\u4e3a\u4e4b\u4e2d\uff0c\u767e\u62db\u4e4b\u540e\uff0c\u4ecd\u88ab\u70b9\u4e2d\u4e00\u6307\uff0c\u9ed1\u8863\u4eba\u5df2\u7ecf\u987e\u4e0d\u5f97\u9662\u4e2d\u9057\u5c38\uff0c\u8f6c\u8eab\u4e00\u8dc3\uff0c\u98de\u9a70\u800c\u53bb\u3002 \u3000\u3000\u6c5f\u67ab\u6ca1\u6709\u8ffd\u8d76\uff0c\u5446\u5446\u7684\u7ad9\u5728\u5ead\u9662\u4e2d\uff0c\u671b\u7740\u9ed1\u8863\u4eba\u7684\u53bb\u5411\u51fa\u795e\u3002 \u3000\u3000\u201c\u597d\u5389\u5bb3\u7684\u4e00\u573a\u640f\u6740\u2026\u2026\u201d\u5927\u548c\u5c1a\u8bf4\uff0c\u201c\u6614\u5e74\u4e5d\u534e\u5c71\u4e09\u5723\u6280\u4f0f\u7fa4\u9b54\uff0c\u53ae\u6740\u6570\u4e00\u9635\uff0c\u4e5f\u672a\u6709\u4eca\u591c\u4e00\u6218\u6fc0\u70c8\uff0c\u62db\u62db\u7edd\u5b66\uff0c\u6c5f\u6e56\u9ad8\u624b\uff0c\u5f88\u96be\u6709\u4eba\u80fd\u63a5\u4e0b\u5341\u62db\uff0c\u548c\u5c1a\u5fc3\u4e2d\u4f30\u7b97\uff0c\u4e09\u5341\u62db\u5185\u6211\u5c06\u4f24\u5728\u6c5f\u5144\u5f1f\u7684\u624b\u4e2d\u3002\u201d \u3000\u3000\u201c\u4f46\u4ed6\u63a5\u6211\u767e\u62db\uff0c\u8fd8\u6709\u518d\u6218\u4e4b\u80fd\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u9eef\u7136\u8bf4\u9053\uff1b\u201c\u4ed6\u8eab\u4f53\u575a\u5982\u94c1\u77f3\uff0c\u6211\u90a3\u4e00\u6307\uff0c\u53ea\u80fd\u591f\u4f7f\u4ed6\u53d7\u5230\u60ca\u5413\uff0c\u771f\u8981\u5206\u51fa\u80dc\u8d1f\uff0c\u6050\u8fd8\u8981\u767e\u62db\u4ee5\u4e0a\u3002\u201d \u3000\u3000\u5927\u548c\u5c1a\u4f4e\u58f0\u9053\uff1a\u201c\u4e24\u767e\u62db\u53ef\u4ee5\u51fb\u8d25\u4ed6\u5417\uff1f\u201d \u3000\u3000\u201c\u5e94\u8be5\u53ef\u4ee5\uff0c\u4e0d\u8fc7\uff0c\u90a3\u65f6\uff0c\u6211\u4e5f\u5c06\u6c14\u529b\u8017\u5c3d\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u53ea\u6015\u8fde\u4f60\u7684\u5341\u62db\u4e5f\u63a5\u4e0d\u4e0b\u4e86\u3002\u201d \u3000\u3000\u5927\u548c\u5c1a\u5fc3\u4e2d\u5fd6\u9053\uff1a\u201c\u539f\u6765\uff0c\u4ed6\u5fc3\u4e2d\u65e9\u5df2\u4e86\u7136\u6211\u6b66\u529f\u6210\u5c31\uff0c\u770b\u6765\uff0c\u548c\u5c1a\u662f\u771f\u7684\u5dee\u4ed6\u4e00\u5927\u622a\u4e86\uff0c\u4f46\u4e0d\u77e5\u6b27\u9633\u662d\u592b\u5987\u7684\u6210\u5c31\uff0c\u5728\u4ed6\u5fc3\u4e2d\u5b9a\u4f4d\u5982\u4f55\uff1f\u6b27\u9633\u5144\u592b\u5987\u8054\u624b\u80fd\u548c\u90a3\u9ed1\u8863\u4eba\u5206\u51fa\u80dc\u8d25\u5417\uff1f\u201d \u3000\u3000\u201c\u5cb3\u7236\u3001\u6bcd\uff0c\u5728\u5251\u672f\u4e0a\u7684\u6210\u5c31\uff0c\u975e\u6211\u80fd\u53ca\uff0c\u4f46\u6b66\u529f\u5230\u4e00\u5b9a\u7684\u5883\u754c\u4e4b\u540e\uff0c\u51b3\u6b7b\u4e4b\u6218\u5c31\u8981\u529f\u529b\u6df1\u6d45\uff0c\u62db\u672f\u53d8\u5316\uff0c\u7efc\u5408\u8ba1\u7b97\u4e86\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u4ed6\u4eec\u624b\u4e2d\u6709\u5251\uff0c\u8054\u624b\u5bf9\u654c\uff0c\u53ef\u62da\u4e24\u767e\u62db\uff0c\u2026\u2026\u201d \u3000\u3000\u6ca1\u6709\u8bf4\u51fa\u80dc\u8d1f\uff0c\u957f\u53f9\u4e00\u58f0\uff0c\u4f4f\u53e3\u4e0d\u8a00\u3002 \u3000\u3000\u8fd9\u80dc\u8d1f\u4e5f\u5c31\u4e0d\u8a00\u53ef\u55bb\u4e86\u3002 \u3000\u3000\u201c\u4e94\u73af\u9f50\u51fa\uff0c\u7ed5\u98de\u76f8\u52a9\uff0c\u548c\u5c1a\u53ef\u4ee5\u548c\u4ed6\u62fc\u4e0a\u4e00\u767e\u4e94\u5341\u62db\uff0c\u53ea\u6b64\u4e00\u4eba\uff0c\u5e76\u4e0d\u53ef\u6015\uff0c\u4f46\u4ed6\u4eec\u2026\u2026\u201d \u3000\u3000\u201c\u6700\u5c11\u6709\u516d\u4e2a\u4eba\u2026\u2026\u201d\u6c5f\u67ab\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u4e5f\u8bb8\u66f4\u591a\uff0c\u6211\u4eec\u96c6\u4e2d\u4e86\u4e00\u8d77\uff0c\u6700\u591a\u80fd\u5bf9\u4ed8\u4e09\u4e2a\uff0c\u4ed6\u4eec\u51fa\u52a8\u56db\u4e2a\u8fd9\u6837\u9ad8\u624b\uff0c\u6211\u4eec\u6216\u53ef\u52c9\u5f3a\u5e94\u4ed8\uff0c\u518d\u591a\u4e00\u4eba\uff0c\u6211\u4eec\u5c31\u65e0\u6cd5\u5e94\u4ed8\u4e86\u2026\u2026\u201d \u3000\u3000\u5927\u548c\u5c1a\u5fc3\u4e2d\u5fd6\u601d\uff0c\u4f60\u53ef\u80dc\u4e00\u4eba\uff0c\u6211\u548c\u5c1a\u53ef\u6218\u767e\u62db\u4ee5\u4e0a\uff0c\u6b27\u9633\u592b\u5987\u8054\u624b\uff0c\u53ef\u62b5\u4e00\u4eba\uff0c\u8fd8\u6709\u4e00\u4eba\u8c01\u80fd\u62b5\u5fa1\uff1f \u3000\u3000\u4e16\u4e0a\u4e5f\u8bb8\u6ca1\u6709\u771f\u80fd\u529b\u654c\u4e07\u592b\u7684\u52c7\u58eb\uff0c\u4f46\u6b64\u7b49\u9ad8\u624b\uff0c\u817e\u63a0\u5982\u98de\uff0c\u6765\u53bb\u4f3c\u98ce\uff0c\u867d\u5343\u519b\u4e07\u9a6c\uff0c\u4e5f\u56f0\u4ed6\u4e0d\u4f4f\uff0c\u6709\u8c01\u53ef\u518d\u76f8\u5dee\u4e00\u4eba\u5462\uff1f \u3000\u3000\u6c5f\u67ab\u4f3c\u5df2\u77a7\u51fa\u4e03\u5b9d\u548c\u5c1a\u5fc3\u4e2d\u4e4b\u7591\uff0c\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u5f20\u56db\u59d1\u548c\u96ea\u3001\u971c\u4e8c\u5973\u8054\u624b\uff0c\u53ef\u62b5\u4e00\u4eba\uff0c\u4ee5\u56db\u59d1\u7684\u6b66\u529f\uff0c\u81ea\u7136\u65e0\u6cd5\u529b\u654c\uff0c\u4f46\u5979\u6280\u827a\u535a\u6742\uff0c\u4e94\u884c\u9041\u672f\uff0c\u80fd\u501f\u7528\u767e\u7269\uff0c\u9690\u73b0\u65e0\u5e38\uff0c\u96ea\u3001\u971c\u7684\u6280\u827a\uff0c\u5b9e\u5df2\u5f97\u5929\u72d0\u771f\u4f20\uff0c\u5982\u8bba\u767e\u62db\u5185\u6709\u53d6\u80dc\u654c\u4eba\u7684\u673a\u4f1a\uff0c\u5979\u4eec\u7684\u7ec4\u5408\u662f\u552f\u4e00\u7684\u53ef\u80fd\u4e86\u3002\u201d \u3000\u3000\u201c\u627f\u6559\u4e86\uff0c\u548c\u5c1a\u7adf\u672a\u601d\u8651\u53ca\u6b64\u3002\u201d \u3000\u3000\u541f\u96ea\u3001\u541f\u971c\uff0c\u9690\u8eab\u5728\u5385\u4e2d\uff0c\u5e76\u672a\u73b0\u8eab\uff0c\u4e24\u4eba\u4f38\u624b\u76f8\u63e1\uff0c\u7075\u7280\u4e92\u901a\uff0c\u5979\u4eec\u611f\u53d7\u5230\u4e86\u6c5f\u67ab\u7684\u60f6\u7136\u75db\u82e6\u3002 \u3000\u3000\u4e5f\u542c\u5230\u6c5f\u67ab\u90a3\u53e5\u5cb3\u7236\u3001\u5cb3\u6bcd\u7684\u8bed\u58f0\uff0c\u6c5f\u5927\u54e5\u5fc3\u4e2d\u5df2\u627f\u8ba4\u4e86\u5979\u4eec\u59bb\u5b50\u7684\u8eab\u4efd\uff0c\u4e5f\u627f\u8ba4\u4e86\u5979\u4eec\u7684\u6b66\u827a\uff0c\u5341\u51e0\u5e74\u7684\u82e6\u529f\uff0c\u679c\u672a\u767d\u8d39\uff0c\u5df2\u6709\u80fd\u76f8\u52a9\u592b\u5a7f\u3002 \u3000\u3000\u201c\u4f46\u613f\u5c11\u6797\u7684\u5b66\uff0c\u771f\u6709\u8d77\u6b7b\u56de\u751f\u4e4b\u529b\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u4eca\u591c\u4e00\u6218\uff0c\u6211\u5df2\u5168\u529b\u65bd\u4e3a\uff0c\u767e\u660e\u8001\u65b9\u4e08\u8bf4\u7684\u4e0d\u9519\uff0c\u4ed6\u4eec\u51ed\u85c9\u7684\u662f\u771f\u5b9e\u6b66\u529f\uff0c\u9664\u975e\u4e09\u4f4d\u5e08\u957f\u590d\u751f\uff0c\u6211\u5df2\u56de\u5929\u65e0\u672f\uff0c\u6709\u8d1f\u4ed6\u4eec\u7684\u5bc4\u671b\u4e86\u3002\u201d \u3000\u3000\u8bed\u610f\u82cd\u51c9\u3001\u58f0\u97f3\u51c4\u7136\uff0c\u5927\u6709\u82f1\u96c4\u672b\u8def\uff0c\u58ee\u58eb\u65e0\u989c\u7684\u611f\u6168\uff0c\u542c\u5f97\u4e24\u4e2a\u5c0f\u72d0\u5973\u7684\u5fc3\u90fd\u51b7\u4e86\u3002 \u3000\u3000\u201c\u6c5f\u5144\u5f1f\u2026\u2026\u201d\u5927\u548c\u5c1a\u4f4e\u58f0\u529d\u9053\uff1a\u201c\u5c11\u6797\u5bfa\u4e00\u573a\u8840\u6218\uff0c\u7cbe\u9510\u5c3d\u5931\uff0c\u8001\u65b9\u4e08\u73b0\u573a\u76ee\u7779\uff0c\u5982\u662f\u5fc3\u4e2d\u6ca1\u6709\u51e0\u5206\u628a\u63e1\uff0c\u5c82\u4f1a\u8981\u6211\u7b49\u5343\u91cc\u5954\u6ce2\uff0c\u5148\u53bb\u53d6\u5f97\u5c11\u6797\u85cf\u7269\uff0c\u518d\u4f5c\u9053\u7406\uff0c\u7530\u5927\u592b\u5b88\u5019\u4e09\u4ee3\uff0c\u5df2\u7136\u5c3d\u4e86\u5fc3\u610f\uff0c\u8def\u4e0a\u8fd8\u53ef\u80fd\u9047\u4e0a\u622a\u51fb\uff0c\u5c31\u4e0d\u7528\u62d6\u7d2f\u4ed6\u4e86\u3002\u201d \u3000\u3000\u201c\u8bf4\u7684\u6709\u7406\uff0c\u2026\u2026\u201d \u3000\u3000\u96ea\u3001\u971c\u4e8c\u5973\u98d8\u7136\u800c\u51fa\uff0c\u63a5\u9053\uff1a\u201c\u6c5f\u5927\u54e5\uff0c\u6211\u4eec\u76d8\u7b97\u8fc7\u4e86\uff0c\u90a3\u4eba\u4e0d\u518d\u73b0\u8eab\uff0c\u7b97\u662f\u4ed6\u7684\u8fd0\u6c14\uff0c\u518d\u8981\u73b0\u8eab\u62e6\u622a\uff0c\u5c31\u4e0d\u7528\u8bb2\u4ec0\u4e48\u6c5f\u6e56\u9053\u4e49\uff0c\u54b1\u4eec\u8054\u624b\u9664\u4e86\u4ed6\u3002\u201d \u3000\u3000\u201c\u4f60\u4eec\u7740\u5230\u8fc7\u4ed6\u7684\u8eab\u624b\uff1f\u201d\u4e03\u5b9d\u548c\u5c1a\u4f4e\u58f0\u8bf4\u3002 \u3000\u3000\u201c\u548c\u5c1a\u4f2f\u4f2f\u4e0d\u76f8\u4fe1\uff1f\u2026\u2026\u201d\u541f\u96ea\u63a5\u9053\uff1a\u201c\u6c5f\u5927\u54e5\u53ea\u8981\u548c\u4ed6\u62fc\u6597\u5341\u62db\uff0c\u6211\u4eec\u5c31\u6709\u673a\u4f1a\u6740\u4e86\u4ed6\uff0c\u521a\u624d\u6211\u4eec\u4e0d\u6562\u51fa\u624b\uff0c\u662f\u6015\u624b\u6bb5\u4e0d\u592a\u5149\u660e\uff0c\u610f\u5f97\u6c5f\u5927\u54e5\u548c\u4f60\u548c\u5c1a\u4f2f\u4f2f\u751f\u6c14\u3002\u201d \u3000\u3000\u201c\u4e24\u4f4d\u59d1\u5a18\u957f\u5927\u4e86\uff0c\u5c0f\u5634\u5df4\u4e5f\u8d8a\u6765\u8d8a\u751c\uff0c\u6c5f\u5927\u54e5\u540e\u52a0\u4e0a\u4e2a\u548c\u5c1a\u4f2f\u4f2f\uff0c\u5927\u548c\u5c1a\u60f3\u4e0d\u8bf4\u4e5f\u4e0d\u6210\u4e86\uff0c\u4ee5\u4f60\u4eec\u6c5f\u5927\u54e5\u7684\u529f\u529b\uff0c\u70b9\u4e2d\u4ed6\u4e00\u6307\uff0c\u4ecd\u88ab\u4ed6\u9003\u4e86\u3002\u201d \u3000\u3000\u201c\u548c\u5c1a\u4f2f\u4f2f\u8bf4\u7684\u662f\u2026\u2026\u201d\u541f\u96ea\u8bf4\uff1a\u201c\u6240\u4ee5\uff0c\u6211\u4eec\u8bf4\u624b\u6bb5\u4e0d\u592a\u5149\u660e\uff0c\u4e0d\u4f46\u662f\u51fa\u624b\u6697\u7b97\uff0c\u9762\u4e14\uff0c\u7528\u5668\u4e5f\u9510\u5229\u6b79\u6bd2\uff0c\u80fd\u7a7f\u91d1\u77f3\uff0c\u89c1\u8840\u593a\u9b42\uff01\u201d \u3000\u3000\u201c\u6211\u660e\u767d\u4e86\uff0c\u5f20\u56db\u59d1\u80fd\u7b97\u4e0d\u9057\u7b56\uff0c\u7269\u5c3d\u5176\u7528\uff0c\u548c\u5c1a\u4f69\u670d\u6781\u4e86\uff0c\u4ee5\u540e\uff0c\u518d\u9047\u4e0a\u8fd9\u7b49\u5f3a\u654c\u9ad8\u624b\uff0c\u4f60\u4eec\u5c3d\u7ba1\u51fa\u624b\uff0c\u4f60\u4eec\u7684\u6c5f\u5927\u54e5\u5982\u662f\u8d23\u602a\u4f60\u4eec\uff0c\u4f60\u4eec\u52a0\u5341\u500d\u8d23\u602a\u4e8e\u6211\uff01\u201d \u3000\u3000\u201c\u8fd9\u6570\u6708\u4e4b\u4e2d\uff0c\u5f97\u5f20\u59e8\u6559\u5bfc\uff0c\u4f7f\u6211\u4eec\u672c\u8eab\u6280\u827a\uff0c\u6210\u500d\u6570\u589e\u957f\u3002\u201d\u541f\u96ea\u8bf4\uff0c\u201c\u5f20\u59e8\u7684\u4e94\u884c\u9041\u672f\uff0c\u65ad\u80a0\u4e09\u5251\u548c\u5929\u72d0\u6b66\u529f\uff0c\u7adf\u662f\u6697\u76f8\u547c\u5e94\uff0c\u6015\u635f\u4e86\u6c5f\u5927\u54e5\u53ca\u4f60\u548c\u5c1a\u4f2f\u4f2f\u7684\u4fa0\u540d\u3002\u201d \u3000\u3000\u201c\u548c\u5c1a\u4e0d\u662f\u5927\u4fa0\uff0c\u4f60\u4eec\u7684\u6c5f\u5927\u54e5\u662f\u4fa0\u5fc3\u5251\u80c6\uff0c\u6267\u5927\u4e49\u4e0d\u62d8\u5c0f\u8282\uff0c\u4e24\u4f4d\u59d1\u5a18\u5c31\u4e0d\u7528\u987e\u5fcc\u592a\u591a\u4e86\u3002\u201d \u3000\u3000\u770b\u4e86\u6c5f\u67ab\u4e00\u773c\uff0c\u541f\u96ea\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u548c\u5c1a\u4f2f\u4f2f\u6559\u8a00\u5982\u6b64\uff0c\u6211\u548c\u59b9\u59b9\u5c31\u53ef\u4ee5\u653e\u80c6\u51fa\u624b\u4e86\u2026\u2026\u201d \u3000\u3000\u597d\u5389\u5bb3\u7684\u5c0f\u72d0\u5973\uff0c\u5168\u5957\u5728\u6211\u548c\u5c1a\u7684\u5934\u4e0a\uff0c\u5012\u50cf\u662f\u6211\u548c\u5c1a\u6002\u607f\u5979\u4eec\u6697\u65bd\u7b97\u8ba1\u4e86\u3002 \u3000\u3000\u201c\u548c\u5c1a\u4f2f\u4f2f\uff0c\u4f60\u522b\u751f\u6c14\u2026\u2026\u201d\u541f\u971c\u4f3c\u662f\u5df2\u77a7\u51fa\u5927\u548c\u5c1a\u7684\u611f\u6168\uff0c\u63a5\u9053\uff1a\u201c\u8fd9\u51e0\u4e2a\u6708\u4e2d\uff0c\u6211\u4eec\u89c1\u8bc6\u5927\u5f00\uff0c\u80dc\u8fc7\u5c71\u5c45\u4e2d\u5341\u5e74\uff0c\u8fd9\u4eba\u95f4\u7684\u82b1\u82b1\u4e16\u754c\uff0c\u7adf\u662f\u5982\u6b64\u7684\u7eb7\u6270\u590d\u6742\uff0c\u6211\u548c\u59d0\u59d0\u5982\u6709\u5bf8\u8fdb\uff0c\u4e5f\u5168\u662f\u548c\u5411\u4f2f\u4f2f\u548c\u5f20\u59e8\u7684\u6559\u5bfc\u4e4b\u529f\u3002\u201d \u3000\u3000\u5927\u548c\u5c1a\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u8bf4\u7684\u597d\uff0c\u8bf4\u7684\u597d\uff0c\u771f\u662f\u957f\u6c5f\u540e\u6d6a\u63a8\u524d\u6d6a\u554a\uff01\u4e24\u4f4d\u673a\u667a\u5927\u8fdb\uff0c\u6b66\u6797\u91cd\u5149\u6709\u671b\u77e3\uff01\u201d \u3000\u3000\u201c\u56db\u4f4d\u53ef\u4ee5\u8d70\u4e86\u2026\u2026\u201d\u7530\u5927\u592b\u7684\u58f0\u97f3\uff0c\u7531\u5ba4\u4e2d\u4f20\u6765\uff0c\u9053\uff1a \u3000\u3000\u201c\u76ee\u7779\u9002\u624d\u4e00\u6218\uff0c\u7530\u67d0\u5df3\u77e5\u51f6\u9669\uff0c\u90a3\u5730\u65b9\u5728\u57ce\u4e1c\u4e94\u91cc\uff0c\u7530\u67d0\u968f\u884c\u65e0\u76ca\uff0c\u53cd\u6210\u7d2f\u8d58\uff0c\u6055\u6211\u4e0d\u80fd\u5e26\u8def\u4e86\u3002\u201d \u3000\u3000\u201c\u5df2\u4e0e\u5927\u592b\u65e0\u5173\uff0c\u591a\u591a\u73cd\u91cd\uff0c\u548c\u5c1a\u5148\u884c\u5e26\u8def\u2026\u2026\u201d \u3000\u3000\u98de\u8eab\u8dc3\u8d77\uff0c\u6c5f\u67ab\u548c\u96ea\u3001\u971c\u4e8c\u5973\uff0c\u5982\u5f71\u968f\u884c\uff0c\u6d88\u5931\u5728\u591c\u8272\u4e2d\u3002 \u3000 \u7b2c\u4e8c\u5341\u56de\u3000\u5165\u6df1\u5c71\u4e09\u4fa0\u7ec3\u795e\u529f \u3000 \u3000\u3000\u57ce\u4e1c\u9f99\u738b\u5e99\uff0c\u7b51\u5efa\u7684\u9ad8\u5927\u5802\u7687\uff0c\u800c\u4e14\uff0c\u9999\u706b\u9f0e\u76db\uff0c\u867d\u5728\u591c\u95f4\uff0c\u4ea6\u662f\u70db\u706b\u901a\u660e\uff0c\u9999\u5ba2\u672a\u6563\u3002 \u3000\u3000\u6570\u5343\u5e74\u6765\uff0c\u9ec4\u6cb3\u7ecf\u5e38\u6cdb\u6ee5\u6210\u707e\uff0c\u4e24\u5cb8\u5c45\u6c11\u5927\u90fd\u5c3d\u51fa\u6240\u6709\u5174\u5efa\u5e99\u5b87\uff0c\u6c42\u4f51\u4e00\u65b9\u5e73\u5b89\u3002 \u3000\u3000\u8fd9\u9f99\u738b\u5e99\u5728\u9ec4\u6cb3\u4e24\u5cb8\u6700\u4e3a\u4f17\u591a\uff0c\u6709\u4e9b\u89c4\u6a21\u5927\u7684\uff0c\u5e74\u6709\u796d\u7960\uff0c\u957f\u8fbe\u4e00\u4f59\u65e5\u3002 \u3000\u3000\u8fd9\u5ea7\u57ce\u4e1c\u9f99\u738b\u5e99\uff0c\u5c31\u5c5e\u5927\u5e99\u4e4b\u4e00\u3002 \u3000\u3000\u6b64\u5e99\u5174\u5efa\u4e4b\u540e\uff0c\u6c74\u4eac\uff08\u5f00\u5c01\uff09\u8fd1\u90ca\u4e8c\u5341\u4f59\u5e74\u672a\u906d\u6c34\u60a3\uff0c\u867d\u5df2\u4e8c\u66f4\u8fc7\u540e\uff0c\u4ecd\u6709\u9999\u5ba2\u8fd8\u613f\uff0c\u8fd9\u5012\u65b9\u4fbf\u4e86\u6c5f\u67ab\u7b49\u4e0d\u5c11\u3002 \u3000\u3000\u5e99\u4e1c\u571f\u5c97\uff0c\u6709\u4e0d\u5c11\u575f\u5893\u78ca\u8d77\uff0c\u8bf4\u662f\u98ce\u6c34\u597d\uff0c\u5012\u4e0d\u5982\u8bf4\u662f\u5730\u52bf\u7a81\u9ad8\uff0c\u6d2a\u6c34\u4e0d\u6613\u6df9\u6ca1\u3002 \u3000\u3000\u501f\u51e0\u5206\u4e0a\u5f26\u6708\u5149\uff0c\u5927\u548c\u5c1a\u5f88\u5feb\u627e\u5230\u4e86\u5893\u7891\u540e\u6709\u9152\u846b\u82a6\u7684\u6807\u5e1c\uff0c\u575f\u4e0d\u5927\uff0c\u5893\u7891\u6b63\u9762\u53ea\u5199\u7740\u6c74\u4eac\u540d\u533b\u7530\u8001\u4eba\u4e4b\u5893\uff0c\u540d\u5b57\u3001\u7c4d\u8d2f\uff0c\u90fd\u4e0d\u7559\u4e0b\uff0c\u663e\u662f\u65e9\u5df2\u9884\u77e5\u51f6\u9669\u3002 \u3000\u3000\u56db\u4eba\u6697\u8fd0\u5185\u529b\uff0c\u6084\u7136\u6cbf\u5893\u7891\u4e0b\u6316\uff0c\u679c\u7136\u5728\u4e09\u5c3a\u5de6\u53f3\u5904\uff0c\u53d6\u5f97\u4e00\u4e2a\u6728\u76d2\uff0c\u5916\u9762\u5df2\u6709\u8150\u673d\u73b0\u8c61\u3002 \u3000\u3000\u5927\u548c\u5c1a\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6b7b\u540e\u4e0d\u5165\u8001\u575f\uff0c\u7528\u6728\u76d2\u76db\u88c5\u6258\u5b58\u4e4b\u7269\uff1b\u57cb\u5165\u7891\u4e0b\uff0c\u8fd9\u4f4d\u7530\u8001\u5148\u751f\uff0c\u679c\u7136\u662f\u4f4d\u6709\u5fc3\u4eba\u4e86\u3002\u201d \u3000\u3000\u541f\u96ea\u56db\u987e\u65e0\u4eba\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u6b7b\u540e\u4e0d\u5165\u8001\u575f\uff0c\u7891\u4e0a\u4e0d\u7559\u540d\u5b57\uff0c\u662f\u6015\u62d6\u7d2f\u540e\u4eba\uff0c\u4f46\u7528\u6728\u5323\u76db\u88c5\u5c11\u6797\u5947\u5b66\uff0c\u5c31\u6709\u4e9b\u5927\u610f\u4e86\uff0c\u52a0\u662f\u518d\u665a\u4e0a\u51e0\u5e74\uff0c\u6728\u5323\u8150\u673d\u4e86\uff0c\u5323\u4e2d\u5b58\u7269\u3002\u5c82\u4e0d\u4e5f\u8981\u5316\u4f5c\u7070\u6ce5\uff1f\u201d \u3000\u3000\u201c\u8fd9\u5c31\u662f\u7530\u8001\u5148\u751f\u7684\u8fc7\u4eba\u4e4b\u5904\u4e86\u2026\u2026\u201d\u5927\u548c\u5c1a\u9053\uff1a\u201c\u8fd9\u4e2a\u67cf\u6728\u5323\u5b50\uff0c\u53ef\u5728\u571f\u4e2d\u627f\u53d7\u4e24\u767e\u5e74\u7684\u65f6\u95f4\uff0c\u8d85\u8fc7\u6b64\u65f6\uff0c\u6728\u5323\u548c\u5323\u4e2d\u4e4b\u7269\uff0c\u5c3d\u5316\u7070\u70ec\uff0c\u5c31\u7b97\u6709\u4eba\u627e\u5230\u4e86\u8fd9\u91cc\uff0c\u4ec0\u4e48\u4e5f\u89c1\u4e0d\u5230\u4e86\uff0c\u4e24\u767e\u5e74\u8fd8\u7528\u4e0d\u5230\uff0c\u7559\u4e0b\u4f55\u76ca\uff1f\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u6c5f\u67ab\u5df2\u6253\u5f00\u6728\u5323\uff0c\u89e3\u5f00\u4e00\u5c42\u6cb9\u7ef8\u5b50\u5305\u88c5\uff0c\u53ea\u89c1\u662f\u4e00\u672c\u624b\u6284\u7684\u518c\u5b50\uff0c\u4e0a\u9762\u5199\u7740\uff1a\u975e\u906d\u9047\u7a7a\u524d\u5927\u53d8\uff0c\u5c11\u6797\u5bfa\u9677\u5165\u5b58\u4ea1\u5173\u5934\uff0c\u4e0d\u5f97\u5b66\u4e60\u518c\u8bb0\u6b66\u529f\uff0c\u4ee5\u514d\u5e72\u8fdd\u5929\u673a\u3002 \u3000\u3000\u201c\u591f\u4e86\uff0c\u5feb\u6536\u8d77\u6765\uff0c\u8fd8\u539f\u79ef\u571f\uff0c\u7acb\u597d\u77f3\u7891\uff0c\u5c3d\u65e9\u79bb\u5f00\u8fd9\u91cc\u3002\u201d\u5927\u548c\u5c1a\u53e3\u8bf4\u8bdd\uff0c\u4eba\u5df2\u7387\u5148\u52a8\u624b\u3002 \u3000\u3000\u6709\u8fc7\u4e00\u573a\u52bf\u5747\u529b\u654c\u7684\u640f\u6740\u4e4b\u540e\uff0c\u6c5f\u67ab\u4e5f\u6536\u53bb\u4e86\u6258\u5927\u4e4b\u5ff5\uff0c\u6025\u6025\u6536\u85cf\u5165\u6000\uff0c\u590d\u8fd8\u7891\u4e0a\u539f\u6837\uff0c\u8d81\u4e00\u591c\u6de1\u6708\u51c9\u98ce\uff0c\u79bb\u5f00\u4e86\u6c74\u4eac\u3002 \u3000\u3000\u5954\u884c\u5341\u4f59\u91cc\uff0c\u541f\u96ea\u7a81\u7136\u505c\u4e0b\u4e86\u811a\u6b65\uff0c\u9053\uff1a\u201c\u4e07\u91cc\u98d8\u9999\u3002\u201d \u3000\u3000\u201c\u6211\u4e5f\u95fb\u5230\u4e86\u2026\u2026\u201d\u541f\u971c\u9053\uff1a\u201c\u662f\u4e0d\u662f\u5f20\u59e8\u5230\u4e86\uff1f\u201d \u3000\u3000\u201c\u4e0d\u662f\uff0c\u6211\u5728\u7530\u8001\u4eba\u5893\u524d\u5e03\u4e0b\u4e86\u9999\u888b\uff0c\u2026\u2026\u201d\u541f\u96ea\u9053\uff1a\u201c\u9999\u888b\u5df2\u88ab\u5f04\u7834\uff0c\u9999\u5473\u968f\u98ce\u98d8\u6765\u3002\u201d \u3000\u3000\u5927\u548c\u5c1a\u7528\u9f3b\u5b50\u5438\u4e00\u53e3\u6c14\uff0c\u679c\u7136\u95fb\u5230\u4e86\u4e00\u79cd\u602a\u5f02\u7684\u6de1\u9999\uff0c\u5fc3\u4e2d\u5bf9\u5f20\u56db\u59d1\u53c8\u591a\u4e00\u5c42\u4f69\u670d\uff0c\u9053\uff1a\u201c\u90a3\u662f\u8bf4\u654c\u4eba\u5df2\u627e\u5230\u4e86\u7530\u8005\u4eba\u7684\u5893\u524d\uff1f\u201d \u3000\u3000\u201c\u662f\u8fd9\u6837\u4e86\uff0c\u6211\u4eec\u6cbf\u7740\u987a\u98ce\u8d70\uff0c\u5c31\u8981\u662f\u4e07\u91cc\u98d8\u9999\u80fd\u751f\u4f5c\u7528\uff0c\u4ee5\u4fbf\u8b66\u89c9\uff0c\u2026\u2026\u201d\u541f\u96ea\u9053\uff1a \u3000\u3000\u201c\u6211\u60f3\u4e0d\u901a\uff0c\u4ed6\u4eec\u600e\u4e48\u4f1a\u90a3\u4e48\u4e00\u4e2a\u5feb\u6cd5\uff1f\u6211\u4eec\u524d\u811a\u8d70\uff0c\u4ed6\u4eec\u540e\u811a\u5c31\u5230\u3002\u201d \u3000\u3000\u201c\u53ef\u80fd\u662f\u4e01\u5f6a\u6cc4\u9732\u4e86\u79d8\u5bc6\uff0c\u2026\u2026\u201d\u5927\u548c\u5c1a\u9053\uff1a\u201c\u770b\u6765\u7530\u5e73\u4e5f\u51f6\u591a\u5409\u5c11\u4e86\u3002\u201d \u3000\u3000\u201c\u59d0\u59d0\u62c5\u5fc3\u7684\u4e0d\u662f\u8fd9\u4e2a\u2026\u2026\u201d\u541f\u96ea\u9053\uff1a\u201c\u59d0\u59d0\u662f\u62c5\u5fc3\uff0c\u6211\u4eec\u4e00\u76f4\u5728\u5bf9\u65b9\u7684\u76d1\u89c6\u4e4b\u4e0b\uff0c\u90a3\u5c31\u7b97\u9041\u8e2a\u5343\u91cc\uff0c\u4e5f\u65e0\u6cd5\u907f\u5f00\u4ed6\u4eec\u7684\u8ffd\u8e2a\u672f\u3002\u201d \u3000\u3000\u201c\u4e00\u5b9a\u8981\u6446\u8131\uff0c\u2026\u2026\u201d\u5927\u548c\u5c1a\u6709\u4e9b\u6025\u4e86\uff0c\u63a5\u9053\uff1a\u201c\u6211\u4eec\u9700\u8981\u5728\u4e00\u4e2a\u9690\u5bc6\u7684\u6240\u5728\uff0c\u4f4f\u4e9b\u65f6\u95f4\uff0c\u4ee5\u4fbf\u6c5f\u5144\u5f1f\u548c\u4e24\u4f4d\u59d1\u5a18\u53c2\u900f\u7edd\u5b66\u3002\u201d \u3000\u3000\u4e24\u4e2a\u5c0f\u72d0\u5973\u4e2d\uff0c\u541f\u96ea\u4f3c\u662f\u4e0d\u5982\u541f\u971c\u5201\u94bb\u3002 \u3000\u3000\u4f46\u6b64\u523b\uff0c\u5012\u73b0\u51fa\u5979\u7684\u672c\u9886\u6765\u4e86\uff0c\u53ea\u89c1\u5979\u8d77\u79c0\u7709\u513f\uff0c\u7a0d\u4e00\u6c89\u541f\uff0c\u9053\uff1a\u201c\u80fd\u8ffd\u8e2a\u6211\u4eec\u7684\u7edd\u4e0d\u662f\u4eba\uff0c\u4ed6\u4eec\u7684\u4eba\u53ea\u5728\u9884\u5e03\u7684\u5b9a\u70b9\u4e0a\u4f1a\u5408\uff0c\u518d\u5e03\u7f6e\u7a81\u51fb\u3001\u72d9\u6740\uff0c\u6211\u548c\u59b9\u59b9\u65e9\u5df2\u9041\u8eab\u67e5\u770b\u8fc7\u6570\u6b21\uff0c\u672a\u53d1\u73b0\u6709\u4eba\u8ffd\u8e2a\u3002\u201d \u3000\u3000\u201c\u5c31\u7b97\u662f\u7b2c\u4e00\u6d41\u7684\u8f7b\u529f\u9ad8\u624b\uff0c\u5728\u6211\u4eec\u5982\u6b64\u4e25\u8c28\u7684\u9632\u8303\u4e0b\uff0c\u8981\u8ffd\u8e2a\u6211\u4eec\uff0c\u4e5f\u4e0d\u662f\u4e00\u4ef6\u5bb9\u6613\u7684\u4e8b\u2026\u2026\u201d\u6c5f\u67ab\u8bf4\uff0c\u201c\u6bcf\u6b21\u4f11\u606f\uff0c\u6211\u90fd\u8fd0\u529f\u67e5\u770b\uff0c\u4e09\u5341\u4e08\u4e4b\u5185\uff0c\u7edd\u65e0\u6f5c\u8e2a\u7684\u654c\u4eba\u3002\u201d \u3000\u3000\u201c\u54ea\u4f1a\u662f\u4ec0\u4e48\u5462\uff1f\u96be\u9053\u4ed6\u4eec\u4e5f\u61c2\u5f97\u4e07\u91cc\u98d8\u9999\u8ffd\u8e2a\u672f\u2026\u2026\u201d\u5927\u548c\u5c1a\u9053\uff1a\u201c\u4ed6\u4eec\u5f04\u51fa\u7684\u9999\u5473\u4e0d\u540c\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u53ef\u80fd\uff0c\u2026\u2026\u201d\u541f\u96ea\u9053\uff1a\u201c\u4e0d\u7ba1\u4ec0\u4e48\u5473\u9053\uff0c\u6709\u6c14\u5473\uff0c\u6211\u5c31\u80fd\u95fb\u5f97\u51fa\u6765\uff0c\u5f20\u59e8\u4f20\u4e86\u6211\u8fa8\u95fb\u6c14\u5473\u7684\u65b9\u6cd5\u3002\u201d \u3000\u3000\u201c\u98de\u9e70\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u83f1\u82b1\u80fd\u7528\u98de\u9e70\u4f20\u9012\u6d88\u606f\uff0c\u4e5f\u53ef\u80fd\u7528\u98de\u9e70\u8ffd\u8e2a\u6211\u4eec\u3002\u201d \u3000\u3000\u201c\u5927\u6982\u662f\u4e86\u2026\u2026\u201d\u541f\u9738\u9053\uff1a\u201c\u6211\u53ea\u6ce8\u610f\u5230\u98de\u9e3d\u8ffd\u8e2a\uff0c\u52a0\u672a\u60f3\u5230\u4ed6\u4eec\u5229\u7528\u98de\u9e70\uff0c\u5b83\u4eec\u9ad8\u98de\u6570\u767e\u4e08\uff0c\u76ee\u5149\u7280\u5229\uff0c\u5f88\u96be\u53d1\u89c9\u9632\u6b62\u3002\u201d \u3000\u3000\u201c\u8fd8\u6709\u72d7\uff0c\u4e00\u79cd\u5f62\u5982\u91ce\u72fc\u5473\u89c9\u7075\u654f\u7684\u602a\u72d7\uff0c\u6211\u4eec\u4e1c\u6765\u6c74\u4eac\u65f6\uff0c\u9014\u4e2d\u53d1\u73b0\u4e24\u6b21\uff0c\u9e70\u3001\u72ac\u914d\u5408\uff0c\u52ff\u602a\u6211\u4eec\u6781\u5c3d\u5c0f\u5fc3\u4e5f\u65e0\u6cd5\u9690\u533f\u5f62\u8ff9\u2026\u2026\u201d \u3000\u3000\u541f\u96ea\u9053\uff1a\u201c\u65e2\u7136\u88ab\u6211\u4eec\u53d1\u89c9\u4e86\uff0c\u5c31\u6709\u529e\u6cd5\u5bf9\u4ed8\u4e86\u3002\u201d \u3000\u3000\u5927\u548c\u5c1a\u5fc3\u4e2d\u53c8\u9707\u60ca\u3001\u53c8\u5feb\u6170\uff0c\u6025\u6025\u9053\uff1a\u201c\u5c0f\u96ea\u513f\uff0c\u5feb\u8bf4\u5440\uff01\u4ec0\u4e48\u529e\u6cd5\uff1f\u201d \u3000\u3000\u201c\u7b2c\u4e00\uff0c\u5148\u65ad\u7edd\u4ed6\u4eec\u7684\u8bbe\u8ba1\uff0c\u8b6c\u5982\u4ed6\u4eec\u9884\u8ba1\u6211\u4eec\u4f1a\u897f\u8fd4\u957f\u5b89\uff0c\u548c\u5f20\u59e8\u4f1a\u5408\uff0c\u6211\u4eec\u504f\u504f\u4e0d\u53bb\uff0c\u60f3\u6211\u4eec\u4f1a\u8fd4\u5c11\u6797\uff0c\u8d76\u4e0a\u6b66\u5f53\uff0c\u6211\u4eec\u4e5f\u4e0d\u53bb\u3002 \u3000\u3000\u56e0\u4e3a\uff0c\u53ea\u8981\u6211\u4eec\u5728\u4ed6\u4eec\u8bbe\u5b9a\u7684\u8def\u7ebf\u4e0a\u6d3b\u52a8\uff0c\u5c31\u6c38\u8fdc\u65e0\u6cd5\u907f\u5f00\u4ed6\u4eec\u7684\u9e70\u3001\u72ac\u8ffd\u8e2a\u6cd5\uff0c\u2026\u2026\u201d\u541f\u96ea\u9053\uff1a\u201c\u7b2c\u4e8c\uff0c\u5165\u6c34\u4e0a\u8239\uff0c\u4ee5\u7edd\u7075\u72ac\u55c5\u89c9\uff1b\u7b2c\u4e09\uff0c\u6df1\u591c\u767b\u5cb8\uff0c\u4ee5\u7edd\u98de\u9e70\u7684\u76d1\u89c6\uff0c\u6211\u4eec\u4e0d\u5b9a\u76ee\u6807\uff0c\u54ea\u91cc\u5408\u9002\u4e86\uff0c\u5c31\u5728\u90a3\u91cc\u505c\u7559\u4e0b\u6765\u3002 \u3000\u3000\u5f53\u7136\uff0c\u4e0a\u4e86\u8239\uff0c\u8981\u5148\u770b\u770b\u8fd9\u672c\u518c\u8bb0\u4e0a\u7684\u6b66\u529f\uff0c\u662f\u5426\u503c\u5f97\u6211\u4eec\u4e0b\u529f\u592b\u4e60\u7ec3\uff0c\u4e0d\u503c\u5f97\uff0c\u5c31\u65e9\u4e9b\u897f\u4e0b\u548c\u5f20\u59e8\u4f1a\u5408\uff0c\u7814\u5546\u5bf9\u654c\u4e4b\u7b56\u3002\u201d \u3000\u3000\u201c\u597d\u529e\u6cd5\uff0c\u8d70\uff01\u8f6c\u5411\u9ec4\u6cb3\uff0c\u5148\u8eb2\u5728\u8239\u4e0a\u2026\u2026\u201d\u5927\u548c\u5c1a\u9053\uff1a\u201c\u6211\u5e26\u8def\u3002\u201d \u3000\u3000\u201c\u548c\u5c1a\u4f2f\u4f2f\u2026\u2026\u201d\u541f\u96ea\u9053\uff1a\u201c\u6211\u8fd9\u662f\u7b2c\u4e00\u6b21\u5927\u80c6\u7684\u8bba\u4e8b\u3001\u63a8\u7406\uff0c\u53ef\u4e0d\u4fdd\u9669\u4e00\u5b9a\u4e0d\u9519\uff0c\u8981\u662f\u60f3\u9519\u4e86\uff0c\u4f60\u53ef\u8981\u4fdd\u8bc1\u6c5f\u67ab\u54e5\u4e0d\u4f1a\u602a\u6211\u3002\u201d \u3000\u3000\u672a\u5f85\u5927\u548c\u5c1a\u5f00\u53e3\uff0c\u6c5f\u67ab\u5df2\u884c\u8fd1\u541f\u96ea\uff0c\u63e1\u4f4f\u5979\u4e00\u53ea\u5c0f\u624b\uff0c\u9053\uff1a\u201c\u4f60\u63a8\u7406\u5206\u660e\uff0c\u6211\u5fc3\u4e2d\u4f69\u670d\u6781\u4e86\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\uff0c\u6211\u6015\u9519\u4e86\u2026\u2026\u201d\u541f\u96ea\u9053\uff1a\u201c\u8981\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u201c\u9519\u4e86\u4e5f\u4e0d\u602a\u4f60\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u5927\u59d0\u8981\u4f60\u4eec\u8ddf\u6211\u6765\uff0c\u5df3\u7ecf\u65e9\u77e5\u9053\u4f60\u4eec\u6709\u8fd9\u4e9b\u673a\u667a\u3001\u80fd\u529b\u4e86\u3002\u201d \u3000\u3000\u541f\u96ea\u9053\uff1a\u201c\u9519\u4e86\uff0c\u4f60\u4e0d\u602a\u6211\u4eec\uff0c\u6211\u548c\u59b9\u59b9\u5c31\u6562\u51fa\u4e3b\u610f\u4e86\u3002\u201d \u3000\u3000\u201c\u5bf9\uff01\u6709\u4e3b\u610f\u5c3d\u7ba1\u8bf4\u51fa\u6765\u2026\u2026\u201d\u5927\u548c\u5c1a\u7b11\u9053\uff1a\u201c\u548c\u5c1a\u4f2f\u4f2f\u5df2\u7ecf\u611f\u89c9\u4f60\u4eec\u9752\u51fa\u4e8e\u84dd\uff0c\u80dc\u6211\u5341\u500d\u4e86\u3002\u201d \u3000\u3000\u884c\u8fd1\u9ec4\u6cb3\u5cb8\u7554\uff0c\u4e0d\u8fc7\u56db\u66f4\u591a\u4e9b\uff0c\u627e\u4e86\u4e00\u6761\u8239\uff0c\u5927\u548c\u5c1a\u51fa\u624b\u5c31\u662f\u4e24\u767e\u4e24\u94f6\u5b50\uff0c\u8239\u8001\u5927\u773c\u775b\u4e5f\u770b\u76f4\u4e86\uff0c\u81ea\u7136\u662f\u4e00\u5207\u7167\u529e\uff0c\u6728\u8239\u5212\u5165\u6cb3\u5fc3\uff0c\u5c31\u8ba9\u5b83\u987a\u6d41\u800c\u4e0b\u3002 \u3000\u3000\u5929\u8272\u4e00\u4eae\uff0c\u6c5f\u67ab\u7b49\u5c31\u8eb2\u5165\u8231\u4e2d\uff0c\u8fd9\u6761\u8239\u4e0d\u5927\uff0c\u53ea\u6709\u4e00\u4e2a\u8231\uff0c\u56db\u4e2a\u4eba\u5168\u8eb2\u5728\u8231\u4e2d\uff0c\u6709\u4e9b\u6324\u7684\u611f\u89c9\u3002 \u3000\u3000\u5f53\u7136\uff0c\u4e24\u4e2a\u5c0f\u72d0\u5973\uff0c\u4e5f\u65e0\u6cd5\u7f20\u5728\u6c5f\u5927\u54e5\u7684\u8eab\u4e0a\u6492\u5a07\u4e86\u3002 \u3000\u3000\u6c5f\u67ab\u53d6\u51fa\u518c\u8bb0\uff0c\u7ec6\u770b\u5c01\u9762\u4e0a\u51e0\u4e2a\u5b57\uff0c\u5fc3\u4e2d\u6697\u9707\u52a8\uff0c\u8fd9\u91cc\u9762\u8bb0\u8f7d\u7684\u4ec0\u4e48\u6b66\u529f\uff0c\u7adf\u7136\u4f1a\u5e72\u8fdd\u5929\u673a\u3002 \u3000\u3000\u90a3\u662f\u8bf4\uff0c\u4eba\u4e0d\u5e94\u8be5\u7ec3\u8fd9\u79cd\u6b66\u529b\uff0c\u4e00\u65e5\u7ec3\u4e86\uff0c\u5e72\u8fdd\u5929\u673a\uff0c\u5c31\u662f\u4f1a\u7834\u574f\u4e86\u81ea\u7136\u8fd0\u884c\u7684\u5929\u9053\u4e86\u3002 \u3000\u3000\u4e24\u4e2a\u5c0f\u72d0\u5df2\u6d17\u5c3d\u8138\u4e0a\u7684\u7070\u571f\uff0c\u867d\u7136\u8863\u7740\u4ecd\u662f\u7537\u88c5\uff0c\u4f46\u4e24\u5f20\u82f9\u679c\u4f3c\u7684\u8138\u86cb\u513f\uff0c\u914d\u7740\u56db\u53ea\u9ed1\u767d\u5206\u660e\u7684\u5927\u773c\u775b\uff0c\u671b\u7740\u6c5f\u67ab\u5fae\u7b11\u3002 \u3000\u3000\u6c5f\u67ab\u6267\u7740\u4e66\u518c\u6c89\u541f\uff0c\u4e8c\u5973\u4e5f\u4e0d\u8a00\u8bed\uff0c\u5979\u4eec\u4e0d\u8be5\u7ba1\u7684\u4e8b\uff0c\u7edd\u4e0d\u5f00\u53e3\u3002 \u3000\u3000\u201c\u6253\u5f00\u5427\uff01\u5c31\u7b97\u662f\u770b\u8fc7\u4e86\u4eba\u4f1a\u53d1\u75af\uff0c\u4f60\u5df1\u662f\u5982\u7bad\u5728\u5f26\uff0c\u4e0d\u5f97\u4e0d\u53d1\u4e86\u2026\u2026\u201d\u5927\u548c\u5c1a\u6c89\u58f0\u7684\u9f13\u52b1\u6c5f\u67ab\u3002 \u3000\u3000\u6c5f\u67ab\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u8bf4\u7684\u662f\uff0c\u6628\u591c\u4e00\u6218\uff0c\u4f7f\u6211\u60f3\u5230\u8fc7\u5f80\u7684\u65e0\u77e5\uff0c\u6211\u8f7b\u85d0\u5c11\u6797\u7edd\u5b66\uff0c\u6211\u76f8\u4fe1\u767e\u660e\u8001\u65b9\u4e08\uff0c\u5fc3\u4e2d\u4e5f\u6709\u611f\u89c9\uff0c\u53ea\u662f\u4ed6\u4fee\u517b\u9ad8\u6df1\uff0c\u4e0d\u613f\u548c\u6211\u8ba1\u8f83\uff0e\u8981\u662f\u8d4c\u4e86\u6c14\uff0c\u4e0d\u80af\u8d50\u4e0b\u4fe1\u7269\u3002 \u3000\u3000\u201c\u6211\u4e0d\u4f46\u6709\u8d1f\u5e08\u957f\u6559\u8bf2\u82e6\u5fc3\uff0c\u4e5f\u5c06\u8bef\u5c3d\u5929\u4e0b\u82cd\u751f\uff0c\u73b0\u5728\u60f3\u6765\uff0c\u771f\u662f\u51b7\u6c57\u6dcb\u6f13\uff0c\u61ca\u6094\u4e0d\u5df2\u3002\u201d \u3000\u3000\u5927\u548c\u5c1a\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8001\u65b9\u4e08\u8981\u6551\u5c11\u6797\u5bfa\uff0c\u8981\u6551\u51fa\u5929\u4e0b\u6b66\u6797\u540c\u9053\uff0c\u4f60\u662f\u552f\u4e00\u53ef\u6258\u7684\u4eba\u3002\u201d \u3000\u3000\u6c5f\u67ab\u7684\u624b\uff0c\u7adf\u7136\u6709\u4e9b\u98a4\u6296\uff0c\u5c01\u518c\u9762\u4e0a\u201c\u5e72\u8fdd\u5929\u673a\uff0c\u6ca6\u52ab\u83ab\u6028\u3002\u201d\u516b\u4e2a\u5b57\uff0c\u4f7f\u4eba\u89e6\u76ee\u60ca\u5fc3\u3002 \u3000\u3000\u4e24\u4e2a\u5c0f\u72d0\u5973\u53e3\u867d\u4e0d\u8a00\uff0c\u4f46\u5fc3\u4e2d\u5374\u5982\u8d1f\u5343\u65a4\uff0c\u8138\u4e0a\u795e\u60c5\u60ca\u5e03\uff0c\u5df2\u7136\u65e0\u6cd5\u63a9\u9970\u3002 \u3000\u3000\u8fd9\u672c\u518c\u8bb0\uff0c\u7a76\u7adf\u5728\u641e\u4ec0\u4e48\u9b3c\uff1f\u4e07\u4e00\u6c5f\u5927\u54e5\u53d7\u4e86\u518c\u8bb0\u4e2d\u5185\u5bb9\u5f71\u54cd\uff0c\u53d8\u5f97\u65e0\u6cd5\u4eb2\u8fd1\uff0c\u8fd9\u4e00\u751f\u8981\u5982\u4f55\u751f\u6d3b\u4e0b\u53bb\uff1f \u3000\u3000\u6c5f\u67ab\u6253\u5f00\u4e86\u5c01\u9875\uff0c\u4e24\u4e2a\u5c0f\u72d0\u5973\u672c\u80fd\u7684\u95ed\u4e0a\u4e86\u773c\u775b\u3002 \u3000\u3000\u201c\u8bba\u5251\u7bc7\u201d\u6c5f\u67ab\u7acb\u523b\u88ab\u5185\u5bb9\u5438\u5f15\uff0c\u5168\u795e\u6295\u6ce8\u3002 \u3000\u3000\u4e24\u4e2a\u5c0f\u72d0\u5973\u6084\u6084\u7741\u5f00\u773c\u775b\uff0c\u53ea\u89c1\u5927\u548c\u5c1a\u522b\u8fc7\u5934\u53bb\uff0c\u7adf\u7136\u672a\u770b\uff0c\u541f\u971c\u5927\u611f\u6c14\u5fff\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u548c\u5c1a\uff0c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u7740\uff0c\u53ea\u8981\u6c5f\u5927\u54e5\u4e00\u4e2a\u4eba\u53bb\u5e94\u52ab\u5417\uff1f\u201d \u3000\u3000\u5979\u771f\u7684\u706b\u4e86\uff0c\u8fde\u4f2f\u4f2f\u4e24\u4e2a\u5b57\u4e5f\u7701\u5566\u3002 \u3000\u3000\u201c\u5c0f\u971c\u513f\uff0c\u548c\u5c1a\u770b\u4e0d\u61c2\u3002\u201d \u3000\u3000\u201c\u771f\u7684\uff01\u2026\u2026\u201d\u541f\u971c\u5947\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u5f88\u6709\u5b66\u95ee\u5417\uff1f\u201d \u3000\u3000\u201c\u6c5f\u6e56\u8dd1\u4e45\u4e86\uff0c\u6211\u53ea\u662f\u7ecf\u9a8c\u4e30\u5bcc\uff0c\u5374\u8bfb\u4e66\u4e0d\u591a\u3002\u201d \u3000\u3000\u4e24\u4eba\u4e89\u8bba\u7684\u58f0\u97f3\u5f88\u5927\uff0c\u4f46\u6c5f\u67ab\u5374\u604d\u5982\u672a\u95fb\u3002 \u3000\u3000\u4e8c\u5973\u5fcd\u4e0d\u4f4f\uff0c\u7ed5\u5728\u6c5f\u67ab\u8eab\u540e\uff0c\u51dd\u76ee\u770b\u53bb\uff0c\u7acb\u523b\u88ab\u518c\u8bb0\u4e0a\u5185\u5bb9\u5438\u5f15\uff0c\u5982\u78c1\u5438\u94c1\uff0c\u518d\u4e5f\u820d\u4e0d\u5f97\u79fb\u5f00\u773c\u775b\u3002 \u3000\u3000\u8fd9\u8bba\u5251\u7bc7\u4e2d\uff0c\u53ea\u6709\u4e00\u62db\u5251\u5f0f\uff0c\u91cd\u8981\u5904\u662f\u5982\u4f55\u628a\u4eba\u878d\u5165\u5251\u4e2d\uff0c\u4eba\u5251\u5408\u4e00\uff0c\u65e0\u62db\u80dc\u6709\u62db\uff0c\u5343\u53d8\u4e07\u5316\uff0c\u5c3d\u5728\u5176\u4e2d\u4e86\u3002 \u3000\u3000\u6c5f\u67ab\u4eba\u5165\u4e66\u4e2d\uff0c\u4e24\u4e2a\u5c0f\u72d0\u5973\u4e5f\u4e0d\u7531\u81ea\u4e3b\u7684\u5f00\u59cb\u7528\u5fc3\u601d\u7d22\u3002 \u3000\u3000\u53ea\u4e0d\u8fc7\u6570\u5343\u5b57\u7684\u8bb0\u8ff0\uff0c\u4f46\u6c5f\u67ab\u548c\u4e24\u4e2a\u5c0f\u72d0\u5973\u5374\u770b\u4e86\u4e24\u4e2a\u65f6\u8fb0\u3002 \u3000\u3000\u8239\u8001\u5927\u84b8\u597d\u4e86\u4e24\u5c3e\u9ec4\u6cb3\u9ca4\u9c7c\uff0c\u7aef\u8fdb\u6765\u8ba8\u597d\u5ba2\u4eba\u3002 \u3000\u3000\u5927\u548c\u5c1a\u63a5\u8fc7\u84b8\u9c7c\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u51fa\u53bb\u638c\u8235\uff0c\u6709\u4e8b\u60c5\uff0c\u6211\u4eec\u4f1a\u62db\u547c\u4f60\u3002\u201d \u3000\u3000\u8239\u8001\u5927\u5fc3\u4e2d\u6709\u4e9b\u5947\u602a\uff1f\u770b\u4e86\u6c5f\u67ab\u7b49\u4e00\u773c\uff0c\u9053\uff1a\u201c\u6211\u5728\u9ec4\u6cb3\u8d70\u8239\u4e8c\u5341\u5e74\uff0c\u5c31\u4f1a\u70e7\u8fd9\u4e00\u9053\u83dc\uff0c\u8d81\u70ed\u5403\uff0c\u4e00\u51c9\u5c31\u6709\u9c7c\u8165\u6c14\u4e86\u3002\u770b\u4e66\u561b\uff01 \u3000\u3000\u4e0d\u4e89\u8fd9\u4e00\u65f6\u7247\u523b\u3002\u201d \u3000\u3000\u201c\u55ef\uff01\u9c7c\u9999\u6251\u9f3b\uff0c\u6211\u4f1a\u62db\u547c\u4ed6\u4eec\u5403\u3002\u201d \u3000\u3000\u8239\u8001\u5927\u7b11\u4e00\u7b11\uff0c\u8f6c\u8eab\u800c\u53bb\uff0c\u4ed6\u8981\u7684\u5c31\u662f\u8fd9\u53e5\u5938\u8d5e\u3002 \u3000\u3000\u5e9f\u5bdd\u5fd8\u98df\uff0c\u5927\u6982\u5c31\u662f\u5982\u6b64\u4e86\uff0c\u84b8\u9c7c\u786e\u662f\u9999\u6c14\u6251\u9f3b\uff0c\u4f46\u6c5f\u67ab\u548c\u4e24\u4e2a\u5c0f\u72d0\u5973\u662f\u55c5\u800c\u4e0d\u89c9\u3002 \u3000\u3000\u4e09\u4eba\u4e0d\u662f\u5728\u770b\uff0c\u800c\u662f\u795e\u5165\u8bba\u5251\u7bc7\uff0c\u5f00\u59cb\u7167\u65b9\u7ec3\u4e60\u4e86\u3002 \u3000\u3000\u7ec3\u7684\u662f\u8fd0\u5251\u65b9\u6cd5\uff0c\u662f\u8fd0\u5251\u4e4b\u6c14\u3002 \u3000\u3000\u4e66\u843d\u8231\u677f\uff0c\u4eba\u4eba\u5fd8\u6211\uff0c\u4e0d\u77e5\u4f55\u65f6\uff0e\u4e09\u4e2a\u4eba\u5df2\u95ed\u4e0a\u773c\u775b\uff0c\u76d8\u819d\u800c\u5750\u3002 \u3000\u3000\u5915\u9633\u897f\u4e0b\uff0c\u665a\u971e\u7eda\u70c2\uff0c\u53cd\u7167\u5728\u6ed4\u6ed4\u9ec4\u6cb3\u6d4a\u6d41\u4e0a\uff0c\u6709\u4e00\u79cd\u65e0\u6cd5\u5f62\u5bb9\u7684\u51c4\u8ff7\u4e4b\u7f8e\uff0c\u662f\u611f\u4f24\uff0c\u4e5f\u662f\u8ffd\u5fc6\u3002 \u3000\u3000\u4f46\u5927\u548c\u5c1a\u5374\u65e0\u5fc3\u89c2\u8d4f\uff0c\u5fc3\u4e2d\u771f\u7684\u6709\u4e9b\u7126\u6025\uff0c\u77aa\u5706\u4e86\u4e00\u5bf9\u773c\u775b\uff0c\u770b\u7740\u6c5f\u67ab\uff0c\u6ee1\u6000\u5173\u987e\u60c5\uff0c\u82e6\u65e0\u4e0b\u624b\u5904\uff01 \u3000\u3000\u6c5f\u67ab\u7ec8\u4e8e\u7741\u5f00\u4e86\u773c\u775b\uff0c\u5c0f\u8239\u4e5f\u9760\u4e86\u5cb8\uff0c\u5df2\u662f\u4e07\u5bb6\u706f\u706b\u7684\u653e\u591c\u65f6\u5206\u3002 \u3000\u3000\u56de\u5934\u770b\u4e8c\u5973\uff0c\u53ea\u89c1\u5979\u4eec\u8138\u8272\u82cd\u767d\uff0c\u6c14\u606f\u5fae\u5f31\uff0c\u4f3c\u662f\u5df2\u6fd2\u4e34\u6c14\u7edd\u3002 \u3000\u3000\u6c5f\u67ab\u5403\u4e86\u4e00\u60ca\uff0c\u53cc\u624b\u9f50\u51fa\uff0c\u5206\u6309\u4f4f\u4e8c\u5973\u80cc\u5fc3\u4e4b\u4e0a\u3002 \u3000\u3000\u4ed6\u5df2\u719f\u4e60\u4e86\u8fd0\u6c14\u5251\u8bc0\uff0c\u4e86\u7136\u5173\u8981\u6240\u5728\uff0c\u771f\u6c14\u5f90\u5f90\u5bfc\u5f15\uff0c\u4e8c\u5973\u6e10\u590d\u6b63\u5e38\u3002 \u3000\u3000\u201c\u597d\u5389\u5bb3\u554a\uff01\u201d\u541f\u971c\u7741\u5f00\u773c\u775b\uff0c\u9053\uff1a\u201c\u6211\u5feb\u8981\u618b\u6b7b\u4e86\uff0c\u90a3\u4e00\u80a1\u771f\u6c14\uff0c\u5c31\u662f\u4e0a\u4e0d\u4e86\u5341\u4e8c\u91cd\u6402\uff0c\u5361\u5728\u4e86\u90a3\u91cc\uff0c\u4e0a\u4e0d\u6765\uff0c\u4e0b\u4e0d\u53bb\uff0c\u52ff\u602a\u548c\u5c1a\u4f2f\u4f2f\u4e0d\u80af\u770b\u3002\u201d \u3000\u3000\u201c\u548c\u5c1a\u6ca1\u6709\u53d1\u89c9\u4f60\u4eec\u7684\u72b6\u51b5\uff0c\u5426\u5219\u65e9\u5c31\u51fa\u624b\u5e2e\u4f60\u4eec\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u5e78\u597d\u4f60\u6ca1\u6709\u53d1\u89c9\uff0c\u8fd9\u548c\u4e00\u822c\u7684\u8c03\u606f\u8fd0\u6c14\u4e0d\u540c\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u4f60\u4e00\u51fa\u624b\uff0c\u5979\u4eec\u5c31\u6c38\u65e0\u751f\u9192\u4e4b\u65e5\u4e86\u3002\u201d \u3000\u3000\u5927\u548c\u5c1a\u5446\u4f4f\u4e86\uff0c\u5fd6\u9053\uff1a\u201c\u4e24\u4e2a\u4e2b\u5934\uff0c\u771f\u6b7b\u5728\u6211\u7684\u624b\u91cc\uff0c\u90a3\u53ef\u662f\u903c\u6211\u8df3\u9ec4\u6cb3\u4e86\u3002\u201d \u3000\u3000\u541f\u96ea\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8c22\u8c22\u4f60\u4e86\uff0c\u6c5f\u5927\u54e5\u2026\u2026\u201d \u3000\u3000\u7f13\u7f13\u4f38\u51fa\u624b\u53bb\u5206\u63e1\u4f4f\u6c5f\u67ab\u3001\u541f\u971c\uff0c\u8138\u4e0a\u60c5\u7231\u6a2a\u6ea2\uff0c\u76ee\u4e2d\u6e29\u67d4\u4e07\u5343\u3002 \u3000\u3000\u6c5f\u67ab\u4ea6\u4e0d\u7981\u5fc3\u4e3a\u4e4b\u52a8\uff0c\u4f38\u51fa\u53e6\u4e00\u53ea\u624b\uff0c\u63e1\u4f4f\u541f\u971c\uff0c\u4e09\u4eba\u516d\u624b\u4e92\u7275\uff0c\u5fc3\u878d\u4e00\u4f53\u3002 \u3000\u3000\u4e0d\u7528\u7247\u8bed\u53ea\u5b57\uff0c\u8bb8\u4e0b\u4e86\u751f\u6b7b\u76df\u7ea6\u3002 \u3000\u3000\u81f3\u60c5\u52a8\u5929\u5730\uff0c\u5927\u548c\u5c1a\u4e5f\u770b\u7684\u5fc3\u9178\u795e\u5f80\uff0c\u70ed\u6cea\u76c8\u7736\uff0c\u6697\u6697\u7948\u7977\uff1a\u201c\u6211\u4f5b\u6709\u7075\uff0c\u548c\u5c1a\u613f\u4ee3\u4ed6\u4eec\u5386\u52ab\u5e94\u96be\uff0c\u4fdd\u4f51\u8fd9\u4e09\u4e2a\u5c0f\u513f\u5973\uff0c\u82b1\u597d\u6708\u5706\uff0c\u9003\u8131\u52ab\u82e6\u3002\u201d \u3000\u3000\u8239\u8001\u5927\u6267\u7740\u4e00\u76cf\u6cb9\u706f\u884c\u5165\u8231\u4e2d\uff0c\u770b\u770b\u4e00\u9505\u84b8\u9c7c\u672a\u52a8\uff0c\u4e0d\u7981\u53f9\u4e00\u53e3\u6c14\u3002 \u3000\u3000\u5927\u548c\u5c1a\u62ed\u53bb\u6d41\u4e0b\u7684\u6cea\u6c34\uff0c\u7b11\u9053\uff1a\u201c\u706b\u5019\u5dee\u4e00\u5206\uff0c\u5473\u9053\u5168\u5931\u771f\uff0c\u4f60\u4eec\u773c\u4e86\u5427\uff01\u8001\u59dc\u8fa3\u5fc3\u554a\uff01\u201d \u3000\u3000\u201c\u4ec0\u4e48\uff1f\u2026\u2026\u201d\u8239\u8001\u5728\u6025\u9053\uff1a\u201c\u6211\u653e\u7684\u8001\u59dc\u4e0d\u591a\u554a\uff01\u201d \u3000\u3000\u5927\u548c\u5c1a\u5475\u5475\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u8239\u8001\u5927\uff0c\u9c7c\u51c9\u4e86\uff0c\u62ff\u53bb\u70ed\u70ed\u5427\uff01\u6709\u5927\u997c\u3001\u9992\u5934\uff0c\u987a\u4fbf\u9001\u4e00\u70b9\u8fc7\u6765\u3002\u201d \u3000\u3000\u201c\u9992\u5934\u5012\u6709\uff0c\u53ea\u662f\u9ec4\u6cb3\u9ca4\u9c7c\u4e00\u56de\u9505\u2026\u2026\u201d\u8239\u8001\u5927\u9053\uff1a\u201c\u5473\u9053\u53ef\u5c31\u5dee\u7684\u8fdc\u4e86\u3002\u201d \u3000\u3000\u653e\u4e0b\u706f\u706b\uff0c\u7aef\u8d77\u4e00\u9505\u9c7c\uff0c\u8d70\u5230\u8231\u95e8\u53e3\uff0c\u53c8\u56de\u5934\u8bf4\uff1a\u201c\u9ec4\u6cb3\u65e0\u98ce\u4e09\u5c3a\u6d6a\uff0c\u6211\u867d\u662f\u8001\u9ec4\u6cb3\u4e86\uff0c\u53ef\u4e5f\u4e0d\u6562\u591c\u4e2d\u884c\u821f\uff0c\u6211\u628a\u8239\u9760\u5cb8\u4e86\u3002\u201d \u3000\u3000\u6c5f\u67ab\u70b9\u70b9\u5934\u3002 \u3000\u3000\u5c0f\u8239\u4e0a\u867d\u7136\u5c40\u4fc3\uff0c\u53ef\u662f\u5f88\u5b89\u9759\uff0c\u6c5f\u67ab\u51b3\u5fc3\u5728\u8239\u4e0a\u770b\u5b8c\u8fd9\u672c\u518c\u8bb0\u3002 \u3000\u3000\u5927\u548c\u5c1a\u8981\u8239\u5bb6\u4e70\u4e86\u98df\u7528\u4e4b\u7269\u3002 \u3000\u3000\u5c0f\u821f\u6cbf\u9ec4\u6cb3\u987a\u6d41\u800c\u4e0b\u3002\u9ec4\u6cb3\u4e2d\u5f88\u5c11\u884c\u8239\uff0c\u5b64\u821f\u6d4a\u6d41\uff0c\u968f\u6c34\u98d8\u6d6e\u3001\u4e0d\u62d8\u5feb\u6162\uff0c\u672a\u5b9a\u884c\u6b62\u3002 \u3000\u3000\u8239\u8001\u5927\u628a\u8235\u907f\u9669\uff0c\u5012\u4e5f\u900d\u9065\u3002 \u3000\u3000\u770b\u5b8c\u4e86\u5168\u672c\u518c\u8bb0\uff0c\u6c5f\u67ab\u624d\u53d1\u89c9\u662f\u4e00\u672c\u4f20\u771f\u8bb0\uff0c\u5341\u4e8c\u4f4d\u5c11\u6797\u9ad8\u50e7\uff0c\u6613\u88c5\u79bb\u5bfa\uff0c\u82b1\u8d39\u4e86\u56db\u5341\u591a\u5e74\u7684\u65f6\u95f4\uff0c\u8fbe\u5230\u5929\u7afa\uff0c\u641c\u5c3d\u5929\u4e0b\u5947\u6280\uff0c\u53bb\u829c\u5b58\u83c1\uff0c\u51b6\u4e8e\u4e00\u7089\uff0c\u6709\u81f3\u6b63\u81f3\u5927\u7684\u6b66\u529f\uff0c\u4e5f\u6709\u6700\u90aa\u6076\u7684\u6280\u827a\u3002 \u3000\u3000\u5c11\u6797\u5bfa\u4e03\u5341\u4e8c\u79cd\u7edd\u6280\uff0c\u9664\u4e86\u8fbe\u6469\u7956\u5e08\u7684\u201c\u4e00\u82c7\u6e21\u6c5f\u201d\u4e4b\u5916\uff0c\u672a\u518d\u5217\u540d\u5176\u4e0a\uff0c\u4e66\u6210\u4e4b\uff0c\u5341\u4e8c\u9ad8\u50e7\u5168\u90fd\u5455\u8840\u800c\u4ea1\uff0c\u771f\u672c\u85cf\u4e8e\u5c11\u6797\u5bfa\u4e2d\uff0c\u5f55\u4e00\u672c\u526f\u518c\uff0c\u85cf\u4e8e\u5c11\u6797\u5bfa\u5916\uff0c\u79d8\u5bc6\u53ea\u4f20\u638c\u95e8\u65b9\u4e08\u4e00\u4eba\u3002 \u3000\u3000\u5c11\u6797\u5bfa\u7684\u548c\u5c1a\uff0c\u4e0d\u80fd\u7ec3\u4f20\u8bb0\u4e0a\u7684\u6b66\u529f\uff0c\u4e8b\u5b9e\u4e0a\uff0c\u638c\u95e8\u65b9\u4e08\u4e5f\u53ea\u77e5\u9053\u53d6\u4e66\u4e4b\u6cd5\uff0c\u5e76\u672a\u770b\u8fc7\u4f20\u771f\u8bb0\uff0c\u975e\u6c5f\u6e56\u5927\u53d8\uff0c\u5c11\u6797\u5b58\u4ea1\uff0c\u4e0d\u5f97\u8ba9\u6b64\u4e66\u9762\u4e16\u3002 \u3000\u3000\u6c5f\u67ab\u4e5f\u53d1\u89c9\u4e86\u4f20\u771f\u8bb0\u4e0a\u7684\u90aa\u6076\uff0c\u6709\u4e9b\u8bb0\u8ff0\uff0c\u5df2\u4ecb\u4e8e\u4eba\u9b3c\u4e4b\u95f4\uff0c\u66f4\u53ef\u6015\u7684\u662f\u6536\u96c6\u8fd9\u4e9b\u6280\u827a\u7684\u9ad8\u50e7\uff0c\u5927\u4e13\u6ce8\u4e86\u3002 \u3000\u3000\u4f9d\u636e\u524d\u4eba\u7684\u7ecf\u9a8c\uff0c\u628a\u4e60\u7ec3\u7684\u65b9\u6cd5\uff0c\u9009\u5176\u7cbe\u8981\uff0c\u538b\u7f29\u7b80\u5316\uff0c\u8017\u5c3d\u5fc3\u667a\u3002 \u3000\u3000\u5f53\u65f6\uff0c\u4ed6\u4eec\u90fd\u5341\u5206\u5f97\u610f\uff0c\u4f46\u4e66\u6210\u4e4b\u540e\uff0c\u624d\u60f3\u5230\uff0c\u4e00\u65e6\u9047\u4e0a\u4e86\u4e00\u4e2a\u5177\u6709\u5929\u5206\u7684\u4eba\uff0c\u518d\u5177\u6709\u6df1\u539a\u7684\u6b66\u529f\u6839\u57fa\uff0c\u5f88\u53ef\u80fd\u5728\u6781\u77ed\u7684\u65f6\u95f4\uff0c\u517c\u901a\u767e\u827a\uff0c\u4e16\u4e0a\u8fd8\u6709\u8c01\u80fd\u5236\u4ed6\uff1f\u2026\u2026\u4f46\u53c8\u4e0d\u5fcd\u6bc1\u3002 \u3000\u3000\u89c1\u6c5f\u67ab\u5408\u4e0a\u4e66\u672c\uff0c\u5927\u548c\u5c1a\u624d\u4f4e\u58f0\u95ee\u9053\uff1a\u201c\u6c5f\u8001\u5f1f\uff0c\u80fd\u4e0d\u80fd\u5bf9\u8fd9\u672c\u4f20\u771f\u8bb0\u4f5c\u4e2a\u8bc4\u4ef7\uff1f\u201d \u3000\u3000\u201c\u597d\uff01\u6211\u4e5f\u6709\u4e8b\u6b63\u8981\u5411\u4f60\u8bf7\u6559\u3002\u5148\u8bf4\u8fd9\u672c\u4f20\u771f\u8bb0\u5427\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u51dd\u795e\u601d\u8fc7\u4e86\u4e00\u9635\uff0c\u63a5\u9053\uff1a\u201c\u8fd9\u4e0a\u9762\u8bb0\u8ff0\u7684\u4e0d\u662f\u5c11\u6797\u6b66\u529f\uff0c\u4e5f\u4e0d\u662f\u6b66\u5b66\u7edd\u5b66\uff0c\u662f\u4e24\u767e\u5e74\u524d\u5c11\u6797\u5bfa\u5341\u4e8c\u9ad8\u50e7\uff0c\u5077\u7a83\u5929\u4e0b\u7edd\u6280\u2026\u2026\u8bf4\u641c\u96c6\u4e5f\u53ef\u4ee5\uff0c\u56e0\u4e3a\uff0c\u4ed6\u4eec\u4e0d\u662f\u7167\u672c\u6284\u5f55\uff0c\u800c\u662f\u878d\u4f1a\u767e\u5bb6\u4e4b\u540e\uff0c\u53c8\u82b1\u4e86\u81ea\u5df1\u7684\u4e0d\u5c11\u5fc3\u8840\u3002 \u3000\u3000\u5341\u4e8c\u4e2a\u4eba\u805a\u4e8e\u4e00\u5904\uff0c\u622a\u957f\u8865\u77ed\uff0c\u7814\u7a76\u51fa\u8fd9\u672c\u4f20\u771f\u8bb0\uff0c\u5b83\u7684\u7b2c\u4e00\u7f3a\u70b9\u662f\uff0c\u53ea\u80fd\u9526\u4e0a\u6dfb\u82b1\uff0c\u4e0d\u80fd\u96ea\u4e2d\u9001\u70ad\u2026\u2026\u201d \u3000\u3000\u201c\u600e\u4e48\u8bf4\u5462\uff1f\u2026\u2026\u201d\u548c\u5c1a\u5947\u602a\u7684\u95ee\u3002 \u3000\u3000\u201c\u8fd9\u672c\u4e66\uff0c\u6b66\u529f\u5dee\u4e00\u70b9\uff0c\u4e66\u8bfb\u7684\u4e0d\u591f\u591a\u7684\u4eba\uff0c\u5f97\u5230\u5b83\u4e5f\u5982\u770b\u65e0\u5b57\u5929\u4e66\uff0c\u770b\u4e0d\u61c2\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u5b66\u5bcc\u4e94\u8f66\uff0c\u6587\u91c7\u98ce\u6d41\uff0c\u4e0d\u4f1a\u6b66\u529f\uff0c\u4e0d\u4f1a\u7ec3\uff0c\u6b66\u529f\u4e0d\u5230\u4e00\u5b9a\u7684\u5883\u754c\uff0c\u6781\u6613\u8d70\u706b\u5165\u9b54\uff0c\u9001\u6389\u8001\u547d\uff0c\u4e0d\u80fd\u7ec3\u2026\u2026\u201d \u3000\u3000\u201c\u50cf\u6211\u548c\u5c1a\u8fd9\u79cd\u5883\u754c\u7684\u6b66\u529f\uff0c\u80fd\u4e0d\u80fd\u7ec3\u5462\uff1f\u201d \u3000\u3000\u201c\u8981\u6211\u8bf4\u5b9e\u8bdd\uff0c\u4f60\u4e0d\u80fd\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u4f60\u7684\u65e0\u76f8\u795e\u529f\uff0c\u53ef\u80fd\u8fd8\u5f3a\u6211\u4e09\u5206\uff0c\u4f46\u4f60\u6ca1\u6709\u5b66\u8fc7\u5251\u672f\uff0c\u6b66\u529f\u5927\u521a\u4e86\uff0c\u521a\u5219\u6613\u6298\uff0c\u6709\u5c11\u6570\u51e0\u79cd\uff0e\u79f0\u53ef\u4ee5\u7ec3\uff0c\u4f46\u4f60\u5982\u770b\u8fc7\u5168\u7bc7\uff0c\u7edd\u4e0d\u4f1a\u653e\u5f03\u4efb\u4f55\u4e00\u79cd\u6b66\u529f\uff0c\u4e24\u767e\u5e74\u524d\uff0c\u5c11\u6797\u5bfa\u5341\u4e8c\u9ad8\u50e7\uff0c\u5728\u7814\u8ba8\u8fd9\u672c\u4f20\u8bb0\u65f6\uff0c\u6ca1\u6709\u4e00\u4e2a\u4eba\u6b7b\u3002 \u3000\u3000\u56e0\u4e3a\uff0c\u4ed6\u4eec\u80f8\u6000\u5927\u516c\uff0c\u6295\u5165\u4e86\u5168\u90e8\u5fc3\u667a\uff0c\u53ea\u7814\u5546\u65b9\u6cd5\uff0c\u6ca1\u6709\u65f6\u95f4\u81ea\u5df1\u7ec3\u4e60\u3002 \u3000\u3000\u6240\u4ee5\uff0c\u4ed6\u4eec\u5b8c\u6210\u4e86\u8fd9\u672c\u5947\u4e66\uff0c\u4f46\u4e66\u6210\u4ea1\u540e\uff0c\u4ed6\u4eec\u5df2\u5f7c\u6b64\u6c9f\u901a\uff0c\u8fd9\u4e9b\u5b58\u5728\u8111\u9645\u4e2d\u7684\u5947\u7384\u6b66\u529f\uff0c\u5370\u8c61\u6df1\u523b\uff0c\u5fcd\u4e0d\u4f4f\u5077\u7ec3\u8d77\u6765\uff0c\u4e00\u7ec3\u5c31\u7ec3\u53bb\u4e86\u5341\u4e8c\u6761\u6027\u547d\uff0c\u5c31\u5982\u7f57\u5170\u4e00\u822c\uff0c\u6ca1\u7ec3\u8ba8\u5929\u72d0\u6b66\u529f\uff0c\u4f46\u5374\u5fc3\u6709\u5929\u72d0\u6b66\u529f\uff0c\u5979\u89c1\u8bc6\u8fc7\u5929\u72d0\u5a9a\u7b11\u3001\u5c31\u4e0d\u81ea\u89c9\u7684\u5077\u7ec3\u8d77\u6765\u3002 \u3000\u3000\u5979\u7684\u5929\u72d0\u5a9a\u7b11\uff0c\u4f18\u8fc7\u5973\u513f\uff0c\u4f46\u5982\u679c\u8981\u5979\u4ee5\u5929\u72d0\u6b66\u529f\uff0c\u548c\u4eba\u4e00\u6218\uff0c\u5979\u5c31\u4e0d\u5982\u5973\u513f\u591a\u77e3\uff01\u56e0\u4e3a\uff0c\u5979\u6ca1\u6709\u771f\u7ec3\u8fc7\u5929\u72d0\u6b66\u529f\u3002\u2026\u2026\u201d \u3000\u3000\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u5929\u72d0\u6b66\u529f\uff0c\u53ef\u4ee5\u9002\u5e94\uff0c\u4e3a\u4ec0\u4e48\u96ea\u3001\u971c\u4e8c\u5973\uff0c\u4f1a\u8d70\u706b\u5165\u9b54\u5462\uff1f\u2026\u2026\u201d\u5927\u548c\u5c1a\u9053\uff1a\u201c\u4f60\u53c8\u4e3a\u4ec0\u4e48\u7ec3\u7684\u987a\u987a\u5f53\u5f53\uff0c\u65e0\u60ca\u65e0\u9669\uff1f\u201d \u3000\u3000\u201c\u6211\u8eab\u517c\u4e09\u5723\u4e4b\u957f\u554a\uff01\u2026\u2026\u201d\u6c5f\u67ab\u7b11\u4e00\u7b11\u53c8\u9053\uff1a\u201c\u521a\u3001\u67d4\u4e4b\u5916\uff0c\u8fd8\u901a\u5251\u672f\uff0c\u5929\u72d0\u6b66\u529f\u9669\u67d4\u591a\u53d8\uff0c\u672c\u662f\u9002\u5e94\u6027\u6700\u5f3a\u7684\u6b66\u529f\uff0c\u96ea\u3001\u971c\u4e5f\u901a\u5251\u672f\uff0c\u4f46\u5374\u5168\u65e0\u9633\u521a\u4e4b\u6c14\uff0c\u4f55\u51b5\uff0c\u6211\u5f97\u52a9\u6700\u591a\u7684\u662f\u91d1\u4e39\u4e66\u751f\u7684\u6b66\u529f\uff0c\u4ed6\u81ea\u53f7\u91d1\u4e39\uff0c\u6781\u5584\u533b\u5b66\uff0c\u628a\u533b\u672f\u878d\u5165\u4e86\u6b66\u529f\u4e2d\u3002 \u3000\u3000\u7f57\u5170\u592b\u5987\u53ea\u5b66\u4e86\u4ed6\u7684\u5251\u672f\uff0c\u5374\u6ca1\u5b66\u4ed6\u72ec\u95e8\u7684\u5410\u7eb3\u4e4b\u6cd5\uff0c\u90a3\u662f\u4e00\u79cd\u6697\u5408\u4eba\u8eab\u6c14\u8840\u8fd0\u884c\u7684\u5185\u606f\u672f\uff0c\u5bf9\u6b66\u529f\uff0c\u770b\u4e0d\u51fa\u660e\u663e\u7684\u5e2e\u52a9\uff0c\u4f46\u5374\u80fd\u8c03\u7406\u9634\u9633\uff0c\u9002\u5e94\u8eab\u4f53\u4e0a\u5404\u79cd\u53d8\u5316\uff0c\u6240\u4ee5\uff0c\u6211\u4e00\u8fc8\u6b65\u5c31\u8e0f\u5165\u5766\u9014\u3002\u201d \u3000\u3000\u5927\u548c\u5c1a\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u4f20\u771f\u8bb0\u8fd8\u6709\u7b2c\u4e8c\u4e2a\u7f3a\u70b9\u5462\uff1f\u201d \u3000\u3000\u201c\u6709\uff01\u7b2c\u4e8c\u7f3a\u70b9\u662f\u8be1\u591a\u4e8e\u6b63\uff0c\u6b63\u9053\u96be\u4fee\uff0c\u90aa\u672f\u6613\u6210\uff0c\u90a3\u5341\u4e8c\u4f4d\u9ad8\u50e7\uff0c\u77e5\u9053\u7684\u5947\u6280\u592a\u591a\u4e86\uff0c\u7814\u5546\u65f6\u4e13\u8d70\u6377\u5f84\uff0c\u53ea\u6c42\u901f\u6210\uff0c\u5bd3\u90aa\u4e8e\u6b63\uff0c\u6211\u5982\u672a\u5b66\u8fc7\u91d1\u4e39\u4e66\u751f\u7684\u5185\u606f\u672f\uff0c\u6050\u4e5f\u4f1a\u8d70\u706b\u5165\u9b54\uff01\u201d \u3000\u3000\u201c\u542c\u8d77\u6765\uff0c\u8fd9\u672c\u5947\u4e66\uff0c\u5012\u4f3c\u662f\u4e13\u95e8\u4e3a\u4f60\u800c\u5199\uff0c\u2026\u2026\u201d\u5927\u548c\u5c1a\u8bf4\uff0c\u201c\u96be\u9053\u51a5\u51a5\u4e4b\u4e2d\uff0c\u771f\u6709\u4e00\u79cd\u795e\u529b\u5728\u4e3b\u5bb0\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\uff01\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u5f53\u4eca\u6b66\u6797\u4e4b\u4e16\uff0c\u6700\u5c11\u6709\u5341\u4e2a\u4ee5\u4e0a\uff0c\u53ef\u4ee5\u4e60\u7ec3\u8fd9\u4f20\u771f\u8bb0\u4e0a\u7684\u6b66\u529f\u3002 \u3000\u3000\u96ea\u3001\u971c\u7ecf\u6211\u4ee5\u771f\u6c14\u5bfc\u5f15\uff0c\u5df2\u7ecf\u53ef\u4ee5\u7ec3\u4e86\uff0c\u5c31\u7b97\u6ca1\u6709\u6211\u5e2e\u5979\u4eec\u6253\u5f00\u751f\u6b7b\u7384\u5173\uff0c\u6c14\u4e0a\u5341\u4e8c\u91cd\u6402\uff0c\u518d\u591a\u4e09\u5e74\uff0c\u5979\u4eec\u5c31\u53ef\u4ee5\u81ea\u5df1\u7ec3\u4e86\uff0c\u4f60\u53ea\u8981\u7ec3\u4e09\u5e74\u9634\u67d4\u6c14\u529f\uff0c\u4e5f\u53ef\u4ee5\u8bd5\u7740\u4e60\u7ec3\uff0c\u4e5f\u8bb8\u4e0d\u4f1a\u50cf\u6211\u8fd9\u4e48\u987a\u5f53\u3002\u201d \u3000\u3000\u201c\u6211\u61c2\u4e86\u2026\u2026\u201d\u5927\u548c\u5c1a\u9053\uff1a\u201c\u8fd9\u672c\u4e66\u90aa\u591a\u4e8e\u6b63\uff0c\u6613\u4e8e\u901f\u6210\uff0c\u7559\u4e4b\u65e0\u76ca\uff0c\u90a3\u5c31\u70e7\u4e86\u5b83\uff01\u201d \u3000\u3000\u201c\u53ef\u662f\uff0c\u8fd9\u662f\u5c11\u6797\u5bfa\u7684\u4e1c\u897f\uff0c\u6211\u4eec\u5df2\u7b54\u5e94\u8fd8\u7ed9\u5c11\u6797\u5bfa\u2026\u2026\u201d\u6c5f\u673a\u9053\uff1a\u201c\u5c82\u80fd\u5931\u4fe1\u4e8e\u8001\u65b9\u4e08\uff1f\u201d \u3000\u3000\u201c\u5c11\u6797\u6b66\u529f\uff0c\u53ea\u6709\u4e00\u62db\u2018\u4e00\u82c7\u6e21\u6c5f\u2019\u4e0d\u662f\u2026\u2026\u201d\u5927\u548c\u5c1a\u9053\uff1a\u201c\u4f20\u56de\u7ed9\u5c11\u6797\u5bfa\u5c31\u662f\uff0c\u5176\u4ed6\u7684\u548c\u5c11\u6797\u5bfa\u626f\u4e0d\u4e0a\u5173\u7cfb\u4e86\u3002\u201d \u3000\u3000\u201c\u662f\u5c11\u6797\u5bfa\u5341\u4e8c\u4f4d\u9ad8\u50e7\u7814\u5546\u51fa\u6765\u7684\uff0c\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u600e\u80fd\u8bf4\u548c\u5c11\u6797\u65e0\u5173\uff1f\u201d \u3000\u3000\u5927\u548c\u5c1a\u4f38\u624b\u53d6\u8fc7\u4f20\u771f\u8bb0\uff0c\u9053\uff1a\u201c\u4f60\u53ef\u662f\u5df2\u5b57\u5b57\u8bb0\u5165\u5fc3\u4e2d\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u548c\u96ea\u3001\u971c\u4e8c\u5973\u7adf\u7136\u90fd\u70b9\u70b9\u5934\u3002 \u3000\u3000\u201c\u4f60\u4eec\u4e5f\u8bb0\u7684\u4e00\u5b57\u4e0d\u6f0f\u4e48\uff1f\u2026\u2026\u201d\u5927\u548c\u5c1a\u770b\u7740\u4e8c\u5973\u95ee\u3002 \u3000\u3000\u201c\u662f\uff01\u2026\u2026\u201d\u541f\u971c\u9053\uff1a\u201c\u7239\u903c\u7740\u6211\u4eec\u8bfb\u56db\u4e66\u3001\u4e94\u7ecf\uff0c\u5a18\u5374\u8981\u6211\u4eec\u8bfb\u5510\u8bd7\u3001\u5b8b\u8bcd\uff0c\u59d0\u59d0\u80fd\u4e00\u53e3\u6c14\u80cc\u51fa\u5510\u8bd7\u4e09\u767e\u9996\uff0c\u4e00\u5b57\u4e0d\u6f0f\uff0c\u6211\u6bd4\u8f83\u5dee\u4e00\u70b9\uff0c\u8fc7\u76ee\u4e0d\u5fd8\u7684\u672c\u9886\uff0c\u603b\u662f\u8fd8\u6709\u3002\u201d \u3000\u3000\u201c\u5c0f\u971c\u513f\uff0c\u53e3\u6c14\u592a\u5927\u4e86\u5427\uff01\u201d \u3000\u3000\u201c\u548c\u5c1a\u4f2f\u4f2f\u4e0d\u76f8\u4fe1\uff0c\u6211\u5c31\u80cc\u8fd9\u672c\u4f20\u771f\u8bb0\u7ed9\u4f60\u542c\u542c\u2026\u2026\u201d\u541f\u971c\u9053\uff1a\u201c\u6f0f\u4e86\u4e00\u4e2a\u5b57\uff0c\u4f60\u5c31\u628a\u6211\u4e22\u5230\u9ec4\u6cb3\u5582\u9c7c\u5403\u2026\u2026\u201d \u3000\u3000\u201c\u514d\u4e86\uff01\u514d\u4e86\uff01\u548c\u5c1a\u542c\u8fdb\u53bb\u5fc3\u91cc\u600e\u4e48\u5f97\u4e86\uff0c\u4f60\u662f\u8bda\u5fc3\u60f3\u5bb3\u6211\u5440\uff01\u201d\u4e3e\u4e66\u5c31\u71c3\uff0c\u771f\u7684\u628a\u4f20\u771f\u8bb0\u7ed9\u70e7\u4e86\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u90fd\u70b9\u4e86\u5934\uff0c\u8bb0\u4e0d\u5f97\u4e0d\u80fd\u602a\u6211\uff0c\u7559\u4e0b\u5b83\u662f\u4e2a\u7978\u60a3\uff0c\u706b\u70e7\u4e86\u5e72\u51c0\u5229\u843d\uff0c\u4e0d\u7528\u62c5\u5fc3\u8001\u65b9\u4e08\uff0c\u5c11\u6797\u5bfa\u8fd8\u5b58\u6709\u6b63\u672c\uff0c\u548c\u5c1a\u6562\u70e7\u4e86\u5b83\uff0c\u5c31\u80fd\u8bf4\u670d\u8001\u65b9\u4e08\u2026\u2026\u201d\u7a81\u7136\u4f4f\u53e3\u4e0d\u8a00\u3002 \u3000\u3000\u6c5f\u67ab\u660e\u767d\u4ed6\u60f3\u5230\u4e86\u662f\u4e0d\u662f\u8fd8\u6709\u6d3b\u7740\u518d\u89c1\u8001\u65b9\u4e08\u7684\u673a\u4f1a\uff1f\u5fcd\u4e0b\u53bb\u6ca1\u6709\u8bf4\u51fa\u6765\u3002 \u3000\u3000\u7ecf\u8fc7\u90a3\u591c\u4e00\u6218\uff0c\u6c5f\u67ab\u7684\u6027\u60c5\u6709\u4e86\u5f88\u5927\u6539\u53d8\u3002 \u3000\u3000\u539f\u6765\u8ba4\u4e3a\u5929\u4e0b\u5f88\u96be\u627e\u51fa\u4e00\u4e2a\u771f\u6b63\u7684\u654c\u624b\uff0c\u73b0\u5728\u5374\u662f\u6709\u7740\u6df1\u5c71\u6709\u731b\u864e\uff0c\u5927\u6cfd\u6f5c\u868a\u9f99\u7684\u611f\u89c9\u3001\u6c5f\u6e56\u4e0a\u80fd\u4eba\u4f17\u591a\uff0c\u4e5f\u8bb8\u8fd8\u6709\u5f3a\u8fc7\u90a3\u9ed1\u8863\u4eba\u7684\u9ad8\u624b\u2026\u2026\u201d \u3000\u3000\u4ed6\u539f\u672c\u8f7b\u6de1\u5c11\u6797\u6b66\u529f\uff0c\u4f46\u8bfb\u8fc7\u4f20\u771f\u8bb0\u540e\uff0c\u5374\u53c8\u4e0d\u80fd\u4e0d\u4f69\u773c\u5c11\u6797\u50e7\u4fa3\u732e\u8eab\u6b66\u6797\u7684\u4e30\u529f\u3001\u4f1f\u7ee9\u3002 \u3000\u3000\u4ed6\u4eec\u5c42\u5c42\u89c4\u5f8b\u675f\u7f1a\uff0c\u5206\u9645\u4e25\u8c28\uff0c\u4e0d\u4f46\u5f55\u8bb0\u4e0b\u6280\u827a\u7cbe\u534e\uff0c\u4e5f\u5728\u6b66\u6797\u4e2d\u6700\u9700\u8981\u5b83\u7684\u65f6\u523b\uff0c\u5c55\u73b0\u4e8e\u4e16\uff0c\u5c11\u6797\u57fa\u4e1a\uff0c\u5343\u5e74\u4e0d\u8d25\uff0c\u5b9e\u975e\u65e0\u56e0\u2026\u2026 \u3000\u3000\u201c\u6c5f\u5144\u5f1f\uff0c\u4f60\u770b\u8981\u591a\u5c11\u65f6\u95f4\uff0c\u80fd\u7ec3\u6210\u4f20\u771f\u8bb0\u7684\u6b66\u529f\uff1f\u201d\u5927\u548c\u5c1a\u9053\uff1a\u201c\u6211\u4eec\u8981\u5b89\u6392\u4e00\u4e2a\u6682\u907f\u654c\u950b\u7684\u7b56\u7565\uff0c\u6df1\u6f5c\u884c\u8e2a\u3002\u201d \u3000\u3000\u6c5f\u67ab\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f20\u771f\u8bb0\u4e0a\u7684\u6b66\u529f\uff0c\u4e0d\u5b9c\u5168\u7ec3\uff0c\u56e0\u4e3a\u5b83\u786e\u6709\u5e72\u8fdd\u5929\u673a\u4e4b\u5904\uff0c\u800c\u4e14\uff0c\u65f6\u95f4\u4e5f\u4e0d\u5141\u8bb8\uff0c\u9009\u62e9\u4e00\u4e24\u6837\u7cfb\u7edf\u53bb\u7ec3\uff0c\u4e5f\u8bb8\u5c31\u53ef\u4ee5\u5bf9\u4ed8\u5f3a\u654c\u2026\u2026\u201d \u3000\u3000\u201c\u5bf9\uff01\u6211\u4eec\u5404\u7ec3\u4e00\u4e24\u79cd\u7cfb\u7edf\u2026\u2026\u201d\u53f6\u96ea\u9053\uff1a\u201c\u53ef\u4ee5\u914d\u5408\u65bd\u5c55\uff0c\u5c31\u80fd\u53d1\u6325\u51fa\u5f88\u5f3a\u5927\u7684\u5a01\u529b\u3002\u201d \u3000\u3000\u201c\u4f60\u4eec\u8981\u5c0f\u5fc3\u554a\uff01\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u771f\u8981\u7ec3\u6210\u4e86\u4f20\u771f\u8bb0\u4e0a\u7684\u5168\u90e8\u6b66\u529f\uff0c\u90a3\u5c31\u8131\u79bb\u4e86\u4eba\u7684\u8303\u56f4\uff0c\u6ca6\u5165\u9b54\u754c\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\u2026\u2026\u201d\u541f\u96ea\u9053\uff1a\u201c\u6211\u548c\u59b9\u59b9\u90fd\u4e0d\u4f1a\u8ffd\u6c42\u4ed9\u4e1a\uff0c\u66f4\u4e0d\u613f\u6ca6\u5165\u9b3c\u57df\uff0c\u6211\u4eec\u8981\u4f5c\u4eba\uff0c\u6709\u8840\u6709\u8089\uff0c\u6709\u60c5\u6709\u7231\u7684\u4eba\uff0c\u6211\u4eec\u4e0d\u4f1a\u89e6\u72af\u5929\u5fcc\uff0c\u6211\u4eec\u53ea\u8981\u5e2e\u52a9\u4f60\u5b8c\u6210\u5fc3\u613f\u2026\u2026\u201d \u3000\u3000\u201c\u6c5f\u5927\u54e5\u2026\u2026\u201d\u541f\u971c\u63a5\u9053\uff1a\u201c\u4e07\u8f7d\u9ad8\u5c71\u6d41\u6c34\uff0c\u5343\u5e74\u677e\u67cf\u957f\u9752\uff0c\u4ed9\u9053\u7edd\u4eb2\uff0c\u4f5b\u67af\u8089\u8eab\uff0c\u6211\u4eec\u90fd\u6ca1\u6709\u8fd9\u6837\u7684\u5927\u667a\u6167\uff0c\u5927\u5b9a\u529b\uff0c\u6211\u4eec\u4e5f\u4e0d\u613f\u5e38\u751f\u4e0d\u8001\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u4e24\u4f4d\u59d1\u5a18\u6709\u6b64\u5165\u4e16\u4e4b\u5ff5\uff0c\u6c5f\u6e56\u5c31\u91cd\u5149\u53ef\u671f\u4e86\uff0c\u201d\u5927\u548c\u5c1a\u9053\uff1a\u201c\u4f46\u4e0d\u77e5\u8981\u591a\u5c11\u65f6\u95f4\uff1b\u624d\u80fd\u6709\u6240\u6210\u5c31\uff1f\u201d \u3000\u3000\u4e8c\u5973\u4e92\u671b\u4e86\u4e00\u773c\uff0c\u541f\u96ea\u9053\uff1a\u201c\u9009\u62e9\u4e86\u4e24\u79cd\u548c\u672c\u8eab\u827a\u4e1a\u76f8\u8fd1\u7684\u7cfb\u7edf\uff0c\u6709\u767e\u65e5\u65f6\u95f4\uff0c\u5e94\u8be5\u53ef\u4ee5\u5165\u95e8\u4e86\u3002\u201d \u3000\u3000\u201c\u8fd9\u6837\u77ed\u7684\u65f6\u95f4\u2026\u2026\u201d\u5927\u548c\u5c1a\u6709\u4e9b\u4e0d\u4fe1\u5730\u8bf4\uff0c\u201c\u5f53\u771f\u662f\u4e00\u672c\u6b66\u5b66\u5b9d\u5178\u4e86\u3002\u201d \u3000\u3000\u76ee\u5149\u8f6c\u6ce8\u5230\u6c5f\u67ab\u7684\u8138\u4e0a\uff0c\u9887\u6709\u6c42\u8bc1\u4e4b\u610f\u3002 \u3000\u3000\u201c\u541f\u96ea\u8bf4\u7684\u4e0d\u9519\uff0c\u4f20\u771f\u8bb0\u53ea\u91cd\u65b9\u6cd5\uff0c\u4e0d\u91cd\u62db\u5f0f\uff0c\u6bcf\u4e00\u79cd\u6b66\u529f\uff0c\u53ea\u6709\u4e00\u4e24\u62db\u7684\u4f8b\u4e3e\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u662f\u4e00\u79cd\u5fc3\u6cd5\u51fb\u654c\uff0c\u5df2\u5230\u62db\u7531\u52bf\u53d8\u7684\u5883\u754c\uff0c\u8fdb\u5165\u4e86\u65e0\u62db\u80dc\u6709\u62db\uff0c\u4f46\u53d8\u5316\u4e8e\u7cfb\u7edf\u4e4b\u4e2d\uff0c\u4e5f\u65e0\u6cd5\u903e\u8d8a\u754c\u9650\u3002 \u3000\u3000\u541f\u96ea\u90a3\u53e5\u548c\u672c\u8eab\u827a\u4e1a\u76f8\u8fd1\uff0c\u5df2\u9053\u5c3d\u4e2a\u4e2d\u7684\u5965\u79d8\u4e86\uff0c\u4e66\u4e2d\u7684\u5947\u53d8\uff0c\u5168\u5728\u4e00\u4e2a\u2018\u609f\u2019\u5b57\u4e0a\u4e86\uff0c\u6240\u4ee5\u5b83\u4e0d\u80fd\u96ea\u4e2d\u9001\u70ad\u3002\u201d \u3000\u3000\u201c\u548c\u5c1a\u660e\u767d\u4e86\uff0c\u771f\u662f\u5929\u9053\u4e0d\u6e83\uff0c\u8981\u627e\u4e00\u4e2a\u4ec0\u4e48\u6837\u7684\u5730\u65b9\uff1f\u624d\u9002\u5408\u4e09\u4ed5\u7684\u4e60\u7ec3\u5462\uff1f\u201d \u3000\u3000\u201c\u8981\u6e05\u9759\uff0c\u4eba\u8ff9\u7f55\u81f3\u7684\u5730\u65b9\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u4e8b\u5b9e\u4e0a\uff0c\u6211\u4eec\u5df2\u7ecf\u5f00\u59cb\u7ec3\u4e86\uff0c\u4e5f\u95ef\u8fc7\u4e86\u4eba\u4f53\u771f\u6c14\u9006\u884c\u7684\u7b2c\u4e00\u5173\uff0c\u4f46\u8fd8\u6709\u51e0\u4e2a\u5173\u952e\u6240\u5728\u9700\u8981\u65f6\u95f4\u4e60\u7ec3\uff0c\u624d\u80fd\u671f\u6709\u6240\u6210\uff0c\u4e0d\u80fd\u53d7\u5230\u60ca\u6270\uff0c\u90a3\u5c31\u8981\u4f60\u4e3a\u6211\u4eec\u62a4\u6cd5\u4e86\u3002\u201d \u3000\u3000\u201c\u548c\u5c1a\u4e00\u4e2a\u4eba\u884c\u5417\uff1f\u8981\u4e0d\u8981\u627e\u5f20\u59d1\u5a18\u548c\u6b27\u9633\u592b\u5987\u540c\u6765\u4e3a\u4e09\u4f4d\u62a4\u6cd5\uff1f\u201d \u3000\u3000\u201c\u90a3\u5012\u4e0d\u7528\uff0c\u91cd\u8981\u7684\u662f\u5730\u65b9\u6e05\u51c0\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u4e0d\u80fd\u8ba9\u9690\u5bc6\u5916\u6cc4\uff0c\u771f\u8981\u88ab\u5f3a\u654c\u95fb\u98ce\u800c\u81f3\uff0c\u5c31\u7b97\u56db\u59d1\u548c\u7f57\u5170\u592b\u5987\u90fd\u5728\uff0c\u4e5f\u65e0\u6cd5\u4fdd\u5168\u6211\u4eec\u3002\u201d \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u9053\uff1a\u201c\u597d\uff01\u660e\u5929\u54b1\u4eec\u5c31\u5f03\u821f\u767b\u9646\uff0c\u5165\u5c71\u5bfb\u627e\u5730\u65b9\uff0c\u591c\u884c\u663c\u5bbf\uff0c\u4ee5\u907f\u9e70\u72ac\u8ffd\u8e2a\u3002\u201d \u3000\u3000\u5927\u548c\u5c1a\u53d6\u51fa\u4e00\u5f20\u767e\u4e24\u94f6\u7968\uff0c\u62db\u547c\u8239\u8001\u5927\u8fdb\u5165\u8231\u4e2d\uff0c\u9053\uff1a\u201c\u8fd9\u8258\u8239\uff0c\u5e02\u503c\u591a\u5c11\u94f6\u5b50\uff1f\u201d \u3000\u3000\u8239\u8001\u5927\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4e00\u8258\u65b0\u8239\uff0c\u5341\u4e24\u94f6\u5b50\u5e94\u8be5\u591f\u4e86\uff0c\u50cf\u6211\u8fd9\u6761\u8001\u8239\u65e7\u8d27\uff0c\u80fd\u5356\u4e2a\u4e09\u4e94\u4e24\u5c31\u4e0d\u9519\u4e86\u3002\u201d \u3000\u3000\u201c\u8239\u8001\u5927\u679c\u7136\u662f\u4e2a\u8001\u5b9e\u4eba\uff0c\u8fd9\u8258\u8239\u5c31\u7b97\u4e00\u767e\u4e24\u94f6\u5b50\u5427\uff01\u6211\u4e70\u4e86\uff0c\u4e0d\u8fc7\uff0c\u6709\u4e2a\u4e0d\u60c5\u4e4b\u6c42\uff0c\u5e0c\u671b\u4f60\u80fd\u7b54\u5e94\u3002\u201d \u3000\u3000\u201c\u4f60\u5df2\u7ed9\u8fc7\u4e8c\u767e\u4e24\u94f6\u5b50\uff0c\u65b0\u8239\u4e5f\u53ef\u4e70\u5341\u6761\u3001\u4e8c\u5341\u6761\u4e86\u2026\u2026\u201d\u8239\u8001\u5927\u9053\uff1a\u201c\u4f60\u8981\u600e\u4e48\u5904\u7f6e\u8fd9\u6761\u8239\uff0c\u5429\u5490\u4e00\u58f0\u5c31\u662f\uff0c\u94f6\u5b50\u6211\u662f\u4e0d\u6562\u518d\u6536\u4e86\u3002\u201d \u3000\u3000\u4ed6\u5e38\u5e74\u5728\u6cb3\u9053\u884c\u821f\uff0c\u89c1\u8bc6\u5f88\u591a\uff0c\u5df2\u611f\u89c9\u5230\u8fd9\u4e9b\u4eba\u4e0d\u662f\u5e73\u5e38\u4eba\u7269\uff0c\u5e73\u5e38\u4eba\u4e5f\u4e0d\u4f1a\u51fa\u624b\u90a3\u4e48\u5927\u65b9\u3002 \u3000\u3000\u201c\u597d\uff01\u6211\u4eec\u4eca\u665a\u4e0a\u521d\u66f4\u4e0a\u5cb8\u2026\u2026\u201d\u5927\u548c\u5c1a\u9053\uff1a\u201c\u4f60\u5c31\u628a\u8fd9\u6761\u8239\u6c89\u5165\u9ec4\u6cb3\uff0c\u4e0d\u8981\u7559\u4e0b\u75d5\u8ff9\uff0c\u6700\u597d\u561b\u4f60\u4e5f\u8eb2\u4e2a\u4e00\u5e74\u534a\u8f7d\u518d\u51fa\u6765\u3002\u201d \u3000\u3000\u201c\u6211\u6696\u4e86\uff0c\u6211\u6ca1\u6709\u89c1\u8fc7\u4f60\u4eec\uff0c\u6c34\u9762\u4e0a\u4e5f\u6ca1\u6709\u8fd9\u6761\u8239\uff0c\u6211\u662f\u5b64\u5bb6\u5be1\u4eba\u4e00\u4e2a\uff0c\u8eab\u4e0a\u6709\u5f88\u591a\u94f6\u5b50\uff0c\u6b63\u597d\u5148\u4f11\u606f\u4e00\u5e74\u534a\u8f7d\uff0c\u7136\u540e\u518d\u8ba8\u623f\u5ab3\u5987\uff0c\u53ef\u60dc\u7684\u4ee5\u540e\u5403\u4e0d\u5230\u81ea\u7f51\u81ea\u716e\u7684\u9ec4\u6cb3\u9ca4\u9c7c\u4e86\u3002\u201d \u3000\u3000\u90a3\u662f\u8bf4\u660e\u4e86\u5f03\u8239\u79bb\u6b64\u540e\uff0c\u4e5f\u4e0d\u518d\u56de\u5230\u9ec4\u6cb3\u6c34\u9762\u3002 \u3000\u3000\u201c\u597d\u6781\u4e86\u2026\u2026\u201d\u5927\u548c\u5c1a\u628a\u4e00\u5f20\u767e\u4e24\u94f6\u7968\u585e\u5165\u8239\u5bb6\u624b\u4e2d\uff0c\u9053\uff1a \u3000\u3000\u201c\u5982\u679c\u80fd\u628a\u8239\u6c89\u5f97\u65e0\u8ff9\u53ef\u5bfb\uff0c\u90a3\u5c31\u66f4\u597d\u4e86\uff0c\u4ed6\u4eec\u7684\u8ffd\u8e2a\u672f\u5341\u5206\u9ad8\u660e\uff0c\u5929\u4e0a\u6709\u98de\u9e70\uff0c\u5730\u4e0a\u6709\u7075\u72ac\u3002\u201d \u3000\u3000\u201c\u6211\u660e\u767d\u4e86\u2026\u2026\u201d\u8239\u8001\u5927\u9053\uff1a\u201c\u8bf8\u4f4d\u4e0a\u4e86\u5cb8\uff0c\u6211\u5c31\u628a\u8239\u79fb\u4e0a\u6c34\u9053\uff0c\u6c89\u4e86\u8239\u6e38\u6c34\u4e0a\u5cb8\u3002\u201d \u3000\u3000\u5929\u8272\u5165\u591c\uff0c\u8239\u4e5f\u9760\u5355\u3002 \u3000\u3000\u541f\u96ea\u7a81\u7136\u8bf4\u9053\uff1a\u201c\u8239\u5bb6\uff0c\u6625\u5bd2\u6599\u5ced\uff0c\u4f60\u8981\u5c0f\u5fc3\u4e00\u4e9b\uff0c\u522b\u8981\u7740\u4e86\u51c9\u3002\u201d \u3000\u3000\u58f0\u97f3\u751c\u7f8e\uff0c\u6709\u5982\u4e00\u676f\u7cd6\u6c34\uff0c\u704c\u5165\u4e86\u8239\u8001\u5927\u7684\u53e3\u4e2d\u3002 \u3000\u3000\u201c\u597d\uff0c\u597d\uff0c\u4e2b\u5934\uff0c\u5bf9\u654c\u8981\u4e03\u5206\u9738\u6c14\uff0c\u4f5c\u4eba\u8981\u4e09\u5206\u7d20\u5fc3\uff0c\u4f60\u8d8a\u6765\u8d8a\u5706\u719f\u4e86\u3002\u201d\u5927\u548c\u5c1a\u98de\u8eab\u800c\u8d77\uff0c\u8dc3\u4e0a\u6cb3\u5cb8\u3002 \u3000\u3000\u56db\u6761\u4eba\u5f71\uff0c\u6709\u5982\u8759\u8760\u5bb5\u98de\uff0c\u7728\u773c\u4e0d\u89c1\u3002 \u3000\u3000\u8239\u8001\u5927\u770b\u5f97\u5446\u4f4f\u4e86\u3002 \u3000\u3000\u4ed6\u542c\u8bf4\u8fc7\uff0c\u6c5f\u6e56\u4eba\u80fd\u98de\u6a90\u8d70\u58c1\uff0c\u4f46\u672a\u60f3\u5230\u8fc7\uff0c\u4eba\u5982\u9e1f\u98de\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u5df2\u4eba\u5c71\u4e1c\u5883\u5185\uff0c\u5927\u548c\u5c1a\u719f\u77e5\u5730\u5f62\uff0c\u4e00\u591c\u5954\u884c\uff0c\u8fdb\u5165\u4e86\u6cf0\u6c82\u5c71\u533a\u3002 \u3000\u3000\u5929\u5df2\u4eae\uff0c\u56db\u4eba\u5c31\u9690\u5165\u4e86\u6797\u4e2d\u5750\u606f\uff0c\u5c71\u533a\u591c\u884c\uff0c\u767b\u9ad8\u8d70\u4f4e\u3002\u89c6\u754c\u4e0d\u6e05\uff0c\u7279\u522b\u7d2f\u4eba\uff0c\u4e24\u591c\u4e0b\u6765\u5c31\u7b97\u4e00\u822c\u8eab\u8d1f\u6b66\u529f\u7684\u4eba\uff0c\u4e5f\u6709\u75b2\u4e4f\u4e4b\u611f\u3002 \u3000\u3000\u4f46\u4ed6\u4eec\u90fd\u975e\u4e00\u822c\u4eba\uff0c\u800c\u662f\u8eab\u6000\u7edd\u4e16\u6b66\u529f\u7684\u9ad8\u624b\uff0c\u6240\u4ee5\uff0c\u4ed6\u4eec\u4e0d\u4f1a\u75b2\u7d2f\uff0c\u4e5f\u4e0d\u80af\u9a6c\u864e\u4ece\u4e8b\uff0c\u82b1\u4e86\u4e09\u4e2a\u665a\u4e0a\uff0c\u624d\u627e\u5230\u4e86\u4e00\u5904\u73af\u5883\u6e05\u5e7d\uff0c\u4eba\u8ff9\u7edd\u5c11\u7684\u5730\u65b9\u3002 \u3000\u3000\u679c\u7136\u4f3c\u907f\u5f00\u4e86\u98de\u9e70\u3001\u7075\u72ac\u7684\u8ffd\u8e2a\uff0c\u51e0\u4eba\u65e5\u591c\u7559\u5fc3\uff0c\u5b8c\u5168\u6ca1\u6709\u53d1\u73b0\u5f02\u5146\u3002 \u3000\u3000\u5927\u548c\u5c1a\u6df1\u8c0b\u8fdc\u8651\uff0c\u5df2\u5229\u7528\u673a\u4f1a\u70e4\u5236\u4e86\u4e0d\u5c11\u7684\u5e72\u7cae\u3002 \u3000\u3000\u8fd9\u662f\u6cf0\u6c82\u5c71\u533a\u4e2d\u4e00\u5904\u5e7d\u8c37\uff0c\u5c71\u4e0d\u9ad8\uff0c\u4f46\u5374\u5341\u5206\u9634\u6076\uff0c\u5341\u4e08\u6df1\u8c37\uff0c\u73af\u7ed5\u7740\u4e00\u5ea7\u77f3\u5cf0\uff0c\u56db\u5468\u5168\u662f\u78f7\u5ccb\u602a\u77f3\uff0c\u6742\u751f\u4e86\u4e00\u4e9b\u8346\u68d8\u4e71\u8349\uff0c\u662f\u4e00\u5ea7\u597d\u6811\u4e5f\u957f\u4e0d\u51fa\u4e00\u68f5\u7684\u8352\u5c71\uff0c\u5c71\u8c37\u53c8\u6df1\u8fc7\u5341\u4e08\u4ee5\u4e0a\uff0c\u771f\u662f\u9760\u5c71\u65e0\u67f4\u70e7\uff0c\u8fd1\u6c34\u65e0\u9c7c\u98df\u7684\u5730\u65b9\uff0c\u6240\u4ee5\uff0c\u65b9\u5706\u6570\u91cc\u65e0\u4e00\u6237\u4eba\u5bb6\u3002 \u3000\u3000\u4f46\u602a\u77f3\u6797\u7acb\u7684\u5c71\u5cf0\u4e2d\uff0c\u5374\u6709\u4e00\u5ea7\u5929\u7136\u7684\u77f3\u7a9f\uff0c\u5e72\u71e5\u6e05\u723d\uff0c\u662f\u4e00\u5904\u5f88\u9002\u5b9c\u7559\u4f4f\uff0e\u800c\u53c8\u9690\u5bc6\u7684\u6240\u5728\u3002 \u3000\u3000\u6c5f\u67ab\u548c\u4e8c\u5973\u5728\u77f3\u7a9f\u4e2d\u5f00\u59cb\u4e60\u7ec3\u4f20\u771f\u8bb0\u4e0a\u7684\u6b66\u529f\u3002 \u3000\u3000\u5927\u548c\u5c1a\u9664\u4e86\u51c6\u5907\u5403\u3001\u559d\u4e4b\u7269\u5916\u3002\u8fd8\u522b\u51fa\u5fc3\u88c1\u7684\u7528\u8346\u68d8\uff0c\u6742\u8349\u5236\u4e86\u4e00\u9876\u5927\u5e3d\u5b50\uff0c\u6234\u4e0a\u5b83\u5750\u5728\u5c71\u77f3\u4e0a\uff0c\u5c31\u50cf\u662f\u5929\u6210\u7684\u4e00\u4e1b\u8346\u8349\u3002 \u3000\u3000\u8fd9\u53ef\u4ee5\u76d1\u89c6\u56db\u91ce\uff0c\u53c8\u907f\u8fc7\u98de\u9e70\u7684\u641c\u5bfb\uff0c\u6df1\u8c37\u7ed5\u5cf0\uff0c\u6d41\u6c34\u6e4d\u6025\uff0c\u65ad\u53bb\u4e86\u7075\u72ac\u55c5\u89c9\u8ffd\u8ff9\uff0c\u7a77\u5c71\u6076\u6c34\uff0c\u4e5f\u6709\u5b83\u53ef\u7528\u7684\u4e00\u9762\u3002 \u3000\u3000\u8fd9\u5730\u65b9\u6ca1\u6709\u540d\u5b57\uff0c\u4e5f\u6ca1\u6709\u597d\u770b\u7684\u666f\u7269\uff0c\u7559\u4f20\u7684\u4ed9\u8ff9\uff0c\u6240\u4ee5\uff0c\u6ca1\u6709\u4eba\u6d89\u5165\u6b64\u5730\uff0c\u662f\u4e00\u4e2a\u5355\u8c03\u53c8\u5bc2\u5bde\u7684\u6240\u5728\u3002 \u3000\u3000\u4f46\u6c5f\u67ab\u548c\u96ea\u3001\u971c\u4e8c\u5973\uff0c\u5374\u751f\u6d3b\u7684\u5341\u5206\u7d27\u5f20\uff0c\u4ed6\u4eec\u4e60\u7ec3\u7684\u6b66\u529f\uff0c\u8981\u4f7f\u6c14\u8840\u9006\u884c\uff0c\u628a\u4eba\u4f53\u6f5c\u80fd\u7684\u6781\u9650\u51b2\u7834\uff0c\u7ec3\u7684\u662f\u4f38\u5367\u76d8\u5750\u3001\u5012\u7acb\u5c48\u817f\uff0c\u628a\u8eab\u4f53\u5c48\u8f6c\u6210\u4e00\u4e2a\u5706\u5708\uff0c\u5f88\u96be\u5f97\u6bd4\u5212\u51fa\u4e00\u4e24\u79cd\u62db\u5f0f\u51fa\u6765\uff0c\u597d\u50cf\u5341\u5206\u8f7b\u677e\uff0c\u5176\u5b9e\u662f\u5bd2\u5929\u996e\u51b0\u6c34\uff0c\u70b9\u6ef4\u5728\u5fc3\u5934\u3002 \u3000\u3000\u4e09\u4e2a\u4eba\u90fd\u7ec3\u7684\u5f02\u5e38\u8f9b\u82e6\uff0c\u76f4\u5230\u4e5d\u5341\u4e94\u5929\u540e\uff0c\u541f\u96ea\u624d\u970d\u7136\u8d2f\u901a\uff0c\u611f\u89c9\u5230\u6574\u4e2a\u8eab\u4f53\uff0c\u5982\u540c\u7ecf\u8fc7\u4e86\u4e00\u756a\u6539\u9020\uff0c\u53ef\u4ee5\u8f7b\u677e\u81ea\u5982\u7684\uff0c\u5de6\u624b\u5212\u65b9\uff0c\u53f3\u624b\u5212\u5706\uff0c\u53cc\u81c2\u80a2\u4f53\uff0c\u53ef\u4ee5\u968f\u5fc3\u6240\u6b32\uff0c\u4f5c\u51fa\u5404\u79cd\u76f8\u53cd\u7684\u52a8\u4f5c\uff0c\u4e5f\u5c31\u662f\u8bf4\u4e0d\u540c\u7684\u80a2\u4f53\u548c\u6b66\u529f\uff0c\u53ef\u4ee5\u540c\u65f6\u7528\u51fa\u6765\u653b\u88ad\u654c\u4eba\uff0c\u4f53\u80fd\u7684\u602a\u5f02\uff0c\u4f3c\u5df2\u5230\u4e86\u53ef\u4ee5\u5206\u5272\u65bd\u7528\u7684\u5883\u754c\u3002 \u3000\u3000\u8f6c\u773c\u770b\u541f\u971c\uff0c\u6b63\u5728\u76d8\u5750\u8c03\u606f\uff0c\u9876\u95e8\u4e0a\u70ed\u6c14\u84b8\u817e\uff0c\u541f\u96ea\u662f\u8fc7\u6765\u4eba\uff0c\u660e\u767d\u541f\u971c\u6b63\u503c\u7d27\u8981\u5173\u5934\uff0c\u53d7\u4e0d\u5f97\u4f4f\u4f55\u60ca\u6270\uff0c\u6084\u7136\u884c\u81f3\u77f3\u7a9f\u95e8\u53e3\u5b88\u62a4\u3002 \u3000\u3000\u539f\u6765\uff0c\u541f\u96ea\u6d41\u76ee\u56db\u987e\uff0c\u4e0d\u89c1\u4e03\u5b9d\u548c\u5c1a\uff0c\u60f3\u4ed6\u53bb\u51c6\u5907\u5403\u7528\u4e4b\u7269\uff0c\u6682\u79bb\u6b64\u5730\u3002 \u3000\u3000\u518d\u770b\u6c5f\u67ab\u65f6\uff0c\u53ea\u89c1\u4ed6\u4ef0\u5367\u77f3\u5730\uff0c\u5168\u8eab\u4f3c\u662f\u88ab\u4e00\u7247\u767d\u6c14\u7b3c\u7f69\u7740\uff0c\u4e0d\u7981\u5fae\u5fae\u4e00\u7b11\uff0c\u5fd6\u9053\uff1a \u3000\u3000\u201c\u4ed6\u679c\u7136\u8d85\u8d8a\u6211\u4eec\u5f88\u591a\uff0c\u4f3c\u662f\u5df2\u8fdb\u5165\u4e86\u53e6\u4e00\u91cd\u5883\u754c\u3002\u201d \u3000\u3000\u96ea\u3001\u971c\u4e8c\u5973\u8d44\u8d28\u76f8\u5dee\u4e0d\u8fdc\u3002 \u3000\u3000\u541f\u971c\u7a0d\u900a\u59d0\u59d0\u4e00\u5206\u7075\u6167\uff0c\u4f46\u5374\u591a\u4e86\u541f\u96ea\u4e09\u5206\u5201\u94bb\u3002 \u3000\u3000\u56db\u4e2a\u65f6\u8fb0\u4e4b\u540e\uff0c\u541f\u971c\u4e5f\u633a\u8eab\u7ad9\u8d77\uff0c\u957f\u957f\u5401\u4e00\u53e3\u6c14\uff0c\u7b11\u9053\uff1a\u201c\u59d0\u59d0\u679c\u7136\u80dc\u8fc7\u5c0f\u59b9\uff0c\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u4e5f\u521a\u521a\u8d2f\u901a\uff0c\u2026\u2026\u201d\u541f\u96ea\u63a5\u9053\uff1a\u201c\u54b1\u4eec\u8bb8\u4e0b\u548c\u5c1a\u4f2f\u4f2f\u7684\u767e\u65e5\u7ea6\u671f\uff0c\u4e0d\u77e5\u662f\u5426\u5df2\u7ecf\u8d85\u8fc7\u2026\u2026\u201d \u3000\u3000\u201c\u6ca1\u6709\uff0c\u8fd8\u65e9\u4e86\u4e94\u5929\uff0c\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u7f13\u6b65\u884c\u5165\u77f3\u7a9f\uff0c\u9053\uff1a\u201c\u8fd9\u4e9b\u65f6\u65e5\uff0c\u4f60\u4eec\u996e\u98df\u5927\u51cf\uff0c\u5c24\u5176\u662f\u6700\u540e\u4e8c\u5341\u5929\u6765\uff0c\u9664\u4e86\u559d\u8fc7\u51e0\u676f\u6cc9\u6c34\u5916\uff0c\u5f88\u5c11\u8fdb\u98df\uff0c\u548c\u5c1a\u6b63\u5728\u62c5\u5fc3\u5462\uff01\u201d \u3000\u3000\u201c\u6211\u4eec\u662f\u4e0d\u662f\u7626\u4e86\uff1f\u201d\u541f\u971c\u95ee\u9053\u3002 \u3000\u3000\u201c\u662f\u7626\u4e86\uff0c\u4e0d\u8fc7\uff0c\u4f60\u4eec\u7626\u7684\u500d\u589e\u6e05\u4e3d\uff0c\u548c\u5c1a\u4f2f\u4f2f\u4e5f\u89c9\u7740\u5947\u602a\uff0c\u6d88\u7626\u51e0\u8bb8\uff0c\u4e0d\u89c1\u9732\u9aa8\uff0c\u5509\uff01\u5f53\u771f\u662f\u5929\u751f\u4e3d\u8d28\u96be\u81ea\u5f03\uff0c\u73af\u80a5\u71d5\u7626\u603b\u76f8\u5b9c\u3002\u201d \u3000\u3000\u201c\u591a\u8c22\u548c\u5c1a\u4f2f\u4f2f\u7684\u5938\u8d5e\uff0c\u4f60\u66ff\u6c5f\u5927\u54e5\u62a4\u6cd5\uff0c\u6211\u4eec\u8981\u6d17\u4e2a\u6fa1\u53bb\u3002\u201d \u3000\u3000\u541f\u971c\u7275\u7740\u59d0\u59d0\u4e00\u652f\u624b\uff0c\u5411\u5916\u5954\u53bb\u3002 \u3000\u3000\u5927\u548c\u5c1a\u60f3\u62e6\u963b\uff0c\u5374\u542c\u5230\u6c5f\u67ab\u58f0\u97f3\uff0c\u4f20\u5165\u8033\u9645\uff0c\u9053\uff1a\u201c\u8ba9\u5979\u4eec\u53bb\u5427\uff01\u201d \u3000\u3000\u5927\u548c\u5c1a\u8f6c\u8fc7\u5934\uff0c\u6c5f\u67ab\u5df2\u633a\u8eab\u5750\u8d77\uff0c\u63a5\u9053\uff1a\u201c\u5979\u4eec\u8fd1\u767e\u65e5\u672a\u66fe\u6dcb\u6d74\uff0c\u6c89\u9189\u4e8e\u7ec3\u6c14\u4e4b\u4e2d\uff0c\u8fd8\u53ef\u5fcd\u53d7\uff0c\u73b0\u5728\u4e0d\u8981\u5979\u4eec\u6d17\u4e2a\u6fa1\uff1f\u90a3\u53ef\u662f\u5343\u96be\u4e07\u96be\u7684\u4e8b\u4e86\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\uff0c\u8fd1\u4e24\u65e5\u4e2d\uff0c\u5e38\u89c1\u98de\u9e70\u4f4e\u63a0\u800c\u8fc7\uff0c\u548c\u5c1a\u867d\u7136\u85c9\u8346\u4e1b\u9690\u8eab\uff0c\u6781\u5c3d\u5c0f\u5fc3\uff0c\u4f46\u9e70\u76ee\u7280\u5229\uff0c\u53ef\u4e0d\u6562\u4fdd\u8bc1\u662f\u5426\u5df2\u88ab\u53d1\u73b0\uff1f \u3000\u3000\u4e5f\u65e0\u6cd5\u786e\u5b9a\u90a3\u4e9b\u98de\u9e70\u662f\u5426\u548c\u654c\u4eba\u6709\u5173\uff1f\u4e07\u4e00\u548c\u5c1a\u4e0d\u5e78\u731c\u4e2d\uff0c\u4e24\u4f4d\u59d1\u5a18\u4f1a\u4e0d\u4f1a\u56e0\u6c90\u6d74\u66b4\u9732\u884c\u8e2a\uff1f\u201d \u3000\u3000\u201c\u53ea\u6015\u4f60\u731c\u4e2d\u4e86\u2026\u2026\u201d\u6c5f\u67ab\u7b11\u9053\uff1a\u201c\u6df1\u8c37\u73af\u7ed5\uff0c\u5b64\u5cf0\u9694\u7edd\uff0c\u8fd9\u91cc\u86c7\u9e1f\u7edd\u8ff9\uff0c\u56e0\u4e3a\u65e0\u53ef\u98df\u4e4b\u7269\uff0c\u98de\u9e70\u4f4e\u63a0\uff0c\u53ef\u80fd\u5df2\u53d1\u73b0\u5f62\u8ff9\uff0c\u53ea\u662f\u7075\u72ac\u672a\u81f3\uff0c\u4ed6\u4eec\u8fd8\u65e0\u6cd5\u786e\u5b9a\uff0c\u8fd8\u672a\u641c\u5bfb\u8fc7\u6765\u3002\u2026\u2026\u201d \u3000\u3000\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u662f\u6211\u9690\u533f\u4e0d\u597d\uff0c\u88ab\u98de\u9e70\u53d1\u73b0\u4e86\uff0c\u2026\u2026\u201d\u5927\u548c\u5c1a\u6709\u4e9b\u81ea\u8d23\u4ed6\u8bf4\uff0c\u201c\u6211\u5df2\u5341\u5206\u5c0f\u5fc3\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u7528\u81ea\u8d23\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u4ed6\u4eec\u786e\u6709\u9ad8\u624b\u5728\u6697\u4e2d\u7b56\u5212\uff0c\u5224\u65ad\u51fa\u6211\u4eec\u7684\u53bb\u5411\uff0c\u4e5f\u53ef\u80fd\u60f3\u5230\u4e86\u6211\u4eec\u9690\u533f\u884c\u8e2a\uff0c\u65e8\u5728\u79d8\u5bc6\u7ec3\u6b66\uff0c\u4f46\u4ed6\u4eec\u60f3\u4e0d\u5230\u4e16\u4e0a\u4f1a\u6709\u4f20\u771f\u8bb0\u8fd9\u6837\u4e00\u672c\u5b9d\u5178\uff0c\u4e5f\u60f3\u4e0d\u5230\u4e66\u4e0a\u9762\u6709\u901f\u6210\u7edd\u6280\u7684\u65b9\u6cd5\uff0c\u6240\u4ee5\uff0c\u6ca1\u6709\u5168\u529b\u641c\u89c5\uff0c\u2026\u2026\u201d \u3000\u3000\u201c\u53ef\u662f\uff0c\u96ea\u3001\u971c\u4e8c\u5973\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u8981\u7d27\uff0c\u6211\u76f8\u4fe1\uff0c\u4e8c\u5973\u65e9\u4e5f\u60f3\u5230\u4e86\u8fd9\u4e2a\u60c5\u51b5\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u53ea\u662f\uff0c\u73b0\u5728\u4e0d\u7528\u6015\u4ed6\u4eec\u4e86\u3002\u4ed6\u4eec\u627e\u4e0a\u95e8\u6765\uff0c\u6b63\u597d\u4e00\u8bd5\u6211\u4eec\u7ec3\u6210\u7684\u8eab\u624b\uff01\u201d \u3000\u3000\u5927\u548c\u5c1a\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8bf4\u7684\u662f\uff0c\u54b1\u4eec\u53ea\u8981\u79bb\u5f00\u6b64\u5730\uff0c\u4e5f\u65e0\u6cd5\u9003\u8fc7\u4ed6\u4eec\u7684\u8feb\u8e2a\uff0c\u548c\u5c1a\u5012\u662f\u60f3\u7684\u75f4\u4e86\u2026\u2026\u201d\u8bed\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u4f60\u4eec\u4e09\u4f4d\u7ec3\u7684\u6b66\u529f\uff0c\u6709\u6ca1\u6709\u8f9f\u8c37\u672f\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\uff0c\u4e0d\u8fc7\uff0c\u6c14\u8840\u9006\u884c\uff0c\u4f1a\u4f7f\u4eba\u7684\u80c3\u53e3\u4e0d\u597d\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff0c\u201c\u6240\u4ee5\uff0c\u8fd1\u65e5\u4e2d\u6211\u4eec\u5f88\u5c11\u8fdb\u98df\uff0c\u4f46\u8fd9\u53ef\u80fd\u662f\u6682\u65f6\u73b0\u8c61\uff0c\u8eab\u4f53\u9010\u6e10\u9002\u5e94\u4e4b\u540e\uff0c\u4e5f\u8bb8\u5c31\u6062\u590d\u98df\u6b32\u4e86\u3002\u201d \u3000\u3000\u4e8c\u5973\u6c90\u6d74\u7f62\u5f52\u6765\uff0c\u5a07\u5bb9\u7115\u53d1\u3002 \u3000\u3000\u541f\u971c\u98a6\u8d77\u79c0\u7709\u513f\uff0c\u9053\uff1a\u201c\u6c5f\u5927\u54e5\uff0c\u79bb\u5f00\u8fd9\u91cc\uff0c\u6211\u4eec\u53ef\u4e0d\u53ef\u4ee5\u6362\u56de\u5973\u88c5\uff0c\u9002\u624d\u89c1\u6c34\u4e2d\u5012\u5f71\uff0c\u8fd9\u8eab\u8863\u670d\uff0c\u96be\u770b\u6b7b\u4e86\uff01\u201d \u3000\u3000\u201c\u5f53\u7136\u53ef\u4ee5\uff0c\u6211\u4e5f\u5e0c\u671b\u770b\u4f60\u4eec\u7a7f\u7740\u5973\u88c5\u7684\u5a40\u5a1c\u591a\u59ff\u3002\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u8fd9\u4e9b\u65f6\u65e5\uff0c\u771f\u662f\u96be\u4e3a\u4f60\u4eec\u4e86\uff01\u201d \u3000\u3000\u201c\u6700\u5927\u7684\u82e6\u695a\u662f\u4e0d\u80fd\u5f00\u53e3\uff0c\u2026\u2026\u201d\u53f6\u96ea\u9053\uff1a\u201c\u7537\u88c5\u5973\u97f3\uff0c\u522b\u4eba\u542c\u7684\u5947\u602a\uff0c\u81ea\u5df1\u4e5f\u8bf4\u7684\u522b\u626d\u3002\u201d \u3000\u3000\u6c5f\u67ab\u70b9\u70b9\u5934\uff0c\u6b65\u51fa\u77f3\u7a9f\u3002 \u3000\u3000\u541f\u971c\u4f4e\u58f0\u9053\uff1a\u201c\u59d0\uff0c\u6c5f\u5927\u54e5\uff0c\u8981\u53bb\u90a3\u91cc\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4e5f\u8981\u6d17\u4e2a\u6fa1\u5427\uff01\u201d\u541f\u96ea\u7b11\u9053\uff1a\u201c\u6211\u4eec\u96be\u8fc7\uff0c\u6c5f\u5927\u54e5\u4e5f\u4e0d\u600e\u4e48\u8212\u670d\u3002\u51e0\u4e2a\u6708\u4e0d\u6d17\u6fa1\u554a\uff01\u60f3\u60f3\u770b\uff0c\u975e\u5410\u4e0d\u53ef\uff0c\u771f\u4e0d\u77e5\u9053\u600e\u4e48\u71ac\u8fc7\u6765\u7684\uff1f\u201d \u3000\u3000\u201c\u7ec3\u6c14\u7ec3\u7684\u5934\u660f\u8111\u80c0\u3002\u2026\u2026\u201d\u541f\u971c\u7b11\u9053\uff1a\u201c\u5927\u6982\u60f3\u4e0d\u5230\u8fd9\u7801\u5b50\u4e8b\u4e86\uff0c\u2026\u2026\u201d \u3000\u3000\u8f6c\u671b\u7740\u4e03\u5b9d\u5927\u5e08\uff0c\u63a5\u9053\uff1a\u201c\u548c\u5c1a\u4f2f\u6cca\uff0c\u54b1\u4eec\u51e0\u65f6\u79bb\u5f00\u8fd9\u91cc\uff1f\u201d \u3000\u3000\u201c\u7b49\u4f60\u4eec\u7684\u6c5f\u5927\u54e5\u56de\u6765\u5c31\u8d70\uff0c\u8fd9\u4e00\u7247\u7a77\u5c71\u6076\u6c34\uff0c\u4e0d\u89c1\u4e00\u652f\u8d70\u517d\uff0c\u548c\u5c1a\u767e\u65e5\u672a\u559d\u70b9\u6ef4\u7684\u9152\uff0c\u6050\u6015\u8fde\u9152\u866b\u90fd\u997f\u6b7b\u4e86\uff1f\u201d \u3000\u3000\u201c\u4e0d\u89c1\u8d70\u517d\uff0c\u2026\u2026\u201d\u541f\u96ea\u5fc3\u4e2d\u4e00\u52a8\uff0c\u9053\uff1a\u201c\u90a3\u662f\u89c1\u8fc7\u98de\u79bd\u4e86\uff1f\u201d \u3000\u3000\u201c\u5bf9\uff01\u8fd1\u65e5\u4e2d\uff0c\u5012\u6709\u51e0\u652f\u98de\u9e70\u63a0\u8fc7\u2026\u2026\u201d \u3000\u3000\u201c\u53ef\u662f\u654c\u4eba\u5df2\u7ecf\u53d1\u73b0\u4e86\u6211\u4eec\u884c\u8e2a\uff1f\u2026\u2026\u201d\u541f\u971c\u9053\uff1a\u201c\u6709\u6ca1\u6709\u4eba\u627e\u4e0a\u6765\uff1f\u201d \u3000\u3000\u201c\u8fd8\u6ca1\u6709\u2026\u2026\u201d\u5927\u548c\u5c1a\u7b11\u9053\uff1a\u201c\u4e24\u4f4d\u53ca\u65f6\u529f\u884c\u5706\u6ee1\uff0c\u5c31\u7b97\u4ed6\u4eec\u627e\u4e0a\u6765\uff0c\u4e5f\u65e0\u6240\u754f\u60e7\u4e86\uff01\u201d \u3000 \u7b2c\u4e8c\u5341\u4e00\u56de\u3000\u8c46\u513f\u5e84\u6c5f\u67ab\u5f15\u9ad8\u624b \u3000 \u3000\u3000\u201c\u55ef\uff01\u8fd9\u91cc\u7a77\u5c71\u5e7d\u9759\uff0c\u6076\u6c34\u60ca\u5fc3\uff0c\u5012\u662f\u4e00\u5904\uff0c\u5f88\u597d\u7684\u6740\u622e\u6218\u573a\u2026\u2026\u201d\u541f\u971c\u5a07\u9765\u6cdb\u5a01\uff0c\u51b7\u5389\u5730\u8bf4\uff0c\u201c\u80fd\u5728\u8fd9\u91cc\u5148\u9664\u53bb\u4ed6\u4eec\u4e00\u4e24\u4e2a\u7edd\u9876\u9ad8\u624b\uff0c\u5bf9\u65e5\u540e\u51b3\u6218\uff0c\u4e0d\u65e0\u52a9\u76ca\u2026\u2026\u201d \u3000\u3000\u201c\u5c0f\u971c\u513f\uff0c\u7531\u73b0\u5728\u8d77\uff0c\u968f\u65f6\u90fd\u53ef\u80fd\u9047\u5230\u5f3a\u654c\uff0c\u5c55\u5f00\u4e00\u573a\u6076\u6218\u2026\u2026\u201d\u5927\u548c\u5c1a\u9053\uff1a\u201c\u8bdb\u51f6\u65a9\u6076\u7684\u5927\u8d23\u91cd\u4efb\uff0c\u5168\u51ed\u4ed7\u4f60\u4eec\u5c0f\u592b\u59bb\u4e09\u4e2a\u4eba\u4e86\u3002\u201d \u3000\u3000\u4e24\u4e2a\u5c0f\u72d0\u5973\u867d\u7136\u5927\u65b9\uff0c\u4f46\u9aa4\u7136\u95f4\u542c\u5230\u5c0f\u592b\u59bb\u4e09\u4e2a\u5b57\uff0c\u4e5f\u4e0d\u7981\u8138\u4e0a\u4e00\u70ed\uff0c\u53cc\u988a\u6cdb\u7f9e\u3002 \u3000\u3000\u201c\u548c\u5c1a\u4f2f\u4f2f\uff0c\u6211\u4eec\u4f1a\u5c3d\u5168\u529b\u2026\u2026\u201d\u541f\u96ea\u9053\uff0c \u3000\u3000\u201c\u53ea\u662f\u6211\u4eec\u5bf9\u654c\u7ecf\u9a8c\u4e0d\u8db3\uff0c\u9047\u4e0a\u5f3a\u654c\uff0c\u8fd8\u8981\u4f2f\u4f2f\u4ece\u4e2d\u70b9\u62e8\u3001\u70b9\u62e8\uff0c\u8fd8\u6709\u4e9b\u5206\u5bf8\u4e4b\u95f4\uff0c\u6211\u4eec\u4e5f\u65e0\u6cd5\u628a\u63e1\uff0c\u5148\u8bf7\u4f2f\u4f2f\u6307\u6559\uff01\u201d \u3000\u3000\u201c\u597d\u4e00\u7897\u53c8\u9999\u53c8\u751c\u7684\u8ff7\u6c64\uff0c\u4f60\u8bf4\u5427\uff01\u4ec0\u4e48\u4e8b\uff1f\u548c\u5c1a\u662f\u77e5\u65e0\u4e0d\u8a00\u3002\u201d \u3000\u3000\u201c\u6740\u4eba\u2026\u2026\u201d\u541f\u96ea\u9053\uff1a\u201c\u5929\u72d0\u6b66\u529f\u914d\u4e0a\u5f20\u59e8\u7684\u4e94\u884c\u9041\u672f\uff0c\u8be1\u5728\u51fa\u624b\u4f24\u4eba\uff0c\u4e00\u65e6\u6211\u4eec\u6740\u4eba\u8fc7\u591a\uff0c\u4f1a\u4e0d\u4f1a\u4f7f\u6c5f\u5927\u54e5\u538c\u6076\u6211\u4eec\uff1f\u201d \u3000\u3000\u201c\u662f\u561b\uff01\u5c0f\u5973\u4eba\u8981\u6e29\u6e29\u67d4\u67d4\uff0c\u6e05\u6e05\u7eaf\u7eaf\uff0c\u624d\u80fd\u8ba8\u4eba\u559c\u7231\u2026\u2026\u201d\u541f\u971c\u9053\uff1a\u201c\u4e00\u65e6\u6211\u4eec\u5f04\u5f97\u4e24\u624b\u8840\u8165\uff0c\u5c82\u4e0d\u80fd\u6210\u4e3a\u8ffd\u9b42\u593a\u547d\u7684\u6076\u5a46\u5a18\uff0c\u628a\u672a\u5a5a\u592b\u5a7f\u7ed9\u5413\u8dd1\u4e86\uff1f\u201d \u3000\u3000\u201c\u4f60\u4eec\u542c\u7740\u2026\u2026\u201d\u5927\u548c\u5c1a\u795e\u60c5\u4e25\u8083\u5730\u8bf4\u9053\uff1a\u201c\u6c5f\u67ab\u80a9\u627f\u4e86\u6b66\u6797\u4e09\u5723\u9057\u5fd7\uff0c\u6c5f\u6e56\u662f\u5426\u80fd\u91cd\u89c1\u5149\u660e\uff0c\u662f\u4ed6\u7684\u8d23\u4f4f\uff0c\u4e5f\u662f\u4ed6\u7684\u5fc3\u613f\uff0c\u4f60\u4eec\u5e2e\u52a9\u4ed6\uff0c\u4ed6\u611f\u8c22\u8fd8\u6765\u4e0d\u53ca\uff0c\u54ea\u91cc\u4f1a\u538c\u6076\u4f60\u4eec\u2026\u2026\u201d \u3000\u3000\u201c\u8bf4\u8bf4\u662f\u4e00\u56de\u4e8b\uff0c\u505a\u8d77\u6765\uff0c\u53c8\u662f\u4e00\u56de\u6765\u4e86\u2026\u2026\u201d\u541f\u96ea\u9053\uff1a\u201c\u90a3\u4e9b\u8840\u6dcb\u6dcb\u7684\u573a\u9762\uff0c\u8be1\u5f02\u7684\u51fa\u6ca1\u9690\u73b0\uff0c\u62db\u62db\u593a\u547d\u7684\u624b\u6cd5\uff0c\u770b\u6211\u4e86\u8c01\u90fd\u4f1a\u5fc3\u751f\u538c\u6076\uff0c\u8981\u662f\uff0c\u4e0d\u5982\u6b64\uff0c\u53c8\u4e0d\u8db3\u5e2e\u4ed6\u7684\u5fd9\uff0c\u771f\u7684\u662f\u597d\u96be\u3001\u597d\u96be\u554a\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u7528\u4e3a\u96be\u2026\u2026\u201d\u6c5f\u67ab\u98d8\u7136\u800c\u5165\uff0c\u63a5\u9053\uff1a\u201c\u90a3\u591c\u5728\u7530\u5e9c\u4e00\u6218\uff0c\u6211\u5df2\u7ecf\u52a8\u4e86\u6740\u673a\uff0c\u4e0d\u65bd\u5c55\u9739\u96f3\u624b\u6bb5\uff0c\u5df2\u65e0\u6cd5\u633d\u6551\u6c5f\u6e56\uff0c\u4f60\u4eec\u4e0d\u7528\u62c5\u5fc3\u72b9\u8c6b\uff0c\u6211\u8981\u9886\u5934\u5e26\u7740\u4f60\u4eec\u5927\u5f00\u6740\u6212\u3002\u201d \u3000\u3000\u4e8c\u5973\u5fae\u5fae\u4e00\u7b11\uff0c\u5fe7\u5fc3\u5c3d\u53bb\u3002 \u3000\u3000\u201c\u8d70\u5427\uff01\u54b1\u4eec\u627e\u4e2a\u5e02\u9547\uff0c\u505a\u51e0\u4ef6\u6f02\u4eae\u8863\u773c\uff0c\u597d\u597d\u7684\u5403\u559d\u4e00\u987f\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u96c7\u4e00\u8f86\u8c6a\u534e\u3001\u8212\u9002\u7684\u5927\u9a6c\u8f66\uff0c\u5b89\u5b89\u9038\u9038\u7684\u5750\u8f66\u897f\u4e0b\uff0c\u7b49\u7740\u4ed6\u4eec\u6cbf\u9014\u8ffd\u8e2a\u9001\u6b7b\uff0c\u89c1\u4e00\u4e2a\u5c31\u6740\u4e00\u4e2a\u2026\u2026\u201d \u3000\u3000\u201c\u6c5f\u5927\u54e5\uff0c\u6709\u4e9b\u6b66\u529f\u4e0d\u9ad8\u7684\u4eba\u2026\u2026\u201d\u541f\u96ea\u9053\uff1a\u201c\u7528\u4e0d\u7740\u4e00\u5b9a\u6740\u6b7b\u5427\uff01\u7ed9\u4ed6\u4eec\u4e00\u70b9\u6559\u8bad\uff0c\u5e9f\u4e86\u4ed6\u4eec\u7684\u6b66\u529f\uff0c\u7559\u7ed9\u4ed6\u4eec\u4e00\u6761\u751f\u8def\u5982\u4f55\uff1f\u201d \u3000\u3000\u201c\u597d\uff01\u914c\u60c5\u4e0b\u624b\uff0c\u5404\u4f5c\u4e3b\u5f20\u2026\u2026\u201d\u6c5f\u67ab\u7b11\u9053\uff1a\u201c\u6211\u8981\u5e2e\u4f60\u4eec\u9009\u51e0\u5957\u8863\u6599\uff0c\u505a\u51e0\u5957\u534e\u4e3d\u5408\u8eab\u7684\u8863\u670d\uff0c\u4e00\u5bf9\u5c0f\u4ed9\u5973\uff0c\u4e24\u4e2a\u5927\u715e\u661f\uff0c\u53ea\u6015\u4e0d\u51fa\u4e00\u4e2a\u6708\uff0c\u5c31\u4f1a\u626c\u540d\u6c5f\u6e56\u4e86\u3002\u201d \u3000\u3000\u4e8c\u5973\u542c\u5f97\u9ad8\u5174\uff0c\u8138\u4e0a\u5fae\u7b11\u5982\u82b1\uff0c\u4e0d\u662f\u788d\u7740\u5927\u548c\u5c1a\uff0c\u65e9\u5c31\u7eb5\u4f53\u5165\u6000\uff0c\u7f20\u7740\u6c5f\u67ab\u6492\u5a07\u4e86\u3002 \u3000\u3000\u51fa\u4e86\u5c71\u533a\uff0c\u5927\u548c\u5c1a\u7acb\u523b\u8fa8\u8ba4\u51fa\u65b9\u5411\u4f4d\u7f6e\uff0c\u653e\u6b65\u76f4\u5954\u90d3\u57ce\uff0c\u6295\u4f4f\u5ba2\u6808\uff0c\u4e00\u5207\u7167\u6c5f\u67ab\u5429\u5490\uff0c\u8d76\u505a\u4e86\u5411\u5957\u8863\u670d\uff0c\u53c8\u597d\u597d\u5403\u4e86\u51e0\u987f\uff0c\u4f11\u606f\u4e86\u4e24\u5929\uff0c\u624d\u96c7\u4e86\u4e00\u8f86\u5927\u7bf7\u8f66\uff0c\u4e58\u8f66\u897f\u4e0b\uff0c\u91cd\u8fd4\u5f00\u5c01\u3002 \u3000\u3000\u8fd9\u4e00\u6b21\u5927\u548c\u5c1a\u672a\u4f5c\u8f66\u592b\uff0c\u4f46\u5374\u548c\u8f66\u628a\u5f0f\u5e76\u5750\u7bf7\u8f66\u524d\u9762\uff0c\u4e3a\u4e86\u884c\u52a8\u65b9\u4fbf\uff0c\u5927\u548c\u5c1a\u4ecd\u7136\u6234\u7740\u5047\u53d1\uff0c\u8eab\u7740\u957f\u886b\uff0c\u4e00\u526f\u5927\u7ba1\u5bb6\u7684\u6c14\u52bf\u3002 \u3000\u3000\u4e00\u51fa\u5c71\u533a\uff0c\u6c5f\u67ab\u7b49\u5c31\u53d1\u89c9\u4e86\u8ffd\u8e2a\u7684\u7075\u72ac\u98de\u9e70\uff0c\u5374\u672a\u7406\u776c\u3002 \u3000\u3000\u4f46\u4f4f\u5165\u90d3\u57ce\u5ba2\u6808\u7b2c\u4e8c\u5929\uff0c\u53c8\u53d1\u89c9\u4e86\u8ffd\u8e2a\u7684\u654c\u4eba\u51fa\u73b0\uff0c\u5bf9\u65b9\u4e00\u76f4\u6ca1\u6709\u52a0\u5bb3\u7684\u884c\u52a8\uff0c\u6c5f\u67ab\u7b49\u4e5f\u6545\u4f5c\u4e0d\u77e5\u3002 \u3000\u3000\u8fd9\u4e9b\u5c0f\u811a\u8272\uff0c\u4e0d\u503c\u4e00\u51fb\uff0c\u4e0d\u8fc7\uff0c\u8ffd\u8e29\u8e2a\u8ff9\u7684\u654c\u4eba\u51fa\u73b0\uff0c\u90a3\u662f\u8bf4\u660e\u4e86\u654c\u4eba\u5df2\u51c6\u5907\u6709\u6240\u884c\u52a8\u3002 \u3000\u3000\u53f6\u96ea\u62e8\u5f00\u8f66\u540e\u7684\u84ec\u5e03\uff0c\u63a2\u770b\u4e86\u4e00\u9635\uff0c\u7b11\u9053\uff1a\u201c\u6c5f\u5927\u54e5\uff0c\u8ffd\u8e2a\u7684\u654c\u4eba\u4f3c\u662f\u5df2\u5f00\u59cb\u96c6\u7ed3\uff0c\u597d\u50cf\u51c6\u5907\u51fa\u624b\u4e86\u3002\u201d \u3000\u3000\u53ea\u542c\u5750\u5728\u8f66\u524d\u5927\u548c\u5c1a\u56b7\u9053\uff1a\u201c\u8f66\u628a\u5f0f\u554a\uff01\u524d\u9762\u90a3\u4e00\u7247\u67a3\u6811\u6797\u597d\u5927\u597d\u5927\uff0c\u4f1a\u4e0d\u4f1a\u6709\u622a\u8def\u7684\u5200\u5ba2\u554a\uff01\u201d \u3000\u3000\u201c\u8fd9\u6761\u8def\u9762\u4e0a\uff0c\u8fd8\u7b97\u5e73\u9759\uff0c\u6ca1\u6709\u542c\u8fc7\u6709\u5200\u5ba2\u622a\u8def\u2026\u2026\u201d\u8f66\u628a\u5f0f\u9053\uff1a\u201c\u8f66\u8fc7\u6df1\u6797\u4e09\u5206\u9669\uff0c\u6211\u53ef\u4e0d\u6562\u4fdd\u8bc1\u4ec0\u4e48\uff1f\u8fd9\u4e00\u6bb5\u8def\uff0c\u867d\u6bd4\u8f83\u8352\u50fb\u4e00\u4e9b\uff0c\u4f46\u8fb0\u5149\u8fd8\u65e9\uff0c\u524d\u6709\u884c\u8f66\uff0c\u540e\u6709\u5546\u65c5\uff0c\u5e94\u8be5\u662f\u4e0d\u4f1a\u6709\u4e8b\u624d\u5bf9\uff01\u201d \u3000\u3000\u201c\u4f1a\u4e0d\u4f1a\u6709\u4eba\u4e13\u6253\u6211\u4eec\u8fd9\u8f86\u9a6c\u8f66\u7684\u4e3b\u610f\u5462\uff1f\u2026\u2026\u201d\u5927\u548c\u5c1a\u5728\u6d4b\u8bd5\u8f66\u628a\u5f0f\u3002 \u3000\u3000\u8f66\u628a\u5f0f\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u8fd9\u4e2a\u5c31\u96be\u8bf4\u4e86\uff0c\u8f66\u91cc\u5934\u4e24\u4f4d\u59d1\u5a18\u2026\u2026\u201d\u653e\u4f4e\u58f0\u97f3\u63a5\u9053\uff1a \u3000\u3000\u201c\u5b9e\u5728\u662f\u592a\u6f02\u4eae\u4e86\uff0c\u5982\u679c\u65e9\u88ab\u6b79\u4eba\u76ef\u4e0a\uff0c\u8fd9\u91cc\u5c31\u53ef\u80fd\u662f\u4e0b\u624b\u7684\u5730\u65b9\uff01\u5509\uff01\u6211\u8bf4\u5927\u7ba1\u5bb6\u5440\uff01\u4f60\u5982\u662f\u65e9\u5df2\u5fc3\u91cc\u6709\u6570\uff0c\u5c31\u4e0d\u8be5\u96c7\u6211\u7684\u8f66\u4e86\uff0c\u8fd9\u4e0d\u662f\u9ebb\u5b50\u6539\u79f0\u547c\u5751\u4eba\u54ea\uff01\u201d \u3000\u3000\u201c\u8fd9\u79cd\u4e8b\uff0c\u8c01\u60f3\u78b0\u4e0a\u5462\uff1f\u65f6\u6765\u98ce\u9001\u6ed5\u738b\u9601\uff0c\u8fd0\u80cc\u96f7\u8f70\u5b5d\u798f\u7891\u2026\u2026\u201d\u5927\u548c\u5c1a\u9053\uff1a\u201c\u771f\u7684\u649e\u4e0a\u4e86\uff0c\u4e5f\u53ea\u597d\u8ba4\u547d\uff0c\u4f60\u53ea\u7ba1\u7167\u987e\u597d\u4f60\u7684\u9a6c\u8f66\uff0c\u522b\u8ba9\u5b83\u4eec\u53d7\u60ca\u5954\u9003\uff0c\u4e71\u4e86\u7ae0\u6cd5\u2026\u2026\u201d \u3000\u3000\u201c\u542c\u53e3\u6c14\uff0c\u4f60\u5927\u7ba1\u5bb6\u8fd8\u7ec3\u8fc7\u6b66\u529f\u554a\u2026\u2026\u201d\u8f66\u628a\u5f0f\u9053\uff1a\u201c\u4f46\u53cc\u62f3\u96be\u654c\u56db\u624b\uff0c\u597d\u6c49\u4e0d\u5f53\u4eba\u591a\uff0c\u94b1\u8d22\u662f\u8eab\u5916\u4e4b\u7269\uff0c\u7528\u4e0d\u7740\u62ff\u547d\u53bb\u62fc\u2026\u2026\u201d \u3000\u3000\u201c\u8fd8\u672a\u9047\u4e0a\u622a\u8def\u5200\u5ba2\uff0c\u54b1\u4eec\u5374\u81ea\u5df1\u5728\u5413\u81ea\u5df1\u4e86\uff0c\u8d70\u5427\uff01\u2026\u2026\u201d\u5927\u548c\u5c1a\u7b11\u9053\uff1a\u201c\u662f\u798f\u4e0d\u662f\u7978\uff0c\u662f\u7978\u8eb2\u4e0d\u8fc7\u3002\u201d \u3000\u3000\u8f66\u628a\u5f0f\u4e0d\u518d\u7b54\u8bdd\uff0c\u5374\u5fae\u6536\u7f30\u7ef3\uff0c\u653e\u6162\u4e86\u8f66\u901f\uff0c\u663e\u7136\u662f\u60f3\u7b49\u540e\u9762\u7684\u5546\u65c5\u8d76\u4e0a\u6765\uff0c\u8d70\u5728\u4e00\u8d77\uff0c\u4eba\u591a\u80c6\u58ee\u554a\uff01 \u3000\u3000\u8f66\u8fd1\u67a3\u6797\uff0c\u8f66\u628a\u5f0f\u7a81\u7136\u53f9\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5927\u7ba1\u5bb6\uff0c\u8fd8\u771f\u7684\u88ab\u4f60\u8bf4\u4e2d\u4e86\u3002\u201d \u3000\u3000\u539f\u6765\uff0c\u67a3\u6797\u65c1\u4fa7\u7684\u5927\u9053\u4e0a\uff0c\u65e9\u5df2\u4e00\u6392\u6a2a\u7acb\u56db\u4e2a\u4eba\uff0c\u5168\u8eab\u9ed1\u8863\uff0c\u624b\u6267\u957f\u5200\uff0c\u5200\u5728\u65e5\u5149\u4e0b\u95ea\u7740\u5bd2\u8292\u3002 \u3000\u3000\u5927\u548c\u5c1a\u770b\u770b\u8f66\u5185\u65e0\u52a8\u9759\uff0c\u7a81\u7136\u98de\u8eab\u800c\u8d77\uff0c\u63a0\u843d\u8def\u4e2d\uff0c\u4e00\u6325\u624b\uff0c\u7b11\u9053\uff1a\u201c\u597d\u72d7\u4e0d\u6321\u8def\uff0c\u56db\u4f4d\u8bf7\u8ba9\u8ba9\u9053\u5427\uff01\u201d \u3000\u3000\u56db\u4e2a\u9ed1\u8863\u4eba\u56db\u628a\u5200\u540c\u65f6\u626c\u8d77\uff0c\u4eba\u5374\u5206\u5217\u4e24\u65c1\uff0c\u8ba9\u51fa\u4e86\u5927\u9053\u3002 \u3000\u3000\u8def\u662f\u8ba9\u51fa\u6765\u4e86\u3002 \u3000\u3000\u4f46\u8f66\u628a\u5f0f\u4e0d\u6562\u8d70\u3002 \u3000\u3000\u56e0\u4e3a\u8981\u4ece\u5200\u53e3\u4e0b\u9762\u8d70\u8fc7\u53bb\uff0c\u90a3\u628a\u9ad8\u4e3e\u7684\u9b3c\u5934\u5927\u5200\uff0c\u968f\u65f6\u53ef\u4ee5\u5288\u4e0b\u6765\u3002 \u3000\u3000\u5927\u548c\u5c1a\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8fc7\u5200\u9635\u554a\uff01\u201d \u3000\u3000\u53cc\u624b\u5411\u8896\u4e2d\u4e00\u62e2\uff0c\u5927\u6b65\u5411\u524d\u884c\u53bb\u3002 \u3000\u3000\u90a3\u8f66\u628a\u5f0f\u770b\u7684\u773c\u90fd\u76f4\u4e86\uff0c\u5fc3\u4e2d\u90a3\u4efd\u4f69\u670d\uff0c\u7b80\u76f4\u662f\u4e0d\u7528\u63d0\u4e86\u3002 \u3000\u3000\u8fd9\u4f4d\u5927\u7ba1\u5bb6\u7684\u80c6\u5b50\u597d\u5927\u3002 \u3000\u3000\u90a3\u9ad8\u4e3e\u7684\u5927\u5200\u6ca1\u6709\u65b0\u4e0b\uff0c\u4efb\u51ed\u5927\u548c\u5c1a\u8d70\u8fc7\u53bb\u3002 \u3000\u3000\u8f66\u628a\u5f0f\u6447\u6447\u5934\uff0c\u4ecd\u662f\u4e0d\u6562\u8fc7\u5200\u9635\u3002 \u3000\u3000\u5927\u548c\u5c1a\u660e\u77e5\u9053\u8fd9\u662f\u6c5f\u6e56\u4e0a\u4e00\u79cd\u8fce\u5ba2\u7684\u89c4\u77e9\uff0c\u4f46\u4ed6\u4e0d\u60f3\u52c9\u5f3a\u8f66\u628a\u5f0f\uff0c\u4e5f\u4e0d\u613f\u6c5f\u67ab\u548c\u4e8c\u5973\u5c48\u4ece\u8fd9\u79cd\u89c4\u77e9\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u6c5f\u6e56\u4e0a\u65e9\u5df2\u65e0\u9053\uff0c\u8fd9\u5200\u9635\u8fce\u5ba2\u7684\u89c4\u77e9\uff0c\u5df2\u7ecf\u5931\u4f20\uff0c\u7528\u4e0d\u7740\u6446\u8fd9\u4e2a\u8c31\u513f\u4e86\u3002\u201d \u3000\u3000\u201c\u8bf4\u7684\u4e5f\u662f\u201d\u6797\u6728\u6df1\u5904\uff0c\u4f20\u51fa\u4e00\u4e2a\u6c89\u91cd\u7684\u58f0\u97f3\uff0c\u9053\uff1a\u201c\u65e2\u7136\u4e0d\u613f\u95ef\u8fc7\u5bf9\u9635\uff0c\u8fd9\u4e09\u676f\u538b\u60ca\u9152\uff0c\u8001\u592b\u53ea\u597d\u81ea\u5df1\u559d\u4e86\u3002\u201d \u3000\u3000\u8f66\u5e18\u542f\u52a8\uff0c\u6c5f\u67ab\u98de\u8eab\u800c\u51fa\uff0c\u9053\uff1a\u201c\u5e0c\u671b\u4ed6\u4eec\u7684\u5200\u80fd\u62ff\u7a33\u4e9b\uff0c\u522b\u8981\u5931\u4e86\u4f60\u7684\u9762\u5b50\u3002\u201d \u3000\u3000\u5927\u6b65\u884c\u8fc7\u5200\u9635\uff0c\u56db\u628a\u9b3c\u5934\u5200\u5c3d\u5165\u6c5f\u67ab\u4e4b\u624b\uff0c\u4f46\u56db\u4e2a\u9ed1\u8863\u5927\u6c49\uff0c\u4ecd\u7136\u7ad9\u5728\u539f\u5730\uff0c\u5f62\u6001\u4e0d\u53d8\uff0c\u663e\u7136\u5df2\u88ab\u70b9\u4e2d\u4e86\u7a74\u9053\u3002 \u3000\u3000\u201c\u8f66\u628a\u5f0f\u8d70\u554a\uff01\u201d\u541f\u971c\u5728\u8f66\u84ec\u5185\u8bf4\uff0c\u201c\u4ed6\u4eec\u624b\u4e2d\u65e0\u5200\uff0c\u4eba\u4e5f\u4e0d\u4f1a\u52a8\u4e86\uff0c\u4f60\u8fd8\u6015\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u8f66\u628a\u5f0f\u4e00\u63d0\u7f30\uff0c\u9a6c\u8f66\u8fc5\u901f\u9a70\u8fc7\u3002 \u3000\u3000\u5947\u602a\uff0c\u540e\u9762\u968f\u6765\u7684\u4e03\u516b\u4f4d\u5546\u65c5\u5374\u672a\u8ddf\u8fc7\u6765\u3002 \u3000\u3000\u548c\u56db\u4e2a\u6267\u5200\u5927\u6c49\u4e00\u6837\uff0c\u5446\u5446\u7684\u7ad9\u5728\u539f\u5730\uff0c\u4f46\u624b\u4e2d\u7269\u54c1\uff0c\u80cc\u4e0a\u5305\u88b1\uff0c\u5374\u662f\u4e00\u6837\u4e0d\u5c11\u3002 \u3000\u3000\u597d\u5bb6\u4f19\uff0c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\u554a\uff01 \u3000\u3000\u6211\u62c9\u7684\u4e24\u4f4d\u5973\u5ba2\uff0c\u83ab\u975e\u662f\u4ed9\u5973\u4e0d\u6210\uff1f \u3000\u3000\u4eba\u95f4\u54ea\u6709\u8fd9\u6837\u5a07\u8273\u7684\u59d1\u5a18\u3002 \u3000\u3000\u8fd9\u4f4d\u5927\u7ba1\u5bb6\u548c\u5e74\u8f7b\u4eba\uff0c\u6ca1\u6709\u52a8\u624b\u62ac\u811a\uff0c\u5c31\u5236\u4f4f\u4e86\u52ab\u8def\u7684\u3002 \u3000\u3000\u8f66\u628a\u5f0f\u5fc3\u4e2d\u5fd6\u601d\uff1a\u201c\u4f20\u8bf4\u7684\u4ed9\u5bb6\u4f1a\u7528\u5b9a\u8eab\u6cd5\uff0c\u5927\u6982\u5c31\u662f\u8fd9\u4e2a\u6837\u5b50\u4e86\u3002\u201d \u3000\u3000\u4ed6\u8ba4\u5b9a\u4e8c\u5973\u662f\u4ed9\u5b50\uff0c\u80c6\u6c14\u58ee\u5927\u4e0d\u5c11\uff0c\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u4e24\u4f4d\u4ed9\u5973\uff0c\u84ec\u8f66\u5411\u524d\u8d70\u5462\uff1f\u8fd8\u662f\u8981\u505c\u5728\u8fd9\u91cc\uff1f\u201d \u3000\u3000\u4ed9\u5973\u5982\u82b1\uff0c\u4e0d\u80fd\u78b0\u89e6\uff0c\u4f46\u80fd\u548c\u5979\u4eec\u8bf4\u4e0a\u51e0\u53e5\u8bdd\uff0c\u4e5f\u597d\u6cbe\u70b9\u4ed9\u6c14\u3002 \u3000\u3000\u201c\u7559\u8fd9\u91cc\u542c\u5927\u7ba1\u5bb6\u5429\u5490\uff01\u201d\u541f\u96ea\u4f4e\u58f0\u56de\u7b54\u3002 \u3000\u3000\u597d\u751c\u7f8e\u7684\u58f0\u97f3\uff0c\u8f66\u628a\u5f0f\u542c\u5f97\u5fc3\u82b1\u5927\u5f00\uff0c\u8fde\u5e94\u4e86\u51e0\u4e2a\u662f\u5b57\uff0c\u62ac\u5934\u633a\u80f8\u7684\u5750\u5728\u8f66\u8f95\u524d\u5ea7\u4e0a\uff0c\u5927\u6709\u4ed9\u5973\u5728\u8f66\uff0c\u767e\u90aa\u83ab\u4fb5\u7684\u6c14\u52bf\u3002 \u3000\u3000\u201c\u8bf8\u4f4d\u8bf7\u8d70\uff0c\u6055\u8001\u673d\u4e0d\u9001\u4e86\u3002\u201d \u3000\u3000\u6797\u6728\u4e2d\u53c8\u4f20\u51fa\u4e86\u90a3\u6c89\u91cd\u7684\u58f0\u97f3\uff0c\u4f3c\u662f\u4e00\u4e2a\u8d1f\u91cd\u5343\u65a4\u7684\u4eba\uff0c\u5728\u6781\u5927\u5e84\u529b\u4e0b\u51fa\u53e3\u8bf4\u8bdd\uff0c\u4e00\u53e5\u4e00\u5b57\u7684\u8bfb\u51fa\u6765\u3002 \u3000\u3000\u201c\u9601\u4e0b\u53ef\u5426\u8bf4\u51fa\u59d3\u540d\u2026\u2026\u201d\u5927\u548c\u5c1a\u9053\uff1a\u201c\u7559\u4e2a\u65e5\u540e\u89c1\u9762\u60c5\u8c0a\u3002\u201d \u3000\u3000\u201c\u8001\u673d\u516b\u5366\u95e8\u638c\u95e8\u5f20\u8fdb\uff0c\u2026\u2026\u201d\u53e6\u4e00\u4e2a\u6e05\u6717\u7684\u58f0\u97f3\u63a5\u9053\uff1a\u201c\u6b66\u5f53\u767d\u4e91\uff0c\u795d\u5404\u4f4d\u4e00\u8def\u987a\u98ce\u3002\u201d \u3000\u3000\u201c\u539f\u6765\u662f\u5f20\u638c\u95e8\u548c\u767d\u4e91\u9053\u957f\uff0c\u4e24\u4f4d\u76db\u60c5\u6211\u4eec\u8bb0\u4e0b\u4e86\u3002\u201d \u3000\u3000\u4e00\u62db\u624b\uff0c\u84ec\u8f66\u5411\u524d\u9a70\u53bb\u3002 \u3000\u3000\u6c5f\u67ab\u64a9\u5f00\u84ec\u5e18\u5165\u8f66\uff0c\u5927\u548c\u5c1a\u4ecd\u548c\u8f66\u628a\u5f0f\u5e76\u5750\u8f66\u524d\uff0c\u8f66\u8f6e\u8f6c\u52a8\uff0c\u5411\u524d\u9a70\u53bb\u3002 \u3000\u3000\u201c\u4f60\u8ba4\u8bc6\u4ed6\u4eec\u2026\u2026\u201d\u6c5f\u67ab\u9694\u7740\u5782\u5e18\u95ee\uff0c\u201c\u4ed6\u4eec\u65e9\u5df2\u8bbe\u8ba1\u597d\u7684\u62e6\u622a\uff0c\u600e\u4f1a\u4e2d\u9014\u7f62\u624b\u5462\uff1f\u201d \u3000\u3000\u201c\u58f0\u76f4\u662f\u516b\u5366\u95e8\u7684\u5f20\u638c\u95e8\u7684\u58f0\u97f3\uff0c\u53d1\u97f3\u6c89\u91cd\uff0c\u662f\u8868\u8fbe\u4ed6\u5185\u5fc3\u4e2d\u7684\u75db\u82e6\uff0c\u767d\u4e91\u9053\u957f\u662f\u73b0\u5728\u6b66\u5f53\u638c\u95e8\u7684\u5e08\u5f1f\u2026\u2026\u201d\u5927\u548c\u5c1a\u9053\uff1a\u201c\u8fd9\u4e24\u4e2a\u4eba\u642d\u914d\u8d77\u6765\uff0c\u5728\u6c5f\u6e56\u4e0a\u672c\u6709\u4e00\u5b9a\u4efd\u91cf\uff0c\u53ea\u53ef\u60dc\u9047\u4e0a\u4e86\u4e09\u4f4d\uff0c\u53ea\u770b\u4f60\u6c5f\u8001\u5f1f\u53d6\u5200\u7684\u624b\u6cd5\uff0c\u4ed6\u4eec\u5df2\u81ea\u77e5\u975e\u654c\u3002 \u3000\u3000\u541f\u971c\u59d1\u5a18\u7684\u8c46\u7c92\u6253\u7a74\u624b\u6cd5\uff0c\u66f4\u4f7f\u4ed6\u4eec\u9707\u60ca\uff0c\u4ed6\u4eec\u627f\u8ba4\u4e86\u5931\u8d25\uff0c\u4f46\u4e5f\u8868\u8fbe\u51fa\u4e86\u4ed6\u4eec\u7684\u5e0c\u671b\uff0c\u8fd9\u4e9b\u4eba\u867d\u5728\u538b\u6291\u4e0b\u82df\u5b89\u5077\u751f\uff0c\u5fc3\u4e2d\u4ecd\u5b58\u6709\u7740\u53cd\u6297\u610f\u8bc6\uff0c\u4e00\u65e6\u6709\u673a\u4f1a\uff0c\u4ed6\u4eec\u5c31\u4f1a\u594b\u8d77\u6297\u67dc\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u6211\u5012\u66ff\u4ed6\u4eec\u62c5\u8d77\u5fc3\u6765\uff0c\u4e0d\u6218\u800c\u9000\uff0c\u4f1a\u4e0d\u4f1a\u53d7\u5230\u60e9\u7f5a\uff1f\u2026\u2026\u201d\u541f\u96ea\u9053\uff1a\u201c\u5176\u5b9e\uff0c\u53ea\u8981\u4ed6\u4eec\u65e9\u548c\u4f2f\u4f2f\u901a\u4e2a\u6d88\u606f\uff0c\u6211\u4eec\u4f1a\u624b\u4e0b\u7559\u60c5\uff0c\u8ba9\u4ed6\u4eec\u5168\u8eab\u800c\u9000\u3002\u201d \u3000\u3000\u5927\u548c\u5c1a\u5fc3\u4e2d\u5fd6\u9053\uff1a\u201c\u5947\u602a\uff1f\u541f\u96ea\u8fd9\u4e2b\u5934\u65f6\u6709\u6148\u60b2\u5fc3\u80a0\uff0c\u7a76\u7adf\u662f\u600e\u4e48\u56de\u4e8b\uff0c\u53d7\u4e86\u4ec0\u4e48\u5f71\u54cd\uff1f\u201d \u3000\u3000\u53e3\u4e2d\u5374\u7b11\u9053\uff1a\u201c\u4ed6\u4eec\u5df2\u7ecf\u8fbe\u5230\u76ee\u7684\uff0c\u5012\u4e0d\u7528\u4e3a\u4ed6\u4eec\u5fe7\u5fc3\u4e86\u3002\u201d \u3000\u3000\u201c\u8fbe\u5230\u4e86\u4ec0\u4e48\u76ee\u7684\uff1f\u2026\u2026\u201d\u541f\u971c\u9053\uff1a\u201c\u4ed6\u4eec\u7684\u6b63\u4e3b\u513f\uff0c\u9762\u4e5f\u672a\u9732\u2026\u2026\u201d \u3000\u3000\u201c\u5c0f\u971c\u513f\uff0c\u4f60\u90a3\u7559\u5728\u7a74\u4e2d\u7684\u6c99\u7c92\u4ecd\u5728\uff0c\u5c31\u662f\u4ed6\u4eec\u8981\u547d\u7684\u4f9d\u636e\uff0c\u2026\u2026\u201d\u5927\u548c\u5c1a\u9053\uff1a\u201c\u9700\u77e5\u5f53\u4eca\u6c5f\u6e56\u4e4b\u4e0a\uff0c\u80fd\u5728\u6570\u4e08\u5916\uff0c\u65bd\u5c55\u8c46\u7c92\u6253\u7a74\u624b\u6cd5\uff0c\u4e00\u4e3e\u5236\u773c\u4e86\u516d\u4e03\u4e2a\u4eba\u7684\u9ad8\u624b\u5c48\u6307\u53ef\u6570\u554a\uff01\u201d \u3000\u3000\u201c\u539f\u6765\uff0c\u548c\u5c1a\u4f2f\u4f2f\u65e9\u77e5\u9053\u4e86\uff0c\u2026\u2026\u201d\u541f\u971c\u7b11\u9053\uff1a\u201c\u770b\u6765\uff0c\u767e\u65e5\u82e6\u4fee\uff0c\u8fd8\u771f\u6709\u7528\uff0c\u767e\u65e5\u4e4b\u524d\uff0c\u6211\u53ef\u6ca1\u8fd9\u4e2a\u672c\u9886\u3002\u201d \u3000\u3000\u201c\u4ed6\u4eec\u5df2\u77e5\u96be\u800c\u9000\uff0c\u4e00\u8def\u4e0a\u4e0d\u4f1a\u518d\u6709\u5c0f\u9ebb\u70e6\u4e86\uff0c\u2026\u2026\u201d\u5927\u548c\u5c1a\u9053\uff1a\u201c\u4f46\u5982\u4e00\u65e6\u518d\u6709\u884c\u52a8\uff0c\u5fc5\u5c06\u662f\u60ca\u5929\u52a8\u5730\u7684\u5927\u4e3e\u6765\u72af\uff0c\u654c\u4eba\u5df2\u5c3d\u6089\u6211\u7b49\u5b9e\u529b\uff0c\u4f46\u6211\u4eec\u5374\u8fd8\u4e0d\u77e5\u654c\u4eba\u4e3b\u529b\u4f55\u5728\uff0c\u5df2\u72af\u5175\u6cd5\u5927\u5fcc\uff0c\u4f55\u51b5\u654c\u4f17\u6211\u5be1\u2026\u2026\u201d \u3000\u3000\u201c\u548c\u5c1a\u4f2f\u4f2f\u4e0d\u7528\u62c5\u5fc3\uff0c\u7531\u6211\u4eec\u59d0\u59b9\u8054\u624b\uff0c\u6c5f\u5927\u54e5\u66f4\u662f\u4e00\u67f1\u60ca\u5929\uff0c\u5c31\u7b97\u654c\u4eba\u4f1a\u805a\u4e0a\u767e\u4f4d\u9ad8\u624b\u2026\u2026\u201d\u541f\u971c\u8c6a\u6c14\u5e72\u4e91\u5730\u8bf4\uff0c\u201c\u4e5f\u8981\u6740\u4ed6\u4eec\u4e2a\u7247\u7532\u4e0d\u56de\uff01\u201d \u3000\u3000\u201c\u5509\uff01\u5982\u662f\u5343\u4f4d\u6c5f\u6e56\u4eba\u7269\u5408\u51fb\u5462\uff1f\u2026\u2026\u201d\u5927\u548c\u5c1a\u9053\uff1a\u201c\u7fa4\u72fc\u541e\u864e\uff0c\u4e07\u8681\u5403\u8c61\uff0c\u4f55\u51b5\uff0c\u4ed6\u4eec\u80fd\u591f\u9a71\u4f7f\u7684\u6c5f\u6e56\u4eba\u7269\uff0c\u4f55\u6b62\u5343\u6570\uff0c\u5982\u518d\u52a0\u4e0a\u5251\u9635\u56f0\u654c\uff0c\u767e\u5200\u5408\u51fb\uff0c\u4e09\u4f4d\u80fd\u652f\u6491\u591a\u4e45\u5462\uff1f\u548c\u5c1a\u81ea\u4f5c\u4f30\u7b97\uff0c\u529b\u6218\u4e09\u4e2a\u65f6\u8fb0\u4e4b\u540e\uff0c\u5982\u4e0d\u80fd\u7a0d\u4f5c\u8c03\u606f\uff0c\u7edd\u65e0\u518d\u6218\u4e4b\u80fd\u3002\u201d \u3000\u3000\u201c\u8bf4\u7684\u5bf9\uff0c\u4eba\u7ec8\u662f\u8840\u8089\u4e4b\u8eaf\uff0c\u529b\u80fd\u62d4\u5c71\u4e3e\u9f0e\uff0c\u4e5f\u65e0\u6cd5\u6301\u4e45\u4e0d\u8870\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u5c31\u7b97\u94dc\u6d47\u94c1\u94f8\u7684\u8eab\u5b50\uff0c\u4e5f\u4f1a\u5728\u70c8\u706b\u70d8\u7089\u4e2d\u5316\u4f5c\u6d41\u6c41\u3002\u201d \u3000\u3000\u541f\u971c\u5fc3\u4e2d\u6709\u4e9b\u4e0d\u773c\u6c14\uff0c\u9053\uff1a\u201c\u5c31\u7b97\u6253\u4e0d\u8fc7\u4ed6\u4eec\u4eba\u591a\uff0c\u4f46\u6211\u4eec\u81f3\u5c11\u53ef\u4ee5\u8dd1\u554a\uff01\u201d \u3000\u3000\u201c\u541f\u971c\u59d1\u5a18\uff0c\u771f\u5230\u4e86\u8981\u8dd1\u7684\u65f6\u5019\u2026\u2026\u201d\u5927\u548c\u5c1a\u9053\uff1a\u201c\u6050\u4f2f\u5df2\u7ecf\u65e0\u6cd5\u8eb2\u8fc7\u522b\u4eba\u8ffd\u88ad\u3001\u62e6\u622a\u4e86\u3002\u201d \u3000\u3000\u201c\u4f2f\u4f2f\u8bf4\u7684\u5bf9\uff0c\u53ef\u662f\uff0c\u6211\u4eec\u5e94\u8be5\u4f5c\u4e9b\u4ec0\u4e48\u5462\uff1f\u2026\u2026\u201d\u541f\u96ea\u9053\uff1a\u201c\u96be\u4e0d\u6210\u548c\u8fc7\u53bb\u4e00\u6837\uff1f\u5316\u660e\u5165\u6697\uff0c\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u884c\uff0c\u51b3\u6218\u5f62\u52bf\u5df2\u6210\uff0c\u6211\u4eec\u7ad9\u5728\u660e\u5904\uff0c\u624d\u80fd\u53f7\u53ec\u7fa4\u96c4\uff0c\u5316\u89e3\u654c\u4eba\u5b9e\u529b\uff0c\u53ea\u662f\u5f20\u59d1\u5a18\u624d\u5177\u6709\u5bf9\u654c\u7b56\u7565\uff0c\u5fc5\u987b\u548c\u5979\u4f1a\u4f1a\u2026\u2026\u201d\u5927\u548c\u5c1a\u9053\uff1a\u201c\u7ea2\u82b1\u66f4\u597d\uff0c\u4ecd\u5f97\u7eff\u53f6\u6258\u793e\uff0c\u6211\u4eec\u9700\u8981\u4eba\u624b\uff0c\u6211\u62c5\u5fc3\u7684\u662f\uff0c\u54b1\u4eec\u5df2\u7ecf\u65e0\u6cd5\u6446\u8131\u9e70\u3001\u72ac\u7684\u76d1\u89c6\uff0c\u897f\u8fd4\u957f\u5b89\uff0c\u65c5\u9014\u8c23\u8fdc\uff0c\u5f3a\u654c\u4e2d\u53c8\u6709\u9ad8\u4eba\u8fd0\u7b79\u5e37\u5e44\uff0c\u6050\u5df2\u4e0d\u4f1a\u518d\u7ed9\u6211\u4eec\u805a\u5408\u7684\u673a\u4f1a\uff0c\u4f55\u51b5\uff0c\u6211\u4eec\u5148\u671f\u7684\u540e\u63f4\u529b\u91cf\uff0c\u4ecd\u5728\u5c11\u6797\u5bfa\u4e2d\u3002 \u3000\u3000\u5982\u80fd\u6e05\u9664\u5c11\u6797\u5bfa\u4e2d\u654c\u4eba\uff0c\u6211\u76f8\u4fe1\u53ef\u4f7f\u5c11\u6797\u91cd\u5149\uff0c\u90a3\u65f6\uff0c\u518d\u805a\u96c6\u4eba\u624b\uff0c\u626b\u7a74\u7281\u5ead\u3002\u201d \u3000\u3000\u6c5f\u67ab\u63ed\u5f00\u7bf7\u8f66\u5782\u5e18\uff0c\u9053\uff1a\u201c\u8fdb\u6765\u5427\uff01\u5927\u5bb6\u7814\u5546\u4e00\u4e2a\u65b9\u6cd5\uff0c\u5982\u6cb3\u548c\u56db\u59d1\u4f1a\u5408\u2026\u2026\u201d \u3000\u3000\u5927\u548c\u5c1a\u79fb\u8eab\u8f66\u5185\uff0c\u9053\uff1a\u201c\u548c\u5c1a\u5df2\u7ecf\u60f3\u4e86\u5f88\u4e45\uff0c\u53ea\u6709\u4e09\u4e2a\u529e\u6cd5\u53ef\u884c\uff0c\u4e00\u4e2a\u662f\u6362\u4e58\u5feb\u9a6c\uff0c\u76f4\u5954\u957f\u5b89\uff0c\u4e0d\u7528\u9690\u5bc6\u884c\u8ff9\uff0c\u786c\u884c\u95ef\u5173\u7ed9\u4ed6\u4ee5\u63aa\u624b\u4e0d\u53ca\uff0c\u6cbf\u9014\u867d\u6709\u91cd\u91cd\u62e6\u622a\u53ea\u8981\u4ed6\u4ee5\u65e0\u6cd5\u96c6\u4e2d\u5927\u6279\u4eba\u624b\uff0c\u5c31\u96be\u56f0\u6211\u7b49\u3002\u7b2c\u4e8c\u662f\u6c74\u4eac\u6f5c\u5f15\u884c\u8e2a\uff0c\u6545\u5e03\u7591\u9635\uff0c\u8ba9\u4ed6\u4eec\u6349\u6478\u4e0d\u5b9a\uff0c\u5b9e\u529b\u5206\u6563\uff0c\u54b1\u4eec\u6f5c\u5728\u957f\u5b89\u3002 \u3000\u3000\u7b2c\u4e8c\u662f\uff0c\u5148\u4e0a\u5c11\u6797\u5bfa\uff0c\u6251\u6740\u63a7\u5236\u5c11\u6797\u5bfa\u7684\u654c\u4eba\uff0c\u4ee5\u5c11\u6797\u5bfa\u4f5c\u57fa\u5730\uff0c\u5927\u5f20\u4e49\u65d7\uff0c\u5f20\u59d1\u5a18\u81ea\u4f1a\u95fb\u98ce\u800c\u6765\u3002\u201d \u3000\u3000\u201c\u4e09\u4e2a\u529e\u6cd5\u90fd\u4e0d\u9519\u2026\u2026\u201d\u6c5f\u67ab\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f46\u53ea\u80fd\u62e9\u4e00\u800c\u884c\u2026\u2026\u201d\u653e\u4f4e\u4e86\u58f0\u97f3\uff0c\u63a5\u9053\uff1a\u201c\u5982\u80fd\u622a\u957f\u8865\u77ed\uff0c\u5206\u5934\u8fdb\u884c\uff0c\u5148\u628a\u5f20\u56db\u59d1\u3001\u7f57\u5170\u592b\u5987\u7387\u9886\u7684\u4eba\u624b\u62db\u6765\uff0c\u518d\u5408\u529b\u5bf9\u4ed8\u5c11\u6797\u5bfa\u4e2d\u654c\u4eba\uff0c\u80dc\u7b97\u8f83\u5927\u3002 \u3000\u3000\u4f55\u4e0d\u6697\u4e2d\u6d3e\u4eba\uff0c\u8d76\u8d74\u957f\u5b89\uff0c\u62db\u8bf7\u56db\u59d1\uff0c\u54b1\u4eec\u518d\u65bd\u8bf1\u654c\u4e4b\u6cd5\uff0c\u4f7f\u654c\u4eba\u628a\u6ce8\u610f\u529b\u96c6\u4e2d\u5728\u6211\u4eec\u8eab\u4e0a\u2026\u2026\u201d \u3000\u3000\u201c\u597d\u529e\u6cd5\u2026\u2026\u201d\u5927\u5229\u5c1a\u9053\uff1a\u201c\u6d3e\u4eba\u897f\u4e0b\u957f\u5b89\uff0c\u5927\u6982\u53ef\u884c\uff0c\u95ee\u9898\u662f\uff0c\u4ed6\u5982\u4f55\u80fd\u627e\u5230\u5f20\u59d1\u5a18\u2026\u2026\u201d \u3000\u3000\u201c\u8fd9\u5012\u4e0d\u96be\u2026\u2026\u201d\u541f\u96ea\u9053\uff1a\u201c\u7ed9\u4ed6\u4e00\u4e2a\u9999\u888b\uff0c\u53ef\u4ee5\u9999\u4f20\u5341\u91cc\uff0c\u5f20\u59e8\u95fb\u9999\u5fc5\u81f3\uff0c\u518d\u7531\u6c5f\u5927\u54e5\u4fee\u4e66\u4e00\u5c01\uff0c\u53d6\u4fe1\u5f20\u59e8\u2026\u2026\u201d \u3000\u3000\u5546\u8bae\u8ba1\u5b9a\uff0c\u5927\u548c\u5c1a\u7a81\u7136\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8fd9\u4e00\u6b21\uff0c\u53ea\u6015\u8981\u6740\u4eba\u706d\u53e3\u4e86\u3002\u201d \u3000\u3000\u201c\u4e0d\u5fc5\uff0c\u6211\u5df2\u7528\u5185\u529b\u6697\u4e2d\u5c01\u4f4f\u4e86\u8d76\u8f66\u7684\u4e24\u5904\u7a74\u9053\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u4ed6\u542c\u4e0d\u5230\u6211\u4eec\u7684\u8c08\u8bdd\u3002\u201d \u3000\u3000\u5927\u548c\u5c1a\u7b11\u9053\uff1a\u201c\u597d\uff01\u597d\uff01\u597d\u6781\u4e86\uff01\u201d \u3000\u3000\u597d\u4ec0\u4e48\uff1f\u5927\u548c\u5c1a\u672a\u8bf4\u51fa\u6765\uff0c\u5fc3\u4e2d\u5374\u6697\u4e2d\u5fd6\u9053\uff1a\u201c\u541f\u96ea\u3001\u6c5f\u67ab\uff0c\u679c\u6709\u8fc7\u4eba\u7684\u667a\u6167\uff0c\u4f20\u771f\u8bb0\u4e0a\u90a3\u53e5\u2018\u4e8e\u8fdd\u5929\u673a\uff0c\u6ca6\u52ab\u83ab\u6028\u2019\u5df2\u4f7f\u4e24\u4eba\u751f\u51fa\u8b66\u60d5\uff0c\u5904\u5904\u5fc3\u5b58\u4ec1\u6148\uff0c\u4f3c\u5df2\u5728\u79ef\u4fee\u5584\u529f\u4e86\uff0c\u6211\u8981\u70b9\u5316\u541f\u971c\u51e0\u53e5\uff0c\u8ba9\u4ed6\u4e5f\u6709\u6240\u8b66\u60d5\u624d\u597d\u3002\u201d \u3000\u3000\u201c\u6c5f\u5144\u5f1f\uff0c\u6709\u4e9b\u53d8\u4e86\uff1f\u201d \u3000\u3000\u201c\u6c5f\u5927\u54e5\u672c\u6765\u5c31\u806a\u660e\u554a\uff01\u2026\u2026\u201d\u541f\u9738\u9053\uff1a\u201c\u53ea\u4e0d\u8fc7\u5730\u7f3a\u5c11\u6c5f\u6e56\u5386\u7ec3\uff0c\u6709\u4e9b\u4e8b\u60c5\u65e0\u6cd5\u4e0b\u8bc4\u65ad\u3002\u201d \u3000\u3000\u201c\u548c\u5c1a\u6307\u7684\u4e0d\u662f\u8fd9\u4e2a\uff0c\u2026\u2026\u201d \u3000\u3000\u541f\u971c\u63a5\u9053\uff1a\u201c\u54ea\u4f60\u6307\u7684\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u541f\u971c\uff0c\u4f60\u4e0d\u89c9\u5f97\u6c5f\u67ab\u7684\u6740\u6c14\u6d88\u9000\u4e86\u5f88\u591a\uff0c\u201d\u5927\u548c\u5c1a\u9053\uff1a\u201c\u5904\u5904\u5fc3\u5b58\u4ec1\u5ff5\uff0c\u907f\u514d\u6740\u622e\uff0c\u9002\u624d\u67a3\u6797\u5bf9\u654c\uff0c\u672c\u53ef\u6740\u5165\u7acb\u5a01\uff0c\u4f46\u4f60\u7684\u6c5f\u5927\u54e5\uff0c\u5374\u53ea\u53d6\u4e0b\u4ed6\u4eec\u624b\u4e2d\u4e4b\u5200\u3002\u201d \u3000\u3000\u541f\u971c\u7728\u773c\u4e00\u4e0b\u773c\u775b\uff0c\u9053\uff1a\u201c\u53d6\u5200\u6bd4\u6740\u4eba\u66f4\u96be\u554a\uff01\u201d\u5ffd\u6709\u6240\u609f\uff0c\u5782\u9996\u4e0d\u8a00\u3002 \u3000\u3000\u5927\u548c\u5c1a\u9053\uff1a\u201c\u6211\u53bb\u966a\u8f66\u628a\u5f0f\u5750\uff0c\u5c31\u7b97\u6599\u654c\u6709\u51c6\uff0c\u4e5f\u4e0d\u80fd\u5927\u610f\uff0c\u8fd9\u4e00\u756a\uff0c\u6211\u8981\u4ed4\u7ec6\u77a7\u77a7\u90a3\u4e9b\u76d1\u89c6\u6211\u4eec\u884c\u8e2a\u7684\u98de\u9e70\u3001\u7075\u72ac\uff0c\u6709\u4ec0\u4e48\u7279\u522b\u4e4b\u5904\uff1f\u201d \u3000\u3000\u8fd9\u4e00\u6bb5\u884c\u7a0b\u662f\u8f66\u8d70\u5b98\u9053\uff0c\u660e\u76ee\u5f20\u80c6\uff0c\u76f4\u5165\u6c74\u4eac\uff0c\u6295\u4e86\u5ba2\u6808\uff0c\u9063\u8d70\u8f66\u628a\u5f0f\uff0c\u8fdb\u5165\u5ba2\u623f\uff0c\u541f\u96ea\u7a81\u7136\u63a9\u4e0a\u623f\u95e8\uff0c\u7b11\u9053\uff1a\u201c\u6c5f\u5927\u54e5\uff0c\u5feb\u4e9b\u4eb2\u6211\u4e00\u4e0b\uff0c\u6211\u8981\u544a\u8bc9\u4f60\u4e00\u4e2a\u597d\u6d88\u606f\u3002\u201d \u3000\u3000\u8fd9\u4e9b\u65f6\u65e5\u4e4b\u4e2d\uff0c\u541f\u96ea\u4f3c\u5df2\u6210\u719f\uff0c\u7a33\u5065\u4e86\u4e0d\u5c11\uff0c\u8fd9\u4e00\u523b\uff0c\u53c8\u6062\u590d\u4e86\u8fc7\u53bb\u5a07\u75f4\u5929\u771f\u3002 \u3000\u3000\u6c5f\u67ab\u6014\u4e86\u4e00\u6014\uff0c\u770b\u770b\u5927\u548c\u5c1a\u548c\u541f\u971c\u3002 \u3000\u3000\u5927\u548c\u5c1a\u522b\u8fc7\u5934\u53bb\uff0c\u541f\u971c\u5374\u63a9\u53e3\u800c\u7b11\u3002 \u3000\u3000\u541f\u96ea\u5a9a\u773c\u4e00\u8f6c\uff0c\u9053\uff1a\u201c\u4f60\u4e0d\u4eb2\u6211\uff0c\u7b49\u4e00\u4e0b\uff0c\u6211\u6362\u4e86\u8863\u670d\uff0c\u4e5f\u8bb8\u4f60\u5f88\u4e45\u5c31\u4eb2\u4e0d\u5230\u554a\uff01\u201d\u95ed\u4e0a\u53cc\u76ee\uff0c\u9001\u4e0a\u6a31\u5507\u3002 \u3000\u3000\u6c5f\u67ab\u65e0\u53ef\u5948\u4f55\uff0c\u53ea\u597d\u8f7b\u8f7b\u4eb2\u4e86\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u8bf4\u5427\uff01\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u541f\u96ea\u5f20\u76ee\u5fae\u7b11\u9053\uff1a\u201c\u5f20\u59e8\u5df2\u7ecf\u5230\u4e86\u6c9b\u4eac\uff0c\u6211\u6362\u8fc7\u8863\u670d\u5c31\u53bb\u627e\u5979\u3002\u201d \u3000\u3000\u201c\u5bf9\u5440\uff01\u548c\u5c1a\u65e9\u8be5\u60f3\u5230\u7684\uff0c\u5f20\u59d1\u5a18\u662f\u4f55\u7b49\u4eba\u7269\uff0c\u8fd8\u7b49\u6211\u4eec\u53bb\u627e\uff1f\u201d \u3000\u3000\u201c\u4f60\u600e\u4e48\u77e5\u9053\u7684\uff1f\u201d\u6c5f\u67ab\u5947\u9053\uff1a\u201c\u6211\u4e5f\u5f88\u7559\u5fc3\uff0c\u5374\u4e00\u70b9\u4e5f\u77a7\u4e0d\u51fa\u6765\u3002\u201d \u3000\u3000\u201c\u6211\u95fb\u5230\u4e86\u4e07\u91cc\u98d8\u9999\u2026\u2026\u201d\u541f\u96ea\u9053\uff1a\u201c\u6211\u6362\u4e0a\u7537\u88c5\uff0c\u8ffd\u9999\u5bfb\u8ff9\uff0c\u5f88\u5feb\u5c31\u4f1a\u628a\u5f20\u59e8\u627e\u6765\u3002\u201d \u3000\u3000\u201c\u679c\u7136\u662f\u5929\u5927\u7684\u597d\u6d88\u606f\uff0c\u548c\u5c1a\u8981\u53bb\u559d\u4e00\u676f\uff0c\u5e86\u795d\uff0c\u5e86\u795d\u2026\u2026\u201d\u4ed6\u501f\u6545\u79bb\u5f00\u3002 \u3000\u3000\u541f\u971c\u5df1\u7eb5\u4f53\u5165\u6000\uff0c\u72e0\u72e0\u7684\u4eb2\u4e86\u6c5f\u67ab\u4e00\u9635\uff0c\u53eb\u9053\uff1a\u201c\u59d0\uff0c\u7b49\u7b49\u6211\uff0c\u54b1\u4eec\u4e00\u8d77\u53bb\u3002\u201d \u3000\u3000\u539f\u6765\uff0c\u8fd9\u4e00\u9635\u5de5\u592b\uff0c\u541f\u96ea\u5df2\u6362\u597d\u7537\u88c5\u3002 \u3000\u3000\u4e8c\u5973\u5df2\u8ba4\u5b9a\u4eca\u751f\u4e3a\u6c5f\u67ab\u6240\u6709\uff0c\u66f4\u8863\u89e3\u5e26\uff0c\u65e9\u4e0d\u907f\u5fcc\uff0c\u6709\u673a\u4f1a\u8fd8\u8981\u6545\u5356\u98ce\u60c5\uff0c\u628a\u7f8e\u4e3d\u7684\u80f4\u4f53\uff0c\u5728\u6c5f\u67ab\u9762\u524d\u534a\u63a9\u534a\u906e\u7684\u5c55\u9732\u4e00\u4e0b\u3002 \u3000\u3000\u4e8c\u5973\u6362\u597d\u7537\u88c5\uff0c\u9041\u51fa\u5e97\u5916\uff0c\u5927\u548c\u5c1a\u63d0\u7740\u4e00\u58f6\u9152\uff0c\u7aef\u4e00\u5927\u76d8\u5364\u83dc\uff0c\u884c\u4e86\u8fdb\u6765\uff0c\u7b11\u9053\uff1a\u201c\u53cd\u6b63\u6211\u6234\u7740\u5047\u53d1\uff0c\u6ca1\u6709\u4eba\u4f1a\u8ba4\u51fa\u6211\u662f\u548c\u5c1a\uff0c\u9152\u8089\u7a7f\u80a0\u8fc7\u2026\u2026\u201d \u3000\u3000\u201c\u4f5b\u5728\u5fc3\u4e2d\u5750\u2026\u2026\u201d\u6c5f\u67ab\u63a5\u7740\uff0c\u201c\u6211\u5df2\u542c\u7684\u4f1a\u80cc\u4e86\uff0c\u4eca\u65e5\u5f00\u6000\u7545\u996e\uff0c\u597d\u597d\u7684\u559d\u51e0\u676f\u5427\uff01\u201d \u3000\u3000\u5f20\u56db\u59d1\u6765\u7684\u5f88\u5feb\uff0c\u4e24\u4e2a\u4eba\u7b2c\u4e8c\u58f6\u8fd8\u672a\u559d\u5b8c\uff0c\u5f20\u56db\u59d1\u5df2\u63a8\u95e8\u800c\u4eba\u3001\u9053\uff1a\u201c\u4e24\u4f4d\u5f88\u6d88\u9065\u554a\uff01\u201d \u3000\u3000\u201c\u51a4\u6789\u554a\uff01\u548c\u5c1a\u53d7\u7684\u82e6\uff0c\u4f60\u5f20\u59d1\u5a18\u6ca1\u6709\u770b\u5230\uff0c\u90a3\u53ef\u662f\u63d0\u5fc3\u540a\u80c6\u4e00\u767e\u5929\u554a\uff01\u9152\u4e5f\u4e0d\u80fd\u559d\uff0c\u6fa1\u4e5f\u4e0d\u80fd\u6d17\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\uff0c\u8fd9\u4e00\u6b21\u662f\u771f\u82e6\u4e86\u4f60\u9152\u8089\u548c\u5c1a\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u603b\u7b97\u529f\u5fb7\u5706\u6ee1\u3002\u201d \u3000\u3000\u201c\u541f\u96ea\uff0c\u541f\u971c\u5462\uff1f\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u6ca1\u6709\u8ddf\u5927\u59d0\u4e00\u8d77\u56de\u6765\uff1f\u201d \u3000\u3000\u201c\u5979\u4eec\u5728\u7167\u987e\u7f57\u5170\u2026\u2026\u201d\u5f20\u56db\u59d1\u53f9\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8fd9\u4e00\u6b21\uff0c\u5982\u4e0d\u662f\u9752\u51e4\u5e2e\u5fd9\uff0c\u6050\u6015\u957f\u5b89\u4e00\u522b\u6c38\u51b3\u4e86\uff0c\u7f57\u5170\u529b\u6218\u53d7\u4f24\u3002\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u8138\u8272\u4e00\u53d8\uff0c\u9053\uff1a\u201c\u4f24\u52bf\u5982\u4f55\uff1f\u201d \u3000\u3000\u5f20\u56db\u59d1\u9053\uff1a\u201c\u4fa5\u5929\u4e4b\u5e78\uff0c\u4fdd\u5168\u4e86\u8eab\u8eaf\uff0c\u672a\u6210\u6b8b\u5e9f\uff0c\u4f46\u79e6\u516b\u516c\u548c\u4e00\u6279\u6b66\u529f\u8f83\u5dee\u7684\u540c\u9053\uff0c\u5927\u90fd\u6218\u6b7b\u2026\u2026\u201d \u3000\u3000\u201c\u8001\u53eb\u5316\u5462\uff1f\u2026\u2026\u201d\u5927\u548c\u5c1a\u6025\u6025\u63a5\u9053\uff1a\u201c\u6b7b\u4e86\u6ca1\u6709\uff1f\u201d \u3000\u3000\u201c\u4ed6\u8fd8\u597d\uff0c\u53ea\u53d7\u4e86\u4e00\u4e9b\u76ae\u8089\u4e4b\u4f24\uff0c\u2026\u2026\u201d\u6da8\u56db\u59d1\u9053\uff0c\u201c\u80e1\u840d\u3001\u6bb5\u4e5d\uff0c\u4f24\u52bf\u5df2\u6108\uff0c\u9093\u98de\u548c\u5201\u9e4f\uff0c\u4e5f\u6302\u4e86\u5f69\uff0c\u6b27\u9633\u662d\u548c\u4e94\u4e2a\u5f1f\u5b50\uff0c\u53d1\u6325\u51fa\u5251\u9635\u5a01\u529b\uff0c\u62d2\u6321\u4f4f\u4e00\u65b9\u653b\u52bf\u3002 \u3000\u3000\u80e1\u5143\u548c\u90ed\u5929\u540c\u5c0f\u4f24\u5df2\u6108\uff0c\u6885\u82b1\u65e0\u6059\uff0c\u9752\u51e4\u6b66\u529f\u4e4b\u9ad8\uff0c\u5927\u51fa\u6211\u610f\u5916\uff0c\u72ec\u6218\u5929\u9a6c\u5802\u56db\u5927\u9ad8\u624b\u3002 \u3000\u3000\u540e\u6765\uff0c\u5929\u540e\u5bab\u7684\u4e09\u5bab\u4e3b\u8d76\u5230\uff0c\u9752\u51e4\u529b\u6218\u4e0d\u654c\uff0c\u738b\u5b37\u3001\u674e\u5b37\u820d\u8eab\u6551\u4e3b\uff0c\u867d\u7136\u4fdd\u4f4f\u4e86\u9752\u51e4\uff0c\u4f46\u4e24\u4eba\u5374\u53cc\u53cc\u6218\u6b7b\u3002 \u3000\u3000\u4e03\u5341\u4e5d\u4f4d\u6b66\u6797\u540c\u9053\u4e2d\uff0c\u53ea\u6709\u94c1\u62c2\u5927\u5e08\u3001\u9752\u9e64\u9053\u957f\uff0c\u7a81\u51fa\u56f4\u56f0\uff0c\u968f\u6765\u6c74\u4eac\uff0c\u6211\u4eec\u4e5f\u53ea\u5269\u4e0b\u8fd9\u4e9b\u4eba\u4e86\uff0c\u5176\u4ed6\u7684\u4e0d\u662f\u6218\u6b7b\uff0c\u5c31\u662f\u91cd\u4f24\uff0c\u6211\u65e0\u6cd5\u6551\u4ed6\u4eec\u51fa\u6765\uff0c\u5b9e\u5728\u5185\u548e\uff0c\u6709\u4e9b\u6127\u5bf9\u4e24\u4f4d\u4e86\uff01\u201d \u3000\u3000\u201c\u5927\u59d0\uff0c\u4e0d\u8981\u81ea\u8d23\uff0c\u6211\u4eec\u5168\u529b\u4ee5\u8d74\uff0c\u6210\u8d25\u5c31\u542c\u51ed\u5929\u547d\u4e86\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u767e\u65e5\u4e4b\u524d\u5728\u6c9b\u4eac\uff0c\u6211\u4e5f\u9047\u4e0a\u4e86\u771f\u6b63\u7684\u9ad8\u624b\u5f3a\u654c\uff0c\u62fc\u8fc7\u767e\u62db\uff0c\u5c0f\u5f1f\u624d\u5e78\u80dc\u4e00\u7740\uff0c\u654c\u52bf\u7684\u58ee\u5927\uff0c\u80fd\u4e00\u4e3e\u640f\u6740\u4e86\u5c11\u6797\u5bfa\u4e24\u4ee3\u9ad8\u50e7\uff0c\u5927\u59d0\u4fdd\u5168\u4e86\u8fd9\u4e48\u591a\u4eba\u7a81\u51fa\u91cd\u56f4\uff0c\u5df2\u662f\u96be\u80fd\u53ef\u8d35\u4e86\uff01\u201d \u3000\u3000\u201c\u4fdd\u5168\u7684\u53c8\u5168\u662f\u7cbe\u9510\u2026\u2026\u201d\u5927\u548c\u5c1a\u9053\uff1a\u201c\u5f20\u59d1\u5a18\uff0c\u4f60\u8db3\u4ee5\u81ea\u8c6a\u4e86\u3002\u201d \u3000\u3000\u201c\u9752\u51e4\u53d7\u4f24\u6ca1\u6709\uff1f\u6c5f\u67ab\u4f4e\u58f0\u7684\u95ee\u3002 \u3000\u3000\u201c\u6ca1\u6709\uff0c\u5979\u5df2\u6218\u81f3\u529b\u5c3d\uff0c\u6655\u5012\u5f53\u573a\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u903c\u7684\u6211\u653e\u51fa\u4e86\u5357\u8352\u8840\u8747\uff0c\u4f24\u4e86\u4ed6\u4eec\u4e0d\u5c11\u4eba\uff0c\u624d\u628a\u4ed6\u4eec\u903c\u8fdf\uff01\u201d \u3000\u3000\u201c\u53d6\u4e4b\u4e8e\u654c\uff0c\u7528\u4e4b\u4e8e\u654c\uff0c\u548c\u5c1a\u4e00\u76f4\u60f3\u4e0d\u901a\uff0c\u65e2\u5df2\u9677\u5165\u7edd\u5883\uff0c\u53c8\u80fd\u7a81\u56f4\u800c\u51fa\uff0c\u85cf\u5947\u5175\u4e8e\u4e00\u7f50\u4e4b\u4e2d\uff0c\u5f20\u59d1\u5a18\u679c\u7136\u9ad8\u660e\uff0c\u548c\u5c1a\u8fd8\u6709\u4e00\u70b9\u60f3\u4e0d\u901a\uff0c\u6bd2\u8747\u4e3a\u4ec0\u4e48\u4e0d\u4f24\u81ea\u5df1\u4eba\uff1f\u201d \u3000\u3000\u201c\u9047\u654c\u4e4b\u65f6\uff0c\u6211\u5df2\u8ba9\u4ed6\u4eec\u64e6\u4e0a\u4e86\u96c4\u9ec4\u836f\u9152\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u672c\u662f\u4e0a\u6b21\u5907\u4ee5\u5fa1\u86ca\u672a\u7528\uff0c\u8fd9\u6b21\u5374\u6d3e\u4e0a\u4e86\u7528\u573a\uff0c\u800c\u65e6\uff0c\u6bd2\u8747\u56de\u907f\uff0c\u5341\u5206\u6709\u6548\uff0c\u5982\u975e\u6709\u6b64\u4e00\u7740\uff0e\u53ea\u6015\u5df2\u5168\u519b\u8986\u6ca1\uff0c\u6211\u4e5f\u89c1\u4e0d\u7740\u4f60\u4eec\u4e86\u3002\u201d \u3000\u3000\u201c\u5927\u59d0\u4f4f\u5728\u4f55\u5904\uff1f\u5c0f\u5f1f\u60f3\u53bb\u63a2\u671b\u4e00\u4e0b\u53d7\u4f24\u7684\u957f\u4eb2\u3001\u6545\u53cb\u3002\u201d \u3000\u3000\u201c\u4f4f\u7684\u79bb\u6b64\u4e0d\u8fdc\uff0c\u4ed6\u4eec\u7528\u98de\u9e70\u3001\u7075\u72ac\u76d1\u89c6\uff0c\u6211\u4eec\u65e0\u6cd5\u9003\u907f\u2026\u2026\u201d \u3000\u3000\u5f20\u56db\u59d1\u9053\uff0c\u6211\u53d1\u89c9\u4ed6\u4eec\u552f\u4e00\u987e\u5fcc\u7684\u662f\u6015\u5f15\u8d77\u5b98\u5e9c\u7684\u6ce8\u610f\uff0c\u8c03\u52a8\u5927\u519b\u56f4\u527f\uff0c\u6240\u4ee5\uff0c\u6211\u9009\u62e9\u57ce\u5185\u4e00\u5904\u5ba2\u6808\uff0c\u90a3\u5730\u65b9\u5f88\u70ed\u95f9\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u8fd9\u4e24\u5929\uff0c\u6709\u4e9b\u4e0d\u5bf9\u4e86\u3002 \u3000\u3000\u654c\u4eba\u626e\u4f5c\u5546\u65c5\uff0c\u4efb\u5165\u5ba2\u6808\uff0c\u5927\u6709\u7acb\u523b\u4e0b\u624b\u7684\u7528\u5fc3\uff0c\u5e78\u597d\u4f60\u4eec\u53ca\u65f6\u8d76\u5230\uff0c\u5c31\u6211\u89c2\u5bdf\u6240\u5f97\uff0c\u4f4f\u5165\u5ba2\u6808\u7684\u5168\u662f\u9ad8\u624b\uff0c\u81f3\u5c11\u6709\u5341\u4eba\u4ee5\u4e0a\u3002\u201d \u3000\u3000\u201c\u9014\u4e2d\u67a3\u6797\u4e00\u6218\uff0c\u6c5f\u5144\u5f1f\u4e00\u4e3e\u593a\u4e86\u654c\u4eba\u624b\u4e2d\u4e4b\u5200\u2026\u2026\u201d\u5927\u548c\u5c1a\u9053\uff1a\u201c\u8fd9\u5927\u6982\u5c31\u662f\u4ed6\u4eec\u4e0d\u518d\u7b49\u5019\u7684\u539f\u56e0\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u5fae\u5fae\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u4f60\u4eec\u4e5f\u8fc7\u53bb\u4f4f\u5427\uff01\u4f46\u53ea\u6709\u4e24\u4e2a\u623f\u95f4\u4e86\uff0c\u4f60\u4eec\u4e24\u4e2a\u5404\u4f4f\u4e00\u95f4\uff0c\u96ea\u3001\u971c\u4e8c\u5973\u8ddf\u9752\u51e4\u3001\u6885\u82b1\u6324\u4e00\u4e0b\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u6211\u4eec\u8fd9\u5c31\u8fc7\u53bb\u3002\u201d\u6c5f\u67ab\u7ad9\u8d77\u8eab\u5b50\u3002 \u3000\u3000\u5f20\u56db\u59d1\u4e00\u628a\u6293\u4f4f\u4e86\u6c5f\u67ab\u7684\u624b\uff0c\u9053\uff1a\u201c\u4e0d\u7528\u6025\u5728\u4e00\u65f6\uff0c\u5927\u767d\u5929\uff0c\u4ed6\u4eec\u4f1a\u6709\u6240\u907f\u5fcc\u3002\u4f55\u51b5\uff0c\u8fd8\u6709\u4e24\u4e2a\u5c0f\u72d0\u5973\u7559\u5728\u90a3\u91cc\uff0c\u8fd9\u5730\u65b9\u5f88\u6e05\u51c0\uff0c\u6211\u4eec\u7814\u5546\u4e00\u4e0b\u5bf9\u654c\u4e4b\u7b56\u3002\u201d \u3000\u3000\u5979\u8868\u8fbe\u51fa\u60f3\u5ff5\u7684\u795e\u8272\uff0c\u53cc\u76ee\u4e2d\u60c5\u7231\u65e0\u9650\uff0c\u4e00\u767e\u591a\u5929\u4e0d\u89c1\uff0c\u4e00\u65e6\u91cd\u9022\uff0c\u5927\u6709\u5c0f\u522b\u80dc\u65b0\u5a5a\u7684\u611f\u89c9\u3002 \u3000\u3000\u5927\u548c\u5c1a\u8bc6\u8da3\u7684\u5f88\uff0c\u7ad9\u8d77\u8eab\u5b50\u9053\uff1a\u201c\u6211\u53bb\u8981\u51e0\u4e2a\u7092\u83dc\uff0c\u6dfb\u4e24\u58f6\u9152\uff0c\u54b1\u4eec\u8fb9\u559d\u8fb9\u804a\u3002\u201d\u8d77\u8eab\u79bb\u53bb\uff0c\u8d70\u5c31\u8d70\u5427\uff01\u987a\u624b\u53c8\u5e26\u4e0a\u623f\u95e8\u3002 \u3000\u3000\u4ed6\u53d1\u89c9\u4e86\u4e00\u5411\u5728\u4eba\u524d\u77dc\u6301\u7684\u5f20\u56db\u59d1\uff0c\u5c0f\u522b\u767e\u65e5\u4e4b\u540e\uff0c\u7adf\u7136\u4e5f\u65e0\u6cd5\u628a\u6301\u81ea\u5df1\u3002 \u3000\u3000\u201c\u5144\u5f1f\uff0c\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u6211\u5f88\u60f3\u62d4\u6167\u5251\u65b0\u60c5\u4e1d\uff0c\u53ef\u662f\uff0c\u6211\u6050\u6015\u505a\u4e0d\u5230\u4e86\uff0c\u8fd9\u51e0\u65e5\u597d\u60f3\u5ff5\u4f60\u3002\u201d \u3000\u3000\u201c\u5c0f\u5f1f\u4e5f\u662f\uff0c\u5927\u59d0\uff0c\u96ea\u3001\u971c\u4e8c\u5973\u548c\u9752\u51e4\u3001\u6885\u82b1\uff0c\u90fd\u65e0\u6cd5\u4ee3\u66ff\u5927\u59d0\uff0c\u4e0d\u7ba1\u65e5\u540e\u60c5\u51b5\u5982\u4f55\uff0c\u6c42\u5927\u59d0\u4e0d\u8981\u5f03\u6211\u800c\u53bb\u3002\u201d \u3000\u3000\u201c\u8fd9\u600e\u4e48\u5f97\u4e86\u554a\uff1f\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u5f53\u771f\u662f\u60c5\u7f18\u80fd\u7edd\uff0c\u5b7d\u7f18\u96be\u65ad\uff0c\u65e5\u540e\uff0c\u53c8\u4f1a\u662f\u600e\u4e48\u6837\u4e00\u4e2a\u7ed3\u679c\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u4e0d\u7ba1\u4ec0\u4e48\u7ed3\u679c\uff0c\u5927\u59d0\u90fd\u4e0d\u80fd\u79bb\u5f00\u6211\uff0c\u541f\u96ea\u3001\u541f\u971c\u671f\u5f85\u5927\u59d0\u7559\u4e0b\u7684\u5fc3\u60c5\uff0c\u7edd\u4e0d\u5728\u6211\u4e4b\u4e0b\uff0c\u9752\u51e4\u3001\u6885\u82b1\uff0c\u6211\u76f8\u4fe1\u4e5f\u9700\u8981\u5927\u59d0\u7167\u987e\u2026\u2026\u201d\u987a\u624b\u628a\u5f20\u56db\u59d1\u62e5\u5165\u6000\u4e2d\u3002 \u3000\u3000\u5f20\u56db\u59d1\u62fc\u4e0a\u4e86\uff0c\u4e5f\u4e0d\u63a8\u62d2\uff0c\u4efb\u51ed\u6c5f\u67ab\u6e29\u5b58\u4eab\u53d7\u3002 \u3000\u3000\u201c\u9152\u83dc\u5230\u4e86\u2026\u2026\u201d\u5927\u548c\u5c1a\u9ad8\u56b7\u7740\u63a8\u95e8\u800c\u5165\u3002 \u3000\u3000\u5f20\u56db\u59d1\u522b\u8fc7\u8138\u53bb\uff0c\u624b\u7406\u7740\u4e71\u53d1\u6a2a\u9497\uff0c\u6c5f\u67ab\u4e5f\u7528\u624b\u5e15\u64e6\u62ed\u7740\u6b8b\u4f59\u5728\u5634\u4e0a\u7684\u80ed\u8102\u3002 \u3000\u3000\u5927\u548c\u5c1a\u5df2\u5230\u4e86\u56db\u5927\u7686\u7a7a\u7684\u5883\u754c\uff0c\u624b\u6367\u6728\u76d8\uff0c\u76d8\u4e2d\u662f\u56db\u6837\u7092\u83dc\u3001\u4e24\u58f6\u9152\uff0c\u89c6\u800c\u4e0d\u89c1\u7684\u653e\u4e0a\u684c\u5b50\uff0c\u53c8\u6446\u4e0a\u4e00\u526f\u676f\u7b77\uff0c\u9053\uff1a\u201c\u559d\u9152\u554a\uff01\u201d \u3000\u3000\u5f20\u56db\u59d1\u5fae\u73b0\u7f9e\u6001\uff0c\u9053\uff1a\u201c\u5927\u548c\u5c1a\uff0c\u4f60\u7b49\u7740\u56db\u6837\u83dc\u4e00\u9f50\u7092\u597d\uff0c\u81ea\u5df1\u62ff\u8fc7\u6765\uff1f\u201d \u3000\u3000\u201c\u541b\u5b50\u6709\u6210\u4eba\u4e4b\u7f8e\u554a\uff01\u6211\u548c\u5c1a\u773c\u775b\u6709\u6bdb\u75c5\uff0c\u5e94\u8be5\u770b\u4e0d\u5230\u7684\uff0c\u80fd\u89c6\u800c\u4e0d\u89c1\uff0c\u5e97\u5c0f\u4e8c\u6015\u5c31\u6ca1\u6709\u548c\u5c1a\u8fd9\u4e2a\u672c\u9886\u4e86\u3002\u201d \u3000\u3000\u201c\u5927\u59d0\uff01\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u5927\u548c\u5c1a\u773c\u89c2\u56db\u9762\u3001\u8033\u542c\u516b\u65b9\uff0c\u4ed6\u5fc3\u4e2d\u660e\u767d\u7684\u5f88\u3002\u201d \u3000\u3000\u201c\u5176\u5b9e\uff0c\u7f57\u5170\u4e5f\u77e5\u9053\uff0c\u2026\u2026\u201d\u5f20\u56db\u59d1\u7f9e\u6029\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5979\u7adf\u7136\u529d\u6211\u966a\u7740\u4e24\u4e2a\u5c0f\u72d0\u5973\u548c\u4f60\u4e00\u8d77\u751f\u6d3b\uff0c\u8fd8\u60f3\u51fa\u4e00\u756a\u9053\u7406\u8bf4\uff0c\u4e24\u4e2a\u4e2b\u5934\u5c11\u4e0d\u66f4\u4e8b\uff0c\u6211\u5e94\u8be5\u7559\u4f60\u8eab\u8fb9\uff0c\u7ba1\u6559\u5979\u4eec\u3002 \u3000\u3000\u5929\u72d0\u6b66\u529f\uff0c\u771f\u6709\u70b9\u90aa\u6c14\uff0c\u5929\u4e0b\u7adf\u6709\u8fd9\u6837\u7684\u4e08\u6bcd\u5a18\uff0c\u6211\u771f\u7684\u4e0d\u660e\u767d\uff0c\u5979\u662f\u8bbd\u523a\u6211\uff0c\u8fd8\u662f\u6015\u5c11\u4e86\u6211\u8fd9\u4e2a\u670b\u53cb\uff1f\u201d \u3000\u3000\u201c\u548c\u5c1a\u6562\u4fdd\u8bc1\uff0c\u5979\u51fa\u81ea\u8877\u8bda\uff0c\u6211\u662f\u65c1\u89c2\u8005\u6e05\uff0c\u4e0d\u8fc7\uff0c\u6c5f\u8001\u5f1f\u628a\u2018\u60d1\u5fc3\u672f\u2019\u7528\u4e8e\u5bf9\u654c\u4e4b\u4e0a\uff0c\u8fd9\u79cd\u82b1\u56e2\u9526\u7c07\uff0c\u7f8e\u5973\u73af\u7ed5\u7684\u7ed3\u679c\uff0c\u65e9\u5df2\u79cd\u56e0\uff0c\u5929\u72d0\u592b\u4eba\u5728\u4f20\u6388\u4ed6\u8fd9\u79cd\u5947\u672f\u65f6\uff0c\u5df2\u7ecf\u6ce8\u5b9a\uff0c\u95ee\u9898\u5728\u5982\u4f55\u4e86\u5c40\u2026\u2026\u201d \u3000\u3000\u5927\u548c\u5c1a\u7b11\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u96be\u9053\u771f\u7684\u8981\u5efa\u4e00\u5ea7\u5927\u5bab\u9662\uff0c\u5bb9\u7eb3\u4e0b\u4e09\u5bab\u516d\u9662\u4e03\u5341\u4e8c\u5983\uff1f\u201d \u3000\u3000\u201c\u6211\u4e5f\u60f3\u8fc7\u8fd9\u4ef6\u4e8b\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u95ee\u9898\u51fa\u5728\u2018\u60d1\u5fc3\u672f\u2019\u4e0d\u662f\u4e00\u822c\u7684\u5a9a\u529f\u3001\u78b0\u4e0a\u4ed6\u7684\u5973\u5b50\uff0c\u4e00\u65e6\u6cbe\u8eab\uff0c\u5c31\u77e2\u5fd7\u96be\u5fd8\uff0c\u53ea\u6c42\u514b\u654c\u81f4\u80dc\uff0c\u8bf1\u654c\u6211\u7528\uff0c\u4e0d\u60f3\u7ed3\u679c\uff0c\u4e5f\u5c31\u7f62\u4e86\u3002 \u3000\u3000\u4e00\u65e6\u60f3\u8d77\u6765\uff0c\u5c31\u70e6\u607c\u7684\u5f88\uff0c\u9664\u4e86\u96ea\u3001\u971c\u4e24\u4e2a\u4e2b\u5934\u5fc3\u65e0\u6b32\u5ff5\u4e4b\u5916\uff0c\u522b\u7684\u4eba\uff0c\u90fd\u662f\u60c5\u6b32\u517c\u6709\uff0c\u773c\u4e0b\u8fd9\u51e0\u4e2a\u4eba\uff0c\u4e5f\u8bb8\u53ef\u5e73\u5b89\u65e0\u4e8b\uff0c\u518d\u591a\u4e86\uff0c\u5c31\u4e0d\u597d\u76f8\u5904\u4e86\uff0c\u4e00\u65e6\u6253\u7ffb\u918b\u575b\u5b50\uff0c\u90a3\u5c31\u4f1a\u5f15\u8d77\u65e0\u7a77\u7684\u98ce\u6ce2\u4e86\u3002\u201d \u3000\u3000\u201c\u5c31\u767e\u660e\u8001\u65b9\u4e08\u7684\u4f20\u544a\u2026\u2026\u201d\u6c5f\u673a\u9053\uff1a\u201c\u771f\u6b63\u5f3a\u654c\uff0c\u81f3\u5c11\u6709\u516d\u4e2a\u4eba\uff0c\u5f53\u65f6\uff0c\u5c0f\u5f1f\u5fc3\u4e2d\u8fd8\u6709\u4e9b\u4e0d\u500d\uff0c\u8001\u5b9e\u8bf4\uff0c\u7530\u5e9c\u4e00\u6218\u4e4b\u524d\uff0c\u6211\u662f\u5750\u4e95\u89c2\u5929\uff0c\u4e0d\u76f8\u4fe1\u5f53\u4eca\u6b66\u6797\u4e4b\u4e2d\uff0c\u771f\u6709\u80fd\u548c\u6211\u6253\u4e0a\u5341\u4e2a\u56de\u5408\u7684\u9ad8\u624b\uff0c\u4f46\u90a3\u4e00\u6218\u4e4b\u540e\uff0c\u6211\u7684\u5fc3\u90fd\u51bb\u4e86\u3002 \u3000\u3000\u7b97\u8ba1\u8d77\u6765\uff0c\u654c\u4eba\u6050\u6015\u6709\u4e00\u4e2a\u4ee5\u4e0a\u80fd\u548c\u6211\u6392\u8fc7\u767e\u62db\u7684\u9ad8\u624b\uff0c\u4e00\u65e6\u4ed6\u4eec\u4e24\u4e2a\u8054\u624b\uff0c\u4e94\u5341\u62db\u5185\u53ef\u4ee5\u6740\u4e86\u6211\u3002 \u3000\u3000\u5e78\u597d\uff0c\u5c11\u6797\u5bfa\u786e\u6709\u7edd\u6280\u7559\u4f20\uff0c\u96ea\u3001\u971c\u4e5f\u4e60\u827a\u6709\u6210\uff0c\u6211\u76f8\u4fe1\u53ef\u4ee5\u5e94\u4ed8\u4e86\uff0c\u7528\u4e0d\u7740\u518d\u7528\u2018\u60d1\u5fc3\u672f\u2019\uff0c\u8bf1\u654c\u964d\u670d\u3002\u201d \u3000\u3000\u201c\u6211\u770b\uff0c\u4f60\u7b97\u76d8\u6253\u7684\u592a\u5982\u610f\u4e86\uff0c\u5982\u679c\u654c\u65b9\u6709\u4e00\u4eba\u80fd\u548c\u4f60\u6328\u8fc7\u767e\u62db\uff0c\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u8fd9\u79cd\u4eba\u8bf4\u4e0d\u5b9a\u4f1a\u4e8c\u4e09\u5341\u4e2a\uff0c\u5f53\u7136\uff0c\u4e5f\u4f1a\u6709\u9ad8\u8fc7\u4ed6\u4eec\u7684\u4eba\uff0c\u624d\u80fd\u6559\u51fa\u8fd9\u4e9b\u4eba\u6765\uff0c\u6211\u76f8\u4fe1\uff0c\u4ed6\u4eec\u8fd8\u6709\u522b\u7684\u51c6\u5907\uff0c\u771f\u80fd\u5e2e\u4f60\u7684\u4eba\uff0c\u4e0d\u4f1a\u5f88\u591a\uff0c\u5c31\u7b97\u6211\u4eec\u80fd\u591f\u6697\u65bd\u7b97\u8ba1\uff0c\u4e5f\u8981\u5148\u6321\u4f4f\u654c\u52bf\u624d\u884c\u3002\u6211\u4e0d\u61c2\u5927\u548c\u5c1a\u4e3a\u4ec0\u4e48\u4e0d\u7ec3\u4f20\u771f\u8bb0\u4e0a\u6b66\u529f\uff1f\u201d \u3000\u3000\u201c\u548c\u5c1a\u4e0d\u80fd\u7ec3\uff0c\u4f60\u5012\u662f\u975e\u5e38\u9002\u5408\uff0c\u4e0d\u59a8\u8981\u4e8c\u5973\u4f20\u4f60\uff0c\u4e0d\u8fc7\uff0c\u548c\u5c1a\u4e5f\u4e0d\u5e0c\u671b\u5927\u591a\u4eba\u7ec3\u8fd9\u4e9b\u6b66\u529f\u3002 \u3000\u3000\u4e00\u65e6\u5b83\u8fdb\u5165\u5927\u6210\u5883\u754c\uff0c\u5fc3\u60c5\u7a0d\u53d8\uff0c\u5c31\u6210\u6b66\u6797\u5927\u60a3\uff0c\u548c\u5c1a\u76f8\u4fe1\uff0c\u4e00\u5bf9\u4e00\uff0c\u5929\u4e0b\u5df2\u65e0\u4eba\u80fd\u80dc\u6c5f\u67ab\u3002 \u3000\u3000\u6211\u4eec\u6015\u7684\u662f\u5bf9\u65b9\u8f66\u8f6e\u5927\u6218\uff0c\u6c5f\u5144\u5f1f\u4e00\u4e2a\u4eba\uff0c\u80fd\u6491\u591a\u4e45\uff1f\u5c31\u7b97\u4efb\u3001\u7763\u4e8c\u8109\u5df2\u901a\uff0c\u51b2\u8fc7\u5341\u4e8c\u91cd\u697c\uff0c\u5185\u529f\u5faa\u73af\u4e0d\u606f\uff0c\u5728\u62db\u62db\u640f\u547d\u4e4b\u4e0b\uff0c\u4e5f\u8981\u4f11\u606f\u3002 \u3000\u3000\u4f55\u51b5\uff0c\u5bf9\u65b9\u6b66\u529f\u8109\u8def\u9634\u67d4\u591a\u4e8e\u9633\u521a\uff0c\u5973\u5b50\u9ad8\u624b\u5e94\u8be5\u5f88\u591a\uff0c\u8fd9\u2018\u60d1\u5fc3\u672f\u2019\u4e0d\u7528\u5c31\u767d\u4e0d\u7528\u4e86\u3002\u201d \u3000\u3000\u201c\u597d\u5427\uff01\u4f60\u548c\u5c1a\u5c31\u51c6\u5907\u5316\u7f18\uff0c\u52df\u6b3e\u5efa\u7b51\u4e00\u5ea7\u5927\u5bab\u9662\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u6c5f\u67ab\u5728\u6d1b\u9633\u5c45\uff0c\u5982\u679c\u653e\u624b\u65bd\u4e3a\uff0c\u56db\u5b63\u82b1\u5a62\u6050\u5df2\u5c3d\u5165\u638c\u63e1\uff0c\u90a3\u4f4d\u4e09\u5bab\u4e3b\u4e5f\u96be\u9003\u8fc7\uff0c\u73b0\u5728\uff0c\u5c31\u65e0\u6cd5\u5f00\u9501\u4e86\u3002\u201d \u3000\u3000\u201c\u5509\uff01\u6c5f\u5144\u5f1f\u5982\u80fd\u628a\u4f4f\u60c5\u5173\uff0c\u2018\u60d1\u5fc3\u672f\u2019\u53ea\u662f\u5bf9\u654c\u624b\u6bb5\u3002\u2026\u2026\u201d \u3000\u3000\u201c\u90a3\u53ea\u6709\u4e00\u4e2a\u529e\u6cd5\uff0c\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u6740\u4e86\u5979\u4eec\uff0c\u5931\u53bb\u4ef7\u503c\uff0c\u5c31\u4e00\u5200\u4e24\u6bb5\uff0c\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u5446\u4e86\u4e00\u5446\uff0c\u63a5\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u5c0f\u5f1f\u6050\u6015\u4e0b\u4e0d\u4e86\u624b\u554a\uff01\u201d \u3000\u3000\u201c\u8ba9\u5979\u4eec\u601d\u5ff5\u6210\u75be\uff0c\u53d1\u72c2\u53d1\u75af\uff0c\u5c31\u66f4\u4e0d\u4eba\u9053\u4e86\uff0c\u2026\u2026\u201d\u5f20\u56db\u59d1\u53f9\u53e3\u6c14\u53c8\u9053\uff1a\u201c\u5144\u5f1f\uff0c\u6211\u5df2\u8eab\u53d7\u5176\u5bb3\uff0c\u653e\u773c\u5929\u4e0b\uff0c\u5b9a\u529b\u8d85\u8fc7\u6211\u7684\u4eba\uff0c\u4e0d\u4f1a\u5f88\u591a\uff0c\u4f60\u5982\u6b64\u591a\u60c5\uff0c\u4e0d\u5fcd\u4e0b\u624b\uff0c\u6050\u96be\u6709\u4e24\u5168\u4e4b\u7b56\u4e86\u3002\u201d \u3000\u3000\u201c\u5947\u602a\u5440\uff01\u8fd9\u79cd\u90aa\u95e8\u6b66\u529f\uff0c\u600e\u4f1a\u57f9\u517b\u51fa\u4e00\u4e2a\u591a\u60c5\u79cd\u5b50\u6765\u2026\u2026\u201d \u3000\u3000\u5927\u548c\u5c1a\u9053\uff1a\u201c\u6211\u60f3\u4e0d\u901a\uff0c\u8fd9\u662f\u4e0d\u662f\u6709\u80cc\u5929\u72d0\u592b\u4eba\uff0c\u5fd7\u590d\u6b66\u6797\u7684\u539f\u610f\u5462\uff1f\u201d \u3000\u3000\u201c\u522b\u602a\u4ed6\uff0c\u6211\u7ec3\u4e86\u5929\u72d0\u6b66\u529f\uff0c\u624d\u77e5\u9053\u5929\u72d0\u6b66\u529f\u90aa\u4e2d\u5bd3\u60c5\uff0c\u7537\u4eba\u4f1a\u601c\u9999\u60dc\u7389\uff0c\u5973\u4eba\u4f1a\u5b9a\u60c5\u4e00\u4eba\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u6c5f\u67ab\u538c\u6076\u83f1\u82b1\uff0c\u6740\u5979\u4e0d\u8fc7\u662f\u4e3e\u624b\u4e4b\u52b3\uff0c\u4f46\u4ed6\u5374\u4e0d\u80af\u4e0b\u624b\uff0c\u6211\u60f3\uff0c\u8fd9\u4e5f\u662f\u5929\u72d0\u592b\u4eba\uff0c\u624b\u5203\u5979\u60c5\u4eba\u4e4b\u540e\uff0c\u60c5\u64cd\u4e0d\u79fb\u7684\u539f\u56e0\u3002 \u3000\u3000\u4e24\u4e2a\u5c0f\u72d0\u5973\u5df2\u5c3d\u5f97\u5929\u72d0\u6b66\u529f\uff0c\u4f60\u548c\u5c1a\u4e5f\u8be5\u770b\u5f97\u51fa\u6765\uff0c\u5979\u4eec\u65e0\u6b32\u65e0\u6c42\uff0c\u5bf9\u6c5f\u67ab\u53ea\u662f\u4e00\u7247\u7eaf\u60c5\uff0c\u6c5f\u67ab\u5982\u6709\u4e2a\u4e09\u957f\u4e24\u77ed\uff0c\u5979\u4eec\u7edd\u96be\u6d3b\u5f97\u4e0b\u53bb\u3002\u201d \u3000\u3000\u201c\u4f60\u5462\uff1f\u201d\u5927\u548c\u5c1a\u77aa\u7740\u5f20\u56db\u59d1\u95ee\u3002 \u3000\u3000\u201c\u8bf4\u5b9e\u8bdd\uff0c\u6211\u4e5f\u628a\u6301\u4e0d\u4f4f\uff0c\u8fd9\u767e\u65e5\u5c0f\u522b\u4e4b\u540e\uff0c\u6211\u4e5f\u9010\u6e10\u5931\u53bb\u4e86\u63a7\u5236\u81ea\u5df1\u7684\u5b9a\u529b\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u4f60\u548c\u5c1a\u5982\u89c1\u5230\u6885\u82b1\uff0c\u9752\u51e4\u90a3\u79cd\u601d\u5ff5\u60c5\u90ce\uff0c\u6697\u4e2d\u60b2\u6ce3\uff0c\u54c0\u4f24\u6b32\u7edd\u7684\u795e\u60c5\uff0c\u4f60\u4e5f\u4f1a\u9eef\u7136\u795e\u4f24\uff0c\u2018\u60d1\u5fc3\u672f\u2019\u5360\u6709\u4e86\u5979\u4eec\u7684\u8eab\u4f53\uff0c\u4e5f\u5e26\u8d70\u4e86\u5979\u4eec\u7684\u5fc3\u3002 \u3000\u3000\u9752\u51e4\u3001\u84dd\u51e4\uff0c\u5e08\u59d0\u59b9\u76f8\u5904\u5341\u4f59\u5e74\uff0c\u60c5\u540c\u9aa8\u8089\uff0c\u7adf\u7136\u4e3a\u6c5f\u67ab\uff0c\u5f53\u9762\u7ffb\u8138\u3002\u84dd\u51e4\u5012\u662f\u6709\u70b9\u60c5\u4e49\uff0c\u4e89\u5435\u4e00\u756a\u540e\uff0c\u5e26\u56db\u5927\u5929\u9e45\u3001\u4e09\u9b54\u3001\u4e94\u602a\u79bb\u53bb\uff0c\u8981\u662f\u5979\u4eec\u5408\u529b\u51fa\u624b\uff0c\u6211\u7edd\u6ca1\u6709\u653e\u51fa\u6bd2\u8747\u7684\u673a\u4f1a\u3002\u201d \u3000\u3000\u5927\u548c\u5c1a\u63a8\u843d\u4e86\u5047\u53d1\uff0c\u4e00\u638c\u62cd\u5728\u8111\u58f3\u4e0a\uff0c\u9053\uff1a\u201c\u8fd9\u5c31\u9ebb\u70e6\u5927\u4e86\uff0c\u6c5f\u5144\u5f1f\uff0c\u4f60\u4e0d\u5fcd\u5979\u4eec\uff0c\u80fd\u4e0d\u80fd\u70b9\u4f4f\u5979\u4eec\u7a74\u9053\u5462\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u8fd9\u4e2a\u5927\u6982\u53ef\u4ee5\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u90a3\u5c31\u7531\u6211\u548c\u5c1a\u548c\u8001\u53eb\u5316\u5b50\u4e0b\u624b\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u548c\u5f20\u56db\u59d1\u542c\u5f97\u6014\u4f4f\u4e86\uff0c\u770b\u7740\u5927\u548c\u5c1a\u51fa\u795e\u3002 \u3000\u3000\u201c\u4e0d\u8981\u6000\u7591\uff01\u548c\u5c1a\u53d6\u8001\u53eb\u5316\u6c5f\u6e56\u8dd1\u7684\u592a\u4e45\u4e86\uff0c\u6211\u4eec\u5bf9\u5584\u3001\u6076\u7684\u770b\u6cd5\uff0c\u548c\u4e24\u4f4d\u4e0d\u540c\uff0c\u6740\u4e00\u4eba\u6551\u767e\u4eba\uff0c\u548c\u5c1a\u662f\u8ba4\u4e3a\u884c\u5584\uff0c\u8001\u53eb\u5316\u65e9\u6709\u4e86\u9664\u6076\u52a1\u5c3d\u4e4b\u5fc3\u3002 \u3000\u3000\u7eb5\u7136\u5979\u5343\u5a07\u767e\u5a9a\uff0c\u82b1\u5bb9\u6708\u8c8c\uff0c\u6211\u4eec\u4e5f\u4e00\u6837\u4e0b\u5f97\u4e86\u624b\uff0c\u4f46\u2018\u60d1\u5fc3\u672f\u2019\u4e0d\u80fd\u4e0d\u7528\uff01\u201d \u3000\u3000\u6c5f\u67ab\u53e3\u9f7f\u542f\u52a8\uff0c\u6b32\u8a00\u53c8\u6b62\u3002 \u3000\u3000\u5927\u548c\u5c1a\u81ea\u8ba4\u8bc6\u6c5f\u67ab\u4ee5\u6765\uff0c\u4e00\u5411\u662f\u548c\u5584\u3001\u670d\u4ece\uff0c\u7b2c\u4e00\u6b21\u5982\u6b64\u7684\u72ec\u65ad\u4e13\u884c\u3002 \u3000\u3000\u201c\u5927\u548c\u5c1a\u2026\u2026\u201d \u3000\u3000\u4e03\u5b9d\u5927\u5e08\u51b7\u51b7\u63a5\u9053\uff1a\u201c\u5f20\u59d1\u5a18\uff0c\u9664\u975e\u4f60\u60f3\u51fa\u4e86\u66f4\u597d\u7684\u529e\u6cd5\uff0c\u90a3\u5c31\u4e0d\u8981\u53cd\u5bf9\uff0c\u5165\u5730\u72f1\uff0c\u8f6e\u516d\u755c\uff0c\u662f\u6211\u548c\u5c1a\u7684\u4e8b\uff01 \u3000\u3000\u4e2a\u4e2a\u5fc3\u5b58\u6148\u60b2\uff0c\u8fd9\u4e2a\u4ed7\u8981\u5982\u4f55\u6253\u6cd5\uff1f \u3000\u3000\u6b66\u6797\u4e09\u5723\uff0c\u4e00\u5ff5\u4ec1\u6148\uff0c\u7559\u4e0b\u4e86\u4eca\u65e5\u7684\u7978\u5bb3\u6b8b\u5c40\u3002 \u3000\u3000\u5c11\u6797\u5bfa\u4e24\u4ee3\u7cbe\u9510\uff0c\u5c3d\u5316\u7070\u6ce5\uff0c\u5404\u5927\u95e8\u6d3e\u6b7b\u4ea1\u7684\u9ad8\u624b\uff0c\u4f55\u81f3\u6570\u767e\uff0c\u4f60\u8981\u963b\u6b62\u6211\u8fa3\u624b\u9664\u9b54\uff0c\u90a3\u662f\u903c\u6211\u81ea\u7edd\u2026\u2026\u201d \u3000\u3000\u4ed6\u8bf4\u7684\u5f88\u4ece\u771f\uff0c\u8138\u4e0a\u662f\u4e00\u7247\u51db\u7136\u96be\u72af\u7684\u795e\u8272\u3002 \u3000\u3000\u5f20\u56db\u59d1\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u80fd\u4e0d\u80fd\u8ba9\u6211\u518d\u60f3\u51e0\u5929\uff1f\u4e0d\u7ba1\u60f3\u51fa\u4ec0\u4e48\u529e\u6cd5\uff1f\u4e00\u5b9a\u4f1a\u4f7f\u5927\u5e08\u540c\u610f\u3002\u201d \u3000\u3000\u201c\u884c\uff01\u548c\u5c1a\u4e0d\u662f\u55dc\u6740\u6210\u6027\u7684\u4eba\uff0c\u4f60\u5f20\u59d1\u5a18\u7684\u529e\u6cd5\u9ad8\u660e\uff0c\u548c\u5c1a\u5b9a\u4f1a\u540c\u610f\u3002\u201d \u3000\u3000\u201c\u662f\uff01\u60f3\u4e0d\u51fa\u597d\u529e\u6cd5\uff0c\u5c31\u4f9d\u5927\u5e08\uff0c\u56db\u59d1\u7edd\u4e0d\u53cd\u5bf9\u3002\u201d \u3000\u3000\u5927\u548c\u5c1a\u7751\u4e0a\u53c8\u73b0\u7b11\u5bb9\uff0c\u9053\uff1a\u201c\u5f20\u59d1\u5a18\uff0c\u4f60\u770b\uff0c\u7b2c\u4e00\u573a\u51b3\u6218\u4e4b\u5730\uff0c\u8be5\u9009\u5728\u4ec0\u4e48\u5730\u65b9\uff1f\u201d \u3000\u3000\u201c\u5c11\u6797\u5bfa\u7684\u50e7\u7fa4\u4eec\uff0c\u5982\u80af\u4f5c\u540e\u63f4\uff0c\u6700\u597d\u9009\u5728\u5c11\u6797\u5bfa\u2026\u2026\u201d \u3000\u3000\u201c\u548c\u5c1a\u4e5f\u6709\u6b64\u610f\uff0c\u5f20\u59d1\u5a18\u7b56\u5212\u884c\u52a8\uff0c\u548c\u5c1a\u613f\u51fa\u9762\u8bf4\u670d\u5c11\u6797\u50e7\u4f17\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u9053\uff1a\u201c\u6700\u91cd\u8981\u7684\u4e00\u4ef6\u4e8b\u662f\uff0c\u5148\u8981\u67e5\u660e\u767d\uff0c\u5c11\u6797\u50e7\u4f17\u4eec\u662f\u5426\u5df2\u8eab\u53d7\u7981\u5236\uff1f\u5426\u5219\u5c31\u4f1a\u4e00\u4e3e\u5bb3\u6b7b\u4ed6\u4eec\u5168\u5bfa\u50e7\u4f17\u4e86\u3002\u201d \u3000\u3000\u201c\u8fd8\u662f\u5f20\u59d1\u5a18\u9ad8\u660e\uff0c\u548c\u5c1a\u7adf\u6ca1\u6709\u60f3\u5230\u8fd9\u4e9b\uff0c\u4f69\u670d\uff01\u4f69\u670d\uff01\u201d \u3000\u3000\u201c\u5c31\u8fd9\u4e48\u51b3\u5b9a\u4e86\uff0c\u5c11\u6797\u50e7\u4f17\u4eec\u5982\u53d7\u7981\u5236\uff0c\u65e0\u6cd5\u5e2e\u624b\uff0c\u54b1\u4eec\u53c8\u65e0\u6cd5\u89e3\u9664\uff0c\u518d\u4f5c\u4ed6\u9014\u2026\u2026\u201d \u3000\u3000\u5f20\u56db\u59d1\u9053\uff1a\u201c\u73b0\u5728\uff0c\u54b1\u4eec\u56de\u4eac\u534e\u5927\u5ba2\u6808\u53bb\uff0c\u770b\u60c5\u5f62\uff0c\u5148\u9664\u4e86\u4ed6\u4eec\u6f5c\u5165\u5ba2\u6808\u7684\u9ad8\u624b\u624d\u884c\u3002\u201d \u3000\u3000\u6c5f\u67ab\u548c\u5f20\u56db\u59d1\u7ad9\u8d77\u8eab\u5b50\uff0c\u51c6\u5907\u79bb\u53bb\u3002 \u3000\u3000\u5927\u548c\u5c1a\u5374\u6447\u7740\u5934\uff0c\u9053\uff1a\u201c\u6162\u8d70\u4e00\u6b65\uff0c\u8fd8\u6709\u4e00\u4ef6\u975e\u5e38\u91cd\u8981\u7684\u4e8b\uff0c\u6ca1\u51b3\u5b9a\u5c31\u5bf9\u4e4b\u7b56\uff1f\u201d \u3000\u3000\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u5f20\u56db\u59d1\u4e5f\u88ab\u95ee\u7684\u6014\u4f4f\u4e86\u3002 \u3000\u3000\u201c\u76d1\u89c6\u6211\u4eec\u7684\u98de\u9e70\u3001\u7075\u72ac\uff0c\u662f\u5426\u6709\u6cd5\u5b50\u9664\u53bb\uff0c\u5426\u5219\uff0c\u6c38\u8fdc\u65e0\u6cd5\u907f\u8fc7\u4ed6\u4eec\u7684\u8033\u76ee\uff0c\u6211\u4eec\u4efb\u4f55\u884c\u52a8\uff0c\u90fd\u4e0d\u80fd\u9003\u8fc7\u76d1\u89c6\u3002\u201d \u3000\u3000\u201c\u5bf9\uff01\u8d2d\u5236\u4e00\u4e9b\u5f3a\u5f13\u957f\u7bad\uff0c\u5c04\u6740\u9e70\u72ac\uff0c\u6211\u76f8\u4fe1\u4ed6\u4eec\u6709\u4e00\u4f4d\u80fd\u6307\u6325\u9e70\u72ac\u7684\u5947\u4eba\u5728\u6697\u4e2d\u4e3b\u6301\uff0c\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u662f\u5426\u771f\u6709\u6614\u5e74\u516c\u51b6\u957f\u8fd9\u79cd\u80fd\u901a\u9e1f\u8bed\u7684\u4eba\uff0c\u4f46\u6211\u53ef\u4ee5\u80af\u5b9a\u90a3\u4eba\u548c\u7075\u72ac\u3001\u98de\u9e70\u4e4b\u95f4\uff0c\u5b9a\u7136\u6709\u4e00\u79cd\u6c9f\u901a\u7684\u65b9\u6cd5\uff0c\u4f7f\u98de\u9e70\u3001\u7075\u72ac\u53ef\u4ee5\u8868\u8fbe\u51fa\u5b83\u4eec\u6240\u89c1\u7684\u4e8b\u7269\u3002 \u3000\u3000\u6240\u4ee5\uff0c\u4e0d\u7528\u4eba\u968f\u884c\u6307\u6325\uff0c\u5c31\u628a\u6211\u4eec\u95f9\u5f97\u624b\u8db3\u65e0\u63aa\uff0c\u6211\u8d39\u5c3d\u5fc3\u673a\u5e03\u7f72\uff0c\u4ee5\u9690\u5bc6\u884c\u8e2a\uff0c\u4f46\u4ed6\u4eec\u5f88\u5feb\u5c31\u627e\u4e0a\u6211\u4eec\u3002 \u3000\u3000\u6211\u82b1\u8d39\u4e00\u65e5\u591c\u7684\u5de5\u592b\u6f5c\u8e2a\u4fa6\u5bdf\uff0c\u624d\u53d1\u89c9\u6bdb\u75c5\u51fa\u5728\u98de\u9e70\u3001\u7075\u72ac\u8eab\u4e0a\uff0c\u540e\u7531\u9752\u51e4\u53e3\u4e2d\u5f97\u5230\u8bc1\u5b9e\uff0c\u8fd9\u4e2a\u79d8\u5bc6\uff0c\u8fde\u6885\u82b1\u90fd\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u201c\u90a3\u662f\u8bf4\uff0c\u65bd\u653e\u3001\u6307\u6325\u98de\u9e70\u3001\u7075\u72ac\u7684\u4eba\uff0c\u4e0d\u662f\u5929\u9a6c\u5802\uff0c\u4e5f\u4e0d\u662f\u5929\u738b\u95e8\u548c\u5929\u540e\u5bab\u7684\u4eba\u4e86\uff1f\u201d\u5927\u548c\u5c1a\u6709\u4e9b\u60ca\u5f02\u7684\u8bf4\u3002 \u3000\u3000\u201c\u5bf9\uff01\u771f\u6b63\u7684\u5e55\u540e\u4eba\u7269\uff0c\u4f3c\u4e4e\u5df2\u6b63\u5f0f\u53c2\u4e0e\u3001\u4ecb\u5165\u4e86\uff0c\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u8fd9\u597d\u50cf\u5df1\u4e0d\u662f\u4e00\u822c\u6c5f\u6e56\u4eba\u7269\u7684\u7eb7\u4e89\uff0c\u4ed6\u4eec\u4f3c\u53e6\u6709\u91cd\u5927\u7684\u76ee\u7684\uff0c\u4e00\u65e6\u4e2d\u539f\u6b66\u6797\u5404\u5927\u95e8\u6d3e\u7684\u529b\u91cf\u5168\u5165\u4ed6\u4eec\u7684\u638c\u63e1\uff0c\u53ef\u80fd\u5c31\u6709\u60ca\u5929\u52a8\u5730\u7684\u53d8\u5316\u4e86\u3002 \u3000\u3000\u6240\u4ee5\uff0c\u4ed6\u4eec\u4e00\u53d1\u73b0\u6211\u4eec\u8fd9\u4e00\u80a1\u6297\u62d2\u7684\u529b\u91cf\uff0c\u5c31\u96c6\u4e2d\u4e86\u5168\u529b\u8ffd\u6740\uff0c\u5929\u540e\u5bab\u548c\u5929\u9a6c\u5802\u7684\u4e92\u6597\uff0c\u4e5f\u5b8c\u5168\u505c\u6b62\u4e0b\u6765\uff0c\u6211\u60f3\u8fd9\u4e0d\u662f\u5929\u9a6c\u5802\u548c\u5929\u540e\u5bab\u80fd\u591f\u4e3b\u5bb0\u7684\u7b97\u4e86\u3002\u201d \u3000\u3000\u201c\u5509\uff01\u53ea\u662f\u4e0d\u77e5\u9053\u4ed6\u4eec\u8bad\u7ec3\u4e86\u591a\u5c11\u53ef\u7528\u7684\u98de\u9e70\u3001\u7075\u72ac\uff0c\u6211\u4eec\u8981\u5982\u4f55\u624d\u80fd\u5c3d\u9664\u5b83\u4eec\uff0c\u2026\u2026\u201d\u5927\u548c\u5c1a\u9053\uff1a\u201c\u5f20\u59d1\u5a18\u4ed4\u7ec6\u7684\u770b\u8fc7\u90a3\u4e9b\u9e70\u72ac\uff0c\u548c\u4e00\u822c\u7684\u6709\u4f55\u4e0d\u540c\uff1f\u201d \u3000\u3000\u201c\u98de\u9e70\u4f3c\u5c5e\u897f\u5317\u5927\u5c71\u4e2d\u7684\u731b\u79bd\u9ed1\u96d5\u7684\u54c1\u79cd\uff0c\u7075\u5149\u4e5f\u4f3c\u662f\u9752\u72fc\u4e00\u7c7b\u2026\u2026\u201d \u3000\u3000\u201c\u80fd\u628a\u8fd9\u4e9b\u51f6\u6b8b\u66b4\u91ce\u7684\u79bd\u517d\uff0c\u9a6f\u670d\u5230\u542c\u4eba\u6307\u6325\uff0c\u771f\u4e0d\u662f\u5bb9\u6613\u7684\u4e8b\u2026\u2026\u201d\u5927\u548c\u5c1a\u9053\uff1a\u201c\u8fd9\u4e2a\u4eba\u7684\u624d\u80fd\uff0c\u5b9e\u5728\u53eb\u4eba\u4f69\u670d\u3002\u201d \u3000\u3000\u201c\u6211\u8bf4\u4f3c\u5c5e\u7684\u610f\u601d\uff0c\u662f\u56e0\u4e3a\u5b83\u4eec\u548c\u5927\u539f\u91ce\u4e2d\u7684\u9ed1\u96d5\u3001\u9752\u72fc\u4e0d\u5c3d\u76f8\u540c\uff0c\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a \u3000\u3000\u201c\u5b83\u4eec\u91ce\u6027\u5316\u53bb\u4e86\u4e0d\u5c11\uff0c\u4f53\u578b\u4e5f\u5c0f\u4e86\u4e00\u4e9b\uff0c\u6211\u8ba4\u4e3a\u8fd9\u662f\u4eba\u5de5\u57f9\u80b2\u51fa\u6765\u7684\u9e70\u3001\u72ac\uff0c\u6570\u91cf\u5e94\u8be5\u4e0d\u4f1a\u592a\u591a\uff0c\u4ece\u5c0f\u7531\u4eba\u9a6f\u517b\uff0c\u624d\u4f1a\u53d7\u4eba\u7684\u63a7\u5236\u6307\u6325\u3002 \u3000\u3000\u5982\u679c\uff0c\u6211\u4eec\u7cbe\u5bc6\u8bbe\u8ba1\uff0c\u5168\u9762\u6251\u6740\uff0c\u7eb5\u7136\u4e0d\u80fd\u5c3d\u9664\u7978\u6e90\uff0c\u4e5f\u80fd\u4f7f\u5b83\u4eec\u5927\u91cf\u51cf\u5c11\uff0c\u8fd9\u4e9b\u5df2\u5177\u7075\u6027\u7684\u9e70\u3001\u72ac\u9a6f\u517b\u4e0d\u6613\uff0c\u5728\u6211\u4eec\u4e0d\u65ad\u730e\u6740\u4e0b\uff0c\u81f3\u5c11\u4f1a\u51cf\u8f7b\u5bf9\u6211\u4eec\u7684\u5a01\u80c1\u3002\u201d \u3000\u3000\u201c\u5c31\u8fd9\u4e48\u51b3\u5b9a\u4e86\uff0c\u548c\u5c1a\u53bb\u8ba2\u5236\u4e8c\u5341\u628a\u5f3a\u5f13\uff0c\u4e00\u5343\u652f\u7279\u522b\u7684\u957f\u7bad\uff0c\u4f7f\u5c04\u7a0b\u5c3d\u91cf\u7684\u53ca\u8fdc\u3002\u201d \u3000\u3000\u201c\u4e8c\u5341\u5f20\u5f3a\u5f13\u5e94\u8be5\u591f\u7528\u4e86\uff0c\u957f\u7bad\u968f\u65f6\u53ef\u4ee5\u8865\u5145\uff0c\u4e0d\u8fc7\uff0c\u5f13\u529b\u8981\u5206\u4e09\u79cd\uff0c\u4e0d\u80fd\u4ee5\u4f60\u5927\u548c\u5c1a\u7684\u81c2\u529b\u4e3a\u51c6\uff0c\u5f13\u592a\u5f3a\uff0c\u6211\u4eec\u4e0d\u80fd\u5f13\u62c9\u6ee1\u6708\uff0c\u90a3\u5c31\u529b\u6709\u4e0d\u902e\u4e86\u3002\u201d\u5f20\u56db\u59d1\u7b11\u9053\uff1a\u201c\u6b27\u9633\u662d\u3001\u6c5f\u67ab\uff0c\u4f60\u5927\u548c\u5c1a\uff0c\u8981\u7528\u4e00\u7ea7\u7684\u5341\u77f3\u5f3a\u5f13\uff0c\u6709\u4e09\u628a\u5c31\u591f\u4e86\uff0c\u8001\u53eb\u5316\u3001\u80e1\u840d\u3001\u6bb5\u4e5d\u3001\u9093\u98de\u7528\u4e94\u77f3\u5f13\uff0c\u5176\u4ed6\u7684\u4e09\u77f3\u5f13\u5c31\u884c\u4e86\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u6211\u548c\u5c1a\u8fd9\u5c31\u53bb\u8ba1\u5236\u5f13\u7bad\uff0c\u54b1\u4eec\u5728\u5ba2\u6808\u78b0\u5934\uff0c\u90a3\u5ea7\u5ba2\u6808\u53eb\u2026\u2026\u201d \u3000\u3000\u201c\u4eac\u534e\u5ba2\u6808\uff01\u201d \u3000\u3000\u5f20\u56db\u59d1\u5305\u4e0b\u4e86\u4eac\u534e\u5ba2\u6808\u4e2d\u4e24\u5ea7\u76f8\u8fde\u7684\u8de8\u9662\uff0c\u628a\u4eba\u624b\u96c6\u4e2d\u4e00\u5904\uff0c\u4ee5\u5907\u5e94\u53d8\u3002 \u3000\u3000\u867d\u5c45\u95f9\u5e02\uff0c\u4f46\u5f20\u56db\u59d1\u4ecd\u7136\u4e0d\u6562\u5927\u610f\uff0c\u65e5\u591c\u90fd\u6709\u9632\u8303\u3002 \u3000\u3000\u8fd9\u5df2\u662f\u5348\u540e\u65f6\u523b\uff0c\u6b63\u662f\u8001\u53eb\u5316\u94b1\u7f3a\u548c\u94c1\u62c2\u5927\u5e08\u5f53\u503c\u3002 \u3000\u3000\u4e24\u4eba\u5206\u5b88\u5728\u9662\u5185\u3001\u95e8\u5916\uff0c\u6c5f\u67ab\u5df2\u5165\u5ba2\u6808\u5927\u95e8\u3002 \u3000\u3000\u8001\u53eb\u5316\u5df2\u8fce\u4e86\u4e0a\u53bb\uff0c\u9053\uff1a\u201c\u6c5f\u5c11\u4fa0\uff0c\u5927\u5385\u4e2d\u4e0d\u80fd\u5750\uff0c\u8fd9\u51e0\u5929\u5df2\u6df7\u8fdb\u6765\u4e0d\u5c11\u9b54\u5b50\u3001\u9b54\u5b59\uff0c\u4ed6\u4eec\u9884\u4ed8\u7684\u8ba2\u91d1\uff0c\u623f\u95f4\u4e00\u6709\u7a7a\uff0c\u4ed6\u4eec\u7684\u4eba\u5c31\u8865\u4e86\u8fdb\u6765\uff0c\u2026\u2026\u201d \u3000\u3000\u653e\u4f4e\u4e86\u58f0\u97f3\uff0c\u63a5\u9053\uff1a\u201c\u5f20\u59d1\u5a18\u4e0a\u5348\u51fa\u95e8\u4e0d\u4e45\uff0c\u4ed6\u4eec\u53c8\u4f4f\u8fdb\u6765\u4e00\u6279\u4eba\uff0c\u5176\u4e2d\u6709\u4e00\u4e2a\u662f\u5728\u7ec8\u5357\u5c71\u56f4\u653b\u6211\u4eec\u7684\u9ad8\u624b\u4e4b\u4e00\uff0c\u6211\u4e0d\u77e5\u9053\u4ed6\u7684\u540d\u5b57\uff0c\u4f46\u5374\u8bb0\u5f97\u4ed6\u7684\u6a21\u6837\uff0c\u4ed6\u4f3c\u662f\u4e5f\u8ba4\u8bc6\u8001\u53eb\u5316\u5b50\u3002\u201d \u3000\u3000\u201c\u51e0\u4e2a\u4eba\uff1f\u201d \u3000\u3000\u201c\u4e94\u4e2a\u2026\u2026\u201d\u8001\u53eb\u5316\u9053\uff1a\u201c\u5176\u4e2d\u6709\u4e24\u4e2a\u4eba\u6709\u70b9\u8bf4\u4e0d\u51fa\u7684\u602a\u5f02\uff1f\u201d \u3000\u3000\u5f20\u56db\u59d1\u52a0\u5feb\u811a\u6b65\uff0c\u884c\u5165\u8de8\u9662\uff0c\u624d\u56de\u5934\u95f4\u9053\uff1a\u201c\u90a3\u4e24\u4e2a\u4eba\u602a\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u201c\u7b2c\u4e00\uff0c\u8001\u53eb\u5316\u770b\u4e0d\u51fa\u4ed6\u7684\u5e74\u9f84\uff1f\u8fd9\u662f\u5f88\u5c11\u6709\u7684\u4e8b\uff0c\u770b\u4e0a\u53bb\u4eba\u5728\u4e2d\u5e74\uff0c\u4f46\u8001\u53eb\u5316\u5374\u611f\u89c9\u4ed6\u81f3\u5c11\u5728\u53e4\u7a00\u4ee5\u4e0a\uff1f\u2026\u2026\u201d \u3000\u3000\u201c\u8fd8\u6709\u7b2c\u4e8c\u5417\uff1f\u2026\u2026\u201d\u6c5f\u67ab\u4e5f\u88ab\u5f15\u52a8\u5174\u81f4\u3002 \u3000\u3000\u201c\u5168\u8eab\u4f3c\u88ab\u4e00\u80a1\u4e91\u6c14\u56f4\u7740\uff0c\u6709\u70b9\u770b\u4e0d\u51fa\u771f\u5207\uff0c\u900f\u51fa\u4e86\u4e00\u79cd\u5bd2\u6c14\u903c\u4eba\u7684\u8baf\u606f\uff0c\u8001\u53eb\u5316\u9605\u4eba\u591a\u77e3\uff01\u8fd9\u79cd\u602a\u5f02\u4e4b\u5f81\uff0c\u8fd8\u662f\u521d\u6b21\u89c1\u5230\u3002\u201d \u3000\u3000\u6c5f\u67ab\u7a81\u7136\u505c\u4e86\u811a\u6b65\uff0c\u9053\uff1a\u201c\u4f60\u4ed4\u7ec6\u60f3\u60f3\u770b\uff0c\u786e\u5b9e\u5982\u6b64\u5417\uff1f\u201d \u3000\u3000\u201c\u9519\u4e0d\u4e86\uff0c\u6240\u4ee5\uff0c\u8001\u53eb\u5316\u624d\u6307\u8bf4\u4ed6\u4eec\u6709\u70b9\u602a\u5f02\u3002\u201d \u3000\u3000\u201c\u8001\u524d\u8f88\uff0c\u4ee3\u6211\u4f20\u4e2a\u6d88\u606f\uff0c\u8981\u6211\u4eec\u7684\u4eba\uff0c\u7531\u6b64\u523b\u8d77\uff0c\u5168\u529b\u6212\u5907\uff0c\u6700\u597d\u5168\u90fd\u96c6\u4e2d\u5728\u4e00\u5904\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u5927\u59d0\uff0c\u6211\u4eec\u5148\u53bb\u770b\u770b\u7f57\u5170\u7684\u4f24\u52bf\uff01\u201d \u3000\u3000\u201c\u8001\u53eb\u5316\u8fd9\u5c31\u53bb\u901a\u77e5\u4ed6\u4eec\u3002\u201d \u3000\u3000\u4ed6\u5fc3\u4e2d\u4f3c\u662f\u65e9\u611f\u5230\u4e8b\u6001\u4e25\u91cd\uff0c\u6025\u6025\u8f6c\u8eab\u800c\u53bb\u3002 \u3000\u3000\u5f20\u56db\u59d1\u884c\u5230\u6b63\u5385\u95e8\u524d\uff0c\u8f7b\u53e9\u6728\u95e8\uff0c\u5f00\u95e8\u7684\u7adf\u662f\u9752\u51e4\u3002 \u3000\u3000\u4e5f\u8bb8\u65e9\u5df2\u7531\u4e24\u4e2a\u5c0f\u72d0\u5973\u53e3\u4e2d\u5f97\u77e5\u8baf\u606f\uff0c\u6240\u4ee5\uff0c\u9752\u51e4\u8138\u4e0a\u5e76\u672a\u6709\u5927\u591a\u7684\u60ca\u559c\uff0c\u76ee\u6ce8\u6c5f\u67ab\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u6765\u4e86\uff01\u201d \u3000\u3000\u5f20\u56db\u59d1\u5f53\u5148\u8de8\u5165\u5385\u95e8\uff0c\u76f4\u5165\u5185\u5ba4\uff0c\u6c5f\u67ab\u5374\u63e1\u4f4f\u4e86\u9752\u51e4\u4e00\u53ea\u624b\uff0c\u9053\uff1a\u201c\u8c22\u8c22\u4f60\uff0c\u5728\u6700\u91cd\u8981\u7684\u65f6\u5019\uff0c\u4f60\u5e2e\u4e86\u5927\u5fd9\u3002\u201d \u3000\u3000\u9752\u51e4\u7f13\u7f13\u62bd\u56de\u5de6\u624b\uff0c\u9053\uff1a\u201c\u5feb\u53bb\u770b\u4f60\u5cb3\u6bcd\u7684\u4f24\u52bf\u3002\u201d \u3000\u3000\u6c5f\u67ab\u6014\u4e00\u6014\uff0c\u63a5\u9053\uff1a\u201c\u4f60\u4f60\u2026\u2026\u201d \u3000\u3000\u4f60\u4e86\u534a\u5929\uff0c\u4e0d\u77e5\u9053\u8be5\u8bf4\u4e9b\u4ec0\u4e48\uff1f \u3000\u3000\u201c\u96ea\u3001\u971c\u4e8c\u4f4d\u59b9\u5b50\uff0c\u662f\u4e0b\u6ef4\u51e1\u5c18\u7684\u4ed9\u5973\uff0c\u6211\u597d\u559c\u6b22\u5979\u4eec\uff0c\u6885\u82b1\u548c\u6211\u4e00\u6837\uff0c\u8eab\u4e16\u582a\u601c\u2026\u2026\u201d\u9752\u6c11\u9053\uff1a\u201c\u5f20\u59e8\u8bf4\u4f60\u547d\u72af\u6843\u82b1\uff0c\u5728\u52ab\u96be\u9003\uff0c\u5f31\u6c34\u4e8c\u5343\uff0c\u6211\u53ea\u53d6\u4e00\u74e2\u996e\uff0c\u4e0d\u8981\u559c\u65b0\u538c\u65e7\uff0c\u6211\u5c31\u5f88\u6ee1\u8db3\u4e86\u3002\u201d \u3000\u3000\u63a9\u4e0a\u623f\u95e8\uff0c\u8f6c\u8eab\u5e26\u8def\u3002 \u3000\u3000\u660e\u77e5\u6885\u82b1\u4e5f\u5728\u7f57\u5170\u7684\u623f\u4e2d\uff0c\u6c5f\u67ab\u7a81\u6709\u7740\u4e3e\u6b65\u7ef4\u8270\u4e4b\u611f\uff0c\u90a3\u6247\u6728\u95e8\uff0c\u7adf\u4f7f\u8fd9\u4f4d\u4e00\u4ee3\u8c6a\u5ba2\u52c7\u6c14\u5c3d\u6d88\uff0c\u4e0d\u6562\u8de8\u5165\u3002 \u3000\u3000\u541f\u971c\u95ea\u8eab\u800c\u51fa\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u6c5f\u5927\u54e5\uff0c\u600e\u4e48\u4e0d\u8fdb\u53bb\u554a\uff01\u201d \u3000\u3000\u201c\u6211\u2026\u2026\u6211\u2026\u2026\u6709\u70b9\u2026\u2026\u201d \u3000\u3000\u201c\u662f\u5bb3\u7f9e\uff0c\u8fd8\u662f\u5bb3\u6015\uff1f\u201d\u541f\u971c\u5ae3\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u4eec\u90fd\u4e0d\u6015\uff0c\u4f60\u6015\u4ec0\u4e48\uff1f\u9752\u51e4\u548c\u6885\u82b1\u5728\u5f20\u59e8\u5b89\u6392\u4e0b\uff0c\u90fd\u62dc\u5728\u6211\u5a18\u819d\u4e0b\uff0c\u53d8\u6210\u4e00\u5bb6\u4eba\u4e86\u3002\u201d \u3000\u3000\u7275\u7740\u6c5f\u67ab\u4e00\u53cc\u624b\uff0c\u884c\u5165\u5ba4\u4e2d\u3002 \u3000\u3000\u7f57\u5170\u9760\u5728\u5e8a\u5934\u4e0a\uff0c\u534a\u5750\u534a\u5367\uff0c\u5bb9\u8272\u4f9d\u65e7\u662f\u90a3\u4e48\u5a07\u8273\u3002 \u3000\u3000\u770b\u6c5f\u67ab\u4e00\u8138\u5fae\u5e26\u60ed\u6127\u7684\u53e4\u602a\u795e\u8272\uff0c\u7f57\u5170\u5f88\u60f3\u7b11\uff0c\u4f46\u53c8\u60f3\u5230\u5f20\u56db\u59d1\u7684\u8b66\u544a\uff0c\u53ea\u597d\u5f3a\u81ea\u5fcd\u4f4f\uff0c\u90a3\u79cd\u8981\u7b11\u4e0d\u7b11\u7684\u795e\u60c5\uff0c\u5176\u53e4\u602a\u5904\u5b9e\u4e0d\u5728\u6c5f\u67ab\u4e4b\u4e0b\u3002 \u3000\u3000\u541f\u971c\u770b\u770b\u6c5f\u67ab\uff0c\u53c8\u770b\u770b\u6bcd\u4eb2\uff0c\u5fcd\u4e0d\u4f4f\u683c\u683c\u5927\u7b11\u8d77\u6765\u3002 \u3000\u3000\u4f4e\u7740\u5934\u524a\u68a8\u5b50\u7684\u6885\u82b1\uff0c\u62ac\u5934\u770b\u4e86\u6c5f\u67ab\u4e00\u773c\uff0c\u53c8\u4f4e\u4e0b\u5934\u53bb\u3002 \u3000\u3000\u5979\u4e5f\u5728\u7b11\uff0c\u53ea\u662f\u7b11\u7684\u9690\u5bc6\u3002 \u3000\u3000\u5f20\u56db\u59d1\u9053\uff1a\u201c\u4e8c\u4e2b\u5934\uff0c\u522b\u7b11\u4e86\uff0c\u8d85\u7ea7\u5f3a\u654c\u5df2\u4f4f\u5165\u5ba2\u6808\uff0c\u5371\u673a\u8feb\u5728\u7709\u776b\uff0c\u4f60\u6c5f\u5927\u54e5\u6101\u7684\u8981\u6b7b\uff0c\u4f60\u8fd8\u7b11\u5f97\u51fa\u6765\u3002\u201d \u3000\u3000\u7f57\u5170\u6380\u88ab\u800c\u8d77\uff0c\u8df3\u4e0b\u5e8a\u6765\uff0c\u9053\uff1a\u201c\u6c5f\u67ab\uff0c\u662f\u4e0d\u662f\u771f\u7684\uff1f\u201d \u3000\u3000\u5929\u72d0\u6b66\u529f\u7684\u53e6\u4e00\u4e2a\u7279\u8272\uff0c\u662f\u9a7b\u989c\u4e5f\u9a7b\u5fc3\uff0c\u8ba9\u5973\u4eba\u4e0d\u5931\u7eaf\u771f\u3002 \u3000\u3000\u201c\u662f\u771f\u7684\uff01\u7f57\u5170\uff0c\u4f60\u7684\u4f24\u52bf\u600e\u4e48\u6837\u4e86\uff1f\u201d\u6c5f\u67ab\u770b\u7740\u7f57\u5170\u5de6\u81c2\u53f3\u817f\u4e0a\uff0c\u4ecd\u5305\u624e\u7740\u767d\u8272\u7ef7\u5e26\u3002 \u3000\u3000\u201c\u81c2\u4e0a\u7684\u5200\u4f24\u5df2\u7ed3\u75a4\uff0c\u53f3\u817f\u4e5f\u597d\u7684\u5dee\u4e0d\u591a\u4e86\uff0c\u2026\u2026\u201d\u7f57\u5170\u9053\uff1a\u201c\u5927\u6982\u53ef\u4ee5\u52a8\u5251\u4e86\u3002\u201d \u3000\u3000\u201c\u4e0d\u8981\u901e\u5f3a\uff0c\u6765\u4eba\u7684\u6b66\u529f\u53ef\u80fd\u5f88\u90aa\u6c14\u2026\u2026\u201d \u3000\u3000\u201c\u6c5f\u5927\u54e5\u2026\u2026\u201d\u541f\u96ea\u6253\u65ad\u4e86\u6c5f\u67ab\u7684\u8bdd\uff0c\u9053\uff1a\u201c\u80fd\u4e0d\u80fd\u770b\u51fa\u6765\uff0c\u4ed6\u7ec3\u7684\u662f\u90a3\u4e00\u7c7b\u7684\u6b66\u529f\uff1f\u201d \u3000\u3000\u201c\u6211\u6ca1\u6709\u89c1\u8fc7\uff0c\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u636e\u94b1\u7f3a\u544a\u8bc9\u6211\uff0c\u4ed6\u4eec\u5468\u8eab\u5982\u6709\u4e91\u6c14\u73af\u7ed5\uff0c\u900f\u53d1\u7740\u51b7\u5bd2\u8feb\u4eba\u7684\u8baf\u606f\uff01\u201d \u3000\u3000\u201c\u90a3\u662f\u4e00\u79cd\u5bd2\u9634\u4e4b\u6c14\u51dd\u805a\u4e4b\u5f81\uff0c\u2026\u2026\u201d\u53f6\u96ea\u9053\uff1a\u201c\u80fd\u5f62\u4e8e\u5916\uff0c\u8fd9\u4eba\u7684\u529f\u529b\u5f88\u6df1\u539a\u4e86\u3002\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u6211\u4eec\u4e0d\u80fd\u5927\u610f\uff0c\u6211\u5df2\u8bf7\u8001\u53eb\u5316\u901a\u77e5\u6240\u6709\u7684\u4eba\uff0c\u5728\u5385\u4e2d\u4f1a\u805a\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a \u3000\u3000\u201c\u4e0d\u80fd\u8ba9\u4ed6\u4eec\u540e\u63f4\u8d76\u5230\uff0c\u6211\u8981\u4e89\u53d6\u4e3b\u52a8\u3002\u201d \u3000\u3000\u201c\u5bf9\u4e86\u5148\u4e0b\u624b\u4e3a\u5f3a\uff0c\u6211\u548c\u59b9\u53bb\u6c42\u8bc1\u4e00\u4e0b\u3002\u201d \u3000\u3000\u541f\u96ea\u7275\u7740\u541f\u971c\u4e00\u53cc\u624b\uff0c\u7834\u7a97\u800c\u53bb\u3002 \u3000\u3000\u5f20\u56db\u59d1\u9053\uff1a\u201c\u9752\u51e4\u3001\u6885\u82b1\uff0c\u4f60\u4eec\u9041\u8eab\u5ead\u9662\uff0c\u76d1\u89c6\u56db\u5916\uff0c\u6700\u597d\u4e0d\u8ba9\u4efb\u4f55\u4eba\u770b\u5230\u4f60\u4eec\u3002\u201d \u3000\u3000\u201c\u662f\uff01\u6211\u4eec\u4f1a\u5c3d\u529b\u800c\u4e3a\u3002\u201d \u3000\u3000\u4e8c\u5973\u5e94\u4e86\u4e00\u58f0\uff0c\u4eba\u4e5f\u7a7f\u7a97\u800c\u53bb\u3002 \u3000\u3000\u201c\u5927\u59d0\u53c8\u6536\u4e86\u4e00\u4e2a\u5f1f\u5b50\uff1f\u201d \u3000\u3000\u201c\u9752\u51e4\u7684\u57fa\u7840\u624e\u5b9e\uff0c\u806a\u6167\u5c24\u8fc7\u6885\u82b1\uff0c\u2026\u2026\u201d\u5f20\u56db\u59d1\u4f4e\u58f0\u9053\uff1a\u201c\u5b66\u8d77\u4e94\u884c\u9041\u672f\uff0c\u9886\u609f\u6781\u5feb\uff0c\u8fd9\u4e16\u4e0a\u7684\u7075\u6167\u5c11\u5973\uff0c\u90fd\u88ab\u4f60\u7ed9\u6b3a\u8d1f\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u56db\u59d1\uff0c\u8fd9\u8bdd\u5c31\u4e0d\u516c\u5e73\u4e86\u2026\u2026\u201d\u7f57\u5170\u7adf\u7136\u63a5\u4e86\u53e3\uff0c\u9053\uff1a\u201c\u6211\u5bf9\u6c5f\u67ab\u8d8a\u6765\u8d8a\u4f69\u670d\u4e86\uff0c\u2026\u2026\u201d \u3000\u3000\u201c\u7f57\u5170\uff0c\u597d\u597d\u7684\u5750\u606f\uff0c\u8bf4\u4e0d\u5b9a\u4e00\u5165\u591c\uff0c\u5c31\u8981\u5c55\u5f00\u4e00\u573a\u6076\u6218\u2026\u2026\u201d \u3000\u3000\u5f20\u56db\u59d1\u7275\u4e00\u4e0b\u6c5f\u67ab\u8863\u8896\uff0c\u6025\u6025\u9000\u51fa\u3002 \u3000\u3000\u6c5f\u67ab\u4f4e\u58f0\u9053\uff1a\u201c\u5927\u59d0\uff0c\u7f57\u5170\u4f69\u670d\u6211\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u4e24\u4e2a\u5c0f\u72d0\u5973\u4ecd\u4fdd\u6709\u7740\u5904\u5b50\u4e4b\u8eab\uff0c\u662f\u5417\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u70b9\u70b9\u5934\u3002 \u3000\u3000\u5f20\u56db\u59d1\u9053\uff1a\u201c\u4f60\u5982\u8981\u5360\u6709\u5979\u4eec\uff0c\u5979\u4eec\u4e0d\u4f1a\u62d2\u7edd\u5427\uff01\u201d \u3000\u3000\u201c\u8fd9\u6211\u5c31\u4e0d\u77e5\u9053\u4e86\uff0c\u6211\u6ca1\u6709\u8bd5\u8fc7\u3002\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u4e5f\u6ca1\u6709\u52a8\u8fc7\u8fd9\u4e2a\u5ff5\u5934\u3002\u201d \u3000\u3000\u201c\u7f57\u5170\u4f69\u670d\u4f60\u5c31\u662f\u8fd9\u4e00\u70b9\u4e86\uff0c\u4e24\u4e2a\u5c0f\u72d0\u5973\u5929\u4ed9\u5316\u4eba\uff0c\u53c8\u6bcf\u5929\u7f20\u5728\u4f60\u8eab\u4e0a\uff0c\u8033\u9b13\u53ae\u78e8\uff0c\u4f60\u80fd\u98df\u6307\u4e0d\u52a8\uff0c\u8fde\u5927\u59d0\u4e5f\u6709\u70b9\u4f69\u670d\u4e86\uff0c\u4f60\u5916\u9762\u653e\u8361\uff0c\u5185\u5fc3\u541b\u5b50\uff0c\u7f57\u5170\u8981\u8bf4\u7684\u5c31\u662f\u8fd9\u4e2a\uff0c\u4f60\u4eec\u7684\u5173\u7cfb\u4e0d\u540c\uff0c\u6211\u4e0d\u60f3\u5979\u8bf4\u7684\u592a\u9732\u9aa8\u3002\u201d \u3000\u3000\u201c\u5927\u59d0\u8bf4\u7684\u662f\uff01\u201d \u3000\u3000\u4f46\u95fb\u6b65\u5c65\u58f0\u52a8\uff0c\u8001\u53eb\u5316\u5e26\u7740\u7fa4\u8c6a\u5927\u6b65\u800c\u5165\u3002 \u3000\u3000\u6b27\u9633\u662d\u8d70\u5728\u6700\u540e\uff0c\u4e94\u4e2a\u5f1f\u5b50\u8138\u4e0a\u6c57\u6c34\u9690\u73b0\uff0c\u60f3\u662f\u5728\u53e6\u4e00\u5ea7\u8de8\u9662\u4e2d\u4e60\u7ec3\u5251\u9635\u3002 \u3000\u3000\u5317\u5251\u7236\u5b50\uff0c\u5357\u5200\u5e08\u5f92\uff0c\u9093\u98de\uff0c\u5201\u9e4f\u3001\u94c1\u62c2\u5927\u5e08\u3001\u9752\u9e64\u9053\u957f\uff0c\u52a0\u4e0a\u6b27\u9633\u662d\u5e08\u5f92\u516d\u4eba\u548c\u8001\u53eb\u5316\u5b50\u4e00\u5171\u5341\u4e94\u4e2a\u4eba\uff0c\u628a\u4e00\u5ea7\u5c0f\u5385\u5feb\u6324\u6ee1\u4e86\u3002 \u3000\u3000\u684c\u6905\u4e0d\u591f\uff0c\u5927\u5bb6\u5c31\u5e2d\u5730\u800c\u5750\u3002 \u3000\u3000\u6c5f\u67ab\u4e00\u62b1\u62f3\uff0c\u9053\uff1a\u201c\u8bf8\u4f4d\u524d\u8f88\uff0c\u5144\u5f1f\uff0c\u7ec8\u5357\u5c71\u4e00\u6218\uff0c\u635f\u5931\u4e86\u6211\u4eec\u5927\u90e8\u5206\u7684\u540c\u9053\uff0c\u6c5f\u67ab\u4e00\u5206\u60b2\u75db\uff01\u201d \u3000\u3000\u201c\u6c5f\u5144\u5f1f\uff0c\u6b66\u6797\u8499\u5c18\uff0c\u6c5f\u6e56\u6ca6\u52ab\u2026\u2026\u201d\u8001\u53eb\u5316\u63a5\u9053\uff1a\u201c\u4ed6\u4eec\u4e3a\u6b66\u6797\u91cd\u5149\u5927\u4e1a\u800c\u6218\uff0c\u6b7b\u800c\u4f55\u61be\uff1f\u5982\u4eca\u5f3a\u654c\u53c8\u8ffd\u8e2a\u800c\u6765\uff0c\u5927\u6709\u65a9\u5c3d\u6740\u7edd\u4e4b\u5fc3\uff0c\u73b0\u5728\u5982\u4f55\u62d2\u654c\uff0c\u624d\u662f\u8981\u7814\u5546\u7684\u4e8b\u60c5\u3002\u201d \u3000\u3000\u6ca1\u6709\u4eba\u53cd\u5bf9\uff0c\u770b\u6765\u6d3b\u4e0b\u6765\u4f3c\u662f\u66f4\u4e3a\u91cd\u8981\u3002 \u3000\u3000\u201c\u640f\u6740\u5f3a\u654c\u4e00\u4e8b\uff0c\u6c5f\u67ab\u5168\u529b\u4ee5\u8d74\uff0c\u4f46\u9762\u9635\u7528\u5175\u4e4b\u7b56\uff0c\u8fd8\u8981\u9ebb\u70e6\u5f20\u5927\u59d0\u4e86\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u9eef\u7136\u53f9\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u654c\u4eba\u4ee5\u98de\u9e70\u3001\u7075\u5149\u89c5\u8e2a\uff0c\u6211\u4eec\u907f\u65e0\u53ef\u907f\uff0c\u53ea\u6709\u633a\u8eab\u800c\u6218\u4e00\u9014\uff0c\u8bf8\u4f4d\u968f\u8eab\u643a\u5e26\u8863\u7269\uff0c\u4eca\u591c\u4e00\u6218\u4e4b\u540e\uff0c\u6211\u4eec\u53ef\u80fd\u64a4\u79bb\u5f00\u5c01\uff0c\u907f\u514d\u5b9e\u529b\u592a\u8fc7\u5206\u6563\u3002 \u3000\u3000\u8bf8\u4f4d\u5206\u7ec4\u57cb\u4f0f\uff0c\u6d4b\u8bd5\u654c\u4eba\u5b9e\u529b\u4e4b\u540e\uff0c\u5982\u80fd\u4e00\u4e3e\u83b7\u80dc\uff0c\u5c31\u628a\u4ed6\u4eec\u8bf1\u5165\u8352\u90ca\uff0c\u4e00\u4e3e\u6b7c\u706d\uff0c\u6b64\u6218\u4e4b\u7b56\uff0c\u4ee5\u6740\u654c\u4e3a\u4e3b\u2026\u2026\u201d \u3000\u3000\u5f20\u56db\u59d1\u5212\u5206\u5404\u7ec4\u7684\u4eba\u624b\u540e\uff0c\u7fa4\u8c6a\u79bb\u5f00\u5c0f\u5385\uff0c\u5404\u53bb\u51c6\u5907\u3002 \u3000\u3000\u4f46\u6b27\u9633\u662d\u6ca1\u6709\u79bb\u53bb\uff0c\u8fd9\u4f4d\u4ee5\u6b63\u9053\u81ea\u8bb8\uff0c\u7aef\u5728\u81ea\u6301\u7684\u4eba\u7269\uff0c\u8eab\u7ecf\u7ec8\u5357\u5c71\u4e00\u573a\u8840\u6218\u4e4b\u540e\uff0c\u4e5f\u6709\u4e86\u5f88\u5927\u7684\u6539\u53d8\uff0c\u5bf9\u6c5f\u67ab\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u7f57\u5170\u53c8\u6536\u4e86\u4e24\u4e2a\u5e72\u5973\u513f\uff0c\u4e5f\u540c\u65f6\u62dc\u5165\u4e86\u5f20\u56db\u7ad9\u7684\u95e8\u4e0b\uff0c\u8fd9\u5012\u7701\u4e86\u4e0d\u5c11\u4e8b\uff0c\u56db\u4e2a\u5973\u513f\u4e00\u6b21\u51fa\u5ac1\uff0c\u4e5f\u7b97\u662f\u524d\u6240\u672a\u6709\u7684\u4e00\u6bb5\u4f73\u8bdd\u4e86\u3002\u201d \u3000\u3000\u6c5f\u67ab\u65e0\u6cd5\u56de\u7b54\uff0c\u53ea\u597d\u8fde\u58f0\u5e94\u662f\u3002 \u3000\u3000\u201c\u53bb\u770b\u770b\u7f57\u5170\u5427\uff01\u4f60\u8001\u5a46\u5df2\u7ecf\u51c6\u5907\u66f4\u8863\u5e94\u6218\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u5979\u817f\u4f24\u8fd8\u672a\u5168\u597d\uff0c\u4e0d\u5b9c\u821e\u5200\u3001\u5f04\u5251\uff0c\u4f46\u6211\u4eec\u529d\u4e0d\u4f4f\u5979\u2026\u2026\u201d \u3000\u3000\u201c\u80e1\u95f9\uff01\u80e1\u95f9\uff01\u5979\u817f\u4f24\u8fd8\u6765\u6536\u53e3\uff0c\u56db\u59d1\uff0c\u4f60\u53ef\u4e0d\u80fd\u6d3e\u4efb\u52a1\u7ed9\u5979\u2026\u2026\u201d\u6b27\u9633\u662d\u771f\u7684\u6025\u4e86\u3002 \u3000\u3000\u201c\u53ef\u4ee5\uff01\u4f46\u5979\u751f\u6027\u597d\u5f3a\uff0c\u81ea\u5df1\u63d0\u5251\u4e0a\u9635\uff0c\u6211\u53ef\u662f\u6ca1\u6709\u6cd5\u5b50\u62e6\u963b\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u4e00\u573a\u82e6\u6218\u4e0b\u6765\uff0c\u5982\u82b1\u4f3c\u7389\u7684\u8001\u5a46\uff0c\u8dcb\u4e86\u4e00\u6761\u817f\uff0c\u90a3\u5c31\u7f8e\u4e2d\u4e0d\u8db3\u4e86\u3002\u201d \u3000\u3000\u6b27\u9633\u662d\u4e0d\u518d\u7b54\u8bdd\uff0c\u51b2\u5165\u4e86\u7f57\u5170\u7684\u623f\u4e2d\u3002 \u3000\u3000\u201c\u53bb\u770b\u770b\u4e24\u4e2a\u5c0f\u72d0\u5973\uff0c\u600e\u4e48\u8fd9\u6837\u4e45\u8fd8\u672a\u56de\u6765\uff1f\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u4f1a\u4e0d\u4f1a\u6709\u4ec0\u4e48\u95ea\u5931\uff1f\u201d \u3000\u3000\u201c\u4e0d\u4f1a\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u5979\u4eec\u4e24\u4e2a\u771f\u7684\u88ab\u4eba\u7559\u4e0b\u4e86\uff0c\u6211\u53bb\u4e86\u4e5f\u6ca1\u6709\u7528\u3002\u201d \u3000\u3000\u201c\u6c5f\u5927\u54e5\u597d\u8c26\u865a\u554a\uff01\u201d\u541f\u971c\u9053\uff1a\u201c\u6211\u548c\u59d0\u90fd\u660e\u767d\uff0c\u4e24\u4e2a\u4eba\u52a0\u8d77\u6765\uff0c\u8fd8\u662f\u4e0d\u5982\u6c5f\u5927\u54e5\u3002\u201d \u3000\u3000\u4e24\u59d0\u59b9\u7275\u7740\u624b\uff0c\u7a7f\u7a97\u800c\u5165\u3002 \u3000\u3000\u201c\u5947\u602a\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u8d70\u5385\u95e8\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u662f\u4e0d\u662f\u7a7f\u7a97\u8d8a\u5899\uff0c\u5df2\u6210\u4e60\u60ef\uff1f\u201d \u3000\u3000\u201c\u4e0d\u662f\uff01\u201d\u541f\u971c\u9053\uff1a\u201c\u9752\u51e4\u548c\u6885\u82b1\u59d0\u59d0\uff0c\u65bd\u5c55\u9041\u672f\uff0c\u9690\u5728\u5ead\u9662\u4e2d\uff0c\u6211\u4eec\u4e0d\u80fd\u770b\u5230\u5979\u4eec\u3002\u201d \u3000\u3000\u201c\u7a76\u7adf\u662f\u770b\u5230\u6ca1\u6709\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\uff01\u201d\u541f\u96ea\u9053\uff1a\u201c\u5f20\u59e8\uff0c\u6211\u4eec\u6ca1\u6709\u7ecf\u8fc7\u5ead\u9662\uff0c\u81ea\u7136\u662f\u770b\u4e0d\u5230\u4e86\uff01\u201d \u3000\u3000\u201c\u90a3\u4e48\u591a\u4eba\uff0c\u90fd\u6ca1\u6709\u770b\u5230\uff0c\u6211\u4eec\u600e\u4e48\u80fd\u770b\u5230\u5462\uff1f\u2026\u2026\u201d\u541f\u971c\u9053\uff1a\u201c\u884c\u8fc7\u5ead\u9662\uff0c\u6b65\u5165\u5385\u95e8\uff0c\u5f20\u59e8\u8981\u662f\u95ee\u6211\u4eec\u770b\u5230\u4e24\u4f4d\u59d0\u59d0\u6ca1\u6709\uff0c\u8981\u6211\u4eec\u600e\u4e48\u56de\u7b54\u5462\uff1f\u201d \u3000\u3000\u5f20\u56db\u59d1\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u679c\u7136\u5389\u5bb3\uff0c\u5df2\u7ecf\u9752\u51fa\u4e8e\u84dd\u4e86\uff01\u201d \u3000\u3000\u201c\u540d\u5e08\u9ad8\u5f92\u554a\uff01\u201d\u6c5f\u67ab\u7b11\u9053\uff1a\u201c\u5979\u4eec\u8bba\u4e8b\u5224\u654c\uff0c\u5df2\u786e\u6709\u5927\u59d0\u7684\u6167\u80fd\u3002\u201d \u3000\u3000\u201c\u8bf4\u5427\uff01\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u654c\u52bf\u5982\u4f55\uff1f\u662f\u5426\u5df2\u6709\u4e86\u67dc\u654c\u4e4b\u7b56\uff1f\u201d \u3000\u3000\u541f\u96ea\u770b\u7740\u541f\u971c\uff0c\u541f\u971c\u5374\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u4f60\u8bf4\u5427\uff01\u59d0\u3002\u6211\u60f3\u7684\u4e0d\u5f88\u5468\u5168\u3002\u201d \u3000\u3000\u201c\u662f\u4e24\u4e2a\u771f\u6b63\u7684\u9ad8\u624b\uff0c\u7384\u9634\u6c14\u529f\uff0c\u5df2\u81f3\u767b\u5cf0\u9020\u6781\u2026\u2026\u201d\u541f\u96ea\u653e\u4f4e\u58f0\u97f3\uff0c\u9053\uff1a\u201c\u4e00\u5bf9\u4e00\uff0c\u7239\u4e5f\u672a\u5fc5\u662f\u4eba\u654c\u624b\uff0c\u4f46\u6700\u4f7f\u6211\u62c5\u5fc3\u7684\u662f\uff0c\u4e0d\u77e5\u9053\u4ed6\u4eec\u8fd8\u7ec3\u4e86\u4ec0\u4e48\u6bd2\u529f\uff1f \u3000\u3000\u6240\u4ee5\uff0c\u6211\u60f3\u628a\u4ed6\u4eec\u8bf1\u5165\u8352\u90ca\uff0c\u8bf7\u6c5f\u5927\u54e5\u548c\u4ed6\u4eec\u653e\u624b\u4e00\u6218\u3002 \u3000\u3000\u4e00\u6765\uff0c\u53ef\u6d4b\u8bd5\u4e00\u4e0b\u6c5f\u5927\u54e5\u7684\u6210\u5c31\u3002 \u3000\u3000\u4e8c\u6765\uff0c\u4e5f\u8ba9\u5bf9\u65b9\u66b4\u9732\u51fa\u4ed6\u4eec\u7684\u6076\u6bd2\u6b66\u529f\uff0c\u4f7f\u6211\u4eec\u6709\u6240\u9632\u8303\uff0c\u4e0d\u77e5\u5f20\u59e8\u7684\u610f\u4e0b\u5982\u4f55\uff1f\u201d \u3000\u3000\u201c\u60f3\u7684\u5f88\u5468\u5230\uff0c\u6211\u4eec\u9009\u62e9\u4e00\u4e2a\u5730\u70b9\uff0c\u5f15\u4ed6\u4eec\u5165\u4f0f\uff0c\u53ea\u662f\u8c01\u4eba\u4f5c\u9975\u5462\uff1f\u201d \u3000\u3000\u201c\u81ea\u7136\u662f\u6c5f\u5927\u54e5\u4e86\u2026\u2026\u201d\u541f\u96ea\u7b11\u9053\uff1a\u201c\u4ed6\u4eec\u6700\u5927\u7684\u76ee\u7684\uff0c\u5c31\u662f\u6740\u4e86\u6c5f\u5927\u54e5\uff0c\u6c5f\u5927\u54e5\u662f\u6700\u597d\u7684\u9975\uff0c\u4ed6\u4eec\u51b3\u4e0d\u4f1a\u653e\u624b\u3002\u201d \u3000\u3000\u201c\u662f\u4e0d\u662f\u8981\u6211\u8bc8\u8d25\uff1f\u201d \u3000\u3000\u201c\u5bf9\uff01\u4f46\u4f60\u4e0d\u80fd\u8ba9\u4ed6\u4eec\u77a7\u51fa\u4e86\u7834\u7efd\uff0c\u6700\u597d\u662f\u4f60\u80fd\u7a0d\u80dc\u4e00\u4eba\uff0c\u8ba9\u4ed6\u4eec\u8054\u624b\u5408\u51fb\uff0c\u4f60\u518d\u8bc8\u8d25\u3002\u201d\u541f\u96ea\u9053\uff1a\u201c\u8352\u90ca\u591c\u6218\uff0c\u518d\u5168\u529b\u65bd\u4e3a\uff0c\u4e94\u5341\u62db\u5185\uff0c\u8fd8\u4e0d\u80fd\u529b\u6bd9\u4e24\u654c\uff0c\u6211\u548c\u59b9\u5c31\u65bd\u4e0b\u6bd2\u624b\u53d6\u4ed6\u4eec\u7684\u6027\u547d\u3002\u201d \u3000\u3000\u201c\u4ee5\u4e00\u4eba\uff0c\u529b\u654c\u4e24\u4e2a\u9ad8\u4eba\u8054\u5408\u51fa\u624b\uff1f\u2026\u2026\u201d\u5f20\u56db\u59d1\u6709\u4e9b\u62c5\u5fc3\u8d77\u6765\uff0c\u8bf4\uff0c\u201c\u6c5f\u67ab\u80fd\u5e94\u4ed8\u5417\uff1f\u201d \u3000\u3000\u201c\u5e94\u8be5\u53ef\u4ee5\u2026\u2026\u201d\u541f\u96ea\u9053\uff1a\u201c\u81f3\u5c11\u4ed6\u53ef\u4ee5\u9003\u6389\uff0c\u5f20\u59e8\uff0c\u653e\u5fc3\u4e86\uff0c\u6211\u600e\u80fd\u8c0b\u6740\u4eb2\u592b\u2026\u2026\u201d \u3000\u3000\u201c\u59d0\uff0c\u6211\u4e5f\u6709\u70b9\u62c5\u5fc3\u554a\uff01\u201d\u541f\u971c\u9053\uff1a\u201c\u4e0d\u80fd\u8ba9\u6c5f\u5927\u54e5\u4e8c\u4eba\u6d89\u9669\u3002\u201d \u3000\u3000\u201c\u90a3\u4f60\u5c31\u9041\u8eab\u6f5c\u8e2a\uff0c\u4e00\u65c1\u89c2\u6218\uff0c\u5fc5\u8981\u65f6\u51fa\u624b\u76f8\u52a9\u2026\u2026\u201d\u541f\u96ea\u9053\uff1a\u201c\u4f46\u4e0d\u53ef\u9732\u51fa\u9a6c\u811a\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u7528\u4e86\uff0c\u541f\u971c\uff0c\u541f\u96ea\u8bf4\u7684\u5bf9\uff0c\u6253\u4e0d\u8fc7\uff0c\u6211\u53ef\u4ee5\u8dd1\u554a\uff01\u4f60\u9690\u8eab\u4e00\u4fa7\uff0c\u6211\u53cd\u800c\u5fc3\u6709\u6240\u8651\uff0c\u505a\u4e0d\u5230\u6070\u5982\u5176\u5206\uff0c\u654c\u4eba\u6709\u9ad8\u624b\uff0c\u4e5f\u6709\u667a\u8005\uff0c\u9519\u4e00\u6b65\uff0c\u5c31\u5750\u5931\u8bf1\u6740\u826f\u673a\u3002\u201d \u3000\u3000\u201c\u597d\u5427\uff01\u6211\u5e94\u8be5\u5bf9\u6c5f\u5927\u54e5\u5bc4\u4e8e\u4fe1\u5fc3\u2026\u2026\u201d \u3000\u3000\u541f\u971c\u8bf4\u7684\u5f88\u59d4\u5c48\uff0c\u773c\u7736\u4e2d\u89c1\u4e86\u6cea\u6c34\u3002 \u3000\u3000\u6c5f\u67ab\u4e0d\u6562\u591a\u770b\uff0c\u6025\u6025\u8bf4\u9053\uff1a\u201c\u6211\u53bb\u770b\u770b\u548c\u5c1a\uff0c\u5927\u59d0\u8d39\u5fc3\u5b89\u6392\u5730\u70b9\uff0c\u5e03\u7f6e\u57cb\u4f0f\u3002\u201d\u6025\u6b65\u884c\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u541f\u96ea\u5446\u5446\u7684\u671b\u7740\u6c5f\u67ab\u6d88\u5931\u7684\u80cc\u666f\uff0c\u9053\uff1a\u201c\u5f20\u59e8\uff0c\u6211\u662f\u4e0d\u662f\u9519\u4e86\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\u9519\uff0c\u9a91\u9a6c\u884c\u8239\u4e09\u5206\u9669\uff0c\u4f55\u51b5\u662f\u5bf9\u654c\u4f5c\u6218\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u51b3\u5b9a\u4e86\u5c31\u8fd9\u4e48\u529e\u3002\u201d \u3000\u3000\u201c\u59b9\uff0c\u4f60\u8bf4\u5462\uff1f\u6211\u5fc3\u91cc\u597d\u4e0d\u5b89\u3002\u201d \u3000\u3000\u5f53\u771f\u662f\u4e8b\u4e0d\u5173\u5df1\uff0c\u5173\u5df1\u5219\u4e71\uff0c\u4e09\u4e2a\u5973\u4eba\u5fc3\u4e2d\uff0c\u90fd\u6df1\u6df1\u5730\u5370\u7740\u6c5f\u90ce\uff0c\u4ee5\u4ed6\u4f5c\u9975\uff0c\u518d\u597d\u8c0b\u7565\uff0c\u4e5f\u6709\u4e9b\u4e0d\u6562\u653e\u624b\u3002 \u3000\u3000\u201c\u7167\u8bf4\uff0c\u6c5f\u5927\u54e5\u80fd\u591f\u80dc\u4efb\uff0c\u6211\u53ea\u662f\u62c5\u5fc3\u4e07\u4e00\u2026\u2026\u201d\u541f\u971c\u9053\uff1a\u201c\u4e07\u4e00\u4ed6\u53d7\u4e86\u4f24\uff0c\u4f60\u4e0d\u5fc3\u75bc\u554a\uff1f\u201d \u3000\u3000\u201c\u5509\uff01\u6c5f\u5927\u54e5\uff0c\u54ea\u91cc\u53d7\u4f24\uff0c\u59d0\u5c31\u628a\u54ea\u91cc\u5272\u4e0b\u6765\u8865\u7ed9\u4ed6\u3002\u201d \u3000\u3000\u201c\u59d0\uff0c\u4e0d\u8981\u8fd9\u6837\u561b\uff01\u6c5f\u5927\u54e5\uff0c\u80dc\u8fc7\u6211\u4eec\u5341\u500d\uff0c\u4ed6\u4e0d\u4f1a\u53d7\u4f24\u7684\uff0c\u6211\u53ea\u662f\u778e\u80e1\u8bf4\u5440\uff01\u201d \u3000\u3000\u201c\u4ed6\u53ea\u6709\u5341\u4e4b\u4e00\u4e8c\u7684\u5371\u9669\uff0c\u4e00\u662f\u654c\u4eba\u53e6\u6709\u9ad8\u624b\u9690\u85cf\uff0c\u5c55\u5f00\u5408\u51fb\uff0c\u4e8c\u662f\u654c\u4eba\u7ec3\u6210\u4e86\u83ab\u53ef\u6d4b\u5ea6\u7684\u6b66\u529f\uff0c\u4e00\u51fa\u624b\u5c31\u65bd\u5c55\u51fa\u6765\uff0c\u2026\u2026\u201d\u541f\u96ea\u9053\uff1a\u201c\u6c5f\u5927\u54e5\u63aa\u624b\u4e0d\u53ca\uff01\u201d \u3000\u3000\u542c\u5230\u4e8c\u5973\u4ea4\u8c08\uff0c\u5f20\u56db\u59d1\u5012\u662f\u653e\u4e86\u5fc3\uff0c\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u6837\u5b50\u77bb\u524d\u987e\u540e\uff0c\u54ea\u91cc\u80fd\u6210\u5c31\u5927\u4e8b\uff0c\u6211\u4f1a\u4ea4\u4ee3\u4ed6\u5c0f\u5fc3\u5e94\u654c\uff0c\u4e0d\u8981\u518d\u60f3\u8fd9\u4ef6\u4e8b\u4e86\uff0c\u6765\uff01\u6211\u4eec\u7814\u5546\u4e00\u4e0b\u8bbe\u57cb\u5730\u70b9\uff01\u201d \u3000\u3000\u6c5f\u67ab\u627e\u5230\u4e86\u5927\u548c\u5c1a\uff0c\u90a3\u662f\u5728\u76f8\u56fd\u5bfa\u5916\u7684\u8c46\u513f\u5e84\uff0c\u5927\u548c\u5c1a\u4e00\u4e2a\u4eba\u8eb2\u5728\u623f\u95f4\u91cc\uff0c\u81ea\u659f\u81ea\u996e\uff0c\u6b63\u559d\u7684\u4e0d\u4ea6\u4e50\u4e4e\uff0c\u6c5f\u67ab\u5374\u7a81\u7136\u8fdb\u95e8\u3002 \u3000\u3000\u5927\u548c\u5c1a\u6014\u4e86\u4e00\u6014\uff0c\u7b11\u9053\uff1a\u201c\u6211\u5fd8\u4e86\uff0c\u4f60\u662f\u80f8\u6709\u9526\u7ee3\uff0c\u548c\u5c1a\u8eb2\u4e0d\u8fc7\u3002\u201d\u4e00\u9762\u7728\u52a8\u773c\u775b\uff0c\u793a\u610f\u6c5f\u67ab\uff0c\u8fd9\u91cc\u6709\u95ee\u9898\uff0c\u4e0d\u53ef\u5927\u610f\u3002 \u3000\u3000\u679c\u7136\uff0c\u4e8c\u8c46\u59d1\u5a18\uff0c\u5f88\u5feb\u7684\u51b2\u4e86\u8fdb\u6765\uff0c\u6253\u91cf\u4e86\u6c5f\u67ab\u4e00\u9635\uff0c\u7b11\u9053\uff1a\u201c\u539f\u6765\u4e24\u4f4d\u662f\u670b\u53cb\u554a\uff01\u6211\u53bb\u62ff\u676f\u7b77\u3002\u201d \u3000\u3000\u201c\u5144\u5f1f\uff0c\u9017\u9017\u5979\u5427\uff01\u201d\u5927\u548c\u5c1a\u7528\u7684\u662f\u4f20\u97f3\u4e4b\u672f\uff0c\u9053\uff1a\u201c\u4e2b\u5934\u8033\u76ee\u806a\u654f\uff0c\u6b66\u529f\u4e0d\u9519\uff0c\u5c0f\u6cb3\u91cc\uff0c\u53ef\u80fd\u6f5c\u85cf\u4e86\u4e00\u6761\u5927\u9c7c\u3002\u201d \u3000\u3000\u201c\u6211\u2026\u2026\u201d \u3000\u3000\u201c\u4f60\u6293\u5230\u4e86\u6211\u548c\u5c1a\u7684\u628a\u67c4\uff0c\u5927\u5757\u5403\u8089\uff0c\u5927\u7897\u559d\u9152\uff0c\u4eca\u5929\u5c31\u7d22\u6027\u559d\u4e2a\u75db\u5feb\uff0c\u4e0d\u9189\u4e0d\u5f52\u2026\u2026\u201d \u3000\u3000\u201c\u597d\u554a\uff01\u4e24\u4f4d\u5c3d\u7ba1\u559d\uff0c\u8fd9\u91cc\u6709\u597d\u9152\u3001\u597d\u83dc\uff0c\u4fdd\u541b\u6ee1\u610f\uff0c\u800c\u4e14\u8ddd\u665a\u9910\u4e0a\u5ba2\u8fd8\u65e9\u3002\u201d\u4e8c\u8c46\u7684\u8bdd\uff0c\u660e\u663e\u5730\u6ca1\u6709\u8bf4\u5b8c\uff0c\u4f46\u5979\u5374\u4f4f\u53e3\u4e0d\u8a00\u3002 \u3000\u3000\u8fd9\u5927\u6982\u5c31\u662f\u5c11\u5973\u7684\u77dc\u6301\u4e86\u3002 \u3000\u3000\u201c\u9152\u83dc\u90fd\u4e0d\u9519\uff0c\u5c31\u662f\u5c11\u4e24\u4e2a\u966a\u9152\u7684\u7f8e\u5973\uff0c\u548c\u5c1a\u662f\u6ca1\u6709\u8fd9\u4e2a\u6bdb\u75c5\uff0c\u6211\u8fd9\u4f4d\u5144\u5f1f\uff0c\u53ef\u5c31\u65e0\u5973\u4e0d\u6b22\u4e86\uff0c\u600e\u4e48\u6837\uff0c\u4e8c\u8c46\u59d1\u5a18\uff0c\u80fd\u7834\u4f8b\u8ba9\u548c\u5c1a\u8eb2\u5728\u623f\u91cc\u559d\u9152\u3001\u5403\u8089\uff0c\u80fd\u4e0d\u80fd\u7834\u4f8b\u627e\u4e00\u4f4d\u59d1\u5a18\u966a\u9152\u3002\u201d \u3000\u3000\u201c\u6211\u4eec\u5e97\u9762\u5c0f\uff0c\u4ece\u672a\u9047\u8fc7\u8fd9\u79cd\u4e8b\uff0c\u6211\u53bb\u95ee\u95ee\u5988\uff0c\u8be5\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u4e8c\u8c46\u8bf4\u5b8c\u8bdd\uff0c\u8f6c\u8eab\u800c\u53bb\u3002 \u3000\u3000\u201c\u5144\u5f1f\uff0c\u548c\u5c1a\u5df2\u62c9\u5f00\u573a\u5b50\uff0c\u4ee5\u540e\u770b\u4f60\u7684\u4e86\u3002\u201d \u3000\u3000\u201c\u5927\u548c\u5c1a\uff0c\u5f3a\u654c\u5df2\u627e\u4e0a\u4eac\u534e\u5ba2\u6808\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u4e5f\u7528\u4f20\u97f3\u4e4b\u672f\uff0c\u9053\uff1a\u201c\u56db\u59d1\u5df2\u5b89\u6392\u4eca\u665a\u7684\u5927\u6218\u5e03\u7f72\uff0c\u559d\u4e24\u676f\uff0c\u6211\u4eec\u4e00\u8d77\u56de\u53bb\u3002\u201d \u3000\u3000\u5927\u548c\u5c1a\u5fae\u5fae\u4e00\u6014\uff0c\u9053\uff1a\u201c\u6865\u5f52\u6865\uff0c\u8def\u5f52\u8def\uff0c\u7b49\u4e00\u4e0b\uff0c\u548c\u5c1a\u5148\u8d70\uff0c\u4f60\u638f\u638f\u770b\uff0c\u4e24\u4e2a\u4e2b\u5934\u809a\u5b50\u91cc\u6709\u4e9b\u4ec0\u4e48\u5b9d\uff1f \u3000\u3000\u4e0d\u8981\u5fc3\u5b58\u4ec1\u6148\uff0c\u5979\u4eec\u662f\u654c\u4eba\uff0c\u4f60\u4e0d\u4e0b\u624b\uff0c\u522b\u4eba\u7740\u4e86\u5148\u97ad\uff0c\u6765\u4e2a\u9634\u6784\u91cc\u7ffb\u8239\uff0c\u53ef\u522b\u602a\u6211\uff01\u201d \u3000\u3000\u5927\u548c\u5c1a\u8bf4\u5bf9\u4e86\uff0c\u4e00\u6765\u5c31\u6765\u4e86\u4e24\u4e2a\uff0c\u4e8c\u8c46\u7275\u7740\u4e09\u8c46\u624b\uff0c\u5e76\u7740\u80a9\u513f\u884c\u8fdb\u6765\u3002 \u3000\u3000\u201c\u6211\u5988\u8bf4\uff0c\u5c0f\u996d\u5e97\u4e0d\u5174\u8fd9\u4e2a\uff0c\u5b9e\u5728\u65e0\u6cd5\u627e\u59d1\u5a18\u966a\u9152\uff0c\u6211\u966a\u4e24\u4f4d\u559d\u51e0\u676f\uff0c\u5988\u53c8\u4e0d\u653e\u5fc3\uff0c\u8981\u6211\u5e26\u7740\u59b9\u59b9\u4e00\u8d77\u6765\u2026\u2026\u201d \u3000\u3000\u4e8c\u8c46\u8bf4\u7684\u5f88\u81ea\u7136\uff0c\u6709\u5982\u80cc\u4e66\u4e00\u822c\u3002 \u3000\u3000\u5927\u548c\u5c1a\u772f\u7740\u773c\u775b\u6253\u91cf\u4e8c\u8c46\u3001\u4e09\u8c46\uff0c\u7adf\u8fd8\u662f\u771f\u7684\u6709\u770b\u5934\uff0c\u867d\u7136\u6bd4\u4e0d\u4e0a\u4e24\u4e2a\u5c0f\u72d0\u5973\uff0c\u4e5f\u7a0d\u900a\u6885\u82b1\u3001\u9752\u51e4\uff0c\u4f46\u4e5f\u7b97\u5a07\u82e5\u6625\u82b1\u3002 \u3000\u3000\u5979\u4eec\u7a7f\u7740\u84dd\u5e03\u8863\u88d9\uff0c\u8fce\u5ba2\u4e0a\u83dc\uff0c\u4e5f\u6ca1\u6709\u64e6\u7c89\u3001\u62b9\u80ed\u8102\uff0c\u5168\u662f\u5929\u751f\u7684\u4e3d\u8d28\uff0c\u59ff\u8272\u3002 \u3000\u3000\u201c\u597d\uff01\u597d\uff01\u548c\u5c1a\u867d\u662f\u5047\u548c\u5c1a\uff0c\u8dd1\u7684\u7801\u5934\u53ef\u4e0d\u5c11\uff0c\u996d\u83dc\u53eb\u7684\u966a\u9152\u59d1\u5a18\uff0c\u4e24\u4f4d\u662f\u62d4\u5c16\u7684\u6f02\u4eae\u3002 \u3000\u3000\u518d\u8bf4\u8bf4\u6211\u8fd9\u4e2a\u670b\u53cb\u5427\uff01\u6211\u662f\u4e0d\u4f2f\u5bb6\u4e2d\u5931\u706b\uff0c\u53ea\u6015\u5728\u5916\u9762\u6454\u4e00\u8de4\uff0c\u5168\u90e8\u5bb6\u5f53\uff0c\u90fd\u7a7f\u5728\u8eab\u4e0a\uff0c\u5bb6\u5f92\u56db\u58c1\uff0c\u98df\u65e0\u5b58\u7cae\u3002 \u3000\u3000\u4f46\u4ed6\u4f1a\u76ef\u7a0d\uff0c\u6293\u4e86\u6211\u548c\u5c1a\u4e0d\u5c11\u628a\u67c4\uff0c\u4e00\u5411\u662f\u6211\u8bf7\u5ba2\uff0c\u4eca\u5929\u4e5f\u4e0d\u4f8b\u5916\uff0c\u94f6\u5b50\u5728\u8fd9\u91cc\uff0c\u4f60\u4eec\u966a\u4ed6\u559d\uff0c\u94b1\u4e0d\u591f\u5c31\u8131\u4ed6\u7684\u8863\u88f3\uff0c\u6211\u548c\u5c1a\u4e0d\u5949\u966a\u4e86\u3002\u201d \u3000\u3000\u638f\u51fa\u4e00\u5757\u4e00\u4e24\u91cd\u94f6\u5b50\u4e22\u5728\u684c\u5b50\u4e0a\uff0c\u8d77\u8eab\u5c31\u8d70\u3002 \u3000\u3000\u6c5f\u67ab\u6697\u6697\u5fd6\u9053\uff1a\u201c\u5927\u548c\u5c1a\u8981\u8d77\u6765\uff0c\u8fd8\u771f\u6709\u70b9\u53eb\u4eba\u62db\u67b6\u4e0d\u4f4f\uff0c\u7b97\u4e86\uff0c\u5df2\u51fa\u73b0\u72ee\u5b50\u3001\u8001\u864e\uff0c\u72af\u4e0d\u7740\u62db\u60f9\u8fd9\u4e24\u4e2a\u5c0f\u767d\u5154\uff0c\u65e9\u4e9b\u8d70\u5427\uff01\u201d\u5fc3\u4e2d\u5b9a\u4e86\u4e3b\u610f\uff0c\u7f13\u7f13\u7ad9\u8d77\u8eab\u5b50\u3002 \u3000\u3000\u5374\u4e0d\u6599\u4e8c\u8c46\u4e00\u628a\u6293\u4f4f\u4e86\u53f3\u624b\uff0c\u7b11\u9053\uff1a\u201c\u653e\u5fc3\u4e86\uff0c\u548c\u5c1a\u4ed8\u7684\u94f6\u5b50\uff0c\u8fd8\u53ef\u4ee5\u8ba9\u4f60\u559d\u4e24\u58f6\uff0c\u5750\u5750\u561b\uff01\u6211\u548c\u59b9\u4ece\u6765\u966a\u7740\u5ba2\u4eba\u559d\u9152\uff0c\u4eca\u5929\u53ef\u662f\u5934\u4e00\u56de\uff0c\u90a3\u548c\u5c1a\u8bf4\u4e86\u4f60\u534a\u57ce\u7684\u574f\u8bdd\uff0c\u5374\u77a7\u4e0d\u51fa\u4f60\u6709\u4e00\u6837\u597d\u5904\uff1f\u201d \u3000\u3000\u201c\u6709\u4ec0\u4e48\u597d\u5904\uff1f\u201d \u3000\u3000\u201c\u8ba9\u5973\u4eba\u4e00\u89c1\u52a8\u5fc3\u2026\u2026\u201d \u3000\u3000\u4e09\u8c46\u9001\u4e0a\u4e00\u676f\u9152\uff0c\u63a5\u9053\uff1a\u201c\u4e8c\u59d0\u8bf4\u7684\u4e0d\u9519\uff0c\u516c\u5b50\u8bf7\u996e\u6b64\u676f\uff0c\u6211\u5c31\u544a\u8bc9\u4f60\u4e00\u4e2a\u5929\u5927\u7684\u79d8\u5bc6\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u4f3c\u662f\u88ab\u5438\u5f15\u4e86\uff0c\u5fc3\u4e2d\u5fd6\u9053\uff1a\u201c\u5982\u6b64\u7c97\u4fd7\u7684\u624b\u6bb5\uff0c\u4e5f\u80fd\u4f7f\u4eba\u4e0a\u94a9\uff0c\u6211\u5012\u8981\u770b\u770b\u4f60\u4eec\u80fd\u73a9\u51fa\u4ec0\u4e48\u628a\u620f\uff1f\u201d \u3000\u3000\u5c82\u77e5\u79d8\u65b9\u533b\u5927\u75c5\uff0c\u9152\u5165\u53e3\u6c5f\u67ab\u5df2\u89c9\u51fa\u4e0d\u5bf9\uff0c\u4e0d\u7981\u5fc3\u5934\u5192\u706b\uff0c\u5982\u5728\u767e\u65e5\u4e4b\u524d\uff0c\u8fd9\u53e3\u9152\u5c31\u80fd\u8981\u4e86\u6c5f\u67ab\u7684\u547d\uff0c\u4f46\u73b0\u5728\u7684\u6c5f\u67ab\uff0c\u5df2\u975e\u6bd2\u9152\u80fd\u4f24\uff0c\u54ac\u54ac\u7259\uff0c\u786c\u628a\u4e00\u676f\u6bd2\u9152\u559d\u4e0b\u53bb\uff0c\u9053\uff1a\u201c\u8bf4\u5427\uff01\u4ec0\u4e48\u79d8\u5bc6\uff1f\u201d \u3000\u3000\u201c\u4f60\u53eb\u6c5f\u67ab\uff0c\u5bf9\u4e0d\u5bf9\uff1f\u201d\u4e09\u8c46\u53f9\u53e3\u6c14\uff0c\u8bf4\uff0c\u201c\u4e3a\u4ec0\u4e48\u4f1a\u50bb\u5230\u4e00\u53e3\u628a\u9152\u559d\u4e0b\u53bb\uff1f\u542c\u8bf4\u4f60\u8eab\u8d1f\u7edd\u4e16\u6b66\u529f\uff0c\u4e5f\u8be5\u673a\u667a\u8fc7\u4eba\u554a\uff1f\u201d \u3000\u3000\u201c\u4e0d\u559d\u8fd9\u676f\u6bd2\u9152\uff0c\u4f60\u4eec\u5c31\u80fd\u653e\u8fc7\u6c5f\u67d0\uff1f\u201d \u3000\u3000\u201c\u6211\u4eec\u77e5\u9053\u7559\u4e0d\u4f4f\u4f60\uff0c\u4f46\u53c8\u4e0d\u80fd\u4e0d\u7559\u4f60\uff0c\u56e0\u4e3a\uff0c\u4e0d\u7559\u4f60\u8235\u4e3b\u4f1a\u6740\u4e86\u6211\u4eec\uff0c\u4f60\u53ef\u4ee5\u53d1\u987f\u813e\u6c14\uff0c\u6253\u6211\u548c\u59d0\u59d0\u4e24\u4e2a\u8033\u5149\uff0c\u56de\u5934\u5c31\u8d70\u2026\u2026\u201d\u4e09\u8c46\u9053\uff1a\u201c\u53ef\u501f\uff0c\u4f60\u7adf\u7136\u559d\u4e0b\u8fd9\u676f\u6bd2\u9152\uff01\u201d \u3000\u3000\u201c\u559d\u4e86\u53c8\u4f1a\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u201c\u559d\u4e86\u4f1a\u7acb\u523b\u6655\u8fc7\u53bb\uff0c\u2026\u2026\u201d\u4e09\u8c46\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4e0d\u5bf9\u5440\uff01\u4f60\u65e9\u8be5\u6655\u8fc7\u53bb\u4e86\uff1f\u201d \u3000\u3000\u201c\u5982\u679c\u4e00\u676f\u6bd2\u9152\uff0c\u5c31\u80fd\u53eb\u6211\u675f\u624b\u5c31\u7f1a\uff0c\u6c5f\u67d0\u54ea\u91cc\u8fd8\u80fd\u548c\u4f60\u4eec\u4e3a\u654c\uff0c\u8bf4\uff01\u4f60\u4eec\u662f\u5929\u9a6c\u5802\u7684\u4eba\uff1f\u8fd8\u662f\u5929\u540e\u5bab\u7684\u4eba\uff1f\u201d \u3000\u3000\u201c\u90fd\u4e0d\u662f\uff0c\u6211\u4eec\u662f\u5929\u738b\u95e8\u7684\u4eba\uff0c\u9752\u51e4\u662f\u6211\u4eec\u5e08\u59d0\uff0c\u6211\u4eec\u4e5f\u77e5\u9053\u5979\u73b0\u5728\u6c74\u4eac\uff0c\u2026\u2026\u201d\u4e09\u8c46\u82e6\u7b11\u4e00\u4e0b\u53c8\u9053\uff1a\u201c\u6740\u4e86\u6211\u4eec\u5427\uff01\u6211\u4eec\u81ea\u77e5\u4e0d\u662f\u4f60\u7684\u654c\u624b\uff0c\u4e5f\u4e0d\u613f\u548c\u4f60\u52a8\u624b\u3002\u201d \u3000\u3000\u201c\u4f60\u4eec\u600e\u4e48\u4f1a\u4e00\u773c\u5c31\u770b\u51fa\u6211\u662f\u6c5f\u67ab\uff1f\u8bf4\u4e86\uff0c\u6211\u4e5f\u8bb8\u4e0d\u4f1a\u6740\u4f60\u4eec\u3002\u201d \u3000\u3000\u201c\u5929\u540e\u5bab\u4f20\u51fa\u4e86\u4f60\u7684\u753b\u50cf\uff0c\u6c5f\u6e56\u4e0a\u4f60\u5df2\u662f\u5bf8\u6b65\u96be\u884c\u3002\u201d\u4e09\u8c46\u8bf4\u9053\uff1a\u201c\u6740\u4e86\u6211\u4eec\u5feb\u4e9b\u8d70\uff01 \u3000\u3000\u8235\u4e3b\u5df2\u4eb2\u81ea\u98de\u8eab\u4f20\u62a5\uff0c\u518d\u7b49\u4e00\u4f1a\uff0c\u5c31\u4f1a\u6709\u9ad8\u624b\u8d76\u5230\uff01\u201d \u3000\u3000\u201c\u53ef\u60dc\u7684\u662f\uff0c\u8fd9\u6e38\u620f\u65e0\u6cd5\u73a9\u4e0b\u53bb\uff0c\u6211\u6709\u4e8b\uff0c\u4e0d\u80fd\u5949\u966a\uff0c\u770b\u5728\u9752\u51e4\u7684\u9762\u4e0a\uff0c\u6211\u4e0d\u6740\u4f60\u4eec\uff0c\u6211\u70b9\u4f60\u4eec\u7a74\u9053\uff0c\u7b49\u4f60\u4eec\u8235\u4e3b\u6765\u6551\u4f60\u4eec\u5427\uff01\u201d \u3000\u3000\u4e8c\u5973\u70b9\u5934\u5fae\u7b11\u3002 \u3000\u3000\u201c\u8235\u4e3b\u5c31\u662f\u90a3\u4f4d\u638c\u67dc\u5148\u751f\u3002\u201d \u3000\u3000\u201c\u4e5f\u662f\u6211\u4eec\u540d\u4e49\u4e0a\u7684\u7236\u4eb2\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u51b7\u51b7\u63a5\u9053\uff1a\u201c\u4e25\u8083\u70b9\uff0c\u88c5\u51fa\u5403\u60ca\u7684\u6837\u5b50\u3002\u201d \u3000\u3000\u4e8c\u5973\u4e00\u6014\u3002 \u3000\u3000\u6c5f\u67ab\u5df2\u7ecf\u53ca\u65f6\u51fa\u624b\uff0c\u70b9\u4e86\u4e8c\u5973\u5927\u9053\uff0c\u5927\u6447\u5927\u6446\u7684\u8d70\u51fa\u4e86\u8c46\u513f\u5728\uff0c\u56de\u5230\u4e86\u4eac\u534e\u5ba2\u6808\u3002 \u3000\u3000\u6c5f\u67ab\u8bf1\u654c\u5f88\u6210\u529f\uff0c\u529b\u6218\u4e86\u4e94\u5341\u62db\u540e\uff0c\u56de\u8eab\u5c31\u8d70\uff01 \u3000\u3000\u653e\u6b65\u591c\u5954\uff0c\u5feb\u5982\u95ea\u7535\uff0c\u8fd9\u5c31\u5982\u6c99\u4e2d\u6dd8\u91d1\u4e00\u822c\uff0c\u5230\u4e86\u5f20\u56db\u59d1\u9884\u8bbe\u57cb\u4ed7\u7684\u5730\u70b9\uff0c\u53ea\u6709\u90a3\u4e24\u4e2a\u7edd\u9876\u9ad8\u624b\u7d27\u8ffd\u8d76\u5230\u3002 \u3000\u3000\u6c5f\u67ab\u505c\u4e0b\u811a\u6b65\uff0c\u56de\u5934\u7b11\u9053\uff1a\u201c\u4e24\u4f4d\uff0c\u522b\u7684\u4eba\u6ca1\u6709\u8ddf\u4e0b\u6765\u3002\u201d \u3000\u3000\u4e24\u4e2a\u4eba\u4e00\u7740\u9ed1\u888d\uff0c\u4e00\u7740\u9752\u886b\uff0c\u4e00\u773c\u770b\u4e0a\u53bb\uff0c\u50cf\u662f\u56db\u5341\u591a\u5c81\u7684\u4eba\uff0c\u8001\u53eb\u5316\u5b50\u63cf\u8ff0\u7684\u4e00\u70b9\u4e0d\u9519\u3002 \u3000\u3000\u201c\u4ed6\u4eec\u8ddf\u4e0a\u6765\uff0c\u4e5f\u5e2e\u4e0d\u4e86\u5fd9\u2026\u2026\u201d\u9ed1\u888d\u4eba\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u672a\u51fa\u5168\u529b\uff0c\u8bc8\u8d25\u8bf1\u654c\uff0c\u5c31\u662f\u8981\u6211\u4eec\u8ffd\u6765\u6b64\u5730\u4e86\u3002\u201d \u3000\u3000\u6c5f\u67ab\u6697\u6697\u5403\u4e86\u4e00\u60ca\uff0c\u5fd6\u9053\uff1a\u201c\u4ed6\u4eec\u65e9\u77e5\u9053\u4e86\uff0c\u679c\u7136\u662f\u6280\u827a\u7cbe\u7edd\uff0c\u597d\u6076\u5e76\u4ff1\u7684\u9ad8\u624b\u3002\u201d \u3000\u3000\u201c\u6211\u76f8\u4fe1\u8fd9\u5730\u65b9\u65e9\u6709\u57cb\u4f0f\uff1f\u4f46\u6211\u60f3\u4e0d\u51fa\u8c01\u80fd\u5e2e\u4e0a\u4f60\u7684\u5fd9\uff1f \u3000\u3000\u2026\u2026\u201d\u9752\u8863\u4eba\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u5e26\u7740\u4e24\u4e2a\u5c0f\u7f8e\u4eba\uff0c\u6f5c\u9690\u7ec3\u6b66\uff0c\u4f46\u53ea\u6709\u4e00\u767e\u591a\u5929\uff0c\u80fd\u6709\u591a\u5927\u957f\u8fdb\uff1f \u3000\u3000\u7ec8\u5357\u5c71\u7684\u5200\u4e0b\u6e38\u9b42\uff0c\u53ea\u6709\u6b27\u9633\u662d\u592b\u5987\u548c\u4e94\u4e2a\u5f1f\u5b50\u7684\u5251\u9635\uff0c\u53ef\u582a\u4e00\u6218\uff0c\u6613\u82b1\u624b\u5f20\u56db\u59d1\u53ea\u6709\u90a3\u4e00\u7f50\u8840\u8747\uff0c\u4e00\u6b21\u7528\u5b8c\uff0c\u65e9\u5df2\u9ed4\u9a74\u6280\u7a77\uff0c\u518d\u96be\u800d\u51fa\u65b0\u82b1\u6837\u4e86\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u662f\u6709\u4e9b\u4f4e\u4f30\u4f60\u4eec\u4e86\uff0c\u4f60\u4eec\u4e0d\u662f\u5929\u9a6c\u5802\u7684\u4eba\u5427\uff1f\u201d \u3000\u3000\u201c\u6c5f\u67ab\uff0c\u4f60\u5982\u4e00\u76f4\u6f5c\u9690\u4e0d\u51fa\uff0c\u4e5f\u8bb8\u8fd8\u80fd\u4fdd\u5b58\u4e0b\u6b66\u6797\u4e09\u5723\u90a3\u4e9b\u6b66\u529f\uff0c\u6211\u4eec\u7684\u758f\u5ffd\uff0c\u7559\u7ed9\u4f60\u4e00\u7ebf\u751f\u673a\u2026\u2026\u201d\u9ed1\u888d\u4eba\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u4f60\u7adf\u7834\u58f3\u800c\u51fa\uff0c\u8fd9\u662f\u4f60\u7684\u5931\u7b56\uff0c\u4f60\u786e\u5b9e\u662f\u4e00\u4f4d\u6b66\u6797\u5947\u8469\uff0c\u65e9\u5341\u4e09\u5341\u5e74\uff0c\u5fc5\u80fd\u4e00\u4e3e\u6210\u540d\uff0c\u4f46\u4e5f\u4f1a\u65e9\u4e9b\u6b7b\u4ea1\u3002 \u3000\u3000\u751f\u9022\u6b64\u8fb0\uff0c\u90a3\u5c31\u662f\u66f4\u5927\u7684\u4e0d\u5e78\u4e86\uff0c\u65e0\u6cd5\u6210\u540d\u6c5f\u6e56\uff0c\u5c31\u8981\u8fdb\u5165\u9b3c\u57df\u3002\u201d \u3000\u3000\u201c\u4e24\u4f4d\u5462\uff1f\u201d\u6c5f\u67ab\u5fc3\u4e2d\u5fd6\u601d\uff1a\u201c\u4f55\u4e0d\u8bf1\u4ed6\u4eec\u591a\u8bf4\u51fa\u4e00\u70b9\u79d8\u5bc6\u3002\u201d\u9ad8\u58f0\u63a5\u9053\uff1a\u201c\u82e6\u7ec3\u4e86\u4e00\u8eab\u9634\u6bd2\u6b66\u529f\uff0c\u53c8\u5f97\u5230\u4e86\u4ec0\u4e48\uff1f\u8fd8\u4e0d\u662f\u6c5f\u6e56\u4e0a\u7684\u65e0\u540d\u5c0f\u5352\u201d \u3000\u3000\u201c\u4f60\u662f\u9f20\u76ee\u5bf8\u5149\uff0c\u533a\u533a\u6c5f\u6e56\uff0c\u6709\u4ec0\u4e48\u597d\u4e89\u7684\u2026\u2026\u201d\u9ed1\u888d\u4eba\u8bf4\u5230\u6b64\u5904\uff0c\u7a81\u7136\u4f4f\u53e3\u3002 \u3000\u3000\u201c\u6211\u660e\u767d\u4e86\uff0c\u8bf8\u4f4d\u662f\u5728\u9020\u53cd\uff0c\u805a\u96c6\u6b66\u6797\u4e2d\u4eba\uff0c\u593a\u53d6\u5929\u4e0b\uff0c\u8fd8\u672a\u89c1\u524d\u4f8b\uff0c\u9700\u77e5\u5927\u519b\u63a9\u6740\uff0c\u6709\u5982\u5c71\u5d29\u5824\u88c2\uff0c\u4e09\u4e94\u5341\u4e2a\u9ad8\u624b\uff0c\u80fd\u6321\u5f97\u591a\u5c11\u519b\u9a6c\uff1f\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u6218\u7b56\u5175\u7565\uff0c\u6b66\u6797\u4e2d\u6709\u51e0\u4eba\u8bfb\u8fc7\uff0c\u9f99\u6f5c\u4e8e\u6e0a\uff0c\u864e\u85cf\u6df1\u5c71\uff0c\u6c5f\u6e56\u4eba\u5c31\u662f\u6c5f\u6e56\u4eba\uff0c\u4e89\u9738\u76df\u4e3b\uff0c\u5feb\u610f\u6069\u4ec7\uff0c\u4e5f\u8bb8\u53ef\u4ee5\uff0c\u4e89\u738b\u4f4d\u3001\u7a83\u56fd\u571f\uff0c\u90a3\u5c31\u662f\u975e\u975e\u4e4b\u60f3\u4e86\u3002 \u3000\u3000\u4f60\u9a82\u6211\u9f20\u76ee\u5bf8\u5149\uff0c\u4f60\u4eec\u624d\u662f\u5750\u5e76\u89c2\u5929\uff0c\u75f4\u4eba\u68a6\u60f3\u2026\u2026\u201d\u58f0\u97f3\u8f6c\u4e3a\u4e25\u5389\u63a5\u9053\uff1a\u201c\u65e2\u7136\u5fd7\u4e0b\u5728\u6c5f\u6e56\u4e89\u9738\uff0c\u66f4\u4e0d\u8be5\u6740\u622e\u6c5f\u6e56\u4e2d\u4eba\uff0c\u4e00\u6b65\u9519\uff0c\u5168\u76d8\u8f93\uff0c\u4f60\u4eec\u9519\u7684\u79bb\u8c31\u4e86\uff01\u201d \u3000\u3000\u201c\u5230\u76ee\u524d\u4e3a\u6b62\uff0c\u6211\u4eec\u8fdb\u884c\u7684\u90fd\u5f88\u987a\u5229\u2026\u2026\u201d\u9ed1\u888d\u4eba\u9053\uff1a\u201c\u4f60\u662f\u552f\u4e00\u7684\u6f0f\u7f51\u4e4b\u9c7c\uff0c\u6740\u4e86\u4f60\uff0c\u6211\u4eec\u5c31\u53ef\u4ee5\u529e\u6b63\u4e8b\u4e86\u3002\u201d \u3000\u3000\u679c\u7136\u662f\u76ee\u65e0\u4f59\u5b50\uff0c\u53ea\u628a\u6c5f\u67ab\u4e00\u4e2a\u4eba\u770b\u4f5c\u654c\u624b\uff01 \u3000\u3000\u201c\u6211\u80fd\u6740\u4f60\u4eec\uff0c\u6613\u82b1\u624b\u5f20\u56db\u59d1\u7684\u6280\u827a\uff0c\u66f4\u662f\u795e\u51fa\u9b3c\u6ca1\uff0c\u4e0d\u662f\u4f60\u4eec\u57f9\u517b\u51fa\u7684\u9e70\u3001\u72ac\u7075\u5de7\uff0c\u65e9\u5c31\u6478\u4e0a\u4f60\u4eec\u7684\u8001\u5de2\uff0c\u9664\u5c3d\u5143\u51f6\u4e86\uff0c\u4eca\u591c\uff0c\u6211\u8981\u653e\u624b\u4e00\u640f\uff0c\u4e94\u5341\u62db\u5185\u6211\u6740\u4e0d\u4e86\u4f60\u4eec\uff0c\u2026\u2026\u201d \u3000\u3000\u201c\u600e\u4e48\u6837\u2026\u2026\u201d\u9752\u886b\u4eba\u9053\uff1a\u201c\u5f03\u5200\u5f52\u987a\uff0c\u65e5\u540e\uff0c\u4e0d\u96be\u4f4d\u5217\u5c01\u4faf\u3002\u201d \u3000\u3000\u201c\u5e08\u957f\u9057\u613f\uff0c\u5c82\u53ef\u4e0d\u987e\uff0c\u4e94\u5341\u62db\u6740\u4e0d\u4e86\u4f60\u4eec\uff0c\u4f60\u4eec\u5c31\u4f1a\u89c1\u8bc6\u5230\u6613\u82b1\u624b\u5f20\u59d1\u5a18\u7684\u5389\u5bb3\u3002\u201d \u3000\u3000\u201c\u6c5f\u67ab\uff0c\u4f60\u771f\u662f\u51a5\u987d\u4e0d\u7075\u554a\uff01\u2026\u2026\u201d \u3000\u3000\u9752\u8863\u4eba\u8bdd\u72b9\u672a\u5b8c\uff0c\u6c5f\u67ab\u638c\u98ce\u5df2\u81f3\uff0c\u53f3\u624b\u7684\u7fe0\u7389\u5200\u4e5f\u653b\u5411\u9ed1\u8863\u4eba\u3002 \u3000\u3000\u4ed6\u5b58\u5fc3\u6d4b\u8bd5\u6210\u5c31\uff0c\u4e00\u51fa\u624b\u5c31\u5f15\u8d77\u4e24\u4eba\u5408\u51fb\u3002 \u3000\u3000\u7a81\u7136\uff0c\u706b\u5149\u4e00\u95ea\uff0c\u4eae\u8d77\u4e24\u652f\u706b\u628a\uff0c\u5f20\u56db\u59d1\u3001\u4e03\u5b9d\u5927\u5e08\u3001\u6b27\u9633\u662d\u3001\u8001\u53eb\u5316\u3001\u80e1\u840d\u3001\u6bb5\u4e5d\u3001\u9093\u98de\u3001\u5201\u9e4f\u3001\u94c1\u62c2\u3001\u9752\u9e64\u7b49\uff0c\u5168\u90fd\u73b0\u8eab\u51fa\u6765\uff0c\u80e1\u5143\u548c\u90ed\u5929\u540c\uff0c\u9ad8\u4e3e\u4e24\u652f\u706b\u628a\uff0c\u4f46\u541f\u96ea\u3001\u541f\u971c\u3001\u9752\u51e4\u3001\u6885\u82b1\uff0c\u4ecd\u662f\u9690\u8eab\u672a\u73b0\u3002 \u3000\u3000\u591c\u6697\u4e2d\uff0c\u7a81\u73b0\u706b\u628a\uff0c\u5149\u4eae\u66f4\u89c1\u660e\u6f88\u3002 \u3000\u3000\u6c5f\u67ab\u72ec\u6218\u4e24\u5927\u9ad8\u624b\u7684\u51cc\u5389\u640f\u6740\uff0c\u4e5f\u6e05\u6670\u53ef\u89c1\u3002 \u3000\u3000\u7fe0\u7389\u5200\u5e7b\u8d77\u4e86\u4e00\u7247\u9752\u8292\uff0c\u6eda\u6eda\u6d41\u52a8\uff0c\u638c\u98ce\u5982\u5578\uff0c\u52b2\u6c14\u56db\u6ea2\uff0c\u7ad9\u5728\u4e09\u4e08\u4e4b\u5916\u7684\u89c2\u6218\u7fa4\u8c6a\uff0c\u90fd\u611f\u53d7\u5230\u638c\u529b\u903c\u4eba\u3002 \u3000 \u7b2c\u4e8c\u5341\u4e8c\u56de\u3000\u5165\u864e\u7a74\u53cc\u63a2\u5929\u9a6c\u5802 \u3000 \u3000\u3000\u5ffd\u7136\u95f4\uff0c\u4f20\u64ad\u51fa\u4e00\u9635\u9634\u5bd2\u4e4b\u6c14\uff0c\u5982\u5bd2\u51ac\u51b7\u98ce\uff0c\u4fb5\u4f53\u900f\u808c\uff0c\u4e24\u652f\u706b\u628a\uff0c\u66f4\u662f\u706b\u7130\u6447\u98a4\u3002 \u3000\u3000\u5f20\u56db\u59d1\u9ad8\u58f0\u8bf4\u9053\uff1a\u201c\u8bf8\u4f4d\u8bf7\u9000\u540e\u4e00\u4e9b\uff0c\u8fd0\u6c14\u6297\u62d2\uff0c\u522b\u8ba9\u9634\u5bd2\u4fb5\u4f53\u3002\u201d \u3000\u3000\u4f46\u95fb\u6c5f\u67ab\u7eb5\u58f0\u957f\u5578\uff0c\u9053\uff1a\u201c\u4e24\u4f4d\u4f0e\u4fe9\uff0c\u5df2\u5c3d\u4e8e\u6b64\uff0c\u6211\u9886\u6559\u8fc7\u4e86\uff0c\u8bf8\u4f4d\uff0c\u8bf7\u770b\u6c5f\u67d0\u4eba\u4e3a\u7ec8\u5357\u5c71\u4e2d\u4f24\u4ea1\u540c\u9053\u590d\u4ec7\uff0c\u8bdb\u6b64\u53cc\u51f6\u3002\u201d \u3000\u3000\u8bf4\u6740\u5c31\u6740\uff0c\u8eab\u4f53\u7a81\u7136\u98d8\u98de\u800c\u8d77\uff0c\u5728\u9ed1\u888d\u4eba\u548c\u9752\u886b\u4eba\u7684\u638c\u529b\u4ea4\u9519\u4e2d\uff0c\u76f4\u6295\u800c\u5165\u3002 \u3000\u3000\u6ca1\u6709\u4eba\u7740\u6e05\u695a\uff0c\u6c5f\u67ab\u5982\u4f55\u6740\u4e86\u4e24\u4eba\uff0c\u4f46\u7a81\u7136\u95f4\uff0c\u4e00\u5207\u5f52\u4e8e\u6c89\u5bc2\uff0c\u4e24\u5177\u5c38\u4f53\uff0c\u5012\u5367\u5728\u8352\u91ce\u4e2d\u3002 \u3000\u3000\u6c5f\u673a\u8eab\u4fa7\u4e00\u4e1b\u8349\uff0c\u7a81\u7136\u98de\u8d77\uff0c\u4e24\u4e2a\u7f8e\u5973\uff0c\u7531\u8349\u4e2d\u5e7b\u5316\u800c\u51fa\u3002 \u3000\u3000\u662f\u541f\u96ea\u3001\u541f\u971c\u3002 \u3000\u3000\u5979\u4eec\u628a\u9752\u8349\u8fde\u5728\u8863\u670d\u4e0a\uff0c\u5de7\u5999\u7684\u5750\u5367\u59ff\u52bf\uff0c\u50cf\u6781\u4e86\u4e00\u4e1b\u9752\u8349\uff0c\u8fde\u6c5f\u67ab\u4e5f\u672a\u53d1\u89c9\u3002 \u3000\u3000\u201c\u6c5f\u5927\u54e5\uff0c\u4f60\u771f\u7684\u7a81\u7834\u4e86\u4f53\u80fd\u6781\u9650\uff0c\u2026\u2026\u201d\u541f\u96ea\u9ad8\u5174\u7684\u8df3\u8d77\u6765\u62b1\u4f4f\u4e86\u6c5f\u67ab\uff0c\u63a5\u9053\uff1a\u201c\u6211\u597d\u62c5\u5fc3\uff0c\u4f1a\u51fa\u5dee\u9519\uff0c\u73b0\u5728\u624d\u653e\u4e0b\u4e86\u5fc3\u4e2d\u5343\u94a7\u91cd\u62c5\u3002\u201d \u3000\u3000\u8f83\u8fdc\u5904\uff0c\u53e6\u4e00\u4e1b\u8349\uff0c\u4e5f\u5206\u5316\u5f00\u6765\uff0c\u662f\u9752\u51e4\u3001\u6885\u82b1\uff0c\u5feb\u6b65\u7684\u5954\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u4e8b\u5b9e\uff0c\u6240\u6709\u7684\u5728\u573a\u7fa4\u8c6a\uff0c\u5168\u90fd\u56f4\u4e86\u4e0a\u6765\u3002 \u3000\u3000\u541f\u96ea\u4f4e\u58f0\u5728\u6c5f\u67ab\u7684\u8033\u8fb9\u8bf4\u9053\uff1a\u201c\u770b\u6837\u5b50\uff01\u5148\u516c\u540e\u79c1\u4e86\u3002\u201d \u3000\u3000\u4e00\u677e\u53cc\u624b\uff0c\u843d\u7740\u5b9e\u5730\u3002 \u3000\u3000\u201c\u4e24\u4e2a\u4e2b\u5934\uff0c\u4f60\u4eec\u5e2e\u5fd9\u6ca1\u6709\uff1f\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u8bf4\u5b9e\u8bdd\uff0c\u4e0d\u8bc8\u9a97\u6211\u3002\u201d \u3000\u3000\u201c\u6ca1\u6709\u554a\uff01\u5f20\u59e8\uff0c\u4e0d\u4fe1\u4f60\u95ee\u6c5f\u5927\u54e5\u561b\uff01\u201d\u541f\u971c\u5fae\u7b11\u7740\u8bf4\u3002 \u3000\u3000\u201c\u5f20\u59e8\uff01\u201d\u541f\u96ea\u9053\uff1a\u201c\u4e0d\u5230\u4e94\u5341\u62db\u554a\uff01\u7b2c\u56db\u5341\u4e03\u62db\u4e0a\uff0c\u6c5f\u5927\u54e5\u6740\u4e86\u4ed6\u4eec\uff0c\u4e8b\u5b9e\u4e0a\uff0c\u53ef\u4ee5\u66f4\u65e9\u4e00\u4e9b\u6740\u4ed6\u4eec\uff0c\u591a\u62d6\u4e86\u4e00\u51e0\u62db\uff0c\u53ea\u662f\u60f3\u591a\u7ecf\u5386\u4e00\u4e0b\u7384\u9634\u6c14\u529f\u7684\u5a01\u529b\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u9053\uff1a\u201c\u6c5f\u67ab\uff0c\u4f60\u5982\u4e00\u5f00\u59cb\u5c31\u4e0b\u8fa3\u624b\uff0c\u80fd\u4e0d\u80fd\u4e00\u4e3e\u6740\u4e86\u4ed6\u4eec\uff1f\u201d \u3000\u3000\u201c\u73b0\u5728\uff0c\u8fd8\u4e0d\u80fd\u3002\u2026\u2026\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\uff0c\u6211\u4e0d\u80fd\u4e00\u4e0b\u5b50\u5c31\u7528\u51fa\u90a3\u79cd\u8eab\u6cd5\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u89e3\u91ca\u9053\uff1a\u201c\u81f3\u5c11\u8981\u5341\u62db\u4ee5\u4e0a\uff0c\u624d\u80fd\u628a\u6c14\u8840\u3001\u5173\u8282\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u61c2\u4e86\uff0c\u2026\u2026\u201d\u5f20\u56db\u59d1\u6325\u6325\u624b\uff0c\u9ad8\u58f0\u8bf4\u9053\uff1a \u3000\u3000\u201c\u8bf8\u4f4d\u90fd\u662f\u4eb2\u773c\u770b\u5230\u4e86\uff0c\u6211\u4eec\u5df2\u6709\u80fd\u529b\u53cd\u51fb\uff0c\u53ea\u4e0d\u8fc7\uff0c\u6211\u4eec\u7684\u4eba\u624b\u592a\u5c11\uff0c\u6240\u4ee5\uff0c\u6211\u4eec\u8fd8\u5f97\u62d6\u4e9b\u65f6\u95f4\uff0c\u624d\u80fd\u5c55\u5f00\u53cd\u51fb\u3002\u201d \u3000\u3000\u201c\u5f3a\u5f13\u3001\u957f\u7bad\uff0c\u90fd\u5df2\u5907\u59a5\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u9053\uff1a\u201c\u5f20\u59d1\u5a18\u8981\u4e0d\u8981\u70b9\u6536\u4e00\u4e0b\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4e48\u5feb\u5440\uff1f\u201d \u3000\u3000\u201c\u6709\u94b1\u597d\u529e\u4e8b\u554a\uff01\u2026\u2026\u201d\u5927\u548c\u5c1a\u7b11\u9053\uff1a\u201c\u5341\u51e0\u5bb6\u94c1\u94fa\uff0c\u5168\u505c\u4e0b\u522b\u4eba\u7684\u5de5\u4f5c\uff0c\u5168\u529b\u5e2e\u6211\u4eec\u8d76\u5de5\u3002\u201d \u3000\u3000\u201c\u597d\u6781\u4e86\uff0c\u8fd9\u4e9b\u5730\u65b9\uff0c\u56db\u59d1\u4e0d\u53ca\u5927\u5e08\u591a\u77e3\uff01\u201d \u3000\u3000\u201c\u4eca\u591c\u4e4b\u6218\uff0c\u5e03\u5c40\u5947\u5de7\uff0c\u9677\u5f3a\u654c\u4e8e\u5fc5\u6b7b\u4e4b\u5730\u2026\u2026\u201d\u5927\u548c\u5c1a\u9053\uff1a\u201c\u5b9e\u5728\u9ad8\u660e\u6781\u4e86\u3002\u201d \u3000\u3000\u201c\u4e0d\u80fd\u63a0\u7f8e\uff0c\u5168\u662f\u96ea\u4e2b\u5934\u7684\u5e03\u5c40\u5b89\u6392\uff0c\u6211\u4e5f\u6709\u70b9\u4f69\u670d\u5462\uff01\u201d \u3000\u3000\u201c\u4ec0\u4e48\uff1f\u56db\u59d1\uff0c\u4f60\u4e0d\u662f\u5f00\u73a9\u7b11\u5427\uff01\u8fd9\u662f\u5927\u4e2b\u5934\u7684\u5e03\u5c40\uff1f\u201d\u6b27\u9633\u662d\u9053\uff1a\u201c\u4f60\u8ba9\u5979\u5360\u5c3d\u5149\u91c7\uff0c\u90a3\u53ef\u662f\u5bb3\u4e86\u5979\u3002\u201d \u3000\u3000\u201c\u4e8e\u771f\u4e07\u786e\uff0c\u6307\u6325\u5168\u5c40\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u5979\u4e0d\u6562\u6307\u6325\u8001\u7239\uff0c\u53ea\u597d\u7531\u6211\u8f6c\u544a\uff0c\u7531\u5934\u5230\u5c3e\uff0c\u6211\u6ca1\u6709\u53c2\u52a0\u8fc7\u4efb\u4f55\u610f\u89c1\uff0c\u53ea\u7ed9\u5979\u51e0\u53e5\u9f13\u52b1\u7684\u8bdd\uff0c\u96be\u9053\u4f60\u6ca1\u6709\u770b\u5230\uff0c\u5979\u90a3\u4efd\u6b22\u6b23\u5feb\u4e50\u7684\u6837\u5b50\u3002\u201d \u3000\u3000\u6b27\u9633\u662d\u77aa\u7740\u541f\u96ea\u770b\uff0c\u5c31\u50cf\u770b\u4e00\u4e2a\u964c\u751f\u4eba\u6837\u3002 \u3000\u3000\u201c\u662f\u771f\u7684\uff0c\u6b27\u9633\u5144\uff0c\u8001\u53eb\u5316\u5c31\u662f\u542c\u5927\u5c0f\u59d0\u547d\u4ee4\u884c\u4e8b\u3002\u201d \u3000\u3000\u201c\u5e72\u7239\uff0c\u4e0d\u8981\u6000\u7591\u541f\u96ea\u59b9\u5b50\u7684\u80fd\u529b\uff0c\u6211\u4eec\u90fd\u662f\u542c\u4ece\u5979\u7684\u5b89\u6392\u5e03\u7f6e\u3002\u201d\u9752\u51e4\u3001\u6885\u82b1\u5f02\u53e3\u540c\u58f0\u7684\u8bf4\u3002 \u3000\u3000\u201c\u771f\u662f\u5bb6\u6709\u4e00\u5b9d\u4e0d\u81ea\u77e5\uff0c\u4f60\u884c\uff0c\u5927\u4e2b\u5934\uff0c\u4ee5\u540e\uff0c\u6709\u4e8b\u9063\u6d3e\u7239\u7239\uff0c\u6211\u4e5f\u4e00\u6837\u542c\u547d\u884c\u4e8b\u3002\u201d \u3000\u3000\u201c\u6211\u53ef\u4e0d\u6562\u554a\uff01\u2026\u2026\u201d\u541f\u96ea\u4e00\u4f38\u820c\u5934\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u6b21\uff0c\u5b89\u6392\u6c5f\u5927\u54e5\uff0c\u8bf1\u654c\u4f5c\u9975\uff0c\u5dee\u4e00\u70b9\u88ab\u541f\u971c\u9a82\u6b7b\uff01\u201d \u3000\u3000\u201c\u59d0\uff0c\u6211\u7ed9\u4f60\u9053\u8fc7\u6b49\u4e86\u561b\uff01\u8fd8\u8bb0\u5728\u5fc3\u91cc\u5440\uff01\u201d \u3000\u3000\u201c\u600e\u4e48\u4f1a\u5462\uff1f\u201d\u76ee\u5149\u4e00\u63a0\u9752\u51e4\u3001\u6885\u82b1\uff0c\u63a5\u9053\uff1a\u201c\u4e00\u65e6\u51fa\u4e86\u5dee\u9519\uff0c\u4e24\u4f4d\u5e72\u59d0\u59d0\uff0c\u4e5f\u4e0d\u4f1a\u653e\u8fc7\u6211\u5440\uff01\u201d \u3000\u3000\u9752\u51e4\u3001\u6885\u82b1\u5fae\u5fae\u4e00\u7b11\uff0c\u4e5f\u4e0d\u8fa9\u9a73\u3002 \u3000\u3000\u201c\u5982\u679c\u8fd9\u7b56\u5931\u7b97\uff0c\u8ba9\u8001\u7239\u53d7\u5230\u4f24\u5bb3\uff0c\u2026\u2026\u201d\u541f\u96ea\u9053\uff1a\u201c\u5a18\u975e\u8981\u628a\u6211\u541e\u56de\u53bb\u4e0d\u53ef\uff01\u201d \u3000\u3000\u6b27\u9633\u662d\u6ca1\u6709\u751f\u6c14\uff0c\u6447\u6447\u5934\u7b11\uff1a\u201c\u4f60\u80e1\u8bf4\u516b\u9053\u4e9b\u4ec0\u4e48\u5440\uff1f\u201d \u3000\u3000\u5fc3\u4e2d\u5374\u662f\u5341\u5206\u6b23\u6170\uff0c\u6bcd\u5973\u8fde\u5fc3\uff0c\u541f\u96ea\u7684\u8bdd\uff0c\u8868\u8fbe\u51fa\u7f57\u5170\u7684\u5fc3\u4e2d\u6df1\u60c5\u3002 \u3000\u3000\u56db\u5468\u7fa4\u8c6a\uff0c\u4e5f\u88ab\u8fd9\u4e00\u756a\u513f\u5973\u60c5\u6000\uff0c\u95f9\u5f97\u6b22\u989c\u5927\u5f00\uff0c\u6682\u65f6\u5fd8\u8bb0\u4e86\u9762\u4e34\u7684\u6b65\u6b65\u5371\u673a\u3002 \u3000\u3000\u65e2\u65e0\u6cd5\u907f\u5f00\u98de\u9e70\u3001\u7075\u72ac\u7684\u8ffd\u8e2a\uff0c\u5f20\u56db\u59d1\u5b89\u6392\u4e86\u730e\u6740\u8ba1\u5212\uff0c\u81ea\u5df1\u5e26\u9886\u4e86\u5927\u548c\u5c1a\u3001\u541f\u96ea\u3001\u541f\u971c\u3001\u9752\u51e4\u3001\u6885\u82b1\u4e3a\u4e00\u7ec4\u3002 \u3000\u3000\u6b27\u9633\u662d\u3001\u7f57\u5170\u5e26\u7740\u4e94\u4e2a\u5f1f\u5b50\u4e3a\u4e00\u7ec4\u3002 \u3000\u3000\u5176\u4f59\u7684\u4eba\uff0c\u7531\u6c5f\u67ab\u7edf\u7387\uff0c\u5404\u7ec4\u4e2d\u5747\u6709\u5341\u77f3\u5f3a\u5f13\u4e00\u5f20\u3002 \u3000\u3000\u4e09\u7ec4\u4eba\u624b\uff0c\u4e92\u76f8\u63a9\u62a4\u3001\u57cb\u4f0f\uff0c\u8bf1\u6740\u98de\u9e70\u3001\u7075\u72ac\u3002 \u3000\u3000\u539f\u6765\uff0c\u9e70\u3001\u72ac\u82e5\u6709\u7075\u6027\uff0c\u635f\u4f24\u4e86\u51e0\u652f\u4e4b\u540e\uff0c\u5c31\u98de\u7684\u66f4\u9ad8\uff0c\u79bb\u7684\u66f4\u8fdc\u3002 \u3000\u3000\u4e09\u5929\u4e4b\u540e\uff0c\u518d\u65e0\u6536\u83b7\uff0c\u4f46\u5374\u672a\u8131\u79bb\u5b83\u4eec\u7684\u76d1\u89c6\u3002 \u3000\u3000\u5f20\u56db\u59d1\u4fdf\u4e09\u59d0\u4eba\u624b\u4f1a\u9f50\u4e4b\u540e\uff0c\u9053\uff1a\u201c\u4e0d\u884c\uff0c\u6211\u4eec\u5c04\u6740\u4e86\u516d\u6761\u72d7\u3001\u56db\u53ea\u98de\u9e70\uff0c\u5c31\u96be\u518d\u5f97\u624b\uff0c\u4f46\u5374\u65e0\u6cd5\u6446\u8131\u4ed6\u4eec\u7684\u8ffd\u8e2a\u76d1\u89c6\uff01\u201d \u3000\u3000\u201c\u5f20\u59e8\uff0c\u730e\u6740\u7075\u72ac\u8fd8\u4e0d\u592a\u96be\uff0c\u2026\u2026\u201d\u541f\u971c\u9053\uff1a\u201c\u6211\u548c\u4e09\u4f4d\u599e\u59d0\uff0c\u6f5c\u8e2a\u9690\u8eab\uff0c\u56f4\u6740\u7075\u72ac\uff0c\u6211\u76f8\u4fe1\u4e0d\u96be\u65ad\u7edd\u5b83\u4eec\u7684\u8ffd\u8e2a\uff0c\u4f46\u9e70\u98de\u592a\u9ad8\uff0c\u5c31\u96be\u60f3\u51fa\u529e\u6cd5\u4e86\uff1f\u201d \u3000\u3000\u201c\u541f\u96ea\uff0c\u4f60\u7684\u770b\u6cd5\u5462\uff1f\u201d\u5f20\u56db\u59d1\u95ee\u3002 \u3000\u3000\u90a3\u591c\u4e00\u6218\u6b7c\u654c\u4e24\u5927\u9ad8\u624b\uff0c\u6c5f\u67ab\u7684\u6b66\u529f\uff0c\u56fa\u4f7f\u7fa4\u8c6a\u656c\u4f69\uff0c\u4f46\u541f\u96ea\u7684\u9677\u654c\u7edd\u5883\u5e03\u7f6e\uff0c\u4e5f\u4f7f\u7fa4\u8c6a\u503e\u5fc3\uff0c\u5927\u90fd\u5168\u795e\u8d2f\u6ce8\uff0c\u503e\u542c\u5979\u7684\u9ad8\u8bba\u3002 \u3000\u3000\u541f\u96ea\u5ffd\u73b0\u7f9e\u6001\uff0c\u9053\uff1a\u201c\u5f20\u59e8\uff0c\u6211\u8bf4\u9519\u4e86\uff0c\u4f60\u8981\u7ea0\u6b63\u6211\u554a\uff01\u201d \u3000\u3000\u201c\u8bf4\u5427\uff01\u5927\u4e2b\u5934\uff0c\u5a18\u4e5f\u5728\u7528\u5fc3\u542c\u5462\uff1f\u8bf4\u9519\u4e86\u6253\u4ec0\u4e48\u7d27\uff0c\u4f60\u8fd8\u5c0f\u554a\uff01\u201d\u7f57\u5170\u5fc3\u76f4\u53e3\u8bc0\uff0c\u4e00\u4e0b\u5b50\u8bf4\u51fa\u4e86\u541f\u96ea\u5fc3\u4e2d\u7684\u538b\u529b\u3002 \u3000\u3000\u201c\u5f20\u59e8\uff0c\u73b0\u5728\u4e0d\u80fd\u5206\u5175\u4f5c\u6218\uff0c\u4ee5\u730e\u6740\u9e70\u3001\u72ac\u4e3a\u4e3b\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u8bf4\u4e0b\u53bb\u5427\uff01\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u7b2c\u4e00\u53e5\u8bdd\uff0c\u5df2\u7ecf\u5207\u4e2d\u8981\u5bb3\uff0c\u5f20\u59e8\u771f\u6709\u70b9\u5403\u60ca\u4e86\uff01\u201d \u3000\u3000\u541f\u96ea\u4f3c\u5f97\u5230\u4e86\u5f88\u5927\u7684\u9f13\u821e\uff0c\u4f83\u4f83\u800c\u8c08\uff0c\u9053\uff1a\u201c\u9e70\u3001\u72ac\u867d\u5177\u4e00\u4e9b\u7075\u6000\uff0c\u4f46\u5374\u4e0d\u80fd\u8fa8\u8bc6\u73af\u5883\u4e0d\u76f8\u540c\u7684\u5371\u673a\uff01 \u3000\u3000\u4f46\u4eba\u80fd\uff0c\u6211\u89c9\u5f97\u6307\u6325\u5b83\u4eec\u7684\u4eba\uff0c\u5df2\u7ecf\u8d76\u6765\uff0c\u800c\u4e14\uff0c\u8ddd\u6211\u4eec\u4e0d\u4f1a\u5f88\u8fdc\uff0c\u654c\u4eba\u7684\u884c\u52a8\uff0c\u4e5f\u5728\u53d8\u5316\uff0c\u4e00\u9762\u5728\u96c6\u4e2d\u529b\u91cf\uff0c\u4e00\u9762\u5728\u5bfb\u627e\u6211\u4eec\u7684\u7834\u7efd\uff0c\u6211\u4eec\u7684\u5206\u7ec4\u884c\u52a8\uff0c\u5982\u88ab\u53d1\u89c9\uff0c\u4e5f\u53ef\u80fd\u5df2\u88ab\u53d1\u89c9\uff01 \u3000\u3000\u654c\u4eba\u6b63\u5728\u4f5c\u622a\u51fb\u6211\u4eec\u7684\u5e03\u7f72\uff0c\u6240\u4ee5\uff0c\u6211\u4eec\u4e0d\u5b9c\u518d\u5206\u6563\u5b9e\u529b\uff0c\u654c\u4eba\u6015\u6211\u4eec\u7684\u6574\u4f53\u529b\u91cf\uff0c\u4e0d\u4e00\u5b9a\u4f1a\u6015\u6211\u5206\u7ec4\u7684\u529b\u91cf\uff0c\u4f55\u51b5\uff0c\u654c\u4eba\u4e5f\u4f1a\u57cb\u4f0f\u3001\u6697\u88ad\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u8fd8\u6709\u5417\uff1f\u201d \u3000\u3000\u201c\u654c\u4eba\u6700\u5927\u7684\u82e6\u607c\u662f\uff0c\u65e0\u6cd5\u786e\u5b9a\u6211\u4eec\u7684\u884c\u8e2a\uff0c\u2026\u2026\u201d\u541f\u96ea\u9053\uff1a\u201c\u4e0d\u80fd\u96c6\u4e2d\u5927\u6279\u4eba\u624b\uff0c\u5168\u529b\u56f4\u653b\u6c5f\u5927\u54e5\uff0c\u4f46\u7edd\u4e0d\u4f1a\u518d\u8f7b\u89c6\u6211\u4eec\uff0c\u4ed6\u4eec\u5df2\u5168\u529b\u548c\u6211\u4eec\u5c55\u5f00\u6597\u667a\u3001\u6597\u529b\u4e86\uff01\u201d \u3000\u3000\u201c\u5f88\u597d\uff0c\u597d\u6781\u4e86\uff0c\u6211\u4eec\u53c8\u8be5\u5982\u4f55\u5462\uff1f\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u7ee7\u7eed\u8bf4\u4e0b\u53bb\uff01\u201d \u3000\u3000\u201c\u5f20\u59e8\uff0c\u4f60\u4f5c\u4e3b\u561b\uff01\u4f60\u5fc3\u4e2d\u660e\u767d\u5f97\u5f88\uff0c\u4e3a\u4ec0\u4e48\u8fd8\u8981\u6211\u8bf4\uff1f\u201d \u3000\u3000\u5f20\u56db\u59d1\u9053\uff1a\u201c\u8bf4\u51fa\u6765\uff0c\u770b\u770b\u662f\u5426\u548c\u6211\u60f3\u7684\u4e00\u6837\uff1f\u201d \u3000\u3000\u201c\u4e00\u5b9a\u8981\u6211\u8bf4\uff0c\u90a3\u5c31\u518d\u6765\u4e00\u6b21\u8bf1\u6740\uff0c\u4e0d\u662f\u9e70\u72ac\uff0c\u800c\u662f\u4eba\u2026\u2026\u201d\u541f\u96ea\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u4e00\u5b9a\u8981\u9009\u4e00\u4e2a\u5bf9\u6211\u4eec\u6700\u6709\u5229\u7684\u5730\u65b9\uff0c\u53ea\u662f\u4e0d\u80fd\u518d\u7528\u8349\u539f\u4e86\uff0c\u6211\u76f8\u4fe1\u6211\u4eec\u90a3\u591c\u7684\u5e03\u7f6e\uff0c\u5df2\u843d\u5165\u654c\u4eba\u7684\u773c\u4e2d\u3002\u201d \u3000\u3000\u201c\u5bf9\uff01\u5c31\u7b97\u4ed6\u4eec\u6ca1\u6709\u770b\u5230\uff0c\u4e8b\u540e\u4e5f\u5df2\u4e86\u7136\uff0c\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u4ed6\u4eec\u60f3\u56f4\u6740\u6c5f\u67ab\uff0c\u90a3\u5c31\u591a\u51e0\u4e2a\u6c5f\u67ab\u51fa\u73b0\uff0c\u4e00\u6837\u7684\u8863\u670d\uff0c\u4e00\u6837\u7684\u5f62\u8c8c\uff0c\u98de\u9e70\u6ca1\u6cd5\u5b50\u8fa8\u8bc6\u4e86\uff0c\u518d\u963b\u7edd\u7075\u72ac\u8ffd\u8e2a\uff0c\u4ed6\u4eec\u5c31\u53ea\u6709\u51fa\u52a8\u4eba\u4e86\uff0c\u90a3\u5c31\u7ed9\u6211\u9009\u654c\u4ee5\u653b\u673a\u4f1a\u4e86\u3002\u201d \u3000\u3000\u201c\u5f20\u59e8\u8981\u7528\u4e07\u91cc\u98d8\u9999\u4e86\uff01\u201d\u541f\u96ea\u9053\uff1a\u201c\u53cd\u5ba2\u4e3a\u4e3b\uff0c\u662f\u5f88\u597d\u529e\u6cd5\u3002\u201d \u3000\u3000\u201c\u5927\u59d0\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u7a81\u7136\u63d2\u5634\u9053\uff1a\u201c\u9752\u51e4\u6709\u4e00\u4e2a\u7279\u522b\u7684\u80fd\u529b\uff0c\u5341\u4e08\u5185\u80fd\u5206\u8fa8\u51fa\u654c\u6211\u8eab\u4e0a\u7684\u4e0d\u540c\u6c14\u5473\uff01\u201d \u3000\u3000\u201c\u597d\u6781\u4e86\uff0c\u9752\u51e4\uff0c\u600e\u4e48\u4e00\u76f4\u6ca1\u6709\u544a\u8bc9\u8fc7\u5f20\u59e8\u5462\uff1f\u201d \u3000\u3000\u201c\u96d5\u866b\u5c0f\u6280\u561b\uff01\u6211\u4e0d\u6562\u8bf4\u51fa\u6765\u5440\u3002\u201d \u3000\u3000\u201c\u662f\u4e86\u4e0d\u8d77\u7684\u80fd\u529b\uff01\u6211\u4e00\u76f4\u62c5\u5fc3\u4e00\u4ef6\u4e8b\uff0c\u5bb3\u6015\u654c\u4eba\u6613\u5bb9\u8499\u6df7\uff0c\u63a5\u8fd1\u6211\u4eec\uff0c\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff0c \u3000\u3000\u201c\u4f60\u6709\u8fd9\u4e2a\u672c\u9886\uff0c\u5c31\u4e0d\u7528\u62c5\u5fc3\u8fd9\u4e2a\u4e86\u3002\u201d \u3000\u3000\u9752\u51e4\u7a81\u7136\u4e00\u76b1\u7709\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u73b0\u5728\uff0c\u5c31\u6709\u4e24\u4e2a\u9ad8\u624b\uff0c\u9690\u4f0f\u5728\u5341\u4e08\u5de6\u53f3\uff0c\u5df2\u7ecf\u8ffd\u8e2a\u6211\u4eec\u4e24\u5929\u4e86\uff0c\u6211\u5077\u5077\u544a\u8bc9\u8fc7\u6c5f\u5927\u54e5\uff0c\u6c5f\u5927\u54e5\u8981\u653e\u957f\u7ebf\u9493\u5927\u9c7c\uff0c\u6240\u4ee5\uff0c\u6211\u6ca1\u6709\u544a\u8bc9\u5f20\u59e8\u3002\u201d \u3000\u3000\u201c\u597d\u554a\uff01\u9752\u51e4\uff0c\u4f60\u4e00\u4e0b\u5b50\u5c31\u6380\u4e86\u6211\u7684\u5e95\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u4f60\u7ed9\u6211\u5c0f\u5fc3\uff0c\u6211\u53ef\u662f\u6709\u4ec7\u5fc5\u62a5\uff01\u201d \u3000\u3000\u9752\u51e4\u771f\u7684\u88ab\u5413\u7740\u4e86\uff0c\u7728\u52a8\u4e0b\u5927\u773c\u775b\uff0c\u4f4e\u4e0b\u5934\u53bb\u3002 \u3000\u3000\u201c\u6c5f\u67ab\u8eab\u4e3a\u7edf\u5e05\uff0c\u77e5\u654c\u4e0d\u62a5\uff0c\u4e5f\u4e0d\u4e0b\u4ee4\u5904\u7406\uff0c\u8be5\u4e0d\u8be5\u7f5a\u5462\uff1f\u201d\u5f20\u56db\u59d1\u4e00\u8138\u4e25\u8083\u795e\u8272\uff0c\u73af\u987e\u4e86\u7fa4\u8c6a\u4e00\u773c\uff0c\u5f81\u8be2\u610f\u89c1\u3002 \u3000\u3000\u5927\u5bb6\u90fd\u8ba4\u4e3a\u5f20\u56db\u59d1\u5728\u5f00\u73a9\u7b11\uff0c\u4f46\u89c1\u5979\u8138\u65e0\u7b11\u610f\uff0c\u795e\u60c5\u51b7\u8083\u7684\u6837\u5b50\uff0c\u60f3\u63d2\u5634\u51d1\u70ed\u95f9\u7684\u4eba\uff0c\u7acb\u523b\u6b62\u4f4f\uff0c\u9762\u9762\u76f8\u89d1\uff0c\u65e0\u4eba\u63a5\u53e3\u3002 \u3000\u3000\u201c\u5f20\u59e8\uff0c\u7f5a\u6211\u5427\uff01\u2026\u2026\u201d\u9752\u51e4\u5e7d\u5e7d\u5730\u8bf4\u9053\uff1a\u201c\u6211\u613f\u63a5\u53d7\u4efb\u4f55\u7684\u60e9\u7f5a\u2026\u2026\u201d \u3000\u3000\u201c\u4f60\u8981\u7f5a\uff0c\u6c5f\u67ab\u4e5f\u8981\u7f5a\u2026\u2026\u201d\u5f20\u56db\u59d1\u4e00\u526f\u94c1\u9762\u65e0\u79c1\u7684\u8bf4\uff0c\u201c\u7f5a\u4f60\u4eec\u53bb\u6349\u4e00\u4e2a\u8ffd\u8e2a\u7684\u654c\u4eba\u56de\u6765\uff0c\u8981\u6d3b\u7684\u3002\u201d \u3000\u3000\u9752\u98ce\u7ad9\u8d77\u8eab\u5b50\uff0c\u9053\uff1a\u201c\u6211\u8fd9\u5c31\u53bb\uff0c\u4ed6\u4eec\u8fd8\u6ca1\u6709\u8d70\u3002\u201d \u3000\u3000\u6c5f\u67ab\u4e00\u628a\u6293\u4f4f\u4e86\u9752\u51e4\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u7ed5\u8fc7\u53bb\u3002\u201d \u3000\u3000\u4e24\u4eba\u9690\u8e2a\u7ed5\u6797\uff0c\u6f5c\u8349\u800c\u884c\uff0c\u8ddd\u654c\u4e94\u516d\u4e08\u65f6\uff0c\u6c5f\u67ab\u7275\u4f4f\u9752\u51e4\u7684\u624b\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u5728\u8fd9\u91cc\u7b49\u6211\uff0c\u6211\u53bb\u6349\u4eba\u3002\u201d \u3000\u3000\u201c\u9752\u51e4\u9053\uff1a\u201c\u4f60\u4e0d\u751f\u6c14\u5417\uff1f\u6211\u60f3\u4e0d\u5230\u5f20\u59e8\u4f1a\u4e00\u4e0b\u53d8\u8138\uff1f\u4f7f\u4f60\u4e0b\u4e0d\u4e86\u53f0\u3002\u201d \u3000\u3000\u201c\u50bb\u4e2b\u5934\uff0c\u5979\u672c\u6765\u5c31\u8981\u8bf7\u6211\u53bb\u6349\u4eba\u7684\uff0c\u8fd9\u4e00\u4e0b\u4e0d\u7528\u8bf7\u4e86\uff0c\u7406\u76f4\u6c14\u58ee\u7684\u6d3e\u6211\u51fa\u52a8\u2026\u2026\u201d\u6c5f\u67ab\u7b11\u9053\uff1a\u201c\u6700\u91cd\u8981\u7684\u5979\u8981\u7ed9\u6211\u4eec\u4e00\u4e2a\u5355\u72ec\u76f8\u805a\u7684\u673a\u4f1a\uff0c\u4f60\u4e0d\u662f\u6709\u4e8b\uff0c\u60f3\u544a\u8bc9\u6211\u5417\uff1f\u201d \u3000\u3000\u9752\u51e4\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u7b2c\u4e00\u4e2a\u8981\u544a\u8bc9\u4f60\u7684\u662f\uff0c\u8fd9\u4e00\u6b21\u5728\u957f\u5b89\uff0c\u5929\u738b\u6ca1\u6709\u78b0\u6211\u2026\u2026\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u662f\u4e0d\u662f\u84dd\u51e4\u7684\u5173\u7cfb\uff1f\u201d \u3000\u3000\u201c\u4e0d\u662f\uff0c\u662f\u5f20\u59e8\u7684\u836f\u7269\u4f5c\u602a\uff0c\u4ed6\u4e0d\u6562\u52a8\u6211\uff0c\u4e8b\u5b9e\u4e0a\uff0c\u6211\u4e5f\u4e0b\u4e86\u51b3\u5fc3\u2026\u2026\u201d\u9752\u98ce\u9053\uff1a\u201c\u6697\u85cf\u5315\u9996\uff0c\u771f\u8981\u8feb\u6211\u4e0a\u5e8a\uff0c\u6211\u5c31\u51fa\u624b\u6740\u4ed6\uff0c\u6740\u4e0d\u4e86\u6211\u5c31\u81ea\u5c3d\uff0c\u4f46\u5f20\u59e8\u7684\u836f\u7269\u6551\u4e86\u6211\uff0c\u8ba9\u4ed6\u81ea\u5df2\u907f\u5f00\u3002\u201d \u3000\u3000\u201c\u8fd8\u6709\u5462\uff1f\u201d \u3000\u3000\u201c\u597d\u60f3\u5ff5\u4f60\uff0c\u4f46\u6211\u4e0d\u6562\u50cf\u541f\u96ea\u3001\u541f\u971c\u4e00\u6837\uff0c\u5728\u4f17\u76ee\u777d\u777d\u4e0b\u8868\u8fbe\u51fa\u5fc3\u4e2d\u7684\u60c5\u7231\u3002\u201d\u9752\u51e4\u9053\uff1a\u201c\u6211\u53ea\u6709\u6697\u4e2d\u60b2\u6ce3\uff0c\u628a\u76f8\u601d\u6df1\u85cf\u5fc3\u5e95\u2026\u2026\u201d \u3000\u3000\u201c\u9752\u51e4\uff0c\u4e00\u5207\u90fd\u6ca1\u6709\u6539\u53d8\uff0c\u6211\u5df2\u544a\u8bc9\u8fc7\u541f\u96ea\u3001\u541f\u971c\uff0c\u5979\u4eec\u90fd\u7518\u613f\u9000\u5c45\u59be\u4f4d\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\uff01\u201d\u9752\u51e4\u6253\u65ad\u4e86\u6c5f\u67ab\u7684\u8bdd\uff0c\u63a5\u9053\uff1a\u201c\u548c\u5f20\u59e8\u76f8\u5904\u4e4b\u540e\uff0c\u6211\u53d1\u89c9\u4e86\u4e00\u79cd\u4ece\u672a\u6709\u8fc7\u7684\u8212\u7545\uff0c\u90a3\u662f\u7cbe\u795e\u4e0a\u7684\u4e00\u79cd\u5951\u5408\uff0c\u96be\u9053\u4f60\u6ca1\u6709\u770b\u5230\u6885\u82b1\u59d0\u6b63\u5728\u8f6c\u6362\u6c14\u8d28\u3002 \u3000\u3000\u6211\u4e5f\u4e00\u6837\uff0c\u96ea\u3001\u971c\u4e8c\u5973\u66f4\u662f\u4ee4\u4eba\u656c\u4f69\u3001\u611f\u52a8\uff0c\u5979\u4eec\u65e0\u6b32\u65e0\u6c42\uff0c\u53ea\u662f\u7231\u4f60\uff0c\u6211\u8981\u5b66\u5979\u4eec\uff0c\u628a\u80f8\u6000\u5f00\u5c55\uff0c\u5728\u6816\u51e4\u697c\u65f6\u60f3\u5ff5\u4f60\uff0c\u53ea\u60f3\u5230\u548c\u4f60\u5728\u95fa\u623f\u4e2d\u62e5\u62b1\u7684\u7537\u6b22\u5973\u7231\uff0c\u90a3\u662f\u56e0\u4e3a\u6211\u4e0d\u77e5\u9053\u4eba\u95f4\u8fd8\u6709\u522b\u7684\u60c5\u611f\u3002 \u3000\u3000\u73b0\u5728\uff0c\u6211\u61c2\u4e86\uff0c\u6211\u89c9\u5f97\uff0c\u8fd9\u4e9b\u4eba\u90fd\u597d\u53ef\u7231\uff0c\u5e72\u7239\u3001\u5e72\u5a18\u3001\u8001\u53eb\u5316\u3001\u80e1\u840d\u3001\u6bb5\u4e5d\u7b49\uff0c\u8fde\u9093\u98de\u548c\u5201\u9e4f\uff0c\u90fd\u548c\u8fc7\u53bb\u4e0d\u540c\u4e86\uff0c\u6c5f\u90ce\uff0c\u6211\u4e0d\u8981\u4f60\u518d\u4e3a\u8a93\u8a00\u8d1f\u8d23\uff0c\u6211\u548c\u96ea\u3001\u971c\u3001\u6885\u82b1\uff0c\u662f\u56db\u4e2a\u60c5\u540c\u9aa8\u8089\u7684\u597d\u59d0\u59b9\uff0c\u6ca1\u6709\u5927\u5c0f\uff0c\u4e0d\u5206\u5f7c\u6b64\uff0c\u5982\u679c\uff0c\u4e00\u5b9a\u8981\u6709\u5927\u5c0f\u4e4b\u5206\uff0c\u541f\u96ea\u5e94\u8be5\u5360\u636e\u9b41\u9996\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u5f88\u611f\u52a8\uff0c\u4f38\u624b\u628a\u9752\u51e4\u62e5\u5165\u6000\u4e2d\uff0c\u5956\u8d4f\u4e86\u4e00\u4e2a\u8ba9\u4eba\u65ad\u6c14\u7684\u957f\u543b\u3002 \u3000\u3000\u9752\u51e4\u63a8\u5f00\u6c5f\u67ab\uff0c\u9053\uff1a\u201c\u5feb\u5440\uff0c\u53bb\u6293\u4eba\u5427\uff01\u6293\u4e0d\u5230\u5982\u4f55\u5bf9\u5f20\u59e8\u4ea4\u4ee3\u3002\u201d \u3000\u3000\u201c\u4e0d\u7528\u6709\u52b3\u4e86\uff0c\u9752\u51e4\u59d0\uff0c\u5c0f\u59b9\u5df2\u6293\u5230\u4e86\u4ed6\u4eec\uff0c\u4e00\u6b7b\u4e00\u6d3b\uff0c\u4e24\u4f4d\u597d\u597d\u7684\u8c08\u8c08\u5427\uff01\u522b\u5fd8\u4e86\u5e26\u4eba\u56de\u6765\u5c31\u662f\u3002\u201d \u3000\u3000\u201c\u541f\u96ea\uff0c\u8fc7\u6765\u5440\uff01\u201d\u9752\u51e4\u4e00\u8138\u7f9e\u6001\uff0c\u6025\u6025\u547c\u5524\uff0c\u4f46\u53c8\u4e0d\u6562\u8ba9\u58f0\u97f3\u592a\u5927\u3002 \u3000\u3000\u201c\u8d70\u4e86\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5176\u5b9e\uff0c\u5927\u59d0\uff0c\u8981\u4f60\u548c\u6211\u540c\u884c\uff0c\u81f3\u5c11\u5df2\u6709\u4e09\u4e2a\u4eba\u660e\u767d\u5979\u7684\u7528\u5fc3\u3002 \u3000\u3000\u96ea\u3001\u971c\u4e4b\u5916\uff0c\u8fd8\u6709\u5927\u548c\u5c1a\uff0c\u6885\u82b1\u60f3\u4e00\u4e0b\u4e5f\u4f1a\u660e\u767d\uff0c\u9752\u51e4\uff0c\u6211\u4eec\u7531\u654c\u5bf9\u6210\u4e3a\u592b\u59bb\uff0c\u6709\u540d\u6709\u5b9e\uff0c\u53ea\u5dee\u4e00\u4e2a\u62dc\u5929\u5730\u7684\u4eea\u5f0f\u7f62\u4e86\u3002 \u3000\u3000\u4f60\u7f8e\u4e3d\u3001\u806a\u6167\uff0c\u6211\u662f\u771f\u5fc3\u7684\u63a5\u7eb3\u4f60\uff0c\u7ec8\u5357\u5c71\u4e2d\u4e00\u6218\uff0c\u66f4\u8868\u73b0\u4e86\u4e00\u4e2a\u59bb\u5b50\u5bf9\u4e08\u592b\u7684\u5fe0\u8bda\uff0c\u4e8b\u5b9e\u4e0a\uff0c\u6240\u6709\u7684\u4eba\uff0c\u5fc3\u4e2d\u90fd\u5bf9\u4f60\u6709\u4efd\u611f\u6fc0\u3002 \u3000\u3000\u5f20\u5927\u59d0\u8d5e\u4f60\u806a\u660e\uff0c\u57fa\u7840\u539a\u5b9e\uff0c\u609f\u6027\u5f88\u9ad8\uff0c\u662f\u96be\u5f97\u7684\u7ec3\u6b66\u4eba\u624d\u2026\u2026\u201d \u3000\u3000\u9752\u51e4\u7a81\u7136\u6d41\u4e0b\u6cea\u6765\uff0c\u63a5\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u4ed6\u4eec\u90fd\u5bf9\u6211\u8fd9\u6837\u597d\uff0c\u5929\u738b\u95e8\u4e2d\u4eba\u5b8c\u5168\u4e0d\u540c\uff0c\u9664\u4e86\u8272\u6b32\u4e4b\u5916\uff0c\u5c31\u662f\u5c14\u865e\u6211\u8bc8\uff0c\u4ece\u672a\u6709\u8fc7\u4e00\u70b9\u771f\u60c5\u3001\u8bda\u610f\u3002\u201d \u3000\u3000\u201c\u4f60\u548c\u84dd\u51e4\uff0c\u4e0d\u662f\u60c5\u610f\u5f88\u6df1\uff1f\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u7ec8\u5357\u5c71\u4e2d\u4e00\u6218\uff0c\u5979\u5982\u51fa\u624b\uff0c\u5c31\u662f\u4e00\u4e2a\u5f88\u53ef\u4f2f\u7684\u540e\u679c\u4e86\uff01\u201d \u3000\u3000\u201c\u5728\u90a3\u4e2a\u73af\u5883\u4e2d\uff0c\u5979\u662f\u6211\u552f\u4e00\u6709\u60c5\u611f\u7684\u670b\u53cb\uff0c\u4f46\u90a3\u662f\u6211\u5fc3\u5b58\u5fcd\u8ba9\uff0c\u4ed8\u51fa\u4e86\u65e0\u6570\u5fc3\u8840\u6362\u6765\u7684\u53cb\u60c5\uff0c\u7ec8\u5357\u5c71\u4e2d\uff0c\u5f97\u5230\u4e86\u5979\u4e00\u4efd\u56de\u62a5\u2026\u2026\u201d \u3000\u3000\u7a81\u7136\u5fae\u5fae\u4e00\u7b11\uff0c\u4f4f\u53e3\u4e0d\u8a00\u3002 \u3000\u3000\u770b\u9752\u51e4\u7b11\u7684\u8be1\u5f02\uff0c\u6c5f\u67ab\u82e5\u6709\u6240\u609f\uff0c\u63a5\u9053\uff1a\u201c\u8bf4\u4e0b\u53bb\u554a\uff01\u600e\u4e48\u653e\u5201\u8d77\u6765\uff01\u201d \u3000\u3000\u201c\u8d31\u59be\u4e0d\u6562\uff0c\u672c\u6765\uff0c\u6211\u5df2\u8bf4\u52a8\u5979\uff0c\u8ddf\u4f60\u89c1\u9762\uff0c\u73b0\u5728\uff0c\u6050\u6015\u8981\u4f60\u5931\u671b\u4e86\u2026\u2026\u201d\u9752\u51e4\u9053\uff1a \u3000\u3000\u201c\u8fd9\u4ef6\u4e8b\uff0c\u6211\u60f3\u8d77\u6765\uff0c\u81ea\u5df1\u4e5f\u89c9\u5f97\u597d\u7b11\uff0c\u90a3\u6709\u59bb\u5b50\u5e2e\u4e08\u592b\u62c9\u76ae\u6761\u7684\uff0c\u6211\u4e0d\u61c2\uff0c\u4e3a\u4ec0\u4e48\u4f1a\u53bb\u505a\u8fd9\u4ef6\u4e8b\uff1f \u3000\u3000\u770b\u5230\u96ea\u3001\u971c\u3001\u6885\u82b1\uff0c\u597d\u50cf\u90fd\u6709\u8fd9\u79cd\u80f8\u6000\uff0c\u6211\u89c9\u5f97\u6211\u4eec\u5973\u4eba\u597d\u53ef\u601c\u554a\uff01\u4e3a\u4e86\u8ba8\u597d\u4e08\u592b\uff0c\u771f\u662f\u65e0\u6240\u4e0d\u4e3a\uff01\u201d \u3000\u3000\u201c\u9752\u51e4\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u4f60\u548c\u96ea\u3001\u971c\u3001\u6885\u82b1\uff0c\u90fd\u662f\u5929\u4e0b\u7075\u79c0\u6240\u949f\u7684\u7f8e\u5973\uff0c\u5f97\u4e00\u7f8e\uff0c\u5df2\u7ecf\u8db3\u6170\u751f\u5e73\u3002\u4f55\u51b5\uff0c\u56db\u7f8e\u5e76\u81f3\u3002 \u3000\u3000\u5929\u4e0b\u4e0d\u4f1a\u771f\u6709\u4f7f\u4f60\u4eec\u5982\u6b64\u6df1\u60c5\u6295\u6ce8\u7684\u7537\u4eba\uff0c\u6211\u4e4b\u6240\u4ee5\u5982\u6b64\uff0c\u662f\u56e0\u4e3a\u7ec3\u4e86\u2018\u60d1\u5fc3\u672f\u2019\u8fd9\u4e2a\u90aa\u95e8\u6b66\u529f\uff0c\u6211\u5df2\u6697\u81ea\u51b3\u5b9a\uff0c\u6b64\u540e\uff0c\u975e\u81f3\u4e07\u4e0d\u5f97\u5df2\uff0c\u4e0d\u518d\u65bd\u5c55\u6b64\u672f\u2026\u2026\u201d \u3000\u3000\u9752\u51e4\u7389\u638c\u4e00\u626c\uff0c\u63a9\u4f4f\u6c5f\u67ab\u7684\u5634\u5df4\uff0c\u9053\uff1a\u201c\u522b\u592a\u65e9\u51b3\u5b9a\uff0c\u6c5f\u90ce\uff0c\u4f60\u5982\u4e0d\u65bd\u672f\u81ea\u4fdd\uff0c\u5f88\u53ef\u80fd\u4f1a\u88ab\u5a9a\u672f\u7275\u5fc3\u3002 \u3000\u3000\u5929\u540e\u5bab\u4e2d\u4eba\uff0c\u90fd\u5584\u5a9a\u672f\uff0c\u84dd\u51e4\u7684\u9020\u8be3\u4ea6\u6df1\uff0c\u6211\u4e0d\u77e5\u9053\uff0c\u4e00\u65e6\u4f60\u4e3a\u5a9a\u672f\u6240\u60d1\uff0c\u4f1a\u6210\u4ec0\u4e48\u6837\u7684\u5c40\u9762\uff0c\u4f46\u4e00\u5b9a\u5f88\u53ef\u6015\u3002 \u3000\u3000\u81f3\u5c11\uff0c\u4f60\u8981\u65bd\u672f\u81ea\u4fdd\uff0c\u6211\u4eec\u4e0d\u8981\u5931\u53bb\u4f60\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u8fd9\u771f\u662f\u6811\u6b32\u9759\u800c\u98ce\u4e0d\u606f\u4e86\uff0c\u5509\uff01\u8d70\u5427\uff01\u201d \u3000\u3000\u4e24\u4e2a\u654c\u4eba\u90fd\u7a7f\u7740\u519c\u4eba\u8863\u7740\uff0c\u8eba\u5728\u6df1\u8349\u4e2d\uff0c\u4e00\u4e2a\u5df2\u6b7b\uff0c\u4e00\u4e2a\u88ab\u70b9\u4e86\u7a74\u9053\uff0c\u9752\u51e4\u63d0\u8d77\u6d3b\u7684\uff0c\u884c\u56de\u6797\u4e2d\uff0c\u9053\uff1a\u201c\u5f20\u59e8\uff0c\u8fd9\u4e2a\u4eba\u2026\u2026\u201d \u3000\u3000\u201c\u653e\u4e0b\u5427\uff01\u201d\u5f20\u56db\u59d1\u6253\u65ad\u4e86\u9752\u51e4\u7684\u8bdd\uff0c\u63a5\u9053\uff1a\u201c\u5201\u9e4f\uff0c\u4f60\u53bb\u95ee\u53e3\u4f9b\uff0c\u8981\u4ed6\u5c3d\u5410\u80f8\u4e2d\u6240\u77e5\u3002\u201d \u3000\u3000\u6c5f\u673a\u62cd\u6d3b\u90a3\u4eba\u660f\u7761\u7a74\uff0c\u5374\u987a\u624b\u70b9\u4e86\u4ed6\u53cc\u81c2\uff0c\u53cc\u817f\u4e0a\u56db\u5904\u7a74\u9053\u3002 \u3000\u3000\u5201\u9e4f\u884c\u8fd1\u8eab\u4fa7\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u670b\u53cb\uff0c\u6211\u8bb2\u8bdd\u7b97\u8bdd\uff0c\u6211\u95ee\u4f60\u7b54\uff0c\u6211\u4e0d\u4f1a\u5f3a\u4eba\u6240\u96be\uff01\u4f46\u8c0e\u8a00\u8bc8\u8bed\uff0c\u7edd\u96be\u8fc7\u5173\uff0c\u4f60\u8be5\u77e5\u9053\u597d\u591a\uff1f\u6211\u5fc3\u4e2d\u6709\u6570\u3002 \u3000\u3000\u4e0d\u56de\u7b54\u5c31\u5148\u5272\u5de6\u8033\uff0c\u63a5\u7740\u53f3\u8033\uff0c\u53cc\u624b\u3001\u53cc\u811a\uff0c\u4f60\u8001\u5144\u80fd\u5fcd\u53d7\uff0c\u90a3\u5c31\u4e0d\u7528\u56de\u7b54\u4ec0\u4e48\uff01\u6211\u4e5f\u4e0d\u4f1a\u8981\u4f60\u7684\u547d\u3002 \u3000\u3000\u65a9\u5b8c\u4f60\u4e24\u652f\u811a\uff0c\u4f60\u4ecd\u7136\u4e0d\u56de\u4e00\u53e5\u8bdd\uff0c\u7b97\u4f60\u662f\u4e00\u6761\u786c\u6c49\uff0c\u6211\u4e5f\u4e0d\u4f1a\u518d\u95ee\u4e86\u3002\u73b0\u5728\uff0c\u6211\u5148\u95ee\u7b2c\u4e00\u4e2a\u95ee\u9898\uff0c\u4f60\u662f\u5c5e\u4e8e\u90a3\u4e00\u4e2a\u7ec4\u5408\u7684\u4eba\uff1f \u3000\u3000\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u201d \u3000\u3000\u90a3\u4eba\u95ed\u76ee\u4e0d\u8bed\u3002 \u3000\u3000\u5201\u9e4f\u5530\u7684\u4e00\u58f0\uff0c\u62bd\u51fa\u5bbd\u9762\u5200\uff0c\u5200\u5149\u4e00\u95ea\u65a9\u4e0b\u5de6\u8033\uff0c\u9053\uff1a\u201c\u8bf4\uff01\u201d \u3000\u3000\u90a3\u4eba\u4e00\u72b9\u8c6b\uff0c\u4e00\u652f\u53f3\u8033\uff0c\u53c8\u88ab\u65b0\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u201c\u8fd9\u4e00\u6b21\u662f\u5de6\u624b\u2026\u2026\u201d\u5201\u9e4f\u53c8\u626c\u8d77\u624b\u4e2d\u4e4b\u5200\u3002 \u3000\u3000\u201c\u6211\u53eb\u9a6c\u9f99\uff0c\u662f\u5929\u901a\u9662\u4e2d\u7684\u4f7f\u8005\uff0c\u4e0d\u5c5e\u4e8e\u5929\u738b\u95e8\u3001\u5929\u540e\u5bab\uff0c\u4e5f\u4e0d\u5c5e\u5929\u9a6c\u5802\uff0c\u6211\u4eec\u53d7\u8fc7\u7279\u6b8a\u7684\u8bad\u7ec3\uff0c\u80fd\u548c\u9e70\u3001\u72ac\u6c9f\u901a\u3002\u201d \u3000\u3000\u201c\u5f88\u597d\uff0c\u50cf\u8fd9\u6837\u8c08\u4e0b\u53bb\uff0c\u6211\u4eec\u90fd\u4f1a\u5f88\u6109\u5feb\u2026\u2026\u201d\u5201\u9e4f\u9053\uff1a\u201c\u5929\u901a\u9662\u662f\u600e\u4e48\u6837\u4e00\u4e2a\u7ec4\u5408\uff1f \u3000\u3000\u6211\u5e0c\u671b\u4f60\u80fd\u8bf4\u5f97\u6e05\u695a\u4e00\u4e9b\u3002\u201d \u3000\u3000\u201c\u8ffd\u8e2a\u8bf8\u4f4d\u7684\u98de\u9e70\u3001\u7075\u5927\uff0c\u7531\u5929\u901a\u9662\u8bad\u7ec3\u6307\u6325\uff0c\u6211\u4eec\u4ee5\u98de\u9e70\u4f20\u4e66\u7684\u65b9\u6cd5\uff0c\u628a\u6d88\u606f\u901a\u77e5\u5929\u738b\u95e8\u3001\u5929\u540e\u5bab\u3001\u5929\u9a6c\u5802\u2026\u2026\u201d\u9a6c\u9f99\u9053\uff1a\u201c\u5929\u901a\u4f7f\u8005\u7684\u884c\u52a8\uff0c\u662f\u8ddf\u8e2a\u9e70\u3001\u72ac\u4f9b\u5e94\u9972\u517b\u98df\u7269\uff0c\u8fd9\u4e9b\u98df\u7269\uff0c\u7ecf\u8fc7\u79d8\u65b9\u8c03\u914d\uff0c\u9e70\u3001\u72ac\u81ea\u5c0f\u98df\u7528\u6210\u4e60\uff0c\u5b83\u4eec\u5df2\u4e0d\u518d\u5403\u7684\u4e1c\u897f\uff0c\u6240\u4ee5\uff0c\u5b83\u4eec\u4e0d\u4f1a\u9003\u8d70\uff0c\u4e5f\u4e0d\u6613\u4e3a\u4eba\u5e03\u4e0b\u6df7\u5408\u836f\u7269\u7684\u8bf1\u9975\u6bd2\u6740\u3002\u201d \u3000\u3000\u201c\u5389\u5bb3\u5440\uff01\u8fde\u755c\u7267\u90fd\u7528\u65b9\u6cd5\u63a7\u5236\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u5929\u901a\u4f7f\u8005\u6709\u591a\u5c11\u4eba\uff0c\u9664\u4e86\u8fd0\u9972\u98df\u7269\u4e4b\u5916\uff0c\u8fd8\u6709\u4ec0\u4e48\u5de5\u4f5c\uff1f\u201d \u3000\u3000\u201c\u8ffd\u8e2a\uff0c\u6211\u4eec\u5341\u4e8c\u4e2a\u795e\u884c\u4f7f\u8005\uff0c\u76f4\u63a5\u542c\u547d\u9662\u4e3b\uff0c\u2026\u2026\u201d\u9a6c\u9f99\u9053\uff1a\u201c\u6211\u4eec\u90fd\u5177\u6709\u7edd\u4f73\u7684\u8f7b\u529f\uff0c\u4e5f\u5b66\u4e60\u8fc7\u8ffd\u8e2a\u8e29\u8ff9\u4e4b\u672f\u3002\u201d \u3000\u3000\u201c\u4f60\u4eec\u7ecf\u5e38\u90fd\u662f\u8ffd\u5728\u9e70\u3001\u72ac\u4e4b\u540e\u5417\uff1f\u201d\u5f20\u56db\u59d1\u95ee\u3002 \u3000\u3000\u201c\u4e0d\uff01\u6211\u4eec\u5f88\u5c11\u51fa\u52a8\uff0c\u4f46\u51ed\u9e70\u3001\u72ac\u4e4b\u529b\uff0c\u5df2\u4f7f\u654c\u4eba\u65e0\u6cd5\u9041\u5f62\uff0c\u5373\u4f7f\u6211\u4eec\u8fd0\u4f9b\u9972\u6599\uff0c\u4e5f\u662f\u8fdc\u843d\u540e\u6570\u5341\u91cc\u5916\u2026\u2026\u201d \u3000\u3000\u5f20\u56db\u59d1\u70b9\u70b9\u5934\uff0c\u9a6c\u9f99\u5df2\u81ea\u52a8\u63a5\u6c11\u9053\uff1a\u201c\u4f46\u8bf8\u4f4d\u7528\u786c\u5f13\u957f\u7bad\uff0c\u730e\u6740\u9e70\u3001\u72ac\uff0c\u4f7f\u5b83\u4eec\u4e0d\u6562\u4f4e\u98de\u63a5\u8fd1\uff0c\u6240\u4ee5\uff0c\u9662\u4e3b\u6d3e\u6211\u4eec\u8ffd\u8e2a\u4fa6\u5bdf\u3002\u201d \u3000\u3000\u201c\u53ea\u662f\u8fd9\u6837\u4e48\uff1f\u2026\u2026\u201d\u5f20\u56db\u59d1\u8138\u8272\u4e00\u51b7\u3002 \u3000\u3000\u201c\u63d0\u4f9b\u7ed9\u5929\u9a6c\u5802\u7684\u5802\u4e3b\uff0c\u4f5c\u4e3a\u5e03\u7f6e\u62d2\u654c\u4e4b\u7528\u3002\u2026\u2026\u201d\u9a6c\u9f99\u6025\u6025\u63a5\u9053\uff1a\u201c\u5728\u4e0b\u77e5\u9053\u7684\uff0c\u5c31\u662f\u8fd9\u4e9b\u4e86\u3002\u201d \u3000\u3000\u201c\u4f60\u8bf4\u5929\u9a6c\u5802\u4e3b\u6765\u4e86\uff1f\u201d\u5201\u9e4f\u9053\uff1a\u201c\u9664\u4e86\u5802\u4e3b\u4e4b\u5916\uff0c\u8fd8\u6709\u4e9b\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u201c\u786e\u5b9e\u7684\u5185\u60c5\uff0c\u5728\u4e0b\u5e76\u4e0d\u6e05\u695a\u3002\u201d\u9a6c\u9f99\u9053\uff1a\u201c\u53ea\u77e5\u9053\u968f\u884c\u7684\u4eba\u6570\u4e0d\u5c11\uff0c\u597d\u50cf\u6709\u526f\u603b\u5802\u4e3b\uff0c\u798f\u5bff\u9662\u4e2d\u7684\u697c\u4e3b\u3001\u9662\u4e3b\u7b49\u2026\u2026\u201d \u3000\u3000\u5201\u9e4f\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u9662\u4e3b\u548c\u516d\u5927\u6402\u4e3b\u90fd\u6765\u4e86\u5417\uff1f\u90a3\u662f\u7cbe\u9510\u5c3d\u51fa\uff0c\u503e\u5de2\u6765\u653b\u4e86\u3002\u201d \u3000\u3000\u201c\u5927\u6982\uff0c\u8fd9\u4e00\u6218\u662f\u5929\u9a6c\u5802\u7684\u603b\u5802\u4e3b\u590f\u5929\u540c\u4e3a\u524d\u950b\u6307\u6325\uff0c\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u7fa4\u9b54\u96c6\u805a\uff0c\u4f46\u4e5f\u53ea\u662f\u4eba\u5c45\u4f59\u6c14\uff0c\u524d\u5bfc\u5352\u5b50\uff0c\u7518\u4e3a\u4eba\u7528\uff0c\u771f\u4e0d\u77e5\u4ed6\u4eec\u7684\u9b54\u7130\u8c6a\u6c14\uff0c\u90fd\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u201c\u679c\u7136\u662f\u53ef\u60b2\u5f97\u5f88\uff0c\u201d\u6c5f\u67ab\u63a5\u9053\uff1a\u201c\u6ca1\u6709\u5929\u738b\u95e8\uff0c\u5929\u540e\u5bab\u8f85\u52a9\u4f5c\u6218\uff0c\u5929\u9a6c\u5802\u53ea\u6709\u5c3d\u51fa\u7cbe\u9510\u4e86\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u76ee\u8f6c\u6ce8\u5728\u9a6c\u9f99\u8eab\u4e0a\uff0c\u9053\uff1a\u201c\u7b54\u590d\u6211\u6700\u540e\u4e00\u4e2a\u95ee\u9898\uff0c\u5929\u901a\u9662\u4e3b\u662f\u8c01\uff1f\u53eb\u4ec0\u4e48\u540d\u5b57\uff0c\u662f\u4e0d\u662f\u4e2d\u539f\u4eba\u6c0f\uff1f\u201d \u3000\u3000\u201c\u91d1\u5f8b\u4eae\uff0c\u8bf4\u7684\u4e00\u53e3\u4eac\u7247\u5b50\uff0c\u6709\u4e24\u4e2a\u5973\u52a9\u624b\u5e2e\u4ed6\uff0c\u636e\u8bf4\u4ed6\u80fd\u901a\u9e1f\u8bdd\uff0c\u4f46\u65e0\u6cd5\u8bc1\u660e\u2026\u2026\u201d \u3000\u3000\u9a6c\u9f99\u9053\uff1a\u201c\u6211\u4eec\u89c1\u4ed6\u7684\u673a\u4f1a\u4e0d\u591a\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u4f60\u53ef\u4ee5\u8d70\u4e86\u3002\u201d\u5f20\u56db\u59d1\u5341\u5206\u6e29\u548c\u8bf4\u3002 \u3000\u3000\u4f46\u9a6c\u9f99\u5374\u7ad9\u7740\u4e0d\u52a8\u3002 \u3000\u3000\u201c\u8001\u5f1f\uff0c\u8ba9\u4f60\u8d70\u4e86\uff0c\u542c\u5230\u6ca1\u6709\u2026\u2026\u201d\u5201\u9e4f\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u96be\u9053\u8981\u6211\u4eec\u6d3e\u9876\u516b\u4eba\u5927\u8f7f\u9001\u4f60\uff1f\u201d \u3000\u3000\u201c\u6211\u4e00\u8d70\u5fc5\u6b7b\u65e0\u7591\u2026\u2026\u201d\u9a6c\u9f99\u9053\uff1a\u201c\u6211\u65e0\u6cd5\u907f\u8fc7\u9e70\u3001\u72ac\uff0c\u91d1\u9662\u4e3b\u4e5f\u7edd\u4e0d\u4f1a\u653e\u8fc7\u6211\uff01\u201d \u3000\u3000\u201c\u4f60\u7684\u610f\u601d\u5462\uff1f\u201d \u3000\u3000\u201c\u5f20\u59d1\u5a18\u5982\u80af\u7559\u7528\u6211\uff0c\u9a6c\u67d0\u613f\u610f\u6548\u547d\uff01\u5982\u662f\u59d1\u5a18\u65e0\u6cd5\u76f8\u4fe1\u5728\u4e0b\uff0c\u53ea\u6c42\u8ba9\u9a6c\u67d0\u6b7b\u4e2a\u75db\u5feb\uff01\u201d \u3000\u3000\u201c\u6885\u82b1\u3001\u9752\u51e4\uff0c\u5e2e\u9a6c\u9f99\u6577\u836f\u5305\u624e\uff0c\u2026\u2026\u201d\u5f20\u56db\u59d1\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u53ef\u4ee5\u7559\u4e0b\u6765\uff0c\u4e5f\u53ef\u4ee5\u968f\u65f6\u79bb\u53bb\uff0c\u6211\u76f8\u4fe1\u4f60\uff0c\u4e5f\u613f\u610f\u7559\u7528\u4f60\uff0c\u4f46\u662f\u5374\u65e0\u6cd5\u4fdd\u969c\u4f60\u7684\u6027\u547d\u3001\u5b89\u5168\uff0c\u6211\u4eec\u4ee5\u5be1\u6297\u4f17\uff0c\u751f\u5b58\u7684\u5371\u673a\u5f88\u5927\u3002\u201d\u8f6c\u8eab\u79bb\u53bb\u3002\u201d \u3000\u3000\u5a07\u7f8e\u7edd\u4f26\u7684\u9752\u51e4\u3001\u6885\u82b1\uff0c\u8f7b\u4e8e\u7ec6\u81f4\u7684\u66ff\u9a6c\u9f99\u5305\u597d\u4f24\u52bf\uff0c\u9894\u9996\u4e00\u7b11\uff0c\u9000\u4e86\u5f00\u53bb\u3002 \u3000\u3000\u9a6c\u9f99\u7acb\u523b\u6709\u7740\u4e00\u79cd\u81ea\u7531\u8212\u7545\u7684\u611f\u89c9\uff0c\u8fd9\u4e9b\u4eba\u8bf4\u4ee5\u505a\u5230\u3002 \u3000\u3000\u201c\u8981\u4e0d\u8981\u6765\u4e00\u6b21\u8bf1\u654c\u4f0f\u6740\uff1f\u201d \u3000\u3000\u5f20\u56db\u59d1\u53ec\u96c6\u4e86\u7fa4\u5bb6\u5546\u8bae\uff0c\u800c\u4e14\uff0c\u8fde\u9a6c\u9f99\u4e5f\u4e0d\u907f\u5fcc\u3002 \u3000\u3000\u4ed6\u53cc\u8033\u88ab\u5272\uff0c\u7eb1\u5e03\u91cd\u88f9\uff0c\u4f46\u4ecd\u9690\u9690\u53ef\u4ee5\u542c\u5230\u4e00\u4e9b\u5185\u60c5\u3001\u8bed\u58f0\u3002 \u3000\u3000\u201c\u548c\u5c1a\u8d5e\u6210\uff0c\u8ba9\u4ed6\u4eec\u53d7\u6b21\u5927\u521b\uff0c\u632b\u5176\u9510\u6c14\u3002\u201d \u3000\u3000\u201c\u4e5f\u8bb8\u8fd8\u53ef\u4ee5\u4f7f\u4e00\u4e9b\u5fc3\u751f\u89c9\u609f\u7684\u540c\u9053\u6295\u9760\u8fc7\u6765\u2026\u2026\u201d\u9093\u98de\u9053\uff1a\u201c\u50cf\u6211\u9093\u67d0\u4e00\u6837\uff0c\u5fc3\u4e2d\u65e9\u5df2\u4e86\u7136\uff0c\u6b63\u81ea\u884c\u5411\u4e0d\u5f52\u8def\uff01\u4f46\u5374\u53c8\u522b\u65e0\u53bb\u5904\u3002\u201d \u3000\u3000\u201c\u541f\u96ea\uff0c\u4f60\u6709\u4ec0\u4e48\u770b\u6cd5\uff1f\u201d\u5f20\u56db\u59d1\uff0c\u201c\u4f60\u4eec\u59d0\u59b9\u548c\u6c5f\u67ab\uff0c\u662f\u4f0f\u6740\u654c\u4eba\u7684\u4e3b\u529b\uff0c\u5c82\u53ef\u4e00\u8bed\u4e0d\u53d1\uff1f\u201d \u3000\u3000\u201c\u8bf1\u654c\u4f0f\u6740\uff0c\u8ba1\u8c0b\u867d\u597d\uff0c\u4f46\u654c\u4eba\u9996\u8111\u5982\u662f\u4e0d\u80af\u6d89\u9669\u4e00\u62fc\uff0c\u6211\u4eec\u5c31\u6536\u83b7\u4e0d\u5927\uff0c\u2026\u2026\u201d\u541f\u96ea\u9053\uff1a\u201c\u5982\u80fd\u5728\u4f0f\u6740\u4e4b\u540e\uff0c\u518d\u4e3b\u52a8\u51fa\u51fb\uff0c\u5f53\u53ef\u51fa\u654c\u610f\u5916\uff0c\u9664\u6389\u4e00\u4e9b\u5929\u9a6c\u5802\u4e2d\u7cbe\u9510\u548c\u9996\u8981\u4eba\u7269\uff0c\u8fd9\u4e00\u80a1\u529b\u91cf\uff0c\u7eb5\u4e0d\u74e6\u89e3\uff0c\u4e5f\u5fc5\u5143\u6c14\u5927\u4f24\uff01\u201d \u3000\u3000\u201c\u597d\u6781\u4e86\uff0c\u5c31\u8fd9\u4e48\u529e\u3002\u73b0\u5728\uff0c\u6211\u4eec\u5c31\u5e03\u7f6e\u4f0f\u6740\uff0c\u4e58\u654c\u53d7\u632b\u4e4b\u4e71\uff0c\u76f4\u6363\u4e2d\u67a2\uff0c\u88ad\u6740\u654c\u4eba\u9996\u607c\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u4f3c\u662f\u65e9\u5df2\u6210\u7af9\u5728\u80f8\uff0c\u7acb\u523b\u5c55\u5f00\u5e03\u7f72\u3002 \u3000\u3000\u8fd9\u662f\u7d27\u90bb\u5d69\u5c71\u7684\u864e\u5cad\uff0c\u5c71\u5c97\u8d77\u4f0f\uff0c\u8346\u68d8\u4e1b\u751f\uff0c\u4e25\u683c\u8bf4\u7b97\u4e0d\u4e0a\u5c71\u5cad\uff0c\u4f46\u5730\u5f62\u590d\u6742\uff0c\u80e1\u6811\u4e71\u8349\uff0c\u8513\u5ef6\u6a2a\u751f\uff0c\u6811\u4e0d\u9ad8\uff0c\u8349\u5374\u5f88\u6df1\uff0c\u9690\u5165\u6df1\u8349\u4e1b\u4e2d\uff0c\u867d\u7136\u9e70\u76ee\u7280\u5229\uff0c\u4e5f\u96be\u53d1\u89c9\u3002 \u3000\u3000\u7075\u72ac\u867d\u7136\u4e45\u7ecf\u8bad\u7ec3\uff0c\u4f46\u5728\u719f\u6089\u60c5\u6027\u7684\u9a6c\u9f99\u6307\u70b9\u4e4b\u4e0b\uff0c\u8feb\u8e2a\u800c\u6765\u7684\u5341\u53ea\u7075\u72ac\uff0c\u5168\u88ab\u730e\u6740\u4e86\u3002 \u3000\u3000\u9e70\u3001\u72ac\u8ffd\u8e2a\u7684\u6d88\u606f\uff0c\u7a81\u7136\u4e2d\u65ad\u3002 \u3000\u3000\u5929\u9a6c\u5802\u4e3b\u590f\u5929\u540c\u4ee5\u7fa4\u8c6a\u6298\u8f6c\u7684\u884c\u8fdb\u8def\u7ebf\uff0c\u7814\u5224\u6c5f\u67ab\u7b49\u4e00\u884c\u7684\u76ee\u7684\u662f\u5d69\u5c71\u5c11\u6797\u5bfa\uff0c\u5728\u8981\u9053\u4e0a\u8bbe\u4f0f\u4ee5\u5f85\uff0c\u4f46\u5374\u4e45\u4e45\u4e0d\u89c1\u6c5f\u67ab\u7b49\u4e00\u884c\u51fa\u73b0\u3002 \u3000\u3000\u85cf\u8eab\u5728\u6742\u6811\u8349\u4e1b\u4e2d\uff0c\u5e76\u4e0d\u597d\u8fc7\uff0c\u5e78\u597d\u5f20\u56db\u59d1\u914d\u5236\u7684\u96c4\u9ec4\u836f\u9152\uff0c\u53d1\u6325\u51fa\u6781\u5927\u7684\u529f\u80fd\uff0c\u4f7f\u8349\u4e1b\u4e2d\u7684\u868a\u8747\u86c7\u9f20\u8d70\u907f\u3002 \u3000\u3000\u5f20\u56db\u59d1\u5e26\u9886\u7fa4\u8c6a\u5c31\u9690\u85cf\u5728\u8fd9\u4e00\u7247\u7eb5\u6a2a\u6570\u91cc\u7684\u8352\u8349\u6742\u6811\u4e1b\u4e2d\u3002 \u3000\u3000\u8fd9\u4e9b\u4eba\u5927\u90fd\u662f\u7ec8\u5357\u5c71\u4e2d\u4e00\u573a\u6076\u6218\u4e2d\u7684\u864e\u53e3\u4f59\u751f\uff0c\u5fc3\u4e2d\u660e\u767d\u654c\u52bf\u7684\u5f3a\u5927\uff0c\u9ad8\u624b\u4f17\u591a\uff0c\u4e00\u65e6\u518d\u4eba\u9677\u9631\uff0c\u8001\u547d\u7edd\u5bf9\u96be\u4fdd\uff0c\u8010\u5fc3\u5947\u5f3a\uff0c\u65e0\u868a\u4fb5\u6270\u4e4b\u82e6\uff0c\u5e03\u597d\u8b66\u6212\uff0c\u5012\u53ef\u5206\u73ed\u4f11\u606f\uff0c\u82e6\u7684\u662f\u65e0\u6cd5\u597d\u597d\u5403\u559d\uff0c\u53ea\u80fd\u4ee5\u5e72\u7cae\u5145\u9965\u3002 \u3000\u3000\u8fd9\u4e00\u6f5c\u9690\u5165\u4f0f\uff0c\u4e00\u7b49\u5c31\u662f\u4e09\u5929\u4e09\u591c\u3002 \u3000\u3000\u4f46\u6c5f\u67ab\u548c\u541f\u971c\uff0c\u5374\u5728\u6f5c\u5165\u8349\u539f\u4e4b\u540e\uff0c\u5c31\u6613\u5bb9\u800c\u51fa\u3002 \u3000\u3000\u541f\u96ea\u6ca1\u6709\u968f\u540c\u51fa\u52a8\uff0c\u5979\u662f\u7559\u5728\u8352\u539f\u4e2d\u7684\u91cd\u8981\u529b\u91cf\u3002 \u3000\u3000\u96ea\u3001\u971c\u4e8c\u5973\uff0c\u90fd\u4e0d\u662f\u85cf\u79c1\u7684\u4eba\uff0c\u5979\u4eec\u5df2\u628a\u4f20\u771f\u8bb0\u7684\u6b66\u529f\uff0c\u9009\u51fa\u4e86\u4e00\u4e9b\u7acb\u5373\u53ef\u6d3e\u4e0a\u7528\u573a\u7684\uff0c\u4f20\u6388\u7ed9\u5f20\u56db\u59d1\u548c\u6885\u82b1\u3001\u9752\u51e4\uff0c\u501f\u8fd9\u8349\u539f\u4e2d\u51e0\u65e5\u5c0f\u606f\uff0c\u6885\u82b1\u3001\u9752\u51e4\u90fd\u5e9f\u5bdd\u5fd8\u98df\u7684\u82e6\u7ec3\u6b66\u529f\u3002 \u3000\u3000\u6b27\u9633\u662d\u548c\u4e94\u4e2a\u5f1f\u5b50\u65e5\u5915\u805a\u4e8e\u4e00\u5904\uff0c\u5546\u8ba8\u5251\u9635\u7684\u6f14\u8fdb\u53d8\u5316\u3002 \u3000\u3000\u7ec8\u5357\u5c71\u4e2d\u4e4b\u6218\uff0c\u5251\u9635\u53d1\u6325\u51fa\u7edd\u5927\u7684\u5a01\u529b\uff0c\u6709\u5982\u64ce\u5929\u4e00\u67f1\uff0c\u529b\u6491\u5371\u5c40\uff0c\u8fd9\u5c31\u4f7f\u6b27\u9633\u662d\u53d7\u5230\u4e86\u5f3a\u5927\u7684\u538b\u529b\uff0c\u611f\u53d7\u5230\u5728\u8fd9\u573a\u51b3\u6218\u4e2d\u8d23\u4efb\u91cd\u5927\uff0c\u4e34\u9635\u78e8\u67aa\uff0c\u65e5\u591c\u548c\u4e94\u4e2a\u5f1f\u5b50\uff0c\u5b88\u5728\u4e00\u8d77\uff0c\u82e6\u7ec3\u5251\u9635\u3002 \u3000\u3000\u7f57\u5170\u5374\u662f\u4e00\u76f4\u5728\u9ed8\u7136\u9759\u5750\uff0c\u539f\u6765\u719f\u8bb0\u4e8e\u5fc3\u7684\u5929\u72d0\u6b66\u529f\u65e5\u51b3\uff0c\u4e00\u4e00\u5728\u8111\u9645\u95ea\u8fc7\uff0c\u9762\u4e34\u7740\u751f\u6b7b\u5173\u5934\uff0c\u7f57\u5170\u5df2\u4e0d\u518d\u987e\u5fcc\uff0c\u5f00\u59cb\u4e60\u7ec3\u8d77\u5929\u72d0\u6b66\u529f\u4e86\u3002 \u3000\u3000\u541f\u96ea\u671b\u671b\u51dd\u76ee\u6c89\u541f\u7684\u5f20\u56db\u59d1\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5f20\u59e8\uff0c\u4ed6\u4eec\u53bb\u4e86\u4e09\u5929\u600e\u7684\u8fd8\u6ca1\u6709\u4e00\u70b9\u6d88\u606f\uff1f\u201d \u3000\u3000\u201c\u6c5f\u67ab\u7684\u6b66\u529f\uff0c\u7a76\u7adf\u5230\u4e86\u4ec0\u4e48\u5883\u754c\uff1f\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u4f1a\u4e0d\u4f1a\u88ab\u4eba\u56f0\u4f4f\uff1f\u5982\u679c\u8eab\u843d\u9677\u9631\uff0c\u541f\u971c\u4e5f\u8be5\u4f20\u51fa\u4e07\u91cc\u98d8\u9999\u554a\uff1f\u201d \u3000\u3000\u201c\u5e94\u8be5\u4e0d\u4f1a\uff0c\u201d\u53f6\u96ea\u9053\uff1a\u201c\u6c5f\u5927\u54e5\u6b66\u529f\u5230\u4e86\u4ec0\u4e48\u5883\u754c\uff0c\u6211\u8bf4\u4e0d\u51fa\u6765\uff0c\u4f46\u541f\u971c\u7684\u6210\u5c31\uff0c\u53ef\u4ee5\u529b\u6597\u56db\u4e2a\u65f6\u8fb0\u4e0d\u7528\u4f11\u606f\u3002\u201d \u3000\u3000\u201c\u56db\u4e2a\u65f6\u8fb0\u4e4b\u540e\u5462\uff1f\u201d\u5f20\u56db\u59d1\u95ee\u3002 \u3000\u3000\u201c\u90a3\u5c31\u9700\u8981\u4e00\u70b9\u65f6\u95f4\uff0c\u5598\u53e3\u6c14\u8c03\u606f\u4e00\u4e0b\uff0c\u201d\u541f\u96ea\u9053\uff1a\u201c\u6709\u56db\u4e2a\u65f6\u8fb0\u7684\u5149\u666f\uff0c\u5979\u8981\u6740\u6389\u591a\u5c11\u4eba\u5462\uff1f\u971c\u59b9\u771f\u7684\u5149\u4e86\u706b\uff0c\u5168\u8eab\u6280\u827a\u51fa\u7b3c\uff0c\u6740\u4f24\u529b\u7684\u5f3a\u5927\uff0c\u53ef\u6bd4\u6c5f\u5927\u54e5\u8fd8\u8981\u5389\u5bb3\uff01\u6211\u76f8\u4fe1\u4ee5\u971c\u59b9\u7684\u673a\u7075\u548c\u6c5f\u5927\u54e5\u7684\u6210\u5c31\uff0c\u7edd\u4e0d\u4f1a\u88ab\u654c\u4eba\u6240\u56f0\u4f4f\u3002\u201d \u3000\u3000\u201c\u90a3\u4f60\u8fd8\u62c5\u7684\u4ec0\u4e48\u5fc3\u5462\uff1f\u201d \u3000\u3000\u201c\u60c5\u4e0d\u81ea\u7981\u561b\uff01\u201d\u541f\u96ea\u9053\uff1a\u201c\u6211\u5fc3\u91cc\u85cf\u4e0d\u4f4f\u79d8\u5bc6\uff0c\u60f3\u5230\u4e86\u5c31\u8bf4\u51fa\u6765\uff0c\u9752\u51e4\u548c\u6885\u82b1\u59d0\uff0c\u5374\u90fd\u7126\u6025\u7a9d\u5728\u5fc3\u5e95\uff01\u201d \u3000\u3000\u5f20\u56db\u59d1\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fc7\u4e86\u5348\u65f6\uff0c\u5982\u679c\u8fd8\u4e0d\u89c1\u4ed6\u4eec\u6d88\u606f\uff0c\u6211\u4eec\u5c31\u6539\u53d8\u4e00\u4e0b\u5e03\u7f72\u3002\u201d \u3000\u3000\u5982\u4f55\u6539\u53d8\uff0c\u5f20\u56db\u59d1\u6ca1\u6709\u8bf4\u4e0b\u53bb\uff0c\u541f\u96ea\u4e5f\u672a\u8ffd\u95ee\uff0c\u4f46\u51dd\u795e\u5077\u542c\u7684\u6885\u82b1\u3001\u9752\u51e4\uff0c\u5374\u7a0d\u677e\u4e86\u4e3a\u90ce\u4f73\u8651\u7684\u5fc3\u60c5\u3002 \u3000\u3000\u6c5f\u67ab\u548c\u53f6\u971c\u4e5f\u6b63\u4ee5\u9ad8\u5ea6\u8010\u5fc3\uff0c\u82b1\u8d39\u4e09\u65e5\u591c\u7684\u8fb0\u5149\u7b49\u5019\u5230\u8fd9\u91cd\u8981\u7684\u65f6\u523b\u5230\u6765\uff0c\u51c6\u5907\u884c\u52a8\u3002 \u3000\u3000\u539f\u6765\uff0c\u4e24\u4eba\u6f5c\u5c71\u8352\u539f\u4e4b\u540e\uff0c\u541f\u971c\u5df2\u5177\u5fc3\u5f97\u7684\u6613\u5bb9\u624b\u6cd5\uff0c\u5192\u9669\u76f4\u8fdb\uff0c\u5316\u88c5\u6210\u654c\u4eba\uff0c\u800c\u4e14\u4e24\u5ea6\u66f4\u6613\u8eab\u4efd\uff0c\u624d\u6df7\u5165\u4e86\u516b\u5341\u91cc\u5916\u7684\u654c\u4eba\u603b\u90e8\u201c\u8521\u5bb6\u8bcd\u5802\u201d\u3002 \u3000\u3000\u4e00\u6b21\u9ec4\u6cb3\u6e83\u5824\uff0c\u4f7f\u8fd9\u89c4\u8303\u5b8f\u5927\u7684\u7960\u5802\u56db\u5468\u7530\u56ed\u6d41\u5931\uff0c\u4f46\u7960\u5802\u672c\u8eab\u56e0\u5efa\u7b51\u7262\u56fa\uff0c\u5730\u52bf\u53c8\u8f83\u9ad8\uff0c\u635f\u574f\u4e0d\u591a\u3002 \u3000\u3000\u53ef\u662f\uff0c\u7960\u5802\u56db\u5916\u5374\u88ab\u6d2a\u6c34\u65cb\u6d41\uff0c\u51b2\u79ef\u6210\u4e00\u7247\u6cbc\u6cfd\uff0c\u65e0\u6cd5\u91cd\u5efa\u3002 \u3000\u3000\u8fd9\u5ea7\u8521\u5bb6\u7960\u5802\u5c31\u6210\u4e00\u5ea7\u552f\u4e00\u77d7\u7acb\u5728\u6cbc\u6cfd\u4e2d\u7684\u5efa\u7b51\u7269\uff0c\u4e5f\u6210\u4e86\u725b\u9b3c\u86c7\u795e\u8054\u6b22\u4f1a\u6240\u5728\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u4e00\u822c\u4eba\u4e5f\u4e0d\u6562\u8f7b\u6613\u7684\u5230\u7960\u5802\u6765\uff0c\u56e0\u6d41\u6c99\u9677\u9631\u56db\u5e03\uff0c\u6ca1\u6709\u4e24\u628a\u5237\u5b50\u7684\u4eba\uff0c\u8fd8\u771f\u7684\u4e0d\u6562\u6d89\u9669\u3002 \u3000\u3000\u4f46\u73b0\u5728\uff0c\u8521\u5bb6\u7960\u5802\u4e2d\uff0c\u5374\u4e91\u96c6\u4e86\u5f88\u591a\u7684\u4eba\uff0c\u6709\u8001\u6709\u5c11\uff0c\u6709\u7537\u6709\u5973\u3002 \u3000\u3000\u8d1f\u8d23\u5b88\u536b\u7684\uff0c\u662f\u4e00\u8272\u9ed1\u8863\u7684\u5251\u571f\uff0c\u4eba\u6570\u7adf\u6709\u56db\u5341\u516b\u4f4d\u4e4b\u591a\uff0c\u516b\u4eba\u4e00\u7ec4\uff0c\u5206\u6210\u516d\u7ec4\uff0c\u4e24\u7ec4\u4e00\u73ed\u3002 \u3000\u3000\u90a3\u662f\u8bf4\uff0c\u4efb\u4f55\u65f6\u523b\uff0c\u90fd\u6709\u5341\u516d\u4f4d\u9ed1\u8863\u5251\u58eb\uff0c\u5206\u5e03\u5728\u8521\u5bb6\u7960\u5802\u5185\u5916\uff0c\u4e25\u5bc6\u76d1\u5b88\u3002 \u3000\u3000\u541f\u971c\u548c\u6c5f\u67ab\u5c31\u6f5c\u8eab\u4e8e\u6ce5\u6c99\u4e2d\uff0c\u51dd\u6ce8\u7740\u8521\u5bb6\u7960\u5802\u3002 \u3000\u3000\u201c\u6c5f\u5927\u54e5\uff0c\u5bb3\u4f60\u53d7\u82e6\u4e86\uff0c\u8fd9\u5730\u65b9\u5b9e\u5728\u627e\u4e0d\u51fa\u53ef\u4ee5\u9041\u8eab\u6240\u5728\uff0c\u53ea\u597d\u501f\u6ce5\u6c99\u63a9\u8eab\uff0c\u51e0\u4e2a\u65f6\u8fb0\u4e4b\u4e45\uff0c\u4f60\u53d7\u5f97\u4e86\u5417\uff1f\u201d \u3000\u3000\u201c\u5168\u8eab\u9690\u5165\u6ce5\u6c99\u4e2d\uff0c\u81ea\u7136\u662f\u4e0d\u592a\u597d\u53d7\uff0c\u4e0d\u8fc7\uff0c\u5f88\u503c\u5f97\uff0c\u5df2\u7ecf\u6709\u56db\u4e94\u6279\u4eba\u8fdb\u5165\u4e86\u8521\u5bb6\u7960\u5802\uff0c\u770b\u4ed6\u4eec\u98de\u6e21\u6cbc\u6cfd\u7684\u8eab\u6cd5\uff0c\u7adf\u7136\u5982\u5c65\u5e73\u5730\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u6bcf\u4e2a\u4eba\uff0c\u90fd\u5df2\u5177\u2018\u767b\u840d\u6e21\u6c34\u2019\u7684\u7edd\u4f73\u8f7b\u529f\uff0c\u5929\u9a6c\u5802\u4e2d\u7684\u91cd\u8981\u9b54\u5934\uff0c\u4f3c\u662f\u5df2\u5927\u4e3e\u51fa\u52a8\uff0c\u5c3d\u96c6\u4e8e\u6b64\u4e86\u3002 \u3000\u3000\u4eca\u5929\uff0c\u53ef\u80fd\u6709\u4e00\u6b21\u91cd\u8981\u4f1a\u8bae\uff1f\u4e5f\u8bb8\uff0c\u6765\u4eba\u4e0d\u6b62\u662f\u5929\u9a6c\u5802\u4e2d\u4eba\u5462\uff01\u201d \u3000\u3000\u201c\u5e0c\u671b\uff0c\u4ed6\u4eec\u7684\u91cd\u8981\u4f1a\u8bae\uff0c\u5165\u591c\u4e4b\u540e\uff0c\u518d\u884c\u5f00\u59cb\u2026\u2026\u201d\u541f\u971c\u9053\uff1a\u201c\u4ed6\u4eec\u6212\u5907\u4e25\u8c28\uff0c\u5927\u767d\u5929\u6f5c\u5165\u8fdb\u53bb\uff0c\u6211\u5fc3\u4e2d\u5b9e\u65e0\u628a\u63e1\uff0c\u4f46\u773c\u770b\u4f60\u50cf\u4e2a\u6ce5\u4eba\u4e00\u6837\uff0c\u6211\u5fc3\u4e2d\u53c8\u597d\u96be\u8fc7\uff0c\u59d0\u59d0\u77e5\u9053\u4e86\uff0c\u4e00\u5b9a\u5f88\u5fc3\u75bc\u3002\u201d \u3000\u3000\u201c\u4f60\u5462\uff1f\u541f\u971c\uff0c\u96be\u9053\u4e0d\u96be\u8fc7\u5417\uff1f\u201d \u3000\u3000\u201c\u5f20\u59e8\u7684\u4e94\u884c\u9041\u672f\uff0c\u5c31\u662f\u8981\u9690\u4f0f\u6f5c\u8e2a\uff0c\u501f\u7528\u4e07\u7269\uff0c\u6eb6\u4e3a\u4e00\u4f53\uff0c\u6709\u7740\u7279\u522b\u547c\u606f\u4e4b\u6cd5\uff0c\u6240\u4ee5\uff0c\u6211\u53ef\u4ee5\u5fcd\u53d7\u3002\u4f46\u4f60\u6ca1\u6709\u7ec3\u8fc7\u554a\uff01\u6211\u77e5\u9053\uff0c\u4f60\u662f\u4e3a\u4e86\u6211\uff0c\u624d\u5fcd\u53d7\u8fd9\u4e9b\u82e6\u695a\uff01\u201d \u3000\u3000\u6c5f\u67ab\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u4f60\u4f1a\u8fd9\u6837\u60f3\u5462\uff1f\u201d \u3000\u3000\u201c\u6211\u76f8\u4fe1\u4f60\u53ef\u4ee5\u6740\u4ed6\u4eec\u4e00\u4e2a\u5251\u58eb\uff0c\u6362\u4e0a\u4ed6\u4eec\u7684\u8863\u670d\uff0c\u6df7\u5165\u4ed6\u4eec\u4e4b\u4e2d\uff0c\u800c\u4e0d\u88ab\u4ed6\u4eec\u53d1\u73b0\uff0c\u4f46\u6211\u6050\u6015\u4e0d\u884c\u2026\u2026\u201d \u3000\u3000\u201c\u541f\u971c\uff0c\u5c31\u7b97\u6211\u6709\u8fd9\u4e2a\u80fd\u529b\uff0c\u4f46\u4e5f\u4e0d\u80fd\u65bd\u7528\uff0c\u56e0\u4e3a\uff0c\u65e0\u6cd5\u907f\u5f00\u4ed6\u4eec\u6362\u73ed\u65f6\u7684\u4e00\u5173\uff0c\u6211\u4eec\u6700\u91cd\u8981\u7684\u4e8b\uff0c\u662f\u63a2\u542c\u4ed6\u4eec\u7684\u884c\u52a8\u8ba1\u5212\uff0c\u786e\u5b9a\u4ed6\u4eec\u7684\u8eab\u4efd\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u975e\u5f97\u501f\u91cd\u4f60\u7684\u65b9\u6cd5\u4e0d\u53ef\uff0c\u8fd9\u53ef\u662f\u547d\u4e2d\u6ce8\u5b9a\u7684\u4e00\u573a\u8f9b\u82e6\u4e86\u3002\u201d \u3000\u3000\u541f\u971c\u542c\u5f97\u5fc3\u4e2d\u9ad8\u5174\uff0c\u5a07\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u4f1a\u62a5\u7b54\u4f60\u7684\uff0c\u4ee5\u540e\uff0c\u4e0d\u8ba9\u4f60\u518d\u5fcd\u53d7\u75db\u82e6\u3002\u201d \u3000\u3000\u201c\u75db\u82e6\uff1f\u2026\u2026\u201d\u6c5f\u67ab\u4e00\u65f6\u4e0d\u80fd\u4f1a\u610f\uff0c\u5947\u9053\uff1a\u201c\u6211\u4f1a\u6709\u4ec0\u4e48\u75db\u82e6\uff1f\u201d \u3000\u3000\u541f\u971c\u7728\u52a8\u4e00\u4e0b\u773c\u775b\uff0c\u9053\uff1a\u201c\u5230\u65f6\u5019\uff0c\u4f60\u5c31\u660e\u767d\u4e86\uff0c\u5176\u5b9e\uff0c\u6211\u548c\u59d0\u59d0\u5546\u91cf\u4e86\u597d\u51e0\u6b21\uff0c\u770b\u5230\u4f60\u88ab\u6211\u4eec\u95f9\u5f97\u90a3\u79cd\u65e0\u53ef\u5948\u4f55\u7684\u795e\u60c5\uff0c\u771f\u60f3\u4e0d\u987e\u4e00\u5207\u7684\u6ee1\u8db3\u4f60\u3002 \u3000\u3000\u6211\u63a8\u7ed9\u5979\uff0c\u5979\u63a8\u7ed9\u6211\uff0c\u7ed3\u679c\uff0c\u90fd\u6ca1\u6709\u5c1d\u8bd5\uff01\u6211\u60f3\uff0c\u6211\u548c\u9752\u51e4\u3001\u6885\u82b1\u59d0\u5728\u4e00\u8d77\u65f6\uff0c\u5c31\u4e0d\u6709\u5fcd\u53d7\u8fd9\u4e9b\u6298\u78e8\u4e86\u3002\u201d \u3000\u3000\u6c5f\u67ab\u7ec8\u4e8e\u60f3\u660e\u767d\u4e86\u541f\u971c\u5728\u8bf4\u4ec0\u4e48\uff0c\u7b11\u4e00\u7b11\uff0c\u6ca1\u6709\u56de\u7b54\uff0c\u8fd9\u5b9e\u5728\u662f\u4e00\u4ef6\u5f88\u96be\u56de\u7b54\u7684\u4e8b\uff01\u53ea\u5f97\u642c\u8f6c\u8bdd\u9898\uff0c\u9053\uff1a\u201c\u541f\u971c\uff0c\u521a\u624d\uff0c\u4f60\u770b\u5230\u90a3\u4e2a\u7d2b\u8863\u5973\u4eba\uff0c\u5e26\u7740\u4e24\u4e2a\u4e2b\u5934\uff0c\u53ef\u77e5\u9053\u5979\u662f\u8c01\u5417\uff1f\u201d \u3000\u3000\u201c\u91d1\u51e4\u51f0\u7533\u96ea\u541b\uff0c\u5bf9\u5417\uff1f\u542c\u8bf4\u5979\u6709\u4e00\u79cd\u975e\u5e38\u7279\u6b8a\u7684\u672c\u9886\uff0c\u53eb\u4f5c\u5341\u51e4\u9f50\u98de\uff01\u201d \u3000\u3000\u201c\u4f60\u600e\u4e48\u8fd9\u6837\u6e05\u695a\u2026\u2026\u201d\u6c5f\u67ab\u6709\u4e9b\u5927\u611f\u610f\u5916\u7684\u9053\u3002 \u3000\u3000\u201c\u7533\u96ea\u541b\u53bb\u8fc7\u6d1b\u9633\u5c45\uff0c\u6211\u4eec\u89c1\u8fc7\u5979\uff0c\u6c5f\u5927\u54e5\uff0c\u4f60\u5e94\u8be5\u77e5\u9053\u554a\uff01\u6211\u548c\u59d0\u90fd\u6709\u8fc7\u76ee\u4e0d\u5fd8\u4e4b\u80fd\uff0c\u89c1\u8fc7\u4e00\u9762\u7684\u4eba\uff0c\u4e09\u4e94\u5e74\u5185\u4e0d\u4f1a\u5fd8\u8bb0\uff01\u201d \u3000\u3000\u201c\u7d27\u8ffd\u7533\u96ea\u541b\u8eab\u540e\u7684\u4eba\uff0c\u770b\u5230\u4e86\u6ca1\u6709\uff1f\u201d \u3000\u3000\u201c\u770b\u5230\u4e86\uff0c\u2026\u2026\u201d\u541f\u971c\u8bf4\uff0c\u201c\u90a3\u4e2a\u7a7f\u7740\u957f\u886b\u7684\u4eba\uff0c\u7559\u7740\u4e09\u67b6\u957f\u9aef\u7684\u4eba\u3002\u201d \u3000\u3000\u201c\u5bf9\uff01\u77e5\u9053\u4ed6\u662f\u8c01\u5417\uff1f\u201d \u3000\u3000\u201c\u662f\u4e0d\u662f\u5929\u9a6c\u884c\u7a7a\u53f8\u5f92\u827a\uff0c\u5929\u9a6c\u5802\u4e2d\u6700\u5177\u6743\u52bf\u7684\u526f\u603b\u5802\u3002\u201d \u3000\u3000\u6c5f\u67ab\u4e0d\u5f97\u4e0d\u4f69\u670d\u4e86\uff0c\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u5bf9\uff01\u4f60\u600e\u4e48\u77e5\u9053\u7684\uff1f\u201d \u3000\u3000\u201c\u8fd9\u8981\u62dc\u59d0\u59d0\u4e4b\u8d50\u4e86\uff0c\u5979\u548c\u5f20\u59e8\uff0c\u7814\u7a76\u5f3a\u654c\uff0c\u5f20\u59e8\u53e3\u8ff0\uff0c\u59d0\u59d0\u7ed8\u5236\u56fe\u50cf\uff0c\u5e76\u8bd5\u56fe\u5217\u51fa\u4ed6\u4eec\u7684\u540d\u5b57\uff0c\u6885\u82b1\u3001\u9752\u51e4\u6765\u4e86\u4e4b\u540e\uff0c\u518d\u7531\u5979\u4eec\u67e5\u770b\u4fee\u6b63\uff0c\u540e\u8001\u53eb\u5316\u4e5f\u52a0\u5165\u53e3\u8ff0\uff0c\u8fd9\u4e9b\u8d44\u6599\uff0c\u4e0d\u662f\u5f88\u5b8c\u7f8e\uff0c\u2026\u2026\u201d\u541f\u971c\u9053\uff1a\u201c\u4e5f\u53ea\u80fd\u7528\u4e8e\u5929\u9a6c\u5802\u4e2d\u4eba\uff0c\u5929\u738b\u95e8\u548c\u5929\u540e\u5bab\u4e2d\u4eba\uff0c\u5c31\u4f9d\u8d56\u9752\u51e4\u3001\u6885\u82b1\u63d0\u4f9b\u4e86\u3002\u5f20\u59e8\u5df2\u6536\u85cf\u4e86\u4e00\u672c\u4e66\u7c3f\uff0c\u4f46\u6ca1\u6709\u628a\u63e1\uff0c\u53ea\u80fd\u7559\u4f5c\u53c2\u8003\uff0c\u4e0d\u4fbf\u62ff\u51fa\u6765\u3002\u201d \u3000\u3000\u201c\u5509\uff01\u5f20\u5927\u59d0\u679c\u7136\u662f\u7ec6\u81f4\u7684\u5f88\u3002\u201d \u3000\u3000\u201c\u6c5f\u5927\u54e5\uff0c\u7559\u4e0b\u5f20\u59e8\u597d\u4e0b\u597d\uff1f\u6211\u548c\u59d0\u90fd\u6709\u4e00\u79cd\u611f\u89c9\uff0c\u6c5f\u6e56\u5e95\u5b9a\u4e4b\u540e\uff0c\u5f20\u59e8\u4f1a\u6084\u7136\u800c\u53bb\uff0c\u600e\u4e48\u529e\u5462\uff1f\u201d \u3000\u3000\u201c\u8fd9\u5c31\u8981\u4f60\u548c\u541f\u96ea\u5e2e\u5fd9\u4e86\uff0c\u6211\u4e5f\u4f1a\u5168\u529b\u6c42\u5979\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u8fc7\u53bb\uff0c\u6211\u4e00\u76f4\u8ba4\u4e3a\u81ea\u5df1\u6bd4\u59d0\u59d0\u806a\u660e\uff0c\u73b0\u5728\uff0c\u6211\u7ec8\u4e8e\u660e\u767d\uff0c\u5f88\u591a\u4e8b\u90fd\u662f\u59d0\u5b58\u5fc3\u8ba9\u6211\uff0c\u2026\u2026\u201d\u541f\u971c\u7b11\u9053\uff1a\u201c\u5979\u662f\u5927\u667a\u82e5\u611a\uff0c\u5c0f\u4e8b\u60c5\uff0c\u6545\u610f\u8981\u6211\u51fa\u98ce\u5934\u3002\u201d \u3000\u3000\u201c\u5982\u8bba\u673a\u53d8\u4e4b\u80fd\uff0c\u4f60\u4e0d\u5728\u541f\u96ea\u4e4b\u4e0b\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u4f60\u4eec\u5404\u6709\u6240\u957f\uff0c\u8fd9\u6b21\u4fa6\u5bdf\u654c\u60c5\uff0c\u8d23\u4efb\u4f55\u7b49\u91cd\u5927\uff0c\u4f60\u4eec\u5f20\u59e8\uff0c\u5c31\u8bf7\u4f60\u51fa\u9a6c\u4e86\u3002\u201d \u3000\u3000\u201c\u6240\u4ee5\u5440\uff01\u67e5\u4e0d\u51fa\u4e00\u70b9\u7709\u76ee\uff0c\u5982\u4f55\u5411\u5f20\u59e8\u4ea4\u4ee3\uff0c\u62d6\u4f60\u966a\u6211\u53d7\u7f6a\uff0c\u597d\u751f\u4e0d\u5b89\u2026\u2026\u201d\u541f\u971c\u9053\uff1a\u201c\u770b\u4ed6\u4eec\u90a3\u4e48\u591a\u9ad8\u624b\u4e91\u96c6\uff0c\u6211\u4e5f\u611f\u53d7\u5230\u524d\u9014\u8346\u68d8\u91cd\u91cd\uff0c\u8fd9\u4e00\u6218\u4e0d\u80fd\u6709\u4efb\u4f55\u5931\u9519\uff0c\u53ea\u4e0d\u77e5\u90a3\u4e2a\u603b\u5802\u4e3b\u590f\u5929\u540c\u662f\u5426\u4e5f\u5230\u4e86\u3002\u201d \u3000\u3000\u201c\u5e94\u8be5\u5230\u4e86\uff0c\u541f\u971c\uff0c\u4f60\u662f\u8bb0\u5f97\u6709\u4e00\u6279\u8fdb\u5165\u7960\u5802\u7684\u4eba\u7269\uff0c\u6709\u4e00\u4e2a\u5168\u8eab\u9ed1\u888d\uff0c\u6234\u4e00\u9876\u5927\u6be1\u5e3d\uff0c\u53c8\u56f4\u4e86\u4e00\u4e2a\u9ed1\u8272\u56f4\u5dfe\uff0c\u53ea\u9732\u51fa\u4e00\u5bf9\u773c\u775b\u7684\u4eba\uff0c\u4ed6\u4e3e\u6b65\u5982\u5e38\u5730\u6e21\u8fc7\u4e86\u4e00\u7247\u6cbc\u6cfd\u3002\u201d \u3000\u3000\u201c\u5bf9\uff01\u4f46\u4ed6\u4e3a\u4ec0\u4e48\u6545\u4f5c\u795e\u79d8\u5462\uff1f\u5728\u6700\u4eb2\u8fd1\u7684\u5c5e\u4e0b\u9762\u524d\uff0c\u4e5f\u8981\u906e\u5934\u63a9\u9762\u2026\u2026\u201d\u541f\u971c\u9053\uff1a \u3000\u3000\u201c\u8fd9\u7b49\u85cf\u5934\u9732\u5c3e\uff0c\u5c82\u4e0d\u66f4\u5bb9\u6613\uff0c\u66b4\u9732\u81ea\u5df1\u7684\u8eab\u4efd\uff1f\u201d \u3000\u3000\u201c\u6211\u60f3\u662f\u53e6\u6709\u82e6\u8877\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u4eca\u591c\uff0c\u5982\u662f\u6709\u673a\u4f1a\uff0c\u6211\u4eec\u5c31\u63ed\u5f00\u4ed6\u7684\u82e6\u8877\u77a7\u77a7\u3002\u201d \u3000\u3000\u592a\u9633\u4e0b\u5c71\u4e86\uff0c\u4f46\u897f\u5929\u6d53\u5bc6\u7684\u4e91\u5c42\uff0c\u63a9\u4f4f\u4e86\u7eda\u70c2\u7684\u665a\u971e\u3002 \u3000\u3000\u541f\u971c\u5f88\u9700\u8981\u8fd9\u4e00\u523b\u63d0\u524d\u800c\u5230\u9ed1\u6697\uff0c\u7275\u8d77\u6c5f\u67ab\uff0c\u884c\u5230\u4e8c\u91cc\u5916\u4e00\u5904\u79ef\u6c34\u7684\u5c0f\u6eaa\u4e2d\uff0c\u8df3\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u8fd9\u65f6\u6cbc\u6cfd\u6f84\u6e17\u51fa\u7684\u6e05\u6d41\uff0c\u6c34\u52bf\u4e0d\u5c0f\uff0c\u541f\u971c\u5e2e\u52a9\u6c5f\u5927\u54e5\u6d17\u53bb\u4e86\u4e00\u8eab\u6ce5\u6c99\uff0c\u7b11\u9053\uff1a\u201c\u6050\u6015\u65e0\u6cd5\u751f\u706b\u70e4\u5e72\u8863\u670d\u4e86\uff0c\u6c5f\u5927\u54e5\uff0c\u53ea\u597d\u5fcd\u8010\u4e00\u4e0b\u4e86\uff01\u201d \u3000\u3000\u6c5f\u67ab\u5fae\u7b11\u4e0d\u8bed\uff0c\u770b\u5979\u5fd9\u5f97\u50cf\u4e00\u4e2a\u5c0f\u5988\u5988\u4f3c\u7684\uff0c\u51e0\u4e4e\u8981\u5931\u58f0\u800c\u7b11\uff0c\u4f46\u4ed6\u5fcd\u4f4f\u4e86\u3002 \u3000\u3000\u541f\u971c\u4e5f\u6d17\u51c0\u672c\u8eab\u7684\u6ce5\u6c99\uff0c\u77eb\u4e3d\u5c3d\u590d\uff0c\u7a81\u7136\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u6c5f\u5927\u54e5\uff0c\u8fd9\u91cc\u96be\u5f97\u6709\u4e00\u6eaa\u6e05\u6d41\uff0c\u8981\u4e0d\u8981\u8131\u5149\u8863\u670d\uff0c\u597d\u597d\u7684\u6d17\u4e2a\u6fa1\u3002\u201d \u3000\u3000\u201c\u4e0d\u884c\uff0c\u654c\u60c5\u77ac\u606f\u4e07\u53d8\uff0e\u6211\u4eec\u4e0d\u80fd\u803d\u8bef\u65f6\u95f4\u3002\u201d \u3000\u3000\u541f\u971c\u8dc3\u767b\u5cb8\u4e0a\uff0c\u7b11\u9053\uff1a\u201c\u81f3\u5c11\u9664\u53bb\u8863\u670d\u4e0a\u7684\u79ef\u6c34\uff0c\u624d\u80fd\u884c\u52a8\u554a\uff01\u201d \u3000\u3000\u53e3\u4e2d\u8bf4\u8bdd\uff0c\u53cc\u624b\u5df2\u7136\u52a8\u4e86\u8d77\u6765\u3002 \u3000\u3000\u6c5f\u67ab\u6ca1\u6709\u6323\u52a8\uff0c\u541f\u971c\u5f88\u987a\u5229\u7684\u5265\u4e86\u6c5f\u67ab\u7684\u5916\u886b\u3001\u957f\u88e4\uff0c\u8fde\u9774\u5b50\u3001\u889c\u5b50\uff0c\u4e5f\u4e00\u9f50\u8131\u4e0b\uff0c\u7528\u624b\u62e7\u5e72\uff0c\u53c8\u5e2e\u6c5f\u67ab\u4e00\u4e00\u7a7f\u597d\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u4f60\u5e2e\u6211\u4e86\u671b\u554a\uff01\u6211\u4e5f\u8981\u8131\u8863\u670d\u4e86\u3002\u201d \u3000\u3000\u6c5f\u67ab\u70b9\u70b9\u5934\u3002 \u3000\u3000\u541f\u971c\u66f4\u5e72\u8106\uff0c\u4e00\u4e0b\u5b50\u5168\u8131\u5149\u4e86\uff0c\u4f46\u5979\u52a8\u4f5c\u5feb\u901f\uff0c\u5f88\u5feb\u7684\u628a\u8863\u670d\u3001\u9774\u889c\u4e0a\u7684\u79ef\u6c34\uff0c\u5904\u7406\u5b8c\u6bd5\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u6c5f\u5927\u54e5\uff0c\u5e2e\u5e2e\u5fd9\u554a\uff01\u5e2e\u6211\u7a7f\u8863\u670d\u3002\u201d \u3000\u3000\u6c5f\u67ab\u6ca1\u6709\u6cd5\u5b50\uff0c\u53ea\u597d\u52a8\u624b\u5e2e\u5fd9\u3002 \u3000\u3000\u5c0f\u72d0\u5973\u7684\u8eaf\u4f53\uff0c\u5df2\u5f88\u6210\u719f\uff0c\u5929\u72d0\u6b66\u529f\uff0c\u6709\u9a7b\u989c\u3001\u4fee\u8eab\u7684\u4f5c\u7528\uff0c\u5c0f\u4e2b\u5934\u5a07\u8eaf\u73b2\u73d1\uff0c\u51f8\u51f9\u6709\u81f4\uff0c\u6bcf\u4e00\u5904\u90fd\u5145\u6ee1\u7740\u5c11\u5973\u7684\u8bf1\u60d1\uff0c\u4e00\u53cc\u5929\u8db3\uff0c\u4e5f\u662f\u7ec6\u817b\u7ea4\u5de7\uff0c\u83b9\u767d\u5982\u738b\u3002 \u3000\u3000\u201c\u6c5f\u5927\u54e5\uff0c\u4f60\u597d\u53ef\u601c\u554a\uff01\u9752\u51e4\u3001\u6885\u82b1\u3001\u96ea\u59d0\u548c\u6211\uff0c\u90fd\u662f\u5927\u811a\u4e2b\u5b50\uff0c\u6ca1\u6709\u4e00\u4e2a\u662f\u4e09\u5bf8\u91d1\u83b2\uff0c\u6211\u8981\u548c\u5f20\u59e8\u5546\u91cf\uff0c\u7ed9\u4f60\u627e\u4e2a\u5c0f\u811a\u5973\u670b\u53cb\uff0c\u597d\u4e0d\u597d\uff1f\u201d \u3000\u3000\u201c\u541f\u971c\uff0c\u73b0\u5728\u662f\u4ec0\u4e48\u65f6\u523b\u554a\uff01\u4f60\u8fd8\u6709\u5fc3\u60c5\u8bf4\u7b11\uff1f\u201d \u3000\u3000\u201c\u82e6\u4e2d\u4f5c\u4e50\u554a\uff01\u4f55\u51b5\uff0c\u6211\u8bf4\u7684\u4e0d\u662f\u73a9\u7b11\uff0c\u6c5f\u5927\u54e5\u624d\u5b50\u98ce\u6d41\uff0c\u6ca1\u6709\u4e2a\u6807\u81f4\u7684\u5c0f\u811a\u5a18\u5b50\u4f3a\u5019\uff0c\u5c82\u4e0d\u662f\u7f8e\u4e2d\u4e0d\u8db3\u3002\u201d \u3000\u3000\u201c\u6c5f\u5927\u54e5\u4e0d\u662f\u624d\u5b50\uff0c\u4e5f\u4e0d\u98ce\u6d41\uff0c\u6709\u4f60\u4eec\u51e0\u4e2a\u5982\u82b1\u7f8e\u5987\uff0c\u65e5\u5915\u76f8\u4f34\uff0c\u6c5f\u5927\u54e5\uff0c\u65e9\u5df2\u7ecf\u5fc3\u6ee1\u610f\u8db3\uff0c\u600e\u6562\u8fd8\u6709\u5984\u5ff5\uff0c\u90a3\u5c31\u5929\u4e0d\u5bb9\u6211\u4e86\u3002\u201d \u3000\u3000\u541f\u971c\u7389\u8db3\u5fae\u6311\uff0c\u6c5f\u67ab\u5df2\u8eab\u4e0d\u7531\u5df1\u7684\u8dcc\u4e86\u4e0b\u53bb\uff0c\u6b63\u597d\u8dcc\u5728\u541f\u971c\u7684\u5a07\u8eaf\u4e0a\uff0c\u88ab\u541f\u971c\u53cc\u81c2\u4e00\u5408\uff0c\u7d27\u7d27\u7684\u62b1\u4f4f\uff0c\u6a31\u5507\u8f7b\u9001\uff0c\u5835\u4e0a\u6c5f\u67ab\u7684\u5634\u5df4\uff01 \u3000\u3000\u771f\u662f\u5927\u654c\u5728\u4fa7\uff0c\u5371\u673a\u56db\u5e03\uff0c\u541f\u971c\u8fd8\u4e0d\u5fd8\u4eab\u53d7\u8fd9\u4e00\u523b\u6e29\u5b58\u3002 \u3000\u3000\u4e00\u523b\u540e\uff0c\u541f\u971c\u633a\u8eab\u800c\u8d77\uff0c\u9053\uff1a\u201c\u8d70\uff01\u54b1\u4eec\u8fdb\u7960\u5802\u53bb\uff0c\u968f\u673a\u5e94\u53d8\uff0c\u5fc5\u8981\u65f6\uff0c\u5c31\u6740\u4eba\u6613\u88c5\uff0c\u6211\u7531\u5de6\u4fa7\u6df7\u5165\uff0c\u4f60\u4ece\u53f3\u9762\u5165\u7960\u3002\u201d \u3000\u3000\u6c5f\u67ab\u4ee5\u7edd\u4e16\u8f7b\u529f\uff0c\u98de\u63a0\u800c\u5165\uff0c\u501f\u591c\u8272\u63a9\u62a4\uff0c\u4e00\u98de\u5165\u7960\uff0c\u9690\u5165\u4e00\u5904\u5899\u89d2\u3002 \u3000\u3000\u53ea\u89c1\u5927\u5385\u4e2d\u70db\u5149\u8f89\u714c\uff0c\u56db\u4e2a\u9ed1\u8863\u5251\u58eb\uff0c\u5e03\u5b88\u5728\u5385\u95e8\u5916\u9762\u3002 \u3000\u3000\u5927\u90e8\u5206\u4eba\uff0c\u90fd\u5728\u5ead\u9662\u4e2d\u4e24\u5eca\u53a2\u623f\uff0c\u8ddd\u79bb\u4e2d\u5385\uff0c\u5c1a\u6709\u4e00\u6bb5\u8ddd\u79bb\u3002 \u3000\u3000\u53ea\u8981\u5385\u4e2d\u4eba\u58f0\u97f3\u4e0d\u662f\u592a\u5927\uff0c\u5c31\u542c\u4e0d\u5230\u5927\u5385\u4e2d\u4eba\u7684\u8bf4\u8bdd\u58f0\u97f3\u3002 \u3000\u3000\u6c5f\u67ab\u7ec6\u67e5\u5f62\u52bf\uff0c\u9664\u4e86\u95e8\u53e3\u540e\u7a97\u5904\uff0c\u5404\u6709\u5b88\u536b\u4eba\u5458\u4e4b\u5916\uff0c\u5927\u5385\u5c4b\u810a\u4e0a\uff0c\u4e5f\u6709\u4e00\u4e2a\u9ed1\u8863\u5251\u58eb\u5728\u5b88\u62a4\u3001\u76fc\u671b\u3002 \u3000\u3000\u8981\u907f\u5f00\u8fd9\u4e48\u591a\u91cd\u91cd\u76d1\u89c6\uff0c\u5b9e\u975e\u6613\u4e8b\uff0c\u4f55\u51b5\uff0c\u5385\u4e2d\u5927\u90fd\u662f\u7edd\u4e16\u9ad8\u624b\u3002 \u3000\u3000\u6c5f\u67ab\u601d\u7d22\u4e86\u826f\u4e45\uff0c\u51b3\u5b9a\u65bd\u5c55\u8c46\u7c92\u6253\u7a74\u4e4b\u6280\uff0c\u4e00\u4e3e\u5236\u670d\u95e8\u524d\u7684\u56db\u4e2a\u9ed1\u8863\u5251\u58eb\uff0c\u7136\u540e\uff0c\u518d\u6f5c\u5165\u5385\u524d\uff0c\u9690\u8eab\u5728\u5c4b\u693d\u4e4b\u4e0b\u3002 \u3000\u3000\u90a3\u662f\u4e00\u5904\uff0c\u6700\u597d\u7684\u5730\u65b9\uff0c\u53ef\u4ee5\u9690\u8eab\u542c\u5230\u5385\u4e2d\u4eba\u7684\u8c08\u8bdd\u58f0\u97f3\uff0c\u4e5f\u53ef\u4ee5\u4f38\u5934\u63a2\u671b\u4e00\u4e0b\u5385\u4e2d\u4eba\u7684\u9762\u76ee\u3002 \u3000\u3000\u51fb\u4e2d\u8fd9\u4e9b\u4eba\uff0c\u5e76\u4e0d\u592a\u96be\uff0c\u96be\u7684\u662f\u8981\u51fb\u4e2d\u4ed6\u4eec\u4e00\u5b9a\u7684\u7a74\u4f4d\uff0c\u4f7f\u4ed6\u4eec\u6682\u5931\u77e5\u89c9\uff0c\u8fd8\u4e0d\u80fd\u6454\u5012\u5730\u4e0b\uff0c\u53d1\u51fa\u58f0\u97f3\u3002 \u3000\u3000\u6c5f\u67ab\u5c3d\u91cf\u6f5c\u8fd1\u5385\u95e8\uff0c\u56db\u7c92\u7ec6\u5c0f\u7684\u6c99\u77f3\uff0c\u8131\u624b\u98de\u51fa\uff0c\u4eba\u4e5f\u540c\u65f6\u98de\u8dc3\u800c\u8d77\u3002 \u3000\u3000\u4ed6\u9009\u7684\u65f6\u673a\uff0c\u6b63\u662f\u90a3\u5c4b\u810a\u4e0a\u76d1\u671b\u654c\u4eba\uff0c\u8f6c\u5934\u4ed6\u987e\u7684\u65f6\u5019\u3002 \u3000\u3000\u6c5f\u67ab\u7684\u8eab\u6cd5\u5947\u5feb\uff0c\u4e00\u4e2a\u53d7\u51fb\u7684\u9ed1\u8863\u4eba\u7a74\u9053\u7a0d\u504f\uff0c\u4eba\u5df2\u5411\u4e0b\u5012\u53bb\uff0c\u6c5f\u67ab\u5df2\u53ca\u65f6\u800c\u81f3\uff0c\u6276\u4f4f\u4e86\u4ed6\u7684\u8eab\u5b50\uff0c\u6697\u9053\u4e86\u4e00\u58f0\u4fa5\u5e78\u3002 \u3000\u3000\u5728\u90a3\u4eba\u6655\u7a74\u4e0a\u8865\u4e86\u4e00\u6307\uff0c\u79fb\u9760\u5728\u5385\u5916\u7684\u95e8\u67f1\u4e4b\u4e0a\u3002\u4e00\u63d0\u6c14\uff0c\u5168\u8eab\u5347\u8d77\uff0c\u6a2a\u8d34\u5728\u5927\u5c4b\u693d\u4e0b\u7684\u95e8\u6963\u4e0a\u9762\u3002 \u3000\u3000\u56db\u4e2a\u9ed1\u8863\u5251\u58eb\uff0c\u4ecd\u7136\u96c4\u7ea0\u7ea0\u7684\u5b88\u5728\u5385\u95e8\u5916\u9762\uff0c\u4f46\u4ed6\u4eec\u5df2\u5931\u53bb\u77e5\u89c9\u3002 \u3000\u3000\u6c5f\u67ab\u63a2\u9996\u671b\u53bb\uff0c\u53ea\u89c1\u4e00\u4e2a\u8eab\u7740\u9ed1\u888d\uff0c\u8116\u4e0a\u56f4\u7740\u4e00\u4e2a\u5bbd\u5927\u56f4\u5dfe\u7684\u4eba\uff0c\u7aef\u5750\u5728\u4e2d\u95f4\u4e00\u5f20\u592a\u5e08\u6905\u4e0a\uff0c\u5468\u56f4\u7684\u5750\u4f4d\uff0c\u90fd\u662f\u957f\u6761\u6728\u51f3\uff0c\u4e0d\u7528\u8d39\u5fc3\u53bb\u731c\uff0c\u90a3\u4eba\u7684\u8eab\u4efd\uff0c\u5b9a\u662f\u9996\u8111\u4eba\u7269\u3002 \u3000\u3000\u53f8\u5f92\u827a\u3001\u7533\u96ea\u541b\uff0c\u4e00\u638c\u62db\u9b42\u4e07\u65b9\uff0c\u5750\u5728\u53f3\u4fa7\uff0c\u4e00\u4e2a\u987b\u7709\u7686\u767d\u7684\u9752\u886b\u8001\u8005\uff0c\u5750\u5728\u5de6\u4fa7\uff0c\u4f9d\u5e8f\u5750\u4e86\u56db\u4f4d\u8001\u4eba\u3002 \u3000\u3000\u5176\u4e2d\u4e4b\u4e00\uff0c\u662f\u5728\u957f\u5b89\u57ce\u5916\uff0c\u4e07\u6885\u576a\u52a8\u8fc7\u4e00\u6b21\u624b\u7684\u4e00\u54c1\u5200\u6881\u9b41\uff0c\u798f\u5bff\u9662\u7684\u516d\u5927\u697c\u4e3b\u4e4b\u4e00\u3002 \u3000\u3000\u770b\u4e0a\u53bb\uff0c\u516d\u5927\u697c\u4e3b\u4f3c\u4e4e\u662f\u5c11\u4e86\u4e24\u4e2a\u3002 \u3000\u3000\u53ea\u542c\u90a3\u987b\u7709\u7686\u767d\u7684\u8001\u8005\u8bf4\u9053\uff1a\u201c\u603b\u5802\u4e3b\uff0c\u6c5f\u67ab\u6b66\u529f\uff0c\u867d\u53ef\u80fd\u5c3d\u5f97\u4e09\u5723\u4e4b\u957f\uff0c\u4f46\u53d7\u5e74\u9f84\u6240\u9650\uff0c\u672a\u5fc5\u80fd\u6709\u4f20\u8a00\u4e2d\u7684\u5389\u5bb3\uff0c\u4f20\u8bf4\u561b\uff0c\u603b\u4f1a\u589e\u52a0\u51e0\u5206\u5938\u5f20\u3002\u201d \u3000\u3000\u201c\u9662\u4e3b\u2026\u2026\u201d\u4e00\u54c1\u5200\u6881\u9b41\u63a5\u9053\uff1a\u201c\u7533\u526f\u5802\u4e3b\u8bf4\u7684\u4e00\u70b9\u4e5f\u4e0d\u5938\u5f20\uff0c\u636e\u8001\u673d\u7684\u770b\u6cd5\uff0c\u8bf4\u7684\u8fd8\u6709\u70b9\u4fdd\u5b88\uff0c\u5c31\u8001\u673d\u4f30\u7b97\uff0c\u6211\u53ef\u80fd\u65e0\u6cd5\u548c\u4ed6\u6328\u8fc7\u4e94\u5341\u62db\u3002\u201d \u3000\u3000\u201c\u767d\u9662\u4e3b\uff0c\u4e0d\u8981\u4f4e\u4f30\u6c5f\u67ab\u2026\u2026\u201d\u8bf4\u8bdd\u7684\u662f\u91d1\u51e4\u51f0\u7533\u96ea\u541b\uff0c\u53ea\u542c\u5979\u53f9\u53e3\u6c14\uff0c\u63a5\u9053\uff1a\u201c\u5728\u957f\u5b89\u6d1b\u9633\u5c45\u4e2d\uff0c\u4ed6\u4e00\u4eba\u72ec\u6297\u516b\u5927\u5251\u58eb\u2026\u2026\u201d \u3000\u3000\u4f46\u95fb\u4e00\u4e2a\u51b7\u51b7\u7684\u58f0\u97f3\u63a5\u9053\uff1a\u201c\u7533\u526f\u5802\u4e3b\u5982\u80af\u548c\u516b\u5927\u5251\u58eb\u8054\u624b\uff0c\u4e5f\u8bb8\u65e9\u5df2\u9664\u6389\u6c5f\u67ab\uff0c\u4e5f\u7528\u4e0d\u7740\u603b\u5802\u4e3b\u4eb2\u7387\u6211\u7b49\uff0c\u8d76\u6765\u6b64\u5730\u4e86\u3002\u201d \u3000\u3000\u201c\u53f8\u5f92\u827a\uff0c\u4f60\u8fd9\u8bdd\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u2026\u2026\u201d\u7533\u96ea\u541b\u5fff\u6012\u5730\u8bf4\uff0c\u201c\u8ddf\u6211\u540c\u53bb\u7684\u516b\u5927\u5251\u58eb\uff0c\u90fd\u662f\u603b\u5802\u4e3b\u4eb2\u81ea\u8c03\u6559\u51fa\u7684\u5251\u624b\uff0c\u5251\u672f\u7cbe\u6e5b\uff0c\u4ed6\u4eec\u2026\u2026\u201d \u3000\u3000\u201c\u4ed6\u4eec\u8054\u624b\u5408\u51fb\uff0c\u4ecd\u672a\u80fd\u6740\u4e86\u6c5f\u67ab\uff0c\u662f\u5417\uff1f\u4f46\u5982\u7533\u526f\u5802\u65bd\u5c55\u5341\u51e4\u9f50\u98de\uff0c\u914d\u5408\u4ed6\u4eec\uff0c\u53ef\u80fd\u6c5f\u67ab\u5c38\u9aa8\u65e9\u5bd2\u4e86\uff1f\u60f3\u662f\u7533\u526f\u5802\u4e3b\u4ecd\u672a\u5fd8\u60c5\u4e8e\u5df2\u6b7b\u7684\u91d1\u4e39\u4e66\u751f\uff0c\u79fb\u7231\u6c5f\u67ab\uff0c\u653e\u4e86\u4ed6\u4e00\u9a6c\u5417\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u63a2\u5934\u671b\u53bb\uff0c\u53ea\u89c1\u90a3\u8bf4\u8bdd\u4e4b\u4eba\uff0c\u957f\u9aef\u98d8\u80f8\uff0c\u9762\u8272\u7ea2\u6da6\uff0c\u770b\u4e0a\u6709\u5982\u56db\u4e94\u5341\u5c81\u7684\u4e2d\u5e74\u4eba\u3002 \u3000\u3000\u4f46\u5929\u9a6c\u884c\u7a7a\u53f8\u5f92\u827a\u548c\u6b66\u6797\u4e09\u5723\u4e3a\u540c\u4e00\u4ee3\u4e2d\u4eba\u7269\uff0c\u5c31\u7b97\u5e74\u8f7b\u4e00\u4e9b\uff0c\u81f3\u5c11\u4e5f\u8be5\u6709\u516d\u5341\u4ee5\u4e0a\u4e86\uff0c\u770b\u4e0a\u53bb\u5982\u56db\u5341\u8bb8\u4eba\uff0c\u5176\u5185\u529f\u7684\u7cbe\u6df1\uff0c\u4f3c\u5df2\u8fbe\u7089\u706b\u7eaf\u9752\u5883\u754c\uff0c\u4e0d\u77e5\u4f55\u6545\uff0c\u5bf9\u7533\u96ea\u541b\u8854\u6068\u6781\u6df1\uff0c\u4e00\u53e3\u54ac\u4f4f\u4e0d\u653e\u3002 \u3000\u3000\u201c\u53f8\u5f92\u827a\uff0c\u4f60\u80e1\u8bf4\u516b\u9053\u4e9b\u4ec0\u4e48\uff01\u201d\u7533\u96ea\u541b\u9053\uff1a\u201c\u51b3\u6218\u5728\u4e00\u5ea7\u5927\u5385\u4e4b\u4e2d\uff0c\u73af\u5883\u6240\u9650\uff0c\u522b\u8bf4\u5341\u51e4\u9f50\u98de\u4e86\uff0c\u5c31\u662f\u4e94\u51e4\u4e5f\u98de\u4e0d\u8d77\u6765\uff0c\u4f60\u5982\u6b64\u8bec\u9677\u6211\uff0c\u7a76\u7adf\u7528\u5fc3\u4f55\u5728\uff1f\u4e3a\u4ec0\u4e48\u8fd9\u6837\u6068\u6211\uff1f\u201d \u3000\u3000\u6700\u540e\u4e00\u53e5\u8bdd\u7684\u4f4e\u5fae\u51c4\u4f24\uff0c\u4e00\u5230\u6ceb\u7136\u6b32\u6ce3\u7684\u6837\u5b50\u3002 \u3000\u3000\u4f46\u6700\u5389\u5bb3\u7684\u53cd\u51fb\uff0c\u4e5f\u5728\u8fd9\u6700\u540e\u4e00\u53e5\u8bdd\u4e86\uff0c\u914d\u5408\u4e0a\u90a3\u526f\u9eef\u7136\u795e\u4f24\u7684\u8868\u60c5\uff0c\u4e00\u4e0b\u5b50\u5c31\u642c\u56de\u4e86\u52a3\u52bf\u5730\u4f4d\uff0c\u7ed9\u4eba\u4e00\u79cd\u53f8\u5f92\u827a\u5728\u516c\u62a5\u79c1\u4ec7\u7684\u611f\u89c9\u3002 \u3000\u3000\u53ea\u542c\u90a3\u9ad8\u636e\u5927\u5e08\u6905\u7684\u9ed1\u8863\u4eba\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u7533\u526f\u5802\u4e3b\u8bf4\u7684\u4e0d\u9519\uff0c\u957f\u5b89\u5f52\u6765\u4e4b\u540e\uff0c\u5979\u5df2\u5411\u6211\u62a5\u544a\u8fc7\u8be6\u7ec6\u60c5\u5f62\uff0c\u8fd9\u4ef6\u4e8b\u5df2\u7ecf\u8fc7\u53bb\uff0c\u4e0d\u8bb8\u518d\u63d0\u3002\u201d \u3000\u3000\u201c\u603b\u5802\u4e3b\u2026\u2026\u201d\u90a3\u987b\u7709\u7686\u767d\u7684\u8001\u8005\u63a5\u9053\uff1a\u201c\u5c31\u767d\u67d0\u542c\u5230\u7684\u6d88\u606f\uff0c\u6c5f\u67ab\u767e\u65e5\u4e4b\u524d\uff0c\u5728\u5f00\u5c01\u5e9c\uff0c\u591c\u632b\u56db\u5927\u795e\u901a\u4f7f\u8005\u7684\u4e1c\u65b9\u4f7f\u8005\uff0c\u8fd9\u624d\u662f\u6559\u4e3b\u4e0b\u4ee4\u603b\u5802\u4e3b\u4eb2\u81ea\u7763\u9635\uff0c\u56f4\u6740\u6c5f\u67ab\u7684\u4e3b\u8981\u539f\u56e0\u3002\u201d \u3000\u3000\u5bbd\u5927\u7684\u56f4\u5dfe\uff0c\u906e\u4f4f\u4e86\u9ed1\u8863\u4eba\u773c\u775b\u4ee5\u4e0b\u7684\u90e8\u5206\uff0c\u770b\u4e0d\u6e05\u90a3\u9ed1\u8863\u4eba\u9762\u76ee\uff0c\u4f46\u4ed6\u4e00\u5bf9\u706b\u7ea2\u7684\u773c\u775b\uff0c\u5374\u4f7f\u4eba\u4e00\u89c1\u96be\u5fd8\u3002 \u3000\u3000\u6c5f\u67ab\u5fc3\u4e2d\u660e\u767d\uff0c\u90a3\u4e0d\u662f\u5929\u7136\u751f\u6210\uff0c\u800c\u662f\u4e00\u79cd\u90aa\u6076\u6b66\u529f\uff0c\u767b\u5802\u5165\u5ba4\u540e\u7684\u8868\u5f81\uff0c\u719f\u8bfb\u8fc7\u4f20\u771f\u8bb0\u540e\uff0c\u6c5f\u67ab\u5bf9\u5929\u4e0b\u5404\u5bb6\u6b66\u529f\uff0c\u5df2\u6709\u4e86\u4e00\u79cd\u660e\u786e\u7684\u6982\u5ff5\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u88ab\u79f0\u4e3a\u603b\u5802\u4e3b\uff0c\u5b9a\u7136\u662f\u590f\u5929\u540c\u4e86\uff0c\u4f46\u6c5f\u67ab\u60f3\u4e0d\u901a\uff0c\u5728\u5982\u6b64\u7684\u73af\u5883\u4e2d\uff0c\u4ed6\u4e3a\u4ec0\u4e48\u8fd8\u8981\u63a9\u63a9\u906e\u906e\uff0c\u6545\u5f04\u7384\u865a\u3002 \u3000\u3000\u201c\u767d\u9662\u4e3b\u2026\u2026\u201d\u590f\u5929\u540c\u5bf9\u90a3\u8001\u8005\u8868\u73b0\u51fa\u76f8\u5f53\u7684\u5c0a\u91cd\uff0c\u9053\uff0c\u201c\u4f60\u53f7\u79f0\u9634\u9633\u79c0\u624d\uff0c\u7cbe\u80fd\u672f\u6570\u661f\u535c\uff0c\u4f60\u8bf4\u8bf4\u770b\uff0c\u56db\u5927\u795e\u901a\u4f7f\u8005\u7684\u6b66\u529f\uff0c\u6bd4\u4f60\u5929\u5316\u5144\u7684\u6b66\u529f\u5982\u4f55\uff1f\u201d \u3000\u3000\u767d\u5929\u5316\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u56db\u5927\u795e\u901a\u4f7f\u8005\uff0c\u6280\u827a\u8be1\u5bc6\u9634\u67d4\uff0c\u4ee5\u5c0f\u897f\u5929\u96f7\u97f3\u5bfa\u7684\u9b54\u6559\u6b66\u529f\u4e3a\u4e3b\uff0c\u4f30\u7b97\u51fa\u4ed6\u4eec\u6709\u591a\u5c11\u5b9e\u529b\uff0c\u5e76\u975e\u6613\u4e8b\u3002 \u3000\u3000\u53ef\u60dc\uff0c\u8001\u673d\u672a\u89c1\u90a3\u591c\u4e00\u6218\uff0c\u5982\u80fd\u76ee\u7779\u6c5f\u67ab\u548c\u4e1c\u65b9\u4f7f\u8005\u4e4b\u6218\uff0c\u4e5f\u8bb8\u80fd\u627e\u51fa\u4e00\u4e2a\u8ba1\u7b97\u7684\u65b9\u6cd5\uff0c\u8861\u91cf\u51fa\u4ed6\u4eec\u7684\u6b66\u529f\uff0c\u4f5c\u4e00\u5b9a\u8bba\u3002\u201d \u3000\u3000\u201c\u6211\u628a\u8bf8\u4f4d\u7ea6\u96c6\u4e8e\u6b64\uff0c\u662f\u5e0c\u671b\u501f\u91cd\u8fd9\u91cc\u7684\u5730\u7406\u5f62\u52bf\uff0c\u8ba9\u4eba\u65e0\u6cd5\u63a5\u8fd1\u7a83\u542c\uff0c\u4f7f\u6211\u4eec\u80fd\u591f\u7545\u6240\u6b32\u8a00\uff0c\u628a\u5fc3\u4e2d\u7684\u5757\u5792\uff0c\u5c3d\u60c5\u5410\u9732\u2026\u2026\u201d\u590f\u5929\u540c\u9053\uff0c\u201c\u6211\u76f8\u4fe1\u5929\u9a6c\u603b\u5802\u4e2d\uff0c\u65e9\u5df2\u6709\u4e86\u6f5c\u4f0f\u7684\u5bc6\u63a2\u3001\u5978\u7ec6\uff0c\u4f46\u53c8\u4e0d\u4fbf\u653e\u624b\u6e05\u9664\uff0c\u8bf8\u4f4d\u662f\u6211\u590f\u5929\u540c\u7684\u4f19\u4f34\uff0c\u4e5f\u662f\u5929\u9a6c\u5802\u4e2d\u7684\u4e3b\u529b\u7cbe\u9510\uff0c\u6211\u4eec\u5982\u4f55\u624d\u80fd\u81ea\u4fdd\uff0c\u5982\u4f55\u624d\u80fd\u9002\u5e94\u5f53\u524d\u5927\u5c40\uff0c\u662f\u6211\u4eec\u4eca\u591c\u8ba8\u8bba\u7684\u91cd\u8981\u4e8b\u60c5\uff0c\u5404\u4f4d\u5fc3\u4e2d\u6709\u8bdd\uff0c\u5c3d\u7ba1\u8bf7\u8bf4\uff0c\u8bf4\u9519\u4e86\u4e5f\u4e0d\u8981\u7d27\uff0c\u6211\u4eec\u518d\u7814\u5546\u51b3\u5b9a\u3002\u201d \u3000\u3000\u6c5f\u67ab\u5fc3\u4e2d\u4e00\u52a8\uff0c\u5fd6\u9053\uff1a\u201c\u539f\u6765\u5929\u9a6c\u5802\u662f\u5982\u6b64\u7684\u4e00\u4e2a\u5904\u5883\u3002\u5012\u8981\u4ed4\u7ec6\u7684\u542c\u542c\uff0c\u8fd9\u4e2a\u795e\u79d8\u7684\u7ec4\u5408\uff0c\u7a76\u7adf\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u5e0c\u671b\u541f\u971c\u80fd\u8010\u7740\u6027\u5b50\uff0c\u4e0d\u8981\u9732\u51fa\u7834\u7efd\u624d\u597d\u3002\u201d \u3000\u3000\u201c\u603b\u5802\u4e3b\uff0c\u4e5f\u5df2\u7ecf\u53d1\u89c9\u4e86\u6211\u4eec\u5904\u5883\u7684\u5371\u673a\u4e86\uff1f\u201d\u767d\u5929\u5316\u9053\uff1a\u201c\u8fd9\u4e00\u6b21\u53d7\u547d\u56f4\u6740\u6c5f\u67ab\uff0c\u4e5f\u6b63\u662f\u6559\u4e3b\u7684\u4e00\u77f3\u4e8c\u9e1f\u4e4b\u8ba1\uff0c\u8981\u6211\u4eec\u4e24\u8d25\u4ff1\u4f24\uff01 \u3000\u3000\u56e0\u4e3a\uff0c\u6c5f\u67ab\u8fd9\u4e00\u6279\u4eba\u624b\uff0c\u5df2\u786e\u5b9a\u4e3a\u4e2d\u539f\u6b66\u6797\u4e4b\u4e2d\uff0c\u6700\u540e\u4e00\u80a1\u53cd\u6297\u7684\u529b\u91cf\u4e86\u3002\u201d \u3000\u3000\u201c\u5bf9\uff01\u6d88\u706d\u4e86\u6c5f\u67ab\u8fd9\u4e00\u6279\u4eba\uff0c\u4e2d\u539f\u6b66\u6797\u4e2d\u529b\u91cf\uff0c\u53ea\u6709\u6211\u4eec\u5929\u9a6c\u5802\u8fd9\u4e9b\u4eba\u4e86\u2026\u2026\u201d\u590f\u5929\u540c\u9053\uff1a\u201c\u4e5f\u5c06\u662f\u5bf9\u4ed8\u6211\u4eec\u7684\u6700\u597d\u65f6\u673a\u2026\u2026\u201d \u3000\u3000\u76ee\u5149\u8f6c\u6ce8\u5230\u53f8\u5f92\u827a\u7684\u8138\u4e0a\uff0c\u53cc\u76ee\u4e2d\uff0c\u95ee\u8d77\u4e86\u4e00\u7247\u8273\u4e3d\u7684\u7ea2\u5149\uff0c\u63a5\u9053\uff1a\u201c\u53f8\u5f92\u603b\u5802\u4e3b\uff0c\u5bf9\u6b64\u4e8b\u6709\u4f55\u9ad8\u89c1\uff1f\u672c\u5802\u4e3b\u5e0c\u671b\u4e00\u8046\u96c5\u6559\u3002\u201d \u3000\u3000\u8bdd\u8bf4\u7684\u592a\u5ba2\u6c14\uff0c\u6f5c\u9690\u7740\u4e00\u80a1\u6d53\u70c8\u7684\u706b\u836f\u6c14\u606f\u3002 \u3000\u3000\u53f8\u5f92\u827a\u5012\u662f\u795e\u60c5\u5e73\u9759\u7684\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5929\u540e\u5bab\u4e2d\u5f1f\u5b50\uff0c\u591a\u65b9\u4fb5\u72af\u672c\u5802\u63a7\u5236\u7684\u5730\u76d8\uff0c\u4f3c\u5df2\u5b58\u5fc3\u5f15\u53d1\u4e89\u6267\uff0c\u603b\u5802\u4e3b\u5747\u80fd\u9690\u5fcd\u4e0d\u53d1\uff0c\u5c5e\u4e0b\u4e5f\u53ea\u6709\u5c48\u4ece\u4e0a\u610f\uff0c\u4e0b\u4ee4\u5404\u5730\u5206\u8235\uff0c\u767e\u822c\u56de\u907f\uff0c\u9006\u6765\u987a\u53d7\uff0c\u6559\u4e3b\u548c\u603b\u5802\u4e3b\u4e4b\u95f4\u7a76\u7adf\u662f\u5426\u53e6\u6709\u5bc6\u7ea6\uff0c\u5c5e\u4e0b\u7b49\u5e76\u4e0d\u6e05\u695a\uff0c\u4e5f\u5c31\u4e0d\u6562\u4e71\u51fa\u4e3b\u610f\u4e86\uff0c\u6211\u5e0c\u671b\u80fd\u771f\u6b63\u4e86\u89e3\u603b\u5802\u4e3b\u7684\u5fc3\u610f\uff0c\u624d\u80fd\u8c03\u6574\u90e8\u7f72\u3002\u4fee\u8ba2\u5e94\u53d8\u8c0b\u7565\u3002\u201d \u3000\u3000\u542c\u8d77\u6765\uff0c\u4e00\u7247\u5fe0\u8bda\uff0c\u552f\u547d\u662f\u4ece\uff0c\u4f46\u5374\u5df2\u53cd\u5ba2\u4e3a\u4e3b\uff0c\u5012\u903c\u603b\u5802\u4e3b\u660e\u767d\u7684\u8bf4\u51fa\u5fc3\u4e2d\u7684\u7b56\u7565\u8ba1\u5212\u3002 \u3000\u3000\u590f\u5929\u540c\u53cc\u76ee\u4e2d\u7684\u7ea2\u5149\uff0c\u66f4\u89c1\u70bd\u70c8\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u53f8\u5f92\u5144\u4e00\u5e74\u524d\u66fe\u8499\u6559\u4e3b\u53ec\u89c1\uff0c\u7559\u5c45\u897f\u57df\u4e09\u6708\u4e4b\u4e45\u3002\u201d \u3000\u3000\u201c\u603b\u5802\u4e3b\u2026\u2026\u201d\u53f8\u5f92\u827a\u8138\u8272\u5fae\u53d8\uff0c\u63a5\u9053\uff1a\u201c\u5c5e\u4e0b\u4e1c\u8fd4\u4e4b\u540e\uff0c\u5df2\u7ecf\u5411\u603b\u5802\u4e3b\u62a5\u544a\u8fc7\u5168\u90e8\u7ecf\u8fc7\uff0c\u56e0\u4e3a\u5c5e\u4e0b\u7cbe\u901a\u9a6f\u9a6c\u4e4b\u672f\uff0c\u88ab\u7559\u5c45\u897f\u57df\u6570\u6708\uff0c\u662f\u5b66\u4e60\u6307\u6325\u9e70\u3001\u72ac\u8ffd\u8e2a\u7684\u65b9\u6cd5\uff0c\u90a3\u5730\u65b9\u5728\u5929\u5c71\u4e00\u5ea7\u5ce1\u53e3\u4e4b\u4e2d\uff0c\u9664\u4e86\u548c\u4e09\u4e2a\u9a6f\u7ec3\u9e70\u3001\u72ac\u7684\u4eba\u63a5\u89e6\u8fc7\u4e4b\u5916\uff0c\u5e76\u672a\u89c1\u8fc7\u6559\u4e3b\u3002\u201d \u3000\u3000\u201c\u5f53\u771f\u662f\u96be\u4ee5\u8ba9\u4eba\u4fe1\u670d\u3002\u2026\u2026\u201d\u590f\u5929\u540c\u7684\u76ee\u5149\u8f6c\u6ce8\u5230\u767d\u5929\u5316\u7684\u8eab\u4e0a\uff0c\u9053\uff1a\u201c\u767d\u5144\u4ee5\u4e3a\u5982\u4f55\u5462\uff1f\u201d \u3000\u3000\u767d\u5929\u5316\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u53cd\u95f4\u9053\uff1a\u201c\u603b\u5802\u4e3b\u89c1\u8fc7\u6559\u4e3b\u4e86\uff1f\u201d \u3000\u3000\u201c\u89c1\u8fc7\u4e09\u6b21\uff0c\u4f46\u4e09\u6b21\u7684\u4eba\u90fd\u4e0d\u540c\uff0c\u4ed6\u4eec\u672a\u6613\u5bb9\uff0c\u4e5f\u4e0d\u8499\u9762\uff0c\u201d \u3000\u3000\u590f\u5929\u540c\u9053\uff1a\u201c\u5c31\u662f\u4e09\u4e2a\u5b8c\u5168\u4e0d\u540c\u7684\u4eba\uff0c\u5f53\u65f6\uff0c\u53ea\u662f\u89c9\u7740\u5947\u602a\uff0c\u4f46\u4e8b\u540e\u60f3\u6765\uff0c\u8fd9\u7b49\u7b80\u5355\u7684\u65b9\u6cd5\uff0c\u5374\u53eb\u4eba\u65e0\u4ece\u6349\u6478\uff0c\u4e09\u4e2a\u4eba\uff0c\u53ef\u80fd\u90fd\u4e0d\u662f\u6559\u4e3b\u3002\u53ea\u662f\u7528\u4e0d\u540c\u7684\u4eba\u4f20\u8fbe\u4e00\u4e2a\u8baf\u606f\uff0c\u8981\u6211\u9075\u7167\u529e\u7406\uff0c\u7f9a\u7f8a\u6302\u89d2\uff0c\u4e0d\u7740\u75d5\u8ff9\uff0c\u5b9e\u662f\u8d8a\u60f3\u8d8a\u53ef\u6015\u4e86\uff01\u201d \u3000\u3000\u201c\u53f8\u5f92\u5144\uff0c\u4e00\u5411\u7cbe\u7ec6\uff0c\u800c\u4e14\uff0c\u6709\u4e09\u4e2a\u6708\u7684\u65f6\u95f4\uff0c\u548c\u4e09\u4e2a\u8bad\u7ec3\u9e70\u3001\u72ac\u7684\u4eba\u63a5\u89e6\u2026\u2026\u201d\u767d\u5929\u5316\u9053\uff1a\u201c\u53ef\u66fe\u5728\u4e09\u4eba\u8eab\u4e0a\u53d1\u73b0\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u6211\u4ed4\u7ec6\u7684\u89c2\u5bdf\u8fc7\uff0c\u90a3\u53ea\u662f\u4e09\u4e2a\u8bad\u7ec3\u9e70\u3001\u72ac\u7684\u5e08\u5085\uff0c\u5982\u8bba\u6b66\u529f\uff0c\u4e09\u4e2a\u4eba\u5408\u8d77\u6765\uff0c\u4e5f\u63a5\u4e0d\u4e0b\u6211\u5341\u62db\u201d\u53f8\u5f92\u827a\u9053\uff1a\u201c\u4ed6\u4eec\u9664\u4e86\u4f20\u6211\u6307\u6325\u9e70\u3001\u72ac\u7684\u65b9\u6cd5\u4e4b\u5916\uff0c\u5f88\u5c11\u548c\u6211\u8bf4\u8bdd\uff0c\u6211\u4e5f\u66fe\u591a\u65b9\u8bf1\u4f7f\u4ed6\u4eec\u5f00\u53e3\uff0c\u4f46\u4ed6\u4eec\u90fd\u56de\u907f\u4e0d\u7406\uff0c\u8001\u5b9e\u8bf4\uff0c\u8fde\u5f79\u65bd\u9e70\u3001\u72ac\u7684\u65b9\u6cd5\uff0c\u4e5f\u7559\u4e86\u4e00\u624b\uff0c\u53ea\u6559\u6211\u5982\u4f55\u63a5\u53d7\u5b83\u4eec\u4f20\u9012\u7684\u8baf\u606f\u3002 \u3000\u3000\u4f46\u4ed6\u4eec\u4f4e\u4f30\u4e86\u6211\uff0c\u9a6f\u9a6c\u7ec3\u72ac\uff0c\u5927\u540c\u5c0f\u5f02\uff0c\u53ea\u8981\u4ed6\u4eec\u7a0d\u4f5c\u6307\u70b9\uff0c\u6211\u5df2\u4e86\u7136\u4e8e\u5fc3\uff0c\u6211\u6545\u610f\u62d6\u5ef6\u5b66\u4e60\u7684\u65f6\u95f4\uff0c\u5c31\u662f\u5e0c\u671b\u7531\u4ed6\u4eec\u8eab\u4e0a\u67e5\u51fa\u4e00\u4e9b\u53ef\u7591\u5f62\u8ff9\u3002 \u3000\u3000\u62d6\u5ef6\u4e86\u4e24\u4e2a\u591a\u6708\uff0c\u4ecd\u662f\u4e00\u65e0\u6240\u83b7\uff0c\u6211\u5fc3\u4e2d\u6709\u4e9b\u706b\u4e86\uff0c\u5c31\u5192\u9669\u6d4b\u9a8c\u4e00\u4e0b\u4ed6\u4eec\u7684\u6b66\u529f\uff0c\u4e09\u4e2a\u4eba\u90fd\u88ab\u6211\u63a8\u649e\u5012\u5730\uff0c\u4e00\u4e2a\u8fd8\u53d7\u4e86\u4e0d\u8f7b\u7684\u4f24\uff0c\u5982\u4e0d\u662f\u5fc3\u5b58\u987e\u5fcc\uff0c\u6211\u65e9\u5c31\u65bd\u5c55\u624b\u6bb5\u903c\u4f9b\u4e86\uff0c\u8fd9\u4e9b\u4e8b\u90fd\u5df2\u5411\u603b\u5802\u4e3b\u8bc4\u4f5c\u62a5\u544a\u3002\u201d \u3000\u3000\u8bdd\u5230\u6b64\u5904\uff0c\u7a81\u7136\u4f4f\u53e3\u3002 \u3000\u3000\u7960\u5802\u5927\u5385\u4e2d\u7a81\u7136\u6c89\u9759\u4e0b\u6765\uff0c\u826f\u4e45\u4e4b\u540e\uff0c\u767d\u5929\u5316\u624d\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u53f8\u5f92\u5144\uff0c\u4ed6\u4eec\u662f\u4e0d\u662f\u4e2d\u539f\u4eba\u6c0f\uff1f\u201d \u3000\u3000\u201c\u770b\u4e0d\u51fa\u5dee\u5f02\uff0c\u4e00\u53e3\u6c49\u8bed\uff0c\u5341\u5206\u6d41\u5229\u2026\u2026\u201d\u53f8\u5f92\u827a\u8bf4\u3002 \u3000\u3000\u201c\u603b\u5802\u4e3b\u89c1\u8fc7\u4e09\u4e2a\u4e0d\u540c\u7684\u6559\u4e3b\u66ff\u8eab\uff0c\u4ed6\u4eec\u53ef\u6709\u5f02\u5f81\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\uff0c\u90fd\u662f\u6c49\u4eba\u3002\u2026\u2026\u201d\u590f\u5929\u540c\u6253\u65ad\u767d\u5929\u5316\u7684\u8bdd\uff0c\u63a5\u9053\uff1a\u201c\u77a7\u4e0d\u51fa\u5916\u65cf\u5f02\u5f81\uff0c\u6211\u5fc3\u4e2d\u72af\u7591\uff0c\u4ed4\u7ec6\u7684\u67e5\u8fc7\u6700\u540e\u4e00\u4e2a\u6559\u4e3b\u7684\u66ff\u8eab\uff1f\u4e5f\u4e0d\u89c1\u4ed6\u6709\u4ec0\u4e48\u7cbe\u6df1\u6b66\u529f\u620c\u5c31\uff0c\u53ea\u662f\u4e2a\u666e\u901a\u7684\u4eba\u3002\u201d \u3000\u3000\u201c\u8fd9\u771f\u662f\u7cbe\u7edd\u65e0\u6bd4\u7684\u7b56\u7565\uff0c\u968f\u4fbf\u627e\u4e00\u4e2a\u4eba\uff0c\u751a\u81f3\u4e0d\u4f1a\u6b66\u529f\uff0c\u2026\u2026\u201d\u767d\u5929\u5316\u9053\uff1a\u201c\u6211\u4eec\u5c31\u5168\u65e0\u8ff9\u8c61\u53ef\u5bfb\u4e86\u3002\u201d \u3000\u3000\u201c\u53ef\u60b2\u7684\u662f\uff0c\u6211\u4eec\u88ab\u9890\u6307\u6c14\u4f7f\u4e86\u5341\u51e0\u5e74\uff0c\u7adf\u7136\u4e0d\u77e5\u9053\u662f\u8c01\uff1f \u3000\u3000\u2026\u2026\u201d\u590f\u5929\u540c\u9053\uff1a\u201c\u6211\u4e0d\u6015\u654c\u4eba\u6709\u5978\u7ec6\u6f5c\u4f0f\uff0c\u65bd\u5c55\u8680\u5fc3\u9b54\u97f3\uff0c\u4f7f\u4ed6\u5fc3\u667a\u53d7\u5236\uff0c\u5c31\u4e3a\u6211\u6240\u7528\uff0c\u60f3\u4e0d\u5230\u5374\u56e0\u6b64\u81ea\u8bef\uff0c\u53cd\u800c\u65e0\u6cd5\u67e5\u51fa\u6f5c\u4f0f\u7684\u5978\u7ec6\uff0c\u80fd\u6297\u62d2\u6211\u9b54\u97f3\u63a7\u5236\u7684\u5978\u7ec6\uff0c\u81ea\u4f1a\u66f4\u52a0\u5c0f\u5fc3\uff0c\u6f5c\u9690\u66f4\u6df1\u4e86\uff0c\u6709\u6b64\u4e00\u609f\uff0c\u6211\u624d\u6539\u53d8\u65b9\u6cd5\uff0c\u5ba3\u5e03\u95ed\u5173\u767e\u65e5\uff0c\u4e0b\u4ee4\u4e0d\u5f97\u548c\u5929\u738b\u95e8\u3001\u5929\u540e\u5bab\u4e2d\u5f1f\u4e8e\u51b2\u7a81\uff0c\u5b9e\u5219\u4eb2\u81ea\u6697\u4e2d\u67e5\u770b\uff0c\u7ec8\u4e8e\u53d1\u89c9\u4e86\u8d35\u9662\u4e2d\u516d\u5927\u697c\u4e3b\u6709\u4e24\u4f4d\u548c\u5929\u540e\u5bab\u6697\u4e2d\u52fe\u7ed3\u751a\u6df1\u201d \u3000\u3000\u201c\u603b\u5802\u4e3b\u53ef\u5df2\u5904\u7f6e\u4e86\u4ed6\u4eec\uff1f\u201d\u767d\u5929\u5316\u63a5\u9053\uff1a\u201c\u6211\u4e5f\u67e5\u89c9\u5230\u4ed6\u4eec\u4e24\u4e2a\u884c\u52a8\u6709\u5f02\uff0c\u53ea\u9053\u662f\u4ed6\u4eec\u4e0d\u5b89\u4e8e\u5ba4\uff0c\u51fa\u5165\u6c5f\u6e56\u95f2\u901b\u3001\u6e38\u8361\uff0c\u627e\u627e\u4e50\u5b50\uff0c\u798f\u5bff\u9662\u6bd5\u7adf\u53ea\u662f\u4e2a\u540d\u4f4d\u5d07\u9ad8\uff0c\u4e0d\u95ee\u5b9e\u52a1\u7684\u6240\u5728\uff0c\u4ee5\u5b89\u4eab\u4f59\u5e74\u4e3a\u4e3b\uff0c\u53ea\u8981\u51c6\u65f6\u51fa\u5e2d\u6bcf\u5e74\u4e00\u6b21\u7684\u7814\u4e60\u6b66\u529f\u5927\u4f1a\uff0c\u4ea4\u51fa\u6280\u827a\u5fc3\u5f97\uff0c\u5e73\u5e38\u6211\u4e5f\u5f88\u5c11\u8fc7\u95ee\u4ed6\u4eec\u7684\u751f\u6d3b\u60c5\u5f62\u3002\u201d \u3000\u3000\u6c5f\u67ab\u5fc3\u4e2d\u4e00\u52a8\uff0c\u5fd6\u9053\uff1a\u201c\u8fd9\u5012\u662f\u4e00\u4e2a\u53ef\u5fc3\u8ba9\u5404\u95e8\u6b66\u529f\u6d41\u4f20\u7684\u597d\u529e\u6cd5\uff0c\u5982\u6709\u8fd9\u6837\u4e00\u4e2a\u7ec4\u5408\uff0c\u4f7f\u5176\u5c55\u51fa\u7edd\u6280\uff0c\u80fd\u591f\u4f20\u8bf8\u540e\u4e16\uff0c\u800c\u4e14\u66f4\u4e3a\u7cbe\u8fdb\uff0c\u53ea\u662f\u6c5f\u6e56\u4e0a\u95e8\u6d3e\u5206\u7acb\uff0c\u5404\u5b58\u79c1\u5fc3\uff0c\u8fd9\u529e\u6cd5\uff0c\u80fd\u5426\u884c\u5f97\u901a\u5462\uff1f\u201d \u3000\u3000\u201c\u6211\u6ca1\u6709\u5904\u7f6e\u4ed6\u4eec\u3002\u4e8b\u5b9e\u4e0a\u6211\u5df2\u53d1\u89c9\u4e86\uff0c\u4ed6\u4eec\u65e9\u6709\u6212\u5907\uff0c\u2026\u2026\u201d \u3000\u3000\u590f\u5929\u540c\u9053\uff1a\u201c\u4ed6\u4eec\u4e0d\u4f1a\u675f\u624b\u5c31\u7f1a\uff0c\u60e9\u5904\u4ed6\u4eec\uff0c\u5fc5\u987b\u8981\u653e\u624b\u4e00\u6218\uff0c\u90a3\u5c06\u4f1a\u9707\u52a8\u5929\u9a6c\u5802\uff0c\u6211\u8fd8\u672a\u53ca\u548c\u81ea\u5144\u5546\u91cf\uff0c\u53f8\u5f92\u827a\u5df2\u8f6c\u4e0a\u6559\u4e3b\u91d1\u5370\u624b\u4e66\u3002\u8981\u5929\u9a6c\u5802\u5c3d\u51fa\u5168\u529b\uff0c\u56f4\u6740\u6c5f\u67ab\uff0c\u6307\u660e\u7531\u6211\u7edf\u9886\u51fa\u5e08\uff0c\u5e76\u8bf4\u660e\u4e86\u5929\u4e3b\u95e8\u3001\u5929\u540e\u5bab\uff0c\u7531\u95e8\u4e3b\u3001\u5bab\u4e3b\u4eb2\u7387\u7cbe\u9510\u63a5\u5e94\uff0c\u6559\u4e3b\u4ea6\u5c06\u8fd1\u65e5\u5185\u4eb2\u7387\u56db\u5927\u4f7f\u8005\uff0c\u5168\u4f53\u62a4\u6cd5\uff0c\u8d76\u6765\u52a9\u9635\u3002\u201d \u3000\u3000\u201c\u98de\u9e70\u4f20\u6765\u8baf\u606f\uff0c\u5929\u738b\u95e8\u3001\u5929\u540e\u5bab\uff0c\u786e\u5df2\u7531\u5929\u738b\u3001\u5bab\u4e3b\uff0c\u4eb2\u7edf\u9ad8\u624b\u8d76\u6765\uff0c\u4e0d\u8fc7\u2026\u2026\u201d\u53f8\u5f92\u827a\u8bf4\uff0c\u201c\u5c5e\u4e0b\u9057\u51fa\u7684\u4fa6\u9a91\u5bc6\u63a2\uff0c\u8fd8\u672a\u89c1\u56de\u62a5\u3002\u201d \u3000\u3000\u201c\u5982\u679c\u6211\u7684\u63a8\u60f3\u4e0d\u9519\uff0c\u201d\u7533\u96ea\u541b\u8bf4\uff0c\u201c\u6211\u4eec\u7684\u5bc6\u63a2\u4fa6\u9a91\uff0c\u6050\u4e5f\u5728\u98de\u9e70\u7075\u72ac\u7684\u76d1\u89c6\u4e4b\u4e0b\u4e86\u3002\u201d \u3000\u3000\u53f8\u5f92\u827a\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u56db\u5341\u5339\u5ea6\u5371\u5929\u9a6c\uff0c\u90fd\u6709\u65e5\u884c\u56db\u767e\u91cc\u4ee5\u4e0a\u7684\u811a\u7a0b\uff0c\u5de1\u884c\u65b9\u5706\u4e94\u5341\u91cc\uff0c\u4e0d\u89c1\u56de\u62a5\uff0c\u53ef\u80fd\u662f\u5929\u738b\u3001\u5929\u540e\u4e24\u8def\u4eba\u9a6c\uff0c\u90fd\u5728\u98de\u9e70\u76d1\u5de1\u4f20\u8baf\u4e2d\uff0c\u6709\u610f\u89c4\u907f\u3002\u201d \u3000\u3000\u201c\u4e5f\u53ef\u80fd\u88ab\u4ed6\u4eec\u56f0\u9677\u3001\u730e\u6740\u4e86\uff0c\u2026\u2026\u201d\u590f\u5929\u540c\u9053\uff1a\u201c\u6c5f\u67ab\u7b49\u4e00\u6279\u4eba\u7684\u884c\u8e2a\u5462\uff1f\u201d \u3000\u3000\u201c\u4ecd\u7136\u9690\u4f0f\u5728\u864e\u5cad\u5c71\u4e00\u7247\u8349\u539f\u4e4b\u4e2d\uff0c\u2026\u2026\u201d\u53f8\u5f92\u827a\u9053\uff1a\u201c\u76ee\u4e0b\u60c5\u52bf\uff0c\u5df2\u5230\u4e86\u53cc\u65b9\u5bf9\u51b3\u7684\u65f6\u523b\uff0c\u6211\u4eec\u4e00\u65e6\u8fdb\u5165\u8349\u539f\uff0c\u5f88\u53ef\u80fd\u548c\u6c5f\u67ab\u90a3\u6279\u4eba\u63a5\u624b\u6076\u6218\uff0c\u4ed6\u4eec\u4eba\u6570\u4e0d\u591a\uff0c\u663e\u7136\u662f\u51c6\u5907\u5229\u7528\u6df1\u8349\u8346\u4e1b\uff0c\u5e03\u5c40\u4f0f\u6740\uff0c\u4e00\u63a5\u624b\uff0c\u5c31\u53ef\u80fd\u6709\u5f88\u5927\u4f24\u4ea1\uff0c\u603b\u5802\u4e3b\u5fc3\u4e2d\u5982\u4f55\u6253\u7b97\uff0c\u5fc5\u5f97\u65e9\u4f5c\u51b3\u5b9a\u4e86\u3002\u201d \u3000\u3000\u539f\u6765\uff0c\u5929\u9a6c\u884c\u7a7a\u53f8\u5f92\u827a\uff0c\u6267\u638c\u7740\u5929\u9a6c\u5802\u5175\u7b26\u5927\u6743\uff0c\u624b\u63e1\u5b9e\u529b\uff0c\u5bf9\u603b\u5802\u4e3b\u590f\u5929\u540c\u656c\u800c\u4e0d\u754f\u3002 \u3000\u3000\u590f\u5929\u540c\u547c\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5df2\u5230\u5fc5\u987b\u51b3\u5b9a\u7684\u5173\u5934\uff0c\u53f8\u5f92\u526f\u603b\u5802\u4e3b\u7684\u770b\u6cd5\u5462\uff1f\u201d \u3000\u3000\u201c\u552f\u603b\u5802\u4e3b\u7684\u9a6c\u9996\u662f\u77bb\uff0c\u2026\u2026\u201d\u53f8\u5f92\u827a\u9053\uff1a\u201c\u5c5e\u4e0b\u4e00\u7247\u5fe0\u8bda\uff0c\u5e0c\u671b\u603b\u5802\u4e3b\u4e0d\u8981\u591a\u7591\u3002\u4f46\u5f97\u4e00\u58f0\u4ee4\u4e0b\uff0c\u5c5e\u4e0b\u5f53\u4e3a\u5148\u950b\u3002\u201d \u3000\u3000\u590f\u5929\u540c\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u53f8\u5f92\u89c1\u4e00\u8a00\u91ca\u7591\uff0c\u4f7f\u6211\u5fc3\u80f8\u5927\u5bbd\u2026\u2026\u201d\u8f6c\u671b\u7740\u767d\u5929\u5316\uff0c\u9053\uff1a\u201c\u767d\u5144\uff0c\u7d20\u591a\u8ba1\u8c0b\uff0c\u4e0d\u77e5\u6709\u4f55\u9ad8\u89c1\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u5fc3\u4e2d\u5fd6\u9053\uff1a\u201c\u590f\u5929\u540c\u4e0d\u80af\u72ec\u65ad\u4e13\u884c\uff0c\u4f3c\u5728\u6536\u63fd\u4eba\u5fc3\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e9b\u5e74\u517b\u5c0a\u5904\u4f18\uff0c\u5f88\u5c11\u82b1\u8d39\u5fc3\u601d\uff0c\u6881\u697c\u4e3b\uff0c\u4f60\u597d\u50cf\u8bf4\u8fc7\uff0c\u2026\u2026\u201d \u3000\u3000\u767d\u5929\u5316\u9053\uff1a\u201c\u5929\u865a\u5b50\u88ab\u9152\u8089\u548c\u5c1a\u4e00\u62f3\u5c31\u6253\u6b7b\u4e86\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\uff01\u201d\u4e00\u54c1\u5200\u6881\u9b41\u63a5\u9053\uff1a\u201c\u5929\u865a\u5b50\u526f\u603b\u5802\u4e3b\uff0c\u867d\u7136\u662f\u6709\u4e9b\u8f7b\u654c\u5927\u610f\uff0c\u4f46\u90a3\u9152\u8089\u548c\u5c1a\u80fd\u4e00\u62f3\u53d6\u547d\uff0c\u6b66\u529f\u4e4b\u9ad8\uff0c\u5185\u529b\u4e4b\u6df1\uff0c\u4e5f\u5f53\u5f97\u4e00\u4f4d\u52b2\u654c\u4e86\uff0c\u4f55\u51b5\uff0c\u4ed6\u80fd\u65bd\u5c55\u4e94\u798f\u7167\u547d\u73af\uff0c\u4f3c\u662f\u5df2\u5c3d\u5f97\u65e0\u76f8\u5927\u5e08\u7684\u4f20\u6388\u4e86\u3002\u201d \u3000\u3000\u201c\u53f8\u5f92\u8001\u5f1f\uff0c\u5929\u865a\u5b50\u6b66\u529f\u7a76\u7adf\u5982\u4f55\u5462\uff1f\u201d\u767d\u5929\u5316\u9053\uff1a\u201c\u600e\u4e48\u4f1a\u88ab\u4eba\u4e00\u62f3\u5c31\u6253\u6b7b\uff1f\u201d \u3000\u3000\u201c\u9152\u8089\u548c\u5c1a\uff0c\u6cd5\u53f7\u4e03\u5b9d\uff0c\u5e38\u5728\u6c5f\u6e56\u4e0a\u8d70\u52a8\u2026\u2026\u201d\u53f8\u5f92\u827a\u9053\uff1a\u201c\u4e00\u5411\u754f\u602f\u6015\u4e8b\uff0c\u5e38\u53d7\u672c\u5802\u4e2d\u7684\u62a4\u6cd5\u3001\u9999\u4e3b\u7b49\u6b3a\u4fae\uff0c\u5747\u80fd\u5fcd\u6c14\u541e\u58f0\uff0c\u60f3\u4e0d\u5230\uff0c\u7adf\u662f\u4e00\u4e2a\u6df1\u85cf\u4e0d\u9732\uff0c\u626e\u732a\u5403\u8001\u864e\u7684\u9ad8\u4eba\uff0c\u6211\u4eec\u5982\u6b64\u8d70\u773c\uff0c\u8bf4\u6765\u5341\u5206\u60ed\u6127\u3002\u2026\u2026\u201d \u3000\u3000\u957f\u957f\u5401\u4e00\u53e3\u6c14\uff0c\u63a5\u9053\uff1a\u201c\u81f3\u4e8e\u5929\u865a\u5b50\u526f\u603b\u5802\u4e3b\uff0c\u4ee5\u4fee\u4e60\u5251\u672f\u4e3a\u4e3b\uff0c\u521b\u6cd5\u7684\u7cbe\u5389\uff0c\u4eca\u4e16\u5251\u624b\u4e2d\uff0c\u5f53\u53ef\u540d\u5217\u524d\u8305\uff01\u201d \u3000\u3000\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u5bf9\u6c5f\u67ab\u4e00\u6279\u4eba\uff0c\u8981\u91cd\u65b0\u4f30\u91cd\u4e86\u2026\u2026\u201d\u767d\u5929\u5316\u9053\uff1a\u201c\u6c5f\u67ab\u4e00\u4eba\u80fd\u72ec\u6297\u516b\u5927\u5251\u58eb\uff0c\u4e03\u5b9d\u548c\u5c1a\u4e00\u62f3\u6253\u6b7b\u4e86\u5929\u865a\u5b50\uff0c\u4f46\u5c31\u8001\u673d\u6240\u77e5\uff0c\u6613\u82b1\u624b\u5f20\u56db\u59d1\u6280\u827a\u7684\u535a\u6742\uff0c\u4ea6\u662f\u5f88\u96be\u5bf9\u4ed8\u7684\u4eba\u7269\u3002 \u3000\u3000\u4e94\u884c\u9041\u672f\u80fd\u501f\u7269\u6f5c\u5f62\uff0c\u6740\u4eba\u4e8e\u4e0d\u77e5\u89c9\u4e4b\u4e2d\uff0c\u6280\u827a\u4ee3\u4ee3\u5355\u4f20\uff0c\u95e8\u4e0b\u5f1f\u5b50\u6700\u9ad8\u9650\u4e8e\u4e03\u4eba\uff0c\u662f\u4e00\u4e2a\u6781\u5c11\u5728\u6c5f\u6e56\u4e0a\u4f20\u626c\u7684\u5947\u602a\u95e8\u6237\uff0c\u5979\u5982\u7834\u4f8b\u628a\u5947\u6280\u4f20\u4eba\uff0c\u90a3\u5c31\u5341\u5206\u53ef\u6015\u4e86\u3002 \u3000\u3000\u6211\u4eec\u5982\u8981\u6297\u62d2\u6559\u4e3b\u7684\u91d1\u5370\u624b\u4e66\uff0c\u7edd\u4e0d\u80fd\u80cc\u8179\u53d7\u654c\u2026\u2026\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u7d27\u508d\u767d\u5929\u5316\u8eab\u4fa7\u800c\u5750\u7684\u4e00\u4e2a\u9752\u888d\u8001\u8005\uff0c\u7a81\u7136\u63a5\u53e3\u8bf4\u9053\uff1a\u201c\u603b\u5802\u4e3b\u3001\u9662\u4e3b\uff0c\u6c5f\u67ab\u662f\u4e09\u5723\u7684\u4f20\u4eba\u3002 \u3000\u3000\u5f53\u5e74\uff0c\u6211\u4eec\u6697\u7b97\u4e09\u5723\uff0c\u53c8\u5408\u529b\u91cd\u521b\u4e86\u91d1\u4e39\u4e66\u751f\uff0c\u8054\u624b\u6740\u4f24\u5929\u72d0\u592b\u4eba\uff0c\u8fd9\u4e9b\u4ec7\u6068\uff0c\u5929\u9ad8\u6d77\u6df1\u3002 \u3000\u3000\u6c5f\u67ab\u9886\u5bfc\u7684\u4e00\u6279\u4eba\uff0c\u6050\u90fd\u548c\u4e09\u5723\u6709\u6240\u5173\u8fde\uff0c\u7edd\u4e0d\u80fd\u7559\u4e0b\u7978\u5bb3\uff0c\u4e00\u65e6\u88ab\u4ed6\u4eec\u7acb\u8db3\u5f97\u52bf\uff0c\u6c5f\u6e56\u4e2d\u5404\u5927\u95e8\u6d3e\u76f2\u4ece\u9644\u548c\uff0c\u90a3\u5c31\u661f\u706b\u71ce\u539f\u3001\u4e0d\u53ef\u6536\u62fe\u4e86\u2026\u2026\u201d \u3000\u3000\u590f\u5929\u540c\u5fae\u5fae\u9894\u9996\uff0c\u9053\uff1a\u201c\u674e\u697c\u4e3b\u7684\u610f\u601d\u5462\uff1f\u201d \u3000\u3000\u539f\u6765\u8bf4\u8bdd\u7684\u4eba\uff0c\u662f\u798f\u5bff\u9662\u516d\u5927\u697c\u4e3b\u4e2d\u7684\u671d\u9633\u697c\u4e3b\u674e\u7075\u98ce\u3002 \u3000\u3000\u201c\u8001\u673d\u7684\u610f\u601d\u662f\uff0c\u4e24\u5bb3\u76f8\u6743\u53d6\u5176\u8f7b\u2026\u2026\u201d\u674e\u7075\u98ce\u9053\uff1a\u201c\u5148\u4f1a\u5408\u5929\u738b\u95e8\u3001\u5929\u540e\u5bab\u4e2d\u4eba\u624b\uff0c\u4e00\u4e3e\u527f\u706d\u6c5f\u67ab\u7b49\u4e00\u6279\u4eba\u3002 \u3000\u3000\u518d\u96c6\u4e2d\u529b\u91cf\u81ea\u4fdd\uff0c\u5bf9\u6297\u6559\u4e3b\u548c\u56db\u5927\u795e\u901a\u4f7f\u8005\uff0c\u56db\u4f7f\u8005\u65e2\u4ee5\u5c0f\u897f\u5929\u96f7\u97f3\u5bfa\u9b54\u6559\u6b66\u529f\u4e3a\u4e3b\uff0c\u90a3\u4f4d\u795e\u79d8\u83ab\u6d4b\u7684\u6559\u4e3b\uff0c\u5927\u7ea6\u662f\u5c0f\u897f\u5929\u96f7\u97f3\u5bfa\u4e2d\u4eba\u4e86\u3002 \u3000\u3000\u6570\u767e\u5e74\u6765\uff0c\u96f7\u97f3\u5bfa\u4e2d\u7684\u9b54\u6280\u6b66\u529f\uff0c\u5e38\u5728\u4e2d\u539f\u6b66\u6797\u4e2d\u51fa\u73b0\uff0c\u4f46\u771f\u6b63\u96f7\u97f3\u5bfa\u7684\u4eba\u7269\uff0c\u5374\u4ece\u672a\u5728\u4e2d\u539f\u73b0\u8eab\u8fc7\uff0c\u53ea\u542c\u697c\u68af\u54cd\uff0c\u4e0d\u89c1\u4eba\u4e0b\u6765\u3001\u4e5f\u8bb8\u5c31\u662f\u56db\u5927\u795e\u901a\u4f7f\u8005\u5728\u6545\u5f04\u7384\u865a\uff01 \u3000\u3000\u91cd\u8981\u7684\u662f\u5bf9\u6c5f\u67ab\u90a3\u6279\u4eba\u5bf9\u51b3\u4e4b\u6218\uff0c\u8981\u5c3d\u91cf\u4fdd\u5b58\u4e0b\u6211\u4eec\u7684\u5b9e\u529b\uff0c\u8ba9\u5929\u738b\u95e8\u3001\u5929\u540e\u5bab\u4e2d\u4eba\uff0c\u5148\u6253\u5934\u9635\uff01\u201d \u3000\u3000\u767d\u5929\u5316\u9053\uff1a\u201c\u529e\u6cd5\u4e0d\u9519\uff0c\u4e0d\u77e5\u603b\u5802\u4e3b\u610f\u4e0b\u5982\u4f55\uff1f\u201d \u3000\u3000\u201c\u95ee\u9898\u5728\u5929\u738b\u95e8\u3001\u5929\u540e\u5bab\u4e2d\u4eba\u2026\u2026\u201d\u590f\u5929\u540c\u8bf4\uff0c\u201c\u80fd\u4e0d\u80fd\u4e3a\u6211\u4eec\u6240\u7528\uff0c\u5148\u884c\u51fa\u624b\u653b\u654c\uff01\u201d \u3000\u3000\u201c\u53ea\u6015\u5f88\u96be\uff01\u201d\u53f8\u5f92\u827a\u9053\uff1a\u201c\u4ed6\u4eec\u4e0d\u80af\u73b0\u8eab\uff0c\u8fdc\u8eb2\u5728\u6570\u5341\u91cc\u5916\uff0c\u5df2\u662f\u5b58\u5fc3\u9003\u907f\u4e86\u3002\u201d \u3000\u3000\u201c\u53f8\u5f92\u8001\u5f1f\uff0c\u7740\u5c31\u8981\u7528\u70b9\u5fc3\u673a\u4e86\u2026\u2026\u201d\u674e\u7075\u98ce\u9053\uff1a\u201c\u79fb\u82b1\u63a5\u6728\uff0c\u5ac1\u7978\u6c5f\u4e1c\uff0c\u8ba9\u4ed6\u4eec\u65e0\u6cd5\u9003\u907f\u3002 \u3000\u3000\u81f3\u4e8e\u4e03\u661f\u697c\u3001\u4e07\u8c61\u697c\uff0c\u4e24\u4f4d\u697c\u4e3b\uff0c\u53ea\u662f\u88ab\u5929\u540e\u5b98\u4e2d\u5973\u5f1f\u5b50\u5a9a\u672f\u6240\u8bf1\uff0c\u624d\u7518\u4e3a\u6240\u7528\uff0c\u53ea\u8981\u4f7f\u4ed6\u4eec\u795e\u5fd7\u6e05\u9192\uff0c\u6211\u76f8\u4fe1\u4ed6\u4eec\u4e0d\u4f1a\u80cc\u5f03\u5929\u9a6c\u5802\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u5982\u65e0\u6cd5\u9632\u5236\u65e0\u540e\u5bab\u4e2d\u5973\u5f1f\u5b50\u7684\u5a9a\u672f\u653b\u52bf\uff0c\u4e00\u65e6\u6446\u660e\u654c\u5bf9\u6001\u52bf\uff0c\u5929\u9a6c\u5802\u4e0d\u77e5\u9053\u4f1a\u6709\u591a\u5c11\u4eba\uff0c\u843d\u5165\u6843\u82b1\u9677\u8015\u4e4b\u4e2d\uff0c\u88ab\u5929\u540e\u5bab\u4e2d\u5973\u5f1f\u5b50\u6536\u4f5c\u88d9\u4e0b\u4e4b\u81e3\u3002\u201d \u3000\u3000\u201c\u603b\u5802\u4e3b\u7684\u8680\u5fc3\u9b54\u97f3\uff0c\u80fd\u8ba9\u4eba\u795e\u667a\u53d7\u5236\u2026\u2026\u201d\u767d\u5929\u5316\u9053\uff1a\u201c\u4f46\u4e0d\u77e5\u80fd\u4e0d\u80fd\u6297\u62d2\u5a9a\u672f\u8bf1\u60d1\uff1f\u201d \u3000\u3000\u201c\u4e0d\u77e5\u9053\uff0c\u9b54\u97f3\u8680\u5fc3\uff0c\u80fd\u8ba9\u4ed6\u4eec\u4e3a\u6211\u62fc\u547d\u6740\u654c\uff0c\u4e0d\u8ba1\u751f\u6b7b\u2026\u2026\u201d \u3000\u3000\u590f\u5929\u540c\u9053\uff1a\u201c\u4f46\u80fd\u5426\u6297\u62d2\u5973\u4eba\u5a9a\u672f\uff0c\u65e0\u6cd5\u9884\u80d6\u3002 \u3000\u3000\u98df\u8272\u4e4b\u6027\uff0c\u662f\u4e00\u79cd\u5929\u8d4b\u672c\u80fd\uff0c\u5728\u7f8e\u5973\u5de7\u7b11\uff0c\u5a07\u5a9a\u6a2a\u751f\u7684\u8bf1\u60d1\u4e0b\uff0c\u4ed6\u4eec\u6709\u4e9b\u4ec0\u4e48\u53cd\u5e94\uff1f\u6211\u5168\u65e0\u628a\u63e1\uff0c\u5509\uff01\u6211\u65e9\u8be5\u6d4b\u8bd5\u4e00\u4e0b\uff0c\u770b\u770b\u7ed3\u679c\u624d\u5bf9\u3002\u201d \u3000\u3000\u201c\u73b0\u5728\u6d4b\u8bd5\uff0c\u65f6\u72b9\u672a\u665a\uff0c\u53f8\u5f92\u8001\u5f1f\uff0c\u54b1\u4eec\u6765\u4e86\u591a\u5c11\u4eba\u624b\u2026\u2026\u201d\u767d\u5929\u5316\u9053\uff1a\u201c\u80fd\u4e0d\u80fd\u9009\u51fa\u51e0\u4e2a\u4eba\u6765\uff0c\u8bd5\u8bd5\u5929\u540e\u5b98\u4e2d\u5973\u5f1f\u5b50\u7684\u5a9a\u529f\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4e00\u6b21\uff0c\u7b97\u5f97\u4e0a\u7cbe\u9510\u5c3d\u51fa\uff0c\u9664\u4e86\u8d35\u9662\u4e2d\u4e03\u661f\u3001\u4e07\u8c61\u4e8c\u4f4d\u697c\u4e3b\uff0c\u7559\u5b88\u672c\u5802\u4e4b\u5916\uff0c\u4e09\u4f4d\u526f\u603b\u5802\u4e3b\u5168\u6765\u4e86\uff0c\u4e09\u5341\u516d\u957f\u8001\u4e2d\uff0c\u4e8c\u6b7b\u4e00\u4f24\uff0c\u4e00\u4e2a\u964d\u4e86\u6c5f\u67ab\uff0c\u8fd8\u6709\u4e09\u5341\u4e09\u4eba\uff0c\u6765\u4e86\u4e09\u5341\u4e2a\u2026\u2026\u201d\u53f8\u5f92\u827a\u9053\uff1a\u201c\u4e03\u5341\u4e8c\u4f4d\u62a4\u6cd5\uff0c\u6765\u4e86\u516d\u5341\u4e8c\u4eba\uff0c\u56db\u5341\u516b\u540d\u5929\u9a6c\u5ea6\u5371\u94c1\u9a91\u58eb\uff0c\u6765\u4e86\u56db\u5341\u4e2a\u4eba\uff0c\u8fd9\u662f\u5929\u9a6c\u5802\u4e2d\u7684\u4e00\u7ea7\u60c5\u9510\uff0c\u53e6\u5916\u6709\u4e8c\u767e\u4f4d\u9999\u4e3b\uff0c\u6bcf\u4eba\u5e26\u4e86\u4e09\u540d\u9a91\u58eb\uff0c\u4ed6\u4eec\u5728\u4e00\u822c\u6c5f\u6e56\u6b66\u5e08\u4e2d\uff0c\u4e5f\u7b97\u5f97\u9ad8\u624b\u4e4b\u79f0\u3002\u201d \u3000\u3000\u6c5f\u67ab\u5403\u4e86\u4e00\u60ca\uff0c\u5fd6\u9053\uff1a\u201c\u4e8c\u767e\u4f4d\u9999\u4e3b\u5e26\u4e86\u516d\u767e\u540d\u9a91\u58eb\uff0c\u518d\u52a0\u4e0a\u957f\u8001\u3001\u62a4\u6cd5\uff0c\u5ea6\u5371\u94c1\u9a91\uff0c\u5c82\u4e0d\u662f\u6709\u8fd1\u5343\u7684\u4eba\u4e86\uff0c\u8fd9\u4e9b\u4eba\u6709\u4e0d\u5c11\u53ef\u5217\u5165\u6c5f\u6e56\u9ad8\u624b\u4e4b\u6750\uff0c\u8fd8\u4e0d\u7b97\u590f\u5929\u540c\u7b49\u8fd9\u4e9b\u7edd\u9876\u9ad8\u624b\uff0c\u653e\u773c\u6c5f\u6e56\uff0c\u4efb\u4f55\u95e8\u6d3e\uff0c\u4ed6\u65e0\u6cd5\u805a\u96c6\u5982\u6b64\u5f3a\u5927\u7684\u5b9e\u529b\u3002\u201d \u3000\u3000\u4f46\u542c\u767d\u5929\u5316\u9053\uff1a\u201c\u56db\u4f4d\u697c\u4e3b\uff0c\u5404\u6709\u56db\u81f3\u516d\u4e2a\u7684\u4ece\u5c5e\uff0c\u6b66\u529f\u5e94\u53ef\u5217\u5165\u957f\u8001\u7b49\u7ea7\uff0c\u4e2a\u4e2a\u90fd\u662f\u51cc\u5389\u7684\u6740\u624b\uff0c\u8001\u673d\u968f\u8eab\u56db\u7ae5\uff0c\u4e5f\u90fd\u8ddf\u7740\u6765\u4e86\u3002\u201d \u3000\u3000\u201c\u8fd8\u6709\u4e00\u80a1\u5f88\u5f3a\u5927\u7684\u529b\u91cf\uff0c\u662f\u603b\u5802\u4e3b\u4eb2\u81ea\u51cb\u6559\u7684\u5251\u624b\u6b7b\u58eb\uff0c\u2026\u2026\u201d\u53f8\u5f92\u827a\u9053\uff1a\u201c\u5c5e\u4e0b\u5c31\u4e0d\u77e5\u6709\u591a\u5c11\u4f4d\u4e86\u3002\u201d \u3000\u3000\u201c\u56db\u5341\u516b\u4f4d\u5251\u571f\uff0c\u5168\u90e8\u5230\u9f50\uff0c\u88ab\u6c5f\u67ab\u6740\u7684\u4e00\u4e2a\uff0c\u6211\u5df2\u8865\u4e0a\u4e86\uff0c\u2026\u2026\u201d\u590f\u5929\u540c\u9053\uff1a\u201c\u7533\u526f\u5802\u7684\u56db\u4f4d\u5973\u5f1f\u5b50\uff0c\u662f\u5426\u5168\u6765\u4e86\u5462\uff1f\u201d \u3000\u3000\u201c\u5168\u6570\u5230\u9f50\uff0c\u8fd9\u53ef\u80fd\u662f\u5929\u9a6c\u603b\u5802\u7684\u5173\u952e\u4e4b\u6218\uff0c\u7533\u96ea\u541b\u4e0d\u6562\u85cf\u79c1\u3002\u201d \u3000\u3000\u201c\u653e\u773c\u6c5f\u6e56\uff0c\u5982\u6b64\u5f3a\u5927\u7684\u5b9e\u529b\uff0c\u518d\u52a0\u4e0a\u6211\u4eec\u7684\u79c1\u623f\u672c\u94b1\uff0c\u5c31\u7b97\u56db\u5927\u795e\u901a\u4f7f\u8005\uff0c\u4e5f\u672a\u5fc5\u80fd\u9003\u8fc7\u8fd9\u7b49\u9635\u52bf\u7684\u56f4\u6740\u3002\u201d\u767d\u5929\u5316\u9053\uff1a\u201c\u6c5f\u67ab\u4e00\u6279\u4eba\uff0c\u4e5f\u9003\u4e0d\u8fc7\uff0c\u5c31\u7b97\u6b66\u6797\u4e09\u5723\u91cd\u751f\uff0c\u4e5f\u65e0\u6240\u754f\uff0c\u95ee\u9898\u5728\uff0c\u6211\u4eec\u5148\u8981\u786e\u5b9a\u8c01\u662f\u5934\u53f7\u654c\u4eba\uff1f\u5148\u5bf9\u54ea\u8fb9\u52a8\u624b\uff1f\u201d \u3000\u3000\u201c\u5148\u7531\u53f8\u5f92\u526f\u603b\u5802\u9009\u6d3e\u51e0\u4e2a\u9ad8\u624b\uff0c\u8bd5\u8bd5\u5929\u540e\u5bab\u4e2d\u5973\u5f1f\u5b50\u7684\u5a9a\u672f\uff0c\u80fd\u4e0d\u80fd\u5f81\u670d\u5728\u8680\u5fc3\u9b54\u97f3\u63a7\u5236\u4e0b\u7684\u4eba\uff1f\u6700\u4e3a\u91cd\u8981\uff0c\u800c\u4e14\u8981\u5c3d\u5feb\u5f97\u5230\u7ed3\u679c\u3002 \u3000\u3000\u81f3\u4e8e\u5bf9\u6c5f\u67ab\u7b49\u4e00\u884c\uff0c\u6211\u4eec\u6765\u4e2a\u56f4\u800c\u4e0d\u653b\uff0c\u9759\u89c2\u5176\u53d8\uff0c\u5929\u738b\u95e8\u3001\u5929\u540e\u5bab\u5982\u4e0d\u51fa\u52a8\u4eba\u624b\u653b\u654c\uff0c\u54b1\u4eec\u4e5f\u6309\u5175\u4e0d\u52a8\u3002\u201d \u3000\u3000\u590f\u5929\u540c\u4f5c\u4e86\u6700\u540e\u7684\u51b3\u5b9a\u3002 \u3000 \u7b2c\u4e8c\u5341\u4e09\u56de\u3000\u6c5f\u5c11\u4fa0\u65bd\u672f\u6536\u84dd\u51e4 \u3000 \u3000\u3000\u4e5f\u8bb8\u662f\u8fd9\u7b49\u9635\u52bf\uff0c\u5413\u4f4f\u4e86\u5c0f\u72d0\u5973\u541f\u971c\uff0c\u5979\u6ca1\u6709\u901e\u5f3a\u5984\u52a8\uff0c\u5374\u6f5c\u56de\u5230\u6c5f\u67ab\u7684\u8eab\u4fa7\uff0c\u4e24\u4e2a\u4eba\u6084\u7136\u79bb\u53bb\u3002 \u3000\u3000\u6c5f\u67ab\u9000\u51fa\u4e24\u4e08\u540e\uff0c\u9065\u53d1\u6307\u529b\uff0c\u5192\u9669\u89e3\u4e86\u56db\u4e2a\u9ed1\u8863\u5251\u58eb\u7684\u7a74\u9053\uff0c\u539f\u60f3\u56db\u4eba\u53ef\u80fd\u4f1a\u4f20\u51fa\u8b66\u8baf\uff0c\u4e24\u4eba\u4ee5\u6700\u5feb\u901f\u7684\u8eab\u6cd5\u9041\u8d70\u5230\u767e\u4e08\u4ee5\u5916\u3002 \u3000\u3000\u4f46\u56db\u4e2a\u5251\u58eb\u5374\u672a\u4f5c\u53cd\u5e94\u3002 \u3000\u3000\u5927\u6982\u662f\u590f\u5929\u540c\u5fa1\u4e0b\u592a\u4e25\uff0c\u56db\u4eba\u62c5\u5fc3\u8bf4\u51fa\u5185\u60c5\uff0c\u4f1a\u906d\u5230\u6740\u8eab\u4e4b\u7978\uff0c\u65e2\u7136\u672a\u88ab\u53d1\u73b0\uff0c\u4e50\u5f97\u9690\u5fcd\u4e0d\u53d1\u4e86\u3002 \u3000\u3000\u201c\u6c5f\u5927\u54e5\uff0c\u2026\u2026\u201d\u541f\u971c\u5401\u4e86\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5982\u82e5\u672a\u7ec3\u8fc7\u4f20\u771f\u8bb0\u4e0a\u6b66\u529f\uff0c\u6211\u53ef\u80fd\u65e9\u88ab\u4ed6\u4eec\u53d1\u73b0\u4e86\u3002\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u6211\u4e5f\u4e00\u6837\u9003\u4e0d\u8fc7\u4ed6\u4eec\u7684\u8033\u76ee\uff0c\u2026\u2026\u201d\u67ab\u9053\uff1a\u201c\u8fd9\u4e9b\u4eba\u90fd\u662f\u6b66\u6797\u4e2d\u7684\u5de8\u6076\u5927\u9b54\uff0c\u771f\u6b63\u7684\u9876\u5c16\u9ad8\u624b\u554a\uff01\u201d \u3000\u3000\u201c\u56de\u53bb\u5427\uff01\u6b64\u4e8b\u5173\u7cfb\u91cd\u5927\uff0c\u5f97\u548c\u5f20\u59e8\uff0c\u597d\u597d\u5546\u91cf\u4e00\u4e0b\u4e86\u3002\u201d \u3000\u3000\u4e24\u4eba\u56de\u53bb\u7684\u5f88\u53ca\u65f6\uff0c\u56e0\u4e3a\u5f20\u56db\u59d1\u4e5f\u7b49\u7684\u53d1\u6025\u4e86\uff0c\u51c6\u5907\u65bd\u653e\u4e07\u91cc\u98d8\u9999\uff0c\u53ec\u5524\u541f\u971c\u3002\u541f\u96ea\u5df2\u4e09\u5ea6\u8bf7\u547d\uff0c\u8981\u5e26\u7740\u6885\u82b1\u3001\u9752\u51e4\u51fa\u52a8\u53bb\u8ffd\u5bfb\u4e24\u4eba\uff0c\u5927\u6709\u62fc\u6b7b\u4e00\u6218\uff0c\u4ee5\u8eab\u6b89\u592b\u7684\u6253\u7b97\uff0c\u4f46\u90fd\u88ab\u5f20\u56db\u59d1\u62e6\u4f4f\u3002 \u3000\u3000\u770b\u4e24\u4eba\u65e0\u6059\u5f52\u6765\uff0c\u5f20\u56db\u59d1\u6697\u6697\u634f\u4e86\u4e00\u628a\u51b7\u6c57\uff0c\u94c1\u9752\u7740\u8138\u8272\uff0c\u9053\uff1a\u201c\u8bf4\u597d\u7684\uff0c\u7edd\u4e0d\u80fd\u8d85\u8fc7\u5341\u4e8c\u4e2a\u65f6\u8fb0\uff0c\u4f46\u4f60\u4eec\u53bb\u4e86\u591a\u4e45\uff1f\u5982\u679c\u654c\u4eba\u5927\u4e3e\u6765\u72af\uff0c\u6211\u4eec\u6218\u4e0d\u80fd\u80dc\uff0c\u8d70\u4e0d\u80fd\u8d70\uff0c\u4f60\u4eec\u4e24\u4e2a\u8bf4\u8bf4\u7740\uff0c\u8be5\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u201c\u4e0d\u8981\u751f\u6c14\u561b\uff01\u5f20\u59e8\uff0c\u2026\u2026\u201d\u541f\u971c\u6492\u5a07\u9053\uff1a\u201c\u6211\u4eec\u53d1\u73b0\u4e86\u590f\u5929\u540c\uff0c\u4e5f\u5e26\u56de\u6765\u4e86\u60ca\u5929\u52a8\u5730\u7684\u5927\u6d88\u606f\u3002\u201d \u3000\u3000\u773c\u770b\u5f20\u56db\u59d1\u4ecd\u7136\u5bd2\u7740\u4e00\u5f20\u8138\uff0c\u541f\u971c\u77e5\u9053\u5f20\u56db\u59d1\u771f\u7684\u706b\u4e86\uff0c\u6c5f\u5927\u54e5\u4f4e\u7740\u5934\u9ed8\u7136\u4e0d\u8bed\uff0c\u541f\u971c\u5fc3\u4e2d\u660e\u767d\uff0c\u81ea\u5df1\u5fc5\u987b\u6311\u8d77\u8fd9\u4e2a\u8d23\u4efb\u4e86\uff0c\u4e00\u4e0b\u5b50\u8dea\u4e86\u4e0b\u53bb\uff0c\u9053\uff1a\u201c\u5f20\u59e8\uff0c\u6211\u77e5\u9053\u4e34\u654c\u8fdd\u547d\uff0c\u7f6a\u4e0d\u53ef\u6055\uff0c\u4f46\u8bf7\u5f20\u59e8\u8ba9\u6211\u8bf4\u5b8c\u6240\u95fb\u6240\u89c1\uff0c\u5f20\u59e8\u8981\u600e\u4e48\u60e9\u7f5a\u6211\uff0c\u971c\u513f\u7edd\u65e0\u6028\u8a00\uff01\u201d \u3000\u3000\u201c\u5927\u59d0\uff0c\u6211\u4eec\u6f5c\u4f0f\u5728\u6ce5\u6cbc\u4e4b\u4e2d\uff0c\u56db\u5468\u6ee1\u5e03\u654c\u4eba\u2026\u2026\u201d\u6c5f\u67ab\u5f00\u4e86\u53e3\u9053\uff1a\u201c\u8eb2\u4e86\u4e94\u516d\u4e2a\u65f6\u8fb0\uff0c\u56e0\u4e3a\uff0c\u4e0d\u5230\u5165\u591c\u65f6\u5206\uff0c\u5c31\u7b97\u98de\u9e1f\u4e5f\u65e0\u6cd5\u907f\u5f00\u654c\u4eba\u7684\u8033\u76ee\uff0c\u4e0d\u8fc7\uff0c\u8f9b\u82e6\u603b\u7b97\u6ca1\u6709\u767d\u53d7\uff0c\u63a2\u5f97\u5f88\u91cd\u5927\u7684\u6d88\u606f\uff0c\u4e0d\u6c42\u8d4f\u8d50\uff0c\u4f46\u6c42\u80fd\u5c06\u529f\u6298\u7f6a\u5c31\u597d\uff01\u201d \u3000\u3000\u5f20\u56db\u59d1\u8138\u4e0a\u4ecd\u65e0\u7b11\u5bb9\uff0c\u4f46\u5374\u4f38\u624b\u62c9\u8d77\u4e86\u541f\u971c\uff0c\u9053\uff1a\u201c\u4f60\u662f\u4e3b\u5e05\uff0c\u5c0f\u971c\u513f\u81ea\u7136\u662f\u552f\u547d\u662f\u4ece\uff0c\u4f46\u8fd9\u4ef6\u4e8b\u5173\u7cfb\u7740\u6211\u4eec\u5927\u5bb6\u7684\u751f\u6b7b\uff0c\u5e0c\u671b\u4f60\u7684\u89e3\u91ca\uff0c\u80fd\u8ba9\u6211\u4eec\u90fd\u542c\u5f97\u5fc3\u670d\u53e3\u670d\u3002\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u5927\u548c\u5c1a\u3001\u6b27\u9633\u662d\u3001\u7f57\u5170\u3001\u8001\u53eb\u5316\u94b1\u7f3a\uff0c\u5168\u90fd\u56f4\u4e86\u8fc7\u6765\uff0c\u4ed6\u4eec\u662f\u62c5\u8d1f\u9a70\u63f4\u7684\u5de5\u4f5c\uff0c\u54ea\u91cc\u6709\u60ca\uff0c\u54ea\u91cc\u53bb\uff0c\u4e0d\u62c5\u8d1f\u8b66\u6212\u8d23\u4efb\u3002 \u3000\u3000\u6c5f\u67ab\u8bf4\u51fa\u4e86\u6240\u95fb\u6240\u89c1\uff0c\u541f\u971c\u4e5f\u4f5c\u4e9b\u8865\u5145\u3002 \u3000\u3000\u679c\u7136\u662f\u60ca\u5929\u52a8\u5730\u7684\u5927\u6d88\u606f\uff0c\u7fa4\u8c6a\u90fd\u542c\u5f97\u76ee\u77aa\u53e3\u5446\u3002 \u3000\u3000\u201c\u539f\u6765\uff0c\u6211\u4eec\u5df2\u9677\u5165\u4e86\u5343\u4eba\u4ee5\u4e0a\u7684\u5927\u5305\u56f4\u4e2d\u2026\u2026\u201d\u5f20\u56db\u59d1\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5e78\u597d\u971c\u513f\u6ca1\u6709\u65bd\u653e\u51fa\u4e07\u91cc\u98d8\u9999\uff0c\u90a3\u80a1\u9999\u5473\uff0c\u7edd\u5bf9\u9003\u4e0d\u8fc7\u9634\u9633\u79c0\u624d\u767d\u5929\u5316\u548c\u590f\u5929\u540c\u7684\u55c5\u89c9\uff0c\u5e94\u53d8\u6709\u672f\uff0c\u65e0\u7f6a\u6709\u8d4f\u3002\u201d \u3000\u3000\u541f\u971c\u4e00\u5934\u504e\u5165\u4e86\u5f20\u56db\u59d1\u7684\u6000\u4e2d\uff0c\u9053\uff1a\u201c\u5f20\u59e8\uff0c\u521a\u624d\u628a\u6211\u5413\u6b7b\u4e86\uff0c\u6211\u4ece\u672a\u89c1\u8fc7\u5f20\u59e8\u90a3\u79cd\u6c89\u91cd\u7684\u795e\u60c5\u2026\u2026\u201d \u3000\u3000\u7d27\u7d27\u62b1\u4f4f\u541f\u971c\uff0c\u5f20\u56db\u59d1\u63a5\u9053\uff1a\u201c\u662f\u5f20\u59e8\u9519\u602a\u4f60\u4e86\uff0c\u5509\uff01\u6211\u8fd9\u628a\u5e74\u7eaa\u4e86\uff0c\u8fd8\u662f\u6c89\u4e0d\u4f4f\u6c14\uff0c\u8fd9\u662f\u4e2a\u5f88\u5927\u7684\u6559\u8bad\uff0c\u6b64\u540e\u9047\u4e8b\uff0c\u771f\u8981\u4e09\u601d\u800c\u884c\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u80fd\u602a\u4f60\uff0c\u5927\u59d0\uff0c\u4f60\u7684\u62c5\u5b50\u592a\u91cd\u4e86\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u654c\u4eba\u5982\u6b64\u4f17\u591a\uff0c\u771f\u8981\u6b63\u9762\u4ea4\u950b\uff0c\u4e0d\u8bba\u80dc\u8d25\u5982\u4f55\uff1f\u6211\u4eec\u90fd\u96be\u514d\u60e8\u91cd\u4f24\u4ea1\u3002\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u9762\u5bf9\u9762\u62fc\u4e0b\u6765\uff0c\u4e5f\u8bb8\u53ea\u6709\u4f60\u96ea\u3001\u971c\u624d\u6709\u7834\u56f4\u800c\u51fa\u7684\u673a\u4f1a\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u4f55\u51b5\uff0c\u4ed6\u4eec\u7684\u9996\u8981\u4eba\u7269\uff0c\u4e0d\u4f1a\u548c\u4f60\u786c\u62da\uff0c\u8fd9\u5df2\u4e0d\u662f\u4ee5\u5be1\u51fb\u4f17\uff0c\u4eba\u5165\u62da\u547d\u53ef\u884c\u4e86\uff0c\u6c5f\u67ab\uff0c\u975e\u5e38\u5f62\u52bf\uff0c\u8981\u7528\u975e\u5e38\u624b\u6bb5\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u5927\u59d0\u4e00\u58f0\u4ee4\u4e0b\uff0c\u5c0f\u5f1f\u8d74\u6c64\u6295\u706b\uff0c\u5728\u6240\u4e0d\u60dc\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u6c89\u601d\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u597d\uff01\u4f60\u548c\u9752\u51e4\u7559\u4e0b\u6765\uff0c\u6211\u4eec\u7acb\u523b\u9041\u8d70\uff0c\u8fde\u591c\u8d76\u8d74\u5c11\u6797\u5bfa\u53bb\uff0c\u6211\u4eec\u79bb\u5f00\u534a\u4e2a\u65f6\u8fb0\uff0c\u4f60\u4eec\u5c31\u653e\u8d77\u4e00\u628a\u706b\uff0c\u70e7\u4e86\u8fd9\u5ea7\u4e71\u8349\u8352\u539f\u3002\u201d \u3000\u3000\u201c\u5f20\u59e8\u53ea\u7559\u4e0b\u9752\u51e4\u59d0\u548c\u6c5f\u5927\u54e5\uff0c\u4e0d\u662f\u529b\u91cf\u592a\u5355\u8584\u4e48\uff1f\u201d\u541f\u971c\u9053\uff1a\u201c\u6211\u4e5f\u7559\u4e0b\u6765\u5e2e\u4ed6\u4eec\u5982\u4f55\uff1f\u201d \u3000\u3000\u201c\u4e0d\u884c\uff01\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u4f60\u548c\u541f\u96ea\u662f\u88ad\u6740\u63a7\u5236\u5c11\u6797\u5bfa\u654c\u4eba\u7684\u4e3b\u529b\u2026\u2026\u201d\u62c9\u8fc7\u9752\u51e4\uff0c\u9644\u8033\u4f4e\u8a00\uff0c\u9752\u51e4\u542c\u5f97\u8fde\u8fde\u70b9\u5934\u3002 \u3000\u3000\u201c\u62db\u56de\u5404\u8def\u8b66\u6212\u4eba\u624b\uff0c\u54b1\u4eec\u7acb\u523b\u884c\u52a8\uff0c\u5e0c\u671b\u8fd9\u573a\u5927\u706b\u80fd\u70e7\u65ad\u8ffd\u8e2a\u7075\u72ac\u7684\u55c5\u89c9\uff0c\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u54b1\u4eec\u5c3d\u534a\u591c\u4e4b\u529b\uff0c\u8d70\u6377\u5f84\u8d76\u5165\u5c11\u6797\u5bfa\u4e2d\u3002\u201d \u3000\u3000\u541f\u971c\u8fd8\u8981\u5f00\u53e3\uff0c\u5374\u88ab\u541f\u96ea\u62c9\u4e86\u4e00\u628a\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u59b9\u59b9\uff0c\u6211\u4eec\u7684\u62c5\u5b50\uff0c\u4e0d\u4f1a\u6bd4\u6c5f\u5927\u54e5\u8f7b\uff0c\u4f60\u5982\u7559\u4e0b\u6765\uff0c\u59d0\u4e00\u4e2a\u4eba\uff0c\u600e\u80fd\u62c5\u5f97\u8d77\u6765\u3002\u201d \u3000\u3000\u8bf4\u8d70\u5c31\u8d70\uff0c\u56db\u5468\u8b66\u6212\u4eba\u624b\u5df2\u5230\uff0c\u5f20\u56db\u59d1\u7acb\u523b\u5e26\u7740\u7fa4\u8c6a\u51fa\u53d1\uff0c\u5927\u548c\u5c1a\u9886\u5148\u5e26\u8def\u3002 \u3000\u3000\u76ee\u7779\u5f20\u56db\u59d1\u7b49\u4e00\u884c\u53bb\u8fdc\uff0c\u6c5f\u67ab\u8f7b\u8f7b\u628a\u9752\u51e4\u63fd\u5165\u6000\u4e2d\uff0c\u9053\uff1a\u201c\u53ea\u5269\u6211\u4eec\u4e24\u4e2a\u4eba\u4e86\uff0c\u4f60\u6015\u4e0d\u6015\uff1f\u201d \u3000\u3000\u201c\u5f3a\u654c\u73af\u4f3a\uff0c\u4eba\u903e\u5343\u6570\uff0c\u4e00\u65e6\u9047\u4e0a\u4e86\uff0c\u7edd\u65e0\u751f\u671b\uff0c\u4f60\u8bf4\u6211\u6015\u4e0d\u6015\u5462\uff1f\u201d\u9752\u51e4\u53cd\u624b\u6402\u4f4f\u4e86\u6c5f\u67ab\u3002 \u3000\u3000\u201c\u5176\u5b9e\uff0c\u6211\u4e00\u4e2a\u4eba\u4e5f\u53ef\u4ee5\u505a\u7684\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u4f60\u5e94\u8be5\u8ddf\u4ed6\u4eec\u4e00\u8d77\u8d70\u2026\u2026\u201d \u3000\u3000\u201c\u4ed6\u4eec\u4e5f\u4e0d\u8f7b\u677e\uff0c\u636e\u6211\u6240\u77e5\uff0c\u63a7\u5236\u5c11\u6797\u7684\u4eba\uff0c\u5f88\u591a\u9ad8\u624b\uff0c\u2026\u2026\u201d\u9752\u51e4\u9053\uff1a\u201c\u4f55\u51b5\uff0c\u4ed6\u4eec\u5982\u679c\u89e3\u4e0d\u4e86\u5c11\u6797\u5bfa\u4e2d\u50e7\u4eba\u8eab\u4e0a\u7684\u7981\u5236\uff0c\u5c11\u6797\u5bfa\u7684\u50e7\u4ece\uff0c\u4e5f\u662f\u4ed6\u4eec\u7684\u654c\u4eba\u3002\u201d \u3000\u3000\u201c\u5927\u59d0\u4e34\u53bb\u524d\uff0c\u7ed9\u4e86\u4f60\u4ec0\u4e48\u9526\u56ca\u5999\u8ba1\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u4e00\u80a1\u8111\u7684\u544a\u8bc9\u6211\uff0c\u7136\u540e\uff0c\u6211\u5c31\u6307\u70b9\u4f60\u4e00\u4e2a\u5b89\u5168\u5730\u65b9\uff0c\u8eb2\u8d77\u6765\uff0c\u6211\u8981\u5355\u5200\u8d74\u4f1a\uff0c\u6253\u7d2f\u4e86\uff0c\u6211\u5c31\u8dd1\uff0c\u4f11\u606f\u4e00\u9635\uff0c\u518d\u53bb\u627e\u4ed6\u4eec\uff0c\u6709\u4e2a\u4e09\u4e94\u6b21\uff0c\u6211\u76f8\u4fe1\u53ef\u4ee5\u6740\u4ed6\u4eec\u4e00\u4e2a\u80c6\u98a4\u5fc3\u60ca\uff0c\u81f3\u5c11\uff0c\u53ef\u4ee5\u7834\u574f\u4e86\u4ed6\u4eec\u56f4\u88ad\u6211\u4eec\u7684\u5e03\u7f6e\u3002\u201d \u3000\u3000\u201c\u6c5f\u90ce\uff0c\u771f\u8981\u6709\u51e0\u767e\u4eba\u628a\u4f60\u56f4\u4e0a\u4e86\uff0c\u4f60\u80fd\u7834\u56f4\u800c\u51fa\u4e48\uff1f\u522b\u5fd8\u4e86\uff0c\u4e00\u4e9b\u4eba\u4e2d\uff0c\u6709\u5f88\u591a\u662f\u7edd\u9876\u9ad8\u624b\u554a\uff01\u201d \u3000\u3000\u6c5f\u67ab\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u8fd9\u6211\u5c31\u4e0d\u80fd\u9884\u4f30\u4e86\uff0c\u5982\u679c\uff0c\u590f\u5929\u540c\u3001\u53f8\u5f92\u827a\u3001\u7533\u96ea\u541b\u3001\u767d\u5929\u5316\u548c\u56db\u5927\u697c\u4e3b\u5168\u90fd\u5728\u573a\uff0c\u4e00\u91cf\u6211\u6253\u5230\u611f\u89c9\u75b2\u52b3\uff0c\u80fd\u4e0d\u80fd\u7a81\u7834\u56f4\u56f0\u800c\u51fa\uff0c\u5b9e\u5728\u8bf4\uff0c\u5e76\u65e0\u628a\u63e1\u3002\u201d \u3000\u3000\u201c\u6614\u5e74\u9738\u738b\u9879\u7fbd\uff0c\u4f55\u7b49\u795e\u52c7\uff0c\u4e00\u65e5\u591c\u653b\u7834\u654c\u4eba\u767e\u4f59\u8425\u5be8\uff0c\u4f46\u88ab\u56f0\u4e4c\u6c5f\u65f6\uff0c\u4ecd\u96be\u514d\u522b\u59ec\u9ad8\u547c\uff0c\u865e\u516e\u865e\u516e\u5948\u82e5\u4f55\uff1f\u6c5f\u90ce\uff0c\u4e00\u52c7\u4e0d\u53ef\u6043\uff0c\u8fd8\u662f\u542c\u4ece\u5f20\u59e8\u7684\u5b89\u6392\u5427\uff01\u4e0d\u8981\u4e3a\u6211\u62c5\u5fc3\uff0c\u4f60\u5c31\u662f\u6211\u6700\u5411\u5f80\u7684\u5b89\u5168\u6240\u5728\uff0c\u751f\u6b7b\u76f8\u4ece\uff0c\u6b7b\u53ef\u7791\u76ee\u3002\u201d \u3000\u3000\u201c\u5927\u59d0\u5b89\u6392\u7684\u4ec0\u4e48\u5999\u8ba1\uff1f\u201d \u3000\u3000\u201c\u6211\u5e26\u4f60\u53bb\u89c1\u84dd\u51e4\uff0c\u8fd9\u5927\u6982\u53eb\u4f5c\u7f8e\u7537\u8ba1\u5427\uff01\u2026\u2026\u201d\u9752\u51e4\u5ae3\u7136\u4e00\u7b11\u53c8\u9053\uff1a\u201c\u8981\u4f60\u65bd\u5c55\u2018\u60d1\u5fc3\u672f\u2019\u628a\u5979\u62c9\u8fc7\u6765\u2026\u2026\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u2026\u2026\u8fd9\u4e2a\u2026\u2026\u201d \u3000\u3000\u201c\u522b\u8fd9\u4e2a\uff0c\u90a3\u4e2a\uff0c\u6211\u8fd8\u6ca1\u6709\u8bf4\u5b8c\u5462\uff01\u2026\u2026\u201d\u9752\u51e4\u9053\uff1a\u201c\u6240\u8c13\u975e\u5e38\u624b\u6bb5\uff0c\u5c31\u662f\u4f60\u4e0d\u80fd\u9009\u62e9\u597d\u6076\uff0c\u84dd\u51e4\u8981\u62c9\uff0c\u4f60\u6700\u4e0d\u559c\u6b22\u7684\u83f1\u82b1\u4e5f\u8981\u62c9\uff0c\u5f20\u59e8\u8bf4\u5979\u57ce\u5e9c\u5f88\u6df1\uff0c\u901a\u6653\u5f79\u4f7f\u9e70\u3001\u72ac\u4e4b\u672f\uff0c\u53ef\u80fd\u662f\u4e00\u4e2a\u975e\u5e38\u4eba\u7269\uff0c\u8bf4\u4e0d\u5b9a\u5979\u80fd\u4e0a\u8fbe\u5929\u5ead\uff0c\u6574\u4e2a\u5929\u540e\u5bab\u90fd\u5728\u5979\u6697\u4e2d\u76d1\u89c6\u4e4b\u4e0b\uff0c\u662f\u771f\u6b63\u7684\u5e55\u540e\u9996\u8111\u6446\u4e0b\u7684\u4e00\u9897\u68cb\u5b50\u2026\u2026\u201d \u3000\u3000\u201c\u5982\u679c\u4e0d\u662f\u5462\uff1f\u201d \u3000\u3000\u201c\u90a3\u4f60\u4e5f\u6ca1\u6709\u4ec0\u4e48\u4e8f\u5403\u554a\uff1f\u201d\u9752\u51e4\u7b11\u9053\uff1a\u201c\u6211\u548c\u96ea\u3001\u971c\u4e24\u4f4d\u59b9\u59b9\u90fd\u8ba4\u4e86\uff0c\u4f60\u5c31\u5c06\u5c31\u4e9b\u3001\u59d4\u5c48\u70b9\u5427\uff01\u201d \u3000\u3000\u201c\u5927\u4e08\u592b\u843d\u5230\u5982\u6b64\u5883\u5730\uff0c\u4f55\u5982\u653e\u624b\u4e00\u6218\uff0c\u62fc\u4e2a\u8840\u6d41\u4e94\u6b65\uff0c\u6211\u6c5f\u67ab\u8fd9\u4e00\u8eab\u6b66\u529f\uff0c\u62fc\u6b7b\u640f\u6740\u590f\u5929\u540c\uff0c\u5e94\u975e\u96be\u4e8b\uff01\u201d \u3000\u3000\u201c\u95ee\u9898\u662f\uff0c\u4f60\u4e0d\u80fd\u6b7b\uff0c\u6740\u6b7b\u590f\u5929\u540c\uff0c\u4e5f\u4e0d\u662f\u4f60\u6700\u7ec8\u7684\u76ee\u7684\u2026\u2026\u201d \u3000\u3000\u9752\u51e4\u9053\uff1a\u201c\u4f60\u8981\u626b\u9664\u5996\u6c14\uff0c\u91cd\u5149\u6b66\u6797\uff0c\u90a3\u624d\u662f\u4e09\u5723\u7684\u5fc3\u613f\uff0c\u4f60\u5b66\u4e86\u5929\u72d0\u6b66\u529f\u2018\u60d1\u5fc3\u672f\u2019\uff0c\u5c31\u5df2\u6ce8\u5b9a\u4e86\u4f60\u6843\u82b1\u7f69\u8eab\u7684\u547d\u8fd0\u3002 \u3000\u3000\u6211\u4eec\u90fd\u7518\u613f\u96e8\u9732\u5747\u6cbe\uff0c\u4f60\u53c8\u4f55\u5fc5\u62d8\u6ce5\u4e8e\u559c\u6076\u4e4b\u611f\u5462\uff1f\u8fd9\u53ea\u662f\u4e00\u79cd\u5bf9\u654c\u7684\u624b\u6bb5\u3002 \u3000\u3000\u518d\u8bf4\uff0c\u4f60\u5982\u672a\u7ec3\u8fc7\u2018\u60d1\u5fc3\u672f\u2019\uff0c\u6211\u4e5f\u672a\u5fc5\u4f1a\u5bf9\u4f60\u5982\u6b64\u7684\u503e\u5fc3\u76f8\u4f9d\uff0c\u522b\u8bf4\u96ea\u3001\u971c\u4e8c\u59b9\u90a3\u6837\u4ed9\u5b50\u822c\u7684\u4eba\u7269\u4e86\uff01\u201d \u3000\u3000\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u6211\u6c5f\u67ab\u5f97\u4f60\u4eec\u503e\u5fc3\u76f8\u7231\uff0c\u5168\u662f\u2018\u6212\u5fc3\u672f\u2019\u7684\u529b\u91cf\u4e86\uff1f\u201d \u3000\u3000\u201c\u78b0\u4e0a\u4f60\uff0c\u5c31\u7acb\u523b\u9677\u5165\u60c5\u7f51\uff0c\u800c\u4e14\uff0c\u81f3\u6b7b\u4e0d\u6e1d\uff0c\u7518\u4e3a\u60c5\u6b7b\uff0c\u5012\u4e5f\u7f62\u4e86\uff0c\u8fd8\u7518\u613f\u5e2e\u4f60\u6413\u5408\u60c5\u7f18\uff0c\u4f60\u60f3\u60f3\u8fd9\u4e16\u4e0a\u771f\u6709\u8fd9\u6837\u8ba9\u4eba\u4e00\u89c1\u949f\u60c5\u7684\u7537\u4eba\u5417\uff1f\u201d\u9752\u51e4\u9053\uff1a\u201c\u5c31\u7b97\u6211\u81ea\u7518\u4e0b\u8d31\u5427\uff01\u4f46\u96ea\u3001\u971c\u4e8c\u4f4d\u59b9\u59b9\u5462\uff1f\u9605\u4eba\u591a\u77e3\u7684\u6885\u82b1\u5462\uff1f\u975e\u672f\u60d1\u4eba\uff0c\u5c82\u80fd\u5982\u6b64\u3002 \u3000\u3000\u518d\u8bf4\u5427\uff0c\u4f60\u4e0d\u65bd\u5c55\u2018\u60d1\u5fc3\u672f\u2019\u65f6\uff0c\u4f60\u5468\u56f4\u7684\u5973\u4eba\uff0c\u4e0d\u662f\u90fd\u80fd\u81ea\u6301\u5206\u5bf8\uff0c\u548c\u4f60\u5766\u7136\u76f8\u5904\u5417\uff1f \u3000\u3000\u6c5f\u90ce\uff0c\u8fd9\u90fd\u662f\u5f20\u59e8\u8981\u6211\u8bf4\u7684\u8bdd\uff0c\u6211\u4eec\u90fd\u77e5\u9053\u4f60\u4e0d\u662f\u4e2a\u597d\u8272\u7684\u4eba\uff0c\u53ef\u662f\uff0c\u5927\u5c40\u4e3a\u91cd\u554a\uff01\u201d \u3000\u3000\u6c5f\u67ab\u957f\u957f\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u597d\u5427\uff01\u7b97\u4f60\u6709\u7406\u3002\u201d \u3000\u3000\u9752\u51e4\u91cd\u91cd\u4eb2\u4e86\u6c5f\u67ab\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u73b0\u5728\uff0c\u6211\u4eec\u653e\u706b\uff0c\u7136\u540e\uff0c\u53bb\u627e\u84dd\u51e4\uff0c\u53ea\u8981\u5979\u4e00\u5165\u60c5\u7f51\uff0c\u4e09\u9b54\u3001\u4e94\u602a\u3001\u56db\u5927\u5929\u9e45\uff0c\u90fd\u4f1a\u4e3a\u4f60\u6548\u547d\u4e86\u3002\u201d \u3000\u3000\u201c\u5929\u738b\u5462\uff1f\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u4e00\u65e6\u88ab\u4ed6\u53d1\u89c9\uff0c\u5c82\u4e0d\u662f\u5bb3\u4e86\u84dd\u51e4\uff1f\u201d \u3000\u3000\u201c\u5509\uff01\u4f60\u662f\u5f53\u771f\u591a\u60c5\uff0c\u84dd\u51e4\u662f\u4e2a\u5f88\u4f1a\u8ba1\u7b97\u7684\u4eba\u2026\u2026\u201d\u9752\u51e4\u9053\uff1a\u201c\u4e0d\u7528\u62c5\u5fc3\uff0c\u5230\u4e86\u5371\u9669\u65f6\u523b\uff0c\u5979\u53ef\u4ee5\u8dd1\u6765\u627e\u4f60\u5440\uff01\u201d \u3000\u3000\u6c5f\u67ab\u82e6\u7b11\u3002 \u3000\u3000\u9752\u51e4\u53d6\u51fa\u706b\u79cd\uff0c\u9009\u4e86\u98ce\u5411\uff0c\u4e00\u9762\u653e\u706b\uff0c\u4e00\u9762\u8bf4\u9053\uff1a\u201c\u672a\u89c1\u84dd\u51e4\u4e4b\u524d\uff0c\u5c0f\u5fc3\u70b9\u4fdd\u62a4\u6211\u554a\uff01 \u3000\u3000\u4e00\u65e6\u6211\u88ab\u4eba\u6740\u4e86\uff0c\u4f60\u8fd9\u526f\u731b\u836f\uff0c\u5c31\u7f3a\u4e86\u836f\u5f15\u4e86\uff0c\u6709\u8d1f\u5f20\u59e8\u6240\u6258\u4e86\u3002\u201d \u3000\u3000\u201c\u770b\u6765\uff0c\u4f60\u4eec\u5bf9\u5927\u59d0\u90fd\u5f88\u4f9d\u604b\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u5f20\u59e8\u50cf\u5927\u59d0\uff0c\u4e5f\u50cf\u5988\u5988\uff0c\u6211\u5bf9\u5979\u53c8\u656c\u53c8\u7231\uff0c\u65e9\u5c31\u5546\u91cf\u8fc7\u4e86\uff0c\u4e00\u5b9a\u8981\u628a\u5f20\u59e8\u7559\u4f4f\u2026\u2026\u201d\u9752\u51e4\u9053\uff1a\u201c\u975e\u5f20\u59e8\u4e4b\u80fd\uff0c\u65e0\u6cd5\u7edf\u7387\u4f60\u7684\u4e09\u5bab\u516d\u9662\uff0c\u518d\u8bf4\u561b\uff01\u6709\u4e2a\u7687\u540e\u538b\u5728\u4f60\u5934\u4e0a\u4e5f\u597d\uff0c\u8bf7\u4f60\u5fc3\u5b58\u987e\u5fcc\uff0c\u4e0d\u6562\u559c\u65b0\u538c\u65e7\u554a\uff01\u201d \u3000\u3000\u6c5f\u67ab\u65e0\u6cd5\u56de\u7b54\uff0c\u53ea\u597d\u5e2e\u9752\u51e4\u70b9\u706b\u3002 \u3000\u3000\u9752\u8349\u6742\u6811\uff0c\u672c\u4e0d\u6613\u71c3\uff0c\u4f46\u4e00\u65e6\u70e7\u8d77\uff0c\u70df\u718f\u6c41\u51fa\uff0c\u53cd\u6709\u52a9\u957f\u706b\u52bf\u4e4b\u529b\uff0c\u5927\u706b\u56db\u5904\u8513\u5ef6\uff0c\u731b\u6076\u65e0\u6bd4\u3002 \u3000\u3000\u9752\u51e4\u65e9\u5df2\u770b\u597d\u51fa\u8def\uff0c\u7275\u4f4f\u6c5f\u67ab\u4e00\u53cc\u624b\uff0c\u5954\u9003\u800c\u51fa\u3002 \u3000\u3000\u9752\u51e4\u5df2\u5b66\u4f1a\u4e86\u5f20\u56db\u59d1\u7684\u9041\u672f\uff0c\u867d\u7136\u6ca1\u6709\u5e26\u9053\u5177\uff0c\u4f46\u80fd\u501f\u7269\u9690\u5f62\uff0c\u4e00\u8def\u4e0a\u867d\u7136\u9047\u4e0a\u4e86\u5f88\u591a\u5929\u9a6c\u5802\u4e2d\u7684\u5de1\u9a91\u54e8\u6869\uff0c\u4f46\u4e24\u4eba\u6ee1\u9762\u5c18\u571f\uff0c\u8863\u7740\u6c61\u57a2\uff0c\u5f88\u50cf\u5954\u9003\u7684\u6751\u4eba\u3001\u730e\u6237\uff0c\u8fd9\u91cc\u867d\u7136\u5c45\u6c11\u5f88\u5c11\uff0c\u4f46\u730e\u4eba\u5f88\u591a\uff0c\u519c\u5bb6\u4e5f\u6709\u51e0\u6237\u3002 \u3000\u3000\u4e24\u4eba\u7559\u5fc3\u89c2\u5bdf\uff0c\u53d1\u89c9\u5de1\u9a91\u5f80\u8fd4\uff0c\u4eba\u5f71\u6d41\u52a8\uff0c\u8fd9\u628a\u706b\u53ef\u80fd\u70e7\u53bb\u4e86\u4e07\u9877\u8352\u539f\u4e2d\u6742\u6811\u4e71\u8349\uff0c\u4f46\u4e5f\u70e7\u4e71\u4e86\u5929\u9a6c\u5802\u7684\u5e03\u7f6e\u9635\u52bf\u3002 \u3000\u3000\u4e00\u53e3\u6c14\u8d70\u51fa\u4e86\u56db\u4e94\u5341\u91cc\uff0c\u9752\u51e4\u624d\u505c\u4e0b\u6765\uff0c\u9053\uff1a\u201c\u679c\u7136\u662f\u8c8c\u5408\u795e\u79bb\uff0c\u5929\u738b\u95e8\u6ca1\u6709\u5fc3\u5b58\u5e2e\u5fd9\u7684\u6253\u7b97\uff0c\u6240\u4ee5\uff0c\u624d\u8eb2\u5728\u8fd9\u4e48\u8fdc\u7684\u5730\u65b9\uff01\u201d \u3000\u3000\u201c\u4e0a\u4e00\u6b21\u4e07\u6885\u576a\u4e4b\u6218\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u5929\u738b\u95e8\u4e34\u9635\u89c2\u6218\uff0c\u8896\u624b\u4e0d\u7ba1\uff0c\u4e8b\u540e\uff0c\u84dd\u51e4\u6ca1\u6709\u53d7\u5230\u8d23\u96be\u5417\uff1f\u201d \u3000\u3000\u201c\u5f53\u65f6\uff0c\u95e8\u4e3b\u4e5f\u5728\u73b0\u573a\u2026\u2026\u201d\u9752\u51e4\u9053\uff1a\u201c\u5750\u5c71\u89c2\u864e\u6597\uff0c\u662f\u95e8\u4e3b\u51b3\u5b9a\u7684\uff0c\u5982\u679c\u4f60\u4eec\u9732\u51fa\u8d25\u8c61\uff0c\u6211\u4eec\u5c31\u4f1a\u5168\u529b\u53c2\u6218\uff0c\u4e0d\u80fd\u8ba9\u5929\u540e\u5bab\u72ec\u9886\u98ce\u9a9a\uff0c\u90a3\u65f6\uff0c\u6211\u597d\u62c5\u5fc3\u54ea\uff01\u5e78\u597d\u4f60\u4eec\u4e00\u5f00\u59cb\u5c31\u529b\u632b\u5f3a\u654c\uff0c\u5929\u738b\u95e8\u5c31\u89c1\u98ce\u8f6c\u8235\uff0c\u8d81\u9ed1\u64a4\u8d70\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\uff0c\u7ec8\u5357\u5c71\u4e2d\u4e00\u6218\uff0c\u5929\u738b\u95e8\u4eec\u662f\u5341\u5206\u5356\u529b\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u5929\u738b\u95e8\u3001\u5929\u540e\u5bab\u3001\u5929\u9a6c\u5802\uff0c\u8054\u624b\u51fa\u51fb\uff0c\u542c\u5f20\u5927\u59d0\u8bf4\uff0c\u5929\u738b\u95e8\u4e00\u9762\u7684\u653b\u52bf\u6700\u4e3a\u51cc\u5389\u3002\u201d \u3000\u3000\u201c\u90a3\u4e0d\u540c\u554a\uff01\u7ec8\u5357\u5c71\u4e2d\u4e4b\u6218\uff0c\u662f\u7531\u5929\u738b\u95e8\u8d1f\u8d23\u8c03\u52a8\u5168\u5c40\uff0c\u2026\u2026\u201d \u3000\u3000\u9752\u51e4\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u5929\u540e\u5bab\u548c\u5929\u9a6c\u5802\u5e76\u6ca1\u6709\u51fa\u52a8\u592a\u591a\u7684\u9ad8\u624b\uff0c\u5929\u738b\u95e8\u5374\u51fa\u52a8\u95e8\u4e2d\u7cbe\u9510\uff0c\u4e5f\u903c\u5f97\u6211\u4e0d\u5f97\u4e0d\u9635\u524d\u5012\u6208\uff0c\u4e3a\u592b\u62fc\u547d\u2026\u2026\u201d \u3000\u3000\u201c\u8fd9\u4ef6\u4e8b\uff0c\u6211\u771f\u7684\u5341\u5206\u611f\u6fc0\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u8fd9\u4e00\u6b21\u662f\u592d\u9a6c\u5802\u8d1f\u8d23\u603b\u5176\u6210\u8d25\uff0c\u6240\u4ee5\uff0c\u5929\u9a6c\u5802\u4e5f\u662f\u9ad8\u624b\u5c3d\u51fa\u4e86\uff01\u201d \u3000\u3000\u201c\u8fd9\u6b21\u6709\u4e9b\u4e0d\u540c\uff0c\u5929\u9a6c\u5802\u53ea\u662f\u524d\u950b\u2026\u2026\u201d\u9752\u51e4\u9053\uff1a\u201c\u597d\u50cf\u662f\u5929\u738b\u95e8\u3001\u5929\u540e\u5bab\uff0c\u5168\u90fd\u503e\u5de2\u800c\u6765\uff0c\u5927\u6709\u6700\u540e\u4e00\u51b3\u7684\u7528\u5fc3\uff0c\u6c5f\u90ce\uff0c\u6211\u4e0d\u77e5\u9053\u4f60\u548c\u96ea\u3001\u971c\u4e24\u4f4d\u59b9\u59b9\uff0c\u7ec3\u4e86\u4ec0\u4e48\u9ad8\u660e\u7684\u6b66\u529f\uff0c\u4f46\u53ea\u6709\u4e00\u767e\u591a\u5929\u554a\uff01 \u3000\u3000\u80fd\u6709\u591a\u5927\u6210\u5c31\u5462\uff1f \u3000\u3000\u8fd9\u4e00\u6218\uff0c\u4e0d\u662f\u4e5d\u6b7b\u4e00\u751f\uff0c\u800c\u662f\u5168\u65e0\u673a\u4f1a\uff0c\u5f20\u59e8\u867d\u7136\u80f8\u85cf\u97ec\u7565\uff0c\u4f46\u4e5f\u5f88\u96be\u7834\u56f4\u800c\u51fa\uff0c\u53ea\u662f\uff0c\u6211\u4e0d\u6562\u8bf4\u51fa\u6765\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u5fae\u5fae\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u9752\u98ce\uff0c\u4e0d\u8981\u90a3\u4e48\u60b2\u89c2\uff0c\u770b\u4f60\u90a3\u526f\u7edd\u671b\u795e\u60c5\uff0c\u5b9e\u5728\u53eb\u4eba\u5fc3\u75bc\u2026\u2026\u201d \u3000\u3000\u201c\u7eb5\u662f\u8c0e\u8a00\u4e5f\u7f8e\u4e3d\uff0c\u6709\u6b64\u4e00\u8a00\uff0c\u59be\u8eab\u6b7b\u800c\u65e0\u61be\u2026\u2026\u201d\u9752\u51e4\u9053\uff1a\u201c\u53ef\u60dc\u7684\u662f\uff0c\u6211\u6ca1\u6709\u673a\u4f1a\uff0c\u66ff\u4f60\u517b\u4e2a\u5c0f\u5b9d\u5b9d\u4e86\uff0c\u4eab\u53d7\u4e00\u4e0b\uff0c\u5929\u4f26\u4e50\u8da3\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u76f8\u4fe1\u8fd9\u4e2a\u673a\u4f1a\u5f88\u5927\uff0c\u6240\u4ee5\uff0c\u4f60\u8981\u4fdd\u91cd\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u9047\u4e0a\u8d85\u5f3a\u7684\u9ad8\u624b\uff0c\u6253\u4e0d\u8fc7\u4eba\u5bb6\u5c31\u7acb\u523b\u9003\u8d70\uff0c\u5343\u4e07\u4e0d\u53ef\u901e\u5f3a\u2026\u2026\u201d \u3000\u3000\u201c\u5f20\u59e8\u7684\u6b66\u529f\u3001\u5947\u672f\uff0c\u8db3\u4ee5\u88ad\u6740\uff0c\u81ea\u4fdd\u4e3a\u4e3b\u2026\u2026\u201d\u9752\u51e4\u9053\uff1a\u201c\u6211\u81ea\u4fe1\u5df2\u6709\u5fc3\u5f97\uff0c\u4f60\u4e5f\u8981\u5c0f\u5fc3\u554a\uff01\u6211\u5982\u6218\u6b7b\u4e86\uff0c\u90a3\u5c31\u4e00\u4e86\u767e\u4e86\uff0c\u5e78\u800c\u4e0d\u6b7b\uff0c\u6211\u53ef\u4e0d\u613f\u4f5c\u5be1\u5987\uff0c\u90a3\u79cd\u9b42\u7275\u68a6\u8426\u7684\u75db\u82e6\uff0c\u751f\u800c\u4f55\u6b22\uff0c\u6211\u76f8\u4fe1\u6885\u82b1\u4e5f\u6709\u8fd9\u79cd\u60f3\u6cd5\uff0c\u6240\u4ee5\uff0c\u4f60\u8981\u597d\u597d\u7684\u7ed9\u6211\u6d3b\u7740\u3002\u201d \u3000\u3000\u6c5f\u67ab\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u6709\u673a\u4f1a\u8f6c\u544a\u6885\u82b1\uff0c\u5c31\u8bf4\u6211\u4e0d\u51c6\u5979\u6b7b\uff01\u201d \u3000\u3000\u201c\u6211\u4f1a\u8f6c\u544a\u5979\uff0c\u4e8b\u5b9e\u4e0a\uff0c\u6211\u4eec\u5408\u4f5c\u5fa1\u654c\uff0c\u6218\u9635\u4e4b\u4e0a\uff0c\u5e94\u662f\u5bf8\u6b65\u4e0d\u79bb\uff0c\u73b0\u5728\uff0c\u627e\u5730\u65b9\u6d17\u4e2a\u8138\uff0c\u6362\u8eab\u8863\u670d\uff0c\u6211\u5e26\u4f60\u53bb\u89c1\u84dd\u51e4\uff0c\u597d\u597d\u7684\u6293\u4f4f\u5979\uff0c\u5979\u662f\u4e2a\u624d\u5973\uff0c\u6709\u5f20\u59e8\u548c\u541f\u96ea\u7684\u624d\u80fd\uff0c\u5979\u5982\u679c\u6295\u541b\u6000\u62b1\uff0c\u7518\u4e3a\u60c5\u7275\u2026\u2026\u201d\u9752\u51e4\u9053\uff1a\u201c\u5929\u738b\u95e8\u7684\u529b\u91cf\uff0c\u5c31\u5168\u4e3a\u6211\u7528\u4e86\uff0c\u5929\u738b\u95e8\u4e3b\u5b09\u6e38\u6210\u6027\uff0c\u95e8\u4e2d\u4e8b\u52a1\u5168\u7531\u84dd\u51e4\u5305\u529e\uff0c\u5927\u6743\u5728\u63e1\uff0c\u4ee4\u51fa\u5982\u5c71\uff0c\u5c55\u541b\u6240\u957f\uff0c\u597d\u597d\u53bb\u53d1\u6325\u4e00\u756a\u5427\uff01\u201d \u3000\u3000\u6c5f\u67ab\u611f\u89c9\u5230\u8138\u4e0a\u53d1\u70e7\uff0c\u6025\u6025\u8f6c\u8fc7\u8bdd\u9898\uff0c\u9053\uff1a\u201c\u8fd9\u4e48\u770b\u8d77\u6765\uff0c\u786e\u6709\u4e00\u53cc\u624b\uff0c\u5728\u6697\u4e2d\u63a7\u5236\u7740\u4e09\u4e2a\u6c5f\u6e56\u4e0a\u6700\u5927\u7684\u7ec4\u5408\u529b\u91cf\uff0c\u90a3\u4e2a\u4eba\u53c8\u662f\u8c01\u5462\uff1f\u201d \u3000\u3000\u201c\u53bb\u95ee\u84dd\u51e4\u5427\uff01\u5979\u80f8\u4e2d\u6240\u77e5\uff0c\u591a\u6211\u5341\u500d\u3002\u201d\u9752\u51e4\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u5979\u662f\u4e2a\u975e\u5e38\u667a\u6167\u3001\u51b7\u9759\u7684\u4eba\uff0c\u4e0d\u8981\u592a\u5927\u610f\u4e86\u3002\u201d \u3000\u3000\u9752\u51e4\u4ee5\u5929\u738b\u95e8\u4e2d\u7684\u7d27\u6025\u4fe1\u53f7\uff0c\u6c42\u89c1\u84dd\u51e4\uff0c\u7acb\u523b\u5c31\u88ab\u4eba\u63a5\u5165\u4e86\u4e00\u5ea7\u5927\u5b85\u9662\u4e2d\u3002 \u3000\u3000\u84dd\u51e4\u5728\u7b2c\u4e09\u8fdb\u9662\u843d\u5185\u4e00\u95f4\u96be\u5ba4\u4e2d\u63a5\u89c1\u4e24\u4eba\uff0c\u7ea6\u7565\u5730\u6253\u91cf\u4e86\u4e24\u4eba\u4e00\u773c\uff0c\u6325\u6325\u624b\uff0c\u793a\u610f\u4ece\u536b\u5973\u5a62\uff0c\u5168\u90e8\u9000\u4e0b\uff0c\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u9752\u51e4\uff0c\u62ff\u4e0b\u4eba\u6b21\u9762\u5177\u5427\uff01\u79fb\u82b1\u624b\u7684\u6280\u827a\u867d\u7136\u7cbe\u6e5b\uff0c\u4f46\u8fd8\u65e0\u6cd5\u6539\u53d8\u4f60\u4e00\u5bf9\u773c\u795e\u3002\u201d \u3000\u3000\u679c\u7136\u662f\u5389\u5bb3\u89d2\u8272\u3002 \u3000\u3000\u6c5f\u67ab\u6697\u6697\u5fd6\u9053\uff1a\u201c\u771f\u8981\u5c0f\u5fc3\u70b9\u5e94\u4ed8\u624d\u884c\u3002\u201d \u3000\u3000\u9752\u51e4\u53d6\u4e0b\u4eba\u76ae\u9762\u5177\uff0c\u9053\uff1a\u201c\u6211\u77e5\u9053\u7792\u4e0d\u8fc7\u4f60\uff0c\u4f46\u4e0d\u5982\u6b64\uff0c\u5f88\u96be\u89c1\u5230\u4f60\uff0c\u84dd\u51e4\uff0c\u6211\u662f\u9053\u8c22\u6765\u7684\uff0c\u4e0d\u662f\u4f60\u653e\u4e86\u4e00\u9a6c\uff0c\u6211\u5df2\u7ecf\u7406\u9aa8\u5728\u7ec8\u5357\u5c71\u4e86\uff01\u201d \u3000\u3000\u201c\u7b97\u4e86\u2026\u2026\u201d\u84dd\u51e4\u9053\uff1a\u201c\u60c5\u540c\u9aa8\u8089\u7684\u597d\u59d0\u59b9\u561b\uff01\u5e94\u8be5\u5728\u4f60\u5371\u96be\u4e2d\uff0c\u62c9\u4f60\u4e00\u628a\uff0c\u4e0d\u8fc7\uff0c\u90a3\u4e00\u6b21\uff0e\u5df2\u7ecf\u7528\u5c3d\u4e86\u6211\u4eec\u4e4b\u95f4\u7684\u60c5\u5206\uff0c\u5e0c\u671b\u4f60\u4e0d\u4f1a\u518d\u4f7f\u6211\u4e3a\u96be\u3002 \u3000\u3000\u9752\u51e4\uff0c\u4eba\u8d35\u81ea\u7acb\uff0c\u6211\u4e0d\u80fd\u518d\u5e2e\u4f60\u4ec0\u4e48\u4e86\u3002\u6211\u4eec\u7684\u60c5\u5206\u5df2\u5c3d\uff0c\u4f60\u8d70\u5427\uff01\u4e00\u4e2a\u65f6\u8fb0\u4e4b\u5185\uff0c\u6211\u4e0d\u4f1a\u4e0b\u4ee4\u622a\u51fb\uff0c\u5e0c\u671b\u4f60\u8dd1\u5f97\u5feb\u4e00\u4e9b\uff0c\u4ee5\u4f60\u7684\u6b66\u529f\uff0c\u8fd9\u6837\u957f\u65f6\u95f4\uff0e\u5e94\u8be5\u8dd1\u5f97\u5f88\u8fdc\u4e86\u3002\u201d \u3000\u3000\u8bdd\u8bf4\u7684\u867d\u7136\u5a49\u8f6c\uff0c\u4f46\u5374\u5341\u5206\u51b3\u7edd\uff0c\u4f3c\u5df2\u65e0\u8f6c\u53d8\u7684\u4f59\u5730\u3002 \u3000\u3000\u201c\u84dd\u51e4\uff0c\u6211\u4e0d\u4f1a\u518d\u8981\u6c42\u4ec0\u4e48\uff1f\u6211\u5df2\u5fc3\u6ee1\u610f\u8db3\uff0c\u4ea4\u4e86\u4f60\u8fd9\u6837\u4e00\u4e2a\u597d\u59d0\u59b9\uff0c\u6211\u662f\u5e26\u4eba\u7ed9\u4f60\u2026\u2026\u201d \u3000\u3000\u84dd\u51e4\u7684\u53cd\u5e94\u5947\u5feb\uff0c\u8138\u8272\u4e00\u53d8\uff0c\u63a5\u9053\uff1a\u201c\u6c5f\u67ab\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u5df2\u53d6\u4e0b\u4eba\u76ae\u9762\u5177\uff0c\u63a5\u9053\uff1a\u201c\u6b63\u662f\u533a\u533a\uff0c\u9752\u51e4\u8d5e\u9882\u59d1\u5a18\u624d\u9ad8\u516b\u5341\uff0c\u5b66\u5bcc\u4e94\u8f66\uff0c\u6c5f\u67d0\u6e34\u6155\u5f97\u5f88\uff0c\u56e0\u6b64\uff0c\u5192\u7740\u5343\u5371\u4e07\u9669\u800c\u6765\uff0c\u4e00\u7779\u6587\u91c7\u59ff\u5bb9\u2026\u2026\u201d \u3000\u3000\u201c\u5c11\u901e\u53e3\u820c\u4e4b\u5229\uff0c\u2026\u2026\u201d\u84dd\u51e4\u51b7\u51b7\u63a5\u9053\uff1a\u201c\u4f60\u4eec\u653e\u628a\u706b\u70e7\u4e86\u8349\u539f\uff0c\u7528\u5fc3\u5728\u963b\u65ad\u7075\u72ac\u8ffd\u8e2a\uff0c\u53ea\u6015\u662f\u767d\u8d39\u5fc3\u673a\u4e86\uff0c\u6211\u548c\u9752\u51e4\u4e0d\u540c\uff0c\u4e0d\u559c\u6b22\u751c\u8a00\u871c\u8bed\uff0c\u4f60\u60f3\u89c1\u89c1\u6211\uff0c\u5df2\u7ecf\u89c1\u8fc7\u4e86\uff0c\u4f60\u4e5f\u53ef\u4ee5\u8d70\u4e86\uff0c\u770b\u5728\u9752\u51e4\u7684\u9762\u4e0a\uff0c\u6211\u653e\u8fc7\u4f60\u8fd9\u4e00\u6b21\uff0c\u4e24\u4f4d\u8bf7\u5427\uff01\u201d \u3000\u3000\u201c\u4e0d\u7528\u5ba2\u6c14\u2026\u2026\u201d\u9752\u51e4\u9053\uff1a\u201c\u62ff\u4e0b\u4ed6\u662f\u4e00\u4ef6\u5929\u5927\u7684\u529f\u52b3\uff0c\u6211\u628a\u4eba\u5e26\u6765\u4e86\uff0c\u62a5\u507f\u4f60\u7ec8\u5357\u5c71\u7f51\u5f00\u4e00\u9762\u4e4b\u60c5\uff0c\u8981\u6740\u4ed6\u3001\u5250\u4ed6\uff0c\u4efb\u51ed\u4f60\u4f5c\u4e3b\uff0c\u6211\u8bf4\u8fc7\u5e26\u4ed6\u89c1\u4f60\uff0c\u4e5f\u7b97\u5c65\u884c\u4e86\u8bfa\u8a00\uff0c\u6211\u8d70\u4e86\uff0c\u84dd\u51e4\uff0c\u5e0c\u671b\u6211\u4eec\u4f1a\u6709\u7f18\u518d\u89c1\u3002\u201d \u3000\u3000\u8fd9\u4e00\u9635\u8bf4\u8bdd\u7684\u5de5\u592b\uff0c\u9752\u51e4\u5df2\u5e26\u4e0a\u4e86\u4eba\u76ae\u9762\u5177\uff0c\u8f6c\u8eab\u4e00\u8dc3\uff0c\u98de\u51fa\u623f\u95e8\u3002 \u3000\u3000\u201c\u84dd\u51e4\uff0c\u4f60\u5e94\u8be5\u660e\u767d\uff0c\u5c31\u7b97\u53ec\u9f50\u4e86\u4e09\u9b54\u3001\u4e94\u602a\uff0c\u4e5f\u672a\u5fc5\u80fd\u7559\u5f97\u4e0b\u6211\u2026\u2026\u201d\u6c5f\u67ab\u8bed\u6c14\u5e73\u9759\u5730\u8bf4\uff0c\u201c\u6211\u65e0\u6076\u610f\uff0c\u5f53\u7136\uff0c\u4e5f\u975e\u65e0\u4e3a\u800c\u6765\u2026\u2026\u201d \u3000\u3000\u201c\u90a3\u662f\u51c6\u5907\u751f\u64d2\u6211\u4f5c\u4eba\u8d28\u4e86\uff0c\u544a\u8bc9\u4f60\uff0c\u5929\u738b\u95e8\u4e2d\u6ca1\u6709\u79c1\u4eba\u60c5\u610f\u53ef\u8a00\uff0c\u6211\u84dd\u51e4\u5728\u8fd9\u91cc\uff0c\u5927\u6743\u5728\u63e1\uff0c\u6211\u6d88\u5931\u4e86\uff0c\u4ed6\u4eec\u518d\u627e\u4e00\u4e2a\u4eba\u8865\u4e0a\u6211\u7684\u4f4d\u7f6e\u3002 \u3000\u3000\u6211\u6551\u4e86\u9752\u51e4\u4e00\u6b21\uff0c\u90a3\u53ea\u662f\u4ef6\u7279\u6b8a\u4e8b\u4f8b\uff0c\u4f55\u51b5\uff0c\u5c31\u7b97\u6211\u975e\u4f60\u4e4b\u654c\uff0c\u4f46\u4e09\u4e94\u5341\u62db\uff0c\u603b\u53ef\u4ee5\u63a5\u5f97\u4e0b\u6765\uff0c\u4e0d\u76f8\u4fe1\uff0c\u4f60\u5c31\u52a8\u624b\u8bd5\u8bd5\uff1f\u201d \u3000\u3000\u5979\u795e\u60c5\u51b7\u8273\uff0c\u76ee\u5149\u522b\u987e\uff0c\u4f3c\u662f\u61d2\u5f97\u518d\u770b\u6c5f\u67ab\u4e00\u773c\u3002 \u3000\u3000\u201c\u59d1\u5a18\u662f\u7edd\u4e16\u9ad8\u624b\uff0c\u5f53\u7136\u4f1a\u660e\u767d\uff0c\u771f\u6b63\u6740\u4eba\u7684\u6b66\u529f\uff0c\u53ea\u662f\u51fa\u624b\u4e00\u51fb\uff0c\u5f81\u6218\u6570\u5341\u5408\uff0c\u8fc7\u624b\u51e0\u767e\u62db\uff0c\u90a3\u662f\u4ee5\u6b66\u4f1a\u53cb\u7684\u65b9\u5f0f\u2026\u2026\u201d \u3000\u3000\u201c\u4f60\u662f\u8bf4\u6211\u4e0d\u582a\u4e00\u51fb\u4e86\u2026\u2026\u201d\u84dd\u51e4\u9053\uff1a\u201c\u5f53\u771f\u662f\u53e3\u6c14\u6258\u5927\uff0c\u53ef\u6076\u7684\u5f88\uff0c\u6eda\u51fa\u53bb\uff0c\u6211\u4e0d\u60f3\u518d\u770b\u5230\u4f60\uff01\u201d \u3000\u3000\u6c5f\u67ab\u88ab\u9a82\u4eba\u4e86\uff0c\u4e5f\u6fc0\u8d77\u4e86\u5f81\u670d\u5979\u7684\u96c4\u5fc3\uff0c\u65bd\u5c55\u51fa\u2018\u60d1\u5fc3\u672f\u2019\uff0c\u8138\u4e0a\u7acb\u523b\u6d6e\u73b0\u51fa\u8ff7\u4eba\u7684\u7b11\u610f\uff0c\u9053\uff1a\u201c\u84dd\u51e4\u59d1\u5a18\u8bef\u4f1a\u4e86\uff0c\u5728\u4e0b\u53ea\u60f3\u8bf4\u7406\uff0c\u8868\u8fbe\u51fa\u4e00\u4efd\u60c5\u610f\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u7528\u8bf4\u9053\u7406\uff0c\u6211\u8bfb\u8fc7\u4e07\u5377\u4e66\uff0c\u2026\u2026\u201d\u84dd\u51e4\u6253\u65ad\u4e86\u6c5f\u67ab\u7684\u8bdd\uff0c\u63a5\u9053\uff1a\u201c\u6df1\u660e\u9053\u7406\uff0c\u4e0d\u8fc7\uff0c\u6709\u4e9b\u5730\u65b9\u4e0d\u9002\u7528\uff0c\u4f60\u60f3\u8868\u8fbe\u51fa\u4e00\u4efd\u60c5\u610f\uff0c\u66f4\u662f\u8bf4\u7684\u8352\u5510\uff0c\u6211\u4eec\u7d20\u65e0\u6765\u5f80\uff0c\u6709\u4ec0\u4e48\u60c5\u610f\u53ef\u8a00\uff1f \u3000\u3000\u9752\u51e4\u8bf4\uff0c\u4f60\u6709\u53e9\u52a8\u5973\u4eba\u5fc3\u5f26\u7684\u9b45\u529b\uff0c\u8ba9\u4eba\u4e00\u89c1\u949f\u60c5\uff0c\u6211\u53ea\u662f\u89c9\u7740\u597d\u5947\u7f62\u4e86\uff0c\u624d\u7b54\u5e94\u5979\u89c1\u4f60\u4e00\u9762\u3002 \u3000\u3000\u6211\u4e0d\u76f8\u4fe1\u4e16\u4eba\u4f1a\u6709\u8fd9\u6837\u7684\u7537\u4eba\uff0c\u4e0a\u4e00\u6b21\u5728\u4e07\u6885\u576a\u770b\u5230\u4f60\uff0c\u77a7\u4e0d\u51fa\u6709\u4ec0\u4e48\u7279\u522b\u7684\u5730\u65b9\uff0c\u8fd9\u4e00\u6b21\u518d\u89c1\u4f60\uff0c\u66f4\u6709\u7740\u4e0d\u8fc7\u5982\u6b64\u7684\u611f\u89c9\u3002 \u3000\u3000\u6211\u8981\u7537\u4eba\uff0c\u53ef\u4ee5\u7531\u6210\u5343\u4e0a\u4e07\u7684\u7537\u4eba\u53bb\u6311\u9009\uff0c\u4f60\u54ea\u91cc\u6bd4\u522b\u4eba\u5f3a\u4e86\uff0c\u9752\u51e4\u662f\u778e\u4e86\u773c\uff0c\u7adf\u4e3a\u4f60\u8fd9\u6837\u4e00\u4e2a\u7537\u4eba\u2026\u2026\u201d \u3000\u3000\u76ee\u5149\u8f6c\u6ce8\u5230\u6c5f\u67ab\u7684\u8138\u4e0a\uff0c\u7a81\u7136\u4e3a\u4e4b\u4e00\u5446\uff0c\u4f4f\u53e3\u4e0d\u8a00\u3002 \u3000\u3000\u5979\u4e0d\u8be5\u770b\u8fd9\u4e00\u773c\u7684\uff0c\u8fd9\u4e00\u770b\uff0c\u770b\u51fa\u4e86\u5927\u6bdb\u75c5\u6765\uff0c\u6709\u5982\u78c1\u77f3\u9047\u94c1\uff0c\u7acb\u523b\u88ab\u5438\u5f15\u4f4f\u4e86\u3002 \u3000\u3000\u53ea\u89c9\u6c5f\u67ab\u8138\u4e0a\u7684\u5fae\u7b11\u9b45\u529b\u56db\u5c04\uff0c\u52a8\u4eba\u5fc3\u5f26\uff0c\u6d2a\u6d41\u5954\u6765\uff0c\u83ab\u53ef\u5fa1\u4e4b\uff0c\u53cc\u76ee\u4e2d\u67d4\u60c5\u5982\u7f51\uff0c\u4e00\u4e0b\u5b50\u7f51\u4f4f\u4e86\u84dd\u51e4\u59d1\u5a18\u7684\u4e00\u5bf8\u82b3\u5fc3\u3002 \u3000\u3000\u4e56\u4e56\uff01\u8fd9\u4e0d\u662f\u7231\u60c5\uff0c\u800c\u662f\u767e\u70bc\u7cbe\u94a2\uff0c\u65e0\u575a\u4e0d\u6467\u7684\u4e00\u628a\u5229\u5251\uff0c\u6d1e\u900f\u4e94\u8151\uff0c\u6355\u4eba\u795e\u9b42\uff0c\u50cf\u6c89\u5728\u74ee\u5e95\u7684\u767e\u5e74\u8001\u9152\uff0c\u559d\u4e00\u53e3\uff0c\u5c31\u8981\u4f60\u9189\u5367\u68a6\u4e61\u3002 \u3000\u3000\u84dd\u51e4\u9676\u9189\u4e86\u3002 \u3000\u3000\u201c\u662f\u771f\u7684\uff01\u9752\u51e4\u6ca1\u6709\u9a97\u6211\uff0c\u4f60\u2026\u2026\u4f60\u662f\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u662f\u6c5f\u67ab\uff0c\u5927\u6c5f\u4e4b\u6c5f\uff0c\u67ab\u53f6\u4e4b\u67ab\u3002\u201d \u3000\u3000\u201c\u6ee1\u5c71\u67ab\u53f6\u7ea2\uff0c\u5c3d\u662f\u79bb\u4eba\u6cea\u2026\u2026\u201d\u84dd\u51e4\u6709\u4e9b\u832b\u7136\u5730\u8bf4\uff0c\u201c\u592a\u4f24\u611f\u4e86\uff0c\u6211\u6240\u4e0d\u53d6\uff0c\u6211\u8981\u8584\u547d\u601c\u537f\u7518\u4f5c\u59be\uff0c\u60c5\u6df1\u5b81\u6258\u8d1f\u5fc3\u4eba\u3002\u201d \u3000\u3000\u5979\u679c\u662f\u4e2a\u624d\u5973\uff0c\u51fa\u53e3\u6210\u7ae0\uff0c\u800c\u4e14\u8868\u8fbe\u51fa\u4e86\u5fc3\u4e2d\u7684\u611f\u89c9\u3002 \u3000\u3000\u6c5f\u67ab\u4ed4\u7ec6\u7684\u6253\u91cf\u4e86\u84dd\u98ce\u4e00\u9635\uff0c\u8bba\u59ff\u8272\uff0c\u5979\u4e0d\u7b97\u662f\u6f02\u4eae\u5973\u4eba\uff0c\u4e0d\u5982\u9752\u51e4\u3001\u6885\u82b1\u591a\u77e3\uff01\u4f46\u5979\u7709\u76ee\u95f4\u7075\u6c14\u8feb\u4eba\uff0c\u653e\u5c04\u51fa\u667a\u6167\u4e4b\u5149\uff0c\u662f\u4e00\u79cd\u4e0d\u4ee5\u59ff\u8272\u53d6\u80dc\uff0c\u522b\u5177\u79c0\u6167\u97f5\u5473\u7684\u4eba\uff0c\u5982\u9752\u677e\u7fe0\u7af9\uff0c\u867d\u6ca1\u6709\u7eda\u70c2\u593a \u3000\u3000\u76ee\u7684\u91c7\u4e3d\uff0c\u5374\u81ea\u6709\u5f15\u4eba\u5165\u80dc\u7684\u98ce\u8c8c\u3002 \u3000\u3000\u84dd\u51e4\u7f13\u7f13\u5411\u6c5f\u67ab\u884c\u53bb\uff0c\u521d\u89c1\u65f6\u7684\u51b7\u5389\u795e\u60c5\uff0c\u5df2\u6d88\u5931\u51c0\u5c3d\uff0c\u4ee3\u4e4b\u800c\u8d77\u7684\u662f\u4e00\u8138\u6e29\u67d4\uff0c\u52a8\u4eba\u601c\u60dc\u3002 \u3000\u3000\u6c5f\u67ab\u5fae\u626c\u53cc\u81c2\uff0c\u84dd\u51e4\u5df2\u5954\u6295\u5165\u6000\u3002 \u3000\u3000\u201c\u600e\u4e48\u4f1a\u8fd9\u6837\uff1f\u6211\u4e0d\u662f\u5bb9\u6613\u52a8\u60c5\u7684\u5973\u4eba\u554a\uff01\u201d \u3000\u3000\u84dd\u51e4\u7684\u53cc\u76ee\u4e2d\u6d8c\u73b0\u51fa\u6cea\u6c34\u3002 \u3000\u3000\u5979\u5728\u6323\u624e\uff0c\u7cbe\u660e\u4e8e\u7ec3\u7684\u624d\u534e\uff0c\u4e0d\u7518\u5c48\u773c\u4e8e\u672f\u8bf1\u60c5\u7231\u4e4b\u4e0b\u3002 \u3000\u3000\u6c5f\u67ab\u7ec8\u4e8e\u660e\u767d\u4e86\u201c\u60d1\u5fc3\u672f\u201d\u7684\u90aa\u6076\u548c\u9738\u9053\uff0c\u5982\u5c60\u622e\u4e4b\u5200\uff0c\u950b\u5229\u4f24\u4eba\u3002 \u3000\u3000\u9752\u51e4\u6ca1\u6709\u8bf4\u8c0e\uff0c\u5929\u4e0b\u6ca1\u6709\u4e00\u4e2a\u7537\u4eba\u771f\u80fd\u5177\u6709\u4f7f\u5973\u4eba\u4e00\u89c1\u503e\u5fc3\u3001\u7518\u4e3a\u60c5\u6b7b\u7684\u9b45\u529b\uff0c\u5c24\u5176\u662f\u7cbe\u660e\u5584\u7b97\uff0c\u80f8\u85cf\u9526\u7ee3\uff0c\u624b\u63e1\u751f\u6740\u5927\u6743\u7684\u5973\u4eba\u3002 \u3000\u3000\u523b\u9aa8\u94ed\u5fc3\uff0c\u751f\u6b7b\u76f8\u8bb8\u7684\u7537\u5973\u60c5\u7231\uff0c\u662f\u9700\u8981\u65f6\u95f4\uff0c\u6162\u6162\u57f9\u517b\u51fa\u6765\u7684\uff0c\u76f8\u77e5\u76f8\u60a6\uff0c\u4e24\u5fc3\u76f8\u6295\uff0c\u624d\u80fd\u5b55\u80b2\u800c\u6210\uff0c\u662f\u4e92\u76f8\u8bb8\u4e0b\u7684\u5fc3\u613f\u627f\u8bfa\u3002 \u3000\u3000\u4f46\u201c\u60d1\u5fc3\u672f\u201d\u4e0d\u9700\u8981\u4e86\uff0c\u56e0\u4e3a\u5b83\u4e0d\u662f\u771f\u6b63\u7684\u611f\u60c5\uff0c\u5b83\u7684\u6076\u6bd2\u5728\u4e00\u4e0b\u5b50\u5c31\u5265\u53bb\u4e86\u5973\u4eba\u7684\u77dc\u6301\u548c\u9a84\u50b2\u3002 \u3000\u3000\u84dd\u98ce\u7684\u6297\u62d2\u6323\u624e\uff0c\u5f88\u5feb\u7684\u6d88\u5931\u3002 \u3000\u3000\u5979\u7d27\u7d27\u7684\u62e5\u62b1\u6c5f\u67ab\uff0c\u751f\u6050\u4ed6\u5316\u4f5c\u70df\u4e91\u800c\u53bb\u3002 \u3000\u3000\u5f81\u670d\u6885\u82b1\u3001\u9752\u51e4\u65f6\uff0c\u6c5f\u67ab\u662f\u5148\u4ee5\u672f\u8bf1\uff0c\u5faa\u5e8f\u800c\u8fdb\uff0c\u8fc7\u7a0b\u867d\u6709\u957f\u77ed\u4e0d\u540c\uff0c\u4f46\u5374\u65e0\u65f6\u4e0d\u6211\u4e88\u7684\u538b\u8feb\u611f\u3002 \u3000\u3000\u8fd9\u6b21\uff0c\u5b8c\u5168\u4e0d\u540c\u4e86\uff0c\u8eab\u5728\u654c\u7a9f\uff0c\u5f62\u52bf\u5371\u6076\uff0c\u51b3\u6218\u8feb\u5728\u7709\u776b\uff0c\u65e0\u6cd5\u9884\u6599\u4e0b\u4e00\u523b\u7684\u53d8\u5316\uff0c\u5929\u738b\u95e8\u4e3b\u968f\u65f6\u53ef\u5230\u3002 \u3000\u3000\u4e09\u9b54\u3001\u4e94\u602a\u3001\u56db\u5927\u5929\u9e45\uff0c\u4e5f\u6709\u6765\u6b64\u8bf7\u547d\u7684\u53ef\u80fd\uff0c\u65f6\u673a\u8be1\u8c32\uff0c\u5982\u4e0d\u80fd\u4e00\u4e3e\u4e4b\u95f4\uff0c\u5b8c\u5168\u5f81\u670d\uff0c\u4ee5\u540e\u662f\u5426\u8fd8\u6709\u673a\u4f1a\uff0c\u65e0\u6cd5\u9884\u77e5\u3002 \u3000\u3000\u6c5f\u67ab\u4e5f\u65e0\u6cd5\u80af\u5b9a\uff0c\u53ea\u8fd9\u6837\u76f8\u62e5\u7247\u523b\uff0c\u5c31\u80fd\u4f7f\u84dd\u51e4\u53d8\u8282\u964d\u670d\uff0c\u4e3a\u6211\u6240\u7528\u3002 \u3000\u3000\u4f55\u51b5\uff0c\u84dd\u51e4\u7684\u9a6f\u670d\uff0c\u6765\u7684\u5f88\u52c9\u5f3a\u3002 \u3000\u3000\u4f46\u8981\u6c5f\u67ab\u62b1\u7740\u84dd\u51e4\u4e0a\u5e8a\uff0c\u5e2e\u5979\u89c9\u8863\u89e3\u5e26\uff0c\u6c5f\u67ab\u53c8\u505a\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u4ed6\u6bd5\u7adf\u4e0d\u662f\u4e2a\u597d\u8272\u7684\u4eba\uff0c\u5fc3\u4e2d\u53c8\u5d4c\u7740\u96ea\u3001\u971c\u4e8c\u5973\uff0c\u6885\u82b1\u3001\u9752\u51e4\u7684\u5029\u5f71\uff0c\u4e00\u65f6\u95f4\uff0c\u4e0d\u77e5\u8be5\u5982\u4f55\u662f\u597d\uff1f \u3000\u3000\u201c\u6c5f\u67ab\uff0c\u80fd\u4e0d\u80fd\u544a\u8bc9\u6211\uff0c\u4f60\u60f3\u8981\u6211\u4ec0\u4e48\uff1f\u6211\u59ff\u8272\u4e0d\u5982\u9752\u51e4\uff0c\u800c\u4e14\uff0c\u8fd8\u6709\u70b9\u5b64\u82b3\u81ea\u8d4f\u7684\u6027\u683c\u2026\u2026\u201d\u84dd\u51e4\u9053\uff1a\u201c\u4e5f\u4e0d\u4f1a\u5f88\u6e29\u67d4\u7684\u670d\u4f8d\u7537\u4eba\uff0c\u4f60\u4f1a\u5931\u671b\u7684\uff01\u201d \u3000\u3000\u771f\u662f\u4e2a\u5014\u5f3a\u7684\u5973\u4eba\uff0c\u5c3d\u7ba1\u7d27\u62b1\u4e2a\u90ce\uff0c\u60c5\u70ed\u5982\u706b\uff0c\u4f46\u5fc3\u4e2d\u4ecd\u6709\u4e00\u70b9\u7075\u5149\u4e0d\u6627\uff0c\u4f3c\u6b32\u6323\u8131\u67b7\u9501\u3002 \u3000\u3000\u201c\u84dd\u51e4\uff0c\u4f60\u80fd\u7ed9\u6211\u4ec0\u4e48\uff1f\u6211\u90fd\u63a5\u53d7\uff0c\u5305\u62ec\u4f60\u7684\u4eba\uff0c\u548c\u4f60\u80fd\u5e2e\u52a9\u6211\u7684\u529b\u91cf\u3002\u201d\u6c5f\u67ab\u8f7b\u629a\u7740\u84dd\u51e4\u7684\u79c0\u53d1\u8bf4\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u660e\u767d\uff0c\u8fd9\u4e00\u523b\uff0c\u5982\u4e0d\u52a0\u628a\u52b2\uff0c\u5b8c\u5168\u5360\u636e\u5979\u7684\u8eab\u5fc3\uff0c\u6b64\u884c\u5373\u5b8c\u5168\u5931\u8d25\uff0c\u51b3\u6218\u4e4b\u540e\uff0c\u5c31\u7b97\u8fd8\u6709\u673a\u4f1a\uff0c\u4e5f\u6ca1\u518d\u89c1\u84dd\u51e4\u7684\u5fc5\u8981\u4e86\u3002 \u3000\u3000\u8fd9\u4e0d\u662f\u537f\u537f\u6211\u6211\u513f\u5973\u79c1\u60c5\uff0c\u662f\u4e00\u573a\u5f71\u54cd\u80dc\u8d1f\uff0c\u7275\u626f\u4e0a\u5f88\u591a\u4eba\u751f\u6b7b\u7684\u6218\u6597\u3002 \u3000\u3000\u6c5f\u67ab\u4e0d\u5f97\u4e0d\u65bd\u5c55\u51fa\u6311\u9017\u7684\u653b\u52bf\u3002 \u3000\u3000\u84dd\u51e4\u5f88\u5feb\u7684\u5d29\u6e83\u4e86\u3002 \u3000\u3000\u5fc3\u88ab\u672f\u8bf1\uff0c\u4eba\u5df2\u6295\u6000\uff0c\u8fd8\u80fd\u591f\u652f\u6301\u591a\u4e45\u5462\uff1f\u6c5f\u67ab\u4e00\u9635\u8f7b\u601c\u871c\u7231\u7684\u6311\u9017\uff0c\u5f15\u8d77\u4e86\u84dd\u51e4\u5f3a\u70c8\u7684\u60c5\u6b32\u53cd\u5e94\u3002 \u3000\u3000\u5979\u5b9e\u5728\u662f\u4e2a\u5b9a\u529b\u5947\u5f3a\u7684\u4eba\uff0c\u5728\u6fc0\u52a8\u96be\u6291\u7684\u60c5\u6b32\u706b\u7130\u4e2d\uff0c\u4ecd\u7136\u672a\u5fd8\u8bb0\u63a9\u4e0a\u5385\u95e8\uff0c\u624d\u7275\u7740\u6c5f\u67ab\u884c\u4eba\u5367\u5ba4\u3002 \u3000\u3000\u5e8a\u4e0a\u7684\u4e8b\uff0c\u4e0d\u8bf4\u4e5f\u7f62\uff0c\u53cd\u6b63\u8fd9\u4e0d\u662f\u81f3\u60c5\u81f3\u6027\u7684\u7231\u604b\uff0c\u53cc\u65b9\u90fd\u6709\u70b9\u52c9\u5f3a\uff0c\u4e00\u4e2a\u662f\u6297\u62d2\u4e0d\u4f4f\uff0c\u4e00\u4e2a\u662f\u5fc3\u6000\u522b\u5177\u3002 \u3000\u3000\u4f46\u6c5f\u67ab\u8fd8\u662f\u80dc\u5229\u4e86\uff0c\u4ed6\u5168\u529b\u65bd\u4e3a\uff0c\u5b8c\u5168\u5f81\u670d\uff0c\u5360\u636e\u4e86\u84dd\u51e4\u7684\u8eab\u5fc3\uff0c\u4e5f\u51fb\u6e83\u4e86\u84dd\u51e4\u5fc3\u4e2d\u6f5c\u4f0f\u7684\u4e00\u70b9\u53cd\u6297\u610f\u8bc6\u3002 \u3000\u3000\u84dd\u98ce\u5f97\u5230\u4e86\u4ece\u672a\u6709\u8fc7\u7684\u6b22\u6109\uff0c\u6cb9\u751f\u51fa\u65e0\u9650\u7231\u604b\uff0c\u671b\u7740\u6000\u62b1\u4e2d\u7684\u7537\u4eba\uff0c\u957f\u957f\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6c5f\u90ce\uff0c\u4f60\u8d62\u4e86\uff0c\u8bf4\u5427\uff01\u8981\u6211\u600e\u4e48\u505a\uff1f\u80fd\u4e0d\u80fd\u5141\u8bb8\u6211\u5728\u60c5\u52bf\u6709\u53d8\u65f6\uff0c\u50cf\u9752\u51e4\u4e00\u6837\uff0c\u8ffd\u968f\u5728\u4f60\u7684\u8eab\u4fa7\uff1f\u201d \u3000\u3000\u5979\u662f\u7406\u667a\u91cd\u8fc7\u60c5\u611f\u7684\u5973\u4eba\uff0c\u60c5\u8bdd\u51fa\u53e3\uff0c\u4e5f\u6709\u7740\u516c\u4e8b\u516c\u529e\u7684\u751f\u786c\uff0c\u7f3a\u5c11\u4e86\u90a3\u79cd\u7f20\u7ef5\u52a8\u4eba\u7684\u5473\u9053\u3002 \u3000\u3000\u201c\u968f\u65f6\u6b22\u8fce\uff0c\u9752\u51e4\u548c\u6211\uff0c\u90fd\u5c06\u7fd8\u9996\u5bc4\u671b\uff01\u201d \u3000\u3000\u84dd\u51e4\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e0d\u5acc\u5f03\u6211\u8fd9\u6b8b\u82b1\u8d25\u67f3\u5c31\u597d\uff0c\u4e8b\u5b9e\u4e0a\uff0c\u5929\u738b\u95e8\u548c\u5929\u540e\u5bab\u4e2d\uff0c\u8d85\u8fc7\u5341\u4e03\u5c81\u7684\u5973\u5f1f\u5b50\uff0c\u4e5f\u5f88\u5c11\u627e\u51fa\u4e00\u4e2a\u5904\u5b50\u4e4b\u8eab\uff0c\u6211\u4eec\u547d\u82e6\u554a\uff01\u4f46\u53ef\u4ee5\u544a\u6170\u4f60\u7684\u662f\uff0c\u4f60\u662f\u7b2c\u4e8c\u4e2a\u5360\u6709\u6211\u8eab\u5b50\u7684\u7537\u4eba\uff0c\u6700\u91cd\u8981\u7684\u662f\u6211\u5fc3\u7518\u60c5\u613f\uff0c\u81ea\u8350\u6795\u5e2d\u3002\u201d \u3000\u3000\u201c\u5929\u738b\u662f\u7b2c\u4e00\u4e2a\u4e86\uff1f\u201d \u3000\u3000\u201c\u5f88\u9ad8\u5174\u542c\u5230\u4f60\u6709\u70b9\u918b\u610f\u8d28\u95ee\uff0c\u2026\u2026\u201d\u84dd\u51e4\u9053\uff1a\u201c\u8fd9\u8868\u793a\u4f60\u76f8\u5f53\u91cd\u89c6\u6211\uff0c\u4e0d\u8fc7\uff0c\u4e0d\u662f\u4ed6\uff0c\u4ed6\u867d\u7136\u6709\u5fc3\uff0c\u6211\u5374\u501f\u6545\u63a8\u62d2\uff0c\u6211\u662f\u6709\u70b9\u624d\u6c14\u7684\u5973\u4eba\uff0c\u53ef\u4ee5\u5e2e\u4ed6\u505a\u5f88\u591a\u7684\u4e8b\u60c5\uff0c\u6211\u4e0d\u591f\u4eae\u4e3d\uff0c\u7f3a\u5c11\u70b9\u6e29\u67d4\uff0c\u4e5f\u662f\u4ed6\u6ca1\u6709\u5f3a\u8feb\u6211\u7684\u539f\u56e0\u3002\u201d \u3000\u3000\u6c5f\u67ab\u70b9\u70b9\u5934\uff0c\u633a\u8eab\u5750\u8d77\u3002 \u3000\u3000\u4f46\u5374\u88ab\u84dd\u51e4\u4e00\u628a\u53c8\u62c9\u5012\u5e8a\u4e0a\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u6709\u70b9\u6015\u5440\uff1f\u201d \u3000\u3000\u201c\u4e00\u65e6\u5929\u738b\u95e8\u4e3b\u95ef\u8fdb\u6765\uff0c\u6216\u662f\u6709\u4eba\u6765\u4f20\u62a5\u6218\u60c5\uff0c\u8fd9\u7b49\u76f8\u62e5\u800c\u5367\uff0c\u4f1a\u4e0d\u4f1a\u4f24\u4e86\u4f60\u5927\u5c0f\u59d0\u7684\u5c0a\u4e25\u5462\uff1f\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u6211\u8981\u66ff\u4f60\u60f3\u554a\uff01\u201d \u3000\u3000\u201c\u6211\u7684\u4ee4\u8c15\u5f88\u4e25\u5389\uff0c\u672a\u5f97\u5230\u6211\u7684\u56de\u5e94\u65f6\uff0c\u6ca1\u6709\u4eba\u6562\u95ef\u8fdb\u6765\u3002\u201d \u3000\u3000\u201c\u5929\u738b\u5462\uff1f\u4ed6\u4e5f\u4e0d\u6562\u4e48\uff1f\u201d \u3000\u3000\u84dd\u51e4\u7b11\u4e00\u7b11\uff0e\u9053\uff1a\u201c\u4ed6\u662f\u552f\u4e00\u53ef\u4ee5\u95ef\u8fdb\u6765\u7684\u4eba\uff0c\u4f46\u4ed6\u4eec\u4eca\u5929\u805a\u4f1a\uff0c\u7814\u5546\u5927\u5c40\uff0c\u8fd9\u4e00\u6b21\u5168\u9762\u52a8\u5458\uff0c\u8fd8\u6709\u4e1c\u3001\u5357\u3001\u897f\u3001\u5317\u56db\u5927\u795e\u901a\u4f7f\u8005\u4e0e\u4f1a\uff0c\u6559\u4e3b\u4e5f\u53ef\u80fd\u4eb2\u4e34\u4e3b\u6301\uff0c\u5927\u4f1a\u5b9a\u4e8e\u4eca\u591c\u5f00\u59cb\uff0c\u6700\u5feb\u4e5f\u8981\u660e\u5929\u624d\u4f1a\u56de\u6765\u3002\u201d \u3000\u3000\u6c5f\u67ab\u770b\u770b\u8fb0\u5149\u8fd8\u65e9\uff0c\u6253\u86c7\u987a\u68cd\u4e0a\u7684\u8bf4\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u5929\u738b\u3001\u5929\u540e\u3001\u5929\u9a6c\uff0c\u4e09\u5927\u7ec4\u5408\u4e4b\u4e0a\uff0c\u53e6\u6709\u4e00\u4e2a\u7edf\u6cbb\u7684\u4eba\u7269\u4e86\u3002\u201d \u3000\u3000\u201c\u5bf9\uff01\u4f60\u5fc3\u4e2d\u6709\u4ec0\u4e48\u7591\u95ee\uff0c\u5c3d\u7ba1\u8bf7\u95ee\uff1f\u2026\u2026\u201d\u84dd\u51e4\u9053\uff1a\u201c\u6211\u80fd\u77e5\u9053\u7684\uff0c\u4e00\u5b9a\u56de\u7b54\uff0c\u4f46\u6211\u4e0d\u662f\u5168\u76d8\u4e86\u89e3\uff0c\u65e0\u6cd5\u4f5c\u4e00\u4e2a\u660e\u6670\u7684\u5168\u76d8\u63cf\u8ff0\u3002\u201d \u3000\u3000\u201c\u5929\u540e\u5bab\u4e3b\uff0c\u662f\u4e0d\u662f\u5929\u738b\u95e8\u4e3b\u7684\u592b\u4eba\uff1f\u201d \u3000\u3000\u201c\u662f\uff01\u2026\u2026\u201d\u84dd\u51e4\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u4f46\u4ed6\u4eec\u592b\u59bb\u53cd\u76ee\uff0c\u5df2\u7ecf\u5206\u5f00\u4e86\uff0c\u6240\u4ee5\uff0c\u591a\u4e86\u4e00\u4e2a\u5929\u540e\u5bab\u7684\u7ec4\u5408\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u5462\uff1f\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u5206\u5f00\u7684\u6709\u70b9\u602a\uff1f\u201d \u3000\u3000\u76ee\u5149\u8f6c\u6ce8\u84dd\u51e4\u8eab\u4e0a\uff0c\u4e00\u8138\u671f\u5f85\u4e4b\u8272\u3002 \u3000\u3000\u201c\u6c5f\u90ce\uff0c\u8fd9\u662f\u5f88\u5927\u7684\u79d8\u5bc6\uff0c\u542c\u8fc7\u4e86\uff0c\u6700\u597d\u80fd\u57cb\u85cf\u5fc3\u4e2d\uff0c\u522b\u8bf4\u51fa\u53bb\uff0c\u4e00\u65e6\u5916\u6cc4\uff0c\u4ed6\u4eec\u4f1a\u5168\u529b\u8ffd\u6740\u4f60\uff0c\u90a3\u5c31\u4e0d\u662f\u6211\u7684\u5fc3\u613f\u4e86\uff01\u201d \u3000\u3000\u8fd9\u84dd\u51e4\u679c\u7136\u77e5\u9053\u5f88\u591a\u79d8\u5bc6\uff0c\u771f\u5f97\u8981\u597d\u597d\u9886\u6559\u4e00\u4e0b\u4e86\u3002 \u3000\u3000\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u53e3\u4e2d\u5374\u8bf4\u9053\uff1a\u201c\u4ed6\u4eec\u5047\u88c5\u53cd\u65e5\u4e86\uff0c\u4f7f\u5929\u738b\u95e8\u7684\u52bf\u529b\uff0c\u6269\u5145\u4e86\u4e00\u500d\uff1f\u201d \u3000\u3000\u201c\u8fd9\u672c\u662f\u4ed6\u4eec\u7684\u8ba1\u5212\uff0c\u4e5f\u7b97\u6210\u529f\u4e86\uff0c\u4f46\u5374\u53d8\u4e86\u8d28\uff0c\u591a\u4e86\u4e00\u4e2a\u5929\u540e\u5bab\uff0c\u5929\u738b\u95e8\u4e3b\u5374\u5931\u53bb\u4e86\u4e00\u4e2a\u8273\u4e3d\u65e0\u53cc\u7684\u592b\u4eba\u2026\u2026\u201d\u84dd\u51e4\u9053\uff1a\u201c\u80fd\u5e72\u7684\u5973\u4eba\uff0c\u63a5\u89e6\u5230\u6743\u52bf\u4e4b\u540e\uff0c\u5c31\u4f1a\u8ff7\u604b\uff0c\u5929\u738b\u592b\u4eba\u9ec4\u79cb\u6c0f\uff0c\u5047\u620f\u771f\u6f14\uff0c\u5206\u5f00\u4e4b\u540e\uff0c\u5c31\u4e0d\u51c6\u5929\u738b\u518d\u767b\u5802\u5165\u5ba4\u3002 \u3000\u3000\u5929\u540e\u5bab\u771f\u7684\u6210\u4e86\u4e00\u4e2a\u72ec\u7acb\u7ec4\u5408\uff0c\u6269\u5927\u5230\u73b0\u5728\u7684\u89c4\u6a21\uff0c\u773c\u4e0b\u8bba\u5b9e\u529b\u4e4b\u5f3a\uff0c\u65e9\u5df2\u4e0d\u5728\u5929\u738b\u95e8\u4e4b\u4e0b\u4e86\u3002\u201d \u3000\u3000\u201c\u53ea\u662f\u5982\u6b64\u4e48\uff1f\u201d \u3000\u3000\u201c\u8fd8\u4f1a\u600e\u6837\u5462\uff1f\u2026\u2026\u201d \u3000\u3000\u84dd\u51e4\u8138\u4e0a\u5fae\u7b11\u8be1\u79d8\uff0c\u65e9\u5df2\u9690\u793a\u51fa\u522b\u6709\u5185\u60c5\u3002 \u3000\u3000\u201c\u79cb\u592b\u4eba\u73b0\u5728\u7684\u79cb\u5927\u5bab\u4e3b\uff0c\u662f\u4e0d\u662f\u6709\u4e86\u65b0\u6b22\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u624d\u4f7f\u5979\u51b3\u7edd\u7684\u548c\u5929\u738b\u4e00\u5200\u4e24\u6bb5\u3002\u201d \u3000\u3000\u84dd\u51e4\u9053\uff1a\u201c\u600e\u4e48\u4f1a\u8fd9\u6837\u60f3\u3002\u201d \u3000\u3000\u5149\u6ed1\u8d64\u88f8\u7684\u8eab\u4f53\uff0c\u4e00\u4e0b\u5b50\u6eda\u5165\u6c5f\u67ab\u6000\u4e2d\uff0c\u7b11\u9053\uff1a\u201c\u4e0d\u5e78\u7684\u662f\uff0c\u88ab\u4f60\u8bf4\u5bf9\u4e86\uff0c\u5c31\u50cf\u6211\u4e00\u6837\uff0c\u88ab\u4f60\u5f15\u8bf1\u5931\u8eab\uff0c\u5bb3\u5f97\u6211\u4e3a\u60c5\u5206\u5fc3\uff0c\u8981\u5982\u4f55\u5728\u7167\u987e\u60c5\u90ce\u7684\u5b89\u5168\u4e0b\uff0c\u53c8\u80fd\u8ba9\u5929\u738b\u95e8\u4e0d\u53d7\u4f24\u5bb3\uff0c\u770b\u6765\u662f\u4e0d\u592a\u53ef\u80fd\u3002 \u3000\u3000\u9ec4\u5929\u738b\u4e00\u751f\u5b09\u620f\u7f8e\u4e3d\u7684\u5973\u8272\u4e4b\u4e2d\uff0c\u4f46\u4ed6\u4e5f\u53d7\u5230\u4e24\u4e2a\u5973\u4eba\u6700\u5927\u7684\u4f24\u5bb3\uff0c\u4e00\u4e2a\u662f\u4ed6\u8001\u5a46\u79cb\u79bb\u82b1\uff0c\u4e00\u4e2a\u662f\u4ed6\u6700\u4fe1\u4efb\u7684\u5973\u5f1f\u5b50\u84dd\u51e4\u3002\u201d\u77e5\u5979\u5728\u501f\u6545\u8ff0\u8bf4\u5fc3\u4e2d\u7684\u7262\u9a9a\u3001\u60b2\u75db\uff0c\u6c5f\u67ab\u53ea\u6709\u7d27\u7d27\u62b1\u4f4f\u5979\u73b2\u73d1\u7684\u8eab\u8eaf\u3002 \u3000\u3000\u201c\u636e\u8bf4\u662f\u79cb\u79bb\u82b1\u88ab\u6559\u4e3b\u770b\u4e0a\u4e86\uff0c\u5979\u5f97\u5230\u6700\u5999\u7684\u6b66\u529f\u4f20\u6388\uff0c\u4e5f\u548c\u6559\u4e3b\u4e0a\u4e86\u5e8a\uff0c\u4e09\u5927\u5929\u7cfb\u7ec4\u5408\u4e2d\u7684\u9996\u8111\uff0c\u771f\u6b63\u89c1\u8fc7\u6559\u4e3b\u7684\uff0c\u53ef\u80fd\u662f\u53ea\u6709\u5979\u4e86\uff0c\u5929\u738b\u95e8\u4e3b\u3001\u5929\u9a6c\u603b\u5802\u4e3b\uff0c\u597d\u50cf\u662f\u90fd\u672a\u89c1\u8fc7\u771f\u6b63\u7684\u6559\u4e3b\u3002 \u3000\u3000\u53ef\u7b11\u7684\u662f\uff0c\u79cb\u79bb\u82b1\u4e5f\u975e\u5e38\u770b\u91cd\u6211\uff0c\u5979\u8ba4\u4e3a\u6211\u4e0d\u4f1a\u4e3a\u7537\u4eba\u52a8\u5fc3\uff0c\u51e0\u6b21\u7ed9\u6211\u8bf4\uff0c\u8981\u6211\u5230\u5929\u540e\u5bab\u53bb\uff0c\u51fa\u4efb\u7b2c\u56db\u5bab\u4e3b\u3002 \u3000\u3000\u4f46\u6211\u4e0d\u559c\u6b22\u5979\u4eec\u7684\u4f5c\u6cd5\uff0c\u5973\u5f1f\u5b50\u90fd\u8981\u5927\u529b\u4e60\u7ec3\u5a9a\u672f\uff0c\u4ee5\u53d6\u60a6\u7537\u4eba\uff0c\u5f53\u4f5c\u6b66\u5668\uff0c\u7528\u4e8e\u6c5f\u6e56\uff0c\u60f3\u4e0d\u5230\u6211\u7adf\u4e5f\u662f\u5728\u52ab\u96be\u9003\u3002 \u3000\u3000\u8fd9\u756a\u8bdd\uff0c\u50cf\u5c16\u5200\u4e00\u822c\uff0c\u523a\u5165\u6c5f\u67ab\u7684\u5fc3\u4e2d\u3002 \u3000\u3000\u5927\u6982\u662f\u84dd\u51e4\u4e5f\u8b66\u89c9\u5230\u4e86\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u6c5f\u90ce\uff0c\u522b\u8bef\u4f1a\u554a\uff01\u6211\u53ea\u662f\u5fc3\u4e2d\u611f\u6168\uff0c\u5c31\u987a\u53e3\u6e9c\u4e86\u51fa\u6765\u3002\u201d \u3000\u3000\u201c\u5509\uff01\u6211\u5728\u60f3\uff0c\u6211\u4ee5\u2018\u60d1\u5fc3\u672f\u2019\u5bf9\u4ed8\u4f60\uff0c\u662f\u4e0d\u662f\u6709\u6b20\u5149\u660e\uff1f\u201d \u3000\u3000\u201c\u8bf4\u8fd9\u4e9b\u592a\u665a\u4e86\uff0c\u6211\u597d\u50cf\u771f\u7684\u5df2\u88ab\u4f60\u60d1\u53bb\u4e86\u5fc3\uff0c\u8001\u5b9e\u8bf4\uff0c\u4e0d\u5982\u6b64\uff0c\u4f60\u7edd\u65e0\u6cd5\u4f7f\u6211\u5c48\u670d\uff0c\u73b0\u5728\uff0c\u6211\u597d\u60f3\u5b66\u4e00\u70b9\u5973\u4eba\u7684\u6e29\u67d4\uff0c\u4f60\u4f7f\u6211\u627e\u56de\u4e86\u5973\u4eba\u7684\u8eab\u4efd\uff01 \u3000\u3000\u8d70\u5427\uff01\u6c5f\u90ce\uff0c\u7ed9\u6211\u4e2a\u673a\u4f1a\uff0c\u8ba9\u6211\u5e2e\u4f60\u6d17\u4e2a\u6fa1\uff0c\u6211\u5b66\u8fc7\u5a9a\u672f\uff0c\u4f46\u4ece\u672a\u7528\u8fc7\uff0c\u4eca\u5929\u5c31\u5f00\u5f20\u5927\u5409\u5427\uff1f\u201d \u3000\u3000\u84dd\u51e4\u662f\u771f\u7684\u60f3\u6539\u53d8\u81ea\u5df1\uff0c\u4f46\u6c5f\u67ab\u5374\u4e00\u5fc3\u60f3\u518d\u63c9\u51fa\u4e00\u4e9b\u4e09\u5927\u7ec4\u5408\u7684\u5185\u60c5\uff0c\u53ea\u4e0d\u8fc7\uff0c\u53c8\u4e0d\u4fbf\u8ffd\u95ee\u5f97\u5927\u6025\uff0c\u5f15\u53d1\u84dd\u51e4\u7684\u4e0d\u6ee1\u53cd\u5e94\u3002 \u3000\u3000\u8fd9\u662f\u4e00\u6bb5\u75db\u82e6\u76f8\u5904\uff0c\u771f\u60c5\u65e0\u6cd5\u4ea4\u6d41\uff0c\u4e5f\u65e0\u6cd5\u7387\u6027\u5766\u7136\u7684\u8868\u8fbe\u4ec0\u4e48\uff1f\u597d\u50cf\u6bcf\u4e00\u53e5\u8a00\u8c08\u548c\u52a8\u4f5c\uff0c\u90fd\u8981\u7ecf\u8fc7\u4e09\u601d\u624d\u884c\u3002 \u3000\u3000\u4f46\u84dd\u51e4\u5374\u6e10\u6e10\u7684\u653e\u5f00\u4e86\u81ea\u5df1\uff0c\u52c9\u5f3a\u81ea\u5df1\u53bb\u670d\u4f8d\u6c5f\u67ab\u8fc7\u7a0b\u4e2d\uff0c\u9010\u6e10\u4f53\u4f1a\u5230\u5973\u6027\u7684\u6e29\u67d4\uff0c\u5979\u5f00\u59cb\u8ba4\u771f\u7684\u4ea4\u51fa\u4e86\u81ea\u5df1\uff0c\u4e5f\u8bf4\u51fa\u80f8\u4e2d\u6240\u77e5\u7684\u9690\u5bc6\uff0c\u5c71\u8270\u6da9\u5230\u65e0\u6028\u65e0\u5fe7\u5730\u4ed8\u51fa\u4e86\u771f\u6b63\u611f\u60c5\u2026\u2026\u3002 \u3000\u3000\u6c5f\u67ab\u4e5f\u611f\u53d7\u5230\u4e00\u79cd\u53d1\u81ea\u84dd\u51e4\u5185\u5fc3\u7684\u60c5\u610f\uff0c\u4f20\u8fbe\u4e86\u8fc7\u6765\uff0c\u5982\u4e0d\u80fd\u4ee5\u771f\u6b63\u7684\u611f\u60c5\u56de\u5e94\uff0c\u6050\u96be\u7f81\u7eca\u4f4f\u8fd9\u5339\u96be\u9a6f\u7684\u91ce\u9a6c\u3002 \u3000\u3000\u4ed6\u7545\u5f00\u4e86\u80f8\u6000\uff0c\u7531\u5fc3\u4e2d\u63a5\u53d7\u84dd\u51e4\u3002 \u3000\u3000\u8bf4\u4e5f\u5947\u602a\uff0c\u7075\u7280\u76f8\u901a\u4e4b\u540e\uff0c\u7acb\u523b\u53e6\u662f\u4e00\u756a\u5c40\u9762\uff0c\u8c08\u8bdd\u6d41\u7545\u4e86\uff0c\u4e24\u4eba\u4e4b\u95f4\uff0c\u518d\u65e0\u6ede\u788d\u3002 \u3000\u3000\u84dd\u51e4\u7709\u5b87\u95f4\u6d6e\u52a8\u7684\u4e00\u7247\u6de1\u6028\u8f7b\u6101\u6d88\u5931\u4e86\uff0c\u5374\u6cdb\u751f\u65e0\u5c3d\u7f20\u7ef5\u7684\u60c5\u7231\uff0c\u4f9d\u504e\u6c5f\u67ab\u6000\u4e2d\uff0c\u5e7d\u5e7d\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u80fd\u7f20\u4f4f\u4f60\uff0c\u518d\u7559\u8fd9\u91cc\uff0c\u4e5f\u4e0d\u77e5\u9053\uff0c\u4ed6\u4eec\u4f1a\u5546\u4e4b\u540e\uff0c\u5982\u4f55\u5b89\u6392\u51fa\u56f4\u653b\u4f60\u4eec\u7684\u7b56\u7565\uff0c\u4f46\u6211\u4f1a\u5c3d\u529b\u5e2e\u52a9\u4f60\uff0c\u6625\u8695\u5230\u6b7b\u4e1d\u65b9\u5c3d\u554a\uff01\u6c5f\u90ce\uff0c\u6211\u4e0d\u60dc\u4e3a\u4f60\u800c\u6b7b\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u8981\u8f7b\u8a00\u6b7b\u4ea1\uff0c\u6211\u8981\u4f60\u6d3b\u4e0b\u53bb\uff0c\u5e0c\u671b\u8fd9\u4e00\u6218\u4e4b\u540e\uff0c\u6211\u4eec\u8fd8\u80fd\u591f\u91cd\u6e29\u65e7\u68a6\u3002\u201d \u3000\u3000\u84dd\u51e4\u70b9\u70b9\u5934\uff0c\u5e2e\u6c5f\u67ab\u7a7f\u4e0a\u8863\u670d\u3002 \u3000\u3000\u6c5f\u67ab\u6084\u7136\u79bb\u53bb\u3002 \u3000\u3000\u84dd\u98ce\u6ca1\u6709\u76f8\u9001\uff0c\u4f46\u661f\u76ee\u4e2d\u542b\u7740\u6cea\u6c34\uff0c\u76f4\u5230\u6c5f\u67ab\u7684\u80cc\u5f71\u6d88\u5931\uff0c\u6cea\u73e0\u513f\u624d\u987a\u816e\u6d41\u4e0b\u3002 \u3000\u3000\u5979\u575a\u5f3a\u8fc7\u4eba\uff0c\u8fde\u6d41\u6cea\u4e5f\u4e0d\u613f\u6c5f\u67ab\u770b\u5230\u3002 \u3000\u3000\u6c5f\u67ab\u8d76\u5230\u4e86\u548c\u9752\u51e4\u7ea6\u5b9a\u7684\u4f1a\u5408\u5730\u65b9\uff0c\u5f85\u5019\u4e86\u4e00\u523b\u5de5\u592b\uff0c\u9752\u51e4\u624d\u6025\u5954\u800c\u81f3\uff0c\u9053\uff1a\u201c\u4e09\u9b54\u3001\u4e94\u602a\u4f3c\u4e4e\u5728\u627e\u5bfb\u4ec0\u4e48\uff1f\u5728\u8fd9\u91cc\u5de1\u89c6\u826f\u4e45\uff0c\u903c\u7684\u6211\u4e0d\u5f97\u4e0d\u8fdc\u8fdc\u7684\u8eb2\u8d77\uff0c\u6211\u62c5\u5fc3\u4ed6\u4eec\u5df2\u7136\u52a8\u7591\uff0c\u90a3\u5c31\u4f7f\u84dd\u51e4\u7684\u5904\u5883\u5371\u9669\u4e86\u3002\u201d \u3000\u3000\u6c5f\u67ab\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u8981\u4e0d\u8981\u8d76\u56de\u901a\u77e5\u84dd\u51e4\u4e00\u58f0\u5462\uff1f\u201d \u3000\u3000\u201c\u4e0d\u7528\u4e86\uff0c\u6700\u91cd\u8981\u7684\u5979\u5982\u662f\u771f\u4e3a\u60c5\u7275\u2026\u2026\u201d\u9752\u51e4\u9053\uff1a\u201c\u4ee5\u84dd\u51e4\u7684\u51b7\u9759\u5584\u8c0b\uff0c\u6709\u8db3\u591f\u7684\u5e94\u53d8\u80fd\u529b\uff0c\u53ea\u8981\u4f60\u4eec\u4e0d\u662f\u88ab\u6349\u5978\u6210\u53cc\uff0c\u5979\u90fd\u6709\u5e94\u4ed8\u7684\u529e\u6cd5\uff0c\u6446\u5f97\u56db\u5e73\u516b\u7a33\uff0c\u73b0\u5728\uff0c\u5c31\u8981\u770b\u4f60\u662f\u5426\u5df2\u771f\u7684\u5360\u636e\u5979\u7684\u82b3\u5fc3\u4e86\uff1f\u201d \u3000\u3000\u201c\u4f60\u731c\u5462\uff1f\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff0c\u5979\u662f\u6211\u6240\u9047\u8fc7\u6700\u987d\u5f3a\u7684\u654c\u4eba\uff0c\u5fc3\u4e2d\u4e00\u76f4\u6709\u4e00\u80a1\u6297\u62d2\u7684\u706b\u7130\u4e0d\u606f\u2026\u2026\u201d \u3000\u3000\u201c\u90a3\u5c31\u9ebb\u70e6\u4e86\uff0c\u6211\u77e5\u9053\u5979\u7acb\u5b9a\u4e86\u4e00\u4e2a\u5fd7\u613f\uff0c\u8fd9\u4e00\u751f\u7edd\u4e0d\u4f5c\u7537\u4eba\u73a9\u7269\uff0c\u4e5f\u4e0d\u613f\u4f9d\u8d56\u7537\u4eba\uff0c\u5979\u8981\u4ee5\u4e00\u4e2a\u5973\u4eba\u7684\u8eab\u4efd\uff0c\u5728\u6c5f\u6e56\u4e0a\u4e89\u4e00\u5e2d\u4e4b\u5730\u2026\u2026\u201d \u3000\u3000\u201c\u53e6\u5916\u6709\u4e00\u4ef6\u4e8b\uff0c\u4f60\u5927\u6982\u8fd8\u4e0d\u77e5\u9053\uff0c\u90a3\u5c31\u662f\u5929\u540e\u5bab\u7684\u5927\u5bab\u4e3b\u79cb\u79bb\u82b1\uff0c\u66fe\u7ecf\u9080\u8bf7\u5979\u5230\u5929\u540e\u5bab\uff0c\u51fa\u4efb\u56db\u5bab\u4e3b\uff0c\u4f46\u5374\u88ab\u5979\u5a49\u62d2\u4e86\u3002 \u3000\u3000\u5979\u662f\u4e2a\u624d\u534e\u6d0b\u6ea2\u7684\u4eba\uff0c\u6211\u770b\u5230\u5979\u5185\u5fc3\u4e2d\u7684\u6323\u624e\uff0c\u60d1\u5fc3\u672f\u751f\u751f\u626d\u66f2\u4e86\u4e00\u4e2a\u4eba\u7684\u601d\u60f3\u62b1\u8d1f\uff0c\u4e3a\u6076\u4e4b\u5927\uff0c\u83ab\u8fc7\u5982\u6b64\u3002 \u3000\u3000\u5b83\u662f\u4eba\u6027\u4e0a\u7684\u67b7\u9501\uff0c\u622e\u4eba\u610f\u5fd7\u7684\u51f6\u5200\uff0c\u9664\u4e86\u7528\u4ee5\u53cd\u6297\u5a9a\u672f\u5916\uff0c\u60d1\u5fc3\u672f\u7edd\u5bf9\u4e0d\u80fd\u518d\u7528\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff0c \u3000\u3000\u201c\u6b64\u672f\u8fd8\u6709\u4e2a\u5f88\u5927\u7684\u7f3a\u70b9\uff0c\u65bd\u672f\u4e4b\u540e\uff0c\u5f81\u670d\u4e86\u5bf9\u65b9\uff0c\u4f46\u4f60\u4e5f\u5fc5\u987b\u4ee5\u76f8\u5bf9\u7684\u6295\u5165\uff0c\u672f\u6cd5\u624d\u4e0d\u4f1a\u5931\u7075\uff0c\u5e38\u5e38\u65bd\u5c55\u5b83\uff0c\u5982\u4f55\u5f97\u4e86\uff01\u201d \u3000\u3000\u201c\u4f46\u5982\u522b\u4eba\u65bd\u5c55\u5a9a\u672f\u7b97\u8ba1\u4f60\u65f6\uff0c\u4f60\u5c31\u4f1a\u5750\u4ee5\u5f85\u6bd9\u5417\uff1f\u636e\u6211\u6240\u77e5\uff0c\u5929\u540e\u5bab\u4e2d\u5973\u5f1f\u5b50\uff0c\u90fd\u662f\u628a\u5a9a\u672f\u5217\u5165\u91cd\u5934\u8bfe\u4e1a\uff0c\u5979\u4eec\u4e0d\u540c\u4e8e\u4f60\u7684\u662f\uff0c\u672f\u4e0b\u4fd8\u864f\u4e4b\u81e3\uff0c\u7528\u8fc7\u5c31\u6740\uff01\u201d \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u4e3a\u53cd\u51fb\u5a9a\u529f\u65bd\u5c55\u7684\u2018\u60d1\u5fc3\u672f\u2019\uff0c\u662f\u4f55\u7ed3\u679c\uff1f\u6211\u8fd8\u6ca1\u7ecf\u9a8c\u8fc7\uff0c\u9664\u4e86\u53cd\u51fb\u5a9a\u672f\uff0c\u8fd9\u4e2a\u5de5\u592b\uff0c\u7edd\u5bf9\u4e0d\u80fd\u518d\u8f7b\u6613\u65bd\u5c55\u4e86\uff01\u201d \u3000\u3000\u9752\u51e4\u7a81\u7136\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u660e\u767d\u4e86\uff0c\u84dd\u51e4\u5c48\u670d\u4e86\uff0c\u662f\u5417\uff1f\u4f60\u62c5\u5fc3\u53c8\u591a\u4e86\u4e00\u4e2a\u806a\u660e\u7684\u59bb\u59be\uff0c\u6c5f\u90ce\uff0c\u8fd9\u79cd\u4e8b\uff0c\u5f20\u59e8\u4f1a\u5e2e\u4f60\u89e3\u51b3\u7684\uff0c\u4f60\u5c31\u4e0d\u7528\u53d1\u6101\u4e86\uff01\u201d \u3000\u3000\u201c\u8fd8\u6709\u4e00\u4ef6\u6050\u6016\u7684\u4e8b\uff0c\u5f53\u522b\u4eba\u4e3a\u65bd\u672f\u6240\u5c48\uff0c\u5168\u5fc3\u63a5\u7eb3\u4e86\u6211\u65f6\uff0c\u6211\u4e5f\u4e0d\u81ea\u89c9\u63a5\u7eb3\u4e86\u5979\uff0c\u751f\u51fa\u601c\u63aa\uff0c\u6211\u771f\u7684\u662f\u6709\u4e9b\u6015\u4e86\uff01\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u8fd9\u4e48\u4e00\u4e2a\u9b3c\u672f\u6cd5\uff0c\u65bd\u5c55\u4e0b\u53bb\uff0c\u600e\u4e48\u5f97\u4e86\uff0c\u5c82\u4e0d\u53d8\u6210\u53d7\u5c3d\u4e07\u4eba\u553e\u5f03\u7684\u8272\u72fc\u4e86\uff01\u201d \u3000\u3000\u9752\u51e4\u4e5f\u542c\u5f97\u5446\u4f4f\u4e86\uff0c\u8fd9\u771f\u662f\u6740\u4eba\u4e00\u5343\uff0c\u81ea\u635f\u516b\u767e\u7684\u4e8b\uff0c\u5929\u72d0\u201c\u60d1\u5fc3\u672f\u201d\u4e0d\u540c\u4e8e\u4e00\u822c\u5a9a\u529f\u7684\u5730\u65b9\uff0c\u662f\u81ea\u5df1\u4e5f\u8981\u6709\u5f88\u591a\u4ed8\u51fa\uff0c\u4ee5\u60c5\u916c\u60c5\uff0c\u624d\u80fd\u4f7f\u60c5\u57fa\u7a33\u56fa\uff0c\u8fd9\u6837\u7684\u5947\u672f\uff0c\u7a76\u7adf\u662f\u597d\u662f\u574f\u5462\uff1f \u3000\u3000\u4eba\u4e3a\u672f\u8ff7\uff0c\u4f46\u4e5f\u5f97\u5230\u4e86\u4e2a\u90ce\u7684\u771f\u6b63\u7231\u60c5\u3002 \u3000\u3000\u201c\u4f46\u6b64\u884c\u7684\u76ee\u7684\uff0c\u603b\u7b97\u662f\u8fbe\u5230\u4e86\u2026\u2026\u201d\u9752\u51e4\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4ee5\u540e\u4e8b\uff0c\u8c01\u53c8\u80fd\u9884\u6599\u5462\uff1f\u4e71\u4e16\u513f\u5973\uff0c\u80fd\u6709\u6bb5\u523b\u9aa8\u76f8\u601d\u4e4b\u60c5\uff0c\u5929\u4e5f\u4e0d\u7b97\u8584\u6211\u4e86\uff0c\u5f20\u59e8\u8bf4\u8fc7\uff0c\u5979\u8981\u4f60\u771f\u6b63\u7684\u63a5\u7eb3\u84dd\u51e4\uff0c\u4f60\u4e0d\u5fc5\u611f\u5230\u6127\u548e\uff0c\u8d70\u4e86\uff0c\u5f20\u59e8\u8fd8\u5728\u7b49\u6211\u4eec\u5462\uff01\u201d \u3000\u3000\u4f46\u95fb\u8863\u8882\u98d8\u98ce\u4e4b\u58f0\uff0c\u5206\u7531\u56db\u9762\u516b\u65b9\u4f20\u6765\u3002 \u3000\u3000\u9752\u51e4\u7a81\u7136\u5c31\u5730\u4e00\u6eda\uff0c\u9690\u5931\u4e0d\u89c1\u3002 \u3000\u3000\u591c\u8272\u4e4b\u4e2d\uff0c\u9041\u672f\u66f4\u80fd\u53d1\u6325\u51fa\u501f\u7269\u9690\u5f62\u7684\u4f5c\u7528\u3002 \u3000\u3000\u201c\u597d\u3001\u597d\uff01\u8bf8\u4f4d\u90fd\u6765\u4e86\u3002\u201d \u3000\u3000\u8fd9\u4e24\u4e2a\u597d\u5b57\uff0c\u8bed\u610f\u53cc\u5173\uff0c\u8d5e\u9752\u51e4\u5e94\u53d8\u5f97\u597d\uff0c\u4f7f\u4ed6\u65e0\u65c1\u987e\u4e4b\u5fe7\uff0c\u4e5f\u6709\u8b66\u544a\u6765\u654c\uff0c\u4ed6\u8981\u653e\u624b\u4e00\u640f\u7684\u8bed\u6c14\u3002 \u3000\u3000\u56db\u4e2a\u4eba\u51e0\u4e4e\u662f\u540c\u65f6\u51fa\u73b0\u3002 \u3000\u3000\u4f7f\u6c5f\u67ab\u5fc3\u5934\u9707\u52a8\uff0c\u5927\u5403\u4e00\u60ca\u7684\u662f\uff0c\u6765\u4eba\u4e0d\u662f\u4e09\u9b54\u3001\u4e94\u602a\uff0c\u4e5f\u4e0d\u662f\u5929\u738b\u5929\u9e45\uff0c\u800c\u662f\u5929\u9a6c\u5802\u7684\u4eba\uff0c\u5929\u9a6c\u5802\u4e2d\u56db\u4e2a\u6700\u96be\u5bf9\u4ed8\u7684\u4eba\u3002 \u3000\u3000\u4ed6\u4eec\u662f\u9634\u9633\u79c0\u624d\u767d\u5929\u5316\uff0c\u4e00\u54c1\u5200\u6881\u9b41\uff0c\u5929\u9a6c\u884c\u7a7a\u53f8\u5f92\u827a\uff0c\u91d1\u51e4\u51f0\u7533\u96ea\u541b\u3002 \u3000\u3000\u7533\u96ea\u541b\u4eca\u591c\u7684\u88c5\u675f\u4e0d\u540c\uff0c\u80cc\u4e0a\u4f3c\u662f\u591a\u4e86\u4e00\u4e2a\u56ca\u888b\uff0c\u60f3\u6765\uff0c\u5b9a\u662f\u5979\u72ec\u6b65\u6b66\u6797\u7684\u7edd\u6280\uff0c\u5341\u51e4\u9f50\u98de\u4e86\u3002 \u3000\u3000\u201c\u767d\u9662\u4e3b\u679c\u7136\u662f\u7b97\u65e0\u9057\u7b56\uff0c\u2026\u2026\u201d\u53f8\u5f92\u827a\u62ac\u5934\u671b\u7740\u6c5f\u67ab\uff0c\u9053\uff1a\u201c\u4f60\u5c31\u662f\u6c5f\u67ab\u4e86\uff0c\u957f\u7684\u662f\u4e00\u8868\u4eba\u624d\uff0c\u4ed6\u4eec\u5462\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4eec\u662f\u8c01\uff1f\u2026\u2026\u201d\u6c5f\u67ab\u51b7\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u662f\u6c5f\u67d0\u4e00\u4e2a\u4eba\uff0c\u4e5f\u591f\u56db\u4f4d\u624b\u5fd9\u811a\u4e71\u4e86\uff0c\u4e00\u54c1\u5200\u6881\u697c\u4e3b\uff0c\u5e94\u8be5\u8fd8\u8bb0\u4e07\u6885\u576a\u4e2d\u7684\u6559\u8bad\u5427\uff1f\u201d \u3000\u3000\u4ed6\u8bdd\u5df3\u7136\u6697\u793a\u9752\u51e4\uff0c\u4e0d\u53ef\u968f\u4fbf\u51fa\u624b\u3002 \u3000\u3000\u201c\u679c\u7136\u662f\u4e00\u4e2a\u72c2\u5984\u5c0f\u5b50\uff0c\u2026\u2026\u201d\u53f8\u5f92\u827a\u9053\uff1a\u201c\u4eca\u591c\u7eb5\u7136\u4e0d\u8981\u4f60\u6b7b\uff0c\u4e5f\u8ba9\u4f60\u8131\u53bb\u4e00\u5c42\u76ae\uff0c\u5148\u8ba9\u5929\u865a\u5b50\u526f\u603b\u5802\u4e3b\u6b7b\u7684\u7a0d\u6709\u6170\u85c9\u3002\u201d \u3000\u3000\u6c5f\u67ab\u6697\u6697\u8fd0\u6c14\u8c03\u606f\uff0c\u548c\u84dd\u51e4\u5927\u534a\u5929\u7684\u7f20\u7ef5\u6069\u7231\uff0c\u5b9e\u4e5f\u6709\u4e00\u4e9b\u75b2\u7d2f\u4e4b\u611f\uff0c\u4e0d\u7ba1\u6c5f\u67ab\u7684\u5185\u529f\u591a\u4e48\u7cbe\u6df1\uff0c\u8fd8\u672a\u80fd\u903e\u8d8a\u4eba\u7684\u8303\u56f4\u3002 \u3000\u3000\u770b\u6c5f\u67ab\u95ed\u76ee\u4e0d\u7406\uff0c\u53f8\u5f92\u827a\u5fc3\u5934\u706b\u8d77\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u9662\u4e3b\uff0c\u8fd9\u5c0f\u5b50\u6258\u5927\u7684\u5f88\uff0c\u6211\u5148\u7ed9\u4ed6\u4e00\u70b9\u6559\u8bad\u3002\u201d \u3000\u3000\u767d\u5143\u5316\u5fae\u5fae\u6447\u9996\uff0c\u793a\u610f\u53f8\u5f92\u827a\u7b49\u4e00\u4e0b\u3002 \u3000\u3000\u53f8\u5f92\u827a\u4e00\u76b1\u7709\u5934\uff0c\u5fd6\u9053\uff1a\u201c\u6211\u95ee\u4f60\u4e00\u58f0\uff0c\u53ea\u4e0d\u8fc7\u662f\u4e00\u53e5\u793c\u8c8c\uff0c\u6211\u8fd9\u526f\u603b\u5802\u4e3b\u7684\u8eab\u4efd\uff0c\u53ef\u4e0d\u5728\u4f60\u798f\u5bff\u9662\u7684\u7ba1\u8f96\u4e4b\u4e0b\u3002\u201d \u3000\u3000\u6b63\u5f85\u6b3a\u8eab\u800c\u4e0a\uff0c\u7a81\u95fb\u98d2\u5730\u4e00\u58f0\uff0c\u4e00\u9053\u51b7\u8292\uff0c\u98de\u5c04\u800c\u8d77\uff0c\u6a2a\u7a7a\u65cb\u98de\u3002 \u3000\u3000\u201c\u9a6d\u5200\u672f\u201d\uff0c\u5200\u5149\u75be\u8f6c\u5982\u8f6e\uff0c\u7f69\u5411\u6c5f\u67ab\u3002 \u3000\u3000\u4e45\u95fb\u4e00\u54c1\u5200\u6881\u9b41\uff0c\u98de\u5200\u80fd\u5728\u767e\u6b65\u5185\u53d6\u4eba\u9996\u7ea7\uff0c\u662f\u6c5f\u6e56\u4eba\u6781\u4e3a\u5c11\u89c1\u7684\u795e\u6280\u3002 \u3000\u3000\u4eca\u665a\u4e0a\uff0c\u771f\u662f\u5927\u5f00\u4e86\u773c\u754c\u3002 \u3000\u3000\u770b\u5200\u52bf\u7684\u98de\u8f6c\uff0c\u5df2\u4e0d\u662f\u6280\u5de7\u8fd0\u5200\uff0c\u800c\u662f\u4ee5\u5185\u529b\u9a6d\u5200\uff0c\u548c\u201c\u9a6d\u5251\u672f\u201d\u4f3c\u6709\u5f02\u66f2\u540c\u5de5\u4e4b\u5999\u3002 \u3000\u3000\u4e0d\u540c\u4e4b\u5904\uff0c\u5251\u662f\u76f4\u51fb\uff0c\u5200\u662f\u6a2a\u98de\u3002 \u3000\u3000\u201c\u9a6d\u5251\u672f\u201d\u4e0d\u90fd\u662f\u4eba\u5251\u5408\u4e00\uff0c\u4eba\u878d\u5165\u5251\uff0c\u6709\u5982\u7535\u63a3\u95ea\u98de\u800c\u6765\u3002 \u3000\u3000\u5200\u5374\u662f\u8131\u624b\u98de\u51fa\uff0c\u65cb\u8f6c\u5982\u8f6e\u3002 \u3000\u3000\u53f8\u5f92\u827a\u6697\u6697\u5fd6\u9053\uff1a\u201c\u798f\u5bff\u9662\u4e2d\u7684\u516d\u5927\u697c\u4e3b\uff0c\u679c\u7136\u90fd\u662f\u8eab\u8d1f\u7edd\u6280\u7684\u4eba\u7269\uff0c\u53ea\u6b64\u4e00\u5200\uff0c\u5c31\u975e\u6211\u6240\u80fd\u4e86\u3002\u201d \u3000\u3000\u6c5f\u67ab\u5fae\u95ed\u7684\u53cc\u76ee\u7a81\u7136\u7741\u5f00\uff0c\u8f6e\u8f6c\u800c\u6765\u7684\u98de\u5200\uff0c\u5df2\u7136\u8fd1\u8eab\u3002 \u3000\u3000\u6c5f\u67ab\u7a81\u7136\u4e3e\u6b65\u4e00\u8de8\uff0c\u4eba\u7adf\u767b\u7a7a\u800c\u8d77\uff0c\u95ea\u8d8a\u5200\u8f6e\uff0c\u659c\u5411\u4e00\u54c1\u5bf9\u6881\u9b41\u98de\u53bb\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4e0d\u591f\u706b\u5019\uff0c\u4f60\u7684\u2018\u9a6d\u5200\u672f\u2019\u5982\u80fd\u518d\u5feb\u4e00\u4e9b\uff0c\u4e5f\u8bb8\u5df2\u7ecf\u53d6\u53bb\u4e86\u6211\u6c5f\u67d0\u4eba\u7684\u9996\u7ea7\uff0c\u53ea\u53ef\u60dc\u554a\uff01\u5dee\u90a3\u4e48\u4e00\u70b9\u70b9\u3002\u201d \u3000\u3000\u53e3\u4e2d\u8bf4\u8bdd\uff0c\u53f3\u638c\u5df2\u5230\u4e86\u6881\u9b41\u7684\u524d\u80f8\u3002 \u3000\u3000\u770b\u4e0a\u53bb\uff0c\u6c5f\u67ab\u4e00\u677f\u4e00\u773c\u7684\u52a8\u4f5c\uff0c\u5b9e\u5219\u5feb\u901f\u81f3\u6781\uff0c\u903c\u5f97\u6881\u9b41\u4e0d\u5f97\u4e0d\u53cc\u638c\u6325\u51fa\uff0c\u63a5\u4e0b\u4e86\u6c5f\u67ab\u4e00\u51fb\u3002 \u3000\u3000\u638c\u529b\u63a5\u5b9e\uff0c\u5fae\u5fae\u8f7b\u9707\uff0c\u4f46\u6881\u9b41\u5374\u9707\u5f97\u8fde\u9000\u4e86\u4e94\u6b65\u3002 \u3000\u3000\u5f53\u7684\u4e00\u58f0\uff0c\u65cb\u98de\u7684\u94a2\u5200\uff0c\u7a81\u7136\u843d\u5730\u3002 \u3000\u3000\u539f\u6765\uff0c\u6881\u9b41\u5168\u529b\u8fd0\u638c\uff0c\u63a7\u5236\u98de\u5200\u7684\u529b\u9053\u6d88\u5931\uff0c\u5df2\u65e0\u9a6d\u5200\u4e4b\u529b\u3002 \u3000\u3000\u6881\u9b41\u8eab\u8eaf\u7ad9\u7a33\uff0c\u7a81\u7136\u4e00\u5f20\u53e3\uff0c\u55b7\u51fa\u6765\u4e00\u80a1\u8840\u7bad\uff0c\u9053\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\u638c\u529b\uff0c\u6467\u5fc3\u88c2\u8151\uff0c\u9662\u4e3b\uff0c\u5c0f\u5fc3\u4e86\u2026\u2026\u201d \u3000\u3000\u8eab\u8eaf\u5012\u4e0b\uff0c\u53e3\u4e2d\u9c9c\u8840\u4e0d\u505c\u6d8c\u51fa\u6765\u3002 \u3000\u3000\u201c\u6881\u697c\u4e3b\uff0c\u5feb\u4e9b\u8fd0\u6c14\u8c03\u606f\u2026\u2026\u201d \u3000\u3000\u767d\u5929\u5316\u4eba\u5f71\u4e00\u95ea\uff0c\u5df2\u6321\u5728\u6c5f\u67ab\u8eab\u524d\u3002 \u3000\u3000\u4e0d\u95fb\u6881\u9b41\u56de\u7b54\u4e4b\u58f0\uff0c\u4f3c\u5df2\u6c14\u7edd\u800c\u901d\u3002 \u3000\u3000\u767d\u5929\u5316\u9707\u60ca\u4e86\u3002 \u3000\u3000\u6c5f\u67ab\u4e00\u638c\u9707\u9000\u6881\u9b41\uff0c\u622a\u65ad\u4e86\u4ed6\u9a6d\u5200\u7684\u5185\u52b2\uff0c\u767d\u5929\u5316\u8fd8\u4e0d\u592a\u653e\u5728\u5fc3\u4e0a\uff0c\u4f46\u6881\u9b41\u7a81\u7136\u5012\u5730\u6b7b\u53bb\uff0c\u767d\u5929\u5316\u5374\u60ca\u9a87\u4e0d\u5df2\u3002 \u3000\u3000\u53f8\u5f92\u827a\u4e5f\u5446\u4f4f\u4e86\u3002 \u3000\u3000\u542c\u8bf4\u4e03\u5b9d\u548c\u5c1a\u4e00\u638c\u9707\u6b7b\u5929\u865a\u5b50\uff0c\u90a3\u662f\u5728\u5929\u865a\u5b50\u5168\u65e0\u9632\u5907\u4e4b\u4e0b\uff0c\u5927\u610f\u94f8\u9519\u3002 \u3000\u3000\u4f46\u6c5f\u67ab\u8fd9\u4e00\u51fb\uff0c\u53ef\u662f\u5728\u6881\u9b41\u5168\u529b\u6212\u5907\u4e4b\u4e0b\u3002 \u3000\u3000\u5982\u4e00\u6876\u51b7\u6c34\uff0c\u515c\u5934\u6d47\u4e0b\uff0c\u53f8\u5f92\u827a\u4e00\u80a1\u72c2\u505a\u4e4b\u6c14\uff0c\u9661\u7136\u6d88\u5931\u65e0\u8e2a\uff0c\u5fc3\u5934\u4e0a\u6cdb\u751f\u51fa\u5f3a\u70c8\u7684\u754f\u60e7\u4e4b\u611f\u3002 \u3000\u3000\u751f\u6b7b\u5927\u4e8b\u554a\uff01 \u3000\u3000\u4e00\u4e2a\u4eba\uff0c\u53ea\u6709\u4e00\u6b21\u7684\u6b7b\u4ea1\u673a\u4f1a\u3002 \u3000\u3000\u767d\u5929\u5316\u8138\u8272\u51dd\u91cd\u9053\uff1a\u201c\u6c5f\u67ab\uff0c\u4f60\u8fd9\u662f\u4ec0\u4e48\u6b66\u529f\uff0c\u5982\u6b64\u7684\u9738\u9053\u51f6\u6b8b\u3002\u201d \u3000\u3000\u201c\u542c\u8bf4\u4f60\u6709\u672a\u535c\u5148\u77e5\u4e4b\u80fd\uff0c\u6587\u662f\u72b6\u5143\u4e4b\u624d\uff0c\u6b66\u5217\u7edd\u4e16\u9ad8\u624b\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u731c\u731c\u770b\uff0c\u6211\u7528\u7684\u4ec0\u4e48\u638c\u529b\uff1f\u201d \u3000\u3000\u201c\u7edd\u4e0d\u662f\u65e0\u76f8\u795e\u529f\uff0c\u5b83\u9634\u67d4\u3001\u6b79\u6bd2\uff0c\u4f24\u4eba\u4e8e\u4e0d\u77e5\u4e0d\u89c9\u4e4b\u4e2d\u2026\u2026\u201d \u3000\u3000\u767d\u5929\u5316\u9053\uff1a\u201c\u5e94\u8be5\u662f\u6467\u5fc3\u638c\u4e00\u7c7b\u7684\u6b79\u6bd2\u6b66\u5b66\u3002\u201d \u3000\u3000\u201c\u4e0d\u662f\u65e0\u76f8\u795e\u529f\uff0c\u88ab\u4f60\u731c\u5bf9\u4e86\u2026\u2026\u201d\u6c5f\u67ab\u7b11\u9053\uff1a\u201c\u4f46\u8bf4\u5b83\u662f\u50ac\u5fc3\u638c\uff0c\u5c31\u5341\u5206\u53ef\u7b11\u4e86\uff0c\u5929\u9a6c\u5802\u798f\u5bff\u9662\u4e2d\u516d\u5927\u697c\u4e3b\u4e4b\u4e00\uff0c\u63a5\u4e0d\u4e0b\u4e00\u8bb0\u63a8\u5fc3\u638c\uff0c\u4f60\u8fd9\u4f5c\u9662\u4e3b\u7684\uff0c\u4e5f\u4e0d\u89c9\u60ed\u6127\u4e48\uff1f\u201d \u3000\u3000\u767d\u5929\u5316\u53cc\u988a\u53d1\u70ed\uff0c\u4f46\u4ed6\u5fc3\u4e2d\u660e\u767d\uff0c\u4eca\u591c\u9047\u4e0a\u7684\u662f\u4e00\u751f\u4e2d\u4ece\u672a\u9047\u8fc7\u7684\u9ad8\u624b\uff0c\u4efb\u4f55\u4e00\u4e2a\u758f\u5ffd\u5927\u610f\uff0c\u90fd\u53ef\u80fd\u9001\u4e86\u8001\u547d\uff0c\u6697\u6697\u5438\u4e00\u53e3\u6c14\uff0c\u5e73\u590d\u4e0b\u6ce2\u52a8\u7684\u5fc3\u60c5\u3002\u9053\uff1a\u201c\u8001\u592b\u7684\u610f\u601d\u662f\u5b83\u5c5e\u4e8e\u9634\u67d4\u4e00\u7c7b\u7684\u6b66\u529f\u3002\u201d \u3000 \u7b2c\u4e8c\u5341\u56db\u56de\u3000\u8001\u65b9\u4e08\u4f24\u6108\u8d60\u795e\u5251 \u3000 \u3000\u3000\u6c5f\u67ab\u7528\u51fa\u8fd9\u4e00\u638c\u4e4b\u540e\uff0c\u4e5f\u89c9\u7740\u5fc3\u795e\u6ce2\u52a8\uff0c\u7acb\u523b\u89c9\u609f\u5230\uff0c\u8fd9\u79cd\u8d2f\u6ce8\u5168\u529b\u7684\u4e00\u51fb\uff0c\u4e09\u4e94\u638c\u5c31\u4f1a\u5927\u5598\u6c14\uff0c\u75b2\u7d2f\u4e0d\u582a\uff0c\u539f\u60f3\u80fd\u529b\u6218\u4e09\u4e94\u4e2a\u65f6\u8fb0\u4e0d\u81f3\u7b4b\u75b2\u529b\u5c3d\u7684\u60f3\u6cd5\uff0c\u7acb\u523b\u6253\u6d88\uff0c\u5bf9\u96ea\u3001\u971c\u4e8c\u5973\u7684\u671f\u8bb8\uff0c\u4e5f\u6253\u4e86\u4e00\u4e2a\u5927\u6298\u6263\uff0c\u654c\u4eba\u771f\u8981\u6709\u4e09\u4e94\u767e\u4eba\u7684\u5408\u51fb\uff0c\u518d\u52a0\u4e0a\u5341\u51e0\u4e2a\u9ad8\u624b\u529b\u6218\uff0c\u7edd\u6ca1\u6709\u7a81\u56f4\u800c\u53bb\u7684\u53ef\u80fd\u3002 \u3000\u3000\u8fc7\u53bb\uff0c\u628a\u81ea\u5df1\u4f30\u8ba1\u7684\u592a\u9ad8\u4e86\u3002 \u3000\u3000\u8fd9\u79cd\u7a0d\u6709\u75b2\u7d2f\u7684\u611f\u89c9\uff0c\u7edd\u4e0d\u80fd\u8ba9\u767d\u5929\u5316\u7b49\u77a7\u51fa\u6765\u3002 \u3000\u3000\u4e3b\u610f\u6697\u5b9a\uff0c\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e0d\u662f\u9634\u67d4\uff0c\u5c31\u662f\u521a\u9633\uff0c\u8fd9\u8303\u56f4\u592a\u5e7f\u9614\u4e86\uff1f\u201d \u3000\u3000\u85c9\u673a\u626f\u8c08\uff0c\u6697\u4e2d\u8c03\u606f\u3002 \u3000\u3000\u201c\u8001\u592b\u5c31\u627f\u8ba4\u5427\uff01\u4ece\u672a\u89c1\u8fc7\u8fd9\u79cd\u638c\u529b\uff0c\u4e5f\u672a\u542c\u8bf4\u8fc7\u8fd9\u79cd\u6b66\u529f\u2026\u2026\u201d\u767d\u5929\u5316\u9053\uff1a\u201c\u6c5f\u5c11\u5144\u80fd\u4e0d\u80fd\u660e\u767d\u8bf4\u51fa\u6765\uff0c\u8ba9\u767d\u67d0\u4e00\u5f00\u8305\u585e\uff0c\u4f46\u767d\u67d0\u4eba\u53ef\u4ee5\u65ad\u8a00\u7684\uff0c\u8fd9\u7edd\u4e0d\u662f\u6b66\u6797\u4e09\u5723\u7684\u6b66\u529f\u3002\u201d \u3000\u3000\u6c5f\u67ab\u5168\u8eab\u8109\u7a74\u5df2\u901a\uff0c\u7a0d\u4f5c\u8c03\u606f\uff0c\u4eba\u5df2\u590d\u5143\uff0c\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e0d\u80fd\u8bf4\uff0c\u767d\u9662\u4e3b\u5982\u60f3\u6382\u51fa\u6c5f\u67d0\u4eba\u7684\u65a4\u4e24\uff0c\u53ea\u6709\u51fa\u624b\u4e00\u8bd5\u4e86\uff0c\u4ee5\u4f60\u767d\u9662\u4e3b\u89c1\u8bc6\u4e4b\u5e7f\uff0c\u76f8\u4fe1\u5728\u4e09\u4e94\u62db\u5185\uff0c\u5c31\u53ef\u77a7\u51fa\u6211\u7ec3\u7684\u4ec0\u4e48\u638c\u529b\u4e86\uff01\u201d \u3000\u3000\u767d\u5929\u5316\u5446\u4f4f\u4e86\uff0c\u6c5f\u67ab\u5fc3\u5b58\u6740\u673a\uff0c\u8f6f\u786c\u4e0d\u5403\uff0c\u5e0c\u671b\u903c\u8feb\u767d\u5929\u5316\u51fa\u624b\uff0c\u85c9\u673a\u9664\u53bb\u5929\u9a6c\u5802\u8fd9\u4e2a\u6700\u96be\u6d4b\u5ea6\u7684\u9ad8\u624b\u3002 \u3000\u3000\u53f8\u5f92\u827a\u5df2\u8c6a\u6c14\u5c3d\u6d88\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u7533\u526f\u5802\u4e3b\uff0c\u6c5f\u67ab\u7684\u6b66\u529f\u5947\u5965\u96be\u6d4b\uff0c\u767d\u9662\u4e3b\u4e00\u4e2a\u4eba\u53ea\u6015\u5e94\u4ed8\u4e0d\u4e86\uff0c\u6211\u548c\u4ed6\u8054\u624b\u8fce\u654c\uff0c\u4f60\u65bd\u5c55\u5341\u51e4\u9f50\u98de\uff0c\u52a9\u6211\u4e00\u81c2\u4e4b\u529b\u5982\u4f55\uff1f\u201d \u3000\u3000\u6001\u5ea6\u5927\u53d8\uff0c\u5b8c\u5168\u662f\u4e00\u79cd\u60c5\u5546\u6c42\u52a9\u7684\u53e3\u6c14\u3002 \u3000\u3000\u6c5f\u67ab\u5fc3\u4e2d\u4e00\u52a8\uff0c\u5fd6\u9053\uff1a\u201c\u8fd9\u4e2a\u4eba\u80fd\u5c48\u80fd\u4f38\uff0c\u672c\u6027\u9634\u9669\uff0c\u8981\u65e9\u4e9b\u9664\u53bb\uff0c\u4ee5\u7edd\u540e\u60a3\uff01\u201d \u3000\u3000\u6c5f\u67ab\u5fc3\u4e2d\u867d\u662f\u6697\u4f5c\u76d8\u7b97\uff0c\u4f46\u5374\u5e76\u672a\u62a2\u5148\u51fa\u624b\u3002 \u3000\u3000\u770b\u51c6\u654c\u4eba\u7684\u7f3a\u5931\u6240\u5728\uff0c\u4e00\u4e3e\u95f4\u53d6\u654c\u4e4b\u547d\uff0c\u4f7f\u654c\u4eba\u60f6\u6050\u8ff7\u4e71\uff0c\u5df2\u662f\u6c5f\u67ab\u91cd\u8981\u7684\u5bf9\u654c\u89c2\u5ff5\uff0c\u51cc\u5389\u4e00\u51fb\uff0c\u8fd8\u4e0d\u80fd\u53d6\u654c\u4e4b\u547d\uff0c\u5c31\u5fc5\u9700\u8981\u65bd\u5c55\u51fa\u7531\u4f20\u771f\u8bb0\u4e0a\u201c\u609f\u201d\u5f97\u7684\u6b66\u529f\u7edd\u6280\uff0c\u624d\u80fd\u6446\u8131\u7f20\u6597\u3002 \u3000\u3000\u5982\u679c\u52a8\u4e0a\u624b\uff0c\u5341\u62db\u5185\u65e0\u6cd5\u514b\u654c\u81f4\u80dc\uff0c\u9677\u5165\u4e86\u93d6\u6218\u7684\u5c40\u9762\uff0c\u5f88\u53ef\u80fd\u5c31\u843d\u5165\u91cd\u56f4\uff0c\u4e0d\u4f46\u4f1a\u51cf\u5c11\u4e86\u9707\u64bc\u654c\u4eba\u7684\u5a01\u671b\uff0c\u4e5f\u5c06\u6fc0\u8d77\u4f17\u591a\u654c\u4eba\u7684\u62fc\u6597\u610f\u5fd7\uff0c\u6ca6\u5165\u8d25\u4ea1\u7684\u53ef\u80fd\u3002 \u3000\u3000\u6e83\u654c\u6218\u5fd7\uff0c\u624d\u80fd\u5c48\u654c\u4e4b\u5175\uff0c\u4ee5\u5be1\u80dc\u4f17\u3002 \u3000\u3000\u6c5f\u67ab\u660e\u767d\u81ea\u5df1\u662f\u5a01\u5c48\u654c\u4eba\u6218\u5fd7\u7684\u738b\u8005\uff0c\u4e0d\u80fd\u6ca6\u5165\u93d6\u6218\u6570\u767e\u62db\u7684\u5f62\u52bf\uff0c\u8fd9\u5c31\u662f\u6c5f\u67ab\u4e0d\u5f97\u4e0d\u8c28\u614e\u4ece\u4e8b\uff0c\u771f\u662f\u9ad8\u5904\u4e0d\u80dc\u5bd2\u554a\uff01 \u3000\u3000\u7533\u96ea\u541b\u679c\u7136\u7531\u80cc\u56ca\u4e2d\u53d6\u51fa\u4e86\u540d\u52a8\u6b66\u6797\u7684\u5341\u51e4\u9f50\u98de\u3002 \u3000\u3000\u90a3\u662f\u5341\u652f\u91d1\u9ec4\u8272\u51e4\u5934\u7684\u9e1f\u5f62\u6697\u5668\uff0c\u53f3\u7ffc\u6709\u5c3e\uff0c\u4f46\u4f53\u578b\u4e0d\u5927\uff0c\u8bf4\u5b83\u662f\u91d1\u51e4\uff0c\u5012\u4e0d\u5982\u8bf4\u5b83\u50cf\u91d1\u4e1d\u96c0\u6765\u7684\u6070\u5f53\u3002 \u3000\u3000\u7533\u96ea\u541b\u7ea4\u5de7\u7684\u53cc\u624b\uff0c\u7adf\u7136\u80fd\u5404\u63e1\u56db\u652f\uff0c\u5341\u51e4\u9f50\u98de\uff0c\u5df2\u7136\u516b\u51e4\u5728\u624b\u3002 \u3000\u3000\u6c5f\u67ab\u51dd\u76ee\u77a7\u7740\uff0c\u53d1\u89c9\u4e86\u51e4\u7ffc\u4f1a\u52a8\u3002 \u3000\u3000\u8fd9\u5c31\u975e\u540c\u5c0f\u53ef\u4e86\u3002 \u3000\u3000\u53cc\u7ffc\u80fd\u591f\u6d3b\u52a8\u7684\u6697\u5668\uff0c\u5df2\u4e0d\u662f\u653b\u53d6\u654c\u4eba\u65f6\u529b\u9053\u7684\u5f3a\u5f31\u4e86\uff0c\u800c\u662f\u5b83\u5fc5\u9700\u5177\u6709\u9ad8\u5ea6\u7684\u7279\u6b8a\u6280\u5de7\uff0c\u624d\u80fd\u51fa\u624b\u3002 \u3000\u3000\u5b83\u4e5f\u660e\u663e\u7684\u8868\u793a\u51fa\uff0c\u8fd9\u5c0f\u5c0f\u91d1\u51e4\u7684\u8179\u5185\uff0c\u53ef\u80fd\u522b\u85cf\u6740\u673a\u3002 \u3000\u3000\u5343\u9762\u5973\u9b45\u65b9\u5999\uff0c\u6dec\u6bd2\u7684\u5bd2\u94c1\u593a\u9b42\u9488\uff0c\u4e00\u51fb\u4e4b\u4e0b\uff0c\u51e0\u4e4e\u8981\u4e86\u6c5f\u67ab\u7684\u6027\u547d\uff0c\u6b66\u827a\u6280\u827a\u4e2d\u6709\u627f\u53d7\u4e00\u51fb\u7684\u91d1\u949f\u7f69\u3001\u94c1\u5e03\u886b\u7b49\u5916\u95e8\u6c14\u529f\uff0c\u4e0d\u754f\u4e00\u822c\u5200\u5251\u4f24\u8eab\u7684\u5185\u5bb6\u91cc\u624b\u3002 \u3000\u3000\u4f46\u6c5f\u6e56\u4e0a\uff0c\u4e5f\u6709\u7cbe\u70bc\u5c16\u5229\uff0c\u7ec6\u5982\u53d1\u4e1d\uff0c\u7a7f\u77f3\u900f\u91d1\u7684\u9510\u950b\u6697\u5668\uff0c\u8fd9\u4e9b\u6697\u5668\u51fa\u81ea\u5de7\u624b\u3001\u540d\u5320\uff0c\u5927\u90e8\u5206\u90fd\u6dec\u6709\u5947\u6bd2\u3002 \u3000\u3000\u6c5f\u67ab\u660e\u767d\u518d\u4e5f\u53d7\u4e0d\u8d77\u7b2c\u4e8c\u6b21\u7684\u4f24\u5bb3\u4e86\uff0c\u56e0\u4e3a\uff0c\u6613\u82b1\u624b\u5f20\u56db\u59d1\u6ca1\u6709\u7b2c\u4e8c\u9897\u5927\u8fd8\u4e39\u80fd\u89e3\u5947\u6bd2\uff0c\u4f55\u51b5\uff0c\u4e5f\u65e0\u6cd5\u8d76\u6765\u63f4\u6551\u3002 \u3000\u3000\u6b66\u6797\u4e2d\u4f20\u8bf5\u7740\u5341\u51e4\u9f50\u98de\u7684\u51f6\u7edd\u9738\u9053\uff0c\u4f46\u5374\u6ca1\u6709\u4eba\u80fd\u8bf4\u51fa\u5b83\u6740\u4eba\u7684\u7ecf\u8fc7\u60c5\u5f62\uff1f \u3000\u3000\u56e0\u4e3a\uff0c\u89c1\u8fc7\u5b83\u51fa\u624b\u7684\u4eba\u90fd\u5df2\u6b7b\u5728\u4e86\u5341\u51e4\u9f50\u98de\u4e4b\u4e0b\u3002 \u3000\u3000\u6b66\u6797\u4e09\u5723\u4e2d\u65e0\u76f8\u5927\u5e08\u7684\u4e94\u798f\u7167\u547d\u5708\uff0c\u53ea\u6709\u91d1\u3001\u94f6\u3001\u94dc\u3001\u94c1\u3001\u9521\uff0c\u4e94\u5708\u73af\u98de\uff0c\u5341\u51e4\u9f50\u98de\u5374\u591a\u4e86\u4e00\u500d\u3002 \u3000\u3000\u6c5f\u67ab\u65e0\u6cd5\u660e\u767d\uff0c\u7533\u96ea\u541b\u7684\u5341\u51e4\u9f50\u98de\u662f\u5426\u4e5f\u50cf\u4e94\u798f\u7167\u547d\u5708\u4e00\u6837\uff0c\u6709\u4e00\u6839\u7ec6\u7d22\u5728\u624b\u4e2d\u64cd\u7eb5\uff0c\u4f46\u5b83\u53cc\u7ffc\u4f1a\u52a8\uff0c\u8bf4\u660e\u4e86\u7edd\u4e0d\u662f\u4e00\u51fb\u4e0d\u4e2d\u5c31\u4f1a\u8dcc\u843d\u5728\u5730\u4e0a\u7684\u6697\u5668\u3002 \u3000\u3000\u731c\u4e0d\u900f\u4e2a\u4e2d\u6280\u5de7\uff0c\u4f7f\u6c5f\u67ab\u6709\u70b9\u7d27\u5f20\uff0c\u4f20\u771f\u8bb0\u4e0a\u827a\u517c\u767e\u5bb6\uff0c\u5c31\u662f\u6ca1\u6709\u8bf4\u660e\u5bf9\u4ed8\u8fd9\u79cd\u575a\u5f02\u6697\u5668\u7684\u529e\u6cd5\u3002 \u3000\u3000\u6c5f\u67ab\u62d4\u51fa\u4e86\u7fe0\u7389\u5200\u3002 \u3000\u3000\u4e5f\u8bb8\u90a3\u5c0f\u5c0f\u91d1\u51e4\u4e0a\u5168\u90fd\u6dec\u4e86\u5947\u6bd2\uff0c\u662f\u4e0d\u80fd\u7528\u624b\u78b0\u51fb\u7684\u5229\u5668\u3002 \u3000\u3000\u76ee\u7779\u7533\u96ea\u541b\u91d1\u51e4\u5728\u624b\uff0c\u53f8\u5f92\u827a\u80c6\u6c14\u4e00\u58ee\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u767d\u5144\uff0c\u6c5f\u67ab\u6b66\u529f\u7cbe\u7edd\uff0c\u4e0d\u53ef\u5c0f\u89d1\uff0c\u54b1\u4eec\u8054\u624b\u5408\u51fb\u5427\u3002 \u3000\u3000\u518d\u52a0\u4e0a\u7533\u526f\u603b\u5802\u4e3b\u7684\u5341\u51e4\u9f50\u98de\u76f8\u52a9\uff0c\u5982\u662f\u8fd8\u4e0d\u80fd\u80dc\u4ed6\uff0c\u5c31\u7b97\u603b\u5802\u4e3b\u4eb2\u8eab\u4e34\u654c\uff0c\u4e5f\u65e0\u80dc\u4ed6\u7684\u628a\u63e1\u4e86\u3002\u201d \u3000\u3000\u201c\u8bf4\u7684\u6709\u7406\uff0c\u2026\u2026\u201d\u767d\u5929\u5316\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u4e0d\u53ef\u71e5\u8fdb\uff01\u201d \u3000\u3000\u201c\u767d\u5144\u7684\u610f\u601d\u662f\u8ba9\u4ed6\u5148\u673a\u4e86\uff1f\u201d \u3000\u3000\u201c\u6c5f\u67ab\u4e0d\u4f46\u6b66\u827a\u7cbe\u7edd\uff0c\u800c\u4e14\u89c2\u5bdf\u5165\u5fae\u2026\u2026\u201d\u767d\u5929\u5316\u9053\uff0c\u201c\u54b1\u4eec\u4e00\u51fa\u624b\uff0c\u5c31\u53ef\u80fd\u88ab\u4ed6\u770b\u51fa\u7834\u7efd\uff0c\u518d\u4f5c\u96f7\u9706\u4e00\u51fb\uff0c\u4ed6\u5df2\u6df1\u5f97\u4ee5\u9759\u5236\u52a8\u4e4b\u5999\uff0c\u53ef\u53f9\u7684\u662f\u5929\u4e0b\u6ca1\u6709\u5168\u65e0\u7834\u7efd\u7684\u6b66\u529f\u3002 \u3000\u3000\u4e0a\u4e58\u5883\u754c\u7684\u6280\u827a\uff0c\u4e0d\u4f46\u8981\u6bd4\u7075\u52a8\u3001\u901f\u5ea6\uff0c\u4e5f\u8981\u6bd4\u8010\u5fc3\u3001\u5fcd\u6027\uff0c\u6881\u9b41\u6402\u4e3b\u7684\u6b66\u529f\uff0c\u4e5f\u8bb8\u4e0d\u5982\u6c5f\u67ab\uff0c\u4f46\u6c5f\u67ab\u5982\u679c\u770b\u4e0d\u51fa\u4ed6\u7684\u7834\u7efd\u6240\u5728\uff0c\u7edd\u65e0\u6cd5\u5728\u4e00\u51fb\u4e2d\u5c31\u53d6\u4e86\u6881\u697c\u4e3b\u7684\u6027\u547d\u3002\u201d \u3000\u3000\u201c\u9ad8\u8bba\uff0c\u9ad8\u8bba\uff0c\u2026\u2026\u201d\u53f8\u5f92\u827a\u9053\uff1a\u201c\u60c5\u5230\u6df1\u5904\u65e0\u6028\u5fe7\uff0c\u827a\u767b\u7edd\u9876\u5728\u4e00\u51fb\uff0c\u54b1\u4eec\u8ddf\u4ed6\u8017\u4e0b\u53bb\u5566\uff01\u201d \u3000\u3000\u6c5f\u67ab\u5fc3\u4e2d\u5fd6\u9053\uff1a\u201c\u767d\u5929\u5316\u80fd\u4e00\u8a00\u9053\u51fa\u6211\u5fc3\u4e2d\u4e4b\u79d8\uff0c\u679c\u7136\u662f\u6587\u3001\u6b66\u5168\u624d\uff0c\u53ea\u4e0d\u8fc7\uff0c\u4ed6\u8fd8\u65e0\u6cd5\u4f30\u900f\u4f20\u771f\u8bb0\u4e0a\u7684\u6b66\u529f\u4e4b\u5999\uff0c\u6211\u5e94\u6709\u5341\u62db\u4e4b\u5185\uff0c\u6740\u6b7b\u4ed6\u7684\u80fd\u529b\uff0c\u52a0\u4e0a\u53f8\u5f92\u827a\uff0c\u4e5f\u65e0\u6cd5\u62d6\u8fc7\u4e8c\u5341\u62db\uff0c\u4f46\u53ef\u6015\u7684\u662f\u5341\u51e4\u9f50\u98de\u554a\uff01\u201d \u3000\u3000\u4f46\u95fb\u8863\u8882\u98d8\u98ce\u4e4b\u58f0\uff0c\u4e5d\u6761\u4eba\u5f71\u75be\u98de\u800c\u81f3\u3002 \u3000\u3000\u662f\u84dd\u51e4\uff0c\u5e26\u7740\u4e09\u9b54\u3001\u4e94\u602a\uff0c\u5954\u9a70\u800c\u5230\u3002 \u3000\u3000\u201c\u84dd\u51e4\u59d1\u5a18\uff0c\u6765\u7684\u597d\uff01\u201d\u53f8\u5f92\u827a\u9053\uff1a\u201c\u8fd9\u4e2a\u4eba\u5c31\u662f\u6c5f\u67ab\uff0c\u7adf\u6562\u5355\u4eba\u4e00\u5200\uff0c\u6df1\u5165\u8d35\u95e8\u8179\u5730\uff0c\u54b1\u4eec\u5e2e\u59d1\u5a18\u62e6\u4e0b\u6765\u4e86\uff01\u201d \u3000\u3000\u201c\u65e2\u7136\u62e6\u4f4f\u4e86\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u628a\u4ed6\u6349\u4f4f\u5462\uff1f\u2026\u2026\u201d\u84dd\u51e4\u76ee\u5149\u8f6c\u52a8\uff0c\u770b\u770b\u6881\u9b41\u4ef0\u5367\u5730\u4e0a\u7684\u5c38\u4f53\uff0c\u63a5\u9053\uff1a\u201c\u64d2\u83b7\u6c5f\u67ab\uff0c\u53ef\u662f\u4e00\u4ef6\u5f88\u5927\u7684\u529f\u52b3\uff0c\u5929\u9a6c\u5802\u3001\u5929\u540e\u5bab\u548c\u672c\u95e8\u4e2d\u4eba\uff0c\u90fd\u662f\u4e3a\u4ed6\u5927\u5174\u5e72\u6208\u800c\u6765\uff0c\u5982\u4eca\u4eba\u5728\u773c\u524d\uff0c\u8bf8\u4f4d\u5374\u6309\u5175\u4e0d\u52a8\uff0c\u662f\u4f55\u9053\u7406\uff1f\u201d \u3000\u3000\u201c\u59d1\u5a18\u2026\u2026\u201d\u53f8\u5f92\u827a\u63a5\u9053\uff1a\u201c\u9002\u624d\u4e00\u756a\u640f\u6740\uff0c\u6211\u4eec\u4e00\u65f6\u5927\u610f\uff0c\u6b7b\u4e86\u4e00\u4f4d\u697c\u4e3b\uff0c\u6b63\u51c6\u5907\u8054\u624b\u5408\u51fb\uff0c\u59d1\u5a18\u6b63\u597d\u7387\u4eba\u5230\u6b64\uff0c\u8fd9\u6c5f\u67ab\u6b66\u529f\u4e4b\u9ad8\uff0c\u5c24\u8fc7\u4f20\u65b9\uff0c\u7edd\u5bf9\u4e0d\u53ef\u8f7b\u654c\uff0c\u59d1\u5a18\u5982\u80af\u548c\u655d\u5802\u8054\u624b\uff0c\u751f\u64d2\u6c5f\u67ab\u5c31\u6613\u5982\u53cd\u638c\u4e86\u3002\u201d \u3000\u3000\u201c\u5929\u738b\u95e8\u4e0d\u6562\u63a0\u7f8e\uff0c\u6c5f\u67ab\u65e2\u662f\u8d35\u5802\u5148\u53d1\u73b0\u7684\uff0c\u8fd9\u4ef6\u5929\u5927\u7684\u529f\u52b3\uff0c\u5c31\u7531\u5929\u9a6c\u5802\u72ec\u9886\u98ce\u9a9a\u5427\uff01\u2026\u2026\u201d\u84dd\u51e4\u7b11\u9053\uff1a\u201c\u8bf8\u4f4d\u5982\u662f\u4e0d\u654c\u6218\u8d25\uff0c\u5929\u738b\u95e8\u518d\u63a5\u624b\u4e0d\u8fdf\uff0c\u53cc\u65b9\u8054\u624b\uff0c\u529f\u8fc7\u7f20\u5939\u4e00\u8d77\uff0c\u5f88\u96be\u8bf4\u5f97\u6e05\u695a\u4e86\uff1f\u201d \u3000\u3000\u53f8\u5f92\u827a\u4f3c\u662f\u672a\u60f3\u5230\u4f1a\u662f\u8fd9\u4e48\u4e00\u4e2a\u7ed3\u679c\uff0c\u5fae\u5fae\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4e0d\u7ba1\u751f\u64d2\u6216\u662f\u51fb\u6bd9\u6c5f\u67ab\uff0c\u5927\u5c40\u7acb\u523b\u5e95\u5b9a\uff0c\u4f59\u5b50\u788c\u788c\uff0c\u4e0d\u8db3\u754f\u4e5f\u3002\u8d35\u95e8\u548c\u655d\u5802\u8054\u624b\uff0c\u8fc7\u5219\u5206\u62c5\uff0c\u529f\u4ea6\u5206\u4eab\u3002\u201d \u3000\u3000\u6c5f\u67ab\u9759\u9759\u7684\u7ad9\u7740\uff0c\u5f88\u6f47\u6d12\uff0c\u4e5f\u5f88\u6c89\u7740\uff0c\u4f3c\u662f\u5728\u7b49\u7740\u5bf9\u65b9\u8c08\u5224\u51fa\u4e00\u4e2a\u5982\u4f55\u6740\u4ed6\u7684\u7ed3\u8bba\u3002 \u3000\u3000\u84dd\u51e4\u5fc3\u4e2d\u7740\u6025\uff0c\u6697\u6697\u7684\u9a82\u9053\uff1a\u201c\u8822\u554a\uff01\u4e3a\u4ec0\u4e48\u4e0d\u8d81\u673a\u6e9c\u4e86\u5462\uff01 \u3000\u3000\u4ed6\u4eec\u51fa\u624b\u8feb\u88ad\uff0c\u6211\u53ef\u4ee5\u63a9\u62a4\u4f60\u5440\uff01\u201d \u3000\u3000\u5947\u602a\u7684\u662f\uff01\u90ce\u4f2b\u7acb\u4e0d\u53bb\uff0c\u5374\u53c8\u5f15\u8d77\u5979\u65e0\u9650\u7684\u7eee\u5ff5\uff0c\u90a3\u79cd\u9526\u5e10\u6625\u6696\uff0c\u76f8\u62e5\u76f8\u4f9d\uff0c\u7f20\u7ef5\u96be\u820d\u60c5\u7231\uff0c\u4e00\u9635\u9635\u6cdb\u4e0a\u5fc3\u5934\u3002 \u3000\u3000\u770b\u5230\u4e86\u7bee\u98ce\u7709\u5b87\u95f4\u6625\u60c5\u8361\u6f3e\uff0c\u7533\u96ea\u541b\u7acb\u523b\u8b66\u89c9\uff0c\u9053\uff1a\u201c\u53f8\u5f92\u5144\uff0c\u8fd9\u91cc\u662f\u5929\u738b\u95e8\u7684\u8179\u5730\uff0c\u84dd\u51e4\u59d1\u5a18\u65e2\u7136\u6765\u4e86\uff0c\u7528\u4e0d\u7740\u54b1\u4eec\u518d\u63d2\u624b\u4e86\uff0c\u6211\u770b\u5c31\u4ea4\u7ed9\u84dd\u51e4\u59d1\u5a18\u5904\u7406\u5427\uff1f\u201d \u3000\u3000\u201c\u6b63\u5f53\u5982\u6b64\uff0c\u2026\u2026\u201d\u767d\u5929\u5316\u9053\uff1a\u201c\u55a7\u5bbe\u5c82\u53ef\u593a\u4e3b\uff0c\u6211\u4eec\u8d70\u5427\uff01\u201d \u3000\u3000\u84dd\u51e4\u5df1\u751f\u8b66\u89c9\uff0c\u5239\u90a3\u95f4\u7751\u8272\u4e00\u53d8\uff0c\u5982\u7f69\u5bd2\u971c\uff0c\u9053\uff1a\u201c\u4e0d\u7528\u5ba2\u6c14\uff0c\u4e09\u4f4d\u8981\u51fa\u624b\uff0c\u5c3d\u7ba1\u8bf7\u554a\uff01\u201d \u3000\u3000\u4e00\u6325\u624b\uff0c\u5e26\u7740\u4e09\u9b54\u3001\u4e94\u602a\uff0c\u8f6c\u8eab\u8d70\u4e86\u3002 \u3000\u3000\u5999\uff01 \u3000\u3000\u7533\u96ea\u541b\u3001\u767d\u5929\u5316\uff0c\u7adf\u4e5f\u7f13\u7f13\u9000\u8d70\u3002 \u3000\u3000\u6c5f\u67ab\u4e00\u4e2a\u8f6c\u8eab\uff0c\u98de\u5c04\u800c\u901d\u3002 \u3000\u3000\u53f8\u5f92\u827a\u56db\u987e\u4e00\u773c\uff0c\u62b1\u8d77\u6881\u9b41\u5c38\u4f53\uff0c\u5feb\u6b65\u6d88\u5931\u4e8e\u591c\u6697\u4e2d\u3002 \u3000\u3000\u6c5f\u673a\u5954\u884c\u767e\u4e08\uff0c\u505c\u4e0b\u4e86\u8eab\u5b50\u3002 \u3000\u3000\u9752\u51e4\u7531\u8349\u4e1b\u4e2d\u98de\u8eab\u800c\u51fa\uff0c\u6293\u7740\u6c5f\u67ab\u4e00\u53cc\u624b\uff0c\u9053\uff1a\u201c\u8d70\u5566\uff01\u4f60\u5bb3\u5f97\u5979\u624b\u8db3\u65e0\u63aa\uff0c\u5dee\u4e00\u70b9\u9732\u51fa\u9a6c\u811a\u3002\u201d \u3000\u3000\u7275\u7740\u6c5f\u67ab\u5411\u524d\u8dd1\u53bb\u3002 \u3000\u3000\u6c5f\u67ab\u5374\u7b54\u975e\u6240\u95ee\u7684\u9053\uff1a\u201c\u5927\u59d0\u7684\u501f\u7269\u9690\u5f62\u672f\uff0c\u5b9e\u5728\u9ad8\u660e\uff0c\u8fde\u6211\u4e5f\u77a7\u4e0d\u51fa\u4e00\u70b9\u7834\u7efd\uff0c\u6211\u4e5f\u8981\u5b66\u3002\u201d \u3000\u3000\u201c\u5bb9\u6613\u5440\uff01\u56db\u4e2a\u8001\u5a46\u90fd\u53ef\u4ee5\u6559\u4f60\u2026\u2026\u201d\u9752\u51e4\u9053\uff1a\u201c\u6700\u91cd\u8981\u7684\u662f\u7ec3\u51fa\u4e00\u53e3\u957f\u6c14\uff0c\u5305\u4f60\u4e09\u4e94\u5929\u5c31\u80fd\u5b66\u4f1a\uff0c\u95ee\u9898\u662f\u5f20\u59e8\u4e0d\u51c6\u4f60\u5b66\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u4f46\u6211\u77e5\u9053\u8fd9\u4e0d\u662f\u8c0e\u8bdd\uff0c\u5982\u8981\u6211\u5b66\uff0c\u5927\u59d0\u65e9\u5c31\u8be5\u4f20\u6388\u6211\u4e86\u3002\u201d \u3000\u3000\u201c\u56de\u53bb\u95ee\u5f20\u59e8\u5427\uff01\u201d\u9752\u51e4\u9053\uff1a\u201c\u6211\u771f\u7684\u4e0d\u77e5\u9053\uff0c\u6240\u4ee5\uff0c\u6211\u4e0d\u6562\u4f20\u6388\u4f60\uff0c\u4ee5\u4f60\u6210\u5c31\uff0c\u5b66\u8d77\u6765\uff0c\u81ea\u7136\u5f88\u5feb\uff0c\u4f46\u5982\u4e0d\u544a\u8bc9\u4f60\u90a3\u4e9b\u79d8\u8bc0\uff0c\u4f60\u5c31\u5f88\u96be\u8fd0\u7528\u7684\u5f97\u5fc3\u5e94\u624b\u3002\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\uff0c\u8fd9\u662f\u4e00\u79cd\u9ad8\u96be\u5ea6\u7684\u6280\u827a\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u6ca1\u6709\u4eba\u6307\u70b9\uff0c\u6478\u7d22\u4e00\u5e74\u534a\u8f7d\uff0c\u4e5f\u672a\u5fc5\u4f1a\u6709\u6210\u5c31\u3002\u201d \u3000\u3000\u9752\u51e4\u653e\u5f00\u4e86\u6c5f\u67ab\u7684\u5de6\u624b\uff0c\u4e5f\u653e\u7f13\u4e86\u811a\u6b65\uff0c\u957f\u957f\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u662f\u6076\u4f5c\u5267\u5440\uff1f\u8fd8\u662f\u60f3\u8bc1\u660e\u4e00\u4e0b\u81ea\u5df1\u7684\u9b45\u529b\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u8038\u8038\u80a9\uff0c\u7b11\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\u554a\uff1f\u201d \u3000\u3000\u201c\u522b\u88c5\u7cca\u6d82\uff0c\u84dd\u51e4\u88ab\u4f60\u6574\u60e8\u4e86\uff0c\u7533\u96ea\u541b\u3001\u767d\u5929\u5316\u90fd\u770b\u51fa\u4e86\u5f62\u8272\u4e0d\u5bf9\u2026\u2026\u201d\u9752\u51e4\u9053\uff1a\u201c\u4ed6\u4eec\u624d\u7f62\u624b\u800c\u9000\uff0c\u4ed6\u4eec\u8981\u662f\u628a\u8fd9\u4ef6\u4e8b\u544a\u8bc9\u5929\u738b\uff0c\u600e\u4e48\u5f97\u4e86\u554a\uff01\u201d \u3000\u3000\u6c5f\u67ab\u65e0\u6cd5\u88c5\u4e0b\u53bb\u4e86\uff0c\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u786e\u5b9e\u6709\u610f\u6c42\u8bc1\u4e00\u4e0b\uff0c\u84dd\u51e4\u662f\u5426\u771f\u80af\u4e3a\u6211\u4eec\u6548\u529b\uff0c\u5982\u56e0\u6b64\u5bb3\u4e86\u5979\uff0c\u5c31\u975e\u6211\u6240\u613f\u4e86\uff0c\u65e9\u77e5\u5982\u6b64\uff0c\u5c31\u8be5\u51fa\u624b\uff0c\u6740\u4e86\u767d\u5929\u5316\u3001\u7533\u96ea\u541b\u548c\u53f8\u5f92\u827a\uff0c\u8fd9\u6837\u5427\uff01\u4f60\u5148\u53bb\u5c11\u6797\uff0c\u4f1a\u5408\u56db\u59d1\uff0c\u6211\u518d\u6f5c\u56de\u53bb\uff0c\u4e8b\u60c5\u6709\u53d8\uff0c\u6211\u5c31\u6551\u84dd\u51e4\u51fa\u6765\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u884c\u2026\u201d\u9752\u51e4\u9053\uff1a\u201c\u90a3\u4f1a\u5750\u5b9e\u84dd\u51e4\u7684\u53db\u9006\u5927\u7f6a\uff0c\u4e5f\u548c\u5f20\u59e8\u7684\u8c0b\u7565\u4e0d\u5408\uff0c\u5f20\u59e8\u8981\u7684\u662f\u5929\u738b\u95e8\u4e2d\u7684\u529b\u91cf\uff0c\u4e0d\u662f\u53ea\u4e3a\u4f60\u518d\u5a36\u4e00\u4e2a\u8001\u5a46\u3002 \u3000\u3000\u4e8b\u60c5\u8fd8\u6ca1\u6709\u574f\u5230\u767e\u53e3\u83ab\u8fa9\u7684\u5883\u5730\uff0c\u4ee5\u84dd\u51e4\u4e4b\u80fd\uff0c\u5e94\u8be5\u53ef\u4ee5\u5e94\u4ed8\uff0c\u5979\u62a2\u5148\u9000\u8d70\uff0c\u5c31\u662f\u7559\u4e0b\u4e86\u8fa9\u89e3\u7684\u4f59\u5730\u3002 \u3000\u3000\u4e00\u9519\u4e0d\u80fd\u518d\u9519\uff0c\u54b1\u4eec\u8d76\u5f80\u5c11\u6797\u5bfa\u5427\uff01\u6211\u76f8\u4fe1\u96ea\u3001\u971c\u4e24\u4f4d\u59b9\u5b50\u7684\u80fd\u529b\uff0c\u4f46\u654c\u4eba\u4f17\u591a\uff0c\u9ad8\u624b\u5982\u4e91\uff0c\u5077\u88ad\u5c11\u6797\u4e4b\u6218\uff0c\u5982\u6709\u95ea\u5931\uff0c\u90a3\u5c31\u975e\u5e38\u4e25\u91cd\u4e86\u3002\u201d \u3000\u3000\u4e0d\u77e5\u4f55\u65f6\uff0c\u9752\u51e4\u5df2\u53d8\u5f97\u5229\u53e3\u5982\u5200\uff0c\u800c\u4e14\u8bba\u4e8b\u7406\u667a\uff0c\u5df2\u4e0d\u662f\u90a3\u79cd\u53ea\u6c42\u4e2a\u90ce\u591a\u60c5\uff0c\u6ee1\u8db3\u4e00\u5df1\u5229\u6b32\u7684\u5c0f\u5973\u4eba\u4e86\u3002 \u3000\u3000\u6613\u82b1\u624b\u5f20\u56db\u59d1\u679c\u7136\u6709\u975e\u5e38\u4e4b\u80fd\uff0c\u6f5c\u79fb\u9ed8\u5316\u4e2d\uff0c\u79fb\u8f6c\u4e86\u4eba\u7684\u6c14\u8d28\u3002 \u3000\u3000\u201c\u9752\u51e4\uff0c\u6bd4\u8d77\u4f60\uff0c\u6211\u5f88\u60ed\u6127\u3002\u201d\u6c5f\u67ab\u6709\u70b9\u9eef\u7136\u5730\u8bf4\uff0c\u201c\u6538\u5173\u5174\u4ea1\u7684\u5927\u4e8b\uff0c\u6211\u5374\u5904\u7406\u7684\u5982\u6b64\u4e4b\u7cdf\uff01\u201d \u3000\u3000\u795e\u60c5\u51c4\u7136\uff0c\u60f6\u6050\u4e0d\u5df2\u3002 \u3000\u3000\u201c\u4e0d\u8981\u8fd9\u6837\uff0c\u6c5f\u90ce\uff0c\u4e5f\u8bb8\u662f\u6211\u5728\u5992\u5fcc\uff0c\u624d\u51fa\u8a00\u65e0\u72b6\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\u4f60\u4e0d\u662f\uff0c\u9752\u51e4\uff0c\u8fd9\u4e00\u573a\u6c5f\u6e56\u91cd\u5149\u5927\u6218\uff0c\u5927\u59d0\u9886\u5bfc\u7684\u5dfe\u5e3c\u5973\u5c06\uff0c\u624d\u662f\u81f4\u80dc\u7684\u5173\u952e\uff0c\u6211\u6c5f\u67ab\u5145\u5176\u91cf\u4e5f\u53ea\u662f\u4e00\u4e2a\u8fc7\u5173\u65a9\u5c06\u7684\u52c7\u592b\u7f62\u4e86\u3002\u201d \u3000\u3000\u201c\u4f60\u662f\u771f\u6b63\u4e3b\u5bb0\u5927\u5c40\u7684\u7edf\u5e05\uff0c\u2026\u2026\u201d\u9752\u51e4\u9053\uff1a\u201c\u6c5f\u90ce\uff0c\u4e0d\u662f\u4f60\uff0c\u6211\u4eec\u90fd\u5fc3\u65e0\u6240\u4f9d\uff0c\u5305\u62ec\u5f20\u59e8\u5728\u5185\uff0c\u96ea\u3001\u971c\u3001\u6885\u82b1\uff0c\u90fd\u4e00\u5fc3\u4e00\u610f\u7684\u4e3a\u4f60\u6548\u547d\uff0c\u7ea2\u7c89\u5c0f\u4f73\u4eba\uff0c\u90a3\u91cc\u6709\u9010\u9e7f\u4e2d\u539f\u3001\u4e89\u9738\u6c5f\u6e56\u7684\u96c4\u5fc3\u5927\u5fd7\uff0c\u60c5\u7518\u4ed8\u51fa\uff0c\u624d\u884d\u751f\u4e86\u6211\u4eec\u89c6\u6b7b\u5982\u5f52\u7684\u52c7\u6c14\uff0c\u4f60\u53ef\u4e0d\u80fd\u6cc4\u6c14\uff01\u6211\u4eec\u51e0\u6735\u5c0f\u82b1\u513f\uff0c\u8fd8\u8981\u4f9d\u9644\u5728\u4f60\u8fd9\u68f5\u5927\u6811\u4e0a\uff0c\u6321\u98ce\u906e\u96e8\u554a\uff01\u2026\u2026\u201d \u3000\u3000\u201c\u8fd9\u4e5f\u662f\u5f20\u5927\u59d0\u6559\u7684\u5417\uff1f\u2026\u2026\u201d\u6c5f\u67ab\u8c6a\u6c14\u4e00\u632f\u3002 \u3000\u3000\u201c\u4e0d\u662f\uff0c\u201d\u9752\u51e4\u7b11\u9053\uff1a\u201c\u8fd9\u662f\u6211\u7531\u5fc3\u5e95\u53d1\u51fa\u6765\u58f0\u97f3\uff0c\u5c0f\u5973\u4eba\u4e3a\u60c5\u8ff7\u9189\uff0c\u4e3a\u90ce\u6194\u60b4\uff0c\u53ef\u4e5f\u80fd\u4e3a\u60c5\u7231\u6fc0\u8d77\u4e86\u65e0\u6bd4\u7684\u52c7\u6bc5\uff0c\u751f\u513f\u80b2\u5973\uff0c\u662f\u4f55\u7b49\u75db\u82e6\u7684\u4e8b\uff0c\u4f46\u5979\u4eec\u524d\u4ec6\u540e\u7ee7\uff0c\u7edd\u4e0d\u754f\u60e7\u3002\u201d \u3000\u3000\u90a3\u4e2a\u65f6\u4ee3\uff0c\u53ef\u6ca1\u6709\u73b0\u4ee3\u7684\u533b\u672f\uff0c\u9ebb\u9189\u5f00\u5200\uff0c\u65e0\u75bc\u5206\u5a29\uff0c\u751f\u513f\u5973\u7684\u75db\u82e6\uff0c\u53ef\u771f\u662f\u60ca\u5929\u52a8\u5730\u3002 \u3000\u3000\u6c5f\u67ab\u542c\u5f97\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u8bf4\u7684\u662f\u5440\uff01\u6211\u542c\u5230\u8fc7\u4e00\u6b21\u5b55\u5987\u751f\u4ea7\uff0c\u6574\u6574\u628a\u4e08\u592b\u9a82\u4e86\u4e00\u591c\uff0c\u9a82\u7684\u6dcb\u6f13\u5c3d\u81f4\uff0c\u5165\u8033\u60ca\u5fc3\u3002\u201d \u3000\u3000\u9752\u51e4\u5ae3\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6c5f\u90ce\uff0c\u4f60\u6562\u5417\uff1f\u201d \u3000\u3000\u201c\u5927\u6982\u4e0d\u6562\uff01\u201d\u9752\u51e4\u9053\uff1a\u201c\u6c5f\u6e56\u91cd\u5149\u4e86\uff0c\u6211\u8fd8\u6d3b\u7740\uff0c\u6211\u5c31\u751f\u4e00\u4e2a\u7ed9\u4f60\u77a7\u77a7\uff0c\u6700\u591a\u9a82\u4f60\u4e09\u4e94\u53e5\uff0c\u7edd\u4e0d\u4f1a\u9a82\u4f60\u4e00\u591c\uff01\u201d \u3000\u3000\u201c\u5509\uff01\u60f3\u4e00\u60f3\uff0c\u6709\u4e9b\u5730\u65b9\uff0c\u5973\u4eba\u5b9e\u5728\u6bd4\u7537\u4eba\u52c7\u6562\uff01\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u770b\u4e0a\u53bb\u5a07\u67d4\u65e0\u9650\uff0c\u72e0\u8d77\u6765\uff0c\u5929\u5730\u4e0d\u6015\uff0c\u4e2a\u6027\u4e4b\u5f3a\uff0c\u7537\u4eba\u662f\u671b\u5c18\u83ab\u53ca\u4e86\u3002\u201d \u3000\u3000\u201c\u8fd9\u5c31\u662f\u5973\u4eba\u554a\uff01\u94dc\u5fc3\u94c1\u80c6\uff0c\u4e0d\u6562\uff0c\u8fd8\u6562\uff01\u201d\u9752\u51e4\u9053\uff1a\u201c\u60f3\u60f3\u6211\u4eec\u8981\u5fcd\u53d7\u7684\u75db\u82e6\uff0c\u4f60\u5c31\u8be5\u591a\u601c\u60dc\u6211\u4eec\u4e00\u4e9b\uff01\u201d \u3000\u3000\u7709\u76ee\u4f20\u60c5\uff0c\u53c8\u6062\u590d\u65e0\u9650\u6e29\u67d4\u3002 \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u6211\u4f1a\u7684\uff0c\u6211\u4ee5\u672f\u63b3\u83b7\u4e86\u4f60\u4eec\uff0c\u4f46\u5c06\u4ee5\u5168\u90e8\u7684\u60c5\u7231\uff0c\u4f5c\u4e3a\u56de\u62a5\uff0c\u9752\u51e4\uff0c\u6211\u4e0d\u77e5\u9053\u65e5\u540e\u8fd8\u4f1a\u6709\u4ec0\u4e48\u53d8\u5316\uff0c\u4f46\u6211\u7edd\u4e0d\u518d\u7528\u2018\u60d1\u5fc3\u672f\u2019\u4e86\uff0c\u5c31\u7b97\u5927\u59d0\u903c\u6211\uff0c\u6211\u4e5f\u4e0d\u5e72\u4e86\u3002\u201d \u3000\u3000\u770b\u4e2a\u90ce\u9634\u973e\u5c3d\u9664\uff0c\u9752\u51e4\u6b22\u989c\u4ea6\u5c55\uff0c\u7275\u7740\u6c5f\u67ab\u4e00\u53cc\u624b\uff0c\u9053\uff1a\u201c\u4ee5\u540e\u7684\u4e8b\uff0c\u4ee5\u540e\u518d\u8bf4\u5427\uff01\u5feb\u4e9b\u8d70\u4e86\uff0c\u60f3\u60f3\u96ea\u3001\u971c\u4e24\u4f4d\u59b9\u5b50\u671b\u7a7f\u79cb\u6c34\u7684\u671f\u76fc\uff0c\u4f60\u5fcd\u5fc3\u8ba9\u5979\u4eec\u9762\u5bf9\u5f3a\u654c\u65f6\uff0c\u8fd8\u6709\u7740\u5fc3\u6000\u60c5\u90ce\u7684\u714e\u71ac\u5417\uff1f\u201d \u3000\u3000\u5c11\u6797\u5bfa\u666f\u7269\u4f9d\u65e7\uff0c\u4f46\u7ad9\u5728\u5bfa\u95e8\u53e3\u7684\u9664\u4e86\u4e24\u4e2a\u7070\u8863\u50e7\u4eba\u4e4b\u5916\uff0c\u8fd8\u6709\u4e09\u4e2a\u5982\u82b1\u4f3c\u7389\u7684\u7f8e\u4e3d\u5c11\u5973\u3002 \u3000\u3000\u662f\u96ea\u3001\u971c\u4e8c\u5973\u548c\u6885\u82b1\u59d1\u5a18\u3002 \u3000\u3000\u65e5\u6b63\u5f53\u4e2d\uff0c\u521d\u590f\u7684\u9633\u5149\u6709\u70b9\u70ed\uff0c\u4e24\u4eba\u517c\u7a0b\u98de\u5954\uff0c\u6c5f\u67ab\u8fd8\u77a7\u4e0d\u51fa\u4ec0\u4e48\uff0c\u9752\u51e4\u53ef\u662f\u7d2f\u5f97\u9999\u6c41\u6dcb\u6f13\u3002 \u3000\u3000\u4f46\u5979\u5fc3\u4e2d\u5145\u6ee1\u7740\u6b22\u6109\uff0c\u62ed\u53bb\u8138\u4e0a\u7684\u6c57\u73e0\u513f\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u7b49\u4e86\u591a\u4e45\u4e86\uff0c\u770b\u6837\u5b50\uff0c\u5c11\u6797\u5bfa\u7684\u4e8b\uff0c\u5df2\u7ecf\u89e3\u51b3\uff1f\u201d \u3000\u3000\u201c\u539f\u6765\u60f3\u514d\u4e0d\u4e86\u4e00\u573a\u6d74\u8840\u82e6\u6218\uff0c\u5374\u4e0d\u6599\u541f\u96ea\u3001\u541f\u971c\u53d1\u4e86\u72e0\uff0c\u2026\u2026\u201d\u6885\u82b1\u9053\uff1a\u201c\u4e94\u884c\u9041\u672f\u914d\u5408\u4e86\u795e\u51fa\u9b3c\u6ca1\u7684\u6b66\u529f\uff0c\u518d\u52a0\u4e0a\u6536\u53d1\u968f\u5fc3\u7684\u5bd2\u94c1\u593a\u9b42\u9488\uff0c\u4e00\u53e3\u6c14\u6740\u6b7b\u4e8c\u5341\u51e0\u4e2a\u5f3a\u654c\uff0c\u771f\u662f\u5a01\u98ce\u51db\u51db\uff0c\u8fde\u5f20\u59e8\u548c\u5e72\u7239\uff0c\u5e72\u5a18\u90fd\u7740\u5446\u4e86\uff0c\u5c31\u522b\u8bf4\u8001\u53eb\u5316\u548c\u9093\u98de\u4e00\u6279\u4eba\u4e86\uff0c\u8138\u4e0a\u90a3\u80a1\u5403\u60ca\u7684\u795e\u8272\uff0c\u5dee\u70b9\u5c31\u6d41\u51fa\u4e86\u773c\u6cea\u3002 \u3000\u3000\u6211\u4eec\u4eae\u51fa\u5175\u5203\uff0c\u5c31\u5df2\u7ecf\u5927\u83b7\u5168\u80dc\uff0c\u6b7b\u7684\u6b7b\uff0c\u8dd1\u7684\u8dd1\uff0c\u4e00\u4e0b\u5b50\u5c31\u89e3\u53bb\u4e86\u5c11\u6797\u4e4b\u5371\uff0c\u5c11\u6797\u5bfa\u7684\u5927\u5e08\u4eec\uff0c\u5df2\u628a\u96ea\u971c\u4e24\u4f4d\u59b9\u5b50\uff0c\u770b\u6210\u4e86\u6551\u4e16\u89e3\u96be\u7684\u89c2\u97f3\u83e9\u8428\u3002\u201d \u3000\u3000\u201c\u770b\u4e0a\u53bb\u5a07\u67d4\u65e0\u9650\uff0c\u72e0\u8d77\u6765\u5929\u5730\u4e0d\u6015\uff0c\u771f\u662f\u94dc\u5fc3\u94c1\u80c6\uff0c\u4e0d\u6562\u3001\u8fd8\u6562\u5440\uff01\u201d\u9752\u51e4\u662f\u4e00\u9762\u8bf4\u4e00\u9762\u7b11\u7684\u5f2f\u4e0b\u4e86\u8170\u3002 \u3000\u3000\u6c5f\u67ab\u4e5f\u7b11\u7684\u4e00\u8138\u8be1\u79d8\u3002 \u3000\u3000\u53f6\u96ea\u3001\u541f\u971c\u542c\u4e0d\u61c2\uff0c\u60f3\u4e0d\u51fa\u8fd9\u51e0\u53e5\u8bdd\u600e\u4e48\u4f1a\u90a3\u6837\u597d\u7b11\u3002 \u3000\u3000\u201c\u6885\u82b1\u59d0\uff01\u201d\u541f\u971c\u9053\uff1a\u201c\u9752\u51e4\u8bf4\u7684\u4ec0\u4e48\u5440\uff1f\u770b\u5979\u7b11\u7684\u82b1\u679d\u4e71\u6296\uff0c\u6211\u600e\u6837\u4e00\u70b9\u4e5f\u60f3\u4e0d\u660e\u767d\uff1f\u201d \u3000\u3000\u6885\u82b1\u4e5f\u662f\u4e00\u8138\u832b\u7136\uff0c\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u61c2\uff0c\u4f46\u6c5f\u5927\u54e5\u7b11\u7684\u4e00\u7751\u8be1\u79d8\uff0c\u4e00\u5b9a\u4e0d\u662f\u597d\u8bdd\u3002\u201d \u3000\u3000\u541f\u971c\u6709\u7edd\u6d3b\uff0c\u62c9\u8d77\u9752\u51e4\u7684\u8863\u8896\uff0c\u6492\u8d77\u5a07\u6765\uff0c\u9053\uff1a\u201c\u9752\u51e4\u59d0\u59d0\uff0c\u544a\u8bc9\u6211\u561b\uff01\u4e3a\u4ec0\u4e48\u8fd9\u6837\u597d\u7b11\uff0c\u8bf4\u51fa\u6765\uff0c\u8ba9\u6211\u4eec\u4e5f\u5f00\u5fc3\u4e00\u4e0b\u3002\u201d \u3000\u3000\u201c\u75bc\u82e6\u5f97\u8981\u547d\uff0c\u8fd8\u80fd\u5f00\u5fc3\u54ea\uff01\u2026\u2026\u201d\u9752\u51e4\u9053\uff1a\u201c\u53bb\u5411\u6c5f\u5927\u54e5\u5427\uff01 \u3000\u3000\u8fd9\u662f\u4ed6\u8bf4\u7684\uff0c\u4e5f\u662f\u4ed6\u7684\u9519\u3002\u201d \u3000\u3000\u201c\u6c5f\u5927\u54e5\u4f1a\u6709\u4ec0\u4e48\u9519\u5462\uff1f\u2026\u2026\u201d\u541f\u971c\u76ef\u5728\u6c5f\u67ab\u8138\u4e0a\u770b\u4e86\u826f\u4e45\uff0c\u8fd8\u662f\u770b\u4e0d\u51fa\u4e00\u70b9\u5934\u7eea\uff0c\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u8fd8\u662f\u4e00\u6837\u554a\uff01\u6c5f\u5927\u54e5\u4e00\u70b9\u6ca1\u53d8\u3002\u201d \u3000\u3000\u201c\u59b9\uff0c\u4e0d\u8981\u95ee\u4e86\u2026\u2026\u201d\u541f\u96ea\u82e5\u6709\u6240\u609f\u5730\u9053\uff1a\u201c\u7b49\u4e00\u4f1a\uff0c\u6211\u544a\u8bc9\u4f60\u3002\u201d \u3000\u3000\u6c5f\u67ab\u6025\u6025\u8bf4\u9053\uff1a\u201c\u4e0d\u5173\u6211\u4e8b\u554a\uff01\u90fd\u662f\u9752\u51e4\u8bf4\u7684\u3002\u201d \u3000\u3000\u201c\u5982\u662f\u9752\u51e4\u59d0\u59d0\u8bf4\u7684\uff0c\u5c31\u4e00\u5b9a\u4e0d\u4f1a\u6709\u9519\u4e86\uff01\u201d\u541f\u96ea\u9053\uff1a\u201c\u4f46\u6bdb\u75c5\u662f\u51fa\u5728\u6c5f\u5927\u54e5\u8eab\u4e0a\u3002\u201d \u3000\u3000\u6c5f\u67ab\u6014\u4f4f\u4e86\uff0c\u8fd9\u79cd\u4e8b\u53eb\u4ed6\u5982\u4f55\u56de\u7b54\u3002 \u3000\u3000\u541f\u96ea\u867d\u806a\u6167\u7edd\u4f26\uff0c\u4f46\u5979\u4ecd\u662f\u5904\u5b50\u4e4b\u8eab\uff0c\u82e5\u6709\u6240\u609f\uff0c\u4e5f\u4e0d\u662f\u5b8c\u5168\u660e\u767d\u3002 \u3000\u3000\u5012\u662f\u6885\u82b1\u60f3\u901a\u4e86\uff0c\u4e5f\u4e0d\u7981\u5fae\u5fae\u4e00\u7b11\u3002 \u3000\u3000\u201c\u6211\u61c2\u4e86\uff0c\u4e00\u5b9a\u548c\u84dd\u51e4\u6709\u5173\u2026\u2026\u201d\u541f\u971c\u81ea\u4f5c\u806a\u660e\u5730\u8bf4\uff0c\u201c\u6c5f\u5927\u54e5\uff0c\u6211\u731c\u7684\u5bf9\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u8fd9\u4e2a\u2026\u2026\u201d \u3000\u3000\u5f53\u7740\u56db\u4e2a\u5973\u4eba\u4e4b\u9762\uff0c\u6c5f\u67ab\u5b9e\u5728\u65e0\u6cd5\u51fa\u53e3\u3002 \u3000\u3000\u201c\u6709\u4e00\u70b9\u5173\u7cfb\u4e86\u2026\u2026\u201d\u9752\u51e4\u9053\uff1a\u201c\u53ea\u4e0d\u8fc7\uff0c\u4e0d\u662f\u5979\u4e00\u4e2a\u4eba\u7684\u4e8b\u3002\u201d \u3000\u3000\u201c\u600e\u4e48\uff1f\u6211\u4eec\u90fd\u6709\u4efd\u54ea\uff01\u54ea\u4f1a\u662f\u4ec0\u4e48\u4e8b\u5462\uff1f\u201d\u541f\u971c\u771f\u7684\u662f\u60f3\u4e0d\u901a\u4e86\uff0c\u7f20\u7740\u9752\u51e4\uff0c\u9053\uff1a \u3000\u3000\u201c\u544a\u8bc9\u6211\u561b\uff01\u7a76\u7adf\u662f\u4ec0\u4e48\u4e8b\u60c5\uff1f\u201d \u3000\u3000\u4e24\u4e2a\u7070\u8863\u50e7\u4eba\uff0c\u770b\u51e0\u4e2a\u5c0f\u7f8e\u4eba\uff0c\u5431\u5431\u55b3\u55b3\uff0c\u6709\u8bf4\u6709\u7b11\uff0c\u58f0\u5982\u9ec4\u83ba\uff0c\u5a07\u5a9a\u6a2a\u6ea2\uff0c\u4e0d\u6562\u518d\u770b\u4e86\uff0c\u8fdc\u8fdc\u907f\u5f00\uff0c\u770b\u591a\u4e86\u4f1a\u52a8\u51e1\u5fc3\u554a\uff01 \u3000\u3000\u9752\u51e4\u88ab\u7f20\u5f97\u6ca1\u6cd5\u5b50\u4e86\uff0c\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u771f\u60f3\u77e5\u9053\u5c31\u9644\u8033\u8fc7\u6765\u5427\uff01\u201d \u3000\u3000\u541f\u971c\u771f\u7684\u9644\u8033\u8fc7\u53bb\uff0c\u5374\u542c\u5f97\u8138\u8272\u82cd\u767d\uff0c\u9053\uff1a\u201c\u771f\u7684\u4f1a\u90a3\u4e48\u75bc\u82e6\u554a\uff01\u6709\u6b66\u529f\uff0c\u96be\u9053\u4e5f\u4e0d\u80fd\u6297\u62d2\uff1f\u201d \u3000\u3000\u201c\u6211\u4e0d\u77e5\u9053\u4f60\u548c\u541f\u96ea\u7ec3\u7684\u5185\u529f\uff0c\u80fd\u4e0d\u80fd\u6297\u62d2\uff1f\u201d\u9752\u51e4\u9053\uff1a\u201c\u4f46\u6211\u548c\u6885\u82b1\u662f\u80af\u5b9a\u4e0d\u884c\uff0c\u6709\u6b66\u529f\u4e5f\u4e0d\u6562\u7528\u554a\uff01\u201d \u3000\u3000\u201c\u8fd9\u4e48\u8bf4\uff0c\u6211\u548c\u59d0\u4e5f\u662f\u4e0d\u80fd\u7528\u4e86\uff01\u201d \u3000\u3000\u541f\u971c\u771f\u7684\u88ab\u5413\u574f\u4e86\uff0c\u7c89\u988a\u4e0a\u89c1\u4e86\u51b7\u6c57\u3002 \u3000\u3000\u201c\u4ec0\u4e48\u4e8b\u554a\uff1f\u2026\u2026\u201d\u5f20\u56db\u59d1\uff0c\u7f13\u6b65\u884c\u6765\uff0c\u9053\uff1a\u201c\u8fdb\u5165\u5bfa\u4e2d\u8bf4\u5427\uff01\u201d \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u3001\u8001\u53eb\u5316\u7d27\u968f\u5728\u5f20\u56db\u59d1\u7684\u8eab\u540e\u3002 \u3000\u3000\u541f\u971c\u7528\u8863\u8896\u62ed\u4e00\u4e0b\u7c89\u988a\u6c57\u6c34\uff0c\u9053\uff1a\u201c\u6ca1\u6709\u4e8b\uff0c\u9752\u51e4\u59d0\u5728\u8ff0\u8bf4\u5979\u548c\u6c5f\u5927\u54e5\u7684\u5c65\u9669\u7ecf\u8fc7\u2026\u2026\u201d \u3000\u3000\u201c\u6df1\u5165\u654c\u8179\uff0c\u51f6\u9669\u53ef\u671f\uff0c\u6c5f\u67ab\uff0c\u8f9b\u82e6\u4f60\u4e86\u3002\u201d \u3000\u3000\u201c\u591a\u4e8f\u9752\u51e4\u534f\u52a9\uff0c\u5c0f\u5f1f\u5e78\u672a\u8fb1\u547d\uff0c\u53ea\u662f\u672a\u80fd\u53ca\u65f6\u8d76\u56de\u5c11\u6797\uff0c\u52a9\u5927\u59d0\u4e00\u81c2\u4e4b\u529b\u3002\u201d \u3000\u3000\u201c\u541f\u96ea\u3001\u541f\u971c\uff0c\u5a01\u98ce\u516b\u9762\uff0c\u672f\u6cd5\u767e\u53d8\uff0c\u6b7c\u5f3a\u654c\u5982\u6467\u67af\u62c9\u673d\u2026\u2026\u201d \u3000\u3000\u5f20\u56db\u59d1\u7b11\u9053\uff1a\u201c\u6b27\u9633\u5144\u592b\u5987\u90fd\u770b\u50bb\u4e86\uff0c\u9752\u51fa\u4e8e\u84dd\uff0c\u5f69\u4e3d\u8000\u76ee\uff0c\u4ed6\u4eec\u51e0\u4e4e\u4e0d\u76f8\u4fe1\u4e24\u4e2a\u4e2b\u5934\u4f1a\u662f\u4ed6\u4eec\u7684\u5973\u513f\uff1f\u73b0\u5728\u68d8\u624b\u7684\u662f\uff0c\u5c11\u6797\u5bfa\u8001\u65b9\u4e08\u88ab\u5bf9\u65b9\u4e0b\u4e86\u590d\u6bd2\uff0c\u89e3\u53bb\u4e00\u6bd2\uff0c\u590d\u4e3a\u4e00\u6bd2\uff0c\u6211\u7528\u5c3d\u5168\u8eab\u672c\u9886\uff0c\u4ecd\u662f\u65e0\u6cd5\u6cbb\u6108\u4ed6\u2026\u2026\u201d \u3000\u3000\u4e00\u628a\u62c9\u8fc7\u9752\u51e4\uff0c\u63a5\u9053\uff1a\u201c\u4f60\u7d2f\u4e86\u4e00\u8eab\u6c57\uff0c\u8d70\uff01\u8ddf\u5f20\u59e8\u8fdb\u5bfa\u53bb\u6362\u4ef6\u8863\u670d\u3002\u201d \u3000\u3000\u201c\u5c0f\u5f1f\u53bb\u770b\u770b\uff0c\u80fd\u4e0d\u80fd\u4e3a\u767e\u660e\u8001\u65b9\u4e08\u7565\u5c3d\u7ef5\u529b\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u6211\u968f\u540e\u5c31\u5230\uff0c\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u6ca1\u6709\u628a\u63e1\u5c31\u4e0d\u8981\u968f\u4fbf\u51fa\u624b\uff0c\u5982\u4eca\u60c5\u52bf\u8fd8\u672a\u660e\u6717\uff0c\u51fa\u4e0d\u5f97\u4e00\u70b9\u5dee\u9519\u3002\u201d \u3000\u3000\u201c\u5c0f\u5f1f\u8bb0\u4e0b\u4e86\u3002\u201d \u3000\u3000\u201c\u548c\u5c1a\u5e26\u8def\uff0c\u5148\u5230\u6392\u623f\u4e2d\u559d\u676f\u8336\uff0c\u5598\u53e3\u6c14\uff0c\u518d\u53bb\u664b\u8c12\u8001\u65b9\u4e08\u3002\u201d \u3000\u3000\u6c5f\u67ab\u4e3e\u6b65\u800c\u884c\uff0c\u56de\u987e\u7740\u8001\u53eb\u5316\uff0c\u9053\uff1a\u201c\u94b1\u5144\uff0c\u5982\u679c\u6211\u4eec\u6709\u5e78\u8d62\u4e86\u8fd9\u573a\u51b3\u6218\uff0c\u4f60\u53ef\u8981\u62c5\u8d1f\u8d77\u4e32\u8fde\u5404\u5927\u95e8\u6d3e\uff0c\u91cd\u590d\u6614\u65e5\u6c5f\u6e56\u6c14\u52bf\u7684\u5927\u4efb\u4e86\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u5c31\u4e0d\u52b3\u4f60\u6c5f\u8001\u5f1f\u518d\u8d39\u5fc3\u4e86\u2026\u2026\u201d\u8001\u53eb\u5316\u9053\uff1a\u201c\u7ecf\u6b64\u4e00\u5f79\uff0c\u5341\u4e94\u5e74\u5185\uff0c\u6c5f\u6e56\u4e0a\u4e0d\u4f1a\u6709\u95e8\u6237\u4e4b\u4e89\u7684\u5927\u98ce\u6ce2\uff0c\u5341\u4e94\u5e74\u540e\uff0c\u8001\u53eb\u5316\u5c31\u4e0d\u6562\u62c5\u4fdd\u4e86\uff0c\u5509\uff01\u65e0\u5a3c\u4e0d\u6210\u7801\u5934\uff0c\u65e0\u7eb7\u4e89\u5c31\u4e0d\u7b97\u6c5f\u6e56\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u91ce\u706b\u70e7\u4e0d\u5c3d\uff0c\u6625\u98ce\u5439\u53c8\u751f\uff0c\u201d\u5927\u548c\u5c1a\u82e6\u7b11\u9053\uff1a\u201c\u6ca1\u6709\u7eb7\u4e89\u7684\u6c5f\u6e56\uff0c\u4e5f\u5c31\u4e0d\u4f1a\u51fa\u73b0\u4ed7\u5251\u536b\u9053\u7684\u4fa0\u58eb\u4e86\u3002\u201d \u3000\u3000\u201c\u660e\u6708\u65e0\u9650\u597d\uff0c\u201d\u6c5f\u67ab\u53f9\u606f\u4e00\u58f0\u63a5\u9053\uff1a\u201c\u4e00\u6837\u7167\u6c9f\u6e20\u3002\u201d \u3000\u3000\u8c08\u8bdd\u4e2d\u8fdb\u5165\u4e86\u4e00\u5ea7\u53a2\u623f\uff0c\u6c5f\u67ab\u559d\u676f\u8336\u7565\u4f5c\u8c03\u6025\uff0c\u6362\u4e86\u8eab\u5e72\u51c0\u8863\u670d\uff0c\u8d76\u5f80\u65b9\u4e08\u5ba4\u4e2d\u3002 \u3000\u3000\u65b9\u4e08\u5ba4\u666f\u7269\u5df2\u590d\uff0c\u4f5b\u50cf\u3001\u5b57\u753b\uff0c\u91cd\u65b0\u6302\u8d77\uff0c\u9f0e\u7089\u4e2d\u4e5f\u5347\u8d77\u4e86\u8885\u8885\u7684\u6a80\u9999\u3002 \u3000\u3000\u8001\u65b9\u4e08\u767e\u660e\u5927\u5e08\uff0c\u6362\u7740\u4e86\u4e00\u4ef6\u65b0\u7684\u9ec4\u8272\u8888\u88df\uff0c\u53ea\u662f\u7751\u8272\u5982\u814a\uff0c\u53cc\u5507\u6de1\u9752\uff0c\u663e\u7136\u662f\u4e2d\u6bd2\u4e4b\u5f81\u3002 \u3000\u3000\u201c\u5927\u5e08\uff0c\u73b0\u5728\uff0c\u54ea\u91cc\u4e0d\u8212\u670d\uff1f\u201d \u3000\u3000\u201c\u6c5f\u5c11\u4fa0\uff0c\u4f60\u505a\u5f97\u597d\uff0c\u5c11\u6797\u5bfa\u9677\u5165\u654c\u624b\u4e94\u5e74\u4e4b\u4e45\uff0c\u4e00\u5915\u95f4\uff0c\u5371\u96be\u5168\u89e3\uff0c\u8001\u8896\u771f\u8be5\u8c22\u8c22\u4f60\u4e86\u3002\u201d \u3000\u3000\u7ad9\u8d77\u8eab\u5b50\uff0c\u5408\u638c\u8eac\u8eab\uff0c\u7ad9\u7740\u8eab\u540e\u7684\u4e24\u4e2a\u5c0f\u6c99\u5f25\uff0c\u4e89\u884c\u4e00\u6b65\uff0c\u633d\u6276\u7740\u65b9\u4e08\u3002 \u3000\u3000\u6c5f\u67ab\u4e00\u62b1\u62f3\uff0c\u957f\u63d6\u5230\u5730\uff0c\u9053\uff1a\u201c\u5927\u5e08\u5feb\u4e9b\u8bf7\u5750\uff0c\u6298\u715e\u6c5f\u67d0\u4eba\u4e86\u3002\u201d \u3000\u3000\u201c\u5371\u673a\u4ecd\u5728\u7709\u776b\uff0c\u5927\u6218\u4e00\u89e6\u5373\u53d1\uff0c\u201d\u4e03\u5b9d\u548c\u5c1a\u9053\uff1a\u201c\u5927\u5e08\u8eab\u4e0a\u4f59\u6bd2\u672a\u89e3\uff0c\u793c\u6570\u6682\u514d\uff0c\u5feb\u8bf7\u5750\u597d\uff0c\u8ba9\u6c5f\u5c11\u4fa0\u770b\u770b\uff0c\u662f\u5426\u6709\u53ef\u4ee5\u6548\u529b\u7684\u5730\u65b9\u3002\u201d \u3000\u3000\u6c5f\u67ab\u4e5f\u4f1a\u770b\u75c5\u3001\u9664\u6bd2\uff0c\u8001\u53eb\u5316\u5fc3\u4e2d\u662f\u4e00\u5343\u4e00\u4e07\u4e2a\u4e0d\u4fe1\uff0c\u4f46\u4ed6\u6ca1\u6709\u8bf4\u51fa\u6765\uff0c\u56e0\u4e3a\uff0c\u4ed6\u60f3\u5230\u6c5f\u67ab\u53ef\u80fd\u8981\u4ee5\u5185\u529b\u5e2e\u52a9\u767e\u660e\u65b9\u4e08\u903c\u51fa\u4f53\u5185\u4e4b\u6bd2\u3002 \u3000\u3000\u4f46\u4e03\u5b9d\u548c\u5c1a\u5374\u662f\u522b\u6709\u770b\u6cd5\uff0c\u6b66\u6797\u4e09\u5723\u4e2d\u7684\u91d1\u4e39\u4e66\u751f\uff0c\u8bba\u533b\u9053\u4e4b\u7cbe\uff0c\u7edd\u4e0d\u5728\u6613\u82b1\u624b\u5f20\u56db\u59d1\u4e4b\u4e0b\u3002 \u3000\u3000\u6c5f\u67ab\u8ffd\u968f\u751a\u4e45\uff0c\u5fc5\u6709\u6240\u5f97\uff0c\u4f46\u80fd\u4e0d\u80fd\u533b\u6cbb\u8001\u65b9\u6587\u8eab\u4e2d\u7684\u590d\u6bd2\uff0c\u5c31\u65e0\u6cd5\u9884\u6599\u4e86\uff01 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u5927\u548c\u5c1a\u4e5f\u901a\u70b9\u533b\u9053\uff0c\u53ea\u4e0d\u8fc7\uff0c\u81ea\u77e5\u96be\u53ca\u5f20\u56db\u59d1\uff0c\u5c31\u53ea\u597d\u85cf\u62d9\u4e86\u3002 \u3000\u3000\u4e00\u526f\u90d1\u91cd\u5176\u91cd\u7684\u6837\u5b50\uff0c\u6c5f\u67ab\u5148\u66ff\u8001\u548c\u5c1a\u628a\u8d77\u8109\u6765\uff0c\u800c\u4e14\u95ed\u76ee\u51dd\u795e\uff0c\u8138\u4e0a\u795e\u60c5\u8fd8\u6709\u53d8\u5316\uff0c\u4e0d\u65f6\u76b1\u8d77\u7709\u5934\u3002 \u3000\u3000\u8001\u53eb\u5316\u770b\u7684\u5fc3\u4e2d\u76f4\u4e50\uff0c\u5fd6\u9053\uff1a\u201c\u8fd8\u771f\u6709\u4e00\u70b9\u5927\u592b\u7684\u67b6\u52bf\u554a\uff01\u201d\u5fc3\u4e2d\u6000\u7591\uff0c\u5fcd\u4e0d\u4f4f\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u548c\u5c1a\uff0c\u6c5f\u5c11\u4fa0\u771f\u7684\u4f1a\u770b\u75c5\u554a\uff1f\u201d \u3000\u3000\u201c\u5e94\u8be5\u4f1a\u7684\uff0c\u91d1\u4e39\u4e66\u751f\u4ee5\u70bc\u4e39\u672f\u540d\u626c\u5929\u4e0b\uff0c\u5c82\u4e0d\u901a\u533b\u9053\u4e4b\u7406\uff0c\u2026\u2026\u201d\u4e03\u5b9d\u548c\u5c1a\u9053\uff1a\u201c\u5982\u82e5\u6709\u4e00\u4e2a\u4eba\uff0c\u627f\u53d7\u4e86\u91d1\u4e39\u4e66\u751f\u7684\u4e39\u9053\u533b\u5b66\uff0c\u8fd9\u4e2a\u4eba\u5c31\u662f\u6c5f\u67ab\u4e86\u3002\u201d \u3000\u3000\u201c\u60ed\u6127\u3001\u60ed\u6127\uff0c\u8001\u53eb\u5316\u7adf\u7136\u6ca1\u6709\u60f3\u5230\u3002\u201d \u3000\u3000\u94b1\u7f3a\u539f\u672c\u5e26\u4e00\u70b9\u5632\u7b11\u7684\u5473\u9053\uff0c\u770b\u6c5f\u67ab\u4e3a\u767e\u660e\u65b9\u4e08\u533b\u75c5\u7684\u4e8b\uff0c\u73b0\u5728\uff0c\u5374\u4e00\u8138\u5e84\u91cd\u3002 \u3000\u3000\u201c\u5927\u5e08\u2026\u2026\u201d\u6c5f\u67ab\u7f13\u7f13\u6536\u56de\u628a\u8109\u7684\u53f3\u624b\uff0c\u9053\uff1a\u201c\u4f3c\u662f\u6709\u4e00\u53e3\u771f\u5143\u4e4b\u6c14\uff0c\u7ef4\u62a4\u7740\u5fc3\u809d\u80ba\u8151\uff0c\u8fd8\u672a\u8ba9\u6bd2\u6027\u4fb5\u5165\u3002\u201d \u3000\u3000\u201c\u662f\uff01\u8001\u8872\u4ee5\u6570\u5341\u5e74\u4fee\u70bc\u7684\u4e00\u53e3\u5143\u6c14\uff0c\u62a4\u4f4f\u4e86\u5fc3\u80ba\u8981\u5bb3\uff0c\u53ea\u5e0c\u671b\u80fd\u6682\u4fdd\u4e0d\u6b7b\uff01\u201d\u8001\u65b9\u4e08\u9053\uff1a\u201c\u518d\u62d6\u5ef6\u4e00\u4e9b\u65f6\u65e5\uff0c\u770b\u5230\u6b66\u6797\u91cd\u5149\uff0c\u8001\u8872\u5c31\u6b7b\u53ef\u7791\u76ee\u4e86\u3002\u201d \u3000\u3000\u201c\u8001\u65b9\u6587\u5982\u80fd\u63d0\u4f9b\u4e00\u4e9b\u5185\u60c5\uff0c\u4e5f\u8bb8\u5bf9\u89e3\u6bd2\u6709\u4e9b\u5e2e\u52a9\u2026\u2026\u201d\u5f20\u56db\u59d1\u5e26\u7740\u541f\u96ea\u3001\u9752\u51e4\uff0c\u7f13\u6b65\u800c\u5165\uff0c\u63a5\u9053\uff1a\u201c\u4ed6\u4eec\u4ee5\u6bd2\u5236\u6bd2\uff0c\u4f7f\u591a\u6bd2\u76f8\u514b\u76f8\u6301\uff0c\u4fdd\u6301\u4e86\u4e00\u79cd\u5fae\u5999\u7684\u5e73\u51b2\uff0c\u8fd9\u662f\u6781\u9ad8\u7684\u7528\u6bd2\u624b\u6cd5\uff0c\u8001\u65b9\u4e08\u529f\u529b\u6df1\u539a\uff0c\u4ee5\u771f\u5143\u4e4b\u6c14\u62a4\u4f4f\u4e94\u8151\uff0c\u624d\u80fd\u4f7f\u5947\u6bd2\u76f8\u6301\u4e86\u5982\u6b64\u4e4b\u4e45\u2026\u2026\u201d \u3000\u3000\u201c\u5973\u65bd\u4e3b\uff0c\u4e0d\u7528\u518d\u52b3\u8d39\u5fc3\u529b\u4e86\uff0c\u8fd1\u5e74\u6765\u714e\u71ac\u4e8e\u751f\u6b7b\u4e4b\u95f4\uff0c\u8001\u8872\u5bf9\u751f\u6b7b\u7684\u4e8b\uff0c\u65e9\u5df2\u4e0d\u653e\u5fc3\u4e0a\u4e86\uff01\u201d \u3000\u3000\u201c\u8001\u65b9\u4e08\uff0c\u4f60\u4e0d\u80fd\u6b7b\uff0c\u6b66\u6797\u91cd\u89c1\u5929\u65e5\u4e4b\u540e\uff0c\u8fd8\u8981\u4ee5\u4f60\u7684\u5a01\u671b\u91cd\u6574\u6c5f\u6e56\u4f26\u7406\u3001\u79e9\u5e8f\u2026\u2026\u201d \u3000\u3000\u5f20\u56db\u59d1\u9053\uff1a\u201c\u8d35\u5730\u4e2d\u767e\u5b57\u4e00\u8f88\u9ad8\u624b\uff0c\u5df2\u7ecf\u6b7b\u53bb\u4e86\u5341\u4e4b\u516d\u4e03\uff0c\u5c11\u6797\u5bfa\u4e5f\u9700\u8981\u8001\u65b9\u4e08\u5750\u9547\u9886\u5bfc\uff0c\u6211\u4eec\u613f\u5c3d\u529b\u7597\u6cbb\u8001\u65b9\u4e08\u7684\u6bd2\u4f24\uff0c\u4e5f\u9700\u8981\u8001\u65b9\u4e08\u7684\u5168\u529b\u914d\u5408\u3002\u201d \u3000\u3000\u767e\u660e\u65b9\u4e08\u6c89\u541f\u4e86\u826f\u4e45\uff0c\u9053\uff1a\u201c\u5973\u65bd\u4e3b\u8bf4\u7684\u662f\uff0c\u8001\u8872\u867d\u9886\u5bfc\u65e0\u65b9\uff0c\u4f7f\u5c11\u6797\u5bfa\u635f\u5931\u4e86\u4e24\u4ee3\u7cbe\u82f1\uff0c\u4f46\u5982\u56e0\u60f6\u6127\u800c\u6b7b\uff0c\u66f4\u8f9c\u8d1f\u5e08\u957f\u91cd\u6258\uff0c\u8001\u8872\u5e94\u8be5\u5fcd\u75db\u6d3b\u4e0b\u53bb\u3002\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u6b7b\u4e8e\u5f53\u6b7b\uff0c\u624d\u662f\u6b7b\u5f97\u5176\u65f6\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u5927\u5e08\u65e2\u77e5\u7597\u6cbb\u590d\u6bd2\u4e4b\u6cd5\uff0c\u4f55\u4e0d\u8bf4\u51fa\u6765\u5462\uff1f\u201d \u3000\u3000\u201c\u80fd\u5426\u7597\u6cbb\uff0c\u8001\u8872\u8fd8\u4e0d\u6562\u65ad\u8a00\uff0c\u53ea\u662f\u65e2\u840c\u6c42\u751f\u4e4b\u5ff5\uff0c\u603b\u8981\u5fcd\u75db\u4e00\u8bd5\u2026\u2026\u201d\u767e\u660e\u65b9\u4e08\u9053\uff1a \u3000\u3000\u201c\u4e0d\u8fc7\uff0c\u8fd8\u8981\u5973\u65bd\u4e3b\u548c\u6c5f\u5c11\u4fa0\u76f8\u52a9\u4e00\u81c2\u4e4b\u529b\u4e86\u3002\u201d \u3000\u3000\u201c\u5927\u5e08\u8bf7\u8bf4\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u529b\u80fd\u6240\u53ca\uff0c\u65e0\u4e0d\u4ece\u547d\u3002\u201d \u3000\u3000\u201c\u5148\u4ee5\u91d1\u9488\u8fc7\u7a74\u4e4b\u6cd5\uff0c\u523a\u5165\u8001\u8872\u4e09\u5341\u516d\u5927\u7a74\u4e2d\u2026\u2026\u201d\u767e\u660e\u5927\u5e08\u9053\uff1a\u201c\u518d\u4ee5\u4e8c\u5341\u5e74\u9648\u918b\u767e\u65a4\uff0c\u7f6e\u4e8e\u5927\u9505\u4e4b\u4e2d\uff0c\u653e\u4e00\u7af9\u84d6\uff0c\u5916\u7f69\u84b8\u7b3c\uff0c\u5927\u706b\u70e7\u70ed\u9648\u918b\u540e\uff0c\u6539\u4ee5\u6587\u706b\uff0c\u95f4\u4ee5\u758f\u70ed\u4e4b\u6cd5\uff0c\u4ee5\u80fd\u6301\u4e45\u2026\u2026\u201d \u3000\u3000\u201c\u662f\u5426\u8981\u7f6e\u653e\u8349\u836f\u5462\uff1f\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u5927\u5e08\u7684\u8eab\u8eaf\u56db\u5468\uff0c\u4e5f\u8981\u4ee5\u6728\u67b6\u56fa\u5b9a\uff0c\u4ee5\u514d\u707c\u4f24\u3002\u201d \u3000\u3000\u201c\u5bf9\uff01\u836f\u65b9\u8001\u8872\u77e5\u9053\uff0c\u2026\u2026\u201d\u767e\u660e\u5927\u5e08\u9053\uff1a\u201c\u5982\u82e5\u6b64\u6cd5\u4ecd\u4e0d\u80fd\u4f7f\u8001\u8872\u5c3d\u9664\u4f53\u5185\u4e4b\u6bd2\uff0c\u8bf8\u4f4d\u5c31\u4e0d\u53ef\u963b\u6b62\u8001\u8872\u81ea\u7edd\uff01\u201d \u3000\u3000\u201c\u6211\u660e\u767d\uff0c\u90a3\u79cd\u591a\u6bd2\u4ea4\u7a9c\uff0c\u6d41\u5e03\u5168\u8eab\u7684\u75db\u82e6\uff0c\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u5b9e\u975e\u4eba\u6240\u80fd\u5fcd\u53d7\u7684\u3002\u201d \u3000\u3000\u201c\u6700\u91cd\u8981\u7684\u662f\uff0c\u5947\u6bd2\u6563\u5e03\u5168\u8eab\uff0c\u6df1\u5165\u8840\u8109\u3001\u5173\u8282\uff0c\u6b66\u529f\u5168\u5931\uff0c\u6210\u4e86\u5e9f\u4eba\u2026\u2026\u201d\u767e\u660e\u5927\u5e08\u9053\uff1a\u201c\u8001\u8872\u53ec\u96c6\u5bfa\u4e2d\u5f1f\u5b50\uff0c\u5ba3\u5e03\u6b64\u4e8b\uff0c\u9009\u51e0\u4e2a\u6267\u4e8b\u4e3b\u6301\uff0c\u53c2\u4e0e\u5176\u4e8b\uff0c\u4ee5\u514d\u7d2f\u53ca\u8bf8\u4f4d\uff0c\u4e5f\u987a\u4fbf\u9009\u51fa\u63a5\u4efb\u5c11\u6797\u5bfa\u7684\u65b9\u4e08\u3002\u201d \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u5927\u5e08\u601d\u8651\u5468\u8be6\uff0c\u7406\u5f53\u5982\u6b64\u3002\u201d \u3000\u3000\u539f\u6765\uff0c\u5c11\u6797\u5bfa\u4e2d\uff0c\u9664\u4e86\u65b9\u4e08\u767e\u660e\u5927\u5e08\u4e4b\u5916\uff0c\u5176\u4ed6\u8eab\u53d7\u7981\u5236\u7684\u50e7\u4f17\uff0c\u90fd\u5df2\u89e3\u9664\u3002 \u3000\u3000\u56db\u4f4d\u767e\u5b57\u8f88\u7684\u9ad8\u50e7\uff0c\u5206\u5b88\u5ba4\u4e2d\u56db\u4e2a\u65b9\u4f4d\uff0c\u5341\u516b\u4e2a\u5065\u58ee\u7684\u5c11\u6797\u548c\u5c1a\uff0c\u5e03\u5b88\u5728\u4e00\u5ea7\u5c0f\u9662\u843d\u4e2d\uff0c\u6212\u5907\u68ee\u4e25\u3002 \u3000\u3000\u539f\u6765\uff0c\u8fd9\u91cc\u662f\u5c11\u6797\u5bfa\u4e2d\u4e00\u5ea7\u53a8\u623f\uff0c\u4f46\u5df2\u7ecf\u8fc7\u4e00\u756a\u6d17\u5237\u3001\u6253\u626b\uff0c\u767e\u660e\u8001\u65b9\u4e08\u5750\u5728\u4e00\u53e3\u5927\u9505\u4e0a\u7684\u84b8\u7b3c\u4e2d\uff0c\u53ea\u9732\u51fa\u4e00\u4e2a\u8111\u888b\uff0c\u5149\u5934\u4e0a\u6c57\u51fa\u5982\u96e8\uff0c\u4e09\u95f4\u5927\u7684\u53a8\u623f\u4e2d\uff0c\u918b\u9178\u6251\u9f3b\u3002 \u3000\u3000\u9752\u51e4\u3001\u541f\u96ea\u4f3a\u706b\uff0c\u4e0d\u80fd\u8ba9\u706b\u592a\u5927\uff0c\u9505\u4e2d\u7684\u6cb8\u817e\u7684\u8001\u918b\uff0c\u70eb\u4f24\u4e86\u4eba\uff0c\u4e5f\u4e0d\u80fd\u706b\u592a\u5c0f\uff0c\u8ba9\u8001\u918b\u4e0d\u518d\u6eda\u52a8\u3002 \u3000\u3000\u6c5f\u67ab\u548c\u5f20\u56db\u59d1\u624b\u6267\u7740\u51b7\u6c34\u6d78\u8fc7\u7684\u5e03\u4e2d\uff0c\u4e00\u9762\u66ff\u767e\u660e\u65b9\u4e08\u62ed\u53bb\u6c57\u6c34\uff0c\u8fd8\u8981\u4e0d\u65ad\u542f\u52a8\u84b8\u7b3c\uff0c\u8ba9\u70ed\u6c14\u6563\u53d1\u4e00\u4e9b\uff0c\u4e0d\u80fd\u628a\u4eba\u84b8\u719f\u4e86\u3002 \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u5728\u4e00\u8fb9\u6253\u6742\u5e2e\u5fd9\u3002 \u3000\u3000\u5c11\u6797\u5bfa\u4e2d\u867d\u7136\u6709\u5f88\u591a\u7684\u50e7\u4f17\u53ef\u7528\uff0c\u4f46\u5f20\u56db\u7ad9\u4e2d\u4ecd\u6709\u987e\u5fcc\uff0c\u5c31\u5168\u7528\u81ea\u5df1\u4eba\u6765\u66ff\u4ee3\u3002 \u3000\u3000\u56e0\u4e3a\uff0c\u767e\u660e\u548c\u5c1a\u73b0\u5728\u975e\u5e38\u7684\u8106\u5f31\uff0c\u4efb\u4f55\u6697\u7b97\uff0c\u5c31\u53ef\u4f7f\u4ed6\u6b7b\u4ea1\u4e8e\u4e0d\u77e5\u4e0d\u89c9\u4e4b\u4e2d\u3002 \u3000\u3000\u7a81\u7136\u95f4\uff0c\u6885\u82b1\u7f13\u6b65\u800c\u5165\uff0c\u5f88\u4ece\u5bb9\u7684\u8d70\u5230\u4e86\u5f20\u56db\u59d1\u7684\u8eab\u524d\uff0c\u58f0\u97f3\u5374\u6025\u4fc3\u3001\u4f4e\u6c89\uff0c\u9053\uff1a\u201c\u5f20\u59e8\uff0c\u6709\u4e2a\u5c0f\u7537\u4eba\u8981\u89c1\u9752\u51e4\uff0c\u4f46\u6211\u4e00\u773c\u5c31\u770b\u7a7f\u4ed6\u662f\u5973\u626e\u7537\u88c5\uff0c\u597d\u50cf\u662f\u84dd\u51e4\u6d3e\u6765\u7684\uff1f\u201d \u3000\u3000\u5f20\u56db\u59d1\u70b9\u70b9\u5934\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u4f60\u6765\u4f3a\u706b\uff0c\u6362\u4e0b\u541f\u96ea\u3001\u9752\u51e4\uff0c\u541f\u96ea\u8d1f\u8d23\u5168\u5c40\u5e03\u7f72\uff0c\u51c6\u5907\u5e94\u53d8\uff0c\u5982\u679c\u60c5\u52bf\u7d27\u6025\uff0c\u7acb\u523b\u4f20\u62a5\u8fc7\u6765\uff01\u201d \u3000\u3000\u541f\u96ea\u3001\u9752\u51e4\u4ea4\u5f85\u4e86\u6885\u82b1\u51e0\u53e5\uff0c\u7acb\u523b\u9000\u51fa\u3002 \u3000\u3000\u6c5f\u67ab\u4f4e\u58f0\u9053\uff1a\u201c\u5927\u59d0\uff0c\u5c0f\u5f1f\u2026\u2026\u201d \u3000\u3000\u201c\u4f60\u4e0d\u80fd\u8d70\uff01\u8001\u65b9\u4e08\u5df2\u5c06\u529f\u5fb7\u5706\u6ee1\uff0c\u4f60\u8981\u4ee5\u5185\u529b\u5e2e\u52a9\u4ed6\u8fd0\u6c14\u884c\u52a8\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u4e5f\u5e0c\u671b\u84dd\u51e4\u4f20\u6765\u7684\u6d88\u606f\uff0c\u80fd\u518d\u7ed9\u6211\u4eec\u4e00\u65e5\u591c\u7684\u65f6\u95f4\u3002\u201d \u3000\u3000\u6c5f\u67ab\u70b9\u70b9\u5934\uff0c\u5f20\u56db\u59d1\u51b3\u5b9a\u7684\u4e8b\uff0c\u4ed6\u4ece\u4e0d\u4e89\u8fa9\u3002 \u3000\u3000\u4e00\u6b65\u8e0f\u5165\u5ba2\u623f\uff0c\u4e00\u4e2a\u84dd\u8863\u5c11\u5e74\uff0c\u7acb\u523b\u5feb\u6b65\u5954\u6765\uff0c\u9053\uff1a\u201c\u5c0f\u5a62\u8fce\u6625\u2026\u2026\u201d\u7a81\u7136\u4f4f\u53e3\u4e0d\u8bf4\u4e86\u3002 \u3000\u3000\u9752\u51e4\u6325\u6325\u624b\uff0c\u8ba9\u4e24\u4e2a\u5b88\u5728\u5ba2\u623f\u95e8\u53e3\u7684\u4e24\u4e2a\u50e7\u4eba\u79bb\u53bb\uff0c\u63a9\u4e0a\u623f\u95e8\uff0c\u9053\uff1a\u201c\u8bf4\u5427\uff01\u6709\u4ec0\u4e48\u7d27\u6025\u5927\u4e8b\uff1f\u201d \u3000\u3000\u8fce\u6625\u770b\u770b\u4e00\u8eab\u7537\u88c5\uff0c\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u59d1\u5a18\u8bf4\uff0c\u6559\u4e3b\u548c\u56db\u65b9\u4f7f\u8005\uff0c\u5df3\u7136\u4f1a\u5408\uff0c\u5929\u738b\u95e8\u3001\u5929\u4e2d\u3001\u5929\u9a6c\u5802\u7684\u7cbe\u9510\u4e5f\u90fd\u4f17\u9f50\uff0c\u660e\u5929\u4e2d\u5348\u5c31\u4f1a\u805a\u96c6\u4e8e\u5c11\u6797\u5bfa\u5916\uff0c\u8981\u5802\u5802\u6b63\u6b63\u7684\u548c\u4f60\u4eec\u51b3\u4e00\u80dc\u8d1f\u3002 \u3000\u3000\u59d1\u5a18\u770b\u4e0d\u51fa\u4ed6\u4eec\u6709\u4ec0\u4e48\u9634\u8c0b\u3001\u9b3c\u8ba1\uff0c\u8981\u6211\u5c3d\u5feb\u901a\u77e5\u4f60\uff0c\u5c31\u662f\u8fd9\u51e0\u53e5\u8bdd\uff0c\u4f20\u544a\u5df2\u6bd5\uff0c\u9752\u51e4\u5c0f\u59d0\uff0c\u6211\u8fd9\u91cc\u62dc\u522b\u4e86\u3002\u201d \u3000\u3000\u7a81\u7136\u8dea\u5728\u5730\u4e0a\uff0c\u606d\u606d\u656c\u656c\u5bf9\u9752\u51e4\u53e9\u4e86\u4e09\u4e2a\u5934\uff0c\u8d77\u8eab\u800c\u51fa\u3002 \u3000\u3000\u9752\u51e4\u7565\u4e00\u6c89\u5fc3\uff0c\u4e00\u628a\u6293\u4f4f\u8fce\u6625\uff0c\u9053\uff1a\u201c\u8fd8\u6709\u4ec0\u4e48\u4e8b\u6ca1\u544a\u8bc9\u6211\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\u4e86\uff0c\u518d\u6709\u4e5f\u662f\u5c0f\u5a62\u4e2a\u4eba\u7684\u4e8b\uff0c\u548c\u5c0f\u59d0\u65e0\u5173\u3002\u201d \u3000\u3000\u201c\u6211\u60f3\u77e5\u9053\uff0c\u5c31\u662f\u4f60\u4e2a\u4eba\u7684\u4e8b\uff0c\u4e5f\u8981\u8bf4\u4e2a\u6e05\u695a\u2026\u2026\u201d\u9752\u51e4\u53f9\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5176\u5b9e\uff0c\u4f60\u4e0d\u8bf4\uff0c\u6211\u4e5f\u731c\u5230\u4e86\u5341\u4e4b\u516b\u4e5d\uff0c\u84dd\u51e4\u8981\u4f60\u6b7b\uff0c\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u201c\u662f\uff01\u2026\u2026\u201d\u8fce\u6625\u9053\uff1a\u201c\u59d1\u5a18\u867d\u7136\u5429\u5490\u8fc7\uff0c\u53ef\u662f\u5c0f\u5a62\u4e5f\u89c9\u7740\u6ca1\u6709\u4ec0\u4e48\u4e0d\u5bf9\u5440\uff01\u6b7b\u4e86\u5c0f\u5a62\uff0c\u4fdd\u5168\u59d1\u5a18\uff0c\u662f\u5929\u7ecf\u5730\u4e49\u7684\u4e8b\uff01\u201d \u3000\u3000\u201c\u8fc7\u53bb\uff0c\u6211\u4e5f\u4f1a\u89c9\u7740\u8fd9\u662f\u5e94\u8be5\u7684\uff0c\u6240\u4ee5\uff0c\u73b0\u5728\u6211\u611f\u5230\u662f\u4e2a\u5f88\u5927\u7684\u9519\u8bef\uff0c\u4f60\u662f\u4e00\u6761\u547d\uff0c\u84dd\u51e4\u662f\uff0c\u5929\u738b\u4e5f\u662f\u2026\u2026\u201d\u9752\u51e4\u9053\uff1a\u201c\u4f60\u5fe0\u4e8e\u84dd\u51e4\u3002 \u3000\u3000\u4e3a\u5979\u6548\u547d\uff0c\u5f53\u7136\u5f88\u597d\uff0e\u4f46\u4e0d\u4e00\u5b9a\u5c31\u8981\u6b7b\u554a\uff01\u56e0\u4e3a\uff0c\u8fd9\u79cd\u727a\u7272\u6ca1\u6709\u4ef7\u503c\uff0c\u8bf4\u5b9e\u8bdd\uff0c\u8fce\u6625\uff0c\u4f60\u662f\u771f\u7684\u4e0d\u6015\u6b7b\u5417\uff1f\u201d \u3000\u3000\u201c\u6709\u70b9\u6015\uff01\u53ef\u662f\uff0c\u4e0d\u6b7b\u53c8\u600e\u4e48\u529e\u5462\uff1f\u201d\u8fce\u6625\u4e00\u8138\u65e0\u5948\u7684\u9053\u3002 \u3000\u3000\u201c\u7559\u4e0b\u6765\uff01\u201d \u3000\u3000\u201c\u5c0f\u59d0\u613f\u610f\u6536\u7559\u6211\uff1f\u2026\u2026\u201d\u8fce\u6625\u7acb\u523b\u62dc\u4f0f\u4e8e\u5730\uff0c\u9053\uff1a\u201c\u591a\u8c22\u5c0f\u59d0\uff0c\u5c0f\u5a62\u613f\u7ec8\u751f\u4f8d\u5019\u5c0f\u59d0\uff0c\u751f\u6b7b\u4e0d\u6e1d\u3002\u201d \u3000\u3000\u5979\u5f88\u6015\u6b7b\uff0c\u4f46\u5949\u547d\u6b7b\u4ea1\u5374\u662f\u5979\u81ea\u5df1\u6700\u597d\u7684\u4e00\u4e2a\u9009\u62e9\uff0c\u771f\u662f\u5929\u4e0b\u867d\u5927\uff0c\u8fce\u6625\u5374\u65e0\u7acb\u8db3\u65e0\u5904\u7684\u611f\u89c9\u3002 \u3000\u3000\u6276\u8d77\u8fce\u6625\uff0c\u9752\u51e4\u5374\u795e\u60c5\u8083\u7136\u7684\u8bf4\uff1a\u201c\u84dd\u51e4\u4e0d\u662f\u4e2a\u55dc\u6740\u7684\u4eba\uff0c\u53ea\u662f\u6210\u957f\u90a3\u4e2a\u73af\u5883\u91cc\uff0c\u90fd\u6709\u7740\u89c6\u4eba\u547d\u5982\u8349\u82a5\u7684\u4e60\u60ef\uff0c\u8fc7\u53bb\uff0c\u6211\u4e5f\u4e00\u6837\uff0c\u4f46\u73b0\u5728\uff0c\u6211\u7684\u770b\u6cd5\u53d8\u4e86\uff0c\u6211\u975e\u5e38\u6b22\u8fce\u4f60\u7559\u8fd9\u91cc\uff0c\u4e5f\u5c0a\u91cd\u4f60\u7684\u9009\u62e9\uff0c\u552f\u4e00\u7684\u8981\u6c42\u662f\uff0c\u4f60\u8981\u6682\u65f6\u8eb2\u5728\u5c11\u6797\u5bfa\u4e2d\uff0c\u4e0d\u80fd\u51fa\u53bb\uff0c\u6211\u6015\u4f60\u9732\u51fa\u884c\u8e2a\uff0c\u4f1a\u5bb3\u4e86\u84dd\u51e4\u2026\u2026\u201d \u3000\u3000\u201c\u662f\uff01\u5c0f\u5a62\u4e00\u5207\u9075\u547d\u3002\u201d \u3000\u3000\u9752\u51e4\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u53bb\u8ddf\u7740\u5f20\u59e8\u5427\uff01\u7167\u987e\u5979\u7684\u751f\u6d3b\u8d77\u5c45\u3002\u201d \u3000\u3000\u201c\u600e\u4e48\uff1f\u4e8c\u5c0f\u59d0\u53ef\u662f\u89c9\u7740\u6211\u592a\u7b28\u4e86\uff0c\u4e0d\u8981\u7528\u6211\u3002\u201d\u8fce\u6625\u6709\u4e9b\u60f6\u6050\u7684\u8bf4\u3002 \u3000\u3000\u201c\u4e0d\u662f\uff01\u6211\u4eec\u5728\u8fd9\u91cc\uff0c\u90fd\u662f\u81ea\u7406\u8d77\u5c45\uff0c\u2026\u2026\u201d\u9752\u51e4\u9053\uff1a\u201c\u5f20\u59e8\u662f\u957f\u8f88\uff0c\u4e5f\u7b97\u662f\u6211\u7684\u5e08\u5c0a\uff0c\u4e0d\u8fc7\uff0c\u4f60\u53ef\u4ee5\u653e\u5fc3\uff0c\u6211\u4eec\u4e0d\u4f1a\u903c\u4f60\u53bb\u5bf9\u654c\u6253\u4f0f\uff0c\u4f60\u53ea\u8981\u8eb2\u8d77\u6765\uff0c\u5c11\u6797\u5bfa\u8fd9\u4e00\u6218\u7ed3\u675f\u540e\uff0c\u4f60\u60f3\u8d70\u5c31\u53ef\u4ee5\u8d70\u4e86\uff0c\u90a3\u65f6\u5019\uff0c\u5929\u7a7a\u4efb\u9e1f\u98de\uff0c\u4f60\u60f3\u5230\u54ea\u91cc\uff0c\u5c31\u53bb\u54ea\u91cc\u3002\u201d \u3000\u3000\u8fce\u6625\u8fd8\u65e0\u6cd5\u9002\u5e94\u8fd9\u79cd\u5b8c\u5168\u7684\u81ea\u7531\uff0c\u60f3\u4e86\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u597d\u5427\uff01\u5c0f\u5a62\u4e00\u5207\u542c\u4ece\u4e8c\u5c0f\u59d0\u7684\u5b89\u6392\u3002\u201d \u3000\u3000\u201c\u8fce\u6625\uff0c\u5f20\u59e8\u5982\u80af\u6536\u4e0b\u4f60\uff0c\u90a3\u53ef\u771f\u662f\u4f60\u7684\u798f\u6c14\u2026\u2026\u201d\u9752\u51e4\u7b11\u9053\uff1a\u201c\u73b0\u5728\uff0c\u6211\u8bf4\u4ec0\u4e48\uff0c\u4f60\u90fd\u4e0d\u592a\u660e\u767d\uff0c\u4ee5\u540e\uff0c\u4f60\u5c31\u975e\u5e38\u7684\u611f\u6fc0\u6211\u4e86\u3002\u201d \u3000\u3000\u201c\u73b0\u5728\uff0c\u8fce\u6625\u5c31\u5f88\u611f\u6fc0\u4e8c\u5c0f\u59d0\uff0c\u4f60\u662f\u6211\u7684\u6551\u547d\u6069\u4eba\u54ea\uff01\u201d \u3000\u3000\u9752\u51e4\u660e\u767d\uff0c\u8fce\u6625\u8fd8\u65e0\u6cd5\u6709\u81ea\u5df1\u7684\u4e3b\u89c1\uff0c\u5979\u4e00\u76f4\u751f\u6d3b\u5728\u84dd\u51e4\u7684\u6743\u5a01\u4e4b\u4e0b\uff0c\u751f\u6b7b\u4e5f\u7531\u84dd\u51e4\u4e3b\u88c1\uff0c\u4e00\u65f6\u95f4\uff0c\u8fd8\u96be\u6709\u81ea\u4f5c\u4e3b\u610f\u7684\u80fd\u529b\u3002 \u3000\u3000\u201c\u9752\u51e4\u59d0\u2026\u2026\u201d\u541f\u96ea\u7f13\u6b65\u800c\u5165\uff0c\u9053\uff1a\u201c\u5c11\u6797\u50e7\u4f17\u5df2\u805a\u96c6\u4e8e\u8fbe\u6469\u9662\u4e2d\uff0c\u6709\u4e8c\u767e\u516b\u5341\u516d\u4eba\uff0c\u6211\u6ca1\u6709\u8981\u6c42\u4ed6\u4eec\u51fa\u52a8\u5bf9\u654c\uff0c\u4ed6\u4eec\u90fd\u662f\u81ea\u613f\u7684\u3002 \u3000\u3000\u5176\u4e2d\u6709\u8fd1\u534a\u6570\u4e60\u7ec3\u8fc7\u7f57\u6c49\u9635\uff0c\u53ef\u4ee5\u6392\u51fa\u516d\u5ea7\u5341\u516b\u4eba\u7684\u7f57\u6c49\u9635\uff0c\u4e5f\u53ef\u4ee5\u5206\u6392\u6210\u4e09\u5341\u516d\u4eba\u7684\u7f57\u6c49\u9635\u4e09\u5ea7\uff0c\u8fd9\u8981\u7740\u654c\u4eba\u5982\u4f55\u653b\u6765\uff1f\u624d\u80fd\u51b3\u5b9a\u5982\u4f55\u8fce\u654c\u3002\u201d \u3000\u3000\u201c\u4e86\u4e0d\u8d77\uff0c\u541f\u96ea\uff0c\u8fd9\u4e48\u77ed\u77ed\u4e00\u9635\u5de5\u592b\uff0c\u4f60\u5df2\u7ecf\u628a\u5c11\u6797\u5bfa\u7684\u50e7\u4f17\u52a8\u5458\u4e86\u2026\u2026\u201d\u9752\u51e4\u7b11\u9053\uff1a \u3000\u3000\u201c\u770b\u4f60\u50cf\u6735\u82b1\u4f3c\u7684\u5c0f\u4eba\u51e0\uff0c\u60f3\u4e0d\u901a\uff0c\u600e\u4e48\u6837\u53bb\u6307\u6325\u90a3\u4e48\u591a\u4eba\u9ad8\u9a6c\u5927\u7684\u5c11\u6797\u50e7\u4f17\u3002\u201d \u3000\u3000\u201c\u7b80\u5355\u554a\uff01\u2026\u2026\u201d\u541f\u96ea\u9053\uff1a\u201c\u6211\u4f20\u544a\u4ed6\u4eec\uff0c\u654c\u4eba\u53ef\u80fd\u8981\u627e\u4e0a\u95e8\u4e86\uff0c\u613f\u610f\u6253\u62fc\u81ea\u6551\u7684\u7559\u4e0b\u6765\u4fdd\u62a4\u5c11\u6797\u5bfa\uff0c\u6015\u6b7b\u7684\u8d81\u8fd8\u6709\u4e00\u70b9\u9003\u547d\u65f6\u95f4\uff0c\u65e9\u4e9b\u8d70\u5427\uff01\u201d \u3000\u3000\u201c\u6709\u6ca1\u6709\u9003\u8d70\u7684\uff1f\u201d\u9752\u51e4\u95ee\u3002 \u3000\u3000\u201c\u6709\u554a\uff01\u5927\u6982\u6709\u5341\u51e0\u4e2a\u5427\uff01\u201d\u53f6\u96ea\u9063\uff0c\u201c\u7edd\u5927\u90e8\u5206\u7684\u4eba\uff0c\u90fd\u96c6\u4e2d\u5230\u8fbe\u6469\u9662\u53bb\uff0c\u51c6\u5907\u4e00\u6218\uff0c\u6211\u8ba9\u4ed6\u4eec\u81ea\u5206\u7b49\u7ea7\uff0c\u9009\u51fa\u6765\u4e8c\u767e\u516b\u4e00\u4e2a\u6b66\u529f\u597d\u7684\u4eba\u5907\u6218\uff0c\u5176\u4f59\u7684\uff0c\u5206\u6d3e\u5230\u56db\u5916\u653e\u54e8\u3002 \u3000\u3000\u51c6\u5907\u708a\u98df\u3001\u836f\u7269\uff0c\u6551\u62a4\u53d7\u4f24\u7684\u4eba\uff0c\u56db\u4e94\u767e\u4e2a\u548c\u5c1a\uff0c\u7247\u523b\u5de5\u592b\uff0c\u5c31\u628a\u4ed6\u4eec\u5b89\u6392\u59a5\u5f53\uff0c\u5907\u6218\u7684\u4eba\u5df2\u5f00\u59cb\u5728\u8fbe\u6469\u9662\u4e2d\u6f14\u4e60\u9635\u6cd5\u3001\u6b66\u529f\u4e86\u3002\u201d \u3000\u3000\u201c\u541f\u96ea\uff0c\u52ff\u602a\u5f20\u59e8\u5668\u91cd\u4f60\uff0c\u8001\u53eb\u5316\u548c\u9093\u98de\u7b49\u4f69\u670d\u4f60\uff0c\u8fd9\u4e48\u56f0\u96be\u7684\u4e8b\uff0c\u4f60\u5c31\u6709\u529e\u6cd5\u4e00\u4e0b\u5b50\u6446\u7684\u56db\u5e73\u516b\u7a33\uff0c\u2026\u2026\u201d\u9752\u51e4\u9053\uff1a\u201c\u6709\u4ec0\u4e48\u9063\u6d3e\u6211\u7684\uff0c\u5c31\u8bf7\u5429\u5490\u5427\uff1f\u201d \u3000\u3000\u201c\u541f\u971c\u5df2\u79bb\u5bfa\u53bb\u523a\u63a2\u654c\u60c5\uff0c\u2026\u2026\u201d\u541f\u96ea\u7b11\u9053\uff1a\u201c\u5c0f\u59b9\u6765\u8fd9\u91cc\u662f\u5411\u59d0\u59d0\u8bf7\u6559\uff0c\u84dd\u51e4\u59d0\u4f20\u6765\u4e86\u4ec0\u4e48\u6d88\u606f\uff1f\u201d \u3000\u3000\u9752\u51e4\u5fae\u5fae\u4e00\u6014\uff0c\u5fd6\u9053\uff1a\u201c\u597d\u6062\u5b8f\u7684\u6c14\u5ea6\uff0c\u597d\u751c\u871c\u7684\u5634\u5df4\uff01\u770b\u5979\u90a3\u526f\u81ea\u81ea\u7136\u7136\uff0c\u5168\u65e0\u5992\u5fcc\u7684\u6837\u5b50\uff0c\u771f\u662f\u80f8\u6000\u5343\u91cc\uff0c\u4e00\u8154\u7d20\u5fc3\uff0c\u600e\u4e0d\u53eb\u4eba\u75bc\u7231\uff0c\u6211\u662f\u4e07\u96be\u53ca\u5979\u4e86\uff01\u84dd\u51e4\u554a\uff01\u84dd\u51e4\uff0c\u6211\u81ea\u8ba4\u7075\u6167\u8fc7\u4eba\u9047\u4e0a\u8fd9\u4f4d\u541f\u96ea\u59b9\u5b50\uff0c\u53ea\u6015\u4e5f\u8981\u900a\u8ba9\u4e09\u5206\u4e86\uff01\u201d \u3000\u3000\u201c\u8fce\u6625\uff0c\u5feb\u89c1\u8fc7\u541f\u96ea\u59d1\u5a18\u2026\u2026\u201d\u9752\u51e4\u9053\uff1a\u201c\u5979\u662f\u5f20\u59e8\u65b0\u4f20\u5f1f\u5b50\uff0c\u4e5f\u662f\u6211\u4eec\u7684\u95fa\u623f\u73ed\u5934\uff0c\u5feb\u628a\u6765\u610f\u8bf4\u51fa\u6765\u3002\u201d \u3000\u3000\u5979\u5b9e\u5728\u662f\u656c\u7231\u541f\u96ea\uff0c\u4e0d\u77e5\u8be5\u5982\u4f55\u8d5e\u7f8e\uff1f\u90a3\u53e5\u95fa\u623f\u73ed\u5934\uff0c\u66f4\u662f\u5256\u5fc3\u793a\u60c5\uff0c\u610f\u601d\u662f\u8ba9\u4f60\u4f5c\u8001\u5927\u4e86\u3002 \u3000\u3000\u541f\u96ea\u62a5\u4ee5\u5fae\u7b11\uff0c\u4e5f\u4e0d\u77e5\u5979\u662f\u5426\u660e\u767d\u4e86\u9752\u51e4\u7684\u5fc3\u610f\u3002 \u3000\u3000\u8fce\u6625\u867d\u7136\u8fd8\u4e0d\u61c2\u81ea\u4f5c\u4e3b\u89c1\uff0c\u4f46\u5374\u6709\u9022\u8fce\u4eba\u7684\u4e56\u5de7\uff0c\u7acb\u523b\u8eac\u8eab\u884c\u793c\uff0c\u9053\uff1a\u201c\u5c0f\u5a62\u8fce\u6625\uff0c\u62dc\u89c1\u541f\u96ea\u59d1\u5a18\u3002\u201d \u3000\u3000\u201c\u7f62\u4e86\uff0c\u7f62\u4e86\uff0c\u4e0d\u7528\u591a\u793c\u2026\u2026\u201d\u541f\u96ea\u6ca1\u8fd8\u793c\uff0c\u4e5f\u672a\u53d7\u793c\uff0c\u4e00\u95ea\u8eab\uff0c\u7b11\u9053\uff1a\u201c\u84dd\u51e4\u59d0\u6709\u4ec0\u4e48\u6307\u793a\u4f20\u6765\uff1f\u201d \u3000\u3000\u201c\u6559\u4e3b\u96c6\u805a\u4e86\u56db\u65b9\u4f7f\u8005\uff0c\u4eb2\u7387\u5929\u738b\u95e8\u3001\u5929\u540e\u5bab\u3001\u5929\u9a6c\u5802\u7cbe\u9510\u9ad8\u624b\uff0c\u660e\u65e5\u5348\u65f6\u53ef\u5230\u5c11\u6797\u5bfa\u5916\uff01\u201d\u8fce\u6625\u9053\uff1a\u201c\u6b63\u5f0f\u6311\u6218\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u591a\u8c22\u4f60\u4f20\u6765\u7684\u8baf\u606f\u3002\u201d\u53f6\u96ea\u7b11\u9053\uff1a\u201c\u9752\u51e4\u59d0\uff0c\u4f60\u770b\u4ed6\u4eec\u4f1a\u4e0d\u4f1a\u6539\u53d8\u8ba1\u5212\uff1f\u4eca\u591c\u5077\u88ad\uff1f\u201d \u3000\u3000\u201c\u84dd\u51e4\u59d1\u5a18\u4ea4\u4ee3\u8fc7\uff0c\u5982\u679c\u6559\u4e3b\u6539\u53d8\u4e86\u8ba1\u5212\uff0c\u5979\u4f1a\u4ee5\u672c\u95e8\u4e2d\u7684\u8baf\u53f7\u4f20\u5165\u5c11\u6797\u5bfa\u4e2d\uff01\u201d\u8fce\u6625\u9053\uff1a\u201c\u4e8c\u5c0f\u59d0\u4e00\u542c\u5373\u77e5\u3002\u201d \u3000\u3000\u201c\u8fd9\u5c31\u66f4\u597d\u4e86\uff0c\u5c31\u8bf7\u9752\u51e4\u59d0\u8d1f\u8d77\u8fd9\u4e2a\u8d23\u4efb\u3002\u2026\u2026\u201d\u53f6\u96ea\u9053\uff1a\u201c\u8fce\u6625\u59d1\u5a18\u5982\u4e0d\u7528\u56de\u53bb\u590d\u547d\uff0c\u6682\u8bf7\u7559\u5728\u59d0\u59d0\u8eab\u4fa7\u4f5c\u4e3a\u52a9\u624b\u3002 \u3000\u3000\u5e0c\u671b\u8001\u65b9\u4e08\u65e9\u4e9b\u8131\u51fa\u82e6\u6d77\uff0c\u660e\u5348\u7684\u5927\u6218\uff0c\u5c31\u53ef\u4ee5\u7531\u5f20\u59e8\u6307\u6325\u5168\u5c40\uff0c\u5509\uff01\u6211\u771f\u7684\u6709\u70b9\u5bb3\u6015\u554a\uff01\u201d \u3000\u3000\u201c\u4f60\u4e3e\u91cd\u82e5\u8f7b\uff0c\u624d\u534e\u6d0b\u6ea2\uff0c\u5f20\u59e8\u628a\u8fd9\u6837\u5927\u4e8b\u60c5\u4ea4\u7ed9\u4f60\uff0c\u81ea\u7136\u662f\u7edd\u5bf9\u4fe1\u4efb\u4f60\u2026\u2026\u201d\u9752\u51e4\u9053\uff1a\u201c\u6211\u4eec\u4e5f\u90fd\u652f\u6301\u4f60\uff0c\u541f\u96ea\uff0c\u4e0d\u8981\u754f\u60e7\u561b\uff01\u201d \u3000\u3000\u201c\u9752\u51e4\u59d0\uff0c\u6211\u4e0d\u6015\u654c\u4eba\uff0c\u518d\u5389\u5bb3\u7684\u654c\u4eba\uff0c\u6211\u4e5f\u4e0d\u6015\uff01\u2026\u2026\u201d\u201c\u90a3\u4f60\u6015\u4ec0\u4e48\u5462\uff1f\u201d\u9752\u51e4\u5947\u9053\uff1a\u201c\u6211\u4eec\u90fd\u62e5\u62a4\u4f60\uff0c\u5c11\u6797\u5bfa\u50e7\u4f17\u4eec\u4e5f\u628a\u4f60\u770b\u6210\u4e86\u5973\u83e9\u8428\u3002\u201d \u3000\u3000\u201c\u6211\u6015\u7239\u548c\u6c5f\u5927\u54e5\u2026\u2026\u201d\u541f\u96ea\u9053\uff1a\u201c\u7239\u7684\u5634\u91cc\uff0c\u867d\u8bf4\u613f\u610f\u542c\u4ed6\u7684\u5b9d\u8d1d\u5973\u513f\u6307\u6325\uff0c\u4f46\u5fc3\u91cc\u5374\u672a\u5fc5\u80fd\u771f\u7684\u63a5\u53d7\uff0c\u4e00\u65e6\u4ed6\u81ea\u884c\u5176\u662f\uff0c\u7f6e\u4ee4\u6e1d\u4e8e\u4e0d\u987e\uff0c\u6211\u600e\u4e48\u529e\uff1f \u3000\u3000\u81f3\u4e8e\u6c5f\u5927\u54e5\uff0c\u90a3\u5c31\u66f4\u8981\u547d\u4e86\uff0c\u4ed6\u662f\u6218\u9635\u4e3b\u529b\uff0c\u62fc\u6740\u5f3a\u654c\uff0c\u51b2\u950b\u9677\u9635\uff0c\u5168\u8981\u4ed6\u594b\u4e0d\u987e\u8eab\uff0c\u54ea\u91cc\u5371\u9669\u90a3\u91cc\u53bb\uff0c\u6211\u4f1a\u5fc3\u75bc\uff0c\u6015\u4e0d\u4e0b\u4e86\u8fd9\u4e2a\u72e0\u5fc3\uff0c\u4ed6\u662f\u6211\u4eec\u7684\u672a\u5a5a\u4e08\u592b\u554a\uff01 \u3000\u3000\u4f55\u51b5\uff0c\u4e00\u65e6\u6709\u6240\u95ea\u5931\uff0c\u7b2c\u4e00\u4e2a\u971c\u59b9\u5c31\u70e7\u4e0d\u8fc7\u6211\uff0c\u4f60\u548c\u6885\u82b1\u59d0\u4e5f\u4e0d\u53ef\u80fd\u539f\u8c05\u6211\uff0c\u518d\u52a0\u4e2a\u84dd\u51e4\u6765\u4e00\u8d77\u6068\u6211\uff1f \u3000\u3000\u4e0d\u80fd\u7f6e\u60c5\u90ce\u4e8e\u6b7b\u5730\u800c\u540e\u751f\uff0c\u8fd9\u4e2a\u4ed7\u5c31\u65e0\u6cd5\u6253\u4e86\uff0c\u4f60\u8bf4\u8bf4\u770b\uff0c\u9752\u51e4\u59d0\uff0c\u6211\u80fd\u4e0d\u6015\u5417\uff1f\u201d \u3000\u3000\u201c\u8bf4\u7684\u4e5f\u662f\u554a\uff01\u5e72\u7239\u4e49\u4fa0\u809d\u80c6\uff0c\u8981\u4ed6\u65bd\u5c55\u8be1\u8bc8\uff0c\u5f88\u53ef\u80fd\u4e0d\u5c51\u4e3a\u4e4b\uff0c\u2026\u2026\u201d\u9752\u51e4\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u6c5f\u90ce\u591a\u60c5\uff0c\u7edd\u4e0d\u4f1a\u8fdd\u6297\u4f60\u7684\u4fe1\u8c15\uff0c\u6700\u91cd\u8981\u7684\u662f\uff0c\u4f60\u4e0d\u5fcd\u8ba9\u4ed6\u591a\u5192\u950b\u955d\u4e4b\u9669\uff0c\u5fc3\u4e00\u8f6f\uff0c\u5c31\u4e0d\u662f\u5c06\u5e05\u4e4b\u624d\u4e86\uff0c\u53ef\u662f\u5f20\u59e8\u80fd\u4e0b\u8fd9\u4e2a\u51b3\u5fc3\u5417\uff1f\u201d \u3000\u3000\u201c\u5f20\u59e8\u80fd\uff0c\u5f20\u59e8\u5bf9\u6c5f\u90ce\u4e4b\u60c5\uff0c\u7edd\u4e0d\u5728\u6211\u4eec\u4e4b\u4e0b\uff0c\u4f46\u5979\u5386\u7ecf\u4e86\u6c5f\u6e56\u5927\u53d8\uff0c\u53c8\u53d7\u4e86\u6b66\u6797\u4e09\u5723\u5631\u6258\u2026\u2026\u201d\u541f\u96ea\u9053\uff1a\u201c\u5979\u5bf9\u6b66\u6797\u4e09\u5723\u7684\u8d23\u4efb\u611f\uff0c\u65e9\u5df2\u8d85\u8d8a\u4e86\u5979\u7684\u751f\u6b7b\uff0c\u5f04\u6b7b\u4e86\u6c5f\u90ce\uff0c\u5979\u53ef\u80fd\u4ee5\u8eab\u76f8\u6b89\uff0c\u4f46\u6218\u9635\u4e4b\u4e0a\uff0c\u7edd\u4e0d\u4f1a\u5fc3\u8f6f\uff0c\u6211\u4eec\u662f\u65e0\u6cd5\u548c\u5f20\u59e8\u6bd4\u7684\uff0c\u96be\u9053\u4f60\u6ca1\u6709\u53d1\u89c9\u5417\uff1f \u3000\u3000\u5927\u5bb6\u90fd\u559c\u6b22\u5f20\u59e8\uff0c\u4e5f\u90fd\u5f88\u6015\u5f20\u59e8\uff0c\u5305\u62ec\u6211\u7239\u5728\u5185\uff0c\u7edd\u4e0d\u6562\u6298\u6263\u5f20\u59e8\u7684\u547d\u4ee4\uff0c\u4e0d\u7ba1\u4ed6\u5185\u5fc3\u4e2d\u591a\u4e48\u53cd\u5bf9\uff0c\u4e5f\u4e0d\u4f1a\u6297\u547d\u3002 \u3000\u3000\u5f20\u59e8\u80fd\u70e7\u51fa\u4e16\u4e0a\u6700\u53ef\u53e3\u7684\u4f73\u80b4\uff0c\u4f46\u4e5f\u5efa\u7acb\u8d77\u4e86\u6e29\u67d4\u7684\u6743\u5a01\uff0c\u6211\u7239\u6015\u5979\uff0c\u6211\u5a18\u4e5f\u6709\u70b9\u656c\u6015\u5979\uff0c\u81f3\u4e8e\u6c5f\u67ab\uff0c\u90a3\u5c31\u4e0d\u7528\u63d0\u4e86\uff0c\u5f20\u59e8\u4e00\u58f0\u4ee4\u4e0b\uff0c\u8981\u6c5f\u90ce\u79bb\u5f00\u6211\u4eec\u2026\u2026\u201d \u3000\u3000\u201c\u4f1a\u600e\u6837\u554a\uff01\u201d\u9752\u51e4\u9053\uff1a\u201c\u6211\u548c\u6885\u82b1\uff0c\u4e5f\u5c31\u7b97\u4e86\uff0c\u4f46\u6c5f\u90ce\u600e\u820d\u5f97\u4f60\u548c\u541f\u971c\uff1f\u201d \u3000\u3000\u201c\u653e\u5fc3\u4e86\uff0c\u9752\u51e4\u59d0\uff0c\u5f20\u59e8\u6c38\u8fdc\u4e0d\u4f1a\u8bf4\u51fa\u8fd9\u53e5\u8bdd\uff0c\u5c31\u7b97\u6211\u4eec\u79bb\u5f03\u5979\uff0c\u5979\u4e5f\u4e0d\u4f1a\u2026\u2026\u201d \u3000\u3000\u201c\u90a3\u5c31\u51c6\u5907\u66ff\u6c5f\u90ce\u6536\u5c38\uff0c\u4f5c\u5be1\u5987\u5427\uff01\u201d\u541f\u96ea\u9053\uff1a\u201c\u6c5f\u67ab\u7edd\u5bf9\u4e0d\u4f1a\u8fdd\u6297\u5f20\u59e8\u4e4b\u547d\uff0c\u4f46\u4e5f\u4e0d\u5fcd\u5fc3\u79bb\u5f00\u6211\u4eec\uff0c\u5c31\u53ea\u6709\u6b7b\u8def\u4e00\u6761\u4e86\uff0c\u4f46\u5f20\u59e8\u662f\u4ee5\u5927\u7231\u548c\u667a\u6167\u670d\u4eba\uff0c\u6211\u4eec\u4e5f\u90fd\u4e0d\u613f\u79bb\u5f00\u5979\u3002\u201d \u3000\u3000\u201c\u5bf9\uff01\u541f\u96ea\uff0c\u4f60\u8981\u60f3\u51fa\u4e2a\u6cd5\u5b50\u7559\u4f4f\u5f20\u59e8\uff0c\u522b\u8ba9\u5979\u529f\u6210\u8fdc\u9041\u2026\u2026\u201d \u3000\u3000\u9752\u51e4\u9053\uff1a\u201c\u542c\u4f60\u4e00\u5e2d\u8bdd\uff0c\u6211\u771f\u662f\u4f69\u773c\u4f60\u4e86\uff0c\u7b80\u76f4\u662f\u4e94\u4f53\u6295\u5730\uff0c\u4f60\u6b66\u80fd\u7528\u5175\uff0c\u6587\u80fd\u541f\u8bd7\uff0c\u60f3\u4e0d\u5230\u5bf9\u4eba\u60c5\u4e16\u6545\uff0c\u7adf\u4e5f\u662f\u5982\u6b64\u7684\u6d1e\u8fbe\u900f\u6f88\uff0c\u4e86\u89e3\u4f60\u5468\u56f4\u6240\u6709\u7684\u4eba\uff0c\u4f60\u7684\u5fc3\u7a76\u7adf\u662f\u4ec0\u4e48\u505a\u7684\u554a\uff1f\u80fd\u5982\u6b64\u7684\u73b2\u73d1\u8fc7\u4eba\u3002\u201d \u3000\u3000\u201c\u9752\u51e4\u59d0\uff0c\u522b\u704c\u6211\u8ff7\u6c64\u4e86\uff0c\u201d\u541f\u96ea\u7b11\u9053\uff1a\u201c\u7531\u5f20\u59e8\u6307\u6325\u5927\u5c40\uff0c\u6211\u8fd8\u6709\u4e00\u70b9\u79c1\u5fc3\uff0c\u662f\u4e3a\u6211\uff0c\u4e5f\u4e3a\u4f60\u4eec\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u8981\u8bf4\uff0c\u8ba9\u6211\u731c\u731c\u770b\uff1f\u2026\u2026\u201d\u9752\u51e4\u9053\uff1a\u201c\u4f60\u4e0d\u7528\u5750\u9547\u4e2d\u67a2\uff0c\u4e5f\u597d\u548c\u6c5f\u90ce\u5e76\u80a9\u6297\u654c\uff0c\u5bf9\u5417\uff1f\u201d \u3000\u3000\u201c\u4f60\u4e5f\u4e0d\u7b28\u554a\uff01\u9752\u51e4\u59d0\uff0c\u731c\u5bf9\u4e86\uff0c\u6211\u6b66\u529f\u867d\u4e0d\u5982\u4ed6\uff0c\u4f46\u5343\u9762\u5973\u9b45\u65b9\u5999\u7684\u5bd2\u94c1\u593a\u9b42\u9488\uff0c\u5374\u662f\u65e0\u575a\u4e0d\u6467\u7684\u5229\u5668\u2026\u2026\u201d\u541f\u96ea\u7b11\u9053\uff1a\u201c\u5f20\u59e8\u9ad8\u77bb\u8fdc\u77a9\uff0c\u7559\u4e0b\u4e86\u8fd9\u7edd\u6bd2\u7684\u6697\u5668\uff0c\u7adf\u6210\u4e86\u9664\u9b54\u536b\u9053\u7684\u6cd5\u5b9d\uff0c\u6211\u8ddf\u5728\u4ed6\u8eab\u8fb9\uff0c\u529f\u7531\u4ed6\u4eab\uff0c\u6b7b\u7531\u6211\u5f53\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u8981\uff01\u201d\u9752\u51e4\u6025\u6025\u53eb\u9053\uff1a\u201c\u6211\u7231\u6c5f\u90ce\uff0c\u4e5f\u4e00\u6837\u7231\u4f60\uff0c\u4f60\u4eec\u8c01\u4e5f\u4e0d\u80fd\u6b7b\u554a\uff01\u201d \u3000\u3000\u201c\u4e0d\u4f1a\u5440\uff01\u6253\u4e2a\u6bd4\u55bb\u7f62\u4e86\uff0c\u4f60\u60f3\u60f3\u770b\uff1f\u6211\u5728\u4ed6\u8eab\u8fb9\uff0c\u4ed6\u600e\u4e48\u4f1a\u770b\u7740\u6211\u6b7b\u2026\u2026\u201d\u53f6\u96ea\u9053\uff1a \u3000\u3000\u201c\u4e3a\u4e86\u6551\u6211\uff0c\u4ed6\u624d\u80fd\u53d1\u6325\u51fa\u4f53\u80fd\u6f5c\u529b\uff0c\u4f5c\u51fa\u77f3\u7834\u5929\u60ca\u7684\u4e00\u51fb\u2026\u2026\u201d \u3000\u3000\u201c\u541f\u96ea\uff0c\u8fd9\u4e2a\u60f3\u6cd5\u5c31\u4e0d\u597d\u2026\u2026\u201d\u9752\u51e4\u9053\uff1a\u201c\u4e0d\u8981\u53bb\u5192\u8fd9\u4e2a\u9669\u3002\u201d \u3000\u3000\u201c\u5509\uff01\u4f60\u8fd8\u4e0d\u662f\u771f\u7684\u4e86\u89e3\u6c5f\u5927\u54e5\u2026\u2026\u201d\u541f\u96ea\u9053\uff1a\u201c\u4e0d\u80fd\u602a\u4f60\u7684\uff0c\u9752\u51e4\u59d0\uff0c\u8fd9\u4ef6\u4e8b\u53ea\u6015\u541f\u971c\u548c\u5f20\u59e8\u90fd\u4e0d\u6e05\u695a\uff0c\u6211\u6697\u4e2d\u7559\u610f\u4ed6\uff0c\u770b\u4ed6\u4e60\u7ec3\u4e0a\u4e58\u7684\u5fa1\u5251\u672f\uff0c\u603b\u662f\u534a\u9014\u505c\u987f\uff0c\u4e0d\u80fd\u4e00\u6c14\u5475\u6210\u2026\u2026\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u5462\uff1f\u201d\u9752\u51e4\u5947\u9053\uff1a\u201c\u662f\u4e0d\u662f\u529f\u529b\u4e0d\u591f\uff1f\u201d \u3000\u3000\u201c\u4e0d\u662f\u529f\u529b\u4e0d\u591f\uff01\u800c\u662f\u4fe1\u5fc3\u4e0d\u8db3\u2026\u2026\u201d\u53f6\u96ea\u9053\uff1a\u201c\u4ed6\u4e0d\u76f8\u4fe1\u81ea\u5df1\u8fd9\u70b9\u5e74\u7eaa\uff0c\u80fd\u5165\u5251\u672f\u5316\u5883\uff0c\u6c5f\u5927\u54e5\u7ec3\u7fe0\u7389\u5200\uff0c\u6ca1\u6709\u6df1\u5165\u5251\u9053\u4e4b\u5883\uff0c\u5206\u5fc3\u4e8e\u4e09\u5723\u6b66\u529f\uff0c\u81ea\u89c9\u5251\u9053\u6839\u57fa\u4e0d\u56fa\u3002 \u3000\u3000\u73b0\u5728\uff0c\u4ed6\u80f8\u6709\u5fa1\u5251\u4e4b\u6cd5\uff0c\u529f\u529b\u4e5f\u5165\u4e0a\u4e58\uff0c\u5c31\u662f\u81ea\u5df1\u4e0d\u4fe1\u81ea\u5df1\uff0c\u5176\u5b9e\u7167\u6211\u89c2\u5bdf\uff0c\u4ed6\u5df2\u8eab\u5165\u5fa1\u5251\u4e4b\u5883\uff0c\u53ea\u53ef\u60dc\u6f5c\u529b\u4e0d\u80fd\u5b8c\u5168\u53d1\u6325\u3002 \u3000\u3000\u6211\u542c\u8fc7\u4ed6\u63b7\u5251\u957f\u53f9\uff0c\u81ea\u8a00\u81ea\u8bed\u8bf4\uff0c\u82cd\u5929\u554a\uff01\u4e3a\u4ec0\u4e48\u4e0d\u80fd\u518d\u591a\u7ed9\u6211\u4e00\u5e74\u65f6\u95f4\u3002\u8fd9\u5c31\u5fc5\u9700\u8981\u4e00\u4e2a\u6781\u5927\u7684\u8bf1\u56e0\uff0c\u624d\u80fd\u4f7f\u4ed6\u5168\u529b\u4e00\u51fb\u2026\u2026\u201d \u3000\u3000\u4e00\u628a\u62b1\u4f4f\u4e86\u53f6\u96ea\uff0c\u9752\u51e4\u6025\u6025\u8bf4\u9053\uff1a\u201c\u8fd9\u600e\u4e48\u884c\uff1f\u541f\u96ea\u4e0d\u8981\u5413\u6211\u4e86\uff0c\u4e00\u5b9a\u8981\u8bf1\u56e0\u4e5f\u8be5\u6211\u53bb\u2026\u2026\u201d \u3000\u3000\u201c\u4f60\u4e0d\u884c\u2026\u2026\u201d\u53f6\u96ea\u9053\uff1a\u201c\u9752\u51e4\u59d0\uff0c\u522b\u751f\u6c14\uff0c\u8fde\u541f\u971c\u4e5f\u5dee\u4e00\u622a\uff0c\u4f60\u4eec\u4f5c\u8bf1\u56e0\uff0c\u7edd\u5bf9\u662f\u9001\u6b7b\uff0c\u6211\u5374\u6709\u4e94\u5206\u751f\u673a\uff0c\u4ed7\u7740\u5bd2\u94c1\u593a\u9b42\u9488\u4e4b\u52a9\uff0c\u6211\u8fd8\u53ef\u4ee5\u589e\u591a\u4e00\u5206\u4fdd\u547d\u673a\u4f1a\uff0c\u516d\u5bf9\u56db\u554a\uff01\u662f\u4e0d\u662f\u673a\u4f1a\u5f88\u5927\u5462\uff1f \u3000\u3000\u522b\u8ddf\u6211\u4e89\u4e86\uff0c\u4e5f\u522b\u628a\u8fd9\u4ef6\u4e8b\u8bf4\u51fa\u53bb\uff0c\u6c5f\u5927\u54e5\u77e5\u9053\u4e86\uff0c\u90a3\u5c31\u4f1a\u5f04\u7838\u4e86\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u5df2\u6709\u51c6\u5907\uff0c\u80af\u5b9a\u4e0d\u80fd\u65bd\u5c55\u5fa1\u5251\u672f\uff0c\u4e5f\u4e0d\u80fd\u8ba9\u5f20\u59e8\u548c\u53f6\u971c\u77e5\u9053\uff0c\u541f\u971c\u5982\u679c\u6697\u4e2d\u51c6\u5907\u548c\u6211\u62a2\u6b7b\uff0c\u90a3\u5c31\u5bb3\u4e86\u5979\uff0c\u4e5f\u5bb3\u4e86\u6211\uff0c\u4e5f\u4f1a\u5bb3\u4e86\u6c5f\u90ce\u3002\u201d \u3000\u3000\u9752\u51e4\u542b\u6cea\u70b9\u5934\uff0c\u56de\u987e\u8fce\u6625\u9053\uff1a\u201c\u4f60\u90fd\u542c\u5230\u4e86\uff0c\u4e0d\u80fd\u8bf4\u51fa\u53bb\u3002\u201d \u3000\u3000\u201c\u5c0f\u5a62\u4ec0\u4e48\u90fd\u6ca1\u6709\u542c\u5230\uff0c\u53ea\u8bb0\u7740\u4e8c\u5c0f\u59d0\u6551\u4e86\u5c0f\u5a62\u4e00\u6761\u547d\uff0c\u5c0f\u5a62\u6c38\u94ed\u5728\u5fc3\uff0c\u56e0\u4e3a\uff0c\u6211\u4e00\u76f4\u5728\u95e8\u53e3\u7ad9\u7740\u3002\u201d \u3000\u3000\u8bf4\u5b8c\u8bdd\uff0c\u8d70\u51fa\u5ba2\u5ba4\uff0c\u7ad9\u5728\u95e8\u53e3\u3002 \u3000\u3000\u541f\u96ea\u7b11\u9053\uff1a\u201c\u5f88\u53ef\u4eba\u7684\u5c0f\u4e2b\u5934\uff0c\u53ea\u53ef\u60dc\u592a\u665a\u4e86\u2026\u2026\u4e00\u4e9b\u2026\u2026\u201d \u3000\u3000\u201c\u53c8\u662f\u4ec0\u4e48\u4e8b\u554a\uff1f\u4f60\u771f\u662f\u8bed\u4e0d\u60ca\u4eba\uff0c\u6b7b\u4e0d\u4f11\u554a\uff1f\u201d \u3000\u3000\u9752\u98ce\u62ed\u53bb\u773c\u6cea\uff0c\u671b\u7740\u541f\u96ea\u3002 \u3000\u3000\u201c\u6ca1\u6709\u66ff\u6c5f\u90ce\u7559\u4e0b\u8840\u8109\u2026\u2026\u201d\u541f\u96ea\u9053\uff1a\u201c\u6211\u4eec\u8981\u6253\u62fc\uff0c\u4e0d\u80fd\u6000\u5b55\uff0c\u8001\u5b9e\u8bf4\uff0c\u8fd9\u4e00\u6218\u51f6\u9669\u7684\u5f88\uff0c\u4ed6\u4eec\u5802\u5802\u6b63\u6b63\u7684\u6765\u6311\u6218\uff0c\u662f\u5df2\u7b97\u5b9a\u4e86\u5403\u4f4f\u6211\u4eec\u3002 \u3000\u3000\u6211\u4eec\u867d\u5e94\u53d8\u6709\u7b56\uff0c\u5b9e\u529b\u4e5f\u4f1a\u51fa\u4e8e\u4ed6\u4eec\u7684\u610f\u6599\u4e4b\u5916\uff0c\u4f46\u4e0d\u80fd\u4fdd\u8bc1\u4f1a\u80dc\uff0c\u4e5f\u8bb8\u662f\u4e2a\u540c\u5f52\u4e8e\u5c3d\u4e4b\u5c40\uff0c\u5982\u80fd\u8ba9\u6c5f\u90ce\u7559\u4e2a\u513f\u5973\uff0c\u90a3\u5c31\u6b7b\u65e0\u61be\u4e8b\u4e86\uff01\u201d \u3000\u3000\u201c\u73b0\u5728\u5462\uff1f\u8fd8\u6709\u4e00\u591c\u65f6\u95f4\uff0c\u53cd\u6b63\u8fce\u6625\u4e5f\u5e2e\u4e0d\u4e0a\u5927\u5fd9\uff0c\u2026\u2026\u201d\u9752\u51e4\u9053\uff1a\u201c\u6211\u53eb\u5979\u8fdb\u6765\u5546\u91cf\u3002\u201d \u3000\u3000\u201c\u9752\u51e4\u59d0\uff0c\u6ca1\u6709\u7528\u7684\uff0c\u5c31\u7b97\u8fce\u6625\u7b54\u5e94\u4e86\uff0c\u6c5f\u90ce\u4e5f\u4e0d\u4f1a\u7b54\u5e94\uff0c\u2026\u2026\u201d \u3000\u3000\u541f\u96ea\u9053\uff1a\u201c\u8fd9\u79cd\u4e8b\uff0c\u8981\u65e9\u4f5c\u8bbe\u8ba1\uff0c\u8ba9\u4ed6\u4e0d\u77e5\u4e0d\u89c9\u4e2d\u6b65\u5165\u5708\u5957\u624d\u884c\uff0c\u6211\u4ece\u6ca1\u6709\u8fd9\u79cd\u7ecf\u9a8c\uff0c\u4eca\u5929\u624d\u60f3\u5230\u8fd9\u6863\u5b50\u4e8b\u3002\u201d \u3000\u3000\u9752\u98ce\u7a81\u7136\u55e4\u7684\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u771f\u7684\u662f\u6765\u4e0d\u53ca\u4e86\uff0c\u8fd9\u79cd\u4e8b\uff0c\u6ca1\u6709\u4e09\u4e24\u4e2a\u6708\uff0c\u53ef\u80fd\u65e0\u6cd5\u6210\u529f\uff0c\u5012\u4e0d\u5982\u4f60\u548c\u541f\u971c\u8bd5\u8bd5\u5427\uff01\u660e\u5929\u5982\u662f\u4e0d\u5e78\u6218\u6b7b\uff0c\u4f60\u4eec\u4e24\u4e2a\u5c0f\u7f8e\u4eba\uff0c\u4eba\u4e8b\u672a\u7ecf\uff0c\u53ef\u662f\u6789\u5230\u4eba\u95f4\u8d70\u4e00\u906d\u554a\uff01\u201d \u3000\u3000\u201c\u4e5f\u4e0d\u884c\uff0c\u7239\u548c\u5a18\u656c\u91cd\u6c5f\u5927\u54e5\u7684\u5c31\u662f\u8fd9\u4e00\u70b9\u4e86\u2026\u2026\u201d\u541f\u96ea\u9053\uff1a\u201c\u4ed6\u5982\u771f\u60f3\u6536\u62fe\u6211\u4eec\uff0c\u7528\u4e0d\u7740\u8d39\u529b\u7684\uff0c\u6211\u548c\u59b9\u90fd\u4e0d\u4f1a\u6297\u62d2\uff0c\u4f46\u4ed6\u6ca1\u6709\uff0c\u597d\u96be\u554a\uff01\u7389\u4eba\u5728\u62b1\uff0c\u98df\u6307\u4e0d\u52a8\uff0c\u67f3\u4e0b\u60e0\u4e0d\u8fc7\u662f\u5750\u6000\u4e0d\u4e71\u5427\uff01 \u3000\u3000\u4ed6\u53ef\u662f\u4e00\u767e\u591a\u5929\u5bf9\u7740\u6211\u4eec\uff0c\u80fd\u591f\u5fcd\u8010\u4e0d\u52a8\uff0c\u4f55\u51b5\uff0c\u6211\u4eec\u53c8\u5f88\u7231\u9017\u4ed6\uff0c\u5c24\u5176\u662f\u59b9\u59b9\uff0c\u95f9\u8d77\u6765\u5929\u7ffb\u5730\u8986\uff0c\u6211\u770b\u5f97\u51fa\u4ed6\u5f88\u82e6\uff0c\u4f60\u548c\u6885\u82b1\u53c8\u4e0d\u5728\u3002\u5929\u554a\uff01\u4ed6\u600e\u4e48\u5fcd\u8fc7\u53bb\u7684\uff0c\u8fd9\u4e2a\u4eba\u771f\u7684\u8fd1\u4e4e\u795e\u4e86\u3002\u201d \u3000\u3000\u9752\u51e4\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u771f\u7684\u662f\u53eb\u4eba\u4f69\u670d\uff0c\u4ed6\u53ef\u662f\u8c03\u60c5\u9ad8\u624b\uff0c\u53ea\u8981\u7a0d\u52a8\u624b\u811a\uff0c\u5c31\u4f1a\u8ba9\u4f60\u4eec\u81ea\u52a8\u7684\u5bbd\u8863\u89e3\u5e26\uff0c\u4ed6\u4e0d\u52a8\u4f60\u4eec\uff0c\u662f\u771f\u7684\u5b58\u5fc3\u4e0d\u52a8\uff0c\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\uff0c\u4ed6\u662f\u771f\u7684\u75bc\u7231\u6211\u4eec\uff0c\u4fdd\u7559\u4e86\u6211\u4eec\u7684\u5b8c\u6574\uff0c\u8ba9\u7239\u5a18\u656c\u4f69\uff0c\u8ba9\u5f20\u59e8\u5403\u60ca\uff0c\u2026\u2026\u201d \u3000\u3000\u541f\u96ea\u9053\uff1a\u201c\u4f46\u4e5f\u8d62\u5f97\u4e86\u6211\u548c\u59b9\u5168\u90e8\u7684\u771f\u60c5\uff0c\u65e0\u4fdd\u7559\u7684\u5949\u732e\u3002\u201d \u3000\u3000\u201c\u4e86\u89e3\u4e00\u4e2a\u4eba\u53ef\u771f\u4e0d\u5bb9\u6613\uff01\u201d\u9752\u51e4\u9053\uff1a\u201c\u4f60\u4e0d\u77e5\u9053\uff0c\u6c5f\u90ce\u5728\u52a8\u6211\u8111\u7b4b\u65f6\u90a3\u79cd\u98ce\u6d41\u501c\u50a5\uff0c\u771f\u662f\u52fe\u9b42\u593a\u9b44\uff0c\u9189\u4eba\u5982\u9152\uff0c\u84dd\u98ce\u6700\u8ba8\u538c\u7537\u4eba\uff0c\u80fd\u62d2\u6297\u5929\u738b\u8fd1\u8eab\uff0c\u4f46\u4e5f\u6297\u4e0d\u4f4f\u6c5f\u90ce\u7684\u8bf1\u60d1\uff0c\u5012\u662f\u4f60\uff0c\u82b1\u5f84\u672a\u66fe\u7f18\u5ba2\u626b\uff0c\u84ec\u95e8\u4e5f\u672a\u4e3a\u541b\u5f00\uff0c\u600e\u4e48\u4f1a\u77e5\u9053\u8fd9\u4e48\u591a\uff01\u201d \u3000\u3000\u541f\u96ea\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u9752\u51e4\u59d0\uff0c\u6211\u5df2\u662f\u6000\u6625\u7684\u5c11\u5973\u4e86\uff0c\u7ec3\u7684\u53c8\u662f\u5929\u72d0\u6b66\u529f\uff0c\u90a3\u662f\u81f3\u5a07\u81f3\u5a9a\u7684\u6280\u827a\u554a\uff01 \u3000\u3000\u8981\u8bb2\u6c42\u4e00\u7b11\u52fe\u4eba\u9b42\uff0c\u518d\u7b11\u53d6\u4eba\u547d\uff0c\u4e0a\u4e58\u5185\u529f\u901a\u533b\u9053\uff0c\u4ec0\u4e48\u6211\u4e0d\u77e5\u9053\uff0c\u53ea\u662f\u4f60\u7684\u541f\u96ea\u59b9\u5b50\u6211\uff0c\u751f\u5177\u7d20\u5fc3\uff0c\u4e0d\u5c1a\u98ce\u6d41\uff0c\u624d\u4fdd\u6709\u4e86\u5904\u5b50\u4e4b\u8eab\u3002 \u3000\u3000\u53ef\u60dc\u7684\u662f\uff0c\u4e00\u51fa\u9053\u5c31\u9047\u4e0a\u6c5f\u90ce\uff0c\u8fd9\u4e00\u8eab\u9b41\u60d1\u4f17\u751f\u7684\u5929\u72d0\u6280\u827a\uff0c\u65e0\u6cd5\u5728\u6c5f\u6e56\u4e0a\u5c55\u73b0\u4e86\u3002\u201d \u3000\u3000\u201c\u8bf4\u7684\u4e5f\u662f\u554a\uff01\u6211\u662f\u5973\u4eba\uff0c\u800c\u4e14\u4e5f\u7b97\u662f\u76f8\u5f53\u6f02\u4eae\u51fa\u8272\u7684\u5973\u4eba\uff0c\u6709\u65f6\u5019\u770b\u4f60\u7b11\u7684\u52a8\u4eba\uff0c\u6068\u4e0d\u5f97\u8eab\u5316\u7537\u513f\uff0c\u62b1\u4f60\u5165\u6000\u2026\u2026\u201d \u3000\u3000\u201c\u90a3\u53ef\u4e0d\u884c\uff0c\u4f60\u662f\u59d0\u59d0\uff0c\u8fd8\u53ef\u4ee5\u4eb2\u8fd1\u4e00\u4e0b\uff0c\u5316\u4f5c\u7537\u513f\u2026\u2026\u201d\u541f\u96ea\u9053\uff1a\u201c\u78b0\u4e00\u4e0b\u5c31\u8840\u6d41\u4e94\u6b65\uff0c\u5c0f\u59b9\u53ef\u5df2\u662f\u540d\u82b1\u6709\u4e3b\uff0c\u6211\u8d70\u4e86\uff0c\u8bb0\u7740\u6211\u7684\u8bdd\uff0c\u522b\u5728\u541f\u9732\u9762\u524d\u9732\u4e86\u53e3\u98ce\u3002\u201d \u3000\u3000\u4e00\u8f6c\u8eab\uff0c\u5ffd\u7136\u6d88\u5931\uff0c\u597d\u5feb\u7684\u8eab\u6cd5\uff0c\u662f\u5b58\u5fc3\u9732\u4e00\u624b\uff0c\u8ba9\u9752\u51e4\u5fc3\u4e2d\u6709\u6570\uff0c\u6253\u6d88\u53bb\u6025\u6b7b\u4e4b\u5ff5\u3002 \u3000\u3000\u671b\u7740\u541f\u96ea\u901d\u53bb\u7684\u80cc\u5f71\uff0c\u9752\u51e4\u611f\u6168\u3001\u656c\u4f69\uff0c\u4ea4\u96c6\u5fc3\u5934\uff0c\u8fd9\u4e2a\u5c0f\u7f8e\u4eba\u554a\uff01\u4eba\u4e4e\uff1f\u4ed9\u4e4e\uff1f \u3000\u3000\u767e\u660e\u8001\u4e08\u7ec8\u4e8e\u6e21\u8fc7\u4e86\u9664\u6bd2\u7684\u8fc7\u7a0b\uff0c\u672c\u5df2\u6e05\u7626\u7684\u8001\u548c\u5c1a\uff0c\u4f3c\u4e4e\u66f4\u7626\u4e86\u4e00\u4e9b\uff0c\u51e0\u4e2a\u65f6\u8fb0\u7684\u918b\u70ed\u84b8\u817e\uff0c\u51e0\u4e4e\u662f\u6ee4\u5e72\u4e86\u8001\u548c\u5c1a\u8eab\u4f53\u5185\u7684\u6c34\u5206\u3002 \u3000\u3000\u62ac\u51fa\u84b8\u7b3c\uff0c\u8001\u548c\u5c1a\u5168\u8eab\u8d64\u7ea2\uff0c\u6709\u5982\u84b8\u719f\u7684\u867e\u5b50\u3002 \u3000\u3000\u4f46\u4ed6\u8fd8\u6709\u6c14\u606f\uff0c\u800c\u4e14\uff0c\u6c14\u606f\u4e0d\u5f31\uff0c\u6570\u5341\u5e74\u4fee\u7ec3\u7684\u7cbe\u6df1\u5185\u529f\uff0c\u5df2\u5177\u6709\u4e86\u8d85\u5f02\u5e38\u4eba\u7684\u4f53\u80fd\u3002 \u3000\u3000\u5f20\u56db\u59d1\u53d6\u4e0b\u8001\u65b9\u4e08\u8eab\u4e0a\u4e09\u5341\u516d\u679a\u91d1\u9488\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u52a9\u4ed6\u8212\u901a\u7ecf\u7a74\uff0c\u770b\u6837\u5b50\u4f53\u5185\u5404\u79cd\u5947\u6bd2\uff0c\u5df2\u88ab\u903c\u51fa\uff0c\u73b0\u5728\uff0c\u4ed6\u592a\u865a\u5f31\uff0c\u6050\u6015\u6ca1\u6cd5\u5b50\u81ea\u884c\u8fd0\u6c14\u8c03\u606f\u3002\u6211\u53bb\u770b\u770b\u541f\u96ea\u3002\u201d \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u4f4e\u58f0\u9053\uff1a\u201c\u5f20\u59d1\u5a18\uff0c\u8981\u4e0d\u8981\u7559\u4e2a\u4eba\u2026\u2026\u201d \u3000\u3000\u201c\u8981\uff01\u6885\u82b1\u7559\u4e0b\u6765\uff0c\u6211\u4eec\u53bb\u548c\u541f\u96ea\u7814\u5546\u5bf9\u654c\u4e4b\u7b56\u2026\u2026\u201d\u5f20\u56db\u59d1\u7b11\u9053\uff1a\u201c\u6c5f\u67ab\uff0c\u8001\u65b9\u4e08\u8c03\u606f\u8fc7\u6765\uff0c\u76f8\u4fe1\u4f1a\u6709\u6307\u793a\uff0c\u4f60\u8981\u8010\u5fc3\u7b49\u5019\u3002\u201d \u3000\u3000\u6c5f\u67ab\u70b9\u70b9\u5934\uff0c\u76d8\u819d\u5750\u4e0b\uff0c\u53f3\u638c\u62b5\u5728\u767e\u660e\u5927\u5e08\u7684\u80cc\u5fc3\u4e4b\u4e0a\uff0c\u5185\u529b\u6e90\u6e90\u800c\u51fa\uff0c\u5148\u7531\u5f31\u59cb\uff0c\u9010\u6e10\u589e\u5f3a\u3002 \u3000\u3000\u6885\u82b1\u6084\u7136\u79fb\u6b65\uff0c\u884c\u5230\u4e86\u6c5f\u67ab\u8eab\u4fa7\u3002 \u3000\u3000\u76ee\u7779\u8001\u65b9\u4e08\u7684\u6c14\u606f\u6e10\u590d\u6b63\u5e38\uff0c\u5f20\u56db\u59d1\u624d\u4e3e\u6b65\u79bb\u53bb\u3002 \u3000\u3000\u6885\u82b1\u8f6c\u52a8\u4e00\u4e0b\u7075\u6d3b\u7684\u5927\u773c\u775b\uff0c\u6253\u91cf\u4e86\u56db\u5468\u4e00\u773c\uff0c\u53d1\u89c9\u56db\u4e2a\u539f\u672c\u5206\u5b88\u5728\u623f\u5185\u5c4b\u89d2\u7684\u548c\u5c1a\uff0c\u7adf\u968f\u7740\u5f20\u56db\u59d1\u7684\u8eab\u540e\u884c\u52a8\uff0c\u4e24\u4e2a\u5728\u95e8\u53e3\u505c\u4e0b\u5b88\u62a4\uff0c\u4e24\u4e2a\u51fa\u5ba4\u800c\u53bb\u3002 \u3000\u3000\u539f\u6765\uff0c\u56db\u4e2a\u767e\u5b57\u8f88\u7684\u9ad8\u618e\uff0c\u65e2\u8d1f\u6709\u4fdd\u62a4\u65b9\u4e08\u5e08\u5144\u7684\u8d23\u4efb\uff0c\u4e5f\u6709\u7740\u6697\u4e2d\u76d1\u89c6\u5f20\u56db\u59d1\u7684\u7528\u5fc3\u3002 \u3000\u3000\u773c\u770b\u65b9\u4e08\u6e21\u8fc7\u52ab\u96be\uff0c\u5df2\u786e\u5b9a\u522b\u4eba\u662f\u771f\u5fc3\u5e2e\u5fd9\uff0c\u6697\u53eb\u4e86\u4e00\u58f0\u60ed\u6127\uff0c\u79bb\u5f00\u53a8\u623f\u3002 \u3000\u3000\u6885\u82b1\u6ca1\u963b\u6b62\uff0c\u4e5f\u672a\u591a\u95ee\u3002 \u3000\u3000\u5979\u660e\u767d\u53cc\u65b9\u81ea\u4e0b\u7684\u5173\u7cfb\u5341\u5206\u5fae\u5999\uff0c\u65e2\u662f\u5b58\u4ea1\u76f8\u4f9d\u4e4b\u5c40\uff0c\u53c8\u672a\u5efa\u7acb\u8d77\u5b8c\u5168\u4e92\u4fe1\u7684\u53cb\u8c0a\u3002 \u3000\u3000\u6c5f\u67ab\u529f\u529b\u6df1\u539a\uff0c\u53c8\u660e\u767d\u7ecf\u8109\u6c14\u8840\u8fd0\u884c\u4e4b\u7406\uff0c\u5185\u529b\u8fd0\u9001\uff0c\u6070\u5230\u597d\u5904\uff0c\u6548\u679c\u975e\u5e38\u663e\u6b8a\uff0c\u4e00\u523b\u529f\u592b\u4e4b\u540e\uff0c\u8001\u65b9\u4e08\u8138\u4e0a\u5df2\u89c1\u8840\u8272\uff0c\u5df2\u53ef\u914d\u5408\u6c5f\u67ab\u8fd0\u884c\u771f\u6c14\u3002 \u3000\u3000\u8bb2\u8fc7\u987f\u996d\u5de5\u592b\uff0c\u767e\u660e\u65b9\u4e08\u5ffd\u7136\u5f00\u53e3\u8bf4\u8bdd\uff0c\u9053\uff1a\u201c\u884c\u4e86\uff0c\u8001\u8872\u5df2\u7ecf\u53ef\u4ee5\u81ea\u884c\u8fd0\u6c14\u8c03\u606f\u3002\u201d \u3000\u3000\u201c\u8001\u65b9\u4e08\u5185\u529f\u7cbe\u7eaf\uff0c\u6c5f\u67d0\u597d\u751f\u4f69\u670d\u3002\u201d\u7ad9\u8d77\u8eab\u5b50\uff0c\u63a5\u9053\uff1a\u201c\u8bf7\u81ea\u4f5c\u8c03\u606f\uff0c\u6211\u518d\u52a9\u5927\u5e08\u4e00\u81c2\u4e4b\u529b\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\uff01\u5927\u6218\u4e00\u89e6\u5373\u53d1\uff0c\u6c5f\u65bd\u4e3b\u4e0d\u5b9c\u518d\u8017\u6d88\u4f53\u80fd\uff0c\u2026\u2026\u201d\u8001\u65b9\u4e08\u9053\uff1a\u201c\u85cf\u7ecf\u9601\u5343\u624b\u89c2\u97f3\u4f5b\u50cf\u7684\u83b2\u82b1\u5750\u4e0b\uff0c\u9690\u85cf\u4e86\u6625\u79cb\u6b27\u51b6\u5b50\u4e3a\u8d8a\u738b\u94f8\u9020\u7684\u4e94\u5927\u540d\u5251\u4e4b\u4e00\uff0c\u5251\u540d\u80dc\u90aa\uff0c\u867d\u7136\u6392\u540d\u7b2c\u4e09\uff0c\u4f46\u4ecd\u5177\u5207\u91d1\u65ad\u7389\u4e4b\u5229\uff0c\u5c31\u8d60\u7ed9\u65bd\u4e3b\u4f5c\u4e3a\u5fa1\u654c\u4e4b\u624b\u4e86\u3002\u201d \u3000\u3000\u6c5f\u67ab\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u8d8a\u738b\u4e94\u5251\uff0c\u6e5b\u5362\u3001\u6e5b\u9619\u3001\u80dc\u90aa\u3001\u9c7c\u80a0\u3001\u7eaf\u94a9\uff0c\u76f8\u4f20\u4e3a\u5343\u53e4\u795e\u5668\uff0c\u6c5f\u67d0\u4f55\u5fb7\u4f55\u80fd\uff0c\u600e\u6562\u636e\u6709\u3002\u201d \u3000\u3000\u201c\u5b9d\u5251\u8d60\u70c8\u58eb\uff0c\u7ea2\u7c89\u9001\u4f73\u4eba\uff0c\u80dc\u90aa\u5b9d\u5251\uff0c\u5df2\u57cb\u85cf\u4e8e\u5c11\u6797\u5bfa\u4e2d\u6570\u767e\u5e74\uff0c\u7b97\u4e0a\u8001\u8872\uff0c\u5df2\u4f20\u5386\u516b\u4ee3\u65b9\u4e08\uff0c\u2026\u2026\u201d\u767e\u660e\u5927\u5e08\u9053\uff1a\u201c\u90fd\u89c9\u5b83\u950b\u5203\u5927\u5229\uff0c\u4e0d\u5b9c\u51fa\u73b0\u6c5f\u6e56\uff0c\u5b9e\u662f\u660e\u73e0\u8499\u5c18\uff0c\u5b9d\u5251\u5165\u9798\uff0c\u518d\u4e0d\u8ba9\u5b83\u7834\u571f\u800c\u51fa\uff0c\u5f88\u53ef\u80fd\u6c38\u65e0\u518d\u73b0\u6c5f\u6e56\u4e4b\u65e5\u4e86\u3002 \u3000\u3000\u5982\u4eca\u6b66\u6797\u5927\u9650\u5173\u5934\uff0c\u6b63\u662f\u51fa\u571f\u65f6\u673a\uff0c\u8001\u8872\u4ee5\u5229\u5668\u76f8\u8d60\uff0c\u52a9\u65bd\u4e3b\u633d\u6551\u6c5f\u6e56\u5927\u52ab\uff0c\u4e5f\u7b97\u662f\u4e3a\u6b66\u6797\u540c\u9053\u5c3d\u4efd\u5fc3\u529b\uff0c\u6c5f\u5c11\u4fa0\u4e0d\u7528\u63a8\u8f9e\u4e86\u3002\u201d \u3000\u3000\u6c5f\u67ab\u5fc3\u4e2d\u5fd6\u601d\uff1a\u201c\u6211\u5fa1\u5251\u6280\u827a\u672a\u6210\uff0c\u867d\u6709\u795e\u5668\u4e3a\u52a9\uff0c\u53ea\u6015\u4e5f\u96be\u53d1\u6325\u51fa\u5947\u6548\u529f\u80fd\uff0c\u4f46\u81f3\u5c11\u53ef\u589e\u5f3a\u6211\u4e00\u500d\u6297\u654c\u7684\u80fd\u529b\uff0c\u4e0d\u5b9c\u63a8\u62d2\u4e86\u3002\u201d \u3000\u3000\u5f53\u4e0b\u62b1\u62f3\u4e00\u63d6\uff0c\u9053\uff1a\u201c\u6211\u8c22\u65b9\u4e08\u8d60\u5251\u6069\u60c5\uff0c\u6c5f\u67d0\u8b66\u5c06\u5168\u529b\u8d74\u654c\uff0c\u4e0d\u9664\u7fa4\u9b54\uff0c\u6218\u6b7b\u65e0\u6094\u3002\u201d \u3000\u3000\u767e\u660e\u65b9\u4e08\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6212\u5f8b\u9662\u5185\u6a2a\u6881\u4e4b\u4e0a\uff0c\u6709\u4e00\u5d4c\u5165\u7684\u7389\u76d2\uff0c\u5185\u6709\u5341\u4e8c\u7c92\u4e38\u9633\u795e\u4e39\uff0c\u867d\u65e0\u9664\u6bd2\u4e4b\u80fd\uff0c\u5374\u662f\u7597\u6cbb\u4f24\u52bf\u7684\u5723\u54c1\uff0c\u8bf7\u8f6c\u4ea4\u5f20\u59d1\u5a18\uff0c\u5168\u6743\u5904\u7f6e\uff0c\u5979\u662f\u533b\u9053\u9ad8\u624b\uff0c\u65bd\u7528\u4e4b\u6cd5\uff0c\u7528\u4e0d\u7740\u8001\u8872\u8bf4\u4e86\u3002\u201d \u3000\u3000\u8a00\u7f62\uff0c\u95ed\u76ee\u8c03\u606f\u3002 \u7b2c\u4e8c\u5341\u4e94\u56de\u3000\u5927\u51b3\u6218\u89e3\u4f53\u5929\u9a6c\u5802 \u3000 \u3000\u3000\u53ef\u771f\u662f\u5927\u8239\u7834\u4e86\u4e09\u5343\u9489\uff0c\u8fd9\u5c11\u6797\u5bfa\u4e2d\u7684\u85cf\u73cd\uff0c\u65e0\u4e00\u4e0d\u662f\u7f55\u4e16\u5947\u54c1\uff0c\u53ea\u6050\u8fd8\u6709\u4f59\u73cd\u5728\u5bfa\uff0c\u8001\u65b9\u4e08\u4e0d\u80af\u518d\u8bf4\u51fa\u6765\u4e86\u3002 \u3000\u3000\u5f20\u56db\u59d1\u4f3c\u4e4e\u662f\u65e9\u5df2\u80f8\u6709\u6210\u7af9\uff0c\u5631\u5490\u6c5f\u67ab\u52a9\u767e\u660e\u8fd0\u6c14\u884c\u529f\uff0c\u8fd8\u8981\u4ed6\u8010\u5fc3\u7684\u7b49\u5019\u6307\u793a\uff0c\u5b58\u5fc3\u6253\u8001\u65b9\u4e08\u7684\u4e3b\u610f\u5566\uff01 \u3000\u3000\u6c5f\u67ab\u773c\u770b\u8001\u65b9\u4e08\u5df2\u8fd0\u6c14\u81ea\u5982\uff0c\u4e5f\u4e0d\u591a\u7559\uff0c\u643a\u6885\u82b1\u79bb\u5f00\u53a8\u623f\u3002 \u3000\u3000\u5f20\u56db\u59d1\u6599\u60f3\u5230\u8001\u65b9\u4e08\u5fc5\u6709\u5b9d\u732e\uff0c\u53ef\u4e5f\u672a\u60f3\u5230\u5927\u5e08\u8bf4\u51fa\u8981\u8d60\u9001\u4e00\u4ef6\u5fa1\u654c\u5229\u5668\u3002 \u3000\u3000\u7531\u767e\u5fcd\u5927\u5e08\u966a\u5f80\u85cf\u7ecf\u9601\uff0c\u5728\u5343\u624b\u89c2\u97f3\u7684\u83b2\u82b1\u5ea7\u4e0b\uff0c\u8d77\u51fa\u4e86\u4e00\u628a\u53e4\u5251\uff0c\u5251\u672a\u51fa\u9798\uff0c\u950b\u8292\u4e0d\u9732\uff0c\u5c31\u4ea4\u7ed9\u4e86\u6c5f\u67ab\u6536\u85cf\uff0c\u53c8\u5f80\u6212\u6043\u9662\u6a2a\u6881\u4e0a\u627e\u51fa\u7389\u76d2\uff0c\u8fd9\u4e00\u6b21\uff0c\u5f53\u573a\u542f\u5f00\uff0c\u6570\u51fa\u5341\u4e8c\u7c92\u4e5d\u9633\u795e\u4e39\u3002 \u3000\u3000\u5c11\u6797\u50e7\u4f17\uff0c\u53ea\u770b\u5230\u4e00\u628a\u6591\u9508\u5904\u5904\u7684\u9752\u94dc\u5251\u9798\uff0c\u5374\u4e0d\u77e5\u662f\u4e00\u628a\u524a\u94c1\u5982\u6ce5\u7684\u5b9d\u5251\u3002 \u3000\u3000\u65e2\u662f\u8001\u65b9\u4e08\u8d60\u4e0e\u6c5f\u67ab\u4e4b\u7269\uff0c\u767e\u5fcd\u4e5f\u672a\u591a\u95ee\uff0c\u8001\u65b9\u4e08\u8fd8\u4e0d\u80fd\u4e3b\u6301\u5bfa\u52a1\uff0c\u767e\u5fcd\u662f\u6682\u4ee3\u65b9\u4e08\u804c\u52a1\uff0c\u529e\u5b8c\u4e8b\u5c31\u8d76\u56de\u8fbe\u6469\u9662\u7763\u4fc3\u4f17\u50e7\u6f14\u7ec3\u9635\u6cd5\uff0c\u6e29\u4e60\u6b66\u529f\uff0c\u541f\u96ea\u5df2\u4f20\u544a\u51fa\u660e\u65e5\u5348\u65f6\uff0c\u5f3a\u654c\u538b\u5883\u7684\u8baf\u606f\u3002 \u3000\u3000\u5c11\u6797\u50e7\u4f17\uff0c\u6bd2\u5bb3\u89e3\u9664\u4e0d\u4e45\uff0c\u5fc3\u4e2d\u79ef\u5fff\u4ecd\u5b58\uff0c\u8fbe\u6469\u9662\u4e2d\u7fa4\u50e7\u90fd\u5728\u57cb\u5934\u82e5\u7ec3\uff0c\u5fc3\u795e\u4e13\u4f4f\uff0c\u9e26\u96c0\u65e0\u58f0\u3002 \u3000\u3000\u5929\u867d\u672a\u9ed1\uff0c\u767e\u5fcd\u5927\u5e08\u5df2\u547d\u50e7\u4fa3\u6811\u8d77\u706b\u70ac\uff0c\u5df2\u51c6\u5907\u8fde\u591c\u52a0\u52b2\uff0c\u8fce\u63a5\u660e\u5348\u4e4b\u6218\u3002 \u3000\u3000\u5f20\u56db\u59d1\u884c\u56de\u540e\u5904\uff0c\u624d\u957f\u957f\u5401\u4e00\u53e3\u6c14\uff0c\u63a9\u4e0a\u623f\u95e8\uff0c\u8bf4\u9053\uff1a\u201c\u6c5f\u67ab\uff0c\u62d4\u51fa\u5251\u6765\uff0c\u77a7\u77a7\u770b\uff0c\u5f88\u5c0f\u65f6\u90fd\u542c\u5230\u8fd9\u4e9b\u4f20\u8bf4\uff0c\u53ef\u662f\u4ece\u6765\u6ca1\u6709\u89c1\u8fc7\uff0c\u8001\u5b9e\u8bf4\uff0c\u6211\u5fc3\u4e2d\u8fd8\u6709\u4e9b\u4e0d\u592a\u76f8\u4fe1\u5462\uff1f\u5929\u5c71\u5bd2\u94c1\uff0c\u4e3a\u94c1\u4e2d\u4e4b\u6700\uff0c\u4e5f\u53ea\u80fd\u7ec3\u6210\u5c16\u9510\u4e4b\u7269\uff0c\u624d\u53ef\u900f\u7a7f\u91d1\u77f3\uff0c\u7834\u4eba\u6c14\u529f\uff0c\u957f\u8fc7\u4e09\u5c3a\u7684\u950b\u5229\u4e4b\u5251\uff0c\u5982\u6709\u529f\u7389\u524a\u94c1\u4e4b\u80fd\uff0c\u90a3\u662f\u795e\u5668\u4e86\u3002\u201d \u3000\u3000\u201c\u6c5f\u6e56\u4e0a\u7684\u5b9d\u5200\u3001\u5b9d\u5251\uff0c\u4f20\u8bf4\u5f88\u591a\uff0c\u5927\u59d0\uff0c\u5374\u4ece\u672a\u4eb2\u773c\u89c1\u8fc7\uff0c\u4eca\u5929\u8981\u5927\u5f00\u4e00\u756a\u773c\u754c\uff0c\u4e5f\u8981\u8bc1\u5b9e\u4e00\u4e0b\uff0c\u6c5f\u6e56\u4f20\u8a00\uff0c\u662f\u771f\u662f\u5047\u3002\u201d \u3000\u3000\u6c5f\u67ab\u624b\u6309\u673a\u7c27\uff0c\u6ce2\u7684\u8f7b\u54cd\uff0c\u5251\u5203\u5f39\u51fa\u4e09\u5bf8\u3002 \u3000\u3000\u4e00\u80a1\u51b7\u68ee\u7684\u5251\u6c14\uff0c\u5df2\u7136\u6563\u5e03\u5168\u5ba4\u3002 \u3000\u3000\u5927\u548c\u5c1a\u4e00\u76f4\u6ca1\u6709\u8bf4\u8bdd\uff0c\u4f46\u5374\u4f38\u957f\u8116\u5b50\u770b\u3001\u4e00\u8138\u4e13\u6ce8\u795e\u8272\uff0c\u5fc3\u4e2d\u7684\u671f\u671b\uff0c\u7edd\u4e0d\u5728\u5f20\u56db\u59d1\u4e4b\u4e0b\u4e86\u3002 \u3000\u3000\u8fd9\u79cd\u5343\u53e4\u540d\u5251\u7684\u4f20\u8bf4\uff0c\u542c\u5f97\u53ef\u662f\u591a\u4e86\uff0c\u4f46\u662f\u4ece\u6765\u89c1\u8fc7\uff0c\u8fd9\u4e9b\u4e8b\u5df2\u6210\u4e86\u6b66\u6797\u4e2d\u65e0\u6cd5\u8bc1\u660e\u7684\u516c\u6848\uff0c\u8bf4\u5b83\u6ca1\u6709\u5427\uff1f\u5374\u53f2\u4e66\u8f7d\u8bb0\uff0c\u4f20\u8a00\u751a\u5e7f\uff0c\u8bf4\u5b83\u662f\u771f\u7684\u5427\uff01\u4e3a\u4ec0\u4e48\u767e\u5e74\u6765\uff0c\u4ece\u6765\u6ca1\u6709\u4eba\u89c1\u8fc7\u3002 \u3000\u3000\u5c24\u5176\u50cf\u9152\u8089\u548c\u5c1a\u4e03\u5b9d\u50e7\u8fd9\u6837\u7684\u4eba\uff0c\u8d70\u904d\u4e86\u6c5f\u6e56\u5404\u5730\uff0c\u6df1\u5165\u897f\u57df\u3001\u5357\u8352\uff3c\u4e1c\u6d77\u3001\u5317\u7586\uff0c\u9605\u5386\u4e4b\u4e30\uff0c\u5929\u4e0b\u5f88\u5c11\u4eba\u80fd\u591f\u53ca\u5f97\uff0c\u4f46\u4ed6\u5bf9\u5b9d\u5251\u597d\u5947\u4e4b\u5fc3\uff0c\u7adf\u662f\u4e0d\u5728\u5f20\u56db\u59d1\u4e4b\u4e0b\u3002 \u3000\u3000\u539f\u6765\uff0c\u5927\u548c\u5c1a\u5fc3\u4e2d\u65e9\u6697\u6697\u7acb\u5fd7\uff0c\u8981\u627e\u4e00\u628a\u5b9d\u5200\u6216\u662f\u5b9d\u5251\uff0c\u4f5c\u4e3a\u8fce\u6218\u7fa4\u9b54\u4e4b\u7528\uff0c\u5982\u65e0\u65ad\u91d1\u3001\u5207\u7389\u7684\u5229\u5668\uff0c\u8981\u6740\u6b7b\u590f\u592d\u540c\u8fd9\u6837\u6210\u5c31\u7684\u4eba\uff0c\u6050\u6015\u4e0d\u662f\u4e00\u4ef6\u5bb9\u6613\u7684\u4e8b\u4e86\u3002 \u3000\u3000\u4f46\u4ed6\u5954\u8d70\u6570\u5341\u725b\uff0c\u5374\u4e00\u76f4\u662f\u5fc3\u613f\u96be\u507f\uff0c\u4ed6\u8d70\u8fc7\u5317\u4eac\uff3c\u626c\u5dde\uff0c\u4e24\u5904\u5168\u56fd\u95fb\u540d\u7684\u5927\u53e4\u8463\u5546\uff0c\u4e5f\u770b\u4e00\u4e9b\u5229\u5668\uff0c\u5229\u5219\u5229\u77e3\uff01\u53ef\u6ca1\u65a9\u65ad\u91d1\u94c1\u7684\u80fd\u529b\u3002 \u3000\u3000\u6240\u4ee5\uff0c\u5927\u548c\u5c1a\u7684\u5fc3\u4e2d\u671f\u671b\uff0c\u6bd4\u4e4b\u5f20\u56db\u59d1\uff0c\u5c31\u66f4\u4e3a\u70ed\u5207\u4e86\u3002\u53ea\u662f\u6df1\u85cf\u4e8e\u5fc3\u4e2d\uff0c\u5341\u8bf4\u51fa\u53e3\u3002 \u3000\u3000\u80dc\u90aa\u5251\u79bb\u8def\u800c\u51fa\uff0c\u7acb\u523b\u53ef\u4ee5\u611f\u53d7\u5230\u6709\u4e00\u80a1\u5251\u6c14\uff0c\u5728\u5ba4\u4e2d\u6d41\u52a8\uff0c\u5e26\u7740\u4e00\u4e1d\u4e1d\u4fb5\u5165\u4eba\u7684\u5bd2\u610f\u3002 \u3000\u3000\u5251\u8eab\u4e0a\u4f3c\u88ab\u4e00\u5c42\u4e91\u96fe\u8ff7\u8499\u7740\uff0c\u5ba4\u4e2d\u6ca1\u6709\u5149\u6e90\uff0c\u770b\u4e0d\u5230\u745e\u6c14\u5343\u6761\uff0c\u91d1\u5149\u4e07\u9053\u7684\u8000\u773c\u5251\u8292\u3002 \u3000\u3000\u201c\u662f\u4e00\u628a\u597d\u5251\uff0c\u548c\u5c1a\u8fd9\u4e00\u751f\u4e2d\u89c1\u8fc7\u6700\u597d\u7684\u5251\uff0c\u4f46\u4e0d\u77e5\u9053\u662f\u5426\u771f\u80fd\u5207\u91d1\u65ad\u94c1\uff0c\u65e0\u575a\u4e0d\u6467\u3002\u201d \u3000\u3000\u201c\u5927\u548c\u5c1a\u5fc3\u4e2d\u6709\u7591\uff0c\u4f55\u4e0d\u8bd5\u8bd5\u770b\u5462\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u5fc3\u4e2d\u4e5f\u6709\u6000\u7591\uff0c\u5fd6\u9053\uff1a\u201c\u8981\u4ee5\u672c\u8eab\u5185\u529b\uff0c\u8d2f\u6ce8\u5251\u8eab\u4e4b\u4e0a\uff0c\u624d\u80fd\u65ad\u5165\u5175\u5203\uff0c\u8fd9\u628a\u5251\uff0c\u662f\u628a\u597d\u5251\uff0c\u53ef\u5c31\u79f0\u4e0d\u5f97\u4e3a\u4f20\u4e16\u4e4b\u5b9d\u7684\u795e\u5668\u4e86\u3002\u201d \u3000\u3000\u201c\u6211\u548c\u5c1a\u6b63\u6709\u6b64\u610f\uff0c\u4e0d\u8fc7\uff0c\u8bdd\u8981\u5148\u8bf4\u660e\u767d\uff0c\u8981\u662f\u4e00\u65e6\u4f24\u4e86\u4f60\u8fd9\u5b9d\u5251\uff0c\u88ab\u649e\u4e2a\u7f3a\u53e3\u51fa\u6765\uff0c\u53ef\u4e0d\u8981\u602a\u6211\u83bd\u649e\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5982\u662f\u88ab\u4e00\u51fb\u6253\u65ad\u4e86\u8fd9\u628a\u5251\uff0c\u53ef\u4f5c\u6697\u5668\u65bd\u7528\u3002 \u3000\u3000\u56e0\u4e3a\u4e22\u4e86\u5b83\u6709\u4e9b\u53ef\u60dc\uff0c\u81f3\u5c11\uff0c\u5b83\u662f\u6211\u542c\u89c1\u8fc7\u6700\u5229\u7684\u4e00\u628a\u5251\uff0c\u4f46\u5982\u548c\u4f20\u8a00\u4e0d\u7b26\uff0c\u90a3\u53ef\u80fd\u8bef\u5bfc\u6211\u53d7\u654c\u4f24\u5bb3\u3002\u201d \u3000\u3000\u201c\u8bf4\u7684\u662f\u554a\uff01\u597d\u5200\u597d\u5251\uff0c\u6c5f\u6e56\u4e0a\uff0c\u53ef\u662f\u4e0d\u5c11\uff0c\u627e\u5230\u5929\u5c71\u5343\u5e74\u5bd2\u94c1\uff0c\u8bf7\u4e00\u4e2a\u5584\u4e8e\u51b6\u91d1\u7684\u540d\u5320\uff0c\u5316\u4e0a\u4e09\u4e24\u5e74\uff0c\u4e5f\u4e0d\u96be\u6dec\u5236\u4e00\u628a\u950b\u5229\u7684\u597d\u5200\uff0c\u91d1\u949f\u7f69\uff0c\u94c1\u5e03\u886b\u4e00\u7c7b\u7684\u5916\u5bb6\u6c14\u529f\uff0c\u7edd\u96be\u6297\u62d2\u3002 \u3000\u3000\u4f46\u80fd\u4e0d\u80fd\u5288\u5f00\u5185\u5bb6\u7f61\u6c14\uff0c\u65ad\u53bb\u654c\u4eba\u7684\u62a4\u8eab\u5175\u5203\uff0c\u90a3\u5c31\u96be\u8bf4\u4e86\uff0c\u6bd5\u7adf\u5200\u5251\u4e4b\u7c7b\uff0c\u5927\u90fd\u662f\u767e\u70bc\u7cbe\u94a2\u554a\uff01\u201d \u3000\u3000\u5927\u548c\u5c1a\u6ed4\u6ed4\u4e0d\u7edd\uff0c\u5c31\u662f\u5e0c\u671b\u5f20\u56db\u59d1\u4e5f\u540c\u610f\u4ed6\u548c\u6c5f\u67ab\u7684\u770b\u6cd5\u3002 \u3000\u3000\u4f46\u5f20\u56db\u59d1\u53ea\u662f\u5fae\u7b11\uff0c\u4e0d\u80af\u8bf4\u8bdd\u3002 \u3000\u3000\u770b\u6837\u5b50\uff0c\u5979\u662f\u540c\u610f\u4e86\u4e24\u4eba\u7684\u610f\u89c1\uff0c\u53ef\u5c31\u662f\u4e0d\u80af\u8bf4\u51fa\u6765\u3002 \u3000\u3000\u201c\u597d\u5427\uff01\u6211\u548c\u5c1a\u4e00\u6076\u5230\u5e95\uff0c\u6bc1\u4e86\u8fd9\u4e00\u628a\u5343\u5e74\u795e\u5668\uff0c\u767e\u5e74\u4e4b\u540e\uff0c\u518d\u5411\u6b27\u51b6\u5b50\u9762\u81f4\u6b49\u610f\u3002\u201d \u3000\u3000\u4f38\u624b\u53d6\u8fc7\u4e86\u4e00\u53ea\u7985\u6756\u3002 \u3000\u3000\u8fd9\u662f\u5c11\u6797\u5bfa\u4e2d\u548c\u5c1a\u624b\u4e2d\u7684\u5175\u5203\uff0c\u5927\u548c\u5c1a\u968f\u624b\u53d6\u4e86\u4e00\u53ea\uff0c\u85cf\u5728\u4e86\u5f20\u56db\u59d1\u7684\u4f4f\u623f\u5c4b\u89d2\uff0c\u4f5c\u5f20\u56db\u59d1\u8fce\u654c\u65f6\u5175\u5203\u3002 \u3000\u3000\u5f20\u56db\u7ad9\u81ea\u7136\u77e5\u9053\u4e86\uff0c\u6697\u4e2d\u79f0\u8d5e\u9152\u8089\u548c\u5c1a\u5fc3\u7ec6\u5982\u53d1\u3002 \u3000\u3000\u56e0\u4e3a\uff0c\u5f20\u56db\u59d1\u867d\u4ee5\u65ad\u80a0\u4e09\u5251\u9a70\u540d\u6c5f\u6e56\uff0c\u4f46\u5927\u548c\u5c1a\u5374\u4e00\u76f4\u6ca1\u6709\u770b\u5230\u8fc7\u5f20\u56db\u59d1\u7684\u5251\u85cf\u4f55\u5904\uff0c\u4f46\u5374\u77e5\u9053\u5979\u5185\u529f\u5f88\u6df1\u539a\uff0c\u987a\u624b\u6284\u8d77\u4e00\u53ea\u7985\u6756\u62d2\u654c\uff0c\u4e5f\u8bb8\u53ef\u6536\u51fa\u654c\u4e0d\u610f\u4e4b\u6548\u3002 \u3000\u3000\u6613\u82b1\u624b\u5f88\u60f3\u9762\u81f4\u8c22\u610f\uff0c\u4f46\u5979\u53c8\u62c5\u5fc3\u4e3a\u5927\u548c\u5c1a\u6dfb\u81f4\u70e6\u607c\uff0c\u60c5\u4e1d\u7f20\u5fc3\u554a\uff01\u4e00\u65e6\u60f9\u52a8\u4e86\u5927\u548c\u5c1a\u7684\u51e1\u5fc3\uff0c\u90a3\u5c31\u662f\u53ef\u60b2\u7684\u9ebb\u70e6\u4e86\u3002 \u3000\u3000\u5f20\u56db\u59d1\u5fc3\u4e2d\u660e\u767d\uff0c\u7537\u5973\u76f8\u5904\uff0c\u65e5\u4e45\u60c5\u751f\uff0c\u4e00\u65e6\u60c5\u52a8\uff0c\u90a3\u5c31\u6ca1\u6709\u4ec0\u4e48\u662f\u975e\u597d\u5206\u4e86\u3002 \u3000\u3000\u4f55\u51b5\uff0c\u5927\u548c\u5c1a\u5bf9\u5979\u5df2\u4e0d\u907f\u5acc\u7591\uff0c\u5979\u5e26\u6c5f\u67ab\u5165\u5c45\u5ba4\uff0c\u5e76\u6ca1\u6709\u9080\u8bf7\u5927\u548c\u5c1a\u6765\u3002 \u3000\u3000\u4f46\u5927\u548c\u5c1a\u8ddf\u7740\u6765\u4e86\uff0c\u800c\u4e14\u662f\u81ea\u7136\u987a\u7545\uff0c\u5168\u65e0\u52c9\u5f3a\u3002 \u3000\u3000\u5927\u548c\u5c1a\u6c5f\u6e56\u9605\u5386\u4e30\u5bcc\uff0c\u5927\u667a\u82e5\u611a\uff0c\u6709\u4e9b\u4e8b\u626e\u61f5\u88c5\u50bb\uff0c\u53eb\u4eba\u5f04\u4e0d\u6e05\u771f\u5047\u3002 \u3000\u3000\u4f46\u5f20\u56db\u59d1\u5f88\u7ec6\u5fc3\uff0c\u5ffd\u7136\u53d1\u89c9\u6709\u70b9\u4e0d\u5bf9\u4e86\uff01 \u3000\u3000\u5927\u548c\u5c1a\u660e\u77e5\u81ea\u5df1\u662f\u6c5f\u67ab\u7684\u60c5\u5987\uff0c\u60c5\u90ce\u5165\u623f\uff0c\u4ee5\u5927\u548c\u5c1a\u5e73\u65e5\u7684\u4e3a\u4eba\uff0c\u5e94\u8be5\u4f1a\u7559\u7ed9\u4e24\u4eba\u4e00\u6bb5\u72ec\u5904\u65f6\u5149\u3002 \u3000\u3000\u4e5f\u8bb8\u4ed6\u771f\u7684\u662f\u4e3a\u4e00\u7779\u5343\u5e74\u7684\u795e\u5668\u800c\u6765\uff0c\u5f20\u56db\u59d1\u4e0d\u6562\u786e\u5b9a\u3002 \u3000\u3000\u4f46\u5fc3\u4e2d\u5df2\u6709\u8b66\u60d5\uff0c\u4e07\u4e8b\u5c31\u5c0f\u5fc3\u8d77\u6765\u4e86\uff0c\u9759\u9759\u7ad9\u5728\u4e00\u4fa7\uff0c\u4e0d\u8a00\u4e0d\u8bed\u3002 \u3000\u3000\u5927\u548c\u5c1a\u4e3e\u8d77\u624b\u4e2d\u7985\u6756\uff0c\u7b11\u9053\uff1a\u201c\u6c5f\u5c11\u5144\uff0c\u4f60\u8981\u5251\u5288\u7985\u6756\uff0c\u8fd8\u8981\u8001\u8872\uff0c\u4ee5\u6756\u51fb\u5251\u3002\u201d \u3000\u3000\u6c5f\u67ab\u770b\u90a3\u53ea\u7985\u6756\uff0c\u5927\u6982\u6709\u9e21\u86cb\u7c97\u7ec6\uff0c\u957f\u903e\u516b\u5c3a\uff0c\u6ca1\u6709\u5343\u65a4\u81c2\u529b\u7684\u4eba\uff0c\u5f88\u96be\u65bd\u5c55\uff0c\u90a3\u53ef\u662f\u7cbe\u94c1\u6253\u9020\u4e4b\u7269\uff0c\u8584\u8584\u4e00\u628a\u5251\uff0c\u5982\u4f55\u80fd\u591f\u524a\u65ad\uff0c\u8fd0\u6ce8\u5185\u529b\u4e8e\u5251\u8eab\u4e4b\u4e0a\uff0c\u53c8\u65e0\u6cd5\u6d4b\u51fa\u795e\u5668\u4e4b\u5229\u4e86\u3002 \u3000\u3000\u60f3\u4e86\u60f3\uff0c\u4f38\u5251\u800c\u51fa\uff0c\u9053\uff1a\u201c\u5927\u548c\u5c1a\uff0c\u4f60\u6253\u5427\uff01\u201d \u3000\u3000\u4e03\u5b9d\u50e7\u7985\u6756\u51fb\u4e0b\uff0c\u76f4\u7838\u5251\u5203\u3002 \u3000\u3000\u53ea\u542c\u6ce2\u7684\u4e00\u58f0\u8f7b\u54cd\uff0c\u94c1\u7985\u6756\u7acb\u523b\u4e2d\u65ad\u3002 \u3000\u3000\u201c\u679c\u7136\u662f\u795e\u7269\u5229\u5668\uff0c\u548c\u5c1a\u6ca1\u6709\u611f\u89c9\u5230\u5175\u5200\u76f8\u51fb\u7684\u611f\u89c9\uff0c\u54c8\u54c8\uff0c\u5927\u5f00\u773c\u754c\u4e86\uff0c\u5927\u5f00\u773c\u754c\u4e86\u3002\u201d \u3000\u3000\u5927\u7b11\u58f0\u4e2d\uff0c\u542f\u5f00\u623f\u95e8\u800c\u53bb\u3002 \u3000\u3000\u5f20\u56db\u59d1\u671b\u7740\u5927\u548c\u5c1a\u7684\u80cc\u5f71\uff0c\u5fc3\u4e2d\u6697\u6697\u5fd6\u9053\uff1a\u201c\u4ed6\u53ef\u80fd\u5fc3\u65e0\u90aa\u5ff5\uff0c\u5012\u662f\u6211\u591a\u8651\u4e86\u3002\u201d\u7f13\u7f13\u63a9\u4e0a\u623f\u95e8\uff0c\u56de\u5934\u7b11\u9053\uff1a\u201c\u4f60\u6709\u4ec0\u4e48\u611f\u89c9\u3002\u201d \u3000\u3000\u6c5f\u67ab\u4ecd\u7136\u5728\u671b\u7740\u624b\u4e2d\u7684\u5b9d\u5251\u51fa\u795e\u3002 \u3000\u3000\u5f20\u56db\u59d1\u83b2\u6b65\u8f7b\u79fb\uff0c\u7f13\u7f13\u628a\u6c5f\u67ab\u63fd\u5165\u6000\u4e2d\uff0c\u9053\uff1a\u201c\u5c0f\u60c5\u90ce\uff0c\u60f3\u4ec0\u4e48\u5440\uff01\u201d \u3000\u3000\u201c\u662f\u4e00\u628a\u597d\u5251\u554a\uff01\u6211\u6ca1\u6709\u611f\u89c9\u5230\u4ec0\u4e48\uff0c\u90a3\u4e48\u7c97\u7684\u94c1\u7985\u6756\u4e00\u4e0b\u5b50\u5c31\u65ad\u4e86\u3002\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u800c\u4e14\uff0c\u65ad\u5904\u5e73\u6574\uff0c\u771f\u662f\u524a\u94c1\u5982\u6ce5\uff0c\u53e4\u4eba\u662f\u8bda\u4e0d\u6b3a\u6211\u4e86\u3002\u201d \u3000\u3000\u201c\u6709\u4e86\u8fd9\u628a\u5b9d\u5251\uff0c\u4f60\u589e\u52a0\u4e86\u591a\u5c11\u80dc\u7b97\uff1f\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u8bb0\u7740\u5440\uff01\u5343\u4e07\u4e0d\u53ef\u8f7b\u751f\uff0c\u4e00\u65e6\u4f60\u6b7b\u4e86\uff0c\u591a\u5c11\u4eba\u4f1a\u4e3a\u4f60\u75db\u5fc3\u6b32\u7edd\uff0c\u5305\u62ec\u5927\u59d0\u5728\u5185\u3002\u201d \u3000\u3000\u201c\u6211\u4e0d\u4f1a\u8f7b\u751f\uff0c\u4f46\u4e5f\u4e0d\u613f\u8ba4\u8f93\uff0c\u8fd9\u4e00\u6218\uff0c\u6211\u4f1a\u5168\u529b\u4ee5\u8d74\uff0c\u5927\u59d0\uff0c\u4e07\u4e00\u6211\u6709\u4e0d\u5e78\uff0c\u4f60\u8981\u597d\u597d\u7684\u7167\u987e\u96ea\u3001\u971c\uff0c\u6211\u6ca1\u78b0\u8fc7\u5979\u4eec\uff0c\u5979\u4eec\u5e94\u8be5\u8fd8\u6709\u81ea\u5df1\u7684\u4eba\u751f\u3002\u201d \u3000\u3000\u201c\u5509\uff01\u50bb\u5f1f\u5f1f\uff0c\u4f60\u5df2\u5360\u636e\u4e86\u5979\u4eec\u7684\u5fc3\uff0c\u5c31\u7b97\u5979\u4eec\u8eab\u8eaf\u8bf7\u767d\uff0c\u53ef\u662f\uff0c\u4e5f\u65e0\u6cd5\u518d\u5bb9\u7eb3\u522b\u4eba\uff0c\u96ea\u3001\u971c\u4e8c\u5973\uff0c\u7231\u7684\u6700\u75f4\uff0c\u800c\u4e14\u662f\u7eaf\u6d01\u65e0\u7455\uff0c\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u4f60\u8981\u7ed9\u6211\u597d\u597d\u7684\u6d3b\u7740\uff0c\u6211\u76f8\u4fe1\u8fd9\u628a\u5b9d\u5251\uff0c\u4f1a\u7ed9\u4f60\u5f88\u5927\u7684\u5e2e\u52a9\uff0c\u4e0d\u8fc7\u2026\u2026\u201d \u3000\u3000\u201c\u5c0f\u5f1f\u606d\u5019\u6307\u793a\uff0c\u5927\u59d0\u5356\u4ec0\u4e48\u5173\u5b50\u5462\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u8fd8\u5251\u5165\u9798\uff0c\u504e\u5165\u4e86\u5f20\u56db\u59d1\u7684\u6000\u4e2d\u3002 \u3000\u3000\u96ea\u3001\u971c\u4e8c\u5973\uff0c\u9752\u51e4\u3001\u6885\u82b1\uff0c\u90fd\u53ea\u6709\u5bf9\u4ed6\u6492\u5a07\u7684\u4efd\uff0c\u4ed6\u8981\u50cf\u4e00\u53ea\u5927\u4f1e\u822c\uff0c\u66ff\u5979\u4eec\u906e\u853d\u98ce\u96e8\uff0c\u8981\u4f5c\u51fa\u82f1\u96c4\u6c14\u6982\uff0c\u65e0\u60e7\u65e0\u754f\u7684\u795e\u60c5\uff0c\u81f3\u5c11\u7cbe\u795e\u8981\u4f5c\u5979\u4eec\u7684\u652f\u67f1\u3002 \u3000\u3000\u4f46\u5f20\u56db\u59d1\u5c31\u4e0d\u540c\u4e86\u3002 \u3000\u3000\u5979\u662f\u5927\u59d0\uff0c\u662f\u60c5\u4eba\uff0c\u4e5f\u6709\u6bcd\u4eb2\u822c\u7684\u6148\u6000\uff0c\u5bb9\u8bb8\u4ed6\u503e\u8bc9\u75db\u82e6\uff0c\u4e5f\u5305\u5bb9\u4ed6\u7684\u59d4\u5c48\u3001\u611f\u4f24\uff0c\u5bb9\u8bb8\u4ed6\u4f9d\u9644\u4e8e\u5375\u7ffc\u4e4b\u4e0b\uff0c\u53d1\u6cc4\u4e00\u4e0b\u8d64\u5b50\u7684\u5b7a\u6155\u3002 \u3000\u3000\u201c\u5c0f\u60c5\u90ce\uff0c\u6709\u60b2\u82e6\u5c31\u8bf4\u51fa\u6765\uff0c\u4e0d\u60f3\u8bf4\u5c31\u54ed\u4e00\u573a\u5427\uff01\u6211\u77e5\u9053\uff0c\u4f60\u4e00\u76f4\u5728\u538b\u6291\u81ea\u5df1\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u9002\u5e94\u73af\u5883\uff0c\u914d\u5408\u522b\u4eba\uff0c\u4f60\u80a9\u4e0a\u62c5\u8d1f\u4e86\u4e07\u94a7\u7684\u91cd\u8d23\u5927\u4efb\uff0c\u5374\u53c8\u4e0d\u80fd\u8868\u73b0\u51fa\u4f60\u7684\u5de8\u5927\u6c14\u6982\uff0c\u662f\u5417\uff1f\u201d \u3000\u3000\u201c\u6211\u2026\u2026\u6211\u2026\u2026\u201d\u6c5f\u67ab\u771f\u7684\u6d41\u4e0b\u4e86\u773c\u6cea\uff0c\u9053\uff1a\u201c\u6211\u660e\u5929\u4f1a\u7528\u51fa\u6211\u6bd5\u751f\u6240\u5b66\uff0c\u6211\u8981\u5168\u529b\u640f\u6740\uff0c\u5927\u59d0\uff0c\u7ed9\u6211\u4e2a\u673a\u4f1a\uff0c\u5e0c\u671b\u80fd\u5728\u8fd9\u4e00\u6218\u5c3d\u6b7c\u5f3a\u654c\uff0c\u751f\u6b7b\u7684\u4e8b\uff0c\u5c0f\u5f1f\u5df2\u7f6e\u4e4b\u5ea6\u5916\u4e86\uff0c\u5e0c\u671b\u5927\u59d0\u4e0d\u8981\u518d\u675f\u7f1a\u6211\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u597d\uff01\u8ba9\u4f60\u5c3d\u60c5\u53d1\u6325\uff0c\u4f46\u4e5f\u6709\u51e0\u4ef6\u4e8b\uff0c\u6211\u8981\u63d0\u9192\u4f60\u3002\u201d \u3000\u3000\u201c\u5927\u59d0\u8bf7\u8bf4\u3002\u201d \u3000\u3000\u201c\u4e24\u4e2a\u5c0f\u72d0\u5973\u90fd\u53ef\u80fd\u5b58\u4e0b\u5168\u529b\u4e00\u62fc\u7684\u51b3\u5fc3\uff0c\u4f60\u4e0d\u80fd\u5f88\u5feb\u7684\u5012\u4e0b\u53bb\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u5728\u4f60\u5f15\u5bfc\u4e0b\uff0c\u5979\u4eec\u624d\u80fd\u53d1\u6325\u51fa\u5168\u90e8\u7684\u6f5c\u80fd\uff0c\u8bb0\u7740\u6211\u7684\u8bdd\uff0c\u611f\u89c9\u5230\u6c14\u529b\u4e0d\u7ee7\u65f6\uff0c\u5c31\u4e0d\u8981\u592a\u52c9\u5f3a\u81ea\u5df1\uff0c\u9000\u4e0b\u6765\u7a0d\u4f5c\u8c03\u606f\u3002 \u3000\u3000\u4f60\u5f53\u7136\u53ef\u4ee5\u5168\u529b\u65bd\u4e3a\uff0c\u4f46\u4e5f\u8981\u7167\u987e\u5230\u522b\u4eba\u7684\u751f\u6b7b\uff0c\u6709\u4e9b\u4e8b\uff0c\u7a0d\u9000\u4e00\u6b65\uff0c\u53cd\u800c\u662f\u81f4\u80dc\u4e4b\u673a\u3002 \u3000\u3000\u96ea\u3001\u971c\u7684\u5bd2\u94c1\u593a\u9b42\u9488\uff0c\u662f\u6740\u4eba\u7684\u5229\u5668\uff0c\u8bba\u6076\u6bd2\uff0c\u7edd\u4e0d\u5728\u80dc\u90aa\u5251\u4e4b\u4e0b\uff0c\u4f60\u548c\u5979\u4eec\u914d\u5408\u8d77\u6765\uff0c\u5e94\u8be5\u662f\u4e00\u4e2a\u65e0\u654c\u5929\u4e0b\u7684\u7ec4\u5408\u3002 \u3000\u3000\u6211\u5df2\u7ecf\u5c3d\u4e86\u5fc3\u529b\uff0c\u771f\u8981\u6253\u8d25\u4e86\uff0c\u53ea\u597d\u8bf4\u5929\u4ea1\u6211\u4eec\uff0c\u4f60\u5982\u6218\u6b7b\uff0c\u4e24\u4e2a\u5c0f\u72d0\u5973\u7edd\u4e0d\u4f1a\u5077\u751f\uff0c\u5979\u4eec\u4f1a\u75af\u72c2\u6740\u654c\uff0c\u6b7b\u800c\u540e\u5df2\uff0c\u522b\u7684\u4eba\u60f3\u6d3b\u4e5f\u6d3b\u4e0d\u4e0b\u53bb\u3002 \u3000\u3000\u56e0\u4e3a\uff0c\u4e0d\u4f1a\u518d\u6709\u9003\u547d\u7684\u673a\u4f1a\uff0c\u8840\u67d3\u5c11\u6797\u5c71\u95e8\u4e4b\u540e\uff0c\u6574\u4e2a\u6b66\u6797\u5c06\u6ca6\u5165\u9b54\u9053\uff0c\u4e00\u767e\u5e74\u5185\uff0c\u4e0d\u4f1a\u518d\u73b0\u751f\u673a\uff0c\u8fd9\u5c31\u662f\u4f60\u6218\u6b7b\u7684\u6c5f\u6e56\u5f62\u52bf\u3002 \u3000\u3000\u6c5f\u67ab\uff0c\u4f60\u4e00\u4eba\u7cfb\u6b66\u6797\u5b89\u5371\u5440\uff01\u8001\u65b9\u4e08\u4e5f\u770b\u51fa\u4e86\u8fd9\u4e2a\u60c5\u52bf\uff0c\u624d\u516b\u5b9d\u5c3d\u51fa\uff0c\u628a\u80dc\u90aa\u5b9d\u5251\u4e5f\u7531\u83e9\u8428\u7684\u83b2\u82b1\u5ea7\u4e0b\u642c\u51fa\u6765\u4e86\u3002\u201d \u3000\u3000\u201c\u6211\u2026\u2026\u201d \u3000\u3000\u201c\u4f60\u53ef\u4ee5\u6218\u6b7b\uff0c\u5927\u59d0\u4e0d\u4f1a\u963b\u6b62\uff0c\u4f46\u6218\u6b7b\u4e0d\u662f\u627e\u6b7b\uff01\u8001\u5b9e\u8bf4\uff0c\u6211\u4e0d\u4f1a\u56e0\u79c1\u5bb3\u516c\uff0c\u56e0\u4e3a\uff0c\u4f60\u6b7b\u4e86\uff0c\u6211\u4f1a\u968f\u4f60\u800c\u53bb\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u90a3\u4f4d\u795e\u79d8\u6559\u4e3b\uff0c\u4e5f\u4e0d\u4f1a\u653e\u8fc7\u6211\u4eec\uff0c\u4f46\u6211\u8981\u7684\u662f\u80dc\u5229\uff01 \u3000\u3000\u4f60\u529b\u6218\u7b4b\u75b2\u65f6\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u80fd\u9000\u4e0b\u6765\u4f11\u606f\u4e0b\u5462\uff1f\u201d \u3000\u3000\u201c\u597d\u5427\uff01\u660e\u65e5\u4e34\u654c\uff0c\u5c0f\u5f1f\u542c\u547d\u884c\u4e8b\u5c31\u662f\u3002\u201d \u3000\u3000\u201c\u5c0f\u60c5\u90ce\uff0c\u5fcd\u53e3\u6c14\u5427\uff01\u4e0d\u8981\u611f\u89c9\u5f88\u59d4\u5c48\uff0c\u660e\u5929\u51b3\u6218\u65f6\uff0c\u522b\u592a\u4efb\u6027\uff0c\u51b3\u6218\u4e4b\u540e\uff0c\u6211\u4eec\u8fd8\u6d3b\u7740\uff0c\u5927\u59d0\u5c31\u6d17\u624b\u4f5c\u7fb9\u6c64\uff0c\u5e2e\u4f60\u70e7\u4e09\u4e2a\u6708\u7684\u4f73\u80b4\u7f8e\u5473\uff0c\u4f5c\u4e3a\u8865\u507f\uff0c\u628a\u4f60\u5f04\u7684\u80d6\u4e00\u70b9\uff01\u201d \u3000\u3000\u201c\u4e09\u4e2a\u6708\u4e4b\u540e\u5462\uff1f\u201d \u3000\u3000\u201c\u4f60\u4e5f\u8be5\u548c\u96ea\u3001\u971c\u4e24\u4e2a\u4e2b\u5934\u6210\u4eb2\u4e86\uff0c\u5802\u5802\u6b63\u6b63\u7684\u82b1\u8f7f\u8fce\u5a36\uff0c\u6d1e\u623f\u82b1\u70db\uff0c\u5927\u59d0\u5fc3\u4e8b\u5168\u4e86\uff0c\u518d\u65e0\u6302\u7591\uff0c\u6b27\u9633\u662d\u4e5f\u627e\u4e0d\u51fa\u4e00\u70b9\u6bdb\u75c5\u4e86\u3002\u201d \u3000\u3000\u201c\u5c0f\u5f1f\u53ef\u4e0d\u53ef\u4ee5\u63d0\u51fa\u4e00\u4e2a\u6761\u4ef6\uff1f\u201d \u3000\u3000\u201c\u597d\u554a\uff01\u4f60\u8bf4\u3002\u201d \u3000\u3000\u201c\u5927\u59d0\u9700\u5148\u7b54\u5e94\u6211\uff0c\u6211\u624d\u80fd\u8bf4\uff0c\u6211\u77e5\u9053\u5927\u59d0\u4e00\u8a00\u5982\u5c71\uff0c\u7b54\u5e94\u4e86\u4e0d\u4f1a\u53d8\u5366\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u70b9\u70b9\u5934\u3002 \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u5982\u679c\u6211\u4eec\u8fd8\u6d3b\u7740\uff0c\u6211\u8981\u5927\u59d0\u7559\u4e0b\u6765\u966a\u6211\u4eec\u3002\u201d \u3000\u3000\u201c\u72e1\u733e\u7684\u5c0f\u60c5\u90ce\uff0c\u4f60\u53c8\u54ed\u53c8\u6492\u5a07\uff0c\u5c31\u662f\u4e3a\u8fd9\u4e2a\u554a\uff1f\u4e16\u4e0a\u771f\u4f1a\u6709\u8fd9\u4e48\u5b8c\u7f8e\u7684\u4e8b\u5417\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u8fd8\u60f3\u518d\u8bf4\uff0c\u903c\u4f7f\u5f20\u56db\u59d1\u4f5c\u6b63\u9762\u627f\u8bfa\uff0c\u53ef\u60dc\u5374\u6ca1\u6709\u673a\u4f1a\u4e86\u3002 \u3000\u3000\u56e0\u4e3a\uff0c\u7f57\u5170\u6025\u6025\u5fd9\u5fd9\u7684\u51b2\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u201c\u4ec0\u4e48\u4e8b\u554a\uff1f\u7f57\u5170\uff01\u201d \u3000\u3000\u7f57\u5170\u6ca1\u6709\u56de\u7b54\u5f20\u56db\u59d1\uff0c\u4e00\u5bf9\u7f8e\u4e3d\u7684\u773c\u775b\uff0c\u6253\u91cf\u6c5f\u67ab\u4e00\u9635\uff0c\u9053\uff1a\u201c\u6c5f\u67ab\uff0c\u4f60\u597d\u50cf\u54ed\u8fc7\u4e86\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\u554a\uff01\u4f60\u4eec\u804a\u5427\uff01\u6211\u8d70\u4e86\u3002\u201d \u3000\u3000\u8f6c\u8eab\u6025\u6b65\u800c\u53bb\u3002 \u3000\u3000\u201c\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\uff1f\u201d\u7f57\u5170\u4e00\u8138\u8ff7\u60d8\u7684\u671b\u7740\u5f20\u56db\u59d1\u3002 \u3000\u3000\u201c\u6c5f\u67ab\u6709\u70b9\u5b69\u5b50\u6c14\uff0c\u89c9\u7740\u6211\u7ba1\u7684\u592a\u591a\uff0c\u7ea6\u675f\u4e86\u4ed6\u7684\u82f1\u96c4\u6c14\u6982\uff0c\u4e0d\u80fd\u653e\u624b\u65bd\u4e3a\uff0c\u4e5f\u96be\u602a\u5440\uff01\u624d\u4e8c\u5341\u4e09\u56db\u7684\u5c0f\u4f19\u5b50\uff0c\u53c8\u5f88\u5c11\u6c5f\u6e56\u5386\u7ec3\u2026\u2026\u201d\u5f20\u56db\u59d1\u7b11\u9053\uff1a\u201c\u4e00\u8eab\u597d\u6b66\u529f\uff0c\u4e0d\u80fd\u5c3d\u60c5\u65bd\u5c55\uff0c\u5904\u5904\u8981\u8ba9\u907f\u654c\u950b\uff0c\u5fc3\u91cc\u618b\u4e86\u4e00\u80a1\u6028\u6c14\uff0c\u627e\u6211\u6765\u53d1\u7262\u9a9a\u5566\uff01\u201d \u3000\u3000\u7f57\u5170\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u771f\u662f\u5c11\u4e0d\u66f4\u4e8b\u2026\u2026\u201d \u3000\u3000\u201c\u600e\u4e48\uff1f\u5973\u513f\u8fd8\u672a\u8fc7\u95e8\uff0c\u8001\u5cb3\u6bcd\u7684\u67b6\u52bf\u5c31\u6446\u51fa\u6765\u4e86\u3002\u201d \u3000\u3000\u201c\u957f\u5e7c\u6709\u5e8f\u554a\uff01\u541f\u96ea\u3001\u541f\u971c\u5ac1\u8fc7\u53bb\uff0c\u4ed6\u4ee5\u540e\u5f97\u53eb\u6211\u5a18\u2026\u2026\u201d\u5a18\u5b57\u51fa\u53e3\uff0c\u7f57\u5170\u81ea\u5df1\u4e5f\u5fcd\u4e0d\u4f4f\uff0c\u55e4\u7684\u4e00\u58f0\u7b11\u4e86\u51fa\u6765\u3002 \u3000\u3000\u201c\u7f57\u5170\uff0c\u6211\u6b63\u60f3\u901a\u77e5\u4f60\uff0c\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\u4e86\uff0c\u660e\u65e5\u5348\u65f6\uff0c\u5927\u654c\u538b\u5883\uff01\u201d\u7f57\u5170\u9053\uff1a\u201c\u6211\u731c\u7684\u5bf9\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u544a\u8bc9\u4f60\u8001\u516c\uff0c\u597d\u597d\u751f\u606f\u4e00\u4e0b\uff0c\u517b\u517b\u7cbe\u795e\uff0c\u624d\u5230\u5c11\u6797\u5bfa\uff0c\u5c31\u6392\u7ec3\u5251\u9635\uff0c\u7ec3\u7684\u600e\u4e48\u6837\u4e86\uff1f\u201d \u3000\u3000\u201c\u597d\u50cf\u5df2\u7ecf\u5927\u529f\u544a\u6210\uff0c\u56db\u59d1\uff0c\u6211\u4e5f\u5728\u82e6\u7ec3\u5929\u72d0\u6b66\u529f\uff0c\u800c\u4e14\uff0c\u5f88\u6709\u6210\u5c31\u2026\u2026\u201d \u3000\u3000\u201c\u6b27\u9633\u662d\u540c\u610f\u4f60\u7ec3\uff1f\u201d \u3000\u3000\u201c\u662f\u4ed6\u529d\u6211\u7ec3\u7684\u2026\u2026\u201d\u7f57\u5170\u7b11\u9053\uff1a\u201c\u5927\u6982\u662f\u770b\u5230\u4e24\u4e2a\u4e2b\u5934\u548c\u6c5f\u67ab\u7adf\u7136\u80fd\u70c8\u706b\u4e0d\u71c3\u5e72\u67f4\uff0c\u4f69\u670d\u7684\u4e0d\u5f97\u4e86\uff01\u201d \u3000\u3000\u5f20\u56db\u59d1\u55e4\u7684\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u7f57\u5170\uff0c\u4f60\u80fd\u4e0d\u80fd\u9009\u4e00\u4e9b\u597d\u542c\u7684\u8bcd\u513f\u8bf4\uff1f\u201d \u3000\u3000\u201c\u8fd9\u53e5\u8bdd\u6700\u4f20\u795e\u4e86\uff0c\u6211\u8001\u516c\u4e5f\u662f\u8fd9\u4e48\u8bf4\u7684\uff0c\u4ed6\u4eba\u867d\u53e4\u677f\uff0c\u4f46\u5f88\u8bb2\u7406\u2026\u2026\u201d \u3000\u3000\u201c\u597d\u5566\uff01\u7f57\u5170\uff0c\u4f60\u628a\u4f60\u4eec\u592b\u5987\u7684\u6084\u6084\u8bdd\uff0c\u5168\u8bf4\u51fa\u6765\u4e86\uff0c\u6211\u53ef\u4e0d\u60f3\u542c\uff0c\u8bf4\u5427\uff01\u7a76\u7adf\u6709\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u201c\u5509\uff01\u4ec0\u4e48\u4e8b\u90fd\u7792\u4e0d\u8fc7\u4f60\uff0c\u56db\u59d1\uff0c\u6211\u662f\u4e3a\u5927\u4e2b\u5934\u6765\u7684\uff0c\u5979\u4e0d\u6562\u8ddf\u4f60\u8bf4\uff0c\u53ea\u597d\u628a\u8001\u5a18\u642c\u51fa\u6765\u4e86\u3002\u201d \u3000\u3000\u201c\u541f\u96ea\u4f1a\u6709\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u201c\u5979\u60f3\u6c42\u4f60\uff0c\u660e\u5929\u5728\u6218\u9635\u4e4b\u4e0a\uff0c\u6d3e\u5979\u548c\u6c5f\u67ab\u8054\u624b\u5bf9\u654c\uff0c\u5979\u5077\u5077\u8981\u6c42\u6211\u7684\uff0c\u8fde\u5979\u7239\u4e5f\u4e0d\u77e5\u9053\uff0c\u5979\u8bf4\uff0c\u6218\u9635\u4e4b\u4e0a\uff0c\u5979\u4e0d\u6562\u6297\u547d\u2026\u2026\u201d\u7f57\u5170\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u8981\u62ff\u6211\u7684\u8001\u9762\u5b50\u6765\u625b\u4e00\u4e0b\u4e86\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u63a5\u9053\uff1a\u201c\u672c\u6765\u5c31\u51c6\u5907\u6d3e\u5979\u4eec\u59d0\u59b9\u548c\u6c5f\u67ab\u8054\u624b\u5bf9\u654c\u7684\uff0c\u541f\u96ea\u5e94\u8be5\u60f3\u5f97\u5230\u554a\uff01\u201d \u3000\u3000\u201c\u95ee\u9898\u5c31\u51fa\u5728\u8fd9\u91cc\u4e86\uff0c\u5927\u4e2b\u8fd8\u8bf4\u4e0d\u80fd\u8ba9\u59b9\u59b9\u53c2\u52a0\uff0c\u8981\u4f60\u628a\u541f\u971c\u652f\u6d3e\u5f00\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u2026\u2026\u201d\u5f20\u56db\u59d1\u6709\u70b9\u832b\u7136\u4e86\u3002 \u3000\u3000\u201c\u5927\u4e2b\u5934\u6ca1\u8bf4\u6e05\u695a\uff0c\u53ea\u8bf7\u6c42\u522b\u8ba9\u541f\u971c\u77a7\u51fa\u7834\u7efd\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u6c89\u4e86\u826f\u4e45\uff0c\u9053\uff1a\u201c\u6211\u660e\u767d\u4e86\uff01\u201d \u3000\u3000\u201c\u660e\u767d\u4e86\uff1f\u201d\u7f57\u5170\u82e6\u7b11\u9053\uff1a\u201c\u6211\u6765\u4f5c\u8bf4\u5ba2\uff0c\u53ef\u662f\uff0c\u81ea\u5df1\u4e00\u70b9\u4e5f\u4e0d\u660e\u767d\uff0c\u544a\u8bc9\u6211\uff0c\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d \u3000\u3000\u201c\u7f57\u5170\uff0c\u4e0d\u8981\u95ee\uff0c\u6c38\u8fdc\u653e\u5728\u5fc3\u91cc\uff01\u4e0d\u8bb8\u5bf9\u4efb\u4f55\u4eba\u63d0\u8d77\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u5305\u62ec\u4f60\u8001\u516c\u5728\u5185\u3002\u201d \u3000\u3000\u7f57\u5170\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u597d\u5427\uff01\u6211\u4eec\u4ea4\u670b\u53cb\uff0c\u8868\u9762\u4e0a\u60c5\u540c\u59d0\u59b9\uff0c\u53ef\u662f\u6211\u8fd9\u4e2a\u4f5c\u59d0\u59d0\u7684\uff0c\u9aa8\u5b50\u91cc\u4e00\u76f4\u542c\u4f60\u652f\u4f7f\uff0c\u73b0\u5728\u66f4\u597d\u4e86\uff0c\u8fde\u5927\u4e2b\u5934\uff0c\u4e5f\u628a\u5979\u5a18\u88c5\u5728\u95f7\u846b\u82a6\u91cc\u4e86\u3002\u201d \u3000\u3000\u201c\u6211\u4e0d\u662f\u7ed9\u8db3\u4e86\u4f60\u7684\u9762\u5b50\u5417\uff1f\u4f60\u8bb2\u7684\uff0c\u6211\u7167\u529e\uff0c\u5c0f\u59b9\u6211\uff0c\u53ef\u662f\u5b8c\u5168\u9075\u4ece\u4f60\u4f5c\u59d0\u59d0\u7684\u5429\u5490\u554a\uff01\u201d \u3000\u3000\u7f57\u5170\u55e4\u7684\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8bf4\u7684\u4e5f\u662f\uff01\u4f60\u80af\u9075\u529e\u5c31\u597d\uff0c\u6211\u4e5f\u61d2\u5f97\u591a\u95ee\u4e86\uff0c\u80fd\u5bf9\u5927\u4e2b\u5934\u6709\u4e2a\u4ea4\u4ee3\uff0c\u6211\u8fd9\u4f5c\u5a18\u7684\uff0c\u5c82\u4e0d\u662f\u633a\u98ce\u5149\u5417\uff1f\u201d \u3000\u3000\u5f20\u56db\u59d1\u7a81\u7136\u6293\u4f4f\u4e86\u7f57\u5170\u4e00\u53ea\u624b\u9053\uff1a\u201c\u544a\u8bc9\u6b27\u9633\u662d\uff0c\u8981\u4ed6\u81ea\u5df1\u5c0f\u5fc3\u4e00\u4e9b\uff0c\u5148\u8981\u81ea\u4fdd\uff0c\u518d\u6c42\u80dc\u654c\uff0c\u4e0d\u8981\u592a\u901e\u5f3a\uff0c\u660e\u65e5\u4e00\u6218\uff0c\u867d\u7136\u51f6\u9669\u4e07\u5206\uff0c\u4f46\u6211\u4eec\u7684\u80dc\u7b97\u5f88\u5927\u2026\u2026\u201d \u3000\u3000\u201c\u771f\u7684\u6709\u80dc\u7b97\u54ea\uff1f\u201d \u3000\u3000\u201c\u662f\u771f\u7684\uff0c\u7f57\u5170\uff0c\u4f60\u4e5f\u8981\u591a\u591a\u4fdd\u91cd\uff0c\u4e0d\u8981\u901e\u5f3a\u786c\u62da\u3002\u201d \u3000\u3000\u7f57\u5170\u7709\u5b87\u5411\u95ee\u8d77\u4e00\u62b9\u8bb6\u5f02\uff0c\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u5982\u6b64\u8ba4\u771f\uff0c\u5927\u6982\u4e0d\u662f\u9a97\u6211\u4e86\uff1f\u201d \u3000\u3000\u5979\u65e9\u5df2\u8ba4\u5b9a\uff0c\u660e\u65e5\u4e00\u6218\u662f\u5fc5\u6b7b\u4e4b\u5c40\uff0c\u5fc3\u4e2d\u5df2\u6709\u51c6\u5907\uff0c\u5012\u4e5f\u5766\u7136\u542c\u5230\u8fd8\u6709\u80dc\u7b97\uff0c\u53cd\u800c\u6709\u4e9b\u60ca\u5947\u8d77\u6765\u3002 \u3000\u3000\u201c\u7f57\u5170\u2026\u2026\u201d\u5f20\u56db\u59d1\u7684\u773c\u4e2d\u6709\u6e7f\u6da6\uff0c\u9053\uff1a\u201c\u6253\u7d2f\u4e86\uff0c\u5c31\u60f3\u6cd5\u5b50\u907f\u5230\u5c11\u6797\u50e7\u4f17\u7684\u6392\u7684\u7f57\u6c49\u9635\u4e2d\u5598\u53e3\u6c14\uff0c\u4f60\u4eec\u4e00\u5bb6\u4eba\uff0c\u5168\u90fd\u6295\u5165\u660e\u65e5\u7684\u51b3\u6218\u4e4b\u4e2d\uff0c\u6211\u5fc3\u4e2d\u5341\u5206\u4e0d\u5b89\u2026\u2026\u201d \u3000\u3000\u201c\u662f\u6211\u4eec\u81ea\u613f\u7684\uff0c\u548c\u4f60\u65e0\u5173\uff0c\u5929\u72d0\u592b\u4eba\u4f20\u6211\u5929\u72d0\u53e3\u8bc0\u65f6\uff0c\u5df2\u7ecf\u6ce8\u5b9a\u8fd9\u4e2a\u547d\u8fd0\u2026\u2026\u201d\u7f57\u5170\u5ae3\u7136\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u6211\u8bb0\u4e0b\u4e86\uff0c\u5148\u8981\u81ea\u4fdd\uff0c\u6ca1\u628a\u63e1\uff0c\u7edd\u4e0d\u786c\u62fc\uff01\u201d \u3000\u3000\u5f20\u56db\u59d1\u5446\u5446\u7684\u671b\u7740\u7f57\u5170\uff0c\u9053\uff1a\u201c\u4f60\u7b11\u7684\u771f\u597d\u770b\uff01\u201d \u3000\u3000\u201c\u5bf9\u4e0d\u4f4f\u5566\uff01\u4e00\u6025\u5c31\u5fd8\u8bb0\uff0c\u4f60\u662f\u5973\u4eba\uff0c\u8ff7\u4f4f\u4f60\u4e5f\u4e0d\u8981\u7d27\uff0c\u53cd\u6b63\u6c5f\u67ab\u4e5f\u4e0d\u5728\u8fd9\u91cc\u3002\u201d \u3000\u3000\u201c\u7f57\u5170\uff0c\u660e\u5929\u4f60\u7684\u5bf9\u624b\uff0c\u5982\u679c\u662f\u7537\u4eba\uff0c\u5371\u6025\u7684\u65f6\u523b\uff0c\u5c31\u5bf9\u4ed6\u7b11\u4e00\u7b11\u3002\u201d \u3000\u3000\u201c\u65bd\u5c55\u5929\u72d0\u5a9a\u7b11\uff1f\u201d \u3000\u3000\u201c\u5bf9\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u8fd9\u672c\u6765\u5c31\u662f\u4e00\u79cd\u6b66\u529f\uff0c\u4f60\u627f\u7ee7\u5929\u72d0\u592b\u4eba\u8863\u94b5\uff0c\u5df2\u5165\u5316\u5883\uff0c\u9b45\u529b\u52fe\u9b42\u593a\u9b44\u3002\u201d \u3000\u3000\u201c\u6253\u7684\u4e00\u8eab\u81ed\u6c57\uff0c\u6c14\u5598\u5982\u725b\uff0c\u771f\u5e0c\u671b\u90a3\u8fb0\u5149\uff0c\u6211\u8fd8\u80fd\u7b11\u7684\u51fa\u6765\uff0c\u7b11\u5f97\u4ecd\u6709\u9b45\u529b\uff0c\u5982\u662f\u7b11\u7684\u6bd4\u54ed\u8fd8\u8981\u96be\u770b\uff0c\u88ab\u4eba\u780d\u4e86\u4e00\u5200\uff0c\u90a3\u5c31\u5f97\u4e0d\u507f\u5931\u4e86\u3002\u201d \u3000\u3000\u201c\u4e0d\u4f1a\u7684\uff0c\u7f57\u5170\uff0c\u4f60\u5df2\u7ec3\u7684\u7089\u706b\u7eaf\u9752\uff0c\u6211\u76f8\u4fe1\u968f\u65f6\u53ef\u4ee5\u65bd\u4e3a\uff0c\u5929\u72d0\u5a9a\u7b11\uff0c\u503e\u5012\u4f17\u751f\uff0c\u6253\u4e0d\u8fc7\uff0c\u5c31\u5bf9\u4ed6\u7b11\u5427\uff01\u4e0d\u8fc7\uff0c\u522b\u5fd8\u4e86\u628a\u63e1\u673a\u4f1a\u7ed9\u4ed6\u4e00\u5251\u3002\u201d \u3000\u3000\u7f57\u5170\u70b9\u70b9\u5934\u3002 \u3000\u3000\u5f20\u56db\u59d1\u4e5f\u89c2\u5bdf\u51fa\u4e86\u5fc3\u5f97\uff0c\u8fd9\u5929\u72d0\u5a9a\u7b11\uff0c\u6210\u4e8e\u81ea\u7136\uff0c\u53d1\u4e8e\u5fc3\u673a\uff0c\u8d8a\u662f\u6ca1\u6709\u5fc3\u673a\u7684\u4eba\uff0c\u8d8a\u6613\u6b65\u5165\u5316\u5883\u3002 \u3000\u3000\u96ea\u3001\u971c\u4e8c\u5973\uff0c\u7f8e\u4e3d\u5c24\u80dc\u7f57\u5170\uff0c\u53ef\u60dc\u662f\u5927\u806a\u660e\u4e86\uff0c\u4f46\u7f57\u5170\u5374\u5168\u65e0\u5fc3\u673a\uff0c\u6697\u5408\u5173\u8981\u6240\u5728\uff0c\u5f97\u5931\u5bf8\u5fc3\u95f4\uff0c\u8fd9\u79cd\u5947\u672f\uff0c\u4e8c\u4e2a\u5c0f\u72d0\u5973\u53ea\u6015\u662f\u6c38\u8fdc\u65e0\u6cd5\u8d76\u4e0a\u6bcd\u4eb2\u4e86\u3002 \u3000\u3000\u8981\u6765\u7684\uff0c\u7ec8\u4e8e\u6765\u4e86\u3002 \u3000\u3000\u65e5\u6b63\u5f53\u4e2d\u3002 \u3000\u3000\u5c11\u6797\u5bfa\u5916\u4e00\u7247\u7a7a\u9614\u7684\u8349\u5730\u4e0a\uff0c\u516d\u5ea7\u5341\u516b\u4eba\u7684\u7f57\u6c49\u9635\uff0c\u4e95\u7136\u6709\u5e8f\u7684\u6392\u5217\u7740\uff0c\u6563\u5e03\u6210\u4e00\u4e2a\u534a\u6708\u5f62\u3002 \u3000\u3000\u53ea\u770b\u5217\u5e03\u7684\u9635\u5f0f\uff0c\u4f3c\u4e4e\u662f\u91c7\u53d6\u5b88\u52bf\u3002 \u3000\u3000\u5728\u516d\u5ea7\u7f57\u6c49\u9635\u4e2d\u95f4\u7684\u7a7a\u4f4d\u4e0a\uff0c\u7531\u6539\u7a7f\u4e86\u4e00\u8eab\u52b2\u88c5\u7684\u6c5f\u67ab\uff0c\u5f53\u5148\u800c\u7acb\uff0c\u5de6\u4fa7\u662f\u5f20\u56db\u59d1\u548c\u6b27\u9633\u541f\u96ea\uff0c\u53f3\u8fb9\u662f\u767e\u660e\u65b9\u4e08\u548c\u767e\u5fcd\u5927\u5e08\u3002 \u3000\u3000\u6b27\u9633\u662d\u3001\u7f57\u5170\uff0c\u5e26\u7740\u4e94\u4e2a\u5251\u58eb\uff0c\u4e03\u5b9d\u548c\u5c1a\u3001\u8001\u53eb\u5316\u3001\u5201\u9e4f\u5341\u4e2a\u4eba\uff0c\u6392\u7acb\u5de6\u540e\uff0c\u4e00\u4e2a\u5c11\u6797\u7cbe\u9009\u51fa\u7684\u9ad8\u624b\uff0c\u6392\u5728\u53f3\u540e\u3002 \u3000\u3000\u541f\u971c\u3001\u6885\u82b1\u3001\u9752\u51e4\u3001\u80e1\u840d\u3001\u6bb5\u4e5d\u3001\u9093\u98de\u7b49\u90fd\u672a\u73b0\u8eab\uff0c\u5c11\u6797\u50e7\u4fa3\u4e5f\u6709\u5927\u90e8\u5206\uff0c\u4e0d\u5728\u73b0\u573a\u3002 \u3000\u3000\u4f46\u5bf9\u65b9\u7684\u9635\u5bb9\uff0c\u5c31\u5341\u5206\u5e9e\u5927\u4e86\uff0c\u800c\u4e14\uff0c\u65d7\u5e1c\u9c9c\u660e\u3002 \u3000\u3000\u5929\u738b\u95e8\u3001\u5929\u540e\u5bab\u3001\u5929\u9a6c\u5802\uff0c\u5404\u6210\u4e00\u4e2a\u4f53\u7cfb\uff0c\u5404\u95e8\u9996\u8111\uff0c\u5206\u96c6\u5404\u95e8\u65d7\u5e1c\u4e4b\u4e0b\uff0c\u8eab\u540e\u5404\u5217\u96c6\u5927\u6279\u7684\u95e8\u4e0b\u5c5e\u4f17\uff0c\u4ee5\u5929\u9a6c\u5802\u4eba\u6570\u6700\u591a\uff0c\u4e0d\u4e0b\u4e09\u767e\u4f59\u4eba\u3002 \u3000\u3000\u5929\u540e\u5bab\u7684\u4eba\u6570\u6700\u5c11\uff0c\u4e5f\u6709\u4e94\u5341\u4f59\u4eba\uff0c\u53ea\u6709\u4e24\u4e2a\u4e2d\u5e74\u7537\u4eba\uff0c\u4f59\u4e0b\u7684\u5168\u662f\u5973\u5b50\u3002 \u3000\u3000\u4f46\u5374\u4e0d\u89c1\u6c5f\u67ab\u6700\u5728\u610f\u7684\u795e\u79d8\u6559\u4e3b\u548c\u56db\u5927\u795e\u901a\u4f7f\u8005\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5927\u59d0\uff0c\u6559\u4e3b\u548c\u56db\u5927\u4f7f\u8005\u4e0d\u5728\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u5728\u6700\u597d\uff0c\u5148\u51fb\u6e83\u8fd9\u4e09\u4e2a\u7ec4\u5408\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u518d\u67e5\u51fa\u4ed6\u8001\u5de2\uff0c\u4e0d\u662f\u96be\u4e8b\uff0c\u8fd9\u6279\u722a\u7259\u6e83\u6563\uff0c\u4ed6\u5c31\u6ca1\u6709\u80fd\u529b\u675f\u7f1a\u6c5f\u6e56\u4e86\u3002\u201d \u3000\u3000\u201c\u5509\uff01\u654c\u4eba\u4f17\u591a\u2026\u2026\u201d\u6c5f\u67ab\u611f\u6168\u4e07\u5e72\u7684\u9053\uff1a\u201c\u6740\u8d77\u6765\u53ef\u771f\u8981\u5c38\u6a2a\u904d\u91ce\uff0c\u8840\u6d41\u6210\u6cb3\u4e86\u3002\u201d \u3000\u3000\u201c\u53ea\u6740\u9996\u8111\u3001\u5143\u51f6\uff0c\u6c5f\u6e56\u5df2\u65e0\u53ef\u7528\u4e4b\u4eba\uff0c\u8fd8\u8981\u7559\u4e0b\u4e00\u4e9b\u4eba\uff0c\u6062\u590d\u6c5f\u6e56\u518d\u751f\u4e4b\u673a\u5462\uff01\u201d\u5f20\u56db\u59d1\u4f4e\u58f0\u56de\u7b54\u3002 \u3000\u3000\u5927\u7ea6\u662f\u5929\u9a6c\u5802\u8d1f\u8d23\u4e86\u8fd9\u6b21\u56f4\u6740\u5927\u8ba1\uff0c\u6240\u4ee5\uff0c\u4ed6\u4eec\u5f53\u5148\u7684\u8fce\u4e86\u535c\u6765\u3002 \u3000\u3000\u590f\u5929\u540c\u5e26\u7740\u53f8\u5f92\u827a\u3001\u7533\u96ea\u541b\u3001\u4e07\u65b9\u4e09\u4e2a\u526f\u603b\u5802\u4e3b\uff0c\u7f13\u6b65\u884c\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u767d\u5929\u5316\u7d27\u968f\u5728\u590f\u5929\u540c\u7684\u53f3\u540e\u4fa7\uff0c\u5e26\u4e24\u4e2a\u8001\u8005\uff0c\u548c\u4e00\u4e2a\u4e00\u5934\u82b1\u767d\u5934\u53d1\u7684\u4e2d\u5e74\u5987\u4eba\u3002 \u3000\u3000\u8fd9\u4e9b\u4eba\u5404\u6709\u8fd1\u536b\uff0c\u4e8c\u81f3\u5341\u4f59\u4eba\u4e0d\u7b49\u3002 \u3000\u3000\u590f\u5929\u540c\u4e00\u4eba\u8eab\u540e\uff0c\u5c31\u968f\u7740\u5168\u8eab\u9ed1\u8863\u7684\u5341\u4e8c\u4f4d\u5251\u571f\u3002 \u3000\u3000\u7533\u96ea\u541b\u5e26\u7740\u56db\u4e2a\u52b2\u88c5\u6367\u5251\u7684\u5973\u5a62\u3002 \u3000\u3000\u53f8\u5f92\u827a\u4e5f\u7531\u4e24\u4f69\u5200\u4e2d\u5e74\u4eba\u7d27\u968f\u5de6\u53f3\u3002 \u3000\u3000\u53ea\u6709\u4e00\u638c\u62db\u9b42\u4e07\u65b9\uff0c\u5149\u68cd\u4e00\u6761\u3002 \u3000\u3000\u798f\u5bff\u9662\u4e3b\u767d\u5929\u5316\uff0c\u7531\u56db\u4e2a\u6e05\u79c0\u7ae5\u5b50\u76f8\u968f\u3002 \u3000\u3000\u4e24\u4e2a\u8005\u8005\uff0c\u548c\u90a3\u4e2d\u5e74\u5987\u4eba\uff0c\u4e5f\u5404\u5e26\u4e8c\u4e2a\u4ece\u4eba\u3002 \u3000\u3000\u5201\u9e4f\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u767d\u5929\u5316\u8eab\u8fb9\u7684\u4e09\u4e2a\u4eba\uff0c\u662f\u516d\u5927\u697c\u4e3b\u4e2d\u7684\u4e09\u4e2a\uff0c\u7537\u7684\u662f\u671d\u9633\u3001\u6c34\u6708\u697c\u4e3b\uff0c\u90a3\u4e2a\u5973\u7684\u662f\u955c\u82b1\u697c\u697c\u4e3b\uff0c\u9b41\u5143\u697c\u4e3b\u6881\u9b41\u5df2\u6b7b\uff0c\u9664\u4e86\u4e03\u661f\u3001\u4e07\u8c61\u4e24\u4f4d\u697c\u4e3b\uff0c\u4e0d\u89c1\u73b0\u8eab\u5916\uff0c\u5929\u9a6c\u5802\u7684\u7cbe\u9510\u5168\u6765\u4e86\u3002\u201d \u3000\u3000\u6c5f\u67ab\u70b9\u70b9\u5934\u3002 \u3000\u3000\u539f\u6765\uff0c\u8981\u5201\u9e4f\u7ad9\u5728\u8eab\u4fa7\uff0c\u5c31\u662f\u8981\u4ed6\u968f\u65f6\u63d0\u51fa\u5929\u9a6c\u5802\u7684\u5f62\u52bf\u8bc6\u660e\u3002 \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u5927\u59d0\uff0c\u5e94\u8be5\u5f00\u59cb\u4e86\u3002\u201d \u3000\u3000\u4e3e\u6b65\u5411\u524d\u8fce\u53bb\uff0c\u541f\u96ea\u7d27\u968f\u8eab\u540e\u3002 \u3000\u3000\u597d\u5bb6\u4f19\uff0c\u771f\u662f\u80c6\u5927\u5984\u4e3a\uff0c\u4eba\u5bb6\u4e3b\u4ece\u4e00\u4f19\uff0c\u4e09\u56db\u5341\u4e2a\u4eba\uff0c\u4f46\u51c6\u5907\u63a5\u6218\u7684\u53ea\u662f\u6c5f\u67ab\u3001\u541f\u96ea\u4e24\u4e2a\u3002 \u3000\u3000\u5148\u4e0d\u8bba\u52a8\u4e0a\u624b\u7684\u80dc\u8d25\uff0c\u53ea\u770b\u4e24\u4e2a\u52bf\u5b64\u4eba\u5355\u7684\u5f71\u5b50\uff0c\u5c31\u7ed9\u4eba\u4e00\u79cd\u72ec\u7acb\u5343\u5bfb\u5c71\uff0c\u9a6c\u5578\u8fce\u897f\u98ce\u7684\u8c6a\u8fc8\uff0c\u5e26\u7740\u4e00\u79cd\u82cd\u51c9\u65e0\u6094\u7684\u52c7\u6c14\uff0c\u770b\u7684\u4ee4\u4eba\u656c\u4f69\u3001\u5fc3\u9178\u3002 \u3000\u3000\u201c\u6c5f\u67ab\uff0c\u6559\u800c\u540e\u8bdb\u4e4b\uff0c\u522b\u5fd8\u4e86\u4e0d\u6218\u800c\u5c48\u4eba\u4e4b\u5175\u554a\uff01\u201d\u5f20\u56db\u59d1\u53cc\u76ee\u6d8c\u73b0\u4e86\u6cea\u6c34\u3002 \u3000\u3000\u541f\u96ea\u7a7f\u4e86\u4e00\u4ef6\u96ea\u767d\u7684\u957f\u886b\uff0c\u9ed1\u5e15\u5305\u53d1\uff0c\u4e2d\u675f\u4e86\u4e00\u6761\u9ed1\u8272\u7684\u8170\u5e26\uff0c\u9ed1\u767d\u5206\u660e\uff0c\u957f\u886b\u98d8\u98ce\uff0c\u6709\u5982\u8fce\u5411\u98ce\u96e8\u4e2d\u7684\u4e00\u682a\u767e\u5408\u82b1\u3002 \u3000\u3000\u201c\u963f\u5f25\u9640\u4f5b\uff0c\u8fd9\u5927\u6982\u5c31\u662f\u867d\u5343\u4e07\u4eba\u543e\u5f80\u77e3\u7684\u5927\u52c7\u4e4b\u6c14\u4e86\u3002\u201d\u767e\u660e\u8001\u65b9\u4e08\u53cc\u76ee\u4e2d\u7adf\u4e5f\u6709\u70b9\u6e7f\u6da6\u3002 \u3000\u3000\u201c\u662f\u4e00\u5e45\u597d\u7f8e\u3001\u597d\u7f8e\u7684\u753b\u9762\u2026\u2026\u201d\u7f57\u5170\u9053\uff1a\u201c\u53ea\u6709\u753b\u4e2d\u4e4b\u5723\u5434\u9053\u5b50\u91cd\u751f\u8fd8\u9b42\uff0c\u624d\u80fd\u753b\u51fa\u8fd9\u79cd\u51c4\u51c9\u4e4b\u7f8e\u7684\u795e\u97f5\u3002\u201d \u3000\u3000\u201c\u53ea\u6b64\u6c14\u6982\uff0c\u5c31\u80fd\u9707\u70c1\u5343\u53e4\uff0c\u632b\u654c\u6218\u5fd7\uff0c\u5927\u82f1\u96c4\u5f53\u5982\u662f\u4e5f\u3002\u201d\u6b27\u5339\u662d\u5fae\u7b11\u4f4e\u8bed\uff0c\u76ee\u5149\u4e2d\u662f\u65e0\u9650\u7684\u656c\u4f69\u548c\u7fa1\u6155\u3002 \u3000\u3000\u679c\u7136\uff0c\u590f\u5929\u540c\u7b49\u4e00\u884c\uff0c\u4f3c\u662f\u88ab\u9707\u4f4f\u4e86\uff0c\u81f3\u5c11\u662f\u5fc3\u4e2d\u60ca\u5947\u4e0d\u5df2\uff0c\u505c\u4e0b\u4e86\u811a\u6b65\u3002 \u3000\u3000\u76f4\u903c\u654c\u4eba\u4e94\u5c3a\u5904\uff0c\u624d\u505c\u4f4f\u8eab\u5b50\uff0c\u9053\uff1a\u201c\u6211\u662f\u6c5f\u67ab\u3002\u201d \u3000\u3000\u590f\u5929\u540c\u7a7f\u7740\u4e00\u4ef6\u6df1\u9752\u957f\u886b\uff0c\u4e00\u6761\u9ed1\u8272\u7684\u56f4\u5dfe\uff0c\u63a9\u4f4f\u4e86\u5927\u534a\u4e2a\u9762\u76ee\uff0c\u957f\u957f\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a \u3000\u3000\u201c\u6b66\u6797\u4e09\u5723\u7684\u4f20\u4eba\uff1f\u201d \u3000\u3000\u201c\u6b63\u662f\u533a\u533a\uff01\u201d \u3000\u3000\u201c\u5c31\u7b97\u6b66\u6797\u4e09\u5723\u590d\u751f\uff0c\u4e5f\u4e0d\u6562\u5982\u6b64\u6258\u5927\uff0c\u4f60\u5374\u53ea\u5e26\u4e00\u4e2a\u5c0f\u5973\u5b69\u5b50\u2026\u2026\u201d\u590f\u5929\u540c\u9053\uff1a\u201c\u6765\u5bf9\u6297\u6c5f\u6e56\u4e0a\u6570\u5341\u4f4d\u9ad8\u624b\uff1f\u201d \u3000\u3000\u201c\u662f\u554a\uff01\u4e24\u4e2a\u6bdb\u5b69\u5b50\u4e00\u4e2a\u8fd8\u662f\u5c0f\u4e2b\u5934\uff0c\u4e5f\u6562\u5192\u8bfb\u603b\u5802\u4e3b\u7684\u795e\u5a01\u2026\u2026\u201d\u4e07\u65b9\u51b7\u51b7\u63a5\u9053\uff1a \u3000\u3000\u201c\u5c31\u7b97\u4f60\u4eec\u662f\u94c1\u6253\u94dc\u9976\u7684\u4eba\uff0c\u4e5f\u8981\u4f60\u4eec\u5316\u4f5c\u6d41\u6c41\u98de\u7070\u3002\u201d \u3000\u3000\u6c5f\u67ab\u51b7\u51b7\u7684\u770b\u4e86\u4e07\u65b9\u4e00\u773c\uff0c\u6ca1\u6709\u7406\u4ed6\u3002 \u3000\u3000\u541f\u96ea\u5374\u5ae3\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e07\u8001\u5934\uff0c\u4f60\u51fa\u8a00\u4e0d\u900a\uff0c\u4e00\u52a8\u4e0a\u624b\uff0c\u6211\u5148\u6740\u4f60\u3002\u201d \u3000\u3000\u5979\u7b11\u7684\u5982\u82b1\u76db\u653e\uff0c\u9b45\u529b\u56db\u5c04\uff0c\u590f\u5929\u540c\u7b49\u4e00\u822c\u8001\u9b54\u5934\uff0c\u867d\u7136\u5b9a\u529b\u6df1\u539a\uff0c\u4e5f\u4e0d\u7981\u770b\u7684\u4e00\u5446\u3002 \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u590f\u5929\u540c\uff0c\u4e00\u65e6\u52a8\u4e0a\u624b\uff0c\u5c31\u662f\u4e2a\u8840\u8089\u6a2a\u98de\u7684\u4e0d\u4e86\u4e4b\u5c40\uff0c\u52a8\u624b\u4e4b\u524d\uff0c\u6211\u6709\u4e24\u4ef6\u4e8b\u60c5\u8bf7\u6559\uff01\u201d \u3000\u3000\u590f\u5929\u540c\u76ee\u5149\u8f6c\u52a8\uff0c\u89c1\u5929\u738b\u95e8\u3001\u5929\u540e\u5bab\u7684\u4eba\uff0c\u5e76\u672a\u5408\u56f4\u4e0a\u6765\uff0c\u5fc3\u77e5\u4ed6\u4eec\u4e0d\u4f1a\u63d2\u624b\u4e86\uff0c\u8fd9\u4e00\u4ed7\u5168\u8981\u5929\u9a6c\u5802\u72ec\u529b\u652f\u6491\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u5bf9\u65b9\u53ea\u6709\u4e24\u4e2a\u4eba\u51c6\u5907\u8fce\u6218\uff0c\u5929\u9a6c\u5802\u5df2\u4eba\u624b\u4f17\u591a\uff0c\u522b\u4eba\u4e5f\u4e0d\u77e5\u5982\u4f55\u63d2\u624b\u4e86\u3002 \u3000\u3000\u201c\u597d\uff01\u4f60\u8bf4\u5427\uff01\u201d\u590f\u5929\u540c\u4f30\u91cf\u8fc7\u73b0\u573a\u5f62\u52bf\u4e4b\u540e\uff0c\u9053\uff1a\u201c\u65e2\u662f\u8bf7\u6559\uff0c\u8001\u592b\u5c31\u4e0d\u4e00\u5b9a\u56de\u7b54\u4f60\u4e86\u3002\u201d \u3000\u3000\u679c\u7136\u662f\u8001\u5978\u7684\u5f88\u3002 \u3000\u3000\u6c5f\u67ab\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5148\u8bf4\u7b2c\u4e00\u4ef6\u4e8b\uff0c\u4f60\u4e00\u76f4\u7528\u5e03\u5dfe\u63a9\u4f4f\u4e86\u5927\u534a\u4e2a\u9762\u76ee\uff0c\u7a76\u7adf\u4e3a\u4e86\u4ec0\u4e48\uff1f\u4eca\u65e5\u4e4b\u6218\uff0c\u751f\u6b7b\u5bf9\u51b3\uff0c\u4eec\u5df2\u7528\u4e0d\u7740\u518d\u63a9\u63a9\u906e\u906e\uff0c\u4f55\u4e0d\u8ba9\u6211\u4e00\u7779\u5e90\u5c71\u771f\u9762\u76ee\uff1f\u201d \u3000\u3000\u5929\u9a6c\u5802\u4e2d\u4eba\uff0c\u5f88\u5c11\u89c1\u8fc7\u590f\u5929\u540c\u7684\u771f\u6b63\u9762\u76ee\uff0c\u5728\u573a\u4e4b\u4eba\uff0c\u4f3c\u4e4e\u53ea\u6709\u7533\u96ea\u541b\u89c1\u8fc7\uff0c\u8fde\u53f8\u9a6c\u827a\u548c\u767d\u5929\u5316\uff0c\u4f3c\u662f\u90fd\u672a\u89c1\u8fc7\u3002 \u3000\u3000\u8fd9\u5012\u662f\u4e00\u4ef6\u5341\u5206\u5f15\u4eba\u5165\u80dc\u7684\u4e8b\uff0c\u6240\u6709\u4eba\u7684\u76ee\u5149\uff0c\u90fd\u6295\u6ce8\u5728\u590f\u592d\u540c\u7684\u8eab\u4e0a\u3002 \u3000\u3000\u201c\u8fd9\u771f\u662f\u4e00\u4e2a\u975e\u5e38\u5927\u7684\u79d8\u5bc6\uff0c\u8fde\u672c\u5802\u4e2d\u4eba\uff0c\u90fd\u4e0d\u77e5\u9053\u539f\u56e0\u2026\u2026\u201d \u3000\u3000\u590f\u5929\u540c\u9053\uff1a\u201c\u8001\u592b\u53ef\u4ee5\u7ed9\u4f60\u4e2a\u6b63\u786e\u56de\u7b54\uff0c\u8fd9\u4fdd\u6709\u6570\u5341\u5e74\u7684\u79d8\u5bc6\uff0c\u5c31\u7acb\u523b\u63ed\u7a7f\u2026\u2026\u201d \u3000\u3000\u201c\u90a3\u5c31\u591a\u8c22\u590f\u603b\u5802\u4e3b\u4e86\uff01\u201d \u3000\u3000\u201c\u53ef\u662f\u6709\u6761\u4ef6\uff01\u201d \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u8bf4\u8bf4\u770b\uff1f\u201d \u3000\u3000\u201c\u8001\u592b\u4e5f\u6709\u4e00\u4e2a\u95ee\u9898\u5411\u4f60\uff1f\u4f46\u4f60\u5fc5\u9700\u8981\u636e\u5b9e\u56de\u7b54\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u201d \u3000\u3000\u590f\u5929\u540c\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6700\u597d\u7684\u56de\u7b54\uff0c\u5c31\u662f\u8001\u592b\u53d6\u4e0b\u56f4\u9762\u9ed1\u4e2d\uff0c\u8ba9\u4f60\u4e00\u7779\u5e90\u5c71\u771f\u9762\u76ee\uff0c\u5bf9\u5417\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u70b9\u70b9\u5934\u3002 \u3000\u3000\u590f\u5929\u540c\u4e3e\u624b\u62c9\u5f00\u4e86\u8499\u9762\u9ed1\u4e2d\u3002 \u3000\u3000\u7533\u96ea\u541b\u6ca1\u6709\u770b\uff0c\u522b\u8fc7\u5934\u53bb\uff0c\u8f7b\u8f7b\u53f9\u606f\u4e00\u58f0\u3002 \u3000\u3000\u6c5f\u67ab\u5374\u770b\u7684\u4e00\u5446\u3002 \u3000\u3000\u6b27\u9633\u541f\u96ea\u66f4\u662f\u770b\u7684\u82b1\u5bb9\u53d8\u8272\u3002 \u3000\u3000\u767d\u5929\u5316\u3001\u53f8\u5f92\u827a\uff0c\u4e5f\u90fd\u51fa\u73b0\u60ca\u5bb9\u3002 \u3000\u3000\u90a3\u5f20\u7751\u8bf4\u4e0d\u4e0a\u6050\u6016\uff0c\u53ea\u662f\u592a\u5947\u602a\u4e86\uff0c\u4eba\u662f\u4e0d\u53ef\u80fd\u751f\u51fa\u90a3\u4e48\u5947\u602a\u7684\u4e00\u5f20\u8138\u3002 \u3000\u3000\u590f\u5929\u540c\u5f88\u5feb\u56f4\u597d\u9ed1\u4e2d\uff0c\u9053\uff1a\u201c\u7b97\u4e0d\u7b97\u4e00\u4e2a\u5927\u7684\u79d8\u5bc6\u5462\uff1f\u201d \u3000\u3000\u201c\u662f\uff01\u5728\u4e0b\u8fde\u505a\u68a6\u4e5f\u68a6\u4e0d\u5230\u7684\u4e8b\uff01\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u5b83\u4e0d\u662f\u5929\u751f\u7684\u5427\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u4e0d\u662f\uff0c\u8fd9\u662f\u4ec7\u6068\u7684\u6807\u5e1c\uff0c\u91d1\u4e39\u4e66\u751f\u8fd9\u4e00\u6869\u7f6a\u6076\uff0c\u7ec8\u5176\u4e00\u751f\u884c\u5584\u52a9\u4eba\uff0c\u4e5f\u662f\u633d\u6551\u4e0d\u6765\u4e86\u2026\u2026\u201d\u590f\u5929\u540c\u9053\uff1a\u201c\u4f60\u8fd8\u6709\u4ec0\u4e48\u8981\u95ee\u7684\uff0c\u4e5f\u8981\u7b49\u8001\u592b\u5148\u95ee\u8fc7\u4f60\u518d\u8bf4\u3002\u201d \u3000\u3000\u201c\u8bf7\uff01\u201d \u3000\u3000\u201c\u9664\u4e86\u6b66\u6797\u4e09\u5723\u4f20\u4f60\u7684\u6b66\u529f\u4e4b\u5916\uff0c\u4f60\u8fd8\u6709\u4ec0\u4e48\u4ed7\u6043\uff0c\u7adf\u6562\u5982\u6b64\u7684\u72c2\u5984\uff01\u201d\u590f\u5929\u540c\u9053\uff1a \u3000\u3000\u201c\u4ee5\u4e24\u4e2a\u4eba\u7684\u529b\u91cf\uff0c\u6562\u8fce\u6218\u6b66\u6797\u6570\u5341\u9ad8\u624b\uff1f\u201d \u3000\u3000\u201c\u6c5f\u67d0\u786e\u5b66\u4e86\u6b66\u6797\u4e09\u5723\u4f20\u6388\u4ee5\u5916\u7684\u6b66\u529f\uff0c\u800c\u4e14\uff0c\u5305\u7f57\u751a\u5e7f\u3002\u201d \u3000\u3000\u590f\u5929\u540c\u5fae\u5fae\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\u4f20\u6388\u7ed9\u4f60\u7684\uff1f\u8001\u592b\u60f3\u4e0d\u901a\uff0c\u8fd1\u767e\u5e74\u6765\uff0c\u6b66\u6797\u4e2d\u8fd8\u6709\u4ec0\u4e48\u4eba\u9ad8\u8fc7\u6b66\u6797\u4e09\u5723\uff1f\u201d \u3000\u3000\u201c\u4e0d\u662f\u4e0d\u60f3\u8bf4\uff0c\u800c\u662f\u8bf4\u4e0d\u6e05\u695a\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u603b\u4e4b\uff0c\u6211\u4e00\u8eab\u6280\u827a\uff0c\u5341\u5206\u535a\u6742\uff0c\u6740\u4eba\u7684\u624b\u6bb5\uff0c\u4e5f\u975e\u5e38\u51cc\u5389\uff0c\u4ee5\u4f60\u590f\u603b\u5802\u4e3b\u8eab\u4efd\u4e4b\u5c0a\uff0c\u4e0d\u7ba1\u5fc3\u4e2d\u6709\u591a\u5c11\u4ec7\u6068\uff0c\u4f46\u91d1\u4e39\u4e66\u751f\u5df2\u6b7b\uff0c\u8fd9\u4e2a\u4ec7\u6068\uff0c\u4e5f\u5e94\u8be5\u6d88\u9000\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u8001\u592b\u5728\u8c08\u4f60\u7684\u6b66\u529f\uff0c\u4f60\u8fd8\u672a\u636e\u5b9e\u56de\u7b54\u5462\uff1f\u201d\u590f\u5929\u540c\u7684\u53cc\u76ee\u4e2d\u5ffd\u7136\u6cdb\u8d77\u4e00\u51b7\u91cd\u7684\u6df1\u7ea2\u989c\u8272\uff0c\u4f3c\u662f\u8981\u6ef4\u51fa\u8840\u6765\u3002 \u3000\u3000\u201c\u6c5f\u5927\u54e5\uff0c\u5c0f\u5fc3\u554a\uff01\u201d\u541f\u96ea\u5a07\u8eaf\u5fae\u6643\uff0c\u4eba\u5df2\u548c\u6c5f\u67ab\u4e95\u80a9\u800c\u7acb\u3002 \u3000\u3000\u6c5f\u67ab\u539f\u672c\u751f\u51fa\u7684\u4e00\u80a1\u540c\u60c5\u4e4b\u5fc3\uff0c\u987f\u7136\u6d88\u5931\uff0c\u4e00\u63d0\u771f\u6c14\uff0c\u4e00\u8eab\u52b2\u88c5\uff0c\u7a81\u7136\u9f13\u80c0\u8d77\u6765\uff0c\u5934\u9876\u4e0a\u4e5f\u6cdb\u8d77\u4e00\u7247\u767d\u6c14\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6b66\u6797\u4e09\u5723\u53ef\u90fd\u662f\u6b7b\u5728\u4f60\u7684\u624b\u4e2d\uff1f\u4f60\u8fd9\u4e00\u751f\u4e2d\u53c8\u6b8b\u5bb3\u4e86\u591a\u5c11\u6b66\u6797\u540c\u9053\uff1f\u90a3\u4f4d\u9b3c\u9b3c\u795f\u795f\u7684\u6559\u4e3b\u5462\uff1f\u56db\u5927\u795e\u901a\u4f7f\u8005\u5462\uff1f\u4e3a\u4ec0\u4e48\u4e0d\u80af\u73b0\u8eab\uff0c\u4f60\u5982\u80af\u4ed4\u7ec6\u7684\u60f3\u4e00\u60f3\uff0c\u5c31\u8be5\u660e\u767d\uff0c\u4f60\u53ea\u4e0d\u8fc7\u662f\u522b\u4eba\u624b\u4e00\u679a\u68cb\u5b50\uff0c\u800c\u4e14\u662f\u8fc7\u6cb3\u5352\u5b50\uff0c\u6709\u8fdb\u65e0\u56de\u2026\u2026\u201d \u3000\u3000\u201c\u95ee\u9898\u662f\u8c01\u80fd\u6740\u4e86\u8001\u592b\uff1f\u201d\u590f\u5929\u540c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8001\u592b\u786e\u5b9e\u88ab\u4eba\u5229\u7528\u4e86\uff0c\u4f46\u4ed6\u4eec\u4e5f\u5e2e\u6211\u5efa\u7acb\u8d77\u5929\u9a6c\u5802\u7684\u5b9e\u529b\uff0c\u8001\u592b\u5e76\u4e0d\u5403\u4e8f\u554a\uff01\u201d \u3000\u3000\u6c5f\u67ab\u5251\u7709\u8038\u52a8\uff0c\u4f7f\u76ee\u653e\u5149\uff0c\u6012\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u5c31\u80fd\u6740\u4f60\uff0c\u4f60\u7684\u8680\u5fc3\u9b54\u97f3\uff0c\u4e5f\u65e0\u6cd5\u63a7\u5236\u8fd9\u4e9b\u4e3a\u4f60\u6548\u547d\u7684\u6c5f\u6e56\u540c\u9053\uff0c\u522b\u5fd8\u4e86\u65e0\u76f8\u5927\u5e08\u4f20\u7559\u4e0b\u7684\u5927\u60b2\u68b5\u5531\uff0c\u662f\u4f60\u8680\u5fc3\u9b54\u97f3\u7684\u514b\u661f\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u8001\u592b\u5012\u4e0d\u6000\u7591\uff0c\u4f46\u4f60\u6ca1\u6709\u673a\u4f1a\u65bd\u5c55\u5927\u60b2\u68b5\u5531\uff0c\u90a3\u662f\u96c6\u4e2d\u5168\u529b\u65bd\u5c55\u7684\u5185\u529f\uff0c\u6211\u4e0d\u4fe1\u4f60\u80fd\u4e00\u9762\u52a8\u624b\uff0c\u4e00\u9762\u65bd\u5c55\u5927\u60b2\u68b5\u5531\u2026\u2026\u201d \u3000\u3000\u590f\u5929\u540c\u8bdd\u6b3e\u8bf4\u5b8c\uff0c\u4e00\u7f15\u68b5\u97f3\uff0c\u7a81\u7136\u4f20\u6765\u3002 \u3000\u3000\u58f0\u97f3\u4e0d\u662f\u5f88\u5927\uff0c\u4f46\u6bcf\u4e2a\u4eba\u90fd\u542c\u5f97\u5341\u5206\u6e05\u695a\u3002 \u3000\u3000\u4e09\u5ea7\u5c11\u6797\u5bfa\u7684\u7f57\u6c49\u9635\u7a81\u7136\u79fb\u52a8\uff0c\u5206\u6321\u5728\u4e03\u5b9d\u548c\u5c1a\u7684\u5de6\u3001\u53f3\u540e\uff0c\u4e09\u4e2a\u65b9\u4f4d\uff0c\u6b27\u9633\u662d\u957f\u5251\u51fa\u9798\uff0c\u914d\u5408\u7740\u4e94\u4e2a\u5f1f\u5b50\u7684\u8eab\u8eaf\u79fb\u8f6c\uff0c\u6321\u5728\u4e86\u4e03\u5b9d\u548c\u5c1a\u7684\u6b63\u9762\u3002 \u3000\u3000\u4f46\u7f57\u5170\u548c\u5f20\u56db\u59d1\u5374\u540c\u65f6\u79fb\u6b65\uff0c\u9041\u51fa\u9635\u5916\uff0c\u7f57\u5170\u548c\u6b27\u9633\u662d\u53cc\u5251\u8054\u8882\u800c\u7acb\u3002 \u3000\u3000\u53e6\u5916\u4e09\u5ea7\u7f57\u6c49\u9635\u4e5f\u5728\u79fb\u52a8\uff0c\u5206\u522b\u8f6c\u6392\u4e8e\u4e24\u4fa7\u3002 \u3000\u3000\u767e\u660e\u3001\u767e\u5fcd\u5927\u5e08\u548c\u4e00\u4f4d\u5c11\u6797\u9ad8\u50e7\uff0c\u7adf\u81ea\u5e76\u5165\u4e86\u6392\u5217\u4e24\u4fa7\u7684\u7f57\u9635\u4e2d\uff0c\u5341\u4e8c\u4eba\u5206\u5c45\u4e09\u9635\u4e4b\u4e2d\uff0c\u5e76\u672a\u7834\u574f\u539f\u6709\u9635\u52bf\u3002 \u3000\u3000\u9635\u52bf\u79fb\u52a8\u5982\u8f6c\u8f6e\u6ed1\u6eda\uff0c\u4e95\u7136\u6709\u5e8f\uff0c\u4f46\u770b\u67b6\u5f0f\uff0c\u5374\u5b8c\u5168\u662f\u91c7\u53d6\u5b88\u52bf\u3002 \u3000\u3000\u68b5\u97f3\u98d8\u6e3a\u56db\u5916\uff0c\u4e03\u5b9d\u5927\u5e08\u5408\u638c\u800c\u7acb\uff0c\u6cd5\u50cf\u6108\u89c1\u5e84\u4e25\u3002 \u3000\u3000\u4e03\u5b9d\u5927\u5e08\u5df2\u4e0d\u662f\u5e73\u65e5\u559c\u611f\u5341\u8db3\u7684\u9152\u8089\u548c\u5c1a\uff0c\u800c\u4f3c\u4e00\u5c0a\u6d8c\u73b0\u800c\u51fa\u7684\u7f57\u6c49\uff0c\u53cc\u76ee\u5982\u5b9a\uff0c\u4f5b\u5149\u9690\u7136\u3002 \u3000\u3000\u5305\u62ec\u590f\u5929\u540c\u3001\u767d\u5929\u5316\u5728\u5185\uff0c\u5929\u5802\u4e2d\u4eba\uff0c\u90fd\u4e0d\u7981\u5446\u4f4f\u4e86\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u5929\u738b\u95e8\u3001\u5929\u540e\u5bab\u4e2d\u7684\u4eba\u4e5f\u6709\u7740\u5f88\u5927\u7684\u611f\u5e94\uff0c\u5fc3\u4e2d\u6740\u4f10\u4e4b\u5ff5\uff0c\u6e10\u6e10\u6d88\u9000\u3002 \u3000\u3000\u53ea\u4e0d\u8fc7\uff0c\u5929\u9a6c\u5802\u4e2d\u4eba\u7684\u611f\u5e94\u6700\u4e3a\u5f3a\u70c8\uff0c\u4f3c\u662f\u5fc3\u7075\u4e0a\u7684\u4e00\u79cd\u65e0\u5f62\u675f\u7f1a\uff0c\u6b63\u9010\u6e10\u5f00\u59cb\u8212\u89e3\uff0c\u672c\u6027\u6e10\u9732\u3002 \u3000\u3000\u8001\u53eb\u5316\u548c\u4e5d\u5934\u9e1f\uff0c\u5206\u7acb\u5728\u4e03\u5b9d\u5927\u5e08\u7684\u4e24\u4fa7\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u5927\u4e0d\u76f8\u540c\uff0c\u94b1\u7f3a\u662f\u4fa0\u809d\u4e49\u80c6\uff0c\u6c14\u8282\u51db\u7136\u7684\u6c5f\u6e56\u5927\u4fa0\u3002 \u3000\u3000\u5201\u9e4f\u662f\u53cc\u624b\u8840\u8165\uff0c\u4f5c\u6076\u591a\u7aef\u7684\u7eff\u6797\u532a\u5f92\uff0c\u4f46\u73b0\u5728\u4e24\u4e2a\u4eba\u4e00\u822c\u7684\u795e\u8272\u8083\u7a46\uff0c\u90fd\u6709\u7740\u5165\u5730\u72f1\uff0c\u4e0a\u5200\u5c71\uff0c\u820d\u6211\u5176\u8c01\u7684\u6c14\u6168\u3002 \u3000\u3000\u590f\u5929\u540c\u7a81\u7136\u5927\u559d\u4e00\u58f0\uff1a\u201c\u6740\uff01\u201d\u53f3\u624b\u4e00\u6325\uff0c\u8eab\u540e\u5341\u4e8c\u4e2a\u9ed1\u8863\u5251\u571f\uff0c\u4e00\u9f50\u51b2\u4e86\u51fa\u6765\uff0c\u56f4\u4f4f\u4e86\u6c5f\u67ab\u3001\u541f\u96ea\u3002 \u3000\u3000\u541f\u96ea\u5251\u4ea6\u51fa\u9798\uff0c\u662f\u4e00\u628a\u957f\u7ea6\u4e24\u5c3a\u56db\u5bf8\u5de6\u53f3\uff0c\u6bd4\u4e00\u822c\u4e09\u5c3a\u516d\u5bf8\u7684\u957f\u5251\uff0c\u77ed\u4e86\u4e00\u5c3a\u4e8c\u5bf8\u3002 \u3000\u3000\u4f46\u6bd4\u6240\u8c13\u4e00\u5c3a\u4e8c\u5bf8\u7684\u77ed\u5251\uff0c\u5374\u53c8\u957f\u4e86\u4e00\u500d\u3002 \u3000\u3000\u6c5f\u67ab\u7ad9\u5f97\u7a33\u5982\u6cf0\u5c71\uff0c\u52a8\u4e5f\u672a\u52a8\u4e00\u4e0b\u3002 \u3000\u3000\u56e0\u4e3a\uff0c\u5341\u4e8c\u4e2a\u9ed1\u8863\u5251\u571f\uff0c\u51b2\u4e0a\u6765\u7684\u901f\u5ea6\u4e0d\u5feb\uff0c\u5251\u867d\u65e9\u5df2\u79bb\u9798\uff0c\u4f46\u5374\u672a\u523a\u5411\u6c5f\u67ab\u3002\u53f6\u96ea\u4e5f\u672a\u52a8\u3002 \u3000\u3000\u5982\u662f\u534a\u5e74\u4e4b\u524d\uff0c\u541f\u96ea\u7edd\u65e0\u6cd5\u63a7\u5236\u7684\u5982\u6b64\u4e4b\u7a33\uff0c\u65e9\u5df2\u6325\u5251\u8fce\u4e86\u4e0a\u53bb\uff0c\u7528\u4e0d\u7740\u90a3\u4e9b\u9ed1\u8863\u5251\u58eb\u51fa\u624b\uff0c\u53cc\u65b9\u65e9\u5df2\u6253\u5728\u4e86\u4e00\u8d77\u3002 \u3000\u3000\u73b0\u5728\uff0c\u5df2\u7ecf\u662f\u6740\u7684\u6fc0\u70c8\u7edd\u4f26\u4e86\u3002 \u3000\u3000\u53ef\u662f\u6b64\u65f6\u7684\u541f\u96ea\uff0c\u5df2\u662f\u9ad8\u624b\u4e2d\u7684\u9ad8\u624b\u4e86\uff0c\u80fd\u5728\u5343\u94a7\u4e00\u53d1\u4e2d\u63a7\u5236\u4f4f\u81ea\u5df1\u3002 \u3000\u3000\u6c5f\u67ab\u66f4\u662f\u65e9\u6d1e\u5931\u673a\uff0c\u53d1\u89c9\u4e86\u5341\u4e8c\u5251\u58eb\uff0c\u8eab\u4e0a\u4e0d\u73b0\u6740\u6c14\uff0c\u5fc3\u4e2d\u65e9\u65e0\u6740\u610f\uff0c\u4eba\u867d\u5e94\u547d\u800c\u4e0a\uff0c\u957f\u5251\u5374\u672a\u653b\u654c\u3002 \u3000\u3000\u5f53\u7136\uff0c\u6c5f\u67ab\u5fc3\u4e2d\u4e5f\u5145\u6ee1\u81ea\u4fe1\uff0c\u8fd9\u4e9b\u4eba\u5c31\u7b97\u771f\u8981\u51fa\u5251\u653b\u51fb\uff0c\u4e5f\u65e0\u6cd5\u4f24\u5f97\u4e86\u4ed6\u3002 \u3000\u3000\u201c\u597d\uff01\u5927\u4e2b\u5934\u6c89\u5f97\u4f4f\u6c14\u2026\u2026\u201d\u7f57\u5170\u9053\uff1a\u201c\u597d\u50cf\u6bd4\u6211\u8fd8\u5f3a\u4e0a\u4e09\u5206\u4e86\u3002\u201d \u3000\u3000\u201c\u541f\u96ea\u9020\u8be3\u4e4b\u9ad8\uff0c\u6050\u5df2\u975e\u6211\u4eec\u80fd\u53ca\u2026\u2026\u201d\u6b27\u9633\u662d\u9053\uff1a\u201c\u6c5f\u67ab\u6599\u654c\u673a\u5148\uff0c\u65e9\u5df2\u77a7\u51fa\u4ed6\u4eec\u4e0d\u4f1a\u51fa\u5251\uff0c\u5fc3\u4e2d\u67af\u4e95\u4e0d\u6ce2\uff0c\u8fde\u8863\u8882\u4e5f\u672a\u52a8\u8361\u4e00\u4e0b\uff0c\u5927\u4e2b\u5934\u5251\u52bf\u6b32\u51fa\u53c8\u6b62\uff0c\u7adf\u4e5f\u80fd\u4e0d\u7740\u75d5\u8ff9\uff0c\u8fd9\u4e00\u70b9\u522b\u8bf4\u4f60\u4f5c\u5230\uff0c\u8fde\u6211\u4e5f\u4e0d\u53ca\u5979\u2026\u2026\u201d \u3000\u3000\u201c\u54fc\uff01\u4f60\u662f\u8bf4\uff0c\u4f60\u6bd4\u6211\u5f3a\u4e86\u2026\u2026\u201d\u7f57\u5170\u51b7\u51b7\u63a5\u9053\uff1a\u201c\u603b\u8981\u6709\u4e00\u5929\uff0c\u6211\u4eec\u5f97\u6bd4\u4e2a\u9ad8\u4e0b\u51fa\u6765\uff01\u201d \u3000\u3000\u201c\u5982\u8bba\u5251\u672f\u4e00\u9053\uff0c\u6211\u76f8\u4fe1\u6bd4\u4f60\u9ad8\u660e\u2026\u2026\u201d\u6b27\u9633\u662d\u9053\uff1a\u201c\u5982\u679c\u8981\u5404\u5c55\u7edd\u5b66\uff0c\u62fc\u4e2a\u80dc\u8d1f\u2026\u2026\u201d \u3000\u3000\u201c\u600e\u4e48\u6837\uff1f\u4f60\u4e5f\u662f\u6bd4\u6211\u5f3a\u4e86\uff1f\u201d \u3000\u3000\u201c\u597d\u5427\uff01\u7b97\u6211\u7518\u62dc\u4e0b\u98ce\u2026\u2026\u201d\u6b27\u9633\u662d\u9053\uff1a\u201c\u4f46\u541f\u96ea\u786e\u5b9e\u80dc\u8fc7\u6211\u4eec\u4e00\u4e9b\u2026\u2026\u201d \u3000\u3000\u201c\u4f55\u6b62\u5927\u4e2b\u5934\u554a\uff01\u2026\u2026\u201d\u7f57\u5170\u63a5\u9053\uff1a\u201c\u53ea\u6015\u4e8c\u4e2b\u5934\u4e5f\u6bd4\u5979\u4eec\u7684\u7239\u5a18\u5f3a\u4e86\uff0c\u771f\u4e0d\u77e5\u9053\u6c5f\u67ab\u600e\u4e48\u6559\u51fa\u6765\u7684\uff1f\u201d \u3000\u3000\u6570\u5341\u5e74\u7684\u5c71\u5c45\u548c\u4e0d\u77e5\uff0c\u4f7f\u4e24\u4e2a\u52a0\u8d77\u6765\uff0c\u8fc7\u4e86\u4e00\u767e\u5c81\u7684\u4eba\uff0c\u90fd\u8fd8\u4fdd\u6709\u7ae5\u5fc3\uff0c\u4e0d\u7ba1\u4ec0\u4e48\u573a\u5408\uff0c\u60f3\u8bf4\u4ec0\u4e48\uff1f\u5c31\u51b2\u53e3\u800c\u51fa\u3002 \u3000\u3000\u201c\u597d\u4e86\uff01\u7f57\u5170\uff0c\u7b97\u4f60\u8d62\u4e86\u2026\u2026\u201d\u6b27\u9633\u662d\u53d1\u73b0\u4e86\u573a\u5408\u4e0d\u5bf9\uff0c\u53ea\u597d\u8ba4\u8f93\u3002 \u3000\u3000\u7f57\u5170\u8fd8\u8981\u53cd\u5507\u76f8\u8ba5\uff0c\u5df2\u542c\u6c5f\u67ab\u9ad8\u58f0\u8bf4\u9053\uff1a\u201c\u590f\u5929\u540c\uff0c\u4ed6\u4eec\u5df2\u5fc3\u65e0\u6218\u5fd7\uff0c\u4e5f\u7f6a\u4e0d\u8be5\u6b7b\uff0c\u4f60\u4eec\u51e0\u4e2a\u5143\u51f6\u9996\u8111\uff0c\u624d\u662f\u7f6a\u4e0d\u53ef\u8d66\uff01\u201d \u3000\u3000\u8d66\u5b57\u51fa\u53e3\uff0c\u541f\u96ea\u5df2\u98de\u5c04\u800c\u51fa\u3002 \u3000\u3000\u4f46\u89c1\u767d\u8292\u4e00\u95ea\uff0c\u8033\u9645\u95f4\u5df2\u54cd\u8d77\u4e86\u4e07\u65b9\u7684\u4e00\u58f0\u60e8\u53eb\uff01 \u3000\u3000\u4e00\u638c\u62db\u9b42\u53f3\u624b\u7684\u9752\u7af9\u6756\uff0c\u53ea\u662f\u521a\u521a\u4e3e\u8d77\uff0c\u5251\u950b\u5df2\u900f\u80f8\u800c\u8fc7\u3002 \u3000\u3000\u201c\u6211\u8bf4\u8fc7\u5148\u6740\u4f60\u7684\uff01\u201d\u541f\u96ea\u9053\uff1a\u201c\u4f60\u51fa\u8a00\u4e0d\u900a\uff0c\u7f6a\u5f53\u5148\u6b7b\u3002\u201d \u3000\u3000\u8fd9\u4e00\u5251\u6df1\u5f97\u4e00\u4e2a\u5feb\u5b57\uff0c\u4f46\u6700\u56f0\u96be\u7684\u4e0d\u662f\u6740\u4eba\uff0c\u800c\u662f\u8131\u51fa\u4e8e\u5341\u4e8c\u4e2a\u5251\u571f\u7684\u56f4\u56f0\u3002 \u3000\u3000\u6ca1\u6709\u4eba\u770b\u6e05\u695a\uff0c\u541f\u96ea\u662f\u5982\u4f55\u9041\u8eab\u800c\u51fa\u7684\uff0c\u53ea\u89c1\u5230\u767d\u5f71\u6643\u52a8\uff0c\u5251\u5df2\u523a\u5165\u4e86\u4e07\u65b9\u7684\u524d\u80f8\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u7ed9\u4eba\u7684\u611f\u89c9\u662f\uff0c\u541f\u96ea\u4ecd\u5728\u56f4\u56f0\u4e2d\u6253\u8f6c\uff0c\u6240\u4ee5\uff0c\u4e07\u65b9\u6765\u4e0d\u53ca\u6325\u6756\u62db\u67b6\u3002 \u3000\u3000\u201c\u8001\u516c\uff01\u201d\u7f57\u5170\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u5f20\u56db\u59d1\u7684\u4e94\u884c\u9041\u672f\u9ad8\u5427\uff1f\u6709\u65f6\u5019\u5206\u95e8\u5de6\u9053\uff0c\u53ef\u6bd4\u5802\u5802\u6b63\u6b63\u7684\u5251\u6cd5\u6709\u7528\uff01\u201d \u3000\u3000\u201c\u771f\u7684\u662f\u56db\u59d1\u7684\u4e94\u884c\u9041\u672f\u5417\uff1f\u201d \u3000\u3000\u6b27\u9633\u662d\u5fc3\u4e2d\u6709\u4e9b\u6000\u7591\uff1f\u4f46\u53c8\u8bf4\u4e0d\u51fa\u90a3\u91cc\u4e0d\u5bf9\u3002 \u3000\u3000\u201c\u5f53\u7136\u5566\uff01\u8fd8\u8981\u52a0\u4e0a\u5929\u72d0\u767e\u53d8\u8eab\u6cd5\u624d\u884c\u2026\u2026\u201d\u7f57\u5170\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u7ea2\u82b1\u7eff\u53f6\u76f8\u5f97\u76ca\u5f70\u3002\u201d \u3000\u3000\u6b27\u9633\u662d\u70b9\u70b9\u5934\uff0c\u5012\u662f\u771f\u7684\u4fe1\u4e86\u3002 \u3000\u3000\u4f46\u6c5f\u67ab\u77e5\u9053\u4e0d\u662f\uff0c\u53ea\u662f\u6027\u8d28\u76f8\u8fd1\uff0c\u5c42\u6b21\u66f4\u9ad8\uff0c\u90a3\u662f\u8f7d\u4e8e\u201c\u4f20\u771f\u8bb0\u201d\u4e0a\u7684\u7edd\u6280\u201c\u5316\u8eab\u9041\u884c\u201d\u3002 \u3000\u3000\u6c5f\u67ab\u6ca1\u6709\u7ec3\u8fd9\u79cd\u8eab\u6cd5\uff0c\u56e0\u4e3a\uff0c\u4ed6\u6ca1\u6709\u4e60\u7ec3\u8fc7\u5f20\u56db\u59d1\u4e94\u9041\u8eab\u6cd5\uff0c\u6ca1\u6709\u8fd1\u4f3c\u7cfb\u7edf\u6b66\u529f\u4f5c\u57fa\u7840\uff0c\u7ec3\u8d77\u6765\u5c31\u4e8b\u500d\u529f\u534a\u4e86\u3002 \u3000\u3000\u7a81\u7136\u95f4\uff0c\u6c5f\u67ab\u4e5f\u5bf9\u541f\u96ea\u6709\u4e9b\u83ab\u6d4b\u9ad8\u6df1\u4e86\uff1f \u3000\u3000\u7ec6\u60f3\u4f20\u771f\u8bb0\u4e0a\u7684\u5947\u5b66\uff0c\u5341\u4e4b\u4e03\u516b\u90e8\u548c\u5929\u72d0\u6b66\u529f\u53ca\u5f20\u56db\u59d1\u7684\u6280\u827a\u7cfb\u7edf\u63a5\u8fd1\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u6c5f\u67ab\u65e9\u5df2\u770b\u51fa\u541f\u96ea\u7684\u5929\u72d0\u6b66\u529f\u57fa\u7840\u4e4b\u9ad8\uff0c\u8d85\u8d8a\u4e86\u541f\u971c\u5f88\u591a\uff0c\u4f46\u5979\u4e00\u76f4\u6291\u5236\u7740\u81ea\u5df1\uff0c\u4e0d\u8ba9\u950b\u8292\u592a\u9732\uff0c\u6015\u4f24\u4e86\u59b9\u59b9\u7684\u5fc3\u3002 \u3000\u3000\u4eca\u65e5\u662f\u4e00\u573a\u5927\u5bf9\u51b3\uff0c\u541f\u96ea\u597d\u50cf\u5df2\u51c6\u5907\u5c3d\u51fa\u516b\u5b9d\uff0c\u5927\u5c55\u8eab\u624b\uff0c\u8fd9\u4e2a\u7075\u6167\u949f\u8eab\u7684\u5c11\u5973\uff0c\u7a76\u7adf\u7ec3\u4e86\u591a\u5c11\u201c\u4f20\u771f\u8bb0\u201d\u4e0a\u6b66\u529f\uff1f\u6c5f\u67ab\u4e0d\u6562\u5984\u6d4b\u4e86\u3002 \u3000\u3000\u4f46\u6c5f\u67ab\u5df2\u4f53\u4f1a\u5230\u541f\u96ea\u7ec3\u7684\u6bd4\u4ed6\u591a\uff0c\u8bba\u5185\u529f\u7cbe\u8fdb\uff0c\u5251\u9053\u6210\u5c31\uff0c\u6c5f\u67ab\u81ea\u89c9\u8fd9\u77ed\u77ed\u4e00\u767e\u591a\u5929\uff0c\u5982\u540c\u8131\u80ce\u6362\u9aa8\uff0c\u6536\u83b7\u5947\u5927\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u6c5f\u67ab\u4e5f\u4e00\u76f4\u5927\u529b\u6295\u5165\u4e86\u8bba\u5251\u4e2d\uff0c\u5ba1\u4ed6\u8fc7\u53bb\u6240\u6709\u6280\u827a\u4e2d\u6700\u5f31\u7684\u4e00\u73af\u3002 \u3000\u3000\u541f\u96ea\u5462\uff1f \u3000\u3000\u90fd\u7ec3\u6210\u4e9b\u4ec0\u4e48\uff1f \u3000\u3000\u90a3\u201c\u5316\u8eab\u9041\u5f62\u201d\u8eab\u6cd5\uff0c\u5982\u53ea\u662f\u5979\u4e13\u6ce8\u4e00\u9879\u7684\u6210\u5c31\uff1f \u3000\u3000\u6c5f\u67ab\u8fd9\u65b9\u9762\u81ea\u53f9\u5f17\u5982\uff0c\u4e5f\u5c31\u7f62\u4e86\uff0c\u5982\u679c\u53ea\u662f\u5979\u6210\u5c31\u4e4b\u4e00\uff0c\u6c5f\u67ab\u4e0d\u6562\u60f3\u4e0b\u53bb\u4e86\u2026\u2026\u541f\u96ea\u554a\uff01\u4f60\u4f1a\u4e0d\u4f1a\u6c42\u6210\u5fc3\u5207\uff0c\u6ca6\u5165\u9b54\u52ab\u3002 \u3000\u3000\u6c5f\u67ab\u7a81\u7136\u95f4\u5fc3\u795e\u98a4\u52a8\u8d77\u6765\uff0c\u4e5f\u660e\u767d\u4e86\u4e3a\u4ec0\u4e48\u541f\u971c\u6ca1\u6709\u51fa\u73b0\uff0c\u4e09\u4eba\u8054\u624b\u62d2\u654c\uff0c\u539f\u662f\u65e9\u5c31\u8bb2\u597d\u4e86\uff0c\u5ffd\u7136\u53d8\u5366\uff0c\u663e\u7136\u6709\u9b3c\u3002 \u3000\u3000\u8fd9\u4e2a\u9b3c\u662f\u541f\u96ea\u6363\u7684\uff0c\u501f\u91cd\u5f20\u56db\u59d1\u5ba3\u5e03\u66f4\u6539\u3002 \u3000\u3000\u5979\u65e2\u652f\u5f00\u541f\u971c\uff0c\u4f0e\u4fe9\u5f53\u4e0d\u53ea\u6b64\uff0c\u53ef\u60dc\u7684\u662f\u4f30\u4e0d\u900f\u5979\u6d89\u9669\u5165\u9b54\uff0c\u5230\u4e86\u90a3\u91cd\u5883\u754c\uff1f \u3000\u3000\u8fd9\u672c\u5e72\u8fdd\u5929\u673a\u7684\u5947\u4e66\uff0c\u6ca1\u6709\u5bb3\u4e86\u6c5f\u67ab\uff0c\u5374\u597d\u50cf\u5bb3\u4e86\u541f\u96ea\uff1f\u6c5f\u67ab\u5f03\u6613\u9009\u96be\uff0c\u5728\u5251\u9053\u4e0a\u4e0b\u529f\u592b\uff0c\u541f\u96ea\u5374\u4ed7\u51ed\u4e86\u8fc7\u4eba\u7684\u624d\u827a\u3001\u609f\u6027\uff0c\u5168\u529b\u7684\u53bb\u8ffd\u6c42\u6613\u5b66\u901f\u6210\uff0c\u51f6\u6b8b\u90aa\u6076\u7684\u6280\u827a\u3002 \u3000\u3000\u4e0d\u77e5\u662f\u541f\u96ea\u4e00\u5251\u60ca\u654c\uff0c\u8fd8\u662f\u5927\u60b2\u68b5\u5531\u7684\u4f5b\u6cd5\u65e0\u8fb9\uff0c\u805a\u96c6\u5728\u590f\u5929\u540c\u8eab\u540e\u6570\u767e\u5c5e\u4f17\uff0c\u7f13\u7f13\u5f00\u59cb\u6563\u52a8\u3002 \u3000\u3000\u5ffd\u7136\u95f4\u2014\u2014 \u3000\u3000\u68b5\u97f3\u9ad8\u62d4\uff0c\u76f4\u51b2\u4e91\u9704\uff0c\u4f59\u97f3\u8885\u8885\uff0c\u6563\u5165\u56db\u65b9\u3002 \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4eba\u8d70\u4eba\u8def\uff0c\u9b3c\u884c\u9b3c\u9053\uff0c\u5404\u4f4d\u5fc3\u4e0a\u7981\u5236\u5df2\u89e3\uff0c\u53bb\u7559\u5c31\u51ed\u672c\u6027\u5fc3\u5ff5\u4e86\uff0c\u548c\u5c1a\u5df2\u5c3d\u4e86\u5fc3\u529b\uff0c\u963f\u5f25\u9640\u4f5b\uff01\u201d \u3000\u3000\u4f46\u89c1\u4eba\u5f71\u95ea\u52a8\uff0c\u5929\u9a6c\u5802\u4e09\u767e\u591a\u4f4d\u5c5e\u4f17\uff0c\u7adf\u7136\u6563\u53bb\u4e86\u5341\u4e4b\u516b\u4e5d\uff0c\u4f59\u4e0b\u6570\u5341\u4eba\u4e5f\u662f\u4e00\u8138\u60f6\u7136\uff0c\u4e0d\u77e5\u8be5\u4f55\u53bb\u4f55\u4ece\uff1f \u3000\u3000\u767d\u5929\u5316\u3001\u53f8\u5f92\u827a\uff0c\u6ca1\u6709\u8d70\uff01 \u3000\u3000\u7533\u96ea\u541b\u5e26\u7740\u56db\u4e2a\u5973\u5a62\uff0c\u7f13\u7f13\u5411\u540e\u9000\u53bb\uff0c\u671d\u9633\u3001\u6c34\u6708\u3001\u955c\u82b1\u4e09\u5927\u6402\u4e3b\uff0c\u6ca1\u6709\u8d70\uff0c\u4f46\u90fd\u76b1\u8d77\u4e86\u7709\u5934\uff0c\u4f3c\u662f\u5728\u6697\u4f5c\u76d8\u7b97\u3002 \u3000\u3000\u5012\u662f\u56f4\u4f4f\u6c5f\u67ab\u7684\u5341\u4e8c\u4e2a\u9ed1\u8863\u5251\u58eb\uff0c\u5728\u68b5\u5531\u505c\u4e0b\u4e4b\u540e\uff0c\u6740\u6c14\u6e10\u91cd\uff0c\u5927\u6709\u968f\u65f6\u51fa\u624b\u7684\u53ef\u80fd\u3002 \u3000\u3000\u4ed6\u4eec\u90fd\u662f\u590f\u5929\u540c\u5e7c\u5c0f\u8c03\u6559\u800c\u6210\u7684\u5251\u58eb\uff0c\u65c1\u65e0\u522b\u5ff5\uff0c\u552f\u547d\u662f\u4ece\u3002 \u3000\u3000\u201c\u7533\u96ea\u541b\uff0c\u4f60\u8981\u8d70\u4e48\uff1f\u2026\u2026\u201d\u590f\u5929\u540c\u51b7\u51b7\u5730\u9053\uff1a\u201c\u770b\u6765\u4f60\u5fc3\u4e2d\u8fd8\u5fd8\u4e0d\u4e86\u91d1\u4e39\u4e66\u751f\u3002\u201d \u3000\u3000\u201c\u603b\u5802\u4e3b\uff0c\u5929\u9a6c\u5802\u7684\u4eba\uff0c\u5df2\u7ecf\u6563\u53bb\u4e86\u5341\u4e4b\u516b\u4e5d\u201d\u7533\u96ea\u541b\u9053\uff1a\u201c\u5bf8\u5fc3\u5df2\u5316\u70df\u4e91\u53bb\uff0c\u4e0d\u6b7b\u53ea\u4e3a\u916c\u541b\u6069\uff0c\u5929\u540c\uff0c\u591f\u4e86\uff0c\u4e8c\u5341\u5e74\u4efb\u541b\u8f7b\u8584\uff0c\u6211\u4e5f\u8be5\u8fd8\u591f\u4f60\u7684\u6069\u60c5\u4e86\uff0c\u653e\u6211\u53bb\u5427\uff01\u201d \u3000\u3000\u201c\u4e8c\u5341\u5e74\u8fd8\u65e0\u6cd5\u7ba1\u4f4f\u4f60\u4e00\u7f15\u60c5\u4e1d\uff0c\u52ff\u602a\u91d1\u4e39\u4e66\u751f\u8981\u628a\u6211\u6574\u6210\u8fd9\u4e2a\u6837\u5b50\uff1f\u201d\u590f\u5929\u540c\u53cc\u76ee\u4e2d\u6d88\u9000\u7684\u7ea2\u5149\uff0c\u53c8\u6d8c\u73b0\u51fa\u6765\u3002 \u3000\u3000\u201c\u4e0d\u8981\u51a4\u6211\uff0c\u6211\u5982\u5acc\u4f60\u8c8c\u4e11\uff0c\u600e\u4f1a\u7559\u4f60\u8eab\u8fb9\u4e8c\u5341\u51e0\u5e74\u2026\u2026\u201d\u7533\u96ea\u541b\u9053\uff1a\u201c\u6069\u5df2\u8fdc\uff0c\u60c5\u5df2\u5c3d\uff0c\u6211\u8be5\u8d70\u6211\u7684\u8def\u4e86\uff01\u201d \u3000\u3000\u201c\u4f60\u8d70\u4e0d\u4e86\u7684\uff01\u201d\u590f\u5929\u540c\u9053\uff1a\u201c\u4ed6\u4eec\u4e5f\u8d70\u4e0d\u4e86\u3002\u201d \u3000\u3000\u4f46\u95fb\u60e8\u53eb\u4e4b\u58f0\uff0c\u5206\u7531\u591a\u65b9\u4f20\u6765\u3002 \u3000\u3000\u90a3\u662f\u6b7b\u4ea1\u7684\u6012\u543c\uff0c\u751f\u547d\u5c3d\u5934\u7684\u60b2\u568e\u3002 \u3000\u3000\u201c\u662f\u4ed6\u4eec\uff0c\u6211\u4e0d\u6015\uff01\u201d\u7533\u96ea\u541b\u56de\u987e\u4e86\u56db\u5a62\u4e00\u9650\uff0c\u9053\uff1a\u201c\u54b1\u4eec\u8d70\uff01\u201d \u3000\u3000\u201c\u7533\u59e8\uff01\u201d\u6c5f\u67ab\u7a81\u7136\u5f00\u53e3\u8bf4\u9053\uff1a\u201c\u4e50\u53d4\u4ece\u672a\u5fd8\u8bb0\u8fc7\u4f60\uff0c\u4ed6\u59e8\u5a18\u662f\u6e05\u767d\u7684\uff0c\u4ed6\u4eec\u60c5\u540c\u59d0\u5f1f\uff0c\u4f60\u8bef\u4f1a\u4e86\u3002\u201d \u3000\u3000\u201c\u6c5f\u67ab\uff0c\u4f60\u521a\u624d\u53eb\u6211\u4ec0\u4e48\uff1f\u80fd\u4e0d\u80fd\u518d\u53eb\u4e00\u58f0\uff01\u201d \u3000\u3000\u201c\u7533\u59e8\u7231\u542c\uff0c\u6211\u53ef\u4ee5\u53eb\u4f60\u4e00\u5343\u58f0\uff0c\u4e00\u4e07\u58f0\u2026\u2026\u201d \u3000\u3000\u201c\u8c01\u8981\u4f60\u53eb\u6211\u7533\u59e8\uff1f\u201d \u3000\u3000\u201c\u4e50\u53d4\u7684\u9057\u8a00\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff0c \u3000\u3000\u201c\u4e16\u4eba\u90fd\u77e5\u9053\u91d1\u4e39\u4e66\u751f\uff0c\u53ef\u662f\u5f88\u5c11\u4eba\u77e5\u9053\u4ed6\u59d3\u4e50\uff0c\u4e50\u53d4\u5728\u6b7b\u524d\u624d\u544a\u8bc9\u6211\u4ed6\u59d3\u4e50\uff0c\u4e5f\u544a\u8bc9\u6211\uff0c\u4ed6\u5f88\u540e\u6094\u6ca1\u6709\u627e\u4f60\u5f53\u9762\u89e3\u91ca\u6e05\u695a\u3002\u201d \u3000\u3000\u201c\u4ed6\u4e0d\u6068\u6211\u4e48\uff1f\u6211\u4e5f\u662f\u6bd2\u5bb3\u4ed6\u4eec\u7684\u51f6\u624b\u4e4b\u4e00\u2026\u2026\u201d \u3000\u3000\u201c\u4e50\u53d4\u77e5\u9053\uff0c\u4ed6\u4eec\u8eab\u53d7\u6697\u7b97\u4e4b\u540e\uff0c\u6d3b\u4e86\u5341\u5e74\uff0c\u4e3a\u4e86\u9020\u5c31\u6211\uff0c\u4ed6\u4eec\u5fcd\u75db\u800c\u6d3b\uff0c\u4ed6\u4e00\u76f4\u6ca1\u6709\u6068\u8fc7\u4f60\uff0c\u4e5f\u7740\u91cd\u7684\u4ea4\u4ee3\u6211\uff0c\u4e0d\u8981\u6068\u4f60\u3002\u201d \u3000\u3000\u7533\u96ea\u541b\u6cea\u6d41\u53cc\u988a\uff0c\u9eef\u7136\u8bf4\u9053\uff1a\u201c\u5992\u6068\u8bef\u4eba\u2026\u2026\u201d \u3000\u3000\u76ee\u5149\u4e00\u63a0\u541f\u96ea\uff0c\u63a5\u9053\uff1a\u201c\u4e00\u5ff5\u4e4b\u9519\uff0c\u60c5\u5929\u96be\u8865\uff0c\u6c5f\u67ab\uff0c\u80af\u5e26\u6211\u5230\u4ed6\u7684\u57cb\u9aa8\u4e4b\u5730\u53bb\u5c0a\u62dc\u4e00\u756a\u4e48\uff1f\u201d \u3000\u3000\u201c\u662f\uff01\u7533\u59e8\u4e4b\u547d\uff0c\u600e\u6562\u4e0d\u4ece\u2026\u2026\u201d\u6c5f\u67ab\u53f9\u606f\u4e00\u58f0\u53c8\u9053\uff1a\u201c\u4e50\u53d4\u9634\u7075\u6709\u77e5\uff0c\u4e5f\u4f1a\u7791\u76ee\u6cc9\u4e0b\u3002\u201d \u3000\u3000\u201c\u8c22\u8c22\u4f60\uff0c\u6c5f\u67ab\u2026\u2026\u201d\u7533\u96ea\u541b\u76ee\u5149\u4e00\u63a0\u5341\u4e8c\u4e2a\u56f4\u4f4f\u6c5f\u67ab\u7684\u9ed1\u8863\u5251\u58eb\uff0c\u9053\uff1a\u201c\u9000\u4e0b\u53bb\u3002\u201d \u3000\u3000\u8fd9\u4e9b\u9ed1\u8863\u5251\u571f\uff0c\u8f6c\u671b\u7740\u590f\u5929\u540c\u3002 \u3000\u3000\u201c\u7533\u96ea\u541b\uff0c\u4f60\u8ba4\u4e3a\u4ed6\u4eec\u8fd8\u4f1a\u542c\u4f60\u4e4b\u547d\u5417\uff1f\u2026\u2026\u201d\u590f\u5929\u540c\u51b7\u51b7\u7684\u8bf4\u9053\uff1a\u201c\u4f60\u5df2\u7ecf\u80cc\u53db\u4e86\u5929\u9a6c\u5802\u2026\u2026\u201d \u3000\u3000\u201c\u4f60\u8981\u903c\u6211\u7528\u5341\u51e4\u9f50\u98de\u5bf9\u4ed8\u4ed6\u4eec\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u6562\u6709\u52b3\u7533\u59e8\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u4f46\u786e\u6709\u51e0\u4ef6\u4e8b\u8981\u7533\u59e8\u6307\u70b9\u3001\u6307\u70b9\u2026\u2026\u201d \u3000\u3000\u201c\u4f60\u8bf4\u5427\uff01\u201d \u3000\u3000\u201c\u767d\u5929\u5316\u6587\u6b66\u517c\u8d44\uff0c\u662f\u4e00\u79d1\u72b6\u5143\u4e4b\u624d\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u8fd9\u4e2a\u4eba\u8be5\u4e0d\u8be5\u6b7b\uff1f\u201d \u3000\u3000\u201c\u6211\u548c\u4ed6\u4eec\u76f8\u5904\u4e86\u4e8c\u5341\u5e74\uff0c\u2026\u2026\u201d\u7533\u96ea\u541b\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\u8be5\u8bf4\u4e9b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u6211\u61c2\u4e86\uff0c\u7533\u59e8\u8bf7\u5728\u4e00\u4fa7\u4f11\u606f\u3002\u201d \u3000\u3000\u201c\u8fdd\u4ee4\u8005\u6b7b\uff01\u201d\u541f\u96ea\u5a07\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u4e0d\u542c\u7533\u59e8\u4e4b\u547d\uff0c\u6b7b\u4e86\u53ef\u4e0d\u80fd\u602a\u6211\uff01\u201d \u3000\u3000\u5979\u81ea\u8bf4\u81ea\u8bdd\uff0c\u4eba\u4e5f\u5f00\u59cb\u884c\u52a8\u3002 \u3000\u3000\u4f46\u89c1\u767d\u5f71\u95ea\u52a8\uff0c\u9c9c\u8840\u6e85\u98de\uff0c\u5341\u4e8c\u4e2a\u9ed1\u8863\u5251\u58eb\u50cf\u9aa8\u724c\u4e00\u6837\uff0c\u4e00\u4e2a\u8fde\u4e00\u4e2a\u5012\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u60ca\u4eba\u7684\u5feb\u901f\u6740\u622e\uff0c\u4e5f\u851a\u7136\u6210\u6218\u9635\u5947\u89c2\u3002 \u3000\u3000\u541f\u96ea\u7684\u884c\u52a8\u592a\u8be1\u5947\u4e86\uff0c\u90a3\u4e9b\u9ed1\u8863\u5251\u571f\uff0c\u770b\u4e0d\u5230\u5979\u5728\u90a3\u91cc\uff0c\u624b\u4e2d\u4e4b\u5251\uff0c\u5168\u90fd\u523a\u5411\u4e86\u6c5f\u67ab\uff0c\u4f46\u541f\u96ea\u51fa\u5251\u5f88\u5feb\uff0c\u4e5d\u4e2a\u4eba\u6765\u4e0d\u53ca\u6325\u5251\u51fb\u51fa\u3002 \u3000\u3000\u4eba\u5df2\u5012\u4e86\u4e0b\u53bb\uff0c\u4f46\u8fd8\u6709\u4e09\u53ea\u5251\u523a\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u4e09\u5251\u5168\u4e2d\uff0c\u523a\u5165\u4e86\u6c5f\u67ab\u524d\u80f8\u3002 \u3000\u3000\u541f\u96ea\u662f\u53f3\u624b\u51fa\u5251\uff0c\u7ed5\u6c5f\u67ab\u8eab\u540e\u81f3\u524d\uff0c\u6240\u4ee5\uff0c\u6700\u540e\u6b7b\u7684\u4e09\u4e2a\u4eba\u5168\u5728\u6c5f\u67ab\u7684\u8eab\u4f53\u6b63\u9762\u3002 \u3000\u3000\u6c5f\u67ab\u6ca1\u6709\u52a8\u8fc7\uff0c\u8eab\u4e2d\u4e09\u5251\uff0c\u4ecd\u7136\u7ad9\u5728\u539f\u5730\u3002 \u3000\u3000\u201c\u6c5f\u67ab\uff01\u2026\u2026\u201d\u7533\u96ea\u541b\u9053\uff0c \u3000\u3000\u201c\u4f60\u4e0d\u80fd\u5012\u4e0b\u53bb\u554a\uff01\u4f60\u8fd8\u8981\u5e26\u6211\u53bb\u5960\u62dc\u4f60\u4e50\u53d4\uff01\u201d \u3000\u3000\u201c\u7533\u59e8\uff0c\u6c5f\u5927\u54e5\u5728\u73a9\u628a\u620f\u2026\u2026\u201d\u541f\u96ea\u4f4e\u58f0\u9053\uff1a\u201c\u4e0d\u7528\u4e3a\u4ed6\u62c5\u5fc3\u3002\u201d \u3000\u3000\u5176\u5b9e\uff0c\u7533\u96ea\u541b\u4e5f\u52a8\u7591\u4e86\uff0c\u6c5f\u67ab\u8eab\u4e2d\u4e09\u5251\uff0c\u4e0d\u89c1\u4e00\u6ef4\u8840\u51fa\u3002 \u3000\u3000\u4f46\u95fb\u6012\u543c\u3001\u60e8\u53eb\uff0c\u548c\u6fc0\u70c8\u7684\u6253\u6597\u4e4b\u58f0\uff0c\u4e0d\u505c\u7684\u4f20\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u5012\u662f\u573a\u5916\u7684\u62fc\u6218\uff0c\u5341\u5206\u6fc0\u70c8\uff0c\u539f\u5b9a\u4e3b\u8981\u7684\u6218\u573a\u4e0a\uff0c\u7adf\u8fd8\u672a\u95fb\u8fc7\u5175\u5203\u649e\u51fb\u4e4b\u58f0\u3002 \u3000\u3000\u201c\u7533\u59e8\uff0c\u62e6\u6740\u5929\u9a6c\u5802\u79bb\u53bb\u7684\u4eba\uff0c\u53ef\u662f\u90a3\u4f4d\u795e\u79d8\u6559\u4e3b\u548c\u56db\u5927\u795e\u901a\u4f7f\u8005\uff1f\u201d \u3000\u3000\u8bf4\u8bdd\u7684\u662f\u6c5f\u67ab\uff0c\u4ed6\u8eab\u4e0a\u8fd8\u63d2\u7740\u4e09\u652f\u957f\u5251\uff0c\u5251\u8eab\u5728\u98ce\u4e2d\u98a4\u52a8\uff0c\u90a3\u60c5\u5f62\u770b\u4e0a\u53bb\u5341\u5206\u8be1\u5f02\u3002 \u3000\u3000\u201c\u5c31\u7b97\u4ed6\u4eec\u6ca1\u6709\u4eb2\u81ea\u51fa\u624b\uff0c\u2026\u2026\u201d\u7533\u96ea\u541b\u9053\uff1a\u201c\u4e5f\u662f\u4ed6\u4eec\u5b89\u6392\u7684\u4f0f\u5175\u3002 \u3000\u3000\u201c\u53ef\u6076\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u6012\u9053\uff1a\u201c\u590f\u5929\u540c\uff0c\u4f60\u5c31\u4efb\u4eba\u5c60\u6740\u4f60\u5929\u9a6c\u5802\u4e2d\u4eba\uff0c\u5750\u89c6\u4e0d\u7406\u5417\uff1f\u201d \u3000\u3000\u201c\u6c5f\u67ab\uff0c\u5982\u679c\u4f60\u613f\u610f\u5408\u4f5c\uff0c\u6211\u4eec\u53ef\u4ee5\u8c08\u8c08\u3002\u201d\u53f8\u5f92\u827a\u9053\uff1a\u201c\u6211\u4eec\u5728\u6740\u622e\u8fd8\u672a\u9020\u6210\u4e4b\u524d\uff0c\u8fd8\u6709\u53ef\u4ee5\u8c08\u8c08\u7684\u673a\u4f1a\uff1f\u201d \u3000\u3000\u201c\u4f60\u6700\u8be5\u6b7b\uff0c\u5929\u9a6c\u5802\u6e21\u5371\uff0c\u662f\u4f60\u4ece\u4e2d\u4e3b\u6301\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u7a81\u7136\u98de\u8dc3\u800c\u8d77\uff0c\u6251\u5411\u53f8\u5f92\u827a\u3002 \u3000\u3000\u4ed6\u80f8\u524d\u4ecd\u7136\u5e26\u7740\u4e09\u652f\u957f\u5251\uff0c\u4f46\u5728\u63a5\u8fd1\u53f8\u5f92\u827a\u65f6\uff0c\u4e09\u5251\u5ffd\u7136\u98de\u51fa\uff0c\u4e00\u9f50\u5411\u53f8\u5f92\u827a\u6fc0\u5c04\u800c\u53bb\u3002 \u3000\u3000\u4f46\u95fb\u4e00\u9635\u91d1\u94c1\u4ea4\u9e23\uff0c\u638c\u98ce\u6fc0\u8361\uff0c\u84ec\u7136\u4e92\u51fb\u4e2d\uff0c\u6c5f\u67ab\u843d\u7740\u5b9e\u5730\u3002 \u3000\u3000\u4e09\u53ea\u5c04\u5411\u53f8\u5f92\u827a\u7684\u957f\u5251\uff0c\u5168\u88ab\u51fb\u843d\uff0c\u6c5f\u67ab\u4e5f\u88ab\u963b\u6b62\u4e8e\u4e09\u5c3a\u4e4b\u5916\u3002 \u3000\u3000\u539f\u6765\uff0c\u8fd9\u4e00\u6b21\u767d\u5929\u5316\u3001\u590f\u5929\u540c\u4e00\u9f50\u51fa\u624b\u4e86\u3002 \u3000\u3000\u6c5f\u67ab\u529b\u62d2\u4e24\u5927\u9ad8\u624b\u3001\u548c\u4ed6\u4eec\u786c\u62fc\u4e86\u4e00\u638c\u3002 \u3000\u3000\u53f8\u5f92\u827a\u7a7a\u51fa\u8eab\u5b50\uff0c\u6325\u5200\u51fb\u843d\u4e09\u5251\u3002 \u3000\u3000\u767d\u5929\u5316\u9053\uff1a\u201c\u603b\u5802\u4e3b\uff0c\u54b1\u4eec\u88ab\u4e24\u4e2a\u5e74\u8f7b\u4eba\u800d\u4e86\uff0c\u4ed6\u4eec\u4e00\u6367\u4e00\u5531\uff0c\u5236\u9020\u4e86\u6740\u4eba\u7684\u673a\u4f1a\u3002\u201d \u3000\u3000\u5fc3\u4e2d\u5374\u662f\u6697\u6697\u9707\u60ca\uff0c\u5fd6\u9053\uff1a\u201c\u638c\u529b\u5bf9\u62fc\uff0c\u53ef\u662f\u771f\u529f\u5b9e\u5b66\uff0c\u4e00\u70b9\u4e5f\u4e0d\u80fd\u53d6\u5de7\uff0c\u8fd9\u5c0f\u5b50\u7adf\u7136\u80fd\u63a5\u4e0b\u6211\u548c\u590f\u5929\u540c\u8054\u624b\u4e4b\u529b\uff0c\u518d\u4e5f\u4e0d\u80fd\u6b7b\u8981\u9762\u5b50\uff0c\u6d3b\u53d7\u7f6a\u4e86\u3002\u201d \u3000\u3000\u201c\u8bf4\u7684\u662f\u554a\uff01\u5dee\u4e00\u70b9\u88ab\u4ed6\u4eec\u552c\u4f4f\u4e86\u3002\u201d \u3000\u3000\u590f\u5929\u540c\u62a2\u5148\u800c\u4e0a\uff0c\u4e00\u5200\u5288\u51fa\u3002 \u3000\u3000\u5bf9\u62fc\u4e00\u638c\uff0c\u590f\u5929\u540c\u5fc3\u4e2d\u6709\u6570\uff0c\u6740\u6c5f\u67ab\u7684\u552f\u4e00\u673a\u4f1a\uff0c\u662f\u5728\u548c\u767d\u5929\u5316\u3001\u53f8\u5f92\u827a\u7684\u8054\u624b\u5408\u51fb\u4e4b\u4e2d\uff0c\u4e00\u4e0b\u5b50\u4eae\u5200\u800c\u653b\uff0c\u662f\u793a\u610f\u767d\u5929\u5316\u4e5f\u52a8\u5175\u5203\u3002 \u3000\u3000\u201c\u4f60\u4eec\u8981\u4e0d\u8981\u8138\uff1f\u4e09\u4e2a\u4eba\u5408\u6253\u4e00\u4e2a\u2026\u2026\u201d\u7533\u96ea\u541b\u4f38\u624b\u7531\u4e00\u4e2a\u5973\u5a62\u624b\u4e2d\u53d6\u8fc7\u957f\u5251\uff0c\u51c6\u5907\u633a\u8eab\u800c\u51fa\uff0c\u7ffb\u8138\u52a8\u624b\u4e86\u3002 \u3000\u3000\u201c\u7533\u59e8\uff0c\u4e0d\u7528\u52b3\u9a7e\uff0c\u6c5f\u5927\u54e5\u5e94\u4ed8\u5f97\u4e86\u2026\u2026\u201d\u541f\u96ea\u7b11\u9053\uff1a\u201c\u6211\u662f\u6295\u673a\u53d6\u5de7\uff0c\u5e78\u800c\u5f97\u901e\uff0c\u6c5f\u5927\u54e5\u662f\u8981\u51ed\u4ed7\u771f\u672c\u9886\u548c\u4ed6\u4eec\u5bf9\u51b3\uff0c\u8fd9\u4e09\u4e2a\u4eba\u5408\u529b\u8fd8\u5403\u4e0d\u4f4f\u6c5f\u5927\u54e5\uff0c\u5929\u540e\u5bab\u548c\u5929\u738b\u95e8\u4e5f\u4f1a\u77e5\u96be\u800c\u9000\u4e86\u3002\u201d \u3000\u3000\u7533\u96ea\u541b\u9053\uff1a\u201c\u6c5f\u67ab\u771f\u80fd\u4ee5\u4e00\u5bf9\u4e09\uff1f\u201d \u3000\u3000\u541f\u96ea\u76ee\u5149\u4e00\u8f6c\uff0c\u89c1\u6c5f\u67ab\u672a\u4eae\u80dc\u90aa\u5b9d\u5251\uff0c\u53ea\u7528\u7fe0\u7389\u5200\u5728\u548c\u4e09\u4eba\u7f20\u6597\uff0c\u5df2\u77e5\u6c5f\u67ab\u5e76\u672a\u5b58\u6740\u6b7b\u4e09\u4eba\u4e4b\u5fc3\uff0c\u7acb\u523b\u660e\u767d\u4e86\u6c5f\u67ab\u7684\u7528\u5fc3\uff0c\u4e0d\u613f\u5b8c\u5168\u66b4\u9732\u5b9e\u529b\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u7533\u59e8\uff0c\u4f60\u6ce8\u610f\u770b\u770b\uff0c\u90a3\u4e2a\u6559\u4e3b\u548c\u56db\u5927\u795e\u901a\u4f7f\u8005\uff0c\u662f\u5426\u5728\u573a\uff1f\u4ed6\u4eec\u53ef\u80fd\u6613\u5bb9\u6539\u88c5\uff0c\u6df7\u5728\u5929\u9a6c\u5802\u7684\u5c5e\u4f17\u4e4b\u5185\uff0c\u5f53\u7136\uff0c\u4e5f\u53ef\u80fd\u6f5c\u9690\u5728\u5929\u738b\u95e8\u3001\u5929\u540e\u5bab\u7684\u4ece\u5f92\u4e4b\u4e2d\uff0c\u4ed6\u4eec\u624d\u662f\u83ab\u53ef\u9884\u6d4b\u7684\u53d8\u6570\u3002\u201d \u3000\u3000\u7533\u96ea\u541b\u7acb\u523b\u8b66\u89c9\uff0c\u541f\u96ea\u8bf4\u7684\u4e0d\u9519\uff0c\u4ed6\u4eec\u795e\u79d8\u6559\u4e3b\u624d\u662f\u4e07\u6076\u4e4b\u6e90\uff0c\u6ca1\u6709\u4eba\u4e86\u89e3\u4ed6\u771f\u6b63\u7684\u8eab\u4efd\u3002 \u3000\u3000\u81f3\u5c11\u662f\u5929\u9a6c\u5802\u4e2d\u6ca1\u6709\uff0c\u4ed6\u5982\u7f29\u5934\u4e0d\u51fa\uff0c\u90a3\u5c31\u7559\u4e0b\u4e86\u518d\u5ea6\u7978\u5bb3\u6c5f\u6e56\u7684\u706b\u79cd\uff0c\u7ec6\u60f3\u5f53\u5e74\uff0c\u5bf9\u4ed8\u6b66\u6797\u4e09\u5723\uff0c\u867d\u662f\u7531\u590f\u5929\u540c\u51fa\u9762\u7ec4\u5408\uff0c\u4f46\u6697\u4e2d\u5374\u4f3c\u6709\u7740\u4e00\u53cc\u63a8\u52a8\u7684\u9ed1\u624b\u3002 \u3000\u3000\u53ea\u53ef\u60dc\uff0c\u90a3\u65f6\uff0c\u81ea\u5df1\u88ab\u5992\u6068\u4e4b\u706b\u70e7\u660f\u4e86\u5934\uff0c\u672a\u80fd\u7ec6\u5fc3\u4f53\u4f1a\uff0c\u89c2\u5bdf\uff0c\u4e8b\u540e\uff0c\u53c8\u5fc3\u5982\u67af\u6728\uff0c\u6094\u6068\u4ea4\u4f5c\uff0c\u884c\u5c38\u8d70\u8089\u822c\u751f\u6d3b\u4e86\u4e8c\u5341\u591a\u5e74\uff0c\u96be\u9053\uff0c\u81ea\u5df1\u4e5f\u88ab\u590f\u5929\u540c\u7684\u8680\u5fc3\u9b54\u97f3\u675f\u7f1a\u4e86\uff1f\u59cb\u624d\u88ab\u5927\u60b2\u68b5\u5531\u60ca\u9192\u2026\u2026 \u3000\u3000\u5b9a\u662f\u5982\u6b64\u4e86\uff0c\u60f3\u5230\u4f24\u5fc3\u75db\u7edd\u4e4b\u5904\uff0c\u4e0d\u7981\u6f5c\u7136\u6cea\u4e0b\u3002 \u3000\u3000\u201c\u7533\u59e8\uff0c\u8fc7\u53bb\u7684\u522b\u518d\u60f3\u4e86\uff0c\u91cd\u590d\u6c5f\u6e56\u6c14\u52bf\uff0c\u662f\u6b66\u6797\u4e09\u5723\u7684\u5fc3\u613f\u2026\u2026\u201d\u541f\u96ea\u9053\uff1a\u201c\u6c5f\u5927\u54e5\uff0c\u51e0\u4e4e\u662f\u4e3a\u6b64\u800c\u751f\uff0c\u4e0d\u505c\u7684\u8fdb\u53d6\u3001\u594b\u6597\uff0c\u6211\u4eec\u90fd\u5728\u5168\u529b\u5e2e\u4ed6\uff0c\u53cc\u65b9\u4e0d\u4f46\u8981\u6bd4\u6b66\u529f\u3001\u6597\u667a\u6167\uff0c\u4e5f\u8981\u6bd4\u8010\u5fc3\u3001\u5fcd\u6027\u3002\u201d \u3000\u3000\u7533\u96ea\u541b\u9053\uff1a\u201c\u8bf4\u7684\u662f\uff0c\u4f60\u8fd9\u70b9\u5e74\u7eaa\uff0c\u5982\u6b64\u61c2\u4e8b\uff0c\u5f53\u5e74\uff0c\u6211\u8fd8\u5927\u4f60\u51e0\u5c81\uff0c\u5374\u662f\u4e00\u65e0\u6240\u77e5\uff0c\u56de\u9996\u524d\u5c18\uff0c\u5168\u662f\u6068\u4e8b\u2026\u2026\u201d \u3000\u3000\u201c\u770b\u7533\u59e8\u4e4b\u7f8e\uff0c\u5f53\u662f\u806a\u6167\u7edd\u4f26\u2026\u2026\u201d\u541f\u96ea\u9053\uff1a\u201c\u53ef\u80fd\u662f\uff0c\u4f60\u65e9\u5c31\u88ab\u4eba\u8bbe\u8ba1\u4e86\uff0c\u624d\u4f1a\u843d\u5165\u9677\u9631\uff0c\u90a3\u4eba\u53ef\u80fd\u4e0d\u662f\u590f\u5929\u540c\u3002\u201d \u3000\u3000\u7533\u96ea\u541b\u957f\u957f\u5401\u4e00\u53e3\u6c14\uff0c\u70b9\u70b9\u5934\uff0c\u5fcd\u4e0d\u4f4f\u53c8\u6d41\u4e0b\u773c\u6cea\uff0c\u9053\uff1a\u201c\u5bf9\uff01 \u3000\u3000\u6211\u662f\u65e9\u88ab\u4ed6\u4eec\u8bbe\u8ba1\u4e86\uff0c\u6211\u5e94\u8be5\u4e0d\u662f\u90a3\u4e48\u5992\u6068\u8ff7\u5fc3\u7684\u4eba\u3002\u201d \u3000\u3000\u5c18\u5c01\u4e8c\u5341\u51e0\u5e74\u7684\u60c5\u7231\uff0c\u4e00\u65e6\u590d\u6e05\u660e\uff0c\u7adf\u662f\u5982\u6b64\u4e4b\u6df1\uff0c\u91d1\u4e39\u4e66\u751f\u5728\u5979\u5fc3\u4e2d\u7684\u4efd\u91cf\u4e4b\u91cd\uff0c\u5b9e\u5df2\u5230\u4e86\u751f\u6b7b\u76f8\u4f9d\u7684\u5883\u754c\u3002 \u3000\u3000\u8f6c\u5934\u770b\u53bb\uff0c\u53ea\u89c1\u529b\u654c\u4e09\u5927\u9ad8\u624b\u7684\u6c5f\u67ab\uff0c\u4f3c\u662f\u53ea\u6709\u62db\u67b6\u4e4b\u529b\uff0c\u624b\u4e2d\u7fe0\u7389\u5200\u5b88\u591a\u653b\u5c11\u3002 \u3000\u3000\u5929\u738b\u95e8\u4e3b\u5e26\u7740\u84dd\u51e4\uff0c\u7f13\u7f13\u5411\u56db\u4eba\u4ea4\u624b\u4e4b\u5904\u903c\u8fdb\uff0c\u5929\u540e\u5bab\u7684\u4e09\u4f4d\u5bab\u4e3b\uff0c\u4e5f\u5f00\u59cb\u5411\u524d\u79fb\u52a8\u3002 \u3000\u3000\u4f46\u4e24\u4e2a\u7ec4\u7684\u95e8\u4e0b\u4ece\u5c5e\uff0c\u5e76\u672a\u79fb\u52a8\uff0c\u5979\u50cf\u8fd8\u672a\u52a8\u5408\u51fb\u4e4b\u5fc3\u3002 \u3000\u3000\u4f46\u7533\u96ea\u541b\u5df2\u770b\u7684\u5fc3\u6025\u5982\u711a\uff0c\u624b\u6267\u957f\u5251\uff0c\u79fb\u5411\u6218\u5708\u3002 \u3000\u3000\u5fc3\u6025\u7684\u4e0d\u6b62\u4e2d\u96ea\u541b\uff0c\u6b27\u9633\u662d\u3001\u7f57\u5170\u3001\u4e03\u5b9d\u548c\u5c1a\uff0c\u4e09\u4e2a\u4eba\u4e5f\u56f4\u4e86\u4e0a\u6765\u3002 \u3000\u3000\u7f57\u6c49\u9635\u548c\u5c11\u6797\u5bfa\u50e7\u4f17\u6ca1\u6709\u79fb\u52a8\uff0c\u53cc\u65b9\u4eba\u624b\u4f17\u591a\uff0c\u4e00\u65e6\u5927\u6279\u4eba\u624b\u79fb\u52a8\u3001\u6050\u6015\u4f1a\u5f15\u8d77\u6df7\u6218\u4e4b\u5c40\u3002 \u3000\u3000\u541f\u96ea\u4ed4\u7ec6\u7684\u770b\u4e86\u4e00\u9635\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u7533\u59e8\uff0c\u4e0d\u8981\u51fa\u624b\u554a\uff01\u6c5f\u5927\u54e5\u6ca1\u6709\u62db\u547c\uff0c\u90a3\u5c31\u662f\u8fd8\u6709\u5e94\u6218\u4e4b\u80fd\u3002\u201d \u3000\u3000\u7533\u96ea\u541b\u53ea\u597d\u70b9\u5934\u3002 \u3000\u3000\u541f\u96ea\u8f6c\u8eab\u8fce\u5411\u7236\u6bcd\u548c\u4e03\u5b9d\u5927\u5e08\u3002 \u3000\u3000\u201c\u600e\u4e48\u56de\u4e8b\uff1f\u2026\u2026\u201d\u7f57\u5170\u5df2\u62a2\u5148\u8bf4\u9053\uff1a\u201c\u4f60\u600e\u4e48\u4e0d\u51fa\u624b\u5e2e\u5fd9\uff0c\u8ba9\u6c5f\u67ab\u4ee5\u4e00\u5bf9\u4e09\uff0c\u4ed6\u597d\u50cf\u5feb\u6321\u4e0d\u4f4f\u4e86\uff0c\u90a3\u53ef\u662f\u4e09\u4e2a\u4e00\u6d41\u7684\u9876\u5c16\u9ad8\u624b\u554a\uff01\u201d \u3000\u3000\u201c\u5a18\uff01\u6c5f\u5927\u54e5\u7f69\u5f97\u4f4f\u7684\uff0c\u771f\u8981\u6211\u5e2e\u624b\u65f6\uff0c\u4ed6\u4f1a\u53eb\u6211\u3002\u201d\u541f\u96ea\u9053\uff1a\u201c\u4f60\u4eec\u4e0d\u7528\u51fa\u624b\u5e2e\u5fd9\u3002\u201d \u3000\u3000\u770b\u541f\u96ea\u786e\u65e0\u7126\u6025\u4e4b\u8272\uff0c\u5927\u548c\u5c1a\u604d\u7136\u800c\u609f\uff0c\u7b11\u9053\uff1a\u201c\u6b27\u9633\u5144\uff0c\u7528\u4e0d\u7740\u54b1\u4eec\u63d2\u624b\uff0c\u7ad9\u65c1\u8fb9\u770b\u70ed\u95f9\u5c31\u884c\u4e86\u3002\u201d \u3000\u3000\u201c\u5927\u548c\u5c1a\uff0c\u4f60\u518d\u4ed4\u7ec6\u77a7\u77a7\uff1f\u6211\u770b\u4ed6\u5feb\u9876\u4e0d\u4f4f\u4e86\uff0c\u5982\u679c\u6211\u6ca1\u6709\u770b\u9519\u2026\u2026\u201d\u7f57\u5170\u8bf4\uff1a\u201c\u4ed6\u597d\u50cf\u5df2\u7d2f\u5f97\u6ee1\u5934\u6c57\u6c34\u3002\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u6c5f\u67ab\u662f\u6ee1\u5934\u6c57\u6c34\uff0c\u4f46\u590f\u5929\u540c\u3001\u767d\u5929\u5316\u3001\u53f8\u5f92\u827a\u4e24\u628a\u5200\u3001\u4e00\u652f\u5251\uff0c\u5168\u529b\u65bd\u4e3a\uff0c\u8fd8\u662f\u6ca1\u6cd5\u5b50\u4f24\u5f97\u4e86\u6c5f\u67ab\uff0c\u4f3c\u4e4e\u662f\u53cc\u65b9\u9762\u90fd\u5f88\u5403\u529b\u3002 \u3000\u3000\u8389\u201d\u4e0d\u8981\u7d27\uff0c\u6c5f\u5927\u54e5\u81ea\u4fdd\u7684\u80fd\u529b\u5f88\u5f3a\u4e00\u201d\u541f\u96ea\u9053\uff1a\u201c\u522b\u770b\u4ed6\u7d2f\u51fa\u4e00\u5934\u6c57\uff0c\u518d\u6491\u4e2a\u4e09\u4e94\u767e\u62db\uff0c\u8fd8\u662f\u4e00\u6837\u3002\u201d \u3000\u3000\u7f57\u5170\u77aa\u4e86\u5973\u513f\u4e00\u773c\uff0c\u9053\uff1a\u201c\u5927\u4e2b\u5934\uff0c\u4f60\u518d\u770b\u770b\uff0c\u6c5f\u67ab\u53ef\u662f\u4f60\u7684\u592b\u5a7f\uff0c\u4ed6\u88ab\u6740\u4e86\uff0c\u4f60\u5c31\u8981\u5b88\u671b\u95e8\u5be1\uff0c\u6211\u53ef\u4e0d\u8bb8\u4f60\u518d\u5ac1\u4eba\u3002\u201d \u3000\u3000\u201c\u5a18\uff01\u522b\u5492\u6c5f\u5927\u54e5\uff0c\u4ed6\u6b7b\u4e86\uff0c\u4f60\u4e5f\u4f1a\u5931\u53bb\u4e24\u4e2a\u5973\u513f\u3002\u6211\u548c\u971c\u59b9\u4e0d\u4f1a\u5b88\u671b\u95e8\u5be1\uff0c\u4f1a\u8ffd\u968f\u4ed6\u4e8e\u6cc9\u4e0b\uff0c\u2026\u2026\u201d\u541f\u96ea\u58f0\u97f3\u8f6c\u4f4e\uff0c\u9053\uff1a\u201c\u5a18\uff0c\u518d\u4e89\u5435\u4e0b\u53bb\uff0c\u5c31\u4f1a\u771f\u7684\u7834\u574f\u4ed6\u7684\u9884\u8c0b\u4e86\u3002\u201d \u3000\u3000\u6b27\u9633\u662d\u9053\uff1a\u201c\u597d\uff01\u6211\u4eec\u53ea\u770b\u4e0d\u8bf4\u5c31\u662f\u3002\u201d \u3000\u3000\u201c\u5bf9\uff01\u770b\u7684\u795e\u60c5\u8d8a\u7d27\u5f20\u8d8a\u597d\uff01\u5c31\u662f\u4e0d\u8981\u51fa\u624b\u5e2e\u5fd9\u3002\u201d\u541f\u96ea\u9053\uff1a\u201c\u4e00\u5e2e\u5c31\u4e71\u3002\u201d\u8f6c\u8eab\u5411\u6218\u5708\u884c\u53bb\u3002 \u3000\u3000\u201c\u5927\u4e2b\u5934\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u662f\u4e0d\u6709\u70b9\u75af\u4e86\uff0c\u7b80\u76f4\u662f\u8bed\u65e0\u4f26\u6b21\u2026\u2026\u201d \u3000\u3000\u7f57\u5170\u9053\uff1a\u201c\u6709\u5907\u65e0\u60a3\uff0c\u54b1\u4eec\u4e5f\u8ddf\u4e0a\u53bb\uff0c\u60c5\u5f62\u4e0d\u5bf9\uff0c\u5c31\u51fa\u624b\u5e2e\u6c5f\u67ab\u4e00\u628a\u3002\u201d \u3000\u3000\u5927\u548c\u5c1a\u53f9\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u7f57\u5170\uff0c\u53ef\u4ee5\u770b\uff0c\u4e5f\u53ef\u4ee5\u9a82\uff0c\u5c31\u662f\u4e0d\u8981\u51fa\u624b\u3002\u201d \u3000\u3000\u201c\u5929\u738b\u95e8\u3001\u5929\u540e\u5bab\u7684\u4e94\u4e2a\u9996\u8981\u4eba\u7269\uff0c\u5168\u90fd\u56f4\u4e86\u4e0a\u53bb\u2026\u2026\u201d\u7f57\u5170\u9053\uff1a\u201c\u81f3\u5c11\uff0c\u54b1\u4eec\u53ef\u4ee5\u62e6\u4f4f\u4ed6\u4eec\u63d2\u624b\u5427\uff1f\u201d \u3000\u3000\u201c\u63d2\u4e0d\u4e0a\u624b\u7684\uff0c\u4f60\u770b\u4e24\u628a\u5200\u3001\u4e00\u652f\u5251\uff0c\u5408\u51fb\u7684\u5a01\u52bf\uff0c\u5df2\u5728\u4e24\u4e08\u65b9\u5706\u5185\uff0c\u5e03\u6ee1\u4e86\u5200\u5251\u5229\u5203\uff0c\u65e0\u575a\u4e0d\u6467\uff0c\u9510\u98ce\u5982\u5200\uff0c\u2026\u2026\u201d\u5927\u548c\u5c1a\u9053\uff1a\u201c\u590f\u5929\u540c\u3001\u767d\u5929\u5316\u3001\u53f8\u5f92\u827a\uff0c\u90e8\u5df2\u5728\u5168\u529b\u62a2\u653b\uff0c\u53ef\u5c31\u662f\u6740\u4e0d\u4e86\u6c5f\u67ab\uff0c\u518d\u6709\u4eba\u63d2\u624b\uff0c\u5f88\u53ef\u80fd\u662f\u81ea\u5df1\u6740\u81ea\u5df1\u4e86\u3002\u201d \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u56db\u4e2a\u4eba\u640f\u6740\u7684\u6fc0\u70c8\uff0c\u5df2\u5230\u4e86\u65e0\u6cd5\u518d\u5206\u654c\u6211\u7684\u5883\u754c\uff0c\u4e5f\u6ca1\u6709\u4eba\u80fd\u9884\u6599\uff0c\u8c01\u4f1a\u5728\u5200\u5251\u51fb\u649e\u4e0b\u6e85\u8840\u800c\u4ea1\u3002 \u3000\u3000\u5929\u5de5\u95e8\u4e3b\u4e00\u76b1\u7709\u5934\uff0c\u9053\uff1a\u201c\u84dd\u51e4\uff0c\u4f60\u8bf4\u8bf4\u770b\uff0c\u4ed6\u4eec\u4f1a\u4e0d\u4f1a\u5f88\u5feb\u51b3\u51fa\u80dc\u8d25\uff1f\u201d \u3000\u3000\u201c\u770b\u4e0d\u51fa\u6765\u3002\u201d\u84dd\u51e4\u9053\uff1a\u201c\u5200\u5200\u53d6\u547d\uff0c\u5251\u5251\u593a\u7470\uff0c\u597d\u50cf\u662f\u53cc\u65b9\u90fd\u7528\u51fa\u4e86\u5168\u529b\uff0c\u90a3\u4e2a\u4e00\u95ea\u795e\uff0c\u5c31\u4f1a\u4e22\u53bb\u6027\u547d\u3002\u201d \u3000\u3000\u5929\u738b\u95e8\u51b7\u51b7\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u5fc3\u4e2d\u5e0c\u671b\u4ed6\u4eec\u90a3\u4e00\u4e2a\u80fd\u6d3b\u7740\uff1f\u201d\u8fd9\u53e5\u8bdd\u95ee\u7684\u8e4a\u8df7\uff1f \u3000\u3000\u84dd\u51e4\u867d\u7136\u5fc3\u5934\u9707\u52a8\uff0c\u4f46\u5374\u795e\u8272\u5e73\u9759\uff0c\u4ece\u5bb9\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8c01\u80fd\u6d3b\u4e0b\u6765\u90fd\u662f\u4e00\u6837\uff0c\u7559\u4e0b\u7684\u603b\u662f\u6700\u5f3a\u7684\u654c\u4eba\uff01\u201d \u3000\u3000\u679c\u7136\u662f\u6c89\u7740\u51b7\u9759\uff0c\u5e94\u5bf9\u5f97\u76f4\uff0c\u5929\u738b\u95e8\u4e3b\u4e00\u76f4\u7559\u5fc3\u89c2\u5bdf\uff0c\u5374\u77a7\u4e0d\u51fa\u4e00\u70b9\u7834\u5b9a\u3002 \u3000\u3000\u201c\u672c\u95e8\u8981\u5982\u4f55\u5e94\u53d8\uff1f\u201d\u5929\u738b\u95e8\u4e3b\u4f4e\u58f0\u9053\uff1a\u201c\u6c5f\u67ab\u6b66\u529f\u5f88\u9ad8\uff0c\u4f46\u6700\u53ef\u6015\u7684\u654c\u4eba\uff0c\u597d\u50cf\u662f\u90a3\u4e2a\u4e2b\u5934\uff1f\u201d\u76ee\u5149\u4e00\u63a0\u541f\u96ea\u3002 \u3000\u3000\u201c\u53d8\u6570\u51fa\u4eba\u610f\u5916\uff0c\u6211\u4eec\u638c\u63e1\u7684\u654c\u60c5\uff0c\u5168\u4e0d\u51c6\u786e\u3002\u201d\u84dd\u51e4\u9053\uff1a\u201c\u6b27\u9633\u541f\u96ea\u7684\u6b66\u529f\u8be1\u5f02\uff0c\u5177\u6709\u51fa\u624b\u53d6\u547d\u7684\u9510\u5229\uff0c\u6b27\u9633\u541f\u971c\u548c\u5f20\u56db\u59d1\u53c8\u9690\u800c\u4e0d\u73b0\uff0c\u662f\u4e00\u652f\u53ef\u6015\u7684\u4f0f\u5175\u3002 \u3000\u3000\u53db\u5f92\u9752\u51e4\uff0c\u4e5f\u4e0d\u662f\u672c\u95e8\u4e2d\u4e09\u9b54\u3001\u4e94\u602a\u3001\u56db\u5927\u5929\u9e45\u6240\u80fd\u5bf9\u4ed8\uff0c\u5982\u679c\u6211\u4eec\u6ca1\u6709\u9ad8\u624b\u652f\u63f4\uff0c\u6700\u597d\u662f\u4e0d\u8981\u6295\u5165\u6218\u9635\u3002 \u3000\u3000\u5206\u6790\u654c\u52bf\u5982\u6b64\uff0c\u4f46\u5982\u4f55\u88c1\u5b9a\uff0c\u8fd8\u8981\u5929\u738b\u4f5c\u4e3b\u4e86\uff0c\u56e0\u4e3a\uff0c\u5f1f\u5b50\u65e0\u6cd5\u4e86\u89e3\u6559\u4e3b\u6709\u4f55\u6307\u793a\uff1f\u5929\u540e\u5bab\u80fd\u4e0d\u80fd\u548c\u672c\u95e8\u771f\u6b63\u914d\u5408\uff1f\u201d \u3000\u3000\u5929\u738b\u95e8\u4e3b\u70b9\u5934\uff0c\u9053\uff1a\u201c\u597d\uff01\u51b3\u5b9a\u6295\u5165\u65f6\uff0c\u6211\u4f1a\u4e0b\u4ee4\u7ed9\u4f60\u3002\u201d \u3000\u3000\u663e\u7136\uff0c\u4ed6\u7684\u5185\u5fc3\u4e4b\u4e2d\uff0c\u8fd8\u662f\u85cf\u6709\u9690\u5bc6\uff0c\u6ca1\u6709\u8bf4\u51fa\u6765\u3002 \u3000\u3000\u84dd\u51e4\u5341\u5206\u5e0c\u671b\u80fd\u4e86\u89e3\u5168\u76d8\u5185\u60c5\uff1f\u90a3\u624d\u771f\u80fd\u7ed9\u6c5f\u67ab\u6700\u5927\u7684\u5e2e\u52a9\uff0c\u5236\u654c\u673a\u5148\uff0c\u4f46\u5979\u6ca1\u6709\u8ffd\u95ee\uff0c\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u7b49\u5019\u95e8\u4e3b\u4ee4\u8c15\u3002\u201d \u3000\u3000\u8f6c\u5934\u640f\u6740\u60c5\u5f62\uff0c\u4ecd\u662f\u4e2a\u4e0d\u80dc\u4e0d\u8d25\u4e4b\u5c40\uff0c\u6c5f\u67ab\u5b88\u591a\u653b\u5c11\u3002 \u3000\u3000\u84dd\u51e4\u5fc3\u4e2d\u4e00\u52a8\uff0c\u5fd6\u9053\uff1a\u201c\u541f\u96ea\u5c82\u771f\u80fd\u5f3a\u8fc7\u6c5f\u67ab\u4e0d\u6210\uff1f\u6c5f\u90ce\u662f\u5728\u4f2a\u88c5\u6b3a\u654c\uff1f\u51e0\u4e4e\u88ab\u4ed6\u7792\u8fc7\u53bb\u4e86\uff0c\u771f\u662f\u5173\u5df1\u5219\u4e71\uff01 \u3000\u3000\u5012\u662f\u541f\u96ea\u7684\u7f8e\u4e3d\uff0c\u5c24\u80dc\u9752\u51e4\uff0c\u770b\u5929\u738b\u65e9\u5df2\u88ab\u64a9\u52a8\u5fc3\u795e\uff0c\u5f88\u53ef\u80fd\u7a81\u51fa\u5947\u62db\uff0c\u63a0\u8d70\u541f\u96ea\uff0c\u4e0d\u77e5\u5979\u80fd\u4e0d\u80fd\u5e94\u4ed8\u5f97\u4e86\uff1f\u201d \u3000\u3000\u7a81\u7136\u95f4\uff0c\u54cd\u8d77\u4e86\u4e00\u58f0\u5c16\u5389\u5578\u58f0\uff0c\u4f20\u5165\u6218\u9635\u3002 \u3000\u3000\u4e00\u76f4\u91c7\u53d6\u5b88\u52bf\u7684\u6c5f\u67ab\uff0c\u5e94\u58f0\u53cd\u51fb\uff0c\u7fe0\u7389\u5200\u63b7\u8d77\u4e00\u9053\u9752\u8679\uff0c\u4ece\u6781\u4e0d\u53ef\u80fd\u7684\u89d2\u5ea6\uff0c\u653b\u51fa\u4e86\u4e00\u5200\u3002 \u3000\u3000\u4e00\u5200\u4f24\u4eba\uff0c\u53f8\u5f92\u827a\u5f53\u5148\u53d7\u521b\uff0c\u5200\u7531\u524d\u80f8\u5212\u8fc7\uff0c\u88c2\u5f00\u4e86\u8fd1\u4e24\u5c3a\u7684\u4e00\u9053\u4f24\u53e3\u3002 \u3000\u3000\u90a3\u662f\u81f4\u547d\u7684\u91cd\u4f24\uff0c\u4eba\u867d\u672a\u6b7b\uff0c\u4f46\u4e5f\u53ea\u7559\u4e0b\u4e00\u53e3\u6c14\u4e86\u3002 \u3000\u3000\u201c\u8d70\uff01\u201d \u3000\u3000\u590f\u5929\u540c\u5168\u529b\u5288\u51fa\u4e00\u5200\uff0c\u4eba\u5374\u75be\u9000\u516b\u5c3a\uff0c\u767d\u5929\u5316\u7d27\u968f\u9000\u4e0b\u3002 \u3000\u3000\u5929\u540e\u5bab\u4e3b\u3001\u5929\u738b\u95e8\u4e3b\uff0c\u8d70\u7684\u66f4\u5feb\uff0c\u590f\u5929\u540c\u8d70\u5b57\u51fa\u53e3\uff0c\u4ed6\u4eec\u5df2\u9000\u56de\u4e86\u539f\u4f4d\uff0c\u7387\u7740\u5f92\u4ece\u79bb\u53bb\u3002 \u3000\u3000\u671d\u9633\u3001\u6c34\u6708\u3001\u955c\u82b1\u4e09\u5927\u697c\u4e3b\u65ad\u540e\u63a9\u62a4\uff0c\u5929\u9a6c\u5802\u7684\u6b8b\u5175\u8d25\u5c06\uff0c\u75be\u5982\u98d8\u98ce\u800c\u9000\uff0c\u8fde\u53f8\u5f92\u827a\u8fd9\u4e2a\u526f\u603b\u5802\u4e3b\u4e5f\u4e0d\u7ba1\u4e86\u3002 \u3000\u3000\u6c5f\u67ab\u6839\u672c\u6ca1\u6709\u8ffd\u88ad\uff0c\u4ed6\u4f3c\u662f\u6253\u7684\u592a\u7d2f\u4e86\uff0c\u76d8\u5750\u5728\u5730\u4e0a\u4f11\u606f\u3002 \u3000\u3000\u5927\u519b\u63a9\u6765\uff0c\u65e0\u58f0\u65e0\u606f\uff0c\u6570\u767e\u9ad8\u624b\u7a81\u7136\u51fa\u73b0\u3002 \u3000\u3000\u4f46\u64a4\u9000\u65f6\u4ea6\u5982\u98ce\u63b7\u843d\u53f6\uff0c\u4e00\u8f6c\u5934\uff0c\u5c31\u8d70\u7684\u4eba\u5f71\u4e0d\u89c1\u3002 \u3000\u3000\u6ca1\u6709\u51fa\u73b0\u622a\u6740\uff0c\u5929\u540e\u5bab\u3001\u5929\u738b\u95e8\u3001\u5929\u9a6c\u5802\uff0c\u90fd\u64a4\u9000\u7684\u5341\u5206\u987a\u5229\u3002 \u3000\u3000\u201c\u6c5f\u67ab\uff0c\u4f60\u662f\u771f\u4e86\u4e0d\u8d77\u2026\u2026\u201d\u7533\u96ea\u541b\u9053\uff1a\u201c\u4ee5\u4e00\u5bf9\u4e09\uff0c\u4e09\u4e2a\u90fd\u662f\u6b66\u6797\u4e2d\u7684\u9876\u5c16\u9ad8\u624b\uff0c\u5509\uff01\u53ea\u6015\u4f60\u4e5f\u7d2f\u574f\u4e86\uff0c\u4e0d\u7528\u56de\u7b54\u6211\uff0c\u597d\u597d\u7684\u5750\u606f\u4e00\u9635\u5427\uff01\u201d \u3000\u3000\u201c\u522b\u88c5\u4e86\uff0c\u6c5f\u67ab\uff0c\u5feb\u8c22\u8c22\u4f60\u7533\u59e8\u7684\u5173\u5fc3\u3002\u201d\u5f20\u56db\u59d1\u7a81\u7136\u51fa\u73b0\uff0c\u8eab\u540e\u7d27\u968f\u7740\u541f\u971c\u3001\u9752\u51e4\u548c\u6885\u82b1\u3002 \u3000\u3000\u6c5f\u67ab\u4e00\u4e0b\u5b50\u8df3\u8d77\u6765\uff0c\u9053\uff1a\u201c\u591a\u8c22\u7533\u59e8\u3002\u201d \u3000\u3000\u201c\u4f60\u2026\u2026\u4f60\u5c0f\u5b50\u8fd8\u6ca1\u6709\u7528\u51fa\u5168\u529b\u2026\u2026\u5440\uff01\u201d\u5944\u5944\u4e00\u606f\u7684\u53f8\u5f92\u827a\uff0c\u7528\u5c3d\u6c14\u529b\u8bf4\u4e86\u6700\u540e\u4e00\u53e5\u8bdd\uff0c\u95ed\u76ee\u800c\u901d\u3002 \u3000\u3000\u201c\u597d\u554a\uff01\u56db\u59d1\uff0c\u4f60\u4eec\u65e9\u5c31\u5546\u91cf\u597d\u5566\uff01\u201d\u7f57\u5170\u53eb\u9053\uff1a\u201c\u53ef\u628a\u6211\u7ed9\u51a4\u82e6\u4e86\u3002\u201d \u3000\u3000\u201c\u5a18\uff01\u6211\u4e0d\u662f\u65e9\u8bf4\u4e86\uff0c\u4e0d\u7528\u62c5\u5fc3\u6c5f\u5927\u54e5\uff01\u201d\u541f\u96ea\u7b11\u9053\uff1a\u201c\u5a18\u5c31\u662f\u4e0d\u76f8\u4fe1\u6211\u3002\u201d \u3000\u3000\u201c\u4ed6\u7d2f\u5f97\u4e00\u5934\u5927\u6c57\uff0c\u6211\u600e\u4e48\u77e5\u9053\u4f1a\u662f\u88c5\u7684\uff01\u201d\u7f57\u5170\u7684\u5a07\u55d4\u672a\u606f\u3002 \u3000\u3000\u201c\u5bf9\u4e0d\u4f4f\u5566\uff01\u6211\u662f\u60f3\u8bf1\u51fa\u90a3\u4f4d\u5927\u6559\u4e3b\u548c\u56db\u5927\u795e\u901a\u4f7f\u8005\uff01\u201d\u6c5f\u67ab\u6025\u6025\u89e3\u91ca\u3002 \u3000\u3000\u201c\u6ca1\u6cd5\u5b50\u554a\uff01\u4e08\u6bcd\u5a18\u5173\u5fc3\u5973\u5a7f\uff0c\u597d\u50cf\u4f20\u4e86\u51e0\u5343\u5e74\u5566\uff01\u6211\u53ef\u4e0d\u80fd\u514d\u4fd7\u3002\u201d\u7f57\u5170\u5fcd\u4e0d\u4f4f\u7b11\u4e86\u8d77\u6765\uff0c\u6025\u6025\u4e3e\u624b\u63a9\u4f4f\u5634\u5df4\u3002 \u3000\u3000\u201c\u7f57\u5170\uff0c\u6211\u6ca1\u6709\u548c\u4ed6\u5546\u91cf\u8fc7\uff01\u201d\u5f20\u56db\u59d1\u9053\uff0c \u3000\u3000\u201c\u6211\u6839\u672c\u4e0d\u77e5\u9053\u6c5f\u67ab\u7684\u6b66\u529f\u9ad8\u5230\u4ec0\u4e48\u5883\u754c\uff01\u53ea\u662f\u6211\u786e\u5b9a\u4e86\u90a3\u4f4d\u6559\u4e3b\u4e0d\u5728\uff0c\u5c31\u60f3\u8ba9\u4ed6\u5168\u529b\u53d1\u6325\u4e00\u4e0b\uff0c\u679c\u7136\u662f\u4e0d\u8d1f\u4f17\u671b\u3002\u201d \u3000\u3000\u76ee\u5149\u4e00\u63a0\u541f\u971c\uff0c\u63a5\u9053\uff1a\u201c\u5012\u662f\u6e05\u695a\u4e86\u541f\u971c\u7684\u5389\u5bb3\uff0c\u7eb5\u6a2a\u654c\u9635\uff0c\u5927\u5f00\u6740\u6212\u3002\u201d \u3000\u3000\u201c\u9752\u51e4\u548c\u6885\u82b1\u59d0\u59d0\uff0c\u4e5f\u662f\u5fc3\u72e0\u624b\u8fa3\u5440\uff01\u201d\u541f\u9732\u7b11\u9053\uff1a\u201c\u5979\u4eec\u628a\u5f20\u59e8\u7684\u4e94\u884c\u9041\u672f\uff0c\u5c55\u73b0\u5df2\u5230\u6781\u81f4\uff0c\u6740\u4eba\u4e4b\u591a\uff0c\u7edd\u4e0d\u5728\u6211\u4e4b\u4e0b\u3002\u201d \u3000\u3000\u6c5f\u67ab\u8f6c\u5934\u770b\u53bb\uff0c\u89c1\u4e09\u5973\u8eab\u4e0a\u8863\u670d\u6e85\u8840\u6591\u6591\uff0c\u60f3\u6765\uff0c\u5979\u4eec\u786e\u5b9e\u6740\u4f24\u4e86\u4e0d\u5c11\u7684\u4eba\u3002 \u3000\u3000\u201c\u8bf4\u8d77\u6765\uff0c\u8fd8\u8981\u8c22\u8c22\u541f\u971c\u59b9\u5b50\u4e86\u2026\u2026\u201d\u9752\u51e4\u9053\uff1a\u201c\u4f60\u8fde\u591c\u4f20\u6388\u6211\u4eec\u4e09\u62db\u6740\u6cd5\uff0c\u6211\u4eec\u662f\u73b0\u5b66\u73b0\u5356\u3002\u201d \u3000\u3000\u201c\u7075\u7684\u5f88\u554a\uff01\u201d\u6885\u82b1\u9053\uff1a\u201c\u51fa\u5fc5\u4f24\u4eba\uff0c\u4ece\u4e0d\u843d\u7a7a\uff01\u201d \u3000\u3000\u4e00\u77a5\u6c5f\u67ab\uff0c\u63a5\u9053\uff1a\u201c\u5e2e\u6211\u548c\u9752\u51e4\u8c22\u8c22\u541f\u971c\u5427\uff01\u6c42\u5979\u518d\u591a\u4f20\u6211\u4eec\u4e24\u62db\u3002\u201d \u3000\u3000\u6c5f\u67ab\u5012\u662f\u542c\u8bdd\u7684\u5f88\uff0c\u76ee\u5149\u8f6c\u6ce8\u5728\u541f\u971c\u7684\u8138\u4e0a\uff0c\u9053\uff1a\u201c\u8f9b\u82e6\u4f60\u4e86\u3002\u201d \u3000\u3000\u541f\u971c\u5fc3\u4e2d\u9ad8\u5174\uff0c\u5a07\u58f0\u8bf4\u9053\uff1a\u201c\u5f20\u59e8\u8981\u6211\u4eec\u6f5c\u4f0f\u4e00\u4fa7\uff0c\u4ed6\u4eec\u4e00\u62e5\u800c\u4e0a\u65f6\uff0c\u6211\u4eec\u5c31\u7531\u4fa7\u9762\u7a81\u88ad\uff0c\u5e2e\u52a9\u6c5f\u5927\u54e5\u7684\uff0c\u51fa\u624b\u5f53\u7136\u8981\u72e0\u3002 \u3000\u3000\u60f3\u4e0d\u5230\u6ca1\u5e2e\u4e0a\u6c5f\u5927\u54e5\u7684\u5fd9\uff0c\u5374\u5e2e\u4e86\u5929\u9a6c\u5802\u6563\u5175\u9003\u5c06\u7684\u5fd9\uff0c\u8fd9\u771f\u662f\u4e00\u573a\u5927\u6df7\u6218\uff0c\u90a3\u4e9b\u963b\u6740\u5929\u9a6c\u5802\u7684\u9ed1\u8863\u4eba\uff0c\u4e2a\u4e2a\u5200\u6cd5\u6bd2\u8fa3\uff0c\u6740\u4e86\u5929\u9a6c\u5802\u4e0d\u5c11\u7684\u4eba\u3002 \u3000\u3000\u5f20\u59e8\u4e00\u58f0\u4ee4\u4e0b\uff0c\u8981\u6211\u4eec\u52a9\u654c\u6740\u654c\uff0c\u8c01\u77e5\u9053\uff0c\u4e00\u4e0b\u5b50\u6740\u7ea2\u4e86\u773c\uff0c\u6536\u4e0d\u4f4f\u624b\u5566\uff01\u201d\u8f6c\u987e\u4e86\u4e03\u5b9d\u5927\u5e08\u4e00\u9650\uff0c\u63a5\u9053\uff1a\u201c\u548c\u5c1a\u4f2f\u4f2f\uff0c\u6211\u4e0d\u662f\u5b58\u5fc3\u5982\u6b64\u2026\u2026\u201d \u3000\u3000\u201c\u90a3\u4e9b\u9ed1\u8863\u5200\u624b\uff0c\u771f\u7684\u8be5\u6b7b\uff0c\u4ed6\u4eec\u5b66\u7684\u4e0d\u662f\u6b66\u529f\uff0c\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u800c\u662f\u6740\u4eba\u624b\u6cd5\uff0c\u5929\u9a6c\u5802\u4e2d\u7684\u7cbe\u9510\uff0c\u6ca1\u6709\u4eba\u80fd\u6321\u8fc7\u4e09\u5200\uff0c\u5927\u90fd\u662f\u4e00\u5200\u5c31\u6b7b\uff0c\u6240\u4ee5\uff0c\u6211\u6ca1\u6709\u963b\u6b62\u541f\u971c\u6740\u4ed6\u4eec\uff01\u201d \u3000\u3000\u201c\u963f\u5f25\u9640\u4f5b\uff01\u6211\u548c\u5c1a\u6ca1\u8bb2\u8bdd\u5440\uff01\u201d \u3000\u3000\u201c\u53ef\u662f\uff0c\u4f60\u7684\u8d23\u4efb\u2026\u2026\u201d \u3000\u3000\u4e03\u5b9d\u5927\u5e08\u4e00\u6014\uff0c\u9053\uff1a\u201c\u6211\u548c\u5c1a\u6709\u8d23\u4efb\uff1f\u201d \u3000\u3000\u201c\u662f\u554a\uff01\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u4ed6\u4eec\u88ab\u590f\u5929\u540c\u8680\u5fc3\u9b54\u97f3\u63a7\u5236\u4e86\u5341\u51e0\u5e74\uff0c\u4f60\u7684\u5927\u60b2\u68b5\u5531\uff0c\u4f7f\u4ed6\u4eec\u7a81\u7136\u7684\u89e3\u7f1a\u9192\u609f\uff0c\u8fd8\u6ca1\u6709\u8c03\u6574\u8fc7\u6765\uff0c\u5c31\u9047\u4e0a\u622a\u6740\uff0c\u5426\u5219\uff0c\u90a3\u4e9b\u9ed1\u8863\u5200\u624b\uff0c\u867d\u7136\u51f6\u72e0\uff0c\u4e5f\u65e0\u6cd5\u5982\u780d\u74dc\u5207\u83dc\u4e00\u822c\u7684\u6740\u4ed6\u4eec\uff0c\u4ed6\u4eec\u90fd\u662f\u5404\u6709\u7edd\u6280\u7684\u6c5f\u6e56\u9ad8\u624b\u554a\uff01\u201d \u3000\u3000\u201c\u8bf4\u7684\u6709\u7406\uff0c\u8fd9\u7b14\u8d26\u5e94\u8be5\u7b97\u5728\u6211\u548c\u5c1a\u7684\u5934\u4e0a\uff0c\u6211\u4f5b\u6709\u7075\uff0c\u548c\u5c1a\u8ba4\u7f6a\u4e86\u3002\u201d\u5408\u638c\u5f53\u80f8\uff0c\u53e3\u4e2d\u5ff5\u5ff5\u6709\u8bcd\u3002 \u3000\u3000\u6ca1\u6709\u4eba\u542c\u51fa\u4ed6\u5ff5\u8bf5\u7684\u4ec0\u4e48\uff1f\u60f3\u6765\u4f3c\u662f\u8d85\u5ea6\u7ecf\u6587\u3002 \u3000\u3000\u4f46\u89c1\u4ed6\u795e\u60c5\u6e10\u5165\u8083\u7a46\uff0c\u4f5b\u5149\u53c8\u9690\u9690\u800c\u73b0\u3002 \u3000\u3000\u8fd9\u548c\u5c1a\u6e38\u620f\u98ce\u5c18\uff0c\u4e0d\u4f46\u6709\u4e00\u8eab\u597d\u6b66\u529f\uff0c\u800c\u4e14\u662f\u4e00\u4f4d\u4f5b\u6cd5\u7cbe\u6df1\u7684\u9ad8\u50e7\u3002 \u3000\u3000\u53ea\u89c1\u5c11\u6797\u50e7\u4f17\u653e\u4e0b\u5175\u5203\uff0c\u5408\u638c\u5f53\u80f8\uff0c\u4e00\u9f50\u8dea\u4e86\u4e0b\u53bb\uff0c\u53e3\u9f7f\u542f\u52a8\uff0c\u8ddf\u7740\u5ff5\u8d77\u7ecf\u6765\u3002 \u3000\u3000\u541f\u971c\u3001\u6885\u82b1\u3001\u9752\u51e4\uff0c\u7f13\u7f13\u79fb\u52a8\u83b2\u6b65\uff0c\u884c\u5230\u4e86\u4e03\u5b9d\u5927\u5e08\u8eab\u524d\uff0c\u8dea\u4e86\u4e0b\u53bb\uff0c\u5782\u9996\u4e0d\u8bed\u3002 \u3000\u3000\u541f\u96ea\u4e5f\u5f03\u53bb\u624b\u4e2d\u4e4b\u5251\uff0c\u884c\u8fd1\u5927\u548c\u5c1a\u7684\u8eab\u4fa7\uff0c\u5408\u638c\u8dea\u4e0b\u3002 \u3000\u3000\u201c\u5b7d\u662f\u5b7d\u6765\u7f18\u662f\u7f18\uff0c\u6148\u822a\u666e\u6e21\u5341\u4e07\u51a4\uff0c\u62a4\u6cd5\u987b\u501f\u91d1\u94a2\u6775\uff0c\u8840\u6c61\u4e0d\u67d3\u7d20\u5fc3\u83b2\u3002\u201d\u4e03\u5b9d\u5927\u5e08\u4e24\u53ea\u624b\u6309\u5728\u541f\u96ea\u3001\u971c\u7684\u5934\u4e0a\uff0c\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8d77\u6765\u5566\uff0c\u4e2b\u5934\u4eec\u3002\u201d \u3000\u3000\u8fd9\u4e00\u7b11\u53c8\u590d\u5e38\u6001\uff0c\u5c11\u6797\u7fa4\u50e7\u8ddf\u7740\u6361\u5203\u800c\u8d77\u3002 \u3000\u3000\u541f\u96ea\u3001\u541f\u971c\u3001\u9752\u51e4\u3001\u6885\u82b1\uff0c\u4e5f\u7f13\u7f13\u7ad9\u8d77\u8eab\u5b50\u3002 \u3000\u3000\u5f20\u56db\u59d1\u957f\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5927\u548c\u5c1a\uff0c\u4f60\u597d\u50cf\u771f\u6709\u70b9\u9053\u884c\u554a\uff1f\u201d \u3000\u3000\u201c\u548c\u5c1a\u5403\u8089\u559d\u9152\uff0c\u6709\u4ec0\u4e48\u9053\u884c\u53ef\u8a00\uff1f\u201d \u3000\u3000\u201c\u9152\u8089\u7a7f\u80a0\u8fc7\uff0c\u4f5b\u5728\u5fc3\u4e2d\u5750\u5440\uff01\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u8840\u6c61\u4e0d\u67d3\u7d20\u5fc3\u83b2\uff0c\u4e0b\u5730\u72f1\u5e26\u7740\u6211\u5c31\u884c\u4e86\u3002\u201d \u3000\u3000\u201c\u548c\u5c1a\u4e0d\u6562\uff0c\u4f60\u662f\u4e03\u4e16\u4fee\u5f97\u4eca\u751f\u7f18\uff0c\u83ab\u767b\u9e4a\u6865\u7a7a\u5f80\u8fd4\uff01\u201d \u3000\u3000\u201c\u6211\u662f\u4ee5\u672f\u2026\u2026\u201d \u3000\u3000\u201c\u4ee5\u672f\u5fa1\u9053\u2026\u2026\u201d\u5927\u548c\u5c1a\u6253\u65ad\u4e86\u6c5f\u67ab\u7684\u8bdd\uff0c\u9053\uff1a\u201c\u4e00\u4e16\u98ce\u6d41\u4e09\u4e16\u8fd8\uff0c\u4e09\u4e16\u98ce\u6d41\u8fd8\u4e0d\u5b8c\uff0c\u81f3\u60c5\u53ef\u4ee5\u611f\u5929\u5730\uff0c\u5170\u56e0\u7d6e\u679c\u603b\u662f\u7f18\u2026\u2026\u201d \u3000\u3000\u201c\u90a3\u662f\u8bf4\uff0c\u6211\u4eec\u4e0b\u8f88\u5b50\u8fd8\u8981\u5ac1\u7ed9\u6c5f\u5927\u54e5\u4e86\u3002\u201d \u3000\u3000\u5927\u548c\u5c1a\u4e00\u62cd\u8111\u888b\uff0c\u7b11\u9053\uff1a\u201c\u541f\u971c\uff0c\u4f60\u771f\u628a\u548c\u5c1a\u6015\u7ed9\u5411\u4f4f\u4e86\uff0c\u662f\u4e0d\u662f\u771f\u6709\u4e0b\u8f88\u5b50\uff1f\u548c\u5c1a\u53ef\u4e0d\u6562\u80af\u5b9a\uff0c\u4f5b\u66f0\u897f\u65b9\u6709\u51c0\u571f\uff0c\u4e16\u8bf4\u6cb9\u5c3d\u706f\u81ea\u706d\uff0c\u672c\u65e0\u660e\u955c\uff0c\u4f55\u5904\u62ed\u5c18\u57c3\u3002\u201d \u3000\u3000\u6c5f\u67ab\u5fc3\u7ed3\u5ffd\u89e3\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5bf9\uff01\u54b1\u4eec\u4e0b\u5c71\u53bb\uff0c\u559d\u4e2a\u75db\u5feb\u3002\u201d \u3000\u3000\u767e\u660e\u5927\u5e08\u7f13\u6b65\u884c\u6765\uff0c\u5bf9\u4e03\u5b9d\u50e7\u5408\u638c\u4e00\u793c\uff0c\u9053\uff1a\u201c\u5931\u656c\u4e86\u3002\u201d \u3000\u3000\u5927\u548c\u5c1a\u6025\u6025\u8fd8\u793c\uff0c\u9053\uff1a\u201c\u8001\u65b9\u4e08\uff0c\u8bf7\u56de\u5bfa\u5427\uff01\u4f59\u4e0b\u7684\u4e8b\uff0c\u4ea4\u7ed9\u5f20\u59d1\u5a18\u3002\u201d \u3000\u3000\u201c\u8001\u8872\u660e\u767d\uff0c\u5f20\u59d1\u5a18\u4f46\u6709\u6240\u547d\uff0c\u5c11\u6797\u5bfa\u5168\u529b\u4ee5\u8d74\u3002\u201d \u3000\u3000\u5e26\u7740\u5c11\u6797\u50e7\u4f17\uff0c\u9000\u56de\u5bfa\u5185\u3002 \u3000\u3000\u201c\u5927\u548c\u5c1a\uff0c\u62a4\u6cd5\u987b\u501f\u91d1\u94a2\u6775\uff0c\u6211\u4eec\u90fd\u662f\u90a3\u6839\u6775\u4e86\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u4f60\u548c\u5c1a\u53ef\u4e5f\u8981\u7b97\u4e00\u4efd\u3002\u201d \u3000\u3000\u201c\u548c\u5c1a\u672c\u662f\u5e94\u52ab\u4eba\uff0c\u5f20\u59d1\u5a18\u4e00\u58f0\u4ee4\u4e0b\uff0c\u8d74\u6c64\u6295\u706b\uff0c\u4e07\u6b7b\u4e0d\u8f9e\u3002\u201d \u3000\u3000\u6b27\u9633\u662d\u5ffd\u7136\u53f9\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8fd9\u4e9b\u65f6\uff0c\u65e5\u591c\u82e6\u7ec3\u5251\u9635\uff0c\u53c8\u62c9\u4e86\u534a\u5929\u7684\u67b6\u5f0f\uff0c\u5374\u662f\u4e00\u5251\u672a\u51fa\uff0c\u4e00\u77e2\u672a\u53d1\u3002\u201d \u3000\u3000\u4ed6\u80f8\u6000\u5927\u4e49\uff0c\u60c5\u7231\u4e13\u4e00\uff0c\u677e\u6708\u98ce\u6807\uff0c\u5149\u660e\u78ca\u843d\uff0c\u56de\u9996\u524d\u5c18\u65e0\u4e00\u6068\u4e8b\uff0c\u662f\u53d7\u611f\u5e94\u6700\u8f7b\u7684\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u201c\u6b27\u9633\u5144\uff0c\u6076\u6218\u624d\u8981\u5f00\u59cb\uff0c\u6709\u5f97\u4f60\u5927\u663e\u8eab\u624b\u7684\u673a\u4f1a\u3002\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u5927\u548c\u5c1a\uff0c\u4f60\u5e26\u7740\u541f\u96ea\u3001\u541f\u971c\uff0c\u7acb\u523b\u51fa\u52a8\uff0c\u6613\u5bb9\u8feb\u91c7\u654c\u8e2a\uff0c\u81f3\u4e8e\u9152\u561b\uff01 \u3000\u3000\u53ea\u6709\u5fd9\u4e2d\u5077\u95f2\u559d\u4e24\u53e3\u4e86\uff0c\u80e1\u840d\u3001\u6bb5\u4e5d\uff0c\u5e08\u5f92\u7236\u5b50\uff0c\u5df2\u5728\u5c71\u4e0b\u7b49\u5019\u3002\u201d \u3000\u3000\u201c\u548c\u5c1a\u8fd9\u5c31\u8d70\uff01\u201d \u3000\u3000\u5e26\u7740\u4e24\u4e2a\u5c0f\u72d0\u5973\uff0c\u6025\u6025\u79bb\u53bb\u3002 \u3000\u3000\u201c\u9752\u51e4\uff01\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u60f3\u6cd5\u5b50\u548c\u84dd\u51e4\u8054\u7edc\uff0c\u80fd\u7ea6\u5979\u8ddf\u6211\u89c1\u4e00\u9762\u90a3\u5c31\u66f4\u597d\u4e86\u3002\u201d \u3000\u3000\u201c\u662f\uff01\u201d\u9752\u51e4\u8f6c\u8eab\u4e24\u4e2a\u98de\u8dc3\uff0c\u4eba\u8e2a\u5df2\u5931\u3002 \u3000\u3000\u201c\u96ea\u541b\uff0c\u6211\u4eec\u4eba\u624b\u5c11\uff0c\u4f60\u7684\u5341\u51e4\u9f50\u98de\u662f\u6b66\u6797\u4e00\u7edd\uff0c\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u5e2e\u5e2e\u6211\u4eec\u5982\u4f55\uff1f\u201d \u3000\u3000\u201c\u6211\u548c\u56db\u5a62\uff0c\u606d\u5019\u9063\u5dee\u3002\u201d \u3000\u3000\u201c\u529e\u5b8c\u4e86\u8fd9\u4ef6\u4e8b\uff0c\u6211\u4eec\u4e00\u8d77\u53bb\u5c0a\u62dc\u91d1\u4e39\u4e66\u751f\u7684\u7075\u51a2\u3002\u201d \u3000\u3000\u201c\u8c22\u8c22\u4f60\uff0c\u56db\u59d1\uff0c\u6211\u5bf9\u4ed6\u7684\u6127\u548e\u592a\u6df1\u4e86\uff01\u201d\u7533\u96ea\u541b\u53c8\u6d41\u4e0b\u6cea\u6765\u3002 \u3000\u3000\u7f57\u5170\u60f3\u529d\uff0c\u5374\u4e0d\u77e5\u5982\u4f55\u529d\u8d77\uff0c\u5e72\u8106\u56b7\u9053\uff1a\u201c\u5582\uff01\u56db\u59d1\uff0c\u6211\u8981\u5e72\u4ec0\u4e48\u5462\uff1f\u201d \u3000\u3000\u201c\u6211\u4eec\u966a\u7740\u96ea\u541b\u59d0\uff0c\u5927\u519b\u540e\u884c\uff0c\u63a5\u5e94\u56db\u65b9\u3002\u201d\u5f20\u56db\u59d1\u58f0\u97f3\u8f6c\u4f4e\u9053\uff1a\u201c\u6885\u82b1\uff0c\u53bb\u5e26\u8fce\u6625\u6765\u5427\uff01\u8ddf\u6211\u4eec\u4e00\u8d77\u8d70\u3002\u201d \u3000\u3000\u201c\u6211\u60f3\u95ee\u4ef6\u4e8b\uff0c\u4e0d\u77e5\u9053\u53ef\u4e0d\u53ef\u4ee5\uff1f\u201d\u7f57\u5170\u9053\uff1a\u201c\u8fd1\u534a\u5e74\u6765\uff0c\u6211\u662f\u7cca\u7cca\u6d82\u6d82\u7684\u6253\uff0c\u6bcf\u6b21\u90fd\u51c6\u5907\u62fc\uff0c\u4f46\u9664\u4e86\u7ec8\u5357\u5c71\u4e00\u6218\u4e4b\u5916\uff0c\u6211\u4eec\u5b8c\u5168\u6ca1\u6709\u51fa\u624b\u7684\u673a\u4f1a\uff0c\u8fd9\u4e00\u6b21\uff0c\u53c8\u8981\u6211\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4e00\u6b21\uff0c\u4f60\u60f3\u4e0d\u6253\u90fd\u4e0d\u884c\u4e86\u3002\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u7f57\u5170\uff0c\u6211\u539f\u60f3\u501f\u5c11\u6797\u50e7\u4fa3\u4e4b\u529b\uff0c\u5728\u8fd9\u91cc\u548c\u4ed6\u4eec\u4f5c\u4e00\u6b21\u4e2a\u6b7b\u5bf9\u51b3\uff0c\u4e8b\u5148\u7684\u4f30\u8ba1\uff0c\u8fd9\u4e00\u6218\u5fc5\u5c06\u662f\u51f6\u9669\u7edd\u4f26\uff0c\u6253\u4e0b\u6765\u662f\u4e2a\u4ec0\u4e48\u5c40\u9762\uff1f \u3000\u3000\u6211\u6839\u672c\u4e0d\u6562\u53bb\u60f3\uff0c\u610f\u5916\u7684\u662f\u4f60\u4e00\u5bf9\u5b9d\u8d1d\u5973\u513f\u548c\u5973\u5a7f\uff0c\u6210\u5c31\u9ad8\u7684\u51fa\u5947\uff0c\u800c\u4e14\uff0c\u90a3\u4f4d\u795e\u79d8\u6559\u4e3b\u4e5f\u672a\u51fa\u73b0\u3002\u2026\u2026\u201d \u3000\u3000\u201c\u5927\u59d0\u2026\u2026\u201d\u6c5f\u67ab\u63d2\u5634\uff0c\u9053\uff1a\u201c\u90a3\u4f4d\u6559\u4e3b\u7a76\u7adf\u662f\u6765\u4e86\u6ca1\u6709\uff1f\u201d \u3000 \u7b2c\u4e8c\u5341\u516d\u56de\u3000\u6885\u82b1\u5973\u6b7c\u654c\u65ad\u9999\u9b42 \u3000 \u3000\u3000\u201c\u5e94\u8be5\u662f\u6765\u4e86\uff0c\u4f46\u6211\u6ca1\u6709\u53d1\u73b0\u4ed6\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u8fd9\u4e00\u6b21\uff0c\u4ed6\u4eec\u4e09\u4e2a\u7ec4\u5408\u8054\u624b\u51fa\u52a8\uff0c\u4f46\u7528\u5fc3\u5374\u662f\u8981\u5929\u9a6c\u5802\u4e2d\u5168\u90e8\u7cbe\u9510\uff0c\u548c\u6211\u4eec\u6253\u5230\u4e24\u8d25\u4ff1\u4f24\u3002 \u3000\u3000\u5929\u540e\u5bab\u3001\u5929\u738b\u95e8\u7684\u4eba\u624b\uff0c\u4e0d\u662f\u53c2\u6218\u6765\u7684\uff0c\u800c\u662f\u51c6\u5907\u6536\u62fe\u6b8b\u5c40\uff0c\u4e00\u4e3e\u5c3d\u6b7c\u4f59\u654c\uff0c\u5728\u90a3\u4f4d\u6559\u4e3b\u7684\u4f30\u7b97\u4e2d\uff0c\u5929\u9a6c\u5802\u548c\u6211\u4eec\u8fd9\u6b21\u5927\u5bf9\u51b3\u4e2d\uff0c\u662f\u4e00\u4e2a\u60e8\u80dc\u548c\u8d25\u4ea1\u7684\u5c40\u9762\uff0c\u5929\u9a6c\u5802\u5c31\u7b97\u80dc\u4e86\uff0c\u4e5f\u5143\u6c14\u5927\u4f24\uff0c\u9003\u4e0d\u8fc7\u5929\u540e\u5bab\u548c\u5929\u738b\u95e8\u7684\u8054\u624b\u5c60\u622e\u3002 \u3000\u3000\u4f55\u51b5\uff0c\u8fd8\u6709\u4e00\u80a1\u9075\u4f0f\u5728\u4fa7\u7684\u751f\u529b\u519b\uff0c\u9ed1\u8863\u5200\u624b\u65e9\u5df2\u5949\u547d\uff0c\u622a\u6740\u5929\u9a6c\u91cc\u4e34\u654c\u9003\u8d70\u7684\u4eba\u3002 \u3000\u3000\u6709\u5f88\u591a\u83ab\u53ef\u9884\u6d4b\u7684\u56e0\u7d20\uff0c\u4f7f\u6218\u9635\u53d1\u751f\u53d8\u6570\uff0c\u4e0d\u4f46\u51fa\u4e86\u6211\u7684\u610f\u5916\uff0c\u4e5f\u51fa\u4e86\u90a3\u4f4d\u6559\u4e3b\u548c\u5929\u9a6c\u5802\u7684\u610f\u5916\u3002\u201d \u3000\u3000\u201c\u8bf4\u7684\u662f\u554a\uff01\u2026\u2026\u201d\u7f57\u5170\u9053\uff1a\u201c\u6628\u5929\u4f60\u8bf4\u7684\u90a3\u4e48\u4e25\u91cd\uff0c\u6211\u8fde\u8bc0\u522b\u7684\u8bdd\u90fd\u548c\u8001\u516c\u8bf4\u4e86\uff0c\u8c01\u77e5\u9053\u96f7\u58f0\u5927\uff0c\u96e8\u70b9\u5c0f\u2026\u2026\u201d \u3000\u3000\u201c\u662f\u4e00\u9635\u4e4c\u4e91\u8fc7\u53bb\uff0c\u4e5f\u672a\u4e0b\u4e00\u6ef4\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u541f\u96ea\u51fa\u5251\u4e00\u51fb\uff0c\u9707\u9a87\u5168\u573a\uff0c\u4ed6\u4eec\u8ba1\u7b97\u7684\u5bf9\u624b\u662f\u6c5f\u67ab\uff0c\u4f46\u541f\u96ea\u5374\u8868\u73b0\u51fa\u4e86\u6bd4\u6c5f\u67ab\u66f4\u53ef\u6015\u7684\u6740\u4eba\u624b\u6cd5\uff0c\u6c5f\u67ab\uff0c\u544a\u8bc9\u7f57\u5170\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d \u3000\u3000\u201c\u541f\u96ea\u90a3\u4e00\u5251\u8131\u56f0\u6740\u654c\uff0c\u975e\u6211\u6240\u80fd\u2026\u2026\u201d \u3000\u3000\u201c\u4ec0\u4e48\uff1f\u2026\u2026\u201d\u7f57\u5170\u63a5\u9053\uff1a\u201c\u6c5f\u67ab\uff0c\u4f60\u662f\u8bf4\u771f\u7684\uff0c\u8fd8\u662f\u8c26\u865a\uff1f\u201d \u3000\u3000\u201c\u662f\u771f\u7684\uff01\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u6211\u4eec\u5404\u9009\u9002\u5408\u672c\u8eab\u6280\u827a\u7cfb\u7edf\u7684\u6b66\u529f\u53bb\u7ec3\uff0c\u8c01\u4e5f\u4e0d\u5e72\u9884\u8c01\uff0c\u541f\u96ea\u624d\u6167\u5353\u7edd\uff0c\u624d\u6709\u7740\u90a3\u79cd\u60ca\u4eba\u7684\u6210\u5c31\uff0c\u541f\u971c\u53ea\u6015\u4e5f\u6210\u5c31\u4e0d\u51e1\u3002\u201d \u3000\u3000\u201c\u975e\u5e38\u7684\u4e0d\u51e1\uff01\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u5168\u662f\u6211\u6ca1\u6709\u89c1\u8fc7\u7684\u6740\u4eba\u624b\u6cd5\uff0c\u4e24\u4e2a\u4e2b\u5934\u4f3c\u662f\u5df2\u7ecf\u7a81\u7834\u4e86\u6b66\u5b66\u5e38\u89c4\uff0c\u6740\u4eba\u7684\u6280\u672f\uff0c\u5df2\u5230\u4e86\u968f\u5fc3\u6240\u6b32\u4e4b\u5883\uff01\u201d \u3000\u3000\u6c5f\u67ab\u5fc3\u5934\u9707\u52a8\uff0c\u5fd6\u9053\uff1a\u201c\u597d\u53ef\u6015\uff01\u4e24\u59d0\u59b9\u4f3c\u4e4e\u90fd\u5f03\u96be\u5c31\u6613\uff0c\u6b65\u5165\u9b54\u9053\uff0c\u771f\u5230\u5927\u6210\u5883\u754c\uff0c\u4eba\u6027\u4f1a\u9010\u6e10\u6d88\u5931\uff0c\u4e0d\u884c\uff01\u6211\u8981\u5168\u529b\u963b\u6b62\u5979\u4eec\uff0c\u53ea\u6015\u8fd8\u5f97\u501f\u91cd\u5927\u548c\u5c1a\u7684\u4f5b\u6cd5\u5e2e\u4e00\u628a\u4e86\u3002\u201d \u3000\u3000\u4ed6\u8fd9\u91cc\u6697\u6697\u62c5\u5fc3\uff0c\u5f20\u56db\u59d1\u4ea6\u9887\u6709\u6240\u611f\uff0c\u5fae\u5fae\u7684\u98a6\u8d77\u4e86\u7709\u5934\u3002 \u3000\u3000\u8001\u53eb\u5316\u3001\u5201\u9e4f\u3001\u9093\u98de\uff0c\u6885\u82b1\u3001\u6b27\u9633\u662d\u3001\u7533\u96ea\u541b\u7b49\uff0c\u5374\u90fd\u542c\u5f97\u5168\u795e\u8d2f\u6ce8\uff0c\u751f\u6050\u6f0f\u6389\u4e86\u53ea\u5b57\u7247\u8bed\u3002 \u3000\u3000\u7f57\u5170\u5374\u662f\u5fc3\u82b1\u6012\u653e\uff0c\u5973\u513f\u80dc\u5a18\u5341\u500d\uff0c\u603b\u8fd8\u662f\u81ea\u5df1\u7684\u5973\u513f\u554a\uff01 \u3000\u3000\u5979\u662f\u6700\u4e0d\u4f1a\u63a9\u9970\u81ea\u5df1\u7684\u4eba\uff0c\u7acb\u523b\u559c\u4e0a\u7709\u68a2\uff0c\u6d0b\u6ea2\u51fa\u4e00\u7751\u6b22\u6109\uff0c\u9053\uff1a\u201c\u6c5f\u67ab\uff0c\u4f60\u72ec\u6597\u4e09\u5927\u9ad8\u624b\uff0c\u6545\u610f\u9690\u85cf\u5b9e\u529b\uff0c\u6211\u662f\u65e9\u77e5\u9053\u4e86\uff0c\u73b0\u5728\u8001\u5b9e\u8bf4\uff0c\u4f60\u771f\u8981\u6740\u4ed6\u4eec\uff0c\u9700\u8981\u51e0\u62db\u514b\u654c\uff1f\u201d \u3000\u3000\u201c\u8bf4\u5b9e\u8bdd\uff0c\u52a8\u4e0a\u624b\uff0c\u6211\u53ef\u4ee5\u5728\u5341\u62db\u5185\u6740\u4e86\u4ed6\u4eec\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u4f46\u90a3\u662f\u4ed6\u4eec\u7684\u7b56\u7565\u9519\u8bef\uff0c\u5982\u679c\uff0c\u4ed6\u4eec\u5148\u8981\u5c5e\u4ece\u51fa\u624b\uff0c\u7b49\u6211\u8fde\u65a9\u5f3a\u654c\u4e4b\u540e\uff0c\u4ed6\u4eec\u518d\u51fa\u624b\uff0c\u90a3\u65f6\uff0c\u6211\u9510\u6c14\u5df2\u632b\uff0c\u9ebb\u70e6\u5c31\u5927\u4e86\u3002 \u3000\u3000\u4ed6\u4eec\u4efb\u4f55\u4e00\u4e2a\u4eba\uff0c\u5c31\u80fd\u548c\u6211\u62fc\u4e2a\u6570\u5341\u62db\uff0c\u8f66\u8f6e\u5927\u6218\uff0c\u56fa\u53ef\u4ee5\u4f7f\u6211\u632b\u8d25\uff0c\u4e09\u4eba\u5408\u624b\uff0c\u66f4\u53ef\u5728\u767e\u62db\u5185\u53d6\u6211\u6027\u547d\uff0c\u6211\u4e0d\u662f\u91d1\u521a\u4e0d\u574f\u4e4b\u8eab\uff0c\u4e0d\u7ed9\u6211\u8c03\u606f\u673a\u4f1a\uff0c\u6211\u4e5f\u4f1a\u6c14\u529b\u4e0d\u7ee7\uff0c\u6709\u5f88\u591a\u6b66\u529f\uff0c\u4e5f\u65e0\u6cd5\u65bd\u5c55\u4e86\u2026\u2026\u201d \u3000\u3000\u6b27\u9633\u662d\u63a5\u9053\uff1a\u201c\u4ee5\u5b88\u4ee3\u653b\uff0c\u80fd\u4e0d\u80fd\u501f\u673a\u51cb\u606f\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u53ef\u80fd\uff0c\u4eca\u65e5\u4e4b\u6218\uff0c\u6211\u5c31\u91c7\u53d6\u6b64\u7b56\uff0c\u4ee5\u5b88\u4ee3\u653b\uff0c\u6240\u4ee5\uff0c\u4e00\u76f4\u4fdd\u6301\u5168\u529b\u51fb\u6740\u654c\u4eba\u7684\u4f53\u80fd\uff0c\u51e1\u662f\u4e00\u51fb\u654c\u547d\u7684\u6740\u4eba\u6b66\u529f\uff0c\u90fd\u5fc5\u9700\u5168\u529b\u65bd\u5c55\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u51fb\u53d6\u6570\u5341\u654c\uff0c\u5f53\u4f1a\u6709\u529b\u5c3d\u4e4b\u611f\uff0c\u5927\u548c\u5c1a\u7684\u5927\u60b2\u68b5\u5531\uff0c\u4f7f\u5929\u9a6c\u5802\u4e71\u4e86\u7ae0\u6cd5\uff0c\u5b9e\u4e3a\u4eca\u65e5\u81f4\u80dc\u9996\u529f\u3002\u201d \u3000\u3000\u6b27\u9633\u662d\u9053\uff1a\u201c\u4f60\u9ad8\u554a\uff01\u6211\u9047\u4e0a\u76f8\u5f53\u5bf9\u624b\uff0c\u8981\u5168\u529b\u4e00\u51fb\u53d6\u654c\uff0c\u5927\u6982\uff0c\u4e00\u51fb\u4e4b\u540e\uff0c\u5c31\u65e0\u80fd\u518d\u6218\u4e86\uff0c\u4f60\u80fd\u8fde\u51fb\u6570\u5341\u654c\uff0c\u5df2\u975e\u51e1\u4eba\u80fd\u4e3a\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u597d\u554a\uff01\u4f60\u4eec\u628a\u5f31\u70b9\u5168\u8bf4\u51fa\u6765\u4e86\u2026\u2026\u201d\u7f57\u5170\u63a5\u9053\uff1a\u201c\u5982\u662f\u88ab\u654c\u4eba\u542c\u53bb\u90a3\u8fd8\u5f97\u4e86\uff1f\u201d \u3000\u3000\u201c\u7f57\u5170\uff01\u201d\u6b27\u9633\u662d\u8bf4\uff1a\u201c\u4eba\u90fd\u4e00\u6837\uff0c\u8fd9\u4e0d\u662f\u79d8\u5bc6\uff0c\u8d35\u5728\u4e34\u654c\u5e94\u53d8\uff0c\u5176\u5999\u5904\uff0c\u5b58\u4e4e\u4e00\u5fc3\u4e86\u3002\u201d \u3000\u3000\u201c\u5bf9\uff01\u8fd9\u4e00\u6b21\u5927\u8ffd\u6740\uff0c\u6211\u4eec\u5c31\u7528\u6211\u4eec\u4e4b\u957f\uff0c\u91c7\u53d6\u4e2a\u4e2a\u51fb\u7834\u7684\u65b9\u6cd5\uff0c\u4ee5\u6c5f\u67ab\u548c\u541f\u96ea\u3001\u541f\u971c\u4e3a\u6740\u654c\u4e3b\u529b\uff01\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u5176\u4f59\u7684\u4eba\uff0c\u5168\u529b\u63a5\u5e94\uff0c\u4ee5\u4fdd\u8bc1\u4ed6\u4eec\u4e09\u4e2a\u4eba\u6709\u8c03\u606f\u4f53\u80fd\u7684\u673a\u4f1a\u3002\u201d \u3000\u3000\u8fd9\u662f\u4e2a\u5c0f\u5c0f\u7684\u519c\u5e84\uff0c\u53ea\u6709\u4e09\u6237\u4eba\u5bb6\uff0c\u5f20\u56db\u59d1\u88ab\u6697\u8bb0\u6307\u5f15\u800c\u6765\uff0c\u5927\u548c\u5c1a\u65e9\u5df2\u5019\u5728\u8def\u4fa7\uff0c\u5e26\u51e0\u4eba\u8fdb\u5165\u4e86\u4e00\u5ea7\u519c\u820d\u3002 \u3000\u3000\u201c\u6709\u4e86\u4ec0\u4e48\u53d8\u5316\uff1f\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u96ea\u3001\u971c\u4e8c\u5973\u5462\uff1f\u201d \u3000\u3000\u201c\u5979\u4eec\u592a\u9ad8\u660e\u4e86\uff0c\u80fd\u8ffd\u8e2a\u654c\u4eba\u5341\u4e08\u5185\u4e0d\u88ab\u53d1\u89c9\uff0c\u59ff\u5bb9\u767e\u53d8\uff0c\u5ffd\u7537\u5ffd\u5973\uff0c\u8fde\u8eab\u4e0a\u7684\u8863\u670d\uff0c\u4e5f\u5ffd\u7136\u4f1a\u53d8\u989c\u8272\uff0c\u6709\u65f6\u95f4\uff0c\u5979\u4eec\u5230\u4e86\u6211\u7684\u8eab\u4fa7\uff0c\u6211\u8fd8\u6ca1\u6cd5\u5b50\u8ba4\u51fa\u5979\u4eec\u201d \u3000\u3000\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u5c0f\u59b9\u8fd9\u70b9\u5fae\u672a\u4e4b\u6280\uff0c\u8fd8\u5165\u4f60\u5927\u548c\u5c1a\u7684\u6cd5\u773c\u4e86\uff01\u201d\u5f20\u56db\u59d1\u7b11\u9053\uff1a\u201c\u73b0\u5728\uff0c\u8a00\u5f52\u6b63\u4f20\u5427\uff01\u201d \u3000\u3000\u201c\u5929\u9a6c\u5802\u8fd9\u4e00\u6218\u5b8c\u5168\u6e83\u6563\uff0c\u4f46\u767d\u5929\u5316\u548c\u4e09\u5927\u697c\u4e3b\uff0c\u5e76\u672a\u5f03\u522b\u590f\u5929\u540c\uff0c\u4e00\u884c\u4e3b\u4ece\uff0c\u8fd8\u6709\u5341\u516d\u4e2a\u4eba\u3002 \u3000\u3000\u4ed6\u4eec\u884c\u52a8\u7684\u5f88\u8c28\u614e\uff0c\u4f46\u548c\u5c1a\u53d1\u89c9\u4e86\uff0c\u9664\u4e86\u6211\u4eec\u4e4b\u5916\uff0c\u8fd8\u6709\u8ffd\u8e2a\u4ed6\u4eec\u7684\u4eba\u3002 \u3000\u3000\u96ea\u3001\u971c\u4e8c\u5973\u8981\u548c\u5c1a\u4f5c\u4e3b\uff0c\u4e0b\u4ee4\u88ad\u6740\u4ed6\u4eec\uff0c\u4f46\u548c\u5c1a\u60f3\u4e00\u60f3\uff0c\u8fd9\u53ef\u80fd\u5173\u7cfb\u5f88\u5927\uff0c\u4e0d\u6562\u81ea\u4f5c\u4e3b\u610f\uff0c\u53ea\u597d\u8bf7\u6559\u5f20\u59d1\u5a18\u4e86\uff01\u201d \u3000\u3000\u5f20\u56db\u59d1\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u770b\u6765\uff0c\u7528\u4e0d\u7740\u6211\u4eec\u4e0b\u624b\uff0c\u90a3\u4f4d\u6559\u4e3b\u4e5f\u4e0d\u4f1a\u653e\u8fc7\u4ed6\u4eec\u4e86\uff0c\u548c\u5c1a\uff0c\u53d1\u89c9\u4e86\u8ffd\u8e2a\u7684\u98de\u9e70\u7075\u72ac\u6ca1\u6709\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\uff0c\u597d\u50cf\u662f\u4ed6\u4eec\u6709\u907f\u5fcc\u4e86\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u96ea\u541b\uff0c\u8981\u4f60\u51fa\u52a8\u4e86\uff0c\u5e26\u7740\u56db\u5a62\uff0c\u53bb\u89c1\u590f\u5929\u540c\uff0c\u6709\u4e9b\u8bdd\u4f60\u4e0d\u4fbf\u8bf4\uff0c\u6211\u8bf7\u5201\u9e4f\u8ddf\u4f60\u540c\u884c\u3002 \u3000\u3000\u6885\u82b1\uff0c\u4f60\u66ff\u4ee3\u4e00\u4f4d\u4ece\u5a62\uff0c\u7d27\u968f\u7533\u59e8\u8eab\u4fa7\u4fdd\u62a4\uff0c\u89c1\u5230\u96ea\u3001\u971c\u65f6\uff0c\u8981\u5979\u4eec\u6697\u4e2d\u63a5\u5e94\uff0c\u4e0d\u8bb8\u5979\u4eec\u8f7b\u6613\u51fa\u624b\uff0c\u53ea\u6709\u4f60\u4eec\u9677\u5165\u751f\u6b7b\u5173\u5934\u65f6\uff0c\u624d\u80fd\u51fa\u624b\u76f8\u6551\u3002\u201d \u3000\u3000\u201c\u662f\uff01\u201d\u6885\u82b1\u5e26\u4e00\u5a62\u5165\u5ba4\u66f4\u8863\u3002 \u3000\u3000\u5f20\u56db\u59d1\u53c8\u4ea4\u5f85\u4e2d\u96ea\u541b\u3001\u5201\u9e4f\u4e00\u4e9b\u8bdd\u3002 \u3000\u3000\u201c\u590f\u5929\u540c\u975e\u5e38\u9634\u9669\uff0c\u8fd9\u4e2a\u4eba\u4e0d\u80fd\u7559\u3002\u201d\u7533\u96ea\u541b\u9053\u3002 \u3000\u3000\u201c\u4e0d\u4f1a\u653e\u8fc7\u4ed6\u7684\uff0c\u4f46\u5982\u8ba9\u4ed6\u6b7b\u5728\u90a3\u4f4d\u6559\u4e3b\u7684\u624b\u4e2d\uff0c\u662f\u542b\u6068\u800c\u7ec8\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u4f55\u51b5\uff0c\u90a3\u4f4d\u6559\u4e3b\u8981\u6740\u4e86\u8fd9\u4e00\u884c\u4eba\uff0c\u4e5f\u8981\u4ed8\u51fa\u76f8\u5f53\u7684\u4ee3\u4ef7\u2026\u2026\u201d \u3000\u3000\u201c\u597d\u6781\u4e86\uff01\u56db\u59d1\uff0c\u6b66\u6797\u4e09\u602a\u6258\u4ed8\u4f60\u4e3b\u6301\u5927\u5c40\u2026\u2026\u201d\u7f57\u5170\u9053\uff1a\u201c\u679c\u7136\u65e9\u6709\u8bc6\u4eba\u4e4b\u660e\uff0c\u4f60\u8fd0\u7b79\u5e37\u5e44\uff0c\u51b3\u80dc\u5343\u91cc\u2026\u2026\u201d \u3000\u3000\u201c\u7edf\u5e05\u662f\u6c5f\u67ab\uff0c\u6211\u53ea\u662f\u51fa\u70b9\u5b50\u7684\u4eba\u2026\u2026\u201d\u5f20\u56db\u59d1\u671b\u7740\u6c5f\u67ab\u5fae\u7b11\uff0c\u7b11\u4e2d\u6709\u6b49\u610f\uff0c\u4e5f\u6709\u6e29\u67d4\u3002 \u3000\u3000\u201c\u4e0d\u7528\u5ba2\u6c14\uff0c\u5927\u59d0\uff0c\u4f60\u662f\u5927\u5c06\u548c\u7528\u5175\u4e4b\u624d\uff0c\u5c0f\u5f1f\u4e0d\u6562\u81ea\u8c26\uff0c\u662f\u4e00\u5458\u6b66\u52c7\u4e4b\u5c06\u3002\u201d \u3000\u3000\u6885\u82b1\u5df2\u6613\u5bb9\u6539\u88c5\u800c\u51fa\uff0c\u773c\u524d\u51fa\u73b0\u4e24\u4e2a\u9762\u8c8c\u5b8c\u5168\u76f8\u540c\u7684\u5c11\u5973\uff0c\u8fde\u7533\u96ea\u541b\u4e5f\u6709\u7740\u65e0\u6cd5\u5206\u8fa8\u4e4b\u611f\u3002 \u3000\u3000\u4e00\u884c\u516d\u4e2a\u4eba\u79bb\u53bb\u4e4b\u540e\uff0c\u5927\u548c\u5c1a\u624d\u7b11\u9053\uff1a\u201c\u5f20\u59d1\u5a18\uff0c\u4f60\u770b\u80fd\u4e0d\u80fd\u903c\u90a3\u4f4d\u6559\u4e3b\u73b0\u8eab\uff1f\u201d \u3000\u3000\u201c\u53ef\u80fd\u6709\u673a\u4f1a\uff0c\u4f46\u4ed6\u5fc5\u7136\u4f1a\u5e03\u4e0b\u9677\u9631\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u6211\u4eec\u8fd8\u8981\u6d89\u9669\uff0c\u5982\u4f55\u628a\u63e1\u4f4f\u4e0d\u518d\u8ba9\u4ed6\u9041\u8d70\uff0c\u8fd8\u8981\u5927\u8d39\u4e00\u756a\u601d\u91cf\uff0c\u4e5f\u6ca1\u6cd5\u5148\u8c0b\u826f\u7b56\uff0c\u53ea\u80fd\u968f\u673a\u5e94\u53d8\u3002\u201d \u3000\u3000\u201c\u4f60\u770b\uff0c\u4e24\u4e2a\u6708\u7684\u65f6\u95f4\u591f\u4e0d\u591f\uff1f\u201d \u3000\u3000\u201c\u5927\u548c\u5c1a\uff0c\u6709\u4ec0\u4e48\u4e8b\uff1f\u4e3a\u4ec0\u4e48\u541e\u541e\u5410\u5410\uff1f\u201d\u5f20\u56db\u59d1\u76ef\u7740\u516d\u5b9d\u548c\u5c1a\u95ee\u3002 \u3000\u3000\u201c\u4e56\u4e56\uff0c\u53ef\u771f\u662f\u7978\u4ece\u53e3\u51fa\u554a\uff01\u548c\u5c1a\u5df2\u534a\u5e74\u591a\uff0c\u6ca1\u6709\u9189\u8fc7\uff0c\u53ef\u53c8\u6015\u9152\u540e\u8bef\u4e8b\uff0c\u6240\u4ee5\uff0c\u4e0d\u6562\u591a\u559d\uff0c\u5e0c\u671b\u80fd\u6709\u9650\u671f\uff0c\u8ba9\u6211\u5f00\u6000\u5927\u996e\u4e00\u756a\uff0c\u9189\u4e2a\u5fc3\u6ee1\u610f\u8db3\u3002\u201d \u3000\u3000\u8fd9\u756a\u9041\u8bcd\uff0c\u5982\u4f55\u7792\u8fc7\u5f20\u56db\u59d1\uff0c\u4f46\u5979\u672a\u518d\u8ffd\u95ee\u3002 \u3000\u3000\u5374\u4e0d\u6599\u7f57\u5170\u7a81\u7136\u7ec6\u5fc3\u806a\u660e\u8d77\u6765\uff0c\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u4e0d\u5bf9\u5427\uff01\u5927\u548c\u5c1a\uff0c\u4e00\u5b9a\u522b\u6709\u539f\u56e0\uff1f\u5feb\u8bf4\u5b9e\u8bdd\u3002\u201d \u3000\u3000\u5927\u548c\u5c1a\u5fc3\u4e2d\u5ff5\u4f5b\uff0c\u5fd6\u9053\uff1a\u201c\u5c31\u662f\u4e0d\u80fd\u8bf4\u5440\uff01\u4f60\u7f57\u5170\u5e73\u5e38\u7c97\u679d\u5927\u53f6\uff0c\u4eca\u65e5\u600e\u4e48\u5982\u6b64\u7cbe\u7ec6\uff0c\u771f\u662f\u8981\u548c\u5c1a\u7684\u8001\u547d\u554a\uff01\u201d \u3000\u3000\u201c\u6709\u4eba\u6765\u4e86\u2026\u2026\u201d\u5927\u548c\u5c1a\u7a81\u7136\u7ad9\u8d77\uff0c\u9053\uff1a\u201c\u6211\u53bb\u770b\u770b\u3002\u201d \u3000\u3000\u201c\u6bb5\u4e5d\u5e08\u5f92\u3001\u80e1\u840d\u7236\u5b50\u5462\uff1f\u201d\u5f20\u56db\u59d1\u95ee\u3002 \u3000\u3000\u201c\u4ed6\u4eec\u57cb\u4f0f\u5728\u519c\u820d\u56db\u5916\uff0c\u521a\u624d\u5c31\u662f\u6bb5\u4e5d\u4f20\u51fa\u8b66\u8baf\u2026\u2026\u201d\u4e00\u8f6c\u8eab\uff0c\u4eba\u5230\u5ba4\u5916\u3002 \u3000\u3000\u201c\u5927\u548c\u5c1a\u4e00\u5b9a\u6709\u5fc3\u75c5\uff0c\u88ab\u6211\u62ff\u8bdd\u4e00\u903c\uff0c\u7adf\u5413\u5f97\u795e\u8272\u4e0d\u5b89\u3002\u201d\u7f57\u5170\u9053\uff1a\u201c\u7b97\u4ed6\u8fd0\u6c14\u597d\uff0c\u8eb2\u8fc7\u8fd9\u4e00\u52ab\u3002\u201d \u3000\u3000\u679c\u7136\u662f\u6709\u4eba\u6765\u4e86\uff0c\u5927\u548c\u5c1a\u8eab\u540e\u8ddf\u9996\u4e24\u4f4d\u5927\u59d1\u5a18\u3002 \u3000\u3000\u662f\u9752\u51e4\u548c\u84dd\u51e4\u3002 \u3000\u3000\u6c5f\u67ab\u633a\u8eab\u800c\u8d77\uff0c\u9053\uff1a\u201c\u84dd\u51e4\uff0c\u4f60\u6765\u4e86\u3002\u201d \u3000\u3000\u58f0\u97f3\u4e2d\u5145\u6ee1\u4e86\u559c\u60a6\u3002 \u3000\u3000\u84dd\u51e4\u6709\u70b9\u611f\u52a8\uff0c\u51dd\u6ce8\u9996\u6c5f\u67ab\u9053\uff1a\u201c\u5929\u98ce\u4e0d\u64bc\u6ee1\u6000\u6089\uff0c\u7adf\u968f\u6e05\u97f3\u5165\u4e66\u697c\u3002\u4f60\u771f\u7684\u6ca1\u6709\u53d7\u4f24\uff1f\u201d \u3000\u3000\u201c\u771f\u7684\u6ca1\u6709\uff01\u201d \u3000\u3000\u6c5f\u67ab\u70b9\u5934\u5fae\u7b11\u3002 \u3000\u3000\u84dd\u98ce\u6ca1\u6709\u6251\u8fc7\u53bb\uff0c\u5979\u662f\u4e2a\u77dc\u6301\u7684\u5973\u4eba\u3002 \u3000\u3000\u201c\u5c0f\u624d\u5973\uff0c\u8fc7\u6765\u5750\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u62cd\u7740\u8eab\u4fa7\u4e00\u7b2c\u7a7a\u6905\u5b50\u3002 \u3000\u3000\u201c\u4f60\u662f\u5f20\u59e8\uff1f\u201d\u84dd\u51e4\u671b\u7740\u5f20\u56db\u59d1\u6df1\u6df1\u4e00\u793c\uff0c\u76ee\u5149\u8f6c\u6ce8\u5230\u6b27\u9633\u662d\u548c\u7f57\u5170\u7684\u8eab\u4e0a\uff0c\u63a5\u9053\uff1a \u3000\u3000\u201c\u4e24\u4f4d\u662f\u6b27\u9633\u4f2f\u7236\u548c\u4f2f\u6bcd\u4e86\u3002\u201d \u3000\u3000\u6b27\u9633\u662d\u70b9\u9996\u5fae\u7b11\uff0c\u7f57\u5170\u5374\u5927\u58f0\u8d5e\u9053\uff1a\u201c\u51fa\u54c1\u6210\u7ae0\uff0c\u5b57\u5b57\u611f\u4eba\uff0c\u597d\uff01 \u3000\u3000\u597d\u6781\u5566\uff01\u84dd\u51e4\uff0c\u4e0d\u8981\u56de\u53bb\u4e86\u3002\u201d \u3000\u3000\u201c\u53ea\u6015\u8fd8\u96be\u4e86\u5374\u2026\u2026\u201d\u84dd\u51e4\u7b11\u9053\uff1a\u201c\u5026\u9e1f\u867d\u77e5\u8fd4\uff0c\u5915\u9633\u672a\u4e0b\u5c71\uff0c\u6211\u662f\u6765\u542c\u5f20\u59e8\u6307\u547d\u7684\u3002\u201d \u3000\u3000\u67f3\u8170\u8f6c\u52a8\uff0c\u5bf9\u8001\u53eb\u5316\u3001\u9093\u98de\u7b49\u4e00\u4e00\u9894\u9996\u4f5c\u793c\u3002 \u3000\u3000\u201c\u9752\u51e4\u8981\u6211\u591a\u70b9\u793c\u6570\uff0c\u5b66\u4e9b\u6e29\u67d4\u2026\u2026\u201d\u84dd\u51e4\u4e3e\u6b65\u884c\u8fd1\u5f20\u56db\u59d1\u4f4e\u58f0\u63a5\u9053\uff1a\u201c\u4f46\u6211\u91ce\u60ef\u4e86\uff0c\u864e\u5e10\u8bba\u5175\uff0c\u6740\u4f10\u8054\u8033\uff0c\u65e9\u5df2\u7ecf\u6ca1\u6709\u5973\u4eba\u5473\u4e86\uff0c\u5f20\u59e8\u4e0d\u8981\u7b11\u6211\u3002\u201d\u76ee\u5149\u4e00\u63a0\u7ad9\u5728\u4e00\u4fa7\uff0c\u8eab\u8eaf\u5fae\u5fae\u6296\u52a8\u7684\u8fce\u6625\uff0c\u7f13\u7f13\u5728\u5f20\u56db\u59d1\u7684\u8eab\u4fa7\u5750\u4e0b\u3002 \u3000\u3000\u4f46\u95fb\u6251\u901a\u4e00\u58f0\uff0c\u8fce\u6625\u5df2\u8dea\u5728\u84dd\u6c11\u8eab\u524d\uff0c\u9053\uff1a\u201c\u4e0d\u662f\u5a62\u5b50\u4e0d\u6562\u6b7b\uff0c\u662f\u4e8c\u5c0f\u59d0\u4e0d\u8981\u6211\u6b7b\uff0c\u6240\u4ee5\uff0c\u5a62\u5b50\u5077\u751f\u6d3b\u4e0b\u6765\u3002\u201d \u3000\u3000\u9752\u51e4\u6ca1\u6709\u52a8\uff0c\u5f20\u56db\u59d1\u4e5f\u672a\u8bb2\u8bdd\uff0c\u6c5f\u67ab\u53e3\u9f7f\u542f\u52a8\uff0c\u5374\u4e5f\u6ca1\u6709\u53d1\u51fa\u58f0\u97f3\u3002 \u3000\u3000\u519c\u820d\u95f4\u7a81\u7136\u9759\u4e0b\u6765\uff0c\u9759\u5f97\u6389\u9488\u53ef\u95fb\u3002 \u3000\u3000\u84dd\u51e4\u4f38\u624b\u6276\u8d77\u8fce\u6625\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e8c\u5c0f\u59d0\u4e0d\u8981\u4f60\u6b7b\uff0c\u4f60\u5c31\u8ddf\u7740\u4e8c\u5c0f\u59d0\u5427\uff01\u201d \u3000\u3000\u8fce\u6625\u4f3c\u662f\u4e0d\u76f8\u4fe1\u84dd\u51e4\u4f1a\u6276\u5979\u8d77\u6765\uff0c\u7740\u7740\u84dd\u51e4\u62d8\u5728\u53f3\u81c2\u7684\u7ec7\u5de7\u7389\u624b\uff0c\u70ed\u6cea\u76c8\u7736\uff0c\u9053\uff1a\u201c\u5927\u5c0f\u59d0\u771f\u7684\u9976\u6055\u4e86\u5c0f\u5a62\u3002\u201d \u3000\u3000\u201c\u4f60\u5df2\u662f\u4e8c\u5c0f\u59d0\u7684\u4ece\u5a62\u4e86\uff0c\u6b64\u540e\uff0c\u6211\u4e0d\u4f1a\u518d\u7ba1\u4f60\u7684\u4e8b\u4e86\uff01\u201d\u84dd\u51e4\u9053\uff1a\u201c\u8fc7\u53bb\u5427\uff01\u201d \u3000\u3000\u201c\u662f\uff01\u201d \u3000\u3000\u8fce\u6625\u5feb\u6b65\u9000\u5230\u9752\u51e4\u8eab\u4fa7\u3002 \u3000\u3000\u5f20\u56db\u59d1\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u84dd\u51e4\uff0c\u6709\u4ec0\u4e48\u544a\u8bc9\u6211\u7684\uff1f\u201d \u3000\u3000\u201c\u6559\u4e3b\u6765\u4e86\uff01\u201d\u84dd\u51e4\u9053\uff1a\u201c\u5982\u975e\u4e8b\u5173\u91cd\u5927\uff0c\u665a\u8f88\u4e5f\u4e0d\u6562\u4eb2\u8eab\u6d89\u9669\u800c\u6765\u2026\u2026\u201d \u3000\u3000\u201c\u90a3\u4e2a\u6559\u4e3b\uff0c\u73b0\u5728\u4f55\u5904\uff1f\u201d\u6c5f\u67ab\u6025\u6025\u8ffd\u95ee\u3002 \u3000\u3000\u201c\u8d31\u59be\u65e0\u80fd\uff0c\u6293\u4e0d\u51c6\u4ed6\u7684\u4f4f\u5904\u2026\u2026\u201d\u84dd\u51e4\u9053\uff1a\u201c\u4f60\u60f3\u6740\u4ed6\uff0c\u4ed6\u4e5f\u60f3\u6740\u4f60\u2026\u2026\u201d \u3000\u3000\u201c\u597d\uff01\u90a3\u4f60\u5c31\u901a\u77e5\u4ed6\uff0c\u7ea6\u4e00\u5904\u51b3\u6218\u6240\u5728\uff0c\u6211\u4eec\u4e00\u5bf9\u4e00\uff0c\u4f5c\u4e00\u573a\u751f\u6b7b\u4e4b\u6218\u3002\u201d\u6c5f\u67ab\u9053\uff1a \u3000\u3000\u201c\u5bf9\u4f60\u800c\u8a00\uff0c\u5e94\u8be5\u4e0d\u662f\u5f88\u96be\u7684\u4e8b\uff0c\u8fd9\u4e2a\u9b54\u5934\u4e0d\u9664\u53bb\uff0c\u6b66\u6797\u4e0d\u4f1a\u5b89\u5b81\u3002\u201d \u3000\u3000\u201c\u6211\u2026\u2026\u201d \u3000\u3000\u201c\u84dd\u51e4\uff0c\u4f60\u662f\u4e0d\u662f\u4e0d\u6562\uff1f\u201d \u3000\u3000\u201c\u4e0d\u662f\uff01\u6211\u4e0d\u6015\u4ed6\u3002\u201d \u3000\u3000\u201c\u90a3\u5c31\u597d\uff01\u4ed6\u662f\u6559\u4e3b\u4e4b\u5c0a\uff0c\u603b\u4e0d\u4f1a\u4e0d\u5e94\u6218\u5427\uff1f\u8fd9\u65b9\u6cd5\u867d\u7136\u7b80\u5355\uff0c\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u4f46\u53ef\u80fd\u6709\u6548\u3002\u201d \u3000\u3000\u201c\u597d\u5427\uff01\u4f60\u771f\u7684\u51b3\u5b9a\u4e86\uff0c\u6211\u5c31\u53bb\u529e\uff01\u201d \u3000\u3000\u84dd\u51e4\u4e00\u8138\u7126\u8651\uff0c\u663e\u7136\u8fd8\u6709\u5f88\u591a\u8bdd\u8bf4\uff0c\u4f46\u53c8\u65e0\u6cd5\u5728\u6c5f\u67ab\u8fd9\u79cd\u6025\u8feb\u7684\u538b\u529b\u4e0b\uff0c\u8868\u8fbe\u51fa\u6765\u3002 \u3000\u3000\u5f20\u56db\u59d1\u5fc3\u4e2d\u5fd6\u9053\uff1a\u201c\u8fd9\u4e2b\u5934\u5b64\u50b2\u4e0d\u7fa4\uff0c\u624d\u827a\u8fc7\u4eba\uff0c\u5177\u6709\u6307\u6325\u4e09\u519b\uff0c\u864e\u5e10\u4e3a\u5e05\u7684\u6c14\u5ea6\uff0c\u4f46\u88ab\u6c5f\u67ab\u4e00\u903c\uff0c\u5374\u4e5f\u4f1a\u624b\u8db3\u65e0\u63aa\uff0c\u770b\u6765\uff0c\u5979\u5df2\u88ab\u5f7b\u5e95\u5f81\u670d\u4e86\uff0c\u8fd9\u60c5\u53ea\u4e00\u5b57\uff0c\u5b9e\u5728\u5bb3\u4eba\u4e0d\u6d45\u3002\u201d \u3000\u3000\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u53e3\u4e2d\u8bf4\u9053\uff1a\u201c\u6c5f\u67ab\uff0c\u4f60\u6025\u4ec0\u4e48\uff1f\u8ba9\u84dd\u51e4\u628a\u8bdd\u8bf4\u5b8c\u597d\u4e0d\u597d\uff1f\u201d \u3000\u3000\u201c\u5f20\u59e8\uff0c\u522b\u602a\u4ed6\u561b\uff01\u4ed6\u5c31\u662f\u8bf4\u9519\u4e86\uff0c\u6211\u4e5f\u8981\u53bb\u529e\u7684\u2026\u2026\u201d \u3000\u3000\u84dd\u51e4\u4e00\u8138\u575a\u51b3\uff0c\u8bf4\u7684\u5341\u5206\u8ba4\u771f\u3002 \u3000\u3000\u9752\u51e4\u5fc3\u4e2d\u6697\u7b11\u9053\uff1a\u201c\u597d\u554a\uff01\u84dd\u51e4\uff0c\u4f60\u5e73\u65e5\u9890\u6307\u6c14\u4f7f\uff0c\u4f55\u7b49\u5a01\u98ce\uff0c\u5bf9\u7537\u4eba\u4e00\u8138\u4e0d\u5c51\uff0c\u600e\u4e48\u4e00\u5165\u60c5\u7f51\uff0c\u548c\u6211\u4eec\u4e00\u4e2a\u6837\u5b50\uff0c\u8fde\u9ed1\u767d\u4e5f\u5206\u4e0d\u6e05\u695a\u4e86\uff1f\u201d \u3000\u3000\u5f20\u56db\u59d1\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u84dd\u51e4\uff0c\u8fd9\u4e0d\u662f\u6c5f\u67ab\u4e00\u4e2a\u4eba\u7684\u4e8b\uff0c\u628a\u4e8b\u60c5\u8bf4\u6e05\u695a\uff0c\u6211\u4eec\u518d\u659f\u914c\u529e\u7406\u3002\u201d \u3000\u3000\u84dd\u51e4\u9053\uff1a\u201c\u53ef\u662f\u6211\u2026\u2026\u6211\u2026\u2026\u201d \u3000\u3000\u5979\u6709\u51fa\u53e3\u6210\u7ae0\u4e4b\u80fd\uff0c\u4f46\u73b0\u5728\u7adf\u7136\u8a00\u4e0d\u53ca\u4e49\uff0c\u6211\u4e86\u534a\u5929\uff0c\u6211\u4e0d\u51fa\u4e2a\u6240\u4ee5\u7136\u6765\u3002 \u3000\u3000\u6c5f\u67ab\u4f4e\u4f30\u4e86\u5bf9\u84dd\u51e4\u7684\u5f71\u54cd\u529b\uff0c\u4e5f\u611f\u89c9\u6709\u70b9\u8a00\u8bcd\u6fc0\u70c8\uff0c\u518d\u52a0\u4e0a\u5927\u548c\u5c1a\u6345\u4e86\u4ed6\u4e00\u6307\uff0c\u624d\u604d\u7136\u5927\u609f\u5230\u84dd\u51e4\u5df2\u88ab\u4ed6\u8bdd\u8d76\u8bdd\uff0c\u903c\u5f97\u5598\u4e0d\u8fc7\u6c14\u4e86\u3002 \u3000\u3000\u201c\u84dd\u51e4\uff0c\u542c\u5927\u59d0\u7684\uff0c\u56db\u59d1\u624d\u662f\u7edf\u5e08\u4e4b\u5e05\uff0c\u2026\u2026\u3002\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u6211\u4eec\u90fd\u542c\u547d\u884c\u4e8b\u3002\u201d \u3000\u3000\u84dd\u51e4\u7a81\u7136\u4e00\u95ed\u53cc\u76ee\uff0c\u957f\u957f\u5401\u4e00\u53e3\u6c14\uff0c\u5fc3\u4e2d\u7684\u7126\u6025\uff0c\u9010\u6e10\u5e73\u590d\u3002 \u3000\u3000\u5979\u6709\u4e00\u767e\u53e5\u3001\u4e00\u5343\u53e5\u8981\u6279\u8bc4\u6c5f\u67ab\u7684\u4e0d\u5bf9\uff0c\u53ef\u60dc\u7684\u662f\u60c5\u6000\u60d8\u60d8\uff0c\u8bf4\u4e0d\u51fa\u53e3\uff0c\u4e3a\u793a\u771f\u7231\u60c5\u7518\u6b7b\uff0c\u51c6\u5907\u6253\u4e00\u573a\u7cca\u6d82\u4ed7\u4e86\uff0c\u4f46\u53c8\u4e0d\u662f\u771f\u613f\u96cc\u4f0f\uff0c\u7406\u667a\u548c\u611f\u60c5\uff0c\u4ea4\u96c6\u4e8e\u5fc3\uff0c\u6025\u5f97\u8fde\u6c57\u90fd\u51fa\u6765\u4e86\u3002 \u3000\u3000\u201c\u84dd\u51e4\uff0c\u8bf4\u51fa\u6765\u4ed6\u4eec\u7684\u8c0b\u7565\u8ba1\u5212\uff0c\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u770b\u770b\u6211\u4eec\u80fd\u4e0d\u80fd\u5e94\u5bf9\u6709\u7b56\uff1f\u201d \u3000\u3000\u201c\u662f\uff01\u201d\u84dd\u98ce\u60c5\u7eea\u5e73\u590d\uff0c\u53e3\u9f7f\u987f\u7136\u4f36\u4fd0\uff0c\u4f83\u4f83\u800c\u8c08\uff0c\u9053\uff1a\u201c\u90a3\u4e00\u591c\u5c0f\u697c\u8bba\u5175\uff0c\u665a\u8f88\u88ab\u8feb\u53d1\u8a00\uff0c\u8c08\u5230\u76ee\u524d\u7684\u6c5f\u6e56\u5f62\u52bf\uff0c\u4e00\u5e2d\u8c2c\u8bba\uff0c\u7adf\u4f7f\u90a3\u4f4d\u521d\u5ea6\u4f1a\u6664\u7684\u6559\u4e3b\u52a8\u5bb9\uff0c\u4ee4\u665a\u8f88\u4e3b\u76df\u5927\u5c40\uff0c\u7edf\u5e05\u5168\u519b\uff0c\u5305\u62ec\u5929\u540e\u5bab\u3001\u5929\u738b\u95e8\u7684\u5168\u6570\u90e8\u4f17\u2026\u2026\u201d \u3000\u3000\u201c\u6162\u6162\u6162\uff01\u201d\u5927\u548c\u5c1a\u9053\uff1a\u201c\u4f60\u8bf4\u5168\u6570\u90e8\u4f17\uff0c\u662f\u5426\u5305\u62ec\u5929\u540e\u5bab\u4e09\u4f4d\u5bab\u4e3b\u5728\u5185\uff1f\u201d \u3000\u3000\u201c\u662f\uff01\u8fd8\u6709\u6211\u5e08\u7236\u5929\u738b\u95e8\u4e3b\u2026\u2026\u201d\u84dd\u51e4\u9053\uff1a\u201c\u518d\u52a0\u4e0a\u6559\u4e3b\u5e26\u6765\u5929\u9b54\u6d88\u9b42\u9635\u7684\u4e5d\u4f4d\u9b54\u5973\uff0c\u56db\u5927\u795e\u9b54\u3001\u4e09\u5341\u516b\u4f4d\u9ed1\u8863\u5200\u624b\u3002\u201d \u3000\u3000\u6de1\u6de1\u51e0\u53e5\u8bdd\uff0c\u4f7f\u5168\u573a\u9707\u52a8\uff0c\u4eba\u4eba\u8272\u53d8\uff0c\u8fde\u5f20\u56db\u59d1\u4e5f\u542c\u5f97\u5446\u4f4f\u4e86\u3002 \u3000\u3000\u201c\u8fd8\u6709\u5982\u6b64\u5f3a\u5927\u7684\u5b9e\u529b\u5440\uff01\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u8fd9\u662f\u65e9\u6709\u51c6\u5907\u4e86\u3002\u201d \u3000\u3000\u201c\u662f\uff01\u516d\u5341\u56db\u4f4d\u5200\u624b\uff0c\u88ab\u541f\u971c\u3001\u9752\u51e4\u3001\u6885\u82b1\uff0c\u6740\u4e86\u4e8c\u5341\u516d\u4f4d\uff0c\u8fd8\u6709\u4e09\u5341\u516b\u4eba\u2026\u2026\u201d\u84dd\u51e4\u9053\uff1a\u201c\u6211\u89c1\u8fc7\u541f\u96ea\u59d1\u5a18\u7684\u6b66\u529f\uff0c\u4e3e\u4e00\u53cd\u4e09\uff0c\u9ed1\u8863\u5200\u624b\u4e0d\u8db3\u754f\uff0c\u7531\u541f\u971c\u4e3b\u9635\uff0c\u4f50\u4ee5\u9752\u51e4\u548c\u6885\u82b1\uff0c\u5c31\u53ef\u4ee5\u5bf9\u4ed8\u4ed6\u4eec\u3002 \u3000\u3000\u4f46\u56db\u5927\u795e\u9b54\uff0c\u5374\u662f\u6765\u81ea\u5c0f\u897f\u5929\u7684\u9b54\u6559\u9ad8\u624b\uff0c\u5929\u9b54\u6d88\u9b42\u9635\uff0c\u66f4\u662f\u4e13\u4e3a\u5bf9\u4ed8\u6c5f\u67ab\u800c\u6765\u2026\u2026\u201d \u3000\u3000\u76ee\u5149\u4e00\u63a0\u60c5\u90ce\uff0c\u987f\u611f\u59d4\u5c48\u6ee1\u8179\uff0c\u5dee\u4e00\u70b9\u88ab\u903c\u6ca6\u4e3a\u4f20\u4e66\u7ea6\u6218\u7684\u4e00\u4e2a\u5c0f\u5352\uff0c\u8fd9\u4e00\u4ee3\u624d\u5973\u7684\u9b54\u5e08\u7edf\u5e05\uff0c\u7acb\u523b\u5316\u4f5c\u70df\u4e91\uff0c\u81ea\u7136\u4e5f\u65e0\u6cd5\u4f20\u51fa\u8baf\u606f\uff0c\u771f\u6b63\u4e3a\u60c5\u90ce\u4e00\u5c3d\u5fc3\u529b\u4e86\u3002 \u3000\u3000\u56e0\u4e3a\uff0c\u4f20\u4e66\u4e4b\u540e\uff0c\u5fc5\u7136\u88ab\u6740\u3002 \u3000\u3000\u654c\u52bf\u4e0d\u660e\uff0c\u5f20\u56db\u59d1\u7b49\u4e5f\u53ef\u80fd\u9677\u5165\u5371\u5883\uff0c\u5168\u519b\u5c3d\u6ca1\uff0c\u96be\u602a\u84dd\u59d1\u5a18\u4f1a\u6025\u51fa\u4e00\u8eab\u6c57\u6c34\u4e86\uff01 \u3000\u3000\u201c\u84dd\u51e4\uff0c\u6211\u4eec\u8be5\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u5f20\u56db\u59d1\u4e00\u7247\u771f\u8bda\u5411\u84dd\u59d1\u5a18\u6c42\u6559\u3002 \u3000\u3000\u201c\u5f20\u59e8\uff0c\u665a\u8f88\u4e0d\u6562\u903e\u8d8a\u2026\u2026\u201d \u3000\u3000\u201c\u84dd\u51e4\uff0c\u6211\u662f\u771f\u5fc3\u8bf7\u6559\u2026\u2026\u201d\u5f20\u56db\u7ad9\u6253\u65ad\u4e86\u84dd\u51e4\u7684\u8bdd\uff0c\u9053\uff1a\u201c\u4f60\u5c31\u4e0d\u8981\u5ba2\u6c14\u4e86\u3002\u201d \u3000\u3000\u201c\u84dd\u51e4\uff0c\u8bf4\u51fa\u4f60\u7684\u9ad8\u89c1\u3002\u201d\u9752\u51e4\u9053\uff1a\u201c\u5f20\u59e8\u5927\u5ea6\u96cd\u5bb9\uff0c\u91cf\u5927\u624d\u6167\uff0c\u4f60\u5c31\u662f\u8bf4\u9519\u4e86\uff0c\u4e5f\u4e0d\u8981\u7d27\u3002\u201d \u3000\u3000\u201c\u662f\uff0c\u591a\u8c22\u4e8c\u59b9\u6307\u70b9\u3002\u201d\u84dd\u51e4\u9053\uff1a\u201c\u4f1a\u76df\u5728\u4e09\u65e5\u4e4b\u540e\uff0c\u5929\u540e\u5bab\u3001\u5929\u738b\u4eec\uff0c\u8981\u5728\u8fd9\u4e09\u5929\u4e4b\u5185\uff0c\u56f4\u6740\u5929\u9a6c\u5802\u4e3b\u8981\u7684\u6f0f\u7f51\u4e4b\u9c7c\uff0c\u590f\u5929\u540c\u3001\u767d\u5929\u5316\uff0c\u548c\u4e09\u4f4d\u6402\u4e3b\uff0c\u665a\u8f88\u89c9\u7740\u90a3\u6279\u4eba\u8fd8\u582a\u5927\u7528\uff0c\u5f20\u59e8\u5982\u80fd\u6536\u670d\u4ed6\u4eec\uff0c\u4e5f\u53ef\u6d4e\u4e00\u65f6\u4eba\u624b\u4e4b\u6025\u3002\u201d \u3000\u3000\u771f\u662f\u82f1\u96c4\u6240\u89c1\u7565\u540c\uff0c\u5f20\u56db\u59d1\u5df2\u6d3e\u7533\u96ea\u541b\u7b49\u53bb\u4e86\uff0c\u53ea\u662f\u4e0d\u5982\u84dd\u51e4\u60f3\u5f97\u900f\u5f7b\uff0c\u8981\u6536\u4e3a\u5df1\u7528\u4e86\u3002 \u3000\u3000\u84dd\u51e4\u7ad9\u8d77\u8eab\u5b50\uff0c\u9053\uff1a\u201c\u56db\u5927\u795e\u9b54\u6b66\u529f\u5982\u4f55\uff1f\u5929\u9b54\u6d88\u9b42\u9635\u6709\u4f55\u5389\u5bb3\u4e4b\u5904\uff0c\u665a\u8f88\u8fd8\u4e0d\u77e5\u6653\uff0c\u8981\u9752\u51e4\u548c\u6211\u4fdd\u6301\u8fde\u7edc\uff0c\u4e00\u6709\u53d8\u5316\uff0c\u5c31\u901a\u77e5\u5f20\u59e8\uff0c\u665a\u8f88\u4e0d\u5b9c\u591a\u7559\uff0c\u6211\u8d70\u4e86\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u6c5f\u67ab\uff0c\u53bb\u9001\u84dd\u51e4\u4e00\u7a0b\u3002\u201d \u3000\u3000\u84dd\u51e4\u4e5f\u4e0d\u63a8\u62d2\uff0c\u6c5f\u67ab\u5c31\u8ddf\u7740\u8d70\u51fa\u5ba4\u5916\uff0c\u84dd\u98ce\u8f6c\u5165\u4e00\u5904\u5c4b\u89d2\uff0c\u9053\uff1a\u201c\u9001\u541b\u5343\u91cc\uff0c\u7ec8\u987b\u4e00\u522b\uff0c\u4e0d\u9001\u4e5f\u7f62\uff01\u201d \u3000\u3000\u201c\u4f60\u6562\u5e26\u6211\u56de\u53bb\uff0c\u6211\u5c31\u8ddf\u7740\u4f60\u8d70\uff01\u201d \u3000\u3000\u201c\u518d\u6709\u4e00\u5bb5\u7f20\u7ef5\uff0c\u6b7b\u800c\u65e0\u61be\uff0c\u4e0d\u8fc7\uff0c\u8981\u4ee5\u5927\u5c40\u4e3a\u91cd\uff0c\u7b49\u8fc7\u4e86\u8fd9\u573a\u52ab\u96be\uff0c\u2026\u2026\u201d\u84dd\u51e4\u4f4e\u58f0\u9053\uff1a\u201c\u518d\u53d9\u95fa\u623f\u4e4b\u4e50\uff01\u90a3\u65f6\uff0c\u4efb\u90ce\u8f7b\u8584\uff0c\u6211\u4f1a\u4f5c\u4e2a\u767e\u4f9d\u767e\u987a\u7684\u5c0f\u5987\u4eba\u3002\u201d \u3000\u3000\u201c\u5509\uff01\u84dd\u51e4\uff0c\u521a\u624d\u5bf9\u4e0d\u4f4f\u5566\uff01\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u6211\u4e00\u5fc3\u53ea\u60f3\u5230\u6740\u6b7b\u90a3\u4f4d\u6559\u4e3b\uff0c\u53e3\u4e0d\u62e9\u8a00\uff0c\u903c\u5f97\u4f60\u65e0\u6cd5\u89e3\u8bf4\uff0c\u51e0\u4e4e\u9020\u6210\u5927\u9519\u3002\u201d \u3000\u3000\u201c\u77e5\u9519\u5c31\u597d\uff01\u4e0b\u4e00\u6b21\uff0c\u5c31\u522b\u592a\u903c\u6211\uff0c\u8ba9\u6211\u6162\u6162\u7684\u8bf4\u6e05\u695a\uff0c\u4f60\u518d\u4f5c\u51b3\u5b9a\u2026\u2026\u201d\u84dd\u51e4\u9053\uff1a \u3000\u3000\u201c\u6211\u4e0d\u5584\u8868\u8fbe\u60c5\u610f\uff0c\u4f46\u5fc3\u4e2d\u8bb8\u60c5\u81f3\u6df1\uff0c\u4f60\u4e00\u903c\u6211\uff0c\u6211\u5c31\u8bf4\u4e0d\u6e05\u695a\u5566\u3002\u201d \u3000\u3000\u201c\u4e0d\u4f1a\u4e86\uff0c\u4eca\u65e5\u89c1\u8bc6\u4e86\u4f60\u7684\u667a\u7565\uff0c\u4ee5\u540e\uff0c\u6211\u542c\u4f60\u7684\u3002\u201d \u3000\u3000\u84dd\u51e4\u7b11\u4e86\uff0c\u7b11\u7684\u597d\u6ee1\u8db3\uff0c\u9053\uff1a\u201c\u53ef\u662f\uff0c\u8fd9\u6b21\u4e5f\u8be5\u7f5a\u4f60\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u6211\u8ba4\u7f5a\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u8bf4\u5427\uff01\u7f5a\u4ec0\u4e48\u6211\u90fd\u63a5\u53d7\u3002\u201d \u3000\u3000\u201c\u4f60\u903c\u6211\u6025\u7684\u51fa\u4e86\u4e00\u8eab\u6c57\uff0c\u7f5a\u4f60\u5e2e\u6211\u6d17\u6fa1\u3002\u201d\u84dd\u51e4\u7709\u68a2\u773c\u89d2\u8361\u8d77\u6625\u60c5\uff0c\u63a5\u9053\uff1a\u201c\u8981\u6d17\u5f97\u5e72\u5e72\u51c0\u51c0\uff0c\u4e00\u5c18\u4e0d\u67d3\u3002\u201d \u3000\u3000\u201c\u5bb9\u6613\u5440\uff01\u73b0\u5728\u5c31\u6d17\u2026\u2026\u201d\u6c5f\u67ab\u4f38\u624b\u6293\u4f4f\u4e86\u84dd\u51e4\u4e00\u53ea\u624b\u3002 \u3000\u3000\u201c\u73b0\u5728\u4e0d\u884c\uff0c\u4ee5\u540e\u8ba9\u4f60\u6d17\u4e2a\u591f\u2026\u2026\u201d\u84dd\u51e4\u7f29\u56de\u624b\uff0c\u9053\uff1a\u201c\u522b\u8981\u5f20\u59e8\u77a7\u89c1\u4e86\uff0c\u8bf4\u6211\u8f7b\u6d6e\uff0c\u6211\u4e0d\u80fd\u591a\u7559\uff0c\u771f\u8981\u8d70\u4e86\u3002\u201d \u3000\u3000\u8f6c\u8fc7\u8eab\u5b50\uff0c\u98de\u8dc3\u800c\u53bb\u3002 \u3000\u3000\u5979\u4e0d\u5584\u8c08\u60c5\u8bf4\u7231\uff0c\u90a3\u79cd\u6b32\u62d2\u8fd8\u8fce\u7684\u795e\u97f5\uff0c\u5012\u4e5f\u522b\u6709\u60c5\u8da3\u3002 \u3000\u3000\u6c5f\u67ab\u8fd4\u56de\u519c\u820d\uff0c\u89c1\u4eba\u4eba\u4e00\u8138\u51dd\u91cd\u4e4b\u8272\uff0c\u5176\u5b9e\uff0c\u4ed6\u5fc3\u4e2d\u538b\u529b\uff0c\u5c24\u8fc7\u4ed6\u4eba\uff0c\u4f20\u6765\u7684\u8baf\u606f\u5927\u9707\u52a8\uff0c\u6c5f\u67ab\u4e5f\u5b9e\u5728\u6f47\u6d12\u4e0b\u8d77\u6765\u4e86\u3002 \u3000\u3000\u8fd8\u662f\u5f20\u56db\u59d1\u5148\u5f00\u53e3\uff0c\u9053\uff1a\u201c\u6c5f\u67ab\uff0c\u4f60\u4f5c\u4e86\u4e00\u4ef6\u6700\u53ef\u8d35\u7684\u4e8b\uff0c\u662f\u90a3\u5929\u5c11\u6797\u5bfa\u5916\u4e4b\u6218\uff0c\u9690\u85cf\u4e86\u795e\u5251\u5229\u5668\uff0c\u8fd9\u4e2a\u79d8\u5bc6\uff0c\u53ef\u80fd\u4f1a\u51fa\u4e86\u4ed6\u4eec\u7684\u9884\u8ba1\uff0c\u4e34\u654c\u65f6\u96f7\u9706\u4e00\u51fb\uff0c\u6740\u4ed6\u4eec\u4e2a\u63aa\u624b\u4e0d\u53ca\u3002\u201d \u3000\u3000\u51e0\u53e5\u8bdd\u4f7f\u6c5f\u67ab\u8c6a\u6c14\u4e00\u632f\uff0c\u9053\uff1a\u201c\u5c0f\u5f1f\u4e5f\u6709\u8fd9\u6837\u4e00\u4e2a\u60f3\u6cd5\uff0c\u5b9d\u5251\u51fa\u9798\uff0c\u5c31\u7b97\u56db\u5927\u795e\u9b54\u8054\u624b\uff0c\u4e5f\u672a\u5fc5\u6297\u62d2\u5f97\u4f4f\uff0c\u5012\u662f\u90a3\u5ea7\u5929\u9b54\u6d88\u9b42\u9635\uff0c\u4e0d\u77e5\u9053\u662f\u4ec0\u4e48\u73a9\u827a\uff1f\u6709\u4e9b\u4f30\u4e0d\u900f\u5b83\uff01\u201d \u3000\u3000\u201c\u5929\u9b54\u6d88\u9b42\uff0c\u5927\u6982\u662f\u5a9a\u672f\u4e4b\u6700\u5427\uff01\u201d\u7f57\u5170\u9053\uff1a\u201c\u5929\u540e\u5bab\u4e2d\u5973\u5f1f\u5b50\uff0c\u4e2a\u4e2a\u7cbe\u64c5\u5a9a\u672f\uff0c\u5c31\u53ef\u80fd\u6765\u81ea\u5c0f\u897f\u5929\u96f7\u97f3\u5bfa\u4e2d\u3002\u201d \u3000\u3000\u201c\u4e0d\u5f97\u4e86\uff0c\u7f57\u5170\uff0c\u4f60\u662f\u8d8a\u6765\u8d8a\u7075\u5149\u4e86\u2026\u2026\u201d\u5927\u548c\u5c1a\u9063\uff0c\u201c\u8bf4\u7684\u5341\u5206\u6709\u7406\u2026\u2026\u201d\u201c\u5927\u548c\u5c1a\uff0c\u8001\u53eb\u5316\uff0c\u4f60\u4eec\u5bf9\u5929\u9b54\u6d88\u9b42\u9635\u77e5\u9053\u591a\u5c11\uff1f\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u4e0d\u8981\u6709\u6240\u907f\u5fcc\uff0c\u77e5\u9053\u7684\u5168\u8bf4\u51fa\u6765\uff01\u201d \u3000\u3000\u201c\u6ca1\u542c\u8fc7\u8fd9\u79cd\u6b66\u529f\uff0c\u8001\u53eb\u5316\u4e5f\u662f\u7b2c\u4e00\u6b21\u542c\u5230\u8fd9\u4e2a\u540d\u5b57\uff0c\u4f46\u987e\u540d\u601d\u4e49\uff0c\u5929\u9b54\u6d88\u9b42\u9635\uff0c\u7edd\u4e0d\u662f\u6b63\u7ecf\u540d\u5802\u3002\u201d \u3000\u3000\u201c\u4e2d\u539f\u6ca1\u6709\u8fd9\u79cd\u6b66\u529f\uff0c\u8fd9\u4e2a\u9635\u6cd5\u3002\u201d\u5927\u548c\u5c1a\u9053\uff1a\u201c\u9093\u98de\uff0c\u4f60\u5e94\u8be5\u542c\u5230\u8fc7\u4e00\u70b9\u4f20\u8bf4\u624d\u5bf9\uff1f\u201d \u3000\u3000\u201c\u8001\u673d\u5012\u662f\u542c\u8fc7\u4e00\u6b21\u2026\u2026\u201d\u9093\u98de\u9b54\u773c\u4e2d\u78a7\u5149\u4e00\u95ea\uff0c\u9053\uff1a\u201c\u53ea\u662f\u8bf4\u7684\u4eba\u8bed\u7109\u4e0d\u8be6\uff0c\u6211\u4eec\u542c\u7684\u4eba\u4e5f\u4f3c\u61c2\u975e\u61c2\uff0c\u6211\u60f3\u9752\u51e4\u59d1\u5a18\u4e5f\u8bb8\u53ef\u4ee5\u63d0\u51fa\u6df1\u5165\u4e00\u4e9b\u7684\u8bf4\u660e\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u9752\u51e4\uff0c\u4f60\u77e5\u9053\u5427\uff1f\u77e5\u9053\u591a\u5c11\u5c31\u8bf4\u591a\u5c11\uff01\u201d \u3000\u3000\u201c\u662f\uff01\u5929\u9b54\u6d88\u9b42\u9635\u662f\u5c0f\u897f\u5929\u96f7\u97f3\u5bfa\u4e2d\u4e09\u7edd\u4e4b\u2014\u2026\u2026\u201d\u9752\u51e4\u9053\uff1a\u201c\u5f88\u5c11\u88ab\u6d3e\u51fa\u6765\uff0c\u7528\u4e8e\u5bf9\u654c\uff0c\u5e72\u5a18\u8bf4\u5b83\u662f\u5a9a\u672f\u4e4b\u6700\uff0c\u4e00\u8bed\u9053\u7834\u7384\u673a\u2026\u2026\u201d \u3000\u3000\u201c\u53ea\u662f\u5982\u6b64\uff0c\u90a3\u5c31\u4e0d\u8db3\u754f\u4e86\u2026\u2026\u201d\u6c5f\u67ab\u6253\u65ad\u4e86\u9752\u51e4\u7684\u8bdd\uff0c\u9053\uff1a\u201c\u5929\u72d0\u6280\u827a\u4e2d\u7684\u60d1\u5fc3\u672f\uff0c\u5e94\u8be5\u6297\u62d2\u5f97\u4f4f\uff0c\u53ea\u8981\u80fd\u7ed9\u6211\u4e00\u523b\u5de5\u592b\uff0c\u6211\u5e94\u8be5\u80fd\u6740\u4e86\u5979\u4eec\u3002\u201d \u3000\u3000\u9752\u51e4\u9759\u9759\u7684\u7b49\u7740\u6c5f\u67ab\u628a\u8bdd\u8bf4\u5b8c\uff0c\u624d\u7b11\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u84dd\u51e4\u8bf4\uff0c\u9664\u4e86\u4ee4\u4eba\u8ff7\u9189\u9b54\u5973\u4e4b\u821e\u5916\uff0c\u8fd8\u53ef\u80fd\u6563\u5e03\u5f88\u591a\u79cd\u7684\u5947\u6bd2\u3002\u201d \u3000\u3000\u6c5f\u67ab\u5446\u4e86\u4e00\u5446\uff0c\u4e0d\u8bf4\u8bdd\u4e86\u3002 \u3000\u3000\u4ed6\u6709\u4fe1\u5fc3\u80fd\u6297\u62d2\u5404\u79cd\u5d4b\u672f\uff0c\u53ef\u4e0d\u662f\u767e\u6bd2\u4e0d\u4fb5\u4e4b\u4f53\u3002 \u3000\u3000\u201c\u662f\u4ec0\u4e48\u6837\u5947\u6bd2\u5462\uff1f\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u4e5d\u5927\u9b54\u5973\uff0c\u4e5f\u662f\u4eba\uff0c\u4eba\u5165\u9b54\u9053\uff0c\u53ea\u80fd\u8bf4\u6280\u827a\u90aa\u6076\uff0c\u5979\u4eec\u5e94\u8be5\u4e5f\u65e0\u6cd5\u907f\u5f00\u5947\u6bd2\u4fb5\u88ad\u3002\u201d \u3000\u3000\u201c\u662f\u7684\uff01\u4f46\u5979\u4eec\u53ef\u4ee5\u5148\u670d\u4e86\u89e3\u836f\u2026\u2026\u201d\u9752\u51e4\u9053\uff1a\u201c\u56f0\u96be\u7684\u662f\u4e0d\u77e5\u9053\u5979\u4eec\u4f1a\u7528\u4ec0\u4e48\u6bd2\uff1f\u4e5d\u4f4d\u9b54\u5973\uff0c\u5404\u64c5\u4e00\u6bd2\uff0c\u8fce\u654c\u4e4b\u524d\uff0c\u624d\u51b3\u5b9a\u7528\u54ea\u79cd\u6bd2\u7269\u5bf9\u654c\uff0c\u84dd\u51e4\u53ea\u542c\u5230\u8fd9\u4e9b\uff0c\u5374\u60f3\u4e0d\u51fa\u7834\u89e3\u4e4b\u6cd5\uff0c\u9b54\u6559\u672f\u6cd5\uff0c\u4ee5\u5a9a\u672f\u3001\u6bd2\u6280\u6700\u7cbe\uff0c\u4f46\u4e5f\u6709\u53ef\u6015\u7684\u6b66\u529f\u548c\u4e00\u4e9b\u4e0d\u53ef\u601d\u8bae\u90aa\u672f\uff0c\u84dd\u51e4\u7f9e\u4e8e\u65e0\u7b56\u4ee5\u5bf9\uff0c\u4e0d\u6562\u5411\u5f20\u59e8\u62a5\u544a\uff0c\u624d\u8981\u6211\u8f6c\u62a5\u5f20\u59e8\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u5fc3\u4e2d\u5fd6\u9053\uff1a\u201c\u5c0f\u624d\u5973\u679c\u7136\u662f\u597d\u80dc\u7684\u5f88\u3002\u201d \u3000\u3000\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u53e3\u4e2d\u5374\u8bf4\u9053\uff1a\u201c\u6c5f\u67ab\u4f60\u4f1a\u7684\u6b66\u529f\u4e2d\uff0c\u6709\u6ca1\u6709\u6297\u6bd2\u7684\u6280\u827a\uff1f\u201d \u3000\u3000\u201c\u6709\uff01\u53ea\u4e0d\u8fc7\uff0c\u6211\u706b\u5019\u672a\u5230\uff0c\u53ea\u6015\u662f\u96be\u6709\u6548\u7528\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u4f46\u4ed7\u51ed\u5b9d\u5203\u5229\u5668\uff0c\u4ee5\u5feb\u653b\u654c\uff0c\u5e94\u8be5\u6709\u4e00\u534a\u81f4\u80dc\u673a\u4f1a\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u94b1\u5144\uff0c\u4f60\u548c\u6c5f\u67ab\u8d70\u4e00\u8d9f\uff0c\u529d\u963b\u541f\u96ea\u548c\u541f\u971c\u7684\u6740\u610f\uff0c\u4e5f\u963b\u6b62\u7533\u96ea\u541b\u7684\u62a5\u4ec7\u4e4b\u5ff5\uff0c\u8bf4\u670d\u590f\u5929\u540c\u3001\u767d\u5929\u5316\u7b49\u5f52\u670d\u8fc7\u6765\uff0c\u5408\u529b\u5bf9\u6297\u90a3\u4f4d\u6559\u4e3b\u3002\u201d \u3000\u3000\u8001\u53eb\u5316\u5fae\u5fae\u4e00\u5446\uff0c\u5fd6\u9053\uff1a\u201c\u8bf4\u670d\u4eba\u7684\u672c\u9886\uff0c\u6211\u96be\u6bd4\u9152\u8089\u548c\u5c1a\u5341\u4e4b\u4e00\u4e8c\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u6d3e\u548c\u5c1a\u53bb\u5462\uff1f \u3000\u3000\u4f46\u5f20\u56db\u59d1\u5df2\u7136\u4f20\u4ee4\u51fa\u53e3\uff0c\u81ea\u662f\u4e0d\u80fd\u8fdd\u6297\uff0c\u53cd\u6b63\u6709\u6c5f\u67ab\u540c\u884c\uff0c\u8fd9\u5c0f\u5b50\u53ef\u4ee5\u5403\u5b9a\u4e86\u4e24\u4e2a\u5c0f\u72d0\u5973\uff0c\u7533\u96ea\u541b\u4e5f\u4e0d\u4f1a\u6267\u610f\u548c\u4ed6\u4e89\u8fa9\u3002\u201d \u3000\u3000\u8001\u53eb\u5316\u867d\u7136\u4e0d\u5584\u8bf4\u7406\uff0c\u4f46\u541f\u96ea\u5374\u5584\u8c0b\u80fd\u65ad\uff0c\u53e3\u9f7f\u4f36\u4fd0\u3002 \u3000\u3000\u5fc3\u4e2d\u76d8\u7b97\u4e86\u4e00\u4e0b\uff0c\u7acb\u523b\u5e94\u9053\uff1a\u201c\u662f\uff01\u8001\u53eb\u5316\u8fd9\u5c31\u51fa\u52a8\u3002\u201d\u548c\u6c5f\u67ab\u8054\u8882\u800c\u53bb\u3002 \u3000\u3000\u201c\u9093\u98de\uff0c\u4f60\u5e2e\u52a9\u9752\u51e4\uff0c\u7ef4\u62a4\u5979\u548c\u84dd\u51e4\u7684\u8fde\u7edc\u3002\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u4ee5\u6697\u4e2d\u63a5\u5e94\u4e3a\u4e3b\uff0c\u4e0d\u8981\u548c\u654c\u4eba\u6b63\u9762\u51b2\u7a81\uff0c\u6c5f\u6cb3\u4e0d\u62e9\u7ec6\u6d41\uff0c\u4f60\u81ea\u5df1\u914c\u91cf\u7740\u529e\u5427\uff01\u201d \u3000\u3000\u201c\u9093\u67d0\u9886\u547d\u3002\u201d\u548c\u9752\u51e4\u53cc\u53cc\u79bb\u53bb\u3002 \u3000\u3000\u56de\u5934\u53c8\u770b\u770b\u6b27\u9633\u662d\u9053\uff1a\u201c\u80e1\u840d\u7236\u5b50\uff0c\u6bb5\u6c0f\u5e08\u5f92\uff0c\u6682\u7531\u4f60\u4eec\u592b\u5987\u7edf\u5e05\uff0c\u4e00\u65e6\u6709\u654c\u6765\u88ad\uff0c\u80fd\u907f\u5219\u907f\u3002\u2026\u2026\u201d \u3000\u3000\u7f57\u5170\u63a5\u9053\uff1a\u201c\u8981\u662f\u907f\u4e0d\u5f00\u5462\uff1f\u201d \u3000\u3000\u201c\u90a3\u5c31\u53ea\u597d\u52a8\u624b\u4e00\u6218\u4e86\uff0c\u4e0d\u8fc7\uff0c\u654c\u52bf\u5f3a\u5927\uff0c\u6c5f\u67ab\u548c\u53f6\u96ea\u3001\u541f\u971c\u63a5\u5e94\u4e0d\u53ca\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u4ed6\u4eec\u7684\u6027\u547d\uff0c\u5c31\u65ad\u9001\u5728\u4f60\u4eec\u592b\u5987\u624b\u4e2d\u4e86\u3002\u201d \u3000\u3000\u6b27\u9633\u662d\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u56db\u59d1\u653e\u5fc3\uff0c\u6211\u4e0d\u4f1a\u79f0\u4e00\u65f6\u8c6a\u5f3a\uff0c\u8349\u83c5\u4eba\u547d\uff0c\u4eca\u65e5\u4e4b\u654c\uff0c\u65e0\u4e0d\u662f\u5f53\u4e16\u7cbe\u9510\uff0c\u6211\u5df2\u5ba1\u5ea6\u56db\u5916\u5f62\u52bf\uff0c\u654c\u52bf\u5f3a\u5927\uff0c\u6211\u4eec\u4f1a\u9041\u5165\u897f\u65b9\u4e94\u91cc\u5916\u4e00\u7247\u6811\u6797\u4e2d\u9690\u85cf\u8d77\u6765\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u9053\uff1a\u201c\u5f88\u597d\uff0c\u6211\u548c\u548c\u5c1a\u56db\u5904\u5de1\u89c6\u4e00\u756a\u3002\u201d \u3000\u3000\u201c\u8fd9\u91cc\u662f\u4e2d\u519b\u5927\u5be8\uff0c\u4f60\u8d70\u4e86\u5982\u4f55\u6536\u4f20\u519b\u60c5\u3002\u201d\u7f57\u5170\u6025\u4e86\uff0c\u5927\u58f0\u56b7\u9053\u3002 \u3000\u3000\u5f20\u56db\u59d1\u5fd6\u9053\uff1a\u201c\u679c\u7136\u662f\u6fc0\u5c06\u8981\u6bd4\u9063\u5c06\u5f3a\u3002\u201d\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u7f57\u5170\uff0c\u6211\u53bb\u53bb\u5c31\u6765\uff0c\u4f60\u56b7\u56b7\u4ec0\u4e48\uff1f\u522b\u5fd8\u4e86\u7167\u987e\u8fce\u6625\uff0c\u5927\u548c\u5c1a\uff0c\u6211\u4eec\u8d70\uff01\u201d \u3000\u3000\u4e24\u4eba\u884c\u51fa\u4e86\u56db\u4e94\u91cc\u8def\uff0c\u5230\u4e00\u5904\u9690\u907f\u6240\u5728\uff0c\u5f20\u56db\u59d1\u56db\u987e\u65e0\u4eba\uff0c\u624d\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u544a\u8bc9\u6211\uff0c\u4e24\u4e2a\u5c0f\u72d0\u5973\u600e\u4e48\u4e86\uff1f\u201d \u3000\u3000\u201c\u5979\u4eec\u4e3a\u4e86\u5e2e\u52a9\u6c5f\u5927\u54e5\uff0c\u4e0d\u7ea6\u800c\u540c\uff0c\u9009\u62e9\u4e86\u90aa\u6076\u7684\u6b66\u6280\uff0c\u6613\u5b66\u6613\u6210\uff0c\u5a01\u529b\u60ca\u4eba\u2026\u2026\u201d\u5927\u548c\u5c1a\u53f9\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u8fd9\u7b49\u6280\u827a\uff0c\u9677\u6eba\u592a\u6df1\uff0c\u4eba\u4e5f\u4f1a\u6ca6\u5165\u9b54\u9053\uff0c\u90a3\u5c31\u4f1a\u5931\u53bb\u4eba\u6027\uff0c\u60f3\u4e0d\u5230\u6ca6\u52ab\u7684\u4e0d\u662f\u6c5f\u67ab\uff0c\u5374\u662f\u4e24\u4e2a\u4e2b\u5934\u3002\u201d \u3000\u3000\u5f20\u56db\u59cb\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u6c5f\u67ab\u77e5\u4e0d\u77e5\u9053\uff1f\u201d \u3000\u3000\u201c\u4ed6\u6ca1\u6709\u544a\u8bc9\u548c\u5c1a\uff0c\u4f46\u5c31\u548c\u5c1a\u89c2\u5bdf\uff0c\u4ed6\u597d\u50cf\u5bdf\u89c9\u4e86\u3002\u201d \u3000\u3000\u201c\u6709\u6ca1\u6709\u529e\u6cd5\u633d\u6551\uff1f\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u6211\u4eec\u4e0d\u80fd\u5750\u89c6\u4e0d\u7ba1\u5427\uff1f\u201d \u3000\u3000\u201c\u5e94\u8be5\u6709\uff0c\u4e0d\u8fc7\uff0c\u8981\u5728\u5979\u4eec\u6ca6\u9b54\u4e0d\u6df1\uff0c\u5fc3\u6027\u672a\u53d8\u4e4b\u524d\u4e0b\u624b\uff0c\u8054\u5408\u6c5f\u67ab\u7684\u529b\u91cf\uff0c\u4f7f\u5979\u4eec\u60ac\u5d16\u52d2\u9a6c\uff0c\u548c\u5c1a\u51c6\u5907\u4ee5\u4e03\u65e5\u591c\u4e0d\u505c\u606f\u7684\u65b9\u6cd5\uff0c\u5ff5\u51fa\u5927\u60b2\u68b5\u5531\uff0c\u4f7f\u5979\u4eec\u6210\u957f\u7684\u9b54\u5fc3\u6d88\u9000\u3002\u201d \u3000\u3000\u201c\u4ee5\u4e8c\u5973\u672c\u8d28\u7684\u7eaf\u51c0\uff0c\u8fd8\u80fd\u6491\u591a\u5c11\u65f6\u95f4\uff1f\u201d \u3000\u3000\u201c\u6700\u591a\u4e09\u4e2a\u6708\uff0c\u8fd9\u8fd8\u662f\u6c5f\u67ab\u5b9a\u529b\u9ad8\u660e\uff0c\u4fdd\u7559\u4e86\u5979\u4eec\u6e05\u767d\u7684\u8eab\u4f53\uff0c\u52a0\u5f3a\u4e86\u5979\u4eec\u7075\u5149\u6297\u529b\u2026\u2026\u201d\u5927\u548c\u5c1a\u9053\uff1a\u201c\u73b0\u5728\uff0c\u4e8c\u5973\u9b54\u529f\u8fdb\u5883\uff0c\u5df2\u5165\u5766\u9014\uff0c\u5b9e\u6709\u4e00\u65e5\u5e72\u91cc\u4e4b\u52bf\uff0c\u541f\u96ea\u8fd8\u80fd\u81ea\u6301\uff0c\u541f\u971c\u53ea\u8981\u4e00\u6740\u4eba\u89c1\u8840\uff0c\u5c31\u96be\u81ea\u7981\uff0c\u73b0\u5728\uff0c\u5927\u6982\u53ea\u6709\u4f60\u548c\u6c5f\u67ab\uff0c\u53ef\u4ee5\u963b\u6b62\u5979\u7684\u6740\u610f\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\uff01\u7528\u4f60\u5927\u548c\u5c1a\u7684\u65e0\u8fb9\u4f5b\u6cd5\uff0c\u624d\u80fd\u5524\u56de\u5979\u4eec\u7684\u7eaf\u826f\u5929\u6027\u3002\u201d \u3000\u3000\u201c\u662f\u4e0d\u9519\uff0c\u4f46\u548c\u5c1a\u8981\u5ff5\u4f5b\u8bf5\u7ecf\uff0c\u5148\u53d8\u6c14\u8d28\uff0c\u624d\u80fd\u4f7f\u5979\u4eec\u606f\u6b62\u6740\u5ff5\uff0c\u548c\u5c1a\u4e0d\u6562\u591a\u559d\u9152\uff0c\u4e5f\u662f\u4e3a\u4e86\u4e24\u4e2a\u5c0f\u72d0\u5973\uff0c\u6211\u6015\u90a3\u79cd\u9189\u6001\u6726\u80e7\u7684\u795e\u60c5\uff0c\u4f1a\u5f71\u54cd\u4e86\u5979\u4eec\u5bf9\u6211\u7684\u89c2\u611f\u3002\u201d \u3000\u3000\u201c\u4e00\u8eab\u98ce\u6d41\u5b7d\u503a\u7684\u6c5f\u67ab\uff0c\u8fd8\u80fd\u81ea\u6301\uff0c\u5012\u662f\u4e24\u4e2a\u7eaf\u7eaf\u5982\u6c34\u7684\u5c0f\u59d1\u5a18\uff0c\u7adf\u7136\u4f1a\u6ca6\u5165\u9b54\u9053\uff0c\u5929\u554a\uff01\u8fd9\u771f\u662f\u54ea\u91cc\u8bf4\u8d77\u5462\uff1f\u201d\u5f20\u56db\u59d1\u6709\u4e9b\u9eef\u7136\u9053\uff1a\u201c\u81ea\u5c0f\u5c31\u7ec3\u5929\u72d0\u6b66\u529f\uff0c\u53ef\u80fd\u662f\u52a9\u957f\u4e86\u5979\u4eec\u6ca6\u5165\u9b54\u52ab\u7684\u8981\u56e0\uff1f\u201d \u3000\u3000\u201c\u5f20\u59d1\u5a18\uff0c\u522b\u592a\u7126\u6025\uff0c\u8fd9\u65f6\u523b\u751f\u6b7b\u5173\u5934\uff0c\u4f60\u5982\u4e71\u4e86\u65b9\u5bf8\uff0c\u90a3\u624d\u662f\u6700\u5927\u7684\u5931\u7b56\uff0c\u4f55\u51b5\uff0c\u5c31\u548c\u5c1a\u89c2\u5bdf\uff0c\u5929\u72d0\u6b66\u529f\uff0c\u90aa\u800c\u4e0d\u6076\uff0c\u5df2\u7ecf\u8fc7\u5929\u72d0\u592b\u4eba\u518d\u4e00\u756a\u51b6\u7ec3\u3002 \u3000\u3000\u4f60\u7684\u9041\u6cd5\u6280\u827a\uff0c\u4e5f\u975e\u6b63\u9053\uff0c\u4f46\u4f60\u5374\u6b63\u800c\u4e0d\u90aa\uff0c\u6076\u6e90\u51fa\u81ea\u2018\u4f20\u76f4\u8bb0\u2019\uff0c\u90a3\u662f\u4e16\u4e0a\u6700\u90aa\u6076\u7684\u4e00\u672c\u4e66\u3002 \u3000\u3000\u4f46\u548c\u5c1a\u76f8\u4fe1\uff0c\u4e09\u4e2a\u6708\u5185\uff0c\u8fd8\u4e0d\u4f1a\u4f7f\u4e24\u4e2a\u5c0f\u72d0\u5973\u4eba\u6027\u6ca6\u5931\uff0c\u518d\u957f\u5927\u548c\u5c1a\u5c31\u6ca1\u6709\u628a\u63e1\u4e86\uff0c\u4f46\u6700\u53ef\u6015\u7684\u662f\u541f\u96ea\u7684\u6253\u7b97\uff0c\u5979\u7075\u6167\u7edd\u4f26\uff0c\u4ee5\u8eab\u6d89\u9b54\uff0c\u6050\u6015\u662f\u65e9\u6709\u8ba1\u7b97\u4e86\u3002\u201d \u3000\u3000\u201c\u4f60\u548c\u5c1a\u7684\u610f\u601d\uff0c\u53ef\u662f\u8bf4\u541f\u96ea\u5df2\u51c6\u5907\u81ea\u6217\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u80fd\u4e0d\u9632\u554a\u2026\u2026\u201d\u5927\u548c\u5c1a\u9053\uff1a\u201c\u54b1\u4eec\u65e2\u5df3\u77e5\u6653\uff0c\u7edd\u4e0d\u80fd\u8ba9\u5979\u4eec\u518d\u51fa\u610f\u5916\u3002\u201d \u3000\u3000\u201c\u8fd9\u4ef6\u4e8b\u6765\u7684\u7a81\u7136\uff0c\u8001\u5b9e\u8bf4\uff0c\u6211\u771f\u60f3\u4e0d\u51fa\u5e94\u8be5\u5982\u4f55\u5904\u7f6e\uff1f\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u8fd9\u65b9\u9762\uff0c\u8981\u4f60\u5927\u548c\u5c1a\u591a\u8d39\u5fc3\u4e86\uff01\u201d \u3000\u3000\u201c\u548c\u5c1a\u5c3d\u529b\u800c\u4e3a\uff0c\u6211\u4f1a\u627e\u673a\u4f1a\u548c\u541f\u96ea\u4ea4\u8c08\uff0c\u4e5f\u8bb8\u8fd9\u4f4d\u7075\u6c14\u903c\u4eba\u7684\u5c0f\u59d1\u5a18\uff0c\u65e9\u5df2\u7ecf\u6709\u4e86\u5e94\u5bf9\u4e4b\u7b56\uff1f\u54b1\u4eec\u5728\u767d\u767d\u62c5\u5fc3\u5566\u3002\u201d \u3000\u3000\u201c\u4e0d\u7ba1\u5982\u4f55\uff1f\u6211\u8981\u4e24\u4e2a\u6708\u5185\u51fb\u6e83\u9b54\u6559\u4e2d\u7684\u9ad8\u624b\uff0c\u6740\u4e86\u90a3\u4f4d\u6559\u4e3b\uff0c\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u6211\u4e00\u76f4\u6709\u4e2a\u5947\u602a\u7684\u611f\u89c9\uff0c\u90a3\u4f4d\u6559\u4e3b\u624d\u662f\u53d1\u52a8\u8fd9\u573a\u6c5f\u6e56\u5927\u53d8\u7684\u4e3b\u51f6\uff0c\u5c0f\u897f\u5929\u96f7\u97f3\u5bfa\u4e2d\u7684\u9b54\u6559\u4eba\u7269\uff0c\u53ea\u662f\u5e2e\u51f6\u3002\u201d \u3000\u3000\u201c\u6700\u91cd\u8981\u7684\u4e00\u4ef6\u4e8b\uff0c\u662f\u67e5\u660e\u6559\u4e3b\u7684\u771f\u6b63\u8eab\u4efd\uff0c\u5982\u662f\u4e0d\u80fd\u516c\u8bf8\u4e8e\u4e16\u3002\u201d\u5927\u548c\u5c1a\u9053\uff1a\u201c\u90a3\u5c31\u4e0b\u624b\u9664\u53bb\uff0c\u522b\u8ba9\u4e8b\u6001\u6269\u5927\uff0c\u518d\u5174\u5200\u5175\u4e4b\u707e\uff01\u201d \u3000\u3000\u201c\u770b\u6765\uff0c\u4f60\u548c\u5c1a\u4e5f\u6709\u9884\u611f\u4e86\uff1f\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u53ea\u53ef\u60dc\uff0c\u6709\u4e9b\u60f3\u4e0d\u660e\u767d\uff01\u2026\u2026\u201d \u3000\u3000\u201c\u90a3\u5c31\u4e0d\u8981\u60f3\uff0c\u60f3\u591a\u4e86\u6613\u5165\u5e7b\u5883\u2026\u2026\u201d\u5927\u548c\u5c1a\u9053\uff1a\u201c\u5230\u76ee\u524d\u4e3a\u6b62\uff0c\u8fd9\u53ea\u662f\u4e00\u573a\u6b66\u6797\u4e2d\u7684\u53d8\u4e71\uff0c\u4f60\u53d7\u6b66\u6797\u4e09\u5723\u5631\u6258\uff0c\u5b8c\u6210\u4ed6\u4eec\u7684\u9057\u5fd7\uff0c\u5012\u662f\u5bf9\u4e8c\u4e2a\u5c0f\u72d0\u5973\uff0c\u4f60\u6709\u7740\u7edd\u5bf9\u4fdd\u62a4\u7684\u8d23\u4efb\uff0c\u5f53\u7136\uff0c\u6211\u548c\u5c1a\u4e5f\u7b97\u4e00\u4efd\u3002\u201d \u3000\u3000\u201c\u5bf9\u84dd\u51e4\uff0c\u4f60\u53ef\u6709\u4ec0\u4e48\u770b\u6cd5\uff1f\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u5979\u80f8\u85cf\u97ec\u7565\uff0c\u6df1\u8c19\u6218\u7b56\uff0c\u662f\u4e2a\u771f\u6b63\u7684\u5c06\u624d\u3002\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u548c\u5c1a\u4e5f\u6709\u540c\u611f\uff0c\u53ea\u53ef\u60dc\u5df2\u5165\u60c5\u7f51\uff0c\u4e5f\u96be\u514d\u513f\u5973\u60c5\u957f\uff0c\u4f46\u5979\u6210\u4e86\u51b3\u80dc\u4e4b\u6218\u7684\u5173\u952e\uff0c\u597d\u597d\u7684\u7b3c\u7edc\u5979\uff0c\u8ba9\u5979\u5c3d\u5c55\u6240\u80fd\uff0c\u5e03\u4e0b\u6b7b\u5c40\uff0c\u9677\u654c\u5165\u7b3c\uff0c\u548c\u5c1a\u8981\u5ff5\u7ecf\u6c42\u4f5b\uff0c\u522b\u8ba9\u90a3\u4f4d\u6559\u4e3b\u4e8b\u5148\u77a7\u51fa\u4e86\u7834\u7efd\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u7b11\u9053\uff1a\u201c\u84dd\u98ce\u5f88\u51b7\u9759\uff0c\u5927\u6982\u4e0d\u4f1a\uff01\u54b1\u4eec\u5404\u5c3d\u5fc3\u529b\uff0c\u4f60\u53bb\u5e2e\u6c5f\u67ab\u6536\u670d\u590f\u5929\u540c\u90a3\u6279\u4eba\uff0c\u987a\u4fbf\u67e5\u770b\u4e24\u4e2a\u5c0f\u72d0\u5973\u5165\u9b54\u60c5\u666f\uff1f\u201d \u3000\u3000\u5927\u548c\u5c1a\u9053\uff1a\u201c\u6211\u4eec\u8fd9\u756a\u4ea4\u8c08\uff0c\u4e0d\u53ef\u6cc4\u9732\uff0c\u5c24\u5176\u662f\u7f57\u5170\uff0c\u5979\u5e73\u65e5\u7c97\u679d\u5927\u53f6\uff0c\u4f46\u5ffd\u7136\u7cbe\u660e\u8d77\u6765\uff0c\u4f1a\u8ffd\u7684\u4eba\u76f4\u5192\u51b7\u6c57\uff0c\u5979\u80f8\u65e0\u57ce\u5e9c\uff0c\u53e3\u65e0\u906e\u62e6\uff0c\u4e00\u9ad8\u5174\u56b7\u51fa\u6765\uff0c\u90a3\u5c31\u5927\u715e\u98ce\u666f\u4e86\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u70b9\u70b9\u5934\uff0c\u65e0\u9650\u611f\u6168\u7684\u9053\uff1a\u201c\u4e3a\u4eba\u8981\u6709\u4e09\u5206\u50bb\u6c14\uff0c\u624d\u80fd\u6d3b\u5f97\u5feb\u4e50\uff0c\u6211\u771f\u6709\u70b9\u7fa1\u6155\u7f57\u5170\u5462\uff01\u201d \u3000\u3000\u201c\u5f20\u59d1\u5a18\uff0c\u4e09\u5206\u50bb\u6c14\u4e4b\u5916\uff0c\u8fd8\u5f97\u6709\u4e03\u5206\u81ea\u7136\u3002\u201d\u5927\u548c\u5c1a\u6253\u65ad\u4e86\u5f20\u56db\u59d1\u7684\u8bdd\uff0c\u9053\uff1a\u201c\u5404\u6709\u56e0\u7f18\u83ab\u7fa1\u4eba\uff0c\u60f3\u60f3\u770b\u8fd9\u756a\u5149\u590d\u6b66\u6797\u7684\u5fd7\u4e1a\u5b8c\u6210\uff0c\u4f60\u5728\u6c5f\u6e56\u4e0a\u53d7\u5230\u7684\u5c0a\u5d07\uff0c\u6b66\u6797\u4e09\u5723\u4e5f\u5c06\u6709\u6240\u4e0d\u53ca\uff0c\u4ed8\u51fa\u591a\u5c11\u5fc3\u8840\u3001\u727a\u7272\uff0c\u4e5f\u5c31\u4e0d\u7528\u8ba1\u8f83\u4e86\u3002\u201d \u3000\u3000\u8bf4\u5b8c\u8bdd\uff0c\u8f6c\u8eab\u4e00\u8dc3\uff0c\u4eba\u5df2\u5230\u4e09\u4e08\u5f00\u5916\u3002 \u3000\u3000\u671b\u7740\u5927\u548c\u5c1a\u8fdc\u53bb\u7684\u80cc\u5f71\uff0c\u5f20\u53e3\u59d1\u5fc3\u4e2d\u5fd6\u9053\uff1a\u201c\u60ed\u6127\uff0c\u60ed\u6127\uff0c\u8fc7\u53bb\u4f4e\u4f30\u4ed6\u4e86\uff0c\u8fd9\u4e2a\u9152\u8089\u4e0d\u5fcc\u7684\u91ce\u548c\u5c1a\u554a\uff01\u5b9e\u662f\u4e2a\u662f\u4f5b\u5149\u7167\u4eba\u7684\u9ad8\u50e7\u3002\u201d \u3000\u3000\u7533\u96ea\u541b\u53cc\u76ee\u76ef\u6ce8\u590f\u5929\u540c\uff0c\u8138\u4e0a\u6012\u5bb9\u6d8c\u73b0\uff0c\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u4eba\uff0c\u8bbe\u8ba1\u6211\u3001\u5229\u7528\u6211\uff0c\u8ba9\u6211\u7cca\u6d82\u6d3b\u4e86\u4e8c\u5341\u5e74\uff0c\u73b0\u5728\uff0c\u6211\u6e05\u9192\u4e86\uff0c\u5374\u53c8\u8981\u6ee1\u6000\u6127\u548e\u75db\u82e6\u7684\u6d3b\u4e0b\u53bb\u3002\u2026\u2026\u201d \u3000\u3000\u201c\u96ea\u541b\u3002\u201d \u3000\u3000\u590f\u5929\u540c\u6253\u65ad\u4e86\u7533\u96ea\u541b\u7684\u8bdd\u3002 \u3000\u3000\u201c\u4e0d\u8981\u53eb\u6211\uff0c\u6211\u6068\u4f60\uff0c\u6068\u6b7b\u4f60\u4e86\uff0c\u6211\u7adf\u7136\u966a\u4f60\u751f\u6d3b\u4e86\u4e8c\u5341\u5e74\uff0c\u6211\u8fde\u81ea\u5df1\u4e5f\u65e0\u6cd5\u539f\u8c05\u4e86\uff0c\u2026\u2026\u201d \u3000\u3000\u7533\u96ea\u541b\u6cea\u5782\u53cc\u988a\uff0c\u9762\u8272\u60e8\u767d\uff0c\u7d27\u54ac\u94f6\u7259\uff0c\u9053\uff1a\u201c\u767d\u5929\u5316\uff0c\u4f60\u4eec\u4e5f\u60f3\u60f3\uff0c\u662f\u4e0d\u662f\u4e5f\u4e3a\u590f\u5929\u540c\u8680\u5fc3\u9b54\u97f3\u8ff7\u60d1\u4e86\uff1f\u201d \u3000\u3000\u201c\u6211\u6d3b\u7684\u4e5f\u5f88\u75db\u82e6\uff0c\u96ea\u541b\uff0c\u4e0d\u662f\u4f60\u966a\u7740\u6211\uff0c\u7ed9\u4e86\u6211\u6d3b\u4e0b\u53bb\u7684\u52c7\u6c14\uff0c\u4e8c\u5341\u5e74\u524d\u6211\u5c31\u53bb\u6b7b\u4e86\u2026\u2026\u201d\u590f\u5929\u540c\u9053\uff1a\u201c\u4e8b\u5b9e\u4e0a\uff0c\u6211\u4e5f\u662f\u88ab\u4eba\u5229\u7528\u4e86\uff01\u4f60\u4ee5\u8eab\u76f8\u8bb8\u65f6\uff0c\u8fd8\u6709\u4e9b\u51fa\u6211\u610f\u5916\uff0c\u6211\u76f8\u4fe1\u662f\u771f\u7684\u6709\u4eba\u8bbe\u8ba1\u4e86\u4f60\uff0c\u4f46\u7edd\u4e0d\u662f\u6211\uff01\u201d \u3000\u3000\u7533\u96ea\u541b\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u771f\u7684\u5417\uff1f\u201d \u3000\u3000\u201c\u5b57\u5b57\u771f\u5b9e\uff0c\u4e00\u5b57\u6709\u865a\uff0c\u5929\u5730\u4e0d\u5bb9\u3002\u201d\u590f\u5929\u540c\u9063\uff1a\u201c\u5343\u9519\uff0c\u4e07\u9519\uff0c\u9519\u5df2\u6210\uff0c\u8fc7\u53bb\u7684\u518d\u4e5f\u65e0\u6cd5\u91cd\u590d\u6765\u8fc7\u3002\u201d \u3000\u3000\u201c\u4f60\u6740\u4e86\u6b66\u6797\u4e09\u5723\uff0c\u8be5\u662f\u771f\u7684\u5427\uff1f\u201d \u3000\u3000\u201c\u6211\u6740\u4e0d\u4e86\u4ed6\u4eec\u7684\uff0c\u6211\u53ea\u662f\u5728\u6697\u4e2d\u4e0b\u6bd2\u2026\u2026\u201d\u590f\u5929\u540c\u82e6\u7b11\u4e00\u4e0b\u53c8\u9053\uff1a\u201c\u73b0\u5728\u60f3\u6765\uff0c\u8981\u6211\u4e0b\u6bd2\u53ea\u662f\u8981\u6211\u80cc\u8d1f\u4e0a\u8fd9\u4e2a\u7f6a\u540d\uff0c\u4ee5\u91d1\u4e39\u4e66\u751f\u533b\u672f\u4e4b\u7cbe\uff0c\u5929\u72d0\u592b\u4eba\u7684\u673a\u8b66\u591a\u7591\uff0c\u600e\u4f1a\u5403\u4e0b\u6211\u4e0b\u5728\u8336\u4e2d\u7684\u6bd2\u836f\uff0c\u6c5f\u6e56\u4e0a\u53c8\u6709\u8c01\u80fd\u8ba9\u4ed6\u4eec\u5168\u65e0\u6212\u5fc3\u7684\u996e\u4e0b\u6bd2\u8336\u6216\u662f\u6bd2\u9152\uff1f \u3000\u3000\u6211\u4eec\u529f\u529b\u76f8\u82e5\uff0c\u8680\u5fc3\u9b54\u97f3\u8ff7\u4e0d\u4f4f\u4f60\uff0c\u4f55\u51b5\uff0c\u6211\u4e5f\u4ece\u6ca1\u6709\u8fc7\u4f24\u4f60\u7684\u7528\u5fc3\u3002\u201d \u3000\u3000\u201c\u603b\u5802\u4e3b\u8bf4\u7684\u6709\u7406\u2026\u201d\u7ad9\u5728\u7533\u96ea\u541b\u8eab\u540e\u7684\u5201\u9e4f\uff0c\u5f00\u53e3\u63a5\u9053\uff1a\u201c\u5929\u9a6c\u5802\u4e2d\u4eba\uff0c\u5168\u88ab\u4eba\u5bb6\u5229\u7528\u4e86\uff0c\u5929\u9a6c\u6e21\u5371\uff0c\u53ea\u662f\u8981\u628a\u6211\u4eec\u903c\u96c6\u4e8e\u4e00\u5802\u7684\u624b\u6bb5\uff0c\u7eff\u6797\u9053\u4e0a\u7684\u532a\u76d7\u51f6\u715e\uff0c\u5168\u90fd\u96c6\u4e2d\u4e00\u5904\uff0c\u624d\u5bb9\u6613\u63a7\u5236\uff0c\u603b\u5802\u4e3b\u7684\u8680\u5fc3\u9b54\u97f3\uff0c\u80fd\u4f7f\u4eba\u5fd8\u53bb\u81ea\u6211\uff0c\u5168\u5fc3\u6548\u547d\uff0c\u624d\u662f\u4ed6\u4eec\u6367\u4f60\u4e3a\u603b\u5802\u4e3b\u7684\u539f\u56e0\u3002\u201d \u3000\u3000\u590f\u5929\u540c\u51b7\u51b7\u7684\u770b\u4e86\u5201\u9e4f\u4e00\u773c\uff0c\u4f3c\u60f3\u53d1\u4f5c\uff0c\u4f46\u53c8\u5fcd\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u8fc7\u53bb\uff0c\u5201\u9e4f\u89c1\u4e86\u590f\u5929\u540c\u548c\u767d\u5929\u5316\uff0c\u6709\u5982\u8001\u9f20\u89c1\u4e86\u732b\uff0c\u53ea\u6709\u5168\u8eab\u6253\u54c6\u55e6\u7684\u4efd\uff0c\u54ea\u91cc\u6562\u8bb2\u4e00\u53e5\u8bdd\u3002 \u3000\u3000\u73b0\u5728\u5374\u662f\u70b9\u5c18\u4e0d\u60ca\u5730\u4f83\u4f83\u800c\u8c08\uff0c\u9053\uff1a\u201c\u5929\u9a6c\u5802\u9003\u7684\u4eba\uff0c\u5168\u88ab\u9884\u5148\u57cb\u4f0f\u7684\u9ed1\u8863\u5200\u624b\u622a\u6740\uff0c\u5982\u975e\u6b27\u9633\u541f\u971c\u4e8c\u5c0f\u59d0\uff0c\u7387\u4eba\u89e3\u6551\uff0c\u6740\u4e86\u6570\u4e00\u4e2a\u9ed1\u8863\u5200\u624b\uff0c\u53ea\u6015\u8fde\u5341\u4e4b\u4e00\u4e8c\u4e5f\u9003\u4e0d\u8d70\u4e86\u3002\u201d \u3000\u3000\u201c\u73b0\u5728\u5462\uff0c\u8d70\u4e86\u591a\u5c11\uff1f\u201d\u767d\u5929\u5316\u7a81\u7136\u5f00\u53e3\u95ee\u4e86\u4e00\u53e5\u3002 \u3000\u3000\u9003\u8d70\u591a\u5c11\u4eba\uff1f \u3000\u3000\u5201\u9e4f\u5b9e\u5728\u5e76\u4e0d\u6e05\u695a\uff0c\u4f46\u5374\u6beb\u4e0d\u8fdf\u7591\u7684\u63a5\u9053\uff1a\u201c\u81f3\u5c11\u6709\u516d\u6210\u4ee5\u4e0a\u3002\u201d \u3000\u3000\u8fd9\u662f\u51ed\u85c9\u6c5f\u6e56\u7ecf\u9a8c\u4f30\u51fa\u6765\u7684\u6570\u5b57\u3002 \u3000\u3000\u201c\u590f\u603b\u5802\u4e3b\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u6740\u4e86\u4ed6\u2026\u2026\u201d\u5929\u738b\u95e8\u4e3b\u5e26\u7740\u56db\u5927\u5929\u9e45\u7f13\u6b65\u884c\u4e86\u8fdb\u6765\uff0c\u9053\uff1a\u201c\u4e00\u4e2a\u624b\u4e0b\u5506\u7f57\uff0c\u4e5f\u6562\u548c\u4f60\u9876\u5634\uff0c\u4f60\u8fd9\u603b\u5802\u4e3b\uff0c\u771f\u662f\u8d8a\u5e72\u8d8a\u56de\u53bb\u4e86\u3002\u201d \u3000\u3000\u539f\u6765\uff0c\u590f\u5929\u540c\u7b49\u505c\u8eab\u5728\u4e00\u5904\u8352\u5e99\u7684\u5927\u6bbf\u4e4b\u4e2d\u3002 \u3000\u3000\u201c\u9ec4\u5929\u738b\uff0c\u4f60\u600e\u4e48\u8fdb\u6765\u7684\uff1f\u201d \u3000\u3000\u201c\u5662\uff01\u4f60\u662f\u8bf4\u4f60\u90a3\u51e0\u4e2a\u5b88\u5728\u56db\u5468\u653e\u54e8\u7684\u5251\u58eb\u554a\uff1f\u2026\u2026\u201d\u9ec4\u5929\u4e3b\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5bf9\u4e0d\u4f4f\u5566\uff01\u4ed6\u4eec\u88ab\u5144\u5f1f\u4e00\u51fa\u624b\u5c31\u6446\u5e73\u4e86\u3002\u201d \u3000\u3000\u201c\u5c31\u51ed\u4f60\u4e00\u4e2a\u4eba\uff0c\u4e00\u51fa\u624b\u5c31\u5236\u670d\u4e86\u6211\u4e09\u4e2a\u4ece\u536b\u5251\u571f\u3002\u201d\u590f\u5929\u540c\u6709\u4e9b\u4e0d\u76f8\u4fe1\uff0c\u56e0\u4e3a\uff0c\u56db\u5927\u5929\u9e45\uff0c\u7edd\u6ca1\u6709\u4e00\u62db\u6740\u6b7b\u4ed6\u4eec\u7684\u80fd\u529b\u3002 \u3000\u3000\u201c\u9ec4\u5929\u738b\u4e00\u4e2a\u4eba\u786e\u65e0\u6cd5\u8349\u6728\u4e0d\u60ca\u5730\u4e00\u51fa\u624b\u5c31\u6446\u5e73\u4f60\u4e09\u5927\u5251\u571f\uff0c\u4f46\u52a0\u4e0a\u5c0f\u59b9\u6211\u548c\u4e24\u4f4d\u59b9\u59b9\uff0c\u90a3\u5c31\u4e0d\u540c\u4e86\u3002\u201d \u3000\u3000\u79cb\u79bb\u82b1\u4e00\u8eab\u9ec4\u886b\uff0c\u5e26\u7740\u4e8c\u5bab\u4e3b\u548c\u4e09\u5bab\u4e3b\uff0c\u7d27\u968f\u800c\u5165\u3002 \u3000\u3000\u201c\u597d\u3001\u597d\uff0c\u5929\u540e\u5bab\u4e09\u4f4d\u5bab\u4e3b\u548c\u9ec4\u5929\u738b\uff0c\u8054\u8882\u800c\u6765\uff0c\u53ef\u662f\u96be\u5f97\u4e00\u89c1\u7684\u76db\u4e3e\u2026\u2026\u201d\u590f\u5929\u540c\u9053\uff1a\u201c\u5fc5\u7136\u6709\u5927\u4e8b\u8d50\u6559\u4e86\uff1f\u201d \u3000\u3000\u201c\u7b97\u4e0d\u5f97\u4ec0\u4e48\u5927\u4e8b\uff0c\u201d\u79cb\u79bb\u82b1\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u53ea\u4e0d\u8fc7\u60f3\u8bf7\u4f60\u590f\u603b\u5802\u4e3b\u53bb\u89c1\u89c1\u6559\u4e3b\u7f62\u4e86\uff01\u201d \u3000\u3000\u5979\u5df2\u662f\u56db\u4e00\u591a\u5c81\u7684\u534a\u8001\u5f90\u5a18\uff0c\u4f46\u770b\u4e0a\u53bb\u82e5\u4e8c\u5341\u8bb8\u4eba\uff0c\u4e00\u7b11\u4e4b\u4e0b\uff0c\u6709\u5982\u767e\u82b1\u5410\u854a\uff0c\u5a07\u5a9a\u6a2a\u6ea2\u3002 \u3000\u3000\u4ee5\u590f\u5929\u540c\u529f\u529b\u4e4b\u6df1\uff0c\u4e5f\u770b\u7684\u4e3a\u4e4b\u4e00\u5446\uff0c\u6025\u6025\u522b\u8fc7\u5934\u53bb\u3002 \u3000\u3000\u201c\u79cb\u79bb\u82b1\uff0c\u5927\u6bbf\u4e2d\uff0c\u53ea\u4e0d\u8fc7\u5341\u51e0\u4e2a\u6bdb\u4eba\uff0c\u7528\u4e0d\u7740\u5356\u5f04\u98ce\u60c5\uff01\u201d\u9ec4\u5929\u738b\u6012\u9053\uff1a\u201c\u96be\u4e0d\u6210\u4f60\u8981\u7528\u88e4\u8170\u5e26\u628a\u4ed6\u4eec\u6346\u53bb\u89c1\u6559\u4e3b\u5417\uff1f\u201d \u3000\u3000\u201c\u4e0b\u5802\u4e4b\u59bb\uff0c\u5982\u6c34\u6cfc\u51fa\uff0c\u4f60\u8fd8\u5403\u7684\u4ec0\u4e48\u98de\u918b\uff1f\u201d\u79cb\u79bb\u82b1\u67af\u4e95\u4e0d\u5c0f\u6ce2\u5730\u8bf4\uff1a\u201c\u672c\u5bab\u4e3b\u8981\u7528\u4ec0\u4e48\u624b\u6bb5\u5bf9\u654c\uff0c\u7528\u4e0d\u7740\u5148\u5411\u4f60\u8bf7\u793a\u5427\uff1f\u201d \u3000\u3000\u9ec4\u5929\u738b\u51b7\u7136\u8bf4\u9053\uff1a\u201c\u672c\u95e8\u4e2d\u7f8e\u5973\u5982\u4e91\uff0c\u4e2a\u4e2a\u5e74\u8f7b\u8c8c\u7f8e\uff0c\u7528\u4e0d\u7740\u518d\u5403\u4f60\u7684\u9648\u5e74\u8001\u918b\uff0c\u6211\u53ea\u662f\u770b\u4e0d\u60ef\u4f60\u90a3\u4e2a\u98ce\u9a9a\u6837\u5b50\uff01\u201d \u3000\u3000\u79cb\u79bb\u82b1\u67f3\u7709\u8038\u52a8\u4f3c\u60f3\u53d1\u4f5c\uff0c\u5374\u88ab\u7ad9\u5728\u8eab\u4fa7\u7684\u4e8c\u5bab\u4e3b\u8f7b\u8f7b\u4e00\u626f\u8863\u886b\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5927\u59d0\uff0c\u8ba9\u5929\u738b\u51e0\u53e5\u561b\uff01\u4f60\u8be5\u660e\u767d\uff0c\u4ed6\u5fc3\u4e2d\u7a9d\u4e86\u591a\u5c11\u59d4\u5c48\uff1f\u201d \u3000\u3000\u662f\u554a\uff01\u5a07\u59bb\u5982\u82b1\uff0c\u79fb\u60c5\u522b\u604b\uff0c\u9ec4\u5929\u738b\u80fd\u4e0d\u62d4\u5200\u76f8\u5411\uff0c\u8fd9\u4efd\u4fee\u517b\uff0c\u5df2\u7ecf\u662f\u5f88\u4e0d\u9519\u4e86\u3002 \u3000\u3000\u201c\u597d\u597d\u597d\uff01\u7b97\u4f60\u82f1\u96c4\uff0c\u8fd9\u91cc\u5c31\u7531\u4f60\u4f5c\u4e3b\u3002\u201d\u79cb\u79bb\u82b1\u9053\uff1a\u201c\u4f60\u9ec4\u5929\u738b\u600e\u4e48\u8bf4\uff1f\u5c31\u600e\u4e48\u529e\u3002\u201d \u3000\u3000\u9ec4\u5929\u738b\u51b7\u54fc\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u590f\u5929\u540c\uff0c\u5929\u738b\u95e8\u3001\u5929\u540e\u5bab\u5927\u6279\u4eba\u624b\uff0c\u5df2\u628a\u8fd9\u5ea7\u7834\u5e99\u56f4\u4f4f\uff0c\u4f60\u4eec\u60f3\u7834\u56f4\u800c\u53bb\uff0c\u53ef\u53ea\u6709\u6b7b\u8def\u4e00\u6761\uff0c\u4e56\u4e56\u7684\u53bb\u89c1\u6559\u4e3b\uff0c\u4e5f\u8bb8\u8fd8\u6709\u6d3b\u547d\u7684\u673a\u4f1a\uff01\u201d \u3000\u3000\u201c\u590f\u67d0\u4eba\u5982\u679c\u4e0d\u53bb\u5462\uff1f\u201d \u3000\u3000\u201c\u90a3\u6211\u4eec\u5c31\u53ea\u597d\u51fa\u624b\u4e86\uff0c\u6349\u4e0d\u6210\u6d3b\u7684\uff0c\u6b7b\u7684\u4e5f\u597d\u3002\u201d \u3000\u3000\u590f\u5929\u540c\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u96ea\u541b\uff0c\u4f60\u600e\u4e48\u8bf4\uff1f\u201d \u3000\u3000\u7533\u96ea\u541b\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u5148\u8fc7\u4eca\u5929\u4e00\u5173\uff0c\u6211\u4eec\u7684\u5e10\u65e5\u540e\u518d\u7b97\uff01\u201d \u3000\u3000\u201c\u597d\uff01\u4eba\u4e0d\u6b7b\uff0c\u503a\u4e0d\u8d56\u2026\u2026\u201d\u590f\u5929\u540c\u53cc\u76ee\u4e2d\u95ea\u8d77\u4e00\u7247\u7ea2\u5149\uff0c\u8f6c\u671b\u7740\u9ec4\u5929\u738b\uff0c\u9053\uff1a\u201c\u6562\u4e0d\u6562\u5355\u6311\u72ec\u6597\uff1f\u4f60\u6211\u4e00\u51b3\u751f\u6b7b\uff1f\u201d \u3000\u3000\u201c\u603b\u5802\u4e3b\uff01\u201d\u767d\u5929\u5316\u9053\uff1a\u201c\u4e09\u601d\u800c\u884c\u554a\uff01\u53bb\u89c1\u6559\u4e3b\uff0c\u672a\u5fc5\u5c31\u4f1a\u6709\u4e0d\u6d4b\u4e4b\u7978\uff1f\u201d \u3000\u3000\u201c\u7b97\u4e86\uff0c\u767d\u9662\u4e3b\uff0c\u6211\u521a\u521a\u60f3\u901a\u4e86\uff0c\u8fd9\u4e9b\u5e74\uff0c\u6211\u4e00\u76f4\u51a4\u6789\u4e86\u53f8\u5f92\u827a\uff0c\u4ed6\u5e74\u5c11\u6c14\u76db\uff0c\u6bdb\u71e5\u4e86\u4e00\u4e9b\uff0c\u4f46\u5bf9\u5929\u9a6c\u5802\uff0c\u53ef\u8fd8\u662f\u5fe0\u5fc3\u803f\u803f\uff01\u201d\u590f\u5929\u540c\u82e6\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4e00\u7740\u9519\uff0c\u6ee1\u76d8\u8f93\uff0c\u4f60\u80fd\u4e24\u4e0b\u4e0d\u5e2e\uff0c\u6211\u5c31\u611f\u6fc0\u4e0d\u5c3d\u4e86\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e48\u8bf4\uff01\u603b\u5802\u4e3b\u662f\u6000\u7591\u6211\u4e86\uff1f\u201d\u767d\u5929\u5316\u6de1\u6de1\u4e00\u7b11\u53c8\u9053\uff1a\u201c\u53ea\u53ef\u60dc\uff0c\u4f60\u77e5\u9053\u7684\u592a\u665a\u4e86\u4e00\u4e9b\uff01\u201d \u3000\u3000\u201c\u767d\u8001\u513f\uff0c\u4f60\u5982\u679c\u771f\u662f\u6559\u4e3b\u5e03\u4e0b\u7684\u4e00\u6b65\u68cb\uff0c\u73b0\u5728\uff0c\u53ef\u4ee5\u8868\u73b0\u4e00\u4e0b\u4e86\uff1f\u201d\u9ec4\u5929\u738b\u9053\uff1a\u201c\u5148\u51fa\u624b\u62ff\u4e0b\u590f\u5929\u540c\uff01\u201d \u3000\u3000\u767d\u5929\u5316\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u9ec4\u5929\u738b\uff0c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u6211\u8bf4\u7684\u5f88\u6e05\u695a\u4e86\uff0c\u53ef\u662f\u8981\u6211\u518d\u8bf4\u4e00\u904d\uff1f\u201d \u3000\u3000\u201c\u767d\u5929\u5316\uff0c\u4f60\u597d\u5978\u8bc8\uff01\u201d\u7533\u96ea\u541b\u9053\uff1a\u201c\u590f\u5929\u540c\u5bf9\u4e0d\u8d77\u4efb\u4f55\u4eba\uff0c\u4f46\u5bf9\u4f60\u767d\u5929\u5316\u53ef\u662f\u4ec1\u5c3d\u4e49\u81f3\uff0c\u4fe1\u4efb\u6709\u52a0\u2026\u2026\u201d \u3000\u3000\u201c\u7533\u59d1\u5a18\uff0c\u9e1f\u5c3d\u5f13\u85cf\uff0c\u5154\u6b7b\u72d7\u70f9\uff0c\u6211\u4eec\u5982\u80fd\u6d3b\u4e0b\u53bb\uff0c\u767d\u67d0\u4eba\u4f1a\u544a\u8bc9\u4f60\u5168\u90e8\u5185\u60c5\uff0c\u4eca\u5929\u60c5\u5f62\uff0c\u5df2\u5f88\u660e\u663e\uff0c\u6559\u4e3b\u8981\u628a\u6211\u4eec\u4e00\u9505\u716e\u4e86\u3002\u201d \u3000\u3000\u201c\u767d\u8001\u513f\uff0c\u72d0\u72f8\u7ec8\u662f\u9732\u51fa\u4e86\u5c3e\u5df4\uff01\u201d\u9ec4\u5929\u738b\u9053\uff1a\u201c\u590f\u5929\u540c\u7684\u8680\u5fc3\u9b54\u97f3\uff0c\u8fd8\u771f\u628a\u4f60\u4e5f\u7ed9\u9b54\u4f4f\u4e86\uff0c\u79cb\u79bb\u82b1\uff0c\u4f60\u8bf4\uff01\u54b1\u4eec\u600e\u4e48\u5206\u914d\uff1f\u201d \u3000\u3000\u201c\u4f60\u5429\u5490\u5427\uff01\u201d\u79cb\u79bb\u82b1\u9053\uff1a\u201c\u4f60\u65e2\u7136\u4e0d\u559c\u6b22\u7b80\u5355\u7684\u514b\u654c\u4e4b\u6cd5\uff0c\u90a3\u5c31\u5927\u5f00\u4e00\u6b21\u6740\u6212\u3002\u201d \u3000\u3000\u201c\u6211\u6536\u62fe\u590f\u5929\u540c\uff0c\u76f8\u4fe1\u4e0d\u4f1a\u8d85\u8fc7\u4e94\u5341\u62db\uff0c\u56db\u5927\u5929\u9e45\u5bf9\u4ed8\u7533\u96ea\u541b\u548c\u5979\u56db\u4e2a\u4e2b\u5934\u2026\u2026\u201d\u9ec4\u5929\u738b\u9053\uff1a\u201c\u8fd9\u5927\u6bbf\u4e2d\u67f1\u6881\u4ea4\u9519\uff0c\u7533\u96ea\u541b\u7684\u5341\u51e4\u9f50\u98de\u53d7\u5230\u9650\u5236\uff0c\u90a3\u5c31\u7b49\u4e8e\u5b59\u7334\u5b50\u6ca1\u4e86\u91d1\u7b8d\u68d2\uff0c\u800d\u4e0d\u8d77\u6765\u4e86\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u6211\u4eec\u4e09\u4e2a\u5bf9\u4ed8\u767d\u5929\u5316\u548c\u4e09\u4f4d\u697c\u4e3b\u2026\u2026\u201d\u79cb\u79bb\u82b1\u7b11\u9053\uff1a\u201c\u4fdd\u8bc1\u4e0d\u4f1a\u8ba9\u4f60\u5931\u671b\u3002\u201d \u3000\u3000\u767d\u5929\u5316\u4eae\u51fa\u4e86\u957f\u5251\uff0c\u7b11\u9053\uff1a\u201c\u603b\u5802\u4e3b\uff0c\u8fd9\u4e00\u6b21\u662f\u771f\u6b63\u7684\u5408\u4f5c\uff0c\u751f\u6b7b\u540c\u547d\uff0c\u2026\u2026\u201d \u3000\u3000\u56de\u76ee\u4e00\u987e\u671d\u9633\u3001\u6c34\u6708\u3001\u955c\u82b1\u4e09\u5927\u697c\u4e3b\uff0c\u9053\uff1a\u201c\u4e09\u4f4d\uff0c\u613f\u6218\u613f\u8d70\uff0c\u6089\u51ed\u5c0a\u610f\uff0c\u767d\u67d0\u4eba\u7edd\u4e0d\u52c9\u5f3a\u3002\u201d \u3000\u3000\u4e09\u4f4d\u697c\u4e3b\u7684\u7b54\u590d\u662f\u4eae\u51fa\u4e86\u5175\u5203\u3002 \u3000\u3000\u7533\u96ea\u541b\u5de6\u624b\u63e1\u4f4f\u4e86\u56db\u53ea\u91d1\u51e4\uff0c\u53f3\u624b\u62bd\u51fa\u4e86\u5973\u5a62\u624b\u4e2d\u7684\u957f\u5251\uff0c\u5fc3\u4e2d\u6697\u53eb\u53ef\u60dc\uff0c\u8fd9\u5ea7\u7834\u843d\u7684\u5927\u6bbf\uff0c\u786e\u5b9e\u9650\u5236\u4e86\u5341\u51e4\u9f50\u98de\u7684\u5a01\u529b\u3002 \u3000\u3000\u5979\u6bd5\u751f\u7cbe\u529b\uff0c\u90fd\u6295\u6ce8\u5728\u8fd9\u79cd\u5947\u5f02\u7684\u6697\u5668\u4e4b\u4e0a\uff0c\u786e\u6709\u4e86\u8d85\u51e1\u4eba\u602a\u7684\u6210\u5c31\uff0c\u5c24\u5176\u5728\u5929\u9a6c\u603b\u5802\u5341\u4f59\u5e74\u4e2d\uff0c\u65e0\u6240\u4e8b\u4e8b\uff0c\u6bcf\u65e5\u8eb2\u5728\u65e0\u4eba\u6240\u5728\uff0c\u82e6\u7ec3\u5341\u51e4\u9f50\u98de\uff0c\u6280\u827a\u4e4b\u60c5\uff0c\u5df2\u5165\u5316\u5883\uff0c\u53ea\u8981\u80fd\u8ba9\u5341\u51e4\u9f50\u98de\uff0c\u5979\u76f8\u4fe1\u518d\u5f3a\u5927\u7684\u654c\u4eba\uff0c\u4e5f\u4e0d\u80fd\u6297\u62d2\uff0c\u90a3\u950b\u5229\u7684\u5c16\u5634\u3001\u7ffc\u5203\uff0c\u53ef\u80fd\u4f24\u4eba\uff0c\u85cf\u4e8e\u8179\u5185\u7684\u6bd2\u9488\u66f4\u662f\u81f4\u547d\u7684\u5229\u5668\u3002 \u3000\u3000\u7533\u96ea\u541b\u4ee5\u4e00\u4e2a\u7f8e\u8c8c\u5c0f\u5987\u4eba\uff0c\u4e89\u5f97\u4e86\u6c5f\u6e56\u4e0a\u4e00\u5e2d\u4e4b\u5730\uff0c\u5c31\u662f\u51ed\u85c9\u5200\u64cd\u7eb5\u5341\u51e4\u9f50\u98de\u7684\u6280\u5de7\uff0c\u4f7f\u5b83\u80fd\u5f62\u5982\u6d3b\u7269\uff0c\u76d8\u65cb\u4e8e\u7a7a\uff0c\u4f7f\u654c\u4eba\u60ca\u9707\u8d25\u9003\u3002 \u3000\u3000\u590f\u5929\u540c\u62d4\u5200\u51fa\u9798\uff0c\u53cc\u76ee\u4e2d\u7ea2\u5149\u66f4\u70c8\u3002 \u3000\u3000\u9ec4\u5929\u738b\u4e5f\u4eae\u51fa\u4e86\u5175\u5203\uff0c\u662f\u4e00\u628a\u56f4\u5728\u8170\u4e2d\u7684\u8f6f\u5251\uff0c\u957f\u8fc7\u56db\u5c3a\u516b\u5bf8\uff0c\u5176\u8584\u5982\u7eb8\uff0c\u4f46\u7cbe\u8292\u6d41\u52a8\uff0c\u663e\u89c1\u5341\u5206\u950b\u5229\u3002 \u3000\u3000\u8f6f\u5251\u5728\u624b\uff0c\u7adf\u7136\u4f38\u76f4\uff0c\u53ea\u770b\u8fd9\u4e00\u80a1\u5f79\u5251\u7684\u5185\u529b\uff0c\u5df2\u77e5\u5176\u5185\u529f\u7684\u6df1\u539a\u3002 \u3000\u3000\u5de6\u624b\u662f\u4e00\u628a\u5f62\u5982\u534a\u6708\u7684\u77ed\u5200\uff0c\u53ea\u770b\u8fd9\u4e24\u4ef6\u5175\u5203\uff0c\u5c31\u4e0d\u96be\u60f3\u5230\u4ed6\u6740\u6cd5\u7684\u5947\u5f02\uff0c\u51cc\u5389\u3002 \u3000\u3000\u53cc\u65b9\u8bdd\u8bf4\u7684\u90fd\u5f88\u8f7b\u677e\uff0c\u4f46\u5185\u5fc3\u4e4b\u4e2d\uff0c\u53ef\u662f\u660e\u767d\u7684\u5f88\uff0c\u5927\u5bb6\u90fd\u662f\u6c5f\u6e56\u4e00\u5145\u9ad8\u624b\u4e2d\u7684\u9876\u5c16\u4eba\u7269\uff0c\u8c01\u90fd\u6709\u7edd\u6d3b\u5728\u8eab\uff0c\u7a0d\u4e00\u5927\u610f\uff0c\u5c31\u4f1a\u751f\u6b7b\u6b8a\u9014\u3002 \u3000\u3000\u6240\u4ee5\uff0c\u5929\u540e\u5bab\u7684\u4e09\u4f4d\u5bab\u4e3b\uff0c\u4e5f\u4eae\u51fa\u4e86\u5175\u5203\u3002 \u3000\u3000\u662f\u4e09\u79cd\u6781\u5c11\u89c1\u5230\u7684\u5947\u95e8\u5175\u5203\uff0c\u5176\u5f62\u5982\u8f6e\uff0c\u4e2d\u6709\u6a2a\u628a\uff0c\u4e0d\u7528\u65f6\uff0c\u53ef\u4ee5\u6298\u5408\u8d77\u6765\uff0c\u53d6\u51fa\u5bf9\u654c\u65f6\uff0c\u53ea\u8981\u7528\u529b\u4e00\u6296\uff0c\u91d1\u94c1\u54cd\u52a8\u4e2d\uff0c\u7acb\u523b\u5f20\u5f00\uff0c\u624b\u5728\u8f6e\u4e2d\uff0c\u8f6e\u9762\u5374\u6709\u4e5d\u67c4\u5229\u5203\uff0c\u4e2d\u5203\u6700\u957f\u8db3\u8db3\u6709\u4e00\u5c3a\u4e94\u5bf8\u3002 \u3000\u3000\u53e6\u5916\u516b\u5203\uff0c\u90fd\u5728\u4e5d\u5bf8\u5de6\u53f3\uff0c\u6709\u5982\u98df\u8840\u4e4b\u9f7f\uff0c\u7ed9\u4eba\u4e00\u79cd\u5996\u5f02\u6050\u6016\u7684\u611f\u89c9\u3002 \u3000\u3000\u8fd9\u4e48\u4e00\u4e2a\u602a\u5f02\u7684\u5175\u5203\uff0c\u5f53\u7136\u6709\u7279\u5f02\u7684\u7528\u6cd5\uff0c\u62ff\u5728\u4e09\u4e2a\u7f8e\u5982\u6625\u82b1\u7684\u5973\u4eba\u624b\u4e2d\uff0c\u770b\u4e0a\u53bb\uff0c\u5f88\u4e0d\u8c03\u548c\u3002 \u3000\u3000\u79cb\u79bb\u82b1\u5ae3\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6c5f\u6e56\u4e0a\u4ece\u672a\u51fa\u73b0\u8fdb\u7684\u5175\u5203\uff0c\u5c0f\u59b9\u5c31\u505a\u6b21\u597d\u4eba\uff0c\u5148\u5411\u5404\u4f4d\u8bf4\u660e\u4e00\u4e0b\uff0c\u8fd9\u5175\u5203\u79f0\u4e4b\u8c13\u8f6e\u5200\uff0c\u7531\u5de7\u5320\u7528\u7cbe\u94a2\u6253\u9020\u800c\u6210\uff0c\u8fd9\u5175\u5203\u53d8\u5316\u8be1\u5f02\uff0c\u5c0f\u59b9\u53ea\u8bf4\u4e00\u79cd\uff0c\u706b\u8d77\u6765\uff0c\u5b83\u4f1a\u98de\u51fa\u4f24\u4eba\uff0c\u5404\u4f4d\u53ef\u8981\u5c0f\u5fc3\u4e86\u3002\u201d \u3000\u3000\u8bf4\u660e\u8f6e\u5200\u4e4b\u5a01\uff0c\u4e5f\u542b\u6709\u5148\u58f0\u593a\u4eba\u4e4b\u52bf\u3002 \u3000\u3000\u767d\u5929\u5316\u7684\u56db\u4e2a\u5251\u7ae5\uff0c\u6392\u6210\u4e86\u4e00\u4e2a\u602a\u5f02\u7684\u67b6\u52bf\uff0c\u56db\u4e2a\u4eba\u90fd\u534a\u4f19\u8eab\u8eaf\uff0c\u624b\u4e2d\u77ed\u5251\u524d\u6307\uff0c\u5728\u767d\u5929\u5316\u8eab\u540e\u4e09\u5c3a\u5de6\u53f3\u5904\uff0c\u4f5c\u52bf\u6b32\u6251\u3002 \u3000\u3000\u4e09\u4f4d\u697c\u4e3b\u4e5f\u6765\u91c7\u8054\u624b\u62d2\u654c\u7684\u7ec4\u5408\uff0c\u800c\u5404\u6210\u4e00\u4e2a\u62d2\u654c\u6001\u52bf\uff0c\u4e09\u5927\u697c\u4e3b\u5404\u6709\u4e8c\u4e2a\u4ece\u4eba\uff0c\u5206\u7ad9\u4e24\u4fa7\uff0c\u81ea\u6210\u8fce\u6218\u4f53\u7cfb\u3002 \u3000\u3000\u56db\u5927\u5929\u9e45\u5168\u7a7f\u7740\u767d\u8272\u957f\u886b\uff0c\u5206\u522b\u5904\u5728\u8eab\u4e0a\u7684\u8170\u5e26\uff0c\u4e09\u5bf8\u5bbd\u7684\u8170\u5e26\uff0c\u5206\u6210\u4e86\u7ea2\u3001\u84dd\u3001\u9ed1\u3001\u9ec4\uff0c\u7d27\u675f\u67f3\u8170\uff0c\u7a81\u51fa\u4e86\u8eab\u6750\u4e4b\u7f8e\uff0c\u770b\u4e0a\u53bb\u4e5f\u5e72\u51c0\u4fd0\u843d\uff0c\u56db\u4e2a\u5929\u9e45\u7684\u5bf9\u8c61\u662f\u7533\u96ea\u541b\u3002 \u3000\u3000\u6885\u82b1\u7d27\u508d\u5728\u7533\u96ea\u541b\u7684\u53f3\u4fa7\uff0c\u53e6\u5916\u4e09\u5a62\u5374\u5e76\u6392\u800c\u7acb\uff0c\u9762\u5bf9\u4e1c\u65b9\uff0c\u770b\u6837\u5b50\u662f\u51c6\u5907\u5408\u4e09\u4eba\u4e4b\u529b\uff0c\u6321\u4f4f\u4e00\u4e2a\u5929\u9e45\u3002 \u3000\u3000\u5200\u5df2\u5728\u624b\uff0c\u9635\u5df2\u5e03\u6210\uff0c\u4e00\u573a\u60e8\u70c8\u7684\u640f\u6740\uff0c\u968f\u65f6\u53ef\u4ee5\u5c55\u5f00\u3002 \u3000\u3000\u8fd9\u5ea7\u8352\u5e99\u7a17\u6bbf\uff0c\u539f\u672c\u5f88\u5927\uff0c\u4f46\u4e8c\u4e09\u5341\u4e2a\u62c9\u5f00\u67b6\u52bf\uff0c\u7acb\u523b\u611f\u89c9\u5230\u5730\u65b9\u592a\u5c0f\u4e86\uff0c\u8fd9\u7b49\u6df7\u6218\u8d77\u6765\uff0c\u5200\u5251\u6a2a\u98de\uff0c\u654c\u6211\u4e4b\u95f4\u7684\u56de\u65cb\u7a7a\u95f4\uff0c\u6210\u4e86\u5f88\u5927\u7684\u95ee\u9898\uff1f\u4e00\u4e2a\u4e0d\u597d\uff0c\u53ef\u80fd\u4f1a\u4f24\u5230\u4e86\u81ea\u5df1\u7684\u4eba\u3002 \u3000\u3000\u9ec4\u5929\u738b\u624b\u4e2d\u56db\u5c3a\u516b\u5bf8\u7684\u8f6f\u5251\uff0c\u66f4\u6709\u7740\u96be\u4ee5\u53d1\u6325\u4e4b\u611f\u3002 \u3000\u3000\u201c\u590f\u5929\u540c\uff0c\u4f60\u60f3\u5355\u6c40\u72ec\u6597\uff0c\u54b1\u4eec\u5c31\u5230\u5bfa\u5916\u4e00\u51b3\u2026\u2026\u201d\u9ec4\u5929\u738b\u9053\uff1a\u201c\u5927\u5bb6\u4e5f\u597d\u653e\u624b\u65bd\u4e3a\uff1f\u201d \u3000\u3000\u201c\u603b\u5802\u4e3b\uff0c\u4e0d\u8981\u4e0a\u5f53\uff0c\u4ed6\u4eec\u6709\u5907\u800c\u6765\uff0c\u53ef\u80fd\u65e9\u5df2\u5728\u5e99\u5916\u8bbe\u4e0b\u4e86\u57cb\u4f0f\u2026\u2026\u201d\u767d\u5929\u5316\u9053\uff1a \u3000\u3000\u201c\u5927\u6bbf\u5185\u867d\u7136\u5c0f\u4e86\u4e00\u4e9b\uff0c\u4e5f\u6b63\u597d\u53ef\u4ee5\u963b\u788d\u4e86\u4ed6\u4eec\u7684\u591a\u4eba\u56f4\u653b\u7684\u8ba1\u5212\u3002\u201d \u3000\u3000\u7533\u96ea\u541b\u5012\u662f\u522b\u6709\u60f3\u6cd5\uff0c\u5341\u51e4\u9f50\u98de\u7684\u5a01\u529b\uff0c\u662f\u8981\u5341\u51e4\u5168\u51fa\uff0c\u5a01\u529b\u66b4\u589e\u5230\u83ab\u53ef\u5fa1\u4e4b\u7684\u5883\u754c\uff0c\u5c11\u4e00\u98ce\u5a01\u529b\u5c31\u51cf\u5c11\u5f88\u591a\u3002 \u3000\u3000\u8fd9\u5ea7\u5927\u6bbf\uff0c\u80fd\u51fa\u56db\u51e4\uff0c\u5c31\u7b97\u5f88\u4e0d\u9519\u4e86\u3002 \u3000\u3000\u5f53\u4e0b\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5230\u5bfa\u5916\u4e00\u51b3\u751f\u6b7b\uff0c\u672a\u5c1d\u4e0d\u53ef\uff0c\u4f46\u8981\u53bb\u5927\u5bb6\u53bb\uff01\u590f\u5929\u540c\u4e00\u4e2a\u4eba\uff0c\u4e0d\u4f1a\u7b54\u5141\u3002\u201d \u3000\u3000\u201c\u8d8a\u4fce\u4ee3\u5e96\u4e86\uff0c\u771f\u662f\u604b\u5978\u60c5\u70ed\u554a\uff01\u201d\u9ec4\u5929\u738b\u5927\u7b11\u9053\uff1a\u201c\u7533\u96ea\u541b\uff0c\u4f60\u5927\u6982\u5df2\u7ecf\u5fd8\u4e86\u540c\u662f\u6740\u6b7b\u91d1\u4e39\u4e66\u751f\u7684\u51f6\u624b\uff0c\u660e\u660e\u5df2\u53db\u79bb\u964d\u654c\uff0c\u600e\u4e48\uff1f \u3000\u3000\u5df2\u5230\u751f\u6b7b\u5173\u5934\uff0c\u53c8\u6709\u4e9b\u65e7\u60c5\u590d\u70bd\uff0c\u96be\u820d\u96be\u5206\u4e86\uff1f\u201d \u3000\u3000\u7533\u96ea\u541b\u7684\u6012\u706b\u70e7\u7ea2\u4e86\u53cc\u988a\uff0c\u631f\u5e26\u7740\u5fc3\u4e2d\u4e00\u4efd\u7f9e\u6127\uff0c\u5341\u5206\u6fc0\u52a8\uff0c\u4f46\u4ecd\u7136\u5fcd\u8010\u4f4f\u6ca1\u6709\u51fa\u624b\u3002 \u3000\u3000\u5979\u4e5f\u662f\u8001\u6c5f\u6e56\u4e86\uff0c\u65e9\u5df2\u5386\u7ec3\u4e34\u654c\u65f6\u7684\u8010\u6027\uff0c\u8fd9\u7b49\u9762\u5bf9\u5f3a\u654c\u7684\u65f6\u523b\uff0c\u614c\u6025\u4e0d\u5f97\uff0c\u4e00\u6025\u5c31\u4e71\u4e86\u7ae0\u6cd5\uff0c\u8fde\u672c\u8eab\u7684\u6b66\u529f\uff0c\u4e5f\u8981\u5927\u6253\u6298\u6263\uff0c\u4f46\u53c8\u4e0d\u77e5\u5982\u4f55\u51fa\u53e3\u53cd\u51fb\uff0c\u6fc0\u52a8\u7684\u5168\u8eab\u53d1\u6296\u3002 \u3000\u3000\u4e5d\u5934\u9e1f\u5201\u9e4f\u5f00\u53e3\u4e86\uff0c\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u9ec4\u5929\u738b\uff0c\u7533\u59d1\u5a18\u4e0d\u7406\u4f60\uff0c\u662f\u5979\u5fe0\u539a\uff0c\u8bf4\u4e0d\u51fa\u8ba9\u4f60\u4e00\u5934\u649e\u6b7b\u7684\u96be\u542c\u8bdd\u3002 \u3000\u3000\u5201\u67d0\u4eba\u53ef\u4e0d\u79d8\u8fd9\u4e2a\uff0c\u4f60\u62ff\u8001\u5a46\u6362\u5f97\u4e2a\u5929\u738b\u95e8\u4e3b\u4e4b\u4f4d\uff0c\u770b\u8d77\u6765\uff0c\u8fd8\u6709\u70b9\u6cbe\u6cbe\u81ea\u559c\uff0c\u4f60\u2026\u2026\u201d \u3000\u3000\u5ffd\u89c1\u5bd2\u8292\u4e00\u95ea\uff0c\u9ec4\u5929\u738b\u624b\u4e2d\u7684\u957f\u5251\uff0c\u75be\u5982\u7535\u63a3\uff0c\u523a\u5411\u5201\u9e4f\u7684\u54bd\u5589\uff0c\u5feb\u901f\u7684\u4f7f\u5201\u9e4f\u6765\u4e0d\u53ca\u62d4\u5200\u62d2\u6321\u3002 \u3000\u3000\u8bdd\u5b9e\u5728\u592a\u96be\u542c\u4e86\uff0c\u9ec4\u5929\u738b\u542c\u4e0d\u4e0b\u53bb\u3002 \u3000\u3000\u7533\u96ea\u541b\u7adf\u7136\u4e5f\u6765\u4e0d\u53ca\u51fa\u5251\u5c01\u67b6\u3002 \u3000\u3000\u4f46\u65e9\u5df2\u5168\u795e\u6212\u5907\u7684\u590f\u5929\u540c\uff0c\u5200\u51fa\u5982\u98ce\uff0c\u6321\u4f4f\u4e00\u5251\u3002 \u3000\u3000\u8f6f\u5251\u867d\u88ab\u5c01\u5f00\uff0c\u4f46\u9ec4\u5929\u738b\u5de6\u624b\u7684\u6708\u5f62\u5f2f\u5200\uff0c\u5374\u5df2\u5230\u4e86\u590f\u5929\u540c\u7684\u9762\u524d\u3002 \u3000\u3000\u8fd9\u4e00\u5200\u6765\u52bf\u4e4b\u5feb\uff0c\u7b80\u76f4\u532a\u5937\u6240\u601d\uff0c\u590f\u5929\u540c\u7f29\u8eab\u66b4\u9000\uff0c\u80f8\u524d\u8863\u670d\uff0c\u4ecd\u88ab\u5212\u7834\uff0c\u51e0\u4e4e\u4f24\u53ca\u76ae\u8089\u3002 \u3000\u3000\u590f\u5929\u540c\u5446\u4f4f\u4e86\u3002 \u3000\u3000\u4f46\u4ed6\u7ec8\u662f\u4e45\u7ecf\u5927\u654c\u7684\u4eba\uff0c\u60ca\u800c\u4e0d\u4e71\uff0c\u53cc\u76ee\u76ef\u6ce8\u5728\u9ec4\u5929\u738b\u7684\u5de6\u624b\u6708\u7259\u5200\u77a7\u4e86\u4e00\u9635\uff0c\u604d\u7136\u800c\u609f\uff0c\u9053\uff1a\u201c\u5200\u4e0a\u6709\u4e1d\u7d22\uff0c\u52ff\u602a\u4e4e\u6765\u7684\u5982\u6b64\u4e4b\u5feb\u4e86\u3002\u201d \u3000\u3000\u201c\u5c31\u7b97\u4f60\u77a7\u51fa\u6765\u4e86\uff0c\u53c8\u80fd\u600e\u6837\uff0c\u2026\u2026\u201d\u9ec4\u5929\u738b\u9053\uff1a\u201c\u8981\u80fd\u907f\u5f97\u5f00\u624d\u80fd\u4fdd\u547d\u3002\u201d\u8f6f\u5251\u7b14\u76f4\u523a\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u590f\u5929\u540c\u5200\u5149\u6492\u51fa\u4e00\u7247\u5149\u8292\uff0c\u5c01\u5f00\u8f6f\u5251\uff0c\u76f4\u6b3a\u800c\u4e0a\u3002 \u3000\u3000\u9ec4\u5929\u738b\u624b\u4e2d\u8f6f\u5251\u592a\u957f\uff0c\u5982\u4e0d\u80fd\u6b3a\u8fd1\u8eab\u53bb\u640f\u6740\uff0c\u53ea\u5728\u5251\u5149\u5916\u9762\u5c01\u67b6\uff0c\u90a3\u5c31\u6ca1\u6709\u4f24\u654c\u673a\u4f1a\uff0c\u5168\u662f\u6328\u6253\u7684\u4efd\u4e86\u3002 \u3000\u3000\u8fd1\u8eab\u4e09\u5c3a\uff0c\u9ec4\u5929\u738b\u7684\u957f\u5251\uff0c\u65e0\u6cd5\u65bd\u5c55\u4e86\uff0c\u4f46\u4ed6\u5de6\u624b\u8fd8\u6709\u6708\u7259\u5200\uff0c\u5c31\u662f\u90a3\u628a\u77ed\u5200\uff0c\u5c01\u4f4f\u4e86\u590f\u5929\u540c\u7684\u653b\u52bf\u3002 \u3000\u3000\u9ec4\u5929\u738b\u7684\u56db\u5c3a\u516b\u5bf8\u8f6f\u5251\u3001\u672c\u8fd8\u6709\u5f88\u591a\u7684\u7cbe\u5999\u53d8\u5316\uff0c\u53ef\u60dc\u5927\u6bbf\u4e2d\u4eba\u592a\u591a\u4e86\uff0c\u56db\u5927\u5929\u9e45\u8ddd\u79bb\u6700\u8fd1\uff0c\u6709\u4e9b\u7edd\u5999\u62db\u5f0f\uff0c\u65e0\u6cd5\u65bd\u5c55\uff0c\u6015\u4f1a\u4f24\u5230\u81ea\u5df1\u4eba\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u5de6\u624b\u77ed\u5200\uff0c\u4e5f\u5c31\u591f\u4e86\u3002 \u3000\u3000\u590f\u5929\u540c\u5200\u5149\u5982\u5e55\uff0c\u5168\u529b\u5288\u65a9\uff0c\u9ec4\u5929\u738b\u4ecd\u80fd\u4ece\u5bb9\u5e94\u4ed8\u3002 \u3000\u3000\u4ed6\u53f3\u624b\u62d6\u7740\u957f\u5251\uff0c\u4e0d\u4f46\u65e0\u6cd5\u8ba9\u5200\u5251\u914d\u5408\u65bd\u5c55\uff0c\u53cd\u6210\u4e86\u6781\u5927\u7684\u7d2f\u8d58\uff0c\u4f46\u590f\u5929\u540c\u731b\u70c8\u7684\u653b\u52bf\uff0c\u4e5f\u65e0\u6cd5\u4f24\u5f97\u4e86\u4ed6\uff0c\u5bf9\u6bd4\u4e4b\u4e0b\uff0c\u4e24\u4eba\u7684\u6b66\u529f\u663e\u6709\u5dee\u8ddd\uff0c\u9ec4\u5929\u738b\u5f3a\u4e0a\u4e09\u5206\u3002 \u3000\u3000\u4f46\u9ec4\u5929\u738b\u4e5f\u65e0\u6cd5\u53cd\u5b88\u4e3a\u653b\uff0c\u590f\u5929\u540c\u72c2\u98ce\u66b4\u96e8\u822c\u7684\u5200\u52bf\uff0c\u8fde\u7ef5\u4e0d\u7edd\uff0c\u5b8c\u5168\u662f\u4e00\u526f\u62fc\u547d\u67b6\u52bf\u3002 \u3000\u3000\u56db\u5927\u5929\u9e45\u770b\u4e86\u4e00\u9635\uff0c\u7a81\u7136\u5408\u56f4\u800c\u4e0a\uff0c\u653b\u5411\u7533\u96ea\u541b\u3002 \u3000\u3000\u7533\u96ea\u541b\u4e09\u4e2a\u4ece\u5a62\uff0c\u65e9\u5df2\u5ba1\u5ea6\u597d\u654c\u6211\u5f62\u52bf\uff0c\u4e09\u5251\u5e76\u80a9\u800c\u4e0a\uff0c\u63a5\u4e0b\u4e00\u4e2a\u5929\u9e45\uff0c\u6885\u82b1\u548c\u7533\u96ea\u541b\u8054\u624b\u63a5\u4e0b\u53e6\u5916\u4e09\u4e2a\u3002 \u3000\u3000\u201c\u5c31\u8fd9\u6837\u6253\u8d77\u6765\u4e86\uff1f\u201d\u79cb\u79bb\u82b1\u7b11\u9053\uff1a\u201c\u4e24\u4f4d\u59b9\u5b50\uff0c\u54b1\u4eec\u4e5f\u4e0a\u5427\uff01\u6211\u5bf9\u4ed8\u767d\u5929\u5316\uff0c\u4f60\u4eec\u8054\u624b\u6297\u62d2\u4e09\u5927\u697c\u4e3b\uff0c\u4e0b\u72e0\u624b\u7ed9\u6211\u6740\uff01\u201d \u3000\u3000\u5979\u7f8e\u8273\u81f3\u6781\uff0c\u5374\u5fc3\u5982\u86c7\u874e\u3002 \u3000\u3000\u4e8c\u5bab\u4e3b\u3001\u4e09\u5bab\u4e3b\u5e94\u58f0\u800c\u4e0a\uff0c\u5206\u5411\u4e09\u5927\u697c\u4e3b\u6251\u53bb\u3002 \u3000\u3000\u5bf9\u79cb\u79bb\u82b1\u767d\u5929\u5316\u662f\u95fb\u540d\u4e45\u77e3\uff01\u4e00\u70b9\u4e5f\u4e0d\u6562\u5927\u610f\uff0c\u5168\u795e\u8d2f\u6ce8\uff0c\u6a2a\u5251\u4ee5\u5f85\u3002 \u3000\u3000\u79cb\u79bb\u82b1\u67f3\u8170\u6b3e\u6446\uff0c\u8d9f\u7684\u662f\u6625\u98ce\u4fcf\u6b65\uff0c\u8138\u4e0a\u7b11\u610f\u8ff7\u4eba\uff0c\u6a31\u5507\u542f\u529d\uff0c\u6e05\u97f3\u5a49\u8f6c\uff0c\u9053\uff1a\u201c\u767d\u5929\u5316\uff0c\u542c\u8bf4\u4f60\u662f\u72b6\u5143\u4e4b\u624d\uff0c\u6587\u6b66\u517c\u8d28\u3001\u4e0d\u8fd1\u5973\u8272\uff0c\u5374\u517b\u4e86\u56db\u4e2a\u4fca\u7ae5\uff0c\u53ef\u662f\u6709\u65ad\u8872\u4e4b\u8150\uff1f\u2026\u2026\u201d \u3000\u3000\u201c\u80e1\u8bf4\u516b\u9053\uff0c\u771f\u662f\u4e00\u4e2a\u539a\u989c\u65e0\u803b\u7684\u8361\u5987\u2026\u2026\u201d \u3000\u3000\u201c\u4f60\u543c\u4ec0\u4e48\uff1f\u2026\u2026\u201d\u79cb\u79bb\u82b1\u8138\u4e0a\u63a0\u8fc7\u4e00\u62b9\u6740\u673a\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4ed6\u4eec\u56db\u4e2a\u6446\u7684\u67b6\u5f0f\u4e0d\u9519\uff0c\u4e0d\u77e5\u9053\u7ba1\u4e0d\u7ba1\u7528\uff1f\u201d \u3000\u3000\u4f46\u89c1\u5bd2\u8292\u4e00\u95ea\uff0c\u4e00\u4e2a\u7ae5\u5b50\u5df2\u4eba\u5251\u5e76\u8d77\uff0c\u76f4\u5c04\u8fc7\u6765\u3002 \u3000\u3000\u79cb\u79bb\u82b1\u626c\u8d77\u8f6e\u5200\uff0c\u6321\u5f00\u4e00\u5251\u3002 \u3000\u3000\u4f46\u7b2c\u4e8c\u4e2a\u5251\u7ae5\u5df2\u8ddf\u8e2a\u800c\u5230\u3002 \u3000\u3000\u56db\u4e2a\u5251\u7ae5\uff0c\u6b64\u843d\u5f7c\u8d77\uff0c\u8fde\u653b\u4e09\u8f6e\u3002 \u3000\u3000\u56db\u5251\u7ae5\u653b\u52bf\u5bc6\u5982\u8fde\u73e0\uff0c\u767d\u5929\u5316\u60f3\u559d\u6b62\u90fd\u6765\u4e0d\u53ca\u3002 \u3000\u3000\u79cb\u79bb\u82b1\u5df2\u770b\u6e05\u695a\u56db\u4e2a\u5251\u7ae5\u7684\u8fde\u73af\u653b\u654c\u7684\u8eab\u6cd5\uff0c\u6de1\u6de1\u4e00\u7b11\uff0c\u8f6e\u5200\u7a81\u7136\u8f6c\u5982\u98ce\u8f66\u3002 \u3000\u3000\u4f46\u95fb\u60e8\u53eb\u58f0\u8d77\uff0c\u9c9c\u8840\u6e85\u98de\uff0c\u56db\u4e2a\u5c0f\u547d\uff0c\u77ac\u606f\u800c\u6b81\u3002 \u3000\u3000\u6700\u53ef\u60b2\u7684\u662f\uff0c\u727a\u7272\u4e86\u56db\u4e2a\u5251\u91cd\uff0c\u767d\u5929\u5316\u4ecd\u6ca1\u6709\u770b\u6e05\u695a\u79cb\u79bb\u82b1\u7684\u6740\u4eba\u624b\u6cd5\u3002 \u3000\u3000\u8fd9\u8f6e\u5200\u5df2\u751a\u602a\u5f02\uff0c\u4f46\u6740\u4eba\u7684\u624b\u6cd5\u66f4\u662f\u5947\u8be1\u83ab\u6d4b\u3002 \u3000\u3000\u201c\u770b\u6e05\u695a\u6ca1\u6709\uff1f\u201d\u79cb\u79bb\u82b1\u5df2\u7aa5\u51fa\u767d\u5929\u5316\u7684\u7528\u5fc3\uff0c\u8981\u56db\u4e2a\u5251\u7ae5\u8fde\u653b\uff0c\u662f\u60f3\u627e\u51fa\u8f6e\u5200\u62d2\u654c\u65f6\u7684\u7834\u7efd\u3002 \u3000\u3000\u201c\u72e0\u3001\u6bd2\u3001\u9634\u3001\u8be1\uff0c\u517c\u800c\u6709\u4e4b\u3002\u201d\u767d\u5929\u5316\u5fc3\u75bc\u56db\u4e2a\u5251\u7ae5\u4e4b\u6b7b\uff0c\u5fc3\u60c5\u6fc0\u52a8\u83ab\u540d\uff0c\u51b7\u51b7\u5730\u8bf4\u9053\uff1a\u201c\u8001\u592b\u62da\u6b7b\u8f6e\u5200\u4e4b\u4e0b\uff0c\u4e5f\u8981\u62d6\u4f60\u57ab\u80cc\uff0c\u54b1\u4eec\u5c31\u5e76\u9aa8\u8352\u5e99\u5427\uff01\u201d \u3000\u3000\u201c\u4f60\u60f3\u7684\u7f8e\u5440\uff01\u201d\u79cb\u79bb\u82b1\u6e29\u67d4\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e5f\u4e0d\u62ff\u955c\u5b50\u7167\u7167\u770b\u4f60\u90a3\u526f\u5fb7\u884c\uff0c\u4f60\u5c31\u81ea\u5df1\u6b7b\u5427\uff01\u6211\u53ef\u4e0d\u60f3\u966a\u4f60\u4e00\u628a\u8001\u9aa8\u5934\u3002\u201d \u3000\u3000\u5979\u7b11\u7684\u5a07\u5a9a\u52a8\u4eba\uff0c\u4f46\u5374\u8a00\u8bcd\u5982\u5200\uff0c\u6345\u4eba\u5fc3\u8179\u3002 \u3000\u3000\u767d\u5929\u5316\u9053\uff1a\u201c\u90a3\u5c31\u7531\u4e0d\u5f97\u4f60\u4e86\uff01\u201d\u957f\u5251\u7f13\u7f13\u523a\u51fa\u3002 \u3000\u3000\u5251\u52bf\u867d\u6162\uff0c\u4f46\u5251\u5c16\u98a4\u52a8\uff0c\u7f69\u4f4f\u4e86\u79cb\u79bb\u82b1\u4e03\u5904\u5927\u7a74\u8981\u5bb3\uff0c\u79cb\u79bb\u82b1\u77a7\u4e0d\u51fa\u8fd9\u4e00\u5251\u653b\u5411\u4f55\u5904\u4e86\uff1f \u3000\u3000\u4f46\u5979\u624b\u4e2d\u8f6e\u5200\u7684\u7279\u8272\uff0c\u6709\u5982\u4e00\u4e2a\u5c0f\u578b\u7684\u76fe\u724c\uff0c\u9632\u5b88\u9762\u5f88\u5927\uff0c\u63d0\u6321\u80f8\u524d\uff0c\u6539\u91c7\u5b88\u52bf\uff0c\u4e00\u53cc\u5999\u76ee\uff0c\u5374\u76ef\u6ce8\u5728\u767d\u5929\u5316\u7684\u5251\u5c16\u4e4b\uff2c\uff0c\u5168\u529b\u6212\u5907\u5e94\u53d8\u3002 \u3000\u3000\u8fd9\u771f\u662f\u4e00\u628a\u8bbe\u8ba1\u7cbe\u7edd\u7684\u5175\u5203\uff0c\u5b83\u7684\u653b\u52bf\u51f6\u5389\uff0c\u5e45\u9762\u5f88\u5927\uff0c\u5148\u58f0\u593a\u4eba\uff0c\u4e0d\u6613\u5c01\u6321\uff0c\u9632\u5b88\u65f6\u53c8\u4f3c\u76fe\u724c\u62a4\u8eab\uff0c\u4f7f\u654c\u4eba\u6709\u7740\u65e0\u4ece\u4e0b\u624b\u4e4b\u611f\u3002 \u3000\u3000\u73b0\u5728\uff0c\u767d\u5929\u5316\u5c31\u6709\u7740\u8fd9\u79cd\u611f\u89c9\uff0c\u53ea\u89c9\u8f6e\u5bf9\u9632\u62a4\u4e4b\u80fd\uff0c\u65e0\u6240\u4e0d\u5728\uff0c\u7b80\u76f4\u627e\u4e0d\u51fa\u4e00\u70b9\u7834\u7efd\u6765\u3002 \u3000\u3000\u79cb\u79bb\u82b1\u662f\u7cbe\u7ec6\u7684\u4eba\uff0c\u611f\u53d7\u5230\u767d\u5929\u5316\u771f\u5b58\u4e86\u62fc\u547d\u4e4b\u5fc3\uff0c\u53ea\u8981\u4e00\u73b0\u7834\u7efd\uff0c\u4ed6\u5c31\u4f1a\u5168\u529b\u4e00\u51fb\uff0c\u5f88\u53ef\u80fd\u662f\u4e2a\u4e24\u8d25\u4ff1\u4f24\u7684\u7ed3\u679c\uff0c\u5728\u6ca1\u6709\u5b8c\u5168\u628a\u63e1\u4e4b\u4e0b\uff0c\u4e5f\u4e0d\u6562\u8f7b\u6613\u51fa\u624b\uff0c\u6682\u65f6\u6210\u4e86\u4e00\u4e2a\u76f8\u6301\u4e4b\u5c40\u3002 \u3000\u3000\u51e0\u58f0\u5c16\u5389\u7684\u60e8\u53eb\u54cd\u8d77\uff0c\u7533\u96ea\u541b\u4e09\u4e2a\u5973\u5a62\uff0c\u5bf9\u6297\u4e00\u4e2a\u5929\u9e45\uff0c\u4ecd\u662f\u96be\u4ee5\u6297\u67dc\uff0c\u4e00\u5408\u4e4b\u540e\uff0c\u4e24\u6b7b\u4e00\u4f24\u3002 \u3000\u3000\u7533\u96ea\u541b\u6025\u75db\u4ea4\u96c6\uff0c\u6b63\u8981\u6279\u51fa\u5de6\u624b\u56db\u53ea\u91d1\u51e4\uff0c\u6885\u82b1\u5374\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u7533\u59e8\uff0c\u56db\u5927\u5929\u9e45\u4e00\u8054\u624b\u3001\u5e03\u6210\u5408\u51fb\u4e4b\u9635\uff0c\u5c31\u4e0d\u6613\u6740\u5979\u4eec\u4e86\uff0c\u6b64\u523b\u5f62\u52bf\uff0c\u53c8\u4e0d\u5b9c\u65bd\u5c55\u5341\u51e4\u9f50\u98de\uff0c\u4f60\u5c0f\u5fc3\u5e94\u654c\uff0c\u6211\u8981\u4e0b\u6bd2\u624b\u4e86\u3002\u201d \u3000\u3000\u5ffd\u7136\u4e00\u4e2a\u4ef0\u8eab\uff0c\u968f\u7740\u654c\u4eba\u653b\u6765\u7684\u5251\uff0c\u5012\u5367\u4e8e\u5730\u3002 \u3000\u3000\u7533\u96ea\u541b\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u6885\u82b1\uff0c\u4f60\u2026\u2026\u201d \u3000\u3000\u4f46\u89c1\u6885\u82b1\u8d34\u5730\u98de\u65cb\uff0c\u95ea\u5165\u654c\u4eba\u5251\u5149\u4e4b\u4e2d\uff0c\u5168\u8eab\u5982\u86c7\u76d8\u8d77\uff0c\u5251\u52bf\u5374\u4ece\u4e00\u4e2a\u6781\u4e0d\u53ef\u80fd\u7684\u89d2\u5ea6\u4e2d\u653b\u51fa\u3002 \u3000\u3000\u4e00\u5251\u6740\u654c\uff0c\u4e00\u53ea\u5929\u9e45\u524d\u80f8\u4e2d\u5251\uff0c\u7acb\u523b\u4e86\u5e10\u3002 \u3000\u3000\u6885\u82b1\u76d8\u98de\u7684\u8eab\u8eaf\u5e76\u672a\u505c\u4e0b\uff0c\u4eba\u5251\u98de\u8f6c\uff0c\u62db\u62db\u53d6\u547d\uff0c\u5251\u8fc7\u8840\u6e85\uff0c\u4e00\u4e3e\u95f4\uff0c\u6740\u6b7b\u4e86\u56db\u5927\u5929\u9e45\u3002 \u3000\u3000\u7533\u96ea\u541b\u5446\u4f4f\u4e86\u3002 \u3000\u3000\u5979\u4ece\u672a\u89c1\u8fc7\u8fd9\u79cd\u6b66\u529f\uff0c\u8fd9\u662f\u541f\u971c\u4f20\u7ed9\u6885\u82b1\u7684\u4e09\u62db\u6740\u6cd5\uff0c\u662f\u201c\u4f20\u771f\u8bb0\u201d\u4e0a\uff0c\u90aa\u6076\u7684\u6740\u4eba\u624b\u6cd5\u3002 \u3000\u3000\u4f46\u6885\u82b1\u4e5f\u7d2f\u7684\u4e00\u8eab\u6c57\u6c34\uff0c\u8fde\u811a\u6b65\u90fd\u7ad9\u4e0d\u7a33\u4e86\u3002 \u3000\u3000\u4e00\u9053\u5251\u5149\u98de\u6765\uff0c\u7531\u6885\u82b1\u540e\u80cc\u76f4\u900f\u524d\u5fc3\u3002 \u3000\u3000\u662f\u9ec4\u5929\u738b\u7684\u957f\u5251\u3002 \u3000\u3000\u6885\u82b1\u5728\u7b4b\u75b2\u529b\u7aed\u4e2d\uff0c\u81ea\u7136\u662f\u65e0\u80fd\u95ea\u907f\uff0c\u4f55\u51b5\uff0c\u9ec4\u5929\u738b\u7684\u5251\u52bf\u53c8\u6765\u7684\u65e0\u58f0\u65e0\u606f\uff0c\u7533\u96ea\u541b\u4e5f\u6551\u63f4\u4e0d\u53ca\u3002 \u3000\u3000\u9ec4\u5929\u738b\u867d\u662f\u4e00\u5251\u6740\u4e86\u6885\u82b1\uff0c\u4f46\u5374\u5bd2\u610f\u88ad\u5fc3\uff0c\u60ca\u60b8\u4e0d\u5df2\uff0c\u4ed6\u660e\u767d\u56db\u5927\u5929\u9e45\u7684\u6b66\u529f\uff0c\u5c31\u7b97\u81ea\u5df1\u8981\u6740\u56db\u4eba\uff0c\u4e5f\u975e\u6613\u4e8b\u3002 \u3000\u3000\u8fd9\u4e2a\u4e2b\u5934\uff0c\u5374\u80fd\u4e00\u53e3\u6c14\u6482\u5012\u4e86\u56db\u4e2a\uff0c\u4e00\u5bf9\u4e00\u7684\u640f\u6740\uff0c\u81ea\u5df1\u6050\u4e5f\u975e\u5979\u654c\u624b\u4e86\uff0c\u5c01\u5f00\u590f\u5929\u540c\u7684\u5200\u52bf\uff0c\u75be\u9000\u516b\u5c3a\u3002 \u3000\u3000\u4ed6\u548c\u590f\u5929\u540c\u6253\u7684\u6fc0\u70c8\uff0c\u6839\u672c\u672a\u770b\u5230\u6885\u82b1\u6740\u6b7b\u56db\u5927\u5929\u9e45\u624b\u6cd5\uff0c\u770b\u5230\u4e86\uff0c\u4e5f\u8bb8\u4f1a\u66f4\u89c9\u5fc3\u5bd2\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u9664\u4e86\u7533\u96ea\u541b\u5916\uff0c\u90fd\u6ca1\u4eba\u770b\u5230\u6885\u82b1\u662f\u5982\u4f55\u6740\u4e86\u56db\u5927\u5929\u9e45\u3002 \u3000\u3000\u590f\u5929\u540c\u4e5f\u6536\u4f4f\u4e86\u5200\u52bf\uff0c\u6570\u5341\u62db\u62da\u6597\u4e0b\u6765\uff0c\u4ed6\u5df2\u786e\u5b9a\u81ea\u5df1\u6ca1\u6709\u6740\u6b7b\u9ec4\u5929\u738b\u7684\u672c\u9886\u3002 \u3000\u3000\u7533\u96ea\u541b\u4e5f\u770b\u7684\u4e0d\u662f\u771f\u6e05\u695a\uff0c\u53ea\u89c1\u5230\u4e00\u56e2\u98de\u65cb\u7684\u9ed1\u5f71\uff0c\u9b3c\u9b45\u822c\u5728\u8f6c\u52a8\u3001\u56db\u5927\u5929\u9e45\u5c31\u5012\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u8352\u5e99\u7684\u5927\u6bbf\u4e0a\uff0c\u5df2\u6210\u4e86\u89e6\u76ee\u60ca\u5fc3\u7684\u5c60\u573a\uff0c\u4e09\u5927\u697c\u4e3b\u7684\u516d\u4e2a\u4ece\u536b\uff0c\u4e5f\u6b7b\u5728\u4e8c\u5bab\u4e3b\u3001\u4e09\u5bab\u4e3b\u7684\u8f6e\u5200\u4e4b\u4e0b\uff0c\u4e09\u4f4d\u697c\u4e3b\uff0c\u867d\u7136\u6beb\u53d1\u672a\u635f\uff0c\u4f46\u5728\u4e24\u67c4\u602a\u5f02\u7684\u8f6e\u5200\u653b\u51fb\u4e4b\u4e0b\uff0c\u4e5f\u88ab\u8feb\u91c7\u53d6\u5b88\u52bf\u4e86\u3002 \u3000\u3000\u767d\u5929\u5316\u7ec8\u4e8e\u51fa\u624b\uff0c\u653b\u51fa\u4e86\u4e09\u5251\u3002 \u3000\u3000\u79cb\u79bb\u82b1\u4e5f\u627e\u51fa\u8fd8\u51fb\u7684\u7a7a\u9699\uff0c\u4e24\u4eba\u4ea4\u9519\u800c\u8fc7\uff0c\u8f6e\u5200\u957f\u5251\uff0c\u4e92\u51fb\u62a2\u653b\uff0c\u53ea\u662f\u9519\u8eab\u800c\u8fc7\u4e4b\u95f4\uff0c\u54cd\u8d77\u4e86\u5341\u4f59\u58f0\u91d1\u94c1\u4ea4\u9e23\u3002 \u3000\u3000\u767d\u5929\u5316\u5de6\u81c2\u4e3a\u5200\u950b\u5212\u4f24\uff0c\u8840\u67d3\u8863\u8896\u3002 \u3000\u3000\u79cb\u79bb\u82b1\u957f\u53d1\u6563\u98de\uff0c\u62d8\u53d1\u7684\u7389\u7c2a\uff0c\u88ab\u5251\u5149\u626b\u65ad\u3002 \u3000\u3000\u767d\u5929\u5316\u624d\u662f\u5929\u9a6c\u5802\u4e2d\u7b2c\u4e00\u9ad8\u624b\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\u6b66\u529f\u5230\u4e86\u4e00\u5b9a\u7684\u5883\u754c\uff0c\u4f24\u654c\u4e4b\u6cd5\uff1b\u5c31\u8981\u9760\u81ea\u5df1\u7684\u53d8\u5316\u8fd0\u7528\u4e86\uff0c\u5b66\u95ee\u548c\u624d\u6167\u5c31\u6210\u4e86\u6781\u91cd\u8981\u7684\u6761\u4ef6\u3002 \u3000\u3000\u5982\u4f55\u627e\u51fa\u653b\u654c\u7684\u7834\u7efd\u3001\u7a7a\u9699\uff0c\u6210\u4e86\u751f\u6b7b\u7684\u5173\u952e\uff0c\u767d\u5929\u5316\u7684\u5b66\u95ee\u3001\u624d\u6167\uff0c\u662f\u5929\u9a6c\u5802\u4e2d\u6700\u9ad8\u7684\u4e00\u4e2a\u3002 \u3000\u3000\u6563\u53d1\u62c2\u9762\uff0c\u6fc0\u8d77\u4e86\u79cb\u79bb\u82b1\u771f\u6b63\u7684\u6012\u706b\u3001\u6597\u5fd7\uff0c\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u767d\u5929\u5316\u4f60\u9634\u7684\u72e0\u554a\uff0c\u5077\u5b66\u4e86\u6559\u4e3b\u4e0d\u5c11\u6b66\u529f\uff0c\u7f6a\u4e0d\u53ef\u8d66\uff0c\u6211\u4e00\u5b9a\u8981\u6740\u4e86\u4f60\uff01\u201d \u3000\u3000\u201c\u90a3\u5012\u672a\u5fc5\uff1f\u706b\u5230\u7eaf\u5883\u8272\u81ea\u9752\uff0c\u6559\u4e3b\u7684\u6b66\u529f\uff0c\u53ef\u4e0d\u662f\u5929\u4e0b\u7b2c\u4e00\u3002\u201d\u767d\u5929\u5316\u4e00\u53e3\u5426\u8ba4\u3002 \u3000\u3000\u201c\u79cb\u79bb\u82b1\uff0c\u62db\u4eba\u8fdb\u6765\uff0c\u5168\u529b\u56f4\u6740\uff0c\u4eca\u5929\uff0c\u7edd\u4e0d\u80fd\u653e\u8fc7\u4e00\u4e2a\u4eba\uff01\u201d\u9ec4\u5929\u738b\u6709\u4e9b\u754f\u60e7\u4e86\uff0c\u60f3\u5230\u4e86\u53ec\u4ee4\u5c5e\u4e0b\u8fdb\u6765\u52a9\u62f3\u3002 \u3000\u3000\u56db\u5927\u5929\u9e45\u8fd9\u4e00\u6b7b\uff0c\u4f7f\u4ed6\u8b66\u89c9\u5230\u7533\u96ea\u541b\u7684\u5341\u51e4\u9f50\u98de\uff0c\u90a3\u4e2a\u593a\u9b42\u53d6\u547d\u7684\u73a9\u827a\uff0c\u4e00\u65e6\u51fa\u624b\uff0c\u4ed6\u5c06\u662f\u7b2c\u4e00\u4e2a\u76ee\u6807\u3002 \u3000\u3000\u4f46\u95fb\u4e00\u8fde\u4e32\u7684\u60b2\u547c\u60e8\u53eb\uff0c\u5206\u7531\u5927\u6bbf\u5916\u56db\u9762\u4f20\u6765\u3002 \u3000\u3000\u90a3\u4e0d\u662f\u6fc0\u70c8\u7684\u62fc\u6740\uff0c\u800c\u662f\u5f3a\u5f31\u4e0d\u6210\u6bd4\u4f8b\u7684\u5c60\u6740\u3002 \u3000\u3000\u53eb\u58f0\u51c4\u5389\uff0c\u662f\u4e00\u79cd\u751f\u547d\u5c3d\u5904\u7684\u54c0\u568e\u3002 \u3000\u3000\u9ec4\u5929\u738b\u3001\u79cb\u79bb\u82b1\u7b49\uff0c\u90fd\u4e0d\u662f\u7b2c\u4e00\u6b21\u542c\u5230\u8fd9\u79cd\u58f0\u97f3\uff0c\u4ed6\u4eec\u5728\u5c11\u6797\u5bfa\u524d\u542c\u5230\u8fc7\u3002 \u3000\u3000\u6700\u53ef\u6015\u7684\u662f\u90a3\u4e9b\u58f0\u97f3\u542c\u8d77\u6765\u5f88\u591a\u90fd\u6709\u70b9\u719f\u6089\u3002 \u3000\u3000\u662f\u7684\uff0c\u90a3\u662f\u5929\u738b\u95e8\u3001\u5929\u540e\u5bab\u4e2d\u5e26\u6765\u7684\u7cbe\u9510\u5c5e\u4f17\uff0c\u5b88\u5728\u8352\u5e99\u56db\u5468\u7684\u5f1f\u5b50\u3002 \u3000\u3000\u9ec4\u5929\u738b\u548c\u5929\u540e\u5bab\u4e09\u4f4d\u5bab\u4e3b\uff0c\u90fd\u542c\u5f97\u5446\u4f4f\u4e86\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u590f\u5929\u540c\u3001\u767d\u5929\u5316\u3001\u7533\u96ea\u541b\uff0c\u4e5f\u90fd\u542c\u5f97\u5fc3\u4e2d\u76f4\u5192\u5bd2\u6c14\uff0c\u4ee5\u4ed6\u4eec\u7684\u6b66\u529f\u4fee\u4e3a\uff0c\u90fd\u542c\u5f97\u51fa\u6765\uff0c\u90a3\u79cd\u9762\u5bf9\u6b7b\u4ea1\u7684\u65e0\u5948\u547c\u53eb\uff0c\u4e00\u58f0\u8fde\u4e00\u58f0\uff0c\u5982\u4e00\u53ea\u65e0\u5f62\u4e4b\u624b\uff0c\u63ea\u7684\u4eba\u5fc3\u90fd\u7f29\u8d77\u6765\u4e86\u3002 \u3000\u3000\u5927\u6bbf\u4e0a\u7684\u7537\u7537\u5973\u5973\uff0c\u5927\u90fd\u662f\u4e00\u4e16\u82f1\u8c6a\uff0c\u7adf\u90fd\u611f\u67d3\u5230\u6b7b\u4ea1\u6b63\u6b65\u6b65\u63a5\u8fd1\u7684\u6050\u60e7\u3002 \u3000\u3000\u58f0\u97f3\u9759\u4e86\u4e0b\u6765\uff0c\u53ea\u89c1\u4e24\u4e2a\u9752\u5e15\u675f\u53d1\u7684\u767d\u8863\u5c11\u5973\uff0c\u5e76\u80a9\u513f\u51fa\u73b0\u5728\u5927\u6bbf\u95e8\u53e3\uff0c\u6765\u7684\u662f\u90a3\u4e48\u65e0\u58f0\u65e0\u606f\u3002 \u3000\u3000\u662f\u6b27\u9633\u541f\u96ea\u548c\u541f\u971c\u3002 \u3000\u3000\u9c9c\u8840\u67d3\u5f81\u8863\uff0c\u5979\u4eec\u7684\u767d\u886b\u4e0a\u5168\u662f\u8840\u8ff9\uff0c\u624b\u4e2d\u4e8c\u5c3a\u56db\u5bf8\u7684\u5b9d\u5251\uff0c\u4ecd\u5728\u6dcc\u4e0b\u8840\u6ef4\u3002 \u3000\u3000\u4e24\u5f20\u7f8e\u4e3d\u7edd\u4f26\u7684\u5ae9\u8138\u4e0a\uff0c\u8ff7\u8499\u7740\u4e00\u80a1\u5996\u5f02\u7684\u51b7\u5389\uff0c\u5982\u6765\u767d\u4e91\u5929\u98d8\u7f08\u5904\u7684\u4ed9\u5973\uff0c\u6765\u81ea\u4e5d\u5e7d\u6df1\u5904\u7684\u5996\u59ec\uff0c\u65e2\u7f8e\u4e3d\u53c8\u6050\u6016\u3002 \u3000\u3000\u6c5f\u67ab\u3001\u5927\u548c\u5c1a\u3001\u8001\u53eb\u5316\uff0c\u7f13\u7f13\u51fa\u73b0\u5728\u4e24\u4eba\u8eab\u540e\u3002 \u3000\u3000\u8fd9\u4e09\u4eba\u8863\u5c65\u4e0a\u672a\u6cbe\u67d3\u70b9\u6ef4\u8840\u75d5\uff0c\u624b\u4e2d\u4e5f\u6ca1\u63e1\u5175\u5203\uff0c\u597d\u50cf\u6740\u4eba\u7684\u4e8b\u5168\u662f\u4e24\u4e2a\u5c0f\u72d0\u5973\u5e72\u7684\u3002 \u3000\u3000\u4e09\u4e2a\u4eba\u6ca1\u6709\u80dc\u5229\u7684\u7b11\u610f\uff0c\u4e5f\u6ca1\u6709\u8d5e\u8bb8\u4e8c\u5973\u6b66\u529f\u7684\u795e\u60c5\uff0c\u8138\u4e0a\u53cd\u800c\u662f\u4e00\u79cd\u5e26\u70b9\u6101\u8651\u7684\u4e25\u8083\u3002 \u3000\u3000\u201c\u662f\u6885\u82b1\u59d0\u59d0\u2026\u2026\u201d\u541f\u971c\u7a81\u7136\u4e00\u8dc3\u800c\u524d\uff0c\u6276\u8d77\u4e86\u6885\u82b1\u7684\u5c38\u4f53\uff0c\u53cc\u76ee\u4e2d\u6d41\u51fa\u6cea\u6c34\u3002 \u3000\u3000\u541f\u971c\u4eba\u6027\u5c55\u73b0\uff0c\u6cea\u6c34\u51b2\u8d70\u4e86\u6ee1\u8138\u5996\u5f02\u7684\u51b7\u5389\uff0c\u6062\u590d\u4e86\u6e05\u7eaf\u7684\u65e7\u65f6\u5f62\u8c8c\uff0c\u9053\uff1a\u201c\u7533\u59e8\uff0c\u662f\u8c01\u6740\u6b7b\u4e86\u6885\u82b1\u59d0\u59d0\uff1f\u201d \u3000\u3000\u201c\u9ec4\u5929\u738b\u2026\u2026\u201d\u7533\u96ea\u541b\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6885\u82b1\u4e00\u4e3e\u95f4\u6740\u6b7b\u4e86\u56db\u5927\u5929\u9e45\uff0c\u9ec4\u5929\u738b\u5c31\u5728\u6885\u82b1\u5598\u606f\u4e2d\u7a81\u7136\u51fa\u5251\uff0c\u6885\u82b1\u5e94\u53d8\u4e0d\u53ca\u3002\u201d \u3000\u3000\u4e00\u53ea\u624b\u4f38\u4e86\u8fc7\u6765\uff0c\u6162\u6162\u7684\u7531\u541f\u971c\u624b\u4e2d\u63a5\u8fc7\u53bb\u4fae\u82b1\u7684\u5c38\u4f53\uff0c\u7d27\u7d27\u7684\u62e5\u5165\u6000\u4e2d\u3002 \u3000\u3000\u662f\u6c5f\u67ab\uff0c\u5c48\u8dea\u7740\u53f3\u819d\uff0c\u63fd\u5c38\u5165\u6000\uff0c\u5de6\u988a\u7d27\u8d34\u5728\u6885\u82b1\u7684\u53f3\u7751\u4e0a\uff0c\u4ed6\u6ca1\u6709\u6d41\u6cea\uff0c\u4f46\u8138\u4e0a\u7684\u75db\u82e6\uff0c\u5374\u53eb\u4eba\u4e00\u89c1\u94ed\u5fc3\u3002 \u3000\u3000\u5c38\u4f53\u4e0a\u4f59\u6e29\u72b9\u5b58\uff0c\u662f\u6b7b\u4e86\uff0c\u4f46\u4e0d\u592a\u4e45\u3002 \u3000\u3000\u5947\u602a\uff0c\u6885\u82b1\u7d27\u95ed\u7684\u6a31\u5507\uff0c\u7adf\u7136\uff0c\u5fae\u5fae\u5f00\u542f\uff0c\u5e26\u51fa\u4e86\u4e00\u5206\u5b89\u6170\u7684\u7b11\u610f\u3002 \u3000\u3000\u662f\u6885\u82b1\u9634\u7075\u6709\u77e5\uff0c\u8fd8\u662f\u6c5f\u67ab\u62b1\u5f97\u592a\u7d27\uff0c\u7275\u52a8\u4e86\u6885\u82b1\u5fae\u50f5\u7684\u808c\u80a4\u3002 \u3000\u3000\u201c\u6c5f\u5927\u54e5\uff0c\u6885\u82b1\u59d0\u59d0\u7b11\u7684\u597d\u6e29\u67d4\uff01\u201d\u541f\u971c\u62ed\u4e00\u4e0b\u4e0d\u505c\u6eda\u843d\u7684\u6cea\u6c34\uff0c\u9053\uff1a\u201c\u6885\u82b1\u59d0\u59d0\uff0c\u5b89\u606f\u5427\uff01\u6c5f\u5927\u54e5\u597d\u7231\u4f60\uff0c\u597d\u7231\u4f60\uff01\u201d \u3000\u3000\u6c5f\u67ab\u628a\u6885\u82b1\u5c38\u4f53\u653e\u4e0b\uff0c\u8138\u4e0a\u54c0\u4f24\u6df1\u6c89\uff0c\u9053\uff1a\u201c\u4f60\u5c31\u662f\u5929\u540e\u5bab\u7684\u5927\u5bab\u4e3b\u79cb\u79bb\u82b1\uff0c\u6885\u82b1\u7684\u5e08\u7236\uff1f\u201d \u3000\u3000\u201c\u662f\uff01\u6885\u82b1\u662f\u5f88\u806a\u6167\u7684\u4eba\uff0c\u662f\u6211\u5168\u5fc3\u57f9\u517b\u7684\u5f1f\u5b50\u4e4b\u4e00\uff0c\u2026\u2026\u201d\u79cb\u79bb\u82b1\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u5374\u88ab\u4f60\u62a2\u8d70\u4e86\u3002\u201d \u3000\u3000\u201c\u6211\u4eec\u662f\u771f\u60c5\u76f8\u8bb8\u2026\u2026\u201d\u6c5f\u67ab\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4e5f\u8bb8\u4f60\u4e0d\u4f1a\u660e\u767d\uff0c\u770b\u5728\u6885\u82b1\u4efd\u4e0a\uff0c\u6211\u8fd8\u662f\u8d39\u70b9\u53e3\u820c\u544a\u8bc9\u4f60\uff0c\u5979\u4eec\u4e60\u7ec3\u7684\u5a9a\u672f\uff0c\u662f\u4e0b\u4e5d\u6d41\u7684\u6280\u827a\uff0c\u4ee5\u91c7\u8865\u4e3a\u4e3b\uff0c\u65e8\u5728\u5bb3\u4eba\uff0c\u5f53\u7136\u4e0d\u4f1a\u6295\u5165\u60c5\u4e49\uff0c\u4f46\u2018\u60d1\u5fc3\u672f\u2019\u4e0d\u540c\uff0c\u6885\u82b1\u7ed9\u6211\u591a\u5c11\uff0c\u6211\u4f1a\u52a0\u500d\u5949\u8fd8\uff0c\u8fd9\u624d\u662f\u6211\u4eec\u6700\u5927\u7684\u4e0d\u540c\u4e4b\u5904\u3002 \u3000\u3000\u7b49\u6211\u66ff\u6885\u82b1\u62a5\u4e86\u4ec7\uff0c\u518d\u627e\u4f60\u7b97\u5e10\uff0c\u5979\u4eec\u4e3a\u6076\u592a\u591a\uff0c\u4e0d\u80fd\u9976\u6055\uff0c\u4f46\u6211\u4e0d\u4f1a\u6740\u4f60\uff0c\u6211\u8981\u5e9f\u4e86\u4f60\u7684\u6b66\u529f\uff0c\u4e0d\u80fd\u8ba9\u5979\u4eec\u7ee7\u7eed\u4e3a\u6076\uff01\u2026\u2026\u201d \u3000\u3000\u201c\u6885\u82b1\u80fd\u72ec\u6740\u56db\u5927\u5929\u9e45\u2026\u2026\u201d\u79cb\u79bb\u82b1\u63a5\u9053\uff1a\u201c\u8fd9\u6b66\u529f\u5982\u662f\u4f60\u4f20\u7ed9\u5979\u7684\uff0c\u6211\u76f8\u4fe1\uff0c\u4f60\u6709\u6740\u6211\u7684\u672c\u9886\uff0c\u4f46\u4f60\u5e9f\u4e86\u6211\u7684\u6b66\u529f\uff0c\u6bd4\u6740\u6211\u8fd8\u8981\u51b7\u9177\uff0c\u56e0\u4e3a\uff0c\u90a3\u91cc\uff0c\u8c01\u90fd\u53ef\u4ee5\u6740\u4e86\u6211\uff0c\u51cc\u8fb1\u6211\uff01\u201d \u3000\u3000\u201c\u4e16\u4e0a\u6ca1\u6709\u5b8c\u7f8e\u7684\u4e8b\uff0c\u5979\u4eec\u5bb3\u4eba\u65f6\uff0c\u53ef\u66fe\u60f3\u5230\u8fd9\u4e9b\u62a5\u5e94\uff0c\u4f60\u5982\u4e0d\u60f3\u6b7b\uff0c\u90a3\u5c31\u4e0d\u8981\u5b58\u9003\u8d70\u7684\u4fa5\u5e78\u4e4b\u5fc3\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u90a3\u4f1a\u4f7f\u4f60\u6b7b\u7684\u66f4\u5feb\u4e00\u4e9b\uff01\u201d \u3000\u3000\u76ee\u5149\u8f6c\u6ce8\u5230\u9ec4\u5929\u738b\u7684\u8138\u4e0a\uff0c\u9053\uff1a\u201c\u51fa\u5251\u5427\uff01\u6885\u82b1\u7684\u9634\u7075\u4e0d\u8fdc\uff0c\u5979\u5728\u7b49\u7740\u6211\u624b\u5203\u6740\u5979\u7684\u51f6\u624b\u2026\u2026\u201d \u3000\u3000\u201c\u6c5f\u5927\u54e5\uff0c\u8ba9\u7ed9\u6211\u5427\uff01\u201d\u541f\u96ea\u9053\uff1a\u201c\u662f\u6211\u5bb3\u4e86\u6885\u82b1\u59d0\u59d0\uff0c\u6211\u5982\u4e0d\u4f20\u5979\u90a3\u51e0\u62db\u6740\u654c\u7684\u624b\u6cd5\uff0c\u4e5f\u8bb8\u5979\u4e0d\u4f1a\u6b7b\uff01\u201d \u3000\u3000\u9ec4\u5929\u738b\u6ca1\u6709\u56de\u7b54\uff0c\u4ed6\u5fc3\u4e2d\u5b9e\u5df2\u754f\u60e7\u81f3\u6781\uff0c\u5927\u6279\u4ece\u5c5e\uff0c\u4e00\u4e2a\u4e0d\u89c1\uff0c\u5c31\u7b97\u672a\u88ab\u6740\u5149\uff0c\u52ab\u540e\u4f59\u751f\u7684\uff0c\u4e5f\u5fc5\u662f\u9003\u547d\u53bb\u4e86\uff0c\u5929\u540e\u5bab\u7684\u4e09\u4f4d\u5bab\u4e3b\u867d\u5728\u5927\u6bbf\uff0c\u4f46\u4f3c\u5df2\u88ab\u5bf9\u65b9\u5a01\u52bf\u6240\u6298\uff0c\u6050\u4ea6\u96be\u4e3a\u52a9\u529b\u4e86\u3002 \u3000\u3000\u89c6\u4eba\u547d\u5982\u8349\u82a5\u7684\u4eba\uff0c\u5927\u90fd\u662f\u6700\u6015\u6b7b\u7684\u4eba\uff0c\u4ed6\u4eec\u770b\u591a\u4e86\u6b7b\u4ea1\uff0c\u4e5f\u6df1\u77e5\u90a3\u79cd\u75db\u82e6\uff0c\u8fd9\u79cd\u4eba\u5927\u90fd\u662f\u53ef\u4ee5\u51cc\u8650\u4eba\u7684\u5f3a\u8005\uff0c\u4f46\u4ed6\u4eec\u81ea\u5df1\u9762\u5bf9\u6b7b\u4ea1\u65f6\uff0c\u5fc3\u4e2d\u7684\u5fe7\u60e7\uff0c\u5374\u5f3a\u8fc7\u5e38\u4eba\u5341\u500d\u3002 \u3000\u3000\u9ec4\u5929\u738b\u5c31\u662f\u8fd9\u79cd\u4eba\u3002 \u3000\u3000\u4ed6\u50cf\u6d6e\u6c89\u5728\u56db\u987e\u832b\u832b\u7684\u5927\u6d77\u4e2d\uff0c\u5c31\u7b97\u98d8\u6765\u7684\u53ea\u662f\u4e00\u6839\u7ec6\u5c0f\u7684\u6811\u679d\uff0c\u4e5f\u8981\u4e00\u628a\u6293\u4f4f\u5b83\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u8fc7\u6765\uff0c\u4ed6\u4eec\u6740\u4e86\u6211\u4e4b\u540e\uff0c\u4e00\u6837\u8981\u6740\u5979\u4eec\u3002\u201d\u58f0\u97f3\u98a4\u6296\uff0c\u53d1\u97f3\u4e0d\u5f88\u6e05\u695a\uff0c\u5b8c\u5168\u53d8\u4e86\u8154\u8c03\u3002 \u3000 \u7b2c\u4e8c\u5341\u4e03\u56de\u3000\u5927\u6559\u4e3b\u5047\u548c\u5dee\u84dd\u51e4 \u3000 \u3000\u3000\u201c\u9ec4\u5929\u738b\uff0c\u53ef\u662f\u4f60\u5728\u54ed\u554a\uff1f\u201d \u3000\u3000\u79cb\u79bb\u82b1\u8fd8\u5f88\u9547\u5b9a\uff0c\u81f3\u5c11\u53ef\u4ee5\u6682\u4fdd\u6027\u547d\uff0c\u53ea\u4e0d\u8fc7\uff0c\u5fc3\u4e2d\u8fd8\u5728\u76d8\u7b97\uff0c\u5982\u662f\u5168\u529b\u7a81\u56f4\u800c\u9003\uff0c\u6709\u51e0\u5206\u6210\u529f\u7684\u673a\u4f1a\u3002 \u3000\u3000\u56e0\u4e3a\u88ab\u5e9f\u4e86\u6b66\u529f\u4e4b\u540e\uff0c\u662f\u5426\u8fd8\u80fd\u6062\u590d\u5462\uff1f\u4ee5\u6559\u4e3b\u4e4b\u80fd\uff0c\u4e5f\u8bb8\u6709\u51e0\u5206\u673a\u4f1a\uff0c\u4f46\u5374\u6ca1\u6709\u628a\u63e1\u3002\u5c31\u7b97\u5206\u624b\u4e86\uff0c\u4e5f\u8be5\u6709\u4e00\u70b9\u592b\u59bb\u60c5\u610f\u554a\uff01 \u3000\u3000\u201c\u9ec4\u5929\u738b\uff0c\u6c5f\u6e56\u4eba\u7684\u8138\uff0c\u5168\u88ab\u4f60\u7ed9\u4e22\u5149\u4e86\u2026\u2026\u201d\u590f\u5929\u540c\u9053\uff1a\u201c\u6b7b\u5c31\u6b7b\u4e86\uff0c\u54ea\u6709\u4f60\u8fd9\u4e2a\u7a9d\u56ca\u6cd5\uff1f\u201d \u3000\u3000\u201c\u6740\u6b7b\u4e86\u6885\u82b1\u59d0\u59d0\uff0c\u4e00\u5b9a\u8981\u507f\u547d\u3002\u201d \u3000\u3000\u541f\u971c\u51fa\u5251\u4e86\uff0c\u5251\u5149\u5982\u95ea\u7535\uff0c\u523a\u5411\u9ec4\u5929\u738b\u7684\u524d\u80f8\u3002 \u3000\u3000\u9ec4\u5929\u738b\u6708\u7259\u5200\u6a2a\u91cc\u5c01\u6321\uff0c\u91d1\u94c1\u4ea4\u9e23\u58f0\u4e2d\uff0c\u7adf\u628a\u4e00\u5251\u6321\u5f00\u3002 \u3000\u3000\u5168\u573a\u4e2d\u4eba\uff0c\u90fd\u628a\u6ce8\u610f\u529b\u6295\u6ce8\u5728\u4e24\u4eba\u8eab\u4e0a\uff0c\u53f6\u96ea\u3001\u541f\u971c\u7ed9\u4ed6\u4eec\u5fc3\u7406\u4e0a\u7684\u5a01\u80c1\u5927\u5927\u4e86\uff0c\u4ed6\u4eec\u8981\u770b\u6e05\u695a\u541f\u971c\u6740\u6b7b\u9ec4\u5929\u738b\u7684\u624b\u6cd5\u3002 \u3000\u3000\u9ec4\u5929\u738b\u53ea\u5b88\u4e0d\u653b\uff0c\u541f\u971c\u5374\u8fde\u653b\u4e03\u5251\uff0c\u5168\u88ab\u5c01\u5f00\u3002 \u3000\u3000\u5200\u3001\u5251\u4ea4\u51fb\u4e4b\u95f4\uff0c\u541f\u971c\u7684\u5185\u529b\u663e\u7136\u4e0d\u5982\u9ec4\u5929\u738b\u7684\u6d51\u539a\uff0c\u88ab\u9707\u7684\u8fde\u8fde\u540e\u9000\u3002 \u3000\u3000\u201c\u4e0d\u8fc7\u5982\u6b64\u554a\uff01\u201d \u3000\u3000\u9ec4\u5929\u738b\u5b9a\u4e0b\u4e86\u5fc3\uff0c\u6697\u81ea\u5fd6\u9053\uff1a\u201c\u8fd9\u6837\u7684\u5251\u62db\u3001\u5185\u529b\uff0c\u5982\u4f55\u80fd\u6740\u4e86\u4e09\u9b54\u3001\u4e94\u602a\uff0c\u548c\u95e8\u4e0b\u5c5e\u4f17\uff1f\u770b\u6765\uff0c\u4ed6\u4eec\u662f\u522b\u6709\u65b9\u6cd5\uff1f\u6211\u53ef\u5728\u5341\u62db\u5185\u53d6\u5979\u6027\u547d\uff01\u54fc\u54fc\uff0c\u771f\u662f\u88ab\u4ed6\u4eec\u7ed9\u5413\u660f\u4e86\u3002\u201d \u3000\u3000\u60f3\u5230\u521a\u624d\u90a3\u79cd\u568e\u53eb\u3001\u7a9d\u56ca\uff0c\u5fc3\u4e2d\u6028\u6068\u987f\u751f\uff0c\u7a81\u7136\u95f4\u6539\u91c7\u653b\u52bf\uff0c\u957f\u5251\u3001\u77ed\u5200\uff0c\u914d\u5408\u53cd\u51fb\uff0c\u7acb\u523b\u628a\u541f\u971c\u5377\u5165\u4e86\u4e00\u7247\u5200\u3001\u5251\u4ea4\u653b\u7684\u5bd2\u8292\u4e4b\u4e2d\u3002 \u3000\u3000\u590f\u5929\u540c\u6697\u6697\u53f9\u606f\u4e00\u58f0\uff0c\u5fd6\u9053\uff1a\u201c\u770b\u6765\uff0c\u6b66\u529f\u4e00\u9053\uff0c\u5b9e\u662f\u65e0\u6cd5\u6295\u5de7\u4e86\uff0c\u6b27\u9633\u541f\u971c\u8fd9\u70b9\u5e74\u7eaa\uff0c\u5c82\u771f\u80fd\u7ec3\u6210\u4e86\u60ca\u4eba\u7edd\u827a\uff0c\u4ed6\u4eec\u76f4\u6b63\u7684\u4e3b\u529b\uff0c\u8fd8\u662f\u5728\u6c5f\u67ab\u4e00\u4eba\u8eab\u4e0a\uff0c\u8fd9\u4e2b\u5934\u52bf\u96be\u6491\u8fc7\u4e00\u62db\uff0c\u6211\u8981\u4e0d\u8981\u51fa\u4e30\u5e2e\u5979\u5462\uff1f\u201d \u3000\u3000\u5fc3\u4e2d\u767e\u611f\u4ea4\u96c6\uff0c\u65e0\u6cd5\u4e0b\u5b9a\u51b3\u5fc3\uff01 \u3000\u3000\u4f46\u95fb\u79cb\u79bb\u82b1\u4e00\u9635\u683c\u683c\u5927\u7b11\uff0c\u9053\uff1a\u201c\u9ec4\u5929\u738b\uff0c\u53ef\u8981\u6211\u4eec\u5929\u540e\u5bab\u5e2e\u4f60\u4e00\u628a\u5417\uff1f\u201d \u3000\u3000\u201c\u7528\u4e0d\u7740\u4e86\u2026\u2026\u201d\u9ec4\u5929\u738b\u9053\uff1a\u201c\u770b\u6211\u5341\u62db\u5185\u6740\u4e86\u8fd9\u4e2a\u4e2b\u5934\uff0c\u54b1\u4eec\u518d\u5408\u529b\u5bf9\u4ed8\u6c5f\u67ab\uff01\u4ed6\u62d0\u8dd1\u4e86\u6211\u95e8\u4e0b\u5f1f\u5b50\u9752\u51e4\uff0c\u8fd9\u7b14\u5e10\u5c82\u53ef\u4e0d\u7b97\uff1f\u201d \u3000\u3000\u79cb\u79bb\u82b1\u5fd6\u9053\uff1a\u201c\u73b0\u5728\u662f\u5929\u9a6c\u5802\u7684\u95ee\u9898\u4e86\uff0c\u590f\u5929\u540c\u3001\u767d\u5929\u5316\u7b49\u5982\u662f\u53cd\u529b\u52a9\u654c\uff0c\u56f4\u653b\u6c5f\u67ab\uff0c\u5c31\u4e0d\u592a\u53ef\u80fd\u4e86\uff01\u201d \u3000\u3000\u201c\u6c5f\u67ab\u2026\u2026\u201d\u590f\u5929\u540c\u7a81\u7136\u5f00\u4e86\u53e3\uff0c\u9053\uff1a\u201c\u5929\u9a6c\u5802\u5bf9\u4ed8\u5929\u540e\u5bab\u4e09\u4f4d\u5bab\u4e3b\uff0c\u4f60\u4eec\u5408\u529b\u6740\u4e86\u9ec4\u5929\u738b\u3002\u201d \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u7528\u4e0d\u7740\u8d39\u4e8b\u4e86\u3002\u201d \u3000\u3000\u79cb\u79bb\u82b1\u5fc3\u4e2d\u4e00\u52a8\uff0c\u5fd6\u9053\uff1a\u201c\u662f\u554a\uff01\u4ed6\u4eec\u53cc\u65b9\u5408\u529b\uff0c\u4e0d\u7ba1\u7ed3\u679c\u5982\u4f55\uff0c\u6211\u4eec\u597d\u50cf\u662f\u6682\u5904\u52a3\u52bf\u4e4b\u4e0b\u3002\u4e09\u5341\u516d\u8ba1\uff0c\u8d70\u4e3a\u4e0a\u7b56\uff0c\u7b49\u6559\u4e3b\u6536\u62fe\u4ed6\u4eec\u5427\uff01\u201d \u3000\u3000\u4e3b\u610f\u62ff\u5b9a\uff0c\u4f4e\u58f0\u559d\u9053\uff1a\u201c\u4e8c\u59b9\u3001\u4e09\u59b9\uff0c\u6211\u4eec\u8d70\uff01\u201d \u3000\u3000\u8f6e\u5200\u4e00\u6325\uff0c\u5411\u5916\u95ef\u53bb\u3002 \u3000\u3000\u201c\u8d70\u5f97\u4e86\u5417\uff1f\u201d \u3000\u3000\u541f\u96ea\u4e0d\u77e5\u4f55\u65f6\uff0c\u5df2\u5230\u4e86\u79cb\u79bb\u82b1\u7684\u8eab\u4fa7\u3002 \u3000\u3000\u4e09\u628a\u8f6e\u5200\uff0c\u98de\u5e03\u51fa\u4e00\u7247\u8000\u773c\u7684\u5200\u5c71\u5149\u5e55\uff0c\u4e00\u9f50\u5411\u541f\u96ea\u653b\u6765\u3002 \u3000\u3000\u201c\u6c5f\u5927\u54e5\u8bf4\u8fc7\u4e86\uff0c\u5979\u4eec\u8981\u9003\u8d70\uff0c\u5c31\u4f1a\u6b7b\u7684\u66f4\u5feb\uff01\u201d\u541f\u96ea\u9053\uff1a\u201c\u81ea\u8d8b\u6b7b\u8def\uff0c\u53ef\u4e0d\u80fd\u602a\u6211\uff0c\u66f4\u4e0d\u80fd\u602a\u6c5f\u5927\u54e5\u8a00\u800c\u65e0\u4fe1\u4e86\u3002\u201d \u3000\u3000\u4eba\u5df2\u95ea\u5165\u5200\u5c71\u5149\u5e55\u4e4b\u4e2d\uff0c\u5b9d\u5251\u65cb\u98de\uff0c\u91d1\u94c1\u4ea4\u54cd\u4e2d\u5b57\u5b57\u6e05\u6670\u3002 \u3000\u3000\u79cb\u79bb\u82b1\u7a81\u751f\u8b66\u89c9\uff0c\u6697\u9053\uff1a\u201c\u8fd9\u662f\u8bf1\u6740\u4e4b\u8ba1\u554a\uff01\u201d\u60f3\u559d\u4ee4\u4f4f\u624b\u3002 \u3000\u3000\u53ef\u662f\uff0c\u592a\u665a\u4e86\u3002 \u3000\u3000\u541f\u96ea\u7684\u5b9d\u5251\uff0c\u5df2\u7136\u5212\u8fc7\u4e86\u4e09\u4eba\u7684\u54bd\u5589\uff0c\u9c9c\u8840\u98d9\u5c04\u800c\u51fa\u3002 \u3000\u3000\u4e09\u628a\u8f6e\u5200\u5e03\u6210\u7684\u62a4\u8eab\u5149\u5e55\uff0c\u7adf\u65e0\u6cd5\u963b\u6b62\u90a3\u4e00\u652f\u4e8c\u5c3a\u56db\u5bf8\u7684\u5b9d\u5251\uff0c\u5c31\u597d\u50cf\u8fd9\u4e09\u4e2a\u7edd\u4e16\u9ad8\u624b\uff0c\u4f38\u51fa\u9888\u5b50\u5728\u7b49\u7740\u6328\u90a3\u8981\u547d\u7684\u4e00\u5251\u3002 \u3000\u3000\u541f\u971c\u914d\u5408\u541f\u96ea\u51fa\u5251\uff0c\u4e00\u4e0b\u5b50\u5c31\u95ea\u5230\u4e86\u9ec4\u5929\u738b\u7684\u53f3\u4fa7\uff0c\u4f46\u624b\u4e2d\u957f\u5251\uff0c\u5374\u4ece\u524d\u80f8\u523a\u5165\u4e86\u9ec4\u5929\u738b\u7684\u524d\u5fc3\u8981\u5bb3\u3002 \u3000\u3000\u4eba\u548c\u5251\uff0c\u4f3c\u662f\u5b8c\u5168\u5206\u79bb\u4e86\uff0c\u5206\u5728\u4e24\u4e2a\u65b9\u4f4d\u4e0a\u3002 \u3000\u3000\u541f\u971c\u4e0d\u77e5\u662f\u5b58\u5fc3\u5356\u5f04\uff0c\u8fd8\u662f\u771f\u4e0d\u5982\u59d0\u59d0\u4e4b\u5feb\uff0c\u53ea\u8981\u7559\u5fc3\u770b\uff0c\u90fd\u53ef\u4ee5\u6e05\u695a\u7684\u770b\u5230\u5979\u6740\u6b7b\u9ec4\u592d\u738b\u7684\u5251\u6cd5\u3002 \u3000\u3000\u590f\u5929\u540c\u3001\u767d\u5929\u5316\u90fd\u770b\u6e05\u695a\u4e86\uff0c\u4f46\u5374\u770b\u7684\u5fc3\u795e\u4ff1\u98a4\u3002 \u3000\u3000\u90a3\u4e0d\u662f\u4e00\u822c\u5251\u6cd5\uff0c\u662f\u9b54\u6cd5\uff0c\u60f3\u6740\u4f60\uff0c\u5c31\u975e\u8981\u4f60\u6027\u547d\u4e0d\u53ef\u3002 \u3000\u3000\u201c\u6c5f\u5927\u54e5\uff0c\u662f\u5979\u4eec\u4e0d\u542c\u8bdd\u554a\uff01\u4e0d\u8981\u602a\u6211\u554a\uff01\u201d \u3000\u3000\u541f\u96ea\u5728\u7b11\uff0c\u7b11\u7684\u4e00\u7751\u5996\u5f02\u3002 \u3000\u3000\u541f\u971c\u5c31\u5728\u9ec4\u5929\u738b\u7684\u8863\u670d\u4e0a\uff0c\u62b9\u53bb\u5251\u4e0a\u8840\u8ff9\uff0c\u9053\uff1a\u201c\u590f\u5929\u540c\u3001\u767d\u5929\u5316\uff0c\u4f60\u4eec\u8bf4\uff0c\u6211\u4eec\u59d0\u59b9\u662f\u4e0d\u662f\u9075\u7167\u7740\u6c5f\u5927\u54e5\u7684\u547d\u4ee4\u529e\u4e8b\uff1f\u201d \u3000\u3000\u201c\u662f\uff01\u201d \u3000\u3000\u590f\u5929\u540c\u7684\u58f0\u97f3\u6709\u70b9\u6296\uff0c\u5fc3\u4e2d\u4f59\u60b8\u72b9\u5b58\u3002 \u3000\u3000\u201c\u8bf4\u7684\u5bf9\u554a\uff01\u201d\u767d\u5929\u5316\u9053\uff1a\u201c\u6c5f\u67ab\u5df2\u7ecf\u8b66\u544a\u5979\u4eec\u4e86\uff0c\u5979\u4eec\u4e0d\u80af\u542c\u4ece\uff0c\u662f\u81ea\u627e\u6b7b\u8def\u3002\u201d \u3000\u3000\u541f\u96ea\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u767d\u5929\u5316\uff0c\u4f60\u662f\u6587\u6b66\u5168\u624d\uff0c\u4e5f\u5f88\u8bc6\u65f6\u52a1\uff0c\u8bc6\u65f6\u52a1\u8005\u662f\u4fca\u6770\u5417\uff1f\u5f20\u59e8\u8981\u6211\u4eec\u7559\u4e0b\u4f60\u4eec\u7684\u6027\u547d\uff0c\u5171\u540c\u6297\u62d2\u6559\u4e3b\uff0c\u4f60\u4eec\u4e00\u5b9a\u4f1a\u540c\u610f\u4e86\u3002\u201d \u3000\u3000\u4ee5\u541f\u96ea\u4e4b\u7f8e\uff0c\u7b11\u8d77\u6765\u5e94\u8be5\u5341\u5206\u52a8\u4eba\uff0c\u4f46\u767d\u5929\u5316\u5374\u770b\u7684\u80cc\u810a\u4e0a\u5192\u51fa\u4e86\u4e00\u80a1\u5bd2\u610f\uff0c\u9053\uff1a\u201c\u59d1\u5a18\u8bf4\u7684\u662f\uff01\u201d \u3000\u3000\u6c5f\u67ab\u76ee\u5149\u5728\u4e8c\u5973\u8138\u4e0a\u8f6c\u52a8\u4e00\u9635\u3002\u53f9\u606f\u4e00\u58f0\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u5979\u4eec\u90fd\u7ec3\u6210\u4e86\u9b54\u5f71\u5316\u8eab\uff1f\u201d\u6c5f\u67ab\u6ca1\u6709\u7ec3\u8fc7\uff0c\u4f46\u8fd8\u662f\u77a7\u5f97\u51fa\u6765\u3002 \u3000\u3000\u201c\u662f\uff01\u201d\u541f\u971c\u9053\uff1a\u201c\u6211\u548c\u59d0\u59d0\u6ca1\u6709\u5546\u91cf\u8fc7\uff0c\u5404\u7ec3\u5404\u7684\uff0c\u4e0d\u77e5\u9053\u600e\u4e48\u4f1a\u8d70\u4e0a\u4e86\u4e00\u6761\u8def\uff0c\u6211\u60f3\uff0c\u8fd9\u90fd\u662f\u4e3a\u4e86\u60f3\u5e2e\u52a9\u6c5f\u5927\u54e5\u5427\uff01\u201d \u3000\u3000\u201c\u8bf4\u7684\u662f\u554a\uff01\u201d\u5927\u548c\u5c1a\u63a5\u4e0a\u8154\uff0c\u9053\uff1a\u201c\u975e\u4e24\u4f4d\u8fd9\u7b49\u7f55\u4e16\u5947\u529f\uff0c\u5982\u4f55\u80fd\u529b\u514b\u5f3a\u654c\u3002\u201d \u3000\u3000\u201c\u6211\u4eec\u5168\u529b\u5728\u5e2e\u52a9\u6c5f\u5927\u54e5\uff01\u201d\u541f\u971c\u9053\uff1a\u201c\u4f46\u4ed6\u597d\u50cf\u662f\u4e0d\u592a\u9ad8\u5174\u554a\uff1f\u201d \u3000\u3000\u8bdd\u662f\u8bf4\u7684\u4e0d\u9519\uff0c\u4f46\u795e\u60c5\u8bed\u6c14\uff0c\u548c\u8fc7\u53bb\u5168\u4e0d\u76f8\u540c\u4e86\u3002 \u3000\u3000\u662f\u7684\uff01 \u3000\u3000\u4e24\u4e2a\u5c0f\u72d0\u5973\u5df2\u7ecf\u5f00\u59cb\u53d8\u4e86\u3002 \u3000\u3000\u541f\u96ea\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u597d\u4eba\u96be\u505a\u554a\uff01\u971c\u59b9\uff0c\u6211\u4eec\u8be5\u600e\u4e48\u529e\u5462\uff1f\u201d \u3000\u3000\u4f46\u95fb\u8863\u8882\u98d8\u98ce\u4e4b\u58f0\uff0c\u5f20\u56db\u59d1\u548c\u7f57\u5170\u98de\u8dc3\u800c\u81f3\u3002 \u3000\u3000\u201c\u5a18\u548c\u5f20\u59e8\u90fd\u6765\u4e86\uff01\u201d\u541f\u971c\u7b11\u9053\uff1a\u201c\u5e0c\u671b\u5979\u4eec\u522b\u548c\u6c5f\u5927\u54e5\u4e00\u6837\u602a\u6211\u4eec\u3002\u201d \u3000\u3000\u7f57\u5170\u6b63\u8981\u5927\u58f0\u56b7\u53eb\uff0c\u800c\u4e14\u5df2\u5f20\u5f00\u5634\u5df4\uff01\u4f46\u5374\u6ca1\u6709\u53eb\u51fa\u58f0\u97f3\u3002 \u3000\u3000\u56e0\u4e3a\uff0c\u5979\u770b\u5230\u4e86\u4e24\u4e2a\u5973\u513f\u3002 \u3000\u3000\u53ea\u4e0d\u8fc7\u4e00\u4e24\u5929\u6ca1\u6709\u770b\u5230\uff0c\u4f46\u7f57\u5170\u4f3c\u4e4e\u5df2\u4e0d\u518d\u8ba4\u8bc6\u5979\u4eec\u3002 \u3000\u3000\u541f\u96ea\u3001\u541f\u971c\u7684\u6837\u5b50\uff0c\u5e76\u65e0\u6539\u53d8\uff0c\u6539\u53d8\u7684\u662f\u795e\u60c5\uff0c\u7709\u5b87\u95f4\u4f3c\u6709\u7740\u4e00\u80a1\u6d6e\u52a8\u7684\u9752\u6c14\uff0c\u8138\u4e0a\u662f\u4e00\u79cd\u8bf4\u4e0d\u51fa\u7684\u5947\u602a\u795e\u8272\uff0c\u6709\u70b9\u5028\u50b2\uff0c\u6709\u70b9\u6de1\u6f20\uff0c\u6709\u70b9\u4e0d\u5c51\uff0c\u6709\u70b9\u5fff\u6012\u2026\u2026\u3002 \u3000\u3000\u597d\u50cf\u968f\u65f6\u53ef\u4ee5\u7ffb\u8138\uff0c\u968f\u65f6\u4f1a\u51fa\u624b\u6740\u4eba\uff0c\u5168\u8eab\u4e5f\u4f3c\u4e4e\u6563\u53d1\u51fa\u903c\u4eba\u7684\u5bd2\u6c14\uff0c\u6574\u4e2a\u4eba\u5c31\u88f9\u5728\u90a3\u79cd\u5bd2\u6c14\u4e4b\u4e2d\u3002 \u3000\u3000\u7f57\u5170\u770b\u5446\u4e86\uff0c\u517b\u4e86\u5341\u51e0\u5e74\u7684\u53ef\u7231\u5973\u513f\uff0c\u5ffd\u7136\u95f4\u662f\u90a3\u4e48\u964c\u751f\u3002 \u3000\u3000\u201c\u541f\u96ea\u3001\u541f\u971c\uff0c\u5979\u4eec\u597d\u602a\u5f02\uff0c\u5a18\u90fd\u5feb\u4e0d\u8ba4\u8bc6\u5979\u4eec\u4e86\uff1f\u201d \u3000\u3000\u201c\u8fd8\u4e0d\u662f\u4e00\u4e2a\u6837\u5b50\uff0c\u6211\u4eec\u6ca1\u6709\u957f\u9ad8\uff0c\u4e5f\u6ca1\u6709\u53d8\u77ee\u2026\u2026\u201d\u541f\u971c\u9053\uff1a\u201c\u53ea\u56e0\u4e3a\u6211\u4eec\u6740\u4e86\u4e00\u4e9b\u654c\u4eba\u2026\u2026\u201d \u3000\u3000\u201c\u90a3\u4e9b\u4eba\uff0c\u90fd\u662f\u4f60\u4eec\u4e24\u4e2a\u6740\u7684\uff1f\u201d\u7f57\u5170\u6253\u65ad\u4e86\u541f\u971c\u7684\u8bdd\u3002 \u3000\u3000\u201c\u662f\u554a\uff01\u6740\u4e00\u4e2a\u4e5f\u662f\u6740\uff0c\u6740\u4e00\u767e\u4e2a\u4e5f\u662f\u6740\u3002\u201d\u541f\u971c\u9053\uff1a\u201c\u65e2\u7136\u8981\u6740\u4e86\uff0c\u591a\u5c11\u6709\u4ec0\u4e48\u4e0d\u540c\uff1f\u201d \u3000\u3000\u7f57\u5170\u53f9\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8fd9\u600e\u4e48\u80fd\u4e00\u6837\u5462\uff1f\u201d \u3000\u3000\u201c\u5a18\uff01\u6211\u4eec\u6740\u4eba\uff0c\u662f\u4e3a\u4e86\u5e2e\u52a9\u6c5f\u5927\u54e5\u3002\u201d\u541f\u96ea\u9053\uff1a\u201c\u4ed6\u5374\u4e00\u70b9\u4e5f\u4e0d\u5feb\u4e50\uff0c\u51b7\u51b7\u7684\uff0c\u597d\u50cf\u8fd8\u6709\u4e9b\u5acc\u5f03\u6211\u4eec\u4e86\uff0c\u76f4\u7684\u53eb\u4eba\u4f24\u5fc3\u3002\u201d \u3000\u3000\u632f\u632f\u6709\u8bcd\uff0c\u6b6a\u7406\u4ea6\u7406\uff0c\u7f57\u5170\u4e00\u65f6\u95f4\u8fd8\u771f\u7684\u60f3\u4e0d\u51fa\u5982\u4f55\u56de\u7b54\u3002 \u3000\u3000\u201c\u6c5f\u67ab\u4f1a\u4e0d\u9ad8\u5174\uff1f\u201d\u5f20\u56db\u59d1\u53d1\u89c9\u8fd9\u65f6\u523b\u4e0d\u5b9c\u4e89\u8fa9\uff0c\u4e8c\u5973\u795e\u8272\u51b7\u5389\uff0c\u5fc3\u4e2d\u7684\u4e0d\u6ee1\uff0c\u5df2\u7ecf\u6ea2\u4e8e\u8a00\u8868\uff0c\u52a0\u4e0a\u90a3\u4e00\u8eab\u67d3\u6ee1\u8840\u8ff9\u7684\u767d\u886b\uff0c\u5c31\u5982\u8840\u6c60\u4e2d\u7684\u83b2\u82b1\uff0c\u9b3c\u57df\u4e2d\u7684\u5e7d\u7075\uff0c\u5947\u8be1\u83ab\u53ef\u540d\u72b6\uff0c\u5c3d\u91cf\u653e\u7f13\u4e86\u8bed\u6c14\uff0c\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u4ed6\u7684\u4e0d\u5bf9\u4e86\uff0c\u5f20\u59e8\u5e2e\u4f60\u4eec\u3002\u201d \u3000\u3000\u201c\u8fd8\u662f\u5f20\u59e8\u660e\u7406\uff0c\u201d\u541f\u96ea\u9053\uff1a\u201c\u6211\u548c\u971c\u59b9\uff0c\u4e3a\u60c5\u62d6\u7d2f\uff0c\u62da\u547d\u6740\u654c\uff0c\u53ef\u662f\u4e3a\u4e86\u8ba8\u53d6\u6c5f\u5927\u54e5\u7684\u6b22\u5fc3\uff0c\u4ed6\u5750\u4eab\u5176\u6210\uff0c\u5374\u4e00\u70b9\u4e5f\u4e0d\u611f\u6fc0\u3002\u201d \u3000\u3000\u4e8b\u60c5\u662f\u4e0d\u9519\uff0c\u4f46\u7528\u53e5\u8bed\u6c14\uff0c\u5374\u662f\u5484\u5484\u903c\u4eba\uff0c\u54ea\u91cc\u8fd8\u6709\u5f80\u65e5\u7684\u6e29\u67d4\u5a49\u7ea6\uff0c\u5b8c\u5168\u4e0d\u662f\u541f\u96ea\u4e86\u3002 \u3000\u3000\u201c\u9b3c\u4e2b\u5934\uff0c\u4f60\u4eec\u662f\u4e0d\u662f\u75af\u5566\u2026\u2026\u201d\u7f57\u5170\u5fcd\u4e0d\u4f4f\u4e86\uff0c\u7aef\u8d77\u4f5c\u5a18\u7684\u67b6\u5f0f\uff0c\u9053\uff1a\u201c\u6c5f\u67ab\u662f\u5979\u4eec\u7684\u672a\u5a5a\u592b\u5a7f\u554a\uff01\u201d \u3000\u3000\u541f\u971c\u5fae\u95ed\u773c\u775b\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u672a\u5a5a\u59bb\u5b50\u4e3a\u672a\u5a5a\u7684\u4e08\u592b\u5356\u547d\uff0c\u5f97\u4e0d\u5230\u4e00\u70b9\u601c\u60dc\uff0c\u53cd\u906d\u767d\u773c\u5acc\u5f03\uff0c\u5a18\uff0c\u4f60\u80f3\u818a\u8098\u5b50\u5411\u5916\u5f2f\uff0c\u5168\u4e0d\u540c\u60c5\u5973\u513f\u554a\uff01\u201d \u3000\u3000\u5979\u4e00\u5411\u7b11\u7684\u5a07\u5a9a\u52a8\u4eba\uff0c\u4f46\u8fd9\u4e00\u95ed\u4e0a\u773c\u775b\uff0c\u53ea\u9732\u51fa\u4e86\u4e00\u53e3\u767d\u7259\uff0c\u5473\u9053\u5c31\u5168\u53d8\u4e86\u3002 \u3000\u3000\u7f57\u5170\u6c14\u574f\u4e86\uff0c\u6b63\u8981\u5927\u53d1\u813e\u6c14\uff0c\u5374\u88ab\u5f20\u56db\u59d1\u62e6\u4f4f\u4e86\u3002 \u3000\u3000\u201c\u6885\u82b1\u59d0\u59d0\u6b7b\u4e86\uff0c\u6c5f\u5927\u54e5\u62b1\u7740\u5c38\u4f53\u5f88\u4e45\u4e0d\u80af\u653e\u4e0b\u6765\u2026\u2026\u201d\u541f\u971c\u9053\uff1a\u201c\u6211\u66ff\u6885\u82b1\u59d0\u59d0\u62a5\u4e86\u4ec7\uff0c\u4e5f\u542c\u4e0d\u5230\u6c5f\u5927\u54e5\u4e00\u4e2a\u8c22\u5b57\u3002\u201d \u3000\u3000\u5973\u4eba\u7684\u5992\u5fcc\uff0c\u4e5f\u5b8c\u5168\u663e\u9732\u4e86\u51fa\u6765\u3002 \u3000\u3000\u5f20\u56db\u59d1\u5fc3\u795e\u9707\u52a8\uff0c\u5fd6\u9053\uff1a\u201c\u5b8c\u5168\u53d8\u4e86\uff0c\u5df2\u4e0d\u662f\u96ea\u3001\u971c\u4e8c\u5973\u7684\u6027\u683c\uff0c\u4e00\u5165\u9b54\u5883\uff0c\u6539\u53d8\u5982\u6b64\u4e4b\u901f\uff0c\u628a\u5979\u4eec\u8001\u5a18\u4e5f\u4e0d\u653e\u5728\u5fc3\u4e0a\u4e86\u3002\u201d \u3000\u3000\u56de\u5934\u770b\u770b\u5927\u548c\u5c1a\uff0c\u5927\u548c\u5c1a\u793a\u610f\u5411\u6c5f\u67ab\u6c42\u63f4\u3002 \u3000\u3000\u6c5f\u67ab\u4e00\u76f4\u5728\u76b1\u7740\u7709\u5934\u6c89\u601d\uff0c\u5f20\u56db\u59d1\u53ea\u597d\u8d70\u8fc7\u53bb\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u96ea\u3001\u971c\u592a\u7d2f\u4e86\uff0c\u5979\u4eec\u9700\u8981\u4f60\u53bb\u5b89\u6170\u4e00\u4e0b\u3002\u201d \u3000\u3000\u6c5f\u67ab\u5982\u68a6\u521d\u9192\u822c\uff0c\u70b9\u70b9\u5934\uff0c\u7f13\u6b65\u5411\u541f\u971c\u884c\u53bb\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u6740\u4e86\u9ec4\u5929\u738b\uff0c\u66ff\u6885\u82b1\u62a5\u4e86\u4ec7\uff0c\u6885\u82b1\u5728\u4e5d\u6cc9\u4e4b\u4e0b\uff0c\u4e5f\u4f1a\u5341\u5206\u611f\u6fc0\u3002\u201d \u3000\u3000\u541f\u971c\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u5462\uff1f\u201d \u3000\u3000\u201c\u6c5f\u5927\u54e5\u5f53\u7136\u4e5f\u5f88\u611f\u6fc0\u3002\u201d \u3000\u3000\u541f\u971c\u70b9\u70b9\u5934\uff0c\u6d6e\u52a8\u5728\u7709\u5b87\u95f4\u9752\u6c14\uff0c\u9010\u6e10\u6d88\u9000\u3002 \u3000\u3000\u6c5f\u67ab\u8f6c\u8eab\u884c\u8fd1\u53f6\u96ea\u9053\uff1a\u201c\u4e00\u5251\u6740\u4e86\u5929\u540e\u5bab\u4e09\u4f4d\u5bab\u4e3b\uff0c\u6c5f\u5927\u54e5\u6050\u6015\u5df2\u4e0d\u53ca\u96ea\u59b9\u4e86\u3002\u201d \u3000\u3000\u201c\u6211\u6740\u4e86\u5f88\u591a\u4eba\uff0c\u4f60\u4e0d\u4ecb\u610f\uff1f\u201d \u3000\u3000\u201c\u4e0d\u4ecb\u610f\uff0c\u52a8\u624b\u62da\u547d\uff0c\u6211\u4e0d\u6740\u654c\uff0c\u654c\u5c31\u6740\u6211\u4e86\u3002\u201d \u3000\u3000\u541f\u96ea\u53f9\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6211\u5728\u60f3\u554a\uff01\u2026\u2026\u201d \u3000\u3000\u770b\u7740\u6c5f\u67ab\u5ffd\u7136\u5fae\u7b11\u4e0d\u8bed\u3002 \u3000\u3000\u201c\u60f3\u4ec0\u4e48\u5462\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u7684\u58f0\u97f3\u5f88\u6e29\u67d4\uff0c\u5145\u6ee1\u7740\u60c5\u610f\u3002 \u3000\u3000\u201c\u6c5f\u6e56\u4e0a\u7684\u9ad8\u624b\u3001\u5f3a\u8005\uff0c\u5168\u90fd\u5012\u4e0b\u53bb\u4e86\u3002\u201d\u541f\u96ea\u9053\uff1a\u201c\u54e6\u4eec\u4f1a\u4e0d\u4f1a\u6253\u4e00\u67b6\uff0c\u5206\u4e2a\u80dc\u8d1f\u51fa\u6765\uff1f\u201d \u3000\u3000\u771f\u662f\u6674\u5929\u9739\u96f3\u5f53\u5934\u9707\uff0c\u8bed\u4e0d\u60ca\u4eba\u6b7b\u4e0d\u4f11\uff0c\u53ea\u542c\u5f97\u7f57\u5170\u5dee\u4e00\u70b9\u660f\u53a5\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u5982\u662f\u6b27\u9633\u662d\u4e5f\u5728\u73b0\u573a\uff0c\u7236\u5973\u4e8c\u4eba\uff0c\u7acb\u523b\u4f1a\u5f53\u573a\u7ffb\u8138\u3002 \u3000\u3000\u6c5f\u67ab\u5374\u662f\u51fa\u5947\u5730\u5e73\u9759\uff0c\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u53ea\u6015\u6c5f\u5927\u54e5\u4e0d\u662f\u4f60\u7684\u654c\u624b\uff1f\u201d \u3000\u3000\u541f\u96ea\u5f88\u6ee1\u610f\uff0c\u5fae\u5fae\u9894\u9996\uff0c\u8138\u4e0a\u7684\u51b7\u5389\uff0c\u4e5f\u9010\u6e10\u5f00\u59cb\u6d88\u51cf\u3002 \u3000\u3000\u5927\u548c\u5c1a\u5fc3\u4e2d\u53f9\u606f\uff0c\u6697\u9053\uff1a\u201c\u5992\u3001\u55d4\u4e8c\u6076\uff0c\u5df2\u7136\u6210\u5f62\uff0c\u4e0d\u77e5\u5979\u4eec\u8fd8\u6709\u591a\u5c11\u7075\u667a\u53bb\u6297\u62d2\u8d2a\u3001\u5984\u3001\u75f4\u3001\u6068\u8bf8\u9b54\uff0c\u4e00\u65e6\u6068\u4e0a\u5fc3\u5934\uff0c\u5c40\u52bf\u5c31\u96be\u4ee5\u6536\u62fe\u4e86\u3002\u201d \u3000\u3000\u96ea\u3001\u971c\u9010\u6e10\u7684\u5e73\u590d\u4e0b\u6765\uff0c\u5996\u5f02\u7684\u795e\u60c5\u5c3d\u53bb\uff0c\u5982\u6c34\u6da4\u6c61\uff0c\u795e\u83b9\u91cd\u590d\u3002 \u3000\u3000\u541f\u96ea\u6447\u6447\u5934\uff0c\u4f3c\u662f\u53e6\u4e00\u4e2a\u5883\u754c\u4e2d\u56de\u8fc7\u6765\uff0c\u6025\u9053\uff1a\u201c\u6c5f\u5927\u54e5\uff0c\u6885\u82b1\u59d0\u59d0\u6b7b\u4e86\uff0c\u7533\u59e8\u8bf4\u5979\u6b7b\u7684\u5f88\u82f1\u52c7\uff0c\u4f60\u8981\u8282\u54c0\u987a\u53d8\u554a\uff01\u201d \u3000\u3000\u6c5f\u67ab\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u6211\u77e5\u9053\uff0c\u4f60\u548c\u541f\u971c\uff0c\u4e5f\u4e0d\u8981\u592a\u4f24\u5fc3\u554a\uff01\u201d \u3000\u3000\u201c\u600e\u4e48\u53c8\u53d8\u56de\u6765\u4e86\u2026\u2026\u201d\u7f57\u5170\u4f4e\u58f0\u5bf9\u5f20\u56db\u59d1\u9053\uff1a\u201c\u4e24\u4e2a\u4e2b\u5934\uff0c\u597d\u50cf\u662f\u4e2d\u4e86\u90aa\u5566\uff1f\u5ffd\u597d\u5ffd\u574f\u5440\uff1f\u201d \u3000\u3000\u5f20\u56db\u59d1\u770b\u4e86\u7f57\u5170\u4e00\u773c\uff0c\u70b9\u70b9\u5934\uff0c\u884c\u8fd1\u96ea\u3001\u971c\u4e8c\u5973\uff0c\u9053\uff1a\u201c\u8d70\uff01\u8ddf\u5f20\u59e8\u53bb\u6362\u4ef6\u8863\u773c\uff0c\u5403\u4e9b\u70b9\u5fc3\uff0c\u5f20\u59e8\u5e26\u6765\u4e86\uff0c\u5979\u4eec\u6700\u559c\u6b22\u5403\u7684\u516b\u5b9d\u6842\u82b1\u7cd5\uff4f\u201d \u3000\u3000\u4e00\u624b\u4e00\u4e2a\u62c9\u7740\u4e24\u4e2a\u4eba\u79bb\u5f00\u4e86\u5927\u6bbf\u3002 \u3000\u3000\u4e24\u4e2a\u5c0f\u72d0\u5973\u5a07\u67d4\u5168\u590d\uff0c\u56de\u8fc7\u5934\uff0c\u6709\u70b9\u4f9d\u604b\u5730\u671b\u7740\u6c5f\u67ab\uff0c\u4f46\u5374\u88ab\u5f20\u56db\u59d1\u7275\u7740\u8d70\u4e86\u3002 \u3000\u3000\u7f57\u5170\u6ca1\u6709\u8d70\uff0c\u5979\u89c9\u7740\u6c5f\u67ab\u5fcd\u53d7\u4e86\u5f88\u5927\u7684\u59d4\u5c48\uff0c\u60f3\u7559\u4e0b\u6765\u5b89\u6170\u6c5f\u67ab\u51e0\u53e5\u3002 \u3000\u3000\u4f46\u89c1\u6c5f\u67ab\u795e\u60c5\u5982\u5e38\uff0c\u5168\u65e0\u6120\u610f\uff0c\u624d\u653e\u4e0b\u5fc3\u4e2d\u4e00\u5757\u77f3\u5934\uff0c\u4e5f\u5fcd\u4e0b\u6765\u672a\u518d\u591a\u8a00\u3002 \u3000\u3000\u201c\u590f\u5929\u540c\u3001\u767d\u5929\u5316\uff0c\u4f60\u4eec\u613f\u7559\uff0c\u613f\u8d70\uff0c\u6089\u51ed\u5c0a\u4fbf\uff0c\u7531\u6b64\u523b\u8d77\uff0c\u5f80\u4e8b\u4e00\u7b14\u52fe\u9500\u3002\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u56e0\u4e3a\uff0c\u6211\u77e5\u9053\u4e86\uff0c\u4f60\u4eec\u4e0d\u662f\u4f24\u5bb3\u6b66\u6797\u4e09\u5723\u7684\u771f\u6b63\u51f6\u624b\u3002\u201d \u3000\u3000\u201c\u590f\u67d0\u7cca\u6d82\u4e86\u4e8c\u5341\u5e74\uff0c\u51b3\u5b9a\u7559\u4e0b\u6765\u770b\u4e2a\u6c34\u843d\u77f3\u51fa\uff0c\u6211\u77e5\u9053\u5e2e\u4e0d\u4e0a\u5927\u5fd9\uff0c\u4f46\u7565\u5c3d\u7ef5\u529b\uff0c\u4ee5\u8d4e\u7f6a\u884d\uff0c\u4e5f\u597d\u6b7b\u7684\u5b89\u5fc3\uff0c\u4f55\u51b5\uff0c\u6211\u5df2\u7b54\u5e94\u4e86\u7533\u96ea\u541b\uff0c\u8fd8\u5979\u65e7\u6b20\uff0c\u4ee5\u5e73\u590d\u5979\u80f8\u4e2d\u79ef\u5fff\u3002\u201d \u3000\u3000\u201c\u5728\u4e0b\u548c\u4e09\u4f4d\u697c\u4e3b\uff0c\u5df2\u7ecf\u4f1a\u5546\u51b3\u5b9a\uff0c\u8ffd\u968f\u590f\u603b\u5802\u4e3b\u7559\u4e0b\u6765\u3002\u201d\u767d\u5929\u5316\u9053\uff1a\u201c\u6211\u80f8\u4e2d\u85cf\u6709\u4e0d\u5c11\u9690\u5bc6\uff0c\u6216\u53ef\u7a0d\u6709\u5949\u732e\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u5728\u4e0b\u53bb\u5b89\u846c\u4e86\u6885\u82b1\u59d1\u5a18\uff0c\u54b1\u4eec\u7a0d\u5019\u8be6\u8c08\u3002\u201d \u3000\u3000\u6c5f\u67ab\u62b1\u8d77\u4e86\u6885\u82b1\u7684\u5c38\u4f53\uff0c\u6b65\u51fa\u5927\u6bbf\u3002 \u3000\u3000\u5927\u548c\u5c1a\u4e00\u53e3\u6c14\u559d\u5e72\u4e86\u5f20\u56db\u59d1\u5e26\u6765\u7684\u4e00\u74f6\u8001\u9152\uff0c\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\u201c\u4f60\u770b\u5230\u60c5\u52bf\u7684\u53ef\u6015\u4e86\uff0c\u2018\u4f20\u771f\u8bb0\u2019\u679c\u7136\u662f\u4e00\u672c\u90aa\u6076\u65e0\u6bd4\u7684\u8bb0\u8ff0\uff0c\u8bb0\u8ff0\u7b2c\u4e00\u9875\u5df2\u7ecf\u63d0\u51fa\u4e86\u8b66\u544a\uff0c\u5e72\u8fdd\u5929\u673a\uff0c\u6ca6\u52ab\u83ab\u6028\uff0c\u60f3\u4e0d\u5230\u7684\u662f\u6c5f\u67ab\u6ca1\u6709\u6ca6\u5165\u9b54\u57df\uff0c\u8ba9\u4e24\u4e0a\u5c0f\u72d0\u5973\u9677\u5165\u9b54\u52ab\u2026\u2026\u201d \u3000\u3000\u201c\u5927\u548c\u5c1a\u2026\u2026\u201d\u5f20\u56db\u59d1\u6253\u65ad\u4e86\u4e03\u5b9d\u5927\u5e08\u7684\u8bdd\uff0c\u9053\uff1a\u201c\u4e8b\u5df2\u81f3\u6b64\uff0c\u6025\u5728\u5584\u540e\uff0c\u4e24\u4e2a\u4e2b\u5934\u5165\u9b54\u751a\u6df1\uff0c\u6211\u770b\u662f\u62d6\u4e0d\u4e0b\u53bb\u4e86\uff0c\u5f97\u65e9\u4e9b\u60f3\u6cd5\u5b50\u963b\u6b62\u624d\u884c\u3002\u201d \u3000\u3000\u201c\u95ee\u9898\u662f\u4e0d\u80fd\u73b0\u5728\u5c31\u963b\u6b62\u5979\u4eec\uff0c\u8001\u5b9e\u8bf4\uff0c\u5bf9\u4ed8\u5f3a\u654c\u9ad8\u624b\uff0c\u541f\u96ea\u3001\u541f\u971c\u7684\u5a01\u529b\uff0c\u5df2\u8d85\u8d8a\u4e86\u6c5f\u67ab\uff0c\u5982\u679c\u73b0\u5728\u5c31\u963b\u6b62\u5979\u4eec\uff0c\u5373\u5c06\u6765\u4e34\u7684\u4e00\u573a\u5927\u6218\uff0c\u5c31\u4f1a\u5168\u538b\u5728\u6c5f\u67ab\u7684\u80a9\u4e0a\uff0c\u2026\u2026\u201d \u3000\u3000\u201c\u8fd9\u4e5f\u4e0d\u8981\u2026\u2026\u201d\u5f20\u56db\u59d1\u63a5\u9053\uff1a\u201c\u8981\u60f3\u4e2a\u4e24\u5168\u7684\u529e\u6cd5\u624d\u884c\u3002\u201d \u3000\u3000\u201c\u6ca1\u6709\u4e24\u4e2a\u5176\u7f8e\u7684\u529e\u6cd5\uff0c\u552f\u4e00\u4e4b\u7b56\uff0c\u53ea\u6709\u51cf\u5c11\u5979\u4eec\u51fa\u52a8\u5bf9\u654c\u7684\u6b21\u6570\uff0c\u5c31\u548c\u5c1a\u89c2\u5bdf\u6240\u5f97\uff0c\u5979\u4eec\u4e00\u6740\u5165\u9b54\u6027\u7acb\u523b\u9ad8\u6da8\uff0c\u5fc3\u4e2d\u7684\u5992\u3001\u55d4\u4e4b\u5ff5\uff0c\u4e5f\u968f\u4e4b\u4e0a\u5347\uff0c\u6027\u5411\u4e5f\u5f00\u59cb\u504f\u6fc0\u3001\u591a\u7591\u3001\u51b7\u50b2\uff0c\u4e00\u526f\u516d\u4eb2\u4e0d\u8ba4\u7684\u795e\u6001\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u786e\u662f\u5982\u6b64\u3002\u201d \u3000\u3000\u201c\u518d\u4e0b\u53bb\uff0c\u9b54\u6027\u6e10\u6df1\uff0c\u4f1a\u9010\u6e10\u53d6\u4ee3\u672c\u6027\uff0c\u7b49\u5230\u5979\u4eec\u6068\u5145\u5fc3\u5934\uff0c\u65e0\u6cd5\u63a7\u5236\u65f6\uff0c\u5f88\u53ef\u80fd\u771f\u7684\u516d\u4eb2\u4e0d\u8ba4\u4e86\u3002 \u3000\u3000\u90a3\u65f6\uff0c\u5979\u4eec\u5df2\u65a9\u60c5\u3001\u706d\u6027\u3001\u65ad\u4e49\u3001\u7edd\u4eb2\uff0c\u4e0d\u5141\u8bb8\u4efb\u4f55\u4eba\u5fe4\u9006\u5979\u4eec\uff0c\u66f4\u4e0d\u5141\u8bb8\u53ef\u80fd\u7684\u5bf9\u624b\u5b58\u5728\u2026\u2026\u201d\u5927\u548c\u5c1a\u53f9\u65e5\u6c14\u53c8\u9053\uff1a\u201c\u6c5f\u67ab\u5c31\u6210\u4e86\u5979\u4eec\u5fc5\u6740\u7684\u5bf9\u8c61\uff0c\u6c5f\u67ab\u4e0d\u53cd\u6297\u5c31\u5f97\u88ab\u6740\uff0c\u8981\u53cd\u6297\uff0c\u5c31\u662f\u4e00\u573a\u7231\u4fa3\u76f8\u6b8b\u7684\u4eba\u95f4\u60b2\u5267\u4e86\u3002\u201d \u3000\u3000\u201c\u6c5f\u67ab\u80fd\u4e0d\u80fd\u5236\u670d\u5979\u4eec\u5462\uff1f\u201d \u3000\u3000\u5f20\u56db\u59d1\u7d27\u5f20\u7684\u7c89\u988a\u4e0a\u89c1\u4e86\u6c57\u6c34\u3002 \u3000\u3000\u201c\u6c5f\u67ab\u501f\u5b9d\u5203\u4e4b\u529b\uff0c\u5168\u529b\u53cd\u51fb\uff0c\u53ef\u80fd\u6740\u4e86\u5979\u4eec\uff0c\u4f46\u7edd\u4e0d\u80fd\u4f7f\u5979\u4eec\u675f\u624b\u5c31\u7f1a\uff0c\u5982\u662f\u66f4\u4e0d\u5e78\u7684\u6c5f\u67ab\u6b7b\u4f24\u4e8e\u5979\u4eec\u624b\u4e2d\uff0c\u7f57\u5170\u3001\u6b27\u9633\u662d\uff0c\u4f60\u5f20\u59d1\u5a18\uff0c\u90fd\u5c06\u96be\u9003\u52ab\u8fd0\uff0c\u6740\u5c3d\u4eb2\u4eba\uff0c\u5979\u4eec\u624d\u6210\u4e86\u771f\u6b63\u7684\u9b54\u5973\u2026\u2026\u201d\u5927\u548c\u5c1a\u4e5f\u6dcc\u4e0b\u4e86\u4e00\u8138\u6c57\u6c34\u53c8\u9053\uff1a\u201c\u4ee5\u540e\uff0c\u5979\u4eec\u4f1a\u53d8\u6210\u4ec0\u4e48\u6837\u5b50\uff1f\u548c\u5c1a\u4e0d\u80fd\u6d4b\u65ad\uff0c\u4f46\u968f\u5fc3\u6240\u6b32\uff0c\u51fa\u624b\u6740\u4eba\uff0c\u662f\u4e0d\u5728\u8bdd\u4e0b\u4e86\u3002\u201d \u3000\u3000\u201c\u770b\u5230\u5979\u4eec\u521a\u624d\u7740\u9b54\u7684\u795e\u8272\uff0c\u4e0d\u76f8\u4fe1\uff0c\u4e5f\u4e0d\u884c\u4e86\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u73b0\u5728\uff0c\u53ea\u6709\u966a\u7740\u5979\u4eec\u5b81\u9759\u7684\u751f\u6d3b\u51e0\u5929\uff0c\u4e0d\u8ba9\u5979\u4eec\u518d\u51fa\u52a8\u5bf9\u654c\u3002\u201d \u3000\u3000\u201c\u7559\u5979\u4eec\u5230\u6700\u540e\u5bf9\u51b3\u3002\u5979\u4eec\u4e00\u89c1\u9c9c\u8840\uff0c\u9b54\u6027\u7acb\u523b\u53d1\u4f5c\uff0c\u4e00\u6b21\u6bd4\u4e00\u6b21\u7684\u65f6\u95f4\u957f\u2026\u2026\u201d\u5927\u548c\u5c1a\u9053\uff1a\u201c\u5b81\u9759\u4e8c\u5b57\u6700\u4e3a\u91cd\u8981\uff0c\u4e00\u5207\u987a\u7740\u5979\u4eec\uff0c\u4e0d\u8981\u5979\u4eec\u542f\u52a8\u55d4\u6012\uff0c\u5fc3\u9b54\u7684\u53ef\u6015\u5904\uff0c\u5c31\u662f\u4e58\u865a\u800c\u5165\uff0c\u4f60\u548c\u7f57\u5170\uff0c\u8981\u8d1f\u8d77\u8fd9\u4e2a\u8d23\u4efb\uff0c\u91cd\u8981\u7684\u662f\u544a\u8bc9\u7f57\u5170\uff0c\u4e0d\u8981\u6446\u51fa\u8001\u5a18\u7684\u67b6\u5f0f\uff0c\u5979\u4eec\u9b54\u6027\u5df2\u91cd\uff0c\u53d7\u4e0d\u4e86\u4e00\u70b9\u523a\u6fc0\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u6211\u4f1a\u8bf4\u670d\u7f57\u5170\uff0c\u4f46\u6b27\u9633\u662d\u5462\uff1f\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u4ed6\u4eec\u6709\u7236\u5973\u4e4b\u60c5\uff0c\u80fd\u4e0d\u80fd\u89c1\u9762\u5462\uff1f\u201d \u3000\u3000\u201c\u6700\u597d\u522b\u89c1\uff0c\u6b27\u9633\u662d\u90a3\u79cd\u78ca\u843d\u5149\u660e\u7684\u82f1\u96c4\u672c\u8272\uff0c\u5c31\u548c\u9b54\u6027\u76f8\u51b2\uff0c\u5982\u51b0\u70ad\u4e0d\u80fd\u540c\u7089\u3002\u201d \u3000\u3000\u201c\u4f60\u7684\u5927\u60b2\u68b5\u5531\uff0c\u6709\u795b\u9b54\u89e3\u5384\u4e4b\u529b\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u4e3a\u4e24\u4e2a\u5c0f\u4e2b\u5934\u5531\u4e00\u9635\u5462\uff1f\u201d\u5f20\u56db\u59d1\u9053\uff1a \u3000\u3000\u201c\u81f3\u5c11\u53ef\u4ee5\u4f7f\u5979\u4eec\u9b54\u6027\u6d88\u9000\u4e00\u4e9b\u3002\u201d \u3000\u3000\u201c\u5979\u4eec\u5165\u9b54\u7684\u5feb\u901f\uff0c\u5b8c\u5168\u51fa\u4e86\u548c\u5c1a\u7684\u610f\u5916\uff0c\u5927\u60b2\u68b5\u5531\u53ef\u4ee5\u5524\u56de\u5979\u9010\u6e10\u5931\u53bb\u7684\u672c\u6027\uff0c\u4f46\u8981\u5230\u51c6\u5907\u6539\u9020\u5979\u4eec\u65f6\u518d\u7528\uff0c\u548c\u5c1a\u62c5\u5fc3\u7528\u591a\u4e86\uff0c\u523d\u5f15\u8d77\u5979\u4eec\u5fc3\u4e2d\u7684\u6392\u62d2\uff0c\u73b0\u5728\u6d88\u9000\u5979\u4eec\u9b54\u6027\uff0c\u4e5f\u8bb8\u4e0d\u96be\u3002 \u3000\u3000\u53ef\u662f\u518d\u8ba9\u5979\u4eec\u51fa\u624b\u6297\u62d2\u6559\u4e3b\uff0c\u4e00\u756a\u5927\u5c60\u6740\uff0c\u5fc5\u4f7f\u9b54\u6027\u91cd\u590d\uff0c\u5927\u60b2\u68b5\u5531\u662f\u5426\u518d\u80fd\u4f7f\u5979\u4eec\u5b89\u9759\u4e0b\u6765\uff0c\u5c31\u5f88\u96be\u9884\u6599\u4e86\uff0c\u81f3\u5c11\u662f\u6548\u679c\u4e0d\u5f70\u3002\u201d \u3000\u3000\u201c\u5c31\u7167\u4f60\u548c\u5c1a\u7684\u5429\u5490\uff0c\u6211\u53bb\u544a\u8bc9\u7f57\u5170\uff0c\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u522b\u8981\u5979\u5192\u5192\u5931\u5931\u95ef\u51fa\u7978\u6765\u3002\u201d \u3000\u3000\u201c\u56db\u59d1\u3002\u201d \u3000\u3000\u5f20\u56db\u59cb\u8f6c\u8eab\u6b32\u53bb\uff0c\u5927\u548c\u5c1a\u8fd9\u58f0\u56db\u59d1\uff0c\u558a\u7684\u5979\u5fc3\u5934\u4e00\u8df3\u5fd6\u9053\uff1a\u201c\u5982\u4eca\u7684\u9ebb\u70e6\uff0c\u5df2\u95f9\u7684\u4eba\u5598\u4e0d\u8fc7\u6c14\u3002\u5927\u548c\u5c1a\u554a\uff01\u4f60\u53ef\u5343\u4e07\u522b\u4e5f\u52a8\u4e86\u51e1\u5fc3\u3002\u201d \u3000\u3000\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u7f13\u7f13\u8f6c\u8fc7\u8eab\u5b50\uff0c\u9053\uff1a\u201c\u8fd8\u6709\u4ec0\u4e48\u5429\u5490\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u8bdd\uff0c\u771f\u7684\u662f\u4e0d\u597d\u5f00\u53e3\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u5fc3\u5934\u9e7f\u649e\uff0c\u5401\u4e00\u53e3\u6c14\u9053\uff1a\u201c\u96be\u5f00\u53e3\uff0c\u5c31\u4e0d\u8981\u8bf4\u4e86\uff0c\u4ee5\u540e\uff0c\u6709\u673a\u4f1a\u518d\u8bf4\u5427\uff01\u201d \u3000\u3000\u201c\u4e0d\u884c\uff0c\u6b64\u4e8b\u5173\u7cfb\u91cd\u5927\uff0c\u975e\u5f97\u5148\u8bf4\u6e05\u695a\u4e0d\u53ef\uff01\u201d \u3000\u3000\u201c\u4e58\u4eba\u4e4b\u5371\u5440\uff01\u201d\u5f20\u56db\u59d1\u5fc3\u4e2d\u6697\u6697\u6253\u9f13\uff0c\u9053\uff1a\u201c\u597d\u5c31\u8bf4\u5427\uff01\u201d \u3000\u3000\u5fc3\u4e2d\u76d8\u7b97\u7740\u5e94\u4ed8\u4e4b\u7b56\u3002 \u3000\u3000\u201c\u5343\u4e07\u522b\u8ba9\u6c5f\u67ab\u52a8\u4e86\u5979\u4eec\u2026\u2026\u201d\u5927\u548c\u5c1a\u62ed\u4e00\u4e0b\u5934\u4e0a\u6025\u51fa\u7684\u6c57\u6c34\u53c8\u9053\uff1a\u201c\u8981\u4fdd\u6709\u5979\u4eec\u7684\u5904\u5b50\u4e4b\u8eab\uff0c\u4ee5\u52a9\u957f\u5979\u4eec\u7684\u6297\u9b54\u7075\u6167\uff0c\u8fd8\u6709\u4f60\uff0c\u548c\u6c5f\u67ab\u7684\u4eb2\u70ed\u52a8\u4f5c\uff0c\u4e5f\u4e0d\u80fd\u8ba9\u5979\u4eec\u770b\u5230\u3002\u201d \u3000\u3000\u5927\u548c\u5c1a\u8bf4\u7684\u5f88\u5403\u529b\uff0c\u4ed6\u867d\u7136\u4e00\u5411\u6e38\u620f\u98ce\u5c18\uff0c\u4e0d\u62d8\u5c0f\u8282\uff0c\u4f46\u8fd9\u7b49\u7537\u5973\u95f4\u5f88\u76f4\u63a5\u7684\u8a00\u8bed\uff0c\u53ef\u662f\u4ece\u672a\u8bf4\u8fc7\u3002 \u3000\u3000\u5f20\u56db\u59d1\u66f4\u662f\u542c\u5f97\u8138\u4e0a\u53d1\u70e7\uff0c\u5927\u548c\u5c1a\u7ed9\u5979\u5236\u9020\u8fc7\u591a\u6b21\u548c\u6c5f\u67ab\u4eb2\u70ed\u7684\u673a\u4f1a\uff0c\u4f46\u5374\u5c3d\u5728\u4e0d\u8a00\u4e2d\uff0c\u5ffd\u7136\u95f4\u8bf4\u4e86\u51fa\u6765\u3002 \u3000\u3000\u5f20\u56db\u59d1\u4e5f\u7f9e\u5f97\u8138\u7ea2\u5230\u8033\u6839\u540e\u9762\uff0c\u6697\u9053\uff1a\u201c\u5927\u548c\u5c1a\u662f\u6709\u9053\u9ad8\u50e7\u554a\uff01 \u3000\u3000\u6211\u600e\u4e48\u8001\u662f\u7591\u795e\u7591\u9b3c\uff1f\u201d \u3000\u3000\u5927\u548c\u5c1a\u8f6c\u8fc7\u5934\uff0c\u80cc\u5bf9\u7740\u5f20\u56db\u59d1\u63a5\u9053\uff1a\u201c\u6700\u597d\u522b\u8ba9\u6c5f\u67ab\u548c\u4e8c\u5973\u4eb2\u8fd1\uff0c\u6c5f\u67ab\u7ec3\u7684\u6b66\u529f\uff0c\u4e0d\u5fcc\u7537\u5973\u4e4b\u4e8b\uff0c\u800c\u4e14\uff0c\u53cd\u6709\u52a9\u957f\u4ed6\u529f\u529b\u6548\u7528\u3002 \u3000\u3000\u8001\u5b9e\u8bf4\uff0c\u8fd9\u662f\u91d1\u4e39\u4e66\u751f\u548c\u592d\u72d0\u592b\u4eba\u8054\u5408\u52a8\u7684\u624b\u811a\uff0c\u671f\u4f7f\u4ed6\u80fd\u628a\u2018\u60d1\u5fc3\u672f\u2019\u53d1\u6325\u7684\u6dcb\u6f13\u5c3d\u81f4\u3002\u201d \u3000\u3000\u201c\u5982\u679c\uff0c\u4e24\u4e2a\u5c0f\u72d0\u5973\u8981\u89c1\u6c5f\u67ab\u5462\uff1f\u201d\u5f20\u56db\u59d1\u7684\u58f0\u97f3\u5f88\u4f4e\uff0c\u9053\uff1a\u201c\u4ed6\u4eec\u76f8\u5904\u4e86\u90a3\u4e48\u4e45\uff0c\u5c31\u6ca1\u6709\u51fa\u8fc7\u4e8b\u60c5\u3002\u201d \u3000\u3000\u201c\u73b0\u5728\u4e0d\u4e00\u6837\u4e86\u2026\u2026\u201d\u5927\u548c\u5c1a\u9053\uff1a\u201c\u8fc7\u53bb\uff0c\u4ed6\u4eec\u95f9\u5f97\u5929\u7ffb\u5730\u8986\uff0c\u4f46\u51fa\u4e8e\u771f\u60c5\u81f3\u6027\uff0c\u59cb\u7ec8\u80fd\u628a\u6301\u4f4f\u6700\u540e\u4e00\u5173\uff0c\u662f\u6c5f\u67ab\u5fc3\u6709\u6240\u5fcc\u3002 \u3000\u3000\u5982\u4ee4\uff0c\u4e8c\u5973\u9b54\u6027\u5df2\u6df1\uff0c\u52a8\u4e86\u6b32\u706b\uff0c\u9b54\u751f\u73b0\u51fa\uff0c\u4e3b\u52a8\u7684\u5e03\u65bd\u8272\u76f8\uff0c\u4f60\u8bf4\uff0c\u6c5f\u67ab\u771f\u80fd\u591f\u628a\u6301\u5f97\u4f4f\u5417\uff1f \u3000\u3000\u5c31\u7b97\u4ed6\u628a\u6301\u7684\u4f4f\uff0c\u4ed6\u53ef\u80fd\u5f15\u52a8\u4e8c\u5973\u6740\u673a\uff0c\u7a81\u7136\u4e00\u51fb\uff0c\u53ef\u80fd\u4f1a\u8981\u4e86\u6c5f\u67ab\u7684\u547d\uff0c\u56e0\u4e3a\uff0c\u9b54\u6027\u4e00\u53d1\u52a8\uff0c\u4e00\u5207\u5c31\u83ab\u53ef\u9884\u6d4b\u4e86\u3002\u201d \u3000\u3000\u201c\u77e5\u9053\u4e86\uff0c\u77e5\u9053\u4e86\uff01\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u6211\u4f1a\u5168\u529b\u9632\u8303\uff0c\u4e0d\u8ba9\u4ed6\u4eec\u89c1\u9762\u3002\u6211\u4e5f\u8981\u793a\u610f\u6c5f\u67ab\uff0c\u8981\u4ed6\u907f\u5f97\u8fdc\u4e9b\u3002\u201d \u3000\u3000\u201c\u5c31\u662f\u8fd9\u4e9b\u4e86\uff0c\u4f60\u591a\u8f9b\u82e6\uff01\u201d\u5927\u548c\u5c1a\u9053\uff1a\u201c\u522b\u5fd8\u4e86\u590f\u5929\u540c\u548c\u767d\u5929\u5316\uff0c\u8fd8\u5728\u5927\u6bbf\u7b49\u4f60\uff0c\u53ef\u80fd\u4f1a\u63ed\u7a7f\u6559\u4e3b\u7684\u8eab\u4efd\uff1f\u201d \u3000\u3000\u201c\u4e24\u4e2a\u5c0f\u72d0\u5973\u7684\u4e8b\uff0c\u5df2\u7ecf\u95f9\u7684\u6211\u516d\u795e\u65e0\u4e3b\u3002\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u4f60\u548c\u6c5f\u67ab\u53bb\u5427\uff1f\u5927\u548c\u5c1a\uff0c\u4f60\u5927\u667a\u82e5\u611a\uff0c\u5927\u5de7\u82e5\u62d9\uff0c\u6211\u76f8\u4fe1\u4f60\u4f1a\u5904\u7406\u5f97\u6bd4\u6211\u66f4\u597d\uff0c\u7528\u4e0d\u7740\u6211\u53bb\u4e86\u3002\u201d \u3000\u3000\u201c\u9519\u4e86\uff0c\u5f20\u59d1\u5a18\u2026\u2026\u201d\u5927\u548c\u5c1a\u7a81\u7136\u56de\u8fc7\u8eab\u5b50\uff0c\u53cc\u963f\u76ef\u4f4f\u5728\u5f20\u56db\u59d1\u7684\u8138\u4e0a\uff0c\u9053\uff1a\u201c\u7528\u8ba1\u5bf9\u654c\uff0c\u548c\u5c1a\u7edd\u4e0d\u5982\u4f60\uff0c\u6211\u4e5f\u6ca1\u6709\u4f60\u5df2\u6811\u7acb\u8d77\u7684\u6743\u5a01\u3001\u58f0\u8a89\u3002 \u3000\u3000\u6700\u91cd\u8981\u7684\u662f\u5bf9\u4ed8\u6c5f\u67ab\uff0c\u4ed6\u5bf9\u96ea\u3001\u971c\u6709\u60c5\uff0c\u53ef\u4ee5\u5fcd\u53d7\u5979\u4eec\u7684\u7f9e\u8fb1\u3001\u5632\u5f04\uff0c\u53ef\u4ee5\u618b\u7740\u4e00\u809a\u5b50\u6c14\uff0c\u88c5\u51fa\u7b11\u5bb9\u3002 \u3000\u3000\u4f46\u4ed6\u6bd5\u7adf\u53ea\u662f\u4e2a\u4e8c\u5341\u4e09\u56db\u7684\u5c0f\u4f19\u5b50\u554a\u3002\u4e00\u53e3\u6028\u6c14\u5168\u53d1\u5728\u590f\u5929\u540c\u548c\u767d\u5929\u5316\u7684\u8eab\u4e0a\uff0c\u6740\u4e86\u4ed6\u4eec\u4e8b\u5c0f\uff0c\u5bb3\u4e86\u5927\u5c40\uff0c\u548c\u5c1a\u53ef\u62c5\u5f85\u4e0d\u8d77\uff0c\u4f60\u5f20\u59d1\u5a18\u5750\u90a3\u91cc\uff0c\u767e\u90aa\u56de\u907f\uff0c\u6c5f\u67ab\u5c31\u706b\u4e0d\u8d77\u6765\uff0c\u5c31\u7b97\u4e00\u8154\u6012\u706b\uff0c\u4e5f\u4e0d\u6562\u53d1\u51fa\u6765\u3002\u201d \u3000\u3000\u201c\u522b\u628a\u6211\u6367\u7684\u592a\u9ad8\uff0c\u6c5f\u673a\u771f\u8981\u6bdb\u71e5\u8d77\u6765\uff0c\u6211\u4e5f\u4e0d\u4e00\u5b9a\u80fd\u5236\u5f97\u4f4f\u4ed6\uff0c\u4ed6\u662f\u7edf\u5e05\u554a\uff01\u201d \u3000\u3000\u201c\u5f20\u59d1\u5a18\u2026\u2026\u201d\u5927\u548c\u5c1a\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u767e\u91cc\u884c\u7a0b\u534a\u4e5d\u5341\uff0c\u6700\u540e\u8fd9\u4e00\u523b\uff0c\u4e0d\u80fd\u6709\u6240\u758f\u5931\uff0c\u5b59\u609f\u7a7a\u5927\u95f9\u592d\u5bab\uff0c\u5341\u4e07\u5929\u5175\u5929\u5c06\u6321\u4e0d\u4f4f\uff0c\u53ef\u5c31\u662f\u7ffb\u4e0d\u8fc7\u5982\u6765\u4f5b\u4e4b\u624b\u638c\u5fc3\uff0c\u4f60\u5c31\u4e0d\u7528\u8c26\u865a\u4e86\uff0c\u80fd\u8005\u591a\u52b3\u561b\uff1f\u201d \u3000\u3000\u5f20\u56db\u59d1\u7684\u9669\u53c8\u7ea2\u4e86\uff0c\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u8bf4\u7684\u4e5f\u662f\u554a\uff01\u5c0f\u60c5\u90ce\u786e\u662f\u5bf9\u6211\u767e\u4f9d\u767e\u987a\uff0c\u4f46\u4e5f\u4e0d\u80fd\u758f\u5ffd\u4e86\u4ed6\uff0c\u6b27\u9633\u541f\u96ea\u90a3\u7b49\u7edd\u4e16\u806a\u6167\u7684\u53ef\u4eba\u513f\uff0c\u8bf4\u53d8\u5c31\u53d8\u4e86\uff0c\u6c5f\u67ab\u4e5f\u770b\u8fc7\u2018\u4f20\u771f\u8bb0\u2019\uff0c\u522b\u8981\u4e5f\u5165\u4e86\u9b54\u5883\u624d\u597d\u3002\u201d \u3000\u3000\u626d\u5934\u770b\u53bb\uff0c\u5927\u548c\u5c1a\u5df2\u4e0d\u77e5\u4f55\u65f6\u79bb\u53bb\u3002 \u3000\u3000\u8352\u5e99\u5927\u6bbf\u4e2d\uff0c\u5df2\u626b\u626b\u5e72\u51c0\uff0c\u5201\u9e4f\u7ad9\u5728\u6bbf\u95e8\u53e3\u56db\u4e0b\u5f20\u671b\uff0c\u4e00\u89c1\u5f20\u56db\u59d1\u7acb\u523b\u8fce\u4e86\u4e0a\u53bb\uff0c\u9053\uff1a \u3000\u3000\u201c\u6c5f\u5c11\u4fa0\u8fd8\u6ca1\u6709\u5230\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u7565\u4e00\u6c89\u541f\uff0c\u9063\uff1a\u201c\u8bf7\u4ed6\u4eec\u7b49\u5019\u7247\u523b\uff0c\u6211\u53bb\u627e\u4ed6\u3002\u201d \u3000\u3000\u6c5f\u67ab\u8dea\u5728\u4e00\u5ea7\u65b0\u5806\u7684\u5c0f\u575f\u524d\u9762\uff0c\u4e00\u6839\u6811\u5e72\u524a\u6210\u7684\u6728\u724c\uff0c\u63d2\u5728\u575f\u524d\uff0c\u7528\u6307\u529b\u5199\u9053\uff1a\u4ea1\u59bb\u6885\u82b1\u4e4b\u5893\uff0c\u4e08\u592b\u6c5f\u67ab\u53e9\u7acb\u3002 \u3000\u3000\u4ed6\u6ca1\u6709\u54ed\uff0c\u53ea\u662f\u9759\u9759\u7684\u8dea\u5728\u575f\u524d\uff0c\u4f46\u8eab\u4e0a\u6563\u51fa\u6765\u7684\u54c0\u75db\uff0c\u7adf\u4f7f\u5f20\u56db\u59d1\u4e3a\u4e4b\u5fc3\u788e\u3002 \u3000\u3000\u56e0\u4e3a\uff0c\u90a3\u662f\u771f\u6b63\u7684\u54c0\u601d\u75db\u82e6\uff0c\u5fc3\u6000\u4f0a\u4eba\u53bb\uff0c\u65e0\u8bed\u95ee\u82cd\u5929\u3002 \u3000\u3000\u5f20\u56db\u59d1\u6ca1\u6709\u60ca\u52a8\u6c5f\u67ab\uff0c\u7adf\u4e5f\u8dea\u4e86\u4e0b\u53bb\uff0c\u6697\u6697\u7948\u7977\uff1a\u6885\u82b1\uff0c\u5b89\u5fc3\u53bb\u5427\uff01\u4f60\u7684\u6b7b\uff0c\u662f\u5f20\u59e8\u8ba1\u5212\u7684\u758f\u5931\uff0c\u6211\u4eec\u90fd\u6765\u7684\u6162\u4e00\u6b65\u3002 \u3000\u3000\u6c5f\u67ab\u7f13\u7f13\u56de\u8fc7\u5934\u6765\uff0c\u53cc\u76ee\u9971\u542b\u6cea\u6c34\uff0c\u5fcd\u7740\u6ca1\u6709\u6d41\u4e0b\u6765\uff0c\u9053\uff1a\u201c\u5927\u59d0\uff0c\u6211\u600e\u4e48\u529e\u54ea\uff1f\u6885\u82b1\u6218\u6b7b\uff0c\u96ea\u3001\u971c\u5165\u9b54\uff0c\u6211\u7684\u5fc3\u597d\u75bc\u3001\u597d\u75bc\u3002\u201d \u3000\u3000\u7f13\u7f13\u4f38\u51fa\u7389\u8155\uff0c\u628a\u6c5f\u67ab\u63fd\u5165\u6000\u4e2d\uff0c\u5f20\u56db\u59d1\u4e5f\u6709\u7740\u65e0\u9650\u4f24\u611f\uff0c\u9053\uff1a\u201c\u5c0f\u60c5\u90ce\uff0c\u54ed\u5427\uff01\u54ed\u51fa\u4f60\u5fc3\u4e2d\u7684\u59d4\u5c48\uff0c\u54ed\u51fa\u4f60\u4e07\u5343\u54c0\u4f24\uff0c\u4e0d\u8981\u95f7\u5728\u5fc3\u91cc\u3002\u201d \u3000\u3000\u6c5f\u67ab\u771f\u7684\u54ed\u4e86\u3002 \u3000\u3000\u4f46\u5f20\u56db\u59d1\u5fc3\u60c5\u53cd\u800c\u5b9a\u4e86\u4e0b\u6765\uff0c\u6c5f\u67ab\u5982\u6b64\u591a\u60c5\uff0c\u4e5f\u65e0\u504f\u6fc0\u7684\u53cd\u5e94\uff0c\u5927\u6982\u662f\u771f\u7684\u672a\u7ec3\u9b54\u529f\uff0c\u60f3\u6765\uff0c\u53ef\u80fd\u662f\u65e0\u76f8\u5927\u5e08\u7684\u4f5b\u95e8\u7985\u529f\uff0c\u52a9\u957f\u4e86\u4ed6\u7684\u5b9a\u529b\uff0c\u9762\u4e34\u5173\u5934\uff0c\u6709\u6240\u6289\u62e9\uff0c\u96ea\u3001\u971c\u4e8c\u5973\uff0c\u5c31\u5c11\u4e86\u8fd9\u4efd\u57fa\u7840\uff0c\u53d7\u4e0d\u4f4f\u9b54\u529f\u7684\u8be1\u5947\u8bf1\u60d1\u3002 \u3000\u3000\u75db\u75db\u5feb\u5feb\u54ed\u4e86\u4e00\u9635\uff0c\u5fc3\u60c5\u5e73\u9759\u4e0d\u5c11\uff0c\u6c5f\u67ab\u6536\u4f4f\u773c\u6cea\uff0c\u53d1\u89c9\u5f20\u56db\u59d1\u524d\u80f8\u7684\u8863\u886b\uff0c\u88ab\u6cea\u6c34\u6d78\u6e7f\u4e86\u4e00\u5927\u7247\uff0c\u6709\u4e9b\u4e0d\u597d\u610f\u601d\u4e86\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5927\u59d0\uff0c\u6211\u600e\u4f1a\u6709\u8fd9\u4e48\u591a\u7684\u773c\u6cea\uff1f\u88ab\u4eba\u77e5\u9053\uff0c\u4e00\u5b9a\u4f1a\u7b11\u8bdd\u6211\uff1f\u201d \u3000\u3000\u201c\u5b9d\u8d35\u5440\uff01\u9897\u9897\u773c\u6cea\u90fd\u662f\u7231\uff01\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u5927\u59d0\u8981\u597d\u597d\u6536\u8d77\u8fd9\u4ef6\u8863\u670d\uff0c\u4f46\u7b49\u5927\u4e8b\u5e95\u5b9a\uff0c\u9009\u4e00\u5904\u597d\u98ce\u6c34\uff0c\u518d\u628a\u6885\u82b1\u57cb\u846c\u90a3\u91cc\u3002\u201d\u6c5f\u67ab\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u6211\u7b54\u5e94\u767d\u5929\u5316\u5728\u5927\u6bbf\u89c1\u9762\uff0c\u4ed6\u8bf4\uff0c\u6709\u79d8\u5bc6\u76f8\u544a\u3002\u201d \u3000\u3000\u201c\u6211\u4eec\u4e00\u8d77\u53bb\u3002\u201d\u5f20\u56db\u59d1\u7275\u8d77\u6c5f\u67ab\uff0c\u9053\uff1a\u201c\u96ea\u3001\u971c\u4e8c\u5973\u5165\u9b54\u5f88\u6df1\uff0c\u4f60\u770b\u8fc7\u2018\u4f20\u771f\u8bb0\u2019\uff0c\u4e8b\u60c5\u6709\u591a\u4e25\u91cd\uff1f\u4f60\u5fc3\u4e2d\u5e94\u8be5\u6709\u6570\u3002\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\uff0c\u6740\u4e86\u90a3\u4e2a\u6559\u4e3b\uff0c\u6211\u8981\u4e0d\u60dc\u4e00\u5207\u7684\u633d\u6551\u5979\u4eec\u3002\u201d\u6c5f\u67ab\u9053\u201c\u6211\u8981\u628a\u5979\u4eec\u5e26\u5165\u6df1\u5c71\u7edd\u57df\u4e4b\u4e2d\uff0c\u4e0d\u80fd\u4f7f\u5979\u4eec\u8131\u51fa\u9b54\u9053\uff0c\u6211\u5c31\u966a\u5979\u4eec\u7ec8\u8001\u6df1\u5c71\uff0c\u6c38\u4e0d\u51fa\u4e16\u3002\u201d \u3000\u3000\u201c\u9752\u51e4\u5462\uff1f\u5927\u59d0\u5462\uff1f\u662f\u8981\u6211\u4eec\u966a\u4f60\u7ec8\u8001\u6df1\u5c71\u5462\uff1f\u8fd8\u662f\u4ece\u6b64\u51b3\u7edd\uff0c\u4e0d\u518d\u89c1\u9762\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u5446\u4f4f\u4e86\uff0c\u5fd6\u9053\uff1a\u201c\u662f\u554a\uff01\u9752\u51e4\u4e0d\u80fd\u5f03\uff0c\u5927\u59d0\u66f4\u96be\u820d\uff0c\u8fd8\u6709\u4e3a\u60c5\u5f52\u6b63\u7684\u84dd\u51e4\uff0c\u603b\u4e0d\u80fd\u8ba9\u5979\u4eec\u4e5f\u5377\u8fdb\u53bb\uff0c\u966a\u5165\u6df1\u5c71\uff0c\u4e00\u65e6\u96ea\u3001\u971c\u53d1\u4e86\u9b54\u6027\uff0c\u628a\u5979\u4eec\u5168\u90fd\u6740\u4e86\uff0c\u8be5\u600e\u4e48\u529e\uff1f\u201d\u6c5f\u67ab\u96be\u574f\u4e86\u3002 \u3000\u3000\u201c\u5927\u548c\u5c1a\u7684\u5927\u60b2\u68b5\u5531\uff0c\u80fd\u4e0d\u80fd\u4f7f\u96ea\u3001\u971c\u4e8c\u5973\u8131\u51fa\u9b54\u52ab\uff1f\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u4f20\u771f\u8bb0\u4e0a\uff0c\u6709\u6ca1\u6709\u6446\u8131\u9b54\u529f\u7684\u6cd5\u5b50\uff1f\u201d \u3000\u3000\u201c\u5bf9\u554a\uff01\u5927\u60b2\u68b5\u5531\uff0c\u5e94\u8be5\u6709\u7528\u3002\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u88ab\u590f\u5929\u540c\uff0c\u8680\u5fc3\u9b54\u97f3\uff0c\u675f\u7f1a\u5341\u51e0\u5e74\u7684\u795e\u5fd7\uff0c\u95fb\u58f0\u800c\u9192\uff0c\u6211\u771f\u7684\u6025\u7cca\u6d82\u4e86\uff0c\u8fd9\u4e2a\u5927\u548c\u5c1a\uff0c\u4e0d\u4f46\u627f\u7ee7\u4e86\u5e08\u7236\u65e0\u76f8\u5927\u5e08\u7684\u6b66\u529f\uff0c\u4e5f\u627f\u7ee7\u4e86\u8001\u4eba\u5bb6\u7684\u4f5b\u6cd5\u7985\u5b97\uff0c\u5c11\u6797\u5bfa\u5916\uff0c\u5df2\u4f7f\u96ea\u3001\u971c\u95fb\u58f0\u53e9\u62dc\uff0c\u81f3\u4e8e\u2018\u4f20\u771f\u8bb0\u2019\u4e0a\uff0c\u5374\u6ca1\u6709\u63d0\u5230\u6446\u8131\u9b54\u6027\u7684\u65b9\u6cd5\u3002\u201d \u3000\u3000\u201c\u6211\u5728\u60f3\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u6280\u827a\u7684\u672c\u8eab\uff0c\u5e94\u65e0\u90aa\u6b63\u4e4b\u5206\uff0c\u95ee\u9898\u5728\u7ec3\u529f\u7684\u65b9\u6cd5\uff0c\u5982\u80fd\u627e\u51fa\u5b83\u5f0a\u75c5\u6240\u5728\uff0c\u6216\u53ef\u4ece\u6839\u672c\u4e0a\u89e3\u6551\u4e8c\u5973\uff0c\u4e5f\u53ef\u4ee5\u4fdd\u7559\u8fd9\u7b49\u795e\u5947\u7684\u6280\u827a\u3002\u201d \u3000\u3000\u201c\u6211\u4e0d\u77e5\u9053\uff0c\u662f\u5426\u80fd\u53c2\u609f\u51fa\u8fd9\u79cd\u529e\u6cd5\uff0c\u4f46\u4f20\u771f\u8bb0\u4e0a\u7684\u9b54\u529f\uff0c\u7ec3\u5230\u6781\u81f4\uff0c\u597d\u50cf\u8131\u79bb\u4e86\u4eba\u7684\u8303\u56f4\uff0c\u4eba\u5982\u9b3c\u9b41\uff0c\u6765\u53bb\u5982\u98ce\uff0c\u5316\u8eab\u8bf1\u654c\uff0c\u6740\u4eba\u4e8e\u4e0d\u77e5\u4e0d\u89c9\u4e4b\u4e2d\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u5fc3\u6027\u591a\u7591\uff0c\u559c\u6012\u65e0\u5e38\uff0c\u8fd9\u5c31\u662f\u9b54\u9053\u4e86\u3002\u201d \u3000\u3000\u201c\u5979\u4eec\u4f1a\u4e0d\u4f1a\u6740\u4f60\u5462\uff1f\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u5982\u679c\u8981\u6740\uff0c\u80fd\u4e0d\u80fd\u6740\u5f97\u4e86\uff1f\u201d \u3000\u3000\u201c\u5982\u679c\u6211\u89e6\u72af\u4e86\u5979\u4eec\uff0c\u6fc0\u8d77\u6012\u706b\uff0c\u5979\u4eec\u4f1a\u6740\u6211\uff0c\u56e0\u4e3a\uff0c\u5165\u9b54\u592a\u6df1\u65f6\uff0c\u5979\u4eec\u65e0\u6cd5\u63a7\u5236\u81ea\u5df1\u2026\u2026\u201d\u6c5f\u67ab\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u6211\u5982\u5168\u529b\u6212\u5907\uff0c\u51fa\u624b\u5c01\u6321\uff0c\u81ea\u7136\u53ef\u4ee5\u963b\u5979\u4eec\u6740\u6211\uff0c\u4f46\u6211\u4e0d\u80fd\u65f6\u65f6\u9884\u9632\uff0c\u523b\u523b\u6212\u5907\uff0c\u548c\u5979\u4eec\u957f\u65e5\u76f8\u5904\uff0c\u786e\u5b9e\u5145\u6ee1\u7740\u51f6\u9669\uff0c\u4e0d\u8fc7\uff0c\u6211\u53ef\u4ee5\u5c3d\u91cf\u987a\u7740\u5979\u4eec\u3002\u201d \u3000\u3000\u201c\u90a3\u5c31\u5371\u9669\u4e86\uff0c\u672a\u66fe\u8fd1\u8fc7\u5973\u8272\u7684\u5927\u548c\u5c1a\uff0c\u771f\u662f\u65c1\u89c2\u8005\u6e05\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u5fc3\u4e2d\u6697\u6697\u5fd6\u601d\uff0c\u53e3\u4e2d\u5374\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u5728\u4e00\u8d77\u53ae\u95f9\u60ef\u4e86\uff0c\u6c5f\u5c71\u4e0d\u6539\uff01\u65e5\u989c\u8272\uff0c\u7ea0\u7f20\u4e00\u5904\uff0c\u5c31\u591a\u4e86\u6fc0\u6012\u5979\u4eec\u7684\u673a\u4f1a\uff0c\u8fd8\u662f\u907f\u5f00\u5979\u4eec\u51e0\u5929\uff0c\u51b3\u6218\u4e4b\u540e\uff0c\u518d\u548c\u5979\u4eec\u89c1\u9762\uff0c\u90a3\u65f6\u523b\u4f60\u4e5f\u53ef\u5168\u529b\u5168\u610f\u53bb\u5e2e\u52a9\u5979\u4eec\uff0c\u518d\u4ee5\u5927\u548c\u5c1a\u7684\u5927\u60b2\u68b5\u5531\u76f8\u52a9\uff0c\u4e5f\u8bb8\u53ef\u4ee5\u4f7f\u5979\u4eec\u8fd4\u749e\u5f52\u771f\uff0c\u9b54\u6027\u6d88\u9664\u3002\u201d \u3000\u3000\u201c\u5927\u59d0\u7684\u610f\u601d\uff0c\u53ef\u662f\u8bf4\uff0c\u73b0\u5728\u8fd8\u4e0d\u80fd\u6d88\u9664\u5979\u4eec\u7684\u9b54\u6027\u4e86\u3002\u201d \u3000\u3000\u201c\u84dd\u51e4\u4f20\u6765\u7684\u8baf\u606f\uff0c\u4f60\u90fd\u542c\u5230\u4e86\uff0c\u5bf9\u4ed8\u90a3\u4f4d\u795e\u79d8\u6559\u4e3b\u548c\u6765\u81ea\u96f7\u97f3\u5bfa\u7684\u9ad8\u624b\u3001\u9b54\u5973\uff0c\u53ea\u6015\u8fd8\u5f97\u96ea\u971c\u4e8c\u5973\u5168\u529b\u52a9\u4f60\u3002 \u3000\u3000\u73b0\u5728\u5c31\u6d88\u9664\u5979\u4eec\u7684\u9b54\u6027\uff0c\u53ef\u80fd\u4f1a\u5f71\u54cd\u5230\u5979\u4eec\u7684\u8eab\u624b\uff0c\u4f55\u51b5\uff0c\u4f60\u4e5f\u5206\u8eab\u4e4f\u672f\uff0c\u65e0\u6cd5\u7167\u987e\u5979\u4eec\u3002\u201d \u3000\u3000\u201c\u5509\uff01\u5927\u59d0\u60f3\u7684\u6bd4\u6211\u5468\u5230\uff0c\u5c0f\u5f1f\u552f\u547d\u662f\u4ece\u4e86\u3002\u201d \u3000\u3000\u539f\u60f3\u963b\u6b62\u6c5f\u67ab\u548c\u4e8c\u5973\u89c1\u9762\uff0c\u662f\u4e00\u4ef6\u5f88\u56f0\u96be\u7684\u4e8b\uff0c\u5fc5\u5f97\u5927\u8d39\u4e00\u756a\u5507\u820c\uff0c\u60f3\u4e0d\u5230\u4e09\u8a00\u4e24\u8bed\uff0c\u5c31\u628a\u4e8b\u60c5\u6446\u5e73\u4e86\u3002 \u3000\u3000\u5f20\u56db\u59d1\u7a81\u7136\u4f38\u51fa\u624b\u53bb\uff0c\u63e1\u4f4f\u4e86\u6c5f\u67ab\u5de6\u8155\uff0c\u9053\uff1a\u201c\u5c0f\u60c5\u90ce\uff0c\u5927\u59d0\u4e00\u5b9a\u4f1a\u5c3d\u6211\u5fc3\u529b\uff0c\u4f46\u5929\u610f\u96be\u6d4b\uff0c\u5927\u548c\u5c1a\u4e5f\u4fdd\u8bc1\u5168\u529b\u4ee5\u8d74\uff0c\u4e07\u4e00\u6709\u4ec0\u4e48\u96be\u6d4b\u4e4b\u53d8\uff0c\u4f60\u4e00\u5b9a\u8981\u51b7\u9759\u5bf9\u4ed8\uff0c\u4f60\u8981\u77e5\u9053\uff0c\u4f60\u662f\u552f\u4e00\u80fd\u591f\u5728\u6700\u5371\u9669\u65f6\u523b\uff0c\u80fd\u6551\u5979\u4eec\u7684\u4eba\uff0c\u5982\u662f\u60c5\u6025\u5931\u5e38\uff0c\u90a3\u5c31\u5168\u76d8\u7686\u8f93\u3002 \u3000\u3000\u6b64\u65f6\u6b64\u523b\uff0c\u65e2\u65e0\u6cd5\u963b\u6b62\u5979\u4eec\u7684\u9b54\u6027\u8fdb\u5c55\uff0c\u4f60\u5c31\u8981\u5b8c\u5168\u5fcd\u53d7\u8fd9\u4efd\u76f8\u601d\u4e4b\u82e6\u3002\u201d \u3000\u3000\u201c\u5c0f\u5f1f\u7b54\u5e94\u4e86\uff0c\u4e0d\u7ba1\u5982\u4f55\u75db\u82e6\uff0c\u5c0f\u5f1f\u4e00\u5b9a\u9075\u4ece\u5927\u59d0\u4e4b\u547d\u2026\u2026\u201d \u3000\u3000\u201c\u5982\u679c\u662f\u5979\u4eec\u627e\u4e0a\u4f60\u5462\uff1f\u201d\u5f20\u56db\u59d1\u7d27\u76ef\u7740\u95ee\u3002 \u3000\u3000\u201c\u8fd9\u4e2a\uff0c\u5c0f\u5f1f\u5c31\u4e0d\u77e5\u6240\u63aa\u4e86\u3002\u201d \u3000\u3000\u201c\u6c5f\u67ab\uff01\u201d\u5f20\u56db\u59d1\u795e\u60c5\u4e25\u8083\u5730\u9053\uff1a\u201c\u4e07\u4e00\u5979\u4eec\u627e\u4e0a\u4e86\u4f60\uff0c\u800c\u4e14\u8981\u4ee5\u8eab\u5e03\u65bd\uff0c\u4f60\u8981\u5982\u4f55\u5e94\u4ed8\u5462\uff1f\u201d \u3000\u3000\u201c\u5c0f\u5f1f\u4e0d\u77e5\u9053\uff0c\u8fc7\u53bb\uff0c\u5c0f\u5f1f\u4e00\u76f4\u80fd\u60ac\u5d16\u52d2\u9a6c\uff0c\u4f46\u73b0\u5728\uff0c\u5979\u4eec\u559c\u6012\u65e0\u6cd5\u9884\u6d4b\uff0c\u5979\u4eec\u8981\u4ec0\u4e48\uff1f\u5c0f\u5f1f\u90fd\u4e0d\u80fd\u62d2\u7edd\uff0c\u53ea\u6709\u52c9\u4e3a\u5176\u96be\u4e86\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u4e0d\u884c\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\uff0c\u5979\u4eec\u6709\u65f6\u6e05\u9192\u5982\u5e38\uff0c\u4fdd\u6301\u4e86\u4e00\u62b9\u7075\u5149\u4e0d\u6627\uff0c\u53ea\u5728\u4e0d\u6740\u4eba\uff0c\u6ca1\u6709\u592a\u5927\u7684\u523a\u6fc0\uff0c\u8fd8\u80fd\u7eaf\u7eaf\u5982\u6c34\uff0c\u662f\u4ec0\u4e48\u539f\u56e0\u5417\uff1f\u201d \u3000\u3000\u201c\u96be\u9053\u8fd9\u79cd\u4e8b\u5979\u4eec\u4ecd\u662f\u4e0e\u6e05\u767d\u7684\u8eab\u4f53\u6709\u5173\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u5143\u9634\u4e0d\u5931\uff0c\u6e05\u54c1\u9ad8\u5343\uff0c\u4e00\u65e6\u5979\u4eec\u5931\u53bb\u4e86\u8fd9\u70b9\u81ea\u6301\u2026\u2026\u201d \u3000\u3000\u5f20\u56db\u59d1\u9053\uff1a\u201c\u5f88\u53ef\u80fd\u4f1a\u968f\u6ce2\u9010\u6d41\uff0c\u6240\u8c13\u9b54\u6027\u6deb\u6076\uff0c\u8eab\u5b50\u5df2\u7834\uff0c\u6b32\u5ff5\u5e38\u8d77\uff0c\u627e\u4e0d\u5230\u4f60\u65f6\uff0c\u4e5f\u53ef\u80fd\u627e\u4e0a\u522b\u4eba\u3002 \u3000\u3000\u5979\u4eec\u9b54\u529f\u5230\u4e86\u67d0\u4e00\u79cd\u5883\u754c\uff0c\u60c5\u4e4b\u4e00\u5b57\uff0c\u5df2\u4e0d\u80fd\u675f\u7f1a\u5979\u4eec\uff0c\u4f60\u8bf4\u5427\uff1f\u90a3\u4f1a\u662f\u4e2a\u4ec0\u4e48\u5c40\u9762\u5462\uff1f\u201d \u3000\u3000\u8fd9\u4e9b\u8bdd\uff0c\u53ea\u662f\u5f20\u56db\u59d1\u968f\u53e3\u7f16\u9020\u7684\uff0c\u5979\u4e0d\u80fd\u628a\u5927\u548c\u5c1a\u7684\u8bdd\uff0c\u636e\u5b9e\u8f6c\u544a\uff0c\u4f9d\u60c5\u63a8\u6f14\uff0c\u8bf4\u7684\u662f\u534a\u771f\u534a\u5047\u3002 \u3000\u3000\u4f46\u6c5f\u67ab\u5df2\u542c\u51fa\u7531\u5fc3\u5e95\u6cdb\u5347\u8d77\u4e00\u80a1\u5bd2\u610f\uff0c\u5a07\u59bb\u7ea2\u674f\u51fa\u5899\uff0c\u90a3\u53ef\u662f\u4eba\u751f\u4e00\u5927\u61be\u4e8b\uff0c\u4f46\u9519\u53c8\u4e0d\u5728\u96ea\u3001\u971c\u4e8c\u5973\u8eab\u4e0a\uff0c\u5165\u9b54\u592a\u6df1\uff0c\u672c\u6027\u5df2\u5931\uff0c\u706b\u8d77\u6765\u8fde\u4eb2\u4eba\u90fd\u53ef\u4ee5\u6740\u4e86\uff0c\u4f55\u51b5\u662f\u627e\u4e2a\u7537\u4eba\u73a9\u73a9\u5462\uff01 \u3000\u3000\u73b0\u5728\uff0c\u5979\u4eec\u8fd8\u80fd\u4e0d\u53ca\u6deb\u4e71\uff0c\u662f\u56e0\u4e3a\u4fdd\u6709\u4e86\u5904\u5b50\u4e4b\u8eab\uff0c\u5e74\u5c11\u4e0d\u8bc6\u60c5\u6ecb\u5473\uff0c\u4e00\u65e6\u7834\u4e86\u8eab\uff0c\u90a3\u5c31\u4e0d\u582a\u8bbe\u60f3\u4e86\u3002 \u3000\u3000\u6c5f\u67ab\u81ea\u4f5c\u4e00\u756a\u63a8\u8bba\uff0c\u51b7\u6c57\u6d94\u6d94\u800c\u4e0b\uff0c\u9053\uff1a\u201c\u5927\u59d0\u8bf4\u7684\u5bf9\uff0c\u5927\u654c\u672a\u706d\u4e4b\u524d\uff0c\u6211\u4e0d\u80fd\u518d\u89c1\u5979\u4eec\uff0c\u6211\u8981\u8eb2\u5f00\u548c\u5979\u4eec\u5355\u72ec\u76f8\u5904\u7684\u673a\u4f1a\u3002\u201d \u3000\u3000\u76ee\u7779\u5c0f\u60c5\u90ce\u60ca\u614c\u4e4b\u60c5\uff0c\u5f20\u56db\u59d1\u611f\u6168\u5f88\u6df1\uff0c\u5fd6\u9053\uff1a\u201c\u7537\u4eba\u554a\uff01\u4f3c\u662f\u90fd\u5f88\u81ea\u79c1\uff0c\u628a\u5fc3\u7231\u7684\u5973\u4eba\u89c6\u82e5\u7981\u8114\uff0c\u4e0d\u8bb8\u4ed6\u4eba\u67d3\u6307\uff0c\u4e5f\u60f3\u5c3d\u4e86\u5404\u79cd\u529e\u6cd5\uff0c\u6811\u7acb\u8d77\u660e\u6559\u3001\u5178\u4f8b\uff0c\u628a\u5973\u6027\u7262\u56fa\u7684\u9501\u5165\u6df1\u95fa\uff0c\u76f8\u592b\u6559\u5b50\uff0c\u6d17\u624b\u4f5c\u7fb9\u6c64\uff0c\u62c8\u7ebf\u7f1d\u8863\u5fd9\uff0c\u826f\u4eba\u8fdc\u884c\uff0c\u53ef\u4ee5\u5728\u5916\u9762\u82b1\u5929\u9152\u5730\uff0c\u6df1\u95fa\u5bc2\u5bc2\uff0c\u4fcf\u4f73\u4eba\u53ea\u80fd\u4f34\u5b64\u887e\u5bd2\u6795\uff0c\u6c5f\u67ab\u662f\u5929\u72d0\u95e8\u4e2d\u5f1f\u5b50\uff0c\u4e5f\u96be\u514d\u6b32\uff0c\u4e0d\u5bb9\u4ed6\u4eba\u67d3\u6307\u7231\u4fa3\uff0c\u770b\u4ed6\u5bf9\u6885\u82b1\u4e00\u5f80\u60c5\u6df1\uff0c\u5bf9\u9752\u51e4\u4e5f\u6709\u7740\u65e0\u9650\u5173\u7231\uff0c\u5bf9\u4e24\u4e2a\u5c0f\u72d0\u5973\u66f4\u662f\u7231\u5982\u81f3\u5b9d\uff0c\u5bf9\u6211\u662f\u767e\u4f9d\u767e\u987a\uff0c\u6c5f\u67ab\u554a\uff1f \u3000\u3000\u4f60\u80fd\u6709\u6df1\u60c5\u51e0\u8bb8\uff0c\u80fd\u5206\u7ed9\u8fd9\u4e48\u591a\u5973\u4eba\u5206\u4eab\u5462\uff1f\u201d \u3000\u3000\u201c\u5927\u59d0\uff0c\u4f60\u5728\u60f3\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u6211\u5728\u60f3\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u5982\u4f55\u624d\u80fd\u4f7f\u4e24\u4e2a\u4e2b\u5934\u5e73\u9759\u4e0b\u6765\uff0c\u5979\u4eec\u8fd1\u534a\u5e74\uff0c\u548c\u4f60\u671d\u5915\u76f8\u5904\uff0c\u4e00\u65e6\u4e0d\u8ba9\u5979\u4eec\u548c\u4f60\u89c1\u9762\uff0c\u53ef\u4e0d\u662f\u4e00\u4ef6\u5bb9\u6613\u7684\u4e8b\u3002\u201d \u3000\u3000\u201c\u662f\u554a\uff01\u5927\u59d0\uff0c\u4e00\u65e6\u5979\u4eec\u70e6\u6101\u8fc7\u5ea6\uff0c\u4e5f\u4f1a\u5f15\u53d1\u9b54\u6027\uff0c\u6240\u8c13\u9b54\u6027\uff0c\u5c31\u662f\u968f\u5fc3\u6240\u6b32\uff0c\u4e0d\u7ba1\u522b\u4eba\u6b7b\u6d3b\uff0c\u53ea\u6c42\u4e00\u5df1\u5feb\u4e50\uff0c\u4ec0\u4e48\u793c\u6559\u3001\u9996\u5148\uff0c\u5168\u4e0d\u653e\u5728\u5fc3\u4e0a\u3002\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u60f3\u5230\u4ec0\u4e48\uff0c\u5c31\u505a\u4ec0\u4e48\uff0c\u6ca1\u6709\u662f\u975e\uff0c\u4e0d\u8bba\u5584\u6076\uff0c\u66f4\u522b\u8bf4\u5929\u7406\u3001\u56fd\u6cd5\u3001\u4eba\u60c5\u4e86\u3002\u201d \u3000\u3000\u201c\u771f\u7684\uff01\u201d\u8fd9\u4e00\u4e0b\uff0c\u8f6e\u5230\u5f20\u56db\u59d1\u5403\u60ca\u4e86\u3002 \u3000\u3000\u6c5f\u67ab\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u662f\u771f\u7684\u3002\u201d \u3000\u3000\u201c\u8fd9\u5012\u771f\u8981\u8d39\u756a\u5fc3\u601d\uff01\u8d70\uff01\u542c\u5b8c\u767d\u5929\u5316\u7684\u79d8\u5bc6\uff0c\u6211\u8981\u53bb\u597d\u597d\u7167\u987e\u5979\u4eec\u3002\u201d \u3000\u3000\u5927\u6bbf\u4e0a\u9ad8\u70e7\u7740\u4e24\u53ea\u706b\u70db\uff0c\u4e00\u5f20\u5706\u684c\u4e0a\uff0c\u8fd8\u94fa\u4e86\u4e00\u5757\u5927\u7ea2\u5e03\uff0c\u684c\u4e0a\u6446\u4e86\u9e21\u3001\u9e2d\u3001\u725b\u8089\uff0c\u867d\u7136\u90e8\u662f\u51b7\u83dc\uff0c\u53ef\u4e5f\u8089\u9999\u6251\u9f3b\uff0c\u8fd8\u6709\u4e24\u575b\u8001\u9152\u3002 \u3000\u3000\u767d\u5929\u5316\u3001\u590f\u5929\u540c\u3001\u4e09\u5927\u697c\u4e3b\u3001\u7533\u96ea\u541b\u3001\u5927\u548c\u5c1a\u3001\u8001\u53eb\u5316\uff0c\u65e9\u5df2\u5165\u5ea7\u7b49\u5019\uff0c\u5201\u9e4f\u5b88\u5728\u6bbf\u5916\u8b66\u6212\uff0c\u8fce\u6625\u5728\u4e00\u4fa7\u4f3a\u9152\u3002 \u3000\u3000\u5f20\u56db\u59d1\u548c\u6c5f\u67ab\u5165\u5ea7\uff0c\u4e00\u4e2a\u4eba\u56f4\u5750\u4e00\u684c\u3002 \u3000\u3000\u9152\u8fc7\u4e09\u5de1\uff0c\u5f20\u56db\u59d1\u76ee\u5149\u73af\u987e\u4e86\u5168\u573a\u4e00\u773c\uff0c\u9053\uff1a\u201c\u59be\u8eab\u8fd8\u4e0d\u77e5\u9053\u90a3\u4f4d\u795e\u79d8\u6559\u4e3b\u7684\u8eab\u4efd\uff0c\u4f46\u5374\u77e5\u9053\uff0c\u4ed6\u5df2\u9080\u7ea6\u5230\u5c0f\u897f\u5929\u96f7\u97f3\u5bfa\u56db\u5927\u795e\u9b54\u52a9\u9635\uff0c\u968f\u6765\u7684\u4e5d\u4f4d\u9b54\u5973\uff0c\u80fd\u6446\u51fa\u4e00\u5ea7\u5929\u9b54\u6d88\u9b42\u5927\u9635\uff0c\u8fd8\u6709\u4e09\u5341\u516d\u4f4d\u9ed1\u8863\u5200\u624b\uff01\u201d \u3000\u3000\u201c\u518d\u52a0\u4e0a\u56db\u5927\u795e\u901a\u4f7f\u8005\uff0c\u548c\u4ed6\u4eb2\u81ea\u7edf\u7387\u7684\u90e8\u4f17\uff0c\u5b9e\u529b\u5341\u5206\u5f3a\u5927\u3002\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u5929\u9a6c\u5802\u4e2d\u4eba\uff0c\u65e9\u5df2\u662f\u4ed6\u51c6\u5907\u6d88\u706d\u7684\u529b\u91cf\uff0c\u4f3c\u672a\u8ba1\u7b97\u5728\u5185\uff0c\u6050\u6015\u5929\u738b\u95e8\u3001\u5929\u540e\u5bab\u4e5f\u4e0d\u7b97\u4ed6\u4eec\u7684\u4e3b\u529b\u4e86\u3002\u201d \u3000\u3000\u201c\u679c\u7136\u662f\u65e9\u6709\u9884\u8c0b\u2026\u2026\u201d\u767d\u5929\u5316\u53f9\u606f\u4e00\u58f0\u53c8\u9053\uff1a\u201c\u6c5f\u6e56\u4e2d\u4eba\u5343\u7b97\u4e07\u8ba1\uff0c\u4e5f\u65e0\u6cd5\u548c\u4ed6\u4eec\u719f\u8bfb\u6218\u7b56\u5175\u7565\u7684\u5fc3\u673a\u6297\u8861\u3002\u201d \u3000\u3000\u6c5f\u67ab\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u4f60\u662f\u8bf4\uff0c\u90a3\u4f4d\u6559\u4e3b\u4e0d\u662f\u6c5f\u6e56\u4e2d\u4eba\u3002\u201d \u3000\u3000\u201c\u8001\u673d\u5728\u795e\u524d\u7acb\u8fc7\u91cd\u8a93\uff0c\u4e0d\u7ba1\u5728\u4efb\u4f55\u9177\u5211\u903c\u8feb\u4e4b\u4e0b\uff0c\u4e5f\u4e0d\u80fd\u6cc4\u6f0f\u4ed6\u7684\u8eab\u4efd\u3002\u201d\u767d\u5929\u5316\u9053\uff1a\u201c\u73b0\u5728\uff0c\u4e5f\u987e\u4e0d\u5f97\u8fd9\u4e48\u591a\u4e86\uff01\u201d \u3000\u3000\u201c\u8fd9\u4e48\u8bf4\uff0c\u4ed6\u4eec\u662f\u671d\u5ef7\u4e2d\u4eba\u4e86\uff1f\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u4e5f\u662f\u6709\u610f\u7684\u6d88\u706d\u6c5f\u6e56\u4eba\u7269\uff1f\u201d \u3000\u3000\u201c\u539f\u672c\u7684\u7528\u5fc3\uff0c\u53ea\u662f\u8981\u6c5f\u6e56\u4e2d\u7ef4\u6301\u4e00\u4e2a\u591a\u52bf\u529b\u7684\u5e73\u8861\u5c40\u9762\u3002\u201d\u767d\u5929\u5316\u9053\uff1a\u201c\u4e0d\u8ba9\u6b66\u6797\u4e2d\u52bf\u529b\uff0c\u96c6\u4e2d\u4e8e\u51e0\u4e2a\u5e2e\u4f1a\uff0c\u81a8\u80c0\u8fc7\u5927\uff0c\u5a01\u80c1\u5230\u671d\u5ef7\u7684\u5b89\u5371\uff0c\u672c\u671d\u5e1d\u4e1a\u521b\u5efa\u548c\u53d8\u8fed\uff0c\u90fd\u548c\u6c5f\u6e56\u4e2d\u4eba\u591a\u6709\u5173\u8fde\u3002 \u3000\u3000\u671d\u5ef7\u4e5f\u6df1\u77e5\u6c5f\u6e56\u4e2d\u4eba\u7684\u53ef\u6015\uff0c\u4fa0\u4ee5\u6b66\u72af\u7981\uff0c\u6240\u4ee5\uff0c\u51b3\u5fc3\u5927\u529b\u652f\u63f4\u4ee5\u5e73\u8861\u6b66\u6797\u52bf\u529b\uff0c\u4e5f\u7f57\u96c6\u4e86\u6c5f\u6e56\u9ad8\u624b\uff0c\u521b\u7814\u5bab\u5ead\u6b66\u529f\uff0c\u90a3\u662f\u96c6\u5404\u5927\u95e8\u6d3e\u4e4b\u957f\u7684\u6740\u4eba\u6b66\u529f\uff0c\u4e0d\u8bb2\u7a76\u6b66\u5b66\u6e90\u6d41\uff0c\u4e0d\u7ba1\u95e8\u6237\u4e4b\u89c1\uff0c\u53ea\u6c42\u653b\u52bf\u9510\u5229\uff0c\u4e00\u51fb\u53d6\u547d\uff0c\u90a3\u4e9b\u9ed1\u8863\u5200\u624b\uff0c\u90fd\u662f\u5bab\u5ead\u6b66\u571f\u3002\u201d \u3000\u3000\u201c\u90a3\u4f4d\u6559\u4e3b\uff0c\u53c8\u662f\u4ec0\u4e48\u4eba\u5462\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u6700\u5173\u5fc3\u4e8b\uff0c\u662f\u6559\u4e3b\u7684\u8eab\u4efd\u3002 \u3000\u3000\u767d\u5929\u5316\u4e3e\u676f\u559d\u4e86\u4e00\u53e3\u9152\uff0c\u9053\uff1a\u201c\u6211\u662f\u4e8c\u54c1\u5927\u5458\uff0c\u4ed6\u662f\u2026\u2026\u3002\u201d \u3000\u3000\u7a81\u7136\uff0c\u8eab\u8eaf\u4e00\u9635\u6296\u98a4\uff0c\u7acb\u523b\u4f0f\u6848\u6c14\u7edd\u3002 \u3000\u3000\u590f\u5929\u540c\u4e00\u4ef2\u624b\uff0c\u6293\u8d77\u4e86\u767d\u5929\u5316\u7684\u8111\u888b\uff0c\u53ea\u89c1\u4ed6\u4e00\u5f20\u8138\u53d8\u6210\u4e86\u6df1\u9752\u4e4b\u8272\u3002 \u3000\u3000\u201c\u597d\u5389\u5bb3\u7684\u6bd2\u836f\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u76ee\u5149\u8f6c\u52a8\uff0c\u56db\u4e0b\u4e0d\u505c\u7684\u6253\u91cf\u3002 \u3000\u3000\u5979\u64c5\u957f\u9041\u672f\uff0c\u76ee\u53ca\u7ec6\u5fae\uff0c\u4f46\u4ed4\u7ec6\u7684\u770b\u4e86\u534a\u5929\uff0c\u4ecd\u7136\u770b\u4e0d\u51fa\u4efb\u4f55\u53ef\u7591\u4e4b\u5904\uff0c\u4e00\u76b1\u7709\u5934\uff0c\u9053\uff1a\u201c\u4e0d\u662f\u6709\u4eba\u6697\u7b97\uff0c\u96be\u9053\u662f\u9884\u5148\u4e0b\u6bd2\uff0c\u8ba1\u65f6\u53d1\u4f5c\uff0c\u4f46\u4e0d\u592a\u53ef\u80fd\u554a\uff1f\u201d \u3000\u3000\u201c\u5927\u59d0\u8bf4\u7684\u5bf9\uff0c\u662f\u4e0b\u6bd2\uff0c\u4f46\u4e16\u4e0a\u5374\u6ca1\u6709\u5982\u6b64\u7684\u63a7\u6bd2\u9ad8\u624b\u3002\u201d\u6c5f\u67ab\u7f13\u7f13\u628a\u76ee\u5149\u8f6c\u6ce8\u8fce\u6625\uff0c\u9053\uff1a\u201c\u662f\u4f60\u4e0b\u7684\u624b\uff0c\u4e3a\u4ec0\u4e48\u53ea\u6bd2\u5bb3\u767d\u5929\u5316\u4e00\u4e2a\u4eba\u5462\uff1f\u201d \u3000\u3000\u5f20\u56db\u59d1\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u8fce\u6625\uff0c\u4f60\u771f\u6709\u4e0b\u6bd2\u7684\u80c6\u91cf\uff1f\u201d \u3000\u3000\u201c\u8fce\u6625\u6ca1\u6709\uff0c\u4f46\u6211\u6709\u3002\u201d \u3000\u3000\u8fce\u6625\u7a81\u7136\u6253\u4e2a\u8f6c\uff0c\u4e00\u5f20\u8138\u5168\u53d8\u4e86\uff0c\u8eab\u6750\u4e5f\u4f3c\u4e4e\u7a81\u7136\u957f\u9ad8\u4e86\u5f88\u591a\u3002 \u3000\u3000\u662f\u4e2a\u975e\u5e38\u660e\u4eae\u3001\u5996\u5a9a\u7684\u5973\u4eba\u3002 \u3000\u3000\u5927\u5927\u7684\u773c\u775b\uff0c\u6d53\u6d53\u7684\u7709\u6bdb\uff0c\u9ad8\u6311\u7684\u8eab\u6750\uff0c\u4fee\u957f\u7684\u7389\u817f\uff0c\u5634\u5df4\u5927\u4e86\u4e00\u4e9b\uff0c\u4f46\u914d\u7740\u5927\u773c\u3001\u9ad8\u9f3b\uff0c\u5c31\u5341\u5206\u5747\u79f0\u4e86\u3002 \u3000\u3000\u201c\u4f60\u4e0d\u50cf\u4e2d\u571f\u7684\u4eba\uff0c\u60f3\u662f\u6765\u81ea\u96f7\u97f3\u5bfa\u4e86\uff1f\u201d \u3000\u3000\u5f20\u56db\u59d1\u7acb\u523b\u8b66\u89c9\u5230\u4e8b\u6001\u4e25\u91cd\uff0c\u4e00\u9762\u793a\u610f\u5201\u9e4f\u53d1\u51fa\u8b66\u8baf\uff0c\u4f20\u544a\u6b27\u9633\u662d\u7b49\u6212\u5907\uff0c\u4e00\u9762\u793a\u610f\u6c5f\u67ab\uff0c\u4e0d\u80fd\u653e\u8d70\u5996\u5973\u3002 \u3000\u3000\u201c\u4e0d\u9519\uff0c\u4e5d\u5927\u9b54\u5973\uff0c\u4e2a\u4e2a\u64c5\u957f\u7528\u6bd2\u3001\u6613\u5bb9\uff0c\u4f60\u4eec\u4e5f\u8bb8\u5f88\u5947\u602a\uff0c\u6211\u80fd\u4e5f\u8eab\u4f53\u53d8\u77ee\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u90a3\u4e0d\u7b97\u5947\u602a\uff0c\u7f29\u8eab\u672f\uff0c\u53ef\u4e0d\u662f\u5f88\u96be\u7ec3\u7684\u6b66\u529f\u3002\u201d\u6c5f\u67ab\u6740\u610f\u5df2\u52a8\uff0c\u7f13\u7f13\u7ad9\u8d77\u4e86\u8eab\u5b50\uff0c\u7b11\u9053\uff1a\u201c\u5929\u9b54\u6d88\u9b42\u5927\u9635\uff0c\u5c11\u4e86\u4e00\u4e2a\u9b54\u5973\uff0c\u9635\u52bf\u53d8\u5316\uff0c\u53ea\u6015\u8981\u5927\u53d7\u5f71\u54cd\u4e86\u3002\u201d \u3000\u3000\u201c\u4f60\u7b11\u7684\u771f\u597d\u770b\uff0c\u542c\u84dd\u51e4\u8bf4\u4f60\u6709\u8bf1\u5973\u5165\u6000\u4e4b\u80fd\uff0c\u8981\u6211\u4eec\u522b\u7740\u4e86\u4f60\u7684\u9053\u513f\uff0c\u770b\u6765\uff0c\u4e0d\u662f\u865a\u8a00\u3002 \u3000\u3000\u56e0\u4e3a\u6211\u4e0d\u76f8\u4fe1\u5979\u7684\u8bdd\u5148\u4e00\u6b65\u8dd1\u6765\u770b\u770b\uff0c\u4f60\u5c31\u662f\u84dd\u51e4\u8bf4\u7684\u6c5f\u67ab\u4e86\uff1f\u201d \u3000\u3000\u5f20\u56db\u59d1\u6697\u65bd\u4f20\u97f3\u4e4b\u672f\uff0c\u9053\uff1a\u201c\u6c5f\u67ab\uff0c\u8bf1\u4f7f\u5979\u8bf4\u51fa\u6740\u5bb3\u8fce\u6625\u7684\u7ecf\u8fc7\uff0c\u770b\u770b\u84dd\u51e4\u662f\u5426\u5df2\u9677\u5165\u5371\u5883\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u5df2\u51c6\u5907\u51fa\u624b\uff0c\u542c\u5230\u5f20\u56db\u59d1\u7684\u5429\u5490\uff0c\u7acb\u523b\u6362\u4e0a\u4e86\u4e00\u526f\u66f4\u8ff7\u4eba\u7684\u7b11\u5bb9\uff0c\u201c\u60d1\u5fc3\u672f\u201d\u5148\u4e00\u6b65\u65bd\u5c55\u51fa\u6765\uff0c\u9053\uff1a\u201c\u4f60\u662f\u4e5d\u5927\u9b54\u5973\u4e4b\u4e00\u554a\uff01\u53ef\u771f\u662f\u5927\u5927\u7684\u5931\u656c\u4e86\uff0c\u4f60\u4e3a\u4ec0\u4e48\u8981\u6740\u4e86\u4e00\u4e2a\u7c97\u901a\u6b66\u529f\u7684\u5973\u5a62\uff1f\u201d \u3000\u3000\u9b54\u5973\u626d\u52a8\u4e86\u4e00\u4e0b\u67f3\u8170\uff0c\u7b11\u9053\uff1a\u201c\u5979\u7684\u8fd0\u6c14\u592a\u73af\uff0c\u6211\u60f3\u6df7\u5165\u5927\u6bbf\uff0c\u521a\u597d\u770b\u5230\u5979\u62ff\u7740\u9152\u884c\u4e86\u8fc7\u6765\uff0c\u987a\u624b\u5c31\u6740\u4e86\u5979\uff0c\u6613\u670d\u800c\u5165\uff0c\u4f60\u4eec\u4e0d\u53eb\u5979\u7684\u540d\u5b57\uff0c\u6211\u8fd8\u4e0d\u77e5\u9053\u5979\u4eec\u53eb\u8fce\u6625\u5462\uff1f\u201d \u3000\u3000\u5f20\u56db\u59d1\u6697\u6697\u5401\u4e00\u53e3\u6c14\uff0c\u5fd6\u9053\uff1a\u201c\u770b\u6765\uff0c\u53ea\u662f\u4e00\u4e2a\u5de7\u5408\uff0c\u84dd\u51e4\u4e0d\u4f1a\u6709\u4e8b\uff0c\u53ea\u53ef\u60dc\u8fce\u6625\u8fd9\u4e2a\u4e2b\u5934\uff0c\u521a\u521a\u5f03\u90aa\u5f52\u6b63\uff0c\u5c31\u51a4\u6b7b\u5728\u4e86\u8fd9\u4e2a\u5996\u5973\u7684\u624b\u4e2d\u3002\u201d \u3000\u3000\u4f46\u89c1\u90a3\u5996\u5973\u773c\u6ce2\u6b32\u6d41\uff0c\u8138\u4e0a\u6d6e\u52a8\u51fa\u65e0\u9650\u5a07\u5a9a\uff0c\u67f3\u8170\u4e5f\u5fae\u5fae\u6446\u52a8\uff0c\u4f3c\u662f\u8981\u5e94\u58f0\u8d77\u821e\u3002 \u3000\u3000\u5979\u8eab\u6750\u9ad8\u6311\uff0c\u66f2\u7ebf\u660e\u663e\uff0c\u5168\u8eab\u5fae\u5fae\u626d\u52a8\uff0c\u90a3\u79cd\u52a8\u4eba\u7684\u98ce\u60c5\uff0c\u5c31\u4e0d\u662f\u6c5f\u5357\u7684\u7ea2\u7c89\u5c0f\u4f73\u4eba\uff0c\u80fd\u591f\u53ca\u5f97\u4e86\u3002 \u3000\u3000\u6c5f\u67ab\u7684\u7b11\u610f\u4e5f\u8d8a\u53d1\u7684\u8ff7\u4eba\u4e86\uff0c\u5168\u8eab\u6563\u53d1\u51fa\u7537\u6027\u7684\u9b45\u529b\uff0c\u7533\u96ea\u541b\u548c\u955c\u82b1\u697c\u4e3b\u4e3a\u4e4b\u6026\u7136\u5fc3\u52a8\u3002 \u3000\u3000\u671d\u9633\u3001\u6c34\u6708\u4e24\u5927\u697c\u4e3b\u548c\u590f\u5929\u540c\uff0c\u4e5f\u90fd\u4e3a\u9b54\u5973\u7684\u5996\u5a9a\u6240\u60d1\uff0c\u770b\u7684\u773c\u775b\u53d1\u76f4\u3002 \u3000\u3000\u4e56\u4e56\uff0c\u4e24\u4e2a\u4eba\u8f83\u91cf\u4e0a\u4e86\uff0c\u9b54\u5bab\u5a9a\u672f\uff0c\u5bf9\u4e0a\u4e86\u5929\u72d0\u6280\u827a\u201c\u60d1\u5fc3\u672f\u201d\uff0c\u4e24\u5927\u5947\u672f\u51b3\u9ad8\u4f4e\uff0c\u7537\u5973\u9b45\u529b\u5927\u5bf9\u6297\uff0c\u5374\u82e6\u4e86\u5927\u6bbf\u4e2d\u7684\u5176\u4ed6\u7537\u5973\uff0c\u4e2a\u4e2a\u5fc3\u52a8\uff0c\u65e0\u6cd5\u81ea\u7981\u3002 \u3000\u3000\u53ef\u60dc\u7684\u662f\u4e24\u4e2a\u5c0f\u72d0\u5973\u4e0d\u5728\u73b0\u573a\uff0c\u52a0\u4e0a\u96ea\u3001\u971c\u4e8c\u5973\uff0c\u60f3\u5fc5\u4f1a\u66f4\u89c1\u70ed\u95f9\u3002 \u3000\u3000\u5927\u548c\u5c1a\u95ed\u7740\u773c\u775b\uff0c\u4f4e\u58f0\u5728\u5f20\u56db\u59d1\u8033\u8fb9\u5531\u8d77\u5927\u60b2\u68b5\u97f3\u3002 \u3000\u3000\u6562\u60c5\u8fd9\u4f4d\u5973\u4e2d\u8c6a\u6770\uff0c\u4e5f\u6709\u4e9b\u610f\u4e71\u60c5\u8ff7\u4e86\u3002 \u3000\u3000\u5927\u60b2\u68b5\u5531\uff0c\u4f7f\u5979\u7684\u79bb\u9b42\u5f52\u4f53\uff0c\u6697\u6697\u547b\u4e86\u4e00\u58f0\uff0c\u5fd6\u9053\uff1a\u201c\u6d3b\u89c1\u9b3c\u5440\uff01 \u3000\u3000\u88ab\u4ed6\u62b1\u7740\u4e0a\u5e8a\u591a\u5c11\u6b21\u4e86\uff0c\u600e\u4e48\u8fd8\u662f\u8fd9\u4e48\u6ca1\u6709\u5b9a\u529b\u3002\u201d \u3000\u3000\u4e0d\u80fd\u8ba9\u7533\u96ea\u541b\u5f53\u573a\u51fa\u4e11\uff0c\u5f20\u56db\u59d1\u4e00\u6e05\u9192\uff0c\u5c31\u628a\u7533\u96ea\u541b\u62c9\u8fd1\u5927\u548c\u5c1a\uff0c\u68b5\u97f3\u5165\u8033\uff0c\u795e\u5fd7\u4e00\u6e05\u3002\u987f\u89c9\u53cc\u988a\u53d1\u70e7\uff0c\u770b\u4e86\u5f20\u56db\u59d1\u4e00\u773c\uff0c\u8138\u4e0a\u6ee1\u662f\u611f\u6fc0\u4e4b\u60c5\u3002 \u3000\u3000\u68b5\u97f3\u5982\u4e1d\uff0c\u900f\u5165\u590f\u5929\u540c\u548c\u4e09\u5927\u697c\u4e3b\u7684\u8033\u4e2d\uff0c\u4e09\u4eba\u5982\u6709\u68a6\u4e2d\u9192\u6765\uff0c\u6697\u6697\u634f\u4e86\u4e00\u628a\u51b7\u6c57\u3002 \u3000\u3000\u4f46\u5927\u60b2\u68b5\u5531\uff0c\u5e76\u6ca1\u6709\u5e72\u6270\u5230\u6c5f\u67ab\u548c\u9b54\u5973\u7684\u5947\u672f\u5bf9\u51b3\uff0c\u5927\u548c\u5c1a\u4f3c\u662f\u5b58\u5fc3\u8981\u770b\u770b\u6c5f\u67ab\u5728\u8fd9\u65b9\u9762\u7684\u6210\u5c31\u3002 \u3000\u3000\u5929\u72d0\u6280\u827a\uff0c\u80fd\u5426\u76f4\u903c\u9b54\u5bab\u5a9a\u672f\uff0c\u80fd\u5165\u4e3b\u5929\u9b54\u6d88\u9b42\u5927\u9635\u7684\u9b54\u5bab\u5f1f\u5b50\uff0c\u5f53\u7136\u662f\u62d4\u5c16\u7684\u5a9a\u672f\u9ad8\u624b\u3002 \u3000\u3000\u5f20\u56db\u59d1\u6253\u91cf\u4e24\u4eba\u5f62\u52bf\uff0c\u9b54\u5973\u7684\u8eaf\u4f53\u6446\u52a8\uff0c\u6108\u89c1\u5996\u5a9a\uff0c\u539f\u672c\u5c31\u5c0f\u4e00\u70b9\u7684\u8863\u670d\uff0c\u4e5f\u7a81\u7136\u7247\u7247\u7eb7\u88c2\uff0c\u8131\u843d\u4e8e\u5730\u3002 \u3000\u3000\u597d\u5bb6\u4f19\uff0c\u5916\u8863\u4e4b\u5185\uff0c\u7adf\u65e0\u4eb5\u8863\uff0c\u4e0a\u534a\u8eab\u5b8c\u5168\u7684\u88f8\u9732\u51fa\u6765\u3002 \u3000\u3000\u6c5f\u67ab\u6ca1\u6709\u4ec0\u4e48\u52a8\u4f5c\uff0c\u53ea\u662f\u8138\u4e0a\u7684\u5fae\u7b11\uff0c\u4f3c\u662f\u7531\u5168\u8eab\u6563\u5c55\u5f00\u53bb\uff0c\u53ef\u662f\u9876\u95e8\u4e0a\u5374\u9690\u9690\u89c1\u4e86\u6c57\u6c34\u3002 \u3000\u3000\u4f46\u4ed6\u8fd8\u80fd\u8bf4\u8bdd\uff0c\u58f0\u97f3\u4f4e\u6c89\uff0c\u5e26\u7740\u5fae\u54d1\u7684\u78c1\u6027\uff0c\u9053\uff1a\u201c\u8bf4\u5427\uff01\u5927\u9b54\u5973\uff0c\u4e3a\u4ec0\u4e48\u8981\u6740\u4e86\u767d\u5929\u5316\uff1f\u201d\u201c\u6559\u4e3b\u7684\u8eab\u4efd\u4e0d\u80fd\u6cc4\u9732\u2026\u2026\u201d\u5927\u9b54\u5973\u771f\u7684\u56de\u7b54\u4e86\uff1a\u201c\u4ed6\u7acb\u8fc7\u8a93\u8a00\uff0c\u6240\u4ee5\uff0c\u6211\u624d\u6740\u6b7b\u4ed6\u3002\u201d \u3000\u3000\u201c\u4f60\u5982\u4f55\u4e0b\u6bd2\u7684\uff1f\u600e\u4e48\uff1f\u6211\u7adf\u7136\u6ca1\u6709\u77a7\u51fa\u6765\uff1f\u201d \u3000\u3000\u9b54\u5973\u626d\u52a8\u7684\u8eab\u8eaf\uff0c\u7a81\u7136\u9759\u6b62\u4e0b\u6765\uff0c\u9053\uff1a\u201c\u4ed6\u4e2d\u4e86\u5929\u9b54\u9488\uff0c\u4f60\u4eec\u53ea\u60f3\u5230\u9152\u4e2d\u6709\u6bd2\uff0c\u5374\u4e0d\u77e5\u9053\uff0c\u6211\u7528\u6bd2\u9488\u6697\u7b97\u4ed6\u3002 \u3000\u3000\u90a3\u662f\u4e00\u679a\u7ec6\u5c0f\u5982\u725b\u6bdb\u7684\u6697\u5668\uff0c\u7528\u6307\u5f39\u51fa\uff0c\u53ea\u8981\u624b\u6307\u5fae\u52a8\uff0c\u9488\u5c31\u5c04\u51fa\uff0c\u53ea\u53ef\u60dc\u4e0d\u80fd\u53ca\u8fdc\uff0c\u4efd\u91cf\u592a\u8f7b\uff0c\u4e5f\u65e0\u6cd5\u4e00\u6b21\u53d1\u51fa\u591a\u679a\uff0c\u9488\u7531\u5267\u6bd2\u6dec\u70bc\u800c\u6210\uff0c\u89c1\u8840\u5c01\u5589\uff0c\u6b7b\u7684\u5f88\u5feb\u2026\u2026\u201d \u3000\u3000\u201c\u90a3\u4f60\u5c31\u4e3a\u4ed6\u507f\u547d\u5427\uff01\u201d\u6c5f\u67ab\u7a81\u7136\u70b9\u51fa\u4e00\u6307\u3002 \u3000\u3000\u4e00\u6307\u53d6\u547d\uff0c\u5927\u9b54\u5973\u53e3\u9f3b\u95f4\uff0c\u6d8c\u51fa\u4e86\u9c9c\u8840\uff0c\u4f46\u5979\u4ecd\u7136\u62fc\u5c3d\u4f59\u529b\u8bf4\u9053\uff1a\u201c\u6211\u771f\u7684\u559c\u6b22\u4f60\u4e86\uff0c\u84dd\u51e4\u8bf4\u7684\u5bf9\uff0c\u4f60\u4f7f\u5973\u4eba\u7740\u8ff7\uff0c\u2026\u2026\u6211\u53ef\u4ee5\u5e2e\u52a9\u4f60\u7684\uff0c\u2026\u2026\u4f60\u2026\u2026\u4f60\u5374\u5fcd\u5fc3\u6740\u4e86\u6211\u3002\u201d \u3000\u3000\u8bf4\u5b8c\u5fc3\u4e2d\u60c5\u610f\uff0c\u4e5f\u7528\u5c3d\u4e86\u6700\u540e\u4e00\u53e3\u5143\u6c14\uff0c\u7acb\u523b\u5012\u5730\u800c\u901d\u3002 \u3000\u3000\u573a\u4e2d\u4eba\u90fd\u65e0\u6cd5\u660e\u663e\u7684\u5206\u8fa8\u51fa\u6765\uff0c\u6c5f\u67ab\u662f\u4ee5\u672f\u53d6\u80dc\u5462\uff1f\u8fd8\u662f\u4ee5\u6b66\u529f\u6740\u4e86\u9b54\u5973\uff0c\u4f46\u4e00\u6307\u53d6\u547d\uff0c\u529b\u9053\u4e4b\u5f3a\uff0c\u6d1e\u788e\u4e86\u9b54\u5973\u7684\u5fc3\u810f\u3002 \u3000\u3000\u6c5f\u67ab\u957f\u957f\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5979\u7684\u5a9a\u672f\u9ad8\u660e\uff0c\u6211\u5dee\u4e00\u70b9\u6491\u4e0d\u4f4f\u4e86\u3002\u201d \u3000\u3000\u201c\u4f60\u662f\u5426\u5df2\u7ecf\u5f81\u670d\u4e86\u5979\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u5979\u5982\u80af\u5e2e\u5fd9\uff0c\u4e5f\u8bb8\u53ef\u4ee5\u51cf\u5c11\u4e86\u5929\u9b54\u9635\u7684\u538b\u529b\u3002\u201d \u3000\u3000\u201c\u5979\u4e0d\u662f\u771f\u7684\u5c48\u670d\uff0c\u53ea\u51ed\u8fd9\u4e00\u6218\uff0c\u4e5f\u65e0\u6cd5\u4f7f\u5979\u6548\u529b\u3002\u201d \u3000\u3000\u5927\u5bb6\u90fd\u4e0d\u592a\u660e\u767d\u6c5f\u67ab\u7684\u89e3\u91ca\uff1f \u3000\u3000\u4f46\u5f20\u56db\u59d1\u5fc3\u4e2d\u6e05\u695a\uff0c\u8fd9\u53ea\u662f\u7b2c\u4e00\u5173\uff0c\u5982\u4e0d\u80fd\u7537\u5973\u5408\u4f53\uff0c\u6c5f\u67ab\u65e0\u6cd5\u771f\u5f81\u670d\u5979\u3002\u8fc7\u4e00\u5929\uff0c\u5979\u5c31\u4f1a\u6e05\u9192\u8fc7\u6765\uff0c\u6c5f\u67ab\u60c5\u5b7d\u7f20\u8eab\uff0c\u5df2\u7136\u5c1d\u5230\u75db\u82e6\uff0c\u4e5f\u4e0d\u80fd\u518d\u60f9\u9ebb\u70e6\u4e86\u3002 \u3000\u3000\u5929\u72d0\u6b66\u529f\u7684\u53ef\u6015\u5904\uff0c\u5728\u4ee5\u60c5\u6362\u60c5\uff0c\u4ee5\u5fc3\u6362\u5fc3\uff0c\u6c5f\u67ab\u6709\u591a\u5c11\u60c5\u7231\u5206\u820d\u5462\uff1f \u3000\u3000\u5f20\u56db\u59d1\u6ca1\u6709\u8ffd\u95ee\uff0c\u5927\u5bb6\u66f4\u4e0d\u4f1a\u95ee\u4e86\uff0c\u8fd9\u79cd\u4e8b\uff0c\u4e0d\u662f\u4eba\u4eba\u90fd\u80fd\u95ee\u7684\u3002 \u3000\u3000\u4e00\u9635\u5c16\u9510\u7684\u7af9\u54e8\u58f0\uff0c\u4f20\u5165\u8033\u9645\uff0c\u5927\u548c\u5c1a\u8138\u8272\u5fae\u53d8\uff0c\u51b2\u51fa\u5927\u6bbf\u3002 \u3000\u3000\u590f\u5929\u540c\u548c\u4e09\u5927\u697c\u4e3b\uff0c\u7d27\u968f\u800c\u51fa\uff0c\u7533\u96ea\u541b\u5341\u51e4\u5165\u624b\uff0c\u4e5f\u8ddf\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u767d\u5929\u5316\u4e4b\u6b7b\uff0c\u6709\u4e86\u66f4\u660e\u767d\u7684\u4e00\u4e2a\u542f\u793a\uff0c\u90a3\u4f4d\u795e\u79d8\u6559\u4e3b\u5df2\u5b58\u4e86\u8d76\u5c3d\u6740\u7edd\u4e4b\u5fc3\u3002 \u3000\u3000\u8fd9\u5c31\u6fc0\u8d77\u4e86\u590f\u5929\u540c\u7b49\u7684\u62fc\u6b7b\u4e4b\u5fc3\uff0c\u65e2\u7136\u5df2\u65e0\u751f\u8def\uff0c\u80fd\u6740\u4e00\u4e2a\u654c\u4eba\u591f\u672c\uff0c\u6740\u4e24\u4e2a\u7b97\u8d5a\u4e86\u3002 \u3000\u3000\u201c\u662f\uff01\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u4e5d\u4e2a\u9b54\u5973\u4e3b\u6301\u7684\u5929\u9b54\u6d88\u9b42\u5927\u9635\uff0c\u53ea\u53bb\u5176\u4e00\uff0c\u8fd8\u6709\u516b\u4e2a\u9b54\u5973\uff0c\u6211\u6050\u6015\u6297\u4e0d\u4f4f\u554a\uff01\u201d \u3000\u3000\u201c\u770b\u4f60\u7b11\u7684\u53ef\u7231\u8ff7\u4eba\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u624b\u8db3\u4e0d\u52a8\uff0c\u96be\u9053\u662f\u5185\u529b\u6bd4\u62fc\uff1f\u201d \u3000\u3000\u201c\u662f\uff01\u53cc\u65b9\u5404\u4ee5\u672f\u6cd5\u5bf9\u6297\uff0c\u8fd9\u4e2d\u95f4\u5c31\u4e0d\u662f\u7537\u5973\u60c5\u7231\u90a3\u56de\u4e8b\u4e86\u3002\u201d \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u5979\u4ee5\u672f\u8bf1\u6211\uff0c\u6211\u4ee5\u672f\u60d1\u5979\uff0c\u53cc\u65b9\u5168\u529b\u65bd\u5c55\uff0c\u5c31\u5f62\u6210\u5185\u529b\u6bd4\u6597\uff0c\u50f5\u6301\u4e0d\u4e0b\uff0c\u521a\u624d\uff0c\u5c0f\u5f1f\u5df2\u5168\u529b\u4ee5\u8d74\uff0c\u8fd9\u4e9b\u9b54\u5973\u7684\u529f\u529b\uff0c\u975e\u540c\u5c0f\u53ef\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e48\u8bf4\uff0c\u5979\u4eec\u5982\u4ee5\u516b\u5bf9\u4e00\u2026\u2026\u201d \u3000\u3000\u201c\u5c0f\u5f1f\u51c6\u8f93\u65e0\u8d62\u4e86\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u7d27\u5f20\u4e86\uff0c\u9053\uff1a\u201c\u96ea\u3001\u971c\u4e8c\u5973\u5e2e\u4f60\u5462\uff1f\u201d \u3000\u3000\u201c\u8be5\u6709\u80dc\u7b97\uff0c\u53ea\u4e0d\u8fc7\uff0c\u5979\u4eec\u4e00\u52a8\u624b\uff0c\u5c31\u5728\u9635\u4e2d\u653e\u6bd2\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u90a3\u5c31\u96be\u4f5c\u9884\u4f30\u4e86\u3002\u201d \u3000\u3000\u4f46\u89c1\u9752\u51e4\u6025\u6025\u5954\u5165\u5927\u6bbf\uff0c\u9053\uff1a\u201c\u5f20\u59e8\uff0c\u84dd\u51e4\u4f20\u6765\u8baf\u606f\uff0c\u90a3\u4f4d\u6559\u4e3b\uff0c\u5df2\u7ecf\u96c6\u805a\u4eba\u624b\uff0c\u51c6\u5907\u51fa\u52a8\u2026\u2026\u201d\u76ee\u5149\u4e00\u63a0\u8eba\u5728\u5927\u6bbf\u7684\u9b54\u5973\uff0c\u63a5\u9053\uff1a\u201c\u8fd9\u91cc\u6709\u4e66\u4fe1\u4e00\u5c01\uff0c\u8981\u5f20\u59e8\u4eb2\u81ea\u62c6\u9605\u3002\u201d \u3000\u3000\u9752\u51e4\u7531\u8d34\u8eab\u5904\uff0c\u53d6\u51fa\u4e00\u5c01\u5bc6\u51fd\uff0c\u4ea4\u7ed9\u4e86\u5f20\u56db\u59d1\u3002 \u3000\u3000\u4e66\u4e0a\u5b57\u8ff9\u5f88\u8349\uff0c\u60f3\u662f\u5f88\u6025\u4fc3\u4e4b\u4e0b\u5199\u6210\u3002 \u3000\u3000\u5185\u5bb9\u4e5f\u5f88\u7b80\u5355\uff0c\u4f46\u5374\u5341\u5206\u4ee4\u4eba\u9707\u52a8\u3002 \u3000\u3000\u6559\u4e3b\u4f3c\u662f\u53e6\u6709\u7406\u4f0f\uff0c\u5b88\u53e3\u5982\u74f6\uff0c\u65e0\u4eba\u77e5\u6653\uff0c\u665a\u8f88\u5c3d\u529b\u800c\u4e3a\uff0c\u8bf7\u7559\u5fc3\u9635\u524d\u53d8\u5316\u3002 \u3000\u3000\u679c\u7136\u662f\u9634\u9669\u65e0\u6bd4\u3002 \u3000\u3000\u5f20\u56db\u59d1\u6495\u788e\u4e66\u4fe1\uff0c\u7adf\u7136\uff0c\u6ca1\u6709\u4f20\u9605\u6c5f\u67ab\u3002 \u3000\u3000\u201c\u4fe1\u4e0a\u8bf4\u4e9b\u4ec0\u4e48\uff1f\u201d\u6c5f\u67ab\u4f4e\u58f0\u8be2\u95ee\u3002 \u3000\u3000\u201c\u84dd\u51e4\u8981\u6211\u4eec\u7559\u5fc3\u9635\u524d\u53d8\u5316\uff0c\u597d\u50cf\u662f\u90a3\u4f4d\u6559\u4e3b\u53e6\u6709\u57cb\u4f0f\uff0c\u8fde\u84dd\u51e4\u4e5f\u4e0d\u6e05\u695a\u3002\u201d\u5f20\u56db\u59d1\u7adf\u6709\u70b9\u6025\u71e5\u4e86\u3002 \u3000\u3000\u4e00\u4e2a\u5929\u9b54\u6d88\u9b42\u9635\uff0c\u5df2\u7ecf\u65e0\u6cd5\u5bf9\u4ed8\uff0c\u53e6\u6709\u7406\u4f0f\uff0c\u5982\u4f55\u4e0d\u53eb\u6307\u6325\u5168\u519b\u7684\u5f20\u56db\u59d1\u5fc3\u614c\u610f\u4e71\u3002 \u3000\u3000\u201c\u5927\u59d0\uff0c\u628a\u4eba\u624b\u96c6\u4e2d\u8fd9\u91cc\u5427\uff1f\u8fd9\u5ea7\u8352\u5e99\u56db\u5468\uff0c\u5e73\u5766\u3001\u8352\u51c9\uff0c\u662f\u4e00\u7247\u5f88\u597d\u7684\u6218\u573a\u3002\u201d \u3000\u3000\u201c\u4e8b\u5b9e\u4e0a\uff0c\u53ea\u6015\u6211\u4eec\u4e5f\u8d70\u4e0d\u4e86\u5566\uff01\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u6211\u53bb\u62db\u547c\u6b27\u9633\u662d\u592b\u5987\uff0c\u548c\u96ea\u3001\u971c\u4e8c\u5973\uff0c\u7a0d\u4f5c\u5e03\u7f6e\uff0c\u5927\u6982\uff0c\u8fd9\u91cc\u5c31\u662f\u51b3\u6218\u4e4b\u5730\u4e86\u3002\u201d \u3000\u3000\u201c\u5f20\u59e8\uff0c\u6211\u5462\uff1f\u201d\u9752\u51e4\u9053\uff1a\u201c\u53bb\u548c\u84dd\u51e4\u4f1a\u5408\uff1f\u8fd8\u662f\u7559\u5728\u8fd9\u91cc\uff1f\u201d \u3000\u3000\u5f20\u56db\u59d1\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u7559\u8fd9\u91cc\u5427\uff01\u5c31\u5728\u5927\u6bbf\u5de6\u53f3\uff0c\u9009\u4e00\u5904\u9690\u8eab\u7684\u5730\u65b9\uff0c\u53d1\u6325\u6240\u957f\uff0c\u62bd\u51b7\u5b50\u4f24\u654c\uff0c\u4e0d\u8981\u6b63\u9762\u51fa\u624b\uff0c\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff0c\u201c\u9752\u51e4\uff0c\u591a\u591a\u73cd\u91cd\u81ea\u5df1\uff0c\u6885\u82b1\u5df2\u7ecf\u6218\u6b7b\uff0c\u96ea\u3001\u971c\u5165\u9b54\uff0c\u5f20\u59e8\u518d\u4e5f\u7981\u4e0d\u8d77\u4e00\u6b21\u7684\u4f24\u5bb3\u4e86\u3002\u201d \u3000\u3000\u9752\u51e4\u5446\u4f4f\u4e86\uff0c\u96ea\u3001\u971c\u3001\u6885\u82b1\uff0c\u90fd\u662f\u5979\u6700\u5173\u5fc3\u7684\u4eba\uff0c\u4f46\u6b64\u523b\u5f62\u52bf\u7d27\u6025\uff0c\u5f20\u56db\u59d1\u6ca1\u6709\u8bf4\uff0c\u9752\u51e4\u4e5f\u5fcd\u4e0b\u672a\u95ee\u3002 \u3000\u3000\u5386\u5c3d\u6ca7\u6851\u5c11\u5973\u5fc3\uff0c\u4f7f\u9752\u51e4\u6709\u7740\u8d85\u8d8a\u5e74\u9f84\u7684\u514b\u5236\u5de5\u592b\u3002 \u3000\u3000\u5f20\u56db\u59d1\u8bf4\u5b8c\u8bdd\uff0c\u8f6c\u8eab\u79bb\u53bb\u3002 \u3000\u3000\u671b\u7740\u5f20\u56db\u59d1\u7684\u80cc\u5f71\uff0c\u9752\u51e4\u4e3e\u624b\u62ed\u53bb\u53cc\u76ee\u4e2d\u6d41\u51fa\u7684\u6cea\u6c34\u3002 \u3000\u3000\u4e00\u53ea\u624b\u4f38\u51fa\u8fc7\u6765\uff0c\u7d27\u7d27\u63e1\u7740\u4e86\u9752\u51e4\u7684\u7389\u8155\uff0c\u8033\u9645\u95f4\u54cd\u8d77\u4e86\u6c5f\u67ab\u7684\u58f0\u97f3\uff0c\u9053\uff1a\u201c\u9752\u51e4\uff0c\u542c\u5927\u59d0\u7684\u8bdd\uff0c\u597d\u597d\u7684\u73cd\u60dc\u81ea\u5df1\uff0c\u4e3a\u592b\u7684\uff0c\u4e5f\u53d7\u4e0d\u4f4f\u518d\u4e00\u6b21\u7684\u6253\u51fb\u3002\u201d \u3000\u3000\u9752\u51e4\u7f13\u7f13\u8f6c\u8fc7\u8eab\u5b50\uff0c\u504e\u5165\u4e86\u6c5f\u67ab\u7684\u6000\u4e2d\uff0c\u9053\uff1a\u201c\u6c5f\u90ce\uff0c\u600e\u4e48\u4f1a\u8fd9\u6837\u5462\uff1f\u6885\u82b1\u59d0\u59d0\uff0c\u4e00\u5fc3\u5411\u5584\uff0c\u4e0a\u5929\u4e3a\u4ec0\u4e48\u4e0d\u7ed9\u5979\u4e00\u6761\u81ea\u65b0\u4e4b\u8def\uff1f \u3000\u3000\u96ea\u3001\u971c\u4e24\u4f4d\u59b9\u5b50\uff0c\u6709\u5982\u4ed9\u9732\u660e\u73e0\uff0c\u662f\u90a3\u4e48\u7eaf\u6d01\u65e0\u90aa\uff0c\u53c8\u600e\u4f1a\u6ca6\u5165\u9b54\u9053\uff1f\u201d \u3000\u3000\u201c\u96ea\u3001\u971c\u5165\u9b54\uff0c\u662f\u4e3a\u4e86\u5e2e\u52a9\u6211\uff0c\u6c42\u529f\u5fc3\u5207\uff0c\u7ec3\u4e86\u9b54\u529f\uff0c\u5165\u9b54\u9053\uff0c\u5f88\u96be\u81ea\u7981\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u9752\u51e4\uff0c\u6b64\u65f6\u6b64\u523b\uff0c\u662f\u6211\u6700\u9700\u8981\u5e2e\u52a9\u7684\u65f6\u5019\u2026\u2026\u201d\u201c\u53ea\u8981\u6211\u529b\u80fd\u6240\u53ca\uff0c\u6211\u4f1a\u5168\u529b\u4ee5\u8d74\uff0c\u6c5f\u90ce\uff0c\u8840\u6e85\u9ec4\u6c99\u60c5\u4e0d\u5c3d\uff0c\u8bf4\u5427\uff0c\u8981\u6211\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u6211\u8981\u4f60\u597d\u597d\u7684\u6d3b\u7740\uff0c\u4e0d\u8981\u901e\u4e00\u65f6\u8c6a\u5f3a\uff0c\u8f7b\u8eab\u6d89\u9669\u3002\u9752\u51e4\uff0c\u4f60\u8981\u7559\u4e0b\u6027\u547d\u966a\u6211\u3002\u201d \u3000\u3000\u9752\u51e4\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u6211\u4eec\u8981\u5c3d\u4e00\u5207\u80fd\u529b\uff0c\u628a\u96ea\u3001\u971c\u4e24\u4f4d\u59b9\u5b50\u6551\u51fa\u9b54\u9053\uff0c\u6240\u4ee5\uff0c\u6c5f\u90ce\uff0c\u4f60\u4e5f\u8981\u591a\u591a\u4fdd\u91cd\uff0c\u4f60\u8981\u660e\u767d\uff0c\u4f60\u5982\u8eab\u6709\u4e0d\u6d4b\uff0c\u8fd9\u4e00\u5207\u7684\u751f\u751f\u6b7b\u6b7b\uff0c\u90fd\u53d8\u7684\u6ca1\u6709\u610f\u4e49\u4e86\uff0c\u96ea\u3001\u971c\u4e24\u4f4d\u5bf9\u4f60\u7528\u60c5\u4e4b\u6df1\uff0c\u5df2\u4e0d\u662f\u5c71\u76df\u6d77\u8a93\u6240\u80fd\u8868\u8fbe\uff0c\u8d31\u59be\u53ef\u4e3a\u6c5f\u90ce\u751f\uff0c\u53ef\u4e3a\u6c5f\u90ce\u6b7b\uff0c\u4f46\u8d31\u59be\u81ea\u77e5\u4e0d\u5982\u96ea\u3001\u971c\u591a\u60c5\uff01\u4f60\u77e5\u9053\u5417\uff1f \u3000\u3000\u541f\u96ea\u4e3a\u4e86\u8981\u6fc0\u53d1\u51fa\u4f60\u9a6d\u5251\u7684\u6f5c\u529b\uff0c\u66fe\u51c6\u5907\u4ee5\u81ea\u5df1\u7684\u6027\u547d\uff0c\u4f5c\u4e3a\u8bf1\u56e0\uff0c\u5979\u60f3\u7684\uff0c\u5df2\u4e0d\u662f\u90ce\u60c5\u59be\u610f\uff0c\u800c\u662f\u5173\u5fc3\u4f60\uff0c\u5904\u5904\u4e3a\u4f60\u6253\u7b97\uff0c\u9ed8\u9ed8\u7684\u5949\u732e\u51fa\u81ea\u5df1\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\u2026\u2026\u201d\u6c5f\u67ab\u75db\u82e6\u4ed6\u8bf4\uff1a\u201c\u6240\u4ee5\uff0c\u6211\u6ca1\u6709\u4e1d\u6beb\u8d23\u602a\u5979\u4eec\u7684\u610f\u601d\uff0c\u6211\u53ea\u60f3\u6551\u5979\u4eec\uff0c\u5343\u6b7b\u2026\u2026\u201d \u3000\u3000\u4e0b\u9762\u7684\u8bdd\uff0c\u8bf4\u4e0d\u51fa\u6765\u4e86\uff0c\u88ab\u9752\u51e4\u4e00\u5f20\u6a31\u6843\u5c0f\u53e3\uff0c\u5835\u4f4f\u53c8\u7d27\u53c8\u5bc6\u3002 \u3000\u3000\u201c\u5bf9\u4e0d\u4f4f\u5566\uff0c\u6253\u6405\u4e86\u4e24\u4f4d\uff0c\u53ef\u662f\uff0c\u6211\u6ca1\u6709\u5f88\u591a\u65f6\u95f4\u7b49\u4e0b\u53bb\u4e86\u3002\u201d \u3000\u3000\u201c\u84dd\u51e4\uff01\u201d \u3000\u3000\u9752\u51e4\u6025\u6025\u8131\u51fa\u4e86\u6c5f\u67ab\u7684\u6000\u62b1\uff0c\u8f6c\u5934\u770b\u53bb\u3002 \u3000\u3000\u53ea\u89c1\u84dd\u51e4\u501a\u95e8\u800c\u7acb\uff0c\u4f4e\u5782\u7740\u8793\u9996\u3002 \u3000\u3000\u5927\u6709\u649e\u7834\u597d\u4e8b\uff0c\u4eba\u548e\u751a\u6df1\u7684\u611f\u89c9\u3002 \u3000\u3000\u201c\u6211\u4e0d\u8981\u6c5f\u67ab\u8bf4\u51fa\u4e0d\u5409\u5229\u7684\u8bdd\u6765\uff0c\u9752\u51e4\u89e3\u91ca\u9053\uff1a\u201c\u6765\u4e0d\u53ca\u7528\u624b\u6342\u4ed6\u5634\u5df4\u4e86\uff01\u201d \u3000\u3000\u84dd\u51e4\u62ac\u8d77\u5934\u6765\uff0c\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6325\u5251\u73a9\u547d\uff0c\u8840\u96e8\u8165\u98ce\uff0c\u5c0f\u62e5\u7247\u523b\uff0c\u7a0d\u758f\u60c5\u6000\uff0c\u4e5f\u53ef\u51cf\u5c11\u6c5f\u90ce\u4e00\u4e9b\u7d27\u5f20\u538b\u529b\uff0c\u6ca1\u6709\u4ec0\u4e48\u4e0d\u597d\u554a\uff01\u201d \u3000\u3000\u201c\u53cc\u65b9\u5df2\u77ed\u5175\u76f8\u63a5\uff0c\u4f60\u600e\u4f1a\u6709\u7a7a\u6765\u6b64\uff1f\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u662f\u4e0d\u662f\u53c8\u6709\u4ec0\u4e48\u53d8\u5316\uff1f\u201d \u3000\u3000\u201c\u662f\uff01\u201d\u84dd\u51e4\u9053\uff1a\u201c\u5f20\u59e8\u6307\u793a\u6211\u6765\u6b64\u89c1\u4f60\uff0c\u7980\u62a5\u519b\u60c5\u3002\u201d \u3000\u3000\u6c5f\u67ab\u7a81\u7136\u4e00\u4f38\u624b\uff0c\u62c9\u8fc7\u84dd\u51e4\uff0c\u62e5\u5165\u6000\u4e2d\uff0c\u9053\uff1a\u201c\u5feb\u4e9b\u8bf4\u5427\uff01\u6211\u8981\u5c3d\u5feb\u7684\u548c\u4ed6\u4eec\u4e00\u51b3\u80dc\u8d1f\uff01\u6211\u4e0d\u80fd\u518d\u62d6\u5ef6\u65f6\u65e5\u4e86\uff0c\u6211\u5fc3\u4e2d\u597d\u6025\u597d\u6025\uff0c\u84dd\u51e4\uff0c\u5feb\u5e2e\u6211\u60f3\u6cd5\u5b50\u5427\uff1f\u201d \u3000\u3000\u4ed6\u5fc3\u4e2d\u60f3\u7740\u96ea\u3001\u971c\u5165\u9b54\u4e4b\u5371\uff0c\u8981\u65e9\u4e9b\u7740\u624b\u65bd\u6551\u624d\u597d\u3002 \u3000\u3000\u201c\u662f\u7684\uff01\u5bf9\u65b9\u4e5f\u5f88\u7126\u6025\u2026\u2026\u201d\u84dd\u51e4\u9053\uff1a\u201c\u5929\u738b\u95e8\u3001\u5929\u540e\u5bab\u9003\u56de\u7684\u5f1f\u5b50\uff0c\u8bf4\u51fa\u4e86\u4e8b\u60c5\u7684\u7ecf\u8fc7\uff0c\u90a3\u4f4d\u5e73\u65e5\u96be\u6d4b\u9ad8\u6df1\u7684\u6559\u4e3b\uff0c\u7adf\u4e5f\u6709\u4e9b\u795e\u8272\u9707\u52a8\u4e86\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u8fde\u6211\u4e5f\u542c\u5f97\u6709\u4e9b\u5403\u60ca\uff0c\u6740\u4eba\u7684\u53ea\u662f\u4e24\u4e2a\u5c11\u5973\uff0c\u7f8e\u8273\u4e0d\u53ef\u65b9\u7269\uff0c\u5374\u53c8\u98d8\u5ffd\u83ab\u53ef\u9884\u6d4b\u3002 \u3000\u3000\u57cb\u4f0f\u8fd9\u5ea7\u8352\u5e99\u56db\u5468\u7684\u9ad8\u624b\uff0c\u51e0\u4e4e\u88ab\u5979\u4eec\u6740\u5149\uff0c\u4e03\u516b\u5341\u4e2a\u9ad8\u624b\u554a\uff01\u6ca1\u6709\u4eba\u80fd\u591f\u8fd8\u51fb\uff0c\u8fde\u5929\u540e\u5bab\u4e09\u5927\u5bab\u4e3b\u548c\u5929\u738b\u95e8\u4f4f\uff0c\u4e5f\u5168\u6570\u5c31\u622e\u4e86\uff0c\u5979\u4eec\u662f\u4e0d\u662f\u96ea\u3001\u971c\u4e24\u4f4d\u59b9\u5b50\uff1f\u201d \u3000\u3000\u201c\u662f\u554a\uff01\u84dd\u51e4\uff0c\u5979\u4eec\u5165\u9b54\u4e86\uff0c\u6211\u4eec\u8981\u6551\u5979\u4eec\uff0c\u5feb\u60f3\u6cd5\u5b50\uff0c\u6740\u4e86\u90a3\u4f4d\u6559\u4e3b\uff0c\u518d\u665a\u5c31\u9ebb\u70e6\u4e86\uff0c\u84dd\u51e4\uff0c\u4f60\u8981\u5feb\u4e9b\u60f3\u529e\u6cd5\u5440\uff01\u201d \u3000\u3000\u6c5f\u67ab\u7d27\u62b1\u84dd\u51e4\uff0c\u773c\u7736\u4e2d\u6ee1\u542b\u6cea\u6c34\uff0c\u5fc3\u4e2d\u7684\u7126\u6025\uff0c\u5168\u6ea2\u8a00\u8868\uff0c\u90a3\u79cd\u4e5e\u6c42\u7684\u795e\u8272\uff0c\u5b8c\u5168\u662f\u4e00\u526f\u7a1a\u6c14\u672a\u8131\u7684\u4f9d\u8d56\u8868\u60c5\u3002 \u3000\u3000\u84dd\u51e4\u5ffd\u7136\u611f\u89c9\u7d27\u62b1\u81ea\u5df1\u7684\u60c5\u90ce\uff0c\u50cf\u4e2a\u5b69\u5b50\uff0c\u5728\u832b\u832b\u5927\u6d77\u4e2d\u6293\u4f4f\u4e86\u4e00\u6839\u6d6e\u6728\u822c\uff0c\u662f\u90a3\u4e48\u5168\u5fc3\u5168\u610f\u7684\u4f9d\u8d56\u4e8e\u81ea\u5df1\u3002 \u3000\u3000\u8fd9\u65f6\u523b\uff0c\u4f3c\u4e4e\u5df2\u4e0d\u662f\u8bba\u5175\u5b9a\u8c0b\u80fd\u4f7f\u4ed6\u5e73\u9759\u4e0b\u6765\u3002 \u3000\u3000\u4f55\u51b5\uff0c\u6c5f\u67ab\u8fd9\u4e9b\u52a8\u4f5c\u6fc0\u8d77\u5973\u6027\u6f5c\u5728\u7684\u4e00\u79cd\u6bcd\u7231\u3002 \u3000\u3000\u84dd\u51e4\u5fc3\u4e2d\u60c5\u7231\u6fc0\u52a8\uff0c\u53d6\u51fa\u7ee2\u5e15\uff0c\u62ed\u7740\u6c5f\u67ab\u593a\u7736\u800c\u51fa\u7684\u6cea\u6c34\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4e0d\u8981\u54ed\u561b\uff01\u8981\u6211\u505a\u4ec0\u4e48\uff1f\u6211\u90fd\u4f1a\u5168\u529b\u5e2e\u52a9\u4f60\u3002\u2026\u2026\u201d \u3000\u3000\u5ffd\u7136\u95f4\u60f3\u5230\u9752\u51e4\u5728\u4fa7\uff0c\u987f\u89c9\u7f9e\u610f\u88ad\u5fc3\u3002 \u3000\u3000\u5979\u5b9e\u5728\u662f\u4e2a\u5f88\u4fdd\u5b88\u7684\u5973\u4eba\u3002 \u3000\u3000\u5077\u773c\u770b\u53bb\uff0c\u5927\u6bbf\u4e2d\u7a7a\u65e0\u4e00\u4eba\uff0c\u4e0d\u77e5\u4f55\u65f6\uff0c\u9752\u51e4\u5df2\u6084\u7136\u79bb\u53bb\u3002 \u3000\u3000\u84dd\u51e4\u6697\u6697\u5401\u4e00\u53e3\u6c14\uff0c\u63a5\u9053\uff1a\u201c\u90a3\u4f4d\u6559\u4e3b\uff0c\u4e0d\u50cf\u662f\u4e2a\u6c5f\u6e56\u4eba\uff0c\u9634\u6c89\u591a\u7591\uff0c\u5374\u53c8\u5904\u4e8b\u679c\u51b3\uff0c\u4ed6\u501f\u9152\u88c5\u75af\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u5403\u4e86\u4e00\u60ca\uff0c\u4eba\u4e5f\u5ffd\u7136\u6e05\u9192\u8fc7\u6765\uff0c\u653e\u5f00\u84dd\u51e4\u95ee\u9053\uff1a\u201c\u4ed6\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u84dd\u51e4\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u600e\u4e48\u4e86\u561b\uff1f\u597d\u50cf\u88ab\u9488\u523a\u4e86\u4e00\u4e0b\u4f3c\u7684\uff01\u201d \u3000\u3000\u201c\u4ed6\u662f\u4e0d\u662f\u5bf9\u4f60\u4e0d\u89c4\u77e9\u554a\uff1f\u201d \u3000\u3000\u201c\u653e\u5fc3\u4e86\uff0c\u8eab\u5fc3\u4ff1\u5c5e\u6c5f\u90ce\u6709\uff0c\u6b64\u751f\u4e0d\u4f5c\u51fa\u7684\u4e91\u2026\u201d\u84dd\u51e4\u9053\uff1a\u201c\u4f46\u4ed6\u8bf4\u7684\u8bdd\uff0c\u5374\u662f\u5145\u6ee1\u7740\u8bf1\u60d1\uff0c\u5c24\u5176\u5bf9\u6211\u8fd9\u6837\u7684\u5973\u4eba\u3002\u201d \u3000\u3000\u201c\u4ed6\u8bf4\u4e9b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u4ed6\u8bf4\uff0c\u6211\u662f\u7edf\u7387\u5927\u519b\u7684\u5c06\u624d\uff0c\u6c5f\u6e56\u4eba\u7acb\u540d\u6768\u4e07\uff0c\u7ec4\u5e2e\u7ed3\u76df\uff0c\u53ea\u80fd\u7b97\u662f\u5c0f\u5c40\u9762\u2026\u2026\u201d\u84dd\u98ce\u9053\uff1a\u201c\u5982\u80fd\u7edf\u5175\u5341\u4e07\uff0c\u9a70\u9a8b\u4e8e\u7586\u573a\u4e4b\u4e0a\uff0c\u8fd0\u7b79\u5e44\u5e44\uff0c\u51b3\u80dc\u5343\u91cc\uff0c\u7eb5\u6a2a\u56db\u6d77\uff0c\u9010\u9e7f\u4e2d\u539f\uff0c\u624d\u80fd\u771f\u6b63\u7684\u4e00\u5c55\u5c06\u624d\u62b1\u8d1f\u3002 \u3000\u3000\u8fd9\u6b63\u662f\u6211\u6614\u65e5\u68a6\u5bd0\u4ee5\u6c42\u7684\u4e8b\uff0c\u6211\u719f\u8bfb\u6218\u7b56\u6cd5\uff0c\u5e0c\u671b\u6709\u4e00\u5929\u80fd\u864e\u5e10\u70b9\u5c06\uff0c\u5f81\u6218\u4e07\u91cc\uff0c\u4e0d\u8ba9\u5dfe\u5e3c\u524d\u8d24\uff0c\u4e13\u7f8e\u4e8e\u524d\uff0c\u53ea\u53ef\u60dc\u5440\uff01\u2026\u2026\u201d \u3000\u3000\u201c\u53ef\u60dc\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u53ef\u60dc\uff0c\u5168\u88ab\u4f60\u7834\u574f\u4e86\u3002\u201d\u84dd\u98ce\u9053\uff1a\u201c\u5982\u4eca\u662f\u96c4\u5fc3\u5c3d\u6d88\uff0c\u53ea\u671f\u671b\u9000\u5c45\u6df1\u95fa\uff0c\u6d17\u624b\u4f5c\u7fb9\u6c64\uff0c\u65e5\u65e5\u4e3a\u541b\u5fd9\uff0c\u4f5c\u4e2a\u7f1d\u8863\u716e\u996d\u7684\u5c0f\u5987\u4eba\u4e86\u3002\u201d \u3000\u3000\u6c5f\u67ab\u795e\u60c5\u8083\u7136\u7684\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u8fd9\u4e2a\u4eba\u679c\u7136\u5389\u5bb3\uff0c\u8bc6\u4eba\u6709\u672f\uff0c\u53c8\u80fd\u6295\u5176\u6240\u597d\uff0c\u52ff\u602a\u590f\u5929\u540c\u3001\u767d\u5929\u5316\u3001\u79cb\u79bb\u82b1\u7b49\u6c5f\u6e56\u4e0a\u7684\u4e00\u6d41\u4eba\u7269\uff0c\u4e5f\u88ab\u4ed6\u73a9\u5f04\u4e8e\u638c\u80a1\u4e4b\u4e0a\uff0c\u4f46\u4ed6\u771f\u80fd\u7ed9\u4f60\u5341\u4e07\u5927\u519b\uff0c\u8ba9\u4f60\u7edf\u9886\u5417\uff1f\u201d \u3000\u3000\u201c\u6211\u4e0d\u77e5\u9053\uff0c\u56e0\u4e3a\u6211\u6ca1\u6709\u591a\u95ee\uff1f\u6211\u5df2\u77a7\u51fa\u4ed6\u5b58\u5fc3\u4e0d\u826f\u2026\u2026\uff0c\u201d\u84dd\u51e4\u9053\uff1a\u201c\u62c5\u5fc3\u4ed6\u8ba1\u8bf1\u4e0d\u6210\uff0c\u51fa\u624b\u52a8\u5f3a\uff0c\u5168\u4ed8\u5fc3\u601d\uff0c\u90fd\u5728\u6697\u4f5c\u6212\u5907\u3002\u201d \u3000\u3000\u201c\u4ed6\u80fd\u964d\u670d\u590f\u5929\u540c\uff0c\u5f79\u4f7f\u9ec4\u5929\u738b\uff0c\u5236\u6536\u79cb\u79bb\u82b1\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u4f60\u5982\u4f55\u662f\u4ed6\u654c\u624b\uff1f\u201d \u3000\u3000\u201c\u6211\u4e5f\u8bb8\u4e0d\u662f\u4ed6\u7684\u654c\u624b\uff0c\u4f46\u6211\u4e00\u5b9a\u8981\u8bd5\u8bd5\uff0c\u2026\u2026\u201d\u84dd\u51e4\u9053\uff1a\u201c\u6211\u662f\u90a3\u79cd\u4e0d\u5230\u9ec4\u6cb3\u4e0d\u7518\u5fc3\u7684\u5973\u4eba\uff0c\u5c31\u7b97\u4ed6\u5236\u670d\u4e86\u6211\uff0c\u6c5f\u90ce\uff0c\u4e5f\u4e0d\u4f1a\u8ba9\u4ed6\u8d81\u5fc3\u5982\u613f\uff0c\u6cbe\u6c61\u5230\u4f60\u7684\u2026\u2026\u201d \u3000\u3000\u84dd\u51e4\u6ca1\u6709\u8bf4\u4e0b\u53bb\uff0c\u5979\u4e0d\u80fd\u81ea\u5df1\u5b9a\u4f4d\uff0c\u6c5f\u67ab\u7ed9\u5979\u4ec0\u4e48\u540d\u4efd\uff0c\u5979\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u201c\u8001\u5a46\uff01\u201d \u3000\u3000\u6c5f\u67ab\u4f4e\u58f0\u8bf4\u51fa\u84dd\u98ce\u5728\u4ed6\u5fc3\u4e2d\u7684\u5730\u4f4d\u3002 \u3000\u3000\u84dd\u51e4\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u81ea\u90a3\u65e5\u88ab\u4f60\u5f15\u8bf1\u4e0a\u5e8a\uff0c\u6211\u5df3\u4e86\u609f\u5230\u5904\u8eab\u73af\u5883\u7684\u51f6\u9669\u53ef\u6016\uff0c\u6b64\u673a\u5bc6\u4e00\u65e6\u5916\u6cc4\uff0c\u9ec4\u5929\u738b\u4e0d\u4f1a\u662f\u6740\u6211\u4e86\u4e8b\uff0c\u4ed6\u4e00\u5b9a\u4f1a\u5148\u5f97\u5230\u6211\u8eab\u4f53\u3002\u201d \u3000\u3000\u6240\u4ee5\uff0c\u6211\u5df2\u5148\u5728\u53e3\u4e2d\u85cf\u4e86\u4e00\u9897\u5947\u6bd2\u7684\u836f\u7269\uff1b\u53ea\u8981\u54ac\u7834\u5916\u5c42\uff0c\u7acb\u523b\u5c31\u6bd2\u53d1\u8eab\u6b7b\uff0c\u8f7b\u8584\u6211\uff0c\u4eb2\u6211\u4e00\u4e0b\u4e5f\u8ba9\u4ed6\u6bd2\u6027\u653b\u5fc3\u3002\u201d \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u8fd9\u5b9e\u5728\u592a\u5371\u9669\u4e86\uff0c\u5feb\u4e9b\u628a\u5b83\u4e22\u6389\u3002\u201d \u3000\u3000\u201c\u8fd8\u4e0d\u662f\u65f6\u5019\uff0c\u7b49\u90a3\u4f4d\u6559\u4e3b\u5c31\u622e\uff0c\u6b66\u6797\u91cd\u5f52\u5b81\u9759\uff0c\u518d\u8ba9\u4f60\u4e00\u7779\u836f\u6027\u4e4b\u6bd2\uff0c\u8fd9\u662f\u6211\u7684\u4fdd\u8eab\u7075\u7b26\uff0c\u6709\u4e86\u8fd9\u9897\u6bd2\u836f\uff0c\u6211\u624d\u80fd\u5fc3\u65e0\u6240\u60e7\u5730\u548c\u4ed6\u4eec\u5468\u65cb\u2026\u2026\u201d\u4e3e\u624b\u7406\u4e00\u4e0b\u98d8\u6d6e\u7684\u957f\u53d1\uff0c\u63a5\u9053\uff1a \u3000\u3000\u201c\u6559\u4e3b\u9634\u6c89\u7684\u53eb\u4eba\u65e0\u6cd5\u9884\u6d4b\uff0c\u90a3\u4e00\u65e5\uff0c\u4ed6\u76ee\u5149\u4e2d\u60c5\u6b32\u5954\u653e\uff0c\u89c1\u59be\u8eab\u51b7\u82e5\u51b0\u971c\uff0c\u7adf\u80fd\u514b\u5236\u6b32\u5ff5\uff0c\u6ca1\u6709\u52a8\u5f3a\uff0c\u53ea\u6b64\u4e00\u6869\uff0c\u5c31\u4e0d\u662f\u4e00\u822c\u6c5f\u6e56\u4eba\u80fd\u591f\u505a\u5230\u3002 \u3000\u3000\u7b49\u5230\u5929\u540e\u5bab\u7684\u4e09\u5927\u5bab\u4e3b\u548c\u9ec4\u5929\u738b\u7684\u5669\u8017\u4f20\u5230\uff0c\u4ed6\u7adf\u7136\u6d3e\u6211\u6765\uff0c\u548c\u4f60\u5546\u91cf\uff0c\u53ea\u8981\u4f60\u80af\u7f62\u624b\uff0c\u606f\u53bb\u8fd9\u573a\u7eb7\u4e89\uff0c\u4ed6\u613f\u610f\u5316\u5e72\u6208\uff0c\u64a4\u79bb\u4e2d\u539f\u3002 \u3000\u3000\u4ed6\u867d\u7136\u6545\u4f5c\u9547\u9759\uff0c\u4f46\u6211\u5df2\u77a7\u51fa\u4ed6\u5fc3\u4e2d\u5fae\u6709\u60ca\u614c\uff0c\u4e0d\u8fc7\uff0c\u8fd9\u4e2a\u4eba\uff0c\u4e0d\u80fd\u4fe1\u4efb\u3002\u201d \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u5bf9\uff01\u4e0d\u4e00\u4e3e\u6b7c\u706d\u4ed6\u4eec\uff0c\u4e0d\u80fd\u4f4f\u624b\uff0c\u4e5f\u65e0\u6cd5\u4f7f\u6c5f\u6e56\u5e73\u9759\u4e0b\u6765\u3002\u201d \u3000\u3000\u201c\u4e5f\u65e0\u6cd5\u5b89\u5fc3\u7684\u5e2e\u52a9\u541f\u96ea\u3001\u541f\u971c\u4e24\u4f4d\u59b9\u5b50\u8131\u79bb\u9b54\u5883\u2026\u2026\u201d\u84dd\u51e4\u9053\uff1a\u201c\u4f46\u90a3\u4f4d\u6559\u4e3b\u8be1\u8bc8\uff0c\u6211\u4e5f\u65e0\u6cd5\u9884\u6d4b\u5929\u9b54\u6d88\u9b42\u5927\u9635\u4e4b\u540e\uff0c\u4ed6\u8fd8\u6709\u4ec0\u4e48\u9634\u8c0b\u9b3c\u8ba1\uff1f\u4e0d\u8fc7\uff0c\u4e00\u5b9a\u4f1a\u6709\u3002\u201d \u3000\u3000\u201c\u4e58\u52bf\u4e00\u51fb\uff0c\u9664\u53bb\u6559\u4e3b\uff0c\u4e0d\u80fd\u6539\u53d8\uff0c\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\uff0c\u6240\u4ee5\uff0c\u6211\u4eec\u8981\u51fa\u5947\u5236\u80dc\u2026\u2026\u201d\u84dd\u51e4\u4f4e\u58f0\u7684\u8bf4\u51fa\u5979\u7684\u8ba1\u5212\u3002 \u3000\u3000\u6c5f\u67ab\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u597d\uff0c\u5c31\u8fd9\u4e48\u529e\uff01\u201d \u3000 \u7b2c\u4e8c\u5341\u516b\u56de\u3000\u6c5f\u5c11\u4fa0\u8ba1\u529d\u96ea\u971c\u5973 \u3000 \u3000\u3000\u4e00\u5904\u53e4\u6728\u53c2\u5929\u7684\u5bc6\u6797\uff0c\u770b\u4e0d\u51fa\u4efb\u4f55\u5f02\u72b6\u3002 \u3000\u3000\u6797\u5916\u662f\u4e00\u7247\u65f7\u91ce\uff0c\u89c6\u91ce\u8fbd\u9614\u3002 \u3000\u3000\u8fd9\u65f6\u5df2\u662f\u5b50\u591c\u65f6\u5206\uff0c\u6708\u9ed1\u98ce\u9ad8\uff0c\u65b9\u5706\u6570\u91cc\u4e4b\u5185\uff0c\u6ca1\u6709\u4e1d\u6beb\u52a8\u9759\u3002 \u3000\u3000\u9759\u7684\u5f88\u51fa\u5947\uff0c\u66f4\u6709\u4e9b\u602a\u5f02\uff01 \u3000\u3000\u500f\u5730\uff0c\u4e00\u6761\u4eba\u5f71\u75be\u5954\u800c\u6765\uff0c\u7531\u8fdc\u800c\u8fd1\uff0c\u8eab\u6cd5\u4e4b\u5feb\uff0c\u770b\u51fa\u662f\u4e2a\u8f7b\u529f\u5df2\u81fb\u6781\u9ad8\u5883\u754c\u7684\u5e74\u8f7b\u5973\u5b50\u3002 \u3000\u3000\u5f53\u5e74\u8f7b\u5973\u5b50\u5954\u8fd1\u6797\u5916\u65f6\uff0c\u7acb\u5373\u6536\u4f4f\u5954\u52bf\uff0c\u6717\u58f0\u9053\uff1a\u201c\u5929\u540e\u5bab\u84dd\u51e4\uff01\u201d \u3000\u3000\u539f\u6765\uff0c\u5979\u7adf\u662f\u5949\u795e\u79d8\u6559\u4e3b\u4e4b\u547d\uff0c\u53bb\u89c1\u6c5f\u67ab\u4f20\u8bdd\u7684\u84dd\u51e4\u5462\u3002 \u3000\u3000\u5c31\u5728\u5979\u6717\u58f0\u62a5\u51fa\u8eab\u4efd\u7684\u540c\u65f6\uff0c\u56db\u6761\u4eba\u5f71\u65e0\u58f0\u65e0\u606f\u5730\u81ea\u6797\u5185\u63a0\u51fa\uff0c\u8eab\u6cd5\u4e4b\u77eb\u6377\uff0c\u7edd\u4e0d\u5728\u84dd\u51e4\u4e4b\u4e0b\u3002 \u3000\u3000\u751a\u81f3\u6709\u8fc7\u4e4b\uff0c\u800c\u65e0\u4e0d\u53ca\u3002 \u3000\u3000\u4ed6\u4eec\u6b63\u662f\u795e\u79d8\u6559\u4e3b\u8eab\u8fb9\u7684\u9ed1\u8863\u5200\u624b\uff01 \u3000\u3000\u56db\u540d\u9ed1\u8863\u5200\u624b\u8ba4\u6e05\u662f\u84dd\u51e4\u65e0\u8bb9\uff0c\u4fbf\u9f50\u5411\u5979\u4e00\u62b1\u62f3\uff0c\u8f6c\u8eab\u53c8\u9690\u5165\u6797\u5185\uff0c\u663e\u7136\u4ed6\u4eec\u662f\u8d1f\u8d23\u62c5\u4efb\u8b66\u6212\u7684\u3002 \u3000\u3000\u84dd\u51e4\u6beb\u4e0d\u8fdf\u7591\uff0c\u901a\u884c\u65e0\u963b\u5730\u8fdb\u5165\u6797\u5185\u3002 \u3000\u3000\u53e4\u6728\u53c2\u5929\uff0c\u6797\u5185\u4e00\u7247\u6f06\u9ed1\uff0c\u51e0\u4e4e\u4f38\u624b\u4e0d\u89c1\u4e94\u6307\u3002 \u3000\u3000\u4f46\u84dd\u51e4\u5185\u529f\u6df1\u539a\uff0c\u8fd0\u8d77\u591c\u89c6\u81ea\u529b\uff0c\u80fd\u5c06\u773c\u524d\u666f\u7269\u770b\u7684\u4e00\u6e05\u4e8c\u695a\u3002 \u3000\u3000\u6df1\u5165\u5341\u4f59\u6587\uff0c\u53ea\u89c1\u5bc6\u6797\u6df1\u5904\uff0c\u8f9f\u51fa\u65b9\u5706\u6570\u4e08\u7684\u4e00\u5757\u7a7a\u5730\uff0c\u56db\u5468\u6811\u6728\u8349\u85e4\u5c3d\u88ab\u6e05\u9664\u3002 \u3000\u3000\u7a7a\u5730\u4e2d\u592e\uff0c\u505c\u7f6e\u4e00\u9876\u5f62\u6001\u7279\u6b8a\u7684\u9ed1\u8272\u5927\u8f7f\uff0c\u56db\u5468\u5206\u7acb\u56db\u540d\u6234\u7740\u9762\u5177\u7684\u9ed1\u8863\u5927\u6c49\uff0c\u53ca\u516b\u540d\u82b1\u679d\u62db\u5c55\uff0c\u5343\u5a07\u767e\u5a9a\u7684\u51b6\u8273\u5973\u5b50\u3002 \u3000\u3000\u5927\u8f7f\u7684\u5f62\u5f0f\u5f02\u4e8e\u4e2d\u539f\uff0c\u957f\u7ea6\u4e00\u4e08\uff0c\u5bbd\u5927\u5c3a\uff0c\u9876\u9ad8\u4e08\u4e8c\uff0c\u524d\u540e\u8f7f\u6746\u5404\u957f\u516b\u5c3a\u3002\u6746\u7684\u524d\u7aef\u88c5\u6709\u950b\u5229\u94c1\u77db\u3002 \u3000\u3000\u524d\u9762\u4ee5\u9ed1\u8272\u5e03\u5e18\u4ee3\u95e8\uff0c\u5176\u4ed6\u4e09\u9762\u5747\u6709\u5c0f\u7a97\uff0c\u91cc\u9762\u5782\u6302\u9ed1\u5e03\u5e18\uff0c\u4ece\u5185\u53ef\u63a2\u89c6\u56db\u5468\u4e00\u5207\u3002 \u3000\u3000\u6574\u4e2a\u5927\u8f7f\u5448\u73b0\u4e00\u7247\u9ed1\u8272\uff0c\u5927\u6982\u662f\u4e3a\u4e86\u4fbf\u4e8e\u591c\u95f4\u884c\u52a8\u5427\uff1f\u504f\u504f\u8fd9\u516b\u540d\u51b6\u8273\u5973\u5b50\uff0c\u7a7f\u7740\u5374\u4efd\u5916\u663e\u773c\u3001\u62db\u6447\u3002 \u3000\u3000\u5750\u5728\u8f7f\u5185\u7684\uff0c\u60f3\u5fc5\u5c31\u662f\u90a3\u4f4d\u795e\u79d8\u6559\u4e3b\u4e86\uff01 \u3000\u3000\u84dd\u51e4\u5bf9\u6559\u4e3b\u6267\u793c\u751a\u606d\uff0c\u8ddd\u79bb\u4e00\u652f\u5916\u7ad9\u5b9a\uff0c\u53cc\u624b\u4e00\u62b1\u62f3\uff0c\u606d\u58f0\u9053\uff1a\u201c\u5c5e\u4e0b\u8c28\u5411\u6559\u4e3b\u590d\u884c\uff01\u201d \u3000\u3000\u8f7f\u5185\u4f20\u51fa\u4e2a\u6df1\u6c89\u7684\u58f0\u97f3\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u89c1\u8fc7\u6c5f\u67ab\u4e86\uff1f\u201d \u3000\u3000\u201c\u56de\u6559\u4e3b\uff0c\u5c5e\u4e0b\u5df2\u89c1\u8fc7\u4ed6\u672c\u4eba\u3002\u201d \u3000\u3000\u201c\u4ed6\u600e\u4e48\u8bf4\uff1f\u201d \u3000\u3000\u84dd\u98ce\u4ece\u6000\u91cc\u638f\u51fa\u4e00\u5c01\u4fe1\uff0c\u53cc\u624b\u9012\u5411\u524d\uff0c\u9053\uff1a\u201c\u6c5f\u67ab\u5df2\u540c\u610f\uff0c\u5c5e\u4e0b\u5e26\u56de\u4ed6\u7684\u4eb2\u7b14\u5bc6\u51fd\uff0c\u8bf7\u6559\u4e3b\u8fc7\u76ee\u3002\u201d \u3000\u3000\u201c\u62ff\u6765\u6211\u770b\u3002\u201d\u201c\u662f\uff01\u201d \u3000\u3000\u84dd\u51e4\u521a\u8981\u4e3e\u6b65\u5411\u524d\uff0c\u4e00\u540d\u51b6\u8273\u5973\u5b50\u5df2\u8d70\u6765\uff0c\u4ece\u5979\u624b\u4e0a\u63a5\u8fc7\u5bc6\u51fd\uff0c\u663e\u7136\u4e0d\u8ba9\u84dd\u51e4\u63a5\u8fd1\u5927\u8f7f\u3002 \u3000\u3000\u7531\u6b64\u53ef\u89c1\uff0c\u8fd9\u4f4d\u795e\u79d8\u6559\u4e3b\u6027\u60c5\u591a\u7591\uff0c\u9664\u4e86\u56db\u540d\u9ed1\u8863\u52b2\u88c5\u5927\u6c49\u53ca\u8fd9\u516b\u540d\u5973\u5b50\uff0c\u8fde\u84dd\u51e4\u4e5f\u4e0d\u5927\u4fe1\u4efb\u3002 \u3000\u3000\u51b6\u8273\u5973\u5b50\u8d70\u56de\u8f7f\u524d\uff0c\u6380\u5f00\u8f7f\u95e8\u5e03\u5e18\uff0c\u5c06\u5bc6\u51fd\u4ece\u5e18\u7f1d\u4e2d\u9012\u4e86\u8fdb\u53bb\u3002 \u3000\u3000\u8fd9\u5c01\u5bc6\u51fd\u662f\u6c5f\u67ab\u4eb2\u7b14\u6240\u5199\uff0c\u4f46\u4e5f\u662f\u84dd\u51e4\u732e\u7684\u8ba1\u3002 \u3000\u3000\u4fe1\u4e0a\u867d\u4ec5\u5be5\u5be5\u6570\u8bed\uff0c\u4f46\u7b80\u5355\u660e\u4e86\uff0c\u8868\u660e\u63a5\u53d7\u795e\u79d8\u6559\u4e3b\u5316\u89e3\u5e72\u6208\u7684\u5efa\u8bae\u3002 \u3000\u3000\u8f7f\u5185\u7684\u795e\u79d8\u6559\u4e3b\u770b\u9ed1\u5bc6\u51fd\uff0c\u4f3c\u4e4e\u5f88\u6ee1\u610f\uff0c\u8fde\u5938\u4e24\u58f0\u201c\u5f88\u597d\uff0c\u5f88\u597d\u3002\u201d\u63a5\u7740\u95ee\u9053\uff1a\u201c\u4ed6\u6ca1\u6709\u4e3a\u96be\u4f60\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\u3002\u201d\u84dd\u51e4\u606d\u58f0\u56de\u7b54\uff1a\u201c\u5c5e\u4e0b\u4e00\u53bb\u5c31\u8868\u660e\u6765\u610f\uff0c\u4ed6\u4eec\u5012\u8fd8\u9075\u5b88\u4e24\u56fd\u4ea4\u6218\uff0c\u4e0d\u65a9\u6765\u4f7f\u3002\u201d \u3000\u3000\u795e\u79d8\u6559\u4e3b\u201c\u55ef\u201d\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u628a\u4f60\u53bb\u89c1\u6c5f\u67ab\u7684\u60c5\u5f62\u8bf4\u4e00\u904d\uff0c\u8981\u8be6\u7ec6\u4e9b\uff0c\u5de8\u7ec6\u65e0\u9057\u3002\u201d \u3000\u3000\u201c\u662f\uff01\u201d\u84dd\u51e4\u552f\u547d\u662f\u4ece\uff0c\u4ece\u5bb9\u4e0d\u8feb\u9053\uff1a\u201c\u5c5e\u4e0b\u53bb\u65f6\uff0c\u5373\u88ab\u4ed6\u4eec\u8d1f\u8d23\u6212\u5907\u7684\u4eba\u8ba4\u51fa\u622a\u4f4f\uff0c\u867d\u7ecf\u5c5e\u4e0b\u8868\u660e\u662f\u5949\u547d\u5f80\u89c1\u6c5f\u67ab\uff0c\u4ecd\u88ab\u4ed6\u4eec\u641c\u8eab\u540e\uff0c\u59cb\u62bc\u5165\u8352\u5e99\u5927\u6bbf\u3002 \u3000\u3000\u5f53\u65f6\u4e00\u5e72\u4eba\u6b63\u5728\u5927\u5403\u5927\u559d\uff0c\u7531\u5f20\u56db\u59d1\u5728\u95ee\u660e\u5c5e\u4e0b\u6765\u610f\u540e\uff0c\u4f3c\u4e4e\u4e0d\u4fbf\u64c5\u81ea\u4f5c\u4e3b\uff0c\u4fbf\u5c06\u6211\u5e26\u5f80\u540e\u9762\u7684\u65b9\u4e08\u5ba4\u3002 \u3000\u3000\u65b9\u4e08\u5ba4\u5916\u6212\u5907\u68ee\u4e25\uff0c\u539f\u6765\u6c5f\u67ab\u53d7\u4e86\u4f24\u2026\u2026\u201d \u3000\u3000\u8f7f\u5185\u7684\u795e\u79d8\u6559\u4e3b\u6253\u65ad\u5979\u7684\u8bdd\uff0c\u4f3c\u5bf9\u6c5f\u67ab\u53d7\u4f24\u5f88\u91cd\u89c6\uff0c\u5173\u5207\u5730\u95ee\uff1a\u201c\u53d7\u7684\u4ec0\u4e48\u4f24\uff1f\u201d \u3000\u3000\u84dd\u51e4\u5fae\u5fae\u4e00\u6014\uff0c\u7b54\u9053\uff1a\u201c\u5c5e\u4e0b\u4e0d\u77e5\u9053\uff0c\u4e0d\u8fc7\uff0c\u770b\u60c5\u5f62\u50cf\u662f\u7ec3\u529f\u64cd\u4e4b\u8fc7\u6025\uff0c\u6709\u4e9b\u8d70\u706b\u5165\u9b54\u8ff9\u8c61\u3002\u201d \u3000\u3000\u795e\u79d8\u6559\u4e3b\u201c\u54e6\uff1f\u201d\u4e86\u4e00\u58f0\uff0c\u6ca1\u6709\u8ffd\u95ee\u4e0b\u53bb\u3002 \u3000\u3000\u201c\u7ee7\u7eed\u8bf4\uff01\u201d \u3000\u3000\u201c\u662f\uff01\u201d\u84dd\u51e4\u63a5\u4e0b\u53bb\u8bf4\uff1a\u201c\u6c5f\u67ab\u5bf9\u5c5e\u4e0b\u8fd9\u4e0d\u901f\u4e4b\u5ba2\u9887\u89c9\u60ca\u8bb6\uff0c\u7ecf\u5f20\u56db\u59d1\u5411\u4ed6\u9644\u8033\u8bf4\u660e\u6211\u7684\u6765\u610f\u540e\uff0c\u4ed6\u5f88\u679c\u65ad\u5f53\u4e0b\u5373\u6beb\u4e0d\u72b9\u8c6b\u5730\u4e00\u56de\u7b54\u5e94\u3002 \u3000\u3000\u5c5e\u4e0b\u89c1\u4ed6\u7b54\u5e94\u7684\u592a\u8fc7\u723d\u5feb\uff0c\u4e0d\u514d\u6709\u4e9b\u8d77\u7591\uff0c\u803d\u5fc3\u4ed6\u53e3\u662f\u5fc3\u975e\uff0c\u4fbf\u8868\u793a\u53e3\u8bf4\u65e0\u51ed\uff0c\u8981\u6c42\u4ed6\u4eb2\u7b14\u51fd\u590d\uff0c\u8ba9\u6211\u63e3\u56de\u590d\u547d\u3002 \u3000\u3000\u60f3\u4e0d\u5230\u4ed6\u7adf\u672a\u62d2\u7edd\uff0c\u7acb\u5373\u8981\u5f20\u56db\u59d1\u53bb\u53d6\u7eb8\u7b14\uff0c\u8d81\u7740\u65b9\u4e08\u5ba4\u91cc\u6ca1\u6709\u7b2c\u4e09\u8005\u5728\u573a\uff0c\u4ea4\u5f85\u4e86\u5c5e\u4e0b\u4e00\u756a\u8bdd\u2026\u2026\u201d \u3000\u3000\u8bf4\u5230\u6b64\u5904\uff0c\u50cf\u662f\u6709\u6240\u987e\u5fcc\uff0c\u7a81\u7136\u6b32\u8a00\u53c8\u6b62\u8d77\u6765\u3002 \u3000\u3000\u795e\u79d8\u6559\u4e3b\u6c89\u58f0\u559d\u95ee\uff1a\u201c\u4e3a\u4ec0\u4e48\u4e0d\u8bf4\u4e0b\u53bb\uff1f\u201d \u3000\u3000\u201c\u8fd9\u2026\u2026\u201d\u84dd\u51e4\u8fdf\u7591\u4e86\u4e00\u4e0b\uff0c\u90d1\u91cd\u5176\u4e8b\u9053\uff1a\u201c\u6c5f\u67ab\u518d\u4e09\u4ea4\u5f85\uff0c\u6b64\u4e8b\u5173\u7cfb\u91cd\u5927\uff0c\u9664\u4e86\u6559\u4e3b\u4e4b\u5916\uff0c\u7edd\u4e0d\u80fd\u8ba9\u4efb\u4f55\u4eba\u5f97\u77e5\u3002\u201d \u3000\u3000\u795e\u79d8\u6559\u4e3b\u4e0d\u60a6\u5730\u51b7\u51b7\u4e00\u54fc\uff0c\u9053\uff1a\u201c\u5728\u573a\u7684\u4efb\u4f55\u4e00\u4eba\uff0c\u6211\u90fd\u4fe1\u5f97\u8fc7\uff0c\u6709\u8bdd\u5c3d\u7ba1\u76f4\u8bf4\uff0c\u4e0d\u5fc5\u987e\u5fcc\uff01\u201d \u3000\u3000\u84dd\u51e4\u4e3a\u96be\u9053\uff1a\u201c\u53ef\u662f\uff0c\u5c5e\u4e0b\u5df2\u7b54\u5e94\u6c5f\u67ab\u2026\u2026\u201d \u3000\u3000\u201c\u54fc\uff01\u201d\u795e\u79d8\u6559\u4e3b\u6012\u58f0\u9053\uff1a\u201c\u597d\uff0c\u4f60\u5c31\u8fdb\u8f7f\u6765\u5427\uff01\u201d \u3000\u3000\u201c\u662f\uff01\u201d \u3000\u3000\u84dd\u51e4\u6b63\u4e2d\u4e0b\u6000\uff0c\u606d\u5e94\u4e86\u4e00\u58f0\uff0c\u5411\u5927\u8f7f\u8d70\u53bb\u3002 \u3000\u3000\u65e2\u7531\u6559\u4e3b\u4eb2\u53e3\u4e0b\u4ee4\uff0c\u5b88\u5728\u8f7f\u524d\u7684\u4e24\u540d\u9ed1\u8863\u52b2\u88c5\u5927\u6c49\uff0c\u53ca\u56db\u540d\u51b6\u8273\u5973\u5b50\u5747\u672a\u62e6\u963b\u3002 \u3000\u3000\u5426\u5219\uff0c\u4efb\u4f55\u4eba\u5982\u64c5\u81ea\u63a5\u8fd1\u5929\u8f7f\u4e94\u5c3a\u4e4b\u5185\uff0c\u5fc5\u906d\u622a\u6740\uff01 \u3000\u3000\u84dd\u51e4\u867d\u6709\u70b9\u7d27\u5f20\uff0c\u4ecd\u80fd\u529b\u6301\u9547\u5b9a\uff0c\u4ece\u5bb9\u4e0d\u8feb\u5730\u8d70\u5230\u8f7f\u524d\uff0c\u4ef2\u624b\u4e00\u6380\u6df1\u5782\u7684\u9ed1\u5e03\u5e18\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u7535\u5149\u77f3\u706b\u4e4b\u95f4\uff0c\u84dd\u51e4\u4ece\u8896\u7ba1\u5185\u62bd\u51fa\u6697\u85cf\u7684\u7fe0\u7389\u5200\uff0c\u8fde\u4eba\u5e26\u5200\u6251\u8fdb\u4e86\u8f7f\u5185\u3002 \u3000\u3000\u7fe0\u7389\u5200\u662f\u6c5f\u67ab\u7684\u968f\u8eab\u5175\u5203\uff0c\u600e\u4f1a\u4ea4\u7ed9\u84dd\u51e4\uff1f \u3000\u3000\u539f\u6765\uff0c\u8fd9\u4f4d\u84dd\u98ce\u6b63\u662f\u6c5f\u67ab\u6240\u4e54\u626e\uff01 \u3000\u3000\u84dd\u51e4\u9887\u6709\u81ea\u77e5\u4e4b\u660e\uff0c\u7edd\u5bf9\u65e0\u529b\u4e00\u4e3e\u640f\u6740\u795e\u79d8\u6559\u4e3b\uff0c\u5373\u4f7f\u8fd1\u5728\u54ab\u5c3a\u4e5f\u51fa\u4e0d\u4e86\u624b\uff0c\u6240\u4ee5\u5979\u5411\u6c5f\u67ab\u732e\u51fa\u4e00\u8ba1\uff0c\u82e5\u6b32\u9664\u6b64\u7f6a\u9b41\u7978\u9996\uff0c\u53ea\u6709\u6548\u6cd5\u8346\u67ef\u523a\u79e6\u738b\uff0c\u6765\u4e2a\u201c\u56fe\u7a77\u5315\u73b0\u201d\u8fd9\u4e2a\u640f\u6740\u8ba1\u5212\uff0c\u4e0d\u4ec5\u987b\u5192\u6781\u5927\u5371\u9669\uff0c\u800c\u4e14\u662f\u5b64\u6ce8\u4e00\u63b7\uff0c\u4e07\u4e00\u5931\u624b\uff0c\u7edd\u4e0d\u53ef\u80fd\u518d\u6709\u7b2c\u4e8c\u6b21\u51fa\u624b\u7684\u673a\u4f1a\u3002 \u3000\u3000\u66f4\u91cd\u8981\u7684\u662f\uff0c\u5373\u4f7f\u987a\u5229\u5f97\u624b\uff0c\u4e5f\u5f88\u53ef\u80fd\u662f\u4e24\u8d25\u4ff1\u4f24\uff0c\u751a\u81f3\u540c\u5f52\u4e8e\u5c3d\u3002\u90a3\u4e48\u6267\u884c\u8fd9\u4e2a\u4efb\u52a1\u7684\u4eba\uff0c\u60f3\u5fc5\u62b1\u5b9a\u5fc5\u6b7b\u51b3\u5fc3\uff0c\u5c82\u4e0d\u5f62\u540c\u6b7b\u58eb\uff1f \u3000\u3000\u4f46\u8fd9\u662f\u53ef\u9047\u800c\u4e0d\u53ef\u6c42\u7684\u5927\u597d\u826f\u673a\uff0c\u9519\u8fc7\u4eca\u591c\uff0c\u518d\u60f3\u8bdb\u6b64\u5de8\u5978\uff0c\u6050\u6015\u5c31\u4e0d\u592a\u5bb9\u6613\u4e86\u3002 \u3000\u3000\u5230\u76ee\u524d\u4e3a\u6b62\uff0c\u6240\u6709\u4eba\u90fd\u672a\u6b63\u9762\u4e0e\u90a3\u795e\u79d8\u6559\u4e3b\u63a5\u89e6\u8fc7\uff0c\u4e0d\u77e5\u4ed6\u7684\u6b66\u529f\u7a76\u7adf\u6709\u591a\u9ad8\u3002\u6839\u636e\u84dd\u51e4\u7684\u4f30\u8ba1\uff0c\u53ea\u53ef\u7528\u201c\u9ad8\u6df1\u83ab\u6d4b\u201d\u6765\u5f62\u5bb9\u3002 \u3000\u3000\u5728\u573a\u7684\u4eba\u4e4b\u4e2d\uff0c\u541f\u96ea\u3001\u541f\u971c\u6700\u5177\u6740\u4f24\u529b\uff0c\u800c\u4e14\u8eab\u6750\u4e0e\u84dd\u51e4\u76f8\u4f3c\uff0c\u53c8\u5c3d\u5f97\u5f20\u56db\u59d1\u6613\u5bb9\u672f\u7684\u771f\u4f20\uff0c\u5e94\u662f\u5192\u5145\u84dd\u51e4\u56de\u53bb\u590d\u547d\uff0c\u4f3a\u673a\u63a5\u8fd1\u795e\u89c4\u6559\u4e3b\uff0c\u6267\u884c\u640f\u6740\u8ba1\u5212\u7684\u6700\u9002\u5f53\u4eba\u9009\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u6c5f\u67ab\u4e0d\u613f\u5979\u4eec\u4ee5\u8eab\u6d89\u9669\u3002 \u3000\u3000\u4ed6\u6240\u987e\u5fcc\u7684\uff0c\u5e76\u975e\u6015\u5979\u4eec\u5931\u624b\uff0c\u800c\u662f\u803d\u5fc3\u8fd9\u4e24\u4e2a\u5c11\u5973\u4e00\u65e6\u6740\u7ea2\u4e86\u773c\uff0c\u51f6\u6027\u5927\u53d1\uff0c\u5c06\u4f1a\u9020\u6210\u4e0d\u53ef\u6536\u62fe\u7684\u540e\u679c\u3002 \u3000\u3000\u9664\u4e86\u541f\u96ea\u548c\u541f\u971c\uff0c\u9752\u51e4\u65e0\u6b64\u80fd\u8010\uff0c\u5f20\u56db\u59d1\u3001\u7f57\u5170\u90fd\u6ca1\u6709\u628a\u63e1\u80fd\u4e00\u51fb\u640f\u6740\u90a3\u795e\u79d8\u6559\u4e3b\uff0c\u6700\u540e\u7531\u6c5f\u67ab\u81ea\u544a\u594b\u52c7\uff0c\u5f53\u4ec1\u4e0d\u8ba9\u5730\u5411\u5f20\u56db\u59d1\u8bf7\u547d\uff0c\u81ea\u613f\u5192\u5145\u84dd\u51e4\u53bb\u89c1\u795e\u79d8\u6559\u4e3b\u3002 \u3000\u3000\u6c5f\u67ab\u662f\u6602\u85cf\u516d\u5c3a\u4e4b\u8eaf\u7684\u7537\u5b50\uff0c\u5982\u4f55\u80fd\u4e54\u626e\u84dd\u51e4\uff1f \u3000\u3000\u8fd9\u4e2a\u95ee\u9898\u96be\u4e0d\u5230\u4ed6\uff0c\u4ece\u201c\u4f20\u771f\u8bb0\u201d\u4e0a\u4e60\u5f97\u7684\u201c\u7f29\u9aa8\u672f\u201d\uff0c\u4e0d\u4f46\u53ef\u4ee5\u968f\u5fc3\u6240\u6b32\u6539\u53d8\u4f53\u578b\uff0c\u66f4\u80fd\u4ee5\u201c\u53d8\u97f3\u529f\u201d\u6a21\u4eff\u84dd\u98ce\u7684\u53e3\u97f3\uff0c\u800c\u4e14\u7ef4\u5999\u7ef4\u8096\uff0c\u51e0\u53ef\u4e71\u771f\u3002 \u3000\u3000\u52a0\u4e0a\u5f20\u56db\u59d1\u5de7\u593a\u5929\u5de5\u7684\u6613\u5bb9\u672f\uff0c\u6c5f\u67ab\u5c31\u6210\u4e86\u84dd\u51e4\u3002 \u3000\u3000\u6c5f\u67ab\u8fd9\u51fa\u5176\u4e0d\u610f\u7684\u4e00\u51fb\uff0c\u771f\u53ef\u8bf4\u662f\u96f7\u9706\u4e07\u94a7\uff0c\u75be\u5982\u6d41\u661f\uff0c\u4efb\u4f55\u8f7f\u5185\u7684\u795e\u79d8\u6559\u4e3b\u6b66\u529f\u518d\u9ad8\uff0c\u4e5f\u4f1a\u63aa\u624b\u4e0d\u53ca\u3002 \u3000\u3000\u4e0d\u6599\u4ed6\u7a81\u88ad\u7684\uff0c\u7adf\u7136\u662f\u4e00\u9876\u7a7a\u8f7f\uff01 \u3000\u3000\u6c5f\u67ab\u60c5\u77e5\u4e0d\u5999\uff0c\u867d\u5728\u6781\u5ea6\u60ca\u5f02\u4e4b\u4e0b\uff0c\u53cd\u5e94\u4ecd\u7136\u6781\u5feb\uff0c\u7acb\u65f6\u5de6\u638c\u75be\u53d1\uff0c\u85c9\u90a3\u4e00\u80a1\u53cd\u9707\u4e4b\u529b\uff0c\u5168\u8eab\u66b4\u9000\u800c\u51fa\u3002 \u3000\u3000\u51e0\u4e4e\u662f\u5728\u540c\u65f6\uff0c\u8f7f\u5185\u7684\u4e0a\u4e0b\u5de6\u53f3\u548c\u540e\u65b9\uff0c\u5f39\u51fa\u6570\u5341\u628a\u5229\u5203\uff0c\u4ea4\u53c9\u523a\u5411\u4e2d\u95f4\u3002 \u3000\u3000\u5982\u679c\u6c5f\u67ab\u4e0d\u65f6\u53ca\u65f6\u66b4\u9000\u51fa\u8f7f\u5916\uff0c\u5fc5\u88ab\u523a\u624e\u6210\u8089\u9171\u3002 \u3000\u3000\u597d\u9669\uff01 \u3000\u3000\u6c5f\u67ab\u771f\u662f\u5343\u94a7\u4e00\u53d1\u4e0b\uff0c\u6b7b\u91cc\u9003\u751f\u3002 \u3000\u3000\u56db\u540d\u9ed1\u8863\u52b2\u88c5\u5927\u6c49\uff0c\u53ca\u516b\u540d\u5e74\u8f7b\u5973\u5b50\u7684\u53cd\u5e94\u4e5f\u4e0d\u6162\uff0c\u53ea\u89c1\u4eba\u5f71\u7ffb\u98de\uff0c\u5404\u636e\u4e00\u65b9\uff0c\u5df2\u5c06\u6c5f\u67ab\u56e2\u56e2\u56f4\u4f4f\u3002 \u3000\u3000\u4ed6\u4eec\u672a\u53ca\u51fa\u624b\uff0c\u5ffd\u542c\u8f7f\u5185\u53d1\u51fa\u4e00\u9635\u72c2\u7b11\u3002 \u3000\u3000\u6c5f\u67ab\u4e0d\u7981\u6697\u89c9\u8be7\u5f02\u4e0d\u5df2\uff0c\u5206\u660e\u662f\u4e00\u9876\u7a7a\u8f7f\uff0c\u8fd9\u72c2\u7b11\u58f0\u53c8\u662f\u4ece\u4f55\u5904\u4f20\u51fa\uff1f \u3000\u3000\u4f46\u4ed6\u968f\u5373\u660e\u767d\u4e86\uff0c\u8f7f\u9876\u9ad8\u8fbe\u4e08\u4e8c\uff0c\u60f3\u5fc5\u9876\u90e8\u8bbe\u6709\u5939\u5c42\uff0c\u6697\u85cf\u7384\u673a\uff0c\u795e\u79d8\u6559\u4e3b\u663e\u7136\u662f\u85cf\u8eab\u5728\u8f7f\u9876\u5939\u5c42\u5185\u3002 \u3000\u3000\u8fd9\u4e00\u7740\u5b9e\u5927\u51fa\u610f\u6599\u4e4b\u5916\u3002 \u3000\u3000\u5f53\u7136\u4e0d\u80fd\u602a\u84dd\u51e4\uff0c\u5979\u54ea\u4f1a\u60f3\u5230\u795e\u79d8\u6559\u4e3b\u4f3c\u6709\u5148\u89c1\u4e4b\u660e\uff0c\u968f\u65f6\u968f\u5730\u90fd\u4e0d\u5fd8\u4e25\u5bc6\u9632\u8303\u3002 \u3000\u3000\u7b11\u58f0\u4e00\u843d\uff0c\u4fbf\u542c\u795e\u79d8\u6559\u4e3b\u51b7\u58f0\u9053\uff1a\u201c\u4e0d\u7ba1\u4f60\u662f\u4ec0\u4e48\u4eba\uff0c\u80fd\u628a\u84dd\u51e4\u4e54\u626e\u5f97\u5982\u6b64\u7ef4\u5999\u7ef4\u8096\uff0c\u8fd9\u6837\u7684\u6613\u5bb9\u672f\u786e\u5b9e\u9ad8\u660e\u3002 \u3000\u3000\u5c24\u5176\uff0c\u4f60\u80fd\u5177\u6709\u8fd9\u4efd\u80c6\u8bc6\u548c\u8eab\u624b\uff0c\u5b9e\u5728\u4ee4\u6211\u8877\u5fc3\u4f69\u670d\uff01\u5982\u679c\u4e0d\u51fa\u6211\u6240\u6599\uff0c\u5927\u6982\u4f60\u5c31\u662f\u5f20\u56db\u59d1\u5427\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u5fc3\u91cc\u6697\u7b11\uff0c\u5fd6\u9053\uff1a\u201c\u4f60\u8fd9\u8001\u5978\u5de8\u733e\uff0c\u5c45\u7136\u4e5f\u770b\u8d70\u4e86\u773c\uff01\u201d \u3000\u3000\u8fd9\u4e5f\u96be\u602a\uff0c\u5f20\u56db\u59d1\u7684\u6613\u5bb9\u672f\u4eab\u8a89\u6b66\u6797\u591a\u5e74\uff0c\u81f3\u4eca\u65e0\u51fa\u5176\u53f3\u3002\u66f4\u4f55\u51b5\uff0c\u5373\u4f7f\u795e\u79d8\u6559\u4e3b\u6000\u7591\u8fd9\u84dd\u51e4\u662f\u4efb\u4f55\u4eba\u4e54\u626e\uff0c\u751a\u81f3\u731c\u5230\u7f57\u5170\uff0c\u6216\u662f\u96ea\u3001\u971c\u4e24\u59d0\u59b9\uff0c\u4e5f\u7edd\u4e0d\u4f1a\u60f3\u5230\u662f\u7537\u626e\u5973\u88c5\u7684\u6c5f\u67ab\u3002 \u3000\u3000\u6c5f\u67ab\u7f6e\u4e4b\u4e00\u7b11\uff0c\u65e2\u4e0d\u627f\u8ba4\uff0c\u4e5f\u4e0d\u5426\u8ba4\u3002\u53ea\u662f\u9ed8\u9ed8\u6ce8\u89c6\u7740\u8f7f\u9876\u3002 \u3000\u3000\u4ed6\u5df2\u53ef\u786e\u5b9a\uff0c\u8f7f\u9876\u53e6\u6709\u7384\u673a\uff0c\u795e\u79d8\u6559\u4e3b\u5c31\u85cf\u8eab\u5176\u4e2d\uff0c\u624d\u9003\u8fc7\u4ed6\u81f4\u547d\u4e00\u51fb\u3002 \u3000\u3000\u73b0\u5728\u6c5f\u67ab\u6697\u81ea\u76d8\u7b97\uff0c\u5305\u56f4\u7684\u8fd9\u6279\u7537\u5973\u5e76\u4e0d\u770b\u5728\u773c\u91cc\uff0c\u795e\u79d8\u6559\u4e3b\u4e5f\u4e0d\u8db3\u4e3a\u60e7\uff0c\u95ee\u9898\u662f\uff0c\u5982\u679c\u4ed6\u518d\u6b21\u594b\u4e0d\u987e\u8eab\u98de\u6251\u4e00\u51fb\uff0c\u624b\u4e2d\u7684\u7fe0\u7389\u5200\u662f\u5426\u80fd\u8d2f\u7a7f\u8f7f\u9876\u2026\u2026 \u3000\u3000\u5ff5\u72b9\u672a\u4e86\uff0c\u53c8\u542c\u8f7f\u9876\u5185\u53d1\u51fa\u795e\u79d8\u6559\u4e3b\u7684\u58f0\u97f3\uff0c\u8bf4\u9053\uff1a\u201c\u5f20\u56db\u59d1\uff0c\u4f60\u4e0d\u7528\u6789\u8d39\u5fc3\u673a\uff0c\u867d\u7136\u4f60\u624b\u6267\u7fe0\u7389\u5200\uff0c\u65e0\u575a\u4e0d\u6467\uff0c\u4f46\u8981\u60f3\u4e00\u5200\u51fb\u7a7f\u8fd9\u8f7f\u9876\uff0c\u6050\u6015\u4f60\u5c06\u5927\u5931\u6240\u671b\u4e86\uff01\u54c8\u54c8\u2026\u2026\u201d \u3000\u3000\u5999\u554a\uff0c\u4ed6\u8ba4\u5b9a\u4e86\u773c\u524d\u7684\u84dd\u51e4\u662f\u5f20\u56db\u59d1\u4e54\u626e\uff01 \u3000\u3000\u4f46\u4ed6\u80fd\u6599\u4e8b\u5982\u795e\uff0c\u4e00\u8bed\u9053\u7834\u6c5f\u67ab\u5fc3\u4e2d\u6253\u7684\u4ec0\u4e48\u4e3b\u610f\uff0c\u8db3\u89c1\u4e5f\u4e0d\u662f\u4e2a\u7701\u6cb9\u4e4b\u706f\u3002 \u3000\u3000\u6362\u8a00\u4e4b\uff0c\u4ed6\u6709\u6043\u65e0\u6050\uff0c\u770b\u51c6\u7fe0\u7389\u5200\u7834\u4e0d\u4e86\u7cbe\u94a2\u6253\u9020\u7684\u8f7f\u9876\uff0c\u4f24\u4e0d\u4e86\u4ed6\u3002 \u3000\u3000\u6c5f\u67ab\u4ecd\u7136\u4fdd\u6301\u6c89\u9ed8\uff0c\u773c\u5149\u6d41\u8f6c\uff0c\u89c1\u5341\u4e8c\u540d\u7537\u5973\u4e25\u9635\u4ee5\u5f85\uff0c\u8822\u8822\u6b32\u52a8\uff0c\u53ea\u5f85\u795e\u79d8\u6559\u4e3b\u4e00\u58f0\u4ee4\u4e0b\uff0c\u7acb\u65f6\u7fa4\u8d77\u800c\u653b\u3002 \u3000\u3000\u795e\u79d8\u6559\u4e3b\u53c8\u9053\uff1a\u201c\u4e0d\u8fc7\u6211\u4e5f\u5f88\u5931\u671b\uff0c\u6d3e\u84dd\u51e4\u53bb\u8ddf\u4f60\u4eec\u8c08\u5224\uff0c\u539f\u4ee5\u4e3a\u6c5f\u67ab\u4e00\u5b9a\u4f1a\u8ddf\u8e2a\u5979\u6765\u6b64\uff0c\u60f3\u4e0d\u5230\u6765\u7684\u7adf\u7136\u662f\u4f60\uff01\u201d \u3000\u3000\u6c5f\u67ab\u7ec8\u4e8e\u5f00\u4e86\u91d1\u53e3\uff1a\u201c\u4f60\u5e0c\u671b\u6765\u7684\u662f\u6c5f\u67ab\uff1f\u201d \u3000\u3000\u795e\u79d8\u6559\u4e3b\u9053\uff1a\u201c\u5982\u679c\u4e0d\u662f\u4e3a\u4e86\u4ed6\uff0c\u6211\u4f55\u5fc5\u52b3\u5e08\u52a8\u4f17\uff0c\u9080\u6765\u5c0f\u897f\u5929\u96f7\u97f3\u5bfa\u56db\u5927\u795e\u9b54\u52a9\u9635\uff0c\u8fd8\u52a8\u7528\u4e5d\u9b54\u5973\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u63a5\u53e3\u8ba5\u9053\uff1a\u201c\u6211\u597d\u50cf\u53ea\u89c1\u5230\u516b\u4e2a\uff01\u201d \u3000\u3000\u795e\u79d8\u6559\u4e3b\u563f\u7136\u51b7\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u867d\u635f\u5931\u4e86\u4e00\u540d\u9b54\u5973\uff0c\u4f46\u8fd9\u516b\u4eba\u5df2\u8db3\u591f\u6c5f\u67ab\u90a3\u5c0f\u5b50\u6d88\u53d7\u4e86\uff01\u201d \u3000\u3000\u6c5f\u67ab\u7b11\u95ee\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u4f60\u7684\u4e00\u5207\u5b89\u6392\u548c\u5e03\u7f6e\uff0c\u90fd\u662f\u51b2\u7740\u6c5f\u67ab\u800c\u6765\u7f57\uff1f\u201d \u3000\u3000\u795e\u79d8\u6559\u4e3b\u6c89\u58f0\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u4e0d\u5fc5\u5f53\u66ff\u6b7b\u9b3c\uff01\u201d \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u4f46\u6211\u5df2\u7ecf\u6765\u4e86\uff01\u201d \u3000\u3000\u201c\u65e2\u6765\u4e4b\uff0c\u5219\u5b89\u4e4b\u3002\u201d\u795e\u79d8\u6559\u4e3b\u9053\uff1a\u201c\u773c\u524d\u4f60\u53ea\u6709\u4e24\u4e2a\u9009\u62e9\uff0c\u964d\u6216\u6b7b\uff01\u672c\u5ea7\u6b63\u503c\u7528\u4eba\u4e4b\u9645\uff0c\u5982\u679c\u5f20\u59d1\u5a18\u613f\u610f\u5c48\u5c31\uff0c\u5929\u540e\u5bab\uff0c\u5929\u9a6c\u5802\u6216\u5929\u738b\u95e8\uff0c\u4efb\u7531\u4f60\u62e9\u4e00\u4e3b\u6301\uff0c\u5426\u5219\u561b\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u6de1\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u90a3\u5c31\u6b7b\u5b9a\u4e86\uff1f\u201d \u3000\u3000\u795e\u79d8\u6559\u4e3b\u8868\u793a\u5a49\u60dc\u9053\uff1a\u201c\u5567\u5567\u5567\uff0c\u5f20\u56db\u59d1\u7684\u6613\u5bb9\u672f\uff0c\u582a\u79f0\u72ec\u6b65\u6b66\u6797\uff0c\u540d\u6ee1\u5929\u4e0b\u3002\u6211\u8981\u7f6e\u4f60\u4e8e\u6b7b\u5730\uff0c\u53ea\u4e0d\u8fc7\u662f\u4e3e\u624b\u4e4b\u52b3\uff0c\u751a\u81f3\u4e0d\u987b\u4eb2\u81ea\u52a8\u624b\uff0c\u4e0d\u8fc7\uff0c\u8bf4\u5b9e\u5728\u7684\uff0c\u6211\u771f\u4e0d\u613f\u4f60\u7684\u6613\u5bb9\u5999\u672f\u4ece\u6b64\u6210\u4e3a\u7edd\u54cd\u5462\u3002\u201d \u3000\u3000\u6c5f\u67ab\u51b7\u51b7\u4e00\u54fc\uff0c\u9053\uff1a\u201c\u6211\u4eca\u591c\u6765\u6b64\uff0c\u65e9\u5df2\u5c06\u751f\u6b7b\u7f6e\u4e4b\u5ea6\u5916\uff0c\u53ea\u8981\u80fd\u4e3a\u6b66\u6797\u9664\u5bb3\uff0c\u867d\u6b7b\u4f55\u61be\uff01\u201d \u3000\u3000\u795e\u79d8\u6559\u4e3b\u6709\u6043\u65e0\u6050\u9053\uff1a\u201c\u95ee\u9898\u662f\u4f60\u6740\u5f97\u4e86\u6211\u5417\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u5df2\u6697\u81ea\u8fd0\u8db3\u771f\u529b\uff0c\u76ee\u5149\u73af\u987e\u4e86\u5168\u573a\u4e00\u773c\uff0c\u7a81\u7136\u53cc\u8db3\u4e00\u8e6c\uff0c\u98de\u8eab\u76f4\u5411\u8f7f\u9876\u5c04\u53bb\u3002 \u3000\u3000\u53ea\u89c1\u4eba\u5f71\u7ffb\u98de\uff0c\u56db\u5927\u795e\u9b54\u5df2\u98de\u8eab\u62e6\u622a\uff0c\u5404\u81ea\u51cc\u7a7a\u53d1\u638c\u3002 \u3000\u3000\u56db\u80a1\u638c\u529b\u5408\u800c\u4e3a\u4e00\uff0c\u5f62\u6210\u4e00\u80a1\u72c2\u98d9\u6012\u98d3\uff0c\u5a01\u529b\u597d\u4e0d\u9a87\u4eba\uff01 \u3000\u3000\u6c5f\u67ab\u594b\u4e0d\u987e\u8eab\uff0c\u62fc\u7740\u5f3a\u5927\u638c\u529b\u7684\u8fce\u9762\u51fb\u6765\uff0c\u50cf\u6d41\u661f\u822c\u5c04\u5411\u5927\u8f7f\uff0c\u5200\u4eba\u5408\u4e00\uff0c\u5168\u529b\u5411\u8f7f\u9876\u4e00\u51fb\u3002 \u3000\u3000\u7fe0\u7389\u5200\u65e0\u575a\u4e0d\u6467\uff0c\u8f7f\u9876\u7eb5\u662f\u7cbe\u94a2\u6253\u9020\uff0c\u4e5f\u5fc5\u5c06\u8fce\u5203\u800c\u7834\uff0c\u85cf\u8eab\u5176\u4e2d\u7684\u795e\u79d8\u6559\u4e3b\u54ea\u80fd\u5e78\u514d\uff0c\u5fc5\u6b7b\u65e0\u7591\u3002 \u3000\u3000\u53ef\u60dc\u6c5f\u67ab\u6839\u672c\u6ca1\u6709\u673a\u4f1a\uff0c\u4e00\u6325\u7fe0\u7389\u5200\u7684\u950b\u8292\uff0c\u8ddd\u79bb\u5c1a\u5dee\u4e24\u5c3a\uff0c\u5c31\u89c1\u8f7f\u9876\u56db\u5468\u6392\u5217\u6574\u9f50\u7684\u5c0f\u6d1e\u4e2d\uff0c\u53d1\u5c04\u51fa\u65e0\u6570\u6bd2\u9488\uff0c\u4eff\u4f5b\u6ee1\u5929\u98de\u96e8\uff0c\u671d\u5411\u6c5f\u67ab\u8fce\u9762\u75be\u5c04\u800c\u6765\u3002 \u3000\u3000\u6c5f\u67ab\u5927\u60ca\uff0c\u6025\u5fd9\u51cc\u7a7a\u4ee5\u5de6\u638c\u62cd\u51fa\u4e00\u80a1\u201c\u65e0\u76f8\u7f61\u6c14\u201d\uff0c\u5c06\u8fce\u9762\u5c04\u6765\u7684\u4e00\u84ec\u6bd2\u9488\u9707\u843d\uff0c\u540c\u65f6\u4e00\u63d0\u771f\u6c14\uff0c\u8eab\u5f62\u62d4\u8d77\u4e24\u652f\u6709\u4f59\uff0c\u51cc\u7a7a\u4e00\u4e2a\u201c\u9e5e\u5b50\u7ffb\u8eab\u201d\u66b4\u9000\u51fa\u4e09\u4e08\u5f00\u5916\uff0c\u843d\u8db3\u5728\u4f17\u7537\u5973\u5e03\u6210\u7684\u5305\u56f4\u5708\u5916\u3002 \u3000\u3000\u201c\u54fc\uff01\u201d\u6c5f\u67ab\u4e0d\u5c51\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u4f60\u6240\u4ed7\u6043\u7684\uff0c\u53ea\u4e0d\u8fc7\u662f\u4e9b\u9b3c\u57df\u4f0e\u4fe9\uff01\u201d \u3000\u3000\u8f7f\u5185\u53d1\u51fa\u795e\u79d8\u6559\u4e3b\u7684\u72de\u7b11\uff0c\u53cd\u5507\u76f8\u8ba5\u9053\uff1a\u201c\u96be\u9053\u4f60\u7528\u7684\u8be1\u8ba1\uff0c\u5984\u56fe\u640f\u6740\u672c\u5ea7\uff0c\u53c8\u662f\u4ec0\u4e48\u5149\u660e\u6b63\u5927\u884c\u5f84\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u987f\u65f6\u54d1\u53e3\u65e0\u8a00\u3002 \u3000\u3000\u5bf9\u65b9\u8bf4\u7684\u6ca1\u9519\uff0c\u8fd9\u79cd\u640f\u6740\u8ba1\u5212\uff0c\u672c\u6765\u5c31\u4e0d\u767b\u5927\u96c5\uff0c\u4e95\u975e\u5404\u51ed\u771f\u624d\u5b9e\u5b66\u4e00\u51b3\u751f\u6b7b\u3002 \u3000\u3000\u795e\u79d8\u6559\u4e3b\u5f97\u7406\u4e0d\u9976\u4eba\uff0c\u54c8\u54c8\u4e00\u7b11\u9053\uff1a\u201c\u8fd9\u70b9\u96d5\u866b\u5c0f\u6280\uff0c\u6839\u672c\u4f24\u4e0d\u5230\u4f60\uff0c\u53ea\u4e0d\u8fc7\u662f\u8ddf\u4f60\u5f00\u5f00\u73a9\u7b11\u800c\u5df2\u3002 \u3000\u3000\u5426\u5219\uff0c\u4f60\u8981\u8fde\u8fd9\u4e2a\u90fd\u5e94\u4ed8\u4e0d\u4e86\uff0c\u7adf\u6562\u5192\u5145\u84dd\u51e4\uff0c\u8dd1\u6765\u6253\u7b97\u884c\u523a\u672c\u5ea7\uff0c\u90a3\u5c31\u672a\u514d\u592a\u4e0d\u81ea\u91cf\u529b\u5566\uff01\u201d \u3000\u3000\u6c5f\u67ab\u5fc3\u5ff5\u4e00\u52a8\uff0c\u7528\u6fc0\u5c06\u6cd5\u9053\uff1a\u201c\u4f60\u65e2\u5982\u6b64\u81ea\u8d1f\uff0c\u4f55\u4e0d\u73b0\u8eab\u51fa\u6765\uff0c\u8ddf\u6211\u4e00\u5bf9\u4e00\uff0c\u5404\u51ed\u6240\u5b66\u6765\u4e2a\u751f\u6b7b\u4e4b\u6597\uff01\u4f60\u6562\u5417\uff1f\u201d \u3000\u3000\u201c\u6740\u9e21\u7109\u7528\u725b\u5200\uff0c\u76f8\u5ea7\u6ca1\u6709\u4eb2\u81ea\u52a8\u624b\u7684\u5fc5\u8981\u3002\u201d\u795e\u79d8\u6559\u4e3b\u4e0d\u6709\u6240\u52a8\uff0c\u8bdd\u950b\u4e00\u8f6c\u9053\uff1a\u201c\u5f20\u56db\u59d1\uff0c\u65e0\u8bba\u4f60\u662f\u81ea\u544a\u594b\u52c7\uff0c\u6216\u662f\u4ed6\u4eec\u6d3e\u4f60\u6765\u640f\u6740\u6211\uff0c\u60f3\u6765\u4f60\u5df2\u62b1\u5b9a\u5fc5\u6b7b\u7684\u51b3\u5fc3\u3002 \u3000\u3000\u4ee5\u4f60\u4eab\u8a89\u6b66\u6797\u591a\u5e74\u7684\u58f0\u671b\uff0c\u7518\u4e3a\u6b7b\u58eb\uff0c\u5b9e\u5728\u662f\u5927\u6750\u5c0f\u7528\uff0c\u8fde\u6211\u90fd\u4e3a\u4f60\u53eb\u5c48\uff0c\u5b9e\u5728\u592a\u72af\u4e0d\u7740\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u51b7\u54fc\u9053\uff1a\u201c\u4f60\u4e0d\u5fc5\u6311\u62e8\u79bb\u95f4\uff01\u201d \u3000\u3000\u201c\u6211\u8bf4\u7684\u662f\u4e8b\u5b9e\uff01\u201d\u795e\u79d8\u6559\u4e3b\u4ece\u5bb9\u4e0d\u8feb\u9053\uff1a\u201c\u8b6c\u5982\u8bf4\u5427\uff0c\u6211\u4e00\u76f4\u4ee5\u5929\u540e\u5bab\uff0c\u5929\u738b\u95e8\u548c\u5929\u9a6c\u5802\u7684\u4eba\u8ddf\u4f60\u4eec\u5468\u65cb\uff0c\u4e0d\u5230\u6700\u540e\u5173\u5934\uff0c\u6211\u662f\u7edd\u4e0d\u4f1a\u8f7b\u6613\u51fa\u52a8\u4e3b\u529b\u7684\u3002 \u3000\u3000\u867d\u7136\uff0c\u8fd9\u4e09\u65b9\u9762\u7684\u4eba\u9a6c\uff0c\u5982\u4eca\u51e0\u4e4e\u5df2\u4f24\u4ea1\u6b86\u5c3d\uff0c\u4e8b\u5b9e\u4e0a\uff0c\u4f60\u4eec\u5374\u5e2e\u4e86\u6211\u4e2a\u5927\u5fd9\uff0c\u56e0\u4e3a\u4ed6\u4eec\u591c\u90ce\u81ea\u5927\uff0c\u6211\u65e9\u5df2\u51c6\u5907\u6d88\u706d\u8fd9\u4e09\u65b9\u9762\u7684\u529b\u91cf\u3002\u201d \u3000\u3000\u6c5f\u67ab\u51b7\u51b7\u5730\u95ee\uff1a\u201c\u4f60\u662f\u501f\u5200\u6740\u4eba\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\uff01\u201d\u795e\u79d8\u6559\u4e3b\u56de\u7b54\u7684\u5f88\u5e72\u8106\u3002 \u3000\u3000\u6c5f\u67ab\u76ee\u5149\u4e00\u626b\uff0c\u89c1\u5341\u4e8c\u540d\u7537\u5973\u4e00\u5b57\u6392\u5f00\uff0c\u5f62\u6210\u4e00\u5835\u4eba\u5899\u963b\u5728\u4ed6\u9762\u524d\uff0c\u53c8\u95ee\uff1a\u201c\u8fd9\u4e9b\u4eba\u5c31\u662f\u4f60\u6240\u8c13\u7684\u4e3b\u529b\uff1f\u201d \u3000\u3000\u795e\u79d8\u6559\u4e3b\u9053\uff1a\u201c\u4ed6\u4eec\u867d\u662f\u672c\u5ea7\u7684\u6b7b\u571f\uff0c\u4f46\u5e76\u975e\u6211\u7684\u771f\u6b63\u4e3b\u529b\u3002 \u3000\u3000\u521a\u624d\u6211\u5df2\u8bf4\u8fc7\uff0c\u4e0d\u5230\u6700\u540e\u5173\u5934\uff0c\u6211\u662f\u7edd\u4e0d\u4f1a\u8f7b\u6613\u51fa\u52a8\u4e3b\u529b\u7684\u3002\u201d \u3000\u3000\u201c\u4f60\u8ba4\u4e3a\u73b0\u5728\u8fd8\u4e0d\u662f\u6700\u540e\u5173\u5934\uff1f\u201d\u6c5f\u67ab\u4f3c\u5df2\u542c\u51fa\u5bf9\u65b9\u7684\u8bdd\u4e2d\u53e6\u6709\u7384\u673a\u3002 \u3000\u3000\u795e\u79d8\u6559\u4e3b\u90d1\u91cd\u9053\uff1a\u201c\u4e0d\u662f\uff01\u201d \u3000\u3000\u6c5f\u67ab\u201c\u54e6\uff1f\u201d\u4e86\u4e00\u58f0\uff0c\u8bd5\u63a2\u5730\u95ee\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u4f60\u6240\u8c13\u7684\u6700\u540e\u5173\u5934\u662f\u6307\u4f55\u65f6\uff1f\u201d \u3000\u3000\u201c\u738b\u89c1\u738b\u7684\u65f6\u523b\uff01\u201d\u795e\u79d8\u6559\u4e3b\u52a0\u91cd\u4e86\u8bed\u6c14\u3002 \u3000\u3000\u6c5f\u67ab\u7b11\u95ee\uff1a\u201c\u4f60\u6307\u7684\u662f\u6c5f\u67ab\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\uff01\u201d\u795e\u79d8\u6559\u4e3b\u9053\uff1a\u201c\u6c5f\u67ab\u89c6\u6211\u4e3a\u8fd1\u4e8c\u5341\u5e74\u6765\uff0c\u6697\u4e2d\u63a7\u5236\u6574\u4e2a\u6b66\u6797\uff0c\u5728\u6c5f\u6e56\u4e2d\u5174\u98ce\u4f5c\u6d6a\u7684\u7f6a\u9b41\u7978\u9996\u3002 \u3000\u3000\u6211\u5374\u89c6\u4ed6\u4e3a\u5f53\u5e74\u2018\u6b66\u6797\u4e09\u5723\u2019\u501f\u5c38\u8fd8\u9b42\uff0c\u7834\u574f\u6211\u6574\u4e2a\u5927\u5c40\u7684\u552f\u4e00\u963b\u529b\uff0c\u53ea\u8981\u9664\u6389\u4ed6\uff0c\u5176\u4ed6\u4eba\u6839\u672c\u5fae\u4e0d\u8db3\u9053\uff0c\u5305\u62ec\u4f60\u5f20\u56db\u59d1\u5728\u5185\uff01\u201d \u3000\u3000\u597d\u72c2\u5984\u7684\u53e3\u6c14\uff01 \u3000\u3000\u4f46\u8fd9\u4e5f\u8bf4\u660e\u4e86\u4ed6\u5fc3\u76ee\u4e2d\uff0c\u552f\u4e00\u7684\u987e\u5fcc\u5c31\u662f\u6c5f\u67ab\u3002 \u3000\u3000\u663e\u7136\uff0c\u8fd9\u4f4d\u795e\u79d8\u6559\u4e3b\u4f3c\u5df3\u77e5\u9053\uff0c\u6c5f\u67ab\u662f\u201c\u6b66\u6797\u4e09\u5723\u201d\u552f\u4e00\u7684\u4f20\u4eba\u3002 \u3000\u3000\u6362\u53e5\u8bdd\u8bf4\uff0c\u6c5f\u67ab\u5df2\u6df1\u5f97\u201c\u4e09\u5723\u201d\u771f\u4f20\uff0c\u800c\u4e14\uff0c\u662f\u96c6\u4e09\u4eba\u6240\u957f\u4e8e\u4e00\u8eab\uff0c\u6210\u4e86\u795e\u79d8\u6559\u4e3b\u771f\u6b63\u7684\u52b2\u654c\u3002 \u3000\u3000\u4e00\u65f6\u4e4b\u95f4\uff0c\u6c5f\u67ab\u771f\u4e0d\u77e5\u8be5\u5f15\u4ee5\u4e3a\u50b2\uff0c\u8fd8\u662f\u5fc3\u6709\u621a\u621a\u3002 \u3000\u3000\u201c\u4f60\u662f\u610f\u601d\u662f\u2026\u2026\u201d \u3000\u3000\u4e0d\u5f85\u6c5f\u67ab\u8bf4\u5b8c\uff0c\u795e\u79d8\u6559\u4e3b\u5df2\u63a5\u53e3\u9053\uff1a\u201c\u5f20\u56db\u59d1\uff0c\u4e0d\u5fc5\u628a\u8bdd\u626f\u8fdc\u4e86\uff0c\u6211\u7684\u8bdd\u5df2\u7ecf\u8bf4\u5f97\u62a5\u6e05\u695a\uff0c\u4f60\u662f\u806a\u660e\u4eba\uff0c\u4e0d\u76f8\u4fe1\u4f60\u4f1a\u542c\u4e0d\u61c2\u3002\u73b0\u5728\u7ed9\u4f60\u6700\u540e\u4e00\u4e2a\u673a\u4f1a\uff0c\u7531\u4f60\u81ea\u884c\u9009\u62e9\uff0c\u7a76\u7adf\u662f\u751f\u6216\u6b7b\u3002\u201d \u3000\u3000\u6c5f\u67ab\u521a\u624d\u672c\u60f3\u6062\u590d\u771f\u9762\u76ee\uff0c\u5f15\u51fa\u5bf9\u65b9\u73b0\u8eab\u51b3\u4e00\u6b7b\u6218\uff0c\u4f46\u542c\u4ed6\u8fd9\u4e00\u8bf4\uff0c\u4e34\u65f6\u6539\u53d8\u4e86\u4e3b\u610f\uff0c\u6545\u610f\u95ee\u9053\uff1a\u201c\u4f60\u80fd\u4e0d\u80fd\u628a\u8bdd\u8bf4\u7684\u66f4\u660e\u767d\u4e9b\uff1f\u201d \u3000\u3000\u201c\u597d\u5427\uff01\u201d\u795e\u79d8\u6559\u4e3b\u52c9\u4e3a\u5176\u96be\u9053\uff1a\u201c\u6211\u7684\u610f\u601d\u662f\u8bf4\uff0c\u5c3d\u7ba1\u4f60\u80c6\u8bc6\u8fc7\u4eba\uff0c\u52c7\u6c14\u53ef\u5609\uff0c\u4f46\u4ed6\u4eec\u4e0d\u8fc7\u89c6\u4f60\u4e3a\u6b7b\u58eb\u800c\u5df2\u3002\u5c31\u50cf\u6211\u6709\u7684\u6b7b\u571f\u4e00\u6837\uff0c\u4ec5\u51ed\u4e00\u80a1\u611a\u52c7\u611a\u5fe0\uff0c\u552f\u547d\u662f\u4ece\uff0c\u4e0d\u987e\u81ea\u5df1\u751f\u547d\u53ea\u6c42\u8fbe\u6210\u4efb\u52a1\u3002 \u3000\u3000\u4f46\u6211\u5bf9\u4f60\u7684\u770b\u6cd5\u4e0d\u540c\uff0c\u51ed\u4f60\u5f20\u56db\u59d1\u7684\u4e00\u5207\uff0c\u4e0d\u5e94\u5f53\u4f5c\u4e00\u4e2a\u6b7b\u58eb\u6765\u7528\uff0c\u90a3\u4f55\u5f02\u66b4\u73cd\u5929\u7269\u3002 \u3000\u3000\u5982\u679c\u4f60\u613f\u5f52\u987a\uff0c\u4f60\u4e0d\u4f46\u53ef\u4ee5\u53d1\u6325\u6240\u957f\uff0c\u5927\u5c55\u96c4\u624d\uff0c\u800c\u4e14\u6211\u4fdd\u8bc1\u4f60\u662f\u4f4d\u5c45\u4e00\u4eba\u4e4b\u4e0b\uff0c\u4e07\u4eba\u4e4b\u4e0a\uff01\u201d \u3000\u3000\u8fd9\u53e3\u6c14\u5c82\u4e0d\u50cf\u5e1d\u738b\uff1f \u3000\u3000\u6c5f\u67ab\u5fd6\u9053\uff1a\u201c\u539f\u6765\u4f60\u60f3\u6536\u4e70\u5f20\u56db\u59d1\u54e6\uff1f\u90a3\u53ef\u6253\u9519\u4e86\u4e3b\u610f\uff01\u201d \u3000\u3000\u53e3\u4e0a\u5374\u8bf4\uff1a\u201c\u5982\u679c\u6211\u4e0d\u63a5\u53d7\u5462\uff1f\u201d \u3000\u3000\u795e\u79d8\u6559\u4e3b\u51b7\u58f0\u9053\uff1a\u201c\u90a3\u5b9e\u5728\u5f88\u53ef\u60dc\u2026\u2026\u4e0d\u8fc7\uff0c\u6211\u4f1a\u6210\u5168\u4f60\u7684\u6b7b\u5fd7\uff01\u201d \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u6211\u53ef\u4ee5\u544a\u8bc9\u4f60\uff0c\u51ed\u773c\u524d\u8fd9\u6279\u6765\u81ea\u9b54\u6559\u7684\u5b7d\u969c\uff0c\u52a0\u4e0a\u6797\u5185\u5e03\u7f6e\u7684\u4e09\u5341\u540d\u9ed1\u8863\u5200\u624b\uff0c\u5168\u90e8\u52a8\u624b\u4e5f\u5948\u4f55\u4e0d\u4e86\u6211\uff0c\u53ea\u4e0d\u8fc7\u662f\u8ba9\u6211\u624b\u4e2d\u7fe0\u7389\u5200\u591a\u6cbe\u4e0a\u4e9b\u8840\u8165\u7f62\u4e86\u3002\u9664\u975e\u4f60\u4eb2\u81ea\u73b0\u8eab\u51fa\u624b\uff0c\u624d\u6709\u4e0e\u6211\u5168\u529b\u4e00\u640f\u7684\u673a\u4f1a\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u4e0d\u7528\u4f60\u64cd\u5fc3\u3002\u201d\u795e\u79d8\u6559\u4e3b\u72de\u7b11\u9053\uff1a\u201c\u6211\u77e5\u9053\uff0c\u4f60\u8ddf\u6c5f\u67ab\u6e0a\u6e90\u6781\u6df1\uff0c\u4e14\u6709\u7279\u6b8a\u4ea4\u60c5\uff0c\u6740\u4e86\u4f60\uff0c\u4e0d\u6015\u4ed6\u4e0d\u6765\uff0c\u5230\u65f6\u5019\u6211\u81ea\u4f1a\u8ddf\u4ed6\u4e00\u51b3\u751f\u6b7b\u7684\u3002\u201d \u3000\u3000\u6c5f\u67ab\u95fb\u8a00\u5fc3\u4e2d\u4e00\u6014\uff0c\u6697\u5fd6\u9053\uff1a\u201c\u96be\u9053\u4ed6\u5df2\u77e5\u9053\u6211\u4e0e\u5f20\u56db\u59d1\u7684\u4e0d\u5bfb\u5e38\u5173\u7cfb\uff1f\u201d \u3000\u3000\u795e\u79d8\u6559\u4e3b\u4f3c\u5df2\u4e0d\u8010\u70e6\u4e86\uff0c\u50ac\u95ee\u9053\uff1a\u201c\u5f20\u56db\u59d1\uff0c\u4f60\u62ff\u5b9a\u4e86\u4e3b\u610f\u6ca1\u6709\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u5fc3\u5ff5\u4e00\u8f6c\uff0c\u5ffd\u9053\uff1a\u201c\u5982\u679c\u6c5f\u67ab\u6b64\u523b\u51fa\u73b0\u5462\uff1f\u201d \u3000\u3000\u795e\u79d8\u6559\u4e3b\u6beb\u4e0d\u72b9\u8c6b\u9053\uff1a\u201c\u672c\u5ea7\u6b63\u597d\u9886\u6559\u4e09\u5723\u4f20\u4ed6\u7684\u7edd\u4e16\u6b66\u5b66\uff01\u201d \u3000\u3000\u201c\u597d\uff01\u201d \u3000\u3000\u6c5f\u67ab\u4e00\u58f0\u5927\u559d\uff0c\u8eab\u5f62\u66b4\u957f\uff0c\u5c06\u501f\u81ea\u84dd\u98ce\u7684\u90a3\u8eab\u52b2\u88c5\u6491\u5f97\u7834\u88c2\u5f00\u6765\uff0c\u8fde\u90a3\u4f2a\u88c5\u80f8\u4e73\u7684\u4e24\u56e2\u68c9\u82b1\u4e5f\u5f39\u51fa\u3002 \u3000\u3000\u63a5\u7740\u4e00\u4f38\u624b\uff0c\u63ed\u53bb\u8138\u4e0a\u4eba\u76ae\u9762\u5177\uff0c\u987f\u65f6\u6062\u590d\u4e86\u672c\u6765\u771f\u9762\u76ee\u3002 \u3000\u3000\u201c\u6c5f\u67ab\uff01\u201d\u8fd9\u662f\u53d1\u81ea\u8f7f\u5185\u7684\u60ca\u547c\u3002 \u3000\u3000\u663e\u7136\uff0c\u795e\u79d8\u6559\u4e3b\u505a\u68a6\u4e5f\u672a\u60f3\u5230\uff0c\u4e54\u626e\u84dd\u51e4\u7684\u5e76\u975e\u5f20\u56db\u59d1\uff0c\u800c\u662f\u4ed6\u5fc3\u76ee\u4e2d\u7684\u6b63\u70b9\u5b50\u2014\u2014\u6c5f\u67ab\uff01 \u3000\u3000\u8fd9\u4e5f\u96be\u602a\uff0c\u201c\u7f29\u9aa8\u672f\u201d\u867d\u80fd\u4f7f\u4f53\u578b\u7f29\u5c0f\uff0c\u4f46\u8981\u7ec3\u6210\u968f\u5fc3\u6240\u6b32\uff0c\u53d8\u6602\u85cf\u4e03\u5c3a\u4e4b\u8eaf\u4e3a\u5973\u513f\u8eab\uff0c\u81f3\u5c11\u5f97\u82b1\u4e0a\u4e00\u7532\u5b50\u7684\u82e6\u7ec3\uff0c\u624d\u80fd\u8fbe\u5230\u90a3\u79cd\u4e0d\u53ef\u601d\u8bae\u7684\u81f3\u9ad8\u5316\u5883\u3002 \u3000\u3000\u5bf9\u65b9\u5973\u5c06\u4e2d\uff0c\u867d\u7136\u662f\u6b66\u529f\u582a\u4e0e\u5f20\u56db\u59d1\u76f8\u63d0\u5e76\u8bba\u8005\uff0c\u4e2a\u4e2a\u90fd\u80fd\u4e54\u626e\u84dd\u51e4\uff0c\u6267\u884c\u6b64\u4e00\u640f\u6740\u8ba1\u5212\u3002 \u3000\u3000\u4f46\u82e5\u6ca6\u673a\u667a\uff0c\u80c6\u8bc6\uff0c\u5c24\u5176\u662f\u5de7\u593a\u5929\u5de5\u7684\u6613\u5bb9\u672f\uff0c\u90a3\u5c31\u975e\u5f20\u56db\u59d1\u83ab\u5c5e\uff0c\u7edd\u4e0d\u4f5c\u7b2c\u4e8c\u4eba\u60f3\u4e86\u3002 \u3000\u3000\u6240\u4ee5\uff0c\u795e\u79d8\u6559\u4e3b\u4e00\u5bdf\u89c9\u56de\u6765\u590d\u547d\u7684\u4e0d\u662f\u84dd\u98ce\uff0c\u5c31\u8ba4\u5b9a\u4e86\u662f\u5f20\u56db\u59d1\u6240\u4e54\u626e\u3002 \u3000\u3000\u4e07\u4e07\u6ca1\u6709\u6599\u5230\uff0c\u8fd9\u4e2a\u4eba\u7adf\u662f\u6c5f\u67ab\uff01 \u3000\u3000\u987f\u65f6\uff0c\u56db\u7537\u516b\u5973\u5982\u4e34\u5927\u654c\uff0c\u8fc5\u901f\u79fb\u5f62\u6362\u4f4d\uff0c\u6446\u51fa\u4e86\u9635\u5f0f\uff0c\u6321\u5728\u8f7f\u524d\u4e25\u9635\u4ee5\u5f85\u3002 \u3000\u3000\u6c5f\u67ab\u8863\u886b\u5c3d\u88c2\uff0c\u5168\u8eab\u4ec5\u7559\u4e00\u6761\u77ed\u5185\u88e4\u72b9\u5c5e\u5b8c\u6574\uff0c\u624b\u63e1\u7fe0\u7389\u5200\uff0c\u6602\u7136\u6311\u6218\u9053\uff1a\u201c\u73b0\u5728\u8be5\u4f60\u73b0\u8eab\u4e86\uff01\u201d \u3000\u3000\u795e\u79d8\u6559\u4e3b\u7a0d\u4e00\u8fdf\u7591\uff0c\u4fbf\u6717\u58f0\u4e0b\u4ee4\uff1a\u201c\u96f7\u97f3\u5bfa\u52a9\u9635\u7684\u56db\u5927\u795e\u9b54\uff0c\u516b\u9b54\u5973\u7acb\u5373\u9000\u51fa\u6797\u5916\uff0c\u6797\u5185\u7684\u4e09\u5341\u516d\u5200\u624b\u4e5f\u4e0d\u5f97\u4ecb\u5165\uff0c\u7531\u672c\u5ea7\u4e0e\u6c5f\u67ab\u4e00\u5bf9\u4e00\u51b3\u4e00\u6b7b\u6218\uff0c\u8fdd\u8005\u683c\u6740\u52ff\u8bba\uff01\u201d \u3000\u3000\u201c\u662f\uff01\u201d\u4e25\u9635\u4ee5\u5f85\u7684\u7537\u5973\u9f50\u58f0\u606d\u5e94\uff0c\u7acb\u65f6\u64a4\u9635\u9000\u51fa\u4e86\u6797\u5916\u3002 \u3000\u3000\u8fd9\u4e00\u7740\uff0c\u5012\u5927\u51fa\u6c5f\u67ab\u610f\u6599\u4e4b\u5916\uff0c\u60f3\u4e0d\u5230\u8fd9\u79cd\u795e\u79d8\u6559\u4e3b\u7adf\u9887\u6709\u82f1\u96c4\u6c14\u6982\uff0c\u4f3c\u4e0d\u613f\u4ed7\u4eba\u591a\u52bf\u4f17\u53d6\u80dc\uff0c\u4ee5\u514d\u843d\u4eba\u8bdd\u67c4\uff0c\u80dc\u4e4b\u4e0d\u6b66\u3002 \u3000\u3000\u96be\u9053\u795e\u79d8\u6559\u4e3b\u6709\u6043\u65e0\u6050\uff0c\u771f\u6709\u5fc5\u80dc\u7684\u628a\u63e1\uff1f \u3000\u3000\u6216\u8005\u662f\u53e6\u6709\u8be1\u8ba1\u2026\u2026 \u3000\u3000\u6c5f\u67ab\u6b63\u5728\u63e3\u6478\u5bf9\u65b9\u7684\u5c45\u5fc3\u7a81\u95fb\u4e00\u58f0\u7206\u54cd\uff0c\u8f7f\u9876\u5982\u540c\u4e00\u6735\u542b\u82de\u5f85\u653e\u7684\u82b1\u6735\u7a81\u7136\u7efd\u5f00\uff0c\u73b0\u51fa\u4e00\u4e2a\u8eab\u957f\u4e0d\u53ca\u56db\u5c3a\u7684\u9752\u8863\u8001\u8005\u3002 \u3000\u3000\u5982\u679c\u4e0d\u662f\u767d\u53d1\u82cd\u82cd\uff0c\u957f\u987b\u53ca\u80f8\uff0c\u770b\u4e0a\u53bb\u5c31\u50cf\u4e2a\u5341\u6765\u5c81\u7684\u513f\u7ae5\u3002 \u3000\u3000\u83ab\u975e\u59cb\u7ec8\u795e\u9f99\u89c1\u5c3e\u4e0d\u89c1\u9996\u7684\u795e\u79d8\u6559\u4e3b\uff0c\u7adf\u7136\u662f\u4e2a\u4f8f\u5112\uff1f \u3000\u3000\u6c5f\u67ab\u60ca\u9b42\u672a\u5b9a\uff0c\u5df2\u542c\u795e\u79d8\u6559\u4e3b\u6c89\u58f0\u9053\uff1a\u201c\u6c5f\u67ab\uff0c\u73b0\u5728\u6211\u4eec\u662f\u4e00\u5bf9\u4e00\u7684\u5c40\u9762\uff0c\u867d\u7136\u4f60\u4eec\u7684\u4eba\u53ef\u80fd\u53c8\u63a9\u8fd1\u6797\u5916\u63a5\u5e94\uff0c\u4f46\u4f60\u4e0d\u5fc5\u6307\u671b\u4ed6\u4eec\u652f\u63f4\u3002 \u3000\u3000\u6211\u5df2\u4e0b\u4ee4\u4f4f\u4f55\u4eba\u4e0d\u5f97\u4ecb\u5165\uff0c\u8fdd\u8005\u683c\u6740\u52ff\u8bba\uff0c\u540c\u6837\u4e5f\u5305\u62ec\u4f60\u4eec\u7684\u4eba\u5728\u5185\u3002 \u3000\u3000\u5982\u679c\u6709\u4efb\u4f55\u4eba\u5984\u56fe\u95ef\u5165\u6797\u6765\uff0c\u76f8\u4fe1\u56db\u5927\u795e\u9b54\uff0c\u516b\u4f4d\u9b54\u5973\uff0c\u4ee5\u53ca\u4e09\u5341\u516d\u5200\u624b\uff0c\u7edd\u5bf9\u6709\u80fd\u529b\u4e25\u683c\u6267\u884c\u6211\u7684\u547d\u4ee4\u3002 \u3000\u3000\u6240\u4ee5\uff0c\u4eca\u5728\u4e00\u6218\uff0c\u4e0d\u662f\u4f60\u6b7b\uff0c\u5c31\u662f\u6211\u4ea1\uff0c\u73b0\u5728\u4f60\u6211\u53ef\u4ee5\u653e\u624b\u4e00\u640f\u4e86\uff01\u201d \u3000\u3000\u4e24\u6b21\u640f\u6740\u672a\u6210\uff0c\u9669\u906d\u8f7f\u5185\u673a\u5173\u6697\u7b97\uff0c\u5df2\u4f7f\u6c5f\u67ab\u6709\u6240\u8b66\u60d5\uff0c\u54ea\u6562\u6389\u4ee5\u8f7b\u5fc3\u3002 \u3000\u3000\u5c24\u5176\u4ee4\u4ed6\u803d\u5fc3\u7684\uff0c\u662f\u5f20\u56db\u59d1\u7b49\u4eba\uff0c\u7edd\u4e0d\u4f1a\u542c\u4ed6\u7684\u529d\u963b\uff0c\u4efb\u7531\u4ed6\u72ec\u529b\u6267\u884c\u8fd9\u4e2a\u640f\u6740\u8ba1\u5212\uff0c\u5f88\u53ef\u80fd\u679c\u5982\u795e\u79d8\u6559\u4e3b\u6240\u6599\uff0c\u65e9\u5df2\u6084\u7136\u8ddf\u8e2a\u800c\u6765\uff0c\u63a9\u8fd1\u6797\u5916\u4f3a\u673a\u95ef\u5165\u63a5\u5e94\u3002 \u3000\u3000\u5982\u6b64\u4e00\u6765\uff0c\u52bf\u5fc5\u8ddf\u5bf9\u65b9\u7684\u4eba\u5c55\u5f00\u4e00\u573a\u6076\u6218\u3002 \u3000\u3000\u5c0f\u897f\u5929\u96f7\u97f3\u5bfa\u7684\u56db\u5927\u9b54\u795e\uff0c\u516b\u4f4d\u9b54\u5973\uff0c\u52a0\u4e0a\u4e09\u5341\u516d\u4f4d\u9ed1\u8863\u5200\u624b\uff0c\u5b9e\u529b\u6781\u4e3a\u53ef\u89c2\uff0c\u7edd\u4e0d\u53ef\u5c0f\u89c6\u3002 \u3000\u3000\u5c31\u51ed\u521a\u624d\u56db\u7537\u516b\u5973\u62e6\u622a\u6c5f\u67ab\u7684\u8eab\u624b\uff0c\u5373\u53ef\u7aa5\u51fa\u4e00\u822c\u3002 \u3000\u3000\u66f4\u4f55\u51b5\uff0c\u4ed6\u4eec\u4e2a\u4e2a\u5f62\u540c\u6b7b\u571f\uff0c\u53ea\u6c42\u6740\u654c\uff0c\u6839\u672c\u4e0d\u987e\u81ea\u5df1\u7684\u751f\u6b7b\u3002 \u3000\u3000\u76f8\u5f62\u4e4b\u4e0b\uff0c\u4ee5\u5f20\u56db\u59d1\u4e3a\u9996\u7684\u8fd9\u65b9\u9762\uff0c\u5982\u4eca\u52a0\u5165\u4e86\u590f\u5929\u540c\uff0c\u4e09\u5927\u697c\u4e3b\uff0c\u7533\u96ea\u541b\uff0c\u5201\u9e4f\u7b49\u751f\u529b\u519b\uff0c\u5b9e\u529b\u4e5f\u4e0d\u5f31\u3002\u5c24\u5176\uff0c\u6b27\u9633\u541f\u96ea\u3001\u541f\u971c\u4e24\u59d0\u59b9\u5df2\u4e0e\u6740\u624b\u65e0\u5f02\uff0c\u62fc\u6597\u8d77\u6765\u7684\u72fc\u52b2\u66f4\u4ee4\u4eba\u548b\u820c\u3002 \u3000\u3000\u4f46\u6740\u654c\u4e00\u5343\uff0c\u6211\u4f24\u516b\u767e\uff0c\u5374\u4e0d\u662f\u6c5f\u67ab\u6240\u613f\u89c1\u5230\u7684\u3002 \u3000\u3000\u6c5f\u67ab\u4e4b\u6240\u4ee5\u540c\u610f\u84dd\u51e4\u7684\u732e\u8ba1\uff0c\u5e76\u4e14\u81ea\u544a\u594b\u52c7\uff0c\u51b3\u5fc3\u72ec\u529b\u524d\u5f80\u5bc6\u6797\u640f\u6740\u90a3\u795e\u79d8\u6559\u4e3b\uff0c\u6b63\u662f\u57fa\u4e8e\u8fd9\u5c42\u987e\u8651\u3002 \u3000\u3000\u56e0\u6b64\uff0c\u4ed6\u5fc5\u987b\u901f\u6218\u901f\u51b3\uff0c\u4e00\u4e3e\u89e3\u51b3\u5bf9\u65b9\uff0c\u7edd\u4e0d\u80fd\u62d6\u6ce5\u5e26\u6c34\uff0c\u4f7f\u53cc\u65b9\u7684\u4eba\u5728\u6797\u5916\u5c55\u5f00\u6fc0\u6218\u3002 \u3000\u3000\u53ea\u8981\u89e3\u51b3\u4e86\u795e\u79d8\u6559\u4e3b\uff0c\u6811\u5012\u7322\u72f2\u6563\uff0c\u5176\u4ed6\u4eba\u4fbf\u4e0d\u8db3\u4e3a\u60a3\u4e86\u3002 \u3000\u3000\u6c5f\u67ab\u4e3b\u610f\u65e2\u5b9a\uff0c\u4e0d\u7531\u5730\u51b7\u7b11\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u4f60\u8fd8\u7ad9\u5728\u8f7f\u9876\u4e0a\u5e72\u561b\uff1f\u201d \u3000\u3000\u795e\u79d8\u6559\u4e3b\u9053\uff1a\u201c\u6211\u5728\u7b49\u4f60\u8fdb\u653b\u5440\uff01\u54c8\u54c8\u201d \u3000\u3000\u6c5f\u67ab\u4e0d\u5c51\u5730\u51b7\u51b7\u4e00\u54fc\uff0c\u8ba5\u9053\uff1a\u201c\u4f60\u662f\u60f3\u4ed7\u6043\u8f7f\u5185\u8bbe\u7684\u673a\u5173\u6d88\u606f\u62a4\u8eab\uff1f\u201d \u3000\u3000\u795e\u79d8\u6559\u4e3b\u6beb\u4e0d\u9690\u8bb3\u9053\uff1b \u3000\u3000\u201c\u4e0d\u9519\uff01\u4f60\u6015\u4e86\u5417\uff1f\u201d \u3000\u3000\u6b64\u4eba\u679c\u7136\u8001\u5978\u5de8\u733e\uff0c\u975e\u4f46\u4e0d\u4e3a\u6240\u6fc0\uff0c\u53cd\u800c\u5c06\u4e86\u6c5f\u67ab\u4e00\u519b\u3002 \u3000\u3000\u6c5f\u67ab\u6709\u4e9b\u8fdf\u7591\u4e86\u3002 \u3000\u3000\u4ed6\u4e0d\u662f\u6015\uff0c\u800c\u662f\u5fc3\u4e2d\u8d77\u4e86\u7591\u5ff5\uff0c\u6000\u7591\u7ad9\u5728\u8f7f\u9876\u4e0a\u7684\u201c\u4f8f\u5112\u201d\uff0c\u7a76\u7adf\u662f\u4e0d\u662f\u795e\u79d8\u6559\u4e3b\u672c\u4eba\u3002 \u3000\u3000\u6839\u636e\u84dd\u51e4\u7684\u63cf\u8ff0\uff0c\u795e\u79d8\u6559\u4e3b\u7684\u4f53\u578b\u5c5e\u4e8e\u7626\u957f\u578b\uff0c\u800c\u773c\u524d\u7684\u8fd9\u4eba\u5374\u8eab\u9ad8\u4e0d\u53ca\u56db\u5c3a\u3002 \u3000\u3000\u5373\u4f7f\u795e\u79d8\u6559\u4e3b\u4e5f\u7ec3\u6210\u4e86\u7f29\u9aa8\u672f\uff0c\u4f46\u4ed6\u6b64\u523b\u6beb\u65e0\u5c55\u9732\u7684\u5fc5\u8981\u3002 \u3000\u3000\u9664\u975e\u662f\u4ed6\u4e0d\u7518\u793a\u5f31\uff0c\u4e5f\u9732\u51fa\u4e00\u624b\u7ed9\u6c5f\u67ab\u77a7\uff0c\u6216\u8005\u522b\u6709\u5c45\u5fc3\u3002 \u3000\u3000\u6240\u4ee5\uff0c\u6c5f\u67ab\u867d\u6709\u4e00\u4e3e\u6b7c\u654c\u7684\u6740\u624b\u950f\u5374\u4e0d\u6562\u8f7b\u4e3e\u5984\u52a8\uff0c\u4ee5\u9632\u5bf9\u65b9\u6709\u8bc8\u3002\u4e07\u4e00\u8fd9\u4e2a\u4eba\u4e0d\u662f\u795e\u79d8\u6559\u4e3b\uff0c\u6740\u4e86\u4ed6\u4e5f\u6ca1\u7528\uff0c\u53cd\u800c\u6253\u8349\u60ca\u86c7\uff0c\u4f7f\u771f\u7684\u6559\u4e3b\u6709\u6240\u8b66\u60d5\uff0c\u518d\u51fa\u624b\u65f6\u5a01\u529b\u867d\u4e0d\u51cf\uff0c\u6548\u679c\u53ef\u80fd\u5c31\u8981\u5927\u6253\u6298\u6263\u4e86\u3002 \u3000\u3000\u8bf4\u4e0d\u5b9a\uff0c\u4e0a\u524d\u8fd9\u4e2a\u4f8f\u5112\u6839\u672c\u4e0d\u662f\u795e\u79d8\u6559\u4e3b\u672c\u4eba\uff0c\u53ea\u4e0d\u8fc7\u7528\u4ed6\u4e3a\u9975\uff0c\u8bf1\u4f7f\u6c5f\u67ab\u4f7f\u51fa\u6740\u624b\u950f\uff0c\u4ee5\u4fbf\u51b7\u773c\u65c1\u89c2\uff0c\u601d\u51fa\u7834\u89e3\u4e4b\u9053\u3002 \u3000\u3000\u53cc\u65b9\u90fd\u662f\u5f53\u4eca\u6b66\u6797\u7edd\u4e16\u9ad8\u624b\uff0c\u4e00\u4ea4\u624b\u5373\u53ef\u7acb\u5224\u751f\u6b7b\uff0c\u5148\u673a\u5e76\u4e0d\u91cd\u8981\uff0c\u6210\u8d25\u5b8c\u5168\u51b3\u4e8e\u81f4\u547d\u7684\u4e00\u51fb\u3002 \u3000\u3000\u5ff5\u53ca\u4e8e\u6b64\uff0c\u6c5f\u67ab\u4e0d\u5f97\u4e0d\u8c28\u614e\u4e86\u3002 \u3000\u3000\u6c5f\u67ab\u6de1\u7136\u4e00\u7b11\u9053\uff1a\u201c\u9b3c\u57df\u4f0e\u4fe9\uff0c\u4f55\u8db3\u4e3a\u4ff1\uff01\u201d\u4fbf\u6602\u7136\u4e3e\u6b65\u5411\u5927\u6865\u8d70\u53bb\u3002 \u3000\u3000\u73b0\u5728\u6ca1\u6709\u4efb\u4f55\u4eba\u62e6\u622a\uff0c\u6c5f\u67ab\u8868\u9762\u4e0a\u6beb\u4e0d\u5728\u4e4e\uff0c\u5176\u5b9e\u66f4\u5c0f\u5fc3\u7ffc\u7ffc\uff0c\u6697\u81ea\u5168\u795e\u6212\u5907\u3002 \u3000\u3000\u5f53\u5730\u63a5\u8fd1\u5927\u8f7f\u524d\u65f6\uff0c\u7a81\u95fb\u201c\u98d5\u98d5\u201d\u4e24\u58f0\uff0c\u8f7f\u6746\u524d\u7aef\u7684\u94c1\u77db\uff0c\u76f4\u5411\u4ed6\u75be\u5c04\u800c\u6765\u3002 \u3000\u3000\u6c5f\u67ab\u8eab\u5f62\u4e00\u62d4\u800c\u8d77\uff0c\u907f\u5f00\u4e24\u652f\u94c1\u4e88\u7684\u540c\u65f6\uff0c\u4eba\u5df2\u98de\u6251\u5411\u8f7f\u9876\u4e0a\u7684\u795e\u79d8\u6559\u4e3b\u3002 \u3000\u3000\u201c\u535f\u55b3\u201d\u4e00\u58f0\uff0c\u795e\u79d8\u6559\u4e3b\u7684\u8eab\u5b50\u5411\u4e0b\u964d\u843d\u4eba\u8f7f\u5185\uff0c\u7efd\u5f00\u7684\u56db\u7247\u8f7f\u9876\u4e5f\u540c\u65f6\u5408\u62e2\uff0c\u6062\u590d\u4e86\u539f\u72b6\u3002 \u3000\u3000\u6c5f\u673a\u8eab\u5728\u51cc\u7a7a\uff0c\u8fd9\u79cd\u60c5\u52bf\u4e4b\u4e0b\uff0c\u5fc5\u7136\u6536\u52bf\u4e0d\u53ca\uff0c\u52bf\u5fc5\u987a\u52bf\u8fde\u5200\u5e26\u4eba\u653b\u5411\u8f7f\u9876\u3002 \u3000\u3000\u4f46\u4e60\u81ea\u201c\u4f20\u771f\u8bb0\u201d\u4e0a\u7684\u6b66\u529f\uff0c\u51e0\u4e4e\u5168\u662f\u4e0d\u53ef\u601d\u8bae\u7684\u5947\u827a\u7279\u6280\uff0c\u5b8c\u5168\u4e0d\u5408\u5e38\u7406\uff0c\u751a\u81f3\u8d85\u8d8a\u4e86\u4eba\u7c7b\u4f53\u80fd\u7684\u6781\u9650\u3002 \u3000\u3000\u4e3e\u4f8b\u6765\u8bf4\uff0c\u53cc\u65b9\u4ea4\u624b\u65f6\uff0c\u4e00\u65b9\u88ab\u903c\u5165\u6b7b\u89d2\uff0c\u5206\u660e\u7edd\u65e0\u8fd8\u51fb\u7684\u53ef\u80fd\uff0c\u5374\u80fd\u5728\u4ee4\u4eba\u610f\u60f3\u4e0d\u5230\u7684\u89d2\u5ea6\u51fa\u624b\uff0c\u653b\u5bf9\u65b9\u4e2a\u63aa\u624b\u4e0d\u53ca\uff0c\u53cd\u8d25\u4e3a\u80dc\u3002 \u3000\u3000\u6c5f\u67ab\u4e00\u89c1\u795e\u79d8\u6559\u4e3b\u964d\u5165\u8f7f\u5185\uff0c\u5df2\u7136\u60c5\u77e5\u6709\u5f02\uff0c\u5de6\u638c\u75be\u51fa\uff0c\u53d1\u51fa\u4e00\u80a1\u65e0\u5f62\u638c\u529b\uff0c\u786c\u751f\u751f\u6536\u4f4f\u6251\u52bf\uff0c\u501f\u90a3\u53cd\u9707\u4e4b\u529b\uff0c\u8eab\u5f62\u51b2\u5929\u800c\u8d77\u3002 \u3000\u3000\u51e0\u4e4e\u662f\u540c\u65f6\uff0c\u53ea\u542c\u8f70\u7136\u4e00\u58f0\u5de8\u54cd\uff0c\u6574\u4e2a\u5927\u8f7f\u53d1\u751f\u4e86\u7206\u70b8\uff0c\u58f0\u52bf\u597d\u4e0d\u9a87\u4eba\uff01 \u3000\u3000\u60ca\u5929\u52a8\u5730\u7684\u7206\u70b8\u58f0\u4e2d\uff0c\u5239\u65f6\u706b\u5149\u51b2\u5929\uff0c\u65b9\u5706\u5341\u4e08\u4e4b\u5185\u7684\u6811\u6728\u5168\u5012\uff0c\u4e0d\u662f\u6298\u65ad\uff0c\u5c31\u662f\u8fde\u6839\u62d4\u8d77\u3002 \u3000\u3000\u7206\u70b8\u7684\u5a01\u529b\u786e\u5b9e\u60ca\u4eba\uff01 \u3000\u3000\u5bf9\u65b9\u4e0d\u60dc\u4ee5\u8eab\u4e3a\u9975\uff0c\u8bf1\u4f7f\u6c5f\u67ab\u653b\u5411\u5927\u8f7f\uff0c\u4f01\u56fe\u6765\u4e2a\u540c\u5f52\u4e8e\u5c3d\u3002 \u3000\u3000\u663e\u7136\u662f\u5fd7\u5728\u5fc5\u5f97\uff0c\u51b3\u5fc3\u7f6e\u6c5f\u67ab\u4e8e\u6b7b\u5730\u3002 \u3000\u3000\u8fd9\u4e2a\u8ba1\u8c0b\u4e0d\u4f46\u591f\u72e0\uff0c\u591f\u6bd2\uff0c\u4e5f\u76f8\u5f53\u9ad8\u660e\u3002 \u3000\u3000\u7531\u6b64\u53ef\u89c1\uff0c\u795e\u79d8\u6559\u4e3b\u679c\u7136\u5de5\u4e8e\u5fc3\u8ba1\uff0c\u8be1\u8ba1\u591a\u7aef\uff0c\u4ed6\u547d\u84dd\u51e4\u524d\u5f80\u4f20\u8bdd\uff0c\u5176\u5b9e\u53ea\u662f\u4e2a\u5e4c\u5b50\uff0c\u4f3c\u6709\u5148\u89c1\u4e4b\u660e\uff0c\u6599\u77e5\u6c5f\u67ab\u5fc5\u4f1a\u4e0d\u60dc\u5192\u9669\uff0c\u8ddf\u8e2a\u590d\u547d\u7684\u84dd\u51e4\u56de\u6765\uff0c\u4e0e\u4ed6\u51b3\u4e00\u751f\u6b7b\u3002 \u3000\u3000\u4e0d\u6599\u4e54\u626e\u84dd\u51e4\u7684\u5374\u662f\u4e2a\u5973\u5b50\u3002 \u3000\u3000\u795e\u79d8\u6559\u4e3b\u81ea\u4f5c\u806a\u660e\uff0c\u5224\u65ad\u8fd9\u5973\u5b50\u5fc5\u662f\u5f20\u56db\u59d1\uff0c\u624d\u6ca1\u6709\u63a3\u52a8\u673a\u5173\uff0c\u5f15\u7206\u88c5\u7f6e\u8f7f\u5185\u7684\u70b8\u836f\u3002 \u3000\u3000\u5f53\u7136\uff0c\u8fd9\u5ea7\u5927\u8f7f\u662f\u4e13\u4e3a\u6c5f\u67ab\u800c\u8bbe\uff0c\u6b63\u70b9\u5b50\u672a\u5230\uff0c\u6740\u5176\u4ed6\u4efb\u4f55\u4eba\u90fd\u4e0d\u5fc5\u5c0f\u9898\u5927\u505a\uff0c\u66f4\u4f55\u51b5\u4ed6\u5e76\u4e0d\u60f3\u6740\u5f20\u56db\u59d1\u3002 \u3000\u3000\u66f4\u91cd\u8981\u7684\u662f\uff0c\u8fd9\u4e5f\u662f\u4ed6\u51fa\u5947\u5236\u80dc\u7684\u6740\u624b\u94dc\uff0c\u53ea\u80fd\u7528\u4e00\u6b21\u3002\u9664\u4e86\u5bf9\u4ed8\u6c5f\u67ab\uff0c\u5b9e\u65e0\u5fc5\u8981\u518d\u7528\u3002 \u3000\u3000\u60f3\u4e0d\u5230\u4e54\u626e\u84dd\u51e4\u7684\u5973\u5b50\uff0c\u7adf\u7136\u662f\u7ec3\u5c31\u7f29\u9aa8\u672f\u7684\u6c5f\u67ab\u3002 \u3000\u3000\u795e\u79d8\u6559\u4e3b\u867d\u611f\u610f\u5916\u548c\u60ca\u5f02\uff0c\u5374\u5f88\u6c89\u5f97\u4f4f\u6c14\uff0c\u6545\u4f5c\u82f1\u96c4\u6c14\u6168\uff0c\u547d\u56db\u7537\u516b\u5927\u9000\u51fa\u6797\u5916\u540e\uff0c\u8bf1\u4f7f\u6c5f\u67ab\u98de\u8eab\u653b\u8fd1\u5927\u8f7f\uff0c\u59cb\u5f15\u53d1\u7206\u70b8\uff0c\u4ee5\u514d\u90a3\u5341\u4e8c\u540d\u7537\u5973\u906d\u5230\u6c60\u9c7c\u4e4b\u6b83\u3002 \u3000\u3000\u4f46\u662f\uff0c\u4e3a\u4e86\u70b8\u6b7b\u6c5f\u67ab\uff0c\u795e\u79d8\u6559\u4e3b\u7adf\u4e0d\u501f\u4ee5\u8eab\u76f8\u6b89\uff0c\u8fd9\u4e2a\u4ee3\u4ef7\u6765\u514d\u592a\u5927\u4e86\u5427\uff1f \u3000\u3000\u8fd9\u662f\u4e0d\u592a\u53ef\u80fd\u7684\u3002 \u3000\u3000\u663e\u800c\u6613\u89c1\uff0c\u8fd9\u682a\u5112\u53ea\u662f\u4e2a\u6b7b\u571f\uff0c\u7edd\u4e0d\u53ef\u80fd\u662f\u90a3\u795e\u79d8\u6559\u4e3b\u672c\u4eba\uff01 \u3000\u3000\u6c5f\u67ab\u53ef\u771f\u547d\u5927\uff0c\u5728\u5f3a\u70c8\u7206\u70b8\u4e4b\u65f6\uff0c\u4ed7\u4e09\u5723\u6240\u4f20\u795e\u529f\u62a4\u4f53\uff0c\u88ab\u9707\u98de\u51fa\u6797\u5916\uff0c\u4ec5\u53ea\u80cc\u90e8\u53d7\u4e86\u4e9b\u7684\u4f24\uff0c\u7adf\u7136\u9003\u8fc7\u4e00\u52ab\u3002 \u3000\u3000\u5f53\u4ed6\u6454\u8dcc\u5730\u4e0a\u65f6\uff0c\u63a9\u8fd1\u6797\u5916\u4f3a\u673a\u800c\u52a8\u7684\u5f20\u56db\u59d1\u7b49\u4eba\uff0c\u8fd9\u65f6\u7eb7\u7eb7\u73b0\u8eab\uff0c\u4e00\u62e5\u800c\u4e0a\u3002 \u3000\u3000\u9996\u5148\u8d76\u5230\u7684\u662f\u5927\u548c\u5c1a\uff0c\u4e00\u89c1\u5168\u8eab\u5f62\u540c\u8d64\u88f8\u7684\u6c5f\u67ab\uff0c\u4e0d\u7531\u5730\u5927\u5403\u4e00\u60ca\uff0c\u5fd9\u4e0d\u8fed\u4e0a\u524d\u8e72\u4e0b\uff0c\u6276\u8d77\u4ed6\u6025\u95ee\uff1a\u201c\u6c5f\u8001\u5f1f\uff0c\u4f60\u8981\u7d27\u5417\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u60ca\u9b42\u752b\u5b9a\uff0c\u5f3a\u81ea\u4e00\u7b11\u9053\uff1a\u201c\u8fd8\u4e0d\u81f4\u4e8e\u8981\u5927\u548c\u5c1a\u4e3a\u6211\u8d85\u5ea6\u54e6\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u4e5f\u8d76\u5230\u4e86\u8fd1\u524d\uff0c\u60ca\u95ee\uff1a\u201c\u600e\u4e48\u5566\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u82e6\u7b11\u9053\uff1a\u201c\u90a3\u795e\u79d8\u6559\u4e3b\u786e\u5b9e\u9ad8\u660e\uff0c\u6211\u4eec\u53cd\u800c\u4e2d\u4e86\u4ed6\u7684\u8be1\u8ba1\uff01\u201d \u3000\u3000\u201c\u54e6\uff1f\u201d\u5f20\u56db\u59d1\u95fb\u8a00\u4e00\u6014\uff0c\u8feb\u4e0d\u53ca\u5f85\u5730\u8ffd\u95ee\uff1a\u201c\u4f60\u89c1\u5230\u4ed6\u4e86\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u89c1\u5230\u4e86\uff0c\u4f46\u4e0d\u662f\u4ed6\u672c\u4eba\uff01\u201d \u3000\u3000\u5927\u548c\u5c1a\u60ca\u8be7\u9053\uff1a\u201c\u4e5f\u662f\u66ff\u8eab\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u5fae\u5fae\u53bb\u4e86\u4e0b\u5934\uff0c\u6025\u95ee\uff1a\u201c\u4f60\u4eec\u6ca1\u6709\u89c1\u5230\u9000\u51fa\u6797\u5916\u7684\u90a3\u6279\u4eba\uff1f\u201d \u3000\u3000\u5f20\u56db\u59d1\u4e00\u8138\u56f0\u60d1\u9053\uff1a\u201c\u6211\u4eec\u63a9\u8fd1\u8fd9\u91cc\u65f6\uff0c\u4e0d\u77e5\u6797\u5185\u7684\u60c5\u51b5\uff0c\u4e0d\u6562\u8d38\u7136\u8f7b\u4e3e\u5984\u52a8\uff0c\u53ea\u597d\u5404\u81ea\u9690\u85cf\u8eab\u5f62\uff0c\u6309\u5175\u4e0d\u529d\uff0c\u6253\u7b97\u5fc5\u8981\u65f6\u51b2\u8fdb\u6797\u5185\u63a5\u5e94\u3002 \u3000\u3000\u53ef\u662f\uff0c\u521a\u624d\u5ffd\u89c1\u5341\u51e0\u540d\u7537\u5973\u5306\u5306\u9000\u51fa\uff0c\u4e0d\u77e5\u4e3a\u4ec0\u4e48\uff0c\u8fde\u6563\u5e03\u56db\u5468\u7684\u4e8c\u3001\u4e09\u5341\u540d\u9ed1\u8863\u5200\u624b\uff0c\u4e5f\u8ddf\u4ed6\u4eec\u4ed3\u7687\u64a4\u8d70\u4e86\u3002 \u3000\u3000\u6211\u4eec\u867d\u89c9\u60c5\u51b5\u6709\u5f02\uff0c\u4f46\u4ecd\u4e0d\u4fbf\u91c7\u53d6\u884c\u52a8\uff0c\u8fc7\u4e86\u4e00\u4f1a\u513f\uff0c\u5c31\u542c\u5f97\u4e00\u58f0\u60ca\u5929\u52a8\u5730\u7684\u7206\u70b8\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u8f7b\u53f9\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8fd9\u6b21\u6211\u4eec\u53c8\u5931\u7b97\u4e86\uff0c\u6211\u80fd\u5927\u96be\u4e0d\u6b7b\uff0c\u771f\u7b97\u662f\u547d\u5927\uff01\u201d \u3000\u3000\u6b27\u9633\u662d\u4e00\u8d76\u6765\u5c31\u95ee\uff1a\u201c\u7a76\u7adf\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u53c8\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u56de\u53bb\u518d\u8bf4\u5427\u3002\u201d \u3000\u3000\u56de\u5230\u53e4\u5e99\uff0c\u5f53\u6c5f\u67ab\u8bf4\u51fa\u4e00\u5207\u540e\uff0c\u542c\u5f97\u4f17\u4eba\u65e0\u4e0d\u77a0\u76ee\u7ed3\u820c\u3002 \u3000\u3000\u5c24\u5176\u662f\u84dd\u51e4\uff0c\u5fcd\u4e0d\u4f4f\u6cea\u5149\u95ea\u52a8\u9053\uff1a\u201c\u6211\uff0c\u6211\u771f\u7684\u4e0d\u77e5\u60c5\uff0c\u60f3\u4e0d\u5230\u6559\u4e3b\u662f\u5229\u7528\u6211\u2026\u2026\u201d \u3000\u3000\u5f20\u56db\u59d1\u5728\u65c1\u8f7b\u62cd\u5979\u4e24\u4e0b\u80a9\u8180\uff0c\u5b89\u629a\u9053\uff1a\u201c\u8fd9\u4e0d\u662f\u4f60\u7684\u9519\uff0c\u53ea\u602a\u6211\u4eec\u4f4e\u4f30\u4e86\u90a3\u4f4d\u8be1\u8ba1\u591a\u7aef\u7684\u795e\u79d8\u6559\u4e3b\u3002\u201d \u3000\u3000\u84dd\u51e4\u81ea\u8d23\u9053\uff1a\u201c\u53ef\u662f\uff0c\u8fd9\u4e2a\u523a\u6740\u8ba1\u5212\u662f\u6211\u51fa\u7684\u54e6\u3002\u201d \u3000\u3000\u6c5f\u67ab\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u7684\u4e3b\u610f\u786e\u5b9e\u4e0d\u9519\uff0c\u5426\u5219\u6211\u4e5f\u4e0d\u4f1a\u540c\u610f\u3002\u5c31\u50cf\u5f20\u56db\u59d1\u521a\u624d\u6240\u8bf4\uff0c\u95ee\u9898\u51fa\u5728\u6211\u4eec\u4f4e\u4f30\u4e86\u5bf9\u624b\uff0c\u90a3\u4f4d\u795e\u79d8\u6559\u4e3b\u786e\u5b9e\u5389\u5bb3\uff0c\u5982\u540c\u4e0b\u68cb\uff0c\u6211\u4eec\u8d70\u4e00\u6b65\u770b\u4e00\u6b65\uff0c\u4ed6\u5374\u80fd\u770b\u51fa\u4e09\u6b65\u3002\u201d \u3000\u3000\u8001\u53eb\u5316\u4e0d\u7981\u5fe7\u5f62\u4e8e\u8272\u9053\uff1a\u201c\u90a3\u6211\u4eec\u4e0b\u4e00\u6b65\u68cb\uff0c\u53c8\u8be5\u600e\u4e48\u8d70\u5462\uff1f\u201d \u3000\u3000\u5f20\u56db\u59d1\u9053\uff1a\u201c\u4eca\u591c\u867d\u7136\u4ed6\u4eec\u53ea\u727a\u7272\u4e00\u4e2a\u66ff\u6b7b\u9b3c\uff0c\u53cc\u65b9\u90fd\u6ca1\u6709\u9020\u6210\u4f24\u4ea1\u3002\u73b0\u5728\u7684\u5f62\u52bf\uff0c\u662f\u654c\u6697\u6211\u660e\u3002\u9664\u975e\u795e\u79d8\u6559\u4e3b\u5fc3\u6709\u4e0d\u7518\uff0c\u4e3b\u52a8\u627e\u4e0a\u6211\u4eec\uff0c\u6211\u4eec\u8981\u627e\u4ed6\u5c31\u66f4\u56f0\u96be\u4e86\u3002\u201d \u3000\u3000\u4e0d\u6127\u662f\u5973\u4e2d\u8c6a\u6770\uff0c\u9887\u5177\u5927\u5c06\u4e4b\u98ce\uff0c\u867d\u5728\u52a3\u52bf\u4e0b\uff0c\u4ecd\u7136\u4fdd\u6301\u6c89\u7740\u51b7\u9759\u3002 \u3000\u3000\u5f20\u56db\u59d1\u987f\u4e86\u4e00\u4e0b\uff0c\u63a5\u7740\u9053\uff1a\u201c\u5982\u4eca\u4e4b\u8ba1\uff0c\u6211\u4eec\u552f\u6709\u6309\u5175\u4e0d\u52a8\uff0c\u9759\u5f85\u5bf9\u65b9\u627e\u4e0a\u95e8\u6765\uff0c\u4e0d\u8fc7\uff0c\u53ef\u4ee5\u60f3\u8c61\u5230\u5982\u5bf9\u65b9\u6ca1\u6709\u7a33\u64cd\u80dc\u7b97\u7684\u628a\u63e1\uff0c\u7edd\u4e0d\u6562\u518d\u8f7b\u4e3e\u5984\u52a8\u3002 \u3000\u3000\u56e0\u6b64\uff0c\u8d81\u7740\u53cc\u65b9\u6682\u65f6\u4f11\u5175\u7684\u7a7a\u6321\uff0c\u6211\u4eec\u5fc5\u987b\u52a0\u7d27\u7ec3\u6210\u4e00\u4e24\u6837\u80fd\u901f\u6210\u7684\u72ec\u7279\u6b66\u529f\u3002\u56e0\u4e3a\u4e0b\u4e00\u6b21\u7684\u906d\u9047\uff0c\u5f88\u53ef\u80fd\u5c31\u662f\u53cc\u65b9\u751f\u6b7b\u5b58\u4ea1\u7684\u6700\u540e\u51b3\u6218\u3002\u201d \u3000\u3000\u201c\u4e0d\u9519\uff01\u201d\u5927\u548c\u5c1a\u63a5\u9053\uff1a\u201c\u5f20\u56db\u59d1\u6240\u8a00\u6781\u662f\uff0c\u6211\u4eec\u4e0d\u4f46\u8981\u52a0\u7d27\u7ec3\u529f\uff0c\u66f4\u8981\u52a0\u5f3a\u6212\u5907\uff0c\u4ee5\u9632\u5bf9\u65b9\u968f\u65f6\u7a81\u88ad\uff0c\u751a\u81f3\u503e\u5de2\u5927\u4e3e\u6765\u72af\u3002\u201d \u3000\u3000\u4f17\u4eba\u7eb7\u7eb7\u70b9\u5934\u79f0\u662f\u3002 \u3000\u3000\u8fd9\u65f6\u7f57\u5170\u5df2\u4e3a\u6c5f\u67ab\u80cc\u4e0a\u7684\u4f24\u5904\u6577\u4e0a\u4e86\u836f\uff0c\u9053\uff1a\u201c\u4eca\u591c\u4f60\u5f88\u8f9b\u82e6\uff0c\u5148\u53bb\u7985\u623f\u6b47\u6b47\uff0c\u5176\u4ed6\u7684\u4e8b\u7531\u6211\u4eec\u6765\u5546\u8bae\u3002\u201d \u3000\u3000\u6c5f\u67ab\u6beb\u4e0d\u5728\u4e4e\u9053\uff1a\u201c\u8fd9\u70b9\u7684\u4f24\u7b97\u4e0d\u4e86\u4ec0\u4e48\uff0c\u6211\u8fd8\u633a\u5f97\u4f4f\u3002\u201d \u3000\u3000\u201c\u4e0d\u8981\u901e\u5f3a\u4e86\u3002\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u4f60\u662f\u6211\u4eec\u7684\u652f\u67f1\uff0c\u66f4\u662f\u6b66\u6797\u91cd\u89c1\u5929\u65e5\u7684\u5e0c\u671b\uff0c\u53ef\u4e0d\u80fd\u5012\u4e0b\u54e6\uff01\u201d \u3000\u3000\u6c5f\u67ab\u5bf9\u5f20\u56db\u59d1\u8a00\u542c\u8ba1\u4ece\uff0c\u542c\u5979\u8fd9\u4e48\u4e00\u8bf4\uff0c\u679c\u7136\u4e0d\u518d\u575a\u6301\uff0c\u5148\u884c\u53bb\u4e86\u540e\u9762\u7684\u7985\u623f\u3002 \u3000\u3000\u707c\u4f24\u4e0d\u7b97\u592a\u4e25\u91cd\uff0c\u4f46\u4f24\u5728\u80cc\u90e8\uff0c\u8fd9\u53ef\u82e6\u4e86\u6c5f\u67ab\uff0c\u4e0d\u80fd\u4ef0\u7761\uff0c\u53ea\u597d\u4f0f\u5367\u3002 \u3000\u3000\u8fd9\u4e00\u591c\uff0c\u4ed6\u4e0d\u4f46\u5728\u8d39\u5fc3\u673a\uff0c\u767d\u5fd9\u4e00\u573a\uff0c\u800c\u4e14\u9669\u4e9b\u628a\u547d\u9001\u6389\uff0c\u5b9e\u5728\u5f88\u4e0d\u5212\u7b97\u3002 \u3000\u3000\u5bf9\u65b9\u53ea\u4e0d\u8fc7\u635f\u5931\u4e00\u9876\u5927\u8f7f\uff0c\u53ca\u4e00\u4e2a\u4e0d\u77e5\u6765\u5386\uff0c\u751a\u81f3\u59d3\u540d\u90fd\u4e0d\u8be6\u7684\u4f8f\u5112\u3002 \u3000\u3000\u4e5f\u8bb8\uff0c\u8fde\u84dd\u51e4\u90fd\u4ece\u672a\u89c1\u8fc7\u6b64\u4eba\u3002 \u3000\u3000\u9664\u4e86\u4e09\u5341\u4f4d\u9ed1\u8863\u5200\u624b\uff0c\u56db\u5927\u795e\u901a\u4f7f\u8005\uff0c\u52a0\u4e0a\u795e\u79d8\u6559\u4e3b\u4eb2\u81ea\u7edf\u7387\u7684\u4e00\u6279\u6b7b\u58eb\u4e4b\u5916\uff0c\u5f88\u53ef\u80fd\u5c1a\u6709\u4e00\u652f\u4e0d\u4e3a\u4eba\u77e5\u7684\u5947\u5175\uff0c\u50cf\u4eca\u591c\u4f2a\u88c5\u4ed6\u7684\u4f8f\u5112\u5c31\u662f\u5176\u4e2d\u4e4b\u4e00\u3002 \u3000\u3000\u76f8\u53cd\u7684\uff0c\u6c5f\u67ab\u4ed6\u4eec\u7684\u5b9e\u529b\uff0c\u51e0\u4e4e\u5df2\u5168\u90e8\u4eae\u76f8\u3002\u552f\u4e00\u5c1a\u672a\u66dd\u5149\u7684\uff0c\u53ea\u662f\u4ed6\u4e2a\u4eba\u672a\u66fe\u65bd\u5c55\u7684\u201c\u6740\u624b\u950f\u201d\u800c\u5df2\u3002 \u3000\u3000\u5e78\u597d\u4eca\u591c\u9762\u5bf9\u90a3\u4f8f\u5112\uff0c\u53ca\u65f6\u5bdf\u89c9\u5f62\u52bf\u6709\u5f02\uff0c\u6000\u7591\u5bf9\u65b9\u4e0d\u662f\u795e\u79d8\u6559\u4e3b\u672c\u4eba\uff0c\u672a\u5c06\u6740\u624b\u950f\u4f7f\u51fa\uff0c\u5426\u5219\u5c31\u6beb\u65e0\u4fdd\u7559\uff0c\u5fc5\u8981\u65f6\u4e5f\u62ff\u4e0d\u51fa\u5236\u654c\u7684\u672c\u94b1\u4e86\u3002 \u3000\u3000\u6240\u8c13\u7684\u201c\u6740\u624b\u950f\u201d\uff0c\u4fbf\u662f\u5251\u672f\u4e0a\u81f3\u9ad8\u5883\u754c\u7684\u201c\u9a6d\u5251\u4e4b\u672f\u201d\uff0c\u8fde\u5f20\u56db\u59d1\u81f3\u4eca\u90fd\u88ab\u8499\u5728\u9f13\u91cc\uff0c\u4e0d\u77e5\u6c5f\u67ab\u5df2\u7ecf\u7ec3\u6210\uff0c\u5374\u59cb\u7ec8\u5c1a\u65e0\u673a\u4f1a\u4e00\u5c55\u7edd\u6280\u3002 \u3000\u3000\u5f53\u5e74\u4e09\u5723\u4f3c\u6709\u5148\u89c1\u4e4b\u660e\uff0c\u6599\u77e5\u5e55\u540e\u63a7\u5236\u5929\u4e0b\u6b66\u6797\u7684\u795e\u79d8\u4eba\u5fc5\u662f\u6b66\u529f\u76d6\u4e16\uff0c\u5177\u6709\u8d85\u4eba\u667a\u6167\uff0c\u4e0e\u8fc7\u4eba\u5fc3\u673a\u7684\u5389\u5bb3\u89d2\u8272\uff0c\u5426\u5219\u90a3\u80fd\u628a\u6574\u4e2a\u6c5f\u6e56\u63a7\u5236\u4e86\u8fd9\u4e48\u591a\u5e74\u3002 \u3000\u3000\u56e0\u6b64\u4ed6\u4eec\u518d\u4e09\u544a\u8beb\u6c5f\u67ab\uff0c\u9664\u975e\u662f\u9762\u5bf9\u90a3\u795e\u79d8\u4eba\u7269\u51b3\u4e00\u6b7b\u6218\u65f6\uff0c\u7edd\u4e0d\u53ef\u8f7b\u6613\u65bd\u5c55\u51fa\u9a6d\u5251\u4e4b\u672f\u3002 \u3000\u3000\u6c5f\u67ab\u786e\u5b9e\u505a\u5230\u4e86\uff0c\u5386\u7ecf\u6570\u6b21\u6fc0\u70c8\u6076\u6218\uff0c\u4ed6\u90fd\u672a\u4ee5\u9a6d\u5251\u4e4b\u672f\u53d6\u80dc\u3002 \u3000\u3000\u5373\u4f7f\u4eca\u591c\u5230\u4e86\u4e00\u51b3\u751f\u6b7b\u7684\u5173\u5934\uff0c\u7531\u4e8e\u7a81\u7136\u6000\u7591\u90a3\u4f8f\u5112\u5e76\u975e\u795e\u79d8\u6559\u4e3b\u672c\u4eba\uff0c\u4ecd\u80fd\u53ca\u65f6\u6536\u4f4f\u53bb\u52bf\uff0c\u672a\u4ee5\u9a6d\u5251\u4e4b\u672f\u653b\u5411\u4f8f\u5112\u3002 \u3000\u3000\u6b64\u523b\u56de\u60f3\u8d77\u6765\uff0c\u5f53\u65f6\u6c5f\u67ab\u82e5\u771f\u65bd\u5c55\u51fa\u9a6d\u5251\u4e4b\u672f\uff0c\u7fe0\u7389\u5200\u6fc0\u53d1\u51fa\u7684\u51b7\u8292\u75be\u5982\u95ea\u7535\uff0c\u4e0d\u5f85\u90a3\u4f8f\u5112\u542f\u52a8\u673a\u62ec\uff0c\u5c31\u5df2\u5f15\u53d1\u4e86\u5f3a\u70c8\u7206\u70b8\u3002 \u3000\u3000\u800c\u9a6d\u5251\u4e4b\u672f\u662f\u4eba\u5251\u5408\u4e00\uff0c\u5728\u90a3\u79cd\u98de\u6251\u75be\u5c04\u7684\u60ca\u4eba\u5feb\u901f\u4e4b\u4e0b\uff0c\u6c5f\u67ab\u7edd\u5bf9\u6536\u52bf\u4e0d\u53ca\uff0c\u6839\u672c\u6beb\u65e0\u9009\u547d\u7684\u673a\u4f1a\u3002 \u3000\u3000\u4ed6\u53ef\u771f\u662f\u547d\u5927\uff01 \u3000\u3000\u5ff5\u53ca\u4e8e\u6b64\uff0c\u6c5f\u67ab\u4e0d\u7981\u72b9\u6709\u4f59\u60b8\uff0c\u6697\u53eb\u4e86\u58f0\uff1a\u201c\u597d\u9669\u554a\uff01\u201d \u3000\u3000\u5c31\u5728\u4ed6\u6697\u81ea\u5e86\u5e78\u65f6\uff0c\u5f20\u56db\u59d1\u8d70\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u6c5f\u67ab\u4f0f\u5728\u7985\u69bb\u4e0a\uff0c\u867d\u672a\u770b\u5230\u8fdb\u6765\u7684\u662f\u8c01\uff0c\u4f46\u4ed6\u4ece\u811a\u6b65\u58f0\u4e2d\u5df2\u80fd\u5206\u8fa8\u51fa\u6765\uff1a\u201c\u662f\u5f20\u56db\u59d1\u5417\uff1f\u201d \u3000\u3000\u5f20\u56db\u59d1\u5e94\u4e86\u4e00\u58f0\uff0c\u8d70\u5230\u7985\u69bb\u8fb9\u4fa7\u8eab\u5750\u4e0b\uff0c\u5173\u5fc3\u5730\u95ee\u9053\uff1a\u201c\u5f88\u75db\u5417\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u7b11\u9053\uff1a\u201c\u75db\u662f\u4e0d\u592a\u75db\uff0c\u53ea\u662f\u7761\u89c9\u4e0d\u5927\u65b9\u4fbf\uff0c\u8fd9\u6837\u4f0f\u7740\u6bd4\u8f83\u8212\u670d\u4e9b\u3002\u201d \u3000\u3000\u987f\u4e86\u987f\uff0c\u6025\u5207\u95ee\u9053\uff1a\u201c\u5546\u8bae\u51fa\u7ed3\u679c\u4e86\u5417\uff1f\u201d \u3000\u3000\u5f20\u56db\u59d1\u5fae\u5fae\u628a\u5934\u4e00\u70b9\uff0c\u795e\u60c5\u51dd\u91cd\u9053\uff1a\u201c\u521a\u624d\u542c\u84dd\u51e4\u8bf4\uff0c\u795e\u79d8\u6559\u4e3b\u7a76\u7adf\u9690\u85cf\u4e86\u591a\u5c11\u5b9e\u529b\uff0c\u8fde\u5979\u4e5f\u65e0\u6cd5\u77e5\u9053\uff0c\u800c\u4ed6\u5bf9\u6211\u4eec\u7684\u5b9e\u529b\u5374\u4e86\u5982\u6307\u638c\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u63a5\u53e3\u9053\uff1a\u201c\u5982\u679c\u4ed6\u4ee5\u4e3a\u6211\u5df2\u88ab\u70b8\u6b7b\u5462\uff1f\u201d \u3000\u3000\u201c\u4e0d\u53ef\u80fd\u7684\u3002\u201d\u5f20\u56db\u59a8\u5224\u65ad\u9053\uff1a\u201c\u5f53\u65f6\u90a3\u795e\u79d8\u6559\u4e3b\uff0c\u4e00\u5b9a\u85cf\u5728\u9644\u8fd1\uff0c\u6697\u4e2d\u76d1\u89c6\u3002 \u3000\u3000\u751a\u81f3\u4f2a\u88c5\u6df7\u8ff9\u5728\u90a3\u6279\u9ed1\u8863\u5200\u624b\u4e2d\uff0c\u4ee5\u4fbf\u5c31\u8fd1\u968f\u65f6\u53d1\u53f7\u65bd\u4ee4\uff0c\u5fc5\u8981\u65f6\u7d27\u6025\u5e94\u53d8\uff0c\u81ea\u7136\u770b\u5230\u4e86\u4e00\u5207\u3002\u201d \u3000\u3000\u201c\u90a3\u4ed6\u81f3\u5c11\u77e5\u9053\u6211\u53d7\u4e86\u707c\u4f24\u3002\u201d\u6c5f\u67ab\u8bf4\u3002 \u3000\u3000\u5f20\u56db\u59d1\u9053\uff1a\u201c\u6211\u4eec\u4e5f\u662f\u8fd9\u6837\u60f3\uff0c\u795e\u79d8\u6559\u4e3b\u89c6\u4f60\u7b2c\u4e00\u53f7\u654c\u4eba\uff0c\u7531\u4eca\u591c\u7684\u8be1\u8ba1\u66f4\u53ef\u770b\u51fa\uff0c\u4ed6\u662f\u4e0d\u60dc\u4efb\u4f55\u4ee3\u4ef7\uff0c\u975e\u7f6e\u4f60\u4e8e\u6b7b\u5730\u4e0d\u53ef\uff0c\u5426\u5219\u5bdd\u98df\u96be\u5b89\u3002 \u3000\u3000\u5982\u679c\u4ed6\u77e5\u9053\u4f60\u672a\u88ab\u70b8\u6b7b\uff0c\u4ec5\u53ea\u662f\u53d7\u5230\u707c\u4f24\uff0c\u65e0\u8bba\u4f60\u7684\u4f24\u52bf\u662f\u5426\u4e25\u91cd\uff0c\u5f88\u53ef\u80fd\u8d81\u4f60\u4f24\u52bf\u672a\u6108\u524d\uff0c\u4e0d\u987e\u4e00\u5207\u5927\u4e3e\u6765\u72af\u3002\u6240\u4ee5\u6211\u521a\u624d\u628a\u6240\u6709\u7684\u4eba\u624b\uff0c\u5206\u4e3a\u4e24\u73ed\u8f6e\u6d41\u62c5\u4efb\u6212\u5907\uff0c\u4ee5\u514d\u4ed3\u4fc3\u5e94\u654c\u63aa\u624b\u4e0d\u53ca\u3002\u201d \u3000\u3000\u201c\u5bf9\uff01\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u4ed6\u4eec\u5f88\u53ef\u80fd\u4f1a\u6765\u4e2a\u5b64\u6ce8\u4e00\u63b7\u3002\u4e0d\u8fc7\uff0c\u7167\u4eca\u591c\u7684\u60c5\u5f62\u770b\uff0c\u84dd\u51e4\u5e76\u672a\u63d0\u5230\u8fc7\u90a3\u4f2a\u88c5\u795e\u79d8\u6559\u4e3b\u7684\u4f8f\u5112\uff0c\u7a81\u7136\u5192\u4e86\u51fa\u6765\uff0c\u662f\u5426\u610f\u5473\u7740\u9664\u4e86\u84dd\u98ce\u77e5\u9053\u7684\u4eba\u4e4b\u5916\uff0c\u795e\u79d8\u6559\u4e3b\u5c1a\u9690\u85cf\u4e86\u5b9e\u529b\uff0c\u53e6\u6709\u4e00\u652f\u4f0f\u5175\uff1f\u201d \u3000\u3000\u5f20\u56db\u59d1\u70b9\u70b9\u5934\u9053\uff1a\u201c\u8fd9\u4e2a\u6211\u4eec\u4e5f\u60f3\u5230\u4e86\uff0c\u636e\u8001\u53eb\u5316\u8bf4\uff0c\u7167\u4f60\u521a\u624d\u6240\u63cf\u8ff0\u7684\u5f62\u8c8c\uff0c\u4eca\u591c\u51fa\u73b0\u7684\u90a3\u4e2a\u4f8f\u5112\uff0c\u6781\u53ef\u80fd\u662f\u5f53\u5e74\u7684\u5173\u4e1c\u4e94\u5947\u4e4b\u4e00\u2026\u2026\u201d \u3000\u3000\u201c\u5173\u4e1c\u4e94\u5947\uff1f\u201d\u6c5f\u67ab\u832b\u7136\u95ee\uff1a\u201c\u4ed6\u4eec\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u5f20\u56db\u59d1\u9053\uff1a\u201c\u5927\u7ea6\u4e00\u7532\u5b50\u524d\uff0c\u5173\u4e1c\u6731\u5bb6\u5be8\u5728\u6c5f\u6e56\u4e0a\u9887\u8d1f\u76db\u540d\uff0c\u5c24\u5176\u5728\u5173\u4e1c\u4e00\u5e26\uff0c\u66f4\u662f\u79f0\u9738\u4e00\u65b9\u3002 \u3000\u3000\u5be8\u4e3b\u59d3\u6731\u540d\u6c5d\uff0c\u4eba\u79f0\u7d2b\u9762\u91d1\u521a\uff0c\u8eab\u9ad8\u4e03\u5c3a\u56db\uff0c\u540d\u7b26\u5176\u5b9e\u662f\u4e2a\u91d1\u521a\u3002 \u3000\u3000\u4ed6\u4ed7\u7740\u4f53\u578b\u5f02\u4e8e\u5e38\u4eba\u7ec3\u7684\u662f\u5916\u5bb6\u529f\u592b\uff0c\u4f20\u8bf4\u66fe\u6709\u4e00\u62f3\u6bd9\u864e\uff0c\u53cc\u624b\u6293\u8d77\u6d3b\u4eba\u4e24\u811a\uff0c\u6495\u88c2\u6210\u4e24\u7247\u4e4b\u5a01\u3002 \u3000\u3000\u8fd9\u5bb6\u4f19\u55dc\u9152\u597d\u8272\uff0c\u6027\u60c5\u53c8\u66b4\u8e81\uff0c\u4eba\u4eba\u656c\u9b3c\u795e\u800c\u8fdc\u4e4b\uff0c\u54ea\u6709\u4eba\u5bb6\u6562\u628a\u5973\u513f\u5ac1\u7ed9\u4ed6\u3002\u4f46\u4ed6\u5a36\u4e0d\u5230\u8001\u5a46\uff0c\u5e72\u8106\u7528\u62a2\u7684\uff0c\u88ab\u4ed6\u770b\u4e2d\u5173\u5185\u91d1\u5927\u6237\u7684\u5343\u91d1\uff0c\u592e\u5a92\u63d0\u4eb2\u4e0d\u6210\uff0c\u5c31\u4eb2\u81ea\u4e0a\u95e8\u62a2\u4e86\u56de\u53bb\u6210\u4eb2\u3002 \u3000\u3000\u91d1\u5927\u6237\u6151\u4e8e\u4ed6\u7684\u5a01\u52bf\uff0c\u6562\u6012\u800c\u4e0d\u6562\u8a00\uff0c\u660e\u77e5\u544a\u5230\u4e86\u5b98\u5e9c\u4e5f\u6551\u4e0d\u56de\u5973\u513f\uff0c\u53ea\u597d\u5fcd\u6c14\u541e\u58f0\u3002 \u3000\u3000\u540e\u6765\u4e0d\u60dc\u91cd\u91d1\uff0c\u8bf7\u4e86\u51e0\u4f4d\u6b66\u529f\u6781\u9ad8\u7684\u6c5f\u6e56\u4eba\u51fa\u9762\uff0c\u6253\u7b97\u903c\u6731\u6c5d\u628a\u4ed6\u5973\u513f\u653e\u56de\u3002 \u3000\u3000\u7ed3\u679c\u90a3\u51e0\u4e2a\u6c5f\u6e56\u4eba\u7269\u5230\u4e86\u6731\u5bb6\u6765\uff0c\u7adf\u662f\u8089\u5305\u5b50\u6253\u72d7\uff0c\u843d\u4e2a\u6709\u53bb\u65e0\u56de\u3002\u4ece\u6b64\u4ee5\u540e\uff0c\u91d1\u5927\u6237\u975e\u4f46\u4e0d\u6562\u518d\u8bd5\uff0c\u53cd\u800c\u88ab\u8feb\u4ea4\u51fa\u5168\u90e8\u5bb6\u8d22\u5f53\u4ed6\u5973\u513f\u7684\u5ac1\u5986\u3002 \u3000\u3000\u8fd9\u4e00\u6765\uff0c\u5413\u5f97\u91d1\u5927\u6237\u8d76\u5feb\u53d8\u5356\u5bb6\u4ea7\uff0c\u4e3e\u5bb6\u8fde\u591c\u9003\u8d70\uff0c\u4e0d\u5e78\u90fd\u5728\u9014\u4e2d\u9047\u5230\u62e6\u622a\uff0c\u5168\u5bb6\u5341\u4f59\u53e3\u6089\u906d\u6bd2\u624b\uff0c\u65e0\u4e00\u5e78\u514d\u3002 \u3000\u3000\u91d1\u5927\u5c0f\u59d0\u5c1a\u4e0d\u77e5\u60c5\uff0c\u65e5\u591c\u76fc\u671b\u5bb6\u91cc\u6d3e\u4eba\u6765\u8425\u6551\uff0c\u4ee5\u81f4\u79ef\u5fe7\u6210\u75be\uff0c\u4e00\u75c5\u4e0d\u8d77\uff0c\u4e0d\u4e45\u5c31\u9999\u6d88\u7389\u6b92\u4e86\u3002 \u3000\u3000\u6731\u6c5d\u597d\u8272\u672c\u6027\u4e0d\u6539\uff0c\u9646\u7eed\u53c8\u62a2\u56de\u4e86\u4e0d\u5c11\u826f\u5bb6\u5987\u5973\u5f53\u8001\u5a46\u3002\u800c\u4e14\u4ed6\u7acb\u4e0b\u4e2a\u2018\u5bb6\u6cd5\u2019\uff0c\u534a\u5e74\u4e4b\u5185\u4e0d\u6000\u8eab\u5b55\uff0c\u90a3\u5c31\u683c\u6740\u52ff\u6cbb\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u5fcd\u4e0d\u4f4f\u6124\u58f0\u53eb\u9053\u3002 \u3000\u3000\u201c\u54ea\u6709\u8fd9\u79cd\u8dcb\u6248\u7684\u4eba\uff01\u201d \u3000\u3000\u201c\u4ed6\u5c31\u662f\u8fd9\u79cd\u4eba\uff01\u201d\u5f20\u56db\u59d1\u63a5\u4e0b\u53bb\u9053\uff1a\u201c\u516d\u5e74\u4e4b\u5185\uff0c\u4ed6\u4e00\u8fde\u62a2\u56de\u5341\u4e8c\u4e2a\u8001\u5a46\uff0c\u504f\u504f\u6ca1\u6709\u4e00\u4e2a\u8001\u5a46\u7684\u809a\u76ae\u6323\u6c14\uff0c\u6240\u4ee5\u5341\u4e8c\u4e2a\u65e0\u8f9c\u7684\u5973\u5b50\u3001\u5168\u88ab\u4ed6\u4e00\u4e00\u5904\u6b7b\u3002 \u3000\u3000\u7b49\u5230\u62a2\u56de\u7b2c\u5341\u4e09\u4e2a\u8001\u5a46\uff0c\u8fd9\u5973\u5b50\u603b\u7b97\u547d\u4e0d\u8be5\u7edd\uff0c\u7b2c\u4e8c\u4e2a\u6708\u5c31\u6000\u4e86\u5b55\u3002\u6731\u6c5d\u559c\u51fa\u671b\u5916\uff0c\u628a\u90a3\u5973\u5b50\u89c6\u82e5\u7687\u6bcd\u5a18\u5a18\u822c\u770b\u5f85\uff0c\u4e0d\u60dc\u4ee3\u4ef7\u5927\u6279\u91c7\u8d2d\u5929\u4e0b\u73cd\u836f\u8865\u54c1\uff0c\u751a\u81f3\u6d3e\u4eba\u6f5c\u5165\u7687\u5bab\uff0c\u76d7\u51fa\u7687\u540e\u670d\u7528\u7684\u8865\u836f\uff0c\u8ba9\u90a3\u5973\u5b50\u5b89\u80ce\u8865\u8eab\u3002 \u3000\u3000\u90a3\u5973\u5b50\u6000\u80ce\u8db3\u8db3\u5341\u4e09\u4e2a\u6708\uff0c\u624d\u4e00\u4e3e\u4ea7\u4e0b\u4e94\u4e2a\u7537\u5a74\u3002\u7167\u7406\u8bf4\uff0c\u671b\u5b50\u5fc3\u5207\u7684\u6731\u6c5d\uff0c\u4e00\u80ce\u5f97\u4e94\u5b50\uff0c\u5c82\u4e0d\u662f\u5929\u5927\u7684\u559c\u4e8b\uff1f\u5176\u5b9e\u4e0d\u7136\uff0c\u5927\u6982\u4ed6\u4f5c\u6076\u591a\u7aef\uff0c\u5929\u7406\u96be\u5bb9\uff0c\u4ea7\u4e0b\u7684\u4e94\u4e2a\u7537\u5a74\uff0c\u7adf\u7136\u5168\u662f\u7578\u5f62\u513f\uff01\u201d \u3000\u3000\u6c5f\u67ab\u4e0d\u7981\u7b11\u95ee\uff1a\u201c\u90a3\u4ed6\u4e0d\u662f\u6c14\u75af\u4e86\uff1f\u201d \u3000\u3000\u5f20\u56db\u59d1\u9053\uff1a\u201c\u90a3\u8fd8\u7528\u8bf4\uff0c\u6731\u6c5d\u4e00\u6c14\u4e4b\u4e0b\uff0c\u4e00\u638c\u51fb\u6bd9\u4e86\u4e94\u4e2a\u5a74\u513f\u7684\u6bcd\u4eb2\uff0c\u5e76\u4e14\u4e00\u811a\u8e22\u6b7b\u4e86\u63a5\u751f\u5a46\uff0c\u5413\u5f97\u4e00\u65c1\u534f\u52a9\u7684\u5987\u4eba\u548c\u4e2b\u73af\uff0c\u5f53\u573a\u90fd\u660f\u6b7b\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u4f46\u864e\u6bd2\u4e0d\u98df\u5b50\uff0c\u90a3\u4e94\u4e2a\u7537\u5b69\u867d\u662f\u7578\u5f62\u513f\uff0c\u6bd5\u7adf\u662f\u6731\u6c5d\u7684\u4eb2\u9aa8\u8089\u3002\u672c\u60f3\u628a\u4ed6\u4eec\u5168\u6454\u6b7b\uff0c\u4ee5\u514d\u88ab\u4eba\u80cc\u540e\u9a82\u4ed6\u7f3a\u5fb7\u4e8b\u505a\u591a\u4e86\uff0c\u624d\u4f1a\u5f97\u5230\u62a5\u5e94\uff0c\u5374\u53c8\u4e8e\u5fc3\u4e0d\u5fcd\uff0c\u53ea\u597d\u7559\u4e0b\u4e94\u6761\u5c0f\u547d\uff0c\u96c7\u4e86\u5976\u5a18\u56de\u6765\u629a\u517b\u3002 \u3000\u3000\u4e94\u4e2a\u7537\u5b69\u867d\u5929\u751f\u7578\u578b\uff0c\u5176\u8c8c\u4e0d\u626c\uff0c\u4f46\u5374\u751f\u6765\u5c31\u662f\u7ec3\u6b66\u7684\u6750\u6599\u3001\u4e09\u5c81\u5c31\u6210\u5929\u821e\u67aa\u5f04\u68d2\uff0c\u62f3\u6253\u811a\u8e22\u3002 \u3000\u3000\u6731\u6c5d\u770b\u5728\u773c\u91cc\uff0c\u603b\u7b97\u804a\u6170\u4e8e\u5fc3\uff0c\u4fbf\u4eb2\u81ea\u4f20\u6388\u4ed6\u4eec\u4e94\u5144\u5f1f\u6b66\u529f\uff0c\u6253\u4e0b\u4e86\u57fa\u7840\u540e\uff0c\u65e6\u53c8\u91cd\u91d1\u793c\u8058\u4e2d\u539f\u7684\u6b66\u672f\u540d\u5bb6\uff0c\u524d\u5f80\u5173\u4e1c\u6559\u7ec3\u3002 \u3000\u3000\u4ed6\u4eec\u771f\u662f\u5929\u8d4b\u5f02\u80fd\uff0c\u65e0\u8bba\u4ec0\u4e48\u6b66\u529f\uff0c\u4e00\u5b66\u5c31\u4f1a\uff0c\u901a\u5e38\u4eba\u5bb6\u8981\u7ec3\u4e0a\u4e09\u4e94\u5e74\u529f\u592b\uff0c\u4e94\u5144\u5f1f\u53ea\u987b\u4e00\u5e74\u534a\u8f7d\u5c31\u7ec3\u6210\uff0c\u800c\u4e14\u9752\u51fa\u4e8e\u84dd\uff0c\u80dc\u4e8e\u84dd\u3002 \u3000\u3000\u4ec5\u4ec5\u5341\u51e0\u5e74\uff0c\u4ed6\u4eec\u5c31\u5148\u540e\u638f\u5149\u4e86\u8fd1\u767e\u4f4d\u540d\u5e08\u7684\u7bb1\u5e95\u770b\u5bb6\u672c\u9886\uff0c\u8001\u53eb\u5316\u4e4b\u6240\u4ee5\u77e5\u9053\u8fd9\u4e9b\uff0c\u56e0\u4e3a\u5f53\u5e74\u53d7\u8058\u53bb\u5173\u4e1c\u6731\u5bb6\u5be8\u7684\u540d\u5e08\u4e2d\uff0c\u6709\u4e00\u4f4d\u662f\u4ed6\u7684\u5e08\u53d4\u3002 \u3000\u3000\u6731\u6c5d\u8fd9\u4e94\u4e2a\u5b9d\u8d1d\u513f\u5b50\uff0c\u5230\u4e86\u4e8c\u5341\u5c81\uff0c\u8eab\u9ad8\u4ecd\u4e0d\u8db3\u56db\u5c3a\u3002\u5728\u4ed6\u4eec\u6b22\u5ea6\u4e8c\u5341\u5c81\u751f\u65e5\u7684\u90a3\u5929\uff0c\u6731\u6c5d\u7a81\u544a\u4e2d\u98ce\u4e0d\u6cbb\uff0c\u5f53\u591c\u5c31\u4e00\u547d\u5f52\u897f\u3002 \u3000\u3000\u4ece\u6b64\u4ee5\u540e\uff0c\u6731\u5bb6\u5be8\u4fbf\u7531\u4e94\u5144\u5f1f\u7ee7\u627f\u3002 \u3000\u3000\u7531\u4e8e\u4ed6\u4eec\u6b66\u529f\u8fdc\u8d85\u8fc7\u7236\u4eb2\uff0c\u4f7f\u6731\u5bb6\u5be8\u7684\u58f0\u52bf\u66f4\u76db\u2018\u5173\u4e1c\u4e94\u5947\u2019\u4e4b\u540d\u4e5f\u4f20\u4e86\u5173\u6765\uff0c\u751a\u81f3\u8fdc\u53ca\u4e2d\u539f\u3002 \u3000\u3000\u51fa\u5176\u7236\uff0c\u5fc5\u6709\u5176\u5b50\uff0c\u5173\u4e1c\u4e94\u5947\u7684\u597d\u8272\uff0c\u6bd4\u6731\u6c5d\u66f4\u6709\u8fc7\u4e4b\u800c\u65e0\u4e0d\u53ca\u3002\u5999\u7684\u662f\u4ed6\u4eec\u5a36\u4e0d\u5230\u8001\u5a46\uff0c\u4e5f\u5b66\u7236\u4eb2\u7528\u62a2\u7684\u3002\u6709\u4e00\u6b21\uff0c\u4ed6\u4eec\u4e94\u5144\u5f1f\u90fd\u770b\u4e2d\u4e00\u4e2a\u6c5f\u6e56\u5356\u827a\u7684\u5e74\u8f7b\u59d1\u5a18\uff0c\u62a2\u56de\u53bb\u4f60\u4e89\u6211\u593a\uff0c\u4ee5\u81f4\u5927\u6253\u51fa\u624b\u3002 \u3000\u3000\u90a3\u59d1\u5a18\u8d81\u4ed6\u4eec\u95f9\u5f97\u4e0d\u53ef\u5f00\u4ea4\u65f6\uff0c\u4f3a\u673a\u9003\u4e4b\u592d\u592d\uff0c\u4e94\u5144\u5f1f\u53ca\u65f6\u53d1\u89c9\u8ffd\u4e86\u51fa\u5be8\uff0c\u7ed3\u679c\u5374\u4e00\u53bb\u4e0d\u8fd4\uff0c\u4ece\u6b64\u4e0d\u77e5\u4ed6\u4eec\u4e94\u4eba\u7684\u53bb\u5411\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u5ffd\u95ee\uff1a\u201c\u5927\u59d0\uff0c\u8001\u53eb\u5316\u6709\u6ca1\u6709\u8bf4\uff0c\u90a3\u662f\u53d1\u751f\u5728\u591a\u4e45\u524d\u7684\u4e8b\uff1f\u201d \u3000\u3000\u5f20\u56db\u59d1\u9053\uff1a\u201c\u5927\u7ea6\u4e0d\u5230\u4e8c\u5341\u5e74\u5427\u3002\u201d \u3000\u3000\u201c\u8fd9\u5c31\u662f\u4e86\uff01\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u4e8c\u5341\u5e74\u524d\uff0c\u6b63\u662f\u795e\u79d8\u6559\u4e3b\u6697\u4e2d\u7b56\u5212\u63a7\u5236\u5929\u4e0b\u6b66\u6797\u4e4b\u65f6\uff0c\u8bf4\u4e0d\u5b9a\u90a3\u6c5f\u6e56\u5356\u827a\u7684\u59d1\u5a18\uff0c\u5c31\u662f\u5949\u547d\u8bf7\u51fa\u90a3\u4e94\u5144\u5f1f\uff0c\u4f7f\u4ed6\u4eec\u4e00\u8def\u8fdb\u5165\u4e2d\u539f\uff0c\u843d\u5165\u5708\u5957\u53d7\u5230\u63a7\u5236\u5462\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u918d\u9190\u704c\u9876\uff0c\u5931\u58f0\u53eb\u9053\uff1a\u201c\u5bf9\u554a\uff01\u8fd9\u4e00\u70b9\u6211\u4eec\u7adf\u7136\u6ca1\u6709\u60f3\u5230\uff0c\u5982\u6b64\u770b\u6765\uff0c\u5173\u4e1c\u4e94\u5947\u4e00\u5b9a\u5168\u53d7\u795e\u79d8\u6559\u4e3b\u63a7\u5236\u4e86\u3002\u201d \u3000\u3000\u6c5f\u67ab\u7b11\u4e86\u7b11\uff0c\u66f4\u6b63\u9053\uff1a\u201c\u73b0\u5728\u53ea\u5269\u56db\u5947\u5566\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u6b63\u8272\u9053\uff1a\u201c\u4e0d\u7ba1\u5269\u4e0b\u4e86\u51e0\u5947\uff0c\u4eca\u591c\u4f01\u56fe\u8ddf\u4f60\u540c\u5f52\u4e8e\u5c3d\u7684\u90a3\u4e2a\u4f8f\u5112\uff0c\u5e94\u662f\u5173\u4e1c\u4e94\u5947\u4e4b\u4e00\u5927\u6982\u9519\u4e0d\u4e86\u3002 \u3000\u3000\u95ee\u9898\u5c31\u5728\u8fd9\u91cc\uff0c\u4ed6\u4eec\u88ab\u63a7\u5236\u4e86\u8fd1\u4e8c\u5341\u5e74\uff0c\u4e00\u65e6\u6d3e\u51fa\u5c31\u7518\u5fc3\u53d7\u795e\u79d8\u6559\u4e3b\u9a71\u4f7f\uff0c\u4e0d\u60dc\u727a\u7272\u751f\u547d\uff0c\u5f62\u540c\u6b7b\u58eb\uff0c\u8fd9\u79cd\u89c6\u6b7b\u5982\u5f52\u7684\u4f5c\u98ce\uff0c\u5b9e\u5728\u5f88\u53ef\u6015\u3002 \u3000\u3000\u56e0\u4e3a\uff0c\u5bf9\u624b\u6b66\u529f\u518d\u9ad8\uff0c\u76ee\u7684\u90fd\u662f\u5236\u654c\u4e8e\u6b7b\uff0c\u4fdd\u5168\u81ea\u5df1\u7684\u751f\u547d\u3002 \u3000\u3000\u800c\u8fd9\u79cd\u4eba\u5374\u4e0d\u987e\u4e00\u5207\uff0c\u53ea\u6c42\u5b8c\u6210\u7f6e\u5bf9\u65b9\u4e8e\u6b7b\u7684\u4efb\u52a1\uff0c\u6839\u672c\u4e0d\u5728\u4e4e\u8d54\u4e0a\u81ea\u5df1\u7684\u4e00\u6761\u547d\u3002 \u3000\u3000\u9047\u4e0a\u8fd9\u79cd\u73a9\u547d\u7684\u5bb6\u4f19\uff0c\u6211\u4eec\u4e0d\u5f97\u4e0d\u9632\uff0c\u5374\u53c8\u9632\u4e0d\u80dc\u9632\uff0c\u771f\u6559\u4eba\u597d\u4e0d\u5fc3\u70e6\u3002\u201d \u3000\u3000\u6c5f\u67ab\u8f7b\u63cf\u6de1\u5199\u9053\uff1a\u201c\u8fd9\u6709\u4ec0\u4e48\u597d\u70e6\u7684\uff0c\u5175\u6765\u5c06\u6321\uff0c\u6c34\u6765\u571f\u63a9\u561b\u3002\u201d \u3000\u3000\u201c\u8bdd\u4e0d\u662f\u8fd9\u4e48\u8bf4\uff0c\u4f60\u5927\u6168\u8fd8\u6ca1\u542c\u61c2\u6211\u7684\u610f\u601d\u3002\u201d\u5f20\u56db\u59d1\u8f7b\u53f9\u4e00\u58f0\uff0c\u5fe7\u5fc3\u5fe1\u5fe1\u9053\uff1a\u201c\u6211\u6240\u803d\u5fc3\u7684\uff0c\u662f\u4e07\u4e00\u5bf9\u65b9\u91cd\u65bd\u6545\u6280\uff0c\u4e0b\u6b21\u53c8\u6d3e\u51fa\u4e2a\u6b7b\u58eb\u5192\u5145\u795e\u79d8\u6559\u4e3b\uff0c\u4e00\u63a5\u8fd1\u4f60\uff0c\u4e0d\u7b49\u4ea4\u624b\u5c31\u5f15\u53d1\u85cf\u5728\u8eab\u4e0a\u7684\u70b8\u836f\uff0c\u51b3\u5fc3\u8ddf\u4f60\u540c\u5f52\u4e8e\u5c3d\uff0c\u4f7f\u4f60\u8fde\u9003\u5f00\u7684\u673a\u4f1a\u90fd\u6ca1\u6709\uff0c\u54ea\u4f60\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u6c89\u601d\u4e0d\u8bed\u3002 \u3000\u3000\u5f20\u56db\u59d1\u63a5\u7740\u53c8\u8bf4\uff1a\u201c\u4e0d\u4ec5\u662f\u4f60\uff0c\u5176\u4ed6\u4eba\u4e5f\u4e00\u6837\u3002\u800c\u5bf9\u65b9\u6709\u7684\u662f\u4eba\uff0c\u66f4\u4e0d\u4e4f\u73a9\u547d\u7684\u6b7b\u571f\uff0c\u5982\u679c\u4e00\u4e2a\u62fc\u4e00\u4e2a\uff0c\u6700\u540e\u4e0d\u62e9\u624b\u6bb5\u628a\u4eba\u90fd\u62fc\u5b8c\u4e86\uff0c\u4ed6\u4eec\u4ecd\u6709\u4e0d\u5c11\u4eba\u3002\u4f60\u60f3\u60f3\u770b\uff0c\u54ea\u53c8\u5c06\u6210\u4f55\u5c40\u9762\uff1f\u201d \u3000\u3000\u201c\u8fd9\u5012\u786e\u5b9e\u5f88\u9ebb\u70e6\u2026\u2026\u201d\u6c5f\u67ab\u6c89\u541f\u4e00\u4e0b\uff0c\u95ee\u9053\uff1a\u201c\u5927\u59d0\u53ef\u60f3\u51fa\u4e86\u4ec0\u4e48\u5bf9\u7b56\uff1f\u201d \u3000\u3000\u5f20\u56db\u59d1\u9053\uff1a\u201c\u76ee\u524d\u552f\u4e00\u7684\u4e07\u5168\u4e4b\u8ba1\uff0c\u53ea\u6709\u6839\u672c\u4e0d\u8ba9\u5bf9\u65b9\u7684\u4eba\u63a5\u8fd1\u6211\u4eec\uff0c\u8fdb\u5165\u7206\u70b8\u529b\u8303\u56f4\u4e4b\u5916\u5c31\u6740\u3002\u201d \u3000\u3000\u6c5f\u67ab\u4e0d\u4ee5\u4e3a\u7136\u9053\uff1a\u201c\u8fd9\u6837\u4e00\u6765\uff0c\u5145\u5176\u91cf\u6211\u4eec\u53ea\u80fd\u591a\u6740\u4e00\u4e9b\u5bf9\u65b9\u7684\u6b7b\u58eb\uff0c\u8981\u60f3\u9664\u6389\u90a3\u7f6a\u9b41\u7978\u9996\u5c31\u66f4\u96be\u4e86\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u65e0\u5948\u5730\u53f9\u9053\uff1a\u201c\u8fd9\u70b9\u6211\u4e5f\u8003\u8651\u5230\u4e86\uff0c\u53ef\u662f\uff0c\u9664\u6b64\u4e4b\u5916\uff0c\u5b9e\u5728\u60f3\u4e0d\u51fa\u4e24\u5168\u4e4b\u8ba1\u2026\u2026\u201d \u3000\u3000\u6b63\u8bf4\u4e4b\u95f4\uff0c\u5ffd\u89c1\u9752\u51e4\u5306\u5306\u8d70\u4e86\u8fdb\u6765\uff0c\u6025\u5207\u9053\uff1a\u201c\u5f20\u59e8\uff0c\u4f60\u5feb\u53bb\u770b\u541f\u96ea\u3001\u541f\u971c\u59d0\u59b9\u5427\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u4e00\u6014\uff0c\u60ca\u95ee\uff1a\u201c\u5979\u4eec\u600e\u4e48\u4e86\uff1f\u201d \u3000\u3000\u9752\u98ce\u795e\u60c5\u614c\u5f20\u9053\uff1a\u201c\u5979\u4eec\u60c5\u7eea\u5f88\u4e0d\u7a33\u5b9a\uff0c\u521a\u624d\u6b27\u9633\u524d\u8f88\u8bf4\u4e86\u5979\u4eec\u51e0\u53e5\uff0c\u5979\u4eec\u5dee\u70b9\u8ddf\u6b27\u9633\u524d\u8f88\u51b2\u7a81\u8d77\u6765\u3002\u7f57\u5170\u524d\u8f88\u628a\u5979\u4eec\u529d\u5230\u5ba2\u623f\u53bb\u4e86\uff0c\u8981\u6211\u6765\u8bf7\u5f20\u59e8\uff0c\u5feb\u53bb\u5b89\u629a\u5b89\u629a\u5979\u4eec\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u6c89\u541f\u4e00\u4e0b\uff0c\u8f6c\u5411\u6c5f\u67ab\u9053\uff1a\u201c\u6211\u770b\u8fd8\u662f\u4f60\u53bb\u6bd4\u8f83\u7ba1\u7528\u3002\u201d \u3000\u3000\u201c\u6211\uff1f\u201d\u6c5f\u67ab\u9762\u6709\u96be\u8272\u9053\uff1a\u201c\u5927\u59d0\uff0c\u6211\u6050\u6015\u2026\u2026\u201d \u3000\u3000\u5f20\u56db\u59d1\u7b11\u9053\uff1a\u201c\u653e\u5fc3\u5979\u4eec\u4e0d\u4f1a\u5403\u4e86\u4f60\u7684\u3002\u4eca\u591c\u5728\u6797\u5916\uff0c\u5979\u4eec\u65e9\u5df2\u6309\u637a\u4e0d\u4f4f\uff0c\u8822\u8822\u6b32\u52a8\uff0c\u88ab\u6211\u5f3a\u884c\u963b\u6b62\uff0c\u5fc3\u91cc\u5df2\u7ecf\u5bf9\u6211\u5f88\u4e0d\u6ee1\u4e86\u3002 \u3000\u3000\u8fd9\u4f1a\u513f\u8fde\u5979\u4eec\u7236\u4eb2\u90fd\u7ba1\u4e0d\u4f4f\uff0c\u6211\u53bb\u80fd\u7ba1\u5f97\u4e86\u7528\u5417\uff1f\u6240\u4ee5\uff0c\u53ea\u6709\u4f60\u53bb\u5b89\u629a\u5979\u4eec\u51e0\u53e5\uff0c\u624d\u80fd\u4f7f\u5979\u4eec\u7684\u60c5\u7eea\u7a33\u5b9a\u4e0b\u6765\u3002\u201d \u3000\u3000\u201c\u597d\u5427\uff01\u201d \u3000\u3000\u6c5f\u67ab\u52c9\u4e3a\u5176\u96be\u5730\u8d77\u8eab\u4e0b\u4e86\u7985\u69bb\uff0c\u5f20\u56db\u59d1\u62ff\u4e86\u4ef6\u957f\u886b\u4e3a\u4ed6\u62ab\u4e0a\uff0c\u8f7b\u58f0\u53ee\u5631\u9053\uff1a\u201c\u8bb0\u4f4f\uff0c\u4efb\u4f55\u60c5\u51b5\u4e4b\u4e0b\uff0c\u5fc5\u987b\u4f7f\u5979\u4eec\u4fdd\u6301\u5b8c\u58c1\uff0c\u5426\u5219\u540e\u679c\u4e0d\u582a\u8bbe\u60f3\u3002\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\u3002\u201d\u6c5f\u67ab\u70b9\u70b9\u5934\uff0c\u8d70\u51fa\u7985\u623f\u3002 \u3000\u3000\u9752\u51e4\u8fd8\u6b32\u8ddf\u5f80\uff0c\u88ab\u5f20\u56db\u59d1\u4e00\u628a\u62d6\u4f4f\u3002 \u3000\u3000\u201c\u4f60\u53bb\u5e72\u561b\uff1f\u201d \u3000\u3000\u9752\u51e4\u987f\u9762\u7ea2\u8033\u8d64\uff0c\u7a98\u8feb\u9053\uff1a\u201c\u6211\uff0c\u6211\u2026\u2026\u201d \u3000\u3000\u5f20\u56db\u59d1\u7b11\u7740\u62d6\u5979\u5230\u7985\u69bb\u8fb9\uff0c\u9053\uff1a\u201c\u6765\uff0c\u6211\u4eec\u5750\u4e0b\u804a\u804a\u5427\u3002\u201d \u3000\u3000\u9752\u51e4\u53ea\u597d\u5750\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u6c5f\u67ab\u521a\u8d70\u8fd1\u5ba2\u623f\u95e8\u5916\uff0c\u5c31\u542c\u4e24\u59d0\u59b9\u5728\u4e89\u6267\u3002 \u3000\u3000\u201c\u4f60\u4e5f\u4e0d\u80fd\u5168\u602a\u5f20\u59e8\u5440\u3002\u201d\u8fd9\u662f\u541f\u971c\u7684\u58f0\u97f3\u3002 \u3000\u3000\u541f\u96ea\u9a73\u9053\uff1a\u201c\u600e\u4e48\u4e0d\u602a\u5979\uff1f\u4eca\u591c\u6b63\u662f\u6211\u4eec\u5927\u663e\u8eab\u624b\u7684\u673a\u4f1a\uff0c\u5979\u5374\u963b\u6b62\u6211\u4eec\u52a8\u624b\uff0c\u773c\u7741\u7741\u5730\u770b\u7740\u90a3\u6279\u7537\u5973\u64a4\u8d70\uff0c\u771f\u4e0d\u77e5\u5979\u5b58\u7684\u4ec0\u4e48\u5fc3\uff01\u201d \u3000\u3000\u541f\u971c\u9053\uff1a\u201c\u8c01\u6559\u4f60\u53bb\u5411\u5979\u54ea\uff0c\u8981\u6740\uff0c\u6211\u4eec\u5c31\u52a8\u624b\u6740\uff0c\u6740\u4e86\u518d\u8bf4\uff0c\u5979\u53c8\u80fd\u628a\u6211\u4eec\u600e\u6837\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u542c\u5f97\u6697\u81ea\u5fc3\u60ca\uff0c\u8fd9\u4e24\u4e2a\u59d0\u59b9\u679c\u7136\u5df2\u6740\u6027\u5927\u53d1\uff0c\u82e5\u4e0d\u53ca\u65e9\u8bbe\u6cd5\u633d\u6551\uff0c\u5f88\u5feb\u5c31\u4f1a\u6108\u9677\u6108\u6df1\uff0c\u7ec8\u81f3\u65e0\u4ee5\u81ea\u62d4\u7684\u5730\u6b65\u3002 \u3000\u3000\u4ed6\u5fd9\u8f7b\u54b3\u4e00\u58f0\uff0c\u5411\u5ba2\u623f\u8d70\u8fdb\u53bb\u3002 \u3000\u3000\u4e24\u59d0\u59b9\u89c1\u4ed6\u7a81\u7136\u6765\u5230\uff0c\u4f3c\u89c9\u6709\u4e9b\u610f\u5916\u3002 \u3000\u3000\u541f\u96ea\u8fce\u4e0a\u524d\uff0c\u8be7\u5f02\u5730\u95ee\uff1a\u201c\u4f60\u600e\u4e48\u4e0d\u5728\u7985\u623f\u6b47\u7740\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u7b11\u9053\uff1a\u201c\u600e\u4e48\uff0c\u4e0d\u6b22\u8fce\u6211\u5417\uff1f\u201d \u3000\u3000\u541f\u971c\u51b7\u58f0\u9053\uff1a\u201c\u4e0d\u662f\u6211\u4eec\u4e0d\u6b22\u8fce\u4f60\uff0c\u662f\u6015\u6709\u4eba\u4f1a\u4e0d\u9ad8\u5174\uff01\u201d \u3000\u3000\u201c\u8c01\u4f1a\u4e0d\u9ad8\u5174\uff1f\u201d\u6c5f\u67ab\u6545\u610f\u95ee\u5979\u3002 \u3000\u3000\u541f\u971c\u4ee5\u4e0d\u5c51\u7684\u53e3\u543b\u9053\uff1a\u201c\u5f20\u59e8\uff01\u201d \u3000\u3000\u201c\u5979\uff1f\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u5979\u4e3a\u4ec0\u4e48\u4f1a\u4e0d\u9ad8\u5174\uff1f\u201d\u541f\u971c\u51b7\u54fc\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u5fc3\u91cc\u660e\u767d\u4f55\u5fc5\u660e\u77e5\u6545\u95ee\uff0c\u5728\u6211\u4eec\u9762\u524d\u88c5\u7cca\u6d82\uff01\u201d \u3000\u3000\u6c5f\u67ab\u6d12\u7136\u4e00\u7b11\u9053\uff1a\u201c\u5982\u679c\u6211\u8bf4\uff0c\u662f\u5979\u8981\u6211\u6765\u966a\u4f60\u4eec\u804a\u804a\uff0c\u4f60\u4eec\u76f8\u4fe1\u5417\uff1f\u201d \u3000\u3000\u541f\u96ea\u3001\u541f\u971c\u5f02\u53e3\u540c\u58f0\u9053\uff1a\u201c\u9b3c\u624d\u76f8\u4fe1\uff01\u201d \u3000\u3000\u6c5f\u67ab\u8038\u8038\u80a9\u9053\uff1a\u201c\u4fe1\u4e0d\u4fe1\u5728\u4e8e\u5979\u4eec\u81ea\u5df1\uff0c\u4f46\u4e8b\u5b9e\u4e0a\u662f\u5979\u8981\u6211\u6765\u7684\u3002\u201d \u3000\u3000\u541f\u96ea\u534a\u4fe1\u534a\u7591\u5730\u95ee\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u7b11\u4e86\u7b11\u9053\uff1a\u201c\u5979\u770b\u6211\u7684\u4f24\u4e86\u80cc\u90e8\uff0c\u7761\u561b\u4e0d\u80fd\u7761\uff0c\u4f0f\u7740\u53c8\u4e0d\u8212\u670d\uff0c\u800c\u4e14\u5750\u7acb\u4e0d\u5b89\uff0c\u5c31\u8981\u6211\u6765\u627e\u4f60\u4eec\u804a\u804a\u3002\u5982\u679c\u6211\u4e0d\u53d7\u6b22\u8fce\uff0c\u90a3\u53ea\u597d\u2026\u2026\u201d \u3000\u3000\u541f\u971c\u63a5\u9053\uff1a\u201c\u5f20\u59e8\u81ea\u5df1\u4e0d\u80fd\u966a\u4f60\u804a\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u8f7b\u559f\u4e00\u58f0\uff0c\u8868\u60c5\u903c\u771f\u9053\uff1a\u201c\u5f3a\u654c\u5f53\u5934\uff0c\u5979\u6b63\u5728\u82e6\u601d\u5bf9\u7b56\uff0c\u54ea\u6709\u8fd9\u4efd\u5fc3\u60c5\u3002\u201d \u3000\u3000\u541f\u971c\u9053\uff1a\u201c\u6709\u4ec0\u4e48\u597d\u803d\u5fc3\u7684\uff0c\u4ed6\u4eec\u53ea\u8981\u4e0d\u4f2f\u6b7b\u5c31\u5c3d\u7ba1\u6765\uff0c\u6765\u4e00\u4e2a\u6740\u4e00\u4e2a\uff0c\u6765\u4e24\u4e2a\u6740\u4e00\u53cc\uff01\u201d \u3000\u3000\u201c\u54fc\uff01\u201d\u541f\u96ea\u6124\u58f0\u9053\uff1a\u201c\u4eca\u591c\u8981\u4e0d\u662f\u5f20\u59e8\u963b\u6b62\uff0c\u6211\u4eec\u4e0d\u6740\u4ed6\u4e00\u767e\uff0c\u4e5f\u6740\u4ed6\u4e2a\u516b\u5341\uff01\u201d \u3000\u3000\u6c5f\u67ab\u4e0d\u4ee5\u4e3a\u7136\u9053\uff1a\u201c\u6740\u4ed6\u4e00\u767e\u53c8\u6709\u4ec0\u4e48\u7528\uff1f\u6211\u4eec\u8981\u9664\u7684\u53ef\u662f\u90a3\u795e\u79d8\u6559\u4e3b\u54e6\uff01\u201d \u3000\u3000\u541f\u96ea\u6ee1\u8138\u6740\u673a\u9053\uff1a\u201c\u53ea\u8981\u628a\u4ed6\u7684\u624b\u4e0b\u8d76\u5c3d\u6740\u7edd\uff0c\u770b\u4ed6\u8fd9\u4e2a\u72ec\u811a\u620f\u600e\u4e48\u5531\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u6b63\u8272\u9053\uff1a\u201c\u5f53\u5e74\u4e00\u5f00\u59cb\uff0c\u4ed6\u8fd8\u4e0d\u662f\u5531\u7684\u72ec\u811a\u620f\uff0c\u7ed3\u679c\u6697\u4e2d\u62db\u5175\u5356\u9a6c\uff0c\u52bf\u529b\u5c31\u50cf\u6eda\u96ea\u7403\u4f3c\u7684\uff0c\u6108\u6eda\u6108\u5927\u3002 \u3000\u3000\u8fd9\u6b21\u5982\u679c\u4e0d\u80fd\u628a\u4ed6\u4e00\u4e3e\u6b7c\u706d\uff0c\u4e07\u4e00\u88ab\u4ed6\u9003\u8d70\uff0c\u5c06\u6765\u53c8\u4f1a\u5377\u571f\u91cd\u6765\uff0c\u5174\u98ce\u4f5c\u6d6a\uff0c\u540e\u60a3\u65e0\u7a77\uff0c\u6c5f\u6e56\u5c31\u6c38\u65e0\u5b81\u65e5\u4e86\u3002\u201d \u3000\u3000\u201c\u8bdd\u662f\u4e0d\u9519\uff0c\u4f46\u73b0\u5728\u53d1\u6101\u53c8\u6709\u4ec0\u4e48\u7528\uff1f\u201d\u541f\u96ea\u9053\uff1a\u201c\u5982\u679c\u90a3\u795e\u79d8\u6559\u4e3b\u81ea\u77e5\u4e0d\u654c\uff0c\u8981\u9003\u65e9\u5df2\u9003\u4e86\uff0c\u6211\u4eec\u8ffd\u90fd\u6765\u4e0d\u53ca\u3002 \u3000\u3000\u53cd\u8fc7\u6765\u8bf4\uff0c\u82e5\u662f\u4ed6\u5fc3\u6709\u4e0d\u7518\uff0c\u52bf\u5fc5\u4eb2\u7387\u5927\u519b\u6765\u72af\uff0c\u90a3\u65f6\u6211\u4eec\u6b63\u597d\u6765\u4e2a\u8fce\u5934\u75db\u51fb\uff0c\u6740\u5f97\u4ed6\u7247\u7532\u4e0d\u7559\u3002\u6240\u4ee5\uff0c\u6839\u672c\u7528\u4e0d\u7740\u70e6\u5fc3\uff0c\u7b49\u7740\u4ed6\u4eec\u81ea\u6295\u7f57\u7f51\u5c31\u5bf9\u5566\u3002\u201d \u3000\u3000\u541f\u971c\u5e2e\u8154\u9053\uff1a\u201c\u5982\u679c\u4e0d\u51fa\u6240\u6599\uff0c\u4ed6\u4eec\u7edd\u4e0d\u4f1a\u5c31\u6b64\u7f62\u624b\uff0c\u968f\u65f6\u90fd\u53ef\u80fd\u5927\u4e3e\u6765\u72af\u3002\u201d \u3000\u3000\u6c5f\u67ab\u90d1\u91cd\u5176\u4e8b\u9053\uff1a\u201c\u5927\u59d0\u548c\u6211\u803d\u5fc3\u7684\u6b63\u662f\u8fd9\u4e2a\uff0c\u5c24\u5176\u803d\u5fc3\u4f60\u4eec\u4e24\u59d0\u59b9\uff01\u201d \u3000\u3000\u201c\u803d\u5fc3\u6211\u4eec\uff1f\u201d\u4e24\u59d0\u59b9\u4e0d\u7ea6\u800c\u540c\u5730\u95ee\u3002 \u3000\u3000\u6c5f\u67ab\u70b9\u70b9\u5934\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u4eec\u6240\u803d\u5fc3\u7684\uff0c\u5c31\u662f\u4f60\u4eec\u4e24\u4e2a\uff01\u201d \u3000\u3000\u53f6\u96ea\u54fc\u58f0\u9053\uff1a\u201c\u6211\u4eec\u6709\u4ec0\u4e48\u597d\u803d\u5fc3\u7684\uff0c\u662f\u6015\u6211\u4eec\u5927\u5f00\u6740\u6212\uff0c\u6740\u4eba\u592a\u591a\u5417\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u95ee\u9898\u5c31\u5728\u8fd9\u91cc\uff0c\u4f60\u4eec\u89c1\u4e86\u654c\u4eba\u6765\u72af\uff0c\u662f\u4e0d\u662f\u975e\u6740\u4e0d\u53ef\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u6740\uff01\u201d\u4e24\u59d0\u59b9\u9f50\u58f0\u53eb\u51fa\u3002 \u3000\u3000\u6c5f\u67ab\u53c8\u95ee\uff1a\u201c\u600e\u4e48\u4e2a\u6740\u6cd5\uff1f\u201d \u3000\u3000\u541f\u96ea\u4e0d\u52a0\u601d\u7d22\u9053\uff1a\u201c\u4e00\u5251\u4e00\u4e2a\uff0c\u5251\u5230\u547d\u6bd9\uff01\u201d \u3000\u3000\u6c5f\u67ab\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u90a3\u5c31\u7cdf\u4e86\uff01\u201d \u3000\u3000\u201c\u7cdf\u4ec0\u4e48\uff1f\u201d\u541f\u971c\u8ffd\u95ee\u3002 \u3000\u3000\u6c5f\u67ab\u4ece\u5bb9\u4e0d\u8feb\u9053\uff1a\u201c\u4eca\u591c\u7684\u60c5\u5f62\u4f60\u4eec\u90fd\u77e5\u9053\u4e86\uff0c\u4ed6\u4eec\u4e0d\u60dc\u4ee5\u90a3\u4f8f\u5112\u4e3a\u4e3b\uff0c\u5192\u5145\u90a3\u795e\u79d8\u6559\u4e3b\uff0c\u8bf1\u4f7f\u6211\u653b\u5411\u90a3\u9876\u5927\u8f7f\uff0c\u610f\u5728\u8ddf\u6211\u540c\u5f52\u4e8e\u5c3d\uff0c\u5c06\u6211\u70b8\u6b7b\u3002 \u3000\u3000\u53d7\u795e\u79d8\u6559\u4e3b\u63a7\u5236\uff0c\u50cf\u90a3\u4f8f\u5112\u4e00\u6837\u7684\u6b7b\u571f\u4e0d\u5728\u5c11\u6570\uff0c\u5982\u679c\u6bcf\u4eba\u8eab\u4e0a\u90fd\u7ed1\u4e86\u5f3a\u70c8\u70b8\u836f\u3002\u4f60\u4eec\u8981\u6740\u4ed6\u4eec\uff0c\u52bf\u5fc5\u8fd1\u8eab\u51fa\u5251\uff0c\u70b8\u836f\u4e00\u65e6\u5f15\u7206\uff0c\u7ed3\u679c\u4f1a\u81ea\u600e\u6837\u3002\u201d \u3000\u3000\u201c\u8fd9\u2026\u2026\u201d\u4e24\u59d0\u59b9\u679c\u7136\u6697\u81ea\u4e00\u60ca\uff0c\u76f8\u987e\u6115\u7136\u3002 \u3000\u3000\u6c5f\u67ab\u8d81\u673a\u9053\uff1a\u201c\u4ed6\u4eec\u6709\u7684\u662f\u4eba\uff0c\u5927\u4e0d\u4e86\u4e00\u4e2a\u62fc\u4e00\u4e2a\uff0c\u62fc\u5230\u6700\u540e\uff0c\u6211\u4eec\u4f24\u4ea1\u6b86\u5c3d\uff0c\u4ed6\u4eec\u5374\u8fd8\u4fdd\u5b58\u7740\u5e9e\u5927\u5b9e\u529b\uff0c\u751a\u81f3\u795e\u79d8\u6559\u4e3b\u7684\u9762\u90fd\u672a\u9732\u5462\uff01\u201d \u3000\u3000\u4e24\u59d0\u59b9\u54d1\u53e3\u65e0\u8a00\u4e86\u3002 \u3000\u3000\u6c5f\u67ab\u5ffd\u95ee\uff1a\u201c\u4f60\u4eec\u542c\u8fc7\u9a6d\u5251\u4e4b\u672f\u5417\uff1f\u201d \u3000\u3000\u53f6\u96ea\u8be7\u5f02\u9053\uff1a\u201c\u4f60\u95ee\u8fd9\u4e2a\u5e72\u561b\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u9a6d\u5251\u4e4b\u672f\uff0c\u53ef\u4ee5\u5251\u6c14\u53d6\u4eba\u6027\u547d\u4e8e\u4e24\u4e09\u4e08\u4e4b\u5916\uff0c\u7ec3\u81f3\u6700\u9ad8\u5883\u754c\uff0c\u5251\u6c14\u751a\u81f3\u53ef\u8fbe\u4e00\u4e08\uff0c\u4e0d\u987b\u4e0e\u5bf9\u624b\u77ed\u5175\u76f8\u63a5\u3002\u6240\u4ee5\u6211\u5728\u60f3\uff0c\u5982\u679c\u4f60\u4eec\u2026\u2026\u201d \u3000\u3000\u4e0d\u7b49\u4ed6\u8bf4\u5b8c\uff0c\u541f\u971c\u5df2\u5fcd\u4e0d\u4f4f\u55e4\u7b11\u9053\uff1a\u201c\u5f00\u73a9\u7b11\uff0c\u8981\u7ec3\u6210\u9a6d\u5251\u4e4b\u672f\u8c08\u4f55\u5bb9\u6613\uff0c\u82e6\u7ec3\u4e09\u4e94\u5341\u5e74\uff0c\u4e5f\u672a\u5fc5\u6709\u6240\u6210\u5c31\uff0c\u90a3\u65f6\u6211\u4eec\u5df2\u7ecf\u6210\u4e86\u767d\u53d1\u82cd\u82cd\u7684\u8001\u592a\u5a46\u5566\uff01\u201d \u3000\u3000\u201c\u90a3\u5012\u672a\u5fc5\u3002\u201d\u6c5f\u67ab\u7b11\u9053\uff1a\u201c\u6211\u6709\u4e00\u5957\u901f\u6210\u4e4b\u6cd5\uff01\u201d \u3000\u3000\u541f\u96ea\u6025\u95ee\uff1a\u201c\u771f\u7684\uff1f\u201d \u3000\u3000\u541f\u971c\u5374\u6447\u7740\u5934\u9053\uff1a\u201c\u4e0d\u53ef\u80fd\u7684\uff01\u5982\u679c\u771f\u6709\u4e00\u5957\u901f\u6210\u4e4b\u6cd5\uff0c\u4f60\u81ea\u5df1\u600e\u4e48\u4e0d\u7ec3\uff1f\u201d \u3000\u3000\u201c\u8bf4\u7684\u4e5f\u662f\u3002\u201d\u541f\u96ea\u82e6\u6709\u6240\u609f\u9053\uff1a\u201c\u90a3\u4f60\u65e9\u5c31\u5e94\u8be5\u7ec3\u6210\u4f20\u8bf4\u4e2d\u7684\u5251\u5ba2\u5566\u3002\u201d \u3000\u3000\u6c5f\u67ab\u65e9\u5df2\u6253\u597d\u8179\u7a3f\uff0c\u7b11\u9053\uff1a\u201c\u53ef\u60dc\u8fd9\u5957\u901f\u6210\u6cd5\uff0c\u5b9c\u5973\u4e0d\u5b9c\u7537\uff0c\u6211\u53ea\u80fd\u6068\u4e0d\u751f\u4e3a\u5973\u513f\u8eab\u4e86\u3002\u201d \u3000\u3000\u53f6\u96ea\u4fe1\u4ee5\u4e3a\u771f\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u65e9\u4e0d\u6559\u6211\u4eec\u7ec3\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u90d1\u91cd\u9053\uff1a\u201c\u90a3\u672c\u7ec3\u5251\u79d8\u5ea6\uff0c\u6211\u662f\u5728\u65e0\u610f\u4e2d\u5f97\u5230\u7684\uff0c\u5730\u70b9\u662f\u4e00\u5904\u4eba\u70df\u7edd\u8ff9\u7684\u6df1\u5c71\u5ca9\u6d1e\u3002\u6d1e\u5185\u6709\u5177\u9ab7\u9ac5\uff0c\u4ecd\u53ef\u8fa8\u8ba4\u51fa\u662f\u5973\u6027\uff0c\u79d8\u7b08\u5c31\u5728\u5979\u624b\u4e2d\u3002 \u3000\u3000\u9ab7\u9ac5\u5df2\u7ecf\u98ce\u5316\uff0c\u90a3\u5973\u5b50\u6b7b\u4e86\u81f3\u5c11\u767e\u5e74\u4ee5\u4e0a\u3002\u7b08\u4e0a\u6ca1\u6709\u7f72\u540d\uff0c\u65e0\u6cd5\u77e5\u9053\u5b83\u7684\u4e3b\u4eba\u662f\u8c01\uff0c\u4e0d\u8fc7\u5c01\u9762\u4e0a\u5199\u4e86\u2018\u6709\u7f18\u4eba\u5f97\u4e4b\u2019\u4e94\u4e2a\u5b57\u3002 \u3000\u3000\u5f53\u65f6\u6211\u559c\u51fa\u671b\u5916\uff0c\u8ba4\u4e3a\u90a3\u2018\u6709\u7f18\u4eba\u2019\u5c31\u662f\u6211\u4e86\uff0c\u53ef\u60dc\u6253\u5f00\u7b2c\u4e00\u9875\u4e00\u770b\uff0c\u5c31\u62e8\u4e86\u6211\u4e00\u5934\u51b7\u6c34\uff0c\u4f7f\u6211\u5927\u5931\u6240\u671b\uff0c\u539f\u6765\u4e0a\u9762\u6ce8\u660e\u2018\u6b64\u9a6d\u5251\u4e4b\u672f\u901f\u6210\u7bc7\uff0c\u5b9c\u5973\u4e0d\u5b9c\u7537\uff0c\u614e\u4e4b\uff01\u614e\u4e4b\uff01\u2019\u3002\u63a5\u7740\u540e\u9762\u53c8\u6709\u4e24\u884c\u5b57\uff0c\u5199\u7684\u662f\u2018\u6b64\u6cd5\u4e0d\u8c19\u5251\u672f\u8005\u52ff\u7ec3\uff0c\u5fc3\u672f\u4e0d\u6b63\u8005\u52ff\u7ec3\uff0c\u751f\u6027\u55dc\u6740\u8005\u52ff\u7ec3\uff0c\u7ec3\u5fc5\u8d70\u706b\u5165\u9b54\uff0c\u8f7b\u5219\u6210\u6b8b\uff0c\u91cd\u5219\u4e27\u547d\uff01\u2019\u3002 \u3000\u3000\u5f20\u56db\u59d1\u628a\u4f60\u4eec\u5f15\u89c1\u6211\u65f6\uff0c\u6211\u66fe\u4ed4\u7ec6\u89c2\u5bdf\u8fc7\u4f60\u4eec\uff0c\u8ba4\u4e3a\u4f60\u4eec\u662f\u975e\u5e38\u9002\u5408\u7ec3\u9a6d\u5251\u4e4b\u672f\u7684\u4eba\u9009\u3002\u53ef\u662f\uff0c\u540e\u6765\u6211\u53d1\u73b0\u4f60\u4eec\u7684\u6740\u6027\u592a\u91cd\u2026\u2026\u201d \u3000\u3000\u541f\u96ea\u8138\u8272\u4e00\u6c89\uff0c\u51b7\u58f0\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u6539\u53d8\u4e86\u521d\u8877\u3002\u201d \u3000\u3000\u6c5f\u67ab\u5fae\u5fae\u628a\u5934\u4e00\u70b9\uff0c\u6b63\u8272\u9053\uff1a\u201c\u662f\u7684\uff0c\u5982\u679c\u6211\u4f20\u4e86\u4f60\u4eec\u9a6d\u5251\u4e4b\u672f\uff0c\u6210\u4e8b\u4e0d\u8db3\uff0c\u53cd\u800c\u5bb3\u4e86\u4f60\u4eec\u3002\u201d \u3000\u3000\u541f\u971c\u8d28\u95ee\u9053\uff1a\u201c\u90a3\u4f60\u521a\u624d\u4f55\u5fc5\u63d0\u8d77\u9a6d\u5251\u4e4b\u672f\uff0c\u662f\u4e0d\u662f\u5b58\u5fc3\u540a\u6211\u4eec\u80c3\u53e3\uff1f\u201d \u3000\u3000\u201c\u4e0d\uff01\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u4f60\u4eec\u7684\u6740\u6027\u592a\u91cd\uff0c\u662f\u56e0\u7ec3\u4e86\u2018\u4f20\u771f\u8bb0\u2019\u6240\u8f7d\u6b66\u529f\u800c\u8d77\u3002\u6240\u4ee5\u6211\u5728\u60f3\uff0c\u5982\u679c\u4f60\u4eec\u80fd\u628a\u90a3\u4e9b\u6b6a\u95e8\u90aa\u9053\u7684\u529f\u592b\u629b\u5f03\uff0c\u6062\u590d\u7eaf\u771f\u672c\u6027\uff0c\u4ecd\u7136\u5927\u6709\u53ef\u4e3a\u3002\u201d \u3000\u3000\u541f\u96ea\u82e6\u7b11\u9053\uff1a\u201c\u6728\u5df2\u6210\u821f\uff0c\u80fd\u6539\u53d8\u5f97\u4e86\u65e2\u6210\u7684\u4e8b\u5b9e\u5417\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u89c1\u8ba1\u5df2\u6210\uff0c\u5fc3\u4e2d\u6697\u559c\uff0c\u65ad\u7136\u9053\uff1a\u201c\u80fd\uff01\u53ea\u8981\u4f60\u4eec\u4e0b\u5b9a\u51b3\u5fc3\uff0c\u6211\u53ef\u4ee5\u5e2e\u52a9\u4f60\u4eec\u3002\u201d \u3000\u3000\u201c\u54e6\uff1f\u201d\u541f\u971c\u970d\u7136\u5fc3\u52a8\uff0c\u6025\u95ee\uff1a\u201c\u4f60\u6253\u7b97\u600e\u6837\u5e2e\u52a9\u6211\u4eec\uff0c\u629b\u5f03\u90a3\u4e9b\u5728\u6211\u4eec\u5fc3\u4e2d\u5df2\u6839\u6df1\u8482\u56fa\u7684\u6b66\u529f\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u4e0d\u52a0\u601d\u7d22\u9053\uff1a\u201c\u7528\u6211\u7684\u2018\u611f\u5fc3\u672f\u2019\u4e00\u8bd5\u3002\u201d \u3000\u3000\u4e24\u59d0\u59b9\u5931\u58f0\u53eb\u9053\uff1a\u201c\u60d1\u5fc3\u672f\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u90d1\u91cd\u5176\u4e8b\u9053\uff1a\u201c\u6c34\u80fd\u8f7d\u821f\uff0c\u4ea6\u80fd\u8986\u821f\u3002\u4e94\u6bd2\u8db3\u4ee5\u81f4\u547d\uff0c\u540c\u6837\u4e5f\u53ef\u7528\u6765\u914d\u836f\u6cbb\u75c5\u3002\u6211\u51b3\u5b9a\u4ee5\u2018\u60d1\u5fc3\u672f\u2019\u4e00\u8bd5\uff0c\u7b49\u4e8e\u662f\u4ee5\u6bd2\u653b\u6bd2\u54e6\u3002\u201d \u3000\u3000\u4e24\u59d0\u59b9\u4ea4\u6362\u4e00\u4e0b\u773c\u8272\uff0c\u5f53\u673a\u7acb\u65ad\uff0c\u9f50\u58f0\u9053\uff1a\u201c\u90a3\u5c31\u8bd5\u8bd5\u5427\uff01\u201d \u3000 \u7b2c\u4e8c\u5341\u4e5d\u56de\u3000\u53e4\u5e99\u65c1\u7a81\u964d\u9752\u677e\u6797 \u3000 \u3000\u3000\u6700\u8fd1\u8fd9\u4e9b\u65f6\u65e5\u6765\uff0c\u541f\u96ea\u3001\u541f\u971c\u4e24\u59d0\u59b9\u81ea\u4ece\u7ec3\u4e86\u201c\u4f20\u771f\u8bb0\u201d\u4e0a\u6240\u8f7d\u6b66\u529f\uff0c\u6027\u60c5\u5927\u53d8\uff0c\u6740\u6027\u6108\u6765\u6108\u91cd\uff0c\u4e0e\u8fc7\u53bb\u76f8\u6bd4\uff0c\u7b80\u76f4\u5224\u82e5\u4e24\u4eba\u3002 \u3000\u3000\u5982\u6b64\uff0c\u4f7f\u5f97\u5927\u5bb6\u90fd\u5728\u5fc3\u4e2d\u4e3a\u5979\u4eec\u803d\u5fc3\uff0c\u552f\u6050\u4e24\u59d0\u59b9\u6108\u9677\u6108\u6df1\uff0c\u96be\u4ee5\u81ea\u62d4\uff0c\u7ec8\u5fc5\u53d8\u6210\u4e24\u4e2a\u6740\u4eba\u5973\u9b54\u3002 \u3000\u3000\u5f20\u56db\u59d1\u751a\u81f3\u5df2\u79c1\u4e0b\u5f81\u5f97\u6b27\u9633\u662d\u592b\u5987\u540c\u610f\uff0c\u4e07\u4e0d\u5f97\u5df2\u65f6\uff0c\u53ea\u6709\u5fcd\u75db\u6740\u4e86\u8fd9\u5bf9\u59d0\u59b9\uff0c\u522b\u65e0\u9009\u62e9\u4f59\u5730\u3002 \u3000\u3000\u4ed6\u4eec\u7684\u51b3\u5b9a\uff0c\u867d\u672a\u5411\u6c5f\u67ab\u660e\u8bf4\uff0c\u4f46\u6c5f\u67ab\u5df2\u9690\u7ea6\u5bdf\u89c9\u51fa\u8fd9\u79cd\u53ef\u6015\u7684\u8baf\u606f\u3002\u4e5f\u8bb8\uff0c\u8fd9\u662f\u4e00\u79cd\u611f\u5e94\u6216\u76f4\u89c9\u5427\u3002 \u3000\u3000\u5b9e\u9645\u4e0a\uff0c\u5230\u4e86\u5fc5\u8981\u7684\u60c5\u51b5\u4e0b\uff0c\u4ed6\u81ea\u5df1\u4e5f\u4f1a\u88ab\u8feb\u4f5c\u51fa\u8fd9\u4e2a\u75db\u82e6\u7684\u51b3\u5b9a\uff0c\u66f4\u53ef\u80fd\u51fa\u624b\u7684\u5c31\u662f\u4ed6\uff01 \u3000\u3000\u56e0\u4e3a\uff0c\u9664\u4e86\u4ed6\u4e4b\u5916\uff0c\u522b\u4eba\u6839\u672c\u52a8\u4e0d\u4e86\u8fd9\u65f6\u59d0\u59b9\u3002 \u3000\u3000\u6c5f\u67ab\u81ea\u7136\u4e0d\u613f\u771f\u6709\u8fd9\u4e00\u5929\uff0c\u6240\u4ee5\u4ed6\u6781\u529b\u60f3\u633d\u6551\u8fd9\u5bf9\u59d0\u59b9\uff0c\u4f7f\u5979\u4eec\u60ac\u5d16\u52d2\u9a6c\uff0c\u4e0d\u81f4\u5760\u5165\u4e07\u52ab\u4e0d\u590d\u7684\u7f6a\u6076\u6df1\u6e0a\u3002 \u3000\u3000\u600e\u6837\u62ef\u6551\u5462\uff1f \u3000\u3000\u552f\u4e00\u7684\u65b9\u6cd5\uff0c\u4fbf\u662f\u4fc3\u4f7f\u5979\u4eec\u653e\u5f03\u201c\u4f20\u771f\u8bb0\u201d\u4e0a\u6240\u5b66\uff0c\u90a3\u4e9b\u6b6a\u95e8\u90aa\u9053\u7684\u6b66\u529f\u3002 \u3000\u3000\u8fd9\u4e8b\u8bf4\u6765\u8f7b\u677e\uff0c\u771f\u8981\u505a\u5230\u8c08\u4f55\u5bb9\u6613\u3002\u56e0\u4e3a\u4e00\u65e6\u7ec3\u6210\u201c\u4f20\u771f\u8bb0\u201d\u4e0a\u6240\u8f7d\u6b66\u529f\uff0c\u4fbf\u5df2\u6839\u6df1\u8482\u56fa\u5b58\u5728\u5fc3\u4e2d\uff0c\u5982\u540c\u5438\u6bd2\u4e0a\u763e\uff0c\u5f88\u96be\u628a\u5b83\u6212\u6389\uff0c\u6bd2\u7624\u53ea\u4f1a\u6108\u6765\u6108\u5927\uff0c\u6108\u9677\u6108\u6df1\u3002 \u3000\u3000\u5c24\u5176\u662f\uff0c\u6c5f\u67ab\u4e0d\u80fd\u76f4\u622a\u4e86\u5f53\u660e\u8bf4\uff0c\u975e\u5f97\u62d0\u5f2f\u62b9\u89d2\uff0c\u7ed5\u7740\u5708\u5b50\u8bf1\u4f7f\u5979\u4eec\u81ea\u613f\u653e\u5f03\u4e0d\u53ef\u3002 \u3000\u3000\u800c\u4e14\uff0c\u5fc5\u987b\u627e\u5230\u6700\u9002\u5f53\u7684\u673a\u4f1a\u3002 \u3000\u3000\u60f3\u4e0d\u5230\u521a\u624d\u4e34\u65f6\u7f16\u51fa\u7684\u4e00\u756a\u8bf4\u8bcd\uff0c\u7adf\u4f7f\u96ea\u3001\u971c\u4e24\u59d0\u59b9\u4fe1\u4ee5\u4e3a\u771f\u3002 \u3000\u3000\u5176\u5b9e\u5462\uff0c\u8bf4\u7a7f\u4e86\u8fd8\u662f\u4e00\u4e2a\u201c\u8d2a\u201d\u5b57\uff01 \u3000\u3000\u51e1\u662f\u7ec3\u6b66\u7684\u4eba\uff0c\u5c24\u5176\u662f\u7cbe\u4e8e\u5251\u672f\u7684\u4eba\uff0c\u8c01\u80fd\u5bf9\u201c\u9a6d\u5251\u4e4b\u672f\u201d\u65e0\u52a8\u4e8e\u8877\uff1f \u3000\u3000\u6bd5\u7adf\uff0c\u5b83\u7684\u8bf1\u60d1\u529b\u5b9e\u5728\u592a\u5927\u4e86\u554a\uff01 \u3000\u3000\u6c5f\u67ab\u5fc3\u4e2d\u6697\u559c\uff0c\u51fa\u623f\u53bb\u4ea4\u5f85\u4e86\u5f20\u56db\u59d1\u51e0\u53e5\u540e\uff0c\u4fbf\u5306\u5306\u56de\u5230\u4e86\u5ba2\u623f\u3002 \u3000\u3000\u4ed6\u5df2\u4e0d\u7528\u803d\u5fc3\uff0c\u5fc3\u77e5\u5f20\u56db\u59d1\u5fc5\u4f1a\u4eb2\u81ea\u5728\u623f\u5916\u62a4\u6cd5\uff0c\u4e25\u7981\u4efb\u4f55\u4eba\u95ef\u5165\u3002 \u3000\u3000\u4f46\u4ed6\u803d\u5fc3\u4e00\u70b9\uff0c\u90a3\u5c31\u662f\u96ea\u3001\u971c\u4e24\u59d0\u59b9\u5df2\u5f97\u5929\u72d0\u592b\u4eba\u201c\u60d1\u5fc3\u672f\u201d\u7684\u771f\u4f20\uff0c\u4e07\u4e00\u4e0d\u77e5\u4e0d\u89c9\u65bd\u5c55\u51fa\u6765\uff0c\u5bf9\u6297\u6c5f\u67ab\u7684\u201c\u60d1\u5fc3\u672f\u201d\uff0c\u7a0d\u4e00\u4e0d\u614e\uff0c\u4fbf\u53ef\u80fd\u9020\u6210\u4e0d\u53ef\u6536\u62fe\uff0c\u751a\u81f3\u4e24\u8d25\u4ff1\u4f24\u7684\u5c40\u9762\u3002 \u3000\u3000\u5f53\u4ed6\u4eec\u4ee5\u201c\u54c1\u201d\u5b57\u5f62\uff0c\u9762\u5bf9\u9762\u5e2d\u5730\u5750\u4e0b\u540e\uff0c\u6c5f\u67ab\u4e0d\u5f97\u4e0d\u63d0\u51fa\u8b66\u544a\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u5fc3\u60c5\u8981\u653e\u677e\uff0c\u5343\u4e07\u4e0d\u53ef\u7528\u2018\u60d1\u672f\u672f\u2019\u4e2d\u6765\u5bf9\u6297\u54e6\uff1f\u201d \u3000\u3000\u96ea\u3001\u971c\u4e24\u59d0\u59b9\u5fae\u5fae\u70b9\u5934\u4e00\u7b11\uff0c\u5a07\u5a9a\u4e4b\u6001\u6bd5\u9732\u3002 \u3000\u3000\u6c5f\u67ab\u4e0d\u7531\u5730\u5fc3\u795e\u4e00\u9707\uff0c\u60ca\u5fd6\u9053\uff1a\u201c\u5979\u4eec\u2018\u60d1\u5fc3\u672f\u2019\u7684\u529f\u529b\uff0c\u6050\u6015\u5df2\u4e0d\u5728\u7f57\u5170\u4e4b\u4e0b\uff0c\u4e07\u4e00\u6211\u5236\u4e0d\u4f4f\u5979\u4eec\uff0c\u53cd\u800c\u5f04\u5de7\u6210\u62d9\uff0c\u90a3\u53ef\u5c31\u7cdf\u4e86\uff01\u201d \u3000\u3000\u53ef\u662f\uff0c\u6b64\u523b\u5df2\u662f\u9a91\u864e\u96be\u4e0b\uff0c\u7bad\u5728\u5f26\u4e0a\uff0c\u4e0d\u5f97\u4e0d\u53d1\u4e86\u3002 \u3000\u3000\u65e0\u53ef\u5948\u4f55\uff0c\u6c5f\u67ab\u53ea\u5f97\u65bd\u5c55\u51fa\u201c\u60d1\u5fc3\u672f\u201d\u6765\u3002 \u3000\u3000\u4e09\u4eba\u51dd\u795e\u5c4f\u606f\uff0c\u76ee\u5149\u6d41\u8f6c\u2026\u2026 \u3000\u3000\u201c\u6212\u5fc3\u672f\u201d\u7684\u81f3\u9ad8\u5883\u754c\uff0c\u4e0d\u662f\u6414\u9996\u5f04\u59ff\uff0c\u5356\u5f04\u98ce\u60c5\uff0c\u6216\u662f\u6324\u7709\u5f04\u773c\uff0c\u5c3d\u60c5\u6311\u9017\u5bf9\u65b9\u3002\u800c\u4e14\u85c9\u6f5c\u5728\u7684\u9b45\u529b\uff0c\u914d\u5408\u6df1\u539a\u529f\u529b\u81ea\u7136\u6563\u53d1\u51fa\u6765\u3002 \u3000\u3000\u6c5f\u67ab\u5f97\u5929\u72ec\u539a\u3001\u5177\u6709\u6f47\u6d12\u7684\u4eea\u8868\uff0c\u8db3\u4ee5\u4ee4\u4efb\u4f55\u6000\u6625\u7684\u4e3a\u4e4b\u52a8\u5fc3\u3002\u66f4\u4f55\u51b5\uff0c\u4ed6\u4e00\u65e6\u65bd\u5c55\u51fa\u201c\u60d1\u5fc3\u672f\u201d\uff0c\u8c01\u80fd\u6297\u62d2\u8fd9\u79cd\u5929\u72d0\u5947\u672f\u7684\u9b45\u529b\uff1f \u3000\u3000\u8fde\u9752\u51e4\uff0c\u84dd\u51e4\u90fd\u65e0\u6cd5\u6297\u62d2\uff0c\u4e00\u822c\u5973\u5b50\u5c31\u66f4\u65e0\u6cd5\u81ea\u5236\u4e86\u3002 \u3000\u3000\u4f46\u96ea\u3001\u971c\u4e24\u59d0\u59b9\u4e0d\u662f\u4e00\u822c\u5973\u5b50\u3002 \u3000\u3000\u5c24\u5176\u5979\u4eec\u5df2\u5c3d\u5f97\u201c\u60d1\u5fc3\u672f\u201d\u771f\u4f20\uff0c\u518d\u52a0\u4e0a\u4e24\u5973\u4e3d\u8d28\u5929\u751f\uff0c\u914d\u5408\u8fc7\u4eba\u7684\u9886\u609f\u529b\uff0c\u66f4\u80fd\u5c06\u5929\u72d0\u5947\u672f\u7684\u9b45\u529b\u53d1\u6325\u5f97\u6dcb\u6f13\u5c3d\u81f4\u3002 \u3000\u3000\u9762\u5bf9\u8fd9\u6837\u7684\u201c\u5f3a\u654c\u201d\uff0c\u6c5f\u67ab\u54ea\u6562\u6389\u4ee5\u8f7b\u5fc3\u3002 \u3000\u3000\u5e78\u800c\u4e24\u59d0\u59b9\u4e00\u5fc3\u60f3\u7ec3\u201c\u9a6d\u5251\u4e4b\u672f\u201d\uff0c\u81ea\u7518\u53d7\u201c\u60d1\u201d\uff0c\u4e0d\u6562\u4ee5\u201c\u60d1\u5fc3\u672f\u201d\u5bf9\u6297\uff0c\u4ee5\u4fbf\u5c06\u201c\u4f20\u771f\u8bb0\u201d\u4e0a\u6240\u5b66\u90aa\u95e8\u5de5\u592b\u8fde\u6839\u62d4\u9664\u3002 \u3000\u3000\u5426\u5219\uff0c\u6c5f\u67ab\u5c31\u66f4\u5403\u529b\u4e86\u3002 \u3000\u3000\u8fd9\u65f6\u6c5f\u67ab\u5df2\u5c06\u201c\u60d1\u5fc3\u672f\u201d\uff0c\u9010\u6e10\u589e\u5f3a\u5a01\u529b\uff0c\u85c9\u773c\u6ce2\u4f20\u5411\u4e24\u59d0\u59b9\uff0c\u4f7f\u5979\u4eec\u611f\u53d7\u5230\u4e86\u4e00\u6ce2\u6ce2\u5f3a\u5927\u78c1\u529b\u88ad\u6765\uff0c\u5f15\u8d77\u5185\u5fc3\u6df1\u5904\u4e00\u9635\u9635\u9707\u64bc\u3002 \u3000\u3000\u541f\u96ea\u6709\u4e9b\u628a\u6301\u4e0d\u4f4f\u4e86\uff0c\u8138\u988a\u4e0a\u6cdb\u8d77\u7ea2\u6655\uff0c\u770b\u6765\u76ca\u589e\u59a9\u5a9a\u5a07\u8273\u3002 \u3000\u3000\u53ea\u89c1\u5979\u5996\u8eaf\u5f00\u59cb\u8f6c\u52a8\uff0c\u540c\u65f6\u53e3\u4e2d\u4e0d\u81ea\u89c9\u5730\u53d1\u51fa\u8f7b\u5fae\u547b\u541f\u3002 \u3000\u3000\u518d\u770b\u541f\u971c\uff0c\u7adf\u60c5\u4e0d\u81ea\u7981\u5730\uff0c\u4ee5\u53cc\u624b\u5728\u81ea\u5df1\u80f8\u524d\u8f7b\u629a\u7740\uff0c\u4eff\u4f5b\u662f\u5728\u85c9\u6b64\u804a\u4ee5\u81ea\u6170\u3002 \u3000\u3000\u6c5f\u67ab\u770b\u5728\u773c\u91cc\uff0c\u5fc3\u5982\u201c\u60d1\u5fc3\u672f\u201d\u5df2\u594f\u6548\uff0c\u63a7\u5236\u4e86\u8fd9\u5bf9\u59d0\u59b9\u7684\u5fc3\u5fd7\uff0c\u8d81\u673a\u6d12\u7136\u4e00\u7b11\u9053\uff1a \u3000\u3000\u201c\u6765\u5427\uff01\u201d \u3000\u3000\u4e24\u59d0\u59b9\u65e9\u5df2\u201c\u8822\u8822\u6b32\u52a8\u201d\uff0c\u53ea\u56e0\u4e0d\u77e5\u6c5f\u67ab\u6253\u7b97\u5982\u4f55\u4e3a\u5979\u4eec\u89e3\u9664\u5fc3\u4e2d\u9b54\u969c\uff0c\u552f\u6050\u8bef\u72af\u7981\u5fcc\uff0c\u5168\u529f\u5c3d\u5f03\uff0c\u6240\u4ee5\u4e0d\u6562\u8f7b\u4e3e\u5984\u52a8\u3002 \u3000\u3000\u73b0\u5728\u542c\u6c5f\u67ab\u4e00\u58f0\u201c\u6765\u5427\u201d\uff0c\u987f\u65f6\u60c5\u6b32\u5954\u653e\uff0c\u8feb\u4e0d\u53ca\u5f85\u5730\uff0c\u5411\u4ed6\u6251\u53bb\u3002 \u3000\u3000\u6c5f\u67ab\u9669\u4e9b\u88ab\u5979\u4eec\u6251\u5012\uff0c\u6025\u5c06\u53cc\u81c2\u4e00\u5f20\uff0c\u628a\u4e24\u59d0\u59b9\u62e5\u5165\u6000\u91cc\u3002 \u3000\u3000\u96ea\u3001\u971c\u4e24\u59d0\u59b9\u6625\u5fc3\u8361\u6f3e\uff0c\u6b32\u706b\u4e0a\u5347\uff0c\u5168\u5411\u6c5f\u67ab\u9001\u4e0a\u70ed\u60c5\u5982\u706b\u7684\u72c2\u543b\u3002 \u3000\u3000\u6c5f\u67ab\u53ea\u6709\u4e00\u5f20\u5634\uff0c\u65e0\u6cd5\u517c\u987e\u4e24\u4e2a\u5c11\u5973\u7684\u70ed\u543b\uff0c\u53c8\u4e0d\u80fd\u539a\u6b64\u8584\u5f7c\uff0c\u53ea\u5f97\u4e24\u8fb9\u5e94\u4ed8\uff0c\u543b\u4e86\u8fd9\u4e2a\u53c8\u543b\u90a3\u4e2a\uff0c\u6765\u4e2a\u96e8\u9732\u5747\u6cbe\uff0c\u5e73\u5206\u79cb\u8272\u3002 \u3000\u3000\u8fd9\u4e00\u6765\uff0c\u4e24\u59d0\u59b9\u7adf\u4e92\u4e0d\u76f8\u8ba9\uff0c\u5c45\u7136\u4e89\u98ce\u5403\u918b\u8d77\u6765\u3002 \u3000\u3000\u541f\u96ea\u731b\u5c06\u541f\u971c\u63a8\u5f00\uff0c\u559d\u65a5\u9053\uff1a\u201c\u4f60\u4e0d\u80fd\u7b49\u4e00\u4e0b\u5417\uff1f\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u4f60\u4e0d\u80fd\u7b49\uff1f\u201d \u3000\u3000\u541f\u971c\u4e5f\u4e0d\u7518\u793a\u5f31\u3002 \u3000\u3000\u6c5f\u67ab\u6697\u81ea\u4e00\u60ca\uff0c\u60f3\u4e0d\u5230\u4e2d\u6bd2\u5df2\u6df1\uff0c\u6027\u60c5\u5927\u53d8\uff0c\u4e0e\u8fc7\u53bb\u76f8\u6bd4\uff0c\u7b80\u76f4\u5224\u82e5\u4e24\u4eba\u3002 \u3000\u3000\u8fc7\u53bb\u5979\u4eec\u59d0\u59b9\u60c5\u6df1\uff0c\u5f7c\u6b64\u4e92\u656c\u4e92\u7231\uff0c\u5982\u4eca\u4e3a\u4e86\u4e89\u732e\u70ed\u543b\uff0c\u7adf\u7136\u8bf4\u7ffb\u8138\u5c31\u7ffb\u8138\u3002 \u3000\u3000\u5176\u5b9e\uff0c\u8fd9\u5df2\u4e0d\u8db3\u4e3a\u5947\uff0c\u6700\u8fd1\u5979\u4eec\u6027\u60c5\u53d8\u5f97\u6781\u7aef\u4e56\u623e\uff0c\u52a8\u8f84\u53d1\u706b\uff0c\u8fde\u7236\u6bcd\u90fd\u65f6\u65f6\u8d77\u51b2\u7a81\uff0c\u4f55\u51b5\u662f\u59d0\u59b9\u3002 \u3000\u3000\u5c24\u5176\u662f\u4e3a\u4e86\u4e89\u6c5f\u67ab\uff01 \u3000\u3000\u6c5f\u67ab\u552f\u6050\u5f04\u5de7\u6210\u62d9\uff0c\u5f15\u8d77\u5979\u4eec\u4e24\u59d0\u59b9\u53cd\u76ee\uff0c\u6025\u8feb\uff1a\u201c\u5582\u5582\u5582\uff0c\u4f60\u4eec\u4e24\u4e2a\u5c0f\u72d0\u5973\u4e89\u4ec0\u4e48\uff0c\u662f\u4e0d\u662f\u60f3\u628a\u6211\u751f\u541e\u6d3b\u5403\u4e86\u5440\uff1f\u201d \u3000\u3000\u541f\u96ea\u77aa\u4e86\u541f\u971c\u4e00\u773c\uff0c\u9053\uff1a\u201c\u6211\u5012\u60c5\u613f\u88ab\u4f60\u751f\u541e\u3002\u201d \u3000\u3000\u541f\u971c\u4e5f\u56de\u77aa\u541f\u96ea\u4e00\u773c\uff0c\u7528\u540c\u6837\u8bed\u6c14\u9053\uff1a\u201c\u6211\u613f\u8ba9\u4f60\u6d3b\u5403\uff01\u201d \u3000\u3000\u6c5f\u67ab\u54ea\u4f1a\u542c\u4e0d\u51fa\uff0c\u5979\u4eec\u6240\u8bf4\u7684\u201c\u751f\u541e\u201d\u201c\u6d3b\u5403\u201d\u662f\u6307\u4ec0\u4e48\u3002 \u3000\u3000\u5f3a\u81ea\u4e00\u7b11\uff0c\u6545\u610f\u95ee\uff1a\u201c\u4f60\u4eec\u8981\u6211\u600e\u6837\u541e\uff0c\u600e\u6837\u5403\uff1f\u201d \u3000\u3000\u541f\u96ea\u98ce\u60c5\u4e07\u79cd\u5730\u7b11\u95ee\uff1a\u201c\u4f60\u6562\u5417\uff1f\u201d \u3000\u3000\u8fd9\u8bdd\u5145\u6ee1\u6311\u9017\u610f\u5473\uff0c\u4e5f\u95ee\u7684\u975e\u5e38\u9732\u9aa8\u3002 \u3000\u3000\u539f\u56e0\u662f\u8fd9\u4e9b\u65f6\u65e5\u76f8\u5904\uff0c\u4ed6\u4eec\u4e09\u4eba\u5728\u4e00\u8d77\u65f6\uff0c\u65e0\u8bba\u600e\u6837\u7ea0\u7f20\u80e1\u95f9\uff0c\u59cb\u7ec8\u672a\u66fe\u771f\u4e2a\u9500\u9b42\uff0c\u7a81\u7834\u6700\u540e\u9632\u7ebf\u3002 \u3000\u3000\u4e24\u59d0\u59b9\u65e9\u6709\u732e\u8eab\u5fc3\u610f\uff0c\u504f\u504f\u6c5f\u67ab\u6bcf\u6b21\u90e8\u80fd\u201c\u4e34\u5371\u4e0d\u4e71\u201d\uff0c\u53ca\u65f6\u60ac\u5d16\u52d2\u9a6c\u3002\u6240\u4ee5\uff0c\u541f\u96ea\u624d\u4f1a\u5192\u51fa\u201c\u4f60\u6562\u5417\u201d\u8fd9\u4e00\u95f4\u3002 \u3000\u3000\u5176\u5b9e\u4ed6\u4eec\u65e9\u5df2\u77e5\u9053\uff0c\u6c5f\u67ab\u5e76\u975e\u4e0d\u89e3\u98ce\u60c5\uff0c\u987d\u51a5\u4e0d\u5316\uff0c\u4ed6\u4e0e\u5f20\u56db\u59d1\uff0c\u6885\u82b1\uff0c\u9752\u51e4\u84dd\u51e4\u90fd\u66fe\u4e0a\u8fc7\u5e8a\uff0c\u6709\u8fc7\u808c\u80a4\u4e4b\u4eb2\uff0c\u4e3a\u4ec0\u4e48\u5bf9\u5979\u4eec\u201c\u4e0d\u5c51\u4e00\u987e\u201d\u3002 \u3000\u3000\u8fd9\u4f7f\u5979\u4eec\u5f88\u4e0d\u80fd\u63a5\u53d7\uff0c\u4e5f\u96be\u4ee5\u7406\u89e3\u7684\u3002 \u3000\u3000\u867d\u7136\u6c5f\u67ab\u4e5f\u66fe\u89e3\u91ca\uff0c\u4e3a\u4e86\u8981\u8ba9\u6b27\u9633\u662d\u592b\u5987\u5bf9\u4ed6\u53e6\u773c\u76f8\u770b\uff0c\u4e0d\u5f97\u4e0d\u6781\u529b\u514b\u5236\u81ea\u5df1\u7684\u60c5\u6b32\uff0c\u4ee5\u4fdd\u6301\u4e24\u59d0\u59b9\u7684\u5b8c\u58c1\u3002 \u3000\u3000\u4f46\u662f\uff0c\u4e24\u59d0\u59b9\u5bf9\u8fd9\u4e2a\u89e3\u91ca\u5e76\u4e0d\u6ee1\u610f\uff0c\u751a\u81f3\u6000\u7591\uff01 \u3000\u3000\u6c5f\u67ab\u4e0d\u6562\u76f4\u63a5\u56de\u7b54\u541f\u96ea\u7684\u95ee\u9898\uff0c\u7b11\u4e86\u7b11\uff0c\u53cd\u95f4\u4e00\u53e5\uff1a\u201c\u4f60\u8ba4\u4e3a\u5462\uff1f\u201d \u3000\u3000\u541f\u96ea\u4e5f\u7b11\u800c\u4e0d\u7b54\uff0c\u970d\u5730\u7ad9\u4e86\u8d77\u6765\uff0c\u9762\u5bf9\u7740\u6c5f\u67ab\uff0c\u6beb\u65e0\u987e\u5fcc\u5730\u52a8\u624b\u5bbd\u8863\u89e3\u5e26\uff0c\u5c06\u5168\u8eab\u8863\u886b\u5c3d\u892a\uff0c\u8131\u4e86\u4e2a\u7cbe\u5149\u3002 \u3000\u3000\u6c5f\u67ab\u770b\u5728\u773c\u91cc\uff0c\u4e0d\u7981\u8d5e\u9053\uff1a\u201c\u7f8e\uff01\u597d\u7f8e\uff01\u201d \u3000\u3000\u541f\u971c\u4e00\u542c\uff0c\u5979\u54ea\u7518\u793a\u5f31\uff0c\u4e5f\u5fd9\u4e0d\u8fed\u8d77\u8eab\u8131\u4e86\u4e2a\u7cbe\u5149\u8d64\u68f5\uff0c\u95ee\u9053\uff1a\u201c\u6211\u5462\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u4e0d\u80fd\u539a\u6b64\u8584\u5f7c\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u4e5f\u4e00\u6837\u7f8e\uff01\u201d \u3000\u3000\u4e24\u59d0\u59b9\u6ee1\u610f\u5730\u76f8\u89c6\u4e00\u7b11\u3002 \u3000\u3000\u201c\u6211\u8981\u4f60\u751f\u541e\uff01\u201d \u3000\u3000\u201c\u6211\u8ba9\u4f60\u6d3b\u5403\uff01\u201d \u3000\u3000\u4e24\u59d0\u59b9\u51e0\u4e4e\u540c\u65f6\u53d1\u52a8\uff0c\u6251\u5411\u4e86\u76d8\u5750\u5730\u4e0a\u7684\u6c5f\u67ab\u3002 \u3000\u3000\u6c5f\u67ab\u88ab\u6251\u5012\uff0c\u4e0a\u8eab\u5411\u540e\u4e00\u4ef0\uff0c\u8eba\u5728\u4e86\u5730\u4e0a\u3002 \u3000\u3000\u4e24\u59d0\u59b9\u53c8\u4e92\u4e0d\u76f8\u8ba9\uff0c\u4e89\u5411\u6c5f\u67ab\u8eab\u4e0a\u6251\u53bb\u3002 \u3000\u3000\u8fd9\u56de\u541f\u971c\u62a2\u4e86\u4e2a\u5148\uff0c\u6251\u4f4f\u6c5f\u67ab\u5c31\u51d1\u4e0a\u9999\u5507\uff0c\u6765\u4e86\u4e2a\u706b\u8fa3\u8fa3\u7684\u70ed\u543b\u3002 \u3000\u3000\u541f\u96ea\u843d\u4e86\u7a7a\uff0c\u63a8\u4e0d\u5f00\u7d27\u7d27\u62b1\u4f4f\u6c5f\u67ab\u7684\u541f\u971c\uff0c\u53ea\u597d\u8f6c\u79fb\u76ee\u6807\uff0c\u53e6\u8f9f\u6218\u573a\uff0c\u6539\u5411\u6c5f\u67ab\u7684\u8eab\u4f53\u8fdb\u653b\u3002 \u3000\u3000\u6c5f\u67ab\u5f3a\u5fcd\u4f4f\u7684\u4f24\u7684\u75db\u80cc\uff0c\u4efb\u7531\u5979\u4eec\u4e3a\u6240\u6b32\u4e3a\u3002 \u3000\u3000\u4ed6\u8eab\u4e0a\u4ec5\u7a7f\u4e00\u6761\u77ed\u5185\u88e4\uff0c\u5f20\u56db\u59d1\u4e3a\u4ed6\u62ab\u4e0a\u7684\u957f\u886b\uff0c\u88ab\u4e24\u59d0\u59b9\u4e00\u9635\u62c9\u626f\uff0c\u8fd9\u65f6\u65e9\u5df2\u6ed1\u843d\u4e0b\u53bb\uff0c\u843d\u5728\u4e86\u5730\u4e0a\u3002 \u3000\u3000\u4e24\u59d0\u59b9\u653e\u6d6a\u5f62\u9ab8\uff0c\u5f62\u540c\u75af\u72c2\uff0c\u4e00\u4e2a\u6402\u4f4f\u6c5f\u67ab\u72c2\u543b\u4e0d\u5df2\uff0c\u53e6\u4e00\u4e2a\u5219\u5728\u4ed6\u904d\u8eab\u629a\u5f04\u3002\u5b58\u5fc3\u8981\u6311\u9017\u8d77\u4ed6\u7684\u6b32\u706b\u3002 \u3000\u3000\u800c\u8fd9\u65f6\u5728\u5ba2\u623f\u5916\uff0c\u7f57\u5170\u5df2\u6084\u7136\u6765\u5230\u4eb2\u81ea\u62c5\u4efb\u62a4\u6cd5\u7684\u5f20\u56db\u59d1\u8eab\u8fb9\uff0c\u5fe7\u6302\u4e8e\u8272\u5730\u8f7b\u58f0\u9053\uff1a \u3000\u3000\u201c\u56db\u59d1\uff0c\u8fd9\u6837\u4e0b\u53bb\uff0c\u6050\u6015\u2026\u2026\u201d \u3000\u3000\u5f20\u56db\u59d1\u8f6c\u8fc7\u8138\u9053\uff1a\u201c\u4e0d\u7528\u803d\u5fc3\uff0c\u6c5f\u67ab\u4f1a\u628a\u63e1\u5206\u5bf8\u7684\u3002\u201d \u3000\u3000\u7f57\u5170\u8f7b\u53f9\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u53ef\u662f\uff0c\u4ed6\u6bd5\u7adf\u662f\u4e2a\u8840\u6c14\u65b9\u521a\u7684\u5c0f\u4f19\u5b50\uff0c\u4e24\u4e2a\u4e2b\u5934\u5982\u6b64\u7ea0\u7f20\uff0c\u6559\u4ed6\u5982\u4f55\u80fd\u5fcd\u53d7\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u6b63\u8272\u9053\uff1a\u201c\u5979\u4eec\u81ea\u4e60\u2018\u4f20\u771f\u8bb0\u2019\u6b66\u529f\uff0c\u5df1\u4e2d\u6bd2\u592a\u6df1\uff0c\u9664\u4e86\u7528\u8fd9\u4ee5\u6bd2\u653b\u6bd2\u4e4b\u6cd5\u4e00\u8bd5\uff0c\u6216\u80fd\u594f\u6548\u4e4b\u5916\uff0c\u53ea\u6015\u4e24\u4e2a\u4e2b\u5934\u662f\u771f\u7684\u65e0\u6551\u4e86\u3002\u201d \u3000\u3000\u7f57\u5170\u7231\u5973\u5fc3\u5207\uff0c\u6025\u95ee\u9053\uff1a\u201c\u56db\u59d1\uff0c\u4eca\u591c\u4f60\u6253\u7b97\u8ba9\u6c5f\u67ab\u4e3a\u4e24\u4e2a\u4e2b\u5934\u7834\u8eab\uff1f\u201d \u3000\u3000\u5f20\u56db\u59d1\u90d1\u91cd\u9053\uff1a\u201c\u4f46\u613f\u6c5f\u67ab\u80fd\u81ea\u5236\uff0c\u5426\u5219\u4e0d\u4f46\u6789\u8d39\u4ed6\u4e00\u756a\u82e6\u5fc3\uff0c\u5168\u529f\u5c3d\u5f03\uff0c\u8fde\u4ed6\u672c\u8eab\u90fd\u4f1a\u53d1\u751f\u610f\u60f3\u4e0d\u5230\u7684\u5371\u9669\u5462\uff01\u201d \u3000\u3000\u7f57\u5170\u4e60\u8fc7\u201c\u60d1\u5fc3\u672f\u201d\uff0c\u81ea\u7136\u6df1\u77e5\u5929\u72d0\u6b66\u529f\u7684\u5389\u5bb3\uff0c\u5c24\u5176\u96ea\u3001\u971c\u4e24\u59d0\u59b9\u4e5f\u5df2\u5c3d\u5f97\u201c\u60d1\u5fc3\u672f\u201d\u771f\u4f20\uff0c\u4e07\u4e00\u53cc\u65b9\u5404\u5c3d\u5168\u529b\u65bd\u5c55\u51fa\u6765\uff0c\u6b32\u706b\u4e00\u53d1\u4e0d\u53ef\u6536\u62fe\uff0c\u540e\u679c\u5c06\u4e0d\u582a\u8bbe\u60f3\u3002 \u3000\u3000\u8bd5\u60f3\uff0c\u4e00\u65b9\u65bd\u5c55\u201c\u60d1\u5fc3\u672f\u201d\uff0c\u53e6\u4e00\u65b9\u5df2\u96be\u6297\u62d2\u3002\u82e5\u662f\u53cc\u65b9\u4e92\u653b\uff0c\u72b9\u5982\u706b\u4e0a\u52a0\u6cb9\uff0c\u5373\u5c06\u5f62\u6210\u600e\u4e48\u4e00\u4e2a\u5c40\u9762\u3002 \u3000\u3000\u6700\u53ef\u80fd\u7684\u7ed3\u679c\uff0c\u4fbf\u662f\u53cc\u65b9\u5747\u88ab\u6b32\u706b\u711a\u8eab\uff0c\u540c\u5f52\u4e8e\u5c3d\uff01 \u3000\u3000\u60f3\u5230\u8fd9\u79cd\u53ef\u80fd\uff0c\u7f57\u5170\u4e0d\u7531\u5730\u53d1\u51fa\u58f0\u8f7b\u547c\uff1a\u201c\u554a\uff01\u2026\u2026\u201d \u3000\u3000\u5f20\u56db\u59d1\u529d\u6170\u9053\uff1a\u201c\u975e\u5e38\u7684\u60c5\u51b5\uff0c\u53ea\u6709\u7528\u975e\u5e38\u624b\u6bb5\uff0c\u9664\u6b64\u4e4b\u5916\uff0c\u6211\u5b9e\u5728\u60f3\u4e0d\u51fa\u4ec0\u4e48\u66f4\u597d\u7684\u65b9\u6cd5\uff0c\u80fd\u62ef\u6551\u4e24\u4e2a\u4e2b\u5934\u4e86\u3002\u4f55\u51b5\uff0c\u7eb5\u4f7f\u6b7b\u9a6c\u5f53\u505a\u6d3b\u9a6c\u533b\uff0c\u4e5f\u603b\u6709\u4e00\u4e1d\u5e0c\u671b\uff0c\u603b\u6bd4\u88ab\u8feb\u975e\u5f97\u4f60\u4eec\u4eb2\u624b\u6740\u4e86\u4e24\u4e2a\u4e2b\u5934\u5f3a\u4e9b\u5427\u3002\u201d \u3000\u3000\u7f57\u5170\u6cea\u5149\u95ea\u52a8\uff0c\u53f9\u606f\u4e00\u58f0\uff0c\u9eef\u7136\u4f24\u795e\u5730\u8f6c\u8eab\u79bb\u53bb\uff0c\u4f3c\u4e4e\u4e0d\u6562\u4e5f\u4e0d\u5fcd\u7559\u4e0b\uff0c\u76ee\u7779\u968f\u65f6\u6709\u53ef\u80fd\u53d1\u751f\u7684\u4e0d\u5e78\u3002 \u3000\u3000\u5f20\u56db\u59d1\u5f88\u80fd\u4e86\u89e3\u7f57\u5170\u6b64\u523b\u7684\u5fc3\u60c5\uff0c\u6ca1\u6709\u62e6\u963b\u5979\u3002 \u3000\u3000\u5728\u8fd9\u79cd\u60c5\u5f62\u4e4b\u4e0b\uff0c\u5979\u4e5f\u65e0\u80fd\u4e3a\u529b\uff0c\u5e2e\u4e0d\u4e0a\u6c5f\u67ab\u4efb\u4f55\u5fd9\u3002\u552f\u4e00\u7684\u5e0c\u671b\uff0c\u5c31\u662f\u6c5f\u67ab\u80fd\u4fdd\u6301\u6e05\u9192\uff0c\u4e0d\u8981\u5fd8\u4e86\u4e09\u5723\u5f53\u5e74\u6240\u4ea4\u4ed8\u7684\u91cd\u4efb\u3002 \u3000\u3000\u540c\u65f6\u5979\u66f4\u6697\u81ea\u7948\u7977\uff0c\u4f46\u613f\u4eca\u591c\u80fd\u5e73\u5b89\u5ea6\u8fc7\u3002 \u3000\u3000\u4e07\u4e00\u795e\u79d8\u6559\u4e3b\u65b9\u9762\u5927\u4e3e\u6765\u72af\uff0c\u96ea\u3001\u971c\u4e24\u59d0\u59b9\u60f3\u5fc5\u4e89\u76f8\u6740\u654c\uff0c\u5979\u4eec\u4e00\u6740\u7ea2\u4e86\u773c\uff0c\u90a3\u5c31\u771f\u7684\u65e0\u6551\u4e86\u3002 \u3000\u3000\u5ba2\u623f\u91cc\uff0c\u4f20\u51fa\u4e86\u9635\u9635\u7684\u5a07\u541f\u3002 \u3000\u3000\u5f20\u56db\u59d1\u6697\u81ea\u4e00\u60ca\uff0c\u5fcd\u4e0d\u4f4f\u5c06\u53f3\u773c\u51d1\u8fce\u7a97\u7f1d\uff0c\u5411\u5185\u4e00\u63a2\u671b\uff0c\u53ea\u89c1\u4e09\u6761\u8089\u866b\u5728\u5730\u4e0a\u53e0\u6210\u4e86\u4e00\u5806\u3002 \u3000\u3000\u541f\u96ea\u88ab\u6c5f\u67ab\u6251\u4f4f\uff0c\u5728\u5979\u904d\u4f53\u72c2\u543b\u3002 \u3000\u3000\u800c\u541f\u971c\u53c8\u4f0f\u5728\u6c5f\u67ab\u80cc\u4e0a\uff0c\u53cc\u624b\u7d27\u7d27\u5c06\u5730\u6402\u4f4f\u3002 \u3000\u3000\u4e24\u5973\u4e00\u7537\uff0c\u6784\u6210\u4e00\u526f\u6625\u8272\u65e0\u8fb9\u7684\u753b\u9762\uff0c\u52a0\u4e0a\u541f\u96ea\u7684\u8f7b\u5624\u5a07\u541f\uff0c\u53ca\u541f\u971c\u653e\u6d6a\u5f62\u9ab8\u7684\u7b11\u58f0\u3002 \u3000\u3000\u4f7f\u7a97\u5916\u7684\u5f20\u56db\u59d1\u770b\u5728\u773c\u91cc\uff0c\u542c\u5165\u8033\u4e2d\uff0c\u4e5f\u4e0d\u7981\u611f\u5230\u8138\u4e0a\u53d1\u8d77\u70eb\u6765\u3002 \u3000\u3000\u5979\u5012\u5e76\u975e\u8d2a\u770b\u623f\u5185\u9563\u4eba\u5fc3\u5f26\u7684\u4e00\u5e55\uff0c\u800c\u662f\u803d\u5fc3\u6c5f\u67ab\u7ecf\u4e0d\u8d77\u4e24\u59d0\u59b9\u7684\u7ea0\u7f20\uff0c\u4e07\u4e00\u60c5\u51b5\u5371\u6025\uff0c\u4e5f\u53ea\u5f97\u4e0d\u987e\u4e00\u5207\uff0c\u7518\u5192\u96ea\u3001\u971c\u4e24\u59d0\u59b9\u88ab\u649e\u7834\u4e86\u597d\u4e8b\uff0c\u6b32\u706b\u9ad8\u6da8\u4e0b\u5931\u53bb\u7406\u6027\u5411\u5979\u51fa\u624b\u4e4b\u9669\uff0c\u95ef\u8fdb\u53bb\u6765\u4e2a\u7d27\u6025\u5e94\u53d8\u4e86\u3002 \u3000\u3000\u5e78\u597d\u8fd9\u4e00\u770b\uff0c\u770b\u51fa\u6c5f\u67ab\u59cb\u7ec8\u63a7\u5236\u5168\u5c40\uff0c\u867d\u5c06\u4e24\u59d0\u59b9\u6311\u9017\u5f97\u6b32\u706b\u96be\u7981\uff0c\u81ea\u5df1\u5374\u80fd\u6781\u529b\u81ea\u5236\uff0c\u603b\u7b97\u5982\u91ca\u91cd\u8d1f\u5730\u677e\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u5b9e\u9645\u4e0a\uff0c\u6c5f\u67ab\u6bd4\u8ddf\u4e24\u4f4d\u7edd\u4e16\u9ad8\u624b\u62fc\u6597\u66f4\u5403\u529b\uff0c\u4ed6\u73b0\u5728\u5df2\u4e0d\u80fd\u4ed7\u201c\u60d1\u5fc3\u672f\u201d\u63a7\u5236\u4e24\u59d0\u59b9\uff0c\u5168\u9760\u81ea\u5df1\u7684\u5b9a\u529b\u652f\u6301\u3002 \u3000\u3000\u8981\u77e5\u201c\u60d1\u5fc3\u672f\u201d\u7684\u65bd\u5c55\uff0c\u4e3b\u8981\u662f\u5f53\u53cc\u65b9\u56db\u76ee\u76f8\u4ea4\uff0c\u773c\u5149\u63a5\u89e6\u7684\u7247\u523b\u4e4b\u95f4\uff0c\u4ee5\u773c\u6ce2\u60d1\u4f4f\u5bf9\u65b9\u5fc3\u795e\u3002 \u3000\u3000\u8b6c\u5982\u8bf4\uff0c\u5728\u4f38\u624b\u4e0d\u89c1\u4e94\u6307\u7684\u9ed1\u6697\u4e2d\uff0c\u6216\u662f\u5f7c\u6b64\u4ee5\u80cc\u76f8\u5bf9\uff0c\u201c\u60d1\u5fc3\u672f\u201d\u4fbf\u65e0\u6cd5\u65bd\u5c55\u3002 \u3000\u3000\u6b64\u523b\u7684\u60c5\u5f62\uff0c\u6b63\u662f\u5982\u6b64\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u4e24\u59d0\u59b9\u5df2\u53d7\u5230\u201c\u60d1\u5fc3\u672f\u201d\u5f81\u670d\uff0c\u89e6\u53d1\u4e86\u5979\u4eec\u5f3a\u70c8\u7684\u60c5\u6b32\uff0c\u5982\u540c\u88ab\u50ac\u7720\u672f\u63a7\u5236\u4e00\u6837\uff0c\u6f5c\u610f\u8bc6\u4e2d\u5b8c\u5168\u4f9d\u7167\u50ac\u7720\u8005\u7684\u6307\u793a\uff0c\u7edd\u5bf9\u670d\u4ece\uff0c\u65e0\u6cd5\u66f4\u6539\uff0c\u800c\u4e14\u662f\u6709\u8fdb\u65e0\u9000\u3002 \u3000\u3000\u800c\u8fd9\u80a1\u72c2\u70bd\u7684\u6b32\u706b\uff0c\u5fc5\u987b\u4f7f\u5b83\u53d1\u6cc4\u51fa\u6765\u3002 \u3000\u3000\u5728\u8fc7\u53bb\u76f8\u5904\u7684\u65e5\u5b50\u91cc\uff0c\u50cf\u6b64\u523b\u8fd9\u79cd\u7ea0\u7f20\u5728\u4e00\u8d77\u7684\u573a\u9762\uff0c\u4ed6\u4eec\u4e09\u4eba\u65e9\u5df2\u4e60\u4ee5\u4e3a\u5e38\uff0c\u4e0d\u8db3\u4e3a\u5947\u3002\u6bcf\u6b21\u4e00\u4e86\u7d27\u8981\u5173\u5934\uff0c\u5f7c\u6b64\u90fd\u80fd\u60ac\u5d16\u52d2\u9a6c\uff0c\u5b88\u4f4f\u4e86\u6700\u540e\u9632\u7ebf\u3002 \u3000\u3000\u4e0d\u540c\u7684\u662f\uff0c\u6c5f\u67ab\u4ece\u6765\u5bf9\u5979\u4eec\u65bd\u5c55\u8fc7\u201c\u60d1\u5fc3\u672f\u201d\u3002 \u3000\u3000\u4f46\u662f\uff0c\u6b64\u523b\u5b8c\u5168\u4e0d\u662f\u90a3\u4e48\u56de\u4e8b\u4e86\uff0c\u4e24\u59d0\u59b9\u5fc3\u795e\u5df2\u4e71\uff0c\u6b32\u706b\u4e0d\u65ad\u4e0a\u5347\uff0c\u653e\u6d6a\u5f62\u9ab8\u4e4b\u60c5\uff0c\u6bd4\u6c34\u6027\u6768\u82b1\u7684\u8361\u5987\u66f4\u6709\u8fc7\u4e4b\uff0c\u800c\u65e0\u4e0d\u53ca\u3002 \u3000\u3000\u5f62\u5bb9\u5979\u4eec\u50cf\u8361\u5987\uff0c\u4e00\u70b9\u4e5f\u4e0d\u4e3a\u8fc7\uff0c\u53ea\u89c1\u4e24\u59d0\u59b9\u5168\u8eab\u8d64\u88f8\uff0c\u4e0a\u4e0b\u5939\u653b\u8fde\u77ed\u5185\u88e4\u90fd\u88ab\u5979\u4eec\u6252\u6389\u7684\u6c5f\u67ab\uff0c\u597d\u4e00\u526f\u82e5\u75f4\u82e5\u72c2\u7684\u60c5\u6001\u3002\u770b\u5728\u7a97\u5916\u7684\u5f20\u56db\u59d1\u773c\u65f6\uff0c\u4e0d\u7981\u4e3a\u4e4b\u548b\u820c\uff0c\u81ea\u53f9\u671b\u5c18\u83ab\u53ca\u3002 \u3000\u3000\u6bd5\u7adf\uff0c\u5979\u4eec\u53ea\u662f\u5341\u51e0\u5c81\u7684\u9752\u6625\u5c11\u5973\uff0c\u4ece\u672a\u7ecf\u8fc7\u4eba\u9053\uff0c\u5c1a\u5c5e\u5b8c\u74a7\u5904\u5b50\u5440\uff01 \u3000\u3000\u4e24\u59d0\u59b9\u5f62\u540c\u75af\u72c2\uff0c\u6108\u6765\u6108\u51b2\u52a8\u4e86\u3002 \u3000\u3000\u770b\u60c5\u5f62\uff0c\u5979\u4eec\u7ee7\u7eed\u518d\u95f9\u4e0b\u53bb\uff0c\u6c5f\u67ab\u4e5f\u5c06\u60c5\u4e0d\u81ea\u7981\u3002\u2026\u2026 \u3000\u3000\u5f20\u56db\u59d1\u60c5\u6025\u4e4b\u4e0b\uff0c\u6b63\u5f85\u4e0d\u987e\u4e00\u5207\u540e\u679c\uff0c\u95ef\u8fdb\u623f\u53bb\u52a0\u4ee5\u963b\u6b62\uff0c\u5ffd\u542c\u5927\u60b2\u68b5\u97f3\u54cd\u8d77\uff0c\u4f3c\u4ece\u56db\u9762\u516b\u65b9\u4f20\u6765\u3002 \u3000\u3000\u539f\u6765\uff0c\u7f57\u5170\u9eef\u7136\u4f24\u795e\u79bb\u53bb\u65f6\uff0c\u9047\u4e0a\u4e86\u8d1f\u8d23\u5404\u5904\u5de1\u89c6\u7684\u5927\u548c\u5c1a\u3002 \u3000\u3000\u5927\u548c\u5c1a\u5bdf\u8a00\u89c2\u8272\uff0c\u770b\u51fa\u7f57\u5170\u5fc3\u6709\u5343\u5343\u7ed3\uff0c\u8ffd\u95ee\u4e4b\u4e0b\uff0c\u5f97\u77e5\u60c5\u7531\uff0c\u4fbf\u53cc\u624b\u5408\u5341\uff0c\u53e3\u8bf5\u4f5b\u53f7\u9053\uff1a\u201c\u963f\u5f25\u9640\u4f5b\uff0c\u5584\u54c9\u5584\u54c9\u3002\u7f57\u5973\u4fa0\u4e0d\u7528\u803d\u5fc3\uff0c\u6b64\u4e8b\u4ea4\u7ed9\u8d2b\u50e7\u597d\u4e86\uff0c\u5fc5\u8981\u65f6\uff0c\u53ea\u597d\u7531\u8d2b\u50e7\u6765\u5316\u89e3\u51f6\u9669\u4e86\u3002\u201d \u3000\u3000\u7f57\u5170\u8c22\u4e86\u4e00\u58f0\uff0c\u8ff3\u81ea\u56de\u5230\u4e08\u592b\u8eab\u8fb9\u53bb\u3002 \u3000\u3000\u5927\u548c\u5c1a\u5219\u8d70\u5411\u540e\u7985\u9662\uff0c\u4e00\u76f4\u5b88\u5019\u5728\u6697\u5904\u3002 \u3000\u3000\u5f53\u5730\u60ca\u89c1\u7a97\u5916\u7684\u5f20\u56db\u59d1\u5728\u5c06\u8981\u91c7\u53d6\u884c\u52a8\u65f6\uff0c\u60c5\u77e5\u4e8b\u4e0d\u5b9c\u8fdf\uff0c\u6025\u5fd9\u4ee5\u6df1\u539a\u5185\u529b\u53d1\u51fa\u5927\u60b2\u68b5\u97f3\u3002 \u3000\u3000\u5ba2\u623f\u5185\uff0c\u5343\u94a7\u4e00\u53d1\u4e4b\u9645\uff0c\u68b5\u97f3\u4f20\u5165\uff0c\u987f\u4f7f\u7ea0\u7f20\u4e00\u8d77\u7684\u8d64\u88f8\u4e24\u5973\u4e00\u7537\uff0c\u5982\u540c\u4ece\u6d51\u6d51\u5669\u5669\u4e2d\u88ab\u5524\u9192\u3002 \u3000\u3000\u4f46\u4ed6\u4eec\u90fd\u5df2\u5fc3\u529b\u4ea4\u7601\uff0c\u968f\u5373\u53c8\u660f\u660f\u5165\u7761\u2026\u2026 \u3000\u3000\u4e00\u591c\u5e73\u5b89\u65e0\u4e8b\u5730\u5ea6\u8fc7\u3002 \u3000\u3000\u51fa\u4e4e\u610f\u6599\uff0c\u795e\u79d8\u6559\u4e3b\u65b9\u9762\u5e76\u672a\u5927\u4e3e\u6765\u72af\u3002 \u3000\u3000\u751a\u81f3\u53e4\u5e99\u65b9\u5706\u4e00\u91cc\u4e4b\u5185\uff0c\u6ca1\u6709\u53d1\u73b0\u4efb\u4f55\u98ce\u5439\u8349\u52a8\u3002 \u3000\u3000\u96be\u9053\u4ed6\u4eec\u5df2\u77e5\u96be\u800c\u9000\uff0c\u64a4\u79bb\u4e86\u5c71\u4e1c\u5883\u5185\uff1f \u3000\u3000\u8fd9\u4f3c\u4e4e\u4e0d\u592a\u53ef\u80fd\u5427\uff01 \u3000\u3000\u6628\u591c\uff0c\u53ef\u8bf4\u662f\u6709\u60ca\u65e0\u9669\uff0c\u8981\u4e0d\u662f\u5927\u548c\u5c1a\u53ca\u65f6\u53d1\u51fa\u5927\u60b2\u68b5\u97f3\uff0c\u4e07\u4e00\u5f20\u56db\u59d1\u60c5\u6025\u4e4b\u4e0b\uff0c\u4e0d\u987e\u4e00\u5207\u95ef\u8fdb\u5ba2\u623f\u53bb\uff0c\u6b63\u9645\u6b32\u706b\u72c2\u70bd\u7684\u96ea\u3001\u971c\u4e24\u59d0\u59b9\uff0c\u6781\u53ef\u80fd\u5931\u53bb\u7406\u6027\uff0c\u8fc1\u6012\u649e\u7834\u5979\u4eec\u597d\u4e8b\u7684\u8fd9\u4f4d\u5f20\u59e8\u3002 \u3000\u3000\u5982\u679c\u4e24\u59d0\u59b9\u540c\u65f6\u51fa\u624b\uff0c\u5f20\u56db\u59d1\u7edd\u5bf9\u62b5\u6321\u4e0d\u4f4f\u3002 \u3000\u3000\u5728\u90a3\u79cd\u60c5\u52bf\u4e0b\uff0c\u6c5f\u67ab\u82e5\u8981\u62a2\u6551\u5f20\u56db\u59d1\uff0c\u53ea\u6709\u51fa\u624b\u6740\u4e86\u8fd9\u5f62\u540c\u75af\u72c2\u7684\u4e24\u59d0\u59b9\uff01 \u3000\u3000\u5f20\u56db\u59d1\u60f3\u8d77\u5f53\u65f6\u7684\u60c5\u51b5\uff0c\u4e0d\u7981\u72b9\u6709\u4f59\u60b8\uff0c\u4e00\u65e9\u5c31\u6765\u5411\u5927\u548c\u5c1a\u81f4\u8c22\u3002 \u3000\u3000\u5927\u548c\u5c1a\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8c22\u6211\u5012\u4e0d\u5fc5\uff0c\u5f85\u4f1a\u513f\u8981\u662f\u6709\u4eba\u5411\u8d2b\u50e7\u5174\u5e08\u95ee\u7f6a\uff0c\u602a\u6211\u6628\u591c\u574f\u4e86\u4ed6\u4eec\u7684\u597d\u4e8b\uff0c\u8fd8\u5f97\u5f20\u59d1\u5a18\u591a\u591a\u66ff\u6211\u62c5\u5f85\u4e00\u4e9b\u624d\u662f\u5462\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u9053\uff1a\u201c\u6c5f\u67ab\u4e0d\u4f1a\u7684\u3002\u201d \u3000\u3000\u201c\u6211\u8bf4\u7684\u4e0d\u662f\u4ed6\u3002\u201d\u5927\u548c\u5c1a\u9053\uff1a\u201c\u662f\u90a3\u4e24\u4e2a\u5c0f\u72d0\u5973\u2026\u2026\u201d \u3000\u3000\u8bdd\u72b9\u672a\u4e86\uff0c\u5df2\u89c1\u96ea\u3001\u971c\u4e24\u59d0\u59b9\u7ad9\u5728\u5927\u6bbf\u95e8\u53e3\u3002 \u3000\u3000\u53ea\u89c1\u541f\u96ea\u9762\u7f69\u5bd2\u971c\uff0c\u5411\u541f\u971c\u51b7\u51b7\u5730\u95ee\uff1a\u201c\u597d\u50cf\u6709\u4eba\u5728\u80cc\u540e\u8bf4\u6211\u4eec\u574f\u8bdd\uff0c\u4f60\u542c\u89c1\u4e86\u5417\uff1f\u201d \u3000\u3000\u541f\u971c\u4e0d\u5c51\u9053\uff1a\u201c\u6709\u79cd\u5c31\u5f53\u9762\u8bf4\uff0c\u80cc\u540e\u8bf4\u5f53\u4ed6\u653e\u5c41\uff01\u201d \u3000\u3000\u563f\uff01\u5e74\u7eaa\u8f7b\u8f7b\u7684\u5927\u59d1\u5a18\uff0c\u8fde\u8fd9\u79cd\u8bdd\u90fd\u51fa\u53e3\u4e86\u3002 \u3000\u3000\u5f20\u56db\u59d1\u5fc3\u77e5\u5979\u4eec\u8981\u627e\u5927\u548c\u5c1a\u9ebb\u70e6\uff0c\u5927\u6982\u662f\u4e3a\u4e86\u6628\u591c\u7684\u4e8b\uff0c\u4e0d\u7531\u5730\u8138\u8272\u4e00\u6c89\uff0c\u6b63\u5f85\u51fa\u8a00\u76f8\u8d23\uff0c\u5927\u548c\u5c1a\u5374\u62a2\u5148\u5f00\u4e86\u53e3\u3002 \u3000\u3000\u201c\u4e24\u4f4d\u59d1\u5a18\uff0c\u65e9\u554a\u3002\u201d \u3000\u3000\u4e24\u59d0\u59b9\u8de8\u8fdb\u4e86\u5927\u6bbf\uff0c\u541f\u96ea\u773c\u76ae\u4e00\u7ffb\u9053\uff1a\u201c\u8c01\u662f\u59d1\u5a18\uff1f\u6211\u4eec\u662f\u5c0f\u72d0\u5973\uff01\u201d \u3000\u3000\u5176\u5b9e\u201c\u5c0f\u72d0\u5973\u201d\u53ea\u662f\u4e2a\u6635\u79f0\uff0c\u5979\u4eec\u4ece\u672a\u4ecb\u610f\uff0c\u4eca\u6668\u663e\u7136\u662f\u5b58\u5fc3\u800d\u627e\u5927\u548c\u5c1a\u7684\u78b4\u513f\uff0c\u53cd\u6b63\u4ed6\u8bf4\u4ec0\u4e48\u90fd\u4e0d\u5bf9\u3002 \u3000\u3000\u5927\u548c\u5c1a\u4ecd\u7136\u7b11\u5bb9\u6ee1\u9762\u9053\uff1a\u201c\u5bf9\u5bf9\u5bf9\uff0c\u4f60\u4eec\u5df2\u5c3d\u5f97\u5929\u72d0\u592b\u4eba\u6b66\u529f\u771f\u4f20\uff0c\u5c0f\u72d0\u5973\u81ea\u662f\u5f53\u4e4b\u65e0\u6127\u3002\u201d \u3000\u3000\u541f\u971c\u51fa\u8a00\u8ba5\u9053\uff1a\u201c\u8fd9\u4ee5\u8bf4\uff0c\u5927\u548c\u5c1a\u5ff5\u4e86\u8fd9\u4e48\u591a\u4f5b\u7ecf\uff0c\u5e94\u8be5\u662f\u5c0f\u4f5b\u7956\u7f57\uff1f\u201d \u3000\u3000\u5927\u548c\u5c1a\u53c8\u53cc\u624b\u5408\u5341\uff0c\u9053\uff1a\u201c\u963f\u5f25\u9640\u4f5b\uff0c\u7f6a\u8fc7\uff0c\u7f6a\u8fc7\uff0c\u8d2b\u50e7\u53ea\u4e0d\u8fc7\u662f\u4e2a\u9152\u8089\u548c\u5c1a\uff0c\u54ea\u6562\u6cbe\u8fb1\u4f5b\u7956\u5723\u540d\u3002\u201d \u3000\u3000\u201c\u54fc\uff01\u201d\u541f\u9738\u9053\uff1a\u201c\u51fa\u5bb6\u4eba\u4e0d\u6cbe\u8364\u8165\uff0c\u4f60\u8fd9\u548c\u5c1a\u5374\u662f\u5927\u7897\u559d\u9152\uff0c\u5927\u5757\u5403\u8089\uff0c\u5916\u52a0\u6740\u4eba\u4e0d\u7728\u773c\uff0c\u7b80\u76f4\u662f\u4f5b\u95e8\u4e2d\u7684\u8d25\u7c7b\uff01\u201d \u3000\u3000\u5f20\u56db\u59d1\u5fcd\u65e0\u53ef\u5fcd\uff0c\u6012\u65a5\u9053\uff1a\u201c\u541f\u971c\uff0c\u4e0d\u5f97\u5bf9\u5927\u548c\u5c1a\u65e0\u793c\uff01\u201d \u3000\u3000\u541f\u971c\u8fde\u6b63\u773c\u90fd\u4e0d\u770b\u5f20\u56db\u59d1\u4e00\u4e0b\uff0c\u51b7\u51b7\u8bf4\uff1a\u201c\u6211\u4eec\u5728\u8ddf\u8fd9\u9152\u8089\u548c\u5c1a\u8bf4\u8bdd\uff0c\u4e0d\u5e72\u4f60\u7684\u4e8b\uff01\u201d \u3000\u3000\u201c\u54fc\uff01\u201d \u3000\u3000\u5f20\u56db\u59d1\u6b63\u5f85\u53d1\u4f5c\uff0c\u5927\u548c\u5c1a\u5df2\u5475\u5475\u7b11\u4e86\u8d77\u6765\u3002 \u3000\u3000\u201c\u5f20\u59d1\u5a18\uff0c\u5e74\u8f7b\u4eba\u4e0d\u62d8\u5c0f\u8282\uff0c\u559c\u6b22\u8ddf\u6211\u8fd9\u516d\u6839\u4e0d\u51c0\u7684\u51fa\u5bb6\u4eba\u6253\u6210\u4e00\u7247\uff0c\u4f60\u53c8\u4f55\u5fc5\u8ddf\u6211\u4eec\u51d1\u70ed\u95f9\u3002\u201d\u5927\u548c\u5c1a\u552f\u6050\u53cc\u65b9\u53d1\u751f\u51b2\u7a81\uff0c\u4e00\u65bd\u773c\u8272\uff1a\u201c\u4f60\u4e0d\u662f\u8981\u63a5\u8001\u53eb\u5316\u7684\u73ed\u53bb\u5417\uff1f\u201d \u3000\u3000\u5f20\u56db\u59d1\u54ea\u4f1a\u4e0d\u660e\u767d\u5927\u548c\u5c1a\u7684\u5fc3\u610f\uff0c\u4f46\u5979\u6b64\u523b\u5374\u4e0d\u80fd\u8d70\u5f00\uff0c\u8ba9\u5927\u548c\u5c1a\u4e00\u4eba\u5e94\u5bf9\u4e24\u4e2a\u5c0f\u72d0\u5973\u3002 \u3000\u3000\u201c\u6211\u8fd8\u8981\u5728\u8fd9\u91cc\u7b49\u6c5f\u67ab\u3002\u201d\u5979\u6446\u660e\u4e86\u4e0d\u8d70\u7684\u6001\u52bf\u3002 \u3000\u3000\u5979\u6545\u610f\u62ac\u51fa\u6c5f\u67ab\uff0c\u7528\u610f\u662f\u4f7f\u4e24\u4e2a\u5c0f\u72d0\u5973\u6709\u6240\u987e\u5fcc\uff0c\u81f3\u5c11\u6001\u5ea6\u4e0a\u4f1a\u6536\u655b\u4e9b\u3002 \u3000\u3000\u60f3\u4e0d\u5230\u63d0\u5230\u66f9\u64cd\u66f9\u64cd\u5c31\u5230\u3002 \u3000\u3000\u201c\u5927\u5bb6\u65e9\u554a\uff01\u201d \u3000\u3000\u6c5f\u67ab\u53f3\u4fa7\u62f1\u95e8\u8d70\u4e86\u51fa\u6765\uff0c\u8138\u4e0a\u6302\u7740\u8f7b\u677e\u7684\u7b11\u5bb9\u3002 \u3000\u3000\u96ea\u3001\u971c\u4e24\u59d0\u59b9\u7adf\u672a\u52a0\u7406\u4f1a\uff0c\u901e\u5411\u5de6\u4fa7\u62f1\u95e8\u8d70\u4e86\u8fdb\u53bb\u3002 \u3000\u3000\u5927\u6bbf\u4e24\u4fa7\u7684\u62f1\u95e8\u90fd\u901a\u540e\u9762\u7985\u9662\uff0c\u663e\u7136\u5979\u4eec\u662f\u51c6\u5907\u56de\u9662\u5185\u7684\u5ba2\u623f\u3002 \u3000\u3000\u8fd9\u5ea7\u5e9f\u5f03\u5df2\u4e45\u7684\u53e4\u5e99\u5360\u5730\u751a\u5e7f\uff0c\u867d\u5df2\u662f\u5230\u5904\u6b8b\u57a3\u65ad\u58c1\uff0c\u7834\u65e7\u4e0d\u582a\uff0c\u4e0d\u590d\u5f53\u5e74\u7684\u96c4\u4f1f\u65e7\u8c8c\u3002 \u3000\u3000\u4f46\u4f9d\u7a00\u4ecd\u53ef\u770b\u51fa\uff0c\u6614\u65e5\u9999\u706b\u9f0e\u76db\u65f6\uff0c\u5fc5\u662f\u4fe1\u5f92\u7edc\u7ece\u4e0d\u7edd\u7684\u540d\u5239\u3002 \u3000\u3000\u4e0d\u8bf4\u522b\u7684\uff0c\u4ec5\u4ec5\u540e\u7985\u9662\u4f9b\u5584\u7537\u4fe1\u5973\u4f11\u606f\u7684\u4e24\u6392\u5ba2\u623f\uff0c\u5c31\u591a\u8fbe\u5341\u4e8c\u95f4\u3002 \u3000\u3000\u5982\u4eca\uff0c\u6b63\u597d\u88ab\u4ed6\u4eec\u8fd9\u6279\u4eba\u52a0\u4ee5\u5229\u7528\u3002 \u3000\u3000\u521a\u624d\u96ea\u3001\u971c\u4e24\u59d0\u59b9\u662f\u4ece\u524d\u9662\u8fdb\u5165\u5927\u6bbf\uff0c\u5979\u4eec\u4e00\u65e9\u5916\u51fa\u53bb\u4e86\u54ea\u91cc\uff1f \u3000\u3000\u5f20\u56db\u59d1\u3001\u5927\u548c\u5c1a\u88ab\u5979\u4eec\u4e00\u8fdb\u6765\u5c31\u51fa\u8a00\u627e\u78b4\uff0c\u4ee5\u81f4\u6ca1\u6709\u60f3\u5230\u8fd9\u95ee\u9898\u3002 \u3000\u3000\u6c5f\u67ab\u5374\u63d0\u4e86\u51fa\u6765\uff1a\u201c\u5979\u4eec\u521a\u56de\u6765\uff1f\u201d \u3000\u3000\u5f20\u56db\u59d1\u88ab\u4ed6\u4e00\u8bed\u63d0\u9192\uff0c\u8be7\u5f02\u9053\uff1a\u201c\u54a6\uff1f\u6628\u591c\u5979\u4eec\u4e0d\u662f\u5b89\u5b89\u7a33\u7a33\u7761\u5728\u5ba2\u623f\u91cc\uff0c\u6ca1\u89c1\u5979\u4eec\u51fa\u52a8\u5440\uff01\u201d \u3000\u3000\u6c5f\u67ab\u7b11\u8ff8\uff1a\u201c\u5927\u59d0\uff0c\u5979\u4eec\u5df2\u6df1\u5f97\u4f60\u7684\u9041\u6cd5\u6280\u827a\u771f\u4f20\uff0c\u8981\u6e9c\u51fa\u53bb\u592a\u5bb9\u6613\u4e86\uff0c\u8c01\u4e5f\u4e0d\u4f1a\u53d1\u89c9\uff0c\u4e0d\u7136\u5c31\u602a\u4f60\u6388\u827a\u4e0d\u529b\u5566\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u77aa\u4ed6\u4e00\u773c\uff0c\u6b63\u8272\u9053\uff1a\u201c\u6211\u5728\u8ddf\u4f60\u8bf4\u6b63\u7ecf\u7684\uff01\u201d \u3000\u3000\u5982\u679c\u5927\u5229\u5c1a\u4e0d\u5728\u573a\uff0c\u6c5f\u67ab\u5fc5\u4e00\u4f1a\u56de\u4e00\u53e5\uff1a\u201c\u6211\u6ca1\u6709\u4e0d\u6b63\u7ecf\u5440\uff01\u201d \u3000\u3000\u65e6\u4ed6\u6ca1\u6709\u628a\u8fd9\u53e5\u8bdd\u8bf4\u51fa\u6765\u53e3\uff0c\u53ea\u662f\u5fae\u5fae\u4e00\u7b11\u3002 \u3000\u3000\u5f20\u56db\u59d1\u63a5\u4e0b\u53bb\u9053\uff1a\u201c\u9041\u6cd5\u4e0d\u9041\u6cd5\u5e76\u4e0d\u91cd\u8981\uff0c\u5b58\u5fc3\u8981\u6e9c\u51fa\u53bb\uff0c\u5229\u7528\u591c\u8272\u63a9\u62a4\uff0c\u6211\u4eec\u4e4b\u4e2d\u7684\u4efb\u4f55\u4eba\u90fd\u80fd\u529e\u5230\uff0c\u5e76\u975e\u96be\u4e8b\u3002\u95ee\u9898\u662f\uff0c\u6628\u591c\u5979\u4eec\u6e9c\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u53bb\u505a\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u5927\u59d0\u521a\u624d\u600e\u4e48\u4e0d\u95f4\u5979\u4eec\uff1f\u4f60\u662f\u7edf\u5e05\uff0c\u5fc5\u987b\u638c\u63e1\u60c5\u51b5\u53ca\u6240\u6709\u4eba\u7684\u884c\u52a8\uff0c\u5979\u4eec\u600e\u53ef\u672a\u7ecf\u5927\u59d0\u540c\u610f\uff0c\u64c5\u81ea\u884c\u52a8\u5462\uff01\u201d \u3000\u3000\u5f20\u56db\u59d1\u82e6\u7b11\u9053\uff1a\u201c\u521a\u624d\u5979\u4eec\u4e00\u8fdb\u6765\uff0c\u5c31\u6545\u610f\u627e\u5927\u548c\u5c1a\u9ebb\u70e6\uff0c\u5dee\u70b9\u5f15\u8d77\u4e2d\u7a81\u3002\u6211\u6392\u89e3\u90fd\u6765\u4e0d\u53ca\uff0c\u54ea\u60f3\u5230\u8fd9\u4e9b\u3002\u201d \u3000\u3000\u201c\u627e\u5927\u548c\u5c1a\u9ebb\u70e6\uff1f\u201d\u6c5f\u67ab\u7684\u76ee\u5149\u8f6c\u5411\u4e86\u5927\u548c\u5c1a\u3002 \u3000\u3000\u5927\u548c\u5c1a\u5475\u5475\u7b11\u9053\uff1a\u201c\u5927\u6168\u662f\u602a\u6211\u6628\u591c\u591a\u4e8b\uff0c\u6216\u8005\u5bf9\u5927\u60b2\u68b5\u97f3\u4e0d\u592a\u6b23\u8d4f\u3002\u201d \u3000\u3000\u6c5f\u67ab\u5f53\u7136\u660e\u767d\u5927\u548c\u5c1a\u6307\u7684\u662f\u4ec0\u4e48\uff0c\u4e0d\u7981\u6709\u4e9b\u5c34\u5c2c\u3002 \u3000\u3000\u504f\u504f\u5f20\u56db\u59d1\u53c8\u6beb\u4e0d\u4fdd\u7559\u9053\uff1a\u201c\u4f60\u7684\u2018\u4ee5\u6bd2\u653b\u6bd2\u2019\u5999\u65b9\uff0c\u5bf9\u5979\u4eec\u6050\u6015\u65e0\u6548\u54e6\u3002\u201d \u3000\u3000\u6c5f\u67ab\u66f4\u7a98\u8feb\u4e86\u3002 \u3000\u3000\u5ffd\u542c\u5927\u548c\u5c1a\u53f9\u9053\uff1a\u201c\u5509\uff01\u5982\u4eca\u5979\u4eec\u4e24\u59d0\u59b9\u7684\u9b54\u529f\u8fdb\u5883\uff0c\u5df2\u7136\u6b65\u5165\u5766\u9014\uff0c\u5927\u6709\u4e00\u65e5\u5343\u91cc\u4e4b\u52bf\u3002 \u3000\u3000\u5c24\u5176\u662f\u541f\u971c\uff0c\u53ea\u8981\u4e00\u6740\u4eba\u89c1\u8840\uff0c\u8fd9\u65f6\u9b54\u6027\u5927\u53d1\uff0c\u4e0d\u81ea\u514b\u5236\u3002\u548c\u5c1a\u539f\u6253\u7b97\u4ee5\u4e03\u65e5\u4e03\u591c\u4e0d\u505c\u606f\u7684\u65b9\u6cd5\uff0c\u5ff5\u8bf5\u51fa\u5927\u60b2\u68b5\u5531\uff0c\u52a9\u5979\u4eec\u4f7f\u6210\u957f\u7684\u9b54\u5fc3\u6d88\u9000\u3002 \u3000\u3000\u53ef\u662f\uff0c\u7ecf\u8fc7\u6628\u591c\u7684\u4e8b\uff0c\u548c\u5c1a\u53d1\u73b0\u5979\u4eec\u4e0d\u4f46\u5bf9\u68b5\u97f3\u6709\u6240\u6392\u65a5\uff0c\u4e14\u5bf9\u548c\u5c1a\u5fc3\u5b58\u82a5\u8482\uff0c\u518d\u8981\u5979\u4eec\u5fc3\u7518\u60c5\u613f\u63a5\u53d7\u68b5\u97f3\uff0c\u6050\u6015\u76f8\u5f53\u56f0\u96be\u4e86\u3002\u201d \u3000\u3000\u201c\u6211\u4eec\u603b\u4e0d\u80fd\u5750\u89c2\u4e0d\u7ba1\uff0c\u4efb\u7531\u5979\u4eec\u6c89\u6ca6\u4e0b\u53bb\u5427\uff1f\u201d\u6c5f\u67ab\u771f\u7684\u6025\u4e86\uff0c\u8bed\u6c14\u4e0d\u514d\u6fc0\u52a8\u8d77\u6765\u3002 \u3000\u3000\u5f20\u56db\u59d1\u6df1\u6df1\u4e00\u53f9\uff0c\u795e\u60c5\u51dd\u91cd\u9053\uff1a\u201c\u5982\u4eca\u5f3a\u654c\u5f53\u524d\uff0c\u53c8\u53d1\u751f\u5979\u4eec\u7684\u95ee\u9898\uff0c\u6211\u4eec\u771f\u662f\u5185\u5fe7\u5916\u60a3\uff01\u201d \u3000\u3000\u4e09\u4eba\u9762\u9762\u76f8\u89d1\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u8c01\u4e5f\u62ff\u4e0d\u51fa\u66f4\u597d\u7684\u4e3b\u610f\u3002 \u3000\u3000\u5927\u6bbf\u4e0a\uff0c\u6c14\u6c1b\u7a81\u7136\u6c89\u95f7\u8d77\u6765\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u7a81\u89c1\u6b27\u9633\u662d\u592b\u5987\u5954\u5165\u3002 \u3000\u3000\u7f57\u5170\u6c14\u6025\u8d25\u574f\uff0c\u4e00\u8fdb\u6765\u5c31\u95ee\uff1a\u201c\u770b\u89c1\u4e24\u4e2b\u5934\u5417\uff1f\u201d \u3000\u3000\u5f20\u56db\u59d1\u70b9\u4e86\u70b9\u5934\uff0c\u9053\uff1a\u201c\u5979\u4eec\u521a\u56de\u6765\uff0c\u56de\u540e\u7985\u5ba2\u623f\u53bb\u4e86\u3002\u201d \u3000\u3000\u201c\u5662\u3002\u201d\u7f57\u5170\u5982\u91ca\u91cd\u8d1f\u5730\u677e\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u5f20\u56db\u59d1\u5bdf\u89c9\u51fa\u4fe9\u53e3\u5b50\u795e\u8272\u6709\u5f02\uff0c\u5fd9\u95ee\uff1a\u201c\u4f60\u4eec\u8ddf\u4e24\u4e2a\u4e2b\u5934\u4e00\u8d77\u51fa\u53bb\u7684\uff1f\u201d \u3000\u3000\u7f57\u5170\u70b9\u4e86\u4e0b\u5934\uff0c\u9053\uff1a\u201c\u6628\u591c\u4e24\u4e2a\u4e2b\u5934\u5165\u7761\u540e\uff0c\u6211\u4eec\u4ecd\u4e0d\u653e\u5fc3\uff0c\u4e00\u76f4\u5b88\u5728\u6697\u4e2d\uff0c\u6ce8\u610f\u5979\u4eec\u623f\u91cc\u7684\u52a8\u9759\u3002\u679c\u4e0d\u5176\u7136\uff0c\u8fc7\u4e86\u6ca1\u591a\u4e45\uff0c\u5979\u4eec\u5c31\u6e9c\u51fa\u623f\uff0c\u98de\u8eab\u8d8a\u9662\u5899\u800c\u53bb\u3002 \u3000\u3000\u5f53\u65f6\u6211\u4eec\u6ca1\u6709\u51fa\u58f0\u963b\u6b62\uff0c\u51b3\u5fc3\u8ddf\u53bb\u770b\u770b\u5979\u4eec\u8981\u53bb\u54ea\u91cc\uff0c\u8981\u505a\u4ec0\u4e48\uff0c\u6240\u4ee5\u4e00\u8def\u6084\u6084\u8ddf\u8e2a\u3002 \u3000\u3000\u7ed3\u679c\u5979\u4eec\u5e76\u6ca1\u6709\u76ee\u6807\uff0c\u53ea\u662f\u5230\u5904\u5bfb\u627e\u654c\u8e2a\uff0c\u770b\u60c5\u5f62\u662f\u6740\u6027\u5927\u53d1\uff0c\u60f3\u627e\u4e9b\u5012\u5a9a\u7684\u4eba\u6740\u4e2a\u75db\u5feb\u3002 \u3000\u3000\u504f\u504f\u641c\u5bfb\u4e86\u597d\u51e0\u5341\u91cc\uff0c\u7adf\u7136\u4e0d\u89c1\u654c\u8e2a\uff0c\u4e0d\u77e5\u90a3\u6279\u4eba\u8eb2\u5230\u4e86\u90a3\u91cc\u53bb\uff0c\u5bb3\u6211\u4eec\u75b2\u4e8e\u5954\u547d\uff0c\u8ddf\u7740\u4e24\u4e2a\u4e2b\u5934\u5230\u5904\u4e71\u8f6c\uff0c\u4e00\u76f4\u5c06\u5230\u5929\u4eae\uff0c\u770b\u5979\u4eec\u6740\u4e86\u4e0d\u5c11\u7684\u5c71\u732a\u548c\u91ce\u72fc\u3002\u201d \u3000\u3000\u6b27\u9633\u662d\u94c1\u9752\u7740\u8138\uff0c\u6124\u58f0\u9053\uff1a\u201c\u6211\u770b\u4e24\u4e2a\u4e2b\u5934\u5df2\u65e0\u53ef\u6551\u836f\uff0c\u4e0e\u5176\u4efb\u7531\u5979\u4eec\u6108\u9677\u6108\u6df1\uff0c\u5c06\u6765\u53d8\u6210\u5371\u5bb3\u5929\u4e0b\u6b66\u6797\u7684\u4e24\u5927\u5973\u9b54\uff0c\u5012\u4e0d\u5982\u7531\u6211\u4eb2\u624b\u2026\u2026\u201d \u3000\u3000\u201c\u6740\u201d\u5b57\u521a\u8981\u51fa\u53e3\uff0c\u5df2\u88ab\u7f57\u5170\u4ee5\u773c\u8272\u5236\u6b62\u3002 \u3000\u3000\u6b27\u9633\u662d\u6df1\u6df1\u53f9\u53e3\u6c14\uff0c\u4e0d\u518d\u8bf4\u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u201c\u963f\u5f25\u9640\u4f5b\uff01\u201d\u5927\u548c\u5c1a\u55a7\u4e86\u58f0\u602b\u53f7\uff0c\u8d70\u4e0a\u524d\u9053\uff1a\u201c\u6b27\u9633\u65bd\u4e3b\uff0c\u4e8b\u60c5\u6f14\u53d8\u5230\u4eca\u65e5\u5730\u6b65\uff0c\u5b9e\u975e\u59cb\u6599\u6240\u53ca\u3002\u5c31\u8fde\u5c11\u6797\u638c\u95e8\u767e\u660e\u65b9\u4e08\u4e5f\u672a\u60f3\u5230\uff0c\u5426\u5219\u4ed6\u54ea\u4f1a\u8ba9\u6c5f\u8001\u5f1f\u53bb\u627e\u4e86\u2018\u4f20\u771f\u8bb0\u2019\uff1f \u3000\u3000\u4e24\u4f4d\u4ee4\u5a9b\u672c\u6027\u7eaf\u51c0\uff0c\u5929\u8d28\u806a\u6167\uff0c\u5b9e\u4e3a\u4e0d\u53ef\u591a\u5f97\u7684\u7ec3\u6b66\u5947\u624d\u3002\u5979\u4eec\u4e4b\u6240\u4ee5\u9677\u5165\u9b54\u5883\uff0c\u5b8c\u5168\u662f\u8bef\u7ec3\u2018\u4f20\u771f\u8bb0\u2019\u4e0a\u90aa\u672f\u9b54\u529f\u800c\u8d77\uff0c\u4ee5\u81f4\u6108\u9677\u6108\u6df1\uff0c\u96be\u4ee5\u81ea\u62d4\u3002 \u3000\u3000\u5982\u4eca\u4e4b\u8ba1\uff0c\u5fc5\u987b\u5148\u606f\u4e0a\u5979\u4eec\u7684\u6740\u5ff5\uff0c\u518d\u8bbe\u6cd5\u4ece\u9b54\u5883\u4e2d\u62ef\u6551\u51fa\u5979\u4eec\u3002\u6240\u4ee5\u5f97\u8bf7\u5f20\u59d1\u5a18\u914d\u5408\uff0c\u8fd1\u65e5\u4e4b\u5185\uff0c\u795e\u79d8\u6559\u4e3b\u65b9\u9762\u7740\u5927\u4e3e\u6765\u72af\uff0c\u5343\u4e07\u4e0d\u53ef\u6d3e\u5979\u4eec\u6740\u654c\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u70b9\u70b9\u5934\u9053\uff1a\u201c\u6211\u4f1a\u5c3d\u91cf\u4e0d\u4f7f\u5979\u4eec\u6740\u4eba\u89c1\u8840\u7684\uff0c\u4e0d\u8fc7\u3002\u2026\u2026\u201d \u3000\u3000\u7a81\u7136\u6b32\u8a00\u53c8\u4e0a\u8d77\u6765\u3002 \u3000\u3000\u7f57\u5170\u6002\u607f\u9053\uff1a\u201c\u56db\u59d1\uff0c\u4e8b\u5230\u5982\u4eca\uff0c\u4e0d\u5fc5\u6709\u4efb\u4f55\u987e\u5fcc\uff0c\u6709\u8bdd\u5c3d\u7ba1\u76f4\u8bf4\u5427\uff01\u201d \u3000\u3000\u5f20\u56db\u59d1\u8fdf\u7591\u4e86\u4e00\u4e0b\uff0c\u59cb\u90d1\u91cd\u9053\uff1a\u201c\u4e07\u4e00\u5979\u4eec\u4e0d\u542c\u547d\uff0c\u8feb\u4e0d\u5f97\u5df2\u7684\u60c5\u51b5\u4e4b\u4e0b\uff0c\u6211\u53ea\u597d\u51fa\u624b\u6740\u4e86\u5979\u4eec\uff01\u201d \u3000\u3000\u6b64\u8a00\u4e00\u51fa\uff0c\u5176\u4ed6\u51e0\u4eba\u4e0d\u7531\u5730\u4e00\u6014\uff0c\u795e\u60c5\u5927\u53d8\u3002 \u3000\u3000\u53ea\u89c1\u5927\u548c\u5c1a\u53cc\u624b\u5408\u4ec0\uff0c\u53e3\u55a7\u4f5b\u53f7\uff1a\u201c\u963f\u5f25\u9640\u4f5b\uff01\u201d \u3000\u3000\u6c5f\u67ab\u4fca\u7709\u4e00\u76b1\uff0c\u832b\u7136\u4e0d\u77e5\u6240\u63aa\u3002 \u3000\u3000\u7f57\u5170\u773c\u5708\u4e00\u7ea2\uff0c\u6cea\u6c34\u593a\u7736\u800c\u51fa\u3002 \u3000\u3000\u6b27\u9633\u662d\u5219\u662f\u4e49\u65e0\u53cd\u987e\u9053\uff1a\u201c\u56db\u59d1\u770b\u7740\u529e\u5427\uff1f\u6b64\u4e8b\u7531\u4f60\u5168\u6743\u4f5c\u4e3b\uff01\u201d \u3000\u3000\u201c\u4e0d\uff01\u201d\u7f57\u5170\u53eb\u9053\uff1a\u201c\u56db\u59d1\uff0c\u4f60\u53ef\u4ee5\u5e9f\u4e86\u4e24\u4e2a\u4e2b\u5934\u7684\u6b66\u529f\uff0c\u4f46\u4e0d\u80fd\u6740\u5979\u4eec\uff01\u201d \u3000\u3000\u6b27\u9633\u662d\u559d\u65a5\u9053\uff1a\u201c\u5170\u59b9\uff0c\u4f60\u2026\u2026\u201d \u3000\u3000\u201c\u5979\u4eec\u662f\u6211\u4eec\u7684\u5973\u513f\u554a\uff01\u201d\u7f57\u5170\u7a81\u5411\u5f20\u56db\u59d1\u53cc\u819d\u8dea\u4e0b\uff0c\u6ce3\u58f0\u54c0\u6c42\u9053\uff1a\u201c\u56db\u59d1\uff0c\u7b54\u5e94\u6211\u4e0d\u8981\u6740\u5979\u4eec\u2026\u2026\u201d \u3000\u3000\u5f20\u56db\u59d1\u614c\u5fd9\u8e72\u4e0b\uff0c\u53cc\u624b\u8981\u6276\u8d77\u5979\uff1a\u201c\u522b\uff0c\u522b\u8fd9\u6837\u2026\u2026\u201d \u3000\u3000\u7f57\u5170\u6cea\u5982\u96e8\u4e0b\u9053\uff1a\u201c\u4e0d\uff01\u56db\u59d1\uff0c\u4f60\u4e0d\u7b54\u5e94\u6211\uff0c\u6211\u5c31\u957f\u8dea\u4e0d\u8d77\uff01\u201d \u3000\u3000\u6b27\u9633\u662d\u6c14\u5f97\u987f\u8db3\u9053\uff1a\u201c\u5170\u59b9\uff0c\u4f60\u600e\u53ef\u5982\u6b64\uff0c\u771f\u4ee4\u6211\u5927\u5931\u671b\u4e86\uff01\u201d \u3000\u3000\u5f20\u56db\u59d1\u9053\uff1a\u201c\u6709\u8bdd\u7ad9\u8d77\u6765\u8bf4\uff0c\u8fd9\u6837\u6211\u5b9e\u5728\u62c5\u5f53\u4e0d\u8d77\u554a\u3002\u201d \u3000\u3000\u7f57\u5170\u5df2\u6ce3\u4e0d\u6210\u58f0\uff0c\u8fde\u5979\u8bf4\u4ec0\u4e48\u90fd\u65e0\u6cd5\u542c\u6e05\u3002 \u3000\u3000\u6c5f\u67ab\u7ad9\u5728\u4e00\u65c1\u50bb\u4e86\u773c\uff0c\u4e00\u65f6\u4e0d\u77e5\u5982\u4f55\u529d\u6170\u8fd9\u4f4d\u672a\u6765\u7684\u5cb3\u6bcd\u3002 \u3000\u3000\u5f53\u7136\uff0c\u4e07\u4e00\u60c5\u52bf\u6240\u903c\uff0c\u975e\u6740\u5bb3\u96ea\u3001\u971c\u4e24\u59d0\u59b9\u4e0d\u53ef\uff0c\u8fde\u4ed6\u8fd9\u4e2a\u6b27\u9633\u5bb6\u7684\u4e58\u9f99\u5feb\u5a7f\u4e5f\u5f53\u4e0d\u6210\u4e86\u3002 \u3000\u3000\u5f20\u56db\u59d1\u53ea\u597d\u59d4\u5a49\u9053\uff1a\u201c\u7f57\u5170\uff0c\u4f60\u662f\u77e5\u9053\u7684\uff0c\u6211\u5bf9\u4e24\u4e2a\u4e2b\u5934\u7684\u75bc\u7231\uff0c\u4ee5\u53ca\u5bf9\u5979\u4eec\u7684\u611f\u60c5\uff0c\u7edd\u4e0d\u4e9a\u4e8e\u4f60\u8fd9\u505a\u6bcd\u4eb2\u7684\u3002 \u3000\u3000\u9664\u975e\u4e07\u4e0d\u5f97\u5df2\uff0c\u53ea\u8981\u6709\u4e00\u7ebf\u62ef\u6551\u5979\u4eec\u7684\u5e0c\u671b\uff0c\u6211\u7edd\u4e0d\u4f1a\u8f7b\u6613\u653e\u5f03\u3002 \u3000\u3000\u521a\u624d\u4f60\u4eec\u5c1a\u672a\u56de\u6765\u4e4b\u524d\uff0c\u6211\u4eec\u6b63\u5728\u5546\u91cf\uff0c\u51b3\u5fc3\u8981\u5c3d\u4e00\u5207\u53ef\u80fd\u3002 \u3000\u3000\u628a\u4e24\u4e2a\u4e2b\u5934\u4ece\u9b54\u5883\u4e2d\u62ef\u6551\u51fa\u6765\u3002 \u3000\u3000\u771f\u8981\u5230\u4e86\u7edd\u671b\u7684\u4e00\u5929\uff0c\u4e3a\u4e86\u5979\u4eec\uff0c\u4e5f\u4e3a\u4e86\u5929\u4e0b\u6b66\u6797\u7740\u60f3\uff0c\u5982\u679c\u88ab\u8feb\u975e\u4e0b\u624b\u4e0d\u53ef\u3002\u6211\u53ef\u4ee5\u5411\u4f60\u4fdd\u8bc1\uff0c\u6740\u4e86\u5979\u4eec\uff0c\u6211\u7edd\u4e0d\u4f1a\u5077\u751f\uff0c\u5fc5\u5b9a\u966a\u7740\u4e24\u4e2a\u4e2b\u5934\u540c\u8d74\u4e5d\u6cc9\uff01\u201d \u3000\u3000\u8bf4\u5230\u8fd9\u91cc\uff0c\u5f20\u56db\u59d1\u4e5f\u60c5\u4e0d\u81ea\u7981\u54ed\u4e86\u8d77\u6765\u3002 \u3000\u3000\u201c\u56db\u59d1\u2026\u2026\u201d \u3000\u3000\u7f57\u5170\u62b1\u4f4f\u4e86\u5f20\u56db\u59d1\uff0c\u4e24\u4eba\u628a\u5934\u75db\u54ed\u3002 \u3000\u3000\u4e00\u65c1\u7684\u6c5f\u67ab\uff0c\u5927\u548c\u5c1a\u53ca\u6b27\u9633\u662d\u4e5f\u4e3a\u4e4b\u52a8\u5bb9\u3002 \u3000\u3000\u4e0d\u77e5\u4ec0\u4e48\u65f6\u5019\uff0c\u9752\u51e4\u5df2\u6084\u7136\u8fdb\u4e86\u5927\u6bbf\uff0c\u5728\u573a\u7684\u4eba\u7adf\u6d51\u7136\u672a\u89c9\u3002 \u3000\u3000\u5979\u628a\u6c5f\u67ab\u62d6\u5f00\u4e00\u65c1\uff0c\u8f7b\u58f0\u8bf4\u4e86\u51e0\u53e5\u3002 \u3000\u3000\u53ea\u89c1\u6c5f\u5e06\u628a\u5934\u5fae\u5fae\u4e00\u70b9\uff0c\u8d70\u53bb\u62cd\u62cd\u5f20\u56db\u59d1\u7684\u80a9\u5934\uff0c\u9053\uff1a\u201c\u5927\u59d0\uff0c\u9752\u51e4\u6709\u8bdd\u8981\u5355\u72ec\u8ddf\u4f60\u8bf4\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u62ac\u773c\u4e00\u770b\uff0c\u9752\u51e4\u4ecd\u7ad9\u5728\u53f3\u4fa7\u62f1\u95e8\u65c1\u3002 \u3000\u3000\u8fd9\u4f1a\u513f\u9752\u51e4\u7a81\u7136\u6765\u5230\uff0c\u60f3\u5fc5\u662f\u6781\u5176\u91cd\u8981\u7684\u4e8b\uff0c\u5426\u5219\u5979\u5c82\u975e\u5927\u4e0d\u8bc6\u65f6\u52a1\u4e86\u3002 \u3000\u3000\u5f20\u56db\u7ad9\u4fbf\u5411\u7f57\u5170\u529d\u6170\u9053\uff1a\u201c\u7f57\u5170\uff0c\u4e0d\u8981\u96be\u8fc7\uff0c\u5929\u65e0\u7edd\u4eba\u4e4b\u8def\uff0c\u6211\u4eec\u4e00\u5b9a\u4f1a\u60f3\u51fa\u529e\u6cd5\u6765\u7684\u3002\u201d \u3000\u3000\u6276\u8d77\u4e86\u7f57\u5170\uff0c\u5979\u624d\u8d70\u5411\u9752\u51e4\uff0c\u4e24\u4eba\u4e00\u8d77\u8d70\u8fdb\u62f1\u95e8\u3002 \u3000\u3000\u5230\u4e86\u7985\u623f\uff0c\u9752\u51e4\u653e\u4e0b\u5f62\u540c\u865a\u8bbe\uff0c\u53c8\u7834\u53c8\u65e7\u7684\u5e03\u95e8\u5e18\uff0c\u624d\u90d1\u91cd\u5176\u4e8b\u9053\uff1a\u201c\u5f20\u59e8\uff0c\u5176\u5b9e\u4f60\u7528\u4e0d\u7740\u6740\u96ea\u3001\u971c\u4e24\u59d0\u59b9\u7684\u2026\u2026\u201d \u3000\u3000\u201c\u4f60\u90fd\u542c\u89c1\u4e86\uff1f\u201d\u5f20\u56db\u59d1\u6697\u81ea\u4e00\u6014\u3002 \u3000\u3000\u9752\u51e4\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u5f20\u56db\u59d1\u5373\u95ee\uff1a\u201c\u54ea\u4f60\u6709\u4ec0\u4e48\u66f4\u597d\u7684\u4e3b\u610f\uff1f\u201d \u3000\u3000\u9752\u51e4\u732e\u8ba1\u9053\uff1a\u201c\u6211\u4f1a\u914d\u5236\u4e00\u79cd\u836f\uff0c\u670d\u4e0b\u4e4b\u540e\u529f\u529b\u5168\u5931\u3002\u4e00\u65e6\u5e9f\u4e86\u6b66\u529f\uff0c\u5979\u4eec\u5c31\u65e0\u6cd5\u6740\u4eba\u4e86\u3002\u201d \u3000\u3000\u201c\u4e0d\u884c\u554a\uff01\u201d\u5f20\u56db\u59d1\u6447\u5934\u82e6\u7b11\u9053\uff1a\u201c\u73b0\u5728\u4e0d\u662f\u529f\u529b\u7684\u95ee\u9898\uff0c\u800c\u662f\u5979\u4eec\u7684\u672c\u6027\u5df2\u7ecf\u8ff7\u5931\uff0c\u5b8c\u5168\u60c5\u4e0d\u7531\u5df2\u3002\u5373\u4f7f\u5e9f\u4e86\u6b66\u529f\uff0c\u5979\u4eec\u4ecd\u7136\u9677\u5165\u9b54\u5883\uff0c\u53ef\u80fd\u4f1a\u60f3\u51fa\u66f4\u6b8b\u9177\uff0c\u66f4\u6781\u7aef\u7684\u6740\u4eba\u624b\u6bb5\u3002 \u3000\u3000\u51b5\u4e14\u2018\u4f20\u771f\u8bb0\u2019\u4e0a\u7684\u6b66\u529f\uff0c\u5747\u5c5e\u6b6a\u95e8\u90aa\u9053\uff0c\u4e00\u65e6\u7ec3\u6210\uff0c\u5728\u4f53\u5185\u5c31\u5f62\u6210\u4e86\u4e00\u79cd\u81ea\u7136\u6297\u529b\u3002 \u3000\u3000\u5982\u679c\u670d\u4e0b\u4f60\u7684\u836f\uff0c\u60f3\u5fc5\u6c34\u706b\u4e0d\u5bb9\uff0c\u5979\u4eec\u5982\u4f55\u80fd\u627f\u53d7\u5f97\u4e86\uff1f\u4e07\u4e00\u4e0d\u614e\uff0c\u5f53\u573a\u5c31\u53ef\u80fd\u9001\u547d\uff0c\u8fd9\u4e2a\u4e07\u4e07\u4f7f\u4e0d\u5f97\uff0c\u4f7f\u4e0d\u5f97\uff01\u201d \u3000\u3000\u9752\u51e4\u6c89\u541f\u4e00\u4e0b\uff0c\u53c8\u9053\uff1a\u201c\u53e6\u5916\u8fd8\u6709\u4e00\u4e2a\u65b9\u6cd5\uff0c\u53ea\u4e0d\u8fc7\u2026\u2026\u201d\u597d\u4f3c\u4e4e\u8bf4\u4e0d\u51fa\u53e3\uff0c\u6b32\u8a00\u53c8\u6b62\u8d77\u6765\u3002 \u3000\u3000\u5f20\u56db\u59d1\u7b11\u9053\uff1a\u201c\u9752\u51e4\uff0c\u5728\u5f20\u59e8\u9762\u524d\uff0c\u5e72\u561b\u541e\u541e\u5410\u5410\u7684\uff0c\u6709\u4ec0\u4e48\u8bdd\u4e0d\u80fd\u8bf4\uff1f\u51b5\u4e14\uff0c\u8fd9\u662f\u4e3a\u4e86\u6551\u96ea\u3001\u971c\u4e24\u59d0\u59b9\uff0c\u5c31\u7b97\u4f60\u8bf4\u7684\u65b9\u6cd5\u4e0d\u80fd\u7528\uff0c\u5f20\u59e8\u4e5f\u4e0d\u4f1a\u602a\u4f60\u5440\u3002\u201d \u3000\u3000\u9752\u51e4\u5fae\u5fae\u70b9\u4e86\u70b9\u5934\uff0c\u95ee\u9053\uff1a\u201c\u5f20\u59e8\u53ef\u77e5\u9053\u2018\u7d20\u5973\u7ecf\u2019\u8fd9\u90e8\u5947\u4e66\uff1f\u201d \u3000\u3000\u5f20\u56db\u59d1\u5766\u7136\u9053\uff1a\u201c\u4e0d\u4f46\u77e5\u9053\uff0c\u800c\u4e14\u770b\u8fc7\uff0c\u4f60\u95ee\u8fd9\u4e2a\u5e72\u561b\uff1f\u201d \u3000\u3000\u9752\u51e4\u9053\uff1a\u201c\u7d20\u5973\u7ecf\u6709\u4e24\u5377\uff0c\u4e00\u4e3a\u7d20\u5973\u79d8\u9053\u7ecf\uff0c\u4e00\u4e3a\u7d20\u5973\u65b9\u3002\u6211\u4eec\u5929\u4e09\u95e8\u7684\u5973\u5f1f\u5b50\uff0c\u4ece\u5c0f\u5c31\u628a\u5b83\u5217\u4e3a\u7ec3\u529f\u4e60\u6b66\u4e4b\u5916\u5fc5\u4fee\u7684\u8bfe\u4e1a\uff0c\u4ee5\u4e86\u89e3\u7537\u6b22\u5973\u7231\u4e4b\u4e8b\u3002 \u3000\u3000\u4f46\u4e09\u4f4d\u5bab\u4e3b\u5374\u6839\u636e\u7d20\u5973\u7ecf\uff0c\u6df1\u5165\u7814\u7a76\uff0c\u521b\u51fa\u4e86\u53e6\u4e00\u90e8\u79d8\u7ecf\uff0c\u5e76\u4e14\u8eab\u4f53\u529b\u884c\u3002\u5979\u4eec\u4e4b\u6240\u4ee5\u5982\u6b64\uff0c\u4e3a\u7684\u662f\u8981\u85c9\u7531\u5f7c\u6b64\u7684\u7231\u629a\uff0c\u4e0d\u987b\u7537\u4eba\u4ea6\u53ef\u8fbe\u5230\u53d1\u6cc4\u6b32\u706b\u7684\u76ee\u7684\u3002 \u3000\u3000\u4e45\u800c\u4e45\u4e4b\uff0c\u4e00\u65e6\u6df1\u8ff7\u6b64\u9053\uff0c\u5c31\u5982\u540c\u5438\u6bd2\u4e0a\u764c\u4f3c\u7684\uff0c\u5bf9\u5176\u4ed6\u4efb\u4f55\u4e8b\u90fd\u4e0d\u611f\u5174\u8da3\u3002\u4e09\u4f4d\u5bab\u4e3b\u6b64\u756a\u4eb2\u81ea\u51fa\u9a6c\uff0c\u529f\u529b\u4e0d\u5927\u5982\u524d\uff0c\u5f88\u53ef\u80fd\u5c31\u662f\u7ec8\u65e5\u73a9\u8fd9\u2018\u78e8\u955c\u4e4b\u620f\u2019\uff0c\u4ee5\u81f4\u6b66\u529f\u8352\u5e9f\u3002\u5426\u5219\u7684\u8bdd\uff0c\u5979\u4eec\u7edd\u4e0d\u53ef\u80fd\u5982\u6b64\u4e0d\u6d4e\u3002 \u3000\u3000\u6240\u4ee5\uff0c\u6211\u5728\u60f3\uff0c\u5982\u679c\u80fd\u6240\u96ea\u3001\u971c\u4e24\u4f4d\u59b9\u59b9\u5bfc\u4eba\u6b64\u9014\uff0c\u4e0d\u77e5\u662f\u5426\u80fd\u51b2\u6de1\u5979\u4eec\u7684\u6740\u6c14\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u561b\u2026\u2026\u201d\u5f20\u56db\u59d1\u6c89\u601d\u4e86\u7247\u523b\uff0c\u95ee\u9053\uff1a\u201c\u4e09\u4f4d\u5bab\u4e3b\u628a\u5979\u4eec\u7684\u5fc3\u5f97\u4f20\u6388\u4e86\u4f60\uff1f\u201d \u3000\u3000\u9752\u51e4\u8138\u4e0a\u4e00\u7ea2\uff0c\u5a07\u7f9e\u4e07\u72b6\u9053\uff1a\u201c\u5929\u738b\u95e8\u7684\u4f17\u5f1f\u5b50\u4e2d\uff0c\u53ea\u6709\u6211\u548c\u84dd\u51e4\u4e60\u8fc7\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u795e\u60c5\u51dd\u91cd\u9053\uff1a\u201c\u6211\u61c2\u4f60\u7684\u610f\u601d\uff0c\u662f\u60f3\u85c9\u6b64\u8f6c\u79fb\u5979\u4eec\u7684\u2018\u5174\u8da3\u2019\uff0c\u9010\u6e10\u6de1\u5316\u55dc\u6740\u7684\u9b54\u6027\u3002\u8fd9\u4e2a\u65b9\u6cd5\uff0c\u4e0e\u6c5f\u67ab\u6628\u591c\u7528\u7684\u53ef\u8bf4\u662f\u4e0d\u8c0b\u800c\u5408\uff0c\u5f02\u66f2\u540c\u5de5\u3002 \u3000\u3000\u53ef\u662f\uff0c\u6c5f\u67ab\u6628\u591c\u9669\u4e9b\u5f04\u5de7\u6210\u62d9\uff0c\u5f53\u65f6\u6211\u4e5f\u51e0\u4e4e\u6c89\u4e0d\u4f4f\u6c14\uff0c\u82e5\u4e0d\u662f\u5927\u548c\u5c1a\u53ca\u65f6\u4f20\u51fa\u5927\u60b2\u68b5\u97f3\uff0c\u6211\u5dee\u4e00\u70b9\u4e0d\u987e\u4e00\u5207\u95ef\u8fdb\u53bb\u963b\u6b62\u3002 \u3000\u3000\u5982\u679c\u6211\u771f\u95ef\u4e86\u8fdb\u53bb\uff0c\u89e6\u6012\u4e24\u4e2a\u4e27\u5931\u7406\u6027\u7684\u4e2b\u5934\uff0c\u9996\u5f53\u5176\u51b2\u7684\u53ef\u80fd\u5c31\u662f\u6211\u8fd9\u5f20\u59e8\u3002\u73b0\u5728\u56de\u60f3\u8d77\u6765\uff0c\u6211\u4ecd\u6709\u4f59\u60b8\uff0c\u9669\u8087\u5927\u7978\u5462\uff01\u201d \u3000\u3000\u9752\u51e4\u4e0d\u4ee5\u4e3a\u7136\u9053\uff1a\u201c\u5f20\u59e8\uff0c\u4f60\u4e0d\u77e5\u9053\u4e09\u4f4d\u5bab\u4e3b\u90a3\u5957\u529f\u592b\u7684\u5389\u5bb3\uff0c\u90a3\u6bd4\u2018\u5a9a\u672f\u2019\u5f3a\u8fc7\u4f55\u6b62\u5341\u500d\u3002\u5185\u5bab\u7684\u5973\u5f1f\u5b50\uff0c\u5305\u62ec\u6211\u548c\u84dd\u51e4\u5728\u5185\uff0c\u5f53\u521d\u5c31\u662f\u88ab\u5979\u4eec\u7528\u8fd9\u65b9\u6cd5\u63a7\u5236\uff0c\u5fc3\u7518\u60c5\u613f\u53d7\u9a71\u4f7f\uff0c\u751a\u81f3\u4e3a\u5979\u4eec\u5356\u547d\u3002 \u3000\u3000\u521a\u624d\u5728\u5927\u6bbf\u91cc\uff0c\u6211\u89c1\u4e86\u5f20\u59e8\u548c\u7f57\u5170\u524d\u8f88\u62b1\u5934\u75db\u54ed\u7684\u60c5\u5f62\uff0c\u77e5\u9053\u4f60\u4eec\u8c01\u4e5f\u4e0d\u5fcd\u5411\u96ea\u3001\u971c\u4e24\u4f4d\u59b9\u59b9\u4e0b\u624b\u3002\u6240\u4ee5\u624d\u60f3\u5230\uff0c\u4e0d\u59a8\u5e9f\u4e86\u5979\u4eec\u6b66\u529f\uff0c\u6216\u7528\u8fd9\u65b9\u6cd5\u4e00\u8bd5\uff0c\u4e5f\u8bb8\u80fd\u594f\u6548\uff0c\u90a3\u5c31\u4e0d\u5fc5\u975e\u6740\u5979\u4eec\u4e0d\u53ef\u4e86\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u672a\u7f6e\u53ef\u5426\uff0c\u6c89\u541f\u4e86\u4e00\u4e0b\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u6709\u628a\u63e1\u5417\uff1f\u201d \u3000\u3000\u9752\u51e4\u80af\u5b9a\u628a\u5934\u4e00\u70b9\u3002 \u3000\u3000\u5f20\u56db\u59d1\u4e0d\u7531\u5730\u7b11\u65a5\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u4f60\u8fd9\u4e2b\u5934\u8fd8\u85cf\u4e86\u79c1\u5462\uff01\u201d \u3000\u3000\u9752\u51e4\u7a98\u9053\uff1a\u201c\u8fd9\u79cd\u89c1\u4e0d\u5f97\u4eba\u7684\u6280\u827a\uff0c\u96be\u767b\u5927\u96c5\uff0c\u90a3\u6562\u5728\u5f20\u59e8\u9762\u524d\u70ab\u8000\u54ea\u3002\u201d \u3000\u3000\u201c\u8bf4\u7684\u4e5f\u662f\u2026\u2026\u201d\u5f20\u56db\u59d1\u90d1\u91cd\u9053\uff1a\u201c\u8fd9\u4e8b\u5173\u91cd\u5927\uff0c\u53ea\u6709\u5929\u77e5\uff0c\u5730\u77e5\uff0c\u4f60\u77e5\u6211\u77e5\uff0c\u7edd\u4e0d\u80fd\u8ba9\u4efb\u4f55\u7b2c\u4e09\u8005\u77e5\u9053\uff0c\u4e07\u4e00\u4e0d\u6210\uff0c\u4e5f\u514d\u5f97\u843d\u4eba\u7b11\u67c4\u3002\u201d \u3000\u3000\u9752\u51e4\u8fde\u8fde\u5e94\u9053\uff1a\u201c\u6211\u77e5\u9053\uff0c\u6211\u77e5\u9053\uff0c\u5f20\u59e8\u8bf7\u653e\u5fc3\uff0c\u6211\u4f1a\u4e0d\u9732\u75d5\u8ff9\uff0c\u89c1\u673a\u884c\u4e8b\u7684\u3002\u4e07\u4e00\u60c5\u51b5\u4e0d\u5bf9\uff0c\u6211\u4f1a\u7acb\u523b\u60ac\u5d16\u52d2\u9a6c\uff0c\u7edd\u4e0d\u8ba9\u5979\u4eec\u770b\u51fa\u7834\u7efd\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u5fae\u5fae\u70b9\u5934\uff1a\u201c\u5979\u4eec\u56de\u623f\u4e0d\u4e45\uff0c\u4e0d\u77e5\u7761\u4e86\u6ca1\u6709\uff0c\u4f60\u53bb\u770b\u770b\u5427\u3002\u201d \u3000\u3000\u76ee\u9001\u9752\u51e4\u51fa\u4e86\u7985\u623f\uff0c\u5f20\u56db\u59d1\u975e\u4f46\u6ca1\u6709\u611f\u5230\u677e\u4e86\u53e3\u6c14\uff0c\u53cd\u800c\u5fc3\u60c5\u66f4\u6c89\u91cd\u8d77\u6765\u3002 \u3000\u3000\u56e0\u4e3a\uff0c\u5979\u5bf9\u9752\u51e4\u7684\u65b9\u6cd5\uff0c\u5b9e\u5728\u4e0d\u6562\u62b1\u592a\u5927\u5e0c\u671b\u3002 \u3000\u3000\u4f46\u9664\u6b64\u4e4b\u5916\uff0c\u53c8\u6709\u4ec0\u4e48\u66f4\u597d\u7684\u6709\u6548\u65b9\u6cd5\u5462\uff1f \u3000\u3000\u5f20\u56db\u59d1\u832b\u7136\u4e86\u3002 \u3000\u3000\u6362\u73ed\u4e86\uff0c\u62c5\u4efb\u591c\u95f4\u8b66\u6212\u7684\u4e00\u6279\u4eba\u53bb\u4f11\u606f\uff0c\u8f6e\u5230\u4e86\u53e6\u4e00\u6279\u4eba\u6765\u63a5\u66ff\u3002 \u3000\u3000\u8fd9\u6279\u63a5\u73ed\u7684\u4eba\uff0c\u5305\u62ec\u6c5f\u67ab\uff0c\u5f20\u56db\u59d1\uff0c\u6b27\u9633\u662d\u592b\u5987\uff0c\u5927\u548c\u5c1a\uff0c\u9752\u51e4\uff0c\u53ca\u96ea\u3001\u971c\u4e24\u59d0\u59b9\u7b49\u4eba\u3002 \u3000\u3000\u4ece\u4eba\u624b\u7684\u8c03\u6d3e\u53ef\u4ee5\u770b\u51fa\uff0c\u5f20\u56db\u59d1\u4ecd\u4ee5\u539f\u6765\u7684\u7ec4\u5408\u4e3a\u4e3b\u529b\uff0c\u591c\u95f4\u8ba9\u5927\u5bb6\u5c3d\u91cf\u4f11\u606f\uff0c\u517b\u7cbe\u84c4\u9510\uff0c\u4ee5\u5907\u654c\u4eba\u5927\u4e3e\u6765\u72af\u53f3\u5386\u529b\u4e00\u6218\u3002 \u3000\u3000\u81f3\u4e8e\u5f03\u6697\u6295\u660e\u7684\u7533\u96ea\u541b\uff0c\u590f\u5929\u540c\u7b49\u90a3\u6279\u4eba\uff0c\u5f20\u56db\u59d1\u5bf9\u4ed6\u4eec\u4ecd\u7136\u6000\u6709\u6212\u5fc3\uff0c\u6240\u4ee5\u5206\u6d3e\u4ed6\u4eec\u62c5\u4efb\u591c\u95f4\u8b66\u6212\uff0c\u5e76\u4e14\u6307\u6d3e\u8001\u53eb\u5316\u8d1f\u8d23\u5de1\u89c6\uff0c\u4ee5\u9632\u4e07\u4e00\u6709\u53d8\u3002 \u3000\u3000\u84dd\u51e4\u5219\u662f\u7559\u4e0b\u7684\u4e00\u6b65\u6697\u68cb\uff0c\u5982\u4eca\u5979\u867d\u5df3\u6295\u8bda\uff0c\u4f46\u795e\u79d8\u6559\u4e3b\u5c1a\u4e0d\u77e5\u60c5\u3002 \u3000\u3000\u5979\u662f\u88ab\u795e\u79d8\u6559\u4e3b\u5229\u7528\uff0c\u5949\u547d\u6765\u4f20\u8bdd\uff0c\u65e8\u5728\u8bf1\u6c5f\u67ab\u4e2d\u8ba1\u7684\u3002 \u3000\u3000\u6240\u4ee5\uff0c\u795e\u79d8\u6559\u4e3b\u53ef\u80fd\u4ee5\u4e3a\u5979\u88ab\u6293\uff0c\u5fc5\u8981\u65f6\u53ef\u4ee5\u5c06\u8ba1\u5c31\u8ba1\uff0c\u8ba9\u5979\u88c5\u6210\u4ece\u8fd9\u91cc\u9003\u51fa\uff0c\u9003\u56de\u795e\u79d8\u6559\u4e3b\u90a3\u8fb9\uff0c\u8bf4\u4e0d\u5b9a\u80fd\u6d3e\u4e0a\u5927\u7528\u573a\u5462\u3002 \u3000\u3000\u7531\u6b64\u53ef\u89c1\uff0c\u5f20\u56db\u59d1\u4e0d\u6127\u662f\u5973\u4e2d\u8c6a\u6770\uff0c\u786e\u5b9e\u5177\u6709\u8fd0\u7b79\u5e37\u5e44\u7684\u5927\u5c06\u4e4b\u98ce\u3002 \u3000\u3000\u6b64\u523b\u5979\u6b63\u4e0e\u6c5f\u67ab\u4e24\u4eba\u4e00\u7ec4\uff0c\u5728\u8ddd\u53e4\u5239\u4e00\u91cc\u5916\u65b9\u5706\u4e4b\u5185\u5de1\u89c6\u3002 \u3000\u3000\u4f46\u6240\u7ecf\u4e4b\u5904\uff0c\u5e76\u672a\u53d1\u73b0\u654c\u8e2a\u3002 \u3000\u3000\u6c5f\u67ab\u5ffd\u95ee\uff1a\u201c\u5927\u59d0\uff0c\u4ed6\u4eec\u4f1a\u4e0d\u4f1a\u77e5\u96be\u800c\u9000\uff0c\u6628\u591c\u65e9\u5df2\u8fdc\u9053\u4e86\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u6447\u6447\u5934\u9053\uff1a\u201c\u4e0d\u592a\u53ef\u80fd\uff01\u8bd5\u60f3\uff0c\u4f60\u662f\u90a3\u795e\u79d8\u6559\u4e3b\u7684\u5fc3\u8179\u4e4b\u60a3\uff0c\u773c\u4e2d\u4e4b\u9489\uff0c\u4e00\u65e6\u672a\u80fd\u7f6e\u4f60\u4e8e\u6b7b\u5730\uff0c\u4ed6\u5c31\u4e00\u65e5\u5bdd\u98df\u96be\u5b89\u3002\u6628\u591c\u8d39\u5c3d\u5fc3\u673a\uff0c\u4ecd\u672a\u628a\u4f60\u70b8\u6b7b\uff0c\u4ed6\u600e\u4f1a\u7518\u5fc3\u5c31\u6b64\u7f62\u624b\u5462\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u2026\u2026\u201d\u6c5f\u67ab\u7709\u5934\u4e00\u76b1\uff0c\u56f0\u60d1\u9053\uff1a\u201c\u4ed6\u4eec\u4e3a\u4ec0\u4e48\u6beb\u65e0\u52a8\u9759\uff1f \u3000\u3000\u81f3\u5c11\u5e94\u8be5\u6709\u4eba\u5728\u9644\u8fd1\u4e00\u5e26\u76d1\u89c6\u6211\u4eec\u5440\uff01\u201d \u3000\u3000\u5f20\u56db\u59d1\u8038\u8038\u80a9\u9053\uff1a\u201c\u8fd9\u4f4d\u795e\u79d8\u6559\u4e3b\uff0c\u5b9e\u5728\u4ee4\u4eba\u83ab\u6d4b\u9ad8\u6df1\u2026\u2026\u201d \u3000\u3000\u201c\u66dd\u4e86\u5149\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u63a5\u9053\uff1a\u201c\u8fde\u84dd\u51e4\u4e5f\u4e0d\u80fd\u7b97\u4e0a\u4e86\u3002\u201d \u3000\u3000\u201c\u4e0d\uff01\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u5230\u76ee\u524d\u4e3a\u6b62\uff0c\u795e\u79d8\u6559\u4e3b\u53ef\u80fd\u8fd8\u641e\u4e0d\u6e05\uff0c\u84dd\u51e4\u7a76\u7adf\u662f\u88ab\u6293\uff0c\u8fd8\u662f\u88ab\u6211\u4eec\u6740\u4e86\uff0c\u624d\u7531\u4f60\u4e54\u626e\u5979\u56de\u53bb\u590d\u547d\u3002\u4e0d\u8fc7\uff0c\u6709\u4e00\u70b9\u662f\u53ef\u4ee5\u786e\u5b9a\u7684\uff0c\u4f60\u65e2\u80fd\u627e\u5230\u90a3\u5ea7\u5bc6\u6797\uff0c\u4ed6\u4e00\u5b9a\u4ee5\u4e3a\u84dd\u51e4\u662f\u53d7\u4e86\u9177\u5211\uff0c\u88ab\u903c\u4f9b\u8bf4\u51fa\u4e86\u4ed6\u4eec\u7684\u85cf\u8eab\u4e4b\u5904\u3002\u201d \u3000\u3000\u6c5f\u67ab\u5374\u6301\u4e0d\u540c\u7684\u770b\u6cd5\uff1a\u201c\u96be\u9053\u4ed6\u60f3\u4e0d\u5230\uff0c\u53ef\u80fd\u662f\u88ab\u84dd\u51e4\u51fa\u5356\u4e86\uff1f\u201d \u3000\u3000\u5f20\u56db\u59d1\u5224\u65ad\u9053\uff1a\u201c\u4e0d\u592a\u53ef\u80fd\uff0c\u84dd\u51e4\u8ddf\u9752\u51e4\u4e0d\u540c\uff0c\u5979\u4e00\u5411\u5c45\u4e8e\u5e55\u540e\uff0c\u4e0d\u50cf\u9752\u51e4\u629b\u5934\u9732\u9762\u4ee5\u6b4c\u5973\u59ff\u6001\u51fa\u73b0\uff0c\u6240\u4ee5\u795e\u79d8\u6559\u4e3b\u60f3\u4e0d\u5230\u5979\u4f1a\u4e0e\u4f60\u53d1\u751f\u63a5\u89e6\u3002 \u3000\u3000\u5426\u5219\u7684\u8bdd\uff0c\u6628\u591c\u4ed6\u54ea\u4f1a\u6d3e\u51fa\u84dd\u51e4\u6765\u4f20\u8bdd\u3002\u201d \u3000\u3000\u6c5f\u67ab\u70b9\u70b9\u5934\u9053\uff1a\u201c\u5927\u59d0\u8bf4\u7684\u5f88\u6709\u9053\u7406\uff0c\u5173\u4e8e\u8fd9\u70b9\uff0c\u6628\u591c\u6211\u4e5f\u60f3\u5230\u3002\u795e\u79d8\u6559\u4e3b\u6d3e\u84dd\u51e4\u6765\u89c1\u6211\u4eec\uff0c\u5927\u6982\u7b97\u5b9a\u6211\u4eec\u5fc5\u7136\u4f1a\u8ddf\u8e2a\u5979\uff0c\u800c\u4e14\u8ddf\u8e2a\u7684\u4eba\u5f88\u53ef\u80fd\u5c31\u662f\u6211\u3002\u6240\u4ee5\u5e03\u4e0b\u9677\u9631\uff0c\u7b49\u6211\u53bb\u81ea\u6295\u7f57\u7f51\u3002 \u3000\u3000\u60f3\u4e0d\u5230\u6211\u4eec\u4e0d\u662f\u8ddf\u8e2a\uff0c\u800c\u662f\u4e54\u626e\u6210\u84dd\u98ce\uff0c\u8fd9\u70b9\u4f3c\u4e4e\u51fa\u4e4e\u4ed6\u610f\u6599\u4e4b\u5916\u3002 \u3000\u3000\u5f53\u65f6\u4ed6\u81ea\u4f5c\u806a\u660e\u4ee5\u4e3a\u4e54\u626e\u84dd\u51e4\u7684\u662f\u4f60\uff0c\u7ed3\u679c\u66f4\u51fa\u4e4e\u610f\u6599\uff0c\u90a3\u4e2a\u84dd\u51e4\u7adf\u7136\u662f\u6211\uff01\u201d \u3000\u3000\u5fcd\u4e0d\u4f4f\u54c8\u54c8\u4e00\u7b11\uff0c\u6c5f\u67ab\u63a5\u4e0b\u53bb\u53c8\u8bf4\uff1a\u201c\u7531\u6b64\u53ef\u89c1\uff0c\u4ed6\u5bf9\u84dd\u98ce\u5c1a\u672a\u6000\u7591\uff0c\u5426\u5219\u5c31\u4e0d\u4f1a\u6d3e\u5979\u51fa\u9a6c\u4e86\u3002 \u3000\u3000\u4f46\u6211\u6709\u4e00\u70b9\u60f3\u4e0d\u901a\uff0c\u5f53\u65f6\u6211\u5df2\u9732\u51fa\u672c\u6765\u771f\u9762\u76ee\uff0c\u5c1a\u672a\u8ddf\u90a3\u5192\u5145\u795e\u79d8\u6559\u4e3b\u7684\u4f8f\u5112\u51b2\u7a81\uff0c\u5982\u679c\u4ed6\u6df7\u5728\u90a3\u6279\u9ed1\u8863\u5200\u624b\u4e2d\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u7b49\u4eb2\u773c\u770b\u5230\u6211\u88ab\u70b8\u6b7b\uff0c\u5c31\u7387\u4f17\u5306\u5306\u64a4\u8d70\uff1f\u201d \u3000\u3000\u5f20\u56db\u59d1\u7b11\u65a5\u9053\uff1a\u201c\u5c0f\u60c5\u90ce\uff0c\u4f60\u771f\u662f\u806a\u660e\u4e00\u4e16\uff0c\u7cca\u6d82\u4e00\u65f6\u54e6\uff01\u201d \u3000\u3000\u201c\u600e\u4e48\u8bf4\uff1f\u201d\u6c5f\u67ab\u6014\u4e86\u6014\u3002 \u3000\u3000\u5f20\u56db\u59d1\u5206\u6790\u9053\uff1a\u201c\u4f60\u60f3\u770b\uff0c\u5728\u90a3\u4e48\u5f3a\u70c8\u7206\u70b8\u7684\u5a01\u529b\u8303\u56f4\u4e4b\u5185\uff0c\u8c01\u80fd\u6d3b\u547d\uff1f\u6240\u4ee5\u4ed6\u4e0d\u5fc5\u4eb2\u773c\u89c1\u5230\uff0c\u5df2\u8ba4\u5b9a\u4e86\u4f60\u5fc5\u6b7b\u65e0\u7591\uff0c\u6ca1\u60f3\u5230\u4f60\u547d\u5927\uff0c\u7adf\u7136\u80fd\u6b7b\u91cc\u9003\u751f\u3002 \u3000\u3000\u53e6\u4e00\u4e2a\u539f\u56e0\uff0c\u662f\u4ed6\u4f30\u8ba1\u4f60\u7edd\u4e0d\u4f1a\u53ea\u8eab\u6d89\u9669\uff0c\u6797\u5916\u5fc5\u5b9a\u6709\u4eba\u8ddf\u6765\u63a5\u5e94\uff0c\u65e2\u7136\u8ba4\u4e3a\u4f60\u5df2\u6b7b\u5b9a\u4e86\uff0c\u5176\u4ed6\u4eba\u5df2\u4e0d\u8db3\u4e3a\u60e7\uff0c\u4e3a\u4e86\u4fdd\u5b58\u5b9e\u529b\uff0c\u4ee5\u514d\u9020\u6210\u91cd\u5927\u4f24\u4ea1\uff0c\u635f\u4e86\u4ed6\u7684\u5143\u6c14\uff0c\u53c8\u4f55\u5fc5\u6025\u4e8e\u4e00\u65f6\u8ddf\u6211\u4eec\u786c\u62fc\u5462\uff1f \u3000\u3000\u4e5f\u8bb8\u9664\u4f60\u4e4b\u5916\uff0c\u795e\u79d8\u6559\u4e3b\u5bf9\u96ea\u3001\u971c\u4e24\u59d0\u59b9\u4e5f\u6709\u6240\u5fcc\u60ee\uff0c\u4e0b\u4e00\u6b21\u7684\u76ee\u6807\u5c31\u662f\u5979\u4eec\u4e86\u3002\u201d \u3000\u3000\u4e00\u542c\u63d0\u5230\u96ea\u971c\u4e24\u59d0\u59b9\uff0c\u6c5f\u67ab\u7684\u7709\u5934\u53c8\u76b1\u8d77\u6765\u3002 \u3000\u3000\u5f20\u56db\u59d1\u529d\u6170\u9053\uff1a\u201c\u5409\u4eba\u81ea\u6709\u5929\u76f8\uff0c\u4e0d\u7528\u592a\u4e3a\u5979\u4eec\u803d\u5fc3\u3002\u201d \u3000\u3000\u6c5f\u67ab\u5ffd\u95ee\uff1a\u201c\u5927\u59d0\uff0c\u4e07\u4e0d\u5f97\u5df2\u65f6\uff0c\u4f60\u771f\u5fcd\u5fc3\u5411\u5979\u4eec\u4e0b\u624b\u5417\uff1f\u201d \u3000\u3000\u5f20\u56db\u59d1\u5f3a\u81ea\u4e00\u7b11\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u60f3\u628a\u547d\u8d54\u4e0a\u554a\uff01\u201d \u3000\u3000\u6c5f\u67ab\u4e0d\u613f\u81ea\u5bfb\u70e6\u607c\uff0c\u8d76\u7d27\u8f6c\u79fb\u8bdd\u9898\uff1a\u201c\u5927\u59d0\uff0c\u4f60\u548b\u591c\u4e0d\u662f\u8bf4\uff0c\u5bf9\u65b9\u5f88\u53ef\u80fd\u91c7\u7528\u81ea\u6740\u5f0f\u7684\u653b\u51fb\uff0c\u6307\u6d3e\u4e00\u6279\u6b7b\u58eb\u6bcf\u4eba\u8eab\u4e0a\u7ed1\u4e0a\u70b8\u836f\uff0c\u8ddf\u6211\u4eec\u4e00\u4e2a\u62fc\u4e00\u4e2a\u5417\uff1f\u201d \u3000\u3000\u201c\u4f46\u662f\u4e0d\u80fd\u4e0d\u9632\u3002\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u6628\u591c\u8001\u53eb\u5316\u51fa\u4e86\u4e2a\u4e3b\u610f\uff0c\u6211\u8ba4\u4e3a\u5012\u4e0d\u59a8\u4e00\u8bd5\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u8feb\u4e0d\u53ca\u5f85\u5730\u95ee\uff1a\u201c\u4ec0\u4e48\u4e3b\u610f\uff1f\u201d \u3000\u3000\u5f20\u56db\u59d1\u9053\uff1a\u201c\u4ed6\u4e3b\u5f20\u7531\u6211\u7528\u901f\u6210\u6cd5\uff0c\u6559\u4f1a\u6240\u6709\u4eba\u51e0\u79cd\u7b80\u6613\u9041\u6cd5\u6280\u827a\uff0c\u57cb\u4f0f\u5728\u53e4\u5e99\u56db\u5468\uff0c\u4e00\u89c1\u654c\u4eba\u51fa\u73b0\uff0c\u5c31\u4ee5\u8fdc\u8ddd\u79bb\u6697\u5668\u5c04\u6740\uff0c\u6839\u672c\u4e0d\u8ba9\u4ed6\u4eec\u4efb\u4f55\u4eba\u6709\u673a\u4f1a\u63a5\u8fd1\u6211\u4eec\u3002 \u3000\u3000\u8fd9\u6837\u4e00\u6765\uff0c\u4ed6\u4eec\u7684\u6562\u6b7b\u961f\u5c31\u5931\u53bb\u4f5c\u7528\uff0c\u541f\u96ea\u3001\u971c\u4e24\u59d0\u59b9\u4e5f\u4e0d\u5fc5\u4e0a\u9635\u51fa\u624b\u6740\u654c\uff0c\u4e0d\u662f\u4e24\u5168\u5176\u7f8e\u5417\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u8fd9\u5012\u786e\u5b9e\u662f\u4e2a\u53ef\u884c\u4e4b\u8ba1\uff0c\u4e0d\u8fc7\uff0c\u8981\u963b\u6b62\u96ea\u971c\u4e24\u59d0\u59b9\u51fa\u624b\u6740\u654c\uff0c\u6050\u6015\u5979\u4eec\u4e0d\u4f1a\u542c\u2026\u2026\u201d \u3000\u3000\u5f20\u56db\u59d1\u7b11\u4e86\u7b11\uff0c\u80f8\u6709\u6210\u7af9\u9053\uff1a\u201c\u8fd9\u4e2a\u5fc3\u7531\u6211\u6765\u64cd\u5427\u3002\u201d \u3000\u3000\u6c5f\u67ab\u5fd9\u95ee\uff1a\u201c\u5927\u59d0\u6709\u4ec0\u4e48\u5999\u65b9\uff1f\u201d \u3000\u3000\u5f20\u56db\u59d1\u4e0d\u4fbf\u6cc4\u9732\u9752\u51e4\u7684\u732e\u8ba1\uff0c\u6545\u610f\u5356\u8d77\u5173\u5b50\u6765\uff0c\u7b11\u9053\uff1a\u201c\u65f6\u673a\u672a\u5230\uff0c\u5929\u673a\u4e0d\u53ef\u6cc4\u9732\u3002\u201d \u3000\u3000\u8fd9\u65f6\u4eec\u5df2\u4e0d\u77e5\u4e0d\u89c9\u8d70\u4e86\u5f88\u8fdc\uff0c\u8ddd\u79bb\u53e4\u5e99\u81f3\u5c11\u4e09\u91cc\u4e4b\u5916\uff0c\u5230\u4e86\u4e00\u7247\u7af9\u6797\u524d\u3002 \u3000\u3000\u6c5f\u67ab\u7a81\u7136\u6b62\u6b65\uff0c\u4f38\u624b\u62c9\u4e86\u5f20\u56db\u59d1\u4e00\u628a\uff0c\u8f7b\u58f0\u9053\uff1a\u201c\u5927\u59d0\uff0c\u8fd9\u7247\u7af9\u6797\u53ef\u66fe\u6d3e\u4eba\u641c\u7d22\u8fc7\uff1f\u201d \u3000\u3000\u5f20\u56db\u59d1\u6ca1\u6709\u7b54\u8bdd\uff0c\u5411\u7af9\u6797\u6ce8\u89c6\u4e86\u7247\u523b\uff1b\u5ffd\u9053\uff1a\u201c\u602a\u4e8b\uff0c\u8fd9\u4e00\u5e26\u6211\u5de1\u89c6\u8fc7\u4e0d\u6b62\u4e24\u4e09\u904d\uff0c\u597d\u50cf\u4e0d\u8bb0\u5f97\u6709\u8fd9\u4e00\u7247\u7af9\u6797\u5440\uff01\u201d \u3000\u3000\u6c5f\u67ab\u7b11\u9053\uff1a\u201c\u96be\u9053\u5b83\u662f\u7a81\u7136\u5192\u51fa\u6765\u7684\uff1f\u2018\u4e8c\u5341\u56db\u5b5d\u2019\u4e2d\uff0c\u5b5d\u5b50\u53ea\u80fd\u54ed\u51fa\u7af9\u7b0b\uff0c\u54ed\u4e0d\u51fa\u4e00\u5927\u7247\u7af9\u6797\u54e6\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u6b63\u8272\u9053\uff1a\u201c\u6211\u8ddf\u4f60\u8bf4\u6b63\u7ecf\u7684\uff01\u201d \u3000\u3000\u6b64\u523b\u6ca1\u6709\u7b2c\u4e09\u8005\u5728\u573a\uff0c\u6c5f\u67ab\u5df2\u4e0d\u5fc5\u987e\u5fcc\uff0c\u4fcf\u76ae\u5730\u7b11\u9053\uff1a\u201c\u6211\u4e0d\u6b63\u7ecf\u4e86\u5417\uff1f\u201d \u3000\u3000\u5f20\u56db\u59d1\u6ca1\u6709\u5fc3\u60c5\u8ddf\u4ed6\u6253\u60c5\u9a82\u4fcf\uff0c\u77aa\u4e86\u4ed6\u4e00\u773c\uff0c\u901e\u5411\u7af9\u6797\u8d70\u53bb\u3002 \u3000\u3000\u6c5f\u67ab\u8ba8\u4e86\u4e2a\u6ca1\u8da3\uff0c\u53ea\u597d\u5c34\u5c2c\u5730\u7b11\u7b11\uff0c\u8ddf\u7740\u8d70\u81f3\u6797\u524d\u3002 \u3000\u3000\u5f20\u56db\u59d1\u4e00\u5411\u8c28\u614e\u5c0f\u5fc3\uff0c\u4e0d\u6562\u5192\u7136\u8fdb\u5165\u6797\u5185\uff0c\u8e72\u4e0b\u4ed4\u7ec6\u89c2\u5bdf\u4e86\u7247\u523b\uff0c\u8f7b\u58f0\u60ca\u9053\uff1a\u201c\u4f60\u770b\uff0c\u8fd9\u4e9b\u9752\u7af9\u662f\u4e0d\u4e45\u524d\u624d\u690d\u5165\u571f\u4e2d\u7684\uff01\u201d \u3000\u3000\u201c\u54e6\uff1f\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u4e0d\u7981\u4e00\u6014\uff0c\u6025\u5fd9\u8e72\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u4ed4\u7ec6\u4e00\u770b\uff0c\u7af9\u6839\u5468\u56f4\u867d\u7136\u57cb\u5f97\u6781\u5e73\u6574\uff0c\u4ecd\u53ef\u4ece\u7ffb\u51fa\u7684\u65b0\u571f\u8272\u6cfd\uff0c\u770b\u51fa\u9752\u7af9\u521a\u690d\u5165\u571f\u58e4\u4e2d\u4e0d\u4e45\u3002 \u3000\u3000\u751a\u81f3\u53ef\u80fd\u662f\u6628\u591c\u624d\u690d\u5165\uff01 \u3000\u3000\u8fd9\u4e00\u7247\u7af9\u6797\uff0c\u9ad8\u8fdc\u4e24\u4e09\u4e08\u7684\u9752\u7af9\uff0c\u4e3a\u6570\u81f3\u5c11\u5728\u5343\u682a\u4ee5\u4e0a\u3002\u4e00\u591c\u4e4b\u95f4\u8981\u4ece\u522b\u5904\u79fb\u690d\u8fc7\u6765\uff0c\u7b80\u76f4\u4e0d\u53ef\u601d\u8bae\u3002 \u3000\u3000\u66f4\u4ee4\u4eba\u4e0d\u89e3\u7684\uff0c\u662f\u4ec0\u4e48\u4eba\u8fd9\u6837\u505a\uff0c\u76ee\u7684\u4f55\u5728\uff1f \u3000\u3000\u6c5f\u67ab\u8fde\u770b\u4e86\u5341\u51e0\u682a\uff0c\u60c5\u5f62\u5b8c\u5168\u4e00\u6837\uff0c\u4e0d\u7981\u60ca\u8be7\u9053\uff1a\u201c\u5927\u59d0\uff0c\u4f60\u770b\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d \u3000\u3000\u5f20\u56db\u59cb\u6ca1\u6709\u56de\u7b54\uff0c\u7ad9\u8d77\u6765\u89c2\u5bdf\u4e86\u4e00\u9635\uff0c\u53c8\u6c89\u601d\u7247\u523b\u59cb\u9053\uff1a\u201c\u5514\u2026\u2026\u770b\u6765\u5f88\u50cf\u662f\u4e00\u5ea7\u5947\u95e8\u9635\uff01\u201d \u3000\u3000\u6c5f\u67ab\u7b11\u9053\uff1a\u201c\u5927\u59d0\uff0c\u6211\u5bf9\u5e03\u9635\u867d\u4e0d\u7cbe\u901a\uff0c\u4f46\u6211\u4e49\u7236\u91d1\u4e39\u4e66\u751f\u5bf9\u6b64\u9887\u6709\u7814\u7a76\uff0c\u4e5f\u66fe\u6559\u6211\u5947\u95e8\u6613\u6570\u4e4b\u5b66\uff0c\u53ef\u60dc\u6211\u5fd7\u4e0d\u5728\u6b64\uff0c\u540c\u65f6\u53c8\u8981\u8ddf\u59e8\u5a18\u5929\u72d0\u592b\u4eba\uff0c\u5e08\u7236\u65e0\u76f8\u5927\u5e08\u5b66\u827a\uff0c\u4ee5\u81f4\u5bf9\u5947\u95e8\u6613\u6570\u3001\u5e03\u9635\u4e4b\u5b66\u4ec5\u5b66\u5f97\u70b9\u76ae\u6bdb\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u81f3\u5c11\u6211\u51ed\u8fd9\u70b9\u76ae\u6bdb\uff0c\u4e5f\u77e5\u9053\u4f4f\u4f55\u4e00\u79cd\u5947\u9635\uff0c\u90fd\u662f\u6309\u4e5d\u5bab\uff0c\u516b\u5366\uff0c\u4e94\u884c\u6f14\u53d8\u5e03\u8bbe\u800c\u6210\u3002 \u3000\u3000\u5c24\u5176\u662f\u5947\u95e8\u4e94\u884c\u9635\uff0c\u4ec5\u987b\u4e00\u4e9b\u77f3\u5757\u53ca\u7af9\u679d\u5c31\u53ef\u5e03\u6210\uff0c\u786e\u5b9e\u7384\u5947\u65e0\u6bd4\uff0c\u4ee4\u4eba\u4e0d\u53ef\u601d\u8bae\u3002 \u3000\u3000\u4f46\u65e0\u8bba\u4f55\u95e8\u4f55\u6d3e\uff0c\u4efb\u4f55\u4e00\u79cd\u5947\u9635\u90fd\u6709\u4e2a\u5171\u540c\u4e4b\u5904\uff0c\u90a3\u5c31\u662f\u8868\u9762\u7edd\u5bf9\u8ba9\u4eba\u770b\u4e0d\u51fa\u5f02\u72b6\uff0c\u770b\u4e0d\u900f\u7384\u673a\uff0c\u66f4\u770b\u4e0d\u51fa\u51f6\u9669\u3002\u4e00\u8fdb\u5165\u9635\u5185\uff0c\u7acb\u65f6\u4e0e\u9635\u5916\u6240\u89c1\u60c5\u666f\u5c31\u6709\u5929\u58e4\u4e4b\u522b\uff0c\u8bf4\u7a7f\u4e86\u5b8c\u5168\u662f\u4e00\u79cd\u9519\u89c9\u6240\u9020\u6210\u3002 \u3000\u3000\u539f\u6765\u7684\u51e0\u5806\u77f3\u5757\uff0c\u51e0\u679d\u7af9\u679d\uff0c\u7a81\u7136\u4e4b\u95f4\u53d8\u6210\u4e86\u5d07\u5c71\u5cfb\u5cad\uff0c\u60ac\u5d16\u7edd\u58c1\uff0c\u4ee5\u53ca\u4e0d\u89c1\u5929\u65e5\u7684\u539f\u59cb\u68ee\u6797\uff0c\u4f7f\u4eba\u88ab\u56f0\u5728\u5176\u4e2d\uff0c\u65e0\u6cd5\u627e\u5230\u51fa\u8def\u3002 \u3000\u3000\u7eb5\u7136\u8eab\u6000\u7edd\u4e16\u6b66\u529f\uff0c\u56f0\u5728\u9635\u4e2d\u4e5f\u65bd\u5c55\u4e0d\u51fa\u3002\u5341\u5929\u516b\u65e5\u4e0b\u6765\uff0c\u5373\u4f7f\u4e0d\u88ab\u9965\u6e34\u800c\u6b7b\uff0c\u6050\u6015\u4e5f\u4f1a\u53d1\u72c2\u4e86\u3002 \u3000\u3000\u800c\u773c\u524d\u8fd9\u5ea7\u7af9\u6797\u5374\u4e0d\u540c\uff0c\u9752\u7af9\u4f55\u6b62\u5343\u682a\uff0c\u5df2\u7136\u6210\u6797\uff0c\u5929\u4e0b\u54ea\u6709\u8fd9\u79cd\u5e03\u9635\u4e4b\u6cd5\u7684\uff1f\u201d \u3000\u3000\u5f20\u56db\u59d1\u6ca1\u6709\u6253\u5c94\uff0c\u4e00\u76f4\u542c\u4ed6\u4e00\u53e3\u6c14\u8bf4\u5b8c\uff0c\u624d\u7b11\u9053\uff1a\u201c\u5c0f\u60c5\u90ce\uff0c\u8c22\u8c22\u4f60\u66ff\u6211\u4e0a\u4e86\u4e00\u8bfe\uff0c\u4f7f\u6211\u8305\u585e\u987f\u5f00\u3002\u5c31\u7b97\u91d1\u4e39\u4e66\u751f\u4eb2\u81ea\u6388\u4e1a\uff0c\u5927\u6982\u4e5f\u4e0d\u8fc7\u5982\u6b64\uff0c\u53ef\u662f\uff0c\u626e\u732a\u5403\u8001\u864e\u8fd9\u53e5\u8bdd\uff0c\u4e0d\u77e5\u4f60\u542c\u8fc7\u6ca1\u6709\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u6014\u4e86\u6014\uff0c\u832b\u7136\u9053\uff1a\u201c\u6211\u4e0d\u61c2\u4f60\u7684\u610f\u601d\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u9053\uff1a\u201c\u6211\u8fd9\u4e2a\u6bd4\u55bb\u4e0d\u7b97\u5f88\u6070\u5f53\uff0c\u4f46\u5374\u662f\u4e00\u9488\u89c1\u8840\u3002\u5c31\u62ff\u4f60\u6765\u8bf4\u5427\uff01\u8ba4\u5b9a\u8fd9\u7247\u7af9\u6797\u4e0d\u53ef\u80fd\u662f\u4e2a\u5947\u9635\uff0c\u5426\u5219\u5c31\u662f\u5929\u4e0b\u7b2c\u4e00\u5927\u7b28\u86cb\u5e03\u8bbe\u7684\u3002\u5176\u5b9e\uff0c\u5bf9\u65b9\u4e0d\u4f46\u662f\u5e03\u9635\u884c\u5bb6\uff0c\u800c\u4e14\u66f4\u662f\u6df1\u8bf8\u5175\u6cd5\u2018\u653b\u5fc3\u4e3a\u4e0a\u2019\u4e4b\u5b66\u7684\u9ad8\u4eba\u3002 \u3000\u3000\u8bda\u5982\u4f60\u6240\u8bf4\uff0c\u5947\u95e8\u7af9\u77f3\u9635\u4ec5\u987b\u4e00\u4e9b\u77f3\u5757\u548c\u7af9\u679d\u5373\u80fd\u5e03\u6210\uff0c\u4e0d\u5fc5\u5927\u8d39\u5468\u6298\uff0c\u6240\u4ee5\u4f60\u4e00\u89c1\u8fd9\u7247\u7af9\u6797\uff0c\u7edd\u4e0d\u8ba4\u4e3a\u5b83\u662f\u4e2a\u5947\u9635\uff0c\u5c82\u4e0d\u7b49\u4e8e\u88ab\u626e\u732a\u7684\u5403\u4e86\u4f60\u8fd9\u53ea\u8001\u864e\u3002\u201d \u3000\u3000\u201c\u5514\uff0c\u597d\u50cf\u6709\u70b9\u9053\u7406\u2026\u2026\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u4f46\u4f60\u53c8\u6839\u636e\u4ec0\u4e48\uff0c\u8ba4\u5b9a\u8fd9\u662f\u4e2a\u5947\u9635\u5462\uff1f\u201d \u3000\u3000\u5f20\u56db\u59d1\u6307\u7740\u7af9\u6797\u9053\uff1a\u201c\u4f60\u4ed4\u7ec6\u770b\uff0c\u8fd9\u7247\u7af9\u6797\u770b\u4f3c\u53c8\u5bc6\u53c8\u4e71\uff0c\u53c2\u5dee\u4e0d\u9f50\uff0c\u4f46\u5176\u4e2d\u6697\u85cf\u4e5d\u5bab\u516b\u5366\u7384\u673a\uff0c\u5229\u7528\u9644\u8fd1\u5176\u4ed6\u9752\u7af9\u52a0\u4ee5\u63a9\u9970\uff0c\u4f7f\u4eba\u4e0d\u6613\u5bdf\u89c9\u51fa\u6765\u3002 \u3000\u3000\u5982\u679c\u6211\u6ca1\u770b\u8d70\u9650\uff0c\u4e0d\u4fe1\u7684\u8bdd\uff0c\u6211\u4eec\u53ea\u8981\u4e00\u8fdb\u5165\u6797\u5185\uff0c\u773c\u524d\u7684\u4e00\u5207\u7acb\u65f6\u4f1a\u751f\u53d8\u5316\uff0c\u5b8c\u5168\u4e0d\u662f\u6211\u4eec\u73b0\u5728\u6240\u770b\u5230\u7684\u60c5\u666f\u4e86\u3002\u201d \u3000\u3000\u6c5f\u67ab\u773c\u5149\u4e00\u626b\uff0c\u9894\u9996\u9053\uff1a\u201c\u6ca1\u9519\uff0c\u5c24\u5176\u8fd9\u7247\u7af9\u6797\u7684\u4f4d\u7f6e\uff0c\u6b63\u6321\u4f4f\u4e86\u901a\u5f80\u5c71\u8c37\u7684\u8c37\u53e3\uff0c\u5b9e\u5728\u6709\u6096\u5e38\u60c5\u3002 \u3000\u3000\u7167\u8fd9\u6837\u770b\u6765\uff0c\u663e\u7136\u662f\u8ba9\u60f3\u8981\u8fdb\u5165\u5c71\u8c37\u7684\u4eba\uff0c\u52bf\u5fc5\u975e\u901a\u8fc7\u8fd9\u7247\u7af9\u6797\u4e0d\u53ef\u3002\u6362\u53e5\u8bdd\u8bf4\uff0c\u8fd9\u662f\u4e2a\u9677\u9631\u3002\u4e5f\u65e0\u5f02\u662f\u2018\u6b64\u5730\u65e0\u94f6\u4e09\u767e\u4e24\u2019\uff0c\u5f88\u53ef\u80fd\u4ed6\u4eec\u7684\u4eba\u5c31\u85cf\u5728\u8c37\u5185\uff01\u201d \u3000\u3000\u5f20\u56db\u59d1\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u603b\u7b97\u660e\u767d\u4e86\u3002\u201d \u3000\u3000\u6c5f\u67ab\u60f3\u4e86\u60f3\uff0c\u95ee\u9053\uff1a\u201c\u5927\u73b0\u73b0\u5728\u6211\u4eec\u662f\u56de\u53bb\uff0c\u8fd8\u662f\u4eba\u9635\u4e00\u63a2\u7a76\u7adf\uff1f\u201d \u3000\u3000\u5f20\u56db\u59d1\u9053\uff1a\u201c\u90a3\u5c31\u5f97\u770b\u4f60\u7684\u4e86\uff0c\u5982\u679c\u4f60\u6709\u81ea\u4fe1\uff0c\u4e0d\u6015\u88ab\u56f0\u5728\u9635\u4e2d\u51fa\u4e0d\u6765\u2026\u2026\u201d \u3000\u3000\u4e0d\u7b49\u5979\u8bf4\u5b8c\uff0c\u6c5f\u67ab\u5df2\u6bc5\u7136\u9053\uff1a\u201c\u4e0d\u5165\u864e\u7a74\uff0c\u7109\u5f97\u864e\u5b50\u3002\u65e2\u7136\u4ed6\u4eec\u4e3a\u4e86\u5e03\u9635\uff0c\u4e0d\u60dc\u52b3\u5e08\u52a8\u4f17\uff0c\u8d39\u4e86\u4e5d\u725b\u4e8c\u864e\u4e4b\u529b\uff0c\u5927\u8001\u8fdc\u79fb\u6765\u4e0a\u5e72\u682a\u9752\u7af9\uff0c\u51b2\u7740\u8fd9\u4efd\u5fc3\u610f\uff0c\u6211\u4eec\u4e5f\u4e0d\u80fd\u8f9c\u8d1f\u4eba\u5bb6\u7684\u76db\u60c5\u54ea\uff01\u201d \u3000\u3000\u4e24\u4eba\u76f8\u89c6\u4e00\u7b11\uff0c\u5c0f\u5fc3\u7ffc\u7ffc\u5730\u5411\u7af9\u6797\u5185\u8d70\u53bb\u3002 \u3000\u3000\u4ed6\u4eec\u5982\u5c65\u8584\u51b0\uff0c\u4e1d\u6beb\u4e0d\u6562\u6389\u4ee5\u8f7b\u5fc3\uff0c\u7ed3\u679c\u8fdb\u5165\u6797\u5185\u7adf\u6beb\u65e0\u5f02\u72b6\uff0c\u5927\u51fa\u4e24\u4eba\u610f\u6599\u4e4b\u5916\u3002 \u3000\u3000\u6797\u5185\u7684\u60c5\u666f\uff0c\u4e0e\u4ed6\u4eec\u5728\u6797\u5916\u6240\u89c1\u5b8c\u5168\u4e00\u6837\u3002 \u3000\u3000\u8fd9\u7a76\u7adf\u662f\u600e\u4e48\u56de\u4e8b\uff1f \u3000\u3000\u6c5f\u67ab\u4e0d\u7981\u611f\u5230\u5341\u5206\u56f0\u60d1\uff0c\u5f20\u56db\u59d1\u4e5f\u83ab\u540d\u5176\u5999\u3002 \u3000\u3000\u201c\u5927\u59d0\uff0c\u6211\u4eec\u597d\u50cf\u53c8\u4f30\u8ba1\u9519\u4e86\u3002\u201d\u6c5f\u67ab\u8f6c\u8eab\u770b\u7740\u5f20\u56db\u59d1\u3002 \u3000\u3000\u5f20\u56db\u59d1\u5f3a\u81ea\u4e00\u7b11\u9053\uff1a\u201c\u4e0d\u662f\u2018\u6211\u4eec\u2019\uff0c\u662f\u6211\u4f30\u8ba1\u9519\u4e86\uff0c\u4f60\u8bf4\u5bf9\u4e86\uff0c\u8fd9\u6839\u672c\u4e0d\u662f\u4e2a\u5947\u9635\u3002\u201d \u3000\u3000\u201c\u4f46\u4ed6\u4eec\u4e3a\u4ec0\u4e48\u5462\uff1f\u201d\u6c5f\u67ab\u9053\uff1a\u201c\u81f3\u5c11\u6709\u4e2a\u5408\u7406\u7684\u89e3\u91ca\u5440\uff01\u201d \u3000\u3000\u5f20\u56db\u59d1\u6c89\u601d\u4e00\u4e0b\uff0c\u5f53\u673a\u7acb\u65ad\u9053\uff1a\u201c\u8d70\uff01\u6211\u4eec\u65e2\u7136\u8fdb\u6765\u4e86\uff0c\u5c31\u8fdb\u5c71\u8c37\u53bb\u627e\u7b54\u6848\uff01\u201d \u3000\u3000\u201c\u5bf9\uff01\u201d\u6c5f\u67ab\u4e49\u65e0\u53cd\u987e\u3002 \u3000\u3000\u4e24\u4eba\u4ea4\u6362\u4e00\u4e0b\u773c\u8272\uff0c\u4e92\u793a\u5173\u6000\u4e0e\u8b66\u60d5\uff0c\u968f\u5373\u5411\u8c37\u53e3\u4e00\u76f4\u8d70\u53bb\u3002 \u3000\u3000\u5c3d\u7ba1\u6797\u5185\u6beb\u65e0\u5f02\u72b6\uff0c\u4ed6\u4eec\u4ecd\u4e0d\u6562\u6389\u4ee5\u8f7b\u5fc3\uff0c\u4e00\u9762\u5411\u524d\u8d70\uff0c\u4e00\u9762\u76ee\u5149\u6d41\u8f6c\uff0c\u6ce8\u610f\u4efb\u4f55\u98ce\u5439\u8349\u52a8\u3002 \u3000\u3000\u56e0\u4e3a\uff0c\u8fd9\u5b9e\u5728\u4ee4\u4eba\u8d39\u89e3\uff0c\u4e5f\u592a\u4e0d\u53ef\u601d\u8bae\uff0c\u79fb\u690d\u5343\u682a\u9752\u7af9\uff0c\u4e00\u591c\u4e4b\u95f4\u5e03\u6210\u4e00\u5ea7\u7af9\u6797\uff0c\u53ef\u79f0\u5f97\u4e0a\u5de5\u7a0b\u6d69\u5927\u3002 \u3000\u3000\u82e5\u662f\u6beb\u65e0\u76ee\u7684\uff0c\u90a3\u4ed6\u4eec\u4e3a\u7684\u662f\u4ec0\u4e48\uff1f \u3000\u3000\u7edd\u4e0d\u4f1a\u662f\u4e3a\u4e86\u7f8e\u5316\u666f\u89c2\uff0c\u6216\u8005\u6539\u53d8\u98ce\u6c34\u3002 \u3000\u3000\u552f\u4e00\u7684\u89e3\u91ca\uff0c\u5c31\u662f\u90a3\u4e9b\u4eba\u5403\u5f97\u592a\u9971\u6ca1\u4e8b\u5e72\uff01 \u3000\u3000\u4e24\u4eba\u4e00\u8def\u901a\u884c\u65e0\u963b\uff0c\u4e00\u76f4\u8d70\u5230\u7af9\u6797\u5c3d\u5934\uff0c\u4ecd\u672a\u53d1\u73b0\u4efb\u4f55\u5f02\u72b6\u3002 \u3000\u3000\u51fa\u4e86\u7af9\u6797\uff0c\u773c\u524d\u4fbf\u662f\u8c37\u53e3\u3002 \u3000\u3000\u653e\u773c\u770b\u53bb\uff0c\u8c37\u5728\u4e24\u5ea7\u5c71\u95f4\uff0c\u5a49\u8693\u5411\u524d\u4f38\u5c55\uff0c\u7531\u4e8e\u8c37\u53e3\u88ab\u5c71\u77f3\u6321\u4f4f\u89c6\u7ebf\uff0c\u4e0d\u77e5\u5c71\u8c37\u901a\u5f80\u4f55\u5904\uff0c\u4e5f\u770b\u4e0d\u5230\u5c3d\u5934\u3002 \u3000\u3000\u5f20\u56db\u59d1\u7a81\u89c9\u4e00\u9635\u8f7b\u5fae\u7729\u6655\uff0c\u4e0d\u7531\u5730\u4ee5\u624b\u6309\u989d\uff0c\u8db3\u4e0b\u4e5f\u6709\u4e9b\u4e0d\u7a33\u8d77\u6765\u3002 \u3000\u3000\u6c5f\u67ab\u6025\u95ee\uff1a\u201c\u5927\u59d0\uff0c\u4f60\u600e\u4e48\u4e86\uff1f\u201d \u3000\u3000\u5f20\u56db\u59d1\u5f3a\u81ea\u632f\u4f5c\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u5927\u6982\u6574\u591c\u672a\u7761\uff0c\u7cbe\u795e\u6709\u4e9b\u604d\u60da\u5427\u3002\u201d \u3000\u3000\u6c5f\u67ab\u5173\u5207\u9053\uff1a\u201c\u90a3\u6211\u4eec\u8fd8\u662f\u56de\u53bb\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\uff01\u201d\u5f20\u56db\u59d1\u575a\u51b3\u4e0a\uff1a\u201c\u65e2\u7136\u6765\u4e86\uff0c\u597d\u6b79\u4e5f\u5f97\u8fdb\u5c71\u8c37\u53bb\u2026\u2026\u201d \u3000\u3000\u8bdd\u672a\u8bf4\u5b8c\uff0c\u5979\u5df2\u6447\u6447\u6b32\u5760\u8d77\u6765\u3002 \u3000\u3000\u6c5f\u67ab\u6697\u81ea\u4e00\u60ca\uff0c\u6025\u5fd9\u628a\u5979\u6276\u4f4f\uff0c\u540c\u65f6\u4f38\u624b\u4e00\u63fd\u5979\u7684\u7ea4\u8170\uff1a\u201c\u5927\u59d0\uff0c\u4e0d\u8981\u901e\u5f3a\u4e86\uff0c\u5feb\u56de\u53bb\u5427\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u5374\u6447\u6447\u5934\u9053\uff1a\u201c\u4e0d\u788d\u4e8b\u7684\uff0c\u6276\u6211\u5230\u524d\u9762\u77f3\u5934\u90a3\u91cc\uff0c\u5750\u4e0b\u6b47\u4e00\u4f1a\u513f\u5c31\u6ca1\u4e8b\u4e86\u3002\u201d \u3000\u3000\u6c5f\u67ab\u62d7\u4e0d\u8fc7\u5979\uff0c\u53ea\u597d\u6276\u5979\u5411\u524d\u8d70\u53bb\u3002 \u3000\u3000\u8d70\u8fd1\u6321\u5728\u8c37\u53e3\u7684\u5c71\u77f3\uff0c\u5df2\u770b\u6e05\u8c37\u5185\u7684\u60c5\u51b5\u4e86\u3002 \u3000\u3000\u4e24\u4e2a\u4e4d\u89c1\u4e4b\u4e0b\uff0c\u4e0d\u7531\u4e00\u60ca\uff0c\u5927\u58f0\u8f7b\u547c\uff1a\u201c\u554a\uff01\u2026\u2026\u201d \u3000\u3000\u539f\u6765\uff0c\u4ed6\u4eec\u6240\u89c1\u5230\u7684\uff0c\u7adf\u662f\u516b\u5bf9\u8d64\u88f8\u88f8\u7684\u7537\u5973\uff0c\u6b63\u5728\u8c37\u5185\u505a\u7740\u7ffb\u4e91\u8986\u96e8\u4e4b\u620f\uff01 \u3000\u3000\u4f46\u4ed6\u4eec\u7684\u5931\u58f0\u60ca\u547c\uff0c\u5e76\u672a\u60ca\u52a8\u8fd9\u5341\u516d\u540d\u7537\u5973\u3002 \u3000\u3000\u66f4\u5999\u7684\u662f\uff0c\u516b\u5bf9\u7537\u5973\u5982\u540c\u6ce5\u5851\u6728\u96d5\uff0c\u4fdd\u6301\u7740\u539f\u6765\u7684\u59ff\u52bf\uff0c\u8fde\u52a8\u90fd\u4e0d\u52a8\u4e00\u4e0b\u3002 \u3000\u3000\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f \u3000\u3000\u96be\u9053\u662f\u88ab\u4eba\u70b9\u7a74\u6240\u5236\u2026\u2026 \u3000\u3000\u5b9a\u795e\u4e00\u770b\uff0c\u7ec8\u4e8e\u604d\u7136\u5927\u609f\u3002\u539f\u6765\u516b\u5bf9\u7537\u5973\u662f\u8fd1\u4f3c\u8089\u8272\u7684\u5927\u7406\u77f3\u96d5\u6210\u3002\u4e14\u7c98\u4e0a\u6bdb\u53d1\uff0c\u4e4d\u770b\u6829\u6829\u5982\u751f\uff0c\u51e0\u53ef\u4e71\u771f\uff01 \u3000\u3000\u5c71\u8c37\u91cc\uff0c\u54ea\u6765\u7684\u8fd9\u6279\u7537\u5973\u7684\u77f3\u96d5\uff1f \u3000\u3000\u6c5f\u67ab\u6b63\u611f\u83ab\u540d\u5176\u5999\uff0c\u5ffd\u89c9\u5f20\u56db\u59d1\u7d27\u7d27\u6402\u4f4f\u4e86\u4ed6\u8eab\u4f53\u3002 \u3000\u3000\u83ab\u975e\u89e6\u666f\u751f\u60c5\uff0c\u5f15\u8d77\u4e86\u5979\u7684\u6b32\u5ff5\uff1f \u3000\u3000\u201c\u5927\u59d0\uff0c\u6211\u4eec\u8d70\u8fd1\u4e9b\u53bb\u770b\u770b\u3002\u201d\u6c5f\u67ab\u4f3c\u5bf9\u77f3\u96d5\u53d1\u751f\u4e86\u597d\u5947\u3002 \u3000\u3000\u5f20\u56db\u59d1\u672a\u7f6e\u53ef\u5426\uff0c\u4efb\u7531\u6c5f\u67ab\u62e5\u7740\u8d70\u8fd1\u77f3\u96d5\u3002 \u3000\u3000\u5341\u516d\u540d\u8d64\u88f8\u7537\u5973\u77f3\u96d5\uff0c\u5206\u4e3a\u516b\u7ec4\u6392\u6210\u4e00\u5708\uff0c\u6bcf\u4e00\u5bf9\u7537\u5973\uff0c\u5747\u5404\u81ea\u4f5c\u51fa\u4e0d\u540c\u7684\u4ea4\u5408\u59ff\u52bf\u3002 \u3000\u3000\u4e0d\u4f46\u7ef4\u5999\u7ef4\u8096\uff0c\u800c\u4e14\u6829\u6829\u5982\u751f\uff0c\u751a\u81f3\u8fde\u8138\u4e0a\u90a3\u79cd\u4ea2\u594b\u7d27\u5f20\u7684\u8868\u60c5\uff0c\u4e5f\u5341\u5206\u903c\u771f\u4f20\u795e\uff0c\u8868\u9732\u5f97\u6dcb\u6f13\u5c3d\u81f4\u3002 \u3000\u3000\u8fd9\u4e0d\u77e5\u51fa\u81ea\u4ec0\u4e48\u540d\u5320\u7684\u6770\u4f5c\uff0c\u771f\u53ef\u79f0\u5f97\u4e0a\u662f\u201c\u6625\u5bab\u56fe\u201d\u4e2d\u7684\u73cd\u54c1\uff01 \u3000\u3000\u6c5f\u67ab\u7a81\u7136\u95f4\u60f3\u5230\u4e86\u4ec0\u4e48\uff0c\u8f6c\u8fc7\u8138\u6025\u9053\uff1a\u201c\u5927\u59d0\u2026\u2026\u201d \u3000\u3000\u4e0d\u6599\u5374\u89c1\u5f20\u56db\u59d1\u4e24\u988a\u98de\u8d77\u7ea2\u6655\uff0c\u53cc\u76ee\u6d41\u9732\u65e0\u9650\u67d4\u60c5\uff0c\u4e00\u526f\u6625\u5fc3\u8361\u6f3e\u4e4b\u6001\u3002 \u3000\u3000\u6c5f\u67ab\u4e0d\u7531\u5730\u6697\u81ea\u4e00\u60ca\uff0c\u60ca\u95ee\uff1a\u201c\u5927\u59d0\uff0c\u4f60\u600e\u4e48\u4e86\uff1f\u201d \u3000\u3000\u5f20\u56db\u59d1\u53cc\u81c2\u9f50\u5f20\uff0c\u6402\u7d27\u4e86\u6c5f\u67ab\u7684\u8116\u5b50\uff0c\u4ef0\u8d77\u8138\u6765\uff0c\u6e34\u6c42\u5730\u9053\uff1a\u201c\u5c0f\u60c5\u90ce\uff0c\u5feb\u62b1\u7d27\u5927\u59d0\uff0c\u543b\u6211\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u5fc3\u4e2d\u5927\u9a87\uff0c\u4ed6\u4e0e\u8fd9\u4f4d\u5927\u59d0\u6625\u98ce\u5df2\u4e0d\u6b62\u4e00\u5ea6\uff0c\u4f46\u4ece\u672a\u89c1\u8fc7\u5979\u5982\u6b64\u653e\u8361\uff0c\u7b80\u76f4\u5982\u540c\u4e0d\u7518\u5bc2\u5bde\u7684\u5e74\u8f7b\u5be1\u5987\u3002 \u3000\u3000\u4f46\u4ed6\u65e0\u6cd5\u62d2\u7edd\uff0c\u4e5f\u4e0d\u613f\u62d2\u7edd\u5f20\u56db\u59d1\u7684\u8981\u6c42\u3002 \u3000\u3000\u4ed6\u4f4e\u4e0b\u5934\uff0c\u543b\u4e0a\u4e86\u5979\u7684\u6731\u5507\u3002 \u3000\u3000\u5f20\u56db\u59d1\u8feb\u4e0d\u53ca\u5f85\uff0c\u6402\u7d27\u4e86\u6c5f\u67ab\u5c31\u72c2\u543b\u4e0d\u5df2\uff0c\u540c\u65f6\u5c06\u90a3\u6761\u6ed1\u817b\u817b\u7684\u5999\u820c\u653b\u5165\u5bf9\u65b9\u53e3\u4e2d\uff0c\u6063\u610f\u7ffb\u817e\u5377\u52a8\u8d77\u6765\u3002 \u3000\u3000\u6c5f\u67ab\u89c9\u5f97\u51fa\uff0c\u5f20\u56db\u59d1\u7684\u5168\u8eab\u53d1\u70ed\uff0c\u4e5f\u610f\u8bc6\u51fa\u5979\u5185\u5fc3\u7684\u4e00\u80a1\u6b32\u706b\u6b63\u5728\u71c3\u70e7\u3002 \u3000\u3000\u600e\u4f1a\u5982\u6b64\u5462\uff1f \u3000\u3000\u6c5f\u67ab\u731b\u7136\u95f4\u82e5\u6709\u6240\u609f\uff0c\u60f3\u5230\u4e86\u95ee\u9898\u51fa\u5728\u90a3\u7247\u7af9\u6797\uff01 \u3000\u3000\u5bf9\u65b9\u4e0d\u60dc\u52b3\u5e08\u52a8\u4f17\uff0c\u4e00\u591c\u4e4b\u95f4\u5e03\u6210\u8fd9\u7247\u7af9\u6797\uff0c\u7edd\u4e0d\u53ef\u80fd\u662f\u6545\u5e03\u7591\u9635\u3002 \u3000\u3000\u5c24\u5176\u662f\uff0c\u8c37\u5185\u53c8\u5e03\u7f6e\u4e86\u8fd9\u516b\u5ea7\u7537\u5973\u4ea4\u5408\u77f3\u96d5\u3002 \u3000\u3000\u663e\u7136\uff0c\u7af9\u6797\u770b\u4f3c\u6beb\u65e0\u5f02\u72b6\uff0c\u5176\u5b9e\u6797\u5185\u5f88\u53ef\u80fd\u5f25\u6f2b\u7740\u65e0\u8272\u65e0\u81ed\u7684\u6c14\u4f53\uff0c\u5438\u5165\u540e\u8db3\u4ee5\u4f7f\u4eba\u610f\u4e71\u60c5\u8ff7\uff0c\u6b32\u706b\u96be\u7981\u3002 \u3000\u3000\u521a\u624d\u5f20\u56db\u59d1\u4e00\u8d70\u51fa\u6797\u5916\uff0c\u4fbf\u89c9\u4e00\u9635\u8f7b\u5fae\u7729\u6655\uff0c\u8db3\u89c1\u53d7\u5230\u5438\u5165\u6797\u4e2d\u6c14\u4f53\u5f71\u54cd\u3002 \u3000\u3000\u5982\u679c\u4e0d\u51fa\u6240\u6599\uff0c\u90a3\u79cd\u6c14\u4f53\u5e94\u8be5\u662f\u50ac\u60c5\u5242\uff0c\u6216\u662f\u6625\u836f\u4e4b\u7c7b\u3002\u4e0d\u540c\u7684\u662f\u4e0d\u5fc5\u670d\u4e0b\uff0c\u5438\u5165\u5373\u53ef\u53d1\u751f\u4f5c\u7528\uff0c\u5927\u6982\u53ef\u4e0e\u6c5f\u6e56\u4e0a\u6700\u9738\u9053\u7684\u8ff7\u836f\u201c\u516d\u91cc\u9999\u201d\u5ab2\u7f8e\u5427\uff01 \u3000\u3000\u52a0\u4e4b\u76ee\u7779\u8c37\u5185\u516b\u5ea7\u7537\u5973\u4ea4\u5408\u77f3\u96d5\uff0c\u4fbf\u4f7f\u836f\u6ce8\u52a0\u901f\u53d1\u4f5c\uff0c\u4e00\u9f50\u4e0d\u53ef\u6536\u62fe\u3002 \u3000\u3000\u6c5f\u67ab\u4ed7\u7740\u529f\u529b\u6df1\u539a\uff0c\u52a0\u4e0a\u5929\u72d0\u592b\u4eba\u6240\u4f20\u201c\u60d1\u5fc3\u672f\u201d\uff0c\u66f4\u91cd\u8981\u7684\u662f\u5f97\u529b\u4e8e\u65e0\u76f8\u5927\u5e08\uff0c\u53ca\u91d1\u4e39\u4e66\u751f\u7684\u901a\u529b\u5408\u4f5c\uff0c\u65e9\u4e3a\u4ed6\u6253\u901a\u4efb\u3001\u77a5\u4e8c\u8109\uff0c\u51b2\u7834\u751f\u6b7b\u7384\u5173\u3002 \u3000\u3000\u5982\u4eca\u4ed6\u5df2\u5f02\u4e8e\u5e38\u4eba\uff0c\u8fde\u5929\u540e\u5bab\u79d8\u4f20\u7684\u5a9a\u6cd5\u90fd\u5931\u6548\uff0c\u66f4\u4f55\u51b5\u662f\u533a\u533a\u8ff7\u836f\u3002 \u3000\u3000\u6309\u7406\u8bf4\uff0c\u5f20\u56db\u59d1\u4e5f\u5c3d\u5f97\u5929\u72d0\u592b\u4eba\u771f\u4f20\uff0c\u4e0d\u5e94\u5982\u6b64\u4e0d\u6d4e\u624d\u662f\u3002\u4f46\u5979\u53d7\u5230\u53d1\u81ea\u5185\u5fc3\u6df1\u7231\u6c5f\u67ab\u7684\u5f71\u54cd\uff0c\u8eab\u5fc3\u5bf9\u8fd9\u5c0f\u60c5\u90ce\u4ece\u4e0d\u8bbe\u9632\uff0c\u5982\u540c\u95e8\u6237\u5927\u5f00\uff0c\u4ee5\u81f4\u88ab\u90a3\u8ff7\u9b42\u6c14\u4f53\u8d81\u865a\u800c\u5165\uff0c\u5168\u7136\u4e27\u5931\u4e86\u81ea\u5236\u4e4b\u529b\u3002 \u3000\u3000\u6c5f\u67ab\u8fd9\u65f6\u660e\u77e5\u5979\u662f\u610f\u4e71\u60c5\u8ff7\uff0c\u8eab\u4e0d\u7531\u5df1\uff0c\u4f46\u5374\u4e0d\u5fcd\u62d2\u7edd\u5979\u7684\u5982\u706b\u70ed\u60c5\u3002 \u3000\u3000\u72c2\u70bd\u7684\u6b32\u706b\uff0c\u4f7f\u5f20\u56db\u59d1\u6108\u6765\u6108\u51b2\u52a8\u2026\u2026 \u3000\u3000\u6c5f\u67ab\u5bf9\u8fd9\u4f4d\u5927\u59d0\uff0c\u4e0d\u4f3c\u5bf9\u96ea\u3001\u971c\u4e24\u59d0\u59b9\uff0c\u4e0d\u5fc5\u803d\u5fc3\u60c5\u4e0d\u81ea\u7981\u65f6\uff0c\u6b32\u706b\u4e00\u53d1\u4e0d\u53ef\u6536\u62fe\u3002 \u3000\u3000\u53ef\u662f\uff0c\u6b64\u65f6\u6b64\u5730\uff0c\u4f7f\u4ed6\u66f4\u803d\u5fc3\u7684\u662f\u60df\u6050\u8c37\u5185\u65e9\u6709\u7406\u4f0f\uff0c\u4f3a\u673a\u800c\u52a8\u3002\u6781\u53ef\u80fd\u8d81\u4ed6\u4eec\u610f\u4e71\u60c5\u8ff7\uff0c\u751a\u81f3\u7ffb\u4e91\u8986\u96e8\u65f6\uff0c\u51b7\u4e0d\u9632\u6d8c\u51fa\u53d1\u52a8\u7a81\u88ad\u3002 \u3000\u3000\u4ed6\u867d\u6697\u81ea\u63d0\u9ad8\u8b66\u89c9\uff0c\u968f\u65f6\u53ef\u4ee5\u5e94\u53d8\uff0c\u4f46\u5374\u65e0\u6cd5\u517c\u987e\u795e\u5fd7\u4e0d\u6e05\u7684\u5f20\u56db\u59d1\u3002 \u3000\u3000\u5c24\u5176\u60f3\u5230\uff0c\u654c\u4eba\u4e07\u4e00\u653b\u6765\u7684\u662f\u6562\u6b7b\u961f\uff0c\u8eab\u4e0a\u7ed1\u4e86\u5f3a\u70c8\u70b8\u836f\uff0c\u51b3\u5fc3\u540c\u5f52\u4e8e\u5c3d\u2026\u2026 \u3000\u3000\u6c5f\u67ab\u4e0d\u6562\u518d\u5f80\u4e0b\u60f3\u4e86\u3002 \u3000\u3000\u201c\u5927\u59d0\u2026\u2026\u201d \u3000\u3000\u4ed6\u6323\u8131\u4e86\u5f20\u56db\u59d1\u7684\u72c2\u543b\uff0c\u521a\u8981\u5411\u5979\u63d0\u51fa\u8b66\u544a\uff0c\u53ea\u89c1\u8fd9\u4f4d\u6625\u60c5\u8361\u6f3e\u7684\u5927\u59d0\uff0c\u6781\u5176\u59a9\u5a9a\u7684\u5ae3\u7136\u4e00\u7b11\uff0c\u7adf\u7136\u4e0d\u81ea\u89c9\u5730\u65bd\u5c55\u51fa\u201c\u60d1\u5fc3\u672f\u201d \u3000\u3000\u6765\u3002 \u3000\u3000\u6c5f\u67ab\u5fc3\u795e\u731b\u7136\u4e00\u9707\uff0c\u60c5\u77e5\u4e0d\u5999\uff0c\u6025\u53eb\u9053\uff1a\u201c\u5927\u59d0\uff01\u4e0d\u53ef\u4ee5\u2026\u2026\u201d \u3000\u3000\u5f20\u56db\u59d1\u7b11\u5f97\u66f4\u5996\u5a9a\u4e86\uff0c\u6709\u5982\u767e\u82b1\u5410\u854a\uff0c\u7fa4\u82b3\u4e89\u8273\u3002 \u3000\u3000\u6c5f\u67ab\u5fc3\u4e2d\u5927\u9a87\u3002 \u3000\u3000\u51ed\u4ed6\u7684\u5b9a\u529b\uff0c\u5373\u4f7f\u4e0d\u4ee5\u201c\u60d1\u5fc3\u672f\u201d\uff0c\u4e5f\u8db3\u4ee5\u62b5\u5236\u5929\u540e\u5bab\u7684\u201c\u5a9a\u6cd5\u201d \u3000\u3000\u4fb5\u88ad\u3002 \u3000\u3000\u66f4\u4f55\u51b5\uff0c\u4ed6\u82e5\u65bd\u5c55\u201c\u60d1\u5fc3\u672f\u201d\u5bf9\u6297\uff0c\u5fc5\u5360\u4e0a\u98ce\u3002 \u3000\u3000\u4ee5\u524d\u5c5e\u8bd5\u4e0d\u723d\uff0c\u8b6c\u5982\u6885\u82b1\uff0c\u9752\u51e4\uff0c\u84dd\u51e4\u7b49\u51e0\u4f4d\u7cbe\u4e60\u201c\u5a9a\u6cd5\u201d\u7684\u5973\u5b50\uff0c\u4fbf\u5c3d\u88ab\u4ed6\u5f81\u670d\uff0c\u83b7\u5f97\u82b3\u5fc3\uff0c\u4e0d\u60dc\u80cc\u53db\u795e\u79d8\u6559\u4e3b\uff0c\u7518\u4e3a\u4ed6\u9a71\u4f7f\u5356\u547d\u3002 \u3000\u3000\u5982\u4eca\u7684\u5bf9\u624b\u5374\u662f\u5f20\u56db\u59d1\uff0c\u8fd9\u65f6\u53c8\u4f7f\u51fa\u4e86\u201c\u6212\u5fc3\u672f\u201d\u3002 \u3000\u3000\u6c5f\u67ab\u4e0d\u6562\u4ee5\u201c\u60d1\u5fc3\u672f\u201d\u5bf9\u6297\uff0c\u90a3\u65e0\u5f02\u706b\u4e0a\u52a0\u6cb9\uff0c\u6b32\u706b\u66f4\u52a0\u4e00\u53d1\u4e0d\u53ef\u6536\u62fe\u3002\u4e07\u4e00\u5931\u63a7\uff0c\u96be\u4ee5\u81ea\u5236\uff0c\u52bf\u5fc5\u88ab\u6b32\u706b\u541e\u566c\uff0c\u843d\u4e2a\u4e24\u8d25\u4ff1\u4f24\uff0c\u540c\u5f52\u4e8e\u5c3d\u3002 \u3000\u3000\u60c5\u52bf\u5df2\u6025\uff0c\u4e07\u822c\u65e0\u5948\u4e4b\u4e0b\uff0c\u6c5f\u67ab\u552f\u4e00\u80fd\u505a\u7684\uff0c\u5c31\u662f\u4ec0\u4e48\u90fd\u4e0d\u505a\uff0c\u4efb\u51ed\u8fd9\u4f4d\u5927\u59d0\u4e3a\u6240\u6b32\u4e3a\u3002 \u3000\u3000\u8fd9\u597d\u6709\u4e00\u6bd4\uff0c\u5982\u540c\u72c2\u6d2a\u51b3\u5824\uff0c\u65e2\u7136\u65e0\u6cd5\u963b\u6321\uff0c\u4e0d\u5982\u758f\u6d41\uff0c\u5c06\u6cdb\u6ee5\u7684\u6d2a\u6c34\u5bfc\u5165\u5927\u6d77\uff0c\u8ba9\u5b83\u81ea\u7136\u5316\u4e8e\u65e0\u5f62\u3002 \u3000\u3000\u5f20\u56db\u59d1\u6beb\u65e0\u987e\u5fcc\uff0c\u5bbd\u8863\u89e3\u5e26\uff0c\u892a\u5c3d\u4e86\u5168\u8eab\u8863\u886b\u3002 \u3000\u3000\u5149\u5929\u5316\u65e5\u4e4b\u4e0b\uff0c\u8fd9\u4f4d\u5927\u59d0\u4e30\u6ee1\u6210\u719f\u7684\u80f4\u4f53\uff0c\u8d64\u88f8\u88f8\u5730\u5448\u73b0\u5728\u6c5f\u67ab\u773c\u524d\u3002 \u3000\u3000\u4ee5\u5f80\u51e0\u5ea6\u6625\u98ce\uff0c\u51e0\u756a\u4e91\u96e8\uff0c\u6c5f\u67ab\u4e0e\u5f20\u56db\u59d1\u90fd\u662f\u5728\u660f\u6697\u7684\u70db\u5149\u4e0b\u8fdb\u884c\uff0c\u672a\u80fd\u5c3d\u60c5\u6b23\u8d4f\u5979\u7684\u4f53\u6001\u3002 \u3000\u3000\u6b64\u523b\u4e00\u89c8\u65e0\u9057\uff0c\u624d\u53d1\u73b0\u8fd9\u4f4d\u5927\u59d0\u7684\u80f4\u4f53\uff0c\u7adf\u7136\u66f2\u7ebf\u73b2\u62e2\uff0c\u5a40\u5a1c\u591a\u59ff\u3002\u5c24\u5176\u4e00\u8eab\u7ec6\u76ae\u767d\u8089\uff0c\u808c\u8d28\u6676\u83b9\uff0c\u662f\u5982\u6b64\u7684\u8bf1\u60d1\uff0c\u4ee4\u4eba\u4e00\u89c1\u72b9\u601c\u3002 \u3000\u3000\u5f20\u56db\u59d1\u88f8\u8eab\u9762\u5bf9\u6c5f\u67ab\uff0c\u53cc\u624b\u9f50\u52a8\u3002\u8feb\u4e0d\u53ca\u5f85\u5730\u4e3a\u4ed6\u8131\u5149\u4e86\u5168\u8eab\u8863\u886b\u3002 \u3000\u3000\u4e24\u4eba\u8d64\u8eab\u76f8\u5bf9\uff0c\u4eff\u4f5b\u8c37\u4e2d\u591a\u4e86\u4e00\u5ea7\u7537\u5973\u4ea4\u5408\u7684\u77f3\u96d5\u3002 \u3000\u3000\u4f46\u4ed6\u4eec\u662f\u6709\u8840\u6709\u8089\u7684\uff0c\u800c\u4e14\u5728\u6d3b\u52a8\u2026\u2026 \u3000 \u7b2c\u4e09\u5341\u56de\u3000\u96ea\u971c\u5973\u529b\u5288\u56db\u4f8f\u5112 \u3000 \u3000\u3000\u7ecf\u8fc7\u51e0\u756a\u7ffb\u4e91\u8986\u96e8\uff0c\u6fc0\u60c5\u7684\u8089\u640f\u540e\u2026\u2026 \u3000\u3000\u6c5f\u67ab\u548c\u5f20\u56db\u59d1\u90fd\u7cbe\u75b2\u529b\u5c3d\u4e86\u3002 \u3000\u3000\u4ed6\u4eec\u4ecd\u7136\u7d27\u7d27\u76f8\u62e5\uff0c\u8d64\u88f8\u88f8\u5730\uff0c\u8eba\u5728\u516b\u5ea7\u77f3\u96d5\u56f4\u6210\u7684\u5708\u5185\uff0c\u53d1\u51fa\u6025\u4fc3\u7684\u5598\u606f\u58f0\u3002 \u3000\u3000\u6c5f\u67ab\u4fdd\u6301\u7740\u6e05\u9192\uff0c\u4e5f\u4fdd\u6301\u7740\u8b66\u89c9\u3002\u7fe0\u7389\u5200\u5c31\u5728\u4ed6\u8eab\u8fb9\uff0c\u4f38\u624b\u53ef\u53ca\uff0c\u968f\u65f6\u53ef\u4ee5\u6293\u8d77\u5b83\u8dc3\u8eab\u8fce\u654c\u3002 \u3000\u3000\u4f46\u662f\uff0c\u8fde\u4ed6\u90fd\u611f\u5230\u610f\u5916\uff0c\u654c\u4eba\u7adf\u7136\u6beb\u65e0\u52a8\u9759\u3002 \u3000\u3000\u5f53\u4ed6\u4eec\u6b63\u5728\u7ffb\u4e91\u8986\u96e8\u65f6\uff0c\u6b63\u662f\u53ef\u9047\u800c\u4e0d\u53ef\u6c42\u7684\u7a81\u88ad\u5927\u597d\u65f6\u673a\uff0c\u654c\u4eba\u600e\u4f1a\u6ca1\u6709\u597d\u597d\u628a\u63e1\uff1f \u3000\u3000\u6c5f\u67ab\u5b9e\u5728\u60f3\u4e0d\u900f\u3002 \u3000\u3000\u8fd9\u65f6\u5f20\u56db\u59d1\u7684\u6b32\u706b\u5df2\u6d88\u9000\uff0c\u5a07\u5598\u4e86\u4e00\u9635\uff0c\u970d\u5730\u633a\u8eab\u5750\u8d77\uff0c\u53d1\u73b0\u81ea\u5df1\u5168\u8eab\u4e00\u4e1d\u4e0d\u6302\uff0c\u4e0d\u7531\u5730\u5931\u58f0\u60ca\u547c\uff1a\u201c\u554a\uff01\u6211\u600e\u4e48\u2026\u2026\u201d \u3000\u3000\u663e\u7136\u5979\u6839\u672c\u4e0d\u77e5\u9053\uff0c\u81ea\u5df1\u521a\u624d\u505a\u4e86\u4ec0\u4e48\u3002 \u3000\u3000\u518d\u4e00\u8f6c\u5934\uff0c\u89c1\u5230\u6c5f\u67ab\u8d64\u88f8\u88f8\u7684\u8eba\u5728\u8eab\u8fb9\uff0c\u66f4\u89c9\u60ca\u8be7\u9053\uff1a\u201c\u4f60\uff01\u4f60\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u5e76\u4e0d\u6025\u7740\u8d77\u8eab\uff0c\u7b11\u9053\uff1a\u201c\u5927\u59d0\uff0c\u8fd8\u662f\u4f60\u6bd4\u6211\u9ad8\u660e\uff0c\u88ab\u4f60\u8bf4\u4e2d\u4e86\uff0c\u90a3\u7247\u7af9\u6797\u679c\u7136\u662f\u4e2a\u5947\u9635\u3002\u201d \u3000\u3000\u201c\u54e6\uff1f\u201d \u3000\u3000\u5f20\u56db\u59d1\u672a\u53ca\u8ffd\u95f4\uff0c\u76ee\u5149\u4e00\u626b\uff0c\u53d1\u73b0\u4e86\u56f4\u6210\u4e00\u5708\u7684\u516b\u5ea7\u7537\u5973\u4ea4\u5408\u77f3\u96d5\uff0c\u987f\u89c9\u9762\u7ea2\u8033\u8d64\u3002 \u3000\u3000\u5979\u5fd9\u4e0d\u8fed\u6293\u8d77\u4e00\u65c1\u7684\u8863\u886b\uff0c\u8df3\u8d77\u6765\u5306\u5306\u7a7f\u4e0a\uff0c\u59cb\u9053\uff1a\u201c\u6211\u4eec\u88ab\u56f0\u4f4f\u4e86\uff1f\u201d \u3000\u3000\u201c\u6211\u6307\u7684\u4e0d\u662f\u8fd9\u516b\u5ea7\u77f3\u96d5\uff0c\u662f\u8c37\u53e3\u5916\u7684\u90a3\u7247\u7af9\u6797\u3002\u201d \u3000\u3000\u6c5f\u67ab\u8fb9\u8bf4\u8d77\u8eab\uff0c\u5306\u5306\u7a7f\u4e0a\u8863\u886b\uff0c\u53d1\u73b0\u5f20\u56db\u59d1\u4f3c\u4e4e\u5e76\u672a\u6ce8\u610f\u542c\u4ed6\u8bf4\u8bdd\uff0c\u53ea\u662f\u5168\u795e\u8d2f\u6ce8\u5730\u5728\u7aef\u8be6\u77f3\u96d5\uff0c\u5f88\u611f\u5174\u8da3\u3002 \u3000\u3000\u5f20\u56db\u59d1\u5145\u8033\u672a\u95fb\uff0c\u4f3c\u5728\u52aa\u529b\u56de\u60f3\u4ec0\u4e48\u3002 \u3000\u3000\u6c5f\u67ab\u89c1\u72b6\uff0c\u5fc3\u77e5\u8fd9\u4f4d\u5927\u59d0\u4e2a\u6027\u5f88\u597d\u5f3a\uff0c\u4e0d\u613f\u8ffd\u95ee\uff0c\u5e0c\u671b\u51ed\u81ea\u5df1\u7684\u8bb0\u5fc6\uff0c\u60f3\u51fa\u521a\u624d\u7a76\u7adf\u505a\u4e86\u4e9b\u4ec0\u4e48\u3002\u5c24\u5176\u60f3\u77e5\u9053\uff0c\u600e\u4f1a\u8d64\u88f8\u88f8\u5730\u8ddf\u4ed6\u8eba\u5728\u8fd9\u91cc\u3002 \u3000\u3000\u7ec8\u4e8e\u5979\u82e5\u6709\u6240\u609f\uff0c\u5931\u58f0\u53eb\u9053\uff1a\u201c\u4ed6\u4eec\u662f\u6545\u5e03\u7591\u9635\uff01\u201d \u3000\u3000\u6c5f\u67ab\u53cd\u800c\u7cca\u6d82\u4e86\uff0c\u832b\u7136\u95ee\uff1a\u201c\u6b64\u8bdd\u600e\u8bb2\uff1f\u201d \u3000\u3000\u5f20\u56db\u59d1\u5224\u65ad\u9053\uff1a\u201c\u4ed6\u4eec\u4e0d\u60dc\u52b3\u5e08\u52a8\u4f17\uff0c\u8fde\u591c\u5728\u8c37\u53e3\u5916\u79fb\u690d\u4e0a\u5343\u682a\u9752\u7af9\uff0c\u76ee\u7684\u662f\u60f3\u6545\u5e03\u7591\u9635\uff0c\u8ba9\u6211\u4eec\u4ee5\u4e3a\u4ed6\u4eec\u85cf\u8eab\u5728\u8c37\u5185\u3002\u5176\u5b9e\u7b49\u4e8e\u58f0\u4e1c\u51fb\u897f\uff0c\u4ed6\u4eec\u662f\u85cf\u5728\u522b\u5904\u3002 \u3000\u3000\u4e5f\u8bb8\u7167\u4ed6\u4eec\u7684\u4f30\u8ba1\uff0c\u8ba4\u4e3a\u6211\u4eec\u7edd\u4e0d\u6562\u8d38\u7136\u95ef\u5165\u7af9\u6797\uff0c\u8fdb\u5230\u8c37\u4e2d\u6765\u3002\u5373\u4f7f\u8fdb\u5165\u8c37\u4e2d\uff0c\u7ed3\u679c\u5374\u53d1\u73b0\u53ea\u6709\u516b\u5ea7\u77f3\u96d5\uff0c\u4e0d\u89c1\u534a\u4e2a\u4eba\u5f71\uff0c\u88ab\u4ed6\u4eec\u800d\u4e86\u4e00\u62db\u3002 \u3000\u3000\u53ef\u662f\uff0c\u6211\u4eec\u600e\u4e48\u8eba\u5728\u8fd9\u91cc\u7684\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u5475\u5475\u4e00\u7b11\u9053\uff1a\u201c\u6211\u8fd8\u4ee5\u4f60\u4e0d\u4f1a\u95ee\u5462\uff01\u201d \u3000\u3000\u5f20\u56db\u59d1\u767d\u4ed6\u4e00\u773c\uff0c\u987f\u4fc3\u9053\uff1a\u201c\u5feb\u8bf4\u561b\uff0c\u7a76\u7adf\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u8fd9\u624d\u4ece\u5bb9\u4e0d\u8feb\u4ed6\u8bf4\u51fa\u4e86\u4e00\u5207\u3002 \u3000\u3000\u5f20\u56db\u59d1\u542c\u6bd5\uff0c\u987f\u65f6\u9762\u7ea2\u8033\u8d64\uff0c\u7a98\u8feb\u4e07\u72b6\u9053\uff1a\u201c\u5929\u554a\uff01\u6211\u7adf\u4e00\u70b9\u4e5f\u8bb0\u4e0d\u8d77\uff0c\u81ea\u5df1\u505a\u4e86\u4e9b\u4ec0\u4e48\u3002\u201d \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u5e78\u597d\u662f\u5927\u59d0\uff0c\u5982\u679c\u662f\u96ea\u3001\u971c\u4e24\u59d0\u59b9\u8ddf\u6211\u540c\u6765\uff0c\u540e\u679c\u771f\u4e0d\u582a\u8bbe\u60f3\u3002\u8bf4\u4e0d\u5b9a\u6211\u4eec\u4f1a\u540c\u5f52\u4e8e\u5c3d\uff0c\u8fd9\u91cc\u5c31\u662f\u6211\u548c\u5979\u4eec\u7684\u846c\u8eab\u4e4b\u5730\uff01\u201d \u3000\u3000\u5f20\u56db\u59d1\u542c\u5f97\u6697\u81ea\u4e00\u60ca\uff0c\u5fc3\u4e2d\u72b9\u6709\u4f59\u60b8\u3002 \u3000\u3000\u6c5f\u67ab\u77a5\u4e86\u5979\u4e00\u773c\uff0c\u5ffd\u95ee\uff1a\u201c\u5927\u59d0\uff0c\u4f60\u770b\u4ed6\u4eec\u6545\u5e03\u7591\u9635\uff0c\u9664\u4e0b\u8bef\u5bfc\u6211\u4eec\u8ffd\u654c\u7684\u65b9\u5411\uff0c\u8fd8\u6709\u4ec0\u4e48\u5176\u4ed6\u5c45\u5fc3\uff1f\u201d \u3000\u3000\u201c\u5514\u2026\u201d\u5f20\u56db\u59d1\u6c89\u541f\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u6709\u4e24\u79cd\u53ef\u80fd\uff0c\u5176\u4e00\u662f\u5047\u5b9a\u6211\u4eec\u4e0d\u6562\u8d38\u7136\u95ef\u5165\u7af9\u6797\uff0c\u52bf\u5fc5\u8c03\u96c6\u6240\u6709\u4eba\u624b\uff0c\u5168\u90e8\u5b88\u5728\u6797\u5916\u3002\u8fd9\u6837\u6765\uff0c\u6211\u4eec\u7684\u4eba\u5c31\u7b49\u4e8e\u9677\u5728\u4e86\u8fd9\u91cc\uff0c\u8ba9\u4ed6\u4eec\u6709\u5145\u88d5\u7684\u65f6\u95f4\u51c6\u5907\uff0c\u53ca\u91cd\u65b0\u5e03\u7f6e\u4e00\u5207\uff0c\u6216\u8005\u7b49\u5f85\u66f4\u5f3a\u5927\u7684\u589e\u63f4\u8d76\u6765\uff0c\u8ddf\u6211\u4eec\u51b3\u4e00\u6b7b\u6218\u3002 \u3000\u3000\u53e6\u4e00\u79cd\u53ef\u80fd\uff0c\u5219\u662f\u4e07\u4e00\u6211\u4eec\u81ea\u6043\u827a\u9ad8\u80c6\u5927\uff0c\u4e0d\u987e\u4e00\u5207\u786c\u95ef\u7af9\u6797\uff0c\u51b2\u8fdb\u8c37\u91cc\u6765\u3002\u6bcf\u4e2a\u4eba\u4e0d\u77e5\u4e0d\u89c9\u4e2d\uff0c\u5438\u5165\u6797\u5185\u65e0\u8272\u65e0\u55c5\u7684\u6c14\u4f53\uff0c\u5fc5\u7136\u795e\u5fd7\u4e0d\u6e05\uff0c\u610f\u4e71\u60c5\u8ff7\u3002\u90a3\u65f6\u5927\u5bb6\u90fd\u5931\u53bb\u7406\u6027\uff0c\u65e0\u6cd5\u81ea\u5236\uff0c\u5c82\u4e0d\u4e71\u6210\u4e00\u7247\u3002 \u3000\u3000\u679c\u771f\u5982\u6b64\uff0c\u6211\u4eec\u5c31\u96be\u514d\u4e00\u573a\u81ea\u76f8\u6b8b\u6740\uff0c\u5c24\u5176\u662f\u96ea\u3001\u971c\u4e24\u4e2a\u4e2b\u5934\u2026\u2026 \u3000\u3000\u5509\uff01\u8fd9\u4e8b\u60f3\u8d77\u6765\u5b9e\u5728\u53ef\u6015\uff01\u201d \u3000\u3000\u6c5f\u67ab\u5618\u4e86\u53e3\u6c14\uff0c\u9042\u9053\uff1a\u201c\u5927\u59d0\uff0c\u6211\u4eec\u51fa\u6765\u8fd9\u4e48\u4e45\uff0c\u5927\u5bb6\u4e00\u5b9a\u5f88\u803d\u5fc3\uff0c\u4e0d\u77e5\u6211\u4eec\u51fa\u4e86\u4ec0\u4e48\u610f\u5916\uff0c\u5feb\u56de\u53bb\u5427\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u628a\u5934\u4e00\u70b9\uff0c\u4e24\u4eba\u98de\u8eab\u51fa\u4e86\u516b\u5ea7\u77f3\u96d5\uff0c\u76f4\u5411\u8c37\u53e3\u5954\u53bb\u3002 \u3000\u3000\u6765\u5230\u6797\u524d\uff0c\u6c5f\u67ab\u6025\u9053\uff1a\u201c\u5927\u59d0\uff0c\u5c4f\u4f4f\u547c\u5438\uff0c\u6211\u4eec\u4e00\u53e3\u6c14\u7a7f\u51fa\u7af9\u6797\u3002\u201d \u3000\u3000\u53ea\u89c1\u4ee5\u5927\u548c\u5c1a\u4e3a\u9996\uff0c\u8001\u53eb\u5316\uff0c\u6b27\u9633\u662d\u592b\u5987\u53ca\u4e94\u5f1f\u5b50\uff0c\u5201\u9e4f\u7b49\u4eba\u6b63\u5728\u6797\u5916\u5b88\u7740\u3002\u4f3c\u4e4e\u770b\u51fa\u7af9\u6797\u6709\u8e4a\u8df7\uff0c\u4e0d\u6562\u8d38\u7136\u95ef\u5165\uff0c\u4ee5\u81f4\u8d91\u8d84\u4e0d\u524d\u3002 \u3000\u3000\u4e00\u89c1\u6c5f\u67ab\u4e0e\u5f20\u56db\u59d1\u5954\u51fa\uff0c\u5927\u548c\u5c1a\u5fd9\u98de\u8eab\u8fce\u4e0a\u524d\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u679c\u7136\u5728\u8fd9\u91cc\uff01\u201d \u3000\u3000\u8001\u53eb\u5316\u4e5f\u8d76\u6765\uff0c\u62b1\u6028\u9053\uff1a\u201c\u5509\uff01\u4e5f\u4e0d\u544a\u8bc9\u6211\u4eec\u53bb\u54ea\u91cc\uff0c\u53ef\u628a\u6211\u4eec\u6025\u574f\u5566\uff01\u201d \u3000\u3000\u6c5f\u67ab\u82e5\u65e0\u5176\u4e8b\u5730\u7b11\u9053\uff1a\u201c\u6025\u4ec0\u4e48\uff0c\u6211\u4eec\u53c8\u4e0d\u662f\u4e09\u5c81\u5c0f\u5b69\uff0c\u8fd8\u6015\u6211\u4eec\u88ab\u4eba\u62d0\u53bb\u4e86\u4e0d\u6210\u3002\u201d \u3000\u3000\u201c\u54fc\uff01\u4f60\u5012\u8bf4\u7684\u8f7b\u677e\u2026\u2026\u201d \u3000\u3000\u8001\u53eb\u5316\u773c\u73e0\u5b50\u4e00\u5f39\uff0c\u6b63\u5f85\u501a\u8001\u5356\u8001\uff0c\u8bad\u4ed6\u51e0\u53e5\uff0c\u6b27\u9633\u662d\u5df2\u6765\u5230\u9762\u524d\uff0c\u62a2\u7740\u95ee\uff1a\u201c\u5c71\u8c37\u91cc\u53ef\u53d1\u73b0\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5f20\u56db\u59d1\u552f\u6050\u6c5f\u67ab\u8bf4\u6f0f\u4e86\u5634\uff0c\u90a3\u53ef\u4ee4\u5979\u65e0\u5730\u81ea\u5bb9\uff0c\u5fd9\u9053\uff1a\u201c\u6b64\u5904\u4e0d\u5b9c\u4e45\u7559\uff0c\u5927\u5bb6\u56de\u53bb\u518d\u8bf4\u5427\u3002\u201d \u3000\u3000\u5927\u5bb6\u867d\u6025\u4e8e\u60f3\u77e5\u9053\u8c37\u5185\u60c5\u5f62\uff0c\u4f46\u4e0d\u4fbf\u8ffd\u95ee\u3002 \u3000\u3000\u5954\u56de\u53e4\u5e99\u7684\u9014\u4e2d\u7f57\u5170\u6545\u610f\u8d70\u5728\u5f20\u56db\u59d1\u8eab\u65c1\uff0c\u8f7b\u58f0\u9053\uff1a\u201c\u56db\u59d1\uff0c\u9752\u51e4\u4eca\u5929\u597d\u50cf\u6709\u4e9b\u4e0d\u592a\u5bf9\u52b2\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u6697\u81ea\u4e00\u60ca\uff0c\u6025\u95ee\uff1a\u201c\u5979\u600e\u4e48\u4e86\uff1f\u201d \u3000\u3000\u7f57\u5170\u5fe7\u5fc3\u5fe1\u4ef2\u9053\uff1a\u201c\u4e24\u4e2a\u4e2b\u5934\u56de\u623f\u53bb\u540e\uff0c\u4e00\u76f4\u6ca1\u6709\u51fa\u623f\uff0c\u6211\u6709\u4e9b\u4e0d\u653e\u5fc3\uff0c\u60f3\u8ddf\u5979\u4eec\u597d\u597d\u8c08\u4e00\u8c08\u3002\u521a\u8fdb\u540e\u7985\u9662\uff0c\u5c31\u77a5\u89c1\u9752\u51e4\u6e9c\u8fdb\u4e86\u5979\u4eec\u623f\u91cc\u3002 \u3000\u3000\u6211\u5728\u623f\u5916\u5b88\u4e86\u534a\u5929\uff0c\u542c\u4e0d\u89c1\u4e00\u70b9\u52a8\u9759\uff0c\u53c8\u4e0d\u597d\u95ef\u8fdb\u623f\u53bb\u67e5\u770b\uff0c\u8d76\u7d27\u60f3\u901a\u77e5\u4f60\u3002\u9047\u89c1\u8001\u53eb\u5316\uff0c\u8bf4\u662f\u770b\u89c1\u4f60\u548c\u6c5f\u67ab\u53bb\u9644\u8fd1\u5de1\u89c6\u4e86\uff0c\u53ef\u662f\u6211\u5230\u5904\u627e\u4e0d\u5230\u4f60\u4eec\uff0c\u624d\u56de\u53bb\u53ec\u96c6\u4e86\u4ed6\u4eec\u56db\u5904\u5bfb\u627e\uff0c\u4e00\u76f4\u627e\u5230\u4e86\u8fd9\u91cc\u6765\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u5fc3\u77e5\u9752\u51e4\u5df2\u5f00\u59cb\u884c\u52a8\uff0c\u4f46\u4e0d\u4fbf\u5411\u7f57\u5170\u8bf4\u660e\uff0c\u53ea\u597d\u7b11\u7740\u8bf4\uff1a\u201c\u522b\u60ca\u5c0f\u602a\uff0c\u9752\u51e4\u53ea\u4e0d\u8fc7\u60f3\u6211\u4e24\u4e2a\u4e2b\u5934\u804a\u804a\uff0c\u627e\u673a\u4f1a\u5f00\u5bfc\u5979\u4eec\u7f62\u4e86\u3002\u201d \u3000\u3000\u7f57\u5170\u4e0d\u4ee5\u4e3a\u7136\u9053\uff1a\u201c\u4e0d\u662f\u6211\u5927\u60ca\u5c0f\u602a\uff0c\u8fd9\u662f\u4ec0\u4e48\u8282\u9aa8\u773c\uff0c\u5f3a\u654c\u5f53\u524d\uff0c\u53c8\u4e0d\u662f\u4ee5\u524d\u5728\u6b22\u573a\u732e\u5531\uff0c\u9752\u51e4\u4eca\u5929\u5c45\u7136\u523b\u610f\u6253\u626e\uff0c\u6d53\u5986\u8273\u62b9\uff0c\u82b1\u679d\u62db\u5c55\uff0c\u6211\u624d\u89c9\u5f97\u5979\u4e0d\u592a\u5bf9\u52b2\u54e6\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u7b11\u9053\uff1a\u201c\u7231\u7f8e\u662f\u5973\u4eba\u7684\u5929\u6027\uff0c\u4f55\u51b5\u5979\u90a3\u4e48\u5e74\u8f7b\uff0c\u4f60\u4e5f\u672a\u514d\u592a\u4fdd\u5b88\u4e86\u5427\u3002\u201d \u3000\u3000\u7f57\u5170\u53ea\u597d\u4e0d\u8bf4\u4e86\u3002 \u3000\u3000\u4e24\u4e09\u91cc\u8def\uff0c\u4ee5\u5404\u4eba\u7684\u8f7b\u529f\u65bd\u5c55\uff0c\u53ea\u7528\u4e00\u76cf\u70ed\u8336\u7684\u65f6\u95f4\uff0c\u4fbf\u5df2\u5954\u56de\u5230\u53e4\u5e99\u3002 \u3000\u3000\u5b88\u5728\u5e99\u95e8\u5916\u7684\u80e1\u5143\u548c\u90ed\u5929\u540c\uff0c\u5fd9\u4e0a\u524d\u606d\u8fce\u3002 \u3000\u3000\u6c5f\u67ab\u7b49\u4eba\u7b54\u4e86\u793c\uff0c\u8ff3\u5165\u5927\u6bbf\u3002 \u3000\u3000\u7f57\u5170\u5173\u5fc3\u96ea\u3001\u971c\u4e24\u4e2a\u5973\u513f\uff0c\u4e0d\u77e5\u9752\u51e4\u8ddf\u5979\u4eec\u5728\u623f\u91cc\u5e72\u561b\uff0c\u4e00\u56de\u5e99\u5c31\u8feb\u4e0d\u53ca\u5f85\u628a\u5f20\u56db\u59d1\u62d6\u53bb\u3002 \u3000\u3000\u5f20\u56db\u59d1\u5374\u662f\u803d\u5fc3\u6c5f\u67ab\u8bf4\u6f0f\u5634\uff0c\u5c06\u8c37\u4e2d\u60c5\u5f62\u548c\u76d8\u6258\u51fa\uff0c\u906d\u4eba\u80cc\u540e\u8bae\u8bba\u5012\u5728\u5176\u6b21\u3002\u4e07\u4e00\u4f20\u8fdb\u96ea\uff0c\u971c\u4e24\u59d0\u59b9\u8033\u91cc\uff0c\u9ebb\u70e6\u5c31\u66f4\u5927\u4e86\u3002 \u3000\u3000\u6bd5\u7adf\uff0c\u5979\u4e0e\u6c5f\u67ab\u7684\u4e0d\u5bfb\u5e38\u5173\u7cfb\uff0c\u540d\u65e2\u4e0d\u6b63\uff0c\u8a00\u4e5f\u4e0d\u987a\uff0c\u4f20\u5f00\u4e86\u8138\u4e0a\u5b9e\u5728\u6709\u4e9b\u6302\u4e0d\u4f4f\u3002 \u3000\u3000\u6765\u5230\u540e\u7985\u9662\uff0c\u5c1a\u672a\u8d70\u8fd1\u4e24\u59d0\u59b9\u4f4f\u7684\u623f\uff0c\u5c31\u542c\u4f20\u51fa\u9635\u9635\u5b09\u7b11\u58f0\uff0c\u4f7f\u5f20\u56db\u59d1\u548c\u7f57\u5170\u4e0d\u7531\u5730\u6b62\u6b65\u4e0d\u524d\u3002 \u3000\u3000\u7f57\u5170\u4e00\u7751\u832b\u7136\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u56db\u59d1\uff0c\u600e\u4e48\u56de\u4e8b\uff1f\u201d \u3000\u3000\u5f20\u56db\u59d1\u5fc3\u91cc\u6709\u6570\uff0c\u663e\u7136\u9752\u51e4\u5df2\u83b7\u8fdb\u5c55\uff0c\u81f3\u5c11\u96ea\u3001\u971c\u4e24\u59d0\u59b9\u5e76\u672a\u6392\u65a5\uff0c\u62d2\u9752\u51e4\u4e8e\u5343\u91cc\u4e4b\u5916\u3002 \u3000\u3000\u6548\u679c\u5982\u4f55\uff0c\u867d\u4e0d\u5f97\u800c\u77e5\uff0c\u4f46\u8fd9\u662f\u4e2a\u597d\u7684\u5f00\u59cb\u3002 \u3000\u3000\u201c\u7f57\u5170\uff0c\u5979\u4eec\u76f8\u5904\u5f97\u5f88\u878d\u6d3d\uff0c\u4f60\u803d\u5fc3\u4e2a\u4ec0\u4e48\uff1f\u201d\u5f20\u56db\u59d1\u7b11\u9053\u62d6\u4e86\u7f57\u5170\u4e00\u628a\uff1a\u201c\u6211\u4eec\u53bb\u5927\u6bbf\u8c08\u6b63\u4e8b\u5427\uff01\u201d \u3000\u3000\u7f57\u5170\u7efd\u5f00\u4e86\u6b23\u6170\u7684\u7b11\u5bb9\u3002 \u3000\u3000\u4e24\u4eba\u5230\u5927\u6bbf\uff0c\u6c5f\u67ab\u7684\u8bdd\u521a\u8bf4\u5b8c\u3002 \u3000\u3000\u4ece\u5404\u4eba\u4e25\u8083\u7684\u795e\u60c5\u4e0a\u53ef\u4ee5\u770b\u51fa\uff0c\u6c5f\u67ab\u5b9a\u7136\u662f\u907f\u91cd\u5c31\u8f7b\uff0c\u4ec5\u5c06\u7af9\u6797\u53ca\u8c37\u5185\u7684\u60c5\u51b5\u8bf4\u51fa\uff0c\u7701\u7565\u4e86\u4ed6\u4e0e\u5f20\u56db\u59d1\u90a3\u6bb5\u4e0d\u53ef\u544a\u4eba\u7684\u79d8\u5bc6\u3002 \u3000\u3000\u5f20\u56db\u59d1\u8fd9\u624d\u5982\u91ca\u91cd\u8d1f\uff0c\u8212\u5766\u7684\u677e\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u4e00\u8fde\u4e24\u5929\uff0c\u795e\u79d8\u6559\u4e3b\u65b9\u9762\u6ca1\u6709\u4efb\u4f55\u4e3e\u52a8\u3002 \u3000\u3000\u96be\u9053\u4ed6\u4eec\u771f\u7684\u77e5\u96be\u800c\u9000\uff0c\u5df2\u5927\u519b\u64a4\u53bb\uff0c\u8fdc\u79bb\u5883\u5185\uff1f \u3000\u3000\u8fd9\u79cd\u5047\u5b9a\u4f3c\u4e4e\u4e0d\u592a\u53ef\u80fd\u3002 \u3000\u3000\u9996\u5148\uff0c\u795e\u79d8\u6559\u4e3b\u82b1\u4e86\u4e8c\u5341\u5e74\u5fc3\u8840\uff0c\u529f\u4e8f\u4e00\u7bd1\uff0c\u88ab\u6c5f\u67ab\u7834\u574f\u5927\u4e8b\uff0c\u5fc5\u7136\u4e8e\u5fc3\u4e0d\u7518\uff0c\u7edd\u4e0d\u4f1a\u5c31\u6b64\u7f62\u624b\u3002 \u3000\u3000\u5176\u6b21\uff0c\u6c5f\u67ab\u4e0d\u9664\uff0c\u4ed6\u4eec\u7eb5\u6709\u5377\u571f\u91cd\u6765\uff0c\u4e1c\u5c71\u518d\u8d77\u7684\u96c4\u5fc3\uff0c\u5fc3\u8179\u4e4b\u60a3\u4ecd\u7136\u5b58\u5728\u3002 \u3000\u3000\u5c3d\u7ba1\u8fde\u756a\u5143\u6c14\u5927\u4f24\uff0c\u4f46\u4ed6\u4eec\u4ecd\u5177\u6709\u51b3\u4e00\u6b7b\u6218\u7684\u5b9e\u529b\uff0c\u4e0d\u81f4\u4e0d\u6218\u800c\u9000\u3002 \u3000\u3000\u518d\u8bf4\u5427\uff0c\u65e2\u7136\u51b3\u5fc3\u8fdc\u884c\uff0c\u53c8\u4f55\u5fc5\u52b3\u5e08\u52a8\u4f17\uff0c\u5728\u4e34\u8d70\u4e4b\u524d\uff0c\u8fde\u591c\u5e03\u4e0b\u90a3\u5ea7\u7af9\u6797\uff0c\u4e14\u5728\u8c37\u4e2d\u8bbe\u7f6e\u516b\u5ea7\u7537\u5973\u4ea4\u5408\u7684\u77f3\u96d5\uff1f \u3000\u3000\u56e0\u6b64\uff0c\u4ece\u79cd\u79cd\u8ff9\u50cf\u770b\u6765\uff0c\u795e\u79d8\u6559\u4e3b\u4ecd\u672a\u6b7b\u5fc3\u3002 \u3000\u3000\u8fde\u65e5\u6beb\u65e0\u52a8\u9759\uff0c\u4e5f\u8bb8\u6b63\u662f\u66b4\u98ce\u96e8\u524d\u7684\u5b81\u9759\u5427\uff01 \u3000\u3000\u8352\u5e99\u65b9\u9762\u4e0d\u6562\u677e\u5f1b\u6212\u5907\u548c\u9632\u8303\uff0c\u4ed6\u4eec\u5df2\u5728\u4e24\u65e5\u5185\uff0c\u7531\u5f20\u56db\u59d1\u6559\u4f1a\u4e86\u5404\u4eba\u7b80\u6613\u901f\u6210\u9041\u6cd5\u4e4b\u827a\uff0c\u85c9\u4ee5\u63a9\u62a4\u5f62\u85cf\uff0c\u6563\u5e03\u56db\u5468\u62c5\u4efb\u8b66\u6212\u3002 \u3000\u3000\u540c\u65f6\uff0c\u5404\u4eba\u5747\u5907\u6709\u6697\u5668\uff0c\u4e00\u65e6\u53d1\u73b0\u4efb\u4f55\u654c\u4eba\uff0c\u7edd\u4e0d\u5bb9\u4ed6\u4eec\u8fd1\u8eab\uff1b\u7acb\u5373\u4ee5\u6697\u5668\u51fa\u624b\uff0c\u6bd9\u654c\u4e8e\u4e24\u4e08\u4e4b\u5916\uff0c\u4ee5\u9632\u5bf9\u65b9\u6d3e\u6765\u7684\u6562\u6b7b\u961f\uff0c\u4e0d\u60dc\u7528\u70b8\u836f\u540c\u5f52\u4e8e\u5c3d\u3002 \u3000\u3000\u4e00\u5207\u7531\u5f20\u56db\u59d1\u8fd0\u7b79\u5e37\u5e44\uff0c\u6307\u6325\u82e5\u5b9a\uff0c\u5206\u6d3e\u5404\u4eba\u4efb\u52a1\uff0c\u65e5\u591c\u4fdd\u6301\u5907\u6218\u72b6\u6001\uff0c\u6beb\u4e0d\u677e\u61c8\u3002 \u3000\u3000\u8fd9\u4e24\u5929\u6c5f\u67ab\u5012\u5f88\u8f7b\u677e\uff0c\u96ea\u3001\u971c\u4e24\u59d0\u59b9\u5c45\u7136\u672a\u6765\u7ea0\u7f20\uff0c\u4f7f\u4ed6\u611f\u5230\u975e\u5e38\u610f\u5916\u3002 \u3000\u3000\u600e\u4e48\u56de\u4e8b\uff1f \u3000\u3000\u6c5f\u67ab\u4e3a\u4e86\u67e5\u660e\u539f\u56e0\uff0c\u8d81\u7740\u5927\u5bb6\u5fd9\u7740\u8d76\u5236\u6697\u5668\u5907\u7528\uff0c\u51b3\u5fc3\u6084\u7136\u6e9c\u81f3\u540e\u7985\u9662\u4e00\u63a2\u7a76\u7adf\u3002 \u3000\u3000\u4e0d\u6599\u521a\u8de8\u5165\u9662\u95e8\uff0c\u5c31\u88ab\u4ece\u65c1\u95e8\u51fa\u7684\u5f20\u56db\u59d1\u62e6\u4f4f\uff0c\u628a\u4ed6\u62d6\u8fdb\u5bf9\u9762\u7684\u5ba2\u623f\u3002 \u3000\u3000\u201c\u5927\u59d0\uff0c\u5e72\u561b\u795e\u79d8\u516e\u516e\u7684\uff1f\u201d\u6c5f\u67ab\u83ab\u540d\u5176\u5999\u3002 \u3000\u3000\u5f20\u56db\u59d1\u6309\u4ed6\u5750\u4e0b\u540e\uff0c\u624d\u7b11\u95ee\uff1a\u201c\u5c0f\u60c5\u90ce\uff0c\u8fd9\u4e24\u65e5\u672a\u89c1\u96ea\u3001\u971c\u4e24\u59d0\u59b9\u51fa\u623f\uff0c\u662f\u4e0d\u662f\u60f3\u5979\u4eec\u4e86\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u6b63\u8272\u9053\uff1a\u201c\u6211\u803d\u5fc3\u5979\u4eec\u8eb2\u5728\u623f\u91cc\uff0c\u4e0d\u77e5\u53c8\u5728\u6697\u4e2d\u7ec3\u2018\u4f20\u771f\u8bb0\u2019\u4ec0\u4e48\u66f4\u9738\u9053\u7684\u6740\u4eba\u90aa\u529f\u54ea\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u6545\u610f\u9053\uff1a\u201c\u6ca1\u9519\uff0c\u5979\u4eec\u7ec3\u7684\u662f\u2018\u79fb\u60c5\u529f\u2019\uff01\u201d \u3000\u3000\u201c\u79fb\u60c5\u529f\uff1f\u201d\u6c5f\u67ab\u542c\u5f97\u6ee1\u5934\u6c57\u6c34\uff0c\u5bf9\u8fd9\u964c\u751f\u7684\u6b66\u529f\uff0c\u597d\u50cf\u4ece\u672a\u542c\u8fc7\u3002 \u3000\u3000\u5f20\u56db\u59d1\u4ecd\u7136\u4e00\u672c\u6b63\u7ecf\u9053\uff1a\u201c\u8fd9\u795e\u529f\u592b\u4e00\u65e6\u7ec3\u6210\uff0c\u522b\u8bf4\u662f\u4f60\u6c5f\u67ab\uff0c\u7eb5\u7136\u662f\u7f8e\u7537\u5b50\u6f58\u5b89\u518d\u4e16\uff0c\u5979\u4eec\u4e5f\u4e0d\u5c51\u591a\u770b\u4e00\u773c\u5462\uff01\u201d \u3000\u3000\u6c5f\u67ab\u5f3a\u81ea\u4e00\u7b11\u9053\uff1a\u201c\u54e6\uff1f\u987e\u540d\u601d\u4e49\uff0c\u8fd9\u2018\u79fb\u60c5\u529f\u2019\uff0c\u60f3\u5fc5\u662f\u4e00\u79cd\u79fb\u60c5\u522b\u604b\u7684\u5947\u529f\u4e86\uff0c\u4f46\u4e0d\u77e5\u51fa\u81ea\u4f55\u95e8\u4f55\u6d3e\uff0c\u4f20\u6388\u5979\u4eec\u7684\u53c8\u662f\u54ea\u4f4d\u540d\u5e08\uff1f\u201d \u3000\u3000\u5f20\u56db\u59d1\u9053\uff1a\u201c\u6b64\u4e43\u5929\u673a\uff0c\u4e0d\u53ef\u6cc4\u9732\uff0c\u5c0f\u60c5\u90ce\uff0c\u8fd9\u4e24\u5929\u5979\u4eec\u4e24\u59d0\u59b9\u5f88\u5b89\u9759\uff0c\u4f60\u4e5f\u843d\u5f97\u6e05\u95f2\uff0c\u6700\u597d\u4e0d\u8981\u53bb\u9a9a\u6270\u5979\u4eec\uff0c\u4ee5\u514d\u81ea\u627e\u9ebb\u70e6\uff0c\u8fd9\u662f\u6211\u7ed9\u4f60\u7684\u5fe0\u544a\u3002\u201d \u3000\u3000\u201c\u5927\u59d0\u4e4b\u547d\uff0c\u5c0f\u5f1f\u54ea\u6562\u4e0d\u4ece\uff01\u201d\u6c5f\u67ab\u6545\u4f5c\u4e25\u8083\u9053\uff1a\u201c\u4f46\u5927\u59d0\u65e2\u5df2\u900f\u9732\u7384\u673a\uff0c\u4e0d\u5982\u5e72\u8106\u660e\u544a\uff0c\u53c8\u4f55\u5fc5\u540a\u6211\u80c3\u53e3\u5462\uff1f\u201d \u3000\u3000\u5f20\u56db\u59d1\u770b\u770b\u4ed6\uff0c\u72b9\u8c6b\u4e86\u4e00\u4e0b\uff0c\u5ffd\u95ee\uff1a\u201c\u4f60\u4e0d\u5e0c\u671b\u5927\u59d0\u4eb2\u624b\u6740\u5979\u4eec\u5427\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u542c\u5f97\u6697\u81ea\u4e00\u60ca\uff0c\u90d1\u91cd\u9053\uff1a\u201c\u90a3\u5f53\u7136\uff0c\u76f8\u4fe1\u5927\u59d0\u4e5f\u4e0d\u613f\u771f\u6709\u8fd9\u4e00\u5929\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u9053\uff1a\u201c\u6240\u4ee5\u6211\u8981\u8bbe\u6cd5\u6551\u5979\u4eec\uff0c\u90a3\u6015\u662f\u5e0c\u671b\u4e0d\u5927\uff0c\u4e5f\u5f97\u5c3d\u529b\u4e00\u52a9\u3002\u201d \u3000\u3000\u6c5f\u67ab\u6025\u95ee\uff1a\u201c\u5927\u59d0\uff0c\u6211\u80fd\u5e2e\u5f97\u4e0a\u5fd9\u5417\uff1f\u201d \u3000\u3000\u5f20\u56db\u59d1\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u8fde\u6211\u90fd\u63d2\u4e0d\u4e0a\u624b\uff0c\u4f60\u80fd\u5e2e\u4ec0\u4e48\u4e48\u5fd9\uff0c\u5c31\u8ba9\u9752\u51e4\u2026\u201d\u53d1\u89c9\u8bf4\u6e9c\u4e86\u5634\uff0c\u6025\u5fd9\u628a\u8bdd\u6b62\u4f4f\u3002 \u3000\u3000\u6c5f\u67ab\u4e00\u8bc1\uff0c\u60ca\u8be7\u9053\uff1a\u201c\u9752\u51e4\uff01\u5979\u5728\u73a9\u4ec0\u4e48\u82b1\u6837\uff1f\u201d \u3000\u3000\u5f20\u56db\u59d1\u65e2\u5df2\u8bf4\u6e9c\u5634\uff0c\u53ea\u597d\u8bf4\u51fa\u4e86\u539f\u59d4\u3002 \u3000\u3000\u6c5f\u67ab\u542c\u6bd5\uff0c\u82e5\u6709\u6240\u609f\u9053\uff1a\u201c\u96be\u602a\u8fd9\u4e24\u5929\u6211\u4e5f\u6ca1\u6709\u5230\u9752\u51e4\uff0c\u539f\u6765\u5927\u59d0\u53e6\u6709\u4efb\u52a1\u4ea4\u4ed8\u7ed9\u5979\u54e6\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u9053\uff1a\u201c\u8fd9\u4e24\u5929\u4e24\u4e2a\u4e2b\u5934\u5f88\u5b89\u9759\uff0c\u4e5f\u8bb8\u9752\u51e4\u7684\u65b9\u6cd5\u771f\u7684\u594f\u6548\u4e86\u3002\u201d \u3000\u3000\u201c\u4f46\u613f\u5982\u6b64\uff01\u201d\u6c5f\u67ab\u51b5\u541f\u4e00\u4e0b\uff0c\u586b\uff1a\u201c\u53ef\u662f\uff0c\u5927\u59d0\u53ef\u66fe\u8003\u8651\u5230\uff0c\u4e00\u65e6\u5979\u4eec\u4e50\u6b64\u4e0d\u5026\uff0c\u5c06\u6765\u2026\u2026\u201d \u3000\u3000\u5f20\u56db\u59d1\u7b11\u9053\uff1a\u201c\u6015\u5979\u4eec\u5bf9\u4f60\u4e0d\u611f\u5174\u8da3\u4e86\uff1f\u201d\u6c5f\u67ab\u5c34\u5c2c\u9053\uff1a\u201c\u8fd8\u6709\u9752\u51e4\u554a\uff01\u201d \u3000\u3000\u5f20\u56db\u59d1\u5b89\u629a\u9053\uff1a\u201c\u9752\u51e4\u5012\u4e0d\u7528\u4e3a\u5979\u803d\u5fc3\uff0c\u5979\u65e2\u6df1\u6089\u5176\u6cd5\uff0c\u5f53\u77e5\u81ea\u5236\uff0c\u7edd\u4e0d\u4f1a\u6c89\u8ff7\u5176\u4e2d\u7684\u3002\u81f3\u4e8e\u96ea\u3001\u971c\u4e24\u59d0\u59b9\u561b\uff0c\u76ee\u524d\u53ea\u6c42\u80fd\u6682\u65f6\u6d88\u9664\u5979\u4eec\u7684\u51f6\u6740\u4e4b\u6c14\uff0c\u4ee5\u540e\u603b\u4f1a\u60f3\u51fa\u529e\u6cd5\u633d\u6551\u3002\u201d \u3000\u3000\u6c5f\u67ab\u53f9\u9053\uff1a\u201c\u5509\uff0c\u4e00\u4e0b\u5b50\u5931\u53bb\u4e09\u4e2a\u5927\u7f8e\u4eba\uff0c\u6211\u7684\u635f\u5931\u5b9e\u5728\u592a\u5927\u4e86\u3002\u5927\u59d0\uff0c\u4f60\u6253\u7b97\u600e\u6837\u8865\u507f\u6211\uff1f\u201d \u3000\u3000\u5f20\u56db\u59d1\u81ea\u6211\u89e3\u5632\u9053\uff1a\u201c\u5927\u59d0\u867d\u7136\u540c\u60c5\u4f60\uff0c\u53ef\u60dc\u6211\u8fd9\u534a\u8001\u5f90\u5a18\uff0c\u600e\u80fd\u8ddf\u4f60\u7684\u5927\u7f8e\u4eba\u6bd4\uff0c\u60f3\u8865\u507f\u4e5f\u672a\u5fc5\u4e2d\u4f60\u7684\u610f\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u51fa\u5176\u4e0d\u610f\u5730\uff0c\u4e0a\u524d\u62b1\u4f4f\u5979\u5c31\u543b\u3002 \u3000\u3000\u5f20\u56db\u59d1\u5fd9\u63a8\u62d2\u9053\uff1a\u201c\u522b\u80e1\u95f9\uff0c\u6211\u4eec\u8c08\u6b63\u4e8b\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u53ea\u597d\u653e\u5f00\u5979\uff0c\u7b11\u7b11\u9053\uff1a\u201c\u8fd8\u6709\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u5f20\u56db\u59d1\u6574\u7406\u4e00\u4e0b\u8863\u886b\uff0c\u6b63\u8272\u9053\uff1a\u201c\u654c\u4eba\u4e00\u8fde\u4e09\u5929\u6beb\u65e0\u52a8\u9759\uff0c\u8fd9\u60c5\u5f62\u9887\u6709\u8e4a\u8df7\uff0c\u6240\u4ee5\u6211\u5728\u60f3\uff0c\u6211\u4eec\u4e0d\u80fd\u8001\u662f\u5904\u4e8e\u88ab\u52a8\uff0c\u56f0\u5728\u8fd9\u91cc\u7b49\u7740\u6328\u6253\uff0c\u5e94\u8be5\u91c7\u53d6\u4e3b\u52a8\u624d\u662f\u3002\u201d \u3000\u3000\u6c5f\u67ab\u671b\u7740\u5979\u95ee\uff1a\u201c\u5927\u59d0\u6709\u4ec0\u4e48\u4e3b\u610f\uff1f\u201d \u3000\u3000\u5f20\u56db\u59d1\u80f8\u6709\u6210\u7af9\u9053\uff1a\u201c\u84dd\u51e4\u662f\u6211\u4fdd\u7559\u7684\u4e00\u6b65\u6697\u68cb\uff0c\u73b0\u5728\u53ef\u4ee5\u6d3e\u4e0a\u7528\u573a\u4e86\u3002\u201d \u3000\u3000\u201c\u54e6\uff1f\u201d\u6c5f\u67ab\u8be7\u5f02\u9053\uff1a\u201c\u5979\u80fd\u6d3e\u4e0a\u4ec0\u4e48\u7528\u573a\uff1f\u201d \u3000\u3000\u5f20\u56db\u59d1\u9053\uff1a\u201c\u90a3\u591c\u5979\u662f\u5949\u795e\u79d8\u6559\u4e3b\u4e4b\u547d\uff0c\u524d\u6765\u4f20\u8bdd\u7684\uff0c\u867d\u7136\u7531\u4f60\u5192\u5145\u5979\u56de\u53bb\u590d\u547d\uff0c\u640f\u6740\u672a\u6210\uff0c\u4f46\u5bf9\u65b9\u81f3\u4eca\u5c1a\u4e0d\u77e5\u5979\u6b7b\u6d3b\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u5979\u5df2\u843d\u5728\u6211\u4eec\u624b\u4e2d\uff0c\u90a3\u662f\u6beb\u65e0\u7591\u95ee\u7684\u3002 \u3000\u3000\u795e\u79d8\u6559\u4e3b\u7edd\u6599\u4e0d\u5230\uff0c\u84dd\u51e4\u65e9\u5df2\u5f52\u987a\u6211\u4eec\uff0c\u8fd9\u5f53\u7136\u662f\u4f60\u548c\u9752\u51e4\u7684\u529f\u52b3\uff0c\u4ed6\u4e00\u5b9a\u4ee5\u4e3a\u84dd\u98ce\u90a3\u591c\u88ab\u6293\uff0c\u53d7\u4e0d\u4e86\u9177\u5211\uff0c\u88ab\u8feb\u8bf4\u51fa\u4e86\u4ed6\u4eec\u7684\u4eba\u85cf\u8eab\u4e4b\u5904\u3002 \u3000\u3000\u5982\u679c\u4e0d\u51fa\u6240\u6599\uff0c\u795e\u79d8\u6559\u4e3b\u5229\u7528\u84dd\u51e4\u6765\u4f20\u8bdd\uff0c\u53c8\u6307\u5b9a\u5979\u56de\u90a3\u5bc6\u6797\u53bb\u590d\u547d\uff0c\u76ee\u7684\u662f\u8981\u8bf1\u4f7f\u4f60\u53bb\u8ddf\u8e2a\u84dd\u51e4\uff0c\u6240\u4ee5\u5e03\u4e0b\u9677\u9631\uff0c\u7b49\u4f60\u53bb\u81ea\u6295\u7f57\u7f51\u3002 \u3000\u3000\u6ca1\u60f3\u5230\u4f60\u5e76\u975e\u8ddf\u8e2a\uff0c\u800c\u662f\u4eb2\u81ea\u626e\u6210\u84dd\u51e4\uff0c\u8fd9\u4e00\u62cd\u5b8c\u5168\u51fa\u4e4e\u5bf9\u65b9\u610f\u6599\u4e4b\u5916\u3002 \u3000\u3000\u8fd9\u4e09\u5929\u6beb\u65e0\u52a8\u9759\uff0c\u4e3b\u8981\u662f\u4ed6\u4eec\u4e0d\u60dc\u52b3\u5e08\u52a8\u4f17\uff0c\u5e03\u4e0b\u7684\u7591\u9635\u672a\u80fd\u8bf1\u4f7f\u6211\u4eec\u5165\u8c37\u4e00\u63a2\uff0c\u88ab\u8ff7\u836f\u4e71\u6027\uff0c\u5f15\u8d77\u81ea\u76f8\u6b8b\u6740\u3002 \u3000\u3000\u4e5f\u53ef\u80fd\u662f\u5728\u7b49\u5f85\u66f4\u5f3a\u7684\u63f4\u5175\u8d76\u6765\uff0c\u4e0d\u6562\u8f7b\u4e3e\u5984\u52a8\u3002 \u3000\u3000\u7531\u6b64\u53ef\u89c1\uff0c\u4ee5\u5bf9\u65b9\u76ee\u524d\u7684\u5b9e\u529b\uff0c\u5c1a\u65e0\u628a\u63e1\u7a33\u64cd\u80dc\u7b97\uff0c\u4e00\u4e3e\u5c06\u6211\u4eec\u6b7c\u706d\uff0c\u624d\u4f1a\u6309\u5175\u4e0d\u52a8\uff0c\u53ea\u5728\u6697\u4e2d\u76d1\u89c6\u3002 \u3000\u3000\u6240\u4ee5\u6211\u5728\u60f3\uff0c\u5982\u679c\u5b89\u6392\u84dd\u51e4\u88c5\u6210\u4ece\u8fd9\u91cc\u4f3a\u673a\u9003\u51fa\uff0c\u9003\u56de\u795e\u79d8\u6559\u4e3b\u90a3\u8fb9\uff0c\u90a3\u4e48\u5979\u8fd9\u6b65\u6b7b\u68cb\u5c31\u53c8\u6210\u4e86\u6d3b\u68cb\u3002\u201d \u3000\u3000\u201c\u5927\u59d0\u662f\u8981\u5979\u7ee7\u7eed\u5367\u5e95\uff0c\u505a\u6211\u4eec\u7684\u5185\u5e94\uff1f\u201d\u6c5f\u67ab\u5411\u3002 \u3000\u3000\u5f20\u56db\u59d1\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u6b63\u662f\u3002\u5f53\u521d\u6211\u4eec\u62c9\u5979\u8fc7\u6765\uff0c\u76ee\u7684\u5c31\u662f\u4e3a\u4e86\u8fd9\u4e2a\u3002 \u3000\u3000\u5982\u4eca\u795e\u79d8\u6559\u4e3b\u5c1a\u672a\u5bf9\u5979\u6000\u7591\uff0c\u800c\u6211\u4eec\u53c8\u6ca1\u6709\u4efb\u4f55\u53ef\u4ee5\u66ff\u4ee3\u5979\u7684\u4eba\u53ef\u7528\uff0c\u4f55\u4e0d\u5c31\u5730\u53d6\u6750\uff0c\u628a\u5979\u6d3e\u4e0a\u7528\u573a\u5462\u3002\u201d \u3000\u3000\u6c5f\u67ab\u9053\uff1a\u201c\u5927\u59d0\u8fd9\u4e2a\u4e3b\u610f\u662f\u4e0d\u9519\uff0c\u4f46\u5979\u4e5f\u4e0d\u77e5\u9053\uff0c\u5982\u4eca\u5bf9\u65b9\u7684\u5927\u6279\u4eba\u9a6c\u5728\u54ea\u91cc\u5440\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u7b11\u9053\uff1a\u201c\u5c0f\u60c5\u90ce\uff0c\u4f60\u6700\u8fd1\u662f\u600e\u4e48\u4e86\uff0c\u8111\u7b4b\u597d\u50cf\u6253\u4e86\u7ed3\u4f3c\u7684\u3002\u5bf9\u65b9\u65e2\u6709\u4eba\u5728\u6697\u4e2d\u76d1\u89c6\uff0c\u84dd\u51e4\u4e00\u65e6\u9003\u51fa\uff0c\u4ed6\u4eec\u4f1a\u4e0d\u53d1\u73b0\uff1f\u6839\u672c\u4e0d\u7528\u84dd\u51e4\u53bb\u627e\u4ed6\u4eec\uff0c\u4ed6\u4eec\u81ea\u4f1a\u627e\u4e0a\u84dd\u51e4\u3002\u201d \u3000\u3000\u6c5f\u67ab\u70b9\u4e86\u70b9\u5934\u9053\uff1a\u201c\u4f46\u6211\u4eec\u4e00\u5b9a\u5f97\u5047\u620f\u771f\u505a\uff0c\u8868\u6f14\u903c\u771f\uff0c\u5343\u4e07\u4e0d\u80fd\u88ab\u6697\u4e2d\u76d1\u89c6\u7684\u4eba\u77a7\u51fa\u7834\u7efd\u624d\u884c\u3002\u201d \u3000\u3000\u201c\u90a3\u5f53\u7136\uff0c\u5426\u5219\u6211\u4eec\u5c31\u4f1a\u5bb3\u6b7b\u84dd\u51e4\u3002\u201d \u3000\u3000\u4e8e\u662f\uff0c\u4ed6\u4eec\u7f1c\u5bc6\u8ba1\u5212\uff0c\u5b89\u6392\u4e86\u84dd\u51e4\u7684\u9003\u51fa\u3002 \u3000\u3000\u591c\u53c8\u6765\u4e34\u4e86\u3002 \u3000\u3000\u4eca\u591c\u6708\u6717\u98ce\u9ad8\uff0c\u8352\u5e99\u5468\u56f4\u663e\u5f97\u683c\u5916\u5b81\u9759\uff0c\u51c4\u51c9\u3002 \u3000\u3000\u7a81\u7136\u95f4\uff0c\u4ece\u5927\u6bbf\u65ad\u65ad\u7eed\u7eed\u5730\uff0c\u4f20\u51fa\u58f0\u58f0\u51c4\u5389\u7684\u5973\u5b50\u60e8\u53eb\u58f0\uff0c\u5212\u7834\u4e86\u591c\u7684\u6c89\u5bc2\u3002 \u3000\u3000\u5927\u6bbf\u7684\u56db\u6839\u5706\u67f1\u4e0a\uff0c\u90fd\u63d2\u7740\u677e\u6cb9\u706b\u628a\u3002 \u3000\u3000\u706b\u5149\u6447\u66f3\u4e0b\uff0c\u53ea\u89c1\u84dd\u51e4\u5012\u5367\u5728\u5730\u4e0a\uff0c\u8eab\u4e0a\u8863\u886b\u5df2\u88ab\u76ae\u97ad\u62bd\u5f97\u8863\u4e0d\u853d\u4f53\uff0c\u5f62\u540c\u534a\u88f8\u3002 \u3000\u3000\u7ad9\u5728\u5979\u8eab\u8fb9\u7684\uff0c\u6b63\u662f\u80e1\u5143\u548c\u90ed\u5929\u540c\uff0c\u4ed6\u4eec\u624b\u4e0a\u90fd\u63e1\u7740\u76ae\u97ad\u3002\u800c\u7aef\u5750\u5728\u4f9b\u684c\u65c1\u4e00\u5f20\u7834\u6905\u4e0a\u7684\uff0c\u5219\u662f\u94c1\u9752\u7740\u8138\u7684\u5f20\u56db\u59d1\u3002 \u3000\u3000\u6c5f\u67ab\u4e0d\u5728\u573a\u3002 \u3000\u3000\u5927\u548c\u5c1a\u4e0e\u8001\u53eb\u5316\uff0c\u9ed8\u9ed8\u5206\u7acb\u5728\u5f20\u56db\u59d1\u4e24\u65c1\u3002 \u3000\u3000\u4ece\u8fd9\u573a\u9762\u53ef\u4ee5\u770b\u51fa\uff0c\u84dd\u98ce\u6b63\u5728\u88ab\u97ad\u631e\u903c\u4f9b\u3002 \u3000\u3000\u53ea\u89c1\u5f20\u56db\u59d1\u4e00\u4f7f\u773c\u8272\uff0c\u80e1\u5143\u624b\u4e2d\u76ae\u97ad\u4e00\u626c\uff0c\u72e0\u72e0\u62bd\u5728\u4e86\u84dd\u51e4\u8eab\u4e0a\u3002 \u3000\u3000\u8fd9\u662f\u4e00\u5e55\u8868\u6f14\u903c\u771f\u7684\u903c\u4f9b\u3002 \u3000\u3000\u5c3d\u7ba1\u5e76\u65e0\u201c\u5916\u4eba\u201d\u5728\u573a\uff0c\u80e1\u5143\u53ef\u662f\u771f\u62bd\u771f\u6253\uff0c\u624b\u4e0b\u6beb\u4e0d\u7559\u60c5\u3002 \u3000\u3000\u84dd\u51e4\u75db\u5f97\u60e8\u53eb\u4e00\u58f0\uff0c\u6ce3\u9053\uff1a\u201c\u6211\u771f\u7684\u4e0d\u77e5\u9053\u54e6\uff0c\u4f60\u4eec\u6253\u6b7b\u6211\u4e5f\u6ca1\u6709\u7528\u2026\u2026\u201d \u3000\u3000\u5f20\u56db\u59d1\u6012\u5f62\u4e8e\u8272\u9053\uff1a\u201c\u4f60\u662f\u5929\u738b\u95e8\u7684\u624d\u5973\uff0c\u53c8\u662f\u90a3\u795e\u79d8\u6559\u4e3b\u7684\u4eb2\u4fe1\uff0c\u600e\u4f1a\u4e0d\u77e5\u4ed6\u4eec\u7684\u85cf\u8eab\u4e4b\u5904\uff1f\u5982\u4eca\u4f60\u5df2\u843d\u5728\u6211\u4eec\u624b\u4e2d\uff0c\u63d2\u7fc5\u96be\u98de\uff0c\u653e\u806a\u660e\u4e9b\u5427\uff01\u514d\u5f97\u76ae\u8089\u53d7\u82e6\uff01\u201d \u3000\u3000\u84dd\u51e4\u6124\u58f0\u5927\u53eb\uff1a\u201c\u6211\u771f\u7684\u4e0d\u77e5\u9053\uff01\u201d \u3000\u3000\u5f20\u56db\u59d1\u6012\u65a5\u58f0\u4e2d\uff0c\u4e00\u4f7f\u773c\u8272\uff0c\u80e1\u5143\u4e0e\u90ed\u5929\u540c\u7acb\u65f6\u626c\u8d77\u76ae\u97ad\uff0c\u8f6e\u6d41\u5411\u84dd\u51e4\u8eab\u4e0a\u62bd\u6253\u3002 \u3000\u3000\u84dd\u51e4\u8fde\u58f0\u60e8\u547c\u54c0\u53f7\uff0c\u4ee4\u4eba\u4e0d\u5fcd\u5352\u95fb\u3002 \u3000\u3000\u5927\u548c\u5c1a\u7ec8\u4e8e\u6309\u637a\u4e0d\u4f4f\uff0c\u65e5\u5ba3\u53f7\u9053\uff1a\u201c\u963f\u5f25\u9640\u4f5b\uff01\u59d1\u5a18\u8fd8\u662f\u8bf4\u4e86\u5427\uff01\u4f55\u5fc5\u53d7\u8fd9\u76ae\u8089\u4e4b\u82e6\u3002\u201d \u3000\u3000\u80e1\u5143\uff0c\u90ed\u5929\u540c\u5e76\u672a\u4f4f\u624b\uff0c\u4ecd\u7136\u4e00\u97ad\u97ad\u62bd\u4e0b\u3002 \u3000\u3000\u84dd\u51e4\u5df2\u58f0\u5636\u529b\u7aed\uff0c\u60e8\u547c\u54c0\u53f7\u58f0\u9010\u6e10\u8870\u5f31\u2026\u2026 \u3000\u3000\u8001\u53eb\u5316\u4e5f\u5f00\u4e86\u53e3\uff1a\u201c\u5f20\u59d1\u5a18\uff0c\u6211\u770b\u5979\u5feb\u633a\u4e0d\u4f4f\u4e86\uff0c\u53ef\u4ee5\u6b47\u624b\u5566\u3002 \u3000\u3000\u7559\u5979\u4e00\u547d\uff0c\u603b\u6bd4\u6b7b\u7684\u6709\u7528\u54e6\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u8fd9\u624d\u559d\u4ee4\u9053\uff1a\u201c\u597d\u4e86\uff0c\u4eca\u591c\u5230\u6b64\u4e3a\u6b62\u3002\u201d \u3000\u3000\u80e1\u5143\u3001\u90ed\u592d\u540c\u4f4f\u4e86\u624b\u3002 \u3000\u3000\u5f20\u56db\u59d1\u51b7\u54fc\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u628a\u5979\u5e26\u56de\u540e\u7985\u9662\u53bb\u5173\u8d77\u6765\uff0c\u6d3e\u4eba\u597d\u597d\u770b\u4f4f\uff0c\u660e\u5929\u518d\u95ee\uff0c\u6211\u5012\u8981\u770b\u770b\u5979\u80fd\u633a\u591a\u4e45\uff01\u201d \u3000\u3000\u80e1\u5143\u4e0e\u90ed\u5929\u540c\u9f50\u58f0\u606d\u5e94\uff0c\u4e0a\u524d\u62d6\u8d77\u8eba\u5728\u5730\u4e0a\u7684\u84dd\u51e4\uff0c\u628a\u5979\u67b6\u5f80\u540e\u7985\u9662\u53bb\u3002 \u3000\u3000\u5f20\u56db\u59d1\u76ee\u9001\u84dd\u51e4\u88ab\u67b6\u51fa\u5927\u6bbf\uff0c\u4e0d\u7981\u8f7b\u53f9\u4e00\u58f0\u9053\uff1a\u201c\u5509\uff01\u771f\u82e6\u4e86\u8fd9\u59d1\u5a18\u2026\u2026\u201d \u3000\u3000\u8001\u53eb\u5316\u9053\uff1a\u201c\u4f46\u613f\u5979\u8fd9\u987f\u6253\u4e0d\u662f\u767d\u6328\u7684\u3002\u201d \u3000\u3000\u5927\u548c\u5c1a\u53cc\u624b\u5408\u5341\uff0c\u5ba3\u4e86\u58f0\u4f5b\u53f7\uff1a\u201c\u963f\u5f25\u9640\u4f5b\uff01\u201d \u3000\u3000\u8001\u53eb\u5316\u60f3\u4e86\u60f3\uff0c\u5ffd\u9053\uff1a\u201c\u5f20\u59d1\u5a18\uff0c\u8001\u53eb\u5316\u5e74\u7eaa\u5b9e\u5728\u592a\u5927\u4e86\uff0c\u8111\u7b4b\u6108\u6765\u6108\u4e0d\u7ba1\u7528\uff0c\u5230\u73b0\u5728\u8fd8\u6ca1\u5b8c\u5168\u641e\u61c2\uff0c\u8ba9\u84dd\u51e4\u88c5\u4f5c\u4ece\u6211\u4eec\u8fd9\u91cc\u9003\u51fa\uff0c\u7a76\u7adf\u80fd\u8d77\u4ec0\u4e48\u4f5c\u7528\uff1f\u201d \u3000\u3000\u5927\u548c\u5c1a\u4ee3\u7b54\u9053\uff1a\u201c\u5f53\u7136\u662f\u629b\u7816\u5f15\u7389\uff0c\u8bf1\u51fa\u654c\u4eba\u6765\u3002\u201d \u3000\u3000\u8001\u53eb\u5316\u8038\u8038\u80a9\u9053\uff1a\u201c\u600e\u4e48\u5f15\uff1f\u5979\u53c8\u4e0d\u77e5\u9053\u795e\u79d8\u6559\u4e3b\u7684\u85cf\u8eab\u4e4b\u5904\uff0c\u9003\u51fa\u53bb\u8fd8\u4e0d\u662f\u6ca1\u5904\u627e\u4ed6\u3002 \u3000\u3000\u6c5f\u67ab\u603b\u4e0d\u80fd\u75b2\u4e8e\u5954\u547d\u8ddf\u8e2a\uff0c\u50cf\u4e2a\u6ca1\u5934\u82cd\u8747\u4f3c\u5730\u5230\u5904\u4e71\u98de\u5440\u3002\u201d \u3000\u3000\u5927\u548c\u5c1a\u9053\uff1a\u201c\u522b\u5fd8\u4e86\uff0c\u9644\u8fd1\u4e00\u5e26\uff0c\u4e00\u5b9a\u6709\u654c\u4eba\u5728\u6697\u4e2d\u76d1\u89c6\uff0c\u5426\u5219\u4eca\u591c\u8fd9\u82e6\u8089\u8ba1\u6f14\u7ed9\u8c01\u770b\u3002\u201d \u3000\u3000\u8001\u53eb\u5316\u628a\u7709\u5934\u4e00\u76b1\uff0c\u9053\uff1a\u201c\u5982\u679c\u4eba\u5bb6\u6bd4\u6211\u4eec\u66f4\u9ad8\u660e\uff0c\u6765\u4e2a\u76f8\u5e94\u4e0d\u7406\uff0c\u90a3\u84dd\u98ce\u7684\u8fd9\u987f\u76ae\u97ad\u4e0d\u662f\u767d\u6328\u4e86\uff1f\u201d \u3000\u3000\u5927\u548c\u5c1a\u5475\u5475\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u653e\u5fc3\uff0c\u6211\u4eec\u7684\u5f20\u59d1\u5a18\u662f\u5973\u8bf8\u845b\uff0c\u4e00\u5411\u6599\u4e8b\u5982\u795e\u3002\u80fd\u60f3\u51fa\u8fd9\u4e00\u6b65\u68cb\uff0c\u90a3\u5c31\u7edd\u5bf9\u9519\u4e0d\u4e86\u7684\u3002\u201d \u3000\u3000\u201c\u5927\u548c\u5c1a\uff0c\u4f60\u53ef\u522b\u628a\u6211\u6367\u5f97\u592a\u9ad8\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u9053\uff1a\u201c\u6211\u4eec\u7684\u654c\u4eba\uff0c\u7edd\u975e\u7b49\u95f2\u4e4b\u8f88\u3002\u4ed6\u80fd\u5728\u5e55\u540e\u53d1\u53f7\u65bd\u4ee4\uff0c\u4e25\u5bc6\u63a7\u5236\u4e86\u5929\u4e0b\u6b66\u6797\u4e8c\u5341\u5e74\uff0c\u8fde\u5f53\u5e74\u7684\u6b66\u6797\u4e09\u5723\u90fd\u6597\u4ed6\u4e0d\u8fc7\uff0c\u624d\u4f1a\u715e\u8d39\u82e6\u5fc3\uff0c\u5408\u529b\u57f9\u690d\u51fa\u4e00\u4e2a\u6c5f\u67ab\u6765\u3002\u5e76\u4e14\u6697\u4e2d\u5e03\u4e0b\u4e86\u6211\u4eec\u8fd9\u51e0\u9897\u68cb\u5b50\uff0c\u65e5\u540e\u52a9\u6c5f\u67ab\u4e00\u5c51\u4e4b\u529b\uff0c\u633d\u6551\u6574\u4e2a\u6b66\u6797\u7684\u5384\u8fd0\u3002 \u3000\u3000\u7531\u4e09\u5723\u7684\u957f\u8fdc\u5e03\u7f6e\u770b\u6765\uff0c\u4ed6\u4eec\u624d\u79f0\u5f97\u4e0a\u662f\u6599\u4e8b\u5982\u795e\uff0c\u663e\u7136\u65e9\u5df2\u6599\u5230\uff0c\u90a3\u4e2a\u5728\u5e55\u540e\u63a7\u5236\u5929\u4e0b\u6b66\u6797\u7684\u4eba\u7269\uff0c\u662f\u4f4d\u6b66\u529f\u4e0e\u667a\u6167\u72b9\u5728\u4ed6\u4eec\u4e4b\u4e0a\u7684\u5389\u5bb3\u89d2\u8272\u3002 \u3000\u3000\u6240\u4ee5\uff0c\u8bf4\u5b9e\u5728\u7684\uff0c\u6211\u628a\u84dd\u51e4\u8fd9\u6b65\u6b7b\u68cb\u5f53\u6d3b\u68cb\u7528\uff0c\u5e76\u6ca1\u6709\u592a\u5927\u628a\u63e1\uff0c\u53ea\u4e0d\u8fc7\u662f\u59d1\u4e14\u4e00\u8bd5\u7f62\u4e86\u3002\u201d \u3000\u3000\u8bf4\u5b8c\uff0c\u5979\u4e0d\u7981\u6df1\u6df1\u53f9\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u5927\u548c\u5c1a\u542c\u4e86\u5979\u8fd9\u756a\u8bdd\uff0c\u4e5f\u6df1\u611f\u8eab\u8d1f\u633d\u6551\u5929\u4e0b\u6b66\u6797\u7684\u91cd\u4efb\uff0c\u538b\u529b\u5b9e\u5728\u5f88\u6df1\u91cd\u3002 \u3000\u3000\u5c24\u5176\u5982\u4eca\u654c\u8e2a\u4e0d\u73b0\uff0c\u66f4\u4ee4\u4eba\u83ab\u6d4b\u9ad8\u6df1\uff0c\u96be\u514d\u611f\u5230\u60f6\u6050\u4e0d\u5b89\u3002\u52a0\u4e0a\u96ea\u3001\u971c\u4e24\u59d0\u59b9\u81ea\u4e60\u201c\u4f20\u771f\u8bb0\u201d\u6b66\u529f\uff0c\u53d8\u5f97\u6027\u6b8b\u55dc\u6740\uff0c\u4e0e\u8fc7\u53bb\u7684\u7eaf\u771f\u65e0\u90aa\u76f8\u6bd4\uff0c\u7b80\u76f4\u5224\u82e5\u4e24\u4eba\u3002 \u3000\u3000\u4e07\u4e00\u9664\u4e86\u795e\u79d8\u6559\u4e3b\uff0c\u53c8\u5192\u51fa\u4e24\u4e2a\u66f4\u6b8b\u66b4\u7684\u5973\u9b54\u2026\u2026 \u3000\u3000\u5927\u548c\u5c1a\u4e0d\u6562\u5f80\u4e0b\u60f3\u4e86\uff0c\u53ea\u597d\u8a00\u4e0d\u7531\u8877\u9053\uff1a\u201c\u5f20\u59d1\u5a18\u4e0d\u5fc5\u6c14\u9981\uff0c\u6211\u4eec\u90fd\u5bf9\u4f60\u5145\u6ee1\u4fe1\u5fc3\uff01\u201d \u3000\u3000\u5176\u5b9e\uff0c\u4ed6\u7684\u4fe1\u5fc3\u5df2\u6709\u4e9b\u52a8\u6447\u4e86\u3002 \u3000\u3000\u201c\u8c22\u8c22\u4f60\u3002\u201d\u5f20\u56db\u59d1\u5f3a\u81ea\u4e00\u7b11\u9053\uff1a\u201c\u662f\u798f\u4e0d\u662f\u7978\u662f\u7978\u8eb2\u4e0d\u8fc7\uff0c\u4e00\u5207\u53ea\u6709\u5c3d\u4eba\u4e8b\uff0c\u542c\u5929\u547d\u4e86\u3002\u201d \u3000\u3000\u4e09\u4eba\u6c89\u9ed8\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u5982\u4eca\u662f\u5185\u5fe7\u5916\u60a3\uff0c\u5f62\u6210\u4e24\u80a1\u91cd\u5927\u538b\u529b\uff0c\u538b\u5f97\u6bcf\u4e00\u4e2a\u4eba\u90fd\u900f\u4e0d\u8fc7\u6c14\u6765\u3002\u4eff\u4f5b\u968f\u65f6\u968f\u523b\uff0c\u90fd\u611f\u5230\u4e25\u91cd\u5a01\u80c1\uff0c\u65e0\u6cd5\u9884\u77e5\u5371\u673a\u662f\u6765\u81ea\u654c\u65b9\uff0c\u6216\u662f\u7206\u53d1\u81ea\u6027\u60c5\u7a81\u53d8\u7684\u96ea\u3001\u971c\u4e24\u59d0\u59b9\u3002 \u3000\u3000\u5979\u4eec\u90fd\u6210\u4e86\u5371\u9669\u4eba\u7269\uff0c\u7ed9\u4eba\u611f\u53d7\u5230\u7684\u5a01\u80c1\uff0c\u4f3c\u4e4e\u72b9\u80dc\u90a3\u795e\u79d8\u6559\u4e3b\u3002 \u3000\u3000\u6bd5\u7adf\uff0c\u795e\u79d8\u6559\u4e3b\u662f\u4ed6\u4eec\u516c\u8ba4\u7684\u7f6a\u9b41\u7978\u9996\uff0c\u53c8\u662f\u7b2c\u4e00\u53f7\u5f3a\u654c\u3002\u800c\u96ea\u3001\u971c\u4e24\u59d0\u59b9\u7684\u5371\u9669\u6027\uff0c\u5374\u662f\u4ee4\u4eba\u9632\u4e0d\u80dc\u9632\uff0c\u4e0d\u77e5\u4ec0\u4e48\u65f6\u5019\u5979\u4eec\u4f1a\u51f6\u6027\u5927\u53d1\uff0c\u5982\u540c\u8eab\u8fb9\u6e29\u9a6f\u7684\u5c0f\u732b\uff0c\u7a81\u7136\u95f4\u53d8\u6210\u566c\u4eba\u7684\u731b\u517d\u3002 \u3000\u3000\u6b63\u5728\u8fd9\u65f6\uff0c\u7f57\u5170\u6c14\u6025\u8d25\u574f\u5730\u95ef\u8fdb\u4e86\u5927\u6bbf\u3002 \u3000\u3000\u5f20\u56db\u59d1\u89c1\u5979\u795e\u8272\u5f20\u60f6\uff0c\u60c5\u77e5\u6709\u5f02\uff0c\u5fd9\u8d77\u8eab\u8fce\u4e0a\u524d\uff0c\u6025\u95ee\uff1a\u201c\u51fa\u4e86\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u7f57\u5170\u788d\u4e8e\u5927\u548c\u5c1a\u53ca\u8001\u53eb\u5316\u5728\u573a\uff0c\u4f3c\u6709\u987e\u5fcc\uff0c\u4e0d\u65b9\u4fbf\u76f4\u8a00\uff0c\u5728\u5f20\u56db\u59d1\u8033\u65c1\u8f7b\u58f0\u8bf4\u4e86\u51e0\u53e5\u3002 \u3000\u3000\u53ea\u89c1\u5f20\u56db\u59d1\u795e\u60c5\u500f\u5730\u4e00\u53d8\uff0c\u968f\u7740\u7f57\u5170\u76f4\u5954\u540e\u7985\u9662\u3002 \u3000\u3000\u5306\u5306\u8d76\u5230\u96ea\u3001\u971c\u4e24\u59d0\u59b9\u4f4f\u7684\u5ba2\u623f\uff0c\u5df2\u4e0d\u89c1\u5979\u4eec\u5f71\u8e2a\uff0c\u9752\u51e4\u5374\u8d64\u88f8\u88f8\u660f\u5012\u5728\u5e8a\u4e0a\uff01 \u3000\u3000\u5f20\u56db\u59d1\u6025\u95ee\uff1a\u201c\u6ca1\u770b\u5230\u4e24\u4e2a\u4e2b\u5934\uff1f\u201d \u3000\u3000\u7f57\u5170\u6447\u5934\u9053\uff1a\u201c\u6ca1\u6709\u3002\u6211\u770b\u5979\u4eec\u623f\u91cc\u8001\u534a\u5929\u6ca1\u6709\u58f0\u606f\uff0c\u8d76\u6765\u770b\uff0c\u53ea\u6709\u9752\u51e4\u8eba\u5728\u5e8a\u4e0a\uff0c\u50cf\u662f\u88ab\u70b9\u4e86\u660f\u7a74\u3002\u201d \u3000\u3000\u539f\u6765\u6b27\u9633\u662d\u592b\u5987\u7684\u5ba2\u623f\uff0c\u5c31\u5728\u96ea\u3001\u971c\u4e24\u59d0\u59b9\u7684\u659c\u5bf9\u9762\uff0c\u4ece\u7a97\u4fbf\u53ef\u6697\u4e2d\u76d1\u89c6\u3002 \u3000\u3000\u6700\u8fd1\u4e24\u5929\uff0c\u9752\u51e4\u6bcf\u665a\u90fd\u6765\u5979\u4eec\u623f\u91cc\uff0c\u751a\u81f3\u6574\u591c\u7559\u8fde\u4e0d\u53bb\uff0c\u4ed6\u8ddf\u96ea\u3001\u971c\u4e24\u59d0\u59b9\u76f8\u5904\u6781\u878d\u6d3d\u3002 \u3000\u3000\u867d\u7136\u5f20\u56db\u59d1\u672a\u4fbf\u660e\u8bf4\uff0c\u7f57\u5170\u4e5f\u80fd\u770b\u51fa\uff0c\u9752\u51e4\u8d1f\u6709\u7279\u6b8a\u4efb\u52a1\uff0c\u7684\u662f\u201c\u7f20\u201d\u4f4f\u96ea\u3001\u971c\u4e24\u59d0\u59b9\uff0c\u6545\u610f\u5f15\u5979\u4eec\u8f6c\u79fb\u5174\u8da3\uff0c\u6de1\u5fd8\u51fa\u53bb\u627e\u4eba\u90a3\u6321\u5b50\u4e8b\u3002 \u3000\u3000\u7231\u5973\u5fc3\u5207\u7684\u7f57\u5170\uff0c\u81ea\u7136\u5e0c\u671b\u4e24\u4e2a\u5973\u513f\u5f97\u6551\uff0c\u4e0d\u613f\u773c\u770b\u5979\u4eec\u8d70\u81ea\u53d6\u6bc1\u706d\u4e4b\u9014\u3002 \u3000\u3000\u4f46\u5979\u4ecd\u4e0d\u653e\u5fc3\uff0c\u968f\u65f6\u968f\u5730\u5bc6\u5207\u6ce8\u610f\u5979\u4eec\u7684\u4e00\u4e3e\u4e00\u52a8\u3002 \u3000\u3000\u4eca\u591c\u5979\u4eec\u7684\u623f\u91cc\u51fa\u5947\u5b89\u9759\uff0c\u4e0d\u4f3c\u8fc7\u53bb\u4e24\u591c\u4e0d\u65ad\u4f20\u51fa\u5ab3\u7b11\u58f0\u3002 \u3000\u3000\u7f57\u5170\u60c5\u77e5\u6709\u5f02\uff0c\u8d76\u6765\u4e00\u770b\uff0c\u679c\u7136\u51fa\u4e86\u4e71\u5b50\u3002 \u3000\u3000\u6b64\u523b\u5979\u5fc3\u4e2d\u7684\u4f18\u6025\uff0c\u81ea\u7136\u53ef\u60f3\u800c\u77e5\uff0c\u5fcd\u4e0d\u4f4f\u8fde\u95ee\u4e86\u4e24\u58f0\uff1b\u201c\u600e\u4e48\u529e\uff1f\u600e\u4e48\u529e\uff1f\u201d\u5f20\u56db\u59d1\u5b89\u629a\u9053\uff1a\u201c\u5148\u522b\u4e71\uff0c\u628a\u9752\u51e4\u5f04\u9192\uff0c\u95ee\u6e05\u4e86\u518d\u8bf4\u3002\u201d \u3000\u3000\u9752\u51e4\u679c\u7136\u662f\u88ab\u70b9\u4e86\u660f\u7a74\uff0c\u4e00\u7ecf\u5f20\u56db\u59d1\u62cd\u5f00\uff0c\u7acb\u65f6\u9192\u8fc7\u6765\u3002 \u3000\u3000\u7f57\u5170\u8feb\u4e0d\u53ca\u5f85\u5730\u95ee\uff1a\u201c\u9752\u51e4\uff0c\u4e24\u4e2a\u4e2b\u5934\u5462\uff1f\u201d \u3000\u3000\u201c\u4e0d\u77e5\u9053\u554a\uff01\u201d\u9752\u51e4\u5982\u68a6\u521d\u9192\uff0c\u4e00\u8138\u832b\u7136\u9053\uff1a\u201c\u6211\u4eec\u672c\u6765\u5728\u4e00\u8d77\u597d\u597d\u7684\uff0c\u7a81\u7136\u95f4\u5c31\u5931\u53bb\u4e86\u77e5\u89c9\u2026\u2026\u201d \u3000\u3000\u5f20\u56db\u59d1\u4e0d\u7531\u5730\u6697\u81ea\u4e00\u60ca\uff0c\u5fc3\u77e5\u96ea\u3001\u971c\u4e24\u59d0\u59b9\u5fc5\u662f\u88ab\u9752\u51e4\u201c\u7f20\u201d\u4e86\u4e24\u5929\uff0c\u597d\u4e0d\u5bb9\u6613\u4f7f\u5979\u4eec\u628a\u6740\u763e\u6de1\u5fd8\u3002 \u3000\u3000\u4eca\u591c\u6f5c\u4f0f\u7684\u51f6\u6027\u53c8\u53d1\uff0c\u51fa\u5176\u4e0d\u610f\u5730\u5236\u4f4f\u9752\u51e4\uff0c\u53c8\u6e9c\u51fa\u53bb\u627e\u4eba\u6740\u4e86\u3002 \u3000\u3000\u6025\u5411\u7f57\u5170\u4ea4\u5f85\u9053\uff1a\u201c\u8fd9\u91cc\u4ea4\u7ed9\u4f60\u4e86\uff0c\u4ee3\u6211\u901a\u77e5\u4e03\u5b9d\u548c\u5c1a\u4ed6\u4eec\u4e00\u58f0\uff0c\u6211\u627e\u6c5f\u67ab\u53bb\u8ffd\u5979\u4eec\u3002\u201d \u3000\u3000\u7f57\u5170\u4e00\u628a\u6293\u4f4f\u5979\u624b\u81c2\uff0c\u592e\u6c42\u9053\uff1a\u201c\u56db\u59d1\uff0c\u7b54\u5e94\u6211\uff0c\u4e0d\u8981\u6740\u5979\u4eec\u2026\u2026\u201d \u3000\u3000\u5f20\u56db\u59d1\u6f2b\u5e94\u4e00\u58f0\uff0c\u98de\u8eab\u8d8a\u51fa\u9662\u5899\u800c\u53bb\u3002 \u3000\u3000\u5229\u7528\u9041\u6cd5\u63a9\u62a4\uff0c\u85cf\u8eab\u5728\u4e0d\u8fdc\u5904\u77ee\u6811\u4e1b\u5185\uff0c\u51c6\u5907\u5b88\u5019\u84dd\u51e4\u9003\u51fa\u52a0\u4ee5\u8ddf\u8e2a\u7684\u6c5f\u67ab\uff0c\u4e00\u89c1\u8d8a\u5899\u98de\u51fa\u7684\u662f\u5f20\u56db\u59d1\uff0c\u5fd9\u73b0\u51fa\u8eab\uff0c\u8fce\u4e86\u4e0a\u524d\u8be7\u7136\u95ee\uff1a\u201c\u5927\u59d0\uff0c\u600e\u4e48\u662f\u4f60\uff1f\u201d \u3000\u3000\u5f20\u56db\u59d1\u8f7b\u58f0\u9053\uff1a\u201c\u4e24\u4e2a\u4e2b\u5934\u5236\u4f4f\u9752\u51e4\u6e9c\u4e86\uff0c\u6211\u4eec\u5feb\u53bb\u627e\u5979\u4eec\uff01\u201d \u3000\u3000\u6c5f\u67ab\u95fb\u8a00\u5927\u60ca\uff0c\u6025\u9053\uff1a\u201c\u90a3\u6211\u4eec\u539f\u5b9a\u7684\u8ba1\u5212\u2026\u2026\u201d \u3000\u3000\u5f20\u56db\u59d1\u60c5\u6025\u9053\uff1a\u201c\u73b0\u5728\u987e\u4e0d\u5f97\u5176\u4ed6\u7684\u4e86\uff01\u201d \u3000\u3000\u6c5f\u67ab\u54ea\u6562\u6020\u6162\uff0c\u7acb\u5373\u968f\u7740\u5f20\u56db\u59d1\u63a0\u8eab\u800c\u53bb\u3002 \u3000\u3000\u4f46\u662f\uff0c\u4e0d\u77e5\u96ea\u3001\u971c\u4e24\u59d0\u59b9\u7684\u53bb\u5411\uff0c\u4e0a\u54ea\u91cc\u53bb\u627e\u5979\u4eec\u3002 \u3000\u3000\u591c\u8272\u82cd\u832b\uff0c\u6c5f\u67ab\u4e0e\u5f20\u56db\u59d1\u75be\u5954\u4e86\u4e00\u9635\uff0c\u5374\u672a\u89c1\u96ea\u971c\u4e24\u59d0\u59b9\u7684\u5f71\u8e2a\u3002 \u3000\u3000\u4e24\u4eba\u65bd\u5c55\u7edd\u9876\u8f7b\u529f\uff0c\u5954\u4e0a\u4e00\u5ea7\u5c71\u5934\u3002 \u3000\u3000\u5c45\u9ad8\u4e34\u4e0b\uff0c\u65b9\u5706\u6570\u91cc\u4e4b\u5185\u7684\u4e00\u5207\uff0c\u5c3d\u6536\u773c\u5e95\u3002 \u3000\u3000\u4f46\u6781\u76ee\u8fdc\u773a\uff0c\u6beb\u65e0\u53d1\u73b0\u3002 \u3000\u3000\u6c5f\u67ab\u8f7b\u53f9\u4e00\u58f0\u9053\uff1a\u201c\u5982\u679c\u6211\u4eec\u627e\u5230\u4e86\u5979\u4eec\uff0c\u800c\u5979\u4eec\u4e0d\u80af\u56de\u53bb\u5462\uff1f\u201d \u3000\u3000\u5f20\u56db\u59d1\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u90a3\u5c31\u8981\u770b\u4f60\u7684\u4e86\u3002\u201d \u3000\u3000\u6c5f\u67ab\u95ee\uff1a\u201c\u7528\u2018\u60d1\u5fc3\u672f\u2019\uff1f\u201d \u3000\u3000\u5f20\u56db\u59d1\u5fae\u5fae\u70b9\u5934\u9053\uff1a\u201c\u6211\u4eec\u4e0d\u59a8\u4ee5\u5176\u4eba\u4e4b\u9053\uff0c\u8fd8\u6cbb\u5176\u4eba\u4e4b\u8eab\u3002 \u3000\u3000\u7531\u4f60\u65bd\u5c55\u2018\u6212\u5fc3\u672f\u2019\uff0c\u6211\u6765\u51fa\u624b\u5236\u4f4f\u5979\u4eec\u3002\u201d \u3000\u3000\u6c5f\u67ab\u803d\u5fc3\u9053\uff1a\u201c\u4e07\u4e00\u5979\u8ddf\u654c\u4eba\u906d\u9047\uff0c\u6740\u7ea2\u4e86\u773c\uff0c\u6211\u7684\u2018\u60d1\u5fc3\u672f\u2019\u53ea\u6015\u4e5f\u82f1\u96c4\u65e0\u7528\u6b66\u4e4b\u5730\u5566\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u9053\uff1a\u201c\u90a3\u6b63\u597d\uff0c\u6211\u4eec\u53ef\u4ee5\u5148\u52a9\u5979\u4eec\u6740\u654c\uff0c\u6740\u4e2a\u75db\u5feb\uff0c\u7136\u540e\u2026\u2026\u201d \u3000\u3000\u201c\u5927\u59d0\uff01\u201d\u6c5f\u67ab\u63a5\u53e3\u9053\uff1a\u201c\u4f60\u600e\u4e48\u5fd8\u4e86\uff0c\u654c\u4eba\u53ef\u80fd\u91c7\u53d6\u540c\u5f52\u4e8e\u5c3d\u7684\u624b\u6bb5\u54e6\uff01\u201d \u3000\u3000\u5f20\u56db\u59d1\u4e00\u6014\uff0c\u5fe7\u5f62\u4e8e\u8272\u9053\uff1a\u201c\u8fd9\u2026\u2026\u201d \u3000\u3000\u201c\u770b\uff01\u201d\u6c5f\u67ab\u7a81\u5411\u5c71\u4e0b\u4e00\u6307\u3002 \u3000\u3000\u5f20\u56db\u59d1\u653e\u773c\u770b\u53bb\uff0c\u591c\u8272\u819d\u9647\u4e0b\uff0c\u9065\u89c1\u4e00\u6761\u4eba\u5f71\u75be\u5954\u5982\u98de\uff0c\u6b63\u662f\u4ece\u8352\u5e99\u65b9\u5411\u5954\u51fa\u3002 \u3000\u3000\u201c\u597d\u50cf\u662f\u84dd\u51e4\uff01\u201d\u6c5f\u67ab\u7684\u773c\u529b\u679c\u7136\u4e0d\u9519\uff0c\u4e00\u773c\u5c31\u8ba4\u4e86\u51fa\u6765\u3002 \u3000\u3000\u5f20\u56db\u59d1\u9053\uff1a\u201c\u5979\u5df2\u4f9d\u8ba1\u800c\u884c\u9003\u51fa\u4e86\uff0c\u53ef\u662f\u4f60\u73b0\u5728\u65e0\u6cd5\u62bd\u8eab\u53bb\u8ddf\u8e2a\u5979\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u7b11\u9053\uff1a\u201c\u4e0d\u7528\u8ddf\u8e2a\u4e86\uff0c\u6211\u4eec\u73b0\u5728\u5c45\u9ad8\u4e34\u4e0b\uff0c\u5982\u679c\u771f\u6709\u6697\u4e2d\u76d1\u89c6\u7684\u4eba\u73b0\u8eab\u63a5\u5e94\u5979\uff0c\u8fd9\u91cc\u53ef\u4ee5\u770b\u5f97\u4e00\u6e05\u4e8c\u695a\u2026\u2026\u201d \u3000\u3000\u8bdd\u72b9\u672a\u4e86\uff0c\u53c8\u51fa\u73b0\u4e86\u4e24\u6761\u4eba\u5f71\u3002 \u3000\u3000\u4e24\u6761\u4eba\u5f71\u662f\u4ece\u53e6\u4e00\u65b9\u5411\u51fa\u73b0\uff0c\u4f3c\u4e4e\u53d1\u73b0\u4e86\u75be\u5954\u7684\u84dd\u51e4\uff0c\u53cc\u53cc\u98de\u8eab\u8d76\u53bb\u62e6\u622a\u3002 \u3000\u3000\u5ffd\u542c\u5f20\u56db\u59d1\u5931\u58f0\u60ca\u53eb\uff1a\u201c\u7cdf\u4e86\uff01\u662f\u96ea\u3001\u971c\u4e24\u59d0\u59b9\u2026\u2026\u201d \u3000\u3000\u6c5f\u67ab\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u5b89\u6392\u84dd\u51e4\u4f6f\u88c5\u9003\u51fa\u7684\u8ba1\u5212\uff0c\u5979\u4eec\u4e24\u59d0\u59b9\u5e76\u4e0d\u77e5\u60c5\u3002\u4e07\u4e00\u62e6\u622a\u4e0b\u84dd\u51e4\u3002\u4e0d\u5206\u9752\u7ea2\u7682\u767d\u5c31\u6740\u4e86\u5979\uff0c\u90a3\u53ef\u5982\u4f55\u662f\u597d\u3002 \u3000\u3000\u4e24\u4eba\u54ea\u6562\u6020\u6162\uff0c\u6025\u5411\u5c71\u4e0b\u51b2\u53bb\u3002 \u3000\u3000\u5148\u51fa\u73b0\u7684\u90a3\u6761\u4eba\u5f71\uff0c\u679c\u7136\u662f\u84dd\u51e4\uff0c\u5979\u4f9d\u8ba1\u800c\u884c\uff0c\u4f3a\u673a\u51fa\u624b\uff0c\u51fa\u5176\u4e0d\u610f\u51fb\u5012\u8d1f\u8d23\u770b\u5b88\u7684\u80e1\u5143\u548c\u90ed\u5929\u540c\u3002 \u3000\u3000\u82e5\u8bba\u6b66\u529f\uff0c\u4ed6\u4eec\u76f8\u5dee\u84dd\u51e4\u4e00\u5927\u622a\uff0c\u4f55\u51b5\u662f\u5949\u5f20\u56db\u59d1\u4e4b\u547d\uff0c\u4f9d\u8ba1\u884c\u4e8b\uff0c\u5047\u88c5\u63aa\u624b\u4e0d\u53ca\u3002 \u3000\u3000\u84dd\u51e4\u5229\u7528\u591c\u8272\u63a9\u62a4\uff0c\u9003\u51fa\u540e\u7985\u9662\uff0c\u53c8\u51fb\u5012\u6b27\u9633\u662d\u7684\u4e24\u540d\u5f1f\u5b50\uff0c\u624d\u5f97\u8131\u8eab\u3002 \u3000\u3000\u5979\u4e00\u8def\u72c2\u5954\uff0c\u6f2b\u65e0\u76ee\u6807\uff0c\u53ea\u662f\u5e0c\u671b\u80fd\u88ab\u795e\u79d8\u6559\u4e3b\u6d3e\u5728\u9644\u8fd1\uff0c\u6697\u4e2d\u76d1\u89c6\u8352\u5e99\u7684\u4eba\u53d1\u73b0\u3002 \u3000\u3000\u4e00\u53e3\u6c14\u5954\u51fa\u6570\u91cc\uff0c\u7a81\u89c1\u53f3\u4fa7\u63a0\u73b0\u4e24\u6761\u4eba\u5f71\uff0c\u4f3c\u8981\u5c06\u5979\u62e6\u622a\u4e0b\u3002 \u3000\u3000\u84dd\u51e4\u4e0d\u77e5\u6765\u8005\u662f\u54ea\u4e00\u65b9\u7684\u4eba\uff0c\u4e3a\u4e86\u8868\u6f14\u903c\u771f\uff0c\u89c1\u4eba\u8ffd\u6765\u6216\u62e6\u622a\u5c31\u9003\uff0c\u624d\u7b26\u5408\u4e00\u822c\u4eba\u7684\u6b63\u5e38\u53cd\u5e94\u3002 \u3000\u3000\u5979\u6beb\u4e0d\u8fdf\u7591\uff0c\u6025\u5411\u5de6\u8fb9\u9003\u53bb\u3002 \u3000\u3000\u96ea\u3001\u971c\u4e24\u59d0\u59b9\u6025\u8d77\u76f4\u8ffd\u3002 \u3000\u3000\u5979\u4eec\u7684\u8f7b\u529f\u9020\u8be3\uff0c\u539f\u4e0d\u53ca\u84dd\u51e4\uff0c\u4f46\u81ea\u4e60\u5f97\u201c\u4f20\u771f\u8bb0\u201d\u6240\u8f7d\u6b66\u529f\uff0c\u529f\u529b\u7a81\u98de\u731b\u8fdb\uff0c\u5927\u6709\u4e00\u65e5\u5343\u91cc\u4e4b\u52bf\u3002 \u3000\u3000\u51e0\u4e2a\u8d77\u843d\uff0c\u84dd\u51e4\u5df2\u88ab\u5979\u4eec\u8fce\u5934\u8d76\u4e0a\uff0c\u53cc\u53cc\u4ed7\u5251\u5c06\u5979\u62e6\u622a\u4e0b\u6765\u3002 \u3000\u3000\u84dd\u51e4\u5b9a\u795e\u4e00\u770b\uff0c\u4e4d\u89c1\u662f\u96ea\u3001\u971c\u4e24\u59d0\u59b9\uff0c\u4e0d\u7531\u5730\u6697\u81ea\u4e00\u60ca\uff0c\u5fc3\u4e2d\u5927\u611f\u610f\u5916\u3002 \u3000\u3000\u8fd9\u4e24\u5929\u5979\u542c\u8bf4\uff0c\u4e24\u59d0\u59b9\u7684\u5f02\u5e38\u201c\u7a81\u53d8\u201d\uff0c\u4f7f\u6c5f\u67ab\u7b49\u4eba\u4e3a\u5979\u4eec\u5927\u4f24\u8111\u7b4b\uff0c\u65e0\u4e0d\u89c6\u4e3a\u9690\u5fe7\u3002 \u3000\u3000\u5c24\u5176\uff0c\u5728\u5f20\u56db\u59d1\u5b89\u6392\u7684\u9003\u4ea1\u8ba1\u5212\u4e2d\uff0c\u5e76\u4e0d\u5305\u62ec\u8fd9\u4e24\u59d0\u59b9\u62e6\u622a\u7684\u201c\u60c5\u8282\u201d\uff0c\u81ea\u7136\u4f7f\u84dd\u51e4\u89c9\u51fa\u4e8b\u6709\u8dcc\u8df7\u3002 \u3000\u3000\u5979\u60c5\u77e5\u6709\u5f02\uff0c\u4f46\u529b\u6301\u9547\u5b9a\u3002 \u3000\u3000\u96ea\u3001\u971c\u4e24\u59d0\u59b9\uff0c\u89c1\u62e6\u4e0b\u7684\u662f\u84dd\u51e4\uff0c\u4f3c\u4e4e\u4e5f\u6709\u4e9b\u610f\u5916\u3002 \u3000\u3000\u201c\u54fc\uff01\u201d\u541f\u971c\u51b7\u58f0\u9053\uff1a\u201c\u4f60\u60f3\u9003\u5417\uff1f\u201d \u3000\u3000\u84dd\u51e4\u552f\u6050\u6697\u4e2d\u6709\u4eba\u76d1\u89c6\uff0c\u4e0d\u6562\u8d38\u7136\u7b54\u8bdd\uff0c\u51fa\u5176\u4e0d\u610f\u5730\u53cc\u638c\u9f50\u53d1\uff0c\u540c\u65f6\u6389\u5934\u5c31\u9003\u3002 \u3000\u3000\u96ea\u3001\u971c\u4e24\u59d0\u59b9\u8eab\u5f62\u4e00\u6643\uff0c\u907f\u5349\u84dd\u51e4\u7684\u638c\u529b\uff0c\u9f50\u559d\u4e00\u58f0\uff1a\u201c\u54ea\u91cc\u9003\uff01\u201d\u53cc\u53cc\u98de\u8eab\u6025\u8d77\u76f4\u8ffd\u3002 \u3000\u3000\u84dd\u51e4\u9003\u51fa\u4e0d\u53ca\u5341\u4e08\uff0c\u53c8\u88ab\u5979\u4eec\u8ffd\u4e0a\u4e86\u3002 \u3000\u3000\u4e24\u59d0\u59b9\u4e0d\u7531\u5206\u8bf4\uff0c\u633a\u5251\u5c31\u653b\u3002 \u3000\u3000\u84dd\u51e4\u624b\u65e0\u5bf8\u94c1\uff0c\u4e0d\u6562\u8f7b\u6a31\u5176\u950b\uff0c\u53ea\u6709\u4ed7\u8be1\u5f02\u7075\u6d3b\u7684\u8eab\u6cd5\u8fde\u8fde\u95ea\u907f\u3002 \u3000\u3000\u4e0d\u6599\u4e24\u59d0\u59b9\u7684\u5251\u672f\u66f4\u8be1\u5f02\uff0c\u8eab\u6cd5\u66f4\u7075\u6d3b\uff0c\u7adf\u7136\u5982\u5f71\u968f\u5f62\uff0c\u4f7f\u84dd\u51e4\u6beb\u65e0\u8131\u8eab\u7684\u673a\u4f1a\u3002 \u3000\u3000\u53ea\u89c1\u5bd2\u5149\u95ea\u52a8\uff0c\u4e24\u628a\u5229\u5251\u4eff\u4f5b\u4ece\u56db\u9762\u516b\u65b9\u653b\u6765\uff0c\u84dd\u51e4\u4e0d\u7981\u5fc3\u4e2d\u5927\u9a87\u3002 \u3000\u3000\u4e24\u59d0\u59b9\u7a81\u7136\u5251\u6cd5\u4e00\u7d27\uff0c\u8fde\u8fde\u5411\u84dd\u51e4\u8eab\u4e0a\u62db\u547c\u3002 \u3000\u3000\u4f46\u5251\u950b\u8fc7\u5904\uff0c\u84dd\u73b0\u7adf\u7136\u6beb\u53d1\u672a\u635f\uff0c\u53ea\u662f\u8eab\u4e0a\u65e9\u5df2\u4e0d\u8db3\u853d\u4f53\u7684\u7834\u8863\u886b\uff0c\u5239\u65f6\u5316\u4f5c\u65e0\u6570\u5c0f\u5e03\u7247\uff0c\u5982\u540c\u8774\u8776\u6f2b\u5929\u98de\u821e\uff0c\u5411\u56db\u9762\u516b\u65b9\u4ee5\u843d\u5f00\u53bb\u3002 \u3000\u3000\u987f\u65f6\uff0c\u84dd\u51e4\u6210\u4e86\u5168\u8eab\u8d64\u88f8\uff0c\u4e00\u4e1d\u4e0d\u6302\uff01 \u3000\u3000\u96ea\u3001\u971c\u4e24\u59d0\u59b9\u50cf\u662f\u6293\u4f4f\u8001\u9f20\u7684\u732b\uff0c\u5e76\u4e0d\u6025\u4e8e\u7f6e\u730e\u7269\u4e8e\u6b7b\u5730\uff0c\u53cd\u6b63\u8dd1\u4e0d\u4e86\uff0c\u51b3\u5fc3\u8981\u4f5c\u5f04\u628a\u73a9\u4e2a\u591f\u3002 \u3000\u3000\u663e\u7136\uff0c\u5979\u4eec\u7684\u6740\u673a\u5c1a\u672a\u52a8\u3002 \u3000\u3000\u4f46\u8fd9\u79cd\u5fc3\u6001\u66f4\u53ef\u6015\uff01 \u3000\u3000\u8fd9\u65f6\u6c5f\u67ab\u548c\u5f20\u56db\u59d1\u5df2\u8d76\u5230\uff0c\u5229\u7528\u9041\u6cd5\u6280\u827a\u63a9\u8fd1\uff0c\u89c1\u72b6\u4e0d\u6562\u51fa\u58f0\u60ca\u52a8\u5979\u4eec\u3002 \u3000\u3000\u5f20\u56db\u59d1\u6025\u5fd9\u4f20\u97f3\u5165\u5bc6\u4e4b\u58f0\uff0c\u5411\u8eab\u65c1\u7684\u6c5f\u67ab\u8b66\u544a\uff1a\u201c\u5343\u4e07\u4e0d\u53ef\u73b0\u8eab\uff0c\u5426\u5219\u5fc5\u4f1a\u6fc0\u53d1\u4e24\u4e2a\u4e2b\u5934\u6740\u673a\uff0c\u90a3\u5c31\u5bb3\u6b7b\u4e86\u84dd\u98ce\u3002\u201d \u3000\u3000\u6c5f\u67ab\u53ea\u597d\u6309\u5175\u4e0d\u52a8\u3002 \u3000\u3000\u5176\u5b9e\u4ed6\u4e5f\u77e5\u9053\uff0c\u5982\u679c\u60f3\u62a2\u6551\u84dd\u98ce\uff0c\u5c31\u5fc5\u51fa\u624b\u653b\u5411\u96ea\u3001\u971c\u4e24\u59d0\u59b9\uff0c\u90a3\u6837\u4e00\u6765\uff0c\u540e\u679c\u66f4\u4e0d\u582a\u8bbe\u60f3\u3002 \u3000\u3000\u4f55\u51b5\uff0c\u4ed6\u7edd\u4e0d\u53ef\u80fd\u529b\u62a2\u6551\u84dd\u51e4\uff0c\u51fa\u624b\u6740\u8fd9\u4e24\u59d0\u59b9\uff01 \u3000\u3000\u73b0\u5728\u5979\u4eec\u4ec0\u4e48\u90fd\u4e0d\u80fd\u505a\uff0c\u552f\u6709\u4f5c\u58c1\u4e0a\u89c2\uff0c\u51b7\u773c\u770b\u4e24\u59d0\u59b9\u5982\u4f55\u6298\u78e8\u84dd\u98ce\u4e86\u3002 \u3000\u3000\u53ea\u542c\u541f\u971c\u4f3c\u7b11\u975e\u7b11\u9053\uff1a\u201c\u59d0\uff0c\u8fd9\u5973\u4eba\u8c8c\u4e0d\u60ca\u4eba\uff0c\u8eab\u6750\u5012\u633a\u4e0d\u9519\u7684\u561b\u3002\u201d \u3000\u3000\u541f\u96ea\u4e0d\u5c51\u5730\u54fc\u4e86\u4e00\u58f0\uff0c\u5145\u6ee1\u5992\u610f\u9053\uff1a\u201c\u8981\u4e0d\u7136\u6c5f\u67ab\u600e\u4f1a\u770b\u4e0a\u5979\uff01\u201d \u3000\u3000\u6c5f\u67ab\u542c\u5f97\u5fc3\u795e\u731b\u4e00\u9707\u3002 \u3000\u3000\u504f\u504f\u5f20\u56db\u59d1\u51fa\u5176\u4e0d\u610f\u5730\uff0c\u5728\u4ed6\u8170\u4e0a\u7528\u529b\u62e7\u4e86\u4e00\u628a\uff0c\u4f7f\u4ed6\u51e0\u4e4e\u5931\u58f0\u53eb\u4e86\u51fa\u6765\u3002 \u3000\u3000\u8fd9\u65f6\u53c8\u542c\u541f\u971c\u7b11\u9053\uff1a\u201c\u59d0\uff0c\u5979\u4e0e\u9752\u51e4\u5e08\u51fa\u540c\u95e8\uff0c\u90a3\u5957\u529f\u592b\u5927\u6982\u4e5f\u4e0d\u9519\u5427\u3002\u201d \u3000\u3000\u541f\u96ea\u6f2b\u5e94\u4e00\u58f0\uff0c\u7a81\u4ee5\u5251\u6307\u84dd\u51e4\uff0c\u51b7\u58f0\u559d\u4ee4\uff1a\u201c\u8eba\u4e0b\uff01\u201d \u3000\u3000\u84dd\u98ce\u6697\u81ea\u4e00\u6014\uff0c\u60ca\u8be7\u5730\u95ee\uff1a\u201c\u4f60\u4eec\u8981\u5e72\u561b\uff1f\u201d \u3000\u3000\u541f\u96ea\u65a5\u9053\uff1a\u201c\u4e0d\u5fc5\u591a\u95ee\uff0c\u8981\u60f3\u6d3b\u547d\u5c31\u7167\u6211\u7684\u8bdd\u505a\uff01\u201d \u3000\u3000\u201c\u5e72\u561b\u8fd9\u4e48\u6837\u5b50\u561b\uff1f\u201d\u84dd\u51e4\u5ae3\u7136\u4e00\u7b11\uff0c\u7adf\u7136\u4f7f\u51fa\u4e86\u5a9a\u672f\u3002 \u3000\u3000\u8fd9\u662f\u5929\u738b\u95e8\u5f1f\u5b50\u6cd5\u5b9d\uff0c\u4e0d\u4ec5\u80fd\u4ee4\u7537\u4eba\u795e\u9b42\u98a0\u5012\uff0c\u610f\u4e71\u60c5\u8ff7\uff0c\u5fc5\u8981\u65f6\u4ea6\u53ef\u5bf9\u540c\u6027\u65bd\u5c55\u3002 \u3000\u3000\u5c31\u50cf\u5929\u72d0\u592b\u4eba\u7684\u201c\u60d1\u5fc3\u672f\u201d\u4e00\u6837\uff0c\u7f57\u5170\u4e0d\u81ea\u89c9\u7684\u59a9\u5a9a\u4e00\u7b11\uff0c\u5c31\u66fe\u4f7f\u5728\u65c1\u7684\u5f20\u56db\u59d1\u5fc3\u795e\u8361\u6f3e\u3002 \u3000\u3000\u4e0d\u6599\u541f\u96ea\u5374\u4e0d\u4e3a\u6240\u60d1\uff0c\u65e0\u52a8\u4e8e\u8877\u5730\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u4e00\u5957\u5bf9\u6211\u4eec\u4e0d\u7ba1\u7528\uff0c\u9752\u51e4\u8bd5\u8fc7\uff0c\u6211\u4eec\u5e76\u672a\u53d7\u60d1\uff0c\u53ea\u4e0d\u8fc7\u662f\u88ab\u5979\u7684\u59ff\u8272\u6240\u8bf1\uff0c\u624d\u6709\u5174\u8da3\u8ddf\u5979\u95f9\u7740\u73a9\u73a9\u7f62\u4e86\u3002\u4f60\u53ef\u4e0d\u884c\uff0c\u59ff\u8272\u6bd4\u5979\u5dee\u591a\u4e86\uff0c\u4e0d\u5fc5\u4e1c\u65bd\u6548\u98a6\uff01\u201d \u3000\u3000\u84dd\u51e4\u660e\u77e5\u81ea\u5df1\u59ff\u8272\u5e73\u5eb8\uff0c\u4e0d\u582a\u4e0e\u9752\u51e4\u76f8\u6bd4\u3002\u4f46\u5973\u4eba\u6ca1\u6709\u4e0d\u559c\u6b22\u53d7\u4eba\u8d5e\u7f8e\u7684\u3002\u4e00\u542c\u4e4b\u4e0b\uff0c\u81ea\u5c0a\u5fc3\u5927\u53d7\u4f24\u5bb3\uff0c\u4e0d\u7531\u5730\u607c\u7f9e\u6210\u6012\uff0c\u628a\u5fc3\u4e00\u6a2a\uff0c\u53cd\u5507\u76f8\u8ba5\u9053\uff1a\u201c\u4f60\u4eec\u4e5f\u4e0d\u89c1\u5f97\u7f8e\u5230\u54ea\u91cc\u53bb\uff0c\u5145\u5176\u91cf\u53ea\u4e0d\u8fc7\u662f\u4e2a\u86c7\u874e\u7f8e\u4eba\uff0c\u5426\u5219\u6c5f\u67ab\u600e\u4f1a\u5bf9\u4f60\u4eec\u6beb\u65e0\u5174\u8da3\u3002\u201d \u3000\u3000\u6c5f\u67ab\u4e00\u542c\u5927\u60ca\uff0c\u6697\u53eb\u4e00\u58f0\uff1a\u201c\u7cdf\uff01\u2026\u2026\u201d \u3000\u3000\u679c\u89c1\u541f\u96ea\u6012\u4ece\u5fc3\u8d77\uff0c\u633a\u5251\u5c31\u5411\u84dd\u51e4\u5fc3\u7a9d\u523a\u53bb\u3002 \u3000\u3000\u541f\u971c\u773c\u6025\u624b\u5feb\u4e00\u5251\u5c06\u541f\u96ea\u7684\u5251\u683c\u5f00\uff0c\u529d\u963b\u9053\uff1a\u201c\u59d0\uff0c\u4e0d\u8981\u6740\u5979\uff01\u201d \u3000\u3000\u541f\u96ea\u6012\u95ee\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u541f\u971c\u51b7\u58f0\u9053\uff1a\u201c\u6740\u5979\u592a\u4fbf\u5b9c\u4e86\uff0c\u6211\u6709\u66f4\u597d\u7684\u4e3b\u610f\u3002\u201d \u3000\u3000\u201c\u54e6\uff1f\u201d\u541f\u96ea\u4f3c\u4e4e\u53d1\u751f\u4e86\u5174\u8da3\u3002 \u3000\u3000\u541f\u971c\u4e24\u773c\u6ce8\u89c6\u5168\u8eab\u8d64\u88f8\u7684\u84dd\u51e4\uff0c\u5634\u89d2\u6302\u7740\u8be1\u5f02\u7684\u7b11\u610f\uff0c\u4e56\u623e\u4ed6\u8bf4\uff1a\u201c\u59d0\uff0c\u5979\u521a\u624d\u4e0d\u662f\u60f3\u65bd\u5c55\u5a9a\u672f\u5417\uff1f\u6211\u60f3\u5979\u4e00\u5b9a\u5f88\u81ea\u4fe1\uff0c\u81ea\u4ee5\u4e3a\u5177\u6709\u4ee4\u4eba\u96be\u4ee5\u62d2\u6297\u7684\u9b45\u529b\u3002\u53cd\u6b63\u6211\u4eec\u95f2\u7740\u4e5f\u662f\u95f2\u7740\uff0c\u4f55\u4e0d\u8ba9\u5979\u4f7f\u4e86\u6df7\u8eab\u89e3\u6570\uff0c\u4e5f\u597d\u8ba9\u6211\u4eec\u5927\u5f00\u773c\u754c\u5462\u3002\u201d \u3000\u3000\u201c\u597d\u4e3b\u610f\uff01\u201d\u541f\u96ea\u9053\uff1a\u201c\u51ed\u5979\u8fd9\u526f\u5eb8\u4fd7\u8102\u7c89\uff0c\u5c45\u7136\u80fd\u4f7f\u6c5f\u67ab\u4e3a\u4e4b\u5fc3\u52a8\uff0c\u5fc5\u6709\u4e24\u628a\u5237\u5b50\uff0c\u6211\u4eec\u5012\u8981\u597d\u597d\u5411\u5979\u8ba8\u6559\u8ba8\u6559\uff0c\u4ee5\u540e\u624d\u61c2\u5f97\u600e\u6837\u8ff7\u4f4f\u7537\u4eba\u3002\u201d \u3000\u3000\u6c5f\u67ab\u5fc3\u4e2d\u767e\u611f\u4ea4\u96c6\uff0c\u8bf4\u4e0d\u51fa\u662f\u79cd\u4ec0\u4e48\u6ecb\u5473\u3002\u4ed6\u4e00\u76f4\u6781\u529b\u514b\u5236\u81ea\u5df1\u60c5\u6b32\uff0c\u4f7f\u8fd9\u4e24\u59d0\u59b9\u4fdd\u4f4f\u5b8c\u58c1\uff0c\u539f\u662f\u4e3a\u5979\u4eec\u7740\u60f3\uff0c\u60f3\u4e0d\u5230\u53cd\u800c\u5f04\u5de7\u6210\u62d9\uff0c\u5f15\u8d77\u5979\u4eec\u6ee1\u6000\u6028\u5c24\u4e4b\u60c5\u3002 \u3000\u3000\u4ece\u96ea\u3001\u971c\u4e24\u59d0\u59b9\u7684\u8bed\u6c14\u548c\u8bdd\u97f3\u4e2d\uff0c\u542c\u51fa\u5979\u4eec\u662f\u5145\u6ee1\u5992\u6068\u4e0e\u6124\u9075\uff0c\u4eca\u591c\u51b3\u5fc3\u8981\u53d1\u6cc4\u5728\u84dd\u51e4\u8eab\u4e0a\u3002 \u3000\u3000\u6c5f\u67ab\u6697\u81ea\u5fc3\u60ca\uff0c\u8033\u9645\u53c8\u542c\u5f20\u56db\u59d1\u4ee5\u4f20\u97f3\u5165\u5bc6\u9053\uff1a\u201c\u4e24\u4e2a\u4e2b\u5934\u4e2d\u9b54\u592a\u6df1\uff0c\u6211\u4eec\u5c31\u662f\u5168\u5fc3\u5168\u529b\u62ef\u6551\u5979\u4eec\uff0c\u6050\u6015\u4e5f\u65e0\u80fd\u4e3a\u529b\u4e86\u3002\u201d \u3000\u3000\u201c\u5927\u59d0\u3002\u201d\u6c5f\u67ab\u4e5f\u4ee5\u4f20\u97f3\u5165\u5bc6\u9053\uff1a\u201c\u6211\u4eec\u8d76\u5feb\u73b0\u8eab\u963b\u6b62\u2026\u2026\u201d \u3000\u3000\u5f20\u56db\u59d1\u8b66\u544a\u9053\uff1a\u201c\u5343\u4e07\u4f7f\u4e0d\u5f97\uff0c\u90a3\u6837\u4e00\u6765\uff0c\u7acb\u65f6\u89e6\u53d1\u5979\u4eec\u6740\u673a\u3002 \u3000\u3000\u9b54\u6027\u4e00\u53d1\uff0c\u516d\u4eb2\u4e0d\u8ba4\uff0c\u5bf9\u4f60\u6211\u90fd\u5c06\u53cd\u76ee\u6210\u4ec7\u5462\uff01\u201d \u3000\u3000\u6c5f\u67ab\u95fb\u8a00\u5927\u9a87\uff0c\u54ea\u8fd8\u6562\u8f7b\u4e3e\u5984\u52a8\uff0c\u53ea\u597d\u653e\u5f03\u73b0\u8eab\u963b\u6b62\u4e24\u59d0\u59b9\uff0c\u62a2\u6551\u84dd\u51e4\u7684\u5ff5\u5934\u4e86\u3002 \u3000\u3000\u8fd9\u65f6\u53f6\u7528\u5251\u4e00\u633a\uff0c\u62b5\u5411\u84dd\u51e4\uff0c\u8ddd\u79bb\u4e0d\u8db3\u534a\u5bf8\uff0c\u559d\u4ee4\u9053\uff1a\u201c\u8eba\u4e0b\uff01\u201d \u3000\u3000\u84dd\u51e4\u5df2\u8c41\u4e86\u51fa\u53bb\uff0c\u6012\u95ee\uff1a\u201c\u51ed\u4ec0\u4e48\u542c\u4f60\u7684\uff1f\u201d \u3000\u3000\u201c\u51ed\u8fd9\u4e2a\uff01\u201d \u3000\u3000\u541f\u971c\u624b\u8155\u4e00\u6296\uff0c\u5251\u950b\u5411\u53f3\u4e00\u504f\uff0c\u5feb\u5982\u7535\u63a3\u5730\u5411\u4e0a\u75be\u6311\uff0c\u5251\u950b\u8fc7\u5904\uff0c\u84dd\u51e4\u7684\u5de6\u7709\u68a2\u5df2\u88ab\u524a\u53bb\u4e00\u534a\uff0c\u76ae\u8089\u5374\u672a\u4f24\u53ca\u5206\u6beb\u3002 \u3000\u3000\u597d\u60ca\u4eba\u7684\u5251\u6cd5\uff01 \u3000\u3000\u201c\u554a\uff01\u2026\u2026\u201d\u84dd\u51e4\u60ca\u5f97\u5931\u58f0\u53eb\u51fa\u3002 \u3000\u3000\u541f\u971c\u7684\u5251\u53c8\u62b5\u5411\u5979\u5589\u95f4\uff0c\u51b7\u68ee\u6797\u9053\uff1a\u201c\u4e0b\u4e00\u5251\u6211\u8981\u524a\u53bb\u4f60\u7684\u5de6\u8033\uff01\u201d \u3000\u3000\u84dd\u51e4\u6c14\u9981\u4e86\uff0c\u65e0\u53ef\u5948\u4f55\u5730\u8eba\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u96ea\u3001\u971c\u4e24\u59d0\u59b9\u5f97\u610f\u5730\u5c55\u51fa\u4e86\u7b11\u5bb9\u3002 \u3000\u3000\u5979\u4eec\u7b11\u5f97\u59a9\u5a9a\u5df2\u6781\uff0c\u4f46\u4e5f\u6709\u79cd\u903c\u4eba\u7684\u90aa\u6c14\u3002 \u3000\u3000\u5929\u72d0\u592b\u4eba\u7684\u201c\u60d1\u5fc3\u672f\u201d\uff0c\u7adf\u7136\u6ce8\u5165\u4e86\u90aa\u672f\u9b54\u6cd5\uff01 \u3000\u3000\u84dd\u51e4\u65e0\u6cd5\u6297\u62d2\uff0c\u5fc3\u795e\u4e00\u9635\u9707\u6398\uff0c\u4eff\u4f5b\u88ab\u50ac\u7720\u4f3c\u5730\uff0c\u7a81\u7136\u9677\u5165\u4e86\u8ff7\u4e71\u4e2d\u3002 \u3000\u3000\u541f\u96ea\u4ee5\u5145\u6ee1\u9b41\u529b\u7684\u58f0\u97f3\uff0c\u547d\u4ee4\u9053\uff1a\u201c\u4e56\u5973\u5b69\uff0c\u628a\u4f60\u5185\u5fc3\u7684\u6b32\u671b\uff0c\u70ed\u60c5\uff0c\u68a6\u60f3\uff0c\u5c3d\u60c5\u5730\u89e3\u6551\u51fa\u6765\u5427\uff01\u201d \u3000\u3000\u84dd\u51e4\u82e5\u75f4\u82e5\u9189\uff0c\u552f\u547d\u662f\u4ece\u5730\u5f00\u59cb\u626d\u52a8\u5996\u8eaf\uff0c\u540c\u65f6\u53cc\u624b\u9f50\u52a8\uff0c\u5728\u81ea\u5df2\u904d\u4f53\u629a\u5f04\u7740\u3002 \u3000\u3000\u5229\u7528\u9041\u6cd5\u63a9\u85cf\u8eab\u5f62\u7684\u6c5f\u67ab\u548c\u5f20\u56db\u59d1\uff0c\u76f8\u8ddd\u4e0d\u8db3\u4e09\u4e08\uff0c\u770b\u5f97\u4e00\u6e05\u4e8c\u695a\uff0c\u53ea\u89c1\u84dd\u51e4\u90a3\u526f\u6625\u5fc3\u8361\u6f3e\uff0c\u6b32\u706b\u96be\u8010\u7684\u60c5\u6001\uff0c\u771f\u4e2a\u662f\u9500\u9b42\u8680\u9aa8\uff0c\u8fde\u4ed6\u4eec\u770b\u4e86\u90fd\u4e3a\u4e4b\u5fc3\u52a8\uff0c\u8138\u4e0a\u53d1\u70eb\u3002 \u3000\u3000\u84dd\u51e4\u5df2\u653e\u6d6a\u5f62\u9ab8\uff0c\u6108\u6765\u6108\u51b2\u52a8\u2026\u2026 \u3000\u3000\u7a81\u7136\u95f4\uff0c\u54cd\u8d77\u4e00\u9635\u72c2\u7b11\u3002 \u3000\u3000\u6c5f\u67ab\u5b9a\u795e\u4e00\u770b\uff0c\u4e0d\u77e5\u65f6\uff0c\u5df2\u6084\u7136\u5230\u6765\uff0c\u8d6b\u7136\u662f\u56db\u4e2a\u8eab\u9ad8\u4e0d\u53ca\u56db\u5c3a\u7684\u4f8f\u5112\uff01 \u3000\u3000\u83ab\u975e\u6b63\u662f\u8001\u53eb\u5316\u8bf4\u7684\u201c\u5173\u4e1c\u4e94\u5947\u201d\uff1f \u3000\u3000\u7531\u4e8e\u4ed6\u4eec\u6765\u7684\u65e0\u58f0\u65e0\u606f\uff0c\u96ea\u3001\u971c\u4e24\u59d0\u59b9\u6216\u8bb8\u5168\u795e\u8d2f\u6ce8\u5728\u84dd\u51e4\u8eab\u4e0a\uff0c\u770b\u5f97\u592a\u5165\u795e\u3002\u53ef\u662f\uff0c\u6c5f\u67ab\u4e0e\u5f20\u56db\u59d1\u662f\u65c1\u89c2\u8005\uff0c\u7adf\u6d51\u7136\u672a\u89c9\uff0c\u8db3\u89c1\u6765\u4eba\u8f7b\u529f\u8eab\u6cd5\u4e4b\u9ad8\uff0c\u5df2\u5230\u51fa\u795e\u5165\u5316\u7684\u81f3\u9ad8\u5883\u754c\uff0c\u4ed6\u4eec\u4efb\u4f55\u4e00\u4eba\u90fd\u671b\u5c18\u83ab\u53ca\u3002 \u3000\u3000\u96ea\u3001\u971c\u4e24\u59d0\u59b9\u84e6\u5730\u4e00\u60ca\uff0c\u53cc\u53cc\u8f6c\u8eab\uff0c\u6a2a\u5251\u5728\u624b\u3002 \u3000\u3000\u201c\u54ea\u6765\u7684\u56db\u4e2a\u8001\u602a\u7269\uff1f\u201d\u541f\u96ea\u663e\u7136\u4e0d\u8bc6\u6765\u4eba\u8eab\u4efd\u3002 \u3000\u3000\u541f\u971c\u51b7\u58f0\u9053\uff1a\u201c\u6d3b\u5f97\u4e0d\u8010\u70e6\u4e86\uff01\u201d \u3000\u3000\u6c5f\u67ab\u672a\u53ca\u51fa\u58f0\u559d\u963b\uff0c\u4e24\u59d0\u59b9\u5df2\u53cc\u53cc\u633a\u5251\u653b\u53bb\u3002 \u3000\u3000\u5979\u4eec\u53bb\u52bf\u75be\u5982\u7535\u5149\u77f3\u706b\uff0c\u51fa\u624b\u65e2\u72e0\u53c8\u6bd2\uff0c\u6beb\u4e0d\u7559\u60c5\uff0c\u4f3c\u51b3\u5fc3\u6bcf\u4eba\u5404\u6311\u4e24\u4e2a\uff0c\u4e00\u4e3e\u6b7c\u654c\u56db\u547d\uff0c\u7701\u5f97\u591a\u8d39\u624b\u811a\u3002 \u3000\u3000\u4f46\u5979\u4eec\u4f30\u8ba1\u9519\u4e86\u3002 \u3000\u3000\u56db\u4e2a\u4f8f\u5112\u7684\u8eab\u6cd5\u6bd4\u5979\u4eec\u66f4\u5feb\uff0c\u53ea\u89c1\u8eab\u5f62\u4e00\u6643\uff0c\u8fde\u6c5f\u67ab\u4e0e\u5f20\u56db\u59d1\u90fd\u672a\u770b\u6e05\uff0c\u4ed6\u4eec\u5df2\u907f\u5f00\u4e86\u4e24\u59d0\u59b9\u96f7\u9706\u4e07\u94a7\u7684\u81f4\u547d\u4e00\u51fb\u3002 \u3000\u3000\u66f4\u51fa\u4e4e\u6c5f\u67ab\u610f\u6599\u4e4b\u5916\u7684\uff0c\u662f\u56db\u4e2a\u4f8f\u5112\u5e76\u672a\u5f15\u7206\u70b8\u836f\uff0c\u4f3c\u4e4e\u5e76\u4e0d\u6253\u7b97\u62fc\u4e2a\u540c\u5f52\u4e8e\u5c3d\u3002 \u3000\u3000\u6216\u8bb8\u4e24\u59d0\u59b9\u5728\u4ed6\u4eec\u773c\u4e2d\uff0c\u4e0d\u503c\u5f97\u5c0f\u9898\u5927\u505a\u5427\uff1f \u3000\u3000\u53e6\u4e00\u4e2a\u63a5\u9053\uff1a\u201c\u5979\u4eec\u73a9\u771f\u7684\uff0c\u54b1\u4eec\u53ef\u5f00\u4e0d\u5f97\u73a9\u7b11\uff01\u201d \u3000\u3000\u6700\u4e11\u7684\u4e00\u4e2a\u633a\u8eab\u4e0a\u524d\uff0c\u53cc\u81c2\u4e00\u5f20\uff0c\u62e6\u4f4f\u5176\u4ed6\u4e09\u4eba\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u90fd\u4e0d\u8bb8\u63d2\u624b\uff0c\u6211\u8981\u6d3b\u6349\u4e24\u4e2a\u4e2b\u5934\u56de\u53bb\uff0c\u4e00\u4e2a\u66ff\u6211\u6413\u80cc\uff0c\u4e00\u4e2a\u66ff\u6211\u634f\u817f\u3002\u201d \u3000\u3000\u4e09\u4eba\u9f50\u58f0\u9053\uff1a\u201c\u4f60\u662f\u8001\u5927\uff0c\u542c\u4f60\u7684\uff01\u201d \u3000\u3000\u96ea\u3001\u971c\u4e24\u59d0\u59b9\u5341\u62ff\u4e5d\u7a33\u7684\u4e00\u5251\u653b\u53bb\uff0c\u5c45\u7136\u843d\u4e86\u4e2a\u7a7a\uff0c\u5fc3\u4e2d\u5df2\u662f\u60ca\u6012\u4ea4\u52a0\uff0c\u518d\u542c\u6700\u4e11\u7684\u8001\u5927\u51fa\u8a00\u65e0\u72b6\uff0c\u66f4\u662f\u6012\u4e0d\u53ef\u904f\uff0c\u6740\u6027\u5927\u53d1\u3002 \u3000\u3000\u5a07\u53f1\u58f0\u4e2d\uff0c\u53cc\u53cc\u633a\u5251\u5411\u90a3\u8001\u5927\u653b\u53bb\u3002 \u3000\u3000\u8001\u5927\u8eab\u5f62\u4e00\u62d4\u4e09\u4e08\uff0c\u51cc\u7a7a\u4e00\u7ffb\u8eab\uff0c\u6210\u4e86\u5934\u4e0b\u811a\u4e0a\uff0c\u76f4\u671d\u5251\u52bf\u518d\u5ea6\u8d70\u7a7a\u7684\u4e24\u59d0\u59b9\u5f53\u5934\u6251\u4e0b\u3002 \u3000\u3000\u4e24\u59d0\u59b9\u4e3e\u5251\u8fce\u523a\uff0c\u7adf\u88ab\u5f53\u5934\u6251\u4e0b\u7684\u4f8f\u5112\uff0c\u5f92\u624b\u6293\u4f4f\u4e24\u67c4\u5229\u5251\u7684\u524d\u7aef\u3002 \u3000\u3000\u5979\u4eec\u8fd9\u4e24\u67c4\u5251\u867d\u975e\u795e\u5175\u5229\u5668\uff0c\u4ea6\u7b97\u662f\u7cbe\u94a2\u6253\u9020\u7684\u597d\u5251\uff0c\u5c24\u5176\u4e24\u59d0\u59b9\u8fd0\u8db3\u771f\u529b\uff0c\u8d2f\u6ce8\u5728\u5251\u8eab\u4e4b\u4e0a\uff0c\u4e00\u5251\u523a\u51fa\uff0c\u52bf\u5982\u95ea\u7535\u6d41\u661f\uff0c\u529b\u8db3\u788e\u77f3\u5f00\u7891\uff0c\u65ad\u91d1\u524a\u7389\u3002 \u3000\u3000\u60f3\u4e0d\u5230\u8fd9\u4f8f\u5112\u7adf\u6562\u4ee5\u4e00\u53cc\u5411\u638c\uff0c\u5c06\u4e24\u628a\u5251\u6293\u4e2a\u6b63\u7740\uff0c\u770b\u5728\u6c5f\u67ab\u548c\u5f20\u56db\u59d1\u773c\u91cc\uff0c\u4e0d\u7981\u819b\u76ee\u7ed3\u820c\u3002\u82e5\u975e\u4eb2\u773c\u76ee\u51fb\uff0c\u7b80\u76f4\u96be\u4ee5\u7f6e\u4fe1\u3002 \u3000\u3000\u5c31\u5728\u96ea\u3001\u971c\u4e24\u59d0\u59b9\u6697\u81ea\u5fc3\u51c9\u4e4b\u9645\uff0c\u5176\u4ed6\u4e09\u540d\u4f8f\u5112\u8d81\u673a\u53d1\u96be\uff0c\u8eab\u5982\u6d41\u77e2\u75be\u6251\u800c\u6765\u3002 \u3000\u3000\u539f\u6765\u4f8f\u5112\u4e2d\u7684\u8001\u5927\uff0c\u53cc\u624b\u6234\u7740\u5929\u8327\u4e1d\u7f16\u7ec7\u624b\u5957\uff0c\u4e0d\u754f\u5200\u5251\uff0c\u5012\u4e0d\u662f\u4ed6\u5f53\u771f\u7ec3\u5c31\u4e86\u4e00\u53cc\u94c1\u638c\u3002 \u3000\u3000\u7531\u4e8e\u591c\u8272\u6726\u80e7\uff0c\u4e24\u59d0\u59b9\u4e00\u65f6\u4e0d\u5bdf\uff0c\u4ee5\u81f4\u672a\u80fd\u770b\u51fa\u3002 \u3000\u3000\u52a0\u4e0a\u8001\u5927\u4e24\u624b\u6293\u4f4f\u53cc\u5251\uff0c\u8eab\u5728\u7a7a\u4e2d\u5012\u7acb\u4e0d\u5760\uff0c\u7b49\u4e8e\u7f20\u4f4f\u4e86\u96ea\u3001\u971c\u4e24\u59d0\u59b9\uff0c\u6b63\u597d\u8ba9\u5176\u4ed6\u4e09\u4eba\u8d81\u673a\u7a81\u51fb\uff0c\u60f3\u653b\u5979\u4eec\u4e2a\u642d\u624b\u4e0d\u53ca\u3002 \u3000\u3000\u4e0d\u6599\u4e24\u59d0\u59b9\u6240\u7ec3\u201c\u4f20\u771f\u8bb0\u201d\u6b66\u529f\uff0c\u9664\u4e86\u65c1\u95e8\u90aa\u9053\u7684\u5947\u529f\u4e4b\u5916\uff0c\u5927\u591a\u662f\u51fa\u5947\u5236\u80dc\uff0c\u6216\u53cd\u8d25\u4e3a\u80dc\u7684\u602a\u62db\u3002 \u3000\u3000\u5979\u4eec\u7684\u51fa\u624b\u548c\u62db\u5f0f\uff0c\u4e0d\u4ec5\u5feb\uff0c\u72e0\uff0c\u7edd\uff0c\u66f4\u80fd\u4ee5\u8d85\u8d8a\u4eba\u4f53\u6781\u9650\u7684\u4efb\u4f55\u89d2\u5ea6\u653b\u654c\uff0c\u4ee4\u4eba\u9632\u4e0d\u80dc\u9632\uff0c\u751a\u81f3\u65e0\u4eba\u9632\u8d77\u3002 \u3000\u3000\u53ea\u89c1\u4e24\u59d0\u59b9\u624b\u4e2d\u63e1\u5251\u4e0d\u653e\uff0c\u5a07\u8eaf\u5411\u4e0a\u4e00\u7eb5\uff0c\u5e73\u98de\u800c\u8d77\uff0c\u5c31\u5728\u907f\u5f00\u4e09\u540d\u4f8f\u5112\u75be\u6251\u7684\u540c\u65f6\uff0c\u53cc\u811a\u5df2\u7167\u51c6\u8001\u5927\u9762\u95e8\u5168\u529b\u8e22\u53bb\u3002 \u3000\u3000\u201c\u54c7\uff01\u2026\u2026\u201d \u3000\u3000\u60e8\u53eb\u58f0\u4e2d\uff0c\u8001\u5927\u7684\u5934\u88ab\u8e22\u4e2d\uff0c\u987f\u65f6\u8111\u888b\u5f00\u82b1\uff0c\u9c9c\u8840\u4e0e\u8111\u6d46\u8ff8\u5c04\uff0c\u5012\u7acb\u7684\u8eab\u5b50\u76f4\u5760\u800c\u4e0b\u3002 \u3000\u3000\u4e24\u59d0\u59b9\u593a\u56de\u88ab\u4ed6\u6293\u4f4f\u7684\u5251\uff0c\u8db3\u4e00\u843d\u5730\u5c31\u5411\u6251\u7a7a\u7684\u4e09\u540d\u4f8f\u5112\u653b\u53bb\u3002 \u3000\u3000\u8fd9\u4e00\u8fde\u4e32\u52a8\u4f5c\uff0c\u4eff\u4f5b\u4e00\u6c14\u5475\u6210\uff0c\u5feb\u5f97\u4ee4\u4eba\u76ee\u4e0d\u6687\u63a5\u3002 \u3000\u3000\u4e09\u540d\u4f8f\u5112\u4e00\u6251\u7a7a\uff0c\u5df2\u60c5\u77e5\u4e0d\u5999\uff0c\u672a\u53ca\u56de\u8eab\u8fce\u654c\uff0c\u4fbf\u542c\u8001\u5927\u53d1\u51fa\u60e8\u53eb\uff0c\u66f4\u662f\u5927\u5403\u4e00\u60ca\u3002 \u3000\u3000\u53ea\u602a\u4ed6\u4eec\u770b\u8d70\u4e86\u773c\uff0c\u4e0d\u77e5\u4e24\u59d0\u59b9\u7684\u6765\u5934\uff0c\u4ee5\u4e3a\u5979\u4eec\u5e74\u8f7b\u53ef\u6b3a\uff0c\u4ee5\u81f4\u8fc7\u4e8e\u8f7b\u654c\u3002 \u3000\u3000\u5c24\u5176\u4e00\u89c1\u8001\u5927\u4e00\u51fa\u624b\uff0c\u5c31\u5f92\u624b\u6293\u4f4f\u4e24\u59d0\u59b9\u653b\u51fa\u7684\u5251\uff0c\u66f4\u4e0d\u628a\u5979\u4eec\u653e\u5728\u5fc3\u4e0a\u4e86\u3002 \u3000\u3000\u6b64\u523b\u81ea\u8d23\u62db\u5b50\u4e0d\u4eae\uff0c\u4e3a\u65f6\u5df2\u665a\u3002 \u3000\u3000\u60c5\u6025\u4e4b\u4e0b\uff0c\u4e09\u4eba\u5e78\u4ed7\u8eab\u6cd5\u7075\u5feb\uff0c\u6025\u5411\u4e24\u65c1\u95ea\u5f00\uff0c\u63a5\u7740\u5c31\u5730\u4e00\u6eda\uff0c\u6025\u7b97\u907f\u5f00\u4e86\u4e24\u59d0\u59b9\u7684\u8ffd\u6740\u3002 \u3000\u3000\u4e09\u4eba\u633a\u8eab\u8dc3\u8d77\uff0c\u6362\u5f62\u4e00\u6643\uff0c\u4ee5\u201c\u54c1\u201d\u5b57\u5f62\u56f4\u4f4f\u4e86\u96ea\u3001\u971c\u4e24\u59d0\u59b9\u3002 \u3000\u3000\u4ed6\u4eec\u4e0d\u6562\u518d\u8f7b\u654c\uff0c\u5404\u81ea\u4eae\u51fa\u4e86\u5175\u5668\u3002 \u3000\u3000\u201c\u5173\u4e1c\u4e94\u5947\u201d\u6b66\u529f\u535a\u6742\uff0c\u4f46\u4ee5\u5bb6\u4f20\u7684\u5916\u5bb6\u529f\u592b\u4e3a\u4e3b\uff0c\u7ec3\u7684\u662f\u94c1\u6c99\u638c\uff0c\u5f00\u7891\u624b\uff0c\u91d1\u521a\u62f3\u4e4b\u7c7b\u3002 \u3000\u3000\u50cf\u6240\u6709\u88ab\u795e\u79d8\u6559\u4e3b\u7f51\u7f57\u7684\u6b66\u6797\u8c6a\u6770\uff0c\u6c5f\u6e56\u67ad\u96c4\u4e00\u6837\uff0c\u4e00\u65e6\u53d7\u5230\u63a7\u5236\uff0c\u4fbf\u4e27\u5931\u7406\u6027\u4e0e\u81ea\u7531\uff0c\u5fc5\u987b\u552f\u547d\u662f\u4ece\u3002 \u3000\u3000\u90a3\u591c\u5728\u5bc6\u6797\u4e2d\uff0c\u5192\u5145\u795e\u79d8\u6559\u4e3b\u7684\u662f\u8001\u4e48\uff0c\u4e0d\u60dc\u540c\u5f52\u4e8e\u5c3d\uff0c\u65e8\u5728\u70b8\u6bd9\u6c5f\u67ab\uff0c\u7ed3\u679c\u5374\u843d\u5f97\u767d\u767d\u5f80\u9001\u4e86\u4e00\u6761\u547d\u3002 \u3000\u3000\u800c\u521a\u624d\u4e00\u65f6\u8f7b\u654c\uff0c\u60e8\u906d\u4e24\u59d0\u59b9\u8e22\u6b7b\u7684\u662f\u8001\u5927\uff0c\u73b0\u5728\u53ea\u5269\u4e0b\u4e86\u4e09\u4eba\u3002 \u3000\u3000\u4ed6\u4eec\u5929\u751f\u7578\u578b\uff0c\u4eba\u65e2\u957f\u4e0d\u9ad8\uff0c\u5176\u8c8c\u53c8\u4e0d\u626c\uff0c\u4f7f\u7528\u7684\u5175\u5668\u66f4\u602a\u3002 \u3000\u3000\u8001\u4e8c\u662f\u4e00\u53ea\u94a2\u5236\u516d\u6307\u602a\u624b\uff0c\u8fde\u6709\u516b\u5c3a\u6781\u7ec6\u94a2\u7d22\uff0c\u5df2\u7ec3\u5f97\u6536\u653e\u81ea\u5982\uff0c\u8fd1\u53ef\u8d34\u8eab\u8fde\u653b\u5e26\u5b88\uff0c\u8fdc\u5219\u80fd\u53d6\u654c\u4eba\u6027\u547d\u4e8e\u4e00\u4e08\u4e4b\u5916\u3002 \u3000\u3000\u8001\u4e09\u7528\u7684\u662f\u4e00\u628a\u4e09\u5c3a\u5de8\u526a\uff0c\u4ee5\u7cbe\u94a2\u6253\u9020\uff0c\u5200\u53e3\u950b\u5229\u65e0\u6bd4\uff0c\u5408\u800c\u4e3a\u7684\uff0c\u5206\u5f00\u5219\u6210\u4e24\u628a\u5355\u5200\u3002 \u3000\u3000\u800c\u8001\u56db\u7684\u5175\u5668\u66f4\u5947\u7279\uff0c\u7adf\u662f\u4e00\u5bf9\u62f3\u5934\u822c\u4e0d\u5927\u7684\u94c1\u9ab7\u9ac5\uff0c\u72b6\u4f3c\u6d41\u661f\u9524\uff0c\u4ee5\u94a2\u70bc\u4ee3\u7d20\u76f8\u8fde\uff0c\u4f7f\u8d77\u6765\u5a01\u529b\u975e\u540c\u5c0f\u53ef\u3002 \u3000\u3000\u4ec5\u770b\u4ed6\u4eec\u4e09\u4eba\u7684\u5175\u5668\uff0c\u5df2\u9887\u6709\u5148\u58f0\u593a\u4eba\u7684\u6c14\u52bf\u3002 \u3000\u3000\u4e09\u4eba\u5404\u636e\u4e00\u65b9\uff0c\u4ee5\u201c\u54c1\u201d\u5b57\u5f62\u56f4\u4f4f\u4e86\u4e24\u59d0\u59b9\uff0c\u5374\u672a\u6562\u62a2\u5148\u51fa\u624b\uff0c\u53ea\u662f\u4e25\u9635\u4ee5\u5f85\u7740\u3002 \u3000\u3000\u6c5f\u67ab\u89c1\u72b6\uff0c\u552f\u6050\u4ed6\u4eec\u662f\u865a\u5f20\u58f0\u52bf\uff0c\u4f01\u56fe\u4ee5\u70b8\u836f\u8ddf\u4e24\u59d0\u59b9\u62fc\u4e2a\u540c\u5f52\u4e8e\u5c3d\uff0c\u4e0d\u7981\u8822\u8822\u6b32\u52a8\u8d77\u6765\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u56db\u59d1\u4ee5\u4f20\u97f3\u5165\u5bc6\u529f\u529b\uff0c\u8b66\u544a\u9053\uff1a\u201c\u5c0f\u60c5\u90ce\uff0c\u5343\u4e07\u4e0d\u53ef\u591a\u4e8b\uff0c\u4ee5\u514d\u4e24\u4e2a\u4e2b\u5934\u2026\u2026\u201d \u3000\u3000\u8bdd\u72b9\u672a\u4e86\uff0c\u96ea\u3001\u971c\u4e24\u59d0\u59b9\u5df2\u7a81\u7136\u53d1\u96be\u3002 \u3000\u3000\u53ea\u89c1\u4eba\u5f71\u7ffb\u98de\uff0c\u91d1\u94c1\u4ea4\u9e23\u58f0\u5927\u8d77\uff0c\u53cc\u65b9\u5df2\u4ea4\u4e0a\u4e86\u624b\u3002 \u3000\u3000\u5bd2\u8292\u66b4\u8d77\uff0c\u4fbf\u542c\u63a5\u8fde\u51e0\u58f0\u60e8\u53eb\uff0c\u8840\u5149\u8fdb\u5c04\uff0c\u968f\u5373\u6076\u6597\u7ed3\u675f\u3002 \u3000\u3000\u5f53\u4e24\u59d0\u59b9\u655b\u5251\u7ad9\u5b9a\u65f6\uff0c\u4e09\u4e2a\u4f8f\u5112\u5df2\u5012\u5730\u4e0d\u8d77\u3002 \u3000\u3000\u771f\u4ee4\u4eba\u4e0d\u6562\u76f8\u4fe1\uff0c\u7728\u773c\u4e4b\u95f4\uff0c\u4e09\u4e2a\u6b66\u529f\u5947\u9ad8\u7684\u4f8f\u5112\uff0c\u51e0\u4e4e\u6beb\u65e0\u8fd8\u624b\u4e4b\u529b\uff0c\u4fbf\u5df2\u4e27\u547d\u5728\u5979\u4eec\u7684\u5feb\u5251\u4e4b\u4e0b\uff01 \u3000\u3000\u6c5f\u67ab\u4e0d\u7981\u5927\u9a87\uff0c\u5fc3\u77e5\u5979\u4eec\u4e00\u65e6\u6740\u4eba\u89c1\u8840\uff0c\u6b8b\u66b4\u4e4b\u6027\u5fc5\u7136\u4e00\u53d1\u4e0d\u53ef\u6536\u62fe\uff0c\u975e\u6740\u4e2a\u75db\u5feb\u4e0d\u53ef\u3002 \u3000\u3000\u4f46\u5979\u4eec\u8f6c\u5934\u4e00\u770b\uff0c\u88f8\u8eab\u8eba\u5728\u5730\u4e0a\u7684\u84dd\u51e4\u5df2\u4e0d\u77e5\u53bb\u5411\u3002 \u3000\u3000\u539f\u6765\u84dd\u51e4\u795e\u5fd7\u5df2\u6e05\uff0c\u8d81\u7740\u4e24\u59d0\u59b9\u4e0e\u56db\u4e2a\u4f8f\u5112\u4ea4\u624b\u65f6\uff0c\u6025\u5fd9\u722c\u8d77\u6765\u9003\u4e4b\u592d\u592d\u3002 \u3000\u3000\u8fde\u6c5f\u67ab\u548c\u5f20\u56db\u59d1\uff0c\u7531\u4e8e\u5168\u795e\u8d2f\u6ce8\u53cc\u65b9\uff0c\u7adf\u4e5f\u5fd8\u4e86\u8eba\u5728\u5730\u4e0a\u7684\u84dd\u51e4\uff0c\u6d51\u7136\u672a\u89c9\uff0c\u4e0d\u77e5\u5979\u4f55\u65f6\u5f00\u6e9c\u4e86\u3002 \u3000\u3000\u541f\u971c\u773c\u5149\u4e00\u626b\uff0c\u624b\u6307\u53f3\u65b9\u53eb\u9053\uff1a\u201c\u5979\u5728\u54ea\u91cc\uff01\u201d \u3000\u3000\u591c\u8272\u82cd\u832b\u4e0b\uff0c\u679c\u89c1\u4e00\u6761\u4eba\u5f71\u75be\u5954\u5982\u98de\uff0c\u5df2\u9003\u51fa\u6570\u5341\u4e08\u5916\uff0c\u65b9\u5411\u6b63\u662f\u5c71\u8c37\u53e3\u5916\u7684\u90a3\u7247\u7af9\u6797\u3002 \u3000\u3000\u96ea\u3001\u971c\u4e24\u59d0\u59b9\u54ea\u80af\u653e\u8fc7\u84dd\u51e4\uff0c\u7acb\u5373\u98de\u8eab\u8ffd\u53bb\u3002 \u3000\u3000\u6c5f\u67ab\u60c5\u6025\u9053\uff1a\u201c\u5927\u59d0\uff0c\u4e0d\u80fd\u8ba9\u5979\u4eec\u6740\u84dd\u51e4\u554a\uff01\u201d \u3000\u3000\u5f20\u56db\u59d1\u5e94\u4e86\u4e00\u58f0\uff0c\u4e24\u4eba\u90a3\u6562\u6020\u6162\uff0c\u65bd\u5c55\u8f7b\u529f\u6025\u8d77\u76f4\u8ffd\u3002 \u3000\u3000\u84dd\u51e4\u987e\u4e0d\u5f97\u5168\u8eab\u8d64\u88f8\uff0c\u75be\u5954\u5982\u98de\uff0c\u5411\u7af9\u6797\u9003\u53bb\u3002 \u3000\u3000\u96ea\u3001\u971c\u4e24\u59d0\u59b9\u5df2\u8ffd\u8fd1\u2026\u2026 \u3000\u3000\u7a81\u89c1\u6797\u5185\u51b2\u51fa\u5341\u51e0\u540d\u9ed1\u8863\u5200\u624b\uff0c\u8ba9\u5f00\u4e00\u6761\u8def\u4f7f\u84dd\u51e4\u901a\u8fc7\uff0c\u968f\u5373\u5408\u62e2\u4e00\u5b57\u6392\u5f00\uff0c\u6321\u4f4f\u4e86\u4e24\u59d0\u59b9\u7684\u53bb\u8def\u3002 \u3000\u3000\u96ea\u3001\u971c\u4e24\u59d0\u59b9\u6740\u6027\u5927\u8d77\uff0c\u90a3\u628a\u8fd9\u6279\u9ed1\u8863\u5200\u624b\u770b\u5728\u773c\u91cc\uff0c\u72c2\u559d\u4e00\u58f0\uff0c\u53cc\u53cc\u6325\u5251\u51b2\u6740\u4e0a\u524d\u3002 \u3000\u3000\u5341\u51e0\u540d\u9ed1\u8863\u5200\u624b\u4ea6\u975e\u5f31\u8005\uff0c\u4e2a\u4e2a\u8eab\u624b\u77eb\u5065\uff0c\u72e0\u62fc\u5584\u6597\uff0c\u53c8\u662f\u795e\u79d8\u6559\u4e3b\u624b\u4e0b\u7684\u6b7b\u58eb\u3002 \u3000\u3000\u4ed6\u4eec\u54ea\u7518\u793a\u5f31\uff0c\u9635\u5f62\u4e00\u53d8\uff0c\u4ee5\u9a6c\u8e44\u578b\u5305\u6284\u4e0a\u6765\uff0c\u5408\u529b\u5411\u4e24\u59d0\u59b9\u5c55\u5f00\u56f4\u653b\u3002 \u3000\u3000\u6c5f\u67ab\u5927\u60ca\uff0c\u8001\u8fdc\u5c31\u5927\u58f0\u53d1\u51fa\u8b66\u544a\uff0c\u53eb\u9053\uff1a\u201c\u5feb\u9000\uff01\u5f53\u5fc3\u4ed6\u4eec\u8eab\u4e0a\u85cf\u6709\u70b8\u836f\u2026\u2026\u201d \u3000\u3000\u4e24\u59d0\u59b9\u5145\u8033\u4e0d\u95fb\uff0c\u52c7\u5f80\u76f4\u524d\uff0c\u9510\u4e0d\u53ef\u6321\u3002 \u3000\u3000\u5251\u950b\u8fc7\u5904\uff0c\u53ea\u542c\u8fde\u58f0\u60e8\u53eb\uff0c\u51e0\u540d\u9996\u5f53\u5176\u51b2\u7684\u9ed1\u8863\u5200\u624b\uff0c\u5df2\u7136\u4e27\u547d\u5728\u5979\u4eec\u5251\u4e0b\u3002 \u3000\u3000\u51fa\u4e4e\u610f\u6599\u4e4b\u5916\uff0c\u53cc\u65b9\u77ed\u5175\u76f8\u63a5\uff0c\u5e76\u672a\u53d1\u751f\u60ca\u5929\u52a8\u5730\u7684\u5f3a\u70c8\u7206\u70b8\u3002 \u3000\u3000\u663e\u7136\u6c5f\u67ab\u4f30\u8ba1\u9519\u8bef\uff0c\u8fd9\u6279\u9ed1\u8863\u5200\u571f\u867d\u662f\u6b7b\u58eb\uff0c\u5374\u4e0d\u662f\u6562\u6b7b\u961f\u3002 \u3000\u3000\u4ed6\u4eec\u4e2a\u4e2a\u8eab\u624b\u4e0d\u5f31\uff0c\u5404\u51ed\u6b66\u529f\uff0c\u52a0\u4e0a\u62fc\u547d\u4e09\u90ce\u7684\u4f5c\u98ce\uff0c\u5fd7\u5728\u5168\u529b\u6740\u654c\uff0c\u53ef\u4ee5\u594b\u4e0d\u987e\u8eab\u3002 \u3000\u3000\u4f46\u4e0d\u4f3c\u6562\u6b7b\u961f\uff0c\u5168\u65e0\u6597\u5fd7\uff0c\u8ba4\u5b9a\u5bf9\u8c61\u4fbf\u5f15\u7206\u70b8\u836f\uff0c\u4e0d\u60dc\u6765\u4e2a\u540c\u5f52\u4e8e\u5c3d\u3002 \u3000\u3000\u773c\u770b\u521a\u4e00\u4ea4\u624b\uff0c\u5c31\u6709\u51e0\u4e2a\u9ed1\u8863\u5200\u624b\u4e27\u547d\uff0c\u4ed6\u4eec\u867d\u6697\u81ea\u5fc3\u60ca\uff0c\u5374\u4e0d\u77e5\u96be\u800c\u9000\uff0c\u53cd\u800c\u53d1\u51fa\u4e86\u72e0\u52b2\u3002 \u3000\u3000\u96ea\u3001\u971c\u4e24\u59d0\u59b9\u5df2\u6740\u7ea2\u4e86\u773c\uff0c\u51fa\u624b\u66f4\u89c1\u6b8b\u66b4\uff0c\u597d\u4f3c\u4e00\u4e2a\u6740\u4eba\u4e0d\u7728\u773c\u7684\u51f6\u795e\u6076\u715e\u3002 \u3000\u3000\u53cc\u65b9\u5c55\u5f00\u4e86\u6fc0\u6218\uff0c\u6740\u58f0\u9707\u5929\uff0c\u661f\u6708\u65e0\u5149\u3002 \u3000\u3000\u6c5f\u67ab\u89c1\u72b6\u6309\u637a\u4e0d\u4f4f\uff0c\u6025\u9053\uff1a\u201c\u5927\u59d0\u2026\u2026\u201d \u3000\u3000\u5f20\u56db\u59d1\u5012\u6c89\u5f97\u4f4f\u6c14\uff0c\u529d\u963b\u9053\uff1a\u201c\u4e24\u4e2a\u4e2b\u5934\u80fd\u5bf9\u4ed8\u5f97\u4e86\uff0c\u6211\u4eec\u4e0d\u5b9c\u63d2\u624b\u3002\u201d \u3000\u3000\u6c5f\u67ab\u803d\u5fc3\u9053\uff1a\u201c\u53ef\u662f\uff0c\u4e07\u4e00\u5979\u4eec\u6740\u6027\u4e00\u53d1\u4e0d\u53ef\u6536\u62fe\uff0c\u54ea\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u5f20\u56db\u59d1\u53f9\u4e86\u53e3\u6c14\uff0c\u795e\u60c5\u51dd\u91cd\u9053\uff1a\u201c\u53cd\u6b63\u6740\u7684\u654c\u4eba\uff0c\u5c31\u8ba9\u5979\u4eec\u6740\u4e2a\u75db\u5feb\u5427\u3002\u4e8b\u5230\u5982\u4eca\uff0c\u4f60\u6211\u90fd\u65e0\u80fd\u4e3a\u529b\u4e86\uff0c\u53ea\u597d\u7b49\u5979\u4eec\u6740\u591f\u4e86\u518d\u8bf4\u5427\u3002\u201d \u3000\u3000\u6c5f\u67ab\u9887\u89c9\u4e0d\u4ee5\u4e3a\u7136\uff0c\u60bb\u7136\u9053\uff1a\u201c\u5927\u59d0\uff0c\u96be\u9053\u4f60\u771f\u8981\u6740\u4e86\u5979\u4eec\uff1f\u201d \u3000\u3000\u5f20\u56db\u59d1\u53c8\u6df1\u6df1\u4e00\u53f9\uff0c\u6c89\u9ed8\u4e0d\u8bed\u3002 \u3000\u3000\u6c5f\u67ab\u518d\u4e5f\u6c89\u4e0d\u4f4f\u6c14\u4e86\uff0c\u54fc\u58f0\u9053\uff1a\u201c\u65e2\u7136\u5927\u59d0\u65e0\u80fd\u4e3a\u529b\uff0c\u6551\u4e0d\u4e86\u5979\u4eec\uff0c\u4e0d\u5982\u7531\u6211\u66ff\u5979\u4eec\u6740\u51e0\u4e2a\u654c\u4eba\uff0c\u4e5f\u597d\u66ff\u5979\u4eec\u62c5\u4ee3\u4e00\u4e9b\u55dc\u6740\u7684\u7f6a\u540d\uff01\u201d \u3000\u3000\u970d\u5730\u62bd\u51fa\u7fe0\u7389\u5200\uff0c\u6b63\u5f85\u4e0d\u987e\u4e00\u5207\u51b2\u4e0a\u524d\u53bb\uff0c\u5374\u88ab\u5f20\u56db\u59d1\u4e00\u628a\u62d6\u4f4f\u3002 \u3000\u3000\u201c\u4f60\u770b\uff01\u201d\u5f20\u56db\u59d1\u5411\u524d\u4e00\u6307\uff1a\u201c\u8fd8\u8f6e\u5f97\u5230\u4f60\u51fa\u624b\u5417\uff1f\u201d \u3000\u3000\u6c5f\u67ab\u5b9a\u795e\u4e00\u770b\uff0c\u5341\u51e0\u540d\u9ed1\u8863\u5200\u624b\u5df2\u88ab\u5979\u4eec\u8d76\u5c3d\u6740\u7edd\uff0c\u5168\u90e8\u4f5c\u4e86\u5251\u4e0b\u6e38\u9b42\u3002 \u3000\u3000\u201c\u5509\u2026\u2026\u201d\u6c5f\u67ab\u65e0\u5948\u5730\u53f9\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u96ea\u3001\u971c\u4e24\u59d0\u59b9\u5c45\u7136\u610f\u72b9\u672a\u5c3d\uff0c\u63d0\u5251\u51c6\u5907\u95ef\u5165\u7af9\u6797\u3002 \u3000\u3000\u6c5f\u67ab\u5927\u60ca\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u4e0d\u80fd\u2026\u2026\u201d \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u6797\u5185\u53c8\u51b2\u51fa\u5341\u51e0\u540d\u9ed1\u8863\u5200\u624b\u3002 \u3000\u3000\u4ed6\u4eec\u4ecd\u662f\u4e00\u5b57\u6392\u6b7c\uff0c\u6321\u5728\u4e86\u6797\u524d\u3002 \u3000\u3000\u201c\u59d0\u3002\u201d\u541f\u971c\u632f\u594b\u9053\uff1a\u201c\u4eca\u591c\u6211\u4eec\u53ef\u4ee5\u6740\u4e2a\u8fc7\u763e\u4e86\u3002\u201d \u3000\u3000\u541f\u96ea\u7b11\u9053\uff1a\u201c\u53cd\u6b63\u4ed6\u4eec\u4eba\u591a\u561b\uff01\u201d \u3000\u3000\u8bdd\u58f0\u752b\u843d\uff0c\u5c31\u89c1\u5bf9\u65b9\u4e24\u540d\u9ed1\u8863\u5200\u624b\u633a\u8eab\u8d70\u51fa\uff0c\u5176\u4e2d\u4e00\u4eba\u53d1\u8bdd\u9053\uff1a\u201c\u4f60\u4eec\u5c11\u8bf4\u5927\u8bdd\uff0c\u6709\u79cd\u4e00\u5bf9\u4e00\uff0c\u6211\u4eec\u4e0d\u60f3\u4ed7\u4eba\u591a\u52bf\u4f17\uff0c\u4ee5\u591a\u53d6\u80dc\uff0c\u80dc\u4e4b\u4e0d\u6b66\u3002\u201d \u3000\u3000\u8bf4\u7740\uff0c\u4e5f\u4e0d\u7ba1\u4e24\u59d0\u59b9\u662f\u5426\u63a5\u53d7\u6311\u6218\uff0c\u4fbf\u9017\u81ea\u5411\u5979\u4eec\u8d70\u6765\u3002 \u3000\u3000\u6c5f\u67ab\u89c1\u60c5\u51b5\u6709\u5f02\uff0c\u672a\u53ca\u559d\u963b\uff0c\u5f20\u56db\u59d1\u5df2\u51fa\u624b\u3002 \u3000\u3000\u53ea\u89c1\u5979\u53cc\u624b\u9f50\u6768\uff0c\u5bd2\u8292\u95e8\u5904\uff0c\u4e24\u68f5\u9e3d\u86cb\u822c\u5927\u5c0f\u7684\u94a2\u73e0\u5df2\u75be\u5c04\u800c\u53bb\u3002 \u3000\u3000\u4e24\u540d\u9ed1\u8863\u5200\u624b\u8ddd\u79bb\u4e24\u59d0\u59b9\u4e0d\u8db3\u4e24\u4e08\uff0c\u51b7\u4e0d\u9632\u88ab\u5f20\u56db\u59d1\u5c04\u6765\u7684\u94a2\u73e0\u51fb\u4e2d\u8170\u90e8\u3002 \u3000\u3000\u201c\u554a\u2026\u2026\u201d \u3000\u3000\u60ca\u547c\u58f0\u4e2d\uff0c\u7a81\u95fb\u8f70\u7136\u4e24\u58f0\u5de8\u54cd\u3002 \u3000\u3000\u679c\u7136\u4e0d\u51fa\u6240\u6599\uff0c\u4ed6\u4eec\u8eab\u4e0a\u85cf\u4e86\u5f3a\u70c8\u70b8\u836f\u3002 \u3000\u3000\u58f0\u9707\u5929\u5730\uff0c\u96ea\u3001\u971c\u4e24\u59d0\u59b9\u867d\u53ca\u65f6\u66b4\u9000\uff0c\u4e0d\u81f4\u50cf\u4e24\u540d\u6562\u6b7b\u961f\u4e00\u6837\uff0c\u88ab\u70b8\u5f97\u8840\u8089\u6a21\u7cca\uff0c\u80a2\u79bb\u7834\u788e\uff0c\u4e5f\u88ab\u5f3a\u70c8\u7206\u70b8\u529b\u9707\u5f97\u5f53\u573a\u660f\u8ff7\uff0c\u5012\u5730\u4e0d\u8d77\u3002 \u3000\u3000\u6c5f\u67ab\u60ca\u6012\u4ea4\u52a0\uff0c\u987e\u4e0d\u5f97\u5bdf\u770b\u5979\u4eec\uff0c\u51b2\u4e0a\u524d\u5c31\u53cc\u624b\u9f50\u626c\uff0c\u8fde\u8fde\u5c06\u88c5\u5728\u8eab\u4e0a\u7684\u94a2\u73e0\u5c04\u5411\u6797\u524d\u90a3\u6279\u9ed1\u8863\u5200\u624b\u3002\u5f20\u56db\u59d1\u4e5f\u8fde\u8fde\u51fa\u624b\uff0c\u4e0d\u5bb9\u5bf9\u65b9\u4efb\u4f55\u4e00\u4eba\u6251\u8fd1\u3002 \u3000\u3000\u987f\u65f6\uff0c\u60ca\u5929\u52a8\u5730\u7684\u7206\u70b8\u58f0\u8fde\u8d77\uff0c\u4e0d\u7edd\u4e8e\u8033\uff0c\u706b\u5149\u76f4\u51b2\u4e91\u9704\uff0c\u58f0\u52bf\u597d\u4e0d\u9a87\u4eba\uff01 \u3000 \u7b2c\u4e09\u5341\u4e00\u56de\u3000\u6b63\u4e49\u5251\u8bdb\u65ad\u4e71\u4e16\u9b54 \u3000 \u3000\u3000\u4eca\u591c\u65e0\u6708\uff0c\u8352\u5e99\u7684\u5927\u6bbf\u4e0a\uff0c\u7528\u6728\u677f\u652f\u8d77\u4e86\u56db\u5f20\u4e34\u65f6\u7684\u6728\u5e8a\u3002 \u3000\u3000\u541f\u96ea\u3001\u541f\u971c\uff0c\u5e76\u69bb\u800c\u5367\u3002 \u3000\u3000\u6c5f\u67ab\u548c\u767d\u5929\u5316\u5206\u8eba\u5728\u53e6\u5916\u4e24\u5f20\u6728\u69bb\u4e0a\u3002 \u3000\u3000\u767d\u5929\u5316\u5df2\u7ecf\u6b7b\u4e86\uff0c\u4f46\u9003\u8d70\u7684\u5929\u540e\u5bab\u4e0b\u5f1f\u5b50\u5e76\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u6700\u5947\u602a\u7684\u662f\u6b7b\u4e86\u7684\u5927\u9b54\u5973\uff0c\u7adf\u4e5f\u590d\u6d3b\u4e86\uff0c\u5c31\u5750\u5728\u6c5f\u67ab\u7684\u5012\u5367\u7684\u6728\u69bb\u65c1\u4fa7\uff0c\u795e\u60c5\u95f4\u65e0\u9650\u54c0\u51c4\u3002 \u3000\u3000\u5927\u6bbf\u4e2d\u70e7\u8d77\u56db\u652f\u5de8\u70db\uff0c\u706f\u5149\u660e\u4eae\uff0c\u666f\u7269\u6e05\u6670\u53ef\u89c1\u3002 \u3000\u3000\u5f20\u56db\u59d1\u3001\u7f57\u5170\uff0c\u5206\u7ad9\u5728\u96ea\u3001\u971c\u4e8c\u5973\u69bb\u524d\uff0c\u624b\u4e2d\u6267\u7740\u836f\u7897\uff0c\u5582\u4e8c\u5973\u8fdb\u98df\u836f\u7269\uff0c\u5927\u6bbf\u4e2d\u836f\u5473\u6251\u9f3b\u3002 \u3000\u3000\u8001\u53eb\u5316\u3001\u6b27\u9633\u662d\u548c\u4e94\u4e2a\u5f1f\u5b50\uff0c\u4ec1\u3001\u4e49\u3001\u793c\u3001\u667a\u3001\u4fe1\uff0c\u624b\u6267\u5175\u5203\uff0c\u7ad9\u5728\u6bbf\u4e2d\u6212\u5907\u3002 \u3000\u3000\u80e1\u840d\u3001\u6bb5\u4e5d\uff0c\u5b88\u5728\u5927\u6bbf\u95e8\u53e3\uff0c\u80e1\u5143\u548c\u90ed\u5929\u540c\uff0c\u5206\u7acb\u5728\u4e24\u4eba\u8eab\u540e\uff0c\u4f3c\u662f\u51c6\u5907\u63a5\u5e94\u3002 \u3000\u3000\u4e03\u5b9d\u548c\u5c1a\u3001\u5201\u9e4f\uff0c\u7387\u9886\u5341\u4f59\u4eba\uff0c\u5206\u5750\u5927\u6bbf\u56db\u89d2\uff0c\u90fd\u5728\u76d8\u5750\u8c03\u606f\u3002 \u3000\u3000\u590f\u5929\u540c\u3001\u7533\u96ea\u541b\u548c\u4e09\u5927\u697c\u4e3b\uff0c\u56f4\u7ed5\u5728\u767d\u5929\u5316\u7684\u6728\u69bb\u56db\u5468\uff0c\u9690\u9690\u6709\u4fdd\u62a4\u4e4b\u610f\u3002 \u3000\u3000\u8fd9\u60c5\u666f\u5f88\u660e\u663e\uff0c\u96ea\u3001\u971c\u4e8c\u5973\u548c\u6c5f\u67ab\u4f3c\u662f\u90fd\u53d7\u4e86\u4f24\uff0c\u767d\u5929\u5316\u53c8\u4f3c\u4f24\u7684\u6700\u91cd\u3002 \u3000\u3000\u5728\u5f3a\u654c\u76d1\u89c6\u4e4b\u4e0b\uff0c\u65e0\u6cd5\u628a\u4f24\u8005\u8fd0\u8d70\uff0c\u8fd9\u4e9b\u4f24\u7684\u4eba\uff0c\u53c8\u90fd\u662f\u4e3b\u529b\u4eba\u7269\uff0c\u65e2\u8981\u4eba\u7167\u987e\u4ed6\u4eec\u7684\u4f24\u52bf\uff0c\u53c8\u9700\u8981\u4ed6\u4eec\u5728\u5f3a\u654c\u627e\u4e0a\u6765\u65f6\uff0c\u62b1\u4f24\u8fce\u6218\uff0c\u51fa\u624b\u514b\u654c\u3002 \u3000\u3000\u6240\u4ee5\uff0c\u5f20\u56db\u59d1\u628a\u6240\u6709\u7684\u4eba\uff0c\u5168\u90fd\u96c6\u4e2d\u5728\u5927\u6bbf\u4e2d\uff0c\u51c6\u5907\u5fc5\u8981\u65f6\uff0c\u5168\u529b\u53cd\u51fb\u3002 \u3000\u3000\u590f\u5929\u540c\u7a81\u7136\u5f00\u4e86\u53e3\uff0c\u9053\uff1a\u201c\u5f20\u59d1\u5a18\uff0c\u5b88\u5728\u8fd9\u5ea7\u8352\u5e99\u4e2d\uff0c\u4e0d\u662f\u529e\u6cd5\uff0c\u4f55\u4e0d\u5e26\u7740\u4f24\u8005\uff0c\u8fde\u591c\u9041\u8d70\u3002 \u3000\u3000\u5728\u4e0b\u7684\u5929\u9a6c\u5802\uff0c\u8fd8\u6709\u90e8\u5206\u5b9e\u529b\uff0c\u5f20\u59d1\u5a18\u5982\u80af\u76f8\u4fe1\u590f\u67d0\uff0c\u4f55\u4e0d\u540c\u5230\u5929\u9a6c\u603b\u5802\u53bb\uff0c\u90a3\u91cc\u5f62\u52bf\u9669\u8981\uff0c\u53ef\u4ee5\u56fa\u5b88\u2026\u2026\u201d \u3000\u3000\u201c\u4f60\u8ba4\u4e3a\uff0c\u6211\u4eec\u8fd8\u80fd\u8d70\u5f97\u4e86\u5417\uff1f\u201d \u3000\u3000\u5f20\u56db\u59d1\u51b7\u51b7\u7684\u6253\u65ad\u4e86\u590f\u5929\u540c\u7684\u8bdd\u3002 \u3000\u3000\u201c\u5e94\u8be5\u8d70\u5f97\u4e86\u2026\u2026\u201d\u590f\u5929\u540c\u63a5\u9053\uff1a\u201c\u6211\u4eec\u867d\u6709\u4f24\u4ea1\uff0c\u4f46\u6559\u4e3b\u51ed\u4ed7\u7684\u5929\u540e\u5bab\u3001\u5929\u738b\u95e8\uff0c\u4e5f\u5df2\u5168\u519b\u5c3d\u6ca1\uff0c\u79cb\u79bb\u82b1\u7b49\u4e09\u5927\u5bab\u4e3b\u5168\u6570\u88ab\u6740\u3002 \u3000\u3000\u9ec4\u5929\u738b\u548c\u56db\u5927\u5929\u9e45\uff0c\u4e5f\u65e0\u4e00\u4eba\u6f0f\u7f51\uff0c\u4e3b\u6301\u5929\u9b54\u5927\u9635\u7684\u4e5d\u5927\u9b54\u5973\u4e4b\u4e00\uff0c\u53c8\u4e3a\u6c5f\u67ab\u6240\u83b7\uff0c\u5f7c\u6b64\u90fd\u4f24\u4e86\u5143\u6c14\uff0c\u54b1\u4eec\u6b64\u65f6\u4e0d\u8d70\uff0c\u66f4\u5f85\u4f55\u65f6\uff1f\u201d \u3000\u3000\u201c\u5982\u679c\u767d\u5929\u5316\u8bf4\u7684\u662f\u5b9e\u8bdd\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u90a3\u4f4d\u6559\u4e3b\uff0c\u5e94\u8be5\u8fd8\u6709\u522b\u7684\u529b\u91cf\uff0c\u54b1\u4eec\u4e00\u4e0a\u8def\uff0c\u4ed6\u4eec\u6697\u4e2d\u622a\u51fb\uff0c\u5c82\u4e0d\u662f\u8981\u88ab\u4e2a\u4e2a\u51fb\u7834\uff0c\u6c5f\u67ab\u548c\u4e8c\u5973\u867d\u7136\u53d7\u4f24\uff0c\u4f46\u4ee5\u6211\u7684\u533b\u672f\uff0c\u76f8\u4fe1\u4e09\u4e24\u5929\uff0c\u5c31\u53ef\u4ee5\u4f7f\u4ed6\u4eec\u7684\u4f24\u52bf\u597d\u8f6c\uff0c\u2026\u2026\u201d \u3000\u3000\u201c\u6050\u6015\u662f\u6765\u4e0d\u53ca\u4e86\uff01\u201d \u3000\u3000\u4e00\u4e2a\u5a01\u91cd\u7684\u58f0\u97f3\uff0c\u6253\u65ad\u4e86\u5f20\u56db\u59d1\u7684\u8bdd\uff0c\u98d8\u4f20\u5165\u6bbf\uff0c\u9053\uff1a\u201c\u6c5f\u67ab\u548c\u4e24\u4e2a\u5c0f\u5996\u5973\u53d7\u4e86\u4f24\uff0c\u4f60\u4eec\u5c31\u5b8c\u5168\u6ca1\u6709\u8fd8\u624b\u7684\u673a\u4f1a\u5566\uff01\u201d \u3000\u3000\u968f\u7740\u8bdd\u58f0\uff0c\u4e00\u4e2a\u8eab\u7740\u9752\u8272\u957f\u886b\u7684\u4eba\uff0c\u5728\u56db\u4e2a\u9ed1\u8272\u52b2\u88c5\u5927\u6c49\u73af\u62a4\u4e4b\u4e0b\uff0c\u7f13\u6b65\u5411\u6bbf\u4e2d\u884c\u8fdb\u6765\u3002 \u3000\u3000\u80e1\u840d\u3001\u6bb5\u4e5d\uff0c\u5200\u3001\u5251\u5e76\u51fa\uff0c\u6b32\u963b\u6765\u4eba\uff0c\u4f46\u5374\u88ab\u5f20\u56db\u59d1\u963b\u6b62\uff0c\u9053\uff1a\u201c\u8ba9\u4ed6\u4eec\u8fdb\u6765\uff0c\u5927\u4f19\u5168\u529b\u6212\u5907\uff0c\u653e\u624b\u4e00\u62fc\uff0c\u8fd8\u4e0d\u77e5\u8c01\u80dc\u8c01\u8d1f\u5462\uff1f\u201d \u3000\u3000\u56db\u4e2a\u9ed1\u8863\u52b2\u88c5\u5927\u6c49\uff0c\u9762\u76ee\u51b7\u8083\uff0c\u6740\u6c14\u56db\u6ea2\uff0c\u4f46\u4ed6\u4eec\u6ca1\u6709\u51fa\u624b\u8fd8\u51fb\uff0c\u56e0\u4e3a\u80e1\u840d\u3001\u6bb5\u4e5d\uff0c\u5728\u5f20\u56db\u59d1\u53ca\u65f6\u7684\u559d\u6b62\u4e0b\uff0c\u6536\u4e86\u5200\u5251\u800c\u9000\u3002 \u3000\u3000\u6c5f\u67ab\u8eba\u5728\u75c5\u5e8a\u4e0a\uff0c\u4e00\u773c\u4e4b\u95f4\uff0c\u5df2\u77a7\u51fa\u56db\u4e2a\u9ed1\u8863\u52b2\u88c5\u5927\u6c49\uff0c\u5c31\u662f\u56db\u5927\u795e\u901a\u4f7f\u8005\u3002 \u3000\u3000\u90a3\u591c\u5f00\u5c01\u4e00\u6218\uff0c\u6c5f\u67ab\u5bf9\u90a3\u4f4d\u795e\u901a\u4f7f\u8005\uff0c\u7559\u4e0b\u4e86\u6781\u4e3a\u6df1\u523b\u7684\u5370\u8c61\u3002 \u3000\u3000\u8fd9\u56db\u4eba\u4e2d\uff0c\u5fc5\u6709\u5176\u4e00\u662f\u90a3\u4e2a\u4eba\uff0c\u56db\u4e2a\u4eba\u9762\u76ee\u5446\u677f\uff0c\u663e\u7136\u90fd\u5e26\u4e86\u9762\u5177\uff0c\u7531\u56db\u4eba\u8eab\u4e0a\u6563\u53d1\u51fa\u6740\u6c14\u5224\u8bba\uff0c\u6b66\u529f\u5728\u4f2f\u3001\u4ef2\u4e4b\u95f4\u3002 \u3000\u3000\u6559\u4e3b\u8eab\u4fa7\u6709\u56db\u5927\u795e\u901a\u4f7f\u8005\uff0c\u4e3e\u4e00\u53cd\u4e09\uff0c\u5982\u6b64\u76f8\u4f3c\u7684\u56db\u4e2a\u9ad8\u624b\uff0c\u81ea\u7136\u662f\u56db\u5927\u795e\u901a\u4f7f\u8005\u4e86\u3002 \u3000\u3000\u6c5f\u67ab\u4e5f\u540c\u65f6\u60f3\u901a\u4e86\u53e6\u4e00\u4ef6\u4e8b\uff0c\u6240\u8c13\u56db\u5927\u795e\u901a\u4f7f\u8005\uff0c\u5c31\u662f\u6559\u4e3b\u8fd1\u8eab\u4ece\u536b\uff0c\u7531\u4ed6\u4eec\u56db\u4eba\u4f20\u8fbe\u6559\u4e3b\u7684\u547d\u4ee4\uff0c\u81ea\u7136\u662f\u6070\u5f53\u4e0d\u8fc7\u3002 \u3000\u3000\u6240\u8c13\u795e\u901a\u4f7f\u8005\uff0c\u53ea\u4e0d\u8fc7\u662f\u4e00\u79cd\u60d1\u4eba\u8033\u76ee\u7684\u540d\u79f0\uff0c\u4ee5\u6df7\u6dc6\u8eab\u4efd\uff0c\u7531\u8fd9\u56db\u4eba\u62a4\u9a7e\uff0c\u90a3\u4f4d\u9752\u886b\u4eba\uff0c\u53ef\u80fd\u662f\u771f\u7684\u6559\u4e3b\u4e86\uff1f \u3000\u3000\u4f46\u8ba9\u6c5f\u67ab\u65e0\u6cd5\u80af\u5b9a\u7684\u662f\uff0c\u84dd\u51e4\u8fd8\u672a\u73b0\u8eab\uff0c\u53cc\u65b9\u65e9\u5df2\u7ea6\u597d\u4e86\uff0c\u5979\u4f1a\u73b0\u8eab\u6307\u51fa\u90a3\u4f4d\u6559\u4e3b\u7684\u8eab\u4efd\uff0c\u4ee5\u514d\u4f7f\u771f\u6b63\u7f6a\u9b41\u7978\u9996\u6f0f\u7f51\u3002 \u3000\u3000\u84dd\u51e4\u4e0d\u73b0\u8eab\uff0c\u6c5f\u67ab\u53ea\u597d\u5fcd\u4e0b\u53bb\u3002 \u3000\u3000\u201c\u4f0a\u5170\u4e1d\u2026\u2026\u201d\u9752\u886b\u4eba\u51b7\u51b7\u7684\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u638c\u6559\u4eb2\u4f20\u5f1f\u5b50\uff0c\u7adf\u6562\u80cc\u53db\uff0c\u4e0d\u6015\u9b54\u706b\u711a\u8eab\u7684\u6559\u89c4\u5417\uff1f\u201d \u3000\u3000\u4f0a\u5170\u4e1d\u662f\u8c01\uff1f \u3000\u3000\u6ca1\u6709\u4eba\u77e5\u9053\uff0c\u4f46\u5f20\u56db\u59d1\u5f88\u5feb\u9192\u609f\uff0c\u662f\u90a3\u4f4d\u88ab\u6740\u7684\u9b54\u5973\u4e86\u3002 \u3000\u3000\u201c\u9b54\u6559\u5a9a\u672f\uff0c\u4e0d\u8fc7\u5c14\u5c14\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u5e76\u4e0d\u6bd4\u5929\u6293\u6280\u827a\u9ad8\u660e\u3002\u201d \u3000\u3000\u6c5f\u67ab\u548c\u5047\u626e\u9b54\u5973\u7684\u9752\u51e4\uff0c\u7acb\u523b\u88ab\u5f20\u56db\u59d1\u7684\u8a00\u8bed\u70b9\u9192\u3002 \u3000\u3000\u9752\u51e4\u4e0d\u80fd\u5f00\u53e3\uff0c\u5979\u5bf9\u9b54\u5973\u4f0a\u5170\u4e1d\u77e5\u9053\u7684\u592a\u5c11\u4e86\uff0c\u7ecf\u4e0d\u8d77\u4e00\u6b21\u76d8\u95ee\u3002 \u3000\u3000\u4f46\u5979\u7684\u53cd\u5e94\u5f88\u7edd\uff0c\u7d27\u7d27\u5730\u6293\u4f4f\u4e86\u6c5f\u67ab\u5de6\u624b\uff0c\u7f13\u7f13\u56de\u5934\uff0c\u671b\u5411\u9752\u886b\u4eba\uff0c\u8138\u4e0a\u662f\u4e00\u7247\u54c0\u4f24\u3001\u6101\u82e6\u3002 \u3000\u3000\u8fd9\u79cd\u8868\u60c5\uff0c\u7ed9\u4eba\u4e00\u79cd\u6781\u5927\u7684\u56f0\u60d1\uff0c\u4e0d\u77e5\u662f\u75db\u4f24\u6c5f\u67ab\u7684\u4f24\u52bf\u4e25\u91cd\u5462\uff1f\u8fd8\u662f\u8eab\u53d7\u7981\u5236\uff0c\u65e0\u6cd5\u6446\u8131\u3002 \u3000\u3000\u5b66\u8fc7\u5a9a\u672f\u7684\u4eba\uff0c\u90fd\u5584\u88c5\u4f5c\uff0c\u8868\u60c5\u4e4b\u4f73\uff0c\u5165\u6728\u4e09\u5206\uff0c\u5929\u540e\u5bab\u3001\u5929\u738b\u95e8\u7684\u5a9a\u672f\uff0c\u90fd\u6e90\u51fa\u9b54\u6559\uff0c\u548c\u9b54\u5973\u4e00\u8109\u76f8\u627f\uff0c\u53ea\u6709\u529f\u529b\u6df1\u6d45\u4e4b\u522b\uff0c\u5e76\u65e0\u95e8\u6237\u4e4b\u89c1\u3002 \u3000\u3000\u6240\u4ee5\uff0c\u9752\u51e4\u7684\u8868\u73b0\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u7adf\u4f7f\u90a3\u4f4d\u9752\u8863\u4eba\u65e0\u6cd5\u5206\u8fa8\u771f\u5047\u3002 \u3000\u3000\u201c\u770b\u6765\uff0c\u4f60\u662f\u771f\u7684\u80cc\u53db\u4e86\u672c\u6559\uff0c\u4e5f\u72af\u4e86\u6559\u89c4\u2026\u2026\u201d \u3000\u3000\u9752\u886b\u4eba\u7a81\u7136\u98de\u8eab\u800c\u8d77\uff0c\u76f4\u51b2\u8fc7\u6765\u3002 \u3000\u3000\u6c5f\u67ab\u51e0\u4e4e\u8981\u62d4\u5251\u51fb\u51fa\uff0c\u4f46\u5374\u53c8\u52c9\u5f3a\u5fcd\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u56e0\u4e3a\uff0c\u4ed6\u7a81\u7136\u60f3\u5230\u4e86\uff0c\u8fd9\u4e2a\u4eba\u4e0d\u662f\u6559\u4e3b\uff0c\u800c\u662f\u771f\u6b63\u7684\u9b54\u6559\u4e2d\u4eba\u3002 \u3000\u3000\u9752\u51e4\u4e5f\u6ca1\u6709\u52a8\uff0c\u52a8\u7684\u662f\u5f20\u56db\u59d1\uff0c\u98de\u8eab\u8fce\u4e86\u4e0a\u53bb\uff0c\u53f3\u624b\u6325\u52a8\uff0c\u5251\u5149\u6253\u95ea\uff0c\u8fde\u653b\u4e09\u5251\u3002 \u3000\u3000\u65ad\u80a0\u4e09\u5251\uff0c\u662f\u5f20\u56db\u59d1\u7684\u7edd\u6280\u4e4b\u4e00\u3002 \u3000\u3000\u9752\u8863\u4eba\u53f3\u624b\u98de\u5c04\u51fa\u4e00\u7247\u91d1\u5149\uff0c\u5168\u8eab\u73af\u7ed5\uff0c\u6321\u5f00\u4e09\u5251\u3002 \u3000\u3000\u9752\u8863\u4eba\u867d\u7136\u5c01\u4f4f\u4e86\u5f20\u56db\u59d1\u7684\u65ad\u80a0\u4e09\u5251\uff0c\u4f46\u6251\u5411\u9752\u51e4\u7684\u653b\u52bf\uff0c\u4e5f\u88ab\u62e6\u4f4f\u3002 \u3000\u3000\u201c\u597d\uff01\u770b\u6765\uff0c\u9664\u4e86\u6c5f\u67ab\u548c\u4e24\u4e2a\u5c0f\u5996\u5973\u4e4b\u5916\uff0c\u8fd8\u6709\u4f60\u5f20\u56db\u59d1\u4e5f\u662f\u4e00\u4f4d\u9ad8\u624b\u3002\u201d\u53c8\u4e00\u4e2a\u8eab\u7740\u9752\u886b\u7684\u4e2d\u5e74\u4eba\uff0c\u5728\u84dd\u51e4\u76f8\u4f34\u4e2d\uff0c\u7f13\u6b65\u800c\u5165\u3002 \u3000\u3000\u6b64\u4eba\u4e00\u73b0\u8eab\uff0c\u56db\u4e2a\u9ed1\u8863\u52b2\u88c5\u5927\u6c49\uff0c\u7acb\u523b\u5fae\u5fae\u4e00\u8eab\u4f5c\u793c\u3002 \u3000\u3000\u5f20\u56db\u59d1\u548c\u90a3\u521d\u73b0\u8eab\u7684\u9752\u8863\u4eba\u4ea4\u624b\u4e00\u51fb\uff0c\u53ea\u89c9\u4ed6\u624b\u4e2d\u4e4b\u7269\u53cd\u9707\u4e4b\u529b\uff0c\u5341\u5206\u5f3a\u5927\uff0c\u4e0d\u77e5\u7528\u7684\u4ec0\u4e48\u5175\u5203\uff0c\u7adf\u7136\u80fd\u5e7b\u51fa\u4e00\u7247\u91d1\u5149\uff0c\u62a4\u4f4f\u5168\u8eab\uff0c\u5fcd\u4e0d\u4f4f\u8f6c\u773c\u770b\u53bb\u3002 \u3000\u3000\u53ea\u89c1\u90a3\u4eba\u624b\u4e2d\uff0c\u6267\u7740\u4e00\u9762\u94dc\u94b9\uff0c\u94b9\u9762\u6709\u8f6e\u6708\u5927\u5c0f\uff0c\u96be\u602a\u80fd\u821e\u51fa\u4e00\u7247\u91d1\u5149\uff0c\u62a4\u4f4f\u5168\u8eab\u4e86\u3002 \u3000\u3000\u8fd9\u662f\u6c5f\u6e56\u4eba\u5f88\u5c11\u4f7f\u7528\u7684\u5175\u5203\uff0c\u4f7f\u7528\u8005\u5927\u90fd\u662f\u50e7\u3001\u9053\u8eab\u4efd\uff0c\u4e5f\u5fc5\u6709\u7279\u6b8a\u7684\u51fa\u51fb\u624b\u6cd5\uff0c\u8fd9\u4eba\u5df2\u663e\u7136\u4e0d\u662f\u6559\u4e3b\uff0c\u4f46\u4ed6\u529f\u529b\u4e0d\u5f31\uff0c\u53c8\u4f1a\u662f\u4ec0\u4e48\u4eba\u5462\uff1f \u3000\u3000\u5979\u80f8\u6709\u6210\u7af9\uff0c\u9047\u4e8b\u51b7\u9759\uff0c\u5224\u5b9a\u7b2c\u4e00\u4e2a\u9752\u8863\u4eba\u7684\u8eab\u4efd\uff0c\u624d\u8f6c\u5411\u84dd\u51e4\u76f8\u4f34\u800c\u5165\u7684\u9752\u8863\u4eba\u8eab\u4e0a\uff0c\u9053\uff1a\u201c\u8fc7\u5956\u4e86\uff0c\u4f60\u5927\u6982\u662f\u90a3\u4f4d\u771f\u7684\u6559\u4e3b\u4e86\uff0c\u54fc\uff01\u679c\u7136\u662f\u72e1\u733e\u5f97\u5f88\uff0c\u5904\u5904\u4f2a\u88c5\uff0c\u501f\u7528\u66ff\u8eab\uff0c\u4f60\u7a76\u7adf\u662f\u54ea\u91cc\u89c1\u4e0d\u5f97\u4eba\u554a\uff1f\u201d \u3000\u3000\u201c\u5f20\u56db\u59d1\uff0c\u4f60\u5982\u6b64\u653e\u8086\uff0c\u4e0d\u8fc7\u662f\u51ed\u4f4d\u6c5f\u67ab\u548c\u4e24\u4e2a\u5c0f\u5996\u5973\u4e3a\u4f60\u6491\u8170\u7f62\u4e86\uff0c\u2026\u2026\u201d\u9752\u8863\u4eba\u9053\uff1a\u201c\u5982\u4eca\u6c5f\u67ab\u548c\u4e24\u4e2a\u5c0f\u5996\u5973\uff0c\u90fd\u5df2\u53d7\u4e86\u91cd\u4f24\uff0c\u4f60\u8fd8\u6709\u4ec0\u4e48\u4ed7\u6043\uff1f\u542c\u84dd\u51e4\u8bf4\uff0c\u4f60\u662f\u806a\u660e\u7684\u5973\u4eba\uff0c\u806a\u660e\u4eba\u5e94\u8bc6\u65f6\u52a1\uff0c\u73b0\u5728\uff0c\u8be5\u662f\u6289\u62e9\u7684\u65f6\u5019\u4e86\uff01\u201d \u3000\u3000\u201c\u6289\u62e9\u4ec0\u4e48\uff1f\u6211\u5012\u662f\u771f\u7684\u60f3\u4e0d\u901a\u4e86\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u5929\u9a6c\u5802\u805a\u96c6\u4e86\u6c5f\u6e56\u4e0a\u76f8\u5f53\u5f3a\u5927\u7684\u5b9e\u529b\uff0c\u4f46\u5374\u88ab\u4f60\u5229\u7528\u8fc7\u4e8c\u5341\u5e74\u540e\uff0c\u4eb2\u624b\u628a\u5b83\u6bc1\u53bb\uff0c\u4f60\u901a\u8fc7\u5c0f\u897f\u5929\u96f7\u97f3\u5bfa\u9b54\u6559\u4e2d\u4eba\uff0c\u5728\u4e2d\u539f\u6b66\u6797\u9053\u4e0a\uff0c\u5efa\u7acb\u8d77\u5929\u540e\u5bab\uff0c\u5929\u738b\u95e8\u8fd9\u4e24\u80a1\u529b\u91cf\uff0c\u4f60\u4e5f\u628a\u4ed6\u4eec\u7f6e\u4e8e\u6b7b\u5730\u3002 \u3000\u3000\u79cb\u79bb\u82b1\u662f\u4f60\u7531\u9ec4\u5929\u738b\u624b\u4e2d\u593a\u6765\u7684\u60c5\u5987\uff0c\u4f60\u5374\u662f\u4e00\u70b9\u4e5f\u4e0d\u60dc\u7231\uff0c\u5386\u6765\u5185\u6c5f\u6e56\u4eba\u5f7c\u6b64\u4fb5\u72af\u3001\u640f\u6740\uff0c\u53ea\u6709\u4e24\u4e2a\u539f\u56e0\u3002 \u3000\u3000\u4e00\u4e2a\u539f\u56e0\u662f\u6269\u5c55\u5730\u76d8\uff0c\u4e89\u53d6\u6536\u76ca\uff0c\u4ee5\u517b\u5e2e\u4ece\uff0c\u53e6\u4e00\u4e2a\u662f\u4e89\u53d6\u7edf\u4e00\u6b66\u6797\u7684\u76df\u4e3b\u4e4b\u4f4d\u3002 \u3000\u3000\u4f46\u4f60\u4e0d\u540c\uff0c\u6269\u5c55\u6c5f\u6e56\u52bf\u529b\uff0c\u5374\u53c8\u4e0d\u50cf\u4e89\u53d6\u6b66\u6797\u9738\u6743\uff0c\u4f60\u5fcc\u6068\u6240\u6709\u7684\u6b66\u6797\u95e8\u6237\u3001\u9ad8\u4eba\uff0c\u4e0d\u60dc\u52fe\u7ed3\u897f\u65b9\u9b54\u6559\uff0c\u6b8b\u6740\u4e2d\u539f\u9ad8\u624b\uff0c\u6311\u62e8\u3001\u6bd2\u6740\uff0c\u65e0\u6240\u4e0d\u7528\u5176\u6781\uff0c\u4f60\u7a76\u7adf\u662f\u8c01\uff1f\u7528\u5fc3\u4f55\u5728\uff1f\u96be\u9053\u9b54\u6559\u4e2d\u4eba\uff0c\u771f\u7684\u80fd\u9760\u5f97\u4f4f\u5417\uff1f \u3000\u3000\u4ed6\u4eec\u5728\u4e2d\u539f\u9053\u4e0a\u771f\u7684\u7fbd\u7ffc\u4e30\u6ee1\u4e86\uff0c\u4e00\u6837\u4f1a\u53cd\u566c\u4f60\uff0c\u541e\u4e86\u4f60\uff0c\u4f60\u7834\u574f\u4e86\u6b66\u6797\u4f20\u7edf\uff0c\u5374\u53c8\u5fd7\u4e0d\u5728\u6b66\u6797\u9738\u4e1a\uff01\u201d \u3000\u3000\u9752\u8863\u4eba\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u95ee\u7684\u597d\u3002\u6c5f\u6e56\u4e0a\uff0c\u6709\u4e0d\u5c11\u4eba\u6000\u7591\u6211\uff0c\u4f46\u4ed6\u4eec\u90fd\u6ca1\u6709\u4f60\u60f3\u7684\u8fd9\u6837\u6e05\u695a\u3002 \u3000\u3000\u4e5f\u6ca1\u6709\u4eba\u6562\u5f53\u9762\u8fd9\u6837\u8d28\u95ee\u6211\uff0c\u8001\u5b9e\u8bf4\uff0c\u6211\u4e5f\u5f88\u60f3\u628a\u81ea\u5df1\u7684\u770b\u6cd5\u8bf4\u660e\u4e00\u4e0b\uff0c\u4eca\u5929\u5c31\u7ed9\u4f60\u4e2a\u6ee1\u610f\u7684\u7b54\u590d\u5427\uff01\u201d \u3000\u3000\u9752\u8863\u4eba\u51dd\u795e\u601d\u7d22\u4e00\u9635\uff0c\u9053\uff1a\u201c\u6240\u8c13\u6c5f\u6e56\u4eba\uff0c\u53ea\u4e0d\u8fc7\u662f\u4e00\u7fa4\u6e38\u624b\u597d\u95f2\uff0c\u4e0d\u52a1\u6b63\u4e1a\u7684\u4eba\uff0c\u5578\u805a\u4e00\u5904\uff0c\u4ed6\u4eec\u8981\u4e0d\u8015\u800c\u98df\uff0c\u4e0d\u7ec7\u800c\u8863\uff0c\u6210\u7fa4\u7ed3\u515a\uff0c\u9c7c\u8089\u4e61\u91cc\uff0c\u8fd9\u6837\u7684\u4eba\uff0c\u662f\u4e0d\u662f\u8be5\u6b7b\u5462\uff1f\u2026\u2026\u201d \u3000\u3000\u201c\u56fd\u5bb6\u6709\u6cd5\uff0c\u6c5f\u6e56\u6709\u9053\uff0c\u4f60\u4e0d\u80fd\u4e00\u7aff\u5b50\u6253\u7ffb\u4e00\u8239\u4eba\uff0c\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u4f55\u51b5\uff0c\u5f88\u591a\u6c5f\u6e56\u4eba\uff0c\u662f\u51ed\u4ed7\u81ea\u5df1\u7684\u6280\u827a\uff0c\u7acb\u8db3\u4e8e\u4e16\uff0c\u6df7\u53e3\u996d\u5403\uff0c\u4fdd\u9556\u3001\u62a4\u9662\uff0c\u4ee5\u7ef4\u62a4\u9053\u8def\u7545\u901a\uff0c\u5c45\u5bb6\u5b89\u5b81\uff0c\u8fd9\u7b97\u4e0d\u7b97\u6b63\u4e1a\u5462\uff1f\u201d \u3000\u3000\u201c\u805a\u4f17\u4e71\u6cd5\uff0c\u4ee5\u6b66\u72af\u7981\u2026\u2026\u201d\u9752\u886b\u4eba\u9053\uff1a\u201c\u5b98\u5e9c\u7684\u4e8b\uff0c\u4ed6\u4eec\u4e5f\u662f\u8d8a\u4fce\u4ee3\u5e96\uff0c\u8fd9\u4e9b\u4eba\u5982\u4e0d\u9664\u6839\u52a1\u5c3d\uff0c\u5929\u4e0b\u6c38\u65e0\u592a\u5e73\u4e4b\u65e5\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u5fc3\u4e2d\u5df2\u5927\u81f4\u660e\u767d\u4e86\u4ed6\u7684\u8eab\u4efd\uff0c\u6de1\u6de1\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u4f46\u4f60\u7528\u7684\u624b\u6bb5\uff0c\u4e5f\u672a\u514d\u592a\u8fc7\u4efd\uff0c\u6bd4\u6c5f\u6e56\u4e2d\u4eba\u66f4\u4e3a\u6076\u6bd2\uff0c\u800c\u4e14\u5f15\u7528\u9b54\u6559\uff0c\u4e1c\u8fdb\u4e2d\u571f\uff0c\u5f15\u5f02\u4f10\u540c\uff0c\u4ed6\u4eec\u52bf\u529b\u5927\u4e86\uff0c\u4f60\u80fd\u5236\u670d\u5f97\u4e86\u5417\uff1f\u201d \u3000\u3000\u201c\u8fd9\u5c31\u4e0d\u7528\u4f60\u62c5\u5fc3\u4e86\u3002\u201d \u3000\u3000\u201c\u6211\u660e\u767d\u4e86\uff0c\u4f60\u662f\u5229\u7528\u4ed6\u4eec\uff0c\u7136\u540e\uff0c\u4e00\u9f13\u800c\u6b7c\u4e4b\u2026\u2026\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u542c\u4f60\u53e3\u97f3\uff0c\u5e74\u5c81\u4e0d\u5927\uff0c\u7edd\u975e\u6614\u5e74\u7b56\u52a8\u6c5f\u6e56\u5927\u53d8\u7684\u4eba\uff0c\u90a3\u4e2a\u4eba\u5462\uff1f\u662f\u4e0d\u662f\u4e5f\u88ab\u4f60\u6740\u4e86\u3002\u201d \u3000\u3000\u201c\u4f60\u5c3d\u5fc3\u6076\u6bd2\uff0c\u6311\u62d4\u79bb\u95f4\uff0c\u201d\u9752\u886b\u4eba\u5ffd\u7136\u52a8\u4e86\u6012\u706b\uff0c\u63a5\u9053\uff1a\u201c\u6740\u4e86\u5979\u3002\u201d \u3000\u3000\u84dd\u51e4\u7a81\u7136\u5f00\u4e86\u53e3\uff0c\u9053\uff1a\u201c\u6559\u4e3b\uff0c\u9b54\u6559\u4e2d\u4eba\uff0c\u597d\u50cf\u5df2\u7ecf\u64a4\u8d70\u4e86\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u51b7\u51b7\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u73b0\u5728\uff0c\u9601\u4e0b\u53ea\u6709\u81ea\u6c42\u591a\u798f\u4e86\uff0c\u6740\uff01\u4e00\u4e2a\u4e5f\u4e0d\u80fd\u653e\u8d70\uff01\u201d \u3000\u3000\u590f\u5929\u540c\u3001\u4e09\u5927\u697c\u4e3b\uff0c\u7a81\u7136\u98de\u8dc3\u800c\u8d77\uff0c\u653b\u5411\u9752\u886b\u4eba\u3002 \u3000\u3000\u4f46\u56db\u4e2a\u9ed1\u8863\u52b2\u88c5\u5927\u6c49\uff0c\u4e5f\u4e00\u9f50\u98de\u8d77\uff0c\u8fce\u5411\u590f\u5929\u540c\u548c\u4e09\u5927\u697c\u4e3b\u3002 \u3000\u3000\u4e00\u6765\u4e00\u8fce\uff0c\u5feb\u5982\u7535\u63a3\uff0c\u5175\u5203\u4ea4\u51fb\u58f0\u4e2d\uff0c\u590f\u5929\u540c\u548c\u4e09\u5927\u697c\u4e3b\uff0c\u5168\u88ab\u9707\u843d\u5b9e\u5730\u3002 \u3000\u3000\u9ed1\u8863\u52b2\u88c5\u5927\u6c49\uff0c\u7adf\u662f\u56db\u4e2a\u6b66\u529f\u7edd\u4e16\u7684\u9ad8\u624b\u3002 \u3000\u3000\u201c\u6c5f\u67ab\uff0c\u66ff\u4e09\u5723\u62a5\u4ec7\uff0c\u5728\u4f60\u4e00\u51fb\u4e86\uff01\u201d\u5f20\u56db\u59d1\u4f4e\u58f0\u9053\uff1a\u201c\u8fdf\u6050\u6709\u53d8\u3002\u201d\u6c5f\u67ab\u5e94\u58f0\u800c\u8d77\uff0c\u4e00\u9053\u51b7\u8292\uff0c\u98de\u7ed5\u5927\u6bbf\uff0c\u6bbf\u4e2d\u4eba\u90fd\u611f\u5230\u4e00\u80a1\u51b7\u6c14\uff0c\u4fb5\u80a4\u900f\u808c\uff0c\u4e0d\u81ea\u4e3b\u6253\u4e86\u4e00\u4e2a\u5bd2\u98a4\u3002 \u3000\u3000\u5c31\u662f\u4e00\u5239\u90a3\u95f4\uff0c\u4e8b\u60c5\u5df2\u7ecf\u7ed3\u675f\u4e86\uff0c\u516d\u5927\u9897\u4eba\u5934\uff0c\u6eda\u843d\u5730\u4e0a\uff0c\u4e24\u4e2a\u9752\u886b\u4eba\u548c\u56db\u4e2a\u9ed1\u8863\u52b2\u88c5\u5927\u6c49\uff0c\u65e0\u4e00\u5e78\u514d\u3002 \u3000\u3000\u201c\u606d\u559c\u6c5f\u5927\u54e5\uff0c\u7ec8\u4e8e\u7ec3\u6210\u4e86\u9a6d\u5251\u4e4b\u672f\u3002\u201d\u541f\u96ea\u3001\u541f\u971c\uff0c\u767d\u8863\u4e0a\u8840\u75d5\u72b9\u65b0\uff0c\u63d0\u7740\u5b9d\u5251\uff0c\u5e76\u80a9\u800c\u5165\u3002 \u3000\u3000\u6c5f\u67ab\u56de\u987e\u4e86\u5e76\u69bb\u800c\u5367\u7684\u4e24\u4e2a\u5973\u5b50\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u4eec\u5df2\u66ff\u4f60\u9664\u53bb\u4e86\u516b\u4e2a\u9b54\u5973\uff0c\u548c\u56db\u5927\u795e\u9b54\uff0c\u5c0f\u897f\u5929\u96f7\u97f3\u5bfa\u4e2d\u4eba\uff0c\u7ecf\u6b64\u632b\u6298\uff0c\u5927\u6982\u4e0d\u6562\u518d\u8f7b\u89c6\u4e2d\u539f\u6b66\u6797\u4e86\u3002\u201d\u541f\u96ea\u7b11\u9053\uff1a\u201c\u5e8a\u4e0a\u7684\u662f\u66ff\u8eab\uff0c\u6c5f\u5927\u54e5\u4e0d\u4f1a\u5fd8\u4e86\u6211\u4eec\u5df2\u5b66\u4f1a\u4e86\u5f20\u59e8\u7684\u5feb\u901f\u6613\u5bb9\u624b\u6cd5\uff0c\u4e3e\u624b\u4e4b\u95f4\uff0c\u5c31\u80fd\u6539\u53d8\u4e00\u4e2a\u4eba\u7684\u5bb9\u8c8c\u3002\u201d \u3000\u3000\u541f\u971c\u7a81\u7136\u7b11\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u6c5f\u5927\u54e5\u65e9\u5df2\u79fb\u60c5\u522b\u604b\uff0c\u54ea\u91cc\u8fd8\u4f1a\u5173\u5fc3\u5230\u6211\u548c\u59d0\u662f\u771f\u662f\u5047\uff1f\u201d \u3000\u3000\u201c\u8bf4\u7684\u662f\u554a\uff01\u2026\u2026\u201d\u541f\u96ea\u9053\uff1a\u201c\u6c5f\u5927\u54e5\u53c8\u7ec3\u6210\u9a6d\u5251\u4e4b\u672f\uff0c\u5df2\u6210\u5f53\u4eca\u4e4b\u4e16\u4e2d\u7b2c\u4e00\u5251\u5ba2\uff0c\u54b1\u4eec\u53ea\u6015\u4e0d\u4f1a\u662f\u4ed6\u7684\u654c\u624b\u4e86\uff1f\u201d \u3000\u3000\u201c\u90a3\u5012\u4e0d\u4e00\u5b9a\u554a\uff01\u2026\u2026\u201d\u541f\u971c\u9053\uff1a\u201c\u4ed6\u5251\u827a\u521d\u6210\uff0c\u6211\u4eec\u8054\u624b\u4e00\u51fb\uff0c\u4e5f\u8bb8\u53ef\u4ee5\u4e00\u51fb\u6210\u529f\u5462\uff1f\u201d \u3000\u3000\u5979\u4eec\u8bf4\u8bf4\u7b11\u7b11\uff0c\u8c01\u4e5f\u96be\u6d4b\u771f\u5047\uff1f \u3000\u3000\u4f46\u6c5f\u67ab\u5fc3\u4e2d\u660e\u767d\uff0c\u4e8c\u5973\u5fc3\u4e2d\u5df2\u4f0f\u6740\u673a\uff0c\u8bf4\u4e0d\u5b9a\u771f\u4f1a\u51fa\u624b\u6740\u4ed6\u3002 \u3000\u3000\u53ea\u56e0\u6b64\u523b\uff0c\u4e8c\u5973\u5df2\u628a\u4ed6\u770b\u6210\u4e86\u552f\u4e00\u7684\u654c\u624b\uff0c\u662f\u963b\u6b62\u5979\u4eec\u767b\u4e0a\u6c5f\u6e56\u7b2c\u4e00\u9ad8\u624b\u7684\u6700\u5927\u969c\u788d\u4e86\u3002 \u3000\u3000\u201c\u771f\u662f\u80e1\u8bf4\u516b\u9053\u2026\u2026\u201d\u6b27\u9633\u662d\u5fcd\u4e0d\u4f4f\u4e86\uff0c\u5389\u58f0\u559d\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4e2a\u5c0f\u4e2b\u5934\uff0c\u53ef\u662f\u75af\u4e86\uff1f\u201d \u3000\u3000\u201c\u4f60\u770b\u770b\uff0c\u7239\u4e5f\u5e2e\u5fd9\u6c5f\u5927\u54e5\u4e86\uff0c\u2026\u2026\u201d\u541f\u971c\u9053\uff1a\u201c\u4ed6\u593a\u8d70\u4e86\u6211\u4eec\u7684\u7236\u7231\uff0c\u4e5f\u9a97\u4e86\u6211\u4eec\u7684\u611f\u60c5\uff0c\u771f\u662f\u53ef\u607c\u7684\u5f88\u3002\u201d \u3000\u3000\u201c\u541f\u971c\uff0c\u6211\u662f\u771f\u5fc3\u7684\u559c\u7231\u4f60\u4eec\u54ea\uff01\u201d \u3000\u3000\u6c5f\u67ab\u611f\u89c9\u5230\u6108\u6765\u6108\u4e0d\u5bf9\u4e86\u3002 \u3000\u3000\u4e8c\u5973\u8a00\u8bed\u4e92\u6fc0\uff0c\u4e00\u65e6\u5931\u63a7\uff0c\u5c31\u9020\u6210\u60c5\u4fa3\u81ea\u6b8b\u7684\u5927\u60b2\u5267\u4e86\uff0c\u53ea\u597d\u59d4\u5c48\u6c42\u5168\uff0c\u5148\u6d88\u9664\u4e8c\u5973\u9010\u6e10\u5347\u9ad8\u7684\u6740\u673a\u3002 \u3000\u3000\u201c\u771f\u5fc3\u559c\u6b22\u6211\uff0c\u8fd8\u662f\u59b9\u59b9\uff1f\u201d\u541f\u96ea\u7b11\u9053\uff1a\u201c\u9752\u51e4\u5462\uff1f\u201d \u3000\u3000\u9752\u51e4\u62b9\u53bb\u8138\u4e0a\u9762\u5177\u3001\u836f\u7269\uff0c\u9053\uff1a\u201c\u541f\u96ea\u59b9\u59b9\uff0c\u6211\u5728\u8fd9\u91cc\uff01\u201d \u3000\u3000\u201c\u5509\uff01\u4f60\u8fd8\u6d3b\u7740\u554a\uff01\u201d\u541f\u971c\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u4e0d\u8ddf\u6885\u82b1\u4e00\u8d77\u6b7b\u53bb\u5462\uff1f\u201d \u3000\u3000\u201c\u5982\u662f\u541f\u971c\u59b9\u5b50\u8981\u6211\u6b7b\uff0c\u6211\u5c31\u7acb\u523b\u81ea\u520e\u3002\u201d\u9752\u51e4\u7f13\u7f13\u4e3e\u8d77\u53f3\u624b\u3002 \u3000\u3000\u201c\u9752\u51e4\uff0c\u4e0d\u8981\u52a8\uff0c\u201d\u6c5f\u67ab\u4f4e\u58f0\u9053\uff1a\u201c\u5979\u4eec\u6740\u673a\u5145\u5fc3\uff0c\u4f46\u53c8\u6709\u4e00\u70b9\u7075\u5149\u4e0d\u6627\uff0c\u4e0d\u8981\u5f15\u8d77\u5979\u4eec\u7684\u8bef\u4f1a\u3002\u201d \u3000\u3000\u9752\u51e4\u653e\u4e0b\u4e86\u53f3\u624b\uff0c\u53cc\u76ee\u4e2d\u6cea\u6c34\u6eda\u843d\uff0c\u9053\uff1a\u201c\u6c5f\u90ce\uff0c\u6211\u7684\u6b7b\uff0c\u5982\u80fd\u6362\u5f97\u4e24\u4f4d\u59b9\u5b50\u56de\u5fc3\u8f6c\u610f\uff0c\u6b7b\u800c\u65e0\u61be\uff01\u201d \u3000\u3000\u201c\u8bf4\u7684\u597d\u53ef\u601c\u554a\uff01\u201d\u541f\u96ea\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8584\u547d\u601c\u537f\u7518\u4f5c\u59be\uff0c\u6c5f\u5927\u54e5\u5c31\u662f\u88ab\u4f60\u8fd9\u6837\u8ff7\u4f4f\u4e86\u3002\u201d \u3000\u3000\u9752\u51e4\u5446\u4f4f\u4e86\uff0c\u8fd9\u54ea\u91cc\u662f\u5c0f\u4ed9\u5973\u822c\u7684\u541f\u96ea\u59b9\u5b50\uff0c\u90a3\u4e2a\u624d\u6167\u7075\u6c14\uff0c\u90fd\u8ba9\u5979\u4e94\u4f53\u6295\u5730\u7684\u4eba\u3002 \u3000\u3000\u5f20\u56db\u59d1\u4e5f\u5446\u4f4f\u4e86\uff0c\u5343\u601d\u4e07\u60f3\uff0c\u4e5f\u6ca1\u6709\u60f3\u5230\u4f1a\u6709\u8fd9\u4e9b\u53d8\u5316\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u6218\u5c40\u7684\u8f6c\u53d8\uff0c\u4e8b\u60c5\u7684\u53d1\u5c55\uff0c\u5df2\u5b8c\u5168\u4e0d\u5728\u5979\u63a8\u65ad\u4e4b\u4e0b\uff0c\u201c\u4f20\u771f\u8bb0\u201d\u6539\u53d8\u4e86\u4e00\u5207\u9884\u60f3\u7684\u56f0\u96be\uff0c\u4f46\u4e5f\u5236\u9020\u51fa\u4e00\u4e2a\u66f4\u5927\u7684\u9ebb\u70e6\u3002 \u3000\u3000\u6709\u6240\u5f97\uff0c\u5fc5\u6709\u6240\u5931\uff0c\u5929\u4e0b\u4e4b\u5927\uff0c\u6c5f\u6e56\u4e4b\u5947\uff0c\u4e0d\u662f\u4e00\u4e2a\u4eba\u6240\u80fd\u9884\u5224\u63a8\u8bba\u4e86\u3002 \u3000\u3000\u4f46\u5f20\u56db\u59d1\u6700\u5927\u7684\u5fcc\u8bb3\uff0c\u662f\u6015\u96ea\u3001\u971c\u4e8c\u5973\uff0c\u628a\u7bad\u5934\u6307\u5411\u5979\uff0c\u76f4\u63a5\u4e86\u5f53\u7684\u8bf4\u51fa\u6765\u5979\u548c\u6c5f\u67ab\u7684\u5173\u7cfb\u3002 \u3000\u3000\u5f53\u7740\u8fd9\u4e48\u591a\u4eba\u7684\u9762\u524d\uff0c\u5f20\u56db\u59d1\u4e5f\u6709\u7740\u62db\u67b6\u4e0d\u4f4f\u7684\u611f\u89c9\uff0c\u6240\u4ee5\u5979\u4e0d\u6562\u5f00\u53e3\uff0c\u662f\u975e\u53ea\u4e3a\u591a\u5f00\u53e3\u554a\uff01 \u3000\u3000\u96ea\u3001\u971c\u4e8c\u5973\uff0c\u4f3c\u662f\u516d\u4eb2\u4e0d\u8ba4\uff0c\u50cf\u8f6c\u52a8\u7684\u5229\u5203\u4e00\u822c\uff0c\u78b0\u5230\u8c01\uff0c\u5c31\u522e\u4e0b\u8c01\u4e00\u5c42\u8840\u8089\u3002 \u3000\u3000\u8fde\u6b27\u9633\u662d\u4e5f\u611f\u5230\u4e8c\u4e2a\u5973\u513f\u5996\u5f02\u96be\u6d4b\uff0c\u5664\u82e5\u5bd2\u8749\uff0c\u4e0d\u6562\u5f00\u53e3\u4e86\u3002 \u3000\u3000\u201c\u4f60\u662f\u84dd\u51e4\u2026\u2026\u201d\u541f\u971c\u8f6c\u4e86\u65b9\u5411\uff0c\u76ee\u6ce8\u84dd\u51e4\uff0c\u63a5\u9053\uff1a\u201c\u4f60\u4e00\u70b9\u4e5f\u4e0d\u7f8e\u4e3d\uff0c\u6c5f\u5927\u54e5\uff0c\u4e3a\u4ec0\u4e48\u4f1a\u559c\u6b22\u4f60\uff1f\u201d \u3000\u3000\u201c\u662f\uff01\u6211\u53ea\u662f\u4e00\u4e2a\u5f88\u5eb8\u4fd7\u7684\u5973\u5b69\u5b50\uff0c\u54ea\u91cc\u6bd4\u5f97\u4e0a\u4e24\u4f4d\u4e3d\u8d28\u5929\u751f\u7684\u59b9\u59b9\u2026\u2026\u201d\u84dd\u51e4\u5e94\u4ed8\u7684\u975e\u5e38\u597d\uff0c\u8138\u4e0a\u4e00\u7247\u7b11\u5bb9\uff0c\u63a5\u9053\uff1a\u201c\u6b64\u540e\uff0c\u8fd8\u8981\u4e24\u4f4d\u59b9\u5b50\u591a\u591a\u7684\u6307\u6559\uff01\u201d \u3000\u3000\u201c\u8fd9\u5c31\u4e0d\u80fd\u602a\u4f60\u4e86\uff01\u201d\u541f\u971c\u9053\uff1a\u201c\u53ea\u602a\u6c5f\u5927\u54e5\u778e\u4e86\u4e00\u5bf9\u773c\u775b\uff0c\u4e0d\u8fa8\u7f8e\u4e11\u3002\u201d \u3000\u3000\u84dd\u51e4\u4e00\u7b11\u800c\u7f62\uff0c\u5fcd\u53d7\u4e86\u6700\u5927\u7684\u4f24\u5bb3\u3002 \u3000\u3000\u6c5f\u67ab\u7684\u8eab\u4f53\u5fae\u5fae\u98a4\u6296\uff0c\u4f3c\u662f\u5df2\u88ab\u6fc0\u51fa\u4e86\u6012\u706b\u3002 \u3000\u3000\u5ffd\u7136\u95f4\uff0c\u68b5\u97f3\u98d8\u6e3a\uff0c\u4e0d\u77e5\u4f55\u65f6\uff0c\u5927\u548c\u5c1a\u5df2\u5f00\u59cb\u541f\u51fa\u4e86\u5927\u60b2\u68b5\u5531\u3002 \u3000\u3000\u4e8c\u5973\u7751\u4e0a\u7684\u5996\u5f02\u3001\u6740\u673a\uff0c\u9010\u6e10\u6d88\u9000\uff0c\u4f3c\u662f\u7531\u53e6\u4e00\u4e2a\u7684\u5883\u754c\u4e2d\u56de\u8f6c\u8fc7\u6765\uff0c\u5bf9\u671b\u4e86\u4e00\u9635\uff0c\u7a81\u7136\u5f03\u53bb\u624b\u4e2d\u4e4b\u5251\uff0c\u5954\u5411\u6c5f\u67ab\uff0c\u9053\uff1a\u201c\u6c5f\u5927\u54e5\uff0c\u6211\u4eec\u521a\u5bf8\u8bf4\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5979\u4eec\u4ecd\u6709\u8bb0\u5fc6\uff0c\u53ea\u662f\u6709\u4e00\u4e9b\u6a21\u7cca\u4e0d\u6e05\u3002 \u3000\u3000\u6c5f\u67ab\u7d27\u62e5\u4e8c\u5973\uff0c\u9053\uff1a\u201c\u90fd\u5df2\u7ecf\u8fc7\u53bb\u4e86\u3002\u201d \u3000\u3000\u84dd\u98ce\u62ed\u53bb\u8138\u4e0a\u7684\u6cea\u75d5\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5f20\u59e8\uff0c\u63ed\u5f00\u6559\u4e3b\u548c\u4ed6\u56db\u4ece\u536b\u8138\u4e0a\u7684\u9762\u5177\u770b\u4e00\u4e0b\uff0c\u6211\u4eec\u4e5f\u8be5\u8d70\u4e86\u3002\u201d \u3000\u3000\u5f20\u56db\u59d1\u9053\uff1a\u201c\u8bf4\u7684\u662f\u2026\u2026\u201d\u4e3e\u6b65\u5411\u6559\u4e3b\u884c\u53bb\u3002 \u3000\u3000\u5927\u548c\u5c1a\u8d70\u8fc7\u6765\uff0c\u7a81\u51fa\u4e24\u6307\uff0c\u70b9\u4e86\u541f\u96ea\u3001\u541f\u971c\u7684\u7a74\u9053\uff0c\u7b11\u9053\uff1a\u201c\u5929\u4e0b\u672c\u65e0\u4e8b\uff0c\u5eb8\u4eba\u81ea\u6270\u4e4b\uff0c\u8fd9\u662f\u6c5f\u6e56\u6069\u6028\u7684\u4ec7\u6740\uff0c\u4ed6\u4eec\u4ee5\u6c5f\u6e56\u8eab\u4efd\u800c\u6b7b\uff0c\u53c8\u4f55\u82e6\u5bfb\u6839\u7a76\u5e95\u5462\uff1f\u201d \u3000\u3000\u201c\u662f\u554a\uff01\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u6211\u4eec\u6740\u4e86\u4e2a\u795e\u79d8\u7684\u6559\u4e3b\uff0c\u7ba1\u4ed6\u662f\u8c01\u5462\uff1f\u201d \u3000\u3000\u201c\u84dd\u51e4\u59d1\u5a18\u8bf4\u7684\u5bf9\uff01\u6211\u4eec\u8be5\u8d70\u4e86\uff0c\u6c5f\u6e56\u4ecd\u7136\u662f\u6c5f\u6e56\uff0c\u53ea\u6709\u4e00\u65f6\u9eef\u6de1\uff0c\u4e0d\u4f1a\u6539\u53d8\u3002\u201d\u5927\u548c\u5c1a\u9053\uff1a\u201c\u6c5f\u67ab\u62b1\u8d77\u4f60\u4e24\u4e2a\u5c0f\u5ab3\u5987\uff0c\u5979\u4eec\u4e5f\u8be5\u6062\u590d\u81ea\u6211\u4e86\uff0c\u771f\u96be\u4e3a\u4e86\u4e24\u4e2a\u5c0f\u4e2b\u5934\uff0c\u4e3a\u4e86\u8fd9\u573a\u6740\u52ab\uff0c\u8ba9\u5979\u4eec\u8ff7\u5931\u4e86\u4e00\u4e2a\u6708\u3002\u201d \u3000\u3000\u7f57\u5170\u4f4e\u58f0\u9053\uff1a\u201c\u56db\u59d1\uff0c\u771f\u7684\u4e0d\u77a7\u77a7\u90a3\u4f4d\u6559\u4e3b\u8eab\u4efd\u5417\uff1f\u201d \u3000\u3000\u201c\u770b\u4e86\u53c8\u5982\u4f55\uff1f\u201d\u5f20\u56db\u59d1\u9053\uff1a\u201c\u5927\u548c\u5c1a\u8bf4\u7684\u5bf9\uff0c\u8fd9\u53ea\u662f\u4e00\u573a\u6c5f\u6e56\u4ec7\u6740\u3002\u201d \u3000\u3000\u4f46\u95fb\u8e44\u58f0\u5982\u96f7\uff0c\u4f3c\u4e4e\u6709\u5e72\u519b\u4e07\u9a6c\uff0c\u6b63\u5411\u8fd9\u9762\u8d76\u6765\u3002 \u3000\u3000\u6b27\u9633\u662d\u9053\uff1a\u201c\u8d70\uff01\u5230\u6211\u5927\u5df4\u5c71\u7684\u5e7d\u8c37\u4e2d\u5c0f\u4f4f\u51e0\u65e5\uff0c\u6211\u8981\u529e\u5ac1\u5986\uff0c\u4e00\u4e0b\u5b50\u51fa\u5ac1\u56db\u4e2a\u5973\u513f\uff0c\u53ef\u662f\u5927\u559c\u554a\uff01\u4f60\u4eec\u90fd\u8981\u53bb\u559d\u676f\u559c\u9152\u3002\u201d \u3000\u3000\u5de6\u53f3\u53cc\u624b\uff0c\u5206\u522b\u6293\u4f4f\u4e86\u9752\u51e4\u3001\u84dd\u51e4\uff0c\u5f53\u5148\u5411\u5916\u884c\u53bb\u3002 \u3000\u3000\u5f20\u56db\u59d1\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8c01\u8bf4\u4eba\u4e0d\u4f1a\u53d8\uff1f\u201d \u3000\u3000\u4e24\u4e2a\u5c0f\u72d0\u5973\u662f\u5426\u771f\u7684\u80fd\u6062\u590d\uff0c\u5f80\u6614\u7684\u5a07\u75f4\u65e0\u90aa\uff0c\u4e16\u4e8b\u662f\u90a3\u4e48\u96be\u6599\uff0c\u201c\u4f20\u771f\u8bb0\u201d\u8fd9\u672c\u90aa\u6076\u4e4b\u4e66\uff0c\u7ed9\u5979\u4eec\u8bb0\u5fc6\u592a\u6df1\u523b\u4e86\u3002 \u3000\u3000\uff08\u5168\u4e66\u5b8c\xb7\u79cb\u8349\u7f16\u8f91\uff09"}),showOnHomepage:!0}]}}]);